INSERT INTO "hussein_taji" VALUES(1,1,'1:1','به نام خداوند بخشندۀ مهربان');
INSERT INTO "hussein_taji" VALUES(1,2,'1:2','ستایش مخصوص الله است که پروردگار جهانیان است.');
INSERT INTO "hussein_taji" VALUES(1,3,'1:3','بخشندۀ مهربان است');
INSERT INTO "hussein_taji" VALUES(1,4,'1:4','مالک روز جزاء است.');
INSERT INTO "hussein_taji" VALUES(1,5,'1:5','تنها تو را می‌پرستیم؛ و تنها از تو یاری می‌جوییم.');
INSERT INTO "hussein_taji" VALUES(1,6,'1:6','ما را به راه راست هدایت کن.');
INSERT INTO "hussein_taji" VALUES(1,7,'1:7','راه کسانی‌که بر آنان نعمت دادی؛ نه خشم گرفتگان بر آن‌ها؛ و نه گمراهان [مراد از ﴿أَنۡعَمۡتَ عَلَيۡهِمۡ﴾: انبیا، صدیقین، شهداء و صالحین هستند، چنانکه در سورۀ نساء آیه آمده است. و مراد از ﴿ٱلۡمَغۡضُوبِ عَلَيۡهِمۡ﴾ یهود اند. مراد از گمراهان، نصاری می‌باشد].');
INSERT INTO "hussein_taji" VALUES(2,1,'2:1','الف. لام. میم [ الف. لام. میم، حروف مقطعه هستند که در اوایل برخی از سوره‌های قرآن آمده است، که معانی آن‌ها را تنها الله متعال می‌داند. (تفسیر ابن کثیر)].');
INSERT INTO "hussein_taji" VALUES(2,2,'2:2','این کتابی است که هیچ شکی در آن نیست و مایه هدایت پرهیزگاران است. [مراد از پرهیزگاران کسانی‌اند که فقط از الله متعال می‌ترسند و احکام او را به‌جای می‌آورند و از چیزهای منع‌شده دوری می‌جویند].');
INSERT INTO "hussein_taji" VALUES(2,3,'2:3','آن کسانی‌که به غیب ایمان می‌آورند و نماز را بر پا می‌دارند و از آنچه روزی‌شان داده‌ایم انفاق می‌کنند. [مراد از غیب، تمام آن چیزهایی است که عقل و حواس نتواند آن را در دنیا درک کند مانند: وجود الله متعال، فرشتگان، حیات بعد از مرگ و غیره، و در اینجا مراد از ایمان به غیب، یقین‌داشتن به تمام آن مسایل ماورای عقل و حواس که الله و رسول ما را از آن خبر داده‌اند می‌باشد مانند: ایمان به الله متعال، اعتقاد داشتن به فرشتگان، کتاب‌های آسمانی، پیامبران، روز رستاخیز و قضا و قدر.]');
INSERT INTO "hussein_taji" VALUES(2,4,'2:4','و آنان که به آنچه بر تو نازل شده و آنچه (بر پیامبران) پیش از تو نازل شده، ایمان می‌آورند، و به روز رستاخیز یقین دارند.');
INSERT INTO "hussein_taji" VALUES(2,5,'2:5','آنان بر هدایت از جانب پروردگارشانند و آنان رستگارانند.');
INSERT INTO "hussein_taji" VALUES(2,6,'2:6','کسانی‌که کافر شدند، برایشان یکسان است که آنان را بترسانی یا نترسانی، ایمان نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(2,7,'2:7','الله بر دلها و گوش‌هایشان مهر زده است و بر چشم‌هایشان پرده‌ای است، و برای آن‌ها عذاب بزرگی است.');
INSERT INTO "hussein_taji" VALUES(2,8,'2:8','گروهی از مردم هستند که می‌گویند: به الله و روز رستاخیز ایمان آورده‌ایم، در حالی‌که ایمان ندارند.');
INSERT INTO "hussein_taji" VALUES(2,9,'2:9','آنان (به نظرشان) الله و مؤمنان را فریب می‌دهند، در حالی‌که جز خودشان را فریب نمی‌دهند ولی نمی‌فهمند.');
INSERT INTO "hussein_taji" VALUES(2,10,'2:10','در دل‌های آنان بیماری است و الله بر بیماری آنان افزوده، و بخاطر دروغ‌هایی که می‌گفتند، برایشان عذاب دردناکی است.');
INSERT INTO "hussein_taji" VALUES(2,11,'2:11','و هنگامی‌که به آن‌ها گفته شود: «در زمین فساد نکنید» می‌گویند: «ما فقط اصلاح‌کننده‌ایم».');
INSERT INTO "hussein_taji" VALUES(2,12,'2:12','آگاه باشید! آن‌ها همان مفسدانند؛ ولی نمی‌فهمند.');
INSERT INTO "hussein_taji" VALUES(2,13,'2:13','و هنگامی‌که به آنان گفته شود: «ایمان بیاورید؛ چنانکه مردم ایمان آورده‌اند» می‌گویند: «آیا ایمان بیاوریم چنانکه بی‌خردان ایمان آوردند؟» آگاه باشید، آن‌ها همان نابخردانند؛ ولی نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(2,14,'2:14','و هنگامی‌که افراد با ایمان را ملاقات می‌کنند، می‌گویند: «ما ایمان آورده‌ایم»، و هنگامی‌که با شیطان‌های خود خلوت می‌کنند، می‌گویند: «ما با شماییم، ما فقط (آن‌ها را) مسخره می‌کنیم».');
INSERT INTO "hussein_taji" VALUES(2,15,'2:15','الله آنان را مسخره می‌کند، و آن‌ها را در طغیان‌شان نگه می‌دارد تا سرگردان شوند.');
INSERT INTO "hussein_taji" VALUES(2,16,'2:16','آنان کسانی هستند که گمراهی را به هدایت خریدند، پس تجارت آن‌ها سودی نداد، و هدایت نیافتند.');
INSERT INTO "hussein_taji" VALUES(2,17,'2:17','داستان اینان همانند داستان کسی است که آتشی افروخته پس چون آتش اطراف او را روشن ساخت، الله نورشان را بگرفت و در تاریکی‌هایی که نمی‌بینند رهایشان کرد.');
INSERT INTO "hussein_taji" VALUES(2,18,'2:18','کرانند، گنگانند، کورانند، پس باز نمی‌گردند.');
INSERT INTO "hussein_taji" VALUES(2,19,'2:19','یا همچون بارانی است که از آسمان (فرو ریزد) و در آن تاریکی‌ها و رعد و برق باشد، و آن‌ها از ترس مرگ، انگشتان خود را در گوش‌هایشان فرو می‌برند تا صدای صاعقه را نشنوند، و الله به کافران احاطه دارد.');
INSERT INTO "hussein_taji" VALUES(2,20,'2:20','نزدیک است که برق چشمان‌شان را برباید، هر گاه که برای آن‌ها روشن شود در (روشنائی) آن راه می‌روند و چون تاریک شود (در جای خود) بایستند. و اگر الله بخواهد گوش و چشمان‌شان را از بین می‌برد، چرا که الله بر هر چیز توانا است.');
INSERT INTO "hussein_taji" VALUES(2,21,'2:21','ای مردم، پروردگار خود را پرستش کنید، آن کسی‌که شما و کسانی را که پیش از شما بودند آفرید، تا پرهیزگار شوید.');
INSERT INTO "hussein_taji" VALUES(2,22,'2:22','آن کس‌که زمین را برای شما بگسترد و آسمان را همچون سقفی بالای سر شما قرار داد. و از آسمان آبی فرو فرستاد و بوسیلۀ آن انواع میوه‌ها را بوجود آورد تا روزی شما باشد. بنابراین برای الله همتایانی قرار ندهید، در حالی‌که می‌دانید.');
INSERT INTO "hussein_taji" VALUES(2,23,'2:23','و اگر دربارۀ آنچه بر بنده خود (محمد صلی الله علیه و سلم) نازل کرده‌ایم؛ در شک و تردید هستید، سوره‌ای همانند آن بیاورید و گواهان خود را - غیر از الله- فرا خوانید؛ اگر راست می‌گویید.');
INSERT INTO "hussein_taji" VALUES(2,24,'2:24','پس اگر چنین نکردید - و هرگز نتوانید کرد- از آتشی بترسید که هیزم آن مردم و سنگ‌ها است، و برای کافران آماده شده است.');
INSERT INTO "hussein_taji" VALUES(2,25,'2:25','به کسانی‌که ایمان آورده، و کارهای شایسته انجام داده‌اند مژده ده که باغ‌هایی از بهشت برای آن‌هاست که نهر‌ها از زیر (درختان) آن جاری است. هر زمان که میوه‌ای از آن به آنان داده شود گویند: «این همان است که قبلاً به ما روزی داده شده بود» و میوه‌هایی همانند آورده شود، و برای آنان همسرانی پاکیزه است و جاودانه در آن خواهند بود.');
INSERT INTO "hussein_taji" VALUES(2,26,'2:26','به‌راستی الله از اینکه به پشه‌ای یا بالاتر از آن مثال بزند شرم نمی‌کند. آنان که ایمان آورده‌اند می‌دانند که آن (مثل) حق است از طرف پروردگارشان و اما آنهایی که کافرند می‌گویند: الله از این مثل چه خواسته است؟ الله بسیاری را با آن گمراه، و گروه بسیاری را هدایت می‌کند، و تنها فاسقان را با آن گمراه می‌سازد.');
INSERT INTO "hussein_taji" VALUES(2,27,'2:27','کسانی‌که پیمان الله را، پس از محکم ساختن آن می‌شکنند، و آنچه را که الله دستور داده بر قرار سازند، قطع نموده، و در روی زمین فساد می‌کنند، این‌ها زیانکارانند.');
INSERT INTO "hussein_taji" VALUES(2,28,'2:28','چگونه به الله کافر می‌شوید؟ در حالی‌که شما مردگان بودید، و او شما را زنده کرد، سپس شما را می‌میراند، سپس شما را زنده می‌کند، آنگاه به‌سوی او باز گردانده می‌شوید.');
INSERT INTO "hussein_taji" VALUES(2,29,'2:29','او (الله) است که همه آنچه را که در زمین است برای شما آفرید، آنگاه آهنگ آسمان کرد و آن‌ها را به‌صورت هفت آسمان مرتب نمود، و او به هر چیز آگاه است.');
INSERT INTO "hussein_taji" VALUES(2,30,'2:30','و (بیاد بیاور) هنگامی را که پروردگارت به فرشتگان گفت: «من در زمین جانشینی قرار خواهم داد». گفتند: «آیا کسی را در آن قرار می‌دهی که در آن فساد و خونریزی کند؟ ما تسبیح و حمد تو را به جا می‌آوریم و تو را تقدیس می‌کنیم». پروردگار فرمود: «یقیناً من می‌دانم آنچه را که شما نمی‌دانید».');
INSERT INTO "hussein_taji" VALUES(2,31,'2:31','و نام‌ها را همگی به آدم آموخت. سپس آن‌ها را به فرشتگان عرضه داشت، و فرمود: «اگر راستگویید، نام‌های این‌ها را به من خبر دهید».');
INSERT INTO "hussein_taji" VALUES(2,32,'2:32','(فرشتگان) گفتند: «منزهی تو!. ما چیزی جز آنچه به ما آموخته‌ای نمی‌دانیم، تو دانای حکیمی».');
INSERT INTO "hussein_taji" VALUES(2,33,'2:33','فرمود: «ای آدم! آنان را از نام‌هایشان آگاه کن» هنگامی‌که آنان را آگاه کرد (الله) فرمود: «آیا به شما نگفتم که من غیب آسمان‌ها و زمین را می‌دانم؟! و نیز می‌دانم آنچه را که آشکار می‌کنید، و آنچه را پنهان می‌داشتید».');
INSERT INTO "hussein_taji" VALUES(2,34,'2:34','(یاد بیاور) هنگامی را که به فرشتگان گفتیم: «برای آدم سجده کنید» پس (همگی) سجده کردند، جز ابلیس که سرباز زد، و تکبر ورزید، و از کافران شد.');
INSERT INTO "hussein_taji" VALUES(2,35,'2:35','و گفتیم: «ای آدم! تو با همسرت در بهشت سکونت کن، و از (میوه‌های) آن هر جا می‌خواهید، گوارا بخورید، و لیکن به این درخت نزدیک نشوید، که از ستمگران خواهید شد».');
INSERT INTO "hussein_taji" VALUES(2,36,'2:36','پس شیطان موجب لغزش آن دو شد، سپس آنان را از آنچه در آن بودند (= بهشت) بیرون کرد. و گفتیم: «همگی (به زمین) فرود آیید در حالی‌که بعضی دشمن بعضی خواهید بود. و برای شما در زمین تا مدت معینی قرارگاه و وسیلۀ بهره برداری خواهد بود».');
INSERT INTO "hussein_taji" VALUES(2,37,'2:37','آنگاه آدم از پروردگارش کلماتی فرا گرفت و الله توبه او را پذیرفت، چرا که الله توبه‌پذیر مهربان است. [مراد از «کلمات» سخنانی است که حضرت آدم علیه السلام با آن دعا و استغفار نمود و در سورۀ اعراف آیه بیان شده است. (تفسیر ابن کثیر)]');
INSERT INTO "hussein_taji" VALUES(2,38,'2:38','گفتیم: «همگی از آن فرود آیید! پس هر گاه هدایتی از طرف من برای شما آمد، کسانی‌که از آن پیروی کنند، نه ترسی بر آنان خواهد بود و نه غمگین خواهند شد».');
INSERT INTO "hussein_taji" VALUES(2,39,'2:39','و کسانی‌که کافر شدند و نشانه‌های ما را دروغ پنداشتند آنان اهل دوزخند، و همیشه در آن خواهند بود.');
INSERT INTO "hussein_taji" VALUES(2,40,'2:40','ای فرزندان اسرائیل (= یعقوب)! نعمت مرا که به شما ارزانی داشتم به یاد آورید، و به پیمانی که با من بسته‌اید وفا کنید، تا من نیز به پیمان شما وفا کنم و تنها از من بترسید.');
INSERT INTO "hussein_taji" VALUES(2,41,'2:41','و به آنچه نازل کرده‌ام (= قرآن) ایمان بیاورید که تصدیق‌کننده چیزی است که با شما است، و نخستین کافر به آن نباشید، و آیات مرا به بهای ناچیز نفروشید، و تنها از من بترسید.');
INSERT INTO "hussein_taji" VALUES(2,42,'2:42','و حق را با باطل نیامیزید، و حق را پنهان نکنید، در حالی‌که می‌دانید.');
INSERT INTO "hussein_taji" VALUES(2,43,'2:43','و نماز را بر پا دارید و زکات را بپردازید، و با نمازگزاران نماز بخوانید.');
INSERT INTO "hussein_taji" VALUES(2,44,'2:44','آیا مردم را به نیکی فرمان می‌دهید، و خودتان را فراموش می‌کنید، در حالی‌که شما کتاب (آسمانی) را می‌خوانید؟! آیا نمی‌اندیشید؟!');
INSERT INTO "hussein_taji" VALUES(2,45,'2:45','و از شکیبایی و نماز یاری جویید، و نماز؛ جز بر فروتنان، دشوار و گران است.');
INSERT INTO "hussein_taji" VALUES(2,46,'2:46','آن کسانی‌که یقیناً می‌دانند دیدار کنندۀ پروردگار خویش‌اند و به‌سوی او باز می‌گردند.');
INSERT INTO "hussein_taji" VALUES(2,47,'2:47','ای فرزندان اسرائیل (= یعقوب)! نعمت مرا که به شما ارزانی داشتم به یاد آورید، و (نیز) اینکه من شما را بر جهانیان برتری دادم.');
INSERT INTO "hussein_taji" VALUES(2,48,'2:48','و از روزی بترسید که کسی از کسی کفایتی نکند، و از او شفاعت پذیرفته نمی‌شود، و نه از او تاوان و فدیه گرفته می‌شود، و نه یاری می‌شوند.');
INSERT INTO "hussein_taji" VALUES(2,49,'2:49','و (نیز به یاد آورید) هنگامی‌که شما را از چنگال فرعونیان نجات دادیم، (آنان) شما را به بدترین صورت آزار و شکنجه می‌دادند، پسران شما را سر می‌بریدند، و زنان شما را زنده نگه می‌داشتند، و در این‌ها آزمایش بزرگی از جانب پروردگارتان برای شما بود.');
INSERT INTO "hussein_taji" VALUES(2,50,'2:50','و (به یاد آورید) هنگامی را که دریا را برای شما شکافتیم، و شما را نجات دادیم، و فرعونیان را غرق ساختیم، در حالی‌که شما می‌نگریستید.');
INSERT INTO "hussein_taji" VALUES(2,51,'2:51','و (به یاد آورید) هنگامی را که با موسی چهل شب وعده گذاردیم، آنگاه شما بعد از او گوساله را (به پرستش) گرفتید، در حالی‌که ستمکار بودید.');
INSERT INTO "hussein_taji" VALUES(2,52,'2:52','سپس شما را بعد از آن بخشیدیم، شاید سپاسگزاری کنید.');
INSERT INTO "hussein_taji" VALUES(2,53,'2:53','و (نیز به یاد آورید) هنگامی را که به موسی کتاب (تورات) و فرقان (معجزه‌های جداکننده میان حق و باطل) را عطا کردیم، تا اینکه هدایت شوید.');
INSERT INTO "hussein_taji" VALUES(2,54,'2:54','و (به یاد آورید) زمانی را که موسی به قوم خود گفت: «ای قوم من! شما با (به پرستش) گرفتن گوساله به خود ستم کرده‌اید، پس به‌سوی آفریدگار خود باز گردید و توبه کنید، و خود را (همدیگر را) بکشید، این کار برای شما در پیشگاه پروردگارتان بهتر است». آنگاه الله توبه شما را پذیرفت، زیرا که او توبه‌پذیر مهربان است.');
INSERT INTO "hussein_taji" VALUES(2,55,'2:55','و (به یاد آورید) هنگامی را که گفتید: «ای موسی! ما هرگز به تو ایمان نمی‌آوریم، مگر اینکه الله را آشکارا ببینیم». پس صاعقه شما را گرفت، در حالی‌که می‌نگریستید.');
INSERT INTO "hussein_taji" VALUES(2,56,'2:56','سپس شما را پس از مردن‌تان بر انگیختیم، شاید سپاسگزاری کنید.');
INSERT INTO "hussein_taji" VALUES(2,57,'2:57','و ابر را بر شما سایبان قرار دادیم، و «من» (ترنجبین) و «سلوی» (بلدرچین) را بر شما فرو فرستادیم، (و گفتیم): «از پاکیزه‌هایی که به شما روزی داده‌ایم؛ بخورید» (ولی شما کفر ورزیدید). آن‌ها به ما ستم نکردند؛ بلکه به خود ستم می‌نمودند.');
INSERT INTO "hussein_taji" VALUES(2,58,'2:58','و (به یاد آورید) هنگامی را که گفتیم: «به این شهر (= بیت‌المقدس) وارد شوید، و از نعمت‌های آن از هر جا که می‌خواهید به فراوانی بخورید، و با خشوع و فروتنی به دروازه در آیید و بگویید: «خواسته ما آمرزش گناهان است» تا گناهان شما را ببخشیم، و به نیکو کاران فزون‌تر می‌بخشیم».');
INSERT INTO "hussein_taji" VALUES(2,59,'2:59','آنگاه ستمگران این سخن را که به آن‌ها گفته شده بود، تغییر دادند (و به جای آن کلمۀ مسخره‌آمیزی گفتند) لذا بر ستمگران در برابر این نافرمانی‌شان، عذابی از آسمان نازل کردیم. [رسول الله صلی الله علیه و سلم فرمودند: «به بنی اسرائیل گفته شد: آنگاه آنان با نشیمنگاه‌هایشان خزیدند و عقب عقب وارد شدند، و به جای (حِطَّةٌ) (گناهان ما را ببخش) گفتند: (حِنّطَةٌ) حَبَّةٌ فَي شَعْرَةٍ، دانه‌ای در جو». (صحیح بخاری: ]');
INSERT INTO "hussein_taji" VALUES(2,60,'2:60','و (به یاد آورید) هنگامی را که موسی برای قوم خویش، آب خواست، پس گفتیم: «عصای خود را بر سنگ بزن» آنگاه دوازده چشمۀ آب از آن جوشید، و هر گروهی آبشخور خود را دانست، (و گفتیم): «از روزی الله بخورید و بیاشامید، و در زمین به تباهی مکوشید».');
INSERT INTO "hussein_taji" VALUES(2,61,'2:61','و (به یاد آورید) زمانی را که گفتید: «ای موسی! ما هرگز بر یک نوع غذا شکیبایی نداریم، پس پروردگار خویش را بخوان، تا از آنچه زمین می‌رویاند، از سبزی و خیار و سیر و عدس و پیازش برای ما بیرون آورد». (موسی) گفت: «آیا چیزی را که پست‌تر است به جای چیز بهتر جایگزین می‌کنید؟ به شهری فرود آیید، زیرا آنچه را خواستید در آنجا برای شما هست» و (مهر) ذلت و بی‌نوایی بر آنان زده شد، و سزاوار خشم الله گردیدند، چرا که آنان به آیات الله کفر می‌ورزیدند، و پیامبران را به ناحق می‌کشتند. این‌ها به خاطر آن بود که گناهکار و متجاوز بودند.');
INSERT INTO "hussein_taji" VALUES(2,62,'2:62','به‌درستی کسانی‌که ایمان آورده‌اند، و کسانی‌که یهودی شدند و نصاری و صابئان (= ستاره پرستان) آنان که به الله و روز قیامت ایمان آوردند و کارهای شایسته انجام دهند، پاداش‌شان نزد پروردگارشان است، و نه ترسی بر آنان خواهد بود و نه غمگین خواهند شد.');
INSERT INTO "hussein_taji" VALUES(2,63,'2:63','و (به یاد آورید) زمانی را که از شما پیمان گرفتیم، و (کوه) طور را بالای سر شما بر افراشتیم، (و به شما گفتیم): «آنچه را به شما داده‌ایم، با قدرت بگیرید، و آنچه را در آن است یاد کنید، شاید پرهیزگار شوید».');
INSERT INTO "hussein_taji" VALUES(2,64,'2:64','سپس شما بعد از این روی گرداندید، پس اگر فضل و رحمت الله بر شما نبود، از زیانکاران بودید.');
INSERT INTO "hussein_taji" VALUES(2,65,'2:65','بی‌گمان شما از حال کسانی از شما که در روز شنبه نافرمانی و تجاوز کردند، آگاه شده‌اید، پس ما به آن‌ها گفتیم: «بوزینه‌های خوار و رانده شده باشید».');
INSERT INTO "hussein_taji" VALUES(2,66,'2:66','پس ما این (کیفر) را عبرتی برای مردم آن زمان و نسل‌های بعد از آنان، و پند و اندرزی برای پرهیزگاران قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(2,67,'2:67','و (به یاد آورید) زمانی را که موسی به قوم خود گفت: «الله به شما دستور می‌دهد که گاوی را ذبح کنید» گفتند: «آیا ما را مسخره می‌کنی؟» (موسی) گفت: «به الله پناه می‌برم از اینکه از نادانان باشم».');
INSERT INTO "hussein_taji" VALUES(2,68,'2:68','گفتند: «از الله خود بخواه که برای ما روشن کند که آن چگونه (گاوی) است؟» (موسی) گفت: (الله) می‌فرماید: «آن گاوی است که نه پیر و نه جوان، بلکه میان سالی بین این دو باشد، پس آنچه به شما دستور داده شده است؛ انجام دهید».');
INSERT INTO "hussein_taji" VALUES(2,69,'2:69','گفتند: «از پروردگارت بخواه تا برای ما روشن سازد، رنگ آن چگونه است؟» (موسی) گفت: (الله) می‌فرماید: «آن گاو زرد پر رنگی است که بینندگان را شاد می‌کند».');
INSERT INTO "hussein_taji" VALUES(2,70,'2:70','گفتند: «از پروردگارت بخواه تا برای ما روشن سازد که آن چگونه (گاوی) است؟ براستی این گاو بر ما مشتبه شده، و اگر الله بخواهد ما هدایت خواهیم شد».');
INSERT INTO "hussein_taji" VALUES(2,71,'2:71','(موسی) گفت: (الله) می‌فرماید: «آن گاوی است که نه برای شخم زدن رام شده، و نه برای زراعت آبکشی کند، بی عیب است و یک رنگ و در آن خالی نیست» گفتند: «اینک سخن درست آوردی» پس گاو را سر بریدند، و نزدیک بود که این کار نکنند.');
INSERT INTO "hussein_taji" VALUES(2,72,'2:72','و (به یاد آورید) هنگامی را که فردی را کشتید، آنگاه دربارۀ او به نزاع پرداختید، و الله آشکار کننده آن چیزی است که پنهان می‌کردید.');
INSERT INTO "hussein_taji" VALUES(2,73,'2:73','پس گفتیم: «پاره‌ای از گاو را به مقتول بزنید، الله این گونه مردگان را زنده می‌کند، و نشانه‌های خود را به شما می‌نمایاند، شاید اندیشه کنید».');
INSERT INTO "hussein_taji" VALUES(2,74,'2:74','سپس بعد از این دل‌های شما سخت شد، همچون سنگ، یا سخت‌تر! زیرا که پاره‌ای از سنگ‌ها می‌شکافد، و از آن‌ها نهرها جاری می‌شود، و پاره‌ای از ترس الله فرو می‌افتد، و الله از آنچه می‌کنید؛ غافل نیست.');
INSERT INTO "hussein_taji" VALUES(2,75,'2:75','آیا امید دارید که (یهودیان) به (آیین) شما ایمان بیاورند، با اینکه گروهی از آنان سخنان الله (تورات) را می‌شنیدند و پس از فهمیدنش آن را تحریف می‌کردند، و حال آنکه علم و اطلاع داشتند؟!');
INSERT INTO "hussein_taji" VALUES(2,76,'2:76','و چون با مؤمنان ملاقات کنند؛ گویند: «ایمان آورده‌ایم» ولی هنگامی‌که با یکدیگر خلوت می‌کنند، می‌گویند: «چرا مطالبی را که الله برای شما بیان کرده، برای مسلمانان بازگو می‌کنید تا (روز قیامت) در پیشگاه پروردگارتان بر ضد شما به آن استدلال کنند؟ آیا نمی‌فهمید؟»');
INSERT INTO "hussein_taji" VALUES(2,77,'2:77','آیا نمی‌دانند که آنچه را پنهان می‌دارند، و آنچه را آشکار می‌کنند؛ الله می‌داند؟!');
INSERT INTO "hussein_taji" VALUES(2,78,'2:78','و برخی از آنان بی‌سوادانی هستند که کتاب الله (تورات) را جز یک مشت خیالات و آرزوها نمی‌دانند، و تنها به پندارهایشان دل بسته‌اند.');
INSERT INTO "hussein_taji" VALUES(2,79,'2:79','پس و ای بر کسانی‌که با دست خود می‌نویسند، سپس می‌گویند: «این از جانب الله است » تا آن را به بهای اندک بفروشند، پس و ای بر آن‌ها از آنچه دست‌هایشان نوشته، و وای بر آنان از آنچه از این راه به دست می‌آورند!');
INSERT INTO "hussein_taji" VALUES(2,80,'2:80','و گفتند: «آتش دوزخ؛ جز چند روز محدودی، هرگز به ما نخواهد رسید. بگو: «آیا از نزد الله پیمانی گرفته اید؟! که الله هرگز خلاف پیمانش نکند؟ یا چیزی را که نمی‌دانید به الله نسبت می‌دهید؟!»');
INSERT INTO "hussein_taji" VALUES(2,81,'2:81','آری هر کس که مرتکب گناه شود، و گناهانش او را احاطه کنند، آن‌ها اهل آتشند، و جاودانه در آن خواهند بود.');
INSERT INTO "hussein_taji" VALUES(2,82,'2:82','و کسانی‌که ایمان آورده و کارهای شایسته انجام داده‌اند، آنان اهل بهشتند، و جاودانه در آن خواهند ماند.');
INSERT INTO "hussein_taji" VALUES(2,83,'2:83','و (به یاد آورید) زمانی را که از بنی اسرائیل پیمان گرفتیم که جز الله یگانه را پرستش نکنید، و به پدر و مادر و نزدیکان و یتیمان و بی‌نوایان نیکی کنید، و به مردم (سخن) نیک بگویید، و نماز را بر پا دارید و زکات را بپردازید، سپس همۀ شما؛ جز عدۀ کمی پشت کردید و رویگردان شدید.');
INSERT INTO "hussein_taji" VALUES(2,84,'2:84','و (به یاد آورید) هنگامی را که از شما پیمان گرفتیم که خون های همدیگر را نریزید و یکدیگر را از سرزمین خود، بیرون نکنید. سپس شما اقرار کردید، و شما (بر آن پیمان) گواهی می‌دهید.');
INSERT INTO "hussein_taji" VALUES(2,85,'2:85','پس از آن این شما هستید که یکدیگر را می‌کشید و گروهی از خودتان را از سرزمین شان بیرون می‌کنید و علیه آن‌ها به گناه و ستم یکدیگر را کمک می‌کنید. و اگر (بعضی از آنان به صورت) اسیران نزد شما آیند، فدیه می‌دهید (و آنان را آزاد می‌کنید) و حال اینکه بیرون کردن آنان بر شما حرام بود. آیا به بخشی از (دستورات) کتاب (آسمانی) ایمان می‌آورید، و به بخشی کافر می‌شوید؟! پس جزای کسی از شما که چنین کند چیست، جز رسوایی و خواری در این جهان، و در روز قیامت به شدیدترین عذاب بر گردانیده می‌شوند، و الله از آنچه انجام می‌دهید غافل نیست.');
INSERT INTO "hussein_taji" VALUES(2,86,'2:86','اینان کسانی هستند که زندگانی دنیا را به بهای آخرت خریدند، پس عذاب آن‌ها تخفیف داده نمی‌شود، و کسی آن‌ها را یاری نخواهد کرد.');
INSERT INTO "hussein_taji" VALUES(2,87,'2:87','بدرستی که به موسی کتاب (تورات) دادیم، و بعد از او، پیامبرانی پشت سر هم فرستادیم، و به عیسی پسر مریم معجزه‌ها و دلایل روشن دادیم، و او را به وسیله روح القدس (= جبریل) تأیید کردیم، آیا هر زمان؛ پیامبری چیزی بر خلاف هوای نفس شما آورد، در برابر او تکبّر کردید، پس گروهی را تکذیب کرده و گروهی را کشتید.');
INSERT INTO "hussein_taji" VALUES(2,88,'2:88','و گفتند: «دل‌های ما در پرده است»، بلکه الله آنان را به خاطر کفر شان نفرین کرده است، پس اندکی ایمان می‌آورند.');
INSERT INTO "hussein_taji" VALUES(2,89,'2:89','و هنگامی‌که از طرف الله کتابی (= قرآن) برای آن‌ها آمد که تصدیق‌کننده چیزهائی بود که با خود داشتند، و پیش از این بر کافران خواستار پیروزی بودند، پس چون آن چه را که (از قبل) شناخته بودند، نزد آن‌ها آمد، به او کافر شدند، پس لعنت الله بر کافران باد.');
INSERT INTO "hussein_taji" VALUES(2,90,'2:90','چه بد است چیزی که خود را به بهای آن فروختند. و از روی حسد به آن چه الله فرستاده بود کافر شوند، که چرا الله از فضل خویش بر هر کس از بندگانش که بخواهد نازل می‌کند؟! پس سزاوار خشمی بر خشمی دیگر شدند، و برای کافران عذاب خوار کننده است.');
INSERT INTO "hussein_taji" VALUES(2,91,'2:91','و چون به آن‌ها گفته شود: «به آنچه الله نازل کرده، ایمان بیاورید» می‌گویند: «ما به چیزی ایمان می‌آوریم که بر خود ما نازل شده است» و به غیر آن، کافر می‌شوند، در حالی‌که آن حق است و تصدیق‌کننده چیزی است که با خود دارند. بگو: «اگر (راست می‌گویید) و مؤمنید پس چرا پیامبران الله را پیش از این می‌کشتید؟!».');
INSERT INTO "hussein_taji" VALUES(2,92,'2:92','و به راستی موسی معجزه‌ها و نشانه‌های روشنی برای شما آورد، و آنگاه شما پس از وی، گوساله را به پرستش گرفتید، در حالی‌که ستمگر بودید.');
INSERT INTO "hussein_taji" VALUES(2,93,'2:93','و (به یاد آورید) هنگامی را که از شما پیمان گرفتیم، و (کوه) طور را بالای سر شما برافراشتیم، (و گفتیم): «آنچه را به شما داده‌ایم محکم بگیرید و بشنوید». گفتند: «شنیدیم و نا فرمانی کردیم» و دل‌های شان بر اثر کفرشان، با (محبت) گوساله آمیخته شد. بگو: «ایمان‌تان چه فرمان بدی به شما می‌دهد، اگر شما مؤمن هستید!».');
INSERT INTO "hussein_taji" VALUES(2,94,'2:94','بگو: «اگر سرای آخرت در نزد خدا، مخصوص شماست، نه سایر مردم، پس آرزوی مرگ کنید؛ اگر راستگوئید».');
INSERT INTO "hussein_taji" VALUES(2,95,'2:95','ولی آن‌ها، بخاطر آنچه دست‌هایشان پیش فرستاده است، هرگز آرزوی مرگ نخواهند کرد، و الله به ستمگران داناست.');
INSERT INTO "hussein_taji" VALUES(2,96,'2:96','و هر آینه آنان را حریص ترین مردم - (حتی) حریص تر از مشرکان - بر زندگی خواهی یافت، هر یک از آنان دوست دارد هزار سال عمر کند، در حالی‌که اگر این عمر (دراز)، به او داده شود، او را از عذاب دور نمی‌کند، و الله به آنچه می‌کنند بیناست.');
INSERT INTO "hussein_taji" VALUES(2,97,'2:97','بگو: «کسی‌که دشمن جبرئیل باشد، (در حقیقت دشمن الله است) زیرا که او به فرمان الله قرآن را بر قلب تو نازل کرده است، در حالی‌که تصدیق‌کننده آنچه پیش از آن است، و برای مؤمنان راهنما و مژده است».');
INSERT INTO "hussein_taji" VALUES(2,98,'2:98','کسی‌که دشمن الله و فرشتگان و پیامبران او، و جبرئیل و میکائیل باشد، پس بدرستی که الله دشمن کافران است.');
INSERT INTO "hussein_taji" VALUES(2,99,'2:99','و هر آینه که ما نشانه‌های روشنی بر تو نازل کردیم، و جز فاسقان کسی به آن‌ها کفر نمی‌ورزد.');
INSERT INTO "hussein_taji" VALUES(2,100,'2:100','آیا (چنین نیست) هر گاه آن‌ها پیمانی بستند گروهی از آنان آن را دور افکندند؟! بلکه بیشترشان ایمان نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(2,101,'2:101','و هنگامی‌که فرستاده‌ای از جانب الله به سوی‌شان آمد، که تصدیق‌کننده آنچه با خود داشتند است، گروهی از آنان که به آن‌ها کتاب داده شده بود، کتاب الله را پشت سرشان افکندند، گویی آنان نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(2,102,'2:102','و از آنچه شیاطین در (عهد) فرمانروایی سلیمان (بر مردم) می‌خواندند پیروی کردند، و درحالیکه سلیمان (هرگز دست به سحر نیالود) و کافر نشد، و لیکن شیاطین کفر ورزیدند، به مردم سحر آموختند. و (نیز)از آنچه بر دو فرشته «هاروت» و «ماروت» در بابل نازل شده بود (پیروی کردند). و (آن دو فرشته) به هیچ کس چیزی یاد نمی‌دادند، مگر اینکه (از پیش به او) می‌گفتند: «ما وسیله آزمایشیم، پس کافر نشو» پس از آن دو (فرشته) چیزهایی می‌آموختند که به وسیلۀ آن، میان مرد و همسرش جدایی بیفکنند، و حال آنکه بدون اجازه الله نمی‌توانند به وسیلۀ آن به کسی زیانی برسانند. و چیزی می‌آموختند که به آنان زیان می‌رسانید، و به آنان سودی نمی‌داد. و قطعاً می‌دانستند که هرکس خریدار آن باشد، در آخرت بهره‌ای نخواهد داشت. و چه بد و زشت است آنچه خود را به آن فروختند، اگر می‌دانستند!.');
INSERT INTO "hussein_taji" VALUES(2,103,'2:103','و اگر آن‌ها ایمان می‌آوردند، و پرهیزگاری می‌کردند، همانا پاداشی از نزد الله بهتر بود، اگر می‌دانستند!.');
INSERT INTO "hussein_taji" VALUES(2,104,'2:104','ای کسانی‌که ایمان آورده‌اید (در گفتگو با پیامبر صلی الله علیه وسلم) نگویید: «راعنا» (یعنی رعایتمان کن) بلکه بگویید: «اُنْظُرْنا» (یعنی: به ما بنگر، ملاحظه ما کن، زیرا که کلمه اول دو معنی دارد که معنای دیگر آن، یعنی: حماقت، و یهود هدف‌شان این معنی بود). و بشنوید، و برای کافران عذاب دردناکی است.');
INSERT INTO "hussein_taji" VALUES(2,105,'2:105','کافران از اهل کتاب و مشرکان دوست ندارند که از جانب پروردگارتان خیر و برکتی بر شما نازل گردد، در حالی‌که الله رحمت خود را به هر کس بخواهد اختصاص می‌دهد، و الله دارای فضل بزرگ است.');
INSERT INTO "hussein_taji" VALUES(2,106,'2:106','هر آیه‌ای را نسخ کنیم، یا (از دل مردم بزدائیم) و فراموشش گردانیم، بهتر از آن یا همانند آن را می‌آوریم، آیا نمی‌دانی که الله بر هر چیز تواناست؟');
INSERT INTO "hussein_taji" VALUES(2,107,'2:107','آیا نمی‌دانی که فرمانروایی آسمان‌ها و زمین، از آن الله است، و جز الله سرپرست و یاوری برای شما نیست.');
INSERT INTO "hussein_taji" VALUES(2,108,'2:108','آیا می‌خواهید از پیامبر خود، (همان چیزی را) در خواست کنید که پیش از این از موسی خواسته شد؟ و کسی‌که کفر را به (جای) ایمان برگزیند، مسلماً راه راست را گم کرده است.');
INSERT INTO "hussein_taji" VALUES(2,109,'2:109','بسیاری از اهل کتاب، از روی حسدی که در وجودشان است، آرزو دارند که شما را بعد از ایمان‌تان به حال کفر باز گردانند، پس از اینکه حق برای آن‌ها روشن شده است، پس شما عفو کنید و در گذرید، تا الله فرمان خویش را (برای جهاد با آن‌ها) صادر نماید، همانا الله بر هر چیزی تواناست.');
INSERT INTO "hussein_taji" VALUES(2,110,'2:110','و نماز را بر پا دارید و زکات را ادا کنید، و هر کار نیکی را که برای خود از پیش می‌فرستید، آن را نزد الله خواهید یافت، یقیناً الله به آنچه می‌کنید بینا است.');
INSERT INTO "hussein_taji" VALUES(2,111,'2:111','و گفتند: «هرگز داخل بهشت نخواهد شد، جز کسی‌که یهودی یا مسیحی باشد، این آرزو‌های (باطل) آن‌هاست. بگو: «اگر راستگویید دلیل خود را بیاورید!».');
INSERT INTO "hussein_taji" VALUES(2,112,'2:112','آری، کسی‌که مخلصانه روی خود را تسلیم الله کند، و نیکوکار باشد، پس پاداش و ی نزد پروردگارش (محفوظ) است، و نه ترسی بر آن‌هاست و نه اندوهگین می‌شوند.');
INSERT INTO "hussein_taji" VALUES(2,113,'2:113','یهودیان گفتند: «مسیحیان بر چیزی نیستند» و مسیحیان (نیز) گفتند: «یهودیان بر چیزی نیستند» در حالی‌که آنان کتاب (آسمانی) را می‌خوانند. همچنین کسانی‌که نمی‌دانند (سخنی) همانند آنان گفتند. پس الله روز قیامت، در آنچه که در آن اختلاف داشتند میان آنان داوری می‌کند.');
INSERT INTO "hussein_taji" VALUES(2,114,'2:114','و کیست ستمکارتر از آن کس که مسجد‌های الله را باز داشت که در آن نام الله برده شود، و در ویرانی آن‌ها کوشید؟! شایسته آنان نیست، جز به حال ترس وارد این مساجد شوند، بهره آنان در دنیا خواری و رسوائی، و در آخرت عذاب بزرگی است.');
INSERT INTO "hussein_taji" VALUES(2,115,'2:115','مشرق و مغرب از آن الله است، پس به هر سو رو کنید، روی الله آنجاست. همانا الله گشایشگر داناست.');
INSERT INTO "hussein_taji" VALUES(2,116,'2:116','و (یهود و نصاری و مشرکان) گفتند: «الله فرزندی (برای خود) بر گزیده است» - منزّه است او- بلکه آنچه در آسمان‌ها و زمین است، از آن اوست، (و)همه فرمانبردار اویند.');
INSERT INTO "hussein_taji" VALUES(2,117,'2:117','پدید آورنده آسمان‌ها و زمین است، و چون به کاری اراده فرماید، تنها به آن می‌گوید: «باش»، پس می‌شود.');
INSERT INTO "hussein_taji" VALUES(2,118,'2:118','و آنانکه نمی‌دانند گفتند: «چرا الله با ما سخن نمی‌گوید؟! و یا (چرا) معجزه و نشانه‌ای برای ما نمی‌آید؟!» کسانی‌که پیش از آنان بودند نیز همین گونه سخن می‌گفتند، دل‌های شان به یکدیگر همانند است. همانا ما آیات و نشانه‌ها را برای گروهی که یقین دارند، آشکار و روشن ساخته ایم.');
INSERT INTO "hussein_taji" VALUES(2,119,'2:119','همانا ما تو را به حق، مژده رسان و بیم‌دهنده فرستادیم. و دربارۀ دوزخیان از تو پرسیده نخواهد شد.');
INSERT INTO "hussein_taji" VALUES(2,120,'2:120','و هرگز یهود و نصاری از تو خشنود نخواهند شد، تا اینکه از آیین آنان، پیروی کنی. بگو: «همانا هدایت الله، تنها هدایت است»، و اگر از خواسته‌ها و آرزوهای آنان پیروی کنی، بعد از اینکه دانشی به تو رسیده است، هیچ سر پرست و یاوری از سوی الله برای تو نخواهد بود.');
INSERT INTO "hussein_taji" VALUES(2,121,'2:121','کسانی‌که کتاب آسمانی، (تورات) به آن‌ها داده‌ایم، آن را چنان که شایستۀ آن است می‌خوانند، آن‌ها به آن (هدایت) ایمان می‌آورند، و کسانی‌که به او کافر شوند پس بی‌گمان ایشان زیانکارانند.');
INSERT INTO "hussein_taji" VALUES(2,122,'2:122','ای بنی اسرائیل! نعمت مرا، که بر شما ارزانی داشتم به یاد آورید، و (نیز) آنکه من شما را بر جهانیان (معاصرتان) برتری بخشیدم.');
INSERT INTO "hussein_taji" VALUES(2,123,'2:123','و از روزی بترسید که کسی از کسی هیچ کفایتی نکند و هیچ گونه تاوان و فدیه از او پذیرفته نمی‌شود، و شفاعت او را سود نمی‌دهد، و یاری نمی‌شوند.');
INSERT INTO "hussein_taji" VALUES(2,124,'2:124','و (به یاد آورید) هنگامی‌که الله، ابراهیم را با سخنانی (مشتمل بر اوامر و نواهی و تکالیف) آزمود، پس او همه را بخوبی به انجام رسانید. (الله به او) فرمود: «بدرستی که من تو را پیشوای مردم قرار می‌دهم». (ابراهیم) گفت: «و از فرزندانم (نیز پیشوایانی قرار بده)». (الله) فرمود: «پیمان و عهد من به ستمکاران نمی‌رسد».');
INSERT INTO "hussein_taji" VALUES(2,125,'2:125','و (به یاد آورید) هنگامی‌که خانۀ (کعبه) را محل بازگشت، و (جای) امنی برای مردم قرار دادیم. و از مقام ابراهیم نماز گاهی برای خود برگیرید. و ما به ابراهیم و اسماعیل وحی کردیم که: «خانه مرا برای طواف کنندگان و مقیمان و رکوع و سجودکنندگان (= نماز گزاران) پاک و پاکیزه کنید».');
INSERT INTO "hussein_taji" VALUES(2,126,'2:126','و (به یاد آورید) هنگامی را که ابراهیم گفت: «پروردگارا! این (سر زمین) را شهری امن بگردان، و اهل آن را - کسانی‌که از ایشان به الله و روز قیامت، ایمان آورده‌اند- از میوه‌های (گوناگون) روزی ده». (الله) فرمود: «و هر کس که کافر شود، اندکی بهره‌مندش می‌سازم، سپس او را به عذاب آتش می‌کشانم، و بد سرانجامی است!».');
INSERT INTO "hussein_taji" VALUES(2,127,'2:127','و (به یاد آورید) هنگامی را که ابراهیم و اسماعیل پایه‌های خانه (کعبه) را بالا می‌بردند، (و می‌گفتند): «پروردگارا! از ما بپذیر، همانا که تویی شنوای دانا.');
INSERT INTO "hussein_taji" VALUES(2,128,'2:128','پروردگارا! ما را فرمانبردار خودت قرار ده، و از فرزندان‌مان امتی فرمانبردار خود (پدید آور) و طرز عبادتمان را به ما نشان ده، و توبه ما را بپذیر، بدرستی که تو توبه‌پذیر مهربانی.');
INSERT INTO "hussein_taji" VALUES(2,129,'2:129','پروردگارا! در میان آن‌ها پیامبری از خودشان برانگیز، تا آیات تو را بر آنان بخواند، و آن‌ها را کتاب و حکمت بیاموزد، و آنان را پاکیزه کند، همانا که تو پیروزمند حکیمی».');
INSERT INTO "hussein_taji" VALUES(2,130,'2:130','و چه کسی از آیین ابراهیم روی گردان خواهد شد؟! جز کسی‌که خود را به نادانی و سبکسری افکند، و بدرستی که ما او را در دنیا برگزیدیم، و همانا او در آخرت از شایستگان است.');
INSERT INTO "hussein_taji" VALUES(2,131,'2:131','آنگاه که پروردگارش به او فرمود: «تسلیم شو» گفت: «برای پروردگار جهانیان تسلیم شدم».');
INSERT INTO "hussein_taji" VALUES(2,132,'2:132','و ابراهیم و یعقوب، فرزندان خود را به این آیین سفارش کردند، (و هر کدام به فرزندان خویش گفتند): «ای فرزندان من! الله این آیین را برای شما برگزیده است، پس نمیرید جز اینکه مسلمان باشید».');
INSERT INTO "hussein_taji" VALUES(2,133,'2:133','آیا هنگامی‌که مرگ یعقوب فرا رسید، شما حاضر بودید؟ آنگاه که به فرزندان خود گفت: «پس از من چه چیز را می‌پرستید؟» گفتند: معبود تو، و معبود پدرانت، ابراهیم و اسماعیل و اسحاق، الله یکتا را، می‌پرستیم، و ما تسلیم او هستیم».');
INSERT INTO "hussein_taji" VALUES(2,134,'2:134','آن‌ها امتی بودند که در گذشتند، برای آن‌ها است آنچه که کسب کرده‌اند، و برای شماست آنچه کسب کرده‌اید. و از آنچه (آنان) می‌کردند، شما پرسیده نمی‌شوید.');
INSERT INTO "hussein_taji" VALUES(2,135,'2:135','و (اهل کتاب) گفتند: «یهودی یا مسیحی شوید، تا راه یابید». بگو: «بلکه از آیین ابراهیم که یکتا پرست بود و هرگز از مشرکان نبود (پیروی می‌کنیم)».');
INSERT INTO "hussein_taji" VALUES(2,136,'2:136','بگویید: «ما به الله ایمان آوردیم، و به آنچه بر ما نازل شده، و آنچه بر ابراهیم و اسماعیل، و اسحاق و یعقوب و نوادگان یعقوب نازل گردید، و به آنچه که به موسی و عیسی داده شده، و به آنچه که پیامبران (دیگر) از طرف پروردگارشان داده شده است، و در میان هیچ یک از آن‌ها جدایی نمی‌اندازیم و ما تسلیم الله هستیم».');
INSERT INTO "hussein_taji" VALUES(2,137,'2:137','پس اگر آن‌ها (نیز) به مانند آنچه شما ایمان آورده‌اید، ایمان بیاورند، قطعاً هدایت یافته‌اند. و اگر پشت کنند، پس جز این نیست که آنان در ستیزند، پس بزودی الله تو را از (شر) آن‌ها کفایت کند، و او ست شنوای دانا.');
INSERT INTO "hussein_taji" VALUES(2,138,'2:138','رنگ الهی (را بپذیرید) و چه کس در رنگ کردن از الله نیکوتر است؟! و ما تنها او را می‌پرستیم.');
INSERT INTO "hussein_taji" VALUES(2,139,'2:139','بگو: «آیا در (باره) الله با ما مجادله می‌کنید؟! در حالی‌که او، پروردگار ما و شماست، و کردار ما از آن ما، و کردار شما از آن شماست، و ما او را با اخلاص پرستش می‌کنیم».');
INSERT INTO "hussein_taji" VALUES(2,140,'2:140','یا می‌گویید: «که همانا ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط (= نوادگان یعقوب) یهودی یا نصرانی بودند؟!» بگو: «شما داناترید یا خدا؟!» و چه کسی ستمکارتر است از آن کسی‌که گواهی و شهادتی را که از (جانب) الله نزد اوست، کتمان می‌کند؟! و الله از آنچه می‌کنید غافل نیست.');
INSERT INTO "hussein_taji" VALUES(2,141,'2:141','(به هر حال) آن‌ها امتی بودند که در گذشتند، برای ایشان است آنچه بدست آورده‌اند، و برای شماست آنچه به دست آورده‌اید، و از آنچه آنان می‌کرده‌اند شما پرسیده نمی‌شوید.');
INSERT INTO "hussein_taji" VALUES(2,142,'2:142','بزودی بی‌خردان از مردم می‌گویند: «چه چیز آن‌ها را از قبله شان که بر آن بودند باز گردانید؟» بگو: مشرق و مغرب از آن الله است هر کس را بخواهد به راه راست هدایت می‌کند.');
INSERT INTO "hussein_taji" VALUES(2,143,'2:143','و همچنین شما را امت میانه قرار دادیم، تا بر مردم گواه باشید، و پیامبر هم بر شما گواه باشد، و ما آن قبله‌ای را که بر آن بودی (قبله) قرار دادیم. مگر برای اینکه بدانیم کسی را که از پیامبر پیروی می‌نماید از کسی‌که بر پاشنه‌های خود به عقب باز می‌گردد. و اگر چه این (حکم) جز بر کسانی‌که الله آن‌ها را هدایت کرده دشوار است. و الله هرگز ایمان (= نماز) شما را ضایع نمی‌گرداند، همانا الله نسبت به مردم رؤوف مهربان است.');
INSERT INTO "hussein_taji" VALUES(2,144,'2:144','به تحقیق ما گرداندن روی تو را به (سوی) آسمان می‌بینیم، اکنون تو را به (سوی) قبله‌ای که به آن خشنود شوی باز می‌گردانیم، پس روی خود را به سوی مسجد الحرام کن، و هر جا باشید، روی خود را به سوی آن بگردانید، و کسانی‌که کتاب (آسمانی) به آن‌ها داده شد، بخوبی می‌دانند این (تغییر قبله) حق است (و) از جانب پروردگارشان می‌باشد، و الله از آنچه می‌کنند؛ غافل نیست.');
INSERT INTO "hussein_taji" VALUES(2,145,'2:145','و اگر برای آنان که کتاب بدیشان داده شده، هر گونه نشانه و (حجتی) بیاوری، از قبله تو پیروی نمی‌کنند، و تو (نیز) از قبله آنان پیروی نخواهی کرد، و برخی از آنان هم قبله برخی دیگر پیروی نمی‌کنند. و اگر بعد از آنکه علم و دانش به تو رسیده است از خواسته‌ها و آرزوهای آنان پیروی کنی، در این صورت مسلماً از ستمکاران خواهی بود.');
INSERT INTO "hussein_taji" VALUES(2,146,'2:146','کسانی‌که به آنان کتاب (آسمانی) داده‌ایم او (پیامبر صلی الله علیه وسلم) را می‌شناسند، چنانکه فرزندان خود را می‌شناسند، و همانا گروهی از آنان در حالی‌که می‌دانند حق را کتمان می‌کنند.');
INSERT INTO "hussein_taji" VALUES(2,147,'2:147','حق از جانب پروردگار تو است، پس هرگز از شک کنندگان مباش.');
INSERT INTO "hussein_taji" VALUES(2,148,'2:148','و برای هر یک جهتی است که (هنگام نماز) بدان رو می‌کند پس در نیکی‌ها بر یکدیگر سبقت بگیرید. هر جا که باشید، الله همه شما را حاضر می‌کند، همانا الله بر هر چیزی تواناست.');
INSERT INTO "hussein_taji" VALUES(2,149,'2:149','و از هر جا که بیرون شدی، پس (به هنگام نماز) روی خود را بسوی مسجد الحرام کن. و بدرستی که این (رو کردنت) حقی از جانب پروردگارت است و الله از آنچه می‌کنید غافل نیست.');
INSERT INTO "hussein_taji" VALUES(2,150,'2:150','و از هر جا که بیرون شدی پس روی خود را بسوی مسجد الحرام کن، و (ای مؤمنان) هر جا که بودید روی خود را به سوی آن کنید، تا مردم؛- جز کسانی از ایشان که ستم کردند - بر شما حجتی نداشته باشند. پس از آن‌ها نترسید، و از من بترسید، و تا نعمت خود را بر شما تمام کنم، و باشد که هدایت شوید.');
INSERT INTO "hussein_taji" VALUES(2,151,'2:151','چنانکه پیامبری از خودتان در میان شما فرستادیم، که آیات ما را بر شما می‌خواند، و شما را پاک می‌گرداند، و به شما کتاب (قرآن) و حکمت می‌آموزد، و آنچه نمی‌دانستید به شما یاد می‌دهد.');
INSERT INTO "hussein_taji" VALUES(2,152,'2:152','پس مرا یاد کنید تا شما را یاد کنم و سپاس مرا گویید و ناسپاسی من نکنید.');
INSERT INTO "hussein_taji" VALUES(2,153,'2:153','ای کسانی‌که ایمان آورده‌اید، از شکیبایی و نماز یاری بجویید، که همانا الله با شکیبایان است.');
INSERT INTO "hussein_taji" VALUES(2,154,'2:154','و به آن‌ها که در راه الله کشته می‌شوند، مرده نگویید، بلکه آنان زنده‌اند، ولی شما نمی‌فهمید.');
INSERT INTO "hussein_taji" VALUES(2,155,'2:155','و قطعاً شما را با چیزی از ترس و گرسنگی و کاهش مال‌ها و جان‌ها و میوه‌ها آزمایش می‌کنیم. و مژده بده به صبر کنندگان.');
INSERT INTO "hussein_taji" VALUES(2,156,'2:156','آن‌ها که هرگاه مصیبتی به ایشان برسد می‌گویند: ما از آنِ الله هستیم، و به سوی او باز می‌گردیم.');
INSERT INTO "hussein_taji" VALUES(2,157,'2:157','این‌ها هستند که درودها و رحمتی از پروردگارشان بر ایشان است و اینانند هدایت یافتگان.');
INSERT INTO "hussein_taji" VALUES(2,158,'2:158','همانا «صفا» و «مروه» از شعائر (نشانه‌های) الله است، پس هر که حج خانه (خدا) کند و یا عمره انجام دهد، بر او گناهی نیست که میان آن دو طواف (سعی) کند، و کسی‌که به رغبت خود کار نیکی را انجام دهد پس بدرستی که الله سپاسگزار داناست.');
INSERT INTO "hussein_taji" VALUES(2,159,'2:159','همانا کسانی‌که آنچه را که از دلائل روشن و هدایت نازل کرده‌ایم؛ بعد از آنکه آن را در کتاب (تورات) برای مردم بیان نمودیم، کتمان می‌کنند، الله آن‌ها را لعنت می‌کند، و لعنت‌کنندگان (از مؤمنان و فرشتگان نیز) آن‌ها را لعنت می‌کنند.');
INSERT INTO "hussein_taji" VALUES(2,160,'2:160','مگر کسانی‌که توبه کردند و (اعمال خود را) اصلاح نمودند (و آنچه را کتمان کرده بودند) آشکار ساختند، پس اینانند که توبه ایشان را می‌پذیرم، و من توبه‌پذیر مهربانم.');
INSERT INTO "hussein_taji" VALUES(2,161,'2:161','همانا کسانی‌که کافر شدند، و در حالی‌که کافر بودند مردند، لعنت الله و فرشتگان و همه مردم بر آن‌ها خواهد بود.');
INSERT INTO "hussein_taji" VALUES(2,162,'2:162','در آن (لعنت) جاودانه باقی می‌مانند، و نه عذاب آنان تخفیف داده می‌شود، و نه ایشان مهلت داده می‌شوند.');
INSERT INTO "hussein_taji" VALUES(2,163,'2:163','و الله شما الله یگانه است که غیر از او معبودی نیست؛ بخشندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(2,164,'2:164','همانا در آفرینش آسمان‌ها و زمین و آمد و شد شب و روز، و کشتی‌هایی که در دریا روانند با آنچه به مردم سود می‌رساند و آبی که الله از آسمان نازل کرده که با آن زمین را پس از مردنش زنده نموده، و انواع جنبندگان را در آن پراکنده کرده و (در) تغییر مسیر بادها و ابرهایی که در میان زمین و آسمان‌ها مسخرند؛ بی‌گمان نشانه‌هایی است برای مردمی که تعقل می‌کند و می‌اندیشند.');
INSERT INTO "hussein_taji" VALUES(2,165,'2:165','و از مردم کسانی هستند غیر از الله همتایانی را انتخاب می‌کنند، و آن‌ها را مانند دوست داشتن الله دوست می‌دارند، و کسانی‌که ایمان آورده‌اند الله را بیشتر دوست می‌دارند، و کسانی‌که ستم کردند اگر می‌دیدند هنگامی‌که عذاب (روز قیامت) را مشاهده می‌کنند (خواهند دانست) که تمام قدرت از آنِ الله است و اینکه الله سخت‌کیفر است.');
INSERT INTO "hussein_taji" VALUES(2,166,'2:166','هنگامی‌که رهبران (گمراه کننده) از پیروان خود، بیزاری می‌جویند و عذاب را مشاهده می‌کنند، و روابط و پیوندشان گسیخته شود.');
INSERT INTO "hussein_taji" VALUES(2,167,'2:167','و (در این هنگام) پیروان می‌گویند: «کاش برای ما بازگشتی (به دنیا) بود، تا از آن‌ها بیزاری جوییم؛ همان گونه که آنان از ما بیزاری جستند، این چنین الله کردارهایشان را به صورت حسرت زایی به آنان نشان می‌دهد، و هرگز از آتش (دوزخ) بیرون نخواهند آمد.');
INSERT INTO "hussein_taji" VALUES(2,168,'2:168','ای مردم از آنچه در زمین است؛ حلال و پاکیزه را بخورید، و از گام‌های شیطان پیروی نکنید، چرا که او دشمن آشکار شماست.');
INSERT INTO "hussein_taji" VALUES(2,169,'2:169','(او) شما را فقط به بدی و زشتی فرمان می‌دهد و اینکه به الله نسبت دهید آنچه را که نمی‌دانید.');
INSERT INTO "hussein_taji" VALUES(2,170,'2:170','و هنگامی‌که به آن‌ها گفته شود: از آنچه الله نازل کرده است پیروی کنید می‌گویند: (نه) بلکه ما از آنچه پدران خود را بر آن یافتیم پیروی می‌کنیم. آیا اگر پدران آن‌ها چیزی نمی‌فهمیدند و هدایت نیافتند (باز هم کورکورانه از آن‌ها پیروی می‌کنند؟)');
INSERT INTO "hussein_taji" VALUES(2,171,'2:171','مثل (دعوت‌کنندۀ) کسانی‌که کافر شدند، همچون مثل کسی است که چیزی را که جز صدا و ندایی نمی‌شنود، بانگ می‌زند، (این کافران) کر، گنگ، و نابینا هستند، و از این رو نمی‌اندیشند.');
INSERT INTO "hussein_taji" VALUES(2,172,'2:172','ای کسانی‌که ایمان آورده‌اید! از چیزهای پاکیزه‌ای که روزی شما کرده‌ایم بخورید و شکر الله را به جای آورید اگر تنها او را می‌پرستید.');
INSERT INTO "hussein_taji" VALUES(2,173,'2:173','(الله) تنها مردار و خون و گوشت خوک و آنچه را که (هنگام ذبح) نام غیر الله بر آن برده شده، بر شما حرام کرده است. و اگر کسی (برای حفظ جان خود به خوردن آن‌ها) ناچار شود، در صورتی که ستمگر و متجاوز نباشد، پس گناهی بر او نیست. همانا الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(2,174,'2:174','به راستی کسانی‌که کتمان می‌کنند آنچه را الله از کتاب (آسمانی) نازل کرده و آن را به بهای اندکی می‌فروشند، آن‌ها جز آتش چیزی نمی‌خوردند، و الله روز قیامت با آن‌ها سخن نمی‌گوید و آنان را پاکیزه نمی‌کند، برای آن‌ها عذاب دردناکی است.');
INSERT INTO "hussein_taji" VALUES(2,175,'2:175','اینان کسانی هستند که گمراهی را به (بهای) هدایت و عذاب را به (بهای) آمرزش خریدند، پس بر آتش (دوزخ) چه شکیبا هستند!');
INSERT INTO "hussein_taji" VALUES(2,176,'2:176','این بخاطر آن است که الله کتاب (آسمانی) را به حق نازل کرده است، و کسانی‌که در آن اختلاف کردند در ستیزی دور (از حق و حقیقت) هستند.');
INSERT INTO "hussein_taji" VALUES(2,177,'2:177','نیکی این نیست که روی خود را به سوی مشرق و مغرب کنید، بلکه نیکی (و نیکوکار)کسی است که به الله و روز قیامت و فرشتگان و کتاب (آسمانی) و پیامبران، ایمان آورده باشد، و مال (خود) را با وجود دوست داشتنش به خویشاوندان و یتیمان و مسکینان و واماندگان در راه و گدایان و در (راه آزادی) بردگان بدهد، و نماز را بر پای دارد و زکات را بدهد، و وفاکنندگان به پیمانشان چون پیمان بندند و (کسانی که) در فقر و سختی و زیان و بیماری و به هنگام نبرد؛ شکیبایند. این‌ها کسانی هستند که راست گفتند، و آن‌ها پرهیزگارانند.');
INSERT INTO "hussein_taji" VALUES(2,178,'2:178','ای کسانی‌که ایمان آورده‌اید! در (مورد) کشتگان (حکم) قصاص بر شما نوشته شده است، آزاد به آزاد، و برده به برده، و زن به زن (کشته می‌شوند). پس اگر کسی از سوی برادر (دینی)اش (یعنی ولی مقتول) چیزی به او بخشیده شد، (و حکم قصاص تبدیل به خون بهاء گردید) باید (ولی مقتول) از راه پسندیده پیروی کند و (قاتل نیز) به نیکی (دیه) به او بپردازد، این (حکم) تخفیف و رحمتی است از سوی پروردگارتان پس اگر کسی بعد از آن (گذشت و عفو) تجاوز کند؛ او را عذاب دردناکی است.');
INSERT INTO "hussein_taji" VALUES(2,179,'2:179','و ای صاحبان خرد! برای شما در قصاص حیات و زندگی است، باشد که شما تقوا پیشه کنید.');
INSERT INTO "hussein_taji" VALUES(2,180,'2:180','بر شما نوشته شده هنگامی‌که یکی شما را مرگ فرا رسد، اگر مالی از خود به جای گذارده، برای پدر و مادر و خویشاوندان به نیکی وصیت کند. این حقی است بر پرهیزگاران.');
INSERT INTO "hussein_taji" VALUES(2,181,'2:181','پس هر کس که بعد از شنیدنش آن را تغییر دهد، بدرستی که گناهش بر کسانی است که آن (وصیت) را تغییر می‌دهند، همانا الله شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(2,182,'2:182','پس کسی‌که از بی عدالتی (و میل بی جای) وصیت‌کننده (نسبت به ورثه) یا از گناه او (در وصیت به کار خلاف) بترسد، آنگاه میان آن‌ها را اصلاح نمود، پس گناهی بر او نیست بدرستی که الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(2,183,'2:183','ای کسانی‌که ایمان آورده‌اید! روزه بر شما فرض شده است همان گونه که بر کسی‌که پیش از شما بودند؛ فرض شده بود، تا پرهیزگار شوید.');
INSERT INTO "hussein_taji" VALUES(2,184,'2:184','(روزه) در روزهایی معدود و اندکی است، پس هر کس از شما که بیمار یا مسافر باشد، (به‌اندازه آن روزهای که افطار کرده) تعدادی از روزهای دیگر (روزه بگیرید). و بر کسانی‌که توانائی آن را ندارند؛ لازم است کفاره بدهند، (و آن) خوراک دادن به مسکینی است، و هر کس که رغبت خود (بیشتر) نیکی انجام دهد پس آن برای او بهتر است، و اگر بدانید روزه گرفتن برای شما بهتر است.');
INSERT INTO "hussein_taji" VALUES(2,185,'2:185','(آن چند روز محدود و اندک) ماه رمضان است (ماهی) که قرآن در آن نازل شده است، (کتابی) که راهنمای مردم است، و (در بردارنده) نشانه‌ها و دلایل آشکار و روشن از هدایت و جدا کننده حق از باطل است. پس هر کس از شما این ماه را (در حضر) دریابد، باید که آن را روزه بدارد، و هر کس که بیمار یا در سفر بود، پس (باید به تعداد روزهای که افطار کرده) روزهای دیگر را (روزه بگیرید). الله برای شما آسانی می‌خواهد، و برای شما دشواری نمی‌خواهد، و تا شمار (روزهای رمضان) را کامل کنید، و الله را بر اینکه شما را هدایت کرده، بزرگ بدارید، و باشد که شکر گزاری کنید.');
INSERT INTO "hussein_taji" VALUES(2,186,'2:186','و چون بندگانم، از تو درباره من بپرسند، بگو: به راستی که من نزدیکم، دعای دعاکننده را هنگامی‌که مرا بخواند؛ اجابت می‌کنم. پس (آنان) باید دعوت مرا بپذیرند، و به من ایمان بیاورند، باشد که راه‌یابند.');
INSERT INTO "hussein_taji" VALUES(2,187,'2:187','آمیزش و نزدیکی با همسرانتان در شبِ روز بر شما حلال گردیده است، آنان لباس شما هستند و شما لباس آن‌ها، الله می‌دانست که شما به خودتان خیانت می‌کردید، پس توبۀ شما را پذیرفت و شما را بخشید، پس اکنون (در شب‌های رمضان می‌توانید) با آنان آمیزش کنید، و آنچه را الله برای شما مقرر داشته طلب کنید. و بخورید و بیاشامید، تا رشتۀ سپید صبح از رشتۀ سیاه (شب) برای شما آشکار شود، سپس روزه را تا شب به پایان رسانید، و در حالی‌که در مساجد معتکف هستید با زنان آمیزش نکنید، این مرزهای الهی است پس به آن نزدیک نشوید. الله این چنین آیات خود را برای مردم روشن می‌سازد؛ باشد که پرهیزگار شوند.');
INSERT INTO "hussein_taji" VALUES(2,188,'2:188','و اموال‌تان را در بین خود به باطل (و ناحق) نخورید، و (به عنوان رشوه قسمتی از) آن را به حاکمان (و قضات) ندهید؛ تا بخشی از اموال مردم را به گناه بخورید، در حالی‌که شما می‌دانید (این کار گناه است.)');
INSERT INTO "hussein_taji" VALUES(2,189,'2:189','درباره هلال‌های (ماه) از تو سؤال می‌کنند، بگو آن‌ها بیان اوقات برای مردم و (تعیین وقت) حج است. و نیکوکاری آن نیست که (در حال احرام) از پشت خانه‌ها وارد شوید، بلکه نیکوکار کسی است که تقوا پیشه کند، و به خانه‌ها از درهایشان وارد شوید، و از الله بترسید تا رستگار شوید.');
INSERT INTO "hussein_taji" VALUES(2,190,'2:190','و در راه الله با کسانی‌که با شما می‌جنگند، جنگ کنید، و از حد تجاوز نکنید، که الله تجاوزگران را دوست نمی‌دارد.');
INSERT INTO "hussein_taji" VALUES(2,191,'2:191','و آن‌ها را هر کجا یافتید، بکشید، و از آنجا (مکه) که شما را بیرون کرده‌اند؛ آن‌ها را بیرون کنید، و فتنه (شرک) از کشتن بدتر است. و با آن‌ها در نزد مسجد الحرام جنگ نکنید، تا اینکه با شما در آنجا بجنگند، پس اگر (در آنجا) با شما جنگیدند، ایشان را بکشید، سزای کافران چنین است.');
INSERT INTO "hussein_taji" VALUES(2,192,'2:192','پس اگر دست برداشتند، بی‌گمان الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(2,193,'2:193','و با آن‌ها جنگ کنید؛ تا فتنه (شرک) باقی نماند، و دین (خالصانه) از آن الله گردد. پس اگر دست برداشتند؛ تجاوز جز بر ستمکاران روا نیست.');
INSERT INTO "hussein_taji" VALUES(2,194,'2:194','ماه حرام در برابر ماه حرام است، و حرمت (شکنی‌ها) دارای قصاص است، پس هر کس تعدی و تجاوز کرد، همان گونه که بر شما تعدی کرده؛ بر او تعدی کنید، و از الله بترسید و بدانید که الله با پرهیزگاران است.');
INSERT INTO "hussein_taji" VALUES(2,195,'2:195','و در راه الله انفاق کنید و (با ترک انفاق) خود را با دست خویش به هلاکت نیفکنید، و نیکی کنید که الله نیکوکاران را دوست می‌دارد.');
INSERT INTO "hussein_taji" VALUES(2,196,'2:196','و حج و عمره را برای الله به اتمام برسانید، و اگر باز داشته شدید (بوسیله دشمن یا بیماری و غیره) پس آنچه از قربانی فراهم شود (ذبح کنید، و آنگاه از احرام بیرون شوید) و سرهای خود را نتراشید تا قربانی به جایگاهش برسد، و اگر کسی از شما بیمار بود، و یا ناراحتی در سر داشت، (و ناچار شود در حال احرام سر خود را بتراشد) پس فدیه دهد از (قبیل: سه روز) روزه یا صدقه (خوراک دادن به شش مسکین) یا قربانی کردن گوسفندی (که میان فقرا تقسیم شود) پس هنگامی‌که در امان بودید، هر کس از عمره بهره‌مند گردد؛ سپس (اعمال) حج را آغاز کند، آنچه از قربانی (برای او) میسر شد (ذبح می‌کند) و هر کس نیافت سه روز در (ایام) حج و هفت روز هنگامی‌که باز گشتید روزه بگیرید. این ده (روز) کامل است، این (حکم) برای کسی است که خانواده او ساکن مسجد الحرام (= مکه و اطراف آن) نباشد، و از الله بترسید و بدانید که الله سخت‌کیفر است.');
INSERT INTO "hussein_taji" VALUES(2,197,'2:197','حج در ماه‌های معلوم و معینی است، پس کسی‌که در این ماه‌ها حج را (بر خود) فرض گرداند. (باید بداند که) در حج آمیزش جنسی و گناه و جدال (روا) نیست، و آنچه از کارهای نیک انجام دهید؛ الله آن را می‌داند، و توشه بر گیرید؛ که بهترین توشه پرهیزگاری است، و ای خردمندان، از من بپرهیزید (بترسید).');
INSERT INTO "hussein_taji" VALUES(2,198,'2:198','گناهی بر شما نیست که (در ایام حج با خرید و فروش) از فضل پروردگارتان (روزی) طلب کنید، و هنگامی‌که از عرفات کوچ کردید؛ الله را نزد مشعر الحرام یاد کنید، و او را یاد کنید همچنان که شما را هدایت کرد، و اگر چه پیش از آن از گمراهان بودید.');
INSERT INTO "hussein_taji" VALUES(2,199,'2:199','پس از آنجا که مردم کوچ می‌کنند؛ کوچ کنید، و از الله آمرزش بخواهید همانا الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(2,200,'2:200','پس چون مناسک (حج) خود را به جای آورید، الله را یاد کنید همانند یاد کردن پدرانتان بلکه بیشتر از آن، از مردم کسانی هستند که می‌گویند «پروردگارا! به ما در دنیا عطا کن» و او را در آخرت بهره‌ای نیست.');
INSERT INTO "hussein_taji" VALUES(2,201,'2:201','و از آنان کسانی هستند که می‌گویند: «پروردگارا! به ما در دنیا نیکی عطا کن، و در آخرت (نیز) نیکی (عطا فرما) و ما را از عذاب آتش نگه دار»');
INSERT INTO "hussein_taji" VALUES(2,202,'2:202','اینانند که از آنچه بدست آورده‌اند، بهره‌ای دارند. و الله سریع الحساب (زود شمار) است.');
INSERT INTO "hussein_taji" VALUES(2,203,'2:203','و الله را در روزهای معین و شمرده (که سه روز ایام تشریق: 12و13 ماه ذیحجه است) یاد کنید. پس کسی‌که شتاب کند؛ و در دو روز (اعمال را انجام دهد) گناهی بر او نیست. و کسی‌که تأخیر کند (و اعمال را در سه روز انجام دهد) گناهی بر او نیست (این رخصت) برای کسی‌که تقوا پیشه کند، و از الله بترسید، و بدانید که شما به سوی او محشور خواهید شد.');
INSERT INTO "hussein_taji" VALUES(2,204,'2:204','و از مردم کسی هست که گفتارش در (باره) زندگی دنیا تو را به شگفت می‌اندازد و الله را بر آنچه در دل خود دارد؛ گواه می‌گیرد، و حال آنکه او سرسخت ترین دشمنان است.');
INSERT INTO "hussein_taji" VALUES(2,205,'2:205','او هنگامی‌که روی بر می‌گرداند و می‌رود (و یا به ریاستی می‌رسد) در راه فساد در زمین کوشش می‌کند و کشت و نسل را نابود می‌سازد، و الله فساد و (تباهکاری) را دوست نمی‌دارد.');
INSERT INTO "hussein_taji" VALUES(2,206,'2:206','و چون به او گفته شود: «از الله بترس» خود بزرگ‌بینی و (تکبر) او را به گناه می‌کشاند. پس (آتش) جهنم او را کافی است و چه بد آرامگاهی است.');
INSERT INTO "hussein_taji" VALUES(2,207,'2:207','و از مردمان کسی هست که جان خود را برای به دست آوردن خشنودی الله می‌فروشد، و الله نسبت به بندگان مهربان است.');
INSERT INTO "hussein_taji" VALUES(2,208,'2:208','ای کسانی‌که ایمان آورده‌اید! همگی به اسلام (و اطاعت خدا) در آیید و گام‌های شیطان را پیروی نکنید، بدرستی که او برای شما دشمن آشکار است.');
INSERT INTO "hussein_taji" VALUES(2,209,'2:209','پس اگر بعد از آنکه نشانه‌های روشن و آشکار برای شما آمد؛ لغزش کردید و (منحرف شدید)، بدانید که الله توانمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(2,210,'2:210','آیا (کسانی‌که راه اسلام نمی‌پویند و در زمین فساد می‌کنند) جز این انتظار ندارند، که الله (برای داوری و قضاوت میان بندگان) در سایه‌هایی از ابرها به سوی آنان بیاید. و (نیز) فرشتگان (برای اجرا فرمان الهی بیایند) و کار یکسره شود؟! و کارها به سوی الله برگردانده می‌شود.');
INSERT INTO "hussein_taji" VALUES(2,211,'2:211','از بنی اسرائیل بپرس: «چه اندازه نشانه‌های روشن و آشکار به آن‌ها داده‌ایم؟» (ولی آن‌ها نعمت‌ها را نادیده گرفته و ناسپاس شدند) و کسی‌که نعمت الله را پس از آنکه برایش آمد دگرگون کند (عذاب سختی خواهد داشت) همانا الله سخت‌کیفر است.');
INSERT INTO "hussein_taji" VALUES(2,212,'2:212','زندگی دنیا برای کافران آراسته شده است، و مؤمنان را مسخره می‌کنند، و (حال آنکه) پرهیزگاران در روز قیامت بالاتر از آنان هستند، و الله هر کس را بخواهد بی شمار روزی می‌دهد.');
INSERT INTO "hussein_taji" VALUES(2,213,'2:213','(در آغاز) مردم یک امت بودند، آنگاه الله پیامبران را مژده‌آور و بیم‌دهنده بر انگیخت و با آنان کتاب (آسمانی) را بحق نازل کرد، تا در میان مردم، در آنچه اختلاف داشتند، داوری کنند، و تنها کسانی‌که (کتاب) به آنان داده شده بود، پس از آنکه نشانه‌های روشن به آن‌ها رسیده بود، بخاطر ستم و حسادتی که در میان‌شان بود، در آن اختلاف کردند، پس الله به فرمان خویش کسانی را که ایمان آورده بودند؛ به آن حق و (حقیقتی) که در آن اختلاف داشتند، هدایت کرد. و الله هر کس را بخواهد به راه راست هدایت می‌کند.');
INSERT INTO "hussein_taji" VALUES(2,214,'2:214','آیا گمان کرده‌اید که داخل بهشت می‌شوید و حال آنکه هنوز بر (سر) شما نیامده است، مانند آنچه که بر (سر) پیشینیان شما آمد؟ سختی و زیان به آن‌ها رسید، و (آنچنان) تکان خوردند تا که پیامبر و کسانی‌که با او ایمان آورده بودند، گفتند: «یاری الله کی خواهد بود؟» آگاه باشید که یاری الله نزدیک است.');
INSERT INTO "hussein_taji" VALUES(2,215,'2:215','از تو می‌پرسند چه چیزی انفاق کنند؟ بگو: «هر مالی که انفاق کردید، پس برای پدر و مادر و نزدیکان و یتیمان و مستمندان و در راه ماندگان است. و هر کار نیکی که انجام دهید، بدرستی که الله به آن آگاه است.»');
INSERT INTO "hussein_taji" VALUES(2,216,'2:216','جهاد (در راه خدا) بر شما واجب شده است، در حالی‌که برای شما ناگوار است، چه بسا چیزی را خوش نداشته باشید، حال آنکه آن برای شما بهتر باشد، و چه بسا چیزی را دوست داشته باشید، حال آنکه آن برای شما بد باشد، و الله می‌داند، و شما نمی‌دانید.');
INSERT INTO "hussein_taji" VALUES(2,217,'2:217','از تو درباره جنگ کردن در ماه حرام، می‌پرسند. بگو: «جنگ در آن (گناهی) بزرگ؛ است، و بازداشتن از راه الله و کفر ورزیدن به او و (باز داشتن از) مسجد الحرام و بیرون راندن اهلش از آن؛ نزد الله (گناهی) بزرگ‌تر، و فتنه (= شرک و شکنجه مؤمنان) از کشتار بزرگ‌تر است.» و پیوسته با شما می‌جنگند، تا آنکه اگر بتوانند شما را از دین‌تان برگردانند، و هر کس از شما که از دینش بر گردد و در حال کفر بمیرد، پس آنان اعمال‌شان در دنیا و آخرت تباه شده است، و آنان اهل آتش (دوزخ) اند، و همیشه در آن خواهند بود.');
INSERT INTO "hussein_taji" VALUES(2,218,'2:218','بدرستی کسانی‌که ایمان آوردند و کسانی‌که هجرت کردند و در راه الله جهاد نمودند، آنان به رحمت الله امیدوارند، و الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(2,219,'2:219','از تو درباره شراب و قمار می‌پرسند بگو: «در آن دو، گناهی بزرگ است و سودهایی برای مردم در بر دارد، و گناه هر دو از سودشان بزرگ‌تر است». و از تو می‌پرسند «چه چیزی انفاق کنند؟» بگو: مازاد (نیازمندی خود) اینچنین الله آیات را برای شما روشن می‌سازد، باشد که اندیشه کنید.');
INSERT INTO "hussein_taji" VALUES(2,220,'2:220','در دنیا و آخرت، و از تو درباره یتیمان می‌پرسند، بگو: «اصلاح کردن (کار) آنان بهتراست، و اگر با آنان همزیستی کنید؛ آن‌ها برادران (دینی) شما هستند». و الله تباهکار را از درستکار می‌شناسد، و اگر الله می‌خواست شما را به زحمت می‌انداخت، به راستی که الله توانمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(2,221,'2:221','و با زنان مشرک تا ایمان نیاورده‌اند؛ ازدواج نکنید، و بی‌گمان کنیز با ایمان از زن (آزاد) مشرک بهتر است، اگر چه (زیبایی یا دارایی او) شما را به شگفت آورد. و (زنان با ایمان را) به ازدواج مردان مشرک در نیاورید؛ تا ایمان بیاورند. و قطعاً بردۀ با ایمان از مرد (آزاد) مشرک؛ بهتر است، اگر چه (زیبائی یا دارایی و موقعیت او) شما را به شگفت آورد. آنان به سوی آتش (دوزخ) دعوت می‌کنند و الله به فرمان و (توفیق) خویش به سوی بهشت و آمرزش دعوت می‌کند، و آیات و (احکام) خود را برای مردم روشن می‌سازد، باشد که متذکّر (و یادآور) شوند.');
INSERT INTO "hussein_taji" VALUES(2,222,'2:222','و از تو دربارۀ حیض (عادت ماهانه زنان) می‌پرسند، بگو «آن پلید و زیانبار است، پس در حالت قاعدگی از زنان کناره گیری کنید، و با آن‌ها نزدیکی نکنید؛ تا پاک شوند، پس هنگامی‌که پاک شدند و غسل کردند. از همان جا که الله به شما فرمان داده، با آن‌ها آمیزش کنید، به راستی که الله توبه کنندگان را دوست می‌دارد و (نیز) پاکان را دوست می‌دارد».');
INSERT INTO "hussein_taji" VALUES(2,223,'2:223','زنان شما کشتزار شما هستند، پس هر گونه که بخواهید به کشتزار خود درآیید، و (توشه‌ای) برای خود از پیش بفرستید، و از الله بترسید، و بدانید که شما او را ملاقات خواهید کرد، و به مؤمنان بشارت ده.');
INSERT INTO "hussein_taji" VALUES(2,224,'2:224','و الله را دستاویز سوگند‌‌‌های خود قرار ندهید، برای اینکه نیکی کنید و تقوا پیشه سازید، و در میان مردم آشتی دهید (قسم یاد نکنید) و الله شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(2,225,'2:225','الله شما را بخاطر سوگند هایی که بیهوده و بدون قصد یاد می‌کنید، مؤاخذه نمی‌کند، ولی شما را به آنچه دل‌های تان کسب کرده (از روی اختیار و قصد بوده) مؤاخذه می‌کند و الله آمرزندۀ بردبار است.');
INSERT INTO "hussein_taji" VALUES(2,226,'2:226','برای کسانی‌که سوگند می‌خورند با زنان خود آمیزش نکنند (ایلاء) چهار ماه انتظار و (مهلت) است. پس اگر (سوگند خود را نادیده گرفتند) و بازگشتند، همانا الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(2,227,'2:227','و اگر تصمیم طلاق گرفتند، به راستی که الله شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(2,228,'2:228','و زنان مطلقه باید مدت سه پاکی (یا دیدن سه بار عادت ماهانه) انتظار بکشند، و اگر به الله و روز قیامت ایمان دارند؛ برای آن‌ها روا نیست که آنچه را الله در رحم‌هایشان آفریده، کتمان کنند. و شوهران‌شان اگر خواهان آشتی هستند، برای بازگرداندن آن‌ها (به زندگی زناشوئی) در این (مدت از دیگران) سزاوارترند. و برای زنان حقوق شایسته‌ای است همانند (حقوق و وظایفی) که بر عهده آن‌هاست، و مردان بر آنان برتری دارند، و الله توانمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(2,229,'2:229','طلاق (رجعی) دوبار است پس (از آن یا باید همسر را) بطور شایسته نگاه داشتن، یا با نیکی رها کردن. و برای شما روا نیست که از آنچه به آنان داده‌اید، چیزی پس بگیرید، مگر آنکه (زن و شوهر) بترسند که نتوانند حدود الهی را بر پا دارند، پس (ای مؤمنان) اگر ترسیدید که حدود الهی را بر پا ندارند، گناهی بر آن دو نیست، که زن فدیه و عوضی بپردازد (و طلاق بگیرد) این‌ها حدود الهی است، پس از آن‌ها تجاوز نکنید و هر کس از حدود الله تجاوز کند، پس اینانند که ستمگرند.');
INSERT INTO "hussein_taji" VALUES(2,230,'2:230','پس اگر (شوهر برای بار سوم) او را طلاق داد، پس از آن (زن) برای او حلال نیست تا اینکه شوهری غیر از او ازدواج کند (و با او آمیزش جنسی نماید) پس اگر (شوهر بعدی) او را طلاق داد، گناهی برآن دو نیست که (با ازدواج مجدد) به یکدیگر بازگردند، اگرآن دو پندارند که حدود الله را بر پا می‌دارند، این‌ها حدود الله است که آن‌ها را برای مردمی که می‌دانند بیان می‌نماید.');
INSERT INTO "hussein_taji" VALUES(2,231,'2:231','و هنگامی‌که زنان را طلاق (رجعی) دادید، آنگاه به پایان (روزهای) عده خویش رسیدند. پس به شایستگی آن‌ها را نگه دارید و یا به نیکی و خوبی آن‌ها را رها سازید، و آنان را بخاطر زیان رساندن نگاه ندارید تا (به حقوق شان) تعدی و تجاوز کنید، و کسی‌که چنین کند قطعاً به خود ستم کرده است، و آیات الله را به مسخره نگیرید. و نعمت الله را بر خود و آ نچه را که از کتاب (قرآن) و حکمت (سنت و اسرار شریعت) بر شما نازل کرده و شما را با آن پند می‌دهد، به یاد بیاورید و از الله بترسید و بدانید که الله به همه چیز داناست.');
INSERT INTO "hussein_taji" VALUES(2,232,'2:232','و هنگامی‌که زنان را طلاق دادید، آنگاه (مدت) عده خود را به پایان رساندند، آنان را از ازدواج با شوهران (سابق) خود باز ندارید، وقتی که به طرز پسندیده‌ای در بین خویش تراضی کنند. این (دستوری است) که هر کس از شما که به الله و روز قیامت ایمان دارد؛ به آن پند داده می‌شود، و این (دستور) برای شما پر برکت‌تر و پاکیزه‌تر است، و الله می‌داند و شما نمی‌دانید.');
INSERT INTO "hussein_taji" VALUES(2,233,'2:233','و مادران (باید) فرزندان خود را دو سال تمام شیر دهند. (این دستور) برای کسی است که بخواهد دوران شیر خوارگی را تکمیل کند. و بر آن کس که فرزند برای او متولد شده (پدر) لازم است خوراک و پوشاک آنان (= مادران) را بطور شایسته (در مدت شیر دادن بپردازد) هیچ کس جز به‌اندازه توانایی‌اش مکلف نمی‌شود. نه مادر به خاطر فرزندش و نه پدر به سبب فرزندش باید زیان ببیند. و مانند همین (احکام) بر وارث (نیز) لازم است، پس اگر آن دو با رضایت و مشورت یکدیگر بخواهند. (کودک را زودتر) از شیر بازگیرند، گناهی بر آن‌ها نیست، و اگر خواستید که برای فرزندان خود دایه‌ای بگیرید، گناهی بر شما نیست؛ به شرط اینکه آنچه را که مقرر کرده‌اید، بطور شایسته بپردازید. و از الله بترسید، و بدانید که الله به آنچه انجام می‌دهید؛ بیناست.');
INSERT INTO "hussein_taji" VALUES(2,234,'2:234','و کسانی از شما که می‌میرند و همسرانی باقی می‌گذارند؛ باید چهار ماه و ده روز انتظار بکشند (وعده نگه دارند) پس هنگامی‌که مدت عدۀ خود را به پایان رساندند، در آنچه آنان به طور شایسته درباره خود انجام دهند؛ گناهی بر شما نیست، و الله به آنچه می‌کنید آگاه است.');
INSERT INTO "hussein_taji" VALUES(2,235,'2:235','و گناهی بر شما نیست در آنچه که بطور کنایه از خواستگاری زنانی (که در عده‌اند) بر زبان آورید، یا در دل‌های خود پنهان دارید. الله دانست که شما به زودی به یاد آنان خواهید افتاد، ولی پنهانی با آن‌ها وعده (زناشوئی) ندهید، مگر اینکه سخنی پسندیده (بطور کنایه) بگویید، و تصمیم به عقد ازدواج نگیرید تا مدت مقرر (عده) به سرآید، و بدانید که الله آنچه را که در دل‌های شماست می‌داند، پس از (مخالفت) او بترسید، و بدانید که الله آمرزندۀ بردبار است.');
INSERT INTO "hussein_taji" VALUES(2,236,'2:236','اگر زنان را مادامی که با آنان آمیزش جنسی نکرده‌اید و مهری برایشان معین نکرده‌اید طلاق دادید؛ گناهی بر شما نیست، و (در این حالت) آن‌ها را (با هدیه‌ای مناسب) بهره‌مند سازید، بر توانگر به‌اندازه توانش، و بر تنگدست به‌اندازه توانش هدیه‌ای شایسته است (و این حکم) بر نیکوکاران الزامی است.');
INSERT INTO "hussein_taji" VALUES(2,237,'2:237','اگر زنان را پیش از آنکه با آنان نزدیکی کنید، طلاق دادید در حالی‌که برای آن‌ها مهری تعیین کرده‌اید؛ پس نصف آنچه را تعیین کرده‌اید (به آن‌ها بدهید) مگر اینکه آنان (حق خود را) ببخشند. یا کسی‌که پیوند نکاح به دست اوست (شوهر) ببخشد (و تمام مهر را بدهد) و آنکه ببخشید به پرهیزگاری نزدیکتر است. و بزرگواری و نیکی را در میان خود فراموش نکنید، به راستی الله به آنچه می‌کنید؛ بیناست.');
INSERT INTO "hussein_taji" VALUES(2,238,'2:238','بر (بجا آوردن) نماز‌ها و (بخصوص) نماز میانه (نماز عصر) محافظت کنید، و فروتنانه برای الله به پا خیزید.');
INSERT INTO "hussein_taji" VALUES(2,239,'2:239','پس اگر (از دشمن و غیره) ترسیدید، در حال پیاده یا سواره (نماز را بجای آورید) پس هنگامی‌که ایمن شدید، الله را یاد کنید. (و نماز‌ها را به تمام و کمال بجای آورید) همان‌گونه که چیزهای را که نمی‌دانستید به شما یاد داد.');
INSERT INTO "hussein_taji" VALUES(2,240,'2:240','و کسانی از شما که می‌میرند و همسرانی به جا می‌گذارند، باید برای همسران خود وصیت کنند که تا یک سال آن‌ها را (با پرداختن هزینه زندگی) بهره‌مند سازند بدون آنکه (از خانه شوهر) بیرون کنند. پس اگر بیرون روند، درباره آنچه که آنان نسبت بخود بطور شایسته انجام می‌دهند (از قبیل زینت دادن خود و ازدواج مجدد) گناهی بر شما نیست، و الله توانمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(2,241,'2:241','و برای زنان طلاق داده شده، هدیه‌ای مناسب و شایسته (بر شوهر) لازم است؛ این حقی است بر پرهیزگاران.');
INSERT INTO "hussein_taji" VALUES(2,242,'2:242','این چنین الله آیات خود را برای شما روشن می‌سازد؛ باشد که بیندیشید.');
INSERT INTO "hussein_taji" VALUES(2,243,'2:243','آیا ندیدی کسانی را که از ترس مرگ از خانه‌های خود بیرون شدند در حالی‌که هزاران نفر بودند؟ پس الله به آنان گفت: «بمیرید» سپس آن‌ها را زنده کرد، همانا الله بر مردم بخشش و فضل دارد، ولی بیشتر مردم شکر بجا نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(2,244,'2:244','و در راه الله بجنگید و بدانید که الله شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(2,245,'2:245','کیست که به الله قرض دهد قرض نیکو تا آن را برای او چندین برابر کند؟ و الله است که (روزی بندگان را) تنگ و گشاده می‌سازد، و به سوی او باز گردانده می‌شوید.');
INSERT INTO "hussein_taji" VALUES(2,246,'2:246','آیا ندیدی جمعی از بنی اسرائیل را بعد از موسی هنگامی‌که به پیامبر خود گفتند: پادشاهی برای ما انتخاب کن؛ تا در راه الله جنگ کنیم (پیامبر آن‌ها) گفت: شاید اگر جهاد بر شما مقرر شود (سر پیچی کنید و) در راه الله جهاد نکنید. گفتند: چرا در راه الله جهاد نکنیم در حالی‌که از خانه‌ها و فرزندانمان بیرون رانده شده‌ایم. پس هنگامی‌که جنگ بر آنان مقرر شد، جز عده کمی از آنان همه (سر پیچی) و روی گردانی کردند، و الله به ستمکاران آگاه است.');
INSERT INTO "hussein_taji" VALUES(2,247,'2:247','و پیامبرشان به آن‌ها گفت: همانا الله «طالوت» را (به عنوان) پادشاه برای شما مبعوث (و انتخاب) کرده است، گفتند: چگونه او بر ما پادشاهی کند با اینکه ما به پادشاهی از او سزاوارتریم، و از مال (دنیا) بهره چندانی داده نشده است؟ گفت: به راستی الله او را بر شما برگزیده است، و او را در علم و (نیروی) بدن وسعت و برتری بخشیده است. و الله پادشاهی خود را به هر کس که بخواهد می‌دهد، و الله گشایشگر داناست.');
INSERT INTO "hussein_taji" VALUES(2,248,'2:248','و پیامبرشان به آن‌ها گفت: همانا نشانه پادشاهی او این است که تابوت (عهد) که در آن آرامشی از پروردگار شما و باقیمانده از آنچه آل موسی و آل هارون بر جای گذشته‌اند (قرار دارد) در حالی‌که فرشتگان آن را حمل می‌کنند به سوی شما خواهد آمد، مسلماً در این نشانه‌ای برای شماست، اگر مؤمن باشید.');
INSERT INTO "hussein_taji" VALUES(2,249,'2:249','پس هنگامی‌که طالوت با لشکریان رهسپار شد، به آن‌ها گفت: همانا الله شما را بوسیله یک جوی (آب) آزمایش می‌کند. پس هر کس از آن بنوشد از (یاران) من نیست. و هر کس از آن نخورد، قطعاً او از (یاران) من است، مگر کسی‌که با دست خویش کفی (از آب) برگیرد. پس جز عدۀ کمی؛ همگی از آن (آب) نوشیدند. و هنگامی‌که او و کسانی‌که با او ایمان آورده بودند، از آن (نهر) گذشتند. (از کمی افراد خود ناراحت شدند و عده‌ای) گفتند: «امروز ما توانایی (مقابله) با جالوت و سپاهیانش را نداریم». (اما) آنانی که به دیدار الله یقین داشتند، گفتند: «چه بسا گروه کوچکی که به فرمان الله بر گروهی بسیار پیروز شدند و الله با بردباران است».');
INSERT INTO "hussein_taji" VALUES(2,250,'2:250','و هنگامی‌که برای (مبارزه) با جالوت و سپاهیانش به میدان آمدند، گفتند: «پرودگارا! بر (دل‌های) ما صبر و شکیبایی فرو ریز و گام‌های ما را استوار و ثابت بدار، و ما را بر گروه کافران پیروز بگردان».');
INSERT INTO "hussein_taji" VALUES(2,251,'2:251','پس به فرمان خدا، آن‌ها را شکست دادند، و داوود (جوان نیرومند و شجاعی که در لشکر طالوت بود) جالوت را کشت، و الله پادشاهی و نبوت را به او بخشید و از آنچه می‌خواست به او آموخت، و اگر الله برخی از مردم را به وسیله برخی دیگر دفع نمی‌کرد، قطعاً زمین تباه می‌گردید. ولی الله بر جهانیان فضل و بخشش دارد.');
INSERT INTO "hussein_taji" VALUES(2,252,'2:252','این‌ها، آیات الله است که ما آن را بحق بر تو می‌خوانیم و به راستی تو از پیامبران هستی.');
INSERT INTO "hussein_taji" VALUES(2,253,'2:253','این پیامبران (که نام برخی از آنان گذشت) بعضی از ایشان را بر بعضی ایشان برتری دادیم، از آنان کسی بود که الله با او سخن گفت و درجات بعضی از آنان را بالا برد. و به عیسی بن مریم نشانه‌های روشن دادیم و او را بوسیلۀ روح القدس (= جبرئیل) تأیید کردیم. و اگر الله می‌خواست کسانی‌که بعد از آن‌ها بودند، پس از آن (همه) نشانه‌های روشن که برایشان آمد با یکدیگر جنگ نمی‌کردند. ولی (آنان با هم) اختلاف کردند، پس برخی از آنان ایمان آورد، و برخی از آنان کفر ورزید، و اگر الله می‌خواست با هم جنگ نمی‌کردند. ولی الله آنچه را می‌خواهد انجام می‌دهد.');
INSERT INTO "hussein_taji" VALUES(2,254,'2:254','ای کسانی‌که ایمان آورده‌اید! از آنچه به شما روزی داده‌ایم؛ انفاق کنید پیش از آنکه روزی فرا رسد که در آن نه خرید و فروش است و نه دوستی و نه شفاعتی، و کافران خود ستمکارانند.');
INSERT INTO "hussein_taji" VALUES(2,255,'2:255','الله (معبود بر حق است) هیچ معبودی بحق جز او نیست، زنده (و جهان هستی را) نگه دار و مدبر است، نه چرت او را فرا گیرد و نه خواب، آنچه در آسمان‌ها و آنچه در زمین است از آن اوست کیست که در نزد او جز به فرمان او شفاعت کند؟ آنچه در پیش روی آنان و آنچه در پشت سرشان است می‌داند. و به چیزی از علم او، جز به آنچه بخواهد احاطه نمی‌یابد، کرسی او آسمان‌ها و زمین را در بر گرفته، و نگاهداری آن دو (زمین و آسمان) بر او گران و دشوار نیاید، و او بلند مرتبه و بزرگ است.');
INSERT INTO "hussein_taji" VALUES(2,256,'2:256','در (قبول) دین هیچ اجباری نیست، به راستی که راه راست (و هدایت) از راه انحراف (و گمراهی) روشن شده است. پس هر کس به طاغوت (شیطان و بت و انسان‌های گمراه و طغیانگر) کفر ورزد و به الله ایمان آورد، پس به راستی که به دستگیره محکمی چنگ زده است، که آن را گسستن نیست، و الله شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(2,257,'2:257','الله یاور و (سر پرست) کسانی است که ایمان آورده‌اند، آن‌ها را از تاریکی‌ها به سوی نور بیرون می‌برد. و کسانی‌که کافر شدند یاور و سر پرست آن‌ها طاغوت است که آنان را از نور، به سوی تاریکی‌ها بیرون می‌برد، آن‌ها اهل آتشند و جاودان در آن خواهند ماند.');
INSERT INTO "hussein_taji" VALUES(2,258,'2:258','آیا ندیدی و (آ گاهی نداری از) کسی (= نمرود پادشاه بابل) که با ابراهیم دربارۀ پروردگارش به مجادله و گفتگو پرداخت؟ بدان خاطر که الله به او پادشاهی داده بود. و (از کبر و غرور سرمست شده بود،) هنگامی‌که ابراهیم گفت: «پروردگار من کسی است که زنده می‌کند و می‌میراند» (نمرود) گفت: «من (نیز) زنده می‌کنم و می‌میرانم». ابراهیم گفت: «همانا الله، خورشید را از مشرق می‌آورد، پس تو آن را از مغرب بیاور!» (در اینجا) آن کسی‌که کفر ورزیده بود مبهوت شد. و الله قوم ستمگر را هدایت نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(2,259,'2:259','یا همانند آن کسی‌که بر دهی گذشت، در حالی‌که دیوارهای آن به روی سقف‌ها فرو ریخته بود. (او با خود) گفت: چگونه الله (اهل) این (ده) را پس از مرگ‌شان زنده می‌کند؟! پس الله او را (به مدت) صد سال میراند، پس زنده‌اش کرد (و به او) فرمود: «چه قدر درنگ کردی؟» گفت: «یک روز، یا بخشی از یک روز را درنگ کردم». فرمود: «(نه) بلکه صد سال درنگ کردی، پس به غذا و نوشیدنی خود نگاه کن (که طعم و رنگ آن) تغییر نکرده است، و به الاغ خود نگاه کن (که چگونه متلاشی شده است ما چنین کردیم تا به تو پاسخ گوییم) و (هم) تو را نشانه‌ای برای مردم (در مورد معاد) قرار دهیم و (اکنون) به استخوان‌های (الاغ خود) نگاه کن (که) چگونه آن‌ها را برداشته به هم پیوند می‌دهیم، سپس گوشت بر آن می‌پوشانیم» پس هنگامی‌که (قدرت الهی بر زنده کردن مردگان) برایش آشکار شد، گفت: «می‌دانم که الله بر هر چیزی تواناست».');
INSERT INTO "hussein_taji" VALUES(2,260,'2:260','و (به یاد بیاور) هنگامی را که ابراهیم گفت: «پروردگارا! به من نشان بده چگونه مردگان را زنده می‌کنی؟» فرمود: «مگر ایمان نیاورده‌ای؟!» گفت: «چرا، ولی (می‌خواهم) که قلبم آرام بگیرد» فرمود: «پس چهار پرنده را برگیر، آنگاه آن‌ها را پیش خود قطعه قطعه کن (و در هم بیامیز) سپس بر هر کوهی قسمتی از آن را بگذار، آنگاه آن‌ها را بخوان؛ شتابان به سوی تو می‌آیند، و بدان که الله توانمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(2,261,'2:261','مثل کسانی‌که اموال خود را در راه الله انفاق می‌کنند، همانند دانه‌ای است که هفت خوشه برویاند که در هر خوشه یکصد دانه باشد، و الله برای هر کس که بخواهد چند برابر می‌کند و الله گشایشگر داناست.');
INSERT INTO "hussein_taji" VALUES(2,262,'2:262','کسانی‌که اموال خود را در راه الله انفاق می‌کنند سپس در پی آنچه اتفاق کرده‌اند، منت نمی‌گذارند و آزاری نمی‌رسانند، پاداش آن‌ها برایشان نزد پروردگارشان (محفوظ) است. و ترسی بر آنان نیست و نه ایشان اندوهگین شوند.');
INSERT INTO "hussein_taji" VALUES(2,263,'2:263','سخن پسندیده و گذشت بهتر از صدقه‌ای است که آزاری به دنبال آن باشد، و الله بی‌نیاز (و) بردبار است.');
INSERT INTO "hussein_taji" VALUES(2,264,'2:264','ای کسانی‌که ایمان آورده‌اید! صدقه‌های خود را با منت و آزار، باطل مکنید، مانند کسی‌که مالش را برای نشان دادن به مردم، انفاق می‌کند، و به الله و روز قیامت ایمان ندارد، پس مثل او همچون مثل سنگ صافی است که برروی آن (اندکی) خاک باشد. آنگاه باران تندی به آن برسد، و آن را صاف و سخت بر جای گذارد. آنان (ریا کاران) از آنچه که انجام داده‌اند چیزی به دست نمی‌آورند، و الله گروه کافران را هدایت نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(2,265,'2:265','و مثل کسانی‌که اموال خود را برای طلب خشنودی خدا، و بخاطر یقین و باوری که در دل‌های شان (به پاداش خدا) دارند، انفاق می‌کنند؛ همچون مثل باغی است که بر پشته‌ای قرار دارد، و باران (زیاد و) تندی به آن برسد. پس میوه‌های خود را دو چندان دهد. و اگر باران تند به آن نرسد، باران خفیف و اندکی (برای آن کافی است). و الله به آنچه می‌کنید؛ بیناست.');
INSERT INTO "hussein_taji" VALUES(2,266,'2:266','آیا کسی از شما دوست دارد که باغی از درختان خرما و انگور داشته باشد که از زیر آن‌ها نهرها روان است. و برای او در آن باغ از هر گونه میوه‌ای (فراهم) باشد. و در حالی‌که به سن پیری رسیده و فرزندانی (خرد سال) و ناتوان دارد. آنگاه گردبادی که در آن آتش (سوزانی) است، به آن بر خورد کند و (یکسره) بسوزد؟! (همین طور است، حال کسانی‌که صدقه‌های خود را با منت و آزار و ریا باطل می‌کنند). این چنین الله آیات (خود) را برای شما روشن می‌سازد، شاید که شما بیندیشید.');
INSERT INTO "hussein_taji" VALUES(2,267,'2:267','ای کسانی‌که ایمان آورده‌اید! از چیزهای پاکیزه‌ای که به دست آورده‌اید، و از آنچه از زمین برای شما بیرون آورده‌ایم، انفاق کنید، و برای انفاق کردن قصد نا پاک آن را نکنید، در حالی‌که خود شما حاضر نیستید آن‌ها را بپذیرید، مگر آنکه در آن چشم پوشی کنید. و بدانید که الله بی‌نیاز ستوده است.');
INSERT INTO "hussein_taji" VALUES(2,268,'2:268','شیطان شما را (به هنگام انفاق می‌ترساند و) وعده تهی دستی می‌دهد، و شما را به زشتی‌ها امر می‌کند. و در حالی‌که الله از جانب خود به شما وعده آمرزش و فزونی (نعمت) می‌دهد، و الله گشایشگر داناست.');
INSERT INTO "hussein_taji" VALUES(2,269,'2:269','(الله) به هر کس که بخواهد حکمت و (دانش) می‌دهد، و به هر کس حکمت داده شود، بی‌شک خیر فراوانی داده شده است، و جز خردمندان پند نمی‌گیرد.');
INSERT INTO "hussein_taji" VALUES(2,270,'2:270','و هر چیزی را که انفاق کنید یا هر نذری که ببندید، قطعاً الله آن را می‌داند، و برای ستمگران هیچ یاوری نیست.');
INSERT INTO "hussein_taji" VALUES(2,271,'2:271','اگر صدقه‌ها را آشکار کنید چه نیکوست، و اگر آن‌ها را پنهان دارید و به تهی دستان بدهید، پس این برای شما بهتر است، و برخی از گناهان شما را می‌زداید، و الله به آنچه می‌کنید، آگاه است.');
INSERT INTO "hussein_taji" VALUES(2,272,'2:272','هدایت آنان بر عهدۀ تو نیست، و لیکن الله هر که را بخواهد هدایت می‌کند. و آنچه از مال انفاق می‌کنید، برای خودتان است، و جز برای طلب خشنودی الله انفاق نکنید، و آنچه از مال انفاق می‌کنید، (پاداش آن) بطور کامل و تمام به شما داده می‌شود، و به شما ستم نخواهد شد.');
INSERT INTO "hussein_taji" VALUES(2,273,'2:273','(صدقات شما) برای تهی دستانی است که در راه الله محصور و بازداشته شده‌اند، و نمی‌توانند (برای تأمین هزینه زندگی) در زمین سفر کنند. از شدّت خویشتن داری، افراد ناگاه آن‌ها را بی‌نیاز می‌پندارد، آن‌ها را از چهره‌هایشان می‌شناسی، با اصرار (چیزی) از مردم نمی‌خواهند و آنچه از مال انفاق می‌کنید، قطعاً الله به آن آگاه است.');
INSERT INTO "hussein_taji" VALUES(2,274,'2:274','کسانی‌که اموال خود را، در شب و روز پنهان و آشکار انفاق می‌کنند، مزد آن‌ها نزد پروردگارشان برایشان (محفوظ) است؛ و نه ترسی بر آن‌هاست، و نه ایشان اندوهگین می‌شوند.');
INSERT INTO "hussein_taji" VALUES(2,275,'2:275','کسانی‌که ربا می‌خورند (روز قیامت از گور) بر نمی‌خیزند؛ مگر مانند برخاستن کسی‌که بر اثر تماس شیطان دیوانه شده و (نمی‌تواند تعادل خود را حفظ کند) این بدان سبب است که آنان گفتند: «همانا داد و ستد هم مثل ربا است» و در حالی‌که الله داد و ستد را حلال کرده و ربا را حرام نموده است. پس همانا هر کس که از (جانب) پروردگارش پندی به او برسد، آنگاه (از ربا خواری) باز ایستاد، آنچه گذشته و (قبل از نزول حکم دریافت کرده است) از آن اوست. و کارش به الله واگذار می‌شود، و کسی‌که (به ربا خواری) باز گردد، آنان اهل آتشند و در آن جاویدان خواهند بود.');
INSERT INTO "hussein_taji" VALUES(2,276,'2:276','الله ربا را نابود می‌کند و صدقات را افزایش و (برکت) می‌دهد. و الله هیچ (انسان) ناسپاس گنهکاری را دوست نمی‌دارد.');
INSERT INTO "hussein_taji" VALUES(2,277,'2:277','به راستی کسانی‌که ایمان آورده‌اند و اعمال شایسته انجام داده‌اند، و نماز را بر پا داشته‌اند و زکات را پرداخته‌اند؛ پاداش آنان نزد پروردگارشان برایشان (محفوظ) است، و نه ترسی بر آن‌هاست و نه ایشان اندوهگین می‌شوند.');
INSERT INTO "hussein_taji" VALUES(2,278,'2:278','ای کسانی‌که ایمان آورده‌اید! از الله بترسید و آنچه از (مطالبات) ربا باقی مانده است رها کنید؛ اگر مؤمن هستید.');
INSERT INTO "hussein_taji" VALUES(2,279,'2:279','پس اگر (چنین) نکردید، یقین داشته باشید که الله و رسولش با شما جنگ خواهند کرد. و اگر توبه کردید اصل سرمایه‌هایتان از آن شماست، نه ستم می‌‌کنید و نه بر شما ستم وارد می‌شود.');
INSERT INTO "hussein_taji" VALUES(2,280,'2:280','و اگر (بدهکار) تنگدست بود، پس تا (هنگام) گشایش (و توانایی) مهلت دهید، و (اگر توانایی پرداخت ندارد) بخشیدن آن برای شما بهتر است؛ اگر بدانید.');
INSERT INTO "hussein_taji" VALUES(2,281,'2:281','و از روزی بترسید که در آن (روز) به سوی الله باز گردانده می‌شوید، سپس به هر کس (پاداش) آنچه انجام داده؛ تمام و کامل داده می‌شود، و به آنان ستم نخواهد شد.');
INSERT INTO "hussein_taji" VALUES(2,282,'2:282','ای کسانی‌که ایمان آورده‌اید! هر گاه به همدیگر تا مدت معینی و امی دادید (یا معاملۀ نسیه‌ای انجام دادید). آن را بنویسید، و باید نویسنده‌ای (آن را) دادگرانه بین شما بنویسد، و هیچ نویسنده‌ای نباید از نوشتن همان‌گونه که الله به او آموزش داده، خود داری کند. پس باید بنویسد و (آن) کسی‌که حق بر ذمۀ اوست (= بدهکار) باید املا کند، و باید از الله که پروردگار اوست بپرهیزد، و از آن چیزی نکاهد، پس اگر کسی‌که حق بر ذمۀ اوست سفیه یا ناتوان باشد، یا خود نمی‌تواند املا کند، پس باید ولی او با (رعایت) عدالت؛ املا کند، و دو شاهد از مردان تان را گواه بگیرید، پس اگر دو مرد نبودند، یک مرد و دو زن از میان گواهانی که (به عدالت آنان) رضایت دارید (گواه بگیرید). تا اگر یکی از آن دو (زن) فراموش کرد، دیگری به او یاد آوری کند. و گواهان نباید به هنگامی‌که آن‌ها را (برای گواهی) دعوت می‌کنند؛ خود داری کنند. و از نوشتن (بدهی) تا سر رسیدش چه کوچک باشد یا بزرگ ملول نشوید. این (نوشتن شما) در نزد الله به عدالت نزدیکتر، و برای گواهی دادن استوارتر، و برای آنکه دچار شک و تردید نشوید؛ بهتر می‌باشد. مگر اینکه داد و ستدی نقدی باشد که بین خود دست به دست می‌کنید. پس در این صورت گناهی بر شما نیست که آن را ننویسید. و هنگامی‌که داد و ستد (نقدی) کردید؛ گواه بگیرید. و نباید به نویسنده و شاهد زیانی برسد و اگر (چنین) کنید؛ بی‌شک (بیانگر) نافرمانی شما خواهد بود. و از الله بترسید. و (آنچه که برای شما مفید است) الله به شما آموزش می‌دهد، و الله به همه چیز داناست.');
INSERT INTO "hussein_taji" VALUES(2,283,'2:283','و اگر در سفر بودید و نویسنده‌ای نیافتید، پس (چیزی) گروگان بگیرید (گروی که در دست طلبکار قرار گیرد). و اگر برخی از شما برخی دیگر را امین دانست، پس آن کس که امین شمرده شده، باید امانت (بدهی) خود را باز پس دهد و باید از الله که پروردگار اوست بترسد، و شهادت را کتمان نکنید، و هر کس آن را کتمان کند، بی‌شک قلبش گناهکار است. و الله به آنچه می‌کنید، داناست.');
INSERT INTO "hussein_taji" VALUES(2,284,'2:284','آنچه در آسمان‌ها و زمین است، از آن الله است. و اگر آنچه را در دل خود دارید، آشکار کنید یا آن را پنهان نمایید، الله شما را به آن محاسبه می‌کند. پس هر کس را بخواهد می‌بخشد، و هر کس را بخواهد؛ عذاب می‌کند، و الله بر هر چیزی تواناست.');
INSERT INTO "hussein_taji" VALUES(2,285,'2:285','پیامبر به آنچه از (سوی) پروردگارش بر او نازل شده، ایمان آورده است، و مؤمنان (نیز) همه به الله و فرشتگان او و کتاب‌هایش و پیامبرانش ایمان آورده‌اند. (و گفتند): «میان هیچ یک از پیامبرانش فرق نمی‌گذاریم» و گفتند: «شنیدیم و اطاعت کردیم. پروردگارا! آمرزش تو را (خواهانیم) و بازگشت به سوی توست».');
INSERT INTO "hussein_taji" VALUES(2,286,'2:286','الله هیچ کس را جز به‌اندازه توانش تکلیف نمی‌کند. آنچه (از خوبی) بدست آورده به سود او، و آنچه (از بدی) به دست آورده به زیان اوست. پروردگارا! اگر فراموش یا خطا کردیم، ما را مؤاخذه نکن، پروردگارا! بار گران (و تکلیف سنگین) را بر (دوش) ما مگذار، چنانکه آن را بر (دوش) کسانی‌که پیش از ما بودند؛ گذاشتی، پروردگارا! آنچه را که طاقت تحمل آن را نداریم بر (دوش) ما مگذار، و ما را بیامرز و به ما رحم فرمای. تو مولا و سرور مایی پس ما را بر گروه کافران پیروز گردان.');
INSERT INTO "hussein_taji" VALUES(3,1,'3:1','الم (الف. لام. میم)');
INSERT INTO "hussein_taji" VALUES(3,2,'3:2','الله (معبود بر حق است) هیچ معبودی بحق جز او نیست، زنده (و جهان هستی را) نگهدار و مدّبر است.');
INSERT INTO "hussein_taji" VALUES(3,3,'3:3','کتاب (قرآن) را بحق بر تو نازل کرد، که تصدیق‌کننده آنچه (از کتاب‌های آسمانی) که پیش از آن است. و تورات و انجیل را...');
INSERT INTO "hussein_taji" VALUES(3,4,'3:4','پیش از آن برای هدایت مردم نازل کرد و (نیز) فرقان را نازل کرد. به راستی کسانی‌که به آیات الله کافر شدند، عذاب سختی دارند. و الله توانمند و صاحب انتقام است.');
INSERT INTO "hussein_taji" VALUES(3,5,'3:5','بی‌شک هیچ چیز، (نه) در زمین و نه در آسمان بر الله پوشیده نمی‌ماند.');
INSERT INTO "hussein_taji" VALUES(3,6,'3:6','او کسی است که شما را در رحم‌ها، هرگونه که بخواهد صورتگری می‌کند. هیچ معبودی (بحق) جز او نیست، که توانمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(3,7,'3:7','او کسی است که کتاب (قرآن) را بر تو نازل کرد. بخشی از آن، آیات محکم (صریح و روشن) است، که آن‌ها اساس کتاب است. و (بخش) دیگر «متشابهات» است (آن آیاتی که درنگاه اول معانی و احتمالات مختلفی دارد و قابل تأویل است، ولی با رجوع به آیات محکم، تفسیر و معنای آن‌ها روشن می‌گردد). اما کسانی‌که در دل‌هایشان کژی و انحراف است، برای فتنه جویی (و گمراه کردن مردم) و بخاطر تأویل آن (به دلخواه خود) از متشابه آن پیروی می‌کنند. و در حالی‌که تأویل آن جز الله نمی‌داند، و راسخان در علم می‌گویند: «ما به همه آن (چه محکم و چه متشابه) ایمان آوردیم، همه از طرف پروردگار ماست» و جز خردمندان متذکر نمی‌شوند.');
INSERT INTO "hussein_taji" VALUES(3,8,'3:8','می‌گویند: پروردگارا! دل‌های ما را، بعد از آنکه ما را هدایت کردی (از راه حق) منحرف مگردان، و از سوی خود، رحمتی بر ما ببخش براستی که تو بخشنده‌ای.');
INSERT INTO "hussein_taji" VALUES(3,9,'3:9','پروردگار! بی‌شک تو مردم را برای روزی‌که در آن تردیدی نیست، جمع خواهی کرد، قطعاً الله وعده (خود) را خلاف نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(3,10,'3:10','به راستی کسانی‌که کافر شدند، اموال و فرزندانشان چیزی از (عذاب) الله را از آنان باز ندارد. و آنان خود هیزم آتش (دوزخ) هستند.');
INSERT INTO "hussein_taji" VALUES(3,11,'3:11','(عادت این کافران) همچون عادت آل فرعون و کسانی است که پیش از آن‌ها بودند. آیات ما را تکذیب کردند، پس الله آن‌ها را به (سزای) گناهان‌شان گرفت، و الله سخت‌کیفر است.');
INSERT INTO "hussein_taji" VALUES(3,12,'3:12','به کسانی‌که کافر شدند بگو: «بزودی مغلوب خواهید شد، و (سپس در قیامت) به سوی جهنم، محشور می‌شوید، و چه بد جایگاهی است».');
INSERT INTO "hussein_taji" VALUES(3,13,'3:13','قطعاً در دو گروهی که (در میان بدر) با هم روبه رو شدند، نشانه و (عبرتی) برای شما بود. یک گروه در راه الله می‌جنگید، و (گروهی) دیگر کافر بودند، آن‌ها (مؤمنان) را با چشم خود، دو برابر آنچه بودند می‌دیدند. و الله هر کس را بخواهد با نصرت و یاری خود، تأیید می‌کند. براستی در این (ماجرا) برای صاحبان بینش عبرتی است.');
INSERT INTO "hussein_taji" VALUES(3,14,'3:14','محبت خواستنی‌های (مادی) از (جمله) زنان و فرزندان و اموال هنگفت از طلا و نقره، و اسب‌های نشاندار، و چهار پایان و زراعت، برای مردم آراسته شده است، این‌ها (همه) کالا و متاع (زود گذر) زندگی دنیاست. و (حال آنکه) سر انجام نیک نزد الله است.');
INSERT INTO "hussein_taji" VALUES(3,15,'3:15','بگو: «آیا شما را به بهتر از این‌ها خبر دهم؟» برای کسانی‌که تقوا پیشه کرده‌اند، در نزد پروردگارشان باغ‌هایی است که از زیر (درختان) آن‌ها نهرها روان است، در آن جاودانه خواهند بود. و همسرانی پاکیزه (دارند)، و (همچنین) از خشنودی الله (برخوردارند) و الله به (احوال) بندگان بیناست.');
INSERT INTO "hussein_taji" VALUES(3,16,'3:16','کسانی‌که می‌گویند: «پروردگارا! به راستی ما ایمان آورده‌ایم، پس گناهان ما را بیامرز، و ما را از عذاب آتش نگاه دار».');
INSERT INTO "hussein_taji" VALUES(3,17,'3:17','همانا شکیبایان و راستگویان و فرمانبرداران و انفاق کنندگان و آمرزش خواهان در سحرگاهان.');
INSERT INTO "hussein_taji" VALUES(3,18,'3:18','الله گواهی داده است که معبودی (بحق) جز او نیست، و فرشتگان و صاحبان دانش، (نیز بر این مطلب گواهی می‌دهند). در حالی‌که (الله در جهان هستی) قیام به عدالت دارد، معبودی (بحق) جز او نیست، که توانمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(3,19,'3:19','همانا دین (حق) نزد خدا، اسلام است. و کسانی‌که کتاب (آسمانی) به آنان داده شد، اختلاف نکردند، مگر بعد از آنکه علم و آگاهی برای آنان آمد، آن هم بخاطر سرکشی و حسدی که میان آنان بود، و هر کس به آیات الله کفر ورزد، پس (بداند که) الله سریع الحساب (زود شمار) است.');
INSERT INTO "hussein_taji" VALUES(3,20,'3:20','پس اگر (دربارۀ دین و توحید) با تو محاجه و ستیز کردند، بگو: «من روی خود را تسلیم الله نموده ام» و هر کس که پیرو من است (نیز خود را تسلیم الله نموده است). و به اهل کتاب (= یهود و نصاری) و بی‌سوادان (مشرکان عرب) بگو: «آیا شما هم تسلیم شده‌اید؟» پس اگر تسلیم شوند، قطعاً هدایت یافته‌اند. و اگر روی گردان شدند و (سر پیچی کردند) پس (نگران مباش زیرا) بر تو فقط رساندن (پیام خدا) است. و الله به (احوال) بندگان بیناست.');
INSERT INTO "hussein_taji" VALUES(3,21,'3:21','به راستی کسانی‌که به آیات الله کفر می‌ورزند، و پیامبران را به ناحق می‌کشند و (نیز) کسانی از مردم را که به عدالت امر می‌کنند؛ به قتل می‌رسانند، پس آنان را به عذابی دردناک مژده بده!');
INSERT INTO "hussein_taji" VALUES(3,22,'3:22','آنان کسانی هستند که اعمال‌شان در دنیا و آخرت تباه شده، و برای آنان هیچ یاور و مددکاری نیست.');
INSERT INTO "hussein_taji" VALUES(3,23,'3:23','آیا ندیدی کسانی را که بهره‌ای از کتاب (آسمانی) داده شدند، به سوی کتاب الله (قرآن) دعوت می‌شوند تا در میان آن‌ها داوری و (حکم) کند، پس گروهی از آنان روی می‌گردانند در حالی‌که اعراض دارند؟!');
INSERT INTO "hussein_taji" VALUES(3,24,'3:24','این (عمل آن‌ها) بدان سبب است که آنان گفتند: «آتش (دوزخ) جز چند روزی به ما نخواهد رسید». و این (افتراها) و دروغ‌هایی که می‌بافتند آنان را در دین‌شان فریفته و مغرور ساخت.');
INSERT INTO "hussein_taji" VALUES(3,25,'3:25','پس چگونه خواهد بود (حال‌شان) هنگامی‌که آن‌ها برای روزی‌که شکی در آن نیست، گرد آوریم و به هر کس (پاداش) آنچه فراهم کرده به تمام و کمال داده شود؟ و به آن‌ها ستم نخواهد شد.');
INSERT INTO "hussein_taji" VALUES(3,26,'3:26','بگو: «بارالها! ای دارنده پادشاهی و (هستی) به هر کس که بخواهی، پادشاهی (و فرمانروایی) می‌بخشی، و از هر کس بخواهی پادشاهی (و فرمانروایی) را می‌گیری، و هر کس را بخواهی عزت میدهی، و هر که را بخواهی خوار می‌کنی، همه خوبی‌ها به دست توست، بی‌شک تو بر هر چیز توانایی.');
INSERT INTO "hussein_taji" VALUES(3,27,'3:27','شب را در روز داخل می‌کنی، و روز را در شب داخل می‌کنی، و زنده را از مرده بیرون می‌آوری، و مرده را از زنده بیرون می‌آوری، و به هر کس بخواهی بی شمار روزی می‌دهی».');
INSERT INTO "hussein_taji" VALUES(3,28,'3:28','مؤمنان نباید کافران را به جای مؤمنان دوست و ولی خود بگیرند، و هر کس چنین کند، با الله هیچ رابطه‌ای ندارد و (عهد و پیمان او با الله گسسته شده است). مگر اینکه (از آزار و اذیت) آن‌ها بترسید (در ظاهر با آنان دوستی کنید). و الله شما را از (کیفر) خودش برحذر می‌دارد، و بازگشت به سوی الله است.');
INSERT INTO "hussein_taji" VALUES(3,29,'3:29','بگو: «اگر آنچه در سینه‌های شماست، پنهان دارید یا آشکارش کنید، الله آن را می‌داند، (و نیز) آنچه در آسمان‌ها و آنچه در زمین است می‌داند، و الله بر هر چیزی تواناست».');
INSERT INTO "hussein_taji" VALUES(3,30,'3:30','روزی‌که هر کس آنچه را از خیر و نیکی انجام داده، و آنچه از بدی مرتکب شده، حاضر شده می‌یابد. و آرزو می‌کند، ای کاش میان او و آن (کارهای بد) فاصله‌ای دور بود، و الله شما را از (کیفر) خودش بر حذر می‌دارد، و (در عین حال) الله به بندگان مهربان است.');
INSERT INTO "hussein_taji" VALUES(3,31,'3:31','بگو: «اگر الله را دوست می‌دارید، پس از من پیروی کنید، تا الله شما را دوست بدارد، و گناهان‌تان را برایتان بیامرزد، و الله آمرزندۀ مهربان است»');
INSERT INTO "hussein_taji" VALUES(3,32,'3:32','بگو: «خدا و پیامبر (او) را اطاعت کنید». پس اگر پشت کردند و (سر پیچی نمودند)، قطعاً الله کافران را دوست نمی‌دارد.');
INSERT INTO "hussein_taji" VALUES(3,33,'3:33','به راستی الله آدم و نوح و آل ابراهیم و آل عمران را بر جهانیان برگزید.');
INSERT INTO "hussein_taji" VALUES(3,34,'3:34','فرزندانی که بعضی از آنان از (نسل) بعضی دیگرند، و الله شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(3,35,'3:35','هنگامی‌که همسر عمران گفت: «پروردگارا! من آنچه را در شکم دارم برای تو نذر کردم که آزاد، (برای خدمت بیت‌المقدس و پرستشگر تو) باشد. پس از من بپذیر که تو خود شنوای دانایی».');
INSERT INTO "hussein_taji" VALUES(3,36,'3:36','پس هنگامی‌که او را بزاد، گفت: «پروردگارا! من او را دختر زاده ام - و الله به آنچه او زایید داناتر بود- و پسر همانند دختر نیست، و من او را مریم نام گذاردم، و من او را و فرزندانش را از (شر) شیطان رانده شده، به تو پناه می‌دهم».');
INSERT INTO "hussein_taji" VALUES(3,37,'3:37','آنگاه پروردگارش او (= مریم) را به طرز نیکویی پذیرفت، و به طرز شایسته‌ای او را رویانید (و پرورش داد) و کفالت او را به زکریا سپرد، هرگاه زکریا وارد محراب (و عبادتگاه) او می‌شد، نزد او غذا و رزقی می‌یافت، (زکریا) می‌گفت: «ای مریم این از کجا برای تو آمده است؟» (مریم) می‌گفت: «این از جانب الله است، که الله به هر کس بخواهد بی شمار روزی می‌دهد».');
INSERT INTO "hussein_taji" VALUES(3,38,'3:38','آنجا (بود که) زکریا پروردگارش را خواند، (و) گفت: «پروردگارا! از طرف خود فرزند پاکیزه‌ای به من عطا کن، به درستی تو شنونده دعایی».');
INSERT INTO "hussein_taji" VALUES(3,39,'3:39','پس در حالی‌که او ایستاده در محراب نماز می‌خواند، فرشتگان او را ندا کردند که: «الله تو را به یحیی بشارت می‌دهد، (و او) تصدیق‌کنندۀ کلمه الله (عیسی) است، و سرور و خویشتندار (از رغبت به زنان) و پیامبری از صالحان است».');
INSERT INTO "hussein_taji" VALUES(3,40,'3:40','(زکریا) گفت: «پروردگارا! چگونه مرا فرزندی خواهد بود؟ در حالی‌که پیری به سراغ من آمده، و همسرم نازا است». (فرشته) گفت: «بدین‌گونه الله هر چه بخواهد انجام می‌دهد».');
INSERT INTO "hussein_taji" VALUES(3,41,'3:41','(زکریا) گفت: «پروردگارا! برای من نشانه‌ای قرار ده». فرمود: «نشانه‌ات این است که سه روز با مردم، جز با اشاره سخن نگویی، و پروردگارت را بسیار یاد کن، و شامگاهان و صبحگاهان (او را) تسبیح بگو».');
INSERT INTO "hussein_taji" VALUES(3,42,'3:42','و (به یاد آورید) هنگامی را که فرشتگان گفتند: «ای مریم! الله تو را برگزیده و پاک ساخته و تو را بر زنان جهان برتری داده است.');
INSERT INTO "hussein_taji" VALUES(3,43,'3:43','ای مریم! فرمانبر پروردگار خود باش و سجده کن و با رکوع‌کنندگان رکوع کن».');
INSERT INTO "hussein_taji" VALUES(3,44,'3:44','(ای پیامبر!) این از خبرهای غیبی است که به تو وحی می‌کنیم، و تو در آن هنگام که قلم‌های خود را (برای قرعه‌کشی به آب) می‌افکندند تا کدام یک سرپرستی مریم را به عهده گیرد، نزد آنان نبودی، و (نیز) وقتی که با یکدیگر کشمکش می‌کردند، نزدشان نبودی.');
INSERT INTO "hussein_taji" VALUES(3,45,'3:45','و (به یاد آورید) هنگامی‌که فرشتگان گفتند: «ای مریم! الله تو را به کلمه‌ای از جانب خود، که نامش مسیح، عیسی بن مریم است؛ بشارت می‌دهد، در حالی‌که (او) در دنیا و آخرت آبرومند و از مقربان (در گاه الهی) است.');
INSERT INTO "hussein_taji" VALUES(3,46,'3:46','و در گهواره و در حالت کهولت (و میانسالی) با مردم سخن می‌گوید و از شایستگان است»');
INSERT INTO "hussein_taji" VALUES(3,47,'3:47','(مریم) گفت: «پروردگارا! چگونه مرا فرزندی خواهد بود، در حالی بشری به من دست نزده است؟» (فرشته) گفت: «الله بدینسان هر چه را بخواهد می‌آفریند، (و) هر گاه که اراده چیزی کند، فقط به آن می‌گوید: «موجود باش» پس (آن بی درنگ) موجود می‌شود.');
INSERT INTO "hussein_taji" VALUES(3,48,'3:48','و به او کتاب و حکمت و تورات و انجیل می‌آموزد.');
INSERT INTO "hussein_taji" VALUES(3,49,'3:49','و (او را به عنوان) پیامبری به سوی بنی اسرائیل (می‌فرستد که او به آنان می‌گوید:) «من از سوی پروردگارتان برایتان نشانه‌ای آورده‌ام، من از گِل برای شما (چیزی) به شکل پرنده می‌سازم، آنگاه در آن می‌دمم، پس به فرمان الله پرنده‌ای می‌شود. و به فرمان الله کور مادر زاد و مبتلایان به برص (پیسی) را بهبودی می‌بخشم، و مردگان را زنده می‌کنم، و از آنچه می‌خورید و در خانه‌هایتان ذخیره می‌کنید؛ به شما خبر می‌دهم، مسلماً در این‌ها، نشانه‌ای برای شماست اگر ایمان داشته باشید.');
INSERT INTO "hussein_taji" VALUES(3,50,'3:50','و آنچه را پیش از من از تورات بوده، تصدیق می‌کنم، و (فرستاده شده‌ام) تا بعضی از چیزهایی را که بر شما حرام شده بود، حلال کنم، و نشانه‌ای از طرف پروردگارتان برای شما آورده‌ام، پس از الله بترسید، و مرا اطاعت کنید.');
INSERT INTO "hussein_taji" VALUES(3,51,'3:51','به راستی که الله، پروردگار من و پروردگار شماست، پس او را بپرستید، این است راه راست».');
INSERT INTO "hussein_taji" VALUES(3,52,'3:52','پس هنگامی‌که عیسی از آنان احساس کفر کرد، گفت: «یاوران من (در راه دعوت) به سوی الله چه کسانی هستند؟» حواریان گفتند: «ما یاوران (دین) الله هستیم، به الله ایمان آورده‌ایم، و (تو نیز) گواه باش که ما تسلیم (او) هستیم.');
INSERT INTO "hussein_taji" VALUES(3,53,'3:53','پروردگارا! به آنچه نازل کرده‌ای، ایمان آوردیم و از فرستاده (تو) پیروی نمودیم؛ پس ما را از زمره گواهان بنویس».');
INSERT INTO "hussein_taji" VALUES(3,54,'3:54','و (یهود و دیگر دشمنان مسیح برای کشتن او) نقشه کشیدند، و الله چاره‌جوئی کرد (و مکر و نقشه آنان را نابود نمود). و الله بهترین چاره جویان است.');
INSERT INTO "hussein_taji" VALUES(3,55,'3:55','(به یاد آورید) هنگامی را که الله (به عیسی) فرمود: « ای عیسی، بی‌گمان من تو را برگرفته و به سوی خویش بالا می‌برم، و تو را از (آلایش و تهمت) کسانی‌که کافر شدند؛ پاک می‌گردانم، و کسانی را که از تو پیروی کردند، تا روز قیامت برتر از کسانی را که کافر شدند قرار می‌دهم، سپس بازگشت شما به سوی من است، آنگاه در آنچه در آن اختلاف می‌کردید در میان شما داوری می‌کنم.');
INSERT INTO "hussein_taji" VALUES(3,56,'3:56','اما کسانی‌که کافر شدند، در دنیا و آخرت آنان را به عذابی سخت عذاب می‌کنم، و برای آن‌ها یاورانی نیست.');
INSERT INTO "hussein_taji" VALUES(3,57,'3:57','و اما کسانی‌که ایمان آورده‌اند و کارهای شایسته انجام دادند، پس (الله) مزدشان را بطور کامل به آنان خواهد داد، و الله ستمکاران را دوست نمی‌دارد.');
INSERT INTO "hussein_taji" VALUES(3,58,'3:58','(ای محمد) این‌ها که بر تو می‌خوانیم از آیات (و نشانه‌های الله) و قرآن حکمت‌آمیز است.');
INSERT INTO "hussein_taji" VALUES(3,59,'3:59','همانا مثل عیسی نزد خدا؛ همچون مثل (خلقت) آدم است (که) او را از خاک آفرید، پس به او فرمود: «باش» پس (بی‌درنگ) موجود شد.');
INSERT INTO "hussein_taji" VALUES(3,60,'3:60','(آنچه درباره عیسی بیان کردیم) حق از جانب پروردگار توست، پس هرگز از تردید کنندگان مباش.');
INSERT INTO "hussein_taji" VALUES(3,61,'3:61','پس هر کس که در این (باره) پس از دانشی که به تو رسیده با تو محاجه و ستیز کند، بگو: «بیایید تا فرزندان مان و فرزندان تان و زنان مان و زنان تان، و (نفوس و) نزدیکان مان و (نفوس و) نزدیکان تان را فرا خوانیم، آنگاه مباهله کنیم، و لعنت الله را بر دروغگویان قرار دهیم».');
INSERT INTO "hussein_taji" VALUES(3,62,'3:62','حقا که این همان داستان حق و واقعی (مسیح) است، و معبودی جز الله نیست، و بی‌گمان الله است که توانمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(3,63,'3:63','پس اگر روی گردانند (بدان که) الله به (حال) تباهکاران آگاه است.');
INSERT INTO "hussein_taji" VALUES(3,64,'3:64','بگو: «ای اهل کتاب! بیایید به سوی سخنی که میان ما و شما یکسان است، که جز الله را نپرستیم، و چیزی را شریک او نسازیم، بعضی از ما بعضی دیگر را به جای الله به خدایی نگیرد». پس اگر (از این دعوت) روی گرداندند، بگویید: «گواه باشید که ما مسلمانیم».');
INSERT INTO "hussein_taji" VALUES(3,65,'3:65','ای اهل کتاب! چرا درباره ابراهیم مجادله و ستیز می‌کنید، در حالی‌که تورات و انجیل بعد از او نازل شده است؟ آیا اندیشه نمی‌کنید.');
INSERT INTO "hussein_taji" VALUES(3,66,'3:66','هان! شما (اهل کتاب) کسانی هستید که دربارۀ آنچه نسبت به آن آگاهی و علم داشتید محاجه و ستیز کردید، پس چرا در مورد چیزی که بدان علم ندارید محاجه و ستیز می‌کنید؟ و الله می‌داند و شما نمی‌دانید.');
INSERT INTO "hussein_taji" VALUES(3,67,'3:67','ابراهیم نه یهودی بود و نه نصرانی، بلکه یکتاپرستی مسلمان بود، و هرگز از مشرکان نبود.');
INSERT INTO "hussein_taji" VALUES(3,68,'3:68','به راستی سزاوار ترین مردم به ابراهیم همان کسانی هستند که او را پیروی کرده‌اند. و (نیز) این پیامبر و کسانی‌که (به او) ایمان آورده‌اند، و الله یاور و ولی مؤمنان است.');
INSERT INTO "hussein_taji" VALUES(3,69,'3:69','گروهی از اهل کتاب (آرزو می‌کردند و) دوست داشتند کاش شما را گمراه می‌کردند، در حالی‌که جز خودشان (کسی) را گمراه نمی‌کنند، و نمی‌فهمند.');
INSERT INTO "hussein_taji" VALUES(3,70,'3:70','ای اهل کتاب! چرا به آیات الله کافر می‌شوید، در حالی‌که خودتان (به صدق و درستی آن) گواهی می‌دهید؟');
INSERT INTO "hussein_taji" VALUES(3,71,'3:71','ای اهل کتاب! چرا حق را به باطل می‌آمیزید. و حق را کتمان می‌کنید در حالی‌که خودتان می‌دانید.');
INSERT INTO "hussein_taji" VALUES(3,72,'3:72','و گروهی از اهل کتاب (به همدیگر) گفتند: «به آنچه بر مؤمنان نازل شده در آغاز روز ایمان بیاورید، و در پایان (روز) کافر شوید، شاید آنان (از اسلام) برگردند».');
INSERT INTO "hussein_taji" VALUES(3,73,'3:73','و جز به کسی‌که از دین شما پیروی می‌کند؛ ایمان نیاورید، بگو: «همانا هدایت، هدایت الله است». (گفتند:) مبادا به کسی همانند آنچه به شما داده شده داده شود، یا در پیشگاه پروردگارتان با شما محاجه و ستیز کنند. بگو: « به راستی فضل و برتری به دست الله است، آن را به هر کس که بخواهد می‌دهد و الله گشایشگر داناست.');
INSERT INTO "hussein_taji" VALUES(3,74,'3:74','هر کس را که بخواهد به رحمت خود مخصوص می‌گرداند، و الله دارای فضل و بخشش بزرگ است».');
INSERT INTO "hussein_taji" VALUES(3,75,'3:75','و از اهل کتاب کسی است که اگر او را بر مال فراوانی امین شمری، آن را به تو باز می‌گرداند، و از آنان کسی است که اگر او را بر دیناری امین شمری آن را به تو باز نمی‌گرداند، مگر تا زمانی که بر (سر) او ایستاده باشی، این بدان خاطر است که آنان می‌گویند: «در مورد (خوردن مال) امییّن (عرب‌ها، غیر یهود) گناهی بر ما نیست». و بر الله دروغ می‌بندند، در حالی‌که می‌دانند.');
INSERT INTO "hussein_taji" VALUES(3,76,'3:76','آری، (گناه هست) هر که به پیمان خود وفا کند و پرهیزگاری نماید، پس بی‌گمان الله پرهیزگاران را دوست دارد.');
INSERT INTO "hussein_taji" VALUES(3,77,'3:77','همانا کسانی‌که پیمان الله و سوگندهای خود را به بهای ناچیزی می‌فروشند، آن‌ها بهره‌ای در آخرت نخواهند داشت، و الله در روز قیامت با آن‌ها سخن نمی‌گوید، و به آنان نمی‌نگرد و آن‌ها را (از گناه) پاک نمی‌سازد و عذاب دردناکی برای آن‌هاست.');
INSERT INTO "hussein_taji" VALUES(3,78,'3:78','و از میان آن‌ها (= یهود) گروهی هستند که زبان خود را به (هنگام تلاوت) کتاب (آسمانی) چنان می‌گردانند که گمان کنید (آنچه را می‌خوانند) از کتاب (آسمان) است، در حالی‌که از کتاب (آسمانی) نیست. و می‌گویند: «آن از جانب الله است»؛ در صورتی که از جانب الله نیست، و بر الله دروغ می‌بندند در حالی‌که خودشان (هم) می‌دانند.');
INSERT INTO "hussein_taji" VALUES(3,79,'3:79','برای هیچ بشری سزاوار نیست که الله به او کتاب و حکم و پیامبری بدهد، سپس او به مردم بگوید: «به جای خدا، بندگان من باشید». بلکه (سزاوار پیامبران این است که به مردم بگویند:) «به سبب آنکه کتاب (آسمانی) آموزش می‌دادید و از آن رو که درس می‌خواندید (مردمانی) ربانی و الهی باشید».');
INSERT INTO "hussein_taji" VALUES(3,80,'3:80','و نه اینکه به شما فرمان دهد که فرشتگان و پیامبران را به خدایی بگیرید، آیا شما را پس از آنکه مسلمان شدید، به کفر فرمان می‌دهد؟!');
INSERT INTO "hussein_taji" VALUES(3,81,'3:81','و (به یاد بیاورید) هنگامی را که الله، از پیامبران پیمان گرفت که هرگاه به شما کتاب و حکمت دادم، سپس پیامبری به سوی شما آمد که آنچه را با شماست تصدیق می‌کند، باید به او ایمان بیاورید و او را یاری کنید. (آنگاه) فرمود: «آیا اقرار کردید، و بر این امر پیمانم را پذیرفتید؟» گفتند: «اقرار کردیم» فرمود: «پس گواه باشید و من (نیز) با شما از گواهانم.');
INSERT INTO "hussein_taji" VALUES(3,82,'3:82','پس هر کس که بعد از این (پیمان) روی گرداند، آنانند که فاسق و نافرمانند».');
INSERT INTO "hussein_taji" VALUES(3,83,'3:83','آیا (کافران) جز دین الله را می‌جویند؟ حال آنکه هر که در آسمان‌ها و زمین است خواه و ناخواه سر به فرمان او نهاده است، و به سوی او باز گردانده می‌شوند.');
INSERT INTO "hussein_taji" VALUES(3,84,'3:84','بگو: «به الله و آنچه بر ما نازل شده، و آنچه بر ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط (= فرزندان یعقوب) نازل گردیده و آنچه به موسی و عیسی و (دیگر) پیامبران از جانب پروردگارشان داده شده؛ ایمان آوردیم، میان هیچ یک از آنان فرقی نمی‌گذاریم، و ما او را فرمانبرداریم».');
INSERT INTO "hussein_taji" VALUES(3,85,'3:85','و هر کس که دینی غیر از اسلام بر گزیند، هرگز از او پذیرفته نخواهد شد، و او در آخرت از زیانکاران است.');
INSERT INTO "hussein_taji" VALUES(3,86,'3:86','چگونه الله قومی را هدایت می‌کند که بعد از ایمان‌شان و (پس از آنکه) گواهی دادند به اینکه پیامبر بر حق است، و برایشان دلایل روشن آمد، کافر شدند؟! و الله قوم ستمکار را هدایت نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(3,87,'3:87','آنان جزای‌شان این است که لعنت الله و فرشتگان و مردم همگی بر آن‌هاست.');
INSERT INTO "hussein_taji" VALUES(3,88,'3:88','در آن (لعنت) جاودانه می‌مانند، نه عذاب از ایشان کاسته می‌شود، و نه آنان مهلت یابند.');
INSERT INTO "hussein_taji" VALUES(3,89,'3:89','مگر کسانی‌که پس از آن توبه کردند و اصلاح و درستکاری نمودند، پس بی‌گمان الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(3,90,'3:90','همانا کسانی‌که پس از ایمان خود کافر شدند، پس بر کفر (خود) افزودند، هرگز توبه آنان پذیرفته نخواهند شد، و آنانند که خود گمراهانند.');
INSERT INTO "hussein_taji" VALUES(3,91,'3:91','به درستی کسانی‌که کافر شدند و در حال کفر مردند، اگر چه زمین را پر از طلا کنند، و آن را (برای خود) فدیه دهند، هرگز از هیچ یک از آن‌ها پذیرفته نمی‌شود، آنان را عذابی دردناک خواهد بود، و یاورانی نخواهند داشت.');
INSERT INTO "hussein_taji" VALUES(3,92,'3:92','هرگز به نیکوکاری نمی‌رسید تا از آنچه دوست دارید انفاق کنید، و آنچه انفاق می‌کنید، بی‌شک الله به آن آگاه است.');
INSERT INTO "hussein_taji" VALUES(3,93,'3:93','همه غذاها برای بنی اسرائیل حلال بود، مگر آنچه پیش از نزول تورات اسرائیل (= یعقوب) بر خود حرام کرده بود. بگو: «اگر راست می‌گویید، تورات را بیاورید و آن را بخوانید».');
INSERT INTO "hussein_taji" VALUES(3,94,'3:94','پس کسی‌که بعد از این بر الله دروغ بندد، آنانند که ستمکارند.');
INSERT INTO "hussein_taji" VALUES(3,95,'3:95','بگو: «خدا راست گفت، پس از آیین ابراهیم که یکتا پرست و حق گرا بود، و از مشرکان نبود، پیروی کنید».');
INSERT INTO "hussein_taji" VALUES(3,96,'3:96','براستی نخستین خانه‌ای که برای (عبادت) مردم قرار داده شده، همان است که در مکه است، که پر برکت و (مایه) هدایت جهانیان است.');
INSERT INTO "hussein_taji" VALUES(3,97,'3:97','در آن نشانه‌های روشن، (از جمله) مقام ابراهیم است. و هر کس داخل آن (حرم) شود؛ در امان خواهد بود، و برای خدا، حج خانه (کعبه) بر مردم واجب است، (البته) کسانی‌که توانایی رفتن به سوی آن دارند، و هر کس کفر ورزد، بی‌شک الله از جهانیان بی‌نیاز است.');
INSERT INTO "hussein_taji" VALUES(3,98,'3:98','بگو: «ای اهل کتاب! چرا به الله کفر می‌ورزید، حال آنکه الله بر آنچه می‌کنید؛ گواه است».');
INSERT INTO "hussein_taji" VALUES(3,99,'3:99','بگو: «ای اهل کتاب! چرا کسی را که ایمان آورده‌است، از راه الله باز می‌دارید، و می‌خواهید آن (راه) را کج نشان دهید؟! در حالی‌که شما (به راستی آن) گواه هستید، و الله از آنچه می‌کنید، غافل نیست».');
INSERT INTO "hussein_taji" VALUES(3,100,'3:100','ای کسانی‌که ایمان آورده‌اید! اگر از گروهی از اهل کتاب اطاعت کنید، شما را بعد از ایمان‌تان به کفر بر می‌گردانند.');
INSERT INTO "hussein_taji" VALUES(3,101,'3:101','و چگونه شما کافر مى‌شوید در حالی‌که آیات الله بر شما خوانده می‌شود و پیامبر او در میان شماست؟! و هر کس که به الله تمسک جوید؛ قطعاً به راه راست، هدایت شده‌است.');
INSERT INTO "hussein_taji" VALUES(3,102,'3:102','ای کسانی‌که ایمان آورده‌اید! از الله؛ آن گونه که شایسته ترسیدن از او است؛ بترسید، و نمیرید مگر اینکه مسلمان باشید.');
INSERT INTO "hussein_taji" VALUES(3,103,'3:103','و همگی به ریسمان الله (= قرآن و اسلام) چنگ زنید و پراکنده نشوید، و نعمت الله را بر خود یاد کنید، آنگاه که دشمنان (یکدیگر) بودید، پس میان دل‌های شما الفت داد، آنگاه به (فضل) نعمت او برادر (یکدیگر) شدید، و شما بر لبه گودالی از آتش بودید، (او) شما را از آن نجات داد. این گونه الله آیات خود را برای شما روشن می‌سازد، باشد که شما راه یابید.');
INSERT INTO "hussein_taji" VALUES(3,104,'3:104','و باید از میان شما، گروهی باشند که (مردم را) به نیکی دعوت کنند، و به کار شایسته و ا دارند و از زشتی باز دارند، و آنانند که رستگارند.');
INSERT INTO "hussein_taji" VALUES(3,105,'3:105','و مانند کسانی نباشید که پراکنده شدند و اختلاف کردند پس از آنکه دلایل روشن برایشان آمد، و اینان برایشان عذاب بزرگی است.');
INSERT INTO "hussein_taji" VALUES(3,106,'3:106','روزی‌که چهره‌هایی سفید و چهره‌هایی سیاه می‌گردد، اما آن‌ها که چهره‌هایشان سیاه شده (به آنان گفته می‌شود) «آیا بعد از ایمان تان کافر شدید؟!پس به سبب آنچه کفر می‌ورزیدید؛ عذاب را بچشید».');
INSERT INTO "hussein_taji" VALUES(3,107,'3:107','و اما آن‌ها که چهره‌هایشان سفیده شده است، در رحمت الله (= بهشت) خواهند بود، آنان در آن جاویدانند.');
INSERT INTO "hussein_taji" VALUES(3,108,'3:108','این‌ها آیات الله است، که آن را به حق بر تو می‌خوانیم، و الله (هیچ گاه) ستمی برای جهانیان نمی‌خواهد.');
INSERT INTO "hussein_taji" VALUES(3,109,'3:109','و آنچه در آسمان‌ها و آنچه در زمین است از آن الله است، و (همه) کارها، به سوی الله باز گردانده می‌شود.');
INSERT INTO "hussein_taji" VALUES(3,110,'3:110','شما بهترین امتی هستید که برای مردم پدیدار شده‌اید: امر به معروف می‌کنید و نهی از منکر می‌نمائید، و به الله ایمان دارید. و اگر اهل کتاب ایمان می‌آوردند؛ قطعاً برایشان بهتر بود، برخی از آنان مؤمنند و بیشتر آنان فاسق و نافرمانند.');
INSERT INTO "hussein_taji" VALUES(3,111,'3:111','(اهل کتاب) هرگز به شما زیانی نخواهند رسانید؛ جز آزاری اندک، و اگر با شما بجنگند، به شما پشت خواهند کرد، سپس یاری نمی‌شوند.');
INSERT INTO "hussein_taji" VALUES(3,112,'3:112','هر کجا یافته شوند، (مهر) ذلت و خواری بر آنان زده شده است. مگر آنکه در پناه امان خدا، و امان مردم باشند. و به خشمی از الله گرفتار شده‌اند، و (مهر) بیچارگی بر آن‌ها زده شده، این بدان سبب بود که به آیات الله کفر می‌ورزیدند و پیامبران را به ناحق می‌کشتند، (و نیز) این (سزا) بخاطر آن بود که نافرمانی کردند و از حدود (الهی) تجاوز می‌نمودند.');
INSERT INTO "hussein_taji" VALUES(3,113,'3:113','(ولی همۀ آنان) یکسان نیستند، از اهل کتاب گروهی هستند که درست‌کردارند، در دل شب در حالی‌که به نماز ایستاده‌اند، آیات الله را می‌خوانند.');
INSERT INTO "hussein_taji" VALUES(3,114,'3:114','به الله و روز قیامت ایمان دارند و امر به معروف می‌کنند و از کار زشت و منکر باز می‌دارند، و در (انجام) کارهای نیک، می‌شتابند، و آنان از شایستگانند. [یعنی تمام اهل کتاب چنانکه در آیات قبلی مورد نکوهش و مذمت قرار گرفته‌اند یکسان نیستند، بلکه از میان آن‌ها کسانی هستند که به دین اسلام گرویدند مانند: عبدالله بن سلام، اسد بن عبید، ثعلبه بن سعیه و اسید بن سعیه و غیره، و همچنین در میان آن‌ها اهل تقوا و نیکوکار نیز وجود دارد.]');
INSERT INTO "hussein_taji" VALUES(3,115,'3:115','و هر کار نیکی انجام دهند، هرگز کفران نخواهد شد، و الله به (حال) پرهیزگاران آگاه است.');
INSERT INTO "hussein_taji" VALUES(3,116,'3:116','بی‌گمان کسانی‌که کافر شدند، هرگز اموال‌شان و فرزندان‌شان چیزی از (عذاب) الله را از آنان دفع نخواهد کرد. و آنان اهل آتشند، آن‌ها در آن جاودانه خواهند بود.');
INSERT INTO "hussein_taji" VALUES(3,117,'3:117','مثل آنچه (کافران) در این زندگی دنیا انفاق می‌کنند؛ همانند بادی است که در آن سرمای سخت و سوزانی است که به کشتزار قومی که بر خود ستم کرده‌اند؛ برسد، و آن را نابود سازد. الله به آن‌ها ستم نکرده، بلکه آن‌ها خود بر خویشتن ستم کرده‌اند.');
INSERT INTO "hussein_taji" VALUES(3,118,'3:118','ای کسانی‌که ایمان آورده‌اید، از غیر خودتان (کافران) (دوست و) محرم اسرار نگیرید، آن‌ها از هیچ نابکاری در حق شما کوتاهی نمی‌کنند. آن‌ها دوست دارند که شما در رنج و زحمت باشید. به راستی دشمنی از دهان (و زبان) شان آشکار شده‌است، و آنچه سینه‌هایشان پنهان می‌دارد؛ بزرگتر است، بی‌شک ما آیات و نشانه‌ها (ی دشمنی آنان) را برای شما بیان کردیم، اگر اندیشه کنید.');
INSERT INTO "hussein_taji" VALUES(3,119,'3:119','هان! شما کسانی هستید که آن‌ها را دوست می‌دارید، در حالی‌که آن‌ها شما را دوست ندارند، و شما به همه کتاب (های آسمانی) ایمان دارید، (و آن‌ها به کتاب شما ایمان ندارند). و چون با شما ملاقات می‌کنند می‌گویند: «ایمان آورده‌ایم». و هنگامی‌که تنها می‌شوند، از شدت خشم بر شما، سر انگشتان خود را به دندان می‌گزند. بگو: «به خشم خود بمیرید». بی‌گمان الله به (راز) درون سینه‌ها آگاه است.');
INSERT INTO "hussein_taji" VALUES(3,120,'3:120','اگر به شما نیکی (و خوشی) برسد؛ آن‌ها را ناراحت می‌کند، و اگر به شما بدی (و گزندی) برسد؛ بدان خوشحال می‌شوند. و اگر شما صبر کنید و پرهیزگاری نمایید، مکر و نیرنگ شان هیچ زیانی به شما نمی‌رسد، بی‌شک الله به آنچه می‌کنند؛ احاطه دارد.');
INSERT INTO "hussein_taji" VALUES(3,121,'3:121','و (به یاد آور) زمانی را که (در جنگ احد) بامدادان از میان خانواده خود بیرون آمدی (و) مؤمنان را برای جنگیدن در مواضعی می‌گماردی، و الله شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(3,122,'3:122','و (نیز به یاد آور) هنگامی‌که دو گروه از شما (= بنوسلمه و بنوحارثه) خواستند که سستی ورزند، و الله یاور (و نگهدار) آن‌ها بود، و مؤمنان باید تنها بر الله توکل کنند.');
INSERT INTO "hussein_taji" VALUES(3,123,'3:123','و بی‌گمان الله شما را در (جنگ) بدر یاری کرد در حالی‌که شما ناتوان بودید، پس از الله بترسید، باشد که سپاسگزاری نمایید.');
INSERT INTO "hussein_taji" VALUES(3,124,'3:124','آنگاه که به مؤمنان می‌گفتی: «آیا شما را کافی نیست، که پروردگارتان شما را با سه هزار فرشته که (از آسمان) فرود می‌آیند، یاری کند؟»');
INSERT INTO "hussein_taji" VALUES(3,125,'3:125','آری، اگر صبر کنید و پرهیزگاری نمایید، و (دشمن) با همین جوش (و خروش) سراغ شما بیاید. پروردگارتان شما را به پنج هزار فرشته نشاندار یاری خواهد کرد.');
INSERT INTO "hussein_taji" VALUES(3,126,'3:126','و الله آن (یاری با فرشتگان) را جز مژده‌ای برای (پیروزی) شما قرار نداد، تا (بدین وسیله) دل‌های شما بدان آرامش گیرد، و گرنه نصر و پیروزی جز از جانب الله توانمند حکیم نیست.');
INSERT INTO "hussein_taji" VALUES(3,127,'3:127','تا گروهی از کسانی‌که کافر شده‌اند؛ هلاک و نابود کند، یا آنان را خوار و مغلوب نماید، آنگاه مأیوس و ناامیدانه بازگردند.');
INSERT INTO "hussein_taji" VALUES(3,128,'3:128','هیچ چیز از کار (بندگان) در دست تو نیست، یا (الله) توبه آنان را می‌پذیرد (و آنان را می‌بخشاید) یا عذاب‌شان می‌کند، زیرا آنان ستمکارند.');
INSERT INTO "hussein_taji" VALUES(3,129,'3:129','و آنچه در آسمان‌ها، و آنچه در زمین است، از آن الله است، هر کس را که بخواهد می‌آمرزد، و هر کس را بخواهد عذاب می‌کند، و الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(3,130,'3:130','ای کسانی‌که ایمان آورده‌اید! ربا را چند برابر نخورید، و از الله بترسید تا رستگار شوید.');
INSERT INTO "hussein_taji" VALUES(3,131,'3:131','و از آتشی که برای کافران آماده شده‌است بترسید.');
INSERT INTO "hussein_taji" VALUES(3,132,'3:132','و الله و پیامبر را اطاعت کنید، تا مورد رحمت (خدا) قرار گیرید.');
INSERT INTO "hussein_taji" VALUES(3,133,'3:133','و به سوی آمرزش پروردگارتان، و بهشتی که پهنای آن (به قدر) آسمان‌ها و زمین است، (و) برای پرهیزگاران آماده شده‌است، بشتابید.');
INSERT INTO "hussein_taji" VALUES(3,134,'3:134','آن کسانی‌که در توانگری و تنگدستی انفاق می‌کنند، و خشم خود را فرو می‌برند، و از مردم در می‌گذرند، و الله نیکوکاران را دوست می‌دارد.');
INSERT INTO "hussein_taji" VALUES(3,135,'3:135','و کسانی‌که چون مرتکب کار زشتی شوند یا بر خود ستم کنند، الله را به یاد می‌آورند، پس برای گناهان‌شان آمرزش می‌خواهند، و چه کسی جز الله گناهان را می‌آمرزد؟ و به آنچه کرده‌اند، در حالی‌که می‌دانند (که گناه است) پا فشاری و اصرار نمی‌کنند.');
INSERT INTO "hussein_taji" VALUES(3,136,'3:136','آن‌ها پاداش‌شان آمرزشی از (جانب) پروردگارشان و باغ‌هایی است که از زیر (درختان) آن جوی‌ها جاری است، جاودانه در آن می‌مانند، و پاداش (نیکو کاران و) اهل عمل چه خوب است.');
INSERT INTO "hussein_taji" VALUES(3,137,'3:137','بی‌شک پیش از شما (ملت‌هایی و) سنت‌هایی گذشته است، پس در زمین گردش کنید و ببینید؛ سرانجام تکذیب کنندگان چگونه است؟');
INSERT INTO "hussein_taji" VALUES(3,138,'3:138','این (قرآن) بیانی است برای مردم و هدایت و اندرزی برای پرهیزگاران است.');
INSERT INTO "hussein_taji" VALUES(3,139,'3:139','و سست نشوید و اندوهگین نگردید، و شما برتر هستید؛ اگر مؤمن باشید.');
INSERT INTO "hussein_taji" VALUES(3,140,'3:140','اگر (در روز احد) به شما زخم و آسیبی رسید؛ به راستی به آن گروه (کافر نیز در روز بدر) زخم و آسیبی همانند آن رسید. و ما این روزها (ی شکست و پیروزی) را در میان مردم می‌گردانیم. و تا الله افرادی را که (واقعاً) ایمان آورده‌اند؛ معلوم بدارد، و از میان شما شاهدانی بگیرد، و الله ستمکاران را دوست نمی‌دارد.');
INSERT INTO "hussein_taji" VALUES(3,141,'3:141','و تا الله کسانی را که ایمان آورده‌اند؛ پاک و خالص گرداند، و کافران را نابود سازد.');
INSERT INTO "hussein_taji" VALUES(3,142,'3:142','آیا پنداشتید که (تنها با ادعای ایمان) به بهشت داخل می‌شوید؛ در حالی‌که الله هنوز مجاهدان از شما معلوم نداشته، و صابران را مشخص و جدا نساخته است؟!');
INSERT INTO "hussein_taji" VALUES(3,143,'3:143','و به راستی شما مرگ (و شهادت) را پیش از آنکه با آن روبرو شوید، آرزو می‌کردید، پس آن را دیدید، در حالی‌که به آن نگاه می‌کردید.');
INSERT INTO "hussein_taji" VALUES(3,144,'3:144','و محمد جز فرستاده‌ای نیست، که به راستی پیش از او (نیز) فرستادگانی (بوده‌اند و) گذشتند. آیا اگر بمیرد یا کشته شود، شما به عقب می‌گردید (و عقیده و دین خود را رها می‌کنید؟) و هر کس به عقب بازگردد (و عقیده و دین راستین را رها کند) هرگز به الله زیانی نمی‌رساند، و به زودی الله سپاسگزاران را پاداش می‌دهد.');
INSERT INTO "hussein_taji" VALUES(3,145,'3:145','و هیچ کسی جز به فرمان الله نمی‌میرد، سر نوشتی است معین و مشخص شده، و هر کس که پاداش این دنیا را بخواهد از آن به او می‌دهیم، و هر کس که پاداش آخرت را بخواهد، از آن به او می‌دهیم، و بزودی سپاسگزاران را پاداش خواهیم داد.');
INSERT INTO "hussein_taji" VALUES(3,146,'3:146','و چه بسیار پیامبرانی که همراه او توده‌های انبوه، مردان خدایی نبرد کردند، آن‌ها هیچ گاه در برابر آنچه در راه الله بدیشان رسید، سستی نورزیدند و ناتوان نشدند، و زبونی نشان ندادند (و تسلیم دشمن نشدند) و الله شکیبایان را دوست دارد.');
INSERT INTO "hussein_taji" VALUES(3,147,'3:147','و سخن آنان جز این نبود که گفتند: «پروردگارا! گناهان ما را بیامرز، و از زیاده روی ما، در کارمان (بر ما بیامرز) و گام‌های ما را استوار بدار و ما را بر گروه کافران، پیروز گردان».');
INSERT INTO "hussein_taji" VALUES(3,148,'3:148','پس الله پاداش این دنیا و پاداش نیک آخرت را به آن‌ها عطا کرد، و الله نیکوکاران را دوست دارد.');
INSERT INTO "hussein_taji" VALUES(3,149,'3:149','ای کسانی‌که ایمان آورده‌اید! اگر از کسانی‌که کافر شده‌اند؛ اطاعت کنید، شما را (به کفر) بر می‌گردانند، آنگاه زیان دیده بر می‌گردید.');
INSERT INTO "hussein_taji" VALUES(3,150,'3:150','(بر کافران تکیه نکنید) بلکه، الله یاور و مولای شما است، و او بهترین یاری دهندگان است.');
INSERT INTO "hussein_taji" VALUES(3,151,'3:151','به زودی در دل‌های کسانی‌که کافر شدند، بیم و ترس خواهیم افکند؛ بخاطر اینکه چیزی را شریک الله قرار داده‌اند که هیچگونه دلیلی بر (حقانیت) آن نازل نکرده‌است. و جایگاه آن‌ها آتش است، و جایگاه ستمکاران چه بد است.');
INSERT INTO "hussein_taji" VALUES(3,152,'3:152','و به راستی الله، وعده خود را با شما (در جنگ احد) تحقق بخشید، آنگاه که در (درآغاز جنگ) به فرمان او، آنان را می‌کشتید. تا اینکه سست شدید و در کار (جنگ و جمع‌آوری غنیمت) با یکدیگر به نزاع پرداختید، و بعد از آنکه آنچه را دوست می‌داشتید (از پیروزی بر دشمن) به شما نشان داد، نافرمانی کردید، بعضی از شما خواهان دنیا بود، و بعضی خواهان آخرت. سپس برای آنکه شما را آزمایش کند از (تعقیب) آنان منصرف‌تان کرد، (و پیروزی شما به شکست انجامید). و از (معصیت و تقصیر) شما (که نافرمانی از دستور پیامبر بود) در گذشت. و الله نسبت به مؤمنان فضل و بخشش دارد.');
INSERT INTO "hussein_taji" VALUES(3,153,'3:153','و (بیاد آورید) هنگامی را که (در حال گریز از کوه) بالا می‌رفتید، و به هیچ کس توجه نمی‌کردید. و پیامبر از پشت سرتان شما را صدا می‌زد، پس (الله) با غم و اندوهی در پی غم و اندوهی (دیگر) به شما سزا داد. این بدین خاطر بود که بر آنچه از دست داده‌اید و بر آنچه به شما رسیده‌است؛ اندوهگین نشوید. و الله از آنچه می‌کنید؛ آگاه است.');
INSERT INTO "hussein_taji" VALUES(3,154,'3:154','سپس بعد از آن غم و اندوه، آرامشی (به صورت) خواب سبکی بر شما فرو فرستاده، که گروهی از شما را فرا گرفت. و گروهی دیگر (= منافقین) در فکر جان خود بودند، و دربارۀ خدا، گمان‌های ناروا و باطل، همچون گمان‌های زمان جاهلیت داشتند. می‌گفتند: «آیا چیزی از نصرت و پیروزی نصیب ما می‌شود؟» بگو: «همه کارها (شکست و پیروزی) به دست الله است». آن‌ها چیزی را در دل‌های شان پنهان می‌دارند که برای تو آشکار نمی‌سازند، می‌گویند: «اگر ما را در این کار اختیاری بود (و بهره‌ای از پیروزی داشتیم) در اینجا کشته نمی‌شدیم». بگو: «اگر شما در خانه‌های خود هم بودید، کسانی‌که کشته شدن بر آنان نوشته شده بود؛ قطعاً به قتلگاه‌های خود، بیرون می‌آمدند». و (این‌ها) برای این است که الله، آنچه را در سینه‌هایتان (پنهان) دارید، بیازماید، و آنچه را در دل‌های تان است؛ خالص و پاک گرداند. و الله به (راز) درون سینه‌ها آگاه است.');
INSERT INTO "hussein_taji" VALUES(3,155,'3:155','به راستی کسانی از شما که در روز بر خورد دو گروه (مسلمانان و کافران در جنگ احد) فرار کردند، در حقیقت شیطان آنان را به سبب بعضی از کردار (و گناهانی) که مرتکب شده بودند، بلغزانید، و قطعاً الله از آنان در گذشت، بی‌گمان الله آمرزندۀ بردبار است.');
INSERT INTO "hussein_taji" VALUES(3,156,'3:156','ای کسانی‌که ایمان آورده‌اید! مانند کسانی نباشید که کفر ورزیدند، و درباره برادران‌شان چون به سفر رفتند (و مردند) یا در جنگ شرکت کردند (و کشته شدند) گفتند: «اگر آن‌ها نزد ما بودند، نمی‌مردند و کشته نمی‌شدند» تا الله این (گفتار) را حسرتی در دل‌های شان قرار دهد، و الله، زنده می‌کند و می‌میراند (زندگی و مرگ همه به دست الله است) و الله به اعمال شما بیناست.‌');
INSERT INTO "hussein_taji" VALUES(3,157,'3:157','و اگر در راه الله کشته شوید یا بمیرید، بی‌گمان آمرزش و رحمت از (جانب) الله، از آنچه آن‌ها جمع می‌کنند، بهتر است.');
INSERT INTO "hussein_taji" VALUES(3,158,'3:158','و اگر بمیرید یا کشته شوید؛ قطعاً به سوی الله محشور می‌شوید.');
INSERT INTO "hussein_taji" VALUES(3,159,'3:159','پس به (سبب) رحمت الهی است که تو با آنان نرم خو (و مهربان) شدی، و اگر تند خو و سخت دل بودی، قطعا از اطراف تو پراکنده می‌شدند، پس از آنان در گذر و برایشان آمرزش بخواه، و در کارها، با آنان مشورت کن، و آنگاه که تصمیم گرفتی بر الله توکل کن، زیرا که الله توکل کنندگان را دوست می‌دارد.');
INSERT INTO "hussein_taji" VALUES(3,160,'3:160','اگر الله شما را یاری کند، هیچ کس بر شما پیروز نخواهد شد، و اگر شما را به خود واگذارد (و یاری‌تان نکند) پس چه کسی بعد از او شما را یاری خواهد کرد؟ و مؤمنان باید تنها بر الله توکّل کنند.');
INSERT INTO "hussein_taji" VALUES(3,161,'3:161','و هیچ پیامبری را نسزد که خیانت کند، و هر کس خیانت کند، روز قیامت، با آنچه (در آن) خیانت کرده بیاید. سپس به هر کس آنچه را کسب کرده‌است (از نیکی یا بدی) بطور کامل (پاداش یا سزا) داده می‌شود، و به آنان ستم نخواهد شد.');
INSERT INTO "hussein_taji" VALUES(3,162,'3:162','آیا کسی‌که خشنودی الله را پیروی می‌کند، همانند کسی است که به خشم الله دچار گردد و جایگاه او جهنم است، و (آن) چه بد جائی است.');
INSERT INTO "hussein_taji" VALUES(3,163,'3:163','(هر یک از) ایشان، درجاتی در نزد الله دارند، و الله به آنچه می‌کنند؛ بیناست.');
INSERT INTO "hussein_taji" VALUES(3,164,'3:164','به راستی که الله بر مؤمنان منّت نهاد، هنگامی‌که در میان آنان پیامبری از خودشان بر انگیخت، که آیات او (= قرآن) را بر آن‌ها می‌خواند و پاکیزه شان می‌دارد و به آنان کتاب (= قرآن) و حکمت (= سنت) می‌آموزد، و اگر چه پیش از آن در گمراهی آشکاری بودند.');
INSERT INTO "hussein_taji" VALUES(3,165,'3:165','آیا هنگامی‌که مصیبتی (در جنگ احد) به شما رسید، در حالی‌که دو برابر آن را (در جنگ بدر به کافران) رسانده بودید، گفتید: «این (مصیبت) از کجا است؟» بگو: «آن از جانب خود تان است» (و نتیجه نا فرمانی دستور پیامبر است) بی‌گمان الله بر هر چیز تواناست.');
INSERT INTO "hussein_taji" VALUES(3,166,'3:166','و آنچه (در روز احد) روزی‌که دو گروه (مؤمنان و کافران) با هم برخورد کردند، به شما رسید، به فرمان الله بود، و برای این بود که مؤمنان را معلوم بدارد.');
INSERT INTO "hussein_taji" VALUES(3,167,'3:167','و (نیز) کسانی‌که نفاق ورزیدند معلوم بدارد. و به ایشان گفته شد: «بیایید در راه الله بجنگید یا (حداقل از خود و شهرتان) دفاع کنید». گفتند: «اگر می‌دانستیم جنگی روی خواهد داد، مسلماً از شما پیروی می‌کردیم». آنان در آن هنگام، به کفر نزدیک‌تر بودند تا به ایمان، به زبان خویش چیزی می‌گویند که در دل‌های شان نیست، و الله به آنچه کتمان می‌کنند؛ آگاه‌تر است.');
INSERT INTO "hussein_taji" VALUES(3,168,'3:168','کسانی‌که (خود در خانه) نشستند و دربارۀ برادران خود گفتند: «اگر آن‌ها از ما پیروی می‌کردند، کشته نمی‌شدند» بگو: «اگر راست می‌گویید؛ پس مرگ را از خودتان دور سازید».');
INSERT INTO "hussein_taji" VALUES(3,169,'3:169','و هرگز کسانی را که در راه الله کشته شده‌اند؛ مرده مپندار، بلکه زنده‌اند، نزد پروردگارشان روزی داده می‌شوند.');
INSERT INTO "hussein_taji" VALUES(3,170,'3:170','به آنچه الله از فضل و کرم خود به ایشان داده‌است، شادمانند، و به کسانی‌که هنوز به آن‌ها نپیوسته‌اند، خوش وقتند، که نه بیمی بر آن‌هاست و نه اندوهگین می‌شوند.');
INSERT INTO "hussein_taji" VALUES(3,171,'3:171','به نعمت و فضل الله شادمانند، و اینکه (می بینند) الله پاداش مؤمنان را ضایع نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(3,172,'3:172','کسانی‌که دعوت الله و پیامبر را پس از آنکه (در میدان احد) به آنان زخم و جراحت رسید اجابت کردند. (و برای تعقیب مشرکان به سوی «حمراء الأسد» رفتند) برای کسانی از آن‌ها که نیکی و پرهیزگاری کردند، پاداش بزرگی است.');
INSERT INTO "hussein_taji" VALUES(3,173,'3:173','کسانی‌که مردم به آنان گفتند: «مردم (= مشرکان مکه) برای (جنگ با) شما گرد آمده‌اند، پس از آن‌ها بترسید». (این سخن) بر ایمان‌شان افزوده و گفتند: «الله ما را بس است، و بهترین حامی است».');
INSERT INTO "hussein_taji" VALUES(3,174,'3:174','پس به نعمت و فضل الله (از میدان جنگ) بازگشتند، هیچ آسیبی به آنان نرسید، و خشنودی الله را پیروی کردند، و الله دارای فضل و بخشش بزرگی است.');
INSERT INTO "hussein_taji" VALUES(3,175,'3:175','(در حقیقت) این شیطان است که دوستانش را می‌ترساند، پس اگر ایمان دارید از آن‌ها نترسید، و از من بترسید.');
INSERT INTO "hussein_taji" VALUES(3,176,'3:176','و کسانی‌که در (یاری) کفر شتاب می‌کنند، تو را اندوهگین نسازد! یقیناً آن‌ها هرگز زیانی به الله نمی‌رسانند، الله می‌خواهد که بهره‌ای برای آنان در آخرت قرار ندهد و برای آن‌ها، عذاب بزرگی است.');
INSERT INTO "hussein_taji" VALUES(3,177,'3:177','به راستی کسانی‌که کفر را به (بهای) ایمان خریدند، هرگز به الله زیانی نمی‌رسانند، و برای آن‌ها، عذاب دردناکی است.');
INSERT INTO "hussein_taji" VALUES(3,178,'3:178','و کسانی‌که کافر شدند، مپندارید که به آنان مهلت می‌دهیم، برایشان بهتر است، ما فقط به آن‌ها مهلت می‌دهیم؛ تا بر گناه (خود) بیفزایند، و برای آن‌ها عذاب خوار کننده‌ای است.');
INSERT INTO "hussein_taji" VALUES(3,179,'3:179','چنین نبود که الله، مؤمنان را به این (حالی) که شما بر آن هستید، و ا گذارد، تا آنکه پلید را از پاک جدا سازد، و چنین نبود که الله شما را از غیب آگاه کند، ولی الله از میان فرستادگانش، هر کس را بخواهد بر می‌گزیند، پس به الله و فرستادگانش ایمان بیاورید، و اگر ایمان بیاورید و تقوا پیشه کنید، پاداش بزرگی برای شماست.');
INSERT INTO "hussein_taji" VALUES(3,180,'3:180','و کسانی‌که به آنچه الله از فضل خویش به آنان داده، بخل می‌ورزند، گمان نکنند که آن (بخل) برای آنان خیر است. بلکه برای آن‌ها شّر است، به زودی آنچه را که نسبت به آن بخل ورزیدن، روز قیامت طوق گردن‌شان می‌شود، و میراث آسمان‌ها و زمین از آن الله است، و الله به آنچه می‌کنید؛آگاه است.');
INSERT INTO "hussein_taji" VALUES(3,181,'3:181','به راستی که الله، سخن کسانی را که گفتند: «خدا فقیر است، و ما بی نیازیم!» شنید، به زودی آنچه را گفتند، و به ناحق کشتن پیامبران را خواهیم نوشت، و می‌گوییم: «بچشید عذاب سوزان را».');
INSERT INTO "hussein_taji" VALUES(3,182,'3:182','این (عقوبت) بخاطر چیزی است که دست های شما از پیش فرستاده‌است، و (گرنه) الله هرگز به بندگان (خود) ستمگر نیست.');
INSERT INTO "hussein_taji" VALUES(3,183,'3:183','کسانی‌که گفتند: «الله با ما عهد و پیمان بسته، که به هیچ پیامبری ایمان نیاوریم، تا برای ما قربانیی بیاورد که آتش (= صاعقه آسمانی) آن را بخورد» بگو: «قطعاً پیامبرانی پیش از من، برای شما آمدند با معجزه‌ها و دلایل روشن، و آنچه را گفتید، (برای شما آوردند) پس اگر راست می‌گویید چرا آن‌ها را کشتید؟!»');
INSERT INTO "hussein_taji" VALUES(3,184,'3:184','پس اگر تو را تکذیب کردند، (غمگین مباش) پیامبرانی (هم) که پیش از تو، دلایل روشن، و نوشته‌ها و کتاب روشن آورده بودند، تکذیب شدند.');
INSERT INTO "hussein_taji" VALUES(3,185,'3:185','هر کس چشنده (طعم) مرگ است، و همانا روز قیامت پاداش‌هایتان را بطور کامل به شما داده می‌شود، پس هر که از آتش (دوزخ) دور داشته شد، و به بهشت در آورده شد، قطعاً رستگار شده‌است. و زندگی دنیا چیزی جز مایه فریب نیست.');
INSERT INTO "hussein_taji" VALUES(3,186,'3:186','قطعاً در اموال‌تان و جان‌هایتان، آزمایش می‌شوید، و از کسانی‌که پیش از شما به آن‌ها کتاب (آسمانی) داده شده‌است (= یهود و نصاری) و (نیز) از کسانی‌که شرک ورزیدند، سخنان آزار دهندۀ بسیاری خواهید شنید، و اگر صبر کنید، و تقوا پیشه سازید، (بدانید که) این از کارهای مهم و سترگ است.');
INSERT INTO "hussein_taji" VALUES(3,187,'3:187','و (به یاد بیاورید) هنگامی را که الله، از کسانی‌که کتاب (آسمانی) به آن‌ها داده شده، پیمان گرفت، که حتماً آن را برای مردم بیان کنید، و آن را کتمان نکنید، پس آن (عهد) را پشت سر خود انداختند، و به بهای کمی فروختند، چه بد است آنچه می‌خرند؟!');
INSERT INTO "hussein_taji" VALUES(3,188,'3:188','گمان مبر کسانی‌که به آنچه کرده‌اند خوشحال می‌شوند، و دوست دارند به آنچه نکرده‌اند ستوده شوند، قطعاً گمان مبر از عذاب (الهی) نجات و رهایی یابند، (بلکه) برای آن‌ها، عذاب دردناکی است.');
INSERT INTO "hussein_taji" VALUES(3,189,'3:189','و فرمانروایی آسمان‌ها و زمین از آن الله است، و الله بر هر چیزی تواناست.');
INSERT INTO "hussein_taji" VALUES(3,190,'3:190','به راستی در آفرینش آسمان‌ها و زمین، و آمد و رفت شب و روز، نشانه‌های (آشکاری) برای خردمندان است.');
INSERT INTO "hussein_taji" VALUES(3,191,'3:191','کسانی‌که الله را در حال ایستاده و نشسته، و بر پهلوی خویش (آرمیده) یاد می‌کنند، و در آفرینش آسمان‌ها و زمین می‌اندیشند، (و می‌گویند:) پروردگارا این‌ها را بیهوده نیافریده‌ای، منزهّی تو! پس ما را از عذاب آتش (دوزخ) نگاه دار.');
INSERT INTO "hussein_taji" VALUES(3,192,'3:192','پروردگارا! کسی را که تو به آتش (دوزخ) درآوری، یقیناً او را خوار و رسوا کرده‌ای، و برای ستمکاران یاورانی نیست.');
INSERT INTO "hussein_taji" VALUES(3,193,'3:193','پروردگارا! ما شنیدیم که ندا دهنده‌ای به ایمان فرا می‌خواند، که: «به پروردگار خود ایمان بیاورید» پس ایمان آوردیم، پروردگارا! پس گناهان ما را ببخش، و بدی‌های ما را بپوشان، و ما را با نیکان بمیران.');
INSERT INTO "hussein_taji" VALUES(3,194,'3:194','پروردگارا! آنچه را که بر (زبان) فرستادگانت به ما وعده داده‌ای به ما عطا کن، و ما را در روز قیامت رسوا مگردان، بدرستی که تو خلاف وعده نمی‌کنی.');
INSERT INTO "hussein_taji" VALUES(3,195,'3:195','پس پروردگارشان دعای آنان را اجابت کرد. (و فرمود:) من عمل هیچ عمل کننده‌ای از شما را، زن باشد یا مرد؛ تباه و ضایع نمی‌کنم، برخی از برخی دیگرید (و همنوعید). پس، کسانی‌که هجرت کردند و از خانه‌های خود رانده شدند، و در راه من آزار دیدند و جنگیدند و کشته شدند، قطعاً گناهان‌شان را می‌بخشم و آنان را به باغ‌های (بهشتی) که از زیر (درختان) آن نهرها جاری است، در می‌آورم. (این) پاداشی است از جانب خدا، و پاداش نیکو تنها نزد الله است.');
INSERT INTO "hussein_taji" VALUES(3,196,'3:196','رفت و آمد کافران در شهرها، تو را نفریبد!');
INSERT INTO "hussein_taji" VALUES(3,197,'3:197','(این) متاع ناچیزی است، پس جایگاه‌شان دوزخ است، و چه بد جایگاهی است.');
INSERT INTO "hussein_taji" VALUES(3,198,'3:198','ولی کسانی‌که از پروردگارشان ترسیدند، برای آن‌ها باغ‌هایی است که از زیر (درختان) آن نهرها جاری است، جاودانه در آن می‌مانند، (این) پذیرائی از جانب الله است، و آنچه در نزد الله است؛ برای نیکوکاران بهتر است.');
INSERT INTO "hussein_taji" VALUES(3,199,'3:199','و از اهل کتاب کسانی هستند که به خدا، و آنچه بر شما نازل شده و آنچه بر خودشان نازل شده؛ ایمان دارند، در برابر (فرمان) الله خاشع و فروتنند، و آیات الله را به بهای ناچیزی نمی‌فروشند، اینانند که پاداش‌شان را در نزد پروردگارشان خواهند داشت، بی‌گمان الله سریع‌الحساب (زود شمار) است.');
INSERT INTO "hussein_taji" VALUES(3,200,'3:200','ای کسانی‌که ایمان آورده‌اید! صبر کنید، و دیگران را به صبر و شکیبایی فرا خوانید (و پایداری کنید) و مرزها را نگهبانی کنید، و از الله بترسید، شاید رستگار شوید.');
INSERT INTO "hussein_taji" VALUES(4,1,'4:1','ای مردم از پروردگارتان بترسید، آن ذاتی که شما را از یک تن آفرید، و همسرش را (نیز) از او آفرید، و از آن دو، مردان و زنان بسیاری پراکنده کرد، و از پروردگاری که به (نام) او از همدیگر درخواست می‌کنید، و (همچنین) از (گسستن) پیوند خویشاوندی بپرهیزید. بی‌گمان الله همواره بر شما مراقب (و نگهبان) است.');
INSERT INTO "hussein_taji" VALUES(4,2,'4:2','و به یتیمان اموال‌شان را بدهید، و (اموال) ناپاک (خود) را با (اموال) پاک (یتیمان) عوض نکنید، و اموال آنان را همراه اموال خودتان (با مخلوط کردن) نخورید، به راستی که این گناه بزرگی است.');
INSERT INTO "hussein_taji" VALUES(4,3,'4:3','و اگر ترسیدید که در حق دختران یتیم نتوانید عدالت را رعایت کنید، (از ازدواج با آن‌ها صرف نظر کنید و) با زنانی (دیگر) که مورد پسند شماست دو دو، و سه سه، چهار چهار، ازدواج کنید. پس اگر بیم دارید که نتوانید عدالت کنید، به یک (زن) یا به آنچه (از کنیزان) که مالک آن‌هایید (اکتفا نمایید) این (کار) نزدیک‌تر است به اینکه ستم نکنید.');
INSERT INTO "hussein_taji" VALUES(4,4,'4:4','و مهریه زنان را به عنوان هدیه (و فریضه‌ای الهی) با طیب خاطر به آنان بدهید، پس اگر آنان چیزی از آن را با رضایت خاطر به شما بخشیدند، آن را حلال و گوارا بخورید.');
INSERT INTO "hussein_taji" VALUES(4,5,'4:5','و اموال خود را که الله آن را وسیله قوام (زندگی) شما قرار داده‌است، به سفیهان (و کم خردان) ندهید، و از آن، به آن‌ها روزی دهید، و آنان را لباس بپوشانید، و با آنان سخن شایسته بگویید.');
INSERT INTO "hussein_taji" VALUES(4,6,'4:6','و یتیمان را بیازمایید تا وقتی که به (سن) ازدواج (و بلوغت) رسند، پس اگر در ایشان رشد (کافی) یافتید، اموال‌شان را به آنان بدهید، و آن را (از بیم) آنکه بزرگ شوند، به اسراف و شتاب مخورید، و هر کس که بی‌نیاز است؛ باید (از گرفتن حق الزحمه) خود داری کند، و هر کس که نیازمند است؛ باید به طرز شایسته (و به‌اندازه حق‌الزحمه و نیاز خود از آن) بخورد، پس هر گاه اموال‌شان را به آن‌ها باز گرداندید؛ بر ایشان گواه بگیرید، و الله برای محاسبه کافی است.');
INSERT INTO "hussein_taji" VALUES(4,7,'4:7','برای مردان، از آنچه پدر و مادر و خویشاوندان (از خود) بر جای گذاشته‌اند؛ سهمی است، و برای زنان (نیز) از آنچه پدر و مادر و خویشاوندان بر جای گذاشته‌اند، سهمی است، خواه آن (مال)کم باشد یا زیاد، این بهره و سهمی فرض و تعین شده‌است.');
INSERT INTO "hussein_taji" VALUES(4,8,'4:8','و هر گاه، خویشاوندان و یتیمان و مستمندان بر تقسیم (میراث) حاضر شدند، (چیزی) از آن (اموال) به آنان بدهید، و با آنان بطور شایسته و نیک سخن بگویید.');
INSERT INTO "hussein_taji" VALUES(4,9,'4:9','و کسانی‌که اگر فرزندان ناتوانی پس از خود بر جای بگذارند، بر (آینده) آنان می‌ترسند؛ باید (از ستم دربارۀ یتیمان مردم نیز) بترسند، پس باید که از الله بترسند و سخنی سنجیده و درست بگویند.');
INSERT INTO "hussein_taji" VALUES(4,10,'4:10','بی‌گمان کسانی‌که اموال یتیمان را به ستم می‌خورند، جز این نیست که آتش را در شکم خود (فرو می‌برند و) می‌خورند، و به زودی به آتشی برافروخته درآیند.');
INSERT INTO "hussein_taji" VALUES(4,11,'4:11','الله دربارۀ فرزندان‌تان به شما سفارش می‌کند، سهم پسر، چون سهم دو دختر است. پس اگر (فرزندان میت همه) دختر (دو دختر یا) بیش از دو دختر باشند، دو سوم ترکه بهره ایشان است، و اگر یک دختر باشد، نیمی از ترکه از آن اوست. و برای هر یک از پدر و مادر وی (= میت) یک ششم از ترکه می‌باشد، در صورتی که (میت) فرزندی داشته باشد، پس اگر فرزندی نداشته باشد و (تنها) پدر و مادرش از او ارث برند، برای مادرش یک سوم است (و باقی از آن پدر خواهد بود) و اگر او (= میت) برادرانی داشته باشد، سهم مادرش یک ششم است، (همه این‌ها) پس از انجام وصیتی است که او (= میت) به آن سفارش کرده و بعد از ادای دین است، شما نمی‌دانید پدران‌تان و فرزندان‌تان کدام یک برای شما سودمندترند. (این احکام) فریضه‌ای (معین و مقرر شده) از جانب الله است و بی‌گمان الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(4,12,'4:12','و برای شما نصف ترکه همسران تان است، اگر آن‌ها فرزندی نداشته باشند، و اگر فرزندی داشته باشند یک چهارم ترکه از آن شماست [در هنگام نبودن فرزند، نوه‌ها حکم فرزند را دارند. در این مورد اجماع علما هست.]. پس از انجام وصیتی که به آن سفارش کرده‌اند، و بعد از ادای دین (آن‌ها) است. و برای زنان شما یک چهارم ترکه شماست، اگر شما فرزندی نداشته باشید، پس اگر فرزندی داشته باشید یک هشتم از ترکه شما از آن آن‌هاست، بعد از انجام وصیتی که به آن سفارش کرده‌اید و بعد از ادای دین. و اگر مرد یا زنی که از او ارث می‌برند کلاله (= بی فرزند و بی پدر) باشد، و برای او برادر یا خواهر (مادری) باشد؛ پس برای هر یک از آن دو یک ششم است، و اگر آن‌ها بیش از این باشند؛ در یک سوم شریکند، پس از انجام وصیتی که به آن سفارش شده و بعد از ادای دَین، (به شرط آنکه) زیانی (به ورثه) نرساند، این (حکم و) سفارش الله است، و الله دانای بردبار است.');
INSERT INTO "hussein_taji" VALUES(4,13,'4:13','این‌ها (احکام و) حدود الهی است، و هر کس از الله و پیامبرش اطاعت کند، و ی را به باغ‌هایی در آورد که از زیر (درختان) آن نهرها جاری است، در آن جاودانه‌اند، و این پیروزی بزرگی است.');
INSERT INTO "hussein_taji" VALUES(4,14,'4:14','و هر کس از الله و پیامبرش نافرمانی کند و از حدود او تجاوز کند، وی را در آتشی وارد می‌کند که جاودانه در آن خواهد ماند، و برای او عذاب خوار کننده‌ای است.');
INSERT INTO "hussein_taji" VALUES(4,15,'4:15','و کسانی از زنان شما که مرتکب زنا می‌شود، چهار نفر از خودتان بر آنان گواه گیرید، پس اگر گواهی دادند، آنان (= زنان) را در خانه‌ها نگاه دارید، تا مرگ‌شان فرا رسد، یا الله راهی برای آنان قرار دهد [ حکم این آیه با آیه سورۀ نور منسوخ گردیده است. (به تفسیر ابن کثیر رجوع کنید)].');
INSERT INTO "hussein_taji" VALUES(4,16,'4:16','و از میان شما، آن مرد و زنی که مرتکب آن (عمل زشت) می‌شوند؛ آن‌ها را آزار دهید، پس اگر توبه کردند و درستکار شدند، از آن‌ها صرف نظر کنید، زیرا الله توبه‌پذیر مهربان است.');
INSERT INTO "hussein_taji" VALUES(4,17,'4:17','جز این نیست، که (پذیرش) توبه از سوی خدا، برای کسانی است که از روی جهالت و نادانی کار بدی انجام می‌دهند. سپس به زودی توبه می‌کنند، اینانند که الله توبه شان را می‌پذیرد، و الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(4,18,'4:18','و برای کسانی‌که کارهای بد مرتکب می‌شوند، تا وقتی که مرگ یکی از ایشان فرا رسد؛ می‌گوید: «اکنون توبه کردم» توبه نیست، و نه برای کسانی‌که در حال کفر می‌میرند، این‌ها کسانی هستند که برایشان عذاب دردناکی آماده کردیم.');
INSERT INTO "hussein_taji" VALUES(4,19,'4:19','ای کسانی‌که ایمان آورده‌اید! برای شما حلال نیست که از زنان به اکراه ارث برید، و آنان را زیر فشار قرار ندهید، تا برخی از آنچه را که به آنان [از مَهر] داده‌اید؛ پس بگیرید، مگر اینکه آن‌ها مرتکب عمل زشت آشکاری شوند، و با آن‌ها بطور شایسته رفتار کنید، و اگر از آنان (خوشتان نیامد و) کراهت داشتید، پس چه بسا چیزی را خوش نمی‌دارید، و الله در آن خیر بسیار قرار می‌دهد.');
INSERT INTO "hussein_taji" VALUES(4,20,'4:20','و اگر خواستید همسری (دیگر) به جای همسر (پیشین خود) برگزینید، و به یکی از آنان مال فراوانی (بعنوان مهریه) پرداخته اید، چیزی ازآن را پس نگیرید، آیا آن (مال) رابه بهتان و گناه آشکار بازپس می‌گیرید؟');
INSERT INTO "hussein_taji" VALUES(4,21,'4:21','و چگونه آن را بازپس می‌گیرید در حالی‌که شما با یکدیگرآمیزش نموده‌اید، و آنان (هنگام عقد ازدواج) از شما پیمانی محکم و استوار گرفته‌اند؟!');
INSERT INTO "hussein_taji" VALUES(4,22,'4:22','و با زنانی که پدران‌تان (باآن‌ها) ازدواج کرده‌اند، ازدواج نکنید، مگر آنچه (زمان جاهلیت بوده و) گذشته است، چرا که این کار، عملی زشت و منفور و راه و روش بسیار بدی است.');
INSERT INTO "hussein_taji" VALUES(4,23,'4:23','حرام شده‌است بر شما: مادران‌تان، و دختران‌تان، و خواهران‌تان، و عمه‌هایتان، و خاله‌هایتان، و دختران برادر، و دختران خواهر، و مادران‌تان که به شما شیر داده‌اند، و خواهران رضاعی شما، و مادران زنان‌تان، و دختران همسران‌تان که در دامان شما پرورش یافته‌اند از همسرانی که با آن‌ها همبستری کرده‌اید، پس اگر با آن‌ها همبستری نکرده‌اید؛ بر شما گناهی نیست (که با دختران‌شان ازدواج کنید) و (همچنین) زنان پسران‌تان که از پشت و صلب خودتان هستند، و (نیز) جمع میان دو خواهر (بر شما حرام است) مگر آنچه که در گذشته رخ داده باشد که الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(4,24,'4:24','و زنان شوهردار (بر شما حرام است) مگر زنانی که مالک آنان شده‌اید. (این) فریضۀ الهی است که بر شما مقرر داشته است. و غیر از این‌ها (که گفته شد) برای شما حلال است با اموال خود (زنان دیگر را) طلب کنید ـ در حالی‌که پاکدامن باشید و زناکار نباشد ـ پس آن زنانی را که (به ازدواج در آوردید و) از آنان کام گرفتید، باید مهرشان را به عنوان فریضه‌ای به آنان بدهید، و گناهی بر شما نیست در آنچه بعد از تعیین مهریه با یکدیگر توافق کنید، بی‌گمان الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(4,25,'4:25','و هر کس از شما از لحاظ مالی توانایی ازدواج با زنان آزاده مؤمن؛ نداشته باشد، پس با کنیزان با ایمانی که ملک یمین شما هستند (ازدواج کند) و الله به ایمان شما داناتر است. برخی از برخی هستند، پس آنان را با اجازه صاحبان‌شان به ازدواج خود در آورید، و مهرشان به طور پسندیده به آنان بدهید. (به شرط آنکه) پاکدامن باشند، نه زناکار، و نه آنان که در پنهانی دوست گیرند. پس چون ازدواج کردند، اگر مرتکب زنا شدند، پس مجازات‌شان؛ نصف مجازات زنان آزاد (پنجاه تازیانه) است [ غلام یا کنیز چه متأهل باشد یا غیر متأهل اگر مرتکب زنا گردد پنجاه ضربۀ شلاق زده می‌شود و بر او حکم رجم (سنگساری) جاری نخواهد گردید.]. این (اجازه ازدواج با کنیزان) برای کسانی از شماست که از آلایش گناه بترسد، و صبر (و پاکدامنی) پیشه کردن؛ برایتان بهتر است. و الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(4,26,'4:26','الله می‌خواهد که (احکام خویش را) برای شما روشن سازد، و شما را به راه و روش کسانی‌که پیش از شما بودند؛ راهنمایی کند، و الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(4,27,'4:27','و الله می‌خواهد توبه شما را بپذیرد (و از آلودگی پاک نماید) و کسانی‌که از خواسته‌های نفسانی پیروی می‌کنند؛ می‌خواهند شما دستخوش انحراف بزرگی شوید.');
INSERT INTO "hussein_taji" VALUES(4,28,'4:28','الله می‌خواهد کار را بر شما سبک و آسان کند، و انسان، ضعیف و ناتوان آفریده شده‌است.');
INSERT INTO "hussein_taji" VALUES(4,29,'4:29','ای کسانی‌که ایمان آورده‌اید! اموال یکدیگر را به باطل (و از راه‌های نامشروع) نخورید، مگر اینکه تجارتی با رضایت شما (انجام گرفته) باشد. و خودتان را نکشید [ یعنی خودکشی نکنید]، زیرا الله (نسبت) به شما مهربان است.');
INSERT INTO "hussein_taji" VALUES(4,30,'4:30','و هر کس که روی تجاوز و ستم چنین کند، پس بزودی او را در آتشی (سوزان) در آوریم، و این بر الله آسان است.');
INSERT INTO "hussein_taji" VALUES(4,31,'4:31','اگر از گناهان بزرگی که از آن نهی شده‌اید؛ دوری کنید، گناهان (صغیره) شما را از شما می‌زداییم، و شما را در جایگاه خوبی (= بهشت) وارد می‌کنیم.');
INSERT INTO "hussein_taji" VALUES(4,32,'4:32','آنچه را که الله بدان بعضی از شما را بر بعضی دیگر برتری بخشیده‌است، آرزو مکنید، مردان را از آنچه به دست آورده‌اند؛ بهره‌ای است، و زنان را (نیز) از آنچه به دست آورده‌اند؛ بهره‌ای است، و از الله از فضل و بخشش او بخواهید، بی‌گمان الله به هر چیزی داناست.');
INSERT INTO "hussein_taji" VALUES(4,33,'4:33','و ما برای هر کس، وارثانی قرار داده‌ایم، که از آنچه پدر و مادر و خویشاوندان، بر جای گذاشته‌اند؛ ارث ببرند، و (نیز) کسانی‌که شما (با آنان) پیمان بسته‌اید؛ نصیب‌شان را بدهید. بی‌گمان الله بر هر چیزی گواه است.');
INSERT INTO "hussein_taji" VALUES(4,34,'4:34','مردان بر زنان سر پرست و نگهبانند، بخاطر آنکه الله برخی از ایشان را بر برخی برتری داده، و (نیز) بخاطر آنکه از اموال‌شان خرج می‌کنند. پس زنان صالح، فرمانبردارند، (و) به پاس آنچه الله (برای آنان) حفظ کرده، (اسرار و حقوق شوهران خود) را در غیبت (آنان) حفظ می‌کنند. و زنانی را که نافرمانی آن‌ها بیم دارید، پند و اندرز شان دهید، (اگر فرمانبردار نشد) در بستر از ایشان دوری کنید، (و اگر تأثیر نکرد) آنان را بزنید، پس اگر از شما اطاعت کردند، هیچ راهی برای (سرزنش و) تعدّی بر آن‌ها مجویید، (بدانید) که الله، بلند مرتبۀ بزرگ است.');
INSERT INTO "hussein_taji" VALUES(4,35,'4:35','و اگر از اختلاف و جدائی میان آن دو (زن و شوهر) بیم داشتید، پس داوری از خانواده شوهر و داوری از خانواده زن (تعیین کنید و) بفرستید، اگر این دو (داور) قصد اصلاح داشته باشند، الله میان آن دو (زن و شوهر) سازگاری خواهد داد. بی‌گمان الله دانای آگاه است.');
INSERT INTO "hussein_taji" VALUES(4,36,'4:36','و الله را بپرستید، و چیزی را با او شریک مگردانید. و به پدر و مادر نیکی کنید، و (نیز) به خویشاوندان و یتیمان و بی‌نوایان و همسایه خویشاوند و همسایه بیگانه و همنشین و در راه‌ماندگان و بردگانی که مالک آن‌ها هستید، (نیکی کنید) بی‌گمان الله کسی را که متکبّر و فخر فروش است؛ دوست نمی‌دارد.');
INSERT INTO "hussein_taji" VALUES(4,37,'4:37','کسانی‌که بخل می‌ورزند، و مردم را به بخل وا می‌دارند، و آنچه را که الله از فضل خویش به آن‌ها داده، کتمان می‌نمایند، و برای کافران (و ناسپاسان) عذابی خوار کننده آماده کرده‌ایم.');
INSERT INTO "hussein_taji" VALUES(4,38,'4:38','و (نیز برای) کسانی‌که اموال‌شان را برای نشان دادن به مردم انفاق می‌کنند، و به الله و روز آخرت ایمان ندارند، و کسی‌که شیطان همدم او باشد، چه بد همدمی است.');
INSERT INTO "hussein_taji" VALUES(4,39,'4:39','و اگر آن‌ها به الله و روز آخرت ایمان می‌آوردند، و از آنچه الله به آنان روزی داده، انفاق می‌کردند؛ چه زیانی برای آنان داشت؟! و الله به (حال و اعمال) آن‌ها داناست.');
INSERT INTO "hussein_taji" VALUES(4,40,'4:40','بی‌گمان الله به‌اندازۀ ذره‌ای ستم نمی‌کند، و اگر کار نیکی با شد، آن را دو چندان می‌کند، و از نزد خود، پاداش بزرگی عطا می‌کند.');
INSERT INTO "hussein_taji" VALUES(4,41,'4:41','پس چگونه است (حال‌شان)آنگاه که از هر امتی گواهی آوریم، و تو را بر اینان گواه آوریم؟!');
INSERT INTO "hussein_taji" VALUES(4,42,'4:42','آن روز، کسانی‌که کافر شدند، و از پیامبر نافرمانی کردند، آرزو می‌کنند که ای کاش با خاک یکسان می‌شدند، و هیچ سخنی را نمی‌توانند از الله پنهان کنند.');
INSERT INTO "hussein_taji" VALUES(4,43,'4:43','ای کسانی‌که ایمان آورده‌اید! در حال مستی به نماز نزدیک نشوید، تا بدانید چه می‌گویید، و (نیز) در حال جنابت، تا غسل کنید، مگر اینکه رهگذار باشید. و اگر بیمار، یا مسافر بودید، و یا یکی از شما از مکان قضای حاجت آمد، یا با زنان آمیزش کرده‌اید، و آب نیافتید، پس با خاک پاک تیمّم کنید، (به این روش که) چهره و دست‌هایتان را (با آن) مسح کنید، بی‌گمان الله بخشندۀ آمرزنده ‌است.');
INSERT INTO "hussein_taji" VALUES(4,44,'4:44','آیا ندیدی کسانی را که بهره‌ای از کتاب به آن‌ها داده شده‌است، گمراهی را می‌خرند، و می‌خواهند شما (نیز) گمراه شوید؟');
INSERT INTO "hussein_taji" VALUES(4,45,'4:45','و الله به دشمنان شما داناتر است، و کافی است که الله سر پرست (شما) باشد، و کافی است که الله یاور (شما) باشد.');
INSERT INTO "hussein_taji" VALUES(4,46,'4:46','برخی از یهود، کلمات (خدا) را از جای خود تحریف می‌کنند، می گویند: «شنیدیم و نافرمانی کردیم» و (نیز می‌گویند:) «بشنو، که هرگز نشنوی». و (از روی تمسخر می‌گویند: «راعنا (= ما را تحمیق کن)» تا با پیچانیدن زبان خود به دین (اسلام) طعنه بزنند، ولی اگر آن‌ها می‌گفتند: «شنیدیم و اطاعت کردیم، و (سخنان ما را) بشنو و به ما مهلت بده». قطعاً برای آنان بهتر و درست تر بود، ولی الله آن‌ها را بخاطر کفر شان لعنت کرده‌است، لذا جز (گروهی) اندک ایمان نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(4,47,'4:47','ای کسانی‌که کتاب (آسمانی) به شما داده شده‌است! به آنچه (بر پیامبر خود) نازل کردیم، و تصدیق‌کنندۀ همان چیزی است که با شماست، ایمان بیاورید، پیش از آنکه صورت‌هایی را محو کنیم، آنگاه آن‌ها را به پشت سر باز گردانیم، یا آن‌ها را لعنت کنیم، همان‌گونه که اصحاب شنبه (گروهی از یهود که نافرمانی کردند و در روز شنبه ماهی گرفتند) را لعنت کردیم. و فرمان الله انجام شدنی است.');
INSERT INTO "hussein_taji" VALUES(4,48,'4:48','بی‌گمان الله این را که به او شرک آورده شود، نمی‌بخشد، و غیر از آن را برای هر کس بخواهد می‌بخشد، و هر کس که به الله شرک ورزد، یقیناً گناهی بزرگ بر بافته است.');
INSERT INTO "hussein_taji" VALUES(4,49,'4:49','آیا ندیدی کسانی را که خویشتن را پاک می‌شمارند، (و خودستائی می‌کنند) بلکه الله هر کس را بخواهد پاک می‌گرداند، و به آن‌ها اندازۀ نخ روی هسته خرمایی ستم نمی‌شوند.');
INSERT INTO "hussein_taji" VALUES(4,50,'4:50','ببین چگونه بر الله دروغ می‌بندند، و همین کافی است که این، گناه آشکاری باشد.');
INSERT INTO "hussein_taji" VALUES(4,51,'4:51','آیا ندیدی کسانی را که بهره‌ای از کتاب (آسمانی) به آنان داده شدند، به جبت (= بت و سحر) و طاغوت (= معبودان باطل) ایمان می‌آورند، و درباره کسانی‌که کفر ورزیده‌اند می‌گویند: «اینان از کسانی‌که ایمان آورده‌اند، هدایت یافته‌ترند»؟!');
INSERT INTO "hussein_taji" VALUES(4,52,'4:52','آن‌ها کسانی هستند که الله لعنت شان کرده، و هر که را الله لعنت کند، هرگز برای او یاوری نخواهی یافت.');
INSERT INTO "hussein_taji" VALUES(4,53,'4:53','آیا آن‌ها بهره‌ای از فرمانروایی و حکومت دارند؟ پس در این صورت به‌اندازه پوست هسته خرمایی (چیزی) به مردم نمی‌دادند.');
INSERT INTO "hussein_taji" VALUES(4,54,'4:54','آیا به مردم (= پیامبر و یارانش) بر آنچه الله از فضل خویش به آنان عطا کرده حسد می‌ورزند؟ در حقیقت ما به خاندان ابراهیم کتاب و حکمت دادیم، و به آنان فرمانروایی بزرگ بخشیدیم.');
INSERT INTO "hussein_taji" VALUES(4,55,'4:55','پس برخی از آنان به وی (= محمد صلی الله علیه وسلم) ایمان آوردند و برخی از آنان از او روی گرداندند، و (برای آن‌ها) آتش افروختۀ جهنم کافی است.');
INSERT INTO "hussein_taji" VALUES(4,56,'4:56','بی‌گمان کسانی‌که به آیات ما کفر ورزیدند، بزودی آن‌ها را در آتشی (سوزان) وارد می‌کنیم که هر چه پوست‌شان بریان گردد، پوست‌های دیگری به جایش قراردهیم، تا (طعم) عذاب را بچشند، حقا که الله توانمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(4,57,'4:57','و کسانی‌که ایمان آوردند و کارهای شایسته انجام دادند، بزودی آن‌ها را به باغ‌هایی (از بهشت) وارد می‌کنیم، که از زیر (درختان) آن نهرها جاری است، همیشه در آن خواهند ماند، و در آنجا همسران پاکیزه دارند، و آنان را به سایه‌های گسترده (و آرام بخش) در آوریم.');
INSERT INTO "hussein_taji" VALUES(4,58,'4:58','بی‌گمان الله به شما فرمان می‌دهد که امانت‌ها را به صاحبان آن‌ها باز گردانید. و هنگامی‌که میان مردم داوری می‌کنید، به عدالت داوری کنید، در حقیقت، نیکو چیزی است که الله شما را به آن اندرز می‌دهد، بی‌گمان الله شنوای بیناست.');
INSERT INTO "hussein_taji" VALUES(4,59,'4:59','ای کسانی‌که ایمان آورده‌اید! اطاعت کنید الله را، و اطاعت کنید پیامبر، و صاحبان امر تان را، و اگر در چیزی اختلاف کردید، آن را به الله و پیامبر باز گردانید؛ اگر به الله و روز قیامت ایمان دارید، این بهتر و خوش فرجام‌تر است.');
INSERT INTO "hussein_taji" VALUES(4,60,'4:60','آیا ندیده‌ای کسانی را که گمان می‌کنند به آنچه بر تو نازل شده و به آنچه پیش از تو نازل شده‌است؛ ایمان آورده‌اند، (ولی) می‌خواهند برای داوری نزد طاغوت (و حکام سرکش) بروند با آن که به آن‌ها دستور داده شده که به او کفر ورزند، و شیطان می‌خواهد گمراه شان کند، (و به) گمراهی دوری (بیفکند).');
INSERT INTO "hussein_taji" VALUES(4,61,'4:61','و چون به آن‌ها گفته شود: «به سوی آنچه الله نازل کرده، و به سوی پیامبر بیایید» منافقان را می‌بینی که از تو سخت روی می‌گردانند.');
INSERT INTO "hussein_taji" VALUES(4,62,'4:62','پس چگونه وقتی که به خاطر اعمال بدشان، مصیبتی به آنان برسد، آنگاه نزد تو می‌آیند، سوگند به الله یاد می‌کنند که منظور ما (از رفتن برای داوری نزد دیگران) جز نیکی و توافق (میان طرفین) نبوده‌است.');
INSERT INTO "hussein_taji" VALUES(4,63,'4:63','آنان کسانی هستند که الله آنچه را در دل دارند، می داند. پس از آنان روی برگردان، و اندرزشان بده، و با بیانی رسا (نتایج)کردار شان را به آن‌ها گوش زد کن.');
INSERT INTO "hussein_taji" VALUES(4,64,'4:64','ما هیچ پیامبری را نفرستادیم؛ مگر برای اینکه به فرمان الله (از وی) اطاعت شود. و اگر آن‌ها هنگامی‌که بر خویشتن ستم می‌کردند، نزد تو می‌آمدند، و از الله طلب آمرزش می‌کردند، بی‌گمان الله را توبه‌پذیر مهربان می‌یافتند.');
INSERT INTO "hussein_taji" VALUES(4,65,'4:65','نه، سوگند به پروردگارت که آن‌ها ایمان نمی‌آورند، مگر اینکه در اختلافات خویش تو را داور قرار دهند، و سپس از داوری تو، در دل خود احساس ناراحتی نکنند و کاملاً تسلیم باشند.');
INSERT INTO "hussein_taji" VALUES(4,66,'4:66','و اگر (مانند بنی اسرائیل) بر آنان مقرر می‌کردیم که خود را بکشید، یا از خانه و شهرتان بیرون روید، جز اندکی از آن‌ها عمل نمی‌کردند، و اگر اندرز های که به آنان داده می‌شود انجام می‌دادند، برای آن‌ها بهتر بود و (برای ایمان‌شان) استوار تر بود.');
INSERT INTO "hussein_taji" VALUES(4,67,'4:67','و در این صورت از جانب خود پاداش بزرگی به آن‌ها می‌دادیم.');
INSERT INTO "hussein_taji" VALUES(4,68,'4:68','و به راه راست، هدایت‌شان می‌کردیم.');
INSERT INTO "hussein_taji" VALUES(4,69,'4:69','و کسی‌که الله و پیامبر را اطاعت کند (روز قیامت) همنشین کسانی خواهد بود که الله بر آنان انعام نموده‌است، از پیامبران، و صدیقان و شهدا و صالحان، و اینان چه نیکو رفیقانی هستند.');
INSERT INTO "hussein_taji" VALUES(4,70,'4:70','این فضل و بخشایش از جانب الله است، و کافی است که او دانا است.');
INSERT INTO "hussein_taji" VALUES(4,71,'4:71','ای کسانی‌که ایمان آورده‌اید! سلاح خود را بر گیرید، آنگاه گروه گروه بیرون روید یا یک‌پارچه (به سوی دشمن) بیرون شوید.');
INSERT INTO "hussein_taji" VALUES(4,72,'4:72','و به راستی از (میان) شما کسی است که (از شرکت در جهاد) سستی می‌کند، پس اگر مصیبتی به شما برسد، می‌گوید: الله بر من انعام کرد که با آنان حاضر نبودم.');
INSERT INTO "hussein_taji" VALUES(4,73,'4:73','و اگر فضل و غنیمتی از جانب الله به شما برسد، چنان که گویا هرگز میان شما و آنان دوستی و مودتی نبوده، می‌گویند: ای کاش من هم با آن‌ها بودم، و به کامیابی بزرگی نائل می‌شدم.');
INSERT INTO "hussein_taji" VALUES(4,74,'4:74','پس کسانی‌که زندگی دنیا را به آخرت فروخته‌اند، باید در راه الله پیکار کنند، و آن کسی‌که در راه الله پیکار کند، چه کشته شود یا پیروز گردد، به زودی پاداش بزرگی خواهیم داد.');
INSERT INTO "hussein_taji" VALUES(4,75,'4:75','و شما را چه شده‌است که در راه الله و (برای یاری) مردان و زنان و کودکان، مستضعف پیکار نمی‌کنید، آنان که می‌گویند: «پروردگارا! ما را از این شهر (مکه) که اهلش ستمگرند، بیرون ببر، و از جانب خود، برای ما سر پرستی قرار بده، و از جانب خود یار و مددکاری برای ما مقرر کن».');
INSERT INTO "hussein_taji" VALUES(4,76,'4:76','کسانی‌که ایمان آورده‌اند در راه الله پیکار می‌کنند، و آنان که کافرند در راه طاغوت (= بت و شیطان) پیکار می‌کنند، پس شما با یاران شیطان پیکار کنید، قطعاً نیرنگ (و نقشه) شیطان ضعیف است.');
INSERT INTO "hussein_taji" VALUES(4,77,'4:77','آیا ندیدی کسانی را که (در مکه) به آن‌ها گفته شد: «(اکنون) دست از جنگ بردارید، و نماز را بر پا کنید و زکات بدهید» پس چون (در مدینه) جهاد بر آنان مقرر شد، ناگاه جمعی از آنان، از مردم می‌ترسیدند همان‌گونه که از الله می‌ترسند یا بیشتر می‌ترسیدند، و گفتند: «پروردگارا، چرا جهاد را بر ما مقرر کردی؟! چرا ما را تا زمانی نزدیک مهلت ندادی؟» بگو: «متاع دنیا ناچیز است، و سرای آخرت، برای کسی‌که پرهیزگار باشد، بهتر است، به‌اندازۀ رشته‌ای (که در میان هسته خرماست) به شما ستم نخواهد شد.');
INSERT INTO "hussein_taji" VALUES(4,78,'4:78','هر کجا باشید، مرگ شما را در می‌یابد، هر چند در برج‌های محکم باشید، و اگر به آن‌ها (= منافقان) خیری برسد، می‌گویند: «این از جانب الله است » و اگر بدی به آن‌ها برسد می‌گویند: «این از جانب توست». بگو: «همه از جانب الله است» پس چرا این قوم حاضر نیستند سخنی را درک کنند؟!');
INSERT INTO "hussein_taji" VALUES(4,79,'4:79','آنچه از نیکی‌ها به تو رسد؛ پس از جانب الله است، و آنچه از بدی به تو رسد؛ از جانب خود توست، و ما تو را رسول برای مردم فرستادیم، و گواهی الله (در این باره) کافی است.');
INSERT INTO "hussein_taji" VALUES(4,80,'4:80','کسی‌که از پیامبر اطاعت کند در حقیقت الله را اطاعت کرده‌است، و کسی‌که سرباز زند، تو را برایشان نگهبان (و مراقب) نفرستادیم.');
INSERT INTO "hussein_taji" VALUES(4,81,'4:81','و آنان (در حضور تو) می‌گویند: «فرمانبرداریم» و چون از نزد تو بیرون روند؛ گروهی از آنان بر خلاف گفته‌های تو، جلسات سرّی شبانه تشکیل می‌دهند. و آنچه (در آنجا) شبانه نقشه می‌کشند؛ الله می‌نویسد. پس از آنان روی بگردان، و بر الله توکل کن، و او یار و مدافع تو باشد کافی است.');
INSERT INTO "hussein_taji" VALUES(4,82,'4:82','آیا در قرآن نمی‌اندیشند؟ که اگر از سوی غیر الله بود، قطعاً اختلاف بسیار در آن می‌یافتند.');
INSERT INTO "hussein_taji" VALUES(4,83,'4:83','و هنگامی‌که خبری از ایمنی (و پیروزی) یا ترس (و شکست) به آن‌ها برسد آن را شایع می‌سازند، در حالی‌که اگر آن را به پیامبر و صاحبان امرشان باز می‌گرداندند، از حقیقت امر آگاه می‌شدند، و اگر فضل و رحمت الله بر شما نبود، جز اندکی، (همگی) از شیطان پیروی می‌کردید.');
INSERT INTO "hussein_taji" VALUES(4,84,'4:84','پس در راه الله پیکار کن، جز بر نفس خودت مکلف نیستی، و مؤمنان را (به جهاد) تشویق کن، امید است الله از آسیب (و قدرت) کافران جلوگیری کند، و الله قدرتش بیشتر، و کیفرش سخت‌تر است.');
INSERT INTO "hussein_taji" VALUES(4,85,'4:85','کسی‌که شفاعتی (= میانجی) نیک کند، نصیبی از آن برای او خواهد بود. و کسی‌که شفاعت بدی کند؛ سهمی از آن خواهد داشت، و الله بر هر چیز توانا (و نگهبانان) است.');
INSERT INTO "hussein_taji" VALUES(4,86,'4:86','و چون شما را تحیت (و سلام) گویند، پس پاسخی بهتر و یا همانند آن بدهید، الله بر همه چیز حساب‌رس است.');
INSERT INTO "hussein_taji" VALUES(4,87,'4:87','الله (معبود بر حق است) معبودی جز او نیست، و یقیناً همۀ شما را در روز قیامت - که شکی در آن نیست؛- جمع می‌کند، و کیست که از الله راستگوتر باشد.');
INSERT INTO "hussein_taji" VALUES(4,88,'4:88','چیست شما را که دربارۀ منافقان دو گروه شده‌اید؟ در حالی‌که الله بخاطر اعمال‌شان، آنان را سرنگون (و مردود) کرده‌است، آیا شما می‌خواهید کسی را که الله گمراه کرده‌است، هدایت کنید؟ در حالی‌که هر کس را الله گمراه کند، برای او راهی نخواهی یافت.');
INSERT INTO "hussein_taji" VALUES(4,89,'4:89','آنان آرزو می‌کنند که شما هم مانند آن‌ها کافر شوید تا با هم یکسان شوید، بنابراین از آن‌ها دوستانی انتخاب نکنید، تا اینکه (مسلمان شوند و) در راه الله هجرت کنند، پس هر گاه از این کار سرباز زدند، هر جا که آن‌ها را یافتید، بگیرید. و بکشید، و از میان آن‌ها یار و یاوری اختیار نکنید.');
INSERT INTO "hussein_taji" VALUES(4,90,'4:90','مگر آن‌هایی که با هم پیمانان شما پیمان بسته‌اند، یا آن‌هایی که بسوی شما می‌آیند و از پیکار با شما یا پیکار با قوم‌شان دل‌تنگ باشند، و اگر الله بخواهد، آنان را بر شما مسلط می‌کند تا با شما پیکار کنند، پس اگر از شما کناره گیری کردند و با شما پیکار نکردند و به شما پیشنهاد صلح دادند، پس (بدانید که) الله برای شما بر علیه آنان راهی قرار نداده‌است.');
INSERT INTO "hussein_taji" VALUES(4,91,'4:91','و بزودی (گروه) دیگری را خواهید یافت که (ظاهراً) می‌خواهند از شما در امان باشند، و (نیز) از قوم‌شان در امان باشند، و هر گاه به فتنه (و بت پرستی) باز گردانده شوند، در آن فرو می‌روند، پس اگر از شما کناره نگیرند و پیشنهاد صلح نفرستند، و دست‌شان را (از شما) باز ندارند، هر کجا که آنان را یافتید، آنان را (به اسارت) بگیرید و (یا) بکشید، آن‌ها کسانی‌اند که ما برای شما تسلط آشکاری بر آنان قرار داده‌ایم.');
INSERT INTO "hussein_taji" VALUES(4,92,'4:92','هیچ مؤمنی مجاز نیست که مؤمنی را به قتل برساند، مگر از روی اشتباه و خطا، و کسی‌که مؤمنی را از روی خطا به قتل رساند، باید یک برده مؤمن را آزاد کند، و خونبهایی به خانواده او بپردازد، مگر اینکه آن‌ها ببخشند، پس اگر (مقتول) از قومی باشد که دشمنان شما هستند؛ ولی او مؤمن بوده، باید (قاتل) یک برده مؤمن را آزاد کند، و اگر (مقتول) از قومی باشد که میان شما و آن‌ها پیمانی بر قرار است، باید خونبهایی به خانواده او بپردازد، و (نیز) یک برده مؤمن را آزاد کند، و هر کس که (برده‌ای) نیافت، پس دو ماه پی در پی روزه بگیرید، این توبه‌ای از جانب الله است، و الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(4,93,'4:93','و هر کس مؤمنی را از روی عمد به قتل برساند، کیفرش دوزخ است، که در آن جاودانه می‌ماند، و الله بر او غضب می‌کند، و او را از رحمتش دور می‌سازد و عذاب بزرگی برای او آماده ساخته است.');
INSERT INTO "hussein_taji" VALUES(4,94,'4:94','ای کسانی‌که ایمان آورده‌اید! هنگامی‌که در راه الله (برای جهاد) رهسپار شدید، پس بررسی کنید، و به کسی‌که بر شما سلام کرد (و اظهار صلح و اسلام نمود) نگویید: «مؤمن نیستی» تا اینکه (غنایم و) سرمایۀ ناپایدار دنیا به دست آورید، زیرا غنیمت‌های بسیاری (برای شما) نزد الله است، شما نیز پیش از این چنین بودید، آنگاه الله بر شما منت گذارد (و هدایت شدید) بنابراین بررسی کنید، که هر آیینه الله به آنچه انجام می‌دهید؛ آگاه است.');
INSERT INTO "hussein_taji" VALUES(4,95,'4:95','(هرگز) مؤمنانی که بدون بیماری و آسیب، از جهاد باز نشستند، با مجاهدانی که در راه الله با مال و جان خود جهاد کردند، یکسان نیستند، الله کسانی را که با مال و جان‌شان جهاد کردند؛ بر بازنشستگان (= ترک کنندگان جهاد با عذر) مرتبه برتری بخشیده‌است، و الله به هر یک (از آن‌ها) وعدۀ نیکو (= بهشت) داده‌است. و الله مجاهدان را بر قاعدان (= بازنشستگان) با پاداشی بزرگ برتری بخشیده‌است.');
INSERT INTO "hussein_taji" VALUES(4,96,'4:96','درجاتی (در بهشت) از جانب الله، و آمرزش و رحمت (نصیب آنان می‌گردد) و الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(4,97,'4:97','همانا کسانی‌که فرشتگان جان‌شان را گرفتند در حالی‌که به خویشتن ستم کرده بودند (فرشتگان) به آنان گفتند: شما در چه حالی بودید؟ گفتند: «ما در زمین مستضعف بودیم» (فرشتگان) گفتند: «مگر سر زمین خدا، پهناور نبود که در آن مهاجرت کنید؟» بنابراین جایگاه‌شان دوزخ است و بدجایی است.');
INSERT INTO "hussein_taji" VALUES(4,98,'4:98','مگر آن مستضعفان از مردان و زنان و کودکانی که نه چاره‌ای دارند و نه راهی (برای نجات) می‌یابند.');
INSERT INTO "hussein_taji" VALUES(4,99,'4:99','پس اینان را امید است که الله از ایشان در گذرد (و ببخشاید) و الله بخشایندۀ آمرزنده ‌است.');
INSERT INTO "hussein_taji" VALUES(4,100,'4:100','و کسی‌که در راه الله هجرت کند در زمین جاهای امن فراوان و گشایش (در رزق و روزی) می‌یابد. و کسی‌که به قصد هجرت به سوی الله و پیامبرش، از خانه خود بیرون رود، آنگاه مرگش فرا رسد، قطعاً پاداشش بر الله است. و الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(4,101,'4:101','و چون در زمین سفر کنید، گناهی بر شما نیست که نماز را کوتاه (= قصر) کنید، اگر ترسید که کافران به شما زیانی برسانند، زیرا کافران، برای شما دشمن آشکاری هستند.');
INSERT INTO "hussein_taji" VALUES(4,102,'4:102','و چون (وقت خوف) در میان آن‌ها باشی، و برای آن‌ها نماز بر پا کردی، باید دسته‌ای از آن‌ها با تو (به نماز) بایستند، و باید سلاح‌هایشان را با خود برگیرند. و چون سجده کردند (و نماز را تمام نمودند) باید (دسته دوم) پشت سرتان باشند، و باید آن دسته‌ای که نماز نخوانده‌اند؛ بیایند و با تو نماز بخوانند، و باید آن‌ها احتیاط کنند و سلاح‌هایشان را (در نماز) با خود بگیرند، (زیرا) کافران آرزو دارند که شما از سلاح‌های و متاع‌های خود غافل شوید؛ پس یکباره بر شما یورش برند [ در این آیه به صلاة الخوف (نماز خوف) حکم داده شده است، این نماز در وقتی خوانده می‌شود که مسلمان‌ها و کافران جهت ستیزه و نبرد مقابل یکدیگر ایستاده‌اند و با کوچک‌ترین غفلتی خسارت سنگینی برای مسلمانان به بار خواهد آورد.]، و اگر برای شما از باران رنجی بود و بیمار (و مجروح) بودید؛ بر شما گناهی نیست (و مانعی ندارد) که سلاح‌های خود را (بر زمین) بگذارید، ولی (وسایل دفاعی و) احتیاط خود را بر گیرید. همانا الله برای کافران عذابی خوارکننده آماده کرده‌است.');
INSERT INTO "hussein_taji" VALUES(4,103,'4:103','پس چون نماز را به پایان رساندید، الله را ایستاده، و نشسته و بر پهلوی خویش (خوابیده) یاد کنید، و هرگاه آرامش یافتید (و خوف زایل شد) نماز را (تمام و کامل) بر پا دارید، که همانا نماز (فریضه‌ای است که) در اوقات معینی بر مؤمنان واجب (و نوشته) شده‌است.');
INSERT INTO "hussein_taji" VALUES(4,104,'4:104','و در تعقیب دشمن سست نشوید. اگر شما رنج می‌برید؛ آن‌ها نیز مانند شما رنج می‌کشند، ولی شما امیدی از الله دارید که آن‌ها ندارند، و الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(4,105,'4:105','یقیناً (این) کتاب را بحق بر تو نازل کردیم. تا به آنچه الله به تو آموخته در میان مردم داوری کنی، و مدافع (و حمایت‌کننده) خائنان مباش.');
INSERT INTO "hussein_taji" VALUES(4,106,'4:106','و از الله آمرزش بخواه، حقا که الله، آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(4,107,'4:107','و از کسانی‌که به خود خیانت کردند، دفاع مکن، یقیناً الله، افراد خیانت پیشۀ گنهکار را دوست ندارد.');
INSERT INTO "hussein_taji" VALUES(4,108,'4:108','آن‌ها (زشتکاری‌های خود را) از مردم پنهان می‌دارند، اما از الله پنهان نمی‌دارند، و او با ایشان است؛ هنگامی‌که در جلسات شبانه سخنانی را که الله نمی‌پسندد، تدبیر می‌کنند، و الله به آنچه انجام می‌دهند؛ احاطه دارد.');
INSERT INTO "hussein_taji" VALUES(4,109,'4:109','هان! شما کسانی هستید که در زندگی دنیا، از آنان دفاع کردید، پس کیست که در روز قیامت در برابر الله از آن‌ها دفاع کند؟ یا چه کسی است که وکیل و حامی آن‌ها خواهد بود؟');
INSERT INTO "hussein_taji" VALUES(4,110,'4:110','و کسی‌که کار بدی انجام دهد یا بر خویشتن ستم کند، پس از الله آمرزش بطلبد، الله را آمرزندۀ مهربان خواهد یافت.');
INSERT INTO "hussein_taji" VALUES(4,111,'4:111','و هر کس گناهی مرتکب شود، پس تنها به زیان خود مرتکب شده‌است، و الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(4,112,'4:112','و هر کس خطا یا گناهی مرتکب شود، سپس بی گناهی را به آن متهم کند، قطعاً بارِ بهتان و گناه آشکاری را بر دوش گرفته است.');
INSERT INTO "hussein_taji" VALUES(4,113,'4:113','و اگر فضل الله و رحمتش شامل حال تو نبود، هر آینه گروهی از آنان قصد داشتند که تو را گمراه کنند، و جز خودشان را گمراه نمی‌کنند، و هیچ زیانی به تو نمی‌رسانند، و الله کتاب و حکمت (= سنت) برتو نازل کرد، و آنچه را نمی‌دانستی، به تو آموخت، و فضل الله بر تو (همیشه) بزرگ بوده‌است.');
INSERT INTO "hussein_taji" VALUES(4,114,'4:114','در بسیاری از (در گوشی و) نجواهای‌شان خیری نیست، مگر که (با این کار) امر به صدقه دادن یا کار نیک، یا اصلاح در میان مردم کند. و هر کس برای خشنودی الله چنین کند، به زودی پاداش بزرگی به او خواهیم داد.');
INSERT INTO "hussein_taji" VALUES(4,115,'4:115','و کسی‌که پس از آنکه هدایت (و راه حق) برایش روشن شد؛ با پیامبر مخالفت کند، و از راهی جز راه مؤمنان پیروی کند، ما او را به آنچه پیروی کرده؛ وا گذاریم، و او را به جهنم در افکنیم [ مراد از مؤمنین صحابۀ گرامی هستند که اولین پیروان اسلام بودند، چرا که هنگام نزول این آیات غیر از صحابه گروهی به عنوان گروه مؤمنین وجود نداشت.]، و بد جایگاهی است.');
INSERT INTO "hussein_taji" VALUES(4,116,'4:116','قطعاً الله، شرک آوردن به او را نمی‌آمرزد، و جز آن (هر گناهی) را برای هر که بخواهد می‌آمرزد. و هر کس به الله شرک آورد، پس بدون شک در گمراهی دوری افتاده‌است.');
INSERT INTO "hussein_taji" VALUES(4,117,'4:117','(مشرکان) جز الله چیزهایی (= بت‌های) ماده را می‌خوانند و (در حقیقت) جز شیطان سرکش را نمی‌خوانند.');
INSERT INTO "hussein_taji" VALUES(4,118,'4:118','الله او را از رحمت خویش دور ساخته، و او گفت: «از بندگان تو، سهمی معین خواهم گرفت.');
INSERT INTO "hussein_taji" VALUES(4,119,'4:119','و مسلماً آن‌ها را گمراه می‌کنم، و آنان را به آرزوهای باطل افکنم، و به آنان دستور می‌دهم که گوش چهار پایان بشکافند، و وادار شان می‌کنم؛ تا خلقت و آفرینش الله را تغییر دهند». و هر کس شیطان را به جای الله ولی و دوست خود بگیرد، قطعاً زیانی آشکار کرده‌است.');
INSERT INTO "hussein_taji" VALUES(4,120,'4:120','(شیطان) به آن‌ها وعده می‌دهد و آنان را به آرزو افکند، و شیطان جز فریب و باطل به آنان وعده نمی‌دهد.');
INSERT INTO "hussein_taji" VALUES(4,121,'4:121','آن‌ها (= پیروان شیطان) جایگاه شان جهنم است، و هیچ راه فراری از آن نخواهند یافت.');
INSERT INTO "hussein_taji" VALUES(4,122,'4:122','و کسانی‌که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، بزودی آنان را در باغ‌هایی (از بهشت) وارد می‌کنیم که نهرها از زیر (درختان) آن جاری است، جاودانه در آن خواهند ماند، وعده الله حق است، و کیست که در گفتار (و وعده‌هایش) از الله راستگوتر باشد؟');
INSERT INTO "hussein_taji" VALUES(4,123,'4:123','(این فضلیت و برتری) به آرزوهای شما و آرزوهای اهل کتاب نیست، هر کس کار بدی انجام دهد، به کیفر آن خواهد رسید و جز خدا، یار و مددکاری برای خود نخواهد یافت.');
INSERT INTO "hussein_taji" VALUES(4,124,'4:124','و کسی‌که چیزی از کارها شایسته را انجام دهد، چه مرد باشد یا زن، در حالی‌که او مؤمن باشد، اینان به بهشت داخل می‌شوند و کمترین ستمی (به اندازۀ گودی پشت هستۀ خرما) به آن‌ها نخواهد شد.');
INSERT INTO "hussein_taji" VALUES(4,125,'4:125','و چه کسی نیک آیین تر است از کسی‌‌که روی خود را به الله سپرد (و مخلصانه تسلیم شد) و نیکو کار (و فرمانبردار) بود و آیین پاک ابراهیم را پیروی کرد؟ و الله ابراهیم را به دوستی خود بر گزیده‌است.');
INSERT INTO "hussein_taji" VALUES(4,126,'4:126','و آنچه در آسمان‌ها و آنچه در زمین است، از آن الله است، و الله به هر چیزی احاطه دارد.');
INSERT INTO "hussein_taji" VALUES(4,127,'4:127','از تو (ای پیامبر) دربارۀ زنان فتوا می‌خواهند، بگو: الله درباره آنان به شما فتوا می‌دهد و (همچنین) آنچه در کتاب (= قرآن کریم) بر شما خوانده می‌شود؛ دربارۀ زنان یتیمی که آنچه را که برای آنان مقرر شده‌است (از مهر و میراث و حقوق) به آن‌ها نمی‌دهید، و می‌خواهید که با آن‌ها ازدواج کنید. و (نیز به شما توصیه می‌کند و فتوا می‌دهد در مورد) کودکان صغیر ناتوان، و اینکه با یتیمان به عدالت رفتار کنید، و آنچه از نیکی‌ها انجام می‌دهید، الله به آن آگاه است.');
INSERT INTO "hussein_taji" VALUES(4,128,'4:128','و اگر زنی، از ناسازگاری یا اعراض شوهرش بیم داشت، پس بر آن دو گناهی نیست که با هم صلح کنند، و صلح، بهتر است، ـ اگر چه نفس‌ها را بخل فرا گرفته است ـ و اگر نیکی کنید و پرهیزگاری پیشه سازید (و گذشت کنید) قطعاً الله به آن چه انجام می‌دهید؛ آگاه است.');
INSERT INTO "hussein_taji" VALUES(4,129,'4:129','و هر چند بکوشید هرگز نمی‌توانید (از نظر محبت قلبی) در میان زنان عدالت بر قرار کنید. پس یکسره به سوی یکی میل نکنید، که دیگری را بلا تکلیف (و سر گشته) رها کنید، و اگر راه اصلاح پیش گیرید، و پرهیزگاری کنید، الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(4,130,'4:130','و اگر آن دو از یکدیگر جدا شوند، الله هر کدام از آن‌ها را با فضل و کرم خود بی‌نیاز می‌کند، و الله گشایشگر حکیم است.');
INSERT INTO "hussein_taji" VALUES(4,131,'4:131','و آنچه در آسمان‌ها و آنچه در زمین است از آن الله است، و ما به کسانی‌که پیش از شما کتاب (آسمانی) به آن‌ها داده شدند و (نیز) به شما سفارش کردیم که «از الله بترسید» (و یکتا پرست باشید) و اگر کافر شوید (بدانید) آنچه در آسمان‌ها و آنچه در زمین است از آن الله است، و الله بی‌نیاز (و) ستوده‌است.');
INSERT INTO "hussein_taji" VALUES(4,132,'4:132','و آنچه در آسمان‌ها و آنچه در زمین است از آن الله است، و الله برای کارسازی کافی است.');
INSERT INTO "hussein_taji" VALUES(4,133,'4:133','ای مردم! اگر او بخواهد، شما را از میان می‌برد و دیگران را (به جای شما) می‌آورد، و الله بر این (کار) تواناست.');
INSERT INTO "hussein_taji" VALUES(4,134,'4:134','هر کس که پاداش دنیوی بخواهد؛ پس (بداند که) پاداش دنیا و آخرت نزد الله است. و الله شنوای بینا است.');
INSERT INTO "hussein_taji" VALUES(4,135,'4:135','ای کسانی‌که ایمان آورده‌اید! برپا دارندۀ عدالت باشید، برای الله شهادت دهید؛ اگر چه به زیان خود شما یا پدر و مادر و نزدیکان (شما) باشد، اگر (مدعی علیه) توانگر یا فقیر باشد. (به هر حال) الله به آنان سزاوارتر (و مهربان‌تر) است. پس از هوای نفس پیروی نکنید، که از حق منحرف می‌شوید و اگر زبان را بگردانید (و حق را تحریف کنید) یا (از اظهار حق) اعراض کنید، مسلماً الله به آنچه انجام می‌دهید؛ آگاه است.');
INSERT INTO "hussein_taji" VALUES(4,136,'4:136','ای کسانی‌که ایمان آورده‌اید! به الله و پیامبرش و کتابی که بر او نازل کرده و کتابی که پیش از این فرستاده‌است؛ ایمان بیاورید (و بر آن پایدار باشید) و هر کس به الله و فرشتگان و کتاب‌هایش و پیامبرانش و روز قیامت کافر شود؛ بتحقیق در گمراهی دور و درازی افتاده‌است.');
INSERT INTO "hussein_taji" VALUES(4,137,'4:137','همانا کسانی‌که ایمان آورده‌اند، سپس کافر شدند، باز ایمان آوردند، سپس کافر شدند، آنگاه بر کفر (خود) افزودند؛ الله هرگز آنان را نخواهد بخشید، و آن‌ها را به راه (راست) هدایت نخواهد کرد.');
INSERT INTO "hussein_taji" VALUES(4,138,'4:138','به منافقان بشارت ده که برایشان عذابی دردناک است.');
INSERT INTO "hussein_taji" VALUES(4,139,'4:139','آنان که کافران را به جای مؤمنان به دوستی بر می‌گزینند، آیا به نزد آنان عزت می‌جویند؟ پس (بدانند که) عزت همه ازآن الله است.');
INSERT INTO "hussein_taji" VALUES(4,140,'4:140','و مسلماً (الله) در قرآن (این حکم) را بر شما نازل کرده که چون شنیدید (افرادی) آیات الله را انکار می‌کنند و آن را به ریشخند می‌گیرند با آنان ننشینید تا به سخن دیگری بپردازند (و اگر با آنان بنشیند) در این صورت قطعاً شما (نیز) هم مانند آن‌ها خواهید بود. همانا الله همۀ منافقان و کافران را در جهنم گرد می‌آورد.');
INSERT INTO "hussein_taji" VALUES(4,141,'4:141','کسانی‌که همواره انتظار می‌کشند (و مراقب شما هستند) اگر فتح و پیروزی از جانب الله نصیب شما گردد، می‌گویند: «مگر ما شما نبودیم؟» و اگر بهره‌ای نصیب کافران گردد، گویند: «مگر ما بر شما چیره نشده‌ایم (و پشتیبان شما نبودیم) و شما را از (آسیب) مؤمنان بازنداشتیم؟». پس الله در روز قیامت میان شما داوری می‌کند، و الله هرگز برای کافران راهی به (زیان) مؤمنان قرار نداده‌است.');
INSERT INTO "hussein_taji" VALUES(4,142,'4:142','بی‌شک منافقان (بگمان خودشان) الله را فریب می‌دهند، در حالی‌که او آن‌ها را فریب می‌دهد، و چون به نماز برخیزند؛ با سستی و‌‌ کاهلی بر می‌خیزند، در چشم مردم خود نمایی می‌کنند و (در نمازشان) الله را جز اندکی یاد نمی‌کنند.');
INSERT INTO "hussein_taji" VALUES(4,143,'4:143','(منافقان) بین این دو (گروه مؤمنان و کافران) دو دل و سرگشته‌اند. نه با اینانند و نه با آنان‌اند، و هر کس را که الله گمراهی کند، راهی برای او نخواهی یافت.');
INSERT INTO "hussein_taji" VALUES(4,144,'4:144','ای کسانی‌که ایمان آوردید! کافران را به جای مؤمنان دوستان (خود) نگیرید، آیا می‌خواهید (با این کار) برای الله دلیل آشکاری بر ضد خود پدید آورید؟');
INSERT INTO "hussein_taji" VALUES(4,145,'4:145','همانا منافقان در پایین‌ترین طبقه (= درکات) آتش (جهنم) هستند، و هرگز یاوری برای آن‌ها نخواهی یافت.');
INSERT INTO "hussein_taji" VALUES(4,146,'4:146','مگر آنان که توبه کردند و جبران و اصلاح نمودند، و به الله تمسک جستند و دین خود را برای الله خالص گرداندند، پس اینان با مؤمنان خواهند بود، و الله به زودی مؤمنان را پاداش بزرگی خواهد داد.');
INSERT INTO "hussein_taji" VALUES(4,147,'4:147','الله چه نیازی به عذاب شما دارد اگر سپاسگزاری کنید و ایمان آورید؟! و الله شکرگزار داناست.');
INSERT INTO "hussein_taji" VALUES(4,148,'4:148','الله بانگ برداشتن به سخنان ناروا را دوست ندارد، مگر (از جانب) آن کس که مورد ستم واقع شده باشد، و الله شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(4,149,'4:149','اگر کار نیک آشکار کنید یا آن را پنهان دارید، یا از بدی و (ستمی که به شما رسیده) گذشت نمایید، پس (بدانید که) الله آمرزندۀ تواناست.');
INSERT INTO "hussein_taji" VALUES(4,150,'4:150','همانا کسانی‌که به الله و پیامبرانش کفر می‌ورزند و می‌خواهند میان الله و پیامبرانش جدایی بیندازند؛ و می‌گویند: «به بعضی ایمان می‌آوریم؛ و بعضی را انکار می‌کنیم» و می‌خواهند بین این (و آن) راهی (برای خود) برگزینند.');
INSERT INTO "hussein_taji" VALUES(4,151,'4:151','آن‌ها در حقیقت کافرند، و برای کافران عذاب خوارکننده‌ای؛ آماده کرده‌ایم.');
INSERT INTO "hussein_taji" VALUES(4,152,'4:152','و کسانی‌که به الله و پیامبرانش ایمان آورده و میان هیچ یک ازآنان جدائی نیفکندند؛ اینان بزودی پاداش‌شان را (الله) خواهد داد، و الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(4,153,'4:153','اهل کتاب (= یهودیان) از تو می‌خواهند که کتابی از آسمان (یکجا) بر آن‌ها نازل کنی، همانا آن‌ها از موسی، بزرگ‌تر از این را خواستند و گفتند: «الله را آشکارا به ما نشان بده». پس بخاطر این ظلم و ستمکاری‌شان صاعقه آن‌ها را فرو گرفت، سپس بعد از آنکه معجزه و دلایل روشن برایشان آمد، گوساله را (به خدائی) گرفتند، و ما از آن گناه در گذشتیم (و بخشیدیم). و به موسی حجت (و برهان) آشکاری دادیم.');
INSERT INTO "hussein_taji" VALUES(4,154,'4:154','و بخاطر گرفتن پیمان از ایشان (کوه) طور را بر فراز آن‌ها بر افراشتیم و به آن‌ها گفتیم: «سجده‌کنان به دروازه (بیت المقدس) داخل شوید». و (نیز) به آنان گفتیم: در روز شنبه تجاوز نکنید (و از صید ماهی دست بکشید) و از آنان پیمان محکمی گرفتیم.');
INSERT INTO "hussein_taji" VALUES(4,155,'4:155','پس بخاطر پیمان شکنی شان و انکار آیات خدا، و به ناحق کشتن پیامبران و (این) گفته شان که دل های ما در پرده‌است (و سخنان پیامبر را درک نمی‌کنیم). بلکه الله به سبب کفرشان بر (دل‌های) آن‌ها مهر زده ‌است؛ پس جز عده کمی ایمان نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(4,156,'4:156','و (نیز) بخاطر کفرشان و تهمت بزرگی که بر مریم زدند.');
INSERT INTO "hussein_taji" VALUES(4,157,'4:157','و (نیز) بخاطر این گفته شان که «ما مسیح، عیسی پسر مریم پیامبر الله را کشتیم». در حالی‌که نه او را کشتند و نه به دار آویختند، لکن امر بر آن‌ها مشتبه شد، و هر آینه آنان که در(بارۀ قتل) او اختلاف کردند، قطعاً از آن در شک هستند، و علم به آن ندارند و تنها از گمان پیروی می‌کنند و به یقین او را نکشته‌اند.');
INSERT INTO "hussein_taji" VALUES(4,158,'4:158','بلکه الله او را به سوی خود بالا برد، و الله پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(4,159,'4:159','و (کسی) از اهل کتاب نیست مگر این‌که قبل از مرگش به او (= حضرت عیسی) ایمان می‌آورد، و (عیسی) روز قیامت بر آن‌ها گواه خواهد بود [ این آیه بر نزول حضرت عیسی علیه السلام از آسمان دلالت دارد، رجوع به سورۀ آل عمران آیه: .].');
INSERT INTO "hussein_taji" VALUES(4,160,'4:160','پس به (کیفر) ستمی که قوم یهود مرتکب شدند، و (نیز) بخاطر جلو گیری بسیار آن‌ها از راه خدا، چیزی‌های پاکیزه‌ای را که برایشان حلال بود؛ بر آن‌ها حرام کردیم.');
INSERT INTO "hussein_taji" VALUES(4,161,'4:161','و (نیز) به سبب ربا گرفتن‌شان، در حالی‌که از آن نهی شده بودند، و (نیز) به سبب آنکه اموال مردم را به ناحق می‌خورند، و ما برای کافران‌شان عذاب دردناکی آماده کردیم.');
INSERT INTO "hussein_taji" VALUES(4,162,'4:162','ولی (دانشمندان و) راسخان در علم از آنان و مؤمنان، به آنچه بر تو نازل شده و آنچه پیش از تو نازل گردیده؛ ایمان می‌آورند، و (همچنین) بر پا دارندگان نماز و زکات دهندگان، و مؤمنان به الله و روز قیامت؛ بزودی به همۀ آن‌ها پاداش بزرگی خواهیم داد.');
INSERT INTO "hussein_taji" VALUES(4,163,'4:163','همانا ما به تو وحی فرستادیم همان گونه که به نوح و پیامبران بعد از او وحی فرستادیم و (نیز) به ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط (= نوادگان دوازده گانه) و عیسی و ایوب و یونس و هارون و سلیمان وحی فرستادیم و به داود زبور دادیم.');
INSERT INTO "hussein_taji" VALUES(4,164,'4:164','و پیامبرانی (مبعوث کردیم) که سر گذشت آن‌ها را پیش از این برای تو بیان کرده‌ایم، و پیامبرانی که سر گذشت آن‌ها را بیان نکردیم، و الله (بدون واسطه) با موسی سخن گفت.');
INSERT INTO "hussein_taji" VALUES(4,165,'4:165','پیامبرانی که بشارت‌دهنده و بیم‌دهنده بودند، تا بعد از (آمدن) این پیامبران برای مردم بر الله حجتی نباشد و الله پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(4,166,'4:166','لیکن الله به آنچه بر تو نازل کرده‌است؛ گواهی می‌دهد، که آن را به علم خود نازل کرده‌است، و فرشتگان (نیز) گواهی می‌دهند، و (اگر چه) گواهی الله کافی است.');
INSERT INTO "hussein_taji" VALUES(4,167,'4:167','هر آینه آنان که کافر شدند و (مردم را) از راه الله باز داشتند، محققاً در گمراهی دوری گرفتار شده‌اند.');
INSERT INTO "hussein_taji" VALUES(4,168,'4:168','آنان که کافر شدند و ستم کردند، الله هرگز آن‌ها را نخواهد بخشید و آن‌ها را به راهی هدایت نخواهد کرد.');
INSERT INTO "hussein_taji" VALUES(4,169,'4:169','مگر راه جهنم، که جاودانه در آن خواهند ماند و این (کار) بر الله آسان است.');
INSERT INTO "hussein_taji" VALUES(4,170,'4:170','ای مردم، بی‌گمان پیامبر (موعود محمد صلی الله علیه وسلم) با (آیین) حق از جانب پروردگارتان برای شما آمد، پس به او ایمان بیاورید که برای شما بهتر است، و اگر کافر شوید؛ پس یقیناً (بدانید که) آنچه در آسمان‌ها و زمین است از آن الله است، (و کفر شما به او زیانی نمی‌رساند) و الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(4,171,'4:171','ای اهل کتاب! در دین خود غلو نکنید و دربارۀ الله جز حق نگویید، همانا مسیح عیسی پسر مریم فرستادۀ الله و کلمۀ اوست؛ که او را به مریم القا نمود، و روحی از جانب اوست، پس به الله و پیامبرانش ایمان بیاورید، و نگویید: (الله) سه گانه است، (از این سخن) باز آیید که برای شما بهتر است، الله تنها معبود یگانه است و از این‌که فرزندی داشته باشد پاک (و منزه) است، آنچه در آسمان‌ها و آنچه در زمین است از آن اوست و الله برای کارسازی کافی است.');
INSERT INTO "hussein_taji" VALUES(4,172,'4:172','مسیح هرگز ابا ندارد که بندۀ الله باشد، و نه فرشتگان مقرب (ابا دارند) و هر کس از بنده گی او سر باز زند و تکبر کند، بزودی همۀ آن‌ها را (در قیامت) نزد خود محشور خواهد کرد.');
INSERT INTO "hussein_taji" VALUES(4,173,'4:173','اما آنان که ایمان آوردند و کارهای شایسته انجام دادند، پاداش‌هایشان را بطور کامل خواهد داد، و از فضل خویش بر آن‌ها خواهد افزود، و اما آنان که ابا کردند و تکبر ورزیدند، آنان را به عذابی دردناک عذاب می‌کند، و برای خود غیر از خدا، دوست و یاوری نخواهند یافت.');
INSERT INTO "hussein_taji" VALUES(4,174,'4:174','ای مردم! همانا دلیل و حجتی از جانب پروردگارتان برای شما آمد، و نوری آشکار به سوی شما نازل کردیم.');
INSERT INTO "hussein_taji" VALUES(4,175,'4:175','اما آنان که به الله ایمان آوردند و به او تمسک جستند، بزودی همه را در رحمت و فضل خود، وارد خواهد کرد، و آنان را در راه راستی به سوی خود هدایت‌شان می‌کند.');
INSERT INTO "hussein_taji" VALUES(4,176,'4:176','از تو (دربارۀ کلاله= مرده نه فرزند دارد نه پدر) فتوا می‌خواهند، بگو: «الله در(بارۀ) کلاله برای شما فتوی می‌دهد، اگر مردی بمیرد که فرزند نداشته باشد و برای او خواهری باشد، نصف اموالی را که به جا گذاشته، برای او است. و او تمام مال خواهر را به ارث می‌برد، (اگر خواهر بمیرد) در صورتی که فرزند نداشته باشد، و چنانچه دو (خواهرش) باقی باشند؛ دو سوم ترکه را می‌برند، و اگر چندین مردان و زنانی برادر و خواهر باشند برای هر مرد دو برابر سهم زن خواهد بود، الله (احکام خویش را) برای شما بیان می‌کند تا گمراه نشوید، و الله به همه چیز داناست».');
INSERT INTO "hussein_taji" VALUES(5,1,'5:1','ای کسانی‌که ایمان آورده‌اید! به پیمان‌ها (و قرار دادهای خود) وفا کنید، (گوشت) چهار پایان برای شما حلال شده است، مگر آنچه بر شما خوانده می‌شود، و در حال احرام شکار را حلال نشمارید، همانا الله هر چه بخواهد حکم می‌کند.');
INSERT INTO "hussein_taji" VALUES(5,2,'5:2','ای کسانی‌که ایمان آورده‌اید! (حرمت) شعائر الهی (را نگه دارید و بی حرمتی به آن‌ها) را حلال ندانید، و نه ماه حرام را، و نه قربانی‌های بی نشان و نشاندار را، و نه قاصدان خانه الله را که فضل و خشنودی پروردگارشان را می‌طلبند. و چون از احرام بیرون آمدید، پس شکار کنید، و دشمنی گروهی که شما از مسجد الحرام باز داشتند، نباید شما را به تعدی و تجاوز وا دار کند. و در راه نیکو کاری و پرهیزگاری با همدیگر همکاری کنید و (هرگز) در راه گناه و تجاوز همکاری نکنید، و از الله بترسید، بی‌گمان الله سخت کیفر است.');
INSERT INTO "hussein_taji" VALUES(5,3,'5:3','(گوشت) مردار، و خون و گوشت خوک و آنچه (هنگام ذبح) نام غیر الله بر آن برده شود، و (حیوانات) خفه شده، و با ضربه مرده، و از بلندی افتاده، و به ضرب شاخ (حیوان دیگری) مرده، و آنچه درندگان خورده باشند، - مگر آنکه ذبح (شرعی) کرده باشید-، و آنچه برای بت‌ها ذبح شده، و آنکه با تیرهای فال؛ بخت و قسمت طلب کنید (همه) بر شما حرام شده، و (روی آوردن به تمام) این‌ها فسق و نافرمانی است. امروز کافران از آیین شما مأیوس شدند، بنابر این از آن‌ها نترسید و از من بترسید، امروز دین شما را کامل کردم و نعمت خود را بر شما تمام نمودم، و اسلام را (بعنوان) دین برای شما بر گزیدم. اما هر کس که از گرسنگی درمانده شود و مایل به گناه نباشد (مانعی ندارد که از گوشت‌های ممنوع بخورد) پس (بداند که) الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(5,4,'5:4','از تو می‌پرسند چه چیز برای آن‌ها حلال شده است؟ بگو: پاکیزه‌ها برای شما حلال گردیده و (نیز) آنچه از (صید) حیوانات شکاری و سگ‌های آموخته (و تربیت شده) که از آنچه الله به شما آموخته است به آن‌ها آموخته‌اید (بر شما حلال است) پس آنچه برای شما (صید می‌کنند و) نگاه می‌دارند، بخورید، و نام الله را (به هنگام فرستادن حیوان برای شکار) بر آن ببرید، و از الله بترسید، بی‌گمان الله زود شمار است.');
INSERT INTO "hussein_taji" VALUES(5,5,'5:5','امروز پاکیزه‌ها برای شما حلال شده، و (همچنین) طعام اهل کتاب برای شما حلال است، و طعام شما برای آن‌ها حلال است و (نیز) زنان پاکدامن از مسلمانان و زنان پاکدامن از کسانی‌که پیش از شما به آن‌ها کتاب (آسمانی) داده شده، هر گاه که مهر آن‌ها را بپردازید، در حالی‌که پاکدامن باشید، نه زنا کار و نه دوست پنهانی گیرنده (برای شما حلال است) و کسی‌که به (ارکان) ایمان کفر ورزد، همانا عملش تباه شده است و او در آخرت از زیانکاران خواهد بود.');
INSERT INTO "hussein_taji" VALUES(5,6,'5:6','ای کسانی‌که ایمان آورده‌اید! چون به نماز برخاستید؛ پس صورت خود و دست‌هایتان را تا آرنج بشویید و سرتان را مسح کنید، و پاهای‌تان را تا دو قوزک (بشویید) و اگر جنب بودید؛ پس خود را پاک سازید (و غسل کنید)، و اگر بیمار یا مسافر هستید، یا یکی از شما از محل قضای حاجت آمده (= قضای حاجت کرده) یا با زنان (تماس گرفته و) آمیزش کرده باشید، آنگاه آب نیابید؛ پس با خاک پاکی تیمّم کنید. و از آن بر صورت‌تان و دست‌هایتان بکشید. الله نمی‌خواهد بر شما دشواری قرار دهد، بلکه می‌خواهد شما را پاک سازد و نعمتش را بر شما تمام کند، باشد که شکر گزارید.');
INSERT INTO "hussein_taji" VALUES(5,7,'5:7','و به یاد آورید نعمت الله را بر خویش، و (به یاد آورید) پیمانی را که با تأکید از شما گرفت، آن هنگام که گفتید: «شنیدیم و اطاعت کردیم» و از الله بترسید، بی‌گمان الله از آنچه درون سینه‌هاست آگاه است.');
INSERT INTO "hussein_taji" VALUES(5,8,'5:8','ای کسانی‌که ایمان آورده‌اید! همواره برای الله قیام کنید و به عدالت گواهی دهید، دشمنی با گروهی شما را بر آن ندارد که عدالت نکنید؛ عدالت کنید که به پرهیزگاری نزدیک‌تر است و از الله بترسید، همانا الله به آنچه می‌کنید؛ آگاه است.');
INSERT INTO "hussein_taji" VALUES(5,9,'5:9','الله به آنان که ایمان آوردند و کارهای شایسته انجام دادند وعدۀ آمرزش و پاداش بزرگی داده است.');
INSERT INTO "hussein_taji" VALUES(5,10,'5:10','و کسانی‌که کافر شدند و آیات ما را تکذیب کردند؛ آنان اهل دوزخند.');
INSERT INTO "hussein_taji" VALUES(5,11,'5:11','ای کسانی‌که ایمان آورده‌اید! نعمت الله را بر خود به یاد آورید، آنگاه که گروهی (از دشمنان) قصد کردند که به سوی شما دست دراز کنند، پس (الله) دست آن‌ها را از شما باز داشت و از الله بترسید، و مومنان باید تنها بر الله توکل کنند.');
INSERT INTO "hussein_taji" VALUES(5,12,'5:12','همانا الله از بنی اسرائیل پیمان گرفت و از آن‌ها دوازده نقیب (= سرپرست) بر انگیختیم. و الله (به آنان) فرمود: «من با شما هستم اگر نماز را بر پاداشتید و زکات را پرداختید و به پیامبران من ایمان آوردید و آن‌ها را یاری نمودید و به الله قرض‌الحسنه دادید (در راه او انفاق نمودید) یقیناً گناهان‌تان را از شما می‌زدایم و شما را به باغ‌هایی (از بهشت) وارد می‌کنم که نهرها از زیر (درختان) آن جاری است، پس هر کس از شما بعد از این کافر شود؛ مسلماً از راه راست منحرف گردیده است».');
INSERT INTO "hussein_taji" VALUES(5,13,'5:13','پس به خاطر پیمان شکنی شان آن‌ها را از رحمت خویش دور ساختیم و دل‌های شان را سخت گردانیدیم سخنان (خدا) را از موردش تحریف می‌کنند و بخشی از آنچه را که به آن پند داده شده بودند؛ فراموش کردند، و پیوسته تو به خیانت (تازه‌ای) از آن‌ها آگاه می‌شوی؛ مگر عده کمی از آنان، پس از آن‌ها درگذر و روی بگردان، بی‌گمان الله نیکو کاران را دوست می‌دارد.');
INSERT INTO "hussein_taji" VALUES(5,14,'5:14','و از کسانی‌که گفتند: ما نصرانی هستیم؛ پیمان گرفتیم، پس بخشی از آنچه را که به آنان پند داده شده بودند؛ فراموش کردند، لذا ما (نیز) بین آنان تا روز قیامت عداوت و دشمنی افکندیم، و الله بزودی آن‌ها را از آنچه انجام می‌دادند؛ آگاه خواهد ساخت.');
INSERT INTO "hussein_taji" VALUES(5,15,'5:15','ای اهل کتاب! بتحقیق پیامبر ما به سوی شما آمد تا بسیاری از حقایق از کتاب (آسمان) را که کتمان کرده‌اید برای شما روشن سازد و از بسیاری درگذرد، به راستی از جانب الله نور و کتاب آشکاری به سوی شما آمد.');
INSERT INTO "hussein_taji" VALUES(5,16,'5:16','الله بوسیلۀ آن کسانی را که از خشنودی او پیروی کنند به راه‌های سلامت هدایت می‌کند و به فرمان خود آن‌ها را از تاریکی‌ها به سوی روشنایی می‌برد و آن‌ها را به راه راست هدایت می‌کند.');
INSERT INTO "hussein_taji" VALUES(5,17,'5:17','کسانی‌که گفتند: «خدا، همان مسیح پسر مریم است» یقیناً کافر شدند. بگو: «اگر الله بخواهد مسیح پسر مریم و مادرش و همۀ کسانی را که روی زمین هستند؛ هلاک کند، چه کسی قدرت بر جلوگیری دارد؟ فرمانروایی آسمان‌ها و زمین و آنچه میان آن دو است از آن الله است، هر چه بخواهد می‌آفریند و الله بر هر چیز تواناست».');
INSERT INTO "hussein_taji" VALUES(5,18,'5:18','یهود و نصاری گفتند: «ما فرزندان الله و دوستان او هستیم». بگو: «پس چرا شما را به کیفر گناهان‌تان عذاب می‌کند؟ بلکه شما (هم) بشری هستید از آنچه آفریده است، هر کس را بخواهد می‌آمرزد و هر کس را بخواهد عذاب می‌کند و فرمانروایی آسمان‌ها و زمین و آنچه میان آن دو است از آن الله است و بازگشت (همه) به سوی اوست»');
INSERT INTO "hussein_taji" VALUES(5,19,'5:19','ای اهل کتاب! پیامبر ما پس از (انقطاع وحی و) فترتی از پیامبران به سوی شما آمد، که حقایق را برایتان بیان کند، تا مبادا (روز قیامت) بگویید: «نه بشارت‌دهنده‌ای به سوی ما آمد و نه بیم دهندای» پس بتحقیق (اینک) پیامبر بشارت‌دهنده و بیم‌دهنده به سوی شما آمده است. و الله بر همه چیز تواناست.');
INSERT INTO "hussein_taji" VALUES(5,20,'5:20','(به یاد آورید) هنگامی را که موسی به قوم خود گفت: «ای قوم من، نعمت الله را بر خود بیاد آورید، آنگاه که در میان شما، پیامبرانی قرار داد و شما را فرمانروا (و شاهان) قرار داد و چیزهایی به شما بخشید که به هیچ یک از جهانیان نداده بود.');
INSERT INTO "hussein_taji" VALUES(5,21,'5:21','ای قوم! به سرزمین مقدس که الله برایتان مقرر نموده است، وارد شوید، و به پشت سر خود باز نگردید (و عقب گرد نکنید) که زیان‌کار خواهید شد».');
INSERT INTO "hussein_taji" VALUES(5,22,'5:22','گفتند: «ای موسی! بی‌گمان در آنجا قومی (زورمند و) ستمگرند، و ما هرگز وارد آن نمی‌شویم تا آن‌ها از آن بیرون شوند، پس اگر آن‌ها از آن بیرون شوند، قطعاً ما وارد خواهیم شد».');
INSERT INTO "hussein_taji" VALUES(5,23,'5:23','دو تن از مردانی که (از الله) می‌ترسیدند و الله به آن‌ها نعمت ارزانی داشته بود گفتند: «شما از دروازه بر آنان وارد شوید پس هنگامی‌که وارد شدید، قطعاً شما پیروز خواهید شد، و بر الله توکل کنید؛ اگر ایمان دارید».');
INSERT INTO "hussein_taji" VALUES(5,24,'5:24','(بنی اسرائیل) گفتند: «ای موسی! تا زمانی که آن‌ها در آنجا هستند؛ ما هرگز وارد نخواهیم شد، تو و پروردگارت بروید و بجنگید؛ ما همین جا نشسته‌ایم».');
INSERT INTO "hussein_taji" VALUES(5,25,'5:25','(موسی) گفت: «پروردگارا! من تنها اختیار خودم و برادرم (هارون) را دارم؛ پس میان ما و این گروه نافرمان جدایی بیفکن».');
INSERT INTO "hussein_taji" VALUES(5,26,'5:26','(الله) فرمود: «آن (سرزمین مقدس) تا چهل سال بر آن‌ها حرام شده است پیوسته در زمین سر گردان خواهند بود، پس بر این گروه نافرمان غمگین مباش».');
INSERT INTO "hussein_taji" VALUES(5,27,'5:27','و (ای پیامبر ما) داستان دو فرزندان آدم (هابیل و قابیل) به حق بر آن‌ها بخوان: هنگامی‌که (هر کدام) برای تقرب (الهی) قربانی تقدیم کردند؛ پس از یکی پذیرفته شد و از دیگری پذیرفته نشد (او) گفت: «قطعاً تو را خواهم کشت» (برادرش) گفت: «الله فقط از پرهیزگاران می‌پذیرد (من تقصیری ندارم)');
INSERT INTO "hussein_taji" VALUES(5,28,'5:28','اگر تو دستت برای کشتن من دراز کنی من هرگز دست خود را به سوی تو دراز نخواهم کرد تا تو را بکشم، زیرا من از الله پروردگار جهانیان می‌ترسم.');
INSERT INTO "hussein_taji" VALUES(5,29,'5:29','من می‌خواهم که تو با گناه من و گناه خودت باز گردی (و بار هر دو را بر دوش کشی) و از اهل دوزخ باشی و این است سزای ستمکاران».');
INSERT INTO "hussein_taji" VALUES(5,30,'5:30','پس نفس (سرکش) او؛ کشتن برادرش را برایش آراسته (و موجه و آسان) جلوه داد، و او را کشت و از زیانکاران شد.');
INSERT INTO "hussein_taji" VALUES(5,31,'5:31','آنگاه الله کلاغی را فرستاد که در زمین جستجو (و کاوش) می‌کرد تا به او نشان دهد چگونه جسد برادرش را بپوشاند او گفت: «وای بر من! آیا من آنقدر درمانده‌ام که همچون این کلاغ باشم و جسد برادرم را بپوشانم؟» و سرانجام (از کار زشت خود) پشیمان شد.');
INSERT INTO "hussein_taji" VALUES(5,32,'5:32','به همین سبب بر بنی اسرائیل مقرر داشتیم که هر کس انسانی را بدون اینکه مرتکب قتل نفس یا فسادی در زمین شده باشد؛ بکشد، چنان است که گویی همۀ مردم را کشته باشد و هر کس انسانی را از مرگ نجات دهد؛ چنان است که گویی همۀ مردم را زنده کرده است، و بی‌گمان پیامبران ما، با دلایل روشن برای آن‌ها آمدند، بازهم بسیاری از آن‌ها پس از آن در روی زمین تجاوز و اسراف کردند.');
INSERT INTO "hussein_taji" VALUES(5,33,'5:33','سزای کسانی‌که با الله و پیامبرش می‌جنگند، و در زمین به فساد می‌کوشند، فقط این است که کشته شوند، یا به دار آویخته شوند، یا دست (راست) و پای (چپ) آن‌ها بعکس یکدیگر بریده شود، و یا از سر زمین خود تبعید گردند، این رسوایی آن‌ها در دنیاست، و در آخرت عذاب بزرگی خواهند داشت.');
INSERT INTO "hussein_taji" VALUES(5,34,'5:34','مگر کسانی‌که پیش ازآنکه بر آنان دست یابید، توبه کنند، پس بدانید که همانا الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(5,35,'5:35','ای کسانی‌که ایمان آورده‌اید! از خدا بترسید، و به سوی او تقرب جویید و در راه او جهاد کنید، باشد که رستگار شوید.');
INSERT INTO "hussein_taji" VALUES(5,36,'5:36','یقینا کسانی‌که کافر شدند، اگر تمام آنچه در روی زمین است و همانند آن، متعلق به آن‌ها باشد، و همۀ آن را برای نجات از عذاب روز قیامت فدیه بدهند، از آنان پذیرفته نخواهد شد، و عذاب دردناکی خواهند داشت.');
INSERT INTO "hussein_taji" VALUES(5,37,'5:37','می خواهند از آتش (جهنم) بیرون شوند، ولی نمی‌توانند از آن بیرون شوند (و راه گریزی نیست) و برای آن‌ها عذابی پایدار است.');
INSERT INTO "hussein_taji" VALUES(5,38,'5:38','دست مرد دزد و زن دزد را به سزای عملی که مرتکب شده‌اند؛ بعنوان یک عقوبتی از جانب الله؛ قطع کنید، و الله نیرومند حکیم است.');
INSERT INTO "hussein_taji" VALUES(5,39,'5:39','پس هر کس که بعد از (گناه و) ستمش توبه کند و (اعمال خود را) اصلاح و جبران نماید، همانا الله توبه او را می‌پذیرد، بی‌گمان الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(5,40,'5:40','آیا نمی‌دانی که فرمانروایی آسمان‌ها و زمین از آن الله است؟! هر کس را بخواهد عذاب می‌کند و هر کس را بخواهد می‌بخشد، و الله بر هر چیزی قادر است.');
INSERT INTO "hussein_taji" VALUES(5,41,'5:41','ای پیامبر! کسانی‌که در (راه) کفر شتاب می‌کنند؛ تو را اندوهگین نسازند، (چه) از کسانی‌که به زبان‌شان می‌گویند: «ایمان آوردیم» و قلب آن‌ها ایمان نیاورده است، و (چه) از کسانی‌که یهودی هستند؛ گوش سپردگان به دروغند، و برای گروهی که نزد تو نیامده‌اند (برای جاسوسی) گوش فرا می‌دهند، آن‌ها سخنان (خدا) را از جایگاه‌شان تحریف می‌کنند. و (به یکدیگر) می‌گویند: «اگر این (حکم تحریف شده) به شما داده شد، پس بپذیرید، و اگر آن را به شما داده نشد (از او) دوری کنید». و هر کس که الله گمراهی‌اش را خواسته باشد، هرگز در برابر الله برای او اختیار نداری (و نمی‌توانی از او دفاع نمانی) آن‌ها کسانی هستند که الله نخواسته دل‌های شان را پاک کند، برای آنان در دنیا رسوایی، و در آخرت برایشان عذاب بزرگی است.');
INSERT INTO "hussein_taji" VALUES(5,42,'5:42','آنان گوش سپردگان دروغند (و) مال حرام فراوان می‌خورند، پس اگر نزد تو آمدند در میان آنان داوری کن، یا از آنان روی بگردان، و اگر از آنان روی گردانی به تو هیچ زیانی نمی‌رسانند، و اگر داوری کردی، با عدالت در میان آن‌ها داوری کن، یقیناً الله عادلان را دوست دارد.');
INSERT INTO "hussein_taji" VALUES(5,43,'5:43','و چگونه تو را به داوری می‌طلبند؟ در حالی‌که تورات نزد ایشان است، (و) حکم الله در آن است، سپس بعد از آن (از حکم تو) روی می‌گردانند، و اینان مؤمن نیستند.');
INSERT INTO "hussein_taji" VALUES(5,44,'5:44','ما تورات را نازل کردیم که در آن هدایت و نور است، پیامبرانی که (در برابر فرمان خدا) تسلیم بودند، بر اساس آن برای یهود حکم می‌کردند و (همچنین) الله پرستان و دانشمندان، که حفاظت و پاسداری کتاب الله به آن‌ها سپرده شده و بر آن گواه بودند، (به آن حکم می‌کردند) پس از مردم نترسید، و از من بترسید، و آیات مرا به بهای ناچیز نفروشید، و هر کس به احکامی که الله نازل کرده است حکم نکند؛ کافر است.');
INSERT INTO "hussein_taji" VALUES(5,45,'5:45','و در آن (= تورات) بر آن‌ها (= بنی اسرائیل) مقرر داشتیم که جان در برابر جان، و چشم در برابر چشم و بینی در برابر بینی، و گوش در برابر گوش، و دندان در برابر دندان می‌باشد، و (نیز) زخم‌ها را قصاص است. پس هر کس که از آن در گذرد (و ببخشد) آن کفاره (گناهان) او است، و هر کس به احکامی که الله نازل کرده است حکم نکند؛ ستمگر است.');
INSERT INTO "hussein_taji" VALUES(5,46,'5:46','و در پی آن‌ها (= پیامبران پیشین) عیسی پسر مریم را فرستادیم که تصدیق‌کنندۀ توراتی بود که پیش از او فرستاده شده بود، و انجیل را که در آن هدایت و نور بود به او دادیم که تصدیق‌کنندۀ تورات پیش از او بود، و هدایت و موعظه‌ای برای پرهیزگاران بود.');
INSERT INTO "hussein_taji" VALUES(5,47,'5:47','و اهل انجیل باید به آنچه الله در آن نازل کرده حکم کنند، و هر کس به آنچه الله نازل کرده حکم نکند؛ نافرمانند.');
INSERT INTO "hussein_taji" VALUES(5,48,'5:48','و (این) کتاب (= قرآن) را به حق بر تو نازل کردیم در حالی‌که تصدیق‌کنندۀ کتاب‌های که پیش از آن است و بر آن‌ها شاهد و نگاهبان است، پس به آنچه الله نازل کرده است در میان آن‌ها حکم کن و از هوی و هوس‌های آنان به جای آنچه از حق که به تو رسیده است، پیروی نکن (و از احکام الهی روی مگردان) ما برای هر کدام از شما، آیین و طریقه روشنی قرار دادیم و اگر الله می‌خواست همۀ شما را یک امت قرار می‌داد، ولی (خدا می‌خواهد) شما در آنچه به شما داده است بیازماید، پس در نیکی‌ها بر یکدیگر پیشی گیرید، بازگشت همۀ شما به سوی الله است، آنگاه از آنچه در آن اختلاف می‌کردید به شما خبر می‌هد.');
INSERT INTO "hussein_taji" VALUES(5,49,'5:49','و در میان آن‌ها (= یهود) بر اساس آنچه الله (در قرآن) نازل کرده حکم کن، و از هوس‌های آنان پیروی مکن، و از آنان بر حذر باش، مبادا تو را از بعضی از چیزهایی که الله بر تو نازل کرده منحرف کنند، و اگر آن‌ها (از حکم تو) روی گردانند، پس بدان که الله می‌خواهد آنان را به (خاطر) پاره‌ای از گناهان‌شان مجازات کند، و بی‌گمان بسیاری از مردم نافرمانند.');
INSERT INTO "hussein_taji" VALUES(5,50,'5:50','آیا آن‌ها حکم جاهلیت را می‌خواهند؟!چه کسی بهتر از الله، برای گروهی که یقین دارند، حکم می‌کند؟!');
INSERT INTO "hussein_taji" VALUES(5,51,'5:51','ای کسانی‌که ایمان آوردید! یهود و نصاری را به دوستی بر نگزینید، آنان دوستان یکدیگرند، و کسانی‌که از شما با آن‌ها دوستی کنند، از آن‌ها هستند، همانا الله گروه ستمکار را هدایت نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(5,52,'5:52','پس کسانی را که در دل‌هایشان بیماری (= نفاق) است می‌بینی که در (دوستی با) آنان می‌شتابند (و) می‌گویند: «می‌ترسیم که آسیبی به ما برسد» پس نزدیک است که الله پیروزی یا چیزی (دیگر) از سوی خود (برای مسلمانان) پیش آورد، آنگاه (این افراد) از آنچه در دل خود پنهان داشتند، پشیمان گردند.');
INSERT INTO "hussein_taji" VALUES(5,53,'5:53','و آن‌های که ایمان آورده‌اند می‌گویند: «آیا این‌ها (= منافقان) همان کسانی هستند که با نهایت تأکید به الله سوگند یاد می‌کردند، که آنان با شما هستند؟!» اعمال‌شان تباه شد، و زیانکار شدند.');
INSERT INTO "hussein_taji" VALUES(5,54,'5:54','ای کسانی‌که ایمان آورده‌اید! هر کس از شما که از دین خود برگردد (به الله زیانی نمی‌رساند) الله بزودی گروهی را می‌آورد که آن‌ها را دوست دارد و آن‌ها (نیز) او را دوست دارند، (آنان) در برابر مؤمنان فروتن و در برابر کافران سر سخت و گردان فراز هستند، در راه الله جهاد می‌کنند و از سرزنش هیچ سرزنش کننده‌ای نمی‌هراسند، این فضل الله است که به هر کس بخواهد می‌دهد، و الله گشایشگر داناست.');
INSERT INTO "hussein_taji" VALUES(5,55,'5:55','یار و ولی شما تنها الله است، و پیامبرانش و آن‌ها که ایمان آورده‌اند، (همان)کسانی‌که نماز را بر پا می‌دارند و آنان با خشوع و فروتنی زکات را می‌دهند.');
INSERT INTO "hussein_taji" VALUES(5,56,'5:56','و هر کس الله و پیامبر او و کسانی‌که ایمان آورده‌اند، دوست بدارد، (او از حزب الله است و) یقیناً حزب الله پیروز است.');
INSERT INTO "hussein_taji" VALUES(5,57,'5:57','ای کسانی‌که ایمان آورده‌اید! آنان که دین شما را به تمسخر و بازی گرفته‌اند؛ از (اهل کتاب) کسانی‌که پیش از شما کتاب داده شده‌اند و (همچنین) کفار را؛ دوست نگیرید. و اگر ایمان دارید از الله بترسید.');
INSERT INTO "hussein_taji" VALUES(5,58,'5:58','و چون به نماز ندا دهید آن را به مسخره و بازی می‌گیرند، این بخاطر آن است که آن‌ها مردمی نابخردند.');
INSERT INTO "hussein_taji" VALUES(5,59,'5:59','بگو: «ای اهل کتاب! آیا از ما خرده و عیب می‌گیرید؟! (مگر ما چه کرده‌ایم؟) جز اینکه به الله و آنچه بر ما نازل شده و آنچه پیش از این نازل شده است؛ ایمان آورده‌ایم، و حال آنکه بیشتر شما نافرمان (و از راه حق منحرف) هستید؟!»');
INSERT INTO "hussein_taji" VALUES(5,60,'5:60','بگو: «آیا شما را خبر بدهم از کسانی‌که نزد الله کیفرشان بدتر از این است. کسانی‌که الله آن‌ها را از رحمت خود دور ساخته و بر آن‌ها خشم گرفته و بعضی از آن‌ها را بوزینه‌ها و خوک‌ها گردانیده و بت (= طاغوت) را پرستیده‌اند، اینان بد جایگاه‌تر و از راه راست گم گشته‌ترند».');
INSERT INTO "hussein_taji" VALUES(5,61,'5:61','و چون نزد شما می‌آیند، می‌گویند: «ایمان آورده‌ایم». حال آنکه با کفر وارد شدند و با کفر خارج گشتند، و الله به آنچه کتمان می‌کنند، آگاه‌تر است.');
INSERT INTO "hussein_taji" VALUES(5,62,'5:62','و بسیاری از آن‌ها را می‌بینی که در گناه و تجاوز، و خوردن مال حرام، شتاب می‌کنند، چه بد است عملی که انجام می‌دادند.');
INSERT INTO "hussein_taji" VALUES(5,63,'5:63','چرا الله پرستان و دانشمندان آن‌ها را از سخنان گناه‌آلود، و خوردن مال حرام، نهی نمی‌کنند؟! چه بد است کاری که انجام می‌دادند.');
INSERT INTO "hussein_taji" VALUES(5,64,'5:64','و یهود گفتند: «دست الله بسته است» دست‌هایشان بسته باد! و بخاطر آنچه گفتند، لعنت شدند. بلکه دو دست او گشاده است، هر گونه بخواهد می‌بخشد، و این آیات که از طرف پروردگارت بر تو نازل شده بر سر کشی و کفر بسیاری از آن‌ها می‌افزاید، و ما در میان آنان تا روز قیامت دشمنی و کینه افکندیم، و هر زمان آتش جنگی افروختند، الله آن را خاموش کرد، و آنان برای فساد در زمین می‌کوشند، و الله مفسدان را دوست ندارد.');
INSERT INTO "hussein_taji" VALUES(5,65,'5:65','و اگر اهل کتاب ایمان بیاورند و تقوا پیشه کنند، یقیناً گناهان‌شان را خواهیم زدود و آن‌ها را به باغ‌هایی پر نعمت (بهشت) وارد خواهیم کرد.');
INSERT INTO "hussein_taji" VALUES(5,66,'5:66','و اگر آنان تورات و انجیل، و آنچه را که از سوی پروردگارشان بر آن‌ها نازل شده (از کتاب‌های آسمانی و قرآن) بر پا دارند (و به آن عمل نمایند) از فراز و فرود پاهایشان (= از آسمان و زمین) روزی خواهند خورد، گروهی از آن‌ها معتدل و میانه رو هستند، و بسیاری از آنان اعمال بدی انجام می‌دهند.');
INSERT INTO "hussein_taji" VALUES(5,67,'5:67','ای پیامبر! آنچه از طرف پروردگارت برتو نازل شده است، (به مردم) برسان، و اگر (این کار) نکنی، رسالت او را نرسانده‌ای، و الله تو را از (شر) مردم حفظ می‌کند، همانا الله گروه کافران را هدایت نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(5,68,'5:68','بگو: «ای اهل کتاب! شما بر هیچ (آیین درستی) نیستید، مگر اینکه تورات و انجیل و آنچه را که از طرف پروردگارتان بر شما نازل شده است، بر پا دارید». و بتحقیق آنچه بر تو از سوی پروردگارت نازل شده، بر سرکشی و کفر بسیاری از آن‌ها می‌افزاید، پس بر (این) گروه کافران، غمگین مباش.');
INSERT INTO "hussein_taji" VALUES(5,69,'5:69','همانا کسانی‌که ایمان آورده‌اند، و کسانی‌که یهودی هستند، و صابئان و نصاری، هر کس که به الله و روز قیامت ایمان بیاورد و کارهای شایسته انجام دهد، نه ترسی بر آن‌ها خواهد بود و نه آنان اندوهگین شوند.');
INSERT INTO "hussein_taji" VALUES(5,70,'5:70','یقیناً ما از بنی اسرائیل پیمان گرفتیم، و رسولانی به سوی آن‌ها فرستادیم، هر گاه که پیامبری چیزی بر خلاف دلخواه آن‌ها می‌آورد، گروهی را تکذیب می‌کردند و گروهی را می‌کشتند.');
INSERT INTO "hussein_taji" VALUES(5,71,'5:71','و پنداشتند که آزمایش (و مجازاتی) در کار نیست، لذا کور و کر شدند. سپس (توبه کردند و) الله توبۀ آن‌ها را پذیرفت، باز بسیاری از آن‌ها کور و کر شدند، و الله به آنچه انجام می‌دهند؛ بیناست.');
INSERT INTO "hussein_taji" VALUES(5,72,'5:72','آن‌ها که گفتند: «الله، همان مسیح پسر مریم است» یقیناً کافر شدند، در حالی‌که (خود) مسیح گفت: «ای بنی اسرائیل! الله را که پروردگار من و پروردگار شما است؛ پرستش کنید، همانا هر کس به الله شرک آورد، الله بهشت را بر او حرام کرده است، و جایگاه او دوزخ است، و ستمکاران را یاوری نیست».');
INSERT INTO "hussein_taji" VALUES(5,73,'5:73','آن‌ها که گفتند: «الله سومین (اقنوم) ثلاثه است» به یقین کافر شدند، معبودی (به حق) جز معبود یگانه نیست، و اگر از آنچه می‌گویند باز نایستند، قطعاً به کافران شان عذاب دردناکی خواهد رسید.');
INSERT INTO "hussein_taji" VALUES(5,74,'5:74','آیا به سوی الله باز نمی‌گردند، و از او طلب آمرزش نمی‌کنند؟! و الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(5,75,'5:75','مسیح پسر مریم فرستاده‌ای بیش نیست که پیش از او (نیز) فرستادگان دیگری بودند، و مادرش زنی راستگوی بود، هر دو غذا می‌خوردند، بنگر چگونه نشانه‌ها را برای آن‌ها روشن می‌سازیم، پس بنگر چگونه (از حق) باز گردانده می‌شوند!');
INSERT INTO "hussein_taji" VALUES(5,76,'5:76','بگو: «آیا چیزی را به جای الله می‌پرستید که دارای سود و زیانی برای شما نیست؟! و الله شنوای داناست».');
INSERT INTO "hussein_taji" VALUES(5,77,'5:77','بگو: «ای اهل کتاب! در دین خود به ناحق غلو (و زیاده‌روی) نکنید، و از هوس‌های گروهی که از پیش گمراه شدند و بسیاری را گمراه کردند و از راه راست منحرف گشتند؛ پیروی نکنید».');
INSERT INTO "hussein_taji" VALUES(5,78,'5:78','کافران بنی اسرائیل، بر زبان داوود و عیسی پسر مریم لعنت شدند، این بخاطر آن بود که نافرمانی (و گناه) کردند و (از حد) تجاوز می‌نمودند.');
INSERT INTO "hussein_taji" VALUES(5,79,'5:79','آن‌ها از کار زشتی که انجام می‌دادند، یکدیگر را نهی نمی‌کردند، قطعاً بد کاری انجام می‌دادند!');
INSERT INTO "hussein_taji" VALUES(5,80,'5:80','بسیاری از آن‌ها را می‌بینی که با کافران (و بت‌پرستان) دوستی می‌کنند، چه بد است آنچه نفس‌هایشان برای آنان پیش فرستاده است، که الله بر آن‌ها خشم بیاورد، و آنان در عذاب جاودانه خواهند ماند.');
INSERT INTO "hussein_taji" VALUES(5,81,'5:81','و اگر به الله و پیامبر و آنچه براو نازل شده، ایمان می‌آوردند (هرگز) آنان (کافران) را به دوستی برنمی گزیدند، ولی بسیاری از آن‌ها فاسقند.');
INSERT INTO "hussein_taji" VALUES(5,82,'5:82','به یقین دشمن‌ترین مردم نسبت به مؤمنان را؛ یهود و مشرکان [ مراد از مشرکین آنانی‌اند که معتقد به معبودان متعدد هستند و نیز بت‌پرست‌ها و کسانی‌که منکر وحدانیت الله متعال و رسالت محمد صلی الله علیه وسلم می‌باشند.] خواهی یافت، و نزدیک‌ترین دوستان به مؤمنان را؛ کسانی می‌یابی که می‌گویند: «ما نصاری هستیم». این بخاطر آن است که از (میان) آن‌ها کشیشان و تارکان دنیا هستند، و آن‌ها تکبّر نمی‌ورزند.');
INSERT INTO "hussein_taji" VALUES(5,83,'5:83','و چون آیاتی را که بر پیامبر نازل شده بشنوند، چشمهای آن‌ها را می‌بینی که اشک ریزان می‌شود، بخاطر حقیقتی که در یافته‌اند، و می‌گویند: «پروردگارا! ایمان آوردیم، پس ما را در زمره گواهان بنویس.');
INSERT INTO "hussein_taji" VALUES(5,84,'5:84','چرا به الله و آنچه از حق به ما رسیده است، ایمان نیاوریم، در حالی‌که آرزو داریم پروردگارمان ما را در زمره صالحان در آورد».');
INSERT INTO "hussein_taji" VALUES(5,85,'5:85','پس الله به خاطر این سخن که گفتند، به آن‌ها باغ‌هایی (از بهشت) پاداش داد که از زیر (درختان) آن نهرها جاری است، و جاودانه در آن خواهند ماند، و این است جزای نیکو کاران.');
INSERT INTO "hussein_taji" VALUES(5,86,'5:86','و کسانی‌که کافر شدند و آیات ما را تکذیب کردند، آن‌ها اهل دوزخند.');
INSERT INTO "hussein_taji" VALUES(5,87,'5:87','ای کسانی‌که ایمان آورده‌اید! چیزهای پاکیزه‌ای را که الله برای شما حلال کرده است (بر خود) حرام نکنید، و از حد تجاوز ننمایید، همانا الله متجاوزان را دوست نمی‌دارد.');
INSERT INTO "hussein_taji" VALUES(5,88,'5:88','و از چیزهای حلال (و) پاکیزه‌ای که الله به شما روزی داده است؛ بخورید، و از الله که به او ایمان دارید؛ بترسید.');
INSERT INTO "hussein_taji" VALUES(5,89,'5:89','الله شما را بخاطر سوگندهای لغو (و بیهوده) تان مؤاخذه نمی‌کند، ولی شما را بخاطر سوگندهایی که (از روی قصد) محکم کرده‌اید؛ مؤاخذه می‌کند، پس کفارۀ آن اطعام ده مسکین است از غذاهای متوسطی که به خانوادۀ خود می‌خورانید، یا لباس پوشانیدن آن‌ها، و یا آزاد کردن یک برده، پس اگر کسی (هیچ یک از این‌ها) نیابد، سه روز روزه بگیرد، این کفارۀ سوگندهای شماست، هر گاه که سوگند یاد کردید (و به آن وفا نکردید) و سوگندهای خود را حفظ کنید (و نشکنید) این چنین الله آیات خود را برای شما بیان می‌کند، باشد که شکر گزارید.');
INSERT INTO "hussein_taji" VALUES(5,90,'5:90','ای کسانی‌که ایمان آورده‌اید! همانا شراب و قمار و بت‌ها، و تیرهای قرعه، پلید و از عمل شیطان است. پس از آن‌ها دوری کنید، تا رستگار شوید.');
INSERT INTO "hussein_taji" VALUES(5,91,'5:91','همانا شیطان می‌خواهد با شراب و قمار در میان شما عداوت و کینه ایجاد کند و شما را از یاد الله و از نماز باز دارد؛ پس آیا شما خودداری خواهید کرد؟!');
INSERT INTO "hussein_taji" VALUES(5,92,'5:92','و الله را اطاعت کنید و (نیز) پیامبر را اطاعت کنید و (از نافرمانی) برحذرباشید، پس اگر روی گرداندید؛ بدانید که بر پیامبر ما جز ابلاغ آشکار چیز دیگری نیست.');
INSERT INTO "hussein_taji" VALUES(5,93,'5:93','بر کسانی‌که ایمان آورده و کارهای شایسته انجام داده‌اند گناهی در آنچه خورده‌اند نیست؛ اگر پرهیزکاری کنند و ایمان آورند و کارهای شایسته انجام دهند، سپس پرهیزکاری کنند و ایمان آورند، سپس پرهیزکاری کنند و نیکی نمایند، و الله نیکوکاران را دوست می‌دارد.');
INSERT INTO "hussein_taji" VALUES(5,94,'5:94','ای کسانی‌که ایمان آورده‌اید! الله شما را به چیزی از شکار که دست‌ها و نیزه‌هایتان به آن می‌رسد؛ آزمایش می‌کند تا الله معلوم بدارد چه کسی در نهان از او می‌ترسد، پس هر کس بعد از آن تجاوز کند؛ برایش عذاب دردناکی خواهد بود.');
INSERT INTO "hussein_taji" VALUES(5,95,'5:95','ای کسانی‌که ایمان آورده‌اید! در حال احرام شکار را نکشید، و هر کس از شما به عمد آن را بکشد؛ باید کفاره‌ای همانند آن از چهارپایان بدهد (بشرطی) که دو نفر عادل از شما (برابر بودن) آن را تصدیق کنند و (بصورت) قربانی به کعبه برساند یا (به جای قربانی) اطعام مساکین کند یا معادل آن روزه بگیرد تا کیفر عمل خود را بچشد، الله از آنچه گذشته در گذشت، و هر کس که (به این گناه) باز گردد؛ الله از او انتقام می‌گیرد، و الله نیرومند (و) انتقام‌گیر است.');
INSERT INTO "hussein_taji" VALUES(5,96,'5:96','شکار دریا و طعام آن برای شما حلال شده است تا برای شما و کاروانیان بهره‌ای باشد، و شکار بیابان تا موقعی که در احرام هستید، بر شما حرام است، و از الله که بسوی او محشور می‌شوید؛ بترسید.');
INSERT INTO "hussein_taji" VALUES(5,97,'5:97','الله کعبه - بیت‌الحرام-، و ماه حرام و قربانی (بی‌نشان) و قربانی نشاندار را وسیله‌ای برای استواری و سامان بخشیدن (به کار) مردم قرار داد، این (گونه احکام برای اینست که) بدانید بتحقیق الله آنچه در آسمان‌ها و آنچه در زمین است می‌داند، و مسلماً الله به هر چیز داناست.');
INSERT INTO "hussein_taji" VALUES(5,98,'5:98','بدانید که الله سخت کیفر است و (در عین حال) الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(5,99,'5:99','بر پیامبر (وظیفه‌ای) جز رسانیدن (پیام الهی) نیست، و الله آنچه را که آشکار می‌کنید و آنچه را که پنهان می‌دارید؛ می‌داند.');
INSERT INTO "hussein_taji" VALUES(5,100,'5:100','بگو: «ناپاک و پاک یکسان نیست. و اگر چه کثرت ناپاک‌ها، تو را به شگفتی اندازد»، پس ای خردمندان، از الله بترسید تا رستگار شوید.');
INSERT INTO "hussein_taji" VALUES(5,101,'5:101','ای کسانی‌که ایمان آورده‌اید! از چیزهای نپرسید که اگر برای شما آشکار گردد، شما را اندوهگین کند، و اگر به هنگام نزول قرآن، از آن‌ها سؤال کنید برای شما آشکار می‌شود الله از آن‌ها عفو کرده است، و الله آمرزندۀ بردبار است.');
INSERT INTO "hussein_taji" VALUES(5,102,'5:102','به راستی گروهی قبل از شما از آن سؤال کردند، سپس (وقتی که جوابشان آمد) بدان کافر شدند.');
INSERT INTO "hussein_taji" VALUES(5,103,'5:103','الله هیچ‌گونه «بحیره» و نه«سائبه» و نه «وصیله» و نه «حام» را قرار نداده است، ولی کسانی‌که کافر شدند، بر الله دروغ می‌بندند، و بیشترشان در نمی‌یابند.');
INSERT INTO "hussein_taji" VALUES(5,104,'5:104','و هنگامی‌که به آن‌ها گفته شود: «به سوی آنچه الله نازل کرده و به سوی پیامبر بیایید» گویند: «آنچه نیاکان‌مان را بر آن یافته‌ایم، ما را بس است». آیا اگر نیاکان آن‌ها چیزی نمی‌دانستند و هدایت نیافته بودند (باز هم از آن‌ها پیروی می‌کنند)؟.');
INSERT INTO "hussein_taji" VALUES(5,105,'5:105','ای کسانی‌که ایمان آورده‌اید! مراقب خود باشید اگر شما هدایت یافته باشید، گمراهی کسانی‌که گمراه شده‌اند به شما زیانی نمی‌رساند، بازگشت همۀ شما به سوی الله است، آنگاه شما را از آنچه عمل می‌کردید؛ آگاه می‌سازد.');
INSERT INTO "hussein_taji" VALUES(5,106,'5:106','ای کسانی‌که ایمان آورده‌اید! چون مرگ یکی از شما فرا رسد در موقع وصیت باید از میان شما دو نفر عادل را گواه بگیرید یا اگر مسافرت کردید و مصیبت مرگ به شما رسید (و مسلمانی را نیافتید) دو نفر از غیر خودتان را به گواهی بطلبید و اگر (هنگام ادای شهادت در صدق آن‌ها) شک کردید آن دو را بعد از نماز نگاه دارید تا به الله سوگند بخورند که: «ما حاضر نیستیم حق را به چیزی بفروشیم اگر چه خویشان‌مان باشند و شهادت الله را کتمان نمی‌کنیم که در این صورت از گناهکاران خواهیم بود».');
INSERT INTO "hussein_taji" VALUES(5,107,'5:107','پس اگر معلوم شد که آن دو مرتکب گناه (خیانت) شده‌اند (و حق را کتمان نموده‌اند) دو نفر از کسانی‌که ستم دیده‌اند و نسبت به میت اولی هستند جای آن‌ها را خواهند گرفت، آنگاه به الله سوگند یاد کنند که: «گواهی ما از گواهی آن دو درست‌تر است؛ و ما تجاوزی نکرده‌ایم، و اگر چنین کرده باشیم از ستمکاران خواهیم بود».');
INSERT INTO "hussein_taji" VALUES(5,108,'5:108','این (روش) به آنکه گواهی را به گونۀ درستش ادا کنند؛ نزدیک‌تر است، و یا بترسند که بعد از سوگندها(ی ورثه) سوگندهای‌شان رد شود؛ و از الله بترسید، و گوش فرا دهید و (بدانید که) الله گروه فاسقان را هدایت نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(5,109,'5:109','روزی‌که الله پیامبران را گرد آورد؛ آنگاه بگوید: «(در برابر دعوت حق) چه پاسخی به شما داده شد؟» گویند: «ما را علمی نیست؛ بی‌گمان تو خود دانای رازهای نهانی».');
INSERT INTO "hussein_taji" VALUES(5,110,'5:110','(به یاد بیاور) هنگامی را که الله (به عیسی پسر مریم) می‌فرماید: «ای عیسی پسر مریم! نعمتی را که به تو و مادرت بخشیدم به یاد آور؛ زمانی که تو را با «روح القدس» (جبرئیل) تقویت کردم، در گهواره و بزرگی با مردم سخن گفتی، و هنگامی‌که کتاب و حکمت، و تورات و انجیل را به تو آموختم، و هنگامی‌که به فرمان من از گِل چیزی مانند شکل پرنده‌ای ساختی؛ پس در آن می‌دمیدی و به فرمان من پرنده‌ای می‌شد، و کور مادر زاد و مبتلا به بیماری پیسی را به فرمان من شفا می‌دادی، و آنگاه که مردگان را به فرمان من (زنده) بیرون می‌آوردی، و هنگامی‌که بنی‌اسرائیل را از آسیب رساندن به تو بازداشتم، آنگاه که دلایل روشن برای آن‌ها آوردی، پس کسانی که از آن‌ها کافر شدند؛ گفتند: «این‌ها جز جادوی آشکار (چیزی دیگر) نیست».');
INSERT INTO "hussein_taji" VALUES(5,111,'5:111','و (به یادآور) زمانی را که به حواریون وحی فرستادم که: «به من و فرستاده من، ایمان بیاورید» گفتند: «ایمان آوردیم، و گواه باش که ما تسلیم شده‌ایم».');
INSERT INTO "hussein_taji" VALUES(5,112,'5:112','(و به یاد آور) چون حواریون گفتند: «ای عیسی پسر مریم! آیا پروردگارت می‌تواند سفره‌ای از آسمان بر ما نازل کند؟». (عیسی در پاسخ) گفت: «اگر مؤمن هستید از الله بترسید».');
INSERT INTO "hussein_taji" VALUES(5,113,'5:113','گفتند: «می‌خواهیم از آن بخوریم و دل‌های مان آرام گیرد و بدانیم که به ما راست گفته‌ای و بر آن از گواهان باشیم».');
INSERT INTO "hussein_taji" VALUES(5,114,'5:114','عیسی پسر مریم (دعا کرد و) گفت: بارالها، ای پروردگار ما، از آسمان سفره‌ای بر ما بفرست تا برای اول و آخر ما عیدی باشد و نشانه‌های از تو، و به ما روزی ده که تو بهترین روزی دهندگانی».');
INSERT INTO "hussein_taji" VALUES(5,115,'5:115','الله (دعای او را اجابت کرد و) فرمود: «من آن را بر شما نازل می‌کنم؛ اما هر یک از شما که بعد از آن کافر شود، او را چنان عذابی می‌کنم که هیچ کس از جهانیان را چنان عذاب نکرده باشم!».');
INSERT INTO "hussein_taji" VALUES(5,116,'5:116','و (بیاد بیاور) که الله می‌فرماید: «ای عیسی پسر مریم! آیا تو به مردم گفتی که من و مادرم را به عنوان دو معبود به جای الله انتخاب کنید؟» (عیسی) گوید: «تو منزهی! من حق ندارم آنچه را که شایسته من نیست بگویم! اگر چنین سخنی را گفته باشم؛ بی‌گمان تو می‌دانی، تو از آنچه در ضمیر من است آگاهی و من از آنچه در ذات (پاک) توست؛ نمی‌دانم، یقیناً تو خود دانای رازهای نهانی.');
INSERT INTO "hussein_taji" VALUES(5,117,'5:117','من چیزی جز آنچه مرا به آن فرمان دادی به آن‌ها نگفتم (به آن‌ها گفتم:) الله را بپرستید که پروردگار من و پروردگار شماست و تا زمانی که در میان آن‌ها بودم مراقب و گواه‌شان بودم و چون مرا بر گرفتی تو خودت مراقب آن‌ها بودی و تو بر هر چیز گواهی.');
INSERT INTO "hussein_taji" VALUES(5,118,'5:118','اگر آن‌ها را عذاب کنی آنان بندگان تو هستند و اگر آن‌ها را بیامرزی تو نیرومند حکیمی».');
INSERT INTO "hussein_taji" VALUES(5,119,'5:119','الله فرمود: «امروز؛ روزی است که راستی راستگویان به آن‌ها سود می‌بخشد، برای آن‌ها باغ‌هایی (از بهشت) است که نهرها از زیر (درختان) آن جاری است و تا ابد، جاودانه در آن می‌مانند، الله از آن‌ها خشنود است و آن‌ها از او خشنودند، این رستگاری بزرگ است».');
INSERT INTO "hussein_taji" VALUES(5,120,'5:120','فرمانروایی آسمان‌ها و زمین و آنچه در آن‌هاست از آنِ الله است؛ و او بر هر چیز تواناست.');
INSERT INTO "hussein_taji" VALUES(6,1,'6:1','ستایش برای الله است که آسمان‌ها و زمین را آفرید و تاریک‌ها و نور را پدید آورد، و با این همه آن‌هایی که کافران شدند با پروردگار خویش دیگری را برابر می‌دارند (و به او شرک می‌آورند).');
INSERT INTO "hussein_taji" VALUES(6,2,'6:2','او کسی است که شما را از گِل آفرید، سپس مدتی را مقرر کرد، و (برای قیامت) مدتی معین نزد اوست، با این همه شک می‌کنید.');
INSERT INTO "hussein_taji" VALUES(6,3,'6:3','و اوست الله معبود در آسمان‌ها و در زمین، نهان شما و آشکار شما را می‌داند، و آنچه به دست می‌آورید (و انجام می‌دهید) می‌داند.');
INSERT INTO "hussein_taji" VALUES(6,4,'6:4','و هیچ نشانه‌ای از نشانه‌های پروردگارشان برای آنان نمی‌آید، مگر اینکه روی‌گردان می‌شوند.');
INSERT INTO "hussein_taji" VALUES(6,5,'6:5','به راستی آنان حق را هنگامی‌که سراغ‌شان آمد، تکذیب کردند، پس به زودی خبر چیزهایی که به مسخره می‌گرفتند به آن‌ها خواهد رسید.');
INSERT INTO "hussein_taji" VALUES(6,6,'6:6','آیا ندیدند که پیش از آن‌ها چه قدر از امت‌ها را هلاک کردیم؟ (امت‌هایی) که در زمین به آن‌ها (قدرت و) اقتدار داده بودیم که به شما (چنان قدرت و) اقتداری نداده‌ایم، و باران‌های پی در پی از آسمان بر آن‌ها فرستادیم، و از زیر (آبادی‌های) آن‌ها رودها را جای ساختیم، آنگاه آنان را به کیفر گناهان‌شان نابودشان کردیم، و پس از آن‌ها گروهی دیگر را پدید آوردیم.');
INSERT INTO "hussein_taji" VALUES(6,7,'6:7','و اگر کتابی (نوشته) بر کاغذ بر تو نازل کنیم، که آن را با دست‌های خود لمس کنند؛ قطعاً کسانی‌که کافر شدند، می‌گویند: «این (چیزی) جز جادویی آشکار نیست».');
INSERT INTO "hussein_taji" VALUES(6,8,'6:8','و گفتند: «چرا فرشته‌ای بر او نازل نشده است؟ (تا او را تصدیق کند؟)» و اگر فرشته‌ای می‌فرستادیم، (و ایمان نمی‌آوردند) قطعاً کار به پایان می‌رسید، سپس به آن‌ها (هیچگونه) مهلتی داده نمی‌شد (و هلاک می‌شدند)');
INSERT INTO "hussein_taji" VALUES(6,9,'6:9','و اگر او (= فرستاده) را فرشته‌ای قرار می‌دادیم، حتما وی را به صورت یک مرد در می‌آوردیم، باز هم (به گمان آنان کار را بر آن‌ها) مشتبه می‌ساختیم همان‌گونه که آن‌ها (بر خود و دیگران) مشتبه می‌سازند (و جای شبه باقی می‌ماند).');
INSERT INTO "hussein_taji" VALUES(6,10,'6:10','(ای پیامبر) بتحقیق پیامبران پیش از تو (نیز) مسخره شدند، پس (سزای) آنچه مسخره‌اش می‌کردند؛ بر سر تمسخر کنندگان فرود آمد (و آن‌ها را احاطه کرد).');
INSERT INTO "hussein_taji" VALUES(6,11,'6:11','بگو: «در زمین گردش کنید، سپس بنگرید سر انجام تکذیب کنندگان چگونه بوده است».');
INSERT INTO "hussein_taji" VALUES(6,12,'6:12','بگو: «آنچه در آسمان‌ها و زمین است، از آن کیست؟» بگو: «از آن الله است، که رحمت را بر خود مقرر داشته است، قطعاً شما را در روز قیامت که در آن شکی نیست، گرد خواهد آورد، کسانی‌که به خویش زیان رسانده‌اند پس ایمان نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(6,13,'6:13','و از آن اوست، آنچه در شب و روز آرام گیرد، و او شنوایی داناست».');
INSERT INTO "hussein_taji" VALUES(6,14,'6:14','بگو: «آیا غیر الله را ولی و یاور گیرم؟ (پروردگاری) که پدید آورندۀ آسمان‌ها و زمین است، اوست که روزی می‌دهد، و به او روزی نمی‌دهند». بگو: «من مأمورم که نخستین کسی باشم که اسلام آورده است، و (فرمان یافته ام که) از مشرکان نباش».');
INSERT INTO "hussein_taji" VALUES(6,15,'6:15','بگو: «همانا من اگر نافرمانی پروردگارم کنم، از عذاب روز بزرگ می‌ترسم».');
INSERT INTO "hussein_taji" VALUES(6,16,'6:16','هر کس که در آن روز (عذاب را) از او بگردانند (و به وی عذاب نرسد) قطعاً (الله) به او رحم نموده است؛ و این پیروزی آشکار است.');
INSERT INTO "hussein_taji" VALUES(6,17,'6:17','اگر الله (بخواهد) زیانی به تو برساند، هیچ کس جز او نمی‌تواند آن را دفع کند، و اگر خیری به تو رساند، پس او بر همه چیز تواناست.');
INSERT INTO "hussein_taji" VALUES(6,18,'6:18','و اوست که بر بندگان خود مسلط و چیره است، و او حکیم آگاه است.');
INSERT INTO "hussein_taji" VALUES(6,19,'6:19','بگو: «چه چیز در گواهی بزرگ‌تر (و برتر) است؟» بگو: «الله، میان من و شما گواه است، و این قرآن بر من وحی شده تا شما و تمام کسانی را که به آن‌ها می‌رسد؛ با آن بیم دهم، آیا براستی شما گواهی می‌دهید که با الله معبودهای دیگری هستند؟» بگو: «من گواهی نمی‌دهم» بگو: «همانا او تنها معبود یگانه است، و من از آنچه با او شریک قرار می‌دهید، بیزارم».');
INSERT INTO "hussein_taji" VALUES(6,20,'6:20','کسانی‌که به آن‌ها کتاب داده‌ایم، به خوبی او را می‌شناسند، همان گونه که فرزندان خود را می‌شناسند، کسانی‌که به خویش زیان رسانده‌اند پس ایمان نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(6,21,'6:21','و چه کسی ستمکارتر است از آن کس که بر الله دروغ ببندد، یا آیاتش را تکذیب کند؟ به راستی ستمکاران رستگار نخواهند شد.');
INSERT INTO "hussein_taji" VALUES(6,22,'6:22','و روزی‌که همه آن‌ها را گرد آوریم، سپس به کسانی‌که شرک ورزیده‌اند؛ می‌گوییم: «معبودهایی که آن‌ها را شریک الله می‌پنداشتید؛ کجا هستند؟»');
INSERT INTO "hussein_taji" VALUES(6,23,'6:23','آنگاه پاسخ و عذرشان جز این نباشد که گویند: «به الله پروردگارمان سوگند، که ما مشرک نبودیم!»');
INSERT INTO "hussein_taji" VALUES(6,24,'6:24','بنگر چگونه بر خویشتن دروغ گفتند، و آن دروغ‌هایی که ساخته بودند همه محو و نابود شد.');
INSERT INTO "hussein_taji" VALUES(6,25,'6:25','و از آنان کسانی هستند که به تو گوش فرا می‌دهند، و ما بر دل‌های شان پرده‌ها افکنده‌ایم تا آن را نفهمند، و در گوش‌هایشان سنگینی (قرار داده‌ایم) و اگر تمام معجزه‌ها را ببینند، به آن ایمان نمی‌آورند، تا اینکه نزد تو آیند و با تو مجادله کنند، کسانی‌که کافر شدند، گویند: «این جز افسانه‌های پیشینیان نیست».');
INSERT INTO "hussein_taji" VALUES(6,26,'6:26','و آن‌ها (مردم را) از (پیروی) او (= پیامبر) باز می‌دارند، و خود (نیز) از او دور می‌شوند، آن‌ها جز خویشتن (کسی) را هلاک نمی‌کنند و نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(6,27,'6:27','و اگر (آن‌ها را) هنگامی‌که در برابر آتش نگه داشته‌اند؛ ببینی (شگفت زده می‌شوی) پس می‌گویند: «ای کاش باز گردانده می‌شدیم، و آیات پروردگارمان را تکذیب نمی‌کردیم، و از مؤمنان می‌بودیم».');
INSERT INTO "hussein_taji" VALUES(6,28,'6:28','(نه چنین نیست) بلکه آنچه را که پیش از این پنهان می‌کردند برایشان آشکار شده، و اگر باز گردانده شوند، بی‌تردید به آنچه از آن نهی شده بودند؛ باز می‌گردند، و اینان دروغگویانند.');
INSERT INTO "hussein_taji" VALUES(6,29,'6:29','و گفتند: «هیچ چیزی جز این زندگی دنیای ما نیست، و ما هرگز بر انگیخته نخواهیم شد».');
INSERT INTO "hussein_taji" VALUES(6,30,'6:30','و اگر آن‌ها را به هنگامی‌که در پیشگاه پروردگارشان بازداشته شده‌اند ببینی (شگفت زده می‌شوی، و الله به آن‌ها) می‌فرماید: «آیا این حق نیست؟» گویند: «چرا قسم به پروردگارمان (حق است) (الله) می‌فرماید: «پس به سزای آنکه کفر می‌ورزیدید عذاب را بچشید».');
INSERT INTO "hussein_taji" VALUES(6,31,'6:31','آن‌ها که لقای پروردگار را تکذیب کردند؛ قطعاً زیان دیدند، هنگامی‌که ناگهان قیامت به سراغ‌شان بیاید، گویند: «ای افسوس بر ما که دربارۀ آن کوتاهی کردیم». و آن‌ها (بار) گناهان‌شان را بر پشت‌های خود حمل می‌کنند، چه بد باری است آنچه را حمل می‌کنند.');
INSERT INTO "hussein_taji" VALUES(6,32,'6:32','زندگی دنیا چیزی جز بازیچه و سرگرمی نیست، و قطعاً سرای آخرت برای کسانی‌که پرهیزگارند، بهتر است، آیا نمی‌اندیشید؟!');
INSERT INTO "hussein_taji" VALUES(6,33,'6:33','یقیناً ما می‌دانیم که آنچه می‌گویند، تو را غمگین می‌کند، پس آن‌ها (در حقیقت) تو را تکذیب نمی‌کنند، بلکه (این) ستمکاران، آیات الله را انکار می‌کنند.');
INSERT INTO "hussein_taji" VALUES(6,34,'6:34','و براستی پیامبرانی پیش از تو تکذیب شدند، و در برابر تکذیب‌ها صبر کردند و (در این راه) آزار دیدند؛ تا یاری ما به آن‌ها رسید، و هیچ کس نمی‌تواند کلام الله را تغییر دهد، و بی‌گمان اخبار پیامبران به تو رسیده است.');
INSERT INTO "hussein_taji" VALUES(6,35,'6:35','و اگر روی گردانی آن‌ها بر تو گران آمده است، پس اگر می‌توانی نقبی در زمین (بزنی) یا نردبانی به آسمان بجوی، آنگاه نشانه‌ای (دیگر) برای آن‌ها بیاوری! (پس چنان کن). و اگر الله بخواهد آن‌ها را بر هدایت گرد می‌آورد، پس هرگز از نادانان مباش.');
INSERT INTO "hussein_taji" VALUES(6,36,'6:36','تنها کسانی (دعوت تو را) اجابت می‌کنند که گوش شنوا دارند، و الله مردگان را (در روز قیامت) بر می‌انگیزد، سپس به سوی او باز گردانده می‌شوند.');
INSERT INTO "hussein_taji" VALUES(6,37,'6:37','و گفتند: «چرا نشانه‌ای از طرف پروردگارش بر او نازل نشده است؟» بگو: «بی‌گمان الله، قادر است که نشانه‌ای نازل کند» ولی بیشتر آن‌ها نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(6,38,'6:38','هیچ جننبده‌ای در زمین نیست، و نه هیچ پرنده‌ای که با دو بال خود پرواز می‌کند، مگر اینکه امت‌هایی مانند شما هستند، ما در کتاب (لوح محفوظ) هیچ چیز را فرو گذار نکردیم، سپس به سوی پروردگارشان محشور می‌گردند.');
INSERT INTO "hussein_taji" VALUES(6,39,'6:39','و کسانی‌که آیات ما را تکذیب کردند، کر و لال هستند (از شنیدن حق و گفتن حق و) در تاریکی‌های قرار دارند، هر کس را الله بخواهد گمراه می‌کند، و هر کس را بخواهد؛ او را بر راه راست قرار خواهد داد.');
INSERT INTO "hussein_taji" VALUES(6,40,'6:40','بگو: «اگر راست می‌گویید، به من خبر دهید اگر عذاب الله به سراغ شما آید، یا قیامت بر پا شود، آیا غیر از الله را می‌خوانید؟!»');
INSERT INTO "hussein_taji" VALUES(6,41,'6:41','(نه) بلکه تنها او را می‌خوانید، و اگر او بخواهد مشکلی را که برای آن؛ او را خوانده‌اید، بر طرف می‌سازد، و آنچه را شریک او قرار می‌دهید؛ فراموش خواهید کرد.');
INSERT INTO "hussein_taji" VALUES(6,42,'6:42','و ما بتحقیق به سوی امت‌هایی که پیش از تو بودند، (پیامبرانی) فرستادیم، (چون نافرمانی کردند) پس آن‌ها را به رنج و سختی‌ها گرفتار ساختیم، تا زاری کنند (و تسلیم گردند)');
INSERT INTO "hussein_taji" VALUES(6,43,'6:43','پس چرا هنگامی‌که عذاب ما به آن‌ها رسید زاری نکردند؟ (و تسلیم نشدند؟!) بلکه دل‌های آن‌ها سخت شد، و شیطان، هر کاری که می‌کردند، در نظرشان آراست.');
INSERT INTO "hussein_taji" VALUES(6,44,'6:44','پس چون آنچه را به آن‌ها یاد آوری شده بود فراموش کردند، درهای همه چیز را به روی آن‌ها گشودیم تا وقتی که به آنچه داده شدند شاد گشتند، ناگهان آن‌ها را گرفتیم، در این هنگام همه مأیوس شدند.');
INSERT INTO "hussein_taji" VALUES(6,45,'6:45','پس ریشه قومی که ستم کرده بودند، قطع شد، و ستایش مخصوص الله است که پروردگار جهانیان است.');
INSERT INTO "hussein_taji" VALUES(6,46,'6:46','بگو: «به من خبر دهید، اگر الله، گوش شما و چشم‌هایتان را بگیرد، و بر دل‌های شما مهر بزند کدام معبود جز الله (می تواند) آن‌ها را به شما بازگرداند؟!» ببین چگونه آیات را (به شیوهای) گوناگون (بیان) می‌کنیم، سپس آن‌ها روی می‌گردانند!');
INSERT INTO "hussein_taji" VALUES(6,47,'6:47','بگو: «به من خبر دهید اگر عذاب الله ناگهان یا آشکارا به سراغ شما بیاید؛ آیا جز قوم ستمکاران نابود می‌شوند؟!».');
INSERT INTO "hussein_taji" VALUES(6,48,'6:48','و ما پیامبران را جز بشارت‌دهنده و بیم‌دهنده نمی‌فرستیم، پس هر کس که ایمان آورد و (خویشتن را) اصلاح کند نه ترسی بر آنان است و نه آنان اندوهگین می‌شوند.');
INSERT INTO "hussein_taji" VALUES(6,49,'6:49','و کسانی‌که آیات ما را تکذیب کردند؛ بخاطر (کفر و) نافرمانی شان عذاب به آنان می‌رسد.');
INSERT INTO "hussein_taji" VALUES(6,50,'6:50','بگو: «من نمی‌گویم خزاین الله نزد من است و غیب نمی‌دانم، و به شما نمی‌گویم من فرشته هستم، جز از آنچه به من وحی می‌شود؛ پیروی نمی‌کنم». بگو: «آیا نابینا و بینا یکسانند؟! چرا نمی‌اندیشید؟!»');
INSERT INTO "hussein_taji" VALUES(6,51,'6:51','و با آن (= قرآن) کسانی را که می‌ترسند به سوی پروردگارشان گرد آیند؛ بیم ده (زیرا که) برایشان (در آن روز) دوست و شفیعی جز او (= خدا) نیست؛ باشد که پرهیزگاری پیشه کنند.');
INSERT INTO "hussein_taji" VALUES(6,52,'6:52','و کسانی را که صبح و شام پروردگارشان را می‌خوانند (و) خشنودی او را می‌خواهند (از خود) دور مکن، نه چیزی از حساب آن‌ها بر توست و نه چیزی از حساب تو بر آن‌ها؛ پس اگر آن‌ها را طرد کنی از ستمکاران خواهی بود.');
INSERT INTO "hussein_taji" VALUES(6,53,'6:53','و این چنین بعضی از آن‌ها را با بعض دیگر آزمودیم؛ تا بگویند: «آیا این‌ها هستند که الله از میان ما بر آنان منت نهاده (و نعمت هدایت بخشیده)است؟!» آیا الله به سپاسگزاران داناتر نیست؟');
INSERT INTO "hussein_taji" VALUES(6,54,'6:54','و هرگاه کسانی‌که به آیات ما ایمان دارند نزد تو آیند (به آن‌ها) بگو: «سلام بر شما، پروردگارتان رحمت را بر خود فرض کرده که هر کس از شما از روی نادانی کار بدی مرتکب شود؛ سپس بعد از آن توبه و درستکاری کند، البته او آمرزندۀ مهربان است».');
INSERT INTO "hussein_taji" VALUES(6,55,'6:55','و این چنین آیات را به تفصیل بیان می‌کنیم و تا راه مجرمان (و گناهکاران) آشکار گردد.');
INSERT INTO "hussein_taji" VALUES(6,56,'6:56','بگو: «من از پرستش کسانی‌که غیر از الله می‌خوانید؛ نهی شده‌ام». بگو: «من از هوس‌های شما پیروی نمی‌کنم که اگر چنین کنم؛ قطعاً گمراه شده‌ام و از هدایت یافتگان نخواهم بود».');
INSERT INTO "hussein_taji" VALUES(6,57,'6:57','بگو: «من دلیل روشنی از پروردگارم دارم و شما آن را تکذیب کرده‌اید و آنچه شما بدان شتاب دارید (از نزول عذاب) نزد من نیست؛ حکم و فرمان تنها از آن الله است، حق را بیان می‌کند و او بهترین جدا کننده (ی حق از باطل) است».');
INSERT INTO "hussein_taji" VALUES(6,58,'6:58','بگو: «اگر آنچه که شما بدان شتاب دارید (از نزول عذاب) نزد من بود (بر شما نازل می‌کردم و) کار میان من و شما به پایان می‌رسید، و الله به ستمکاران داناتر است».');
INSERT INTO "hussein_taji" VALUES(6,59,'6:59','و کلیدهای غیب نزد اوست؛ و جز او کسی آن‌ها را نمی‌داند، آنچه را در خشکی و دریاست می‌داند، و هیچ برگی (از درختی) نمی‌افتد؛ مگر اینکه آن را می‌داند، و نه هیچ دانه‌ای در تاریکی‌های زمین و نه هیچ تر و خشکی (و جود دارد) مگر اینکه در کتابی آشکار (= لوح محفوظ ثبت) است.');
INSERT INTO "hussein_taji" VALUES(6,60,'6:60','و او کسی است که (روح) شما را در شب (هنگام خواب) می‌گیرد و آنچه در روز کرده‌اید می‌داند، سپس شما را در آن (روز از خواب) بر می‌انگیزد تا موعد مقرر فرا رسد، سپس بازگشت شما به سوی اوست، آنگاه شما را از آنچه انجام می‌دادید؛ آگاه می‌سازد.');
INSERT INTO "hussein_taji" VALUES(6,61,'6:61','و او بر بندگانش چیره است و نگهبانانی (از فرشتگان) بر شما می‌فرستد تا زمانی که یکی از شما را مرگ فرا رسد؛ فرستادگان ما (ملک الموت و دست‌یارانش) جانش را می‌گیرند، و آن‌ها (در انجام مأموریت خود) کوتاهی نمی‌کنند.');
INSERT INTO "hussein_taji" VALUES(6,62,'6:62','سپس به سوی الله که مولای حقیقی آن‌هاست باز می‌گردند، بدانید که حکم (و داوری) مخصوص اوست، و او زود شمار‌ترین حساب‌گران است.');
INSERT INTO "hussein_taji" VALUES(6,63,'6:63','بگو: «چه کسی شما را از تاریکی‌های خشکی و دریا رهایی می‌بخشد؟! او را با تضرع و زاری و (در) پنهانی می‌خوانید (و می‌گویید:) اگر ما را از این (خطرات و بلاها) رهایی بخشد؛ مسلماً از شکر گزاران خواهیم بود».');
INSERT INTO "hussein_taji" VALUES(6,64,'6:64','بگو: «الله شما را از این‌ها و از هر اندوهی می‌رهاند، باز هم شما شرک می‌ورزید».');
INSERT INTO "hussein_taji" VALUES(6,65,'6:65','بگو: «او قادر است بر آنکه عذابی از بالای (سر) تان یا از زیر پای‌تان؛ بر شما بفرستد، یا شما را بصورت گروه گروه (و پراکنده) با هم بیامیزد و (طعم) جنگ (و دو دستگی) را به هر یک از شما بوسیلۀ دیگری بچشاند». بنگر، چگونه آیات را گوناگون بیان می‌کنیم؛ تا آنان دریابند.');
INSERT INTO "hussein_taji" VALUES(6,66,'6:66','قوم تو آن (= قرآن) را تکذیب کردند در حالی‌که آن حق است. بگو: «من بر شما نگهبان نیستم».');
INSERT INTO "hussein_taji" VALUES(6,67,'6:67','هر خبری (سر انجام و) قرار گاهی دارد، و به زودی خواهید دانست.');
INSERT INTO "hussein_taji" VALUES(6,68,'6:68','و هر گاه کسانی را دیدی که در آیات ما (از روی عناد و مسخره) به بحث و گفتگو می‌پردازند، از آنان روی بگردان؛ تا به سخن دیگری بپردازند. و اگر شیطان تو را به فراموشی افکند، پس هرگز بعد از یاد آوردن با گروه ستمگران منشین.');
INSERT INTO "hussein_taji" VALUES(6,69,'6:69','و چیزی از حساب (و گناه) آن‌ها بر (عهده) پرهیزگاران نیست، ولی (همنشینی باید) برای پند دادن (و یادآوری) باشد، شاید که پرهیزگاری پیشه کنند.');
INSERT INTO "hussein_taji" VALUES(6,70,'6:70','و کسانی را که آیین خود را به بازیچه و سرگرمی گرفتند، و زندگی دنیا، آن‌ها را فریب داده است، رها کن، و با این (قرآن) اندرزشان بده، تا مبادا کسی به (کیفر) آنچه کرده است، (از کردار بد و گناه) گرفتار (و هلاک) شود، (و در قیامت) جز خدا؛ نه یاوری دارند، و نه شفاعت کننده‌ای، و (چنین افرادی) هر فدیه‌ای را به جای خود بدهند، از او پذیرفته نخواهد شد. آن‌ها کسانی هستند که به (کیفر) آنچه کرده‌اند؛ گرفتار شده‌اند، نوشابه‌ای از آب جوشیده برای آن‌هاست و عذاب دردناکی؛ بخاطر اینکه کفر می‌ورزیدند.');
INSERT INTO "hussein_taji" VALUES(6,71,'6:71','بگو: «آیا غیر از خدا، چیزی را بخوانیم که نه ما را سود می‌دهد و نه زیان می‌رساند؟! و به عقب برگردیم بعد از آنکه الله ما را هدایت کرده است؟! همچون کسی‌که شیطان او را (با وسوسه) در زمین گمراه کرده و سر گردان مانده است، در حالی‌که او یارانی دارد، او را به هدایت دعوت می‌کنند، (و می‌گویند:) به سوی ما بیا». بگو: «به راستی هدایت الله، هدایت (واقعی) است، و به ما امر شده که تسلیم پروردگار جهانیان باشیم.');
INSERT INTO "hussein_taji" VALUES(6,72,'6:72','و اینکه نماز را بر پا دارید، و از او بترسید، و تنها اوست که به سویش محشور می‌شوید».');
INSERT INTO "hussein_taji" VALUES(6,73,'6:73','و اوست (آن خالق یکتا) که آسمان‌ها و زمین را به حق آفرید، و روزی‌که می‌فرماید: «باش» پس موجود می‌شود، گفتار او حق است، و روزی‌که در صور (= شیپور) دمیده می‌شود؛ فرمانروایی از آن اوست، دانای پنهان و آشکار است، و او حکیم آگاه است.');
INSERT INTO "hussein_taji" VALUES(6,74,'6:74','و (بیاد آور) هنگامی را که ابراهیم به پدرش آزر گفت: «آیا بت‌ها را به پرستش می‌گیری؟! من تو و قوم تو را در گمراهی آشکار می‌بینم».');
INSERT INTO "hussein_taji" VALUES(6,75,'6:75','و این چنین، ملکوت آسمان‌ها و زمین را به ابراهیم نشان دادیم، تا از یقین کنندگان باشد.');
INSERT INTO "hussein_taji" VALUES(6,76,'6:76','پس هنگامی‌که (تاریکی) شب او را پوشانید، ستاره‌ای دید، گفت: «این پروردگار من است» و چون غروب کرد؛ گفت: «غروب کنندگان را دوست ندارم».');
INSERT INTO "hussein_taji" VALUES(6,77,'6:77','پس هنگامی‌که ماه را تابنده دید؛ گفت: «این پروردگار من است». و چون غروب کرد، گفت: «اگر پروردگارم مرا راهنمایی نکند، مسلماً از گروه گمراهان خواهم بود».');
INSERT INTO "hussein_taji" VALUES(6,78,'6:78','پس هنگامی‌که خورشید را درخشنده دید، گفت: «این پروردگار من است؛ این بزرگتر است». اما وقتی که غروب کرد، گفت: «ای قوم! من از آنچه (برای خدا) شریک قرار می‌دهید، بیزارم».');
INSERT INTO "hussein_taji" VALUES(6,79,'6:79','بتحقیق من روی خود را به سوی کسی آوردم که آسمان‌ها و زمین را پدپد آورد، حقگرایم و از مشرکان نیستم.');
INSERT INTO "hussein_taji" VALUES(6,80,'6:80','و قومش با وی به مجادله و ستیز پرداختند، گفت: «آیا دربارۀ الله با من مجادله و ستیز می‌کنید؛ در حالی‌که مرا هدایت کرده است؟! و من از آنچه با او شریک قرار می‌دهید، نمی‌ترسم، مگر پروردگارم چیزی را بخواهد، علم و دانش پروردگارم همه چیز را در برگرفته است، آیا پند نمی‌گیرید؟!');
INSERT INTO "hussein_taji" VALUES(6,81,'6:81','و چگونه از چیزی که شریک او قرار داده‌اید؛ بترسم؟! در حالی‌که شما نمی‌ترسید از آنکه چیزی را شریک الله قرار داده‌اید که هیچ گونه دلیلی دربارۀ آن بر شما نازل نکرده است، پس اگر می‌دانید کدام یک از این دو دسته به ایمنی شایسته‌تر است؟');
INSERT INTO "hussein_taji" VALUES(6,82,'6:82','آنان که ایمان آورده‌اند، و ایمان خود را به شرک نیالودند، ایمنی از آن آن‌هاست؛ و آن‌ها هدایت شدگانند».');
INSERT INTO "hussein_taji" VALUES(6,83,'6:83','و این‌ها حجت و دلایل ما بود که به ابراهیم در برابر قومش دادیم، درجات هر کس را بخواهیم بالا می‌بریم، همانا پروردگار تو، حکیم داناست.');
INSERT INTO "hussein_taji" VALUES(6,84,'6:84','و اسحاق و یعقوب را به او بخشیدیم، و هر یک را هدایت کردیم، و نوح را (نیز) پیش از آن هدایت نمودیم، و از فرزندان او داوود، و سلیمان، و ایوب و یوسف و موسی و هارون را (هدایت کردیم) و این چنین نیکوکاران را پاداش می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(6,85,'6:85','و (نیز) زکریا و یحیی و عیسی و الیاس را (هدایت نمودیم) همه از صالحان بودند.');
INSERT INTO "hussein_taji" VALUES(6,86,'6:86','و اسماعیل و الیسع، و یونس و لوط را (هدایت کردیم) و همه را بر جهانیان برتری داده‌ایم.');
INSERT INTO "hussein_taji" VALUES(6,87,'6:87','و (نیز) از پدران‌شان و فرزندان‌شان و برادران‌شان و (برخی را برتری دادیم) و برگزیدیم و به راه راست هدایت نمودیم.');
INSERT INTO "hussein_taji" VALUES(6,88,'6:88','این هدایت الله است، که هر کس از بندگانش را بخواهد با آن هدایت می‌کند. و اگر شرک می‌ورزیدند؛ هرآینه آنچه را که انجام داده بودند از (اعمال نیک) آن‌ها نابود می‌شد.');
INSERT INTO "hussein_taji" VALUES(6,89,'6:89','آن‌ها کسانی هستند که کتاب و حکمت و نبوت به آنان دادیم، پس اگر این (مشرکان) به آن (آیات قرآن) کفر ورزند، بتحقیق ما گروهی را بر آن می‌گماریم که نسبت به آن کافر نیستند.');
INSERT INTO "hussein_taji" VALUES(6,90,'6:90','آن‌ها کسانی هستند که الله هدایت‌شان کرده است؛ پس به روش آنان اقتدا کن، بگو: «من در برابر این (رسالت) پاداشی از شما نمی‌طلبم، این (رسالت و دعوت) چیزی جز (پند و) یاد آوری برای جهانیان نیست».');
INSERT INTO "hussein_taji" VALUES(6,91,'6:91','و آن‌ها (مقام و عظمت) الله را آن گونه که شایسته است؛ نشناخته‌اند، که گفتند: «الله بر هیچ بشری چیزی را نازل نکرده است» بگو: «کتابی را که موسی برای روشنایی و هدایت مردم آورد، چه کسی نازل کرده است؟! (شما) آن را به صورت نوشته‌های پراکنده قرار می‌دهید، قسمتی را آشکار و بسیاری را پنهان می‌کنید، و چیزهایی به شما آموزش داده شده که نه شما می‌دانستید و نه پدران‌تان، (از آن با خبر بودند)» بگو: «الله (آن را نازل کرده است)» سپس آن‌ها را با سخنان باطل شان، رها کن تا (سر گرم باشند) و بازی کنند.');
INSERT INTO "hussein_taji" VALUES(6,92,'6:92','و این (قرآن) کتابی است مبارک که نازل کردیم، که آنچه را پیش از آن (از کتاب‌های آسمانی) آمده تصدیق می‌کند، و تا اهالی مکه و کسانی را که گرد آن ساکنند؛ بیم دهی، و کسانی‌که به آخرت ایمان دارند به آن (نیز) ایمان می‌آورند، و آنان بر نمازهای خود محافظت می‌کنند.');
INSERT INTO "hussein_taji" VALUES(6,93,'6:93','و چه کسی ستمکار تر است از کسی‌که دروغی به الله ببندد، یا بگوید: «به من وحی شده است». در حالی‌که به او وحی نشده است، و کسی‌که بگوید: «من (نیز) همانند آنچه الله نازل کرده است، نازل می‌کنم!» و (شگفت‌زده می‌شوی) اگر ببینی هنگامی‌که (این) ستمکاران در سکرات مرگ فرو رفته‌اند، و فرشتگان (قبض روح) دست‌ها را گشوده (و به آنان می‌گویند:) «جان‌هایتان را بیرون آورید! امروز عذاب خفت‌باری در برابر آنچه ناحق بر الله می‌گفتید، و نسبت به آیات او تکبّر ورزیدید، خواهید دید».');
INSERT INTO "hussein_taji" VALUES(6,94,'6:94','و (در روز قیامت الله می‌فرماید:) «همانا شما یکه (و تنها) به سوی ما آمدید، همان گونه که روز اول شما را آفریدیم، و آنچه را به شما بخشیده بودیم، پشت سرتان رها کردید، و شفیعانی را که شریک در (امور) خود می‌پنداشتید با شما نمی‌بینیم، مسلماً (پیوند و روابط) میان شما بریده شده است، و آنچه را (خدایان و حامیان خود) می‌پنداشتید، از شما دور و گم شده‌اند.');
INSERT INTO "hussein_taji" VALUES(6,95,'6:95','همانا الله شکافندۀ دانه و هسته است، زنده را از مرده خارج می‌سازد، و مرده را از زنده بیرون می‌آورد، این است الله شما، پس چگونه (از حق) منحرف می‌شوید؟!');
INSERT INTO "hussein_taji" VALUES(6,96,'6:96','(او) شکافندۀ صبح است، و شب را (مایۀ) آرامش و خورشید و ماه را (وسیلۀ) حساب (زمان) قرار داده است، این اندازه‌گیری الله توانای داناست.');
INSERT INTO "hussein_taji" VALUES(6,97,'6:97','و او کسی است که ستارگان را برای شما قرار داد، تا در تاریکی‌های خشکی و دریا بوسیلۀ آن‌ها راه یابید. بتحقیق ما آیات (و نشانه‌های خود) را برای گروهی که می‌دانند (بتفصیل) بیان داشتیم.');
INSERT INTO "hussein_taji" VALUES(6,98,'6:98','و او کسی است شما را از یک تن آفرید، پس شما را قرار گاه و امانت گاهی است، ما آیات (خود) را برای گروهی که در می‌یابند (به تفصیل) بیان نمودیم.');
INSERT INTO "hussein_taji" VALUES(6,99,'6:99','و او کسی است که از آسمان آبی نازل کرد، پس بوسیلۀ آن گیاهان گوناگون (از زمین) بیرون آوردیم، سپس از آن سبزه‌ها پدید آوردیم، که از آن دانه‌های متراکم، و از شکوفۀ نخل خوشه‌هایی نزدیک به یکدیگر بیرون آوردیم. و باغ‌هایی از انگور، و زیتون و انار، (میوه‌هایی) که برخی شبیه هم و برخی بی‌شباهت به هم هستند (بیرون آوردیم). هنگامی‌که میوه داد (و به بار نشست) به میوۀ آن، و (به) رسیدنش بنگرید که همانا در آن نشانه‌هایی است برای گروهی که ایمان دارند.');
INSERT INTO "hussein_taji" VALUES(6,100,'6:100','و (مشرکان) برای الله شریک‌های ازجن قرار دادند، در حالی‌که (الله) آن‌ها را آفریده است، و از روی نادانی برای الله پسران و دخترانی ساختند، الله منزه و برتر است از آنچه توصیف می‌کنند.');
INSERT INTO "hussein_taji" VALUES(6,101,'6:101','(او) پدید آورندۀ آسمان‌ها و زمین است، چگونه ممکن است فرزندی داشته باشد؟! در حالی‌که همسری ندارد؟ و همه چیز را آفریده، و او به هر چیز داناست.');
INSERT INTO "hussein_taji" VALUES(6,102,'6:102','این است الله، پروردگار شما، هیچ معبودی (بحق) جز او نیست، آفرینندۀ همه چیز است، پس او را بپرستید، و او بر همه چیز (کار ساز و) نگهبان است.');
INSERT INTO "hussein_taji" VALUES(6,103,'6:103','چشم‌ها او را در نمی‌یابند، و او چشم‌ها را در می‌یابد، و او باریک بین آگاه است.');
INSERT INTO "hussein_taji" VALUES(6,104,'6:104','(ای پیامبر به مشرکین بگو:) (دلائل و) بینش‌هایی از (جانب) پروردگارتان برای شما آمد، پس کسی‌که ببیند، به (سود) خود اوست، و کسی‌که (از دیدن آن چشم بپوشد و) نابینا شود، پس به زیان خود اوست، و من (مراقب و) نگهبان شما نیستم.');
INSERT INTO "hussein_taji" VALUES(6,105,'6:105','و این چنین آیات را گوناگون بیان می‌کنیم، تا مبادا بگویند: «درس خوانده‌ای» و ما این آیات را برای گروهی که می‌دانند (و درک می‌کنند) بیان می‌کنیم.');
INSERT INTO "hussein_taji" VALUES(6,106,'6:106','از آنچه که از سوی پروردگارت بر تو وحی شده، پیروی کن، هیچ معبودی (بحق) جز او نیست، و از مشرکان روی بگردان.');
INSERT INTO "hussein_taji" VALUES(6,107,'6:107','و اگر الله می‌خواست شرک نمی‌ورزیدند، (و به اجبار ایمان می‌آوردند) و ما تو را بر آنان نگهبان قرار نداده‌ایم، و تو کار سازشان نیستی.');
INSERT INTO "hussein_taji" VALUES(6,108,'6:108','و کسانی را که (مشرکان) به جای الله می‌پرستند؛ دشنام ندهید، مبادا آن‌ها (نیز) از روی دشمنی و جهالت؛ الله را دشنام دهند، این چنین برای هر امتی عمل‌شان را آراستیم، آنگاه باز گشت آن‌ها به سوی پروردگارشان است، پس آن‌ها را از آنچه عمل می‌کردند، آگاه می‌سازد.');
INSERT INTO "hussein_taji" VALUES(6,109,'6:109','و آن‌ها به سخت‌ترین سوگندهایش به الله سوگند یاد کردند که اگر معجزه‌ای برای آنان بیاید، قطعاً به آن ایمان می‌آورند، بگو: «معجزات فقط نزد الله است (و در اختیار من نیست) و (شما ای مؤمنان) چه می‌دانید که هر گاه معجزه‌ای (هم) بیاید، ایمان نمی‌آورند؟!»');
INSERT INTO "hussein_taji" VALUES(6,110,'6:110','و ما دل‌های شان و دیدگان‌شان را دگرگون می‌کنیم همان‌گونه که از اول به آن ایمان نیاوردند، و آنان را در سر کشی شان سرگردان رها می‌سازیم.');
INSERT INTO "hussein_taji" VALUES(6,111,'6:111','و اگر فرشتگان را بر آن‌ها نازل می‌کردیم، و مردگان با آن‌ها سخن می‌گفتند، و همه چیز را در برابرشان گرد می‌آوردیم، هرگز ایمان نمی‌آورند، مگر آنکه الله بخواهد، ولی بیشترشان نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(6,112,'6:112','و این چنین برای هر پیامبری، دشمنی از شیاطین انس و جن قرار دادیم، که سخنان فریبنده و بی‌اساس (برای اغفال مردم) به یکدیگر القاء می‌کردند، و اگر پروردگارت می‌خواست، چنین نمی‌کردند، پس آن‌ها را با (تهمت و) دروغ‌هایشان وا‌‌‌ گذار.');
INSERT INTO "hussein_taji" VALUES(6,113,'6:113','و تا دل‌های کسانی‌که به آخرت ایمان ندارند به آن متمایل گردد، و به آن خوشنود شوند، و هر گناهی که بخواهند، انجام دهند.');
INSERT INTO "hussein_taji" VALUES(6,114,'6:114','(ای پیامبر بگو:) آیا غیر الله را به داوری طلب کنم؟! و حال آنکه او کسی است که (این) کتاب روشن (که همه چیز در آن بیان شده) به سوی شما نازل کرده است، و کسانی‌که به آن‌ها کتاب (آسمانی) داده‌ایم؛ می‌دانند که این (قرآن) به حق از سوی پروردگارت نازل شده است، بنابراین هرگز از تردید کنندگان مباش.');
INSERT INTO "hussein_taji" VALUES(6,115,'6:115','و کلام پروردگار تو با صدق و عدل به انجام رسید، هیچ کس نمی‌تواند کلمات او را دگرگون سازد، و او شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(6,116,'6:116','و اگر از بیشتر کسانی‌که در روی زمین هستند اطاعت کنی، تو را از راه الله گمراه می‌کنند، زیرا (آن‌ها) جز از گمان پیروی نمی‌کنند، و جز به دروغ سخن نمی‌گویند.');
INSERT INTO "hussein_taji" VALUES(6,117,'6:117','به راستی پروردگارت به کسانی‌که از راه او گمراه می‌شوند، آگاه‌تر است، و او به هدایت‌یافتگان (نیز) آگاه‌تر است.');
INSERT INTO "hussein_taji" VALUES(6,118,'6:118','پس اگر به آیات او ایمان دارید از (گوشت) آنچه نام الله (هنگام ذبح) بر آن برده شده، بخورید.');
INSERT INTO "hussein_taji" VALUES(6,119,'6:119','و چرا از چیزهایی که (هنگام ذبح) نام الله بر آن‌ها برده شده نمی‌خورید؟! حال آنکه (الله) آنچه را بر شما حرام کرده، به روشنی بیان نموده است! مگر اینکه ناچار باشید. و همانا بسیاری (از مردم) به خاطر هوی و هوس و بی دانشی (دیگران را) گمراه می‌کنند، بی‌گمان پروردگارت به (حال) تجاوزکاران داناتر است.');
INSERT INTO "hussein_taji" VALUES(6,120,'6:120','و گناه آشکار و پنهان را رها کنید، همانا آنانکه مرتکب گناه می‌شوند، بزودی در برابر آنچه مرتکب می‌شوند، مجازات خواهند شد.');
INSERT INTO "hussein_taji" VALUES(6,121,'6:121','و از آنچه (هنگام ذبح) نام الله بر آن برده نشده، نخورید و همانا این (عمل) نافرمانی (و گناه) است و به راستی شیاطین به دوستان خود (شبهه‌هایی) القا می‌کنند، تا با شما مجادله کنند، و اگر از آن‌ها اطاعت کنید بی‌گمان مشرک خواهید بود.');
INSERT INTO "hussein_taji" VALUES(6,122,'6:122','آیا کسی‌که (با کفر) مرده بود، پس (با هدایت) او را زنده کردیم، و نوری برایش قرار دادیم که با آن در (میان) مردم راه می‌رود، همانند کسی است که در تاریکی‌ها باشد، و از آن خارج نگردد؟! این چنین برای کافران آنچه که انجام می‌دادند آراسته شده است.');
INSERT INTO "hussein_taji" VALUES(6,123,'6:123','و این گونه در هر شهر و دیاری، بزرگان (و سران) گناهکاران‌شان را قرار دادیم، تا در آن نیرنگ کنند، ولی نمی‌دانند که جز با خودشان مکر نمی‌ورزند.');
INSERT INTO "hussein_taji" VALUES(6,124,'6:124','و چون نشانه‌ای برای آن‌ها بیاید، می‌گویند: «ما هرگز ایمان نمیآوریم، مگر اینکه همانند آنچه به پیامبران الله داده شده، (به ما هم) داده شود». الله آگاه‌تراست که رسالت خویش را کجا قرار دهد، بزودی به کسانی‌که مرتکب گناه شدند، به سزای مکری که می‌ورزیدند؛ از جانب الله خواری، و عذاب سختی خواهد رسید.');
INSERT INTO "hussein_taji" VALUES(6,125,'6:125','پس هرکس را که الله بخواهد هدایت کند، سینه‌اش را برای (قبول) اسلام می‌گشاید، و هرکس را که بخواهد گمراه کند، سینه‌اش را چنان تنگ می‌کند که گویا به آسمان بالا می‌رود، این چنین الله پلیدی را بر کسانی‌که ایمان نمی‌آورند؛ قرار می‌دهد.');
INSERT INTO "hussein_taji" VALUES(6,126,'6:126','و این راه مستقیم پروردگار توست، بتحقیق ما آیات (خود) را برای گروهی که پند می‌گیرند، بیان کردیم.');
INSERT INTO "hussein_taji" VALUES(6,127,'6:127','برای آن‌ها (در بهشت) سرای آرامش و سلامتی نزد پروردگارشان خواهد بود، و او ولی و دوستدار آن‌هاست بخاطر اعمال (پسندیده‌ای) که انجام می‌دادند.');
INSERT INTO "hussein_taji" VALUES(6,128,'6:128','و روزی‌که (الله) همۀ آنان را گرد آورد (و میفرماید:) «ای گروه جن، شما افراد زیادی از انسان‌ها را گمراه کردید». و دوستان آن‌ها از آدمیان می‌گویند: «پروردگارا! هر یک از ما دوگروه از یکدیگر بهرمند شدیم، و به آن مدتی که برای ما مقرر داشته بودی رسیدیم». (الله) می‌فرماید: «آتش جایگاه شماست، جاودانه در آن خواهید ماند، مگر آنچه الله بخواهد» بی‌گمان پروردگار تو حکیم داناست.');
INSERT INTO "hussein_taji" VALUES(6,129,'6:129','و این چنین بعضی از ستمگران را به کیفر آنچه انجام می‌دادند به بعض دیگر وا می‌گذاریم.');
INSERT INTO "hussein_taji" VALUES(6,130,'6:130','ای گروه جن و انس! آیا پیامبرانی از شما به سوی شما نیامدند که آیات مرا برای‌تان بخوانند، و شما را از ملاقات این روزتان هشدار دهند گویند: «بر (ضد) خودمان گواهی می‌دهیم». و زندگی دنیا آن‌ها را فریب داد، و بر (زیان) خودشان گواهی دهند که کافر بودند.');
INSERT INTO "hussein_taji" VALUES(6,131,'6:131','این (فرستادن پیامبران) بدان سبب است که پروردگارت هیچ گاه شهرها در حالی‌که مردمش غافل و بی‌خبر باشند، به ستم هلاک نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(6,132,'6:132','و برای هر یک در برابر آنچه انجام داده‌اند درجاتی است، و پروردگارت از آنچه انجام می‌دهند؛ غافل نیست.');
INSERT INTO "hussein_taji" VALUES(6,133,'6:133','و پروردگارت بی‌نیاز (و) صاحب رحمت (بی‌کران) است، اگر بخواهد شما را می‌برد، و پس از شما هرکس را بخواهد جانشین می‌کند، چنانکه شما را از نسل گروهی دیگر به وجود آورد.');
INSERT INTO "hussein_taji" VALUES(6,134,'6:134','مسلّماً آنچه به شما وعده داده می‌شود، خواهد آمد، و شما نمی‌توانید (الله را) ناتوان سازید (و از کیفر الهی فرار کنید).');
INSERT INTO "hussein_taji" VALUES(6,135,'6:135','بگو: «ای قوم من! به شیوۀ خود عمل کنید، همانا من (نیز به شیوۀ خود) عمل می‌کنم، اما بزودی خواهید دانست که چه کسی سرانجام نیک خواهد داشت، بتحقیق ستمکاران رستگار نخواهند شد».');
INSERT INTO "hussein_taji" VALUES(6,136,'6:136','و (مشرکان) از آنچه الله از زراعت و چهارپایان آفریده است، بهره‌ای برای او قرار دادند (و نیز بهره‌ای برای بت‌ها) و به گمان خود گفتند: «این برای الله است، و این (هم) برای شریکان ما (= بت‌ها) است [ در این آیه نمونه‌ای از عقیدۀ خود ساختۀ مشرکان بیان شده است، آن‌ها بهره‌ای از زراعت و چهارپایان را برای الله و بهره‌ای مخصوص به بت‌ها قرار می‌دادند، بهرۀ الله را در راه مهمان‌نوازی و فقرا و صله خویشاوندی صرف می‌کردند و بهره بت‌ها را در راه احتیاجات بت‌ها خرج می‌کردند. سپس اگر از بهرۀ تعیین‌شدۀ بت‌ها طبق توقع حاصل نمی‌شد از بهرۀ الله گرفته به او می‌افزودند، ولی برعکس از بهرۀ بت‌ها چیزی نمی‌گرفتند و می‌گفتند: الله متعال بی‌نیاز است.]، پس آنچه از آنِ شریکان‌شان بود به الله نمی‌رسید، و آنچه از آنِ الله بود، به شریکان‌شان می‌رسید، چه بد حکم می‌کنند.');
INSERT INTO "hussein_taji" VALUES(6,137,'6:137','و این چنین شریکان‌شان برای بسیاری از مشرکان؛ کشتن فرزندان‌شان را آراسته جلوه دادند. تا هلاک‌شان کنند، و آیین شان را برآنان مشتبه سازند، و اگر الله می‌خواست، چنین نمی‌کردند، پس آن‌ها را با تهمت‌هایشان و اگذار.');
INSERT INTO "hussein_taji" VALUES(6,138,'6:138','و گفتند: «این چهار پایان و کشتزار‌‌‌‌‌‌‌‌ها (برای همه) ممنوع است، مگر کسانی‌که ما بخواهیم ـ به گمان آنان ـ نباید (از) آن بخورد». و چهارپایانی دیگر است که (سوار شدن بر) پشت آن‌ها حرام است. و (نیز) چهار پایانی (دیگر) است که (هنگام ذبح) نام الله را بر آن نمی‌بردند، (و با این کارشان) به الله دروغ می‌بستند. و (الله) به زودی کیفر افتراهای‌شان را خواهد داد.');
INSERT INTO "hussein_taji" VALUES(6,139,'6:139','و گفتند: «آنچه در شکم این چهارپایان است مخصوص مردان ماست، و بر همسران‌مان حرام است، و اگر مرده باشد پس (همگی) در آن شریکند». بزودی (الله) کیفر این توصیف آن‌ها را می‌دهد، بی‌گمان او حکیم داناست.');
INSERT INTO "hussein_taji" VALUES(6,140,'6:140','مسلّماً زیان کردند آن‌ها که فرزندان خود را به نادانی کشتند؛ و از روی افتراء بر الله، آنچه را الله روزی‌شان نموده بود (بر خود) تحریم کردند، بتحقیق آن‌ها گمراه شدند و راه هدایت را نیافته بودند.');
INSERT INTO "hussein_taji" VALUES(6,141,'6:141','و اوست که باغ‌هایی دار بست و بی‌دار بست، و درخت خرما و کشتزار با میوه‌های گوناگون، و زیتون و انار را شبیه و بی شباهت پدید آورد. از میوۀ آن به هنگامی‌که رسید، بخورید، و حق (= زکات) آن را در روز دروَش بپردازید، و اسراف نکنید، بی‌گمان او اسراف کنندگان را دوست ندارد.');
INSERT INTO "hussein_taji" VALUES(6,142,'6:142','و (نیز) از چهار پایان، (حیوانات) باربر و (حیوانات) کوچک (که توان حمل بار ندارند آفرید). از آنچه الله به شما روزی داده است بخورید، و از گام‌های شیطان پیروی مکنید، بی‌گمان او دشمن آشکار شماست.');
INSERT INTO "hussein_taji" VALUES(6,143,'6:143','هشت جفت (از چهار پایان آفرید) از میش یک جفت (نر و ماده) و از بز یک جفت (نر و ماده)، بگو: «آیا (الله) دو نر را حرام کرده است، یا دو ماده را؟! یا آنچه که رحم‌های (این) دو ماده در بر دارد؟! اگر راست می‌گویید از روی علم به من خبر دهید».');
INSERT INTO "hussein_taji" VALUES(6,144,'6:144','و از شتر یک جفت (نر و ماده) و از گاو یک جفت (نر و ماده، آفرید). بگو: «آیا دو نر را حرام کرده است یا دو ماده را؟! یا آنچه که رحم‌های (این) دو ماده در بر دارد؟! آیا هنگامی‌که الله شما را به (تحریم) آن توصیه کرد، (حاضر و) گواه بودید؟! پس چه کسی ستمکار‌‌تراست از آن کس که بر الله دروغ بندد، تا مردم را از روی نادانی گمراه سازد؟! مسلّماً الله گروه ستمکاران را هدایت نمی‌کند».');
INSERT INTO "hussein_taji" VALUES(6,145,'6:145','بگو: «در آنچه بر من وحی شده است، (چیز) حرامی را که خوردن آن بر خورنده‌ای حرام باشد؛ نمی‌یابم، بجز اینکه مردار باشد یا خون ریخته شده یا گوشت خوک که پلید است، یا (حیوانی که در کشتنش) نافرمانی (خدا) شده، (هنگام ذبح) نام غیر الله بر آن برده شده باشد، اما کسی‌که (به خوردن آن‌ها) ناچار گردد، بی آنکه خواهان لذت آن باشد و یا زیاده روی و تجاوز کند (گناهی بر وی نیست) بی‌گمان پروردگارت آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(6,146,'6:146','و بر یهودیان، هر (حیوان) ناخن دار را حرام کردیم، و از گاو و گوسفند، پیه آن دو را بر آن‌ها حرام ساختیم، مگر آنچه (از پیه و چربی) که بر پشت آن‌ها قرار دارد، و یا در لا به لای روده‌ها یا آنچه به استخوان آمیخته است، این را به سبب سرکشی (و ستمی) که می‌کردند؛ به آن‌ها کیفر دادیم، و حقاً ما راستگوییم.');
INSERT INTO "hussein_taji" VALUES(6,147,'6:147','پس اگر تو را تکذیب کنند، بگو: «پروردگار شما دارای رحمتی گسترده است، و عذابش از گروه مجرم (و گناه‌کار) دفع نخواهد شد».');
INSERT INTO "hussein_taji" VALUES(6,148,'6:148','به زودی کسانی‌که شرک ورزیدند می‌گویند: «اگر الله می‌خواست، نه ما شرک می‌آوردیم و نه پدران ما، و نه چیزی را تحریم می‌کردیم» کسانی‌که پیش از آن‌ها بودند (نیز) این چنین تکذیب کردند، تا آنکه (طعم) عذاب ما را چشیدند. بگو: «آیا نزد شما (دلیل و) دانشی است که آن را برای ما آشکار سازید؟! شما فقط از پندارهای بی‌اساس پیروی می‌کنید، و شما جز گزافه و دروغ نمی‌گویید».');
INSERT INTO "hussein_taji" VALUES(6,149,'6:149','بگو: «دلیل رسا (و قاطع) از آنِ الله است، پس اگر می‌خواست همۀ شما را هدایت می‌کرد».');
INSERT INTO "hussein_taji" VALUES(6,150,'6:150','بگو: «گواهان خود را که گواهی می‌دهند که الله این را حرام کرده است، بیاورید!». پس اگر آن‌ها (به دروغ) گواهی دهند، تو با آنان گواهی نده، و از هوی و هوس کسانی‌که آیات ما را تکذیب کردند، و کسانی‌که به آخرت ایمان ندارند و (بت‌ها را) با الله برابر (و شریک) قرار می‌دهند، پیروی مکن.');
INSERT INTO "hussein_taji" VALUES(6,151,'6:151','بگو: «بیایید آنچه را پروردگارتان بر شما حرام کرده است، برایتان بخوانم: آنکه چیزی را شریک او قرار ندهید، و به پدر و مادر نیکی کنید، و فرزندان‌تان را از (بیم) فقر نکشید، ما شما و آن‌ها را روزی می‌دهیم، و به (کارهای) زشت و ناشایست آنچه آشکار باشد یا پنهان نزدیک نشوید، و کسی را که الله (کشتنش) را حرام کرده است، جز به حق؛ نکشید، این چیزی است که الله شما را به آن سفارش نموده است، باشد که خِرد ورزید.');
INSERT INTO "hussein_taji" VALUES(6,152,'6:152','و به مال یتیم نزدیک نشوید، مگر به نیکو‌ترین وجهی (که برای اصلاح باشد)، تا به سن رشد خود (و بلوغ) برسد، و (حق) پیمانه و وزن را به عدالت ادا کنید، ما هیچ کس را جز به‌اندازۀ توانش، تکلیف نمی‌کنیم. و هرگاه سخن گویید (یا داوری کنید و یا شهادت می‌دهید)، پس عدالت را رعایت کنید، حتی اگر در مورد نزدیکان (شما) بوده باشد. و به عهد و پیمان الله وفا کنید، این چیزی است که الله شما را به آن سفارش نموده است، تا متذکر شوید.');
INSERT INTO "hussein_taji" VALUES(6,153,'6:153','و این راه مستقیم من است، پس از آن پیروی کنید و از راه‌های (پراکنده) پیروی نکنید، که شما را از راه الله دور می‌کند، این چیزی است که الله شما را به آن سفارش نموده است، شاید پرهیزگار شوید».');
INSERT INTO "hussein_taji" VALUES(6,154,'6:154','سپس به موسی کتاب (آسمانی) دادیم تا (نعمت خود را) بر کسی‌که نیکو‌کار بوده تمام (و کامل) کنیم. و همه چیز را به تفصیل بیان کردیم، و (آن را) مایۀ هدایت و رحمت (قرار دادیم) شاید به لقای پروردگارشان ایمان بیاورند.');
INSERT INTO "hussein_taji" VALUES(6,155,'6:155','و این (قرآن) کتابی است پُر برکت، که ما نازل کردیم، از آن پیروی کنید، و پرهیزگار باشید، تا مورد رحمت قرار گیرید.');
INSERT INTO "hussein_taji" VALUES(6,156,'6:156','تا نگویید: «کتاب (آسمانی) تنها بر دو طایفه پیش از ما (یهود و نصاری) نازل شده بود، و ما از خواندن و آموختن آن‌ها بی‌خبر بودیم».');
INSERT INTO "hussein_taji" VALUES(6,157,'6:157','یا بگویید: «اگر کتاب (آسمانی) بر ما نازل می‌شد، از آن‌ها راه یافته‌تر بودیم». مسلماً از جانب پروردگار‌‌تان دلیل روشن، و هدایت و رحمت برای شما آمده است. پس چه کسی ستمکار‌‌‌تر است از کسی‌که آیات الله را تکذیب کند، و از آن روی گرداند، به زودی کسانی را که از آیات ما روی می‌گردانند، به خاطر همین روی گردانی به عذابی سخت مجازات خواهیم داد.');
INSERT INTO "hussein_taji" VALUES(6,158,'6:158','آیا انتظاری جز آن دارند که فرشتگان (مرگ) نزد‌شان بیایند؟ یا پروردگارت (خودش به سوی آن‌ها) بیاید؟ یا بعضی از آیات پروردگارت بیاید؟! روزی‌که بعضی از آیات پروردگارت بیاید (و بر آن‌ها ظاهر شود)، ایمان آوردن افرادی که قبلاً ایمان نیاورده‌اند، یا در ایمان‌شان (عمل) نیکی انجام نداده‌اند، سودی به حال آن‌ها نخواهد داشت. بگو: «منتظر باشید، بی‌گمان ما (نیز) منتظر خواهیم بود».');
INSERT INTO "hussein_taji" VALUES(6,159,'6:159','به راستی کسانی‌که آیین خود را پراکنده ساختند و دسته دسته شدند، تو را با آنان هیچ گونه کاری نیست، کار آن‌ها تنها با الله است، سپس آن‌ها را از آنچه انجام می‌دادند، آگاه می‌سازد.');
INSERT INTO "hussein_taji" VALUES(6,160,'6:160','(روز قیامت) هر کس (کار) نیکی آورد، پس ده برابر آن پاداش دارد، و هرکس (کار) بدی آورد، جز به مانند آن کیفر نخواهد دید، و بر آن‌ها ستمی نخواهد شد.');
INSERT INTO "hussein_taji" VALUES(6,161,'6:161','بگو: «بتحقیق پروردگارم مرا به راه راست هدایت کرده است، دین درست (و پا بر‌ جا)، دین (حنیف) ابراهیم حق پرست، که از مشرکان نبود».');
INSERT INTO "hussein_taji" VALUES(6,162,'6:162','بگو: «بی‌گمان نمازم، و عبادت (و قربانی) من، و زندگی و مرگ من، همه برای الله پروردگار جهانیان است.');
INSERT INTO "hussein_taji" VALUES(6,163,'6:163','شریکی برای او نیست، و به این مأمور شده‌ام، و من نخستین مسلمانم».');
INSERT INTO "hussein_taji" VALUES(6,164,'6:164','بگو: «آیا غیر الله، پروردگاری را بجویم در حالی‌که او پروردگار همه چیز است؟! و هیچ کس گناهی مرتکب نمی‌شود؛ مگر آنکه به (زیان) خود اوست. و هیچ گناه‌کاری بارگناه دیگری را به دوش نمی‌گیرد، سپس بازگشت همۀ شما به سوی پروردگار‌‌تان است، پس شما را از آنچه در آن اختلاف داشتید، آگاه می‌کند.');
INSERT INTO "hussein_taji" VALUES(6,165,'6:165','و او کسی است که شما را جانشینان در زمین قرار داد، و درجات بعضی از شما را نسبت به بعضی دیگر برتری داد، تا شما را (بوسیلۀ) آنچه که به شما داده است بیازماید. مسلّماً پروردگار تو زودکیفر است و همانا او آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(7,1,'7:1','المص (الف. لام. میم. صاد).');
INSERT INTO "hussein_taji" VALUES(7,2,'7:2','این کتابی است که بر تو نازل شده است، پس نباید در سینه‌ات از آن تنگی باشد، تا به آن (مردم را) بیم دهی، و برای مؤمنان پند (و تذکری) باشد.');
INSERT INTO "hussein_taji" VALUES(7,3,'7:3','آنچه از جانب پروردگارتان بر شما نازل شده، پیروی کنید، و از اولیاء (و معبود‌های) دیگر جز او، پیروی نکنید، چه اندک پند می‌‌پذیرید.');
INSERT INTO "hussein_taji" VALUES(7,4,'7:4','چه بسیار شهر‌ها (و آبادی‌ها) که آن‌ها را نابود کردیم، و عذاب ما به هنگام شب، یا در هنگام خواب نیم‌روز، به سراغ‌شان آمد.');
INSERT INTO "hussein_taji" VALUES(7,5,'7:5','و چون عذاب ما به سراغ آن‌ها آمد، سخنی نداشتند جز اینکه گفتند: «بی‌گمان ما ستمکار بودیم».');
INSERT INTO "hussein_taji" VALUES(7,6,'7:6','مسلّماً ما از کسانی‌که (پیامبران) به سوی آن‌ها فرستاده شدند، سؤال خواهیم کرد، و البته از پیامبران (نیز) سؤال می‌کنیم.');
INSERT INTO "hussein_taji" VALUES(7,7,'7:7','یقیناً (کردارشان را) با علم (خود) برایشان بیان می‌کنیم، و ما هرگز غایب نبوده‌‌ایم.');
INSERT INTO "hussein_taji" VALUES(7,8,'7:8','وزن (و سنجش اعمال) در آن روز حق است، پس کسانی‌که کفۀ ترازوی‌شان سنگین است، آنانند که رستگارند.');
INSERT INTO "hussein_taji" VALUES(7,9,'7:9','و کسانی‌که کفۀ ترازوی شان سبک است، آنان کسانی هستند که به (خاطر انکار و) ستمی که نسبت به آیات ما می‌کردند به خویش زیان رساندند (و سرمایۀ وجود خود را از دست داده‌اند).');
INSERT INTO "hussein_taji" VALUES(7,10,'7:10','و به راستی ما شما را در زمین اقتدار (و استقرار) دادیم، و در آن وسایل معیشت را برای شما قرار دادیم. چه اندک شکر گزاری می‌کنید.');
INSERT INTO "hussein_taji" VALUES(7,11,'7:11','و بتحقیق ما شما را آفریدیم، آنگاه شما را شکل و صورت دادیم. سپس به فرشتگان گفتیم: «برای آدم سجده کنید» پس (همه) سجده کردند؛ جز ابلیس که از سجده‌کنندگان نبود.');
INSERT INTO "hussein_taji" VALUES(7,12,'7:12','(الله) فرمود: «وقتی که تو را فرمان دادم، چه چیز مانع سجده کردنت شد؟!» (ابلیس) گفت: «من از او بهترم، مرا از آتش آفریده‌ای و او را از گِل (آفریده‌ای)».');
INSERT INTO "hussein_taji" VALUES(7,13,'7:13','فرمود: «از آن (بهشت) فرود آی، که حق تو نیست که در آن تکبر ورزی، پس بیرون رو، بی‌شک تو از خوارشدگانی».');
INSERT INTO "hussein_taji" VALUES(7,14,'7:14','(ابلیس) گفت: «تا روزی‌که (مردم) بر انگیخه می‌شوند، مرا مهلت ده».');
INSERT INTO "hussein_taji" VALUES(7,15,'7:15','(الله) فرمود: «مسلّماً تواز مهلت یافتگانی».');
INSERT INTO "hussein_taji" VALUES(7,16,'7:16','(ابلیس) گفت: «پس به‌سبب آنکه مرا گمراه کردی، من (نیز) بر سر راه مستقیم تو برای (گمراه ساختن) آن‌ها در کمین می‌نشینم،');
INSERT INTO "hussein_taji" VALUES(7,17,'7:17','سپس از پیش روی شان و از پشت سرشان و از (سمت) راست شان و از (سمت) چپ شان بر آن‌ها می‌تازم، و بیشترشان را شکر گزار نخواهی یافت».');
INSERT INTO "hussein_taji" VALUES(7,18,'7:18','(الله) فرمود: «از آن نکوهیده و خوار بیرون رو، قطعاً هرکس از آن‌ها از تو پیروی کند، جهنم را از شما همگی پر خواهم کرد.');
INSERT INTO "hussein_taji" VALUES(7,19,'7:19','و ای آدم! تو و همسرت در بهشت ساکن (شوید)، پس از هر جا که خواستید، بخورید، و به این درخت نزدیک نشوید، که از ستمکاران خواهید بود».');
INSERT INTO "hussein_taji" VALUES(7,20,'7:20','پس شیطان آن دو را وسوسه کرد، تا آنچه را از شرمگاه‌شان که از آن‌ها پوشیده بود؛ در نظرشان آشکار سازد، و گفت: «پروردگارتان شما را از این درخت نهی نکرده است؛ مگر بخاطر اینکه مبادا (با خوردن آن) فرشته شوید، یا از جاودانان گردید».');
INSERT INTO "hussein_taji" VALUES(7,21,'7:21','و برای آن‌ دو سوگند یاد کرد که من برای شما از خیر خواهانم.');
INSERT INTO "hussein_taji" VALUES(7,22,'7:22','پس (به این ترتیب) آن‌ها را با فریب (از مقام و منزل‌شان) فرود آورد، و چون از آن درخت چشیدند، شرمگاه‌شان بر آن‌ها آشکار شد، و شروع کردند به قرار دادن برگ (درختان) بهشت بر خود (تا آن را بپوشانند) و پروردگارشان آن‌ها را نِدا داد: «آیا شما را از آن درخت منع نکردم، و به شما نگفته بودم که شیطان دشمن آشکار شماست؟!».');
INSERT INTO "hussein_taji" VALUES(7,23,'7:23','(آن دو) گفتند: «پروردگارا! ما به خود ستم کردیم، و اگر ما را نیامرزی و بر ما رحم نکنی، مسلّماً از زیان‌کاران خواهیم بود».');
INSERT INTO "hussein_taji" VALUES(7,24,'7:24','(الله) فرمود: «فرود آیید، در حالی‌که بعضی از شما نسبت به بعض دیگر دشمن خواهید بود، و برای شما در زمین تا مدت معینی قرارگاه و بهرمندی خواهد بود».');
INSERT INTO "hussein_taji" VALUES(7,25,'7:25','فرمود: «در آن زندگی می‌کنید و در آن می‌میرید، و (در رستاخیز) از آن بیرون آورده خواهید شد».');
INSERT INTO "hussein_taji" VALUES(7,26,'7:26','ای فرزندان آدم! به راستی برای شما لباسی که شرمگاه‌تان را می‌پوشاند و (مایۀ) زینت شماست، نازل کردیم، و لباس تقوا بهتر است، این از آیات (و نشانه‌های) الله است تا (انسان‌ها) متذکر شوند.');
INSERT INTO "hussein_taji" VALUES(7,27,'7:27','ای فرزندان آدم! شیطان شما را نفریبد، چنانکه پدر و مادر شما را از بهشت بیرون کرد، و لباس‌شان را از تن‌شان کند (کشید)؛ تا شرمگاه‌شان را به آن‌ها نشان دهد. بی‌گمان او و همکارانش شما را می‌بینند از جایی که شما آن‌ها را نمی‌بینید، براستی ما شیطان‌ها را دوستان کسانی قرار دادیم که ایمان نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(7,28,'7:28','و چون کار‌‌ زشتی انجام دهند، گویند: «نیاکان خود را بر این (کار) یافته‌ایم، و الله ما را بدان فرمان داده است». بگو: «الله (هرگز) به کار زشت فرمان نمی‌دهد، آیا چیزی را که نمی‌دانید به الله نسبت می‌دهید؟!».');
INSERT INTO "hussein_taji" VALUES(7,29,'7:29','بگو: «پروردگارم به عدالت فرمان داده است، و (نیز) در هر مسجدی (و به هنگام هر نمازی) روی‌هایتان را به سوی او کنید و او را (به دعا و نیایش) بخوانید؛ در حالی‌که دین (خود) را برای او خالص گردانید. همانطور که در آغاز شما را آفرید (بعد از مرگ، بار دیگر بسویش) باز می‌گردید.');
INSERT INTO "hussein_taji" VALUES(7,30,'7:30','گروهی را هدایت نمود و گروهی (دیگر) گمراهی بر آن‌ها محقق (و ثابت) گشت، زیرا آن‌ها شیطان‌ها را به جای (الله)، سرپرست (و دوستان خویش) برگزیدند، و گمان می‌کنند که آنان هدایت یافته‌اند».');
INSERT INTO "hussein_taji" VALUES(7,31,'7:31','ای فرزندان آدم! به هنگام رفتن به مسجد (و در هر نمازی) زینت خود را بر گیرید (و لباس‌های شایسته بپوشید) و (نیز) بخورید و بیاشامید ولی اسراف نکنید، بی‌گمان الله اسراف‌کاران را دوست نمی‌دارد.');
INSERT INTO "hussein_taji" VALUES(7,32,'7:32','بگو: «چه کسی لباس (و زینت‌های) را که الله برای بندگانش پدید آورده و روزی‌های پاکیزه را حرام کرده است؟!». بگو: «این (چیزها) در زندگی دنیا، برای کسانی است که ایمان آورده‌اند، (اگر چه کافران نیز از آن‌ها بهره‌مند می‌شوند اما) روز قیامت خاص (برای مومنان) خواهد بود. این چنین آیات (خود) را برای گروهی که می‌دانند بیان می‌کنیم.');
INSERT INTO "hussein_taji" VALUES(7,33,'7:33','بگو: (الله) فقط کارهای زشت را، چه آشکارا باشد و چه پنهان، و (نیز) گناه و سرکشی بنا حق را؛ حرام کرده است، و این که چیزی را شریک الله قرار دهید که الله دلیلی بر (حقانیت) آن نازل نکرده است، و چیزی را که نمی‌دانید به الله نسبت دهید (حرام نموده است).');
INSERT INTO "hussein_taji" VALUES(7,34,'7:34','و برای هر امتی زمان (و مدت معینی) است، پس هنگامی‌که اجل‌شان فرا رسد، نه لحظه‌ای (از آن) تأخیر کنند و نه (بر آن) پیشی گیرند.');
INSERT INTO "hussein_taji" VALUES(7,35,'7:35','ای فرزندان آدم! هرگاه پیامبرانی از خود شما (به نزد شما) بیایند که آیات مرا برای شما بخوانند (از آن‌ها پیروی کنید) پس هرکس که پرهیزگاری کند و (اعمال خود را) اصلاح کند، نه ترسی بر آن‌هاست، و نه آنان اندوهگین شوند.');
INSERT INTO "hussein_taji" VALUES(7,36,'7:36','و کسانی‌که آیات ما را تکذیب کنند، و در برابر آن (گردن‌کشی و) تکبر ورزند، آنان اهل دوزخند، و جاودانه در آن خواهند ماند.');
INSERT INTO "hussein_taji" VALUES(7,37,'7:37','پس چه کسی ستمکار‌‌‌‌تراست از کسی‌که بر الله دروغ می‌بندد، یا آیات او را تکذیب می‌کند؟! آن‌ها نصیب (و بهرۀ) شان از کتاب (لوح محفوظ، آنچه مقرر شده) به آنان می‌رسد، تا زمانی که فرستادگان ما به سراغ‌شان روند که جان‌شان را بگیرند، گویند: «کجایند آن چیز‌هایی که به جای الله می‌پرستیدید؟!» (آن‌ها) گویند: «آن‌ها (نابود و) گم شدند» و بر (زیان) خود گواهی دهند که کافر بوده‌اند.');
INSERT INTO "hussein_taji" VALUES(7,38,'7:38','(الله) به آنان) می‌فرماید: «در بین گروه‌های از جن و انس که پیش از شما گذشته‌اند، در آتش (دوزخ) وارد شوید!». هرگاه که گروهی وارد می‌شوند، گروه دیگر را لعنت می‌کنند، تا هنگامی‌که همگی در آنجا به هم رسند، گروه آخر (و پیروان) دربارۀ گروه اول (و پیشوایان خود) گویند: «پروردگارا! اینها بودند که ما را گمراه کردند، پس عذابی دو چندان از آتش به آنان بده» (الله) می‌فرماید: «برای هر کدام (از شما عذاب) دو چندان است، ولی شما نمی‌دانید».');
INSERT INTO "hussein_taji" VALUES(7,39,'7:39','و گروه اول (و پیشوایان‌ شان) به گروه آخر (و پیروان‌شان) گویند: «شما را بر ما هیچ برتری نیست، پس به (کیفر) آنچه انجام می‌دادید؛ عذاب را بچشید».');
INSERT INTO "hussein_taji" VALUES(7,40,'7:40','بی‌گمان کسانی‌که آیات ما را تکذیب کردند، و در برابر آن (گردن‌کَشی و) تکبر ورزیدند، درهای آسمان (هرگز) برایشان گشوده نمی‌شود، و به بهشت داخل نخواهند شد، مگر این که شتر از سوراخ سوزن درآید، و این چنین، گناه‌کاران را کیفر می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(7,41,'7:41','برای آن‌ها بستری از (آتش) جهنم و (نیز) روی آن‌ها پوشش‌های (از آتش جهنم) است. و این چنین ستمکاران را کیفر می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(7,42,'7:42','و کسانی‌که ایمان آوردند و کارهای شایسته انجام دادند – هیچ کس را جز به‌اندازۀ توانش تکلیف نمی‌کنیم – آن‌ها اهل بهشت هستند، و جاودانه در آن خواهد ماند.');
INSERT INTO "hussein_taji" VALUES(7,43,'7:43','و آنچه از کینه (و نفرت) که در سینه‌هایشان است بر می‌کنیم. و از زیر (درختان و مسکن‌های) آن‌ها نهر‌ها جاری است. و گویند: «ستایش مخصوص الله است که ما را به این (بهشت) هدایت نمود، و اگر الله ما را هدایت نمی‌کرد؛ ما هرگز راه نمی‌یافتیم. مسلّماً فرستاد‌گان پروردگار ما به حق آمدند». و (آنگاه) به آنان نِدا داده می‌شود که: «این بهشت را به پاداش آنچه که انجام می‌دادید، به (شما داده‌اند و) ارث بردید».');
INSERT INTO "hussein_taji" VALUES(7,44,'7:44','و اهل بهشت، اهل دوزخ را صدا می‌زنند که: «آنچه را که پروردگار‌مان به ما وعده داده بود، (همه را) حق یافتیم، پس آیا شما (نیز) آنچه را پروردگار‌تان به شما وعده داده بود؛ حق یافتید؟!». گویند: «آری». پس ندا دهنده‌ای در میان آن‌ها ندا دهد که: «لعنت الله بر ستمکاران باد،');
INSERT INTO "hussein_taji" VALUES(7,45,'7:45','(همان) کسانی‌که (مردم را) از راه الله باز میداشتند، و آن را کج می‌طلبیدند، و (نیز) آن‌ها به آخرت کافر بودند».');
INSERT INTO "hussein_taji" VALUES(7,46,'7:46','و در میان آن دو (گروه بهشتی و دوزخی) حجابی است، و بر اعراف (= بلندی‌های آن) مردانی هستند که هریک (از آن دو گروه) را از سیما (و چهره) شان می‌شناسند، و اهل بهشت را صدا می‌زنند که: «سلام بر شما باد» اینان طمع (و امید) بهشت را دارند، و هنوز بدان در نیامده‌اند.');
INSERT INTO "hussein_taji" VALUES(7,47,'7:47','و چون چشم‌شان به سوی اهل دوزخ گردانده شود، گویند: «پروردگارا! ما را با گروه ستمکاران قرار مده».');
INSERT INTO "hussein_taji" VALUES(7,48,'7:48','و اهل اعراف، مردانی (از اهل دوزخ را) که از سیما (و چهره) شان آن‌ها را می‌شناسند، صدا می‌زنند، گویند: «جمعیت (و مال اندوزی) شما، و آن سرکشی (و تکبر) های شما، به حال‌تان فایده‌ای نبخشید!».');
INSERT INTO "hussein_taji" VALUES(7,49,'7:49','آیا اینها (همان) کسانی هستند که سوگند یاد میکردید که (هرگز) الله رحمتی به آنان نمیرساند؟ (به آن‌ها گفته می‌شود): «به بهشت داخل شوید، نه ترسی بر شماست، و نه شما اندوهگین میشوید».');
INSERT INTO "hussein_taji" VALUES(7,50,'7:50','و اهل دوزخ، اهل بهشت را صدا می‌زنند که: «اندکی از آب، یا از آنچه الله به شما روزی داده، بر ما بریزید» (آن‌ها) گویند: «بی‌گمان الله هر دو را بر کافران حرام کرده است».');
INSERT INTO "hussein_taji" VALUES(7,51,'7:51','آنان که دین خود را سرگرمی و بازیچه گرفتند، و زندگی دنیا آن‌ها را فریفت، پس امروز ما آن‌ها را فراموش می‌کنیم، همانطور که آن‌ها دیدار چنین روزی را فراموش کردند، و آنکه آیات ما را انکار می‌کردند.');
INSERT INTO "hussein_taji" VALUES(7,52,'7:52','و به تحقیق ما کتابی برای آن‌ها آوردیم که آن را از روی دانش به تفصیل بیان کردیم، که مایۀ هدایت و رحمت برای قومی است که ایمان می‌آورند.');
INSERT INTO "hussein_taji" VALUES(7,53,'7:53','آیا آن‌ها جز انتظار (سرانجام و) تأویلش را دارند؟! روزی‌که (نتیجه و) تأویلش فرا رسد، کسانی‌که آن را پیش از این فراموش کرده بودند می‌گویند: «مسلّماً فرستادگان پروردگارمان به حق آمده بودند، پس آیا (امروز) برای ما شفیعانی هستند که برای ما شفاعت کنند، یا باز گردیم آنگاه اعمالی غیر از آنچه میکردیم، انجام دهیم؟!». بتحقیق آن‌ها به خود زیان رسانیدند، و (معبود‌هایی ساختگی و) آنچه دروغ میبافتند (همگی) از (نظر) آنان ناپدید (و گم) شد.');
INSERT INTO "hussein_taji" VALUES(7,54,'7:54','(ای مردم) بی‌گمان پروردگار شما الله است که آسمان‌ها و زمین را در شش روز آفرید، سپس بر عرش مستقر شد، شب را به روز می‌پوشاند که شتابان در پی آن می‌رود، و خورشید و ماه و ستاره‌گان (را آفرید) که مسخر فرمان او هستند، آگاه باشید که آفرینش و فرمانروایی از آنِ او است، پر برکت (و به غایت بزرگ) است الله که پروردگار جهانیان است.');
INSERT INTO "hussein_taji" VALUES(7,55,'7:55','پروردگارتان را با تضرع (و زاری) و در پنهانی، بخوانید، همانا او از حد درگذرندگان را دوست نمیدارد.');
INSERT INTO "hussein_taji" VALUES(7,56,'7:56','و در زمین، پس از اصلاح آن فساد نکنید، و او را با بیم و امید بخوانید، همانا رحمت الله به نیکوکاران نزدیک است.');
INSERT INTO "hussein_taji" VALUES(7,57,'7:57','و او کسی است که باد‌‌‌ها را بشارت‌دهنده در پیشاپیش (باران) رحمتش می‌فرستد، تا هنگامی‌که ابرهای سنگین بار را بر دارند، ما آن را به سوی (شهر و) سرزمینی مرده می‌فرستیم، سپس از آن؛ آب را نازل می‌کنیم، آنگاه از آن (آب) هر گونه میوهای (از دل خاک) بیرون می‌آوریم، این گونه مردگان را (زنده می‌کنیم و از قبرها) بیرون می‌آوریم، شاید پند گیرید.');
INSERT INTO "hussein_taji" VALUES(7,58,'7:58','سرزمین پاکیزه (و خوب) گیاهش به فرمان پروردگارش می‌روید، و سرزمین ناپاک (و بد) جز اندک و بی‌ارزش، از آن نمی‌روید، اینگونه آیات (خود) را برای گروهی که سپاس می‌گزارند، گوناگون بیان می‌کنیم.');
INSERT INTO "hussein_taji" VALUES(7,59,'7:59','همانا ما نوح را به سوی قومش فردستادیم، پس گفت: «ای قوم من! الله را بپرستید، که جز او معبودی (راستین) برای شما نیست، (و اگر او را عبادت نکردید) بی‌گمان من بر شما از عذاب روزی بزرگ می‌ترسم».');
INSERT INTO "hussein_taji" VALUES(7,60,'7:60','اشراف (و بزرگان) قومش گفتند: «به راستی ما تو را در گمراهی آشکار می‌بینیم».');
INSERT INTO "hussein_taji" VALUES(7,61,'7:61','گفت: «ای قوم من! هیچ گونه گمراهی در من نیست، بلکه من فرستاده‌ای از (جانب) پروردگار جهانیان هستم.');
INSERT INTO "hussein_taji" VALUES(7,62,'7:62','پیام‌های پروردگارم را به شما می‌رسانم و شما را اندرز می‌دهم، و از (شریعت) الله چیزهایی می‌دانم که شما نمی‌دانید.');
INSERT INTO "hussein_taji" VALUES(7,63,'7:63','آیا تعجب کرده‌اید که پندی از سوی پروردگارتان بر (زبان) مردی از خود شما، برای شما برسد تا شما را بیم دهد و تا پرهیزگاری پیشه کنید و باشد که مشمول رحمت گردید؟!».');
INSERT INTO "hussein_taji" VALUES(7,64,'7:64','پس او را تکذیب کردند، آنگاه ما او و کسانی را که با وی در کشتی بودند، نجات دادیم، و کسانی‌که آیات ما را تکذیب کردند، غرق کردیم، براستی آن‌ها گروهی نابینا (و کوردل) بودند.');
INSERT INTO "hussein_taji" VALUES(7,65,'7:65','و به (سوی قوم) عاد، برادرشان هود را (فرستادیم) گفت: «ای قوم من! الله را پرستش کنید که جز او معبودی (راستین) برای شما نیست، آیا پرهیزگاری پیشه نمی‌کنید؟!».');
INSERT INTO "hussein_taji" VALUES(7,66,'7:66','اشراف (و بزرگان) قوم او که کافر بودند گفتند: «بی‌گمان ما تو را در سفاهت (و نادانی) می‌بینیم، و ما مسلماً تو را از دروغ‌گویان می‌پنداریم».');
INSERT INTO "hussein_taji" VALUES(7,67,'7:67','گفت: «ای قوم من! هیچ‌گونه سفاهت (و نادانی) در من نیست، بلکه من فرستاده‌ای از (جانب) پروردگار جهانیان هستم،');
INSERT INTO "hussein_taji" VALUES(7,68,'7:68','پیام‌های پروردگارم را به شما می‌رسانم. و من خیرخواه امینی برای شما هستم.');
INSERT INTO "hussein_taji" VALUES(7,69,'7:69','آیا تعجب کرده‌اید که پندی از سوی پروردگارتان بر (زبان) مردی از خود شما، برای شما برسد تا شما را بیم دهد؟! و به یاد آورید هنگامی‌که پس از قوم نوح شما را جانشینان (آنان) قرار داد، و شما را در آفرینش فزونی (و قدرت) داد. پس نعمت‌های الله را به یاد آورید، شاید رستگار شوید».');
INSERT INTO "hussein_taji" VALUES(7,70,'7:70','گفتند: «آیا به (نزد) ما آمده‌ای که تنها الله را عبادت کنیم، و آنچه را نیاکان‌مان می‌پرستیدند، رها کنیم؟! پس اگر از راستگویان هستی، آنچه را (از عذاب الهی) به ما وعده می‌دهی، بیاور».');
INSERT INTO "hussein_taji" VALUES(7,71,'7:71','(هود) گفت: «به تحقیق عذاب و غضب از (جانب) پروردگارتان بر شما واقع شده (و شما را فراگرفته) است، آیا با من در(بارۀ) نام‌هایی مجادله می‌کنید که شما و نیاکان‌تان (بر بت‌ها) نهاده‌اید، در حالی‌که الله هیچ دلیل (و برهانی) دربارۀ آن نازل نکرده است، پس شما منتظر باشید، بی‌گمان من (نیز) با شما از منتظرانم».');
INSERT INTO "hussein_taji" VALUES(7,72,'7:72','پس او، و کسانی را که با او بودند؛ به رحمت (و لطفی) از (سوی) خویش نجات دادیم، و ریشه کسانی‌که آیات ما را تکذیب کردند و از مؤمنان نبودند، قطع کردیم.');
INSERT INTO "hussein_taji" VALUES(7,73,'7:73','و به (سوی قوم) ثمود، برادرشان صالح را (فرستادیم) گفت: «ای قوم من! الله را بپرستید، که جز او معبودی (راستین) برای شما نیست. همانا دلیل روشنی از (سوی) پروردگارتان برای شما آمده است، این ماده شتر الله، برای شما معجزه‌ای است، پس او را (به حال خود) وا گذارید که در زمین الله (بچرد و علف) بخورد، و آن را آزار (و آسیبی) نرسانید، که عذاب درد‌‌ناکی شما را خواهد گرفت.');
INSERT INTO "hussein_taji" VALUES(7,74,'7:74','و به یاد آورید هنگامی‌که شما را پس از (قوم) عاد جانشینان (آنان) قرار داد، و در زمین مستقر ساخت، که در دشت‌های آن قصرهایی بنا می‌کنید، و در کوه‌ها خانه‌هایی می‌تراشید. پس نعمت‌های الله را بیاد آورید، و در زمین به فساد (و تباهی) نکوشید».');
INSERT INTO "hussein_taji" VALUES(7,75,'7:75','اشراف (و بزرگان) از قوم او که گردن‌کشی میکردند به مستضعفان، به (همان) کسانی از آنان که ایمان آورده بودند گفتند: «آیا شما براستی یقین دارید که صالح از (طرف) پروردگارش فرستاده شده است؟!» (آن‌ها) گفتند: «ما به آنچه او بدان فرستاده شده، ایمان داریم».');
INSERT INTO "hussein_taji" VALUES(7,76,'7:76','آنان که گردن‌کشی می‌کردند گفتند: «ما به آنچه شما به آن ایمان آورده‌اید، (منکر و) کافریم».');
INSERT INTO "hussein_taji" VALUES(7,77,'7:77','پس ماده شتر را پی کردند، و از فرمان پروردگارشان سر باز زدند، و گفتند: «ای صالح! اگر تو از پیامبران (الله) هستی، آنچه را به ما وعده می‌دهی بیاور!».');
INSERT INTO "hussein_taji" VALUES(7,78,'7:78','آنگاه زلزله‌ای آن‌ها را فرا گرفت، در نتیجه در خانههای خود از پای در آمدند (و مردند).');
INSERT INTO "hussein_taji" VALUES(7,79,'7:79','پس (صالح) از آن‌ها روی گردانید و گفت: «ای قوم! من پیام پروردگارم را به شما رساندم، و برای شما خیر خواهی کردم، ولی شما خیر خواهان را دوست نمی‌دارید».');
INSERT INTO "hussein_taji" VALUES(7,80,'7:80','و (به یاد آورید) لوط را، هنگامی‌که به قوم خود گفت: «آیا کار بسیار زشتی را انجام می‌دهید که پیش از شما هیچ یک از جهانیان انجام نداده است؟!');
INSERT INTO "hussein_taji" VALUES(7,81,'7:81','همانا شما به جای زنان با مردان شهوت می‌رانید، راستی که شما قومی تجاوز کار هستید».');
INSERT INTO "hussein_taji" VALUES(7,82,'7:82','و پاسخ قومش جز این نبود که گفتند: «اینها را از شهر (و دیار) تان بیرون کنید، که اینها مردمی هستند که پاکیزگی می‌طلبند (و از کار ما بیزارند)».');
INSERT INTO "hussein_taji" VALUES(7,83,'7:83','پس ما او و خاندانش را نجات دادیم، جز همسرش که از بازماندگان بود.');
INSERT INTO "hussein_taji" VALUES(7,84,'7:84','و بارانی (از سنگ) بر آن‌ها بارانیدیم (که آن‌ها را نابود ساخت) پس بنگر که سر‌‌‌‌انجام گناه‌کاران چگونه بود.');
INSERT INTO "hussein_taji" VALUES(7,85,'7:85','و به سوی (مردم) مدین برادرشان شعیب را (فرستادیم) گفت: «ای قوم من! الله را بپرستید، که جز او معبودی (راستین) برای شما نیست، مسلّماً دلیل روشنی از (طرف) پروردگارتان برای شما آمده است، پس (حق) پیمانه و ترازو را کامل (و درست) ادا کنید، و از (اموال و) چیزهای مردم را (چیزی) نکاهید، و در زمین، پس از اصلاحش، فساد نکنید، اگر ایمان دارید، این برای شما بهتر است.');
INSERT INTO "hussein_taji" VALUES(7,86,'7:86','و بر سر هر راهی ننشینید که (مردم را) تهدید کنید، و از راه الله کسی را که به او ایمان آورده است، باز دارید، و آن را کج می‌خواهید، و به یاد آورید زمانی را که اندک بودید، و او شما را بسیار گرداند، و بنگرید سرانجام مفسدان چگونه بود.');
INSERT INTO "hussein_taji" VALUES(7,87,'7:87','و اگر گروهی از شما به آنچه من به آن فرستاده شده‌ام، ایمان آورده‌اند، و گروهی (دیگر) ایمان نیاورده‌اند، پس صبر کنید (و منتظر بمانید) تا الله میان ما داوری کند، که او بهترین داوران است».');
INSERT INTO "hussein_taji" VALUES(7,88,'7:88','اشراف (و بزرگان) از قوم او که سرکشی می‌کردند، گفتند: «ای شعیب! قطعاً تو و کسانی را که با تو ایمان آورده‌اند، از شهر (و دیار) خویش بیرون خواهیم کرد، یا اینکه به آیین ما باز گردید». (شعیب) گفت: «آیا (باز گردیم) اگر چه از آن کراهت داشته (و بیزار) باشیم؟!');
INSERT INTO "hussein_taji" VALUES(7,89,'7:89','اگر ما به آیین شما باز گردیم، بعد از آنکه الله ما را از آن نجات داد، بتحقیق بر الله دروغ بسته‌ایم، و شایستۀ ما نیست که به آن باز گردیم، مگر آنکه الله، پروردگار ما بخواهد، (زیرا) علم (و دانش) پروردگار ما، بر همه چیز احاطه دارد، ما بر الله توکل کرده‌ایم، پروردگارا! میان ما و قوم مان بحق (راهی بگشا و) داوری کن، که تو بهترین (راه‌گشایان و) داورانى».');
INSERT INTO "hussein_taji" VALUES(7,90,'7:90','و اشراف (و بزرگان) از قوم اوکه کافر بودند، گفتند: «اگر از شعیب پیروی کنید، قطعاً شما زیان‌کار خواهید بود».');
INSERT INTO "hussein_taji" VALUES(7,91,'7:91','آنگاه زلزلۀ (شدیدی) آن‌ها را فرا گرفت، پس در خانه‌های خود از پا در آمدند (و مردند).');
INSERT INTO "hussein_taji" VALUES(7,92,'7:92','آنان که شعیب را تکذیب کردند (چنان نابود شدند) که گویا هرگز در آن (دیار و خانه‌ها) نبوده‌اند، و آنان که شعیب را تکذیب کردند؛ خود زیان‌کار بودند.');
INSERT INTO "hussein_taji" VALUES(7,93,'7:93','آنگاه (شعیب) از آنان روی گرداند، و گفت: «ای قوم من! به راستی من پیام‌های پروردگارم را به شما رساندم و برای شما خیر خواهی (و نصیحت) نمودم، پس چگونه بر گروه کافر (و بی ایمان) افسوس بخورم؟!».');
INSERT INTO "hussein_taji" VALUES(7,94,'7:94','و ما در هیچ شهر (و دیاری) پیامبری نفرستادیم؛ مگر اینکه اهلش را به رنج‌ها و سختی‌ها گرفتار ساختیم، شاید تضرع و زاری کنند (و به سوی الله بر گردند).');
INSERT INTO "hussein_taji" VALUES(7,95,'7:95','سپس به جای ناخوشی (و بدی)، خوشی (و نیکی) آوردیم، تا اینکه افزون شدند، و گفتند: «بی‌گمان به نیاکان ما (نیز) رنج و راحتی رسیده بود» پس ناگهان آن‌ها را در حالی‌که بی‌خبر (و غافل) بودند گرفتیم.');
INSERT INTO "hussein_taji" VALUES(7,96,'7:96','و اگر اهل شهرها (و آبادی‌ها) ایمان می‌آوردند و تقوا پیشه می‌کردند، قطعاً (در‌‌های) برکات از آسمان و زمین را بر آن‌ها می‌گشودیم، و لکن (آن‌ها آیات و پیامبران ما را) تکذیب کردند، پس ما (نیز) آن‌ها را به کیفر آنچه انجام می‌دادند، فرو گرفتیم.');
INSERT INTO "hussein_taji" VALUES(7,97,'7:97','آیا اهل (این) شهرها (و آبادی‌ها) ایمن هستند از اینکه عذاب ما؛ در شب، هنگامی‌که خفته باشند سراغ‌شان بیاید؟!');
INSERT INTO "hussein_taji" VALUES(7,98,'7:98','آیا اهل (این) شهرها (و آبادی‌ها) ایمن هستند از اینکه عذاب ما؛ در روز، هنگامی‌که سرگرم بازی (و امور بیهوده) هستند، به سراغ‌شان بیاید؟!');
INSERT INTO "hussein_taji" VALUES(7,99,'7:99','آیا آن‌ها از مکر (و گرفت) الله در امانند؟! در حالی‌که جز زیان‌کاران (کسی) از مکر (و گرفت) الله ایمن نمی‌شود.');
INSERT INTO "hussein_taji" VALUES(7,100,'7:100','آیا برای آنان که زمین را پس از (نابود کردن) اهلش به ارث می‌برند، روشن (و واضح) نشده است که اگر بخواهیم آن‌ها را (نیز) به (کیفر) گناهان‌شان هلاک می‌کنیم، و بر دل‌های شان مهر می‌نهیم، آنگاه آنان (صدای حق و پندی را) نمی‌شنوند.');
INSERT INTO "hussein_taji" VALUES(7,101,'7:101','این‌ها، شهرها (و آبادی‌هایی) است که برخی از اخبار آن را بر تو حکایت می‌کنیم، و محققاً پیامبران‌شان دلایل (و معجزات) روشن برای آنان آورند، پس هرگز به آنچه از پیش تکذیب کرده بودند، ایمان نیاوردند، اینگونه الله بر دل‌های کافران مهر می‌نهد.');
INSERT INTO "hussein_taji" VALUES(7,102,'7:102','و در بیشترشان (وفای به) عهد نیافتیم، و به راستی بیشتر آن‌ها را نافرمان (و فاسق) یافتیم.');
INSERT INTO "hussein_taji" VALUES(7,103,'7:103','سپس بعد آن‌ها موسی را با آیات (و نشانه‌های) خویش به سوی فرعون و اطرافیان او فرستادیم، آنگاه آن‌ها به آن (آیات) ستم کردند (و کافر شدند) پس بنگر عاقبت مفسدان چگونه بود!');
INSERT INTO "hussein_taji" VALUES(7,104,'7:104','و موسی گفت: «ای فرعون! من فرستاده‌ای از (سوی) پروردگار جهانیان هستم.');
INSERT INTO "hussein_taji" VALUES(7,105,'7:105','سزاوار است که جز (سخن) حق بر الله نگویم، بتحقیق من دلیل (و معجزه‌ای روشن) از پروردگار تان برای شما آورده ام، پس بنی اسرائیل را با من بفرست».');
INSERT INTO "hussein_taji" VALUES(7,106,'7:106','(فرعون) گفت: «اگر معجزه‌ای آورده‌ای، آن را بیاور اگر از راستگویانى».');
INSERT INTO "hussein_taji" VALUES(7,107,'7:107','پس (موسی) عصای خود را انداخت، ناگهان آن اژدهای آشکار شد.');
INSERT INTO "hussein_taji" VALUES(7,108,'7:108','و دست خود را (از گریبان) بیرون آورد، پس ناگهان آن سفید (و درخشان) برای بینندگان بود.');
INSERT INTO "hussein_taji" VALUES(7,109,'7:109','اشراف (و بزرگان) از قوم فرعون گفتند: «به راستی این (شخص) جادوگری (ماهرو) داناست.');
INSERT INTO "hussein_taji" VALUES(7,110,'7:110','می‌خواهد شما را از سرزمین تان بیرون کند، پس چه فرمان می‌دهید؟».');
INSERT INTO "hussein_taji" VALUES(7,111,'7:111','(آنگاه به فرعون) گفتند: «او و برادرش را بازداشت کن، (و مهلت دهید) و جمع‌آوری کنندگان را به (همۀ) شهرها بفرست.');
INSERT INTO "hussein_taji" VALUES(7,112,'7:112','تا همۀ ساحران دانا (و ماهر) را به نزد تو بیاورند.');
INSERT INTO "hussein_taji" VALUES(7,113,'7:113','و ساحران نزد فرعون آمدند، گفتند: «آیا اگر ما پیروز شدیم، پاداشی برای ما خواهد بود؟».');
INSERT INTO "hussein_taji" VALUES(7,114,'7:114','(فرعون) گفت: «آری! بتحقیق شما از مقربان خواهید بود».');
INSERT INTO "hussein_taji" VALUES(7,115,'7:115','(روز موعد فرا رسید، ساحران) گفتند: «ای موسی! یا نخست تو (عصای خود را) بیفکن، یا ما (ابزار خود را) می‌افکنیم».');
INSERT INTO "hussein_taji" VALUES(7,116,'7:116','(موسی) گفت: «شما بیفکنید» چون (ابزار خود را) افکندند، چشم‌های مردم را افسون کردند و آن‌ها را ترساندند، و سحر عظیمی (پدید) آوردند.');
INSERT INTO "hussein_taji" VALUES(7,117,'7:117','و (ما) به موسی وحی کردیم که: «عصای خود را بیفکن» پس ناگهان آن (عصا، به صورت اژدهای درآمد) آنچه را که به دروغ بر ساخته بودند، می‌بلعید.');
INSERT INTO "hussein_taji" VALUES(7,118,'7:118','آنگاه حق آشکار (و پیروز) شد و آنچه آن‌ها (ساخته بودند و) انجام می‌دادند باطل گشت.');
INSERT INTO "hussein_taji" VALUES(7,119,'7:119','پس در آنجا (فرعون و فرعونیان همگی) مغلوب شدند و خوار و زبون بازگشتند.');
INSERT INTO "hussein_taji" VALUES(7,120,'7:120','و ساحران به سجده افتادند.');
INSERT INTO "hussein_taji" VALUES(7,121,'7:121','گفتند: «به پروردگار جهانیان ایمان آوردیم،');
INSERT INTO "hussein_taji" VALUES(7,122,'7:122','پروردگار موسی و هارون».');
INSERT INTO "hussein_taji" VALUES(7,123,'7:123','فرعون گفت: «آیا پیش از آنکه به شما اجازه دهم، به او ایمان آوردید؟! قطعاً این دسیسه و نیرنگی است که در شهر اندیشیده‌اید، تا اهلش را از آن بیرون کنید، پس بزودی خواهید دانست.');
INSERT INTO "hussein_taji" VALUES(7,124,'7:124','البته دست‌هایتان و پاهای‌تان را به خلاف یکدیگر خواهم برید، سپس همگی شما را به دار خواهم زد».');
INSERT INTO "hussein_taji" VALUES(7,125,'7:125','گفتند: «(پس از شهادت) ما به سوی پروردگارمان باز می‌گردیم،');
INSERT INTO "hussein_taji" VALUES(7,126,'7:126','و تنها انتقام تو از ما بخاطر این است که ما به آیات پروردگارمان هنگامی‌که به سوی ما آمد، ایمان آوردیم، پروردگارا! بر ما صبر (و استقامت) فرو ریز، و ما را مسلمان بمیران».');
INSERT INTO "hussein_taji" VALUES(7,127,'7:127','و اشراف (و بزرگان) از قوم فرعون (به او) گفتند: «آیا موسی و قومش را رها می‌کنی تا در زمین فساد کنند، و تو و معبودانت را وا گذارند؟». (فرعون) گفت: «بزودی پسران‌شان را می‌کشیم، و زنان‌شان را (برای خدمت‌کاری) زنده نگه می‌داریم، و بی‌گمان ما برآن‌ها چیره و مسلط هستیم».');
INSERT INTO "hussein_taji" VALUES(7,128,'7:128','موسی به قومش گفت: «از الله یاری بجویید، و صبر (و استقامت) پیشه کنید، همانا زمین از آنِ الله است، به هرکس از بندگانش که بخواهد آن را به میراث می‌دهد (و می بخشد) و عاقبت (نیک) از آنِ پرهیزگاران است».');
INSERT INTO "hussein_taji" VALUES(7,129,'7:129','(قوم موسی) گفتند: «پیش از آنکه به سوی ما بیایی آزار دیده‌ایم، و بعد از آنکه نزد ما آمدی (نیز آزار می‌بینیم!)». (موسی) گفت: «امید است پروردگارتان دشمن شما را هلاک کند، و شما را در زمین جانشین سازد، پس بنگرد چگونه عمل می‌کنید».');
INSERT INTO "hussein_taji" VALUES(7,130,'7:130','و به راستی ما قوم (و نزدیکان) فرعون را به قحط سالی و کمبود میوه‌ها گرفتار کردیم، شاید که آنان پندگیرند.');
INSERT INTO "hussein_taji" VALUES(7,131,'7:131','پس هنگامی‌که نیکی (و نعمت) به آن‌ها روی آورد، گفتند: «این حق ماست» و اگر بدی (و رنجی) به آن‌ها می‌رسید، به موسی و کسانی‌که با او بودند فال بد می‌زدند. آگاه باشید، همانا فال بدشان فقط به نزد الله است، (و به کیفر گناهان‌شان می‌باشد) و لیکن بیشتر آن‌ها نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(7,132,'7:132','و گفتند: «هرچه را که از نشانه (و معجزه) برای ما بیاوری، تا ما را بدان جادو کنی، ما به تو ایمان نمی‌آوریم».');
INSERT INTO "hussein_taji" VALUES(7,133,'7:133','پس (عذاب و بلاهای آشکار از قبیل:) طوفان، و ملخ، و شپش، و قورباغه‌ها، و خون را (که هر کدام معجزه و) نشانه‌هایی روشن و جدا جدا بود، بر آن‌ها فرستادیم، باز سرکشی کردند، و گروهی گناهکار (و مجرم) بودند.');
INSERT INTO "hussein_taji" VALUES(7,134,'7:134','و هنگامی‌که عذاب بر آن‌ها واقع می‌شد، می‌گفتند: «ای موسی! پروردگارت را برای ما بخوان، به آنچه به تو وحی (و عهد) کرده است، اگر این عذاب را از ما بر طرف کنی، قطعاً به تو ایمان می‌آوریم، و بنی اسرائیل را با تو خواهیم فرستاد».');
INSERT INTO "hussein_taji" VALUES(7,135,'7:135','پس هنگامی‌که عذاب را، تا مدت معینی که آنان به آن می‌رسیدند، از آن‌ها بر طرف کردیم، ناگهان پیمان‌شکنی کردند.');
INSERT INTO "hussein_taji" VALUES(7,136,'7:136','پس از آن‌ها انتقام گرفتیم، و آن‌ها را در دریا غرق کردیم، زیرا که آیات ما را تکذیب کردند، و از آن غافل بودند.');
INSERT INTO "hussein_taji" VALUES(7,137,'7:137','و شرق و غرب زمین را که در آن برکت داده بودیم؛ به گروهی که ناتوان (و مستضعف) شمرده می‌شدند، به میراث دادیم، و وعدۀ نیک پروردگارت بر بنی اسرائیل، بخاطر صبر (و استقامتی) که کردند، تحقق یافت، و آنچه فرعون و قوم او می‌ساختند، و آنچه را که بر می‌افراشتند در هم کوبیدیم (و ویران کردیم).');
INSERT INTO "hussein_taji" VALUES(7,138,'7:138','و ما بنی اسرائیل را از دریا گذراندیم، آنگاه بر گروهی گذشتند که اطراف بت‌های خود با تواضع و فروتنی گرد آمده بودند. گفتند: «ای موسی! برای ما (نیز) معبودی قرار ده، همان‌گونه که آن‌ها معبودهای دارند». (موسی) گفت: «حقا، شما گروهی نادان و جاهل هستید (و عظمت الله را نمی‌دانید).');
INSERT INTO "hussein_taji" VALUES(7,139,'7:139','بی‌گمان این‌ها عاقبت کارشان نابودی است و آنچه انجام می‌دادند (نیز) باطل است».');
INSERT INTO "hussein_taji" VALUES(7,140,'7:140','(سپس) گفت: «آیا غیر از الله، معبودی برای شما بجویم؟! در حالی‌که او شما را بر جهانیان (= مردم عصرتان) برتری داد».');
INSERT INTO "hussein_taji" VALUES(7,141,'7:141','و (به یاد آورید) زمانی را که شما را از (فرعون و) فرعونیان نجات دادیم، آن‌ها پیوسته شما را به بدترین صورت شکنجه می‌دادند، پسران‌تان را می‌کشتند و زنان‌تان را زنده نگه می‌داشتند، و در این، (برای) شما آزمایش بزرگی از جانب پروردگارتان بود.');
INSERT INTO "hussein_taji" VALUES(7,142,'7:142','و با موسی، سی شب وعده نهادیم، و آن را با ده شب (دیگر) کامل کردیم، پس میعاد پروردگارش (با او) چهل شب تمام شد. و موسی به برادرش هارون گفت: «جانشین من در میان قوم من باش، و (آن‌ها را) اصلاح کن، و از راه مفسدان پیروی نکن».');
INSERT INTO "hussein_taji" VALUES(7,143,'7:143','و هنگامی‌که موسی به میعاد ما آمد، و پروردگارش با او سخن گفت، عرض کرد: «پروردگارا! خودت را به من نشان بده، تا به تو بنگرم» فرمود: «(در دنیا) هرگز مرا نخواهی دید، و لیکن به کوه بنگر، پس اگر در جایش ثابت ماند، تو (نیز) مرا خواهی دید». پس چون پروردگارش بر (آن) کوه تجلی کرد، آن را متلاشی (و هموار) ساخت، و موسی بیهوش افتاد، پس هنگامی‌که به هوش آمد، عرض کرد: «تو پاک و منزهی، من به سوی تو بازگشتم (و توبه کردم) و من نخستین مؤمنانم».');
INSERT INTO "hussein_taji" VALUES(7,144,'7:144','(الله) فرمود: «ای موسی! من تو را با پیام‌هایم و با سخن خود، بر مردم (برتری دادم و) برگزیدم، پس آنچه را به تو داده ام، بگیر، و از سپاس‌گزاران باش».');
INSERT INTO "hussein_taji" VALUES(7,145,'7:145','و برای او در لوح‌ها هرگونه پند، و تفصیل و بیان هر چیز را نوشتیم، (و به موسی فرمودیم:) «پس آن را با نیرومندی (و جدیت) بگیر، و به قومت فرمان ده تا به نیکوترین آن، عمل کنند، و به زودی (جایگاه و) سرای نافرمانان را به شما نشان خواهم داد».');
INSERT INTO "hussein_taji" VALUES(7,146,'7:146','بزودی کسانی را که به نا حق در روی زمین تکبر می‌ورزند، از آیات خود بازمی‌دارم، و اگر هر (معجزه و) نشانه‌ای را ببینند، به آن ایمان نمی‌آورند، و اگر راه هدایت را ببینند، آن را در پیش نمی‌گیرند، و اگر راه گمراهی را ببینند، آن را بر می‌گزینند، این بدان (خاطر) است که آیات ما را تکذیب کردند و از آن غافل بودند.');
INSERT INTO "hussein_taji" VALUES(7,147,'7:147','و کسانی‌که آیات ما، و دیدار آخرت را تکذیب کردند، اعمال‌شان تباه (و نابود) شد، آیا جز آنچه را عمل می‌کردند، کیفر داده می‌شوند؟!');
INSERT INTO "hussein_taji" VALUES(7,148,'7:148','و قوم موسی بعد از (رفتن) او (به میعادگاه) از زیورهای‌شان گوساله‌ای ساختند، جسد بی جانی که صدای گوساله داشت، آیا نمی‌دیدند که با آن‌ها سخن نمی‌گوید، و به راهی هدایت‌شان نمی‌کند؟! آن را (به پرستش) گرفتند و ستمکار بودند.');
INSERT INTO "hussein_taji" VALUES(7,149,'7:149','و چون پشیمان شدند، و دیدند که به راستی گمراه شده‌اند، گفتند: «اگر پروردگار مان به ما رحم نکند و ما را نیامرزد، مسلّماً از زیان‌کاران خواهیم بود».');
INSERT INTO "hussein_taji" VALUES(7,150,'7:150','و چون موسی خشمگین و اندوهناک به سوی قومش بازگشت، گفت: «پس از من، چه بد جانشینانی برایم بودید، آیا از حکم پروردگارتان پیشی گرفتید؟!». و لوح‌ها را انداخت، و سر برادر خود را گرفت و به سوی خود کشید، (هارون) گفت: «ای پسر مادرم! به راستی این قوم مرا (در تنگا قرار دادند و) ناتوان کردند، و نزدیک بود مرا بکشند، پس با (اهانت) من (کاری نکن که) دشمنان مرا شماتت کنند، و مرا با گروه ستمکاران قرار مده».');
INSERT INTO "hussein_taji" VALUES(7,151,'7:151','(موسی) گفت: «پروردگارا! من و برادرم را بیامرز، و ما را در رحمت خویش داخل کن، و تو مهربان‌ترین مهربانان هستی».');
INSERT INTO "hussein_taji" VALUES(7,152,'7:152','به راستی کسانی‌که گوساله را (به معبودیت) گرفتند، بزودی خشمی از پروردگارشان، و ذلت (و خواری) در زندگی دنیا به آن‌ها می‌رسد، و این چنین دروغ‌پردازان را کیفر می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(7,153,'7:153','و کسانی که مرتکب کارهای بد شدند، سپس بعد از آن توبه نمودند، و ایمان آوردند، همانا پروردگار تو پس از آن، مسلّماً آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(7,154,'7:154','و چون خشم از (خاطر) موسی فرو نشست، الواح (تورات) را بر گرفت و در نوشته‌های آن، هدایت و رحمت برای کسانی بود که از پروردگارشان می‌ترسند.');
INSERT INTO "hussein_taji" VALUES(7,155,'7:155','و موسی از قومش، هفتاد مرد را برای میعاد ما برگزید، پس هنگامی‌که زلزله آن‌ها را فرا گرفت، (و همه مردند، موسی) گفت: «پروردگارا، اگر می‌خواستی پیش از این آن‌ها و مرا هلاک می‌کردی، آیا ما را به آنچه نادانان انجام داده‌اند، هلاک می‌کنی؟! این جز آزمایش تو چیزی نیست، هر که را بخواهی به (وسیلۀ) آن گمراه می‌سازی، و هر که را بخواهی، هدایت می‌کنی، تو یاور (و کارساز) مایی، پس ما را بیامرز، و بر ما رحم کن، و تو بهترین آمرزندگانی.');
INSERT INTO "hussein_taji" VALUES(7,156,'7:156','و برای ما در این دنیا، و در سرای آخرت، نیکی مقرر فرما، ما به سوی تو باز گشته‌ایم». (الله) فرمود: «عذابم را به هرکس که بخواهم می‌رسانم، و رحمتم همه چیز را فرا گرفته است، پس آن را برای کسانی‌که تقوا پیشه می‌کنند، و زکات می‌دهند، و کسانی‌که به آیات ما ایمان می‌آورند، مقرر خواهم داشت.');
INSERT INTO "hussein_taji" VALUES(7,157,'7:157','آنان که از (این) رسول (الله)، پیامبر «أمی» (= درس ناخوانده) پیروی می‌کنند، که صفاتش را در تورات و انجیلی که نزدشان است، نوشته می‌یابند. آن‌ها را به (کارهای) خوب و پسندیده فرمان می‌دهد و از زشتی (و کارهای نا پسند) بازشان می‌دارد. و پاکیزه‌ها را برایشان حلال می‌گرداند، و پلیدی‌ها را بر آنان حرام می‌کند، و بارهای سنگین، و قید (و زنجیرهایی) را که بر آن‌ها بود، از (دوش) آن‌ها بر می‌دارد، پس کسانی‌که به او ایمان آوردند، و او را گرامی داشتند، و او را یاری دادند، و از نوری که با او نازل شده پیروی نمودند، آنانند که رستگارند».');
INSERT INTO "hussein_taji" VALUES(7,158,'7:158','بگو: «ای مردم! من فرستادۀ الله به سوی همۀ شما هستم، آن پروردگاری که فرمان‌روایی آسمان‌ها و زمین از آنِ اوست، معبود (راستینی) جز او نیست، زنده می‌کند، و می‌میراند، پس به الله و فرستاده‌اش آن پیامبر «أمّی» (درس ناخوانده) که به الله و کلماتش ایمان دارد، ایمان بیاورید، و از او پیروی کنید؛ باشد که هدایت شوید.');
INSERT INTO "hussein_taji" VALUES(7,159,'7:159','و از قوم موسی، گروهی هستند که (مردم را) به (سوی) حق هدایت می‌کنند، و به آن (حق) عدالت (و حکم) می‌کنند.');
INSERT INTO "hussein_taji" VALUES(7,160,'7:160','و آن‌ها را به دوازده قبیله، گروه گروه (که هریک شاخه‌ای از دودمان یعقوب بود) تقسیم کردیم، و به موسی، آنگاه که قومش از او آب خواستند، وحی کردیم، که: «عصایت را بر سنگ بزن» ناگهان دوازده چشمه از آن بیرون جست، هر گروهی آبشخور خود را دانست. و ابر را بر (سر) آن‌ها سایبان ساختیم، و بر آن‌ها «من» (= ترنجبین) و «سلوی» (= بلدرچین) فرو فرستادیم، (و به آنان گفتیم:) از آنچه از پاکیزه‌ها روزی‌تان کرده‌ایم، بخورید، آن‌ها (با نافرمانی شان) بر ما ستم نکردند؛ بلکه بر خویشتن ستم می‌نمودند.');
INSERT INTO "hussein_taji" VALUES(7,161,'7:161','و (به یاد آورید) هنگامی را که به آن‌ها گفته شد: «در این شهر (بیت المقدس) ساکن شوید، و از هر جا که خواستید، از (نعمت‌های) آن بخورید، و بگویید: «گناهان ما را بریز» و با خشوع و فروتنی به دروازه در آیید، (اگر چنین کنید) گناهان شما را می‌بخشم، (و) نیکوکاران را (پاداش) افزونتری خواهیم داد».');
INSERT INTO "hussein_taji" VALUES(7,162,'7:162','آنگاه ستمگران آن‌ها، این سخن را که به آن‌ها گفته شده بود، تغییر دادند، (و به جای آن کلمۀ مسخره‌آمیزی گفتند) پس به (کیفر) ستمی که می‌کردند، عذابی از آسمان بر آن‌ها نازل کردیم.');
INSERT INTO "hussein_taji" VALUES(7,163,'7:163','و از آن‌ها دربارۀ (اهل) قریه‌ای که نزدیک دریای (سرخ) بود بپرس، هنگامی‌که آن‌ها در (روز) شنبه تجاوز می‌کردند، چون ماهیان آن‌ها در (روز) شنبه شان (که روز تعطیل و عبادت، بود روی آب) آشکار می‌شدند، و روزی‌که (تعظیم) شنبه نمی‌کردند، به (سراغ) آن‌ها نمی‌آمدند، این چنین آن‌ها را به (کیفر) آنکه نافرمانی می‌کردند، آزمایش کردیم.');
INSERT INTO "hussein_taji" VALUES(7,164,'7:164','و (به یاد آور) هنگامی را که گروهی از آن‌ها (به گروه دیگر) گفتند: «چرا گروهی را اندرز می‌دهید که الله آن‌ها را هلاک یا به عذاب شدیدی گرفتارشان خواهد کرد؟!» گفتند: «تا ما را نزد پروردگارتان عذری باشد، و شاید آن‌ها تقوا پیشه کنند».');
INSERT INTO "hussein_taji" VALUES(7,165,'7:165','پس چون آنچه را که به آن پند داده شدند، فراموش کردند، کسانی را که از بدی نهی می‌کردند، نجات دادیم، و کسانی را که ستم کردند، به (کیفر) آنکه نافرمان بودند، به عذابی سخت گرفتار ساختیم.');
INSERT INTO "hussein_taji" VALUES(7,166,'7:166','پس چون از (ترک) آنچه از آن نهی شده بودند، سرپیچی کردند، به آن‌ها گفتیم: «به صورت بوزینه‌های (خوار و) مطرود درآیید».');
INSERT INTO "hussein_taji" VALUES(7,167,'7:167','و (نیز به یاد آور) هنگامی‌که پروردگارت اعلام کرد، مسلّماً تا روز قیامت، کسی را بر آن‌ها مسلط خواهد کرد، که همواره به آن‌ها عذاب‌‌‌ سختی بچشاند، بی‌گمان پروردگارت زود کیفر است، و (برای توبه کننده گان) همانا او آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(7,168,'7:168','و آن‌ها را در زمین، بصورت گروه گروه، پراکنده ساختیم، (گروهی) از آن‌ها صالح، و (گروهی) غیر صالح‌اند، و آن‌ها را با نیکی‌ها و بدی‌ها آزمودیم، شاید (توبه کرده، به راه راست) باز گردند.');
INSERT INTO "hussein_taji" VALUES(7,169,'7:169','پس از آن‌ها، گروهی (غیر صالح) جانشین آن‌ها شدند، که کتاب (تورات) را به ارث بردند، متاع این (دنیای) پست را می‌گیرند، و می‌گویند: «به زودی آمرزیده خواهیم شد». و اگر متاع (دیگری) همانند آن برایشان بیاید، آن را (نیز) می‌گیرند، آیا پیمان کتاب (الله) از آن‌ها گرفته نشده است که بر الله جز حق نگویند؟ و آنچه در آن است خوانده‌اند؟ و سرای آخرت برای کسانی‌که تقوا پیشه کنند، بهتر است، آیا نمی‌اندیشید؟!');
INSERT INTO "hussein_taji" VALUES(7,170,'7:170','و کسانی‌که به کتاب (الله) تمسک می‌جویند، و نماز را بر پا می‌دارند، (پاداش بزرگی خواهند داشت) به راستی ما پا داش درست کاران را ضایع نخواهیم کرد.');
INSERT INTO "hussein_taji" VALUES(7,171,'7:171','و (به یاد آورید) هنگامی‌که کوه را همچون سایبانی بر فرازشان بر‌‌‌‌افراشتیم، و پنداشتند که بر آنان فرود می‌آید، (و در چنین حالت از آن‌ها عهد گرفتیم، و گفتیم:) آنچه را که به شما داده‌ایم، با نیرو (و جدیّت) بگیرید، و آنچه را که در آن است، یاد کنید، (و به آن عمل نمائید) تا پرهیزگار شوید.');
INSERT INTO "hussein_taji" VALUES(7,172,'7:172','و (به یاد بیاور) هنگامی‌که پروردگارت از پشت‌های فرزندان آدم، ذریۀ آن‌ها را برگرفت، و ایشان را بر خودشان گواه ساخت، (و فرمود:) «آیا من پروردگار شما نیستم؟» گفتند: «آری، (هستی) گواهی می‌دهیم». و (این گواهی بدین خاطر بود که مبادا) در روز قیامت بگویید: «ما از این، بی‌خبر بودیم».');
INSERT INTO "hussein_taji" VALUES(7,173,'7:173','یا (مبادا) بگویید: «پدران‌مان، از پیش شرک آورده بودند، و ما هم فرزندانی بعد از آن‌ها بودیم، آیا ما را به آنچه باطل گرایان انجام دادند، نابود (و مجازات) می‌کنی؟».');
INSERT INTO "hussein_taji" VALUES(7,174,'7:174','و این چنین، آیات را بتفصیل بیان می‌کنیم، و شاید (به سوی حق) باز گردند.');
INSERT INTO "hussein_taji" VALUES(7,175,'7:175','و (ای پیامبر!) بر آن‌ها بخوان سر گذشت کسی را که آیات خود را به او دادیم، آنگاه از آن جدا (و عاری) گشت، پس شیطان درپی او افتاد، و از گمراهان شد.');
INSERT INTO "hussein_taji" VALUES(7,176,'7:176','و اگر می‌خواستیم (مقام و منزلت) او را با آن (آیات) بالا می‌بردیم، ولی او بسوی زمین (و دنیا) مایل شد (و به پستی گرایید) و از هوای خویش پیروی کرد، پس مثل او چون مثل سگ (هار) است که اگر به او حمله کنی، زبان از دهان بیرون می‌آورد، و اگر او را به حال خود و اگذاری، (بازهم) زبانش را از دهان بیرون می‌آورد، این مثل گروهی است که آیات ما را تکذیب کردند، پس (این) داستان‌ها را (بر آن‌ها) باز گوکن، شاید بیندیشند.');
INSERT INTO "hussein_taji" VALUES(7,177,'7:177','چه بد مثلی دارند، گروهی که آیات ما را تکذیب کردند، و آن‌ها بر خویشتن ستم می‌کردند.');
INSERT INTO "hussein_taji" VALUES(7,178,'7:178','هرکس را که الله هدایت کند، پس او هدایت یافته (واقعی) است، و هرکس را که گمراه سازد، پس آن‌ها زیان‌کاران (واقعی) هستند.');
INSERT INTO "hussein_taji" VALUES(7,179,'7:179','بتحقیق، گروه بسیاری از جن و انس را برای جهنم آفریده‌ایم، آن‌ها دل‌های دارند که با آن نمی‌فهمند، و چشمانی دارند که با آن نمی‌بینند، و گوش‌هایی دارند که با آن نمی‌شنوند، آنان مانند چهار پایانند، بلکه اینان گمراه‌ترند، اینان همان غافلانند.');
INSERT INTO "hussein_taji" VALUES(7,180,'7:180','و برای خدا، نام‌های نیک است، پس به آن (نام‌ها) او را بخوانید، و کسانی را که در نام‌هایش تحریف (و کجروی) می‌کنند، رها کنید، بزودی آن‌ها کیفر آنچه را می‌کردند، داده خواهند شد.');
INSERT INTO "hussein_taji" VALUES(7,181,'7:181','و از (میان) آنان که آفریده‌ایم، گروهی هستند که به حق هدایت می‌نمایند، و به آن (حکم و) عدالت می‌کنند.');
INSERT INTO "hussein_taji" VALUES(7,182,'7:182','و کسانی‌که آیات ما را تکذیب کردند، به تدریج از جایی که نمی‌دانند، گرفتارشان خواهیم کرد.');
INSERT INTO "hussein_taji" VALUES(7,183,'7:183','و به آن‌ها مهلت می‌دهم، بی‌گمان تدبیر من متین (و استوار) است.');
INSERT INTO "hussein_taji" VALUES(7,184,'7:184','آیا فکر نکرده‌اند که همنشین آن‌ها (= رسول الله) هیچ گونه (نشانی از) دیوانگی ندارد؟ او تنها بیم‌دهندۀ آشکار است.');
INSERT INTO "hussein_taji" VALUES(7,185,'7:185','آیا در ملکوت آسمان‌ها و زمین، و آنچه الله آفریده است، نمی‌نگرند؟! و (آیا فکر نمی‌کنند) شاید اجل‌شان نزدیک شده باشد؟! پس بعد از این (قرآن)، به کدام سخن، ایمان خواهند آورد؟!');
INSERT INTO "hussein_taji" VALUES(7,186,'7:186','هرکس را که الله گمراه کند، پس هدایت کننده‌ای برایش نیست، و آن‌ها را در سرکشی و طغیان‌شان سرگردان رها می‌کند.');
INSERT INTO "hussein_taji" VALUES(7,187,'7:187','(ای پیامبر) دربارۀ قیامت از تو سوال می‌کنند، کی واقع می‌شود؟ بگو: «مسلماً علم آن فقط نزد پروردگار من است، و هیچ‌کس جز او (نمی‌تواند) آن را در وقت معینش آشکار سازد؛ (دانستن آن) در آسمان‌ها و زمین سنگین (و پوشیده) است، و جز بطور ناگهانی، به سوی شما نمی‌آید». (چنان) از تو سوال می‌کنند که گویی تو از و قوع آن خبر داری، بگو: «علم آن، فقط نزد الله است، ولی بیشتر مردم نمی‌دانند».');
INSERT INTO "hussein_taji" VALUES(7,188,'7:188','بگو: «من مالک سود و زیان خویشتن نیستم، مگر آنچه را الله بخواهد، و اگر غیب می‌دانستم، خیر (و سود) بسیاری (برای خود) فراهم می‌ساختم، و هیچ بدی (و زیانی) به من نمی‌رسید، من (کسی) نیستم، جز بیم‌دهنده، و بشارت‌دهنده‌ای برای گروهی که ایمان دارند.');
INSERT INTO "hussein_taji" VALUES(7,189,'7:189','اوست که شما را از یک تن آفرید، و همسرش را (نیز) از خودش پدید آورد؛ تا در کنار او آرام بگیرد، پس چون (مرد با همسرش) آمیزش کرد، حملی سبک برداشت، پس مدتی با آن (حمل) گذرانید، و چون سنگین شد، هر دو (زن و شوهر) به درگاه الله، پروردگار شان دعا کردند، که: «اگر (فرزندی) تندرست (و شایسته) به ما بدهی، مسلماً از سپاس‌گزاران خواهیم بود».');
INSERT INTO "hussein_taji" VALUES(7,190,'7:190','پس چون (الله، فرزندی) تندرست (و شایسته) به آن‌ها داد، برای او در آنچه به آنان داده بود، شریکانی قرار دادند، پس الله برتراست از آنچه شریک (و همتای) او قرار می‌دهند.');
INSERT INTO "hussein_taji" VALUES(7,191,'7:191','آیا چیزی را شریک می‌گیرند که چیزی را نمی‌آفریند و خودشان مخلوق‌اند.');
INSERT INTO "hussein_taji" VALUES(7,192,'7:192','و نمی‌توانند آن‌ها را یاری کنند، و نه خودشان را یاری می‌دهند.');
INSERT INTO "hussein_taji" VALUES(7,193,'7:193','و اگر آن‌ها را به سوی هدایت دعوت کنید، از شما پیروی نمی‌کنند، برای شما یکسان است، خواه آن‌ها را دعوت کنید و یا خاموش باشید.');
INSERT INTO "hussein_taji" VALUES(7,194,'7:194','بی‌گمان آن‌هایی را که غیر از الله می‌خوانید، بندگانی همچون شما هستند، پس آن‌ها را بخوانید، اگر راست می‌گویید (و آن‌ها الله هستند)، پس باید (دعای) شما را اجابت کنند!');
INSERT INTO "hussein_taji" VALUES(7,195,'7:195','آیا پا‌‌هایی دارند که با آن راه بروند؟! یا دست‌هایی دارند، که با آن چیزی را بگیرند؟! یا چشمانی دارند، که با آن ببینند؟! آیا گوش‌هایی دارند، که با آن بشنوند؟! (ای پیامبر! به مشرکین) بگو: «شریکان‌تان را بخوانید، سپس (بر ضد من) تدبیر (و نیرنگ) کنید، و مرا مهلت ندهید.');
INSERT INTO "hussein_taji" VALUES(7,196,'7:196','همانا یاور (و سرپرست) من، الله است که (این) کتاب را نازل کرده، و او یاور (و کار ساز) صالحان است.');
INSERT INTO "hussein_taji" VALUES(7,197,'7:197','و کسانی را که به جای او می‌خوانید، نمی‌توانند شما را یاری کنند، و نه خودشان را یاری دهند.');
INSERT INTO "hussein_taji" VALUES(7,198,'7:198','و اگر آن‌ها را به سوی هدایت دعوت کنید، (سخنان‌تان را) نمی‌شنوند، و آنان را می‌بینی که به تو نگاه می‌کنند، در حالی‌که نمی‌بینند».');
INSERT INTO "hussein_taji" VALUES(7,199,'7:199','(ای پیامبر!) گذشت را پیشه کن، و به نیکی فرمان ده، و از نادانان روی بگردان.');
INSERT INTO "hussein_taji" VALUES(7,200,'7:200','و اگر از سوی شیطان وسوسه‌ای به تو رسد، به الله پناه ببر، بی‌گمان او شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(7,201,'7:201','همانا کسانی‌که پرهیزگاری می‌کنند، چون (خاطره و) وسوسه‌ای از شیطان به آن‌ها برسد، به یاد (الله) می‌افتند (و الله را یاد می‌کنند) پس ناگهان بینا می‌گردند.');
INSERT INTO "hussein_taji" VALUES(7,202,'7:202','و برادران‌شان (از شیطان) آنان را به گمراهی می‌کشانند، سپس (در این زمینه) کوتاهی نمی‌کنند.');
INSERT INTO "hussein_taji" VALUES(7,203,'7:203','و چون آیه (و معجزه‌ای که آن‌ها می‌خواهند) برای آنان نیاوری، گویند: «چرا خودت آن را بر نگزیدی؟!». بگو: «من فقط از چیزی پیروی می‌کنم که از پروردگارم به من وحی می‌شود، این (قرآن) حجت‌ها (و بینا)یی از طرف پروردگارتان، و مایۀ هدایت و رحمت است برای گروهی که ایمان می‌آورند».');
INSERT INTO "hussein_taji" VALUES(7,204,'7:204','و چون قرآن خوانده شود، پس به آن گوش فرا دهید، و خاموش باشید، تا مشمول رحمت شوید.');
INSERT INTO "hussein_taji" VALUES(7,205,'7:205','و پروردگارت را در دل خود، با تضرع و بیم، نه با صدای بلند، در صبحگاهان و شامگاهان، یاد کن، و از غافلان مباش.');
INSERT INTO "hussein_taji" VALUES(7,206,'7:206','همانا کسانی‌که در نزد پروردگار تو هستند، از عبادتش تکبر نمی ‌ورزند، و او را به پاکی یاد می‌کنند، و برایش سجده می‌کنند.');
INSERT INTO "hussein_taji" VALUES(8,1,'8:1','از تو دربارۀ انفال (= غنایم) سوال می‌کنند، بگو: «انفال از آنِ الله و پیامبر است، پس از الله بترسید؛ و میان خودتان را اصلاح کنید، و خدا و پیامبرش را اطاعت کنید؛ اگر ایمان دارید.');
INSERT INTO "hussein_taji" VALUES(8,2,'8:2','مؤمنان تنها کسانی هستند که چون نام الله برده شود، دل‌های شان ترسان گردد، و چون آیات او بر آن‌ها خوانده شود، ایمان‌شان افزون گردد، و بر پروردگارشان توکل می‌کنند.');
INSERT INTO "hussein_taji" VALUES(8,3,'8:3','کسانی‌که نماز را بر پا می‌دارند، و از آنچه به آنان روزی داده‌ایم، انفاق می‌کنند.');
INSERT INTO "hussein_taji" VALUES(8,4,'8:4','اینان، مؤمنان حقیقی هستند، برای آنان درجاتی پیا‌پی (عالی) نزد پروردگارشان است، و (همچنین) آمرزش، و روزی فراوان و نیکو (در بهشت) است.');
INSERT INTO "hussein_taji" VALUES(8,5,'8:5','همان‌گونه که پروردگارت تو را به حق از خانه‌ات (= مدینه، به سوی میدان بدر) بیرون آورد، در حالی‌که گروهی از مؤمنان ناخشنود بودند.');
INSERT INTO "hussein_taji" VALUES(8,6,'8:6','آن‌ها پس از آن که حقیقت آشکار شد؛ دربارۀ حق (= جهاد) با تو مجادله می‌کنند، گویی به سوی مرگ رانده می‌شوند، و خود می‌نگرند.');
INSERT INTO "hussein_taji" VALUES(8,7,'8:7','و (به یاد آورید) هنگامی را که الله به شما وعده داد که یکی از دو گروه (= کاروان تجاری یا لشکر) نصیب شما خواهد بود، و شما دوست می‌داشتید که کاروان غیر جنگی (تجاری) برای شما باشد، و الله می‌خواست با سخنان خویش حق را پایدار (و استوار) کند و ریشۀ کافران را قطع کند.');
INSERT INTO "hussein_taji" VALUES(8,8,'8:8','تا حق را ثابت، و باطل را از میان بر دارد، اگر چه گنهکاران نا‌‌ خشنود باشند.');
INSERT INTO "hussein_taji" VALUES(8,9,'8:9','(بیاد آورید) هنگامی‌که از پروردگارتان (فریاد و) یاری می‌خواستید، پس او (خواستۀ) شما را پذیرفت. (و فرمود:) «من شما را با یک هزار از فرشتگان، که پیا‌‌پی فرود می‌آیند، یاری می‌کنم».');
INSERT INTO "hussein_taji" VALUES(8,10,'8:10','و الله این (یاری و مدد) را تنها برای شادی و اطمینان قلب شما قرار داد، و گرنه، پیروزی جز از طرف الله نیست، به راستی الله پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(8,11,'8:11','(و به یاد آورید) هنگامی را که خواب سبکی که آرامشی از سوی او بود، شما را فرا گرفت، و از آسمان آبی بر شما فرو فرستاد، تا شما را با آن پاک کند، و پلیدی شیطان را از شما دور سازد، و دل‌های شما را محکم بدارد، و گام‌هایتان را با آن استوار کند.');
INSERT INTO "hussein_taji" VALUES(8,12,'8:12','(و به یاد آور) هنگامی‌که پروردگارت به فرشتگان وحی کرد: «من با شما هستم، پس کسانی را که ایمان آورده‌اند؛ ثابت قدم دارید، به زودی دردل کسانی‌که کافر شدند؛ ترس و هراس می‌افکنم، پس بر فراز گردن‌ها بزنید، و همۀ انگشتان‌شان را بزنید (و قطع کنید).');
INSERT INTO "hussein_taji" VALUES(8,13,'8:13','این بخاطر آن است که آن‌ها با الله و رسولش مخالفت کردند، و هر کس با الله و رسولش مخالفت کند، پس (بداند که) الله سخت کیفر است.');
INSERT INTO "hussein_taji" VALUES(8,14,'8:14','(ای کافران) شما این (عذاب و مجازات در دنیا) را بچشید! و (بدانید) که برای کافران (در قیامت) عذاب آتش است.');
INSERT INTO "hussein_taji" VALUES(8,15,'8:15','ای کسانی‌که ایمان آورده‌اید! چون با انبوه کافران (در میدان جنگ) روبرو شوید، پس به آن‌ها پشت نکنید.');
INSERT INTO "hussein_taji" VALUES(8,16,'8:16','و هر کس در آن روز به آن‌ها پشت کند ـ مگر آنکه (هدفش از کناره‌گیری) برای حملۀ دوباره یا (به قصد) پیوستن به گروهی (دیگر باشد) ـ به خشم الله بازگشته (و سزاوار) است، و جایگاه او جهنم است، و چه بد جایگاهی است.');
INSERT INTO "hussein_taji" VALUES(8,17,'8:17','پس شما آن‌ها را نکشتید، بلکه الله آن‌ها را کشت. و هنگامی‌که (به سوی آن‌ها خاک و سنگ) انداختی، تو نینداختی، بلکه الله انداخت، تا مؤمنان را به آزمایشی نیکو از جانب خود بیازماید، بی‌گمان الله شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(8,18,'8:18','این (واقعه) چنین بود (که دیدید) و (بدانید که) همانا الله سست‌کنندۀ مکر (و نقشه‌های) کافران است.');
INSERT INTO "hussein_taji" VALUES(8,19,'8:19','(ای کافران!) خواهان فتح و پیروزی (حق) هستید، پس بی‌گمان فتح و پیروزی (مسلمانان) برایتان آمد، و اگر (از کفر و مخالفت و جنگ) دست بردارید، برای شما بهتر است. و اگر باز گردید، (ما هم) باز خواهیم گشت، و گروه شما هر چند زیاد باشد، شما را بی‌نیاز نخواهد کرد، (زیرا) که الله با مؤمنان است.');
INSERT INTO "hussein_taji" VALUES(8,20,'8:20','ای کسانی‌که ایمان آورده‌اید! الله و رسولش را اطاعت کنید، و از او روی نگردانید در حالی‌که (سخن او را) می‌شنوید.');
INSERT INTO "hussein_taji" VALUES(8,21,'8:21','و مانند کسانی نباشید که گفتند: «شنیدیم» حال آنکه آنان نمی‌شنیدند.');
INSERT INTO "hussein_taji" VALUES(8,22,'8:22','همانا بدترین جنبندگان نزد الله، (افراد) کر و لالی هستند، که نمی‌اندیشند.');
INSERT INTO "hussein_taji" VALUES(8,23,'8:23','و اگر الله خیری در آن‌ها می‌دانست، قطعاً به آنان می‌شنواند، و اگر (هم با این حال) به آنان می‌شنواند، باز روی می‌گرداندند و اعراض (و سرپیچی) می‌کردند.');
INSERT INTO "hussein_taji" VALUES(8,24,'8:24','ای کسانی‌که ایمان آورده‌اید! (دعوت) الله و پیامبر (او) را اجابت کنید، هنگامی‌که شما را به سوی چیزی فرا می‌خواند که شما را حیات (و زندگی) می‌بخشد، و بدانید که الله میان شخص و قلب او حایل می‌شود، و (بدانید) که به سوی او محشور خواهید شد.');
INSERT INTO "hussein_taji" VALUES(8,25,'8:25','و از فتنه‌ای بپرهیزید که تنها به ستمکارانِ شما نمی‌رسد، (بلکه همه را فرا می‌گیرد) و بدانید که الله سخت کیفراست!');
INSERT INTO "hussein_taji" VALUES(8,26,'8:26','وبه یاد آورید زمانی را که شما در روی زمین اندک و ناتوان بودید، می‌ترسیدید که مردم شما را بربایند، پس الله شما را پناه داد، و با یاری خود شما را تأیید (و تقویت) کرد، و از چیزهای پاکیزه به شما روزی بخشید، شاید شما سپاس‌گزار باشید.');
INSERT INTO "hussein_taji" VALUES(8,27,'8:27','ای کسانی‌که ایمان آورده‌اید! به الله و پیامبر (او) خیانت نکنید، و (نیز) در امانت‌های خود، در حالی‌که می‌دانید خیانت نورزید.');
INSERT INTO "hussein_taji" VALUES(8,28,'8:28','و بدانید که اموال‌ شما، و اولاد شما، (وسیلۀ) آزمایش است، و همانا الله است که پاداش بزرگ نزد او است.');
INSERT INTO "hussein_taji" VALUES(8,29,'8:29','ای کسانی‌که ایمان آورده‌اید! اگر از الله بترسید برای شما راه نجات (و وسیله‌ای برای جدایی حق از باطل) قرار می‌دهد، و گناهان‌تان را می‌پوشاند، و شما را می‌آمرزد، و الله صاحب فضل و بخشش بزرگ است.');
INSERT INTO "hussein_taji" VALUES(8,30,'8:30','و (به یاد آور) آنگاه که کافران در(بارۀ) تو نقشه می‌کشیدند، که تو را به زندان بیفکنند، یا تو را بکشند، و یا (از مکه) بیرونت کنند، آن‌ها چاره و مکر می‌اندیشیدند، و الله (هم) تدبیر می‌کرد، و الله بهترین (چاره‌جویان و) تدبیرکنندگان است.');
INSERT INTO "hussein_taji" VALUES(8,31,'8:31','و چون آیات ما بر آن‌ها خوانده شود، گویند: «همانا شنیدیم، اگر ما (هم) بخواهیم، مانند آن را می‌گوییم، این‌ها جز افسانه‌های پیشینیان نیست».');
INSERT INTO "hussein_taji" VALUES(8,32,'8:32','و (به یاد آور) آنگاه که گفتند: «پروردگارا! اگر این (قرآن) حق است، و از جانب توست، پس از آسمان سنگی بر ما بباران، یا عذاب دردناکی بر ما بفرست».');
INSERT INTO "hussein_taji" VALUES(8,33,'8:33','و تا تو (ای پیامبر) در میان آن‌ها هستی، الله آن‌ها را عذاب نخواهد کرد، و (نیز) تا زمانی که استغفار می‌کنند؛ الله عذاب‌شان نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(8,34,'8:34','وچرا الله آن‌ها را عذاب نکند، حال آنکه آنان (مردم را) از مسجد الحرام باز می‌دارند، در حالی‌که آنان متولیان و سرپرست آن نیستند (و لیاقت آن را ندارند) متولی و سرپرست آن تنها پرهیزگارانند، ولی بیشترشان نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(8,35,'8:35','و نمازشان نزد خانۀ (کعبه) چیزی جز سوت کشیدن و کف زدن نبود، پس به (کیفر) کفرتان، عذاب را بچشید.');
INSERT INTO "hussein_taji" VALUES(8,36,'8:36','به راستی کسانی‌که کافر شدند، اموال‌شان را برای باز‌داشتن (مردم) از راه الله خرج می‌کنند، پس به زودی همه را خرج می‌کنند، آنگاه (مایۀ) حسرت و پیشمانی بر آنان خواهد بود، سپس شکست می‌خورند، و (سرانجام) کسانی‌که کافر شدند، بسوی (آتش) جهنم رانده می‌شوند.');
INSERT INTO "hussein_taji" VALUES(8,37,'8:37','تا الله پلید را از پاک جدا سازد، و پلیدان را روی هم بگذارد، آنگاه همگی را متراکم سازد، و سپس یک جا در دوزخ قرار دهد، این‌ها همان زیان‌کاران (واقعی) هستند.');
INSERT INTO "hussein_taji" VALUES(8,38,'8:38','(ای پیامبر!) به کسانی‌که کافر شدند بگو: «اگر (از کفر و دشمنی) دست بر دارند، گذشته‌هایشان آمرزیده می‌شود، و اگر (به همان اعمال خود) بازگردند، پس بی‌گمان سنت (و روش الله دربارۀ) پیشینیان گذشته است (و دربارۀ آن‌ها نیز اجرا می‌شود).');
INSERT INTO "hussein_taji" VALUES(8,39,'8:39','و با آن‌ها پیکار کنید، تا فتنۀ (شرک) ریشه کن شود، و (پرستش و) دین، همه از آن الله باشد، پس اگر آن‌ها (از شرک و آزار و اذیت) دست بر دارند، الله به آنچه می‌کنند؛ بیناست.');
INSERT INTO "hussein_taji" VALUES(8,40,'8:40','و اگر (سرپیچی و) روی گردانی کنند، پس بدانید که الله سرپرست (و مولای) شماست، چه نیکو سرپرست و چه نیکو یاوری است.');
INSERT INTO "hussein_taji" VALUES(8,41,'8:41','و بدانید که هرگونه غنیمتی به دست آوردید، یک پنجمش برای خدا، و برای پیامبر، و برای خویشان، و یتیمان و مسکینان و در راه ماندگان است، اگر به الله و آنچه بر بندۀ خود در روز فرقان (= جدایی حق از باطل)، روز بر خورد دو گروه (مؤمنین و مشرکین در بدر) نازل کردیم، ایمان آورده‌اید، و الله بر هر چیز تواناست.');
INSERT INTO "hussein_taji" VALUES(8,42,'8:42','آنگاه که شما به کناره نزدیکتر بودید، و آن‌ها به کنارۀ دورتر بودند، و کاروان (قریش) پایین‌تر از شما بود، و اگر با یکدیگر وعده می‌گذاشتید، قطعا در (انجام) وعدۀ (خود) اختلاف می‌کردید، ولی الله (بدون میعاد، شما را جمع کرد و روبروی یکدیگر قرار داد) تا کاری را که می‌بایست انجام شود، به انجام برساند، تا آن که هلاک (و نابود) می‌شود، از روی دلیل (و حجت) هلاک شود، و کسی‌که زنده می‌ماند، از روی دلیل (و حجت) زنده ماند، و همانا الله شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(8,43,'8:43','(بیاد آور) آنگاه که الله (عدۀ) آن‌ها را در خوابت به تو اندک نشان داد، و اگر زیاد نشان می‌داد؛ مسلماً سست می‌شدید، و در این کار اختلاف می‌کردید، ولی الله (شما را از سستی و اختلاف) سلامت نگاه داشت، بی‌گمان او به آنچه در سینه‌هاست؛ آگاه است.');
INSERT INTO "hussein_taji" VALUES(8,44,'8:44','و (بیاد آور) آنگاه که چون باهم روبرو شدید، آن‌ها را به چشم شما کم نشان داد، و شما را (نیز) به چشم آن‌ها کم جلوه داد، تا الله، کاری را که می‌بایست انجام شود، به انجام برساند، و همۀ کارها به الله باز گردانده می‌شود.');
INSERT INTO "hussein_taji" VALUES(8,45,'8:45','ای کسانی‌که ایمان آورده‌اید! هنگامی‌که با گروهی (از دشمن) روبرو شدید، پایدار (و ثابت قدم) باشید، و الله را بسیار یاد کنید، تا رستگار شوید.');
INSERT INTO "hussein_taji" VALUES(8,46,'8:46','و الله و پیامبرش را اطاعت کنید، و با همدیگر نزاع نکنید، که سست شوید، و قوت (و مهابت) شما از میان برود، و صبر کنید، بی‌گمان الله با صابران است.');
INSERT INTO "hussein_taji" VALUES(8,47,'8:47','و مانند کسانی نباشید که با سرکشی و خود نمایی به مردم، از سرزمین خود (مکه) بیرون آمدند، و (مردم را) از راه الله باز می‌داشتند، و الله به آنچه می‌کنند؛ احاطه دارد.');
INSERT INTO "hussein_taji" VALUES(8,48,'8:48','و (به یاد بیاورید) هنگامی را که شیطان اعمال آن‌ها را در نظرشان بیاراست، و گفت: «امروز هیچ کس از مردم بر شما پیروز نخواهد شد، و من حامی (و پناه دهندۀ) شما هستم». پس هنگامی‌که دو گروه روبرو شدند، به عقب بازگشت، و گفت: «بی‌گمان من از شما بیزارم، به راستی من چیزی را می‌بینم که شما نمی‌بینید، همانا من از الله می‌ترسم، و الله سخت کیفر است!».');
INSERT INTO "hussein_taji" VALUES(8,49,'8:49','و (به یاد آورید) هنگامی را که منافقان و آنان که در دل‌های شان بیماری است، می‌گفتند: «اینان را دین‌شان بفریفته است». و هرکس بر الله توکل کند، پس بی‌گمان الله پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(8,50,'8:50','و اگر ببینی هنگامی‌که فرشتگان (مرگ) جان کافران را می‌گیرند، به صورت و پشت آن‌ها می‌زنند و (می‌گویند:) «عذاب سوزنده را بچشید» (هر آینه تعجب می‌کردی).');
INSERT INTO "hussein_taji" VALUES(8,51,'8:51','این به (خاطر) کارهایی است که پیش از این فرستاده‌اید، و آنکه الله هرگز نسبت به بندگانش ستم را روا نمی‌دارد.');
INSERT INTO "hussein_taji" VALUES(8,52,'8:52','همانند شیوۀ فرعونیان، و کسانی‌که پیش از آنان بودند، به آیات الله کفر ورزیدند، پس الله آن‌ها را به کیفر گناهان‌شان فرو گرفت، مسلماً الله نیرومند سخت کیفر است.');
INSERT INTO "hussein_taji" VALUES(8,53,'8:53','این، بدان خاطر است که الله، هیچ نعمتی را که بر قومی ارزانی داشته، تغییر نمی‌دهد، تا آنکه آن‌ها (روش) خودشان را تغییر دهند، و بی‌گمان الله شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(8,54,'8:54','مانند شیوۀ فرعونیان و کسانی‌که پیش از آنان بودند، آیات پروردگارشان را تکذیب کردند، پس آنان را به کیفر گناهان‌شان هلاک کردیم، و فرعونیان را غرق نمودیم، و همۀ (آن‌ها) ستمکار بودند.');
INSERT INTO "hussein_taji" VALUES(8,55,'8:55','یقیناً، بدترین جنبندگان نزد الله، کسانی هستند که کافر شدند، پس آنان ایمان نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(8,56,'8:56','کسانی‌که با آن‌ها پیمان بستی، سپس هر بار پیمان خود را می‌شکنند، و آن‌ها (در خیانت و پیمان‌شکنی از الله) نمی‌ترسند.');
INSERT INTO "hussein_taji" VALUES(8,57,'8:57','پس اگر آن‌ها را در (میدان) جنگ بیابی، چنان به آنان حمله ببر تا کسانی‌که پشت سر آن‌ها هستند (بترسند و) باشد که پند گیرند.');
INSERT INTO "hussein_taji" VALUES(8,58,'8:58','و اگر از خیانت قومی بیم داشتی؛ منصفانه (پیمان‌شان را) به سوی آنان بیانداز (و فسخ پیمان‌شان را برای همگان اعلان کن)، به راستی که الله خائنان را دوست نمی‌دارد.');
INSERT INTO "hussein_taji" VALUES(8,59,'8:59','و کسانی‌که کافر شدند، نپندارند که پیشی گرفته‌اند؛ آن‌ها هرگز ما را ناتوان نخواهند کرد.');
INSERT INTO "hussein_taji" VALUES(8,60,'8:60','و آنچه از نیرو در توان دارید؛ برای (مقابله با) آن‌ها آماده سازید، و (همچنین) از اسبان بسته (ی ورزیده، مهیا کنید) تا به وسیلۀ آن دشمن الله و دشمن خود را بترسانید، و (همچنین) دشمنان دیگری غیر از آن‌ها را، که شما نمی‌شناسید و خدا آن‌ها را می‌شناسد (بترسانید) و هر چیزی را که در راه الله خرج می‌کنید، (پاداشش) به تمام و کمال به شما داده می‌شود، و به شما ستم نخواهد شد.');
INSERT INTO "hussein_taji" VALUES(8,61,'8:61','و اگر به صلح مایل شدند، پس تو (نیز) از در صلح درآی، و بر الله توکل کن، بی‌گمان او شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(8,62,'8:62','و اگر بخواهند تو را فریب دهند، پس الله برای تو کافی است، اوست که با یاری خود و مؤمنان تو را تأیید (و تقویت) کرد.');
INSERT INTO "hussein_taji" VALUES(8,63,'8:63','و بین دل‌های آن‌ها الفت داد، اگر تمام آنچه را که در روی زمین است خرج می‌کردی، نمی‌توانستی بین دل‌های شان الفت دهی، ولی الله در میان آن‌ها الفت داد، همانا او پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(8,64,'8:64','ای پیامبر! الله تو را و کسانی از مؤمنان را که از تو پیروی کرده‌اند؛ (از شر دشمنان) کفایت می‌کند.');
INSERT INTO "hussein_taji" VALUES(8,65,'8:65','ای پیامبر! مؤمنان را به جنگ تشویق کن، اگر بیست نفر بردبار از شما باشند، بر دویست نفر (از کافران) غلبه می‌کنند، و اگر صد نفر از شما باشند بر هزار نفر از کسانی‌که کافر شدند، غلبه می‌کنند، زیرا آنان گروهی هستند که نمی‌فهمند.');
INSERT INTO "hussein_taji" VALUES(8,66,'8:66','اکنون الله به شما تخفیف داد، و دانست که در شما ناتوانی است، پس اگر صد نفر بردبار از شما باشند؛ بر دویست نفر پیروز می‌شوند، و اگر از شما هزار نفر باشند، به فرمان الله بر دو هزار نفر پیروز خواهند شد، و الله با صابران است.');
INSERT INTO "hussein_taji" VALUES(8,67,'8:67','هیچ پیامبری را سزاوار نیست که اسیرانی داشته باشد؛ تا آنکه در زمین کشتار بسیار کند، شما (با گرفتن فدیه از اسیران) متاع دنیا را می‌خواهید، و الله (سرای) آخرت را (برای شما) می‌خواهد، و الله پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(8,68,'8:68','اگر حکم پیشین الهی نبود (که غنیمت و فدیۀ اسیر حلال است) [ بعضی می‌گویند که مراد از حکم پیشین حلال‌بودن مال غنیمت است یعنی این که این حکم پیشین (نوشته شده) تقدیر الهی بود که مال غنیمت برای مسلمانان حلال خواهد گردید، لذا شما با گرفتن فدیه کار جایزی را انجام داده‌اید و اگر چنین نمی‌بود به سبب گرفتن فدیه به عذاب بزرگی دچار می‌شدید. بعضی آن را مغفرت اهل بدر دانسته‌اند. و بعضی آن را به نیامدن عذاب در هنگام وجود رسول الله صلی الله علیه وسلم تفسیر کرده‌اند.]، قطعاً در آنچه گرفتید، عذاب بزرگی به شما می‌رسید.');
INSERT INTO "hussein_taji" VALUES(8,69,'8:69','پس از آنچه غنیمت گرفته‌اید، حلال پاکیزه بخورید، و از الله بترسید، همانا الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(8,70,'8:70','ای پیامبر! به اسیرانی که در دست شما هستند بگو: «اگر الله خیری در دل‌های شما بداند، بهتر از آنچه از شما گرفته شد، به شما خواهد داد، و شما را می‌آمرزد، و الله آمرزندۀ مهربان است».');
INSERT INTO "hussein_taji" VALUES(8,71,'8:71','و اگر بخواهند به تو خیانت کنند، پس آن‌ها پیش از این (نیز) به الله خیانت کردند، و الله (تو را) بر آن‌ها پیروز گردانید، و الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(8,72,'8:72','همانا کسانی‌که ایمان آوردند، و هجرت کردند، و با اموال و جان‌هایشان در راه الله جهاد نمودند، و کسانی‌که (ایشان را) پناه دادند و یاری نمودند، اینان یاران (و دوستدار) یکدیگرند، و کسانی‌که ایمان آوردند و هجرت نکرده‌اند، شما هیچ گونه دوستی (و مسؤلیتی) در برابر آن‌ها ندارید؛ تا آنکه هجرت کنند، و اگر در (کار) دین از شما یاری خواستند، پس برشماست که (آن‌ها را) یاری کنید، مگر بر علیه گروهی که میان شما و آن‌ها پیمانی است، و الله به آنچه می‌کنید؛ بیناست.');
INSERT INTO "hussein_taji" VALUES(8,73,'8:73','و کسانی‌که کافر شدند، دوستدار (و یاور) یکدیگرند، اگر (شما ای مؤمنان این کار را) نکنید، فتنه و فسادی بزرگ در زمین بر پا خواهد شد.');
INSERT INTO "hussein_taji" VALUES(8,74,'8:74','و کسانی‌که ایمان آوردند، و هجرت نمودند، و در راه الله جهاد کردند، و کسانی‌که (ایشان را) پناه دادند و یاری نمودند، اینان مؤمنان حقیقی هستند، برای آن‌ها آمرزش و روزی شایسته‌ای خواهد بود.');
INSERT INTO "hussein_taji" VALUES(8,75,'8:75','و کسانی‌که بعد از آن‌ها ایمان آوردند و هجرت نمودند، و به همراه شما جهاد کردند، پس آنان از شما هستند. و خویشاوندان در کتاب الله نسبت به یکدیگر سزاوارترند، و همانا الله به همه چیز داناست.');
INSERT INTO "hussein_taji" VALUES(9,1,'9:1','(اعلام) بیزاری از سوی الله و پیامبرش، به کسانی از مشرکان است که (با آن‌ها) پیمان بسته‌اید.');
INSERT INTO "hussein_taji" VALUES(9,2,'9:2','پس چهار ماه (مهلت دارید که) در زمین سیر کنید، و بدانید که شما نمی‌توانید الله را ناتوان سازید، و همانا الله خوار و رسواکنندۀ کافران است.');
INSERT INTO "hussein_taji" VALUES(9,3,'9:3','و (این) اعلامی است از جانب الله و پیامبرش به مردم، در روز حج اکبر (= روز عید قربان) که: الله و پیامبرش از مشرکان بیزارند، پس اگر توبه کنید، برای شما بهتراست، و اگر روی بگردانید (و سر پیچی کنید) پس بدانید که شما نمی‌توانید الله را نا توان سازید، و کسانی‌که کافر شدند به عذاب دردناکی بشارت ده!.');
INSERT INTO "hussein_taji" VALUES(9,4,'9:4','مگر کسانی از مشرکان که با آن‌ها پیمان بسته‌اید، آنگاه آن‌ها چیزی (از آن در حق شما) فرو گذار نکردند، و کسی را بر (علیه) شما یاری ندادند، پس پیمان آن‌ها را تا مدت (پیمان) شان به آخر (و اتمام) برسانید، همانا الله پرهیزگاران را دوست دارد.');
INSERT INTO "hussein_taji" VALUES(9,5,'9:5','پس هنگامی‌که ماه‌های حرام پایان پذیرفت؛ مشرکان را هر کجا یافتید، بکشید، و آن‌ها را (اسیر) بگیرید، و آن‌ها را محاصره کنید، و در هر کمینگاه، بر سر راه‌شان بنشینید، پس اگر توبه کردند، و نماز را برپا داشتند، و زکات را پرداختند؛ آن‌ها را رها کنید، بی‌گمان الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(9,6,'9:6','و اگر یکی از مشرکان از تو پناه خواست، پس او را پناه بده، تا سخن الله را بشنود، سپس اورا به محل امنش برسان، این بدان سبب است که آن‌ها گروهی‌اند که نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(9,7,'9:7','چگونه برای مشرکان نزد الله و نزد رسولش (عهد و) پیمانی خواهد بود، مگر کسانی‌که نزد مسجد الحرام با آن‌ها پیمان بستید، تا زمانی که (بر پیمان‌شان) وفادار ماندند، شما نیز (بر سرپیمان‌تان) وفادار باشید، بی‌گمان الله پرهیزگاران را دوست دارد.');
INSERT INTO "hussein_taji" VALUES(9,8,'9:8','چگونه (پیمانی باشد) درحالی‌که اگر بر شما پیروز شوند، نه خویشاوندی را رعایت می‌کنند و نه پیمان را؟! شما را با زبان‌های شان خشنود می‌کنند، و (لی) دل‌های شان ابا دارد، و بیشتر شان نافرمانند.');
INSERT INTO "hussein_taji" VALUES(9,9,'9:9','آیات الله را به بهای اندکی فروختند، پس (مردم را) از راه او (= الله) بازداشتند، آن‌ها کار بدی انجام می‌دادند!');
INSERT INTO "hussein_taji" VALUES(9,10,'9:10','دربارۀ هیچ فرد مؤمنی خویشاوندی و پیمان را رعایت نمی‌کنند، و آنان مردمی تجاوز کارند.');
INSERT INTO "hussein_taji" VALUES(9,11,'9:11','پس اگر توبه کنند، و نماز را بر پا دارند، و زکات را بدهند، برادران دینی شما هستند، و ما آیات خود را برای گروهی که می‌دانند بیان می‌کنیم.');
INSERT INTO "hussein_taji" VALUES(9,12,'9:12','و اگر سوگند (و پیمان‌های) خود را پس از عهد (و پیمان) خویش شکستند، و در دین شما طعن زدند، پس با پیشوایان کفر جنگ کنید، زیرا آن‌ها عهد (و پیمانی) ندارند، باشد که (از کردار خود) دست بر دارند.');
INSERT INTO "hussein_taji" VALUES(9,13,'9:13','آیا با گروهی که پیمان‌های خود را شکستند، و قصد اخراج پیامبر کردند، نمی‌جنگید؟! درحالی‌که آن‌ها نخستین بار (جنگ با شما را) آغاز کردند، آیا از آن‌ها می‌ترسید؟! پس الله سزاوارتر است که از او بترسید، اگر ایمان دارید.');
INSERT INTO "hussein_taji" VALUES(9,14,'9:14','با آن‌ها بجنگید، تا الله با دستان شما عذاب‌شان کند، و آنان را رسوا سازد، و شما را بر آن‌ها پیروزی دهد، و سینۀ گروه مؤمنان را شفا می‌بخشد.');
INSERT INTO "hussein_taji" VALUES(9,15,'9:15','و خشم دل‌های شان را از میان می‌برد، و الله توبۀ هرکس را بخواهد می‌پذیرد، و الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(9,16,'9:16','آیا گمان کردید که (به حال خود) رها می‌شوید؛ در حالی‌که الله هنوز کسانی را که از شما جهاد کردند، و غیر از الله و رسولش و مؤمنان را همراز (خویش) نگرفته‌اند (از دیگران) مشخص نکرده است؟! و الله به آنچه انجام می‌دهید؛ آگاه است.');
INSERT INTO "hussein_taji" VALUES(9,17,'9:17','مشرکان را (سزاوار) نیست، که مساجد الله را آباد کنند، در حالی‌که به کفر خویش گواهی می‌دهند، آن‌ها اعمال‌شان نابود (و حبط) شده، و در آتش (جهنم) جاودانه خواهند ماند.');
INSERT INTO "hussein_taji" VALUES(9,18,'9:18','مساجد الله را فقط کسی آباد می‌کند که به الله و روز آخرت ایمان آورده، و نماز را بر پا دارد، و زکات را بدهد، و جز از الله نترسد، امید است که اینان از هدایت‌یافتگان باشند.');
INSERT INTO "hussein_taji" VALUES(9,19,'9:19','آیا آب دادن به حجاج، و آباد ساختن مسجد الحرام را، همچون (عمل) کسی‌که به الله و روز قیامت ایمان آورده، و در راه الله جهاد کرده است، برابر می‌گیرید؟! (خیر، آن‌ها) نزد الله برابر نیستند، و الله گروه ستمکاران را هدایت نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(9,20,'9:20','(مقام) کسانی‌که ایمان آوردند، و هجرت کردند، و با اموال‌شان و جان‌هایشان در راه الله جهاد کردند، نزد الله برتر (و بلندمرتبه‌تر) است، و آن‌ها همان رستگارانند.');
INSERT INTO "hussein_taji" VALUES(9,21,'9:21','پروردگارشان آن‌ها را به رحمتی از نزد خود و خشنودی (خویش) و به باغ‌هایی (از بهشت) که درآن نعمت‌های جاویدانه دارند، بشارت می‌دهد.');
INSERT INTO "hussein_taji" VALUES(9,22,'9:22','همواره در آن جاودانند، بی‌گمان اجر (و پاداش) بزرگ نزد الله است.');
INSERT INTO "hussein_taji" VALUES(9,23,'9:23','ای کسانی‌که ایمان آورده‌اید! پدران خود و برادران خود را دوستان (و اولیاء) خود قرار ندهید، اگر کفر را بر ایمان ترجیح دادند، و کسانی از شما که آنان را دوست (و ولی) خود قرار دهند، پس آنان ستمکارانند.');
INSERT INTO "hussein_taji" VALUES(9,24,'9:24','بگو: «اگر پدران‌تان، و فرزندان‌تان، و برادران‌تان و همسران‌تان، و خویشاوندان‌تان، و اموالی که به دست آورده‌اید، و تجارتی که از کساد آن می‌ترسید، و خانه‌هایی که بدان دلخوش هستید، در نزد شما از الله و پیامبرش و جهاد در راه او محبوب‌تر است، پس منتظر باشید، تا الله فرمان (عذاب) خویش را بیاورد، و الله گروه نافرمان را هدایت نمی‌کند».');
INSERT INTO "hussein_taji" VALUES(9,25,'9:25','به راستی الله شما را در جاهای زیادی یاری کرد، (و پیروزی بخشید) و در روز حنین (نیز یاری کرد) آنگاه که فزونی جمعیت‌تان شما را شگفت آورده بود (و مغرور ساخت) پس سودی به حال‌تان نداشت، و زمین با همۀ فراخی و وسعتش بر شما تنگ آمد، و سپس پشت (به میدان جنگ) نموده، فرار کردید.');
INSERT INTO "hussein_taji" VALUES(9,26,'9:26','آنگاه الله آرامش خود را بر پیامبرش و بر مؤمنان نازل کرد، و لشکریانی فرستاد که شما آن‌ها را نمی‌دیدید، و کسانی را که کافر شدند عذاب (و مجازات) کرد، و این است کیفر کافران.');
INSERT INTO "hussein_taji" VALUES(9,27,'9:27','سپس الله بعد از آن توبۀ هر کس را بخواهد می‌پذیرد، و الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(9,28,'9:28','ای کسانی‌که ایمان آورده‌اید! بی‌گمان مشرکان پلیدند، پس نباید بعد از اِمسال، به مسجد الحرام نزدیک شوند، و اگر از فقر (و تنگدستی) می‌ترسید، پس هرگاه الله بخواهد شما را از فضل خود بی‌نیازتان می‌سازد، بی‌گمان الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(9,29,'9:29','با کسانی از اهل کتاب که نه به الله و نه به روز قیامت ایمان دارند، و نه آنچه را الله و رسولش حرام کرده، حرام می‌دانند، و نه دین حق را می‌پذیرند، بجنگید، تا زمانی که با ذلت و خواری به دست خویش جزیه بدهند.');
INSERT INTO "hussein_taji" VALUES(9,30,'9:30','و یهود گفتند: «عزیر پسر الله است» و نصار گفتند: «مسیح پسر الله است». این سخنی است که با زبان‌هایشان می‌گویند، مشابهت (و تقلید) می‌کنند سخن کسانی‌که پیش از این کافر بودند، الله آن‌ها را بکشد، چگونه (از حق) منحرف می‌شوند؟!');
INSERT INTO "hussein_taji" VALUES(9,31,'9:31','(آن‌ها) دانشمندان و رهبان خویش، و (همچنین) مسیح پسر مریم را معبودانی بجای الله گرفتند؛ در حالی‌که دستور نداشتند جز الله یکتایی را که هیچ معبودی (به حق) جز او نیست؛ بپرستند، او پاک و منزه است از آنچه شریک او قرار می‌دهند.');
INSERT INTO "hussein_taji" VALUES(9,32,'9:32','(آن‌ها) می‌خواهند نور الله را با دهان خود خاموش کنند، و الله ابا می‌کند، مگر اینکه نورش را کامل (و تمام) کند، اگر چه کافران کراهت داشته باشند.');
INSERT INTO "hussein_taji" VALUES(9,33,'9:33','او کسی است که رسول خود را با هدایت و دین حق فرستاد، تا آن را بر همۀ دین‌ها غالب گرداند، اگر چه مشرکان کراهت داشته باشند.');
INSERT INTO "hussein_taji" VALUES(9,34,'9:34','ای کسانی‌که ایمان آورده‌اید! بی‌گمان بسیاری از احبار و رهبان اموال مردم را به باطل می‌خورند، و (دیگران را) از راه الله باز می‌دارند، و کسانی‌که طلا و نقره را می‌اندوزند (و گنجینه می‌کنند) و در راه الله انفاق نمی‌کنند، پس آن‌ها را به عذابی دردناک بشارت ده.');
INSERT INTO "hussein_taji" VALUES(9,35,'9:35','روزی‌که آن را در آتش جهنم گرم (و تفته‌اش) کنند، پس با آن پیشانی‌هایشان و پهلوهای‌شان، و پشت‌هایشان را داغ کنند، (و به آن‌ها گفته شود) این همان چیزی است که برای خود اندوختید، پس بچشید (طعم) آنچه را می‌اندوختید.');
INSERT INTO "hussein_taji" VALUES(9,36,'9:36','بی‌گمان تعداد ماه‌ها نزد الله در کتاب خدا، از آن روز که آسمان‌ها و زمین را آفریده، دوازده ماه است، که چهار ماه از آن، (ماه) حرام است [ ماه‌های حرام عبارتند از: ذی القعده، ذی الحجه، محرم، و رجب؛ که جنگ در آن‌ها حرام است.]. این، دین ثابت و درست (الهی) است. پس در این ماه‌ها به خود ستم نکنید، و دسته‌جمعی با مشرکان بجنگید، همان گونه که آن‌ها دسته‌جمعی با شما می‌جنگند، و بدانید که الله با پرهیزگاران است.');
INSERT INTO "hussein_taji" VALUES(9,37,'9:37','همانا به تأخیر افکندن (و جابجایی ماه‌های حرام) افزونی در کفر است، کسانی‌که کافر شدند، با آن گمراه می‌شوند، یک سال؛ آن را حلال، و یک سال (دیگر) آن را حرام می‌کنند، تا مطابق با تعداد ماه‌هایی شود که الله تحریم کرده است، پس آنچه را که الله حرام کرده، حلال می‌شمارند، اعمال زشت‌شان در نظرشان آراسته شده است، و الله گروه کافران را هدایت نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(9,38,'9:38','ای کسانی‌که ایمان آورده‌اید! شما را چه شده، هنگامی‌که به شما گفته می‌شود: «(به سوی جهاد) در راه الله حرکت کنید» به زمین می‌چسبید (و سستی می‌کنید)) آیا به جای آخرت به زندگی دنیا راضی شده‌اید؟! حال آنکه متاع زندگی دنیا، در (برابر) آخرت، جز اندکی (هیچ) نیست.');
INSERT INTO "hussein_taji" VALUES(9,39,'9:39','اگر (برای جهاد) کوچ نکنید، (الله) شما را به عذاب دردناکی، عذاب می‌کند، و قوم دیگری را بجای شما قرار می‌دهد، و هیچ زیانی به او نمی‌رسانید، و الله بر هر چیزی تواناست.');
INSERT INTO "hussein_taji" VALUES(9,40,'9:40','اگر او (= پیامبر) را یاری نکنید، به راستی که الله او را یاری کرد، آنگاه که کافران او را (از مکه) بیرون کردند، نفر دوم از دو نفری که آن دو در غار بودند، به یار خود (ابوبکر) می‌گفت: «اندوهگین مباش، یقیناً الله با ماست». پس الله آرامش خود را بر او فرو فرستاد، و با لشکرهایی که شما آن را نمی‌دیدید، او را تأیید (و تقویت) نمود، و کلام (و گفتار شرک) کسانی را که کافر شدند؛ پایین قرار داد، و کلام (و آیین) الله بالا (و پیروز) است، و الله پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(9,41,'9:41','سبکبار و گرانبار (همگی برای جهاد) کوچ کنید، و با اموال‌تان، و جان‌هایتان در راه الله جهاد کنید، این برای شما بهتراست، اگر بدانید.');
INSERT INTO "hussein_taji" VALUES(9,42,'9:42','اگر متاعی نزدیک (و دست یافتنی) بود، و سفری آسان (و نزدیک در پیش) بود، البته از تو پیروی می‌کردند، و لیکن مسافت راه (تبوک) بر آن‌ها دور (و پر مشقت) است، و به زودی به الله سوگند یاد می‌کنند که: «اگر توانایی داشتیم، البته همراه شما بیرون می‌شدیم» (آن‌ها با این کارها) خود را هلاک می‌کنند، و الله می‌داند که آن‌ها دروغگو هستند.');
INSERT INTO "hussein_taji" VALUES(9,43,'9:43','(ای پیامبر!) الله تو را ببخشاید! چرا به آن‌ها اجازه دادی؟! پیش از آنکه راستگویان بر تو آشکار شوند، و دروغگویان را بشناسی!.');
INSERT INTO "hussein_taji" VALUES(9,44,'9:44','کسانی‌که به الله و روز آخرت ایمان دارند، هیچ‌گاه برای (ترک) جهاد با اموال شان و جان‌هایشان، از تو اجازه نمی‌خواهند، و الله نسبت به پرهیزگاران داناست.');
INSERT INTO "hussein_taji" VALUES(9,45,'9:45','تنها کسانی از تو اجازه می‌خواهند؛ که به الله و روز آخرت ایمان ندارند، و دل‌های شان گرفتار شک است، و آن‌ها در شک (و تردید) خود سر گردانند.');
INSERT INTO "hussein_taji" VALUES(9,46,'9:46','و اگر آن‌ها می‌خواستند که (همراه شما) بیرون شوند، ساز و برگی برای آن آماده می‌کردند، و لیکن الله از حرکت آن‌ها کراهت داشت، لذا آن‌ها را (از حرکت) بازداشت، و به آنان گفته شد: «با نشستگان بنشینید».');
INSERT INTO "hussein_taji" VALUES(9,47,'9:47','اگر (آن‌ها) همراه شما بیرون می‌آمدند، جز فساد (و تباهی) چیزی بر شما نمی‌افزودند، و به سرعت در میان شما فتنه‌انگیزی می‌کردند، و در میان شما افرادی (ضعیف الایمان) هستند که به (سخنان) آن‌ها گوش فرا می‌دهند، و الله، (نسبت) به ستمکاران داناست.');
INSERT INTO "hussein_taji" VALUES(9,48,'9:48','به راستی که آن‌ها پیش از این (نیز) فتنه‌گری می‌کردند، و کارها را برای تو دگرگون کردند، تا آنکه حق فرا رسید، و فرمان الله آشکار شد، (و شما پیروز شدید) در حالی‌که آن‌ها کراهت داشتند.');
INSERT INTO "hussein_taji" VALUES(9,49,'9:49','بعضی از آن‌ها می‌گویند: «(در ترک جهاد) به من اجازه بده، و مرا به فتنه (و گناه) نیفکن». آگاه باشید که آن‌ها در فتنه افتاده‌اند، و بی‌گمان جهنم کافران را احاطه کرده است.');
INSERT INTO "hussein_taji" VALUES(9,50,'9:50','اگر به تو نیکی برسد، آن‌ها را ناراحت می‌کند، و اگر مصیبتی به تو رسد، می‌گویند: «ما پیش از این چارۀ کار خود را اندیشیده‌ایم» و شادمانه باز می‌گردند.');
INSERT INTO "hussein_taji" VALUES(9,51,'9:51','بگو: «هرگز (مصیبتی) به ما نرسد، جز آنچه الله برای ما نوشته است، او مولای ماست، و مؤمنان باید بر الله توکل کنند».');
INSERT INTO "hussein_taji" VALUES(9,52,'9:52','بگو: «آیا برای ما جز یکی از دو نیکی را انتظار دارید؟! (یا پیروزی یا شهادت) و ما برای شما انتظار داریم که الله، عذابی از نزد خودش به شما برساند، یا بدست ما (شما را مجازات کند) پس شما انتظار بکشید، بی‌گمان ما (نیز) با شما انتظار می‌کشیم».');
INSERT INTO "hussein_taji" VALUES(9,53,'9:53','بگو: «انفاق کنید، چه از روی میل باشد یا به اکراه، هرگز از شما پذیرفته نخواهد شد، زیرا شما مردمی فاسق (و نافرمان) هستید».');
INSERT INTO "hussein_taji" VALUES(9,54,'9:54','و هیچ چیز مانع قبول انفاق‌های آن‌ها نشد، مگر آنکه به الله و پیامبرش کافر شدند، و جز با کسالت (و بی میلی) برای نماز حاضر نمی‌شوند، و جز با کراهت انفاق نمی‌کنند.');
INSERT INTO "hussein_taji" VALUES(9,55,'9:55','پس (فزونی) اموال‌شان و اولاد‌شان، تو را شگفت‌زده نکند، زیرا الله می‌خواهد به (وسیلۀ) آن آن‌ها را در زندگی دنیا عذاب کند، و درحالی‌که کافرند جان‌شان بر آید.');
INSERT INTO "hussein_taji" VALUES(9,56,'9:56','و آن‌ها (= منافقان) به الله سوگند یاد می‌کنند که از شما هستند، درحالی‌که از شما نیستند، و لیکن آن‌ها گروهی‌اند که می‌ترسند، (بدین سبب سوگند می‌خورند).');
INSERT INTO "hussein_taji" VALUES(9,57,'9:57','اگر پناه‌گاه یا غارها یا تونلی (در زیر زمین) بیابند، شتابان به سوی آن می‌گریزند.');
INSERT INTO "hussein_taji" VALUES(9,58,'9:58','و از آن‌ها کسانی هستند که در (تقسیم) صدقات (و غنایم) به تو خرده می‌گیرند، پس اگر از آن (اموال، بهره‌ای) به آن‌ها داده شود، راضی می‌شوند، و اگر از آن (چیزی) داده نشود، فوراً خشم می‌گیرند.');
INSERT INTO "hussein_taji" VALUES(9,59,'9:59','اگر به آنچه الله و رسولش به آن‌ها داده است، راضی باشند، و بگویند: «الله برای ما کافی است، به زودی الله و رسولش از فضل خود به ما عطا می‌فرماید، ما تنها به سوی الله رغبت می‌وزریم». (برای آن‌ها بهتر بود).');
INSERT INTO "hussein_taji" VALUES(9,60,'9:60','صدقات (و زکات‌ها) مخصوص فقراء و مساکین و کار گزاران (جمع آوری) آن، و دلجویی شدگان (نو مسلمانان) و برای (آزاد کردن) بردگان، و (ادای وام) بدهکاران، و در راه الله، و به راه ماندگان، (است، این) فریضه‌ای (مقرر شده) از جانب الله است، و الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(9,61,'9:61','و از آن‌ها (= منافقان) کسانی هستند که پیامبر را آزار می‌دهند، و می‌گویند: «گوش (و خوش باور) است». بگو: «برای شما گوش نیکی است، به الله ایمان دارد، و مؤمنان را تصدیق می‌کند، و رحمت است برای کسانی از شما که ایمان آورده‌اند». و کسانی‌که رسول الله را آزار می‌دهند، برایشان عذاب دردناکی است.');
INSERT INTO "hussein_taji" VALUES(9,62,'9:62','(منافقان) برای شما به الله سوگند یاد می‌کنند، تا شما را خشنود سازند، در حالی‌که اگر ایمان داشتند، شایسته‌تر (این) است که الله و رسولش را خشنود سازند.');
INSERT INTO "hussein_taji" VALUES(9,63,'9:63','آیا نمی‌دانند، بی‌گمان هر کس با الله و رسولش مخالفت (و دشمنی) کند، پس مسلماً برای او آتش جهنم است، جاودانه در آن خواهد بود؟! این (همان) رسوایی بزرگ است.');
INSERT INTO "hussein_taji" VALUES(9,64,'9:64','منافقان می‌ترسند که مبادا سوره‌ای بر (علیه) آنان نازل شود، و به آن‌ها از آنچه درون دل‌های شان (نهفته) است، آگاه سازد، بگو: «استهزا کنید، بی‌گمان الله، آنچه را که از آن می‌ترسید، آشکار می‌سازد».');
INSERT INTO "hussein_taji" VALUES(9,65,'9:65','و (ای پیامبر!) اگر از آن‌ها بپرسی: («چرا چنین کرده‌اید؟»). گویند: «ما حرف می‌زدیم، (شوخی و) بازی می‌کردیم» بگو: «آیا الله و آیات او و پیامبرش را مسخره می‌کردید؟».');
INSERT INTO "hussein_taji" VALUES(9,66,'9:66','عذر نیاورید، به راستی که شما پس از ایمان (آوردن) تان کافر شده‌اید، اگر گروهی از شما را (بخاطر توبه) ببخشیم، گروه دیگری را عذاب خواهیم کرد، زیرا که مجرم بودند.');
INSERT INTO "hussein_taji" VALUES(9,67,'9:67','مردان منافق و زنان منافق، همه (همانند هم و) از یک گروه‌اند، به کارهای زشت فرمان می‌دهند، و از نیکی‌ها باز می‌دارند، و دست‌هایشان را (از بخشش) می‌بندند، الله را فراموش کردند، پس الله (نیز) آن‌ها را فراموش کرد، بی‌گمان منافقان همان فاسقان هستند.');
INSERT INTO "hussein_taji" VALUES(9,68,'9:68','الله به مردان منافق و زنان منافق و کفار، وعدۀ آتش جهنم داده است، جاودانه در آن خواهند ماند، همان برای آن‌ها کافی است، و الله آن‌ها را لعنت کرده است، و برای آن‌ها عذاب همیشگی است.');
INSERT INTO "hussein_taji" VALUES(9,69,'9:69','(شما منافقان) همانند کسانی‌که پیش از شما بودند، (هستید، بلکه) آن‌ها از شما نیرومندتر، و اموال و فرزندان‌شان بیشتر بود، آن‌ها از نصیب خود بهره‌مند شدند، پس شما نیز از نصیب خود بهره‌مند شده‌اید، همچنان‌که کسانی‌که پیش از شما بوده‌اند، از نصیب خویش بهره‌مند شده بودند، و شما (در سخنان ناپسند) فرو رفتید، همان گونه که آن‌ها فرو رفتند؛ آنان اعمال‌شان در دنیا و آخرت نابود شد، و آنان همان زیانکارانند.');
INSERT INTO "hussein_taji" VALUES(9,70,'9:70','آیا خبر کسانی‌که پیش از آن‌ها بودند، (چون) قوم نوح، و عاد و ثمود، و قوم ابراهیم، و اصحاب مدین (= قوم شعیب) و شهرهای زیر و رو شده [ مرا از اهل مؤتفکات قوم لوط است که شهرشان به نام «سدوم» بود. ائتکاف یعنی زیر و روشدن، به طرزی که در ابتدا از آسمان سنگباران گشتند سپس شهرشان به بالا برده شد و از همانجا به پایین افکنده شد.] (قوم لوط) به آن‌ها نرسیده است، که پیامبران‌شان با دلایل روشن به (سوی) آن‌ها آمدند، (ولی نپذیرفتند) پس الله (با هلاک‌شان) به آن‌ها ستم نکرد، و لیکن خودشان بر خویشتن ستم می‌کردند.');
INSERT INTO "hussein_taji" VALUES(9,71,'9:71','و مردان مؤمن و زنان مؤمن دوستان (و یاور) یکدیگرند، امر به معروف و نهی از منکر می‌کنند، و نماز را بر پا می‌دارند، و زکات را می‌پردازند، و الله و پیامبرش را اطاعت می‌کنند، اینانند که الله به زودی آن‌ها را مورد رحمت قرار می‌دهد، بی‌گمان الله پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(9,72,'9:72','الله به مردان مؤمن و زنان مؤمن، باغ‌هایی (از بهشت) وعده داده که نهرها از زیر (درختان) آن جاری است، جاودانه در آن خواهند ماند، و (نیز) خانه‌های پاکیزه‌ای در بهشت‌های جاوید (نصیب شان فرموده) و خشنودی الله (از همۀ این‌ها) برتر است، این، همان کامیابی بزرگ است.');
INSERT INTO "hussein_taji" VALUES(9,73,'9:73','ای پیامبر! با کافران و منافقان جهاد کن، و برآنان سخت گیر باش، و جایگاه‌شان جهنم است، و چه بد سرانجامی است.');
INSERT INTO "hussein_taji" VALUES(9,74,'9:74','(منافقان) به الله سوگند یاد می‌کنند که (سخنان نادرست) نگفته‌اند، و به راستی که سخن کفر گفته‌اند، و پس از اسلام آوردن‌شان، کافر شده‌اند، و قصد چیزی (خطرناک) کردند، که به آن نرسیدند، تنها به این سبب عیب‌جوئی می‌کنند که الله و رسولش، آن‌ها را از فضل خود بی‌نیاز کرد، پس اگر توبه کنند، برای آن‌ها بهتر است، و اگر روی بگردانند؛ الله آن‌ها را در دنیا و آخرت، با عذابی دردناک عذاب‌شان می‌کند، و در (روی) زمین هیچ کارساز و یاوری نخواهند داشت.');
INSERT INTO "hussein_taji" VALUES(9,75,'9:75','بعضی از آن‌ها با الله (عهد و) پیمان بستند که: اگر (الله) از فضل خود (نصیبی) به ما دهد، قطعاً صدقه (و زکات) خواهیم داد، و از نیکوکاران خواهیم بود.');
INSERT INTO "hussein_taji" VALUES(9,76,'9:76','پس چون (الله) از فضل خود به آن‌ها (نصیبی) بخشید، به آن بخل ورزیدند، و اعراض‌کنان روی گرداندند (و سر پیچی کردند).');
INSERT INTO "hussein_taji" VALUES(9,77,'9:77','پس (این عمل) نفاق را تا روزی‌که او (= الله) را ملاقات کنند، در دل‌های شان بر قرار ساخت، به (سبب) آنچه که با الله وعده کرده بودند، خلاف نمودند، (و عهد شکنی کردند) و به (سبب) آنکه دروغ می‌گفتند.');
INSERT INTO "hussein_taji" VALUES(9,78,'9:78','آیا نمی‌دانستند که الله اسرارشان، و (سخنان) در گوشی شان را می‌داند، و بی‌گمان الله دانای غیب‌ها است.');
INSERT INTO "hussein_taji" VALUES(9,79,'9:79','کسانی‌که از مؤمنان رغبت‌کننده (و فرمانبردار) در صدقات (شان) عیب‌جویی می‌کنند، و کسانی را که جز به‌اندازۀ (ناچیز) توانایی خود (چیزی) نمی‌یابند، پس (عیب‌جویی و) مسخره شان می‌کنند، الله آن‌ها را مسخره می‌کند، و برای آن‌ها عذاب دردناکی است.');
INSERT INTO "hussein_taji" VALUES(9,80,'9:80','(ای پیامبر) برای آن‌ها آمرزش بخواه یا برایشان آمرزش نخواه، اگر هفتاد بار برای آن‌ها آمرزش بخواهی، هرگز الله آن‌ها را نمی‌آمرزد، این بدان سبب است که آن‌ها به الله و پیامبرش کافر شدند، و الله گروه فاسقان را هدایت نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(9,81,'9:81','واپس‌ماندگان (از غزوۀ تبوک) از نشستن خود بعد از (مخالفت با) رسول الله، خوشحال شدند، و کراهت داشتند که با اموال خود، و جان‌های خود، در راه الله جهاد کنند، و (به یکدیگر) گفتند: «در این گرما، (برای جهاد) بیرون نروید»، (ای پیامبر! به آنان) بگو: «آتش جهنم (از این) گرم‌تراست» اگر در می‌یافتند.');
INSERT INTO "hussein_taji" VALUES(9,82,'9:82','پس آن‌ها باید کم‌تر بخندند، و بسیار گریه کنند، (این) جزای آنچه که انجام می‌دادند.');
INSERT INTO "hussein_taji" VALUES(9,83,'9:83','پس اگر الله تو را به سوی گروهی از آن‌ها باز گرداند، و از تو اجازۀ خروج (برای جهاد) خواستند، پس بگو: «هرگز با من خارج نخواهید شد، و هرگز همراه من، با هیچ دشمنی نخواهید جنگید، زیرا شما نخستین بار به نشستن (و کناره گیری) راضی شدید، پس با پس‌ماندگان بمانید».');
INSERT INTO "hussein_taji" VALUES(9,84,'9:84','و هرگز بر هیچ یک از آن‌ها که مرده است، نماز نخوان، و بر (کنار) قبرش نایست، بی‌گمان آن‌ها به الله و رسولش کافر شدند، و در حالی‌که فاسق (و نافرمان) بودند؛ مردند.');
INSERT INTO "hussein_taji" VALUES(9,85,'9:85','و (فزونی) اموال‌شان و اولادشان، تو را شگفت‌زده نکند، زیرا الله می‌خواهد به (وسیلۀ) آن، آن‌ها را در زندگی دنیا عذاب کند، و در حالی‌که کافراند؛ جان‌شان برآید.');
INSERT INTO "hussein_taji" VALUES(9,86,'9:86','و هنگامی‌که سوره‌ای نازل شود که به الله ایمان آورید، و همراه پیامبرش جهاد کنید، توانگران‌شان از تو اجازه می‌خواهند، و گویند: «بگذار ما با خانه‌نشینان (که معذورند) باشیم».');
INSERT INTO "hussein_taji" VALUES(9,87,'9:87','آن‌ها راضی شدند که با (زنان و) بازماندگان باشند، و بر دل‌هایشان مُهر زده شده، پس آنان (چیزی) نمی‌فهمند.');
INSERT INTO "hussein_taji" VALUES(9,88,'9:88','لیکن پیامبر و کسانی‌که با او ایمان آوردند، با اموال خود و جان‌های خود (در راه الله) جهاد کردند، نیکی‌ها (همه) از آنِ آن‌هاست، و آنانند که رستگارانند.');
INSERT INTO "hussein_taji" VALUES(9,89,'9:89','الله برای آن‌ها باغ‌هایی (از بهشت) آماده کرده است، که نهرها از زیر (درختان) آن جاری است، جاودانه در آن خواهند ماند، این کامیابی بزرگ است.');
INSERT INTO "hussein_taji" VALUES(9,90,'9:90','و عذرآورندگان از اعراب (بادیه‌نشین، نزد تو) آمدند، تا به آن‌ها اجازه داده شود، و کسانی‌که به الله و رسولش دروغ گفته بودند (در خانه) نشستند، به زودی به کسانی از آن‌ها که کافر شدند، عذاب دردناکی خواهد رسید.');
INSERT INTO "hussein_taji" VALUES(9,91,'9:91','بر ناتوانان گناهی نیست و نه بر بیماران، و نه بر کسانی‌که چیزی نمی‌یابند، خرج کنند، (گناهی است). هرگاه برای الله و پیامبرش خیر خواهی کنند، بر نیکو کاران هیچ راه (سرزنش) نیست، و الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(9,92,'9:92','و نه بر کسانی‌که وقتی نزد تو آمدند که آن‌ها را (بر مرکبی) سوار کنی، گفتی: «چیزی که شما را برآن سوار کنم، نمی‌یابم» آن‌ها بازگشتند، در حالی‌که از اندوه چشمان‌شان اشک ریزان بود، چرا که هیچ چیزی برای انفاق (در راه الله) نیافتند.');
INSERT INTO "hussein_taji" VALUES(9,93,'9:93','تنها راه (سرزنش) بر کسانی (باز) است که از تو اجازه می‌خواهند، در حالی‌که توانگراند، (آن‌ها) راضی شدند که با (زنان و) بازماندگان باشند، و الله بر دل‌های شان مهر نهاده است، پس آنان (چیزی) نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(9,94,'9:94','هنگامی‌که به سوی آن‌ها باز گردید، از شما عذرخواهی می‌کنند، بگو: «عذرخواهی نکنید؛ ما هرگز (گفتار) شما را باور نخواهیم کرد، به راستی‌که الله ما را از اخبار شما آگاه کرده است، و به زودی الله و رسولش، اعمال شما را خواهند دید، آنگاه به سوی (الله) دانای پنهان و آشکار باز گردانده می‌شوید؛ پس شما را به آنچه انجام می‌دادید؛ آگاه می‌کند»');
INSERT INTO "hussein_taji" VALUES(9,95,'9:95','هنگامی‌که به سوی آنان باز گردید، برای شما به الله سوگند یاد می‌کنند، تا از آن‌ها چشم پوشی (و اعراض) کنید، پس از آن‌ها روی بگرانید (و اعراض کنید) بی‌گمان آن‌ها (مردمی) پلیدند، و جایگاه‌شان جهنم است، به کیفر آنچه که انجام می‌دادند.');
INSERT INTO "hussein_taji" VALUES(9,96,'9:96','برای شما سوگند یاد می‌کنند، تا از آن‌ها راضی شوید، اگر شما از آن‌ها راضی شوید، پس بی‌تردید الله از گروه فاسقان راضی نخواهد شد.');
INSERT INTO "hussein_taji" VALUES(9,97,'9:97','کفر و نفاق اعراب (بادیه‌نشین)، شدیدتر است، و به نا آگاهی از حدود (و احکامی) که الله بر پیامبرش نازل کرده، سزاوارترند، و الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(9,98,'9:98','از اعراب (بادیه‌نشین) کسانی‌اند که آنچه را (در راه الله) انفاق می‌کنند، غرامت به حساب می‌آورند، و منتظرند حوادث (و پیشامد ناگواری) به شما برسد، حوادث بد (و ناگوار) بر خودشان باد، و الله شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(9,99,'9:99','و از اعراب (بادیه‌نشین) کسانی اند که به الله و روز آخرت ایمان دارند، و آنچه را (در راه الله) انفاق می‌کنند، سبب تقرب به الله، و دعای پیامبر می‌دانند، آگاه باشید، بی‌گمان این‌ها سبب تقرب‌شان خواهد بود، و الله به زودی آن‌ها را در رحمت خود وارد خواهد کرد، بی‌گمان الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(9,100,'9:100','و پیشگامان نخستین از مهاجرین و انصار، و کسانی‌که به نیکی از آن‌ها پیروی کردند، الله از آن‌ها خشنود گشت، و آن‌ها (نیز) از او خشنود شدند، و برای آن‌ها باغ‌هایی (از بهشت) آماده کرده است، که نهرها از زیر (درختان) آن جاری است، جاودانه در آن خواهند ماند، این کامیابی بزرگ است.');
INSERT INTO "hussein_taji" VALUES(9,101,'9:101','و از (میان) اعراب (بادیه‌نشین) که پیرامون شما هستند، گروهی منافقند، و از اهل مدینه (نیز) گروهی خوی نفاق گرفته‌اند، تو آن‌ها را نمی‌شناسی، (ولی) ما آن‌ها را می‌شناسیم، به زودی آن‌ها را دو بار عذاب خواهیم کرد، سپس به سوی عذابی بزرگ (در قیامت) فرستاده می‌شوند.');
INSERT INTO "hussein_taji" VALUES(9,102,'9:102','و گروه دیگری (نیز) هستند که به گناهان خود اعتراف کردند، کارهای شایسته و ناشایسته را به هم آمیختند، امید است که الله توبۀ آن را بپذیرد، بی‌گمان الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(9,103,'9:103','از اموال آن‌ها صدقه (و زکات) بگیر، تا بوسیلۀ آن آن‌ها را پاک سازی و تزکیه شان کنی، و برایشان دعا کن، یقیناً دعای تو مایۀ آرامش برای آن‌هاست، و الله شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(9,104,'9:104','آیا نمی‌دانند، که تنها الله توبه را از بندگانش می‌پذیرد، و صدقات را می‌گیرد، و بی‌گمان الله است که توبه‌پذیر مهربان است.');
INSERT INTO "hussein_taji" VALUES(9,105,'9:105','و بگو: «عمل کنید، پس بزودی الله، و پیامبرش و مؤمنان اعمال شما را خواهند دید، و به زودی به سوی (الله) دانای پنهان و آشکار، باز گردانده می‌شوید، پس شما را به آنچه انجام می‌دادید؛ آگاه می‌کند»');
INSERT INTO "hussein_taji" VALUES(9,106,'9:106','و گروه دیگری (نیز) موقوف به فرمان (و مشیت) الله است، یا آن‌ها را عذاب می‌کند، و یا توبۀ آن‌ها را می‌پذیرد، و الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(9,107,'9:107','و کسانی‌که برای زیان رسانیدن (به مسلمانان) و (تقویت) کفر و تفرقه‌افکنی میان مؤمنان، مسجدی ساختند، و (تا) کمینگاهی برای کسانی‌که از پیش با الله و رسولش مبارزه کرده‌اند باشد، و البته آن‌ها سوگند یاد می‌کنند که قصدی جز نیکی (و خدمت) نداشته‌ایم و الله گواهی می‌دهد که یقیناً آن‌ها دروغگو هستند.');
INSERT INTO "hussein_taji" VALUES(9,108,'9:108','(ای پیامبر!) هرگز در آن مسجد (برای نماز) نایست، آن مسجد که از روز نخست بر پایه تقوا بنا شده، شایسته‌تر است که در آن (به نماز) بایستی، در آن مردانی هستند که دوست دارند که پاکیزه شوند، و الله پاکیزگان را دوست دارد.');
INSERT INTO "hussein_taji" VALUES(9,109,'9:109','آیا کسی‌که اساس (و بنیان) آن را بر ترس از الله و خشنودی (او) آن را بنا کرده بهتر است، یا کسی‌که اساس (و بنیان) آن برکنار پرتگاه سستی بنا نهاده است، پس (ناگهان) با او در آتش جهنم فرو ریزد؟ و الله گروه ستمکاران را هدایت نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(9,110,'9:110','آن بنای که آن‌ها نهاده‌اند، پیوسته در دل‌های شان مایۀ شک (و نفاق) خواهد بود، مگر اینکه دل‌های شان پاره پاره شود (و بمیرند) و الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(9,111,'9:111','بی‌گمان الله از مؤمنان جان‌هایشان و اموال‌شان را خریده است، به (بهاى) اینکه بهشت برای آن‌ها باشد. (بدین صورت که): در راه الله جنگ می‌کنند، پس می‌کشند، و کشته می‌شوند، (این) وعدۀ حقی است بر او، (که) در تورات و انجیل و قرآن (آمده است) و چه کسی از الله به عهدش وفادارتر است؟! پس شاد باشید، به داد و ستدی که شما با او کرده‌اید، و این همان کامیابی بزرگ است.');
INSERT INTO "hussein_taji" VALUES(9,112,'9:112','(آن‌ها) توبه‌کنندگان، عابدان، سپاس‌گزاران، روزه‌داران، رکوع‌کنندگان، سجده‌کنندگان، فرمان‌دهندگان به معروف و بازدارندگان از منکر، و حافظان (و نگه‌دارندگان) حدود (و احکام) الهی هستند، و مؤمنان را بشارت ده.');
INSERT INTO "hussein_taji" VALUES(9,113,'9:113','برای پیامبر و کسانی‌که ایمان آوردند (شایسته) نبود که برای مشرکان طلب آمرزش کنند، - هر چند از نزدیکان‌شان باشند- بعد از آنکه برای آن‌ها روشن شد که آن‌ها اهل دوزخند.');
INSERT INTO "hussein_taji" VALUES(9,114,'9:114','و آمرزش خواستن ابراهیم برای پدرش نبود؛ مگر به خاطر وعده‌ای که به او داده بود، پس چون برای او آشکار شد که او دشمن الله است؛ از او بیزاری جست، بی‌گمان ابراهیم لابه کنندۀ بردبار بود.');
INSERT INTO "hussein_taji" VALUES(9,115,'9:115','و هرگز الله قومی را، بعد ازآنکه آن‌ها را هدایت کرد گمراه نمی‌کند، تا آنکه چیزهای را که باید از آن بپرهیزند، برای آن‌ها بیان کند، بی‌گمان الله به هر چیزی داناست.');
INSERT INTO "hussein_taji" VALUES(9,116,'9:116','یقیناً فرمانروایی آسمان‌ها و زمین از آن الله است، زنده می‌کند و می‌میراند، و شما را جز الله کارساز و یاوری نیست.');
INSERT INTO "hussein_taji" VALUES(9,117,'9:117','قطعاً الله رحمت خود را شامل حال پیامبر و مهاجران و انصار، آنان که در زمان سختی (و تنگدستی) از او پیروی کردند؛ نمود، بعد ازآنکه نزدیک بود، دل های گروهی از آن‌ها (از حق) منحرف شود، سپس توبۀ آن‌ها را پذیرفت، بی‌گمان او (نسبت) به آن‌ها رؤوف مهربان است.');
INSERT INTO "hussein_taji" VALUES(9,118,'9:118','و به آن سه نفری که (از جنگ تبوک) تخلف نمودند، (لطف و احسان نمود)؛ [ آن سه نفر کعب بن مالک، مراره بن ربیع و هلال بن امیه رضی الله عنهم بودند، داستان آنان را به زبان کعب (یکی از آن سه نفر) در کتاب صحیح بخاری: ملاحظه فرمایید.] تا آنگاه که زمین با همۀ فراخی‌اش بر آن‌ها تنگ شد، و از خود به تنگ آمدند، و دانستند که پناه‌گاهی از الله جز به سوی او نیست، سپس رحمت خود را شامل حال آن‌ها نمود، تا توبه کنند (و توبۀ آنان را پذیرفت) بی‌گمان الله است که توبه‌پذیر مهربان است.');
INSERT INTO "hussein_taji" VALUES(9,119,'9:119','ای کسانی‌که ایمان آورده‌اید، از الله بترسید، و با راستگویان باشید [ زیرا که راستگویی سبب نجات انسان و دروغگویی سبب هلاکت می‌گردد. ].');
INSERT INTO "hussein_taji" VALUES(9,120,'9:120','سزاوار نیست که اهل مدینه، و اعراب (بادیه‌نشین) که اطراف آن‌ها هستند، از (همراهی) رسول الله باز پس مانند، و نه برای (حفظ) جان خود، از جان او چشم بپوشند، این بدان سبب است که هیچ تشنگی و خستگی و گرسنگی در راه الله به آن‌ها نمی‌رسد، و هیچ (جا منزل نمی‌کنند و) گامی که موجب خشم کافران شود؛ بر نمی‌دارند، و هیچ (دستبرد و) گزندی به دشمن نمی‌رسانند، مگر اینکه به (سبب) آن، عمل صالحی برایشان نوشته شود، بی‌گمان الله پاداش نیکوکاران را ضایع نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(9,121,'9:121','و هیچ مال کوچک یا بزرگی را (در راه الله) انفاق نمی‌کنند، و هیچ وادی (و سرزمینی) نمی‌پیمایند، مگر اینکه برای آن‌ها نوشته می‌شود، تا الله پاداش نیکوترین کردارشان را به آن‌ها بدهد.');
INSERT INTO "hussein_taji" VALUES(9,122,'9:122','و نباید که مؤمنان همگی (برای جهاد) کوچک کنند، پس چرا از هر گروهی از آنان، دسته‌ای کوچ نمی‌کنند، تا در دین دانش بیاموزند، و قوم خود را هشدار دهند، هنگامی‌که به سوی آن‌ها باز گشتند، باشد که آن‌ها بترسند.');
INSERT INTO "hussein_taji" VALUES(9,123,'9:123','ای کسانی‌که ایمان آورده‌اید! با کسانی‌که از کافران که نزدیک شمایند، جنگ کنید، و باید آن‌ها در شما شدت و درشتی بیابند، و بدانید که الله با پرهیزگاران است.');
INSERT INTO "hussein_taji" VALUES(9,124,'9:124','و هنگامی‌که سوره‌ای نازل شود، پس بعضی از آن‌ها (به دیگری) می‌گوید: «این سوره، ایمان کدام یک از شما را افزود؟!» (به آن‌ها بگو:) اما کسانی‌که ایمان آورده‌اند، پس به ایمان‌شان افزوده، و آن‌ها شادمانی می‌کنند.');
INSERT INTO "hussein_taji" VALUES(9,125,'9:125','و اما کسانی‌که در دل‌های شان بیماری است، پس پلیدی به پلیدی‌شان افزوده، و در حالی‌که کافر بودند؛ مردند.');
INSERT INTO "hussein_taji" VALUES(9,126,'9:126','آیا آن‌ها نمی‌بینند که در هر سال، یک یا دوبار آزمایش می‌شوند؟! باز توبه نمی‌کنند، و نه آن‌ها پند می‌گیرند.');
INSERT INTO "hussein_taji" VALUES(9,127,'9:127','و هنگامی‌که سوره‌ای نازل شود، بعضی از آن‌ها به بعضی دیگر نگاه می‌کنند (گویند:) «آیا کسی شما را می‌بیند؟!» سپس باز می‌گردند، الله دل‌های آن‌ها را از (از حق) باز گردانید، زیرا که آن‌ها گروهی‌اند که نمی‌فهمند.');
INSERT INTO "hussein_taji" VALUES(9,128,'9:128','یقیناً پیامبری از (میان) خودتان به سوی‌تان آمد که رنج‌های شما بر او دشوار (و گران) است، و بر (هدایت) شما سخت اصرار دارد، و (نسبت) به مؤمنان رؤوف (و) مهربان است.');
INSERT INTO "hussein_taji" VALUES(9,129,'9:129','پس اگر روی بر گردانند، بگو: «الله برای من کافی است، هیچ معبودی (به حق) جز او نیست، بر او توکل کردم، و او پروردگار عرش عظیم است».');
INSERT INTO "hussein_taji" VALUES(10,1,'10:1','الر. (الف. لام. را) این آیات کتاب محکم (و با حکمت) است.');
INSERT INTO "hussein_taji" VALUES(10,2,'10:2','آیا برای مردم موجب شگفتی است که به مردی از خودشان وحی کرده‌ایم که: «مردم را بترسان و کسانی‌که ایمان آورده‌اند بشارت ده که برای آن‌ها سابقۀ نیکو (و پاداشی حتمی) نزد پروردگار‌شان است». کافران گفتند: «این (مرد) جادوگرآشکاری است».');
INSERT INTO "hussein_taji" VALUES(10,3,'10:3','بی‌گمان پروردگار شما، الله است، که آسمان‌ها و زمین را در شش روز آفرید، سپس بر عرش مستوی شد، کار (جهان) را تدبیر می‌کند. هیچ شفاعت‌کننده‌ای نیست، مگر بعد از فرمان او، این است الله پروردگار شما، پس او را پرستش کنید، آیا پند نمی‌گیرید؟!');
INSERT INTO "hussein_taji" VALUES(10,4,'10:4','بازگشت همه شما به سوی اوست، وعدۀ الله حق است، بی‌گمان او آفرینش را آغاز می‌کند، سپس آن‌ها را باز می‌گرداند، تا کسانی را که ایمان آورده و کارهای شایسته کرده‌اند به عدالت پاداش دهد، و کسانی‌که کافر شدند، برایشان نوشیدنی از آب جوشان است، و عذابی دردناک، به سبب آنکه کفر می‌ورزیدند.');
INSERT INTO "hussein_taji" VALUES(10,5,'10:5','او کسی است که خورشید را روشنایی، و ماه را نور قرار داد، و برای آن منازلی مقدر کرد، تا عدد سال‌ها و حساب را بدانید، الله این را جز به حق نیافریده است، آیات را برای گروهی که می‌دانند، به تفصیل بیان می‌کند.');
INSERT INTO "hussein_taji" VALUES(10,6,'10:6','بی‌گمان در آمد و شد شب و روز، و آنچه الله در آسمان‌ها و زمین آفریده است، نشانه‌های است برای گروهی که پرهیز گاری می‌کنند.');
INSERT INTO "hussein_taji" VALUES(10,7,'10:7','همانا کسانی‌که ملاقات ما را (در روز قیامت) امید ندارند، و به زندگی دنیا خشنود شده و بدان دل بسته‌اند، و کسانی‌که از آیات ما غافلند،');
INSERT INTO "hussein_taji" VALUES(10,8,'10:8','آن‌ها (همه) جایگاه‌شان آتش (جهنم) است، به سبب کارهایی که انجام می‌دادند.');
INSERT INTO "hussein_taji" VALUES(10,9,'10:9','بی‌گمان کسانی‌که ایمان آوردند و کارهای شایسته کردند، پروردگارشان به سبب ایمان‌شان آن‌ها را به باغ‌های (بهشت) پر نعمت هدایت می‌کنند که نهرها زیر (درختان) آن جاری است.');
INSERT INTO "hussein_taji" VALUES(10,10,'10:10','دعای‌شان در آن: «الله! تو منزهی» باشد، و تحیت شان در آن: «سلام» و پایان دعای‌شان این است که: «حمد و سپاس، مخصوص الله، پروردگار جهانیان است».');
INSERT INTO "hussein_taji" VALUES(10,11,'10:11','و اگر الله زود مردم را مجازات می‌کرد، همان‌گونه که آن‌ها برای خیر عجله دارند، عمرشان به پایان می‌رسید، (و نابود می‌شدند) پس کسانی‌که به ملاقات ما امید ندارند (به حال خود) رها می‌کنیم تا در طغیان خود سرگردان بمانند.');
INSERT INTO "hussein_taji" VALUES(10,12,'10:12','و چون به انسان زیان (و رنجی) برسد، (در هر حال که باشد) به پهلو خوابیده، یا نشسته، یا ایستاده، ما را می‌خواند. پس هنگامی‌که آن زیان (و رنج) را از او بر طرف کردیم، چنان می‌رود که گویی هرگز ما را برای دفع زیان (و رنج) که به او رسیده بود، نخوانده است، این گونه برای اسراف‌کاران، آنچه می‌کردند (در نظرشان) زینت داده شده است.');
INSERT INTO "hussein_taji" VALUES(10,13,'10:13','به راستی که ما امت‌های پیش از شما را هنگامی‌که ستم کردند، هلاک کردیم، در حالی‌که پیامبران آن‌ها با دلیل روشن به سوی آنان آمدند و آن‌ها ایمان نیاوردند، این گونه گروه مجرمان را کیفر می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(10,14,'10:14','سپس شما را پس از آن‌ها (روی) زمین جانشینان قرار دادیم، تا بنگریم که چگونه عمل می‌کنید.');
INSERT INTO "hussein_taji" VALUES(10,15,'10:15','و هنگامی‌که آیات روشن ما بر آن‌ها خوانده شود، کسانی‌که به ملاقات ما (در روز قیامت) امید ندارند، گویند: «قرآنی غیر از این بیاور یا آن را تغییر بده». (ای پیامبر!) بگو: «من حق ندارم که آن را از پیش خود تغییر دهم، و من پیروی نمی‌کنم مگر آنچه را که بر من وحی می‌شود، و من اگر پروردگارم را نافرمانی کنم از عذاب روز بزرگ می‌ترسم»');
INSERT INTO "hussein_taji" VALUES(10,16,'10:16','بگو: «اگر الله می‌خواست من آن را بر شما نمی‌خواندم، و نه (الله) شما را از آن آگاه می‌کرد، به راستی که پیش از این عمری را در میان شما گذرانده ام، آیا نمی‌اندیشید؟!»');
INSERT INTO "hussein_taji" VALUES(10,17,'10:17','پس چه کسی ستمکارتر است از آن کسی‌که بر الله دروغ می‌بندد، و یا آیات او را تکذیب می‌کند؟! بی‌گمان مجرمان رستگار نخواهند شد.');
INSERT INTO "hussein_taji" VALUES(10,18,'10:18','و غیر از الله چیزهای را می‌پرستند که نه به آن‌ها زیانی می‌‌رساند و نه سودشان می‌بخشد، و می‌گویند: «این‌ها (= بت‌ها) شفیعان ما نزد الله هستند» بگو: «آیا الله را به چیزی که در آسمان‌ها و در زمین نمی‌داند، خبر می‌دهید؟!» منزه است او، و از آنچه با او شریک می‌سازند برتر است.');
INSERT INTO "hussein_taji" VALUES(10,19,'10:19','(در آغاز) مردم جز امت واحدی نبودند، پس اختلاف کردند، و اگر فرمانی نبود پیش از این، از پروردگار صادر شده بود، در میان آن‌ها در آنچه که در آن اختلاف داشتند، داوری می‌شد.');
INSERT INTO "hussein_taji" VALUES(10,20,'10:20','و می‌گویند «چرا معجزه‌ای از (جانب) پروردگارش بر او نازل نشده است؟». پس بگو: «(علم) غیب تنها از آن الله است، پس شما انتظار بکشید که من (نیز) با شما از منتظرانم».');
INSERT INTO "hussein_taji" VALUES(10,21,'10:21','و هنگامی‌که به مردم، پس از رنج (و زیانی) که به آن‌ها رسیده است رحمتی بچشانیم، آنگاه آن‌ها در آیات ما نیرنگ می‌کنند، بگو: الله در نیرنگ زدن سریع‌تر است، بی‌تردید رسولان (= فرشتگان) ما آنچه نیرنگ می‌کنید، می‌نویسند».');
INSERT INTO "hussein_taji" VALUES(10,22,'10:22','او کسی است که شما را در خشکی و دریا به حرکت در می‌آورد، تا آنگاه که در کشتی قرار می‌گیرید، و (کشتی‌ها) با بادی ملایم آن‌ها را (به سوی مقصد) حرکت می‌دهند و (آن‌ها) به آن شادمان می‌شوند. (نا گهان) طوفان شدیدی می‌وزد، و موج از هر سو (به سوی آن‌ها) بیاید، و گمان کنند، که راه گریزی ندارند (و هلاک خواهند شد)، الله را از روی اخلاص می‌خوانند: «که اگر ما را از این (گرفتاری) نجات دهی، قطعاً از سپاسگزاران خواهیم بود».');
INSERT INTO "hussein_taji" VALUES(10,23,'10:23','پس چون (الله) آن‌ها را نجات داد، آن‌ها به نا حق در زمین سرکشی (و ستم) می‌کنند ای مردم! سر کشی (و ستم) شما تنها به زیان خودتان است، متاع (چند روز) زندگی دنیاست، سپس بازگشت‌تان به سوی ماست، پس شما را به آنچه انجام می‌دادید، خبر می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(10,24,'10:24','مثل زندگی دنیا، همانند آبی است که از آسمان نازل کردیم، سپس بر اثر آن گیاهان گوناگون زمین از آنچه مردم و چهارپایان می‌خورند؛ رویید، تا زمانی که زمین پیرایه خود بگرفت، و آراسته شد، و اهل آن گمان کردند که خود بر آن چیره‌اند، (ناگهان) فرمان ما در شب یا روزی فرا رسد، و آنچنان آن (کشتزار) را درو کنیم که گوی دیروز هرگز (کشتزاری) نبوده است. این گونه آیات (خود) را برای گروهی که می‌اندیشند به تفصیل بیان می‌کنیم.');
INSERT INTO "hussein_taji" VALUES(10,25,'10:25','و الله به سرای سلامتی (= بهشت) دعوت می‌کند، و هر کس را بخواهد به راه راست هدایت می‌کند.');
INSERT INTO "hussein_taji" VALUES(10,26,'10:26','برای کسانی‌که نیکی کردند، پاداش نیک (= بهشت) است و افزون بر آن (=رؤیت باری تعالی است). و تیرگی و خواری چهره‌هایشان را نپوشاند، اینان اهل بهشتند، و جاودانه در آن خواهند ماند.');
INSERT INTO "hussein_taji" VALUES(10,27,'10:27','و کسانی‌که مرتکب بدی‌ها شدند، پاداش هر بدی همانند آن است، و خواری (چهره‌های) آن‌ها را می‌پوشاند و هیچ کسی نمی‌تواند آن‌ها را از (عذاب) الله نگه دارد (چنان شوند که) گویی با پاره‌ای از شب تاریک چهره‌هایشان پوشیده شده است، اینان اهل آتش (دوزخ)اند و جاودانه در آن خواهند ماند.');
INSERT INTO "hussein_taji" VALUES(10,28,'10:28','(و به یاد آورید) روزی‌که همۀ آن‌ها را گرد آوریم، سپس به کسانی‌که شرک آوردند گویم: «شما و معبودهای‌تان در مکان خود تان بمانید» پس آن‌ها را از یکدیگر جدا می‌کنیم، و معبودهای‌شان (به آن‌ها) می‌گویند: «شما ما را عبادت نمی‌کردید،');
INSERT INTO "hussein_taji" VALUES(10,29,'10:29','پس، بس است که الله میان ما و شما گواه باشد، که ما یقیناً از عبادت شما بی‌خبر بودیم».');
INSERT INTO "hussein_taji" VALUES(10,30,'10:30','آنجا هر کس هر چه پیش فرستاده است، می‌آزماید، و همه به سوی الله مالک حقیقی شان بازگردانده می‌شوند، و چیزهای را که افترا می‌بستند (از نظرشان) گم و نابود می‌شوند.');
INSERT INTO "hussein_taji" VALUES(10,31,'10:31','بگو: «چه کسی شما را از آسمان و زمین روزی می‌دهد؟کیست که مالک شنوایی و بی‌نوا یی هاست؟ و چه کسی زنده را از مرده بیرون می‌آورد، و مرده را از زنده بیرون می‌آورد؟ و چه کسی کار (جهان) را تدبیر می‌کند؟ پس بی درنگ خواهند گفت: «الله» پس بگو: «آیا (از او) نمی‌ترسید؟!');
INSERT INTO "hussein_taji" VALUES(10,32,'10:32','پس آن الله پروردگار حقیقی شماست، پس بعد از حق، چه چیزی است جز گمراهی؟ پس چگونه (از پرستش او) روی گران می‌شوید؟!».');
INSERT INTO "hussein_taji" VALUES(10,33,'10:33','این چنین سخن پروردگارت بر کسانی‌که فاسق شدند؛ ثابت شده که آن‌ها ایمان نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(10,34,'10:34','بگو: «آیا از این معبودهای شما کسی هست که آفرینش شما را آغاز می‌کند، سپس آن را باز می‌گرداند؟!» بگو: «الله آفرینش را آغاز می‌کند، سپس آن را باز می‌گرداند، پس چگونه (از حق) منحرف می‌شوید؟!».');
INSERT INTO "hussein_taji" VALUES(10,35,'10:35','بگو: «آیا از این معبودهای شما کسی هست که به (سوی) حق هدایت می‌کند؟!» بگو: «الله به (سوی) حق هدایت می‌کند، آیا کسی که به (سوی) حق هدایت می‌کند، برای پیروی سزاوار‌‌‌تر است یا کسی‌که خود هدایت نمی‌شود؛ مگر آن که هدایتش کنند؟! پس شما را چه شده؟! چگونه داوری می‌کنید؟!».');
INSERT INTO "hussein_taji" VALUES(10,36,'10:36','و بیشتر آن‌ها جز از گمان پیروی نمی‌کنند، بی‌تردید، گمان، از حق بی‌نیاز نمی‌کند، یقیناً الله به آنچه می‌کنند، داناست.');
INSERT INTO "hussein_taji" VALUES(10,37,'10:37','و (سزاوار) نیست این قرآن، به دروغ (بدون وحی الهی) به الله نسبت داده شود؛ بلکه تصدیق‌کننده چیزی است که پیش از آن (نازل شده) است، و بیان و تفصیل (آن) کتاب است، شکی در آن نیست، از (جانب) پروردگار جهانیان است.');
INSERT INTO "hussein_taji" VALUES(10,38,'10:38','آیا آن‌ها می‌گویند: «(او) آن را به دروغ (به الله) نسبت داده است؟!» بگو: «اگر راست می‌گویید، پس یک سوره همانند آن بیاورید، و جز الله هر که را که می‌توانید به یاری بخوانید»');
INSERT INTO "hussein_taji" VALUES(10,39,'10:39','بلکه آن‌ها چیزی را تکذیب کردند که به آن علم و احاطه نداشتند، و هنوز برای آن‌ها مصداق (و حقیقت) آن نیامده است، بدین گونه کسانی‌که پیش از آن‌ها بودند (نیز) تکذیب کردند، پس بنگر سرانجام ستمکاران چگونه بوده است.');
INSERT INTO "hussein_taji" VALUES(10,40,'10:40','و از آن‌ها کسی هست که به آن ایمان می‌آورد، و از آن‌ها کسی هست که به آن ایمان نمی‌آورد، و پروردگار تو به مفسدان آگاه‌تر است.');
INSERT INTO "hussein_taji" VALUES(10,41,'10:41','و اگر (کافران) تو را تکذیب کردند، پس بگو: «عمل من برای خودم، و عمل شما برای خودتان است، شما از آنچه من می‌کنم بیزارید، و من (نیز) از آنچه (شما) می‌کنید بیزارم».');
INSERT INTO "hussein_taji" VALUES(10,42,'10:42','و ازآن‌ها کسانی هستند که به تو گوش فرا می‌دهند، آیا تو می‌توانی کران را بشنوانی، و هر چند نفهمند؟!');
INSERT INTO "hussein_taji" VALUES(10,43,'10:43','و از آن‌ها کسانی‌اند که به تو می‌نگرند، آیا تو می‌توانی کوران را راهنمایی کنی، و هر چند نبینند؟!');
INSERT INTO "hussein_taji" VALUES(10,44,'10:44','یقیناً الله به مردم هیچ ستم نمی‌کند، ولی مردم به خویشتن ستم می‌کنند.');
INSERT INTO "hussein_taji" VALUES(10,45,'10:45','و روزی‌که (الله) آن‌ها را محشور می‌کند، (چنان احساس می‌کنند) گویی جز ساعتی از روز که یکدیگر را بشناسند (در دنیا) درنگ نکرده‌اند، به راستی کسانی‌که لقای الله را تکذیب کردند، زیان کردند، و هدایت نیافتند.');
INSERT INTO "hussein_taji" VALUES(10,46,'10:46','و اگر پاره‌ای از چیز‌‌‌های را که به آن‌ها وعده داده‌ایم به تو نشان دهیم، یا تو را بمیرانیم، پس بازگشت‌شان به سوی ماست، پس الله بر آنچه که می‌کنند، گواه است.');
INSERT INTO "hussein_taji" VALUES(10,47,'10:47','برای هر امتی پیامبری است، پس هنگامی‌که پیامبرشان آمد، میان آن‌ها به عدالت داوری شود، و به آن‌ها ستم نخواهد شد.');
INSERT INTO "hussein_taji" VALUES(10,48,'10:48','و می‌گویند: «اگر راست می‌گویید این وعده (عذاب) کی خواهد بود؟!».');
INSERT INTO "hussein_taji" VALUES(10,49,'10:49','بگو: «من برای خودم مالک هیچ زیانی و سودی نیستم، جز آنچه الله بخواهد، برای هر امتی سرآمدی است، هنگامی‌که سرآمد آن‌ها فرا رسد، پس نه ساعتی تأخیر می‌کنند، و نه پیشی می‌گیرند».');
INSERT INTO "hussein_taji" VALUES(10,50,'10:50','بگو: «به من خبر دهید؛ اگر عذاب او شبانگاه یا در روز به سراغ شما آید، مجرمان چه چیز را (اینگونه) به شتاب می‌طلبند؟!»');
INSERT INTO "hussein_taji" VALUES(10,51,'10:51','باز هنگامی‌که (عذاب) واقع شد، آیا به آن ایمان می‌آورید؟ (آنگاه گفته می‌شود): حالا، در حالی‌که با شتاب آن را می‌خواستید.');
INSERT INTO "hussein_taji" VALUES(10,52,'10:52','سپس به کسانی‌که ستم کردند گفته می‌شود: «عذاب جاوید را بچشید! آیا جز به آنچه که می‌کردید کیفر داده می‌شوید؟!»');
INSERT INTO "hussein_taji" VALUES(10,53,'10:53','و از تو خبر می‌گیرند: آیا آن حق است؟! بگو: «آری، سوگند به پروردگارم، قطعاً آن حق است، و شما عاجز کننده نیستید (و نمی‌توانید از آن فرار کنید)».');
INSERT INTO "hussein_taji" VALUES(10,54,'10:54','و هر کس که ستم کرده، اگر تمام آنچه در زمین است داشته باشد، حتماً (همه را) برای نجات خودش می‌دهد، و هنگامی‌که عذاب را ببیند، پشیمانی (خود) را کتمان کنند، و میان آن‌ها به عدالت داوری شود، و به آن‌ها ستم نخواهد شد.');
INSERT INTO "hussein_taji" VALUES(10,55,'10:55','آگاه باشید، بی‌شک آنچه در آسمان‌ها و زمین است، از آن الله است، آگاه باشید همانا وعدۀ الله حق است، و لیکن بیشتر آن‌ها نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(10,56,'10:56','اوست که زنده می‌کند و می‌میراند، و (همه) به سوی او باز گردانده می‌شوید.');
INSERT INTO "hussein_taji" VALUES(10,57,'10:57','ای مردم! به راستی که برای شما از جانب پروردگارتان موعظه‌ای آمد، و شفایی برای آنچه در سینه‌هاست و هدایت و رحمتی برای مؤمنان است.');
INSERT INTO "hussein_taji" VALUES(10,58,'10:58','بگو: «به فضل الله و به رحمت او، پس باید بدان خوشحال شوند، که آن از آنچه می‌اندوزند؛ بهتر است».');
INSERT INTO "hussein_taji" VALUES(10,59,'10:59','بگو: «به من خبر دهید؛ آنچه از رزق که الله برای شما نازل کرده است، پس بخشی از آن را حلال، و بخشی را حرام نموده‌اید؟!» بگو: «آیا الله به شما اجازه داده است یا بر الله دروغ می‌بندید؟»');
INSERT INTO "hussein_taji" VALUES(10,60,'10:60','و کسانی‌که بر الله دروغ می‌بندند، (دربارۀ) روز قیامت چه می‌پندارند؟! بی‌گمان الله بر (همۀ) مردم فضل (و کرم) دارد، و لیکن بیشترشان سپاسگزاری نمی‌کنند.');
INSERT INTO "hussein_taji" VALUES(10,61,'10:61','و در هیچ کار (و حالی) نباشی، و هیچ بخشی از قرآن را نمی‌خوانی، و هیچ عملی را انجام نمی‌دهید، مگر اینکه ما (حاضر و) گواه بر شما هستیم، هنگامی‌که درآن (عمل) وارد می‌شوید، و هم‌وزن ذره‌ای در زمین و نه در آسمان بر پروردگارت پوشیده نمی‌ماند، و نه کوچکتر از آن (ذره) و نه بزرگتر، مگر اینکه در کتاب روشن (لوح محفوظ، ثبت) است.');
INSERT INTO "hussein_taji" VALUES(10,62,'10:62','آگاه باشید! همانا دوستان الله، نه ترسی بر آن‌هاست، و نه آن‌ها غمگین می‌شوند.');
INSERT INTO "hussein_taji" VALUES(10,63,'10:63','(همان) کسانی‌که ایمان آوردند، و پرهیزگاری می‌کردند.');
INSERT INTO "hussein_taji" VALUES(10,64,'10:64','برای آنان در زندگی دنیا و در آخرت بشارت است، سخنان الله تغییر ناپذیر است، این همان کامیابی بزرگ است.');
INSERT INTO "hussein_taji" VALUES(10,65,'10:65','(ای پیامبر!) سخن آن‌ها تو را غمگین نکند بی‌گمان تمام عزت (و پیروزمندی) از آن الله است، او شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(10,66,'10:66','آگاه باشید، هر که در آسمان‌ها و هر که در زمین است، از آن الله است، و کسانی‌که غیر الله؛ شریکان را می‌خوانند، پیروی (از دلیلی) نمی‌کنند، آن‌ها تنها از گمان پیروی می‌کنند، و آن‌ها فقط دروغ می‌گویند.');
INSERT INTO "hussein_taji" VALUES(10,67,'10:67','او کسی است که شب را برای شما قرار داد، تا در آن آرام بگیرید، و روز را روشن (قرار داد) بی‌گمان در این‌ها نشانه‌هایی است برای گروهی که می‌شنوند.');
INSERT INTO "hussein_taji" VALUES(10,68,'10:68','(مشرکان) گفتند: «الله فرزندی برگزیده است» او منزه است، او بی‌نیاز است، آنچه در آسمان‌ها و آنچه در زمین است از آن اوست، هیچ دلیلی نزد شما بر این (ادعا) نیست، آیا بر الله چیزی می‌گویید که نمی‌دانید؟!');
INSERT INTO "hussein_taji" VALUES(10,69,'10:69','بگو: «بی‌گمان کسانی‌که به الله دروغ می‌بندند، رستگار نمی‌شوند».');
INSERT INTO "hussein_taji" VALUES(10,70,'10:70','بر خورداری (ناچیز) در دنیا دارند، آنگاه باز گشت شان به سوی ماست، سپس عذاب سختی بسبب آنکه کفر می‌ورزیدند؛ به آن‌ها می‌چشانیم.');
INSERT INTO "hussein_taji" VALUES(10,71,'10:71','و داستان نوح را بر آن‌ها بخوان، چون به قوم خود گفت: «ای قوم من! اگر اقامت من، و یاد آوری من به آیات الله، بر شما گران آمده است، پس من بر الله توکل کردم، شما تدبیر (و اندیشۀ) خود و معبودهای‌تان را جمع کنید، سپس هیچ چیز از کارهای‌تان بر شما پوشیده نماند، آنگاه (برای کشتنم) به سوی من هجوم آورید، و (لحظه‌ای) مرا مهلت ندهید.');
INSERT INTO "hussein_taji" VALUES(10,72,'10:72','پس اگر روی بگردانید، من (در مقابل دعوتم) از شما هیچ مزدی نخواسته ام، مزد من، تنها بر الله است، و من مأمور شده ام که از مسلمانان باشم».');
INSERT INTO "hussein_taji" VALUES(10,73,'10:73','پس آن‌ها او را تکذیب کردند، آنگاه ما او را، و آنان که با او در کشتی بودند، نجات دادیم، و آن‌ها را جانشین (گذشتگان) قرار دادیم، و کسانی را که آیات ما را تکذیب کردند؛ غرق کردیم. پس بنگر که سر انجام بیم‌داده‌شد‌گان چگونه بود!');
INSERT INTO "hussein_taji" VALUES(10,74,'10:74','سپس بعد از او (= نوح) پیامبرانی به سوی قوم‌شان فرستادیم، پس آنان دلایل روشنی برایشان آوردند، پس آن‌ها (مستعد) نبودند که به آنچه پیش از آن تکذیب کرده بودند، ایمان آوردند. این چنین بر دل‌های تجاوزکاران مهر می‌نهیم.');
INSERT INTO "hussein_taji" VALUES(10,75,'10:75','سپس بعد از آن‌ها موسی و (برادرش) هارون را با آیات خود به سوی فرعون و اطرافیانش فرستادیم، پس آن‌ها تکبر ورزیدند، و آن‌ها گروه مجرم بودند.');
INSERT INTO "hussein_taji" VALUES(10,76,'10:76','پس چون حق از نزد ما برای آن‌ها آمد، گفتند: «بی‌گمان این جادویی آشکار است».');
INSERT INTO "hussein_taji" VALUES(10,77,'10:77','موسی گفت: «آیا دربارۀ حق، چون برای شما آمده است (چنین) می‌گویید؟! آیا این جادو است؟ در حالی‌که جادوگران رستگار نمی‌شوند».');
INSERT INTO "hussein_taji" VALUES(10,78,'10:78','(فرعونیان) گفتند: آیا آمده‌ای که ما را از آنچه نیاکان‌مان بر آن یافتیم، باز داری، و بزرگی (و حکومت) در روی زمین، از آن شما دو تن باشد؟ ما به شما دو نفر ایمان نمی‌آوریم.');
INSERT INTO "hussein_taji" VALUES(10,79,'10:79','و فرعون گفت: «تمام جادوگران (ماهر و) دانا را نزد من بیاورید».');
INSERT INTO "hussein_taji" VALUES(10,80,'10:80','پس چون جادوگران آمدند، موسی به آن‌ها گفت: «آنچه شما (از وسایل سحر) می‌توانید بیفکنید، (اکنون بر زمین) بیفکنید».');
INSERT INTO "hussein_taji" VALUES(10,81,'10:81','پس چون افکندند، موسی گفت: «آنچه شما آورده‌اید، سحر است، بی‌گمان الله آن را باطل خواهد کرد، همانا الله عمل مفسدان را اصلاح نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(10,82,'10:82','و الله با کلمات خود، حق را (ثابت و) پایدار می‌کند گرچه مجرمان کراهت داشته باشند».');
INSERT INTO "hussein_taji" VALUES(10,83,'10:83','پس هیچ کس به موسی ایمان نیاورد، جز گروهی اندک از قوم او، (آن هم) با ترس فرعون و اطرافیانش، که مبادا آن‌ها را بیازارد، و بی‌شک فرعون در زمین سرکش (و جاه طلب) بود، و همانا او از اسراف‌کاران بود.');
INSERT INTO "hussein_taji" VALUES(10,84,'10:84','موسی گفت: «ای قوم من! اگرشما به الله ایمان آورده‌اید، پس بر او توکل کنید، اگر مسلمان هستید».');
INSERT INTO "hussein_taji" VALUES(10,85,'10:85','گفتند: «بر الله توکل کرده‌ایم، پروردگارا! ما را دستخوش فتنۀ گروه ستمگر قرار نده،');
INSERT INTO "hussein_taji" VALUES(10,86,'10:86','و به رحمتت ما را از (شر) گروه کافران نجات بده».');
INSERT INTO "hussein_taji" VALUES(10,87,'10:87','و به موسی و برادرش (هارون علیهما السلام) وحی کردیم که: برای قوم خود، خانه‌های در (سرزمین) مصر انتخاب کنید، و خانه‌هایتان را عبادتگاه قرار دهید، و نماز را بر پا دارید، و (ای موسی!) مؤمنان را (به پیروزی و بهشت) بشارت بده»');
INSERT INTO "hussein_taji" VALUES(10,88,'10:88','و موسی گفت: «پروردگارا! تو به فرعون و اطرافیانش زینت و اموالی (فراوان) در زندگی دنیا داده‌ای، پروردگارا! تا (بدین وسیله دیگران را) از راه تو گمراه کنند. پروردگارا! اموال‌شان را نابود کن، و دل‌های شان را سخت کن، پس آن‌ها ایمان نمی‌آورند، تا عذاب دردناک را ببینند».');
INSERT INTO "hussein_taji" VALUES(10,89,'10:89','(الله) فرمود: «قطعاً دعای شما دو نفر پذیرفته شد، پس استقامت کنید، و از راه کسانی‌که نمی‌دانند، پیروی نکنید»');
INSERT INTO "hussein_taji" VALUES(10,90,'10:90','و (سر انجام ما) بنی اسرائیل را از دریا گذراندیم، پس فرعون و سپاهیانش از روی ستم و تعدی، در پی آن‌ها رفتند، چون (فرعون) هنگام غرق شدنش فرا رسید، گفت: «ایمان آوردم، که هیچ معبودی (به حق)نیست؛ جز کسی‌که بنی اسرائیل به او ایمان آورده‌اند، و من از مسلمانان هستم».');
INSERT INTO "hussein_taji" VALUES(10,91,'10:91','(به او گفته شد:) حالا؟ در حالی‌که قبلاً نافرمانی کردی و از مفسدان بودی!');
INSERT INTO "hussein_taji" VALUES(10,92,'10:92','پس امروز بدنت را (از دریا) نجات می‌دهیم، تا عبرتی برای کسانی‌که بعد از تو می‌آیند باشی، و بی‌گمان بسیاری از مردم از آیات ما غافلند.');
INSERT INTO "hussein_taji" VALUES(10,93,'10:93','و به راستی که بنی اسرائیل را در جایگاه نیکو منزل دادیم، و از پاکیزه‌ها روزی‌شان دادیم، پس اختلاف نکردند تا اینکه علم (و آگاهی) برایشان آمد، بی‌گمان پروردگار تو، روز قیامت در آنچه در آن اختلاف می‌کردند، میان‌شان داوری خواهد کرد.');
INSERT INTO "hussein_taji" VALUES(10,94,'10:94','پس اگر در آنچه بر تو نازل کرده‌ایم در تردید هستی، از کسانی‌که کتاب (آسمانی) را پیش از تو می‌خوانند؛ بپرس، یقیناً حق از طرف پروردگارت به تو رسیده است، پس هرگز از تردید کنندگان مباش! [به سورۀ اعراف آیه رجوع فرمایید.]');
INSERT INTO "hussein_taji" VALUES(10,95,'10:95','و مباش هرگز از کسانی‌که آیات الله را تکذیب کردند، آنگاه از زیانکاران خواهی بود.');
INSERT INTO "hussein_taji" VALUES(10,96,'10:96','بی‌گمان کسانی‌که فرمان پروردگار بر آن‌ها تحقق یافته، ایمان نمی‌آورند،');
INSERT INTO "hussein_taji" VALUES(10,97,'10:97','هر چند که هرگونه آیه (و معجزه) برای آن‌ها بیاید، تا زمانی که عذاب دردناک را ببیند.');
INSERT INTO "hussein_taji" VALUES(10,98,'10:98','پس چرا هیچ شهر (و آبادی) ایمان نیاورد که ایمان‌شان سودشان دهد، مگر قوم یونس، چون ایمان آوردند عذاب رسوا کننده را در زندگی دنیا از آن‌ها بر طرف کردیم، و تا مدتی معین آن‌ها را بهره‌مند ساختیم.');
INSERT INTO "hussein_taji" VALUES(10,99,'10:99','و اگر پروردگار تو می‌خواست، مسلماً تمام کسانی‌که در (روی) زمین هستند، همگی ایمان می‌آوردند، آیا تو می‌خواهی مردم را مجبور کنی تا که ایمان بیاورند؟!');
INSERT INTO "hussein_taji" VALUES(10,100,'10:100','و هیچ کس جز به فرمان الله نمی‌تواند؛ ایمان بیاورد، و (الله) پلیدی را بر کسانی‌که نمی‌اندیشند؛ قرار می‌دهد.');
INSERT INTO "hussein_taji" VALUES(10,101,'10:101','بگو: «بنگرید چه چیز (از نشانه‌های یگانگی‌اش) در آسمان‌ها و زمین است؟» و این آیات و هشدارها به حال گروهی که ایمان نمی‌آورند، سودی نمی‌بخشد.');
INSERT INTO "hussein_taji" VALUES(10,102,'10:102','پس آیا آن‌ها (چیزی) جز همانند روزهای کسانی را که پیش از آن‌ها گذشتند، انتظار می‌کشند؟! بگو: «پس منتظر باشید، که بی‌گمان من (نیز) با شما از منتظرانم».');
INSERT INTO "hussein_taji" VALUES(10,103,'10:103','سپس پیامبران مان، و کسانی را که ایمان آورده‌اند نجات می‌دهیم، اینگونه، بر ما حق است که مؤمنان را نجات دهیم.');
INSERT INTO "hussein_taji" VALUES(10,104,'10:104','بگو: «ای مردم! اگر از دین من در شک هستید، پس من کسانی را که شما به جای الله پرستش می‌کنید؛ نمی‌پرستم، و لیکن من تنها الله را می‌پرستم که شما رامی میراند، و من مأمور شده ام از مؤمنان باشم.');
INSERT INTO "hussein_taji" VALUES(10,105,'10:105','و اینکه روی خود را به آیین حنیف متوجه کن، و هرگز از مشرکان مباش!');
INSERT INTO "hussein_taji" VALUES(10,106,'10:106','و به جای الله، چیزی را که نه سودی به تو می‌رساند و نه زیانی به تو می‌رساند؛ نخوان، پس اگر چنین کنی، بی‌گمان از ستمکاران خواهی بود.');
INSERT INTO "hussein_taji" VALUES(10,107,'10:107','و اگر الله زیانی به تو برساند، پس جز او هیچ کس نتواند آن را بر طرف کند، و اگر ارادۀ خیری برای تو کند، پس هیچ کس فضل او را باز نتواند داشت، به هرکس از بندگانش که بخواهد می‌رساند و او آمرزندۀ مهربان است».');
INSERT INTO "hussein_taji" VALUES(10,108,'10:108','بگو: «ای مردم! بی‌تردید حق از طرف پروردگارتان برای شما آمد، پس هر کس هدایت شد، برای خود هدایت شده، و هر کس گمراه شد، پس به زیان خود گمراه شده است، و من نگهبان بر شما نیستم».');
INSERT INTO "hussein_taji" VALUES(10,109,'10:109','و از آنچه به تو وحی می‌شود پیروی کن، و صبر کن، تا الله داوری کند، و او بهترین داوران است.');
INSERT INTO "hussein_taji" VALUES(11,1,'11:1','الر (الف. لام. را) این کتابی است که آیاتش استوار، سپس روشن شده، از نزد حکیم آگاه (نازل گردیده) است.');
INSERT INTO "hussein_taji" VALUES(11,2,'11:2','که جز«الله» را نپرستید، که من از سوی او هشدار دهنده و بشارت‌دهنده برای شما هستم.');
INSERT INTO "hussein_taji" VALUES(11,3,'11:3','و اینکه: از پروردگارتان آمرزش بخواهید، سپس به سوی او توبه کنید، تا شما را تا مدت معینی به بهرۀ خوبی بهره‌مند سازد، و هر صاحب فضلی را به‌اندازه فضلش ببخشد، و اگر روی بگردانید، پس من بر شما از عذاب روز بزرگ بیم‌ناک هستم.');
INSERT INTO "hussein_taji" VALUES(11,4,'11:4','باز گشت شما به سوی «الله» است، و او بر همه چیز تواناست.');
INSERT INTO "hussein_taji" VALUES(11,5,'11:5','آگاه باشید آن‌ها سینه‌هایشان را خم می‌کنند (و سرها به هم نزدیک می‌کنند) تا خود را از او پنهان دارند، آگاه باشید، وقتی که آن‌ها لباس‌های خویش را به سر می‌کشند، (الله) می‌داند آنچه را (در دل) پنهان می‌کنند و آنچه را آشکار می‌سازند، بی‌تردید او به راز دل‌ها آگاه است.');
INSERT INTO "hussein_taji" VALUES(11,6,'11:6','و هیچ جنبنده‌ای در زمین نیست؛ مگر اینکه روزی‌اش بر (عهدۀ) الله است، و (او) قرار گاه و مکان دفنش را می‌داند، همۀ این‌ها در کتابی روشن (ثبت) است.');
INSERT INTO "hussein_taji" VALUES(11,7,'11:7','و او کسی هست که آسمان‌ها و زمین را در شش روز آفرید، و عرش او بر آب بود، تا شما را بیازماید که کدام یک از شما به عمل نیکوتر است، و اگر (به آن‌ها) بگویی: «شما پس از مرگ برانگیخته می‌شوید» مسلماً کافران می‌گویند: «این (سخن) جز جادوی آشکار نیست».');
INSERT INTO "hussein_taji" VALUES(11,8,'11:8','و اگر تا زمان محدودی عذاب را از آن‌ها به تأخیر اندازیم، البته (از روی تمسخر) می‌گویند: «چه چیزی مانع آن شده است؟!» آگاه باشید، روزی‌که (عذاب) به سراغ‌شان آید، از آن‌ها برگردانده نخواهد شد، و آنچه را به مسخره می‌گرفتند، آنان را در بر خواهد گرفت.');
INSERT INTO "hussein_taji" VALUES(11,9,'11:9','و اگر از جانب خویش رحمتی به انسان بچشانیم، سپس آن را از او بگیریم، همانا او بسیار نا امید (و) ناسپاس خواهد شد.');
INSERT INTO "hussein_taji" VALUES(11,10,'11:10','و اگر پس از رنج و سختی که به او رسیده است؛ نعمت و آسایشی به او بچشانیم، البته می‌گوید: «سختی‌ها از من دور شد». همانا او شادمان (و) خودستا می‌شود.');
INSERT INTO "hussein_taji" VALUES(11,11,'11:11','مگر کسانی‌که صبر کردند، و کارهای نیک انجام دادند، آنان برایشان آمرزش و پاداش بزرگی است.');
INSERT INTO "hussein_taji" VALUES(11,12,'11:12','پس (ای پیامبر!) شاید برخی از آنچه که به تو وحی می‌شود، وا گذاری، و سینه‌ات از این جهت تنگ (و ناراحت) شود که گویند: «چرا گنجی بر او نازل نشده، یا فرشته‌ای همراه او نیامده است؟!» تو تنها هشدار دهنده‌ای، و الله بر همه چیز نگاهبان است.');
INSERT INTO "hussein_taji" VALUES(11,13,'11:13','آیا می‌گویند: «این (قرآن) را از خود بافته (و به الله نسبت داده) است» بگو: «اگر راست می‌گویید، شما هم ده سورۀ به هم بافته‌ مانند آن بیاورید، و غیر از الله هر که می‌توانید (به یاری) بطلبید».');
INSERT INTO "hussein_taji" VALUES(11,14,'11:14','پس اگر آن‌ها دعوت شما را نپذیرفتند، بدانید که (قرآن) فقط به علم الله نازل شده است، و اینکه هیچ معبودی (به حق) جز او نیست، پس آیا شما مسلمان می‌شوید؟!');
INSERT INTO "hussein_taji" VALUES(11,15,'11:15','کسی‌که زندگانی دنیا و زینت آن را بخواهد، (پاداش) اعمالش را در این (دنیا) به طور کامل و تمام به آن‌ها می‌دهیم، و چیزی از آن‌ها کم و کاست نخواهند شد.');
INSERT INTO "hussein_taji" VALUES(11,16,'11:16','اینان کسانی هستند که در آخرت، جز آتش (جهنم، نصیبی) نخواهند داشت، و آنچه را در دنیا انجام داده‌اند، بر باد رفته است، و آنچه را عمل می‌کردند؛ باطل است.');
INSERT INTO "hussein_taji" VALUES(11,17,'11:17','آیا کسی‌که از سوی پروردگارش دلیل روشن دارد، و به دنبال آن شاهدی از سوی او باشد، و پیش از آن کتاب موسی که پیشوا و رحمت بود (بر صحت آن گواهی دارد، همانند کسی است که دلیلی ندارد؟!). اینان به آن (قرآن) ایمان می‌آورند، و از گروهای مختلف هر کس به آن کافر شود؛ پس آتش وعده گاه اوست، پس تردیدی در آن نداشته باش، بی‌گمان آن حق است، از (جانب) پروردگارت (نازل شده است)، و لیکن بیشتر مردم ایمان نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(11,18,'11:18','و چه کسی ستمکارتر است؛ از کسی‌که بر الله دروغ می‌بندد؟! آنان (روز قیامت) بر پروردگارشان عرضه می‌شوند، و گواهان می‌گویند: «این‌ها همان کسانی هستند که بر پروردگارشان دروغ بسته‌اند» هان، لعنت الله بر ستمکاران باد.');
INSERT INTO "hussein_taji" VALUES(11,19,'11:19','(همان) کسانی‌که (مردم را) از راه الله باز می‌دارند، و آن را کج و منحرف می‌جویند، و آنان به آخرت کافرند.');
INSERT INTO "hussein_taji" VALUES(11,20,'11:20','اینان (هیچ گاه) نمی‌توانند در زمین (الله را)عاجز کنند (و بگریزند) و جز الله (دوستان و) یاورانی ندارند، عذاب برای آنان دو چندان خواهد شد، توان شنیدن (حق) را نداشتند، و (حقیقت را) نمی‌دیدند.');
INSERT INTO "hussein_taji" VALUES(11,21,'11:21','اینان کسانی هستند که به خود شان زیان زده‌اند (و سرمایۀ وجود و عمر خویش را از دست داده‌اند) و آنچه بر می‌بافتند از (نظر) آن‌ها گم (و دور) شدند.');
INSERT INTO "hussein_taji" VALUES(11,22,'11:22','بدون شک آن‌ها در آخرت ازهمه زیان کارترند.');
INSERT INTO "hussein_taji" VALUES(11,23,'11:23','بی‌گمان کسانی‌که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، (و توجه‌کنان) به سوی پروردگارشان فروتنی کردند، آن‌ها اهل بهشتند، آنان در آن جاودان خواهند ماند.');
INSERT INTO "hussein_taji" VALUES(11,24,'11:24','مثل این دو گروه (مؤمنان و کافران) مانند کور و کر، و بینا و شنواست، آیا این دو در وصف یکسانند؟! آیا پند نمی‌گیرید؟!');
INSERT INTO "hussein_taji" VALUES(11,25,'11:25','و به راستی ما نوح را به سوی قومش فرستادیم، (و به آن‌ها گفت:) «همانا من برای شما هشدار دهنده آشکار هستم.');
INSERT INTO "hussein_taji" VALUES(11,26,'11:26','جز الله را نپرستید، همانا من از عذاب روز دردناک بر شما می‌ترسم».');
INSERT INTO "hussein_taji" VALUES(11,27,'11:27','پس اشراف کافر قومش گفتند: «ما تو را جز بشری همانند خودمان نمی‌بینیم، و نمی‌بینیم کسانی‌که از تو پیروی کرده‌اند؛ مگر (گروهی) اراذل (= فرومایگان) ساده لوح (قوم) ما هستند، و ما برای شما فضیلتی بر خود نمی‌بینیم، بلکه شما را دروغگو می‌پنداریم».');
INSERT INTO "hussein_taji" VALUES(11,28,'11:28','(نوح) گفت: «ای قوم من! به من خبر دهید، اگر من دلیل (و حجت) آشکاری از پروردگارم داشته باشم و از نزد خود رحمتی به من عطا کرده باشد، پس (آن) بر شما پوشیده مانده است، آیا می‌توانیم شما را به (پذیرش) آن اجبار کنیم، در حالی‌که شما از آن کراهت دارید؟!');
INSERT INTO "hussein_taji" VALUES(11,29,'11:29','و ای قوم من! بر (رساندن) این (دعوت) از شما مالی نمی‌طلبم، پاداش من تنها بر الله است، و من (هرگز) کسانی را که ایمان آورده‌اند، (از خود) طرد نمی‌کنم، همانا آن‌ها پروردگارشان را ملاقات خواهند کرد، و لیکن من شما را قومی نادان می‌بینم.');
INSERT INTO "hussein_taji" VALUES(11,30,'11:30','و ای قوم من! اگر من آن‌ها را طرد کنم، چه کسی من را در برابر (عقوبت) الله یاری می‌کند؟! آیا پند نمی‌گیرید؟!');
INSERT INTO "hussein_taji" VALUES(11,31,'11:31','و من به شما نمی‌گویم که گنج‌های الله نزد من است، و غیب هم نمی‌دانم، و نمی‌گویم که من فرشته‌ام، و (نیز) نمی‌گویم کسانی‌که در نظر شما حقیر می‌آیند، الله به آن‌ها خیری نخواهد داد، الله به آنچه در دل‌های آنان است آگاه‌تر است، (و اگر آن‌ها را طرد کنم) قطعاً آنگاه از ستمکاران خواهم بود».');
INSERT INTO "hussein_taji" VALUES(11,32,'11:32','(قوم) گفتند: «ای نوح! به راستی که با ما جدال (و بحث) کردی، و زیاد هم جدال و بحث کردی، پس اگر از راستگویانی آنچه را که به ما وعده می‌دهی؛ بیاور».');
INSERT INTO "hussein_taji" VALUES(11,33,'11:33','(نوح) گفت: «اگر الله بخواهد آن را برای شما می‌آورد، و شما (عاجزکننده نیستید و) از آن نتوانید گریخت.');
INSERT INTO "hussein_taji" VALUES(11,34,'11:34','و اگر الله بخواهد شما را گمراه سازد، و من بخواهم شما را نصیحت کنم، نصیحت من سودی برای شما نخواهد داشت، او پروردگار شماست، و (همه) به سوی او بازگردانده می‌شوید».');
INSERT INTO "hussein_taji" VALUES(11,35,'11:35','آیا (مشرکان) می‌گویند: (پیامبر) آن قرآن را بافته (و به الله نسبت داده)است؟ (ای پیامبر!) بگو: «اگر من آن را بافته باشم، پس گناهم بر عهدۀ من است، و من از گناهانی که شما می‌کنید؛ بیزارم».');
INSERT INTO "hussein_taji" VALUES(11,36,'11:36','و به نوح وحی شد که: از قوم تو جز آن‌هایی که ایمان آورده‌اند، دیگر هیچ کسی ایمان نخواهد آورد، پس بر آنچه که می‌کردند؛ غمگین مباش.');
INSERT INTO "hussein_taji" VALUES(11,37,'11:37','و (اکنون) به دیدگان ما و به وحی ما، کشتی را بساز، و در(بارۀ) کسانی‌که ستم کردند، با من سخن مگو، که آنان غرق شدنی هستند».');
INSERT INTO "hussein_taji" VALUES(11,38,'11:38','و (نوح) کشتی را می‌ساخت، و هرگاه گروهی از اشراف قومش بر او می‌گذشتند، او را مسخره می‌کردند، (نوح) می‌گفت: «اگر ما را مسخره می‌کنید، پس ما (نیز) شما را مسخره خواهیم کرد؛ همین گونه که مسخره می‌کنید،');
INSERT INTO "hussein_taji" VALUES(11,39,'11:39','پس به زودی خواهید دانست، چه کسی عذاب به سراغش می‌آید که خوارش سازد، و عذاب جاودان بر چه کسی وارد خواهد شد»..');
INSERT INTO "hussein_taji" VALUES(11,40,'11:40','(این حالت همچنان ادامه داشت) تا زمانی که فرمان فرا رسید، و (آب از) تنور جوشیدن گرفت (به نوح) گفتیم: «از هر جفتی (از حیوانات) دوتا (نر ماده) در آن (کشتی) برگیر، و (همچنین) خاندانت را؛ جز کسی‌که وعدۀ (هلاک) قبلاً بر او مقرر شده است، و (نیز) کسانی‌که ایمان آورده‌اند (را برگیر)» و جز عدۀ اندکی همراه او ایمان نیاوردند.');
INSERT INTO "hussein_taji" VALUES(11,41,'11:41','(نوح) گفت: «در آن (کشتی) سوار شوید، حرکت آن و توقف آن به نام الله است، همانا پروردگارم آمرزندۀ مهربان است».');
INSERT INTO "hussein_taji" VALUES(11,42,'11:42','و آن (کشتی) آن‌ها را در موجی همچون کوه‌ها می‌برد و در آن هنگام نوح فرزندش را که در کناره‌ای بود؛ صدا زد: «ای فرزندم‌! همراه ما سوار شو، و با کافران مباش».');
INSERT INTO "hussein_taji" VALUES(11,43,'11:43','گفت: «به زودی به کوهی پناه می‌برم که مرا از آب حفظ می‌کند!» (نوح) گفت: «امروز هیچ نگه دارنده‌ای در برابر فرمان الله نیست، مگر کسی‌که الله به او رحم کند» و (ناگاه) موج در میان آن دو حائل شد، پس (او) از غرق‌شدگان گردید.');
INSERT INTO "hussein_taji" VALUES(11,44,'11:44','و گفته شد: «ای زمین آبت را فرو بر، و ای آسمان (بارانت را) فرو بند. و آب فرو نشست، و کار به انجام رسید و (کشتی) بر (کوه) جودی قرار گرفت، و گفته شد: «دوری (و هلاکت) باد بر گروه ستمکاران».');
INSERT INTO "hussein_taji" VALUES(11,45,'11:45','و نوح پروردگارش را ندا داد، پس گفت: «پروردگارا! همانا پسرم از خاندان من است، و بی‌گمان وعدۀ تو (دربارۀ نجات خاندانم) حق است، و تو بهترین داورانی».');
INSERT INTO "hussein_taji" VALUES(11,46,'11:46','فرمود: «ای نوح! همانا او از خاندان تو نیست، بی‌گمان او عمل غیر صالحی است. پس چیزی که به آن علم نداری از من مخواه، من به تو اندرز می‌دهم (مبادا) که از جاهلان باشی».');
INSERT INTO "hussein_taji" VALUES(11,47,'11:47','(نوح) گفت: «پروردگارا! همانا من به تو پناه می‌برم، که از تو چیزی بخواهم که به آن علم ندارم، و اگر مرا نبخشی و بر من رحم نکنی، از زیان‌کاران خواهم بود».');
INSERT INTO "hussein_taji" VALUES(11,48,'11:48','گفته شد: «ای نوح! به سلامتی از سوی ما و (همراه با) برکت‌های ( فراوان) بر تو و بر امت‌های که با تواند؛ فرود آی، و امت‌هایی (نیز) هستند که به زودی آن‌ها را (از نعمت‌ها) بهرمند سازیم، سپس عذاب دردناکی از سوی ما به آن‌ها خواهد رسید».');
INSERT INTO "hussein_taji" VALUES(11,49,'11:49','این‌ها از خبرهای غیب است که آن را به تو (ای پیامبر) وحی می‌کنیم، نه تو آنها را پیش از این می‌دانستی و نه قوم تو؛ پس صبر کن، بی‌گمان عاقبت از آن پرهیزگاران است.');
INSERT INTO "hussein_taji" VALUES(11,50,'11:50','و (ما) به سوی قوم عاد برادرشان هود را فرستادیم، گفت: «ای قوم من! الله را بپرستید که معبودی جز او برای شما نیست، و شما جز افترا زن نیستید (که بت‌ها را شریک او قرار می‌دهید).');
INSERT INTO "hussein_taji" VALUES(11,51,'11:51','«ای قوم من! بر (رساندن) این (دعوت) از شما پاداشی نمی‌طلبم، پاداش من تنها بر (عهدۀ) ذاتی است که مرا آفرید، آیا خرد نمی‌ورزید؟!');
INSERT INTO "hussein_taji" VALUES(11,52,'11:52','و ای قوم من! از پروردگارتان طلب آمرزش کنید، سپس به سوی او باز گردید (و توبه کنید) تا (باران) آسمان را پی در پی بر شما بفرستد، و نیرویی بر نیروی‌تان بیفزاید، و گناهکارانه روی نگردانید».');
INSERT INTO "hussein_taji" VALUES(11,53,'11:53','گفتند: «ای هود! برای ما دلیل روشنی نیاورده‌ای و ما به (خاطر) گفتار تو معبودهای‌مان را رها نمی‌کنیم، و ما به تو ایمان نمی‌آوریم.');
INSERT INTO "hussein_taji" VALUES(11,54,'11:54','ما (چیزی) جز این نمی‌گویم که« بعضی از معبودان‌مان آسیبی به تو رسانده‌اند». (هود) گفت: «همانا من الله را گواه می‌گیرم، و شما (نیز) گواه باشید، که من از آنچه شریک (الله) قرار می‌دهید، بیزارم،');
INSERT INTO "hussein_taji" VALUES(11,55,'11:55','غیر از او، پس همگی برای من نقشه بکشید، آنگاه مرا مهلت ندهید.');
INSERT INTO "hussein_taji" VALUES(11,56,'11:56','همانا من بر الله که پروردگار من و پروردگار شماست توکل کردم، هیچ جنبنده‌ای نیست مگر آنکه او (= الله) گیرنده پیشانی‌اش است (و برآن چیره است) بی‌گمان پرودگارم بر راه راست است.');
INSERT INTO "hussein_taji" VALUES(11,57,'11:57','پس اگر روی برگردانید، به راستی که (من) آنچه را که با آن به سوی شما فرستاده شده بودم به شما رساندم، و پروردگارم گروه دیگری را جانشین شما خواهد کرد، و (شما) هیچ زیانی به او نمی‌توانید برسانید، بی‌گمان پروردگارم بر همه چیز نگهبان است».');
INSERT INTO "hussein_taji" VALUES(11,58,'11:58','و هنگامی‌که فرمان ما فرا رسید، هود و کسانی‌که با او ایمان آورده بودند، به رحمتی از (سوی) خویش نجات دادیم، و آن‌ها را از عذاب سخت رهاندیم.');
INSERT INTO "hussein_taji" VALUES(11,59,'11:59','و این (قوم) عاد بود که آیات پروردگارشان را انکار کردند، و از پیامبران او نافرمانی کردند، و از فرمان هر سرکش دشمن حق، پیروی کردند.');
INSERT INTO "hussein_taji" VALUES(11,60,'11:60','آن‌ها در این دنیا و (نیز) روز قیامت، لعنت به دنبال دارند، آگاه باشید که (قوم) عاد به پروردگارشان کفر ورزیدند، هان! بر عاد، قوم هود دوری (و هلاکت) باد.');
INSERT INTO "hussein_taji" VALUES(11,61,'11:61','و به سوی (قوم) ثمود برادرشان صالح را (فرستادیم) گفت: «ای قوم من! الله را بپرستید، که معبودی جز او برای شما نیست، او شما را از زمین پدید آورد، و شما را در آن به آباد کردنش گمارد، پس از او آمرزش بطلبید، سپس به سوی او بازگردید (و توبه کنید) همانا پروردگارم نزدیک (و) اجابت‌کننده است»');
INSERT INTO "hussein_taji" VALUES(11,62,'11:62','گفتند: «ای صالح! پیش از این ما به تو امید می‌داشتیم، آیا ما را از پرستش آنچه نیاکان‌مان می‌پرستیدند، باز می‌داری؟ و به راستی ما از آنچه ما را به سوی آن دعوت می‌کنی سخت در شک و تردید هستیم».');
INSERT INTO "hussein_taji" VALUES(11,63,'11:63','گفت: «ای قوم من! به من خبر دهید که اگر من دلیل (و حجت) آشکاری از پروردگارم داشته باشم، و از جانب خود رحمتی به من عطا کرده باشد، پس اگر نافرمانی او کنم، چه کسی مرا در برابر الله یاری می‌کند؟! پس شما جز زیان بر من نخواهید افزود.');
INSERT INTO "hussein_taji" VALUES(11,64,'11:64','ای قوم من! این ماده شترِ الله است، که برای شما نشانه‌ای است، پس بگذاریدش در زمین الله بچرد، و هیچ آزاری به آن نرسانید، که (آنگاه) عذابی نزدیک شما را فرا گیرد».');
INSERT INTO "hussein_taji" VALUES(11,65,'11:65','پس آن‌ها، آن (ماده شتر) را پی کردند، آنگاه (صالح به آن‌ها) گفت: «سه روز در خانه‌هایتان بهره‌مند گردید، (و بعد از آن عذاب خواهد آمد) این وعده‌ای است که دروغ نخواهد بود».');
INSERT INTO "hussein_taji" VALUES(11,66,'11:66','پس چون فرمان (عذاب) ما فرا رسید، صالح و کسانی‌که با او ایمان آورده بودند، به رحمتی از (سوی) خویش (از آن عذاب) و از رسوایی آن روز نجات دادیم، بی‌گمان پروردگار تو همان قوی پیروزمند است.');
INSERT INTO "hussein_taji" VALUES(11,67,'11:67','و کسانی‌که ستم کرده بودند، صیحه‌ای (= بانگی مرگ‌بار) فرو گرفت، پس در خانه‌های خود به روی افتادند و مردند.');
INSERT INTO "hussein_taji" VALUES(11,68,'11:68','چنان که گویی در آن (دیار) نبوده‌اند، آگاه باشید که (قوم) ثمود به پروردگارشان کفر می ‌ورزیدند، هان! بر (قوم) ثمود دوری (و هلاکت) باد.');
INSERT INTO "hussein_taji" VALUES(11,69,'11:69','به راستی فرستادگان ما با بشارت نزد ابراهیم آمدند، گفتند: «سلام»، (ابراهیم نیز در پاسخ) گفت: «سلام» پس طولی نکشید که گوساله‌ای بریان (برای آنان) آورد.');
INSERT INTO "hussein_taji" VALUES(11,70,'11:70','پس چون دید دستان‌شان به سوی آن نمی‌رسد (و نمی‌خورند) به آن‌ها گمان بد برد، و (در دل) از آنان احساس ترس کرد، (فرشتگان) گفتند: «نترس، بی‌شک ما به سوی قوم لوط فرستاده شده‌ایم».');
INSERT INTO "hussein_taji" VALUES(11,71,'11:71','و همسرش (ساره) ایستاده بود، پس (از خوشحالی) خندید، آنگاه او را به اسحاق، و بعد از او به یعقوب بشارت دادیم.');
INSERT INTO "hussein_taji" VALUES(11,72,'11:72','(ساره) گفت: «ای وای بر من! آیا من می‌زایم، در حالی‌که پیرزنم و این شوهرم (نیز) پیر است؟! به راستی این چیز عجیبی است!»');
INSERT INTO "hussein_taji" VALUES(11,73,'11:73','(فرشتگان) گفتند: «آیا از فرمان الله تعجب می‌کنی؟! رحمت الله و برکاتش بر شما اهل بیت باد، همانا او ستودۀ بزرگوار است».');
INSERT INTO "hussein_taji" VALUES(11,74,'11:74','پس چون ترس از (دل) ابراهیم (بیرون) رفت، و بشارت به او رسید، دربارۀ قوم لوط با ما مجادله (و گفت گو) شروع کرد.');
INSERT INTO "hussein_taji" VALUES(11,75,'11:75','بی‌گمان ابراهیم، بردبار، لا‌به گر بازگشت کننده (به سوی الله) بود.');
INSERT INTO "hussein_taji" VALUES(11,76,'11:76','ای ابراهیم! از این (سخن) بگذر، که به راستی فرمان (عذاب) پروردگارت فرا رسیده است، و مسلماً عذابی بدون برگشت برایشان خواهد آمد.');
INSERT INTO "hussein_taji" VALUES(11,77,'11:77','و چون فرستادگان ما نزد لوط آمدند، از (آمدن) آنان نگران و دل‌تنگ شد، و گفت: «این روز بسیار سختی است».');
INSERT INTO "hussein_taji" VALUES(11,78,'11:78','و قوم او شتابان به سوی او آمدند، و پیش از این کارهای زشت مرتکب می‌شدند، (لوط) گفت: «ای قوم من! این‌ها دختران من هستند، (با آن‌ها ازدواج کنید) آنان برای شما پاکیزه‌ترند، و از الله بترسید و مرا در (مورد) مهمانانم رسوا نکنید، آیا در میان شما یک مرد عاقلی وجود ندارد؟!».');
INSERT INTO "hussein_taji" VALUES(11,79,'11:79','گفتند: «به راستی تو می‌دانی که ما به دختران تو نیازی نداریم، و تو خوب می‌دانی که ما چه می‌خواهیم».');
INSERT INTO "hussein_taji" VALUES(11,80,'11:80','(لوط) گفت: «کاش در برابر شما قدرتی داشتم، و یا به تکیه گاه محکمی پناه می‌بردم».');
INSERT INTO "hussein_taji" VALUES(11,81,'11:81','(فرشتگان) گفتند: «ای لوط! ما فرستادگان، پروردگار تو هستیم، (آن‌ها) هرگز به تو نخواهند رسید، پس در پاسی از شب گذشته خانواده‌ات را (از این شهر) ببر، و هیچ یک از شما روی بر نگرداند جز همسرت، که به او خواهد رسید آنچه که به آن‌ها رسد، بی‌گمان وعده‌گاهشان (وقت) صبح است، آیا صبح نزدیک نیست؟!».');
INSERT INTO "hussein_taji" VALUES(11,82,'11:82','پس چون فرمان ما فرا رسید، آن (شهر و سرزمین) را زیر و رو کردیم، و بر (سر) آن‌ها سنگ‌های از سنگ و گِل متراکم روی هم فرو باریدیم.');
INSERT INTO "hussein_taji" VALUES(11,83,'11:83','(سنگهای که) نزد پروردگارت نشاندار بود، و این (سنگ‌ها و عذاب) از (سایر) ستمگران دور نیست.');
INSERT INTO "hussein_taji" VALUES(11,84,'11:84','و به سوی (قوم) مدین برادرشان شعیب را (فرستادیم). گفت: «ای قوم من! الله را بپرستید، که معبودی جز او برای شما نیست، و پیمانه و میزان را کم نکنید، من شما را در نعمت می‌بینم، و من بر شما از عذاب روز فراگیر می‌ترسم.');
INSERT INTO "hussein_taji" VALUES(11,85,'11:85','و ای قوم من! پیمانه و میزان را با عدالت، تمام (و کامل) دهید، و اشیاء مردم را کم ندهید، و در زمین به فساد (و تباهی) نکوشید.');
INSERT INTO "hussein_taji" VALUES(11,86,'11:86','اگر ایمان داشته باشید، باقی گذاشته الله (از مال حلال) برای شما بهتر است، و من بر شما نگهبان نیستم».');
INSERT INTO "hussein_taji" VALUES(11,87,'11:87','گفتند: «ای شعیب! آیا نمازت به تو فرمان می‌دهد که آنچه را که نیاکان‌مان می‌پرستیدند، رها کنیم، یا آنچه را می‌خواهیم در اموال‌مان انجام دهیم؟! بی‌گمان تو (مردی) بردبار عاقلی هستی!».');
INSERT INTO "hussein_taji" VALUES(11,88,'11:88','(شعیب) گفت: «ای قوم من! به من خبر دهید؛ اگر من دلیل (و حجت) آشکاری از پروردگارم داشته باشم، و از سوی خود رزق نیکوی به من عطا کرده باشد، (آیا نباید از او اطاعت کنم؟!)و من نمی‌خواهم با شما مخالفت کنم، دربارۀ چیزی که شما را از آن نهی می‌کنم، (و خود مرتکب شوم) من جز اصلاح؛ - تا آنجا که توانای دارم- نمی‌خواهم، و توفیق من جز به (فضل) الله نیست، بر او توکل کردم، و به سوی او باز می‌گردم.');
INSERT INTO "hussein_taji" VALUES(11,89,'11:89','وای قوم من! دشمنی (و مخالفت) با من سبب نشود که به شما (نیز، عذابی) برسد، همانند آنچه که به قوم نوح یا قوم هود، یا قوم صالح رسید، و قوم لوط از شما چندان دور نیست.');
INSERT INTO "hussein_taji" VALUES(11,90,'11:90','و از پروردگارتان آمرزش بطلبید، سپس به سوی او باز گردید (و توبه کنید) همانا پروردگارم مهربان دوستدار است».');
INSERT INTO "hussein_taji" VALUES(11,91,'11:91','گفتند: «ای شعیب! بسیاری از آنچه را که می‌گویی، نمی‌فهمیم، و به درستی که ما تو را در میان خود ناتوان می‌بینیم، و اگر (احترام) قبیله‌ات نبود؛ مسلماً تو را سنگسار می‌کردیم، و تو نزد ما گرانقدر نیستی»');
INSERT INTO "hussein_taji" VALUES(11,92,'11:92','(شعیب) گفت: «ای قوم من! آیا قبیلۀ من نزد شما گرامی‌تر از الله است؟! و (فرمان) او را پشت سر خویش انداخته‌اید! بی‌گمان پروردگارم به آنچه انجام می‌دهید احاطه دارد.');
INSERT INTO "hussein_taji" VALUES(11,93,'11:93','و ای قوم من! شما به روش خودتان عمل کنید، من هم (به روش خود) عمل می‌کنم، به زودی خواهید دانست، چه کسی عذاب به سراغ‌شان می‌آید که رسوایش کند، و چه کسی خود دروغگو است! شما انتظار بکشید و من (نیز) با شما در انتظارم!»');
INSERT INTO "hussein_taji" VALUES(11,94,'11:94','و چون فرمان ما فرا رسید، شعیب و کسانی را که با او ایمان آورده بودند، به رحمتی از (سوی) خود نجات دادیم و کسانی را که ستم کردند؛ صیحه‌ای (= بانگی مرگبار) فرو گرفت، پس در دیار خود به رو افتادند (و مردند).');
INSERT INTO "hussein_taji" VALUES(11,95,'11:95','چنان که گویی هرگز در آن (دیار) نبودند! آگاه باشید! دوری (و هلاکت) باد بر (قوم) مدین، همان گونه که (قوم) ثمود دور (و هلاک) شدند!');
INSERT INTO "hussein_taji" VALUES(11,96,'11:96','و به راستی موسی را با آیات خود و دلیلی آشکار فرستادیم.');
INSERT INTO "hussein_taji" VALUES(11,97,'11:97','به سوی فرعون و اطرافیانش؛ پس (آن‌ها) از فرمان فرعون پیروی کردند، و فرمان فرعون درست (و بخردانه) نبود.');
INSERT INTO "hussein_taji" VALUES(11,98,'11:98','(فرعون) روز قیامت پیش‌رو قومش است، پس آن‌ها را وارد آتش (جهنم) کرد، و (جهنم) چه بد آبشخور (و جایگاهی) است که بر آن وارد شدند.');
INSERT INTO "hussein_taji" VALUES(11,99,'11:99','و در این (دنیا) و روز قیامت لعنتی به دنبال دارند، چه بد عطایی است (لعنت) که (به آن‌ها) داده شد.');
INSERT INTO "hussein_taji" VALUES(11,100,'11:100','این (برخی) از اخبار (آبادی‌ها و) شهرهاست که آن را برای تو باز گو می‌کنیم، که برخی از آن‌ها (هنوز) باقی است، و برخی (دیگر) درو شده (و از بین رفته) است.');
INSERT INTO "hussein_taji" VALUES(11,101,'11:101','و (ما) به آن‌ها ستم نکردیم، و لیکن آن‌ها خود بر خویشتن ستم کردند، پس هنگامی‌که فرمان پروردگارت فرا رسید، معبودان شان که به جای الله می‌خواندند؛ پس چیزی از آنان دفع نکردند (و یاری‌شان ندادند) و آن‌ها را جز هلاکت (و نابودی) نیفزودند.');
INSERT INTO "hussein_taji" VALUES(11,102,'11:102','و این چنین است (مجازات و) گرفت پروردگارت، هنگامی‌که فرو گیرد و مجازات کند آبادی‌ها و شهرها را که (مردمش) ستمکار باشند، بی‌گمان (مجازات و) گرفت او دردناک سخت است.');
INSERT INTO "hussein_taji" VALUES(11,103,'11:103','یقیناً در این، (پند و) نشانه‌ای است برای کسی‌که از عذاب آخرت بترسد، آن (روز) روزی است که مردم (همه) در آن گرد آورده شوند، و آن (روز) روزی است که (همگی) حاضر شوند.');
INSERT INTO "hussein_taji" VALUES(11,104,'11:104','و ما آن را جز تا مدتی معین به تأخیر نمی‌اندازیم.');
INSERT INTO "hussein_taji" VALUES(11,105,'11:105','روزی بیاید که هیچ کس جز به اذن او سخن نگوید، پس بعضی از آن‌ها بدبختند، و (بعضی دیگر) خوشبخت.');
INSERT INTO "hussein_taji" VALUES(11,106,'11:106','اما کسانی‌که بدبخت شدند، پس در آتش (دوزخ)اند، برای آن‌ها در آن ناله و فریاد (= دم و بازدم) است.');
INSERT INTO "hussein_taji" VALUES(11,107,'11:107','در آن جاودانه خواهند ماند، تا آسمان‌ها و زمین (باقی) است، مگر آنچه پروردگارت بخواهد، بی‌گمان پروردگارت آنچه را که بخواهد؛ انجام دهد.');
INSERT INTO "hussein_taji" VALUES(11,108,'11:108','و اما کسانی‌که خوشبخت (و سعادتمند) شدند، پس جاودانه در بهشت خواهند ماند، تا آسمان‌ها و زمین (باقی) است. مگر آنچه پروردگارت بخواهد، عطایی است قطع نشدنی.');
INSERT INTO "hussein_taji" VALUES(11,109,'11:109','پس از (باطل بودن) آنچه اینان می‌پرستند در شک مباش. آن‌ها نمی‌پرستند جز همان‌گونه که نیاکان‌شان از پیش می‌پرستیدند، و مسلماً ما نصیب آنان را بی کم و کاست خواهیم داد.');
INSERT INTO "hussein_taji" VALUES(11,110,'11:110','و به راستی (ما) به موسی کتاب دادیم، پس در آن اختلاف شد، و اگر فرمان پیشین پروردگارت نبود، قطعاً در میان آنان داوری می‌شد، و همانا آن‌ها در این (قرآن) سخت در شک و تردید هستند.');
INSERT INTO "hussein_taji" VALUES(11,111,'11:111','و بی‌گمان هر کدام (چون بر انگیخته شوند) پروردگارت (پاداش) اعمال‌شان را قطعاً بطور کامل و تمام خواهد داد، زیرا که او به آنچه می‌کنند؛ آگاه است.');
INSERT INTO "hussein_taji" VALUES(11,112,'11:112','پس (ای پیامبر!) همان‌گونه که فرمان یافته‌ای، (در برابر مشرکان) استقامت کن، و (نیز) کسی‌که با تو (به الله) رو آورده است (باید استقامت کند) و سرکشی نکنید، بی‌گمان او به آنچه که می‌کنید؛ بیناست.');
INSERT INTO "hussein_taji" VALUES(11,113,'11:113','به کسانی‌که ستم کردند تمایل (و تکیه) نکنید، که آتش (جهنم) به شما خواهد رسید، و برای شما (سرپرست و) دوستانی جز الله وجود ندارد، آنگاه یاری نمی‌شوید.');
INSERT INTO "hussein_taji" VALUES(11,114,'11:114','و (ای پیامبر!) در دو طرف روز، و ساعاتی از شب، نماز را بر پادار، بی‌شک نیکی‌ها بدی‌ها را از بین می‌برند، این پندی برای پند پذیران است.');
INSERT INTO "hussein_taji" VALUES(11,115,'11:115','و (ای پیامبر!) صبر کن، پس بی‌گمان الله پاداش نیکو کاران را ضایع نخواهد کرد.');
INSERT INTO "hussein_taji" VALUES(11,116,'11:116','پس چرا از امت‌هایی پیش از شما، خردمندانی نبودند که (مردمان را) از فساد در زمین باز دارند؟! مگر اندکی از آنان که نجات‌شان دادیم، و کسانی‌که ستم کردند، از لذات (دنیوی) و آسودگی پیروی کردند، و گناه‌کار بودند (و نابود شدند).');
INSERT INTO "hussein_taji" VALUES(11,117,'11:117','و (ای پیامبر!) پروردگارت چنین نبود که شهرها (و آبادی‌ها) را به ظلم نابود کند، در حالی‌که اهل آن (افرادی شایسته و) نیکوکار باشند.');
INSERT INTO "hussein_taji" VALUES(11,118,'11:118','و اگر پروردگارت می‌خواست، (همۀ) مردم را یک امت قرار می‌داد، ولی (آن‌ها) همواره مختلف‌اند.');
INSERT INTO "hussein_taji" VALUES(11,119,'11:119','مگر کسی‌که پروردگارت (بر او) رحم کند، و برای همین آن‌ها را آفریده است، و سخن پروردگار تو محقق شد که: مسلماً جهنم را از همۀ (عاصیان و مجرمان) جن و انس پر خواهم کرد.');
INSERT INTO "hussein_taji" VALUES(11,120,'11:120','و ما هر یک از اخبار پیامبران را بر تو باز گو کردیم تا بوسیلۀ آن، دلت را استوار گردانیم، و در این (قرآن) برای تو حق، و برای مؤمنان موعظه و تذکری آمده است.');
INSERT INTO "hussein_taji" VALUES(11,121,'11:121','و به کسانی‌که ایمان نمی‌آورند، بگو: «بر روش خودتان عمل کنید، ما (نیز بر روش خود) عمل می‌کنیم،');
INSERT INTO "hussein_taji" VALUES(11,122,'11:122','و منتظر باشید، ما هم منتظریم!».');
INSERT INTO "hussein_taji" VALUES(11,123,'11:123','و (آگاهی از) غیب آسمان‌ها و زمین، از آن الله است، و همۀ کارها به سوی او باز گردانده می‌شود، پس او را بپرست و بر او توکل کن، و پروردگارت از آنچه می‌کنید؛ (هرگز) غافل نیست.');
INSERT INTO "hussein_taji" VALUES(12,1,'12:1','الر (الف. لام. را) این آیات کتاب روشنگر است.');
INSERT INTO "hussein_taji" VALUES(12,2,'12:2','همانا ما آن را قرآن عربی نازل کردیم؛ شاید شما دریابید.');
INSERT INTO "hussein_taji" VALUES(12,3,'12:3','(ای پیامبر!) ما بهترین داستان‌ها را با وحی کردن این قرآن بر تو بازگو می‌کنیم، و مسلماً تو پیش از آن از بی‌خبران بودی (و آگاهی نداشتی).');
INSERT INTO "hussein_taji" VALUES(12,4,'12:4','(به یاد آور) هنگامی را که یوسف به پدرش یعقوب گفت: «پدرم! همانا من (در خواب) یازده ستاره و خورشید و ماه را دیدم، آن‌ها را برای خود سجده‌کنان دیدم».');
INSERT INTO "hussein_taji" VALUES(12,5,'12:5','(یعقوب) گفت: «ای پسرکم! خواب خود را برای برادرانت باز‌گو نکن، که برای تو نقشۀ بدی می‌اندیشند، بی‌گمان شیطان برای انسان دشمن آشکار است.');
INSERT INTO "hussein_taji" VALUES(12,6,'12:6','و این گونه پروردگارت تو را بر می‌گزیند، و از تأویل احادیث (= تعبیر خواب‌ها) می‌آموزد، و نعمتش را بر تو و بر آل یعقوب تمام می‌کند، همان گونه که پیش از این بر اجدادت ابراهیم و اسحاق تمام کرد، یقیناً پروردگارت دانای حکیم است».');
INSERT INTO "hussein_taji" VALUES(12,7,'12:7','بی‌شک در (داستان) یوسف و برادرانش برای سوال کننده‌گان نشانه‌ها و (عبرت‌ها) است.');
INSERT INTO "hussein_taji" VALUES(12,8,'12:8','هنگامی‌که با خود گفتند: «یوسف و برادرش (بنیامین) نزد پدر‌مان از ما محبوب‌تر اند، در حالی‌که ما گروهی (نیرومند) هستیم، بی‌تردید پدرمان در اشتباهی آشکار است.');
INSERT INTO "hussein_taji" VALUES(12,9,'12:9','یوسف را بکشید، یا او را به سرزمینی (دور دست) بیفکنید، تا توجه پدر‌تان فقط به (سوی) شما باشد، و بعد از آن (توبه کنید و) افرادی صالح باشید».');
INSERT INTO "hussein_taji" VALUES(12,10,'12:10','یکی از آن‌ها گفت: «یوسف را نکشید، و اگر می‌خواهید (کاری) انجام دهید، او را به قعر چاه بیندازید، تا بعضی از کاروان‌های (ره‌گذر) او را بر گیرد (و به جای دوری ببرد)».');
INSERT INTO "hussein_taji" VALUES(12,11,'12:11','(و آنگاه نزد پدر آمدند) و گفتند: «پدرجان! تو را چه شده است که ما را بر (برادرمان) یوسف امین نمی‌شماری، در حالی‌که ما خیر خواه او هستیم؟!');
INSERT INTO "hussein_taji" VALUES(12,12,'12:12','فردا او را با ما (به صحرا) بفرست، تا (از میوه‌ها) بهره برد و بازی کند، همانا ما نگهبان او هستیم».');
INSERT INTO "hussein_taji" VALUES(12,13,'12:13','(یعقوب) گفت: «آنکه او را ببرید مرا اندوهگین می‌کند، و از این می‌ترسم که گرگ او را بخورد، در حالی‌که شما از او غافل باشید!».');
INSERT INTO "hussein_taji" VALUES(12,14,'12:14','گفتند: «با این‌که ما گروهی (نیرومند) هستیم، اگر گرگ او را بخورد، براستی آنگاه ما زیان‌کار خواهیم بود».');
INSERT INTO "hussein_taji" VALUES(12,15,'12:15','پس چون او را با خود بردند، و تصمیم گرفتند که او را در قعر چاه قرار دهند، و (تصمیم خود را عملی نمودند) به او وحی کردیم که قطعاً آن‌ها را از این کار‌شان آگاه خواهی کرد، در حالی‌که آن‌ها نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(12,16,'12:16','و (برادران) شبانگاه گریان به نزد پدر‌شان آمدند.');
INSERT INTO "hussein_taji" VALUES(12,17,'12:17','گفتند: «ای پدر‌ ما! همانا ما رفتیم که مسابقه دهیم، و یوسف را نزد اثاث خود گذاشتیم، پس گرگ او را خورد، و تو (هرگز سخن) ما را باور نخواهی کرد، هر چند راستگو باشیم».');
INSERT INTO "hussein_taji" VALUES(12,18,'12:18','و پیراهن او را با خونی دروغین (برای یعقوب) آوردند، (او) گفت: «(چنین نیست) بلکه (هوای) نفس شما کاری (ناشایست) را برای شما آراسته است، پس (کار من) صبر جمیل است، و بر آنچه می‌گویید، الله مدد‌کار (من) است».');
INSERT INTO "hussein_taji" VALUES(12,19,'12:19','و کاروانی فرا رسید، و پس آن‌ها آب‌آورشان را فرستادند، و (او) دلو خود را در (چاه) انداخت (پس چون بیرون آورد، یوسف را در آن دید، صدا زد و) گفت: «مژده باد! این کودکی است» و او را بعنوان یک کالا (از دیگران) پنهان داشتند، و الله به آنچه می‌کردند، آگاه است.');
INSERT INTO "hussein_taji" VALUES(12,20,'12:20','و (سر انجام) او را به بهای اندک – چند درهم – فروختند، و در(بارۀ) او بی‌رغبت بودند.');
INSERT INTO "hussein_taji" VALUES(12,21,'12:21','و کسی از (اهل) مصر که او را خرید؛ به همسرش گفت: «جای او را گرامی دار، امید است برای ما سودمند باشد، یا او را به فرزندی گیریم» و این چنین یوسف را در (آن) سرزمین متمکن ساختیم، و تا از تأویل احادیث (= تعبیر خواب) به او بیاموزیم، و الله بر کارش چیره است، و لیکن بیشتر مردم نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(12,22,'12:22','و چون (یوسف به مرحلۀ بلوغ و) کمال قوت رسید، به او حکم (= نبوت) و علم عطا کردیم. و این چنین نیکو‌کاران را پاداش می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(12,23,'12:23','و آن زنی که او (یوسف) در خانه‌اش بود، از او در خواست کام‌جویی کرد، و در‌ها را بست و گفت: «بیا، (در اختیار تو هستم)». (یوسف) گفت: «به الله پناه می‌برم! بی‌گمان او (عزیز مصر) سرور من است، جایگاه مرا گرامی داشته است (پس چگونه به او خیانت کنم؟!) مسلماً ستمکاران رستگار نمی‌شوند».');
INSERT INTO "hussein_taji" VALUES(12,24,'12:24','و به راستی آن زن قصد او (= یوسف) کرد، و او نیز اگر برهان پروردگارش ندیده بود؛ قصد وی می‌کرد، این چنین (کردیم) تا بدی و فحشا از او دور سازیم، بی‌گمان او از بندگان مخلص ماست.');
INSERT INTO "hussein_taji" VALUES(12,25,'12:25','و هر‌ دو به طرف در شتافتند، و (همسر عزیز) پیراهن او را از پشت پاره کرد، و (در این هنگام) شوهرش را نزد در یافتند. (آن) زن گفت: «کیفر کسی‌که خواسته باشد به خانوادۀ تو بدی (و خیانت) کند چیست؟، جز اینکه زندان شود، یا عذاب درد‌‌‌‌‌ناکی (ببیند).');
INSERT INTO "hussein_taji" VALUES(12,26,'12:26','(یوسف) گفت: «او با اصرار مرا به سوی خود خوانده است!» و (درآن هنگام) شاهدی از بستگان آن زن شهادت داد، اگر پیراهن او از جلو پاره شده باشد، پس آن زن راست می‌گوید، و او از دروغ‌گویان است.');
INSERT INTO "hussein_taji" VALUES(12,27,'12:27','و اگر پیراهن‌اش از پشت پاره شده باشد، (آن) زن دروغ می‌گوید، و او از راستگویان است».');
INSERT INTO "hussein_taji" VALUES(12,28,'12:28','پس چون (عزیز مصر) دید که پیراهن او از پشت پاره شده است، گفت: «این از مکر شما (زنان) است، بی‌گمان مکر شما زنان بزرگ است.');
INSERT INTO "hussein_taji" VALUES(12,29,'12:29','ای یوسف! از این (ماجرا) صرف نظر کن، و (تو ای زن) برای گناه خود آمرزش بخواه، همانا تو از خطا‌کاران بوده‌ای».');
INSERT INTO "hussein_taji" VALUES(12,30,'12:30','و (این خبر به شهر رسید) زنانی در شهر گفتند: «همسر عزیز، برای کام‌جویی غلام (جوان) خود را به سوی خود می‌خواند، به راستی محبت (و عشق این جوان) در قلبش نفوذ کرده است، مسلماً ما او را در گمراهی آشکار می‌بینیم.');
INSERT INTO "hussein_taji" VALUES(12,31,'12:31','پس چون (همسر عزیز) سخنان مکرآمیز ‌شان را شنید، (کسی را) به دنبال آن‌ها فرستاد (و آن‌ها را دعوت نمود) و برای آن‌ها مجلس (با شکوه و با پشتی‌های زیبا) فراهم کرد، و به (دست) هر کدام چاقویی (برای بریدن میوه) داد، و به یوسف گفت: «بر آنان بیرون شو». پس زنان چون او را دیدند، بزرگش یافتند، و دست‌های خود را بریدند، و «گفتند: پناه بر الله! این بشر نیست، این جز فرشته‌ای بزرگوار نیست».');
INSERT INTO "hussein_taji" VALUES(12,32,'12:32','(همسر عزیز) گفت: «این هست همان کسی‌که مرا در(بارۀ) او سرزنش کردید، (آری) به راستی من او را برای کام‌جویی به خود دعوت کردم، پس او خود داری کرد، و اگر آنچه را که به او دستور می‌دهم، انجام ندهد؛ قطعاً زندانی می‌شود و مسلماً خوار و زبون خواهد بود».');
INSERT INTO "hussein_taji" VALUES(12,33,'12:33','(یوسف) گفت: «پروردگارا! زندان نزد من از آنچه (این‌ها) مرا به سوی آن می‌خوانند، محبوب‌تر است، و اگر مکر آن‌ها را از من نگردانی، به سوی آنان متمایل می‌شوم، و از نادانان خواهم بود».');
INSERT INTO "hussein_taji" VALUES(12,34,'12:34','پس پروردگارش (دعای) او را اجابت کرد، و مکر آنان را از او باز گردانید، بی‌گمان او شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(12,35,'12:35','سپس بعد از آنکه نشانه‌های (پاکی یوسف) را دیدند؛ تصمیم گرفتند تا مدتی او را زندانی کنند.');
INSERT INTO "hussein_taji" VALUES(12,36,'12:36','و دو جوان همراه او وارد زندان شدند، یکی از آن دو گفت: «من خواب دیده‌ام که (انگور برای) شراب می‌فشارم» و دیگری گفت: «من خواب دیده ام که نان بر سر حمل می‌کنم، پرندگان از آن می‌خورند، ما را از تعبیر آن آگاه کن، بی‌گمان ما تو را از نیکو‌کاران می‌بینیم».');
INSERT INTO "hussein_taji" VALUES(12,37,'12:37','(یوسف) گفت: «پیش از آنکه (جیره) طعام شما را بیاورند و تناول کنید، شما را از تعبیر خواب‌تان آگاه می‌سازم، این (تعبیر خواب) چیزهای است که پروردگارم به من آموخته است، همانا من کیش قومی را که به الله ایمان نمی‌آورند، و آنان به (سرای) آخرت (نیز) کافرند، ترک کرده ام.');
INSERT INTO "hussein_taji" VALUES(12,38,'12:38','و از کیش نیاکانم ابراهیم و اسحاق و یعقوب پیروی کرده‌ام، برای ما سزاوار نیست که چیزی را شریک الله قرار دهیم، این از فضل الله بر ما و بر (همه) مردم است، و لیکن بیشتر مردم شکر نمی‌گذارند.');
INSERT INTO "hussein_taji" VALUES(12,39,'12:39','ای رفقای زندانی من! آیا معبودان پراکنده (متعدد) بهترند یا الله یکتای قهار؟');
INSERT INTO "hussein_taji" VALUES(12,40,'12:40','شما به جای الله (چیزی را) نمی‌پرستید، مگر نام‌هایی را که خود و نیاکان‌تان به آن‌ها داده‌اید، الله هیچ دلیلی بر (اثبات) آن‌ها نازل نکرده است [ مطلب این است که خود شما آن‌ها را معبود پنداشته‌اید در حالی‌که نه آن‌ها معبودی هستند و نه الله متعال دلیلی بر اثبات آن‌ها نازل کرده است. مطلب دیگر این است که این نام‌هایی که برای معبودان اختصاص داده‌اید مانند: غریب‌نواز، گنج بخش، شکر گنج و غیره همه خود ساختۀ خود شماست و از طرف الله دلیلی بر اثبات آن‌ها نازل نشده است.]، فرمانروایی تنها از آنِ الله است، فرمان داده است که جز او را نپرستید، این است دین راست و استوار، و لیکن بیشتر مردم نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(12,41,'12:41','ای رفقای زندانی من! اما یکی از شما (آزاد می‌شود) پس سرور خویش را شراب خواهد نوشانید، و اما دیگری پس به دار آویخته می‌شود، آنگاه پرندگان از (گوشت) سر او خواهند خورد، امری که در(بارۀ) آن از من نظر خواستید، (چنین) مقدر شده است».');
INSERT INTO "hussein_taji" VALUES(12,42,'12:42','و (یوسف) به آن کسی از دو نفر که دانست رهایی می‌یابد، گفت: «مرا نزد سرورت (= حاکم) یاد کن». ولی شیطان یاد کردن (او را نزد) سرورش از خاطر او برد، پس (یوسف) چند سال در زندان باقی ماند.');
INSERT INTO "hussein_taji" VALUES(12,43,'12:43','و پادشاه گفت: «همانا من (در خواب) هفت گاو چاق را دیدم که هفت گاو لاغر آن‌ها را می‌خورند، و هفت خوشۀ سبز، و (هفت خوشۀ) دیگر را خشک (می بینم). ای بزرگان! در(بارۀ) خوابم نظر دهید؛ اگر خواب را تعبیر می‌کنید.');
INSERT INTO "hussein_taji" VALUES(12,44,'12:44','«(این‌ها) خواب‌های (آشفته و) پریشان است، و ما از تعبیر خواب‌های پریشان آگاه نیستیم».');
INSERT INTO "hussein_taji" VALUES(12,45,'12:45','و یکی از آن دو نفر که نجات یافته بود، بعد از مدتی (یوسف) را به یاد آورد، گفت: «من شما را از تأویل (= تعبیر) آن خبر می‌دهم، پس مرا (به سوی جوان زندانی) بفرستید».');
INSERT INTO "hussein_taji" VALUES(12,46,'12:46','(چون نزد یوسف آمد، گفت:) «یوسف، ای (مرد) راستگو! در(بارۀ این خواب که:) هفت گاو چاق، هفت گاو لاغر آن‌ها را می‌خورند، و هفت خوشۀ سبز و (هفت خوشۀ) دیگر خشک، برای ما نظر بده (و تعبیر کن) تا من به سوی مردم باز گردم، شاید آنان (از تعبیر این خواب) با خبر شوند».');
INSERT INTO "hussein_taji" VALUES(12,47,'12:47','(یوسف) گفت: «هفت سال پی درپی (با جدیت) زراعت کنید، پس آنچه را درو کردید، جز اندکی که می‌خورید، (بقیه) در خوشه‌اش باقی بگذارید.');
INSERT INTO "hussein_taji" VALUES(12,48,'12:48','سپس بعد از آن هفت (سال) سخت (قحطی) می‌آید، که آنچه را برای آن سال‌ها ذخیره کرده‌اید، می‌خورید، جز اندکی از آنچه که (برای بذر) ذخیره خواهید کرد.');
INSERT INTO "hussein_taji" VALUES(12,49,'12:49','سپس بعد از آن هفت (سال) سالی فرا می‌رسد که باران فراوانی نصیب مردم می‌شود، و درآن (سال، مردم افشردنی‌ها را) می‌فشارند».');
INSERT INTO "hussein_taji" VALUES(12,50,'12:50','و پادشاه (چون این تعبیر را شنید) گفت: «او را نزد من بیاورید». پس چون فرستادۀ (پادشاه) نزد او آمد، (یوسف) گفت: «به سوی سرورت باز گرد، پس از او بپرس ماجرای زنانی که دست‌های خود را بریدند چه بود؟! قطعاً پروردگارم به نیرنگ آن‌ها آگاه است».');
INSERT INTO "hussein_taji" VALUES(12,51,'12:51','(پادشاه آن زنان را خواست و) گفت: «جریان کارتان چه بود؛ هنگامی‌که یوسف را به سوی خود دعوت دادید؟!». گفتند: «پناه بر الله! ما هیچ گناهی بر او ندانسته‌ایم» (در این هنگام) همسرعزیز گفت: «اکنون حق آشکار شد، من (بودم که) او را به سوی خود خواندم، (و خواهش مرا رد کرد) و بی‌گمان او از راستگویان است.');
INSERT INTO "hussein_taji" VALUES(12,52,'12:52','این (سخن) بدین خاطر است تا (عزیز) بداند که من در پنهان به او خیانت نکرده‌ام، و اینکه الله مکر خائنان را به هدف نمی‌رساند.');
INSERT INTO "hussein_taji" VALUES(12,53,'12:53','من هرگز نفس خود را تبرئه نمی‌کنم، بی‌شک نفس (اماره، انسان) پیوسته به بدی فرمان می‌دهد، مگر آنچه را پروردگارم رحم کند، بی‌گمان پروردگارم آمرزندۀ مهربان است».');
INSERT INTO "hussein_taji" VALUES(12,54,'12:54','و پادشاه گفت: «او (= یوسف) را نزد من بیاورید، (تا) او را مخصوص خود گردانم» پس چون (یوسف نزد او آمد و) با او صحبت کرد، (پادشاه) گفت: «تو امروز نزد ما صاحب مقام والا (و) امین هستی».');
INSERT INTO "hussein_taji" VALUES(12,55,'12:55','(یوسف) گفت: «مرا بر (سر‌‌‌‌‌پرستی) خزائن زمین (مصر) قرار بده، که من نگهدارندۀ آگاهم».');
INSERT INTO "hussein_taji" VALUES(12,56,'12:56','و این گونه به یوسف در آن سرزمین تمکن (و قدرت) دادیم، از آن (سرزمین) هر جا که می‌خواست منزل می‌گرفت، ما رحمت خود را به هرکس که بخواهیم می‌رسانیم، و پاداش نیکوکاران را ضایع نمی‌کنیم.');
INSERT INTO "hussein_taji" VALUES(12,57,'12:57','و یقیناً پاداش آخرت، برای کسانی‌که ایمان آوردند و پرهیز‌گاری می‌کردند، بهتر است.');
INSERT INTO "hussein_taji" VALUES(12,58,'12:58','و (چون سرزمین کنعان را خشک سالی فرا گرفت) برادران یوسف (برای تهیۀ گندم به مصر) آمدند، پس بر او وارد شدند، آنگاه او آنان را شناخت، در حالی‌که آن‌ها او را نشناختند.');
INSERT INTO "hussein_taji" VALUES(12,59,'12:59','پس چون بار‌هایشان را آماده کرد، (به آنان) گفت: «آن برادری که از پدرتان دارید، (نوبت آینده) نزد من بیاورید، آیا نمی‌بینید که من پیمانه را تمام می‌دهم، و من بهترین میزبان هستم؟');
INSERT INTO "hussein_taji" VALUES(12,60,'12:60','پس اگر او را نزد من نیاوردید، نه پیمانۀ نزد من خواهید داشت، و نه به من نزدیک شوید».');
INSERT INTO "hussein_taji" VALUES(12,61,'12:61','گفتند: «ما در (بار‌ۀ) او با پدرش با اصرار گفتگو خواهیم کرد، و مسلماً این کار را می‌کنیم».');
INSERT INTO "hussein_taji" VALUES(12,62,'12:62','و (یوسف) به غلامانش گفت: «سرمایه‌شان را در بار‌هایشان بگذارید، شاید هنگامی‌که به سوی خانوادۀ خود، باز گشتند، آن را بشناسند، شاید باز آیند».');
INSERT INTO "hussein_taji" VALUES(12,63,'12:63','پس چون به سوی پدر‌شان بازگشتند: « گفتند: ای پدرما! پیمانه (غله) از ما باز داشته شده است، پس برادرمان (بنیامین) را با ما بفرست تا سهمی (از غله) بگیریم، و بی‌گمان ما نگهبان او خواهیم بود».');
INSERT INTO "hussein_taji" VALUES(12,64,'12:64','(یعقوب) گفت: «آیا شما را بر او امین دانم، همان‌گونه که پیش از این نسبت به برادرش (یوسف) امین داشتم، پس الله بهترین نگهبان است، و او مهربان‌ترین مهربانان است».');
INSERT INTO "hussein_taji" VALUES(12,65,'12:65','و چون بار خود را گشودند، سرمایه‌شان را یافتند که به آن‌ها بازگردانده شده است. گفتند: «ای پدر ما! (ما دیگر) چه می‌خواهیم؟ این سرمایه ماست که به ما باز گردانده شده است، (پس او را با ما بفرست) و برای خانوادۀ خود آذوقه می‌آوریم، و برادرمان را حفظ می‌کنیم، و یک بار شتر افزون خواهیم آورد؛ این پیمانه (برای عزیز مصر، کار) آسانی است.');
INSERT INTO "hussein_taji" VALUES(12,66,'12:66','(یعقوب) گفت: «هرگز او را با شما نمی‌فرستم؛ تا آنکه به نام الله به من پیمان دهید که حتماً او را به نزد من باز خواهید آورد، مگر اینکه قدرت از شما گرفته شود (و خود‌‌‌تان گرفتار آیید) پس چون (تعهد و) پیمان استوار به او دادند، (یعقوب) گفت: «الله بر آنچه می‌گوییم نگهبان است».');
INSERT INTO "hussein_taji" VALUES(12,67,'12:67','و (همچنین به آن‌ها) گفت: «ای پسران من! از یک در وارد نشوید، بلکه از درهای متفرق داخل شوید، و (من) نمی‌توانم چیزی از (قضای) الله (که مقرر کرده است) از شما دفع کنم، حکم تنها از آن الله است، بر او توکل کرده‌ام، و (همۀ) متوکلان بر او توکل کنند.');
INSERT INTO "hussein_taji" VALUES(12,68,'12:68','و چون به همان گونه که پدرشان به آن‌ها دستور داده بود، داخل شدند، ـ (این کار) نمی‌توانست چیزی از (قضای) الله را از آنان دفع کند ــ جز حاجتی (و خواهشی) که در دل یعقوب بود که آن را برآورده کرد، و بی‌گمان او علمی داشت که ما به آموخته بودیم، و لیکن بیشتر مردم نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(12,69,'12:69','و چون (برادران) بر یوسف وارد شدند، برادرش (بنیامین) را نزد خود جای داد، (و) گفت: «بدون شک من برادر تو (یوسف) هستم، پس از آنچه آن‌ها انجام می‌دادند، اندوهگین مباش».');
INSERT INTO "hussein_taji" VALUES(12,70,'12:70','پس هنگامی‌که بارهای آن‌ها را مهیا کرد، جام (آبخوری پادشاه) را در بار برادرش گذاشت، سپس ندا دهنده‌ای ندا داد: «ای کاروانیان، بدون شک شما دزد هستید».');
INSERT INTO "hussein_taji" VALUES(12,71,'12:71','به طرف آن‌ها رو‌کرده و گفتند: «چه چیز گم کرده‌اید؟!»');
INSERT INTO "hussein_taji" VALUES(12,72,'12:72','گفتند: «پیمانۀ پادشاه را گم کرده‌ایم، و هر کس آن را بیاورد یک بار شتر (جایزه) دارد، و من ضامن این (وعده) هستم.');
INSERT INTO "hussein_taji" VALUES(12,73,'12:73','گفتند: «به الله سوگند، شما می‌دانید که ما نیامده‌‌ایم تا در این سرزمین فساد کنیم، و ما (هرگز) دزد نبوده‌ایم».');
INSERT INTO "hussein_taji" VALUES(12,74,'12:74','(آن‌ها) گفتند: «پس اگر دروغگو باشید؛ کیفرش چیست؟»');
INSERT INTO "hussein_taji" VALUES(12,75,'12:75','گفتند: «کیفر کسی‌که (آن پیمانه) در بارش پیدا شود، پس خودش کیفر آن خواهد بود (که بردۀ شما خواهد بود) ما این گونه ستم کاران را کیفر می‌دهیم».');
INSERT INTO "hussein_taji" VALUES(12,76,'12:76','پس شروع به (جست و جوی) بار‌های آن‌ها، پیش از بار برادرش پرداخت، آنگاه آن را از بار برادرش بیرون آورد، این گونه برای یوسف چاره‌اندیشی کردیم، او (هرگز) نمی‌توانست در آیین پادشاه برادرش را بگیرد، مگر آنکه الله بخواهد، درجات هرکس را بخواهیم بالا می‌بریم، و بالا‌تر از هر صاحب علمی، دانا تری است.');
INSERT INTO "hussein_taji" VALUES(12,77,'12:77','(برادران) گفتند: «اگر او دزدی کرده است (تعجب ندارد)؛ برادرش (نیز) پیش از این دزدی کرده بود، پس یوسف آن (سخن) را در دل خود پنهان داشت، و برای آن‌ها آشکار نکرد، (در دل خود) گفت: «شما از نظر منزلت بد‌ترین (مردم) هستید، و الله به آنچه توصیف می‌کنید، آگاه‌تراست».');
INSERT INTO "hussein_taji" VALUES(12,78,'12:78','گفتند: «ای عزیز! همانا او پدر پیری دارد، (که از دوری او سخت ناراحت می‌شود) لذا یکی از ما را به جای او بگیر، بی‌گمان ما تو را از نیکو کاران می‌بینیم».');
INSERT INTO "hussein_taji" VALUES(12,79,'12:79','(یوسف) گفت: «پناه بر الله، که ما جز آن کسی‌که کالای‌مان را نزد او یافته‌ایم، (دیگری را) بگیریم، بی‌گمان در آن صورت ستم کار خواهیم بود».');
INSERT INTO "hussein_taji" VALUES(12,80,'12:80','پس چون (برادران) از او نا امید شدند، نجوا کنان به گوشه‌ای رفتند، (برادر) بزرگ‌شان گفت: «آیا نمی‌دانید که پدر‌تان از شما به نام الله پیمان (استوار) گرفته است و پیش از این (نیز) دربارۀ یوسف کوتاهی کرده‌اید؟! پس من هرگز از این سرزمین (مصر) بیرون نمی‌شوم، تا پدرم به من اجازه دهد، یا الله دربارۀ من داوری کند، و او بهترین داوران است.');
INSERT INTO "hussein_taji" VALUES(12,81,'12:81','(شما) به سوی پدر‌تان باز گردید، و بگویید: «ای پدر جان! بی‌گمان پسرت دزدی کرد، و ما جز به آنچه می‌دانستیم گواهی ندادیم، و ما از غیب آگاه نبودیم.');
INSERT INTO "hussein_taji" VALUES(12,82,'12:82','و از (مردم) شهری که در آن بودیم، و از کاروانی که با آن بودیم بپرس، و بدون شک ما راستگو هستیم».');
INSERT INTO "hussein_taji" VALUES(12,83,'12:83','(یعقوب) گفت: «(حقیقت چنین نیست) بلکه (هوای) نفس شما، کاری (ناشایست) را برای شما آراسته است؛ پس (کار من) صبر جمیل است، امید است الله همۀ آن‌ها را به من باز گرداند، بی‌گمان او دانای حکیم است».');
INSERT INTO "hussein_taji" VALUES(12,84,'12:84','و از آن‌ها روی بر گرداند و گفت: «افسوس بر یوسف» و چشمان او از اندوه سفید شد، در حالی‌که (سر‌شار از غم بود) اندوه خود را فرو می‌برد.');
INSERT INTO "hussein_taji" VALUES(12,85,'12:85','گفتند: «به الله سوگند، پیوسته یوسف را یاد می‌‌کنی، تا آنکه سخت بیمار گردی یا بمیری».');
INSERT INTO "hussein_taji" VALUES(12,86,'12:86','(یعقوب) گفت: «من شرح غم و پریشانی خود را تنها به الله می‌گویم، (و به سوی او شکایت می‌برم) و از (سوی) الله چیزهای می‌دانم که شما نمی‌دانید».');
INSERT INTO "hussein_taji" VALUES(12,87,'12:87','ای پسران من! بروید و از یوسف و برادرش جستجو کنید، و از رحمت الله ناامید نشوید، چرا که جز گروه کافران کسی از رحمت الله ناامید نمی‌شود».');
INSERT INTO "hussein_taji" VALUES(12,88,'12:88','پس چون (به مصر رفتند و) بر او (= یوسف) وارد شدند، گفتند: «ای عزیز! به ما و خاندان ما سختی (و ناراحتی) رسیده است، و (اینک) کالای نا چیز (و اندکی) با خود آورده‌ایم؛ پس پیمانه را برای ما کامل کن، و بر ما صدقه (و بخشش) کن، بی‌گمان الله بخشندگان را پاداش می‌دهد».');
INSERT INTO "hussein_taji" VALUES(12,89,'12:89','(یوسف) گفت: «آیا دانستید با یوسف چه کردید، هنگامی‌که نادان بودید؟!».');
INSERT INTO "hussein_taji" VALUES(12,90,'12:90','(برادران) گفتند: «آیا به راستی تو همان یوسفی؟!». (یوسف) گفت: «(آری) من یوسفم، و این برادر من است، یقیناً الله بر ما منّت گذاشت، همانا هر کس پرهیزگاری کند و صبر نماید، بی‌گمان الله پاداش نیکو‌کاران را ضایع نمی‌کند».');
INSERT INTO "hussein_taji" VALUES(12,91,'12:91','(برادران) گفتند: «به الله سوگند، یقیناً الله تو را بر ما برتری داده، و حقاً ما خطا کار بودیم».');
INSERT INTO "hussein_taji" VALUES(12,92,'12:92','(یوسف) گفت: «امروز هیچ سرزنشی بر شما نیست، الله شما را می‌آمرزد، و او مهربان‌ترین مهربانان است».');
INSERT INTO "hussein_taji" VALUES(12,93,'12:93','این پیراهن مرا ببرید، و بر چهرۀ پدرم بیندازید تا بینا گردد، و همۀ خانوادۀ خود را نزد من بیاورید».');
INSERT INTO "hussein_taji" VALUES(12,94,'12:94','و چون کاروان (از سرزمین مصر) جدا شد (و حرکت کرد) پدر‌شان (به اطرافیان) گفت: «یقیناً من بوی یوسف را می‌یابم، اگر مرا به کم عقلی نسبت ندهید».');
INSERT INTO "hussein_taji" VALUES(12,95,'12:95','(آن‌ها) گفتند: «به الله سوگند، بی‌گمان تو در همان اشتباه دیرینه‌ات هستی».');
INSERT INTO "hussein_taji" VALUES(12,96,'12:96','پس چون بشارت‌دهنده آمد و آن (پیراهن) را برچهرۀ او افکند، ناگهان بینا شد، گفت: «آیا به شما نگفتم که من از (سوی) الله چیزهای می‌دانم که شما نمی‌دانید؟!».');
INSERT INTO "hussein_taji" VALUES(12,97,'12:97','گفتند: «ای پدر (جان) ما! برای ما (دربارۀ) گناهان‌مان آمرزش بخواه، بی‌گمان ما خطا کار بودیم».');
INSERT INTO "hussein_taji" VALUES(12,98,'12:98','(یعقوب) گفت: «به زودی برای شما از پروردگارم آمرزش می‌طلبم، بدون شک او آمرزندۀ مهربان است».');
INSERT INTO "hussein_taji" VALUES(12,99,'12:99','پس چون بر یوسف وارد شدند، او پدر و مادرش را (در آغوش گرفت و) نزد خود جای داد و گفت: «(همگی) به مصر درآیید؛ اگر الله بخواهد در امن (و امان) خواهید بود».');
INSERT INTO "hussein_taji" VALUES(12,100,'12:100','و پدر و مادر خود را بر تخت نشاند، و (همگی) برای او به سجده افتادند، و (یوسف) گفت: «ای پدرجان! این تعبیر خوابم است، که از پیش دیده بودم، پروردگارم آن را راست گرداند (و تحقق بخشید)، یقیناً به من نیکی کرد هنگامی‌که مرا از زندان بیرون آورد، و شما را از آن بیابان (به اینجا) آورد، بعد از آنکه شیطان میان من و برادرانم (فتنه و) فساد کرد، بی‌گمان پروردگارم به آنچه که می‌خواهد (تدبیر کند) باریک بین است، همانا او دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(12,101,'12:101','پروردگارا! (بهرۀ عظیمی) از فرمانروایی به من عطا کردی، و از علم (تأویل احادیث =)تعبیر خواب‌ها به من آموختی، ای پدید آورندۀ آسمان‌ها و زمین! تویی کار ساز من در دنیا و آخرت، مرا مسلمان بمیران و به شایستگان ملحق فرما».');
INSERT INTO "hussein_taji" VALUES(12,102,'12:102','(ای پیامبر!) این از خبرهای غیب است که به تو وحی می‌کنیم، و هنگامی‌که (برادران یوسف) بد اندیشی می‌کردند و تصمیم (قطعی)گرفتند، تو نزد آن‌ها نبودی.');
INSERT INTO "hussein_taji" VALUES(12,103,'12:103','و بیشتر مردم – و اگر چه (بر ایمان‌شان) حرص ورزی – مؤمن نخواهند بود.');
INSERT INTO "hussein_taji" VALUES(12,104,'12:104','و تو بر این (دعوت) از آن‌ها پاداشی نمی‌طلبی، آن جز پندی برای جهانیان نیست.');
INSERT INTO "hussein_taji" VALUES(12,105,'12:105','و چه بسا نشانه‌ای در آسمان‌ها و زمین وجود دارد که آن‌ها از کنارش می‌گذرند و از آن روی می‌گردانند.');
INSERT INTO "hussein_taji" VALUES(12,106,'12:106','و بیشتر آن‌ها به الله ایمان نمی‌آورند؛ مگر اینکه آنان (به نوعی) مشرک‌اند.');
INSERT INTO "hussein_taji" VALUES(12,107,'12:107','آیا ایمن هستند از اینکه عذاب فراگیری از سوی الله به سراغ آن‌ها بیاید، یا ناگهان قیامت به سراغ‌شان آید، در حالی‌که آنان بی‌خبرند.');
INSERT INTO "hussein_taji" VALUES(12,108,'12:108','(ای پیامبر!) بگو: «این راه من است، من با بصیرت (کامل) به سوی الله دعوت می‌کنم، و کسانی‌که از من پیروی کردند (نیز چنین می‌کند) و الله پاک و منزه است، و من از مشرکان نیستم».');
INSERT INTO "hussein_taji" VALUES(12,109,'12:109','و ما پیش از تو نفرستادیم، جز مردانی از اهل قریه‌ها که به آن‌ها وحی می‌کردیم، آیا (مشرکان) در زمین سیر نکردند؛ تا ببیند عاقبت کسانی‌که پیش از آن‌ها بودند، چگونه بود؟! و یقیناً سرای آخرت برای کسانی‌که پرهیزگاری کردند، بهتر است، آیا نمی‌اندیشید؟!');
INSERT INTO "hussein_taji" VALUES(12,110,'12:110','(کافران پیوسته به انکار خود ادامه دادند) تا آنگاه که پیامبران (از قوم‌شان) نا امید شدند، و (مردم) پنداشتند که به آن‌ها دروغ گفته شده است، (در این هنگام) یاری ما به سراغ‌شان آمد، پس هر کس را که خواستیم نجات یافت، و عذاب ما از قوم گنهکار باز نخواهد گشت.');
INSERT INTO "hussein_taji" VALUES(12,111,'12:111','یقیناً در داستان‌هایشان عبرتی برای خردمندان است، این (قرآن) سخنی نبود که (به دروغ) بافته شود، بلکه تصدیق‌کنندۀ کتاب‌های است که پیش از آن است، و بیان‌کننده (و شرح) هر چیز است، و هدایت و رحمت برای گروهی است که ایمان می‌آورند.');
INSERT INTO "hussein_taji" VALUES(13,1,'13:1','المر (الف. لام. میم. را) این‌ها آیات کتاب (قرآن) است، و آنچه از (سوی) پروردگارت بر تو نازل شده، حق است، و لیکن بیشتر مردم ایمان نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(13,2,'13:2','الله کسی است که آسمان‌ها را بدون ستون بر افراشت که می‌بینید، آنگاه بر عرش مستقر شد، و خورشید و ماه را مسخر ساخت، که هر کدام تا مدت معینی روان است، کار (و امور هستی) را تدبیر می‌کند، نشانه‌های را (روشن) بیان می‌کند، شاید شما به لقای پروردگار‌تان یقین حاصل کنید.');
INSERT INTO "hussein_taji" VALUES(13,3,'13:3','و او کسی است که زمین را گسترد، و در آن کوه‌ها و نهرها قرار داد، و در آن از هر نوع میوه‌ها دو قسم (ترش و شیرین و...) پدید آورد، شب را بر روز می‌پوشاند، بی‌گمان در این (امور) نشانه‌ها (و عبرت‌ها) است برای گروهی که می‌اندیشند.');
INSERT INTO "hussein_taji" VALUES(13,4,'13:4','و در زمین قطعه‌های (گوناگون) کنارهم (قرار دارد) و باغ‌هایی از انگور و کشتزار و نخل‌های که گاه از یک بن می‌رویند و گاهی از چند بن، (در صورتی) که از یک آب سیراب می‌شوند، و (با این حال) میوۀ بعضی از آن‌ها را بعضی (دیگر) برتری دادیم، بدون شک در این (امور) نشانه‌ها (و عبرت‌ها) است برای گروهی که خرد می‌ورزند.');
INSERT INTO "hussein_taji" VALUES(13,5,'13:5','و (ای پیامبر!) اگر (از ایمان نیاوردن کفار) تعجب می‌کنی، پس سخن آنان عجیب (تر) است، که (می‌گویند:) «آیا هنگامی‌که (مردیم و) خاک شدیم (بار دیگر زنده می‌شویم و) به آفرینش نوینی باز می‌گردیم؟!» اینان کسانی هستند که به پروردگار‌شان کافر شده‌اند، و اینان غل‌ها (و زنجیرها) در گردن‌هایشان است، و اینان اهل آتش (جهنم)اند، و جاودانه درآن خواهند ماند.');
INSERT INTO "hussein_taji" VALUES(13,6,'13:6','و آن‌ها پیش از نیکی (و رحمت) بدی (و عذاب) را به شتاب از تو می‌خواهند، با اینکه (بر امت‌ها) پیش از آن‌ها بلا‌ها (و عقوبت‌های عبرت انگیز) گذشته است، و بی‌گمان پروردگار تو نسبت به مردم – با وجود ستمکار بودن‌شان – دارای مغفرت است (و می‌آمرزد) و همانا پروردگار تو سخت کیفراست.');
INSERT INTO "hussein_taji" VALUES(13,7,'13:7','و کسانی‌که کافر شدند می‌گویند: «چرا نشانه‌ها (و معجزه‌ای) از (سوی) پروردگارش بر او نازل نشده است؟!» (ای پیامبر!) تو فقط هشداردهنده‌ای، و برای هر قومی هدایت کننده‌ای است.');
INSERT INTO "hussein_taji" VALUES(13,8,'13:8','الله می‌داند آنچه را که هر ماده‌ای بدان باردار می‌شود، و آنچه را که رحم‌ها می‌کاهند و آنچه افزون می‌کنند، و هر چیز نزد او مقدار معینی دارد.');
INSERT INTO "hussein_taji" VALUES(13,9,'13:9','(او) دانای غیب و آشکار، بزرگ بلند مرتبه است.');
INSERT INTO "hussein_taji" VALUES(13,10,'13:10','(برای الله) یکسان است که کسی از شما پنهانی سخن بگوید، و کسی‌که آن را آشکار سازد، و کسی‌که در (تاریکی) شب پنهان می‌شود، یا در (روشنایی) روز (آشکارا) راه می‌رود.');
INSERT INTO "hussein_taji" VALUES(13,11,'13:11','برای او (= انسان) فرشتگانی است که پی درپی (صبح و شام) از پیش رویش و از پشت سرش او را به امر الله حفظ می‌کنند، بی‌گمان الله حالت (و سرنوشت) هیچ قومی را تغیر نمی‌دهد، تا وقتی که آنان آنچه را که در ضمیر‌شان است، تغیر دهند. و هنگامی‌که الله برای قومی (بخاطر اعمال‌شان) بدی را اراده کند، پس هیچ چیز مانع آن نخواهد شد، و آنان را جز او هیچ کارسازی نیست.');
INSERT INTO "hussein_taji" VALUES(13,12,'13:12','او کسی است که برق را برای ترساندن، و امیدوار شدن به شما می‌نمایاند، و ابرهای سنگین (بار) را پدید می‌آورد.');
INSERT INTO "hussein_taji" VALUES(13,13,'13:13','و رعد به ستایش او و فرشتگان از ترس او تسبیح می‌گویند. و صاعقه‌ها را می‌فرستد، پس هر کس را که بخواهد بدان آسیب می‌رساند، و آن‌ها (با وجود مشاهده این همه آیات الهی همچنان) دربارۀ الله مجادله می‌کنند، و او بسیار نیرومند (و سخت‌گیر) است.');
INSERT INTO "hussein_taji" VALUES(13,14,'13:14','دعوت حق از آن اوست، و کسانی را که (مشرکان) به جای او می‌خوانند، (هرگز) دعوت‌شان را اجابت نمی‌کنند، مگر مانند کسی‌که کف دستش را به سوی آب می‌گشاید تا (آب) به دهانش برسد، و (هرگز) به آن نخواهد رسید، و دعای کافران جز در گمراهی نیست.');
INSERT INTO "hussein_taji" VALUES(13,15,'13:15','(تمام) کسانی‌که در آسمان‌ها و زمین هستند – از روی اطاعت (و دل خواه) یا اکراه (و نا خواه) – و (همچنین) سایه‌هایشان؛ صبحگاهانه و شامگاهان برای الله سجده می‌کنند.');
INSERT INTO "hussein_taji" VALUES(13,16,'13:16','(ای پیامبر! به مشرکان) بگو: «پروردگار آسمان‌ها و زمین کیست؟» بگو: «الله» (سپس به آن‌ها) بگو: «آیا به جای او اولیاء (و معبودانی) بر گزیده‌‌اید که مالک سود و زیان خود نیستند». بگو: «آیا نابینا و بینا برابرند، یا تاریکی‌ها و نور یکسان است؟ آیا آن‌ها شریکانی برای الله قرار داده‌اند که همچون آفرینش او آفریده‌اند، پس (این) آفرینش (ها) بر آن‌ها مشبه شده است؟!». بگو: «الله آفرینندۀ همه چیز است، و او یگانۀ چیره است.');
INSERT INTO "hussein_taji" VALUES(13,17,'13:17','(الله) از آسمان‌ها آب را فرو فرستاد، پس رودخانه‌ها به‌اندازۀ (ظرفیت) خویش روان شد، آنگاه سیل بر روی خود کفی بالا آمده برداشت، و ازآنچه (در کوره‌ها) برای بدست آوردن زینت آلات یا وسایل زندگی آتش روی آن روشن می‌کنند، کفی مانند آن به وجود می‌آید، الله (مثال) حق و باطل را این گونه بیان می‌کند، پس اما کف‌ها کنار افتاده، از بین می‌رود، و آنچه به مردم سود می‌بخشد در زمین (باقی) می‌ماند، این گونه الله (برای روشن شدن حق از باطل) مثال می‌زند.');
INSERT INTO "hussein_taji" VALUES(13,18,'13:18','برای کسانی‌که (دعوت) پروردگارشان را اجابت کردند، پاداش نیک (= بهشت) است و کسانی‌که (دعوت) او را اجابت نکردند، اگر تمام آنچه در زمین است و (نیز) همانندش، از آن آن‌ها باشد برای رهایی خود (از عذاب) فدیه بدهند، (از آن‌ها پذیرفته نخواهد شد) آن‌ها حساب سختی خواهند داشت، و جایگاه‌شان جهنم است، و چه بد جایگاهی است!.');
INSERT INTO "hussein_taji" VALUES(13,19,'13:19','آیا کسی‌که می‌داند آنچه از (طرف) پروردگارت بر تو نازل شده حق است، مانند کسی است که او نابیناست؟! تنها خردمندان پند می‌گیرند.');
INSERT INTO "hussein_taji" VALUES(13,20,'13:20','(همان) کسانی‌که به پیمان الله وفا می‌کنند، و عهد (و پیمان) را نمی‌شکنند.');
INSERT INTO "hussein_taji" VALUES(13,21,'13:21','و کسانی‌که آنچه را الله به پیوستن آن فرمان داده؛ پیوند می‌دهند، و از پروردگار‌شان می‌ترسند، و از سختی حساب بیم دارند.');
INSERT INTO "hussein_taji" VALUES(13,22,'13:22','و کسانی‌که به طلب روی پروردگار‌شان صبر کردند، و نماز را بر پا داشتند و از آنچه به آن‌ها روزی داده‌ایم، پنهان و آشکارا انفاق کردند، و بدی را با نیکی دفع می‌کنند، فرجام (نیک) سرای آخرت از آن آن‌هاست.');
INSERT INTO "hussein_taji" VALUES(13,23,'13:23','(همان) باغ‌های جاویدان (بهشتی) که وارد آن می‌شوند، و (همچنین) هر کس از پدران‌شان و همسران‌شان و فرزندان‌شان که نیکو کار بوده‌اند، و فرشتگان از هر دری بر آنان وارد می‌شوند.');
INSERT INTO "hussein_taji" VALUES(13,24,'13:24','(و به آن‌ها می‌گویند:) «سلام بر شما به (خاطر) آنچه صبر نموده‌اید، پس چه خوب است عاقبت آن سرای (جاویدان)».');
INSERT INTO "hussein_taji" VALUES(13,25,'13:25','و کسانی‌که پیمان الله را پس از استوار کردنش می‌شکنند، و آنچه را که الله به پیوستن آن فرمان داده؛ قطع می‌کنند، و در زمین فساد می‌نمایند، لعنت برای آن‌هاست، و برای آن‌ها سختی (و بدی) آن سرای (آخرت) است.');
INSERT INTO "hussein_taji" VALUES(13,26,'13:26','الله روزی را برای هرکسی‌که بخواهد وسیع می‌گرداند، و (برای هر کس که بخواهد) تنگ می‌دارد، و (کافران) به زندگی دنیا شاد شدند، در حالی‌که زندگی دنیا در (برابر) آخرت جز متاعی (ناچیز) نیست.');
INSERT INTO "hussein_taji" VALUES(13,27,'13:27','و کسانی‌که کافر شدند می‌گویند: «چرا نشانه (و معجزه‌ای) از (سوی) پروردگارش بر او نازل نشده است؟!». (ای پیامبر!) بگو: «بی‌گمان الله هرکس را بخواهد گمراه می‌کند، و هر کس که باز گردد (و توبه کند) به سوی خویش هدایت می‌کند».');
INSERT INTO "hussein_taji" VALUES(13,28,'13:28','(همانا) کسانی‌که ایمان آوردند، و دل‌های شان به یاد الله آرام می‌گیرد، آگاه باشید! (تنها) با یاد الله دل‌ها آرام می‌گیرند.');
INSERT INTO "hussein_taji" VALUES(13,29,'13:29','و کسانی‌که ایمان آوردند و کارهای شایسته انجام دادند، برایشان خوشی است، و بازگشت‌گاه نیکو (دارند).');
INSERT INTO "hussein_taji" VALUES(13,30,'13:30','(ای پیامبر!) این گونه تو را در (میان) امتی فرستادیم که پیش از آن‌ها امت‌های (دیگر، بودند و) رفتند، تا آنچه را به تو وحی کردیم بر آن‌ها بخوانی، در حالی‌که آن‌ها به (الله) رحمان کفر می‌ورزند. بگو: «او پروردگار من است، معبودی (به حق) جز او نیست، بر او توکل کردم، و بازگشتم به سوی اوست».');
INSERT INTO "hussein_taji" VALUES(13,31,'13:31','اگر (چنین) قرآنی بود که کوه‌ها به آن به حرکت آیند یا زمین به آن پاره پاره شود یا مردگان را به سخن آرد، (باز هم ایمان نمی‌آورند) بلکه همه امور از آن الله است، آیا کسانی‌که ایمان آورده‌اند (هنوز) ندانسته‌اند که اگر الله می‌خواست؛ قطعاً همۀ مردم را هدایت می‌کرد؟!و پیوسته به کسانی‌که کفر ورزیدند به سزای کار‌های‌که کرده‌اند مصیبت کوبنده‌ای می‌رسد یا آن مصیبت به نزدیک خانۀ آنان فرو می‌آید تا آنگاه وعدۀ الهی فرا رسد، یقینا الله متعال وعده‌ای که داده خلاف نمی‌کند');
INSERT INTO "hussein_taji" VALUES(13,32,'13:32','و به راستی به پیامبرانی پیش از تو (نیز) تمسخر (و استهزاء) کرده شد، پس (من) به کسانی‌که کفر ورزیدند مهلت دادم؛ آنگاه آنان را (فرو) گرفتم، پس (بنگر) مجازات من چگونه بود؟!');
INSERT INTO "hussein_taji" VALUES(13,33,'13:33','آیا کسی که بر همه نفوس (جهانیان) با آنچه به دست آورده‌اند مراقب (و نگهبان) است (سزاوار پرستش است یا مخلوقات عاجز و ناتوان و از همه جا بى‌خبر)؟! و برای الله شریکانی قرار دادند، (ای پیامبر!) بگو: «آن‌ها را نام ببرید! آیا او را به چیزی‌که در زمین نمی‌داند، آگاه می‌کنید؟! یا سخن بیهوده (و بی‌محتوا) می‌گویید؟!» بلکه (حق این است که) برای کسانی‌که کافر شدند؛ مکرشان آراسته شده است، و از راه (راست) باز داشته شده‌اند، و کسی را که الله گمراه کند، پس هیچ راهنمایی برای او وجود نخواهد داشت.');
INSERT INTO "hussein_taji" VALUES(13,34,'13:34','در زندگی دنیا برای آن‌ها عذاب (درد‌ناکی) است و قطعاً عذاب آخرت سخت‌تر است، و آن‌ها را هیچ نگه دارنده‌ای از (عذاب) الله نیست.');
INSERT INTO "hussein_taji" VALUES(13,35,'13:35','وصف بهشتی که به پرهیزگاران وعده داده شده است، (چنین است که:) نهرها از زیر (درختان) آن جاری است، میوه‌هایش همیشگی، و (نیز) سایه‌اش (دائمی است)، این است سرانجام کسانی‌که پرهیزگاری کردند، و سرانجام کافران آتش (جهنم) است.');
INSERT INTO "hussein_taji" VALUES(13,36,'13:36','و کسانی‌که به آن‌ها کتاب (آسمانی) دادیم، از آنچه بر تو نازل شده، خوشحال می‌شوند، و از احزاب (و گروه‌ها) کسانی هستند که قسمتی از آن را انکار می‌کنند، (ای پیامبر!) بگو: «من مأمورم که «الله» را بپرستم، و به او شرک نیاورم، و تنها به سوی او دعوت می‌کنم، و بازگشت من به سوی اوست».');
INSERT INTO "hussein_taji" VALUES(13,37,'13:37','و این گونه این (قرآن) را (به عنوان) حکم (و فرمانی به زبان) عربی نازل کردیم، و اگر بعد از آن که آگاهی برای توآمده، از هوس‌های آن‌ها پیروی کنی، در برابر (عذاب) الله هیچ کارساز و نگهدارنده‌ای نخواهی داشت.');
INSERT INTO "hussein_taji" VALUES(13,38,'13:38','و به راستی پیش از تو پیامبرانی فرستادیم و برای آن‌ها همسران و فرزندانی قرار دادیم، و هیچ پیامبری حق نداشت، که جز به فرمان الله معجزه‌ای بیاورد، برای هر (امر و) زمانی، نوشته‌ای مقرر است.');
INSERT INTO "hussein_taji" VALUES(13,39,'13:39','الله هرچه را که بخواهد محو و (هرچه را بخواهد) اثبات می‌کند و ام الکتاب (= لوح محفوظ) نزد اوست.');
INSERT INTO "hussein_taji" VALUES(13,40,'13:40','و اگر برخی از آنچه را که به آن‌ها وعده می‌دهیم به تو نشان دهیم، یا (بیش از فرا رسیدن آن‌ها) تو را بمیرانیم، در هر حال بر (عهدۀ) تو تنها تبلیغ (رسالت) است، و حساب (آن‌ها) بر ماست.');
INSERT INTO "hussein_taji" VALUES(13,41,'13:41','آیا ندیده‌اند که ما پیوسته به سراغ (این سر) زمین (کفر) می‌آییم، که آن را از اطراف (و دامنه‌هایش) می‌کاهیم؟! (و به سرزمین اسلام می‌افزاییم؟) و الله حکم می‌کند، و هیچ کس نمی‌تواند حکم او را رد کند، و او سریع الحساب (= زود شمار) است.');
INSERT INTO "hussein_taji" VALUES(13,42,'13:42','و به راستی کسانی‌که پیش از آن‌ها بودند مکر ورزیدند (و نقشه کشیدند) پس (باید دانست که) تمام مکرها (و نقشه‌ها) از آنِ الله است، می‌داند آنچه را که هر کس انجام می‌دهد، و به زودی کافران خواهند دانست که سر انجام (نیک) سرای (آخرت) از آنِ کیست.');
INSERT INTO "hussein_taji" VALUES(13,43,'13:43','و کسانی‌که کافر شدند می‌گویند: «تو پیامبر (الله) نیستی» بگو: «کافی است که الله و کسی‌که علم (پیشین) نزد اوست، میان من و شما گواه باشند».');
INSERT INTO "hussein_taji" VALUES(14,1,'14:1','الر (الف. لام. را) این (قرآن) کتابی است که برتو (ای پیامبر) نازل کردیم، تا مردم را به فرمان پروردگارشان از تاریکی‌ها(ی شرک و جهالت) به سوی روشنایی (ایمان و دانش) در آوری، به سوی راه (الله) پیروزمند ستوده.');
INSERT INTO "hussein_taji" VALUES(14,2,'14:2','(همان) الله که آنچه در آسمان‌ها و آنچه در زمین است، از آن اوست، و وای بر کافران از عذاب سخت.');
INSERT INTO "hussein_taji" VALUES(14,3,'14:3','(همانا) کسانی‌که زندگی دنیا را بر آخرت ترجیح می‌دهند، و (مردم را) از راه الله باز می‌دارند، و کجی (و انحراف) آن راه را می‌خواهند، اینان در گمراهی دوری هستند.');
INSERT INTO "hussein_taji" VALUES(14,4,'14:4','ما هیچ پیامبری را، مگر به زبان قومش، نفرستادیم؛ تا (حقایق را) برای آن‌ها بیان کند، پس الله هر کس را بخواهد گمراه می‌سازد و هر کس را بخواهد هدایت می‌کند، و او پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(14,5,'14:5','و به راستی ما موسی را با نشانه‌های خود فرستادیم، (و به او دستور دادیم) که: قومت را از تاریکی‌ها به روشنایی بیرون آر، و ایام الله (= روزهای پیروزی حق بر باطل) را به یادشان آور، بی‌گمان در این (امر) برای هر صبرکنندۀ شکرگزاری نشانه‌هایی است.');
INSERT INTO "hussein_taji" VALUES(14,6,'14:6','و (ای پیامبر! به یاد آور) هنگامی‌که موسی به قومش گفت: «نعمت الله را بر خود بیاد آورید، چون شما را از (چنگال) فرعونیان نجات داد، که به بدترین عذاب‌ها شما را عذاب می‌کردند، پسران‌تان را سر می‌بریدند، و زنان‌تان را (برای خدمتکاری) زنده می‌گذاشتند، و در این (امر) از جانب پروردگارتان برای شما آزمایش بزرگ بود».');
INSERT INTO "hussein_taji" VALUES(14,7,'14:7','و (و به یاد آورید) هنگامی را که پروردگارتان اعلام کرد که: «اگر شکر گزاری کنید، یقیناً (نعمت خود را) به شما افزون می‌دهم، و اگر کفران کنید، بی‌گمان عذاب من سخت است».');
INSERT INTO "hussein_taji" VALUES(14,8,'14:8','و موسی به (آن‌ها) گفت: «اگر شما و همۀ مردم روی زمین کافر شوید، پس (به الله هیچ زیانی نمی‌رسد) بی‌گمان الله بی‌نیاز ستوده است».');
INSERT INTO "hussein_taji" VALUES(14,9,'14:9','آیا خبر کسانی‌که پیش از شما بودند، (مانند) قوم نوح و عاد و ثمود، به شما نرسیده است؟! و (خبر) کسانی‌که پس از ایشان بودند، که جز الله آنان را نمی‌شناسد؟! پیامبران شان با دلایل (و معجزات) روشن به سوی آن‌ها آمدند، پس آن‌ها دستان‌شان در دهان‌هایشان گرفتند، و گفتند: «ما به آنچه شما فرستاده شده‌اید، کافریم، و ما نسبت به آنچه ما را به سوی آن فرا می‌خوانید، سخت در شک و تردید هستیم».');
INSERT INTO "hussein_taji" VALUES(14,10,'14:10','پیامبران شان گفتند: «آیا در (و جود) الله آفرینده آسمان‌ها و زمین شک است؟ (او) شما را فرا می‌خواند تا گناهان‌تان را برایتان بیامرزد، و شما را تا مدت معینی مهلت دهد». (آن‌ها) گفتند: «شما جز بشری همانند ما نیستید، می‌خواهید ما را از آنچه که نیاکان‌مان می‌پرستیدند باز دارید، پس معجزه (و دلیل) آشکاری برای ما بیاورید».');
INSERT INTO "hussein_taji" VALUES(14,11,'14:11','پیامبران شان گفتند: «ما جز بشری همانند شما نیستیم، و لیکن الله بر هر کس از بندگانش که بخواهد منّت می‌گذارد (و به پیامبری برمی‌گزیند) و ما (هرگز) نمی‌توانیم (و حق نداریم) که معجزه‌ای جز به فرمان الله برایتان بیاوریم. و مؤمنان باید بر الله توکل کنند.');
INSERT INTO "hussein_taji" VALUES(14,12,'14:12','و ما را چه شده است که بر الله توکل نکنیم، در حالی‌که ما را به راه‌های (سعادت بخش) مان هدایت فرموده است؟! و مسلماً ما بر آزارهای که به ما می‌رسانید صبر خواهیم کرد، (و رسالت خود را خواهیم رساند) و توکل‌کنندگان باید تنها بر الله توکل کنند».');
INSERT INTO "hussein_taji" VALUES(14,13,'14:13','و کسانی‌که کافر شدند به پیامبران شان گفتند: «ما قطعاً شما را از سرزمین خود بیرون خواهیم کرد، یا اینکه به آیین ما باز گردید». پس پروردگارشان به آن‌ها وحی فرستاد که: «یقیناً ستمگران را نابود خواهیم کرد،');
INSERT INTO "hussein_taji" VALUES(14,14,'14:14','و شما را بعد از آن‌ها در زمین سکونت خواهیم داد، این (وعده) برای کسی است که از وقوف در پیشگاه من بترسد، و از وعید (عذاب) من بیمناک باشد».');
INSERT INTO "hussein_taji" VALUES(14,15,'14:15','و (پیامبران از خدا) طلب فتح و پیروزی (بر کفار) کردند، و (سرانجام) هر گردنکش و ستیزه جوی ناکام (و نابود) شد.');
INSERT INTO "hussein_taji" VALUES(14,16,'14:16','پشت سرش جهنم است، و از آب چرک و خون (بد بو) نوشانده شود.');
INSERT INTO "hussein_taji" VALUES(14,17,'14:17','که جرعه جرعه آن را می‌نوشد، و نمی‌تواند آن را (به آسانی) فرو برد (و گوارایش نیست) و مرگ از هر سو به سراغ او می‌آید، در حالی‌که او نخواهد مرد، و عذاب سختی در پیش رو دارد.');
INSERT INTO "hussein_taji" VALUES(14,18,'14:18','وصف کسانی‌که به پروردگارشان کافر شدند، اعمال‌شان همچون خاکستر است که تند بادی در یک روز طوفانی سخت برآن وزیده باشد، توان نگه داشتن آنچه را که به دست آورده‌اند؛ ندارند، و این همان گمراهی دور (و دراز) است.');
INSERT INTO "hussein_taji" VALUES(14,19,'14:19','(ای انسان) آیا ندیدی که الله آسمان‌ها و زمین را به حق آفریده است؟! اگر بخواهد شما را می‌برد و خلق جدیدی (به جای شما پدید) می‌آورد.');
INSERT INTO "hussein_taji" VALUES(14,20,'14:20','و این (کار) بر الله دشوار نیست.');
INSERT INTO "hussein_taji" VALUES(14,21,'14:21','و (در روز قیامت) همگی در پیشگاه الله حاضر (و آشکار) شوند، پس (در این هنگام) ضعیفان به مستکبران می‌گویند: «همانا ما پیرو شما بودیم، پس آیا می‌توانید چیزی از عذاب الله را از ما دفع کنید؟». (آن‌ها) گویند: «اگر الله ما را هدایت کرده بود، ما (نیز) شما را هدایت می‌کردیم، چه بی‌تابی کنیم و چه صبر نماییم، برای ما یکسان است، (هیچ) گریزگاهی برای ما نیست».');
INSERT INTO "hussein_taji" VALUES(14,22,'14:22','و چون (داوری و) کار تمام شود، شیطان گوید: «بی‌گمان الله به شما وعدۀ حق (و راستین) داد، و من به شما وعده دادم، آنگاه خلاف وعده کردم، من بر شما هیچ تسلطی نداشتم، جز اینکه دعوت‌تان کردم، و شما (دعوت) مرا پذیرفتید، پس مرا سرزنش نکنید، و (بلکه) خویش را سرزنش کنید، نه من فریاد رس شما هستم، و نه شما فریاد رس من، همانا من از این‌که مرا پیش از این شریک (الله) ساختید، بیزارم» بی‌گمان برای ستمکاران عذاب دردناکی است.');
INSERT INTO "hussein_taji" VALUES(14,23,'14:23','و کسانی‌که ایمان آوردند و کارهای شایسته انجام دادند به باغ‌های (بهشتی) در آورده میشوند، که نهرها از زیر (درختان) آن جاری است، به فرمان پروردگارشان جاودانه در آن می‌مانند، و تحیت آن‌ها در آن سلام است.');
INSERT INTO "hussein_taji" VALUES(14,24,'14:24','آیا ندیدی که الله چگونه مثل زده است؟ کلمۀ طیبه (= توحید، و گفتار پاکیزه) همچون درخت طیبه و پاکی است که ریشۀ آن (در زمین) ثابت، و شاخه‌اش در آسمان است.');
INSERT INTO "hussein_taji" VALUES(14,25,'14:25','هر زمان (و فصلی) میوۀ خود را به فرمان پروردگارش می‌دهد، و الله برای (هدایت) مردم مثل‌ها می‌زند، باشد که متذکر شوند (و پند گیرند).');
INSERT INTO "hussein_taji" VALUES(14,26,'14:26','و مثل کلمۀ خبیثه (= شرک، و گفتار پلید) همچون درخت پلید (و ناپاکی) است که از زمین برکنده شده، (و هیچ) قرار و ثباتی ندارد.');
INSERT INTO "hussein_taji" VALUES(14,27,'14:27','الله کسانی را که ایمان آوردند، با گفتار ثابت (کلمه توحید) در زندگی دنیا و (هم) آخرت پایدار (و ثابت قدم) می‌دارد، و ستمکاران را گمراه می‌سازد، و الله هر چه بخواهد؛ انجام می‌دهد.');
INSERT INTO "hussein_taji" VALUES(14,28,'14:28','آیا ندیدی آن کسانی را که (شکر) نعمت الله را به کفران تبدیل کردند، و قوم خود را به سرای نابودی کشاندند؟!');
INSERT INTO "hussein_taji" VALUES(14,29,'14:29','که (همان) جهنم است، (که آن‌ها) به آن در آیند، و بد قرار گاهی است.');
INSERT INTO "hussein_taji" VALUES(14,30,'14:30','(آن‌ها) برای الله همتایانی قرار دادند تا (مردم را) از راه او گمراه کنند، بگو: «(چند روزی) بهره گیرید، پس یقیناً باز گشت‌تان به سوی آتش (دوزخ) است».');
INSERT INTO "hussein_taji" VALUES(14,31,'14:31','به بندگان من که ایمان آورده‌اند؛ بگو: نماز را بر پا دارند، و از آنچه به آن‌ها روزی داده‌ایم، پنهان و آشکار انفاق کنند، پیش از آن که روزی فرار رسد که در آن نه خرید و فروش است و نه دوستی.');
INSERT INTO "hussein_taji" VALUES(14,32,'14:32','الله (همان) کسی است که آسمان‌ها و زمین را آفرید، و از آسمان، آبی (گوارا) نازل کرد، پس با آن (آب) میوه‌ها(ی گوناگون) برای روزی شما (از زمین) بیرون آورد، و کشتی (ها) را برای شما مسخر گرداند، تا به فرمان او در دریا روان باشد، و نهرها (نیز) برای شما مسخر گرداند،');
INSERT INTO "hussein_taji" VALUES(14,33,'14:33','و خورشید و ماه را که پیوسته در گردش‌اند برای شما مسخر ساخت، و شب و روز را (نیز) برای شما مسخر گرداند.');
INSERT INTO "hussein_taji" VALUES(14,34,'14:34','و از هر چه از او خواستید به شما ارزانی داشت، و اگر (بخواهید) نعمت‌(های) الله را بشمارید، نمی‌توانید آن را بشمارید، بی‌شک انسان ستمگری ناسپاس است.');
INSERT INTO "hussein_taji" VALUES(14,35,'14:35','و (به یاد آورید) چون ابراهیم گفت: «پروردگارا! این شهر (مکه) را مکان امن قرار بده، و مرا و فرزندانم را از آنکه بت‌ها را پرستش کنیم دور بدار.');
INSERT INTO "hussein_taji" VALUES(14,36,'14:36','پروردگارا! بی‌گمان آن‌ها (= بت‌ها) بسیاری از مردم را گمراه ساختند، پس کسی‌که از من پیروی کند، بدون شک او از من است، و کسی‌که از من نافرمانی کند، بی‌گمان تو آمرزندۀ مهربانی.');
INSERT INTO "hussein_taji" VALUES(14,37,'14:37','پروردگارا! من (برخی) از فرزندانم را در وادی (خشک) و بی آب و بی گیاه، نزد خانه گرامی تو ساکن ساختم. پروردگارا! تا نماز را بر پا دارند، پس تو دل‌های (گروهی) از مردم را به آن‌ها مایل بگردان و از میوه‌ها به آن‌ها روزی ده، باشد که آنان سپاس گزارند.');
INSERT INTO "hussein_taji" VALUES(14,38,'14:38','پروردگارا! تو می‌دانی آنچه را که ما پنهان می‌کنیم و آنچه را که آشکار می‌سازیم، و چیزی نه در زمین و نه در آسمان بر الله پوشیده نمی‌ماند.');
INSERT INTO "hussein_taji" VALUES(14,39,'14:39','ستایش از آن الله است که در (سن) پیری، اسماعیل و اسحاق به من عطا فرمود، مسلماً پروردگار من شنوندۀ دعاست.');
INSERT INTO "hussein_taji" VALUES(14,40,'14:40','پروردگارا! مرا برپا دارندۀ نماز قرار بده، و از فرزندانم (چنین فرما)، پروردگارا! و دعای مرا بپذیر!');
INSERT INTO "hussein_taji" VALUES(14,41,'14:41','پروردگارا! مرا و پدر و مادر مرا، و مؤمنان را (در) روزی‌که حساب بر پا می‌شود بیامرز».');
INSERT INTO "hussein_taji" VALUES(14,42,'14:42','و گمان مبر که الله، از آنچه ستمکاران می‌کنند غافل است، (نه، بلکه کیفر) آن‌ها را برای روزی‌که چشم‌ها در آن خیره می‌شوند، به تأخیر می‌افکند.');
INSERT INTO "hussein_taji" VALUES(14,43,'14:43','شتابان سرها رو به بالا گرفته و چشم برهم نمی‌زنند، و دل‌های شان (فرو ریخته و) خالی است.');
INSERT INTO "hussein_taji" VALUES(14,44,'14:44','و (ای پیامبر!) مردم را از روزی‌که عذاب به سراغ‌شان می‌آید؛ بترسان، آنگاه آنان که ستم کرده‌اند، گویند: «پروردگارا! ما را تا یک مدت نزدیک (و کمی) مهلت بده، (تا) دعوت تو را اجابت کنیم، و پیامبران را پیروی نماییم». (به آنان گفته می‌شود:) آیا شما نبودید که پیش از این سوگند یاد می‌کردید که هیچ (فنا و) زوال برایتان نیست.');
INSERT INTO "hussein_taji" VALUES(14,45,'14:45','و در مسکن کسانی‌که به خویشتن ستم کردند، ساکن شدید، و برای شما روشن شد که با آن‌ها چگونه رفتار کردیم، و برای شما مثل‌ها (از سر گذشت پیشینیان) زدیم.');
INSERT INTO "hussein_taji" VALUES(14,46,'14:46','و به راستی آن‌ها (نهایت) مکر خود را بکار بردند، و (همۀ) مکرشان نزد الله (آشکار) است، و هر چند که با مکرشان کوه‌ها از جا کنده شود.');
INSERT INTO "hussein_taji" VALUES(14,47,'14:47','پس گمان مبر که الله (وعده‌ای را که) به پیامبرانش (داده)، خلف وعده کند، بی‌گمان الله پیروزمند انتقام گیر است.');
INSERT INTO "hussein_taji" VALUES(14,48,'14:48','روزی‌که (این) زمین به زمین دیگر، و آسمان‌ها (به گونه‌ای دیگر) مبدل می‌شود، و آن‌ها (همگی) به پیشگاه الله یکتای قهار ظاهر و (آشکار) می‌شوند.');
INSERT INTO "hussein_taji" VALUES(14,49,'14:49','و در آن روز مجرمان را (دست و پا) به هم بسته در غل‌ها می‌بینی.');
INSERT INTO "hussein_taji" VALUES(14,50,'14:50','جام‌هایشان از قطران است و چهره‌هایشان را آتش (جهنم) می‌پوشاند.');
INSERT INTO "hussein_taji" VALUES(14,51,'14:51','تا الله هر کس را به (سزای) آنچه کرده است؛ کیفر دهد، همانا الله سریع الحساب (= زود شمار) است.');
INSERT INTO "hussein_taji" VALUES(14,52,'14:52','این (قرآن) پیامی برای مردم است، و تا بدان هشدار یابند، و تا بدانند که او معبودی یکتاست، و تا خردمندان پندگیرند.');
INSERT INTO "hussein_taji" VALUES(15,1,'15:1','الر (الف. لام. را) این آیات کتاب و قرآن روشنگر است.');
INSERT INTO "hussein_taji" VALUES(15,2,'15:2','چه بسا کسانی‌که کافر شدند دوست دارند (و آرزو می‌کنند) که ای کاش مسلمان بودند.');
INSERT INTO "hussein_taji" VALUES(15,3,'15:3','بگذار آن‌ها بخورند، و بهره گیرند، و آرزوها (ی دراز) سرگرم (و غافل) شان سازد، پس بزودی خواهند دانست.');
INSERT INTO "hussein_taji" VALUES(15,4,'15:4','و ما (اهل) هیچ دیار (و شهر) ی را هلاک نکردیم؛ مگر اینکه (مهلت و) اجلی معین (و معلوم) داشت.');
INSERT INTO "hussein_taji" VALUES(15,5,'15:5','هیچ امتی بر اجل خود پیش نمی‌گیرد، و از آن عقب نخواهد افتاد.');
INSERT INTO "hussein_taji" VALUES(15,6,'15:6','و (کافران) گفتند: «ای کسی‌که قرآن بر تو نازل شده است، بی‌تردید تو دیوانه‌ای!');
INSERT INTO "hussein_taji" VALUES(15,7,'15:7','اگر از راستگویانی چرا فرشتگان را برای ما نمی‌آوری؟».');
INSERT INTO "hussein_taji" VALUES(15,8,'15:8','ما فرشتگان را جز به حق نازل نمی‌کنیم، و (هر گاه نازل شوند) آنگاه به آن‌ها مهلت داده نمی‌شود، (و کارشان یکسره می‌شود).');
INSERT INTO "hussein_taji" VALUES(15,9,'15:9','همانا ما قرآن را نازل کردیم و قطعاً ما نگهبان آن هستیم.');
INSERT INTO "hussein_taji" VALUES(15,10,'15:10','و یقیناً ما پیش از تو (پیامبرانی) در (میان) گروها (و امت‌ها) ی نخستین فرستاده‌ایم.');
INSERT INTO "hussein_taji" VALUES(15,11,'15:11','هیچ پیامبری به (سوی) آن‌ها نمی‌آمد مگر آن که مسخره‌اش می‌کردند.');
INSERT INTO "hussein_taji" VALUES(15,12,'15:12','این چنین آن (تکذیب و استهزاء) را در دل‌های مجرمان راه می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(15,13,'15:13','آن‌ها به آن (قرآن) ایمان نمی‌آورند، و به راستی سنت (و روش) پیشینیان (نیز چنین) گذشته است.');
INSERT INTO "hussein_taji" VALUES(15,14,'15:14','و اگر دری از آسمان به روی آن‌ها بگشاییم که پیوسته در آن بالا روند،');
INSERT INTO "hussein_taji" VALUES(15,15,'15:15','باز هم می‌گویند: «یقیناً ما را چشم بندی کرده‌اند، بلکه ما گروهی سحر شده‌ایم!».');
INSERT INTO "hussein_taji" VALUES(15,16,'15:16','و به راستی ما در آسمان برج‌هایی قرار دادیم، و آن‌ها را برای بینندگان آراستیم.');
INSERT INTO "hussein_taji" VALUES(15,17,'15:17','و آن را از هر شیطان رانده شده‌ای حفظ کردیم.');
INSERT INTO "hussein_taji" VALUES(15,18,'15:18','مگر آن کسی‌که دزدانه گوش فرا دهد، پس (در آن صورت) شهابی آشکار دنبالش کند.');
INSERT INTO "hussein_taji" VALUES(15,19,'15:19','و زمین را گستردیم، و در آن کوه‌های استواری افکندیم، و از هر چیز سنجیده (و مناسب) در آن رویاندیم.');
INSERT INTO "hussein_taji" VALUES(15,20,'15:20','و برای شما (انواع) وسایل زندگی، و (همچنین برای) کسانی‌که روزی دهندۀ آن‌ها نیستید، در آن قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(15,21,'15:21','و هیچ چیز نیست مگر آنکه گنجینه‌هایش نزد ماست، و ما جز به‌اندازه‌ای معین آن را نازل نمی‌کنیم.');
INSERT INTO "hussein_taji" VALUES(15,22,'15:22','و ما بادها را بارورکنندۀ (ابرها و گیاهان) فرستادیم، پس از آسمان‌ها آبی نازل کردیم و با آن (آب) شما را سیراب ساختیم، در حالی‌که شما خازن (و نگهدارندۀ) آن نیستید.');
INSERT INTO "hussein_taji" VALUES(15,23,'15:23','و یقیناً ماییم که زنده می‌کنیم و می‌میرانیم، و ما وارث (همۀ جهانیان) هستیم.');
INSERT INTO "hussein_taji" VALUES(15,24,'15:24','و به یقین (حال) گذشتگان شما را دانسته‌ایم، و به یقین (حال) آیندگان را (هم به خوبی) دانسته‌ایم.');
INSERT INTO "hussein_taji" VALUES(15,25,'15:25','و (ای پیامبر!) بی‌گمان پروردگار تو، (همۀ) آن‌ها را (در قیامت) محشور می‌کند، همانا او حکیم داناست.');
INSERT INTO "hussein_taji" VALUES(15,26,'15:26','و به راستی ما انسان را از گِل خشکیده‌ای، که از گِل بویناک تیره‌ای (گرفته شده) بود آفریدیم.');
INSERT INTO "hussein_taji" VALUES(15,27,'15:27','و جن را پیش از آن از آتش سوزان آفریدیم.');
INSERT INTO "hussein_taji" VALUES(15,28,'15:28','و (به یاد آور) هنگامی را که پروردگارت به فرشتگان فرمود: «همانا من آفرینندۀ بشری از گِل خشکیده‌ای که از گِل بویناک تیره‌ای (گرفته شده)، هستم.');
INSERT INTO "hussein_taji" VALUES(15,29,'15:29','پس چون (او را آفریدم و) به او سامان دادم، و از روح خود در آن دمیدم، (همگی) برای او سجده کنید».');
INSERT INTO "hussein_taji" VALUES(15,30,'15:30','پس همۀ فرشتگان با همدیگر سجده کردند.');
INSERT INTO "hussein_taji" VALUES(15,31,'15:31','جز ابلیس، که سر پیچی کرد از آنکه با سجده‌کنندگان باشد.');
INSERT INTO "hussein_taji" VALUES(15,32,'15:32','(الله) فرمود: «ای ابلیس! تو را چه شده که با سجده‌کنندگان (همراه) نیستی؟!»');
INSERT INTO "hussein_taji" VALUES(15,33,'15:33','گفت: «من هرگز برای بشری که او را از گِل خشکیده‌ای که از گِل بویناک تیره‌ای (گرفته شده) آفریده‌ای، سجده نخواهم کرد».');
INSERT INTO "hussein_taji" VALUES(15,34,'15:34','(الله) فرمود: «از آن (بهشت) بیرون شو، بی‌گمان تو رانده شده‌ای،');
INSERT INTO "hussein_taji" VALUES(15,35,'15:35','و همانا تا روز قیامت بر تو لعنت خواهد بود».');
INSERT INTO "hussein_taji" VALUES(15,36,'15:36','(ابلیس) گفت: «پروردگارا! مرا تا روزی‌که (مردم) برانگیخته می‌شوند مهلت ده».');
INSERT INTO "hussein_taji" VALUES(15,37,'15:37','(الله) فرمود: «یقیناً تو از مهلت یافتگانی،');
INSERT INTO "hussein_taji" VALUES(15,38,'15:38','تا (آن) روز وقت مقرر (و معین شده)».');
INSERT INTO "hussein_taji" VALUES(15,39,'15:39','گفت: «پروردگارا! به سبب اینکه مرا گمراه ساختی، قطعاً من (گناه و بدی‌ها) در زمین در نظرشان زینت می‌دهم، و همگی آن‌ها را گمراه خواهم کرد،');
INSERT INTO "hussein_taji" VALUES(15,40,'15:40','مگر بندگان مخلص تو را از (میان) آن‌ها ».');
INSERT INTO "hussein_taji" VALUES(15,41,'15:41','(الله) فرمود: «این (اخلاص) راه مستقیم است که به من می‌رسد.');
INSERT INTO "hussein_taji" VALUES(15,42,'15:42','یقیناً تو را بر بندگانم تسلطی نیست، مگر (بر آن) گمراهانی که از تو پیروی می‌کنند.');
INSERT INTO "hussein_taji" VALUES(15,43,'15:43','و بی‌گمان جهنم میعادگاه برای همۀ آن‌هاست.');
INSERT INTO "hussein_taji" VALUES(15,44,'15:44','برای آن هفت در هست، برای هر دری گروهی از آن‌ها تقسیم (و معین) شده‌اند».');
INSERT INTO "hussein_taji" VALUES(15,45,'15:45','یقیناً پرهیزگاران در باغ‌ها (ی بهشت) و در (کنار) چشمه‌ها هستند.');
INSERT INTO "hussein_taji" VALUES(15,46,'15:46','(به آن‌ها گفته می‌شود:) به سلامت و ایمنی به آن (باغ‌ها) وارد شوید.');
INSERT INTO "hussein_taji" VALUES(15,47,'15:47','آنچه در دل‌های شان از کینه (و حسد) بود، بیرون کشیدیم، برادرانه بر تخت‌ها رو بروی یکدیگر قرار دارند.');
INSERT INTO "hussein_taji" VALUES(15,48,'15:48','در آنجا هیچ خستگی و رنجی به آن‌ها نمی‌رسد، و هیچ گاه از آنجا بیرون‌شان نرانند.');
INSERT INTO "hussein_taji" VALUES(15,49,'15:49','(ای پیامبر!) بندگانم را خبر ده که یقیناً من آمرزندۀ مهربانم.');
INSERT INTO "hussein_taji" VALUES(15,50,'15:50','و آنکه عذاب من همان عذاب دردناک است!');
INSERT INTO "hussein_taji" VALUES(15,51,'15:51','و آن‌ها را از مهمانان ابراهیم خبر ده.');
INSERT INTO "hussein_taji" VALUES(15,52,'15:52','چون بر او وارد شدند، و سلام گفتند. (ابراهیم) گفت: «همانا ما از شما می‌ترسیم»');
INSERT INTO "hussein_taji" VALUES(15,53,'15:53','گفتند: «نترس، بی‌گمان ما تو را به پسری دانا بشارت می‌دهیم»');
INSERT INTO "hussein_taji" VALUES(15,54,'15:54','(ابراهیم) گفت: «آیا به من (این) بشارت می‌دهید با اینکه پیری به من رسیده است؟ پس به چه چیز بشارت می‌دهید؟!».');
INSERT INTO "hussein_taji" VALUES(15,55,'15:55','گفتند: «تو را به حق بشارت دادیم، پس از نا امیدان نباش».');
INSERT INTO "hussein_taji" VALUES(15,56,'15:56','(ابراهیم) گفت: «و چه کسی جز گمراهان از رحمت پروردگارش نا امید می‌شود؟!».');
INSERT INTO "hussein_taji" VALUES(15,57,'15:57','(آنگاه) گفت: «پس ای فرستادگان (الله) مقصود شما چیست؟».');
INSERT INTO "hussein_taji" VALUES(15,58,'15:58','گفتند: «همانا ما به سوی قوم گنهکار فرستاده شده‌ایم (تا آن‌ها را هلاک کنیم).');
INSERT INTO "hussein_taji" VALUES(15,59,'15:59','جز خاندان لوط که ما حتماً همۀ آن‌ها را نجات خواهیم داد.');
INSERT INTO "hussein_taji" VALUES(15,60,'15:60','مگر همسر او که مقدر داشتیم او قطعاً از بازماندگان (و هلاکت شوندگان) باشد».');
INSERT INTO "hussein_taji" VALUES(15,61,'15:61','پس هنگامی‌که که فرستادگان (الله) نزد خاندان لوط آمدند.');
INSERT INTO "hussein_taji" VALUES(15,62,'15:62','(لوط) گفت: «همانا شما گروهی ناشناس هستید!»');
INSERT INTO "hussein_taji" VALUES(15,63,'15:63','(فرشتگان) گفتند: «بلکه، ما (همان) چیزی را برای تو آورده‌ایم که آن‌ها در آن تردید داشتند.');
INSERT INTO "hussein_taji" VALUES(15,64,'15:64','و ما حق را برای تو آورده‌ایم، و ما قطعاً راستگویانیم.');
INSERT INTO "hussein_taji" VALUES(15,65,'15:65','پس پاسی از شب گذشته خانواده‌ات را با خود بردار، (و از این جا) ببر، و (خودت) به دنبال آن‌ها برو، و کسی از شما پشت سرش ننگرد، و به آن جایی که فرمان یافته‌اید بروید.');
INSERT INTO "hussein_taji" VALUES(15,66,'15:66','و ما این امر را به او (= لوط) وحی کردیم که همانا صبحگاهان همۀ آن‌ها ریشه کن خواهند شد.');
INSERT INTO "hussein_taji" VALUES(15,67,'15:67','و اهل شهر شادی‌کنان (به طرف خانۀ لوط) آمدند.');
INSERT INTO "hussein_taji" VALUES(15,68,'15:68','(لوط) گفت: «به راستی این‌ها مهمانان من هستند، پس مرا رسوا (و شرمنده) نکنید،');
INSERT INTO "hussein_taji" VALUES(15,69,'15:69','و از الله بترسید و مرا خوار نسازید».');
INSERT INTO "hussein_taji" VALUES(15,70,'15:70','(آن‌ها) گفتند: «مگر ما تو را از (مهمانی و حمایت) مردمان نهی نکرده بودیم؟!»');
INSERT INTO "hussein_taji" VALUES(15,71,'15:71','(لوط) گفت: «این‌ها دختران (قوم) من هستند، (با آن‌ها ازدواج کنید) اگر انجام دهندۀ کاری (مشروع) هستید».');
INSERT INTO "hussein_taji" VALUES(15,72,'15:72','سوگند به جان تو (ای پیامبر) که آن‌ها در مستی خود سر گردانند.');
INSERT INTO "hussein_taji" VALUES(15,73,'15:73','پس هنگام طلوع آفتاب صیحه (= بانگ مرگبار) آن‌ها را فرو گرفت.');
INSERT INTO "hussein_taji" VALUES(15,74,'15:74','آنگاه بالای آن (آبادی‌ها) را پایین قرار دادیم. (و زیر رو کردیم) و بر (سر) آنان سنگ‌های از سنگ گل بارانیدیم.');
INSERT INTO "hussein_taji" VALUES(15,75,'15:75','بی‌گمان در این (سر گذشت) نشانه‌های برای هوشیاران است.');
INSERT INTO "hussein_taji" VALUES(15,76,'15:76','و همانا آن (شهرهای ویرانه) بر سر راه (کاروانیان) پا برجاست.');
INSERT INTO "hussein_taji" VALUES(15,77,'15:77','بی‌گمان در این نشانه‌ای برای مؤمنان است.');
INSERT INTO "hussein_taji" VALUES(15,78,'15:78','و بی‌شک اصحاب أیکه (= قوم شعیب) ستمکار بودند.');
INSERT INTO "hussein_taji" VALUES(15,79,'15:79','پس (ما) از آن‌ها انتقام گرفتیم، و (شهرهای ویرانه شده) این دو (قوم) بر سر راه آشکار است.');
INSERT INTO "hussein_taji" VALUES(15,80,'15:80','و به راستی اصحاب حجر (= قوم ثمود) پیامبران را تکذیب کردند.');
INSERT INTO "hussein_taji" VALUES(15,81,'15:81','و (ما) آیات خود را به آن‌ها دادیم، پس آن‌ها از آنان روی گردان بودند.');
INSERT INTO "hussein_taji" VALUES(15,82,'15:82','آن‌ها با خاطری مطمئن و آسوده از کوه‌ها خانه‌هایی (برای خود) می‌تراشیدند.');
INSERT INTO "hussein_taji" VALUES(15,83,'15:83','پس صبحگاهان صیحه (= بانگ مرگبار) آن‌ها را فرو گرفت.');
INSERT INTO "hussein_taji" VALUES(15,84,'15:84','و آنچه را به دست آورده بودند (عذاب الهی را) از آن‌ها دفع نکرد.');
INSERT INTO "hussein_taji" VALUES(15,85,'15:85','و (ما) آسمان‌ها و زمین و آنچه را که میان آن دو است، جز به حق نیافریدیم، و قیامت قطعاً خواهد آمد، پس (ای پیامبر) به طرز شایسته‌ای گذشت کن (و از آن‌ها صرف نظر فرما)');
INSERT INTO "hussein_taji" VALUES(15,86,'15:86','بی‌گمان پروردگار تو، همان آفرینندۀ داناست.');
INSERT INTO "hussein_taji" VALUES(15,87,'15:87','و به راستی (ما) به تو سبع المثانی (= سورۀ فاتحه) و قرآن عظیم دادیم.');
INSERT INTO "hussein_taji" VALUES(15,88,'15:88','(پس) هرگز چشم خود را به (سوی) آنچه (از نعمت‌ها و متاع دنیوی) که گروه‌هایی از آنان را از آن بهرمند ساخته‌ایم، نیندوز، و بر آن‌ها غمگین نباش، و بال (شفقت) خود را برای مؤمنان فرود آر (و نسبت به آن‌ها فروتن باش).');
INSERT INTO "hussein_taji" VALUES(15,89,'15:89','و بگو: «همانا من هشدار دهندۀ آشکارم».');
INSERT INTO "hussein_taji" VALUES(15,90,'15:90','(و آن‌ها را از عذابی بترسان) همان‌گونه که بر تقسیم‌کنندگان (آیات الهی) نازل کردیم.');
INSERT INTO "hussein_taji" VALUES(15,91,'15:91','(همان) کسانی‌که قرآن را تقسیم (و بخش، بخش) کردند.');
INSERT INTO "hussein_taji" VALUES(15,92,'15:92','پس سوگند به پروردگارت، قطعاً (در قیامت) از همه آن‌ها سوال خواهیم کرد.');
INSERT INTO "hussein_taji" VALUES(15,93,'15:93','از آنچه می‌کردند.');
INSERT INTO "hussein_taji" VALUES(15,94,'15:94','پس (ای پیامبر!) به آنچه مأمور شده‌ای آشکارا ابلاغ کن، و از مشرکان روی بگردان.');
INSERT INTO "hussein_taji" VALUES(15,95,'15:95','بی‌گمان ما (شر) مسخره کنندگان را از تو دفع خواهیم کرد.');
INSERT INTO "hussein_taji" VALUES(15,96,'15:96','کسانی‌که معبود دیگری با الله قرار می‌دهند، پس بزودی خواهند دانست.');
INSERT INTO "hussein_taji" VALUES(15,97,'15:97','و به راستی ما می‌دانیم که سینه تو (ای پیامبر) از آنچه آن‌ها می‌گویند تنگ می‌شود (و تو را آزرده و نا راحت می‌کند).');
INSERT INTO "hussein_taji" VALUES(15,98,'15:98','پس به ستایش پروردگارت تسبیح گوی و از سجده‌کنندگان باش.');
INSERT INTO "hussein_taji" VALUES(15,99,'15:99','و پروردگارت را عبادت کن تا هنگامی‌که یقین (= مرگ) به سراغت آید.');
INSERT INTO "hussein_taji" VALUES(16,1,'16:1','فرمان الله فرا رسیده است، پس برای آن شتاب نکنید، او پاک و برتر است از آنچه شریک او قرار می‌دهند.');
INSERT INTO "hussein_taji" VALUES(16,2,'16:2','(الله) فرشتگان را با روح (= وحی) به فرمان خویش بر هرکس از بندگانش بخواهد نازل می‌کند، (با این پیام) که (مردم را) هشدار دهید، (و بگویید:) معبودی (به حق) جز من نیست، پس از من بترسید.');
INSERT INTO "hussein_taji" VALUES(16,3,'16:3','آسمان‌ها و زمین را به حق آفرید، از آنچه که شریکش می‌سازند برتر است.');
INSERT INTO "hussein_taji" VALUES(16,4,'16:4','انسان را از نطفه آفرید، آنگاه او (دشمنی) ستیزه جویی آشکار شده است.');
INSERT INTO "hussein_taji" VALUES(16,5,'16:5','و چهارپایان را برای شما آفرید، که در آن‌ها پوشش (گرم) و منافع (دیگری) است، و از (گوشت، برخی) آن‌ها می‌خورید.');
INSERT INTO "hussein_taji" VALUES(16,6,'16:6','و در آن‌ها برای شما (زینت و) زیبائی است، چون شامگاهان (از صحرا) باز می‌گردانید، و چون صبحگاهان (به صحرا) می‌فرستید.');
INSERT INTO "hussein_taji" VALUES(16,7,'16:7','و (آن‌ها) بارهای سنگین شما را به شهری که جز با رنج و مشقت زیاد، به آن نمی‌رسیدید، حمل می‌کنند، بی‌گمان پروردگارتان رؤوف مهربان است.');
INSERT INTO "hussein_taji" VALUES(16,8,'16:8','و (همچنین) اسبان و استران و خران را (آفرید) تا برآن‌ها سوار شوید، و (نیز برای شما) زینت است، و چیزهایی را می‌آفریند که نمی‌دانید.');
INSERT INTO "hussein_taji" VALUES(16,9,'16:9','و بر الله است که راه راست را (به بنده‌گانش) بنمایاند، و برخی از آن راه‌ها بیراهه (و کج) است، و اگر می‌خواست همۀ شما را هدایت می‌کرد.');
INSERT INTO "hussein_taji" VALUES(16,10,'16:10','او کسی است که از آسمان برایتان آبی نازل کرد، که از آن (آب) آشامیدنی است، و از آن (گیاهان و) درختانی (می روید) که (حیوانات خود را) در آن می‌چرانید.');
INSERT INTO "hussein_taji" VALUES(16,11,'16:11','(الله) با آن (آب) برای شما زراعت و زیتون و (درختان) خرما و تاکستان‌های انگور، و از همه نوع میوه‌ها می‌رویاند. بی‌گمان در این نشانه‌ای است برای گروهی که اندیشه می‌کنند.');
INSERT INTO "hussein_taji" VALUES(16,12,'16:12','و شب و روز و خورشید و ماه را برای شما مسخر کرد، و ستارگان (نیز) به فرمان او مسخر هستند، بی‌تردید در این نشانه‌ای است برای گروهی که خرد می‌ورزند.');
INSERT INTO "hussein_taji" VALUES(16,13,'16:13','و (همچنین مسخر گرداند) آنچه را که برای شما در زمین به رنگ‌های گوناگون آفرید، مسلماً در این نشانه‌ای است برای گروهی که پند می‌پذیرند.');
INSERT INTO "hussein_taji" VALUES(16,14,'16:14','و او کسی است که دریا را مسخر ساخت، تا از آن گوشت تازه بخورید، و زینتی (مانند مروارید) که آن را بپوشید؛ بیرون آرید، و کشتی‌ها را می‌بینی که در آن روانند (و سینۀ دریا را می‌شکافند) و تا از فضل او (= الله) روزی بجویید، و باشد که شکر‌‌ گزارید.');
INSERT INTO "hussein_taji" VALUES(16,15,'16:15','و در زمین کوه‌های استوار (و محکمی) افکند، که مبادا شما را بلرزاند، و نهرها و راه‌ها (پدید آورد) شاید هدایت شوید.');
INSERT INTO "hussein_taji" VALUES(16,16,'16:16','و (نیز) نشانه‌ها (قرار داد) و (در هنگام شب) بوسیله ستاره‌گان راه می‌یابند.');
INSERT INTO "hussein_taji" VALUES(16,17,'16:17','آیا کسی‌که می‌آفریند، مانند کسی است که نمی‌آفریند؟! آیا پند نمی‌گیرید؟!');
INSERT INTO "hussein_taji" VALUES(16,18,'16:18','و اگر (بخواهید) نعمت‌(های) الله را بشمارید، نمی‌توانید آن را بشمارید، بی‌تردید الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(16,19,'16:19','و الله آنچه را پنهان می‌دارید، و آنچه را آشکار می‌کنید؛ می‌داند.');
INSERT INTO "hussein_taji" VALUES(16,20,'16:20','و آن‌هایی را که به جای الله (به معبودیت) می‌خوانند؛ چیزی را نمی‌آفرینند، و خودشان (نیز) آفریده می‌شوند.');
INSERT INTO "hussein_taji" VALUES(16,21,'16:21','(آن‌ها) مرد‌گانی هستند غیر زنده، و نمی‌دانند در چه زمانی برانگیخته می‌شوند.');
INSERT INTO "hussein_taji" VALUES(16,22,'16:22','معبود شما، معبودی یگانه است، پس کسانی‌که به آخرت ایمان نمی‌آورند، دل‌های شان (حق را) انکار کند، و خودشان مستکبرند.');
INSERT INTO "hussein_taji" VALUES(16,23,'16:23','قطعاً آنچه را پنهان می‌دارند، و آنچه را آشکار می‌کنند؛ می‌داند، بی‌گمان او مستکبران را دوست ندارد.');
INSERT INTO "hussein_taji" VALUES(16,24,'16:24','و چون به آن‌ها گفته شود: «پروردگارتان چه چیز نازل کرده است؟» گویند: «افسانه‌های پیشینیان».');
INSERT INTO "hussein_taji" VALUES(16,25,'16:25','تا در روز قیامت بار گناهان خود را بطور کامل بر دوش کشند، و (نیز) بخشی از گناهان کسانی را که به نادانی گمراه‌شان می‌سازند، آگاه باشید، چه بد است آنچه بر دوش می‌کشند.');
INSERT INTO "hussein_taji" VALUES(16,26,'16:26','به راستی کسانی‌که پیش از ایشان بودند (نیز) مکر ورزیدند، پس الله بنیاد شان را از اساس و پایه ویران کرد، که سقف از بالا بر سرشان فرو ریخت، و عذاب (الهی) ازآن‌جای‌که نمی‌دانستند به سراغ‌شان آمد.');
INSERT INTO "hussein_taji" VALUES(16,27,'16:27','آنگاه روز قیامت (الله) آن‌ها را رسوا می‌سازد، می‌فرماید: «آن شریکان (دروغین) من، (آن) کسانی‌که شما دربارۀ آن‌ها (با پیامبران) دشمنی (و مجادله) می‌کردید، کجا هستند؟! کسانی‌که به آن‌ها علم داده شده گویند: «همانا رسوایی و سختی امروز بر کافران است».');
INSERT INTO "hussein_taji" VALUES(16,28,'16:28','(همان) کسانی‌که فرشتگان (عذاب) جان‌شان را می‌گیرند، در حالی‌که به خود ستم کرده بودند، پس سر تسلیم فرود می‌آورند (و گویند:) ما هیچ (کارِ) بدی انجام نمی‌دادیم، آری، مسلماً الله به آنچه انجام می‌دادید؛ داناست.');
INSERT INTO "hussein_taji" VALUES(16,29,'16:29','پس از در‌های جهنم داخل شوید در حالی‌که جاودانه در آن خواهید بود، چه بد است جایگاه مستکبران.');
INSERT INTO "hussein_taji" VALUES(16,30,'16:30','و به کسانی‌که پرهیزگاری نمودند، گفته شد: «پروردگارتان چه چیز نازل کرده است؟» گویند: «خیر (و هدایت را)». برای کسانی‌که نیکی کردند، در این دنیا نیکی است، و قطعاً سرای آخرت (بسی) بهتر است، و چه خوب است سرای پرهیزگاران.');
INSERT INTO "hussein_taji" VALUES(16,31,'16:31','باغ‌هایی (از بهشت) جاویدان است، که به آن در آیند، نهرها از زیر (درختان) آن جاری است، در آنجا هر چه بخواهند برایشان (مهیا) است، این گونه الله پرهیزگاران را پاداش می‌دهد.');
INSERT INTO "hussein_taji" VALUES(16,32,'16:32','(همان) کسانی‌که فرشتگان (رحمت) جان‌شان را می‌گیرند، در حالی‌که پاکند، (به آن‌ها) می‌گویند: «سلام بر شما، به خاطر آنچه که می‌کردید به بهشت وارد شوید».');
INSERT INTO "hussein_taji" VALUES(16,33,'16:33','آیا (کافران) جز این انتظار دارند که فرشتگان (برای گرفتن جان‌شان) به سراغ آن‌ها بیایند، یا فرمان پروردگارت (برای عذاب‌شان) فرا رسد، کسانی‌که پیش از آن‌ها بودند (نیز) چنین کردند، و الله به آن‌ها ستم نکرد، لیکن آن‌ها به خویشتن ستم می‌کردند.');
INSERT INTO "hussein_taji" VALUES(16,34,'16:34','پس بدی‌های آنچه را که می‌کردند به آن‌ها رسید، و آنچه به آن مسخره می‌کردند، برآنان فرود آمد.');
INSERT INTO "hussein_taji" VALUES(16,35,'16:35','و کسانی‌که شرک ورزیدند گفتند: «اگر الله می‌خواست، نه ما و نه پدران‌مان، غیر از او هیچ چیزی را پرستش نمی‌کردیم، و نه چیزی را بدون فرمان او حرام می‌ساختیم» کسانی‌که پیش از آن‌ها بودند (نیز) چنین کردند، پس آیا بر پیامبران جز ابلاغ آشکار (مسئولیت دیگری) هست؟');
INSERT INTO "hussein_taji" VALUES(16,36,'16:36','یقیناً ما در (میان) هر امت پیامبری را فرستادیم که: «الله یکتا را بپرستید، و از طاغوت اجتناب کنید». پس الله بعضی از آنان را هدایت کرد و بر بعضی از آنان گمراهی محقق گشت، پس در زمین بگردید، آنگاه بنگرید عاقبت تکذیب‌کنندگان چگونه بود.');
INSERT INTO "hussein_taji" VALUES(16,37,'16:37','(ای پیامبر!) هر اندازه بر هدایت آن‌ها حریص باشی (سودی ندارد، زیرا که) الله کسی را که گمراه کرده است، هدایت نمی‌کند، و آن‌ها یاورانی ندارند.');
INSERT INTO "hussein_taji" VALUES(16,38,'16:38','و آن‌ها با شدید‌ترین سوگند‌‌‌‌‌‌‌هایشان به الله سوگند یاد کردند که: «الله کسی را که می‌میرد، بر نمی‌انگیزد!» آری (این) وعدۀ قطعی بر اوست (که همۀ مردگان را بر می‌انگیزد) ولی بیشتر مردم نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(16,39,'16:39','تا آنچه را که درآن اختلاف می‌کردند برایشان روشن سازد، و تا کسانی‌که کافر شدند، بدانند که آن‌ها دروغگو بوده‌اند.');
INSERT INTO "hussein_taji" VALUES(16,40,'16:40','فرمان ما به هر چیزکه چون اراده‌اش کنیم، فقط این است که به او گوییم: «موجود باش» پس (بلا فاصله) موجود می‌شود.');
INSERT INTO "hussein_taji" VALUES(16,41,'16:41','و کسانی‌که پس از آن که ستم دیدند، در راه الله هجرت کردند، یقیناً در این دنیا جایگاه خوبی خواهیم داد، و مسلماً پاداش آخرت بزرگتر است، ای کاش می‌دانستند.');
INSERT INTO "hussein_taji" VALUES(16,42,'16:42','(همان) کسانی‌که صبر کردند، و (تنها) بر پروردگار‌شان توکل می‌کنند.');
INSERT INTO "hussein_taji" VALUES(16,43,'16:43','و پیش از تو (ای پیامبر) جز مردانی که به آن‌ها وحی می‌کردیم نفرستادیم، پس (ای مردم) اگر نمی‌دانید از (آگاهانِ) اهل کتاب بپرسید.');
INSERT INTO "hussein_taji" VALUES(16,44,'16:44','(که آن‌ها را) با دلایل روشن و کتاب‌ها (فرستادیم) و (ما این) قرآن را بر تو نازل کردیم تا برای مردم روشن سازی، آنچه را که به سوی آن‌ها نازل شده است، و باشد که آن‌ها بیندیشند.');
INSERT INTO "hussein_taji" VALUES(16,45,'16:45','آیا کسانی‌که بد اندیشی (و توطئه) کردند، (از این) ایمن هستند که الله (همۀ) آن‌ها را در زمین فرو برد، و یا عذاب (الهی) از جایی که نمی‌دانند، به سراغ‌شان بیاید؟!');
INSERT INTO "hussein_taji" VALUES(16,46,'16:46','یا (آن که) به هنگام رفت و آمدشان (گریبان) آن‌ها را بگیرد، پس نتوانند که (ما را ناتوان کنند و) بگریزند.');
INSERT INTO "hussein_taji" VALUES(16,47,'16:47','یا (آن که) آنان را در حال ترس (و وحشت) بگیرد، پس بی‌تردید پروردگار شما رؤوف مهربان است.');
INSERT INTO "hussein_taji" VALUES(16,48,'16:48','آیا آن‌ها به چیزهای که الله آفریده است نمی‌نگرند که (چگونه) سایه‌هایش از راست و چپ سجده‌کنان برای الله در حرکتند، و آن‌ها (عاجز و) فروتن هستند؟!');
INSERT INTO "hussein_taji" VALUES(16,49,'16:49','و آنچه در آسمان‌ها و آنچه در زمین از جنبنده‌گان وجود دارد، و (نیز) فرشتگان؛ برای الله سجده می‌کنند، و آنان تکبر نمی‌ورزند.');
INSERT INTO "hussein_taji" VALUES(16,50,'16:50','آن‌ها از پروردگارشان که بر فراز‌شان است، می‌ترسند، و آنچه فرمان می‌یابند، انجام دهند.');
INSERT INTO "hussein_taji" VALUES(16,51,'16:51','و الله فرمود: «دو معبود را نگیرید، فقط او معبود یگانه است، پس (تنها) از من بترسید».');
INSERT INTO "hussein_taji" VALUES(16,52,'16:52','و آنچه در آسمان‌ها و زمین است از آنِ اوست، و (نیز) دین (و پرستش) ابدی از آنِ اوست، آیا از غیر الله می‌ترسید؟!');
INSERT INTO "hussein_taji" VALUES(16,53,'16:53','آنچه از نعمت دارید، پس (همه) از (جانب) الله است، سپس هنگامی‌که ناراحتی به شما رسد، باز به سوی او زاری می‌کنید.');
INSERT INTO "hussein_taji" VALUES(16,54,'16:54','سپس هنگامی‌که آن ناراحتی را از شما بر طرف ساخت، آنگاه گروهی از شما به پروردگار‌شان شرک می‌آورند.');
INSERT INTO "hussein_taji" VALUES(16,55,'16:55','تا آنچه را که به آن‌ها داده‌ایم کفران کنند، پس (چند روزی) بهره گیرید، که بزودی خواهید دانست.');
INSERT INTO "hussein_taji" VALUES(16,56,'16:56','و آن‌ها برای چیزی (=بتانی) که نمی‌دانند، سهمی از آنچه روزی‌شان داده‌ایم قرار می‌دهند، به الله سوگند، البته از آنچه افترا می‌بندید، بازخواست خواهید شد.');
INSERT INTO "hussein_taji" VALUES(16,57,'16:57','و (آن‌ها) برای الله دختران قرار می‌دهند، - او منزه است- و برای خودشان آنچه را میل دارند (قرار می‌دهند).');
INSERT INTO "hussein_taji" VALUES(16,58,'16:58','و هرگاه به یکی از آن‌ها به (تولد) دختر بشارت دهند، چهره‌اش سیاه می‌شود در حالی‌که او خشم خود را فرو می‌خورد.');
INSERT INTO "hussein_taji" VALUES(16,59,'16:59','و به سبب بشارتِ بدی که به او داده شده، از قوم (خود) پنهان می‌شود (می‌اندیشد که) آیا با خواری او را نگه دارد یا در خاک پنهانش کند؟! آگاه باشید آنچه را که حکم می‌کنند، بد (حکمی) است.');
INSERT INTO "hussein_taji" VALUES(16,60,'16:60','برای کسانی‌که به (سرای) آخرت ایمان ندارند، مثل (و صفت) بد است. و برای الله صفت برتر (و عالی) است، و او پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(16,61,'16:61','و اگر الله مردم را به (خاطر) ستم‌شان مؤاخذه می‌کرد، هیچ جنبنده‌ای بر روی آن (=زمین) باقی نمی‌گذارد، و لیکن آن‌ها را تا زمان معین مهلت می‌دهد، پس هنگامی‌که اجل‌شان فرا رسد، نه ساعتی تأخیر می‌کنند و نه ساعتی پیشی می‌گیرند.');
INSERT INTO "hussein_taji" VALUES(16,62,'16:62','و (آن‌ها) برای الله چیزهایی قرار می‌دهند که خودشان (از آن) کراهت دارند، و زبان‌هایشان (سخن) دروغ بیان می‌کند که نیکی‌ها از آنِ ایشان است، بی‌‌تردید برای آنان آتش (جهنم) است، و قطعاً آن‌ها از پیشگامان (جهنم) هستند.');
INSERT INTO "hussein_taji" VALUES(16,63,'16:63','(ای پیامبر!) به الله سوگند که به سوی امت‌های پیش از تو (پیامبرانی) فرستادیم، پس شیطان اعمال‌شان را برای آن‌ها بیاراست، لذا امروز او دوست (و یاور) شان است، و عذاب دردناکی برای آن‌هاست.');
INSERT INTO "hussein_taji" VALUES(16,64,'16:64','و (ای پیامبر! ما) کتاب (قرآن) را بر تو نازل نکردیم؛ مگر برای اینکه آنچه را که در آن اختلاف کردند، برای آن‌ها روشن سازی، و (نیز) مایۀ هدایت و رحمت برای گروهی که ایمان می‌آورند؛ باشد.');
INSERT INTO "hussein_taji" VALUES(16,65,'16:65','و الله از آسمان آبی فرو فرستاد، پس با آن زمین را بعد از مردنش زنده گرداند. بی‌گمان در این، نشانه‌ای است برای گروهی که می‌شنوند.');
INSERT INTO "hussein_taji" VALUES(16,66,'16:66','و همانا در (وجود) چهار پایان برای شما عبرتی است، از آنچه در شکم آن‌ها، از میان سرگین و خون، شیر خالص که برای نوشندگان گوارا است، به شما می‌نوشانیم.');
INSERT INTO "hussein_taji" VALUES(16,67,'16:67','و (نیز) از میوه‌های درختان خرما و انگور، مسکرات (نا پاک) و روزی خوب (و پاکیزه) می‌گیرید. بی‌گمان در این نشانه‌ای است برای گروهی که خرد می‌ورزند.');
INSERT INTO "hussein_taji" VALUES(16,68,'16:68','و پروردگار تو به زنبور عسل الهام کرد که: «از کوه‌ها و درختان و داربست‌هایی که (مردم) می‌سازند، خانه‌هایی برگزین،');
INSERT INTO "hussein_taji" VALUES(16,69,'16:69','آنگاه از (شیرۀ) تمام میوه‌ها (و گل‌ها) بخور، سپس راه‌های هموار پروردگارت را بپیما» از شکم آن‌ها، نوشیدنی با رنگ‌های گوناگون بیرون می‌آید که در آن شفا برای مردم است. بی‌شک در این نشانه‌ای است برای گروهی که می‌اندیشند.');
INSERT INTO "hussein_taji" VALUES(16,70,'16:70','و الله شما را آفرید، سپس می‌میراند، و بعضی از شما را به پیری و (فرتوتی) می‌رساند، تا بعد از علم (و آگاهی) چیزی نداند (و همه را فراموش کند) بی‌گمان الله دانای تواناست.');
INSERT INTO "hussein_taji" VALUES(16,71,'16:71','و الله برخی از شما را بر برخی (دیگر) در روزی برتری داده است، پس کسانی‌که برتری داده شده‌اند، حاضر نیستند از روزی خود به بردگانشان بدهند، تا همگی در آن برابر شوند، آیا آن‌ها نعمت الله را انکار می‌کند؟!');
INSERT INTO "hussein_taji" VALUES(16,72,'16:72','و الله برای شما از (جنس) خودتان همسرانی قرار داد، و از همسران‌تان برای شما فرزندان و نوادگان پدید آورد، و از پاکیزه‌ها به شما روزی داد، آیا به باطل ایمان می‌آورند، و به نعمت الله آن‌ها کفر می‌ورزند؟!');
INSERT INTO "hussein_taji" VALUES(16,73,'16:73','و آن‌ها غیر از الله، چیزی را می‌پرستند که هیچ روزی را برای آن‌ها از آسمان‌ها و زمین در اختیار ندارند، و (هرگز) نمی‌توانند.');
INSERT INTO "hussein_taji" VALUES(16,74,'16:74','پس برای الله مثل‌ها مزنید، یقیناً الله می‌داند، و شما نمی‌دانید.');
INSERT INTO "hussein_taji" VALUES(16,75,'16:75','الله مثلی زده: بردۀ مملوکی را که بر هیچ چیز توانا نیست، و کسی را که از جانب خود، روزی نیکو بخشیده‌ایم، پس او پنهان و آشکار از آن (روزی) انفاق می‌کند، آیا (این دو نفر) برابرند؟! [ مطلب این است که با این که برده و شخص آزاد هردو انسان‌اند و مخلوق می‌باشند و نیز در بسیاری از چیزها با هم مشترکند، ولی باز هم از لحاظ درجه و منزلت یکسان شمرده نشده‌اند. پس چگونه می‌شود بت‌ها را با الله متعال یکسان قرار داد؟] ستایش مخصوص الله است، بلکه بیشترشان نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(16,76,'16:76','و الله مثل (دیگری) زده است: دو مرد را، که یکی از آن دو، گنگ است، و بر هیچ چیزی توانا نیست، سر بار صاحب (و مولای) خود است، هر کجا او را بفرستد، هیچ خیری را نمی‌آورد. (و مأموریتش را خوب انجام نمی‌دهد). آیا او و کسی‌که به عدل فرمان می‌دهد، و خود بر راه مستقیم قرار دارد، برابر است؟!');
INSERT INTO "hussein_taji" VALUES(16,77,'16:77','(علم) غیب آسمان‌ها و زمین از آن الله است (و تنها او همه را می‌داند) و امر قیامت مانند چشم برهم‌زدن و یا از آن هم نزدیک‌تر است، بی‌گمان الله بر همه چیز تواناست.');
INSERT INTO "hussein_taji" VALUES(16,78,'16:78','و الله شما را از شکم مادران‌تان بیرون آورد، در حالی‌که هیچ چیز نمی‌دانستید، و برای شما گوش و دیدگان و دل‌ها پدید آورد، شاید سپاس گزارید.');
INSERT INTO "hussein_taji" VALUES(16,79,'16:79','آیا آن‌ها به پرندگانی که درجوّ آسمان مسخر (فرمان الله) هستند، نمی‌نگرند، مسلماً در این (امر) نشانه‌هایی است برای گروهی که ایمان می‌آورند.');
INSERT INTO "hussein_taji" VALUES(16,80,'16:80','و الله برای شما از خانه‌هایتان جای آرامش (و سکونت) قرار داد، و از پوست چهار پایان (نیز) برای شما خانه‌هایی (= خیمه‌هایی) قرار داد که آنرا در روز کوچ کردن‌تان و روز اقامت‌تان سبک می‌یابید (و به آسانی آنرا جا بجا می‌کنید) و از پشم‌هایش و کرک‌هایش و مو‌هایش اثاثیه (خانه) و متاع (و اسباب و وسایل زندگی) تا مدت معینی (برایتان) قرار داد.');
INSERT INTO "hussein_taji" VALUES(16,81,'16:81','و (نیز) الله از آنچه آفریده است سایه‌هایی برای شما قرار داده، و از کوه‌ها پناه‌گا‌ه‌هایی برای شما قرار داده است، و برای شما جامه‌هایی برای شما قرار داد که شما را از از گرما (و سرما) حفظ می‌کند، و جامه‌هایی که شما را در (هنگام) جنگ (و درگیری) مصون می‌دارد. این گونه (الله) نعمت‌هایش را بر شما تمام می‌کند، باشد که تسلیم (فرمان او) شوید.');
INSERT INTO "hussein_taji" VALUES(16,82,'16:82','پس (ای پیامبر!) اگر (آن‌ها) روی گرداندند؛ جز این نیست که بر (عهدۀ) تو ابلاغ آشکار است.');
INSERT INTO "hussein_taji" VALUES(16,83,'16:83','(آن‌ها) نعمت الله را می‌شناسند، سپس انکارش می‌کنند، و بیشترشان کافرند.');
INSERT INTO "hussein_taji" VALUES(16,84,'16:84','و روزی‌که از هر امتی گواهی (بر آنان) بر می‌انگیزیم، آنگاه به کسانی‌که کافر شدند، اجازۀ (سخن گفتن) داده نمی‌شود، و نه از آن‌ها تقاضای توبه (و عذر‌خواهی) می‌شود.');
INSERT INTO "hussein_taji" VALUES(16,85,'16:85','و کسانی‌که ستم کردند، هنگامی‌که عذاب را ببینند، پس نه از (عذاب) آن‌ها کاسته می‌شود، و نه آن‌ها مهلت می‌یابند.');
INSERT INTO "hussein_taji" VALUES(16,86,'16:86','و کسانی‌که شرک ورزیدند هنگامی‌که شریکان (= معبودهایی خود ساختۀ) خود را ببینند، می‌گویند: «پروردگارا! اینان شریکان (و معبودان) ما هستند، کسانی‌که ما به جای تو می‌خواندیم». آنگاه (معبودان‌شان) به آن‌ها پاسخ می‌دهند: «به راستی که شما دروغگو هستید»');
INSERT INTO "hussein_taji" VALUES(16,87,'16:87','و در آن روز (همگی) در پیشگاه الله تسلیم شوند، و (تمام) آنچه را افتراء می‌بستند، از (نظر) آنان گم (و نابود) شوند.');
INSERT INTO "hussein_taji" VALUES(16,88,'16:88','کسانی‌که کافر شدند و (مردم را) از راه الله باز داشتند، به (کیفر) آنکه فساد می‌کردند، عذابی بر عذاب‌شان خواهیم افزود.');
INSERT INTO "hussein_taji" VALUES(16,89,'16:89','و روزی‌که در (میان) هر امتی، گواهی از خودشان بر آن‌ها بر می‌انگیزیم، و تو را بر آن‌ها گواه آوریم. و (ما این) کتاب را بر تو نازل کردیم، که بیانگر همه چیز، و هدایت و رحمت و بشارت برای مسلمانان است.');
INSERT INTO "hussein_taji" VALUES(16,90,'16:90','به راستی الله به عدل و احسان و بخشش به نزدیکان فرمان می‌دهد [ نزدیکان درجاتی دارند که به ترتیب: نخست پدر و مادر، سپس فرزندان، سپس برادر و خواهر، سپس عمو و عمه سپس دایی و خاله سپس نزدیکان دیگر.]، و از فحشا و منکر و ستم نهی می‌کند، و شما را پند می‌دهد؛ شاید متذکر شوید.');
INSERT INTO "hussein_taji" VALUES(16,91,'16:91','و به عهد (و پیمان) الله وفا کنید، هرگاه عهد (و پیمان) بستید، و سوگندها را پس از محکم ساختنش نشکنید، درحالی‌که شما الله را کفیل (و ضامن) بر خود قرار داده‌اید، یقیناً الله آنچه را می‌کنید؛ می‌داند.');
INSERT INTO "hussein_taji" VALUES(16,92,'16:92','و مانند آن زن نباشید که پشم‌های تابیدۀ خود را پس از محکم شدن وا می‌تابد. (و قطعه قطعه می‌کند)، که سوگندهای خود را وسیلۀ (خیانت و) فریب سازید، به خاطر اینکه گروهی از گروه (دیگر) بیشتر است. جز این نیست که الله شما را با این وسیله آزمایش می‌کند، یقیناً روز قیامت، آنچه را درآن اختلاف می‌کردید، برای شما روشن خواهد کرد.');
INSERT INTO "hussein_taji" VALUES(16,93,'16:93','و اگر الله می‌خواست همۀ شما را یک امت قرار می‌داد، و لیکن هر کس را بخواهد؛ گمراه می‌کند، و هر کس را بخواهد؛ هدایت می‌نماید، و قطعاً از آنچه می‌کردید، بازخواست خواهید شد.');
INSERT INTO "hussein_taji" VALUES(16,94,'16:94','و سوگند‌هایتان را وسیلۀ (خیانت و) فریب در میان خود قرار ندهید، که (مبادا) گامی بعد از استواری‌اش (بر ایمان) متزلزل شود، و به خاطر باز داشتن (مردم) از راه الله، بدی آن را (در دنیا) بچشید، و (در آخرت) برای شما عذاب بزرگی خواهد بود.');
INSERT INTO "hussein_taji" VALUES(16,95,'16:95','و پیمان (و عهد) الله را به بهای اندک نفروشید، اگر بدانید! آنچه نزد الله است، یقیناً برای شما بهتر است.');
INSERT INTO "hussein_taji" VALUES(16,96,'16:96','آنچه نزد شماست، فنا می‌شود، و آنچه نزد الله است باقی است، و قطعاً به کسانی‌که صبر کردند، پاداش‌شان را به (حسب) بهترین اعمالی که انجام می‌دادند؛ می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(16,97,'16:97','هر کسی‌که کار شایسته‌ای انجام دهد، (خواه) مرد باشد یا زن، در حالی‌که مؤمن است، او را به حیاتی پاک زنده می‌داریم، و مسلماً پاداش‌شان را به (حسب) بهترین اعمالی که انجام می‌دادند؛ به آن‌ها می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(16,98,'16:98','پس هنگامی‌که قرآن می‌خوانی، از (شر) شیطان رانده شده، به الله پناه ببر.');
INSERT INTO "hussein_taji" VALUES(16,99,'16:99','بی‌گمان او بر کسانی‌که ایمان آورده‌اند و بر پروردگار‌شان توکل می‌کنند، (هیچ) تسلطی ندارد.');
INSERT INTO "hussein_taji" VALUES(16,100,'16:100','تسلط او تنها بر کسانی است که او را به دوستی (و سرپرستی) خود برگزیده‌اند، و کسانی‌که (به پیروی از وی) به او (= الله) شرک می‌ورزند.');
INSERT INTO "hussein_taji" VALUES(16,101,'16:101','و هنگامی‌که آیه‌ای را به جای آیۀ دیگر جایگزین (= نسخ) کنیم - و الله به آنچه که نازل می‌کند، داناتر است - (آن‌ها) گویند: «بی‌گمان تو افترا زنی» (هرگز چنین نیست) بلکه بیشتر آن‌ها نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(16,102,'16:102','بگو: «روح القدس (= جبرئیل) آن را از جانب پروردگارت به حق نازل کرده است، تا کسانی را که ایمان آورده‌اند، ثابت قدم گرداند، و برای (عموم) مسلمانان هدایت و بشارت باشد».');
INSERT INTO "hussein_taji" VALUES(16,103,'16:103','و به راستی (ما) می‌دانیم که آن‌ها می‌گویند: «همانا بشری (این آیات را) به او آموزش می‌دهد» در حالی‌که زبان کسی‌که این‌ها به او نسبت می‌دهند عجمی (= غیر عربی) است، و این (قرآن به) زبان عربی آشکار است.');
INSERT INTO "hussein_taji" VALUES(16,104,'16:104','یقیناً کسانی‌که به آیات الله ایمان نمی‌آورند، الله آن‌ها را هدایت نمی‌کند، و برای آن‌ها عذاب دردناکی است.');
INSERT INTO "hussein_taji" VALUES(16,105,'16:105','تنها کسانی دروغ می‌بندند که به آیات الله ایمان ندارند، و ایشانند که دروغگویان (واقعی) هستند.');
INSERT INTO "hussein_taji" VALUES(16,106,'16:106','کسی‌که بعد از ایمانش به الله کافر شود (باز خواست می‌شود) مگر کسی‌که به زور (به کفر) وا داشته شود در حالی‌که قلبش به ایمان آرام باشد [ اهل علم بر این اجماع کرده‌اند که اگر شخصی به کفر آوردن مجبور شود، و او برای نجات جان خویش مرتکب به کفر قولی و فعلی گردد ولی قلباً ایمان داشته باشد، کافر نخواهد بود.]، لیکن کسی‌که سینۀ خود را برای (پذیرش) کفر بگشاید، پس خشم الله برآن‌هاست، و برای آن‌ها عذاب بزرگی است.');
INSERT INTO "hussein_taji" VALUES(16,107,'16:107','این به (خاطر) آن است که زندگی دنیا را بر آخرت ترجیح دادند، و همانا الله گروه کافران (لجوج) را هدایت نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(16,108,'16:108','اینان کسانی هستند که (بر اثر گناهان شان) الله بر دل‌های شان و گوش‌شان و چشمان‌شان مهر نهاده است، و اینان غافلان (واقعی) هستند.');
INSERT INTO "hussein_taji" VALUES(16,109,'16:109','بدون شک آن‌ها در آخرت زیانکارانند.');
INSERT INTO "hussein_taji" VALUES(16,110,'16:110','آنگاه پروردگار تو برای کسانی‌که هجرت کردند، بعد از آن که شکنجه (و عذاب) کشیدند (تا سخن کفر بگویند) سپس (در راه الله) جهاد کردند، و صبر نمودند، بی‌گمان پروردگارت بعد از این (رنج‌ها) آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(16,111,'16:111','(به یاد آورید روز قیامت، آن) روزی‌که هرکس (به دفاع) از خود به مجادله بر می‌خیزد، و به هر کسی (پاداش) آنچه کرده است؛ به طور کامل (و تمام) داده می‌شود، و به آن‌ها ستم نخواهد شد.');
INSERT INTO "hussein_taji" VALUES(16,112,'16:112','و الله مثلی زده است: قریه‌ای که ایمن و آسوده بود، همواره روزی‌اش به فراوانی از هرجا می‌رسید، پس (اهلش) به نعمت‌های الله ناسپاسی کردند، آنگاه الله به (کیفر) آنچه که انجام می‌دادند؛ لباس گرسنگی و ترس (و نا امنی) را بر آن‌ها پوشانید (و طعمش را چشانید).');
INSERT INTO "hussein_taji" VALUES(16,113,'16:113','و به راستی پیامبری از خودشان به سوی آن‌ها آمد، پس او را تکذیب کردند، آنگاه در حالی‌که ظالم بودند، عذاب (الهی) آن‌ها را فرو گرفت.');
INSERT INTO "hussein_taji" VALUES(16,114,'16:114','پس از آنچه که الله به شما روزی داده است؛ حلال (و) پاکیزه بخورید، و شکر و نعمت الله را بجای می‌آورید، اگر او را می‌پرستید.');
INSERT INTO "hussein_taji" VALUES(16,115,'16:115','(الله) تنها مردار، و خون و گوشت خوک و آنچه را که (هنگام ذبح) نام غیر الله برآن برده شده است؛ بر شما حرام کرده است، پس کسی‌که ناچار شود در حالی‌که نه ستمکار باشد و نه از حد تجاوز کند (و از آن‌ها بخورد) پس بی‌گمان الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(16,116,'16:116','و چیزی را که زبان‌تان به دروغ توصیف می‌کند؛ نگویید: «این حلال است، و آن حرام» تا بر الله افترا ببندید، یقیناً کسانی‌که بر الله افترا می‌بندند؛ رستگار نمی‌شوند.');
INSERT INTO "hussein_taji" VALUES(16,117,'16:117','(در این دنیا) بهره‌ای اندک (نصیب‌شان می‌شود) و برای آن‌ها (در آخرت) عذاب دردناک است.');
INSERT INTO "hussein_taji" VALUES(16,118,'16:118','و برکسانی‌که یهودی شدند؛ حرام کرده بودیم، چیزهای را که پیش از این بر تو حکایت کردیم، و (ما) به آن‌ها ستم نکردیم، و لیکن آن‌ها به خودشان ستم کردند.');
INSERT INTO "hussein_taji" VALUES(16,119,'16:119','سپس پروردگار تو برای کسانی‌که از روی جهالت بدی کردند، آنگاه بعد از آن توبه کردند، و (اعمال خود را) اصلاح کردند، یقیناً پروردگارت بعد از آنکه (توبه، برای آن‌ها) آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(16,120,'16:120','به راستی ابراهیم (به تنهایی) امتی بود، فرمانبردار الله، حنیف (= خالی از انحراف) بود و (هرگز) از مشرکان نبود [ به سورۀ آل عمران آیه رجوع فرمایید.].');
INSERT INTO "hussein_taji" VALUES(16,121,'16:121','شکرگزار نعمت‌های الله بود، (الله) او را برگزید، و به راه راست هدایتش نمود.');
INSERT INTO "hussein_taji" VALUES(16,122,'16:122','(ما) در دنیا به او نیکی عطا کردیم؛ و قطعاً او در آخرت از صالحان است.');
INSERT INTO "hussein_taji" VALUES(16,123,'16:123','سپس به تو (ای پیامبر!) وحی کردیم که از آیین ابراهیم که حنیف بود و از مشرکان نبود؛ پیروی کن.');
INSERT INTO "hussein_taji" VALUES(16,124,'16:124','(رعایت تعظیم روز) شنبه تنها بر کسانی‌که در آن اختلاف کردند (بعنوان مجازات) مقرر شد، و بی‌گمان پروردگارت روز قیامت در آنچه اختلاف می‌کردند؛ میان آن‌ها داوری می‌کند.');
INSERT INTO "hussein_taji" VALUES(16,125,'16:125','با حکمت و پند نیکو (مردم را) به راه پروردگارت دعوت نما، و با روشی که نیکو‌تر است، با آن‌ها (بحث و) مناظره کن، بی‌تردید پروردگارت به (حال) کسی‌که از راه او گمراه شده است؛ دانا‌تر است، و (نیز) او به هدایت‌یافتگان؛ دانا‌تر است.');
INSERT INTO "hussein_taji" VALUES(16,126,'16:126','و اگر خواستید مجازات کنید، پس چنان مجازات کنید که شما را مجازات کرده‌اند، و اگر صبر نمایید، مسلماً آن برای صابران بهتر است.');
INSERT INTO "hussein_taji" VALUES(16,127,'16:127','و (ای پیامبر گرامی) صبر کن و صبر تو جز به (توفیق) الله نیست، و بر آن‌ها اندوهگین مشو، و از آنچه مکر می‌ورزند؛ دلتنگ مباش.');
INSERT INTO "hussein_taji" VALUES(16,128,'16:128','همانا الله با کسانی است که تقوا پیشه کردند، و کسانی‌که نیکو‌کارانند.');
INSERT INTO "hussein_taji" VALUES(17,1,'17:1','پاک و منزه است کسی‌که بنده‌اش را شبی از مسجد الحرام به مسجد الأقصی - که گردا‌گردش را برکت داده‌‌ایم برد؛ تا برخی از آیات خود را به او بنمایانیم، بی‌گمان اوست که شنوای بیناست.');
INSERT INTO "hussein_taji" VALUES(17,2,'17:2','و (ما) به موسی کتاب (تورات) دادیم، و آن را (مایۀ) هدایت برای بنی اسرائیل گرداندیم، (و فرمودیم:) که جز من کارسازی بر مگزینید.');
INSERT INTO "hussein_taji" VALUES(17,3,'17:3','ای فرزندان کسانی‌که با نوح (بر کشتی) سوار کردیم، به راستی او (= نوح) بنده‌ای شکر گزار بود.');
INSERT INTO "hussein_taji" VALUES(17,4,'17:4','و (ما) به بنی اسرائیل در کتاب (تورات) وحی کردیم که دو بار در زمین فساد خواهید کرد، و برتری جویی (و طغیان) بزرگی خواهید نمود.');
INSERT INTO "hussein_taji" VALUES(17,5,'17:5','پس هنگامی‌که وعدۀ نخستین آن دو (بار) فرا رسد؛ گروهی از بندگان بسیار نیرومند (پیکار جوی) خود را بر شما برانگیزیم (تا شما را درهم کوبند و نابود کنند) پس (برای قتل و کشتار) درون خانه‌ها را جستجو کنند، و این وعده شدنی (و قطعی) است.');
INSERT INTO "hussein_taji" VALUES(17,6,'17:6','آنگاه شما را بر آن‌ها چیره گردانیم، و شما را با اموال و فرزندان یاری‌تان کنیم، و (تعداد) نفرات شما را بیشتر (از دشمن) گردانیم.');
INSERT INTO "hussein_taji" VALUES(17,7,'17:7','اگر نیکی کنید، به خودتان نیکی کرده‌اید، و اگر بدی کنید، باز به خود کرده‌اید، پس هنگامی‌که وعدۀ دیگر فرا رسد، (چنان دشمنان بر شما مسلط هستند که) آثار غم و اندوه در چهره‌هایتان ظاهر می‌شود، و به مسجد (الأقصی) در آیند؛ همان‌گونه که در مرتبۀ اول وارد شدند، و آنچه را زیر سلطۀ خود یابند؛ یکسره نابود گردانند.');
INSERT INTO "hussein_taji" VALUES(17,8,'17:8','امید است که پروردگارتان به شما رحم فرماید، و اگر برگردید، ما هم باز می‌گردیم، و جهنم را برای کافران زندان قرار داده‌ایم.');
INSERT INTO "hussein_taji" VALUES(17,9,'17:9','بی‌شک این قرآن، به راهی که آن استوار‌ترین راه‌هاست؛ هدایت می‌کند، و به مؤمنانی که کار‌های شایسته انجام می‌دهند بشارت می‌دهد که برای آن‌ها پاداش بزرگی است.');
INSERT INTO "hussein_taji" VALUES(17,10,'17:10','و این‌که برای کسانی‌که به آخرت ایمان ندارند عذاب دردناکی را آماده کرده‌ایم.');
INSERT INTO "hussein_taji" VALUES(17,11,'17:11','و انسان بدی‌ها را می‌طلبد، آن گونه که نیکی‌ها را طلب می‌کند، و انسان همیشه عجول است.');
INSERT INTO "hussein_taji" VALUES(17,12,'17:12','و (ما) شب و روز را دو نشانه قرار دادیم، پس نشانه شب را محو کردیم، و نشانۀ روز را روشنی بخش ساختیم، تا (در پرتو آن) از پروردگار‌تان فضل (و روزی) بجویید، و تا شمارش سال‌ها و حساب را بدانید، و هر چیز را به تفصیل بیان کرده‌ایم.');
INSERT INTO "hussein_taji" VALUES(17,13,'17:13','و هر انسانی اعمال (خیر و شرش) را در گردنش آویخته‌ایم، و روز قیامت کتابی برای او بیرون می‌آوریم که آن را سرگشوده می‌بینید (که همانا نامۀ اعمالش است).');
INSERT INTO "hussein_taji" VALUES(17,14,'17:14','(و به او می‌فرماییم:) کتابت را بخوان، کافی است که امروز خود حساب گر خویش باشی.');
INSERT INTO "hussein_taji" VALUES(17,15,'17:15','کسی‌که هدایت یابد برای خود هدایت یافته است، و کسی‌که گمراه گردد، فقط به زیان خود گمراه شده است. و هیچ کس بار (گناه) دیگری را به دوش نمی‌کشد، و ما هرگز عذاب نخواهیم کرد؛ مگر آنکه پیامبری بفرستیم.');
INSERT INTO "hussein_taji" VALUES(17,16,'17:16','و هنگامی‌که بخواهیم دیاری را نابود کنیم، به مترفین (و سرکشان) آنجا فرمان (اطاعت) می‌دهیم، پس چون در آنجا به فسق (و فساد) بر خاستند، آنگاه وعدۀ (عذاب) بر آن‌ها محقق می‌گردد، پس آن‌ها را به کلی نابود گردانیم.');
INSERT INTO "hussein_taji" VALUES(17,17,'17:17','و چه بسیار نسل‌ها را که بعد از نوح (زندگی می‌کردند) هلاک کردیم، و کافی است که پروردگارت (نسبت)به گناهان بندگانش دانای بیناست.');
INSERT INTO "hussein_taji" VALUES(17,18,'17:18','هر کس که (دنیای) زود گذر را بخواهد، در آن (دنیا) برای هر که بخواهیم؛ همان اندازه که بخواهیم؛ زود خواهیم داد، سپس جهنم را برای او قرار خواهیم داد که نکوهیدۀ مردود در (آتش سوزان) آن درآید.');
INSERT INTO "hussein_taji" VALUES(17,19,'17:19','و کسی‌که (سرای) آخرت را بخواهد، و برای آن سعی و تلاش خود را بکند، در حالی‌که مؤمن باشد، پس اینان سعی (و تلاش) شان مشکور است (و پاداش داده خواهد شد).');
INSERT INTO "hussein_taji" VALUES(17,20,'17:20','هر یک از اینان و آنان (از هر دو گروه) از عطای پروردگارت (بهره و) کمک می‌دهیم، و عطای پروردگارت هرگز (از کسی) باز داشته شده نیست (مؤمن و کافر همه از نعمت‌های دنیوی بهره‌مند می‌شوند)');
INSERT INTO "hussein_taji" VALUES(17,21,'17:21','بنگر چگونه بعضی را بر بعضی دیگر برتری داده‌ایم، و یقیناً درجات آخرت و برتری‌هایش از این هم بیشتر است.');
INSERT INTO "hussein_taji" VALUES(17,22,'17:22','(هرگز) معبود دیگری را با الله (شریک) قرار مده، که آنگاه نکوهیدۀ خوار (بی یار و یاور) خواهی نشست.');
INSERT INTO "hussein_taji" VALUES(17,23,'17:23','و پروردگارت (چنین) مقرر داشته است که: جز او را نپرستید، و به پدر و مادر نیکی کنید، هرگاه یکی از آن دو، یا هر دوی آن‌ها نزد تو به سن پیری رسند، (حتی) به آن‌ها (کلمۀ) افّ (= کمترین کلمه رنج آور) نگو، و بر (سر) آن‌ها فریاد نزن، و با نیکی (و بزرگوارانه) با آن‌ها سخن بگو.');
INSERT INTO "hussein_taji" VALUES(17,24,'17:24','و از روی مهربانی بال فروتنی (و خاکساری) برای آن‌ها فرود آور، و بگو: «پروردگارا! به آنان رحمت آور، همان‌گونه که مرا در کودکی پرورش دادند».');
INSERT INTO "hussein_taji" VALUES(17,25,'17:25','پروردگار شما به آنچه در دل‌های تان است؛ آگاه‌تر است، اگر صالح باشید، بی‌گمان او برای توبه کنندگان آمرزنده است.');
INSERT INTO "hussein_taji" VALUES(17,26,'17:26','و حق خویشاوندان را بپرداز، و (همچنین حق) مسکین، و در راه مانده را (بپرداز) و هرگز اسراف (و تبذیر) نکن.');
INSERT INTO "hussein_taji" VALUES(17,27,'17:27','به راستی که تبذیرکنندگان؛ برادران شیاطین هستند، و شیطان نسبت به پروردگارش بسیار ناسپاس است.');
INSERT INTO "hussein_taji" VALUES(17,28,'17:28','و اگر به انتظار رحمتی از پروردگارت که به آن امیدواری، از آنان (= مسکینان و در راه ماندگان) اعراض کنی، پس با گفتاری نرم با آنان سخن بگو.');
INSERT INTO "hussein_taji" VALUES(17,29,'17:29','و (هرگز) دستت را به گردن خودت نبند، (و ترک انفاق منما) و بیش از حد آن را مگشا، آنگاه سرزنش شدۀ درمانده بنشینی.');
INSERT INTO "hussein_taji" VALUES(17,30,'17:30','یقیناً پروردگارت روزی را برای هرکس که بخواهد گشاده و (یا) تنگ می‌دارد، بی‌گمان او به بندگانش دانای بیناست.');
INSERT INTO "hussein_taji" VALUES(17,31,'17:31','و فرزندان‌تان را از ترس تنگدستی نکشید، ما آن‌ها و شما را روزی می‌دهیم، به راستی کشتن آن‌ها گناه بزرگی است.');
INSERT INTO "hussein_taji" VALUES(17,32,'17:32','و نزدیک زنا نشوید، که کار بسیار زشت، و بد راهی است!');
INSERT INTO "hussein_taji" VALUES(17,33,'17:33','و جانی که الله (خونش را) حرام شمرده، جز به حق نکشید، و کسی‌که مظلوم کشته شود، برای ولیش (برای حق قصاص) قدرتی قرار داده‌ایم، پس در کشتن (و خون‌خواهی) اسراف نکند، بی‌گمان او مورد حمایت است.');
INSERT INTO "hussein_taji" VALUES(17,34,'17:34','و به مال یتیم نزدیک نشوید، جز به روشی که آن بهتر است، تا آنکه به (حد بلوغ) و رشدش برسد، و به عهد (خویش) وفا کنید، بی‌گمان از عهد سؤال می‌شود.');
INSERT INTO "hussein_taji" VALUES(17,35,'17:35','و هنگامی‌که پیمانه می‌کنید، پیمانه را کامل (و تمام) دهید، و با ترازوی درست وزن کنید، این برای شما بهتر و عاقبتش نیکو‌تر است.');
INSERT INTO "hussein_taji" VALUES(17,36,'17:36','و از آنچه به آن علم نداری، پیروی نکن، بدون شک گوش و چشم و دل، هر یک از این‌ها از آن باز خواست خواهند شد.');
INSERT INTO "hussein_taji" VALUES(17,37,'17:37','و در (روی) زمین با تکبر راه مرو، بی‌گمان تو نمی‌توانی زمین را بشکافی، و (هرگز) در بلندی (و قامت) به کوه‌ها نمی‌رسی.');
INSERT INTO "hussein_taji" VALUES(17,38,'17:38','همۀ این کار‌های (ذکر شده) بدی (و گناه) آن نزد پروردگارت ناپسند است.');
INSERT INTO "hussein_taji" VALUES(17,39,'17:39','این (احکام) از آن حکمتی است که پروردگارت به تو وحی فرستاده، و هرگز معبودی را با الله قرار نده، که نکوهیدۀ مطرود در جهنم افکنده می‌شوی.');
INSERT INTO "hussein_taji" VALUES(17,40,'17:40','آیا پروردگارتان برای شما پسران برگزید، و (خودش) از فرشتگان دختران را برگزیده است؟! بی‌گمان شما سخن بزرگ (و زشتی) می‌‌گویید.');
INSERT INTO "hussein_taji" VALUES(17,41,'17:41','و به راستی (ما) در این قرآن (سخنان) گوناگون آوردیم، تا متذکر شوند، ولی جز به رمیدن‌شان نمی‌افزاید.');
INSERT INTO "hussein_taji" VALUES(17,42,'17:42','(ای پیامبر!) بگو: «اگر آن چنان که آن‌ها (= مشرکان) می‌گویند با او معبودانی بود، در این صورت، (معبودان) سعی می‌کردند راهی به سوی (الله) صاحب عرش پیدا کنند»');
INSERT INTO "hussein_taji" VALUES(17,43,'17:43','او منزه و پاک است، و از آنچه آن‌ها می‌گویند بسا برتر است.');
INSERT INTO "hussein_taji" VALUES(17,44,'17:44','آسمان‌ها ی هفتگانه و زمین و هرکس که در آن‌هاست همه تسبیح او می‌گویند، و هیچ چیز نیست؛ مگر آنکه به ستایش او تسبیح می‌گوید، و لیکن شما تسبیح آن‌ها را در نمی‌یابید، بی‌گمان او بردبار آمرزنده است.');
INSERT INTO "hussein_taji" VALUES(17,45,'17:45','و (ای پیامبر!) هنگامی‌که قرآن بخوانی، میان تو و میان کسانی‌که به آخرت ایمان نمی‌آورند، پرده‌ای پوشیده قرار می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(17,46,'17:46','و بر دل‌های شان پوشش‌هایی قرار می‌دهیم تا آن را در نیابند، و در گوش‌هایشان سنگینی (می گذاریم که نشنوند). و چون پروردگارت را در قرآن به یگانگی یاد کنی، آن‌ها با نفرت پشت می‌کنند (و می‌گریزند).');
INSERT INTO "hussein_taji" VALUES(17,47,'17:47','و چون به تو گوش فرا می‌دهند، ما دانا‌تریم به چه (منظور) به آن (= قرآن) گوش فرا می‌دهند، و (نیز) هنگامی‌که آنان نجوی می‌کنند، آنگاه که ستمگران می‌گویند: «شما جز از مردی که جادو شده پیروی نمی‌کنید».');
INSERT INTO "hussein_taji" VALUES(17,48,'17:48','بنگر چگونه برای تو مثل‌ها زدند، پس گمراه شدند، لذا نمی‌توانند راهی (به سوی حق) بیابند.');
INSERT INTO "hussein_taji" VALUES(17,49,'17:49','و (کافران) گفتند: «آیا هنگامی‌که ما استخوان‌هایی (پوسیده) و پراکنده شدیم، آیا دگر بار با آفرینش تازه‌ای برانگیخته خواهیم شد؟!»');
INSERT INTO "hussein_taji" VALUES(17,50,'17:50','بگو: «سنگ باشید یا آهن،');
INSERT INTO "hussein_taji" VALUES(17,51,'17:51','یا (هر) مخلوقی که در خاطرتان بزرگ می‌نماید، (باز هم الله قادر است شما را دوباره زنده کند). پس خواهند گفت: «چه کسی ما را باز می‌گرداند؟!» بگو: «(همان) کسی‌که نخستین بار شما را آفرید». پس آنان سر‌هایشان را (از روی تردید) به تو می‌جنبانند و می‌گویند: «آن چه زمانی خواهد بود؟!» بگو: «شاید که نزدیک باشد،');
INSERT INTO "hussein_taji" VALUES(17,52,'17:52','روزی‌که (الله) شما را (از قبر‌هایتان) فرا می‌خواند، پس شما هم سپاس گویان (دعوت) او را اجابت می‌کنید، و می‌پندارید جز مدت کوتاهی (در دنیا) درنگ نکرده‌اید».');
INSERT INTO "hussein_taji" VALUES(17,53,'17:53','و به بندگانم بگو: «(با یکدیگر) سخنی بگویند که آن بهترین باشد، بی‌گمان شیطان میان آن‌ها فتنه (و فساد) می‌کند، به راستی که شیطان دشمن آشکاری برای انسان است».');
INSERT INTO "hussein_taji" VALUES(17,54,'17:54','و پروردگار شما به (حال) شما دانا‌تر است، اگر بخواهد بر شما رحمت می‌آورد، یا اگر بخواهد عذاب‌تان می‌کند، و (ای پیامبر!) ما تو را بر آن‌ها نگهبان نفرستاده‌ایم.');
INSERT INTO "hussein_taji" VALUES(17,55,'17:55','پروردگار تو، به (حال هر) کسی‌که در آسمان‌ها و زمین است، داناتر است، و به راستی (ما) بعضی پیامبران را بر بعضی (دیگر) برتری دادیم، و به داود زبور عطا کردیم.');
INSERT INTO "hussein_taji" VALUES(17,56,'17:56','(ای پیامبر!) بگو: «کسانی را که به جای او (معبود خود) می‌پندارید؛ بخوانید، پس آن‌ها نه می‌توانند بلا (و مشکلی) را از شما دورسازند، و نه تغییر دهند.');
INSERT INTO "hussein_taji" VALUES(17,57,'17:57','کسانی را که (کافران) آنان را می‌خوانند، خودشان به سوی پروردگارشان وسیلۀ (تقرب) می‌جویند، که کدام یک از آن‌ها نزدیکترند، و به رحمت او امیدوارند و از عذابش می‌ترسند، بی‌گمان عذاب پروردگارت همواره ‌ترسناک است».');
INSERT INTO "hussein_taji" VALUES(17,58,'17:58','و هیچ (آبادی و) قریه‌ای (از قریه‌های یاغیان و مفسدان) نیست؛ مگر اینکه پیش از (فرا رسیدن) روز قیامت، ما آنان را هلاک خواهیم کرد، یا به عذاب شدیدی عذاب‌شان می‌کنیم، این در کتاب (= لوح محفوظ) ثبت است.');
INSERT INTO "hussein_taji" VALUES(17,59,'17:59','و (هیچ چیز) ما را از فرستادن معجزات باز نداشت؛ جز اینکه پیشینیان آن را تکذیب کردند، و به (قوم) ثمود، ماده شتر که (معجزه‌ای) روشن‌گر بود، دادیم، پس (آن‌ها) به آن ستم کردند (و آن را کشتند). و ما معجزات را جز برای ترسانیدن نمی‌فرستیم.');
INSERT INTO "hussein_taji" VALUES(17,60,'17:60','(و ای پیامبر! به یاد آور) هنگامی را که به تو گفتیم: «بی‌شک پروردگارت به مردم احاطه (کامل) دارد، و رؤیایی را که (در شب اسراء و معراج عیاناً) به تو نشان دادیم و (همچنین) درخت ملعون را در قرآن، (چیزی) جز برای آزمایش مردم قرار ندادیم، ما آن‌ها را می‌ترسانیم، پس (این ترسانیدن) جز سرکشی بزرگ، چیزی بر آن‌ها نمی‌افزاید».');
INSERT INTO "hussein_taji" VALUES(17,61,'17:61','و (به یادآور) چون به فرشتگان گفتیم: «برای آدم سجده کنید» پس (همگی) سجده کردند؛ جز ابلیس که گفت: «آیا برای کسی سجده کنم که (او را) از گِل آفریده‌ای؟!».');
INSERT INTO "hussein_taji" VALUES(17,62,'17:62','(سپس) گفت: «به من خبر بده، این کسی را که بر من برتری داده‌ای (به چه دلیل بوده است؟!) اگر مرا تا روز قیامت مهلت دهی، فرزندانش را؛ جز عدۀ کمی (گمراه و) ریشه کن خواهم کرد».');
INSERT INTO "hussein_taji" VALUES(17,63,'17:63','(الله) فرمود: «برو، پس هر کس از آنان، از تو پیروی کند، بی‌تردید جهنم کیفر شماست، که کیفری کامل (و فراوان) است،');
INSERT INTO "hussein_taji" VALUES(17,64,'17:64','و هرکس از آنان را که می‌توانی با صدایت بر انگیز، و با (یاری) سواران و پیادگانت بر آن‌ها بتاز، و در اموال و فرزندان با آن‌ها شرکت جوی، و به آن‌ها وعده بده - و شیطان جز فریب به آن‌ها وعده نمی‌دهد -');
INSERT INTO "hussein_taji" VALUES(17,65,'17:65','بی‌گمان تو هرگز بر بندگان من سلطه‌‌ای نخواهی داشت؛ و پروردگار تو برای نگهبانی آن‌ها کافی است».');
INSERT INTO "hussein_taji" VALUES(17,66,'17:66','پروردگار شما کسی است که کشتی را در دریا برای شما به حرکت در می‌آورد، تا از فضلش بهره‌مند شوید، بی‌گمان او نسبت به شما مهربان است.');
INSERT INTO "hussein_taji" VALUES(17,67,'17:67','و هنگامی‌که در دریا سختی به شما برسد، جز او همه کسانی را که می‌خوانید؛ فراموش می‌کنید، پس چون شما را به خشکی نجات دهد؛ روی می‌گردانید، و انسان بسیار ناسپاس است.');
INSERT INTO "hussein_taji" VALUES(17,68,'17:68','آیا از آن که شما را به کناره‌ای از خشکی فرو برد؛ یا تند بادی از سنگریزه بر شما بفرستد؛ ایمن شده‌اید؟! آنگاه برای خود هیچ نگهبان (و کارسازی) نیابید.');
INSERT INTO "hussein_taji" VALUES(17,69,'17:69','آیا از آن که بار دیگر شما را به دریا باز گرداند؛ سپس تند باد کوبنده‌ای بر شما بفرستد، آنگاه شما را به (کیفر) کفرتان غرق کند؛ ایمن شده‌اید؟! سپس در برابر ما بازخواست کننده (و خونخواهی برای خود) نیابید.');
INSERT INTO "hussein_taji" VALUES(17,70,'17:70','و به راستی ما فرزندان آدم را گرامی داشتیم، و آن‌ها را در خشکی و دریا (بر مرکبها) حمل کردیم، و از انواع (روزیهای) پاکیزه به آن‌ها روزی دادیم، و آن‌ها را بر بسیاری از موجوداتی که آفریده‌ایم؛ چنانکه باید برتری بخشیدیم.');
INSERT INTO "hussein_taji" VALUES(17,71,'17:71','(به یاد آورید) روزی را که هرگروهی را با پیشوایشان فرا خوانیم، پس کسی‌که نامۀ (اعمال) اش را به (دست) راستش داده شود، پس اینان نامه شان را (با شادمانی) می‌خوانند، و به‌اندازۀ رشتۀ شکاف هستۀ خرمایی ستم نمی‌بینند.');
INSERT INTO "hussein_taji" VALUES(17,72,'17:72','و (اما) کسی‌که در این (دنیا از دیدن حق) نا بینا بوده است؛ پس او در آخرت (نیز) نابینا و گمراه‌تر است.');
INSERT INTO "hussein_taji" VALUES(17,73,'17:73','و (ای پیامبر!) نزدیک بود (مشرکان) تو را (با وسوسه‌های خود) از آنچه بر تو وحی کرده‌ایم؛ منصرف کنند، تا غیر آن را به ما نسبت دهی، و آنگاه تو را دوست خود بگیرند.');
INSERT INTO "hussein_taji" VALUES(17,74,'17:74','و اگر (ما) تو را ثابت قدم نمی‌کردیم؛ به راستی نزدیک بود اندکی به آن‌ها تمایل کنی.');
INSERT INTO "hussein_taji" VALUES(17,75,'17:75','(اگر چنین می‌کردی) آنگاه دو چندان عذاب در زندگی دنیا و دو چندان عذاب (بعد از) مرگ به تو می‌چشاندیم، سپس در برابر ما برای خود یاوری نمی‌یافتی.');
INSERT INTO "hussein_taji" VALUES(17,76,'17:76','و نزدیک بود (با مکر و توطئه) تو را از (سرزمین) (مکه) بلغزانند، تا ترا از آن بیرون کنند، (و اگر چنین می‌کردند) آنگاه پس از تو جز مدت کمی (باقی) نمی‌ماندند.');
INSERT INTO "hussein_taji" VALUES(17,77,'17:77','این (روش و) سنت (ما دربارۀ) پیامبرانی است که پیش از تو فرستادیم، و (هرگز) برای سنت ما تغییر و دگرگونی نخواهی یافت.');
INSERT INTO "hussein_taji" VALUES(17,78,'17:78','نماز را از زوال آفتاب (هنگام ظهر) تا تاریکی شب بر پا دار، و (نیز) قرآن فجر (= نماز صبح) را؛ بی‌گمان هنگام قرآن (خواندن، در نماز) فجر (فرشتگان) حاضر آیند.');
INSERT INTO "hussein_taji" VALUES(17,79,'17:79','و پاسی از شب را (از خواب) برخیز و با آن (= قرآن) نماز بخوان، که برای تو افزون (بر دیگران) است، امید است پروردگارت تو را به مقامی پسندیده برانگیزد.');
INSERT INTO "hussein_taji" VALUES(17,80,'17:80','و بگو: «پروردگارا! مرا به در آوردنی راست (به مدینه) در آور، و به بیرون آوردنی راست، مرا (از مکه) بیرون آور، و از سوی خود حجتی یاری کننده برایم قرار بده».');
INSERT INTO "hussein_taji" VALUES(17,81,'17:81','و بگو: «حق آمد، و باطل نابود شد، بی‌گمان باطل نابود شدنی است».');
INSERT INTO "hussein_taji" VALUES(17,82,'17:82','و از قرآن آنچه را که شفا و رحمت برای مؤمنان است؛ نازل می‌کنیم، و ستمکاران را جز زیان نمی‌افزاید.');
INSERT INTO "hussein_taji" VALUES(17,83,'17:83','و هنگامی‌که برانسان نعمت بخشیم، (از حق) روی بگرداند، و (متکبرانه) پهلویش را دورکند، و هنگامی‌که (کمترین) بدی به او برسد، ناامید گردد.');
INSERT INTO "hussein_taji" VALUES(17,84,'17:84','(ای پیامبر!) بگو: «هر کس به روش خود عمل می‌کند، پس پروردگارتان به کسی‌که او هدایت یافته‌تر است؛ داناتر است».');
INSERT INTO "hussein_taji" VALUES(17,85,'17:85','و (ای پیامبر!) از تو دربارۀ روح سؤال می‌کنند، بگو: «روح از فرمان پروردگار من است، و جز اندکی از دانش به شما داده نشده است».');
INSERT INTO "hussein_taji" VALUES(17,86,'17:86','و اگر بخواهیم آنچه را که به تو وحی کرده‌ایم؛ (از سینه‌ات) می‌بریم، آنگاه در برابر ما برای خود هیچ کار سازی (و مدد کننده‌ای) نخواهی یافت،');
INSERT INTO "hussein_taji" VALUES(17,87,'17:87','لیکن (باقی ماندنش به سبب) رحمتی از جانب پروردگارت است؛ بدون شک فضل او بر تو بزرگ است.');
INSERT INTO "hussein_taji" VALUES(17,88,'17:88','(ای پیامبر!) بگو: «اگر انس و جن (همگی) گرد آیند بر آن که همانند این قرآن را بیاورند، (هرگز) همانند آن را نخواهند آورد، هر چند برخی از آنان یاور برخی (دیگر) باشند».');
INSERT INTO "hussein_taji" VALUES(17,89,'17:89','و به تحقیق (ما) در این قرآن، برای مردم هر مثلی را گوناگون بیان کردیم، پس بیشتر مردم جز ناسپاسی را قبول نکردند،');
INSERT INTO "hussein_taji" VALUES(17,90,'17:90','و گفتند: «ما هرگز به تو ایمان نمی‌آوریم؛ تا اینکه از زمین چشمه‌ای برای ما جاری کنی.');
INSERT INTO "hussein_taji" VALUES(17,91,'17:91','یا باغی از نخل و انگور داشته باشی، آنگاه نهرها در میان آن به خوبی جاری سازی.');
INSERT INTO "hussein_taji" VALUES(17,92,'17:92','یا آسمان را آنچنان که می‌پنداری پاره پاره بر (سر) ما فرود آوری، یا الله و فرشتگان را روبروی (ما) بیاوری،');
INSERT INTO "hussein_taji" VALUES(17,93,'17:93','یا اینکه خآن‌ها ی (پر نقش و نگار) از سیم و زر داشته باشی، یا در آسمان بالا روی، و هرگز به بالا رفتنت ایمان نمی‌آوریم؛ مگر آنکه کتابی بر ما نازل کنی، که آن را بخوانیم». بگو: «پروردگارم منزه است، آیا من جز بشری فرستاده شده هستم؟!».');
INSERT INTO "hussein_taji" VALUES(17,94,'17:94','و مردم را (هیچ چیز) باز نداشت، از آنکه ایمان آوردند هنگامی‌که هدایت به سوی آنان آمد؛ مگر این‌که گفتند: «آیا الله بشری را (بعنوان) پیامبر فرستاده است؟!»');
INSERT INTO "hussein_taji" VALUES(17,95,'17:95','(ای پیامبر!) بگو: «اگر در زمین فرشتگانی بودند که با آرامش (و مطمئن) راه می‌رفتند، مسلماً از آسمان فرشته‌ای را (بعنوان) پیامبر بر آن‌ها نازل می‌کردیم» (اما چون شما انسان هستید پیامبرتان نیز از خودتان است)');
INSERT INTO "hussein_taji" VALUES(17,96,'17:96','(ای پیامبر!) بگو: «همین کافی است که الله میان من و شما گواه باشد، بی‌گمان او (نسبت) به بندگانش دانای بیناست.');
INSERT INTO "hussein_taji" VALUES(17,97,'17:97','و هرکسی را که الله هدایت کند، پس او هدایت یافتۀ (واقعی) است، و هرکسی را که گمراه سازد؛ پس جز الله (سر پرستان و) دوستانی برای او نخواهی یافت، و روز قیامت آنان را بر چهره‌هایشان، نابینا و گنگ و کر محشور می‌کنیم، جایگاه‌شان جهنم است، هرگاه (آتش آن) فرو نشیند، بر آنان شعله‌ای (سوزان) می‌افزاییم.');
INSERT INTO "hussein_taji" VALUES(17,98,'17:98','این کیفر آن‌هاست، به (خاطر) این‌که به آیات ما کافر شدند، و گفتند: «آیا هنگامی‌که ما استخوان‌هایی (پوسیده) و پراکنده شدیم، آیا دگر بار با آفرینش تازه‌ای برانگیخته خواهیم شد؟!».');
INSERT INTO "hussein_taji" VALUES(17,99,'17:99','آیا نمی‌دانند، که الله که آسمان‌ها و زمین را آفریده؛ قادر است که مانند آن‌ها را بیافریند، و برای آنان سر‌آمدی مقررکرده که شکی در آن نیست، سپس ستمکاران جز ناسپاسی را قبول نکردند.');
INSERT INTO "hussein_taji" VALUES(17,100,'17:100','(ای پیامبر!) بگو: «اگرشما مالک گنجینه‌های (بخشش و) رحمت پروردگار من بودید، آنگاه از ترس فقر (و تنگدستی) از انفاق خوداری می‌کردید، و انسان بسیار بخیل (و تنگ نظر) است».');
INSERT INTO "hussein_taji" VALUES(17,101,'17:101','و به راستی (ما) به موسی نُه معجزۀ روشن دادیم، پس (ای پیامبر) از بنی اسرائیل بپرس، آن زمان که (موسی) به سوی آنان آمد، فرعون به او گفت: «ای موسی، گمان می‌کنم تو سحر شده‌ای».');
INSERT INTO "hussein_taji" VALUES(17,102,'17:102','(موسی) گفت: «تو قطعاً می‌دانی که این (معجزها) را جز پروردگار آسمان‌ها و زمین؛ برای روشن‌گری نفرستاده است، و من ای فرعون، یقیناً تو را هلاک شده می‌پندارم».');
INSERT INTO "hussein_taji" VALUES(17,103,'17:103','پس (فرعون) تصمیم گرفت که آنان (= بنی اسرائیل) را از آن سرزمین بیرون کند، پس (ما) او و همۀ کسانی را که با او بودند؛ غرق کردیم.');
INSERT INTO "hussein_taji" VALUES(17,104,'17:104','و بعد از (هلاکت) او به بنی اسرائیل گفتیم: «در (این) سرزمین (شام) ساکن شوید، پس هنگامی‌که وعدۀ آخرت فرا رسد، همۀ شما دسته‌جمعی خواهیم آورد».');
INSERT INTO "hussein_taji" VALUES(17,105,'17:105','و (ما) این (قرآن) را به حق نازل کردیم، و به حق نازل شد، و تو را (ای پیامبر) جز بشارت‌دهنده و هشدار دهنده نفرستادیم.');
INSERT INTO "hussein_taji" VALUES(17,106,'17:106','و قرآنی که (آیات و سوره‌هایش را) جدا ساختیم، تا آن را با درنگ (و تأنّی) بر مردم بخوانی، و آن را تدریج (و کم کم) نازل کردیم.');
INSERT INTO "hussein_taji" VALUES(17,107,'17:107','(ای پیامبر!) بگو: «(خواه) به آن ایمان آورید، یا ایمان نیاورید، بی‌گمان کسانی‌که پیش از آن به آن‌ها دانش داده شده، هنگامی‌که (آیات قرآن) بر آن‌ها خوانده می‌شود، سجده‌کنان به رو در می‌افتند».');
INSERT INTO "hussein_taji" VALUES(17,108,'17:108','و می‌گویند: «منزه است پروردگار ما، که یقیناً وعده‌هایش انجام شدنی است».');
INSERT INTO "hussein_taji" VALUES(17,109,'17:109','و (آن‌ها) گریه‌کنان به رو در می‌افتند، و (تلاوت این قرآن) بر فروتنی شان می‌افزاید.');
INSERT INTO "hussein_taji" VALUES(17,110,'17:110','بگو: «الله» را بخوانید یا «رحمان» را بخوانید، هر کدام را بخوانید، پس (فرقی ندارد) برای او نام‌های نیکوست، و نمازت را نه زیاد بلند بخوان، و نه آنرا خیلی آهسته بخوان، و میان آن دو؛ راهی (معتدل) برگزین.');
INSERT INTO "hussein_taji" VALUES(17,111,'17:111','و بگو: «ستایش برای الله است که فرزندی (برای خود) بر نگزیده و او را در فرمانروای هیچ شریکی نیست، و بخاطر ناتوانی (و ذلت حامی و) سرپرستی برای او نیست، و او را به شایستگی بزرگ بشمار».');
INSERT INTO "hussein_taji" VALUES(18,1,'18:1','حمد و سپاس مخصوص الله است که بر بنده‌اش (محمد) کتاب (قرآن) را نازل کرد، و هیچ گونه کجی و انحرافی در آن قرار نداد.');
INSERT INTO "hussein_taji" VALUES(18,2,'18:2','در حالی‌که ثابت و مستقیم (و نگاهبان کتاب‌های آسمانی) است، تا (بدکاران را) از عذاب سخت او بیم دهد، و مؤمنانی را که کارهای شایسته انجام می‌دهند، مژده دهد که پاداش نیکویی برای آن‌هاست.');
INSERT INTO "hussein_taji" VALUES(18,3,'18:3','(همان بهشتی) که جاودانه در آن خواهند ماند.');
INSERT INTO "hussein_taji" VALUES(18,4,'18:4','و (نیز) کسانی‌که گفتند: الله فرزندی (برای خود) برگزیده است، بیم دهد.');
INSERT INTO "hussein_taji" VALUES(18,5,'18:5','نه ای‌شان به این سخن علم و آگاهی دارند و نه پدران‌شان، سخن بزرگی از دهان‌شان بیرون می‌شود! آنان جز دروغ نمی‌گویند.');
INSERT INTO "hussein_taji" VALUES(18,6,'18:6','پس گویی می‌خواهی در پی ایشان خود را از غم و اندوه هلاک سازی، اگر به این سخن (قرآن) ایمان نیاورند.');
INSERT INTO "hussein_taji" VALUES(18,7,'18:7','ما آنچه که روی زمین است، زینت آن قرار داده‌ایم، تا آن‌ها را بیازماییم که کدام یک‌شان عمل بهتر می‌کنند.');
INSERT INTO "hussein_taji" VALUES(18,8,'18:8','و ما (سرانجام) آنچه که روی زمین است، (صاف می‌کنیم) به خاکی هموار و بی‌گیاه (بیابانی خشک) تبدیل می‌کنیم.');
INSERT INTO "hussein_taji" VALUES(18,9,'18:9','آیا گمان کردی که اصحاب کهف (= غار) و رقیم (= نوشته) از جملۀ نشانه‌های شگفت انگیز ما بوده‌اند؟!');
INSERT INTO "hussein_taji" VALUES(18,10,'18:10','(بیاد آور) آنگاه که آن جوانان به غار پناه بردند، و گفتند: «پروردگارا! ما را از سوی خود رحمتی عطا کن، و راه نجاتی برایمان ما فراهم ساز».');
INSERT INTO "hussein_taji" VALUES(18,11,'18:11','پس (ما پرده‌های خواب را) در غار بر گوش‌هایشان زدیم، و سال‌ها در خواب فرو رفتند.');
INSERT INTO "hussein_taji" VALUES(18,12,'18:12','سپس آنان را بر انگیختیم تا بدانیم کدام یک از آن دو گروه مدت ماندن خود را بهتر حساب کرده است.');
INSERT INTO "hussein_taji" VALUES(18,13,'18:13','ما داستان آنان را به حق برای تو بازگو می‌کنیم. آن‌ها جوانانی بودند که به پروردگار‌شان ایمان آورده بودند، و ما بر هدایت‌شان افزودیم.');
INSERT INTO "hussein_taji" VALUES(18,14,'18:14','و (ما) دل‌های آن‌ها را محکم ساختیم، هنگامی‌که قیام کردند، و گفتند: «پروردگار ما، پروردگار آسمان‌ها و زمین است، و ما هرگز غیر از او معبودی نمی‌پرستیم (اگر چنین کنیم) سخنی گزاف و بیهوده گفته‌ایم.');
INSERT INTO "hussein_taji" VALUES(18,15,'18:15','این قوم ما، معبودهای جز الله انتخاب کرده‌اند، چرا دلیل آشکاری (بر این معبودان) نمی‌آورند؟! چه کسی ستمکار‌تر است از آن فردی که بر الله دروغ ببندد؟!».');
INSERT INTO "hussein_taji" VALUES(18,16,'18:16','و (به آن‌ها گفتیم:) هنگامی‌که از آن‌ها و آنچه را جز الله می‌پرستند؛ کناره‌گیری کردید، پس به غار پناه برید، که پروردگارتان نعمتش را بر شما می‌گستراند، و راه آسایش و نجات از این امر را برای شما فراهم می‌سازد.');
INSERT INTO "hussein_taji" VALUES(18,17,'18:17','و خورشید را می‌دیدی که به هنگام طلوع به سمت راست غار‌شان متمایل می‌گردد، و به هنگام غروب ایشان را وا گذاشته به سمت چپ متمایل میگردد، و آن‌ها در محل وسیعی از آن (غار) قرار داشتند، این از آیات الله است، هر کس را هدایت کند، پس او هدایت یافته (واقعی) است، و هرکه را گمراه کند، سرپرست و راهنمائی هرگز برای او نخواهی یافت.');
INSERT INTO "hussein_taji" VALUES(18,18,'18:18','و (اگر به آن‌ها نگاه می‌کردی) می‌پنداشتی بیدارند، در حالی‌که آن‌ها خفته بودند، و ما آن‌ها را به سمت راست و چپ می‌گرداندیم، و سگ آن‌ها دست‌های خود را (بحالت پاسبانی) بر دهانۀ غار گشوده بود. اگر به آن‌ها نگاه می‌کردی قطعاً فرار می‌کردی، و تو از آن‌ها سخت می‌ترسیدی (و وجودت از وحشت پر می‌شد).');
INSERT INTO "hussein_taji" VALUES(18,19,'18:19','و این گونه ما آن‌ها را (از خواب) بر انگیختیم تا از یکدیگر سؤال کنند، یکی از آن‌ها گفت: «چه مدت خوابیدید؟» گفتند: «یک روز یا بخشی از یک روز» (وقتی که نتوانستند مدت خواب‌شان را بدانند) گفتند: «پروردگارتان داناتر است که چقدر مانده‌اید، پس (اکنون) یک نفر از خودتان را با این سکه‌ای که دارید به شهر بفرستید، و باید بنگرد کدام یک از غذا‌ها پاکیزه‌تر است، پس روزی و طعامی از آن برای شما بیاورد، و باید دقت کند، و هیچ کس را از حال شما آگاه نسازد.');
INSERT INTO "hussein_taji" VALUES(18,20,'18:20','بدرستی که اگر آن‌ها (از وضع شما آگاه شوند و)، بر شما دست یابند، سنگسار‌تان می‌کنند، یا شما را به آئین خویش باز می‌گردانند، و در آن صورت هرگز رستگار نخواهید شد».');
INSERT INTO "hussein_taji" VALUES(18,21,'18:21','و این چنین مردم را متوجه حال آن‌ها کردیم، تا بدانند که وعدۀ الله حق است، و آنکه در (قیام) قیامت شکی نیست، هنگامی‌که (مردم) میان خود در این باره نزاع می‌کردند، پس گروهی گفتند: «بنائی بر آن بسازید»، پروردگارشان به حال آن‌ها آگاه‌تر است. برخی دیگر که قدرت بدست (و اکثریت) داشتند گفتند: «بر (غار) ایشان پرستشگاهی می‌سازیم».');
INSERT INTO "hussein_taji" VALUES(18,22,'18:22','(گروهی) خواهند گفت: «آن‌ها سه نفر بودند، که چهارمین آن‌ها سگ‌شان بود،» و (گروهی) می‌گویند: «پنج نفر بودند، که ششمین آن‌ها سگ‌شان بود» - همۀ این‌ها سخنانی بدون دلیل است - و (گروهی) می‌گویند: «آن‌ها هفت نفر بودند، و هشتمین آن‌ها سگ‌شان بود»، بگو: «پروردگار من از تعداد آن‌ها آگاه‌تر است». جز گروه کمی تعداد آن‌ها را نمی‌دانند. پس دربارۀ آنان گفتگوی مختصر و ساده (و مجادله روشن و آرام) داشته باش، و از هیچ کس دربارۀ آن‌ها سؤال مکن.');
INSERT INTO "hussein_taji" VALUES(18,23,'18:23','و هرگز دربارۀ هیچ چیز نگو: «من فردا آن را انجام می‌دهم».');
INSERT INTO "hussein_taji" VALUES(18,24,'18:24','مگر اینکه الله بخواهد، و هرگاه فراموش کردی (و إن شاء الله نگفتی) پروردگارت را به خاطر بیاور، و بگو: «امیدوارم که پروردگارم مرا به راهی روشن‌تر از این هدایت کند».');
INSERT INTO "hussein_taji" VALUES(18,25,'18:25','و آن‌ها (= اصحاب کهف) مدت سیصد سال در غار‌شان درنگ کردند، و نُه سال (نیز) بر آن افزودند.');
INSERT INTO "hussein_taji" VALUES(18,26,'18:26','(ای پیامبر!) بگو: «الله از مدت ماندن‌شان (در غار) آگاه‌تر است، غیب آسمان‌ها و زمین از آنِ اوست، چقدر بیناست و چقدر شنواست! آن‌ها هیچ ولی (و سرپرستی) جز او ندارند، و او تعالی هیچ کس را در حکم خود شریک نمی‌کند».');
INSERT INTO "hussein_taji" VALUES(18,27,'18:27','و آنچه از کتاب پروردگارت به سوی تو وحی شده؛ تلاوت کن، هیچ کسی نمی‌تواند سخنان پروردگارت را دگرگون سازد، و هرگز پناهگاهی جز او نمی‌یابی.');
INSERT INTO "hussein_taji" VALUES(18,28,'18:28','و با کسانی باش که پروردگار خود را صبحگاهان و شامگاهان می‌خوانند، (و تنها) وجه او را می‌طلبند، و هرگز بخاطر زیورهای دنیا، چشمانت را از آن‌ها بر مگیر، و پیروی نکن از کسانی‌که دل‌های شان را از یاد خود غافل ساخته‌ایم، و هوای نفس خویش را پیروی کردند، و کارهایشان از حد گذشته (و برباد) است.');
INSERT INTO "hussein_taji" VALUES(18,29,'18:29','(ای پیامبر!) بگو: «(این قرآن) حق است از سوی پروردگارتان، پس هر کس می‌خواهد ایمان بیاورد، و هرکس می‌خواهد کافر گردد». ما برای ستمکاران آتشی آماده کرده‌ایم که سرا پرده‌اش آنان را از هر طرف احاطه کرده است. و اگر تقاضای آب کنند، آبی، همچون فلز گداخته که صورت‌ها را بریان می‌کند، برای آنان می‌آورند، چه بد نوشیدنی، و چه بد آرامگاهی است.');
INSERT INTO "hussein_taji" VALUES(18,30,'18:30','بدرستی کسانی‌که ایمان آوردند و کارهای شایسته انجام دادند، همانا ما پاداش نیکو کاران را ضایع نخواهیم کرد.');
INSERT INTO "hussein_taji" VALUES(18,31,'18:31','آن‌ها کسانی هستند که بهشت‌های جاویدان از آنِ ایشان است، (بهشت‌های که) نهرها از زیر (کاخ‌ها و درختان) آن جاری است، در آنجا با دست‌بندهایی از طلا آراسته می‌شوند، و لباس‌هایی (فاخر) سبز رنگ از حریر نازک و ضخیم در بر می‌کنند، در حالی‌که بر تخت‌ها تکیه زده‌اند، چه پاداش خوبی و چه منزل نیکو و خوبی است.');
INSERT INTO "hussein_taji" VALUES(18,32,'18:32','و (ای پیامبر!) برای آن‌ها مثالی بزن، (مثال) آن دو مرد که برای یکی از آن‌ها دو باغ از انواع انگورها قرار دادیم، و گردا‌گرد آن دو (باغ) را با درختان نخل پوشاندیم، و در میان آن زراعت (پر برکتی) قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(18,33,'18:33','هر دو باغ، میوه آورده بود (میوه‌های بسیار) و چیزی فرو‌‌ گذار نکرده بود، و میان آن دو (باغ)، نهر بزرگی جاری ساخته بودیم.');
INSERT INTO "hussein_taji" VALUES(18,34,'18:34','(صاحب این دو باغ) میوه‌های بسیار (و درآمد فراوانی) داشت؛ پس به همین خاطر با دوستش در حالی‌که با او گفتگو می‌کرد گفت: «من از لحاظ ثروت از تو بیشترم و از لحاظ نفرات نیرومند‌ترم».');
INSERT INTO "hussein_taji" VALUES(18,35,'18:35','و در حالی‌که به خود ستمکار بود، در باغ خویش داخل شد، و گفت: «من باور نمی‌کنم هرگز این (باغ) نابود شود.');
INSERT INTO "hussein_taji" VALUES(18,36,'18:36','و باور نمی‌کنم که قیامت بر پا گردد، و اگر به سوی پروردگارم باز گردانده شوم (به فرض این‌که قیامتی باشد) قطعاً جایگاهی بهتر از این خواهم یافت».');
INSERT INTO "hussein_taji" VALUES(18,37,'18:37','دوست وی - در حالی‌که با او گفتگو می‌کرد - به او گفت: «آیا به آن کسی‌که تو را از خاک، سپس از نطفه آفرید، آنگاه تو را مردی (کامل) قرار داد، کافر شدی؟!');
INSERT INTO "hussein_taji" VALUES(18,38,'18:38','لیکن من (می‌گویم) الله پروردگار من است، و هیچ کس را شریک پروردگارم قرار نمی‌دهم.');
INSERT INTO "hussein_taji" VALUES(18,39,'18:39','چرا هنگامی‌که به باغ خویش وارد شدی، نگفتی: ما شاء الله، نیرو و قوتی جز از ناحیه الله نیست، اگر مرا از نظر مال و فرزند کمتر از خود ببینی.');
INSERT INTO "hussein_taji" VALUES(18,40,'18:40','پس امید است که پروردگارم بهتر از باغ تو به من بدهد، و بر باغ تو آفتی از آسمان فرو فرستد؛تا به زمینی بی گیاه لغزنده‌ای تبدیل گردد.');
INSERT INTO "hussein_taji" VALUES(18,41,'18:41','یا آب آن در عمق زمین فرو رفته تا هرگز نتوانی آن را به دست آوری».');
INSERT INTO "hussein_taji" VALUES(18,42,'18:42','(سر انجام عذاب الهی فرا رسید) و تمام میوه‌هایش را در بر گرفت و نابود شد. و او بخاطر هزینه‌هایی که صرف آن کرده بود؛ پیوسته دست‌هایش را به هم می‌مالید - در حالی‌که داربست‌های آن فرو ریخته بود- و می‌گفت: «ای کاش کسی را شریک پروردگارم قرار نداده بودم!»');
INSERT INTO "hussein_taji" VALUES(18,43,'18:43','و او را گروهی نبود که در برابر الله یاریش کنند، و نتوانست که خود را یاری کند.');
INSERT INTO "hussein_taji" VALUES(18,44,'18:44','در آنجا (ثابت گردید) که یاری و کمک از آن الله برحق است، اوست که بهترین پاداش و بهترین عاقبت را (برای مطیعان) دارد.');
INSERT INTO "hussein_taji" VALUES(18,45,'18:45','(ای پیامبر!) برای آنان مثل زندگی دنیا را بزن که مانند آبی است که آن را از آسمان فرو فرستادیم، سپس گیاهان زمین با آن درهم آمیخت سپس خشک گردید، و باد‌ها آن را به هر سو پراکنده‌اش کردند. و الله بر همه چیز تواناست.');
INSERT INTO "hussein_taji" VALUES(18,46,'18:46','مال و فرزندان زینت زندگی دنیاست، و کارهای ماندگارِ شایسته، نزد پروردگارت بهترین ثواب دارد، و بهترین امید است.');
INSERT INTO "hussein_taji" VALUES(18,47,'18:47','و (بیاد بیاور) روزی را که کوه‌ها را به حرکت در آوریم، و زمین را آشکار (و صاف) می‌بینی، همگان را گِرد می‌آوریم، و احدی از آنان را فرو نمی‌گذاریم.');
INSERT INTO "hussein_taji" VALUES(18,48,'18:48','و ایشان به صف بر پروردگارت عرضه می‌شوند (و به آن‌ها می‌فرماید:) «همگی نزد ما آمدید، همان گونه که نخستین بار شمار را آفریدیم، بلکه شما گمان می‌کردید، ما هرگز موعدی برای شما قرار نخواهیم داد».');
INSERT INTO "hussein_taji" VALUES(18,49,'18:49','و کتاب (نامۀ اعمال) در آنجا گذارده می‌شود، پس گنهکاران را می‌بینی که از آنچه در آن است ترسانند، و می‌گویند: «ای وای بر ما، این چه کتابی است که هیچ عمل کوچک و بزرگی را فرو نگذاشته؛ مگر اینکه آن را به شمار آورده است؟!» و آنچه را انجام داده‌اند؛ حاضر یابند و پروردگارت به هیچ کس ستم نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(18,50,'18:50','و (ای پیامبر! برایشان بیان کن) زمانی را که به فرشتگان گفتیم: «برای آدم سجده کنید» پس (همه) سجده کردند، بجز ابلیس - که از جن بود - و از فرمان پروردگارش سرپیچید، آیا او و فرزندانش به جای من دوستان خود می‌گیرید، در حالی‌که آن‌ها دشمن شما هستند؟! ستمکاران چه جایگزین بدی دارند.');
INSERT INTO "hussein_taji" VALUES(18,51,'18:51','من آنان (= ابلیس و فرزندانش) را به هنگام آفرینش آسمان و زمین، و نه به هنگام آفرینش خودشان حاضر نکردم. و من هیچ وقت گمراه کنندگان را دست‌یار و مددکار خود قرار نمی‌دهم (و اصلاً نیازی ندارم).');
INSERT INTO "hussein_taji" VALUES(18,52,'18:52','و (بیاد بیاورید) روزی را که الله می‌فرماید: شریک‌هایی را که برای من می‌پنداشتید، بخوانید (تا شما را از عذاب رها سازند). پس آن‌ها را بخوانند، ولی اجابتشان نکنند، و ما در میان این دو گروه هلاکت گاهی قرار داده‌ایم.');
INSERT INTO "hussein_taji" VALUES(18,53,'18:53','و گناهکاران آتش (دوزخ) را می‌بینند، پس یقین می‌کنند که در آن خواهند افتاد، و از آن هیچ راه گریزی نیابند.');
INSERT INTO "hussein_taji" VALUES(18,54,'18:54','و به راستی در این قرآن، هر گونه مثلی برای مردم بیان کرده‌ایم، ولی انسان بیش از هر چیز به مجادله می‌پردازد.');
INSERT INTO "hussein_taji" VALUES(18,55,'18:55','و چیزی مردم را باز نداشت از اینکه - وقتی هدایت به سوی‌شان آمد - ایمان بیاورند، و از پروردگارشان طلب آمرزش کنند؛ جز اینکه (می‌خواستند) سر نوشت پیشینیان برای آنان بیاید، یا عذاب در برابرشان قرار گیرد.');
INSERT INTO "hussein_taji" VALUES(18,56,'18:56','و ما پیامبران را جز بشارت‌دهنده و هشدار دهنده نمی‌فرستیم، و کسانی‌که کافر شدند، همواره به باطل مجادله می‌کنند تا بوسیلۀ آن، حق را از میان بردارند، و آیات و نشانه‌های من و آنچه به آن بیم داده شده‌اند به باد مسخره گرفتند.');
INSERT INTO "hussein_taji" VALUES(18,57,'18:57','و چه کسی ستم کار‌تر از آن کسی است که به آیات پروردگارش پند داده شود، سپس از آن روی گرداند، و آنچه را با دست‌های خود پیش فرستاده فراموش کرد؟! ما بر دل‌های آنان پرده‌هایی افکنده‌ایم تا نفهمند، و در گوش‌هایشان سنگینی (قرار داده‌ایم). و اگر آن‌ها را به‌سوی هدایت بخوانی، پس هرگز هدایت نمی‌شوند.');
INSERT INTO "hussein_taji" VALUES(18,58,'18:58','و پروردگارت آمرزنده، و صاحب رحمت است، اگر به خاطر آنچه مرتکب شده‌اند، آن‌ها را مؤاخذه می‌کرد، قطعاً هر چه زود‌‌تر عذاب را برای آن‌ها می‌فرستاد، ولی برای آن‌ها موعدی است که هرگز از آن راه فراری نخواهند داشت.');
INSERT INTO "hussein_taji" VALUES(18,59,'18:59','و این شهر‌ها و آبادی‌ها هنگامی‌که ستم کردند؛ هلاکشان نمودیم، و برای هلاکتشان موعدی قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(18,60,'18:60','و (بیاد بیاور) هنگامی را که موسی به جوان (خدمت‌کار) خود (یوشع بن نون) گفت: «من دست از جستجو بر نمی‌دارم تا به محل بر‌خورد دو دریا برسم هر چند مدت طولانی به راه خود ادامه دهم».');
INSERT INTO "hussein_taji" VALUES(18,61,'18:61','پس چون به محل برخورد دو دریا رسیدند، ماهی خود را (که برای تغذیه همراه داشتند) فراموش کردند و ماهی راه خود را در دریا پیش گرفت (و رفت).');
INSERT INTO "hussein_taji" VALUES(18,62,'18:62','پس هنگامی‌که (از آنجا) گذشتند، (موسی) به جوان (خدمت‌کار) خود (یوشع) گفت: «غذای‌مان را بیاور، که سخت از این سفرمان خسته شده‌ایم».');
INSERT INTO "hussein_taji" VALUES(18,63,'18:63','گفت: «به یاد داری، هنگامی‌که (برای استراحت) به کنار آن تخته سنگ پناه بردیم، من (در آنجا) ماهی را فراموش کردم؛ و جز شیطان (کسی) آن را از یاد من نبرد تا به یادش باشم، و ماهی بطرز شگفت آوری راه خود را در دریا پیش گرفت».');
INSERT INTO "hussein_taji" VALUES(18,64,'18:64','(موسی) گفت: «آن همان چیزی بود که ما می‌خواستیم» پس جستجو کنان از همان راه باز گشتند.');
INSERT INTO "hussein_taji" VALUES(18,65,'18:65','پس (در آنجا) بنده‌ای از بندگانم (= خضر) را یافتند که از جانب خود به او رحمتی عطا کرده و علم فراوانی از نزد خود به او آموخته بودیم.');
INSERT INTO "hussein_taji" VALUES(18,66,'18:66','موسی به او گفت: «آیا از تو پیروی کنم تا از آنچه به تو آموخته شده و مایۀ رشد و صلاح است، به من بیاموزی؟».');
INSERT INTO "hussein_taji" VALUES(18,67,'18:67','(خضر) گفت: «تو هرگز نمی‌توانی با من شکیبایی کنی!');
INSERT INTO "hussein_taji" VALUES(18,68,'18:68','و چگونه می‌توانی بر چیزی که به (راز) آن آگاه نیستی، شکیبا باشی؟!»');
INSERT INTO "hussein_taji" VALUES(18,69,'18:69','(موسی) گفت: «به خواست الله مرا شکیبا خواهی یافت، و در هیچ کاری تو را نافرمانی نکنم».');
INSERT INTO "hussein_taji" VALUES(18,70,'18:70','(خضر) گفت: «پس اگر همراهی مرا نمودی، از هیچ چیز مپرس تا آنکه خودم دربارۀ آن برای تو سخن بگویم».');
INSERT INTO "hussein_taji" VALUES(18,71,'18:71','پس آن دو به راه افتادند، تا آنکه سوار کشتی شدند (خضر) کشتی را سوراخ کرد. (موسی) گفت: «آیا آن را سوراخ کردی تا سرنشینان آن را غرق کنی؟ راستی چه‌کار بدی انجام دادی!»');
INSERT INTO "hussein_taji" VALUES(18,72,'18:72','(خضر) گفت: «آیا نگفتم تو هرگز نمی‌توانی با من شکیبایی کنی؟!»');
INSERT INTO "hussein_taji" VALUES(18,73,'18:73','(موسی) گفت: «بخاطر آنچه فراموش کردم، مرا مؤاخذه مکن و از این کارم بر من سخت مگیر».');
INSERT INTO "hussein_taji" VALUES(18,74,'18:74','پس به راه خود ادامه دادند؛ تا هنگامی‌که به نوجوانی بر خوردند، پس (خضر) او را کشت، (موسی) گفت: «آیا انسان پاکی را، بدون آنکه کسی را کشته باشد؛ کشتی؟ به‌راستی کار زشتی انجام دادی!»');
INSERT INTO "hussein_taji" VALUES(18,75,'18:75','گفت: «آیا به تو نگفتم که هرگز نمی‌توانی با من صبر کنی؟!»');
INSERT INTO "hussein_taji" VALUES(18,76,'18:76','(موسی) گفت: «بعد از این اگر از تو دربارۀ چیزی سؤال کردم، دیگر با من همراهی نکن، به‌راستی از جانب من معذور خواهی بود».');
INSERT INTO "hussein_taji" VALUES(18,77,'18:77','پس به راه خود ادامه دادند، تا به اهل قریه‌ای رسیدند، از آنان خواستند که به ایشان غذا دهند، ولی آنان از مهمان نمودن آنان خود داری کردند. آنگاه در آنجا دیواری یافتند که می‌خواست فرو ریزد، پس (خضر)آن را راست و استوار کرد، (موسی) گفت: «اگر می‌خواستی (می‌توانستی) در مقابل این کار؛ مزدی بگیری»');
INSERT INTO "hussein_taji" VALUES(18,78,'18:78','(خضر) گفت: «اینک زمان جدایی من و تو فرا رسیده، به‌زودی تو را از راز (و حکمت) آنچه که نتوانستی در برابر آن صبر کنی؛ آگاه خواهم ساخت.');
INSERT INTO "hussein_taji" VALUES(18,79,'18:79','اما کشتی، از آن بی‌نوایانی بود که در دریا کار می‌کردند، پس من خواستم آن را معیوب کنم، (چرا که) پشت سر آن‌ها پادشاهی (ستمگر) بود که هر کشتی (سالمی) را به‌زور می‌گرفت.');
INSERT INTO "hussein_taji" VALUES(18,80,'18:80','و اما نوجوان، پدر و مادرش (هردو) با ایمان بودند، پس بیم داشتیم که آنان را به سرکشی و کفر وا‌دارد.');
INSERT INTO "hussein_taji" VALUES(18,81,'18:81','از این رو، خواستیم که پروردگارشان فرزند پاک‌تر و پر محبت تری بجای او به آن دو عطا فرماید.');
INSERT INTO "hussein_taji" VALUES(18,82,'18:82','و اما دیوار، از آن دو نوجوان یتیم در آن شهر بود، و زیر آن، گنجی متعلق به آن دو وجود داشت، و پدرشان مردی صالح بود، پس پروردگارت خواست که آنان به حد بلوغ برسند و گنج خود را بیرون آورند، این رحمتی از پروردگارت بود، این (کار‌ها) را من خود سرانه انجام ندادم، این بود راز (و حکمت) کارهای که نتوانستی در برابر آن‌ها صبر کنی».');
INSERT INTO "hussein_taji" VALUES(18,83,'18:83','و (ای پیامبر!) از تو دربارۀ «ذوالقرنین» می‌پرسند؛ بگو: «به زودی چیزی از سر گذشت او برای شما خواهم خواند».');
INSERT INTO "hussein_taji" VALUES(18,84,'18:84','یقیناً ما به او در زمین قدرت و حکومت دادیم، و اسباب هر چیز را در اختیارش گذاشتیم.');
INSERT INTO "hussein_taji" VALUES(18,85,'18:85','پس او (از این) اسباب (پیروی و) استفاده کرد.');
INSERT INTO "hussein_taji" VALUES(18,86,'18:86','تا آن که به غروب گاه آفتاب رسید، (چنین به نظرش رسید) که خورشید در چشمه‌ای گِل آلود و سیاه غروب می‌کند، و در آنجا مردمی را یافت، گفتیم: «ای ذوالقرنین! یا (آنان را) مجازات می‌کنی، و یا روش خوبی دربارۀ آن‌ها انتخاب می‌نمایی».');
INSERT INTO "hussein_taji" VALUES(18,87,'18:87','(ذو القرنین) گفت: «اما کسی‌که ستم کرده است، مجازات خواهیم کرد، سپس به سوی پروردگارش باز گردانده می‌شود، آنگاه او را مجازات سختی خواهد کرد.');
INSERT INTO "hussein_taji" VALUES(18,88,'18:88','و اما کسی‌که ایمان آورد و کار شایسته انجام داد، پس (در آخرت) پاداشی نیکو دارد، و ما دستور، آسانی به او خواهیم داد».');
INSERT INTO "hussein_taji" VALUES(18,89,'18:89','سپس از اسبابی (که در اختیارش بود) استفاده کرد.');
INSERT INTO "hussein_taji" VALUES(18,90,'18:90','تا آنکه به جایگاه برآمدن آفتاب رسید، (در آنجا) دید که آفتاب بر مردمانی طلوع می‌کند که در برابر (تابش) آفتاب برایشان پوششی قرار نداده بودیم.');
INSERT INTO "hussein_taji" VALUES(18,91,'18:91','اینچنین بود (کار ذی القرنین) و به راستی ما از آنچه در اختیار داشت (و انجام می‌داد) کاملاً آگاه بودیم.');
INSERT INTO "hussein_taji" VALUES(18,92,'18:92','سپس از وسایل و اسباب استفاده کرد.');
INSERT INTO "hussein_taji" VALUES(18,93,'18:93','(و به سفر ادامه داد) تا به میان دو کوه رسید، و در کنار آن دو (کوه) مردمی را یافت که هیچ سخنی را نمی‌فهمیدند.');
INSERT INTO "hussein_taji" VALUES(18,94,'18:94','(آنان به او) گفتند: «ای ذوالقرنین! یأجوج و مأجوج در این سرزمین فساد می‌کنند، پس آیا هزینه‌ای برای تو قرار دهیم، که میان ما و آن‌ها سدی بسازی؟!»');
INSERT INTO "hussein_taji" VALUES(18,95,'18:95','(ذوالقرنین) گفت: «آنچه پروردگارم در اختیار من قرار داده (از قدرت و ثروت) بهتر است (از آنچه شما پیشنهاد می‌کنید) پس مرا با نیرو یاری کنید، تا میان شما و آن‌ها سدّ محکمی بسازم.');
INSERT INTO "hussein_taji" VALUES(18,96,'18:96','قطعات بزرگ آهن برایم بیاورید، (و روی هم بچینید)» تا وقتی‌که کاملاً میان دو کوه را برابر کرد (و پوشانید)، گفت: «(در اطراف آن آتش بیفروزید) و در آن بدمید» (آن‌ها دمیدند) تا وقتی قطعات آهن را سرخ و گداخته کرد. گفت: «(اکنون) مس ذوب شده برایم بیاورید؛ تا بر روی آن بریزم».');
INSERT INTO "hussein_taji" VALUES(18,97,'18:97','(سر انجام سد محکمی ساخت) پس آن‌ها (= یأجوج و مأجوج) اصلاً نتوانستند از آن بالا روند، و نتوانستند سوراخ و نقبی درآن ایجاد کنند.');
INSERT INTO "hussein_taji" VALUES(18,98,'18:98','(آنگاه) گفت: «این از رحمت پروردگار من است (و تا الله بخواهد پا برجا می‌ماند) اما هنگامی‌که وعدۀ پروردگارم فرا رسد، آن را درهم می‌کوبد، و وعدۀ پروردگارم حق است».');
INSERT INTO "hussein_taji" VALUES(18,99,'18:99','و در آن روز (که جهان پایان می‌پذیرد) ما آن‌ها را (چنان) رها می‌کنیم که در هم موج می‌زنند، و در شیپور دمیده می‌شود، سپس ما همه را (در یکجا) جمع می‌کنیم.');
INSERT INTO "hussein_taji" VALUES(18,100,'18:100','و درآن روز، جهنم را بر کافران عرضه می‌داریم.');
INSERT INTO "hussein_taji" VALUES(18,101,'18:101','(آن) کسانی‌که چشمان‌شان از یاد من در پردۀ (غفلت) بود، و توان شنیدن (حق را) نداشتند.');
INSERT INTO "hussein_taji" VALUES(18,102,'18:102','آیا کسانی‌که کافر شدند؛ گمان می‌برند که بندگان مرا بجای من اولیای خود بگیرند؟! بی‌گمان ما جهنم را برای پذیرائی کافران آماده کرده‌ایم.');
INSERT INTO "hussein_taji" VALUES(18,103,'18:103','(ای پیامبر!) بگو: «آیا شما را به زیان کارترین (مردم) در کارها، خبر دهیم.');
INSERT INTO "hussein_taji" VALUES(18,104,'18:104','کسانی‌که تلاش و کوشش شان در زندگی دنیا تباه و ضایع شده، و با این حال گمان می‌کنند که کار نیک انجام می‌دهند».');
INSERT INTO "hussein_taji" VALUES(18,105,'18:105','آن‌ها کسانی هستند که به آیات پروردگارشان و لقای او (در جهان آخرت) کافر شدند، در نتیجه اعمال‌شان تباه و نابود شد، سپس در روز قیامت برای آن‌ها (ارزشی قائل نیستیم) و زنی بر پا نخواهیم کرد.');
INSERT INTO "hussein_taji" VALUES(18,106,'18:106','(آری) این (گونه) است سزای‌شان دوزخ است، بخاطر آنکه کافر شدند، و آیات من و پیامبرانم را مسخره گرفتند.');
INSERT INTO "hussein_taji" VALUES(18,107,'18:107','بی‌گمان کسانی‌که ایمان آورده‌اند و کار‌های شایسته انجام دادند، باغ‌های (بهشت) برین محل پذیرائی آنان خواهد بود.');
INSERT INTO "hussein_taji" VALUES(18,108,'18:108','جاودانه در آن خواهند ماند و هرگز تقاضای نقل مکان از آنجا نمی‌کنند.');
INSERT INTO "hussein_taji" VALUES(18,109,'18:109','(ای پیامبر!) بگو: «اگر دریا برای (نگارش) کلمات پروردگارم جوهر شود؛ قطعاً دریا به پایان می‌رسد، پیش از آنکه کلمات پروردگارم پایان یابد، هر چند همانند آن (دریا) به کمک آن بیاوریم».');
INSERT INTO "hussein_taji" VALUES(18,110,'18:110','(ای پیامبر!) بگو: «من فقط بشری هستم مثل شما، (امتیاز من این است که) به من وحی می‌شود، که تنها معبودتان معبود یگانه است، پس هر که به لقای پروردگارش امید دارد، باید کار شایسته انجام دهد، و هیچ کس را در عبادت پروردگارش شریک نسازد».');
INSERT INTO "hussein_taji" VALUES(19,1,'19:1','کهیعص (کاف. ها. یا. عین. صاد).');
INSERT INTO "hussein_taji" VALUES(19,2,'19:2','(این) بیان رحمت پروردگارت (نسبت) به بنده‌اش زکریا است.');
INSERT INTO "hussein_taji" VALUES(19,3,'19:3','چون پروردگارش را به ندایی پنهان (به دعا) خواند.');
INSERT INTO "hussein_taji" VALUES(19,4,'19:4','گفت: «پروردگارا! به‌راستی که استخوانم سست شده، و شعلۀ پیری سرم را فرا گرفته (و موهایم سفید گشته است) و من (هرگز) در دعای تو، ای پروردگارم! محروم نبوده‌ام.');
INSERT INTO "hussein_taji" VALUES(19,5,'19:5','و به‌راستی که من بعد از (مرگ) خودم از وارثانم بیمناکم، و همسرم نازا (و عقیم) است، پس از نزد خویش وارثی به من عطا فرما.');
INSERT INTO "hussein_taji" VALUES(19,6,'19:6','که وارث من و (نیز) وارث آل یعقوب باشد، و پروردگارا! او را پسندیده قرار ده».');
INSERT INTO "hussein_taji" VALUES(19,7,'19:7','(الله فرمود:) «ای زکریا! همانا ما تو را به پسری بشارت می‌دهیم که نامش یحیی است، (که) پیش از این همنامی برای او قرار نداده‌ایم».');
INSERT INTO "hussein_taji" VALUES(19,8,'19:8','(زکریا) گفت: «پروردگارا! چگونه برای من پسری خواهد بود؟! درحالی‌که همسرم نازا است، و من (نیز) از شدت پیری به ناتوانی رسیده‌ام!»');
INSERT INTO "hussein_taji" VALUES(19,9,'19:9','(فرشته) گفت: «این گونه است، پروردگارت فرموده است: این بر من آسان است، و به‌راستی پیش از این تو را آفریدم در حالی‌که چیزی نبودی».');
INSERT INTO "hussein_taji" VALUES(19,10,'19:10','(زکریا) گفت: «پروردگارا! برای من نشانه‌ای قرار ده» فرمود: «نشانۀ تو این است که سه شبانه (روز) در حالی‌که (تندرست و) سالمی؛ نتوانی با مردم سخن بگویی».');
INSERT INTO "hussein_taji" VALUES(19,11,'19:11','پس (او) از محراب (= جایگاه عبادتش) بر قومش بیرون آمد، آنگاه به آن‌ها اشاره کرد که: صبح و شام (الله را) تسبیح گویید».');
INSERT INTO "hussein_taji" VALUES(19,12,'19:12','(الله فرمود:) ای یحیی! کتاب (تورات) را با قوت (و جدیت) بگیر، و در کودکی به او دانش (و نبوت) دادیم.');
INSERT INTO "hussein_taji" VALUES(19,13,'19:13','و (نیز به او) شفقت و پاکیزگی از جانب خود (عطا کردیم). و (او) پرهیزگار بود.');
INSERT INTO "hussein_taji" VALUES(19,14,'19:14','و (او) نسبت به پدر و مادرش نیکو کار بود، و سرکش (و) نافرمان نبود.');
INSERT INTO "hussein_taji" VALUES(19,15,'19:15','و سلام بر او باد، روزی‌که متولد شد، و روزی‌که می‌میرد، و روزی‌که زنده برانگیخته می‌شود.');
INSERT INTO "hussein_taji" VALUES(19,16,'19:16','و در (این) کتاب (قرآن) مریم را یاد کن، هنگامی‌که از خانواده‌اش کناره گرفت، (و در) ناحیۀ شرقی (بیت المقدس) مستقر شد.');
INSERT INTO "hussein_taji" VALUES(19,17,'19:17','پس میان خود و آن‌ها پرده‌ای کشید، آنگاه (ما) روح خود (= جبر ئیل) را به سوی او فرستادیم، پس به هیئت انسانی کامل بر او ظاهر شد.');
INSERT INTO "hussein_taji" VALUES(19,18,'19:18','(مریم) گفت: «همانا من از (شر) تو، به (الله) رحمان پناه می‌برم، اگر تو پرهیزگار هستی».');
INSERT INTO "hussein_taji" VALUES(19,19,'19:19','(فرشته) گفت: «یقیناً من فرستادۀ پروردگار تو هستم، تا پسر پاکیزه‌ای به تو ببخشم».');
INSERT INTO "hussein_taji" VALUES(19,20,'19:20','(مریم) گفت: «چگونه ممکن است مرا پسری باشد در حالی‌که تاکنون انسانی با من تماس نداشته است، و هرگز (زن) بدکاره‌ای (هم) نبوده‌ام؟!».');
INSERT INTO "hussein_taji" VALUES(19,21,'19:21','(فرشته) گفت: «این گونه است، پروردگارت فرموده است: این (کار) بر من آسان است، و او را برای مردم نشانه‌ای قرار می‌دهیم، و (نیز) رحمتی از سوی ماست، و این امری است انجام یافته».');
INSERT INTO "hussein_taji" VALUES(19,22,'19:22','پس (مریم) به او باردار شد، آنگاه با او به جایی دور دستی (رفت و از مردم) کناره گرفت.');
INSERT INTO "hussein_taji" VALUES(19,23,'19:23','پس درد زایمان او را به سوی تنۀ (درخت) خرمایی کشاند، گفت: «ای کاش پیش از این مرده بودم، و به‌کلی از یادها رفته (و فراموش شده) بودم».');
INSERT INTO "hussein_taji" VALUES(19,24,'19:24','ناگهان از (سمت) پایینش (فرشته یا عیسی) او را صدا زد که: «غمگین مباش، به راستی پروردگارت زیر (پای) تو چشمه‌ای (جاری) قرار داده است.');
INSERT INTO "hussein_taji" VALUES(19,25,'19:25','و (این) تنۀ (درخت) خرما را به طرف خود تکان بده، (که) رطب تازه‌ای بر تو فرو می‌ریزد.');
INSERT INTO "hussein_taji" VALUES(19,26,'19:26','پس (از آن) بخور و (از آب چشمه) بنوش، و دیدگان را (به این مولود) روشن دار، پس اگر کسی از انسان‌ها را دیدی (با اشاره) بگو: «همانا من برای (الله) رحمان روزه (سکوت) نذر کرده‌ام، لذا امروز با هیچ بشری سخن نمی‌گویم».');
INSERT INTO "hussein_taji" VALUES(19,27,'19:27','پس (مریم) در حالی‌که او (= عیسی) را برداشته بود، نزد قومش آمد، گفتند: «ای مریم! به راستی کاری (بسیار) زشت (و شگفت انگیزی) انجام داده‌ای!');
INSERT INTO "hussein_taji" VALUES(19,28,'19:28','ای خواهر هارون! پدرت مرد بدی نبود، و مادرت (نیز) زن بدکاره‌ای نبود!».');
INSERT INTO "hussein_taji" VALUES(19,29,'19:29','پس (مریم) به او (=عیسی) اشاره کرد، گفتند: «چگونه با کسی‌که کودکی است در گهواره، سخن بگوییم؟!»');
INSERT INTO "hussein_taji" VALUES(19,30,'19:30','(عیسی) گفت: «من بندۀ الله هستم، به من کتاب داده، و مرا پیامبر قرار داده است.');
INSERT INTO "hussein_taji" VALUES(19,31,'19:31','و مرا هر جا که باشم پر برکت و فرخنده قرار داده، و تا زنده‌ام مرا به نماز و زکات سفارش کرده است.');
INSERT INTO "hussein_taji" VALUES(19,32,'19:32','و (مرا) نسبت به مادرم نیکو کار (قرار داده) و مرا سرکش (و) بدبخت قرار نداده است.');
INSERT INTO "hussein_taji" VALUES(19,33,'19:33','و سلام بر من، روزی‌که متولد شدم، و روزی‌که می‌میرم، و روزی‌که زنده برانگیخته شوم»');
INSERT INTO "hussein_taji" VALUES(19,34,'19:34','این است (حکایت) عیسی پسر مریم، گفتار راستی که در آن تردید (و اختلاف) می‌کنند.');
INSERT INTO "hussein_taji" VALUES(19,35,'19:35','(هرگز) شایسته الله نیست که فرزندی برگیرد، او منزه است، هرگاه کاری را فرمان دهد (و بخواهد)، فقط به آن می‌گوید: «موجود شو» پس (بی‌درنگ) موجود می‌شود.');
INSERT INTO "hussein_taji" VALUES(19,36,'19:36','و (عیسی به قومش گفت:) «یقیناً الله پروردگار من و پروردگار شماست، پس او را پرستش کنید، (که) راه راست این است».');
INSERT INTO "hussein_taji" VALUES(19,37,'19:37','آنگاه گروه‌های (یهود و نصاری) در میان خود اختلاف کردند، پس وای بر کسانی‌که کافر شدند، از حضور (در آن) روز بزرگ (قیامت).');
INSERT INTO "hussein_taji" VALUES(19,38,'19:38','(آن) روزی‌که نزد ما می‌آیند، چه خوب شنوا و چه خوب بینا هستند، لیکن ستمکاران امروز در گمراهی آشکارند.');
INSERT INTO "hussein_taji" VALUES(19,39,'19:39','و (ای پیامبر!) آن‌ها را از روز حسرت بترسان، آنگاه که کار پایان پذیرد، و آن‌ها (همچنان) در غفلت‌اند و ایمان نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(19,40,'19:40','همانا ماییم که زمین و کسانی را که بر آن هستند، به ارث می‌بریم، و (همگی) به‌سوی ما باز گردانده می‌شوند.');
INSERT INTO "hussein_taji" VALUES(19,41,'19:41','و (ای پیامبر!) در (این) کتاب ابراهیم را یاد کن، بی‌گمان او پیامبری راستگو بود.');
INSERT INTO "hussein_taji" VALUES(19,42,'19:42','هنگامی‌که به پدرش گفت: «ای پدر جان! چرا چیزی را پرستش می‌کنی که نه می‌شنود و نه می‌بیند، و نه هیچ نیازی از تو برآورده می‌سازد؟!');
INSERT INTO "hussein_taji" VALUES(19,43,'19:43','ای پدر جان! یقیناً (از جانب الله) دانشی برای من آمده که برای تو نیامده است، پس از من پیروی کن، تا تو را به راه راست هدایت کنم.');
INSERT INTO "hussein_taji" VALUES(19,44,'19:44','ای پدر جان! شیطان را پرستش نکن، زیرا که شیطان نسبت به (الله) رحمان نافرمان بود.');
INSERT INTO "hussein_taji" VALUES(19,45,'19:45','ای پدر جان! من از این می‌ترسم که از (سوی الله) رحمان عذابی به تو برسد، آنگاه از دوستان شیطان (و هم‌نشینان او در آتش) باشی».');
INSERT INTO "hussein_taji" VALUES(19,46,'19:46','(پدرش) گفت: «ای ابراهیم! آیا تو از معبود‌های من روی گردانی؟ اگر باز نیایی، قطعاً تو را سنگسار می‌کنم، و برای مدت طولانی از من دور شو».');
INSERT INTO "hussein_taji" VALUES(19,47,'19:47','(ابراهیم) گفت: «سلام بر تو! به‌زودی از پروردگارم برای تو طلب آمرزش خواهم کرد، بی‌گمان او نسبت به من مهربان است.');
INSERT INTO "hussein_taji" VALUES(19,48,'19:48','و از شما و آنچه به جای الله می‌خوانید، کناره‌گیری می‌کنم، و پروردگارم را می‌خوانم، امید است که در خواندن پروردگارم محروم نباشم».');
INSERT INTO "hussein_taji" VALUES(19,49,'19:49','پس چون از آن‌ها و آنچه به جای الله می‌پرستیدند کناره‌گیری کرد، اسحاق و یعقوب را به او بخشیدیم، و هر یک (از آنان) را پیامبر گردانیدیم.');
INSERT INTO "hussein_taji" VALUES(19,50,'19:50','و از رحمت خود به آن‌ها عطا کردیم، و برایشان (در میان امت‌ها) ذکر خیر بلند آوازه‌ای قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(19,51,'19:51','و (ای پیامبر!) در (این) کتاب موسی را یاد کن، به‌راستی او مخلص بود، و فرستاده‌ای پیامبر بود.');
INSERT INTO "hussein_taji" VALUES(19,52,'19:52','و (ما) او را از طرف راست (کوه) طور نِدا دادیم، و رازگویان او را (به خود) نزدیک ساختیم.');
INSERT INTO "hussein_taji" VALUES(19,53,'19:53','و (ما) از رحمت خود برادرش هارون را - که پیامبر بود - به او عطا کردیم.');
INSERT INTO "hussein_taji" VALUES(19,54,'19:54','و در (این) کتاب اسماعیل را یاد کن، بی‌گمان او راست وعده، و فرستاده‌ای پیامبر بود.');
INSERT INTO "hussein_taji" VALUES(19,55,'19:55','و (او همواره) خانواده‌اش را به نماز و زکات فرمان می‌داد، و نزد پروردگارش پسندیده بود.');
INSERT INTO "hussein_taji" VALUES(19,56,'19:56','و در (این) کتاب ادریس را یاد کن بی‌شک او پیامبری راستگو بود.');
INSERT INTO "hussein_taji" VALUES(19,57,'19:57','و (ما) او را به جایگاهی بلند فرا بردیم.');
INSERT INTO "hussein_taji" VALUES(19,58,'19:58','اینان کسانی از پیامبران بودند که الله بر آنان انعام کرده بود، از فرزندان آدم و از کسانی‌که با نوح (بر کشتی) سوار کردیم، و از فرزندان ابراهیم و اسرائیل (= یعقوب) و از کسانی‌که آن‌ها را هدایت کردیم و بر گزیدیم. چون آیات (الله) رحمان بر آن‌ها تلاوت می‌شد، سجده‌کنان و گریان (به خاک) می‌افتادند.');
INSERT INTO "hussein_taji" VALUES(19,59,'19:59','آنگاه پس از آنان (فرزندان) ناخلفانی جانشین شدند که نماز را (ضایع و) ترک کردند و از شهوات پیروی کردند، پس به‌زودی (کیفر) گمراهی (خود) را خواهند یافت.');
INSERT INTO "hussein_taji" VALUES(19,60,'19:60','مگر آن که توبه کرد و ایمان آورد، و کار شایسته انجام داد، پس اینان به بهشت داخل می‌شوند، و بر آنان هیچ ستمی نخواهد شد.');
INSERT INTO "hussein_taji" VALUES(19,61,'19:61','باغ‌های (بهشتی) جاودانی است که (الله) رحمان به غیب (= نادیده) بندگانش را به آن وعده داده است، مسلماً وعدۀ او فرا خواهد رسید.');
INSERT INTO "hussein_taji" VALUES(19,62,'19:62','در آنجا سخن بیهوده نمی‌شنوند، جز سلام (می‌شنوند) و برای آن‌ها (هر) صبح و شام روزی‌شان (در بهشت آماده) است.');
INSERT INTO "hussein_taji" VALUES(19,63,'19:63','این همان بهشتی است که از (میان) بندگانمان (به) آن کسی‌که (در دنیا) پرهیزگار بوده به ارث می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(19,64,'19:64','و (جبرئیل به پیامبر عرض کرد:) (ما فرشتگان) جز به فرمان پروردگارت نازل نمی‌شویم، (آگاهی از) آنچه پیش روی ما، و آنچه پشت سرما، و آنچه میان این دو است، (همه) از آنِ اوست، و پروردگارت هرگز فراموش‌کار نیست.');
INSERT INTO "hussein_taji" VALUES(19,65,'19:65','(همان) پروردگار آسمان‌ها و زمین، و آنچه میان آن دو است، پس او را پرستش کن، و بر عبادتش شکیبا (و پایدار) باش. آیا (مانند و) هم‌نامی برای او می‌شناسی؟');
INSERT INTO "hussein_taji" VALUES(19,66,'19:66','و انسان (کافر) می‌گوید: «آیا زمانی که بمیریم، زنده (از گور) بیرون آورده خواهم شد؟!»');
INSERT INTO "hussein_taji" VALUES(19,67,'19:67','آیا انسان به یاد نمی‌آورد که ما پیش از این او را آفریدیم در حالی‌که هیچ چیز نبود؟!');
INSERT INTO "hussein_taji" VALUES(19,68,'19:68','پس سوگند به پروردگارت که محققاً (همۀ) آن‌ها را با شیاطین محشور خواهیم کرد، سپس (همۀ) آن‌ها را به زانو در آمده پیرامون جهنم حاضر می‌سازیم.');
INSERT INTO "hussein_taji" VALUES(19,69,'19:69','آنگاه از هر گروه، کسانی را که در برابر (فرمان الله) رحمان (از همه) سرکش‌تر (و پرجرأت‌تر) بوده‌اند، جدا خواهیم کرد.');
INSERT INTO "hussein_taji" VALUES(19,70,'19:70','سپس ما به (حال) کسانی‌که (برای سوختن) در آتش (جهنم) سزاوارترند؛ دانا‌تریم.');
INSERT INTO "hussein_taji" VALUES(19,71,'19:71','و هیچ یک از شما نیست؛ مگر آن که وارد آن (= جهنم) شود، این (وعده) بر پروردگارت فرمانی حتمی (و شدنی) است.');
INSERT INTO "hussein_taji" VALUES(19,72,'19:72','سپس کسانی را که تقوا پیشه کردند از آن رهایی می‌بخشیم، و ستمکاران را به زانو در آمده در آن رها می‌کنیم.');
INSERT INTO "hussein_taji" VALUES(19,73,'19:73','و هنگامی‌که آیات روشن ما بر آن‌ها تلاوت شود، کسانی‌که کافر شدند به کسانی‌که ایمان آوردند؛ می‌گویند: «کدام‌یک از دو گروه (ما و شما) در جایگاه (و مقام) بهتر، و از لحاظ محفل، (محفل‌شان) نیکو‌تر است».');
INSERT INTO "hussein_taji" VALUES(19,74,'19:74','چه بسیار نسل‌ها را پیش از آنان نابود کردیم که آن‌ها از لحاظ ثروت از این‌ها بهتر بودند و ظاهرشان (نیز) آراسته‌تر بود.');
INSERT INTO "hussein_taji" VALUES(19,75,'19:75','(ای پیامبر!) بگو: «کسی‌که در گمراهی است، پس (الله) رحمان (از روی استدراج) او را به فزونی مهلت دهد، تا زمانی‌که آنچه را به آن وعده داده شده‌اند؛ یا عذاب و یا قیامت را ببینند، آنگاه به زودی خواهند دانست چه کسی جایگاهش بدتر، و سپاهش ناتوان‌تر است.');
INSERT INTO "hussein_taji" VALUES(19,76,'19:76','و کسانی‌که هدایت یافته‌اند، الله بر هدایت‌شان می‌افزاید، و (آثار و) نیکی‌های شایسته‌ای ماندگار در نزد پروردگارت پاداش بهتر، و عاقبت خوب‌تر دارد.');
INSERT INTO "hussein_taji" VALUES(19,77,'19:77','آیا کسی را دیده‌ای به آیات ما کافر شد، و گفت: «قطعاً مال و فرزند (فراوانی) به من داده خواهد شد»؟!');
INSERT INTO "hussein_taji" VALUES(19,78,'19:78','آیا بر غیب آگاهی یافته است، یا نزد (الله) رحمان عهدی گرفته است؟!');
INSERT INTO "hussein_taji" VALUES(19,79,'19:79','(هرگز) چنین نیست، (ما) به‌زودی آنچه را می‌گوید خواهیم نوشت، و بر عذاب او بسیار می‌افزاییم.');
INSERT INTO "hussein_taji" VALUES(19,80,'19:80','و آنچه را می‌گوید از او می‌گیریم، و تنها نزد ما می‌آید.');
INSERT INTO "hussein_taji" VALUES(19,81,'19:81','و (آن‌ها) به جای الله معبودانی را (برای خود) بر گزیدند، تا سبب عزت‌شان باشد.');
INSERT INTO "hussein_taji" VALUES(19,82,'19:82','چنین نیست، (بلکه) به زودی (معبودها) عبادت آنان را انکار خواهند کرد، و بر ضدشان برخیزند.');
INSERT INTO "hussein_taji" VALUES(19,83,'19:83','(ای پیامبر!) آیا ندیدی که ما شیاطین را بر کافران فرستادیم (و مسلط نبودیم) تا آن‌ها را سخت بر انگیزند.');
INSERT INTO "hussein_taji" VALUES(19,84,'19:84','پس (ای پیامبر!) دربارۀ آن‌ها شتاب نکن، ما با دقت (اجل و اعمال) آن‌ها را می‌شماریم.');
INSERT INTO "hussein_taji" VALUES(19,85,'19:85','روزی‌که پرهیزگاران را (دسته‌جمعی) - به صورت مهمان - به سوی (الله) رحمان گرد آوریم.');
INSERT INTO "hussein_taji" VALUES(19,86,'19:86','و مجرمان را تشنه‌کام به سوی جهنم برانیم.');
INSERT INTO "hussein_taji" VALUES(19,87,'19:87','آن‌ها مالک شفاعت نیستند؛ مگر کسی‌که نزد (الله) رحمان عهدی گرفته باشد.');
INSERT INTO "hussein_taji" VALUES(19,88,'19:88','و (مشرکان) گفتند: «(الله) رحمان فرزندی (برای خود) برگزیده است».');
INSERT INTO "hussein_taji" VALUES(19,89,'19:89','به راستی چیزی بسیار زشت (و زننده‌ای، در میان) آوردید.');
INSERT INTO "hussein_taji" VALUES(19,90,'19:90','نزدیک است آسمان‌ها از این (سخن) از هم متلاشی گردد، و زمین شکافته شود، و کوه‌ها درهم شکافته فرو ریزند.');
INSERT INTO "hussein_taji" VALUES(19,91,'19:91','(از این رو) که برای (الله) رحمان فرزندی ادعا کرده‌اند.');
INSERT INTO "hussein_taji" VALUES(19,92,'19:92','و هرگز برای (الله) رحمان سزاوار نیست که فرزندی برگزیند.');
INSERT INTO "hussein_taji" VALUES(19,93,'19:93','هیچ چیز در آسمان‌ها و زمین نیست؛ مگر این‌که به بندگی سوی (الله) رحمان بیاید.');
INSERT INTO "hussein_taji" VALUES(19,94,'19:94','یقیناً (الله همۀ) آن‌ها را سر‌شماری کرده، و به دقت شمرده است،');
INSERT INTO "hussein_taji" VALUES(19,95,'19:95','و همۀ آن‌ها روز قیامت، تنها نزد او حاضر می‌شوند.');
INSERT INTO "hussein_taji" VALUES(19,96,'19:96','بی‌گمان کسانی‌که ایمان آوردند و کارهای شایسته انجام دادند، (الله) رحمان برای آن‌ها محبتی (در دل‌ها) قرار می‌دهد.');
INSERT INTO "hussein_taji" VALUES(19,97,'19:97','پس همانا (ای پیامبر!) ما آن (= قرآن) به زبان تو آسان نمودیم، تا پرهیزگاران را با آن بشارت دهی، و گروه ستیزه‌گران (سرسخت) را با آن هشدار دهی.');
INSERT INTO "hussein_taji" VALUES(19,98,'19:98','و چه بسیار نسل‌ها که پیش از آن‌ها بودند هلاک کردیم، آیا کسی از آن‌ها را احساس می‌کنی، یا کمترین صدایی از آن‌ها می‌شنوی؟!');
INSERT INTO "hussein_taji" VALUES(20,1,'20:1','طه (طا. ها).');
INSERT INTO "hussein_taji" VALUES(20,2,'20:2','(ای پیامبر! ما) قرآن را بر تو نازل نکردیم که به مشقت افتی.');
INSERT INTO "hussein_taji" VALUES(20,3,'20:3','مگر (آن) یاد آوری برای کسی است که (از الله) می‌ترسد.');
INSERT INTO "hussein_taji" VALUES(20,4,'20:4','(این قرآن) فرو فرستاده شده از سوی کسی‌که زمین و آسمان‌های بلند را آفریده است.');
INSERT INTO "hussein_taji" VALUES(20,5,'20:5','(الله) رحمان (است که) بر عرش قرار گرفت.');
INSERT INTO "hussein_taji" VALUES(20,6,'20:6','از آنِ اوست آنچه در آسمان‌ها و آنچه در زمین، و آنچه میان آن دو است، و آنچه زیر خاک (پنهان) است.');
INSERT INTO "hussein_taji" VALUES(20,7,'20:7','و اگر سخن آشکارا بگویی (یا آن را پنهان داری) بی‌گمان او (راز) نهان و نهان‌تر را می‌داند.');
INSERT INTO "hussein_taji" VALUES(20,8,'20:8','(او) الله است، که هیچ معبودی جز او نیست؛ نام‌های نیکو از آنِ اوست.');
INSERT INTO "hussein_taji" VALUES(20,9,'20:9','و (ای پیامبر!) آیا خبر موسی به تو رسیده است؟');
INSERT INTO "hussein_taji" VALUES(20,10,'20:10','هنگامی‌که آتشی را (از دور) دید، پس به خانوادۀ خود گفت: «(لحظه‌ای) درنگ کنید، همانا من آتشی را دیده‌ام شاید شعله‌ای از آن برای شما بیاورم، یا بر آن آتش راهنمایی بیابم».');
INSERT INTO "hussein_taji" VALUES(20,11,'20:11','پس چون نزد آن (آتش) آمد، نِدا داده شد که: «ای موسی!');
INSERT INTO "hussein_taji" VALUES(20,12,'20:12','همانا من پروردگار تو هستم، پس کفش‌هایت را بیرون کن، که تو در وادی مقدس طوی هستی.');
INSERT INTO "hussein_taji" VALUES(20,13,'20:13','و من تو را (به پیامبری) بر گزیدم، پس به آنچه (بر تو) وحی می‌شود، گوش فرا ده.');
INSERT INTO "hussein_taji" VALUES(20,14,'20:14','یقیناً من «الله» هستم، هیچ معبودی (به حق) جز من نیست، پس مرا پرستش کن، و نماز را برای یاد من بر پا دار.');
INSERT INTO "hussein_taji" VALUES(20,15,'20:15','قطعاً قیامت خواهد آمد، می‌خواهم (وقت) آن را پنهان دارم، تا هر کس در برابر سعی (و اعمالی) که می‌کند جزا داده شود.');
INSERT INTO "hussein_taji" VALUES(20,16,'20:16','پس مبادا کسی‌که به آن ایمان ندارد، و از هوس‌های خود پیروی می‌کند، تو را از آن باز دارد، که هلاک خواهی شد.');
INSERT INTO "hussein_taji" VALUES(20,17,'20:17','و ای موسی! آن چیست به (دست) راستت؟»');
INSERT INTO "hussein_taji" VALUES(20,18,'20:18','گفت: «این عصای من است، بر آن تکیه می‌کنم، و با آن برای گوسفندانم (برگ درختان) فرو می‌ریزم، و مرا با آن کارهای دیگری (نیز) است (که برآورده می‌کنم).');
INSERT INTO "hussein_taji" VALUES(20,19,'20:19','فرمود: «ای موسی! آن را بیفکن»');
INSERT INTO "hussein_taji" VALUES(20,20,'20:20','پس (موسی) آن (عصا) را افکند، که ناگهان ماری شد که (به هر سو) می‌دوید.');
INSERT INTO "hussein_taji" VALUES(20,21,'20:21','فرمود: «آن را بگیر و نترس، به زودی آن را به صورت اولش باز می‌گردانیم.');
INSERT INTO "hussein_taji" VALUES(20,22,'20:22','و دستت را در بغل خود داخل کن، تا سفید (و) بی عیب بیرون آید، (این نیز) معجزۀ دیگری است.');
INSERT INTO "hussein_taji" VALUES(20,23,'20:23','تا آیات (و نشانه‌های) بزرگ خود را به تو نشان دهیم.');
INSERT INTO "hussein_taji" VALUES(20,24,'20:24','(اینک) به سوی فرعون برو، بی‌گمان او سرکشی کرده است».');
INSERT INTO "hussein_taji" VALUES(20,25,'20:25','(موسی) گفت: «پروردگارا! سینه‌ام را برایم بگشا.');
INSERT INTO "hussein_taji" VALUES(20,26,'20:26','و کارم را برایم آسان گردان.');
INSERT INTO "hussein_taji" VALUES(20,27,'20:27','و گره از زبانم باز کن.');
INSERT INTO "hussein_taji" VALUES(20,28,'20:28','تا سخنانم را بفهمند.');
INSERT INTO "hussein_taji" VALUES(20,29,'20:29','و برای من (یاور و) وزیری از خاندانم قرار ده.');
INSERT INTO "hussein_taji" VALUES(20,30,'20:30','برادرم هارون را.');
INSERT INTO "hussein_taji" VALUES(20,31,'20:31','به (وسیلۀ) او پشت مرا محکم کن،');
INSERT INTO "hussein_taji" VALUES(20,32,'20:32','و او را در کار (رسالت) من شریک ساز،');
INSERT INTO "hussein_taji" VALUES(20,33,'20:33','تا تو را بسیار تسبیح گوییم،');
INSERT INTO "hussein_taji" VALUES(20,34,'20:34','و بسیار تو را یاد کنیم،');
INSERT INTO "hussein_taji" VALUES(20,35,'20:35','بی‌شک تو به (حال) ما خوب بینایی».');
INSERT INTO "hussein_taji" VALUES(20,36,'20:36','فرمود: «ای موسی! هر چه خواستی به تو داده شد.');
INSERT INTO "hussein_taji" VALUES(20,37,'20:37','و به تحقیق ما بار دیگر (نیز) بر تو منت نهاده‌ایم (و نعمت بخشیده‌ایم)');
INSERT INTO "hussein_taji" VALUES(20,38,'20:38','آن هنگام که به مادرت الهام کردیم، آنچه را که باید الهام می‌شد،');
INSERT INTO "hussein_taji" VALUES(20,39,'20:39','که: «او را در صندوقی بگذار، آنگاه آن (صندوق) را به دریا بینداز، تا دریا او را به ساحل افکند، (و) دشمن من و دشمن او، او را برگیرد». و من محبتی از سوی خود بر تو افکندم و تا در برابر دیدگان من پرورش یابی.');
INSERT INTO "hussein_taji" VALUES(20,40,'20:40','آنگاه که خواهرت می‌رفت و می‌گفت: «آیا کسی را به شما نشان دهم که سرپرستی‌اش را به عهده گیرد؟!» پس تو را به مادرت باز گرداندیم، تا چشمش (به دیدار تو) روشن شود، و اندوهگین نگردد، و (بعدها) تو یک نفر (قبطی) را کُشتی، پس (ما) تو را از اندوه (و گرفتاری) نجات دادیم، و بار‌ها (چنانکه باید) تو را آزمودیم. پس (از آن) سال‌ها در میان مردم مدین ماندی، سپس ای موسی! بر (طبق) تقدیر (الهی به اینجا) آمدی.');
INSERT INTO "hussein_taji" VALUES(20,41,'20:41','و تو را برای خودم خاص ساختم (و پرورش دادم).');
INSERT INTO "hussein_taji" VALUES(20,42,'20:42','(اینک) تو و برادرت با آیات من برو، و در یاد من سستی نکنید.');
INSERT INTO "hussein_taji" VALUES(20,43,'20:43','به سوی فرعون بروید، بی‌گمان او سرکشی کرده است.');
INSERT INTO "hussein_taji" VALUES(20,44,'20:44','پس به نرمی با او سخن بگویید، شاید که او پند گیرد یا (از پروردگارش) بترسد،');
INSERT INTO "hussein_taji" VALUES(20,45,'20:45','(موسی و هارون) گفتند: «پروردگارا! به راستی ما می‌ترسیم که بر ما پیش‌دستی نماید (و قبل از بیان حق ما را آزار دهد) یا طغیان کند»');
INSERT INTO "hussein_taji" VALUES(20,46,'20:46','فرمود: «نترسید! بی‌شک من با شما هستم، می‌شنوم و می‌بینم.');
INSERT INTO "hussein_taji" VALUES(20,47,'20:47','پس به نزد او بروید و بگویید: ما فرستادگان پروردگارت هستیم، بنی اسرائیل را با ما بفرست، و آن‌ها را آزار و شکنجه نکن، به‌راستی که ما نشانه‌هاى روشنى از سوی پروردگارت برای تو آورده‌ایم، و سلام بر آن کسی‌که از هدایت پیروی کند.');
INSERT INTO "hussein_taji" VALUES(20,48,'20:48','همانا به ما وحی شده که محققاً عذاب (الهی) بر کسی است که (آیات و معجزات را) تکذیب کند، و (از ایمان) روی بگرداند».');
INSERT INTO "hussein_taji" VALUES(20,49,'20:49','(فرعون) گفت: «ای موسی! پروردگار شما کیست؟»');
INSERT INTO "hussein_taji" VALUES(20,50,'20:50','(موسی) گفت: «پروردگار ما کسی است که آفرینش هر چیزی را به او ارزانی داشته، سپس (آن‌ها را) هدایت کرده است»');
INSERT INTO "hussein_taji" VALUES(20,51,'20:51','(فرعون) گفت: «پس حال (و سرنوشت) نسل‌های گذشته چه می‌شود؟»');
INSERT INTO "hussein_taji" VALUES(20,52,'20:52','(موسی) گفت: «علم آن نزد پروردگارم در کتابی (ثبت) است، پروردگارم نه اشتباه می‌کند و نه فراموش می‌کند.');
INSERT INTO "hussein_taji" VALUES(20,53,'20:53','(همان) کسی‌که زمین را برای شما گهواره (و محل آسایش) قرار داد، و برای شما در آن راه‌هایی ایجاد نمود، و از آسمان آبی فرو فرستاد پس با آن، انواع گوناگون گیاه را (از زمین) بیرون آوردیم.');
INSERT INTO "hussein_taji" VALUES(20,54,'20:54','بخورید، و (نیز) چهارپایانتان را بچرانید، بی‌گمان در این (امور) نشانه‌های برای خردمندان است.');
INSERT INTO "hussein_taji" VALUES(20,55,'20:55','(ما) شما را از آن (= زمین) آفریدیم، و در آن باز می‌گردانیم، و بار دیگر شما را از آن بیرون می‌آوریم.');
INSERT INTO "hussein_taji" VALUES(20,56,'20:56','و به راستی (ما) همۀ آیات خود را به او نشان دادیم، پس (او همه را) تکذیب کرد و سر باز زد.');
INSERT INTO "hussein_taji" VALUES(20,57,'20:57','گفت: «ای موسی! آیا به نزد ما آمده‌ای که با سحر خود، ما را از سرزمینمان بیرون کنی؟!');
INSERT INTO "hussein_taji" VALUES(20,58,'20:58','پس یقیناً ما هم سحری مانند آن برای تو می‌آوریم، پس (هم اکنون) در مکانی هموار (و مقبول همه) موعدی میان ما و خودت قرار بده که نه ما و نه تو از آن تخلف نکنیم،');
INSERT INTO "hussein_taji" VALUES(20,59,'20:59','(موسی) گفت: «موعد شما روز (عید) زینت است و آن که (همۀ) مردم چاشتگاه گرد آورده شوند»');
INSERT INTO "hussein_taji" VALUES(20,60,'20:60','آنگاه فرعون باز گشت، پس (همۀ) مکر و حیله خود را جمع کرد، سپس آمد.');
INSERT INTO "hussein_taji" VALUES(20,61,'20:61','موسی به آن‌ها گفت: «وای بر شما! بر الله دروغ نبندید، که شما را با عذابی نابود می‌سازد، و یقیناً کسی‌که (بر الله) افترا بندد، ناکام (و ناامید) می‌شود»');
INSERT INTO "hussein_taji" VALUES(20,62,'20:62','پس آن‌ها در کار‌شان میان خود اختلاف کردند، و راز‌شان را پنهان داشتند.');
INSERT INTO "hussein_taji" VALUES(20,63,'20:63','گفتند: «مسلماً این دو (نفر) جادوگرند، می‌خواهند شما را با جادو‌شان از سرزمینتان بیرون کنند، و آیین (و راه و رسم) برتر شما را از بین ببرند.');
INSERT INTO "hussein_taji" VALUES(20,64,'20:64','پس (همۀ) حیله (و تدبیر) خود را گرد آورید (و به کار بندید) سپس در یک صف (برای مبارزه) بیایید، و به راستی امروز کسی رستگار خواهد شد که چیره (و غالب) گردد.');
INSERT INTO "hussein_taji" VALUES(20,65,'20:65','(ساحران) گفتند: «ای موسی! یا این است که (اول) تو (عصا را) بیفکنی یا آنکه ما کسانی باشیم که اول بیفکنیم؟!»');
INSERT INTO "hussein_taji" VALUES(20,66,'20:66','(موسی) گفت: «بلکه، (شما) بیفکنید» پس ناگهان ریسمان‌هایشان و عصا‌هایشان از (اثر) سحر‌شان چنان به نظرش رسید که حرکت می‌کند (و می‌دوند)');
INSERT INTO "hussein_taji" VALUES(20,67,'20:67','پس موسی در دل خود ترسی احساس کرد که (مبادا در ایمان مردم خللی ایجاد شود).');
INSERT INTO "hussein_taji" VALUES(20,68,'20:68','گفتیم: «نترس! مسلماً تو (غالب و) برتری،');
INSERT INTO "hussein_taji" VALUES(20,69,'20:69','و آنچه را که در دست راست خود داری بیفکن، تا (تمام) آنچه را که ساخته‌اند، ببلعد، بی‌شک آنچه ساخته‌اند، تنها مکر ساحر است، و ساحر هر جا رود رستگار (و موفق) نخواهد شد».');
INSERT INTO "hussein_taji" VALUES(20,70,'20:70','(پس موسی عصای خود را افکند، ناگهان به مار بزرگی تبدیل شد و همۀ آن‌ها را که ساخته بودند بلعید) آنگاه ساحران (همگی) به سجده افتادند، گفتند: «(ما) به پروردگار هارون و موسی ایمان آوردیم».');
INSERT INTO "hussein_taji" VALUES(20,71,'20:71','(فرعون) گفت: «آیا پیش از آن که به شما اجازه دهم به او ایمان آوردید؟! بی‌تردید او بزرگ شماست که به شما سحر آموخته است، پس یقیناً دستانتان و پاهایتان را بر خلاف یکدیگر قطع می‌کنم، و شما را بر تنه‌های نخل به دار می‌آویزم، و بی‌شک خواهید دانست که عذاب (و شکنجه) کدام‌یک از ما سخت‌تر است و پایدار‌تر است!».');
INSERT INTO "hussein_taji" VALUES(20,72,'20:72','گفتند: «سوگند به آن که ما را آفریده است، هرگز تو را بر این دلایل روشنی که برای ما آمده، ترجیح نمی‌دهیم، پس به هر حکمی که می‌خواهی حکم کن، تو تنها می‌توانی در این زندگی دنیا حکم کنی.');
INSERT INTO "hussein_taji" VALUES(20,73,'20:73','همانا ما به پروردگارمان ایمان آورده‌ایم تا گناهانمان و آنچه را از سحر که ما را به آن وادار کردی، برای‌مان ببخشاید، و الله بهتر و پایدارتر است».');
INSERT INTO "hussein_taji" VALUES(20,74,'20:74','بی‌گمان کسی‌که گناه‌کار به نزد پروردگارش حاضر شود، پس آتش جهنم برای اوست، در آن جا نه می‌میرد، و نه زنده می‌ماند.');
INSERT INTO "hussein_taji" VALUES(20,75,'20:75','و کسی‌که مؤمن نزد او آید، در حالی‌که کارهای شایسته انجام داده باشد، پس اینان برایشان درجات بلند است.');
INSERT INTO "hussein_taji" VALUES(20,76,'20:76','باغ‌های جاویدان (بهشتی) که نهر‌ها زیر (درختان) آن جاری است، همیشه در آن خواهند بود، و این است پاداش کسی‌که خود را (از شرک و گناهان) پاک نموده است.');
INSERT INTO "hussein_taji" VALUES(20,77,'20:77','و به‌راستی (ما) به موسی وحی کردیم که: «شبانه بندگان مرا (از مصر بیرون) ببر، سپس برای آن‌ها در دریا راهی خشک بگشا، که نه از تعقیب (دشمنان) خواهی ترسید، و نه (از غرق شدن در دریا) هراسی داری.');
INSERT INTO "hussein_taji" VALUES(20,78,'20:78','پس فرعون با لشکریانش آنان را دنبال کردند، آنگاه دریا چنانکه باید آنان را (به‌طور کامل در میان امواج خود) فرو پوشانید.');
INSERT INTO "hussein_taji" VALUES(20,79,'20:79','و فرعون قوم خود را گمراه نمود، و هدایت نکرد.');
INSERT INTO "hussein_taji" VALUES(20,80,'20:80','ای بنی اسرائیل! به‌راستی (ما) شما را از (دست) دشمنانتان نجات دادیم، و در طرف راست (کوه) طور با شما وعده گذاردیم، (تا تورات را بر شما نازل کنیم) و بر شما «منّ» (= ترنجبین) و «سلوی» (= بلدرچین) نازل کردیم.');
INSERT INTO "hussein_taji" VALUES(20,81,'20:81','از پاکیزه‌ها آنچه به شما روزی داده‌ایم؛ بخورید، و در آن سرکشی نکنید، که خشم من بر شما وارد می‌شود، و کسی‌که خشم من بر او وارد شود، قطعاً نابود شده است.');
INSERT INTO "hussein_taji" VALUES(20,82,'20:82','و همانا من آمرزنده‌ام برای کسی‌که توبه کرد و ایمان آورد، و کار شایسته انجام داد، سپس هدایت یافت.');
INSERT INTO "hussein_taji" VALUES(20,83,'20:83','ای موسی! چه چیز سبب شد که از قوم خود پیشی گیری؟');
INSERT INTO "hussein_taji" VALUES(20,84,'20:84','(موسی) گفت: «پروردگارا! آنان به دنبال من هستند، و من به سوی تو شتاب کردم، تا (از من) خشنود شوی».');
INSERT INTO "hussein_taji" VALUES(20,85,'20:85','فرمود: «بی‌گمان ما قوم تو را بعد از تو، آزمودیم، و سامری آن‌ها را گمراه کرد».');
INSERT INTO "hussein_taji" VALUES(20,86,'20:86','پس موسی خشمگین (و) اندوهناک به سوی قومش بازگشت، (و) گفت: «ای قوم من! مگر پروردگارتان وعدۀ نیکو به شما نداده بود؟ آیا مدت (جدایی من از شما) به درازا کشید، یا خواستید خشمی از (سوی) پروردگارتان بر شما نازل شود، که وعدۀ مرا خلاف کردید؟».');
INSERT INTO "hussein_taji" VALUES(20,87,'20:87','گفتند: «(ما) به اختیار خود وعدۀ تو را خلاف نکردیم، لیکن بارهای سنگینی از زینت (و زیورهای) قوم (فرعون) که با خود داشتیم، آن را (در آتش) افکندیم» پس بدین‌سان سامری نیز (آنچه داشت) افکند.');
INSERT INTO "hussein_taji" VALUES(20,88,'20:88','آنگاه برای آن‌ها مجسمۀ گوساله‌ای که صدایی (چون صدای گوساله) داشت، پدید آورد، پس (سامری و پیروانش به مردم) گفتند: «این معبود شما، و معبود موسی است، که آن را فراموش کرده است».');
INSERT INTO "hussein_taji" VALUES(20,89,'20:89','آیا نمی‌بینند که (این گوساله) هیچ پاسخی به آن‌ها نمی‌دهد، و مالک هیچ سود و زیانی برای آن‌ها نیست؟');
INSERT INTO "hussein_taji" VALUES(20,90,'20:90','و بی‌شک هارون پیش از این به آن‌ها گفته بود: «ای قوم من! یقیناً شما به (وسیلۀ) این (گوساله) آزمون شده‌اید، و بی‌گمان پروردگار شما (الله) رحمان است، پس از من پیروی نمائید و فرمانم را اطاعت کنید».');
INSERT INTO "hussein_taji" VALUES(20,91,'20:91','(آن‌ها) گفتند: «(ما) پیوسته پیرامون آن (برای پرستش) می‌نشینیم (و آن را عبادت می‌کنیم)، تا موسی به سوی ما باز گردد».');
INSERT INTO "hussein_taji" VALUES(20,92,'20:92','(چون موسی آمد) گفت: «ای هارون! چه چیزی تو را باز داشت، هنگامی‌که آن‌ها را دیدی گمراه شده‌اند،');
INSERT INTO "hussein_taji" VALUES(20,93,'20:93','از من پیروی نکردی؟ آیا فرمان مرا عصیان (و سرپیچی) کردی؟!».');
INSERT INTO "hussein_taji" VALUES(20,94,'20:94','(هارون) گفت: «ای پسر مادرم! نه ریش مرا بگیر، و نه (موی) سرم را، همانا من ترسیدم که بگویی میان بنی اسرائیل تفرقه‌انداختی، و سخن (و سفارش) مرا به کار نبستی».');
INSERT INTO "hussein_taji" VALUES(20,95,'20:95','(موسی رو به سامری کرد و) گفت: «ای سامری! (این) کار تو چیست؟ (چرا چنین کرده‌ای؟)».');
INSERT INTO "hussein_taji" VALUES(20,96,'20:96','گفت: «من چیزی را دیدم که آن‌ها ندیدند، پس مشتی (خاک) از رد پای (اسب) رسول (= جبرئیل) را گرفتم، آنگاه آن را (بر پیکر) افکندم، و این چنین (هوای) نفسم (این کار را) برایم آراسته جلوه داد».');
INSERT INTO "hussein_taji" VALUES(20,97,'20:97','(موسی) گفت: «پس برو، بی‌شک بهرۀ تو در زندگی دنیا این است که بگویی: «(به من) دست نزنید (و نزدیک نشوید)» و همانا وعده‌ای (از عذاب) داری، که هرگز تخلف نخواهد شد، و (اکنون) به معبودت بنگر که پیوسته عبادتش می‌کردی (و آن را رها نمی‌کردی) آن را خواهیم سوزاند، سپس (خاکستر و ذرات) آن را در دریا پراکنده می‌سازیم.');
INSERT INTO "hussein_taji" VALUES(20,98,'20:98','معبود شما تنها الله است که جز او هیچ معبودی نیست؛ و علم او همه چیز را فرا گرفته است».');
INSERT INTO "hussein_taji" VALUES(20,99,'20:99','(ای پیامبر!) این گونه از خبرهای آنچه گذشته است بر تو باز گو می‌کنیم، و به راستی (ما) از جانب خود به تو ذکر (= قرآن) داده‌ایم.');
INSERT INTO "hussein_taji" VALUES(20,100,'20:100','کسی‌که از آن (قرآن) روی گردان شود، پس بی‌گمان او روز قیامت بار سنگینی (از گناه) بر دوش خواهد کشید.');
INSERT INTO "hussein_taji" VALUES(20,101,'20:101','که در آن (رنج) جاودانه خواهند ماند، و برای آن‌ها روز قیامت بد باری است.');
INSERT INTO "hussein_taji" VALUES(20,102,'20:102','(همان) روزی‌که در «صور» دمیده شود، و مجرمان را کبود (چشم) در آن روز گرد آوریم.');
INSERT INTO "hussein_taji" VALUES(20,103,'20:103','آن‌ها در میان خود آهسته سخن می‌گویند، (و به یکدیگر می‌گویند:) «(شما) فقط ده روز (در دنیا) درنگ کرده‌اید».');
INSERT INTO "hussein_taji" VALUES(20,104,'20:104','ما به آنچه می‌گویند؛ داناتریم، چون نیکو روش‌ترین آن‌ها بگوید: «(شما) تنها یک روز درنگ کرده‌اید».');
INSERT INTO "hussein_taji" VALUES(20,105,'20:105','و (ای پیامبر!) از تو دربارۀ کوه‌ها می‌پرسند، پس بگو: «پروردگارم آن‌ها را سخت متلاشی (و پراکنده) خواهد کرد،');
INSERT INTO "hussein_taji" VALUES(20,106,'20:106','پس آن‌ها را (زمینی) صاف و هموار می‌گرداند،');
INSERT INTO "hussein_taji" VALUES(20,107,'20:107','که در آن هیچ پستی و بلندی را نبینی».');
INSERT INTO "hussein_taji" VALUES(20,108,'20:108','(در) آن روز، (همگی) دعوت کننده را پیروی کنند، هیچ راه سرپیچی (و مخالفت) نیست، و همۀ صداها در برابر (الله) رحمان خاشع می‌شود، پس جز صدای آهسته (چیزی) نمی‌شنوی.');
INSERT INTO "hussein_taji" VALUES(20,109,'20:109','آن روز شفاعت (هیچ کس) سود نمی‌بخشد، جز کسی‌که (الله) رحمان به او اجازه داده، و گفتار او را پسندیده است.');
INSERT INTO "hussein_taji" VALUES(20,110,'20:110','آنچه را پیش رو دارند، و آنچه را پشت سرشان است می‌داند، و آن‌ها به علم او احاطه ندارند.');
INSERT INTO "hussein_taji" VALUES(20,111,'20:111','و (همۀ) چهره‌ها در برابر (الله) زندۀ پاینده فروتن می‌شود، و آن که (بار) ستم بر دوش دارد، ناکام (و مأیوس) می‌گردد.');
INSERT INTO "hussein_taji" VALUES(20,112,'20:112','و کسی‌که کارهای شایسته انجام دهد در حالی‌که او مؤمن باشد، پس نه از ستمی می‌ترسد و نه از کم و کاستی، (در حقش می‌هراسد).');
INSERT INTO "hussein_taji" VALUES(20,113,'20:113','و این گونه آن را قرآنی عربی نازل کردیم، و وعید (و هشدارهای) گوناگون را در آن بیان داشتیم، شاید آن‌ها تقوا پیشه کنند، یا این کتاب برای آن‌ها پندی پدید آورد.');
INSERT INTO "hussein_taji" VALUES(20,114,'20:114','پس بلند مرتبه است الله که فرمانروای حق است، به (تلاوت) قرآن شتاب مکن؛ پیش از آن که وحی آن برتو تمام شود، و بگو: «پروردگارا! به علم من بیفزا».');
INSERT INTO "hussein_taji" VALUES(20,115,'20:115','و به راستی پیش از این به آدم سفارش کردیم (و عهد بستیم) پس او فراموش کرد، و برای او عزمی (استوار) نیافتیم.');
INSERT INTO "hussein_taji" VALUES(20,116,'20:116','و (به یاد آور) آنگاه که به فرشتگان گفتیم: «برای آدم سجده کنید» پس (همگی) سجده کردند؛ جز ابلیس که سر باز زد.');
INSERT INTO "hussein_taji" VALUES(20,117,'20:117','پس گفتیم: «ای آدم! بی‌گمان این (ابلیس) دشمن تو و (دشمن) همسرت است، پس مبادا شما را از بهشت بیرون کند که به رنج (و مشقت) افتی.');
INSERT INTO "hussein_taji" VALUES(20,118,'20:118','همانا برای تو این است که در آن نه گرسنه می‌شوی و نه برهنه می‌مانی.');
INSERT INTO "hussein_taji" VALUES(20,119,'20:119','و آن که تو در آن نه تشنه می‌شوی و نه گرما (و سوزش آفتاب) می‌یابی.');
INSERT INTO "hussein_taji" VALUES(20,120,'20:120','پس شیطان او را به وسوسه انداخت، گفت: «ای آدم! آیا (می‌خواهی) تو را به درخت جاودانگی و فرمانروایی بی‌زوال راهنمایی کنم؟!».');
INSERT INTO "hussein_taji" VALUES(20,121,'20:121','آنگاه (آدم و حوا) از آن (درخت) خوردند، (و لباس‌های بهشتی آن‌ها ریخت) پس عورتشان برایشان آشکار گشت، و شروع کردند که از برگ (درختان) بهشت بر خودشان می‌چسباندند، و آدم پروردگارش را نافرمانی کرد، پس گمراه شد.');
INSERT INTO "hussein_taji" VALUES(20,122,'20:122','سپس پروردگارش او را برگزید، پس توبه‌اش را پذیرفت، و (او را) هدایت کرد.');
INSERT INTO "hussein_taji" VALUES(20,123,'20:123','فرمود: «(شما دو تن با ابلیس) همگی از آن (بهشت) فرود آیید، در حالی‌که دشمن یکدیگر خواهید بود، پس اگر از (سوی) من هدایتی برای شما بیاید، هر کس از هدایت من پیروی کند، پس نه گمراه می‌شود و نه به رنج افتد (و بدبخت شود).');
INSERT INTO "hussein_taji" VALUES(20,124,'20:124','و کسی‌که از یاد من روی گردان شود، پس بی‌گمان زندگانی (سخت و) تنگی خواهد داشت، و روز قیامت او را نابینا بر انگیزیم.');
INSERT INTO "hussein_taji" VALUES(20,125,'20:125','گوید: «پروردگارا! چرا مرا نابینا برانگیختی؟ و حال آنکه من (در دنیا) بینا بودم!».');
INSERT INTO "hussein_taji" VALUES(20,126,'20:126','فرمود: «همان گونه که آیات ما برای تو آمد پس آن‌ها را فراموش کردی؛ و این گونه امروز (تو در آتش) فراموش خواهی شد».');
INSERT INTO "hussein_taji" VALUES(20,127,'20:127','و این گونه کسی را که اسراف کند، و به آیات پروردگارش ایمان نیاورد؛ جزا می‌دهیم، و یقیناً عذاب آخرت شدیدتر و پایدارتر است.');
INSERT INTO "hussein_taji" VALUES(20,128,'20:128','آیا برای هدایت (و بیداری) آن‌ها کافی نیست که پیش از اینان چه بسیار از نسل‌ها را هلاک کردیم که در مساکن آن‌ها راه می‌روند؟! بی‌گمان در این (امر) نشانه‌های برای خردمندان است.');
INSERT INTO "hussein_taji" VALUES(20,129,'20:129','و اگر فرمانی که پیش از این از پروردگارت مقرر شده (مبنی بر تأخیر عذاب) و وقت معین، نبود، مسلماً (عذاب الهی) بر آن‌ها محقق می‌شد.');
INSERT INTO "hussein_taji" VALUES(20,130,'20:130','پس (ای پیامبر!) بر آنچه می‌گویند، صبر کن، و پیش از طلوع آفتاب و قبل از غروب آن به ستایش پروردگارت تسبیح گوی، و (نیز) در ساعاتی از شب و اطراف روز تسبیح گوی، باشد که خشنود شوی.');
INSERT INTO "hussein_taji" VALUES(20,131,'20:131','و هرگز چشمان خود را به (سوی) آنچه (از نعمت‌ها و متاع دنیوی) که گروه‌هایی از آنان را از آن بهره‌مند ساخته‌ایم، ندوز، این‌ها زینت‌ها (و شکوفه‌های) زندگی دنیا است، تا آنان را در آن بیازماییم، و روزی پروردگار تو بهتر و پایدارتر است.');
INSERT INTO "hussein_taji" VALUES(20,132,'20:132','و خانواده‌ات را به نماز فرمان بده، و (خود) بر (انجام) آن شکیبا باش، (ما) از تو روزی نمی‌خواهیم، (بلکه) ما به تو روزی می‌دهیم، و سرانجام نیک برای پرهیزگاران است.');
INSERT INTO "hussein_taji" VALUES(20,133,'20:133','و (مشرکان) گفتند: «چرا معجزه‌ای از جانب پروردگارش برای ما نمی‌آورد؟!» بگو: «آیا دلایل روشنی که در کتاب‌های پیشین بوده برای آن‌ها نیامده است؟!');
INSERT INTO "hussein_taji" VALUES(20,134,'20:134','اگر (ما) آن‌ها را پیش از آمدن او (= پیامبر) با عذابی هلاک می‌کردیم؛ قطعاً (روز قیامت) می‌گفتند: «پروردگارا! چرا پیامبری به‌سوی ما نفرستادی، تا پیش از آن که خوار و رسوا شویم از آیات تو پیروی کنیم؟!».');
INSERT INTO "hussein_taji" VALUES(20,135,'20:135','(ای پیامبر!) بگو: «همه منتظرند، شما (نیز) منتظر باشید، پس به‌زودی خواهید دانست که چه کسی اصحاب راه راست، و چه کسی هدایت یافته‌اند».');
INSERT INTO "hussein_taji" VALUES(21,1,'21:1','حساب مردم به آن‌ها نزدیک شده است، در حالی‌که آن‌ها در غفلت روی گردانند.');
INSERT INTO "hussein_taji" VALUES(21,2,'21:2','هیچ پند تازه‌ای از جانب پروردگارشان برای آن‌ها نمی‌آید، مگر آنکه بازی‌کنان به آن گوش می‌دهند.');
INSERT INTO "hussein_taji" VALUES(21,3,'21:3','(در حالی که) دل‌هایشان غافل (و در لهو سرگرم) است. و کسانی‌که ستمکار بودند پنهانی نجوی کردند (و گفتند:) «آیا جز این است که او بشری مانند شماست؟! آیا به جادو روی می‌آورید، در حالی‌که (حقیقت را) می‌بینید؟!»');
INSERT INTO "hussein_taji" VALUES(21,4,'21:4','(پیامبر) گفت: «پروردگارم هر سخنی را که در آسمان و زمین باشد، می‌داند، و او شنوای داناست».');
INSERT INTO "hussein_taji" VALUES(21,5,'21:5','بلکه (آن‌ها) گفتند: «(آنچه او آورده) خواب‌های آشفته است، بلکه آن را به‌دروغ (به الله) افترا بسته است، (نه) بلکه او شاعر است، پس (اگر راست می‌گوید) برای ما معجزه‌ای بیاورد، چنانکه پیامبران پیشین (با معجزات) فرستاده شدند».');
INSERT INTO "hussein_taji" VALUES(21,6,'21:6','پیش از آن‌ها هیچ (آبادی و) قریه‌ای که آن را هلاک کردیم؛ ایمان نیاورده بودند، آیا این‌ها ایمان می‌آورند؟!');
INSERT INTO "hussein_taji" VALUES(21,7,'21:7','و (ای پیامبر!) پیش از تو، جز مردانی را که به آن‌ها وحی می‌کردیم؛ نفرستادیم، پس (ای مردم!) اگر نمی‌دانید از اهل کتاب بپرسید.');
INSERT INTO "hussein_taji" VALUES(21,8,'21:8','و (ما) آن‌ها را پیکرهایی که غذا نخورند، قرار ندادیم، و (در دنیا) جاوید هم نبودند.');
INSERT INTO "hussein_taji" VALUES(21,9,'21:9','سپس وعده‌ای را که به آن‌ها داده بودیم راست گردانیدیم (و وفا کردیم) پس آن‌ها و هر که را خواستیم (از دشمنانشان) نجات دادیم، و مسرفان را هلاک کردیم.');
INSERT INTO "hussein_taji" VALUES(21,10,'21:10','به‌راستی به‌سوی شما کتابی نازل کردیم که آوازه (و عزت) شما در آن است، آیا خرد نمی‌ورزید؟');
INSERT INTO "hussein_taji" VALUES(21,11,'21:11','چه بسیار شهرهایی را که (ساکنانش) ستمکار بودند؛ درهم شکستیم، و بعد از آن‌ها قوم دیگری را پدید آوردیم.');
INSERT INTO "hussein_taji" VALUES(21,12,'21:12','پس چون عذاب ما را احساس کردند، ناگهان آن‌ها ازآنجا می‌گریختند.');
INSERT INTO "hussein_taji" VALUES(21,13,'21:13','(گفتیم:) فرار نکنید، به‌سوی خانه‌هایتان و نعمت‌های که در آن آسوده بودید، باز گردید، باشد که باز خواست شوید.');
INSERT INTO "hussein_taji" VALUES(21,14,'21:14','گفتند: «وای بر ما، یقیناً ما ستمکار بودیم».');
INSERT INTO "hussein_taji" VALUES(21,15,'21:15','پس پیوسته فریادشان این بود، تا آنکه آن‌ها را (چون کشت) درو شده خاموش ساختیم.');
INSERT INTO "hussein_taji" VALUES(21,16,'21:16','و (ما) آسمان و زمین، و آنچه را که در میان آن دو است به بازیچه نیافریدیم.');
INSERT INTO "hussein_taji" VALUES(21,17,'21:17','(به فرض محال) اگر می‌خواستیم که سر گرمی بگیریم، همانا آن را از نزد خودمان بر می‌گرفتیم، اگر (چنین کاری) می‌کردیم.');
INSERT INTO "hussein_taji" VALUES(21,18,'21:18','بلکه ما حق را بر باطل می‌افکنیم، پس آن را درهم می‌شکند و ناگاه آن (باطل) نابود می‌شود، و وای بر شما (ای کافران) از آنچه توصیف می‌کنید.');
INSERT INTO "hussein_taji" VALUES(21,19,'21:19','و هرکس که در آسمان‌ها و زمین است، از آن اوست، و آنان که نزد او هستند، (هیچ‌گاه) از عبادتش سرکشی نمی‌کنند، و (هرگز) خسته نمی‌شوند.');
INSERT INTO "hussein_taji" VALUES(21,20,'21:20','شب و روز تسبیح می‌گویند، سستی نمی‌ورزند.');
INSERT INTO "hussein_taji" VALUES(21,21,'21:21','آیا آن‌ها معبودانی از زمین برگزیده‌اند، که آن‌ها (مردگان را) بر می‌انگیزند.');
INSERT INTO "hussein_taji" VALUES(21,22,'21:22','اگر در این دو (= آسمان و زمین) معبودانی جز «الله» بود، مسلماً هر دو (= آسمان و زمین) تباه می‌شدند (و نظام هستی به هم می‌خورد) پس منزه است الله، پروردگار عرش، از آنچه آن‌ها توصیف می‌کنند.');
INSERT INTO "hussein_taji" VALUES(21,23,'21:23','(او) از آنچه می‌کند، باز خواست نمی‌شود، و آنان باز خواست می‌شوند.');
INSERT INTO "hussein_taji" VALUES(21,24,'21:24','آیا به‌جز او معبودانی را بر گزیده‌اند؟! بگو: «دلیلتان را بیاورید، این پند کسانی است که با من هستند، و پند آنان که پیش از من بودند» بلکه بیشتر آن‌ها حق را نمی‌دانند، لذا آن‌ها (از آن) روی گردانند.');
INSERT INTO "hussein_taji" VALUES(21,25,'21:25','و (ما) پیش از تو هیچ پیامبری را نفرستادیم، مگر آنکه به او وحی کردیم که: «معبودی جز من نیست، پس تنها مرا عبادت کنید».');
INSERT INTO "hussein_taji" VALUES(21,26,'21:26','و گفتند: «(الله) رحمان فرزندی برگزیده است» او منزه است، بلکه آنان (= فرشتگان) بندگان گرامی (او) هستند،');
INSERT INTO "hussein_taji" VALUES(21,27,'21:27','(هرگز) در سخن بر او پیشی نمی‌گیرند، و به‌فرمان او کار می‌کنند.');
INSERT INTO "hussein_taji" VALUES(21,28,'21:28','(الله) آنچه که در پیش روی آن‌ها است، و آنچه که پشت سر آن‌هاست می‌داند، و آن‌ها جز برای کسی‌که (الله از او خشنود باشد و) بپسندد، شفاعت نمی‌کنند، و آن‌ها از ترس او بیمناک‌اند.');
INSERT INTO "hussein_taji" VALUES(21,29,'21:29','و هر کس از آن‌ها بگوید: «همانا من به جای او معبود هستم» پس سزای چنین کسی را جهنم می‌دهیم، و ستمکاران را این گونه سزا می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(21,30,'21:30','آیا کسانی‌که کافر شدند؛ ندیدند که آسمان‌ها و زمین به هم پیوسته بودند، پس (ما) آن‌ها را از یکدیگر باز کردیم، و هر چیز زنده‌ای را از آب پدید آوردیم؟! آیا ایمان نمی‌آورند؟!');
INSERT INTO "hussein_taji" VALUES(21,31,'21:31','و در زمین، کوه‌ها (ی استوار) قرار دادیم، که (مبادا) آن‌ها را بلرزاند، و در آن راه‌هایی گشاده قرار دادیم؛ باشد که آنان راه یابند.');
INSERT INTO "hussein_taji" VALUES(21,32,'21:32','و آسمان را سقفی محفوظ قرار دادیم، و آن‌ها از آیات آن روی گردانند.');
INSERT INTO "hussein_taji" VALUES(21,33,'21:33','و او کسی است که شب و روز، و خورشید و ماه را آفرید، هر یک در مداری شناورند.');
INSERT INTO "hussein_taji" VALUES(21,34,'21:34','و پیش از تو (ای پیامبر!) برای هیچ بشری جاودانگی قرار ندادیم، آیا اگر تو بمیری، پس آن‌ها جاوید خواهند بود؟!');
INSERT INTO "hussein_taji" VALUES(21,35,'21:35','هر کسی چشندۀ مرگ است، و شما را به بدی و نیکی (کاملا) آزمایش می‌کنیم، و به‌سوی ما بازگردانده می‌شوید.');
INSERT INTO "hussein_taji" VALUES(21,36,'21:36','و کسانی‌که کافر شدند؛ هنگامی‌که تو را ببینند، فقط تو را به ریشخند می‌گیرند، (و می‌گویند:) «آیا این همان کسی است که معبودان شما را (به بدی) یاد می‌کند؟!» در حالی‌که خودشان یاد (الله) رحمان را انکار می‌کنند.');
INSERT INTO "hussein_taji" VALUES(21,37,'21:37','انسان از شتاب آفریده شده، به‌زودی آیات خود را به شما نشان خواهیم داد، پس شتاب نکنید.');
INSERT INTO "hussein_taji" VALUES(21,38,'21:38','و می‌گویند: «اگر راست می‌گویید، این وعدۀ (قیامت) کی خواهد بود؟!».');
INSERT INTO "hussein_taji" VALUES(21,39,'21:39','اگر کسانی‌که کافر شدند؛ می‌دانستند زمانی که (قیامت فرا رسد) نمی‌توانند (شعله‌های) آتش را از چهره‌هایشان و نه از پشت‌هایشان باز دارند، و نه کسی آن‌ها را یاری می‌کند، (چنین نمی‌گفتند).');
INSERT INTO "hussein_taji" VALUES(21,40,'21:40','بلکه (حق این است که آن) ناگهانی به سراغ‌شان می‌آید، پس مبهوت‌شان می‌کند، آنگاه نمی‌توانند آن را دفع کنند، و نه به آن‌ها مهلت داده شود.');
INSERT INTO "hussein_taji" VALUES(21,41,'21:41','و به‌راستی به پیامبران پیش از تو (نیز) استهزاء شد (و مورد تمسخر قرار گرفتند)، پس آنچه را مسخره می‌کردند، (از عذاب الهی) دامان کسانی از آن‌ها را که مسخره می‌کردند گرفت (و هلاک شدند).');
INSERT INTO "hussein_taji" VALUES(21,42,'21:42','(ای پیامبر!) بگو: «چه کسی شما را در شب و روز از (عذاب الله) رحمان نگاه می‌دارد؟ بلکه (حق این است که) آن‌ها از یاد پروردگارشان روی گردانند.');
INSERT INTO "hussein_taji" VALUES(21,43,'21:43','آیا آن‌ها معبودانی دارند که (می‌توانند) آن‌ها را در برابر (عذاب) ما (حفظ کنند و) باز دارند. (آن‌ها) نمی‌توانند خودشان را یاری دهند، و نه آن‌ها از (عذاب) ما محفوظ هستند.');
INSERT INTO "hussein_taji" VALUES(21,44,'21:44','بلکه، (ما) آن‌ها و پدران‌شان را (از نعمت‌های دنیوی) بهره‌مند ساختیم، تا اینکه عمرشان به درازا کشید (و مغرور شدند) آیا نمی‌بینند که ما پیوسته به سراغ (سر) زمین (کفر) می‌آییم، که آن را از اطراف (و دامنه‌هایش) می‌کاهیم؟! آیا آن‌ها پیروزند، (یا ما)؟!');
INSERT INTO "hussein_taji" VALUES(21,45,'21:45','(ای پیامبر!) بگو: «من تنها شما را به وحی هشدار می‌دهم» و کران هنگامی‌که هشدار می‌شوند، ندا را نمی‌شنوند.');
INSERT INTO "hussein_taji" VALUES(21,46,'21:46','اگر شمّه‌ای از عذاب پروردگارت به آن‌ها برسد، قطعاً خواهند گفت: «ای وای بر ما! بی‌گمان ما (همگی) ستمکار بودیم».');
INSERT INTO "hussein_taji" VALUES(21,47,'21:47','و (ما) در روز قیامت ترازوهای عدل را می‌نهیم، پس به هیچ کس، هیچ ستمی نمی‌شود، و اگر (عملی) به مقدار سنگینی یک دانۀ خردل باشد، آن را (به حساب) می‌آوریم، و حساب‌رسی ما کافی است.');
INSERT INTO "hussein_taji" VALUES(21,48,'21:48','و به راستی به موسی و هارون فرقان (= جدا کنندۀ حق از باطل) و روشنی و پندی برای پرهیزگاران، دادیم.');
INSERT INTO "hussein_taji" VALUES(21,49,'21:49','(همانا) کسانی‌که در نهان از پروردگارشان می‌ترسند، و آنان از قیامت بیم دارند.');
INSERT INTO "hussein_taji" VALUES(21,50,'21:50','و این (قرآن) پند مبارکی است که آن را نازل کردیم، آیا شما آن را انکار می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(21,51,'21:51','به راستی (ما) پیش از این به ابراهیم رشد (و راه یابی‌اش) را دادیم، و به (حال) او آگاه بودیم.');
INSERT INTO "hussein_taji" VALUES(21,52,'21:52','چون به پدرش و قومش گفت: «این مجسمه‌ها چیست که شما به (پرستش) آن‌ها دل نهاده‌اید؟!');
INSERT INTO "hussein_taji" VALUES(21,53,'21:53','گفتند: «ما پدران خود را پرستش کنندگان آن‌ها یافته‌ایم».');
INSERT INTO "hussein_taji" VALUES(21,54,'21:54','(ابراهیم) گفت: «به‌راستی شما و پدران‌تان، در گمراهی آشکار بوده‌اید».');
INSERT INTO "hussein_taji" VALUES(21,55,'21:55','گفتند: «آیا برای ما (سخن) حق را آورده‌ای، یا تو از بازی کنندگانی (و شوخی می‌کنی)؟!»');
INSERT INTO "hussein_taji" VALUES(21,56,'21:56','گفت: «بلکه پروردگار شما، پروردگار آسمان‌ها و زمین است، آن که آن‌ها را پدید آورد، و من بر این (سخن) از گواهانم.');
INSERT INTO "hussein_taji" VALUES(21,57,'21:57','و (آهسته گفت:) به الله سوگند، بعد از آن که پشت‌کنان روی بگردانید برای (نابودی) بت‌های شما تدبیری خواهم اندیشید.');
INSERT INTO "hussein_taji" VALUES(21,58,'21:58','پس (در غیاب بت‌پرستان) همۀ آن‌ها - جز (بت) بزرگ‌شان - را قطعه قطعه کرد، باشد که به سوی او باز گردند.');
INSERT INTO "hussein_taji" VALUES(21,59,'21:59','(و هنگامی‌که به بت خانه آمدند و دیدند) گفتند: «هر کسی‌که با معبودان ما چنین کرده است، مسلماً او از ستمکاران است».');
INSERT INTO "hussein_taji" VALUES(21,60,'21:60','(عده‌ای) گفتند: «شنیدیم جوانی که او را ابراهیم می‌گویند، آن‌ها (= بت‌ها) را (به بدی) یاد می‌کرد».');
INSERT INTO "hussein_taji" VALUES(21,61,'21:61','گفتند: «پس او را در برابر دیدگان مردم بیاورید، باشد که آن‌ها گواهی دهند».');
INSERT INTO "hussein_taji" VALUES(21,62,'21:62','(هنگامی‌که ابراهیم را حاضر کردند) گفتند: «ای ابراهیم! آیا تو این (کار) را با معبودان ما کرده‌ای؟!»');
INSERT INTO "hussein_taji" VALUES(21,63,'21:63','گفت: «بلکه این بزرگ‌شان این (کار) را کرده است، پس از آن‌ها بپرسید اگر سخن می‌گویند!».');
INSERT INTO "hussein_taji" VALUES(21,64,'21:64','آنگاه (آن‌ها) به خود باز گشتند، پس گفتند: «بی‌گمان شما خود ستمکار هستید».');
INSERT INTO "hussein_taji" VALUES(21,65,'21:65','سپس (با شرمندگی) سر به زیر انداختند (و گفتند:) «مسلماً تو می‌دانی که این‌ها سخن نمی‌گویند».');
INSERT INTO "hussein_taji" VALUES(21,66,'21:66','(ابراهیم) گفت: «آیا به جای الله چیزی را می‌پرستید که هیچ نفعی برای شما ندارد، و نه زیانی به شما می‌رساند؟!');
INSERT INTO "hussein_taji" VALUES(21,67,'21:67','اف بر شما، و بر آنچه به جای الله می‌پرستید، آیا نمی‌اندیشید؟!»');
INSERT INTO "hussein_taji" VALUES(21,68,'21:68','گفتند: «او را بسوزانید، و معبودان خود را یاری کنید، اگر کنندۀ (کاری) هستید».');
INSERT INTO "hussein_taji" VALUES(21,69,'21:69','(سرانجام آن‌ها او را به آتش انداختند، ولی ما) گفتیم: «ای آتش! بر ابراهیم سرد و سلامت باش».');
INSERT INTO "hussein_taji" VALUES(21,70,'21:70','و آن‌ها خواستند برای (نابودی) او نیرنگ (خطرناکی) ورزند، پس آن‌ها را زیانکارترین (مردم) قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(21,71,'21:71','و او و لوط را به سرزمینی که در آن برای جهانیان برکت داده‌ایم، نجات دادیم.');
INSERT INTO "hussein_taji" VALUES(21,72,'21:72','و اسحاق، و افزون (بر او نوه‌اش) یعقوب را به او بخشیدیم، و همۀ آنان را شایسته قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(21,73,'21:73','و (نیز) آن‌ها را پیشوایانی قرار دادیم که به‌فرمان ما (مردم را) هدایت می‌کردند، و انجام کارهای نیک و بر پاداشتن نماز و ادای زکات را به آن‌ها وحی کردیم، و آن‌ها (همه) عبادت‌گزار ما بودند.');
INSERT INTO "hussein_taji" VALUES(21,74,'21:74','و لوط را حکمت (= نبوت) و دانش دادیم، و او را از شهری که (مردمانش) اعمال زشت (و پلید) انجام می‌دادند، نجات دادیم، بی‌شک آن‌ها مردم بدِ فاسقی بودند.');
INSERT INTO "hussein_taji" VALUES(21,75,'21:75','و او را در رحمت خود داخل کردیم، بی‌گمان او از صالحان بود.');
INSERT INTO "hussein_taji" VALUES(21,76,'21:76','و (ای پیامبر!) نوح را (به یاد آور) هنگامی‌که پیش از آن (ما را) ندا داد، پس ما (دعایش) را برای او اجابت کردیم، آنگاه او و خاندانش را از اندوه بزرگ نجات دادیم.');
INSERT INTO "hussein_taji" VALUES(21,77,'21:77','و او را بر قومی‌که آیات ما را تکذیب کرده بودند؛ یاری دادیم، بی‌گمان آن‌ها قوم بدی بودند، پس همۀ آن‌ها را غرق کردیم.');
INSERT INTO "hussein_taji" VALUES(21,78,'21:78','و داود و سلیمان را (به یاد آور) هنگامی‌که در(بارۀ) کشتزاری که گوسفندان قوم، شبانه در آن چریده بودند، داوری می‌کردند، و ما بر حکم (و قضاوت) آن‌ها شاهد بودیم.');
INSERT INTO "hussein_taji" VALUES(21,79,'21:79','پس آن (شیوۀ قضاوت عادلانه و درست) را به سلیمان فهماندیم، و به هر یک از آنان حکمت (= داوری) و دانش عطا کردیم، و کوه‌ها و پرندگان را با داود مسخر ساختیم که (همراه او) تسبیح می‌گفتند، و ما این (همه) را انجام دادیم.');
INSERT INTO "hussein_taji" VALUES(21,80,'21:80','و ساختن زره را بخاطر شما به او آموختیم، تا شما را از (آسیب) جنگ‌هایتان محفوظ دارد، پس آیا شکر گزار هستید؟!.');
INSERT INTO "hussein_taji" VALUES(21,81,'21:81','و تند باد را مسخر (و مطیع) سلیمان کردیم، که به‌فرمان او به‌سوی سرزمینی که در آن برکت دادیم، حرکت می‌کرد، و ما بر هر چیز آگاهیم.');
INSERT INTO "hussein_taji" VALUES(21,82,'21:82','و (نیز) از شیاطین کسانی را (مسخر کردیم) که برایش غواصی می‌کردند، و کارهایی غیر از این (نیز) انجام می‌دادند، و (ما) حافظ آن‌ها بودیم.');
INSERT INTO "hussein_taji" VALUES(21,83,'21:83','و (ای پیامبر!) ایوب را (به یاد آور) هنگامی‌که پروردگارش را ندا داد: «رنج و بیماری به من رسیده است، و تو مهربان‌ترین مهربانانی».');
INSERT INTO "hussein_taji" VALUES(21,84,'21:84','پس (ما) دعای او را اجابت کردیم، و رنج و ناراحتی که داشت بر طرف ساختیم، و خانواده‌اش و (نیز) همانندشان را با آن‌ها به او بازگرداندیم، (تا) رحمتی از جانب ما، و پندی برای عبادت کنندگان باشد.');
INSERT INTO "hussein_taji" VALUES(21,85,'21:85','و اسماعیل و ادریس و ذوالکفل را (به یاد آور) که همگی از صابران بودند.');
INSERT INTO "hussein_taji" VALUES(21,86,'21:86','و (ما) آن‌ها را در رحمت خود وارد کردیم، بی‌گمان آن‌ها از صالحان بودند.');
INSERT INTO "hussein_taji" VALUES(21,87,'21:87','و ذوالنون (= یونس) را (به یاد آور) هنگامی‌که خشمگین (از میان قومش) رفت، پس چنین پنداشت که ما هرگز بر او تنگ نمی‌گیریم، پس (وقتی که در شکم ماهی فرو رفت) در تاریکی‌ها ندا داد که: «(پروردگارا!) هیچ معبودی بر حق جز تو نیست، تو منزهی! بی‌گمان من از ستمکاران بودم».');
INSERT INTO "hussein_taji" VALUES(21,88,'21:88','پس دعای او را اجابت کردیم، و از اندوه نجاتش دادیم، و این گونه مؤمنان را نجات می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(21,89,'21:89','و زکریا را (به یاد آور) هنگامی‌که پروردگارش را ندا داد: «پروردگارا! مرا تنها مگذار، و تو بهترین وارثانی».');
INSERT INTO "hussein_taji" VALUES(21,90,'21:90','پس دعای او را اجابت کردیم، و یحیی را به او بخشیدیم، و همسرش را (بعد از آن که نازا بود) برایش شایسته (و آماده بار داری) گردانیدیم. بی‌گمان آن‌ها همواره در کارهای خیر می‌شتافتند، و در حال بیم و امید ما را می‌خواندند، و پیوسته برای ما (خاشع و) فروتن بودند.');
INSERT INTO "hussein_taji" VALUES(21,91,'21:91','و (ای پیامبر! به یاد آور) زنی را که شرمگاهش را پاک نگاه داشت، پس (ما) از روح خود در آن دمیدیم [ گفته می‌شود که جبرئیل علیه السلام در گریبان حضرت مریم علیها السلام دمید، آنگاه به فرمان الله متعال حامله شد. (تفسیر قرطبی)]، و او و فرزندش را نشانه‌ای برای جهانیان قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(21,92,'21:92','بی‌تردید این است آیین شما که آیینی یگانه است، و من پروردگار شما هستم، پس مرا پرستش کنید.');
INSERT INTO "hussein_taji" VALUES(21,93,'21:93','و (گروهی از پیروان انبیا) در میان خود در امر (دین) شان فرقه فرقه شدند، همگی به‌سوی ما باز می‌گردند.');
INSERT INTO "hussein_taji" VALUES(21,94,'21:94','پس کسی‌که از کارهای شایسته را انجام دهد در حالی‌که مؤمن باشد، کوشش او ناسپاسی نخواهد شد، و بی‌گمان ما (اعمالش را) برایش می‌نویسیم.');
INSERT INTO "hussein_taji" VALUES(21,95,'21:95','و (مردم)هر شهری را که (بر اثر کفر و گناه) آن را هلاک کردیم، محال است که (به دنیا) بازگردند.');
INSERT INTO "hussein_taji" VALUES(21,96,'21:96','تا وقتی‌که (سد) یأجوج و مأجوج گشوده شود، و آن‌ها از هر (تبه و) بلندی شتابان سرازیر گردند.');
INSERT INTO "hussein_taji" VALUES(21,97,'21:97','و وعدۀ حق (= قیامت) نزدیک شود، پس در آن هنگام چشم‌های کسانی‌که کافر شدند (از وحشت) خیره ماند (گویند:) ای وای بر ما که از این (واقعه) در غفلت بودیم، بلکه ما ستمکار بودیم!');
INSERT INTO "hussein_taji" VALUES(21,98,'21:98','یقیناً شما و آنچه به جای الله می‌پرستید، هیزم جهنم هستید، شما در آن وارد خواهید شد.');
INSERT INTO "hussein_taji" VALUES(21,99,'21:99','اگر این‌ها (= بت‌ها) معبودانی (برحق) بودند، هرگز وارد آن نمی‌شدند، و (آن‌ها) همگی در آن جاودان خواهند ماند.');
INSERT INTO "hussein_taji" VALUES(21,100,'21:100','برای آن‌ها در آن (= جهنم) ناله‌های (دردناکی) است، و آن‌ها در آن (چیزی) نمی‌شنوند.');
INSERT INTO "hussein_taji" VALUES(21,101,'21:101','به‌راستی کسانی‌که پیشتر از سوی ما (وعدۀ) نیکویی برای آن‌ها مقرر شده است، آن‌ها از آن (= جهنم) دور نگاه داشته می‌شوند.');
INSERT INTO "hussein_taji" VALUES(21,102,'21:102','آن‌ها (حتی) صدایش را نمی‌شنوند، و در آنچه دل‌شان بخواهد جاودانه (متنعم) هستند.');
INSERT INTO "hussein_taji" VALUES(21,103,'21:103','وحشت بزرگ (قیامت) آن‌ها را اندوهگین نسازد، و فرشتگان به استقبال‌شان آیند (گویند:) «این همان روزتان است که به شما وعده داده می‌شد».');
INSERT INTO "hussein_taji" VALUES(21,104,'21:104','روزی‌که آسمان را چون طوماری نوشته شده درهم می‌پیچیم، همان‌گونه که نخستین آفرینش را آغاز کردیم، (دیگر بار) آن را باز می‌گردانیم، (این) وعده‌ای بر ما است، قطعاً آن را انجام خواهیم داد.');
INSERT INTO "hussein_taji" VALUES(21,105,'21:105','و به راستی بعد از ذکر (= تورات) در زبور نوشتیم که: «زمین را بندگان صالح من به ارث خواهند برد».');
INSERT INTO "hussein_taji" VALUES(21,106,'21:106','بی‌گمان در این (امر) برای گروه عبادت کنندگان پیام روشنی است.');
INSERT INTO "hussein_taji" VALUES(21,107,'21:107','و (ای پیامبر!) تو را جز رحمتی برای جهانیان نفرستادیم.');
INSERT INTO "hussein_taji" VALUES(21,108,'21:108','بگو: «تنها به سوی من وحی می‌شود که همانا معبود شما، معبود یگانه است، پس آیا شما تسلیم می‌شوید؟!».');
INSERT INTO "hussein_taji" VALUES(21,109,'21:109','پس اگر (باز) روی گردان شوند، بگو: «من شما را یکسان آگاه کردم (و اعلام خطر می‌کنم) و نمی‌دانم آنچه که به شما وعده داده می‌شود؛ نزدیک است یا دور؟!');
INSERT INTO "hussein_taji" VALUES(21,110,'21:110','بی‌شک او سخن آشکار را می‌داند، و (نیز) آنچه را (در دل خود) کتمان می‌کنید؛ می‌داند.');
INSERT INTO "hussein_taji" VALUES(21,111,'21:111','و من نمی‌دانم، شاید این (تأخیر عذاب) برای شما آزمایشی، و بهره‌مندی تا مدتی (معین) باشد».');
INSERT INTO "hussein_taji" VALUES(21,112,'21:112','(و پیامبر) گفت: «ای پروردگارا! به حق داوری کن، و پروردگار ما (الله) رحمان است، که از او استمداد می‌شود در برابر آنچه شما بیان می‌کنید».');
INSERT INTO "hussein_taji" VALUES(22,1,'22:1','ای مردم! از پروردگارتان بترسید، بدون شک زلزلۀ قیامت حادثۀ عظیمی است.');
INSERT INTO "hussein_taji" VALUES(22,2,'22:2','روزی‌که آن را می‌بینید، هر مادر شیر دهی، (کودک) شیر خوارش از یاد خود می‌برد، و هر (زن) بارداری، بار خود را بر زمین می‌گذارد، و مردم را مست می‌بینی، در حالی‌که مست نیستند، و لیکن عذاب الله شدید است.');
INSERT INTO "hussein_taji" VALUES(22,3,'22:3','و بعضی از مردم، بدون (هیچ) علم و دانشی، در(بارۀ) الله به مجادله بر می‌خیزند، و از هر شیطان سرکشی پیروی می‌کنند،');
INSERT INTO "hussein_taji" VALUES(22,4,'22:4','(در قضای الهی) بر او (= شیطان) نوشته شده که هر کس با او دوستی کند، مسلماً او گمراهش می‌کند، و او را به عذاب (آتش) سوزان (جهنم) می‌کشاند.');
INSERT INTO "hussein_taji" VALUES(22,5,'22:5','ای مردم! اگر از بر انگیخته شدن در شک هستید، پس (به این نکته دقت کنید:) همانا ما شما را از خاک آفریدیم، سپس از نطفه، و بعد از خون بسته شده، سپس از مضغه (= پاره گوشتی) شکل یافته و شکل نایافته، تا (قدرت و حکمت خود را) برای شما آشکار سازیم، و هر چه را که بخواهیم تا زمانی معین در رحم (مادران) نگه می‌داریم، آنگاه شما را به صورت طفل بیرون می‌آوریم، سپس تا به حد رشد (و بلوغ) خود برسید، و از شما کسی هست که می‌میرد، و از شما کسی هست که (آنقدر عمر می‌کند که) به نهایت فرتوتی (و پیری) باز برده می‌شود، تا آنکه پس از (آن همه) علم و دانش، چیزی نداند، و (به مثال دیگر توجه کنید:) زمین را خشکیده می‌بینی، پس هنگامی‌که آب (باران) بر آن فرو فرستیم، به حرکت در آید، و رشد کند، و از هر نوع گیاه زیبا (و خرم) برویاند.');
INSERT INTO "hussein_taji" VALUES(22,6,'22:6','این به خاطر آن است که (بدانید) الله حق است، و آنکه او مردگان را زنده می‌کند، و آن‌که او بر هر چیز تواناست.');
INSERT INTO "hussein_taji" VALUES(22,7,'22:7','و آنکه قیامت آمدنی است، شکی در آن نیست، و آنکه الله (همۀ) کسانی‌که در گور‌‌ها هستند، بر می‌انگیزد.');
INSERT INTO "hussein_taji" VALUES(22,8,'22:8','از مردم کسی است که بدون هیچ دانش و هیچ هدایت و کتاب روشنی بخشی دربارۀ الله مجادله می‌کند.');
INSERT INTO "hussein_taji" VALUES(22,9,'22:9','(تکبر کنان) پهلو (و گردن) خود را می‌پیچد، تا (مردم را) از راه الله گمراه سازد، برای او در دنیا رسوایی است، و روز قیامت آتش سوزان را به او می‌چشانیم.');
INSERT INTO "hussein_taji" VALUES(22,10,'22:10','(و به او می‌گوییم:) این (کیفر) در برابر چیزی است که دست‌هایت از پیش فرستاده است، و بی‌گمان الله (هرگز) به بندگان ستم نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(22,11,'22:11','و از (میان) مردم کسی است که الله را بر کناره (با تردید) می‌پرستد (و ایمانش بسیار ضعیف و سست است) پس اگر خیری به او برسد (دلش) به آن آرام می‌گیرد، و اگر بلایی (برای آزمایش) به او برسد، روی می‌گرداند (و به کفر باز می‌گردد)، در دنیا و آخرت زیان کرده است، این همان زیان آشکار است،');
INSERT INTO "hussein_taji" VALUES(22,12,'22:12','(او) به جای الله چیزی را می‌خواند که نه زیانی به او می‌رساند و نه سودی به او می‌بخشد، این همان گمراهی دور است.');
INSERT INTO "hussein_taji" VALUES(22,13,'22:13','(او) کسی را می‌خواند که زیانش نزدیکتر از نفعش است، چه بد (سر پرست و) یاوری است، و چه بد (همدم و) معاشری است.');
INSERT INTO "hussein_taji" VALUES(22,14,'22:14','بی‌گمان الله کسانی را که ایمان آوردند و کارهای شایسته انجام دادند، به باغ‌هایی (از بهشت) وارد می‌کند که نهرها از زیر (درختان) آن جاری است، مسلماً الله آنچه را می‌خواهد، انجام می‌دهد.');
INSERT INTO "hussein_taji" VALUES(22,15,'22:15','کسی‌که گمان می‌کند که الله او (= پیامبر) را در دنیا و آخرت یاری نخواهد کرد، (و بدین خاطر عصبانی است) پس ریسمانی به سقف (خانه‌اش) بیاویزید (و خود را حلق آویز کند) سپس آن را قطع کند، آنگاه بنگرد آیا (این) تدبیرش خشم او را از میان خواهد برد؟!');
INSERT INTO "hussein_taji" VALUES(22,16,'22:16','و این گونه (ما) آن (= قرآن) را (بصورت) آیات روشن نازل کردیم، و بی‌گمان الله هر کس را بخواهد هدایت می‌کند.');
INSERT INTO "hussein_taji" VALUES(22,17,'22:17','یقیناً کسانی‌که ایمان آوردند، و کسانی‌که یهودی شدند، و صابئان (= بی دینان) و نصاری و مجوس و کسانی‌که (به الله) شرک آوردند، همانا الله در میان آن‌ها روز قیامت داوری می‌کند، بی‌گمان الله بر هر چیز گواه است.');
INSERT INTO "hussein_taji" VALUES(22,18,'22:18','آیا ندیدی که هر کس در آسمان‌ها و هر کس که در زمین است، و خورشید و ماه و ستارگان و کوه‌ها و درختان و جنبندگان و بسیاری از مردم برای الله سجده می‌کنند، و بسیارند که (فرمان) عذاب بر آنان تحقق یافته است، و هر که را الله خوار سازد، پس کسی او را گرامی نخواهد داشت، بی‌گمان الله هر چه را بخواهد، می‌کند.');
INSERT INTO "hussein_taji" VALUES(22,19,'22:19','این دو (گروه) دشمنان یکدیگرند دربارۀ پروردگارشان (ستیز و) خصومت کردند، پس کسانی‌که کافر شدند لباس‌هایی از آتش برای آن‌ها بریده شده است، از بالای سرشان آب سوزان ریخته می‌شود.');
INSERT INTO "hussein_taji" VALUES(22,20,'22:20','که آنچه در درون‌شان است و (نیز) پوست‌های (شان) با آن گداخته می‌شود.');
INSERT INTO "hussein_taji" VALUES(22,21,'22:21','و برای (زدن) آن‌ها گرز‌های از آهن (داغ) است.');
INSERT INTO "hussein_taji" VALUES(22,22,'22:22','هر گاه بخواهند از (شدت) اندوه از آنجا (= دوزخ) خارج شوند، به آن باز گردانده می‌شوند، و (به آن‌ها گفته می‌شود:) عذاب سوزان (جهنم) را بچشید.');
INSERT INTO "hussein_taji" VALUES(22,23,'22:23','بی‌گمان الله کسانی را که ایمان آورده و کارهای شایسته انجام داده‌اند به باغ‌هایی (از بهشت) وارد می‌کند که از زیر (درختان) آن نهر‌ها جاری است، در آنجا به دستبند‌های از طلا و مروارید آراسته می‌شوند، و لباس‌شان در آنجا ابریشم است.');
INSERT INTO "hussein_taji" VALUES(22,24,'22:24','و به سوی گفتار پاکیزه هدایت می‌شوند، و به راه (الله) ستوده راهنمایی می‌گردند.');
INSERT INTO "hussein_taji" VALUES(22,25,'22:25','بی‌گمان کسانی‌که کافر شدند، و (مردم را) از راه الله باز می‌دارند و (همچنین از) مسجد الحرام، که آن را برای (همۀ) مردم یکسان قرار دادیم، (چه) مقیم در آنجا و یا وارد بر آن، و کسی‌که از روی ستم در آن‌جا کج‌روی (و انحراف از حق را) بخواهد، از عذاب دردناک به او می‌چشانیم.');
INSERT INTO "hussein_taji" VALUES(22,26,'22:26','و (به یاد آور) زمانی را که جای خانه (کعبه) را برای ابراهیم تعیین کردیم؛ (و گفتیم:) چیزی را شریک من قرار مده، و خانۀ مرا برای طواف کنندگان و قیام کنندگان و رکوع (و) سجود کنندگان پاک گردان.');
INSERT INTO "hussein_taji" VALUES(22,27,'22:27','و در (میان) مردم به حج ندا بده، تا پیاده و (سوار) بر هر (مرکب و) شتر لاغری از هر راه دوری به سوی تو بیایند.');
INSERT INTO "hussein_taji" VALUES(22,28,'22:28','تا شاهد منافع (گوناگون) خویش باشند، و در روز‌‌‌های معین (به هنگام قربانی) نام الله را بر چهارپایانی که به آن‌ها روزی داده‌ایم؛ یاد کنند، پس از (گوشت) آن بخورید، و بی‌نوای فقیر را (نیز) اطعام کنید.');
INSERT INTO "hussein_taji" VALUES(22,29,'22:29','سپس، باید آلودگی‌هایشان را بر طرف سازند، و به نذر‌‌های خود وفا کنند، و (برگرد) خانه‌های کهن‌سال (کعبه) طواف کنند.');
INSERT INTO "hussein_taji" VALUES(22,30,'22:30','(حکم) این است، و هر کس (مقدسات و) حرمت نهاده‌های الله را بزرگ دارد، پس این (کار) نزد پروردگارش برای او بهتر است. و چهارپایان برای شما حلال شده، مگر آنچه بر شما خوانده می‌شود، پس از پلیدی بت‌ها اجتناب کنید، و از گفتار باطل (و دروغ) دوری کنید.');
INSERT INTO "hussein_taji" VALUES(22,31,'22:31','در حالی‌که حق‌گرا و مخلص الله و شریک نیاورندگان به او باشید، و کسی‌که به الله شرک آورد، پس گویی از آسمان سقوط کرده است، آنگاه پرندگان (گوشت خوار) او را می‌ربایند، یا باد او را به جایی بسیار دور پرتاب می‌کند.');
INSERT INTO "hussein_taji" VALUES(22,32,'22:32','(حکم) این است، و کسی‌که شعائر الهی را بزرگ دارد، پس بی‌گمان این (کار) از پرهیزگاری دل‌هاست.');
INSERT INTO "hussein_taji" VALUES(22,33,'22:33','در آن (چهارپایان قربانی) تا زمان معین برای شما منافع (و بهره‌ها) است، پس قربانگاه آن‌ها خانۀ کهن‌سال (کعبه) است.');
INSERT INTO "hussein_taji" VALUES(22,34,'22:34','و برای هر امتی (رسم) قربانی دادیم، تا نام الله را (به هنگام ذبح) بر چهارپایانی که روزی‌شان کرده است؛ ببرند، پس معبود شما، معبود یگانه است، (همه) برای او تسلیم شوید، و به فروتنان بشارت ده.');
INSERT INTO "hussein_taji" VALUES(22,35,'22:35','(همان) کسانی‌که چون نام الله برده شود، دل‌های شان ترسان گردد، و شکیبا‌‌یان در برابر مصیبت‌هایی که به آن‌ها می‌رسد، و آن‌ها که نماز را بر پا می‌دارند، و از آنچه روزی‌شان کرده‌ایم؛ انفاق می‌کنند.');
INSERT INTO "hussein_taji" VALUES(22,36,'22:36','و (قربانی) شتران (فربه) را (در حج) برای شما از شعائر الهی قرار دادیم، در آن برای شما خیر (و برکت) است. پس (هنگام قربانی) در حالی‌که بر پا ایستاده‌اند؛ نام الله را بر آن‌ها ببرید، آنگاه چون پهلو‌‌‌‌‌هایشان (بر زمین) افتاد، از (گوشت) آن‌ها بخورید، و (فقیران) قانع (غیر سائل) و (فقیران) سائل را اطعام کنید. این گونه (ما) آن‌ها را برای شما رام کردیم؛ باشد که شما سپاس‌گزاری کنید.');
INSERT INTO "hussein_taji" VALUES(22,37,'22:37','گوشت‌های قربانی و خون‌های آن‌ها هرگز به الله نمی‌رسد، و لیکن پرهیزگاری شما به او می‌رسد، این گونه (الله) آن‌ها را برای شما مسخر کرده تا الله را به (شکرانۀ)آن که شما را هدایت کرده است؛ بزرگ بشمارید، و نیکو‌‌‌‌‌‌کاران را بشارت ده.');
INSERT INTO "hussein_taji" VALUES(22,38,'22:38','یقیناً الله از کسانی‌‌که ایمان آورده‌اند؛ دفاع می‌کند، بی‌گمان الله هیچ خیانت‌کارِ ناسپاسی را دوست ندارد.');
INSERT INTO "hussein_taji" VALUES(22,39,'22:39','به کسانی‌که با آنان جنگ می‌شود، اجازۀ (جهاد) داده شده است، از آن روی که مورد ستم قرار گرفته‌اند، و یقیناً الله بر یاری آن‌ها تواناست.');
INSERT INTO "hussein_taji" VALUES(22,40,'22:40','(همان) کسانی‌که به ناحق از دیار‌شان رانده شدند؛ جز اینکه می‌گفتند: «پروردگار ما الله است». و اگر الله بعضی از مردم را به (وسیلۀ) بعضی دیگر دفع نمی‌کرد، بی‌گمان دیرهای (راهبان) و کلیسا‌های (مسیحیان) و کنشت‌های (یهودیان) و مساجدی که نام الله در آن‌ها بسیار برده می‌شود؛ ویران می‌گردید، و مسلماً الله یاری می‌کند کسی‌که (دین) او را یاری دهد، بی‌گمان الله قوی پیروزمند است.');
INSERT INTO "hussein_taji" VALUES(22,41,'22:41','(همان) کسانی‌که اگر در زمین به آن‌ها قدرت (و حکومت) بخشیم، نماز را بر پا می‌دارند، و زکات را می‌دهند، و امر به معروف و نهی از منکر می‌کنند، و سرانجام کارها از آن الله است.');
INSERT INTO "hussein_taji" VALUES(22,42,'22:42','(ای پیامبر) اگر (این قوم بت پرست) تو را تکذیب می‌کنند، (غمگین نباش) به‌راستی پیش از آن‌ها قوم نوح و عاد و ثمود (پیامبرانشان را) تکذیب کردند.');
INSERT INTO "hussein_taji" VALUES(22,43,'22:43','و (همچنین) قوم ابراهیم و قوم لوط.');
INSERT INTO "hussein_taji" VALUES(22,44,'22:44','و اصحاب مدین (قوم شعیب)، و موسی (نیز) تکذیب شد، پس به کافران مهلت دادم آنگاه آن‌ها را فرو گرفتم، پس (بنگر) انکار (و عقوبت) من چگونه بود!');
INSERT INTO "hussein_taji" VALUES(22,45,'22:45','پس چه بسیار آبادی‌ها را در حالی‌که (مردمش) ستمگر بودند؛ نابودشان کردیم، پس (اینک) بر سقف‌هایش فروریخته (و ویران شده) است، و (چه بسیار) چاه (پر آب) که بی‌حاصل مانده، و (چه) قصر (های) بر افراشته استوار (که بی‌صاحب مانده است).');
INSERT INTO "hussein_taji" VALUES(22,46,'22:46','آیا آن‌ها در زمین سیر نکردند، تا دل‌هایی داشته باشند که با آن (حقیقت را) دریابند، یا گوش‌های که با آن (اخبار گذشتگان را) بشنوند؟! پس بی‌گمان چشم‌های (ظاهر) نا‌‌‌بینا نمی‌شود، و لیکن دل‌هایی که در سینه‌هاست؛ نابینا می‌شود.');
INSERT INTO "hussein_taji" VALUES(22,47,'22:47','و آن‌ها به شتاب از تو عذاب می‌طلبند، و الله هرگز و عده‌اش را خلاف نمی‌کند، و بی‌گمان یک روز نزد پروردگارت، مانند هزار سال از آنچه است که (شما) می‌شمارید.');
INSERT INTO "hussein_taji" VALUES(22,48,'22:48','و چه بسیار آبادی‌ها که به آن‌ها مهلت دادم؛ در حالی‌که (مردمش) ستم‌گر بودند، آنگاه آن‌ها را گرفتم، و باز گشت (همه) به سوی من است.');
INSERT INTO "hussein_taji" VALUES(22,49,'22:49','(ای پیامبر!) بگو: «ای مردم! جز این نیست که من برای شما بیم‌دهندۀ آشکاری هستم».');
INSERT INTO "hussein_taji" VALUES(22,50,'22:50','پس کسانی‌که ایمان آوردند و کار‌‌های شایسته انجام دادند، برای آن‌ها آمرزش و روزی نیک است.');
INSERT INTO "hussein_taji" VALUES(22,51,'22:51','و کسانی‌که مبارزه‌کنان در (انکار) آیات ما تلاش کردند، (و پند‌‌اشتند که ما را به ستوه می‌آورند) آنان اهل دوزخ هستند.');
INSERT INTO "hussein_taji" VALUES(22,52,'22:52','و (ما) هیچ رسول و پیامبری را پیش از تو نفرستادیم؛ مگر اینکه چون (آیات ما را) تلاوت می‌کرد، شیطان در تلاوت او (چیزی) القاء می‌کرد، آنگاه الله آنچه شیطان القاء می‌کند؛ از میان می‌برد، سپس الله آیاتش را استوار (و محکم) می‌دارد، و الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(22,53,'22:53','تا (الله) آنچه را که شیطان القاء می‌کند، آزمونی قرار دهد برای کسانی‌که در دل‌هایشان بیماری است، و آن‌هایی که دل‌هایشان سخت است، و بی‌گمان ستمکاران در مخالفت (و دشمنی) دور (و دراز) هستند.');
INSERT INTO "hussein_taji" VALUES(22,54,'22:54','و تا کسانی‌که به آنان دانش داده شده بدانند که مسلماً آن (قرآن) از سوی پروردگارت حق است، پس به آن ایمان بیاورند، آنگاه دل‌هایشان برای آن (حق) خاضع گردد، و یقیناً الله کسانی را که ایمان آوردند به راه راست هدایت می‌کند.');
INSERT INTO "hussein_taji" VALUES(22,55,'22:55','و کسانی‌که کافر شدند همواره در آن (= قرآن) شک دارند، تا آنکه ناگهان قیامت فرا رسد یا عذاب روز نحس بر آن‌ها (فرود) آید.');
INSERT INTO "hussein_taji" VALUES(22,56,'22:56','فرمانروایی در آن روز از آن الله است، میان آن‌ها داوری می‌کند، پس کسانی‌که ایمان آورده، و کارهای شایسته انجام داده‌اند؛ در باغ‌های پر نعمت (بهشت) هستند.');
INSERT INTO "hussein_taji" VALUES(22,57,'22:57','و کسانی‌که کافر شدند و آیات ما را تکذیب کردند، برای آن‌ها عذاب خوار کننده‌ای است.');
INSERT INTO "hussein_taji" VALUES(22,58,'22:58','و کسانی‌که در راه الله هجرت کردند، سپس کشته شدند یا (به مرگ طبیعی) مردند، مسلماً الله به آن‌ها روزی نیکویی می‌دهد، یقیناً الله بهترین روزی دهندگان است.');
INSERT INTO "hussein_taji" VALUES(22,59,'22:59','بدون شک آن‌ها را به جایگاهی وارد می‌کند که از آن خشنود باشند، و بی‌گمان الله دانای برد بار است.');
INSERT INTO "hussein_taji" VALUES(22,60,'22:60','(احکام) این است، و هر کس به همان مقدار که به او ستم شده مجازات کند، سپس بر او ستم کنند، بدون شک الله او را یاری خواهد کرد، یقیناً الله بخشندۀ آمرزنده است.');
INSERT INTO "hussein_taji" VALUES(22,61,'22:61','این (نصرت مظلوم‌) از آن روست که الله (قادر است چیزى را بر دیگرى غالب کند چنان که‌) شب را در روز داخل مى‌کند (و ظلمت مغلوب مى‌شود) و روز را در شب در مى‌آورد، و بی‌گمان الله شنوای بیناست.');
INSERT INTO "hussein_taji" VALUES(22,62,'22:62','این بخاطر آن است که الله حق است، و آنچه جز او می‌خوانند باطل است، و به‌راستی که الله بلند مرتبۀ بزرگ است.');
INSERT INTO "hussein_taji" VALUES(22,63,'22:63','آیا ندیدی که الله از آسمان آبی فرو فرستاد، سپس زمین (بر اثر آن) سبز و خرم می‌گردد؟ بی‌گمان الله باریک بین آگاه است.');
INSERT INTO "hussein_taji" VALUES(22,64,'22:64','آنچه در آسمان‌ها و آنچه در زمین است از آن اوست، و بدون شک الله بی‌نیاز ستوده است.');
INSERT INTO "hussein_taji" VALUES(22,65,'22:65','آیا ندیدی که الله آنچه را در زمین است مسخر شما کرده است، و (نیز) کشتی‌ها به‌فرمان او در دریا روان است، و آسمان را از آن که بر زمین افتد - جز به فرمانش - نگه می‌دارد، بی‌گمان الله (نسبت) به مردم رؤوف مهربان است.');
INSERT INTO "hussein_taji" VALUES(22,66,'22:66','و او کسی است که شما را زنده کرد، سپس شما را می‌میراند، آنگاه بار دیگر زنده می‌کند، به‌راستی که انسان بسیار ناسپاس است.');
INSERT INTO "hussein_taji" VALUES(22,67,'22:67','برای هر امتی (روش عبادت و) آیینی مقرر کردیم که آنان به آن عمل کنند، پس نباید در این امر با تو به نزاع بر خیزند، به‌سوی پروردگارت دعوت کن، بی‌گمان تو بر هدایت مستقیم قرار داری.');
INSERT INTO "hussein_taji" VALUES(22,68,'22:68','و اگر با تو مجادله کردند، پس بگو: «الله به آنچه انجام می‌دهید؛ داناتر است،');
INSERT INTO "hussein_taji" VALUES(22,69,'22:69','الله روز قیامت میان شما در آنچه در آن اختلاف می‌کردید، داوری می‌کند».');
INSERT INTO "hussein_taji" VALUES(22,70,'22:70','آیا ندانسته‌ای که الله آنچه را در آسمان و زمین است می‌داند؟! بی‌گمان (همۀ) این‌ها در کتابی (ثبت) است، مسلماً این بر الله آسان است.');
INSERT INTO "hussein_taji" VALUES(22,71,'22:71','و (آن‌ها) به جای الله چیزی را می‌پرستند که (او) هیچ دلیلی بر آن نازل نکرده است، و چیزی را که علم به آن ندارند، و برای ستمگران هیچ یاوری نیست.');
INSERT INTO "hussein_taji" VALUES(22,72,'22:72','و هنگامی‌که آیات روشن ما بر آنان خوانده می‌شود، در چهرۀ کسانی‌که کافر شدند (آثار) ناخوشی (و انکار) را می‌شناسی که نزدیک است بر کسانی‌که آیات ما را بر آنان می‌خواند، حمله ور شوند، بگو: «آیا شما را به بدتر از این خبر دهم؟ (همان) آتش (جهنم) است که الله به کسانی‌که کافر شدند؛ وعده داده است، و بد سرانجامی است».');
INSERT INTO "hussein_taji" VALUES(22,73,'22:73','ای مردم! مثلی زده شده است، پس به آن گوش فرا دهید: بی‌گمان کسانی را که به جای الله (به خدایی) می‌خوانید؛ هرگز نمی‌توانند مگسی را بیافرینند، اگر چه (همگی) برای این (کار) گرد آیند، و اگر مگس چیزی را از آن‌ها برباید، نمی‌توانند از آن باز پس گیرند، (آری) طالب و مطلوب (= عابد و معبود) ناتوانند.');
INSERT INTO "hussein_taji" VALUES(22,74,'22:74','(آن‌ها) الله را چنانکه سزاوار اوست نشناختند، یقیناً الله قوی پیروزمند است.');
INSERT INTO "hussein_taji" VALUES(22,75,'22:75','الله از فرشتگان رسولانی را بر می‌گزیند، و (نیز) از مردم، بی‌گمان الله شنوای بیناست.');
INSERT INTO "hussein_taji" VALUES(22,76,'22:76','آنچه را در پیش روی آن‌ها، و آنچه پشت سر آن‌هاست می‌داند، و (همۀ) کارها به سوی الله باز گردانده می‌شود.');
INSERT INTO "hussein_taji" VALUES(22,77,'22:77','ای کسانی‌که ایمان آورده‌اید! رکوع کنید، و سجده کنید، و پروردگارتان را بپرستید، و کار نیک انجام دهید باشد که رستگار شوید.');
INSERT INTO "hussein_taji" VALUES(22,78,'22:78','و در راه الله جهاد کنید، چنانکه سزاوار جهاد (در راه) او است، او شما را بر گزید، و در دین برای شما هیچ سختی (و تنگنایی) قرار نداد، (همان) آیین پدرتان ابراهیم است، او (= الله) پیش از این (در کتب سابقه) و در این (قرآن نیز) شما را مسلمان نامید، تا پیامبر گواه بر شما باشد و شما گواهان بر مردم باشید، پس نماز را بر پا دارید، و زکات را بدهید، و به الله تمسک جویید، که او مولای شماست، چه خوب مولا، و چه خوب یاوری است.');
INSERT INTO "hussein_taji" VALUES(23,1,'23:1','به راستی مؤمنان رستگار شدند.');
INSERT INTO "hussein_taji" VALUES(23,2,'23:2','همان کسانی‌که در نمازشان خاشع (و فروتن) هستند.');
INSERT INTO "hussein_taji" VALUES(23,3,'23:3','و کسانی‌که از لغو (و کارهای بیهوده) روی گردانند.');
INSERT INTO "hussein_taji" VALUES(23,4,'23:4','و کسانی‌که زکات را انجام می‌دهند.');
INSERT INTO "hussein_taji" VALUES(23,5,'23:5','و آن‌ها که شرمگاهشان را حفظ می‌کنند.');
INSERT INTO "hussein_taji" VALUES(23,6,'23:6','جز بر همسرانشان یا (بر) کنیزانشان، پس بی‌گمان اینان (در بهره‌گیری از آنان) ملامت نمی‌شوند.');
INSERT INTO "hussein_taji" VALUES(23,7,'23:7','پس کسی‌که فراتر از این بخواهد، آنانند که تجاوز گرند.');
INSERT INTO "hussein_taji" VALUES(23,8,'23:8','و کسانی‌که امانت‌هایشان و عهد خود را رعایت می‌کنند.');
INSERT INTO "hussein_taji" VALUES(23,9,'23:9','و کسانی‌که بر نمازهایشان مواظبت می‌نمایند.');
INSERT INTO "hussein_taji" VALUES(23,10,'23:10','(آری) اینانند که وارثند.');
INSERT INTO "hussein_taji" VALUES(23,11,'23:11','کسانی‌که (بهشت) فردوس را ارث می‌برند، و جاودانه در آن خواهند ماند.');
INSERT INTO "hussein_taji" VALUES(23,12,'23:12','و به راستی انسان را از چکیده‌ای از گِل آفریدیم.');
INSERT INTO "hussein_taji" VALUES(23,13,'23:13','سپس او را از نطفه‌ای در قرارگاهی استوار (= رحم) قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(23,14,'23:14','سپس نطفه را (به صورت) علقه (= خون بسته) گرداندیم، آنگاه علقه را (به صورت) مضغه (= پاره گوشتی) درآوردیم، آنگاه مضغه را (به صورت) استخوان‌هایی گردانیدیم، پس بر استخوان‌ها گوشت پوشاندیم، سپس آن را آفرینشی دیگر بخشیدیم، پس (پر برکت و) بزرگوار است، الله که بهترین آفرینندگان است.');
INSERT INTO "hussein_taji" VALUES(23,15,'23:15','سپس مسلماً شما بعد از این خواهید مرد.');
INSERT INTO "hussein_taji" VALUES(23,16,'23:16','آنگاه یقیناً شما روز قیامت بر انگیخته می‌شوید.');
INSERT INTO "hussein_taji" VALUES(23,17,'23:17','و به‌راستی بر فرازتان هفت آسمان آفریدیم، و (ما هرگز) از آفرینش (خود) غافل نبوده‌ایم.');
INSERT INTO "hussein_taji" VALUES(23,18,'23:18','و از آسمان آبی به‌اندازه‌ای معین نازل کردیم، پس آن را در زمین جای دادیم، و بی‌گمان ما بر از بین بردن آن کاملاً قادر هستیم.');
INSERT INTO "hussein_taji" VALUES(23,19,'23:19','پس بوسیلۀ آن باغ‌هایی از درختان خرما و انگور برای شما پدید آوردیم، شما را در آن (باغ‌ها) میوه‌های بسیار است، و از آن می‌خورید.');
INSERT INTO "hussein_taji" VALUES(23,20,'23:20','و (نیز) درختی را که از طور سینا بیرون می‌آید (= درخت زیتون) که از آن روغن و (نان) خورش برای خورندگان فراهم می‌شود.');
INSERT INTO "hussein_taji" VALUES(23,21,'23:21','و بی‌گمان برای شما در چهارپایان عبرتی است، از آنچه درون‌شان است (= از شیر آن‌ها) شما را سیراب می‌کنیم؛ و برای شما در آن‌ها منافع فراوان است، و از (گوشت) آن‌ها می‌خورید.');
INSERT INTO "hussein_taji" VALUES(23,22,'23:22','و بر آن‌ها و بر کشتی‌ها سوار می‌شوید.');
INSERT INTO "hussein_taji" VALUES(23,23,'23:23','و به راستی (ما) نوح را به سوی قومش فرستادیم، پس (او به آن‌ها) گفت: «ای قوم من! الله را بپرستید، که جز او معبودی برای شما نیست، آیا پرهیز نمی‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(23,24,'23:24','پس اشراف قومش، (آن) کسانی‌که کافر بودند؛ گفتند: «این (مرد) جز بشری همچون شما نیست، که می‌خواهد بر شما برتری جوید، اگر الله می‌خواست، همانا فرشتگانی را نازل می‌کرد، ما (هرگز) چنین چیزی را در (میان) نیاکان خود نشنیده‌ایم.');
INSERT INTO "hussein_taji" VALUES(23,25,'23:25','او مرد دیوانه‌ای بیش نیست، پس تا مدتی دربارۀ او صبر کنید،');
INSERT INTO "hussein_taji" VALUES(23,26,'23:26','(نوح) گفت: «پروردگارا! مرا در برابر تکذیب آن‌ها یاری کن»');
INSERT INTO "hussein_taji" VALUES(23,27,'23:27','پس به او وحی کردیدم که: «به دیدگان ما و به وحی ما کشتی را بساز، پس هنگامی‌که فرمان ما فرا رسد، و آب از تنور بجوشد، آنگاه از هر نوع (حیوان) یک جفت (نر و ماده) در کشتی سوار کن و (نیز) خانواده خود را (سوار کن) جز کسی از آنان را که وعدۀ (هلاک) از پیش بر او مقرر شده است. و در(بارۀ) کسانی‌که ستم کردند، با من سخن مگو، که آنان غرق شدنی هستند».');
INSERT INTO "hussein_taji" VALUES(23,28,'23:28','هنگامی‌که تو و همراهانت بر کشتی سوار شدید؛ بگو: «ستایش برای الله است که ما را از قوم ستمگر نجات داد».');
INSERT INTO "hussein_taji" VALUES(23,29,'23:29','و (نیز) بگو: «پروردگارا! ما را در منزلگاهی پر برکت فرود آور، و تو بهترین فرود آورندگانی».');
INSERT INTO "hussein_taji" VALUES(23,30,'23:30','بی‌گمان در این (ماجرا) نشانه‌هایی است، و (ما) به‌راستی آزمایش می‌کنیم.');
INSERT INTO "hussein_taji" VALUES(23,31,'23:31','سپس قوم دیگری را بعد از آن‌ها به وجود آوردیم.');
INSERT INTO "hussein_taji" VALUES(23,32,'23:32','پس از خودشان پیامبری در (میان) آنان فرستادیم که: «الله را بپرستید، که جز او معبودی برای شما نیست، آیا پرهیز نمی‌کنید؟!»');
INSERT INTO "hussein_taji" VALUES(23,33,'23:33','و گروهی از اشراف قومش که کافر بودند، و دیدار آخرت را تکذیب می‌کردند، و در زندگی دنیا به آن‌ها نعمت و آسایش داده بودیم، گفتند: «این (مرد) بشری همچون شما است، از آنچه که (شما) می‌خورید، می‌خورد، و از آنچه که شما می‌نوشید، می‌نوشد.');
INSERT INTO "hussein_taji" VALUES(23,34,'23:34','و اگر از بشری همچون خود اطاعت کردید، یقیناً شما زیان‌کار خواهید شد.');
INSERT INTO "hussein_taji" VALUES(23,35,'23:35','آیا (او) به شما وعده می‌دهد: که همانا شما چون مردید و خاک و استخوان‌هایی (پوسیده) شدید، (بار دیگر از گور) بیرون آورده می‌شوید؟!');
INSERT INTO "hussein_taji" VALUES(23,36,'23:36','هیهات، هیهات (= دور است، دور است) آنچه به شما وعده داده می‌شود.');
INSERT INTO "hussein_taji" VALUES(23,37,'23:37','جز زندگی دنیوی ما چیزی نیست، می‌میریم و زنده می‌شویم، و هرگز برانگیخته نخواهیم شد.');
INSERT INTO "hussein_taji" VALUES(23,38,'23:38','او جز مردی نیست که بر الله دروغ بسته است، و ما به او ایمان نمی‌آوریم».');
INSERT INTO "hussein_taji" VALUES(23,39,'23:39','(پیامبرشان) گفت: «پروردگارا! مرا در برابر تکذیب آن‌ها یاری کن».');
INSERT INTO "hussein_taji" VALUES(23,40,'23:40','(الله) فرمود: «به‌زودی (از کار خود) پشیمان خواهند شد»');
INSERT INTO "hussein_taji" VALUES(23,41,'23:41','پس صیحه (= بانگ مرگبار) آن‌ها را به حق فرو گرفت، آنگاه آن‌ها را (همچون) خاشاکی (بر سیلاب) قرار دادیم، پس قوم ستمکار (از رحمت الله) دور (و نابود) باد.');
INSERT INTO "hussein_taji" VALUES(23,42,'23:42','سپس اقوام دیگری را بعد از آن‌ها پدید آوردیم.');
INSERT INTO "hussein_taji" VALUES(23,43,'23:43','هیچ امتی از اجل خود پیشی نمی‌گیرد، و نه تأخیر می‌کند.');
INSERT INTO "hussein_taji" VALUES(23,44,'23:44','سپس پیامبران خود را پی در پی فرستادیم، هر زمان پیامبری برای (هدایت) امتی می‌آمد، او را تکذیب می‌کردند، پس (ما نیز) آن‌ها (= امت‌های کافر) را یکی پس از دیگری هلاک کردیم، و آن‌ها را افسانه‌هایی (برای آیندگان) قرار دادیم. پس دور باد قومی که ایمان نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(23,45,'23:45','سپس موسی و برادرش هارون را با آیات خود، و دلایل آشکار فرستادیم،');
INSERT INTO "hussein_taji" VALUES(23,46,'23:46','به سوی فرعون و اشراف (قوم) او، پس آن‌ها تکبر کردند، و مردمی (سرکش و) برتری جوی بودند.');
INSERT INTO "hussein_taji" VALUES(23,47,'23:47','پس گفتند: «آیا (ما) به دو انسان مانند خود ایمان بیاوریم، در حالی‌که قوم آن‌ها بردگان (و زیر دستان) ما هستند؟!»');
INSERT INTO "hussein_taji" VALUES(23,48,'23:48','پس (آن‌ها) آن دو (پیامبر) را تکذیب کردند، سرانجام (همگی) از هلاک شدگان گشتند.');
INSERT INTO "hussein_taji" VALUES(23,49,'23:49','و به راستی به موسی کتاب (تورات) دادیم، باشد که آنان (= بنی اسرائیل) هدایت شوند.');
INSERT INTO "hussein_taji" VALUES(23,50,'23:50','و پسر مریم و مادرش را آیتی قرار دادیم، و آن‌ها را در مکانی بلند و هموار که دارای امنیت و آب جاری بود، جای دادیم.');
INSERT INTO "hussein_taji" VALUES(23,51,'23:51','ای پیامبران! از (غذاهای) پاکیزه بخورید، و کار شایسته انجام دهید، بی‌شک من به آنچه انجام می‌دهید؛ آگاهم.');
INSERT INTO "hussein_taji" VALUES(23,52,'23:52','و بی‌گمان این امت شما، امت واحدی است، و من پروردگار شما هستم، پس از من بترسید.');
INSERT INTO "hussein_taji" VALUES(23,53,'23:53','پس آن‌ها در میان خود در کار (دین) شان فرقه فرقه و پراکنده شدند، و هر گروه به آنچه نزد خود دارند؛ خوشحال‌اند.');
INSERT INTO "hussein_taji" VALUES(23,54,'23:54','پس (ای پیامبر!) آن‌ها را تا مدتی در جهل و غفلت‌شان بگذار.');
INSERT INTO "hussein_taji" VALUES(23,55,'23:55','آیا گمان می‌کنند که آن مال و فرزندان که با آن‌ها آنان را یاری می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(23,56,'23:56','برای این است که شتابان خیرات (و نیکی‌ها) به آن‌ها برسانیم؟! (چنین نیست) بلکه آن‌ها (حقیقت را) در‌نمی‌یابند.');
INSERT INTO "hussein_taji" VALUES(23,57,'23:57','بی‌گمان کسانی‌که از خوف پروردگارشان بیمناک‌اند.');
INSERT INTO "hussein_taji" VALUES(23,58,'23:58','و کسانی‌که به آیات پروردگارشان ایمان می‌آورند.');
INSERT INTO "hussein_taji" VALUES(23,59,'23:59','و کسانی‌که به پروردگارشان شرک نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(23,60,'23:60','و کسانی‌که (در راه الله) آنچه را باید بدهند، می‌دهند، و (با این حال) دل‌هایشان بیمناک است، که بی‌گمان آنان به‌سوی پروردگار‌شان باز می‌گردند.');
INSERT INTO "hussein_taji" VALUES(23,61,'23:61','(آری) اینانند که در نیکی‌ها شتاب می‌کنند، و در انجام آن (از دیگران) پیشی می‌گیرند.');
INSERT INTO "hussein_taji" VALUES(23,62,'23:62','(ما) هیچ کس را جز به‌اندازۀ توانش تکلیف نمی‌کنیم، و نزد ما کتابی است که به حق سخن می‌گوید، و به آن‌ها هیچ ستمی نمی‌شود.');
INSERT INTO "hussein_taji" VALUES(23,63,'23:63','بلکه دل‌های آن‌ها از این (کتاب) در غفلت است، و آن‌ها جز این؛ اعمال (زشت) دیگری (نیز) دارند که پیوسته آن را انجام می‌دهند.');
INSERT INTO "hussein_taji" VALUES(23,64,'23:64','تا زمانی که متنعمان آن‌ها را به عذاب گرفتار کنیم، در این هنگام آن‌ها ناله و فریاد سر می‌دهند.');
INSERT INTO "hussein_taji" VALUES(23,65,'23:65','(به آن‌ها گفته می‌شود:) امروز فریاد نکنید، یقیناً شما از سوی ما یاری نخواهید شد.');
INSERT INTO "hussein_taji" VALUES(23,66,'23:66','به‌راستی آیات من پیوسته بر شما خوانده می‌شد، پس شما (از حق اعراض می‌کردید و) به عقب باز می‌گشتید.');
INSERT INTO "hussein_taji" VALUES(23,67,'23:67','در حالی‌که در برابر آن تکبر می‌کردید، (و در مجالس) شبانۀ (خود) به بدگویی می‌پرداختید (و افسانه سرایی می‌کردید)');
INSERT INTO "hussein_taji" VALUES(23,68,'23:68','آیا (آن‌ها) در این گفتار نیندیشیدند، یا (اینکه) چیزی برای آنان آمده که برای نیاکان نخستین شان نیامده است؟!');
INSERT INTO "hussein_taji" VALUES(23,69,'23:69','یا (اینکه) پیامبر‌‌شان را نشناخته‌اند، پس او را انکار می‌کنند؟!');
INSERT INTO "hussein_taji" VALUES(23,70,'23:70','یا می‌گویند او جنون دارد؟ (چنین نیست) بلکه (او) حق را برای آن‌ها آورده است، و بیشترشان از حق کراهت دارند.');
INSERT INTO "hussein_taji" VALUES(23,71,'23:71','و اگر حق از هوس‌های آن‌ها پیروی کند، آسمان‌ها و زمین و هرکه در آن‌هاست، تباه شوند، بلکه (ما) برای آن‌ها پند و اندرز‌شان آورده‌ایم، پس آن‌ها از پندشان روی گردانند.');
INSERT INTO "hussein_taji" VALUES(23,72,'23:72','یا (اینکه) تو از آن‌ها مزدی طلب می‌کنی؟ پس پاداش پروردگارت بهتر است، و او بهترین روزی دهندگان است.');
INSERT INTO "hussein_taji" VALUES(23,73,'23:73','و یقیناً تو آن‌ها را به راه راست دعوت می‌کنی.');
INSERT INTO "hussein_taji" VALUES(23,74,'23:74','و بی‌گمان کسانی‌که به آخرت ایمان ندارند از (این) راه (راست) منحرف هستند.');
INSERT INTO "hussein_taji" VALUES(23,75,'23:75','و اگر به آن‌ها رحم کنیم و بلا و رنجی را که بدان گرفتارند، از آن‌ها بر‌طرف سازیم، در طغیان خود لجاجت (و پا‌‌‌فشاری) می‌کنند (و) سرگردان می‌مانند.');
INSERT INTO "hussein_taji" VALUES(23,76,'23:76','و به راستی (ما) آن‌ها را به عذاب (و بلا) گرفتار ساختیم، (تا بیدار شوند) پس برای پروردگارشان فروتنی ننمودند، و (به درگاهش تضرع و) زاری نکردند.');
INSERT INTO "hussein_taji" VALUES(23,77,'23:77','تا زمانی‌که دری از عذاب سخت به روی آن‌ها گشودیم، آنگاه آن‌ها در آن به‌کلی ناامید گردند.');
INSERT INTO "hussein_taji" VALUES(23,78,'23:78','و او کسی است که برای شما گوش و چشم و دل پدید آورد، چه اندک سپاس می‌گزارید.');
INSERT INTO "hussein_taji" VALUES(23,79,'23:79','و او کسی است که شما را در زمین (آفرید و) پراکنده نمود، و به‌سوی او گرد آورده می‌شوید.');
INSERT INTO "hussein_taji" VALUES(23,80,'23:80','و او کسی است که زنده می‌کند و می‌میراند، و رفت و آمد شب و روز از آن اوست، آیا نمی‌اندیشید؟!');
INSERT INTO "hussein_taji" VALUES(23,81,'23:81','(چنین نکردند) بلکه آن‌ها (نیز) مثل آنچه پیشینیان گفته بودند، گفتند.');
INSERT INTO "hussein_taji" VALUES(23,82,'23:82','گفتند: «آیا هنگامی‌که مردیم و خاک و استخوان‌های (پوسیده) شدیم، آیا (دوباره) برانگیخته می‌شویم،');
INSERT INTO "hussein_taji" VALUES(23,83,'23:83','به‌راستی این وعده به ما و نیاکان ما از قبل داده شده، این (چیزی) جز افسانه‌های پیشینیان نیست».');
INSERT INTO "hussein_taji" VALUES(23,84,'23:84','(ای پیامبر!) بگو: «اگر می‌دانید، زمین و هرکه در آن است از آنِ کیست؟!».');
INSERT INTO "hussein_taji" VALUES(23,85,'23:85','به‌زودی خواهند گفت: «(همه) از آن الله است» بگو: «آیا پند نمی‌گیرید؟!».');
INSERT INTO "hussein_taji" VALUES(23,86,'23:86','بگو: «چه کسی پروردگار آسمان‌های هفتگانه، و پروردگار عرش عظیم است؟!»');
INSERT INTO "hussein_taji" VALUES(23,87,'23:87','خواهند گفت: «(همه) از آنِ الله است» بگو: «آیا (از الله) نمی‌ترسید؟!».');
INSERT INTO "hussein_taji" VALUES(23,88,'23:88','بگو: «اگر می‌دانید، چه کسی فرمانروایی همۀ موجودات در دست دارد، و او پناه می‌دهد، و کسی در برابر او پناه داده نمی‌شود؟!');
INSERT INTO "hussein_taji" VALUES(23,89,'23:89','خواهند گفت: «از آن الله است» بگو: «پس چگونه جادو می‌شوید؟!»');
INSERT INTO "hussein_taji" VALUES(23,90,'23:90','(چنین نیست) بلکه حق را برای آن‌ها آوردیم، و بی‌گمان آنان دروغگو هستند.');
INSERT INTO "hussein_taji" VALUES(23,91,'23:91','الله هرگز فرزندی بر نگزیده است، و هیچ معبود (دیگری) با او نیست، (اگر چنین بود) آنگاه هر معبودی آنچه را که آفریده بود؛ می‌برد، و مسلماً بعضی بر بعضی دیگر برتری می‌جستند، الله منزه (و پاک) است از آنچه آن‌ها توصیف می‌کنند.');
INSERT INTO "hussein_taji" VALUES(23,92,'23:92','دانای نهان و آشکار است، پس برتر است از آنچه شریک او می‌سازند.');
INSERT INTO "hussein_taji" VALUES(23,93,'23:93','(ای پیامبر!) بگو: «پروردگارا! اگر آنچه را که به آن‌ها وعده داده می‌شود، به من نشان دهی.');
INSERT INTO "hussein_taji" VALUES(23,94,'23:94','پروردگارا! پس مرا در (زمرۀ) قوم ستمگران قرار مده».');
INSERT INTO "hussein_taji" VALUES(23,95,'23:95','و یقیناً ما تواناییم که آنچه را به آن‌ها وعده می‌دهیم؛ به تو نشان دهیم.');
INSERT INTO "hussein_taji" VALUES(23,96,'23:96','بدی را به روشی که آن بهتر است، دفع کن، ما به آنچه توصیف می‌کنند؛ داناتریم.');
INSERT INTO "hussein_taji" VALUES(23,97,'23:97','و بگو: «پروردگارا! من از وسوسه‌های شیطان به تو پناه می‌برم،');
INSERT INTO "hussein_taji" VALUES(23,98,'23:98','و پروردگارا! از اینکه آنان نزد من حاضر شوند، به تو پناه می‌برم».');
INSERT INTO "hussein_taji" VALUES(23,99,'23:99','(کافران پیوسته به راه و روش خود ادامه می‌دهند) تا زمانی‌که مرگ یکی از آن‌ها فرا رسد، گوید: «پروردگارا! مرا (به دنیا) باز گردان،');
INSERT INTO "hussein_taji" VALUES(23,100,'23:100','باشد که در آنچه (در دنیا) ترک (و کوتاهی) کرده‌ام، کار شایسته‌ای انجام دهم». هرگز چنین نیست، بی‌گمان این سخنی است که او به زبان می‌گوید، (و به فرض محال اگر باز گردد، به کارهای گذشته‌اش ادامه می‌دهد) و پشت سر آن‌ها برزخی است تا روزی‌که برانگیخته شوند.');
INSERT INTO "hussein_taji" VALUES(23,101,'23:101','پس هنگامی‌که در صور دمیده شود، در آن روز هیچ گونه پیوندهای خویشاوندی در میان آن‌ها نخواهد بود، و از (حال) یکدیگر نپرسند.');
INSERT INTO "hussein_taji" VALUES(23,102,'23:102','و کسانی‌که (کفۀ) ترازوی (اعمال‌) شان سنگین شود، پس آنانند که رستگار هستند.');
INSERT INTO "hussein_taji" VALUES(23,103,'23:103','و کسانی‌که (کفۀ) ترازوی (اعمال) شان سبک باشد، پس آن‌ها کسانی هستند که (وجود خود را از دست داده و) به خود زیان رسانده‌اند و در جهنم جاودانه خواهند ماند.');
INSERT INTO "hussein_taji" VALUES(23,104,'23:104','(شعله‌های) آتش چهره‌هایشان را می‌سوزاند، و آن‌ها در آن ترشرو (و عبوس) هستند.');
INSERT INTO "hussein_taji" VALUES(23,105,'23:105','(به آن‌ها گفته می‌شود:) مگر آیات من بر شما خوانده نمی‌شد، پس شما آن‌ها را تکذیب می‌کردید؟!');
INSERT INTO "hussein_taji" VALUES(23,106,'23:106','(در پاسخ) گویند: «پروردگارا! بدبختی ما بر ما چیره شد، و ما قومی گمراه بودیم.');
INSERT INTO "hussein_taji" VALUES(23,107,'23:107','پروردگارا! ما را از این (جهنم) بیرون کن، پس اگر (به کفر و نافرمانی) باز گردیم، قطعاً ستمکار خواهیم بود».');
INSERT INTO "hussein_taji" VALUES(23,108,'23:108','(الله) می‌فرماید: «در آن گم شوید و با من سخن مگویید.');
INSERT INTO "hussein_taji" VALUES(23,109,'23:109','بی‌گمان گروهی از بندگانم می‌گفتند: پروردگارا! (ما) ایمان آوردیم، پس ما را ببخش و بر ما رحم کن، و تو بهترین رحم کنندگانی.');
INSERT INTO "hussein_taji" VALUES(23,110,'23:110','پس شما آن‌ها را به مسخره گرفتید؛ تا یاد مرا فراموش‌تان کردند، و شما به آن‌ها می‌خندیدید!');
INSERT INTO "hussein_taji" VALUES(23,111,'23:111','همانا من امروز آن‌ها را بخاطر صبری‌که کرده‌اند پاداش دادم، قطعاً آن‌ها کامیاب هستند».');
INSERT INTO "hussein_taji" VALUES(23,112,'23:112','(الله) می‌فرماید: «(شما) چند سال در زمین درنگ کردید؟!».');
INSERT INTO "hussein_taji" VALUES(23,113,'23:113','گویند: «یک روز، یا قسمتی از یک روز درنگ کرده‌ایم، پس از حسابگران بپرس».');
INSERT INTO "hussein_taji" VALUES(23,114,'23:114','می‌فرماید: «(آری) اگر می‌دانستید، شما جز مقدار اندکی (در دنیا) درنگ نکردید،');
INSERT INTO "hussein_taji" VALUES(23,115,'23:115','آیا گمان کردید که ما شما را بیهوده آفریده‌ایم، و همانا شما به سوی ما باز گردانده نمی‌شوید؟!');
INSERT INTO "hussein_taji" VALUES(23,116,'23:116','پس بلند مرتبه (و برتر) است الله که فرمانروای حق است، هیچ معبودی (به حق) جز او نیست، پروردگار عرش کریم است.');
INSERT INTO "hussein_taji" VALUES(23,117,'23:117','و هر کس که با الله معبود دیگری را بخواند، مسلماً هیچ دلیلی بر (حقانیت) آن نخواهد داشت، جز این نیست که حسابش نزد پروردگارش خواهد بود، یقیناً کافران رستگار نمی‌شوند.');
INSERT INTO "hussein_taji" VALUES(23,118,'23:118','و (ای پیامبر) بگو: «پروردگارا! ببخش و رحم کن! و تو بهترین رحم کنندگانی».');
INSERT INTO "hussein_taji" VALUES(24,1,'24:1','(این) سوره‌ای است که آن را نازل کرده‌ایم، و (احکام) آن واجب نموده‌ایم، و در آن آیات روشنی نازل کرده‌ایم، شاید شما پند گیرید.');
INSERT INTO "hussein_taji" VALUES(24,2,'24:2','هر یک از زن و مرد زنا کار را صد تازیانه بزنید، و اگر به الله و روز آخرت ایمان دارید نباید در (اجرای احکام) دین الله نسبت به آن دو (زن و مرد) رأفت (و رحمت کاذب) داشته باشید، و باید گروهی از مؤمنان بر مجازات‌شان (شاهد و) حاضر باشند.');
INSERT INTO "hussein_taji" VALUES(24,3,'24:3','مرد زنا کار جز با زن زنا کار یا مشرک ازدواج نمی‌کند، و زن زنا کار را جز مرد زنا کار یا مشرک به ازدواج خود در نمی‌آورد، و این (امر) بر مؤمنان حرام شده است.');
INSERT INTO "hussein_taji" VALUES(24,4,'24:4','و کسانی‌که زنان پاکدامن را (به زنا) متهم می‌کنند، سپس چهار شاهد نمی‌آورند، پس آن‌ها را هشتاد تازیانه بزنید، و هرگز شهادت‌شان را نپذیرید، و اینانند که فاسق‌اند.');
INSERT INTO "hussein_taji" VALUES(24,5,'24:5','مگر کسانی‌که بعد از آن توبه کردند، و (اعمال خود را) اصلاح (و جبران) نمودند، پس بی‌گمان الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(24,6,'24:6','و کسانی‌که همسران خود را (به زنا) متهم می‌کنند، و جز خودشان گواهانی ندارند، پس هر یک از آن‌ها باید چهار مرتبه به نام الله سوگند یاد کنند که او از راستگویان است.');
INSERT INTO "hussein_taji" VALUES(24,7,'24:7','و پنجمین بار (چنین گوید:) که لعنت الله بر او باد؛ اگر از دروغگویان باشد.');
INSERT INTO "hussein_taji" VALUES(24,8,'24:8','و از آن (زن) کیفر (رجم) را دور می‌کند، (به) آن که چهار بار الله را شاهد بگیرد (و سوگند یاد کند) که او (= شوهرش) از دروغگویان است.');
INSERT INTO "hussein_taji" VALUES(24,9,'24:9','و بار پنجم (بگوید:) که خشم الله بر او باد؛ اگر (شوهرش) از راستگویان باشد.');
INSERT INTO "hussein_taji" VALUES(24,10,'24:10','و اگر فضل الله و رحمت او بر شما نمی‌بود، و اینکه الله توبه‌‌پذیر حکیم است (گرفتار عقوبت الهی می‌شدید).');
INSERT INTO "hussein_taji" VALUES(24,11,'24:11','مسلماً کسانی‌که تهمت بزرگ (دربارۀ ام المؤمنین عایشه -رضی الله عنها-) آوردند، گروهی از خود شما هستند، گمان نکنید که این (ماجرا) برای شما بد است، بلکه آن برای شما خیر است، برای هر کدام از آن‌ها سهمی از گناه است که مرتکب شده است، و کسی از آنان که (بخش) بزرگی (و مهمی)از آن (تهمت) را به عهده داشته است [ عبدالله بن أبی سردسته منافقان، عامل پخش شایعه بود.]، برای او عذاب عظیمی است.');
INSERT INTO "hussein_taji" VALUES(24,12,'24:12','چرا هنگامی‌که این (تهمت) را شنیدید، مردان و زنان مؤمن نسبت به خود گمان خیر نبردند، و نگفتند: این تهمتی (بزرگ و) آشکار است.');
INSERT INTO "hussein_taji" VALUES(24,13,'24:13','چرا چهار شاهد بر آن نیاوردند؟! پس چون گواهان را نیاوردند، آنان در نزد الله دروغگویانند.');
INSERT INTO "hussein_taji" VALUES(24,14,'24:14','و اگر فضل الله و رحمت او در دنیا و آخرت نبود، قطعاً در آنچه به آن پرداختید، عذاب (سخت و) بزرگی به شما می‌رسید.');
INSERT INTO "hussein_taji" VALUES(24,15,'24:15','زمانی که آن (شایعه) را از زبان یکدیگر می‌گرفتید، و با‌ دهان خود سخنی می‌گفتید که به آن علم (و یقین) نداشتید، و آن را سهل (و کوچک) می‌پنداشتید در حالی‌که آن نزد الله بسیار بزرگ است.');
INSERT INTO "hussein_taji" VALUES(24,16,'24:16','چرا هنگامی‌که آن را شنیدید؛ نگفتید: «ما حق نداریم که آن (سخن) را بر زبان آوریم، (الهی) تو منزهی! این بهتان بزرگی است؟!».');
INSERT INTO "hussein_taji" VALUES(24,17,'24:17','الله شما را اندرز می‌دهد که اگر مؤمن هستید؛ هرگز به چنین کاری باز نگردید.');
INSERT INTO "hussein_taji" VALUES(24,18,'24:18','و الله آیات (خود) را برای شما بیان می‌کند، و الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(24,19,'24:19','بی‌گمان کسانی‌که دوست دارند زشتی‌ها در (میان) مؤمنان شایع شود، عذاب درد ناکی برای آن‌ها در دنیا و آخرت است، و الله می‌داند و شما نمی‌دانید.');
INSERT INTO "hussein_taji" VALUES(24,20,'24:20','و اگر فضل الله و رحمت او بر شما نمی‌بود، و اینکه الله رؤوف مهربان است، (عقوبت سختی شما را در بر می‌گرفت).');
INSERT INTO "hussein_taji" VALUES(24,21,'24:21','ای کسانی‌که ایمان آوردید! از گام‌های شیطان پیروی نکنید، و هرکس که از گام‌های شیطان پیروی کند، پس بی‌گمان (گمراهش می‌کند، و) او (= شیطان) به فحشا و منکر فرمان می‌دهد، و اگر فضل الله و رحمت او بر شما نمی‌بود، هرگز کسی از شما پاک نمی‌شد، و لیکن الله هر که را بخواهد پاک می‌کند، و الله شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(24,22,'24:22','کسانی از میان شما که توانگر و (دارای) وسعت (زندگی) هستند، نباید سوگند بخورند بر آن که به خویشاوندان و مستمندان و مهاجران در راه الله چیزی ندهند، آن‌ها باید عفو کنند و چشم بپوشند [ مسطح بن اثاثه پسر خالۀ ابوبکر صدیق، فردی مسکین بود که ابوبکر مخارج وی را تأمین می‌کرد، او نیز در این شایعه پراکنی شرکت داشت لذا ابوبکر صدیق رضی الله عنه سوگند یاد کرد که دیگر به او کمک نکند، آنگاه الله متعال این آیه را نازل فرمود (تفسیر ابن کثیر).]، آیا دوست نمی‌دارید که الله شما را ببخشد؟! و الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(24,23,'24:23','بی‌گمان کسانی‌که زنان پاکدامن بی‌خبر (از هرگونه بدکاری و) مؤمن را متهم (به زنا) می‌سازند، در دنیا و آخرت لعنت شده‌اند، و عذاب بزرگی برای آن‌هاست.');
INSERT INTO "hussein_taji" VALUES(24,24,'24:24','روزی که زبان‌هایشان و دست‌‌هایشان و پا‌هایشان به آنچه می‌کردند بر علیه آن‌ها گواهی می‌دهند.');
INSERT INTO "hussein_taji" VALUES(24,25,'24:25','آن روز، الله جزای واقعی آن‌ها را به تمام (و بی کم و کاست) می‌دهد، و خواهند دانست که الله همان حق آشکار است.');
INSERT INTO "hussein_taji" VALUES(24,26,'24:26','زنان ناپاک از آن مردان ناپاکند، و مردان ناپاک (نیز) برای زنان ناپاک هستند، و زنان پاک از آن مردان پاک، و مردان پاک از آن زنان پاک‌اند. اینان از آنچه (مردم دربارۀشان) می‌گویند، مبّرا (و پاک) هستند، برای آن‌ها آمرزش (الهی) و روزی ارزشمندی است.');
INSERT INTO "hussein_taji" VALUES(24,27,'24:27','ای کسانی‌که ایمان آورده‌اید! وارد خانه‌هایی جز خانه‌های خود نشوید، تا اجازه بگیرید، و بر اهل آن (خانه) سلام کنید، این برای شما بهتر است، باشد که پند گیرید.');
INSERT INTO "hussein_taji" VALUES(24,28,'24:28','پس اگر کسی در آن (خانه) نیافتید، وارد نشوید، تا به شما اجازه داده شود، و اگر به شما گفته شد: «باز گردید» پس باز گردید، که آن برای شما پاکیزه‌تر است، و الله به آنچه انجام می‌دهید؛ داناست.');
INSERT INTO "hussein_taji" VALUES(24,29,'24:29','و بر شما گناهی نیست که وارد خانه‌هایی غیر مسکونی بشوید، که در آن متاعی دارید، و الله می‌داند آنچه را آشکار می‌سازید و آنچه را پنهان می‌کنید.');
INSERT INTO "hussein_taji" VALUES(24,30,'24:30','(ای پیامبر!) به مردان مؤمن بگو چشم‌های خود را (از نگاه به نامحرم) فرو گیرند، و شرمگاه‌شان را حفظ کنند، این (کار) برای آن‌ها پاکیز‌ه‌تر است، بی‌گمان الله به آنچه انجام می‌دهند؛ آگاه است.');
INSERT INTO "hussein_taji" VALUES(24,31,'24:31','و به زنان مؤمن بگو چشم‌های خود را (از نگاه به نامحرم) فرو گیرند، و شرمگاهشان را حفظ کنند، و زینت خود را آشکار نکنند؛ جز آنچه از آن که (طبعاً خودش) نمایان است، و باید (اطراف) مقنعه‌های خود را بر گریبان‌هایشان بیفکنند (تا چهره و گردن و سینه با آن پوشیده گردد) و زینت خود را آشکار نسازند؛ مگر برای شوهرانشان، یا پدرانشان، یا پدر شوهرانشان، یا پسرانشان یا پسران شوهرانشان، یا برادرانشان یا پسران برادرانشان، یا پسران خواهرانشان یا زنان (هم کیش) شان یا بردگانشان یا مردان (سفیه) خدمتکاری که رغبتی (به زنان) ندارند، یا کودکانی‌که بر شرمگاه زنان آگاهی نیافته‌اند، و (زنان) نباید (هنگام راه رفتن) پا‌های خود را (به زمین) بکوبند، تا آنچه از زینت‌شان را که پنهان کرده‌اند، دانسته شود، و ای مؤمنان! همگی به سوی الله توبه کنید، تا رستگار شوید.');
INSERT INTO "hussein_taji" VALUES(24,32,'24:32','مردان و زنان مجرد (و بی‌همسر) خود را همسر دهید، و (نیز) غلامان و کنیزان‌تان که شایسته (و نیکوکار) هستند، (همسر دهید) اگر فقیر باشند، الله از فضل (و کرم) خویش آن‌ها را بی‌نیاز می‌کند، و الله گشایش دهندۀ آگاه است.');
INSERT INTO "hussein_taji" VALUES(24,33,'24:33','و کسانی‌که (اسباب و امکانات) ازدواج را نمی‌یابند، باید پاکدامنی پیشه کنند؛ تا الله آنان را از فضل خود بی‌نیاز گرداند، و کسانی از بردگانتان که خواستار مکاتبه (= باز خریدن خویش) هستند، پس با آن‌ها قرار داد ببندید، اگر خیر و صلاح در آن‌ها احساس می‌کنید، و چیزی از مال الله را که به شما داده است؛ به آن‌ها بدهید، و کنیزان خود را که می‌خواهند پاکدامنی کنند، برای بدست آوردن متاع ناچیز زندگانی دنیا به زنا وادار نکنید، و کسی‌که آن‌ها را (بر این کار) مجبور کند، پس بی‌گمان الله بعد از اجبار آن‌ها (برای آنان) آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(24,34,'24:34','و به راستی ما بر شما آیاتی روشن را نازل کردیم، و مثلی از (حال) کسانی‌که پیش از شما گذشتند، و اندرزی برای پرهیزگاران.');
INSERT INTO "hussein_taji" VALUES(24,35,'24:35','الله نور آسمان‌ها و زمین است، مَثل نور او همانند چراغدانی است که در آن چراغی (افروخته) باشد، که آن چراغ در (میان) شیشه‌ای است، آن شیشه گویی ستاره‌ای درخشان است، این چراغ با (روغن) درخت پر برکت زیتون افروخته می‌شود، که نه شرقی است و نه غربی، نزدیک است که روغنش بدون تماس با آتش شعله‌‌ور شود، نوری است افزون بر نور، و الله هر کس را بخواهد به نور خود هدایت می‌کند، و الله برای مردم مثل‌ها می‌زند، و الله به هر چیزی آگاه است.');
INSERT INTO "hussein_taji" VALUES(24,36,'24:36','(این چراغ پر نور) در خانه‌های است که الله اجازه داده است که بلند شود، و نامش در آن‌ها برده شود، (و) صبح و شام در آن‌ها تسبیح او گویند.');
INSERT INTO "hussein_taji" VALUES(24,37,'24:37','مردانی‌که هیچ تجارت و خرید و فروشی آن‌ها را از یاد الله و برپاداشتن نماز و ادای زکات غافل نمی‌کند، (آن‌ها) از روزی می‌ترسند که در آن دل‌ها و چشم‌ها دگرگون (و بی‌تاب) می‌شود.');
INSERT INTO "hussein_taji" VALUES(24,38,'24:38','تا الله بهتر از آنچه انجام داده‌اند به آن‌ها پاداش دهد، و از فضل خود بر پاداش آن‌ها بیفزاید، و الله هر کس را که بخواهد بی‌حساب روزی می‌دهد.');
INSERT INTO "hussein_taji" VALUES(24,39,'24:39','و کسانی‌که کافر شدند؛ اعمال‌شان همچون سرابی است در بیابانی، که (انسان) تشنه (از دور) آن را آب می‌پندارد، تا آنکه به آن نزدیک شود، چیزی نمی‌یابد، و الله را نزد خود یابد که حساب او را کامل بدهد، و الله زود شمار است.');
INSERT INTO "hussein_taji" VALUES(24,40,'24:40','یا (اعمال‌شان) همچون تاریکی‌هایی در دریای پهناور که موج آن را پوشانده، و بر فراز آن موج دیگری است، (و) بر فراز آن ابری (تاریک) است، تاریکی‌هایی است یکی بر فراز دیگری، (آن گونه که) هرگاه دست خود را بیرون آورد، ممکن نیست آن را ببیند، و کسی را که الله نوری برای او قرار نداده است، پس (هیچ) نوری برای او نیست.');
INSERT INTO "hussein_taji" VALUES(24,41,'24:41','آیا ندیدی که همۀ آنان که در آسمان‌ها و زمین هستند، و پرندگان بال گسترده، الله را تسبیح می‌گویند. هریک از آن‌ها نماز و تسبیح خود را می‌داند، و الله به آنچه انجام می‌دهند؛ داناست.');
INSERT INTO "hussein_taji" VALUES(24,42,'24:42','و فرمانروایی آسمان‌ها و زمین از آنِ الله است، و بازگشت (همه) به سوی الله است.');
INSERT INTO "hussein_taji" VALUES(24,43,'24:43','آیا ندیدی که الله ابر‌هایی را به آرامی می‌راند، سپس بین آن‌ها پیوند می‌دهد، آنگاه آن را متراکم می‌کند، پس قطرۀ (باران) را می‌بینی که از میان آن بیرون می‌شود، و از آسمان، از کوه‌های که در آنجاست، دانه‌های تگرگ نازل می‌کند، پس آن را به هر کس که بخواهد می‌رساند، و آن را از هرکس که بخواهد باز می‌دارد، نزدیک است درخشندگی برق آن (نور) چشم‌ها را (از بین) ببرد.');
INSERT INTO "hussein_taji" VALUES(24,44,'24:44','الله شب و روز را دگرگون می‌سازد، بی‌گمان در این (امر) برای دیده‌وران عبرتی است.');
INSERT INTO "hussein_taji" VALUES(24,45,'24:45','و الله هر جنبنده‌ای را از آب آفرید، پس برخی از آن‌ها بر شکم خود راه می‌روند، و برخی بر دو پا راه می‌روند، و برخی از آن‌ها بر چهار (پا) راه می‌روند، الله هر چه را بخواهد می‌آفریند، بی‌گمان الله بر همه چیز تواناست.');
INSERT INTO "hussein_taji" VALUES(24,46,'24:46','به‌راستی (ما) آیات روشنگری نازل کردیم، و الله هر کس را که بخواهد به راه راست هدایت می‌کند.');
INSERT INTO "hussein_taji" VALUES(24,47,'24:47','و (منافقان) می‌گویند: «به الله و به پیامبر (او) ایمان آوردیم، و اطاعت کردیم» سپس گروهی از آن‌ها بعد از این (ادعا) روی گردان می‌شوند، و اینان (در حقیقت) مؤمن نیستند.');
INSERT INTO "hussein_taji" VALUES(24,48,'24:48','و هنگامی‌که به‌سوی الله و پیامبرش فرا‌خوانده شوند تا در میانشان داوری کند، ناگهان گروهی از آن‌ها اعراض می‌کنند.');
INSERT INTO "hussein_taji" VALUES(24,49,'24:49','و اگر حق با آن‌ها باشد، گردن نهاده (با سرعت) به‌سوی او می‌آیند.');
INSERT INTO "hussein_taji" VALUES(24,50,'24:50','آیا در دل‌هایشان بیماری است؟ یا تردید و شک دارند؟ یا می‌ترسند که الله و پیامبرش بر آن‌ها ستم کنند؟! (نه) بلکه آن‌ها خود‌شان ستمگرند.');
INSERT INTO "hussein_taji" VALUES(24,51,'24:51','سخن مؤمنان هنگامی‌که به‌سوی الله و پیامبرش خوانده شوند تا میان آن‌ها داوری کند، فقط این است که می‌گویند: «شنیدیم و اطاعت کردیم» و اینان همان رستگارانند.');
INSERT INTO "hussein_taji" VALUES(24,52,'24:52','و کسی‌که الله و پیامبرش را اطاعت کند، و از الله بترسد، و از (مخالفت فرمان) او بپرهیزد، پس اینانند که کامیاب (واقعی) هستند.');
INSERT INTO "hussein_taji" VALUES(24,53,'24:53','(منافقان) با (نهایت تأکید و) سخت‌ترین سوگندهایشان، به الله سوگند یاد کردند که اگر به آن‌ها فرمان دهی (برای جهاد) بیرون می‌روند، (ای پیامبر! به آن‌ها) بگو: «سوگند یاد نکنید، طاعت پسندیده (و خالصانه مطلوب) است، بی‌گمان الله به آنچه انجام می‌دهید؛ آگاه است».');
INSERT INTO "hussein_taji" VALUES(24,54,'24:54','(ای پیامبر!) بگو: «الله را اطاعت کنید، و از پیامبر اطاعت کنید، پس اگر سر پیچی کردید، بر او (= پیامبر) است آنچه که به آن مکلّف شده است، و بر شماست آنچه که بر آن تکلیف شده‌اید، و اگر از او اطاعت کنید؛ هدایت خواهید شد، و بر (عهدۀ) پیامبر (چیزی) جز ابلاغ آشکار نیست».');
INSERT INTO "hussein_taji" VALUES(24,55,'24:55','الله به کسانی از شما که ایمان آوردند و کارهای شایسته انجام دادند؛ وعده داده است که قطعاً آن‌ها را در زمین جانشین (و حکمران) خواهد کرد، همان گونه که کسانی را که پیش از آن‌ها بودند جانشین (و حکمران) ساخت، و دین‌شان را که برای آن‌ها پسندیده است برای آن‌ها استوار (و پا برجا) سازد، و یقیناً (خوف و) ترس‌شان را به آرامش و امنیت مبدل می‌کند، تنها مرا پرستش می‌کنند و چیزی را با من شریک نمی‌سازند، و کسانی‌که بعد از این کافر شوند، اینانند که فاسقند.');
INSERT INTO "hussein_taji" VALUES(24,56,'24:56','و نماز را بر پا دارید و زکات را بدهید، و پیامبر (الله) را اطاعت کنید، تا شما مورد رحمت قرار گیرید.');
INSERT INTO "hussein_taji" VALUES(24,57,'24:57','گمان مبر کسانی‌که کافر شدند؛ می‌توانند ما را در زمین به ستوه آورند (و از عقوبت ما فرار کنند) جایگاه‌شان آتش است، و چه بد بازگشتگاهی است.');
INSERT INTO "hussein_taji" VALUES(24,58,'24:58','ای کسانی‌که ایمان آورده‌اید! باید بردگان شما، و کسانی از (کودکان) شما که به حد بلوغ نرسیده‌اند، در سه وقت از شما اجازه بگیرند: پیش از نماز صبح، و نیم‌روز هنگامی‌که لباس‌هایتان را بیرون می‌آورید، و بعد از نماز عشا، (این) سه (وقت) وقت عورت (و خلوت) برای شما است. بعد از این (سه وقت) بر شما و بر آن‌ها هیچ گناهی نیست (که بدون اجازه وارد شوند). آن‌ها بر اطراف (و پیرامون) شما در رفت آمدند (و) بعضی بر بعضی دیگر (وارد می‌شوید). این گونه الله آیات (خود) را برای شما بیان می‌کند، و الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(24,59,'24:59','و هنگامی‌که کودکان شما به سن بلوغ رسیدند؛ باید اجازه بگیرند، همانند کسانی‌که پیش از آن‌ها بودند، اجازه می‌گرفتند، این گونه الله آیاتش را برای شما بیان می‌کند و الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(24,60,'24:60','و (پیر) زنان (یائسه) از کار افتاده‌ای که امید ازدواج ندارند، بر آن‌ها گناهی نیست که لباس‌های (رویین) خود را بگذارند، در صورتی‌که زینت (و زیبایی خود) را آشکار نکنند، و اگر خود‌‌ داری کنند (و خود را بپوشانند) برای آن‌ها بهتر است، و الله شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(24,61,'24:61','بر نابینا گناهی نیست، و بر (افراد) لنگ گناهی نیست، و بر بیمار (نیز) گناهی نیست، و (همچنین) بر خود شما گناهی نیست؛ در آن که از خانه‌های خود، یا خانه‌های پدران‌تان یا خانه‌های مادران‌تان یا خانه‌های برادران‌تان یا خانه‌های خواهران‌تان یا خانه‌های عمو‌هایتان یا خانه‌های عمّه‌هایتان یا خانه‌های دائی‌هایتان یا خانه‌های خاله‌هایتان، یا خانه‌ای که کلیدش در اختیار شماست، یا (خانه‌های) دوستان‌تان (چیزی) بخورید، بر شما گناهی نیست که دسته‌جمعی یا جداگانه (غذا) بخورید، پس هنگامی‌که به خانه‌ها وارد شدید بر خویشتن سلام کنید که تحیتی مبارک (و) پاکیزه از سوی الله (مقرر) است، این گونه الله آیات (خود) را برای شما روشن می‌کند، باشد که شما خرد ورزید.');
INSERT INTO "hussein_taji" VALUES(24,62,'24:62','مؤمنان (واقعی) کسانی هستند که به الله و پیامبرش ایمان آورده‌اند و هنگامی‌که در کار همگانی با او باشند (به جایی) نمی‌روند تا از او اجازه بگیرند، بی‌گمان کسانی‌که از تو اجازه می‌گیرند، آن‌ها کسانی‌اند که به الله و پیامبرش ایمان آورده‌اند، پس اگر برای بعضی کارهای (مهم) خود از تو اجازه بخواهند، به هرکس از آن‌ها که می‌خواهی اجازه بده، و برایشان از الله آمرزش بخواه، یقیناً الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(24,63,'24:63','صدا زدن پیامبر (الله) را در میان خود همچون صدا‌ زدن یکدیگر قرار ندهید، یقیناً الله کسانی از شما را که پشت‌سر دیگران پنهان می‌شوند، و یکی بعد از دیگری فرار می‌کنند می‌داند، پس کسانی‌که با فرمان او مخالفت می‌کنند؛ باید بترسند از این‌که فتنه‌ای ایشان را فرا گیرد، یا عذابی دردناک به آن‌ها برسد!');
INSERT INTO "hussein_taji" VALUES(24,64,'24:64','آگاه باشید، بی‌گمان آنچه در آسمان‌ها و زمین است از آن الله است، و یقیناً او آنچه را که شما بر آن هستید؛ می‌داند، و روزی‌که به سوی او باز گردانده می‌شوند، آن‌ها را به آنچه انجام داده‌اند؛ آگاه می‌سازد، و الله به هر چیزی داناست.');
INSERT INTO "hussein_taji" VALUES(25,1,'25:1','پر برکت و بزرگوار است کسی‌که فرقان (= قرآن) را بر بنده‌اش نازل کرد؛ تا بیم دهندۀ جهانیان باشد.');
INSERT INTO "hussein_taji" VALUES(25,2,'25:2','(همان) کسی‌که فرمانروایی آسمان‌ها و زمین از آنِ اوست، و فرزندی (برای خود) بر نگزیده است، و در فرمانروایی هیچ شریکی ندارد، و همه چیز را آفرید، پس اندازۀ هر چیز را چنان که می‌باید؛ معین کرده است.');
INSERT INTO "hussein_taji" VALUES(25,3,'25:3','و (مشرکان) به جای او معبودانی (برای خود) بر گزیدند که چیزی نمی‌آفرینند، و خود‌شان مخلوقند، و مالک زیان و سود خود نیستند، و (نیز) مالک مرگ و حیات و برانگیختن نیستند.');
INSERT INTO "hussein_taji" VALUES(25,4,'25:4','و کسانی‌که کافر شدند؛ گفتند: «این (قرآن) جز دروغی نیست که (خود) بافته است، و گروهی دیگر او را بر این (کار) یاری داده‌اند» به راستی آن‌ها (با این سخنشان) ستم و دروغ بزرگی را مرتکب شده‌اند.');
INSERT INTO "hussein_taji" VALUES(25,5,'25:5','و گفتند: «(این همان) افسانه‌های پیشینیان است که آن را رو‌نویسی کرده و هر صبح و شام بر او املا می‌شود»');
INSERT INTO "hussein_taji" VALUES(25,6,'25:6','(ای پیامبر!) بگو: «کسی آن را نازل کرده که راز (نهان) آسمان‌ها و زمین را می‌داند، بی‌گمان او آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(25,7,'25:7','و گفتند: «این پیامبر را چه شده است که غذا می‌خورد و در بازار‌ها راه می‌رود؟! چرا فرشته‌ای بر او نازل نشده است؛ پس همراه او بیم‌دهنده باشد؟!');
INSERT INTO "hussein_taji" VALUES(25,8,'25:8','یا گنجی (از آسمان) بر او افکنده شود، و یا باغی داشته باشد که از (میوه‌های) آن بخورد؟!» و ستمگران گفتند: «شما فقط از مردی سحر شده پیروی می‌کنید».');
INSERT INTO "hussein_taji" VALUES(25,9,'25:9','بنگر چگونه برای تو مثل‌ها زدند، پس گمراه شدند (آن گونه) که نمی‌توانند راهی را بیابند.');
INSERT INTO "hussein_taji" VALUES(25,10,'25:10','پر برکت و بزرگوار است کسی‌که اگر بخواهد برای تو بهتر از این قرار می‌دهد: باغ‌هایی که نهر‌‌ها از زیر (درختان) آن جاری است، و (نیز) برایت کاخ‌هایی (مجلل) قرار می‌دهد.');
INSERT INTO "hussein_taji" VALUES(25,11,'25:11','(این‌ها همه بهانه است) بلکه (حقیقت این است که) آن‌ها قیامت را تکذیب کرده‌اند، و (ما) برای کسی‌که قیامت را تکذیب کند، آتش سوزان (جهنم) فراهم کرده‌ایم.');
INSERT INTO "hussein_taji" VALUES(25,12,'25:12','هنگامی‌که (جهنم) آن‌ها را از مکانی دور ببیند؛ از آن خشم و خروشی (وحشتناک) می‌شنوند.');
INSERT INTO "hussein_taji" VALUES(25,13,'25:13','و هنگامی‌که دست و پا بسته (با غل و زنجیر) در مکان تنگی از آن انداخته شوند، در آنجا مرگ (و نابودی خود) را به فریاد می‌خوانند.');
INSERT INTO "hussein_taji" VALUES(25,14,'25:14','(به آن‌ها گفته شود:) امروز یک بار مرگ (و نابودی) را نخوانید، و (بلکه) بسیار (و بارها) فریادش کنید.');
INSERT INTO "hussein_taji" VALUES(25,15,'25:15','(ای پیامبر!) بگو: «آیا این بهتر است؛ یا بهشت جاویدانی که به پرهیزگاران وعده داده شده است؟! (بهشتی) که پاداش آن‌ها، و باز‌گشت‌گاه‌شان است».');
INSERT INTO "hussein_taji" VALUES(25,16,'25:16','هر چه بخواهند در آنجا برایشان (مهیا) است؛ جاودانه در آن خواهند ماند، این وعده‌ای است بر (عهدۀ) پروردگارت که (تحقق) آن را از او خواسته‌اند.');
INSERT INTO "hussein_taji" VALUES(25,17,'25:17','و روزی‌که (همۀ) آن‌ها و آنچه را که به جای الله می‌پرستیدند؛ گرد آورد، پس (به معبودان باطل) می‌فرماید: «آیا شما این بندگان مرا گمراه کردید، یا خود‌شان راه را گم کردند؟!»');
INSERT INTO "hussein_taji" VALUES(25,18,'25:18','گویند: «تو منزهی! برای ما شایسته نبود که به جای تو (اولیاء و) دوستانی برگزینیم، و لیکن آن‌ها و پدران‌شان را (از نعمت‌ها) بهره‌مند نمودی تا اینکه یاد (تو) را فراموش کردند، و گروهی هلاک (و تباه) شده شدند».');
INSERT INTO "hussein_taji" VALUES(25,19,'25:19','(الله به آن‌ها می‌فرماید:) به راستی (این معبودان) شما را در آنچه می‌گویید؛ تکذیب کردند، پس (اکنون) نمی‌توانید (عذاب را) برطرف کنید، و نه (از کسی) یاری بجویید، و هرکس از شما که ستم (= شرک) کند، عذاب بزرگی به او می‌چشانیم.');
INSERT INTO "hussein_taji" VALUES(25,20,'25:20','(ای پیامبر!) پیش از تو هیچ پیامبری را نفرستادیم، مگر اینکه آن‌ها هم غذا می‌خورند، و در بازارها راه می‌رفتند، و ما بعضی از شما را وسیلۀ آزمایش بعضی دیگر قرار داده‌ایم، آیا صبر می‌کنید؟! و پروردگار تو بیناست.');
INSERT INTO "hussein_taji" VALUES(25,21,'25:21','کسانی‌که به دیدار ما امید ندارند گفتند: «چرا فرشتگانی بر ما نازل کرده نشدند، یا (چرا) پروردگارمان را نمی‌بینیم؟!» به‌راستی آن‌ها دربارۀ خود تکبر ورزیدند، و طغیان (و سرکشی) بزرگی کردند.');
INSERT INTO "hussein_taji" VALUES(25,22,'25:22','روزی که فرشتگان را ببینند، در آن روز هیچ بشارتی برای مجرمان نخواهد بود؛ (بلکه روز مجازات و عذاب است) و (فرشتگان) می‌گویند: «(بهشت) بر شما ممنوع (و حرام) است (و بهره‌ای جز حرمان ندارید)».');
INSERT INTO "hussein_taji" VALUES(25,23,'25:23','و ما به سراغ هرگونه عملی‌که انجام داده‌اند؛ می‌‌رویم، پس آن را (چون) غبار پراکنده (در هوا) قرار می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(25,24,'25:24','بهشتیان در آن روز جایگاه‌شان (از همه) بهتر، و استراحت‌گاه‌شان نیکوتر است.');
INSERT INTO "hussein_taji" VALUES(25,25,'25:25','و (به یاد آور) روزی را که آسمان با ابرها شکافته شود، و فرشتگان چنانکه باید (پی در پی) فرود آورده شوند.');
INSERT INTO "hussein_taji" VALUES(25,26,'25:26','فرمانروایی راستین (و حقیقی) در آن روز از آنِ (الله) رحمان است، و (آن روز) روز سختی بر کافران خواهد بود.');
INSERT INTO "hussein_taji" VALUES(25,27,'25:27','و (به یاد آور) روزی‌که ستمکار (از حسرت) دست خود را می‌گزد، می‌گوید: «ای کاش من با رسول (الله) راهی بر گزیده بودم،');
INSERT INTO "hussein_taji" VALUES(25,28,'25:28','ای وای بر من، ای کاش فلانی را دوست نمی‌گرفتم.');
INSERT INTO "hussein_taji" VALUES(25,29,'25:29','به راستی او مرا از ذکر (= قرآن) گمراه ساخت، بعد از آن که به سوی من آمده بود، و شیطان همواره انسان را تنها (و خوار) می‌گذارد».');
INSERT INTO "hussein_taji" VALUES(25,30,'25:30','و پیامبر (الله) گوید: «پروردگارا! بی‌گمان قوم من این قرآن را رها کردند».');
INSERT INTO "hussein_taji" VALUES(25,31,'25:31','و این‌گونه برای هر پیامبری دشمنی از مجرمان قرار دادیم، و پروردگار تو برای راهنمایی و یاری (تو) کافی است.');
INSERT INTO "hussein_taji" VALUES(25,32,'25:32','و کسانی‌که کافر شدند؛ گفتند: «چرا قرآن یکباره بر او نازل کرده نمی‌شود؟!» این‌گونه (آن را بخش بخش نازل کردیم) تا با آن قلب تو را استوار داریم، و آن را آرام آرام (و به تدریج بر تو) بخوانیم.');
INSERT INTO "hussein_taji" VALUES(25,33,'25:33','و (کافران) هیچ (اعتراض و) مثلی برای تو نمی‌آورند؛ مگر آن که برای تو حق و (پاسخی) نیک بیان‌تر می‌آوریم.');
INSERT INTO "hussein_taji" VALUES(25,34,'25:34','کسانی‌که بر چهره‌هایشان به سوی جهنم محشور می‌شوند، اینان بد جایگاه‌تر و گمراه‌تر هستند.');
INSERT INTO "hussein_taji" VALUES(25,35,'25:35','و به راستی (ما) به موسی کتاب (تورات) دادیم، و برادرش هارون را همراه او مددکار (او) قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(25,36,'25:36','آنگاه گفتیم: «شما دو نفر سوی قومی که آیات ما را تکذیب کردند، بروید» پس آن‌ها را بشدت (هلاک و) نابود کردیم.');
INSERT INTO "hussein_taji" VALUES(25,37,'25:37','و قوم نوح را هنگامی‌که پیامبران را تکذیب کردند، آن‌ها را غرق کردیم، و آن‌ها را برای مردم (نشانه و) عبرت قرار دادیم، و برای ستمکاران عذاب درد ناکی آماده کرده‌ایم.');
INSERT INTO "hussein_taji" VALUES(25,38,'25:38','و (همچنین) عاد و ثمود و اصحاب رس و نسل‌های بسیار را که بین آن‌ها بودند (هلاک کردیم).');
INSERT INTO "hussein_taji" VALUES(25,39,'25:39','و برای هر یک از آن‌ها مثل‌ها زدیم، و همگی (آن‌ها) را سخت نابود (و هلاک) کردیم.');
INSERT INTO "hussein_taji" VALUES(25,40,'25:40','و به راستی آن‌ها (= مشرکان مکه) بر قریه‌ای که باران بلا بر آن باریده بود، گذشتند، آیا آن را نمی‌دیدند؟! (آری، می‌دیدند) بلکه به (قیامت و) زنده شدن امید (و ایمان) ندارند.');
INSERT INTO "hussein_taji" VALUES(25,41,'25:41','و هنگامی‌که تو را ببینند، فقط تو را به ریشخند (و مسخره) می‌گیرند، (و می‌گویند:) آیا این همان کسی است که الله او را به پیامبری فرستاده است؟!');
INSERT INTO "hussein_taji" VALUES(25,42,'25:42','اگر ما بر (پرستش) معبود‌مان استقامت نمی‌کردیم، نزدیک بود که ما را از (پرستش) آن‌ها گمراه کند! و چون عذاب (الهی) را ببینند؛ به زودی خواهند دانست چه کسی گمراه‌تر (بوده) است.');
INSERT INTO "hussein_taji" VALUES(25,43,'25:43','آیا دیدی کسی را که هوای (نفس) خود را معبود خود برگزیده است؟! آیا تو می‌توانی بر او (وکیل و) نگهبان باشی؟!');
INSERT INTO "hussein_taji" VALUES(25,44,'25:44','آیا گمان می‌کنی بیشتر آن‌ها می‌شنوند یا می‌فهمند؟! آن‌ها همچون چهارپایانی بیش نیستند، بلکه اینان گمراه‌ترند.');
INSERT INTO "hussein_taji" VALUES(25,45,'25:45','آیا ندیدی چگونه پروردگارت سایه را گسترانیده است، و اگر می‌خواست آن را ساکن قرار می‌داد، پس خورشید را بر (وجود) آن دلیل قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(25,46,'25:46','سپس آن (سایه) را اندک اندک به سوی خود باز می‌گیریم.');
INSERT INTO "hussein_taji" VALUES(25,47,'25:47','و او کسی است که شب را برای شما پوششی قرار داد، و خواب را مایۀ آسایش، و روز را (هنگام) بر خاستن (و جنبش) قرار داد.');
INSERT INTO "hussein_taji" VALUES(25,48,'25:48','و او کسی است که باد‌ها را بشارت دهندگانی پیشاپیش رحمتش فرستاد، و از آسمان آبی پاک نازل کردیم.');
INSERT INTO "hussein_taji" VALUES(25,49,'25:49','تا به آن سرزمین مرده‌ای را زنده کنیم، و آن را به چهارپایان و انسان‌های بسیاری را که آفریده‌ایم؛ بنوشانیم.');
INSERT INTO "hussein_taji" VALUES(25,50,'25:50','و به راستی (ما) آن (آب باران) را در میان آن‌ها تقسیم کردیم تا متذکر شوند، پس بیشتر مردم جز ناسپاسی را قبول نکردند.');
INSERT INTO "hussein_taji" VALUES(25,51,'25:51','و اگر می‌خواستیم در هر قریه و دیاری بیم دهنده‌ای می‌فرستادیم.');
INSERT INTO "hussein_taji" VALUES(25,52,'25:52','پس (ای پیامبر!) از کافران اطاعت مکن، و بوسیلۀ آن (= قرآن) با آن‌ها جهاد بزرگی بنما.');
INSERT INTO "hussein_taji" VALUES(25,53,'25:53','و او کسی است که دو دریا را به هم آمیخت، این گوارا و شیرین، و آن شور تلخ است، و در میان آن دو حائل و مانعی استوار قرار داد.');
INSERT INTO "hussein_taji" VALUES(25,54,'25:54','و او کسی است که از آب انسانی را آفرید، آنگاه او را (پیوند) نسبی و سببی قرار داد، و پروردگار تو تواناست.');
INSERT INTO "hussein_taji" VALUES(25,55,'25:55','و (مشرکان) به جای الله چیزی را می‌پرستند که نه به آن‌ها سود می‌بخشد و نه به آن‌ها زیانی می‌رساند. و کافر پیوسته در برابر پروردگارش پشتیبان (یکدیگر و یاور شیطان) است.');
INSERT INTO "hussein_taji" VALUES(25,56,'25:56','و (ای پیامبر!) ما تو را جز بشارت‌دهنده و بیم‌دهنده نفرستادیم.');
INSERT INTO "hussein_taji" VALUES(25,57,'25:57','بگو: «من در برابر (تبلیغ) آن هیچ‌گونه مزدی از شما نمی‌طلبم، مگر کسی‌که بخواهد راهی به سوی پروردگارش برگزیند»');
INSERT INTO "hussein_taji" VALUES(25,58,'25:58','و بر زنده‌ای که هرگز نمی‌میرد؛ توکل کن، و به ستایش او تسبیح گوی، و همین بس که او به گناهان بندگانش آگاه است.');
INSERT INTO "hussein_taji" VALUES(25,59,'25:59','(همان) کسی‌که آسمان‌ها و زمین و آنچه را در میان آن دو است، در شش روز آفرید، سپس بر عرش قرار گرفت (او الله) رحمان است. پس دربارۀ او (تعالی) از کسی (= پیامبر) بپرس که آگاه است.');
INSERT INTO "hussein_taji" VALUES(25,60,'25:60','و هنگامی‌که به آن‌ها گفته شود: «برای (الله) رحمان سجده کنید» گویند: «رحمان چیست؟! (او را نمی‌شناسیم) آیا برای چیزی‌که تو به ما فرمان می‌دهی سجده کنیم؟!» و بر نفرت (و گریز‌شان) افزوده شد.');
INSERT INTO "hussein_taji" VALUES(25,61,'25:61','پر برکت و بزرگوار است کسی‌که در آسمان برج‌هایی قرار داد، و در (میان) آن چراغ روشن و ماه تابانی قرار داد.');
INSERT INTO "hussein_taji" VALUES(25,62,'25:62','و او (همان) کسی است که شب و روز را در پی یکدیگر قرار داد برای کسی‌که بخواهد متذکر شود یا بخواهد شکر گذاری کند.');
INSERT INTO "hussein_taji" VALUES(25,63,'25:63','و بندگان (خاص) رحمان، کسانی هستند که با فروتنی (و آرامش) بر زمین راه می‌روند، و هنگامی‌که جاهلان آن‌ها را مخاطب قرار دهند (و سخنان نابخردانه و ناسزا گویند) به آن‌ها سلام گویند (و از آن‌ها روی می‌گردانند).');
INSERT INTO "hussein_taji" VALUES(25,64,'25:64','و کسانی‌که شب را برای پروردگارشان سجده‌کنان و قیام‌کنان می‌گذرانند.');
INSERT INTO "hussein_taji" VALUES(25,65,'25:65','و کسانی‌که می‌گویند: «پروردگارا! عذاب جهنم را از ما بر‌طرف کن، بی‌تردید عذابش (سخت و) پایدار است.');
INSERT INTO "hussein_taji" VALUES(25,66,'25:66','بی‌گمان آن (جهنم) بد قرار‌گاه و (بد) اقامت‌گاهی است.');
INSERT INTO "hussein_taji" VALUES(25,67,'25:67','و کسانی‌که چون انفاق کنند، اسراف نمی‌نمایند و سختگیری نمی‌کنند، و بین این دو (روش) اعتدال دارند.');
INSERT INTO "hussein_taji" VALUES(25,68,'25:68','و کسانی‌که با الله معبود دیگر را نمی‌خوانند، و نفسی را که الله (کشتنش را) حرام کرده است، جز به حق نمی‌کشند، و زنا نمی‌کنند، و هر کس چنین کند، مجازات گناه (خود) را خواهد دید.');
INSERT INTO "hussein_taji" VALUES(25,69,'25:69','عذاب او در روز قیامت مضاعف می‌گردد و با خواری (و ذلت) در آن جاودان خواهد ماند.');
INSERT INTO "hussein_taji" VALUES(25,70,'25:70','مگر کسی‌که توبه کند و ایمان آورد، و عمل صالح انجام دهد، پس اینانند که الله بدی‌هایشان را به نیکی‌ها مبدل می‌کند، و الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(25,71,'25:71','و کسی‌که توبه کند و عمل صالح انجام دهد، پس بی‌گمان او چنانکه باید به سوی الله بازگشت می‌کند.');
INSERT INTO "hussein_taji" VALUES(25,72,'25:72','و کسانی‌که شهادت دروغ نمی‌دهند، و چون بر (کار) لغو (و بیهوده) بگذرند، بزرگوارانه (از آن) می‌گذرند.');
INSERT INTO "hussein_taji" VALUES(25,73,'25:73','و کسانی‌که چون به آیات پروردگارشان پند داده شدند، کر و کور بر روی آن نمی‌افتند.');
INSERT INTO "hussein_taji" VALUES(25,74,'25:74','و کسانی‌که می‌گویند: «پروردگارا! از همسران‌مان، و فرزندان‌مان مایۀ روشنی چشم به ما عطا فرما، و ما را برای پرهیزگاران پیشوا قرار بده».');
INSERT INTO "hussein_taji" VALUES(25,75,'25:75','(آری) اینان به (خاطر) صبری‌که کرده‌اند درجات (و منازل) عالی (بهشت) به آن‌ها پاداش داده می‌شود، و در آن با تحیت و سلام استقبال می‌شوند.');
INSERT INTO "hussein_taji" VALUES(25,76,'25:76','در آن جاودانه خواهند ماند، چه نیکو قرارگاه و (نیکو) اقامت‌گاهی است.');
INSERT INTO "hussein_taji" VALUES(25,77,'25:77','(ای پیامبر!) بگو: «پروردگار به شما اعتنایی ندارد؛ اگر دعای شما نباشد، به راستی شما (حق را) تکذیب کردید، پس به زودی (کیفرش، گردن گیر و) ملازم‌تان خواهد بود.');
INSERT INTO "hussein_taji" VALUES(26,1,'26:1','طسم (طا. سین. میم)');
INSERT INTO "hussein_taji" VALUES(26,2,'26:2','این آیات کتاب روشن است.');
INSERT INTO "hussein_taji" VALUES(26,3,'26:3','گویی می‌خواهی از (شدت غم و اندوه) این‌که آن‌ها ایمان نمی‌آورند؛ خود را هلاک کنی!');
INSERT INTO "hussein_taji" VALUES(26,4,'26:4','اگر بخواهیم از آسمان بر آن‌ها نشانه‌ای نازل می‌کنیم که گردن‌هایشان در برابر آن تسلیم (و خاضع) گردد.');
INSERT INTO "hussein_taji" VALUES(26,5,'26:5','و هیچ پند تازه‌ای از سوی (الله) رحمان برای آن‌ها نمی‌آید؛ مگر این‌که از آن روی گردان هستند.');
INSERT INTO "hussein_taji" VALUES(26,6,'26:6','به راستی آن‌ها تکذیب کردند، پس به زودی اخبار آنچه را که به آن استهزاء (و تمسخر) می‌کردند؛ به آن‌ها خواهد رسید.');
INSERT INTO "hussein_taji" VALUES(26,7,'26:7','آیا به زمین ننگریسته‌اند که چه قدر از هر گونه گیاهان (نیکو و) پر ارزش در آن رویانده‌ایم؟!');
INSERT INTO "hussein_taji" VALUES(26,8,'26:8','بی‌گمان در این نشانۀ (بزرگی) است، و بیشتر آن‌ها مؤمن نیستند.');
INSERT INTO "hussein_taji" VALUES(26,9,'26:9','و به راستی پروردگار تو پیروزمند مهربان است.');
INSERT INTO "hussein_taji" VALUES(26,10,'26:10','و (به یاد بیاور) هنگامی را که پروردگارت موسی را ندا داد که به سوی قوم ستمکار برو،');
INSERT INTO "hussein_taji" VALUES(26,11,'26:11','قوم فرعون، آیا آن‌ها نمی‌ترسند؟!');
INSERT INTO "hussein_taji" VALUES(26,12,'26:12','(موسی) گفت: «پروردگارا! من می‌ترسم که مرا تکذیب کنند!');
INSERT INTO "hussein_taji" VALUES(26,13,'26:13','و سینه‌ام تنگ گردد و زبانم باز نشود، پس (برادرم) هارون را (نیز) رسالت ده (تا مرا یاری کند).');
INSERT INTO "hussein_taji" VALUES(26,14,'26:14','و آن‌ها بر (گردن) من (ادعای) گناهی دارند، پس می‌ترسم که مرا بکشند».');
INSERT INTO "hussein_taji" VALUES(26,15,'26:15','(الله) فرمود: «چنین نیست، پس شما هردو با آیات ما بروید، یقیناً ما با شما شنوندگان هستیم.');
INSERT INTO "hussein_taji" VALUES(26,16,'26:16','پس (به نزد) فرعون بروید، و بگویید: «ما فرستادۀ پروردگار جهانیان هستیم،');
INSERT INTO "hussein_taji" VALUES(26,17,'26:17','که بنی اسرائیل را با ما بفرست».');
INSERT INTO "hussein_taji" VALUES(26,18,'26:18','(فرعون) گفت: «آیا ما تو را در کودکی نزد خود پرورش ندادیم، و سال‌هایی از عمرت را در میان ما نگذراندی؟!');
INSERT INTO "hussein_taji" VALUES(26,19,'26:19','و (بالاخره) آن کارت را (که نبایست انجام می‌دادی) انجام دادی، و تو از ناسپاسانی».');
INSERT INTO "hussein_taji" VALUES(26,20,'26:20','(موسی) گفت: «(من) آن (کار) را آنگاه انجام دادم که من از بی‌خبران بودم.');
INSERT INTO "hussein_taji" VALUES(26,21,'26:21','پس چون از شما ترسیدم از شما فرار کردم، آنگاه پروردگارم به من دانش (و نبوت) بخشید و مرا از پیامبران قرار داد.');
INSERT INTO "hussein_taji" VALUES(26,22,'26:22','و آیا این نعمتی است که تو بر من منت می‌گذاری که بنی اسرائیل را بردۀ خود ساخته‌ای؟!»');
INSERT INTO "hussein_taji" VALUES(26,23,'26:23','فرعون گفت: «پروردگار جهانیان چیست؟!»');
INSERT INTO "hussein_taji" VALUES(26,24,'26:24','(موسی) گفت: «پروردگار آسمان‌ها و زمین و آنچه میان آن دو است، اگر اهل یقین هستید».');
INSERT INTO "hussein_taji" VALUES(26,25,'26:25','(فرعون) به آن‌های که در اطرافش بودند؛ گفت: «آیا نمی‌شنوید؟»');
INSERT INTO "hussein_taji" VALUES(26,26,'26:26','(موسی) گفت: «(او) پروردگار شما و پروردگار نیاکان پیشین شماست.');
INSERT INTO "hussein_taji" VALUES(26,27,'26:27','(فرعون) گفت: «بی‌تردید پیامبرتان که به سوی شما فرستاده شده دیوانه است».');
INSERT INTO "hussein_taji" VALUES(26,28,'26:28','(موسی) گفت: «(او) پروردگار مشرق و مغرب و آنچه میان آن دو است، اگر بیندیشید».');
INSERT INTO "hussein_taji" VALUES(26,29,'26:29','(فرعون) گفت: «اگر معبودی جز من برگزینی، البته تو را از زندانیان قرار خواهم داد».');
INSERT INTO "hussein_taji" VALUES(26,30,'26:30','(موسی) گفت: «حتی اگر معجزۀ روشنی برای تو آورده باشم؟!»');
INSERT INTO "hussein_taji" VALUES(26,31,'26:31','(فرعون) گفت: «پس اگر از راستگویانی، آن را بیاور».');
INSERT INTO "hussein_taji" VALUES(26,32,'26:32','پس (موسی) عصای خود را افکند، که ناگهان آن اژد‌هایی آشکار شد.');
INSERT INTO "hussein_taji" VALUES(26,33,'26:33','و دست خود را (در گریبان فرو برد و) بیرون آورد که ناگهان در برابر بینندگان سفید (و روشن) بود.');
INSERT INTO "hussein_taji" VALUES(26,34,'26:34','(فرعون) به اشرافی که اطراف او بودند؛ گفت: «بی‌گمان این (مرد) جادوگر دانایی است.');
INSERT INTO "hussein_taji" VALUES(26,35,'26:35','(او) می‌خواهد با سحرش شما را از سرزمین‌تان بیرون کند، پس شما چه نظر می‌دهید؟!»');
INSERT INTO "hussein_taji" VALUES(26,36,'26:36','گفتند: «(کار) او و برادرش را به تأخیر انداز، و جمع آوری کنندگان را در (همۀ) شهر‌ها بفرست».');
INSERT INTO "hussein_taji" VALUES(26,37,'26:37','تا هر جادوگر ماهر (و) دانایی را نزد تو آورند».');
INSERT INTO "hussein_taji" VALUES(26,38,'26:38','پس (سر‌انجام) جادوگران برای وعده‌گاه روز معینی گرد آورده شدند.');
INSERT INTO "hussein_taji" VALUES(26,39,'26:39','و به مردم گفته شد: «آیا شما (نیز) جمع می‌شوید،');
INSERT INTO "hussein_taji" VALUES(26,40,'26:40','تا اگر ساحران پیروز شدند، ما از آن‌ها پیروی کنیم؟!»');
INSERT INTO "hussein_taji" VALUES(26,41,'26:41','پس هنگامی‌که ساحران آمدند، به فرعون گفتند: «اگر ما پیروز شویم، آیا ما پاداشی خواهیم داشت؟!»');
INSERT INTO "hussein_taji" VALUES(26,42,'26:42','گفت: «آری، و بی‌گمان آنگاه شما از مقربان خواهید بود».');
INSERT INTO "hussein_taji" VALUES(26,43,'26:43','موسی به آن‌ها گفت: «هر چه می‌خواهید بیفکنید، بیفکنید»');
INSERT INTO "hussein_taji" VALUES(26,44,'26:44','پس آن‌ها ریسمان‌ها و عصا‌های خود را افکندند، و گفتند: «به عزت فرعون سوگند که، قطعاً ما پیروزیم».');
INSERT INTO "hussein_taji" VALUES(26,45,'26:45','پس موسی عصایش را افکند، ناگهان آنچه را به دروغ بر ساخته بودند، بلعید.');
INSERT INTO "hussein_taji" VALUES(26,46,'26:46','پس ساحران سجده‌کنان (به زمین) افتادند.');
INSERT INTO "hussein_taji" VALUES(26,47,'26:47','گفتند: «ما به پروردگار جهانیان ایمان آوردیم');
INSERT INTO "hussein_taji" VALUES(26,48,'26:48','پروردگار موسی و هارون».');
INSERT INTO "hussein_taji" VALUES(26,49,'26:49','(فرعون) گفت: «آیا پیش از آن‌که به شما اجازه دهم به او ایمان آوردید؟! مسلماً او بزرگتر شماست که سحر را به شما یاد داده است، پس به زودی خواهید دانست! دستان‌تان و پا‌هایتان را به عکس یکدیگر قطع می‌کنم، و همۀ شما را به دار می‌آویزم».');
INSERT INTO "hussein_taji" VALUES(26,50,'26:50','گفتند: «باکی نیست، ما به سوی پروردگارمان باز می‌گردیم.');
INSERT INTO "hussein_taji" VALUES(26,51,'26:51','ما امیدواریم که پروردگارمان خطاهای ما را ببخشد، که ما از نخستین ایمان آورندگان بودیم».');
INSERT INTO "hussein_taji" VALUES(26,52,'26:52','و به موسی وحی کردیم که: «شبانه بندگانم را (از مصر) بیرون ببر، بی‌گمان شما تعقیب می‌شوید».');
INSERT INTO "hussein_taji" VALUES(26,53,'26:53','پس فرعون (چون از ماجرا آگاه گردید) گرد آورندگان (نیرو) را به شهرها فرستاد.');
INSERT INTO "hussein_taji" VALUES(26,54,'26:54','(و گفت:) این‌ها (= بنی اسرائیل) گروهی اندک (و ناچیز) هستند،');
INSERT INTO "hussein_taji" VALUES(26,55,'26:55','و این‌ها ما را به خشم آورده‌اند،');
INSERT INTO "hussein_taji" VALUES(26,56,'26:56','و ما همگی آماده (و بیدار) هستیم».');
INSERT INTO "hussein_taji" VALUES(26,57,'26:57','پس آن‌ها را از باغ‌ها و چشمه‌‌ها بیرون کردیم.');
INSERT INTO "hussein_taji" VALUES(26,58,'26:58','و (نیز) از گنج‌ها و منزل‌گاه نیکو (و قصر‌های مجلل بیرون راندیم).');
INSERT INTO "hussein_taji" VALUES(26,59,'26:59','(آری) این چنین (کردیم) و بنی اسرائیل را وارث آن‌ها ساختیم.');
INSERT INTO "hussein_taji" VALUES(26,60,'26:60','پس آن‌ها (صبحگاهان به) هنگام طلوع آفتاب به تعقیب‌شان پرداختند.');
INSERT INTO "hussein_taji" VALUES(26,61,'26:61','پس چون دو گروه یکدیگر را دیدند، یاران موسی گفتند: «یقیناً ما (در چنگال فرعونیان) گرفتار شدیم».');
INSERT INTO "hussein_taji" VALUES(26,62,'26:62','(موسی) گفت: «چنین نیست، بی‌گمان پروردگارم با من است، به زودی مرا هدایت خواهد کرد»');
INSERT INTO "hussein_taji" VALUES(26,63,'26:63','پس به موسی وحی کردیم: «عصایت را به دریا بزن» آنگاه (دریا) شکافته شد، پس هر بخشی (از آن) همچون کوه عظیمی شد.');
INSERT INTO "hussein_taji" VALUES(26,64,'26:64','و دیگران (= فرعون و فرعونیان) را (نیز) به آنجا نزدیک ساختیم.');
INSERT INTO "hussein_taji" VALUES(26,65,'26:65','و موسی و همراهانش را همگی نجات دادیم.');
INSERT INTO "hussein_taji" VALUES(26,66,'26:66','سپس دیگران را غرق کردیم.');
INSERT INTO "hussein_taji" VALUES(26,67,'26:67','بی‌گمان در این (ماجرا) نشانه‌ای (روشن) است، ولی بیشتر مردم ایمان نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(26,68,'26:68','و بی‌تردید پرورگارتو پیروزمند مهربان است.');
INSERT INTO "hussein_taji" VALUES(26,69,'26:69','و خبر ابراهیم را بر آن‌ها بخوان.');
INSERT INTO "hussein_taji" VALUES(26,70,'26:70','هنگامی‌که به پدر و قومش گفت: «چه چیز را می‌پرستید؟!».');
INSERT INTO "hussein_taji" VALUES(26,71,'26:71','گفتند: «بت‌هایی را می‌پرستیم، و پیوسته برای (عبادت) آن‌ها معتکف هستیم.');
INSERT INTO "hussein_taji" VALUES(26,72,'26:72','(ابراهیم) گفت: «آیا هنگامی‌که آن‌ها را می‌خوانید (صدای) شما را می‌شنوند؟!');
INSERT INTO "hussein_taji" VALUES(26,73,'26:73','یا سود و زیانی به شما می‌رسانند؟!».');
INSERT INTO "hussein_taji" VALUES(26,74,'26:74','گفتند: «(نه) بلکه ما نیاکان خود را یافتیم که چنین می‌کردند».');
INSERT INTO "hussein_taji" VALUES(26,75,'26:75','(ابراهیم) گفت: «آیا دیدید چیزهایی را که پیوسته عبادت می‌کردید،');
INSERT INTO "hussein_taji" VALUES(26,76,'26:76','شما و نیاکان پیشین شما.');
INSERT INTO "hussein_taji" VALUES(26,77,'26:77','پس همۀ آن‌ها دشمن من هستند، جز پروردگار جهانیان.');
INSERT INTO "hussein_taji" VALUES(26,78,'26:78','(همان) کسی‌که مرا آفرید، پس او مرا هدایت می‌کند.');
INSERT INTO "hussein_taji" VALUES(26,79,'26:79','و کسی‌که او به من غذا می‌دهد و سیراب می‌نماید.');
INSERT INTO "hussein_taji" VALUES(26,80,'26:80','و هنگامی‌که بیمار شوم پس او مرا شفا می‌دهد.');
INSERT INTO "hussein_taji" VALUES(26,81,'26:81','و کسی‌که مرا می‌میراند؛ سپس زنده می‌کند.');
INSERT INTO "hussein_taji" VALUES(26,82,'26:82','و کسی‌که امیدوارم گناهانم را در روز جزا برایم ببخشد.');
INSERT INTO "hussein_taji" VALUES(26,83,'26:83','پروردگارا! به من (دانش و) حکمت ببخش، و مرا به صالحان ملحق کن.');
INSERT INTO "hussein_taji" VALUES(26,84,'26:84','و برای من در میان آیندگان آوازۀ نیک (و ذکر خیر) قرار بده.');
INSERT INTO "hussein_taji" VALUES(26,85,'26:85','و مرا از وارثان بهشت پر نعمت بگردان.');
INSERT INTO "hussein_taji" VALUES(26,86,'26:86','و پدرم را بیامرز، بی‌گمان او از گمراهان بود.');
INSERT INTO "hussein_taji" VALUES(26,87,'26:87','و در روزی‌که (همۀ مردم) برانگیخته می‌شوند، مرا رسوا (و شرمنده) مکن.');
INSERT INTO "hussein_taji" VALUES(26,88,'26:88','روزی‌که مال و فرزندان سودی نبخشد.');
INSERT INTO "hussein_taji" VALUES(26,89,'26:89','مگر کسی‌که با قلب سلیم (خالی از شرک و کفر و نفاق) به پیشگاه الله بیاید».');
INSERT INTO "hussein_taji" VALUES(26,90,'26:90','و (در آن روز) بهشت برای پرهیزگاران نزدیک آورده شود.');
INSERT INTO "hussein_taji" VALUES(26,91,'26:91','و دوزخ برای گمراهان آشکار کرده شود.');
INSERT INTO "hussein_taji" VALUES(26,92,'26:92','و به آن‌ها گفته می‌شود: «کجا هستند آنچه که به جای الله پرستش می‌کردید؟!');
INSERT INTO "hussein_taji" VALUES(26,93,'26:93','آیا آن‌ها شما را یاری می‌کنند، یا (خود) انتقام می‌گیرند؟!');
INSERT INTO "hussein_taji" VALUES(26,94,'26:94','پس آن‌ها و گمراهان (به رو) در آن افکنده می‌شوند.');
INSERT INTO "hussein_taji" VALUES(26,95,'26:95','و (نیز) همۀ لشکریان ابلیس.');
INSERT INTO "hussein_taji" VALUES(26,96,'26:96','آن‌ها در آنجا در حالی‌که با یکدیگر مخاصمه می‌کنند، گویند:');
INSERT INTO "hussein_taji" VALUES(26,97,'26:97','«به الله سوگند که ما در گمراهی آشکار بودیم.');
INSERT INTO "hussein_taji" VALUES(26,98,'26:98','چون شما را با پروردگار جهانیان برابر می‌دانستیم.');
INSERT INTO "hussein_taji" VALUES(26,99,'26:99','و کسی جز مجرمان ما را گمراه نکرد.');
INSERT INTO "hussein_taji" VALUES(26,100,'26:100','پس (اکنون) هیچ شفاعت کنندگانی برای ما نیست.');
INSERT INTO "hussein_taji" VALUES(26,101,'26:101','و نه هیچ دوست (مخلص و) مهربانی.');
INSERT INTO "hussein_taji" VALUES(26,102,'26:102','پس ای کاش بار دیگر (به دنیا) باز گردیم، تا از مؤمنان باشیم».');
INSERT INTO "hussein_taji" VALUES(26,103,'26:103','بی‌گمان در این (ماجرا) نشانه‌ای (روشن) است، و بیشتر آن‌ها مؤمن نبودند.');
INSERT INTO "hussein_taji" VALUES(26,104,'26:104','و بی‌تردید پروردگار تو پیروزمند مهربان است.');
INSERT INTO "hussein_taji" VALUES(26,105,'26:105','قوم نوح پیامبران را تکذیب کردند.');
INSERT INTO "hussein_taji" VALUES(26,106,'26:106','هنگامی‌که برادرشان نوح به آن‌ها گفت: «آیا (از الله) نمی‌ترسید؟!».');
INSERT INTO "hussein_taji" VALUES(26,107,'26:107','مسلماً من برای شما پیامبری امین هستم.');
INSERT INTO "hussein_taji" VALUES(26,108,'26:108','پس از الله بترسید، و مرا اطاعت کنید.');
INSERT INTO "hussein_taji" VALUES(26,109,'26:109','و من بر (رساندن) این (دعوت) هیچ مزدی از شما نمی‌طلبم، مزد من تنها بر (عهدۀ) پروردگار جهانیان است.');
INSERT INTO "hussein_taji" VALUES(26,110,'26:110','پس از الله بترسید، و مرا اطاعت کنید».');
INSERT INTO "hussein_taji" VALUES(26,111,'26:111','گفتند: «آیا به تو ایمان بیاوریم در حالی‌که فرومایگان از تو پیروی کرده‌اند؟!»');
INSERT INTO "hussein_taji" VALUES(26,112,'26:112','(نوح) گفت: «من چه می‌دانم آن‌ها چه کار‌ها کرده‌اند!');
INSERT INTO "hussein_taji" VALUES(26,113,'26:113','اگر می‌فهمید، حساب آن‌ها تنها با پروردگار من است.');
INSERT INTO "hussein_taji" VALUES(26,114,'26:114','و من (هرگز) مؤمنان را طرد نخواهم کرد.');
INSERT INTO "hussein_taji" VALUES(26,115,'26:115','من جز بیم دهندۀ آشکار نیستم».');
INSERT INTO "hussein_taji" VALUES(26,116,'26:116','گفتند: «ای نوح! اگر (از دعوتت) دست بر نداری، حتماً سنگسار خواهی شد».');
INSERT INTO "hussein_taji" VALUES(26,117,'26:117','(نوح) گفت: «پروردگارا! همانا قوم من، مرا تکذیب کردند.');
INSERT INTO "hussein_taji" VALUES(26,118,'26:118','پس میان من و آن‌ها داوری کن، و مرا و مؤمنانی را که با من هستند؛ نجات بده».');
INSERT INTO "hussein_taji" VALUES(26,119,'26:119','پس او و کسانی را که همراه او بودند، در (آن) کشتی پر (و انباشته) نجات دادیم.');
INSERT INTO "hussein_taji" VALUES(26,120,'26:120','سپس بقیه را غرق کردیم.');
INSERT INTO "hussein_taji" VALUES(26,121,'26:121','بی‌گمان در این (ماجرا) نشانه‌ای (روشن) است، و بیشتر آن‌ها مؤمن نبودند.');
INSERT INTO "hussein_taji" VALUES(26,122,'26:122','و بی‌تردید پروردگار تو پیروزمند مهربان است.');
INSERT INTO "hussein_taji" VALUES(26,123,'26:123','(قوم) عاد (نیز) پیامبران (الله) را تکذیب کردند.');
INSERT INTO "hussein_taji" VALUES(26,124,'26:124','هنگامی‌که برادر‌شان هود به آن‌ها گفت: «آیا (از الله) نمی‌ترسید؟!»');
INSERT INTO "hussein_taji" VALUES(26,125,'26:125','به راستی من برای شما پیامبری امین هستم.');
INSERT INTO "hussein_taji" VALUES(26,126,'26:126','پس از الله بترسید، و مرا اطاعت کنید.');
INSERT INTO "hussein_taji" VALUES(26,127,'26:127','و من بر (رساندن) این (دعوت) هیچ مزدی از شما نمی‌طلبم، مزد من تنها بر (عهدۀ) پرودگار جهانیان است.');
INSERT INTO "hussein_taji" VALUES(26,128,'26:128','آیا بر هر مکان مرتفعی نشانه‌ای (از روی هوا و هوس و) بیهوده می‌سازید؟!');
INSERT INTO "hussein_taji" VALUES(26,129,'26:129','و قصر‌ها (و قلعه‌های) محکم و استوار بنا می‌کنید، گویی که (در دنیا) جاودانه می‌مانید.');
INSERT INTO "hussein_taji" VALUES(26,130,'26:130','و چون (کسی را) کیفر دهید، همچون جباران کیفر می‌دهید.');
INSERT INTO "hussein_taji" VALUES(26,131,'26:131','پس از الله بترسید و مرا اطاعت کنید.');
INSERT INTO "hussein_taji" VALUES(26,132,'26:132','و از کسی بترسید که شما را به آنچه می‌دانید مدد کرده است.');
INSERT INTO "hussein_taji" VALUES(26,133,'26:133','شما را به چهارپایان و فرزندان مدد (و یاری) فرموده است.');
INSERT INTO "hussein_taji" VALUES(26,134,'26:134','و (همچنین به) باغ‌ها و چشمه‌‌ها.');
INSERT INTO "hussein_taji" VALUES(26,135,'26:135','همانا من از عذاب روزی بزرگ بر شما می‌ترسم!»');
INSERT INTO "hussein_taji" VALUES(26,136,'26:136','گفتند: «برای ما یکسان است، چه ما را پند دهی یا پند ندهی.');
INSERT INTO "hussein_taji" VALUES(26,137,'26:137','این جز (همان) روش پیشینیان نیست.');
INSERT INTO "hussein_taji" VALUES(26,138,'26:138','و ما (هرگز) عذاب نخواهیم شد».');
INSERT INTO "hussein_taji" VALUES(26,139,'26:139','پس او (= هود) را تکذیب کردند، در نتیجه ما آن‌ها را هلاک کردیم، بی‌گمان در این (ماجرا) نشانه‌ای است، و بیشتر آن‌ها مؤمن نبودند.');
INSERT INTO "hussein_taji" VALUES(26,140,'26:140','و بی‌تردید پروردگار تو پیروزمند مهربان است.');
INSERT INTO "hussein_taji" VALUES(26,141,'26:141','(قوم) ثمود پیامبران (الله) را تکذیب کردند.');
INSERT INTO "hussein_taji" VALUES(26,142,'26:142','هنگامی‌که برادر‌شان صالح به آن‌ها گفت: «آیا (از الله) نمی‌ترسید؟!');
INSERT INTO "hussein_taji" VALUES(26,143,'26:143','بی‌گمان من برای شما پیامبری امین هستم.');
INSERT INTO "hussein_taji" VALUES(26,144,'26:144','پس از الله بترسید و مرا اطاعت کنید.');
INSERT INTO "hussein_taji" VALUES(26,145,'26:145','و من بر (رساندن) این (دعوت) هیچ مزدی از شما نمی‌طلبم، مزد من تنها بر (عهدۀ) پروردگار جهانیان است.');
INSERT INTO "hussein_taji" VALUES(26,146,'26:146','آیا (می پندارید که) شما در (نعمت‌‌‌هایی) اینجا ایمن رها خواهید شد؟!');
INSERT INTO "hussein_taji" VALUES(26,147,'26:147','در (این) باغ‌ها و چشمه‌ها.');
INSERT INTO "hussein_taji" VALUES(26,148,'26:148','و (در این) زراعت‌ها و نخل‌هایی که شکوفه‌هایش (نرم و) رسیده است؟!');
INSERT INTO "hussein_taji" VALUES(26,149,'26:149','و شادمانه از کوه‌ها خانه‌هایی می‌تراشید؟');
INSERT INTO "hussein_taji" VALUES(26,150,'26:150','پس از الله بترسید و مرا اطاعت کنید.');
INSERT INTO "hussein_taji" VALUES(26,151,'26:151','و فرمان اسراف کاران را اطاعت نکنید.');
INSERT INTO "hussein_taji" VALUES(26,152,'26:152','(همان) کسانی‌که در زمین فساد می‌کنند و اصلاح نمی‌کنند».');
INSERT INTO "hussein_taji" VALUES(26,153,'26:153','گفتند: «(ای صالح) جز این نیست که تو از جادوشدگانی.');
INSERT INTO "hussein_taji" VALUES(26,154,'26:154','تو جز بشری همانند ما نیستی، پس اگر از راستگویانی نشانه‌ای بیاور».');
INSERT INTO "hussein_taji" VALUES(26,155,'26:155','(صالح) گفت: «این ماده شتری است، که برای او یک نوبت آب خوردن، و برای شما (نیز) نوبت روز معینی است.');
INSERT INTO "hussein_taji" VALUES(26,156,'26:156','و (کمترین) آزاری به آن نرسانید، که عذاب روزی بزرگ شما را فرو می‌گیرد».');
INSERT INTO "hussein_taji" VALUES(26,157,'26:157','پس آن را پی کردند، آنگاه (از کردۀ خود) پشیمان شدند.');
INSERT INTO "hussein_taji" VALUES(26,158,'26:158','پس عذاب (الهی) آن‌ها را فرو گرفت، بی‌گمان در این (ماجرا) نشانه‌ای است، و بیشتر آن‌ها مؤمن نبودند.');
INSERT INTO "hussein_taji" VALUES(26,159,'26:159','و بی‌تردید پروردگار تو پیروزمند مهربان است.');
INSERT INTO "hussein_taji" VALUES(26,160,'26:160','قوم لوط (نیز) پیامبران (الله) را تکذیب کردند.');
INSERT INTO "hussein_taji" VALUES(26,161,'26:161','هنگامی‌که برادر‌شان لوط به آن‌ها گفت: «آیا (از الله) نمی‌ترسید؟!');
INSERT INTO "hussein_taji" VALUES(26,162,'26:162','بی‌گمان من برای شما پیامبری امین هستم.');
INSERT INTO "hussein_taji" VALUES(26,163,'26:163','پس از الله بترسید، و مرا اطاعت کنید.');
INSERT INTO "hussein_taji" VALUES(26,164,'26:164','و من بر (رساندن) این (دعوت) هیچ مزدی از شما نمی‌طلبم، مزد من تنها بر (عهدۀ) پروردگار جهانیان است.');
INSERT INTO "hussein_taji" VALUES(26,165,'26:165','آیا در میان جهانیان شما به سراغ مردان می‌روید؟!');
INSERT INTO "hussein_taji" VALUES(26,166,'26:166','و همسرانی‌که الله برای شما آفریده است؛ رها می‌کنید، بلکه شما قومی متجاوز هستید.');
INSERT INTO "hussein_taji" VALUES(26,167,'26:167','گفتند: «ای لوط، اگر دست بر نداری، یقیناً از اخراج شدگان خواهی بود».');
INSERT INTO "hussein_taji" VALUES(26,168,'26:168','(لوط) گفت: «من دشمن سرسخت عمل شما هستم.');
INSERT INTO "hussein_taji" VALUES(26,169,'26:169','پروردگارا! من و خانواده ام را از (عاقبت) آنچه (این‌ها) انجام می‌دهند، نجات بده».');
INSERT INTO "hussein_taji" VALUES(26,170,'26:170','پس او و خانواده‌اش را همگی نجات دادیم.');
INSERT INTO "hussein_taji" VALUES(26,171,'26:171','مگر پیر زنی که در میان بازماندگان بود.');
INSERT INTO "hussein_taji" VALUES(26,172,'26:172','سپس دیگران را نابود ساختیم.');
INSERT INTO "hussein_taji" VALUES(26,173,'26:173','و بر آن‌ها بارانی (از سنگ) بارانیدیم، پس باران بیم داده شدگان چه بد بود.');
INSERT INTO "hussein_taji" VALUES(26,174,'26:174','بی‌گمان در این (ماجرا) نشانه‌ای است، و بیشتر آن‌ها مؤمن نبودند.');
INSERT INTO "hussein_taji" VALUES(26,175,'26:175','و بی‌تردید پروردگار تو پیروزمند مهربان است.');
INSERT INTO "hussein_taji" VALUES(26,176,'26:176','اصحاب ایکه (= قوم شعیب) پیامبران (الله) را تکذیب کردند.');
INSERT INTO "hussein_taji" VALUES(26,177,'26:177','هنگامی‌که شعیب به آن‌ها گفت: «آیا (از الله) نمی‌ترسید؟!');
INSERT INTO "hussein_taji" VALUES(26,178,'26:178','همانا من برای شما پیامبری امین هستم.');
INSERT INTO "hussein_taji" VALUES(26,179,'26:179','پس از الله بترسید، و مرا اطاعت کنید.');
INSERT INTO "hussein_taji" VALUES(26,180,'26:180','و من بر (رساندن) این (دعوت) هیچ مزدی از شما نمی‌طلبم، مزد من تنها بر (عهدۀ) پروردگار جهانیان است.');
INSERT INTO "hussein_taji" VALUES(26,181,'26:181','پیمانه را تمام بدهید، و از کاهندگان (و کم فروشان) نباشید.');
INSERT INTO "hussein_taji" VALUES(26,182,'26:182','و با ترازوی درست وزن کنید.');
INSERT INTO "hussein_taji" VALUES(26,183,'26:183','و چیز‌های مردم را کم ندهید (و حق آن‌ها را ضایع نکنید) و در زمین به فساد نکوشید.');
INSERT INTO "hussein_taji" VALUES(26,184,'26:184','و از کسی‌که شما و آفریدگان نخستین را آفرید بترسید.');
INSERT INTO "hussein_taji" VALUES(26,185,'26:185','گفتند: «(ای شعیب!) جز این نیست که تو از جادو شدگانی،');
INSERT INTO "hussein_taji" VALUES(26,186,'26:186','تو جز بشری همانند ما نیستی، و تو را از دروغگویان می‌پنداریم.');
INSERT INTO "hussein_taji" VALUES(26,187,'26:187','پس اگر از راستگویانی، پاره‌ای از آسمان را بر (سر) ما بینداز»');
INSERT INTO "hussein_taji" VALUES(26,188,'26:188','(شعیب) گفت: «پروردگارم! به آنچه انجام می‌دهید؛ داناتر است.');
INSERT INTO "hussein_taji" VALUES(26,189,'26:189','پس او را تکذیب کردند، آنگاه عذاب روز سایه آن‌ها را فرو گرفت، بی‌گمان آن عذاب روز بزرگی بود.');
INSERT INTO "hussein_taji" VALUES(26,190,'26:190','بی‌گمان در این (ماجرا) نشانه‌ای است، و بیشتر آن‌ها مؤمن نبودند.');
INSERT INTO "hussein_taji" VALUES(26,191,'26:191','و بی‌تردید پروردگار تو پیروزمند مهربان است.');
INSERT INTO "hussein_taji" VALUES(26,192,'26:192','و همانا این (قرآن) نازل شده (از جانب) پروردگار جهانیان است.');
INSERT INTO "hussein_taji" VALUES(26,193,'26:193','روح الامین (= جبرئیل) آن را فرود آورده است.');
INSERT INTO "hussein_taji" VALUES(26,194,'26:194','بر قلب تو، تا از هشدار دهندگان باشی.');
INSERT INTO "hussein_taji" VALUES(26,195,'26:195','(آن را) به زبان عربی روشن (نازل کرد).');
INSERT INTO "hussein_taji" VALUES(26,196,'26:196','و همانا (توصیف) آن در کتاب‌های پیشینیان (نیز آمده) است.');
INSERT INTO "hussein_taji" VALUES(26,197,'26:197','آیا همین نشانه برای آن‌ها کافی نیست که علمای بنی اسرائیل [ مانند عبدالله بن سلام رضی الله عنه و دیگران. (تفسیر ابن کثیر)] از آن (قرآن) آگاهند؟!');
INSERT INTO "hussein_taji" VALUES(26,198,'26:198','و اگر آن را بر بعضی از عجمیان (= غیر عرب‌ها) نازل می‌کردیم.');
INSERT INTO "hussein_taji" VALUES(26,199,'26:199','پس او آن را برایشان می‌خواند، به آن ایمان نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(26,200,'26:200','(آری) این گونه آن (تکذیب) را در دل‌های مجرمان در آوردیم.');
INSERT INTO "hussein_taji" VALUES(26,201,'26:201','به آن ایمان نمی‌آورند تا عذاب دردناک را (با چشم خود) ببینند.');
INSERT INTO "hussein_taji" VALUES(26,202,'26:202','پس ناگهان در حالی‌که بی‌خبرند به سراغ آن‌ها می‌آید.');
INSERT INTO "hussein_taji" VALUES(26,203,'26:203','آنگاه می‌گویند: «آیا مهلتی به ما داده خواهد شد؟!»');
INSERT INTO "hussein_taji" VALUES(26,204,'26:204','آیا برای عذاب ما شتاب می‌کنند؟!');
INSERT INTO "hussein_taji" VALUES(26,205,'26:205','آیا دیدی که اگر سالیانی آن‌ها را (از این زندگی دنیا) بهره‌مند سازیم.');
INSERT INTO "hussein_taji" VALUES(26,206,'26:206','سپس آنچه که به آن‌ها وعده داده شده است به سراغ‌شان بیاید.');
INSERT INTO "hussein_taji" VALUES(26,207,'26:207','این بهره‌مندیشان (از دنیا) سودی برای آن‌ها نخواهد داشت.');
INSERT INTO "hussein_taji" VALUES(26,208,'26:208','و هیچ قریه‌ای را هلاک نکردیم؛ مگر این‌که هشدار دهندگانی (از پیامبران) داشتند.');
INSERT INTO "hussein_taji" VALUES(26,209,'26:209','برای پند دادن، و ما هرگز ستمکار نبودیم.');
INSERT INTO "hussein_taji" VALUES(26,210,'26:210','و شیطان‌ها (هرگز) این (قرآن) را نازل نکرده‌اند.');
INSERT INTO "hussein_taji" VALUES(26,211,'26:211','و برای آن‌ها سزاوار نیست؛ و نمی‌توانند.');
INSERT INTO "hussein_taji" VALUES(26,212,'26:212','بی‌گمان آن‌ها از شنیدن (وحی، و استراق سمع) برکنارند.');
INSERT INTO "hussein_taji" VALUES(26,213,'26:213','پس (ای پیامبر!) هیچ معبودی را با الله مخوان، که از عذاب شدگان خواهی بود.');
INSERT INTO "hussein_taji" VALUES(26,214,'26:214','و خویشاوندان نزدیکت را هشدار بده.');
INSERT INTO "hussein_taji" VALUES(26,215,'26:215','و بال (رحمت و فروتنی) خود را برای مؤمنانی‌که از تو پیروی می‌کنند؛ بگستر.');
INSERT INTO "hussein_taji" VALUES(26,216,'26:216','پس اگر از (فرمان) تو سرپیچی کردند، بگو: «همانا من از آنچه انجام می‌دهید؛ بیزارم».');
INSERT INTO "hussein_taji" VALUES(26,217,'26:217','و بر الله پیروزمند مهربان توکل کن.');
INSERT INTO "hussein_taji" VALUES(26,218,'26:218','(همان) کسی‌که تو را به هنگامی‌که (برای عبادت) بر می‌خیزی می‌بیند.');
INSERT INTO "hussein_taji" VALUES(26,219,'26:219','و (نیز) حرکت (و نشست و بر خاست) تو را میان سجده‌کنندگان (می‌بیند).');
INSERT INTO "hussein_taji" VALUES(26,220,'26:220','همانا او شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(26,221,'26:221','آیا به شما خبردهم که شیاطین بر چه کسی نازل می‌شوند؟!');
INSERT INTO "hussein_taji" VALUES(26,222,'26:222','بر هر دروغگوی گنهکار نازل می‌گردند.');
INSERT INTO "hussein_taji" VALUES(26,223,'26:223','(به شیاطین) گوش فرا می‌دهند و بیشترشان دروغگو هستند.');
INSERT INTO "hussein_taji" VALUES(26,224,'26:224','و شاعران (کسانی) هستند که گمراهان از آن‌ها پیروی می‌کنند.');
INSERT INTO "hussein_taji" VALUES(26,225,'26:225','آیا ندیدی که آن‌ها در هر وادیی سرگشته (و حیران) هستند؟!');
INSERT INTO "hussein_taji" VALUES(26,226,'26:226','و بی‌گمان آن‌ها چیزی می‌گویند که (خود به آن) عمل نمی‌کنند؟!');
INSERT INTO "hussein_taji" VALUES(26,227,'26:227','مگر کسانی‌که ایمان آوردند و کارهای شایسته انجام دادند و الله را بسیار یاد کردند، و بعد از آن که ستم دیدند، انتقام گرفتند، (و با شعر خود از اسلام و مسلمین دفاع نمودند)، و کسانی‌که ستم کردند؛ به زودی خواهند دانست به چه بازگشتگاهی باز می‌گردند.');
INSERT INTO "hussein_taji" VALUES(27,1,'27:1','طس (طا. سین) این آیات قرآن و کتاب مبین است.');
INSERT INTO "hussein_taji" VALUES(27,2,'27:2','که هدایت و بشارت برای مؤمنان است.');
INSERT INTO "hussein_taji" VALUES(27,3,'27:3','(همان) کسانی‌که نماز را بر پا می‌دارند، و زکات را ادا می‌کنند، و آنان به آخرت یقین دارند.');
INSERT INTO "hussein_taji" VALUES(27,4,'27:4','بی‌گمان کسانی‌که به آخرت ایمان نمی‌آورند، اعمال‌شان را برای آن‌ها زینت داده‌ایم، پس آن‌ها سرگردان می‌شوند.');
INSERT INTO "hussein_taji" VALUES(27,5,'27:5','آن‌ها کسانی هستند که عذاب بد (و درد ناک) برایشان است، و آن‌ها در آخرت زیانکارترینند.');
INSERT INTO "hussein_taji" VALUES(27,6,'27:6','و بی‌تردید تو (این) قرآن را از سوی (الله) حکیم دانا دریافت می‌داری.');
INSERT INTO "hussein_taji" VALUES(27,7,'27:7','(به یاد بیاور) هنگامی را که موسی به خانوادۀ خود گفت: «(بایستید) همانا من آتشی (از دور) دیده‌ام، به‌زودی خبری از آن برای شما می‌آورم یا شعلۀ آتشی از آن می‌آورم تا گرم شوید».');
INSERT INTO "hussein_taji" VALUES(27,8,'27:8','پس چون به آن (آتش) رسید، ندا داده شد که: «مبارک باد کسی‌که در آتش است و کسی‌که پیرامون آن است. و منزه است الله که پروردگار جهانیان است.');
INSERT INTO "hussein_taji" VALUES(27,9,'27:9','ای موسی! همانا من الله پیروزمند حکیم هستم.');
INSERT INTO "hussein_taji" VALUES(27,10,'27:10','و عصایت را بیفکن- پس چون آن را دید که (با سرعت) می‌جنبد، گویی که آن ماری است، (ترسید و) به عقب برگشت، و پشت سر خود نگاه نکرد- ای موسی! نترس، که پیامبران در نزد من نمی‌ترسند.');
INSERT INTO "hussein_taji" VALUES(27,11,'27:11','مگر کسی‌که ستم کند، پس (توبه کند و) بدی را به نیکی تبدیل نماید، پس بی‌گمان من آمرزندۀ مهربان هستم.');
INSERT INTO "hussein_taji" VALUES(27,12,'27:12','و دستت را در گریبانت فرو کن، که سفید (و درخشندۀ) بی‌عیب بیرون بیاید، (این معجزه) در زمرۀ معجزات نه‌گانه است، به‌سوی فرعون و قومش (برو) که آن‌ها قومی فاسقند».');
INSERT INTO "hussein_taji" VALUES(27,13,'27:13','پس چون آیات روشن (و کاملاً آشکار) ما به سراغ آن‌ها آمد؛ گفتند: «این جادویی آشکار است».');
INSERT INTO "hussein_taji" VALUES(27,14,'27:14','و آن را از روی ستم و سرکشی انکار کردند، در حالی‌که دل‌هایشان به آن یقین داشت، پس بنگر عاقبت مفسدان چگونه بود.');
INSERT INTO "hussein_taji" VALUES(27,15,'27:15','و به‌راستی به داوود و سلیمان دانش (عظیمی) دادیم، و (آنان) گفتند: «ستایش از آن الله است که ما را بر بسیاری از بندگان مؤمن خود برتری بخشید».');
INSERT INTO "hussein_taji" VALUES(27,16,'27:16','و سلیمان وارث داوود شد، و گفت: «ای مردم! زبان پرندگان به ما آموخته شده، و از هر چیز به ما عطا شده، بی‌تردید این فضل آشکاری است».');
INSERT INTO "hussein_taji" VALUES(27,17,'27:17','و برای سلیمان لشکریانش از جن و انس و پرندگان گرد آورده شدند، آنگاه نگاه داشته شدند (تا همۀ آن‌ها به یکدیگر بپیوندند).');
INSERT INTO "hussein_taji" VALUES(27,18,'27:18','(بعد حرکت کردند) تا هنگامی‌که به وادی مورچگان رسیدند، مورچه‌ای گفت: «ای مورچگان! به لانه‌های خود بروید تا سلیمان و لشکریانش شما را پایمال نکنند در حالی‌که نمی‌فهمند».');
INSERT INTO "hussein_taji" VALUES(27,19,'27:19','پس (سلیمان) از سخن آن (مورچه) تبسمی کرد (و) خندید، گفت: «پروردگارا! به من توفیق ده تا شکر نعمت‌هایی را که بر من و بر پدر و مادرم ارزانی داشتۀ بجای آورم، و (توفیق عطا فرما تا) کار(های) شایسته‌ای که تو خشنود شوی، انجام دهم، و مرا به رحمت خود در (زمرۀ) بندگان صالحت در آور».');
INSERT INTO "hussein_taji" VALUES(27,20,'27:20','و (سلیمان) از حال پرندگان جویا شد، و گفت: «مرا چه شده است که هدهد را نمی‌بینم، یا اینکه او از غایبان است؟!');
INSERT INTO "hussein_taji" VALUES(27,21,'27:21','قطعاً او را به کیفر شدید کیفر خواهم داد، یا او را ذبح خواهم کرد، یا (باید) دلیلی روشن (برای غیبتش) برای من بیاورد».');
INSERT INTO "hussein_taji" VALUES(27,22,'27:22','پس درنگش چندان طول نکشید (که هدهد آمد) آنگاه گفت: «من به چیزی دست یافته‌ام که تو به آن دست نیافته‌ای، و از (سرزمین) سبا برایت خبر قطعی آورده‌ام.');
INSERT INTO "hussein_taji" VALUES(27,23,'27:23','من زنی را یافتم که بر آن‌ها فرمانروایی می‌کند، و (به او) از هرگونه نعمتی داده شده است، و تخت عظیمی دارد.');
INSERT INTO "hussein_taji" VALUES(27,24,'27:24','او و قومش را دیدم که به جای الله برای خورشید سجده می‌کنند، و شیطان اعمال آن‌ها را در نظر‌‌‌‌‌شان آراسته پس آن‌ها را از راه (الله) باز داشته است، از این رو آن‌ها هدایت نمی‌شوند».');
INSERT INTO "hussein_taji" VALUES(27,25,'27:25','چرا برای الله سجده نمی‌کنند که آنچه را در آسمان‌ها و زمین پنهان است بیرون می‌آورد، و آنچه را پنهان می‌دارید و آنچه را آشکار می‌کنید (همه را) می‌داند.');
INSERT INTO "hussein_taji" VALUES(27,26,'27:26','الله که جز او معبودی نیست، پروردگار عرش عظیم است.');
INSERT INTO "hussein_taji" VALUES(27,27,'27:27','(سلیمان) گفت: «اکنون خواهیم دید که آیا راست گفته‌ای یا از دروغ‌گویان هستی.');
INSERT INTO "hussein_taji" VALUES(27,28,'27:28','این نامۀ مرا ببر، و بر آن‌ها بیفکن، سپس از آن‌ها روی بگردان، پس (در گوشه‌ای توقف کن) بنگر که چه جواب می‌دهند».');
INSERT INTO "hussein_taji" VALUES(27,29,'27:29','(ملکۀ سبا) گفت: «ای بزرگان، به راستی نامۀ پر ارزشی به سوی من افکنده شده!');
INSERT INTO "hussein_taji" VALUES(27,30,'27:30','همانا آن (نامه) از سلیمان است، و چنین است: به نام الله بخشندۀ مهربان.');
INSERT INTO "hussein_taji" VALUES(27,31,'27:31','بر من برتری مجویید، و تسلیم شده نزد من بیایید»');
INSERT INTO "hussein_taji" VALUES(27,32,'27:32','(ملکه) گفت: «ای بزرگان، در کار من رأی دهید، که من هیچ کار مهمی را بدون حضور (و مشورت) شما تصمیم نگرفته‌ام (و انجام نداده‌ام)»');
INSERT INTO "hussein_taji" VALUES(27,33,'27:33','گفتند: «ما دارای نیروی کافی و جنگ‌جویانِ با قدرتی هستیم، و اختیار (و تصمیم نهایی) با توست، پس بنگر که چه فرمان می‌دهی».');
INSERT INTO "hussein_taji" VALUES(27,34,'27:34','گفت: «بی‌تردید پادشا‌‌‌هان هنگامی‌که وارد قریه‌ای شوند آن را ویران (و تباه) سازند، و عزیزانش را ذلیل می‌کنند، و این گونه رفتار می‌کنند.');
INSERT INTO "hussein_taji" VALUES(27,35,'27:35','و همانا من هدیه‌ای برای آن‌ها می‌فرستم، پس می‌نگرم که فرستادگان چه جواب می‌آورند».');
INSERT INTO "hussein_taji" VALUES(27,36,'27:36','پس چون (فرستادۀ ملکه) نزد سلیمان آمد، (سلیمان) گفت: «آیا می‌خواهید مرا با مال یاری کنید؟! آنچه الله به من داده، بهتر است از آنچه به شما داده است. بلکه شما به هدیۀتان شادمان می‌شوید.');
INSERT INTO "hussein_taji" VALUES(27,37,'27:37','(اکنون) به سوی آن‌ها باز گرد، پس البته با لشکریانی به سوی آن‌ها خواهیم آمد که قدرت مقابله با آن را نداشته باشند، و بی‌تردید آن‌ها را از آن (سرزمین) با ذلت و خواری بیرون می‌کنیم!»');
INSERT INTO "hussein_taji" VALUES(27,38,'27:38','(سلیمان) گفت: «ای بزرگان! کدام‌یک از شما تخت او را برای من می‌آورد، پیش از آن‌که تسلیم شده نزد من آیند؟»');
INSERT INTO "hussein_taji" VALUES(27,39,'27:39','عفریتی از جن گفت: «من آن را نزد تو می‌آورم پیش از آنکه از جایت برخیزی، و همانا من بر آن توانای امین هستم».');
INSERT INTO "hussein_taji" VALUES(27,40,'27:40','کسی‌که دانشی از کتاب (الهی) داشت گفت: «من پیش از آن‌که چشم بر هم زنی، آن را نزد تو می‌آورم». پس چون (سلیمان) آن (تخت) را نزد خود مستقر دید؛ گفت: «این از فضل پروردگار من است، تا مرا بیازماید که آیا شکر او را به جای می‌آورم یا ناسپاسی می‌کنم!» پس هر که شکر کند، تنها به نفع خود شکر می‌کند، و هر که ناسپاسی کند پس (به زیان خود نموده است،) بی‌گمان پروردگار من بی‌نیاز کریم است.');
INSERT INTO "hussein_taji" VALUES(27,41,'27:41','(سلیمان) گفت: «تخت او را برایش ناشناس کنید، (تا) ببینیم آیا (آن را) باز می‌شناسد، یا از آن‌هاست که باز نتوانند شناخت.');
INSERT INTO "hussein_taji" VALUES(27,42,'27:42','پس چون (ملکۀ سبا) آمد، (به او) گفته شد: «آیا تخت تو این گونه است؟» گفت: «گویی همان است، و ما پیش از این هم (به قدرت الله و حقانیت سلیمان) دانش یافته بوده‌ایم، و مسلمان (و فرمانبردار) بودیم.');
INSERT INTO "hussein_taji" VALUES(27,43,'27:43','و آنچه به جای الله می‌پرستید او را (از ایمان به الله) باز داشته بود، بی‌گمان او (= ملکه سبا) از قوم کافران بود.');
INSERT INTO "hussein_taji" VALUES(27,44,'27:44','به او گفته شد: «به صحن (قصر) داخل شو» پس هنگامی‌که آن را دید پنداشت که آب عمیقی است، و دو ساق (پای) خود را برهنه کرد (تا از آب بگذرد، سلیمان) گفت: «(این آب نیست) این صحنی صاف از آبگینه است». (ملکه) گفت: «پروردگارا! من به خود ستم کردم، و (اینک) با سلیمان برای الله پروردگار جهانیان تسلیم شدم»');
INSERT INTO "hussein_taji" VALUES(27,45,'27:45','و به راستی به سوی (قوم) ثمود، برادر‌شان صالح را فرستادیم که: الله یگانه را بپرستید، آنگاه آن‌ها دو گروه شدند که به مخاصمه پرداختند.');
INSERT INTO "hussein_taji" VALUES(27,46,'27:46','(صالح) گفت: «ای قوم من! چرا پیش از نیکی بر بدی می‌شتابید (و بجای رحمت، عذاب الهی را می‌طلبید) چرا از الله آمرزش نمی‌خواهید، تا شاید مورد رحمت قرار گیرید؟!»');
INSERT INTO "hussein_taji" VALUES(27,47,'27:47','گفتند: «ما به تو و کسانی‌که با تو هستند فال بد زده‌ایم» (صالح) گفت: «فال بد شما نزد الله است، بلکه شما گروهی هستید که مورد آزمایش قرار می‌گیرید».');
INSERT INTO "hussein_taji" VALUES(27,48,'27:48','و در (آن) شهر (حِجر) نُه نفر بودند که در زمین فساد می‌کردند و اصلاح نمی‌کردند.');
INSERT INTO "hussein_taji" VALUES(27,49,'27:49','گفتند: «به الله سوگند یاد کنید که حتماً بر او (= صالح) و خانواده‌اش شبیخون می‌زنیم، (و آن‌ها را می‌کشیم) سپس به ولی (دم) او می‌گوییم: ما هنگام هلاکت و نابودی خانواده‌اش؛ هرگز حاضر نبودیم (و از آن‌ها خبر نداریم) و بی‌گمان ما راستگو هستیم».');
INSERT INTO "hussein_taji" VALUES(27,50,'27:50','و آن‌ها (برای قتل صالح و پیروانش) حیله و نیرنگی به کار بردند و ما (نیز) حیله و تدبیری (برای هلاک آن‌ها و نجات صالح و پیروانش) اندیشیدیم، در حالی‌که آن‌ها نمی‌دانستند.');
INSERT INTO "hussein_taji" VALUES(27,51,'27:51','پس بنگر عاقبت نیرنگ‌شان چه شد؟! ما آن‌ها و قوم‌شان همگی را نابود کردیم.');
INSERT INTO "hussein_taji" VALUES(27,52,'27:52','پس این خانه‌های آن‌هاست که به سزای ستمی که می‌کردند خالی مانده است، بی‌گمان در این (ماجرا) نشانه‌ای است برای گروهی که می‌دانند.');
INSERT INTO "hussein_taji" VALUES(27,53,'27:53','و کسانی را که ایمان آورده، و پرهیزگار بودند، نجات دادیم.');
INSERT INTO "hussein_taji" VALUES(27,54,'27:54','و لوط را (به یاد آور) هنگامی‌که به قومش گفت: «آیا کار بسیار زشت (لواط) مرتکب می‌شوید؛ در حالی‌که خود (سرانجام و زشتی آن را) می‌بینید؟!');
INSERT INTO "hussein_taji" VALUES(27,55,'27:55','آیا شما به جای زنان، از روی شهوت سراغ مردان می‌روید؟! بلکه شما مردمی نادان هستید».');
INSERT INTO "hussein_taji" VALUES(27,56,'27:56','پس جواب قومش جز این نبود که (به یکدیگر) گفتند: «خاندان لوط را از قریۀ خود بیرون کنید، که این‌ها مردمانی (پاکدامن و) پاکیزه‌جو هستند!»');
INSERT INTO "hussein_taji" VALUES(27,57,'27:57','آنگاه او و خانواده‌اش را نجات دادیم؛ مگر همسرش که مقدر کردیم او از بازماندگان (در آن شهر) باشد.');
INSERT INTO "hussein_taji" VALUES(27,58,'27:58','و بارانی (از سنگ) بر (سر) آن‌ها باراندیم، پس باران بیم داده‌شدگان چه بد است!');
INSERT INTO "hussein_taji" VALUES(27,59,'27:59','(ای پیامبر) بگو: «حمد و سپاس مخصوص الله است، و سلام بر بندگانش، کسانی‌که او (آن‌ها را) بر گزیده است». آیا الله بهتر است یا آن چیزهای که شرک می‌آورند؟');
INSERT INTO "hussein_taji" VALUES(27,60,'27:60','(آیا این معبودان باطل شما بهترند) یا کسی‌که آسمان‌ها و زمین را آفرید، و از آسمان برای شما آبی نازل کرد، پس با آن باغ‌های خرم (و زیبا) رویاندیم، که شما هرگز توان رویاندن درختان آن را نداشتید، آیا معبودی دیگری با الله است؟! (نه) بلکه آن‌ها گروهی هستند که (از حق منحرف می‌شوند و بت‌ها را) هم‌طراز (الله) قرار می‌دهند.');
INSERT INTO "hussein_taji" VALUES(27,61,'27:61','(آیا این بت‌ها بهترند) یا کسی‌که زمین را قرارگاه ساخت، و میان آن نهر‌هایی قرار داد، و برای آن کوه‌‌های (ثابت و استوار) پدید آورد، و میان دو دریا مانعی قرار داد، آیا معبود دیگری با الله است؟! (نه) بلکه بیشتر‌شان نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(27,62,'27:62','(آیا این بت‌ها بهترند) یا کسی‌که (دعای) مضطر (= درمانده) را اجابت می‌کند؛ چون او را بخواند، و گرفتاری را بر طرف می‌سازد، و شما را جانشینان زمین قرار می‌دهد، آیا معبودی دیگری با الله است؟! چه اندک پند می‌گیرید.');
INSERT INTO "hussein_taji" VALUES(27,63,'27:63','(آیا این بت‌ها بهترند) یا کسی‌که شما را در تاریکی‌های بیابان و دریا هدایت می‌کند، و کسی‌که پیش از (باران) رحمت خود باد‌ها را به مژده می‌فرستد، آیا معبودی دیگر با الله است؟! الله برتر است از آنچه برای او شریک قرار می‌دهند.');
INSERT INTO "hussein_taji" VALUES(27,64,'27:64','(آیا این معبودان باطل بهترند) یا کسی‌که آفرینش را آغاز کرد، سپس آن را (بار دیگر) باز می‌گرداند، و کسی‌که شما را از آسمان و زمین روزی می‌دهد، آیا معبود دیگری با الله است؟! (ای پیامبر!) بگو: «اگر راست می‌گویید دلیل‌تان را بیاورید».');
INSERT INTO "hussein_taji" VALUES(27,65,'27:65','بگو: «در آسمان‌ها و زمین جز الله هیچ کس غیب نمی‌داند و (آن معبودان باطل) نمی‌دانند که کی برانگیخته می‌شوند.');
INSERT INTO "hussein_taji" VALUES(27,66,'27:66','بلکه علم آن‌ها (= مشرکان) دربارۀ آخرت به پایان رسیده است (و هیچ اطلاعی ندارند) بلکه آن‌ها در (وقوع) آن شک دارند، بلکه آن‌ها نسبت به آن نابینایند.');
INSERT INTO "hussein_taji" VALUES(27,67,'27:67','و کسانی‌که کافر شدند؛ گفتند: «آیا هنگامی‌که ما و پدران مان خاک شدیم، آیا (باز زنده می‌شویم و از گور‌ها) بیرون آورده می‌شویم؟!');
INSERT INTO "hussein_taji" VALUES(27,68,'27:68','به راستی این وعده‌ای است که از پیش به ما و نیاکان‌مان داده شده است، این جز افسانه‌های پیشینیان نیست».');
INSERT INTO "hussein_taji" VALUES(27,69,'27:69','(ای پیامبر!) بگو: «در زمین سیر کنید، پس بنگرید عاقبت مجرمان چگونه بود».');
INSERT INTO "hussein_taji" VALUES(27,70,'27:70','و بر آن‌ها غمگین مباش و از آنچه مکر می‌ورزند؛ دل‌تنگ نشو.');
INSERT INTO "hussein_taji" VALUES(27,71,'27:71','و می‌گویند: «اگر راست می‌گویید، این وعده کی خواهد بود؟!»');
INSERT INTO "hussein_taji" VALUES(27,72,'27:72','(ای پیامبر!) بگو: «شاید پاره‌ای از آنچه را که به شتاب می‌طلبید به شما نزدیک شده‌باشد».');
INSERT INTO "hussein_taji" VALUES(27,73,'27:73','و بی‌گمان پروردگار تو نسبت به مردم دارای فضل (و رحمت) است، و لیکن بیشترشان شکر نمی‌گزارند.');
INSERT INTO "hussein_taji" VALUES(27,74,'27:74','و بی‌تردید پروردگار تو آنچه در سینه‌هایشان پنهان می‌دارند و آنچه را آشکار می‌کنند؛ بخوبی می‌داند.');
INSERT INTO "hussein_taji" VALUES(27,75,'27:75','و هیچ پنهانی در آسمان و زمین نیست؛ مگر آن‌که در کتاب روشن (لوح محفوظ، نوشته شده) است.');
INSERT INTO "hussein_taji" VALUES(27,76,'27:76','بی‌گمان این قرآن بر بنی اسرائیل بیشتر چیزهایی را که آن‌ها در آن اختلاف دارند، بیان می‌کند.');
INSERT INTO "hussein_taji" VALUES(27,77,'27:77','و به راستی آن (قرآن) هدایت و رحمت برای مؤمنان است.');
INSERT INTO "hussein_taji" VALUES(27,78,'27:78','مسلماً پروردگار تو میان آن‌ها (در قیامت) به حکم خود داوری می‌کند، و او پیروزمند داناست.');
INSERT INTO "hussein_taji" VALUES(27,79,'27:79','پس (ای پیامبر!) بر الله توکل کن، بی‌شک تو بر حق آشکار هستی.');
INSERT INTO "hussein_taji" VALUES(27,80,'27:80','و بی‌گمان تو نمی‌توانی (سخنت را) به (گوش) مردگان بشنوانی، و نمی‌توانی کران را هنگامی‌که پشت‌کنان روی می‌گردانند؛ سخن (و نِدای خود را) بشنوانی.');
INSERT INTO "hussein_taji" VALUES(27,81,'27:81','و تو نمی‌توانی کوران را از گمراهی‌شان (باز گردانی و) هدایت کنی، تو تنها می‌توانی (سخن خود را) به کسانی بشنوانی که به آیات ما ایمان دارند؛ پس آن‌ها (در برابر حق) تسلیم شدگان هستند.');
INSERT INTO "hussein_taji" VALUES(27,82,'27:82','و هنگامی‌که فرمان (عذاب) بر آن‌ها واقع شود (و حادثۀ قیامت نزدیک شود) جنبنده‌ای را از زمین برای آن‌ها بیرون می‌آوریم که با آن‌ها سخن گوید، که مردم به آیات ما یقین نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(27,83,'27:83','و (به یاد آور) روزی را که ما از هر امتی گروهی را از آنان که آیات ما را تکذیب می‌کردند؛ محشور می‌کنیم، آنگاه آن‌ها (به صف) نگاه داشته می‌شوند.');
INSERT INTO "hussein_taji" VALUES(27,84,'27:84','تا هنگامی‌که (به سرزمین محشر) می‌آیند (الله) می‌فرماید: «آیا مرا تکذیب کردید در حالی‌که به آن احاطۀ علمی نداشتید، یا چه (کاری) بود که می‌کردید؟!»');
INSERT INTO "hussein_taji" VALUES(27,85,'27:85','و بخاطر ستمی که کرده‌اند؛ فرمان (عذاب) بر آن‌ها واقع می‌شود، پس آن‌ها هیچ سخنی نمی‌گویند.');
INSERT INTO "hussein_taji" VALUES(27,86,'27:86','آیا ندیدند که ما شب را برای آرامش آن‌ها قرار دادیم، و روز را روشنی بخش (قرار دادیم) بی‌گمان در این (امور) نشانه‌های (روشنی) است برای گروهی که ایمان می‌آورند.');
INSERT INTO "hussein_taji" VALUES(27,87,'27:87','و (به یاد آور) روزی را که در صور دمیده می‌شود، پس (تمام) کسانی‌که در آسمان‌ها و زمین هستند وحشت کنند (و بترسند) جز کسی‌که الله بخواهد، و همگی با خواری (و ذلت) به پیشگاه او (تعالی) بیایند.');
INSERT INTO "hussein_taji" VALUES(27,88,'27:88','و کوه‌ها را می‌بینی، (و) آن‌ها را جامد (و بی‌حرکت) پنداری، در حالی‌که مانند حرکت ابر‌ها در حرکت هستند (این) صنع (و آفرینش) اللهِ است که هر چیزی را محکم (و استوار) ساخته است، بی‌گمان او از آنچه انجام می‌دهید؛ با خبر است.');
INSERT INTO "hussein_taji" VALUES(27,89,'27:89','هر کس‌که نیکی آورد، پس (پاداشی) بهتر از آن خواهد داشت، و آنان از وحشت آن روز در امان هستند.');
INSERT INTO "hussein_taji" VALUES(27,90,'27:90','و هر کس که بدی آورد، پس چهره‌های‌شان در آتش نگونسار شود، (و به آن‌ها گفته می‌شود:) آیا جزایی جز آنچه می‌کردید به شما داده می‌شود؟');
INSERT INTO "hussein_taji" VALUES(27,91,'27:91','(ای پیامبر! بگو:) من فقط مأمورم که پروردگار این شهر (مکه) را عبادت کنم، همان کسی‌که این (شهر) را حرمت بخشید، و همه چیز از آنِِ اوست، و من مأمورم که از مسلمانان باشم.');
INSERT INTO "hussein_taji" VALUES(27,92,'27:92','و این‌که قرآن را تلاوت کنم، پس هر کس هدایت شود به نفع خود هدایت شده است، و هر کس که گمراه گردد؛ بگو: «من فقط از هشدار ‌دهندگان هستم».');
INSERT INTO "hussein_taji" VALUES(27,93,'27:93','و بگو: «حمد و ستایش مخصوص الله است، به زود آیاتش را به شما نشان می‌دهد، پس آن‌ها را خواهید شناخت، و پروردگار تو از آنچه انجام می‌دهید؛ غافل نیست.');
INSERT INTO "hussein_taji" VALUES(28,1,'28:1','طسم (طا. سین. میم)');
INSERT INTO "hussein_taji" VALUES(28,2,'28:2','این آیات کتاب مبین است.');
INSERT INTO "hussein_taji" VALUES(28,3,'28:3','از داستان موسی و فرعون به حق بر تو می‌خوانیم، برای گروهی که ایمان می‌آورند.');
INSERT INTO "hussein_taji" VALUES(28,4,'28:4','بی‌گمان فرعون در زمین برتری جست و اهل آن را گروه گروه کرد، گروهی از آن‌ها را به ضعف و ناتوانی می‌کشاند، پسران‌شان را سر می‌برید و زنان‌شان را (برای خدمت) زنده نگه می‌داشت، بی‌تردید او از مفسدان بود.');
INSERT INTO "hussein_taji" VALUES(28,5,'28:5','و می‌خواهیم بر کسانی‌که در زمین به استضعاف کشیده شده‌اند، منت گذاریم و آنان را پیشوایان سازیم، و آنان را وارثان (زمین) قرار دهیم.');
INSERT INTO "hussein_taji" VALUES(28,6,'28:6','و آن‌ها را در زمین تمکّن (و حکومت) دهیم و به فرعون و هامان و لشکریان آن دو، از (دست) آن‌ها (= بنی اسرائیل) آنچه را که از آن می‌ترسیدند، نشان دهیم [ یعنی آن اندیشه‌ای که تخت فرعون و لشکر او به دست یک شخص اسرائیلی نابود خواهد شد ما آن را به حقیقت در آوردیم.].');
INSERT INTO "hussein_taji" VALUES(28,7,'28:7','و به مادر موسی الهام کردیم که: «او را شیر بده، پس هنگامی‌که بر او ترسیدی، او را به دریای (نیل) بینداز، و نترس، و اندوهگین مشو، یقیناً ما او را به تو باز می‌گردانیم و او را از پیامبران قرار می‌دهیم».');
INSERT INTO "hussein_taji" VALUES(28,8,'28:8','پس (هنگامی‌که مادر موسی او را به دریا افکند) خاندان فرعون او را (از آب) بر گرفتند، تا سرانجام دشمن و مایۀ اندوه‌شان شود، مسلماً فرعون و هامان و لشکریان‌شان خطا کار بودند.');
INSERT INTO "hussein_taji" VALUES(28,9,'28:9','و (آسیه) همسر فرعون گفت: «(این کودک) نور چشم من و توست! او را نکشید، شاید به ما سود بخشد، یا او را به فرزندی گیریم» و آنان (از عاقبت آن) بی‌خبر بودند (و نمی‌فهمیدند).');
INSERT INTO "hussein_taji" VALUES(28,10,'28:10','و قلب مادر موسی (از همه چیز) تهی گشت، اگر دل او را (به ایمان) محکم نکرده بودیم تا از باور دارندگان باشد، نزدیک بود آن (راز) را فاش کند.');
INSERT INTO "hussein_taji" VALUES(28,11,'28:11','و (مادر موسی) به خواهر او گفت: «به دنبالش برو» پس (خواهرش) او را از دور دید، در حالی‌که آن‌ها بی‌خبر بودند.');
INSERT INTO "hussein_taji" VALUES(28,12,'28:12','و (پستان) همۀ زنان شیر‌ده را از پیش بر او حرام کردیم، آنگاه (خواهرش) گفت: «آیا شما را به خانواده‌ای راهنمایی کنم که او را برایتان نگه دارند در حالی‌که آن‌ها خیر خواه او باشند؟!».');
INSERT INTO "hussein_taji" VALUES(28,13,'28:13','پس او را به مادرش باز گرداندیم تا چشمش روشن شود و غمگین نباشد، و تا بداند که وعدۀ الله حق است، و لیکن بیشتر آن‌ها نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(28,14,'28:14','و چون (موسی) به (مرحلۀ بلوغ و) کمال قوت رسید و برومند شد، به او حکمت و علم دادیم، و این گونه نیکو‌کاران را پاداش می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(28,15,'28:15','او هنگامی به شهر وارد شد که اهل آن در غفلت بودند، ناگهان دو مرد را دید با هم نزاع (و جنگ) می‌کردند، یکی از آن‌ها از پیروان او بود، و دیگری از دشمنانش، پس آن که از پیروان او بود بر (علیه) کسی‌که از دشمنانش بود از وی یاری خواست. پس (موسی) به او مشت (محکمی) زد و کارش را ساخت، (و مُرد، موسی) گفت: «این از عمل شیطان بود، بی‌گمان او دشمن گمراه‌کنندۀ آشکاری است».');
INSERT INTO "hussein_taji" VALUES(28,16,'28:16','(سپس) گفت: «پروردگارا! من به خود ستم کردم، پس مرا ببخش» آنگاه (الله) او را بخشید، بی‌تردید او آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(28,17,'28:17','گفت: «پروردگارا! به شکرانۀ نعمتی که بر من عطا کردی، پس هرگز پشتیبان مجرمان نخواهم بود».');
INSERT INTO "hussein_taji" VALUES(28,18,'28:18','پس (موسی) در شهر ترسان، و چشم به راهِ حادثه (شب را) صبح نمود، که ناگهان (دید) همان کسی‌که دیروز از او یاری خواسته بود، او را فریاد می‌زند (و کمک می‌خواهد) موسی به او گفت: «بی‌گمان تو آشکارا (انسان ماجرا جو و) گمراه هستی».');
INSERT INTO "hussein_taji" VALUES(28,19,'28:19','پس هنگامی‌که (موسی) خواست به کسی‌که دشمن هر دوی آن‌ها بود، حمله برد (و بزند، او فریاد زد و) گفت: «ای موسی! آیا می‌خواهی مرا بکشی همان گونه که دیروز کسی را کشتی؟! تو فقط می‌خواهی در زمین جبّاری باشی، و نمی‌خواهی که از مصلحان باشی!».');
INSERT INTO "hussein_taji" VALUES(28,20,'28:20','و (در این هنگام) مردی از دورترین نقطۀ شهر شتابان آمد (و) گفت: «ای موسی! همانا بزرگان (و اشراف قوم) دربارۀ تو مشورت می‌کنند؛ تا تو را بکشند، پس بی‌درنگ (از شهر) بیرون شو، بی‌تردید من از خیر خواهان تو هستم»');
INSERT INTO "hussein_taji" VALUES(28,21,'28:21','پس ترسان و چشم به راه حادثه، از شهر بیرون شد، گفت: «پروردگارا! مرا از قوم ستمکار نجات بده».');
INSERT INTO "hussein_taji" VALUES(28,22,'28:22','و هنگامی‌که به (جانب شهر) مدین رو آورد؛ گفت: «امید است پروردگارم مرا به راه راست هدایت کند».');
INSERT INTO "hussein_taji" VALUES(28,23,'28:23','و چون به (چاه) آب مدین رسید، بر آن (چاه) گروهی از مردم را دید که (چهارپایان خود را) آب می‌دهند، و در کنار آنان دو زن را دید که (گوسفندان خود را) باز می‌دارند (و به چاه نزدیک نمی‌شوند، موسی) گفت: «کار شما چیست؟ (چرا گوسفند‌‌ان خود را آب نمی‌دهید؟!) گفتند: «ما (آن‌ها را) آب نمی‌دهیم تا چوپان‌ها باز گردند (و بروند) و پدر ما پیر کهن‌سال است (و توانایی این کار را ندارد)».');
INSERT INTO "hussein_taji" VALUES(28,24,'28:24','پس (موسی) برای گوسفندان آن دو آب داد، سپس رو به سایه آورد، آنگاه گفت: «پروردگارا! من هر خیری که بر من بفرستی نیازمند هستم».');
INSERT INTO "hussein_taji" VALUES(28,25,'28:25','آنگاه یکی از آن دو (زن) در حالی‌که با نهایت حیا گام بر می‌داشت، نزد او آمد، گفت: «همانا پدرم تو را دعوت می‌کند؛ تا مزد آب دادن (گوسفندان) را که برای ما انجام دادی، به تو بدهد» پس چون (موسی) به نزد او آمد و داستان (سرگذشت خود) را بر او حکایت کرد، گفت: «نترس، از گروه ستمکاران نجات یافتی».');
INSERT INTO "hussein_taji" VALUES(28,26,'28:26','یکی از آن دو (دختر) گفت: «پدر جان! او را اجیر کن، بی‌تردید بهترین کسی را که می‌توانی اجیر کنی، کسی است که نیرومند امین باشد (و او چنین است)».');
INSERT INTO "hussein_taji" VALUES(28,27,'28:27','(شعیب) گفت: «من می‌خواهم یکی از این دو دختر را به ازدواج تو در بیاورم به شرطی‌که هشت سال برای من کار کنی، و اگر (آن را) تا ده سال تمام کنی، پس (لطف و) محبتی از سوی توست، من نمی‌خواهم بر تو سختگیری کنم، اگر الله بخواهد مرا از صالحان خواهی یافت.');
INSERT INTO "hussein_taji" VALUES(28,28,'28:28','(موسی) گفت: «این (قرار داد) میان من و تو باشد که هر کدام از این دو مدت را انجام دادم بر من ستمی نخواهد بود، و الله بر آنچه می‌گوییم گواه است».');
INSERT INTO "hussein_taji" VALUES(28,29,'28:29','پس هنگامی‌که موسی (آن) مدت (معین) را به پایان رسانید، و همراه خانواده‌اش حرکت کرد، از سوی (کوه) طور آتشی دید؛ به خانواده‌اش گفت: «درنگ کنید، همانا من آتشی دیدم، شاید خبری از آن برای شما بیاورم، یا شعله‌ای از آتش (بیاورم) تا شما (با آن) گرم شوید».');
INSERT INTO "hussein_taji" VALUES(28,30,'28:30','پس هنگامی‌که به (نزدیکی) آن رسید، از کرانۀ راست وادی در جایگاه پر برکت، از (میان) یک درخت ندا داده شد که «ای موسی! یقیناً من الله، پروردگار جهانیانم.');
INSERT INTO "hussein_taji" VALUES(28,31,'28:31','و اینکه عصایت را بیفکن»- پس چون آن را دید که (با سرعت) می‌جنبد گویی که آن ماری است، (ترسید و) به عقب برگشت و پشت سر خود را نگاه نکرد - (الله فرمود:) «ای موسی! پیش آی، و نترس، بی‌گمان تو از ایمنان هستی.');
INSERT INTO "hussein_taji" VALUES(28,32,'28:32','دستت را در گریبان خود فرو کن، که سفید (و درخشنده) بی‌عیب بیرون بیاید، و بازویت را (برای حذر) از ترس به سوی خود (بیار و) جمع کن، پس این دو (معجزه عصا و ید بیضا) دو برهان (روشن) از پروردگارت به سوی فرعون و اشراف (قوم) اوست، بی‌گمان آن‌ها گروهی فاسق هستند».');
INSERT INTO "hussein_taji" VALUES(28,33,'28:33','(موسی) گفت: «پروردگارا! همانا من یک تن از آن‌ها را کشته‌ام، پس می‌ترسم که مرا بکشند.');
INSERT INTO "hussein_taji" VALUES(28,34,'28:34','و برادرم هارون زبانش از من فصیح‌تر است، پس او را همراه من بفرست تا یاورم باشد و مرا تصدیق کند، زیرا که می‌ترسم مرا تکذیب کنند».');
INSERT INTO "hussein_taji" VALUES(28,35,'28:35','(الله) فرمود: «به زودی بازوی تو را با برادرت قوی (و نیرومند) خواهیم کرد، و برای شما سلطه و برتری قرار می‌دهیم، پس به سبب آیات ما، بر شما دست نیابند، و شما دو نفر و کسی‌که از شما پیروی کند؛ پیروزید»');
INSERT INTO "hussein_taji" VALUES(28,36,'28:36','پس هنگامی‌که موسی با آیات روشن ما به سوی آن‌ها آمد، گفتند: «این (چیزی) جز سحر ساختگی نیست، و ما چنین چیزی را در نیاکان پیشینیان خود نشنیده‌ایم».');
INSERT INTO "hussein_taji" VALUES(28,37,'28:37','و موسی گفت: «پروردگار من از (حال) کسی‌که هدایت را از نزد او آورده، و کسی‌که سرانجام سرای (دیگر) از آنِ اوست، دانا‌تر است، بی‌گمان ستمکاران رستگار نخواهند شد» [ مراد از «ظالم» مشرک و کافر است. چرا که ظلم یعنی گذاشتن چیزی در جای غیر مختص به خود، و مشرک ظالم شمرده می‌شود چون که کسانی را در مقام الوهیت قرار می‌دهد که لایق آن نیست، و همچنین کافر چون که از حقیقت مقام پروردگار کاملاً جاهل و ناآشنا می‌باشد ظالم شمرده شده‌است.].');
INSERT INTO "hussein_taji" VALUES(28,38,'28:38','و فرعون گفت: «ای بزرگان! من معبودی جز خودم برای شما نمی‌شناسم، پس ای هامان! برایم آتشی بر گِل بیفروز (و آجر بپز) آنگاه برایم برج بلندی بساز، شاید که از الله موسی با خبر شوم، و هرچند من گمان می‌کنم که او از دروغ‌گویان است».');
INSERT INTO "hussein_taji" VALUES(28,39,'28:39','و او (= فرعون) و لشکریانش به ناحق در زمین سر‌کشی کردند، و پنداشتند که آن‌ها به سوی ما باز گردانده نمی‌شوند.');
INSERT INTO "hussein_taji" VALUES(28,40,'28:40','پس (ما) او و لشکریانش را فرو گرفتیم، آنگاه در دریا انداختیم، پس بنگر عاقبت ستمکاران چگونه بود؟!');
INSERT INTO "hussein_taji" VALUES(28,41,'28:41','و آن‌ها را پیشوایانی قرار دادیم که به سوی آتش (دوزخ) دعوت می‌کنند، و روز قیامت یاری نخواهند شد.');
INSERT INTO "hussein_taji" VALUES(28,42,'28:42','در این دنیا لعنتی به دنبال آنان روانه کردیم؛ و روز قیامت از زشت رویانند.');
INSERT INTO "hussein_taji" VALUES(28,43,'28:43','و به راستی بعد از آنکه امت‌های پیشین را هلاک نمودیم؛ به موسی کتاب (تورات) دادیم که برای مردم بصیرت و هدایت و رحمت باشد، شاید پند گیرند.');
INSERT INTO "hussein_taji" VALUES(28,44,'28:44','و تو (ای پیامبر!) در ناحیه غربی نبودی، هنگامی‌که فرمان (نبوت) را به موسی دادیم و تو از حاضران نبودی.');
INSERT INTO "hussein_taji" VALUES(28,45,'28:45','و لیکن (ما) نسل‌هایی آفریدیم که عمر دراز یافتند، و تو در میان اهل مدین اقامت نداشتی که آیات ما را بر آن‌ها بخوانی، و لیکن ما بودیم که (پیامبرانی) می‌فرستادیم.');
INSERT INTO "hussein_taji" VALUES(28,46,'28:46','و تو در کنار طور نبودی آنگاه که ندا دادیم، ولی این (و حی) رحمتی از جانب پروردگارت است؛ تا گروهی را بیم دهی که پیش از تو بیم‌دهنده‌ای برای آن‌ها نیامده بود، شاید که آن‌ها پند گیرند.');
INSERT INTO "hussein_taji" VALUES(28,47,'28:47','و اگر آن نبود که به (سبب) اعمالی‌که مرتکب شده‌اند؛ مصیبتی به آن‌ها برسد، آنگاه بگویند: «پروردگارا! چرا به سوی ما پیامبری نفرستادی تا از آیات تو پیروی کنیم و از مؤمنان باشیم؟».');
INSERT INTO "hussein_taji" VALUES(28,48,'28:48','پس هنگامی‌که حق از نزد ما برای آن‌ها آمد، گفتند: «چرا (به این پیامبر) مانند آنچه به موسی داده شد، داده نشده است؟!» آیا به آنچه پیش از این به موسی داده شد، کفر نورزیدند؟ گفتند: «این دو (قرآن و تورات) جادوی تأیید کنندۀ یکدیگرند» و گفتند: «همانا ما به هر کدام (ازآن‌ها) کافریم».');
INSERT INTO "hussein_taji" VALUES(28,49,'28:49','بگو: «اگر راست می‌گویید، پس کتابی هدایت‌کننده‌تر از این دو (کتاب) از نزد الله بیاورید، تا من از آن پیروی کنم».');
INSERT INTO "hussein_taji" VALUES(28,50,'28:50','پس اگر (این سخن) تو را نپذیرفتند، بدان که آن‌ها فقط از هوس‌های خود پیروی می‌کنند، و گمراه‌تر از آن کس که هوای نفس خویش را بدون (هیچ) هدایتی از (سوی) الله پیروی می‌کند؛ کیست؟! بی‌گمان الله گروه ستمکاران را هدایت نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(28,51,'28:51','و به راستی (ما این) سخن را یکی پس از دیگری برای آن‌ها آوردیم؛ شاید متذکر شوند.');
INSERT INTO "hussein_taji" VALUES(28,52,'28:52','کسانی‌که پیش از این (قرآن) به آنان کتاب (آسمانی) داده‌ایم؛ به آن ایمان می‌آورند.');
INSERT INTO "hussein_taji" VALUES(28,53,'28:53','و چون (قرآن) بر آن‌ها خوانده شود، گویند: «به آن ایمان آوردیم، بی‌تردید، این حق از سوی پروردگار ما است، همانا ما پیش از (نزول) آن هم مسلمان بودیم».');
INSERT INTO "hussein_taji" VALUES(28,54,'28:54','اینان (کسانی‌اند که) به (سبب) صبری که کرده‌اند، پاداش‌شان دوبار (به آنان) داده می‌شود، و (این‌ها) با نیکی‌ها بدی ها را دفع می‌کنند، و از آنچه به آن‌ها روزی داده‌ایم؛ انفاق می‌کنند.');
INSERT INTO "hussein_taji" VALUES(28,55,'28:55','و هنگامی‌که سخن لغو (و بیهوده) بشوند، از آن روی می‌گردانند و گویند: «اعمال ما از آن ماست، و اعمال شما از آن شما، سلام بر شما ما خواستار جاهلان نیستیم».');
INSERT INTO "hussein_taji" VALUES(28,56,'28:56','(ای پیامبر!) بی‌تردید تو هر کس را دوست داری نمی‌توانی هدایت کنی، ولی الله هر کس را بخواهد هدایت می‌کند، و او به هدایت شدگان آگاه‌تر است.');
INSERT INTO "hussein_taji" VALUES(28,57,'28:57','و (مشرکان) گفتند: «اگر به همراه تو از هدایت پیروی کنیم، ما را از سرزمینمان می‌ربایند» آیا ما (آن‌ها را) در حرم امنی جای‌شان ندادیم، که ثمرات (و فرآورده‌های) هر چیزی (از هر سو) به سوی آن آورده می‌شود، (این) رزقی است از جانب ما، و لیکن بیشتر آن‌ها نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(28,58,'28:58','و چه بسیار از قریه‌ها (و آبادی‌ها) یی را که بر اثر فزونی نعمت، مست و مغرور شده بودند؛ هلاک کردیم، پس این خانه‌های (ویران شدۀ) آن‌هاست، که بعد از آن‌ها جز اندکی (کسی) در آن‌ها سکونت نکرد، و ما وارث آن‌ها بودیم.');
INSERT INTO "hussein_taji" VALUES(28,59,'28:59','و پروردگار تو هرگز نابود کنندۀ قریه‌ها (و آبادی‌ها) نبود، تا اینکه در کانون آن‌ها پیامبری بفرستد، که آیات ما را برآن‌ها بخواند، و ما هرگز قریه‌ها (و آبادی‌ها) را هلاک نکردیم؛ مگر آن که اهالی آن ستمکار بودند.');
INSERT INTO "hussein_taji" VALUES(28,60,'28:60','و آنچه به شما داده شده، متاع زندگی دنیا و زینت آن است، و آنچه نزد الله است، بهتر و پایدارتر است، آیا نمی‌اندیشید؟!');
INSERT INTO "hussein_taji" VALUES(28,61,'28:61','آیا کسی‌که به او وعده نیک داده‌ایم، پس او به آن (وعده) خواهد رسید، همانند کسی است که او را از متاع زندگی دنیا بهرمند ساخته‌ایم، سپس روز قیامت از احضار شدگان (در عذاب) خواهد بود؟');
INSERT INTO "hussein_taji" VALUES(28,62,'28:62','و روزی‌که (الله) آنان را ندا دهد، آنگاه فرماید: «کجا هستند، شریکانی که برای من می‌پنداشتید؟!»');
INSERT INTO "hussein_taji" VALUES(28,63,'28:63','کسانی (از معبودان) که فرمان (عذاب) بر آنان تحقیق یافته است. می‌گویند: «پروردگارا! این‌ها (= عابدان) کسانی‌اند که گمراهشان کردیم؛ (آری) آن‌ها را گمراه کردیم، همان گونه که (خودمان) گمراه شدیم، از آن‌ها به‌سوی تو بیزاری می‌جوییم، آن‌ها (در حقیقت) ما را نمی‌پرستیدند (بلکه آن‌ها شیطان را پرستش می‌کردند)»');
INSERT INTO "hussein_taji" VALUES(28,64,'28:64','و (به عابدان) گفته می‌شود: «معبودهای‌تان را (که شریک الله می‌پنداشتید) بخوانید (تا شما را یاری کنند)» پس آن‌ها را می‌خوانند، ولی جوابی به ایشان نمی‌دهند، و عذاب (الهی) را (با چشم خود) ببینید، (و آرزو کنند) که ای کاش هدایت یافته بودند.');
INSERT INTO "hussein_taji" VALUES(28,65,'28:65','و روزی‌که (الله) آنان را ندا دهد، آنگاه فرماید: «پیامبران را چه پاسخی دادید؟!»');
INSERT INTO "hussein_taji" VALUES(28,66,'28:66','در آن روز همۀ خبرها (و حجت‌ها) بر آن‌ها پوشیده ماند، (و همه چیز را فراموش کنند) پس آنان از یکدیگر نپرسند.');
INSERT INTO "hussein_taji" VALUES(28,67,'28:67','اما کسی‌که توبه کرد و ایمان آورد و عمل شایسته انجام داد، پس امید است که از رستگاران باشد.');
INSERT INTO "hussein_taji" VALUES(28,68,'28:68','و پروردگار تو هر چه بخواهد می‌آفریند، و (هر چه بخواهد) بر می‌گزیند، آنان را (هیچ) اختیاری نیست، الله منزه و برتر است از آنچه شریک او قرار می‌دهند.');
INSERT INTO "hussein_taji" VALUES(28,69,'28:69','و پروردگار تو می‌داند آنچه را که سینه‌های آنان پنهان می‌دارد، و آنچه را آشکار می‌کنند.');
INSERT INTO "hussein_taji" VALUES(28,70,'28:70','و او ذاتی است که هیچ معبودی (به حق) جز او نیست، ستایش در دنیا و آخرت برای اوست، و حکم (و فرمانروایی) از آن اوست، و (همۀ شما) به سوی او باز گردانده می‌شوید.');
INSERT INTO "hussein_taji" VALUES(28,71,'28:71','(ای پیامبر!) بگو: «به من خبر دهید اگر الله شب را تا روز قیامت بر شما پایدار سازد، کیست معبودی جز الله که برای شما روشنی آورد؟! آیا نمی‌شنوید؟!».');
INSERT INTO "hussein_taji" VALUES(28,72,'28:72','بگو: «به من خبر دهید اگر الله روز را تا روز قیامت بر شما پایدار گرداند، کیست معبودی جز الله که برای شما شبی آورد که در آن آرام بگیرید؟! آیا نمی‌بینید؟!');
INSERT INTO "hussein_taji" VALUES(28,73,'28:73','و از رحمت خویش شب و روز را برای شما قرار داد، تا در آن (= شب) آرام بگیرید، و تا (در روز) از فضل او (روزی) بجویید، و شاید سپاس گزارید.');
INSERT INTO "hussein_taji" VALUES(28,74,'28:74','و روزی‌که (الله) آنان را ندا دهد، آنگاه فرماید: «کجا هستند، شریکانی را که برای من می‌پنداشتید؟!»');
INSERT INTO "hussein_taji" VALUES(28,75,'28:75','و از هر امتی گواهی بر می‌گزینیم، آنگاه می‌گوییم: «دلیل خود را بیاورید» آنگاه بدانند که حق از آن الله است، و (تمام) آنچه را افترا می‌بستند از (نظر) آنان ناپدید شود (و تباه گردد).');
INSERT INTO "hussein_taji" VALUES(28,76,'28:76','بی‌گمان قارون از قوم موسی بود، پس بر آن‌ها ستم کرد، آنقدر از گنج‌ها به او داده بودیم که حمل کلید‌هایش بر گروهی زورمند دشوار (و سنگین) بود. هنگامی‌که قومش به او گفت: «(به خاطر مالت این همه مغرورانه) شادی نکن، همانا الله شادی کنندگان (مغرور) را دوست ندارد.');
INSERT INTO "hussein_taji" VALUES(28,77,'28:77','و در آنچه الله به تو داده، سرای آخرت را بجوی و بهره‌ات را از دنیا فراموش نکن [ یعنی در مباحات دنیا نیز اسراف و ولخرجی نکن. مباحات دنیا مانند خوردن، نوشیدن، لباس، خانه، ازدواج و غیره. مطلب اینست که همچنانکه بایستی حق پروردگار را ادا کرد نیز باید حق بدن خود و زن و بچه و مهمان‌ها و غیره را ادا کرد. یعنی باید حق هر حق‌داری را به جای آورد.]، و همان‌گونه که الله به تو نیکی کرده است؛ نیکی کن، و هرگز در زمین در پی فساد مباش، بی‌گمان الله مفسدان را دوست ندارد»');
INSERT INTO "hussein_taji" VALUES(28,78,'28:78','(قارون) گفت: «تنها به سبب دانشی که نزد من است آن (مال و ثروت) به من داده شده است» آیا او نمی‌دانست که الله پیش از او اقوامی را هلاک کرد که از او نیرومندتر و ثروتمندتر بودند؟! و مجرمان از گناهان‌شان پرسیده نمی‌شوند.');
INSERT INTO "hussein_taji" VALUES(28,79,'28:79','پس (روزی قارون) با آرایش و زینت خویش بر قومش بیرون آمد، کسانی‌که خواهان زندگی دنیا بودند، گفتند: «ای کاش همانند آنچه به قارون داده شده است؛ ما نیز داشتیم، به راستی که او از بهرۀ عظیمی برخوردار است».');
INSERT INTO "hussein_taji" VALUES(28,80,'28:80','و کسانی‌که دانش به آن‌ها داده شده بود، گفتند: «وای بر شما! ثواب الله برای کسی‌که ایمان آورد و کار شایسته انجام دهد بهتر است، و آن (سخن را کسانی) جز صابران نمی‌پذیرند»');
INSERT INTO "hussein_taji" VALUES(28,81,'28:81','پس او و خانه‌اش را در زمین فرو بردیم، و هیچ گروهی نداشت که او را در برابر (عذاب) الله یاری کنند، و خود (نیز) نمی‌توانست خویش را یاری کند.');
INSERT INTO "hussein_taji" VALUES(28,82,'28:82','و کسانی‌که دیروز آرزو می‌کردند به جای او باشند، می‌گفتند: «وای بر ما! گویی الله روزی را برای هر کس از بندگانش که بخواهد فراخ می‌گرداند، و (یا) تنگ می‌گیرد، اگر الله بر ما منّت ننهاده بود، بی‌تردید ما را (نیز به قعر زمین) فرو می‌برد، ای وای! گویی کافران هرگز رستگار نمی‌شوند.');
INSERT INTO "hussein_taji" VALUES(28,83,'28:83','(آری) این سرای آخرت را (فقط) برای کسانی قرار می‌دهیم که نه خواهان برتری جویی هستند و نه خواهان فساد، و سرانجام نیک برای پرهیزگاران است.');
INSERT INTO "hussein_taji" VALUES(28,84,'28:84','کسی‌که نیکی آورد، پس برای او پاداش بهتر از آن است، و کسی‌که بدی آورد، پس کسانی‌که بدی‌ها انجام دادند، جز (به‌اندازۀ) آنچه می‌کردند، مجازات نخواهند داشت.');
INSERT INTO "hussein_taji" VALUES(28,85,'28:85','بی‌تردید کسی‌که قرآن را بر تو فرض (و نازل) کرد، قطعاً تو را به جایگاهت (= مکه) باز می‌گرداند. بگو: «پروردگار من بهتر می‌داند چه کسی هدایت آورده، و چه کسی در گمراهی آشکار است».');
INSERT INTO "hussein_taji" VALUES(28,86,'28:86','و تو امید نداشتی که کتاب (آسمانی) بر تو القا شود، جز این‌که رحمت پروردگارت بود، پس هرگز پشتیبان کافران مباش.');
INSERT INTO "hussein_taji" VALUES(28,87,'28:87','و هرگز (کافران) تو را از آیات الله، بعد از آن که بر تو نازل شد، باز ندارند، و به‌سوی پروردگارت دعوت کن، و هرگز از مشرکان مباش.');
INSERT INTO "hussein_taji" VALUES(28,88,'28:88','و معبود دیگری با الله مخوان، هیچ معبودی (به حق) جز او نیست، همه چیز جز روی (= ذات) او نابود می‌شود، فرمانروایی (تنها) از آنِ اوست، و (همه) به‌سوی او بازگردانده می‌شوید.');
INSERT INTO "hussein_taji" VALUES(29,1,'29:1','الم (الف. لام. میم).');
INSERT INTO "hussein_taji" VALUES(29,2,'29:2','آیا مردم پنداشتند که چون بگویند: «ایمان آوردیم» (به حال خود) رها می‌شوند، و آنان آزمایش نمی‌شوند؟!');
INSERT INTO "hussein_taji" VALUES(29,3,'29:3','و به راستی کسانی را که پیش از آن‌ها بودند؛ آزمودیم، پس البته الله کسانی را که راست گفتند معلوم می‌دارد، و دروغگویان را (نیز) معلوم خواهد داشت.');
INSERT INTO "hussein_taji" VALUES(29,4,'29:4','آیا کسانی‌که اعمال بد انجام می‌دهند؛ گمان کردند که از ما پیشی می‌گیرند؟ چه بد داوری می‌کنند!');
INSERT INTO "hussein_taji" VALUES(29,5,'29:5','کسی‌که به دیدار الله امید دارد، پس به راستی که وعدۀ الله خواهد آمد، و او شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(29,6,'29:6','کسی‌که (در راه الله) جهاد کند، تنها برای (نفع) خودش جهاد می‌کند، بی‌گمان الله از (همۀ) جهانیان بی‌نیاز است.');
INSERT INTO "hussein_taji" VALUES(29,7,'29:7','و کسانی‌که ایمان آوردند و کارهای شایسته انجام دادند، قطعاً گناهان‌‌شان را از آنان می‌زداییم، و آنان را به بهترین اعمالی‌که انجام می‌داند، پاداش می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(29,8,'29:8','و انسان را به نیک رفتاری (نسبت) به پدر و مادرش سفارش کردیم، و اگر آن دو (مشرک باشند و) تلاش کنند که تو چیزی را که به آن علم نداری شریک من قرار دهی، پس از آنان اطاعت نکن، بازگشت شما به سوی من است، آنگاه شما را به آنچه انجام می‌دادید؛ آگاه خواهم کرد.');
INSERT INTO "hussein_taji" VALUES(29,9,'29:9','و کسانی‌که ایمان آوردند و کارهای شایسته انجام دادند، هر آیینه آن‌ها را در (زمرۀ) صالحان در می‌آوریم.');
INSERT INTO "hussein_taji" VALUES(29,10,'29:10','و از مردم کسانی هستند که می‌گویند: «به الله ایمان آورده‌ایم» پس هنگامی‌که در راه الله آزار بیند، آزار (و اذیت) مردم را همچون عذاب الله بشمار می‌آورد، و اگر پیروزی از سوی پروردگارت (برای شما) بیاید؛ حتماً می‌گویند: «به راستی‌که ما (نیز) با شما بودیم» آیا الله به آنچه در سینه‌های جهانیان است؛ داناتر نیست؟!');
INSERT INTO "hussein_taji" VALUES(29,11,'29:11','و مسلماً الله کسانی را که ایمان آوردند معلوم می‌دارد، و یقیناً منافقان را (نیز) معلوم خواهد داشت.');
INSERT INTO "hussein_taji" VALUES(29,12,'29:12','و کسانی‌که کافر شدند به کسانی‌که ایمان آوردند؛ گفتند: «از راه ما پیروی کنید، و البته ما (بار) گناهان‌تان را بر می‌داریم». و آن‌ها هرگز چیزی از گناهان ایشان را بر نخواهند داشت، بی‌گمان آن‌ها دروغگو هستند.');
INSERT INTO "hussein_taji" VALUES(29,13,'29:13','و البته بار‌های سنگین (گناهان) خویش را بر می‌دارند، و (همچنین) بار‌های دیگری را با بارهای سنگین خود بر می‌دارند، و روز قیامت یقیناً از آنچه افترا می‌بستند پرسیده خواهند شد.');
INSERT INTO "hussein_taji" VALUES(29,14,'29:14','و به راستی‌که (ما) نوح را به سوی قومش فرستادیم، پس او در میان آنان هزار سال به جز پنجاه سال (= نهصد و پنجاه سال) درنگ کرد، آنگاه طوفان (و سیلاب) آنان را فرو گرفت در حالی‌که ستمکار بودند.');
INSERT INTO "hussein_taji" VALUES(29,15,'29:15','آنگاه او و سرنشینان کشتی را نجات دادیم، و آن (کشتی) را نشانۀ (عبرتی) برای جهانیان قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(29,16,'29:16','و ابراهیم را (نیز فرستادیم)، هنگامی‌که به قومش گفت: «الله را بپرستید و از او بترسید، که اگر بدانید این برای شما بهتر است.');
INSERT INTO "hussein_taji" VALUES(29,17,'29:17','شما به جای الله فقط بت‌های (از سنگ و چوب) را می‌پرستید، و دروغی (بزرگ) به هم می‌بافید، بی‌گمان کسانی را که به جای الله می‌پرستید، مالک هیچ رزقی برای شما نیستند، پس روزی را تنها (از) نزد الله طلب کنید، و او را بپرستید، و شکر او را بجای آورید، که به سوی او باز گردانیده می‌شوید.');
INSERT INTO "hussein_taji" VALUES(29,18,'29:18','و اگر شما تکذیب کنید (عجیب نیست) به راستی که امت‌هایی پیش از شما نیز تکذیب کردند، و بر (عهدۀ) پیامبر (الله) چیزی جز تبلیغ آشکار نیست».');
INSERT INTO "hussein_taji" VALUES(29,19,'29:19','آیا ندیدند که الله چگونه آفرینش را آغاز می‌کند، سپس باز می‌گرداند؟! بی‌تردید این (کار) بر الله آسان است.');
INSERT INTO "hussein_taji" VALUES(29,20,'29:20','(ای پیامبر!) بگو: «در زمین بگردید؛ آنگاه بنگرید (الله) چگونه آفرینش را آغاز کرد، سپس الله (به همین گونه) پیدایش باز پسین را پدید می‌آورد، بی‌گمان الله بر هر چیز تواناست.');
INSERT INTO "hussein_taji" VALUES(29,21,'29:21','هر کس را بخواهد عذاب می‌کند، و هر کس را بخواهد مورد رحمت قرار می‌دهد، و (شما) به سوی او باز گردانیده می‌شوید.');
INSERT INTO "hussein_taji" VALUES(29,22,'29:22','و شما هرگز نمی‌توانید ما را در زمین و نه در آسمان به ستوه آورید (و از عقوبت ما فرار کنید) و برای شما جز الله هیچ کار ساز و یاوری نیست».');
INSERT INTO "hussein_taji" VALUES(29,23,'29:23','و کسانی‌که به آیات الله و دیدار او کافر شدند، اینانند که از رحمت من نا امید هستند، و آنان برایشان عذاب درد ناکی است.');
INSERT INTO "hussein_taji" VALUES(29,24,'29:24','آنگاه جواب قوم او (= ابراهیم) جز این نبود که گفتند: «او را بکشید، یا او را بسوزانید» پس الله او را از آتش نجات داد، بی‌گمان در این (ماجرا) نشانه‌هایی است برای گروهی‌که ایمان می‌آورند.');
INSERT INTO "hussein_taji" VALUES(29,25,'29:25','و (ابراهیم) گفت: «شما غیر از الله بت‌هایی را برای خود برگزیده‌اید که مایۀ دوستی و محبت میان شما در زندگی دنیا باشد، سپس روز قیامت برخی از شما برخی (دیگر) را انکار می‌کند، و برخی از شما برخی (دیگر) را لعنت می‌کند، و جایگاه شما آتش است، و هیچ یاوری برای شما نخواهد بود.');
INSERT INTO "hussein_taji" VALUES(29,26,'29:26','پس لوط به او (= ابراهیم) ایمان آورد، و (ابراهیم) گفت: «من به سوی پروردگارم هجرت می‌کنم، بی‌گمان او پیروزمند حکیم است»');
INSERT INTO "hussein_taji" VALUES(29,27,'29:27','و اسحاق و یعقوب را به او بخشیدیم، و در فرزندانش نبوت و کتاب (آسمانی) قرار دادیم، و پاداش او را در دنیا دادیم، و بی‌تردید او در آخرت از صالحان است.');
INSERT INTO "hussein_taji" VALUES(29,28,'29:28','و لوط را (فرستادیم) هنگامی‌که به قومش گفت: «بی‌گمان شما (عمل) بسیار زشتی انجام می‌دهید، که هیچ کس از جهانیان پیش از شما آن را انجام نداده است!');
INSERT INTO "hussein_taji" VALUES(29,29,'29:29','آیا شما (برای کامجویی) به سراغ مردان می‌روید، و راه را قطع می‌کنید، و در محافل خود اعمال نا پسند انجام می‌دهید؟!» پس جواب قومش جزاین نبود که گفتند: «اگر از راستگویانی عذاب الله را برای ما بیاور».');
INSERT INTO "hussein_taji" VALUES(29,30,'29:30','(لوط) گفت: «پروردگارا! مرا بر قوم مفسدان یاری فرما».');
INSERT INTO "hussein_taji" VALUES(29,31,'29:31','و هنگامی‌که فرستادگان ما ابراهیم را بشارت (تولد فرزند) آوردند، گفتند: «به راستی ما اهل این قریه (سدوم) را هلاک خواهیم کرد، بی‌شک اهل آن ستمکار بودند».');
INSERT INTO "hussein_taji" VALUES(29,32,'29:32','(ابراهیم) گفت: «همانا لوط در آنجاست!» (فرشتگان) گفتند: «ما به هر کس‌که در آنجاست داناتریم، یقیناً او و خانواده‌اش را نجات می‌دهیم، جز همسرش که از بازماندگان خواهد بود».');
INSERT INTO "hussein_taji" VALUES(29,33,'29:33','و چون فرستادگان ما نزد لوط آمدند، از (دیدن) آن‌ها اندوهگین، و به خاطر آن‌ها دل‌تنگ شد، و (فرشتگان) گفتند: «نترس، و غمگین مباش، بی‌گمان ما تو و خانواده‌ات را نجات خواهیم داد؛ جز همسرت‌که از بازماندگان خواهد بود.');
INSERT INTO "hussein_taji" VALUES(29,34,'29:34','همانا ما بر اهل این قریه به (خاطر) آن‌که نافرمانی (و گناه) می‌کردند، عذابی از آسمان فرو خواهیم ریخت».');
INSERT INTO "hussein_taji" VALUES(29,35,'29:35','و به راستی از آن (قریه) نشانه (و عبرت) روشنی برای گروهی که خرد می‌ورزند؛ باقی گذاشتیم.');
INSERT INTO "hussein_taji" VALUES(29,36,'29:36','و به سوی مدین برادرشان شعیب را فرستادیم، پس گفت: «ای قوم من! الله را بپرستید، و به روز باز پسین امیدوار باشید، و در زمین به فساد نکوشید».');
INSERT INTO "hussein_taji" VALUES(29,37,'29:37','آنگاه او را تکذیب کردند، پس زلزله آنان را فرو گرفت، پس در خانه‌های خود به رو افتادند (و مردند).');
INSERT INTO "hussein_taji" VALUES(29,38,'29:38','و (قوم) عاد و ثمود را (نیز هلاک کردیم) و یقیناً برخی از مساکن (ویران شدۀ) آن‌ها برای شما آشکار شده است. و شیطان اعمال‌شان را برای آن‌ها بیاراست، پس آن‌ها را از راه (الله) بازداشت در حالی‌که بینا بودند.');
INSERT INTO "hussein_taji" VALUES(29,39,'29:39','و قارون و فرعون و هامان را (نیز هلاک کردیم) و به راستی موسی با دلایل روشن نزد آنان آمد، پس آن‌ها در زمین سرکشی کردند و پیشی گیرندگان (بر الله) نبودند.');
INSERT INTO "hussein_taji" VALUES(29,40,'29:40','آنگاه هر یک از آن‌ها را به (کیفر) گناهش گرفتیم، پس بر بعضی از آن‌ها طوفانی از سنگریزه فرستادیم، و بعضی از آن‌ها را صیحه (= بانگ مرگبار) فرو گرفت، و بعضی را در زمین فرو بردیم، و بعضی را غرق کردیم، و الله هرگز به آن‌ها ستم نکرد، و لیکن آن‌ها (خود) بر خویشتن ستم می‌کردند.');
INSERT INTO "hussein_taji" VALUES(29,41,'29:41','مثل کسانی‌که بجای الله، اولیاء (و دوستانی برای خود) برگزیدند، همچون مثل عنکبوت است، که خانه‌ای (برای خود) برگزیده است، و بی‌گمان سست‌ترین خانه‌ها خانۀ عنکبوت است، اگر می‌دانستند. [یعنی همانگونه که خانه عنکبوت سست و ناپایدار و با کوچکترین اشارۀ دست از بین می‌رود، معبودان خود ساخته خود که آن را مشکل‌گشا و حاجت‌روا می‌پندارید بدینگونه تماماً بیهوده و پوچ می‌باشند، چرا که از هر لحاظ قادر به کاری نیستند، بدین سبب آن معبودان غیر از الله متعال مانند خانه عنکبوت ناپایدار می‌باشند و اگر واقعاً آن‌ها قادر به کاری و یا سودبخش بودند چرا نتوانستند ملت‌های گذشته را از نابودی نجات دهند.]');
INSERT INTO "hussein_taji" VALUES(29,42,'29:42','یقیناً الله هر آنچه را که به جای او می‌خوانند، می‌داند، و او پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(29,43,'29:43','و این مثال‌هایی است که آن‌ها را برای مردم می‌زنیم، و جز دانایان آن‌ها را در نمی‌یابند.');
INSERT INTO "hussein_taji" VALUES(29,44,'29:44','الله آسمان‌ها و زمین را به حق آفرید، بی‌گمان در این نشانه‌ای (بزرگ) برای مؤمنان است.');
INSERT INTO "hussein_taji" VALUES(29,45,'29:45','(ای پیامبر!) آنچه را از (این) کتاب بر تو وحی شده است تلاوت کن، و نماز را بر پا دار، یقیناً نماز (انسان را) از زشتی‌ها و منکر باز می‌دارد، و البته یاد الله بزرگ‌تر است، و الله آنچه را انجام می‌دهید؛ می‌داند.');
INSERT INTO "hussein_taji" VALUES(29,46,'29:46','و با اهل کتاب جز به روشی‌که آن بهتر است مجادله نکنید، مگر کسانی از آنان که ستم کردند، و بگویید: «به آنچه (از سوی الله) بر ما نازل شده و آنچه بر شما نازل شده، ایمان آورده‌ایم، و معبود ما و معبود شما یکی است، و ما برای او تسلیم هستیم».');
INSERT INTO "hussein_taji" VALUES(29,47,'29:47','و این گونه کتاب (= قرآن) را بر تو نازل کردیم، پس کسانی‌که به آن‌ها کتاب (آسمانی) دادیم، به آن ایمان می‌آورند، و از همین گروه (مشرکان نیز) کسی است که به آن ایمان می‌آورد، و آیات ما را جز کافران انکار نمی‌کنند.');
INSERT INTO "hussein_taji" VALUES(29,48,'29:48','و تو (هرگز) پیش از این (قرآن) هیچ کتابی را نمی‌خواندی، و با (دست) راست خود چیزی نمی‌نوشتی، اگر چنین بود، باطل گرایان به شک (و تردید) می‌افتادند.');
INSERT INTO "hussein_taji" VALUES(29,49,'29:49','بلکه آن (قرآن) آیات روشنی است که در سینۀ کسانی‌که دانش داده شده‌اند، جای دارد، و آیات ما را جز ستمکاران انکار نمی‌کنند.');
INSERT INTO "hussein_taji" VALUES(29,50,'29:50','و گفتند: «چرا نشانه‌هایی از سوی پروردگارش بر او نازل نشده است؟!» (ای پیامبر!) بگو: «همانا نشانه‌ها (همه) نزد الله است (و به فرمان او نازل می‌شود) و من تنها هشداردهنده‌ای آشکار هستم».');
INSERT INTO "hussein_taji" VALUES(29,51,'29:51','آیا برای آن‌ها کافی نیست که همانا ما (این) کتاب (قرآن) را بر تو نازل کردیم که (پیوسته) بر آن‌ها تلاوت می‌شود؟! بی‌گمان در این (قرآن) رحمت و پندی است برای کسانی‌که ایمان می‌آورند.');
INSERT INTO "hussein_taji" VALUES(29,52,'29:52','بگو: «همین بس که الله میان من و شما گواه است، آنچه را در آسمان‌ها و زمین است می‌داند، و کسانی‌که به باطل ایمان آوردند و به الله کافر شدند، اینان زیانکاران (واقعی) هستند»');
INSERT INTO "hussein_taji" VALUES(29,53,'29:53','و آن‌ها با شتاب عذاب را از تو طلب می‌کنند، و اگر موعد مقرری تعیین نشده بود، یقیناً عذاب (الهی) به (سراغ) آن‌ها می‌آمد، و ناگهان در حالی‌‌که نمی‌دانند به (سراغ) آن‌ها خواهد آمد.');
INSERT INTO "hussein_taji" VALUES(29,54,'29:54','و آن‌ها با شتاب عذاب را از تو می‌طلبند، و بی‌گمان جهنم به کافران احاطه دارد.');
INSERT INTO "hussein_taji" VALUES(29,55,'29:55','روزی‌که عذاب آن‌ها را از بالای (سر)شان و از زیر پاهای‌شان در بر می‌گیرد، و (الله به آن‌ها) می‌فرماید: «بچشید آنچه را انجام می‌دادید.');
INSERT INTO "hussein_taji" VALUES(29,56,'29:56','ای بندگان من که ایمان آورده‌اید! بی‌گمان زمین من وسیع است، پس تنها مرا بپرستید».');
INSERT INTO "hussein_taji" VALUES(29,57,'29:57','هر کسی چشندۀ مرگ است، سپس به سوی ما باز گردانده می‌شوید.');
INSERT INTO "hussein_taji" VALUES(29,58,'29:58','و کسانی‌که ایمان آوردند و کار‌های شایسته انجام دادند، قطعاً آن‌ها را در غرفه‌هایی از بهشت جای می‌دهیم که نهر‌ها در زیر آن جاری است، جاودانه در آن خواهند ماند، چه خوب است پاداش عمل کنندگان.');
INSERT INTO "hussein_taji" VALUES(29,59,'29:59','(همان) کسانی‌که صبر کردند و تنها بر پروردگارشان توکل می‌کنند.');
INSERT INTO "hussein_taji" VALUES(29,60,'29:60','و چه بسیار جنبدگانی‌که روزی خود را بر دوش نمی‌گیرند، الله به آن‌ها و شما روزی می‌دهد، و او شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(29,61,'29:61','و اگر از آن‌ها بپرسی: «چه کسی آسمان‌ها و زمین را آفریده، و خورشید و ماه را مسخر کرده است؟!» البته می‌گویند: «الله» پس چگونه (از حق) منحرف می‌شوند؟!');
INSERT INTO "hussein_taji" VALUES(29,62,'29:62','الله روزی را برای هر کس از بندگانش که بخواهد فراخ می‌گرداند، و (یا) برایش تنگ می‌دارد، بی‌گمان الله به همه چیز داناست.');
INSERT INTO "hussein_taji" VALUES(29,63,'29:63','و اگر از آن‌ها بپرسی: «چه کسی از آسمان آبی را نازل کرد، آنگاه با آن (آب) زمین را بعد از مردنش زنده کرد؟» البته می‌گویند: «الله» بگو: «حمد و ستایش مخصوص الله است» بلکه بیشتر آن‌ها در نمی‌یابند.');
INSERT INTO "hussein_taji" VALUES(29,64,'29:64','و این زندگی دنیا (چیزی) جز سرگرمی و بازیچه نیست، و بی‌تردید اگر می‌دانستند سرای آخرت همان زندگی (واقعی) است.');
INSERT INTO "hussein_taji" VALUES(29,65,'29:65','پس هنگامی‌که در کشتی سوار شوند، الله را با اخلاص می‌خوانند، پس چون (الله) آن‌ها را (با آوردن) به خشکی نجات داد، باز آنان شرک می‌آورند.');
INSERT INTO "hussein_taji" VALUES(29,66,'29:66','تا آنچه را به آن‌ها داده‌ایم؛ کفران کنند، و تا (از لذات زندگی دنیا) بهره گیرند، پس به زودی خواهند دانست.');
INSERT INTO "hussein_taji" VALUES(29,67,'29:67','آیا ندیدند که همانا ما حرم امنی (برای آن‌ها) قرار دادیم، در حالی‌که مردم از اطراف آنان ربوده می‌شوند؟! آیا به باطل ایمان می‌آورند و به نعمت الله ناسپاسی می‌کنند؟!');
INSERT INTO "hussein_taji" VALUES(29,68,'29:68','و کیست ستمکار‌تر از آن کسی‌که بر الله دروغ بندد یا حق را چون به سوی او آید تکذیب کند؟! آیا جایگاه کافران در جهنم نیست؟!');
INSERT INTO "hussein_taji" VALUES(29,69,'29:69','و کسانی‌که در راه ما (کوشش و) جهاد کنند؛ قطعاً به راه‌های خویش هدایت شان می‌کنیم، و یقیناً الله با نیکوکاران است.');
INSERT INTO "hussein_taji" VALUES(30,1,'30:1','الم (الف. لام. میم).');
INSERT INTO "hussein_taji" VALUES(30,2,'30:2','رومیان مغلوب شدند.');
INSERT INTO "hussein_taji" VALUES(30,3,'30:3','در نزدیکترین سرزمین، و آن‌ها بعد از مغلوب شدن‌شان به زودی غالب خواهند شد.');
INSERT INTO "hussein_taji" VALUES(30,4,'30:4','در (ظرف) چند سال؛ پیش از (این پیروزی) و بعد از این (پیروزی) فرمان از آن الله است، و در آن روز مؤمنان شادمان خواهند شد.');
INSERT INTO "hussein_taji" VALUES(30,5,'30:5','به (سبب) یاری الله، و (الله) هر که را بخواهد یاری می‌کند، و او پیروزمند مهربان است.');
INSERT INTO "hussein_taji" VALUES(30,6,'30:6','الله وعده داده است، الله هرگز وعدۀ خویش را خلاف نمی‌کند، و لیکن بیشتر مردم نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(30,7,'30:7','آن‌ها فقط ظاهری از زندگی دنیا را می‌دانند، و آن‌ها از آخرت غافلند.');
INSERT INTO "hussein_taji" VALUES(30,8,'30:8','آیا در ضمیر خود نیندیشیده‌اند که (دریابند) الله آسمان‌ها و زمین و آنچه را میان آن دو است؛ جز به حق و (تا) زمان معینی نیافریده است؟! و بی‌تردید بسیاری از مردم به دیدار پروردگارشان (در روز قیامت) نا باورند.');
INSERT INTO "hussein_taji" VALUES(30,9,'30:9','آیا در زمین گردش نکردند، تا بنگرند عاقبت کسانی‌که پیش از آن‌ها بودند؛ چگونه بود؟! (آن‌ها) نیرومند‌تر از اینان بودند، و در زمین دگرگونی به وجود آوردند و آن را بیشتر از آنچه (اینان) آبادش کرده‌اند، آباد کردند، و پیامبران‌شان با دلایل روشن به سوی آن‌ها آمدند، پس الله بر آن نبود که برآن‌ها ستم کند، و لیکن (آن‌ها) به خود‌شان ستم می‌کردند.');
INSERT INTO "hussein_taji" VALUES(30,10,'30:10','سپس سرانجام کسانی‌که بدی کردند، بدتر شد، که آیات الله را تکذیب کردند، و آن را به مسخره می‌گرفتند.');
INSERT INTO "hussein_taji" VALUES(30,11,'30:11','الله آفرینش را آغاز می‌کند، سپس آن را باز می‌گرداند، آنگاه به سوی او باز گردانده می‌شوید.');
INSERT INTO "hussein_taji" VALUES(30,12,'30:12','و روزی‌که قیامت بر پا شود، مجرمان نا امید شوند.');
INSERT INTO "hussein_taji" VALUES(30,13,'30:13','و برای آن‌ها از معبودان‌شان شفیعانی نخواهد بود، و نسبت به معبودان‌شان (که آن‌ها را همتای الله قرار داده بودند) کافر می‌شوند.');
INSERT INTO "hussein_taji" VALUES(30,14,'30:14','و روزی‌که قیامت بر پا شود، آن روز (مردم) از هم جدا می‌شوند.');
INSERT INTO "hussein_taji" VALUES(30,15,'30:15','اما کسانی‌که ایمان آورده‌اند و کار‌های شایسته انجام داده‌اند، آنان در باغی (از بهشت) شادمان و مسرور خواهند بود.');
INSERT INTO "hussein_taji" VALUES(30,16,'30:16','و اما کسانی‌که کافر شدند و آیات ما و دیدار آخرت را تکذیب کردند، پس اینان در عذاب (الهی) احضار شوند.');
INSERT INTO "hussein_taji" VALUES(30,17,'30:17','پس الله را تسبیح گویید؛ هنگامی‌که شام می‌کنید و هنگامی‌که صبح می‌کنید.');
INSERT INTO "hussein_taji" VALUES(30,18,'30:18','و حمد و ستایش مخصوص اوست، در آسمان‌ها و زمین، و عصرگاهان و هنگامی‌که ظهر می‌کنید.');
INSERT INTO "hussein_taji" VALUES(30,19,'30:19','زنده را از مرده بیرون می‌آورد، و مرده را از زنده بیرون می‌آورد، و زمین را پس از مردنش زنده می‌سازد، و همین گونه (روز قیامت از گور‌ها) بیرون آورده می‌شوید.');
INSERT INTO "hussein_taji" VALUES(30,20,'30:20','و از نشانه‌های او (این) است که شما را از خاک آفرید، پس ناگاه شما انسان‌های (زیادی شدید و در زمین) پراکنده گشتید.');
INSERT INTO "hussein_taji" VALUES(30,21,'30:21','و از نشانه‌های او (این) است‌که همسرانی از جنس خود‌تان برای شما آفرید؛ تا به آن‌ها آرام گیرید، و در میان‌تان مودت و رحمت قرار داد، بی‌گمان در این (امر) نشانه‌های است برای گروهی‌که تفکر می‌کنند.');
INSERT INTO "hussein_taji" VALUES(30,22,'30:22','و از نشانه‌های او آفرینش آسمان‌ها و زمین، و اختلاف زبان‌هایتان و رنگ‌هایتان است، بی‌تردید در این نشانه‌های برای دانایان است.');
INSERT INTO "hussein_taji" VALUES(30,23,'30:23','و از نشانه‌های او خوابیدن شما در شب و روز است، و تلاش و بهره جستن شما از فضل اوست، بی‌شک در این نشانه‌های است؛ برای گروهی که می‌شنوند.');
INSERT INTO "hussein_taji" VALUES(30,24,'30:24','و از نشانه‌های او (این) است که برق را برای بیم و امید به شما نشان می‌دهد، و از آسمان آبی نازل می‌کند، آنگاه زمین را بعد از مردنش به آن (آب) زنده می‌کند؛ بی‌گمان در این نشانه‌های است؛ برای گروهی که می‌اندیشند.');
INSERT INTO "hussein_taji" VALUES(30,25,'30:25','و از نشانه‌های او (این) است که آسمان‌ها و زمین به فرمانش (بر جای) می‌ایستند، پس هنگامی‌که با ندایی شما را از زمین فرا خواند، ناگهان شما خارج می‌شوید.');
INSERT INTO "hussein_taji" VALUES(30,26,'30:26','و از آنِ اوست هر که در آسمان‌ها و زمین است، و همگی فرمانبردار او هستند.');
INSERT INTO "hussein_taji" VALUES(30,27,'30:27','و او کسی است آفرینش را آغاز می‌کند، سپس آن را باز می‌گرداند و این (باز گرداندن) بر او آسان‌تر است، و توصیف برتر در آسمان‌ها و زمین برای اوست، و او پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(30,28,'30:28','(الله) برای شما از خود‌تان مَثَلی زده است؛ آیا بردگان‌تان در آنچه به شما روزی دادایم، با شما شریک هستند، تا شما در آن برابر (و یکسان) باشید، همان‌گونه که شما از (شرکای آزاد) خود بیم دارید از آن‌ها هم بیم داشته باشید؟! این چنین آیات را برای گروهی که تعقل می‌کنند به روشنی بیان می‌کنیم.');
INSERT INTO "hussein_taji" VALUES(30,29,'30:29','بلکه ستمکاران بدون علم و آگاهی از هوی و هوس‌های خود پیروی کردند، پس آن را که الله گمراه کرده است چه کسی هدایت می‌کند؟! و برای آن‌ها هیچ یاوری نخواهد بود.');
INSERT INTO "hussein_taji" VALUES(30,30,'30:30','پس روی خود را با حق‌گرایی (و اخلاص) به سوی دین آور، فطرت الهی است، که (الله) مردم را بر آن آفریده است، دگرگونی در آفرینش الله نیست، این است دین استوار، و لیکن بیشتر مردم نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(30,31,'30:31','به سوی او باز گردید، و از او بترسید، و نماز را بر پا دارید، و از مشرکان نباشید.');
INSERT INTO "hussein_taji" VALUES(30,32,'30:32','از کسانی‌که دین خود را پاره پاره کردند، و فرقه فرقه شدند، و هر گروهی به آنچه نزد خود دارند؛ خوشحالند.');
INSERT INTO "hussein_taji" VALUES(30,33,'30:33','و هنگامی‌که (رنج و) زیانی به مردم برسد پروردگار‌شان را می‌خوانند، (و توبه‌کنان) به سوی او باز می‌گردند، سپس هنگامی‌که رحمتی از خودش به آنان بچشاند، ناگاه گروهی از آنان به پروردگارشان شرک می‌آورند.');
INSERT INTO "hussein_taji" VALUES(30,34,'30:34','تا به آنچه که به آن‌ها داده‌ایم؛ کفران کنند، پس (از لذات دنیا) بهره بگیرید، که به زودی خواهید دانست.');
INSERT INTO "hussein_taji" VALUES(30,35,'30:35','آیا دلیل محکمی بر آن‌ها نازل کردیم، پس آن (دلیل) از چیزی‌که با الله شریک می‌سازند، سخن می‌گوید؟!');
INSERT INTO "hussein_taji" VALUES(30,36,'30:36','و هنگامی‌که رحمتی به مردم بچشانیم به آن خوشحال می‌شوند، و اگر به خاطر اعمالی‌که انجام داده‌اند؛ رنج و مصیبتی به آن‌ها برسد، ناگهان مأیوس می‌شوند.');
INSERT INTO "hussein_taji" VALUES(30,37,'30:37','آیا ندیدند که الله روزی را برای هر کس که بخواهد فراخ می‌گرداند، و (یا) تنگ می‌گیرد؟! بی‌گمان در این (گشایش و تنگی) نشانه‌هایی است برای گروهی که ایمان می‌آورند.');
INSERT INTO "hussein_taji" VALUES(30,38,'30:38','پس حقِ خویشاوندان و مسکینان و در راه ماندگان را بده، این برای کسانی‌که رضا و روی الله را می‌طلبند؛ بهتر است، و اینانند که رستگارند.');
INSERT INTO "hussein_taji" VALUES(30,39,'30:39','و آنچه (به قصد) ربا می‌دهید تا در اموال مردم فزونی یابد، نزد الله فزونی نخواهد یافت، و آنچه را (به عنوان) زکات می‌پردازید که تنها وجه الله را می‌طلبید (برکت و فزونی می‌یابد) پس (کسانی‌که چنین کنند) اینان فزونی یافتگانند.');
INSERT INTO "hussein_taji" VALUES(30,40,'30:40','الله همان کسی است که شما را آفرید، آنگاه به شما روزی داد، پس شما را می‌میراند، آنگاه شما را زنده می‌کند، آیا هیچ یک از شریکان‌تان می‌توانند چیزی از این (کار‌ها) انجام دهند؟! او منزه و برتر است از آنچه شریک او قرار می‌دهند.');
INSERT INTO "hussein_taji" VALUES(30,41,'30:41','به سبب کار‌های‌که مردم انجام داده‌اند، فساد در خشکی و دریا آشکار شده است، تا (الله) جزای بعضی از آنچه را که انجام داده‌اند، به آنان بچشاند، باشد که آنان (به سوی حق) باز گردند.');
INSERT INTO "hussein_taji" VALUES(30,42,'30:42','(ای پیامبر!) بگو: «در زمین سیر کنید، پس بنگرید عاقبت کسانی‌که پیش از این بودند؛ چگونه بود، بیشتر آن‌ها مشرک بودند.');
INSERT INTO "hussein_taji" VALUES(30,43,'30:43','پس روی خود را به آیین پایدار بیاور، پیش از آن‌که روزی فرا رسد که برایش هیچ بازگشتی از (سوی) الله نیست، آن روز (مردم) متفرق شوند.');
INSERT INTO "hussein_taji" VALUES(30,44,'30:44','کسی‌که کفر ورزد، پس کفرش بر زیان (خود) اوست، و کسی‌که کار شایسته انجام دهد، پس برای خودشان آماده می‌سازند.');
INSERT INTO "hussein_taji" VALUES(30,45,'30:45','تا (الله) کسانی را ایمان آورده‌اند و کارهای شایسته انجام داده‌اند از فضل خود پاداش دهد، بی‌گمان او کافران را دوست نمی‌دارد.');
INSERT INTO "hussein_taji" VALUES(30,46,'30:46','و از نشانه‌های (عظمت) او آن است که باد‌های مژده‌دهنده را می‌فرستد، و تا از رحمت خود به شما بچشاند، و تا کشتی‌ها به فرمان او حرکت کنند، و تا از فضل او (روزی) بجویید، و باشد که سپاس گویید.');
INSERT INTO "hussein_taji" VALUES(30,47,'30:47','و به راستی پیش از تو پیامبرانی را به سوی قوم‌شان فرستادیم، پس با دلایل روشن نزد‌شان آمدند، آنگاه از کسانی‌که گناه کردند، انتقام گرفتیم، (و مؤمنان را یاری دادیم) و یاری دادن مؤمنان بر (عهدۀ) ما بود.');
INSERT INTO "hussein_taji" VALUES(30,48,'30:48','الله همان کسی است که باد‌ها را می‌فرستد، آنگاه ابر‌ها را به حرکت در می‌آورد، سپس آن (ابر‌ها) را در آسمان آن‌گونه که بخواهد می‌گستراند، و آن را پاره پاره می‌گرداند، پس می‌بینی‌که دانه‌های باران از لابه لای آن خارج می‌شود. پس هنگامی‌که این (بارانِ حیات بخش) را به هر کس از بندگانش که بخواهد برساند، ناگهان آنان خوشحال می‌شوند.');
INSERT INTO "hussein_taji" VALUES(30,49,'30:49','و اگر چه پیش از آن که (باران رحمت) بر آنان نازل شود؛ مأیوس بودند.');
INSERT INTO "hussein_taji" VALUES(30,50,'30:50','پس به آثار رحمت الله بنگر که چگونه زمین را بعد از مردنش زنده می‌کند، یقیناً این (الله) زنده کنندۀ مردگان است، و او بر همه چیز تواناست.');
INSERT INTO "hussein_taji" VALUES(30,51,'30:51','و اگر بادی بفرستیم (گرم و سوزان) آنگاه آن (کشتزار‌ها) را زرد شده ببینند، (مأیوس شده، و) پس از آن ناسپاس می‌شوند.');
INSERT INTO "hussein_taji" VALUES(30,52,'30:52','پس (ای پیامبر!) تو نمی‌توانی (سخنت را) به (گوش) مردگان بشنوانی، و نمی‌توانی کران را هنگامی‌که پشت‌کنان روی می‌گردانند؛ سخن (و ندای خود را) بشنوانی.');
INSERT INTO "hussein_taji" VALUES(30,53,'30:53','و تو نمی‌توانی کوران را از گمراهی‌شان (باز گردانی و) هدایت کنی، تو تنها می‌توانی (سخن خود را) به کسانی بشنوانی که به آیات ما ایمان دارند، پس آن‌ها (در برابر حق) تسلیم شدگان هستند.');
INSERT INTO "hussein_taji" VALUES(30,54,'30:54','الله همان کسی است که شما را ناتوان آفرید، سپس بعد از ناتوانی، قوت بخشید، و آنگاه بعد از قوت ناتوانی و پیری قرار داد، هرچه بخواهد می‌آفریند، و او دانای تواناست.');
INSERT INTO "hussein_taji" VALUES(30,55,'30:55','و روزی‌که قیامت بر پا شود، مجرمان سوگند یاد می‌کنند که جز ساعتی (در دنیا) درنگ نکردند! (آری) این گونه (از حقیقت و راه حق) باز گردانده می‌شوند.');
INSERT INTO "hussein_taji" VALUES(30,56,'30:56','و کسانی‌که دانش و ایمان داده شده‌اند، گویند: «به راستی شما به فرمان کتاب الله (لوح محفوظ) تا روز رستاخیز درنگ کردید، پس این روز رستاخیز است، لیکن شما نمی‌دانستید».');
INSERT INTO "hussein_taji" VALUES(30,57,'30:57','پس آن روز کسانی‌که ستم کردند؛ عذر خواهی‌شان سودی ندارد و به آنان مهلت (توبه) داده نخواهد شد.');
INSERT INTO "hussein_taji" VALUES(30,58,'30:58','به راستی برای مردم در این قرآن از هر گونه مَثَلی آوردیم، و اگر نشانه‌ای برای آنان بیاوری، البته کسانی‌که کافر شدند، گویند: «شما جز بیهوده گویان نیستید».');
INSERT INTO "hussein_taji" VALUES(30,59,'30:59','این گونه الله بر دل‌های کسانی‌که نمی‌دانند، مهر می‌نهد.');
INSERT INTO "hussein_taji" VALUES(30,60,'30:60','پس (ای پیامبر گرامی!) صبر پیشه کن، بی‌گمان وعدۀ الله حق است، و هرگز کسانی‌که یقین ندارند تو را سبک‌سر نگردانند.');
INSERT INTO "hussein_taji" VALUES(31,1,'31:1','الم (الف. لام. میم).');
INSERT INTO "hussein_taji" VALUES(31,2,'31:2','این آیات کتاب حکیم است.');
INSERT INTO "hussein_taji" VALUES(31,3,'31:3','هدایت و رحمت برای نیکوکاران است.');
INSERT INTO "hussein_taji" VALUES(31,4,'31:4','کسانی‌که نماز را بر پا می‌دارند، و زکات را می‌پردازند، و آن‌ها به آخرت یقین دارند.');
INSERT INTO "hussein_taji" VALUES(31,5,'31:5','اینان از جانب پرودگار‌شان بر هدایت هستند، و اینان رستگارانند.');
INSERT INTO "hussein_taji" VALUES(31,6,'31:6','و از مردم کسی هست که سخنان بیهوده را می‌خرد؛ تا به نادانی (مردم را) از راه الله گمراه سازد، و آن (آیات) را به ریخشند می‌گیرد، اینان برایشان عذاب خوارکننده‌ای است.');
INSERT INTO "hussein_taji" VALUES(31,7,'31:7','و هنگامی‌که آیات ما بر او خوانده شود، تکبر‌کنان روی می‌گرداند، گویا که آن را نشنیده است، گویا در گوش‌هایش سنگینی است، پس او را به عذابی درد‌ناک بشارت ده.');
INSERT INTO "hussein_taji" VALUES(31,8,'31:8','بی‌گمان کسانی‌که ایمان آوردند و کار‌های شایسته انجام دادند، برای آن‌ها باغ‌های پر نعمت (بهشت) است.');
INSERT INTO "hussein_taji" VALUES(31,9,'31:9','جاودانه در آن خواهند ماند، وعدۀ الله راست است، و او پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(31,10,'31:10','(الله) آسمان‌ها را بدون ستون آفرید که می‌بینید، و در زمین کوه‌های استوار افکند تا شما را نلرزاند، و از هرگونه جنبنده‌ای در آن بپراکند، و از آسمان آبی نازل کردیم، آنگاه در آن (زمین) هر جفت (گیاه) پر ارزش (و نیکو) رویاندیم.');
INSERT INTO "hussein_taji" VALUES(31,11,'31:11','این آفرینش الله است، پس به من نشان دهید کسانی‌که غیر او هستند، چه چیزی را آفریده‌اند؟! بلکه ستمکاران در گمراهی آشکاری هستند.');
INSERT INTO "hussein_taji" VALUES(31,12,'31:12','و به راستی به لقمان حکمت دادیم، (و به او گفتیم) که: شکر الله را به جای آور؛ و هرکس شکر کند تنها به سود خود شکر کرده است، و هر کس که کفران کند، پس بی‌تردید الله بی‌نیاز ستوده است.');
INSERT INTO "hussein_taji" VALUES(31,13,'31:13','و (به یاد آور) هنگامی را که لقمان به فرزندش در حالی‌که او را پند می‌داد، گفت: «ای پسرک من! به الله شرک نیاور، بی‌گمان شرک، ستم بزرگی است.');
INSERT INTO "hussein_taji" VALUES(31,14,'31:14','و به انسان دربارۀ پدر و مادرش سفارش کردیم، مادرش او را با ناتوانی روی ناتوانی حمل کرد، و از شیر باز گرفتنش در دو سال است، (آری، سفارش کردیم) که برای من و برای پدر و مادرت شکر به جای آور که باز‌گشت (همۀ شما) به سوی من است.');
INSERT INTO "hussein_taji" VALUES(31,15,'31:15','و اگر آن دو (مشرک باشند و) تلاش کنند که تو چیزی را که به آن علم نداری شریک من قرار دهی، پس از آنان اطاعت نکن، و در دنیا با آنان به شایستگی رفتار کن، و از راه کسی پیروی کن که (توبه‌کنان) به سوی من باز آمده است، سپس بازگشت (همۀ) شما به سوی من است، آنگاه شما را از آنچه انجام می‌دادید؛ آگاه می‌کنم.');
INSERT INTO "hussein_taji" VALUES(31,16,'31:16','ای پسرک من! اگر (عمل نیک یا بد) به‌اندازۀ سنگینی دانۀ خردل باشد، آنگاه در درون تختۀ سنگی یا در آسمان‌ها یا در زمین باشد، الله آن را (در روز قیامت برای حساب) می‌آورد، بی‌گمان الله باریک‌بین آگاه است.');
INSERT INTO "hussein_taji" VALUES(31,17,'31:17','ای پسرک من! نماز را بر پا دار، و امر به معروف کن و نهی از منکر کن، و بر هر آنچه به تو برسد صبر کن، بی‌تردید این از کار‌های سترگ است.');
INSERT INTO "hussein_taji" VALUES(31,18,'31:18','و با بی اعتنایی از مردم روی مگردان، و مغرورانه در زمین راه مرو، بی‌گمان الله هیچ متکبر خود‌ستای را دوست ندارد.');
INSERT INTO "hussein_taji" VALUES(31,19,'31:19','و در راه رفتنت اعتدال را رعایت کن، و از صدای خود بکاه (و هرگز فریاد نزن) زیرا زشت‌ترین صدا‌ها؛ صدای خران است.');
INSERT INTO "hussein_taji" VALUES(31,20,'31:20','آیا ندیدید که الله آنچه را که در آسمان‌ها و آنچه را که در زمین است؛ مسخر شما کرده است، و نعمت‌های خود را آشکار و پنهان (بطور فراوان) بر شما ارزانی داشته است؟! و از مردم کسی هست که بدون هیچ دانش و هدایت و کتاب روشنگری دربارۀ الله مجادله می‌کند.');
INSERT INTO "hussein_taji" VALUES(31,21,'31:21','و هنگامی‌که به آنان گفته شود: «از آنچه الله نازل کرده است؛ پیروی کنید» گویند: «(نه) بلکه ما از چیزی پیروی می‌کنیم که نیاکان خود را بر آن یافتیم». آیا حتی اگر شیطان آنان را به سوی عذاب (آتش) سوزان دعوت کند (باز هم ازآن‌ها پیروی می‌کنند؟!)');
INSERT INTO "hussein_taji" VALUES(31,22,'31:22','و کسی‌که روی خویش را تسلیم الله کند در حالی‌که نیکو‌کار باشد، به راستی به دستگیرۀ محکمی چنگ زده است، و سرانجام (همۀ) کار‌ها به سوی الله است.');
INSERT INTO "hussein_taji" VALUES(31,23,'31:23','و کسی‌که کافر شود، پس کفر او تو را غمگین نسازد، بازگشت (همۀ) آنان به سوی ماست، پس آنان را از آنچه انجام داده‌اند؛ آگاه خواهیم کرد، بی‌تردید الله به آنچه درون سینه‌هاست؛ دانا است.');
INSERT INTO "hussein_taji" VALUES(31,24,'31:24','آن‌ها را اندکی (از متاع دنیا) بهره‌مند می‌کنیم، سپس آن‌ها را به (تحمل) عذاب سختی ناگزیر سازیم.');
INSERT INTO "hussein_taji" VALUES(31,25,'31:25','و اگر از آن‌ها بپرسی: «چه کسی آسمان‌ها و زمین را آفرید؟!» مسلماً می‌گویند: «الله». بگو: «حمد و ستایش مخصوصِ الله است» بلکه بیشتر آن‌ها نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(31,26,'31:26','آنچه در آسمان‌ها و زمین است از آنِ الله است، بی‌گمان الله است که بی‌نیاز ستوده است.');
INSERT INTO "hussein_taji" VALUES(31,27,'31:27','و اگر (همۀ) درختان روی زمین قلم شود، و دریا (مرکب گردد) و هفت دریای دیگر به مدد آن بیاید، سخنان الله پایان نمی‌یابد، بی‌تردید الله پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(31,28,'31:28','آفرینش و بر انگیختن (همۀ) شما (در روز قیامت) جز مانند (آفرینش و زنده کردن) یک تن بیش نیست، بی‌گمان الله شنوای بیناست.');
INSERT INTO "hussein_taji" VALUES(31,29,'31:29','آیا ندیدی‌که الله شب را در روز داخل می‌کند، و روز را در شب داخل می‌گرداند، و خورشید و ماه را مسخر کرده‌که هر یک تا زمانی معین در حرکت است، و یقیناً الله به آنچه انجام می‌دهید؛ آگاه است.');
INSERT INTO "hussein_taji" VALUES(31,30,'31:30','این بدان (سبب) است که الله حق است، و همانا آنچه به جز او می‌خوانند؛ باطل است، و همانا الله است که بالا و بلند مرتبۀ بزرگ است.');
INSERT INTO "hussein_taji" VALUES(31,31,'31:31','آیا ندیدی که کشتی به (فرمان و) نعمت الله در دریا حرکت می‌کند، تا (الله) بخشی از نشانه‌های خود را به شما نشان دهد؟! بی‌گمان در این برای هر شکیبای سپاسگزار نشانه‌هایی است.');
INSERT INTO "hussein_taji" VALUES(31,32,'31:32','و هنگامی‌که (در دریا) موجی همچون سایه‌بان آن‌ها را بپوشاند، الله را با اخلاص می‌خوانند، پس چون آن‌ها را (با آوردن) به خشکی نجات داد، آنگاه بعضی از آنان راه اعتدال را پیش می‌گیرند. (و بر ایمان و عهد خود می‌مانند، در حالی‌که بعضی دیگر راه کفر را پیش می‌گیرند) و آیات ما را هیچ کس جز پیمان‌شکنان ناسپاس؛ انکار نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(31,33,'31:33','ای مردم! از پروردگار‌تان بترسید، و از روزی بترسید که نه پدر کیفر فرزندش را به عهده می‌گیرید، و نه فرزند چیزی از کیفر (اعمال) پدرش را عهده دار می‌شود، یقیناً وعدۀ الله حق است، پس زندگی دنیا شما را نفریبد، و (شیطان) فریبکار شما را نسبت به الله نفریبد.');
INSERT INTO "hussein_taji" VALUES(31,34,'31:34','بی‌گمان علم (وقوع) قیامت نزد الله است، و (او) باران را نازل می‌کند، و آنچه را که در رحم‌هاست می‌داند، و هیچ کس نمی‌داند فردا چه چیز به دست می‌آورد، و هیچ کس نمی‌داند که در کدام سرزمین می‌میرد، یقیناً الله دانای آگاه است.');
INSERT INTO "hussein_taji" VALUES(32,1,'32:1','الم (الف. لام. میم)');
INSERT INTO "hussein_taji" VALUES(32,2,'32:2','نازل شدن (این) کتاب، که در آن هیچ شکی نیست، از سوی پروردگار جهانیان است.');
INSERT INTO "hussein_taji" VALUES(32,3,'32:3','آیا می‌گویند: «(محمد-صلی الله علیه وسلم-) آن را به دروغ بافته است؟» (نه) بلکه آن حق است، از جانب پرودگار توست، تا هشدار دهی قومی را که پیش از تو هشدار دهنده‌ای برایشان نیامده است، شاید هدایت شوند.');
INSERT INTO "hussein_taji" VALUES(32,4,'32:4','الله کسی است که آسمان‌ها و زمین و آنچه را میان آن‌هاست در شش روز آفرید، سپس بر عرش قرار گرفت، شما را جز او هیچ کار ساز و شفیعی نیست، آیا پند نمی‌گیرید؟!');
INSERT INTO "hussein_taji" VALUES(32,5,'32:5','کار (جهان هستی) را از آسمان تا زمین تدبیر می‌کند، سپس در روزی‌که مقدار آن هزار سال از سال‌هایی است که شما می‌شمارید؛ به سوی او بالا می‌رود.');
INSERT INTO "hussein_taji" VALUES(32,6,'32:6','این (توصیفِ) دانای پنهان و آشکار، پیروزمند مهربان است.');
INSERT INTO "hussein_taji" VALUES(32,7,'32:7','(همان) کسی‌که هر چه را آفرید به نیکو‌ترین (وجه) آفرید، و آفرینش انسان را از گِل آغاز کرد.');
INSERT INTO "hussein_taji" VALUES(32,8,'32:8','سپس نسل او را از عصاره‌ای از آب ناچیز پدید آورد.');
INSERT INTO "hussein_taji" VALUES(32,9,'32:9','آنگاه (اندام) او را سامان داد، و از روح خود در آن دمید، و برای شما گوش و چشم‌ها و دل‌ها قرار داد، چه اندک سپاس می‌گزارید.');
INSERT INTO "hussein_taji" VALUES(32,10,'32:10','و (کافران) گفتند: «آیا هنگامی‌که (مردیم و) در زمین ناپدید شدیم، آیا در آفرینش تازه‌ای خواهیم بود؟!» بلکه آنان به دیدار پروردگارشان کافرند.');
INSERT INTO "hussein_taji" VALUES(32,11,'32:11','(ای پیامبر!) بگو: «فرشتۀ مرگ که بر شما گماشته شده؛ جان‌تان را می‌گیرد، سپس به سوی پروردگارتان باز گردانده می‌شوید».');
INSERT INTO "hussein_taji" VALUES(32,12,'32:12','و اگر ببینی مجرمان را هنگامی‌که در پیشگاه پروردگارشان سر‌‌‌های‌شان به زیر افکنده‌اند، (گویند:) «پروردگارا! دیدیم، و شنیدیم، پس ما را بازگردان تا کار شایسته‌ای انجام دهیم، بی‌گمان ما (به قیامت) یقین داریم»');
INSERT INTO "hussein_taji" VALUES(32,13,'32:13','و اگر می‌خواستیم به هرکس هدایتش را می‌دادیم، و لیکن سخن (و و عدۀ) من حق است که جهنم را از جنیان و آدمیان همگی پُر می‌کنم.');
INSERT INTO "hussein_taji" VALUES(32,14,'32:14','پس به کیفر آن‌که دیدار امروزتان را فراموش کردید، (عذاب را) بچشید، بی‌گمان ما (نیز) شما را فراموش کردیم. و عذاب جاودان را بخاطر اعمالی‌که انجام می‌دادید؛ بچشید.');
INSERT INTO "hussein_taji" VALUES(32,15,'32:15','تنها کسانی به آیات ما ایمان می‌آورند که چون به آن پند داده شوند، سجده‌کنان (به زمین) افتند، و پروردگارشان را به پاکی بستایند، و آنان تکبر نمی‌کنند.');
INSERT INTO "hussein_taji" VALUES(32,16,'32:16','پهلو‌هایشان از بسترها (در دل شب) دور می‌شود، پروردگار‌شان را با بیم و امید می‌خوانند، و از آنچه به آن‌ها روزی داده‌ایم انفاق می‌کنند.');
INSERT INTO "hussein_taji" VALUES(32,17,'32:17','پس هیچ کس نمی‌داند چه پاداش‌های (عظیمی) که مایۀ روشنی چشم‌هاست برای آن‌ها نهفته شده است، به (پاس) آنچه که (در دنیا) انجام می‌دادند.');
INSERT INTO "hussein_taji" VALUES(32,18,'32:18','آیا کسی‌که مؤمن است همچون کسی است که فاسق است؟! (نه) هرگز برابر نیستند.');
INSERT INTO "hussein_taji" VALUES(32,19,'32:19','اما کسانی‌که ایمان آوردند و کارهای شایسته انجام دادند، پس برای آن‌ها باغ‌های بهشت جاویدان خواهد بود، (این به عنوان) محل پذیرایی (از آن‌ها) است؛ به پاداش آنچه که انجام می‌دادند.');
INSERT INTO "hussein_taji" VALUES(32,20,'32:20','و اما کسانی‌که فاسق شدند، پس جایگاه‌شان آتش (جهنم) است، هرگاه که بخواهند از آن خارج شوند، به آن باز گردانده شوند، و به آن‌ها گفته شود: «بچشید عذاب آتشی را که آن را تکذیب می‌کردید».');
INSERT INTO "hussein_taji" VALUES(32,21,'32:21','و یقیناً به آنان از عذاب نزدیک (این دنیا) پیش از عذاب بزرگ (آخرت) می‌چشانیم، شاید باز گردند.');
INSERT INTO "hussein_taji" VALUES(32,22,'32:22','و کیست ستمکار‌تر از آن کسی‌که به آیات پروردگارش پند داده شد، آنگاه از آن اعراض کند؟ بی‌گمان ما از مجرمان انتقام خواهیم گرفت.');
INSERT INTO "hussein_taji" VALUES(32,23,'32:23','به راستی به موسی کتاب (تورات) دادیم، پس (ای پیامبر گرامی!) از دیدار او (= موسی) در شک و تردید مباش و ما آن (تورات) را (وسیلۀ) هدایت برای بنی اسرائیل قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(32,24,'32:24','و از آنان پیشوایانی قرار دادیم که به فرمان ما (مردم را) هدایت می‌کردند، چون شکیبایی ورزیدند، و به آیات ما یقین داشتند.');
INSERT INTO "hussein_taji" VALUES(32,25,'32:25','مسلماً پروردگار‌‌‌‌‌ توست که در روز قیامت میان آن‌ها در آنچه اختلاف می‌کردند؛ داوری می‌کند.');
INSERT INTO "hussein_taji" VALUES(32,26,'32:26','آیا برای آن‌ها روشن نشده است که چه نسل‌های بسیاری را پیش از آن‌ها هلاک کردیم؟! که اینان در خانه‌های (ویران شدۀ) آن‌ها راه می‌روند، بی‌گمان در این نشانه‌ها (و عبرت‌ها) است، آیا نمی‌شنوند؟!');
INSERT INTO "hussein_taji" VALUES(32,27,'32:27','آیا ندیدند که ما آب را به سوی زمین خشک (و بی‌گیاه) می‌رانیم، پس به (وسیلۀ) آن کشتزار را بیرون می‌آوریم، که چهارپایان‌شان و خود‌‌‌‌شان از آن می‌خورند، آیا نمی‌بینند؟!');
INSERT INTO "hussein_taji" VALUES(32,28,'32:28','و گویند: «اگر راستگو‌‌یید، این پیروزی کی خواهد بود؟!»');
INSERT INTO "hussein_taji" VALUES(32,29,'32:29','بگو: «روز پیروزی (هنگام نزول عذاب یا روز قیامت)، کسانی‌که کافر شدند؛ ایمان شان سودی نخواهد داشت، و نه به آن‌ها مهلت داده می‌شود».');
INSERT INTO "hussein_taji" VALUES(32,30,'32:30','سپس (ای پیامبر!) از آن‌ها روی بگردان، و منتظر باش که آن‌ها (نیز) منتظر هستند.');
INSERT INTO "hussein_taji" VALUES(33,1,'33:1','ای پیامبر! از الله بترس، و از کافران و منافقان اطاعت نکن، بی‌گمان الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(33,2,'33:2','و از آنچه از سوی پروردگارت به تو وحی می‌شود؛ پیروی کن، یقیناً الله به آنچه انجام می‌دهید؛ آگاه است.');
INSERT INTO "hussein_taji" VALUES(33,3,'33:3','و بر الله توکل کن، و الله (به عنوان) کارساز (و مدافع) بسنده است.');
INSERT INTO "hussein_taji" VALUES(33,4,'33:4','الله برای هیچ‌کس دو قلب در درونش قرار نداده است، و هرگز همسران‌تان را که مورد ظهار قرار می‌دهید؛ مادران شما قرار نداده است [ به سورۀ مجادله آیه و 3 رجوع شود.]، و (نیز) فرزند خوانده‌های شما را فرزند (حقیقی) شما قرار نداده است، این سخن (باطل) شماست که به دهان خود می‌گویید، و الله سخن حق را می‌گوید، و او به راه راست هدایت می‌کند.');
INSERT INTO "hussein_taji" VALUES(33,5,'33:5','آن‌ها (= فرزند خوانده‌ها) را به نام پدران‌شان بخوانید که این (کار) نزد الله منصفانه‌تر است، پس اگر پدران‌شان را نمی‌شناسید، برادران دینی شما، و موالی شما هستند. و در آنچه که (قبلاً) اشتباه کرده‌اید؛ گناهی بر شما نیست، و لیکن آنچه را که دل‌های شما از روی عمد می‌خواهد (و می‌گویید، گناه است) و الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(33,6,'33:6','پیامبر نسبت به مؤمنان از خودشان سزاوار‌تر است، و همسران او مادران آن‌ها هستند، و در کتاب الله خویشاوندان برخی از آنان به برخی (دیگر) از (سایر) مؤمنان و مهاجران سزاوارترند [ یعنی باید مؤمنان پیامبر گرامی صلی الله علیه وسلم را از جان خود بیشتر دوست داشته باشند.]، مگر این‌که به دوستان‌تان نیکی کنید (و سهمی از اموال خود را به آنان بدهید) این (حکم) در کتاب (لوح محفوظ) نوشته شده است.');
INSERT INTO "hussein_taji" VALUES(33,7,'33:7','و (ای پیامبر گرامی! به یاد آور) هنگامی را که از پیامبران پیمان شان را گرفتیم و (همچنین) از تو و از نوح و ابراهیم و موسی و عیسی بن مریم، و از همۀ آنان پیمان محکمی گرفتیم.');
INSERT INTO "hussein_taji" VALUES(33,8,'33:8','تا (الله) راستگویان را از صدق‌شان بپرسد، و برای کافران عذابی دردناک آماده کرده است.');
INSERT INTO "hussein_taji" VALUES(33,9,'33:9','ای کسانی‌که ایمان آورده‌اید! نعمت الله را بر خود به یاد آورید، هنگامی‌که لشکر‌هایی به سوی شما آمدند، پس ما تند باد (سختی) بر آن‌ها فرستادیم، و لشکر‌هایی (از فرشتگان) که آن‌ها را نمی‌دیدید، (و آن‌ها را درهم کوبیدیم) و الله به آنچه انجام می‌دهید؛ بیناست.');
INSERT INTO "hussein_taji" VALUES(33,10,'33:10','هنگامی‌که آن‌ها (= دشمنان در غزوۀ احزاب) از (سمت) بالای (شهر) شما و از (سمت) پایین (شهر) تان به سوی شما آمدند (و شما را محاصره کردند) و زمانی را که چشم‌ها (از شدت و حشت) خیره شد، و دل‌ها به حنجره‌ها رسید، و گمان های گوناگونی به الله می‌بردید.');
INSERT INTO "hussein_taji" VALUES(33,11,'33:11','آنجا بود که مؤمنان آزمایش شدند و به سختی تکان خوردند.');
INSERT INTO "hussein_taji" VALUES(33,12,'33:12','و (نیز به یاد آورید) هنگامی را که منافقان و کسانی‌که در دل‌های شان بیماری است، می‌گفتند: «الله و پیامبرش جز فریب به ما وعده‌ای نداده است».');
INSERT INTO "hussein_taji" VALUES(33,13,'33:13','و (نیز) هنگامی‌که گروهی از آنان گفتند: «ای اهل یثرب! اینجا جای ماندن شما نیست، پس (به خانه‌های خود) برگردید» و گروهی از آنان از پیامبر اجازۀ (بازگشت) می‌خواستند، می‌گفتند: «بی‌شک خانه‌های ما بی حفاظ است» در حالی‌که بی حفاظ نبود، آن‌ها فقط می‌خواستند (از جهاد) فرار کنند.');
INSERT INTO "hussein_taji" VALUES(33,14,'33:14','و اگر (لشکر دشمنان) از اطراف مدینه بر آنان وارد می‌شدند (و خانه‌هایشان را محاصره می‌کردند) سپس پیشنهاد بازگشت به کفر و شرک (به آن‌ها) می‌کردند، قطعاً می‌پذیرفتند، و جز اندکی برای (انتخاب) آن درنگ نمی‌کردند.');
INSERT INTO "hussein_taji" VALUES(33,15,'33:15','و به راستی که آنان پیش از این با الله عهد کرده بودند که پشت (به دشمن) نکنند، و عهد (و پیمان) الله باز خواست دارد.');
INSERT INTO "hussein_taji" VALUES(33,16,'33:16','(ای پیامبر!) بگو: «اگر از مرگ یا کشته شدن فرار کنید، هرگز فرار سودی به حال شما نخواهد داشت، و آنگاه جز اندکی (از زندگی دنیا) بهره‌مند نخواهید شد».');
INSERT INTO "hussein_taji" VALUES(33,17,'33:17','(ای پیامبر!) بگو: «چه کسی است که شما را از (ارادۀ) الله حفظ می‌کند، اگر (او) برای شما بدی اراده کرده باشد، یا رحمتی برای شما اراده کرده باشد؟!» و آن‌ها برای خود جز الله هیچ دوست و یاوری نخواهند یافت.');
INSERT INTO "hussein_taji" VALUES(33,18,'33:18','قطعاً الله باز دارندگان را (از جهاد) از شما، و آنان را که به برادران‌شان گفتند: «به سوی ما بیایید» و جز اندکی به کارزار نمی‌آیند؛ می‌شناسد.');
INSERT INTO "hussein_taji" VALUES(33,19,'33:19','(در حالی‌که) آن‌ها بر شما بخل می‌ورزند، پس هنگامی‌که (زمان) ترس (پیش) آید، می‌بینی که به تو نگاه می‌کنند، در حالی‌که چشم‌هایشان (در حدقه) می‌چرخد، مانند کسی‌که از (سکرات) مرگ بی هوش شده باشد، پس چون ترس (و وحشت) بر طرف شد، شما را با زبان‌های تیز (و تند خود) می‌رنجانند، در حالی‌که بر مال (غنایم) سخت حریص و آزمندند، اینان (هرگز) ایمان نیاورده‌اند، لذا الله اعمال‌شان را تباه (و نابود) کرد، و این (کار) بر الله آسان است.');
INSERT INTO "hussein_taji" VALUES(33,20,'33:20','آن‌ها گمان می‌کنند که (لشکریان) احزاب (هنوز) نرفته‌اند، و اگر (لشکریان) احزاب (بار دیگر) بر گردند، آرزو می‌کنند، ای کاش! آنان در میان اعراب بادیه‌نشین بودند، که از اخبار شما جویا می‌شدند، و اگر در میان شما باشند؛ جز اندکی پیکار نکنند.');
INSERT INTO "hussein_taji" VALUES(33,21,'33:21','یقیناً برای شما در زندگی رسول الله سرمشق نیکویی است، برای آنان که به الله و روز آخرت امید دارند، و الله را بسیار یاد می‌کنند.');
INSERT INTO "hussein_taji" VALUES(33,22,'33:22','و چون مؤمنان (لشکر) احزاب را دیدند، گفتند: «این همان چیزی است که الله و پیامبرش به ما وعده داده، و الله و پیامبرش راست گفته‌اند» و (این امر) جز بر ایمان و تسلیم‌شان نیفزود.');
INSERT INTO "hussein_taji" VALUES(33,23,'33:23','از مؤمنان مردانی هستند که به عهد و پیمانی که با الله بسته بودند (صادقانه) وفا کردند، پس کسی از آنان هست که پیمان خود را به آخر رساند (و شهید شد) و از آنان کسی هست که در انتظار (و چشم براه) است، و هرگز تغییر و تبدیلی (در پیمان خود) نیاورده‌اند.');
INSERT INTO "hussein_taji" VALUES(33,24,'33:24','تا الله راستگویان را به (خاطر) صدق‌شان پاداش دهد، و منافقان را اگر بخواهد عذاب کند، یا توبۀ آن‌ها را بپذیرد، بی‌تردید الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(33,25,'33:25','و (در غزوۀ احزاب) الله کافران را با خشم شان (از مدینه) باز گرداند، (در حالی‌که) هیچ خیر و منفعتی نیافتند، و الله مؤمنان را از جنگ بی‌نیاز کرد، (و پیروزی نصیب‌شان ساخت) و الله توانای پیروزمند است.');
INSERT INTO "hussein_taji" VALUES(33,26,'33:26','و (الله) کسانی از اهل کتاب (= یهود بنی قریظه) را که از آن‌ها (= مشرکان) حمایت کرده بودند؛ از قلعه‌هایشان فرود آورد، و در دل‌های شان و حشت انداخت، (و شما) گروهی را می‌کشتید و گروهی را اسیر می‌گرفتید.');
INSERT INTO "hussein_taji" VALUES(33,27,'33:27','و (الله) زمین آنان و خانه‌هایشان و اموال‌شان را به شما وا گذاشت و (همچنین) زمینی را که در آن گام ننهاده بودید، و الله بر هر چیز تواناست.');
INSERT INTO "hussein_taji" VALUES(33,28,'33:28','ای پیامبر! به همسرانت بگو: «اگر شما زندگی دنیا و زینت آن می‌خواهید، پس بیایید تا (با هدیه‌ای مناسب) شما را بهرمند سازم، و شما را به طرز نیکوئی رها کنم.');
INSERT INTO "hussein_taji" VALUES(33,29,'33:29','و اگر شما الله و پیامبرش و سرای آخرت را می‌خواهید، پس (بدانید که) الله برای نیکوکارانتان پاداش عظیمی آماده کرده است».');
INSERT INTO "hussein_taji" VALUES(33,30,'33:30','ای زنان پیامبر! هر کس از شما که (گناه) نا شایست آشکاری مرتکب شود، عذاب او دو چندان افزوده می‌شود، و این بر الله آسان است.');
INSERT INTO "hussein_taji" VALUES(33,31,'33:31','و هر کس از شما برای الله و پیامبرش خضوع (و فرمانبرداری) کند، و عمل صالح انجام دهد، پاداش او را دوبار به او خواهیم داد، و روزی ارزشمندی برای او آماده کرده‌ایم.');
INSERT INTO "hussein_taji" VALUES(33,32,'33:32','ای زنان پیامبر! شما مانند هیچ یک از زنان (عادی دیگر) نیستید، اگر پرهیزگاری کنید؛ پس در سخن گفتن نرمی نکنید که آنگاه کسی‌که در دلش بیماری است؛ طمع کند، و سخن شایسته بگویید.');
INSERT INTO "hussein_taji" VALUES(33,33,'33:33','و در خانه‌های خود بمانید، و به شیو‌ۀ جاهلیت نخستین زینت‌های خود را آشکار نکنید، و نماز را بر پا دارید، و زکات را بدهید، و الله و پیامبرش را اطاعت کنید، الله قطعاً می‌خواهد پلیدی را از شما اهل بیت دور کند و چنانکه باید شما را پاک سازد.');
INSERT INTO "hussein_taji" VALUES(33,34,'33:34','و آنچه را در خانه‌هایتان از آیات الله و حکمت (= سنت پیامبر) خوانده می‌شود یاد کنید؛ بی‌گمان الله باریک‌بین آگاه است.');
INSERT INTO "hussein_taji" VALUES(33,35,'33:35','همانا مردان مسلمان و زنان مسلمان، مردان مؤمن و زنان مؤمن، مردان فرمانبردار و زنان فرمانبردار، مردان راستگو و زنان راستگو، مردان صابر و زنان صابر، مردان با خشوع و زنان با خشوع، مردان صدقه دهنده و زنان صدقه دهنده، مردان روزه دار و زنان روزه دار، مردانی‌که شرمگاه خود را حفظ می‌کنند، و زنانی‌که شرمگاه خود را حفظ می‌کنند، و مردانی‌که الله را بسیار یاد می‌کنند، و زنانی‌که الله را بسیار یاد می‌کنند، الله برای (همۀ) آنان آمرزش و پاداش عظیمی آماده کرده است.');
INSERT INTO "hussein_taji" VALUES(33,36,'33:36','و هیچ مرد مؤمن و زن مؤمنی را نسزد؛ هنگامی‌که الله و پیامبرش کاری را حکم کند، آنکه آن‌ها در کارشان اختیاری باشد، و هرکس الله و پیامبرش را نافرمانی کند، به راستی‌که در گمراهی آشکاری گرفتار شده است.');
INSERT INTO "hussein_taji" VALUES(33,37,'33:37','و (به یاد بیاور) زمانی را که به آن کس‌که الله به او نعمت داده بود، و تو (نیز) به او نعمت داده بودی، می‌گفتی: «همسرت را نگاه دار و از الله بترس» و در دل خود چیزی را پنهان می‌داشتی‌که الله آن را آشکار می‌کند، و از مردم می‌ترسیدی؛ در حالی‌که الله سزاوارتر است‌که از او بترسی، پس چون زید (بن حارثه -رضی الله عنه-) از او حاجت خویش بگزارد، (و طلاقش داد) او را به ازدواج تو در آوردیم؛ تا بر مؤمنان در (بارۀ ازدواج با) زنان پسر خواندگان‌شان هیچ حرجی نباشد؛ هنگامی‌که حاجت خویش از او بگزارده (و طلاق‌شان داده) باشند، و فرمان الله انجام شدنی است.');
INSERT INTO "hussein_taji" VALUES(33,38,'33:38','بر پیامبر در آنچه الله برای او حلال (و مقرر) کرده است؛ هیچ حرجی نیست، این سنت الله است در (بارۀ) کسانی‌که پیش از این بوده‌اند، و فرمان الله حساب شده و دقیق است.');
INSERT INTO "hussein_taji" VALUES(33,39,'33:39','(همان) کسانی‌که پیام‌های الله را می‌رسانند و از او می‌ترسند و از هیچ کس جز او نمی‌ترسند، و الله برای حساب‌گری (اعمال‌شان) کافی است.');
INSERT INTO "hussein_taji" VALUES(33,40,'33:40','محمد ( صلی الله علیه وسلم) پدر هیچ‌یک از مردان شما نیست، و لیکن رسول الله و خاتم پیامبران است، و الله به هر چیز آگاه است.');
INSERT INTO "hussein_taji" VALUES(33,41,'33:41','ای کسانی‌که ایمان آورده‌اید! الله را بسیار یاد کنید.');
INSERT INTO "hussein_taji" VALUES(33,42,'33:42','و صبح و شام او را تسبیح گویید.');
INSERT INTO "hussein_taji" VALUES(33,43,'33:43','او کسی است که بر شما درود می‌فرستد، و فرشتگانش (نیز بر شما درود می‌فرستند) تا شما را از تاریکی‌ها به سوی نور بیرون برد، و (الله) نسبت به مؤمنان مهربان است.');
INSERT INTO "hussein_taji" VALUES(33,44,'33:44','تحیت آنان در روزی‌که با او دیدار می‌کنند «سلام» است، و برای آن‌ها پاداش ارزشمندی آماده کرده است.');
INSERT INTO "hussein_taji" VALUES(33,45,'33:45','ای پیامبر! ما تو را گواه، و بشارت‌دهنده و بیم‌دهنده فرستادیم.');
INSERT INTO "hussein_taji" VALUES(33,46,'33:46','و دعوت کننده به سوی الله به فرمان او، و چراغی روشنی بخش قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(33,47,'33:47','و مؤمنان را بشارت ده که برای آن‌ها از سوی الله فضل بزرگی است.');
INSERT INTO "hussein_taji" VALUES(33,48,'33:48','و از کافران و منافقان اطاعت مکن، و آزار‌شان را و اگذار (و به آنان اعتنای نکن) و بر الله توکل کن، و الله (به عنوان) کارساز (و مدافع) بسنده است.');
INSERT INTO "hussein_taji" VALUES(33,49,'33:49','ای کسانی‌که ایمان آورده‌اید! هنگامی‌که با زنان با ایمان ازدواج کردید، آنگاه پیش از آن‌که با آن‌ها همبستری کنید، طلاق‌شان دادید، پس عده‌ای برای شما بر آن‌ها نیست که شما آن را بشمارید، لذا آن‌ها را (با هدیه‌ای مناسب) بهره‌مند سازید و به نیکوترین وجه رهای‌شان کنید.');
INSERT INTO "hussein_taji" VALUES(33,50,'33:50','ای پیامبر! ما همسرانت را که مهر‌شان را پرداخته‌ای برای تو حلال کردیم، و (نیز) کنیزانی را که (از طریق غنایم جنگی) الله به تو ارزانی داشته است، و دختران عمویت، و دختران عمه‌هایت و دختران دایی‌ات، و دختران خاله‌هایت که با تو هجرت کرده‌اند، (ازدواج با آن‌ها برای تو حلال کرده‌ایم) و (نیز) زن مؤمنی را که اگر خود را به پیامبر ببخشد، اگر پیامبر بخواهد او را به زنی گیرد، (این حکم) ویژۀ توست؛ نه دیگر مؤمنان، به راستی که ما می‌دانیم آنچه را که بر آن‌ها در (بارۀ) همسران‌شان و کنیزان‌شان واجب کرده‌ایم، تا بر تو هیچ حرجی نباشد، و الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(33,51,'33:51','(نوبت) هر یک از همسرانت را که بخواهی (می‌توانی) به تأخیر اندازی، و هرکدام را بخواهی نزد خود جای دهی، و اگر (بعضی) از آن‌ها که بر کنار ساخته‌ای بخواهی نزد خود جای دهی، هیچ گناهی بر تو نیست، این (گزینش) نزدیکتر است برای آن‌که چشم آنان روشن گردد و این‌که غمگین نباشند، و همگی‌شان به آنچه به آنان می‌دهی خشنود گردند، و الله آنچه را که در دل‌های شماست می‌داند، و الله دانای بردبار است.');
INSERT INTO "hussein_taji" VALUES(33,52,'33:52','بعد از این، (دیگر) هیچ زنی برای تو حلال نیست، و نه (حلال است) آن‌که همسران دیگری جایگزین آن‌ها کنی، هر چند زیبایی آن‌ها تو را به شگفت آورد، مگر آنچه‌که (بصورت کنیز) دست تو مالک شد، و الله بر همه چیز نگهبان است.');
INSERT INTO "hussein_taji" VALUES(33,53,'33:53','ای کسانی‌که ایمان آورده‌اید! به خانه‌های پیامبر داخل نشوید؛ مگر آنکه برای (صرف) غذا به شما اجازه داده شود، بی‌آنکه منتظر پخته شدنش باشید، ولی چون دعوت شدید، داخل شوید، پس هنگامی‌که غذا خوردید؛ پراکنده شوید، و (بعد از صرف غذا) برای بحث و گفتگو ننشینید، بی‌گمان این (عمل) پیامبر را آزار می‌دهد، پس (او) از شما شرم می‌کند (و چیزی نمی‌گوید) و الله از (بیان) حق شرم نمی‌کند، و هنگامی‌که چیزی (از وسایل زندگی به عاریت) خواستید، پس از آنان (= زنان پیامبر) از پشت پرده بخواهید، این (کار) برای دل‌های شما و دل‌های آنان پاکیزه‌تر است، و شما را نسزد که پیامبر الله را بیازارید، و نه آن‌که همسرانش را بعد از او هرگز به همسری گیرید، بی‌تردید این (کار) نزد الله (گناهی) بزرگ است.');
INSERT INTO "hussein_taji" VALUES(33,54,'33:54','اگر چیزی را آشکار کنید یا آن را پنهان دارید، پس بی‌گمان الله به هر چیز آگاه است.');
INSERT INTO "hussein_taji" VALUES(33,55,'33:55','بر آنان (= زنان) گناهی نیست (که) در (حضور) پدران‌شان و پسران‌شان و برادران‌شان و پسران برادران‌شان و پسران خواهران‌شان و زنان (هم دین) شان و بردگان‌شان (بدون حجاب باشند) و (ای زنان!) از الله بترسید، قطعاً الله بر همه چیز گواه است.');
INSERT INTO "hussein_taji" VALUES(33,56,'33:56','بی‌گمان الله و فرشتگانش بر پیامبر درود می‌فرستند؛ ای کسانی‌که ایمان آورده‌اید! بر او درود فرستید، و سلام بگویید، سلامی نیکو.');
INSERT INTO "hussein_taji" VALUES(33,57,'33:57','همانا کسانی‌که الله و پیامبرش را آزار می‌دهند، الله آن‌ها را در دنیا و آخرت لعنت کرده، و برای آن‌ها عذاب خوارکننده‌ای آماده کرده است.');
INSERT INTO "hussein_taji" VALUES(33,58,'33:58','و کسانی‌که مردان مؤمن و زنان مؤمن را بی هیچ گناهی‌که مرتکب شده باشند؛ آزار می‌دهند، به راستی (بار) بهتان و گناه آشکاری را به دوش کشیده‌اند.');
INSERT INTO "hussein_taji" VALUES(33,59,'33:59','ای پیامبر! به همسرانت و دخترانت و زنان مؤمنان بگو: «چادر‌های خود را بر خویش فرو افکنند، (و با آن سر و صورت و سینه‌هایشان را بپوشانند) این مناسب‌تر است تا شناخته شوند؛ پس مورد آزار قرار نگیرند، و الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(33,60,'33:60','اگر منافقان و کسانی‌که در دل‌های شان بیماری است، و شایعه پراکنان در مدینه (از کار خود) باز نایستند، یقیناً تو را بر (ضد) آن‌ها می‌شورانیم (و بر آن‌ها مسلط می‌گردانیم) سپس جز اندکی در کنار تو در آن (شهر مدینه) نباشند.');
INSERT INTO "hussein_taji" VALUES(33,61,'33:61','طرد شدگانند، هر کجا یافت شوند، گرفته خواهند شد، و به سختی کشته خواهند شد.');
INSERT INTO "hussein_taji" VALUES(33,62,'33:62','(این)سنت الله است در (بارۀ) کسانی‌که پیش از این گذشتند، و هرگز برای سنت الله دگرگونی نخواهی یافت.');
INSERT INTO "hussein_taji" VALUES(33,63,'33:63','(ای پیامبر!) مردم تو را از (زمان قیام) قیامت می‌پرسند، بگو: «علم آن تنها نزد الله است» و تو چه می‌دانی شاید‌که قیامت نزدیک باشد.');
INSERT INTO "hussein_taji" VALUES(33,64,'33:64','بی‌تردید الله کافران را لعنت کرده، و برای آن‌ها آتش سوزان آماده کرده است.');
INSERT INTO "hussein_taji" VALUES(33,65,'33:65','که جاودانه در آن خوهند ماند، هیچ دوست و یاوری نخواهند یافت.');
INSERT INTO "hussein_taji" VALUES(33,66,'33:66','روزی‌که صورت‌هایشان در آتش (جهنم) گردانیده شود (پشیمان می‌شوند و) می‌گویند: «ای کاش الله را اطاعت کرده بودیم، و رسول (الله) را اطاعت کرده بودیم»');
INSERT INTO "hussein_taji" VALUES(33,67,'33:67','و گفتند: «پروردگارا! ما از سروران و بزرگان خود اطاعت کردیم، پس ما را از راه به در بردند (و گمراه کردند)');
INSERT INTO "hussein_taji" VALUES(33,68,'33:68','پروردگارا! آن‌ها را از عذاب دو چندان ده، و آن‌ها را به لعنتی بزرگ (و سخت) لعنت فرما»');
INSERT INTO "hussein_taji" VALUES(33,69,'33:69','ای کسانی‌که ایمان آورده‌اید! مانند کسانی نباشید که موسی را آزار دادند، آنگاه الله او را از آنچه (در حق او) گفته بودند؛ مبّرا ساخت، و (او) نزد الله آبرومند (و صاحب منزلت) بود.');
INSERT INTO "hussein_taji" VALUES(33,70,'33:70','ای کسانی‌که ایمان آورده‌اید! از الله بترسید، و سخن درست (و حق) بگویید.');
INSERT INTO "hussein_taji" VALUES(33,71,'33:71','تا (الله) کارهای‌تا‌ن را برایتان اصلاح کند، و گناهان‌تان را بیامرزد، و هرکس‌که از الله و پیامبرش اطاعت کند؛ یقیناً به کامیابی عظیمی نائل شده است.');
INSERT INTO "hussein_taji" VALUES(33,72,'33:72','همانا ما امانت (خویش) را بر آسمان‌ها و زمین و کوه‌ها عرضه داشتیم، پس آن‌ها از تحمل آن سر باز زدند و از آن ترسیدند، و انسان آن را بر دوش گرفت که او بسیار ستمکار نادان است.');
INSERT INTO "hussein_taji" VALUES(33,73,'33:73','تا (سرانجام) الله مردان منافق و زنان منافق و مردان مشرک و زنان مشرک را عذاب کند، و الله توبۀ مردان مؤمن و زنان مؤمن را بپذیرد (و از گناهان‌شان در گذرد) و الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(34,1,'34:1','حمد (و ستایش) مخصوصاللهِ است که آنچه در آسمان‌ها و آنچه در زمین است از آنِ اوست، و ستایش در (سرای) آخرت (نیز) از آنِ اوست، و او حکیم آگاه است.');
INSERT INTO "hussein_taji" VALUES(34,2,'34:2','آنچه را که در زمین فرو می‌رود و آنچه را که از آن بیرون می‌آید، و آنچه را که از آسمان نازل می‌شود و آنچه را که در آن بالا می‌رود؛ می‌داند، و او مهربان آمرزنده است.');
INSERT INTO "hussein_taji" VALUES(34,3,'34:3','و کسانی‌که کافر شدند، گفتند: «قیامت به سراغ ما نخواهد آمد» (ای پیامبر!) بگو: «آری، به پروردگارم (آن) دانای غیب سوگند که حتماً به سراغ شما خواهد آمد، به‌اندازۀ ذره‌ای در آسمان‌ها و در زمین از او پنهان نیست، و نه کوچکتر از آن و نه بزرگتر، مگر (اینکه) در کتابی آشکار (ثبت) است.');
INSERT INTO "hussein_taji" VALUES(34,4,'34:4','تا کسانی را که ایمان آورده‌اند و کار‌های شایسته انجام داده‌اند؛ پاداش دهد، آنان برایشان مغفرت و روزی ارزشمندی است.');
INSERT INTO "hussein_taji" VALUES(34,5,'34:5','و کسانی‌که مبارزه‌کنان در (انکار) آیات ما تلاش کردند (و گمان کردند که ما را به ستوه می‌آورند) برای آنان عذابی از کیفر دردناک خواهد بود.');
INSERT INTO "hussein_taji" VALUES(34,6,'34:6','و کسانی‌که به ایشان علم داده شده است؛ می‌دانند آنچه را که از سوی پروردگارت بر تو نازل شده، حق است، و به راه (الله) پیروزمند ستوده هدایت می‌کند.');
INSERT INTO "hussein_taji" VALUES(34,7,'34:7','و کسانی‌که کافر شدند، گفتند: «آیا مردی به شما نشان دهیم که به شما خبر می‌دهد هنگامی‌که (مردید و) کاملاً متلاشی (و پراکنده) شدید، همانا شما (بار دیگر) در آفرینش تازه‌ای خواهید بود؟!');
INSERT INTO "hussein_taji" VALUES(34,8,'34:8','آیا بر الله دروغ بسته یا به او دیوانگی است؟!» (چنین نیست) بلکه کسانی‌که به آخرت ایمان نمی‌آورند در عذاب و گمراهی دور و درازی هستند.');
INSERT INTO "hussein_taji" VALUES(34,9,'34:9','آیا به آنچه پیش روی و آنچه پشت سر آنان از آسمان و زمین است، نگاه نکرده‌اند؟! اگر بخواهیم آن‌ها را در زمین فرو می‌بریم، یا تکه‌ای از آسمان را بر (سر) آنان می‌افکنیم، بی‌گمان در این نشانه‌ای برای هر بندۀ بازگشت کننده‌ای است.');
INSERT INTO "hussein_taji" VALUES(34,10,'34:10','و به راستی (ما) به داوود از سوی خود فضیلتی (بزرگ) دادیم، (گفتیم:) ای کوه‌ها و ای پرندگان! با او (در تسبیح گفتن) هم آواز شوید، و آهن را برای او نرم کردیم.');
INSERT INTO "hussein_taji" VALUES(34,11,'34:11','(و به او دستور دادیم) که زره‌های بلند (و فراخ) بساز و در بافتن (حلقه‌ها) اندازه نگه دار، و کار شایسته انجام دهید، همانا من به آنچه انجام می‌دهید؛ بینا هستم.');
INSERT INTO "hussein_taji" VALUES(34,12,'34:12','و برای سلیمان باد را (مسخر ساختیم) که صبحگاهان مسیر یک ماه را طی می‌کرد، و شامگاهان مسیر یک ماه را، و چشمۀ مس (مذاب) را برایش روان ساختیم، و از جن کسانی را (مسخر کردیم) که به فرمان پروردگارش در پیش او کار می‌کردند، و هر که از آنان که از فرمان ما سرپیچی می‌کرد، او را از عذاب آتش سوزان می‌چشاندیم.');
INSERT INTO "hussein_taji" VALUES(34,13,'34:13','(سلیمان) هر چه می‌خواست (جن‌ها) برایش می‌ساختند: از (قبیل) معبد‌ها، و تمثال‌ها، و کاسه‌هایی (غذا خوری) همچون حوض‌ها، و دیگ‌های ثابت (که از بزرگی قابل حمل و نقل نبود، و به آن‌ها گفتیم:) ای آل داوود! سپاس (این همه نعمت را) به جا آوردید، و اندکی از بندگان من سپاسگزارند.');
INSERT INTO "hussein_taji" VALUES(34,14,'34:14','پس چون مرگ را بر او (= سلیمان) مقرر داشتیم، (کسی) آن‌ها را از مرگش آگاه نساخت؛ مگر جنبندۀ زمین (= موریانه) که عصایش را می‌خورد، پس چون (بر زمین) افتاد، جنیان دریافتند که اگر غیب می‌دانستند در (آن) عذاب خوار کننده نمی‌ماندند.');
INSERT INTO "hussein_taji" VALUES(34,15,'34:15','همانا برای (قوم) سبا در مساکن‌شان نشانه‌ای بود، دو باغ (بزرگ) از راست و چپ، (و به آن‌ها گفتیم:) از روزی پروردگار‌تان بخورید و برای او شکر گزارید، شهری است پاکیزه، و پروردگاری آمرزنده.');
INSERT INTO "hussein_taji" VALUES(34,16,'34:16','پس آن‌ها روی گرداندند، آنگاه سیلی ویران‌گر را برآن‌ها فرستادیم، و دو باغ (پر میوه) شان را به دو باغ (بی ارزش) با میوه‌های تلخ و (درختان) شوره‌گز و اندکی از (درخت) کنار مبّدل ساختیم.');
INSERT INTO "hussein_taji" VALUES(34,17,'34:17','این کیفر را به (خاطر) آن‌که نا سپاسی کردند؛ به آن‌ها دادیم، و آیا جز نا سپاسان را مجازات می‌کنیم؟!');
INSERT INTO "hussein_taji" VALUES(34,18,'34:18','و میان آن‌ها و قریه‌هایی که برکت داده بودیم، قریه‌هایی (آباد و) به هم پیوسته (بر سر راه) قرار دادیم، و در (میان) آن‌ها آمد‌ و رفت مقرر داشتیم (و به آنان گفتیم:) در این (آبادی‌ها) با امن (و امان) شب‌ها و روز‌ها سفر کنید.');
INSERT INTO "hussein_taji" VALUES(34,19,'34:19','پس گفتند: «پروردگارا‌! میان سفر‌های ما دوری بیفکن» و بر خویشتن ستم کردند، پس ما آن‌ها را داستان‌هایی (برای عبرت دیگران) قرار دادیم، و آنان را سخت پراکنده (و متلاشی) ساختیم، بی‌گمان در این (ماجرا) برای هر صبر کنندۀ سپاسگزاری نشانه‌هایی است.');
INSERT INTO "hussein_taji" VALUES(34,20,'34:20','و یقیناً ابلیس گمان خود را دربارۀ آن‌ها درست یافت، پس (همگی) از او پیروی کردند؛ جز گروهی (اندک) از مؤمنان.');
INSERT INTO "hussein_taji" VALUES(34,21,'34:21','و او (= شیطان) را بر آنان هیچ سلطه‌ای نبود؛ مگر اینکه (خواستیم) معلوم بداریم کسی را که به آخرت ایمان می‌آورد، از کسی‌که او از آن در شک (و تردید) است. و پروردگار تو بر همه چیز نگاهبان است.');
INSERT INTO "hussein_taji" VALUES(34,22,'34:22','(ای پیامبر!) بگو: «کسانی را که غیر از الله (معبود خود) می‌پندارید، (به فریاد) بخوانید، (آن‌ها) هم‌وزن ذره‌ای در آسمان‌ها و در زمین مالک نیستند، و در (آفرینش و تدبیر) آن دو هیچ شرکتی ندارند، و او (= الله) از میان آن‌ها یاور و پشتیبانی ندارد.');
INSERT INTO "hussein_taji" VALUES(34,23,'34:23','و شفاعت نزد او سود نبخشد؛ مگر برای کسی‌که (او خود) برایش اجازت داده باشد، تا زمانی‌که اضطراب (و نگرانی) از دل‌های شان بر طرف شود، گویند: «پروردگار‌تان چه گفت؟» گویند: «حق را (فرمود)، و او بالا و بلند مرتبۀ بزرگ است»');
INSERT INTO "hussein_taji" VALUES(34,24,'34:24','(ای پیامبر! به مشرکان) بگو: «چه کسی شما را از آسمان‌ها و زمین روزی می‌دهد؟» (در پاسخ) بگو: «الله، و ما یا شما بر (طریق) هدایت یا در گمراهی آشکار هستیم».');
INSERT INTO "hussein_taji" VALUES(34,25,'34:25','بگو: «شما از گناهی‌که ما مرتکب شده‌ایم؛ باز خواست نمی‌شوید، و ما (نیز) از آنچه شما انجام می‌دهید؛ باز خواست نمی‌شویم».');
INSERT INTO "hussein_taji" VALUES(34,26,'34:26','بگو: « پروردگار ما، (همۀ) ما (و شما) را گرد می‌آورد؛ سپس در میان ما به حق داوری می‌کند، و او داور داناست».');
INSERT INTO "hussein_taji" VALUES(34,27,'34:27','بگو: «کسانی را که به عنوان شریک به او ملحق ساخته‌اید؛ به من نشان دهید، (هرگز) چنین نیست، بلکه او الله پیروزمندِ حکیم است.');
INSERT INTO "hussein_taji" VALUES(34,28,'34:28','و (ای پیامبر!) ما تو را برای همۀ مردم؛ جز بشارت‌دهنده و بیم‌دهنده نفرستادیم، و لیکن بیشتر مردم نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(34,29,'34:29','و می‌گویند: «اگر راستگویید؛ این وعدۀ (قیامت) کی خواهد بود؟!»');
INSERT INTO "hussein_taji" VALUES(34,30,'34:30','بگو: «میعاد شما روزی خواهد بود که نه ساعتی از آن تأخیر می‌کنید و نه (بر آن) پیشی می‌گیرید»');
INSERT INTO "hussein_taji" VALUES(34,31,'34:31','و کسانی‌که کافر شدند، گفتند: «ما هرگز به این قرآن ایمان نمی‌آوریم و نه به آن (کتاب‌های) که پیش از آن بوده». (ای پیامبر!) اگر ببینی (در روز قیامت) هنگامی‌که ستمکاران در پیشگاه پروردگار‌شان (برای حساب‌رسی) نگه داشته شده‌اند، (از حال آن‌ها به شگفت می‌آیی) برخی از آن‌ها سخن برخی دیگر را پاسخ می‌دهند (و هر کدام گناه خود را به گردن دیگری می‌اندازد) کسانی‌که به استضعاف کشیده شده‌اند، به کسانی‌که استکبار (و سرکشی) نمودند، گویند: «اگر شما نبودید ما قطعاً مؤمن بودیم».');
INSERT INTO "hussein_taji" VALUES(34,32,'34:32','کسانی‌که سرکشی (و تکبر) کردند (= مستکبران) به کسانی‌که به استضعاف کشیده شده‌اند، (= مستضعفان) گویند: «آیا ما شما را از هدایت باز داشتیم بعد از آن‌که به سراغ شما آمد؟! (هرگز نه) بلکه شما خود مجرم بودید».');
INSERT INTO "hussein_taji" VALUES(34,33,'34:33','و مستضعفان به مستکبران گویند: «بلکه، مکر و حیلۀ شب و روز شما (سبب شد که از هدایت بازمانیم) هنگامی‌که به ما دستور می‌دادید که به الله کافر شویم و برای او همتایانی قرار دهیم». و هنگامی‌که عذاب (الهی) را ببینند، پشیمانی (خود) را (در دل) پنهان دارند، و ما غل‌ها در گردن‌های کسانی‌که کافر شدند؛ بگذاریم، آیا جز آنچه عمل می‌کردند، به آن‌ها جزا داده می‌شود؟!');
INSERT INTO "hussein_taji" VALUES(34,34,'34:34','ما در هیچ قریه‌ای هشداردهنده‌ای نفرستادیم؛ مگر اینکه متنعّمان آن‌ها (که مست ناز و نعمت و قدرت بودند) گفتند: «بی‌تردید ما به آنچه فرستاده شده‌اید؛ کافریم».');
INSERT INTO "hussein_taji" VALUES(34,35,'34:35','و گفتند: «اموال و اولاد ما (از شما) بیشتر است، (و این دلیل آن است که الله از ما خشنود است) و ما هرگز مجازات نخواهیم شد».');
INSERT INTO "hussein_taji" VALUES(34,36,'34:36','(ای پیامبر!) بگو: «بی‌گمان پروردگارم روزی را برای هر کس‌که بخواهد فراخ و (یا) تنگ می‌کند، (و این نشانۀ محبت و رضایت الله نیست) و لیکن بیشتر مردم نمی‌دانند».');
INSERT INTO "hussein_taji" VALUES(34,37,'34:37','و اموال‌تان و اولاد‌تان چیزی نیستند که شما را نزد ما نزدیک و مقّرب سازند، جز کسانی‌که ایمان آوردند و عمل صالح انجام دادند، پس اینان پاداش مضاعف در برابر کار‌های‌که انجام داده‌اند؛ دارند، و آن‌ها در غرفه‌های (بلند بهشتی) در امن و (امان) هستند».');
INSERT INTO "hussein_taji" VALUES(34,38,'34:38','و کسانی‌که مبارزه‌کنان در (انکار) آیات ما تلاش کردند (و گمان کردند‌ که ما را به ستوه می‌آورند) اینان در عذاب احضار می‌شوند.');
INSERT INTO "hussein_taji" VALUES(34,39,'34:39','(ای پیامبر!) بگو: «بی‌گمان پروردگارم روزی را برای هرکس‌که بخواهد فراخ می‌گرداند، و (یا) برای او تنگ (و محدود) می‌سازد، و هر چیزی را (در راه الله) انفاق کنید، پس او عوض آن را می‌دهد، و او بهترین روزی دهندگان است».');
INSERT INTO "hussein_taji" VALUES(34,40,'34:40','و (به یاد آور) روزی را که (الله) همۀ آنان را بر می‌انگیزد، سپس به فرشتگان می‌گوید: «آیا این‌ها شما را پرستش می‌کردند؟!».');
INSERT INTO "hussein_taji" VALUES(34,41,'34:41','(فرشتگان) گویند: «تو منزهی! (تنها) تو ولی (و کار ساز) ما هستی، نه آن‌ها، (آنان هرگز ما را پرستش نمی‌کردند) بلکه جن (و شیاطین) را پرستش می‌کردند، بیشتر‌‌شان به آن‌ها ایمان داشتند».');
INSERT INTO "hussein_taji" VALUES(34,42,'34:42','پس امروز برخی از شما برای برخی دیگر مالک هیچ سود و زیانی نیست، و به کسانی‌که ستم (= شرک) کردند، می‌گوییم: «بچشید عذاب آتشی را که آن را تکذیب می‌کردید».');
INSERT INTO "hussein_taji" VALUES(34,43,'34:43','و هنگامی‌که آیات روشن (و واضح) ما بر آنان خوانده شود، گویند: «این (محمد صلی الله علیه وسلم) جز مردی نیست که می‌خواهد شما را از آنچه نیاکان‌تان می‌پرستیدند؛ باز دارد» و گویند: «این (قرآن) جز دروغ بزرگی‌که به هم بافته شده نیست». و کسانی‌که کافر شدند، هنگامی‌که حق به سراغ آن‌ها آمد، دربارۀ آن گفتند: «این (قرآن) جز سحری آشکار نیست».');
INSERT INTO "hussein_taji" VALUES(34,44,'34:44','و ما (پیش از این) چیزی از کتاب‌های (آسمانی) را به آن‌ها نداده‌ایم که آن را بخوانند (و بر اساس آن تو را تکذیب کنند) و پیش از تو هیچ هشداردهنده‌ای به سوی آن‌ها نفرستادیم.');
INSERT INTO "hussein_taji" VALUES(34,45,'34:45','و کسانی‌که پیش از آن‌ها بودند (نیز پیامبران ما را) تکذیب کردند، در حالی‌که این‌ها به یک دهم آنچه به آن‌ها داده بودیم؛ نرسیده‌اند، آنگاه آن‌ها پیامبران مرا تکذیب کردند، (بنگر) مجازات من چگونه بود؟!');
INSERT INTO "hussein_taji" VALUES(34,46,'34:46','(ای پیامبر!) بگو: «همانا من شما را به یک (خصلت) اندرز می‌دهم، که: دو نفر دو نفر یا یک نفر یک نفر برای الله بر خیزید، سپس بیندیشید که این همنشین شما (= محمد صلی الله علیه وسلم) هیچ‌گونه دیوانگی ندارد، او برای شما جز هشداردهندۀ پیشاپیش عذاب شدید نیست».');
INSERT INTO "hussein_taji" VALUES(34,47,'34:47','(ای پیامبر!) بگو: «هر مزدی‌که از شما خواسته‌ام؛ پس آن برای خود شما باد، پاداش من تنها بر (عهدۀ) الله است، و او بر همه چیز گواه است».');
INSERT INTO "hussein_taji" VALUES(34,48,'34:48','(ای پیامبر!) بگو: «بی‌گمان پروردگارم حق را (بر قلب پیامبران خود) می‌افکند، (آن پروردگاری‌که) دانای غیب‌هاست».');
INSERT INTO "hussein_taji" VALUES(34,49,'34:49','(ای پیامبر!) بگو: «حق آمد، و (معبودان) باطل نه (آفرینش را) آغاز می‌کند، و نه (دوباره) باز می‌گرداند».');
INSERT INTO "hussein_taji" VALUES(34,50,'34:50','(ای پیامبر!) بگو: «اگر من گمراه شوم، تنها بر زیان خودم گمراه می‌شوم، و اگر هدایت یابم، پس به (خاطر) آن چیزی است که پروردگارم به من وحی می‌کند، یقیناً او شنوای نزدیک است».');
INSERT INTO "hussein_taji" VALUES(34,51,'34:51','و (ای پیامبر!) اگر ببینی هنگامی‌که مضطرب (و نگران) شوند، آنگاه رهایی (از عذاب) نباشد، و از جای نزدیکی گرفته شوند (تعجب خواهی کرد)');
INSERT INTO "hussein_taji" VALUES(34,52,'34:52','و (در آن‌حال) گویند: «به آن (قرآن) ایمان آوردیم» ولی چگونه می‌توانند از فاصلۀ دور به آن دست یابند؟!');
INSERT INTO "hussein_taji" VALUES(34,53,'34:53','در حالی‌که آن‌ها پیش از این (در دنیا) به آن کافر شدند، و از جای دور نادیده (و بدون آگاهی، تیری) می‌افکندند.');
INSERT INTO "hussein_taji" VALUES(34,54,'34:54','و میان آن‌ها و آنچه می‌خواستند جدایی افکنده شد، همان‌گونه که از پیش با امثال آن‌ها انجام شد، بی‌گمان آن‌ها سخت در شک (و تردید) بودند.');
INSERT INTO "hussein_taji" VALUES(35,1,'35:1','حمد (و ستایش) مخصوص الله است، آفرینندۀ آسمان‌ها و زمین، (کسی) که فرشتگان را رسولانی قرار داد؛ دارای بال‌های دوگانه و سه‌گانه و چهارگانه، هر چه بخواهد در آفرینش می‌افزاید، بی‌گمان الله بر هر چیزی تواناست.');
INSERT INTO "hussein_taji" VALUES(35,2,'35:2','(هر) رحمتی را که الله برای مردم بگشاید، کسی نمی‌تواند که بازش دارد، و آنچه را باز دارد کسی جز او نمی‌تواند آن را روان سازد، و او پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(35,3,'35:3','ای مردم! نعمت الله را بر خود به یاد آورید، آیا آفریننده‌ای جز الله هست که شما را از آسمان و زمین روزی دهد؟! هیچ معبودی جز او نیست، پس چگونه (از حق) منحرف می‌شوید؟!');
INSERT INTO "hussein_taji" VALUES(35,4,'35:4','و اگر تو را تکذیب کنند (غمگین نباش) همانا پیامبران پیش از تو (نیز) تکذیب شدند، و (همۀ) کار‌ها به سوی الله باز گردانده می‌شود.');
INSERT INTO "hussein_taji" VALUES(35,5,'35:5','ای مردم! یقیناً وعدۀ الله حق است، پس زندگی دنیا شما را نفریبد، و (شیطان) فریبکار شما را نسبت به الله نفریبد.');
INSERT INTO "hussein_taji" VALUES(35,6,'35:6','مسلما شیطان دشمن شماست، پس او را دشمن گیرید، او فقط پیروانش را دعوت می‌کند تا (همه) از اهل آتش سوزان (جهنم) باشند.');
INSERT INTO "hussein_taji" VALUES(35,7,'35:7','کسانی‌که کافر شدند، برای آنان عذابی سخت است، و کسانی‌که ایمان آوردند و کار‌های شایسته انجام دادند، برایشان آمرزش و پاداش بزرگی است.');
INSERT INTO "hussein_taji" VALUES(35,8,'35:8','آیا کسی‌که کردار بدش برای او آراسته شده، پس آن را نیک (و زیبا) می‌بیند (همانند کسی است که چنین نیست؟) پس بی‌گمان الله هر کس را که بخواهد گمراه می‌سازد و هر کس را که بخواهد هدایت می‌کند، پس نباید که جانت به خاطر شدت تأسف (و حسرت) بر آنان از بین برود، بی‌شک الله به آنچه انجام می‌دهند؛ داناست.');
INSERT INTO "hussein_taji" VALUES(35,9,'35:9','و الله کسی است که باد‌ها را فرستاد، آنگاه (آن باد‌ها) ابر‌ها را بر می‌انگیزند، پس آن (ابر‌ها) را به سوی سرزمین مرده راندیم، آنگاه بوسیلۀ آن زمین را بعد از مردنش زنده کردیم، بر انگیختن (مردگان از گور‌ها نیز) چنین است.');
INSERT INTO "hussein_taji" VALUES(35,10,'35:10','کسی‌که خواهان عزت است، پس (بداند که) عزت همگی از آن الله است، سخن پاکیزه به سوی او بالا می‌رود، و (الله) عمل صالح را بالا می‌برد، و کسانی‌که (نقشه و) نیرنگ‌های بد می‌کشند، برای آن‌ها عذاب سختی است، و نیرنگ (و نقشه‌های بد) آن‌ها نابود می‌شود.');
INSERT INTO "hussein_taji" VALUES(35,11,'35:11','و الله شما را از خاک، سپس از نطفه آفرید، آنگاه شما را جفت‌های یکدیگر قرار داد، و هیچ ماده‌ای باردار نمی‌شود، و نمی‌زاید؛ مگربه علم او، و هیچ سالخورده‌ای عمر طولانی نمی‌کند، و (یا) از عمرش کاسته نمی‌شود؛ مگر آنکه در کتاب (لوح محفوظ ثبت) است، بی‌گمان این (کار‌ها) بر الله آسان است.');
INSERT INTO "hussein_taji" VALUES(35,12,'35:12','و (آن) دو دریا یکسان نیستند: این یکی (آبش) شیرین گوارا است که نوشیدنش خوش‌گوار است، و آن یکی شور تلخ است، و از هر دو گوشتی تازه می‌خورید، و (از آن‌ها) چیز‌‌هایی زینتی بیرون می‌آورید که می‌پوشید، و کشتی‌ها را در آن (دریا) می‌بینی که (امواج را) می‌شکافد (و به سوی مقصد خویش پیش می‌رود) تا از فضل الله (روزی) بجویید، و شاید که سپاس گزارید.');
INSERT INTO "hussein_taji" VALUES(35,13,'35:13','(او) شب را در روز داخل می‌کند و روز را در شب داخل می‌گرداند، و خورشید و ماه را مسخر کرده که هر یک تا زمانی معین در حرکت است، این است الله پروردگار شما، فرمانروایی از آنِ اوست، و کسانی را که به جای او می‌خوانید (هم‌وزن) پوست نازک هستۀ خرما مالک نیستند.');
INSERT INTO "hussein_taji" VALUES(35,14,'35:14','اگر آن‌ها را بخوانید (صدای) خواندن شما را نمی‌شنوند، و (به فرض) اگر بشنوند به شما پاسخ نمی‌دهند، و روز قیامت شرک شما را انکار می‌کنند، و هیچ‌کس مانند (الله) آگاه خبردارت نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(35,15,'35:15','ای مردم! شما (همگی) به الله نیازمندید، و (تنها) الله است که بی‌نیاز ستوده است.');
INSERT INTO "hussein_taji" VALUES(35,16,'35:16','اگر بخواهد شما را (از بین) می‌برد، و آفرینش تازه‌ای می‌آورد.');
INSERT INTO "hussein_taji" VALUES(35,17,'35:17','و این (کار) بر الله دشوار نیست.');
INSERT INTO "hussein_taji" VALUES(35,18,'35:18','و هیچ‌کس بار (گناه) دیگری را به دوش نمی‌کشد، و اگر (شخص) گرانباری (دیگری را) برای حمل بار (گناه) خود بخواند، چیزی از آن (بار) برداشته نمی‌شود، هر چند خویشاوند (نزدیک او) باشد. تو (ای پیامبر!) تنها کسانی را هشدار می‌دهی‌که از پروردگارشان در پنهانی می‌ترسند، و نماز را بر پا می‌دارند، و هر کس پاک گردد (و تقوا پیشه کند) پس (بداند که) تنها به سود خویش پاک شده است، و باز گشت (همه) به سوی الله است.');
INSERT INTO "hussein_taji" VALUES(35,19,'35:19','و نابینا و بینا (هرگز) برابر نیستند.');
INSERT INTO "hussein_taji" VALUES(35,20,'35:20','و نه تاریکی‌ها و نور،');
INSERT INTO "hussein_taji" VALUES(35,21,'35:21','و نه سایه و باد سوزان.');
INSERT INTO "hussein_taji" VALUES(35,22,'35:22','و (هرگز) زندگان و مردگان یکسان نیستند، بی‌گمان الله هر کس را بخواهد می‌شنواند، و تو (ای پیامبر!) نمی‌توانی (سخنت را) به در گور خفتگان بشنوانی.');
INSERT INTO "hussein_taji" VALUES(35,23,'35:23','تو جز هشداردهنده‌ای نیستی،');
INSERT INTO "hussein_taji" VALUES(35,24,'35:24','یقیناً ما تو را به حق بشارت‌دهنده و هشدار‌‌دهنده فرستادیم، و هیچ امتی نیست؛ مگر آن‌که در (میان) آن هشداردهنده‌ای (آمده و) گذشته است.');
INSERT INTO "hussein_taji" VALUES(35,25,'35:25','و اگر تو را تکذیب کنند، (چیز تازه‌ای نیست) همانا کسانی‌که پیش از آنان بودند (نیز پیامبران خود را) تکذیب کردند، پیامبران‌شان با دلایل روشن و نوشته‌های (مشتمل بر پند و موعظه) و کتاب روشنگر به سراغ آن‌ها آمدند.');
INSERT INTO "hussein_taji" VALUES(35,26,'35:26','سپس کسانی‌که کفر ورزیدند؛ فرو گرفتم، پس (بنگر) مجازات من چگونه بود؟!');
INSERT INTO "hussein_taji" VALUES(35,27,'35:27','آیا ندیدی همانا الله از آسمان آبی را نازل کرد که بوسیلۀ آن میوه‌هایی با رنگ‌های گوناگون (از زمین) بیرون آوردیم، و از کوه‌ها (نیز) راه‌های (به رنگ) سفید و سرخ رنگارنگ و به غایت سیاه (پدید آوردیم).');
INSERT INTO "hussein_taji" VALUES(35,28,'35:28','و همچنین از مردم و جنبندگان و چهارپایان با رنگ‌های مختلف (پدید آوردیم)، جز این نیست که از میان بندگان الله دانایان از او می‌ترسند، بی‌تردید الله پیروزمند آمرزنده است.');
INSERT INTO "hussein_taji" VALUES(35,29,'35:29','بی‌گمان کسانی‌که کلام الله را تلاوت می‌کنند و نماز را بر پا می‌دارند، و از آنچه روزی‌شان داده‌ایم؛ پنهان و آشکار انفاق می‌کنند، به تجارتی (پر سود) که هرگز زیان (و کساد) ندارد؛ امید دارند.');
INSERT INTO "hussein_taji" VALUES(35,30,'35:30','تا (الله) پاداش آن‌ها را به تمامی بدهد و از فضل خود بر آن‌ها بیفزاید، بی‌گمان او آمرزندۀ سپاسگزار است.');
INSERT INTO "hussein_taji" VALUES(35,31,'35:31','و (ای پیامبر!) آنچه از (این) کتاب به تو وحی کردیم؛ حق است که تصدیق کنندۀ آنچه پیش از آن است، یقیناً الله به (حال) بندگانش آگاه بیناست.');
INSERT INTO "hussein_taji" VALUES(35,32,'35:32','سپس (این) کتاب را به کسانی از بندگان‌مان که بر گزیده بودیم؛ به میراث دادیم، پس (برخی) از آنان بر خود ستم کردند و (برخی) از آنان میانه رَو بودند، و (برخی) از آنان به فرمان الله در نیکی‌ها پیشتازند، این همان فضل (و بخشایش) بزرگی است.');
INSERT INTO "hussein_taji" VALUES(35,33,'35:33','(پاداش آن‌ها) باغ‌های جاویدان (بهشتی) است که به آن وارد می‌شوند، در آنجا به دستبند‌های از طلا و مروارید آراسته می‌گردند، و لباس‌هایشان در آنجا حریر است.');
INSERT INTO "hussein_taji" VALUES(35,34,'35:34','و گویند: «حمد (و ستایش) مخصوص الله است که اندوه را از ما دور کرد، یقیناً پروردگار ما آمرزندۀ سپاس‌گزار است.');
INSERT INTO "hussein_taji" VALUES(35,35,'35:35','(همان) کسی‌که از فضل خویش ما را در این سرای اقامت (جاویدان) جای داد که در آنجا هیچ رنجی به ما نمی‌رسد، و در آنجا هیچ خستگی (و واماندگی) به ما نمی‌رسد.');
INSERT INTO "hussein_taji" VALUES(35,36,'35:36','و کسانی‌که کافر شدند؛ برای آن‌ها آتش جهنم است، نه فرمان (مرگ) بر آن صادر شود تا بمیرند (و راحت گردند) و نه چیزی از عذابش از آن‌ها کاسته شود، این گونه هر ناسپاسی را کیفر می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(35,37,'35:37','و آن‌ها در آن (آتش) فریاد می‌زنند: «پروردگارا! ما را بیرون آر تا عمل صالحی انجام دهیم؛ غیر از آنچه انجام می‌دادیم» (در پاسخ آنان می‌فرماییم:) آیا آن قدر شما را عمر ندادیم که پند گیرندگان در آن پند گیرند؟! و هشدار دهنده (نیز) به سراغ شما آمد؟! پس (طعم عذاب را) بچشید که برای ستمکاران هیچ یاوری نیست.');
INSERT INTO "hussein_taji" VALUES(35,38,'35:38','بی‌گمان الله دانای نهان آسمان‌ها و زمین است و همانا او به آنچه در درون سینه‌هاست؛ دانا است.');
INSERT INTO "hussein_taji" VALUES(35,39,'35:39','او کسی است که شما را در زمین جانشینان (پیشینیان) قرار داد، پس هر کس که کافر شود، او به زیان خودش خواهد بود، و کافران را کفر‌شان جز خشم (و دشمنی) در نزد پروردگار‌‌شان (چیزی) نمی‌افزاید، و (نیز) کافران را کفر‌شان جز زیان (چیزی) نمی‌افزاید.');
INSERT INTO "hussein_taji" VALUES(35,40,'35:40','(ای پیامبر!) بگو: «آیا معبودان‌تان که به جای الله می‌خوانید؛ دیده‌اید؟ به من نشان دهید چه چیزی از زمین را آفریده‌اند؟ یا اینکه در (آفرینش) آسمان‌ها شرکتی دارند؟ یا به آن‌ها کتابی داده‌ایم که آن‌ها دلیلی از آن (برای شرک خود) دارند؟» (نه هیچ‌یک از این‌ها نیست) بلکه ستمکاران جز فریب به یکدیگر وعده‌ای نمی‌دهند.');
INSERT INTO "hussein_taji" VALUES(35,41,'35:41','بی‌تردید الله آسمان‌ها و زمین را نگه می‌دارد از آن‌که (از نظام خود) منحرف شوند، و اگر منحرف گردند، کسی جز او نمی‌تواند آن‌ها را نگه دارد، بی‌گمان او بردبار آمرزنده است.');
INSERT INTO "hussein_taji" VALUES(35,42,'35:42','و (کفار قریش) با محکم‌ترین سوگند‌های‌شان (و با نهایت تأکید) به الله سوگند یاد کردند که اگر هشداردهنده‌ای برای آنان بیاید، از هدایت یافته‌ترین امت‌ها خواهند بود، پس چون (پیامبر) هشداردهنده‌ای بر آنان آمد، جز (فرار و) نفرت (از حق چیزی) بر آنان نیفزود.');
INSERT INTO "hussein_taji" VALUES(35,43,'35:43','(این‌ها) بخاطر استکبار در زمین و نیرنگ‌های بد (شان) بود، و نیرنگ‌های بد جز دامن‌گیر صاحبانش نمی‌گردد، آنگاه آیا آنان (چیزی) جز سنت (و شیوۀ الله با) پیشینیان را انتظار دارند؟! پس هرگز برای سنت (و شیوۀ) الله تغیری پیدا نمی‌کنی و هرگز درطرز کار الله دگرگونی نمی‌یابی.');
INSERT INTO "hussein_taji" VALUES(35,44,'35:44','آیا (آن‌ها) در زمین نگشته‌اند؛ تا ببینند عاقبت کسانی را که پیش از آن‌ها بودند؛ چگونه بود؟! در حالی‌که آنان از اینان نیرومند‌تر بودند، و هیچ چیزی در آسمان‌ها و زمین نیست که الله را ناتوان سازد (و از حوزۀ قدرت او بیرون رود) بی‌گمان او دانای تواناست.');
INSERT INTO "hussein_taji" VALUES(35,45,'35:45','و اگر الله مردم را به (سبب) آنچه که انجام داده‌اند؛ مجازات کند، هیچ جنبنده‌ای را بر روی آن (= زمین) باقی نخواهد گذاشت، و لیکن آن‌ها را تا زمانی معین به تأخیر می‌اندازد، پس هنگامی‌که اجل آن‌ها فرا رسد، یقیناً الله به بندگانش بیناست.');
INSERT INTO "hussein_taji" VALUES(36,1,'36:1','یس (یا. سین)');
INSERT INTO "hussein_taji" VALUES(36,2,'36:2','سوگند به قرآن حکیم');
INSERT INTO "hussein_taji" VALUES(36,3,'36:3','که تو قطعاً از پیامبران (ما) هستی.');
INSERT INTO "hussein_taji" VALUES(36,4,'36:4','بر راه راست (قرار داری).');
INSERT INTO "hussein_taji" VALUES(36,5,'36:5','(این قرآن) فرو فرستادۀ (الله) پیروزمند مهربان (است).');
INSERT INTO "hussein_taji" VALUES(36,6,'36:6','تا گروهی را هشدار دهی که نیاکان‌شان هشدار نیافته‌اند، پس آنان غافلند.');
INSERT INTO "hussein_taji" VALUES(36,7,'36:7','به راستی فرمان (الهی) بر بیشتر آنان تحقق یافته است، پس آنان ایمان نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(36,8,'36:8','بی‌گمان ما در گردن‌های آنان غل‌هایی قرار دادیم پس آن (غل‌ها) تا چانه‌ها است، لذا سر‌‌های‌شان را بالا نگاه داشته است.');
INSERT INTO "hussein_taji" VALUES(36,9,'36:9','و پیش روی آنان سدی (= دیواری) و از پشت سر‌شان (نیز) سدی قراردادیم، آنگاه (دیدگان) آنان را پوشاندیم، لذا آنان نمی‌بینند.');
INSERT INTO "hussein_taji" VALUES(36,10,'36:10','و (ای پیامبر) بر آنان یکسان است، چه آن‌ها را هشدار دهی و یا هشدار‌شان ندهی، ایمان نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(36,11,'36:11','(ای پیامبر) تو تنها کسی را هشدار می‌دهی که از (این) پند پیروی کند، و در نهان از (الله) رحمان بترسد، پس او را به آمرزش و پاداشی گرامی بشارت ده.');
INSERT INTO "hussein_taji" VALUES(36,12,'36:12','یقیناً ماییم که مردگان را زنده می‌کنیم، و آنچه را (از اعمال نیک و بد) که از پیش فرستاده‌اند، و آثار (و گام‌های) شان را می‌نویسیم، و همه چیز را در کتاب روشنگر (لوح محفوظ) شمار کرده‌ایم.');
INSERT INTO "hussein_taji" VALUES(36,13,'36:13','و (ای پیامبر) برای آن‌ها مثال (و داستان) مردم آن قریه (انطاکیه) را بزن، چون رسولان (ما) بدان جا آمدند.');
INSERT INTO "hussein_taji" VALUES(36,14,'36:14','آنگاه که دو نفر (از رسولان) را به سوی آن‌ها فرستادیم، پس آنان را تکذیب کردند، سپس با (فرستادن) سومی (آن دو را) تقویت کردیم، پس گفتند: «همانا ما به سوی شما فرستاده شده‌ایم».');
INSERT INTO "hussein_taji" VALUES(36,15,'36:15','(آن‌ها در پاسخ) گفتند: شما جز بشری همانند ما نیستید، و (الله) رحمان چیزی را نازل نکرده است، شما فقط دروغ می‌گویید.');
INSERT INTO "hussein_taji" VALUES(36,16,'36:16','(پیامبران) گفتند: «پروردگار ما می‌داند که ما قطعاً به سوی شما فرستاده شده‌ایم.');
INSERT INTO "hussein_taji" VALUES(36,17,'36:17','و بر (عهدۀ) ما جز رساندن (پیام) آشکار نیست».');
INSERT INTO "hussein_taji" VALUES(36,18,'36:18','(آن‌ها) گفتند: «بی‌گمان ما شما را به فال بد گرفته‌ایم، اگر دست بر ندارید البته سنگسارتان خواهیم کرد، و ازسوی ما عذاب (و شکنجۀ) درد‌ناکی به شما خواهد رسید».');
INSERT INTO "hussein_taji" VALUES(36,19,'36:19','(پیامبران) گفتند: «شومی (و فال بد) تان با خود شماست، اگر به شما پند داده شود (چنین می‌گویید، و به فال بد می‌گیرید؟!) بلکه (حقیقت این است که) شما مردمی اسراف‌کار هستید».');
INSERT INTO "hussein_taji" VALUES(36,20,'36:20','و مرد (مؤمنی) از دور‌ترین نقطۀ شهر شتابان آمد، (و خطاب به مردم) گفت: «ای قوم من! پیامبران (الله) را پیروی کنید.');
INSERT INTO "hussein_taji" VALUES(36,21,'36:21','کسانی را پیروی کنید که از شما مزدی طلب نمی‌کنند، و خود‌شان هدایت یافته‌اند.');
INSERT INTO "hussein_taji" VALUES(36,22,'36:22','و چه شده است مرا که پرستش نکنم کسی را که مرا آفریده است، (همه) به سوی او باز گردانده می‌شوید؟');
INSERT INTO "hussein_taji" VALUES(36,23,'36:23','آیا غیر از او معبود‌هایی را بر گزینم که اگر (الله) رحمان بخواهد به من زیانی برساند، شفاعت آن‌ها چیزی را از من دفع نمی‌کند (و کمترین فایده‌ای برایم ندارد) و مرا نجات نخواهند داد؟!');
INSERT INTO "hussein_taji" VALUES(36,24,'36:24','(اگر چنین کنم) یقیناً آنگاه من در گمراهی آشکاری خواهم بود.');
INSERT INTO "hussein_taji" VALUES(36,25,'36:25','من به پروردگارتان ایمان آورده‌ام، پس (سخنان) مرا بشنوید.');
INSERT INTO "hussein_taji" VALUES(36,26,'36:26','(پس او را شهید کردند) به او گفته شد: «وارد بهشت شو» گفت: ای کاش قوم من می‌دانستند،');
INSERT INTO "hussein_taji" VALUES(36,27,'36:27','که پروردگارم مرا آمرزیده و از گرامی شدگان قرار داده است!');
INSERT INTO "hussein_taji" VALUES(36,28,'36:28','و بعد از او بر قومش هیچ لشکری از آسمان فرو نفرستادیم و ما فرو فرستنده نبودیم.');
INSERT INTO "hussein_taji" VALUES(36,29,'36:29','(بلکه کیفر آن‌ها) جز یک بانگ مرگبار نبود، پس ناگاه همه خاموش (=هلاک) شدند.');
INSERT INTO "hussein_taji" VALUES(36,30,'36:30','ای افسوس بر (این) بندگان، هیچ پیامبری به سوی آن‌ها نمی‌آمد مگر اینکه او را استهزاء می‌کردند!');
INSERT INTO "hussein_taji" VALUES(36,31,'36:31','آیا ندیدند که پیش از آن‌ها چه بسیار نسل‌ها را نابود کردیم، که آن‌ها هرگز به سوی ایشان باز نمی‌گردند؟!');
INSERT INTO "hussein_taji" VALUES(36,32,'36:32','و همۀ آن‌ها (برای حساب) نزد ما جمع و احضار می‌شوند.');
INSERT INTO "hussein_taji" VALUES(36,33,'36:33','و زمین مرده برایشان نشانه (و عبرتی) است که ما آن را زنده گردانیدیم، و دانۀ (غذایی) که از آن می‌خورند، بیرون آوردیم.');
INSERT INTO "hussein_taji" VALUES(36,34,'36:34','و در آن باغ‌هایی از نخل‌ها و انگور‌‌ها قرار دادیم، و در آن چشمه‌های جاری ساختیم.');
INSERT INTO "hussein_taji" VALUES(36,35,'36:35','تا از میوۀ آن بخورند در حالی‌که دست آنان (در تولید آن‌ها) هیچ دخالتی نداشته است [ و یا: «از دست آورد خود (و آنچه که با دست خود ساخته اند) بخورند».]، آیا سپاس نمی‌گویند.');
INSERT INTO "hussein_taji" VALUES(36,36,'36:36','منزه است کسی‌که همۀ جفت‌ها را آفرید، از آنچه زمین می‌رویاند، و از (جنس) خودشان، و از آنچه نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(36,37,'36:37','و شب (نیز) برای آن‌ها نشانه (و عبرتی) است ما روز را از آن بر می‌کشیم، پس آنگاه همه در تاریکی فرو می‌روند.');
INSERT INTO "hussein_taji" VALUES(36,38,'36:38','و خورشید (نیز که) پیوسته به سوی قرار‌‌گاهش در حرکت است، این تقدیر (الله) پیروزمند داناست.');
INSERT INTO "hussein_taji" VALUES(36,39,'36:39','و برای ماه منزلگاه‌های مقدر کرده‌ایم، تا چون شاخۀ (خشک و) کهنۀ خرما باز گردد.');
INSERT INTO "hussein_taji" VALUES(36,40,'36:40','نه خورشید را سزد که به ماه رسد، و نه شب بر روز پیشی می‌گیرد، و هر یک در مدار خود شناورند.');
INSERT INTO "hussein_taji" VALUES(36,41,'36:41','نشانه‌ای (دیگر) برای آنان این است که فرزندان‌شان را بر کشتی پر بار سوار کردیم.');
INSERT INTO "hussein_taji" VALUES(36,42,'36:42','و (مرکب‌های دیگری) همانند آن برای آن‌ها آفریدیم، که سوار می‌شوند.');
INSERT INTO "hussein_taji" VALUES(36,43,'36:43','و اگر بخواهیم آن‌ها را غرق می‌کنیم، پس آن‌ها را هیچ فریاد رسی نباشد، و نه آن‌ها رهایی یابند.');
INSERT INTO "hussein_taji" VALUES(36,44,'36:44','مگر اینکه رحمتی از سوی ما (شامل حال آن‌ها شود) و تا زمان معینی (از دنیا) بهره بگیرند.');
INSERT INTO "hussein_taji" VALUES(36,45,'36:45','و هنگامی‌که به آن‌ها گفته شود: «از آنچه پیش روی شماست، و از آنچه پشت سر شماست، بترسید، شاید مورد رحمت قرار گیرید» (اعراض می‌کنند).');
INSERT INTO "hussein_taji" VALUES(36,46,'36:46','و هیچ آیه‌ای از آیات پروردگار‌‌‌شان برای آن‌ها نیاید مگر آنکه از آن روی بگردانند.');
INSERT INTO "hussein_taji" VALUES(36,47,'36:47','و هنگامی‌که به آن‌ها گفته شود: از آنچه الله به شما روزی داده، انفاق کنید، کسانی‌که کافر شدند، به کسانی‌که ایمان آوردند می‌گویند: آیا ما کسی را طعام بدهیم که اگر الله می‌خواست، (خود) به او طعام می‌داد و شما جز در گمراهی آشکار نیستید.');
INSERT INTO "hussein_taji" VALUES(36,48,'36:48','و می‌گویند: «اگر راست می‌گویید، این وعده (عذاب) کی خواهد بود؟!»');
INSERT INTO "hussein_taji" VALUES(36,49,'36:49','آن‌ها جز انتظار یک بانگ مرگبار نمی‌کشند، که آن‌ها را فرو گیرد در حالی‌که سرگرم جدال (و دشمنی) هستند.');
INSERT INTO "hussein_taji" VALUES(36,50,'36:50','پس (در آن وقت) نمی‌توانند وصیتی کنند، و نه به سوی خانوادۀ خود باز گردند.');
INSERT INTO "hussein_taji" VALUES(36,51,'36:51','و (چون بار دیگر) در صور دمیده شود، پس ناگهان آن‌ها از قبرها (بیرون آیند) شتابان به سوی پروردگارشان می‌روند.');
INSERT INTO "hussein_taji" VALUES(36,52,'36:52','گویند: «وای بر ما چه کسی ما را از خوابگاه‌مان بر انگیخت؟!» (به آن‌ها گفته شود:) این همان است که (الله) رحمان وعده داده بود، و پیامبران (الله) راست گفتند.');
INSERT INTO "hussein_taji" VALUES(36,53,'36:53','(آن) جز یک بانگ مرگبار نخواهد بود، پس ناگهان همه نزد ما جمع و احضار می‌شوند.');
INSERT INTO "hussein_taji" VALUES(36,54,'36:54','پس امروز به هیچ کس ذره‌ای ستم نخواهند شد، و جز به آنچه عمل می‌کردید، جزا داده نمی‌شوید.');
INSERT INTO "hussein_taji" VALUES(36,55,'36:55','بی گمان اهل بهشت امروز در کاری (لذت بخش) مشغولند (و) شادمان هستند.');
INSERT INTO "hussein_taji" VALUES(36,56,'36:56','آن‌ها و همسران‌شان در سایه‌های (گستردۀ بهشتی) بر تخت‌ها تکیه زده‌اند.');
INSERT INTO "hussein_taji" VALUES(36,57,'36:57','برای آن‌ها در آن (= بهشت، انواع) میوه است، و هر چه که بخواهند برایشان (فراهم) است.');
INSERT INTO "hussein_taji" VALUES(36,58,'36:58','(به آن‌ها گفته می‌شود) سلام (بر شما باد) این سخنی است از جانب پروردگار مهربان.');
INSERT INTO "hussein_taji" VALUES(36,59,'36:59','ای گنهکاران امروز (از نیکان) جدا شوید.');
INSERT INTO "hussein_taji" VALUES(36,60,'36:60','ای فرزندان آدم! آیا با شما پیمان نبستم که شیطان را نپرستید، که او برای شما دشمن آشکار است؟!');
INSERT INTO "hussein_taji" VALUES(36,61,'36:61','و اینکه مرا بپرستید، این راه مستقیم است.');
INSERT INTO "hussein_taji" VALUES(36,62,'36:62','و به راستی او گروه زیادی از شما را گمراه ساخت آیا اندیشه نکردید؟!');
INSERT INTO "hussein_taji" VALUES(36,63,'36:63','این (همان) جهنمی است که به شما وعده داده می‌شد.');
INSERT INTO "hussein_taji" VALUES(36,64,'36:64','امروز به سزای کفری که ورزیده‌اید به آن در آیید.');
INSERT INTO "hussein_taji" VALUES(36,65,'36:65','امروز بر دهآن‌ها یشان مُهر می‌نهیم، و دست‌هایشان با ما سخن می‌گویند، و پا‌هایشان به آنچه می‌کردند گواهی می‌دهند.');
INSERT INTO "hussein_taji" VALUES(36,66,'36:66','و اگر بخواهیم چشمان‌شان را محو (و نابود) می‌کنیم، پس شتابان قصد راه می‌کنند، اما چگونه می‌توانند ببینند؟!');
INSERT INTO "hussein_taji" VALUES(36,67,'36:67','و اگر بخواهیم آن‌ها را در جایگاه‌شان مسخ (و میخ کوب) می‌کنیم، پس نتوانند که به پیش قدم بردارند، و نه به عقب باز گردند.');
INSERT INTO "hussein_taji" VALUES(36,68,'36:68','و هر کس را طول عمر دهیم، در آفرینش دگرگونش کنیم، (و به حالت ناتوانی کودکی بر می‌گردانیم) آیا اندیشه نمی‌کنند؟!');
INSERT INTO "hussein_taji" VALUES(36,69,'36:69','ما (هرگز) شعر به او (= رسول) نیاموخته‌ایم و شایستۀ او نیست، این (کتاب چیزی) جز اندرز، و قرآنی روشنگر نیست.');
INSERT INTO "hussein_taji" VALUES(36,70,'36:70','تا هر کس را که زنده (دل) باشد، بیم دهد و سخن حق بر کافران ثابت شود.');
INSERT INTO "hussein_taji" VALUES(36,71,'36:71','آیا ندیدند که ما برای آن‌ها از آنچه ساختۀ دست‌های ماست، چهار پایانی را آفریده‌ایم، پس آنان مالک آن هستند.');
INSERT INTO "hussein_taji" VALUES(36,72,'36:72','و آن‌ها را برایشان رام کردیم، پس بر بعضی سوار می‌شوند، و از (گوشت) بعضی می‌خورند.');
INSERT INTO "hussein_taji" VALUES(36,73,'36:73','و برای آن‌ها بهره‌های دیگر، و نوشیدنی‌ها در آن (حیوانات) است، آیا سپاس نمی‌گویند؟!');
INSERT INTO "hussein_taji" VALUES(36,74,'36:74','و آن‌ها غیر از الله معبودانی (برای خود) بر گزیدند، (به این امید) شاید که یاری شوند.');
INSERT INTO "hussein_taji" VALUES(36,75,'36:75','آن‌ها قادر به یاری ایشان نیستند، و آنان (= مشرکان) برای این‌ها (= معبودان) لشکری احضار شده (و آماده) هستند.');
INSERT INTO "hussein_taji" VALUES(36,76,'36:76','پس (ای پیامبر) سخن آنان تو را غمگین نسازد، بی‌گمان ما آنچه را که پنهان می‌دارند و آنچه را که آشکار می‌کنند، می‌دانیم.');
INSERT INTO "hussein_taji" VALUES(36,77,'36:77','آیا انسان ندیده است که ما او را از نطفه‌ای آفریده‌ایم، پس او ستیزه جویی آشکار شده است.');
INSERT INTO "hussein_taji" VALUES(36,78,'36:78','و برای ما مَثَلی زد، و آفرینش خود را فراموش کرد، گفت: «چه کسی (این) استخوان‌ها را (دوباره) زنده می‌کند در حالی‌که پوسیده است؟!».');
INSERT INTO "hussein_taji" VALUES(36,79,'36:79','بگو: «(همان) کسی آن را زنده می‌کند که نخستین بار آن را پدید آورد، و او به هر آفرینشی دانا‌ست،');
INSERT INTO "hussein_taji" VALUES(36,80,'36:80','(همان) کسی‌که از درخت سبز برای شما آتش پدید آورد، پس آنگاه شما از آن (آتش) می‌افروزید».');
INSERT INTO "hussein_taji" VALUES(36,81,'36:81','آیا کسی‌که آسمان‌ها و زمین را آفریده است، نمی‌تواند همانند آن‌ها (= انسان‌ها) را بیافریند؟! آری (می‌تواند) و او آفرینندۀ دانا‌‌ست.');
INSERT INTO "hussein_taji" VALUES(36,82,'36:82','جز این نیست که فرمان او، چون چیزی را اراده کند، (این است) که به آن می‌گوید: «موجود شو» پس (بی درنگ) موجود می‌شود.');
INSERT INTO "hussein_taji" VALUES(36,83,'36:83','پس منزه است، کسی‌که پادشاهی همه چیز در دست اوست، و (همه) به سوی او باز گردانده می‌شوید.');
INSERT INTO "hussein_taji" VALUES(37,1,'37:1','سوگند به (فرشتگان) صف کشیده.');
INSERT INTO "hussein_taji" VALUES(37,2,'37:2','پس سوگند به (فرشتگان) باز دارنده،');
INSERT INTO "hussein_taji" VALUES(37,3,'37:3','و به تلاوت کنندگان ذکر (= قرآن)،');
INSERT INTO "hussein_taji" VALUES(37,4,'37:4','که همانا معبود شما یکی است.');
INSERT INTO "hussein_taji" VALUES(37,5,'37:5','پروردگار آسمان‌ها و زمین و آنچه میان آن‌هاست، و پروردگار مشرق‌ها.');
INSERT INTO "hussein_taji" VALUES(37,6,'37:6','بی‌گمان ما آسمان دنیا (= پایین) را به زیور ستارگان بیاراستیم.');
INSERT INTO "hussein_taji" VALUES(37,7,'37:7','و از هر شیطان سر کشی حفظ کردیم.');
INSERT INTO "hussein_taji" VALUES(37,8,'37:8','(تا) به (سخنان) عالم بالا گوش فرا ندهند، و از هر سو (بوسیلۀ شهاب) زده (و رانده) می‌شوند.');
INSERT INTO "hussein_taji" VALUES(37,9,'37:9','(آن‌ها به سختی) عقب رانده می‌شوند، و برای آن‌ها عذاب دایمی است.');
INSERT INTO "hussein_taji" VALUES(37,10,'37:10','مگر کسی‌که ناگهان چیزی را برباید، پس شهابی درخشان او را دنبال می‌کند.');
INSERT INTO "hussein_taji" VALUES(37,11,'37:11','پس (ای پیامبر) از آن‌ها بپرس که آیا آفرینش آن‌ها سخت‌تر است، یا (آفرینش) آنچه ما آفریده‌ایم، ما آن‌ها را از گل چسپنده‌ای آفریدیم.');
INSERT INTO "hussein_taji" VALUES(37,12,'37:12','بلکه تو (از انکار آن‌ها) در شگفت شدی، و آن‌ها مسخره می‌کنند.');
INSERT INTO "hussein_taji" VALUES(37,13,'37:13','و هنگامی‌که پند داده شوند، پند نمی‌پذیرند.');
INSERT INTO "hussein_taji" VALUES(37,14,'37:14','و هنگامی‌که معجز‌ه‌ای ببینند (آنرا) مسخره می‌کنند.');
INSERT INTO "hussein_taji" VALUES(37,15,'37:15','و می‌گویند: «این جز جادویی آشکار نیست،');
INSERT INTO "hussein_taji" VALUES(37,16,'37:16','آیا هنگامی‌که مردیم و به خاک و استخوان‌های (پوسیده، مبدل) شدیم، (دوباره) بر انگیخته خواهیم شد؟!');
INSERT INTO "hussein_taji" VALUES(37,17,'37:17','آیا نیاکان نخستین ما (نیز بر انگیخته می‌شوند؟!)»');
INSERT INTO "hussein_taji" VALUES(37,18,'37:18','بگو: «آری، و شما خوار و زبون (زنده) می‌شوید».');
INSERT INTO "hussein_taji" VALUES(37,19,'37:19','پس آن تنها یک بانگ (سهمناک) است، ناگهان آن‌ها (از قبرها بیرون می‌آیند و) می‌نگرند.');
INSERT INTO "hussein_taji" VALUES(37,20,'37:20','و گویند: «وای بر ما این (همان) روز جزا است!»');
INSERT INTO "hussein_taji" VALUES(37,21,'37:21','(آری) این (همان) روز داوری است، که شما آن را تکذیب می‌کردید.');
INSERT INTO "hussein_taji" VALUES(37,22,'37:22','(به فرشتگان فرمان داده می‌شود) گرد آورید کسانی را که ستم کردند، و (همپایگان و) همراهانشان و آنچه را می‌پرستیدند.');
INSERT INTO "hussein_taji" VALUES(37,23,'37:23','به جای الله، پس (همۀ) آن‌ها را به راه دوزخ هدایت کنید.');
INSERT INTO "hussein_taji" VALUES(37,24,'37:24','و آن‌ها را نگاه دارید که یقینا باز خواست خواهند شد.');
INSERT INTO "hussein_taji" VALUES(37,25,'37:25','شما را چه شده است که یکدیگر را یاری نمی‌دهید؟!');
INSERT INTO "hussein_taji" VALUES(37,26,'37:26','بلکه آن‌ها امروز (همه) تسلیم (و فرمانبردار) هستند.');
INSERT INTO "hussein_taji" VALUES(37,27,'37:27','آن‌ها سوال کنان رو به یکدیگر کنند،');
INSERT INTO "hussein_taji" VALUES(37,28,'37:28','(به سردمداران) گویند: «به راستی که شما (برای گمراه کردن ما) از (راه) خیر خواهی به نزد ما می‌آمدید».');
INSERT INTO "hussein_taji" VALUES(37,29,'37:29','(سردمداران در پاسخ) گویند: «بلکه، شما خودتان مؤمن نبودید (تقصیر ما چیست؟!)');
INSERT INTO "hussein_taji" VALUES(37,30,'37:30','و ما هیچ‌گونه تسلطی بر شما نداشتیم، بلکه شما خود گروهی سر کش بودید.');
INSERT INTO "hussein_taji" VALUES(37,31,'37:31','پس فرمان پروردگارمان بر ما ثابت شد، البته ما چشندۀ (عذاب) خواهیم بود.');
INSERT INTO "hussein_taji" VALUES(37,32,'37:32','پس شما را گمراه کردیم، بی‌گمان ما (نیز) خود گمراه بودیم».');
INSERT INTO "hussein_taji" VALUES(37,33,'37:33','پس مسلماً در آن روز همۀ آن‌ها (= پیروان و سردمداران) در عذاب شریک خواهند بود.');
INSERT INTO "hussein_taji" VALUES(37,34,'37:34','بی‌شک ما با مجرمان این گونه رفتار می‌کنیم!');
INSERT INTO "hussein_taji" VALUES(37,35,'37:35','آن‌ها (در دنیا چنان) بودند که چون به آن‌ها گفته می‌شد: «معبودی (به حق) جز الله نیست» سر کشی (و تکبر) می‌کردند.');
INSERT INTO "hussein_taji" VALUES(37,36,'37:36','و می‌گفتند: «آیا ما معبودان‌مان را به خاطر (سخن) شاعری دیوانه رها کنیم؟!»');
INSERT INTO "hussein_taji" VALUES(37,37,'37:37','(چنین نیست) بلکه (پیامبر الله) حق را آورد، و پیامبران (پیشین) را تصدیق کرد.');
INSERT INTO "hussein_taji" VALUES(37,38,'37:38','مسلماً شما عذاب دردناک را خواهید چشید.');
INSERT INTO "hussein_taji" VALUES(37,39,'37:39','و جز به آنچه می‌کردید، کیفر داده نمی‌شوید.');
INSERT INTO "hussein_taji" VALUES(37,40,'37:40','مگر بندگان مخلص الله،');
INSERT INTO "hussein_taji" VALUES(37,41,'37:41','(که) آن‌ها رزق و روزی معین دارند.');
INSERT INTO "hussein_taji" VALUES(37,42,'37:42','(انواع) میوه‌ها، و آنان گرامی داشتگانند.');
INSERT INTO "hussein_taji" VALUES(37,43,'37:43','در باغ‌هایی پر نعمت (بهشت)،');
INSERT INTO "hussein_taji" VALUES(37,44,'37:44','بر تخت‌ها رو به روی یکدیگر (نشسته‌اند).');
INSERT INTO "hussein_taji" VALUES(37,45,'37:45','گرداگردشان جامی از (شراب) روان می‌گردانند.');
INSERT INTO "hussein_taji" VALUES(37,46,'37:46','(شرابی) سفید (و) لذت‌بخش برای نوشندگان است.');
INSERT INTO "hussein_taji" VALUES(37,47,'37:47','نه در آن تبهکاری (و فساد) است، و نه آن‌ها از آن (شراب) مست شوند.');
INSERT INTO "hussein_taji" VALUES(37,48,'37:48','و نزدشان (همسرانی) سیه چشم، دیده فرو هشته (که تنها به شوهران خود نظر دارند) خواهند بود.');
INSERT INTO "hussein_taji" VALUES(37,49,'37:49','گویی آن‌ها بیضه‌های (شتر مرغ) در پرده پوشیده‌اند.');
INSERT INTO "hussein_taji" VALUES(37,50,'37:50','پس آن‌ها سوال‌کنان رو به یکدیگر کنند.');
INSERT INTO "hussein_taji" VALUES(37,51,'37:51','یکی از آن‌ها گوید: «به راستی من (در دنیا) همنشینی داشتم.');
INSERT INTO "hussein_taji" VALUES(37,52,'37:52','که (پیوسته) می‌گفت: آیا (واقعاً) تو از باور‌کنندگان (بعث و قیامت) هستی؟!');
INSERT INTO "hussein_taji" VALUES(37,53,'37:53','آیا وقتی‌که مردیم و خاک و استخوان‌های (پوسیده) شدیم، (زنده می‌شویم و) کیفرمان می‌دهند؟!»');
INSERT INTO "hussein_taji" VALUES(37,54,'37:54','(او به دوستان بهشتی‌اش) گوید: «آیا شما می‌توانید (به دوزخیان) بنگرید (و از او خبری بگیرید)؟!»');
INSERT INTO "hussein_taji" VALUES(37,55,'37:55','پس (چشم برگرداند و) نگریست، ناگاه او را در میان دوزخ دید.');
INSERT INTO "hussein_taji" VALUES(37,56,'37:56','گوید: به الله سوگند، نزدیک بود که مرا به هلاکت افکنی.');
INSERT INTO "hussein_taji" VALUES(37,57,'37:57','و اگر نعمت پروردگارم نبود، من (نیز) از احضار شدگان (دوزخ) بودم.');
INSERT INTO "hussein_taji" VALUES(37,58,'37:58','(سپس به دوستان بهشتی خود گوید:) آیا ما (هرگز) نمی‌میریم (و در بهشت جاودان هستیم)');
INSERT INTO "hussein_taji" VALUES(37,59,'37:59','جز همان مرگ نخستین خود، (دیگر مرگی به سراغ‌مان نخواهد آمد) و ما (هرگز) عذاب نخواهیم شد؟!');
INSERT INTO "hussein_taji" VALUES(37,60,'37:60','بی‌گمان این همان کامیابی بزرگ است!');
INSERT INTO "hussein_taji" VALUES(37,61,'37:61','(آری) عمل کنندگان باید برای چنین (پاداشی) عمل کنند.');
INSERT INTO "hussein_taji" VALUES(37,62,'37:62','آیا برای پذیرایی این (نعمت‌های بهشتی) بهتر است یا درخت زقوم؟');
INSERT INTO "hussein_taji" VALUES(37,63,'37:63','همانا ما آن را ابتلای برای ستمکاران قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(37,64,'37:64','به راستی آن درختی است که از قعر جهنم می‌روید.');
INSERT INTO "hussein_taji" VALUES(37,65,'37:65','شکوفه‌اش همانند سر‌های شیاطین است.');
INSERT INTO "hussein_taji" VALUES(37,66,'37:66','پس حتما آن‌ها (= جهنمیان) از آن می‌خورند، و شکم‌ها را از آن پر می‌کنند.');
INSERT INTO "hussein_taji" VALUES(37,67,'37:67','آنگاه بر روی آن (خوراک) آمیزه‌ای از آب گرم (سوزان) دارند.');
INSERT INTO "hussein_taji" VALUES(37,68,'37:68','سپس باز‌گشت آن‌ها به سوی جهنم است.');
INSERT INTO "hussein_taji" VALUES(37,69,'37:69','بی‌گمان آن‌ها نیاکان خود را گمراه یافتند.');
INSERT INTO "hussein_taji" VALUES(37,70,'37:70','پس این‌ها در دنبال آنان شتابان می‌روند.');
INSERT INTO "hussein_taji" VALUES(37,71,'37:71','و به راستی پیش از آن‌ها بیشتر پیشینیان گمراه شدند.');
INSERT INTO "hussein_taji" VALUES(37,72,'37:72','و به تحقیق ما هشدار دهندگانی در میان آن‌ها فرستادیم.');
INSERT INTO "hussein_taji" VALUES(37,73,'37:73','پس بنگر عاقبت هشدار یافتگان چگونه بود!');
INSERT INTO "hussein_taji" VALUES(37,74,'37:74','مگر بندگان مخلص الله.');
INSERT INTO "hussein_taji" VALUES(37,75,'37:75','و یقیناً نوح ما را نِدا داد، پس (ما) چه خوب اجابت کننده‌ای هستیم.');
INSERT INTO "hussein_taji" VALUES(37,76,'37:76','و او و خاندانش را از اندوه بزرگ نجات دادیم.');
INSERT INTO "hussein_taji" VALUES(37,77,'37:77','و تنها فرزندانش را باقی گذاشتیم [ تمام انسان‌ها از نسل سه فرزند نوح علیه السلام بنام: سام، و حام و یافث هستند، (به تفسیر ابن کثیر رجوع کنید).].');
INSERT INTO "hussein_taji" VALUES(37,78,'37:78','و برای او در میان امت‌های بعد (نام نیک) بر جای نهادیم.');
INSERT INTO "hussein_taji" VALUES(37,79,'37:79','در میان جهانیان سلام بر نوح باد.');
INSERT INTO "hussein_taji" VALUES(37,80,'37:80','بی‌گمان ما این گونه نیکوکاران را پاداش می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(37,81,'37:81','یقیناً او از بندگان مؤمن ما بود.');
INSERT INTO "hussein_taji" VALUES(37,82,'37:82','آنگاه دیگران (= تکذیب کنندگان) را غرق کردیم.');
INSERT INTO "hussein_taji" VALUES(37,83,'37:83','و همانا ابراهیم از پیروان او بود.');
INSERT INTO "hussein_taji" VALUES(37,84,'37:84','چون با قلب سلیم (خالی از شرک و شک و شهوت) به پیشگاه پروردگارش آمد.');
INSERT INTO "hussein_taji" VALUES(37,85,'37:85','چون به پدر و قومش گفت: «چه چیزی را می‌پرستید؟!');
INSERT INTO "hussein_taji" VALUES(37,86,'37:86','آیا به جای الله، معبود‌های دروغین می‌خواهید؟!');
INSERT INTO "hussein_taji" VALUES(37,87,'37:87','پس شما نسبت به پروردگار جهانیان چه گمان می‌برید؟!»');
INSERT INTO "hussein_taji" VALUES(37,88,'37:88','پس (ابراهیم) یک نگاه به ستارگان نگریست.');
INSERT INTO "hussein_taji" VALUES(37,89,'37:89','آنگاه گفت: «من بیمارم (و با شما به جشن نمی‌آیم)»');
INSERT INTO "hussein_taji" VALUES(37,90,'37:90','پس آن‌ها پشت‌کنان از او دور شدند (و رفتند).');
INSERT INTO "hussein_taji" VALUES(37,91,'37:91','آنگاه پنهانی نزد معبودان‌شان رفت و گفت: «آیا (چیزی) نمی‌خورید؟');
INSERT INTO "hussein_taji" VALUES(37,92,'37:92','شما را چه شده است که سخن نمی‌گویید».');
INSERT INTO "hussein_taji" VALUES(37,93,'37:93','پس به سوی آنان روی آورد (و) ضربه‌ای محکم (با دست راست) بر آن‌ها زد (و بجز بُت بزرگ همه بت‌ها را درهم شکست).');
INSERT INTO "hussein_taji" VALUES(37,94,'37:94','پس آن‌ها (= قومش) شتابان به سوی او روی آوردند.');
INSERT INTO "hussein_taji" VALUES(37,95,'37:95','(ابراهیم) گفت: «آیا چیزی را می‌پرستید که خود تراشیده‌اید؟!');
INSERT INTO "hussein_taji" VALUES(37,96,'37:96','در حالی‌که الله شما را و آنچه را که انجام می‌دهید (و می‌سازید) آفریده است؟!»');
INSERT INTO "hussein_taji" VALUES(37,97,'37:97','(بت پرستان) گفتند: «بنای (بلندی) برایش بسازید، و او را در (خرمن) آتش بیفکنید».');
INSERT INTO "hussein_taji" VALUES(37,98,'37:98','پس آن‌ها در حق او نیرنگ و مکر ورزیدند و ما آن‌ها را (مغلوب و) پست ساختیم. (و آتش به فرمان الله سرد شد).');
INSERT INTO "hussein_taji" VALUES(37,99,'37:99','و (ابراهیم) گفت: «من به سوی پروردگارم می‌روم، (او) مرا هدایت خواهد کرد.');
INSERT INTO "hussein_taji" VALUES(37,100,'37:100','پروردگارا! به من (فرزندی) از صالحان عطا فرما».');
INSERT INTO "hussein_taji" VALUES(37,101,'37:101','ما او را به (تولد) پسری بردبار [ آن پسر، حضرت اسماعیل علیه السلام بود که به اتفاق مسلمانان و اهل کتاب از حضرت اسحاق علیه السلام بزرگتر بود. (تفسیر ابن کثیر).] بشارت دادیم.');
INSERT INTO "hussein_taji" VALUES(37,102,'37:102','پس هنگامی‌که (فرزندش) با او به (سن) سعی و کوشش رسید گفت: «ای فرزندم! من در خواب دیدم که تو را قربانی می‌کنم، ببین، تو چه نظر داری؟!» گفت: «پدرم، به آنچه مأمور شده‌ای، عمل کن، اگر الله بخواهد، مرا از صابران خواهی یافت». [خواب پیامبران علیهم السلام وحی است.]');
INSERT INTO "hussein_taji" VALUES(37,103,'37:103','پس چون هردو تسلیم شدند، و (ابراهیم) او را به پیشانی (بر زمین) افکند.');
INSERT INTO "hussein_taji" VALUES(37,104,'37:104','و او را ندا دادیم: «ای ابراهیم!');
INSERT INTO "hussein_taji" VALUES(37,105,'37:105','یقیناً خواب (خویش) را تحقق بخشیدی». بدون شک ما این گونه نیکوکاران را پاداش می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(37,106,'37:106','مسلماً این (خواب) آزمایشی آشکار بود.');
INSERT INTO "hussein_taji" VALUES(37,107,'37:107','و او را به ذبح بزرگی فدا دادیم.');
INSERT INTO "hussein_taji" VALUES(37,108,'37:108','و برای او در (میان) امت‌های بعد (نام نیک) بر جای نهادیم.');
INSERT INTO "hussein_taji" VALUES(37,109,'37:109','سلام بر ابراهیم.');
INSERT INTO "hussein_taji" VALUES(37,110,'37:110','این گونه نیکوکاران را پاداش می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(37,111,'37:111','بی‌گمان او از بندگان مؤمن ما بود.');
INSERT INTO "hussein_taji" VALUES(37,112,'37:112','و او را به (تولد) اسحاق پیامبری از شایستگان بشارت دادیم.');
INSERT INTO "hussein_taji" VALUES(37,113,'37:113','و (ما) بر او و اسحاق برکت دادیم، و از دودمان آن دو، (افرادی) نیکوکار بودند، و (افرادی) آشکار بر خود ستم کردند.');
INSERT INTO "hussein_taji" VALUES(37,114,'37:114','و به راستی (ما) بر موسی و هارون منت نهادیم.');
INSERT INTO "hussein_taji" VALUES(37,115,'37:115','و آن دو و قوم‌شان را از اندوه بزرگ نجات دادیم.');
INSERT INTO "hussein_taji" VALUES(37,116,'37:116','و یاریشان کردیم، پس آنان پیروز شدند.');
INSERT INTO "hussein_taji" VALUES(37,117,'37:117','و به آن دو کتاب روشنگر دادیم.');
INSERT INTO "hussein_taji" VALUES(37,118,'37:118','و آن دو را به راه راست هدایت کردیم.');
INSERT INTO "hussein_taji" VALUES(37,119,'37:119','و برای آنان در (میان) امت‌های بعد (نام نیک) باقی گذاشتیم.');
INSERT INTO "hussein_taji" VALUES(37,120,'37:120','سلام بر موسی و هارون!');
INSERT INTO "hussein_taji" VALUES(37,121,'37:121','بی‌گمان ما این گونه نیکوکاران را پاداش می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(37,122,'37:122','یقیناً آن دو از بندگان مؤمن ما بودند.');
INSERT INTO "hussein_taji" VALUES(37,123,'37:123','و بی‌گمان الیاس از رسولان (ما) بود.');
INSERT INTO "hussein_taji" VALUES(37,124,'37:124','چون به قومش گفت: «آیا پرهیزگاری نمی‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(37,125,'37:125','آیا (بت) «بعل» را می‌خوانید، و بهترین آفرینندگان را رها می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(37,126,'37:126','الله که پروردگار شما و پروردگار نیاکان نخستین شماست».');
INSERT INTO "hussein_taji" VALUES(37,127,'37:127','پس (آن‌ها) او را تکذیب کردند، و یقیناً (همۀ) آنان (به جهنم) احضار خواهند شد.');
INSERT INTO "hussein_taji" VALUES(37,128,'37:128','مگر بندگان مخلص الله.');
INSERT INTO "hussein_taji" VALUES(37,129,'37:129','و برای او در (میان) امت‌های بعد (نام نیک) باقی گذاشتیم.');
INSERT INTO "hussein_taji" VALUES(37,130,'37:130','سلام بر «ال یاسین» (= الیاس).');
INSERT INTO "hussein_taji" VALUES(37,131,'37:131','ما این گونه نیکوکاران را پاداش می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(37,132,'37:132','بی‌گمان او از بندگان مؤمن ما بود.');
INSERT INTO "hussein_taji" VALUES(37,133,'37:133','و یقیناً لوط از رسولان (ما) بود.');
INSERT INTO "hussein_taji" VALUES(37,134,'37:134','هنگامی‌که او و خانواده‌اش را همگی نجات دادیم.');
INSERT INTO "hussein_taji" VALUES(37,135,'37:135','مگر پیر زنی که از باقی ماندگان بود.');
INSERT INTO "hussein_taji" VALUES(37,136,'37:136','سپس دیگران را هلاک کردیم.');
INSERT INTO "hussein_taji" VALUES(37,137,'37:137','و شما (پیوسته) صبحگاهان بر (ویرانه‌های شهرهای) آن‌ها می‌گذرید.');
INSERT INTO "hussein_taji" VALUES(37,138,'37:138','و (همچنین) شامگاهان آیا نمی‌اندیشید؟!');
INSERT INTO "hussein_taji" VALUES(37,139,'37:139','و یقیناً یونس از رسولان (ما) بود.');
INSERT INTO "hussein_taji" VALUES(37,140,'37:140','هنگامی به کشتی پر (از مسافر و بار) گریخت.');
INSERT INTO "hussein_taji" VALUES(37,141,'37:141','پس (اهل کشتی) قرعه زدند، پس (قرعه بنام او افتاد و) مغلوب شد.');
INSERT INTO "hussein_taji" VALUES(37,142,'37:142','آنگاه ماهی (عظیمی) او را بلعید، در حالی‌که او سزاوار نکوهش بود.');
INSERT INTO "hussein_taji" VALUES(37,143,'37:143','پس اگر او از تسبیح‌گویان نبود [ چنانکه در سورۀ انبیاء آیه آمده.].');
INSERT INTO "hussein_taji" VALUES(37,144,'37:144','یقیناً تا روزی‌که (مردم) بر انگیخته می‌شوند، در شکمش باقی می‌ماند.');
INSERT INTO "hussein_taji" VALUES(37,145,'37:145','پس او را به سرزمین خشک (و خالی از گیاه) افکندیم، در حالی‌که او بیمار بود،');
INSERT INTO "hussein_taji" VALUES(37,146,'37:146','و درخت کدویی بر (فراز) او رویاندیم (تا زیر سایۀ آن آرام بگیرد).');
INSERT INTO "hussein_taji" VALUES(37,147,'37:147','و او را بسوی یکصد هزار نفر (از قومش) یا بیشتر فرستادیم.');
INSERT INTO "hussein_taji" VALUES(37,148,'37:148','پس (آن‌ها) ایمان آوردند، بنا براین تا مدتی آنان را (از نعمت‌های زندگی) بهره‌مند ساختیم.');
INSERT INTO "hussein_taji" VALUES(37,149,'37:149','پس (ای پیامبر) از آنان (= مشرکان) بپرس: آیا دختران از آنِ پروردگارت است، و پسران از آنِ آن‌هاست؟');
INSERT INTO "hussein_taji" VALUES(37,150,'37:150','آیا ما فرشتگان را مؤنث آفریدیم و آن‌ها حاضر بودند؟');
INSERT INTO "hussein_taji" VALUES(37,151,'37:151','آگاه باش! بی‌گمان آن‌ها از (روی تهمت و) دروغ‌گویی‌شان است که می‌گویند:');
INSERT INTO "hussein_taji" VALUES(37,152,'37:152','«الله (فرزندی) زاده است» و یقیناً آن‌ها دروغ‌گو هستند.');
INSERT INTO "hussein_taji" VALUES(37,153,'37:153','آیا (الله) دختران را بر پسران ترجیح داد؟');
INSERT INTO "hussein_taji" VALUES(37,154,'37:154','شما را چه شده است؟ چگونه حکم می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(37,155,'37:155','آیا نمی‌اندیشید؟!');
INSERT INTO "hussein_taji" VALUES(37,156,'37:156','یا شما (بر ادعای خود) دلیل روشنی دارید؟!');
INSERT INTO "hussein_taji" VALUES(37,157,'37:157','پس اگر راستگویید، کتاب‌تان را بیاورید.');
INSERT INTO "hussein_taji" VALUES(37,158,'37:158','و آن‌ها (= مشرکان) میان او (= الله) و جنیان (نسب و) خویشاوندی قرار دادند، در حالی‌که جنیان به خوبی می‌دانند که آنان احضار خواهند شد.');
INSERT INTO "hussein_taji" VALUES(37,159,'37:159','الله منزه است از آنچه توصیف می‌کنند.');
INSERT INTO "hussein_taji" VALUES(37,160,'37:160','مگر بندگان مخلص الله.');
INSERT INTO "hussein_taji" VALUES(37,161,'37:161','پس شما (مشرکان) و آنچه می‌پرستید.');
INSERT INTO "hussein_taji" VALUES(37,162,'37:162','(هرگز) نمی‌توانید بر (پرستش) آن (بت‌ها کسی را) گمراه کنید.');
INSERT INTO "hussein_taji" VALUES(37,163,'37:163','مگر کسی‌که راهی (آتش) جهنم باشد.');
INSERT INTO "hussein_taji" VALUES(37,164,'37:164','(فرشتگان گفتند:) «و هیچ‌کس از ما نیست، مگر آن که جایگاه (و مقام) معلومی دارد.');
INSERT INTO "hussein_taji" VALUES(37,165,'37:165','و همانا ما (برای اطاعت فرمان الله) به صف ایستاده‌ایم.');
INSERT INTO "hussein_taji" VALUES(37,166,'37:166','و بی‌گمان ما تسبیح گویان (او) هستیم».');
INSERT INTO "hussein_taji" VALUES(37,167,'37:167','و (کافران) پیوسته می‌گفتند:');
INSERT INTO "hussein_taji" VALUES(37,168,'37:168','«اگر نزد ما کتابی از (کتاب‌های) پیشینیان می بود.');
INSERT INTO "hussein_taji" VALUES(37,169,'37:169','مسلماً ما از بندگان مخلص الله می‌شدیم».');
INSERT INTO "hussein_taji" VALUES(37,170,'37:170','پس (هنگامی‌که پیامبر برای آن‌ها قرآن آورد) آن‌ها به آن (قرآن) کافر شدند، و به زودی خواهند دانست.');
INSERT INTO "hussein_taji" VALUES(37,171,'37:171','و به راستی وعدۀ ما برای بندگان فرستادۀ ما از پیش صادر شده است.');
INSERT INTO "hussein_taji" VALUES(37,172,'37:172','که هر آینه آن‌ها یاری شدگانند.');
INSERT INTO "hussein_taji" VALUES(37,173,'37:173','و بی‌گمان لشکر ما پیروزند.');
INSERT INTO "hussein_taji" VALUES(37,174,'37:174','پس (ای پیامبر) تا مدتی از آن‌ها (= مشرکان) روی بگردان.');
INSERT INTO "hussein_taji" VALUES(37,175,'37:175','و آن‌ها را ببین، پس (آن‌ها نیز، نتیجۀ کردار خود را) به زودی خواهند دید.');
INSERT INTO "hussein_taji" VALUES(37,176,'37:176','آیا آن‌ها برای (دیدن) عذاب ما شتاب دارند؟!');
INSERT INTO "hussein_taji" VALUES(37,177,'37:177','پس چون (عذاب) بر آستانۀ خانه‌هایشان فرود آید، هشداریافتگان چه صبحگاه بدی خواهند داشت.');
INSERT INTO "hussein_taji" VALUES(37,178,'37:178','و تا مدتی از آن‌ها روی بگردان.');
INSERT INTO "hussein_taji" VALUES(37,179,'37:179','و (آن‌ها را) ببین، پس (آن‌ها نیز) به زودی خواهند دید.');
INSERT INTO "hussein_taji" VALUES(37,180,'37:180','پاک و منزه است پروردگار تو، پروردگار عزت، از آنچه (مشرکان) توصیف می‌کنند.');
INSERT INTO "hussein_taji" VALUES(37,181,'37:181','و سلام، بر رسولان.');
INSERT INTO "hussein_taji" VALUES(37,182,'37:182','و سپاس و ستایشِ مخصوص الله است که پروردگار جهانیان است.');
INSERT INTO "hussein_taji" VALUES(38,1,'38:1','ص (صاد) سوگند به قرآنی که دارای پند و اندرز است.');
INSERT INTO "hussein_taji" VALUES(38,2,'38:2','بلکه، کسانی‌که کافر شدند در سرکشی و اختلافند.');
INSERT INTO "hussein_taji" VALUES(38,3,'38:3','چه بسیار نسل‌های را که پیش از آن‌ها هلاک کردیم، پس (هنگام نزول عذاب) فریاد می‌زدند، اما (آن زمان) وقت نجات نبود.');
INSERT INTO "hussein_taji" VALUES(38,4,'38:4','و آن‌ها به شگفت آمدند که (پیامبر) هشداردهنده‌ای از میان (خود) ‌شان به سوی‌شان آمده است، و کافران گفتند: «این جادوگری بسیار دروغگو است،');
INSERT INTO "hussein_taji" VALUES(38,5,'38:5','آیا (همۀ) معبودان را یک معبود قرار داده است؟ بی‌گمان این چیزی عجیبی است».');
INSERT INTO "hussein_taji" VALUES(38,6,'38:6','و بزرگان (و اشراف) شان، به راه افتادند و گفتند: «بروید و بر (پرستش) معبودان‌تان پایدار باشید، بی‌گمان این (دعوت) چیزی است خواسته شده (که شما را گمراه کنند).');
INSERT INTO "hussein_taji" VALUES(38,7,'38:7','ما (هرگز) این (چنین سخنی) در آیین واپسین نشنیده‌ایم، این جز دروغ (چیزی) نیست.');
INSERT INTO "hussein_taji" VALUES(38,8,'38:8','آیا از میان (همۀ) ما قرآن بر او نازل شده است؟!» (در حقیقت چنین نیست که آن‌ها می‌گویند) بلکه آن‌ها در (وحی و) قرآن من تردید دارند، بلکه هنوز عذاب را نچشیده‌اند.');
INSERT INTO "hussein_taji" VALUES(38,9,'38:9','یا آنکه گنجینه‌های رحمت پروردگار پیروزمند بخشنده‌ات نزد آن‌هاست؟');
INSERT INTO "hussein_taji" VALUES(38,10,'38:10','یا فرمانروایی آسمان‌ها و زمین و آنچه میان آن دوست از آنِ آن‌هاست؟! پس (اگر چنین است) با ریسمان (و هر وسیله‌ای دیگر به آسمان) بالا روند.');
INSERT INTO "hussein_taji" VALUES(38,11,'38:11','(این‌ها) لشکری شکست خورده از احزاب هستند.');
INSERT INTO "hussein_taji" VALUES(38,12,'38:12','پیش از آن‌ها قوم نوح و عاد و فرعون صاحب میخ‌ها (و سپاه، نیز پیامبران ما را) تکذیب کردند.');
INSERT INTO "hussein_taji" VALUES(38,13,'38:13','و (نیز) ثمود، و قوم لوط، و اصحاب ایکه (= قوم شعیب) این‌ها احزاب (و گروه‌های) بودند (که پیامبران را تکذیب کردند).');
INSERT INTO "hussein_taji" VALUES(38,14,'38:14','هر یک (از این‌ها) بودند که پیامبران را تکذیب کردند، پس عقوبت (من) تحقق یافت.');
INSERT INTO "hussein_taji" VALUES(38,15,'38:15','و این‌ها جز بانگی سهمناک را انتظار نمی‌کشند، که هیچ مهلت بازگشتی ندارد.');
INSERT INTO "hussein_taji" VALUES(38,16,'38:16','و (آن‌ها) گفتند: «پروردگارا! بهرۀ ما را (از عذاب) پیش از فرا رسیدن روز حساب به ما بده».');
INSERT INTO "hussein_taji" VALUES(38,17,'38:17','بر آنچه می‌گویند، صبر کن، و بندۀ ما داود صاحب (نیرو و) قوت را یاد آور، بی‌گمان او بسیار رجوع کننده بود.');
INSERT INTO "hussein_taji" VALUES(38,18,'38:18','همانا ما کوه‌ها را با او مسخر (و رام) کردیم، که شامگاهان و صبحگاهان (با او) تسبیح می‌گفتند.');
INSERT INTO "hussein_taji" VALUES(38,19,'38:19','و پرندگان را (نیز، مسخر او کردیم) که گرد می‌آمدند، همۀ (این‌ها) فرمانبردار (و باز گشت کننده به سوی) او بودند.');
INSERT INTO "hussein_taji" VALUES(38,20,'38:20','و فرمانروایی او را استوار ومحکم کردیم، و به او حکمت و سخنان فصیح (فیصله کننده) عطا کردیم.');
INSERT INTO "hussein_taji" VALUES(38,21,'38:21','و آیا خبر شاکیان هنگامی‌که از (دیوار) عبادتگاه (داود) بالا رفتند، به تو رسیده است؟!');
INSERT INTO "hussein_taji" VALUES(38,22,'38:22','چون بر داود وارد شدند، پس (او) از آن‌ها وحشت کرد، (آن‌ها) گفتند: «نترس، دو نفر شاکی هستیم که یکی از ما بر دیگری ستم کرده است. پس بین ما به حق داوری کن، و ستم نکن، و ما را به راه راست راهنمایی کن.');
INSERT INTO "hussein_taji" VALUES(38,23,'38:23','بی‌گمان این برادر من است، او نود و نه میش دارد، و من یک میش دارم. پس (او) می‌گوید: آن (یک میش) را به من وا گذار، و در سخن بر من غلبه کرده است».');
INSERT INTO "hussein_taji" VALUES(38,24,'38:24','(داوود) گفت: به راستی او با در خواست یک میش تو که آن را به میش‌هایش بیفزاید بر تو ستم کرده است، و البته بسیاری از شریکان به یکدیگر ستم می‌کنند، مگر کسانی‌که ایمان آورده‌اند و کار‌های شایسته انجام داده‌اند، و اینان (نیز) اندک هستند. و داوود دانست که ما او را آزموده‌ایم، پس از پروردگارش آمرزش خواست، (و فروتنانه) به (رکوع =) سجده افتاد و (به سوی الله) رجوع کرد.');
INSERT INTO "hussein_taji" VALUES(38,25,'38:25','پس ما او را بخشیدیم، و بی‌گمان برای او نزد ما (قرب و) مقامی و الا و بازگشت نیکوست.');
INSERT INTO "hussein_taji" VALUES(38,26,'38:26','ای داوود! ما تو را در زمین خلیفه (= فرمانروا) قرار دادیم، پس به حق در میان مردم داوری کن، و از هوای (نفس) پیروی نکن که تو را از راه الله گمراه می‌کند، بی‌گمان کسانی‌که از راه الله گمراه می‌شوند، به خاطر آنکه روز حساب را فراموش کردند، عذاب شدیدی (در پیش) دارند.');
INSERT INTO "hussein_taji" VALUES(38,27,'38:27','(ما) آسمان و زمین و آنچه میان آن‌هاست بیهوده نیافریده‌ایم، این گمان کسانی است که کافر شدند، پس وای بر کسانی‌که کافر شدند از آتش (دوزخ!).');
INSERT INTO "hussein_taji" VALUES(38,28,'38:28','آیا کسانی‌که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، مانند مفسدان در زمین قرار می‌دهیم؟! آیا پرهیزگاران را مانند بدکاران قرار می‌دهیم؟');
INSERT INTO "hussein_taji" VALUES(38,29,'38:29','کتابی است پر برکت، که آن را بر تو نازل کردیم، تا در آیاتش تدبّر کنند، و خردمندان پند گیرند.');
INSERT INTO "hussein_taji" VALUES(38,30,'38:30','و (ما) سلیمان را به داوود عطا کردیم، چه بندۀ خوبی! بی‌گمان او بسیار رجوع کننده بود.');
INSERT INTO "hussein_taji" VALUES(38,31,'38:31','(به یاد بیاور) چون عصرگاهان اسبان چابک تیزرو بر او عرضه شد،');
INSERT INTO "hussein_taji" VALUES(38,32,'38:32','پس گفت: «من این اسبان را به خاطر (فرمان) پروردگارم دوست دارم» (و پیوسته به آن‌ها نگاه می‌کرد) تا از دیدگانش پنهان شدند [ یا: تا اینکه آفتاب در پرده پنهان شد و غروب کرد. (به تفسیر ابن کثیر و طبری رجوع کنید).].');
INSERT INTO "hussein_taji" VALUES(38,33,'38:33','گفت: «آن اسبان را نزد من باز گردانید». سپس به ساق‌ها و گردن‌های (آن‌ها) دست کشید (و نوازش کرد).');
INSERT INTO "hussein_taji" VALUES(38,34,'38:34','و به راستی (ما) سلیمان را آزمودیم، و بر تخت او جسدی افکندیم، سپس (او) رو (به درگاه الله) آورد (و توبه کرد).');
INSERT INTO "hussein_taji" VALUES(38,35,'38:35','گفت: «پروردگارا! مرا ببخش، و به من فرمانروایی عطا فرما که پس از من کسی را سزاوار نباشد، بی‌گمان تو بخشاینده‌ای».');
INSERT INTO "hussein_taji" VALUES(38,36,'38:36','پس (ما) باد را برای او مسخر کردیم، تا به فرمانش به نرمی حرکت کند، هر کجا می‌خواست، برود.');
INSERT INTO "hussein_taji" VALUES(38,37,'38:37','و (نیز) دیوان را، هر بنا و غواصی (از آن‌ها را برای او مسخر کردیم).');
INSERT INTO "hussein_taji" VALUES(38,38,'38:38','و (گروه) دیگری (از دیوان) را (که همه) در غل و زنجیر بودند (برایش مسخر کردیم).');
INSERT INTO "hussein_taji" VALUES(38,39,'38:39','این عطای ماست، پس (به هر کس می‌خواهی) ببخش، و یا نگه دار، حسابی بر تو نیست.');
INSERT INTO "hussein_taji" VALUES(38,40,'38:40','و بی‌گمان برای او (= سلیمان) نزد ما (قرب و) مقامی والا، و بازگشت نیکوست.');
INSERT INTO "hussein_taji" VALUES(38,41,'38:41','و به یاد بیاور، بندۀ ما ایوب را، هنگامی‌که پروردگارش را ندا داد که: همانا شیطان به من رنج و عذاب رسانده است.');
INSERT INTO "hussein_taji" VALUES(38,42,'38:42','(به او فرمان دادیم:) پای خود را بر زمین بکوب، (چشمه‌ای می‌جوشد) این (چشمه آبی) خنک برای شستشو و آشامیدنی است.');
INSERT INTO "hussein_taji" VALUES(38,43,'38:43','و خانواده‌اش و همانند آنان همراه‌شان را به او بخشیدیم، که رحمتی از سوی ما و اندرزی برای خردمندان باشد.');
INSERT INTO "hussein_taji" VALUES(38,44,'38:44','و (به او فرمان دادیم که) دسته‌ای از شاخه‌های (باریک) را برگیر، پس با آن (همسرت را) بزن، و سوگند خود را مشکن، بی‌گمان ما او را شکیبا یافتیم، چه بندۀ خوبی، که بسیار رجوع کننده بود.');
INSERT INTO "hussein_taji" VALUES(38,45,'38:45','و (ای پیامبر) به یاد آور، بندگان ما، ابراهیم، و اسحاق و یعقوب را، صاحبان دست‌های (توانمند) و چشم‌های (بینا).');
INSERT INTO "hussein_taji" VALUES(38,46,'38:46','ما آن‌ها را با خصلت ویژه‌ای که آن یاد سرای آخرت بود، خالص گرداندیم،');
INSERT INTO "hussein_taji" VALUES(38,47,'38:47','و یقیناً آن‌ها نزد ما از بر گزیدگان نیکان بودند.');
INSERT INTO "hussein_taji" VALUES(38,48,'38:48','و به یاد آور، اسماعیل، و الیسع و ذوالکفل را که همگی از نیکان بودند.');
INSERT INTO "hussein_taji" VALUES(38,49,'38:49','این (قرآن) پندی است، و بی‌گمان برای پرهیزگاران منزلگاهی نیکوست.');
INSERT INTO "hussein_taji" VALUES(38,50,'38:50','باغ‌های جاویدان (بهشتی) که در‌هایش برای آنان باز است.');
INSERT INTO "hussein_taji" VALUES(38,51,'38:51','در آنجا تکیه زده‌اند، و در آن میوۀ فراوان و نوشیدنی را می‌طلبند.');
INSERT INTO "hussein_taji" VALUES(38,52,'38:52','و نزد آنان (همسرانی) فرو هشته چشم همسال (که تنها به شوهران خود نظر دارند) است.');
INSERT INTO "hussein_taji" VALUES(38,53,'38:53','این است چیزهایی‌که برای روز حساب به شما وعده داده می‌شود.');
INSERT INTO "hussein_taji" VALUES(38,54,'38:54','بی‌گمان این روزی ماست که آن را هیچ پایانی نیست.');
INSERT INTO "hussein_taji" VALUES(38,55,'38:55','این (پاداش پرهیزگاران) است، و بی‌گمان برای سرکشان بدترین بازگشتگاه است.');
INSERT INTO "hussein_taji" VALUES(38,56,'38:56','جهنم، که در آن وارد می‌شوند، پس چه بد آرامگاهی است.');
INSERT INTO "hussein_taji" VALUES(38,57,'38:57','این (عذاب) آب جوشان و چرک و خونابه است، که باید آن را بچشند.');
INSERT INTO "hussein_taji" VALUES(38,58,'38:58','و (کیفر‌های) دیگر همانند آن به انواع گوناگون (دارند)');
INSERT INTO "hussein_taji" VALUES(38,59,'38:59','(به سرداران آن‌ها گفته می‌شود:) این گروهی است که با شما (به جهنم) وارد می‌شوند، (آن‌ها می‌گویند:) خوش آمد (و گشایش) بر آن‌ها مباد. بی‌گمان آن‌ها داخل شدگان به آتش هستند.');
INSERT INTO "hussein_taji" VALUES(38,60,'38:60','(پیروان) گویند: «بلکه، شما را خوش آمد مباد، که این (عذاب) را پیشاپیش شما برای ما فراهم کرده‌اید، پس چه بد قرارگاهی است».');
INSERT INTO "hussein_taji" VALUES(38,61,'38:61','(سپس) گویند: پروردگارا! هر کس که این (عذاب) را پیشاپیش برای ما فراهم کرده است، پس عذابی دو چنان در آتش بر او بیفزار».');
INSERT INTO "hussein_taji" VALUES(38,62,'38:62','و گویند: «ما را چه شده است، مردمانی را که ما آن‌ها را اشرار می‌شمردیم، (در جهنم) نمی‌بینیم؟!');
INSERT INTO "hussein_taji" VALUES(38,63,'38:63','آیا آن‌ها را به مسخره گرفتیم یا (اینکه) از نظر‌ها دور مانده‌اند؟!»');
INSERT INTO "hussein_taji" VALUES(38,64,'38:64','بی‌گمان این مخاصمه (و گفتگوی) دوزخیان حق (و واقعیت) است.');
INSERT INTO "hussein_taji" VALUES(38,65,'38:65','(ای پیامبر) بگو: به درستی که من تنها یک هشدار دهنده‌ام، و هیچ معبودی (به حق) جز الله یکتای قهّار نیست.');
INSERT INTO "hussein_taji" VALUES(38,66,'38:66','پروردگار آسمان‌ها و زمین و آنچه میان آن دو است، (آن) پیروزمند آمرزنده».');
INSERT INTO "hussein_taji" VALUES(38,67,'38:67','(ای پیامبر) بگو: «این خبری بزرگ است،');
INSERT INTO "hussein_taji" VALUES(38,68,'38:68','که شما از آن روی می‌گردانید!');
INSERT INTO "hussein_taji" VALUES(38,69,'38:69','مـن از سـاکـنان عــالــم بالا (= فرشتگان) چون (دربارۀ آفرینش آدم) با یکدیگر جدال می‌کردند، خبری ندارم [ به سورۀ بقره آیه رجوع کنید.].');
INSERT INTO "hussein_taji" VALUES(38,70,'38:70','به من وحی نمی‌شود مگر این‌که من هشداردهنده‌ای آشکار هستم».');
INSERT INTO "hussein_taji" VALUES(38,71,'38:71','(و به یاد آور) هنگامی‌که پروردگارت به فرشتگان گفت: بی‌تردید من بشری را از گِل می‌آفرینم،');
INSERT INTO "hussein_taji" VALUES(38,72,'38:72','پس هنگامی‌که درستش کردم، و از روح خود در آن دمیدم، برای او سجده‌کنان بیفتید».');
INSERT INTO "hussein_taji" VALUES(38,73,'38:73','پس فرشتگان همگی یکپارچه سجده کردند.');
INSERT INTO "hussein_taji" VALUES(38,74,'38:74','جز ابلیس که تکبّر ورزید و از کافران بود.');
INSERT INTO "hussein_taji" VALUES(38,75,'38:75','(الله) فرمود: «ای ابلیس! چه چیز مانع تو شد، از آنکه برای آنچه من با دو دست خود آفریده ام، سجده کنی؟ آیا تکبر ورزیدی یا از بلند پایگان هستی؟!»');
INSERT INTO "hussein_taji" VALUES(38,76,'38:76','گفت: «من از او بهترم، مرا از آتش آفریده‌ای، و او را از گِل آفریده‌ای».');
INSERT INTO "hussein_taji" VALUES(38,77,'38:77','فرمود: «پس از اینجا بیرون شو، که تو رانده شده‌ای.');
INSERT INTO "hussein_taji" VALUES(38,78,'38:78','و همانا لعنت من تا روز قیامت بر تو خواهد بود».');
INSERT INTO "hussein_taji" VALUES(38,79,'38:79','(ابلیس) گفت: «پروردگارا! پس مرا تا روزی‌که (دوباره) برانگیخته می‌شوند، مهلت ده».');
INSERT INTO "hussein_taji" VALUES(38,80,'38:80','(الله) فرمود: «همانا تو از مهلت یافتگانی،');
INSERT INTO "hussein_taji" VALUES(38,81,'38:81','تا آن روز زمان معین».');
INSERT INTO "hussein_taji" VALUES(38,82,'38:82','(ابلیس) گفت: «پس به عزتت سوگند، که همۀ آن‌ها را گمراه خواهم کرد،');
INSERT INTO "hussein_taji" VALUES(38,83,'38:83','مگر بندگان مخلص تو را، از میان آن‌ها».');
INSERT INTO "hussein_taji" VALUES(38,84,'38:84','(الله) فرمود: «پس (این سخن) حق است، و حق را می‌گویم،');
INSERT INTO "hussein_taji" VALUES(38,85,'38:85','یقیناً جهنم را از تو و از آنان که از تو پیروی کنند، پر خواهم کرد».');
INSERT INTO "hussein_taji" VALUES(38,86,'38:86','(ای پیامبر) بگو: «من بر (رساندن) این (دین) هیچ مزدی از شما در خواست نمی‌کنم، و من از متکلفان نیستم».');
INSERT INTO "hussein_taji" VALUES(38,87,'38:87','این (قرآن) جز پند برای جهانیان نیست.');
INSERT INTO "hussein_taji" VALUES(38,88,'38:88','و قطعاً خبر (صدق) آن را بعد از مدتی خواهید دانست.');
INSERT INTO "hussein_taji" VALUES(39,1,'39:1','نازل شدن (این) کتاب از سوی الله پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(39,2,'39:2','بی‌گمان ما (این) کتاب را به حق بر تو نازل کردیم، پس الله را پرستش کن و دین را برای او خالص گردان.');
INSERT INTO "hussein_taji" VALUES(39,3,'39:3','آگاه باشید که دین خالص از آن الله است، و کسانی‌که به جای او معبودان (و اولیای) گرفتند (وگفتند) این‌ها را نمی‌پرستیم جز برای این‌که ما را به الله نزدیک کنند بی‌گمان الله (روز قیامت) میان آن‌ها در آنچه اختلاف داشتند، داوری می‌کند، الله آن کسی را‌ که دروغگوی ناسپاس است، هدایت نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(39,4,'39:4','اگر (به فرض مُحال) الله می‌خواست فرزندی بگیرد، مسلماً از میان آنچه را می‌آفریند، چیزی را که می‌خواست بر می‌گزید، او پاک و منزه است، او الله یکتای قهار است.');
INSERT INTO "hussein_taji" VALUES(39,5,'39:5','آسمان‌ها و زمین را به حق آفرید، شب را بر روز می‌پیچد، و روز را بر شب می‌پیچد، و خورشید و ماه را مسخر (و رام) گردانید، هر کدام تا زمانی معین در حرکتند، آگاه باشید، که او پیروزمند آمرزنده است.');
INSERT INTO "hussein_taji" VALUES(39,6,'39:6','(او) شما را از یک تن آفرید، سپس همسرش را از آن پدید آورد، و از چهار پایان برای شما هشت جفت فرو فرستاد، و شما را در شکم‌های مادران‌تان آفرینشی بعد از آفرینش دیگر، در تاریکی‌های سه‌گانه، می‌آفریند، این الله، پروردگار شماست که فرمانروایی از آنِ اوست، و جز او معبودی (به حق) نیست، پس چگونه (از حق) منحرف (و روی گردان) می‌شوید؟!');
INSERT INTO "hussein_taji" VALUES(39,7,'39:7','اگر ناسپاسی کنید، پس (بدانید که) الله از شما بی‌نیاز است، و هرگز ناسپاسی را برای بندگانش نمی‌پسندد. و اگر شکر او را به جای آورید آن را برای شما می‌پسندد. و هیچ کس بار گناه دیگررا بر دوش نمی‌کشد، پس بازگشت‌تان به سوی پروردگارتان است، آنگاه شما را به آنچه انجام می‌دادید، آگاه می‌کند، یقیناً او به راز دل‌ها آگاه است.');
INSERT INTO "hussein_taji" VALUES(39,8,'39:8','و هنگامی‌که به انسان (رنج و) زیانی برسد، پروردگارش را می‌خواند، و به سوی او باز می‌گردد، پس هنگامی‌که نعمتی از سوی خود به او بخشید، آنچه را که از پیش برای آن دعا می‌کرد فراموش می‌کند، و برای الله همتایانی قرار می‌دهد، تا (مردم را) از راه او گمراه کند. بگو: «اندکی از کفرت (در دنیا) بهره‌مند شو، بی‌شک تو از دوزخیان خواهی بود».');
INSERT INTO "hussein_taji" VALUES(39,9,'39:9','آیا (این مشرک بهتر است، یا) کسی‌که در ساعات شب در حال سجده و قیام به عبادت مشغول است، و از (عذاب) آخرت بیم ناک است، و به رحمت پروردگارش امیدوار است؟! بگو: «آیا کسانی‌که می‌دانند، و کسانی‌که نمی‌دانند یکسان هستند؟! تنها خردمندان پند می‌پذیرند».');
INSERT INTO "hussein_taji" VALUES(39,10,'39:10','(ای پیامبر) بگو: «ای بندگان من که ایمان آورده‌اید! از پروردگارتان بترسید، برای کسانی‌که در این دنیا نیکی کرده‌اند، پاداش نیکوست، و (اگر در شکنجه و آزار مشرکان بودید، هجرت کنید، که) زمین الله وسیع است، جز این نیست که صابران، پاداش خود را بی‌شمار و تمام دریافت می‌دارند».');
INSERT INTO "hussein_taji" VALUES(39,11,'39:11','بگو: «من مأمور شده‌ام که الله را پرستش کنم، در حالی‌که دینم را برای او خالص گردانم».');
INSERT INTO "hussein_taji" VALUES(39,12,'39:12','و مأمور شده ام که نخستین مسلمانان باشم».');
INSERT INTO "hussein_taji" VALUES(39,13,'39:13','بگو: «همانا من اگر نافرمانی پروردگارم کنم، از عذاب روزبزرگ (قیامت) می‌ترسم».');
INSERT INTO "hussein_taji" VALUES(39,14,'39:14','(ای پیامبر) بگو: «من الله را می‌پرستم، در حالی‌که دینم را برای او خالص می‌گردانم،');
INSERT INTO "hussein_taji" VALUES(39,15,'39:15','پس شما هر چه را که می‌خواهید به جای او پرستش کنید. بگو: «بی‌گمان زیانکاران (واقعی) کسانی اند که خود و خاندان‌شان را در روز قیامت از دست داده‌اند، آگاه باشید این همان زیان آشکار است.');
INSERT INTO "hussein_taji" VALUES(39,16,'39:16','برای آن‌ها از بالای (سر) شان سایبان‌هایی از آتش، و در زیر (پای) شان (نیز) سایبان‌هایی است، این (چیزی) است که الله با آن بندگانش را می‌ترساند، ای بندگان من! از من بترسید.');
INSERT INTO "hussein_taji" VALUES(39,17,'39:17','و کسانی‌که از طاغوت (= شیطان و بت‌ها) اجتناب کردند از آنکه آن را عبادت کنند، و رو به سوی الله آوردند، برای آنان بشارت است، پس بندگان مرا بشارت ده.');
INSERT INTO "hussein_taji" VALUES(39,18,'39:18','(همان) کسانی‌که سخن‌(ها) را می‌شنوند، پس (از) نیکو‌ترین آن پیروی می‌کنند، آن‌ها کسانی هستند که الله هدایت‌شان کرده است، و آن‌ها خردمندانند.');
INSERT INTO "hussein_taji" VALUES(39,19,'39:19','آیا (تو می‌توانی) کسی‌ را که فرمان عذاب بر او تحقق یافته است (نجات دهی؟!) آیا تو (می توانی) کسی را که در آتش است نجات دهی؟!');
INSERT INTO "hussein_taji" VALUES(39,20,'39:20','لکن برای کسانی‌که از پروردگارشان ترسیدند، غرفه‌هایی (در بهشت) است، که بر فراز آن‌ها غرفه‌های دیگر بنا شده است، و از زیر آن‌ها نهر‌ها جاری است، (این) وعدۀ الله است (و) الله وعدۀ خود را خلاف نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(39,21,'39:21','آیا ندیدی که الله از آسمان آبی را نازل کرد، پس آن را بصورت چشمه‌هایی در زمین به جریان در آورد، سپس با آن کشت و زرعی که رنگ‌های گوناگون دارد بیرون می‌آورد، آنگاه خشک می‌شود، و آن را زرد می‌بینی، سپس آن را (ریزه ریزه و) درهم می‌شکند؟! بی‌گمان در این (امر) پندی برای خردمندان است.');
INSERT INTO "hussein_taji" VALUES(39,22,'39:22','آیا کسی‌که الله سینه‌اش را برای (پذیرش) اسلام گشوده است، پس او از سوی پروردگارش بر نور (و روشنی) است (همچون سخت دلان است؟!) پس وای بر آنان که دل‌های شان در برابر ذکر الله سخت است. آنان در گمراهی آشکار هستند.');
INSERT INTO "hussein_taji" VALUES(39,23,'39:23','الله بهترین سخن را نازل کرده است، کتابی که (آیاتش) متشابه (= همانند یکدیگر، و) مکرر است که از (شنیدن) آن پوست‌های کسانی‌که از پروردگارشان می‌ترسند، به لرزه می‌افتد، آنگاه پوست‌هایشان و دل‌های شان با یاد الله نرم می‌شود (و آرام می‌گیرد). این هدایت الله است که هر کس را بخواهد با آن هدایت می‌کند، و هر کس را که گمراه سازد، پس برای او هیچ هدایت کننده‌ای نخواهد بود.');
INSERT INTO "hussein_taji" VALUES(39,24,'39:24','آیا کسی‌که با چهره‌اش سختی عذاب روز قیامت را (از خود) دور می‌سازد، (همانند بهشتیان هستند؟!) و به ستمکاران گفته می‌شود: «بچشید (کیفر) آنچه را که بدست می‌آوردید».');
INSERT INTO "hussein_taji" VALUES(39,25,'39:25','کسانی‌که پیش از آن‌ها بودند (نیز آیات ما را) تکذیب کردند، پس عذاب (الهی) از جایی که نمی‌دانستند، به سراغ‌شان آمد.');
INSERT INTO "hussein_taji" VALUES(39,26,'39:26','پس الله (طعم) خواری را در زندگی دنیا به آن‌ها چشاند، و مسلماً عذاب آخرت بزرگتر (و سخت‌تر) است، اگر می‌دانستند.');
INSERT INTO "hussein_taji" VALUES(39,27,'39:27','و به راستی ما برای مردم در این قرآن از هر (نوع) مثلی زده‌ایم، شاید که آنان پند گیرند.');
INSERT INTO "hussein_taji" VALUES(39,28,'39:28','قرآنی عربی، بی‌هیچ انحراف (و کجی را نازل کردیم) شاید آنان تقوا پیشه کنند.');
INSERT INTO "hussein_taji" VALUES(39,29,'39:29','الله مردی (مملوک) را مثل زده که در (بردگی) او شریکانی است که پیوسته با هم اختلاف (و مشاجره) دارند، و مردی (مملوک) که تنها تسلیم یک شخص است، آیا (این دو) در وصف یکسانند؟! حمد و ستایش مخصوص الله است، بلکه بیشتر آن‌ها نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(39,30,'39:30','(ای پیامبر) قطعاً تو خواهی مرد، و آن‌ها (نیز) خواهند مرد.');
INSERT INTO "hussein_taji" VALUES(39,31,'39:31','سپس بی‌گمان شما روز قیامت نزد پروردگار‌تان مخاصمه (و جدال) می‌کنید.');
INSERT INTO "hussein_taji" VALUES(39,32,'39:32','پس چه کسی ستمکار‌تر است از کسی‌که بر الله دروغ ببندد، و سخن راست را چون به سراغ او آمد تکذیب کند؟ آیا در جهنم جایگاهی برای کافران نیست؟!');
INSERT INTO "hussein_taji" VALUES(39,33,'39:33','و کسی‌که سخن راست را آورد و آن را تصدیق کرد، اینانند که پرهیزگارانند.');
INSERT INTO "hussein_taji" VALUES(39,34,'39:34','برایشان هر چه بخواهند نزد پروردگارشان (آماده) است، این پاداش نیکوکاران است.');
INSERT INTO "hussein_taji" VALUES(39,35,'39:35','تا الله بد‌ترین اعمالی را که مرتکب شده‌اند، از آن‌ها دور کند (و بزداید) و آن‌ها را به بهترین اعمالی‌که انجام می‌دادند پاداش دهد.');
INSERT INTO "hussein_taji" VALUES(39,36,'39:36','آیا الله برای بنده‌اش کافی نیست؟! و تو را به کسانی غیر از او می‌ترسانند، و هر کس را الله گمراه کند، پس او را هیچ هدایت‌گری نیست.');
INSERT INTO "hussein_taji" VALUES(39,37,'39:37','و هر کس را الله هدایت کند، پس او را هیچ گمراه‌کننده‌ای نیست. آیا الله پیروزمند انتقام‌گیر نیست؟!');
INSERT INTO "hussein_taji" VALUES(39,38,'39:38','و اگر از آن‌ها (= مشرکان) بپرسی: «چه کسی آسمان‌ها و زمین را آفریده است؟» مسلماً گویند: «الله» بگو: «آیا دیدید آنچه را که به جای الله می‌خوانید، اگر الله زیانی برای من بخواهد، آیا آن‌ها (= معبودان) خواهند توانست زیان او را بر طرف کنند؟! و یا اگر رحمتی برای من بخواهد، آیا آن‌ها خواهند توانست رحمت او را باز دارند؟!». بگو: «الله مرا کافی است، و (همه) توکل کنندگان بر او توکل می‌کنند».');
INSERT INTO "hussein_taji" VALUES(39,39,'39:39','بگو: «ای قوم من! شما بر روش خود عمل کنید، من (نیز به روش خود) عمل می‌کنم، پس به زودی خواهید دانست،');
INSERT INTO "hussein_taji" VALUES(39,40,'39:40','چه کسی عذاب خوارکننده‌ای به سراغش می‌آید، و عذابی دایم بر او فرود می‌آید».');
INSERT INTO "hussein_taji" VALUES(39,41,'39:41','بی‌گمان ما (این) کتاب (آسمانی) را برای مردم به حق بر تو نازل کردیم، پس هر کس که هدایت شود به سود خود اوست، و هر کس‌که گمراه شود، تنها به زیان خودش خواهد بود، و تو بر آن‌ها نگهبان نیستی (که آن‌ها را به هدایت اجبار کنی).');
INSERT INTO "hussein_taji" VALUES(39,42,'39:42','الله جان‌ها را به هنگام مرگ‌شان قبض می‌کند، و (نیز جان) آن‌ها را که درخواب خود نمرده‌اند، (قبض می‌کند) آنگاه (جان) کسانی‌که فرمان مرگ‌شان را صادر کرده نگه می‌داد، و (جان) دیگری را تا مدتی معین باز پس می‌فرستد، بی‌گمان در این (امر) نشانه‌های (روشنی) است برای گروهی که اندیشه می‌کنند.');
INSERT INTO "hussein_taji" VALUES(39,43,'39:43','آیا غیر از الله شفیعانی گرفته‌اند؟! (به آن‌ها) بگو: اگر چه مالک چیزی نباشند، و (چیزی را) درک نکنند (باز هم از آن‌ها شفاعت می‌طلبید).');
INSERT INTO "hussein_taji" VALUES(39,44,'39:44','بگو: «تمام شفاعت از آن الله است، فرمانروایی آسمان‌ها و زمین از آن اوست، سپس (همگی) به سوی او باز گردانده می‌شوید».');
INSERT INTO "hussein_taji" VALUES(39,45,'39:45','و هنگامی‌که الله به تنهایی یاد شود دل‌های کسانی‌که به آخرت ایمان ندارند متنفر (و بیزار) می‌شود، و هنگامی‌که کسانی غیر از او یاد شوند، آنگاه است که آن‌ها شادمان می‌شوند.');
INSERT INTO "hussein_taji" VALUES(39,46,'39:46','بگو: پروردگارا! (ای) آفرینندۀ آسمان‌ها و زمین دانای ‌نهان و آشکار، تو در میان بندگانت در آنچه اختلاف می‌کردند، داوری خواهی کرد.');
INSERT INTO "hussein_taji" VALUES(39,47,'39:47','و اگر تمام آنچه که در روی زمین است و همانند آن همراهش از آن کسانی باشد که ستم کردند [ (ظلم): ستم، در اینجا بمعنای شرک و کفر است، چنانکه در سورۀ لقمان آیه می‌فرماید ]، یقیناً همه را بخاطر سختی عذاب روز قیامت (برای رهایی خود از عذاب) فدا کنند، و برای آن‌ها از سوی الله چیزهایی آشکار می‌شود که هرگز گمان نمی‌کردند.');
INSERT INTO "hussein_taji" VALUES(39,48,'39:48','و بدی‌های آنچه انجام داده‌اند برای آن‌ها آشکار می‌شود و آنچه به آن مسخره می‌کردند، آن‌ها را فرو می‌گیرد.');
INSERT INTO "hussein_taji" VALUES(39,49,'39:49','پس چون به انسان (رنج و) زیانی برسد ما را می‌خواند، آنگاه چون از جانب خود به او نعمتی عطا کنیم، می‌گوید: «به سبب دانایی (و لیاقت) خودم به من داده شده» بلکه این آزمایشی است، و لیکن بیشتر‌شان نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(39,50,'39:50','به راستی این سخن را کسانی‌که پیش از آن‌ها بودند (نیز) گفتند، پس آنچه را بدست آوردند، برای آن‌ها سودی نبخشید (و عذاب را دفع نکرد).');
INSERT INTO "hussein_taji" VALUES(39,51,'39:51','پس بدی‌های آنچه می‌کردند به آن‌ها رسید، و کسانی از آن‌ها که ستم کردند (نیز) بدی‌های آنچه می‌کردند به زودی به آن‌ها خواهد رسید، و آن‌ها (الله را) عاجزکننده نیستند (و راه گریزی ندارند).');
INSERT INTO "hussein_taji" VALUES(39,52,'39:52','آیا آن‌ها ندانستند که الله روزی را برای هر کس که بخواهد گسترده یا تنگ می‌سازد، بی‌گمان در این، نشانه‌هایی است برای گروهی که ایمان می‌آورند.');
INSERT INTO "hussein_taji" VALUES(39,53,'39:53','(ای پیامبر، از جانب من) بگو: «ای بندگان من که بر خود اسراف (و ستم) کرده‌اید! از رحمت الله نا امید نشوید، همانا الله همۀ گناهان را می‌بخشد، یقیناً او بسیار آمرزندۀ مهربان است [ همۀ گناهان با توبه بخشیده می‌شود، و گناه هر چند بزرگ باشد، نباید از رحمت الله نا امید شد. (تفسیر ابن کثیر) و به سورۀ فرقان آیه نیز رجوع فرمائید.].');
INSERT INTO "hussein_taji" VALUES(39,54,'39:54','و به سوی پروردگار‌تان باز گردید و در برابر او تسلیم شوید، پیش از آنکه عذاب به سراغ شما آید، آنگاه (از سوی هیچ کس) یاری نشوید.');
INSERT INTO "hussein_taji" VALUES(39,55,'39:55','و از نیکو‌ترین چیزی که از جانب پروردگار‌تان بر شما نازل شده پیروی کنید، پیش از آنکه ناگهان عذاب به سراغ شما آید، در حالی‌که شما از آن بی‌خبرید.');
INSERT INTO "hussein_taji" VALUES(39,56,'39:56','تا (مبادا) کسی (در روز قیامت) بگوید: «ای افسوس بر من، که در حق الله کوتاهی کردم، و بی‌گمان از مسخره‌کنندگان (آیین او) بودم».');
INSERT INTO "hussein_taji" VALUES(39,57,'39:57','یا بگوید: «اگر الله مرا هدایت می‌کرد، یقیناً از پرهیزگاران بودم»');
INSERT INTO "hussein_taji" VALUES(39,58,'39:58','یا هنگامی‌که عذاب را می‌بیند، بگوید: «ای کاش بار دیگر (به دنیا) باز می‌گشتم تا از نیکوکاران می‌شدم».');
INSERT INTO "hussein_taji" VALUES(39,59,'39:59','(الله می‌فرماید:) آری، آیات من برایت آمد، و تو آن را تکذیب نمودی، و تکبّر ورزیدی، و از کافران بودی.');
INSERT INTO "hussein_taji" VALUES(39,60,'39:60','و روز قیامت کسانی را که بر الله دروغ بسته‌اند، می‌بینی که چهره‌هایشان سیاه شده است، آیا در جهنم جایگاهی برای متکبران نیست؟!');
INSERT INTO "hussein_taji" VALUES(39,61,'39:61','و الله کسانی را که پرهیزگاری کردند با رستگاری‌شان نجات می‌دهد، نه رنجی به آن‌ها رسد، و نه آن‌ها اندوهگین شوند.');
INSERT INTO "hussein_taji" VALUES(39,62,'39:62','الله آفریدگار همه چیز است، و او بر همه چیز نگهبان است.');
INSERT INTO "hussein_taji" VALUES(39,63,'39:63','کلیدهای آسمان‌ها و زمین از آنِ اوست، و کسانی‌که به آیات الله کافر شدند، اینان زیانکارانند.');
INSERT INTO "hussein_taji" VALUES(39,64,'39:64','بگو: «ای نادانان آیا به من فرمان می‌دهید که غیر الله را پرستش کنم؟»');
INSERT INTO "hussein_taji" VALUES(39,65,'39:65','و به راستی که به تو و به کسانی‌که پیش از تو بودند، وحی شد که اگر شرک آوری، یقیناً اعمالت (تباه و) نابود می‌شود، و از زیان‌کاران خواهی بود.');
INSERT INTO "hussein_taji" VALUES(39,66,'39:66','بلکه تنها الله را پرستش کن، و از شکر گزاران باش.');
INSERT INTO "hussein_taji" VALUES(39,67,'39:67','و آن‌ها (= مشرکان) الله را چنانکه سزاوار بزرگی اوست نشناختند در حالی‌که روز قیامت تمام زمین در مشت اوست و آسمان‌ها درهم پیچیده در دست راست اوست، او منزه و برتر است از آنچه شریک او می‌پندارند.');
INSERT INTO "hussein_taji" VALUES(39,68,'39:68','و در «صور» دمیده شود [ این دمیدن دوم است، که با آن تمام کسانی‌که در زمین و آسمان هستند، - بجز کسانی‌که الله بخواهد- می‌میرند، چنانکه در حدیث «صور» مشهور آمده است. (تفسیر ابن کثیر)]، پس هر که در آسمان‌ها و هر که در زمین است) جز آن‌ها که الله بخواهد) (بی‌هوش شده و) بمیرند. سپس بار دیگر در آن دمیده می‌شود، ناگهان آن‌ها به پا خیزند (و) می‌نگرند.');
INSERT INTO "hussein_taji" VALUES(39,69,'39:69','و زمین به نور پروردگارش روشن می‌شود، و نامۀ (اعمال) نهاده شود، و پیامبران و گواهان را بیاورند، و در میان آن‌ها به حق داوری شود، و به آن‌ها ستم نخواهند شد.');
INSERT INTO "hussein_taji" VALUES(39,70,'39:70','و به هر کس (پاداش) آنچه کرده است به تمام داده شود، و او به آنچه انجام می‌دهند دانا‌تر است.');
INSERT INTO "hussein_taji" VALUES(39,71,'39:71','و کسانی‌که کافر شدند گروه گروه به سوی جهنم رانده می‌شوند، تا وقتی به (کنار) آن (= دوزخ) رسند در‌هایش گشوده شود، و نگهبانانش به آنان گویند: «آیا پیامبرانی از میان خود‌تان به سوی شما نیامدند که آیات پروردگارتان را برای شما بخوانند، و شما را از دیدار امروز‌تان هشدار دهند؟!» گویند: «آری، و لیکن فرمان عذاب (الهی) بر کافران محقق شده است».');
INSERT INTO "hussein_taji" VALUES(39,72,'39:72','گفته شود: «از در‌های جهنم وارد شوید، جاودانه در آن بمانید، پس چه بد است، جایگاه متکبران».');
INSERT INTO "hussein_taji" VALUES(39,73,'39:73','و کسانی‌که از پروردگار‌شان ترسیده‌اند، گروه گروه به بهشت برده می‌شوند، تا وقتی به (کنار) آن رسند، و در‌هایش گشوده شود، و نگهبانانش به آنان گویند: «سلام بر شما پاکیزه بوده‌اید (خوش باشید) پس جاودانه به آن (= بهشت) وارد شوید».');
INSERT INTO "hussein_taji" VALUES(39,74,'39:74','و (بهشتی ها) گویند: «حمد و ستایش مخصوص الله است که وعده‌اش در حق ما راست بود، و سرزمین (بهشت) را به ارث به ما داد، که از بهشت هر جا که بخواهیم منزل می‌گیریم، پس چه نیکو است پاداش عمل کنندگان.»');
INSERT INTO "hussein_taji" VALUES(39,75,'39:75','و فرشتگان را می‌بینی که بر گرد عرش حلقه زده‌اند به ستایش پروردگار‌شان تسبیح می‌گویند، و در میان مردم به حق داوری می‌شود، و گفته شود: «حمد و ستایش مخصوص الله پروردگار جهانیان است».');
INSERT INTO "hussein_taji" VALUES(40,1,'40:1','حم (حا. میم)');
INSERT INTO "hussein_taji" VALUES(40,2,'40:2','نازل شدن (این) کتاب از سوی الله پیروزمند داناست.');
INSERT INTO "hussein_taji" VALUES(40,3,'40:3','آمرزندۀ گناه، و پذیرندۀ توبه، سخت عقوبت، صاحب نعمت است، هیچ معبودی (به حق) جز او نیست، بازگشت به سوی اوست.');
INSERT INTO "hussein_taji" VALUES(40,4,'40:4','در آیات الله جز کسانی‌که کافر شدند، جدال نمی‌کنند، پس رفت و آمد آنان در شهر‌ها تو را نفریبد.');
INSERT INTO "hussein_taji" VALUES(40,5,'40:5','پیش از آن‌ها قوم نوح و گروه‌های (دیگر) که پس از آنان (بودند، پیامبران‌شان را) تکذیب کردند، و هر امتی ارادۀ آن کرد که پیامبرش را دستگیر، و به باطل جدال کردند، تا با آن حق را از میان بردارند، پس من آن‌ها را (فرو) گرفتم پس (بنگر) عقوبت من چگونه بود؟!');
INSERT INTO "hussein_taji" VALUES(40,6,'40:6','و این‌گونه فرمان پروردگارت بر کسانی‌که کافر شدند محقق شد، که آن‌ها اهل آتشند.');
INSERT INTO "hussein_taji" VALUES(40,7,'40:7','کسانی‌که عرش را حمل می‌کنند، و آنان که بر گرد آن هستند به ستایش پروردگار‌شان تسبیح می‌گویند، و به او ایمان دارند و برای کسانی‌که ایمان آورده‌اند استغفار می‌کنند. (می‌گویند:) «پروردگارا! رحمت و علم تو همه چیز را فرا گرفته است، پس برای کسانی‌که توبه کرده‌اند، و راه تو را پیروی کرده‌اند، بیامرز، و آنان را از عذاب دوزخ نگه دار».');
INSERT INTO "hussein_taji" VALUES(40,8,'40:8','پروردگارا! و آن‌ها را به باغ‌های جاویدان (بهشت) که به آن‌ها وعده داده‌ای وارد کن، و (همچنین) هر که صالح باشد از پدران‌شان، و همسران‌شان و فرزندان‌شان (به همان بهشت وارد کن) همانا تویی که پیروزمند حکیمی.');
INSERT INTO "hussein_taji" VALUES(40,9,'40:9','و آن‌ها را از بدی‌ها نگاهدار، و هر کس را که در آن روز از (کیفر) بدی‌ها نگاهداری مسلماً به او رحم کرده‌ای، و این همان کامیابی بزرگ است.');
INSERT INTO "hussein_taji" VALUES(40,10,'40:10','بی‌گمان کسانی‌که کافر شدند (روز قیامت) ندا می‌زنند که: حقا که خشم (و دشمنی) الله (نسبت به شما) از خشم (و دشمنی) خودتان (نسبت به خودتان) بیشتر است، آنگاه که به (سوی) ایمان دعوت می‌شدید، پس شما انکار می‌کردید.');
INSERT INTO "hussein_taji" VALUES(40,11,'40:11','گویند: «پروردگارا! ما را دوبار میراندی و دو بار زنده کردی [ این آیه همانند آیه سورۀ بقره است (تفسیر ابن کثیر).]، پس (اکنون) به گناهان خود اعتراف کردیم، آیا راهی برای خروج (از جهنم) وجود دارد؟»');
INSERT INTO "hussein_taji" VALUES(40,12,'40:12','(به آن‌ها گفته می‌شود:) این (عذاب) بدان سبب است که چون الله به تنهایی خوانده می‌شد، انکار می‌کردید، و اگر به او شرک آورده می‌شد ایمان می‌آوردید، پس (اینک) داوری از آن الله بلند مرتبه است.');
INSERT INTO "hussein_taji" VALUES(40,13,'40:13','او کسی است که آیات خود را به شما نشان می‌دهد و از آسمان برای شما روزی می‌فرستد، تنها کسی پند می‌گیرد که (به سوی الله) رجوع می‌کند.');
INSERT INTO "hussein_taji" VALUES(40,14,'40:14','پس الله را در حالی‌که دین خود را برای او خالص گردانیدید بخوانید، و اگر چه کافران خوش نداشته باشند.');
INSERT INTO "hussein_taji" VALUES(40,15,'40:15','(او) بالا برنده درجات [ یا او صاحب درجات بلند است.] صاحب عرش، که روح (= وحی) را به فرمان خویش بر هر کس از بندگانش که بخواهد می‌فرستد، تا (او مردم را) از روز ملاقات (= قیامت) بترساند.');
INSERT INTO "hussein_taji" VALUES(40,16,'40:16','روزی‌که (همه) آنان آشکار شوند چیزی از آن‌ها بر الله پوشیده نخواهد ماند، (الله می‌فرماید:) «امروز فرمانروایی از آنِ کیست؟» (آنگاه خود می‌فرماید:) از آن الله یگانه قهّار است. [در حدیث «صور» آمده: «الله ارواح همۀ خلایق را قبض می‌کند، و جز ذات اقدسش کسی باقی نمی‌ماند، آنگاه می‌فرماید: «امروز پادشاهی از آن کیست؟» سه بار تکرار می‌فرماید، آنگاه خود پاسخ می‌دهد: «لِلَّهِ الْوَاحِدِ الْقَهَّارِ»». (تفسیر ابن کثیر)]');
INSERT INTO "hussein_taji" VALUES(40,17,'40:17','امروز هر کس به (حسب) آنچه کرده است، پاداش داده می‌شود، امروز هیچ ستمی نیست، بی‌گمان الله زود شمار است.');
INSERT INTO "hussein_taji" VALUES(40,18,'40:18','و آن‌ها را از روز نزدیک (= قیامت) بترسان، آنگاه که دل‌ها لبریز از غم به حنجره‌ها رسد، برای ستمکاران نه دوستی وجود دارد، و نه شفیعی که (شفاعتش) پذیرفته شود.');
INSERT INTO "hussein_taji" VALUES(40,19,'40:19','(الله) خیانت چشم‌ها و آنچه را که سینه‌ها پنهان می‌دارند، می‌داند.');
INSERT INTO "hussein_taji" VALUES(40,20,'40:20','و الله به حق داوری می‌کند، و کسانی را که بجای او می‌خوانند، به هیچ چیزی داوری نمی‌کنند، بی‌گمان الله همان شنوای بیناست.');
INSERT INTO "hussein_taji" VALUES(40,21,'40:21','آیا آن‌ها در زمین سیر نکرده‌اند پس بنگرند که سرانجام کسانی‌که پیش از آن‌ها بودند، چگونه بود؟! آن‌ها در توانایی و (پدید آوردن) آثار در زمین از این‌ها برتر (و سخت‌تر) بودند، پس الله آن‌ها را به (سبب) گناهان‌شان (فرو) گرفت، و برای آن‌ها از (عذاب) الله هیچ پناه دهنده‌ای نبود.');
INSERT INTO "hussein_taji" VALUES(40,22,'40:22','این بدان سبب بود که پیامبران‌شان با دلایل روشن به سوی آن‌ها می‌آمدند پس آن‌ها کفر می‌ورزیدند، بنابر این الله آن‌ها را (فرو) گرفت، بی‌گمان او نیرومند سخت کیفر است.');
INSERT INTO "hussein_taji" VALUES(40,23,'40:23','به راستی ما موسی را با آیات خود و حجتی آشکار فرستادیم.');
INSERT INTO "hussein_taji" VALUES(40,24,'40:24','به سوی فرعون و هامان و قارون، پس (آن‌ها) گفتند: «(او) جادوگری دروغگوست».');
INSERT INTO "hussein_taji" VALUES(40,25,'40:25','پس چون حق را از جانب ما برای آن‌ها آورد، گفتند: «پسران کسانی را که با او ایمان آورده‌اند، بکشید و زنان‌شان را (برای خدمت‌کاری) زنده بگذارید». و نیرنگ کافران جز در گمراهی نیست.');
INSERT INTO "hussein_taji" VALUES(40,26,'40:26','و فرعون گفت: «بگذارید موسی را بکشم، و او پروردگارش را (برای نجات) بخواند، بی‌گمان من می‌ترسم که دین شما را دگرگون کند یا در (این) سرزمین فساد انگیزد».');
INSERT INTO "hussein_taji" VALUES(40,27,'40:27','و موسی گفت: «من از (شر) هر متکبری که به روز حساب ایمان نمی‌آورد، به پروردگارم و پروردگار شما پناه می‌برم».');
INSERT INTO "hussein_taji" VALUES(40,28,'40:28','و مرد مؤمنی از آل فرعون که ایمانش را پنهان می‌داشت، گفت: آیا مردی را می‌کشید (برای آن) که می‌گوید: پروردگار من الله است، و همانا دلایل روشنی از سوی پروردگار‌تان برای شما آورده است؟! و اگر او درغگو باشد، (گناه) دروغش بر گردن اوست، و اگر راست‌گو باشد پاره‌ای از آنچه به شما وعده می‌دهد به شما خواهد رسید، بی‌گمان الله کسی را که اسراف‌کار دروغگو است، هدایت نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(40,29,'40:29','«ای قوم من! امروز فرمانروایی از آن شماست، در این سرزمین پیروزید پس چه کسی ما را از عذاب الله یاری می‌دهد، اگر (عذاب) بیاید». فرعون گفت: «من شما را جز به راهی که (مصلحت) می‌بینم، (سفارش نمی‌کنم) و شما را جز به راه صحیح (و رشد) هدایت نمی‌کنم».');
INSERT INTO "hussein_taji" VALUES(40,30,'40:30','و کسی‌که ایمان آورده بود، گفت: «ای قوم من! به راستی من بر شما (از روزی) همانند روز گروه‌های (پیشین) می‌ترسم».');
INSERT INTO "hussein_taji" VALUES(40,31,'40:31','مانند (حال و) شیوۀ قوم نوح و عاد و ثمود، و کسانی‌که بعد از آن‌ها بودند، و الله برای بندگانش خواستار ستم نیست.');
INSERT INTO "hussein_taji" VALUES(40,32,'40:32','وای قوم من! بی‌گمان من بر شما از روزی‌که (مردم) یکدیگر را صدا می‌زنند، (روز قیامت) می‌ترسم،');
INSERT INTO "hussein_taji" VALUES(40,33,'40:33','(همان) روزی‌که پشت کنان روی می‌گردانید، و برای شما هیچ نگه دارنده‌ای از (عذاب) الله نیست، و هر کس را که الله گمراه کند، پس هدایت کننده‌ای برای او نیست.');
INSERT INTO "hussein_taji" VALUES(40,34,'40:34','و به راستی پیش از این یوسف با دلایل روشن به (نزد) شما آمده بود، پس شما از آنچه او برای شما آورده بود پیوسته در شک بودید، تا زمانی‌که او مرد گفتید: «هرگز الله بعد از او پیامبری را مبعوث نخواهد کرد». این‌گونه الله کسی را که اسراف‌کار شکاک است گمراه می‌سازد.');
INSERT INTO "hussein_taji" VALUES(40,35,'40:35','(همان) کسانی‌که بی آنکه حجتی برایشان آمده باشد، در آیات الله مجادله می‌کنند (این عمل) نزد الله و نزد کسانی‌که ایمان آورده‌اند سخت ناپسند (و موجب خشم) است. این گونه الله بر دل هر متکبر سرکشی مهر می‌نهد».');
INSERT INTO "hussein_taji" VALUES(40,36,'40:36','و فرعون گفت: «ای هامان! برای من کوشک بلندی بساز، باشد که به آن درها (و راه‌ها) دست یابم.');
INSERT INTO "hussein_taji" VALUES(40,37,'40:37','(راه‌ها و) در‌های آسمان‌ها، تا به معبود موسی بنگرم (و از او آگاه شوم)، و البته من او را دروغگو می‌پندارم». و این‌گونه برای فرعون زشتی کردارش (در نظرش) آراسته شد، و از راه (حق) باز داشته شد، وحیله (و نیرنگ) فرعون جز در تباهی نبود.');
INSERT INTO "hussein_taji" VALUES(40,38,'40:38','و کسی‌که ایمان آورده بود، گفت: «ای قوم من! از من پیروی کنید، تا شما را به راه درست (و صواب) هدایت کنم».');
INSERT INTO "hussein_taji" VALUES(40,39,'40:39','ای قوم من! این زندگی دنیا تنها متاع (زود گذر) است و همانا آخرت سرای همیشگی است،');
INSERT INTO "hussein_taji" VALUES(40,40,'40:40','هر کس کار بدی مرتکب شود، پس جز به همانند آن کیفر نیابد، و هر کس از مرد یا زن در حالی‌که مؤمن است، کار شایسته‌ای انجام دهد، پس آن‌ها وارد بهشت می‌شوند، و در آن روزی بی‌شمار به آن‌ها داده خواهد شد.');
INSERT INTO "hussein_taji" VALUES(40,41,'40:41','و ای قوم من! چیست مرا که من شما را به سوی نجات می‌خوانم، و (شما) مرا به سوی آتش می‌خوانید؟!');
INSERT INTO "hussein_taji" VALUES(40,42,'40:42','مرا دعوت می‌کنید که به الله کافر شوم و چیزی را که به آن علم ندارم شریک او قرار دهم، و من شما را به سوی (الله) پیروزمند آمرزگار دعوت می‌کنم.');
INSERT INTO "hussein_taji" VALUES(40,43,'40:43','قطعاً آنچه مرا به سوی آن دعوت می‌دهید نه (حق) دعوت در دنیا دارد و نه در آخرت، و همانا بازگشت ما به سوی الله است و بی‌گمان اسراف‌کارانند که اهل آتشند.');
INSERT INTO "hussein_taji" VALUES(40,44,'40:44','پس به زودی آنچه را به شما می‌گویم به یاد خواهید آورد، من کار خودم را به الله و اگذار می‌کنم، بی‌گمان الله (نسبت) به بندگان بیناست».');
INSERT INTO "hussein_taji" VALUES(40,45,'40:45','پس الله او را از آسیب آنچه که (برایش) مکر کرده بودند، حفظ کرد، و بد‌ترین عذاب آل فرعون را در میان گرفت.');
INSERT INTO "hussein_taji" VALUES(40,46,'40:46','(آن عذاب) آتش (دوزخ است) که هر صبح و شام بر آن عرضه می‌شوند. و روزی‌که قیامت بر پا می‌شود (گفته می‌شود:) «آل فرعون را در سخترین عذاب وارد کنید».');
INSERT INTO "hussein_taji" VALUES(40,47,'40:47','و چون در آتش (جهنم) با همدیگر مجادله (و کشمکش) می‌کنند، پس ناتوانان به کسانی‌که تکبر ورزیدند، می‌گویند: «بی‌شک ما (در دنیا) پیرو شما بودیم، پس آیا شما (امروز) می‌توانید بخشی از آتش را که نصیب ما شده از ما دور کنید؟!»');
INSERT INTO "hussein_taji" VALUES(40,48,'40:48','کسانی‌که تکبر ورزیدند، گویند: «بی‌گمان ما همگی در آن (آتش) هستیم، همانا الله در میان بندگان داوری کرده است».');
INSERT INTO "hussein_taji" VALUES(40,49,'40:49','و کسانی‌که در آتشند به نگهبانان جهنم می‌گویند: «از پروردگار‌تان بخواهید تا یک روز عذاب را از ما بکاهد».');
INSERT INTO "hussein_taji" VALUES(40,50,'40:50','(آن‌ها) می‌گویند: «آیا پیامبران شما با دلایل روشن نزد شما نیامدند؟!» گویند: «آری» (نگهبانان) گویند: «پس (خودتان) دعا کنید، و دعای کافران جز در تباهی نیست».');
INSERT INTO "hussein_taji" VALUES(40,51,'40:51','یقیناً ما پیامبران خود و کسانی را که ایمان آورده‌اند، در زندگی دنیا و روزی‌که گواهان (برای گواهی) بر خیزند، یاری می‌کنیم.');
INSERT INTO "hussein_taji" VALUES(40,52,'40:52','روزی‌که عذر خواهی ستمکاران سودی ندهد، و لعنت (الله) برای آنان است، و (نیز) سرای بد (= جهنم) برای آن‌هاست.');
INSERT INTO "hussein_taji" VALUES(40,53,'40:53','و به راستی ما به موسی هدایت عطا کردیم، و بنی اسرائیل را وارثان کتاب (تورات) قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(40,54,'40:54','(کتابی‌که) برای خردمندان هدایت و پند است.');
INSERT INTO "hussein_taji" VALUES(40,55,'40:55','پس (ای پیامبر) صبر کن، بی‌گمان وعدۀ الله حق است، و برای گناهت آمرزش بخواه و هر صبح و شام به ستایش پروردگارت تسبیح گوی.');
INSERT INTO "hussein_taji" VALUES(40,56,'40:56','همانا کسانی‌که در آیات الله بدون هیچ حجت (و دلیلی) که برای آن‌ها آمده باشد، مجادله می‌کنند، در سینه‌هایشان جز تکبر نیست، که خود به آن نخواهند رسید، پس به الله پناه ببر، بی‌گمان اوست که شنوای بیناست.');
INSERT INTO "hussein_taji" VALUES(40,57,'40:57','مسلماً آفرینش آسمان‌ها و زمین بزرگتر از آفرینش مردم است، و لیکن بیشتر مردم نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(40,58,'40:58','و هرگز نابینا و بینا یکسان نیستند و (همچنین) کسانی‌که ایمان آورده، و کارهای شایسته انجام داده‌اند با (افراد) بدکار (یکسان نباشند) چه اندک پند می‌پذیرید.');
INSERT INTO "hussein_taji" VALUES(40,59,'40:59','یقیناً قیامت آمدنی است، شکی در آن نیست و لیکن بیشتر مردم ایمان نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(40,60,'40:60','و پروردگار شما فرمود: «مرا بخوانید، تا (دعای) شما را اجابت کنم همانا کسانی‌که از عبادت من سرکشی می‌کنند، به زودی با خواری به جهنم وارد می‌شوند».');
INSERT INTO "hussein_taji" VALUES(40,61,'40:61','الله کسی است که شب را برای شما قرار داد تا در آن آرام گیرید، و روز را روشن (قرار داد)، بی‌گمان الله بر مردم فضل (و کرم) دارد، و لیکن بیشتر مردم سپاس نمی‌گزارند.');
INSERT INTO "hussein_taji" VALUES(40,62,'40:62','این است الله، پروردگار شما، (که) آفرینندۀ همه چیز (است) هیچ معبودی (به حق) جز او نیست، پس چگونه (از حق) منحرف می‌شوید؟!');
INSERT INTO "hussein_taji" VALUES(40,63,'40:63','این گونه کسانی‌که آیات الله را انکار می‌کردند، (از حق) منحرف می‌شوند.');
INSERT INTO "hussein_taji" VALUES(40,64,'40:64','الله کسی است که زمین را برای شما قرارگاه، و آسمان را (همچون) سقفی قرار داد، و شما را صورت بخشید، پس صورت‌تان را نیکو گرداند، و از چیزهای پاکیزه به شما روزی داد این است الله پروردگار شما، پس پر برکت و بزرگوار است الله که پروردگار جهانیان است.');
INSERT INTO "hussein_taji" VALUES(40,65,'40:65','او زنده است، هیچ معبودی (به حق) جز او نیست، پس در حالی‌که دین خود را خالص گردانیده او را بخوانید، حمد و ستایش مخصوص الله پروردگار جهانیان است.');
INSERT INTO "hussein_taji" VALUES(40,66,'40:66','(ای پیامبر) بگو: «همانا من نهی شده‌ام از اینکه کسانی را پرستش کنم که شما بجای الله می‌خوانید، چون نشانه‌های روشن از جانب پروردگارم برای من آمده است، و مأمور شده‌ام که برای پروردگار جهانیان تسلیم باشم».');
INSERT INTO "hussein_taji" VALUES(40,67,'40:67','او کسی است که شما را از خاک آفرید، سپس از نطفه، سپس از خون بسته، سپس شما را (به صورت) کودکی (از شکم مادر) بیرون می‌آورد، سپس (بزرگ می‌شود) تا به کمال نیرو و قوت خود می‌رسید، سپس (زندگی می‌کنید) تا بعد از آن پیر می‌شوید، و از (میان) شما کسی است که پیش از این (مرحله) می‌میرد و (به بعضی از شما مهلت می‌دهد) تا به اجل معین برسید، و شاید بیندیشید.');
INSERT INTO "hussein_taji" VALUES(40,68,'40:68','او کسی است که زنده می‌کند و می‌میراند، پس هنگامی‌که چیزی را بخواهد، تنها به او می‌گوید: «موجود شو» پس (بی درنگ) موجود می‌شود.');
INSERT INTO "hussein_taji" VALUES(40,69,'40:69','آیا ندیدی کسانی را که در آیات الله مجادله می‌کنند، چگونه (از راه حق) منحرف می‌شوند؟!');
INSERT INTO "hussein_taji" VALUES(40,70,'40:70','کسانی‌که کتاب (آسمانی) و آنچه پیامبران‌مان را بدان فرستاده‌ایم، تکذیب کردند، پس به زودی خواهند دانست.');
INSERT INTO "hussein_taji" VALUES(40,71,'40:71','آنگاه که غل‌ها و زنجیر‌ها در گردن‌شان خواهد بود، و کشیده می‌شوند.');
INSERT INTO "hussein_taji" VALUES(40,72,'40:72','در آب جوش، سپس در آتش (جهنم) افروخته می‌شوند.');
INSERT INTO "hussein_taji" VALUES(40,73,'40:73','آنگاه به آن‌ها گفته می‌شود: آنچه را که شریک (الله) قرار مى‏دادید، کجایند؟');
INSERT INTO "hussein_taji" VALUES(40,74,'40:74','(همان معبودانی که) جز الله (عبادت می‌کردید؛) گویند: «از (نظر) ما ناپدید شدند، بلکه ما هرگز پیش از این چیزی را نمی‌خواندیم». این‌گونه الله کافران را گمراه می‌کند.');
INSERT INTO "hussein_taji" VALUES(40,75,'40:75','این بدان (سبب) است که به ناحق در زمین شادمان می‌شدید، و به آنکه می‌نازیدید.');
INSERT INTO "hussein_taji" VALUES(40,76,'40:76','از در‌های جهنم وارد شوید، جاودانه در آن بمانید، پس جایگاه متکبران چه بد است.');
INSERT INTO "hussein_taji" VALUES(40,77,'40:77','پس (ای پیامبر) صبر کن، بی‌گمان وعدۀ الله حق است، پس اگر قسمتی از چیز‌هایی را که به آن‌ها وعده داده‌ایم به تو نشان دهیم، یا تو را بمیرانیم، پس (در هر صورت) به سوی ما باز گردانده می‌شوند (و آنجا کیفر می‌شوند).');
INSERT INTO "hussein_taji" VALUES(40,78,'40:78','و به راستی پیش از تو پیامبرانی فرستادیم، (داستان) بعضی از آن‌ها را برای تو بازگو کردیم و (داستان) بعضی را برای تو بازگو نکرده‌ایم، و هیچ پیامبری را اجازه نیست که معجزه‌ای جز به فرمان الله بیاورد، پس چون فرمان الله در رسد، به حق داوری شود، و آنجا بیهوده‌گویان (اهل باطل) زیان خواهند کرد.');
INSERT INTO "hussein_taji" VALUES(40,79,'40:79','الله کسی است که چهار پایان را برای شما آفرید، تا بر بعضی از آن‌ها سوار شوید، و از بعضی بخورید.');
INSERT INTO "hussein_taji" VALUES(40,80,'40:80','و برای شما در آن‌ها منافعی است، و تا (با سوار شدن) بر آن‌ها به مقصدی که در دل دارید برسید، و بر آن‌ها و (نیز) بر کشتی (ها) سوار می‌شوید.');
INSERT INTO "hussein_taji" VALUES(40,81,'40:81','و (الله) آیاتش را به شما نشان می‌دهد، پس کدام‌یک از آیات الله را انکار می‌کنید.');
INSERT INTO "hussein_taji" VALUES(40,82,'40:82','آیا در (روی) زمین سیر نکردند، پس ببینند سرانجام کسانی‌که پیش از آن‌ها بودند، چگونه بود؟ آن‌ها (از لحاظ) تعداد افراد از این‌ها بیشتر بودند، و در توانایی (و پدید آوردن) آثار در زمین از این‌ها برتر (و سختر) بودند، پس آنچه بدست می‌آوردند نتوانست آن‌ها را بی‌نیاز کند (و عذاب‌شان را دور نماید).');
INSERT INTO "hussein_taji" VALUES(40,83,'40:83','پس چون پیامبران‌شان با دلایل روشن به سوی آن‌ها آمدند، به دانشی که نزد خود داشتند خوشحال شدند، و آنچه را که مسخره‌اش می‌گرفتند آن‌ها را فرا گرفت.');
INSERT INTO "hussein_taji" VALUES(40,84,'40:84','پس چون عذاب ما را دیدند گفتند: «تنها به الله ایمان آوردیم، و به معبودهایی که شریک او قرار داده بودیم، کافر شدیم».');
INSERT INTO "hussein_taji" VALUES(40,85,'40:85','پس هنگامی‌که عذاب ما را دیدند، ایمان‌شان برای آن‌ها سودی نبخشید، (چنین) است سنت الله که در میان بندگانش گذشته است، و آنجا کافران زیان‌کار شدند.');
INSERT INTO "hussein_taji" VALUES(41,1,'41:1','حم (حا. میم)');
INSERT INTO "hussein_taji" VALUES(41,2,'41:2','نازل شدن (این کتاب) از سوی (الله) بخشندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(41,3,'41:3','کتابی است که آیاتش به روشنی بیان شده است، در حالی‌که (بزبان) عربی است، برای گروهی‌که می‌دانند.');
INSERT INTO "hussein_taji" VALUES(41,4,'41:4','بشارت‌دهنده، و هشدار دهنده است، پس بیشتر آن‌ها روی‌گردان شدند، پس آن‌ها نمی‌شنوند.');
INSERT INTO "hussein_taji" VALUES(41,5,'41:5','و گفتند: «دل‌های ما از آنچه ما را به آن دعوت می‌کنی در پوشش‌های است، و در گوش‌هایمان سنگینی است، و میان ما و تو حجابی است، پس تو (به شیوۀ خود) عمل کن، ما (نیز به شیوۀ خود) عمل می‌کنیم».');
INSERT INTO "hussein_taji" VALUES(41,6,'41:6','بگو: «من فقط انسانی مانند شما هستم، به من وحی می‌شود که معبود شما معبود یگانه است، پس به او روی آورید و از او طلب آمرزش کنید، و وای بر مشرکان»!');
INSERT INTO "hussein_taji" VALUES(41,7,'41:7','(همان) کسانی‌که زکات نمی‌دهند، و آن‌ها که به آخرت ایمان ندارد.');
INSERT INTO "hussein_taji" VALUES(41,8,'41:8','بی‌گمان کسانی‌که ایمان آوردند و کار‌های شایسته انجام دادند، برای آن‌ها پاداش بی‌پایان است.');
INSERT INTO "hussein_taji" VALUES(41,9,'41:9','بگو: آیا شما به آن کسی‌که زمین را در دو روز آفرید کافر می‌شوید، و برای او همتایانی قرار می‌دهید؟! او پروردگار جهانیان است.');
INSERT INTO "hussein_taji" VALUES(41,10,'41:10','و در آن (زمین) کوه‌‌ها را از فرازش پدید آورد، و در آن برکت داد، و خوراک (و رزق، اهل) آن را مقدر (و معین) فرمود، (این‌ها همه) در چهار روز بود، (بنا بر این) برای سؤال کنندگان (و اضح و) روشن گردید.');
INSERT INTO "hussein_taji" VALUES(41,11,'41:11','سپس به سوی آسمان متوجه شد، در حالی‌که بصورت دود بود، پس به آن و به زمین فرمود: «خواسته یا ناخواسته بیایید» گفتند: «به دلخواه آمدیم».');
INSERT INTO "hussein_taji" VALUES(41,12,'41:12','پس آن‌ها را (بصورت) هفت آسمان در دو روز ساخت، و در هر آسمانی کار (و تدبیر) آن (آسمان) را وحی کرد، و آسمان دنیا را با چراغ‌هایی (= ستارگان) بیاراستیم (و از شیاطین) حفظ کردیم، این است تقدیر (الله) پیروزمند دانا.');
INSERT INTO "hussein_taji" VALUES(41,13,'41:13','پس اگر آن‌ها روی گرداندند، بگو: «من شما را از صاعقه‌ای همانند صاعقۀ عاد و ثمود بیم می‌دهم».');
INSERT INTO "hussein_taji" VALUES(41,14,'41:14','چون پیامبران ازپیش رو و پشت سر (از هر سو) نزد‌شان آمدند، (گفتند) که جز الله را نپرستید، (آن‌ها) گفتند: «اگر پروردگار ما می‌خواست، فرشتگانی را نازل می‌کرد، پس ما به آنچه شما بدان فرستاده شده‌اید، کافر هستیم».');
INSERT INTO "hussein_taji" VALUES(41,15,'41:15','اما (قوم) عاد به ناحق در زمین تکبر ورزیدند، و گفتند: «چه کسی از ما نیرومند‌تر است؟!» آیا ندیدند الله ذاتی‌که آنان را آفریده از آن‌ها نیرومندتر است؟! و آن‌ها همواره آیات ما را انکار می‌کردند.');
INSERT INTO "hussein_taji" VALUES(41,16,'41:16','پس (ما) تند بادی شدید (و سرد) در روز‌هایی شوم بر آن‌ها فرستادیم، تا عذاب خوار کننده را در زندگی دنیا به آن‌ها بچشانیم، و بی‌گمان عذاب آخرت خوار کننده‌تر است، و آن‌ها (از هیچ سویی) یاری نخواهند شد.');
INSERT INTO "hussein_taji" VALUES(41,17,'41:17','و اما (قوم) ثمود را هدایت کردیم، پس آن‌ها نابینایی را بر هدایت ترجیح دادند، لذا به (خاطر) آنچه می‌کردند، صاعقۀ عذاب خوارکننده آن‌ها را (فرو) گرفت.');
INSERT INTO "hussein_taji" VALUES(41,18,'41:18','و کسانی را که ایمان آورده بودند و پرهیزگار بودند، نجات دادیم.');
INSERT INTO "hussein_taji" VALUES(41,19,'41:19','و روزی‌که دشمنان الله را به سوی آتش گرد آورده شوند، پس آن‌ها (برای پیوستن دیگران) باز داشته شوند.');
INSERT INTO "hussein_taji" VALUES(41,20,'41:20','تا چون (همگی) به آن (آتش دوزخ) برسند، گوش‌هایشان و چشم‌هایشان و پوست‌هایشان به آنچه می‌کردند بر علیه آن‌ها گواهی می‌دهند.');
INSERT INTO "hussein_taji" VALUES(41,21,'41:21','و آن‌ها به پوست‌هایشان گویند: «چرا بر علیه ما گواهی دادید؟!» (در جواب) گویند: «همان الهی که هر چیز را به سخن آورده است، ما را گویا ساخته است، و او نخستین بار شما را آفرید، و به سوی او باز گردانده می‌شوید».');
INSERT INTO "hussein_taji" VALUES(41,22,'41:22','از اینکه (مبادا) گوش‌هایتان و چشم‌هایتان و پوست‌هایتان بر علیه شما گواهی دهند (گناهان‌تان را) پنهان نمی‌کردید، و لیکن شما گمان می‌کردید که الله بسیار از آنچه را که انجام می‌دهید نمی‌داند.');
INSERT INTO "hussein_taji" VALUES(41,23,'41:23','این گمان (بد) شما بود، که دربارۀ پروردگارتان داشتید، شما را هلاک کرد، پس از زیانکاران شدید.');
INSERT INTO "hussein_taji" VALUES(41,24,'41:24','پس اگر صبر کنند آتش (جهنم) جایگاه‌شان است، و اگر پوزش بخواهند، پس عذر‌شان پذیرفته نمی‌شود.');
INSERT INTO "hussein_taji" VALUES(41,25,'41:25','و ما برای آن‌ها همنشینانی (از شیاطین) قرار دادیم، پس (زشتی‌ها را) از پیش رو و پشت سر آن‌ها برایشان بیاراستند، و بر آن‌ها نیز همانند امت‌هایی از جن و انس که پیش از آن‌ها بودند، فرمان (عذاب) تحقق یافت، بی‌گمان آن‌ها زیانکار بودند.');
INSERT INTO "hussein_taji" VALUES(41,26,'41:26','و کسانی‌که کافر شدند گفتند: «به این قرآن گوش فرا ندهید، و در (هنگام تلاوت) آن جنجال کنید (و سخنان بیهوده بگویید) شاید پیروز گردید».');
INSERT INTO "hussein_taji" VALUES(41,27,'41:27','پس یقیناً کسانی را که کافر شدند، عذاب سختی می‌چشانیم، و آن‌ها را به (خاطر) بدترین اعمالی‌که انجام می‌دادند، کیفر می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(41,28,'41:28','این آتش کیفر دشمنان الله است، که آنان در آن سرای جاوید دارند، کیفری است به (سبب) آنکه آیات ما را انکار می‌کردند.');
INSERT INTO "hussein_taji" VALUES(41,29,'41:29','و کسانی‌که کافر شدند، گفتند: «پروردگارا! آن دو کسانی از جن و انس که ما را گمراه کردند، به ما نشان بده، که آن دو را زیر پای خود قرار دهیم (و لگد کوب‌شان کنیم) تا از پست‌ترین (مردم) باشند».');
INSERT INTO "hussein_taji" VALUES(41,30,'41:30','بی‌گمان کسانی‌که گفتند: «پروردگار ما الله است» سپس استقامت کردند، فرشتگان بر آن‌ها نازل می‌شوند (و می‌گویند:) «نترسید، و اندوهگین نباشید، و بشارت باد، به بهشتی‌که شما وعده داده می‌شدید.');
INSERT INTO "hussein_taji" VALUES(41,31,'41:31','ما در زندگی دنیا و (نیز) در آخرت دوستان (و یاران) شما هستیم، و برای شما در آن (بهشت) هر چه دل‌تان بخواهد فراهم است، و هر چه در خواست کنید برایتان (حاضر) است.');
INSERT INTO "hussein_taji" VALUES(41,32,'41:32','(این‌ها) پذیرایی از سوی (الله) آمرزندۀ مهربان است».');
INSERT INTO "hussein_taji" VALUES(41,33,'41:33','و چه کسی خوش گفتار‌تر است از کسی‌که به سوی الله دعوت کند، و کار شایسته انجام دهد، و گوید: «بی‌شک من از مسلمانان هستم؟!»');
INSERT INTO "hussein_taji" VALUES(41,34,'41:34','و (هرگز) نیکی و بدی یکسان نیست، همیشه به نیکو‌ترین شیوه پاسخ ده، پس ناگاه (می‌بینی) همان کس‌که میان تو و او دشمنی است، گویی دوست صمیمی است.');
INSERT INTO "hussein_taji" VALUES(41,35,'41:35','و (اما) جز کسانی‌که شکیبا باشند آن را نپذیرند، و جز دارندۀ بهره‌ای بزرگ (از ایمان و اخلاق) آن را نپذیرد.');
INSERT INTO "hussein_taji" VALUES(41,36,'41:36','و هرگاه وسوسه‌ای (باز دارنده) از سوی شیطان تو را باز گرداند، پس به الله پناه ببر، یقیناً اوست که شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(41,37,'41:37','و از نشانه‌های او، شب و روز، و خورشید و ماه است، برای خورشید و برای ماه سجده نکنید، و برای الله که آن‌ها را آفریده است، سجده کنید، اگر تنها او را می‌پرستید.');
INSERT INTO "hussein_taji" VALUES(41,38,'41:38','پس اگر (کافران) تکبر کنند، (باکی نیست) پس کسانی‌که نزد پروردگارت هستند، شب و روز او را تسبیح می‌گویند و آنان خسته نمی‌شوند.');
INSERT INTO "hussein_taji" VALUES(41,39,'41:39','و از نشانه‌های او (این) است که زمین را خشک (و بی‌جان) می‌بینی، پس هنگامی‌که آب (باران) را بر آن فرو فرستیم، به جنبش در آید و برویاند، بی‌گمان کسی‌که آن را زنده کرد، یقیناً مردگان را زنده می‌کند، بی‌شک او بر هر چیز تواناست.');
INSERT INTO "hussein_taji" VALUES(41,40,'41:40','بی‌گمان کسانی‌که در آیات ما کجروی می‌کنند، بر ما پوشیده نیستند، آیا کسی‌که در آتش انداخته شود بهتر است، یا کسی‌که روز قیامت ایمن (و بدون وحشت) بیاید؟! هر چه می‌خواهید بکنید، مسلماً او به آنچه انجام می‌دهید بیناست.');
INSERT INTO "hussein_taji" VALUES(41,41,'41:41','بی‌گمان کسانی‌که به ذکـر (= قرآن) هنگامی‌که برای (هدایت) آنان آمده کافر شدند، (نیز بر ما پوشیده نیستند). و به راستی که آن کتابی ارجمند است.');
INSERT INTO "hussein_taji" VALUES(41,42,'41:42','که هیچ گونه باطلی نه از پیش روی آن و نه از پشت سر آن، به او راه نیابد، از سوی حکیم ستوده نازل شده است.');
INSERT INTO "hussein_taji" VALUES(41,43,'41:43','(ای پیامبر) به تو (چیزی) گفته نمی‌شود، مگر آنچه به پیامبران پیش از تو گفته شد، مسلماً پروردگار تو دارای آمرزش، و (هم) دارای مجازات دردناک است.');
INSERT INTO "hussein_taji" VALUES(41,44,'41:44','و اگر آن را قرآنی عجمی (= غیر عربی) قرار می‌دادیم، یقیناً می‌گفتند: «چرا آیاتش روشن (و واضح) نیست؟! آیا (قرآن) عجمی است، و (مخاطب آن) عربی است؟!» بگو: «این (قرآن) برای کسانی‌که ایمان آوردند، هدایت و شفاست، و کسانی‌که ایمان نمی‌آورند، در گوش‌هایشان سنگینی است، و آن (قرآن) برایشان (مایۀ) کوری است، آنان (همچون کسانی اند که گـویا) از جـایی دور ندا داده می‌شوند».');
INSERT INTO "hussein_taji" VALUES(41,45,'41:45','و به راستی ما به موسی کتاب (تورات) دادیم، پس در آن اختلاف شد، و اگر فرمانی از پروردگار تو پیش از این نبود، مسلماً در میان آن‌ها داوری می‌شد، و بی‌گمان آن‌ها دربارۀ آن (= قرآن) در شکی قوی هستند.');
INSERT INTO "hussein_taji" VALUES(41,46,'41:46','هر کس‌که کار شایسته‌ای انجام دهد، پس به سود خود اوست، و هر کس بدی کند، پس به زیان اوست، و پروردگار تو هرگز به بندگان ستم نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(41,47,'41:47','علم (وقوع) قیامت تنها به سوی او (الله) باز گردانده می‌شود، و هیچ میوه‌ای از غلافش بیرون نمی‌شود و هیچ ماده‌ای باردار نمی‌شود و نمی زاید مگر به علم او، و روزی‌که آن‌ها را ندا می‌دهد (و می فرماید:) کجایند شریکانی (که برای) من (می پنداشتید؟!) گویند: «(پروردگارا!) ما (در درگاه تو) عرضه داشتیم که هیچ کس از ما (بر شرک) گواهی نمی‌دهد».');
INSERT INTO "hussein_taji" VALUES(41,48,'41:48','و آنچه را پیش از این می‌خواندند، از (نظر‌شان) ناپدید (و نابود) شد، و دریافتند که (در آن روز) هیچ راه گریزی ندارند.');
INSERT INTO "hussein_taji" VALUES(41,49,'41:49','انسان (هرگز) از طلب خیر (و نیکی) خسته نمی‌شود، و اگر شر (و بدی) به او برسد، مأیوس و ناامید می‌شود.');
INSERT INTO "hussein_taji" VALUES(41,50,'41:50','و اگر پس از رنجی که به او رسیده است، رحمتی از سوی خود به او بچشانیم، مسلماً می‌گوید: «این حق من است، و گمان نمی‌کنم قیامت بر پا می‌شود، و (به فرض) اگر به سوی پروردگارم باز گردانده شوم، بی‌گمان برای من نزد او (پاداش‌های) نیکو (و خوش) است». پس بی‌گمان کسانی‌که کافر شدند به آنچه کرده‌اند آگاه خواهیم کرد، و از عذاب سخت به آن‌ها می‌چشانیم.');
INSERT INTO "hussein_taji" VALUES(41,51,'41:51','و هرگاه بر انسان (خیر و) نعمتی عطاء نمائیم، روی می‌گرداند، و متکبرانه (از حق) دور می‌شود، و هرگاه شر (و بدی) به او برسد، پس دعا (و تقاضا) یش (برای دفع آن) افزون می‌شود.');
INSERT INTO "hussein_taji" VALUES(41,52,'41:52','(ای پیامبر!) بگو: «به من خبر دهید اگر این (قرآن) از سوی الله باشد، سپس شما به آن کافر شوید، چه کسی گمراه‌تر خواهد بود از کسی‌که (با آن) در مخالفت (شدید و) دور است.');
INSERT INTO "hussein_taji" VALUES(41,53,'41:53','به زودی نشانه‌‌های خود را در آفاق و در نفس خود‌‌شان به آن‌ها نشان خواهیم داد، تا برای آن‌ها روشن گردد که او حق است، آیا کافی نیست که پروردگارت بر همه چیز گواه است؟!');
INSERT INTO "hussein_taji" VALUES(41,54,'41:54','آگاه باش، که آن‌ها از لقای پروردگار‌شان در شک اند، و آگاه باش که او به همه چیز احاطه دارد.');
INSERT INTO "hussein_taji" VALUES(42,1,'42:1','حم (حا. میم).');
INSERT INTO "hussein_taji" VALUES(42,2,'42:2','عسق (عین. سین. قاف).');
INSERT INTO "hussein_taji" VALUES(42,3,'42:3','این گونه الله پیروزمند حکیم به سوی تو و پیامبرانی که پیش از تو بودند، وحی می‌کند.');
INSERT INTO "hussein_taji" VALUES(42,4,'42:4','آنچه در آسمان‌ها و آنچه در زمین است از آنِ اوست، و او بلند مرتبۀ بزرگ است.');
INSERT INTO "hussein_taji" VALUES(42,5,'42:5','نزدیک است که آسمان‌ها (بخاطر عظمت الهی) بر فراز یکدیگر شکافته (و متلاشی) شوند، و فرشتگان به ستایش پروردگار‌شان تسبیح می‌گویند، و برای کسانی‌که در زمین هستند آمرزش می‌طلبند، آگاه باش، همانا الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(42,6,'42:6','و کسانی‌که جز او دوستان (و سرپرستانی) گرفته‌اند، الله بر آن‌ها نگهبان (و مراقب) است، و تو (ای پیامبر) بر آن‌ها متعهد (و مسئول) نیستی.');
INSERT INTO "hussein_taji" VALUES(42,7,'42:7','و این چنین قرآنی عربی به تو وحی کردیم تا «ام القری» (= مکه) و کسانی را که پیرامون آن هستند هشدار دهی، و از روز جمع شدن (= روز قیامت) که در آن شک و تردیدی نیست، بترسانی، (که در آن روز) گروهی در بهشت، و گروهی در آتش سوزانند!');
INSERT INTO "hussein_taji" VALUES(42,8,'42:8','و اگر الله می‌خواست یقیناً (همۀ) آن‌ها را یک امت قرار می‌داد، و لیکن هرکس را که بخواهد در رحمتش داخل می‌کند، و برای ستمکاران هیج یار و یاوری نیست.');
INSERT INTO "hussein_taji" VALUES(42,9,'42:9','آیا آن‌ها جز او دوستانی (برای خود) بر گزیده‌اند؟ در حالی‌که الله فقط کارساز (و دوست حقیقی) است، و اوست که مردگان را زنده می‌کند، و اوست که بر هر چیزی تواناست.');
INSERT INTO "hussein_taji" VALUES(42,10,'42:10','و در هر چیزی که در آن اختلاف کنید، پس حکم (و داوری) آن با الله است، این الله، پروردگار من است. بر او توکل کردم، و به (سوی) او باز می‌گردم.');
INSERT INTO "hussein_taji" VALUES(42,11,'42:11','(او) آفرینندۀ آسمان‌ها و زمین است، و از خودتان برای شما همسرانی قرار داد، و (نیز) جفت‌هایی از چهار پایان (آفرید) شما را بدین (وسیله) افزون می‌کند، هیچ چیز همانند او نیست، و او شنوای بیناست.');
INSERT INTO "hussein_taji" VALUES(42,12,'42:12','کلید‌های آسمان‌ها و زمین از آن اوست، روزی هر که را بخواهد گسترش می‌دهد، و (برای هر کس که بخواهد) تنگ می‌گیرد، به درستى که او به همه چیز داناست.');
INSERT INTO "hussein_taji" VALUES(42,13,'42:13','دینی را برای شما تشریع کرد، از همان گونه که به نوح توصیه کرده بود، و از آنچه بر تو وحی کرده‌ایم، و به ابراهیم و موسی و عیسی سفارش کرده‌ایم، که دین را بر پا دارید و در آن فرقه فرقه نشوید، بر مشرکان گران است، آنچه شما آن‌ها را بدان دعوت می‌کنید. الله هر که را بخواهد بر می‌گزیند، و هر که به سوی او باز گردد، هدایت می‌کند.');
INSERT INTO "hussein_taji" VALUES(42,14,'42:14','آن‌ها پراکنده نشدند مگر بعد از آنکه علم (و دانش) به سراغ‌شان آمد (و کاملاً آگاهی یافتند، آن هم) از روی حسادت در میان خود (بود) و اگر فرمانی پیش از این از جانب پروردگارت صادر نشده بود، (که آن‌ها) را تا زمانی معین (مهلت) داده است، یقیناً در میان آن‌ها داوری می‌شد، و بی‌گمان کسانی‌که بعد از آن‌ها وارث کتاب شده‌اند، دربارۀ آن سخت در شک و تردیدند، (و نسبت به آن بد‌بینند).');
INSERT INTO "hussein_taji" VALUES(42,15,'42:15','پس به این (دین) دعوت کن، و همان‌گونه که مأمور شده‌ای پایداری (و استقامت) ورز، و از هوی و هوس‌های آنان پیروی مکن، و بگو: «به هر کتابی که الله نازل کرده است، ایمان آورده‌ام، و مأمورم در میان شما به عدالت رفتار کنم، الله پروردگار ما، و پروردگار شماست، (نتیجۀ) اعمال ما از آنِ ما، و (نتیجۀ) اعمال شما از آنِ شماست، بین ما و شما هیچ جدال (و خصومتی) نیست، الله ما و شما را (دریک‌‌جا) گرد می‌آورد، و بازگشت (همه) به سوی اوست».');
INSERT INTO "hussein_taji" VALUES(42,16,'42:16','وکسانی‌که در (بارۀ) الله محاجه می‌کنند، پس از آنکه (فرمان) او پذیرفته شد، دلیل‌شان نزد پروردگار‌شان باطل (و بی‌اساس) است، و خشم (الله) بر آن‌هاست. و عذاب شدیدی برای آن‌هاست.');
INSERT INTO "hussein_taji" VALUES(42,17,'42:17','الله کسی است که کتاب و میزان را به حق نازل کرد، و تو چه می‌دانی شاید قیامت نزدیک باشد!');
INSERT INTO "hussein_taji" VALUES(42,18,'42:18','کسانی‌که به آن ایمان ندارند، در (رسیدن) آن شتاب می‌کنند، و کسانی‌که ایمان آورده‌اند (پیوسته) ازآن بیمناکند، و می‌دانند که آن حق است، آگاه باشید، بی‌گمان کسانی‌که در (بارۀ) قیامت جدال می‌کنند، در گمراهی دور و دراز هستند.');
INSERT INTO "hussein_taji" VALUES(42,19,'42:19','الله نسبت به بندگانش لطیف (و مهربان) است، هر کس را بخواهد روزی می‌دهد، و او توانای پیروزمنداست.');
INSERT INTO "hussein_taji" VALUES(42,20,'42:20','کسی‌که کِشت آخرت را بخواهد، در کشت او (برکت می‌دهیم، و) می‌افزاییم، و کسی‌که کشت دنیا را بخواهد، از آن به او می‌دهیم،و او در آخرت هیچ (نصیب و) بهره‌ای ندارد.');
INSERT INTO "hussein_taji" VALUES(42,21,'42:21','آیا (مشرکان) معبودانی دارند که بدون اجازۀ الله آیینی برای آن‌ها مقرر داشته‌اند؟! و اگر وعدۀ فیصله بخش (سابق، در تأخیر عذاب‌شان) نبود، یقیناً در میان آن‌ها داوری می‌شد، و بی‌گمان برای ستمکاران عذابی دردناک است.');
INSERT INTO "hussein_taji" VALUES(42,22,'42:22','(در روز قیامت) ستمکاران را می‌بینی که از آنچه به دست آورده‌اند، بیمناکند، و (کیفر) آن به آن‌ها خواهد رسید. و کسانی‌که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند در باغ‌های بهشتند، و هرچه بخواهند نزد پروردگار‌شان برای آن‌ها (مهیا) است. این همان فضل (و بخشش) بزرگ است.');
INSERT INTO "hussein_taji" VALUES(42,23,'42:23','این همان چیزی است که الله بندگانش را که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، به آن مژده می‌دهد. بگو: «من بر (رساندن) آن هیچ پاداشی از شما در خواست نمی‌کنم، جز محبت خویشاوندی، (که بجای آورید)» [ عبد الله بن عباس رضی الله عنهما می‌گوید: هیچ تیره‌ای از قریش نبود، مگر اینکه با رسول الله صلی الله علیه وسلم نسبت خویشاوندی داشت، پس رسول الله صلی الله علیه وسلم فرمود: «از شما چیزی جز ادا کردن حق خویشاوندی که میان من و شما است، نمی‌خواهم». (صحیح بخاری .]. و هرکس که نیکی کند، بر نیکی‌اش می‌افزاییم، بی‌گمان الله آمرزندۀ قدر شناس است.');
INSERT INTO "hussein_taji" VALUES(42,24,'42:24','آیا می‌گویند: «(پیامبر) بر الله دروغ بسته است؟!» پس اگر الله بخواهد بر قلب تو مهر می‌زند، و الله باطل را محو می‌کند، و حق را با سخنان خویش محقق (و پا برجا) می‌سازد، بی‌گمان او به راز سینه‌ها آگاه است.');
INSERT INTO "hussein_taji" VALUES(42,25,'42:25','و او کسی است که از بندگانش توبه می‌پذیرد، و از بدی‌ها در می‌گذرد، و آنچه را انجام می‌دهید می‌داند.');
INSERT INTO "hussein_taji" VALUES(42,26,'42:26','(دعای) کسانی را که ایمان آورده و کارهای شایسته انجام داده‌اند، اجابت می‌کند، و از فضل (و کرم) خویش آنان را افزون می‌دهد، و برای کافران، عذاب شدیدی است.');
INSERT INTO "hussein_taji" VALUES(42,27,'42:27','و اگر الله روزی را برای بندگانش فراخ (و گشاده) می‌گرداند، یقیناً در زمین فساد می‌کردند، و لکن به‌اندازه‌ای که بخواهد نازل می‌کند، بی‌گمان او به بندگانش دانای بیناست.');
INSERT INTO "hussein_taji" VALUES(42,28,'42:28','و او کسی است که باران (سودمند) را پس از آنکه نا‌‌‌‌‌‌‌امید شدند، نازل می‌کند، و رحمت خود را می‌گستراند، و اوکارساز ستوده است.');
INSERT INTO "hussein_taji" VALUES(42,29,'42:29','و از نشانه‌های او آفرینش آسمان‌ها و زمین است، و آنچه از جنبدگان که در آن دو پراکنده است. و او هرگاه بخواهد، بر گرد آوری آن‌ها تواناست.');
INSERT INTO "hussein_taji" VALUES(42,30,'42:30','و هر مصیبتی که به شما رسد، به خاطر کارهایی است که انجام داده‌اید، و (الله) از بسیاری (گناهان) در می‌گذرد.');
INSERT INTO "hussein_taji" VALUES(42,31,'42:31','و شما هرگز در زمین عاجز کننده نیستید (و نمی توانید از او بگریزید) و شما را غیر از الله، هیچ یار و یاوری نیست.');
INSERT INTO "hussein_taji" VALUES(42,32,'42:32','واز نشانه‌های او کشتی‌هایی است که همچون کوه در دریا روانند.');
INSERT INTO "hussein_taji" VALUES(42,33,'42:33','اگر بخواهد باد را (از حرکت) باز می‌دارد، پس کشتی‌ها بر پشت آن (دریا) بی حرکت بمانند، بی‌گمان در این نشانه‌هایی برای هر صبر کنندۀ شکر‌‌‌‌‌‌‌گزاری است.');
INSERT INTO "hussein_taji" VALUES(42,34,'42:34','یا (اگر بخواهد) آن‌ها (= سرنشینان کشتی) را به خاطر اعمال‌شان نابود (و غرق) می‌سازد، و از بسیاری (گناهان) در می‌گذرد.');
INSERT INTO "hussein_taji" VALUES(42,35,'42:35','و (تا) کسانی‌که در آیات ما مجادله می‌کنند، بدانند که هیچ گریزگاهی ندارند.');
INSERT INTO "hussein_taji" VALUES(42,36,'42:36','پس آنچه به شما داده است، متاع زود گذر زندگی دنیاست، و آنچه نزد الله است برای کسانی‌که ایمان آورده‌اند و بر پروردگارشان توکّل می‌کنند بهتر و پایدار‌تر است.');
INSERT INTO "hussein_taji" VALUES(42,37,'42:37','و (همان) کسانی‌که از گناهان بزرگ و زشتی‌ها اجتناب می‌کنند، و هنگامی‌که خشمگین شوند، گذشت می‌کنند. [گناهان بزرگ، گناهان کبیره مانند: شرک، نافرمانی پدر و مادر، قتل، دزدی، زنا، گواهی دروغ ... می‌باشد (به سوره انعام آیه ـ 152 رجوع کنید).]');
INSERT INTO "hussein_taji" VALUES(42,38,'42:38','و کسانی‌که (دعوت) پروردگارشان را اجابت کردند، و نماز را بر پا می‌دارند، و کارهای‌شان بین آنان به (صورت) مشورت است، و از آنچه به آن‌ها روزی داده‌ایم. (در راه الله) انفاق می‌کنند.');
INSERT INTO "hussein_taji" VALUES(42,39,'42:39','و کسانی‌که چون ستمی به آن‌ها برسد، انتقام می‌گیرند.');
INSERT INTO "hussein_taji" VALUES(42,40,'42:40','و جزایی بدی، بدیی است همانند آن، پس هر کس که در گذرد، و اصلاح (و آشتی) کند، پاداشش بر الله است، بی‌گمان او ستمکاران را دوست ندارد.');
INSERT INTO "hussein_taji" VALUES(42,41,'42:41','و کسی‌که پس از ستمی که بر او رفته است، انتقام بگیرد، پس اینانند که هیچ راه (ملامتی) بر آنان نیست.');
INSERT INTO "hussein_taji" VALUES(42,42,'42:42','تنها راه (ملامت) بر کسانی است که به مردم ستم می‌کنند، و در زمین به ناحق سرکشی (و ستم) می‌کنند، اینانند که برایشان عذابی دردناک است.');
INSERT INTO "hussein_taji" VALUES(42,43,'42:43','و کسی‌که شکیبایی کند، و در گذرد، بی‌گمان این از کارهای سترگ (پسندیده) است.');
INSERT INTO "hussein_taji" VALUES(42,44,'42:44','و کسی را که الله گمراه کند، پس بعد از او برایش هیچ ولی (و کارسازی) نیست، و ستمکاران را (روز قیامت) می‌بینی که چون عذاب (الهی) را مشاهده کنند، می‌گویند: «آیا هیچ راهی به سوی بازگشت وجود دارد؟!».');
INSERT INTO "hussein_taji" VALUES(42,45,'42:45','و آن‌ها را می‌بینی که بر آن (= آتش) عرضه می‌شوند، که از شدت خواری خاشعند، از گوشۀ چشم نیم گشوده، نگاه می‌کنند، و کسانی‌که ایمان آورده‌اند می‌گویند: «بی‌گمان زیانکاران کسانی هستند که در روز قیامت خود و خانوادۀ خویش را به زیان افکندند». آگاه باشید که ستمکاران در عذابی دایم خواهند بود.');
INSERT INTO "hussein_taji" VALUES(42,46,'42:46','و آن‌ها جز الله، یاورانی ندارند، که یاری‌شان کنند، و کسی را که الله گمراه سازد، پس برایش هیچ راه (نجاتی) نیست.');
INSERT INTO "hussein_taji" VALUES(42,47,'42:47','(دعوت) پروردگار‌تان را اجابت کنید، پیش از آنکه روزی فرا رسد که از (جانب) الله (راه) بازگشتی در آن نیست، در آن روز پناهی نخواهید داشت، و هیچ مدافعی (و راه انکاری) ندارید.');
INSERT INTO "hussein_taji" VALUES(42,48,'42:48','پس (ای پیامبر) اگر (مشرکان اعراض کنند، (نگران نباش)، ما تو را نگهبان بر آن‌ها نفرستاده‌ایم، بر (عهدۀ) تو جز تبلیغ (رسالت) نیست، و ما چون رحمتی از سوی خود به انسان بچشانیم، به آن شاد می‌شود، و اگر به خاطر کار‌هایی که انجام داده‌اند بلایی به آن‌ها برسد، پس (به ناسپاسی می‌پردازند) بی‌گمان انسان بسیار نا سپاس است.');
INSERT INTO "hussein_taji" VALUES(42,49,'42:49','فرمانروایی آسمان‌ها و زمین از آن الله است، هر چه را بخواهد می‌آفریند، به هر کس بخواهد دختر می‌بخشد، و به هرکس بخواهد پسر می‌بخشد.');
INSERT INTO "hussein_taji" VALUES(42,50,'42:50','یا پسر و دختر ـ هر دو ـ با هم می‌دهد، و هرکس را که بخواهد عقیم می‌گرداند، بی‌گمان او دانای قادر است.');
INSERT INTO "hussein_taji" VALUES(42,51,'42:51','و برای هیچ بشری (ممکن) نیست که الله با او سخن بگوید، مگر با وحی یا از پس پرده یا رسولی بفرستد که به فرمان او (= الله) آنچه را بخواهد (به او) وحی کند، بی‌گمان او بلند مرتبۀ حکیم است.');
INSERT INTO "hussein_taji" VALUES(42,52,'42:52','و این‌گونه بر تو (ای پیامبر) روحی (= قرآن کریم) را به فرمان خود وحی کردیم، تو (پیش از این) نمی‌دانستی کتاب و ایمان چیست، ولی ما آن را نوری قرار دادیم، با آن هر کس از بندگان‌مان را که بخواهیم، هدایت می‌کنیم، و مسلماً تو (ای پیامبر) به راه راست هدایت می‌کنی.');
INSERT INTO "hussein_taji" VALUES(42,53,'42:53','راه الهی که آنچه در آسمان‌ها و آنچه در زمین است از آنِ اوست، آگاه باشید، که (همۀ) کار‌ها به سوی الله باز می‌گردد.');
INSERT INTO "hussein_taji" VALUES(43,1,'43:1','حم (حا. میم).');
INSERT INTO "hussein_taji" VALUES(43,2,'43:2','سوگند به کتاب روشنگر.');
INSERT INTO "hussein_taji" VALUES(43,3,'43:3','بی‌گمان ما این (کتاب) را قرآنی عربی قرار دادیم، شاید که شما (آن را) در یابید.');
INSERT INTO "hussein_taji" VALUES(43,4,'43:4','و به راستی که آن در ام الکتاب (لوح محفوظ) نزد ما بلند مرتبه (و) حکمت آمیز است.');
INSERT INTO "hussein_taji" VALUES(43,5,'43:5','آیا بدان سبب که شما (مشرکان) گروهی اسراف‌کارید، اعراض کنان این پند (قرآن) را از شما بگیریم؟!.');
INSERT INTO "hussein_taji" VALUES(43,6,'43:6','و چه بسیار پیامبرانی که (برای هدایت) به میان پیشینیان فردستادیم.');
INSERT INTO "hussein_taji" VALUES(43,7,'43:7','و هیچ پیامبری به سوی آنان نیامد، مگر این‌که او را مسخره (و استهزاء) می‌کردند.');
INSERT INTO "hussein_taji" VALUES(43,8,'43:8','پس ما (کافرانی) نیرومند‌تر از این‌ها (= کفار قریش) را هلاک کردیم، و داستان پیشینیان گذشت.');
INSERT INTO "hussein_taji" VALUES(43,9,'43:9','(ای پیامبر) اگر از آن‌ها (= مشرکان) بپرسی: «چه کسی آسمان‌ها و زمین را آفریده است؟» یقیناً می‌گویند: «(الله) پیروزمند دانا آن‌ها را آفریده است».');
INSERT INTO "hussein_taji" VALUES(43,10,'43:10','(همان) کسی‌که زمین را برای شما گهواره (و محل آسایش) قرار داد، و برای شما در آن راه‌هایی قرار داد، باشد که هدایت شوید.');
INSERT INTO "hussein_taji" VALUES(43,11,'43:11','و (آن) کسی‌که از آسمان آبی به‌اندازۀ (معین) فرو فرستاد، پس با آن (آب) سرزمین مرده را زنده کردیم شما (نیز) این‌گونه (پس از مرگ، از گور‌ها) بیرون آورده می‌شوید.');
INSERT INTO "hussein_taji" VALUES(43,12,'43:12','و (آن) کسی‌که همۀ اصناف را (جفت) آفرید، و برای شما از کشتی و چهار پایان مرکب‌هایی قرار داد که (بر آن‌ها) سوار شوید.');
INSERT INTO "hussein_taji" VALUES(43,13,'43:13','تا بر پشت‌شان (درست) قرار گیرید، آنگاه چون بر آن‌ها قرار گرفتید (و سوار شدید) نعمت پروردگار‌تان را یاد کنید، و بگویید: «پاک و منزه است کسی‌که این را برای‌مان مسخر گرداند، و (گرنه) ما توانایی آن را نداشتیم،');
INSERT INTO "hussein_taji" VALUES(43,14,'43:14','و ما به سوی پروردگار‌مان باز می‌گردیم».');
INSERT INTO "hussein_taji" VALUES(43,15,'43:15','و آن‌ها برای او (الله) از میان بندگانش جزئی (= فرزندی) قرار دادند، بی‌گمان انسان نا سپاس آشکاری است.');
INSERT INTO "hussein_taji" VALUES(43,16,'43:16','آیا از (میان) آنچه می‌آفریند دختران را (برای خود) انتخاب کرده است، و پسران را برای شما بر گزیده است؟!');
INSERT INTO "hussein_taji" VALUES(43,17,'43:17','و چون یکی از آن‌ها را به (تولد) همان چیزی‌که برای (الله) رحمان نسبت داده، مژده دهند، (از شدت ناراحتی) چهره‌اش سیاه گردد، و او خشمگین شود.');
INSERT INTO "hussein_taji" VALUES(43,18,'43:18','آیا کسی را که در زیور (و زینت) پرورده می‌شود، و او در (هنگام) جدال (قادر به) آشکار (و تبین مقصودش) نیست (به الله نسبت می‌دهید؟!).');
INSERT INTO "hussein_taji" VALUES(43,19,'43:19','و آن‌ها فرشتگان را که بندگان (الله) رحمان هستند، مونث پنداشتند، آیا به هنگام آفرینش آن‌ها (آنجا) حاضر بوده‌اند؟ به زودی گواهی آنان نوشته خواهد شد، و (ازآن) باز خواست می‌شوند.');
INSERT INTO "hussein_taji" VALUES(43,20,'43:20','و گفتند: «اگر (الله) رحمان می‌خواست، ما آن‌ها را پرستش نمی‌کردیم» آنان به این (امر) هیچ گونه علمی ندارند، آنان جز دروغ (چیزی) نمی‌گویند.');
INSERT INTO "hussein_taji" VALUES(43,21,'43:21','آیا پیش از این (قرآن) کتابی به آنان داده‌ایم پس آنان به آن تمسک می‌جویند؟!');
INSERT INTO "hussein_taji" VALUES(43,22,'43:22','بلکه می‌گویند: «بی‌گمان ما نیاکان خود را بر آیینی یافتیم، و ما (نیز) از پی آن‌ها (می‌رویم و) هدایت یافته‌ایم».');
INSERT INTO "hussein_taji" VALUES(43,23,'43:23','و این‌گونه در هیچ (شهر و) قریه‌ای پیش از تو هشداردهنده‌ای نفرستادیم، مگر اینکه متنعمانش گفتند: «ما نیاکان خود را بر آیینی یافتیم، و بی‌گمان ما به (اعمال و) آثار آن‌ها اقتداء می‌کنیم».');
INSERT INTO "hussein_taji" VALUES(43,24,'43:24','(پیامبر‌شان) گفت: «آیا اگر برای شما چیزی هدایت‌کننده‌تر از آنچه که نیاکانتان را بر آن یافتید، آورده باشم؟! (بازهم به آن‌ها اقتداء می‌کنید؟!) گفتند: «(آری،) ما به آنچه که شما به آن فرستاده شده‌اید، کافریم».');
INSERT INTO "hussein_taji" VALUES(43,25,'43:25','بنا براین از آن‌ها انتقام گرفتیم، پس بنگر سرانجام تکذیب کنندگان چگونه بود!');
INSERT INTO "hussein_taji" VALUES(43,26,'43:26','و (به یاد آور) را هنگامی‌که ابراهیم به پدرش و قومش گفت: «بی‌گمان من از آنچه می‌پرستید، بیزارم،');
INSERT INTO "hussein_taji" VALUES(43,27,'43:27','مگر آن کسی‌که مرا آفریده، و او به زودی هدایتم خواهد کرد».');
INSERT INTO "hussein_taji" VALUES(43,28,'43:28','و (ابراهیم) این کلمۀ (توحید) را (کلمۀ پاینده) در نسل‌های بعد از خود قرار داد، باشد که (به سوی الله) باز گردند.');
INSERT INTO "hussein_taji" VALUES(43,29,'43:29','بلکه، آنان و پدران‌شان را (از زندگی) بهره‌مند ساختیم، تا حق و فرستاده‌ای آشکار برایشان آمد.');
INSERT INTO "hussein_taji" VALUES(43,30,'43:30','و هنگام‌ که حق برایشان آمد، گفتند: «این سحراست، و ما (نسبت) به آن کافریم».');
INSERT INTO "hussein_taji" VALUES(43,31,'43:31','و گفتند: «چرا این قرآن بر مرد بزرگ، از (مردم) این دو شهر (مکه و طایف) نازل نشده است؟!».');
INSERT INTO "hussein_taji" VALUES(43,32,'43:32','آیا آن‌ها رحمت پروردگارت را تقسیم می‌کنند؟! ما روزی (و معیشت) آن‌ها را در زندگی دنیا میان‌شان تقسیم کردیم، و بعضی را بر بعضی (درجات و) برتری دادیم، تا بعضی از آنان بعضی دیگر را به خدمت گیرند، و رحمت پروردگارت از (تمام) آنچه گرد می‌آورند، بهتراست.');
INSERT INTO "hussein_taji" VALUES(43,33,'43:33','اگر (چنین) نبود که همۀ مردم یک امت می‌شدند، یقیناً ما برای خانه‌های کسانی‌که به (الله) رحمان کافر می‌شدند، سقف‌های از نقره قرار می‌دادیم، و (نیز) نردبان‌هایی که از آن بالا روند.');
INSERT INTO "hussein_taji" VALUES(43,34,'43:34','و برای خانه‌هایشان در‌ها و تخت‌هایی (نقره‌ای، قرار می‌دادیم) که بر آن تکیه زنند.');
INSERT INTO "hussein_taji" VALUES(43,35,'43:35','و تجمل (و زینت‌های) فراوان (برایشان قرار می‌دادیم) و تمام این‌ها بهرۀ (ناچیز) زندگی دنیاست، و آخرت نزد پروردگارت برای پرهیزگاران است.');
INSERT INTO "hussein_taji" VALUES(43,36,'43:36','و هرکس از یاد (الله) رحمان غافل (و روی گردان) شود، برای او شیطانی بر می‌گماریم، پس همواره آن (شیطان) قرین (و همنشین) او باشد.');
INSERT INTO "hussein_taji" VALUES(43,37,'43:37','و آن‌ها (= شیاطین) این‌ها (=انسان‌ها) را از راه (الله) باز می‌دارند، و گمان می‌کنند که آنان هدایت یافته‌اند.');
INSERT INTO "hussein_taji" VALUES(43,38,'43:38','تا زمانی که (در روز قیامت) نزد ما آید، (به همنشین خود) می‌گوید: «ای کاش میان من و تو مسافت شرق و غرب فاصله بود، پس تو چه بد همنشینی بودی».');
INSERT INTO "hussein_taji" VALUES(43,39,'43:39','(الله می‌فرماید:) «چون ستم کردید، امروز (این گفته‌ها) هرگز سود‌‌تان ندهد، چرا که (همۀ) شما در عذاب شریک هستید».');
INSERT INTO "hussein_taji" VALUES(43,40,'43:40','(ای پیامبر) آیا تو می‌توانی به کران (سخنی) بشنوانی، یا کوران، و آن‌هایی را که در گمراهی آشکار هستند، هدایت کنی؟!');
INSERT INTO "hussein_taji" VALUES(43,41,'43:41','پس اگر تو را (از این دنیا) ببریم، بی‌گمان ما از آن‌ها انتقام خواهیم گرفت.');
INSERT INTO "hussein_taji" VALUES(43,42,'43:42','یا آنچه را که به آن‌ها وعده داده‌ایم، به تو نشان می‌دهیم، پس یقیناً ما بر آن‌ها توانا هستیم.');
INSERT INTO "hussein_taji" VALUES(43,43,'43:43','پس به چیزی که به تو وحی شده است تمسک بجوی، که بی‌شک تو بر راه راست هستی.');
INSERT INTO "hussein_taji" VALUES(43,44,'43:44','و بی‌گمان این (قرآن) برای تو و قومت پند (و سربلندی) است، و به زودی باز خواست خواهید شد.');
INSERT INTO "hussein_taji" VALUES(43,45,'43:45','و از پیامبرانی که پیش از تو فرستادیم بپرس: آیا غیر از (الله) رحمان، معبودانی برای پرستش قرار دادیم؟!');
INSERT INTO "hussein_taji" VALUES(43,46,'43:46','و به راستی ما موسی را با نشانه‌های خود به سوی فرعون و درباریان او فرستادیم، پس گفت: «بی‌گمان من فرستادۀ پروردگار جهانیان هستم».');
INSERT INTO "hussein_taji" VALUES(43,47,'43:47','پس چون با نشانه‌های ما (به سوی) آن‌ها آمد، ناگاه (همه) از آن خندیدند.');
INSERT INTO "hussein_taji" VALUES(43,48,'43:48','و ما هیچ نشانه (و معجزه‌ای) به آن‌ها نشان نمی‌دادیم، مگر اینکه از دیگری بزرگتر (و مهم‌تر) بود، و آن‌ها را به عذاب گرفتار کردیم، باشد که (توبه کنند و) باز گردند.');
INSERT INTO "hussein_taji" VALUES(43,49,'43:49','و (به موسی) گفتند: «ای جادوگر! پروردگارت را با عهدی که با تو کرده، برای ما بخوان، بی‌گمان ما هدایت خواهیم یافت».');
INSERT INTO "hussein_taji" VALUES(43,50,'43:50','پس چون عذاب را از آن‌ها برطرف ساختیم، آنگاه آن‌ها عهد شکنی کردند.');
INSERT INTO "hussein_taji" VALUES(43,51,'43:51','و فرعون در (میان) قوم خود ندا داد، گفت: «ای قوم من! آیا فرمانروایی مصر، و این نهر‌ها که زیر (کاخ) من روان است، از آنِ من نیست؟! آیا نمی‌بینید؟!');
INSERT INTO "hussein_taji" VALUES(43,52,'43:52','بلکه من بهترم، از این مردی که خوار است، و نمی‌تواند درست سخن بگوید.');
INSERT INTO "hussein_taji" VALUES(43,53,'43:53','پس چرا بر او دستنبدهای طلا فرو فرستاده نشده، یا (چرا) فرشتگان صف کشیده همراه او نیامده‌اند، (تا او را یاری کنند)؟!».');
INSERT INTO "hussein_taji" VALUES(43,54,'43:54','پس (فرعون) قوم خود را (بی‌خرد و) سبک شمرد، پس آن‌ها از او اطاعت کردند، بی‌گمان آن‌ها گروهی فاسق بودند.');
INSERT INTO "hussein_taji" VALUES(43,55,'43:55','پس چون ما را به خشم آوردند از آن‌ها انتقام گرفتیم، و همگی‌شان را (در دریا) غرق کردیم.');
INSERT INTO "hussein_taji" VALUES(43,56,'43:56','آنگاه آن‌ها را در شمار گذشتگان و عبرتی (برای آیندگان) قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(43,57,'43:57','و چون (دربارۀ آفرینش) فرزند مریم مَثَلی زده شد، ناگهان قوم تو از آن (شادی کنان) داد و فریاد زدند.');
INSERT INTO "hussein_taji" VALUES(43,58,'43:58','و گفتند: «آیا معبودان ما بهترند یا او؟». آن‌ها آن (مثل) را جز برای جدال (و لجاجت) با تو نزدند، بلکه آن‌ها گروهی ستیزه جویند.');
INSERT INTO "hussein_taji" VALUES(43,59,'43:59','او (=عیسی) جز بنده‌ای نبود که ما بر او نعمت بخشیدیم، و او را (مایۀ) عبرت برای بنی اسرائیل گرداندیم.');
INSERT INTO "hussein_taji" VALUES(43,60,'43:60','و اگر می‌خواستیم به جای شما فرشتگانی قرار می‌دادیم که در (روی) زمین جانشین (شما) باشند.');
INSERT INTO "hussein_taji" VALUES(43,61,'43:61','و بی‌گمان او (= عیسی) نشانه‌ای برای قیامت است [ یعنی: نزول عیسی علیه السلام از نشانه‌های قیامت است، و روایات نزول حضرت عیسی علیه السلام از آسمان، متواتر می‌باشد.]، پس هرگز در آن (= قیامت) شک نکنید، و از من پیروی کنید، (که) این راه راست است.');
INSERT INTO "hussein_taji" VALUES(43,62,'43:62','و شیطان شما را (از راه حق) باز ندارد، بی‌گمان او برای شما دشمنی آشکار است.');
INSERT INTO "hussein_taji" VALUES(43,63,'43:63','و چون عیسی با دلایل (و معجزات) روشن آمد، گفت: «من برای شما حکمت آورده ام، و (آمده ام) تا چیزهایی را که درآن اختلاف دارید، برایتان روشن کنم، پس از الله بترسید، و از من اطاعت کنید.');
INSERT INTO "hussein_taji" VALUES(43,64,'43:64','بی‌گمان الله پروردگار من، و پروردگار شما ست، پس او را بپرستید، (که) این راه راست است».');
INSERT INTO "hussein_taji" VALUES(43,65,'43:65','آنگاه گرو‌های (مختلف، بنی اسرائیل) میان خود (دربارۀ عیسی) اختلاف کردند، پس وای بر کسانی‌که ستم کردند، از عذاب روز دردناک!');
INSERT INTO "hussein_taji" VALUES(43,66,'43:66','آیا (چیزی) جز قیامت را انتظار می‌کشند، که ناگهان و در حالی‌که بی‌خبرند به سراغ‌شان بیاید.');
INSERT INTO "hussein_taji" VALUES(43,67,'43:67','دوستان درآن روز (قیامت) دشمن یکدیگرند، جز پرهیزگاران.');
INSERT INTO "hussein_taji" VALUES(43,68,'43:68','ای بندگان من! امروز نه ترسی بر شما است، و نه شما اندوهگین می‌شوید.');
INSERT INTO "hussein_taji" VALUES(43,69,'43:69','(همان) کسانی‌که به آیات ما ایمان آوردند و مسلمان بودند.');
INSERT INTO "hussein_taji" VALUES(43,70,'43:70','شما و همسران‌تان با شادمانی (و اکرام) به بهشت وارد شوید.');
INSERT INTO "hussein_taji" VALUES(43,71,'43:71','بر (گرداگرد) آن‌ها سینی‌هایی زرین (غذا) و (نیز) جام‌های (طلایی، شراب) می‌گردانند، و در آن (بهشت) آنچه دل‌ها تمنا می‌کند، و چشم‌ها از آن لذّت می‌برد، وجود دارد، و شما در آن همیشه خواهید بود.');
INSERT INTO "hussein_taji" VALUES(43,72,'43:72','و (این) همان بهشتی است، که به (پاداش) آنچه که انجام می‌دادید، آن را به ارث برده‌اید.');
INSERT INTO "hussein_taji" VALUES(43,73,'43:73','در آن (بهشت) برای شما میوۀ فراوانی است، که از آن می‌خورید.');
INSERT INTO "hussein_taji" VALUES(43,74,'43:74','بی‌گمان مجرمان در عذاب جهنم جاودانند.');
INSERT INTO "hussein_taji" VALUES(43,75,'43:75','(عذاب) از آن‌ها کاهش نمی‌یابد، و آن‌ها در آن (از همه چیز) نا امید هستند.');
INSERT INTO "hussein_taji" VALUES(43,76,'43:76','و (ما) به آن‌ها ستم نکردیم، لیکن آنان خود ستمکار بودند.');
INSERT INTO "hussein_taji" VALUES(43,77,'43:77','و (آن‌ها) فریاد می‌زنند: «ای مالک، (بخواه) که پروردگارت کار ما را یکسره کند (و بمیراند)». (او) گوید: «بی‌گمان شما (در این جا) ماندنی هستید».');
INSERT INTO "hussein_taji" VALUES(43,78,'43:78','به راستی ما حق را برای شما آوردیم، و لیکن بیشترتان از حق کراهت داشتید.');
INSERT INTO "hussein_taji" VALUES(43,79,'43:79','آیا آن‌ها (= مشرکان مکه) تصمیم قاطع بر کاری گرفتند، پس ما (نیز) تصمیم (واردۀ) قاطع (دربارۀ آن‌ها) داریم.');
INSERT INTO "hussein_taji" VALUES(43,80,'43:80','آیا آن‌ها می‌پندارند که ما راز گویی و (سخنان) در گوشی آنان را نمی‌شنویم؟! آری، (می شنویم) و فرستادگان ما (از فرشتگان) نزد آن‌ها (هستند) و می‌نویسند.');
INSERT INTO "hussein_taji" VALUES(43,81,'43:81','(ای پیامبر، به مشرکان) بگو: «اگر (به فرض محال) برای (الله) رحمان فرزندی بود، پس من نخستین پرستندگانش بودم».');
INSERT INTO "hussein_taji" VALUES(43,82,'43:82','پاک و منزه است پروردگار آسمان‌ها و زمین، پروردگار عرش، از آنچه آن‌ها توصیف می‌کنند.');
INSERT INTO "hussein_taji" VALUES(43,83,'43:83','پس (ای پیامبر) آن‌ها را (به حال خود) رها کن، تا (در باطل) غوطه بخورند، و به بازی سرگرم باشند، تا روزی را که به آن‌ها وعده شده است، ملاقات کنند (و سزای اعمال خود را ببینند).');
INSERT INTO "hussein_taji" VALUES(43,84,'43:84','و او کسی است که در آسمان معبود است، و در زمین (نیز) معبود است، و او حکیم داناست.');
INSERT INTO "hussein_taji" VALUES(43,85,'43:85','و پر برکت و بزرگوار است کسی‌که فرمانروایی آسمان‌ها و زمین و آنچه در میان آن دواست، از آنِ اوست. و علم (بر‌پا شدن) قیامت نزد اوست، و (همه) به سوی او باز گردانده می‌شوید.');
INSERT INTO "hussein_taji" VALUES(43,86,'43:86','و کسانی را که به جای او می‌خوانند، اختیار شفاعت ندارند، مگر کسانی‌که به حق گواهی دادند، و آنان (به خوبی) می‌دانند.');
INSERT INTO "hussein_taji" VALUES(43,87,'43:87','و اگر از آن‌ها (= مشرکان) بپرسی، چه کسی آن‌ها را آفریده است، قطعاً می‌گویند: «الله» پس چگونه (از حق) منحرف می‌شوند؟!');
INSERT INTO "hussein_taji" VALUES(43,88,'43:88','و (شکایت و) گفتار او (= پیامبر) (این بود:) «پروردگارا! بی‌گمان این‌ها قومی هستند که ایمان نمی‌آورند».');
INSERT INTO "hussein_taji" VALUES(43,89,'43:89','پس (ای پیامبر) از آن‌ها در گذر، و بگو: «سلام (بر شما)». پس به زودی خواهند دانست [ این آیات پیش از فرض شدن جهاد نازل شده است. (تفسیر طبری و ابن کثیر).].');
INSERT INTO "hussein_taji" VALUES(44,1,'44:1','حم (حا. میم).');
INSERT INTO "hussein_taji" VALUES(44,2,'44:2','سوگند به کتاب روشنگر.');
INSERT INTO "hussein_taji" VALUES(44,3,'44:3','بی‌گمان ما آن را در شب مبارک (و فرخندۀ قدر) نازل کردیم، بی‌شک ما (همواره) هشدار دهنده بوده‌ایم.');
INSERT INTO "hussein_taji" VALUES(44,4,'44:4','در آن شب هر امر با حکمت (و استواری) فیصله (و تدبیر) می‌شود [ آنچه از روزی و عمر و مصیبت، برای انسان‌ها فیصله شده است، و در لوح محفوظ ثبت است، به فرشتگان سپرده می‌شود. (تفسیر ابن کثیر).].');
INSERT INTO "hussein_taji" VALUES(44,5,'44:5','فرمانی از جانب ما (بود)، بی‌گمان ما فرستندۀ (آن) بوده‌ایم.');
INSERT INTO "hussein_taji" VALUES(44,6,'44:6','رحمتی است از سوی پروردگارت، بی‌گمان او ست که شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(44,7,'44:7','پروردگار آسمان‌ها و زمین و آنچه در میان آن دو است، اگر شما یقین دارید.');
INSERT INTO "hussein_taji" VALUES(44,8,'44:8','هیچ معبودی (به حق) جز او نیست، زنده می‌کند و می‌میراند، پروردگار شما و پروردگار پدران نخستین شماست.');
INSERT INTO "hussein_taji" VALUES(44,9,'44:9','بلکه، آن‌ها در شک اند (و) بازی می‌کنند.');
INSERT INTO "hussein_taji" VALUES(44,10,'44:10','پس (ای پیامبر) منتظر روزی باش که آسمان دودی آشکار (پدید) آورد.');
INSERT INTO "hussein_taji" VALUES(44,11,'44:11','که (تمام) مردم را فرا می‌گیرد، این عذابی دردناک است.');
INSERT INTO "hussein_taji" VALUES(44,12,'44:12','(آنگاه کافران می‌گویند:) «پروردگارا! (این) عذاب را از ما برطرف کن، بی‌گمان ما ایمان می‌آوریم».');
INSERT INTO "hussein_taji" VALUES(44,13,'44:13','چگونه (و از کجا) پند می‌پذیرند در حالی‌که پیامبر روشنگر برایشان آمد!');
INSERT INTO "hussein_taji" VALUES(44,14,'44:14','سپس از او روی گرداندند، و گفتند: «(او) آموزش یافته‌ای دیوانه است».');
INSERT INTO "hussein_taji" VALUES(44,15,'44:15','بی‌گمان ما اندکی عذاب را برطرف می‌کنیم، (ولی) شما (به کفر و اعمال زشت خود) باز می‌گردید.');
INSERT INTO "hussein_taji" VALUES(44,16,'44:16','روزی‌که به گرفتنی سخت (آن‌ها را) فروگیریم، بی‌گمان ما انتقام گیرنده‌ایم.');
INSERT INTO "hussein_taji" VALUES(44,17,'44:17','و به راستی ما پیش از آن‌ها قوم فرعون را آزمودیم، و پیامبری بزرگوار به سوی‌شان آمد.');
INSERT INTO "hussein_taji" VALUES(44,18,'44:18','(و به آن‌ها گفت:) که «بندگان الله (= بنی اسرائیل) را به من و ا گذارید، بی‌گمان من برای شما فرستادۀ امینی هستم.');
INSERT INTO "hussein_taji" VALUES(44,19,'44:19','و (نیز) بر الله برتری مجویید، بی‌تردید من برای شما دلیل روشنی آورده ام.');
INSERT INTO "hussein_taji" VALUES(44,20,'44:20','و همانا من به پروردگار خود و پروردگار شما پناه می‌برم، از اینکه مرا سنگسار کنید.');
INSERT INTO "hussein_taji" VALUES(44,21,'44:21','و اگر به من ایمان نمی‌آورید، پس از من کناره بگیرید.');
INSERT INTO "hussein_taji" VALUES(44,22,'44:22','آنگاه به درگاه پروردگارش دعا کرد که: این‌ها گروهی مجرمند.');
INSERT INTO "hussein_taji" VALUES(44,23,'44:23','پس (به او حکم داده شد:) بندگان مرا شبانه ببر، یقیناً شما تعقیب می‌شوید.');
INSERT INTO "hussein_taji" VALUES(44,24,'44:24','و دریا را آرام وا گذار، بی‌گمان آن‌ها لشکری غرق شده خواهند بود.');
INSERT INTO "hussein_taji" VALUES(44,25,'44:25','چه بسیار باغ‌ها و چشمه‌ها را (بعد از غرق شدن‌شان) رها کردند.');
INSERT INTO "hussein_taji" VALUES(44,26,'44:26','و (نیز) زراعت‌ها و قصر‌های نیکو (و زیبا).');
INSERT INTO "hussein_taji" VALUES(44,27,'44:27','و نعمتی که در آن (شادمان) بودند.');
INSERT INTO "hussein_taji" VALUES(44,28,'44:28','اینچنین بود، و ما (همۀ) آن‌ها را به مردمی ‌دیگر وا گذاشتیم.');
INSERT INTO "hussein_taji" VALUES(44,29,'44:29','پس آسمان و زمین بر آن‌ها نگریست، و (به آن‌ها) مهلت داده نشد.');
INSERT INTO "hussein_taji" VALUES(44,30,'44:30','و مسلماً بنی اسرائیل را از عذاب ذلت بار نجات دادیم.');
INSERT INTO "hussein_taji" VALUES(44,31,'44:31','از فرعون، که او سرکشی از اسراف‌کاران بود.');
INSERT INTO "hussein_taji" VALUES(44,32,'44:32','و به راستی ما آن‌ها را با علم (و آگاهی) بر جهانیان (زمان‌شان) برگزیدیم.');
INSERT INTO "hussein_taji" VALUES(44,33,'44:33','و از نشانه‌ها آنچه به آن‌ها دادیم که آزمایش آشکاری در آن بود.');
INSERT INTO "hussein_taji" VALUES(44,34,'44:34','بی‌گمان این‌ها (= مشرکان) می‌گویند:');
INSERT INTO "hussein_taji" VALUES(44,35,'44:35','«سرانجام جز همین مرگ نخستین ما نیست، و ما هرگز بر انگیخته نخواهیم شد.');
INSERT INTO "hussein_taji" VALUES(44,36,'44:36','پس اگر راست می‌گویید: نیاکان ما را (زنده کنید و) بیاورید».');
INSERT INTO "hussein_taji" VALUES(44,37,'44:37','آیا آن‌ها بهترند یا قوم «تبّع» و کسانی‌که پیش از آن‌ها بودند؟ ما آن‌ها را هلاک نمودیم، بی‌گمان آن‌ها مجرم بودند.');
INSERT INTO "hussein_taji" VALUES(44,38,'44:38','و (ما) آسمان‌ها و زمین و آنچه را که در میان آن دو است، به بازیچه نیافریده‌ایم.');
INSERT INTO "hussein_taji" VALUES(44,39,'44:39','و (ما) آن دو را جز به حق نیافریدیم، و لیکن بیشتر آن‌ها نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(44,40,'44:40','بی‌گمان روز داوری و جدایی (= روز قیامت) وعده‌گاه همۀ آن‌هاست.');
INSERT INTO "hussein_taji" VALUES(44,41,'44:41','روزی‌که هیچ دوستی برای دوست خود سودمند نباشد، و آن‌ها (از سوی کسی) یاری نمی‌شوند.');
INSERT INTO "hussein_taji" VALUES(44,42,'44:42','مگر کسی‌که الله اورا مورد رحمت قرار داده، یقیناً او پیروزمند مهربان است.');
INSERT INTO "hussein_taji" VALUES(44,43,'44:43','بی‌گمان درخت زقوم.');
INSERT INTO "hussein_taji" VALUES(44,44,'44:44','غذای گناهکاران است.');
INSERT INTO "hussein_taji" VALUES(44,45,'44:45','همانند مس گداخته در شکم‌ها می‌جوشد.');
INSERT INTO "hussein_taji" VALUES(44,46,'44:46','جوششی همچون آب جوشان.');
INSERT INTO "hussein_taji" VALUES(44,47,'44:47','(آنگاه به فرشتگان می‌گوییم:) او را بگیرید، پس به سختی او را میان دوزخ بکشید.');
INSERT INTO "hussein_taji" VALUES(44,48,'44:48','سپس بر سرش از عذاب جوشان بریزید.');
INSERT INTO "hussein_taji" VALUES(44,49,'44:49','(و به او می‌گوییم:) بچش که تو (به پندار خود) پیروزمند بزرگواری.');
INSERT INTO "hussein_taji" VALUES(44,50,'44:50','بی‌گمان این (همان) چیزی است که در آن شک داشتید.');
INSERT INTO "hussein_taji" VALUES(44,51,'44:51','یقیناً پرهیزگاران در جایگاه امنی هستند.');
INSERT INTO "hussein_taji" VALUES(44,52,'44:52','در (میان) باغ‌ها و (کنار) چشمه‌ها.');
INSERT INTO "hussein_taji" VALUES(44,53,'44:53','(لباس‌هایی) از دیبای نازک و دیبای ضخیم می‌پوشند، رو بروی هم می‌نشینند.');
INSERT INTO "hussein_taji" VALUES(44,54,'44:54','بدین‌سان (است) و حوریان گشاده چشم (بهشتی) را به همسری آن‌ها در می‌آوریم.');
INSERT INTO "hussein_taji" VALUES(44,55,'44:55','در آن (بهشت) با امن (و آرامش خاطر) هر میوه‌ای را (که بخواهند) می‌طلبند.');
INSERT INTO "hussein_taji" VALUES(44,56,'44:56','در آن (بهشت) مرگ را نمی‌چشند، مگر همان مرگ نخستین (که در دنیا چشیده‌اند) و (الله) آن‌ها را از عذاب دوزخ حفظ کند.');
INSERT INTO "hussein_taji" VALUES(44,57,'44:57','(این) بخششی است از (سوی) پروردگارت، این همان کامیابی بزرگ است.');
INSERT INTO "hussein_taji" VALUES(44,58,'44:58','پس (ای پیامبر) ما آن (= قرآن) را به زبان تو آسان کردیم، تا که آن‌ها پند گیرند.');
INSERT INTO "hussein_taji" VALUES(44,59,'44:59','پس (ای پیامبر) منتظر (وعده‌های الهی) باش، بی‌گمان آن‌ها (نیز) منتظرند.');
INSERT INTO "hussein_taji" VALUES(45,1,'45:1','حم (حا. میم).');
INSERT INTO "hussein_taji" VALUES(45,2,'45:2','نازل شدن (این) کتاب از سوی الله پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(45,3,'45:3','یقیناً در آسمان‌ها و زمین نشانه‌های برای مومنان است.');
INSERT INTO "hussein_taji" VALUES(45,4,'45:4','و (نیز) در آفرینش شما، و (در) آنچه از جنبندگان پراکنده می‌سازد، نشانه‌های است برای گروهی که یقین دارند.');
INSERT INTO "hussein_taji" VALUES(45,5,'45:5','و (نیز در) آمد و شد شب و روز، و رزقی (= بارانی) که الله از آسمان نازل کرده پس بوسیلۀ آن زمین را بعد از مردنش زنده گرداند و (نیز در) وزش باد‌ها، نشانه‌های است برای گروهی که خرد می‌ورزند.');
INSERT INTO "hussein_taji" VALUES(45,6,'45:6','این‌ها آیات الله است که ما آن‌ها را به حق، بر تو می‌خوانیم، پس به کدام سخن بعد (از سخن) الله، و آیات او ایمان می‌آورند؟!');
INSERT INTO "hussein_taji" VALUES(45,7,'45:7','وای بر هر دروغگوی گنهکار.');
INSERT INTO "hussein_taji" VALUES(45,8,'45:8','که آیات الله را که بر او خوانده می‌شود، می‌شنود، سپس متکبرانه اصرار (بر کفر) دارد، گویی که آن را نشینده است، پس او را به عذابی دردناک بشارت ده.');
INSERT INTO "hussein_taji" VALUES(45,9,'45:9','و هرگاه چیزی از آیات ما را فراگیرد، آن را به مسخره می‌گیرد، اینان برایشان عذاب خوارکننده‌ای است.');
INSERT INTO "hussein_taji" VALUES(45,10,'45:10','از پشت سر‌شان جهنم است، و آنچه را بدست آورده‌اند به حال‌شان هیچ سودمند نخواهد بود، و نه آنچه به جای الله (برای خود) دوست (و معبود) بر گزیده‌اند (آن‌ها را نجات می‌دهد) و برای آن‌ها عذاب بزرگی است.');
INSERT INTO "hussein_taji" VALUES(45,11,'45:11','این (قرآن) هدایت است، و کسانی‌که به آیات پروردگار‌شان کافر شدند، برایشان عذابی از سخت‌ترین عذاب‌های درد آور است.');
INSERT INTO "hussein_taji" VALUES(45,12,'45:12','الله (همان) کسی است که دریا را برای شما مسخر کرد، تا کشتی‌ها به فرمان او در آن روان باشند، و (نیز) تا شما (با سفر و تجارت) از فضل او (روزی) بجویید، و باشد که شما سپاسگزارید.');
INSERT INTO "hussein_taji" VALUES(45,13,'45:13','و آنچه را که در آسمان‌ها و آنچه را که در زمین است همگی از آن اوست که برای شما مسخّر (و رام) کرد، بی‌گمان در این نشانه‌های است برای گروهی‌که اندیشه می‌کنند.');
INSERT INTO "hussein_taji" VALUES(45,14,'45:14','(ای پیامبر) به مؤمنان بگو: «از (خطای) کسانی‌که به ایام الله (= روزهای گرفت و پیروزی ایمان بر کفر) امید ندارند، در گذرند، تا (الله) هر گروهی را به (سبب) آنچه که انجام می‌دادند، سزا دهد».');
INSERT INTO "hussein_taji" VALUES(45,15,'45:15','هرکس کار شایسته‌ای انجام دهد، پس به سود خود اوست، و کسی‌که مرتکب کار بدی شود، پس به زیان خود اوست، آنگاه (همه) به سوی پروردگارتان باز گردانده می‌شوید.');
INSERT INTO "hussein_taji" VALUES(45,16,'45:16','و یقیناً ما بنی اسرائیل را کتاب (آسمانی) و حکومت و نبوت بخشیدیم، و از پاکیزه‌ها روزیشان دادیم، و آن‌ها را بر جهانیان (زمانشان) برتری دادیم.');
INSERT INTO "hussein_taji" VALUES(45,17,'45:17','و آن‌ها را در امر (دین) نشانه‌های روشنی عطا کردیم، پس آن‌ها اختلاف نکردند مگر بعد از آنکه برای آنان علم و دانش آمد، (آن هم) از روی تجاوز (و ستم) به یکدیگر بود. بی‌گمان پروردگارتو روز قیامت در آنچه اختلاف می‌کردند در میان آن‌ها داوری می‌کند.');
INSERT INTO "hussein_taji" VALUES(45,18,'45:18','سپس تو را (ای پیامبر) بر راه (و شریعتی) روشن از دین قرار دادیم. پس از آن پیروی کن، و از (هوی و) هوس‌های کسانی‌که نمی‌دانند پیروی نکن.');
INSERT INTO "hussein_taji" VALUES(45,19,'45:19','بی‌شک آن‌ها هرگز نمی‌توانند تو را از الله بی‌نیاز کنند، (و عذاب اورا از تو دفع نمایند) و مسلماً ستمکاران برخی شان یاور برخی دیگراند، و الله یاور پرهیزگاران است.');
INSERT INTO "hussein_taji" VALUES(45,20,'45:20','این (قرآن) برای مردم مایۀ بصیرت است، و برای گروهی که یقین دارند، هدایت و رحمت است.');
INSERT INTO "hussein_taji" VALUES(45,21,'45:21','آیا کسانی‌که مرتکب بدیها شدند، گمان کردند که (ما) آن‌ها را همچون کسانی قرار می‌دهیم که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، که زندگی شان و مرگشان یکسان باشد؟! چه بد داوری می‌کنند!');
INSERT INTO "hussein_taji" VALUES(45,22,'45:22','و الله آسمان‌ها و زمین را به حق آفریده است، و تا هرکس را در برابر آنچه که انجام داده است پاداش دهد، و به آن‌ها ستمی نخواهد شد.');
INSERT INTO "hussein_taji" VALUES(45,23,'45:23','آیا دیده‌ای کسی را که معبود خود را هوای (نفسانی) خویش قرار داد، و الله از روی علم او را گمراه کرد، و بر گوشش و دلش مهر نهاد، و بر دیده اش پرده قرار داد؟! پس (اگر الله هدایتش نکند (چه کسی بعد از الله اورا هدایت خواهد کرد؟ آیا پند نمی‌گیرید؟!');
INSERT INTO "hussein_taji" VALUES(45,24,'45:24','و (مشرکان) گفتند: «چیزی جز (این) زندگانی دنیای ما، (در کار) نیست، می‌میریم و زنده می‌شویم، و جز طبیعت (و روزگار) ما را هلاک نمی‌سازد» و آن‌ها به این (امر) هیچ علمی ندارند، و آن‌ها جز (وهم و) گمان می‌کنند.');
INSERT INTO "hussein_taji" VALUES(45,25,'45:25','و هنگامی‌که آیات روشن ما بر آن‌ها خوانده شود، حجتشان جز این نیست که گویند: «اگر راستگویید، نیاکان ما را (زنده کنید و) بیاورید».');
INSERT INTO "hussein_taji" VALUES(45,26,'45:26','(ای پیامبر) بگو: «الله شما را زنده می‌کند، سپس شما را می‌میراند، باز شما را در روز قیامت ـ که تردیدی در آن نیست ـ جمع می‌کند. و لیکن بیشتر مردم نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(45,27,'45:27','و فرمانروایی آسمان‌ها و زمین از آنِ الله است، و روزی‌که قیامت بر پا شود، اهل باطل زیان خواهند کرد.');
INSERT INTO "hussein_taji" VALUES(45,28,'45:28','و (در آن روز) هر امتی را می‌بینی که به زانو در آمده، هر امتی به سوی نامۀ اعمالش خوانده می‌شود، (و به آن‌ها گفته می‌شود:) «امروز در برابر آنچه می‌کردید، پاداش می‌یابید».');
INSERT INTO "hussein_taji" VALUES(45,29,'45:29','این کتاب ماست که به حق بر علیه شما سخن می‌گوید (و گواهی می‌دهد) البته ما آنچه را که می‌کردید، می‌نوشتیم.');
INSERT INTO "hussein_taji" VALUES(45,30,'45:30','پس اما کسانی‌که ایمان آوردند و کارهای شایسته انجام دادند، پروردگارشان آن‌ها در رحمت خود (= بهشت) داخل می‌کند، این همان کامیابی بزرگ است.');
INSERT INTO "hussein_taji" VALUES(45,31,'45:31','و اما کسانی‌که کافر شدند، (به آن‌ها گفته می‌شود:) آیا آیات من بر شما خوانده نمی‌شد، پس شما تکبر کردید، و قومی مجرم بودید؟!');
INSERT INTO "hussein_taji" VALUES(45,32,'45:32','و چون گفته می‌شود: «بی‌گمان و عدۀ الله حق است، و (در آمدن) قیامت هیچ شکی نیست» می‌گفتید: «ما نمی‌دانیم قیامت چیست؟ ما فقط گمانی داریم، و ما (دربارۀ آن) یقین کننده نیستیم».');
INSERT INTO "hussein_taji" VALUES(45,33,'45:33','و بدیهای آنچه کرده‌اند، برای آنان آشکار شد، و آنچه که مسخره اش می‌کردند آن‌ها را فرو گرفت.');
INSERT INTO "hussein_taji" VALUES(45,34,'45:34','و (به آن‌ها) گفته می‌شود: «امروز شما را فراموش می‌کنیم، همان گونه که (شما) دیدار امروزتان را فراموش کردید، و جایگاه شما آتش (دوزخ) است، و شما هیچ یاوری ندارید.');
INSERT INTO "hussein_taji" VALUES(45,35,'45:35','این بدان سبب است که شما آیات الله را به مسخره گرفتید، و زندگی دنیا شما را فریفت». پس امروز نه آن‌ها از آن (دوزخ) بیرون آورده می‌شوند، و نه هیچ عذری از آن‌ها پذیرفته می‌شود.');
INSERT INTO "hussein_taji" VALUES(45,36,'45:36','پس ستایش مخصوص الله است، پروردگار آسمان‌ها و پروردگار زمین، پروردگار جهانیان.');
INSERT INTO "hussein_taji" VALUES(45,37,'45:37','و کبریاء و بزرگی در آسمان‌ها و زمین، از آنِ اوست، و او پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(46,1,'46:1','حم،');
INSERT INTO "hussein_taji" VALUES(46,2,'46:2','نازل شدن (این) کتاب از سوی الله پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(46,3,'46:3','ما آسمان‌ها و زمین و آنچه را که در میان آن دواست جز به حق و (برای) مدتی معین نیافریده‌ایم. وکسانی‌که کافر شدند از آنچه بیم شان می‌دهند روی گردانند.');
INSERT INTO "hussein_taji" VALUES(46,4,'46:4','(ای پیامبر، به آن‌ها) بگو: «آیا دیده‌اید آنچه را که جز الله می‌خوانید، به من نشان دهید چه چیزی از زمین را آفریده‌اند؟ یا اینکه آن‌ها در (آفرینش) آسمان‌ها شرکت داشته‌اند؟! اگر راست می‌گویید کتابی (آسمانی) پیش از این (قرآن)، یا اثری از علم (گذشتگان، بر صدق ادعای خود) برای من بیاورید».');
INSERT INTO "hussein_taji" VALUES(46,5,'46:5','و چه کسی گمراه‌تراست از آن که (معبودی) غیر از الله را می‌خواند که تا روز قیامت (هم دعای) او را اجابت نکند، و آن‌ها (= معبودان باطل) از خواندن (و دعای) ایشان (کاملاً) بی‌خبرند؟!');
INSERT INTO "hussein_taji" VALUES(46,6,'46:6','وهنگامی‌که مردم (در قیامت) گرد آورده شوند، آن‌ها (معبودان باطل) دشمنانشان خواهند بود، و عبادت شان را انکار می‌کنند [ سورۀ بقره آیه ملاحظه فرمائید.].');
INSERT INTO "hussein_taji" VALUES(46,7,'46:7','و هنگامی‌که آیات روشن ما بر آن‌ها خوانده شود، کسانی‌که کافر شدند، در برابر حقی که برای آن‌ها آمده است گویند: «این جادویی آشکار است».');
INSERT INTO "hussein_taji" VALUES(46,8,'46:8','یا می‌گویند: «(پیامبر خود) آن را بافته (و به الله نسبت داده) است». (ای پیامبر) بگو: «اگر من آن را بافته (و به الله نسبت داده) باشم پس شما نمی‌توانید در برابر (عذاب و خشم) الله از من دفاع کنید، (تنها) او به آنچه شما دربارۀ آن (= قرآن می‌گویید و گفتگو می‌کنید) آگاه‌تراست، و گواهی او (= الله) میان من و شما کافی است، و او آمرزندۀ مهربان است».');
INSERT INTO "hussein_taji" VALUES(46,9,'46:9','(ای پیامبر) بگو: «من از میان پیامبران (و جودی) نو ظهور نیستم. و نمی دانم که با من و شما چگونه رفتار خواهد شد، من تنها از چیزی پیروی می‌کنم که به من وحی می‌شود، و جز بیم‌دهندۀ آشکاری نیستم».');
INSERT INTO "hussein_taji" VALUES(46,10,'46:10','(ای پیامبر) بگو: «به من خبر دهید اگر (این قرآن) از سوی الله باشد، و شما به آن کافر شوید، و شاهدی از بنی اسرائیل بر (نشانه‌های) آن (در تورات) شهادت داد پس (او) ایمان آورد، و شما سرکشی (و تکبر) می‌وزرید (چه بر سر شما خواهد آمد؟) مسلماً الله گروه ستمگر را هدایت نمی‌کند».');
INSERT INTO "hussein_taji" VALUES(46,11,'46:11','و کسانی‌که کافر شدند به کسانی‌که ایمان آوردند گفتند: «اگر (این دین) بهتر بود، هرگز آن‌ها (در پذیرش آن) بر ما پیشی نمی‌گرفتند». و چون بدان هدایت نشدند، خواهند گفت: «این (یک) دروغ قدیم است».');
INSERT INTO "hussein_taji" VALUES(46,12,'46:12','و پیش از آن، کتاب موسی پیشوا و رحمت بود، و این (قرآن) کتابی است تصدیق کنندۀ (آن) که به زبان عربی است، تا کسانی‌که ستم کردند بیم دهد و برای نیکوکاران بشارتی باشد.');
INSERT INTO "hussein_taji" VALUES(46,13,'46:13','بی‌گمان کسانی‌که گفتند: «پروردگار ما الله است، سپس استقامت کردند، پس بر آن‌ها ترسی نیست و نه آن‌ها اندوهگین شوند.');
INSERT INTO "hussein_taji" VALUES(46,14,'46:14','آن‌ها اهل بهشتند، جاودانه در آن باشند، (این) پاداش اعمالی است که انجام می‌دادند.');
INSERT INTO "hussein_taji" VALUES(46,15,'46:15','ما به انسان سفارش کردیم که به پدر و مادرش نیکی کند، مادرش او را به دشواری حمل می‌کند، و به دشواری بر زمین گذارد، و دوران حمل او و از شیر باز گرفتن او سی ماه است [ در سورۀ بقره مدت رضاعت را دو سال بیان کرده است، و در اینجا مدت بارداری و رضاعت ماه بیان شده است، لذا فقهاء و دانشمندان اسلامی می‌گویند: کمترین مدت بار داری شش ماه می‌باشد.]، تا هنگامی‌که به کمال (توانمندی و) رشدش برسد، و به (سن) چهل سالگی برسد، گوید: «پروردگارا! مرا توفیق ده تا شکر نعمتت را که بر من و بر پدر و مادرم ارزانی داشته‌ای به جای آورم و کار شایسته‌ای انجام دهم که تو از آن خشنود شوی، و فرزندانم را صالح گردان، بی‌گمان من به سوی تو بازگشتم (و توبه نمودم)، و بی‌شک من از مسلمانانم».');
INSERT INTO "hussein_taji" VALUES(46,16,'46:16','آن‌ها کسانی هستند که ما نیکوترین کارشان را می‌پذیریم، و از گناهانشان می‌گذریم و در زمرۀ اهل بهشت هستند، این و عدۀ راستی است که وعده داده می‌شدند.');
INSERT INTO "hussein_taji" VALUES(46,17,'46:17','و کسی‌که به پدر و مادرش گفت: «اف بر شما! آیا شما به من وعده‌ای می‌دهید که (از گورم) بیرون آورده می‌شوم؟ در حالی‌که نسلها پیش از من گذشته‌اند؟! (و هرگز برانگیخته نشدند) و آن دو (= پدر و مادر) الله را به یاری می‌طلبند که: «وای برتو! ایمان بیاور، یقیناً و عدۀ الله حق است»، پس (او) می‌گوید: «این‌ها چیزی جز افسانه‌های پیشینیان نیست».');
INSERT INTO "hussein_taji" VALUES(46,18,'46:18','آن‌ها کسانی هستند که همان سخن (= وعدۀ عذاب) در (زمرۀ) امتهایی که پیش از آن‌ها از جن و انس گذشته‌اند، بر آن‌ها محقق شده است، بی‌گمان آن‌ها زیانکار بودند.');
INSERT INTO "hussein_taji" VALUES(46,19,'46:19','و برای هر یک (از آن‌ها) درجاتی است به (حسب) آنچه انجام داده‌اند، و تا الله (پاداش) کارهایشان را به تمامی به آن‌ها بدهد، و به آن‌ها هیچ ستمی نخواهد شد.');
INSERT INTO "hussein_taji" VALUES(46,20,'46:20','و روزی‌که کسانی‌که کافر شدند بر آتش عرضه کنند، (و به آن‌ها گفته شود:) «در زندگی دنیا (نعمت‌های) پاکیزه (و لذت‌های) خود را از بین بردید و از آن‌ها بهره‌مند شدید، پس (ای کافران) امروز به عذاب خوار کننده پاداشتان می‌دهند، (و این) به (خاطر) گردنکشی (و استکباری) که به نا حق در زمین می‌کردید، و به (خاطر) نافرمانی (و گناهانی) که انجام می‌دادید.');
INSERT INTO "hussein_taji" VALUES(46,21,'46:21','(ای پیامبر، داستان هود) برادر (قوم) عاد، را به یاد آور که چون قومش را در (سرزمین) «احقاف» (= ریگستان‌ها) هشدار داد، ـ و به راستی هشدار دهندگانی پیش از او و بعد از او گذشته بودند ـ که: جز الله را نپرستید، بی‌گمان من از عذاب روزی بزرگ بر شما می‌ترسم.');
INSERT INTO "hussein_taji" VALUES(46,22,'46:22','(آن‌ها) گفتند: «آیا آمده‌ای تا ما را از (پرستش) معبودهایمان برگردانی، پس اگر از راستگویانی آنچه به ما وعده داده‌ای برایمان بیاور».');
INSERT INTO "hussein_taji" VALUES(46,23,'46:23','(هود) گفت: «علم (تحقق آن وعده‌ها) تنها نزد الله است، و من آنچه را که به آن فرستاده شده ام به شما می‌رسانم، و لیکن من شما را قومی می‌بینم که (پیوسته) نادانی می‌کنید».');
INSERT INTO "hussein_taji" VALUES(46,24,'46:24','پس هنگامی‌که آن (عذاب) را به صورت ابری که رو به راه و ادیهایشان است، دیدند، (خوشحال شدند) گفتند: «این ابری است که بر ما (باران) می‌بارد». (هود گفت: نه،) بلکه این همان (عذابی) است که در آمدنش شتاب می‌کردید، بادی است که عذاب دردناکی در آن است.');
INSERT INTO "hussein_taji" VALUES(46,25,'46:25','همه چیز را به فرمان پروردگارش (ویران و) نا بود می‌کند، پس چنان شدند که جز خانه‌هایشان دیده نمی‌شد، ما این گونه گروه مجرمان را کیفر می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(46,26,'46:26','به راستی ما به آن‌ها (= قوم عاد) چنان (تمکّن و) قدرتی دادیم که به شما آن قدرت (و تمکن) نداده‌ایم و برای آن‌ها گوش و چشمها و دلها قرار دادیم. پس گوش‌هایشان و نه چشم‌هایشان و نه دل‌هایشان چیزی (از عذاب الهی) را از آن‌ها دفع نکرد (و به حالشان سودی نبخشید)، چون آیات الله را انکار می‌کردند، و (سرانجام) آنچه را که به (مسخره و) ریشخند می‌گرفتند آن‌ها را فرو گرفت.');
INSERT INTO "hussein_taji" VALUES(46,27,'46:27','و به راستی ما قریه‌ها (و آبادیها) یی را که اطراف شما بوده‌اند هلاک کردیم، و آیات (خود) را (به صورتهای) گوناگون (برای آن‌ها) بیان کردیم، تا شاید باز گردند.');
INSERT INTO "hussein_taji" VALUES(46,28,'46:28','پس چرا کسانی را که آن‌ها به جای الله برای (قصد) تقرب (به خدا) معبود (خود) گرفتند، یاریشان نکردند؟ بلکه از نظرشان گم شدند، و این بود (نتیجۀ) دروغشان، و آنچه را افترا می‌بستند.');
INSERT INTO "hussein_taji" VALUES(46,29,'46:29','و (بیاد آور، ای پیامبر) هنگامی‌که گروهی از جن را به سوی تو متوجه ساختیم که قرآن را بشنوند، پس چون نزد او (= پیامبر) حضور یافتند، (به یکدیگر) گفتند: «خاموش باشید» آنگاه چون (تلاوت قرآن) به پایان رسید، (همچون) بیم دهندگانی به سوی قوم خود بازگشتند.');
INSERT INTO "hussein_taji" VALUES(46,30,'46:30','گفتند: «ای قوم ما! یقیناً ما کتابی را شنیدیم که بعد از موسی نازل شده است که تصدیق کنندۀ (کتابهای) پیش از خود است، به سوی حق و به سوی راه راست هدایت می‌کند.');
INSERT INTO "hussein_taji" VALUES(46,31,'46:31','ای قوم ما! (دعوت، این) دعوت‌کنندۀ الله را اجابت کنید، و به او ایمان آورید تا (الله) برخی از گناهانتان را برای شما بیامرزد، و شما را از عذابی دردناک پناه دهد.');
INSERT INTO "hussein_taji" VALUES(46,32,'46:32','و هرکس (دعوت) دعوت کنندۀ الله را پاسخ نگوید، پس هرگز نمی‌تواند در زمین (از عذاب خدا) بگریزد و او را غیر از الله هیچ یاوری نیست، آنان در گمراهی آشکاری هستند».');
INSERT INTO "hussein_taji" VALUES(46,33,'46:33','آیا ندیدند اللهى که آسمان‌ها و زمین را آفریده است، و در آفرینش آن‌ها (ناتوان و) در مانده نشده، قادر است بر آنکه مردگان را زنده کند؟! آری، او بر هر چیزی تواناست.');
INSERT INTO "hussein_taji" VALUES(46,34,'46:34','و (روز قیامت) روزی (است) که کسانی‌که کافر شدند (برای عذاب) بر آتش عرضه می‌شوند، (و به آن‌ها گفته می‌شود:) «آیا این (عذاب) حق نیست؟» گویند: «چرا، سوگند به پروردگارمان (که حق است)». گوید: «پس به خاطر کفری که می‌ورزیدید عذاب را بچشید».');
INSERT INTO "hussein_taji" VALUES(46,35,'46:35','پس (ای پیامبر) صبر کن، همچنانکه پیامبران اولو العزم صبر کردند [ پیامبران اولو العزم پنج تن هستند: ـ نوح 2 ـ ابراهیم 3 ـ موسی 4 ـ عیسی 5 ـ محمد خاتم الانبیاء علیهم السلام . (تفسیر ابن کثیر)]، و برای (عذاب) آن‌ها شتاب نکن، روزی‌که آنچه را که وعده داده می‌شوند ببینند، گویی جز ساعتی از روز (در دنیا) درنگ نکرده‌اند، این (قرآن) ابلاغی است، پس آیا (قومی) جز قوم فاسق (و نافرمان) هلاک می‌شوند؟!');
INSERT INTO "hussein_taji" VALUES(47,1,'47:1','کسانی‌که کافر شدند و (مردم را) از راه الله بازداشتند، (الله) اعمال آنان را نابود کرد.');
INSERT INTO "hussein_taji" VALUES(47,2,'47:2','و کسانی‌که ایمان آوردند و کارهای شایسته انجام دادند و به آنچه بر محمد ( صلی الله علیه وسلم) نازل شده ـ و همه از سوی پروردگارشان حق است ـ (نیز) ایمان آوردند، (الله) گناهان‌شان را از آن‌ها دور ساخت (و بخشید) و کار‌‌شان را اصلاح نمود.');
INSERT INTO "hussein_taji" VALUES(47,3,'47:3','این بدان سبب است که کسانی‌که کافر شدند از باطل پیروی کردند، و همانا کسانی‌که ایمان آوردند از حقی که از سوی پروردگار‌شان (نازل شده) است پیروی کردند، این گونه الله برای مردم مثل‌هایشان را بیان می‌کند.');
INSERT INTO "hussein_taji" VALUES(47,4,'47:4','پس هنگامی‌که (در میدان نبرد) با کسانی‌که کفر ورزیدند، روبرو شدید گردن‌ها (یشان) را بزنید، (و نبرد را ادامه دهید) تا هنگامی‌که (بسیاری از) آن‌ها را از پای در آوردید و) کشتید، پس بند (اسیران) را محکم ببندید، آنگاه پس از آن یا بر آنان منت گذارید (و آزاد‌شان کنید) یا (از آنان) فدیه بگیرید (و رهای‌شان کنید) تا جنگ بار سنگین خود را (بر زمین) نهد، (حکم) این است، و اگر الله می‌خواست از آن‌ها انتقام می‌گرفت و لیکن (می خواهد) بعضی از شما را با بعضی دیگر بیازماید، و کسانی‌که در راه الله کشته شدند، پس (الله) هرگز اعمال آن‌ها را نابود نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(47,5,'47:5','به زودی آن‌ها را هدایت می‌نماید و (حال و) کارشان را اصلاح می‌کند.');
INSERT INTO "hussein_taji" VALUES(47,6,'47:6','و آن‌ها را به بهشتی که (اوصافش) برای آن‌ها بیان داشته است، وارد می‌کند.');
INSERT INTO "hussein_taji" VALUES(47,7,'47:7','ای کسانی‌که ایمان آورده‌اید! اگر (دین) الله را یاری کنید، (الله) شما را یاری می‌کند و گام‌هایتان را (ثابت و) استوار می‌دارد.');
INSERT INTO "hussein_taji" VALUES(47,8,'47:8','و کسانی‌که کافر شدند، مرگ بر آنان باد! و (الله)اعمال‌شان را نابود کرد.');
INSERT INTO "hussein_taji" VALUES(47,9,'47:9','این بدان سبب است که آنان آنچه را که الله نازل کرده است ناپسند داشتند، پس الله اعمال‌شان را تباه (و نابود) کرد.');
INSERT INTO "hussein_taji" VALUES(47,10,'47:10','آیا در زمین سیر نکردند تا بنگرند عاقبت کسانی‌که پیش از آن‌ها بودند چگونه بود؟ الله (اموال و دیار‌شان را) بر سر‌شان ویران کرد، (و آن‌ها را نابود ساخت)، و برای کافران (کیفری) مانند آن خواهد بود.');
INSERT INTO "hussein_taji" VALUES(47,11,'47:11','این بدان سبب است که الله یاور (و مولای) کسانی است که ایمان آوردند، و آنکه کافران (مولا و) یاوری ندارند.');
INSERT INTO "hussein_taji" VALUES(47,12,'47:12','بی‌گمان الله کسانی را که ایمان آوردند و کارهای شایسته انجام دادند به باغ‌هایی (از بهشت) وارد می‌کند که نهرها از زیر (درختان) آن جاری است، و کسانی‌که کافر شدند (از متاع دنیوی) بهره می‌گیرند و می‌خورند همچنان‌که چارپایان می‌خورند، و آتش (جهنم) جایگاه آنان است.');
INSERT INTO "hussein_taji" VALUES(47,13,'47:13','و چه بسیار شهرهایی که (مردم) آن‌ها از (مردمِ) شهر تو که بیرونت کرده‌اند، توانمند‌تر بودند، ما هلاک‌شان کردیم، پس هیچ یاوری نداشتند.');
INSERT INTO "hussein_taji" VALUES(47,14,'47:14','آیا کسی‌که از سوی پروردگارش بر شیوۀ روشنی باشد، همانند کسی است که کردار بد او در نظرش آراسته شده و از هوا‌های (نفسانی) خود پیروی می‌کنند؟!');
INSERT INTO "hussein_taji" VALUES(47,15,'47:15','وصف بهشتی که به پرهیزگاران وعده داده شده، چنین است که: در آن نهرهایی از آب (زلال) بد‌بو نشده، و نهر‌هایی از شیر که طعم آن دگرگون نگشته، و نهرهایی از شراب (ناب) که مایۀ لذت نوشندگان است، و نهرهایی از عسل مصفی وجود دارد، و (نیز) در آن برای آن‌ها از هرگونه میوه‌ها (مهیا) است، و (از همه برتر) آمرزشی از سوی پروردگارشان است. (آیا این‌ها) همانند کسانی هستند که همیشه در آتش (جهنم) اند، و از آب جوشان نوشانده شوند که روده‌هایشان را پاره پاره (و متلاشی) کند؟');
INSERT INTO "hussein_taji" VALUES(47,16,'47:16','(ای پیامبر) گروهی از آن‌ها(= منافقان) به تو گوش فرا می‌دهند، تا وقتی که از نزدت بیرون رفتند، به کسانی‌که به آنان علم عطا کرده شده، (از روی استهزاء) می‌گویند: «الآن (محمد- صلی الله علیه وسلم-) چه گفت؟!» آن‌ها کسانی هستند که الله بر دل‌هایشان مهر نهاده، و از هواهای (نفسانی) خود پیروی کرده‌اند.');
INSERT INTO "hussein_taji" VALUES(47,17,'47:17','و کسانی‌که هدایت یافته‌اند (الله) بر هدایت‌شان افزود، و به آنان پرهیزگاری عطا فرمود.');
INSERT INTO "hussein_taji" VALUES(47,18,'47:18','پس آیا آن‌ها (= کافران) جز این انتظار دارند که قیامت ناگهان فرا رسد؟ به راستی نشانه‌هایش آمده است، پس چون فرا رسد، پند گرفتن (و ایمان) شان چه سودی خواهد داشت؟!');
INSERT INTO "hussein_taji" VALUES(47,19,'47:19','پس (ای پیامبر) بدان که معبودی (به حق) جز «الله» نیست، و برای گناه خود و برای مردان و زنان مؤمن آمرزش طلب کن، و الله محل حرکت شما و قرار گاه شما را می‌داند.');
INSERT INTO "hussein_taji" VALUES(47,20,'47:20','وکسانی‌که ایمان آورده‌اند می‌گویند: «چرا سوره‌ای نازل نشده؟! (که فرمان جهاد را بدهد؟)». پس چون سوره‌ای محکم (و روشن) نازل شود و در آن (سخن از) جنگ ذکر شده باشد، کسانی‌که در دل‌های شان بیماری است می‌بینی که همچون کسی‌که در سکرات مرگ (قرار گرفته و) بی‌هوش شده باشد به تو نگاه می‌کنند، پس [همین حالت] برای آنان شایسته‌تر است [ برخی از مفسرین کلمۀ «أَوْلَىٰ» را بمعنای: تهدید و وعید (یعنی وای بر آنان) تفسیر کرده‌اند، اما برخی دیگر همچون امام ابن کثیر آن را بمعنای شایسته‌تر، و سزاوار‌‌تر تفسیر نموده است.].');
INSERT INTO "hussein_taji" VALUES(47,21,'47:21','فرمانبرداری (نمودن) و سخن نیکو (گفتن)، پس چون تصمیم قطعی (جهاد) گرفته شد، اگر با الله صادق باشند برای آن‌ها بهتر است.');
INSERT INTO "hussein_taji" VALUES(47,22,'47:22','پس آیا اگر به حکومت رسیدید، جز این (امید) است که در زمین فساد کنید، و پیوند خویشاوندان‌تان را ببرید؟!');
INSERT INTO "hussein_taji" VALUES(47,23,'47:23','اینان کسانی هستند که الله لعنت‌شان کرده است، پس (گوش‌های) آنان را کر نموده و چشم‌هایشان را نابینا کرده است.');
INSERT INTO "hussein_taji" VALUES(47,24,'47:24','آیا در قرآن تدبّر نمی‌کنند یا بر دل‌هایشان قفل‌هاست؟!');
INSERT INTO "hussein_taji" VALUES(47,25,'47:25','بی‌گمان کسانی‌که بعد از روشن شدن (راه) هدایت برای آن‌ها، به پشت سر خود باز گشتند (و مرتد شدند) شیطان (اعمال زشت‌شان را) برای آن‌ها بیاراست، و (با آرزوهای دراز) فریب‌شان داد.');
INSERT INTO "hussein_taji" VALUES(47,26,'47:26','این بدان سبب است که آن‌ها (=منافقان) به کسانی (= یهودیان) که نسبت به آنچه الله نازل کرده (از وحی) کراهت داشتند، گفتند: «ما در پاره‌ای از امور از شما پیروی می‌کنیم» و الله پنهان‌کاری آن‌ها را می‌داند.');
INSERT INTO "hussein_taji" VALUES(47,27,'47:27','پس (حال آن‌ها) چگونه خواهد بود هنگامی‌که فرشتگان (مرگ) جان‌هایشان را می‌گیرند، بر چهره‌ها و پشت‌هایشان می‌زنند.');
INSERT INTO "hussein_taji" VALUES(47,28,'47:28','این (کیفر) بدان سبب است که آن‌ها از آنچه الله را به خشم می‌آورد پیروی کردند، و خشنودیش را کراهت داشتند، پس (الله) اعمال‌شان را نابود کرد.');
INSERT INTO "hussein_taji" VALUES(47,29,'47:29','آیا کسانی‌که در دل‌هایشان بیماری (نفاق) است گمان کردند که الله کینه‌هایشان را آشکار نخواهد کرد؟!');
INSERT INTO "hussein_taji" VALUES(47,30,'47:30','و اگر ما بخواهیم آن‌ها را به تو (ای پیامبر) نشان می‌دهیم، آنگاه آنان را با سیما (و قیافه) های‌شان بشناسی، و مسلماً تو آنان را در طرز سخن گفتن‌شان می‌شناسی، و الله اعمال شما را می‌داند.');
INSERT INTO "hussein_taji" VALUES(47,31,'47:31','و (همۀ) شما را قطعاً آزمایش می‌کنیم تا از میان شما مجاهدان و صابران را معلوم کنیم، و (اخبار و) احوال شما را بیازماییم.');
INSERT INTO "hussein_taji" VALUES(47,32,'47:32','بی‌گمان کسانی‌که کافر شدند و (دیگران را) از راه الله باز داشتند و بعد از آشکار شدن (راه) هدایت برای آن‌ها با پیامبر مخالفت ورزیدند، هرگز زیانی به الله نمی‌رسانند، و (الله) به زودی اعمال‌شان را تباه خواهد کرد.');
INSERT INTO "hussein_taji" VALUES(47,33,'47:33','ای کسانی‌که ایمان آورده‌اید! الله را اطاعت کنید، و رسول (الله) را اطاعت کنید، و اعمال‌تان را باطل نکنید.');
INSERT INTO "hussein_taji" VALUES(47,34,'47:34','بی‌گمان کسانی‌که کافر شدند و (دیگران را) از راه الله باز داشتند سپس در حال کفر مردند، پس الله هرگز آن‌ها را نخواهد آمرزید.');
INSERT INTO "hussein_taji" VALUES(47,35,'47:35','پس (ای مؤمنان) هرگز (هنگام جهاد) سست نشوید، و (دشمنان را) به صلح و آشتی دعوت نکنید، در حالی‌که شما برتر هستید و الله با شماست، و (چیزی از پاداش) اعمال‌تان را نخواهد کاست.');
INSERT INTO "hussein_taji" VALUES(47,36,'47:36','جز این نیست که زندگی دنیا بازی و سرگرمی است، و اگر ایمان بیاورید و پرهیزگاری کنید (الله) پاداش‌های‌تان را به شما خواهد داد، و (تمام) اموال‌تان را از شما نمی‌طلبد.');
INSERT INTO "hussein_taji" VALUES(47,37,'47:37','اگر (تمام) آن را از شما مطالبه کند، آنگاه اصرار نماید، بخل می‌ورزید، و کینه‌های شما را آشکار می‌سازد.');
INSERT INTO "hussein_taji" VALUES(47,38,'47:38','آگاه باشید، شما همان کسانی هستید که برای انفاق در راه الله فرا خوانده می‌شوید، پس بعضی از شما بخل می‌ورزد، و هرکس بخل ورزد، جز این نیست که بر خویشتن بخل ورزیده است، و الله بی‌نیاز است و شما (همه) فقیر (و نیازمند)ید، و اگر روی بگردانید (الله) گروهی دیگر را بجای شما می‌آورد، آنگاه آن‌ها مانند شما نخواهند بود.');
INSERT INTO "hussein_taji" VALUES(48,1,'48:1','به راستی ما برای تو (فتح و) پیروزی آشکاری مقرر کرده‌ایم. [مراد از این فتح و پیروزی صلح حدیبیه است. (تفسیر ابن کثیر).]');
INSERT INTO "hussein_taji" VALUES(48,2,'48:2','تا الله گناه گذشته و آیندۀ تو را بیامرزد، و نعمتش را بر تو تمام کند، و به راه راست هدایتت نماید.');
INSERT INTO "hussein_taji" VALUES(48,3,'48:3','و الله به پیروزی شکست ناپذیری تو را یاری کند.');
INSERT INTO "hussein_taji" VALUES(48,4,'48:4','او کسی است که در دل‌های مؤمنان آرامش نازل کرد، تا ایمانی بر ایمان‌شان بیفزایند، و سپاهیان آسمان‌ها و زمین از آن الله است، و الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(48,5,'48:5','تا (سرانجام) مردان و زنان مؤمن را به باغ‌هایی (از بهشت) وارد کند، که نهرها از زیر (درختان) آن جاری است، جاودانه در آنجا خواهند بود، و تا گناهان‌شان را از آن‌ها بزداید (و ببخشد) و این نزد الله کامیابی بزرگی است.');
INSERT INTO "hussein_taji" VALUES(48,6,'48:6','و (نیز) مردان و زنان منافق و مردان و زنان مشرک را که به الله گمان بد می‌برند، عذاب کند، حوادث ناگوار و بد بر آنان باد، و الله بر آن‌ها خشم گرفته، و آن‌ها را لعنت کرده، و جهنم را برای آن‌ها آماده ساخته است، و چه بد جایگاهی است.');
INSERT INTO "hussein_taji" VALUES(48,7,'48:7','و سپاهیان آسمان‌ها و زمین از آن الله است، و الله پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(48,8,'48:8','(ای پیامبر) به راستی ما تو را گواه و مژده‌دهنده و بیم‌دهنده فرستادیم.');
INSERT INTO "hussein_taji" VALUES(48,9,'48:9','تا (شما ای مردم) به الله و پیامبرش ایمان بیاورید و (دین) اورا یاری کنید، و او را بزرگ دارید، و بامداد و شامگاه او را تسبیح گویید.');
INSERT INTO "hussein_taji" VALUES(48,10,'48:10','بی‌گمان کسانی‌که با تو بیعت می‌کنند، جز این نیست که با الله بیعت می‌کنند، دست الله بالای دست آن‌هاست، پس هرکس که پیمان شکنی کند، تنها به زیان خودش پیمان شکسته است، و هرکس به آنچه که بر آن با الله عهد بسته وفا کند، بزودی پاداش عظیمی به او خواهد داد. [مراد از این بیعت، بیت رضوان است که در روز حدیبیه زیر درختی انجام گرفت. (تفسیر ابن کثیر).]');
INSERT INTO "hussein_taji" VALUES(48,11,'48:11','به زودی باز ماندگان از اعراب (بادیه‌نشین، عذر آورده و) به تو (ای پیامبر) می‌گویند: «(نگهداری) اموال و زن و فرزندان‌مان ما را به خود مشغول داشت، (و نتوانستیم در سفر حدیبیه همراه شما باشیم) پس برای ما طلب آمرزش کن» آن‌ها با زبان‌های خود چیزی می‌گویند که در دل‌های آن‌ها نیست، بگو: «چه کسی می‌تواند در برابر الله از شما دفاع کند، اگر در حق شما زیانی بخواهد و یا در حق شما نفی بخواهد؟! بلکه الله به آنچه انجام می‌دهید آگاه است».');
INSERT INTO "hussein_taji" VALUES(48,12,'48:12','بلکه شما گمان بردید که پیامبر (الله) و مؤمنان هرگز به سوی خانوادۀ خویش باز نخواهند گشت، و این (گمان) در دل‌های شما زینت یافته بود، و گمان بد بردید، و قومی هلاک شده (و نابود) گشتید.');
INSERT INTO "hussein_taji" VALUES(48,13,'48:13','و هرکس که به الله و پیامبرش ایمان نیاورد، پس (بداند که) ما برای کافران آتش سوزان (جهنم) را آماده کرده‌ایم.');
INSERT INTO "hussein_taji" VALUES(48,14,'48:14','فرمانروایی آسمان‌ها و زمین از آنِ الله است، هر که را بخواهد می‌آمرزد، و هر که را بخواهد عذاب می‌کند، و الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(48,15,'48:15','هنگامی‌که به سوی غنایم (خیبر) حرکت نمودید تا آن‌ها را برگیرید، بازماندگان (حدیبیه) خواهند گفت: «بگذارید ما (نیز) در پی شما بیاییم». آن‌ها می‌خواهند کلام الله را تغییر دهند، (ای پیامبر) بگو: «هرگز از پی ما نیایید، این چنین الله از پیش فرموده است». پس آن‌ها خواهند گفت: «(نه) بلکه، شما به ما حسادت می‌ورزید». بلکه (حقیقیت این است که) جز اندکی در نمی‌یابند.');
INSERT INTO "hussein_taji" VALUES(48,16,'48:16','(ای پیامبر،) به باز ماندگان از اعراب (بادیه نیشن) بگو: «به زودی فراخوانده می‌شوید به سوی قومی سخت جنگجو (و نیرومند) که با آن‌ها نبرد کنید، یا اسلام بیاورند، پس اگر اطاعت کنید، الله پاداش نیکی به شما می‌دهد، و اگر سرپیچی کنید همان گونه که پیش از این سرپیچی کردید، شما را به عذابی دردناک عذاب می‌دهد».');
INSERT INTO "hussein_taji" VALUES(48,17,'48:17','بر نابینا گناهی نیست، و بر لنگ (نیز) گناهی نیست، و بر بیمار (هم) گناهی نیست، (اگر این‌ها در جهاد شرکت نکنند). و هرکس از الله و رسولش اطاعت نماید، او را در باغ‌هایی (از بهشت) وارد می‌سازد که نهرها از زیر (درختان) آن جاری است، و هر کس که سرپیچی کند به عذابی دردناک او را عذاب می‌کند.');
INSERT INTO "hussein_taji" VALUES(48,18,'48:18','به راستی الله از مؤمنان - هنگامی‌که زیر درخت با تو بیعت کردند- خشنود شد، پس آنچه که در درون دل‌های شان بود دانست، لذا آرامش را بر آن‌ها نازل کرد و پیروزی (و فتحی) نزدیک را به آن‌ها پاداش داد.');
INSERT INTO "hussein_taji" VALUES(48,19,'48:19','و (همچنین) غنیمت‌های فراوانی که آن را به دست می‌آوردند، و الله پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(48,20,'48:20','الله غنیمت‌های فراوانی به شما وعده داده است که آن‌ها را به دست می‌آورید، پس این (غنیمت خیبر) را برای شما زودتر فراهم ساخت، و دست (تعدّی) مردم (مخالف و دشمن) را از شما باز داشت، و تا (عبرت و) نشانه‌ای برای مؤمنان باشد، و شما را به راه راست هدایت کند.');
INSERT INTO "hussein_taji" VALUES(48,21,'48:21','و (نیز غنیمت‌های) دیگری که هنوز به آن‌ها دست نیافته‌اید، به تحقیق الله به آن احاطه دارد، و الله بر همه چیز تواناست.');
INSERT INTO "hussein_taji" VALUES(48,22,'48:22','و اگر کسانی‌که کافر شدند، با شما می‌جنگیدند، یقیناَ پشت می‌کردند (و از میدان فرار می‌کردند) آنگاه یار و یاوری نمی‌یافتند.');
INSERT INTO "hussein_taji" VALUES(48,23,'48:23','(این) سنت الله است، که پیش از این (نیز) گذشته است، و برای سنت الله هرگز دگرگونی نخواهی یافت.');
INSERT INTO "hussein_taji" VALUES(48,24,'48:24','و او کسی است که دست آن‌ها را از شما، و دست شما را از آن‌ها در درون مکه بعد از آنکه شما را بر آن‌ها پیروز گرداند، (باز داشت و) کوتاه کرد. و الله به آنچه انجام می‌دهید بیناست.');
INSERT INTO "hussein_taji" VALUES(48,25,'48:25','آن‌ها کسانی هستند که کافر شدند و شما را از مسجد الحرام بازداشتند، و از رسیدن قربانی به قربانگاهش (بازداشتند)، و اگر مردان و زنان مومن (مستضعف در مکه) نبودند، که شما آن‌ها را نمی‌شناسید، (و بیم آن نبود) که آن‌ها را پایمال (و هلاک) کنید، پس ندانسته (از کشتن) آن‌ها به شما گناه (و ننگ و رنج) می‌رسید، (الله هرگز از این جنگ جلوگیری نمی‌کرد) تا الله هرکس را بخواهد در رحمت خود وارد کند، اگر (مؤمنان و کافران در مکه) از یکدیگر جدا بودند، مسلماً کافران‌شان را به عذابی دردناک عذاب می‌کردیم.');
INSERT INTO "hussein_taji" VALUES(48,26,'48:26','آنگاه که کسانی‌که کافر شدند در دل‌های خود حمیت (= تعصب و نخوت) - همان حمیت جاهلیت - نهادند [ که نبوت رسول الله صلی الله علیه وسلم را قبول نکردند، و به نوشتن بسم الله الرحمن الرحیم اقرار ننمودند، و مانع رفتن مسلمانان به خانۀ الله شدند.]، پس الله آرامش خود را بر رسولش و بر مؤمنان نازل فرمود، و آن‌ها را به کلمۀ تقوا ملزم ساخت، و آن‌ها از هر کس سزاوار تر و اهل آن بودند، و الله به همه چیز داناست.');
INSERT INTO "hussein_taji" VALUES(48,27,'48:27','به راستی الله رؤیای رسولش را به حق تحقق بخشید، قطعاً اگر الله بخواهد همه شما در نهایت امنیت (و) در حالی‌که سرهای خود را تراشیده و (یا) کوتاه کرده‌اید، و از هیچ کس ترس ندارید وارد مسجد الحرام خواهید شد، پس (الله) چیزهایی را می‌دانست که شما نمی‌دانستید، و پیش از آن، فتح (و پیروزی) نزدیکی (برای شما) مقرر نمود.');
INSERT INTO "hussein_taji" VALUES(48,28,'48:28','او کسی است که رسولش را به هدایت و دین حق فرستاد، تا آن را بر همۀ ادیان پیروز گرداند و کافی است که الله گواه باشد.');
INSERT INTO "hussein_taji" VALUES(48,29,'48:29','محمد ( صلی الله علیه وسلم) رسول الله است، و کسانی‌که با او هستند، بر کافران سخت گیر (و شدید) و در میان خود مهربانند، آن‌ها را در حال رکوع و سجده می‌بینی که از الله فضل و خشنودی می‌طلبند، نشانۀ (درستکاری) آن‌ها در چهره‌هایشان از اثر سجده (نمایان) است. این توصیف آن‌ها در تورات است، و توصیف آن‌ها در انجیل همانند زراعتی که جوانه بزند، سپس آن را تقویت کرد، تا محکم گردیده، و بر پای خودایستاده است، و کشاورزان را به شگفتی وامی‌دارد، تا از (دیدن) آن‌ها کافران را به خشم آورد، الله به کسانی از آن‌ها که ایمان آوردند و کارهای شایسته انجام دادند وعدۀ آمرزش و پاداش عظیمی داده‌است.');
INSERT INTO "hussein_taji" VALUES(49,1,'49:1','ای کسانی‌که ایمان آورده‌اید! بر الله و رسولش پیشدستی نکنید، و از الله بترسید، بی‌گمان الله شنوای داناست.');
INSERT INTO "hussein_taji" VALUES(49,2,'49:2','ای کسانی‌که ایمان آورده‌اید! صدای‌تان را فراتر از صدای پیامبر نکنید، و با او با آواز بلند سخن نگویید، همان گونه که با یکدیگر بلند سخن می‌گویید، که مبادا در حالی‌که نمی‌دانید اعمال شما نابود گردد.');
INSERT INTO "hussein_taji" VALUES(49,3,'49:3','بی‌گمان کسانی‌که صدای خود را نزد رسول الله پایین می‌آورند، آن‌ها کسانی هستند که الله دل‌های شان را برای تقوا آزموده (و خالص گردانیده) است، برای آن‌ها آمرزش و پاداش عظیمی است.');
INSERT INTO "hussein_taji" VALUES(49,4,'49:4','بی‌گمان کسانی‌که از پشت حجرهها صدایت می‌زنند، بیشترشان بی‌خردانند.');
INSERT INTO "hussein_taji" VALUES(49,5,'49:5','و اگر آن‌ها صبر می‌کردند تا که (خود) به سوی آنان بیرون آیی برای آن‌ها بهتر بود، و الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(49,6,'49:6','ای کسانی‌که ایمان آورده‌اید! اگر (شخص) فاسقی خبری برای شما آورد، پس (دربارۀ آن) بررسی (و تحقیق) کنید، تا آنکه (مبادا) به گروهی از روی نادانی آسیب برسانید، آنگاه بر آنچه انجام داده‌اید پشیمان شوید.');
INSERT INTO "hussein_taji" VALUES(49,7,'49:7','و بدانید که همانا رسول الله در میان شماست، اگر در بسیاری از کارها از شما اطاعت کند، یقیناً به مشقّت خواهید افتاد، و لیکن الله ایمان را محبوب شما ساخت و آن را در دل‌های تان زینت بخشید، و کفر و فسق و نافرمانی را برایتان (منفور و) ناپسند گرداند، اینانند که هدایت یافتگانند.');
INSERT INTO "hussein_taji" VALUES(49,8,'49:8','(و این (خیر) فضل و نعمتی از سوی الله است، و الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(49,9,'49:9','و اگر دو گروه از مؤمنان با یکدیگر به جنگ بر خاستند، میان‌شان آشتی بر قرار کنید، پس اگر یکی از آن دو بر دیگری تجاوز کرد، با گروه متجاوز بجنگید تا به فرمان الله باز گردد، پس اگر بازگشت، میان آن‌ها به عدالت صلح بر قرار کنید، و عدالت پیشه کنید، بی‌گمان الله عادلان را دوست می‌دارد.');
INSERT INTO "hussein_taji" VALUES(49,10,'49:10','یقیناً مؤمنان برادرند، پس میان برادران‌تان صلح (و آشتی) بر قرار کنید، و از الله بترسید، باشد که به شما رحم کرده شود.');
INSERT INTO "hussein_taji" VALUES(49,11,'49:11','ای کسانی‌که ایمان آورده‌اید! گروهی (از مردان) گروه دیگر را مسخره نکند، شاید آنان (= مسخره شدگان) از این‌ها بهتر باشند، و نه زنانی، زنان دیگر را، شاید آنان (= مسخره شدگان) از این‌ها بهتر باشند، و از یکدیگر عیبجوئی نکنید، و یکدیگر را با القاب زشت نخوانید، که پس از ایمان (آوردن) نام فسق بسیار (زشت و) بد است و هر که توبه نکرد، پس آنانند که ستمکارند.');
INSERT INTO "hussein_taji" VALUES(49,12,'49:12','ای کسانی‌که ایمان آورده‌اید! از بسیاری از گمان (های بد) بپرهیزید، بی‌شک بعضی از گمان (ها) گناه است، و (در امور دیگران) تجسس (و کنجکاوی) نکنید، و بعضی از شما بعضی دیگر را غیبت نکند، آیا کسی از شما دوست دارد که گوشت برادرش را در حالی‌که مرده است، بخورد؟! پس (البته) آن را ناپسند دارید، و از الله بترسید، بی‌گمان الله توبه‌پذیر مهربان است.');
INSERT INTO "hussein_taji" VALUES(49,13,'49:13','ای مردم! بی‌شک ما شما را از یک مرد و زن آفریدیم، و شما را تیره‌ها و قبیله‌ها قرار دادیم، تا یکدیگر را بشناسید، همانا گرامی‌ترین شما نزد الله پرهیزگارترین شماست، بی‌گمان الله دانای آگاه است.');
INSERT INTO "hussein_taji" VALUES(49,14,'49:14','اعراب (بادیه‌نشین) گفتند: «ایمان آورده‌ایم» (ای پیامبر!) بگو: «شما ایمان نیاورده‌اید، لیکن بگوئید: اسلام آورده‌ایم، و هنوز ایمان در دل‌های تان وارد نشده است. و اگر الله و پیامبرش را اطاعت کنید چیزی از (پاداش) اعمال‌تان کاسته نمی‌شود، همانا الله آمرزندۀ مهربان است».');
INSERT INTO "hussein_taji" VALUES(49,15,'49:15','مؤمنان (حقیقی) تنها کسانی اند که به الله و پیامبرش ایمان آورده‌اند، سپس (در این باره) شک (و تردید) نکرده‌اند، و با اموال خود و جان های خود در راه الله جهاد کرده‌اند، اینانند که راستگویانند.');
INSERT INTO "hussein_taji" VALUES(49,16,'49:16','(ای پیامبر!) بگو: «آیا الله را به دین خود با خبر می‌سازید؟! در حالی‌که الله آنچه در آسمان‌ها و آنچه در زمین است می‌داند، و الله به همه چیز آگاه است».');
INSERT INTO "hussein_taji" VALUES(49,17,'49:17','آن‌ها بر تو (ای پیامبر) منتّ می‌گذارند که اسلام آورده‌اند، بگو: «اسلام آوردن‌تان را بر من منّت نگذارید، بلکه اگر راستگویید الله بر شما منّت می‌نهد که شما را به (سوی) ایمان هدایت کرده است».');
INSERT INTO "hussein_taji" VALUES(49,18,'49:18','همانا الله غیب آسمان‌ها و زمین را می‌داند، و الله به آنچه انجام می‌دهید بیناست.');
INSERT INTO "hussein_taji" VALUES(50,1,'50:1','ق، سوگند به قرآن مجید (که تو پیامبر الله ای).');
INSERT INTO "hussein_taji" VALUES(50,2,'50:2','بلکه آن‌ها تعجب کردند که هشدار دهنده‌ای از (میان) خودشان به سوی آنان آمده، پس کافران گفتند: «این چیزی عجیب است،');
INSERT INTO "hussein_taji" VALUES(50,3,'50:3','آیا هنگامی‌که مردیم و خاک شدیم، (دوباره برا نگیخته خواهیم شد؟!) این باز گشتی بعید است».');
INSERT INTO "hussein_taji" VALUES(50,4,'50:4','به راستی ما می‌دانیم آنچه را زمین از آن‌ها می‌کاهد، و کتابی که همه چیز در آن نگهداری شده، نزد ماست.');
INSERT INTO "hussein_taji" VALUES(50,5,'50:5','بلکه آن‌ها حق را هنگامی‌که به سراغ‌شان آمد تکذیب کردند، پس آنان در کاری سرگردان (و پریشان) هستند.');
INSERT INTO "hussein_taji" VALUES(50,6,'50:6','آیا آن‌ها به آسمان بالای سرشان نمی‌نگرند که چگونه آن را بنا کرده‌ایم و آراسته‌ایم، و هیچ شکافی در آن نیست؟!');
INSERT INTO "hussein_taji" VALUES(50,7,'50:7','و زمین را گستردیم و در آن کوه‌های بلند (و استوار) افکندیم، و از هر نوع (گیاه) خوش منظر در آن رویاندیم.');
INSERT INTO "hussein_taji" VALUES(50,8,'50:8','(تا همۀ این‌ها) بینش و پندی برای هر بندۀ توبه کاری باشد.');
INSERT INTO "hussein_taji" VALUES(50,9,'50:9','و از آسمان آبی پر برکت نازل کردیم، پس با آن باغ‌ها و دانه (های) درو شدنی رویاندیم،');
INSERT INTO "hussein_taji" VALUES(50,10,'50:10','و نخل‌های بلند (قامت) که خوشه‌های برهم چیده دارند.');
INSERT INTO "hussein_taji" VALUES(50,11,'50:11','(همۀ این‌ها) برای روزی بندگان (رویانیدیم) و با آن (باران) سرزمین مرده را زنده کردیم، بر آمدن (از گور و زنده شدن نیز) چنین است.');
INSERT INTO "hussein_taji" VALUES(50,12,'50:12','پیش از آن‌ها قوم نوح و اصحاب رسّ (= چاه، که در یمامه بودند). و (قوم) ثمود (پیامبران‌شان را) تکذیب کردند.');
INSERT INTO "hussein_taji" VALUES(50,13,'50:13','و (همچنین قوم) عاد و فرعون و قوم لوط.');
INSERT INTO "hussein_taji" VALUES(50,14,'50:14','و اصحاب «ایکه» (= قوم شعیب) و قوم «تبّع» (حمیری‌ها، که در یمن بودند) هر یک (از آن‌ها) رسولان (الهی) را تکذیب کردند، پس وعدۀ عذاب (و بیم) من (بر آن‌ها) تحقّق یافت.');
INSERT INTO "hussein_taji" VALUES(50,15,'50:15','آیا ما از آفرینش نخستین عاجز ماندیم؟! (خیر) بلکه آن‌ها (با این همه دلایل) از آفرینش جدید در تردید (و شک) اند.');
INSERT INTO "hussein_taji" VALUES(50,16,'50:16','به راستی ما انسان را آفریده‌ایم و چیزی را که نفسش به او وسوسه می‌کند می‌دانیم، و ما از (شاه) رگ گردن به او نزدیک‌تریم.');
INSERT INTO "hussein_taji" VALUES(50,17,'50:17','هنگامی‌که دو (فرشتۀ) فراگیرنده بر جانب راست و چپ نشسته‌اند، (و اعمالش را) فرا می‌گیرند.');
INSERT INTO "hussein_taji" VALUES(50,18,'50:18','هیچ سخنی را بر زبان نمی‌آورد، مگر اینکه نزدش مراقبی حاضر (و آماده نوشتن) است.');
INSERT INTO "hussein_taji" VALUES(50,19,'50:19','و سکرات (و سختی) مرگ به راستی فرا رسید، (و به او گفته می‌شود: ای انسان،) این همان چیزی است که تو از آن می‌گریختی.');
INSERT INTO "hussein_taji" VALUES(50,20,'50:20','و در «صور» دمیده شود، آن روز، (روز تحقق) وعدۀ (عذاب) است.');
INSERT INTO "hussein_taji" VALUES(50,21,'50:21','و هرکسی (به عرصۀ محشر) می‌آید در حالی‌که همراه او سوق دهنده و گواهی است.');
INSERT INTO "hussein_taji" VALUES(50,22,'50:22','(به او گفته می‌شود (: به راستی تو از این (صحنه) غافل بودی، پس ما پرده را از (چشم) تو برداشتیم، لذا امروز چشمانت تیز بین است.');
INSERT INTO "hussein_taji" VALUES(50,23,'50:23','و (فرشتۀ) همنشین او می‌گوید: «این (اعمال اوست) آنچه که نزد من آماده (و حاضر) است».');
INSERT INTO "hussein_taji" VALUES(50,24,'50:24','(الله به فرشتگان مراقب فرمان می‌دهد) «هر نا سپاس سرکشی را در جهنم افکنید.');
INSERT INTO "hussein_taji" VALUES(50,25,'50:25','(همان) باز دارندۀ خیر، تجاوز گر شکّاک را.');
INSERT INTO "hussein_taji" VALUES(50,26,'50:26','(همان) کسی‌که با الله معبود دیگری قرار داده است، پس او را در عذاب سخت بیفکنید».');
INSERT INTO "hussein_taji" VALUES(50,27,'50:27','همنشین او (از شیاطین) گوید: «پروردگارا! (من) اورا به طغیان (و گمراهی) وا دار نکردم، لیکن (او خود) در گمراهی دور و درازی بود».');
INSERT INTO "hussein_taji" VALUES(50,28,'50:28','(الله) می‌فرماید: «نزد من (مجادله و) مخاصمه نکنید، و مسلماً من (پیش از این) برای شما هشدار فرستاده بودم.');
INSERT INTO "hussein_taji" VALUES(50,29,'50:29','(آن) سخن نزد من تغییر داده نمى شود، و من هرگز به بندگان ستم نخواهم کرد».');
INSERT INTO "hussein_taji" VALUES(50,30,'50:30','(و به یاد آور) روزی‌که به جهنم می‌گوییم: «آیا پرشده‌ای؟!» و (او) می‌گوید: «آیا هیچ افزون براین (هم) هست؟!».');
INSERT INTO "hussein_taji" VALUES(50,31,'50:31','(در آن روز) بهشت را برای پرهیزگاران نزدیک آورده شود (تا آن‌ها از آن) دور نباشند.');
INSERT INTO "hussein_taji" VALUES(50,32,'50:32','(به آن‌ها گفته می‌شود:) این چیزی است که به شما وعده داده می‌شد، برای هر رجوع‌کننده (به سوی الله و) نگهدارنده (پیمان و احکام الهی) است.');
INSERT INTO "hussein_taji" VALUES(50,33,'50:33','کسی‌که در نهان از (الله) رحمان بترسد، و با قلبی توبه کار (در محضر او) آمده باشد.');
INSERT INTO "hussein_taji" VALUES(50,34,'50:34','(به آن‌ها گفته می‌شود:) «به سلامت وارد آن (بهشت) شوید، (که) این روز جاودانگی است».');
INSERT INTO "hussein_taji" VALUES(50,35,'50:35','هر چه بخواهند در آنجا برای آن‌هاست، و در نزد ما افزون (براین) است.');
INSERT INTO "hussein_taji" VALUES(50,36,'50:36','و چه بسیار امت‌هایی را که پیش از آن‌ها هلاک کردیم، که آن‌ها از این‌ها قوی‌تر بودند، پس در شهرها (سیر و) جستجو کردند، آیا هیچ راه فراری وجود دارد؟!');
INSERT INTO "hussein_taji" VALUES(50,37,'50:37','بی‌گمان در این (سخن) برای صاحب دلان یا آنان که با حضور (خاطر) گوش فرا دهند، پندی است.');
INSERT INTO "hussein_taji" VALUES(50,38,'50:38','و به راستی ما آسمان‌ها و زمین و آنچه در میان آن دوست در شش روز آفریدیم، و هیچ (گونه) خستگی (و رنجی) به ما نرسید.');
INSERT INTO "hussein_taji" VALUES(50,39,'50:39','پس بر آنچه آن‌ها می‌گویند شکیبا باش، و پیش از طلوع خورشید و پیش از غروب (آن) به ستایش پروردگارت تسبیح گوی.');
INSERT INTO "hussein_taji" VALUES(50,40,'50:40','و (نیز) پاره‌ای از شب و بعد از سجده‌ها او را تسبیح گوی.');
INSERT INTO "hussein_taji" VALUES(50,41,'50:41','و (ای پیامبر) گوش فرا ده، روزی‌که منادی از مکانی نزدیک ندا می‌دهد.');
INSERT INTO "hussein_taji" VALUES(50,42,'50:42','روزی‌که همگان صیحه (= بانگ سهمناک رستاخیز) را بحق می‌شنوند، آن روز خروج (از گورها) است.');
INSERT INTO "hussein_taji" VALUES(50,43,'50:43','همانا ماییم که زنده می‌کنیم و می‌میرانیم، و بازگشت (همگان) به سوی ماست.');
INSERT INTO "hussein_taji" VALUES(50,44,'50:44','روزی‌که زمین از (روی) آن‌ها شکافته شود، (و آن‌ها) شتابان (از گورها بیرون آیند)، این گرد آوری برای ما آسان است.');
INSERT INTO "hussein_taji" VALUES(50,45,'50:45','ما به آنچه (مشرکان) می‌گویند داناتریم، و تو (ای پیامبر) بر آن‌ها زورگو نیستی (که آن‌ها را به ایمان آوردن اجبار کنی) پس کسی را که از عذاب (و هشدار) من می‌ترسد، به (وسیلۀ) قرآن پند (و اندرز) بده.');
INSERT INTO "hussein_taji" VALUES(51,1,'51:1','سوگند به بادهای که (خاک را) می‌پراکند،');
INSERT INTO "hussein_taji" VALUES(51,2,'51:2','و سوگند به ابر های که بار سنگینی (از باران را) با خود حمل می‌کنند.');
INSERT INTO "hussein_taji" VALUES(51,3,'51:3','و سوگند به کشتی‌هایی که به آسانی (بر روی دریاها) در حرکتند.');
INSERT INTO "hussein_taji" VALUES(51,4,'51:4','و سوگند به فرشتگانی که کارها را تقسیم می‌کنند.');
INSERT INTO "hussein_taji" VALUES(51,5,'51:5','بی‌گمان آنچه به شما وعده داده شد قطعاً راست است.');
INSERT INTO "hussein_taji" VALUES(51,6,'51:6','و یقیناً (روز) جزا (ی اعمال) واقع شدنی است.');
INSERT INTO "hussein_taji" VALUES(51,7,'51:7','قسم به آسمان که دارای راه‌ها (و ستارگان زیباست).');
INSERT INTO "hussein_taji" VALUES(51,8,'51:8','بی‌گمان شما (کفار) در گفتار مختلف و گوناگون هستید.');
INSERT INTO "hussein_taji" VALUES(51,9,'51:9','کسی‌که (در علم الهی از خیر و نیکی) دور نگه داشته شده است از (ایمان آوردن به) آن (= قرآن) دور نگه داشته می‌شود.');
INSERT INTO "hussein_taji" VALUES(51,10,'51:10','مرگ (و لعنت) بر دروغگویان.');
INSERT INTO "hussein_taji" VALUES(51,11,'51:11','کسانی‌که آنان در غفلت (و جهل) فرو رفته‌اند.');
INSERT INTO "hussein_taji" VALUES(51,12,'51:12','پیوسته می‌پرسند: «روز جزا کی خواهد بود؟».');
INSERT INTO "hussein_taji" VALUES(51,13,'51:13','(همان) روزی‌که آن‌ها بر آتش عذاب می‌شوند.');
INSERT INTO "hussein_taji" VALUES(51,14,'51:14','(و به آن‌ها گفته می‌شود:) «عذاب خود را بچشید، این همان چیزی است که برای آن شتاب داشتید».');
INSERT INTO "hussein_taji" VALUES(51,15,'51:15','(در روز قیامت) مسلماً پرهیزگاران در باغ‌ها و چشمه سارها (ی بهشت) هستند.');
INSERT INTO "hussein_taji" VALUES(51,16,'51:16','آنچه پروردگارشان به آن‌ها عطا فرموده، دریافت کرده‌اند، بی‌گمان آن‌ها پیش از این (در دنیا) نیکوکار بودند.');
INSERT INTO "hussein_taji" VALUES(51,17,'51:17','آن‌ها بخش اندکی از شب را می‌خوابیدند (و به نماز و نیایش مشغول بودند).');
INSERT INTO "hussein_taji" VALUES(51,18,'51:18','و در سحر گاهان آمرزش طلب می‌کردند.');
INSERT INTO "hussein_taji" VALUES(51,19,'51:19','و در اموال‌شان برای سائل و (تنگدست) محروم (بهره و) حقی بود.');
INSERT INTO "hussein_taji" VALUES(51,20,'51:20','و در زمین (عبرت و) نشانه‌های برای اهل یقین است.');
INSERT INTO "hussein_taji" VALUES(51,21,'51:21','و (نیز) در وجود خودتان، آیا نمی‌بینید؟');
INSERT INTO "hussein_taji" VALUES(51,22,'51:22','و رزق (و روزی) شما، و آنچه به شما وعده داده می‌شود در آسمان است.');
INSERT INTO "hussein_taji" VALUES(51,23,'51:23','پس سوگند به پروردگار آسمان و زمین که آن (وعده‌ها) حق است، همان گونه که شما (با یکدیگر) سخن می‌گویید.');
INSERT INTO "hussein_taji" VALUES(51,24,'51:24','(ای پیامبر) آیا خبر مهمانان گرامی ابراهیم به تو رسیده است؟!');
INSERT INTO "hussein_taji" VALUES(51,25,'51:25','آنگاه که بر او وارد شدند و گفتند: «سلام» (ابراهیم در جواب) گفت: «سلام» (و با خود گفت:) گروهی نا شناس هستید».');
INSERT INTO "hussein_taji" VALUES(51,26,'51:26','پس پنهانی به سوی همسرش رفت و گوسالۀ (بریان شده) فربهی (برای آن‌ها) آورد.');
INSERT INTO "hussein_taji" VALUES(51,27,'51:27','سپس آن را به آنان نزدیک کرد، و گفت: «آیا نمی‌خورید؟».');
INSERT INTO "hussein_taji" VALUES(51,28,'51:28','پس (چون دید دست به سوی غذا دراز نمی‌کنند) از آن‌ها احساس ترس (و وحشت) کرد، (آن‌ها) گفتند: «نترس (ما فرستادگان پروردگار تو ایم)» و او را به (تولد) پسری دانا بشارت دادند.');
INSERT INTO "hussein_taji" VALUES(51,29,'51:29','آنگاه همسرش (ساره) فریاد زنان پیش آمد و به صورت خود زد و گفت: «(من) پیر زنی نازا هستم (چگونه فرزند می‌زایم؟!) [ آن زمان حضرت ساره علیها السلام ساله بود. (تفسیر قرطبی 17/47).]».');
INSERT INTO "hussein_taji" VALUES(51,30,'51:30','(فرشتگان) گفتند: «پروردگار تو این چنین فرموده است، و بی‌گمان او حکیم داناست».');
INSERT INTO "hussein_taji" VALUES(51,31,'51:31','(ابراهیم) گفت: «پس ای فرستادگان (الله) کار و مأموریت شما چیست؟!».');
INSERT INTO "hussein_taji" VALUES(51,32,'51:32','(فرشتگان) گفتند: «بی‌گمان ما بسوی قومی مجرم (و گناهکار) [ یعنی قوم لوط که گناهانی از قبیل شرک، لواطت، و فساد در زمین و .. در میان آنها وجود داشت.] فرستاده شده‌ایم،');
INSERT INTO "hussein_taji" VALUES(51,33,'51:33','تا سنگ‌های از گل بر (سر) آن‌ها فرود آوریم.');
INSERT INTO "hussein_taji" VALUES(51,34,'51:34','(سنگ‌هایی) که نزد پروردگارت برای اسراف‌کاران نشان گذاری شده است.');
INSERT INTO "hussein_taji" VALUES(51,35,'51:35','پس کسانی از مؤمنان را که در آن (شهرهای قوم لوط) بودند (پیش از نزول عذاب) بیرون آوردیم.');
INSERT INTO "hussein_taji" VALUES(51,36,'51:36','و جز یک خانه از مسلمانان در آن (شهرها) نیافتیم.');
INSERT INTO "hussein_taji" VALUES(51,37,'51:37','و در آن (شهرهای ویران شده) نشانۀ (روشن) برای کسانی‌که از عذاب دردناک می‌ترسند بر جای گذاشتیم [ این نشانه‌ها تاکنون باقی است، این آبادی‌ها در (ساحل) جنوبی دریای اردن (بحر المیت) قرار داشت و این تنها دریایی است که هیچ نوع موجودات زنده‌ای در آن زندگی نمی‌کند.].');
INSERT INTO "hussein_taji" VALUES(51,38,'51:38','و (نیز) در (داستان) موسی (نشانه و عبرتی است) هنگامی‌که او را با دلیلی آشکار به سوی فرعون فرستادیم،');
INSERT INTO "hussein_taji" VALUES(51,39,'51:39','پس (فرعون) با قدرت (و لشکر) خویش روی گرداند، و گفت: «(این مرد) جادوگر یا دیوانه است».');
INSERT INTO "hussein_taji" VALUES(51,40,'51:40','آنگاه او و لشکریانش را گرفتیم و آنان را به دریا افکندیم در حالی‌که او سزاوار سرزنش بود.');
INSERT INTO "hussein_taji" VALUES(51,41,'51:41','(و (نیز) در (داستان قوم) عاد (نشانه‌ای است) هنگامی‌که تندبادی بی خیر و برکت بر آنان فرستادیم.');
INSERT INTO "hussein_taji" VALUES(51,42,'51:42','(تندبادی) که بر هیچ چیز نمی‌گذشت مگر اینکه آن را مانند استخوان پوسیده می‌گرداند.');
INSERT INTO "hussein_taji" VALUES(51,43,'51:43','و (همچنین) در (داستان قوم) ثمود (نشانه‌ای است) هنگامی‌که به آن‌ها گفته شد: «مدتی کوتاه (از زندگی) بهره‌مند شوید (که تنها سه روز فرصت دارید)».');
INSERT INTO "hussein_taji" VALUES(51,44,'51:44','آنگاه آن‌ها از فرمان پروردگارشان سر پیچی کردند، پس صاعقه آن‌ها را در بر گرفت در حالی که آنان (به عقوبت خود) نگاه می‌کردند.');
INSERT INTO "hussein_taji" VALUES(51,45,'51:45','(چنان بر زمین افتادند) که توان بر خاستن نداشتند، و نتوانستند (برای خود) انتقام بگیرند.');
INSERT INTO "hussein_taji" VALUES(51,46,'51:46','و قوم نوح را پیش از این (هلاک نمودیم)، زیرا آن‌ها قومی بدکار (و فاسق) بودند.');
INSERT INTO "hussein_taji" VALUES(51,47,'51:47','و (ما) آسمان را با قوت (و قدرت عظیم) بنا کردیم و بی‌گمان (بر و سعت پهناوری آن) تواناییم.');
INSERT INTO "hussein_taji" VALUES(51,48,'51:48','و زمین را گستردیم پس چه نیک گستراننده‌ای هستیم،');
INSERT INTO "hussein_taji" VALUES(51,49,'51:49','و از هر چیز جفت آفریدیم، شاید شما پند گیرید.');
INSERT INTO "hussein_taji" VALUES(51,50,'51:50','(ای پیامبر! بگو:) «پس به سوی الله بگریزید، بی‌گمان من از جانب او برای شما هشدار دهنده‌ای آشکار هستم.');
INSERT INTO "hussein_taji" VALUES(51,51,'51:51','و معبود دیگری با الله قرار ندهید، به راستی من از جانب او برای شما هشدار دهنده‌ای آشکار هستم».');
INSERT INTO "hussein_taji" VALUES(51,52,'51:52','همچنین بر پیشینیان آن‌ها هیچ پیامبری نیامد، مگر اینکه گفتند: «(او) جادوگر یا دیوانه است».');
INSERT INTO "hussein_taji" VALUES(51,53,'51:53','آیا آن‌ها یکدیگر را به آن سفارش کرده بودند؟ بلکه آن‌ها مردمی طغیان‌گر هستند.');
INSERT INTO "hussein_taji" VALUES(51,54,'51:54','پس (ای پیامبر) از آن‌ها روی بگردان، که هرگز تو سزاوار ملامت نیستی.');
INSERT INTO "hussein_taji" VALUES(51,55,'51:55','و (پیوسته) پند (و تذکر) بده، زیرا که بی‌گمان (پند و) تذکر مؤمنان را سود می‌بخشد.');
INSERT INTO "hussein_taji" VALUES(51,56,'51:56','و من جن و انس را نیافریده ام مگر برای اینکه مرا عبادت کنند،');
INSERT INTO "hussein_taji" VALUES(51,57,'51:57','هرگز از آن‌ها روزیی نمی‌خواهم، و نمی خواهم که مرا طعام دهند.');
INSERT INTO "hussein_taji" VALUES(51,58,'51:58','بی‌گمان الله است که روزی دهنده است، (و او) قدرتمند استوار است.');
INSERT INTO "hussein_taji" VALUES(51,59,'51:59','پس بی‌شک برای کسانی‌که ستم کردند بهره‌ای (از عذاب) است همانند بهرۀ یاران‌شان (از ستمگران پیشین)، پس نباید شتاب بکنند!');
INSERT INTO "hussein_taji" VALUES(51,60,'51:60','پس وای بر کسانی‌که کافر شدند، از روزشان (= روز قیامت) که وعده داده می‌شوند!');
INSERT INTO "hussein_taji" VALUES(52,1,'52:1','سوگند به (کوه) طور [ همان جایی که الله متعال با حضرت موسی علیه السلام سخن گفت.]،');
INSERT INTO "hussein_taji" VALUES(52,2,'52:2','و (سوگند) به کتاب نوشته شده،');
INSERT INTO "hussein_taji" VALUES(52,3,'52:3','در صحیفه‌ای گشوده.');
INSERT INTO "hussein_taji" VALUES(52,4,'52:4','و (سوگند) به «بیت المعمور» [ «بیت المعمور»: خانه‌ای بموازات خانه کعبه در آسمان هفتم قرار دارد که روزانه هفتاد هزار فرشته برای عبادت وارد آن می‌شوند، پس هرگاه یکبار وارد شدند دوباره باز نگردند.].');
INSERT INTO "hussein_taji" VALUES(52,5,'52:5','و به سقف بر افراشته شده،');
INSERT INTO "hussein_taji" VALUES(52,6,'52:6','و دریای مملو.');
INSERT INTO "hussein_taji" VALUES(52,7,'52:7','بی‌گمان عذاب پروردگارت واقع شدنی است.');
INSERT INTO "hussein_taji" VALUES(52,8,'52:8','آن را هیچ دفع کننده‌ای نیست.');
INSERT INTO "hussein_taji" VALUES(52,9,'52:9','روزی‌که آسمان بشدت بلرزد، (و به حرکت درآید).');
INSERT INTO "hussein_taji" VALUES(52,10,'52:10','و کوه‌ها (از جاکنده) به تندی روان گردد.');
INSERT INTO "hussein_taji" VALUES(52,11,'52:11','پس در آن روز (قیامت) وای بر تکذیب کنندگان.');
INSERT INTO "hussein_taji" VALUES(52,12,'52:12','کسانی‌که در سخنان (و کارهای) باطل به بازی مشغولند.');
INSERT INTO "hussein_taji" VALUES(52,13,'52:13','روزی‌که آن‌ها را به زور به سوی آتش (جهنم) می‌رانند.');
INSERT INTO "hussein_taji" VALUES(52,14,'52:14','(خازنان جهنم به آن‌ها می‌گویند:) «این (همان) آتشی است که شما آن را تکذیب می‌کردید.');
INSERT INTO "hussein_taji" VALUES(52,15,'52:15','آیا این (آتش و عذاب) جادو است یا شما نمی‌بینید؟');
INSERT INTO "hussein_taji" VALUES(52,16,'52:16','به آن در آیید (و بسوزید) پس صبر کنید یا صبر نکنید، برای شما یکسان است، تنها (به کیفر) آنچه انجام می‌دادید جزا داده می‌شوید!».');
INSERT INTO "hussein_taji" VALUES(52,17,'52:17','بی‌گمان پرهیزگاران در باغ‌ها (ی بهشت) و نعمت‌ها (ی فراوان) هستند.');
INSERT INTO "hussein_taji" VALUES(52,18,'52:18','و از آنچه پروردگارشان به آن‌ها داده مسرورند، و پروردگارشان آن‌ها را از عذاب جهنم محفوظ داشته است.');
INSERT INTO "hussein_taji" VALUES(52,19,'52:19','(به آن‌ها گفته می‌شود:) «بخورید و بیاشامید، گوارای‌تان باد، به (خاطر) آنچه که (در دنیا) انجام می‌دادید».');
INSERT INTO "hussein_taji" VALUES(52,20,'52:20','(در حالی‌که آن‌ها) بر تخت‌هایی که کنارهم نهاده شده تکیه می‌زنند، و حوریان گشاده چشم (بهشتی) را به همسری آن‌ها در می‌آوریم.');
INSERT INTO "hussein_taji" VALUES(52,21,'52:21','و کسانی‌که ایمان آوردند و فرزندان‌شان (نیز) در ایمان از آنان پیروی کردند، فرزندان‌شان را (در بهشت) به آن‌ها ملحق می‌کنیم، و از عمل آن‌ها چیزی نمی‌کاهیم، و هرکس درگرو دستاوردِ (و اعمال) خویش است.');
INSERT INTO "hussein_taji" VALUES(52,22,'52:22','و پیوسته ازهر میوه و گوشتی که بخواهند در اختیارشان می‌گذاریم.');
INSERT INTO "hussein_taji" VALUES(52,23,'52:23','در آنجا از یکدیگر جام (شراب طهور) می‌گیرند که نه بیهوده‌گویی در آن است و نه گناهی،');
INSERT INTO "hussein_taji" VALUES(52,24,'52:24','و همواره پسرانی همچون مرواید درون صدف برای (خدمت) آنان بر گردشان می‌چرخند.');
INSERT INTO "hussein_taji" VALUES(52,25,'52:25','و (بهشتیان) رو به یکدیگر می‌کنند و از (حال گذشتۀ) یکدیگر سؤال می‌نمایند.');
INSERT INTO "hussein_taji" VALUES(52,26,'52:26','می‌گویند: «ما پیش از این در میان خانوادۀ خود (از عذاب الهی) ترسان بودیم.');
INSERT INTO "hussein_taji" VALUES(52,27,'52:27','پس الله بر ما منتّ نهاد و ما را از عذاب گرم و کشنده (جهنم) حفظ کرد.');
INSERT INTO "hussein_taji" VALUES(52,28,'52:28','همانا ما پیش از این او را می‌خواندیم، بی‌گمان او نیکوکار مهربان است.');
INSERT INTO "hussein_taji" VALUES(52,29,'52:29','پس (ای پیامبر) پند (و تذکّر) ده، که تو به فضل و نعمت پروردگارت کاهن و دیوانه نیستی.');
INSERT INTO "hussein_taji" VALUES(52,30,'52:30','آیا می‌گویند: «او شاعری است که دربارۀ او منتظر حوادث روزگاریم، (و مرگش را انتظار می‌کشیم)».');
INSERT INTO "hussein_taji" VALUES(52,31,'52:31','بگو: «انتظار بکشید، که من (نیز) با شما از انتظار کشندگانم».');
INSERT INTO "hussein_taji" VALUES(52,32,'52:32','آیا عقل‌هایشان آن‌ها را به این (خیالات باطل) دستور می‌دهد، یا آن‌ها گروهی طغیان‌گرند؟!');
INSERT INTO "hussein_taji" VALUES(52,33,'52:33','آیا می‌گویند: «(محمد) آن (= قرآن) را بافته (و به الله نسبت داده) است؟» (خیر، چنین نیست) بلکه ایمان نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(52,34,'52:34','پس اگر راست گویند باید سخنی مانند آن بیاورند.');
INSERT INTO "hussein_taji" VALUES(52,35,'52:35','آیا آن‌ها از هیچ (و بدون آفریننده)، آفریده شده‌اند، یا خود خالق (خویش) اند.');
INSERT INTO "hussein_taji" VALUES(52,36,'52:36','آیا آن‌ها آسمان‌ها و زمین را آفریده‌اند؟! (خیر) بلکه آن‌ها یقین نمی‌کنند.');
INSERT INTO "hussein_taji" VALUES(52,37,'52:37','آیا خزائن پروردگارت نزد آن‌هاست؟! یا آن‌ها (بر همه چیز) تسلط دارند؟!');
INSERT INTO "hussein_taji" VALUES(52,38,'52:38','آیا نردبانی دارند (که از آن به آسمان‌ها بالا می‌روند و) از آن (اسرار وحی را) می‌شنوند؟ پس باید شنونده شان (بر این ادعا) دلیل روشنی بیاورد.');
INSERT INTO "hussein_taji" VALUES(52,39,'52:39','آیا برای او (= الله) دختران است و برای شما پسران؟!');
INSERT INTO "hussein_taji" VALUES(52,40,'52:40','(ای پیامبر) آیا از آن‌ها پاداشی درخواست می‌کنی، که (آن‌ها از ادای آن در رنجند و) برایشان (گران و) سنگین است؟!');
INSERT INTO "hussein_taji" VALUES(52,41,'52:41','آیا (اسرار) غیب نزد آن‌هاست، پس آنان (از آن) می‌نویسند؟!.');
INSERT INTO "hussein_taji" VALUES(52,42,'52:42','آیا نیرنگ (و نقشۀ پلیدی برای تو) می‌خواهند (بکشند)؟!، پس کسانی‌که کافر شدند خودشان گرفتار نیرنگ اند.');
INSERT INTO "hussein_taji" VALUES(52,43,'52:43','آیا آن‌ها معبودی غیر الله دارند؟! پاک و منزه است الله، از آنچه شریک او قرار می‌دهند.');
INSERT INTO "hussein_taji" VALUES(52,44,'52:44','و اگر (مشرکان) پاره‌ای از آسمان (برای عذاب‌شان) افتاده ببینند، می‌گویند: «(این) ابری متراکم است».');
INSERT INTO "hussein_taji" VALUES(52,45,'52:45','پس (ای پیامبر) آن‌ها را وا گذار، تا آن روزشان (= روز قیامت) را که در آن بی هوش می‌شوند ملاقات کنند!');
INSERT INTO "hussein_taji" VALUES(52,46,'52:46','روزی‌که نیرنگ (و حیلۀ) شان سودی به حال‌شان ندارد، و نه آن‌ها یاری می‌شوند.');
INSERT INTO "hussein_taji" VALUES(52,47,'52:47','و بی‌گمان برای کسانی‌که ستم کردند، عذابی غیر از آن (نیز در همین دنیا) است، ولی بیشترشان نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(52,48,'52:48','(ای پیامبر) برای حکم پروردگارت شکیبا باش، زیرا تو زیر نظر (و در حفاظت) ما هستی، و هنگامی‌که بر می‌خیزی به ستایش پروردگارت تسبیح گوی.');
INSERT INTO "hussein_taji" VALUES(52,49,'52:49','و (همچنین) بخشی از شب او را تسبیح گوی و (نیز) هنگام ناپدید شدن ستارگان.');
INSERT INTO "hussein_taji" VALUES(53,1,'53:1','سوگند به ستاره چون فرود افتد،');
INSERT INTO "hussein_taji" VALUES(53,2,'53:2','(که) یار شما (= محمد صلی الله علیه وسلم) گمراه نشده و راه را گم نکرده است،');
INSERT INTO "hussein_taji" VALUES(53,3,'53:3','و از روی هوای نفس سخن نمی‌گوید.');
INSERT INTO "hussein_taji" VALUES(53,4,'53:4','این نیست جز آنچه به او وحی می‌شود (و بجز وحی چیزی نمی‌گوید).');
INSERT INTO "hussein_taji" VALUES(53,5,'53:5','(فرشته‌ای) بس نیرومند (= جبرئیل) او را تعلیم داده است،');
INSERT INTO "hussein_taji" VALUES(53,6,'53:6','(همان فرشتۀ) نیرومندی که آنگاه راست و درست (به شکل حقیقی فرا رویش) ایستاد.');
INSERT INTO "hussein_taji" VALUES(53,7,'53:7','در حالی‌که او در افق اعلی (آسمان) بود.');
INSERT INTO "hussein_taji" VALUES(53,8,'53:8','سپس (جبرئیل) نزدیک شد، آنگاه فرو آمد (و نزدیک‌تر شد).');
INSERT INTO "hussein_taji" VALUES(53,9,'53:9','پس (فاصلۀ او با پیامبر) به قدر دو کمان یا کمتر بود.');
INSERT INTO "hussein_taji" VALUES(53,10,'53:10','آنگاه (الله) آنچه را باید وحی می‌کرد، به بنده‌اش وحی نمود.');
INSERT INTO "hussein_taji" VALUES(53,11,'53:11','قلب (پیامبر) آنچه را دید، دروغ نگفت.');
INSERT INTO "hussein_taji" VALUES(53,12,'53:12','آیا با او دربارۀ آنچه می‌بیند مجادله می‌کنید؟!.');
INSERT INTO "hussein_taji" VALUES(53,13,'53:13','و به راستی بار دیگر (نیز) او را دید [ جبرئیل علیه السلام را در صورت حقیقی، در حالی‌که ششصد بال داشت دیدند. (صحیح بخاری و تفسیر ابن کثیر)].');
INSERT INTO "hussein_taji" VALUES(53,14,'53:14','نزد «سدرة المنتهی» [ درخت سدر عظیمی که در آسمان هفتم است و علم هیچ کس از آن فراتر نرود. (تفسیر طبری)].');
INSERT INTO "hussein_taji" VALUES(53,15,'53:15','که «جَنَّةُ الْمَأْوَىٰ» نزد آن (درخت) است.');
INSERT INTO "hussein_taji" VALUES(53,16,'53:16','چون (درخت) «سدره» را چیزی (= نوری) پوشاند.');
INSERT INTO "hussein_taji" VALUES(53,17,'53:17','چشم (پیامبر) خطا نکرد و (از حد) در نگذشت.');
INSERT INTO "hussein_taji" VALUES(53,18,'53:18','به راستی (او) پاره‌ای از نشانه‌های بزرگ پروردگارش را دید [ این آیه دربارۀ معراج پیامبر صلی الله علیه وسلم می‌باشد که رسول الله صلی الله علیه وسلم با روح و جسد به آسمان‌ها رفتند. (اختصار از صحیح بخاری شماره .].');
INSERT INTO "hussein_taji" VALUES(53,19,'53:19','آیا «لات» و «عزّی» را دیده‌اید؟!');
INSERT INTO "hussein_taji" VALUES(53,20,'53:20','و «منات» (بت) سومی بی‌ارزش را؟.');
INSERT INTO "hussein_taji" VALUES(53,21,'53:21','آیا شما را پسر باشد و او را دختر؟');
INSERT INTO "hussein_taji" VALUES(53,22,'53:22','در این صورت، این تقسیمی غیر عادلانه است.');
INSERT INTO "hussein_taji" VALUES(53,23,'53:23','این‌ها چیزی جز نام‌هایی نیست که شما و پدارن‌تان بر آن‌ها گذاشته‌اید و الله دلیلی بر (صدق مدعی شما) نازل نکرده است. آن‌ها جز از گمان (های بد و بی‌اساس) و هوای نفس پیروی نمی‌کنند، در حالی‌که هدایت از (سوی) پروردگارشان برای آن‌ها آمده است.');
INSERT INTO "hussein_taji" VALUES(53,24,'53:24','آیا انسان هر چه آرزو کند برایش میسّر است؟!');
INSERT INTO "hussein_taji" VALUES(53,25,'53:25','پس آخرت و دنیا از آنِ الله است.');
INSERT INTO "hussein_taji" VALUES(53,26,'53:26','و چه بسیار فرشتگانی که در آسمان‌ها هستند که شفاعت آن‌ها هیچ سودی نمی‌بخشد، مگر پس از آنکه الله برای هرکس که بخواهد اجازه دهد و راضی باشد.');
INSERT INTO "hussein_taji" VALUES(53,27,'53:27','بی‌گمان کسانی‌که به آخرت ایمان ندارند، فرشتگان را به نام دختران نام‌گذاری می‌کنند.');
INSERT INTO "hussein_taji" VALUES(53,28,'53:28','و آن‌ها به آن هیچ دانشی ندارند، جز از گمان (بی‌اساس) پیروی نمی‌کنند، و یقیناً گمان (انسان را) از (شناخت) حق بی‌نیاز نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(53,29,'53:29','پس (ای پیامبر) از کسی‌که از ذکر ما روی می‌گرداند، و جز زندگی (مادی) دنیا را نخواهد، اعراض کن.');
INSERT INTO "hussein_taji" VALUES(53,30,'53:30','این آخرین حد دانش آن‌هاست، بی‌گمان پروردگار تو به کسانی‌که از راه او گمراه شده‌اند داناتر است، و او (نیز) به کسانی‌که هدایت یافتند داناتر است.');
INSERT INTO "hussein_taji" VALUES(53,31,'53:31','و آنچه در آسمان‌ها و آنچه در زمین است از آنِ الله است تا کسانی را که (کار) بد کردند به (سبب) آنچه کرده‌اند کیفر دهد، و کسانی را که نیکوکاری کردند با (بهشت) نیکو پاداش دهد.');
INSERT INTO "hussein_taji" VALUES(53,32,'53:32','(همان) کسانی‌که از گناهان کبیره و اعمال زشت ـ جز گناهان صغیره ـ دوری می‌کنند بی‌گمان پروردگار تو گسترده آمرزش است، و او نسبت به شما داناتر است، هنگامی‌که شما را از زمین پدید آورد، و هنگامی‌که شما در شکم مادران‌تان بصورت جنین‌هایی بودید، پس خودتان را نستائید (و پاک نشمارید) او به کسانی‌که پرهیزکاری نمودند داناتر است.');
INSERT INTO "hussein_taji" VALUES(53,33,'53:33','آیا دیده‌ای کسی را که (از حق) روی گرداند؟!');
INSERT INTO "hussein_taji" VALUES(53,34,'53:34','و اندکی (مال) بخشید و (سپس سنگدل شد و) خود داری کرد.');
INSERT INTO "hussein_taji" VALUES(53,35,'53:35','آیا نزد او علم غیب است پس او (همه چیز را) می‌بیند؟!');
INSERT INTO "hussein_taji" VALUES(53,36,'53:36','یا از آنچه در صحیفه‌های موسی بوده، آگاه نشده است؟!');
INSERT INTO "hussein_taji" VALUES(53,37,'53:37','و (نیز آنچه در صحیفه‌های) ابراهیم، همان کسی‌که وفا دار بود؟!');
INSERT INTO "hussein_taji" VALUES(53,38,'53:38','که هیچ کس بار گناه دیگری را بر دوش نمی‌کشد.');
INSERT INTO "hussein_taji" VALUES(53,39,'53:39','و اینکه برای انسان (بهره‌ای) جز آنچه سعی کرده، نیست.');
INSERT INTO "hussein_taji" VALUES(53,40,'53:40','و اینکه به زودی (حاصل) تلاش و سعی او دیده خواهد شد.');
INSERT INTO "hussein_taji" VALUES(53,41,'53:41','سپس او به پاداشی تمام (و کافی) پاداش داده خواهد شد،');
INSERT INTO "hussein_taji" VALUES(53,42,'53:42','و یقیناً بازگشت (همۀ امور) به سوی پروردگار توست.');
INSERT INTO "hussein_taji" VALUES(53,43,'53:43','و اینکه او خنداند و گریاند.');
INSERT INTO "hussein_taji" VALUES(53,44,'53:44','و اینکه او میراند و زنده کرد.');
INSERT INTO "hussein_taji" VALUES(53,45,'53:45','و اینکه او دو جفت نر و ماده آفرید.');
INSERT INTO "hussein_taji" VALUES(53,46,'53:46','از نطفه‌ای چون (در رحم) ریخته شود.');
INSERT INTO "hussein_taji" VALUES(53,47,'53:47','و اینکه پدید آوردن بار دیگر بر (عهدۀ) او (= الله) است.');
INSERT INTO "hussein_taji" VALUES(53,48,'53:48','و اینکه اوست که بی‌نیاز کرد و سرمایه بخشید.');
INSERT INTO "hussein_taji" VALUES(53,49,'53:49','و اینکه اوست که پروردگار (ستاره) «شعری» است. [ستارۀ شعری در زمان جاهلیت پرستیده می‌شد بدین خاطر الله آن را ذکر نموده است. (تفسیر ابن کثیر و تفسیر سعدی).]');
INSERT INTO "hussein_taji" VALUES(53,50,'53:50','و اینکه اوست که «عاد نخستین» را هلاک کرد.');
INSERT INTO "hussein_taji" VALUES(53,51,'53:51','و (نیز) «ثمود» را (هلاک کرد) پس (کسی را) باقی نگذاشت.');
INSERT INTO "hussein_taji" VALUES(53,52,'53:52','(و (همچنین) قوم نوح را پیش از آن (هلاک نمود). بی‌گمان آن‌ها از همه ظالم‌تر و سرکش‌تر بودند.');
INSERT INTO "hussein_taji" VALUES(53,53,'53:53','و (شهرهای) مؤتفکه (= قوم لوط) را زیر و رو (و نابود) کرد.');
INSERT INTO "hussein_taji" VALUES(53,54,'53:54','پس آن‌ها را با آنچه سزاوار بودند از عذاب سنگین و دردناک پوشانید.');
INSERT INTO "hussein_taji" VALUES(53,55,'53:55','پس (ای انسان نا سپاس) به کدامیک از نعمت‌های پروردگارت شک داری؟!');
INSERT INTO "hussein_taji" VALUES(53,56,'53:56','این (پیامبر) هشدار دهنده‌ای از (قبیل) هشدار دهندگان پیشین است.');
INSERT INTO "hussein_taji" VALUES(53,57,'53:57','قیامت نزدیک شده است.');
INSERT INTO "hussein_taji" VALUES(53,58,'53:58','و هیچ کس جز الله آشکارش نکند (و نتواند سختی‌های آن را دفع کند).');
INSERT INTO "hussein_taji" VALUES(53,59,'53:59','آیا از این سخن تعجب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(53,60,'53:60','و می‌خندید و نمی‌گریید؟!');
INSERT INTO "hussein_taji" VALUES(53,61,'53:61','و شما همواره در غفلت (و هوسرانی) هستید.');
INSERT INTO "hussein_taji" VALUES(53,62,'53:62','پس (همه) برای الله سجده کنید، و او را بپرستید.');
INSERT INTO "hussein_taji" VALUES(54,1,'54:1','قیامت نزدیک شد و ماه بشکافت [ دونیم شدن ماه، معجزه‌ای از معجزات رسول الله صلی الله علیه وسلم می‌باشد، که در احادیث متواتر با اسناد‌‌‌‌های صحیح روایت شده است (تفسیر ابن کثیر).].');
INSERT INTO "hussein_taji" VALUES(54,2,'54:2','و اگر (کافران) معجزه‌ای ببینند، روی بگردانند و گویند: «(این) جادویی قوی است».');
INSERT INTO "hussein_taji" VALUES(54,3,'54:3','و آن‌ها (آیات و نشانه‌های الهی را) تکذیب کردند و از هوی نفس خود پیروی کردند، و هرکاری قرار‌گاهی دارد.');
INSERT INTO "hussein_taji" VALUES(54,4,'54:4','و به راستی برای آنان به‌اندازۀ کافی خبر‌‌‌های (‌‌‌پندآمیزی) آمده است که (از گناهان و بدی‌ها) باز می‌دارد.');
INSERT INTO "hussein_taji" VALUES(54,5,'54:5','(این آیات) حکمت (و دانش) تمام (و رسا) است. پس (برای افراد جاهل و لجوج) هشدار‌ها سودی نمی‌بخشد.');
INSERT INTO "hussein_taji" VALUES(54,6,'54:6','بنابراین (ای پیامبر!) از آن‌ها روی بگردان، (تا آن) روزی‌که دعوت کنند‌‌‌ه‌ای (مردم را) به امر وحشت‌ناکی دعوت کند.');
INSERT INTO "hussein_taji" VALUES(54,7,'54:7','در حالی‌که چشمان‌شان فروهشته باشد از قبرها بیرون آیند گویی که آن‌ها ملخ‌هایی پراکنده هستند.');
INSERT INTO "hussein_taji" VALUES(54,8,'54:8','و به سوی دعوت کننده می‌شتابند. کافران می‌گویند: «این روز سختی است».');
INSERT INTO "hussein_taji" VALUES(54,9,'54:9','پیش از آن‌ها قوم نوح تکذیب کرده بودند، پس بندۀ ما (نوح) را تکذیب کردند و گفتند: «(او) دیوانه است» و (با او) درشتی کردند (و آزردند).');
INSERT INTO "hussein_taji" VALUES(54,10,'54:10','پس او پروردگارش را خواند (و عرض کرد): «من مغلوب شده‌ام، پس یاریم فرما (و از آن‌ها انتقام بگیر)».');
INSERT INTO "hussein_taji" VALUES(54,11,'54:11','آنگاه درهای آسمان را با آبی (فراوان و) فروریزنده گشودیم.');
INSERT INTO "hussein_taji" VALUES(54,12,'54:12','و از زمین چشمه‌های جوشاندیم (و جاری نمودیم). پس این (دو) آب (آسمان و زمین) برای امری که مقدر شده بود باهم در‌آمیختند.');
INSERT INTO "hussein_taji" VALUES(54,13,'54:13','و او (= نوح) را بر (مرکبی) ساخته شده از تخته و میخ سوار کردیم.');
INSERT INTO "hussein_taji" VALUES(54,14,'54:14','زیر نظر (و حفاظت) ما روان بود، کیفری بود برای کسانی‌که کافر شده بودند.');
INSERT INTO "hussein_taji" VALUES(54,15,'54:15','و به راستی ما این (ماجرا) را (به عنوان) نشانه‌ای بر جای گذاشتیم، پس آیا کسی هست که پند گیرد؟!');
INSERT INTO "hussein_taji" VALUES(54,16,'54:16','پس (بنگرید) عذاب و هشدار‌های من چگونه بود؟!');
INSERT INTO "hussein_taji" VALUES(54,17,'54:17','و یقیناً ما قرآن را برای تذکر آسان نمودیم، پس آیا کسی هست که متذکر شود؟!');
INSERT INTO "hussein_taji" VALUES(54,18,'54:18','(قوم) عاد (نیز) تکذیب کردند، پس (بنگرید) عذاب و هشدار‌های من چگونه بود؟!');
INSERT INTO "hussein_taji" VALUES(54,19,'54:19','ما تند باد سرد (وحشت‌ناکی) در روزی شوم طولانی بر آن‌ها فرستادیم.');
INSERT INTO "hussein_taji" VALUES(54,20,'54:20','که مردم را (از جا) بر می‌کند، گویی که آن‌ها تنه‌های نخل ریشه کن شده‌اند.');
INSERT INTO "hussein_taji" VALUES(54,21,'54:21','پس (بنگرید) عذاب و هشدار‌های من چگونه بود؟!');
INSERT INTO "hussein_taji" VALUES(54,22,'54:22','و یقیناً ما قرآن را برای تذکر آسان نمودیم، پس آیا کسی هست که متذکر شود؟!');
INSERT INTO "hussein_taji" VALUES(54,23,'54:23','(قوم) ثمود (نیز) هشدار دهندگان را تکذیب کردند.');
INSERT INTO "hussein_taji" VALUES(54,24,'54:24','و گفتند: «آیا از یک بشری از (جنس) خودمان پیروی کنیم؟! (اگر چنین کنیم) آنگاه ما در گمراهی و دیوانگی خواهیم بود،');
INSERT INTO "hussein_taji" VALUES(54,25,'54:25','آیا از میان ما تنها بر او وحی نازل شده است؟ (خیر) بلکه او دروغگوی خود پسند است».');
INSERT INTO "hussein_taji" VALUES(54,26,'54:26','فردا خواهند دانست چه کسی دروغگوی خود پسند است.');
INSERT INTO "hussein_taji" VALUES(54,27,'54:27','بی‌گمان ما ماده شتر را برای آزمایش آن‌ها می‌فرستیم، پس (ای صالح) منتظر (پایان کار) شان باش و صبر پیشه کن. [بیرون آوردن ماده شتر از دل کوه معجزه‌ای بود که قوم صالح از پیامبر خود درخواست کرده بودند، و این معجزه تحقق یافت ولی باز هم انکار کردند و ماده شتر را پی کردند.]');
INSERT INTO "hussein_taji" VALUES(54,28,'54:28','و به آن‌ها خبر ده که همانا آب (قریه) در میان آن‌ها (و ناقه) تقسیم شده است، هرکه در نوبت خود باید حاضر شود.');
INSERT INTO "hussein_taji" VALUES(54,29,'54:29','پس آن‌ها یار خود را صدا زدند، آنگاه (آمد و) دست بکار شد و (شتر را) پی کرد.');
INSERT INTO "hussein_taji" VALUES(54,30,'54:30','پس (بنگرید) عذاب و هشدار‌های من چگونه بود؟!');
INSERT INTO "hussein_taji" VALUES(54,31,'54:31','بی‌گمان ما یک صیحه (= بانگ مرگباری) بر آن‌ها فرستادیم، پس (همگی) به صورت گیاه خشک خردشده‌ای در آمدند.');
INSERT INTO "hussein_taji" VALUES(54,32,'54:32','و یقیناً ما قرآن را برای تذکر آسان نمودیم، پس آیا کسی هست که متذکر شود؟!');
INSERT INTO "hussein_taji" VALUES(54,33,'54:33','قوم لوط هشداردهندگان را تکذیب کردند.');
INSERT INTO "hussein_taji" VALUES(54,34,'54:34','بی‌گمان ما شن باد تندی بر آن‌ها فرستادیم (و همگی را هلاک کردیم) جز خاندان لوط که سحرگاهان نجات‌شان دادیم.');
INSERT INTO "hussein_taji" VALUES(54,35,'54:35','(این) نعمتی بود از سوی ما، این گونه کسی را که شکر کند پاداش می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(54,36,'54:36','و به راستی (لوط) آن‌ها را از عقوبت (سخت) ما بیم داد، پس آن‌ها با هشدار‌ها مجادله (و ستیز) کردند.');
INSERT INTO "hussein_taji" VALUES(54,37,'54:37','و به راستی آن‌ها از او (= لوط) خواستند که مهمانش را (برای کار زشت) در اختیار‌شان بگذارد، پس ما دیدگان‌شان را کور کردیم، (و گفتیم:) «پس (طعم) عذاب مرا و (طعم) هشدار‌های مرا بچشید».');
INSERT INTO "hussein_taji" VALUES(54,38,'54:38','و یقیناً صبحگاهان عذابی پایدار آن‌ها را فروگرفت (و هلاک کرد).');
INSERT INTO "hussein_taji" VALUES(54,39,'54:39','(و گفتیم:) «پس (طعم) عذاب مرا (و طعم) هشدار‌های مرا بچشید».');
INSERT INTO "hussein_taji" VALUES(54,40,'54:40','و یقیناً ما قرآن را برای تذکر آسان نمودیم، پس آیا کسی هست که متذکر شود؟!');
INSERT INTO "hussein_taji" VALUES(54,41,'54:41','و به راستی هشدار دهندگان به سراغ خاندان فرعون آمدند.');
INSERT INTO "hussein_taji" VALUES(54,42,'54:42','(آن‌ها) همۀ آیات (و معجزات) ما را تکذیب کردند، پس ما آن‌ها را گرفتیم، گرفتن پیروزمندی توانا!');
INSERT INTO "hussein_taji" VALUES(54,43,'54:43','(ای قریش) آیا کفار شما از آن‌ها بهترند یا برای شما امان نامه‌ای در کتابهای (آسمانی پیشین) است.');
INSERT INTO "hussein_taji" VALUES(54,44,'54:44','یا می‌گویند: «ما جماعتی (نیرومند و) پیروزیم».');
INSERT INTO "hussein_taji" VALUES(54,45,'54:45','به زودی آن جمع (کفار قریش) شکست می‌خورد و پشت می‌کنند، (و پا به فرار می‌گذارند) [ مصداق این آیه در جنگ بدر تحقق یافت، رسول الله صلی الله علیه وسلم روز جنگ بدر پس از مناجات و راز و نیاز با پروردگارش در حالی‌که لباس جنگی پوشیده بود از چادر بیرون آمدند و این آیه را می‌خواندند. (صحیح بخاری ].');
INSERT INTO "hussein_taji" VALUES(54,46,'54:46','بلکه قیامت وعده‌گاه آن‌هاست، و قیامت سخت‌تر و تلخ‌تر است.');
INSERT INTO "hussein_taji" VALUES(54,47,'54:47','بی‌گمان گناهکاران در گمراهی و دیوانگی هستند.');
INSERT INTO "hussein_taji" VALUES(54,48,'54:48','روزی‌که بر چهره‌هایشان در آتش (جهنم) کشیده می‌شوند (و به آن‌ها گفته می‌شود:) «(طعم) آتش دوزخ را بچشید».');
INSERT INTO "hussein_taji" VALUES(54,49,'54:49','بی‌گمان ما همه چیز را به‌اندازه آفریدیم.');
INSERT INTO "hussein_taji" VALUES(54,50,'54:50','و فرمان ما جز یک بار نیست، همچون یک چشم برهم زدنی است.');
INSERT INTO "hussein_taji" VALUES(54,51,'54:51','و به راستی ما (کسانی‌که) همانند شما (بودند)، هلاک کردیم پس آیا کسی هست که متذکر شود (و پندگیرد)؟!');
INSERT INTO "hussein_taji" VALUES(54,52,'54:52','و هر چیزی را که انجام داده‌اند در نامه‌های (اعمال‌شان ثبت) است.');
INSERT INTO "hussein_taji" VALUES(54,53,'54:53','و هر (کار) کوچک و بزرگ نوشته شده است.');
INSERT INTO "hussein_taji" VALUES(54,54,'54:54','بی‌گمان پر هیزگاران در باغ‌ها و (کنار) نهرهای (بهشتی) هستند.');
INSERT INTO "hussein_taji" VALUES(54,55,'54:55','در (جایگاه و) مجلس صدق (و راستی) نزد فرمانروایی مقتدر.');
INSERT INTO "hussein_taji" VALUES(55,1,'55:1','(الله) رحمن');
INSERT INTO "hussein_taji" VALUES(55,2,'55:2','قرآن را تعلیم داد.');
INSERT INTO "hussein_taji" VALUES(55,3,'55:3','انسان را آفرید.');
INSERT INTO "hussein_taji" VALUES(55,4,'55:4','به او (نطق و) سخن گفتن آموخت.');
INSERT INTO "hussein_taji" VALUES(55,5,'55:5','خورشید و ماه به حساب منظم (و دقیقی) در حرکتند.');
INSERT INTO "hussein_taji" VALUES(55,6,'55:6','و گیاه و درخت (برای او) سجده می‌کنند. [مفسران دربارۀ مراد از «النجم» اختلاف نظر دارند، برخی «ستاره» ذکر کرده‌اند، و برخی دیگر به «گیاه» تفسیر کرده‌اند. (تفسیر ابن کثیر).]');
INSERT INTO "hussein_taji" VALUES(55,7,'55:7','و آسمان را بر افراشت و میزان را قرار داد.');
INSERT INTO "hussein_taji" VALUES(55,8,'55:8','تا در میزان تجاوز نکنید.');
INSERT INTO "hussein_taji" VALUES(55,9,'55:9','و وزن را بر اساس عدل بر پا دارید و میزان را کم نکنید.');
INSERT INTO "hussein_taji" VALUES(55,10,'55:10','و زمین را برای مردمان قرار داد (و گسترانید).');
INSERT INTO "hussein_taji" VALUES(55,11,'55:11','که در آن میوه‌ها و نخل‌های خوشه دار است.');
INSERT INTO "hussein_taji" VALUES(55,12,'55:12','و (در آن) دانۀ برگ‌دار (که بصورت کاه در می‌آید) و ریحان (و گیاهان خوشبو) است.');
INSERT INTO "hussein_taji" VALUES(55,13,'55:13','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,14,'55:14','انسان را از گِل خشکیده‌ای چون سفال آفرید،');
INSERT INTO "hussein_taji" VALUES(55,15,'55:15','و جن را از شعله‌ای از آتش خلق کرد.');
INSERT INTO "hussein_taji" VALUES(55,16,'55:16','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,17,'55:17','(او) پروردگار دو مشرق و پروردگار دو مغرب است.');
INSERT INTO "hussein_taji" VALUES(55,18,'55:18','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,19,'55:19','دو دریای (مختلف شور و شیرین) را به جریان آورد در حالی‌که با یکدیگر بر خورد می‌کنند.');
INSERT INTO "hussein_taji" VALUES(55,20,'55:20','میان آن دو حایلی است که یکی بر دیگری غلبه نمی‌کند. (و در هم نیا میزند).');
INSERT INTO "hussein_taji" VALUES(55,21,'55:21','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,22,'55:22','از آن دو (دریا) مروارید و مرجان بیرون می‌آید.');
INSERT INTO "hussein_taji" VALUES(55,23,'55:23','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,24,'55:24','و برای اوست کشتی‌هایی همچون کوه که در دریا به حرکت در می‌آیند.');
INSERT INTO "hussein_taji" VALUES(55,25,'55:25','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,26,'55:26','هر چه بر روی آن (= زمین) است، فانی شود.');
INSERT INTO "hussein_taji" VALUES(55,27,'55:27','و (تنها) روی پروردگار ذو الجلال و گرامی توست که باقی می‌ماند.');
INSERT INTO "hussein_taji" VALUES(55,28,'55:28','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,29,'55:29','(تمام) کسانی‌که در آسمان‌ها و زمین هستند از او سؤال (و درخواست نیاز) می‌کنند، و او هر روز در کاری است.');
INSERT INTO "hussein_taji" VALUES(55,30,'55:30','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,31,'55:31','ای دوگروه (انس و جن) بزودی به (حساب) شما می‌پردازیم.');
INSERT INTO "hussein_taji" VALUES(55,32,'55:32','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,33,'55:33','ای گروه جن و انس اگر می‌توانید از کناره‌ها (و مرز‌های) آسمان‌ها و زمین بگذرید، پس بگذرید، ولی جز با غلبه و نیرو (بی فوق العاده) نمی‌توانید بگذرید (که شما ندارید).');
INSERT INTO "hussein_taji" VALUES(55,34,'55:34','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,35,'55:35','شعله‌هایی از آتش، و دود بر شما (جن و انس) فرستاده می‌شود پس نمی‌توانید از خودتان دفاع کنید.');
INSERT INTO "hussein_taji" VALUES(55,36,'55:36','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,37,'55:37','پس آنگاه که آسمان بشکافد، پس همچون چرم (و روغن مذاب) سرخ‌گون شود (در آن روز قیامت حوادث هو‌لناکی رخ می‌دهد).');
INSERT INTO "hussein_taji" VALUES(55,38,'55:38','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,39,'55:39','پس در آن روز (قیامت) هیچ جن و انسی از گناهش پرسیده نشود (چون با علامات چهره‌های‌شان شناخته می‌شوند).');
INSERT INTO "hussein_taji" VALUES(55,40,'55:40','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,41,'55:41','گناهکاران از چهره‌هایشان شناخته می‌شوند، پس (آن‌ها را) با موی جلو سر و پاها (یشان) گرفته (و به دوزخ افکنده) می‌شوند.');
INSERT INTO "hussein_taji" VALUES(55,42,'55:42','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,43,'55:43','این (همان) جهنمی است که گناهکاران آن را انکار می‌کردند.');
INSERT INTO "hussein_taji" VALUES(55,44,'55:44','(امروز آن‌ها) در میان آن و آب سوزان می‌گردند.');
INSERT INTO "hussein_taji" VALUES(55,45,'55:45','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,46,'55:46','و برای کسی‌که از ایستادن در پیشگاه پروردگارش بترسد، دو باغ (بهشتی) است.');
INSERT INTO "hussein_taji" VALUES(55,47,'55:47','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,48,'55:48','(آن دو باغ) دارای (درختان و) شاخسار‌های بسیار است.');
INSERT INTO "hussein_taji" VALUES(55,49,'55:49','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,50,'55:50','در آن دو (باغ) دو چشمۀ روان است.');
INSERT INTO "hussein_taji" VALUES(55,51,'55:51','پس (ای گروه انس و جن)، کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,52,'55:52','در آن دو (باغ) از هر میوه‌ای دو نوع است.');
INSERT INTO "hussein_taji" VALUES(55,53,'55:53','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,54,'55:54','(بهشتیان) بر فرش‌هایی که آستر‌شان از دیبای ضخیم است، تکیه کرده‌اند، و میوۀ (آن) دو باغ) (بهشتی) در دسترس است.');
INSERT INTO "hussein_taji" VALUES(55,55,'55:55','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,56,'55:56','در آن (کاخ‌ها و باغ‌های بهشتی) حوریانی دیده فروهشته هستند، که هیچ انس و جن پیش از این‌ها (= مؤمنان) با آنان تماس نگرفته است.');
INSERT INTO "hussein_taji" VALUES(55,57,'55:57','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,58,'55:58','گویی آن‌ها (= حوریان) یاقوت و مرجانند.');
INSERT INTO "hussein_taji" VALUES(55,59,'55:59','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,60,'55:60','آیا پاداش نیکی جز نیکی است؟!');
INSERT INTO "hussein_taji" VALUES(55,61,'55:61','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,62,'55:62','و غیر از آن دو (باغ) دو باغ (بهشتی) دیگر است.');
INSERT INTO "hussein_taji" VALUES(55,63,'55:63','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,64,'55:64','(آن دو باغ) سر سبز که (از شدت سبزی) به سیاهی متمایل است.');
INSERT INTO "hussein_taji" VALUES(55,65,'55:65','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,66,'55:66','در آن دو (باغ بهشتی) دو چشمۀ جوشان است.');
INSERT INTO "hussein_taji" VALUES(55,67,'55:67','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,68,'55:68','در آن دو (باغ) میوه و نخل و انار است.');
INSERT INTO "hussein_taji" VALUES(55,69,'55:69','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,70,'55:70','در آن (کاخ‌ها و باغ‌های بهشتی زنانی) نیک سیرت و صورت هستند.');
INSERT INTO "hussein_taji" VALUES(55,71,'55:71','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,72,'55:72','حورانی که در خیمه‌های (بهشتی مستور) نگاه داشته شده‌اند.');
INSERT INTO "hussein_taji" VALUES(55,73,'55:73','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,74,'55:74','(دوشیزگانی که) هیچ انس و جن پیش از این‌ها (= مؤمنان) با آنان تماس نگرفته است.');
INSERT INTO "hussein_taji" VALUES(55,75,'55:75','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,76,'55:76','(در حالی‌که بهشتیان) بر بالش‌های سبز و فرش‌های نیکو تکیه زده‌اند،');
INSERT INTO "hussein_taji" VALUES(55,77,'55:77','پس (ای گروه انس و جن) کدامین نعمت‌های پروردگارتان را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(55,78,'55:78','(ای پیامبر) نام پروردگار صاحب جلال و گرامی تو با برکت و فرخنده است.');
INSERT INTO "hussein_taji" VALUES(56,1,'56:1','هنگامی‌که واقعۀ قیامت واقع شود.');
INSERT INTO "hussein_taji" VALUES(56,2,'56:2','که در واقع شدنش هیچ دروغی نیست.');
INSERT INTO "hussein_taji" VALUES(56,3,'56:3','(گروهی را) خوار کننده، (و گروهی را) بر‌افرازنده است [ گروه کافران را خوار می‌کند و به دوزخ روانه می‌کند، و گروه مؤمنان را سربلند و شاد به بهشت می‌فرستد. (تفسیر طبری و ابن کثیر).].');
INSERT INTO "hussein_taji" VALUES(56,4,'56:4','هنگامی‌که زمین بشدّت لرزانده شود.');
INSERT INTO "hussein_taji" VALUES(56,5,'56:5','و کوه‌ها (درهم کوبیده و) متلاشی شود.');
INSERT INTO "hussein_taji" VALUES(56,6,'56:6','پس چون غبار پراکنده گردد');
INSERT INTO "hussein_taji" VALUES(56,7,'56:7','و شما سه گروه (تقسیم) شوید:');
INSERT INTO "hussein_taji" VALUES(56,8,'56:8','پس (گروه نخست سعاد تمندان) یاران دست راست، (سعاد تمندان) یاران دست راست چه حال دارند؟');
INSERT INTO "hussein_taji" VALUES(56,9,'56:9','و (گروه دیگر شقاوتمندان) یاران دست چپ، (شقاوتمندان) یاران دست چپ چه حال دارند؟');
INSERT INTO "hussein_taji" VALUES(56,10,'56:10','و (سومین گروه) پیشگامان پیشرو.');
INSERT INTO "hussein_taji" VALUES(56,11,'56:11','آن‌ها مقربان هستند.');
INSERT INTO "hussein_taji" VALUES(56,12,'56:12','در باغ‌های پر نعمت (بهشت جای دارند).');
INSERT INTO "hussein_taji" VALUES(56,13,'56:13','گروهی بسیاری از پیشینیان.');
INSERT INTO "hussein_taji" VALUES(56,14,'56:14','و اندکی از آیندگان هستند.');
INSERT INTO "hussein_taji" VALUES(56,15,'56:15','بر تخت‌هایی چیده شده و مرصّع.');
INSERT INTO "hussein_taji" VALUES(56,16,'56:16','در حالی‌که رو به روی هم بر آن تکیه زده‌اند.');
INSERT INTO "hussein_taji" VALUES(56,17,'56:17','نو جوانانی جاودان پیوسته بر گرد‌‌شان می‌چرخند.');
INSERT INTO "hussein_taji" VALUES(56,18,'56:18','با قدح‌ها و کوزه‌ها و جام‌هایی از شرابی که (در جوی‌ها) جاری است.');
INSERT INTO "hussein_taji" VALUES(56,19,'56:19','(شرابی) که از آن نه سر درد گیرند و نه بیهوش شوند.');
INSERT INTO "hussein_taji" VALUES(56,20,'56:20','و هر میوه‌ای که خود انتخاب کنند.');
INSERT INTO "hussein_taji" VALUES(56,21,'56:21','و (نیز) گوشت پرنده (از هر نوعی) که میل داشته باشند.');
INSERT INTO "hussein_taji" VALUES(56,22,'56:22','و (همسرانی از) حوریان سیه چشم (دارند).');
INSERT INTO "hussein_taji" VALUES(56,23,'56:23','همچون مروارید پنهان در صدف.');
INSERT INTO "hussein_taji" VALUES(56,24,'56:24','(این‌ها) پاداشی است در برابر آنچه که انجام می‌دادند.');
INSERT INTO "hussein_taji" VALUES(56,25,'56:25','در آن (باغ‌های بهشتی) نه سخن لغو و بیهوده‌ای می‌شنوند و نه گفتار گناه آلود.');
INSERT INTO "hussein_taji" VALUES(56,26,'56:26','تنها یک سخن (می شنوند) سلام است، سلام.');
INSERT INTO "hussein_taji" VALUES(56,27,'56:27','و اصحاب دست راست (سعادت‌مند) چه (وضع و) حالی دارند اصحاب دست راست (سعادت‌‌مندان)؟؟!');
INSERT INTO "hussein_taji" VALUES(56,28,'56:28','در (میان) درختان «سدر» (= کنار) بی خار،');
INSERT INTO "hussein_taji" VALUES(56,29,'56:29','و درختان موز پربار تو برتو.');
INSERT INTO "hussein_taji" VALUES(56,30,'56:30','و سایه‌ای گسترده [ انس بن مالک رضی الله عنه می‌گوید: رسول الله صلی الله علیه وسلم فرمود: «در بهشت درختی است که شخص سواره، صد سال در سایۀ آن حرکت می‌کند و به آخر آن نمی‌رسد» و در روایت ابو هریره آمده: «اگر خواستید این آیه را بخوانید (ظل ممدود)» (صحیح بخاری و 3252 و صحیح مسلم 2826).].');
INSERT INTO "hussein_taji" VALUES(56,31,'56:31','و (در کنار) آبی جاری (و روان).');
INSERT INTO "hussein_taji" VALUES(56,32,'56:32','و میوۀ فراوان.');
INSERT INTO "hussein_taji" VALUES(56,33,'56:33','نه پایان پذیرد و نه (کسی را از آن) باز داشته شود،');
INSERT INTO "hussein_taji" VALUES(56,34,'56:34','و بسترهای برافراشته شده (و همسرانی گرانقدر)');
INSERT INTO "hussein_taji" VALUES(56,35,'56:35','بی‌گمان ما آن‌ها را به آفرینش نوینی آفریده‌ایم.');
INSERT INTO "hussein_taji" VALUES(56,36,'56:36','و آن‌ها را دوشیزه قرار داده‌ایم.');
INSERT INTO "hussein_taji" VALUES(56,37,'56:37','شوهر دوستان هم سن و سال،');
INSERT INTO "hussein_taji" VALUES(56,38,'56:38','(همۀ این‌ها) برای اصحاب دست راست (سعادت‌مندان) است.');
INSERT INTO "hussein_taji" VALUES(56,39,'56:39','که گروهی از پیشینیان.');
INSERT INTO "hussein_taji" VALUES(56,40,'56:40','و عده‌ای از آیندگان هستند.');
INSERT INTO "hussein_taji" VALUES(56,41,'56:41','و اصحاب دست چپ (شقاوت‌مندان) چه (وضع و) حالی دارند اصحاب دست چپ (شقاوت‌مندان)؟!');
INSERT INTO "hussein_taji" VALUES(56,42,'56:42','در (میان) باد سوزان و آب جوشان، قرار دارند.');
INSERT INTO "hussein_taji" VALUES(56,43,'56:43','و (در) سایه‌ای از دود‌های متراکم و سیاه.');
INSERT INTO "hussein_taji" VALUES(56,44,'56:44','نه خنک باشد و نه آرام بخش.');
INSERT INTO "hussein_taji" VALUES(56,45,'56:45','بی‌گمان آن‌ها پیش از این (در دنیا) ناز پرورده (و مترف) بودند.');
INSERT INTO "hussein_taji" VALUES(56,46,'56:46','و برگناه بزرگِ (شرک) اصرار می‌ورزیدند.');
INSERT INTO "hussein_taji" VALUES(56,47,'56:47','و می‌گفتند: «آیا هنگامی‌که مردیم و خاک و استخوان شدیم، آیا باز هم ما بر انگیخته می‌شویم؟!');
INSERT INTO "hussein_taji" VALUES(56,48,'56:48','آیا پدران نخستین ما (نیز بر انگیخته می‌شوند)؟!».');
INSERT INTO "hussein_taji" VALUES(56,49,'56:49','(ای پیامبر) بگو: «بی‌گمان گذشتگان و آیندگان،');
INSERT INTO "hussein_taji" VALUES(56,50,'56:50','(همگی) در موعد روزی معین گرد آورده می‌شوند».');
INSERT INTO "hussein_taji" VALUES(56,51,'56:51','سپس شما ای گمراهان تکذیب کننده!');
INSERT INTO "hussein_taji" VALUES(56,52,'56:52','قطعاً از درخت زقوم خواهید خورد،');
INSERT INTO "hussein_taji" VALUES(56,53,'56:53','پس شکم‌های (خود) را از آن پر می‌کنید،');
INSERT INTO "hussein_taji" VALUES(56,54,'56:54','آنگاه بر آن از آب جوشان می‌نوشید،');
INSERT INTO "hussein_taji" VALUES(56,55,'56:55','پس مانند شتران (مبتلا به بیماری عطش) از آن (آب جوشان) می‌نوشید.');
INSERT INTO "hussein_taji" VALUES(56,56,'56:56','این پذیرایی آن‌ها در روز قیامت است.');
INSERT INTO "hussein_taji" VALUES(56,57,'56:57','ما شما را آفریدیم، پس چرا (دو باره زنده شدن‌را) تصدیق نمی‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(56,58,'56:58','آیا نطفه‌ای را که (در رحم همسران‌تان) می‌ریزید، دیده‌اید؟!');
INSERT INTO "hussein_taji" VALUES(56,59,'56:59','آیا شما او را می‌آفرینید یا ما آفریدگاریم؟!');
INSERT INTO "hussein_taji" VALUES(56,60,'56:60','ما در میان شما مرگ را مقدر کردیم و ما ناتوان (از آن) نیستیم.');
INSERT INTO "hussein_taji" VALUES(56,61,'56:61','که همانند شما را جای‌گزین کنیم، و شما را به صورتی که آن را نمی‌دانید باز آفرینیم.');
INSERT INTO "hussein_taji" VALUES(56,62,'56:62','و یقیناً شما آفرینش نخستین را دانسته‌اید، پس چرا متذکر نمی‌شوید؟!');
INSERT INTO "hussein_taji" VALUES(56,63,'56:63','آیا چیزی را که می‌کارید، دیده‌اید؟!');
INSERT INTO "hussein_taji" VALUES(56,64,'56:64','آیا شما آن را می‌رویانید، یا ما می‌رویانیم؟!');
INSERT INTO "hussein_taji" VALUES(56,65,'56:65','اگر بخواهیم آن را به کاه درهم کوبیده مبدل می‌کنیم که تعجب کنید.');
INSERT INTO "hussein_taji" VALUES(56,66,'56:66','(بگوئید:) براستی ما زیان کرده‌ایم،');
INSERT INTO "hussein_taji" VALUES(56,67,'56:67','بلکه ما بکلی (از محصول) محروم شده‌ایم.');
INSERT INTO "hussein_taji" VALUES(56,68,'56:68','آیا آبی را که می‌نوشید، دیده‌اید؟!');
INSERT INTO "hussein_taji" VALUES(56,69,'56:69','آیا شما آن را از ابر نازل کرده‌اید یا ما نازل می‌کنیم؟!');
INSERT INTO "hussein_taji" VALUES(56,70,'56:70','اگر بخواهیم آن را تلخ (و شور) قرار می‌دهیم، پس چرا شکر نمی‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(56,71,'56:71','آیا آتشی را که می‌افروزید، دیده‌اید؟!');
INSERT INTO "hussein_taji" VALUES(56,72,'56:72','آیا شما درخت آن را آفریده‌اید یا ما آفریده‌ایم؟!');
INSERT INTO "hussein_taji" VALUES(56,73,'56:73','ما آن (= آتش) را (مایۀ) یادآوری و وسیلۀ زندگی برای مسافران قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(56,74,'56:74','پس (ای پیامبر) به نام پروردگار بزرگت تسبیح گوی (و او را به پاکی یا دکن).');
INSERT INTO "hussein_taji" VALUES(56,75,'56:75','پس سوگند به جایگاه ستارگان.');
INSERT INTO "hussein_taji" VALUES(56,76,'56:76','و اگر بدانید این سوگندی بزرگ است.');
INSERT INTO "hussein_taji" VALUES(56,77,'56:77','همانا این (کتاب) قرآنی کریم (و گرامی قدر) است.');
INSERT INTO "hussein_taji" VALUES(56,78,'56:78','در کتاب پوشیده (= لوح محفوظ) است.');
INSERT INTO "hussein_taji" VALUES(56,79,'56:79','که جز پاکان (= فرشتگان) به آن دست نمی‌زنند.');
INSERT INTO "hussein_taji" VALUES(56,80,'56:80','از سوی پروردگار جهانیان نازل شده است.');
INSERT INTO "hussein_taji" VALUES(56,81,'56:81','آیا شما نسبت به این کلام (الهی = قرآن کریم) انکار کننده‌اید؟! (و از پیروی آن سستی می‌کنید).');
INSERT INTO "hussein_taji" VALUES(56,82,'56:82','و (به جای شکر) روزی خود، (رازق) را تکذیب می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(56,83,'56:83','پس چرا هنگامی‌که (جان) به گلوگاه می‌رسد.');
INSERT INTO "hussein_taji" VALUES(56,84,'56:84','و شما در این هنگام نگاه می‌کنید.');
INSERT INTO "hussein_taji" VALUES(56,85,'56:85','و ما از شما به او نزدیک‌تر هستیم، ولی شما نمی‌بینید.');
INSERT INTO "hussein_taji" VALUES(56,86,'56:86','پس اگر (در برابر اعمال‌تان) جزا داده نمی‌شوید.');
INSERT INTO "hussein_taji" VALUES(56,87,'56:87','اگر راست می‌گویید آن (روح) را باز گردانید!');
INSERT INTO "hussein_taji" VALUES(56,88,'56:88','پس اما اگر (آن شخص) از مقربان باشد.');
INSERT INTO "hussein_taji" VALUES(56,89,'56:89','پس (در) آسایش و ریحان و باغ (بهشت) پر نعمت است.');
INSERT INTO "hussein_taji" VALUES(56,90,'56:90','و اما اگر از اصحاب دست راست (سعادتمند) باشد.');
INSERT INTO "hussein_taji" VALUES(56,91,'56:91','پس (به او گفته می‌شود:) سلام بر تو باد، (که) از اصحاب دست راست (هستی)');
INSERT INTO "hussein_taji" VALUES(56,92,'56:92','و اما اگر از تکذیب کنندگان گمراه (یاران دست چپ) باشد.');
INSERT INTO "hussein_taji" VALUES(56,93,'56:93','پس با آب جوشان (دوزخ از او) پذیرایی می‌شود.');
INSERT INTO "hussein_taji" VALUES(56,94,'56:94','و به (آتش) جهنم در آورده (و سوزانده) شود.');
INSERT INTO "hussein_taji" VALUES(56,95,'56:95','بی‌گمان این (خبر) حق یقین است.');
INSERT INTO "hussein_taji" VALUES(56,96,'56:96','پس (ای پیامبر) به نام پروردگار بزرگت تسبیح گوی (و او را به پاکی یادکن).');
INSERT INTO "hussein_taji" VALUES(57,1,'57:1','آنچه در آسمان‌ها و زمین است برای الله تسبیح می‌گویند [ این تسبیح با زبان حال نیست، بلکه با زبان قال تسبیح می‌گویند. نگا: سورۀ اسراء: ]، و او پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(57,2,'57:2','فرمانروایی آسمان‌ها و زمین از آنِ اوست، زنده می‌کند و می‌میراند، و او بر هر چیز تواناست.');
INSERT INTO "hussein_taji" VALUES(57,3,'57:3','او اوّل و آخر و ظاهر و باطن است، و او به هر چیز داناست.');
INSERT INTO "hussein_taji" VALUES(57,4,'57:4','او کسی است که آسمان‌ها و زمین را در شش روز آفرید، سپس بر عرش مستقر شد، آنچه را در زمین فرو می‌رود و آنچه را از ان خارج می‌شود، و آنچه را از آسمان نازل می‌شود و آنچه را که در آن بالا می‌رود، می‌داند [ در حدیث صحیح آمده: «کردار شب پیش از (آغاز) روز، و کردار روز پیش از (فرا رسیدن) شب بسوی الله بالا برده می‌شود». (صحیح مسلم .]. و هر کجا باشید او با شماست، و الله به آنچه می‌کنید بیناست.');
INSERT INTO "hussein_taji" VALUES(57,5,'57:5','فرمانروایی آسمان‌ها و زمین از آنِ اوست، و (همۀ) کارها به الله باز می‌گردد.');
INSERT INTO "hussein_taji" VALUES(57,6,'57:6','شب را در روز (داخل) می‌کند، و روز را در شب (داخل) می‌کند، و او به آنچه در سینه‌هاست داناست.');
INSERT INTO "hussein_taji" VALUES(57,7,'57:7','به الله و پیامبرش ایمان بیاورید و از آنچه شما را در آن جانشین قرار داده انفاق کنید، پس کسانی‌که از شما ایمان آوردند و (در راه الله) انفاق کردند برای آن‌ها پاداش بزرگی است.');
INSERT INTO "hussein_taji" VALUES(57,8,'57:8','و شما را چه شده است که به الله ایمان نمی‌آورید، در حالی‌که پیامبر شما را می‌خواند که به پروردگارتان ایمان بیاورید، و به راستی اگر باور دارید از شما پیمان گرفته است؟!');
INSERT INTO "hussein_taji" VALUES(57,9,'57:9','او کسی است که آیات روشن بر بنده‌اش نازل می‌کند تا شما را از تاریکی‌های (کفر و شرک) به سوی نور (ایمان) ببرد، و یقیناً الله نسبت به شما رؤوف و مهربان است.');
INSERT INTO "hussein_taji" VALUES(57,10,'57:10','و شما را چه شده است که در راه الله انفاق نمی‌کنید، در حالی‌که میراث آسمان‌ها و زمین از آنِ الله است، کسانی از شما که قبل از فتح (مکه) انفاق کردند و (با مشرکان) جنگیدند (با کسانی‌که پس از فتح انفاق کردند و جنگیدند) یکسان نیستند. آن‌ها مقام (و منزلت) شان و الاتر (و برتر) است از کسانی‌که بعد از فتح (مکه) انفاق کردند و جنگیدند، و الله به هر یک وعدۀ نیک (= بهشت) داده است، و الله به آنچه می‌کنید آگاه است.');
INSERT INTO "hussein_taji" VALUES(57,11,'57:11','کیست که به الله «قرض الحسنه» (= وام نیکو) دهد، (و در راه او انفاق نماید) پس برای او دو چندانش کند، و برای او پاداش ارجمندی است.');
INSERT INTO "hussein_taji" VALUES(57,12,'57:12','روزی‌که مردان و زنان مؤمن را بنگری که نورشان پیشاپیش آن‌ها و در سمت راست‌شان بسرعت حرکت می‌کند، (به آن‌ها گفته می‌شود) امروز شما را بشارت باد، به باغ‌هایی (از بهشت) که نهر‌ها زیر (درختان) آن جاری است، جاودانه در آن خواهید ماند، این کامیابی بزرگی است.');
INSERT INTO "hussein_taji" VALUES(57,13,'57:13','روزی‌که مردان و زنان منافق به کسانی‌که ایمان آورده‌اند می‌گویند: «درنگی کنید (و به ما بنگرید) تا از نور شما پرتوی برگیریم». گفته شود: «به پشت سر خود‌تان (به دنیا) باز گردید، پس (در آنجا) نور بجوئید». پس دیواری میان آن‌ها زده می‌شود که دری دارد، درونش رحمت است و از سمت بیرونش عذاب است.');
INSERT INTO "hussein_taji" VALUES(57,14,'57:14','(منافقان) آن‌ها را صدا می‌زنند: «آیا ما با شما نبودیم» (مؤمنان) می‌گویند: «آری، ولی شما خودتان را به بلا افکندید و منتظر ماندید و شک آوردید و آرزو‌های (باطل) شما را فریب داد تا فرمان الله فرا رسید و (شیطان) فریب‌کار شما را در برابر (فرمان) الله فریب داد».');
INSERT INTO "hussein_taji" VALUES(57,15,'57:15','پس امروز نه از شما فدیه‌ای پذیرفته می‌شود و نه از کسانی‌که کافر شدند، و جایگاه شما آتش (جهنم) است. همان شایستۀ شماست، و چه بد جایگاهی است.');
INSERT INTO "hussein_taji" VALUES(57,16,'57:16','آیا برای کسانی‌که ایمان آورده‌اند وقت آن نرسیده است که دل‌های شان برای ذکر الله و آنچه از حق نازل شده است خاشع گردد؟ و مانند کسانی نباشند که پیش از این به آن‌ها کتاب (آسمانی) داده شد، پس مدت زمان طولانی بر آن‌ها گذشت، آنگاه قلب‌هایشان سخت شد، و بسیاری از آن‌ها (گنهکار و) فاسقند.');
INSERT INTO "hussein_taji" VALUES(57,17,'57:17','بدانید که الله زمین را بعد از مردنش زنده می‌کند، به راستی ما آیات (خود) را برای شما (به روشنی) بیان کردیم باشد که شما اندیشه کنید.');
INSERT INTO "hussein_taji" VALUES(57,18,'57:18','بی‌گمان مردان و زنان صدقه دهنده و (آن‌ها که) به الله «قرض الحسنه» دهند، برایشان دوچندان داده می‌شود و برای آن‌ها پاداش ارجمندی است.');
INSERT INTO "hussein_taji" VALUES(57,19,'57:19','و کسانی‌که به الله و پیامبرانش ایمان آوردند، آن‌ها نزد پروردگارشان صدیقین و شهداء هستند، برای آن‌ها است پاداش‌شان و نور‌شان، و کسانی‌که کافر شدند و آیات ما را تکذیب کردند، آن‌ها دوزخی اند.');
INSERT INTO "hussein_taji" VALUES(57,20,'57:20','بدانید که زندگی دنیا تنها بازیچه و سرگرمی و تجمل، و خود‌ستایی در میان شما و افزون‌طلبی در اموال و فرزندان است، همانند بارانی که روییدنی‌اش کشاورزان را به شگفت آورد سپس خشک شود (به‌گونه‌ای که) آن را زرد رنگ بینی، سپس خرد (و به کاه تبدیل) شود و در آخرت (برای کفار) عذاب سخت است و (برای مؤمنان) آمرزشی از جانب الله و خشنودی است، و زندگی دنیا چیزی جز متاع فریبنده نیست.');
INSERT INTO "hussein_taji" VALUES(57,21,'57:21','برای رسیدن به آمرزش پروردگارتان و بهشتی که پهنای آن مانند پهنای آسمان‌ها و زمین است بر یکدیگر پیشی گیرید (که آن) برای کسانی که به الله و پیامبرانش ایمان آورده‌اند آماده شده است، این فضل الله است که به هرکس بخواهد می‌دهد، و الله دارای فضل عظیم است.');
INSERT INTO "hussein_taji" VALUES(57,22,'57:22','هیچ مصیبتی در زمین و نه در وجود شما روی نمی‌دهد مگر پیش از آنکه آن را پدید آوریم در کتاب (لوح محفوظ نوشته) است [ در حدیث آمده است که رسول الله صلی الله علیه وسلم فرمودند: «الله متعال پنجاه هزار سال پیش از خلقت آسمان‌ها و زمین تمام (سرنوشت‌ها و) تقدیر‌ها را مقدر کرده و نوشته است». (صحیح مسلم .]. مسلماً این امر بر الله آسان است.');
INSERT INTO "hussein_taji" VALUES(57,23,'57:23','(این را بیان کردیم) تا به خاطر آنچه از دست‌تان رفته است افسوس نخورید، و به آنچه (الله) به شما داده است شادمان نشوید، و الله هیچ خودپسند فخرکننده را دوست ندارد.');
INSERT INTO "hussein_taji" VALUES(57,24,'57:24','(همان) کسانی‌که بخل می‌ورزند و مردم را (نیز) به بخل فرمان می‌دهند، و هرکس که (از فرمان الهی) روی‌گردان شود، پس (بداند که) الله بی‌نیاز ستوده است.');
INSERT INTO "hussein_taji" VALUES(57,25,'57:25','به‌راستی‌که پیامبران خود را با دلایل روشن فرستادیم، و با آن‌ها کتاب (آسمانی) و میزان (عدالت) نازل کردیم. تا مردم به عدالت قیام کنند، و آهن را نازل کردیم که در آن نیروی سخت و منافعی برای مردم است. و تا الله معلوم بدارد چه کسی به نادیده او و پیامبرانش را یاری می‌کند، بی‌گمان الله توانای پیروزمند است.');
INSERT INTO "hussein_taji" VALUES(57,26,'57:26','و به‌راستی ما نوح و ابراهیم را فرستادیم، و در دودمان آن دو نبوت و کتاب قرار دادیم. پس بعضی از آن‌ها هدایت یافته‌اند و بسیاری از آن‌ها (بدکار و) فاسقند.');
INSERT INTO "hussein_taji" VALUES(57,27,'57:27','سپس در پی آن‌ها پیامبران (دیگر) خود را فرستادیم، و بعد از آنان عیسی پسر مریم را آوردیم و به او انجیل عطا کردیم، و در دل‌های کسانی‌که از او پیروی کردند رأفت و رحمت قرار دادیم [ و مراد از پیروان، حواریین حضرت عیسی علیه السلام می‌باشند که میان آن‌ها همچون صحابه گرامی -رضی الله عنهم- محبت و دوستی برقرار بود.]، و رهبانیتی که خود آن را پدید آوردند، ما بر آن‌ها مقرر نداشته بودیم، گرچه هدف‌شان بدست آوردن خشنودی الله بود، ولی چنانکه باید حق آن را رعایت نکردند، لذا ما به کسانی از آن‌ها که ایمان آوردند پاداش‌شان را دادیم، و بسیاری از آن‌ها (بدکار و) فاسقند.');
INSERT INTO "hussein_taji" VALUES(57,28,'57:28','ای کسانی‌که ایمان آورده‌اید! از الله بترسید و به پیامبرش ایمان بیاورید تا دو بهره از رحمتش را به شما ببخشد و برای شما نوری قرار دهد که با آن راه بروید و گناهان شما را بیامرزد، و الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(57,29,'57:29','(این را بیان کردیم) تا اهل کتاب بدانند که قادر بر چیزی از فضل الله نیستند، و آنکه فضل به دست الله است به هرکس که بخواهد آن را می‌دهد، و الله دارای فضل بزرگ است.');
INSERT INTO "hussein_taji" VALUES(58,1,'58:1','به راستی الله سخن زنی را که دربارۀ شوهرش با تو مجادله می‌کرد، و به الله شکایت می‌برد شنید، و الله گفتگوی شما را می‌شنید، بی‌گمان الله شنوای بیناست.');
INSERT INTO "hussein_taji" VALUES(58,2,'58:2','کسانی از شما که زنان‌شان را ظهار می‌کنند، آنان هرگز مادران‌شان نیستند، مادران‌شان فقط کسانی اند که آن‌ها را زاده‌اند، بی‌گمان آن‌ها سخنی زشت و دروغ می‌گویند، و همانا الله عفوکنندۀ آمرزگار است.');
INSERT INTO "hussein_taji" VALUES(58,3,'58:3','و کسانی‌که زنان‌شان را ظهار می‌کنند، سپس از آنچه گفته‌اند باز می‌گردند، پس باید پیش از آمیزش جنسی باهم برده‌ای را آزاد کنند، این حکمی است که به آن (پند و) اندرز داده می‌شوید، و الله به آنچه می‌کنید آگاه است.');
INSERT INTO "hussein_taji" VALUES(58,4,'58:4','پس کسی‌که (برده‌ای را) نیابد، پیش از آمیزش جنسی، دو ماه پی در پی روزه بگیرد، و کسی‌که نتواند پس شصت مسکین را طعام دهد، این (حکم) برای آن است که به الله و رسولش ایمان بیاورید، و این‌ها حدود (و احکام) الهی است، و برای کافران عذاب دردناکی است.');
INSERT INTO "hussein_taji" VALUES(58,5,'58:5','همانا کسانی‌که با الله و رسولش دشمنی می‌کنند خوار (و ذلیل) می‌شوند، همان‌گونه کسانی‌که پیش از آن‌ها بودند خوار (و ذلیل) شدند، و به راستی ما آیات روشنی نازل کردیم و برای کافران عذاب خوار (و رسوا) کننده‌ای است.');
INSERT INTO "hussein_taji" VALUES(58,6,'58:6','روزی که الله همۀ آن‌ها را بر می‌انگیزد، پس آن‌ها را از آنچه کرده‌اند باخبر می‌سازد، (همان اعمالی که) الله حساب آن را نگه داشته و آن‌ها فراموشش کرده‌اند، و الله بر همه چیز گواه (و ناظر) است.');
INSERT INTO "hussein_taji" VALUES(58,7,'58:7','مگر ندیده‌ای که الله آنچه را که در آسمان‌ها و آنچه را که در زمین است می‌داند؟ هیچ نجوایی میان سه نفر نباشد مگر آن که او چهارمین آن‌ها است، و پنج نفری نیست مگر آن که او ششمین آن‌ها است، و نه کمتر از آن و نه بیشتر، مگر آن که هرکجا باشند او همراه آن‌ها است، سپس روز قیامت آن‌ها را به آنچه کرده‌اند باخبر می‌سازد، بی‌گمان الله به همه چیز داناست.');
INSERT INTO "hussein_taji" VALUES(58,8,'58:8','(ای پیامبر!) مگر ندیده‌ای کسانی را که از نجوا نهی شدند سپس به آنچه از آن نهی شده بودند باز می‌گردند و به گناه و تجاوز و نافرمانی پیامبر نجوا می‌کنند، و هنگامی‌که نزد تو می‌آیند تو را تحیتّی گویند (با کلماتی) که الله با آن تو را تحیت و سلام نگفته است، و در دل خود می‌گویند: چرا الله ما را به (کیفر) آنچه می‌گوییم عذاب نمی‌کند؟ جهنم برای آن‌ها کافی است، به آن وارد می‌شوند، پس (آن) بد (سرانجام و) جایگاهی است.');
INSERT INTO "hussein_taji" VALUES(58,9,'58:9','ای کسانی‌که ایمان آورده‌اید! هنگامی‌که با یکدیگر نجوا می‌کنید پس به گناه و تجاوز و نافرمانی رسول الله نجوا نکنید، و به نیکی و پرهیزگاری نجوا کنید، و از الله بترسید که به سوی او محشور خواهید شد.');
INSERT INTO "hussein_taji" VALUES(58,10,'58:10','جز این نیست که نجوا از (سوی) شیطان است، تا کسانی را که ایمان آورده‌اند اندوهگین سازد، و جز به فرمان الله نمی‌تواند هیچ ضرری به آن‌ها برساند، پس مؤمنان باید بر الله توکل کنند.');
INSERT INTO "hussein_taji" VALUES(58,11,'58:11','ای کسانی‌که ایمان آورده‌اید! هنگامی‌که به شما گفته شود: در مجالس جا باز کنید، پس (جا) باز کنید، الله برای شما گشایش می‌آورد، و هنگامی‌که گفته شود:،،برخیزید،، پس برخیزید، الله مقام (و درجات) کسانی را از شما که ایمان آورده‌اند و کسانی را که علم داده شده‌اند بالا می‌برد، و الله به آنچه می‌کنید آگاه است.');
INSERT INTO "hussein_taji" VALUES(58,12,'58:12','ای کسانی‌که ایمان آورده‌اید! هنگامی‌که می‌خواهید با رسول الله نجوا کنید، پس پیش از نجوای‌تان صدقه‌ای (در راه الله) بدهید، این (کار) برای شما بهتر و پاکیزه‌تر است. پس اگر نیابید، بی‌گمان الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(58,13,'58:13','آیا (از فقر) ترسیدید که پیش از نجوای‌تان صدقاتی دهید؟ پس چون چنین نکردید، و الله از شما درگذشت، پس نماز را برپا دارید و زکات را ادا کنید، و الله و پیامبرش را اطاعت کنید، و الله به آنچه می‌کنید آگاه است.');
INSERT INTO "hussein_taji" VALUES(58,14,'58:14','(ای پیامبر!) آیا ندیدی کسانی را که دوستی کردند با قومی که الله بر آنان خشم (و غضب) کرده است، آن‌ها نه از شما هستند و نه از آنان، و بر دروغ سوگند یاد می‌کنند (که مسلمان هستند) در حالی خودشان می‌دانند (که منافق هستند).');
INSERT INTO "hussein_taji" VALUES(58,15,'58:15','الله عذاب سختی برای آن‌ها آماده کرده است، بی‌گمان بد است آنچه انجام می‌دادند.');
INSERT INTO "hussein_taji" VALUES(58,16,'58:16','آن‌ها سوگندهای خود را سپر قرار دادند، پس (مردم را) از راه الله باز داشتند، لذا برای آن‌ها عذاب خوارکننده‌ای است.');
INSERT INTO "hussein_taji" VALUES(58,17,'58:17','هرگز اموال‌شان و فرزندان‌شان چیزی از عذاب الله را از آن‌ها دفع نخواهد کرد، آن‌ها اهل (آتش) دوزخند، و جاودانه درآن می‌مانند.');
INSERT INTO "hussein_taji" VALUES(58,18,'58:18','روزی‌که الله همۀ آن‌ها را برمی‌انگیزد، پس آن‌ها برای او (نیز) سوگند یاد می‌کنند، همان‌گونه که (امروز در دنیا) برای شما سوگند یاد می‌کنند، و گمان می‌کنند که بر چیزی (سودمند) هستند، آگاه باشید (و بدانید) که آن‌ها دروغگویانند.');
INSERT INTO "hussein_taji" VALUES(58,19,'58:19','شیطان بر آن‌ها چیره شده پس یاد الله را از خاطر آن‌ها برده است، آن‌ها حزب شیطان هستند، و آگاه باشید (و بدانید) که حزب شیطان زیانکارانند.');
INSERT INTO "hussein_taji" VALUES(58,20,'58:20','بی‌گمان کسانی‌که با الله و پیامبرش مخالفت (و دشمنی) می‌کنند، آن‌ها در زمرۀ خوارترین (افراد) اند.');
INSERT INTO "hussein_taji" VALUES(58,21,'58:21','الله مقرر داشته است که یقیناً من و رسولانم پیروز می‌شویم؛ بی‌گمان الله نیرومند شکست‌ ناپذیر است.');
INSERT INTO "hussein_taji" VALUES(58,22,'58:22','(ای پیامبر!) هیچ قومی را که ایمان به الله و روز قیامت دارند نمی‌یابی که با کسانی‌که با الله و رسولش (دشمنی و) مخالفت می‌ورزند، دوستی کنند، اگرچه پدران‌شان یا فرزندان‌شان یا برادران‌شان یا خویشاوندان‌شان باشند، آن‌ها کسانی هستند که الله ایمان را در (صفحۀ) دل‌هایشان نوشته است، و به روحی از جانب خود آن‌ها را تقویت (و تأیید) نموده است، و آن‌ها را به باغ‌هایی (از بهشت) وارد می‌کند که نهرها از زیر (درختان) آن جاری است، جاودانه در آن می‌مانند، الله از آن‌ها خشنود است، و آن‌ها (نیز) از الله خشنودند، آن‌ها حزب الله هستند، آگاه باشید (و بدانید) همانا حزب الله رستگارانند.');
INSERT INTO "hussein_taji" VALUES(59,1,'59:1','آنچه در آسمان‌ها و آنچه در زمین است برای الله تسبیح می‌گویند، و او پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(59,2,'59:2','او کسی است که کسانی از اهل کتاب را که کافر شدند، با نخستین برخورد (و گرد‌آوری لشکر) از دیار‌شان بیرون راند، گمان نمی‌کردید که آن‌ها خارج شوند، و خودشان (نیز) گمان می‌کردند که دژهای (محکم) شان آن‌ها را از (عذاب) الله مانع می‌شود، پس (عذاب) الله از جایی که گمان نمی‌کردند به سراغ‌شان آمد، و در دل‌های‌شان ترس و وحشت افکند، (به گونه‌ای که) خانه‌های خود را با دست خود، و با دست مؤمنان ویران می‌کردند، پس ای دیده‌وران عبرت گیرید.');
INSERT INTO "hussein_taji" VALUES(59,3,'59:3','و اگر الله ترک وطن (و آوارگی) را بر آنان مقرر نداشته بود، یقیناً آن‌ها را در (همین) دنیا عذاب می‌کرد، و برای آنان در آخرت عذاب آتش (جهنم) است.');
INSERT INTO "hussein_taji" VALUES(59,4,'59:4','این (آوارگی و عذاب) به خاطر آن است که آن‌ها با الله و پیامبرش مخالفت (و دشمنی) کردند و هرکس با الله مخالفت (و دشمنی) کند، پس بی‌گمان الله سخت‌کیفر است.');
INSERT INTO "hussein_taji" VALUES(59,5,'59:5','آنچه از درخت خرما بریدید، و یا آن را ایستاده بر ریشه‌اش باقی گذاردید، به فرمان الله بود، تا فاسقان را خوار (و رسوا) سازد.');
INSERT INTO "hussein_taji" VALUES(59,6,'59:6','و آنچه الله از (اموال) آن‌ها به پیامبرش بازگردانده (و بخشیده) است، پس برآن (اموال) نه اسبی تاختید و نه شتری، و لیکن الله رسولانش را بر هرکس بخواهد چیره می‌گرداند، و الله بر هر چیز تواناست.');
INSERT INTO "hussein_taji" VALUES(59,7,'59:7','و آنچه الله از (اموال) اهل آبادی‌ها به پیامبرش بازگردانده (و بخشیده) است، پس از آنِ الله و رسول، و خویشاوندان او، و یتیمان، و بی‌نوایان، و در راه ماندگان است. تا (این اموال) در میان ثروتمندان شما دست به دست نشود، و آنچه که رسول الله به شما بدهد آن را بگیرید، و از آنچه که شما را از آن نهی کرده است پس خودداری کنید، و از الله بترسید، بی‌گمان الله سخت‌کیفر است.');
INSERT INTO "hussein_taji" VALUES(59,8,'59:8','(این اموال فَی) برای فقرای مهاجری است که از خانه و اموال‌شان بیرون رانده شدند، از الله فضل و خشنودی می‌طلبند، و الله و پیامبرش را یاری می‌کنند، آن‌ها راستگویانند.');
INSERT INTO "hussein_taji" VALUES(59,9,'59:9','و (نیز برای) کسانی‌که پیش از آنان در دیار خود (مدینه دار الاسلام) جای گرفتند و (نیز) ایمان آورده بودند، کسانی را که به سوی‌شان هجرت کنند دوست می‌دارند، و در دل‌های خود از آنچه (به مهاجران) داده شده احساس حسد (و نیازی) نمی‌کنند، و آن‌ها را بر خود مقدم می‌دارند، هرچند خودشان نیازمند باشند، و کسانی‌که از بخل (و حرص) نفس خویش باز داشته شده‌اند، پس آن‌ها راستگارانند.');
INSERT INTO "hussein_taji" VALUES(59,10,'59:10','و (نیز) کسانی‌که بعد از آن‌ها (بعد از مهاجران و انصار) آمدند، می‌گویند: «پروردگارا! ما را و برادران‌مان را که در ایمان بر ما پیشی گرفتند بیامرز، و در دل‌های‌مان کینه‌ای نسبت به کسانی‌که ایمان آورده‌اند قرار مده، پروردگارا! بی‌گمان تو رؤوف و مهربانی».');
INSERT INTO "hussein_taji" VALUES(59,11,'59:11','آیا منافقان را ندیدی که پیوسته به برادران اهل کتاب‌شان که کفر ورزیده‌اند می‌گویند: «اگر شما را (از سرزمین‌تان) بیرون کنند، ما (نیز) با شما بیرون خواهیم آمد، و هرگز (سخن) کسی را در مورد شما اطاعت نخواهیم کرد، و اگر با شما جنگ شود، البته یاری‌تان خواهیم کرد!» و الله گواهی می‌دهد که آن‌ها دروغگو هستند.');
INSERT INTO "hussein_taji" VALUES(59,12,'59:12','اگر آن‌ها را (از وطن) بیرون کنند با آن‌ها بیرون نمی‌روند، و اگر با آن‌ها جنگ شود یاری‌شان نخواهند کرد، و اگر (هم) یاری‌شان کنند، البته پشت (به میدان) کرده فرار می‌کنند، سپس یاری نمی‌شوند.');
INSERT INTO "hussein_taji" VALUES(59,13,'59:13','(ای مؤمنان) همانا وحشت از شما در دل‌های آن‌ها بیشتر از (ترس‌شان از) الله است، این بدان سبب است که آن‌ها قومی هستند که نمی‌فهمند.');
INSERT INTO "hussein_taji" VALUES(59,14,'59:14','آن‌ها (یهود) هرگز دسته‌جمعی با شما نمی‌جنگند جز در آبادی‌ها (و دژهای) محکم یا از پشت دیوارها، نزاع (و جنگ) شان در میان خودشان سخت است، تو آن‌ها را متحد می‌پنداری، در حالی‌که دل‌های شان پراکنده است، این بدان سبب است که آن‌ها قومی هستند که تعقل نمی‌کنند.');
INSERT INTO "hussein_taji" VALUES(59,15,'59:15','(داستان این‌ها) مانند (داستان) کسانی است که اندکی پیش از آنان بودند، (طعم) عاقبت کار (بد) شان را چشیدند، و برای آن‌ها عذاب دردناکی است.');
INSERT INTO "hussein_taji" VALUES(59,16,'59:16','همچون (داستان شیطان است، هنگامی‌که به انسان گفت: «کافر شو» پس چون کافر شد، گفت: «بی‌گمان من از تو بیزارم، من از الله پروردگار جهانیان می‌ترسم».');
INSERT INTO "hussein_taji" VALUES(59,17,'59:17','پس سرانجام (کار) هردوی آن‌ها این شد که هردوی آن‌ها در آتش (دوزخ) خواهند بود، جاودانه در آن می‌مانند، و این است کیفر ستمکاران!.');
INSERT INTO "hussein_taji" VALUES(59,18,'59:18','ای کسانی‌که ایمان آورده‌اید! از الله بترسید، و هرکس باید بنگرد که برای فردا چه پیش فرستاده است، و از الله بترسید، بی‌گمان الله به آنچه انجام می‌دهید آگاه است.');
INSERT INTO "hussein_taji" VALUES(59,19,'59:19','و همچون کسانی نباشید که الله را فراموش کردند، پس الله (نیز) خودشان را فراموش‌شان ساخت، آن‌ها (بدکار و) نافرمانند.');
INSERT INTO "hussein_taji" VALUES(59,20,'59:20','هرگز اهل جهنم و اهل بهشت یکسان نیستند، اهل بهشت رستگارانند.');
INSERT INTO "hussein_taji" VALUES(59,21,'59:21','اگر این قرآن را بر کوهی نازل می‌کردیم، یقیناً آن را از ترس الله خاکسار و از هم پاشیده می‌دیدی و این مثل‌ها را برای مردم می‌زنیم، شاید که بیندیشند.');
INSERT INTO "hussein_taji" VALUES(59,22,'59:22','او الله است که جز او معبودی (راستین) نیست، دانای غیب و آشکار است، او بخشندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(59,23,'59:23','او الله است که جز او معبودی (راستین) نیست، پادشاه، نهایت پاک، منزّه (و سالم از هر عیب)، ایمنی دهنده، نگهبان پیروزمند، جبار (جبران‌کننده) و شایستۀ عظمت و بزرگی است. الله پاک و منزّه است از آنچه (برای او) شریک می‌آورند.');
INSERT INTO "hussein_taji" VALUES(59,24,'59:24','او الله است، خالق، نوآفرین، نقشبند (بی‌نظیر) برای او نام‌های نیک است، آنچه در آسمان‌ها و زمین است تسبیح او می‌گویند، و او پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(60,1,'60:1','ای کسانی‌که ایمان آورده‌اید! دشمن من و دشمن خودتان را دوست نگیرید که با آن‌ها طرح دوستی می‌افکنید، در حالی‌که آن‌ها به آنچه از حق برای شما آمده کافر شده‌اند، و رسول الله و شما را به خاطر ایمان‌آوردن به الله که پروردگارتان است (از شهر و دیارتان) بیرون می‌کنند، اگر شما برای جهاد در راه من (بیرون آمده‌اید) و برای خشنودی من هجرت کرده‌اید (هرگز پیوند دوستی با آن‌ها برقرار نکنید) شما پنهانی با آن‌ها پیوند دوستی برقرار می‌کنید در حالی‌که من به آنچه که پنهان می‌دارید و آنچه که آشکار می‌سازید داناترم. و هرکس از شما چنین (کاری) کند یقیناً راه راست را گم کرده است.');
INSERT INTO "hussein_taji" VALUES(60,2,'60:2','اگر آن‌ها بر شما دست یابند، دشمنان‌تان خواهند بود، و به آزارتان دست و زبان می‌گشایند و دوست دارند که شما (نیز) کافر گردید.');
INSERT INTO "hussein_taji" VALUES(60,3,'60:3','هرگز خویشاوندان‌تان و فرزندان‌تان روز قیامت به حال‌تان سودی نخواهند داشت. الله در میان شما جدایی می‌اندازد، و الله به آنچه انجام می‌دهید بیناست.');
INSERT INTO "hussein_taji" VALUES(60,4,'60:4','یقیناً برای شما سر مشق خوبی در (زندگی) ابراهیم و کسانی‌که با او بودند وجود دارد، آنگاه که به قوم خود گفتند: «ما از شما و از آنچه غیر از الله می‌پرستید بیزاریم، به شما کافر (و منکر) شده‌ایم، و میان ما و شما برای همیشه عداوت و دشمنی آشکار شده است، تا این‌که به الله یگانه ایمان آورید – مگر آن سخن ابراهیم که به پدرش (آزر) گفت: «البته من برایت آمرزش طلب می‌کنم، و در برابر الله برای تو اختیار چیزی را ندارم» - پروردگارا! بر تو توکل کردیم، و به سوی تو روی آوردیم، و بازگشت (همه) به سوی توست.');
INSERT INTO "hussein_taji" VALUES(60,5,'60:5','پروردگارا! ما را دستخوش (فتنه) کافران قرار مده، و ما را بیامرز ای پروردگار ما! بی‌گمان تو پیروزمند حکیمی».');
INSERT INTO "hussein_taji" VALUES(60,6,'60:6','مسلماً برای شما در (زندگی) آن‌ها (ابراهیم و یارانش) سرمشق خوبی است، برای کسانی‌که امید به الله و روز قیامت دارند، و هرکس روی گردان شود، پس همانا الله بی‌نیاز ستوده است.');
INSERT INTO "hussein_taji" VALUES(60,7,'60:7','امید است که الله میان شما و کسانی (از مشرکین) که با آن‌ها دشمنی داشته‌اید (پیوند) دوستی (و محبت) برقرار کند، و الله تواناست، و الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(60,8,'60:8','الله شما را از نیکی‌کردن و رعایت عدالت نسبت به کسانی‌که در (امر) دین با شما نجنگیده‌اند و شما را از دیارتان بیرون نکرده‌اند، نهی نمی‌کند، بی‌گمان الله عدالت پیشگان را دوست دارد.');
INSERT INTO "hussein_taji" VALUES(60,9,'60:9','تنها شما را از دوستی با کسانی نهی می‌کند که در (امر) دین با شما جنگیده‌اند و شما را از دیارتان بیرون کرده‌اند، و بر بیرون‌راندن شما (دیگران را) کمک (و پشتیبانی) کرده‌اند، و هرکس با آن‌ها (رابط) دوستی بگیرد، پس آنان ستمکارانند.');
INSERT INTO "hussein_taji" VALUES(60,10,'60:10','ای کسانی‌که ایمان آورده‌اید! هنگامی‌که زنان مؤمن هجرت کرده به نزد شما آیند، پس آن‌ها را آزمایش کنید، الله به ایمان‌شان داناتر است، پس اگر آن‌ها را (زنانی) مؤمن یافتید، آن‌ها را به سوی کفار باز نگردانید، نه آن‌ها برای آنان (کافران) حلالند و نه آن‌ها (کافران) برای این‌ها حلالند و آنچه را (شوهران کافر) خرج کرده‌اند به آن‌ها بدهید، و گناهی بر شما نیست که با آن‌ها ازدواج کنید هرگاه مهریه‌شان را به آنان بدهید، و (در صورت ارتداد از اسلام) به عقد زنان کافر تمسک مجویید (و آن‌ها را رها کنید). و آنچه را خرج کرده‌اید مطالبه کنید، و (کافران نیز) آنچه را خرج کرده‌اند مطالبه کنند. این حکم الله است که در میان شما حکم می‌کند و الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(60,11,'60:11','و اگر یکی از همسران شما به سوی کافران رفت آنگاه شما (در جنگ بر آنان پیروز شدید و) به انتقام از کافران برخاستید، پس به کسانی‌که همسران‌شان رفته‌اند، همانند آنچه خرج کرده‌اند بدهید، و از الهی که شما به او ایمان دارید بترسید.');
INSERT INTO "hussein_taji" VALUES(60,12,'60:12','ای پیامبر! هنگامی‌که زنان مؤمن نزد تو آمدند تا با تو بیعت کنند بر آن که چیزی را با الله شریک نسازند، و دزدی نکنند، و مرتکب زنا نشوند، و فرزندان خود را نکشند، و بهتان و افترایی را پیش دست و پای خود نیاورند (که فرزندی را به دروغ به شوهران‌شان نسبت دهند)، و در کارهای نیک نافرمانی تو نکنند، پس با آن‌ها بیعت کن و از الله برای آن‌ها آمرزش بخواه، بی‌گمان الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(60,13,'60:13','ای کسانی‌که ایمان آورده‌اید! با قومی که الله برآنان خشم (و غضب) گرفته است دوستی نکنید، آن‌ها از آخرت مأیوسند همان‌گونه که کفار از گور خفتگان مأیوس هستند.');
INSERT INTO "hussein_taji" VALUES(61,1,'61:1','آنچه در آسمان‌ها و آنچه در زمین است تسبیح الله می‌گویند، و او پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(61,2,'61:2','ای کسانی‌که ایمان آورده‌اید! چرا چیزی را می‌گویید که عمل نمی‌کنید؟!.');
INSERT INTO "hussein_taji" VALUES(61,3,'61:3','نزد الله سخت ناپسند (و موجب خشم) است آن که چیزی را بگویید که عمل نمی‌کنید.');
INSERT INTO "hussein_taji" VALUES(61,4,'61:4','بی‌گمان الله کسانی را که چون بنیانی آهنین صف بسته در راه او پیکار می‌کنند دوست دارد.');
INSERT INTO "hussein_taji" VALUES(61,5,'61:5','و (ای پیامبر! به یاد آور) هنگامی را که موسی به قومش گفت: «ای قوم من! چرا مرا آزار (و اذیت) می‌رسانید، در حالی‌که می‌دانید بی‌گمان من فرستادۀ الله به سوی شما هستم؟» پس چون آن‌ها (از حق) منحرف شدند، الله دل‌های شان را منحرف ساخت، و الله قوم نافرمان را هدایت نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(61,6,'61:6','و (یادآور باش) هنگامی‌که عیسی پسر مریم گفت: «ای بنی اسرائیل! بی‌گمان من فرستادۀ الله به‌سوی شما هستم، آنچه از تورات که پیش از من بوده تصدیق می‌کنم و (نیز) مژده‌دهنده به رسولی هستم که بعد از من می‌آید، و نامش «احمد» است» پس هنگامی‌که او (احمد) با معجزه‌ها (و دلایل روشن) به‌سوی آنان آمد، گفتند: «این جادوی آشکار است».');
INSERT INTO "hussein_taji" VALUES(61,7,'61:7','و چه کسی ستمکارتر است از آن‌کس که بر الله دروغ ببندد، درحالی‌که او به (دین) اسلام دعوت می‌شود؟! و الله گروه ستمکاران را هدایت نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(61,8,'61:8','آن‌ها می‌خواهند نور الله را با دهان خود خاموش کنند، ولی الله کامل‌کنندۀ نور خویش است هرچند کافران خوش نداشته باشند.');
INSERT INTO "hussein_taji" VALUES(61,9,'61:9','او کسی است که رسول الله را با هدایت و دین حق فرستاد تا آن را بر همۀ ادیان غالب گرداند، هرچند مشرکان خوش نداشته باشند.');
INSERT INTO "hussein_taji" VALUES(61,10,'61:10','ای کسانی‌که ایمان آورده‌اید! آیا شما را به تجارتی راهنمایی کنم که شما را از عذاب دردناک نجات می‌دهد؟.');
INSERT INTO "hussein_taji" VALUES(61,11,'61:11','به الله و پیامبرش ایمان بیاورید، و با اموال و جان‌هایتان در راه الله جهاد کنید، اگر بدانید این (کار) برای شما بهتر است.');
INSERT INTO "hussein_taji" VALUES(61,12,'61:12','(اگر چنین کردید، الله) گناهان‌تان را می‌بخشد، و شما را در باغ‌هایی (از بهشت) وارد می‌کند که نهرها از زیر (درختان) آن جاری است، و در خانه‌های پاکیزه در بهشت جاویدان (جای می‌دهد)، این کامیابی بزرگی است.');
INSERT INTO "hussein_taji" VALUES(61,13,'61:13','و (نعمت) دیگری که آن را دوست دارید (نیز به شما عنایت می‌فرماید) که آن یاری از سوی الله و پیروزی نزدیک است، و مؤمنان را (به این پیروزی) بشارت ده.');
INSERT INTO "hussein_taji" VALUES(61,14,'61:14','ای کسانی‌که ایمان آورده‌اید! یاوران (دین) الله باشید، همان‌گونه که عیسی پسر مریم به حواریون گفت: «چه کسانی یاوران من (در دعوت) به سوی الله هستند؟!» حواریون گفتند: «ما یاوران (دین) الله هستیم» پس گروهی از بنی اسرائیل ایمان آوردند، و گروهی کافر شدند، آنگاه ما کسانی را که ایمان آورده بودند بر دشمنان‌شان قوت (و توانایی) دادیم و سرانجام پیروز شدند.');
INSERT INTO "hussein_taji" VALUES(62,1,'62:1','آنچه در آسمان‌ها و آنچه در زمین است تسبیح الله می‌گویند، (الله) که فرمانروا، پاک پیروزمندِ حکیم است.');
INSERT INTO "hussein_taji" VALUES(62,2,'62:2','او کسی است که در میان درس ناخواندگان رسولی از خودشان برانگیخت که آیاتش را بر آن‌ها می‌خواند، و آن‌ها را پاک (و تزکیه) می‌کند و به آنان کتاب (قرآن) و حکمت (سنت) می‌آموزد، و اگر چه پیش از این در گمراهی آشکار بودند.');
INSERT INTO "hussein_taji" VALUES(62,3,'62:3','و (نیز این پیامبر) بر گروه دیگری از آنان (برانگیخته شده) که هنوز به آن‌ها نپیوسته‌اند، و او پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(62,4,'62:4','این فضل الله است که آن را به هرکس که بخواهد می‌بخشد و الله دارای فضل عظیم است.');
INSERT INTO "hussein_taji" VALUES(62,5,'62:5','مَثَل کسانی‌که به تورات مکلّف شدند، آنگاه برنداشتند آن را (یعنی: حق آن را ادا نکردند) همچون خری است که کتاب‌هایی را حمل می‌کند (اما از آن چیزی نمی‌فهمد) چه بد است مَثَل گروهی که آیات الله را تکذیب کردند، و الله گروه ستمکاران هدایت نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(62,6,'62:6','(ای پیامبر!) بگو: «ای کسانی‌که یهودی شدید! اگر گمان دارید که (تنها) شما دوستان الله هستید نه مردم دیگر، پس آرزوی مرگ کنید، اگر راست می‌گویید».');
INSERT INTO "hussein_taji" VALUES(62,7,'62:7','ولی آن‌ها به خاطر آنچه از پیش فرستاده‌اند هرگز آن آرزو را نخواهند کرد، و الله ستمکاران را خوب می‌شناسد.');
INSERT INTO "hussein_taji" VALUES(62,8,'62:8','(ای پیامبر!) بگو: «بی‌گمان آن مرگی که از آن فرار می‌کنید، یقیناً به شما خواهد رسید، سپس به سوی (الله آن) دانای غیب و آشکار باز گردانده می‌شوید، آنگاه شما را از آنچه انجام می‌دادید خبر می‌دهد».');
INSERT INTO "hussein_taji" VALUES(62,9,'62:9','ای کسانی‌که ایمان آورده‌اید! هنگامی‌که در روز جمعه برای نماز اذان گفته شد به سوی (نماز و) ذکر الله بشتابید و خرید و فروش را رها کنید، اگر بدانید این برای شما بهتر است.');
INSERT INTO "hussein_taji" VALUES(62,10,'62:10','پس هنگامی‌که نماز پایان یافت، (برای کسب رزق و روزی) در زمین پراکنده شوید، و از فضل الله طلب کنید، و الله را بسیار یاد کنید تا رستگار شوید.');
INSERT INTO "hussein_taji" VALUES(62,11,'62:11','و (ای پیامبر! برخی از این مردم) هنگامی‌که تجارتی یا سرگرمی را ببینند به سوی آن پراکنده می‌شوند و تو را ایستاده (بر منبر) رها می‌کنند، بگو: «آنچه در نزد الله است بهتر از سرگرمی و تجارت است، و الله بهترین روزی‌دهندگان است».');
INSERT INTO "hussein_taji" VALUES(63,1,'63:1','هنگامی‌که منافقان نزد تو آیند، گویند: «ما گواهی می‌دهیم که یقیناً تو فرستادۀ الله هستی» و الله می‌داند که بی‌گمان تو فرستادۀ او هستی، و الله گواهی می‌دهد که منافقان یقیناً دروغگو هستند.');
INSERT INTO "hussein_taji" VALUES(63,2,'63:2','آن‌ها سوگندهای خود را سپر قرار دادند، پس (مردم را) از راه الله باز داشتند، به راستی کار بسیار بدی انجام می‌دهند.');
INSERT INTO "hussein_taji" VALUES(63,3,'63:3','این بدان خاطر است که آن‌ها ایمان آوردند، سپس کافر شدند، آنگاه بر دل‌هایشان مهر نهاده شد، پس آن‌ها درنمی‌یابند.');
INSERT INTO "hussein_taji" VALUES(63,4,'63:4','و هنگامی‌که آن‌ها را ببینی، جسم (و قیافۀ) شان تو را به شگفت آورد، و اگر (سخن) گویند به سخنانشان گوش فرا می‌دهی، گویی آن‌ها چوب‌های تکیه داده به دیوارند، هر بانگی را علیه خود می‌پندارند، آن‌ها دشمن (واقعی) هستند، پس از آنان بر حذر باش! الله آن‌ها را بکشد، چگونه (از حق) منحرف می‌شوند؟!.');
INSERT INTO "hussein_taji" VALUES(63,5,'63:5','و هنگامی‌که به آن‌ها گفته شود: «بیایید تا رسول الله برای شما آمرزش بخواهد» سرهای خود را تکان می‌دهند، و آن‌ها را می‌بینی که (از سخنان تو) اعراض می‌کنند و تکبر می‌ورزند.');
INSERT INTO "hussein_taji" VALUES(63,6,'63:6','برای آن‌ها یکسان است که برایشان آمرزش بخواهی یا آمرزش نخواهی، الله هرگز آنان را نمی‌بخشد، بی‌گمان الله قوم نافرمان را هدایت نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(63,7,'63:7','آن‌ها کسانی هستند که می‌گویند: «بر آنان که نزد رسول الله هستند (چیزی) انفاق نکنید تا پراکنده شوند و حال آن که خزاین آسمان‌ها و زمین از آنِ الله است، ولی منافقان درنمی‌یابند.');
INSERT INTO "hussein_taji" VALUES(63,8,'63:8','آن‌ها می‌گویند: «اگر به مدینه باز گردیم، یقیناً صاحبان عزت، ذلیلان را از آنجا بیرون می‌کنند» در حالی‌که عزت از آنِ الله، و فرستادۀ او و مؤمنان است، ولی منافقان نمی‌دانند.');
INSERT INTO "hussein_taji" VALUES(63,9,'63:9','ای کسانی‌که ایمان آورده‌‌اید! اموال‌تان، و فرزندان‌تان شما را از یاد الله غافل نکند، و هرکس که چنین کند، پس آنان زیانکارانند.');
INSERT INTO "hussein_taji" VALUES(63,10,'63:10','و از آنچه به شما روزی داده‌ایم، (در راه الله) انفاق کنید، پیش از آن که مرگ به سراغ یکی از شما آید، پس بگوید: «پروردگارا! ای کاش (مرگ) مرا مدت کمی با تأخیر می‌انداختی، تا (در راه تو) صدقه بدهم و از صالحان باشم».');
INSERT INTO "hussein_taji" VALUES(63,11,'63:11','و الله هرگز (مرگ) کسی را هنگامی‌که اجلش فرا رسد به تأخیر نمی‌اندازد، و الله به آنچه می‌کنید آگاه است.');
INSERT INTO "hussein_taji" VALUES(64,1,'64:1','آنچه در آسمان‌ها و آنچه در زمین است همه تسبیح الله می‌گویند، فرمانروایی از آنِ اوست، و سپاس (و ستایش نیز) از آنِ او است، و او بر همه چیز تواناست.');
INSERT INTO "hussein_taji" VALUES(64,2,'64:2','او کسی است که شما را آفرید، پس (گروهی) از شما کافرند و (گروهی) از شما مؤمن هستند، و الله به آنچه می‌کنید بیناست.');
INSERT INTO "hussein_taji" VALUES(64,3,'64:3','آسمان‌ها و زمین را به‌حق آفرید، و شما را (در رحم مادرانتان) شکل و صورت بخشید، پس شکل و صورت‌تان را نیکو (و زیبا) گرداند، و بازگشت (همه) به سوی اوست.');
INSERT INTO "hussein_taji" VALUES(64,4,'64:4','آنچه را که در آسمان‌ها و زمین است می‌داند، و آنچه را که پنهان می‌دارید و آنچه را که آشکار می‌کنید می‌داند، و الله از آنچه در سینه‌هاست آگاه است.');
INSERT INTO "hussein_taji" VALUES(64,5,'64:5','آیا خبر کسانی‌که پیش از این کافر شدند، به شما نرسیده است؟! پس آن‌ها (طعم) کیفر کار خود را چشیدند، و برای آن‌ها عذاب دردناکی است.');
INSERT INTO "hussein_taji" VALUES(64,6,'64:6','این بدان سبب است که پیامبران‌شان با معجزات (و دلایل روشن) به سوی آنان آمدند، پس (آن‌ها از روی تکبر) گفتند: «آیا بشری (مانند ما) می‌خواهد ما را هدایت کند؟!» آنگاه کافر شدند و روی گرداندند، و الله (از آن‌ها و ایمان‌شان) بی‌نیاز بود، و الله بی‌نیاز ستوده است.');
INSERT INTO "hussein_taji" VALUES(64,7,'64:7','کسانی‌که کافر شدند گمان بردند که هرگز برانگیخته نخواهند شد، (ای پیامبر!) بگو: «آری، به پروردگارم سوگند، یقیناً (همه) برانگیخته خواهید شد، آنگاه از آنچه می‌کردید به شما خبر خواهند داد، و این (کار) بر الله آسان است».');
INSERT INTO "hussein_taji" VALUES(64,8,'64:8','پس به الله و پیامبرش و نوری که نازل کرده‌ایم ایمان بیاورید، و الله به آنچه می‌کنید آگاه است.');
INSERT INTO "hussein_taji" VALUES(64,9,'64:9','روزی‌که همۀ شما را (در محشر) گرد می‌آورد، آن روز، روز مغبونی است، و هرکس به الله ایمان آورد و کارهای شایسته انجام دهد، گناهان او را می‌بخشد و او را به باغ‌هایی (از بهشت) وارد می‌کند که نهرها زیر (درختان) آن جاری است، جاودانه در آن می‌مانند، این کامیابی بزرگ است.');
INSERT INTO "hussein_taji" VALUES(64,10,'64:10','و کسانی‌که کافر شدند و آیات ما را تکذیب کردند آنان اهل آتش (دوزخ) اند که جاودانه درآن می‌مانند، و بد (سرانجام و) جایگاهی است.');
INSERT INTO "hussein_taji" VALUES(64,11,'64:11','هیچ مصیبتی (به انسان) نمی‌رسد، مگر به حکم (و فرمان) الله و هرکس به الله ایمان آورد (الله) قلبش را هدایت می‌کند، و الله به همه چیز داناست.');
INSERT INTO "hussein_taji" VALUES(64,12,'64:12','الله را اطاعت کنید، و رسول الله را اطاعت کنید، پس اگر روی بگردانید، (بدانید که) بر فرستادۀ ما وظیفه‌ای جز ابلاغ آشکار نیست.');
INSERT INTO "hussein_taji" VALUES(64,13,'64:13','الله کسی است که هیچ معبودی (به حق) جز او نیست، پس مؤمنان باید بر الله توکل کنند.');
INSERT INTO "hussein_taji" VALUES(64,14,'64:14','ای کسانی‌که ایمان آورده‌اید! به راستی (بعضی) از همسران‌تان و فرزندان‌تان دشمنان شما هستند، پس از آن‌ها بر حذر باشید، و اگر عفو کنید، و چشم بپوشید، و ببخشید، بی‌گمان الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(64,15,'64:15','جز این نیست که اموال‌تان و اولادتان مایۀ آزمایش هستند، و الله است که پاداش بزرگ نزد اوست.');
INSERT INTO "hussein_taji" VALUES(64,16,'64:16','پس تا (جایی که) می‌توانید از الله بترسید، و گوش دهید، و فرمان برید، و انفاق کنید که برای خود شما بهتر است، و کسانی‌که از بخل و حرص نفس خود مصون بمانند آنانند که رستگارند.');
INSERT INTO "hussein_taji" VALUES(64,17,'64:17','اگر به الله قرض الحسنه بدهید، آن را برای شما افزون می‌کند، و شما را بیامرزد، و الله قدر شناس بردبار است.');
INSERT INTO "hussein_taji" VALUES(64,18,'64:18','دانای پنهان و آشکار، و پیروزمند حکیم است.');
INSERT INTO "hussein_taji" VALUES(65,1,'65:1','ای پیامبر! هرگاه خواستید زنان را طلاق دهید، پس آن‌ها را در زمان (آغاز) عدّه طلاق دهید، و حساب عدّه را نگه دارید، و از الله که پروردگار شماست بترسید، نه شما آن‌ها را از خانه‌هایشان بیرون کنید، و نه آن‌ها بیرون روند، مگر آن که کار زشت آشکاری مرتکب شوند، و این حدود الله است، و هرکس از حدود الله تجاوز کند پس مسلماً به خود ستم کرده است. تو نمی‌دانی، شاید الله پس از آن وضع تازه‌ای فراهم آورد (که به صلح و آشتی منجر شود).');
INSERT INTO "hussein_taji" VALUES(65,2,'65:2','پس چون (آن‌ها را طلاق دادید و) عدّۀ آن‌ها به سر رسید، آن‌ها را به طرز شایسته‌ای نگه دارید یا به طرز شایسته‌ای از آن‌ها جدا شوید، و دو فرد عادل از خودتان را گواه بگیرید، و شهادت را برای الله برپا دارید، این (حکمی است) که کسی‌که به الله و روز قیامت ایمان دارد به آن پند و اندرز داده می‌شود، و هرکس که از الله بترسد، (الله) راه نجاتی برای او قرار می‌دهد.');
INSERT INTO "hussein_taji" VALUES(65,3,'65:3','و او را از جایی که گمان ندارد روزی می‌دهد، و هرکس بر الله توکل کند، پس همان او را کافی است. بی‌گمان الله فرمان خود را به انجام می‌رساند، مسلّماً الله برای هر چیزی اندازه‌ای قرار داده است.');
INSERT INTO "hussein_taji" VALUES(65,4,'65:4','و کسانی از زنان‌تان که از عادت ماهانه مأیوس شده‌اند، - اگر شک کردید - عدّۀ آن‌ها سه ماه است و (نیز) آن‌ها که حیض نشده‌اند. و عدّۀ زنان بار دار این است که بار خود را بگذارند، و هرکس از الله بترسد، (الله) کارش را برایش آسان می‌سازد.');
INSERT INTO "hussein_taji" VALUES(65,5,'65:5','این فرمان الله است که بر شما نازل کرده است، و هرکس از الله بترسد، الله گناهانش را از او می‌زداید (و می‌بخشد) و پاداش او را بزرگ می‌گرداند.');
INSERT INTO "hussein_taji" VALUES(65,6,'65:6','آنان (زنان مطلقه) را در حد توان‌تان هر جای که خودتان سکونت دارید، سکونت دهید، و به آن‌ها زیان نرسانید تا (عرصه را) بر آنان تنگ کنید، (و پیش از پایان عده مجبور به ترک منزل شوند) و اگر باردار باشند نفقۀ آن‌ها را بدهید تا آن که حمل خود را بگذارند. پس اگر (فرزندتان را) برای شما شیر می‌دهند، مزدشان را بپردازید، و (این کار را) به نیکی و با مشورت همدیگر انجام دهید، و اگر به توافق نرسیدید، پس زن دیگری (به درخواست شوهر) او را شیر دهد.');
INSERT INTO "hussein_taji" VALUES(65,7,'65:7','آنان که دارا (و ثروتمند) هستند باید از دارایی (و ثروت) خود انفاق کنند، و کسی‌که تنگدست است پس باید از آنچه که الله به او داده انفاق کند، الله هیچکس را جز به‌اندازۀ (توانایی) که به او داده مکلّف نمی‌کند، الله به زودی بعد از سختی (و تنگدستی) آسانی (و گشایش) قرار می‌دهد.');
INSERT INTO "hussein_taji" VALUES(65,8,'65:8','چه بسیار (اهالی) آبادی‌ها که از فرمان پروردگارشان و (فرمان) پیامبران او سرپیچی کردند، پس ما به شدت از آن‌ها حساب کشیدیم، و به عذابی سخت (و سهمناک) آنان را عذاب کردیم.');
INSERT INTO "hussein_taji" VALUES(65,9,'65:9','پس آن‌ها (طعم) عقوبت کار خود را چشیدند، و سر انجام کارشان زیان بود.');
INSERT INTO "hussein_taji" VALUES(65,10,'65:10','الله عذاب سختی برای آن‌ها آماده کرده است. پس ای خردمندانی که ایمان آورده‌اید! از الله بترسید، محققاً الله ذکر (قرآن) را به سوی شما نازل کرده است.');
INSERT INTO "hussein_taji" VALUES(65,11,'65:11','(و نیز) پیامبری (به سوی شما فرستاده) که آیات روشن الله را بر شما می‌خواند، تا کسانی را که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند از تاریکی‌ها به سوی نور بیرون آورد. و هرکس به الله ایمان آورد و کار شایسته انجام دهد (الله) او را به باغ‌هایی (از بهشت) وارد می‌کند که از زیر (درختان) آن نهرها جاری است، جاودانه در آن بمانند، به راستی الله رزق و روزیش را نیکو گردانیده است.');
INSERT INTO "hussein_taji" VALUES(65,12,'65:12','الله (همان) کسی است که هفت آسمان را آفرید، و از زمین (نیز) همانند آن‌ها را (آفرید). و فرمان (الله) پیوسته در میان آن‌ها نازل می‌شود تا بدانید که الله بر هرچیز تواناست، و آن که علم الله بر همه چیز احاطه دارد.');
INSERT INTO "hussein_taji" VALUES(66,1,'66:1','ای پیامبر! چرا چیزی را که الله بر تو حلال کرده است به خاطر خشنودی همسرانت (بر خود) حرام می‌کنی؟! و الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(66,2,'66:2','به راستی الله (راه) گشودن سوگندهای‌تان را برای شما مقرر داشته است، و الله دوستدار (و مولای) شماست، و او دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(66,3,'66:3','و هنگامی‌که پیامبر به بعضی از همسرانش سخنانی را به راز گفت، پس چون (آن همسر) آن را (به دیگری) خبر داد، و الله او (پیامبر) را بر آن آگاه کرد، قسمتی از آن را برای او بیان کرد و از قسمتی (دیگر) خود داری نمود، پس هنگامی‌که (پیامبر) او را از آن خبر داد، گفت: «چه کسی این را به تو خبر داده است؟» (پیامبر) فرمود: «(الله) دانای آگاه مرا خبر داده است».');
INSERT INTO "hussein_taji" VALUES(66,4,'66:4','(ای همسران پیامبر!) اگر شما دو نفر به درگاه الله توبه کنید (به سود شماست) بی‌گمان دل‌هایتان (از حق) منحرف گشته است، و اگر شما دو نفر بر علیه او همدست (متفق) شوید پس (بدانید) که الله یاور (و مولای) اوست، و (نیز) جبرئیل و مردمان شایسته از مؤمنان، و فرشتگان (نیز) بعد از آن پشتیبان (و مددکار او) هستند.');
INSERT INTO "hussein_taji" VALUES(66,5,'66:5','چه بسا اگر شما را طلاق دهد، پروردگارش همسرانی بهتر از شما برایش جایگزین کند، (که) مسلمان، مؤمن، فرمانبردار، توبه‌کار، عبادت‌کننده، روزه‌دار، بیوه و دوشیزه باشند.');
INSERT INTO "hussein_taji" VALUES(66,6,'66:6','ای کسانی‌که ایمان آورده‌اید! خودتان و خانوادۀ‌تان را از آتشی که هیمۀ آن مردم و سنگ‌هاست نگه دارید (آتشی که) برآن فرشتگان خشن و سخت‌گیر (گمارده شده) که هرگز الله را در آنچه به آنان فرمان داده نافرمانی نمی‌کنند، و هرچه فرمان می‌یابند انجام می‌دهند.');
INSERT INTO "hussein_taji" VALUES(66,7,'66:7','ای کسانی‌که کافر شده‌اید! امروز عذرخواهی نکنید، جز این نیست که در برابر آنچه می‌کردید جزا داده می‌شوید.');
INSERT INTO "hussein_taji" VALUES(66,8,'66:8','ای کسانی‌که ایمان آورده‌اید! به سوی الله توبه کنید، توبه‌ای خالص، امید است پروردگارتان گناهان‌تان را از شما بزداید، و شما را به باغ‌هایی (از بهشت) که نرها زیر (درختان) آن جاری است وارد کند، روزی‌که الله، پیامبر و کسانی را که با او ایمان آورده‌اند خوار نمی‌کند، نورشان پیشاپیش آنان و سمت راست‌شان در حرکت است، می‌گویند: «پروردگارا! نور ما را به تمام (و کمال) برسان، و ما را بیامرز بی‌گمان تو بر هرچیز توانایی».');
INSERT INTO "hussein_taji" VALUES(66,9,'66:9','ای پیامبر! با کفار و منافقان جهاد کن، و بر آن‌ها سخت بگیر، و جایگاهشان جهنم است، و بد جایگاهی است.');
INSERT INTO "hussein_taji" VALUES(66,10,'66:10','الله برای کسانی‌که کافر شده‌اند، همسر نوح و همسر لوط را مَثَل زده است که آن دو در نکاح دو تن از بندگان صالح ما بودند، پس به آن دو خیانت کردند، آنگاه (آن دو بندۀ صالح) نتوانستند چیزی از (عذاب) الله را از آن دو دفع کنند، و (به آن دو) گفته شد: «همراه وارد شوندگان به آتش (جهنم) وارد شوید».');
INSERT INTO "hussein_taji" VALUES(66,11,'66:11','و الله برای کسانی‌که ایمان آورده‌اند، همسر فرعون (آسیه) را مَثَل زده است، هنگامی‌که گفت: «پروردگارا! خانه‌ای‌ برای من نزد خودت در بهشت بساز، و مرا از فرعون و کردارش نجات بده، و مرا از قوم ستمگر رهایی بخش».');
INSERT INTO "hussein_taji" VALUES(66,12,'66:12','و (نیز) مریم دختر عمران را (مَثَل زده) که شرمگاه خویش را (پاک) نگه داشت، پس ما از روح خود در آن دمیدیم، و (او) کلمات پروردگارش و کتاب‌های او را تصدیق کرد و از فرمانبرداران بود.');
INSERT INTO "hussein_taji" VALUES(67,1,'67:1','پر برکت و بزرگوار است کسی‌که فرمانروایی جهان هستی به دست اوست، و او بر هر چیز تواناست.');
INSERT INTO "hussein_taji" VALUES(67,2,'67:2','(همان) کسی‌که مرگ و زندگی را آفرید تا شما را بیازماید که کدام یک از شما نیکو کار‌تر است، و او پیروزمند بخشنده است.');
INSERT INTO "hussein_taji" VALUES(67,3,'67:3','(آن) کس‌که هفت آسمان را بر فراز یکدیگر آفرید، در آفرینش (الله) رحمان هیچ خلل و بی نظمی نمی‌بینی، باز چشم بگردان، آیا نقصانی (و شکافی) می‌بینی؟');
INSERT INTO "hussein_taji" VALUES(67,4,'67:4','سپس بار دیگر چشم بگردان، چشم تو در حالی‌که خسته و ناتوان است به سوی تو باز می‌گردد.');
INSERT INTO "hussein_taji" VALUES(67,5,'67:5','به راستی ما آسمان دنیا را با چراغ‌ها آراستیم، و آن‌ها را برای راندن شیاطین قرار دادیم، و برای آن‌ها عذاب (آتش) سوزان آماده ساختیم.');
INSERT INTO "hussein_taji" VALUES(67,6,'67:6','و برای کسانی‌که به پروردگار‌شان کافر شدند عذاب جهنم است، و چه بد جایگاهی است.');
INSERT INTO "hussein_taji" VALUES(67,7,'67:7','هنگامی‌که در آن انداخته شوند، از آن نعره‌ای (وحشتناک) می‌شنوند، در حالی‌که آن می‌جوشد.');
INSERT INTO "hussein_taji" VALUES(67,8,'67:8','از شدت خشم نزدیک است پاره پاره شود، هرگاه که گروهی در آن انداخته می‌شوند، نگهبانانش از آن‌ها می‌پرسند: «آیا بیم‌دهنده‌ای به سراغ شما نیامد؟!».');
INSERT INTO "hussein_taji" VALUES(67,9,'67:9','گویند: «چرا، بیم‌دهنده به سوی ما آمد، پس ما او را تکذیب کردیم، و گفتیم: هرگز الله چیزی نازل نکرده است، شما جز در گمراهی بزرگ نیستید».');
INSERT INTO "hussein_taji" VALUES(67,10,'67:10','و گویند: «اگر ما می‌شنیدیم یا تعقل می‌کردیم، در (زمرۀ) دوزخیان نبودیم».');
INSERT INTO "hussein_taji" VALUES(67,11,'67:11','پس آن‌ها به گناه خود اعتراف کنند، دور باد دوزخیان از رحمت الله.');
INSERT INTO "hussein_taji" VALUES(67,12,'67:12','بی‌گمان کسانی‌که درنهان از پروردگار‌شان می‌ترسند، آنان آمرزش و پاداش بزرگی دارند.');
INSERT INTO "hussein_taji" VALUES(67,13,'67:13','گفتار خود را پنهان دارید یا آن را آشکار سازید، (به هر حال) او به آنچه در دل‌هاست آگاه است.');
INSERT INTO "hussein_taji" VALUES(67,14,'67:14','آیا کسی‌که (همۀ موجودات را) آفریده است، (از حال آن‌ها) نمی‌داند؟ در حالی‌که او باریک‌بین و آگاه است.');
INSERT INTO "hussein_taji" VALUES(67,15,'67:15','او کسی است که زمین را برای شما رام گردانید، پس در گوشه و نواحی آن راه بروید و از روزی او بخورید، و بازگشت (همه) به سوی اوست.');
INSERT INTO "hussein_taji" VALUES(67,16,'67:16','آیا خود را از کسی‌که در آسمان است، در امان می‌دانید، که (فرمان دهد) زمین شما را فرو برد، پس آن ناگهان به لرزش در آید؟');
INSERT INTO "hussein_taji" VALUES(67,17,'67:17','یا خود را از کسی‌که در آسمان است، در امام می‌دانید، که تند بادی همراه با سنگریزه بر شما فرستد؟ پس بزودی خواهید دانست که هشدار من چگونه است!');
INSERT INTO "hussein_taji" VALUES(67,18,'67:18','و به راستی کسانی‌که پیش از آنان بودند (نیز دعوت پیامبران را) تکذیب کردند، پس عقوبت من چگونه بود!');
INSERT INTO "hussein_taji" VALUES(67,19,'67:19','آیا به پرندگانی که بر فراز سرشان است، که گاهی بال‌های خود را می‌گشایند، و گاهی فرو می‌بندند، نگاه نکردند؟! جز (الله) رحمان (کسی) آن‌ها را (بر فراز آسمان) نگاه نمی‌دارد، بی‌گمان او به هر چیز بیناست.');
INSERT INTO "hussein_taji" VALUES(67,20,'67:20','آیا این چه کسی است آن که لشکر شماست، که به جای (الله) رحمان شما را یاری می‌کند؟ کافران جز در فریب (و غرور‌شان) نیستند.');
INSERT INTO "hussein_taji" VALUES(67,21,'67:21','آیا آن کیست که به شما روزی دهد اگر او روزیش را باز دارد؟ بلکه آن‌ها در سرکشی و دوری از حق لجاجت می‌کنند.');
INSERT INTO "hussein_taji" VALUES(67,22,'67:22','آیا کسی‌که به رو افتاده حرکت می‌کند به هدایت نزدیکتراست، یا آن کسی‌که راست و استوار بر راه راست گام بر می‌دارد؟!');
INSERT INTO "hussein_taji" VALUES(67,23,'67:23','بگو: «او کسی است که شما را آفرید، و برای شما گوش و چشمان و دل قرار داد، (اما شما) اندکی سپاس گزاری می‌کنید».');
INSERT INTO "hussein_taji" VALUES(67,24,'67:24','بگو: «او کسی است شما را در زمین پراکند و به سوی او گرد آورده می‌شوید».');
INSERT INTO "hussein_taji" VALUES(67,25,'67:25','و می‌گویند: «اگر راست می‌گویید این وعدۀ (قیامت) چه زمانی است؟».');
INSERT INTO "hussein_taji" VALUES(67,26,'67:26','(ای پیامبر) بگو: «علم (قیامت) تنها نزد الله است، و من فقط هشداردهنده‌ای آشکار هستم».');
INSERT INTO "hussein_taji" VALUES(67,27,'67:27','پس چون آن (وعده و عذاب الهی) را از نزدیک ببینند، چهرۀ کسانی‌که کافر شدند سیاه و اندوهگین می‌گردد، و (به آن‌ها) گفته می‌شود: این همان چیزی است که شما آن را می‌طلبیدید.');
INSERT INTO "hussein_taji" VALUES(67,28,'67:28','(ای پیامبر) بگو: «به من خبر دهید، اگر الله مرا و کسانی‌که با من هستند، هلاک کند، یا بر ما رحمت آورد، پس چه کسی کافران را از عذاب دردناک پناه می‌دهد؟!».');
INSERT INTO "hussein_taji" VALUES(67,29,'67:29','بگو: «او (الله) رحمان است، به او ایمان آورده‌ایم، و بر او توکل کرده‌ایم، پس (ای کافران) بزودی خواهید دانست چه کسی در گمراهی آشکار است!».');
INSERT INTO "hussein_taji" VALUES(67,30,'67:30','(ای پیامبر) بگو: «به من خبر دهید اگر آب (شرب) شما (در زمین) فرو رود، پس چه کسی می‌تواند برای شما آب روان (و گوارا) بیاورد؟!».');
INSERT INTO "hussein_taji" VALUES(68,1,'68:1','نون، سوگند به قلم و آنچه می‌نویسند.');
INSERT INTO "hussein_taji" VALUES(68,2,'68:2','که تو (ای پیامبر) به نعمت (و فضل) پروردگارت دیوانه نیستی.');
INSERT INTO "hussein_taji" VALUES(68,3,'68:3','و بی‌گمان برای تو (ای پیامبر) پاداشی بی‌پایان (و عظیم) است.');
INSERT INTO "hussein_taji" VALUES(68,4,'68:4','و یقیناً تو (ای محمد) بر اخلاق و خوی بسیار عظیم و والایی هستی.');
INSERT INTO "hussein_taji" VALUES(68,5,'68:5','پس تو بزودی خواهی دید و آن‌ها (نیز) خواهند دید.');
INSERT INTO "hussein_taji" VALUES(68,6,'68:6','که کدام یک از شما دیوانه است!');
INSERT INTO "hussein_taji" VALUES(68,7,'68:7','همانا پروردگارت بهتر می‌داند چه کسی از راه او گمراه گشته است، و (نیز) او به هدایت یافتگان دانا‌تر است.');
INSERT INTO "hussein_taji" VALUES(68,8,'68:8','پس از تکذیب کنندگان اطاعت مکن.');
INSERT INTO "hussein_taji" VALUES(68,9,'68:9','آن‌ها دوست دارند (و می‌خواهند) که نرمی (و مدارا) کنی، پس آن‌ها (نیز) نرمی (و مدارا) کنند.');
INSERT INTO "hussein_taji" VALUES(68,10,'68:10','و از هر فرومایه‌ای که بسیار سوگند (دروغ) یاد می‌کند، اطاعت مکن.');
INSERT INTO "hussein_taji" VALUES(68,11,'68:11','(آن) بسیار عیبجویی که به سخن‌چینی آمد و رفت می‌کند.');
INSERT INTO "hussein_taji" VALUES(68,12,'68:12','و بسیار بازدارندۀ (کار) خیر، و متجاوز و گناهکار است.');
INSERT INTO "hussein_taji" VALUES(68,13,'68:13','خشن (و کینه‌توز) و (بدتر) از آن (بی‌نسب و) حرام زاده است.');
INSERT INTO "hussein_taji" VALUES(68,14,'68:14','بدان خاطر که صاحب مال و فرزندان بسیار است (مبادا از او اطاعت کنی!).');
INSERT INTO "hussein_taji" VALUES(68,15,'68:15','هنگامی‌که آیات ما بر او خواند شود، گوید: «(این‌ها) افسانه‌های گذشتگان است.');
INSERT INTO "hussein_taji" VALUES(68,16,'68:16','به زودی بر بینی او داغ می‌گذاریم.');
INSERT INTO "hussein_taji" VALUES(68,17,'68:17','ما آن‌ها را آزمایش کردیم، همان‌گونه که صاحبان باغ را آزمودیم، هنگامی‌که سوگند یاد کردند که صبحگاهان (که مستندان نباشند) میوه‌های باغ را بچینند.');
INSERT INTO "hussein_taji" VALUES(68,18,'68:18','و ان شاء الله (اگر الله بخواهد) نگفتند.');
INSERT INTO "hussein_taji" VALUES(68,19,'68:19','پس عذابی (فرا‌‌گیر) از سوی پروردگارت بر (باغ) آن‌ها فرو آمد، در حالی‌که همه در خواب بودند.');
INSERT INTO "hussein_taji" VALUES(68,20,'68:20','پس آن باغ (سبز و خرم) همچون شب سیاه شد.');
INSERT INTO "hussein_taji" VALUES(68,21,'68:21','آنگاه صبحگاهان یکدیگر را ندا دادند.');
INSERT INTO "hussein_taji" VALUES(68,22,'68:22','که اگر می‌خواهید درو کنید، به سوی کشتزار (و باغ) خویش بروید.');
INSERT INTO "hussein_taji" VALUES(68,23,'68:23','پس آن‌ها (به سوی کشتزار) حرکت کردند. در حالی‌که آهسته باهم می‌گفتند:');
INSERT INTO "hussein_taji" VALUES(68,24,'68:24','«(مراقب باشید) که امروز هیچ بی‌نوا (و مسکینی) در آن (باغ) وارد نشود».');
INSERT INTO "hussein_taji" VALUES(68,25,'68:25','و صبحگاهان با تصمیم جدی بر منع و جلوگیری (از مستمندان) بیرون شدند.');
INSERT INTO "hussein_taji" VALUES(68,26,'68:26','پس هنگامی‌که آن (باغ) را دیدند، گفتند: «یقیناً ما راه گم کرده‌ایم،');
INSERT INTO "hussein_taji" VALUES(68,27,'68:27','نه، بلکه ما (از حاصلان) بی‌بهره و محروم هستیم».');
INSERT INTO "hussein_taji" VALUES(68,28,'68:28','یکی از بهترین آن‌ها گفت: «آیا به شما نگفتم، چرا (شکر و) تسبیح (الله را) نمی‌گویید؟!».');
INSERT INTO "hussein_taji" VALUES(68,29,'68:29','گفتند: «پروردگار ما پاک و منزه است، مسلّماً ما ستمکار بودیم».');
INSERT INTO "hussein_taji" VALUES(68,30,'68:30','آنگاه ملامت کنان به یکدیگر رو آوردند.');
INSERT INTO "hussein_taji" VALUES(68,31,'68:31','گفتند: «وای بر ما که طغیان‌گر (و سرکش) بودیم،');
INSERT INTO "hussein_taji" VALUES(68,32,'68:32','امید‌‌‌‌‌‌‌واریم که پروردگارمان (ما را ببخشد و) در عوض چیزی بهتر از آن (باغ) به ما بدهد، بی‌گمان ما به سوی پروردگار خود روی آورده‌ایم».');
INSERT INTO "hussein_taji" VALUES(68,33,'68:33','عذاب (الله در دنیا) این چنین است، و عذاب آخرت مسلماً بزرگ‌تر (و بیشتر) است، اگر آن‌ها می‌دانستند.');
INSERT INTO "hussein_taji" VALUES(68,34,'68:34','به راستی برای پرهیزگاران نزد پروردگار‌شان باغ‌های پر نعمت است.');
INSERT INTO "hussein_taji" VALUES(68,35,'68:35','آیا ما مسلمانان را همچون مجرمان (و گناهکاران) قرار می‌دهیم؟!.');
INSERT INTO "hussein_taji" VALUES(68,36,'68:36','شما را چه شده است، چگونه داوری می‌کنید؟!');
INSERT INTO "hussein_taji" VALUES(68,37,'68:37','آیا شما کتابی (آسمانی) دارید که در آن می‌خوانید؟!');
INSERT INTO "hussein_taji" VALUES(68,38,'68:38','و در آن (کتاب) آنچه را انتخاب کنید، برای شما (نوشته شده) است؟!');
INSERT INTO "hussein_taji" VALUES(68,39,'68:39','یا اینکه شما بر (عهدۀ) ما عهد و پیمان استواری تا روز قیامت دارید، که هر چه را حکم کنید، (حق) برای شما باشد؟!');
INSERT INTO "hussein_taji" VALUES(68,40,'68:40','(ای پیامبر) از آن‌ها بپرس، کدام یک از آنان در برابر این (ادعا‌ها) متعهد است؟!');
INSERT INTO "hussein_taji" VALUES(68,41,'68:41','و یا اینکه آن‌ها (معبودان و) شریکانی دارند، پس اگر است می‌گویند باید شریکان (و معبودان) خویش را بیاورند.');
INSERT INTO "hussein_taji" VALUES(68,42,'68:42','روزی‌که (کار بالا گیرد و الله متعال برای داوری میان خلائق بیاید) ساق برهنه و مکشوف گردد، و (مشرکان) دعوت به سجود می‌شوند، ولی نمی‌توانند (سجده کنند).');
INSERT INTO "hussein_taji" VALUES(68,43,'68:43','(در حالی‌که) دیدگانشان (از ندامت و خجالت) فرو افتاده و ذلّت و خواری وجود‌شان را در بر گرفته است، و یقیناً که پیش از آن (در دنیا) در حالی‌که سالم بودند به سجود فرا خوانده می‌شدند.');
INSERT INTO "hussein_taji" VALUES(68,44,'68:44','پس (ای پیامبر) مرا با آن کسی‌که این سخن (قرآن) را تکذیب می‌کند وا‌گذار، ما آنان را از آنجایی‌که نمی‌دانند بتدریج خواهیم گرفت.');
INSERT INTO "hussein_taji" VALUES(68,45,'68:45','و به آن‌ها مهلت می‌دهم، بی‌گمان مکر (و تدبیر) من استوار (و محکم) است.');
INSERT INTO "hussein_taji" VALUES(68,46,'68:46','(ای پیامبر) آیا تو از آن‌ها مزدی درخواست می‌کنی، که (آن‌ها از ادای آن در رنجند، و) برایشان سنگین است؟!');
INSERT INTO "hussein_taji" VALUES(68,47,'68:47','یا (اسرار) غیب نزد آن‌هاست، پس آنان (از آن) می‌نویسند.');
INSERT INTO "hussein_taji" VALUES(68,48,'68:48','پس (ای پیامبر) برای فرمان (و حکم) پروردگارت صبر کن، و مانند صاحب ماهی (= یونس بن متی) مباش، هنگامی‌که سرشار از غم و اندوه بود (الله را) نداد کرد.');
INSERT INTO "hussein_taji" VALUES(68,49,'68:49','اگر نعمت (و رحمتی) از سوی پروردگارش او را در نمی‌یافت، یقیناً (از شکم ماهی) به صحرای بی آب و علف بیرون افکنده می‌شد، در حالی‌که او بد‌حال (و نکوهیده) بود.');
INSERT INTO "hussein_taji" VALUES(68,50,'68:50','پس پروردگارش او را برگزید، و او را از صالحان قرار داد.');
INSERT INTO "hussein_taji" VALUES(68,51,'68:51','و (ای پیامبر) نزدیک است کسانی‌که کافر شدند، هنگامی‌که آیات قرآن را می‌شنوند، با چشم‌های خود تو را چشم زخم بزنند (و بلغزانند) و می‌گویند: «قطعاً او دیوانه است».');
INSERT INTO "hussein_taji" VALUES(68,52,'68:52','در حالی‌که این (قرآن) جز پندی برای جهانیان نیست.');
INSERT INTO "hussein_taji" VALUES(69,1,'69:1','(روز) واقع شدنی (رستاخیز)');
INSERT INTO "hussein_taji" VALUES(69,2,'69:2','آن واقع شدنی چیست؟');
INSERT INTO "hussein_taji" VALUES(69,3,'69:3','و تو چه دانی آن واقع شدنی چیست (و چگونه است)؟!');
INSERT INTO "hussein_taji" VALUES(69,4,'69:4','(قوم) ثمود و (قوم) عاد (قیامت) درهم کوبنده را تکذیب کردند.');
INSERT INTO "hussein_taji" VALUES(69,5,'69:5','پس اما (قوم) ثمود، به بانگ سهمگین هلاک شدند.');
INSERT INTO "hussein_taji" VALUES(69,6,'69:6','و اما (قوم) عاد با تند بادی سرکش و سرد به هلاکت رسیدند.');
INSERT INTO "hussein_taji" VALUES(69,7,'69:7','(الله) هفت شب و هشت روز پی در پی آن را بر آن‌ها مسلط نمود، پس (اگر آنجا می‌بودی) (آن) قوم را آن (تند باد) مانند تنه‌های پوسیدۀ تو خالی درختان خرما، بر زمین افتاده (و هلاک شده) می‌دیدی.');
INSERT INTO "hussein_taji" VALUES(69,8,'69:8','پس آیا از آن‌ها کسی را می‌بینی که باقی مانده باشد؟!');
INSERT INTO "hussein_taji" VALUES(69,9,'69:9','و فرعون و کسانی‌که پیش از او بودند، و (مردم) شهر‌های ویران شده (قوم لوط) مرتکب گناهان (بزرگ) شدند.');
INSERT INTO "hussein_taji" VALUES(69,10,'69:10','پس آن‌ها از (فرمان) فرستادۀ پروردگار‌شان سرپیچی کردند، آنگاه (الله) آن‌ها را به عذاب سختی گرفتارشان کرد.');
INSERT INTO "hussein_taji" VALUES(69,11,'69:11','همانا هنگامی‌که آب طغیان کرد، شما را در کشتی سوار کردیم.');
INSERT INTO "hussein_taji" VALUES(69,12,'69:12','تا که پندی برای شما قرار دهیم، و گوش‌های شنوا آن را بشنوند و به یاد بسپارند.');
INSERT INTO "hussein_taji" VALUES(69,13,'69:13','پس هنگامی‌که یک بار در «صور» دمیده شود.');
INSERT INTO "hussein_taji" VALUES(69,14,'69:14','و زمین و کوه‌ها از جا کنده شود، پس یکباره درهم کوبیده (و متلاشی) شوند.');
INSERT INTO "hussein_taji" VALUES(69,15,'69:15','پس در آن روز واقعۀ (عظیم) قیامت به وقوع می‌پیوندد.');
INSERT INTO "hussein_taji" VALUES(69,16,'69:16','و آسمان شکافته می‌شود، پس در آن روز سست می‌گردد (و فرو می‌ریزد).');
INSERT INTO "hussein_taji" VALUES(69,17,'69:17','و فرشتگان بر کناره‌های آن (= آسمان) قرار می‌گیرند، و آن روز هشت (فرشته) عرش پروردگارت را بر فراز‌شان حمل می‌کنند.');
INSERT INTO "hussein_taji" VALUES(69,18,'69:18','در آن روز شما (همگی برای حساب‌رسی، به پیشگاه الله) عرضه می‌شوید، و هیچ چیز از (کار‌های) شما پنهان نخواهد ماند.');
INSERT INTO "hussein_taji" VALUES(69,19,'69:19','پس اما کسی‌که نامۀ (اعمالش) را به دست راستش دهند، گوید: «بیایید نامۀ (اعمال) مرا بخوانید!');
INSERT INTO "hussein_taji" VALUES(69,20,'69:20','من یقین داشتم که به حساب (اعمال) خودم خواهم رسید».');
INSERT INTO "hussein_taji" VALUES(69,21,'69:21','پس او در یک زندگی پسندیده (و رضایت بخشی) خواهد بود.');
INSERT INTO "hussein_taji" VALUES(69,22,'69:22','در بهشتی برین.');
INSERT INTO "hussein_taji" VALUES(69,23,'69:23','که میوه‌هایش (برای چیده شدن) در دسترس است.');
INSERT INTO "hussein_taji" VALUES(69,24,'69:24','(و به آن‌ها گفته می‌شود (: «بخورید و بیاشامید، گوارایتان باد، به (خاطر) آنچه در ایام گذشته پیش فرستاده‌اید».');
INSERT INTO "hussein_taji" VALUES(69,25,'69:25','و اما کسی‌که نامۀ (اعمالش) را به دست چپش دهند، گوید: «ای کاش هرگز نامۀ (اعمال) مرا به من داده نمی‌شد،');
INSERT INTO "hussein_taji" VALUES(69,26,'69:26','و نمی‌دانستم حسابم چیست.');
INSERT INTO "hussein_taji" VALUES(69,27,'69:27','ای کاش (با مرگ) همه چیز تمام می‌شد (و پایان کار بود).');
INSERT INTO "hussein_taji" VALUES(69,28,'69:28','مال و دارایی‌ام مرا بی‌نیاز نکرد (و سودی نبخشید).');
INSERT INTO "hussein_taji" VALUES(69,29,'69:29','قدرت و فرمانروایی‌ام (نیز) از (دست) من رفت (و به‌کلی نابود شد)».');
INSERT INTO "hussein_taji" VALUES(69,30,'69:30','(به فرشتگان گفته می‌شود:) «او را بگیرید، پس طوق (به گردن) او اندازید.');
INSERT INTO "hussein_taji" VALUES(69,31,'69:31','سپس او را به (آتش) دوزخ بیفکنید.');
INSERT INTO "hussein_taji" VALUES(69,32,'69:32','آنگاه او را در زنجیری که طول آن هفتاد ذراع است، ببندید،');
INSERT INTO "hussein_taji" VALUES(69,33,'69:33','بی‌گمان او هرگز به الله بزرگ ایمان نمی‌آورد،');
INSERT INTO "hussein_taji" VALUES(69,34,'69:34','و هرگز (مردم را) بر اطعام بی‌نوا(یان) تشویق نمی‌کرد.');
INSERT INTO "hussein_taji" VALUES(69,35,'69:35','پس امروز در این جا دوست صمیمی (و مهربانی) ندارد (که کمکش کند).');
INSERT INTO "hussein_taji" VALUES(69,36,'69:36','و غذایی جز از چرک و خون ندارد.');
INSERT INTO "hussein_taji" VALUES(69,37,'69:37','(غذایی‌که) گناهکاران آن را نخورند».');
INSERT INTO "hussein_taji" VALUES(69,38,'69:38','پس سوگند به آنچه می‌بینید،');
INSERT INTO "hussein_taji" VALUES(69,39,'69:39','و به آنچه نمی‌بینید.');
INSERT INTO "hussein_taji" VALUES(69,40,'69:40','بی‌تردید این (قرآن) گفتار رسول بزرگوار است.');
INSERT INTO "hussein_taji" VALUES(69,41,'69:41','و این‌ها گفتار شاعری نیست، (اما شما) اندکی ایمان می‌آورید.');
INSERT INTO "hussein_taji" VALUES(69,42,'69:42','و (نیز) گفتار کاهنی نیست، (لیکن شما) کمتر پند می‌گیرید.');
INSERT INTO "hussein_taji" VALUES(69,43,'69:43','(این قرآن) از سوی پروردگار جهانیان نازل شده است.');
INSERT INTO "hussein_taji" VALUES(69,44,'69:44','و اگر (پیامبر) سخنانی (به دروغ) بر ما می‌بست.');
INSERT INTO "hussein_taji" VALUES(69,45,'69:45','مسلّماً ما دست راست او را می‌گرفتیم.');
INSERT INTO "hussein_taji" VALUES(69,46,'69:46','سپس (شاه) رگ قلبش را قطع می‌کردیم.');
INSERT INTO "hussein_taji" VALUES(69,47,'69:47','پس هیچ کس از شما نمی‌توانست از (عذاب) او مانع شود.');
INSERT INTO "hussein_taji" VALUES(69,48,'69:48','و یقیناً این (قرآن) پند و تذکری برای پرهیزگاران است.');
INSERT INTO "hussein_taji" VALUES(69,49,'69:49','و ما محققاً می‌دانیم که برخی از شما تکذیب کنندگان (این کلام الهی) هستید.');
INSERT INTO "hussein_taji" VALUES(69,50,'69:50','و همانا این (قرآن) بر کافران مایۀ حسرت و ندامت است.');
INSERT INTO "hussein_taji" VALUES(69,51,'69:51','و بی‌گمان این (قرآن) حق الیقین است.');
INSERT INTO "hussein_taji" VALUES(69,52,'69:52','پس (ای پیامبر) به نام پروردگار بزرگت تسبیح گوی.');
INSERT INTO "hussein_taji" VALUES(70,1,'70:1','تقاضاکننده‌ای، تقاضای عذابی وقوع یافتنی کرد.');
INSERT INTO "hussein_taji" VALUES(70,2,'70:2','(این عذاب) برای کافران است. هیچ دفع‌کننده‌ای ندارد.');
INSERT INTO "hussein_taji" VALUES(70,3,'70:3','از سوی الله صاحب مراتب و درجات است.');
INSERT INTO "hussein_taji" VALUES(70,4,'70:4','فرشتگان و روح (جبرئیل) به سوی او عروج می‌کنند، در روزی‌که مقدارش پنجاه هزار سال است.');
INSERT INTO "hussein_taji" VALUES(70,5,'70:5','پس تو (ای پیامبر!) صبر جمیل پیشه کن.');
INSERT INTO "hussein_taji" VALUES(70,6,'70:6','بی‌گمان آن‌ها آن (روز) را دور می‌بینند.');
INSERT INTO "hussein_taji" VALUES(70,7,'70:7','و ما آن را نزدیک می‌بینیم.');
INSERT INTO "hussein_taji" VALUES(70,8,'70:8','(همان) روزی‌که آسمان چون مس گداخته شود.');
INSERT INTO "hussein_taji" VALUES(70,9,'70:9','و کوه‌ها مانند پشم رنگین (متلاشی و پراکنده) شود.');
INSERT INTO "hussein_taji" VALUES(70,10,'70:10','و هیچ دوست صمیمی (و خویشاوندی) از (حال) دوست صمیمی (و خویشاوند خود) نپرسد.');
INSERT INTO "hussein_taji" VALUES(70,11,'70:11','(در حالی‌که) آنان به یکدیگر نشان داده می‌شوند گناهکار (دوست دارد و) آرزو کند خویشتن را از عذاب آن روز باز خرد به فرزندانش.');
INSERT INTO "hussein_taji" VALUES(70,12,'70:12','و زنش و برادرش.');
INSERT INTO "hussein_taji" VALUES(70,13,'70:13','و قبیله‌اش که (همیشه) او را جای و پناه می‌داد.');
INSERT INTO "hussein_taji" VALUES(70,14,'70:14','و همۀ آن‌هایی که روی زمین‌اند (همگی فدا کند) سپس او نجات یابد.');
INSERT INTO "hussein_taji" VALUES(70,15,'70:15','(اما) هرگز چنین نیست، بی‌گمان آن شعله‌های (آتش) سوزان است.');
INSERT INTO "hussein_taji" VALUES(70,16,'70:16','پوست از سر و صورت بر می‌کند.');
INSERT INTO "hussein_taji" VALUES(70,17,'70:17','کسانی را که (به فرمان الله) پشت کردند و (از حق) روی گرداندند، فرا می‌خواند.');
INSERT INTO "hussein_taji" VALUES(70,18,'70:18','و (نیز کسانی را که) اموال را جمع آوری و ذخیره کردند. (و حق آن را نپرداختند).');
INSERT INTO "hussein_taji" VALUES(70,19,'70:19','یقیناً انسان کم طاقت (و حریص) آفریده شده است.');
INSERT INTO "hussein_taji" VALUES(70,20,'70:20','هنگامی‌که بدی (و مصیبتی) به او رسد، بی‌تابی می‌کند.');
INSERT INTO "hussein_taji" VALUES(70,21,'70:21','و هنگامی‌که خوبی به او رسد، باز دارنده (و بخیل) می‌شود.');
INSERT INTO "hussein_taji" VALUES(70,22,'70:22','مگر نمازگزاران.');
INSERT INTO "hussein_taji" VALUES(70,23,'70:23','همان کسانی‌که بر نماز‌هایشان همیشه مداومت می‌کنند.');
INSERT INTO "hussein_taji" VALUES(70,24,'70:24','و کسانی‌که در اموال‌شان حق (معین و) معلومی است.');
INSERT INTO "hussein_taji" VALUES(70,25,'70:25','برای گدا و محروم.');
INSERT INTO "hussein_taji" VALUES(70,26,'70:26','و کسانی‌که روز جزا را تصدیق می‌کنند.');
INSERT INTO "hussein_taji" VALUES(70,27,'70:27','و کسانی‌که از عذاب پروردگارشان می‌ترسند.');
INSERT INTO "hussein_taji" VALUES(70,28,'70:28','بی‌گمان از عذاب پروردگارشان در امان نتوانند بود.');
INSERT INTO "hussein_taji" VALUES(70,29,'70:29','و کسانی‌که شرمگاه‌هایشان را نگه می‌دارند.');
INSERT INTO "hussein_taji" VALUES(70,30,'70:30','مگر با همسران و کنیزانی که مالک آن‌ها هستند که (در این صورت) ملامت و سرزنشی بر آن‌ها نیست.');
INSERT INTO "hussein_taji" VALUES(70,31,'70:31','پس هرکس غیر از این‌ها را بخواهد، آنان متجاوزان هستند.');
INSERT INTO "hussein_taji" VALUES(70,32,'70:32','و کسانی‌که امانت‌ها و عهد‌شان را رعایت می‌کنند.');
INSERT INTO "hussein_taji" VALUES(70,33,'70:33','و کسانی‌که به شهادت‌های خود متعهد هستند.');
INSERT INTO "hussein_taji" VALUES(70,34,'70:34','و کسانی‌که بر نمازشان محافظت می‌کنند.');
INSERT INTO "hussein_taji" VALUES(70,35,'70:35','اینان در باغ‌های (بهشت) گرامی داشته می‌شوند.');
INSERT INTO "hussein_taji" VALUES(70,36,'70:36','پس (ای پیامبر!) کافران را چه شده که به سوی تو می‌شتابند؟');
INSERT INTO "hussein_taji" VALUES(70,37,'70:37','از راست و چپ، گروه گروه (و پراکنده) می‌شوند؟!');
INSERT INTO "hussein_taji" VALUES(70,38,'70:38','آیا هر کدام از آن‌ها (با آن اعمال ناپسندش) طمع دارد که او در بهشت پرنعمت (الهی) درآورده شود؟!');
INSERT INTO "hussein_taji" VALUES(70,39,'70:39','هرگز چنین نیست، بی‌گمان ما آن‌ها را از آنچه (خود‌شان) می‌دانند آفریده‌ایم.');
INSERT INTO "hussein_taji" VALUES(70,40,'70:40','پس به پروردگار مشرق‌ها و مغرب‌ها سوگند می‌خورم که ما یقیناً قادریم.');
INSERT INTO "hussein_taji" VALUES(70,41,'70:41','بر آن که (کسانی دیگر) بهتر از آن‌ها را جایگزین کنیم، و ما (برای این کار) عاجز و ناتوان نیستیم.');
INSERT INTO "hussein_taji" VALUES(70,42,'70:42','پس (ای پیامبر!) آنان را به حال خود وا‌گذار تا در باطل خود فرو روند، و بازی کنند تا به روزی‌که به آن‌ها وعده داده شده است برسند.');
INSERT INTO "hussein_taji" VALUES(70,43,'70:43','(همان) روزی‌که شتابان از قبرها خارج می‌شوند، گویی به سوی نشانه (و بت‌ها) می‌دوند.');
INSERT INTO "hussein_taji" VALUES(70,44,'70:44','(در حالی‌که) چشم‌هایشان (از شرم و وحشت) به زیر افتاده، و ذلت (و خواری) آن‌ها را پوشانده است. این همان روزی است که به آن‌ها وعده داده می‌شد.');
INSERT INTO "hussein_taji" VALUES(71,1,'71:1','بی‌گمان ما نوع را به سوی قومش فرستادیم، (و گفتیم): «قوم خود را پیش از آن که عذاب دردناک به سراغشان آید، هشدار ده (و بترسان).');
INSERT INTO "hussein_taji" VALUES(71,2,'71:2','(نوح) گفت: «ای قوم! همانا من برای شما بیم‌دهندۀ آشکاری هستم.');
INSERT INTO "hussein_taji" VALUES(71,3,'71:3','که الله را پرستش کنید و از او بترسید، و مرا اطاعت کنید.');
INSERT INTO "hussein_taji" VALUES(71,4,'71:4','(پس اگر چنین کردید، الله) گناهان شما را می‌آمرزد، و شما را تا زمان معینی مهلت می‌دهد، زیرا هنگامی‌که اجل الهی فرا رسد تأخیر نخواهد داشت، اگر می‌دانید».');
INSERT INTO "hussein_taji" VALUES(71,5,'71:5','و نوح (پس از نهصد و پنجاه سال نصیحت‌گری و دعوت) گفت: پروردگارا! به راستی که من قوم خود را شب و روز (به ایمان) دعوت کردم.');
INSERT INTO "hussein_taji" VALUES(71,6,'71:6','اما دعوت من (در حق آنان چیزی) جز گریز نیفزود.');
INSERT INTO "hussein_taji" VALUES(71,7,'71:7','و من هرگاه آن‌ها را دعوت کردم تا تو آنان را بیامرزی انگشتان‌‌شان را در گوش‌هایشان فرو کردند و لباس‌هایشان را (به خود) پیچیدند، و (در مخالفت) اصرار (و پا‌فشاری) کردند و به شدت گردن کشی و تکبر نمودند.');
INSERT INTO "hussein_taji" VALUES(71,8,'71:8','سپس من آن‌ها را با صدای بلند (و آشکار) دعوت کردم.');
INSERT INTO "hussein_taji" VALUES(71,9,'71:9','آنگاه به آشکار برایشان گفتم، و در نهان برای (هرکدام از) آنان بیان کردم.');
INSERT INTO "hussein_taji" VALUES(71,10,'71:10','پس (به آن‌ها) گفتم: «از پروردگار خویش آمرزش بخواهید، بی‌گمان او بسیار آمرزنده است.');
INSERT INTO "hussein_taji" VALUES(71,11,'71:11','تا از آسمان (باران) پی در پی بر شما بفرستد.');
INSERT INTO "hussein_taji" VALUES(71,12,'71:12','و شما را با اموال و فرزندان بسیار مدد کند، به شما باغ‌های (سرسبز) بدهد، و برای شما نهر‌های (جاری) قرار دهد.');
INSERT INTO "hussein_taji" VALUES(71,13,'71:13','شما را چه شده است که برای الله، عظمت (و شکوه) قایل نیستید؟!.');
INSERT INTO "hussein_taji" VALUES(71,14,'71:14','در حالی‌که شما را در مراحل گوناگون آفرید. (از نطفه سپس خون لخته شده، آنگاه تکه‌ای گوشت، تا این‌که انسان کامل شد).');
INSERT INTO "hussein_taji" VALUES(71,15,'71:15','آیا نمی‌بینید چگونه الله هفت آسمان را بر فراز یکدیگر آفرید؟');
INSERT INTO "hussein_taji" VALUES(71,16,'71:16','و ماه را در میان آسمان‌ها مایۀ روشنایی قرار داد، و خورشید را چراغ (فروزانی) گردانید.');
INSERT INTO "hussein_taji" VALUES(71,17,'71:17','و الله شما را همچون گیاه و نباتی از زمین رویانید.');
INSERT INTO "hussein_taji" VALUES(71,18,'71:18','سپس شما را به آن (زمین) باز می‌گرداند، و بار دیگر شما را بیرون می‌آورد.');
INSERT INTO "hussein_taji" VALUES(71,19,'71:19','و الله زمین را برای شما گسترده ساخت.');
INSERT INTO "hussein_taji" VALUES(71,20,'71:20','تا از راه‌های پهناور آن (به سوی مقصد خود) رفت و آمد کنید».');
INSERT INTO "hussein_taji" VALUES(71,21,'71:21','نوح گفت: «پروردگارا! بی‌گمان آن‌ها مرا نافرمانی کردند، و از کسی پیروی کردند که مال و فرزندش چیزی جز زیانکاری بر او نیفزوده است.');
INSERT INTO "hussein_taji" VALUES(71,22,'71:22','و (آن پیشوایان گمراه) نیرنگ (و مکر) بزرگی به کار بردند.');
INSERT INTO "hussein_taji" VALUES(71,23,'71:23','و گفتند: معبودان خود را رها نکنید. و (بخصوص) «ود» و «سواع» و «یغوث» و «یعوق» و «نسر» را رها نکنید.');
INSERT INTO "hussein_taji" VALUES(71,24,'71:24','و به راستی آن‌ها بسیاری را گمراه کردند، و (تو ای پروردگار!) ستمکاران را جز گمراهی میفزا».');
INSERT INTO "hussein_taji" VALUES(71,25,'71:25','(سرانجام) به کیفر گناهان‌شان (همگی) غرق شدند، پس در آتش (دوزخ) درآورده شدند، و در برابر (عذاب) الهی یاور (و مددکاری) برای خود نیافتند.');
INSERT INTO "hussein_taji" VALUES(71,26,'71:26','و نوح گفت: «پروردگارا! هیچ یک از کافران (و دیار‌‌شان) را بر روی زمین باقی نگذار.');
INSERT INTO "hussein_taji" VALUES(71,27,'71:27','چرا که اگر آن‌ها را باقی گذاری بندگانت را گمراه می‌کنند، و جز (نسلی) بدکار و کافر به وجود نمی‌آورند.');
INSERT INTO "hussein_taji" VALUES(71,28,'71:28','پروردگارا! مرا و پدر و مادرم را و هرآن کس که با ایمان وارد خانۀ من شود، و (تمام) مردان مؤمن و زنان مؤمن را بیامرز، و ستمکاران را جز هلاکت (و نابودی) میفزا».');
INSERT INTO "hussein_taji" VALUES(72,1,'72:1','(ای پیامبر!) بگو: «به من وحی شده است که همانا گروهی از جنیان (به این قرآن) گوش فرا داده‌اند، پس گفتند: بی‌گمان ما قرآن عجیبی شنیدیم.');
INSERT INTO "hussein_taji" VALUES(72,2,'72:2','که به راه راست هدایت می‌کند، پس ما به آن ایمان آوردیم، و هرگز کسی را با پروردگارمان شریک قرار نمی‌دهیم.');
INSERT INTO "hussein_taji" VALUES(72,3,'72:3','و این‌که شأن و عظمت پروردگارمان بس (عظیم و) بلند است، او (هرگز برای خود) همسر و فرزندی انتخاب نکرده است.');
INSERT INTO "hussein_taji" VALUES(72,4,'72:4','و این‌که سفیه (و نادان) ما (شیطان) دربارۀ الله سخنان ناروایی می‌گفت.');
INSERT INTO "hussein_taji" VALUES(72,5,'72:5','و ما (چنین) گمان می‌کردیم که هرگز انس و جن بر الله دروغ نمی‌بندند.');
INSERT INTO "hussein_taji" VALUES(72,6,'72:6','و این‌که مردانی از آدمیان به مردانی از جنیان پناه می‌بردند، پس آن‌ها به گمراهی و سرکشی‌شان افزودند.');
INSERT INTO "hussein_taji" VALUES(72,7,'72:7','و این‌که آن‌ها گمان کردند، همان‌گونه که شما (جنیان) گمان می‌کردید که الله هیچ کس را (دو باره) زنده نمی‌گرداند.');
INSERT INTO "hussein_taji" VALUES(72,8,'72:8','و این‌که ما آسمان را جستجو کردیم، پس آن را پر از محافظان نیرومند و تیرهای شهاب یافتیم.');
INSERT INTO "hussein_taji" VALUES(72,9,'72:9','و این‌که ما پیش از این در جاهایی (از آسمان) به استراق سمع می‌نشستیم، پس اکنون هرکس (بخواهد) استراق سمع کند، شهابی را در کمین خود خواهد یافت.');
INSERT INTO "hussein_taji" VALUES(72,10,'72:10','و این‌که ما نمی‌دانیم آیا برای اهل زمین شرّ اراده شده، یا پروردگارشان خیر و هدایت آن‌ها را خواسته است.');
INSERT INTO "hussein_taji" VALUES(72,11,'72:11','و این‌که در میان ما افرادی صالح و افرادی نادرستند، ما گروه‌هایی متفاوت (و گوناگون) هستیم.');
INSERT INTO "hussein_taji" VALUES(72,12,'72:12','و این‌که ما یقین داریم که هرگز نمی‌توانیم در روی زمین الله را عاجز و ناتوان کنیم، و هرگز با گریز نمی‌توانیم او را ناتوان سازیم. (و راه فراری نداریم).');
INSERT INTO "hussein_taji" VALUES(72,13,'72:13','و این‌که ما هنگامی‌که (سخن) هدایت را شنیدیم به آن ایمان آوردیم، پس هرکس که به پروردگارش ایمان آورد، نه از نقصان می‌ترسد، و نه از ستم (بیم دارد).');
INSERT INTO "hussein_taji" VALUES(72,14,'72:14','و این‌که گروهی از ما مسلمان و گروهی کافر (و ظالم) هستند، پس هرکس که اسلام آورده آنانند که راه رستگاری را جستجو کرده (و برگزیده) اند.');
INSERT INTO "hussein_taji" VALUES(72,15,'72:15','و اما کافران (و ستمکاران) هیزم (آتش) جهنم خواهند بود.');
INSERT INTO "hussein_taji" VALUES(72,16,'72:16','و این‌که اگر آن‌ها بر راه راست استقامت و پایداری کنند، البته با آب فراوان سیراب‌شان می‌کنیم.');
INSERT INTO "hussein_taji" VALUES(72,17,'72:17','تا آن‌ها را با آن (نعمت‌ها) بیازماییم، و هرکس از یاد (و ذکر) پروردگارش روی برتابد، (الله) او را به عذابی سخت گرفتار می‌سازد.');
INSERT INTO "hussein_taji" VALUES(72,18,'72:18','و این‌که مساجد از آنِ الله است، پس کسی را با الله نخوانید.');
INSERT INTO "hussein_taji" VALUES(72,19,'72:19','و این‌که هنگامی‌که بندۀ الله (محمد صلی الله علیه وسلم) قیام کرد تا او را بخواند (و عبادت کند) نزدیک بود که (از ازدحام بر گرد او) بر سر هم فرو ریزند.');
INSERT INTO "hussein_taji" VALUES(72,20,'72:20','(ای پیامبر! به آن‌ها) بگو: «من تنها پروردگارم را می‌خوانم، و کسی را شریک او نمی‌سازم».');
INSERT INTO "hussein_taji" VALUES(72,21,'72:21','بگو: «بی‌گمان من نمی‌توانم به شما زیانی برسانم، و نه خیر و سودی (برسانم)».');
INSERT INTO "hussein_taji" VALUES(72,22,'72:22','(ای پیامبر!) بگو: «هیچکس مرا از (کیفر) الله پناه نخواهد داد، و غیر از او هرگز پشت و پناهی نخواهم یافت.');
INSERT INTO "hussein_taji" VALUES(72,23,'72:23','مگر (تنها وظیفۀ من) رساندن پیامی از جانب الله و (انجام) رسالت‌هایش است، و هرکس نافرمانی الله و پیامبرش کند، پس بی‌گمان آتش جهنم از آنِ اوست جاودانه در آن خواهد ماند».');
INSERT INTO "hussein_taji" VALUES(72,24,'72:24','(کار کفار همچنین ادامه دارد) تا زمانی که آنچه را به آن‌ها وعده داده شده ببینند، آنگاه خواهند دانست چه کسی یاورش ناتوان‌تر و جمیعتش کمتر است.');
INSERT INTO "hussein_taji" VALUES(72,25,'72:25','(ای پیامبر!) بگو: «نمی‌دانم آیا آنچه که به شما وعده داده شده نزدیک است یا این‌که پروردگارم زمانی (دور) برای آن قرار می‌دهد؟!».');
INSERT INTO "hussein_taji" VALUES(72,26,'72:26','(الله) دانای غیب است، و هیچکس را بر (اسرار) غیب خود آگاه نمی‌سازد.');
INSERT INTO "hussein_taji" VALUES(72,27,'72:27','مگر رسولانی که آنان را پسندیده (و برگزیده) است، پس بی‌گمان از پیش روی او و پشت سرش نگهبانی (و مراقبینی از فرشتگان) می‌گمارد.');
INSERT INTO "hussein_taji" VALUES(72,28,'72:28','تا بداند که رسالت‌های پروردگار‌شان را ابلاغ کرده‌اند، و (الله) به آنچه نزد آن‌هاست احاطت دارد، و عدد هرچیز را شمارش کرده است.');
INSERT INTO "hussein_taji" VALUES(73,1,'73:1','ای جامه برخود پیچیده.');
INSERT INTO "hussein_taji" VALUES(73,2,'73:2','شب را جز اندکی بپا خیز (و عبادت کن).');
INSERT INTO "hussein_taji" VALUES(73,3,'73:3','نیمه‌ای از آن را، یا اندکی از آن کم کن.');
INSERT INTO "hussein_taji" VALUES(73,4,'73:4','یا اندکی بر (نصف) آن بیفزا، و قرآن را با ترتیل و تأمل بخوان.');
INSERT INTO "hussein_taji" VALUES(73,5,'73:5','یقیناً ما به زودی سخنی سنگین بر تو نازل خواهیم کرد.');
INSERT INTO "hussein_taji" VALUES(73,6,'73:6','بی‌گمان شب زنده‌داری (بر نفس) دشوار است و برای گفتار (و نیایش) مناسب و استوارتر است.');
INSERT INTO "hussein_taji" VALUES(73,7,'73:7','یقیناً تو در روز مشغلۀ بسیار داری.');
INSERT INTO "hussein_taji" VALUES(73,8,'73:8','و نام پروردگارت را یاد کن، و تنها به او دل ببند.');
INSERT INTO "hussein_taji" VALUES(73,9,'73:9','(همان) پروردگار مشرق و مغرب که معبودی (بحق) جز او نیست پس او را کارساز (و نگاهبان خود) برگزین.');
INSERT INTO "hussein_taji" VALUES(73,10,'73:10','و بر آنچه (مشرکان) می‌گویند، شکیبا باش، و به شیوۀ نیکو (و شایسته) از آن‌ها دوری کن.');
INSERT INTO "hussein_taji" VALUES(73,11,'73:11','و مرا با این تکذیب‌کنندگان مرفه و منعّم وا گذار، و آن‌ها را کمی مهلت ده.');
INSERT INTO "hussein_taji" VALUES(73,12,'73:12','بی‌گمان نزد ما قید (و بندهای) گران و (آتش) دوزخ.');
INSERT INTO "hussein_taji" VALUES(73,13,'73:13','و غذایی گلوگیر، و عذابی دردناک است.');
INSERT INTO "hussein_taji" VALUES(73,14,'73:14','روزی‌که زمین و کوه‌ها به لرزه درآید، و کوه‌ها (متلاشی می‌شود و) همچون توده‌ای شن نرم (روان) می‌گردد.');
INSERT INTO "hussein_taji" VALUES(73,15,'73:15','یقیناً ما پیامبری به سوی شما فرستادیم که بر شما شاهد (و گواه) است، همان‌گونه که به سوی فرعون پیامبری فرستادیم.');
INSERT INTO "hussein_taji" VALUES(73,16,'73:16','آنگاه فرعون آن پیامبر را نافرمانی (و مخالفت) کرد، پس ما او را به سختی مجازات کردیم.');
INSERT INTO "hussein_taji" VALUES(73,17,'73:17','پس اگر کافر شوید چگونه خود را (از عذاب) در امان می‌دارید، روزی‌که کودکان را پیر می‌کند.');
INSERT INTO "hussein_taji" VALUES(73,18,'73:18','(در آن روز) آسمان شکافته می‌شود، وعدۀ او شدنی است.');
INSERT INTO "hussein_taji" VALUES(73,19,'73:19','بی‌گمان این (آیات) پند (و هشداری) است، پس هرکس که بخواهد راهی به سوی پروردگارش برگزیند.');
INSERT INTO "hussein_taji" VALUES(73,20,'73:20','(ای پیامبر!) یقیناً پروردگارت می‌داند که تو و گروهی از آن‌ها که با تو هستند نزدیک دو سوم شب و (یا) نصف (یا) ثلث آن را بپا می‌خیزید، و الله شب و روز را اندازه‌گیری می‌کند، می‌داند که شما نمی‌توانید حساب آن را (دقیق) محاسبه کنید، لذا شما را بخشید، پس اکنون آنچه (برای شما) میسر باشد از قرآن بخوانید، (الله) می‌داند که برخی از شما بیمار خواهند شد، و گروهی (دیگر) برای بدست‌آوردن فضل و نعمت الله در زمین سفر می‌کنند، و گروهی (نیز) در راه الله جهاد می‌کنند، پس آنچه میسر باشد از آن بخوانید، و نماز را برپا دارید، و زکات بدهید، و به الله قرض الحسنه (وام نیکو) دهید، و (بدانید که) آنچه از کارهای خیر پیشاپیش برای خودتان می‌فرستید نزد الله به بهترین وجه و بزرگ‌ترین پاداش خواهید یافت، و از الله آمرزش بخواهید که همانا الله آمرزندۀ مهربان است.');
INSERT INTO "hussein_taji" VALUES(74,1,'74:1','ای جامه بر سر کشیده!');
INSERT INTO "hussein_taji" VALUES(74,2,'74:2','برخیز و بیم ده.');
INSERT INTO "hussein_taji" VALUES(74,3,'74:3','و پروردگارت را بزرگ شمار.');
INSERT INTO "hussein_taji" VALUES(74,4,'74:4','و لباس‌هایت را پاکیزه دار.');
INSERT INTO "hussein_taji" VALUES(74,5,'74:5','و از پلیدی دوری کن.');
INSERT INTO "hussein_taji" VALUES(74,6,'74:6','و نباید برای افزون‌خواهی چیزی را بدهی.');
INSERT INTO "hussein_taji" VALUES(74,7,'74:7','و برای (خوشنودی) پروردگارت شکیبا باش.');
INSERT INTO "hussein_taji" VALUES(74,8,'74:8','پس هنگامی‌که در «صور» دمیده شود.');
INSERT INTO "hussein_taji" VALUES(74,9,'74:9','آن روز، روز سخت (و دشواری) است.');
INSERT INTO "hussein_taji" VALUES(74,10,'74:10','بر کافران آسان نیست.');
INSERT INTO "hussein_taji" VALUES(74,11,'74:11','(ای پیامبر!) مرا با کسی‌که او را تنها آفریده‌ام واگذار.');
INSERT INTO "hussein_taji" VALUES(74,12,'74:12','و (همان که) مال فراوانی برایش قرار دادم.');
INSERT INTO "hussein_taji" VALUES(74,13,'74:13','و فرزندانی که همیشه (در خدمت او و) با او هستند.');
INSERT INTO "hussein_taji" VALUES(74,14,'74:14','و همه وسایل (و امکانات) زندگی را در اختیارش قرار دادم.');
INSERT INTO "hussein_taji" VALUES(74,15,'74:15','باز (هم) طمع دارد که (برآن) بیفزایم.');
INSERT INTO "hussein_taji" VALUES(74,16,'74:16','هرگز، (چنین نخواهد شد) بی‌گمان او نسبت به آیات ما دشمنی (و عناد) دارد.');
INSERT INTO "hussein_taji" VALUES(74,17,'74:17','و به زودی او را به مشقت و سختی می‌اندازم.');
INSERT INTO "hussein_taji" VALUES(74,18,'74:18','زیرا که او (برای مبارزه با قرآن) اندیشید، و اندازه (و محاسبه) کرد.');
INSERT INTO "hussein_taji" VALUES(74,19,'74:19','پس مرگ بر او باد! چگونه اندازه (و محاسبه) کرد؟!');
INSERT INTO "hussein_taji" VALUES(74,20,'74:20','باز (هم)، مرگ بر او باد! چگونه اندازه (و محاسبه) کرد؟!');
INSERT INTO "hussein_taji" VALUES(74,21,'74:21','سپس نگاهی افکند.');
INSERT INTO "hussein_taji" VALUES(74,22,'74:22','آنگاه چهره درهم کشید و روی ترش کرد.');
INSERT INTO "hussein_taji" VALUES(74,23,'74:23','سپس (به حق) پشت نمود و تکبر ورزید.');
INSERT INTO "hussein_taji" VALUES(74,24,'74:24','آنگاه گفت: «این (قرآن) چیزی جز جادویی که (از دیگران) آموخته شده، نیست.');
INSERT INTO "hussein_taji" VALUES(74,25,'74:25','و این (چیزی) جز گفتار بشر نیست».');
INSERT INTO "hussein_taji" VALUES(74,26,'74:26','به زودی او را به سقر (جهنم) درخواهم آورد.');
INSERT INTO "hussein_taji" VALUES(74,27,'74:27','و تو چه دانی «سقر» چیست؟!');
INSERT INTO "hussein_taji" VALUES(74,28,'74:28','(آتش سوزانی که) نه (چیزی را) باقی می‌گذارد و نه (چیزی را) رها می‌کند.');
INSERT INTO "hussein_taji" VALUES(74,29,'74:29','پوست را (دگرگون کرده و) می‌سوزاند.');
INSERT INTO "hussein_taji" VALUES(74,30,'74:30','برآن (آتش) نوزده (فرشته) گمارده شده است.');
INSERT INTO "hussein_taji" VALUES(74,31,'74:31','و ما مأموران دوزخ را جز فرشتگان قرار ندادیم، و شمار آن‌ها را جز برای آزمایش کافران معین نکردیم، تا اهل کتاب یقین کنند، و کسانی‌که ایمان آورده‌اند بر ایمان خود بیفزایند، و اهل کتاب و مؤمنان، گرفتار شک و تردید نشوند، و تا کسانی‌که در دل‌هایشان مرض است و (نیز) کافران بگویند: «الله از این مثل (و توصیف) چه خواسته است؟» بدین گونه الله هرکس را بخواهد گمراه می‌سازد، و هرکس را بخواهد هدایت می‌کند، و (شمار) لشکریان پروردگارت را جز او (کسی) نمی‌داند، و این جز پند و اندرزی برای انسان‌ها نیست.');
INSERT INTO "hussein_taji" VALUES(74,32,'74:32','آری، (هرگز این‌چنین نیست که آن‌ها تصور می‌کنند) سوگند به ماه.');
INSERT INTO "hussein_taji" VALUES(74,33,'74:33','و به شب هنگامی‌که پشت کند.');
INSERT INTO "hussein_taji" VALUES(74,34,'74:34','و به صبح هنگامی‌که روشن شود.');
INSERT INTO "hussein_taji" VALUES(74,35,'74:35','که آن (آتش دوزخ) یکی از (چیزهای) بزرگ (و هولناک) است.');
INSERT INTO "hussein_taji" VALUES(74,36,'74:36','(ترساننده و) هشداردهنده برای انسان‌هاست.');
INSERT INTO "hussein_taji" VALUES(74,37,'74:37','برای کسی از شما که بخواهد (در راه راست) پیش افتد، یا (از آن) عقب بماند.');
INSERT INTO "hussein_taji" VALUES(74,38,'74:38','هرکس در گرو اعمال خویش است.');
INSERT INTO "hussein_taji" VALUES(74,39,'74:39','مگر «اصحاب یمین».');
INSERT INTO "hussein_taji" VALUES(74,40,'74:40','که در باغ‌های (بهشت) هستند، و می‌پرسند');
INSERT INTO "hussein_taji" VALUES(74,41,'74:41','از گناهکاران.');
INSERT INTO "hussein_taji" VALUES(74,42,'74:42','چه چیز شما را به «سقر» (دوزخ) درآورد؟');
INSERT INTO "hussein_taji" VALUES(74,43,'74:43','گویند: «ما از نمازگزاران نبودیم.');
INSERT INTO "hussein_taji" VALUES(74,44,'74:44','و بی‌نوا(یان) را طعام نمی‌دادیم.');
INSERT INTO "hussein_taji" VALUES(74,45,'74:45','و پیوسته همراه یاوه‌گویان (و اهل باطل) هم‌صدا می‌شدیم.');
INSERT INTO "hussein_taji" VALUES(74,46,'74:46','و همواره روز جزا را تکذیب می‌کردیم.');
INSERT INTO "hussein_taji" VALUES(74,47,'74:47','تا زمانی که مرگ به سراغمان آمد.');
INSERT INTO "hussein_taji" VALUES(74,48,'74:48','پس شفاعت شفاعت‌کنندگان به آن‌ها سودی نمی‌بخشد.');
INSERT INTO "hussein_taji" VALUES(74,49,'74:49','پس آن‌ها را چه شده است که از پند (و تذکر) روی گردانند؟!');
INSERT INTO "hussein_taji" VALUES(74,50,'74:50','گویی که آن‌ها (گور) خران رم کرده‌اند.');
INSERT INTO "hussein_taji" VALUES(74,51,'74:51','که از شیر گریخته‌اند.');
INSERT INTO "hussein_taji" VALUES(74,52,'74:52','بلکه هرکدام از آن‌ها (توقع دارد و) می‌خواهد که (به او) نامۀ گشاده (و جداگانه از سوی الله) داده شود.');
INSERT INTO "hussein_taji" VALUES(74,53,'74:53','هرگز چنین نیست (که آنان می‌پندارند) بلکه آن‌ها از (عذاب) آخرت نمی‌ترسند.');
INSERT INTO "hussein_taji" VALUES(74,54,'74:54','نه، حقاً که این (قرآن) پند (و یادآوری) است.');
INSERT INTO "hussein_taji" VALUES(74,55,'74:55','پس هرکس که بخواهد آن را یاد کند (پند گیرد).');
INSERT INTO "hussein_taji" VALUES(74,56,'74:56','و یاد نمی‌کنند (و پند نمی‌گیرند) مگر این‌که الله بخواهد، او اهل تقوا و اهل آمرزش است.');
INSERT INTO "hussein_taji" VALUES(75,1,'75:1','به روز قیامت سوگند می‌خورم.');
INSERT INTO "hussein_taji" VALUES(75,2,'75:2','و به نفس ملامت‌گر سوگند می‌خورم.');
INSERT INTO "hussein_taji" VALUES(75,3,'75:3','آیا انسان می‌پندارد که (پس از مرگ) هرگز استخوان‌هایش را جمع نخواهیم کرد؟!');
INSERT INTO "hussein_taji" VALUES(75,4,'75:4','آری، ما قادریم که (حتی خطوط سر) انگشتانش را یکسان و مرتب کنیم.');
INSERT INTO "hussein_taji" VALUES(75,5,'75:5','بلکه انسان می‌خواهد که (آزادانه و بدون ترس از آخرت) در گناه و بدکاری مداومت داشته باشد.');
INSERT INTO "hussein_taji" VALUES(75,6,'75:6','(از این رو) می‌پرسد: «روز قیامت کی خواهد بود؟!».');
INSERT INTO "hussein_taji" VALUES(75,7,'75:7','پس هنگامی‌که چشم خیره شود.');
INSERT INTO "hussein_taji" VALUES(75,8,'75:8','و ماه تیره (و بی‌نور) گردد.');
INSERT INTO "hussein_taji" VALUES(75,9,'75:9','و خورشید و ماه یکجا جمع کرده شود.');
INSERT INTO "hussein_taji" VALUES(75,10,'75:10','آن روز انسان می‌گوید: «راه گریز کجاست؟»');
INSERT INTO "hussein_taji" VALUES(75,11,'75:11','هرگز، هیچ گریزگاهی وجود ندارد.');
INSERT INTO "hussein_taji" VALUES(75,12,'75:12','آن روز قرارگاه (نهایی) به سوی پروردگار توست.');
INSERT INTO "hussein_taji" VALUES(75,13,'75:13','در آن روز انسان را از آنچه پیش یا پس فرستاده است خبر داده می‌شود.');
INSERT INTO "hussein_taji" VALUES(75,14,'75:14','بلکه انسان به خوبی بر خویشتن آگاه است.');
INSERT INTO "hussein_taji" VALUES(75,15,'75:15','اگر چه (در دفاع از خود) عذرهایش را در میان آورد.');
INSERT INTO "hussein_taji" VALUES(75,16,'75:16','(ای پیامبر! هنگام نزول قرآن) زبانت را برای (تکرار و خواندن) آن شتاب‌زده حرکت مده.');
INSERT INTO "hussein_taji" VALUES(75,17,'75:17','مسلماً جمع‌آوری و خواندن آن بر (عهدۀ) ماست.');
INSERT INTO "hussein_taji" VALUES(75,18,'75:18','پس هرگاه (توسط جبرئیل) آن را (بر تو) خواندیم، خواندن او را پیروی کن.');
INSERT INTO "hussein_taji" VALUES(75,19,'75:19','سپس بیان‌کردنش بر (عهدۀ) ماست.');
INSERT INTO "hussein_taji" VALUES(75,20,'75:20','هرگز چنین نیست (که شما مشرکان می‌پندارید) بلکه شما دنیا(ی زودگذر) را دوست دارید.');
INSERT INTO "hussein_taji" VALUES(75,21,'75:21','و آخرت را رها می‌کنید.');
INSERT INTO "hussein_taji" VALUES(75,22,'75:22','آن روز چهره‌هایی تازه (و شاداب) است.');
INSERT INTO "hussein_taji" VALUES(75,23,'75:23','به سوی پروردگارش می‌نگرد.');
INSERT INTO "hussein_taji" VALUES(75,24,'75:24','و در آن روز چهره‌هایی عبوس (و درهم کشیده) است.');
INSERT INTO "hussein_taji" VALUES(75,25,'75:25','یقین دارد که آسیبی کمر‌‌شکن به او می‌رسد.');
INSERT INTO "hussein_taji" VALUES(75,26,'75:26','آری، چون (جان) به گلوگاهش برسد.');
INSERT INTO "hussein_taji" VALUES(75,27,'75:27','و گفته شود: چه کسی است که افسون بخواند؟ (و او را نجات دهد).');
INSERT INTO "hussein_taji" VALUES(75,28,'75:28','و یقین کند که زمان جدایی (از دنیا) است.');
INSERT INTO "hussein_taji" VALUES(75,29,'75:29','و ساق‌ها(ی پایش) به هم بپیچد.');
INSERT INTO "hussein_taji" VALUES(75,30,'75:30','در آن روز راندن (و مسیر همه) به سوی پروردگارت خواهد بود.');
INSERT INTO "hussein_taji" VALUES(75,31,'75:31','پس (او) نه تصدیق کرده است و نه نماز گزارده است.');
INSERT INTO "hussein_taji" VALUES(75,32,'75:32','بلکه (قرآن را) تکذیب کرد و (از ایمان) روی گرداند.');
INSERT INTO "hussein_taji" VALUES(75,33,'75:33','سپس خرامان به سوی خانواده‌اش بازگشت.');
INSERT INTO "hussein_taji" VALUES(75,34,'75:34','وای برتو! پس وای برتو!');
INSERT INTO "hussein_taji" VALUES(75,35,'75:35','بازهم وای برتو! پس وای برتو!');
INSERT INTO "hussein_taji" VALUES(75,36,'75:36','آیا انسان گمان می‌کند که (بی‌هدف و بدون حساب و جزا) به خود رها می‌شود؟');
INSERT INTO "hussein_taji" VALUES(75,37,'75:37','آیا (او) نطفه‌ای از منی که (در رحم) ریخته می‌شود نبود؟!');
INSERT INTO "hussein_taji" VALUES(75,38,'75:38','آنگاه به صورت خون بسته درآمد، پس (الله او را) آفرید، و درست و استوار ساخت.');
INSERT INTO "hussein_taji" VALUES(75,39,'75:39','پس از او دو زوج نر و ماده پدید آورد.');
INSERT INTO "hussein_taji" VALUES(75,40,'75:40','آیا (چنین آفریدگاری) قادر نیست که مردگان را (دوباره) زنده کند؟!');
INSERT INTO "hussein_taji" VALUES(76,1,'76:1','یقیناً زمانی طولانی بر انسان گذشت که چیزی (مهم و) قابل ذکر نبود.');
INSERT INTO "hussein_taji" VALUES(76,2,'76:2','به راستی ما انسان را از نطفۀ مختلطی آفریدیم، او را می‌آزماییم، پس او را شنوای بینا قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(76,3,'76:3','همانا ما راه را به او نشان دادیم خواه سپاس‌گزار باشد یا ناسپاس.');
INSERT INTO "hussein_taji" VALUES(76,4,'76:4','به راستی ما برای کافران زنجیرها و غل‌ها و آتش سوزان مهیا کرده‌ایم.');
INSERT INTO "hussein_taji" VALUES(76,5,'76:5','بی‌گمان نیکوکاران (در بهشت) از جامی می‌نوشند که آمیزه‌اش کافور است.');
INSERT INTO "hussein_taji" VALUES(76,6,'76:6','(از) چشمه‌ای که بندگان (خاص) الله از آن می‌نوشند، (هر وقت و) هر جا بخواهند آن را جاری می‌سازند.');
INSERT INTO "hussein_taji" VALUES(76,7,'76:7','(بندگان نیکوکار) به نذر وفا می‌کنند و از روزی می‌ترسند که (عذاب و) شرّ آن فراگیر است.');
INSERT INTO "hussein_taji" VALUES(76,8,'76:8','و غذا را با این‌که (نیاز و) دوست دارند به «مسکین» و «یتیم» و «اسیر» می‌بخشند.');
INSERT INTO "hussein_taji" VALUES(76,9,'76:9','(و می‌گویند:) «ما فقط به خاطر الله به شما غذا می‌دهیم، نه از شما پاداشی می‌خواهیم و نه سپاسی.');
INSERT INTO "hussein_taji" VALUES(76,10,'76:10','همانا ما از پروردگارمان می‌ترسیم، روزی‌که عبوس و سخت و دشوار است».');
INSERT INTO "hussein_taji" VALUES(76,11,'76:11','پس الله آنان را از (سختی و) شر آن روز نگه داشت و آن‌ها را شادمانی و سرور بخشید.');
INSERT INTO "hussein_taji" VALUES(76,12,'76:12','و به (پاداش) صبری که کردند، بهشت و (لباس‌های) حریر (بهشتی) را به آنان پاداش داد.');
INSERT INTO "hussein_taji" VALUES(76,13,'76:13','در آنجا بر تخت‌ها (ی زیبا) تکیه کرده‌اند، نه آفتابی در آنجا می‌بینند و نه سرمایی.');
INSERT INTO "hussein_taji" VALUES(76,14,'76:14','و سایه‌هایش بر آن‌ها فرو افتاده، و میوه‌هایش (برای چیدن) در دسترس (و به فرمان) است.');
INSERT INTO "hussein_taji" VALUES(76,15,'76:15','و در گرداگرد آن‌ها ظرف‌های سیمین (غذا‌ها) و کاسه‌های بلورین (نوشیدنی‌ها) گردانده می‌شود.');
INSERT INTO "hussein_taji" VALUES(76,16,'76:16','(کاسه‌های) بلورین از نقره که آن‌ها را به‌اندازۀ مناسب (پر و) آماده کرده‌اند.');
INSERT INTO "hussein_taji" VALUES(76,17,'76:17','و در آنجا از جام‌هایی سیراب می‌شوند که آمیزه‌اش زنجبیل است.');
INSERT INTO "hussein_taji" VALUES(76,18,'76:18','(از) چشمه‌ای، در آنجا که سلسبیل نامیده می‌شود.');
INSERT INTO "hussein_taji" VALUES(76,19,'76:19','و همواره نوجوانانی جاودانه بر گردشان می‌چرخند که هرگاه آن‌ها را ببینی، گمان می‌کنی که مروارید پراکنده هستند.');
INSERT INTO "hussein_taji" VALUES(76,20,'76:20','و چون (به هر سمتی) بنگری، آنجا نعمت بسیار و فرمانروایی عظیمی را می‌بینی.');
INSERT INTO "hussein_taji" VALUES(76,21,'76:21','بر آنان (بهشتیان) لباس‌هایی سبز رنگ از دیبای نازک و دیبای ضخیم است، و با دستبند‌هایی از نقره آراسته شده‌اند، و پروردگارشان شراب پاک به آن‌ها می‌نوشاند.');
INSERT INTO "hussein_taji" VALUES(76,22,'76:22','(و به آن‌ها گفته می‌شود:) «این (نعمت‌ها) پاداش شماست، و سعی و کوشش شما قدردانی شده است».');
INSERT INTO "hussein_taji" VALUES(76,23,'76:23','(ای پیامبر!) یقیناً ما قرآن را بر تو تدریجاً نازل کردیم.');
INSERT INTO "hussein_taji" VALUES(76,24,'76:24','پس برای حکم پروردگارت شکیبا باش، و از هیچ گنهکار یا ناسپاسی از آنان فرمان نبر.');
INSERT INTO "hussein_taji" VALUES(76,25,'76:25','و صبح و شام نام پروردگارت را یاد کن.');
INSERT INTO "hussein_taji" VALUES(76,26,'76:26','و (نیز) بخشی از شب برایش سجده کن (و نماز گزار) و بخش درازی از شب او را تسبیح گوی.');
INSERT INTO "hussein_taji" VALUES(76,27,'76:27','بی‌گمان اینان (کافران) دنیای زودگذر را دوست دارند، و روز سختی (را که در پیش دارند) پشت سر خود رها می‌کنند.');
INSERT INTO "hussein_taji" VALUES(76,28,'76:28','ما آن‌ها را آفریدیم و پیوند (مفاصل) شان را محکم کردیم، و هر زمان بخواهیم جای آنان را به (گروه دیگری) مانندشان می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(76,29,'76:29','بی‌گمان این یادآوری (و پند) است، پس هرکس که بخواهد راهی به سوی پروردگارش برمی‌گزیند.');
INSERT INTO "hussein_taji" VALUES(76,30,'76:30','و شما چیزی را نمی‌خواهید مگر این‌که الله بخواهد، بی‌گمان الله دانای حکیم است.');
INSERT INTO "hussein_taji" VALUES(76,31,'76:31','هرکس را که بخواهد در رحمت خود وارد می‌کند، و برای ستمکاران عذاب دردناکی آماده کرده است.');
INSERT INTO "hussein_taji" VALUES(77,1,'77:1','سوگند به فرشتگانی که پی در پی فرستاده شدند.');
INSERT INTO "hussein_taji" VALUES(77,2,'77:2','پس سوگند به فرشتگانی که همچون تندباد می‌روند.');
INSERT INTO "hussein_taji" VALUES(77,3,'77:3','و سوگند به فرشتگانی که (ابرها را) پراکنده می‌کنند.');
INSERT INTO "hussein_taji" VALUES(77,4,'77:4','پس سوگند به فرشتگانی که جداکننده (حق از باطل) اند.');
INSERT INTO "hussein_taji" VALUES(77,5,'77:5','و سوگند به فرشتگانی که وحی (الهی) را (به پیامبران) القا می‌کنند.');
INSERT INTO "hussein_taji" VALUES(77,6,'77:6','برای اتمام حجت یا برای بیم و هشدار.');
INSERT INTO "hussein_taji" VALUES(77,7,'77:7','بی‌گمان آنچه به شما وعده داده می‌شود، واقع خواهد شد.');
INSERT INTO "hussein_taji" VALUES(77,8,'77:8','پس هنگامی‌که ستارگان تیره (و محو) شود.');
INSERT INTO "hussein_taji" VALUES(77,9,'77:9','و هنگامی‌که آسمان شکافته شود.');
INSERT INTO "hussein_taji" VALUES(77,10,'77:10','و هنگامی‌که کوه‌ها (از جا کنده و) پراکنده شود.');
INSERT INTO "hussein_taji" VALUES(77,11,'77:11','و هنگامی‌که پیامبران را (جهت گواهی بر امت‌ها) وقت معین شود.');
INSERT INTO "hussein_taji" VALUES(77,12,'77:12','این (امر) برای چه روزی به تأخیر افتاده است؟!');
INSERT INTO "hussein_taji" VALUES(77,13,'77:13','برای روز جدایی، (و داوری).');
INSERT INTO "hussein_taji" VALUES(77,14,'77:14','و تو چه دانی روز جدایی (و داوری) چیست؟!');
INSERT INTO "hussein_taji" VALUES(77,15,'77:15','در آن روز (قیامت) وای بر تکذیب‌کنندگان!.');
INSERT INTO "hussein_taji" VALUES(77,16,'77:16','آیا ما پیشینیان (مجرم) را نابود نکردیم؟!');
INSERT INTO "hussein_taji" VALUES(77,17,'77:17','سپس دیگران را در پی آن‌ها خواهیم آورد.');
INSERT INTO "hussein_taji" VALUES(77,18,'77:18','این چنین با گنهکاران رفتار می‌کنیم.');
INSERT INTO "hussein_taji" VALUES(77,19,'77:19','در آن روز (قیامت) وای بر تکذیب‌کنندگان!');
INSERT INTO "hussein_taji" VALUES(77,20,'77:20','آیا شما را از آبی پست (و ناچیز) نیافریدیم؟!');
INSERT INTO "hussein_taji" VALUES(77,21,'77:21','پس آن را در (رحم) قرارگاهی محفوظ (و استوار) قرار دادیم');
INSERT INTO "hussein_taji" VALUES(77,22,'77:22','تا زمانی معین؟!');
INSERT INTO "hussein_taji" VALUES(77,23,'77:23','پس ما (بر این کار) توانا بودیم، و چه نیک توانا (و قدرتمند) هستیم.');
INSERT INTO "hussein_taji" VALUES(77,24,'77:24','در آن روز (قیامت) وای بر تکذیب‌کنندگان!');
INSERT INTO "hussein_taji" VALUES(77,25,'77:25','آیا زمین را جایگاه (تجمع مردم) قرار ندادیم.');
INSERT INTO "hussein_taji" VALUES(77,26,'77:26','هم در حال حیات و زندگی‌شان و هم مرگ‌شان؟!');
INSERT INTO "hussein_taji" VALUES(77,27,'77:27','و در آن کوه‌های بسیار بلند (و استوار) قرار دادیم، و آبی شیرین (و گوارا) به شما نوشاندیم.');
INSERT INTO "hussein_taji" VALUES(77,28,'77:28','در آن روز (قیامت) وای بر تکذیب‌کنندگان!');
INSERT INTO "hussein_taji" VALUES(77,29,'77:29','(به آن‌ها گفته می‌شود:) به سوی همان چیزی بروید که پیوسته تکذیبش می‌کردید.');
INSERT INTO "hussein_taji" VALUES(77,30,'77:30','بروید به‌سوی سایۀ (دودهای آتش) سه شاخه!');
INSERT INTO "hussein_taji" VALUES(77,31,'77:31','نه سایه افکن (و خنک) است، و نه از (گرمی) شعله‌های آتش جلوگیری می‌کند!');
INSERT INTO "hussein_taji" VALUES(77,32,'77:32','همانا (جهنم) شراره‌هایی چون کوشک پرتاب می‌کند.');
INSERT INTO "hussein_taji" VALUES(77,33,'77:33','گویی که آن (شراره‌ها) شتران زرد رنگ هستند.');
INSERT INTO "hussein_taji" VALUES(77,34,'77:34','در آن روز (قیامت) وای بر تکذیب‌کنندگان!');
INSERT INTO "hussein_taji" VALUES(77,35,'77:35','این (همان) روزی است که سخن نمی‌گویند.');
INSERT INTO "hussein_taji" VALUES(77,36,'77:36','و به آن‌ها اجازه داده نمی‌شود تا عذر خواهی کنند.');
INSERT INTO "hussein_taji" VALUES(77,37,'77:37','در آن روز (قیامت) وای بر تکذیب‌کنندگان!');
INSERT INTO "hussein_taji" VALUES(77,38,'77:38','این (همان) روز جدایی (و داوری) است که شما و گذشتگان را گرد آورده‌ایم.');
INSERT INTO "hussein_taji" VALUES(77,39,'77:39','پس اگر (حیله و) نیرنگی دارید، آن را در حق من بکار گیرید.');
INSERT INTO "hussein_taji" VALUES(77,40,'77:40','در آن روز (قیامت) وای بر تکذیب‌کنندگان!');
INSERT INTO "hussein_taji" VALUES(77,41,'77:41','به راستی که (در آن روز) پرهیزگاران در سایه‌ها و (کنار) چشمه‌ها قرار دارند.');
INSERT INTO "hussein_taji" VALUES(77,42,'77:42','و میوه‌هایی که میل داشته باشند.');
INSERT INTO "hussein_taji" VALUES(77,43,'77:43','(به آن‌ها گفته می‌شود:) گوارا بخورید و بنوشید به پاداش آنچه می‌کردید.');
INSERT INTO "hussein_taji" VALUES(77,44,'77:44','ما اینگونه نیکوکاران را پاداش می‌دهیم».');
INSERT INTO "hussein_taji" VALUES(77,45,'77:45','در آن روز (قیامت) وای بر تکذیب‌کنندگان!');
INSERT INTO "hussein_taji" VALUES(77,46,'77:46','«(ای کافران! در این دنیا) اندکی بخورید و بهره گیرید، زیرا شما گناهکار هستید»');
INSERT INTO "hussein_taji" VALUES(77,47,'77:47','در آن روز (قیامت) وای بر تکذیب‌کنندگان!');
INSERT INTO "hussein_taji" VALUES(77,48,'77:48','و هنگامی‌که به آن‌ها گفته شود: (نماز بگزارید و) رکوع کنید، (نماز نمی‌گزارند و) رکوع نمی‌کنند.');
INSERT INTO "hussein_taji" VALUES(77,49,'77:49','در آن روز (قیامت) وای بر تکذیب‌کنندگان!');
INSERT INTO "hussein_taji" VALUES(77,50,'77:50','(ای پیامبر! اگر این مردم به این قرآن ایمان نمی‌آورند) پس بعد از آن به کدام سخن ایمان می‌آورند؟!');
INSERT INTO "hussein_taji" VALUES(78,1,'78:1','(کافران) از چه چیز از یکدیگر سؤال می‌کنند؟');
INSERT INTO "hussein_taji" VALUES(78,2,'78:2','از خبری بزرگ.');
INSERT INTO "hussein_taji" VALUES(78,3,'78:3','همان (خبری) که آن‌ها در آن اختلاف دارند.');
INSERT INTO "hussein_taji" VALUES(78,4,'78:4','چنین نیست (که آن‌ها می‌اندیشند)، بزودی خواهند دانست.');
INSERT INTO "hussein_taji" VALUES(78,5,'78:5','باز هم چنین نیست، بزودی خواهند دانست.');
INSERT INTO "hussein_taji" VALUES(78,6,'78:6','آیا زمین را بستری (برای آسایش شما) قرار ندادیم؟!');
INSERT INTO "hussein_taji" VALUES(78,7,'78:7','و کوه‌ها را میخ‌های (آن قرار ندادیم؟!).');
INSERT INTO "hussein_taji" VALUES(78,8,'78:8','و شما را جفت (نر و ماده) آفریدیم.');
INSERT INTO "hussein_taji" VALUES(78,9,'78:9','و خواب شما را (مایۀ) آرامش‌تان قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(78,10,'78:10','و شب را پوششی (برای شما) قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(78,11,'78:11','و روز را (وسیله‌ای) برای زندگی و معاش قرار دادیم.');
INSERT INTO "hussein_taji" VALUES(78,12,'78:12','و بر فراز شما هفت (آسمان) محکم بنا کردیم.');
INSERT INTO "hussein_taji" VALUES(78,13,'78:13','و (خورشید را) چراغی درخشان آفریدیم.');
INSERT INTO "hussein_taji" VALUES(78,14,'78:14','و از ابر‌های باران زا آبی فراوان فرو‌‌ فرستادیم');
INSERT INTO "hussein_taji" VALUES(78,15,'78:15','تا بدان دانه و گیاه بسیار برویانیم.');
INSERT INTO "hussein_taji" VALUES(78,16,'78:16','و باغ‌هایی پر درخت (با آن آب پرورش دهیم).');
INSERT INTO "hussein_taji" VALUES(78,17,'78:17','بی‌گمان روز داوری (و جدای) میعاد (همگان) است.');
INSERT INTO "hussein_taji" VALUES(78,18,'78:18','روزی‌که در «صور» دمیده می‌شود، سپس شما گروه گروه (به محشر) می‌آیید.');
INSERT INTO "hussein_taji" VALUES(78,19,'78:19','و آسمان گشوده می‌شود، پس دروازه، دروازه می‌گردد.');
INSERT INTO "hussein_taji" VALUES(78,20,'78:20','و کوه‌ها روان کرده شود، پس (چون) سراب گردند.');
INSERT INTO "hussein_taji" VALUES(78,21,'78:21','و بی‌گمان جهنم کمین‌گاهی است.');
INSERT INTO "hussein_taji" VALUES(78,22,'78:22','بازگشت‌گاه برای سرکشان است.');
INSERT INTO "hussein_taji" VALUES(78,23,'78:23','مدت زمانی دراز در آنجا بمانند.');
INSERT INTO "hussein_taji" VALUES(78,24,'78:24','و در آنجا نه (چیز) خنکی می‌چشند و نه آشامیدنی (گوارایی خواهند داشت).');
INSERT INTO "hussein_taji" VALUES(78,25,'78:25','جز آبی سوزان، و (مایعی که) چرک و خون (است).');
INSERT INTO "hussein_taji" VALUES(78,26,'78:26','(این) کیفری است مناسب و در خور (اعمال‌شان)');
INSERT INTO "hussein_taji" VALUES(78,27,'78:27','زیرا که آن‌ها هیچ امیدی به (روز) حساب نداشتند.');
INSERT INTO "hussein_taji" VALUES(78,28,'78:28','و آیات ما را بشدّت تکذیب کردند.');
INSERT INTO "hussein_taji" VALUES(78,29,'78:29','و ما همه چیز را (در کتاب لوح محفوظ) بشمارش نوشتیم.');
INSERT INTO "hussein_taji" VALUES(78,30,'78:30','پس (کیفر اعمال خود را) بچشید، که چیزی جز عذاب بر شما نمی‌افزاییم!');
INSERT INTO "hussein_taji" VALUES(78,31,'78:31','بی‌گمان برای پرهیزگاران کامیابی است.');
INSERT INTO "hussein_taji" VALUES(78,32,'78:32','باغ‌های (میوه) و تاکستان‌ها.');
INSERT INTO "hussein_taji" VALUES(78,33,'78:33','و حوریانی جوان و هم سن و سال.');
INSERT INTO "hussein_taji" VALUES(78,34,'78:34','و جام‌هایی لبریز و پیاپی (از شراب پاکیزۀ بهشت).');
INSERT INTO "hussein_taji" VALUES(78,35,'78:35','در آنجا نه سخن بیهوده‌ای می‌شنوند و نه دروغی.');
INSERT INTO "hussein_taji" VALUES(78,36,'78:36','(این) پاداش از جانب پروردگار توست و عطایی از روی حساب');
INSERT INTO "hussein_taji" VALUES(78,37,'78:37','(همان) پروردگار آسمان‌ها و زمین، و آنچه در میان آن‌هاست، (همان الله) رحمان هیچ‌کس (درآن روز) یارای سخن گفتن با او ندارد.');
INSERT INTO "hussein_taji" VALUES(78,38,'78:38','روزی‌که روح (= جبرییل) و فرشتگان به صف ایستند، هیچ‌کس سخن نگویید، جز کسی‌که الله رحمان به او اجازه داده باشد، و (او) سخن درست (و صواب) گوید.');
INSERT INTO "hussein_taji" VALUES(78,39,'78:39','آن روز حق است، پس هر کس که بخواهد بازگشت‌گاهی به سوی پروردگارش برگزیند.');
INSERT INTO "hussein_taji" VALUES(78,40,'78:40','به راستی ما شما را از عذابی نزدیک بیم دادیم، روزی‌که انسان آنچه را از قبل با دست‌های خود فرستاده است می‌بیند، و کافر می‌گوید: «ای کاش من خاک بود». (و برای حساب بر انگیخته نمی‌شد).');
INSERT INTO "hussein_taji" VALUES(79,1,'79:1','سوگند به فرشتگانی که جان‌های (کافران) را به شدّت بیرون می‌کشند.');
INSERT INTO "hussein_taji" VALUES(79,2,'79:2','و سوگند به فرشتگانی که جان‌های (مؤمنان) را به نرمی و آسانی می‌گیرند.');
INSERT INTO "hussein_taji" VALUES(79,3,'79:3','و سوگند به فرشتگانی که شناورند.');
INSERT INTO "hussein_taji" VALUES(79,4,'79:4','و سوگند به فرشتگانی که (در اجرای اوامر الهی) بر یگدیگر سبقت می‌گیرند.');
INSERT INTO "hussein_taji" VALUES(79,5,'79:5','و سوگند به فرشتگانی (که به امر الهی) کار‌‌ها را تدبیر می‌کنند.');
INSERT INTO "hussein_taji" VALUES(79,6,'79:6','روزی‌که (سپس از اولین دمیدن در صور) زمین (و کوه‌ها و همه چیز) را بلرزه در آید');
INSERT INTO "hussein_taji" VALUES(79,7,'79:7','در پی آن (دمیدن دوم حشر) بیاید.');
INSERT INTO "hussein_taji" VALUES(79,8,'79:8','دل‌هایی در آن روز سخت مضطرب (و ترسان) است.');
INSERT INTO "hussein_taji" VALUES(79,9,'79:9','و چشم‌های آنان (از ترس و شرمساری) فرو افتاده است.');
INSERT INTO "hussein_taji" VALUES(79,10,'79:10','(کافران در دنیا) می‌گویند: «آیا ما (پس از مرگ دوباره) به حال اول خود باز می‌گردانیده می‌شویم؟!');
INSERT INTO "hussein_taji" VALUES(79,11,'79:11','آیا هنگامی‌که استخوان‌های پوسیده‌ای شدیم (و به خاک تبدیل گشتیم، باز هم زنده می‌شویم؟!)».');
INSERT INTO "hussein_taji" VALUES(79,12,'79:12','گویند: «(اگر چنین وعده‌ای درست باشد) آنگاه آن بازگشتی زیانبار است!».');
INSERT INTO "hussein_taji" VALUES(79,13,'79:13','پس آن (بازگشت) تنها با یک بانگ مهیب است!');
INSERT INTO "hussein_taji" VALUES(79,14,'79:14','پس ناگهان همگی بر عرصۀ زمین (محشر) ظاهر می‌شوند.');
INSERT INTO "hussein_taji" VALUES(79,15,'79:15','(ای پیامبر) آیا داستان موسی به تو رسیده است؟!');
INSERT INTO "hussein_taji" VALUES(79,16,'79:16','آنگاه که پروردگارش او را در وادی مقدس «طوی» ندا داد (و فرمود:).');
INSERT INTO "hussein_taji" VALUES(79,17,'79:17','به سوی فرعون برو، که او طغیان کرده است.');
INSERT INTO "hussein_taji" VALUES(79,18,'79:18','پس به او بگو: «آیا می‌خواهی پاکیزه شوی؟!');
INSERT INTO "hussein_taji" VALUES(79,19,'79:19','و من تو را به سوی پروردگارت هدایت کنم، آنگاه (از او) بترسی، (و فرمانبردار شوی).');
INSERT INTO "hussein_taji" VALUES(79,20,'79:20','پس (موسی) معجزه‌ای بزرگ را به او نشان داد.');
INSERT INTO "hussein_taji" VALUES(79,21,'79:21','اما او تکذیب کرد و عصیان ورزید.');
INSERT INTO "hussein_taji" VALUES(79,22,'79:22','سپس پشت کرد و به کوشش و تلاش (علیه او) پرداخت.');
INSERT INTO "hussein_taji" VALUES(79,23,'79:23','پس (قومش را) جمع کرد و ندا داد.');
INSERT INTO "hussein_taji" VALUES(79,24,'79:24','و آنگاه گفت: «من پروردگار برتر شما هستم».');
INSERT INTO "hussein_taji" VALUES(79,25,'79:25','پس الله او را به عذاب آخرت و دنیا گرفتار کرد.');
INSERT INTO "hussein_taji" VALUES(79,26,'79:26','بی‌گمان در این (واقعه)، عبرتی است برای کسی‌که (از الله) می‌ترسد.');
INSERT INTO "hussein_taji" VALUES(79,27,'79:27','آیا آفرینش شما (بعد از مرگ) سخت‌تر است یا آسمان که (الله) آن را بنا کرد؟');
INSERT INTO "hussein_taji" VALUES(79,28,'79:28','سقف آن را برافراشت، پس به آن شکل و نظم داد.');
INSERT INTO "hussein_taji" VALUES(79,29,'79:29','و شبش را تاریک، و روزش را روشن گردانید.');
INSERT INTO "hussein_taji" VALUES(79,30,'79:30','و زمین را بعد از آن گسترانید.');
INSERT INTO "hussein_taji" VALUES(79,31,'79:31','و از آن آب (چشمه‌ها و چاه‌ها) و چراگاهش را بیرون آورد.');
INSERT INTO "hussein_taji" VALUES(79,32,'79:32','و کوه‌ها را محکم و استوار (بر روی زمین) قرار داد.');
INSERT INTO "hussein_taji" VALUES(79,33,'79:33','(همۀ این‌ها) برای بهره‌گیری شما و چهارپایانتان است.');
INSERT INTO "hussein_taji" VALUES(79,34,'79:34','پس هنگامی‌که (آن) حادثۀ بزرگ (قیامت) فرا رسد،');
INSERT INTO "hussein_taji" VALUES(79,35,'79:35','در آن روز انسان (تمام اعمال و) تلاش‌های خود را به یاد آورد.');
INSERT INTO "hussein_taji" VALUES(79,36,'79:36','و جهنم برای هر بیننده آشکار می‌شود.');
INSERT INTO "hussein_taji" VALUES(79,37,'79:37','پس آن کس که طغیان (و سرکشتی) کرده باشد.');
INSERT INTO "hussein_taji" VALUES(79,38,'79:38','و زندگی دنیا را (بر آخرت) ترجیح داده باشد.');
INSERT INTO "hussein_taji" VALUES(79,39,'79:39','پس بی ترید جهنم جایگاه اوست.');
INSERT INTO "hussein_taji" VALUES(79,40,'79:40','و اما کسی‌که از ایستادن در حضور پروردگارش بیمناک بوده، و نفس را از هوی (و هوس) باز داشته باش.');
INSERT INTO "hussein_taji" VALUES(79,41,'79:41','پس قطعاً بهشت جایگاه اوست.');
INSERT INTO "hussein_taji" VALUES(79,42,'79:42','(ای پیامبر) دربارۀ قیامت از تو می‌پرسند، که در چه زمانی واقع می‌شود؟');
INSERT INTO "hussein_taji" VALUES(79,43,'79:43','تو را با یادآوری این سخن چه کار است؟');
INSERT INTO "hussein_taji" VALUES(79,44,'79:44','(سر انجام و) منت‌های (علم) آن نزد پروردگار تو است.');
INSERT INTO "hussein_taji" VALUES(79,45,'79:45','(ای پیامبر) تو تنها بیم‌دهندۀ کسانی هستی که از آن می‌ترسند.');
INSERT INTO "hussein_taji" VALUES(79,46,'79:46','روزی‌که (کافران قیامت را) ببیند، (چنین احساس می‌کنند که) گویی (در دنیا) جز یک شامگاه یا چاشتگاه درنگ نکرده‌اند.');
INSERT INTO "hussein_taji" VALUES(80,1,'80:1','چهره درهم کشید، و روی بر گردانید،');
INSERT INTO "hussein_taji" VALUES(80,2,'80:2','از این‌که (عبد الله بن ام مکتوم) نابینا به نزدش آمد.');
INSERT INTO "hussein_taji" VALUES(80,3,'80:3','و (ای پیامبر) چه می‌دانی شاید که او پاک می‌شد.');
INSERT INTO "hussein_taji" VALUES(80,4,'80:4','یا پندگیرد، و این پند به او نفع می‌داد.');
INSERT INTO "hussein_taji" VALUES(80,5,'80:5','اما آن کس که مجد نیازی نمود.');
INSERT INTO "hussein_taji" VALUES(80,6,'80:6','پس تو به او روی می‌آوری،');
INSERT INTO "hussein_taji" VALUES(80,7,'80:7','در حالی‌که اگر او خود را (از کفر) پاک نسازد، چیزی بر تو نیست.');
INSERT INTO "hussein_taji" VALUES(80,8,'80:8','اما کسی شتابان به سراغ تو می‌آید.');
INSERT INTO "hussein_taji" VALUES(80,9,'80:9','و او (از الله) می‌ترسد.');
INSERT INTO "hussein_taji" VALUES(80,10,'80:10','پس تو از او غافل می‌شوی (و به او توجه نمی‌کنی؟)');
INSERT INTO "hussein_taji" VALUES(80,11,'80:11','هرگز چنین نیست، بی‌گمان این (سوره) تذکر و یاد آوری است.');
INSERT INTO "hussein_taji" VALUES(80,12,'80:12','پس هرکس بخواهد از آن پند گیرد.');
INSERT INTO "hussein_taji" VALUES(80,13,'80:13','در صحیفه‌‌های پر ارزشی (ثبت) است.');
INSERT INTO "hussein_taji" VALUES(80,14,'80:14','بلند پایه و پاکیزه.');
INSERT INTO "hussein_taji" VALUES(80,15,'80:15','به دست (فرشتگان =) سفیران (وحی است).');
INSERT INTO "hussein_taji" VALUES(80,16,'80:16','بزرگوار و نیکوکار.');
INSERT INTO "hussein_taji" VALUES(80,17,'80:17','مرگ بر (این) انسان، چقدر ناسپاس است؟!');
INSERT INTO "hussein_taji" VALUES(80,18,'80:18','(الله) او را از چه چیز آفریده است؟');
INSERT INTO "hussein_taji" VALUES(80,19,'80:19','او را از نطفۀ (ناچیز) آفریده است، آنگاه او را موزون ساخت.');
INSERT INTO "hussein_taji" VALUES(80,20,'80:20','سپس راه را برای او آسان نمود.');
INSERT INTO "hussein_taji" VALUES(80,21,'80:21','آنگاه (بعد از پایان عمر) او را میراند، پس او را در قبر (پنهان) کرد.');
INSERT INTO "hussein_taji" VALUES(80,22,'80:22','سپس هرگاه که بخواهد او را (زیاده می‌کند و از قبر) بر می‌انگیزد.');
INSERT INTO "hussein_taji" VALUES(80,23,'80:23','هرگز چنین نیست، (که او می‌پندارد) او هنوز آنچه را که (الله) فرمان داده، به‌جای نیاورده است.');
INSERT INTO "hussein_taji" VALUES(80,24,'80:24','پس انسان باید به غذای خود بنگرد.');
INSERT INTO "hussein_taji" VALUES(80,25,'80:25','بی‌گمان ما آب فراوان (از آسمان) فرو ریختیم.');
INSERT INTO "hussein_taji" VALUES(80,26,'80:26','سپس زمین را به نیکی شکافتیم.');
INSERT INTO "hussein_taji" VALUES(80,27,'80:27','آنگاه در آن دانه (های فراوان) رویاندیم.');
INSERT INTO "hussein_taji" VALUES(80,28,'80:28','و انگور و سبزی (بسیار).');
INSERT INTO "hussein_taji" VALUES(80,29,'80:29','و زیتون و نخل.');
INSERT INTO "hussein_taji" VALUES(80,30,'80:30','و باغ‌هایی (انبوه و) پردرخت.');
INSERT INTO "hussein_taji" VALUES(80,31,'80:31','و (انواع) میوه و علوفه (پدید آوردیم).');
INSERT INTO "hussein_taji" VALUES(80,32,'80:32','(همۀ این‌ها) برای بهره‌گیری شما و چهارپایانتان است.');
INSERT INTO "hussein_taji" VALUES(80,33,'80:33','پس هنگامی‌که (آن) صدای مهیب (قیامت) فرا رسد.');
INSERT INTO "hussein_taji" VALUES(80,34,'80:34','روزی‌که انسان از برادرش می‌گریزد.');
INSERT INTO "hussein_taji" VALUES(80,35,'80:35','و از مادرش و از پدرش.');
INSERT INTO "hussein_taji" VALUES(80,36,'80:36','و از زنش و فرزندانش (نیز می‌گریزد).');
INSERT INTO "hussein_taji" VALUES(80,37,'80:37','در آن روز هرکس را کاری است که او را به خود مشغولش دارد.');
INSERT INTO "hussein_taji" VALUES(80,38,'80:38','چهره‌هایی در آن روز گشاده و روشن است.');
INSERT INTO "hussein_taji" VALUES(80,39,'80:39','خندان و شاد است.');
INSERT INTO "hussein_taji" VALUES(80,40,'80:40','و چهره‌هایی در آن روز غبار آلود است.');
INSERT INTO "hussein_taji" VALUES(80,41,'80:41','سیاهی (و تاریکی) آن‌ها را پوشانده است.');
INSERT INTO "hussein_taji" VALUES(80,42,'80:42','اینان همان کافران فاجرند.');
INSERT INTO "hussein_taji" VALUES(81,1,'81:1','هنگامی‌که خورشید درهم پیچیده شود.');
INSERT INTO "hussein_taji" VALUES(81,2,'81:2','و هنگامی‌که ستارگان بی نور شوند.');
INSERT INTO "hussein_taji" VALUES(81,3,'81:3','و هنگامی‌که کوه‌ها به حرکت درآید.');
INSERT INTO "hussein_taji" VALUES(81,4,'81:4','و هنگامی‌که ماده شتران باردار رها شوند.');
INSERT INTO "hussein_taji" VALUES(81,5,'81:5','و هنگامی‌که جانوران وحشی گرد آورده شوند.');
INSERT INTO "hussein_taji" VALUES(81,6,'81:6','و هنگامی‌که دریا‌ها (جوشان و) بر افروخته شود.');
INSERT INTO "hussein_taji" VALUES(81,7,'81:7','و هنگامی‌که روح‌ها (با تن‌ها) قرین شوند.');
INSERT INTO "hussein_taji" VALUES(81,8,'81:8','و هنگامی‌که از دختر زنده به گورشده پرسیده شود:');
INSERT INTO "hussein_taji" VALUES(81,9,'81:9','به کدامین گناه کشته شده است؟!');
INSERT INTO "hussein_taji" VALUES(81,10,'81:10','و هنگامی‌که نامه‌های (اعمال) گشوده شود.');
INSERT INTO "hussein_taji" VALUES(81,11,'81:11','و هنگامی‌که پرده از روی آسمان برگرفته شود.');
INSERT INTO "hussein_taji" VALUES(81,12,'81:12','و هنگامی‌که دوزخ افروخته (و شعله‌‌ور) گردد.');
INSERT INTO "hussein_taji" VALUES(81,13,'81:13','و هنگامی‌که بهشت نزدیک آورده شود.');
INSERT INTO "hussein_taji" VALUES(81,14,'81:14','(در آن هنگام) هرکس خواهد دانست چه چیزی را (برای آخرت خود) حاضر (و آماده) کرده است.');
INSERT INTO "hussein_taji" VALUES(81,15,'81:15','پس سوگند به شارگانی که باز می‌گردند.');
INSERT INTO "hussein_taji" VALUES(81,16,'81:16','(شتابان) حرکت می‌کنند (و از نظر) پنهان می‌شوند.');
INSERT INTO "hussein_taji" VALUES(81,17,'81:17','و سوگند به شب، هنگامی‌که (تاریکی‌اش کاسته شود و) پشت کند.');
INSERT INTO "hussein_taji" VALUES(81,18,'81:18','و سوگند به صبح، هنگامی‌که بدمد.');
INSERT INTO "hussein_taji" VALUES(81,19,'81:19','بی‌گمان این (قرآن) سخن فرستادۀ بزرگوار (= جبرئیل) است.');
INSERT INTO "hussein_taji" VALUES(81,20,'81:20','که (مقتدر و) نیرومند است، و نزد (الله) صاحب عرش، مقام و منزلت والایی دارد.');
INSERT INTO "hussein_taji" VALUES(81,21,'81:21','(در آسمان‌ها) مورد اطاعت (فرشتگان) و امین (وحی) است.');
INSERT INTO "hussein_taji" VALUES(81,22,'81:22','و (ای مردم) همنشین شما (محمد صلی الله علیه وسلم) دیوانه نیست.');
INSERT INTO "hussein_taji" VALUES(81,23,'81:23','و به راستی او (= جبرئیل) را در افق روشن دیده است.');
INSERT INTO "hussein_taji" VALUES(81,24,'81:24','و او بر (آنچه از طریق) غیب (به او وحی شده) بخیل نیست.');
INSERT INTO "hussein_taji" VALUES(81,25,'81:25','و این (قرآن) گفتۀ شیطان رانده شده نیست.');
INSERT INTO "hussein_taji" VALUES(81,26,'81:26','پس به کجا می‌روید؟!');
INSERT INTO "hussein_taji" VALUES(81,27,'81:27','این (قرآن) چیزی جز پند و اندرز برای جهانیان نیست.');
INSERT INTO "hussein_taji" VALUES(81,28,'81:28','برای کسی از شما که بخواهد راه راست در پیش گیرد.');
INSERT INTO "hussein_taji" VALUES(81,29,'81:29','و شما نمی‌خواهید مگر آنکه پروردگار جهانیان بخواهد.');
INSERT INTO "hussein_taji" VALUES(82,1,'82:1','هنگامی‌که آسمان شکافته شود.');
INSERT INTO "hussein_taji" VALUES(82,2,'82:2','و هنگامی‌که ستارگان پراکنده شوند (و فرو ریزد).');
INSERT INTO "hussein_taji" VALUES(82,3,'82:3','و هنگامی‌که دریا‌ها (به هم آمیزند و) روان گردند.');
INSERT INTO "hussein_taji" VALUES(82,4,'82:4','و هنگامی‌که قبرها زیر و رو شود.');
INSERT INTO "hussein_taji" VALUES(82,5,'82:5','(در آن هنگامی) هرکس خواهد دانست آنچه را که پیش فرستاده و آنچه را که باز پس داشته است.');
INSERT INTO "hussein_taji" VALUES(82,6,'82:6','ای انسان! چه چیزی تو را (نسبت) به پروردگار کریمت مغرور ساخته است؟');
INSERT INTO "hussein_taji" VALUES(82,7,'82:7','آن که تو را آفرید، آنگاه شکل و اعضایت را تمام کرد سپس معتدل گرداند.');
INSERT INTO "hussein_taji" VALUES(82,8,'82:8','و به هر شکلی (و صورتی) که خواست، تو را ترکیب نمود.');
INSERT INTO "hussein_taji" VALUES(82,9,'82:9','هرگز چنین نیست (که شما گمان می‌کنید) بلکه شما روز جزا را تکذیب می‌کنید');
INSERT INTO "hussein_taji" VALUES(82,10,'82:10','و بی‌گمان نگهبانانی (از فرشتگان) بر شما گمارده شده‌اند.');
INSERT INTO "hussein_taji" VALUES(82,11,'82:11','که نویسندگانی بزرگوار هستند');
INSERT INTO "hussein_taji" VALUES(82,12,'82:12','آنچه را انجام می‌دهید می‌دانند (و اعمال نیک و بد شما را یادداشت می‌کنند)');
INSERT INTO "hussein_taji" VALUES(82,13,'82:13','مسلماً نیکان در نعمت (های بهشت) هستند.');
INSERT INTO "hussein_taji" VALUES(82,14,'82:14','و یقیناً بدکاران در (آتش) جهنم‌اند.');
INSERT INTO "hussein_taji" VALUES(82,15,'82:15','روز جزا به آن در آیند (و بسوزند).');
INSERT INTO "hussein_taji" VALUES(82,16,'82:16','و هرگز آنان از آنجا غایب نباشند');
INSERT INTO "hussein_taji" VALUES(82,17,'82:17','و تو چه می‌دانی که روز جزا چیست؟!');
INSERT INTO "hussein_taji" VALUES(82,18,'82:18','باز تو چه می‌دانی که روز جزا چیست؟!');
INSERT INTO "hussein_taji" VALUES(82,19,'82:19','روزی‌که هیچ کس قادر به انجام کاری برای دیگری نیست، و در آن روز حکم (و فرمان) از آن الله است.');
INSERT INTO "hussein_taji" VALUES(83,1,'83:1','وای بر کم فروشان.');
INSERT INTO "hussein_taji" VALUES(83,2,'83:2','کسانی‌که چون (برای خود) از مردم پیمانه می‌کنند، حق خود را کامل می‌گیرند.');
INSERT INTO "hussein_taji" VALUES(83,3,'83:3','و هنگامی‌که (می‌خواهند) برای آنان پیمانه یا وزن کنند، کم می‌گذارند.');
INSERT INTO "hussein_taji" VALUES(83,4,'83:4','آیا آن‌ها گمان نمی‌کنند که (قیامت فرا می‌رسد و از قبرها) برانگیخته می‌شود.');
INSERT INTO "hussein_taji" VALUES(83,5,'83:5','در روزی بزرگ؟!');
INSERT INTO "hussein_taji" VALUES(83,6,'83:6','(همان) روزی‌که مردم در پیشگاه پروردگار جهانیان می‌ایستند.');
INSERT INTO "hussein_taji" VALUES(83,7,'83:7','هرگز چنین نیست (که کافران پنداشته‌اند) حقا که نامۀ (اعمال) بدکاران در سجین است.');
INSERT INTO "hussein_taji" VALUES(83,8,'83:8','و تو چه دانی که سجین چیست؟!');
INSERT INTO "hussein_taji" VALUES(83,9,'83:9','کتابی است که (اعمال بدکاران در آن) نوشته شده است.');
INSERT INTO "hussein_taji" VALUES(83,10,'83:10','در آن روز وای بر تکذیب کنندگان!');
INSERT INTO "hussein_taji" VALUES(83,11,'83:11','آنان که روز جزا را (انکار و) تکذیب می‌کنند.');
INSERT INTO "hussein_taji" VALUES(83,12,'83:12','و جز ستمکاران گنهکار (کسی) آن (روز) را تکذیب نمی‌کنند.');
INSERT INTO "hussein_taji" VALUES(83,13,'83:13','چون آیات ما بر او تلاوت شود، گوید: «(این) افسانه‌های گذشتگان است».');
INSERT INTO "hussein_taji" VALUES(83,14,'83:14','هرگز چنین نیست (که آن‌ها گمان می‌کنند) بلکه (بسبب) آنچه کرده‌اند بر دل‌هایشان زنگار بسته است.');
INSERT INTO "hussein_taji" VALUES(83,15,'83:15','هرگز چنین نیست (که آن‌ها می‌پندارند) بی‌گمان آن‌ها در آن روز از (دیدار) پروردگار‌شان یقیناً محجوب و محرومند.');
INSERT INTO "hussein_taji" VALUES(83,16,'83:16','سپس (بعد از حساب) مسلماً وارد دوزخ می‌شوند.');
INSERT INTO "hussein_taji" VALUES(83,17,'83:17','آنگاه به آن‌ها گفته می‌شود: «این (آتش) همان جیزی است که آن را تکذیب می‌کردید!».');
INSERT INTO "hussein_taji" VALUES(83,18,'83:18','هرگز چنین نیست (که آن‌ها می‌پندارند) بی‌گمان نامۀ (اعمال) نیکوکاران در علیین است.');
INSERT INTO "hussein_taji" VALUES(83,19,'83:19','و تو چه دانی که علیین چیست؟!');
INSERT INTO "hussein_taji" VALUES(83,20,'83:20','کتابی است که (اعمال نیکوکاران در آن) نوشته شده است.');
INSERT INTO "hussein_taji" VALUES(83,21,'83:21','که مقربان (درگاه الهی) بر آن حاضر شوند (و گواهی دهند).');
INSERT INTO "hussein_taji" VALUES(83,22,'83:22','همانا نیکوکاران در نعمت (های بهشت) هستند.');
INSERT INTO "hussein_taji" VALUES(83,23,'83:23','برتخت‌ها (تکیه زده و) می‌نگرند.');
INSERT INTO "hussein_taji" VALUES(83,24,'83:24','خرمی و نشاط نعمت را در چهره‌هایشان (می‌بینی و) می‌شناسی.');
INSERT INTO "hussein_taji" VALUES(83,25,'83:25','آن‌ها از شراب ناب مهرشده، (نوشانده و) سیراب می‌شوند.');
INSERT INTO "hussein_taji" VALUES(83,26,'83:26','مهری که بر آن نهاده شده از مشک است، و در این (شراب و نعمت‌هایی دیگر بهشت) مشتاقان باید بر یکدیگر پیشی گیرند.');
INSERT INTO "hussein_taji" VALUES(83,27,'83:27','و آمیزه‌اش از تسنیم است.');
INSERT INTO "hussein_taji" VALUES(83,28,'83:28','(همان) چشمه‌ای که مقربان (درگاه الهی) از آن می‌نوشند.');
INSERT INTO "hussein_taji" VALUES(83,29,'83:29','همانا کسانی‌که جرم و گناه کردند پیوسته (در دنیا) بر کسانی‌که ایمان آورده بودند می‌خندیدند.');
INSERT INTO "hussein_taji" VALUES(83,30,'83:30','و هرگاه (مؤمنان) از کنار‌شان می‌گذشتند با چشم و ابرو به هم اشاره می‌کردند.');
INSERT INTO "hussein_taji" VALUES(83,31,'83:31','و چون به سوی خانوادۀ خود باز می‌گشتند، شادمان و خندان باز می‌گشتند.');
INSERT INTO "hussein_taji" VALUES(83,32,'83:32','و هنگامی‌که مؤمنان را می‌دیدند می‌گفتند: «بی‌گمان این‌ها گمراهانند».');
INSERT INTO "hussein_taji" VALUES(83,33,'83:33','در حالی‌که آنان برای مراقبت (و نگهبانی) بر مؤمنان فرستاده نشده بودند.');
INSERT INTO "hussein_taji" VALUES(83,34,'83:34','پس امروز کسانی‌که ایمان آورده‌اند به کافر می‌خندند.');
INSERT INTO "hussein_taji" VALUES(83,35,'83:35','بر تخت‌های (آراسته نشسته و) می‌نگرند.');
INSERT INTO "hussein_taji" VALUES(83,36,'83:36','آیا کافران پاداش آنچه می‌کردند دریافت کرده‌اند؟!');
INSERT INTO "hussein_taji" VALUES(84,1,'84:1','هنگامی‌که آسمان شکافته شود.');
INSERT INTO "hussein_taji" VALUES(84,2,'84:2','و به (فرمان) پروردگارش گوش دهد (و تسلیم شود) و سزاوار است (که چنین کند).');
INSERT INTO "hussein_taji" VALUES(84,3,'84:3','و هنگامی‌که زمین گسترده (و هموار) شود.');
INSERT INTO "hussein_taji" VALUES(84,4,'84:4','و هر چه را درون خود دارد بیرون ریزد، و تهی گردد،');
INSERT INTO "hussein_taji" VALUES(84,5,'84:5','و به (فرمان) پروردگارش گوش دهد (و تسلیم شود). و سزاوار است (که چنین کند).');
INSERT INTO "hussein_taji" VALUES(84,6,'84:6','ای انسان! بی‌گمان تو در راه (رسیدن به) پروردگارت سخت تلاش و کوشش می‌کنی، پس او را ملاقات خواهی کرد.');
INSERT INTO "hussein_taji" VALUES(84,7,'84:7','پس اما کسی‌که نامۀ (اعمالش) به دست راستش داده شود،');
INSERT INTO "hussein_taji" VALUES(84,8,'84:8','بزودی به حسابی آسان، محاسبه می‌شود.');
INSERT INTO "hussein_taji" VALUES(84,9,'84:9','و شاد و مسرور به سوی خانواده‌اش باز می‌گردد.');
INSERT INTO "hussein_taji" VALUES(84,10,'84:10','و اما کسی‌که نامۀ (اعمالش) از پشت سرش به او داده شود.');
INSERT INTO "hussein_taji" VALUES(84,11,'84:11','پس بزودی (مرگ و) نابودی را می‌طلبند.');
INSERT INTO "hussein_taji" VALUES(84,12,'84:12','و به (آتش) جهنم شعله‌ور در آید.');
INSERT INTO "hussein_taji" VALUES(84,13,'84:13','بی‌گمان او در (دنیا) میان خانوادۀ خود شادمان بود.');
INSERT INTO "hussein_taji" VALUES(84,14,'84:14','به راستی او گمان می‌کرد هرگز باز نخواهد گشت.');
INSERT INTO "hussein_taji" VALUES(84,15,'84:15','آری، همانا پروردگارش (نسبت) به او بینا بود.');
INSERT INTO "hussein_taji" VALUES(84,16,'84:16','پس به شفق سوگند می‌خورم.');
INSERT INTO "hussein_taji" VALUES(84,17,'84:17','و سوگند به شب و آنچه را فرو می‌پوشد.');
INSERT INTO "hussein_taji" VALUES(84,18,'84:18','و سوگند به ماه چون (فروغش) کامل شود.');
INSERT INTO "hussein_taji" VALUES(84,19,'84:19','که شما پیوسته از حالی به حال دیگر (منتقل) می‌شوید.');
INSERT INTO "hussein_taji" VALUES(84,20,'84:20','پس آنان را چه شده که ایمان نمی‌آورند؟');
INSERT INTO "hussein_taji" VALUES(84,21,'84:21','و هنگامی‌که قرآن بر آن‌ها تلاوت شود سجده نمی‌کنند؟');
INSERT INTO "hussein_taji" VALUES(84,22,'84:22','بلکه کسانی‌که کافر شدند، پیوسته (آیات الهی را) تکذیب می‌کنند.');
INSERT INTO "hussein_taji" VALUES(84,23,'84:23','و الله به آنچه در دل پنهان می‌دارند دانا‌تر است.');
INSERT INTO "hussein_taji" VALUES(84,24,'84:24','پس (ای پیامبر) آنان را به عذابی دردناک بشارت ده.');
INSERT INTO "hussein_taji" VALUES(84,25,'84:25','مگر کسانی‌که ایمان آوردنده‌اند و کارهای شایسته انجام داده‌اند، که برای آن‌ها پاداشی قطع نشدنی است.');
INSERT INTO "hussein_taji" VALUES(85,1,'85:1','قسم به آسمان که دارندۀ برج‌ها است.');
INSERT INTO "hussein_taji" VALUES(85,2,'85:2','و سوگند به روز موعود (= روز قیامت).');
INSERT INTO "hussein_taji" VALUES(85,3,'85:3','و سوگند به شاهد (= روز جمعه) و «مشهود» (= روز عرفه).');
INSERT INTO "hussein_taji" VALUES(85,4,'85:4','اصحاب اخدود (= خندق داران) به هلاکت (و نابودی) رسیدند.');
INSERT INTO "hussein_taji" VALUES(85,5,'85:5','(خندق‌های) افروخته از هیزم‌های بسیار.');
INSERT INTO "hussein_taji" VALUES(85,6,'85:6','هنگامی‌که بر (کنارۀ) آن نشسته بودند.');
INSERT INTO "hussein_taji" VALUES(85,7,'85:7','و آنان آنچه را با مؤمنان انجام می‌دادند تماشا می‌کردند.');
INSERT INTO "hussein_taji" VALUES(85,8,'85:8','و هیچ ایرادی از آنان نگرفتند، جز اینکه به الله پیروزمند ستوده ایمان آورده بودند.');
INSERT INTO "hussein_taji" VALUES(85,9,'85:9','(همان) کسی‌که فرمانروایی آسمان‌ها و زمین از آن اوست، و الله و بر همه چیز گواه است.');
INSERT INTO "hussein_taji" VALUES(85,10,'85:10','به راستی کسانی‌که مردان مومن را و زنان مؤمنه را شکنجه (و آزار) دادند، سپس توبه نکردند، پس برای آن‌ها عذاب جهنم است، و برایشان عذاب (آتش) سوزان است.');
INSERT INTO "hussein_taji" VALUES(85,11,'85:11','همانا برای کسانی‌که ایمان آوردند و کار‌های شایسته کردند، باغ‌هایی (از بهشت) است که نهر‌ها زیر (درختان) آن جاری است، و این کامیابی بزرگ است.');
INSERT INTO "hussein_taji" VALUES(85,12,'85:12','به راستی (مجازات و) فرو گرفتن پروردگارت سخت است.');
INSERT INTO "hussein_taji" VALUES(85,13,'85:13','همانا اوست که (آفرینش را) آغاز می‌کند و دوباره (بعد از مرگ) باز می‌گرداند.');
INSERT INTO "hussein_taji" VALUES(85,14,'85:14','و او آمرزندۀ دوستدار است.');
INSERT INTO "hussein_taji" VALUES(85,15,'85:15','صاحب عرش مجید است.');
INSERT INTO "hussein_taji" VALUES(85,16,'85:16','آنچه را بخواهد انجام می‌دهد.');
INSERT INTO "hussein_taji" VALUES(85,17,'85:17','(ای پیامبر) آیا داستان لشکر‌ها به تو رسیده است،');
INSERT INTO "hussein_taji" VALUES(85,18,'85:18','(لشکریان) فرعون و (قوم) ثمود؟!');
INSERT INTO "hussein_taji" VALUES(85,19,'85:19','بلکه کسانی‌که کافر شدند پیوسته در تکذیب (حق) هستند.');
INSERT INTO "hussein_taji" VALUES(85,20,'85:20','و الله از هر سو بر آن‌ها احاطه دارد.');
INSERT INTO "hussein_taji" VALUES(85,21,'85:21','بلکه این قرآن مجید است.');
INSERT INTO "hussein_taji" VALUES(85,22,'85:22','که در لوح محفوظ (نگاشته شده) است.');
INSERT INTO "hussein_taji" VALUES(86,1,'86:1','سوگند به آسمان و سوگند به طارق (= آنچه در شب آید).');
INSERT INTO "hussein_taji" VALUES(86,2,'86:2','و تو چه می‌دانی طارق چیست؟');
INSERT INTO "hussein_taji" VALUES(86,3,'86:3','(همان) ستارۀ درخشان است.');
INSERT INTO "hussein_taji" VALUES(86,4,'86:4','هیچ کس نیست مگر آنکه بر او نگهبان و محافظی (از فرشتگان) است.');
INSERT INTO "hussein_taji" VALUES(86,5,'86:5','پس انسان باید بنگرد که از چه چیز آفریده شده است.');
INSERT INTO "hussein_taji" VALUES(86,6,'86:6','از یک آب جهنده آفریده شده است.');
INSERT INTO "hussein_taji" VALUES(86,7,'86:7','که از میان پشت (مرد) و سینۀ (زن) بیرون می‌آید.');
INSERT INTO "hussein_taji" VALUES(86,8,'86:8','بی‌گمان او (= الله) بر بازگردانیدن او (پس از مرگ) قادر است.');
INSERT INTO "hussein_taji" VALUES(86,9,'86:9','روزی‌که نهان‌ها آشکار می‌شود.');
INSERT INTO "hussein_taji" VALUES(86,10,'86:10','پس او را هیچ نیرو و یاوری نیست.');
INSERT INTO "hussein_taji" VALUES(86,11,'86:11','سوگند به آسمان پر باران.');
INSERT INTO "hussein_taji" VALUES(86,12,'86:12','و سوگند به زمین پر شکاف.');
INSERT INTO "hussein_taji" VALUES(86,13,'86:13','بی‌گمان این (قرآن) سخن جدا کنندۀ حق از باطل است.');
INSERT INTO "hussein_taji" VALUES(86,14,'86:14','و آن (سخن) هزل و بیهوده نیست.');
INSERT INTO "hussein_taji" VALUES(86,15,'86:15','بی‌گمان آن‌ها پیوسته حیله و نیرنگ می‌کنند.');
INSERT INTO "hussein_taji" VALUES(86,16,'86:16','و من (هم) حیله (و تدبیر) می‌کنم.');
INSERT INTO "hussein_taji" VALUES(86,17,'86:17','پس کافران را مهلت بده، اندکی آنان را رها کن.');
INSERT INTO "hussein_taji" VALUES(87,1,'87:1','نام پروردگار بلند مرتبه‌ات را به پاکی یاد کن.');
INSERT INTO "hussein_taji" VALUES(87,2,'87:2','(همان) که آفرید پس درست و استوار ساخت.');
INSERT INTO "hussein_taji" VALUES(87,3,'87:3','و (همان) کسی‌که اندازه گیری کرد پس هدایت نمود.');
INSERT INTO "hussein_taji" VALUES(87,4,'87:4','و (آن) کسی‌که چراگاه را رویانید.');
INSERT INTO "hussein_taji" VALUES(87,5,'87:5','سپس آن را خشک و سیاه گردانید.');
INSERT INTO "hussein_taji" VALUES(87,6,'87:6','ما بزودی (قرآن را) بر تو می‌خوانیم، پس هرگز فراموش نخواهی کرد.');
INSERT INTO "hussein_taji" VALUES(87,7,'87:7','مگر آنچه را که الله بخواهد، همانا او آشکار و نهان را می‌داند.');
INSERT INTO "hussein_taji" VALUES(87,8,'87:8','و ما تو را برای (راه) آسان توفیق می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(87,9,'87:9','پس اگر پند مفید باشد، پند ده.');
INSERT INTO "hussein_taji" VALUES(87,10,'87:10','بزودی کسی‌که (از الله) می‌ترسد، پند می‌پذیرد.');
INSERT INTO "hussein_taji" VALUES(87,11,'87:11','و بدبخت‌ترین (مردم) از آن دوری می‌گزیند.');
INSERT INTO "hussein_taji" VALUES(87,12,'87:12','(همان) کسی‌که در آتش بزرگ (جهنم) در خواهد آمد.');
INSERT INTO "hussein_taji" VALUES(87,13,'87:13','سپس در آن (آتش) نه می‌میرد و نه زنده می‌ماند.');
INSERT INTO "hussein_taji" VALUES(87,14,'87:14','یقیناً کسی‌که خود را پاک (و تزکیه) کند، رستگار شد.');
INSERT INTO "hussein_taji" VALUES(87,15,'87:15','و (نیز کسی که) نام پروردگارش را یادکرد پس نماز گزارد.');
INSERT INTO "hussein_taji" VALUES(87,16,'87:16','بلکه شما (مردم) زندگی دنیا را (بر آخرت) ترجیح می‌دهید.');
INSERT INTO "hussein_taji" VALUES(87,17,'87:17','در حالی‌که آخرت بهتر و پاینده‌تر است.');
INSERT INTO "hussein_taji" VALUES(87,18,'87:18','به راستی این (فحوا) در صحیفه‌های پیشین (نیز) بود.');
INSERT INTO "hussein_taji" VALUES(87,19,'87:19','صحیفه‌های ابراهیم و موسی.');
INSERT INTO "hussein_taji" VALUES(88,1,'88:1','(ای پیامبر) آیا خبر غاشیه (= قیامت فراگیر) به تو رسیده است؟!');
INSERT INTO "hussein_taji" VALUES(88,2,'88:2','چهره‌هایی در آن روز خوار و ذلیل خواهند بود.');
INSERT INTO "hussein_taji" VALUES(88,3,'88:3','(در دنیا) پیوسته تلاش کرده (و) خسته شده (و چونکه در راه حق نبوده نتیجه‌ای ندیده‌اند.');
INSERT INTO "hussein_taji" VALUES(88,4,'88:4','در آتش سوزان در آنید.');
INSERT INTO "hussein_taji" VALUES(88,5,'88:5','از چشمۀ بسیار داغ به آن‌ها نوشانده می‌شود.');
INSERT INTO "hussein_taji" VALUES(88,6,'88:6','طعامی جز از ضریع (= خار خشک و تلخ) ندارند.');
INSERT INTO "hussein_taji" VALUES(88,7,'88:7','که نه (آن‌ها را) فربه کند، و نه دفع گرسنگی نماید.');
INSERT INTO "hussein_taji" VALUES(88,8,'88:8','(و) چهره‌هایی در آن روز شاداب و تازه باشند.');
INSERT INTO "hussein_taji" VALUES(88,9,'88:9','و از سعی (و تلاش) خود خوشنودند.');
INSERT INTO "hussein_taji" VALUES(88,10,'88:10','در بهشت برین هستند.');
INSERT INTO "hussein_taji" VALUES(88,11,'88:11','در آن هیچ سخن بیهوده‌ای نمی‌شنوند.');
INSERT INTO "hussein_taji" VALUES(88,12,'88:12','در آن چشمه(های آب) روان باشد.');
INSERT INTO "hussein_taji" VALUES(88,13,'88:13','در آن تخت‌های (زیبای) بلند (پایه) است.');
INSERT INTO "hussein_taji" VALUES(88,14,'88:14','و جام‌هایی (که در کنار چشمه‌ها) نهاده شده.');
INSERT INTO "hussein_taji" VALUES(88,15,'88:15','و پشتی‌ها بالش‌های منظم چیده شده،');
INSERT INTO "hussein_taji" VALUES(88,16,'88:16','و (نیز) فرش‌های گرانبها گسترده شده است.');
INSERT INTO "hussein_taji" VALUES(88,17,'88:17','آیا (کافران) به شتر نمی‌نگرند، گه چگونه آفریده شده است؟');
INSERT INTO "hussein_taji" VALUES(88,18,'88:18','و به آسمان (نمی نگرند،) که چگونه بر افراشته شده است؟');
INSERT INTO "hussein_taji" VALUES(88,19,'88:19','و به کوه‌ها (نگاه نمی‌کنند) که چگونه (محکم بر زمین محکم) نصب شده است؟');
INSERT INTO "hussein_taji" VALUES(88,20,'88:20','و به زمین (نمی‌نگرند،) که چگونه گسترده شده است؟');
INSERT INTO "hussein_taji" VALUES(88,21,'88:21','پس (ای پیامبر) پند بده، که تو تنها پند دهنده‌ای.');
INSERT INTO "hussein_taji" VALUES(88,22,'88:22','تو بر آن‌ها مسلط (و چیره) نیستی (که بر ایمان مجبور‌شان کنی).');
INSERT INTO "hussein_taji" VALUES(88,23,'88:23','مگر کسی‌که روی گرداند کافر شود.');
INSERT INTO "hussein_taji" VALUES(88,24,'88:24','پس الله با عذاب بزرگ او را عذاب می‌کند.');
INSERT INTO "hussein_taji" VALUES(88,25,'88:25','همانا بازگشت آن‌ها به سوی ماست.');
INSERT INTO "hussein_taji" VALUES(88,26,'88:26','سپس بی‌گمان حساب‌شان (نیز) با ماست.');
INSERT INTO "hussein_taji" VALUES(89,1,'89:1','سوگند به صبح.');
INSERT INTO "hussein_taji" VALUES(89,2,'89:2','و به شب‌های ده‌گانه (ذیحجه).');
INSERT INTO "hussein_taji" VALUES(89,3,'89:3','و به زوج و فرد.');
INSERT INTO "hussein_taji" VALUES(89,4,'89:4','و سوگند به شب هنگامی‌که (حرکت می‌کند و) می‌رود.');
INSERT INTO "hussein_taji" VALUES(89,5,'89:5','آیا در این (چیزها) سوگندی برای صاحب خرد نیست؟!');
INSERT INTO "hussein_taji" VALUES(89,6,'89:6','(ای پیامبر) آیا ندیدی که پروردگارت با (قوم) عاد چه کرد؟');
INSERT INTO "hussein_taji" VALUES(89,7,'89:7','(همان عاد) ارم که دارای ستون‌ها (و قامت بلند) بودند.');
INSERT INTO "hussein_taji" VALUES(89,8,'89:8','که همانند آن در شهرها آفریده نشده بود.');
INSERT INTO "hussein_taji" VALUES(89,9,'89:9','و (نیز قوم) ثمود، آن‌هایی که صخره‌های سخت را از (کنار) وادی می‌تراشیدند (و برای خود خانه می‌ساختند)؟');
INSERT INTO "hussein_taji" VALUES(89,10,'89:10','و فرعون صاحب میخ‌ها (شکنجه‌ها و سپاه).');
INSERT INTO "hussein_taji" VALUES(89,11,'89:11','(همان) کسانی‌که در شهر‌ها طغیان (و سرکشی) کردند.');
INSERT INTO "hussein_taji" VALUES(89,12,'89:12','پس در آن‌ها بسیار فساد به بار آوردند.');
INSERT INTO "hussein_taji" VALUES(89,13,'89:13','آنگاه پروردگارت تازیانه عذاب را بر آنان فرو آورد.');
INSERT INTO "hussein_taji" VALUES(89,14,'89:14','یقیناً پروردگار تو در کمین‌گاه است.');
INSERT INTO "hussein_taji" VALUES(89,15,'89:15','پس اما انسان هنگامی که پروردگارش او را بیازماید و او را گرامی دارد و به او نعمت بخشد، (مغرور می‌شود و) می‌گوید: «پروردگارم مرا گرامی داشته است».');
INSERT INTO "hussein_taji" VALUES(89,16,'89:16','و اما هنگامی‌که او را بیازماید، پس روزیش را بر او تنگ گیرد (ناامید می‌شود و) می‌گوید: «پروردگارم مرا خوار کرده است».');
INSERT INTO "hussein_taji" VALUES(89,17,'89:17','هرگز چنین نیست (که شما می‌پندارید)، بلکه یتیم را گرامی نمی‌دارید.');
INSERT INTO "hussein_taji" VALUES(89,18,'89:18','و یکدیگر را بر اطعام مستمندان ترغیب و تشویق نمی‌کنید.');
INSERT INTO "hussein_taji" VALUES(89,19,'89:19','و میراث را حریصانه می‌خورید (و حق دیگران را نمی‌دهید).');
INSERT INTO "hussein_taji" VALUES(89,20,'89:20','و مال و ثروت (دنیا) را بسیار دوست دارید.');
INSERT INTO "hussein_taji" VALUES(89,21,'89:21','هرگز چنین نیست (که شما گمان می‌کنید) هنگامی‌که زمین سخت درهم کوبیده شود (و هموار گردد).');
INSERT INTO "hussein_taji" VALUES(89,22,'89:22','و پروردگارت (برای دادرسی) بیاید و (نیز) فرشتگان صف در صف (بیانید).');
INSERT INTO "hussein_taji" VALUES(89,23,'89:23','و در آن روز جهنم آورده شود، در آن روز انسان متذکر می‌شود، و (اما) این تذکر چه فایده‌ای برای او دارد؟!');
INSERT INTO "hussein_taji" VALUES(89,24,'89:24','می‌گوید: «ای کاش (در دنیا) برای (این) زندگیم (چیزی از) پیش فرستاده بودم.');
INSERT INTO "hussein_taji" VALUES(89,25,'89:25','پس در آن روز هیچ کسی همانند عذاب او (= الله) عذاب نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(89,26,'89:26','و هیچ کس همچون بند کشیدن او (= الله) کسی را بند نکشد.');
INSERT INTO "hussein_taji" VALUES(89,27,'89:27','«ای روح آرام یافته.');
INSERT INTO "hussein_taji" VALUES(89,28,'89:28','به سوی پروردگارت باز گرد، در حالی‌که تو از او خوشنودی و او از تو خشنود است.');
INSERT INTO "hussein_taji" VALUES(89,29,'89:29','پس در زمرۀ بندگان (خاص) من در آی.');
INSERT INTO "hussein_taji" VALUES(89,30,'89:30','و به بهشت من وارد شو»');
INSERT INTO "hussein_taji" VALUES(90,1,'90:1','سوگند به این شهر (مکه).');
INSERT INTO "hussein_taji" VALUES(90,2,'90:2','و تو در این شهر ساکنی (و جنگ در این شهر برای تو حلال خواهد شد).');
INSERT INTO "hussein_taji" VALUES(90,3,'90:3','و سوگند به پدر (= آدم) و فرزندانش.');
INSERT INTO "hussein_taji" VALUES(90,4,'90:4','یقیناً ما انسان را در رنج آفریدیم.');
INSERT INTO "hussein_taji" VALUES(90,5,'90:5','آیا او گمان می‌کند هیچ کس بر او (توانا و) قادر نیست؟');
INSERT INTO "hussein_taji" VALUES(90,6,'90:6','می‌گوید: «مال زیادی را تباه (و خرج) کرده‌ام».');
INSERT INTO "hussein_taji" VALUES(90,7,'90:7','آیا می‌پندارد که هیچ کس او را ندیده است؟');
INSERT INTO "hussein_taji" VALUES(90,8,'90:8','آیا برای او دو چشم قرار ندادیم،');
INSERT INTO "hussein_taji" VALUES(90,9,'90:9','و یک زبان و دو لب؟');
INSERT INTO "hussein_taji" VALUES(90,10,'90:10','و او را به دو راه (خیر و شر) راهنمایی کردیم.');
INSERT INTO "hussein_taji" VALUES(90,11,'90:11','پس به گردنۀ (سخت) قدم نگذاشت (و نیامد).');
INSERT INTO "hussein_taji" VALUES(90,12,'90:12','و تو چه می‌دانی که گردنۀ (سخت) چیست؟');
INSERT INTO "hussein_taji" VALUES(90,13,'90:13','آزاد کردن برده است.');
INSERT INTO "hussein_taji" VALUES(90,14,'90:14','یا طعام دادن در روز قحطی (و گرسنگی)،');
INSERT INTO "hussein_taji" VALUES(90,15,'90:15','یتیمی را از خویشاوندان،');
INSERT INTO "hussein_taji" VALUES(90,16,'90:16','یا مستمندی خاک نشین را،');
INSERT INTO "hussein_taji" VALUES(90,17,'90:17','آنگاه از کسانی باشد که ایمان آورده‌اند و یکدیگر را به صبر سفارش کرده‌اند، و یکدیگر را به (محبت و) مهربانی توصیه نموده‌اند.');
INSERT INTO "hussein_taji" VALUES(90,18,'90:18','اینان اهل سعادت‌اند (که نامۀ اعمالشان به دست راست‌شان داده می‌شوند و به بهشت می‌روند).');
INSERT INTO "hussein_taji" VALUES(90,19,'90:19','و کسانی‌که به آیات (و نشانه‌های) ما کفر ورزیدند، آنان اهل شقاوتند (که نامۀ اعمال‌شان به دست چپ‌شان داده می‌شود و به جهنم می‌روند).');
INSERT INTO "hussein_taji" VALUES(90,20,'90:20','بر آن‌ها (از هر سو) آتشی سر پوشیده است (که راه فرار ندارند).');
INSERT INTO "hussein_taji" VALUES(91,1,'91:1','سوگند به خورشید و روشنی آن (به هنگام چاشت).');
INSERT INTO "hussein_taji" VALUES(91,2,'91:2','و سوگند به ماه هنگامی‌که بعد از آن در آید.');
INSERT INTO "hussein_taji" VALUES(91,3,'91:3','و سوگند به روز هنگامی‌که آن (=آفتاب) را روشن (و جلوه‌گر) کند.');
INSERT INTO "hussein_taji" VALUES(91,4,'91:4','و سوگند به شب هنگامی‌که را بپوشاند.');
INSERT INTO "hussein_taji" VALUES(91,5,'91:5','و سوگند به آسمان و به آنکه آن را بنا کرد.');
INSERT INTO "hussein_taji" VALUES(91,6,'91:6','و سوگند به زمین و به آنکه آن را گسترانید.');
INSERT INTO "hussein_taji" VALUES(91,7,'91:7','و سوگند به جان (انسان) و آنکه آن را (آفرید و) نیکو گردانید.');
INSERT INTO "hussein_taji" VALUES(91,8,'91:8','سپس بدی‌ها و پرهیزگاری‌هایش را (به او) الهام کرد.');
INSERT INTO "hussein_taji" VALUES(91,9,'91:9','محققاً هرکس که نفس خود را تزکیه (و پاک) کرد، رستگار شد.');
INSERT INTO "hussein_taji" VALUES(91,10,'91:10','و یقیناً هرکس که آن را (با گناه) آلوده ساخت، ناامید (و زیانکار) شد.');
INSERT INTO "hussein_taji" VALUES(91,11,'91:11','(قوم) ثمود از روی سرکشی. (و طنیان‌شان، پیامبر‌شان را) تکذیب کردند.');
INSERT INTO "hussein_taji" VALUES(91,12,'91:12','آنگاه که شقی‌ترینشان (برای اقدام به جنایت) برخاست.');
INSERT INTO "hussein_taji" VALUES(91,13,'91:13','پس پیامبر الله (صالح) به آن‌ها گفت: «ماده شتر الله را با آبشخورش وا‌گذارید».');
INSERT INTO "hussein_taji" VALUES(91,14,'91:14','(ولی آن‌ها) او را تکذیب کردند، آنگاه ماده شتر را پی کردند، پس پروردگار‌شان به سبب گناهان‌شان بر سر‌شان عذاب آورد، و با خاک یکسان کرد.');
INSERT INTO "hussein_taji" VALUES(91,15,'91:15','و (الله) از سرانجام آن بیم ندارد.');
INSERT INTO "hussein_taji" VALUES(92,1,'92:1','سوگند به شب هنگامی‌که (همه چیز را) بپوشاند.');
INSERT INTO "hussein_taji" VALUES(92,2,'92:2','و سوگند به روز هنگامی‌که آشکار شود.');
INSERT INTO "hussein_taji" VALUES(92,3,'92:3','و سوگند به آنکه نر و ماده را آفرید.');
INSERT INTO "hussein_taji" VALUES(92,4,'92:4','که یقیناً سعی (و تلاش) شما مختلف است.');
INSERT INTO "hussein_taji" VALUES(92,5,'92:5','پس اما کسی‌که (در راه الله) بخشید و پرهیزگاری کرد.');
INSERT INTO "hussein_taji" VALUES(92,6,'92:6','و (سخنان) نیک را تصدیق کرد.');
INSERT INTO "hussein_taji" VALUES(92,7,'92:7','پس ما او را برای (راه) آسان توفیق می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(92,8,'92:8','و اما کسی‌که بخل ورزید و بی نیازی طلبید.');
INSERT INTO "hussein_taji" VALUES(92,9,'92:9','و (نیز سخنان) نیک را تکذیب کرد.');
INSERT INTO "hussein_taji" VALUES(92,10,'92:10','پس به‌زودی او را به (راه) دشوار سوق می‌دهیم.');
INSERT INTO "hussein_taji" VALUES(92,11,'92:11','و هنگامی‌که سرازیر (جهنم) شود، مالش به حال او سودی نخواهد داشت.');
INSERT INTO "hussein_taji" VALUES(92,12,'92:12','مسلماً هدایت (و راهنمایی) کردن بر (عهدۀ) ماست.');
INSERT INTO "hussein_taji" VALUES(92,13,'92:13','و محققاً آخرت و دنیا از آن ماست.');
INSERT INTO "hussein_taji" VALUES(92,14,'92:14','پس من شما را از آتشی شعله‌ور بیم دادم.');
INSERT INTO "hussein_taji" VALUES(92,15,'92:15','به آن (آتش کسی) جز بدبخت‌ترین (مردم) در نیفتد');
INSERT INTO "hussein_taji" VALUES(92,16,'92:16','(همان) کسی‌که (آیات ما را) تکذیب کرد');
INSERT INTO "hussein_taji" VALUES(92,17,'92:17','و به‌زودی پرهیزکار‌ترین (مردم) از آن دور داشته می‌شود.');
INSERT INTO "hussein_taji" VALUES(92,18,'92:18','(همان) کسی‌که مال خود را (در راه الله) می‌بخشد تا پاک شود.');
INSERT INTO "hussein_taji" VALUES(92,19,'92:19','و هیچ کس را بر او نعمت (و حقی) نیست، تا بخواهد (با انفاق) پاداش دهد.');
INSERT INTO "hussein_taji" VALUES(92,20,'92:20','مگر (برای) بدست آوردن رضا (و خشنودی) پروردگار برترش.');
INSERT INTO "hussein_taji" VALUES(92,21,'92:21','و به راستی خشنود خواهد شد.');
INSERT INTO "hussein_taji" VALUES(93,1,'93:1','سوگند به آغاز روز.');
INSERT INTO "hussein_taji" VALUES(93,2,'93:2','و سوگند به شب هنگامی‌که آرام گیرد.');
INSERT INTO "hussein_taji" VALUES(93,3,'93:3','(که) پروردگارت تو را وا نگذاشته و (بر تو) خشم نگرفته است.');
INSERT INTO "hussein_taji" VALUES(93,4,'93:4','و مسلماً آخرت برای تو از دنیا بهتر است.');
INSERT INTO "hussein_taji" VALUES(93,5,'93:5','و به‌زودی پروردگارت به تو (آن قدر) عطا کند که خشنود گردی.');
INSERT INTO "hussein_taji" VALUES(93,6,'93:6','آیا تو را یتیم نیافت، پس پناه داد؟!');
INSERT INTO "hussein_taji" VALUES(93,7,'93:7','و تو را (راه نایافته و) سرگشته یافت، پس هدایت کرد،');
INSERT INTO "hussein_taji" VALUES(93,8,'93:8','و تو را فقیر یافت، پس بی‌نیاز نمود،');
INSERT INTO "hussein_taji" VALUES(93,9,'93:9','پس (توهم) یتیم را میازار.');
INSERT INTO "hussein_taji" VALUES(93,10,'93:10','و سؤال کننده را مران (و با او خشونت نکن).');
INSERT INTO "hussein_taji" VALUES(93,11,'93:11','و نعمت‌های پروردگارت را باز گوکن و سپاس گذار.');
INSERT INTO "hussein_taji" VALUES(94,1,'94:1','(ای پیامبر) آیا ما سینه‌ات را برایت نگشودیم؟');
INSERT INTO "hussein_taji" VALUES(94,2,'94:2','و بار (سنگین) تو را از (دوش) تو بر نداشتیم؟');
INSERT INTO "hussein_taji" VALUES(94,3,'94:3','همان (باری) که بر پشت تو سنگینی می‌کرد (و نزدیک بود نیست تو را بشکند)؟');
INSERT INTO "hussein_taji" VALUES(94,4,'94:4','و (نام و) آوازۀ تو را بلند ساختیم.');
INSERT INTO "hussein_taji" VALUES(94,5,'94:5','پس مسلماً با (هر) دشواری آسانی است.');
INSERT INTO "hussein_taji" VALUES(94,6,'94:6','مسلماً با (هر) دشواری آسانی است.');
INSERT INTO "hussein_taji" VALUES(94,7,'94:7','پس هنگامی‌که (از کار و امور دنیا) فارغ شدی (به عبادت پروردگارت) بکوش.');
INSERT INTO "hussein_taji" VALUES(94,8,'94:8','و به سوی پروردگارت راغب (و مشتاق) شو.');
INSERT INTO "hussein_taji" VALUES(95,1,'95:1','سوگند به انجیر و زیتون.');
INSERT INTO "hussein_taji" VALUES(95,2,'95:2','و سوگند به طور سینا.');
INSERT INTO "hussein_taji" VALUES(95,3,'95:3','و سوگند به این شهر امن (= مکه).');
INSERT INTO "hussein_taji" VALUES(95,4,'95:4','یقیناً ما انسان را در بهترین صورت (و هیئت) آفریدیم.');
INSERT INTO "hussein_taji" VALUES(95,5,'95:5','سپس او را به پایین‌تر مرحله بر گرداندیم.');
INSERT INTO "hussein_taji" VALUES(95,6,'95:6','مگر کسانی‌که ایمان آوردند و کار‌های شایسته انجام دادند، پس برای آن‌ها پاداشی قطع نشدنی است.');
INSERT INTO "hussein_taji" VALUES(95,7,'95:7','پس (ای انسان) چه چیز بعد (از این همه دلایل روشن) تو را به تکذیب (روز) جزا وا‌می‌دارد؟!');
INSERT INTO "hussein_taji" VALUES(95,8,'95:8','آیا الله بهترین داوران (و حاکم مطلق) نیست؟!');
INSERT INTO "hussein_taji" VALUES(96,1,'96:1','(ای پیامبر) بخوان به نام پروردگارت که (هستی را) آفرید.');
INSERT INTO "hussein_taji" VALUES(96,2,'96:2','(همان پروردگار که) انسان را از خون بسته آفرید.');
INSERT INTO "hussein_taji" VALUES(96,3,'96:3','بخوان، و پروردگارت (از همه) بزرگوار‌تر است.');
INSERT INTO "hussein_taji" VALUES(96,4,'96:4','(همان) کسی‌که بوسیلۀ قلم (نوشتن) آموخت.');
INSERT INTO "hussein_taji" VALUES(96,5,'96:5','به انسان آنچه را که نمی‌دانست آموخت.');
INSERT INTO "hussein_taji" VALUES(96,6,'96:6','حقا که انسان طغیان (و سرکشی) می‌کند.');
INSERT INTO "hussein_taji" VALUES(96,7,'96:7','چون خون را بی‌نیاز ببیند.');
INSERT INTO "hussein_taji" VALUES(96,8,'96:8','یقیناً بازگشت (همه) به‌سوی پروردگار تو است.');
INSERT INTO "hussein_taji" VALUES(96,9,'96:9','(ای پیامبر) آیا دیده‌ای آن کسی‌که باز می‌دارد،');
INSERT INTO "hussein_taji" VALUES(96,10,'96:10','بنده‌ای را هنگامی‌که نماز می‌خواند؟');
INSERT INTO "hussein_taji" VALUES(96,11,'96:11','به من خبر بده اگر این (بنده) بر (راه) هدایت باشد.');
INSERT INTO "hussein_taji" VALUES(96,12,'96:12','یا (مردم را) به پر هیزگاری فرمان دهد (سزای کسی‌که او را آزاد دهد چیست؟).');
INSERT INTO "hussein_taji" VALUES(96,13,'96:13','به من خبر ده اگر این (شخص سرکش، حق را) تکذیب کرد، و روی‌گردان شد (آیا سزاوار عذاب نیست؟).');
INSERT INTO "hussein_taji" VALUES(96,14,'96:14','آیا نمی‌داند که مسلماً، الله (همه اعمالش را) می‌بیند؟!');
INSERT INTO "hussein_taji" VALUES(96,15,'96:15','چنین نیست (که او گمان می‌کند) اگر باز نیاید (و دست از شرارت بر ندارد) محققاً ناصیه‌اش = (موی پیشانی‌اش) را خواهیم گرفت و کشید.');
INSERT INTO "hussein_taji" VALUES(96,16,'96:16','(همان) پیشانی دروغگوی خطا کار را،');
INSERT INTO "hussein_taji" VALUES(96,17,'96:17','پس باید اهل مجلسش (و همدمانش) را بخواند.');
INSERT INTO "hussein_taji" VALUES(96,18,'96:18','ما (نیز) به‌زودی مأموران آتش را فرا می‌خوانیم.');
INSERT INTO "hussein_taji" VALUES(96,19,'96:19','چنین نیست (که او می‌پندارد) هرگز او را اطاعت نکن، و سجده کن و (به الله) تقرب جوی.');
INSERT INTO "hussein_taji" VALUES(97,1,'97:1','همانا ما آن (= قرآن) را در شب قدر نازل کردیم.');
INSERT INTO "hussein_taji" VALUES(97,2,'97:2','و تو چه دانی شب قدر چیست؟!');
INSERT INTO "hussein_taji" VALUES(97,3,'97:3','شب قدر، بهتر از هزار ماه است.');
INSERT INTO "hussein_taji" VALUES(97,4,'97:4','فرشتگان و روح (= جبرئیل) در آن (شب) به فرمان پروردگارشان برای (انجام) هر کاری نازل می‌شوند.');
INSERT INTO "hussein_taji" VALUES(97,5,'97:5','(آن شب) تا طلوع فجر سلامتی (و رحمت) باشد.');
INSERT INTO "hussein_taji" VALUES(98,1,'98:1','کسانی‌که کافر شدن از اهل کتاب و مشرکان، (از آیین کفر) دست بردار نبودند تا اینکه برای آن‌ها دلیل روشنی بیاید.');
INSERT INTO "hussein_taji" VALUES(98,2,'98:2','پیامبری از سوی الله (بیاید) که صحیفه‌های پاک را بخواند.');
INSERT INTO "hussein_taji" VALUES(98,3,'98:3','و در آن (صحیفه‌ها) نوشته‌هایی استوار (و درست) است.');
INSERT INTO "hussein_taji" VALUES(98,4,'98:4','و کسانی‌که به آنان کتاب داده شد (از یهود و نصاری) پراکنده نشدند (و اختلاف نکردند) مگر بعد از آنکه دلیل روشن برای آن‌ها آمد.');
INSERT INTO "hussein_taji" VALUES(98,5,'98:5','و آنان فرمان نیافتند جز اینکه الله را بپرستند در حالی‌که دین خود را برای او خالص گردانند (و از شرک و بت برستی) به توحید (و دین ابراهیم) روی آورند. و نماز را بر پا دارند و زکات را بپردازند، و این است آیین راستین و مستقیم.');
INSERT INTO "hussein_taji" VALUES(98,6,'98:6','بی‌گمان کسانی‌که کافر شدند از اهل کتاب و مشرکان، در آتش جهنم‌اند که در آن جاودانه خواهند ماند، آنان بدترین آفریدگان هستند.');
INSERT INTO "hussein_taji" VALUES(98,7,'98:7','همانا کسانی‌که ایمان آوردند و کارهای شایسته انجام دادند، آن‌ها بهترین آفریدگان هستند.');
INSERT INTO "hussein_taji" VALUES(98,8,'98:8','پاداش آن‌ها نزد پروردگار‌شان باغ‌های (بهشت) جاویدان است که نهر‌ها از زیر (درختان) آن جاری است، همیشه در آن خواهند ماند، الله از آن‌ها خشنود است، و آن‌ها (نیز) از او خشنودند، و این (مقام و پاداش) برای کسی است که از پروردگارش بترسد.');
INSERT INTO "hussein_taji" VALUES(99,1,'99:1','هنگامی‌که زمین با شدید‌ترین لرزه‌هایش به لرزه در آید.');
INSERT INTO "hussein_taji" VALUES(99,2,'99:2','و زمین بار‌های سنگینش را بیرون ریزد.');
INSERT INTO "hussein_taji" VALUES(99,3,'99:3','و انسان گوید: «آن (= زمین) را چه شده است؟!».');
INSERT INTO "hussein_taji" VALUES(99,4,'99:4','در آن روز (زمین) تمام خبرهایش را بازگو می‌کند.');
INSERT INTO "hussein_taji" VALUES(99,5,'99:5','زیرا که پروردگارت به او (چنین) وحی (و حکم) کرده است.');
INSERT INTO "hussein_taji" VALUES(99,6,'99:6','در آن روز مردم پراکنده (از موقف حساب) باز می‌گردند، تا اعمال‌شان به آن‌ها نشان داده شود.');
INSERT INTO "hussein_taji" VALUES(99,7,'99:7','پس هرکس به‌اندازۀ ذره‌ای کار نیک انجام داده باشد (پاداش) آن را می‌بیند.');
INSERT INTO "hussein_taji" VALUES(99,8,'99:8','و هرکس به‌اندازۀ ذره‌ای کار بد کرده باشد، (کیفر) آن را می‌بیند.');
INSERT INTO "hussein_taji" VALUES(100,1,'100:1','سوگند به اسب‌های دونده که نفس زنان (به‌سوی میدان) پیش می‌رفتند.');
INSERT INTO "hussein_taji" VALUES(100,2,'100:2','و سوگند به اسب‌هایی که (در اثر اصطکاک سم‌شان با سنگ‌ها) جرقۀ (آتش) ایجاد کردند.');
INSERT INTO "hussein_taji" VALUES(100,3,'100:3','باز سوگند به اسب‌های که در صبحگاهان (بر دشمن) یورش برند.');
INSERT INTO "hussein_taji" VALUES(100,4,'100:4','پس در آن هنگام گرد و غبار بر انگیرند.');
INSERT INTO "hussein_taji" VALUES(100,5,'100:5','آنگاه به میان جمع (سپاه دشمن) در آیند، (و آن‌ها را محاصره کننده).');
INSERT INTO "hussein_taji" VALUES(100,6,'100:6','محققاً انسان در برابر (نعمت‌های) پروردگارش بسیار ناسپاس است.');
INSERT INTO "hussein_taji" VALUES(100,7,'100:7','و بی‌گمان او بر این (ناسپاسی) گواه است.');
INSERT INTO "hussein_taji" VALUES(100,8,'100:8','و همانا او علاقۀ فراوان و شدیدی به مال دارد.');
INSERT INTO "hussein_taji" VALUES(100,9,'100:9','آیا او نمی‌داند که چون آنچه در گور‌هاست، (همه زنده و) برانگیخته شوند؟');
INSERT INTO "hussein_taji" VALUES(100,10,'100:10','وآنچه در سینه‌هاست (همه) آشکار گردد.');
INSERT INTO "hussein_taji" VALUES(100,11,'100:11','یقیناً در آن روز پروردگارشان به (وضع و حال) آن‌ها کاملاً آگاه است.');
INSERT INTO "hussein_taji" VALUES(101,1,'101:1','فرو کوبنده.');
INSERT INTO "hussein_taji" VALUES(101,2,'101:2','چیست (آن) فرو کوبنده؟');
INSERT INTO "hussein_taji" VALUES(101,3,'101:3','و تو چه می‌دانی که (آن) فرو کوبنده چیست؟');
INSERT INTO "hussein_taji" VALUES(101,4,'101:4','روزی‌که مردم مانند پروانه‌های پراکنده خواهند بود.');
INSERT INTO "hussein_taji" VALUES(101,5,'101:5','و گوه‌ها مانند پشم رنگین حلاجی شده خواهد بود.');
INSERT INTO "hussein_taji" VALUES(101,6,'101:6','پس اما هرکس که (در آن روز) کفۀ میزانش سنگین باشد.');
INSERT INTO "hussein_taji" VALUES(101,7,'101:7','در یک زندگی خشنود (و پسندیده‌ای) خواهد بود.');
INSERT INTO "hussein_taji" VALUES(101,8,'101:8','و اما هرکس که کفۀ میزانش سبک باشد.');
INSERT INTO "hussein_taji" VALUES(101,9,'101:9','پس (مسکن و) پناهگاهش هاویه است.');
INSERT INTO "hussein_taji" VALUES(101,10,'101:10','و تو چه دانی که آن (هاویه) چیست؟');
INSERT INTO "hussein_taji" VALUES(101,11,'101:11','آتشی سوزان (و شعله‌ور) است.');
INSERT INTO "hussein_taji" VALUES(102,1,'102:1','افزون طلبی شما را به خود مشغول (و غافل) ساخته است.');
INSERT INTO "hussein_taji" VALUES(102,2,'102:2','تا اینکه (مردید و) به گورستان‌ها رسیدند.');
INSERT INTO "hussein_taji" VALUES(102,3,'102:3','هرگز چنین نیست (که شما می‌پندارید) بزودی خواهید دانست.');
INSERT INTO "hussein_taji" VALUES(102,4,'102:4','باز (هم) چنین نیست، به‌زودی خواهید دانست.');
INSERT INTO "hussein_taji" VALUES(102,5,'102:5','چنین نیست (که شما گمان می‌کنید) اگر شما به علم یقین (حقیقت را) می‌دانستید. (از فزون طلبی و غفلت دست بر می‌داشتید).');
INSERT INTO "hussein_taji" VALUES(102,6,'102:6','قطعاً جهنم را خواهید دید.');
INSERT INTO "hussein_taji" VALUES(102,7,'102:7','سپس محققاً آن با چشم یقین خواهید دید.');
INSERT INTO "hussein_taji" VALUES(102,8,'102:8','آنگاه حتماً در آن روز از (همۀ) نعمت‌ها بازخواست خواهید شد.');
INSERT INTO "hussein_taji" VALUES(103,1,'103:1','سوگند به عصر.');
INSERT INTO "hussein_taji" VALUES(103,2,'103:2','که انسان در (خسران و) زیان است.');
INSERT INTO "hussein_taji" VALUES(103,3,'103:3','مگر کسانی‌که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، و یکدیگر را به حق سفارش نموده، و یکدیگر را به صبر سفارش کرده‌اند.');
INSERT INTO "hussein_taji" VALUES(104,1,'104:1','وای بر هر عیب‌جوی غیبت‌کننده‌ای.');
INSERT INTO "hussein_taji" VALUES(104,2,'104:2','(همان) کسی‌که مال فراوانی گرد آورد و شمارش کرد.');
INSERT INTO "hussein_taji" VALUES(104,3,'104:3','گمان می‌کند که مالش او را جاودانه می‌سازد.');
INSERT INTO "hussein_taji" VALUES(104,4,'104:4','هرگز چنین نیست (که او گمان می‌کند) مسلماً در «حطمه» انداخته خواهد شد.');
INSERT INTO "hussein_taji" VALUES(104,5,'104:5','و تو چه دانی «حطمه» چیست؟!');
INSERT INTO "hussein_taji" VALUES(104,6,'104:6','آتش برافروخته الهی است.');
INSERT INTO "hussein_taji" VALUES(104,7,'104:7','(آتشی) که بر دل‌ها چیره گردد (و بسوزاند).');
INSERT INTO "hussein_taji" VALUES(104,8,'104:8','بی‌گمان آن (آتش) بر آن‌ها فرو بسته (و ازهر سو آن‌ها را محاصره کرده است).');
INSERT INTO "hussein_taji" VALUES(104,9,'104:9','در ستون‌های بلند ( کشیده شده) است.');
INSERT INTO "hussein_taji" VALUES(105,1,'105:1','(ای پیامبر) آیا ندیدی پروردگارت با اصحاب فیل چه کرد؟!.');
INSERT INTO "hussein_taji" VALUES(105,2,'105:2','آیا (حیله و) نقشۀ آن‌ها را در تباهی قرار نداد؟!');
INSERT INTO "hussein_taji" VALUES(105,3,'105:3','و پرندگانی گروه گروه بر (سر) آن‌ها فرستاد.');
INSERT INTO "hussein_taji" VALUES(105,4,'105:4','(که) با سنگ‌های (بسیار کوچکی) از سنگ گِل (که در منقار و چنگال داشتند) به سوی آن‌ها می‌انداختند (و آن‌ها را سنگباران می‌کردند).');
INSERT INTO "hussein_taji" VALUES(105,5,'105:5','پس آن‌ها را همچون کاه جویده شده قرار داد.');
INSERT INTO "hussein_taji" VALUES(106,1,'106:1','برای (شکر) الفت و انس قریش.');
INSERT INTO "hussein_taji" VALUES(106,2,'106:2','(همان) الفت آن‌ها در سفر‌های (تجاری) زمستانی (به یمن) و تابستانی (به شام).');
INSERT INTO "hussein_taji" VALUES(106,3,'106:3','پس باید پروردگار این خانۀ (کعبه) را پرستش کنند.');
INSERT INTO "hussein_taji" VALUES(106,4,'106:4','همان (پروردگاری) که آن‌ها را از گرسنگی (نجات و) طعام داد، و از ترس و خوف اِیمن شان داشت.');
INSERT INTO "hussein_taji" VALUES(107,1,'107:1','(ای پیامبر) آیا کسی‌که روز جزا را تکذیب می‌کند دیده‌ای؟!');
INSERT INTO "hussein_taji" VALUES(107,2,'107:2','پس او (همان) کسی‌که یتیم را (با خشونت از خود) می‌راند.');
INSERT INTO "hussein_taji" VALUES(107,3,'107:3','و (دیگران را) به اطعام بی‌نوا (و مسکین) ترغیب و تشویق نمی‌کند.');
INSERT INTO "hussein_taji" VALUES(107,4,'107:4','پس وای بر نماز گزاران.');
INSERT INTO "hussein_taji" VALUES(107,5,'107:5','همان کسانی‌که از نماز‌شان غافلند.');
INSERT INTO "hussein_taji" VALUES(107,6,'107:6','کسانی‌که خودنمایی می‌کنند.');
INSERT INTO "hussein_taji" VALUES(107,7,'107:7','و (از پرداخت زکات و) عاریت دادن وسایل ضروری زندگی دریغ می‌ورزند.');
INSERT INTO "hussein_taji" VALUES(108,1,'108:1','(ای پیامبر) به راستی که ما به تو کوثر عطا کردیم.');
INSERT INTO "hussein_taji" VALUES(108,2,'108:2','پس برای پروردگارت نماز بخوان و قربانی کن.');
INSERT INTO "hussein_taji" VALUES(108,3,'108:3','بدون شک دشمن تو، بریده نسل و بی‌عقب است.');
INSERT INTO "hussein_taji" VALUES(109,1,'109:1','(ای پیامبر) بگو: «ای کافران.');
INSERT INTO "hussein_taji" VALUES(109,2,'109:2','من نمی‌پرستم آنچه را که شما می‌پرستید.');
INSERT INTO "hussein_taji" VALUES(109,3,'109:3','و شما (نیز) نمی‌پرستید آنچه را که من می‌پرستم.');
INSERT INTO "hussein_taji" VALUES(109,4,'109:4','و نه من پرستش‌گر آنچه شما پرستش کرده‌اید خواهم بود.');
INSERT INTO "hussein_taji" VALUES(109,5,'109:5','و نه شما پرستش‌گر آنچه من می‌پرستم خواهید بود.');
INSERT INTO "hussein_taji" VALUES(109,6,'109:6','(بنا بر این) آیین شما برای خود‌تان، و آیین من برای خودم».');
INSERT INTO "hussein_taji" VALUES(110,1,'110:1','(ای پیامبر) هنگامی‌که یاری الله و پیروزی فرا رسد.');
INSERT INTO "hussein_taji" VALUES(110,2,'110:2','و مردم را ببینی که گروه گروه در دین الله داخل می‌شوند.');
INSERT INTO "hussein_taji" VALUES(110,3,'110:3','پس به ستایش پروردگارت تسبیح گوی و از او آمرزش بخواه، همانا او بسیار توبه‌پذیر است.');
INSERT INTO "hussein_taji" VALUES(111,1,'111:1','بریده باد هر دو دست ابو لهب و هلاک باد!');
INSERT INTO "hussein_taji" VALUES(111,2,'111:2','مال و ثروتش و آنچه را به دست آورده بود به او سودی نبخشید.');
INSERT INTO "hussein_taji" VALUES(111,3,'111:3','بزودی به آتش شعله‌ور در خواهد آمد.');
INSERT INTO "hussein_taji" VALUES(111,4,'111:4','و (نیز) همسرش (ام جمیل، آن) هیزم‌کش (وارد آتش می‌شود).');
INSERT INTO "hussein_taji" VALUES(111,5,'111:5','در گردنش طنابی از لیف خرماست.');
INSERT INTO "hussein_taji" VALUES(112,1,'112:1','(ای پیامبر) بگو: «او الله یکتا و یگانه است.');
INSERT INTO "hussein_taji" VALUES(112,2,'112:2','الله بی‌نیاز است (و همه نیازمند او هستند).');
INSERT INTO "hussein_taji" VALUES(112,3,'112:3','نه (فرزندی) زاده و نه زاده شده است.');
INSERT INTO "hussein_taji" VALUES(112,4,'112:4','و هیچ کس همانند و همتای او نبوده و نیست».');
INSERT INTO "hussein_taji" VALUES(113,1,'113:1','(ای پیامبر) بگو: «به پروردگار سپیده دم پناه می‌برم،');
INSERT INTO "hussein_taji" VALUES(113,2,'113:2','از شر تمام آنچه آفریده است،');
INSERT INTO "hussein_taji" VALUES(113,3,'113:3','و از شر تاریکی شب، آنگاه که همه جا را فرا گیرد.');
INSERT INTO "hussein_taji" VALUES(113,4,'113:4','و از شر (زنان جادوگر) که با افسون در گره‌ها می‌دمند.');
INSERT INTO "hussein_taji" VALUES(113,5,'113:5','و از شر حسود آنگاه که حسد ورزد».');
INSERT INTO "hussein_taji" VALUES(114,1,'114:1','(ای پیامبر) بگو: «به پروردگار مردم پناه می‌برم،');
INSERT INTO "hussein_taji" VALUES(114,2,'114:2','فرمانروای مردم،');
INSERT INTO "hussein_taji" VALUES(114,3,'114:3','(إله و) معبود مردم،');
INSERT INTO "hussein_taji" VALUES(114,4,'114:4','از شر (شیطان) وسوسه‌گر باز پس رونده (به هنگام ذکر الله).');
INSERT INTO "hussein_taji" VALUES(114,5,'114:5','همان که در دل‌های مردم وسوسه می‌کند.');
INSERT INTO "hussein_taji" VALUES(114,6,'114:6','از جنیان (باشد) و (یا از) آدمیان».');
