INSERT INTO "imlaei_simple" VALUES(1,1,'1:1','بسم الله الرحمن الرحيم');
INSERT INTO "imlaei_simple" VALUES(1,2,'1:2','الحمد لله رب العالمين');
INSERT INTO "imlaei_simple" VALUES(1,3,'1:3','الرحمن الرحيم');
INSERT INTO "imlaei_simple" VALUES(1,4,'1:4','مالك يوم الدين');
INSERT INTO "imlaei_simple" VALUES(1,5,'1:5','اياك نعبد واياك نستعين');
INSERT INTO "imlaei_simple" VALUES(1,6,'1:6','اهدنا الصراط المستقيم');
INSERT INTO "imlaei_simple" VALUES(1,7,'1:7','صراط الذين انعمت عليهم غير المغضوب عليهم ولا الضالين');
INSERT INTO "imlaei_simple" VALUES(2,1,'2:1','الم');
INSERT INTO "imlaei_simple" VALUES(2,2,'2:2','ذلك الكتاب لا ريب فيه هدى للمتقين');
INSERT INTO "imlaei_simple" VALUES(2,3,'2:3','الذين يؤمنون بالغيب ويقيمون الصلاة ومما رزقناهم ينفقون');
INSERT INTO "imlaei_simple" VALUES(2,4,'2:4','والذين يؤمنون بما انزل اليك وما انزل من قبلك وبالاخرة هم يوقنون');
INSERT INTO "imlaei_simple" VALUES(2,5,'2:5','اولئك على هدى من ربهم واولئك هم المفلحون');
INSERT INTO "imlaei_simple" VALUES(2,6,'2:6','ان الذين كفروا سواء عليهم اانذرتهم ام لم تنذرهم لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(2,7,'2:7','ختم الله على قلوبهم وعلى سمعهم وعلى ابصارهم غشاوة ولهم عذاب عظيم');
INSERT INTO "imlaei_simple" VALUES(2,8,'2:8','ومن الناس من يقول امنا بالله وباليوم الاخر وما هم بمؤمنين');
INSERT INTO "imlaei_simple" VALUES(2,9,'2:9','يخادعون الله والذين امنوا وما يخدعون الا انفسهم وما يشعرون');
INSERT INTO "imlaei_simple" VALUES(2,10,'2:10','في قلوبهم مرض فزادهم الله مرضا ولهم عذاب اليم بما كانوا يكذبون');
INSERT INTO "imlaei_simple" VALUES(2,11,'2:11','واذا قيل لهم لا تفسدوا في الارض قالوا انما نحن مصلحون');
INSERT INTO "imlaei_simple" VALUES(2,12,'2:12','الا انهم هم المفسدون ولكن لا يشعرون');
INSERT INTO "imlaei_simple" VALUES(2,13,'2:13','واذا قيل لهم امنوا كما امن الناس قالوا انؤمن كما امن السفهاء الا انهم هم السفهاء ولكن لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(2,14,'2:14','واذا لقوا الذين امنوا قالوا امنا واذا خلوا الى شياطينهم قالوا انا معكم انما نحن مستهزئون');
INSERT INTO "imlaei_simple" VALUES(2,15,'2:15','الله يستهزئ بهم ويمدهم في طغيانهم يعمهون');
INSERT INTO "imlaei_simple" VALUES(2,16,'2:16','اولئك الذين اشتروا الضلالة بالهدى فما ربحت تجارتهم وما كانوا مهتدين');
INSERT INTO "imlaei_simple" VALUES(2,17,'2:17','مثلهم كمثل الذي استوقد نارا فلما اضاءت ما حوله ذهب الله بنورهم وتركهم في ظلمات لا يبصرون');
INSERT INTO "imlaei_simple" VALUES(2,18,'2:18','صم بكم عمي فهم لا يرجعون');
INSERT INTO "imlaei_simple" VALUES(2,19,'2:19','او كصيب من السماء فيه ظلمات ورعد وبرق يجعلون اصابعهم في اذانهم من الصواعق حذر الموت والله محيط بالكافرين');
INSERT INTO "imlaei_simple" VALUES(2,20,'2:20','يكاد البرق يخطف ابصارهم كلما اضاء لهم مشوا فيه واذا اظلم عليهم قاموا ولو شاء الله لذهب بسمعهم وابصارهم ان الله على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(2,21,'2:21','يا ايها الناس اعبدوا ربكم الذي خلقكم والذين من قبلكم لعلكم تتقون');
INSERT INTO "imlaei_simple" VALUES(2,22,'2:22','الذي جعل لكم الارض فراشا والسماء بناء وانزل من السماء ماء فاخرج به من الثمرات رزقا لكم فلا تجعلوا لله اندادا وانتم تعلمون');
INSERT INTO "imlaei_simple" VALUES(2,23,'2:23','وان كنتم في ريب مما نزلنا على عبدنا فاتوا بسورة من مثله وادعوا شهداءكم من دون الله ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(2,24,'2:24','فان لم تفعلوا ولن تفعلوا فاتقوا النار التي وقودها الناس والحجارة اعدت للكافرين');
INSERT INTO "imlaei_simple" VALUES(2,25,'2:25','وبشر الذين امنوا وعملوا الصالحات ان لهم جنات تجري من تحتها الانهار كلما رزقوا منها من ثمرة رزقا قالوا هذا الذي رزقنا من قبل واتوا به متشابها ولهم فيها ازواج مطهرة وهم فيها خالدون');
INSERT INTO "imlaei_simple" VALUES(2,26,'2:26','ان الله لا يستحيي ان يضرب مثلا ما بعوضة فما فوقها فاما الذين امنوا فيعلمون انه الحق من ربهم واما الذين كفروا فيقولون ماذا اراد الله بهذا مثلا يضل به كثيرا ويهدي به كثيرا وما يضل به الا الفاسقين');
INSERT INTO "imlaei_simple" VALUES(2,27,'2:27','الذين ينقضون عهد الله من بعد ميثاقه ويقطعون ما امر الله به ان يوصل ويفسدون في الارض اولئك هم الخاسرون');
INSERT INTO "imlaei_simple" VALUES(2,28,'2:28','كيف تكفرون بالله وكنتم امواتا فاحياكم ثم يميتكم ثم يحييكم ثم اليه ترجعون');
INSERT INTO "imlaei_simple" VALUES(2,29,'2:29','هو الذي خلق لكم ما في الارض جميعا ثم استوى الى السماء فسواهن سبع سماوات وهو بكل شيء عليم');
INSERT INTO "imlaei_simple" VALUES(2,30,'2:30','واذ قال ربك للملائكة اني جاعل في الارض خليفة قالوا اتجعل فيها من يفسد فيها ويسفك الدماء ونحن نسبح بحمدك ونقدس لك قال اني اعلم ما لا تعلمون');
INSERT INTO "imlaei_simple" VALUES(2,31,'2:31','وعلم ادم الاسماء كلها ثم عرضهم على الملائكة فقال انبئوني باسماء هؤلاء ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(2,32,'2:32','قالوا سبحانك لا علم لنا الا ما علمتنا انك انت العليم الحكيم');
INSERT INTO "imlaei_simple" VALUES(2,33,'2:33','قال يا ادم انبئهم باسمائهم فلما انباهم باسمائهم قال الم اقل لكم اني اعلم غيب السماوات والارض واعلم ما تبدون وما كنتم تكتمون');
INSERT INTO "imlaei_simple" VALUES(2,34,'2:34','واذ قلنا للملائكة اسجدوا لادم فسجدوا الا ابليس ابى واستكبر وكان من الكافرين');
INSERT INTO "imlaei_simple" VALUES(2,35,'2:35','وقلنا يا ادم اسكن انت وزوجك الجنة وكلا منها رغدا حيث شئتما ولا تقربا هذه الشجرة فتكونا من الظالمين');
INSERT INTO "imlaei_simple" VALUES(2,36,'2:36','فازلهما الشيطان عنها فاخرجهما مما كانا فيه وقلنا اهبطوا بعضكم لبعض عدو ولكم في الارض مستقر ومتاع الى حين');
INSERT INTO "imlaei_simple" VALUES(2,37,'2:37','فتلقى ادم من ربه كلمات فتاب عليه انه هو التواب الرحيم');
INSERT INTO "imlaei_simple" VALUES(2,38,'2:38','قلنا اهبطوا منها جميعا فاما ياتينكم مني هدى فمن تبع هداي فلا خوف عليهم ولا هم يحزنون');
INSERT INTO "imlaei_simple" VALUES(2,39,'2:39','والذين كفروا وكذبوا باياتنا اولئك اصحاب النار هم فيها خالدون');
INSERT INTO "imlaei_simple" VALUES(2,40,'2:40','يا بني اسرائيل اذكروا نعمتي التي انعمت عليكم واوفوا بعهدي اوف بعهدكم واياي فارهبون');
INSERT INTO "imlaei_simple" VALUES(2,41,'2:41','وامنوا بما انزلت مصدقا لما معكم ولا تكونوا اول كافر به ولا تشتروا باياتي ثمنا قليلا واياي فاتقون');
INSERT INTO "imlaei_simple" VALUES(2,42,'2:42','ولا تلبسوا الحق بالباطل وتكتموا الحق وانتم تعلمون');
INSERT INTO "imlaei_simple" VALUES(2,43,'2:43','واقيموا الصلاة واتوا الزكاة واركعوا مع الراكعين');
INSERT INTO "imlaei_simple" VALUES(2,44,'2:44','اتامرون الناس بالبر وتنسون انفسكم وانتم تتلون الكتاب افلا تعقلون');
INSERT INTO "imlaei_simple" VALUES(2,45,'2:45','واستعينوا بالصبر والصلاة وانها لكبيرة الا على الخاشعين');
INSERT INTO "imlaei_simple" VALUES(2,46,'2:46','الذين يظنون انهم ملاقو ربهم وانهم اليه راجعون');
INSERT INTO "imlaei_simple" VALUES(2,47,'2:47','يا بني اسرائيل اذكروا نعمتي التي انعمت عليكم واني فضلتكم على العالمين');
INSERT INTO "imlaei_simple" VALUES(2,48,'2:48','واتقوا يوما لا تجزي نفس عن نفس شيئا ولا يقبل منها شفاعة ولا يؤخذ منها عدل ولا هم ينصرون');
INSERT INTO "imlaei_simple" VALUES(2,49,'2:49','واذ نجيناكم من ال فرعون يسومونكم سوء العذاب يذبحون ابناءكم ويستحيون نساءكم وفي ذلكم بلاء من ربكم عظيم');
INSERT INTO "imlaei_simple" VALUES(2,50,'2:50','واذ فرقنا بكم البحر فانجيناكم واغرقنا ال فرعون وانتم تنظرون');
INSERT INTO "imlaei_simple" VALUES(2,51,'2:51','واذ واعدنا موسى اربعين ليلة ثم اتخذتم العجل من بعده وانتم ظالمون');
INSERT INTO "imlaei_simple" VALUES(2,52,'2:52','ثم عفونا عنكم من بعد ذلك لعلكم تشكرون');
INSERT INTO "imlaei_simple" VALUES(2,53,'2:53','واذ اتينا موسى الكتاب والفرقان لعلكم تهتدون');
INSERT INTO "imlaei_simple" VALUES(2,54,'2:54','واذ قال موسى لقومه يا قوم انكم ظلمتم انفسكم باتخاذكم العجل فتوبوا الى بارئكم فاقتلوا انفسكم ذلكم خير لكم عند بارئكم فتاب عليكم انه هو التواب الرحيم');
INSERT INTO "imlaei_simple" VALUES(2,55,'2:55','واذ قلتم يا موسى لن نؤمن لك حتى نرى الله جهرة فاخذتكم الصاعقة وانتم تنظرون');
INSERT INTO "imlaei_simple" VALUES(2,56,'2:56','ثم بعثناكم من بعد موتكم لعلكم تشكرون');
INSERT INTO "imlaei_simple" VALUES(2,57,'2:57','وظللنا عليكم الغمام وانزلنا عليكم المن والسلوى كلوا من طيبات ما رزقناكم وما ظلمونا ولكن كانوا انفسهم يظلمون');
INSERT INTO "imlaei_simple" VALUES(2,58,'2:58','واذ قلنا ادخلوا هذه القرية فكلوا منها حيث شئتم رغدا وادخلوا الباب سجدا وقولوا حطة نغفر لكم خطاياكم وسنزيد المحسنين');
INSERT INTO "imlaei_simple" VALUES(2,59,'2:59','فبدل الذين ظلموا قولا غير الذي قيل لهم فانزلنا على الذين ظلموا رجزا من السماء بما كانوا يفسقون');
INSERT INTO "imlaei_simple" VALUES(2,60,'2:60','واذ استسقى موسى لقومه فقلنا اضرب بعصاك الحجر فانفجرت منه اثنتا عشرة عينا قد علم كل اناس مشربهم كلوا واشربوا من رزق الله ولا تعثوا في الارض مفسدين');
INSERT INTO "imlaei_simple" VALUES(2,61,'2:61','واذ قلتم يا موسى لن نصبر على طعام واحد فادع لنا ربك يخرج لنا مما تنبت الارض من بقلها وقثائها وفومها وعدسها وبصلها قال اتستبدلون الذي هو ادنى بالذي هو خير اهبطوا مصرا فان لكم ما سالتم وضربت عليهم الذلة والمسكنة وباءوا بغضب من الله ذلك بانهم كانوا يكفرون بايات الله ويقتلون النبيين بغير الحق ذلك بما عصوا وكانوا يعتدون');
INSERT INTO "imlaei_simple" VALUES(2,62,'2:62','ان الذين امنوا والذين هادوا والنصارى والصابئين من امن بالله واليوم الاخر وعمل صالحا فلهم اجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون');
INSERT INTO "imlaei_simple" VALUES(2,63,'2:63','واذ اخذنا ميثاقكم ورفعنا فوقكم الطور خذوا ما اتيناكم بقوة واذكروا ما فيه لعلكم تتقون');
INSERT INTO "imlaei_simple" VALUES(2,64,'2:64','ثم توليتم من بعد ذلك فلولا فضل الله عليكم ورحمته لكنتم من الخاسرين');
INSERT INTO "imlaei_simple" VALUES(2,65,'2:65','ولقد علمتم الذين اعتدوا منكم في السبت فقلنا لهم كونوا قردة خاسئين');
INSERT INTO "imlaei_simple" VALUES(2,66,'2:66','فجعلناها نكالا لما بين يديها وما خلفها وموعظة للمتقين');
INSERT INTO "imlaei_simple" VALUES(2,67,'2:67','واذ قال موسى لقومه ان الله يامركم ان تذبحوا بقرة قالوا اتتخذنا هزوا قال اعوذ بالله ان اكون من الجاهلين');
INSERT INTO "imlaei_simple" VALUES(2,68,'2:68','قالوا ادع لنا ربك يبين لنا ما هي قال انه يقول انها بقرة لا فارض ولا بكر عوان بين ذلك فافعلوا ما تؤمرون');
INSERT INTO "imlaei_simple" VALUES(2,69,'2:69','قالوا ادع لنا ربك يبين لنا ما لونها قال انه يقول انها بقرة صفراء فاقع لونها تسر الناظرين');
INSERT INTO "imlaei_simple" VALUES(2,70,'2:70','قالوا ادع لنا ربك يبين لنا ما هي ان البقر تشابه علينا وانا ان شاء الله لمهتدون');
INSERT INTO "imlaei_simple" VALUES(2,71,'2:71','قال انه يقول انها بقرة لا ذلول تثير الارض ولا تسقي الحرث مسلمة لا شية فيها قالوا الان جئت بالحق فذبحوها وما كادوا يفعلون');
INSERT INTO "imlaei_simple" VALUES(2,72,'2:72','واذ قتلتم نفسا فاداراتم فيها والله مخرج ما كنتم تكتمون');
INSERT INTO "imlaei_simple" VALUES(2,73,'2:73','فقلنا اضربوه ببعضها كذلك يحيي الله الموتى ويريكم اياته لعلكم تعقلون');
INSERT INTO "imlaei_simple" VALUES(2,74,'2:74','ثم قست قلوبكم من بعد ذلك فهي كالحجارة او اشد قسوة وان من الحجارة لما يتفجر منه الانهار وان منها لما يشقق فيخرج منه الماء وان منها لما يهبط من خشية الله وما الله بغافل عما تعملون');
INSERT INTO "imlaei_simple" VALUES(2,75,'2:75','افتطمعون ان يؤمنوا لكم وقد كان فريق منهم يسمعون كلام الله ثم يحرفونه من بعد ما عقلوه وهم يعلمون');
INSERT INTO "imlaei_simple" VALUES(2,76,'2:76','واذا لقوا الذين امنوا قالوا امنا واذا خلا بعضهم الى بعض قالوا اتحدثونهم بما فتح الله عليكم ليحاجوكم به عند ربكم افلا تعقلون');
INSERT INTO "imlaei_simple" VALUES(2,77,'2:77','اولا يعلمون ان الله يعلم ما يسرون وما يعلنون');
INSERT INTO "imlaei_simple" VALUES(2,78,'2:78','ومنهم اميون لا يعلمون الكتاب الا اماني وان هم الا يظنون');
INSERT INTO "imlaei_simple" VALUES(2,79,'2:79','فويل للذين يكتبون الكتاب بايديهم ثم يقولون هذا من عند الله ليشتروا به ثمنا قليلا فويل لهم مما كتبت ايديهم وويل لهم مما يكسبون');
INSERT INTO "imlaei_simple" VALUES(2,80,'2:80','وقالوا لن تمسنا النار الا اياما معدودة قل اتخذتم عند الله عهدا فلن يخلف الله عهده ام تقولون على الله ما لا تعلمون');
INSERT INTO "imlaei_simple" VALUES(2,81,'2:81','بلى من كسب سيئة واحاطت به خطيئته فاولئك اصحاب النار هم فيها خالدون');
INSERT INTO "imlaei_simple" VALUES(2,82,'2:82','والذين امنوا وعملوا الصالحات اولئك اصحاب الجنة هم فيها خالدون');
INSERT INTO "imlaei_simple" VALUES(2,83,'2:83','واذ اخذنا ميثاق بني اسرائيل لا تعبدون الا الله وبالوالدين احسانا وذي القربى واليتامى والمساكين وقولوا للناس حسنا واقيموا الصلاة واتوا الزكاة ثم توليتم الا قليلا منكم وانتم معرضون');
INSERT INTO "imlaei_simple" VALUES(2,84,'2:84','واذ اخذنا ميثاقكم لا تسفكون دماءكم ولا تخرجون انفسكم من دياركم ثم اقررتم وانتم تشهدون');
INSERT INTO "imlaei_simple" VALUES(2,85,'2:85','ثم انتم هؤلاء تقتلون انفسكم وتخرجون فريقا منكم من ديارهم تظاهرون عليهم بالاثم والعدوان وان ياتوكم اسارى تفادوهم وهو محرم عليكم اخراجهم افتؤمنون ببعض الكتاب وتكفرون ببعض فما جزاء من يفعل ذلك منكم الا خزي في الحياة الدنيا ويوم القيامة يردون الى اشد العذاب وما الله بغافل عما تعملون');
INSERT INTO "imlaei_simple" VALUES(2,86,'2:86','اولئك الذين اشتروا الحياة الدنيا بالاخرة فلا يخفف عنهم العذاب ولا هم ينصرون');
INSERT INTO "imlaei_simple" VALUES(2,87,'2:87','ولقد اتينا موسى الكتاب وقفينا من بعده بالرسل واتينا عيسى ابن مريم البينات وايدناه بروح القدس افكلما جاءكم رسول بما لا تهوى انفسكم استكبرتم ففريقا كذبتم وفريقا تقتلون');
INSERT INTO "imlaei_simple" VALUES(2,88,'2:88','وقالوا قلوبنا غلف بل لعنهم الله بكفرهم فقليلا ما يؤمنون');
INSERT INTO "imlaei_simple" VALUES(2,89,'2:89','ولما جاءهم كتاب من عند الله مصدق لما معهم وكانوا من قبل يستفتحون على الذين كفروا فلما جاءهم ما عرفوا كفروا به فلعنة الله على الكافرين');
INSERT INTO "imlaei_simple" VALUES(2,90,'2:90','بئسما اشتروا به انفسهم ان يكفروا بما انزل الله بغيا ان ينزل الله من فضله على من يشاء من عباده فباءوا بغضب على غضب وللكافرين عذاب مهين');
INSERT INTO "imlaei_simple" VALUES(2,91,'2:91','واذا قيل لهم امنوا بما انزل الله قالوا نؤمن بما انزل علينا ويكفرون بما وراءه وهو الحق مصدقا لما معهم قل فلم تقتلون انبياء الله من قبل ان كنتم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(2,92,'2:92','ولقد جاءكم موسى بالبينات ثم اتخذتم العجل من بعده وانتم ظالمون');
INSERT INTO "imlaei_simple" VALUES(2,93,'2:93','واذ اخذنا ميثاقكم ورفعنا فوقكم الطور خذوا ما اتيناكم بقوة واسمعوا قالوا سمعنا وعصينا واشربوا في قلوبهم العجل بكفرهم قل بئسما يامركم به ايمانكم ان كنتم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(2,94,'2:94','قل ان كانت لكم الدار الاخرة عند الله خالصة من دون الناس فتمنوا الموت ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(2,95,'2:95','ولن يتمنوه ابدا بما قدمت ايديهم والله عليم بالظالمين');
INSERT INTO "imlaei_simple" VALUES(2,96,'2:96','ولتجدنهم احرص الناس على حياة ومن الذين اشركوا يود احدهم لو يعمر الف سنة وما هو بمزحزحه من العذاب ان يعمر والله بصير بما يعملون');
INSERT INTO "imlaei_simple" VALUES(2,97,'2:97','قل من كان عدوا لجبريل فانه نزله على قلبك باذن الله مصدقا لما بين يديه وهدى وبشرى للمؤمنين');
INSERT INTO "imlaei_simple" VALUES(2,98,'2:98','من كان عدوا لله وملائكته ورسله وجبريل وميكال فان الله عدو للكافرين');
INSERT INTO "imlaei_simple" VALUES(2,99,'2:99','ولقد انزلنا اليك ايات بينات وما يكفر بها الا الفاسقون');
INSERT INTO "imlaei_simple" VALUES(2,100,'2:100','اوكلما عاهدوا عهدا نبذه فريق منهم بل اكثرهم لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(2,101,'2:101','ولما جاءهم رسول من عند الله مصدق لما معهم نبذ فريق من الذين اوتوا الكتاب كتاب الله وراء ظهورهم كانهم لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(2,102,'2:102','واتبعوا ما تتلو الشياطين على ملك سليمان وما كفر سليمان ولكن الشياطين كفروا يعلمون الناس السحر وما انزل على الملكين ببابل هاروت وماروت وما يعلمان من احد حتى يقولا انما نحن فتنة فلا تكفر فيتعلمون منهما ما يفرقون به بين المرء وزوجه وما هم بضارين به من احد الا باذن الله ويتعلمون ما يضرهم ولا ينفعهم ولقد علموا لمن اشتراه ما له في الاخرة من خلاق ولبئس ما شروا به انفسهم لو كانوا يعلمون');
INSERT INTO "imlaei_simple" VALUES(2,103,'2:103','ولو انهم امنوا واتقوا لمثوبة من عند الله خير لو كانوا يعلمون');
INSERT INTO "imlaei_simple" VALUES(2,104,'2:104','يا ايها الذين امنوا لا تقولوا راعنا وقولوا انظرنا واسمعوا وللكافرين عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(2,105,'2:105','ما يود الذين كفروا من اهل الكتاب ولا المشركين ان ينزل عليكم من خير من ربكم والله يختص برحمته من يشاء والله ذو الفضل العظيم');
INSERT INTO "imlaei_simple" VALUES(2,106,'2:106','ما ننسخ من اية او ننسها نات بخير منها او مثلها الم تعلم ان الله على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(2,107,'2:107','الم تعلم ان الله له ملك السماوات والارض وما لكم من دون الله من ولي ولا نصير');
INSERT INTO "imlaei_simple" VALUES(2,108,'2:108','ام تريدون ان تسالوا رسولكم كما سئل موسى من قبل ومن يتبدل الكفر بالايمان فقد ضل سواء السبيل');
INSERT INTO "imlaei_simple" VALUES(2,109,'2:109','ود كثير من اهل الكتاب لو يردونكم من بعد ايمانكم كفارا حسدا من عند انفسهم من بعد ما تبين لهم الحق فاعفوا واصفحوا حتى ياتي الله بامره ان الله على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(2,110,'2:110','واقيموا الصلاة واتوا الزكاة وما تقدموا لانفسكم من خير تجدوه عند الله ان الله بما تعملون بصير');
INSERT INTO "imlaei_simple" VALUES(2,111,'2:111','وقالوا لن يدخل الجنة الا من كان هودا او نصارى تلك امانيهم قل هاتوا برهانكم ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(2,112,'2:112','بلى من اسلم وجهه لله وهو محسن فله اجره عند ربه ولا خوف عليهم ولا هم يحزنون');
INSERT INTO "imlaei_simple" VALUES(2,113,'2:113','وقالت اليهود ليست النصارى على شيء وقالت النصارى ليست اليهود على شيء وهم يتلون الكتاب كذلك قال الذين لا يعلمون مثل قولهم فالله يحكم بينهم يوم القيامة فيما كانوا فيه يختلفون');
INSERT INTO "imlaei_simple" VALUES(2,114,'2:114','ومن اظلم ممن منع مساجد الله ان يذكر فيها اسمه وسعى في خرابها اولئك ما كان لهم ان يدخلوها الا خائفين لهم في الدنيا خزي ولهم في الاخرة عذاب عظيم');
INSERT INTO "imlaei_simple" VALUES(2,115,'2:115','ولله المشرق والمغرب فاينما تولوا فثم وجه الله ان الله واسع عليم');
INSERT INTO "imlaei_simple" VALUES(2,116,'2:116','وقالوا اتخذ الله ولدا سبحانه بل له ما في السماوات والارض كل له قانتون');
INSERT INTO "imlaei_simple" VALUES(2,117,'2:117','بديع السماوات والارض واذا قضى امرا فانما يقول له كن فيكون');
INSERT INTO "imlaei_simple" VALUES(2,118,'2:118','وقال الذين لا يعلمون لولا يكلمنا الله او تاتينا اية كذلك قال الذين من قبلهم مثل قولهم تشابهت قلوبهم قد بينا الايات لقوم يوقنون');
INSERT INTO "imlaei_simple" VALUES(2,119,'2:119','انا ارسلناك بالحق بشيرا ونذيرا ولا تسال عن اصحاب الجحيم');
INSERT INTO "imlaei_simple" VALUES(2,120,'2:120','ولن ترضى عنك اليهود ولا النصارى حتى تتبع ملتهم قل ان هدى الله هو الهدى ولئن اتبعت اهواءهم بعد الذي جاءك من العلم ما لك من الله من ولي ولا نصير');
INSERT INTO "imlaei_simple" VALUES(2,121,'2:121','الذين اتيناهم الكتاب يتلونه حق تلاوته اولئك يؤمنون به ومن يكفر به فاولئك هم الخاسرون');
INSERT INTO "imlaei_simple" VALUES(2,122,'2:122','يا بني اسرائيل اذكروا نعمتي التي انعمت عليكم واني فضلتكم على العالمين');
INSERT INTO "imlaei_simple" VALUES(2,123,'2:123','واتقوا يوما لا تجزي نفس عن نفس شيئا ولا يقبل منها عدل ولا تنفعها شفاعة ولا هم ينصرون');
INSERT INTO "imlaei_simple" VALUES(2,124,'2:124','واذ ابتلى ابراهيم ربه بكلمات فاتمهن قال اني جاعلك للناس اماما قال ومن ذريتي قال لا ينال عهدي الظالمين');
INSERT INTO "imlaei_simple" VALUES(2,125,'2:125','واذ جعلنا البيت مثابة للناس وامنا واتخذوا من مقام ابراهيم مصلى وعهدنا الى ابراهيم واسماعيل ان طهرا بيتي للطائفين والعاكفين والركع السجود');
INSERT INTO "imlaei_simple" VALUES(2,126,'2:126','واذ قال ابراهيم رب اجعل هذا بلدا امنا وارزق اهله من الثمرات من امن منهم بالله واليوم الاخر قال ومن كفر فامتعه قليلا ثم اضطره الى عذاب النار وبئس المصير');
INSERT INTO "imlaei_simple" VALUES(2,127,'2:127','واذ يرفع ابراهيم القواعد من البيت واسماعيل ربنا تقبل منا انك انت السميع العليم');
INSERT INTO "imlaei_simple" VALUES(2,128,'2:128','ربنا واجعلنا مسلمين لك ومن ذريتنا امة مسلمة لك وارنا مناسكنا وتب علينا انك انت التواب الرحيم');
INSERT INTO "imlaei_simple" VALUES(2,129,'2:129','ربنا وابعث فيهم رسولا منهم يتلو عليهم اياتك ويعلمهم الكتاب والحكمة ويزكيهم انك انت العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(2,130,'2:130','ومن يرغب عن ملة ابراهيم الا من سفه نفسه ولقد اصطفيناه في الدنيا وانه في الاخرة لمن الصالحين');
INSERT INTO "imlaei_simple" VALUES(2,131,'2:131','اذ قال له ربه اسلم قال اسلمت لرب العالمين');
INSERT INTO "imlaei_simple" VALUES(2,132,'2:132','ووصى بها ابراهيم بنيه ويعقوب يا بني ان الله اصطفى لكم الدين فلا تموتن الا وانتم مسلمون');
INSERT INTO "imlaei_simple" VALUES(2,133,'2:133','ام كنتم شهداء اذ حضر يعقوب الموت اذ قال لبنيه ما تعبدون من بعدي قالوا نعبد الهك واله ابائك ابراهيم واسماعيل واسحاق الها واحدا ونحن له مسلمون');
INSERT INTO "imlaei_simple" VALUES(2,134,'2:134','تلك امة قد خلت لها ما كسبت ولكم ما كسبتم ولا تسالون عما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(2,135,'2:135','وقالوا كونوا هودا او نصارى تهتدوا قل بل ملة ابراهيم حنيفا وما كان من المشركين');
INSERT INTO "imlaei_simple" VALUES(2,136,'2:136','قولوا امنا بالله وما انزل الينا وما انزل الى ابراهيم واسماعيل واسحاق ويعقوب والاسباط وما اوتي موسى وعيسى وما اوتي النبيون من ربهم لا نفرق بين احد منهم ونحن له مسلمون');
INSERT INTO "imlaei_simple" VALUES(2,137,'2:137','فان امنوا بمثل ما امنتم به فقد اهتدوا وان تولوا فانما هم في شقاق فسيكفيكهم الله وهو السميع العليم');
INSERT INTO "imlaei_simple" VALUES(2,138,'2:138','صبغة الله ومن احسن من الله صبغة ونحن له عابدون');
INSERT INTO "imlaei_simple" VALUES(2,139,'2:139','قل اتحاجوننا في الله وهو ربنا وربكم ولنا اعمالنا ولكم اعمالكم ونحن له مخلصون');
INSERT INTO "imlaei_simple" VALUES(2,140,'2:140','ام تقولون ان ابراهيم واسماعيل واسحاق ويعقوب والاسباط كانوا هودا او نصارى قل اانتم اعلم ام الله ومن اظلم ممن كتم شهادة عنده من الله وما الله بغافل عما تعملون');
INSERT INTO "imlaei_simple" VALUES(2,141,'2:141','تلك امة قد خلت لها ما كسبت ولكم ما كسبتم ولا تسالون عما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(2,142,'2:142','سيقول السفهاء من الناس ما ولاهم عن قبلتهم التي كانوا عليها قل لله المشرق والمغرب يهدي من يشاء الى صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(2,143,'2:143','وكذلك جعلناكم امة وسطا لتكونوا شهداء على الناس ويكون الرسول عليكم شهيدا وما جعلنا القبلة التي كنت عليها الا لنعلم من يتبع الرسول ممن ينقلب على عقبيه وان كانت لكبيرة الا على الذين هدى الله وما كان الله ليضيع ايمانكم ان الله بالناس لرءوف رحيم');
INSERT INTO "imlaei_simple" VALUES(2,144,'2:144','قد نرى تقلب وجهك في السماء فلنولينك قبلة ترضاها فول وجهك شطر المسجد الحرام وحيث ما كنتم فولوا وجوهكم شطره وان الذين اوتوا الكتاب ليعلمون انه الحق من ربهم وما الله بغافل عما يعملون');
INSERT INTO "imlaei_simple" VALUES(2,145,'2:145','ولئن اتيت الذين اوتوا الكتاب بكل اية ما تبعوا قبلتك وما انت بتابع قبلتهم وما بعضهم بتابع قبلة بعض ولئن اتبعت اهواءهم من بعد ما جاءك من العلم انك اذا لمن الظالمين');
INSERT INTO "imlaei_simple" VALUES(2,146,'2:146','الذين اتيناهم الكتاب يعرفونه كما يعرفون ابناءهم وان فريقا منهم ليكتمون الحق وهم يعلمون');
INSERT INTO "imlaei_simple" VALUES(2,147,'2:147','الحق من ربك فلا تكونن من الممترين');
INSERT INTO "imlaei_simple" VALUES(2,148,'2:148','ولكل وجهة هو موليها فاستبقوا الخيرات اين ما تكونوا يات بكم الله جميعا ان الله على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(2,149,'2:149','ومن حيث خرجت فول وجهك شطر المسجد الحرام وانه للحق من ربك وما الله بغافل عما تعملون');
INSERT INTO "imlaei_simple" VALUES(2,150,'2:150','ومن حيث خرجت فول وجهك شطر المسجد الحرام وحيث ما كنتم فولوا وجوهكم شطره لئلا يكون للناس عليكم حجة الا الذين ظلموا منهم فلا تخشوهم واخشوني ولاتم نعمتي عليكم ولعلكم تهتدون');
INSERT INTO "imlaei_simple" VALUES(2,151,'2:151','كما ارسلنا فيكم رسولا منكم يتلو عليكم اياتنا ويزكيكم ويعلمكم الكتاب والحكمة ويعلمكم ما لم تكونوا تعلمون');
INSERT INTO "imlaei_simple" VALUES(2,152,'2:152','فاذكروني اذكركم واشكروا لي ولا تكفرون');
INSERT INTO "imlaei_simple" VALUES(2,153,'2:153','يا ايها الذين امنوا استعينوا بالصبر والصلاة ان الله مع الصابرين');
INSERT INTO "imlaei_simple" VALUES(2,154,'2:154','ولا تقولوا لمن يقتل في سبيل الله اموات بل احياء ولكن لا تشعرون');
INSERT INTO "imlaei_simple" VALUES(2,155,'2:155','ولنبلونكم بشيء من الخوف والجوع ونقص من الاموال والانفس والثمرات وبشر الصابرين');
INSERT INTO "imlaei_simple" VALUES(2,156,'2:156','الذين اذا اصابتهم مصيبة قالوا انا لله وانا اليه راجعون');
INSERT INTO "imlaei_simple" VALUES(2,157,'2:157','اولئك عليهم صلوات من ربهم ورحمة واولئك هم المهتدون');
INSERT INTO "imlaei_simple" VALUES(2,158,'2:158','ان الصفا والمروة من شعائر الله فمن حج البيت او اعتمر فلا جناح عليه ان يطوف بهما ومن تطوع خيرا فان الله شاكر عليم');
INSERT INTO "imlaei_simple" VALUES(2,159,'2:159','ان الذين يكتمون ما انزلنا من البينات والهدى من بعد ما بيناه للناس في الكتاب اولئك يلعنهم الله ويلعنهم اللاعنون');
INSERT INTO "imlaei_simple" VALUES(2,160,'2:160','الا الذين تابوا واصلحوا وبينوا فاولئك اتوب عليهم وانا التواب الرحيم');
INSERT INTO "imlaei_simple" VALUES(2,161,'2:161','ان الذين كفروا وماتوا وهم كفار اولئك عليهم لعنة الله والملائكة والناس اجمعين');
INSERT INTO "imlaei_simple" VALUES(2,162,'2:162','خالدين فيها لا يخفف عنهم العذاب ولا هم ينظرون');
INSERT INTO "imlaei_simple" VALUES(2,163,'2:163','والهكم اله واحد لا اله الا هو الرحمن الرحيم');
INSERT INTO "imlaei_simple" VALUES(2,164,'2:164','ان في خلق السماوات والارض واختلاف الليل والنهار والفلك التي تجري في البحر بما ينفع الناس وما انزل الله من السماء من ماء فاحيا به الارض بعد موتها وبث فيها من كل دابة وتصريف الرياح والسحاب المسخر بين السماء والارض لايات لقوم يعقلون');
INSERT INTO "imlaei_simple" VALUES(2,165,'2:165','ومن الناس من يتخذ من دون الله اندادا يحبونهم كحب الله والذين امنوا اشد حبا لله ولو يرى الذين ظلموا اذ يرون العذاب ان القوة لله جميعا وان الله شديد العذاب');
INSERT INTO "imlaei_simple" VALUES(2,166,'2:166','اذ تبرا الذين اتبعوا من الذين اتبعوا وراوا العذاب وتقطعت بهم الاسباب');
INSERT INTO "imlaei_simple" VALUES(2,167,'2:167','وقال الذين اتبعوا لو ان لنا كرة فنتبرا منهم كما تبرءوا منا كذلك يريهم الله اعمالهم حسرات عليهم وما هم بخارجين من النار');
INSERT INTO "imlaei_simple" VALUES(2,168,'2:168','يا ايها الناس كلوا مما في الارض حلالا طيبا ولا تتبعوا خطوات الشيطان انه لكم عدو مبين');
INSERT INTO "imlaei_simple" VALUES(2,169,'2:169','انما يامركم بالسوء والفحشاء وان تقولوا على الله ما لا تعلمون');
INSERT INTO "imlaei_simple" VALUES(2,170,'2:170','واذا قيل لهم اتبعوا ما انزل الله قالوا بل نتبع ما الفينا عليه اباءنا اولو كان اباؤهم لا يعقلون شيئا ولا يهتدون');
INSERT INTO "imlaei_simple" VALUES(2,171,'2:171','ومثل الذين كفروا كمثل الذي ينعق بما لا يسمع الا دعاء ونداء صم بكم عمي فهم لا يعقلون');
INSERT INTO "imlaei_simple" VALUES(2,172,'2:172','يا ايها الذين امنوا كلوا من طيبات ما رزقناكم واشكروا لله ان كنتم اياه تعبدون');
INSERT INTO "imlaei_simple" VALUES(2,173,'2:173','انما حرم عليكم الميتة والدم ولحم الخنزير وما اهل به لغير الله فمن اضطر غير باغ ولا عاد فلا اثم عليه ان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(2,174,'2:174','ان الذين يكتمون ما انزل الله من الكتاب ويشترون به ثمنا قليلا اولئك ما ياكلون في بطونهم الا النار ولا يكلمهم الله يوم القيامة ولا يزكيهم ولهم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(2,175,'2:175','اولئك الذين اشتروا الضلالة بالهدى والعذاب بالمغفرة فما اصبرهم على النار');
INSERT INTO "imlaei_simple" VALUES(2,176,'2:176','ذلك بان الله نزل الكتاب بالحق وان الذين اختلفوا في الكتاب لفي شقاق بعيد');
INSERT INTO "imlaei_simple" VALUES(2,177,'2:177','ليس البر ان تولوا وجوهكم قبل المشرق والمغرب ولكن البر من امن بالله واليوم الاخر والملائكة والكتاب والنبيين واتى المال على حبه ذوي القربى واليتامى والمساكين وابن السبيل والسائلين وفي الرقاب واقام الصلاة واتى الزكاة والموفون بعهدهم اذا عاهدوا والصابرين في الباساء والضراء وحين الباس اولئك الذين صدقوا واولئك هم المتقون');
INSERT INTO "imlaei_simple" VALUES(2,178,'2:178','يا ايها الذين امنوا كتب عليكم القصاص في القتلى الحر بالحر والعبد بالعبد والانثى بالانثى فمن عفي له من اخيه شيء فاتباع بالمعروف واداء اليه باحسان ذلك تخفيف من ربكم ورحمة فمن اعتدى بعد ذلك فله عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(2,179,'2:179','ولكم في القصاص حياة يا اولي الالباب لعلكم تتقون');
INSERT INTO "imlaei_simple" VALUES(2,180,'2:180','كتب عليكم اذا حضر احدكم الموت ان ترك خيرا الوصية للوالدين والاقربين بالمعروف حقا على المتقين');
INSERT INTO "imlaei_simple" VALUES(2,181,'2:181','فمن بدله بعدما سمعه فانما اثمه على الذين يبدلونه ان الله سميع عليم');
INSERT INTO "imlaei_simple" VALUES(2,182,'2:182','فمن خاف من موص جنفا او اثما فاصلح بينهم فلا اثم عليه ان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(2,183,'2:183','يا ايها الذين امنوا كتب عليكم الصيام كما كتب على الذين من قبلكم لعلكم تتقون');
INSERT INTO "imlaei_simple" VALUES(2,184,'2:184','اياما معدودات فمن كان منكم مريضا او على سفر فعدة من ايام اخر وعلى الذين يطيقونه فدية طعام مسكين فمن تطوع خيرا فهو خير له وان تصوموا خير لكم ان كنتم تعلمون');
INSERT INTO "imlaei_simple" VALUES(2,185,'2:185','شهر رمضان الذي انزل فيه القران هدى للناس وبينات من الهدى والفرقان فمن شهد منكم الشهر فليصمه ومن كان مريضا او على سفر فعدة من ايام اخر يريد الله بكم اليسر ولا يريد بكم العسر ولتكملوا العدة ولتكبروا الله على ما هداكم ولعلكم تشكرون');
INSERT INTO "imlaei_simple" VALUES(2,186,'2:186','واذا سالك عبادي عني فاني قريب اجيب دعوة الداع اذا دعان فليستجيبوا لي وليؤمنوا بي لعلهم يرشدون');
INSERT INTO "imlaei_simple" VALUES(2,187,'2:187','احل لكم ليلة الصيام الرفث الى نسائكم هن لباس لكم وانتم لباس لهن علم الله انكم كنتم تختانون انفسكم فتاب عليكم وعفا عنكم فالان باشروهن وابتغوا ما كتب الله لكم وكلوا واشربوا حتى يتبين لكم الخيط الابيض من الخيط الاسود من الفجر ثم اتموا الصيام الى الليل ولا تباشروهن وانتم عاكفون في المساجد تلك حدود الله فلا تقربوها كذلك يبين الله اياته للناس لعلهم يتقون');
INSERT INTO "imlaei_simple" VALUES(2,188,'2:188','ولا تاكلوا اموالكم بينكم بالباطل وتدلوا بها الى الحكام لتاكلوا فريقا من اموال الناس بالاثم وانتم تعلمون');
INSERT INTO "imlaei_simple" VALUES(2,189,'2:189','يسالونك عن الاهلة قل هي مواقيت للناس والحج وليس البر بان تاتوا البيوت من ظهورها ولكن البر من اتقى واتوا البيوت من ابوابها واتقوا الله لعلكم تفلحون');
INSERT INTO "imlaei_simple" VALUES(2,190,'2:190','وقاتلوا في سبيل الله الذين يقاتلونكم ولا تعتدوا ان الله لا يحب المعتدين');
INSERT INTO "imlaei_simple" VALUES(2,191,'2:191','واقتلوهم حيث ثقفتموهم واخرجوهم من حيث اخرجوكم والفتنة اشد من القتل ولا تقاتلوهم عند المسجد الحرام حتى يقاتلوكم فيه فان قاتلوكم فاقتلوهم كذلك جزاء الكافرين');
INSERT INTO "imlaei_simple" VALUES(2,192,'2:192','فان انتهوا فان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(2,193,'2:193','وقاتلوهم حتى لا تكون فتنة ويكون الدين لله فان انتهوا فلا عدوان الا على الظالمين');
INSERT INTO "imlaei_simple" VALUES(2,194,'2:194','الشهر الحرام بالشهر الحرام والحرمات قصاص فمن اعتدى عليكم فاعتدوا عليه بمثل ما اعتدى عليكم واتقوا الله واعلموا ان الله مع المتقين');
INSERT INTO "imlaei_simple" VALUES(2,195,'2:195','وانفقوا في سبيل الله ولا تلقوا بايديكم الى التهلكة واحسنوا ان الله يحب المحسنين');
INSERT INTO "imlaei_simple" VALUES(2,196,'2:196','واتموا الحج والعمرة لله فان احصرتم فما استيسر من الهدي ولا تحلقوا رءوسكم حتى يبلغ الهدي محله فمن كان منكم مريضا او به اذى من راسه ففدية من صيام او صدقة او نسك فاذا امنتم فمن تمتع بالعمرة الى الحج فما استيسر من الهدي فمن لم يجد فصيام ثلاثة ايام في الحج وسبعة اذا رجعتم تلك عشرة كاملة ذلك لمن لم يكن اهله حاضري المسجد الحرام واتقوا الله واعلموا ان الله شديد العقاب');
INSERT INTO "imlaei_simple" VALUES(2,197,'2:197','الحج اشهر معلومات فمن فرض فيهن الحج فلا رفث ولا فسوق ولا جدال في الحج وما تفعلوا من خير يعلمه الله وتزودوا فان خير الزاد التقوى واتقون يا اولي الالباب');
INSERT INTO "imlaei_simple" VALUES(2,198,'2:198','ليس عليكم جناح ان تبتغوا فضلا من ربكم فاذا افضتم من عرفات فاذكروا الله عند المشعر الحرام واذكروه كما هداكم وان كنتم من قبله لمن الضالين');
INSERT INTO "imlaei_simple" VALUES(2,199,'2:199','ثم افيضوا من حيث افاض الناس واستغفروا الله ان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(2,200,'2:200','فاذا قضيتم مناسككم فاذكروا الله كذكركم اباءكم او اشد ذكرا فمن الناس من يقول ربنا اتنا في الدنيا وما له في الاخرة من خلاق');
INSERT INTO "imlaei_simple" VALUES(2,201,'2:201','ومنهم من يقول ربنا اتنا في الدنيا حسنة وفي الاخرة حسنة وقنا عذاب النار');
INSERT INTO "imlaei_simple" VALUES(2,202,'2:202','اولئك لهم نصيب مما كسبوا والله سريع الحساب');
INSERT INTO "imlaei_simple" VALUES(2,203,'2:203','واذكروا الله في ايام معدودات فمن تعجل في يومين فلا اثم عليه ومن تاخر فلا اثم عليه لمن اتقى واتقوا الله واعلموا انكم اليه تحشرون');
INSERT INTO "imlaei_simple" VALUES(2,204,'2:204','ومن الناس من يعجبك قوله في الحياة الدنيا ويشهد الله على ما في قلبه وهو الد الخصام');
INSERT INTO "imlaei_simple" VALUES(2,205,'2:205','واذا تولى سعى في الارض ليفسد فيها ويهلك الحرث والنسل والله لا يحب الفساد');
INSERT INTO "imlaei_simple" VALUES(2,206,'2:206','واذا قيل له اتق الله اخذته العزة بالاثم فحسبه جهنم ولبئس المهاد');
INSERT INTO "imlaei_simple" VALUES(2,207,'2:207','ومن الناس من يشري نفسه ابتغاء مرضات الله والله رءوف بالعباد');
INSERT INTO "imlaei_simple" VALUES(2,208,'2:208','يا ايها الذين امنوا ادخلوا في السلم كافة ولا تتبعوا خطوات الشيطان انه لكم عدو مبين');
INSERT INTO "imlaei_simple" VALUES(2,209,'2:209','فان زللتم من بعد ما جاءتكم البينات فاعلموا ان الله عزيز حكيم');
INSERT INTO "imlaei_simple" VALUES(2,210,'2:210','هل ينظرون الا ان ياتيهم الله في ظلل من الغمام والملائكة وقضي الامر والى الله ترجع الامور');
INSERT INTO "imlaei_simple" VALUES(2,211,'2:211','سل بني اسرائيل كم اتيناهم من اية بينة ومن يبدل نعمة الله من بعد ما جاءته فان الله شديد العقاب');
INSERT INTO "imlaei_simple" VALUES(2,212,'2:212','زين للذين كفروا الحياة الدنيا ويسخرون من الذين امنوا والذين اتقوا فوقهم يوم القيامة والله يرزق من يشاء بغير حساب');
INSERT INTO "imlaei_simple" VALUES(2,213,'2:213','كان الناس امة واحدة فبعث الله النبيين مبشرين ومنذرين وانزل معهم الكتاب بالحق ليحكم بين الناس فيما اختلفوا فيه وما اختلف فيه الا الذين اوتوه من بعد ما جاءتهم البينات بغيا بينهم فهدى الله الذين امنوا لما اختلفوا فيه من الحق باذنه والله يهدي من يشاء الى صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(2,214,'2:214','ام حسبتم ان تدخلوا الجنة ولما ياتكم مثل الذين خلوا من قبلكم مستهم الباساء والضراء وزلزلوا حتى يقول الرسول والذين امنوا معه متى نصر الله الا ان نصر الله قريب');
INSERT INTO "imlaei_simple" VALUES(2,215,'2:215','يسالونك ماذا ينفقون قل ما انفقتم من خير فللوالدين والاقربين واليتامى والمساكين وابن السبيل وما تفعلوا من خير فان الله به عليم');
INSERT INTO "imlaei_simple" VALUES(2,216,'2:216','كتب عليكم القتال وهو كره لكم وعسى ان تكرهوا شيئا وهو خير لكم وعسى ان تحبوا شيئا وهو شر لكم والله يعلم وانتم لا تعلمون');
INSERT INTO "imlaei_simple" VALUES(2,217,'2:217','يسالونك عن الشهر الحرام قتال فيه قل قتال فيه كبير وصد عن سبيل الله وكفر به والمسجد الحرام واخراج اهله منه اكبر عند الله والفتنة اكبر من القتل ولا يزالون يقاتلونكم حتى يردوكم عن دينكم ان استطاعوا ومن يرتدد منكم عن دينه فيمت وهو كافر فاولئك حبطت اعمالهم في الدنيا والاخرة واولئك اصحاب النار هم فيها خالدون');
INSERT INTO "imlaei_simple" VALUES(2,218,'2:218','ان الذين امنوا والذين هاجروا وجاهدوا في سبيل الله اولئك يرجون رحمت الله والله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(2,219,'2:219','يسالونك عن الخمر والميسر قل فيهما اثم كبير ومنافع للناس واثمهما اكبر من نفعهما ويسالونك ماذا ينفقون قل العفو كذلك يبين الله لكم الايات لعلكم تتفكرون');
INSERT INTO "imlaei_simple" VALUES(2,220,'2:220','في الدنيا والاخرة ويسالونك عن اليتامى قل اصلاح لهم خير وان تخالطوهم فاخوانكم والله يعلم المفسد من المصلح ولو شاء الله لاعنتكم ان الله عزيز حكيم');
INSERT INTO "imlaei_simple" VALUES(2,221,'2:221','ولا تنكحوا المشركات حتى يؤمن ولامة مؤمنة خير من مشركة ولو اعجبتكم ولا تنكحوا المشركين حتى يؤمنوا ولعبد مؤمن خير من مشرك ولو اعجبكم اولئك يدعون الى النار والله يدعو الى الجنة والمغفرة باذنه ويبين اياته للناس لعلهم يتذكرون');
INSERT INTO "imlaei_simple" VALUES(2,222,'2:222','ويسالونك عن المحيض قل هو اذى فاعتزلوا النساء في المحيض ولا تقربوهن حتى يطهرن فاذا تطهرن فاتوهن من حيث امركم الله ان الله يحب التوابين ويحب المتطهرين');
INSERT INTO "imlaei_simple" VALUES(2,223,'2:223','نساؤكم حرث لكم فاتوا حرثكم انى شئتم وقدموا لانفسكم واتقوا الله واعلموا انكم ملاقوه وبشر المؤمنين');
INSERT INTO "imlaei_simple" VALUES(2,224,'2:224','ولا تجعلوا الله عرضة لايمانكم ان تبروا وتتقوا وتصلحوا بين الناس والله سميع عليم');
INSERT INTO "imlaei_simple" VALUES(2,225,'2:225','لا يؤاخذكم الله باللغو في ايمانكم ولكن يؤاخذكم بما كسبت قلوبكم والله غفور حليم');
INSERT INTO "imlaei_simple" VALUES(2,226,'2:226','للذين يؤلون من نسائهم تربص اربعة اشهر فان فاءوا فان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(2,227,'2:227','وان عزموا الطلاق فان الله سميع عليم');
INSERT INTO "imlaei_simple" VALUES(2,228,'2:228','والمطلقات يتربصن بانفسهن ثلاثة قروء ولا يحل لهن ان يكتمن ما خلق الله في ارحامهن ان كن يؤمن بالله واليوم الاخر وبعولتهن احق بردهن في ذلك ان ارادوا اصلاحا ولهن مثل الذي عليهن بالمعروف وللرجال عليهن درجة والله عزيز حكيم');
INSERT INTO "imlaei_simple" VALUES(2,229,'2:229','الطلاق مرتان فامساك بمعروف او تسريح باحسان ولا يحل لكم ان تاخذوا مما اتيتموهن شيئا الا ان يخافا الا يقيما حدود الله فان خفتم الا يقيما حدود الله فلا جناح عليهما فيما افتدت به تلك حدود الله فلا تعتدوها ومن يتعد حدود الله فاولئك هم الظالمون');
INSERT INTO "imlaei_simple" VALUES(2,230,'2:230','فان طلقها فلا تحل له من بعد حتى تنكح زوجا غيره فان طلقها فلا جناح عليهما ان يتراجعا ان ظنا ان يقيما حدود الله وتلك حدود الله يبينها لقوم يعلمون');
INSERT INTO "imlaei_simple" VALUES(2,231,'2:231','واذا طلقتم النساء فبلغن اجلهن فامسكوهن بمعروف او سرحوهن بمعروف ولا تمسكوهن ضرارا لتعتدوا ومن يفعل ذلك فقد ظلم نفسه ولا تتخذوا ايات الله هزوا واذكروا نعمت الله عليكم وما انزل عليكم من الكتاب والحكمة يعظكم به واتقوا الله واعلموا ان الله بكل شيء عليم');
INSERT INTO "imlaei_simple" VALUES(2,232,'2:232','واذا طلقتم النساء فبلغن اجلهن فلا تعضلوهن ان ينكحن ازواجهن اذا تراضوا بينهم بالمعروف ذلك يوعظ به من كان منكم يؤمن بالله واليوم الاخر ذلكم ازكى لكم واطهر والله يعلم وانتم لا تعلمون');
INSERT INTO "imlaei_simple" VALUES(2,233,'2:233','والوالدات يرضعن اولادهن حولين كاملين لمن اراد ان يتم الرضاعة وعلى المولود له رزقهن وكسوتهن بالمعروف لا تكلف نفس الا وسعها لا تضار والدة بولدها ولا مولود له بولده وعلى الوارث مثل ذلك فان ارادا فصالا عن تراض منهما وتشاور فلا جناح عليهما وان اردتم ان تسترضعوا اولادكم فلا جناح عليكم اذا سلمتم ما اتيتم بالمعروف واتقوا الله واعلموا ان الله بما تعملون بصير');
INSERT INTO "imlaei_simple" VALUES(2,234,'2:234','والذين يتوفون منكم ويذرون ازواجا يتربصن بانفسهن اربعة اشهر وعشرا فاذا بلغن اجلهن فلا جناح عليكم فيما فعلن في انفسهن بالمعروف والله بما تعملون خبير');
INSERT INTO "imlaei_simple" VALUES(2,235,'2:235','ولا جناح عليكم فيما عرضتم به من خطبة النساء او اكننتم في انفسكم علم الله انكم ستذكرونهن ولكن لا تواعدوهن سرا الا ان تقولوا قولا معروفا ولا تعزموا عقدة النكاح حتى يبلغ الكتاب اجله واعلموا ان الله يعلم ما في انفسكم فاحذروه واعلموا ان الله غفور حليم');
INSERT INTO "imlaei_simple" VALUES(2,236,'2:236','لا جناح عليكم ان طلقتم النساء ما لم تمسوهن او تفرضوا لهن فريضة ومتعوهن على الموسع قدره وعلى المقتر قدره متاعا بالمعروف حقا على المحسنين');
INSERT INTO "imlaei_simple" VALUES(2,237,'2:237','وان طلقتموهن من قبل ان تمسوهن وقد فرضتم لهن فريضة فنصف ما فرضتم الا ان يعفون او يعفو الذي بيده عقدة النكاح وان تعفوا اقرب للتقوى ولا تنسوا الفضل بينكم ان الله بما تعملون بصير');
INSERT INTO "imlaei_simple" VALUES(2,238,'2:238','حافظوا على الصلوات والصلاة الوسطى وقوموا لله قانتين');
INSERT INTO "imlaei_simple" VALUES(2,239,'2:239','فان خفتم فرجالا او ركبانا فاذا امنتم فاذكروا الله كما علمكم ما لم تكونوا تعلمون');
INSERT INTO "imlaei_simple" VALUES(2,240,'2:240','والذين يتوفون منكم ويذرون ازواجا وصية لازواجهم متاعا الى الحول غير اخراج فان خرجن فلا جناح عليكم في ما فعلن في انفسهن من معروف والله عزيز حكيم');
INSERT INTO "imlaei_simple" VALUES(2,241,'2:241','وللمطلقات متاع بالمعروف حقا على المتقين');
INSERT INTO "imlaei_simple" VALUES(2,242,'2:242','كذلك يبين الله لكم اياته لعلكم تعقلون');
INSERT INTO "imlaei_simple" VALUES(2,243,'2:243','الم تر الى الذين خرجوا من ديارهم وهم الوف حذر الموت فقال لهم الله موتوا ثم احياهم ان الله لذو فضل على الناس ولكن اكثر الناس لا يشكرون');
INSERT INTO "imlaei_simple" VALUES(2,244,'2:244','وقاتلوا في سبيل الله واعلموا ان الله سميع عليم');
INSERT INTO "imlaei_simple" VALUES(2,245,'2:245','من ذا الذي يقرض الله قرضا حسنا فيضاعفه له اضعافا كثيرة والله يقبض ويبسط واليه ترجعون');
INSERT INTO "imlaei_simple" VALUES(2,246,'2:246','الم تر الى الملا من بني اسرائيل من بعد موسى اذ قالوا لنبي لهم ابعث لنا ملكا نقاتل في سبيل الله قال هل عسيتم ان كتب عليكم القتال الا تقاتلوا قالوا وما لنا الا نقاتل في سبيل الله وقد اخرجنا من ديارنا وابنائنا فلما كتب عليهم القتال تولوا الا قليلا منهم والله عليم بالظالمين');
INSERT INTO "imlaei_simple" VALUES(2,247,'2:247','وقال لهم نبيهم ان الله قد بعث لكم طالوت ملكا قالوا انى يكون له الملك علينا ونحن احق بالملك منه ولم يؤت سعة من المال قال ان الله اصطفاه عليكم وزاده بسطة في العلم والجسم والله يؤتي ملكه من يشاء والله واسع عليم');
INSERT INTO "imlaei_simple" VALUES(2,248,'2:248','وقال لهم نبيهم ان اية ملكه ان ياتيكم التابوت فيه سكينة من ربكم وبقية مما ترك ال موسى وال هارون تحمله الملائكة ان في ذلك لاية لكم ان كنتم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(2,249,'2:249','فلما فصل طالوت بالجنود قال ان الله مبتليكم بنهر فمن شرب منه فليس مني ومن لم يطعمه فانه مني الا من اغترف غرفة بيده فشربوا منه الا قليلا منهم فلما جاوزه هو والذين امنوا معه قالوا لا طاقة لنا اليوم بجالوت وجنوده قال الذين يظنون انهم ملاقو الله كم من فئة قليلة غلبت فئة كثيرة باذن الله والله مع الصابرين');
INSERT INTO "imlaei_simple" VALUES(2,250,'2:250','ولما برزوا لجالوت وجنوده قالوا ربنا افرغ علينا صبرا وثبت اقدامنا وانصرنا على القوم الكافرين');
INSERT INTO "imlaei_simple" VALUES(2,251,'2:251','فهزموهم باذن الله وقتل داوود جالوت واتاه الله الملك والحكمة وعلمه مما يشاء ولولا دفع الله الناس بعضهم ببعض لفسدت الارض ولكن الله ذو فضل على العالمين');
INSERT INTO "imlaei_simple" VALUES(2,252,'2:252','تلك ايات الله نتلوها عليك بالحق وانك لمن المرسلين');
INSERT INTO "imlaei_simple" VALUES(2,253,'2:253','تلك الرسل فضلنا بعضهم على بعض منهم من كلم الله ورفع بعضهم درجات واتينا عيسى ابن مريم البينات وايدناه بروح القدس ولو شاء الله ما اقتتل الذين من بعدهم من بعد ما جاءتهم البينات ولكن اختلفوا فمنهم من امن ومنهم من كفر ولو شاء الله ما اقتتلوا ولكن الله يفعل ما يريد');
INSERT INTO "imlaei_simple" VALUES(2,254,'2:254','يا ايها الذين امنوا انفقوا مما رزقناكم من قبل ان ياتي يوم لا بيع فيه ولا خلة ولا شفاعة والكافرون هم الظالمون');
INSERT INTO "imlaei_simple" VALUES(2,255,'2:255','الله لا اله الا هو الحي القيوم لا تاخذه سنة ولا نوم له ما في السماوات وما في الارض من ذا الذي يشفع عنده الا باذنه يعلم ما بين ايديهم وما خلفهم ولا يحيطون بشيء من علمه الا بما شاء وسع كرسيه السماوات والارض ولا يئوده حفظهما وهو العلي العظيم');
INSERT INTO "imlaei_simple" VALUES(2,256,'2:256','لا اكراه في الدين قد تبين الرشد من الغي فمن يكفر بالطاغوت ويؤمن بالله فقد استمسك بالعروة الوثقى لا انفصام لها والله سميع عليم');
INSERT INTO "imlaei_simple" VALUES(2,257,'2:257','الله ولي الذين امنوا يخرجهم من الظلمات الى النور والذين كفروا اولياؤهم الطاغوت يخرجونهم من النور الى الظلمات اولئك اصحاب النار هم فيها خالدون');
INSERT INTO "imlaei_simple" VALUES(2,258,'2:258','الم تر الى الذي حاج ابراهيم في ربه ان اتاه الله الملك اذ قال ابراهيم ربي الذي يحيي ويميت قال انا احيي واميت قال ابراهيم فان الله ياتي بالشمس من المشرق فات بها من المغرب فبهت الذي كفر والله لا يهدي القوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(2,259,'2:259','او كالذي مر على قرية وهي خاوية على عروشها قال انى يحيي هذه الله بعد موتها فاماته الله مائة عام ثم بعثه قال كم لبثت قال لبثت يوما او بعض يوم قال بل لبثت مائة عام فانظر الى طعامك وشرابك لم يتسنه وانظر الى حمارك ولنجعلك اية للناس وانظر الى العظام كيف ننشزها ثم نكسوها لحما فلما تبين له قال اعلم ان الله على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(2,260,'2:260','واذ قال ابراهيم رب ارني كيف تحيي الموتى قال اولم تؤمن قال بلى ولكن ليطمئن قلبي قال فخذ اربعة من الطير فصرهن اليك ثم اجعل على كل جبل منهن جزءا ثم ادعهن ياتينك سعيا واعلم ان الله عزيز حكيم');
INSERT INTO "imlaei_simple" VALUES(2,261,'2:261','مثل الذين ينفقون اموالهم في سبيل الله كمثل حبة انبتت سبع سنابل في كل سنبلة مائة حبة والله يضاعف لمن يشاء والله واسع عليم');
INSERT INTO "imlaei_simple" VALUES(2,262,'2:262','الذين ينفقون اموالهم في سبيل الله ثم لا يتبعون ما انفقوا منا ولا اذى لهم اجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون');
INSERT INTO "imlaei_simple" VALUES(2,263,'2:263','قول معروف ومغفرة خير من صدقة يتبعها اذى والله غني حليم');
INSERT INTO "imlaei_simple" VALUES(2,264,'2:264','يا ايها الذين امنوا لا تبطلوا صدقاتكم بالمن والاذى كالذي ينفق ماله رئاء الناس ولا يؤمن بالله واليوم الاخر فمثله كمثل صفوان عليه تراب فاصابه وابل فتركه صلدا لا يقدرون على شيء مما كسبوا والله لا يهدي القوم الكافرين');
INSERT INTO "imlaei_simple" VALUES(2,265,'2:265','ومثل الذين ينفقون اموالهم ابتغاء مرضات الله وتثبيتا من انفسهم كمثل جنة بربوة اصابها وابل فاتت اكلها ضعفين فان لم يصبها وابل فطل والله بما تعملون بصير');
INSERT INTO "imlaei_simple" VALUES(2,266,'2:266','ايود احدكم ان تكون له جنة من نخيل واعناب تجري من تحتها الانهار له فيها من كل الثمرات واصابه الكبر وله ذرية ضعفاء فاصابها اعصار فيه نار فاحترقت كذلك يبين الله لكم الايات لعلكم تتفكرون');
INSERT INTO "imlaei_simple" VALUES(2,267,'2:267','يا ايها الذين امنوا انفقوا من طيبات ما كسبتم ومما اخرجنا لكم من الارض ولا تيمموا الخبيث منه تنفقون ولستم باخذيه الا ان تغمضوا فيه واعلموا ان الله غني حميد');
INSERT INTO "imlaei_simple" VALUES(2,268,'2:268','الشيطان يعدكم الفقر ويامركم بالفحشاء والله يعدكم مغفرة منه وفضلا والله واسع عليم');
INSERT INTO "imlaei_simple" VALUES(2,269,'2:269','يؤتي الحكمة من يشاء ومن يؤت الحكمة فقد اوتي خيرا كثيرا وما يذكر الا اولو الالباب');
INSERT INTO "imlaei_simple" VALUES(2,270,'2:270','وما انفقتم من نفقة او نذرتم من نذر فان الله يعلمه وما للظالمين من انصار');
INSERT INTO "imlaei_simple" VALUES(2,271,'2:271','ان تبدوا الصدقات فنعما هي وان تخفوها وتؤتوها الفقراء فهو خير لكم ويكفر عنكم من سيئاتكم والله بما تعملون خبير');
INSERT INTO "imlaei_simple" VALUES(2,272,'2:272','ليس عليك هداهم ولكن الله يهدي من يشاء وما تنفقوا من خير فلانفسكم وما تنفقون الا ابتغاء وجه الله وما تنفقوا من خير يوف اليكم وانتم لا تظلمون');
INSERT INTO "imlaei_simple" VALUES(2,273,'2:273','للفقراء الذين احصروا في سبيل الله لا يستطيعون ضربا في الارض يحسبهم الجاهل اغنياء من التعفف تعرفهم بسيماهم لا يسالون الناس الحافا وما تنفقوا من خير فان الله به عليم');
INSERT INTO "imlaei_simple" VALUES(2,274,'2:274','الذين ينفقون اموالهم بالليل والنهار سرا وعلانية فلهم اجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون');
INSERT INTO "imlaei_simple" VALUES(2,275,'2:275','الذين ياكلون الربا لا يقومون الا كما يقوم الذي يتخبطه الشيطان من المس ذلك بانهم قالوا انما البيع مثل الربا واحل الله البيع وحرم الربا فمن جاءه موعظة من ربه فانتهى فله ما سلف وامره الى الله ومن عاد فاولئك اصحاب النار هم فيها خالدون');
INSERT INTO "imlaei_simple" VALUES(2,276,'2:276','يمحق الله الربا ويربي الصدقات والله لا يحب كل كفار اثيم');
INSERT INTO "imlaei_simple" VALUES(2,277,'2:277','ان الذين امنوا وعملوا الصالحات واقاموا الصلاة واتوا الزكاة لهم اجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون');
INSERT INTO "imlaei_simple" VALUES(2,278,'2:278','يا ايها الذين امنوا اتقوا الله وذروا ما بقي من الربا ان كنتم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(2,279,'2:279','فان لم تفعلوا فاذنوا بحرب من الله ورسوله وان تبتم فلكم رءوس اموالكم لا تظلمون ولا تظلمون');
INSERT INTO "imlaei_simple" VALUES(2,280,'2:280','وان كان ذو عسرة فنظرة الى ميسرة وان تصدقوا خير لكم ان كنتم تعلمون');
INSERT INTO "imlaei_simple" VALUES(2,281,'2:281','واتقوا يوما ترجعون فيه الى الله ثم توفى كل نفس ما كسبت وهم لا يظلمون');
INSERT INTO "imlaei_simple" VALUES(2,282,'2:282','يا ايها الذين امنوا اذا تداينتم بدين الى اجل مسمى فاكتبوه وليكتب بينكم كاتب بالعدل ولا ياب كاتب ان يكتب كما علمه الله فليكتب وليملل الذي عليه الحق وليتق الله ربه ولا يبخس منه شيئا فان كان الذي عليه الحق سفيها او ضعيفا او لا يستطيع ان يمل هو فليملل وليه بالعدل واستشهدوا شهيدين من رجالكم فان لم يكونا رجلين فرجل وامراتان ممن ترضون من الشهداء ان تضل احداهما فتذكر احداهما الاخرى ولا ياب الشهداء اذا ما دعوا ولا تساموا ان تكتبوه صغيرا او كبيرا الى اجله ذلكم اقسط عند الله واقوم للشهادة وادنى الا ترتابوا الا ان تكون تجارة حاضرة تديرونها بينكم فليس عليكم جناح الا تكتبوها واشهدوا اذا تبايعتم ولا يضار كاتب ولا شهيد وان تفعلوا فانه فسوق بكم واتقوا الله ويعلمكم الله والله بكل شيء عليم');
INSERT INTO "imlaei_simple" VALUES(2,283,'2:283','وان كنتم على سفر ولم تجدوا كاتبا فرهان مقبوضة فان امن بعضكم بعضا فليؤد الذي اؤتمن امانته وليتق الله ربه ولا تكتموا الشهادة ومن يكتمها فانه اثم قلبه والله بما تعملون عليم');
INSERT INTO "imlaei_simple" VALUES(2,284,'2:284','لله ما في السماوات وما في الارض وان تبدوا ما في انفسكم او تخفوه يحاسبكم به الله فيغفر لمن يشاء ويعذب من يشاء والله على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(2,285,'2:285','امن الرسول بما انزل اليه من ربه والمؤمنون كل امن بالله وملائكته وكتبه ورسله لا نفرق بين احد من رسله وقالوا سمعنا واطعنا غفرانك ربنا واليك المصير');
INSERT INTO "imlaei_simple" VALUES(2,286,'2:286','لا يكلف الله نفسا الا وسعها لها ما كسبت وعليها ما اكتسبت ربنا لا تؤاخذنا ان نسينا او اخطانا ربنا ولا تحمل علينا اصرا كما حملته على الذين من قبلنا ربنا ولا تحملنا ما لا طاقة لنا به واعف عنا واغفر لنا وارحمنا انت مولانا فانصرنا على القوم الكافرين');
INSERT INTO "imlaei_simple" VALUES(3,1,'3:1','الم');
INSERT INTO "imlaei_simple" VALUES(3,2,'3:2','الله لا اله الا هو الحي القيوم');
INSERT INTO "imlaei_simple" VALUES(3,3,'3:3','نزل عليك الكتاب بالحق مصدقا لما بين يديه وانزل التوراة والانجيل');
INSERT INTO "imlaei_simple" VALUES(3,4,'3:4','من قبل هدى للناس وانزل الفرقان ان الذين كفروا بايات الله لهم عذاب شديد والله عزيز ذو انتقام');
INSERT INTO "imlaei_simple" VALUES(3,5,'3:5','ان الله لا يخفى عليه شيء في الارض ولا في السماء');
INSERT INTO "imlaei_simple" VALUES(3,6,'3:6','هو الذي يصوركم في الارحام كيف يشاء لا اله الا هو العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(3,7,'3:7','هو الذي انزل عليك الكتاب منه ايات محكمات هن ام الكتاب واخر متشابهات فاما الذين في قلوبهم زيغ فيتبعون ما تشابه منه ابتغاء الفتنة وابتغاء تاويله وما يعلم تاويله الا الله والراسخون في العلم يقولون امنا به كل من عند ربنا وما يذكر الا اولو الالباب');
INSERT INTO "imlaei_simple" VALUES(3,8,'3:8','ربنا لا تزغ قلوبنا بعد اذ هديتنا وهب لنا من لدنك رحمة انك انت الوهاب');
INSERT INTO "imlaei_simple" VALUES(3,9,'3:9','ربنا انك جامع الناس ليوم لا ريب فيه ان الله لا يخلف الميعاد');
INSERT INTO "imlaei_simple" VALUES(3,10,'3:10','ان الذين كفروا لن تغني عنهم اموالهم ولا اولادهم من الله شيئا واولئك هم وقود النار');
INSERT INTO "imlaei_simple" VALUES(3,11,'3:11','كداب ال فرعون والذين من قبلهم كذبوا باياتنا فاخذهم الله بذنوبهم والله شديد العقاب');
INSERT INTO "imlaei_simple" VALUES(3,12,'3:12','قل للذين كفروا ستغلبون وتحشرون الى جهنم وبئس المهاد');
INSERT INTO "imlaei_simple" VALUES(3,13,'3:13','قد كان لكم اية في فئتين التقتا فئة تقاتل في سبيل الله واخرى كافرة يرونهم مثليهم راي العين والله يؤيد بنصره من يشاء ان في ذلك لعبرة لاولي الابصار');
INSERT INTO "imlaei_simple" VALUES(3,14,'3:14','زين للناس حب الشهوات من النساء والبنين والقناطير المقنطرة من الذهب والفضة والخيل المسومة والانعام والحرث ذلك متاع الحياة الدنيا والله عنده حسن الماب');
INSERT INTO "imlaei_simple" VALUES(3,15,'3:15','قل اؤنبئكم بخير من ذلكم للذين اتقوا عند ربهم جنات تجري من تحتها الانهار خالدين فيها وازواج مطهرة ورضوان من الله والله بصير بالعباد');
INSERT INTO "imlaei_simple" VALUES(3,16,'3:16','الذين يقولون ربنا اننا امنا فاغفر لنا ذنوبنا وقنا عذاب النار');
INSERT INTO "imlaei_simple" VALUES(3,17,'3:17','الصابرين والصادقين والقانتين والمنفقين والمستغفرين بالاسحار');
INSERT INTO "imlaei_simple" VALUES(3,18,'3:18','شهد الله انه لا اله الا هو والملائكة واولو العلم قائما بالقسط لا اله الا هو العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(3,19,'3:19','ان الدين عند الله الاسلام وما اختلف الذين اوتوا الكتاب الا من بعد ما جاءهم العلم بغيا بينهم ومن يكفر بايات الله فان الله سريع الحساب');
INSERT INTO "imlaei_simple" VALUES(3,20,'3:20','فان حاجوك فقل اسلمت وجهي لله ومن اتبعن وقل للذين اوتوا الكتاب والاميين ااسلمتم فان اسلموا فقد اهتدوا وان تولوا فانما عليك البلاغ والله بصير بالعباد');
INSERT INTO "imlaei_simple" VALUES(3,21,'3:21','ان الذين يكفرون بايات الله ويقتلون النبيين بغير حق ويقتلون الذين يامرون بالقسط من الناس فبشرهم بعذاب اليم');
INSERT INTO "imlaei_simple" VALUES(3,22,'3:22','اولئك الذين حبطت اعمالهم في الدنيا والاخرة وما لهم من ناصرين');
INSERT INTO "imlaei_simple" VALUES(3,23,'3:23','الم تر الى الذين اوتوا نصيبا من الكتاب يدعون الى كتاب الله ليحكم بينهم ثم يتولى فريق منهم وهم معرضون');
INSERT INTO "imlaei_simple" VALUES(3,24,'3:24','ذلك بانهم قالوا لن تمسنا النار الا اياما معدودات وغرهم في دينهم ما كانوا يفترون');
INSERT INTO "imlaei_simple" VALUES(3,25,'3:25','فكيف اذا جمعناهم ليوم لا ريب فيه ووفيت كل نفس ما كسبت وهم لا يظلمون');
INSERT INTO "imlaei_simple" VALUES(3,26,'3:26','قل اللهم مالك الملك تؤتي الملك من تشاء وتنزع الملك ممن تشاء وتعز من تشاء وتذل من تشاء بيدك الخير انك على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(3,27,'3:27','تولج الليل في النهار وتولج النهار في الليل وتخرج الحي من الميت وتخرج الميت من الحي وترزق من تشاء بغير حساب');
INSERT INTO "imlaei_simple" VALUES(3,28,'3:28','لا يتخذ المؤمنون الكافرين اولياء من دون المؤمنين ومن يفعل ذلك فليس من الله في شيء الا ان تتقوا منهم تقاة ويحذركم الله نفسه والى الله المصير');
INSERT INTO "imlaei_simple" VALUES(3,29,'3:29','قل ان تخفوا ما في صدوركم او تبدوه يعلمه الله ويعلم ما في السماوات وما في الارض والله على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(3,30,'3:30','يوم تجد كل نفس ما عملت من خير محضرا وما عملت من سوء تود لو ان بينها وبينه امدا بعيدا ويحذركم الله نفسه والله رءوف بالعباد');
INSERT INTO "imlaei_simple" VALUES(3,31,'3:31','قل ان كنتم تحبون الله فاتبعوني يحببكم الله ويغفر لكم ذنوبكم والله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(3,32,'3:32','قل اطيعوا الله والرسول فان تولوا فان الله لا يحب الكافرين');
INSERT INTO "imlaei_simple" VALUES(3,33,'3:33','ان الله اصطفى ادم ونوحا وال ابراهيم وال عمران على العالمين');
INSERT INTO "imlaei_simple" VALUES(3,34,'3:34','ذرية بعضها من بعض والله سميع عليم');
INSERT INTO "imlaei_simple" VALUES(3,35,'3:35','اذ قالت امرات عمران رب اني نذرت لك ما في بطني محررا فتقبل مني انك انت السميع العليم');
INSERT INTO "imlaei_simple" VALUES(3,36,'3:36','فلما وضعتها قالت رب اني وضعتها انثى والله اعلم بما وضعت وليس الذكر كالانثى واني سميتها مريم واني اعيذها بك وذريتها من الشيطان الرجيم');
INSERT INTO "imlaei_simple" VALUES(3,37,'3:37','فتقبلها ربها بقبول حسن وانبتها نباتا حسنا وكفلها زكريا كلما دخل عليها زكريا المحراب وجد عندها رزقا قال يا مريم انى لك هذا قالت هو من عند الله ان الله يرزق من يشاء بغير حساب');
INSERT INTO "imlaei_simple" VALUES(3,38,'3:38','هنالك دعا زكريا ربه قال رب هب لي من لدنك ذرية طيبة انك سميع الدعاء');
INSERT INTO "imlaei_simple" VALUES(3,39,'3:39','فنادته الملائكة وهو قائم يصلي في المحراب ان الله يبشرك بيحيى مصدقا بكلمة من الله وسيدا وحصورا ونبيا من الصالحين');
INSERT INTO "imlaei_simple" VALUES(3,40,'3:40','قال رب انى يكون لي غلام وقد بلغني الكبر وامراتي عاقر قال كذلك الله يفعل ما يشاء');
INSERT INTO "imlaei_simple" VALUES(3,41,'3:41','قال رب اجعل لي اية قال ايتك الا تكلم الناس ثلاثة ايام الا رمزا واذكر ربك كثيرا وسبح بالعشي والابكار');
INSERT INTO "imlaei_simple" VALUES(3,42,'3:42','واذ قالت الملائكة يا مريم ان الله اصطفاك وطهرك واصطفاك على نساء العالمين');
INSERT INTO "imlaei_simple" VALUES(3,43,'3:43','يا مريم اقنتي لربك واسجدي واركعي مع الراكعين');
INSERT INTO "imlaei_simple" VALUES(3,44,'3:44','ذلك من انباء الغيب نوحيه اليك وما كنت لديهم اذ يلقون اقلامهم ايهم يكفل مريم وما كنت لديهم اذ يختصمون');
INSERT INTO "imlaei_simple" VALUES(3,45,'3:45','اذ قالت الملائكة يا مريم ان الله يبشرك بكلمة منه اسمه المسيح عيسى ابن مريم وجيها في الدنيا والاخرة ومن المقربين');
INSERT INTO "imlaei_simple" VALUES(3,46,'3:46','ويكلم الناس في المهد وكهلا ومن الصالحين');
INSERT INTO "imlaei_simple" VALUES(3,47,'3:47','قالت رب انى يكون لي ولد ولم يمسسني بشر قال كذلك الله يخلق ما يشاء اذا قضى امرا فانما يقول له كن فيكون');
INSERT INTO "imlaei_simple" VALUES(3,48,'3:48','ويعلمه الكتاب والحكمة والتوراة والانجيل');
INSERT INTO "imlaei_simple" VALUES(3,49,'3:49','ورسولا الى بني اسرائيل اني قد جئتكم باية من ربكم اني اخلق لكم من الطين كهيئة الطير فانفخ فيه فيكون طيرا باذن الله وابرئ الاكمه والابرص واحيي الموتى باذن الله وانبئكم بما تاكلون وما تدخرون في بيوتكم ان في ذلك لاية لكم ان كنتم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(3,50,'3:50','ومصدقا لما بين يدي من التوراة ولاحل لكم بعض الذي حرم عليكم وجئتكم باية من ربكم فاتقوا الله واطيعون');
INSERT INTO "imlaei_simple" VALUES(3,51,'3:51','ان الله ربي وربكم فاعبدوه هذا صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(3,52,'3:52','فلما احس عيسى منهم الكفر قال من انصاري الى الله قال الحواريون نحن انصار الله امنا بالله واشهد بانا مسلمون');
INSERT INTO "imlaei_simple" VALUES(3,53,'3:53','ربنا امنا بما انزلت واتبعنا الرسول فاكتبنا مع الشاهدين');
INSERT INTO "imlaei_simple" VALUES(3,54,'3:54','ومكروا ومكر الله والله خير الماكرين');
INSERT INTO "imlaei_simple" VALUES(3,55,'3:55','اذ قال الله يا عيسى اني متوفيك ورافعك الي ومطهرك من الذين كفروا وجاعل الذين اتبعوك فوق الذين كفروا الى يوم القيامة ثم الي مرجعكم فاحكم بينكم فيما كنتم فيه تختلفون');
INSERT INTO "imlaei_simple" VALUES(3,56,'3:56','فاما الذين كفروا فاعذبهم عذابا شديدا في الدنيا والاخرة وما لهم من ناصرين');
INSERT INTO "imlaei_simple" VALUES(3,57,'3:57','واما الذين امنوا وعملوا الصالحات فيوفيهم اجورهم والله لا يحب الظالمين');
INSERT INTO "imlaei_simple" VALUES(3,58,'3:58','ذلك نتلوه عليك من الايات والذكر الحكيم');
INSERT INTO "imlaei_simple" VALUES(3,59,'3:59','ان مثل عيسى عند الله كمثل ادم خلقه من تراب ثم قال له كن فيكون');
INSERT INTO "imlaei_simple" VALUES(3,60,'3:60','الحق من ربك فلا تكن من الممترين');
INSERT INTO "imlaei_simple" VALUES(3,61,'3:61','فمن حاجك فيه من بعد ما جاءك من العلم فقل تعالوا ندع ابناءنا وابناءكم ونساءنا ونساءكم وانفسنا وانفسكم ثم نبتهل فنجعل لعنت الله على الكاذبين');
INSERT INTO "imlaei_simple" VALUES(3,62,'3:62','ان هذا لهو القصص الحق وما من اله الا الله وان الله لهو العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(3,63,'3:63','فان تولوا فان الله عليم بالمفسدين');
INSERT INTO "imlaei_simple" VALUES(3,64,'3:64','قل يا اهل الكتاب تعالوا الى كلمة سواء بيننا وبينكم الا نعبد الا الله ولا نشرك به شيئا ولا يتخذ بعضنا بعضا اربابا من دون الله فان تولوا فقولوا اشهدوا بانا مسلمون');
INSERT INTO "imlaei_simple" VALUES(3,65,'3:65','يا اهل الكتاب لم تحاجون في ابراهيم وما انزلت التوراة والانجيل الا من بعده افلا تعقلون');
INSERT INTO "imlaei_simple" VALUES(3,66,'3:66','ها انتم هؤلاء حاججتم فيما لكم به علم فلم تحاجون فيما ليس لكم به علم والله يعلم وانتم لا تعلمون');
INSERT INTO "imlaei_simple" VALUES(3,67,'3:67','ما كان ابراهيم يهوديا ولا نصرانيا ولكن كان حنيفا مسلما وما كان من المشركين');
INSERT INTO "imlaei_simple" VALUES(3,68,'3:68','ان اولى الناس بابراهيم للذين اتبعوه وهذا النبي والذين امنوا والله ولي المؤمنين');
INSERT INTO "imlaei_simple" VALUES(3,69,'3:69','ودت طائفة من اهل الكتاب لو يضلونكم وما يضلون الا انفسهم وما يشعرون');
INSERT INTO "imlaei_simple" VALUES(3,70,'3:70','يا اهل الكتاب لم تكفرون بايات الله وانتم تشهدون');
INSERT INTO "imlaei_simple" VALUES(3,71,'3:71','يا اهل الكتاب لم تلبسون الحق بالباطل وتكتمون الحق وانتم تعلمون');
INSERT INTO "imlaei_simple" VALUES(3,72,'3:72','وقالت طائفة من اهل الكتاب امنوا بالذي انزل على الذين امنوا وجه النهار واكفروا اخره لعلهم يرجعون');
INSERT INTO "imlaei_simple" VALUES(3,73,'3:73','ولا تؤمنوا الا لمن تبع دينكم قل ان الهدى هدى الله ان يؤتى احد مثل ما اوتيتم او يحاجوكم عند ربكم قل ان الفضل بيد الله يؤتيه من يشاء والله واسع عليم');
INSERT INTO "imlaei_simple" VALUES(3,74,'3:74','يختص برحمته من يشاء والله ذو الفضل العظيم');
INSERT INTO "imlaei_simple" VALUES(3,75,'3:75','ومن اهل الكتاب من ان تامنه بقنطار يؤده اليك ومنهم من ان تامنه بدينار لا يؤده اليك الا ما دمت عليه قائما ذلك بانهم قالوا ليس علينا في الاميين سبيل ويقولون على الله الكذب وهم يعلمون');
INSERT INTO "imlaei_simple" VALUES(3,76,'3:76','بلى من اوفى بعهده واتقى فان الله يحب المتقين');
INSERT INTO "imlaei_simple" VALUES(3,77,'3:77','ان الذين يشترون بعهد الله وايمانهم ثمنا قليلا اولئك لا خلاق لهم في الاخرة ولا يكلمهم الله ولا ينظر اليهم يوم القيامة ولا يزكيهم ولهم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(3,78,'3:78','وان منهم لفريقا يلوون السنتهم بالكتاب لتحسبوه من الكتاب وما هو من الكتاب ويقولون هو من عند الله وما هو من عند الله ويقولون على الله الكذب وهم يعلمون');
INSERT INTO "imlaei_simple" VALUES(3,79,'3:79','ما كان لبشر ان يؤتيه الله الكتاب والحكم والنبوة ثم يقول للناس كونوا عبادا لي من دون الله ولكن كونوا ربانيين بما كنتم تعلمون الكتاب وبما كنتم تدرسون');
INSERT INTO "imlaei_simple" VALUES(3,80,'3:80','ولا يامركم ان تتخذوا الملائكة والنبيين اربابا ايامركم بالكفر بعد اذ انتم مسلمون');
INSERT INTO "imlaei_simple" VALUES(3,81,'3:81','واذ اخذ الله ميثاق النبيين لما اتيتكم من كتاب وحكمة ثم جاءكم رسول مصدق لما معكم لتؤمنن به ولتنصرنه قال ااقررتم واخذتم على ذلكم اصري قالوا اقررنا قال فاشهدوا وانا معكم من الشاهدين');
INSERT INTO "imlaei_simple" VALUES(3,82,'3:82','فمن تولى بعد ذلك فاولئك هم الفاسقون');
INSERT INTO "imlaei_simple" VALUES(3,83,'3:83','افغير دين الله يبغون وله اسلم من في السماوات والارض طوعا وكرها واليه يرجعون');
INSERT INTO "imlaei_simple" VALUES(3,84,'3:84','قل امنا بالله وما انزل علينا وما انزل على ابراهيم واسماعيل واسحاق ويعقوب والاسباط وما اوتي موسى وعيسى والنبيون من ربهم لا نفرق بين احد منهم ونحن له مسلمون');
INSERT INTO "imlaei_simple" VALUES(3,85,'3:85','ومن يبتغ غير الاسلام دينا فلن يقبل منه وهو في الاخرة من الخاسرين');
INSERT INTO "imlaei_simple" VALUES(3,86,'3:86','كيف يهدي الله قوما كفروا بعد ايمانهم وشهدوا ان الرسول حق وجاءهم البينات والله لا يهدي القوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(3,87,'3:87','اولئك جزاؤهم ان عليهم لعنة الله والملائكة والناس اجمعين');
INSERT INTO "imlaei_simple" VALUES(3,88,'3:88','خالدين فيها لا يخفف عنهم العذاب ولا هم ينظرون');
INSERT INTO "imlaei_simple" VALUES(3,89,'3:89','الا الذين تابوا من بعد ذلك واصلحوا فان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(3,90,'3:90','ان الذين كفروا بعد ايمانهم ثم ازدادوا كفرا لن تقبل توبتهم واولئك هم الضالون');
INSERT INTO "imlaei_simple" VALUES(3,91,'3:91','ان الذين كفروا وماتوا وهم كفار فلن يقبل من احدهم ملء الارض ذهبا ولو افتدى به اولئك لهم عذاب اليم وما لهم من ناصرين');
INSERT INTO "imlaei_simple" VALUES(3,92,'3:92','لن تنالوا البر حتى تنفقوا مما تحبون وما تنفقوا من شيء فان الله به عليم');
INSERT INTO "imlaei_simple" VALUES(3,93,'3:93','كل الطعام كان حلا لبني اسرائيل الا ما حرم اسرائيل على نفسه من قبل ان تنزل التوراة قل فاتوا بالتوراة فاتلوها ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(3,94,'3:94','فمن افترى على الله الكذب من بعد ذلك فاولئك هم الظالمون');
INSERT INTO "imlaei_simple" VALUES(3,95,'3:95','قل صدق الله فاتبعوا ملة ابراهيم حنيفا وما كان من المشركين');
INSERT INTO "imlaei_simple" VALUES(3,96,'3:96','ان اول بيت وضع للناس للذي ببكة مباركا وهدى للعالمين');
INSERT INTO "imlaei_simple" VALUES(3,97,'3:97','فيه ايات بينات مقام ابراهيم ومن دخله كان امنا ولله على الناس حج البيت من استطاع اليه سبيلا ومن كفر فان الله غني عن العالمين');
INSERT INTO "imlaei_simple" VALUES(3,98,'3:98','قل يا اهل الكتاب لم تكفرون بايات الله والله شهيد على ما تعملون');
INSERT INTO "imlaei_simple" VALUES(3,99,'3:99','قل يا اهل الكتاب لم تصدون عن سبيل الله من امن تبغونها عوجا وانتم شهداء وما الله بغافل عما تعملون');
INSERT INTO "imlaei_simple" VALUES(3,100,'3:100','يا ايها الذين امنوا ان تطيعوا فريقا من الذين اوتوا الكتاب يردوكم بعد ايمانكم كافرين');
INSERT INTO "imlaei_simple" VALUES(3,101,'3:101','وكيف تكفرون وانتم تتلى عليكم ايات الله وفيكم رسوله ومن يعتصم بالله فقد هدي الى صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(3,102,'3:102','يا ايها الذين امنوا اتقوا الله حق تقاته ولا تموتن الا وانتم مسلمون');
INSERT INTO "imlaei_simple" VALUES(3,103,'3:103','واعتصموا بحبل الله جميعا ولا تفرقوا واذكروا نعمت الله عليكم اذ كنتم اعداء فالف بين قلوبكم فاصبحتم بنعمته اخوانا وكنتم على شفا حفرة من النار فانقذكم منها كذلك يبين الله لكم اياته لعلكم تهتدون');
INSERT INTO "imlaei_simple" VALUES(3,104,'3:104','ولتكن منكم امة يدعون الى الخير ويامرون بالمعروف وينهون عن المنكر واولئك هم المفلحون');
INSERT INTO "imlaei_simple" VALUES(3,105,'3:105','ولا تكونوا كالذين تفرقوا واختلفوا من بعد ما جاءهم البينات واولئك لهم عذاب عظيم');
INSERT INTO "imlaei_simple" VALUES(3,106,'3:106','يوم تبيض وجوه وتسود وجوه فاما الذين اسودت وجوههم اكفرتم بعد ايمانكم فذوقوا العذاب بما كنتم تكفرون');
INSERT INTO "imlaei_simple" VALUES(3,107,'3:107','واما الذين ابيضت وجوههم ففي رحمة الله هم فيها خالدون');
INSERT INTO "imlaei_simple" VALUES(3,108,'3:108','تلك ايات الله نتلوها عليك بالحق وما الله يريد ظلما للعالمين');
INSERT INTO "imlaei_simple" VALUES(3,109,'3:109','ولله ما في السماوات وما في الارض والى الله ترجع الامور');
INSERT INTO "imlaei_simple" VALUES(3,110,'3:110','كنتم خير امة اخرجت للناس تامرون بالمعروف وتنهون عن المنكر وتؤمنون بالله ولو امن اهل الكتاب لكان خيرا لهم منهم المؤمنون واكثرهم الفاسقون');
INSERT INTO "imlaei_simple" VALUES(3,111,'3:111','لن يضروكم الا اذى وان يقاتلوكم يولوكم الادبار ثم لا ينصرون');
INSERT INTO "imlaei_simple" VALUES(3,112,'3:112','ضربت عليهم الذلة اين ما ثقفوا الا بحبل من الله وحبل من الناس وباءوا بغضب من الله وضربت عليهم المسكنة ذلك بانهم كانوا يكفرون بايات الله ويقتلون الانبياء بغير حق ذلك بما عصوا وكانوا يعتدون');
INSERT INTO "imlaei_simple" VALUES(3,113,'3:113','ليسوا سواء من اهل الكتاب امة قائمة يتلون ايات الله اناء الليل وهم يسجدون');
INSERT INTO "imlaei_simple" VALUES(3,114,'3:114','يؤمنون بالله واليوم الاخر ويامرون بالمعروف وينهون عن المنكر ويسارعون في الخيرات واولئك من الصالحين');
INSERT INTO "imlaei_simple" VALUES(3,115,'3:115','وما يفعلوا من خير فلن يكفروه والله عليم بالمتقين');
INSERT INTO "imlaei_simple" VALUES(3,116,'3:116','ان الذين كفروا لن تغني عنهم اموالهم ولا اولادهم من الله شيئا واولئك اصحاب النار هم فيها خالدون');
INSERT INTO "imlaei_simple" VALUES(3,117,'3:117','مثل ما ينفقون في هذه الحياة الدنيا كمثل ريح فيها صر اصابت حرث قوم ظلموا انفسهم فاهلكته وما ظلمهم الله ولكن انفسهم يظلمون');
INSERT INTO "imlaei_simple" VALUES(3,118,'3:118','يا ايها الذين امنوا لا تتخذوا بطانة من دونكم لا يالونكم خبالا ودوا ما عنتم قد بدت البغضاء من افواههم وما تخفي صدورهم اكبر قد بينا لكم الايات ان كنتم تعقلون');
INSERT INTO "imlaei_simple" VALUES(3,119,'3:119','ها انتم اولاء تحبونهم ولا يحبونكم وتؤمنون بالكتاب كله واذا لقوكم قالوا امنا واذا خلوا عضوا عليكم الانامل من الغيظ قل موتوا بغيظكم ان الله عليم بذات الصدور');
INSERT INTO "imlaei_simple" VALUES(3,120,'3:120','ان تمسسكم حسنة تسؤهم وان تصبكم سيئة يفرحوا بها وان تصبروا وتتقوا لا يضركم كيدهم شيئا ان الله بما يعملون محيط');
INSERT INTO "imlaei_simple" VALUES(3,121,'3:121','واذ غدوت من اهلك تبوئ المؤمنين مقاعد للقتال والله سميع عليم');
INSERT INTO "imlaei_simple" VALUES(3,122,'3:122','اذ همت طائفتان منكم ان تفشلا والله وليهما وعلى الله فليتوكل المؤمنون');
INSERT INTO "imlaei_simple" VALUES(3,123,'3:123','ولقد نصركم الله ببدر وانتم اذلة فاتقوا الله لعلكم تشكرون');
INSERT INTO "imlaei_simple" VALUES(3,124,'3:124','اذ تقول للمؤمنين الن يكفيكم ان يمدكم ربكم بثلاثة الاف من الملائكة منزلين');
INSERT INTO "imlaei_simple" VALUES(3,125,'3:125','بلى ان تصبروا وتتقوا وياتوكم من فورهم هذا يمددكم ربكم بخمسة الاف من الملائكة مسومين');
INSERT INTO "imlaei_simple" VALUES(3,126,'3:126','وما جعله الله الا بشرى لكم ولتطمئن قلوبكم به وما النصر الا من عند الله العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(3,127,'3:127','ليقطع طرفا من الذين كفروا او يكبتهم فينقلبوا خائبين');
INSERT INTO "imlaei_simple" VALUES(3,128,'3:128','ليس لك من الامر شيء او يتوب عليهم او يعذبهم فانهم ظالمون');
INSERT INTO "imlaei_simple" VALUES(3,129,'3:129','ولله ما في السماوات وما في الارض يغفر لمن يشاء ويعذب من يشاء والله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(3,130,'3:130','يا ايها الذين امنوا لا تاكلوا الربا اضعافا مضاعفة واتقوا الله لعلكم تفلحون');
INSERT INTO "imlaei_simple" VALUES(3,131,'3:131','واتقوا النار التي اعدت للكافرين');
INSERT INTO "imlaei_simple" VALUES(3,132,'3:132','واطيعوا الله والرسول لعلكم ترحمون');
INSERT INTO "imlaei_simple" VALUES(3,133,'3:133','وسارعوا الى مغفرة من ربكم وجنة عرضها السماوات والارض اعدت للمتقين');
INSERT INTO "imlaei_simple" VALUES(3,134,'3:134','الذين ينفقون في السراء والضراء والكاظمين الغيظ والعافين عن الناس والله يحب المحسنين');
INSERT INTO "imlaei_simple" VALUES(3,135,'3:135','والذين اذا فعلوا فاحشة او ظلموا انفسهم ذكروا الله فاستغفروا لذنوبهم ومن يغفر الذنوب الا الله ولم يصروا على ما فعلوا وهم يعلمون');
INSERT INTO "imlaei_simple" VALUES(3,136,'3:136','اولئك جزاؤهم مغفرة من ربهم وجنات تجري من تحتها الانهار خالدين فيها ونعم اجر العاملين');
INSERT INTO "imlaei_simple" VALUES(3,137,'3:137','قد خلت من قبلكم سنن فسيروا في الارض فانظروا كيف كان عاقبة المكذبين');
INSERT INTO "imlaei_simple" VALUES(3,138,'3:138','هذا بيان للناس وهدى وموعظة للمتقين');
INSERT INTO "imlaei_simple" VALUES(3,139,'3:139','ولا تهنوا ولا تحزنوا وانتم الاعلون ان كنتم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(3,140,'3:140','ان يمسسكم قرح فقد مس القوم قرح مثله وتلك الايام نداولها بين الناس وليعلم الله الذين امنوا ويتخذ منكم شهداء والله لا يحب الظالمين');
INSERT INTO "imlaei_simple" VALUES(3,141,'3:141','وليمحص الله الذين امنوا ويمحق الكافرين');
INSERT INTO "imlaei_simple" VALUES(3,142,'3:142','ام حسبتم ان تدخلوا الجنة ولما يعلم الله الذين جاهدوا منكم ويعلم الصابرين');
INSERT INTO "imlaei_simple" VALUES(3,143,'3:143','ولقد كنتم تمنون الموت من قبل ان تلقوه فقد رايتموه وانتم تنظرون');
INSERT INTO "imlaei_simple" VALUES(3,144,'3:144','وما محمد الا رسول قد خلت من قبله الرسل افان مات او قتل انقلبتم على اعقابكم ومن ينقلب على عقبيه فلن يضر الله شيئا وسيجزي الله الشاكرين');
INSERT INTO "imlaei_simple" VALUES(3,145,'3:145','وما كان لنفس ان تموت الا باذن الله كتابا مؤجلا ومن يرد ثواب الدنيا نؤته منها ومن يرد ثواب الاخرة نؤته منها وسنجزي الشاكرين');
INSERT INTO "imlaei_simple" VALUES(3,146,'3:146','وكاين من نبي قاتل معه ربيون كثير فما وهنوا لما اصابهم في سبيل الله وما ضعفوا وما استكانوا والله يحب الصابرين');
INSERT INTO "imlaei_simple" VALUES(3,147,'3:147','وما كان قولهم الا ان قالوا ربنا اغفر لنا ذنوبنا واسرافنا في امرنا وثبت اقدامنا وانصرنا على القوم الكافرين');
INSERT INTO "imlaei_simple" VALUES(3,148,'3:148','فاتاهم الله ثواب الدنيا وحسن ثواب الاخرة والله يحب المحسنين');
INSERT INTO "imlaei_simple" VALUES(3,149,'3:149','يا ايها الذين امنوا ان تطيعوا الذين كفروا يردوكم على اعقابكم فتنقلبوا خاسرين');
INSERT INTO "imlaei_simple" VALUES(3,150,'3:150','بل الله مولاكم وهو خير الناصرين');
INSERT INTO "imlaei_simple" VALUES(3,151,'3:151','سنلقي في قلوب الذين كفروا الرعب بما اشركوا بالله ما لم ينزل به سلطانا وماواهم النار وبئس مثوى الظالمين');
INSERT INTO "imlaei_simple" VALUES(3,152,'3:152','ولقد صدقكم الله وعده اذ تحسونهم باذنه حتى اذا فشلتم وتنازعتم في الامر وعصيتم من بعد ما اراكم ما تحبون منكم من يريد الدنيا ومنكم من يريد الاخرة ثم صرفكم عنهم ليبتليكم ولقد عفا عنكم والله ذو فضل على المؤمنين');
INSERT INTO "imlaei_simple" VALUES(3,153,'3:153','اذ تصعدون ولا تلوون على احد والرسول يدعوكم في اخراكم فاثابكم غما بغم لكيلا تحزنوا على ما فاتكم ولا ما اصابكم والله خبير بما تعملون');
INSERT INTO "imlaei_simple" VALUES(3,154,'3:154','ثم انزل عليكم من بعد الغم امنة نعاسا يغشى طائفة منكم وطائفة قد اهمتهم انفسهم يظنون بالله غير الحق ظن الجاهلية يقولون هل لنا من الامر من شيء قل ان الامر كله لله يخفون في انفسهم ما لا يبدون لك يقولون لو كان لنا من الامر شيء ما قتلنا هاهنا قل لو كنتم في بيوتكم لبرز الذين كتب عليهم القتل الى مضاجعهم وليبتلي الله ما في صدوركم وليمحص ما في قلوبكم والله عليم بذات الصدور');
INSERT INTO "imlaei_simple" VALUES(3,155,'3:155','ان الذين تولوا منكم يوم التقى الجمعان انما استزلهم الشيطان ببعض ما كسبوا ولقد عفا الله عنهم ان الله غفور حليم');
INSERT INTO "imlaei_simple" VALUES(3,156,'3:156','يا ايها الذين امنوا لا تكونوا كالذين كفروا وقالوا لاخوانهم اذا ضربوا في الارض او كانوا غزى لو كانوا عندنا ما ماتوا وما قتلوا ليجعل الله ذلك حسرة في قلوبهم والله يحيي ويميت والله بما تعملون بصير');
INSERT INTO "imlaei_simple" VALUES(3,157,'3:157','ولئن قتلتم في سبيل الله او متم لمغفرة من الله ورحمة خير مما يجمعون');
INSERT INTO "imlaei_simple" VALUES(3,158,'3:158','ولئن متم او قتلتم لالى الله تحشرون');
INSERT INTO "imlaei_simple" VALUES(3,159,'3:159','فبما رحمة من الله لنت لهم ولو كنت فظا غليظ القلب لانفضوا من حولك فاعف عنهم واستغفر لهم وشاورهم في الامر فاذا عزمت فتوكل على الله ان الله يحب المتوكلين');
INSERT INTO "imlaei_simple" VALUES(3,160,'3:160','ان ينصركم الله فلا غالب لكم وان يخذلكم فمن ذا الذي ينصركم من بعده وعلى الله فليتوكل المؤمنون');
INSERT INTO "imlaei_simple" VALUES(3,161,'3:161','وما كان لنبي ان يغل ومن يغلل يات بما غل يوم القيامة ثم توفى كل نفس ما كسبت وهم لا يظلمون');
INSERT INTO "imlaei_simple" VALUES(3,162,'3:162','افمن اتبع رضوان الله كمن باء بسخط من الله وماواه جهنم وبئس المصير');
INSERT INTO "imlaei_simple" VALUES(3,163,'3:163','هم درجات عند الله والله بصير بما يعملون');
INSERT INTO "imlaei_simple" VALUES(3,164,'3:164','لقد من الله على المؤمنين اذ بعث فيهم رسولا من انفسهم يتلو عليهم اياته ويزكيهم ويعلمهم الكتاب والحكمة وان كانوا من قبل لفي ضلال مبين');
INSERT INTO "imlaei_simple" VALUES(3,165,'3:165','اولما اصابتكم مصيبة قد اصبتم مثليها قلتم انى هذا قل هو من عند انفسكم ان الله على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(3,166,'3:166','وما اصابكم يوم التقى الجمعان فباذن الله وليعلم المؤمنين');
INSERT INTO "imlaei_simple" VALUES(3,167,'3:167','وليعلم الذين نافقوا وقيل لهم تعالوا قاتلوا في سبيل الله او ادفعوا قالوا لو نعلم قتالا لاتبعناكم هم للكفر يومئذ اقرب منهم للايمان يقولون بافواههم ما ليس في قلوبهم والله اعلم بما يكتمون');
INSERT INTO "imlaei_simple" VALUES(3,168,'3:168','الذين قالوا لاخوانهم وقعدوا لو اطاعونا ما قتلوا قل فادرءوا عن انفسكم الموت ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(3,169,'3:169','ولا تحسبن الذين قتلوا في سبيل الله امواتا بل احياء عند ربهم يرزقون');
INSERT INTO "imlaei_simple" VALUES(3,170,'3:170','فرحين بما اتاهم الله من فضله ويستبشرون بالذين لم يلحقوا بهم من خلفهم الا خوف عليهم ولا هم يحزنون');
INSERT INTO "imlaei_simple" VALUES(3,171,'3:171','يستبشرون بنعمة من الله وفضل وان الله لا يضيع اجر المؤمنين');
INSERT INTO "imlaei_simple" VALUES(3,172,'3:172','الذين استجابوا لله والرسول من بعد ما اصابهم القرح للذين احسنوا منهم واتقوا اجر عظيم');
INSERT INTO "imlaei_simple" VALUES(3,173,'3:173','الذين قال لهم الناس ان الناس قد جمعوا لكم فاخشوهم فزادهم ايمانا وقالوا حسبنا الله ونعم الوكيل');
INSERT INTO "imlaei_simple" VALUES(3,174,'3:174','فانقلبوا بنعمة من الله وفضل لم يمسسهم سوء واتبعوا رضوان الله والله ذو فضل عظيم');
INSERT INTO "imlaei_simple" VALUES(3,175,'3:175','انما ذلكم الشيطان يخوف اولياءه فلا تخافوهم وخافون ان كنتم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(3,176,'3:176','ولا يحزنك الذين يسارعون في الكفر انهم لن يضروا الله شيئا يريد الله الا يجعل لهم حظا في الاخرة ولهم عذاب عظيم');
INSERT INTO "imlaei_simple" VALUES(3,177,'3:177','ان الذين اشتروا الكفر بالايمان لن يضروا الله شيئا ولهم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(3,178,'3:178','ولا يحسبن الذين كفروا انما نملي لهم خير لانفسهم انما نملي لهم ليزدادوا اثما ولهم عذاب مهين');
INSERT INTO "imlaei_simple" VALUES(3,179,'3:179','ما كان الله ليذر المؤمنين على ما انتم عليه حتى يميز الخبيث من الطيب وما كان الله ليطلعكم على الغيب ولكن الله يجتبي من رسله من يشاء فامنوا بالله ورسله وان تؤمنوا وتتقوا فلكم اجر عظيم');
INSERT INTO "imlaei_simple" VALUES(3,180,'3:180','ولا يحسبن الذين يبخلون بما اتاهم الله من فضله هو خيرا لهم بل هو شر لهم سيطوقون ما بخلوا به يوم القيامة ولله ميراث السماوات والارض والله بما تعملون خبير');
INSERT INTO "imlaei_simple" VALUES(3,181,'3:181','لقد سمع الله قول الذين قالوا ان الله فقير ونحن اغنياء سنكتب ما قالوا وقتلهم الانبياء بغير حق ونقول ذوقوا عذاب الحريق');
INSERT INTO "imlaei_simple" VALUES(3,182,'3:182','ذلك بما قدمت ايديكم وان الله ليس بظلام للعبيد');
INSERT INTO "imlaei_simple" VALUES(3,183,'3:183','الذين قالوا ان الله عهد الينا الا نؤمن لرسول حتى ياتينا بقربان تاكله النار قل قد جاءكم رسل من قبلي بالبينات وبالذي قلتم فلم قتلتموهم ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(3,184,'3:184','فان كذبوك فقد كذب رسل من قبلك جاءوا بالبينات والزبر والكتاب المنير');
INSERT INTO "imlaei_simple" VALUES(3,185,'3:185','كل نفس ذائقة الموت وانما توفون اجوركم يوم القيامة فمن زحزح عن النار وادخل الجنة فقد فاز وما الحياة الدنيا الا متاع الغرور');
INSERT INTO "imlaei_simple" VALUES(3,186,'3:186','لتبلون في اموالكم وانفسكم ولتسمعن من الذين اوتوا الكتاب من قبلكم ومن الذين اشركوا اذى كثيرا وان تصبروا وتتقوا فان ذلك من عزم الامور');
INSERT INTO "imlaei_simple" VALUES(3,187,'3:187','واذ اخذ الله ميثاق الذين اوتوا الكتاب لتبيننه للناس ولا تكتمونه فنبذوه وراء ظهورهم واشتروا به ثمنا قليلا فبئس ما يشترون');
INSERT INTO "imlaei_simple" VALUES(3,188,'3:188','لا تحسبن الذين يفرحون بما اتوا ويحبون ان يحمدوا بما لم يفعلوا فلا تحسبنهم بمفازة من العذاب ولهم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(3,189,'3:189','ولله ملك السماوات والارض والله على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(3,190,'3:190','ان في خلق السماوات والارض واختلاف الليل والنهار لايات لاولي الالباب');
INSERT INTO "imlaei_simple" VALUES(3,191,'3:191','الذين يذكرون الله قياما وقعودا وعلى جنوبهم ويتفكرون في خلق السماوات والارض ربنا ما خلقت هذا باطلا سبحانك فقنا عذاب النار');
INSERT INTO "imlaei_simple" VALUES(3,192,'3:192','ربنا انك من تدخل النار فقد اخزيته وما للظالمين من انصار');
INSERT INTO "imlaei_simple" VALUES(3,193,'3:193','ربنا اننا سمعنا مناديا ينادي للايمان ان امنوا بربكم فامنا ربنا فاغفر لنا ذنوبنا وكفر عنا سيئاتنا وتوفنا مع الابرار');
INSERT INTO "imlaei_simple" VALUES(3,194,'3:194','ربنا واتنا ما وعدتنا على رسلك ولا تخزنا يوم القيامة انك لا تخلف الميعاد');
INSERT INTO "imlaei_simple" VALUES(3,195,'3:195','فاستجاب لهم ربهم اني لا اضيع عمل عامل منكم من ذكر او انثى بعضكم من بعض فالذين هاجروا واخرجوا من ديارهم واوذوا في سبيلي وقاتلوا وقتلوا لاكفرن عنهم سيئاتهم ولادخلنهم جنات تجري من تحتها الانهار ثوابا من عند الله والله عنده حسن الثواب');
INSERT INTO "imlaei_simple" VALUES(3,196,'3:196','لا يغرنك تقلب الذين كفروا في البلاد');
INSERT INTO "imlaei_simple" VALUES(3,197,'3:197','متاع قليل ثم ماواهم جهنم وبئس المهاد');
INSERT INTO "imlaei_simple" VALUES(3,198,'3:198','لكن الذين اتقوا ربهم لهم جنات تجري من تحتها الانهار خالدين فيها نزلا من عند الله وما عند الله خير للابرار');
INSERT INTO "imlaei_simple" VALUES(3,199,'3:199','وان من اهل الكتاب لمن يؤمن بالله وما انزل اليكم وما انزل اليهم خاشعين لله لا يشترون بايات الله ثمنا قليلا اولئك لهم اجرهم عند ربهم ان الله سريع الحساب');
INSERT INTO "imlaei_simple" VALUES(3,200,'3:200','يا ايها الذين امنوا اصبروا وصابروا ورابطوا واتقوا الله لعلكم تفلحون');
INSERT INTO "imlaei_simple" VALUES(4,1,'4:1','يا ايها الناس اتقوا ربكم الذي خلقكم من نفس واحدة وخلق منها زوجها وبث منهما رجالا كثيرا ونساء واتقوا الله الذي تساءلون به والارحام ان الله كان عليكم رقيبا');
INSERT INTO "imlaei_simple" VALUES(4,2,'4:2','واتوا اليتامى اموالهم ولا تتبدلوا الخبيث بالطيب ولا تاكلوا اموالهم الى اموالكم انه كان حوبا كبيرا');
INSERT INTO "imlaei_simple" VALUES(4,3,'4:3','وان خفتم الا تقسطوا في اليتامى فانكحوا ما طاب لكم من النساء مثنى وثلاث ورباع فان خفتم الا تعدلوا فواحدة او ما ملكت ايمانكم ذلك ادنى الا تعولوا');
INSERT INTO "imlaei_simple" VALUES(4,4,'4:4','واتوا النساء صدقاتهن نحلة فان طبن لكم عن شيء منه نفسا فكلوه هنيئا مريئا');
INSERT INTO "imlaei_simple" VALUES(4,5,'4:5','ولا تؤتوا السفهاء اموالكم التي جعل الله لكم قياما وارزقوهم فيها واكسوهم وقولوا لهم قولا معروفا');
INSERT INTO "imlaei_simple" VALUES(4,6,'4:6','وابتلوا اليتامى حتى اذا بلغوا النكاح فان انستم منهم رشدا فادفعوا اليهم اموالهم ولا تاكلوها اسرافا وبدارا ان يكبروا ومن كان غنيا فليستعفف ومن كان فقيرا فلياكل بالمعروف فاذا دفعتم اليهم اموالهم فاشهدوا عليهم وكفى بالله حسيبا');
INSERT INTO "imlaei_simple" VALUES(4,7,'4:7','للرجال نصيب مما ترك الوالدان والاقربون وللنساء نصيب مما ترك الوالدان والاقربون مما قل منه او كثر نصيبا مفروضا');
INSERT INTO "imlaei_simple" VALUES(4,8,'4:8','واذا حضر القسمة اولو القربى واليتامى والمساكين فارزقوهم منه وقولوا لهم قولا معروفا');
INSERT INTO "imlaei_simple" VALUES(4,9,'4:9','وليخش الذين لو تركوا من خلفهم ذرية ضعافا خافوا عليهم فليتقوا الله وليقولوا قولا سديدا');
INSERT INTO "imlaei_simple" VALUES(4,10,'4:10','ان الذين ياكلون اموال اليتامى ظلما انما ياكلون في بطونهم نارا وسيصلون سعيرا');
INSERT INTO "imlaei_simple" VALUES(4,11,'4:11','يوصيكم الله في اولادكم للذكر مثل حظ الانثيين فان كن نساء فوق اثنتين فلهن ثلثا ما ترك وان كانت واحدة فلها النصف ولابويه لكل واحد منهما السدس مما ترك ان كان له ولد فان لم يكن له ولد وورثه ابواه فلامه الثلث فان كان له اخوة فلامه السدس من بعد وصية يوصي بها او دين اباؤكم وابناؤكم لا تدرون ايهم اقرب لكم نفعا فريضة من الله ان الله كان عليما حكيما');
INSERT INTO "imlaei_simple" VALUES(4,12,'4:12','ولكم نصف ما ترك ازواجكم ان لم يكن لهن ولد فان كان لهن ولد فلكم الربع مما تركن من بعد وصية يوصين بها او دين ولهن الربع مما تركتم ان لم يكن لكم ولد فان كان لكم ولد فلهن الثمن مما تركتم من بعد وصية توصون بها او دين وان كان رجل يورث كلالة او امراة وله اخ او اخت فلكل واحد منهما السدس فان كانوا اكثر من ذلك فهم شركاء في الثلث من بعد وصية يوصى بها او دين غير مضار وصية من الله والله عليم حليم');
INSERT INTO "imlaei_simple" VALUES(4,13,'4:13','تلك حدود الله ومن يطع الله ورسوله يدخله جنات تجري من تحتها الانهار خالدين فيها وذلك الفوز العظيم');
INSERT INTO "imlaei_simple" VALUES(4,14,'4:14','ومن يعص الله ورسوله ويتعد حدوده يدخله نارا خالدا فيها وله عذاب مهين');
INSERT INTO "imlaei_simple" VALUES(4,15,'4:15','واللاتي ياتين الفاحشة من نسائكم فاستشهدوا عليهن اربعة منكم فان شهدوا فامسكوهن في البيوت حتى يتوفاهن الموت او يجعل الله لهن سبيلا');
INSERT INTO "imlaei_simple" VALUES(4,16,'4:16','واللذان ياتيانها منكم فاذوهما فان تابا واصلحا فاعرضوا عنهما ان الله كان توابا رحيما');
INSERT INTO "imlaei_simple" VALUES(4,17,'4:17','انما التوبة على الله للذين يعملون السوء بجهالة ثم يتوبون من قريب فاولئك يتوب الله عليهم وكان الله عليما حكيما');
INSERT INTO "imlaei_simple" VALUES(4,18,'4:18','وليست التوبة للذين يعملون السيئات حتى اذا حضر احدهم الموت قال اني تبت الان ولا الذين يموتون وهم كفار اولئك اعتدنا لهم عذابا اليما');
INSERT INTO "imlaei_simple" VALUES(4,19,'4:19','يا ايها الذين امنوا لا يحل لكم ان ترثوا النساء كرها ولا تعضلوهن لتذهبوا ببعض ما اتيتموهن الا ان ياتين بفاحشة مبينة وعاشروهن بالمعروف فان كرهتموهن فعسى ان تكرهوا شيئا ويجعل الله فيه خيرا كثيرا');
INSERT INTO "imlaei_simple" VALUES(4,20,'4:20','وان اردتم استبدال زوج مكان زوج واتيتم احداهن قنطارا فلا تاخذوا منه شيئا اتاخذونه بهتانا واثما مبينا');
INSERT INTO "imlaei_simple" VALUES(4,21,'4:21','وكيف تاخذونه وقد افضى بعضكم الى بعض واخذن منكم ميثاقا غليظا');
INSERT INTO "imlaei_simple" VALUES(4,22,'4:22','ولا تنكحوا ما نكح اباؤكم من النساء الا ما قد سلف انه كان فاحشة ومقتا وساء سبيلا');
INSERT INTO "imlaei_simple" VALUES(4,23,'4:23','حرمت عليكم امهاتكم وبناتكم واخواتكم وعماتكم وخالاتكم وبنات الاخ وبنات الاخت وامهاتكم اللاتي ارضعنكم واخواتكم من الرضاعة وامهات نسائكم وربائبكم اللاتي في حجوركم من نسائكم اللاتي دخلتم بهن فان لم تكونوا دخلتم بهن فلا جناح عليكم وحلائل ابنائكم الذين من اصلابكم وان تجمعوا بين الاختين الا ما قد سلف ان الله كان غفورا رحيما');
INSERT INTO "imlaei_simple" VALUES(4,24,'4:24','والمحصنات من النساء الا ما ملكت ايمانكم كتاب الله عليكم واحل لكم ما وراء ذلكم ان تبتغوا باموالكم محصنين غير مسافحين فما استمتعتم به منهن فاتوهن اجورهن فريضة ولا جناح عليكم فيما تراضيتم به من بعد الفريضة ان الله كان عليما حكيما');
INSERT INTO "imlaei_simple" VALUES(4,25,'4:25','ومن لم يستطع منكم طولا ان ينكح المحصنات المؤمنات فمن ما ملكت ايمانكم من فتياتكم المؤمنات والله اعلم بايمانكم بعضكم من بعض فانكحوهن باذن اهلهن واتوهن اجورهن بالمعروف محصنات غير مسافحات ولا متخذات اخدان فاذا احصن فان اتين بفاحشة فعليهن نصف ما على المحصنات من العذاب ذلك لمن خشي العنت منكم وان تصبروا خير لكم والله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(4,26,'4:26','يريد الله ليبين لكم ويهديكم سنن الذين من قبلكم ويتوب عليكم والله عليم حكيم');
INSERT INTO "imlaei_simple" VALUES(4,27,'4:27','والله يريد ان يتوب عليكم ويريد الذين يتبعون الشهوات ان تميلوا ميلا عظيما');
INSERT INTO "imlaei_simple" VALUES(4,28,'4:28','يريد الله ان يخفف عنكم وخلق الانسان ضعيفا');
INSERT INTO "imlaei_simple" VALUES(4,29,'4:29','يا ايها الذين امنوا لا تاكلوا اموالكم بينكم بالباطل الا ان تكون تجارة عن تراض منكم ولا تقتلوا انفسكم ان الله كان بكم رحيما');
INSERT INTO "imlaei_simple" VALUES(4,30,'4:30','ومن يفعل ذلك عدوانا وظلما فسوف نصليه نارا وكان ذلك على الله يسيرا');
INSERT INTO "imlaei_simple" VALUES(4,31,'4:31','ان تجتنبوا كبائر ما تنهون عنه نكفر عنكم سيئاتكم وندخلكم مدخلا كريما');
INSERT INTO "imlaei_simple" VALUES(4,32,'4:32','ولا تتمنوا ما فضل الله به بعضكم على بعض للرجال نصيب مما اكتسبوا وللنساء نصيب مما اكتسبن واسالوا الله من فضله ان الله كان بكل شيء عليما');
INSERT INTO "imlaei_simple" VALUES(4,33,'4:33','ولكل جعلنا موالي مما ترك الوالدان والاقربون والذين عقدت ايمانكم فاتوهم نصيبهم ان الله كان على كل شيء شهيدا');
INSERT INTO "imlaei_simple" VALUES(4,34,'4:34','الرجال قوامون على النساء بما فضل الله بعضهم على بعض وبما انفقوا من اموالهم فالصالحات قانتات حافظات للغيب بما حفظ الله واللاتي تخافون نشوزهن فعظوهن واهجروهن في المضاجع واضربوهن فان اطعنكم فلا تبغوا عليهن سبيلا ان الله كان عليا كبيرا');
INSERT INTO "imlaei_simple" VALUES(4,35,'4:35','وان خفتم شقاق بينهما فابعثوا حكما من اهله وحكما من اهلها ان يريدا اصلاحا يوفق الله بينهما ان الله كان عليما خبيرا');
INSERT INTO "imlaei_simple" VALUES(4,36,'4:36','واعبدوا الله ولا تشركوا به شيئا وبالوالدين احسانا وبذي القربى واليتامى والمساكين والجار ذي القربى والجار الجنب والصاحب بالجنب وابن السبيل وما ملكت ايمانكم ان الله لا يحب من كان مختالا فخورا');
INSERT INTO "imlaei_simple" VALUES(4,37,'4:37','الذين يبخلون ويامرون الناس بالبخل ويكتمون ما اتاهم الله من فضله واعتدنا للكافرين عذابا مهينا');
INSERT INTO "imlaei_simple" VALUES(4,38,'4:38','والذين ينفقون اموالهم رئاء الناس ولا يؤمنون بالله ولا باليوم الاخر ومن يكن الشيطان له قرينا فساء قرينا');
INSERT INTO "imlaei_simple" VALUES(4,39,'4:39','وماذا عليهم لو امنوا بالله واليوم الاخر وانفقوا مما رزقهم الله وكان الله بهم عليما');
INSERT INTO "imlaei_simple" VALUES(4,40,'4:40','ان الله لا يظلم مثقال ذرة وان تك حسنة يضاعفها ويؤت من لدنه اجرا عظيما');
INSERT INTO "imlaei_simple" VALUES(4,41,'4:41','فكيف اذا جئنا من كل امة بشهيد وجئنا بك على هؤلاء شهيدا');
INSERT INTO "imlaei_simple" VALUES(4,42,'4:42','يومئذ يود الذين كفروا وعصوا الرسول لو تسوى بهم الارض ولا يكتمون الله حديثا');
INSERT INTO "imlaei_simple" VALUES(4,43,'4:43','يا ايها الذين امنوا لا تقربوا الصلاة وانتم سكارى حتى تعلموا ما تقولون ولا جنبا الا عابري سبيل حتى تغتسلوا وان كنتم مرضى او على سفر او جاء احد منكم من الغائط او لامستم النساء فلم تجدوا ماء فتيمموا صعيدا طيبا فامسحوا بوجوهكم وايديكم ان الله كان عفوا غفورا');
INSERT INTO "imlaei_simple" VALUES(4,44,'4:44','الم تر الى الذين اوتوا نصيبا من الكتاب يشترون الضلالة ويريدون ان تضلوا السبيل');
INSERT INTO "imlaei_simple" VALUES(4,45,'4:45','والله اعلم باعدائكم وكفى بالله وليا وكفى بالله نصيرا');
INSERT INTO "imlaei_simple" VALUES(4,46,'4:46','من الذين هادوا يحرفون الكلم عن مواضعه ويقولون سمعنا وعصينا واسمع غير مسمع وراعنا ليا بالسنتهم وطعنا في الدين ولو انهم قالوا سمعنا واطعنا واسمع وانظرنا لكان خيرا لهم واقوم ولكن لعنهم الله بكفرهم فلا يؤمنون الا قليلا');
INSERT INTO "imlaei_simple" VALUES(4,47,'4:47','يا ايها الذين اوتوا الكتاب امنوا بما نزلنا مصدقا لما معكم من قبل ان نطمس وجوها فنردها على ادبارها او نلعنهم كما لعنا اصحاب السبت وكان امر الله مفعولا');
INSERT INTO "imlaei_simple" VALUES(4,48,'4:48','ان الله لا يغفر ان يشرك به ويغفر ما دون ذلك لمن يشاء ومن يشرك بالله فقد افترى اثما عظيما');
INSERT INTO "imlaei_simple" VALUES(4,49,'4:49','الم تر الى الذين يزكون انفسهم بل الله يزكي من يشاء ولا يظلمون فتيلا');
INSERT INTO "imlaei_simple" VALUES(4,50,'4:50','انظر كيف يفترون على الله الكذب وكفى به اثما مبينا');
INSERT INTO "imlaei_simple" VALUES(4,51,'4:51','الم تر الى الذين اوتوا نصيبا من الكتاب يؤمنون بالجبت والطاغوت ويقولون للذين كفروا هؤلاء اهدى من الذين امنوا سبيلا');
INSERT INTO "imlaei_simple" VALUES(4,52,'4:52','اولئك الذين لعنهم الله ومن يلعن الله فلن تجد له نصيرا');
INSERT INTO "imlaei_simple" VALUES(4,53,'4:53','ام لهم نصيب من الملك فاذا لا يؤتون الناس نقيرا');
INSERT INTO "imlaei_simple" VALUES(4,54,'4:54','ام يحسدون الناس على ما اتاهم الله من فضله فقد اتينا ال ابراهيم الكتاب والحكمة واتيناهم ملكا عظيما');
INSERT INTO "imlaei_simple" VALUES(4,55,'4:55','فمنهم من امن به ومنهم من صد عنه وكفى بجهنم سعيرا');
INSERT INTO "imlaei_simple" VALUES(4,56,'4:56','ان الذين كفروا باياتنا سوف نصليهم نارا كلما نضجت جلودهم بدلناهم جلودا غيرها ليذوقوا العذاب ان الله كان عزيزا حكيما');
INSERT INTO "imlaei_simple" VALUES(4,57,'4:57','والذين امنوا وعملوا الصالحات سندخلهم جنات تجري من تحتها الانهار خالدين فيها ابدا لهم فيها ازواج مطهرة وندخلهم ظلا ظليلا');
INSERT INTO "imlaei_simple" VALUES(4,58,'4:58','ان الله يامركم ان تؤدوا الامانات الى اهلها واذا حكمتم بين الناس ان تحكموا بالعدل ان الله نعما يعظكم به ان الله كان سميعا بصيرا');
INSERT INTO "imlaei_simple" VALUES(4,59,'4:59','يا ايها الذين امنوا اطيعوا الله واطيعوا الرسول واولي الامر منكم فان تنازعتم في شيء فردوه الى الله والرسول ان كنتم تؤمنون بالله واليوم الاخر ذلك خير واحسن تاويلا');
INSERT INTO "imlaei_simple" VALUES(4,60,'4:60','الم تر الى الذين يزعمون انهم امنوا بما انزل اليك وما انزل من قبلك يريدون ان يتحاكموا الى الطاغوت وقد امروا ان يكفروا به ويريد الشيطان ان يضلهم ضلالا بعيدا');
INSERT INTO "imlaei_simple" VALUES(4,61,'4:61','واذا قيل لهم تعالوا الى ما انزل الله والى الرسول رايت المنافقين يصدون عنك صدودا');
INSERT INTO "imlaei_simple" VALUES(4,62,'4:62','فكيف اذا اصابتهم مصيبة بما قدمت ايديهم ثم جاءوك يحلفون بالله ان اردنا الا احسانا وتوفيقا');
INSERT INTO "imlaei_simple" VALUES(4,63,'4:63','اولئك الذين يعلم الله ما في قلوبهم فاعرض عنهم وعظهم وقل لهم في انفسهم قولا بليغا');
INSERT INTO "imlaei_simple" VALUES(4,64,'4:64','وما ارسلنا من رسول الا ليطاع باذن الله ولو انهم اذ ظلموا انفسهم جاءوك فاستغفروا الله واستغفر لهم الرسول لوجدوا الله توابا رحيما');
INSERT INTO "imlaei_simple" VALUES(4,65,'4:65','فلا وربك لا يؤمنون حتى يحكموك فيما شجر بينهم ثم لا يجدوا في انفسهم حرجا مما قضيت ويسلموا تسليما');
INSERT INTO "imlaei_simple" VALUES(4,66,'4:66','ولو انا كتبنا عليهم ان اقتلوا انفسكم او اخرجوا من دياركم ما فعلوه الا قليل منهم ولو انهم فعلوا ما يوعظون به لكان خيرا لهم واشد تثبيتا');
INSERT INTO "imlaei_simple" VALUES(4,67,'4:67','واذا لاتيناهم من لدنا اجرا عظيما');
INSERT INTO "imlaei_simple" VALUES(4,68,'4:68','ولهديناهم صراطا مستقيما');
INSERT INTO "imlaei_simple" VALUES(4,69,'4:69','ومن يطع الله والرسول فاولئك مع الذين انعم الله عليهم من النبيين والصديقين والشهداء والصالحين وحسن اولئك رفيقا');
INSERT INTO "imlaei_simple" VALUES(4,70,'4:70','ذلك الفضل من الله وكفى بالله عليما');
INSERT INTO "imlaei_simple" VALUES(4,71,'4:71','يا ايها الذين امنوا خذوا حذركم فانفروا ثبات او انفروا جميعا');
INSERT INTO "imlaei_simple" VALUES(4,72,'4:72','وان منكم لمن ليبطئن فان اصابتكم مصيبة قال قد انعم الله علي اذ لم اكن معهم شهيدا');
INSERT INTO "imlaei_simple" VALUES(4,73,'4:73','ولئن اصابكم فضل من الله ليقولن كان لم تكن بينكم وبينه مودة يا ليتني كنت معهم فافوز فوزا عظيما');
INSERT INTO "imlaei_simple" VALUES(4,74,'4:74','فليقاتل في سبيل الله الذين يشرون الحياة الدنيا بالاخرة ومن يقاتل في سبيل الله فيقتل او يغلب فسوف نؤتيه اجرا عظيما');
INSERT INTO "imlaei_simple" VALUES(4,75,'4:75','وما لكم لا تقاتلون في سبيل الله والمستضعفين من الرجال والنساء والولدان الذين يقولون ربنا اخرجنا من هذه القرية الظالم اهلها واجعل لنا من لدنك وليا واجعل لنا من لدنك نصيرا');
INSERT INTO "imlaei_simple" VALUES(4,76,'4:76','الذين امنوا يقاتلون في سبيل الله والذين كفروا يقاتلون في سبيل الطاغوت فقاتلوا اولياء الشيطان ان كيد الشيطان كان ضعيفا');
INSERT INTO "imlaei_simple" VALUES(4,77,'4:77','الم تر الى الذين قيل لهم كفوا ايديكم واقيموا الصلاة واتوا الزكاة فلما كتب عليهم القتال اذا فريق منهم يخشون الناس كخشية الله او اشد خشية وقالوا ربنا لم كتبت علينا القتال لولا اخرتنا الى اجل قريب قل متاع الدنيا قليل والاخرة خير لمن اتقى ولا تظلمون فتيلا');
INSERT INTO "imlaei_simple" VALUES(4,78,'4:78','اينما تكونوا يدرككم الموت ولو كنتم في بروج مشيدة وان تصبهم حسنة يقولوا هذه من عند الله وان تصبهم سيئة يقولوا هذه من عندك قل كل من عند الله فمال هؤلاء القوم لا يكادون يفقهون حديثا');
INSERT INTO "imlaei_simple" VALUES(4,79,'4:79','ما اصابك من حسنة فمن الله وما اصابك من سيئة فمن نفسك وارسلناك للناس رسولا وكفى بالله شهيدا');
INSERT INTO "imlaei_simple" VALUES(4,80,'4:80','من يطع الرسول فقد اطاع الله ومن تولى فما ارسلناك عليهم حفيظا');
INSERT INTO "imlaei_simple" VALUES(4,81,'4:81','ويقولون طاعة فاذا برزوا من عندك بيت طائفة منهم غير الذي تقول والله يكتب ما يبيتون فاعرض عنهم وتوكل على الله وكفى بالله وكيلا');
INSERT INTO "imlaei_simple" VALUES(4,82,'4:82','افلا يتدبرون القران ولو كان من عند غير الله لوجدوا فيه اختلافا كثيرا');
INSERT INTO "imlaei_simple" VALUES(4,83,'4:83','واذا جاءهم امر من الامن او الخوف اذاعوا به ولو ردوه الى الرسول والى اولي الامر منهم لعلمه الذين يستنبطونه منهم ولولا فضل الله عليكم ورحمته لاتبعتم الشيطان الا قليلا');
INSERT INTO "imlaei_simple" VALUES(4,84,'4:84','فقاتل في سبيل الله لا تكلف الا نفسك وحرض المؤمنين عسى الله ان يكف باس الذين كفروا والله اشد باسا واشد تنكيلا');
INSERT INTO "imlaei_simple" VALUES(4,85,'4:85','من يشفع شفاعة حسنة يكن له نصيب منها ومن يشفع شفاعة سيئة يكن له كفل منها وكان الله على كل شيء مقيتا');
INSERT INTO "imlaei_simple" VALUES(4,86,'4:86','واذا حييتم بتحية فحيوا باحسن منها او ردوها ان الله كان على كل شيء حسيبا');
INSERT INTO "imlaei_simple" VALUES(4,87,'4:87','الله لا اله الا هو ليجمعنكم الى يوم القيامة لا ريب فيه ومن اصدق من الله حديثا');
INSERT INTO "imlaei_simple" VALUES(4,88,'4:88','فما لكم في المنافقين فئتين والله اركسهم بما كسبوا اتريدون ان تهدوا من اضل الله ومن يضلل الله فلن تجد له سبيلا');
INSERT INTO "imlaei_simple" VALUES(4,89,'4:89','ودوا لو تكفرون كما كفروا فتكونون سواء فلا تتخذوا منهم اولياء حتى يهاجروا في سبيل الله فان تولوا فخذوهم واقتلوهم حيث وجدتموهم ولا تتخذوا منهم وليا ولا نصيرا');
INSERT INTO "imlaei_simple" VALUES(4,90,'4:90','الا الذين يصلون الى قوم بينكم وبينهم ميثاق او جاءوكم حصرت صدورهم ان يقاتلوكم او يقاتلوا قومهم ولو شاء الله لسلطهم عليكم فلقاتلوكم فان اعتزلوكم فلم يقاتلوكم والقوا اليكم السلم فما جعل الله لكم عليهم سبيلا');
INSERT INTO "imlaei_simple" VALUES(4,91,'4:91','ستجدون اخرين يريدون ان يامنوكم ويامنوا قومهم كل ما ردوا الى الفتنة اركسوا فيها فان لم يعتزلوكم ويلقوا اليكم السلم ويكفوا ايديهم فخذوهم واقتلوهم حيث ثقفتموهم واولئكم جعلنا لكم عليهم سلطانا مبينا');
INSERT INTO "imlaei_simple" VALUES(4,92,'4:92','وما كان لمؤمن ان يقتل مؤمنا الا خطا ومن قتل مؤمنا خطا فتحرير رقبة مؤمنة ودية مسلمة الى اهله الا ان يصدقوا فان كان من قوم عدو لكم وهو مؤمن فتحرير رقبة مؤمنة وان كان من قوم بينكم وبينهم ميثاق فدية مسلمة الى اهله وتحرير رقبة مؤمنة فمن لم يجد فصيام شهرين متتابعين توبة من الله وكان الله عليما حكيما');
INSERT INTO "imlaei_simple" VALUES(4,93,'4:93','ومن يقتل مؤمنا متعمدا فجزاؤه جهنم خالدا فيها وغضب الله عليه ولعنه واعد له عذابا عظيما');
INSERT INTO "imlaei_simple" VALUES(4,94,'4:94','يا ايها الذين امنوا اذا ضربتم في سبيل الله فتبينوا ولا تقولوا لمن القى اليكم السلام لست مؤمنا تبتغون عرض الحياة الدنيا فعند الله مغانم كثيرة كذلك كنتم من قبل فمن الله عليكم فتبينوا ان الله كان بما تعملون خبيرا');
INSERT INTO "imlaei_simple" VALUES(4,95,'4:95','لا يستوي القاعدون من المؤمنين غير اولي الضرر والمجاهدون في سبيل الله باموالهم وانفسهم فضل الله المجاهدين باموالهم وانفسهم على القاعدين درجة وكلا وعد الله الحسنى وفضل الله المجاهدين على القاعدين اجرا عظيما');
INSERT INTO "imlaei_simple" VALUES(4,96,'4:96','درجات منه ومغفرة ورحمة وكان الله غفورا رحيما');
INSERT INTO "imlaei_simple" VALUES(4,97,'4:97','ان الذين توفاهم الملائكة ظالمي انفسهم قالوا فيم كنتم قالوا كنا مستضعفين في الارض قالوا الم تكن ارض الله واسعة فتهاجروا فيها فاولئك ماواهم جهنم وساءت مصيرا');
INSERT INTO "imlaei_simple" VALUES(4,98,'4:98','الا المستضعفين من الرجال والنساء والولدان لا يستطيعون حيلة ولا يهتدون سبيلا');
INSERT INTO "imlaei_simple" VALUES(4,99,'4:99','فاولئك عسى الله ان يعفو عنهم وكان الله عفوا غفورا');
INSERT INTO "imlaei_simple" VALUES(4,100,'4:100','ومن يهاجر في سبيل الله يجد في الارض مراغما كثيرا وسعة ومن يخرج من بيته مهاجرا الى الله ورسوله ثم يدركه الموت فقد وقع اجره على الله وكان الله غفورا رحيما');
INSERT INTO "imlaei_simple" VALUES(4,101,'4:101','واذا ضربتم في الارض فليس عليكم جناح ان تقصروا من الصلاة ان خفتم ان يفتنكم الذين كفروا ان الكافرين كانوا لكم عدوا مبينا');
INSERT INTO "imlaei_simple" VALUES(4,102,'4:102','واذا كنت فيهم فاقمت لهم الصلاة فلتقم طائفة منهم معك ولياخذوا اسلحتهم فاذا سجدوا فليكونوا من ورائكم ولتات طائفة اخرى لم يصلوا فليصلوا معك ولياخذوا حذرهم واسلحتهم ود الذين كفروا لو تغفلون عن اسلحتكم وامتعتكم فيميلون عليكم ميلة واحدة ولا جناح عليكم ان كان بكم اذى من مطر او كنتم مرضى ان تضعوا اسلحتكم وخذوا حذركم ان الله اعد للكافرين عذابا مهينا');
INSERT INTO "imlaei_simple" VALUES(4,103,'4:103','فاذا قضيتم الصلاة فاذكروا الله قياما وقعودا وعلى جنوبكم فاذا اطماننتم فاقيموا الصلاة ان الصلاة كانت على المؤمنين كتابا موقوتا');
INSERT INTO "imlaei_simple" VALUES(4,104,'4:104','ولا تهنوا في ابتغاء القوم ان تكونوا تالمون فانهم يالمون كما تالمون وترجون من الله ما لا يرجون وكان الله عليما حكيما');
INSERT INTO "imlaei_simple" VALUES(4,105,'4:105','انا انزلنا اليك الكتاب بالحق لتحكم بين الناس بما اراك الله ولا تكن للخائنين خصيما');
INSERT INTO "imlaei_simple" VALUES(4,106,'4:106','واستغفر الله ان الله كان غفورا رحيما');
INSERT INTO "imlaei_simple" VALUES(4,107,'4:107','ولا تجادل عن الذين يختانون انفسهم ان الله لا يحب من كان خوانا اثيما');
INSERT INTO "imlaei_simple" VALUES(4,108,'4:108','يستخفون من الناس ولا يستخفون من الله وهو معهم اذ يبيتون ما لا يرضى من القول وكان الله بما يعملون محيطا');
INSERT INTO "imlaei_simple" VALUES(4,109,'4:109','ها انتم هؤلاء جادلتم عنهم في الحياة الدنيا فمن يجادل الله عنهم يوم القيامة ام من يكون عليهم وكيلا');
INSERT INTO "imlaei_simple" VALUES(4,110,'4:110','ومن يعمل سوءا او يظلم نفسه ثم يستغفر الله يجد الله غفورا رحيما');
INSERT INTO "imlaei_simple" VALUES(4,111,'4:111','ومن يكسب اثما فانما يكسبه على نفسه وكان الله عليما حكيما');
INSERT INTO "imlaei_simple" VALUES(4,112,'4:112','ومن يكسب خطيئة او اثما ثم يرم به بريئا فقد احتمل بهتانا واثما مبينا');
INSERT INTO "imlaei_simple" VALUES(4,113,'4:113','ولولا فضل الله عليك ورحمته لهمت طائفة منهم ان يضلوك وما يضلون الا انفسهم وما يضرونك من شيء وانزل الله عليك الكتاب والحكمة وعلمك ما لم تكن تعلم وكان فضل الله عليك عظيما');
INSERT INTO "imlaei_simple" VALUES(4,114,'4:114','لا خير في كثير من نجواهم الا من امر بصدقة او معروف او اصلاح بين الناس ومن يفعل ذلك ابتغاء مرضات الله فسوف نؤتيه اجرا عظيما');
INSERT INTO "imlaei_simple" VALUES(4,115,'4:115','ومن يشاقق الرسول من بعد ما تبين له الهدى ويتبع غير سبيل المؤمنين نوله ما تولى ونصله جهنم وساءت مصيرا');
INSERT INTO "imlaei_simple" VALUES(4,116,'4:116','ان الله لا يغفر ان يشرك به ويغفر ما دون ذلك لمن يشاء ومن يشرك بالله فقد ضل ضلالا بعيدا');
INSERT INTO "imlaei_simple" VALUES(4,117,'4:117','ان يدعون من دونه الا اناثا وان يدعون الا شيطانا مريدا');
INSERT INTO "imlaei_simple" VALUES(4,118,'4:118','لعنه الله وقال لاتخذن من عبادك نصيبا مفروضا');
INSERT INTO "imlaei_simple" VALUES(4,119,'4:119','ولاضلنهم ولامنينهم ولامرنهم فليبتكن اذان الانعام ولامرنهم فليغيرن خلق الله ومن يتخذ الشيطان وليا من دون الله فقد خسر خسرانا مبينا');
INSERT INTO "imlaei_simple" VALUES(4,120,'4:120','يعدهم ويمنيهم وما يعدهم الشيطان الا غرورا');
INSERT INTO "imlaei_simple" VALUES(4,121,'4:121','اولئك ماواهم جهنم ولا يجدون عنها محيصا');
INSERT INTO "imlaei_simple" VALUES(4,122,'4:122','والذين امنوا وعملوا الصالحات سندخلهم جنات تجري من تحتها الانهار خالدين فيها ابدا وعد الله حقا ومن اصدق من الله قيلا');
INSERT INTO "imlaei_simple" VALUES(4,123,'4:123','ليس بامانيكم ولا اماني اهل الكتاب من يعمل سوءا يجز به ولا يجد له من دون الله وليا ولا نصيرا');
INSERT INTO "imlaei_simple" VALUES(4,124,'4:124','ومن يعمل من الصالحات من ذكر او انثى وهو مؤمن فاولئك يدخلون الجنة ولا يظلمون نقيرا');
INSERT INTO "imlaei_simple" VALUES(4,125,'4:125','ومن احسن دينا ممن اسلم وجهه لله وهو محسن واتبع ملة ابراهيم حنيفا واتخذ الله ابراهيم خليلا');
INSERT INTO "imlaei_simple" VALUES(4,126,'4:126','ولله ما في السماوات وما في الارض وكان الله بكل شيء محيطا');
INSERT INTO "imlaei_simple" VALUES(4,127,'4:127','ويستفتونك في النساء قل الله يفتيكم فيهن وما يتلى عليكم في الكتاب في يتامى النساء اللاتي لا تؤتونهن ما كتب لهن وترغبون ان تنكحوهن والمستضعفين من الولدان وان تقوموا لليتامى بالقسط وما تفعلوا من خير فان الله كان به عليما');
INSERT INTO "imlaei_simple" VALUES(4,128,'4:128','وان امراة خافت من بعلها نشوزا او اعراضا فلا جناح عليهما ان يصلحا بينهما صلحا والصلح خير واحضرت الانفس الشح وان تحسنوا وتتقوا فان الله كان بما تعملون خبيرا');
INSERT INTO "imlaei_simple" VALUES(4,129,'4:129','ولن تستطيعوا ان تعدلوا بين النساء ولو حرصتم فلا تميلوا كل الميل فتذروها كالمعلقة وان تصلحوا وتتقوا فان الله كان غفورا رحيما');
INSERT INTO "imlaei_simple" VALUES(4,130,'4:130','وان يتفرقا يغن الله كلا من سعته وكان الله واسعا حكيما');
INSERT INTO "imlaei_simple" VALUES(4,131,'4:131','ولله ما في السماوات وما في الارض ولقد وصينا الذين اوتوا الكتاب من قبلكم واياكم ان اتقوا الله وان تكفروا فان لله ما في السماوات وما في الارض وكان الله غنيا حميدا');
INSERT INTO "imlaei_simple" VALUES(4,132,'4:132','ولله ما في السماوات وما في الارض وكفى بالله وكيلا');
INSERT INTO "imlaei_simple" VALUES(4,133,'4:133','ان يشا يذهبكم ايها الناس ويات باخرين وكان الله على ذلك قديرا');
INSERT INTO "imlaei_simple" VALUES(4,134,'4:134','من كان يريد ثواب الدنيا فعند الله ثواب الدنيا والاخرة وكان الله سميعا بصيرا');
INSERT INTO "imlaei_simple" VALUES(4,135,'4:135','يا ايها الذين امنوا كونوا قوامين بالقسط شهداء لله ولو على انفسكم او الوالدين والاقربين ان يكن غنيا او فقيرا فالله اولى بهما فلا تتبعوا الهوى ان تعدلوا وان تلووا او تعرضوا فان الله كان بما تعملون خبيرا');
INSERT INTO "imlaei_simple" VALUES(4,136,'4:136','يا ايها الذين امنوا امنوا بالله ورسوله والكتاب الذي نزل على رسوله والكتاب الذي انزل من قبل ومن يكفر بالله وملائكته وكتبه ورسله واليوم الاخر فقد ضل ضلالا بعيدا');
INSERT INTO "imlaei_simple" VALUES(4,137,'4:137','ان الذين امنوا ثم كفروا ثم امنوا ثم كفروا ثم ازدادوا كفرا لم يكن الله ليغفر لهم ولا ليهديهم سبيلا');
INSERT INTO "imlaei_simple" VALUES(4,138,'4:138','بشر المنافقين بان لهم عذابا اليما');
INSERT INTO "imlaei_simple" VALUES(4,139,'4:139','الذين يتخذون الكافرين اولياء من دون المؤمنين ايبتغون عندهم العزة فان العزة لله جميعا');
INSERT INTO "imlaei_simple" VALUES(4,140,'4:140','وقد نزل عليكم في الكتاب ان اذا سمعتم ايات الله يكفر بها ويستهزا بها فلا تقعدوا معهم حتى يخوضوا في حديث غيره انكم اذا مثلهم ان الله جامع المنافقين والكافرين في جهنم جميعا');
INSERT INTO "imlaei_simple" VALUES(4,141,'4:141','الذين يتربصون بكم فان كان لكم فتح من الله قالوا الم نكن معكم وان كان للكافرين نصيب قالوا الم نستحوذ عليكم ونمنعكم من المؤمنين فالله يحكم بينكم يوم القيامة ولن يجعل الله للكافرين على المؤمنين سبيلا');
INSERT INTO "imlaei_simple" VALUES(4,142,'4:142','ان المنافقين يخادعون الله وهو خادعهم واذا قاموا الى الصلاة قاموا كسالى يراءون الناس ولا يذكرون الله الا قليلا');
INSERT INTO "imlaei_simple" VALUES(4,143,'4:143','مذبذبين بين ذلك لا الى هؤلاء ولا الى هؤلاء ومن يضلل الله فلن تجد له سبيلا');
INSERT INTO "imlaei_simple" VALUES(4,144,'4:144','يا ايها الذين امنوا لا تتخذوا الكافرين اولياء من دون المؤمنين اتريدون ان تجعلوا لله عليكم سلطانا مبينا');
INSERT INTO "imlaei_simple" VALUES(4,145,'4:145','ان المنافقين في الدرك الاسفل من النار ولن تجد لهم نصيرا');
INSERT INTO "imlaei_simple" VALUES(4,146,'4:146','الا الذين تابوا واصلحوا واعتصموا بالله واخلصوا دينهم لله فاولئك مع المؤمنين وسوف يؤت الله المؤمنين اجرا عظيما');
INSERT INTO "imlaei_simple" VALUES(4,147,'4:147','ما يفعل الله بعذابكم ان شكرتم وامنتم وكان الله شاكرا عليما');
INSERT INTO "imlaei_simple" VALUES(4,148,'4:148','لا يحب الله الجهر بالسوء من القول الا من ظلم وكان الله سميعا عليما');
INSERT INTO "imlaei_simple" VALUES(4,149,'4:149','ان تبدوا خيرا او تخفوه او تعفوا عن سوء فان الله كان عفوا قديرا');
INSERT INTO "imlaei_simple" VALUES(4,150,'4:150','ان الذين يكفرون بالله ورسله ويريدون ان يفرقوا بين الله ورسله ويقولون نؤمن ببعض ونكفر ببعض ويريدون ان يتخذوا بين ذلك سبيلا');
INSERT INTO "imlaei_simple" VALUES(4,151,'4:151','اولئك هم الكافرون حقا واعتدنا للكافرين عذابا مهينا');
INSERT INTO "imlaei_simple" VALUES(4,152,'4:152','والذين امنوا بالله ورسله ولم يفرقوا بين احد منهم اولئك سوف يؤتيهم اجورهم وكان الله غفورا رحيما');
INSERT INTO "imlaei_simple" VALUES(4,153,'4:153','يسالك اهل الكتاب ان تنزل عليهم كتابا من السماء فقد سالوا موسى اكبر من ذلك فقالوا ارنا الله جهرة فاخذتهم الصاعقة بظلمهم ثم اتخذوا العجل من بعد ما جاءتهم البينات فعفونا عن ذلك واتينا موسى سلطانا مبينا');
INSERT INTO "imlaei_simple" VALUES(4,154,'4:154','ورفعنا فوقهم الطور بميثاقهم وقلنا لهم ادخلوا الباب سجدا وقلنا لهم لا تعدوا في السبت واخذنا منهم ميثاقا غليظا');
INSERT INTO "imlaei_simple" VALUES(4,155,'4:155','فبما نقضهم ميثاقهم وكفرهم بايات الله وقتلهم الانبياء بغير حق وقولهم قلوبنا غلف بل طبع الله عليها بكفرهم فلا يؤمنون الا قليلا');
INSERT INTO "imlaei_simple" VALUES(4,156,'4:156','وبكفرهم وقولهم على مريم بهتانا عظيما');
INSERT INTO "imlaei_simple" VALUES(4,157,'4:157','وقولهم انا قتلنا المسيح عيسى ابن مريم رسول الله وما قتلوه وما صلبوه ولكن شبه لهم وان الذين اختلفوا فيه لفي شك منه ما لهم به من علم الا اتباع الظن وما قتلوه يقينا');
INSERT INTO "imlaei_simple" VALUES(4,158,'4:158','بل رفعه الله اليه وكان الله عزيزا حكيما');
INSERT INTO "imlaei_simple" VALUES(4,159,'4:159','وان من اهل الكتاب الا ليؤمنن به قبل موته ويوم القيامة يكون عليهم شهيدا');
INSERT INTO "imlaei_simple" VALUES(4,160,'4:160','فبظلم من الذين هادوا حرمنا عليهم طيبات احلت لهم وبصدهم عن سبيل الله كثيرا');
INSERT INTO "imlaei_simple" VALUES(4,161,'4:161','واخذهم الربا وقد نهوا عنه واكلهم اموال الناس بالباطل واعتدنا للكافرين منهم عذابا اليما');
INSERT INTO "imlaei_simple" VALUES(4,162,'4:162','لكن الراسخون في العلم منهم والمؤمنون يؤمنون بما انزل اليك وما انزل من قبلك والمقيمين الصلاة والمؤتون الزكاة والمؤمنون بالله واليوم الاخر اولئك سنؤتيهم اجرا عظيما');
INSERT INTO "imlaei_simple" VALUES(4,163,'4:163','انا اوحينا اليك كما اوحينا الى نوح والنبيين من بعده واوحينا الى ابراهيم واسماعيل واسحاق ويعقوب والاسباط وعيسى وايوب ويونس وهارون وسليمان واتينا داوود زبورا');
INSERT INTO "imlaei_simple" VALUES(4,164,'4:164','ورسلا قد قصصناهم عليك من قبل ورسلا لم نقصصهم عليك وكلم الله موسى تكليما');
INSERT INTO "imlaei_simple" VALUES(4,165,'4:165','رسلا مبشرين ومنذرين لئلا يكون للناس على الله حجة بعد الرسل وكان الله عزيزا حكيما');
INSERT INTO "imlaei_simple" VALUES(4,166,'4:166','لكن الله يشهد بما انزل اليك انزله بعلمه والملائكة يشهدون وكفى بالله شهيدا');
INSERT INTO "imlaei_simple" VALUES(4,167,'4:167','ان الذين كفروا وصدوا عن سبيل الله قد ضلوا ضلالا بعيدا');
INSERT INTO "imlaei_simple" VALUES(4,168,'4:168','ان الذين كفروا وظلموا لم يكن الله ليغفر لهم ولا ليهديهم طريقا');
INSERT INTO "imlaei_simple" VALUES(4,169,'4:169','الا طريق جهنم خالدين فيها ابدا وكان ذلك على الله يسيرا');
INSERT INTO "imlaei_simple" VALUES(4,170,'4:170','يا ايها الناس قد جاءكم الرسول بالحق من ربكم فامنوا خيرا لكم وان تكفروا فان لله ما في السماوات والارض وكان الله عليما حكيما');
INSERT INTO "imlaei_simple" VALUES(4,171,'4:171','يا اهل الكتاب لا تغلوا في دينكم ولا تقولوا على الله الا الحق انما المسيح عيسى ابن مريم رسول الله وكلمته القاها الى مريم وروح منه فامنوا بالله ورسله ولا تقولوا ثلاثة انتهوا خيرا لكم انما الله اله واحد سبحانه ان يكون له ولد له ما في السماوات وما في الارض وكفى بالله وكيلا');
INSERT INTO "imlaei_simple" VALUES(4,172,'4:172','لن يستنكف المسيح ان يكون عبدا لله ولا الملائكة المقربون ومن يستنكف عن عبادته ويستكبر فسيحشرهم اليه جميعا');
INSERT INTO "imlaei_simple" VALUES(4,173,'4:173','فاما الذين امنوا وعملوا الصالحات فيوفيهم اجورهم ويزيدهم من فضله واما الذين استنكفوا واستكبروا فيعذبهم عذابا اليما ولا يجدون لهم من دون الله وليا ولا نصيرا');
INSERT INTO "imlaei_simple" VALUES(4,174,'4:174','يا ايها الناس قد جاءكم برهان من ربكم وانزلنا اليكم نورا مبينا');
INSERT INTO "imlaei_simple" VALUES(4,175,'4:175','فاما الذين امنوا بالله واعتصموا به فسيدخلهم في رحمة منه وفضل ويهديهم اليه صراطا مستقيما');
INSERT INTO "imlaei_simple" VALUES(4,176,'4:176','يستفتونك قل الله يفتيكم في الكلالة ان امرؤ هلك ليس له ولد وله اخت فلها نصف ما ترك وهو يرثها ان لم يكن لها ولد فان كانتا اثنتين فلهما الثلثان مما ترك وان كانوا اخوة رجالا ونساء فللذكر مثل حظ الانثيين يبين الله لكم ان تضلوا والله بكل شيء عليم');
INSERT INTO "imlaei_simple" VALUES(5,1,'5:1','يا ايها الذين امنوا اوفوا بالعقود احلت لكم بهيمة الانعام الا ما يتلى عليكم غير محلي الصيد وانتم حرم ان الله يحكم ما يريد');
INSERT INTO "imlaei_simple" VALUES(5,2,'5:2','يا ايها الذين امنوا لا تحلوا شعائر الله ولا الشهر الحرام ولا الهدي ولا القلائد ولا امين البيت الحرام يبتغون فضلا من ربهم ورضوانا واذا حللتم فاصطادوا ولا يجرمنكم شنان قوم ان صدوكم عن المسجد الحرام ان تعتدوا وتعاونوا على البر والتقوى ولا تعاونوا على الاثم والعدوان واتقوا الله ان الله شديد العقاب');
INSERT INTO "imlaei_simple" VALUES(5,3,'5:3','حرمت عليكم الميتة والدم ولحم الخنزير وما اهل لغير الله به والمنخنقة والموقوذة والمتردية والنطيحة وما اكل السبع الا ما ذكيتم وما ذبح على النصب وان تستقسموا بالازلام ذلكم فسق اليوم يئس الذين كفروا من دينكم فلا تخشوهم واخشون اليوم اكملت لكم دينكم واتممت عليكم نعمتي ورضيت لكم الاسلام دينا فمن اضطر في مخمصة غير متجانف لاثم فان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(5,4,'5:4','يسالونك ماذا احل لهم قل احل لكم الطيبات وما علمتم من الجوارح مكلبين تعلمونهن مما علمكم الله فكلوا مما امسكن عليكم واذكروا اسم الله عليه واتقوا الله ان الله سريع الحساب');
INSERT INTO "imlaei_simple" VALUES(5,5,'5:5','اليوم احل لكم الطيبات وطعام الذين اوتوا الكتاب حل لكم وطعامكم حل لهم والمحصنات من المؤمنات والمحصنات من الذين اوتوا الكتاب من قبلكم اذا اتيتموهن اجورهن محصنين غير مسافحين ولا متخذي اخدان ومن يكفر بالايمان فقد حبط عمله وهو في الاخرة من الخاسرين');
INSERT INTO "imlaei_simple" VALUES(5,6,'5:6','يا ايها الذين امنوا اذا قمتم الى الصلاة فاغسلوا وجوهكم وايديكم الى المرافق وامسحوا برءوسكم وارجلكم الى الكعبين وان كنتم جنبا فاطهروا وان كنتم مرضى او على سفر او جاء احد منكم من الغائط او لامستم النساء فلم تجدوا ماء فتيمموا صعيدا طيبا فامسحوا بوجوهكم وايديكم منه ما يريد الله ليجعل عليكم من حرج ولكن يريد ليطهركم وليتم نعمته عليكم لعلكم تشكرون');
INSERT INTO "imlaei_simple" VALUES(5,7,'5:7','واذكروا نعمة الله عليكم وميثاقه الذي واثقكم به اذ قلتم سمعنا واطعنا واتقوا الله ان الله عليم بذات الصدور');
INSERT INTO "imlaei_simple" VALUES(5,8,'5:8','يا ايها الذين امنوا كونوا قوامين لله شهداء بالقسط ولا يجرمنكم شنان قوم على الا تعدلوا اعدلوا هو اقرب للتقوى واتقوا الله ان الله خبير بما تعملون');
INSERT INTO "imlaei_simple" VALUES(5,9,'5:9','وعد الله الذين امنوا وعملوا الصالحات لهم مغفرة واجر عظيم');
INSERT INTO "imlaei_simple" VALUES(5,10,'5:10','والذين كفروا وكذبوا باياتنا اولئك اصحاب الجحيم');
INSERT INTO "imlaei_simple" VALUES(5,11,'5:11','يا ايها الذين امنوا اذكروا نعمت الله عليكم اذ هم قوم ان يبسطوا اليكم ايديهم فكف ايديهم عنكم واتقوا الله وعلى الله فليتوكل المؤمنون');
INSERT INTO "imlaei_simple" VALUES(5,12,'5:12','ولقد اخذ الله ميثاق بني اسرائيل وبعثنا منهم اثني عشر نقيبا وقال الله اني معكم لئن اقمتم الصلاة واتيتم الزكاة وامنتم برسلي وعزرتموهم واقرضتم الله قرضا حسنا لاكفرن عنكم سيئاتكم ولادخلنكم جنات تجري من تحتها الانهار فمن كفر بعد ذلك منكم فقد ضل سواء السبيل');
INSERT INTO "imlaei_simple" VALUES(5,13,'5:13','فبما نقضهم ميثاقهم لعناهم وجعلنا قلوبهم قاسية يحرفون الكلم عن مواضعه ونسوا حظا مما ذكروا به ولا تزال تطلع على خائنة منهم الا قليلا منهم فاعف عنهم واصفح ان الله يحب المحسنين');
INSERT INTO "imlaei_simple" VALUES(5,14,'5:14','ومن الذين قالوا انا نصارى اخذنا ميثاقهم فنسوا حظا مما ذكروا به فاغرينا بينهم العداوة والبغضاء الى يوم القيامة وسوف ينبئهم الله بما كانوا يصنعون');
INSERT INTO "imlaei_simple" VALUES(5,15,'5:15','يا اهل الكتاب قد جاءكم رسولنا يبين لكم كثيرا مما كنتم تخفون من الكتاب ويعفو عن كثير قد جاءكم من الله نور وكتاب مبين');
INSERT INTO "imlaei_simple" VALUES(5,16,'5:16','يهدي به الله من اتبع رضوانه سبل السلام ويخرجهم من الظلمات الى النور باذنه ويهديهم الى صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(5,17,'5:17','لقد كفر الذين قالوا ان الله هو المسيح ابن مريم قل فمن يملك من الله شيئا ان اراد ان يهلك المسيح ابن مريم وامه ومن في الارض جميعا ولله ملك السماوات والارض وما بينهما يخلق ما يشاء والله على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(5,18,'5:18','وقالت اليهود والنصارى نحن ابناء الله واحباؤه قل فلم يعذبكم بذنوبكم بل انتم بشر ممن خلق يغفر لمن يشاء ويعذب من يشاء ولله ملك السماوات والارض وما بينهما واليه المصير');
INSERT INTO "imlaei_simple" VALUES(5,19,'5:19','يا اهل الكتاب قد جاءكم رسولنا يبين لكم على فترة من الرسل ان تقولوا ما جاءنا من بشير ولا نذير فقد جاءكم بشير ونذير والله على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(5,20,'5:20','واذ قال موسى لقومه يا قوم اذكروا نعمة الله عليكم اذ جعل فيكم انبياء وجعلكم ملوكا واتاكم ما لم يؤت احدا من العالمين');
INSERT INTO "imlaei_simple" VALUES(5,21,'5:21','يا قوم ادخلوا الارض المقدسة التي كتب الله لكم ولا ترتدوا على ادباركم فتنقلبوا خاسرين');
INSERT INTO "imlaei_simple" VALUES(5,22,'5:22','قالوا يا موسى ان فيها قوما جبارين وانا لن ندخلها حتى يخرجوا منها فان يخرجوا منها فانا داخلون');
INSERT INTO "imlaei_simple" VALUES(5,23,'5:23','قال رجلان من الذين يخافون انعم الله عليهما ادخلوا عليهم الباب فاذا دخلتموه فانكم غالبون وعلى الله فتوكلوا ان كنتم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(5,24,'5:24','قالوا يا موسى انا لن ندخلها ابدا ما داموا فيها فاذهب انت وربك فقاتلا انا هاهنا قاعدون');
INSERT INTO "imlaei_simple" VALUES(5,25,'5:25','قال رب اني لا املك الا نفسي واخي فافرق بيننا وبين القوم الفاسقين');
INSERT INTO "imlaei_simple" VALUES(5,26,'5:26','قال فانها محرمة عليهم اربعين سنة يتيهون في الارض فلا تاس على القوم الفاسقين');
INSERT INTO "imlaei_simple" VALUES(5,27,'5:27','واتل عليهم نبا ابني ادم بالحق اذ قربا قربانا فتقبل من احدهما ولم يتقبل من الاخر قال لاقتلنك قال انما يتقبل الله من المتقين');
INSERT INTO "imlaei_simple" VALUES(5,28,'5:28','لئن بسطت الي يدك لتقتلني ما انا بباسط يدي اليك لاقتلك اني اخاف الله رب العالمين');
INSERT INTO "imlaei_simple" VALUES(5,29,'5:29','اني اريد ان تبوء باثمي واثمك فتكون من اصحاب النار وذلك جزاء الظالمين');
INSERT INTO "imlaei_simple" VALUES(5,30,'5:30','فطوعت له نفسه قتل اخيه فقتله فاصبح من الخاسرين');
INSERT INTO "imlaei_simple" VALUES(5,31,'5:31','فبعث الله غرابا يبحث في الارض ليريه كيف يواري سوءة اخيه قال يا ويلتا اعجزت ان اكون مثل هذا الغراب فاواري سوءة اخي فاصبح من النادمين');
INSERT INTO "imlaei_simple" VALUES(5,32,'5:32','من اجل ذلك كتبنا على بني اسرائيل انه من قتل نفسا بغير نفس او فساد في الارض فكانما قتل الناس جميعا ومن احياها فكانما احيا الناس جميعا ولقد جاءتهم رسلنا بالبينات ثم ان كثيرا منهم بعد ذلك في الارض لمسرفون');
INSERT INTO "imlaei_simple" VALUES(5,33,'5:33','انما جزاء الذين يحاربون الله ورسوله ويسعون في الارض فسادا ان يقتلوا او يصلبوا او تقطع ايديهم وارجلهم من خلاف او ينفوا من الارض ذلك لهم خزي في الدنيا ولهم في الاخرة عذاب عظيم');
INSERT INTO "imlaei_simple" VALUES(5,34,'5:34','الا الذين تابوا من قبل ان تقدروا عليهم فاعلموا ان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(5,35,'5:35','يا ايها الذين امنوا اتقوا الله وابتغوا اليه الوسيلة وجاهدوا في سبيله لعلكم تفلحون');
INSERT INTO "imlaei_simple" VALUES(5,36,'5:36','ان الذين كفروا لو ان لهم ما في الارض جميعا ومثله معه ليفتدوا به من عذاب يوم القيامة ما تقبل منهم ولهم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(5,37,'5:37','يريدون ان يخرجوا من النار وما هم بخارجين منها ولهم عذاب مقيم');
INSERT INTO "imlaei_simple" VALUES(5,38,'5:38','والسارق والسارقة فاقطعوا ايديهما جزاء بما كسبا نكالا من الله والله عزيز حكيم');
INSERT INTO "imlaei_simple" VALUES(5,39,'5:39','فمن تاب من بعد ظلمه واصلح فان الله يتوب عليه ان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(5,40,'5:40','الم تعلم ان الله له ملك السماوات والارض يعذب من يشاء ويغفر لمن يشاء والله على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(5,41,'5:41','يا ايها الرسول لا يحزنك الذين يسارعون في الكفر من الذين قالوا امنا بافواههم ولم تؤمن قلوبهم ومن الذين هادوا سماعون للكذب سماعون لقوم اخرين لم ياتوك يحرفون الكلم من بعد مواضعه يقولون ان اوتيتم هذا فخذوه وان لم تؤتوه فاحذروا ومن يرد الله فتنته فلن تملك له من الله شيئا اولئك الذين لم يرد الله ان يطهر قلوبهم لهم في الدنيا خزي ولهم في الاخرة عذاب عظيم');
INSERT INTO "imlaei_simple" VALUES(5,42,'5:42','سماعون للكذب اكالون للسحت فان جاءوك فاحكم بينهم او اعرض عنهم وان تعرض عنهم فلن يضروك شيئا وان حكمت فاحكم بينهم بالقسط ان الله يحب المقسطين');
INSERT INTO "imlaei_simple" VALUES(5,43,'5:43','وكيف يحكمونك وعندهم التوراة فيها حكم الله ثم يتولون من بعد ذلك وما اولئك بالمؤمنين');
INSERT INTO "imlaei_simple" VALUES(5,44,'5:44','انا انزلنا التوراة فيها هدى ونور يحكم بها النبيون الذين اسلموا للذين هادوا والربانيون والاحبار بما استحفظوا من كتاب الله وكانوا عليه شهداء فلا تخشوا الناس واخشون ولا تشتروا باياتي ثمنا قليلا ومن لم يحكم بما انزل الله فاولئك هم الكافرون');
INSERT INTO "imlaei_simple" VALUES(5,45,'5:45','وكتبنا عليهم فيها ان النفس بالنفس والعين بالعين والانف بالانف والاذن بالاذن والسن بالسن والجروح قصاص فمن تصدق به فهو كفارة له ومن لم يحكم بما انزل الله فاولئك هم الظالمون');
INSERT INTO "imlaei_simple" VALUES(5,46,'5:46','وقفينا على اثارهم بعيسى ابن مريم مصدقا لما بين يديه من التوراة واتيناه الانجيل فيه هدى ونور ومصدقا لما بين يديه من التوراة وهدى وموعظة للمتقين');
INSERT INTO "imlaei_simple" VALUES(5,47,'5:47','وليحكم اهل الانجيل بما انزل الله فيه ومن لم يحكم بما انزل الله فاولئك هم الفاسقون');
INSERT INTO "imlaei_simple" VALUES(5,48,'5:48','وانزلنا اليك الكتاب بالحق مصدقا لما بين يديه من الكتاب ومهيمنا عليه فاحكم بينهم بما انزل الله ولا تتبع اهواءهم عما جاءك من الحق لكل جعلنا منكم شرعة ومنهاجا ولو شاء الله لجعلكم امة واحدة ولكن ليبلوكم في ما اتاكم فاستبقوا الخيرات الى الله مرجعكم جميعا فينبئكم بما كنتم فيه تختلفون');
INSERT INTO "imlaei_simple" VALUES(5,49,'5:49','وان احكم بينهم بما انزل الله ولا تتبع اهواءهم واحذرهم ان يفتنوك عن بعض ما انزل الله اليك فان تولوا فاعلم انما يريد الله ان يصيبهم ببعض ذنوبهم وان كثيرا من الناس لفاسقون');
INSERT INTO "imlaei_simple" VALUES(5,50,'5:50','افحكم الجاهلية يبغون ومن احسن من الله حكما لقوم يوقنون');
INSERT INTO "imlaei_simple" VALUES(5,51,'5:51','يا ايها الذين امنوا لا تتخذوا اليهود والنصارى اولياء بعضهم اولياء بعض ومن يتولهم منكم فانه منهم ان الله لا يهدي القوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(5,52,'5:52','فترى الذين في قلوبهم مرض يسارعون فيهم يقولون نخشى ان تصيبنا دائرة فعسى الله ان ياتي بالفتح او امر من عنده فيصبحوا على ما اسروا في انفسهم نادمين');
INSERT INTO "imlaei_simple" VALUES(5,53,'5:53','ويقول الذين امنوا اهؤلاء الذين اقسموا بالله جهد ايمانهم انهم لمعكم حبطت اعمالهم فاصبحوا خاسرين');
INSERT INTO "imlaei_simple" VALUES(5,54,'5:54','يا ايها الذين امنوا من يرتد منكم عن دينه فسوف ياتي الله بقوم يحبهم ويحبونه اذلة على المؤمنين اعزة على الكافرين يجاهدون في سبيل الله ولا يخافون لومة لائم ذلك فضل الله يؤتيه من يشاء والله واسع عليم');
INSERT INTO "imlaei_simple" VALUES(5,55,'5:55','انما وليكم الله ورسوله والذين امنوا الذين يقيمون الصلاة ويؤتون الزكاة وهم راكعون');
INSERT INTO "imlaei_simple" VALUES(5,56,'5:56','ومن يتول الله ورسوله والذين امنوا فان حزب الله هم الغالبون');
INSERT INTO "imlaei_simple" VALUES(5,57,'5:57','يا ايها الذين امنوا لا تتخذوا الذين اتخذوا دينكم هزوا ولعبا من الذين اوتوا الكتاب من قبلكم والكفار اولياء واتقوا الله ان كنتم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(5,58,'5:58','واذا ناديتم الى الصلاة اتخذوها هزوا ولعبا ذلك بانهم قوم لا يعقلون');
INSERT INTO "imlaei_simple" VALUES(5,59,'5:59','قل يا اهل الكتاب هل تنقمون منا الا ان امنا بالله وما انزل الينا وما انزل من قبل وان اكثركم فاسقون');
INSERT INTO "imlaei_simple" VALUES(5,60,'5:60','قل هل انبئكم بشر من ذلك مثوبة عند الله من لعنه الله وغضب عليه وجعل منهم القردة والخنازير وعبد الطاغوت اولئك شر مكانا واضل عن سواء السبيل');
INSERT INTO "imlaei_simple" VALUES(5,61,'5:61','واذا جاءوكم قالوا امنا وقد دخلوا بالكفر وهم قد خرجوا به والله اعلم بما كانوا يكتمون');
INSERT INTO "imlaei_simple" VALUES(5,62,'5:62','وترى كثيرا منهم يسارعون في الاثم والعدوان واكلهم السحت لبئس ما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(5,63,'5:63','لولا ينهاهم الربانيون والاحبار عن قولهم الاثم واكلهم السحت لبئس ما كانوا يصنعون');
INSERT INTO "imlaei_simple" VALUES(5,64,'5:64','وقالت اليهود يد الله مغلولة غلت ايديهم ولعنوا بما قالوا بل يداه مبسوطتان ينفق كيف يشاء وليزيدن كثيرا منهم ما انزل اليك من ربك طغيانا وكفرا والقينا بينهم العداوة والبغضاء الى يوم القيامة كلما اوقدوا نارا للحرب اطفاها الله ويسعون في الارض فسادا والله لا يحب المفسدين');
INSERT INTO "imlaei_simple" VALUES(5,65,'5:65','ولو ان اهل الكتاب امنوا واتقوا لكفرنا عنهم سيئاتهم ولادخلناهم جنات النعيم');
INSERT INTO "imlaei_simple" VALUES(5,66,'5:66','ولو انهم اقاموا التوراة والانجيل وما انزل اليهم من ربهم لاكلوا من فوقهم ومن تحت ارجلهم منهم امة مقتصدة وكثير منهم ساء ما يعملون');
INSERT INTO "imlaei_simple" VALUES(5,67,'5:67','يا ايها الرسول بلغ ما انزل اليك من ربك وان لم تفعل فما بلغت رسالته والله يعصمك من الناس ان الله لا يهدي القوم الكافرين');
INSERT INTO "imlaei_simple" VALUES(5,68,'5:68','قل يا اهل الكتاب لستم على شيء حتى تقيموا التوراة والانجيل وما انزل اليكم من ربكم وليزيدن كثيرا منهم ما انزل اليك من ربك طغيانا وكفرا فلا تاس على القوم الكافرين');
INSERT INTO "imlaei_simple" VALUES(5,69,'5:69','ان الذين امنوا والذين هادوا والصابئون والنصارى من امن بالله واليوم الاخر وعمل صالحا فلا خوف عليهم ولا هم يحزنون');
INSERT INTO "imlaei_simple" VALUES(5,70,'5:70','لقد اخذنا ميثاق بني اسرائيل وارسلنا اليهم رسلا كلما جاءهم رسول بما لا تهوى انفسهم فريقا كذبوا وفريقا يقتلون');
INSERT INTO "imlaei_simple" VALUES(5,71,'5:71','وحسبوا الا تكون فتنة فعموا وصموا ثم تاب الله عليهم ثم عموا وصموا كثير منهم والله بصير بما يعملون');
INSERT INTO "imlaei_simple" VALUES(5,72,'5:72','لقد كفر الذين قالوا ان الله هو المسيح ابن مريم وقال المسيح يا بني اسرائيل اعبدوا الله ربي وربكم انه من يشرك بالله فقد حرم الله عليه الجنة وماواه النار وما للظالمين من انصار');
INSERT INTO "imlaei_simple" VALUES(5,73,'5:73','لقد كفر الذين قالوا ان الله ثالث ثلاثة وما من اله الا اله واحد وان لم ينتهوا عما يقولون ليمسن الذين كفروا منهم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(5,74,'5:74','افلا يتوبون الى الله ويستغفرونه والله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(5,75,'5:75','ما المسيح ابن مريم الا رسول قد خلت من قبله الرسل وامه صديقة كانا ياكلان الطعام انظر كيف نبين لهم الايات ثم انظر انى يؤفكون');
INSERT INTO "imlaei_simple" VALUES(5,76,'5:76','قل اتعبدون من دون الله ما لا يملك لكم ضرا ولا نفعا والله هو السميع العليم');
INSERT INTO "imlaei_simple" VALUES(5,77,'5:77','قل يا اهل الكتاب لا تغلوا في دينكم غير الحق ولا تتبعوا اهواء قوم قد ضلوا من قبل واضلوا كثيرا وضلوا عن سواء السبيل');
INSERT INTO "imlaei_simple" VALUES(5,78,'5:78','لعن الذين كفروا من بني اسرائيل على لسان داوود وعيسى ابن مريم ذلك بما عصوا وكانوا يعتدون');
INSERT INTO "imlaei_simple" VALUES(5,79,'5:79','كانوا لا يتناهون عن منكر فعلوه لبئس ما كانوا يفعلون');
INSERT INTO "imlaei_simple" VALUES(5,80,'5:80','ترى كثيرا منهم يتولون الذين كفروا لبئس ما قدمت لهم انفسهم ان سخط الله عليهم وفي العذاب هم خالدون');
INSERT INTO "imlaei_simple" VALUES(5,81,'5:81','ولو كانوا يؤمنون بالله والنبي وما انزل اليه ما اتخذوهم اولياء ولكن كثيرا منهم فاسقون');
INSERT INTO "imlaei_simple" VALUES(5,82,'5:82','لتجدن اشد الناس عداوة للذين امنوا اليهود والذين اشركوا ولتجدن اقربهم مودة للذين امنوا الذين قالوا انا نصارى ذلك بان منهم قسيسين ورهبانا وانهم لا يستكبرون');
INSERT INTO "imlaei_simple" VALUES(5,83,'5:83','واذا سمعوا ما انزل الى الرسول ترى اعينهم تفيض من الدمع مما عرفوا من الحق يقولون ربنا امنا فاكتبنا مع الشاهدين');
INSERT INTO "imlaei_simple" VALUES(5,84,'5:84','وما لنا لا نؤمن بالله وما جاءنا من الحق ونطمع ان يدخلنا ربنا مع القوم الصالحين');
INSERT INTO "imlaei_simple" VALUES(5,85,'5:85','فاثابهم الله بما قالوا جنات تجري من تحتها الانهار خالدين فيها وذلك جزاء المحسنين');
INSERT INTO "imlaei_simple" VALUES(5,86,'5:86','والذين كفروا وكذبوا باياتنا اولئك اصحاب الجحيم');
INSERT INTO "imlaei_simple" VALUES(5,87,'5:87','يا ايها الذين امنوا لا تحرموا طيبات ما احل الله لكم ولا تعتدوا ان الله لا يحب المعتدين');
INSERT INTO "imlaei_simple" VALUES(5,88,'5:88','وكلوا مما رزقكم الله حلالا طيبا واتقوا الله الذي انتم به مؤمنون');
INSERT INTO "imlaei_simple" VALUES(5,89,'5:89','لا يؤاخذكم الله باللغو في ايمانكم ولكن يؤاخذكم بما عقدتم الايمان فكفارته اطعام عشرة مساكين من اوسط ما تطعمون اهليكم او كسوتهم او تحرير رقبة فمن لم يجد فصيام ثلاثة ايام ذلك كفارة ايمانكم اذا حلفتم واحفظوا ايمانكم كذلك يبين الله لكم اياته لعلكم تشكرون');
INSERT INTO "imlaei_simple" VALUES(5,90,'5:90','يا ايها الذين امنوا انما الخمر والميسر والانصاب والازلام رجس من عمل الشيطان فاجتنبوه لعلكم تفلحون');
INSERT INTO "imlaei_simple" VALUES(5,91,'5:91','انما يريد الشيطان ان يوقع بينكم العداوة والبغضاء في الخمر والميسر ويصدكم عن ذكر الله وعن الصلاة فهل انتم منتهون');
INSERT INTO "imlaei_simple" VALUES(5,92,'5:92','واطيعوا الله واطيعوا الرسول واحذروا فان توليتم فاعلموا انما على رسولنا البلاغ المبين');
INSERT INTO "imlaei_simple" VALUES(5,93,'5:93','ليس على الذين امنوا وعملوا الصالحات جناح فيما طعموا اذا ما اتقوا وامنوا وعملوا الصالحات ثم اتقوا وامنوا ثم اتقوا واحسنوا والله يحب المحسنين');
INSERT INTO "imlaei_simple" VALUES(5,94,'5:94','يا ايها الذين امنوا ليبلونكم الله بشيء من الصيد تناله ايديكم ورماحكم ليعلم الله من يخافه بالغيب فمن اعتدى بعد ذلك فله عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(5,95,'5:95','يا ايها الذين امنوا لا تقتلوا الصيد وانتم حرم ومن قتله منكم متعمدا فجزاء مثل ما قتل من النعم يحكم به ذوا عدل منكم هديا بالغ الكعبة او كفارة طعام مساكين او عدل ذلك صياما ليذوق وبال امره عفا الله عما سلف ومن عاد فينتقم الله منه والله عزيز ذو انتقام');
INSERT INTO "imlaei_simple" VALUES(5,96,'5:96','احل لكم صيد البحر وطعامه متاعا لكم وللسيارة وحرم عليكم صيد البر ما دمتم حرما واتقوا الله الذي اليه تحشرون');
INSERT INTO "imlaei_simple" VALUES(5,97,'5:97','جعل الله الكعبة البيت الحرام قياما للناس والشهر الحرام والهدي والقلائد ذلك لتعلموا ان الله يعلم ما في السماوات وما في الارض وان الله بكل شيء عليم');
INSERT INTO "imlaei_simple" VALUES(5,98,'5:98','اعلموا ان الله شديد العقاب وان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(5,99,'5:99','ما على الرسول الا البلاغ والله يعلم ما تبدون وما تكتمون');
INSERT INTO "imlaei_simple" VALUES(5,100,'5:100','قل لا يستوي الخبيث والطيب ولو اعجبك كثرة الخبيث فاتقوا الله يا اولي الالباب لعلكم تفلحون');
INSERT INTO "imlaei_simple" VALUES(5,101,'5:101','يا ايها الذين امنوا لا تسالوا عن اشياء ان تبد لكم تسؤكم وان تسالوا عنها حين ينزل القران تبد لكم عفا الله عنها والله غفور حليم');
INSERT INTO "imlaei_simple" VALUES(5,102,'5:102','قد سالها قوم من قبلكم ثم اصبحوا بها كافرين');
INSERT INTO "imlaei_simple" VALUES(5,103,'5:103','ما جعل الله من بحيرة ولا سائبة ولا وصيلة ولا حام ولكن الذين كفروا يفترون على الله الكذب واكثرهم لا يعقلون');
INSERT INTO "imlaei_simple" VALUES(5,104,'5:104','واذا قيل لهم تعالوا الى ما انزل الله والى الرسول قالوا حسبنا ما وجدنا عليه اباءنا اولو كان اباؤهم لا يعلمون شيئا ولا يهتدون');
INSERT INTO "imlaei_simple" VALUES(5,105,'5:105','يا ايها الذين امنوا عليكم انفسكم لا يضركم من ضل اذا اهتديتم الى الله مرجعكم جميعا فينبئكم بما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(5,106,'5:106','يا ايها الذين امنوا شهادة بينكم اذا حضر احدكم الموت حين الوصية اثنان ذوا عدل منكم او اخران من غيركم ان انتم ضربتم في الارض فاصابتكم مصيبة الموت تحبسونهما من بعد الصلاة فيقسمان بالله ان ارتبتم لا نشتري به ثمنا ولو كان ذا قربى ولا نكتم شهادة الله انا اذا لمن الاثمين');
INSERT INTO "imlaei_simple" VALUES(5,107,'5:107','فان عثر على انهما استحقا اثما فاخران يقومان مقامهما من الذين استحق عليهم الاوليان فيقسمان بالله لشهادتنا احق من شهادتهما وما اعتدينا انا اذا لمن الظالمين');
INSERT INTO "imlaei_simple" VALUES(5,108,'5:108','ذلك ادنى ان ياتوا بالشهادة على وجهها او يخافوا ان ترد ايمان بعد ايمانهم واتقوا الله واسمعوا والله لا يهدي القوم الفاسقين');
INSERT INTO "imlaei_simple" VALUES(5,109,'5:109','يوم يجمع الله الرسل فيقول ماذا اجبتم قالوا لا علم لنا انك انت علام الغيوب');
INSERT INTO "imlaei_simple" VALUES(5,110,'5:110','اذ قال الله يا عيسى ابن مريم اذكر نعمتي عليك وعلى والدتك اذ ايدتك بروح القدس تكلم الناس في المهد وكهلا واذ علمتك الكتاب والحكمة والتوراة والانجيل واذ تخلق من الطين كهيئة الطير باذني فتنفخ فيها فتكون طيرا باذني وتبرئ الاكمه والابرص باذني واذ تخرج الموتى باذني واذ كففت بني اسرائيل عنك اذ جئتهم بالبينات فقال الذين كفروا منهم ان هذا الا سحر مبين');
INSERT INTO "imlaei_simple" VALUES(5,111,'5:111','واذ اوحيت الى الحواريين ان امنوا بي وبرسولي قالوا امنا واشهد باننا مسلمون');
INSERT INTO "imlaei_simple" VALUES(5,112,'5:112','اذ قال الحواريون يا عيسى ابن مريم هل يستطيع ربك ان ينزل علينا مائدة من السماء قال اتقوا الله ان كنتم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(5,113,'5:113','قالوا نريد ان ناكل منها وتطمئن قلوبنا ونعلم ان قد صدقتنا ونكون عليها من الشاهدين');
INSERT INTO "imlaei_simple" VALUES(5,114,'5:114','قال عيسى ابن مريم اللهم ربنا انزل علينا مائدة من السماء تكون لنا عيدا لاولنا واخرنا واية منك وارزقنا وانت خير الرازقين');
INSERT INTO "imlaei_simple" VALUES(5,115,'5:115','قال الله اني منزلها عليكم فمن يكفر بعد منكم فاني اعذبه عذابا لا اعذبه احدا من العالمين');
INSERT INTO "imlaei_simple" VALUES(5,116,'5:116','واذ قال الله يا عيسى ابن مريم اانت قلت للناس اتخذوني وامي الهين من دون الله قال سبحانك ما يكون لي ان اقول ما ليس لي بحق ان كنت قلته فقد علمته تعلم ما في نفسي ولا اعلم ما في نفسك انك انت علام الغيوب');
INSERT INTO "imlaei_simple" VALUES(5,117,'5:117','ما قلت لهم الا ما امرتني به ان اعبدوا الله ربي وربكم وكنت عليهم شهيدا ما دمت فيهم فلما توفيتني كنت انت الرقيب عليهم وانت على كل شيء شهيد');
INSERT INTO "imlaei_simple" VALUES(5,118,'5:118','ان تعذبهم فانهم عبادك وان تغفر لهم فانك انت العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(5,119,'5:119','قال الله هذا يوم ينفع الصادقين صدقهم لهم جنات تجري من تحتها الانهار خالدين فيها ابدا رضي الله عنهم ورضوا عنه ذلك الفوز العظيم');
INSERT INTO "imlaei_simple" VALUES(5,120,'5:120','لله ملك السماوات والارض وما فيهن وهو على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(6,1,'6:1','الحمد لله الذي خلق السماوات والارض وجعل الظلمات والنور ثم الذين كفروا بربهم يعدلون');
INSERT INTO "imlaei_simple" VALUES(6,2,'6:2','هو الذي خلقكم من طين ثم قضى اجلا واجل مسمى عنده ثم انتم تمترون');
INSERT INTO "imlaei_simple" VALUES(6,3,'6:3','وهو الله في السماوات وفي الارض يعلم سركم وجهركم ويعلم ما تكسبون');
INSERT INTO "imlaei_simple" VALUES(6,4,'6:4','وما تاتيهم من اية من ايات ربهم الا كانوا عنها معرضين');
INSERT INTO "imlaei_simple" VALUES(6,5,'6:5','فقد كذبوا بالحق لما جاءهم فسوف ياتيهم انباء ما كانوا به يستهزئون');
INSERT INTO "imlaei_simple" VALUES(6,6,'6:6','الم يروا كم اهلكنا من قبلهم من قرن مكناهم في الارض ما لم نمكن لكم وارسلنا السماء عليهم مدرارا وجعلنا الانهار تجري من تحتهم فاهلكناهم بذنوبهم وانشانا من بعدهم قرنا اخرين');
INSERT INTO "imlaei_simple" VALUES(6,7,'6:7','ولو نزلنا عليك كتابا في قرطاس فلمسوه بايديهم لقال الذين كفروا ان هذا الا سحر مبين');
INSERT INTO "imlaei_simple" VALUES(6,8,'6:8','وقالوا لولا انزل عليه ملك ولو انزلنا ملكا لقضي الامر ثم لا ينظرون');
INSERT INTO "imlaei_simple" VALUES(6,9,'6:9','ولو جعلناه ملكا لجعلناه رجلا وللبسنا عليهم ما يلبسون');
INSERT INTO "imlaei_simple" VALUES(6,10,'6:10','ولقد استهزئ برسل من قبلك فحاق بالذين سخروا منهم ما كانوا به يستهزئون');
INSERT INTO "imlaei_simple" VALUES(6,11,'6:11','قل سيروا في الارض ثم انظروا كيف كان عاقبة المكذبين');
INSERT INTO "imlaei_simple" VALUES(6,12,'6:12','قل لمن ما في السماوات والارض قل لله كتب على نفسه الرحمة ليجمعنكم الى يوم القيامة لا ريب فيه الذين خسروا انفسهم فهم لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(6,13,'6:13','وله ما سكن في الليل والنهار وهو السميع العليم');
INSERT INTO "imlaei_simple" VALUES(6,14,'6:14','قل اغير الله اتخذ وليا فاطر السماوات والارض وهو يطعم ولا يطعم قل اني امرت ان اكون اول من اسلم ولا تكونن من المشركين');
INSERT INTO "imlaei_simple" VALUES(6,15,'6:15','قل اني اخاف ان عصيت ربي عذاب يوم عظيم');
INSERT INTO "imlaei_simple" VALUES(6,16,'6:16','من يصرف عنه يومئذ فقد رحمه وذلك الفوز المبين');
INSERT INTO "imlaei_simple" VALUES(6,17,'6:17','وان يمسسك الله بضر فلا كاشف له الا هو وان يمسسك بخير فهو على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(6,18,'6:18','وهو القاهر فوق عباده وهو الحكيم الخبير');
INSERT INTO "imlaei_simple" VALUES(6,19,'6:19','قل اي شيء اكبر شهادة قل الله شهيد بيني وبينكم واوحي الي هذا القران لانذركم به ومن بلغ ائنكم لتشهدون ان مع الله الهة اخرى قل لا اشهد قل انما هو اله واحد وانني بريء مما تشركون');
INSERT INTO "imlaei_simple" VALUES(6,20,'6:20','الذين اتيناهم الكتاب يعرفونه كما يعرفون ابناءهم الذين خسروا انفسهم فهم لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(6,21,'6:21','ومن اظلم ممن افترى على الله كذبا او كذب باياته انه لا يفلح الظالمون');
INSERT INTO "imlaei_simple" VALUES(6,22,'6:22','ويوم نحشرهم جميعا ثم نقول للذين اشركوا اين شركاؤكم الذين كنتم تزعمون');
INSERT INTO "imlaei_simple" VALUES(6,23,'6:23','ثم لم تكن فتنتهم الا ان قالوا والله ربنا ما كنا مشركين');
INSERT INTO "imlaei_simple" VALUES(6,24,'6:24','انظر كيف كذبوا على انفسهم وضل عنهم ما كانوا يفترون');
INSERT INTO "imlaei_simple" VALUES(6,25,'6:25','ومنهم من يستمع اليك وجعلنا على قلوبهم اكنة ان يفقهوه وفي اذانهم وقرا وان يروا كل اية لا يؤمنوا بها حتى اذا جاءوك يجادلونك يقول الذين كفروا ان هذا الا اساطير الاولين');
INSERT INTO "imlaei_simple" VALUES(6,26,'6:26','وهم ينهون عنه ويناون عنه وان يهلكون الا انفسهم وما يشعرون');
INSERT INTO "imlaei_simple" VALUES(6,27,'6:27','ولو ترى اذ وقفوا على النار فقالوا يا ليتنا نرد ولا نكذب بايات ربنا ونكون من المؤمنين');
INSERT INTO "imlaei_simple" VALUES(6,28,'6:28','بل بدا لهم ما كانوا يخفون من قبل ولو ردوا لعادوا لما نهوا عنه وانهم لكاذبون');
INSERT INTO "imlaei_simple" VALUES(6,29,'6:29','وقالوا ان هي الا حياتنا الدنيا وما نحن بمبعوثين');
INSERT INTO "imlaei_simple" VALUES(6,30,'6:30','ولو ترى اذ وقفوا على ربهم قال اليس هذا بالحق قالوا بلى وربنا قال فذوقوا العذاب بما كنتم تكفرون');
INSERT INTO "imlaei_simple" VALUES(6,31,'6:31','قد خسر الذين كذبوا بلقاء الله حتى اذا جاءتهم الساعة بغتة قالوا يا حسرتنا على ما فرطنا فيها وهم يحملون اوزارهم على ظهورهم الا ساء ما يزرون');
INSERT INTO "imlaei_simple" VALUES(6,32,'6:32','وما الحياة الدنيا الا لعب ولهو وللدار الاخرة خير للذين يتقون افلا تعقلون');
INSERT INTO "imlaei_simple" VALUES(6,33,'6:33','قد نعلم انه ليحزنك الذي يقولون فانهم لا يكذبونك ولكن الظالمين بايات الله يجحدون');
INSERT INTO "imlaei_simple" VALUES(6,34,'6:34','ولقد كذبت رسل من قبلك فصبروا على ما كذبوا واوذوا حتى اتاهم نصرنا ولا مبدل لكلمات الله ولقد جاءك من نبا المرسلين');
INSERT INTO "imlaei_simple" VALUES(6,35,'6:35','وان كان كبر عليك اعراضهم فان استطعت ان تبتغي نفقا في الارض او سلما في السماء فتاتيهم باية ولو شاء الله لجمعهم على الهدى فلا تكونن من الجاهلين');
INSERT INTO "imlaei_simple" VALUES(6,36,'6:36','انما يستجيب الذين يسمعون والموتى يبعثهم الله ثم اليه يرجعون');
INSERT INTO "imlaei_simple" VALUES(6,37,'6:37','وقالوا لولا نزل عليه اية من ربه قل ان الله قادر على ان ينزل اية ولكن اكثرهم لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(6,38,'6:38','وما من دابة في الارض ولا طائر يطير بجناحيه الا امم امثالكم ما فرطنا في الكتاب من شيء ثم الى ربهم يحشرون');
INSERT INTO "imlaei_simple" VALUES(6,39,'6:39','والذين كذبوا باياتنا صم وبكم في الظلمات من يشا الله يضلله ومن يشا يجعله على صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(6,40,'6:40','قل ارايتكم ان اتاكم عذاب الله او اتتكم الساعة اغير الله تدعون ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(6,41,'6:41','بل اياه تدعون فيكشف ما تدعون اليه ان شاء وتنسون ما تشركون');
INSERT INTO "imlaei_simple" VALUES(6,42,'6:42','ولقد ارسلنا الى امم من قبلك فاخذناهم بالباساء والضراء لعلهم يتضرعون');
INSERT INTO "imlaei_simple" VALUES(6,43,'6:43','فلولا اذ جاءهم باسنا تضرعوا ولكن قست قلوبهم وزين لهم الشيطان ما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(6,44,'6:44','فلما نسوا ما ذكروا به فتحنا عليهم ابواب كل شيء حتى اذا فرحوا بما اوتوا اخذناهم بغتة فاذا هم مبلسون');
INSERT INTO "imlaei_simple" VALUES(6,45,'6:45','فقطع دابر القوم الذين ظلموا والحمد لله رب العالمين');
INSERT INTO "imlaei_simple" VALUES(6,46,'6:46','قل ارايتم ان اخذ الله سمعكم وابصاركم وختم على قلوبكم من اله غير الله ياتيكم به انظر كيف نصرف الايات ثم هم يصدفون');
INSERT INTO "imlaei_simple" VALUES(6,47,'6:47','قل ارايتكم ان اتاكم عذاب الله بغتة او جهرة هل يهلك الا القوم الظالمون');
INSERT INTO "imlaei_simple" VALUES(6,48,'6:48','وما نرسل المرسلين الا مبشرين ومنذرين فمن امن واصلح فلا خوف عليهم ولا هم يحزنون');
INSERT INTO "imlaei_simple" VALUES(6,49,'6:49','والذين كذبوا باياتنا يمسهم العذاب بما كانوا يفسقون');
INSERT INTO "imlaei_simple" VALUES(6,50,'6:50','قل لا اقول لكم عندي خزائن الله ولا اعلم الغيب ولا اقول لكم اني ملك ان اتبع الا ما يوحى الي قل هل يستوي الاعمى والبصير افلا تتفكرون');
INSERT INTO "imlaei_simple" VALUES(6,51,'6:51','وانذر به الذين يخافون ان يحشروا الى ربهم ليس لهم من دونه ولي ولا شفيع لعلهم يتقون');
INSERT INTO "imlaei_simple" VALUES(6,52,'6:52','ولا تطرد الذين يدعون ربهم بالغداة والعشي يريدون وجهه ما عليك من حسابهم من شيء وما من حسابك عليهم من شيء فتطردهم فتكون من الظالمين');
INSERT INTO "imlaei_simple" VALUES(6,53,'6:53','وكذلك فتنا بعضهم ببعض ليقولوا اهؤلاء من الله عليهم من بيننا اليس الله باعلم بالشاكرين');
INSERT INTO "imlaei_simple" VALUES(6,54,'6:54','واذا جاءك الذين يؤمنون باياتنا فقل سلام عليكم كتب ربكم على نفسه الرحمة انه من عمل منكم سوءا بجهالة ثم تاب من بعده واصلح فانه غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(6,55,'6:55','وكذلك نفصل الايات ولتستبين سبيل المجرمين');
INSERT INTO "imlaei_simple" VALUES(6,56,'6:56','قل اني نهيت ان اعبد الذين تدعون من دون الله قل لا اتبع اهواءكم قد ضللت اذا وما انا من المهتدين');
INSERT INTO "imlaei_simple" VALUES(6,57,'6:57','قل اني على بينة من ربي وكذبتم به ما عندي ما تستعجلون به ان الحكم الا لله يقص الحق وهو خير الفاصلين');
INSERT INTO "imlaei_simple" VALUES(6,58,'6:58','قل لو ان عندي ما تستعجلون به لقضي الامر بيني وبينكم والله اعلم بالظالمين');
INSERT INTO "imlaei_simple" VALUES(6,59,'6:59','وعنده مفاتح الغيب لا يعلمها الا هو ويعلم ما في البر والبحر وما تسقط من ورقة الا يعلمها ولا حبة في ظلمات الارض ولا رطب ولا يابس الا في كتاب مبين');
INSERT INTO "imlaei_simple" VALUES(6,60,'6:60','وهو الذي يتوفاكم بالليل ويعلم ما جرحتم بالنهار ثم يبعثكم فيه ليقضى اجل مسمى ثم اليه مرجعكم ثم ينبئكم بما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(6,61,'6:61','وهو القاهر فوق عباده ويرسل عليكم حفظة حتى اذا جاء احدكم الموت توفته رسلنا وهم لا يفرطون');
INSERT INTO "imlaei_simple" VALUES(6,62,'6:62','ثم ردوا الى الله مولاهم الحق الا له الحكم وهو اسرع الحاسبين');
INSERT INTO "imlaei_simple" VALUES(6,63,'6:63','قل من ينجيكم من ظلمات البر والبحر تدعونه تضرعا وخفية لئن انجانا من هذه لنكونن من الشاكرين');
INSERT INTO "imlaei_simple" VALUES(6,64,'6:64','قل الله ينجيكم منها ومن كل كرب ثم انتم تشركون');
INSERT INTO "imlaei_simple" VALUES(6,65,'6:65','قل هو القادر على ان يبعث عليكم عذابا من فوقكم او من تحت ارجلكم او يلبسكم شيعا ويذيق بعضكم باس بعض انظر كيف نصرف الايات لعلهم يفقهون');
INSERT INTO "imlaei_simple" VALUES(6,66,'6:66','وكذب به قومك وهو الحق قل لست عليكم بوكيل');
INSERT INTO "imlaei_simple" VALUES(6,67,'6:67','لكل نبا مستقر وسوف تعلمون');
INSERT INTO "imlaei_simple" VALUES(6,68,'6:68','واذا رايت الذين يخوضون في اياتنا فاعرض عنهم حتى يخوضوا في حديث غيره واما ينسينك الشيطان فلا تقعد بعد الذكرى مع القوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(6,69,'6:69','وما على الذين يتقون من حسابهم من شيء ولكن ذكرى لعلهم يتقون');
INSERT INTO "imlaei_simple" VALUES(6,70,'6:70','وذر الذين اتخذوا دينهم لعبا ولهوا وغرتهم الحياة الدنيا وذكر به ان تبسل نفس بما كسبت ليس لها من دون الله ولي ولا شفيع وان تعدل كل عدل لا يؤخذ منها اولئك الذين ابسلوا بما كسبوا لهم شراب من حميم وعذاب اليم بما كانوا يكفرون');
INSERT INTO "imlaei_simple" VALUES(6,71,'6:71','قل اندعو من دون الله ما لا ينفعنا ولا يضرنا ونرد على اعقابنا بعد اذ هدانا الله كالذي استهوته الشياطين في الارض حيران له اصحاب يدعونه الى الهدى ائتنا قل ان هدى الله هو الهدى وامرنا لنسلم لرب العالمين');
INSERT INTO "imlaei_simple" VALUES(6,72,'6:72','وان اقيموا الصلاة واتقوه وهو الذي اليه تحشرون');
INSERT INTO "imlaei_simple" VALUES(6,73,'6:73','وهو الذي خلق السماوات والارض بالحق ويوم يقول كن فيكون قوله الحق وله الملك يوم ينفخ في الصور عالم الغيب والشهادة وهو الحكيم الخبير');
INSERT INTO "imlaei_simple" VALUES(6,74,'6:74','واذ قال ابراهيم لابيه ازر اتتخذ اصناما الهة اني اراك وقومك في ضلال مبين');
INSERT INTO "imlaei_simple" VALUES(6,75,'6:75','وكذلك نري ابراهيم ملكوت السماوات والارض وليكون من الموقنين');
INSERT INTO "imlaei_simple" VALUES(6,76,'6:76','فلما جن عليه الليل راى كوكبا قال هذا ربي فلما افل قال لا احب الافلين');
INSERT INTO "imlaei_simple" VALUES(6,77,'6:77','فلما راى القمر بازغا قال هذا ربي فلما افل قال لئن لم يهدني ربي لاكونن من القوم الضالين');
INSERT INTO "imlaei_simple" VALUES(6,78,'6:78','فلما راى الشمس بازغة قال هذا ربي هذا اكبر فلما افلت قال يا قوم اني بريء مما تشركون');
INSERT INTO "imlaei_simple" VALUES(6,79,'6:79','اني وجهت وجهي للذي فطر السماوات والارض حنيفا وما انا من المشركين');
INSERT INTO "imlaei_simple" VALUES(6,80,'6:80','وحاجه قومه قال اتحاجوني في الله وقد هدان ولا اخاف ما تشركون به الا ان يشاء ربي شيئا وسع ربي كل شيء علما افلا تتذكرون');
INSERT INTO "imlaei_simple" VALUES(6,81,'6:81','وكيف اخاف ما اشركتم ولا تخافون انكم اشركتم بالله ما لم ينزل به عليكم سلطانا فاي الفريقين احق بالامن ان كنتم تعلمون');
INSERT INTO "imlaei_simple" VALUES(6,82,'6:82','الذين امنوا ولم يلبسوا ايمانهم بظلم اولئك لهم الامن وهم مهتدون');
INSERT INTO "imlaei_simple" VALUES(6,83,'6:83','وتلك حجتنا اتيناها ابراهيم على قومه نرفع درجات من نشاء ان ربك حكيم عليم');
INSERT INTO "imlaei_simple" VALUES(6,84,'6:84','ووهبنا له اسحاق ويعقوب كلا هدينا ونوحا هدينا من قبل ومن ذريته داوود وسليمان وايوب ويوسف وموسى وهارون وكذلك نجزي المحسنين');
INSERT INTO "imlaei_simple" VALUES(6,85,'6:85','وزكريا ويحيى وعيسى والياس كل من الصالحين');
INSERT INTO "imlaei_simple" VALUES(6,86,'6:86','واسماعيل واليسع ويونس ولوطا وكلا فضلنا على العالمين');
INSERT INTO "imlaei_simple" VALUES(6,87,'6:87','ومن ابائهم وذرياتهم واخوانهم واجتبيناهم وهديناهم الى صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(6,88,'6:88','ذلك هدى الله يهدي به من يشاء من عباده ولو اشركوا لحبط عنهم ما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(6,89,'6:89','اولئك الذين اتيناهم الكتاب والحكم والنبوة فان يكفر بها هؤلاء فقد وكلنا بها قوما ليسوا بها بكافرين');
INSERT INTO "imlaei_simple" VALUES(6,90,'6:90','اولئك الذين هدى الله فبهداهم اقتده قل لا اسالكم عليه اجرا ان هو الا ذكرى للعالمين');
INSERT INTO "imlaei_simple" VALUES(6,91,'6:91','وما قدروا الله حق قدره اذ قالوا ما انزل الله على بشر من شيء قل من انزل الكتاب الذي جاء به موسى نورا وهدى للناس تجعلونه قراطيس تبدونها وتخفون كثيرا وعلمتم ما لم تعلموا انتم ولا اباؤكم قل الله ثم ذرهم في خوضهم يلعبون');
INSERT INTO "imlaei_simple" VALUES(6,92,'6:92','وهذا كتاب انزلناه مبارك مصدق الذي بين يديه ولتنذر ام القرى ومن حولها والذين يؤمنون بالاخرة يؤمنون به وهم على صلاتهم يحافظون');
INSERT INTO "imlaei_simple" VALUES(6,93,'6:93','ومن اظلم ممن افترى على الله كذبا او قال اوحي الي ولم يوح اليه شيء ومن قال سانزل مثل ما انزل الله ولو ترى اذ الظالمون في غمرات الموت والملائكة باسطو ايديهم اخرجوا انفسكم اليوم تجزون عذاب الهون بما كنتم تقولون على الله غير الحق وكنتم عن اياته تستكبرون');
INSERT INTO "imlaei_simple" VALUES(6,94,'6:94','ولقد جئتمونا فرادى كما خلقناكم اول مرة وتركتم ما خولناكم وراء ظهوركم وما نرى معكم شفعاءكم الذين زعمتم انهم فيكم شركاء لقد تقطع بينكم وضل عنكم ما كنتم تزعمون');
INSERT INTO "imlaei_simple" VALUES(6,95,'6:95','ان الله فالق الحب والنوى يخرج الحي من الميت ومخرج الميت من الحي ذلكم الله فانى تؤفكون');
INSERT INTO "imlaei_simple" VALUES(6,96,'6:96','فالق الاصباح وجعل الليل سكنا والشمس والقمر حسبانا ذلك تقدير العزيز العليم');
INSERT INTO "imlaei_simple" VALUES(6,97,'6:97','وهو الذي جعل لكم النجوم لتهتدوا بها في ظلمات البر والبحر قد فصلنا الايات لقوم يعلمون');
INSERT INTO "imlaei_simple" VALUES(6,98,'6:98','وهو الذي انشاكم من نفس واحدة فمستقر ومستودع قد فصلنا الايات لقوم يفقهون');
INSERT INTO "imlaei_simple" VALUES(6,99,'6:99','وهو الذي انزل من السماء ماء فاخرجنا به نبات كل شيء فاخرجنا منه خضرا نخرج منه حبا متراكبا ومن النخل من طلعها قنوان دانية وجنات من اعناب والزيتون والرمان مشتبها وغير متشابه انظروا الى ثمره اذا اثمر وينعه ان في ذلكم لايات لقوم يؤمنون');
INSERT INTO "imlaei_simple" VALUES(6,100,'6:100','وجعلوا لله شركاء الجن وخلقهم وخرقوا له بنين وبنات بغير علم سبحانه وتعالى عما يصفون');
INSERT INTO "imlaei_simple" VALUES(6,101,'6:101','بديع السماوات والارض انى يكون له ولد ولم تكن له صاحبة وخلق كل شيء وهو بكل شيء عليم');
INSERT INTO "imlaei_simple" VALUES(6,102,'6:102','ذلكم الله ربكم لا اله الا هو خالق كل شيء فاعبدوه وهو على كل شيء وكيل');
INSERT INTO "imlaei_simple" VALUES(6,103,'6:103','لا تدركه الابصار وهو يدرك الابصار وهو اللطيف الخبير');
INSERT INTO "imlaei_simple" VALUES(6,104,'6:104','قد جاءكم بصائر من ربكم فمن ابصر فلنفسه ومن عمي فعليها وما انا عليكم بحفيظ');
INSERT INTO "imlaei_simple" VALUES(6,105,'6:105','وكذلك نصرف الايات وليقولوا درست ولنبينه لقوم يعلمون');
INSERT INTO "imlaei_simple" VALUES(6,106,'6:106','اتبع ما اوحي اليك من ربك لا اله الا هو واعرض عن المشركين');
INSERT INTO "imlaei_simple" VALUES(6,107,'6:107','ولو شاء الله ما اشركوا وما جعلناك عليهم حفيظا وما انت عليهم بوكيل');
INSERT INTO "imlaei_simple" VALUES(6,108,'6:108','ولا تسبوا الذين يدعون من دون الله فيسبوا الله عدوا بغير علم كذلك زينا لكل امة عملهم ثم الى ربهم مرجعهم فينبئهم بما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(6,109,'6:109','واقسموا بالله جهد ايمانهم لئن جاءتهم اية ليؤمنن بها قل انما الايات عند الله وما يشعركم انها اذا جاءت لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(6,110,'6:110','ونقلب افئدتهم وابصارهم كما لم يؤمنوا به اول مرة ونذرهم في طغيانهم يعمهون');
INSERT INTO "imlaei_simple" VALUES(6,111,'6:111','ولو اننا نزلنا اليهم الملائكة وكلمهم الموتى وحشرنا عليهم كل شيء قبلا ما كانوا ليؤمنوا الا ان يشاء الله ولكن اكثرهم يجهلون');
INSERT INTO "imlaei_simple" VALUES(6,112,'6:112','وكذلك جعلنا لكل نبي عدوا شياطين الانس والجن يوحي بعضهم الى بعض زخرف القول غرورا ولو شاء ربك ما فعلوه فذرهم وما يفترون');
INSERT INTO "imlaei_simple" VALUES(6,113,'6:113','ولتصغى اليه افئدة الذين لا يؤمنون بالاخرة وليرضوه وليقترفوا ما هم مقترفون');
INSERT INTO "imlaei_simple" VALUES(6,114,'6:114','افغير الله ابتغي حكما وهو الذي انزل اليكم الكتاب مفصلا والذين اتيناهم الكتاب يعلمون انه منزل من ربك بالحق فلا تكونن من الممترين');
INSERT INTO "imlaei_simple" VALUES(6,115,'6:115','وتمت كلمت ربك صدقا وعدلا لا مبدل لكلماته وهو السميع العليم');
INSERT INTO "imlaei_simple" VALUES(6,116,'6:116','وان تطع اكثر من في الارض يضلوك عن سبيل الله ان يتبعون الا الظن وان هم الا يخرصون');
INSERT INTO "imlaei_simple" VALUES(6,117,'6:117','ان ربك هو اعلم من يضل عن سبيله وهو اعلم بالمهتدين');
INSERT INTO "imlaei_simple" VALUES(6,118,'6:118','فكلوا مما ذكر اسم الله عليه ان كنتم باياته مؤمنين');
INSERT INTO "imlaei_simple" VALUES(6,119,'6:119','وما لكم الا تاكلوا مما ذكر اسم الله عليه وقد فصل لكم ما حرم عليكم الا ما اضطررتم اليه وان كثيرا ليضلون باهوائهم بغير علم ان ربك هو اعلم بالمعتدين');
INSERT INTO "imlaei_simple" VALUES(6,120,'6:120','وذروا ظاهر الاثم وباطنه ان الذين يكسبون الاثم سيجزون بما كانوا يقترفون');
INSERT INTO "imlaei_simple" VALUES(6,121,'6:121','ولا تاكلوا مما لم يذكر اسم الله عليه وانه لفسق وان الشياطين ليوحون الى اوليائهم ليجادلوكم وان اطعتموهم انكم لمشركون');
INSERT INTO "imlaei_simple" VALUES(6,122,'6:122','اومن كان ميتا فاحييناه وجعلنا له نورا يمشي به في الناس كمن مثله في الظلمات ليس بخارج منها كذلك زين للكافرين ما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(6,123,'6:123','وكذلك جعلنا في كل قرية اكابر مجرميها ليمكروا فيها وما يمكرون الا بانفسهم وما يشعرون');
INSERT INTO "imlaei_simple" VALUES(6,124,'6:124','واذا جاءتهم اية قالوا لن نؤمن حتى نؤتى مثل ما اوتي رسل الله الله اعلم حيث يجعل رسالته سيصيب الذين اجرموا صغار عند الله وعذاب شديد بما كانوا يمكرون');
INSERT INTO "imlaei_simple" VALUES(6,125,'6:125','فمن يرد الله ان يهديه يشرح صدره للاسلام ومن يرد ان يضله يجعل صدره ضيقا حرجا كانما يصعد في السماء كذلك يجعل الله الرجس على الذين لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(6,126,'6:126','وهذا صراط ربك مستقيما قد فصلنا الايات لقوم يذكرون');
INSERT INTO "imlaei_simple" VALUES(6,127,'6:127','لهم دار السلام عند ربهم وهو وليهم بما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(6,128,'6:128','ويوم يحشرهم جميعا يا معشر الجن قد استكثرتم من الانس وقال اولياؤهم من الانس ربنا استمتع بعضنا ببعض وبلغنا اجلنا الذي اجلت لنا قال النار مثواكم خالدين فيها الا ما شاء الله ان ربك حكيم عليم');
INSERT INTO "imlaei_simple" VALUES(6,129,'6:129','وكذلك نولي بعض الظالمين بعضا بما كانوا يكسبون');
INSERT INTO "imlaei_simple" VALUES(6,130,'6:130','يا معشر الجن والانس الم ياتكم رسل منكم يقصون عليكم اياتي وينذرونكم لقاء يومكم هذا قالوا شهدنا على انفسنا وغرتهم الحياة الدنيا وشهدوا على انفسهم انهم كانوا كافرين');
INSERT INTO "imlaei_simple" VALUES(6,131,'6:131','ذلك ان لم يكن ربك مهلك القرى بظلم واهلها غافلون');
INSERT INTO "imlaei_simple" VALUES(6,132,'6:132','ولكل درجات مما عملوا وما ربك بغافل عما يعملون');
INSERT INTO "imlaei_simple" VALUES(6,133,'6:133','وربك الغني ذو الرحمة ان يشا يذهبكم ويستخلف من بعدكم ما يشاء كما انشاكم من ذرية قوم اخرين');
INSERT INTO "imlaei_simple" VALUES(6,134,'6:134','ان ما توعدون لات وما انتم بمعجزين');
INSERT INTO "imlaei_simple" VALUES(6,135,'6:135','قل يا قوم اعملوا على مكانتكم اني عامل فسوف تعلمون من تكون له عاقبة الدار انه لا يفلح الظالمون');
INSERT INTO "imlaei_simple" VALUES(6,136,'6:136','وجعلوا لله مما ذرا من الحرث والانعام نصيبا فقالوا هذا لله بزعمهم وهذا لشركائنا فما كان لشركائهم فلا يصل الى الله وما كان لله فهو يصل الى شركائهم ساء ما يحكمون');
INSERT INTO "imlaei_simple" VALUES(6,137,'6:137','وكذلك زين لكثير من المشركين قتل اولادهم شركاؤهم ليردوهم وليلبسوا عليهم دينهم ولو شاء الله ما فعلوه فذرهم وما يفترون');
INSERT INTO "imlaei_simple" VALUES(6,138,'6:138','وقالوا هذه انعام وحرث حجر لا يطعمها الا من نشاء بزعمهم وانعام حرمت ظهورها وانعام لا يذكرون اسم الله عليها افتراء عليه سيجزيهم بما كانوا يفترون');
INSERT INTO "imlaei_simple" VALUES(6,139,'6:139','وقالوا ما في بطون هذه الانعام خالصة لذكورنا ومحرم على ازواجنا وان يكن ميتة فهم فيه شركاء سيجزيهم وصفهم انه حكيم عليم');
INSERT INTO "imlaei_simple" VALUES(6,140,'6:140','قد خسر الذين قتلوا اولادهم سفها بغير علم وحرموا ما رزقهم الله افتراء على الله قد ضلوا وما كانوا مهتدين');
INSERT INTO "imlaei_simple" VALUES(6,141,'6:141','وهو الذي انشا جنات معروشات وغير معروشات والنخل والزرع مختلفا اكله والزيتون والرمان متشابها وغير متشابه كلوا من ثمره اذا اثمر واتوا حقه يوم حصاده ولا تسرفوا انه لا يحب المسرفين');
INSERT INTO "imlaei_simple" VALUES(6,142,'6:142','ومن الانعام حمولة وفرشا كلوا مما رزقكم الله ولا تتبعوا خطوات الشيطان انه لكم عدو مبين');
INSERT INTO "imlaei_simple" VALUES(6,143,'6:143','ثمانية ازواج من الضان اثنين ومن المعز اثنين قل الذكرين حرم ام الانثيين اما اشتملت عليه ارحام الانثيين نبئوني بعلم ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(6,144,'6:144','ومن الابل اثنين ومن البقر اثنين قل الذكرين حرم ام الانثيين اما اشتملت عليه ارحام الانثيين ام كنتم شهداء اذ وصاكم الله بهذا فمن اظلم ممن افترى على الله كذبا ليضل الناس بغير علم ان الله لا يهدي القوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(6,145,'6:145','قل لا اجد في ما اوحي الي محرما على طاعم يطعمه الا ان يكون ميتة او دما مسفوحا او لحم خنزير فانه رجس او فسقا اهل لغير الله به فمن اضطر غير باغ ولا عاد فان ربك غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(6,146,'6:146','وعلى الذين هادوا حرمنا كل ذي ظفر ومن البقر والغنم حرمنا عليهم شحومهما الا ما حملت ظهورهما او الحوايا او ما اختلط بعظم ذلك جزيناهم ببغيهم وانا لصادقون');
INSERT INTO "imlaei_simple" VALUES(6,147,'6:147','فان كذبوك فقل ربكم ذو رحمة واسعة ولا يرد باسه عن القوم المجرمين');
INSERT INTO "imlaei_simple" VALUES(6,148,'6:148','سيقول الذين اشركوا لو شاء الله ما اشركنا ولا اباؤنا ولا حرمنا من شيء كذلك كذب الذين من قبلهم حتى ذاقوا باسنا قل هل عندكم من علم فتخرجوه لنا ان تتبعون الا الظن وان انتم الا تخرصون');
INSERT INTO "imlaei_simple" VALUES(6,149,'6:149','قل فلله الحجة البالغة فلو شاء لهداكم اجمعين');
INSERT INTO "imlaei_simple" VALUES(6,150,'6:150','قل هلم شهداءكم الذين يشهدون ان الله حرم هذا فان شهدوا فلا تشهد معهم ولا تتبع اهواء الذين كذبوا باياتنا والذين لا يؤمنون بالاخرة وهم بربهم يعدلون');
INSERT INTO "imlaei_simple" VALUES(6,151,'6:151','قل تعالوا اتل ما حرم ربكم عليكم الا تشركوا به شيئا وبالوالدين احسانا ولا تقتلوا اولادكم من املاق نحن نرزقكم واياهم ولا تقربوا الفواحش ما ظهر منها وما بطن ولا تقتلوا النفس التي حرم الله الا بالحق ذلكم وصاكم به لعلكم تعقلون');
INSERT INTO "imlaei_simple" VALUES(6,152,'6:152','ولا تقربوا مال اليتيم الا بالتي هي احسن حتى يبلغ اشده واوفوا الكيل والميزان بالقسط لا نكلف نفسا الا وسعها واذا قلتم فاعدلوا ولو كان ذا قربى وبعهد الله اوفوا ذلكم وصاكم به لعلكم تذكرون');
INSERT INTO "imlaei_simple" VALUES(6,153,'6:153','وان هذا صراطي مستقيما فاتبعوه ولا تتبعوا السبل فتفرق بكم عن سبيله ذلكم وصاكم به لعلكم تتقون');
INSERT INTO "imlaei_simple" VALUES(6,154,'6:154','ثم اتينا موسى الكتاب تماما على الذي احسن وتفصيلا لكل شيء وهدى ورحمة لعلهم بلقاء ربهم يؤمنون');
INSERT INTO "imlaei_simple" VALUES(6,155,'6:155','وهذا كتاب انزلناه مبارك فاتبعوه واتقوا لعلكم ترحمون');
INSERT INTO "imlaei_simple" VALUES(6,156,'6:156','ان تقولوا انما انزل الكتاب على طائفتين من قبلنا وان كنا عن دراستهم لغافلين');
INSERT INTO "imlaei_simple" VALUES(6,157,'6:157','او تقولوا لو انا انزل علينا الكتاب لكنا اهدى منهم فقد جاءكم بينة من ربكم وهدى ورحمة فمن اظلم ممن كذب بايات الله وصدف عنها سنجزي الذين يصدفون عن اياتنا سوء العذاب بما كانوا يصدفون');
INSERT INTO "imlaei_simple" VALUES(6,158,'6:158','هل ينظرون الا ان تاتيهم الملائكة او ياتي ربك او ياتي بعض ايات ربك يوم ياتي بعض ايات ربك لا ينفع نفسا ايمانها لم تكن امنت من قبل او كسبت في ايمانها خيرا قل انتظروا انا منتظرون');
INSERT INTO "imlaei_simple" VALUES(6,159,'6:159','ان الذين فرقوا دينهم وكانوا شيعا لست منهم في شيء انما امرهم الى الله ثم ينبئهم بما كانوا يفعلون');
INSERT INTO "imlaei_simple" VALUES(6,160,'6:160','من جاء بالحسنة فله عشر امثالها ومن جاء بالسيئة فلا يجزى الا مثلها وهم لا يظلمون');
INSERT INTO "imlaei_simple" VALUES(6,161,'6:161','قل انني هداني ربي الى صراط مستقيم دينا قيما ملة ابراهيم حنيفا وما كان من المشركين');
INSERT INTO "imlaei_simple" VALUES(6,162,'6:162','قل ان صلاتي ونسكي ومحياي ومماتي لله رب العالمين');
INSERT INTO "imlaei_simple" VALUES(6,163,'6:163','لا شريك له وبذلك امرت وانا اول المسلمين');
INSERT INTO "imlaei_simple" VALUES(6,164,'6:164','قل اغير الله ابغي ربا وهو رب كل شيء ولا تكسب كل نفس الا عليها ولا تزر وازرة وزر اخرى ثم الى ربكم مرجعكم فينبئكم بما كنتم فيه تختلفون');
INSERT INTO "imlaei_simple" VALUES(6,165,'6:165','وهو الذي جعلكم خلائف الارض ورفع بعضكم فوق بعض درجات ليبلوكم في ما اتاكم ان ربك سريع العقاب وانه لغفور رحيم');
INSERT INTO "imlaei_simple" VALUES(7,1,'7:1','المص');
INSERT INTO "imlaei_simple" VALUES(7,2,'7:2','كتاب انزل اليك فلا يكن في صدرك حرج منه لتنذر به وذكرى للمؤمنين');
INSERT INTO "imlaei_simple" VALUES(7,3,'7:3','اتبعوا ما انزل اليكم من ربكم ولا تتبعوا من دونه اولياء قليلا ما تذكرون');
INSERT INTO "imlaei_simple" VALUES(7,4,'7:4','وكم من قرية اهلكناها فجاءها باسنا بياتا او هم قائلون');
INSERT INTO "imlaei_simple" VALUES(7,5,'7:5','فما كان دعواهم اذ جاءهم باسنا الا ان قالوا انا كنا ظالمين');
INSERT INTO "imlaei_simple" VALUES(7,6,'7:6','فلنسالن الذين ارسل اليهم ولنسالن المرسلين');
INSERT INTO "imlaei_simple" VALUES(7,7,'7:7','فلنقصن عليهم بعلم وما كنا غائبين');
INSERT INTO "imlaei_simple" VALUES(7,8,'7:8','والوزن يومئذ الحق فمن ثقلت موازينه فاولئك هم المفلحون');
INSERT INTO "imlaei_simple" VALUES(7,9,'7:9','ومن خفت موازينه فاولئك الذين خسروا انفسهم بما كانوا باياتنا يظلمون');
INSERT INTO "imlaei_simple" VALUES(7,10,'7:10','ولقد مكناكم في الارض وجعلنا لكم فيها معايش قليلا ما تشكرون');
INSERT INTO "imlaei_simple" VALUES(7,11,'7:11','ولقد خلقناكم ثم صورناكم ثم قلنا للملائكة اسجدوا لادم فسجدوا الا ابليس لم يكن من الساجدين');
INSERT INTO "imlaei_simple" VALUES(7,12,'7:12','قال ما منعك الا تسجد اذ امرتك قال انا خير منه خلقتني من نار وخلقته من طين');
INSERT INTO "imlaei_simple" VALUES(7,13,'7:13','قال فاهبط منها فما يكون لك ان تتكبر فيها فاخرج انك من الصاغرين');
INSERT INTO "imlaei_simple" VALUES(7,14,'7:14','قال انظرني الى يوم يبعثون');
INSERT INTO "imlaei_simple" VALUES(7,15,'7:15','قال انك من المنظرين');
INSERT INTO "imlaei_simple" VALUES(7,16,'7:16','قال فبما اغويتني لاقعدن لهم صراطك المستقيم');
INSERT INTO "imlaei_simple" VALUES(7,17,'7:17','ثم لاتينهم من بين ايديهم ومن خلفهم وعن ايمانهم وعن شمائلهم ولا تجد اكثرهم شاكرين');
INSERT INTO "imlaei_simple" VALUES(7,18,'7:18','قال اخرج منها مذءوما مدحورا لمن تبعك منهم لاملان جهنم منكم اجمعين');
INSERT INTO "imlaei_simple" VALUES(7,19,'7:19','ويا ادم اسكن انت وزوجك الجنة فكلا من حيث شئتما ولا تقربا هذه الشجرة فتكونا من الظالمين');
INSERT INTO "imlaei_simple" VALUES(7,20,'7:20','فوسوس لهما الشيطان ليبدي لهما ما ووري عنهما من سواتهما وقال ما نهاكما ربكما عن هذه الشجرة الا ان تكونا ملكين او تكونا من الخالدين');
INSERT INTO "imlaei_simple" VALUES(7,21,'7:21','وقاسمهما اني لكما لمن الناصحين');
INSERT INTO "imlaei_simple" VALUES(7,22,'7:22','فدلاهما بغرور فلما ذاقا الشجرة بدت لهما سواتهما وطفقا يخصفان عليهما من ورق الجنة وناداهما ربهما الم انهكما عن تلكما الشجرة واقل لكما ان الشيطان لكما عدو مبين');
INSERT INTO "imlaei_simple" VALUES(7,23,'7:23','قالا ربنا ظلمنا انفسنا وان لم تغفر لنا وترحمنا لنكونن من الخاسرين');
INSERT INTO "imlaei_simple" VALUES(7,24,'7:24','قال اهبطوا بعضكم لبعض عدو ولكم في الارض مستقر ومتاع الى حين');
INSERT INTO "imlaei_simple" VALUES(7,25,'7:25','قال فيها تحيون وفيها تموتون ومنها تخرجون');
INSERT INTO "imlaei_simple" VALUES(7,26,'7:26','يا بني ادم قد انزلنا عليكم لباسا يواري سواتكم وريشا ولباس التقوى ذلك خير ذلك من ايات الله لعلهم يذكرون');
INSERT INTO "imlaei_simple" VALUES(7,27,'7:27','يا بني ادم لا يفتننكم الشيطان كما اخرج ابويكم من الجنة ينزع عنهما لباسهما ليريهما سواتهما انه يراكم هو وقبيله من حيث لا ترونهم انا جعلنا الشياطين اولياء للذين لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(7,28,'7:28','واذا فعلوا فاحشة قالوا وجدنا عليها اباءنا والله امرنا بها قل ان الله لا يامر بالفحشاء اتقولون على الله ما لا تعلمون');
INSERT INTO "imlaei_simple" VALUES(7,29,'7:29','قل امر ربي بالقسط واقيموا وجوهكم عند كل مسجد وادعوه مخلصين له الدين كما بداكم تعودون');
INSERT INTO "imlaei_simple" VALUES(7,30,'7:30','فريقا هدى وفريقا حق عليهم الضلالة انهم اتخذوا الشياطين اولياء من دون الله ويحسبون انهم مهتدون');
INSERT INTO "imlaei_simple" VALUES(7,31,'7:31','يا بني ادم خذوا زينتكم عند كل مسجد وكلوا واشربوا ولا تسرفوا انه لا يحب المسرفين');
INSERT INTO "imlaei_simple" VALUES(7,32,'7:32','قل من حرم زينة الله التي اخرج لعباده والطيبات من الرزق قل هي للذين امنوا في الحياة الدنيا خالصة يوم القيامة كذلك نفصل الايات لقوم يعلمون');
INSERT INTO "imlaei_simple" VALUES(7,33,'7:33','قل انما حرم ربي الفواحش ما ظهر منها وما بطن والاثم والبغي بغير الحق وان تشركوا بالله ما لم ينزل به سلطانا وان تقولوا على الله ما لا تعلمون');
INSERT INTO "imlaei_simple" VALUES(7,34,'7:34','ولكل امة اجل فاذا جاء اجلهم لا يستاخرون ساعة ولا يستقدمون');
INSERT INTO "imlaei_simple" VALUES(7,35,'7:35','يا بني ادم اما ياتينكم رسل منكم يقصون عليكم اياتي فمن اتقى واصلح فلا خوف عليهم ولا هم يحزنون');
INSERT INTO "imlaei_simple" VALUES(7,36,'7:36','والذين كذبوا باياتنا واستكبروا عنها اولئك اصحاب النار هم فيها خالدون');
INSERT INTO "imlaei_simple" VALUES(7,37,'7:37','فمن اظلم ممن افترى على الله كذبا او كذب باياته اولئك ينالهم نصيبهم من الكتاب حتى اذا جاءتهم رسلنا يتوفونهم قالوا اين ما كنتم تدعون من دون الله قالوا ضلوا عنا وشهدوا على انفسهم انهم كانوا كافرين');
INSERT INTO "imlaei_simple" VALUES(7,38,'7:38','قال ادخلوا في امم قد خلت من قبلكم من الجن والانس في النار كلما دخلت امة لعنت اختها حتى اذا اداركوا فيها جميعا قالت اخراهم لاولاهم ربنا هؤلاء اضلونا فاتهم عذابا ضعفا من النار قال لكل ضعف ولكن لا تعلمون');
INSERT INTO "imlaei_simple" VALUES(7,39,'7:39','وقالت اولاهم لاخراهم فما كان لكم علينا من فضل فذوقوا العذاب بما كنتم تكسبون');
INSERT INTO "imlaei_simple" VALUES(7,40,'7:40','ان الذين كذبوا باياتنا واستكبروا عنها لا تفتح لهم ابواب السماء ولا يدخلون الجنة حتى يلج الجمل في سم الخياط وكذلك نجزي المجرمين');
INSERT INTO "imlaei_simple" VALUES(7,41,'7:41','لهم من جهنم مهاد ومن فوقهم غواش وكذلك نجزي الظالمين');
INSERT INTO "imlaei_simple" VALUES(7,42,'7:42','والذين امنوا وعملوا الصالحات لا نكلف نفسا الا وسعها اولئك اصحاب الجنة هم فيها خالدون');
INSERT INTO "imlaei_simple" VALUES(7,43,'7:43','ونزعنا ما في صدورهم من غل تجري من تحتهم الانهار وقالوا الحمد لله الذي هدانا لهذا وما كنا لنهتدي لولا ان هدانا الله لقد جاءت رسل ربنا بالحق ونودوا ان تلكم الجنة اورثتموها بما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(7,44,'7:44','ونادى اصحاب الجنة اصحاب النار ان قد وجدنا ما وعدنا ربنا حقا فهل وجدتم ما وعد ربكم حقا قالوا نعم فاذن مؤذن بينهم ان لعنة الله على الظالمين');
INSERT INTO "imlaei_simple" VALUES(7,45,'7:45','الذين يصدون عن سبيل الله ويبغونها عوجا وهم بالاخرة كافرون');
INSERT INTO "imlaei_simple" VALUES(7,46,'7:46','وبينهما حجاب وعلى الاعراف رجال يعرفون كلا بسيماهم ونادوا اصحاب الجنة ان سلام عليكم لم يدخلوها وهم يطمعون');
INSERT INTO "imlaei_simple" VALUES(7,47,'7:47','واذا صرفت ابصارهم تلقاء اصحاب النار قالوا ربنا لا تجعلنا مع القوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(7,48,'7:48','ونادى اصحاب الاعراف رجالا يعرفونهم بسيماهم قالوا ما اغنى عنكم جمعكم وما كنتم تستكبرون');
INSERT INTO "imlaei_simple" VALUES(7,49,'7:49','اهؤلاء الذين اقسمتم لا ينالهم الله برحمة ادخلوا الجنة لا خوف عليكم ولا انتم تحزنون');
INSERT INTO "imlaei_simple" VALUES(7,50,'7:50','ونادى اصحاب النار اصحاب الجنة ان افيضوا علينا من الماء او مما رزقكم الله قالوا ان الله حرمهما على الكافرين');
INSERT INTO "imlaei_simple" VALUES(7,51,'7:51','الذين اتخذوا دينهم لهوا ولعبا وغرتهم الحياة الدنيا فاليوم ننساهم كما نسوا لقاء يومهم هذا وما كانوا باياتنا يجحدون');
INSERT INTO "imlaei_simple" VALUES(7,52,'7:52','ولقد جئناهم بكتاب فصلناه على علم هدى ورحمة لقوم يؤمنون');
INSERT INTO "imlaei_simple" VALUES(7,53,'7:53','هل ينظرون الا تاويله يوم ياتي تاويله يقول الذين نسوه من قبل قد جاءت رسل ربنا بالحق فهل لنا من شفعاء فيشفعوا لنا او نرد فنعمل غير الذي كنا نعمل قد خسروا انفسهم وضل عنهم ما كانوا يفترون');
INSERT INTO "imlaei_simple" VALUES(7,54,'7:54','ان ربكم الله الذي خلق السماوات والارض في ستة ايام ثم استوى على العرش يغشي الليل النهار يطلبه حثيثا والشمس والقمر والنجوم مسخرات بامره الا له الخلق والامر تبارك الله رب العالمين');
INSERT INTO "imlaei_simple" VALUES(7,55,'7:55','ادعوا ربكم تضرعا وخفية انه لا يحب المعتدين');
INSERT INTO "imlaei_simple" VALUES(7,56,'7:56','ولا تفسدوا في الارض بعد اصلاحها وادعوه خوفا وطمعا ان رحمت الله قريب من المحسنين');
INSERT INTO "imlaei_simple" VALUES(7,57,'7:57','وهو الذي يرسل الرياح بشرا بين يدي رحمته حتى اذا اقلت سحابا ثقالا سقناه لبلد ميت فانزلنا به الماء فاخرجنا به من كل الثمرات كذلك نخرج الموتى لعلكم تذكرون');
INSERT INTO "imlaei_simple" VALUES(7,58,'7:58','والبلد الطيب يخرج نباته باذن ربه والذي خبث لا يخرج الا نكدا كذلك نصرف الايات لقوم يشكرون');
INSERT INTO "imlaei_simple" VALUES(7,59,'7:59','لقد ارسلنا نوحا الى قومه فقال يا قوم اعبدوا الله ما لكم من اله غيره اني اخاف عليكم عذاب يوم عظيم');
INSERT INTO "imlaei_simple" VALUES(7,60,'7:60','قال الملا من قومه انا لنراك في ضلال مبين');
INSERT INTO "imlaei_simple" VALUES(7,61,'7:61','قال يا قوم ليس بي ضلالة ولكني رسول من رب العالمين');
INSERT INTO "imlaei_simple" VALUES(7,62,'7:62','ابلغكم رسالات ربي وانصح لكم واعلم من الله ما لا تعلمون');
INSERT INTO "imlaei_simple" VALUES(7,63,'7:63','اوعجبتم ان جاءكم ذكر من ربكم على رجل منكم لينذركم ولتتقوا ولعلكم ترحمون');
INSERT INTO "imlaei_simple" VALUES(7,64,'7:64','فكذبوه فانجيناه والذين معه في الفلك واغرقنا الذين كذبوا باياتنا انهم كانوا قوما عمين');
INSERT INTO "imlaei_simple" VALUES(7,65,'7:65','والى عاد اخاهم هودا قال يا قوم اعبدوا الله ما لكم من اله غيره افلا تتقون');
INSERT INTO "imlaei_simple" VALUES(7,66,'7:66','قال الملا الذين كفروا من قومه انا لنراك في سفاهة وانا لنظنك من الكاذبين');
INSERT INTO "imlaei_simple" VALUES(7,67,'7:67','قال يا قوم ليس بي سفاهة ولكني رسول من رب العالمين');
INSERT INTO "imlaei_simple" VALUES(7,68,'7:68','ابلغكم رسالات ربي وانا لكم ناصح امين');
INSERT INTO "imlaei_simple" VALUES(7,69,'7:69','اوعجبتم ان جاءكم ذكر من ربكم على رجل منكم لينذركم واذكروا اذ جعلكم خلفاء من بعد قوم نوح وزادكم في الخلق بسطة فاذكروا الاء الله لعلكم تفلحون');
INSERT INTO "imlaei_simple" VALUES(7,70,'7:70','قالوا اجئتنا لنعبد الله وحده ونذر ما كان يعبد اباؤنا فاتنا بما تعدنا ان كنت من الصادقين');
INSERT INTO "imlaei_simple" VALUES(7,71,'7:71','قال قد وقع عليكم من ربكم رجس وغضب اتجادلونني في اسماء سميتموها انتم واباؤكم ما نزل الله بها من سلطان فانتظروا اني معكم من المنتظرين');
INSERT INTO "imlaei_simple" VALUES(7,72,'7:72','فانجيناه والذين معه برحمة منا وقطعنا دابر الذين كذبوا باياتنا وما كانوا مؤمنين');
INSERT INTO "imlaei_simple" VALUES(7,73,'7:73','والى ثمود اخاهم صالحا قال يا قوم اعبدوا الله ما لكم من اله غيره قد جاءتكم بينة من ربكم هذه ناقة الله لكم اية فذروها تاكل في ارض الله ولا تمسوها بسوء فياخذكم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(7,74,'7:74','واذكروا اذ جعلكم خلفاء من بعد عاد وبواكم في الارض تتخذون من سهولها قصورا وتنحتون الجبال بيوتا فاذكروا الاء الله ولا تعثوا في الارض مفسدين');
INSERT INTO "imlaei_simple" VALUES(7,75,'7:75','قال الملا الذين استكبروا من قومه للذين استضعفوا لمن امن منهم اتعلمون ان صالحا مرسل من ربه قالوا انا بما ارسل به مؤمنون');
INSERT INTO "imlaei_simple" VALUES(7,76,'7:76','قال الذين استكبروا انا بالذي امنتم به كافرون');
INSERT INTO "imlaei_simple" VALUES(7,77,'7:77','فعقروا الناقة وعتوا عن امر ربهم وقالوا يا صالح ائتنا بما تعدنا ان كنت من المرسلين');
INSERT INTO "imlaei_simple" VALUES(7,78,'7:78','فاخذتهم الرجفة فاصبحوا في دارهم جاثمين');
INSERT INTO "imlaei_simple" VALUES(7,79,'7:79','فتولى عنهم وقال يا قوم لقد ابلغتكم رسالة ربي ونصحت لكم ولكن لا تحبون الناصحين');
INSERT INTO "imlaei_simple" VALUES(7,80,'7:80','ولوطا اذ قال لقومه اتاتون الفاحشة ما سبقكم بها من احد من العالمين');
INSERT INTO "imlaei_simple" VALUES(7,81,'7:81','انكم لتاتون الرجال شهوة من دون النساء بل انتم قوم مسرفون');
INSERT INTO "imlaei_simple" VALUES(7,82,'7:82','وما كان جواب قومه الا ان قالوا اخرجوهم من قريتكم انهم اناس يتطهرون');
INSERT INTO "imlaei_simple" VALUES(7,83,'7:83','فانجيناه واهله الا امراته كانت من الغابرين');
INSERT INTO "imlaei_simple" VALUES(7,84,'7:84','وامطرنا عليهم مطرا فانظر كيف كان عاقبة المجرمين');
INSERT INTO "imlaei_simple" VALUES(7,85,'7:85','والى مدين اخاهم شعيبا قال يا قوم اعبدوا الله ما لكم من اله غيره قد جاءتكم بينة من ربكم فاوفوا الكيل والميزان ولا تبخسوا الناس اشياءهم ولا تفسدوا في الارض بعد اصلاحها ذلكم خير لكم ان كنتم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(7,86,'7:86','ولا تقعدوا بكل صراط توعدون وتصدون عن سبيل الله من امن به وتبغونها عوجا واذكروا اذ كنتم قليلا فكثركم وانظروا كيف كان عاقبة المفسدين');
INSERT INTO "imlaei_simple" VALUES(7,87,'7:87','وان كان طائفة منكم امنوا بالذي ارسلت به وطائفة لم يؤمنوا فاصبروا حتى يحكم الله بيننا وهو خير الحاكمين');
INSERT INTO "imlaei_simple" VALUES(7,88,'7:88','قال الملا الذين استكبروا من قومه لنخرجنك يا شعيب والذين امنوا معك من قريتنا او لتعودن في ملتنا قال اولو كنا كارهين');
INSERT INTO "imlaei_simple" VALUES(7,89,'7:89','قد افترينا على الله كذبا ان عدنا في ملتكم بعد اذ نجانا الله منها وما يكون لنا ان نعود فيها الا ان يشاء الله ربنا وسع ربنا كل شيء علما على الله توكلنا ربنا افتح بيننا وبين قومنا بالحق وانت خير الفاتحين');
INSERT INTO "imlaei_simple" VALUES(7,90,'7:90','وقال الملا الذين كفروا من قومه لئن اتبعتم شعيبا انكم اذا لخاسرون');
INSERT INTO "imlaei_simple" VALUES(7,91,'7:91','فاخذتهم الرجفة فاصبحوا في دارهم جاثمين');
INSERT INTO "imlaei_simple" VALUES(7,92,'7:92','الذين كذبوا شعيبا كان لم يغنوا فيها الذين كذبوا شعيبا كانوا هم الخاسرين');
INSERT INTO "imlaei_simple" VALUES(7,93,'7:93','فتولى عنهم وقال يا قوم لقد ابلغتكم رسالات ربي ونصحت لكم فكيف اسى على قوم كافرين');
INSERT INTO "imlaei_simple" VALUES(7,94,'7:94','وما ارسلنا في قرية من نبي الا اخذنا اهلها بالباساء والضراء لعلهم يضرعون');
INSERT INTO "imlaei_simple" VALUES(7,95,'7:95','ثم بدلنا مكان السيئة الحسنة حتى عفوا وقالوا قد مس اباءنا الضراء والسراء فاخذناهم بغتة وهم لا يشعرون');
INSERT INTO "imlaei_simple" VALUES(7,96,'7:96','ولو ان اهل القرى امنوا واتقوا لفتحنا عليهم بركات من السماء والارض ولكن كذبوا فاخذناهم بما كانوا يكسبون');
INSERT INTO "imlaei_simple" VALUES(7,97,'7:97','افامن اهل القرى ان ياتيهم باسنا بياتا وهم نائمون');
INSERT INTO "imlaei_simple" VALUES(7,98,'7:98','اوامن اهل القرى ان ياتيهم باسنا ضحى وهم يلعبون');
INSERT INTO "imlaei_simple" VALUES(7,99,'7:99','افامنوا مكر الله فلا يامن مكر الله الا القوم الخاسرون');
INSERT INTO "imlaei_simple" VALUES(7,100,'7:100','اولم يهد للذين يرثون الارض من بعد اهلها ان لو نشاء اصبناهم بذنوبهم ونطبع على قلوبهم فهم لا يسمعون');
INSERT INTO "imlaei_simple" VALUES(7,101,'7:101','تلك القرى نقص عليك من انبائها ولقد جاءتهم رسلهم بالبينات فما كانوا ليؤمنوا بما كذبوا من قبل كذلك يطبع الله على قلوب الكافرين');
INSERT INTO "imlaei_simple" VALUES(7,102,'7:102','وما وجدنا لاكثرهم من عهد وان وجدنا اكثرهم لفاسقين');
INSERT INTO "imlaei_simple" VALUES(7,103,'7:103','ثم بعثنا من بعدهم موسى باياتنا الى فرعون وملئه فظلموا بها فانظر كيف كان عاقبة المفسدين');
INSERT INTO "imlaei_simple" VALUES(7,104,'7:104','وقال موسى يا فرعون اني رسول من رب العالمين');
INSERT INTO "imlaei_simple" VALUES(7,105,'7:105','حقيق على ان لا اقول على الله الا الحق قد جئتكم ببينة من ربكم فارسل معي بني اسرائيل');
INSERT INTO "imlaei_simple" VALUES(7,106,'7:106','قال ان كنت جئت باية فات بها ان كنت من الصادقين');
INSERT INTO "imlaei_simple" VALUES(7,107,'7:107','فالقى عصاه فاذا هي ثعبان مبين');
INSERT INTO "imlaei_simple" VALUES(7,108,'7:108','ونزع يده فاذا هي بيضاء للناظرين');
INSERT INTO "imlaei_simple" VALUES(7,109,'7:109','قال الملا من قوم فرعون ان هذا لساحر عليم');
INSERT INTO "imlaei_simple" VALUES(7,110,'7:110','يريد ان يخرجكم من ارضكم فماذا تامرون');
INSERT INTO "imlaei_simple" VALUES(7,111,'7:111','قالوا ارجه واخاه وارسل في المدائن حاشرين');
INSERT INTO "imlaei_simple" VALUES(7,112,'7:112','ياتوك بكل ساحر عليم');
INSERT INTO "imlaei_simple" VALUES(7,113,'7:113','وجاء السحرة فرعون قالوا ان لنا لاجرا ان كنا نحن الغالبين');
INSERT INTO "imlaei_simple" VALUES(7,114,'7:114','قال نعم وانكم لمن المقربين');
INSERT INTO "imlaei_simple" VALUES(7,115,'7:115','قالوا يا موسى اما ان تلقي واما ان نكون نحن الملقين');
INSERT INTO "imlaei_simple" VALUES(7,116,'7:116','قال القوا فلما القوا سحروا اعين الناس واسترهبوهم وجاءوا بسحر عظيم');
INSERT INTO "imlaei_simple" VALUES(7,117,'7:117','واوحينا الى موسى ان الق عصاك فاذا هي تلقف ما يافكون');
INSERT INTO "imlaei_simple" VALUES(7,118,'7:118','فوقع الحق وبطل ما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(7,119,'7:119','فغلبوا هنالك وانقلبوا صاغرين');
INSERT INTO "imlaei_simple" VALUES(7,120,'7:120','والقي السحرة ساجدين');
INSERT INTO "imlaei_simple" VALUES(7,121,'7:121','قالوا امنا برب العالمين');
INSERT INTO "imlaei_simple" VALUES(7,122,'7:122','رب موسى وهارون');
INSERT INTO "imlaei_simple" VALUES(7,123,'7:123','قال فرعون امنتم به قبل ان اذن لكم ان هذا لمكر مكرتموه في المدينة لتخرجوا منها اهلها فسوف تعلمون');
INSERT INTO "imlaei_simple" VALUES(7,124,'7:124','لاقطعن ايديكم وارجلكم من خلاف ثم لاصلبنكم اجمعين');
INSERT INTO "imlaei_simple" VALUES(7,125,'7:125','قالوا انا الى ربنا منقلبون');
INSERT INTO "imlaei_simple" VALUES(7,126,'7:126','وما تنقم منا الا ان امنا بايات ربنا لما جاءتنا ربنا افرغ علينا صبرا وتوفنا مسلمين');
INSERT INTO "imlaei_simple" VALUES(7,127,'7:127','وقال الملا من قوم فرعون اتذر موسى وقومه ليفسدوا في الارض ويذرك والهتك قال سنقتل ابناءهم ونستحيي نساءهم وانا فوقهم قاهرون');
INSERT INTO "imlaei_simple" VALUES(7,128,'7:128','قال موسى لقومه استعينوا بالله واصبروا ان الارض لله يورثها من يشاء من عباده والعاقبة للمتقين');
INSERT INTO "imlaei_simple" VALUES(7,129,'7:129','قالوا اوذينا من قبل ان تاتينا ومن بعد ما جئتنا قال عسى ربكم ان يهلك عدوكم ويستخلفكم في الارض فينظر كيف تعملون');
INSERT INTO "imlaei_simple" VALUES(7,130,'7:130','ولقد اخذنا ال فرعون بالسنين ونقص من الثمرات لعلهم يذكرون');
INSERT INTO "imlaei_simple" VALUES(7,131,'7:131','فاذا جاءتهم الحسنة قالوا لنا هذه وان تصبهم سيئة يطيروا بموسى ومن معه الا انما طائرهم عند الله ولكن اكثرهم لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(7,132,'7:132','وقالوا مهما تاتنا به من اية لتسحرنا بها فما نحن لك بمؤمنين');
INSERT INTO "imlaei_simple" VALUES(7,133,'7:133','فارسلنا عليهم الطوفان والجراد والقمل والضفادع والدم ايات مفصلات فاستكبروا وكانوا قوما مجرمين');
INSERT INTO "imlaei_simple" VALUES(7,134,'7:134','ولما وقع عليهم الرجز قالوا يا موسى ادع لنا ربك بما عهد عندك لئن كشفت عنا الرجز لنؤمنن لك ولنرسلن معك بني اسرائيل');
INSERT INTO "imlaei_simple" VALUES(7,135,'7:135','فلما كشفنا عنهم الرجز الى اجل هم بالغوه اذا هم ينكثون');
INSERT INTO "imlaei_simple" VALUES(7,136,'7:136','فانتقمنا منهم فاغرقناهم في اليم بانهم كذبوا باياتنا وكانوا عنها غافلين');
INSERT INTO "imlaei_simple" VALUES(7,137,'7:137','واورثنا القوم الذين كانوا يستضعفون مشارق الارض ومغاربها التي باركنا فيها وتمت كلمت ربك الحسنى على بني اسرائيل بما صبروا ودمرنا ما كان يصنع فرعون وقومه وما كانوا يعرشون');
INSERT INTO "imlaei_simple" VALUES(7,138,'7:138','وجاوزنا ببني اسرائيل البحر فاتوا على قوم يعكفون على اصنام لهم قالوا يا موسى اجعل لنا الها كما لهم الهة قال انكم قوم تجهلون');
INSERT INTO "imlaei_simple" VALUES(7,139,'7:139','ان هؤلاء متبر ما هم فيه وباطل ما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(7,140,'7:140','قال اغير الله ابغيكم الها وهو فضلكم على العالمين');
INSERT INTO "imlaei_simple" VALUES(7,141,'7:141','واذ انجيناكم من ال فرعون يسومونكم سوء العذاب يقتلون ابناءكم ويستحيون نساءكم وفي ذلكم بلاء من ربكم عظيم');
INSERT INTO "imlaei_simple" VALUES(7,142,'7:142','وواعدنا موسى ثلاثين ليلة واتممناها بعشر فتم ميقات ربه اربعين ليلة وقال موسى لاخيه هارون اخلفني في قومي واصلح ولا تتبع سبيل المفسدين');
INSERT INTO "imlaei_simple" VALUES(7,143,'7:143','ولما جاء موسى لميقاتنا وكلمه ربه قال رب ارني انظر اليك قال لن تراني ولكن انظر الى الجبل فان استقر مكانه فسوف تراني فلما تجلى ربه للجبل جعله دكا وخر موسى صعقا فلما افاق قال سبحانك تبت اليك وانا اول المؤمنين');
INSERT INTO "imlaei_simple" VALUES(7,144,'7:144','قال يا موسى اني اصطفيتك على الناس برسالاتي وبكلامي فخذ ما اتيتك وكن من الشاكرين');
INSERT INTO "imlaei_simple" VALUES(7,145,'7:145','وكتبنا له في الالواح من كل شيء موعظة وتفصيلا لكل شيء فخذها بقوة وامر قومك ياخذوا باحسنها ساريكم دار الفاسقين');
INSERT INTO "imlaei_simple" VALUES(7,146,'7:146','ساصرف عن اياتي الذين يتكبرون في الارض بغير الحق وان يروا كل اية لا يؤمنوا بها وان يروا سبيل الرشد لا يتخذوه سبيلا وان يروا سبيل الغي يتخذوه سبيلا ذلك بانهم كذبوا باياتنا وكانوا عنها غافلين');
INSERT INTO "imlaei_simple" VALUES(7,147,'7:147','والذين كذبوا باياتنا ولقاء الاخرة حبطت اعمالهم هل يجزون الا ما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(7,148,'7:148','واتخذ قوم موسى من بعده من حليهم عجلا جسدا له خوار الم يروا انه لا يكلمهم ولا يهديهم سبيلا اتخذوه وكانوا ظالمين');
INSERT INTO "imlaei_simple" VALUES(7,149,'7:149','ولما سقط في ايديهم وراوا انهم قد ضلوا قالوا لئن لم يرحمنا ربنا ويغفر لنا لنكونن من الخاسرين');
INSERT INTO "imlaei_simple" VALUES(7,150,'7:150','ولما رجع موسى الى قومه غضبان اسفا قال بئسما خلفتموني من بعدي اعجلتم امر ربكم والقى الالواح واخذ براس اخيه يجره اليه قال ابن ام ان القوم استضعفوني وكادوا يقتلونني فلا تشمت بي الاعداء ولا تجعلني مع القوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(7,151,'7:151','قال رب اغفر لي ولاخي وادخلنا في رحمتك وانت ارحم الراحمين');
INSERT INTO "imlaei_simple" VALUES(7,152,'7:152','ان الذين اتخذوا العجل سينالهم غضب من ربهم وذلة في الحياة الدنيا وكذلك نجزي المفترين');
INSERT INTO "imlaei_simple" VALUES(7,153,'7:153','والذين عملوا السيئات ثم تابوا من بعدها وامنوا ان ربك من بعدها لغفور رحيم');
INSERT INTO "imlaei_simple" VALUES(7,154,'7:154','ولما سكت عن موسى الغضب اخذ الالواح وفي نسختها هدى ورحمة للذين هم لربهم يرهبون');
INSERT INTO "imlaei_simple" VALUES(7,155,'7:155','واختار موسى قومه سبعين رجلا لميقاتنا فلما اخذتهم الرجفة قال رب لو شئت اهلكتهم من قبل واياي اتهلكنا بما فعل السفهاء منا ان هي الا فتنتك تضل بها من تشاء وتهدي من تشاء انت ولينا فاغفر لنا وارحمنا وانت خير الغافرين');
INSERT INTO "imlaei_simple" VALUES(7,156,'7:156','واكتب لنا في هذه الدنيا حسنة وفي الاخرة انا هدنا اليك قال عذابي اصيب به من اشاء ورحمتي وسعت كل شيء فساكتبها للذين يتقون ويؤتون الزكاة والذين هم باياتنا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(7,157,'7:157','الذين يتبعون الرسول النبي الامي الذي يجدونه مكتوبا عندهم في التوراة والانجيل يامرهم بالمعروف وينهاهم عن المنكر ويحل لهم الطيبات ويحرم عليهم الخبائث ويضع عنهم اصرهم والاغلال التي كانت عليهم فالذين امنوا به وعزروه ونصروه واتبعوا النور الذي انزل معه اولئك هم المفلحون');
INSERT INTO "imlaei_simple" VALUES(7,158,'7:158','قل يا ايها الناس اني رسول الله اليكم جميعا الذي له ملك السماوات والارض لا اله الا هو يحيي ويميت فامنوا بالله ورسوله النبي الامي الذي يؤمن بالله وكلماته واتبعوه لعلكم تهتدون');
INSERT INTO "imlaei_simple" VALUES(7,159,'7:159','ومن قوم موسى امة يهدون بالحق وبه يعدلون');
INSERT INTO "imlaei_simple" VALUES(7,160,'7:160','وقطعناهم اثنتي عشرة اسباطا امما واوحينا الى موسى اذ استسقاه قومه ان اضرب بعصاك الحجر فانبجست منه اثنتا عشرة عينا قد علم كل اناس مشربهم وظللنا عليهم الغمام وانزلنا عليهم المن والسلوى كلوا من طيبات ما رزقناكم وما ظلمونا ولكن كانوا انفسهم يظلمون');
INSERT INTO "imlaei_simple" VALUES(7,161,'7:161','واذ قيل لهم اسكنوا هذه القرية وكلوا منها حيث شئتم وقولوا حطة وادخلوا الباب سجدا نغفر لكم خطيئاتكم سنزيد المحسنين');
INSERT INTO "imlaei_simple" VALUES(7,162,'7:162','فبدل الذين ظلموا منهم قولا غير الذي قيل لهم فارسلنا عليهم رجزا من السماء بما كانوا يظلمون');
INSERT INTO "imlaei_simple" VALUES(7,163,'7:163','واسالهم عن القرية التي كانت حاضرة البحر اذ يعدون في السبت اذ تاتيهم حيتانهم يوم سبتهم شرعا ويوم لا يسبتون لا تاتيهم كذلك نبلوهم بما كانوا يفسقون');
INSERT INTO "imlaei_simple" VALUES(7,164,'7:164','واذ قالت امة منهم لم تعظون قوما الله مهلكهم او معذبهم عذابا شديدا قالوا معذرة الى ربكم ولعلهم يتقون');
INSERT INTO "imlaei_simple" VALUES(7,165,'7:165','فلما نسوا ما ذكروا به انجينا الذين ينهون عن السوء واخذنا الذين ظلموا بعذاب بئيس بما كانوا يفسقون');
INSERT INTO "imlaei_simple" VALUES(7,166,'7:166','فلما عتوا عن ما نهوا عنه قلنا لهم كونوا قردة خاسئين');
INSERT INTO "imlaei_simple" VALUES(7,167,'7:167','واذ تاذن ربك ليبعثن عليهم الى يوم القيامة من يسومهم سوء العذاب ان ربك لسريع العقاب وانه لغفور رحيم');
INSERT INTO "imlaei_simple" VALUES(7,168,'7:168','وقطعناهم في الارض امما منهم الصالحون ومنهم دون ذلك وبلوناهم بالحسنات والسيئات لعلهم يرجعون');
INSERT INTO "imlaei_simple" VALUES(7,169,'7:169','فخلف من بعدهم خلف ورثوا الكتاب ياخذون عرض هذا الادنى ويقولون سيغفر لنا وان ياتهم عرض مثله ياخذوه الم يؤخذ عليهم ميثاق الكتاب ان لا يقولوا على الله الا الحق ودرسوا ما فيه والدار الاخرة خير للذين يتقون افلا تعقلون');
INSERT INTO "imlaei_simple" VALUES(7,170,'7:170','والذين يمسكون بالكتاب واقاموا الصلاة انا لا نضيع اجر المصلحين');
INSERT INTO "imlaei_simple" VALUES(7,171,'7:171','واذ نتقنا الجبل فوقهم كانه ظلة وظنوا انه واقع بهم خذوا ما اتيناكم بقوة واذكروا ما فيه لعلكم تتقون');
INSERT INTO "imlaei_simple" VALUES(7,172,'7:172','واذ اخذ ربك من بني ادم من ظهورهم ذريتهم واشهدهم على انفسهم الست بربكم قالوا بلى شهدنا ان تقولوا يوم القيامة انا كنا عن هذا غافلين');
INSERT INTO "imlaei_simple" VALUES(7,173,'7:173','او تقولوا انما اشرك اباؤنا من قبل وكنا ذرية من بعدهم افتهلكنا بما فعل المبطلون');
INSERT INTO "imlaei_simple" VALUES(7,174,'7:174','وكذلك نفصل الايات ولعلهم يرجعون');
INSERT INTO "imlaei_simple" VALUES(7,175,'7:175','واتل عليهم نبا الذي اتيناه اياتنا فانسلخ منها فاتبعه الشيطان فكان من الغاوين');
INSERT INTO "imlaei_simple" VALUES(7,176,'7:176','ولو شئنا لرفعناه بها ولكنه اخلد الى الارض واتبع هواه فمثله كمثل الكلب ان تحمل عليه يلهث او تتركه يلهث ذلك مثل القوم الذين كذبوا باياتنا فاقصص القصص لعلهم يتفكرون');
INSERT INTO "imlaei_simple" VALUES(7,177,'7:177','ساء مثلا القوم الذين كذبوا باياتنا وانفسهم كانوا يظلمون');
INSERT INTO "imlaei_simple" VALUES(7,178,'7:178','من يهد الله فهو المهتدي ومن يضلل فاولئك هم الخاسرون');
INSERT INTO "imlaei_simple" VALUES(7,179,'7:179','ولقد ذرانا لجهنم كثيرا من الجن والانس لهم قلوب لا يفقهون بها ولهم اعين لا يبصرون بها ولهم اذان لا يسمعون بها اولئك كالانعام بل هم اضل اولئك هم الغافلون');
INSERT INTO "imlaei_simple" VALUES(7,180,'7:180','ولله الاسماء الحسنى فادعوه بها وذروا الذين يلحدون في اسمائه سيجزون ما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(7,181,'7:181','وممن خلقنا امة يهدون بالحق وبه يعدلون');
INSERT INTO "imlaei_simple" VALUES(7,182,'7:182','والذين كذبوا باياتنا سنستدرجهم من حيث لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(7,183,'7:183','واملي لهم ان كيدي متين');
INSERT INTO "imlaei_simple" VALUES(7,184,'7:184','اولم يتفكروا ما بصاحبهم من جنة ان هو الا نذير مبين');
INSERT INTO "imlaei_simple" VALUES(7,185,'7:185','اولم ينظروا في ملكوت السماوات والارض وما خلق الله من شيء وان عسى ان يكون قد اقترب اجلهم فباي حديث بعده يؤمنون');
INSERT INTO "imlaei_simple" VALUES(7,186,'7:186','من يضلل الله فلا هادي له ويذرهم في طغيانهم يعمهون');
INSERT INTO "imlaei_simple" VALUES(7,187,'7:187','يسالونك عن الساعة ايان مرساها قل انما علمها عند ربي لا يجليها لوقتها الا هو ثقلت في السماوات والارض لا تاتيكم الا بغتة يسالونك كانك حفي عنها قل انما علمها عند الله ولكن اكثر الناس لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(7,188,'7:188','قل لا املك لنفسي نفعا ولا ضرا الا ما شاء الله ولو كنت اعلم الغيب لاستكثرت من الخير وما مسني السوء ان انا الا نذير وبشير لقوم يؤمنون');
INSERT INTO "imlaei_simple" VALUES(7,189,'7:189','هو الذي خلقكم من نفس واحدة وجعل منها زوجها ليسكن اليها فلما تغشاها حملت حملا خفيفا فمرت به فلما اثقلت دعوا الله ربهما لئن اتيتنا صالحا لنكونن من الشاكرين');
INSERT INTO "imlaei_simple" VALUES(7,190,'7:190','فلما اتاهما صالحا جعلا له شركاء فيما اتاهما فتعالى الله عما يشركون');
INSERT INTO "imlaei_simple" VALUES(7,191,'7:191','ايشركون ما لا يخلق شيئا وهم يخلقون');
INSERT INTO "imlaei_simple" VALUES(7,192,'7:192','ولا يستطيعون لهم نصرا ولا انفسهم ينصرون');
INSERT INTO "imlaei_simple" VALUES(7,193,'7:193','وان تدعوهم الى الهدى لا يتبعوكم سواء عليكم ادعوتموهم ام انتم صامتون');
INSERT INTO "imlaei_simple" VALUES(7,194,'7:194','ان الذين تدعون من دون الله عباد امثالكم فادعوهم فليستجيبوا لكم ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(7,195,'7:195','الهم ارجل يمشون بها ام لهم ايد يبطشون بها ام لهم اعين يبصرون بها ام لهم اذان يسمعون بها قل ادعوا شركاءكم ثم كيدون فلا تنظرون');
INSERT INTO "imlaei_simple" VALUES(7,196,'7:196','ان وليي الله الذي نزل الكتاب وهو يتولى الصالحين');
INSERT INTO "imlaei_simple" VALUES(7,197,'7:197','والذين تدعون من دونه لا يستطيعون نصركم ولا انفسهم ينصرون');
INSERT INTO "imlaei_simple" VALUES(7,198,'7:198','وان تدعوهم الى الهدى لا يسمعوا وتراهم ينظرون اليك وهم لا يبصرون');
INSERT INTO "imlaei_simple" VALUES(7,199,'7:199','خذ العفو وامر بالعرف واعرض عن الجاهلين');
INSERT INTO "imlaei_simple" VALUES(7,200,'7:200','واما ينزغنك من الشيطان نزغ فاستعذ بالله انه سميع عليم');
INSERT INTO "imlaei_simple" VALUES(7,201,'7:201','ان الذين اتقوا اذا مسهم طائف من الشيطان تذكروا فاذا هم مبصرون');
INSERT INTO "imlaei_simple" VALUES(7,202,'7:202','واخوانهم يمدونهم في الغي ثم لا يقصرون');
INSERT INTO "imlaei_simple" VALUES(7,203,'7:203','واذا لم تاتهم باية قالوا لولا اجتبيتها قل انما اتبع ما يوحى الي من ربي هذا بصائر من ربكم وهدى ورحمة لقوم يؤمنون');
INSERT INTO "imlaei_simple" VALUES(7,204,'7:204','واذا قرئ القران فاستمعوا له وانصتوا لعلكم ترحمون');
INSERT INTO "imlaei_simple" VALUES(7,205,'7:205','واذكر ربك في نفسك تضرعا وخيفة ودون الجهر من القول بالغدو والاصال ولا تكن من الغافلين');
INSERT INTO "imlaei_simple" VALUES(7,206,'7:206','ان الذين عند ربك لا يستكبرون عن عبادته ويسبحونه وله يسجدون');
INSERT INTO "imlaei_simple" VALUES(8,1,'8:1','يسالونك عن الانفال قل الانفال لله والرسول فاتقوا الله واصلحوا ذات بينكم واطيعوا الله ورسوله ان كنتم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(8,2,'8:2','انما المؤمنون الذين اذا ذكر الله وجلت قلوبهم واذا تليت عليهم اياته زادتهم ايمانا وعلى ربهم يتوكلون');
INSERT INTO "imlaei_simple" VALUES(8,3,'8:3','الذين يقيمون الصلاة ومما رزقناهم ينفقون');
INSERT INTO "imlaei_simple" VALUES(8,4,'8:4','اولئك هم المؤمنون حقا لهم درجات عند ربهم ومغفرة ورزق كريم');
INSERT INTO "imlaei_simple" VALUES(8,5,'8:5','كما اخرجك ربك من بيتك بالحق وان فريقا من المؤمنين لكارهون');
INSERT INTO "imlaei_simple" VALUES(8,6,'8:6','يجادلونك في الحق بعدما تبين كانما يساقون الى الموت وهم ينظرون');
INSERT INTO "imlaei_simple" VALUES(8,7,'8:7','واذ يعدكم الله احدى الطائفتين انها لكم وتودون ان غير ذات الشوكة تكون لكم ويريد الله ان يحق الحق بكلماته ويقطع دابر الكافرين');
INSERT INTO "imlaei_simple" VALUES(8,8,'8:8','ليحق الحق ويبطل الباطل ولو كره المجرمون');
INSERT INTO "imlaei_simple" VALUES(8,9,'8:9','اذ تستغيثون ربكم فاستجاب لكم اني ممدكم بالف من الملائكة مردفين');
INSERT INTO "imlaei_simple" VALUES(8,10,'8:10','وما جعله الله الا بشرى ولتطمئن به قلوبكم وما النصر الا من عند الله ان الله عزيز حكيم');
INSERT INTO "imlaei_simple" VALUES(8,11,'8:11','اذ يغشيكم النعاس امنة منه وينزل عليكم من السماء ماء ليطهركم به ويذهب عنكم رجز الشيطان وليربط على قلوبكم ويثبت به الاقدام');
INSERT INTO "imlaei_simple" VALUES(8,12,'8:12','اذ يوحي ربك الى الملائكة اني معكم فثبتوا الذين امنوا سالقي في قلوب الذين كفروا الرعب فاضربوا فوق الاعناق واضربوا منهم كل بنان');
INSERT INTO "imlaei_simple" VALUES(8,13,'8:13','ذلك بانهم شاقوا الله ورسوله ومن يشاقق الله ورسوله فان الله شديد العقاب');
INSERT INTO "imlaei_simple" VALUES(8,14,'8:14','ذلكم فذوقوه وان للكافرين عذاب النار');
INSERT INTO "imlaei_simple" VALUES(8,15,'8:15','يا ايها الذين امنوا اذا لقيتم الذين كفروا زحفا فلا تولوهم الادبار');
INSERT INTO "imlaei_simple" VALUES(8,16,'8:16','ومن يولهم يومئذ دبره الا متحرفا لقتال او متحيزا الى فئة فقد باء بغضب من الله وماواه جهنم وبئس المصير');
INSERT INTO "imlaei_simple" VALUES(8,17,'8:17','فلم تقتلوهم ولكن الله قتلهم وما رميت اذ رميت ولكن الله رمى وليبلي المؤمنين منه بلاء حسنا ان الله سميع عليم');
INSERT INTO "imlaei_simple" VALUES(8,18,'8:18','ذلكم وان الله موهن كيد الكافرين');
INSERT INTO "imlaei_simple" VALUES(8,19,'8:19','ان تستفتحوا فقد جاءكم الفتح وان تنتهوا فهو خير لكم وان تعودوا نعد ولن تغني عنكم فئتكم شيئا ولو كثرت وان الله مع المؤمنين');
INSERT INTO "imlaei_simple" VALUES(8,20,'8:20','يا ايها الذين امنوا اطيعوا الله ورسوله ولا تولوا عنه وانتم تسمعون');
INSERT INTO "imlaei_simple" VALUES(8,21,'8:21','ولا تكونوا كالذين قالوا سمعنا وهم لا يسمعون');
INSERT INTO "imlaei_simple" VALUES(8,22,'8:22','ان شر الدواب عند الله الصم البكم الذين لا يعقلون');
INSERT INTO "imlaei_simple" VALUES(8,23,'8:23','ولو علم الله فيهم خيرا لاسمعهم ولو اسمعهم لتولوا وهم معرضون');
INSERT INTO "imlaei_simple" VALUES(8,24,'8:24','يا ايها الذين امنوا استجيبوا لله وللرسول اذا دعاكم لما يحييكم واعلموا ان الله يحول بين المرء وقلبه وانه اليه تحشرون');
INSERT INTO "imlaei_simple" VALUES(8,25,'8:25','واتقوا فتنة لا تصيبن الذين ظلموا منكم خاصة واعلموا ان الله شديد العقاب');
INSERT INTO "imlaei_simple" VALUES(8,26,'8:26','واذكروا اذ انتم قليل مستضعفون في الارض تخافون ان يتخطفكم الناس فاواكم وايدكم بنصره ورزقكم من الطيبات لعلكم تشكرون');
INSERT INTO "imlaei_simple" VALUES(8,27,'8:27','يا ايها الذين امنوا لا تخونوا الله والرسول وتخونوا اماناتكم وانتم تعلمون');
INSERT INTO "imlaei_simple" VALUES(8,28,'8:28','واعلموا انما اموالكم واولادكم فتنة وان الله عنده اجر عظيم');
INSERT INTO "imlaei_simple" VALUES(8,29,'8:29','يا ايها الذين امنوا ان تتقوا الله يجعل لكم فرقانا ويكفر عنكم سيئاتكم ويغفر لكم والله ذو الفضل العظيم');
INSERT INTO "imlaei_simple" VALUES(8,30,'8:30','واذ يمكر بك الذين كفروا ليثبتوك او يقتلوك او يخرجوك ويمكرون ويمكر الله والله خير الماكرين');
INSERT INTO "imlaei_simple" VALUES(8,31,'8:31','واذا تتلى عليهم اياتنا قالوا قد سمعنا لو نشاء لقلنا مثل هذا ان هذا الا اساطير الاولين');
INSERT INTO "imlaei_simple" VALUES(8,32,'8:32','واذ قالوا اللهم ان كان هذا هو الحق من عندك فامطر علينا حجارة من السماء او ائتنا بعذاب اليم');
INSERT INTO "imlaei_simple" VALUES(8,33,'8:33','وما كان الله ليعذبهم وانت فيهم وما كان الله معذبهم وهم يستغفرون');
INSERT INTO "imlaei_simple" VALUES(8,34,'8:34','وما لهم الا يعذبهم الله وهم يصدون عن المسجد الحرام وما كانوا اولياءه ان اولياؤه الا المتقون ولكن اكثرهم لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(8,35,'8:35','وما كان صلاتهم عند البيت الا مكاء وتصدية فذوقوا العذاب بما كنتم تكفرون');
INSERT INTO "imlaei_simple" VALUES(8,36,'8:36','ان الذين كفروا ينفقون اموالهم ليصدوا عن سبيل الله فسينفقونها ثم تكون عليهم حسرة ثم يغلبون والذين كفروا الى جهنم يحشرون');
INSERT INTO "imlaei_simple" VALUES(8,37,'8:37','ليميز الله الخبيث من الطيب ويجعل الخبيث بعضه على بعض فيركمه جميعا فيجعله في جهنم اولئك هم الخاسرون');
INSERT INTO "imlaei_simple" VALUES(8,38,'8:38','قل للذين كفروا ان ينتهوا يغفر لهم ما قد سلف وان يعودوا فقد مضت سنت الاولين');
INSERT INTO "imlaei_simple" VALUES(8,39,'8:39','وقاتلوهم حتى لا تكون فتنة ويكون الدين كله لله فان انتهوا فان الله بما يعملون بصير');
INSERT INTO "imlaei_simple" VALUES(8,40,'8:40','وان تولوا فاعلموا ان الله مولاكم نعم المولى ونعم النصير');
INSERT INTO "imlaei_simple" VALUES(8,41,'8:41','واعلموا انما غنمتم من شيء فان لله خمسه وللرسول ولذي القربى واليتامى والمساكين وابن السبيل ان كنتم امنتم بالله وما انزلنا على عبدنا يوم الفرقان يوم التقى الجمعان والله على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(8,42,'8:42','اذ انتم بالعدوة الدنيا وهم بالعدوة القصوى والركب اسفل منكم ولو تواعدتم لاختلفتم في الميعاد ولكن ليقضي الله امرا كان مفعولا ليهلك من هلك عن بينة ويحيى من حي عن بينة وان الله لسميع عليم');
INSERT INTO "imlaei_simple" VALUES(8,43,'8:43','اذ يريكهم الله في منامك قليلا ولو اراكهم كثيرا لفشلتم ولتنازعتم في الامر ولكن الله سلم انه عليم بذات الصدور');
INSERT INTO "imlaei_simple" VALUES(8,44,'8:44','واذ يريكموهم اذ التقيتم في اعينكم قليلا ويقللكم في اعينهم ليقضي الله امرا كان مفعولا والى الله ترجع الامور');
INSERT INTO "imlaei_simple" VALUES(8,45,'8:45','يا ايها الذين امنوا اذا لقيتم فئة فاثبتوا واذكروا الله كثيرا لعلكم تفلحون');
INSERT INTO "imlaei_simple" VALUES(8,46,'8:46','واطيعوا الله ورسوله ولا تنازعوا فتفشلوا وتذهب ريحكم واصبروا ان الله مع الصابرين');
INSERT INTO "imlaei_simple" VALUES(8,47,'8:47','ولا تكونوا كالذين خرجوا من ديارهم بطرا ورئاء الناس ويصدون عن سبيل الله والله بما يعملون محيط');
INSERT INTO "imlaei_simple" VALUES(8,48,'8:48','واذ زين لهم الشيطان اعمالهم وقال لا غالب لكم اليوم من الناس واني جار لكم فلما تراءت الفئتان نكص على عقبيه وقال اني بريء منكم اني ارى ما لا ترون اني اخاف الله والله شديد العقاب');
INSERT INTO "imlaei_simple" VALUES(8,49,'8:49','اذ يقول المنافقون والذين في قلوبهم مرض غر هؤلاء دينهم ومن يتوكل على الله فان الله عزيز حكيم');
INSERT INTO "imlaei_simple" VALUES(8,50,'8:50','ولو ترى اذ يتوفى الذين كفروا الملائكة يضربون وجوههم وادبارهم وذوقوا عذاب الحريق');
INSERT INTO "imlaei_simple" VALUES(8,51,'8:51','ذلك بما قدمت ايديكم وان الله ليس بظلام للعبيد');
INSERT INTO "imlaei_simple" VALUES(8,52,'8:52','كداب ال فرعون والذين من قبلهم كفروا بايات الله فاخذهم الله بذنوبهم ان الله قوي شديد العقاب');
INSERT INTO "imlaei_simple" VALUES(8,53,'8:53','ذلك بان الله لم يك مغيرا نعمة انعمها على قوم حتى يغيروا ما بانفسهم وان الله سميع عليم');
INSERT INTO "imlaei_simple" VALUES(8,54,'8:54','كداب ال فرعون والذين من قبلهم كذبوا بايات ربهم فاهلكناهم بذنوبهم واغرقنا ال فرعون وكل كانوا ظالمين');
INSERT INTO "imlaei_simple" VALUES(8,55,'8:55','ان شر الدواب عند الله الذين كفروا فهم لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(8,56,'8:56','الذين عاهدت منهم ثم ينقضون عهدهم في كل مرة وهم لا يتقون');
INSERT INTO "imlaei_simple" VALUES(8,57,'8:57','فاما تثقفنهم في الحرب فشرد بهم من خلفهم لعلهم يذكرون');
INSERT INTO "imlaei_simple" VALUES(8,58,'8:58','واما تخافن من قوم خيانة فانبذ اليهم على سواء ان الله لا يحب الخائنين');
INSERT INTO "imlaei_simple" VALUES(8,59,'8:59','ولا يحسبن الذين كفروا سبقوا انهم لا يعجزون');
INSERT INTO "imlaei_simple" VALUES(8,60,'8:60','واعدوا لهم ما استطعتم من قوة ومن رباط الخيل ترهبون به عدو الله وعدوكم واخرين من دونهم لا تعلمونهم الله يعلمهم وما تنفقوا من شيء في سبيل الله يوف اليكم وانتم لا تظلمون');
INSERT INTO "imlaei_simple" VALUES(8,61,'8:61','وان جنحوا للسلم فاجنح لها وتوكل على الله انه هو السميع العليم');
INSERT INTO "imlaei_simple" VALUES(8,62,'8:62','وان يريدوا ان يخدعوك فان حسبك الله هو الذي ايدك بنصره وبالمؤمنين');
INSERT INTO "imlaei_simple" VALUES(8,63,'8:63','والف بين قلوبهم لو انفقت ما في الارض جميعا ما الفت بين قلوبهم ولكن الله الف بينهم انه عزيز حكيم');
INSERT INTO "imlaei_simple" VALUES(8,64,'8:64','يا ايها النبي حسبك الله ومن اتبعك من المؤمنين');
INSERT INTO "imlaei_simple" VALUES(8,65,'8:65','يا ايها النبي حرض المؤمنين على القتال ان يكن منكم عشرون صابرون يغلبوا مائتين وان يكن منكم مائة يغلبوا الفا من الذين كفروا بانهم قوم لا يفقهون');
INSERT INTO "imlaei_simple" VALUES(8,66,'8:66','الان خفف الله عنكم وعلم ان فيكم ضعفا فان يكن منكم مائة صابرة يغلبوا مائتين وان يكن منكم الف يغلبوا الفين باذن الله والله مع الصابرين');
INSERT INTO "imlaei_simple" VALUES(8,67,'8:67','ما كان لنبي ان يكون له اسرى حتى يثخن في الارض تريدون عرض الدنيا والله يريد الاخرة والله عزيز حكيم');
INSERT INTO "imlaei_simple" VALUES(8,68,'8:68','لولا كتاب من الله سبق لمسكم فيما اخذتم عذاب عظيم');
INSERT INTO "imlaei_simple" VALUES(8,69,'8:69','فكلوا مما غنمتم حلالا طيبا واتقوا الله ان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(8,70,'8:70','يا ايها النبي قل لمن في ايديكم من الاسرى ان يعلم الله في قلوبكم خيرا يؤتكم خيرا مما اخذ منكم ويغفر لكم والله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(8,71,'8:71','وان يريدوا خيانتك فقد خانوا الله من قبل فامكن منهم والله عليم حكيم');
INSERT INTO "imlaei_simple" VALUES(8,72,'8:72','ان الذين امنوا وهاجروا وجاهدوا باموالهم وانفسهم في سبيل الله والذين اووا ونصروا اولئك بعضهم اولياء بعض والذين امنوا ولم يهاجروا ما لكم من ولايتهم من شيء حتى يهاجروا وان استنصروكم في الدين فعليكم النصر الا على قوم بينكم وبينهم ميثاق والله بما تعملون بصير');
INSERT INTO "imlaei_simple" VALUES(8,73,'8:73','والذين كفروا بعضهم اولياء بعض الا تفعلوه تكن فتنة في الارض وفساد كبير');
INSERT INTO "imlaei_simple" VALUES(8,74,'8:74','والذين امنوا وهاجروا وجاهدوا في سبيل الله والذين اووا ونصروا اولئك هم المؤمنون حقا لهم مغفرة ورزق كريم');
INSERT INTO "imlaei_simple" VALUES(8,75,'8:75','والذين امنوا من بعد وهاجروا وجاهدوا معكم فاولئك منكم واولو الارحام بعضهم اولى ببعض في كتاب الله ان الله بكل شيء عليم');
INSERT INTO "imlaei_simple" VALUES(9,1,'9:1','براءة من الله ورسوله الى الذين عاهدتم من المشركين');
INSERT INTO "imlaei_simple" VALUES(9,2,'9:2','فسيحوا في الارض اربعة اشهر واعلموا انكم غير معجزي الله وان الله مخزي الكافرين');
INSERT INTO "imlaei_simple" VALUES(9,3,'9:3','واذان من الله ورسوله الى الناس يوم الحج الاكبر ان الله بريء من المشركين ورسوله فان تبتم فهو خير لكم وان توليتم فاعلموا انكم غير معجزي الله وبشر الذين كفروا بعذاب اليم');
INSERT INTO "imlaei_simple" VALUES(9,4,'9:4','الا الذين عاهدتم من المشركين ثم لم ينقصوكم شيئا ولم يظاهروا عليكم احدا فاتموا اليهم عهدهم الى مدتهم ان الله يحب المتقين');
INSERT INTO "imlaei_simple" VALUES(9,5,'9:5','فاذا انسلخ الاشهر الحرم فاقتلوا المشركين حيث وجدتموهم وخذوهم واحصروهم واقعدوا لهم كل مرصد فان تابوا واقاموا الصلاة واتوا الزكاة فخلوا سبيلهم ان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(9,6,'9:6','وان احد من المشركين استجارك فاجره حتى يسمع كلام الله ثم ابلغه مامنه ذلك بانهم قوم لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(9,7,'9:7','كيف يكون للمشركين عهد عند الله وعند رسوله الا الذين عاهدتم عند المسجد الحرام فما استقاموا لكم فاستقيموا لهم ان الله يحب المتقين');
INSERT INTO "imlaei_simple" VALUES(9,8,'9:8','كيف وان يظهروا عليكم لا يرقبوا فيكم الا ولا ذمة يرضونكم بافواههم وتابى قلوبهم واكثرهم فاسقون');
INSERT INTO "imlaei_simple" VALUES(9,9,'9:9','اشتروا بايات الله ثمنا قليلا فصدوا عن سبيله انهم ساء ما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(9,10,'9:10','لا يرقبون في مؤمن الا ولا ذمة واولئك هم المعتدون');
INSERT INTO "imlaei_simple" VALUES(9,11,'9:11','فان تابوا واقاموا الصلاة واتوا الزكاة فاخوانكم في الدين ونفصل الايات لقوم يعلمون');
INSERT INTO "imlaei_simple" VALUES(9,12,'9:12','وان نكثوا ايمانهم من بعد عهدهم وطعنوا في دينكم فقاتلوا ائمة الكفر انهم لا ايمان لهم لعلهم ينتهون');
INSERT INTO "imlaei_simple" VALUES(9,13,'9:13','الا تقاتلون قوما نكثوا ايمانهم وهموا باخراج الرسول وهم بدءوكم اول مرة اتخشونهم فالله احق ان تخشوه ان كنتم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(9,14,'9:14','قاتلوهم يعذبهم الله بايديكم ويخزهم وينصركم عليهم ويشف صدور قوم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(9,15,'9:15','ويذهب غيظ قلوبهم ويتوب الله على من يشاء والله عليم حكيم');
INSERT INTO "imlaei_simple" VALUES(9,16,'9:16','ام حسبتم ان تتركوا ولما يعلم الله الذين جاهدوا منكم ولم يتخذوا من دون الله ولا رسوله ولا المؤمنين وليجة والله خبير بما تعملون');
INSERT INTO "imlaei_simple" VALUES(9,17,'9:17','ما كان للمشركين ان يعمروا مساجد الله شاهدين على انفسهم بالكفر اولئك حبطت اعمالهم وفي النار هم خالدون');
INSERT INTO "imlaei_simple" VALUES(9,18,'9:18','انما يعمر مساجد الله من امن بالله واليوم الاخر واقام الصلاة واتى الزكاة ولم يخش الا الله فعسى اولئك ان يكونوا من المهتدين');
INSERT INTO "imlaei_simple" VALUES(9,19,'9:19','اجعلتم سقاية الحاج وعمارة المسجد الحرام كمن امن بالله واليوم الاخر وجاهد في سبيل الله لا يستوون عند الله والله لا يهدي القوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(9,20,'9:20','الذين امنوا وهاجروا وجاهدوا في سبيل الله باموالهم وانفسهم اعظم درجة عند الله واولئك هم الفائزون');
INSERT INTO "imlaei_simple" VALUES(9,21,'9:21','يبشرهم ربهم برحمة منه ورضوان وجنات لهم فيها نعيم مقيم');
INSERT INTO "imlaei_simple" VALUES(9,22,'9:22','خالدين فيها ابدا ان الله عنده اجر عظيم');
INSERT INTO "imlaei_simple" VALUES(9,23,'9:23','يا ايها الذين امنوا لا تتخذوا اباءكم واخوانكم اولياء ان استحبوا الكفر على الايمان ومن يتولهم منكم فاولئك هم الظالمون');
INSERT INTO "imlaei_simple" VALUES(9,24,'9:24','قل ان كان اباؤكم وابناؤكم واخوانكم وازواجكم وعشيرتكم واموال اقترفتموها وتجارة تخشون كسادها ومساكن ترضونها احب اليكم من الله ورسوله وجهاد في سبيله فتربصوا حتى ياتي الله بامره والله لا يهدي القوم الفاسقين');
INSERT INTO "imlaei_simple" VALUES(9,25,'9:25','لقد نصركم الله في مواطن كثيرة ويوم حنين اذ اعجبتكم كثرتكم فلم تغن عنكم شيئا وضاقت عليكم الارض بما رحبت ثم وليتم مدبرين');
INSERT INTO "imlaei_simple" VALUES(9,26,'9:26','ثم انزل الله سكينته على رسوله وعلى المؤمنين وانزل جنودا لم تروها وعذب الذين كفروا وذلك جزاء الكافرين');
INSERT INTO "imlaei_simple" VALUES(9,27,'9:27','ثم يتوب الله من بعد ذلك على من يشاء والله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(9,28,'9:28','يا ايها الذين امنوا انما المشركون نجس فلا يقربوا المسجد الحرام بعد عامهم هذا وان خفتم عيلة فسوف يغنيكم الله من فضله ان شاء ان الله عليم حكيم');
INSERT INTO "imlaei_simple" VALUES(9,29,'9:29','قاتلوا الذين لا يؤمنون بالله ولا باليوم الاخر ولا يحرمون ما حرم الله ورسوله ولا يدينون دين الحق من الذين اوتوا الكتاب حتى يعطوا الجزية عن يد وهم صاغرون');
INSERT INTO "imlaei_simple" VALUES(9,30,'9:30','وقالت اليهود عزير ابن الله وقالت النصارى المسيح ابن الله ذلك قولهم بافواههم يضاهئون قول الذين كفروا من قبل قاتلهم الله انى يؤفكون');
INSERT INTO "imlaei_simple" VALUES(9,31,'9:31','اتخذوا احبارهم ورهبانهم اربابا من دون الله والمسيح ابن مريم وما امروا الا ليعبدوا الها واحدا لا اله الا هو سبحانه عما يشركون');
INSERT INTO "imlaei_simple" VALUES(9,32,'9:32','يريدون ان يطفئوا نور الله بافواههم ويابى الله الا ان يتم نوره ولو كره الكافرون');
INSERT INTO "imlaei_simple" VALUES(9,33,'9:33','هو الذي ارسل رسوله بالهدى ودين الحق ليظهره على الدين كله ولو كره المشركون');
INSERT INTO "imlaei_simple" VALUES(9,34,'9:34','يا ايها الذين امنوا ان كثيرا من الاحبار والرهبان لياكلون اموال الناس بالباطل ويصدون عن سبيل الله والذين يكنزون الذهب والفضة ولا ينفقونها في سبيل الله فبشرهم بعذاب اليم');
INSERT INTO "imlaei_simple" VALUES(9,35,'9:35','يوم يحمى عليها في نار جهنم فتكوى بها جباههم وجنوبهم وظهورهم هذا ما كنزتم لانفسكم فذوقوا ما كنتم تكنزون');
INSERT INTO "imlaei_simple" VALUES(9,36,'9:36','ان عدة الشهور عند الله اثنا عشر شهرا في كتاب الله يوم خلق السماوات والارض منها اربعة حرم ذلك الدين القيم فلا تظلموا فيهن انفسكم وقاتلوا المشركين كافة كما يقاتلونكم كافة واعلموا ان الله مع المتقين');
INSERT INTO "imlaei_simple" VALUES(9,37,'9:37','انما النسيء زيادة في الكفر يضل به الذين كفروا يحلونه عاما ويحرمونه عاما ليواطئوا عدة ما حرم الله فيحلوا ما حرم الله زين لهم سوء اعمالهم والله لا يهدي القوم الكافرين');
INSERT INTO "imlaei_simple" VALUES(9,38,'9:38','يا ايها الذين امنوا ما لكم اذا قيل لكم انفروا في سبيل الله اثاقلتم الى الارض ارضيتم بالحياة الدنيا من الاخرة فما متاع الحياة الدنيا في الاخرة الا قليل');
INSERT INTO "imlaei_simple" VALUES(9,39,'9:39','الا تنفروا يعذبكم عذابا اليما ويستبدل قوما غيركم ولا تضروه شيئا والله على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(9,40,'9:40','الا تنصروه فقد نصره الله اذ اخرجه الذين كفروا ثاني اثنين اذ هما في الغار اذ يقول لصاحبه لا تحزن ان الله معنا فانزل الله سكينته عليه وايده بجنود لم تروها وجعل كلمة الذين كفروا السفلى وكلمة الله هي العليا والله عزيز حكيم');
INSERT INTO "imlaei_simple" VALUES(9,41,'9:41','انفروا خفافا وثقالا وجاهدوا باموالكم وانفسكم في سبيل الله ذلكم خير لكم ان كنتم تعلمون');
INSERT INTO "imlaei_simple" VALUES(9,42,'9:42','لو كان عرضا قريبا وسفرا قاصدا لاتبعوك ولكن بعدت عليهم الشقة وسيحلفون بالله لو استطعنا لخرجنا معكم يهلكون انفسهم والله يعلم انهم لكاذبون');
INSERT INTO "imlaei_simple" VALUES(9,43,'9:43','عفا الله عنك لم اذنت لهم حتى يتبين لك الذين صدقوا وتعلم الكاذبين');
INSERT INTO "imlaei_simple" VALUES(9,44,'9:44','لا يستاذنك الذين يؤمنون بالله واليوم الاخر ان يجاهدوا باموالهم وانفسهم والله عليم بالمتقين');
INSERT INTO "imlaei_simple" VALUES(9,45,'9:45','انما يستاذنك الذين لا يؤمنون بالله واليوم الاخر وارتابت قلوبهم فهم في ريبهم يترددون');
INSERT INTO "imlaei_simple" VALUES(9,46,'9:46','ولو ارادوا الخروج لاعدوا له عدة ولكن كره الله انبعاثهم فثبطهم وقيل اقعدوا مع القاعدين');
INSERT INTO "imlaei_simple" VALUES(9,47,'9:47','لو خرجوا فيكم ما زادوكم الا خبالا ولاوضعوا خلالكم يبغونكم الفتنة وفيكم سماعون لهم والله عليم بالظالمين');
INSERT INTO "imlaei_simple" VALUES(9,48,'9:48','لقد ابتغوا الفتنة من قبل وقلبوا لك الامور حتى جاء الحق وظهر امر الله وهم كارهون');
INSERT INTO "imlaei_simple" VALUES(9,49,'9:49','ومنهم من يقول ائذن لي ولا تفتني الا في الفتنة سقطوا وان جهنم لمحيطة بالكافرين');
INSERT INTO "imlaei_simple" VALUES(9,50,'9:50','ان تصبك حسنة تسؤهم وان تصبك مصيبة يقولوا قد اخذنا امرنا من قبل ويتولوا وهم فرحون');
INSERT INTO "imlaei_simple" VALUES(9,51,'9:51','قل لن يصيبنا الا ما كتب الله لنا هو مولانا وعلى الله فليتوكل المؤمنون');
INSERT INTO "imlaei_simple" VALUES(9,52,'9:52','قل هل تربصون بنا الا احدى الحسنيين ونحن نتربص بكم ان يصيبكم الله بعذاب من عنده او بايدينا فتربصوا انا معكم متربصون');
INSERT INTO "imlaei_simple" VALUES(9,53,'9:53','قل انفقوا طوعا او كرها لن يتقبل منكم انكم كنتم قوما فاسقين');
INSERT INTO "imlaei_simple" VALUES(9,54,'9:54','وما منعهم ان تقبل منهم نفقاتهم الا انهم كفروا بالله وبرسوله ولا ياتون الصلاة الا وهم كسالى ولا ينفقون الا وهم كارهون');
INSERT INTO "imlaei_simple" VALUES(9,55,'9:55','فلا تعجبك اموالهم ولا اولادهم انما يريد الله ليعذبهم بها في الحياة الدنيا وتزهق انفسهم وهم كافرون');
INSERT INTO "imlaei_simple" VALUES(9,56,'9:56','ويحلفون بالله انهم لمنكم وما هم منكم ولكنهم قوم يفرقون');
INSERT INTO "imlaei_simple" VALUES(9,57,'9:57','لو يجدون ملجا او مغارات او مدخلا لولوا اليه وهم يجمحون');
INSERT INTO "imlaei_simple" VALUES(9,58,'9:58','ومنهم من يلمزك في الصدقات فان اعطوا منها رضوا وان لم يعطوا منها اذا هم يسخطون');
INSERT INTO "imlaei_simple" VALUES(9,59,'9:59','ولو انهم رضوا ما اتاهم الله ورسوله وقالوا حسبنا الله سيؤتينا الله من فضله ورسوله انا الى الله راغبون');
INSERT INTO "imlaei_simple" VALUES(9,60,'9:60','انما الصدقات للفقراء والمساكين والعاملين عليها والمؤلفة قلوبهم وفي الرقاب والغارمين وفي سبيل الله وابن السبيل فريضة من الله والله عليم حكيم');
INSERT INTO "imlaei_simple" VALUES(9,61,'9:61','ومنهم الذين يؤذون النبي ويقولون هو اذن قل اذن خير لكم يؤمن بالله ويؤمن للمؤمنين ورحمة للذين امنوا منكم والذين يؤذون رسول الله لهم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(9,62,'9:62','يحلفون بالله لكم ليرضوكم والله ورسوله احق ان يرضوه ان كانوا مؤمنين');
INSERT INTO "imlaei_simple" VALUES(9,63,'9:63','الم يعلموا انه من يحادد الله ورسوله فان له نار جهنم خالدا فيها ذلك الخزي العظيم');
INSERT INTO "imlaei_simple" VALUES(9,64,'9:64','يحذر المنافقون ان تنزل عليهم سورة تنبئهم بما في قلوبهم قل استهزئوا ان الله مخرج ما تحذرون');
INSERT INTO "imlaei_simple" VALUES(9,65,'9:65','ولئن سالتهم ليقولن انما كنا نخوض ونلعب قل ابالله واياته ورسوله كنتم تستهزئون');
INSERT INTO "imlaei_simple" VALUES(9,66,'9:66','لا تعتذروا قد كفرتم بعد ايمانكم ان نعف عن طائفة منكم نعذب طائفة بانهم كانوا مجرمين');
INSERT INTO "imlaei_simple" VALUES(9,67,'9:67','المنافقون والمنافقات بعضهم من بعض يامرون بالمنكر وينهون عن المعروف ويقبضون ايديهم نسوا الله فنسيهم ان المنافقين هم الفاسقون');
INSERT INTO "imlaei_simple" VALUES(9,68,'9:68','وعد الله المنافقين والمنافقات والكفار نار جهنم خالدين فيها هي حسبهم ولعنهم الله ولهم عذاب مقيم');
INSERT INTO "imlaei_simple" VALUES(9,69,'9:69','كالذين من قبلكم كانوا اشد منكم قوة واكثر اموالا واولادا فاستمتعوا بخلاقهم فاستمتعتم بخلاقكم كما استمتع الذين من قبلكم بخلاقهم وخضتم كالذي خاضوا اولئك حبطت اعمالهم في الدنيا والاخرة واولئك هم الخاسرون');
INSERT INTO "imlaei_simple" VALUES(9,70,'9:70','الم ياتهم نبا الذين من قبلهم قوم نوح وعاد وثمود وقوم ابراهيم واصحاب مدين والمؤتفكات اتتهم رسلهم بالبينات فما كان الله ليظلمهم ولكن كانوا انفسهم يظلمون');
INSERT INTO "imlaei_simple" VALUES(9,71,'9:71','والمؤمنون والمؤمنات بعضهم اولياء بعض يامرون بالمعروف وينهون عن المنكر ويقيمون الصلاة ويؤتون الزكاة ويطيعون الله ورسوله اولئك سيرحمهم الله ان الله عزيز حكيم');
INSERT INTO "imlaei_simple" VALUES(9,72,'9:72','وعد الله المؤمنين والمؤمنات جنات تجري من تحتها الانهار خالدين فيها ومساكن طيبة في جنات عدن ورضوان من الله اكبر ذلك هو الفوز العظيم');
INSERT INTO "imlaei_simple" VALUES(9,73,'9:73','يا ايها النبي جاهد الكفار والمنافقين واغلظ عليهم وماواهم جهنم وبئس المصير');
INSERT INTO "imlaei_simple" VALUES(9,74,'9:74','يحلفون بالله ما قالوا ولقد قالوا كلمة الكفر وكفروا بعد اسلامهم وهموا بما لم ينالوا وما نقموا الا ان اغناهم الله ورسوله من فضله فان يتوبوا يك خيرا لهم وان يتولوا يعذبهم الله عذابا اليما في الدنيا والاخرة وما لهم في الارض من ولي ولا نصير');
INSERT INTO "imlaei_simple" VALUES(9,75,'9:75','ومنهم من عاهد الله لئن اتانا من فضله لنصدقن ولنكونن من الصالحين');
INSERT INTO "imlaei_simple" VALUES(9,76,'9:76','فلما اتاهم من فضله بخلوا به وتولوا وهم معرضون');
INSERT INTO "imlaei_simple" VALUES(9,77,'9:77','فاعقبهم نفاقا في قلوبهم الى يوم يلقونه بما اخلفوا الله ما وعدوه وبما كانوا يكذبون');
INSERT INTO "imlaei_simple" VALUES(9,78,'9:78','الم يعلموا ان الله يعلم سرهم ونجواهم وان الله علام الغيوب');
INSERT INTO "imlaei_simple" VALUES(9,79,'9:79','الذين يلمزون المطوعين من المؤمنين في الصدقات والذين لا يجدون الا جهدهم فيسخرون منهم سخر الله منهم ولهم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(9,80,'9:80','استغفر لهم او لا تستغفر لهم ان تستغفر لهم سبعين مرة فلن يغفر الله لهم ذلك بانهم كفروا بالله ورسوله والله لا يهدي القوم الفاسقين');
INSERT INTO "imlaei_simple" VALUES(9,81,'9:81','فرح المخلفون بمقعدهم خلاف رسول الله وكرهوا ان يجاهدوا باموالهم وانفسهم في سبيل الله وقالوا لا تنفروا في الحر قل نار جهنم اشد حرا لو كانوا يفقهون');
INSERT INTO "imlaei_simple" VALUES(9,82,'9:82','فليضحكوا قليلا وليبكوا كثيرا جزاء بما كانوا يكسبون');
INSERT INTO "imlaei_simple" VALUES(9,83,'9:83','فان رجعك الله الى طائفة منهم فاستاذنوك للخروج فقل لن تخرجوا معي ابدا ولن تقاتلوا معي عدوا انكم رضيتم بالقعود اول مرة فاقعدوا مع الخالفين');
INSERT INTO "imlaei_simple" VALUES(9,84,'9:84','ولا تصل على احد منهم مات ابدا ولا تقم على قبره انهم كفروا بالله ورسوله وماتوا وهم فاسقون');
INSERT INTO "imlaei_simple" VALUES(9,85,'9:85','ولا تعجبك اموالهم واولادهم انما يريد الله ان يعذبهم بها في الدنيا وتزهق انفسهم وهم كافرون');
INSERT INTO "imlaei_simple" VALUES(9,86,'9:86','واذا انزلت سورة ان امنوا بالله وجاهدوا مع رسوله استاذنك اولو الطول منهم وقالوا ذرنا نكن مع القاعدين');
INSERT INTO "imlaei_simple" VALUES(9,87,'9:87','رضوا بان يكونوا مع الخوالف وطبع على قلوبهم فهم لا يفقهون');
INSERT INTO "imlaei_simple" VALUES(9,88,'9:88','لكن الرسول والذين امنوا معه جاهدوا باموالهم وانفسهم واولئك لهم الخيرات واولئك هم المفلحون');
INSERT INTO "imlaei_simple" VALUES(9,89,'9:89','اعد الله لهم جنات تجري من تحتها الانهار خالدين فيها ذلك الفوز العظيم');
INSERT INTO "imlaei_simple" VALUES(9,90,'9:90','وجاء المعذرون من الاعراب ليؤذن لهم وقعد الذين كذبوا الله ورسوله سيصيب الذين كفروا منهم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(9,91,'9:91','ليس على الضعفاء ولا على المرضى ولا على الذين لا يجدون ما ينفقون حرج اذا نصحوا لله ورسوله ما على المحسنين من سبيل والله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(9,92,'9:92','ولا على الذين اذا ما اتوك لتحملهم قلت لا اجد ما احملكم عليه تولوا واعينهم تفيض من الدمع حزنا الا يجدوا ما ينفقون');
INSERT INTO "imlaei_simple" VALUES(9,93,'9:93','انما السبيل على الذين يستاذنونك وهم اغنياء رضوا بان يكونوا مع الخوالف وطبع الله على قلوبهم فهم لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(9,94,'9:94','يعتذرون اليكم اذا رجعتم اليهم قل لا تعتذروا لن نؤمن لكم قد نبانا الله من اخباركم وسيرى الله عملكم ورسوله ثم تردون الى عالم الغيب والشهادة فينبئكم بما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(9,95,'9:95','سيحلفون بالله لكم اذا انقلبتم اليهم لتعرضوا عنهم فاعرضوا عنهم انهم رجس وماواهم جهنم جزاء بما كانوا يكسبون');
INSERT INTO "imlaei_simple" VALUES(9,96,'9:96','يحلفون لكم لترضوا عنهم فان ترضوا عنهم فان الله لا يرضى عن القوم الفاسقين');
INSERT INTO "imlaei_simple" VALUES(9,97,'9:97','الاعراب اشد كفرا ونفاقا واجدر الا يعلموا حدود ما انزل الله على رسوله والله عليم حكيم');
INSERT INTO "imlaei_simple" VALUES(9,98,'9:98','ومن الاعراب من يتخذ ما ينفق مغرما ويتربص بكم الدوائر عليهم دائرة السوء والله سميع عليم');
INSERT INTO "imlaei_simple" VALUES(9,99,'9:99','ومن الاعراب من يؤمن بالله واليوم الاخر ويتخذ ما ينفق قربات عند الله وصلوات الرسول الا انها قربة لهم سيدخلهم الله في رحمته ان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(9,100,'9:100','والسابقون الاولون من المهاجرين والانصار والذين اتبعوهم باحسان رضي الله عنهم ورضوا عنه واعد لهم جنات تجري تحتها الانهار خالدين فيها ابدا ذلك الفوز العظيم');
INSERT INTO "imlaei_simple" VALUES(9,101,'9:101','وممن حولكم من الاعراب منافقون ومن اهل المدينة مردوا على النفاق لا تعلمهم نحن نعلمهم سنعذبهم مرتين ثم يردون الى عذاب عظيم');
INSERT INTO "imlaei_simple" VALUES(9,102,'9:102','واخرون اعترفوا بذنوبهم خلطوا عملا صالحا واخر سيئا عسى الله ان يتوب عليهم ان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(9,103,'9:103','خذ من اموالهم صدقة تطهرهم وتزكيهم بها وصل عليهم ان صلاتك سكن لهم والله سميع عليم');
INSERT INTO "imlaei_simple" VALUES(9,104,'9:104','الم يعلموا ان الله هو يقبل التوبة عن عباده وياخذ الصدقات وان الله هو التواب الرحيم');
INSERT INTO "imlaei_simple" VALUES(9,105,'9:105','وقل اعملوا فسيرى الله عملكم ورسوله والمؤمنون وستردون الى عالم الغيب والشهادة فينبئكم بما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(9,106,'9:106','واخرون مرجون لامر الله اما يعذبهم واما يتوب عليهم والله عليم حكيم');
INSERT INTO "imlaei_simple" VALUES(9,107,'9:107','والذين اتخذوا مسجدا ضرارا وكفرا وتفريقا بين المؤمنين وارصادا لمن حارب الله ورسوله من قبل وليحلفن ان اردنا الا الحسنى والله يشهد انهم لكاذبون');
INSERT INTO "imlaei_simple" VALUES(9,108,'9:108','لا تقم فيه ابدا لمسجد اسس على التقوى من اول يوم احق ان تقوم فيه فيه رجال يحبون ان يتطهروا والله يحب المطهرين');
INSERT INTO "imlaei_simple" VALUES(9,109,'9:109','افمن اسس بنيانه على تقوى من الله ورضوان خير ام من اسس بنيانه على شفا جرف هار فانهار به في نار جهنم والله لا يهدي القوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(9,110,'9:110','لا يزال بنيانهم الذي بنوا ريبة في قلوبهم الا ان تقطع قلوبهم والله عليم حكيم');
INSERT INTO "imlaei_simple" VALUES(9,111,'9:111','ان الله اشترى من المؤمنين انفسهم واموالهم بان لهم الجنة يقاتلون في سبيل الله فيقتلون ويقتلون وعدا عليه حقا في التوراة والانجيل والقران ومن اوفى بعهده من الله فاستبشروا ببيعكم الذي بايعتم به وذلك هو الفوز العظيم');
INSERT INTO "imlaei_simple" VALUES(9,112,'9:112','التائبون العابدون الحامدون السائحون الراكعون الساجدون الامرون بالمعروف والناهون عن المنكر والحافظون لحدود الله وبشر المؤمنين');
INSERT INTO "imlaei_simple" VALUES(9,113,'9:113','ما كان للنبي والذين امنوا ان يستغفروا للمشركين ولو كانوا اولي قربى من بعد ما تبين لهم انهم اصحاب الجحيم');
INSERT INTO "imlaei_simple" VALUES(9,114,'9:114','وما كان استغفار ابراهيم لابيه الا عن موعدة وعدها اياه فلما تبين له انه عدو لله تبرا منه ان ابراهيم لاواه حليم');
INSERT INTO "imlaei_simple" VALUES(9,115,'9:115','وما كان الله ليضل قوما بعد اذ هداهم حتى يبين لهم ما يتقون ان الله بكل شيء عليم');
INSERT INTO "imlaei_simple" VALUES(9,116,'9:116','ان الله له ملك السماوات والارض يحيي ويميت وما لكم من دون الله من ولي ولا نصير');
INSERT INTO "imlaei_simple" VALUES(9,117,'9:117','لقد تاب الله على النبي والمهاجرين والانصار الذين اتبعوه في ساعة العسرة من بعد ما كاد يزيغ قلوب فريق منهم ثم تاب عليهم انه بهم رءوف رحيم');
INSERT INTO "imlaei_simple" VALUES(9,118,'9:118','وعلى الثلاثة الذين خلفوا حتى اذا ضاقت عليهم الارض بما رحبت وضاقت عليهم انفسهم وظنوا ان لا ملجا من الله الا اليه ثم تاب عليهم ليتوبوا ان الله هو التواب الرحيم');
INSERT INTO "imlaei_simple" VALUES(9,119,'9:119','يا ايها الذين امنوا اتقوا الله وكونوا مع الصادقين');
INSERT INTO "imlaei_simple" VALUES(9,120,'9:120','ما كان لاهل المدينة ومن حولهم من الاعراب ان يتخلفوا عن رسول الله ولا يرغبوا بانفسهم عن نفسه ذلك بانهم لا يصيبهم ظما ولا نصب ولا مخمصة في سبيل الله ولا يطئون موطئا يغيظ الكفار ولا ينالون من عدو نيلا الا كتب لهم به عمل صالح ان الله لا يضيع اجر المحسنين');
INSERT INTO "imlaei_simple" VALUES(9,121,'9:121','ولا ينفقون نفقة صغيرة ولا كبيرة ولا يقطعون واديا الا كتب لهم ليجزيهم الله احسن ما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(9,122,'9:122','وما كان المؤمنون لينفروا كافة فلولا نفر من كل فرقة منهم طائفة ليتفقهوا في الدين ولينذروا قومهم اذا رجعوا اليهم لعلهم يحذرون');
INSERT INTO "imlaei_simple" VALUES(9,123,'9:123','يا ايها الذين امنوا قاتلوا الذين يلونكم من الكفار وليجدوا فيكم غلظة واعلموا ان الله مع المتقين');
INSERT INTO "imlaei_simple" VALUES(9,124,'9:124','واذا ما انزلت سورة فمنهم من يقول ايكم زادته هذه ايمانا فاما الذين امنوا فزادتهم ايمانا وهم يستبشرون');
INSERT INTO "imlaei_simple" VALUES(9,125,'9:125','واما الذين في قلوبهم مرض فزادتهم رجسا الى رجسهم وماتوا وهم كافرون');
INSERT INTO "imlaei_simple" VALUES(9,126,'9:126','اولا يرون انهم يفتنون في كل عام مرة او مرتين ثم لا يتوبون ولا هم يذكرون');
INSERT INTO "imlaei_simple" VALUES(9,127,'9:127','واذا ما انزلت سورة نظر بعضهم الى بعض هل يراكم من احد ثم انصرفوا صرف الله قلوبهم بانهم قوم لا يفقهون');
INSERT INTO "imlaei_simple" VALUES(9,128,'9:128','لقد جاءكم رسول من انفسكم عزيز عليه ما عنتم حريص عليكم بالمؤمنين رءوف رحيم');
INSERT INTO "imlaei_simple" VALUES(9,129,'9:129','فان تولوا فقل حسبي الله لا اله الا هو عليه توكلت وهو رب العرش العظيم');
INSERT INTO "imlaei_simple" VALUES(10,1,'10:1','الر تلك ايات الكتاب الحكيم');
INSERT INTO "imlaei_simple" VALUES(10,2,'10:2','اكان للناس عجبا ان اوحينا الى رجل منهم ان انذر الناس وبشر الذين امنوا ان لهم قدم صدق عند ربهم قال الكافرون ان هذا لساحر مبين');
INSERT INTO "imlaei_simple" VALUES(10,3,'10:3','ان ربكم الله الذي خلق السماوات والارض في ستة ايام ثم استوى على العرش يدبر الامر ما من شفيع الا من بعد اذنه ذلكم الله ربكم فاعبدوه افلا تذكرون');
INSERT INTO "imlaei_simple" VALUES(10,4,'10:4','اليه مرجعكم جميعا وعد الله حقا انه يبدا الخلق ثم يعيده ليجزي الذين امنوا وعملوا الصالحات بالقسط والذين كفروا لهم شراب من حميم وعذاب اليم بما كانوا يكفرون');
INSERT INTO "imlaei_simple" VALUES(10,5,'10:5','هو الذي جعل الشمس ضياء والقمر نورا وقدره منازل لتعلموا عدد السنين والحساب ما خلق الله ذلك الا بالحق يفصل الايات لقوم يعلمون');
INSERT INTO "imlaei_simple" VALUES(10,6,'10:6','ان في اختلاف الليل والنهار وما خلق الله في السماوات والارض لايات لقوم يتقون');
INSERT INTO "imlaei_simple" VALUES(10,7,'10:7','ان الذين لا يرجون لقاءنا ورضوا بالحياة الدنيا واطمانوا بها والذين هم عن اياتنا غافلون');
INSERT INTO "imlaei_simple" VALUES(10,8,'10:8','اولئك ماواهم النار بما كانوا يكسبون');
INSERT INTO "imlaei_simple" VALUES(10,9,'10:9','ان الذين امنوا وعملوا الصالحات يهديهم ربهم بايمانهم تجري من تحتهم الانهار في جنات النعيم');
INSERT INTO "imlaei_simple" VALUES(10,10,'10:10','دعواهم فيها سبحانك اللهم وتحيتهم فيها سلام واخر دعواهم ان الحمد لله رب العالمين');
INSERT INTO "imlaei_simple" VALUES(10,11,'10:11','ولو يعجل الله للناس الشر استعجالهم بالخير لقضي اليهم اجلهم فنذر الذين لا يرجون لقاءنا في طغيانهم يعمهون');
INSERT INTO "imlaei_simple" VALUES(10,12,'10:12','واذا مس الانسان الضر دعانا لجنبه او قاعدا او قائما فلما كشفنا عنه ضره مر كان لم يدعنا الى ضر مسه كذلك زين للمسرفين ما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(10,13,'10:13','ولقد اهلكنا القرون من قبلكم لما ظلموا وجاءتهم رسلهم بالبينات وما كانوا ليؤمنوا كذلك نجزي القوم المجرمين');
INSERT INTO "imlaei_simple" VALUES(10,14,'10:14','ثم جعلناكم خلائف في الارض من بعدهم لننظر كيف تعملون');
INSERT INTO "imlaei_simple" VALUES(10,15,'10:15','واذا تتلى عليهم اياتنا بينات قال الذين لا يرجون لقاءنا ائت بقران غير هذا او بدله قل ما يكون لي ان ابدله من تلقاء نفسي ان اتبع الا ما يوحى الي اني اخاف ان عصيت ربي عذاب يوم عظيم');
INSERT INTO "imlaei_simple" VALUES(10,16,'10:16','قل لو شاء الله ما تلوته عليكم ولا ادراكم به فقد لبثت فيكم عمرا من قبله افلا تعقلون');
INSERT INTO "imlaei_simple" VALUES(10,17,'10:17','فمن اظلم ممن افترى على الله كذبا او كذب باياته انه لا يفلح المجرمون');
INSERT INTO "imlaei_simple" VALUES(10,18,'10:18','ويعبدون من دون الله ما لا يضرهم ولا ينفعهم ويقولون هؤلاء شفعاؤنا عند الله قل اتنبئون الله بما لا يعلم في السماوات ولا في الارض سبحانه وتعالى عما يشركون');
INSERT INTO "imlaei_simple" VALUES(10,19,'10:19','وما كان الناس الا امة واحدة فاختلفوا ولولا كلمة سبقت من ربك لقضي بينهم فيما فيه يختلفون');
INSERT INTO "imlaei_simple" VALUES(10,20,'10:20','ويقولون لولا انزل عليه اية من ربه فقل انما الغيب لله فانتظروا اني معكم من المنتظرين');
INSERT INTO "imlaei_simple" VALUES(10,21,'10:21','واذا اذقنا الناس رحمة من بعد ضراء مستهم اذا لهم مكر في اياتنا قل الله اسرع مكرا ان رسلنا يكتبون ما تمكرون');
INSERT INTO "imlaei_simple" VALUES(10,22,'10:22','هو الذي يسيركم في البر والبحر حتى اذا كنتم في الفلك وجرين بهم بريح طيبة وفرحوا بها جاءتها ريح عاصف وجاءهم الموج من كل مكان وظنوا انهم احيط بهم دعوا الله مخلصين له الدين لئن انجيتنا من هذه لنكونن من الشاكرين');
INSERT INTO "imlaei_simple" VALUES(10,23,'10:23','فلما انجاهم اذا هم يبغون في الارض بغير الحق يا ايها الناس انما بغيكم على انفسكم متاع الحياة الدنيا ثم الينا مرجعكم فننبئكم بما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(10,24,'10:24','انما مثل الحياة الدنيا كماء انزلناه من السماء فاختلط به نبات الارض مما ياكل الناس والانعام حتى اذا اخذت الارض زخرفها وازينت وظن اهلها انهم قادرون عليها اتاها امرنا ليلا او نهارا فجعلناها حصيدا كان لم تغن بالامس كذلك نفصل الايات لقوم يتفكرون');
INSERT INTO "imlaei_simple" VALUES(10,25,'10:25','والله يدعو الى دار السلام ويهدي من يشاء الى صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(10,26,'10:26','للذين احسنوا الحسنى وزيادة ولا يرهق وجوههم قتر ولا ذلة اولئك اصحاب الجنة هم فيها خالدون');
INSERT INTO "imlaei_simple" VALUES(10,27,'10:27','والذين كسبوا السيئات جزاء سيئة بمثلها وترهقهم ذلة ما لهم من الله من عاصم كانما اغشيت وجوههم قطعا من الليل مظلما اولئك اصحاب النار هم فيها خالدون');
INSERT INTO "imlaei_simple" VALUES(10,28,'10:28','ويوم نحشرهم جميعا ثم نقول للذين اشركوا مكانكم انتم وشركاؤكم فزيلنا بينهم وقال شركاؤهم ما كنتم ايانا تعبدون');
INSERT INTO "imlaei_simple" VALUES(10,29,'10:29','فكفى بالله شهيدا بيننا وبينكم ان كنا عن عبادتكم لغافلين');
INSERT INTO "imlaei_simple" VALUES(10,30,'10:30','هنالك تبلو كل نفس ما اسلفت وردوا الى الله مولاهم الحق وضل عنهم ما كانوا يفترون');
INSERT INTO "imlaei_simple" VALUES(10,31,'10:31','قل من يرزقكم من السماء والارض امن يملك السمع والابصار ومن يخرج الحي من الميت ويخرج الميت من الحي ومن يدبر الامر فسيقولون الله فقل افلا تتقون');
INSERT INTO "imlaei_simple" VALUES(10,32,'10:32','فذلكم الله ربكم الحق فماذا بعد الحق الا الضلال فانى تصرفون');
INSERT INTO "imlaei_simple" VALUES(10,33,'10:33','كذلك حقت كلمت ربك على الذين فسقوا انهم لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(10,34,'10:34','قل هل من شركائكم من يبدا الخلق ثم يعيده قل الله يبدا الخلق ثم يعيده فانى تؤفكون');
INSERT INTO "imlaei_simple" VALUES(10,35,'10:35','قل هل من شركائكم من يهدي الى الحق قل الله يهدي للحق افمن يهدي الى الحق احق ان يتبع امن لا يهدي الا ان يهدى فما لكم كيف تحكمون');
INSERT INTO "imlaei_simple" VALUES(10,36,'10:36','وما يتبع اكثرهم الا ظنا ان الظن لا يغني من الحق شيئا ان الله عليم بما يفعلون');
INSERT INTO "imlaei_simple" VALUES(10,37,'10:37','وما كان هذا القران ان يفترى من دون الله ولكن تصديق الذي بين يديه وتفصيل الكتاب لا ريب فيه من رب العالمين');
INSERT INTO "imlaei_simple" VALUES(10,38,'10:38','ام يقولون افتراه قل فاتوا بسورة مثله وادعوا من استطعتم من دون الله ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(10,39,'10:39','بل كذبوا بما لم يحيطوا بعلمه ولما ياتهم تاويله كذلك كذب الذين من قبلهم فانظر كيف كان عاقبة الظالمين');
INSERT INTO "imlaei_simple" VALUES(10,40,'10:40','ومنهم من يؤمن به ومنهم من لا يؤمن به وربك اعلم بالمفسدين');
INSERT INTO "imlaei_simple" VALUES(10,41,'10:41','وان كذبوك فقل لي عملي ولكم عملكم انتم بريئون مما اعمل وانا بريء مما تعملون');
INSERT INTO "imlaei_simple" VALUES(10,42,'10:42','ومنهم من يستمعون اليك افانت تسمع الصم ولو كانوا لا يعقلون');
INSERT INTO "imlaei_simple" VALUES(10,43,'10:43','ومنهم من ينظر اليك افانت تهدي العمي ولو كانوا لا يبصرون');
INSERT INTO "imlaei_simple" VALUES(10,44,'10:44','ان الله لا يظلم الناس شيئا ولكن الناس انفسهم يظلمون');
INSERT INTO "imlaei_simple" VALUES(10,45,'10:45','ويوم يحشرهم كان لم يلبثوا الا ساعة من النهار يتعارفون بينهم قد خسر الذين كذبوا بلقاء الله وما كانوا مهتدين');
INSERT INTO "imlaei_simple" VALUES(10,46,'10:46','واما نرينك بعض الذي نعدهم او نتوفينك فالينا مرجعهم ثم الله شهيد على ما يفعلون');
INSERT INTO "imlaei_simple" VALUES(10,47,'10:47','ولكل امة رسول فاذا جاء رسولهم قضي بينهم بالقسط وهم لا يظلمون');
INSERT INTO "imlaei_simple" VALUES(10,48,'10:48','ويقولون متى هذا الوعد ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(10,49,'10:49','قل لا املك لنفسي ضرا ولا نفعا الا ما شاء الله لكل امة اجل اذا جاء اجلهم فلا يستاخرون ساعة ولا يستقدمون');
INSERT INTO "imlaei_simple" VALUES(10,50,'10:50','قل ارايتم ان اتاكم عذابه بياتا او نهارا ماذا يستعجل منه المجرمون');
INSERT INTO "imlaei_simple" VALUES(10,51,'10:51','اثم اذا ما وقع امنتم به الان وقد كنتم به تستعجلون');
INSERT INTO "imlaei_simple" VALUES(10,52,'10:52','ثم قيل للذين ظلموا ذوقوا عذاب الخلد هل تجزون الا بما كنتم تكسبون');
INSERT INTO "imlaei_simple" VALUES(10,53,'10:53','ويستنبئونك احق هو قل اي وربي انه لحق وما انتم بمعجزين');
INSERT INTO "imlaei_simple" VALUES(10,54,'10:54','ولو ان لكل نفس ظلمت ما في الارض لافتدت به واسروا الندامة لما راوا العذاب وقضي بينهم بالقسط وهم لا يظلمون');
INSERT INTO "imlaei_simple" VALUES(10,55,'10:55','الا ان لله ما في السماوات والارض الا ان وعد الله حق ولكن اكثرهم لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(10,56,'10:56','هو يحيي ويميت واليه ترجعون');
INSERT INTO "imlaei_simple" VALUES(10,57,'10:57','يا ايها الناس قد جاءتكم موعظة من ربكم وشفاء لما في الصدور وهدى ورحمة للمؤمنين');
INSERT INTO "imlaei_simple" VALUES(10,58,'10:58','قل بفضل الله وبرحمته فبذلك فليفرحوا هو خير مما يجمعون');
INSERT INTO "imlaei_simple" VALUES(10,59,'10:59','قل ارايتم ما انزل الله لكم من رزق فجعلتم منه حراما وحلالا قل الله اذن لكم ام على الله تفترون');
INSERT INTO "imlaei_simple" VALUES(10,60,'10:60','وما ظن الذين يفترون على الله الكذب يوم القيامة ان الله لذو فضل على الناس ولكن اكثرهم لا يشكرون');
INSERT INTO "imlaei_simple" VALUES(10,61,'10:61','وما تكون في شان وما تتلو منه من قران ولا تعملون من عمل الا كنا عليكم شهودا اذ تفيضون فيه وما يعزب عن ربك من مثقال ذرة في الارض ولا في السماء ولا اصغر من ذلك ولا اكبر الا في كتاب مبين');
INSERT INTO "imlaei_simple" VALUES(10,62,'10:62','الا ان اولياء الله لا خوف عليهم ولا هم يحزنون');
INSERT INTO "imlaei_simple" VALUES(10,63,'10:63','الذين امنوا وكانوا يتقون');
INSERT INTO "imlaei_simple" VALUES(10,64,'10:64','لهم البشرى في الحياة الدنيا وفي الاخرة لا تبديل لكلمات الله ذلك هو الفوز العظيم');
INSERT INTO "imlaei_simple" VALUES(10,65,'10:65','ولا يحزنك قولهم ان العزة لله جميعا هو السميع العليم');
INSERT INTO "imlaei_simple" VALUES(10,66,'10:66','الا ان لله من في السماوات ومن في الارض وما يتبع الذين يدعون من دون الله شركاء ان يتبعون الا الظن وان هم الا يخرصون');
INSERT INTO "imlaei_simple" VALUES(10,67,'10:67','هو الذي جعل لكم الليل لتسكنوا فيه والنهار مبصرا ان في ذلك لايات لقوم يسمعون');
INSERT INTO "imlaei_simple" VALUES(10,68,'10:68','قالوا اتخذ الله ولدا سبحانه هو الغني له ما في السماوات وما في الارض ان عندكم من سلطان بهذا اتقولون على الله ما لا تعلمون');
INSERT INTO "imlaei_simple" VALUES(10,69,'10:69','قل ان الذين يفترون على الله الكذب لا يفلحون');
INSERT INTO "imlaei_simple" VALUES(10,70,'10:70','متاع في الدنيا ثم الينا مرجعهم ثم نذيقهم العذاب الشديد بما كانوا يكفرون');
INSERT INTO "imlaei_simple" VALUES(10,71,'10:71','واتل عليهم نبا نوح اذ قال لقومه يا قوم ان كان كبر عليكم مقامي وتذكيري بايات الله فعلى الله توكلت فاجمعوا امركم وشركاءكم ثم لا يكن امركم عليكم غمة ثم اقضوا الي ولا تنظرون');
INSERT INTO "imlaei_simple" VALUES(10,72,'10:72','فان توليتم فما سالتكم من اجر ان اجري الا على الله وامرت ان اكون من المسلمين');
INSERT INTO "imlaei_simple" VALUES(10,73,'10:73','فكذبوه فنجيناه ومن معه في الفلك وجعلناهم خلائف واغرقنا الذين كذبوا باياتنا فانظر كيف كان عاقبة المنذرين');
INSERT INTO "imlaei_simple" VALUES(10,74,'10:74','ثم بعثنا من بعده رسلا الى قومهم فجاءوهم بالبينات فما كانوا ليؤمنوا بما كذبوا به من قبل كذلك نطبع على قلوب المعتدين');
INSERT INTO "imlaei_simple" VALUES(10,75,'10:75','ثم بعثنا من بعدهم موسى وهارون الى فرعون وملئه باياتنا فاستكبروا وكانوا قوما مجرمين');
INSERT INTO "imlaei_simple" VALUES(10,76,'10:76','فلما جاءهم الحق من عندنا قالوا ان هذا لسحر مبين');
INSERT INTO "imlaei_simple" VALUES(10,77,'10:77','قال موسى اتقولون للحق لما جاءكم اسحر هذا ولا يفلح الساحرون');
INSERT INTO "imlaei_simple" VALUES(10,78,'10:78','قالوا اجئتنا لتلفتنا عما وجدنا عليه اباءنا وتكون لكما الكبرياء في الارض وما نحن لكما بمؤمنين');
INSERT INTO "imlaei_simple" VALUES(10,79,'10:79','وقال فرعون ائتوني بكل ساحر عليم');
INSERT INTO "imlaei_simple" VALUES(10,80,'10:80','فلما جاء السحرة قال لهم موسى القوا ما انتم ملقون');
INSERT INTO "imlaei_simple" VALUES(10,81,'10:81','فلما القوا قال موسى ما جئتم به السحر ان الله سيبطله ان الله لا يصلح عمل المفسدين');
INSERT INTO "imlaei_simple" VALUES(10,82,'10:82','ويحق الله الحق بكلماته ولو كره المجرمون');
INSERT INTO "imlaei_simple" VALUES(10,83,'10:83','فما امن لموسى الا ذرية من قومه على خوف من فرعون وملئهم ان يفتنهم وان فرعون لعال في الارض وانه لمن المسرفين');
INSERT INTO "imlaei_simple" VALUES(10,84,'10:84','وقال موسى يا قوم ان كنتم امنتم بالله فعليه توكلوا ان كنتم مسلمين');
INSERT INTO "imlaei_simple" VALUES(10,85,'10:85','فقالوا على الله توكلنا ربنا لا تجعلنا فتنة للقوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(10,86,'10:86','ونجنا برحمتك من القوم الكافرين');
INSERT INTO "imlaei_simple" VALUES(10,87,'10:87','واوحينا الى موسى واخيه ان تبوا لقومكما بمصر بيوتا واجعلوا بيوتكم قبلة واقيموا الصلاة وبشر المؤمنين');
INSERT INTO "imlaei_simple" VALUES(10,88,'10:88','وقال موسى ربنا انك اتيت فرعون وملاه زينة واموالا في الحياة الدنيا ربنا ليضلوا عن سبيلك ربنا اطمس على اموالهم واشدد على قلوبهم فلا يؤمنوا حتى يروا العذاب الاليم');
INSERT INTO "imlaei_simple" VALUES(10,89,'10:89','قال قد اجيبت دعوتكما فاستقيما ولا تتبعان سبيل الذين لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(10,90,'10:90','وجاوزنا ببني اسرائيل البحر فاتبعهم فرعون وجنوده بغيا وعدوا حتى اذا ادركه الغرق قال امنت انه لا اله الا الذي امنت به بنو اسرائيل وانا من المسلمين');
INSERT INTO "imlaei_simple" VALUES(10,91,'10:91','الان وقد عصيت قبل وكنت من المفسدين');
INSERT INTO "imlaei_simple" VALUES(10,92,'10:92','فاليوم ننجيك ببدنك لتكون لمن خلفك اية وان كثيرا من الناس عن اياتنا لغافلون');
INSERT INTO "imlaei_simple" VALUES(10,93,'10:93','ولقد بوانا بني اسرائيل مبوا صدق ورزقناهم من الطيبات فما اختلفوا حتى جاءهم العلم ان ربك يقضي بينهم يوم القيامة فيما كانوا فيه يختلفون');
INSERT INTO "imlaei_simple" VALUES(10,94,'10:94','فان كنت في شك مما انزلنا اليك فاسال الذين يقرءون الكتاب من قبلك لقد جاءك الحق من ربك فلا تكونن من الممترين');
INSERT INTO "imlaei_simple" VALUES(10,95,'10:95','ولا تكونن من الذين كذبوا بايات الله فتكون من الخاسرين');
INSERT INTO "imlaei_simple" VALUES(10,96,'10:96','ان الذين حقت عليهم كلمت ربك لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(10,97,'10:97','ولو جاءتهم كل اية حتى يروا العذاب الاليم');
INSERT INTO "imlaei_simple" VALUES(10,98,'10:98','فلولا كانت قرية امنت فنفعها ايمانها الا قوم يونس لما امنوا كشفنا عنهم عذاب الخزي في الحياة الدنيا ومتعناهم الى حين');
INSERT INTO "imlaei_simple" VALUES(10,99,'10:99','ولو شاء ربك لامن من في الارض كلهم جميعا افانت تكره الناس حتى يكونوا مؤمنين');
INSERT INTO "imlaei_simple" VALUES(10,100,'10:100','وما كان لنفس ان تؤمن الا باذن الله ويجعل الرجس على الذين لا يعقلون');
INSERT INTO "imlaei_simple" VALUES(10,101,'10:101','قل انظروا ماذا في السماوات والارض وما تغني الايات والنذر عن قوم لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(10,102,'10:102','فهل ينتظرون الا مثل ايام الذين خلوا من قبلهم قل فانتظروا اني معكم من المنتظرين');
INSERT INTO "imlaei_simple" VALUES(10,103,'10:103','ثم ننجي رسلنا والذين امنوا كذلك حقا علينا ننج المؤمنين');
INSERT INTO "imlaei_simple" VALUES(10,104,'10:104','قل يا ايها الناس ان كنتم في شك من ديني فلا اعبد الذين تعبدون من دون الله ولكن اعبد الله الذي يتوفاكم وامرت ان اكون من المؤمنين');
INSERT INTO "imlaei_simple" VALUES(10,105,'10:105','وان اقم وجهك للدين حنيفا ولا تكونن من المشركين');
INSERT INTO "imlaei_simple" VALUES(10,106,'10:106','ولا تدع من دون الله ما لا ينفعك ولا يضرك فان فعلت فانك اذا من الظالمين');
INSERT INTO "imlaei_simple" VALUES(10,107,'10:107','وان يمسسك الله بضر فلا كاشف له الا هو وان يردك بخير فلا راد لفضله يصيب به من يشاء من عباده وهو الغفور الرحيم');
INSERT INTO "imlaei_simple" VALUES(10,108,'10:108','قل يا ايها الناس قد جاءكم الحق من ربكم فمن اهتدى فانما يهتدي لنفسه ومن ضل فانما يضل عليها وما انا عليكم بوكيل');
INSERT INTO "imlaei_simple" VALUES(10,109,'10:109','واتبع ما يوحى اليك واصبر حتى يحكم الله وهو خير الحاكمين');
INSERT INTO "imlaei_simple" VALUES(11,1,'11:1','الر كتاب احكمت اياته ثم فصلت من لدن حكيم خبير');
INSERT INTO "imlaei_simple" VALUES(11,2,'11:2','الا تعبدوا الا الله انني لكم منه نذير وبشير');
INSERT INTO "imlaei_simple" VALUES(11,3,'11:3','وان استغفروا ربكم ثم توبوا اليه يمتعكم متاعا حسنا الى اجل مسمى ويؤت كل ذي فضل فضله وان تولوا فاني اخاف عليكم عذاب يوم كبير');
INSERT INTO "imlaei_simple" VALUES(11,4,'11:4','الى الله مرجعكم وهو على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(11,5,'11:5','الا انهم يثنون صدورهم ليستخفوا منه الا حين يستغشون ثيابهم يعلم ما يسرون وما يعلنون انه عليم بذات الصدور');
INSERT INTO "imlaei_simple" VALUES(11,6,'11:6','وما من دابة في الارض الا على الله رزقها ويعلم مستقرها ومستودعها كل في كتاب مبين');
INSERT INTO "imlaei_simple" VALUES(11,7,'11:7','وهو الذي خلق السماوات والارض في ستة ايام وكان عرشه على الماء ليبلوكم ايكم احسن عملا ولئن قلت انكم مبعوثون من بعد الموت ليقولن الذين كفروا ان هذا الا سحر مبين');
INSERT INTO "imlaei_simple" VALUES(11,8,'11:8','ولئن اخرنا عنهم العذاب الى امة معدودة ليقولن ما يحبسه الا يوم ياتيهم ليس مصروفا عنهم وحاق بهم ما كانوا به يستهزئون');
INSERT INTO "imlaei_simple" VALUES(11,9,'11:9','ولئن اذقنا الانسان منا رحمة ثم نزعناها منه انه ليئوس كفور');
INSERT INTO "imlaei_simple" VALUES(11,10,'11:10','ولئن اذقناه نعماء بعد ضراء مسته ليقولن ذهب السيئات عني انه لفرح فخور');
INSERT INTO "imlaei_simple" VALUES(11,11,'11:11','الا الذين صبروا وعملوا الصالحات اولئك لهم مغفرة واجر كبير');
INSERT INTO "imlaei_simple" VALUES(11,12,'11:12','فلعلك تارك بعض ما يوحى اليك وضائق به صدرك ان يقولوا لولا انزل عليه كنز او جاء معه ملك انما انت نذير والله على كل شيء وكيل');
INSERT INTO "imlaei_simple" VALUES(11,13,'11:13','ام يقولون افتراه قل فاتوا بعشر سور مثله مفتريات وادعوا من استطعتم من دون الله ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(11,14,'11:14','فالم يستجيبوا لكم فاعلموا انما انزل بعلم الله وان لا اله الا هو فهل انتم مسلمون');
INSERT INTO "imlaei_simple" VALUES(11,15,'11:15','من كان يريد الحياة الدنيا وزينتها نوف اليهم اعمالهم فيها وهم فيها لا يبخسون');
INSERT INTO "imlaei_simple" VALUES(11,16,'11:16','اولئك الذين ليس لهم في الاخرة الا النار وحبط ما صنعوا فيها وباطل ما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(11,17,'11:17','افمن كان على بينة من ربه ويتلوه شاهد منه ومن قبله كتاب موسى اماما ورحمة اولئك يؤمنون به ومن يكفر به من الاحزاب فالنار موعده فلا تك في مرية منه انه الحق من ربك ولكن اكثر الناس لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(11,18,'11:18','ومن اظلم ممن افترى على الله كذبا اولئك يعرضون على ربهم ويقول الاشهاد هؤلاء الذين كذبوا على ربهم الا لعنة الله على الظالمين');
INSERT INTO "imlaei_simple" VALUES(11,19,'11:19','الذين يصدون عن سبيل الله ويبغونها عوجا وهم بالاخرة هم كافرون');
INSERT INTO "imlaei_simple" VALUES(11,20,'11:20','اولئك لم يكونوا معجزين في الارض وما كان لهم من دون الله من اولياء يضاعف لهم العذاب ما كانوا يستطيعون السمع وما كانوا يبصرون');
INSERT INTO "imlaei_simple" VALUES(11,21,'11:21','اولئك الذين خسروا انفسهم وضل عنهم ما كانوا يفترون');
INSERT INTO "imlaei_simple" VALUES(11,22,'11:22','لا جرم انهم في الاخرة هم الاخسرون');
INSERT INTO "imlaei_simple" VALUES(11,23,'11:23','ان الذين امنوا وعملوا الصالحات واخبتوا الى ربهم اولئك اصحاب الجنة هم فيها خالدون');
INSERT INTO "imlaei_simple" VALUES(11,24,'11:24','مثل الفريقين كالاعمى والاصم والبصير والسميع هل يستويان مثلا افلا تذكرون');
INSERT INTO "imlaei_simple" VALUES(11,25,'11:25','ولقد ارسلنا نوحا الى قومه اني لكم نذير مبين');
INSERT INTO "imlaei_simple" VALUES(11,26,'11:26','ان لا تعبدوا الا الله اني اخاف عليكم عذاب يوم اليم');
INSERT INTO "imlaei_simple" VALUES(11,27,'11:27','فقال الملا الذين كفروا من قومه ما نراك الا بشرا مثلنا وما نراك اتبعك الا الذين هم اراذلنا بادي الراي وما نرى لكم علينا من فضل بل نظنكم كاذبين');
INSERT INTO "imlaei_simple" VALUES(11,28,'11:28','قال يا قوم ارايتم ان كنت على بينة من ربي واتاني رحمة من عنده فعميت عليكم انلزمكموها وانتم لها كارهون');
INSERT INTO "imlaei_simple" VALUES(11,29,'11:29','ويا قوم لا اسالكم عليه مالا ان اجري الا على الله وما انا بطارد الذين امنوا انهم ملاقو ربهم ولكني اراكم قوما تجهلون');
INSERT INTO "imlaei_simple" VALUES(11,30,'11:30','ويا قوم من ينصرني من الله ان طردتهم افلا تذكرون');
INSERT INTO "imlaei_simple" VALUES(11,31,'11:31','ولا اقول لكم عندي خزائن الله ولا اعلم الغيب ولا اقول اني ملك ولا اقول للذين تزدري اعينكم لن يؤتيهم الله خيرا الله اعلم بما في انفسهم اني اذا لمن الظالمين');
INSERT INTO "imlaei_simple" VALUES(11,32,'11:32','قالوا يا نوح قد جادلتنا فاكثرت جدالنا فاتنا بما تعدنا ان كنت من الصادقين');
INSERT INTO "imlaei_simple" VALUES(11,33,'11:33','قال انما ياتيكم به الله ان شاء وما انتم بمعجزين');
INSERT INTO "imlaei_simple" VALUES(11,34,'11:34','ولا ينفعكم نصحي ان اردت ان انصح لكم ان كان الله يريد ان يغويكم هو ربكم واليه ترجعون');
INSERT INTO "imlaei_simple" VALUES(11,35,'11:35','ام يقولون افتراه قل ان افتريته فعلي اجرامي وانا بريء مما تجرمون');
INSERT INTO "imlaei_simple" VALUES(11,36,'11:36','واوحي الى نوح انه لن يؤمن من قومك الا من قد امن فلا تبتئس بما كانوا يفعلون');
INSERT INTO "imlaei_simple" VALUES(11,37,'11:37','واصنع الفلك باعيننا ووحينا ولا تخاطبني في الذين ظلموا انهم مغرقون');
INSERT INTO "imlaei_simple" VALUES(11,38,'11:38','ويصنع الفلك وكلما مر عليه ملا من قومه سخروا منه قال ان تسخروا منا فانا نسخر منكم كما تسخرون');
INSERT INTO "imlaei_simple" VALUES(11,39,'11:39','فسوف تعلمون من ياتيه عذاب يخزيه ويحل عليه عذاب مقيم');
INSERT INTO "imlaei_simple" VALUES(11,40,'11:40','حتى اذا جاء امرنا وفار التنور قلنا احمل فيها من كل زوجين اثنين واهلك الا من سبق عليه القول ومن امن وما امن معه الا قليل');
INSERT INTO "imlaei_simple" VALUES(11,41,'11:41','وقال اركبوا فيها بسم الله مجراها ومرساها ان ربي لغفور رحيم');
INSERT INTO "imlaei_simple" VALUES(11,42,'11:42','وهي تجري بهم في موج كالجبال ونادى نوح ابنه وكان في معزل يا بني اركب معنا ولا تكن مع الكافرين');
INSERT INTO "imlaei_simple" VALUES(11,43,'11:43','قال ساوي الى جبل يعصمني من الماء قال لا عاصم اليوم من امر الله الا من رحم وحال بينهما الموج فكان من المغرقين');
INSERT INTO "imlaei_simple" VALUES(11,44,'11:44','وقيل يا ارض ابلعي ماءك ويا سماء اقلعي وغيض الماء وقضي الامر واستوت على الجودي وقيل بعدا للقوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(11,45,'11:45','ونادى نوح ربه فقال رب ان ابني من اهلي وان وعدك الحق وانت احكم الحاكمين');
INSERT INTO "imlaei_simple" VALUES(11,46,'11:46','قال يا نوح انه ليس من اهلك انه عمل غير صالح فلا تسالن ما ليس لك به علم اني اعظك ان تكون من الجاهلين');
INSERT INTO "imlaei_simple" VALUES(11,47,'11:47','قال رب اني اعوذ بك ان اسالك ما ليس لي به علم والا تغفر لي وترحمني اكن من الخاسرين');
INSERT INTO "imlaei_simple" VALUES(11,48,'11:48','قيل يا نوح اهبط بسلام منا وبركات عليك وعلى امم ممن معك وامم سنمتعهم ثم يمسهم منا عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(11,49,'11:49','تلك من انباء الغيب نوحيها اليك ما كنت تعلمها انت ولا قومك من قبل هذا فاصبر ان العاقبة للمتقين');
INSERT INTO "imlaei_simple" VALUES(11,50,'11:50','والى عاد اخاهم هودا قال يا قوم اعبدوا الله ما لكم من اله غيره ان انتم الا مفترون');
INSERT INTO "imlaei_simple" VALUES(11,51,'11:51','يا قوم لا اسالكم عليه اجرا ان اجري الا على الذي فطرني افلا تعقلون');
INSERT INTO "imlaei_simple" VALUES(11,52,'11:52','ويا قوم استغفروا ربكم ثم توبوا اليه يرسل السماء عليكم مدرارا ويزدكم قوة الى قوتكم ولا تتولوا مجرمين');
INSERT INTO "imlaei_simple" VALUES(11,53,'11:53','قالوا يا هود ما جئتنا ببينة وما نحن بتاركي الهتنا عن قولك وما نحن لك بمؤمنين');
INSERT INTO "imlaei_simple" VALUES(11,54,'11:54','ان نقول الا اعتراك بعض الهتنا بسوء قال اني اشهد الله واشهدوا اني بريء مما تشركون');
INSERT INTO "imlaei_simple" VALUES(11,55,'11:55','من دونه فكيدوني جميعا ثم لا تنظرون');
INSERT INTO "imlaei_simple" VALUES(11,56,'11:56','اني توكلت على الله ربي وربكم ما من دابة الا هو اخذ بناصيتها ان ربي على صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(11,57,'11:57','فان تولوا فقد ابلغتكم ما ارسلت به اليكم ويستخلف ربي قوما غيركم ولا تضرونه شيئا ان ربي على كل شيء حفيظ');
INSERT INTO "imlaei_simple" VALUES(11,58,'11:58','ولما جاء امرنا نجينا هودا والذين امنوا معه برحمة منا ونجيناهم من عذاب غليظ');
INSERT INTO "imlaei_simple" VALUES(11,59,'11:59','وتلك عاد جحدوا بايات ربهم وعصوا رسله واتبعوا امر كل جبار عنيد');
INSERT INTO "imlaei_simple" VALUES(11,60,'11:60','واتبعوا في هذه الدنيا لعنة ويوم القيامة الا ان عادا كفروا ربهم الا بعدا لعاد قوم هود');
INSERT INTO "imlaei_simple" VALUES(11,61,'11:61','والى ثمود اخاهم صالحا قال يا قوم اعبدوا الله ما لكم من اله غيره هو انشاكم من الارض واستعمركم فيها فاستغفروه ثم توبوا اليه ان ربي قريب مجيب');
INSERT INTO "imlaei_simple" VALUES(11,62,'11:62','قالوا يا صالح قد كنت فينا مرجوا قبل هذا اتنهانا ان نعبد ما يعبد اباؤنا واننا لفي شك مما تدعونا اليه مريب');
INSERT INTO "imlaei_simple" VALUES(11,63,'11:63','قال يا قوم ارايتم ان كنت على بينة من ربي واتاني منه رحمة فمن ينصرني من الله ان عصيته فما تزيدونني غير تخسير');
INSERT INTO "imlaei_simple" VALUES(11,64,'11:64','ويا قوم هذه ناقة الله لكم اية فذروها تاكل في ارض الله ولا تمسوها بسوء فياخذكم عذاب قريب');
INSERT INTO "imlaei_simple" VALUES(11,65,'11:65','فعقروها فقال تمتعوا في داركم ثلاثة ايام ذلك وعد غير مكذوب');
INSERT INTO "imlaei_simple" VALUES(11,66,'11:66','فلما جاء امرنا نجينا صالحا والذين امنوا معه برحمة منا ومن خزي يومئذ ان ربك هو القوي العزيز');
INSERT INTO "imlaei_simple" VALUES(11,67,'11:67','واخذ الذين ظلموا الصيحة فاصبحوا في ديارهم جاثمين');
INSERT INTO "imlaei_simple" VALUES(11,68,'11:68','كان لم يغنوا فيها الا ان ثمود كفروا ربهم الا بعدا لثمود');
INSERT INTO "imlaei_simple" VALUES(11,69,'11:69','ولقد جاءت رسلنا ابراهيم بالبشرى قالوا سلاما قال سلام فما لبث ان جاء بعجل حنيذ');
INSERT INTO "imlaei_simple" VALUES(11,70,'11:70','فلما راى ايديهم لا تصل اليه نكرهم واوجس منهم خيفة قالوا لا تخف انا ارسلنا الى قوم لوط');
INSERT INTO "imlaei_simple" VALUES(11,71,'11:71','وامراته قائمة فضحكت فبشرناها باسحاق ومن وراء اسحاق يعقوب');
INSERT INTO "imlaei_simple" VALUES(11,72,'11:72','قالت يا ويلتى االد وانا عجوز وهذا بعلي شيخا ان هذا لشيء عجيب');
INSERT INTO "imlaei_simple" VALUES(11,73,'11:73','قالوا اتعجبين من امر الله رحمت الله وبركاته عليكم اهل البيت انه حميد مجيد');
INSERT INTO "imlaei_simple" VALUES(11,74,'11:74','فلما ذهب عن ابراهيم الروع وجاءته البشرى يجادلنا في قوم لوط');
INSERT INTO "imlaei_simple" VALUES(11,75,'11:75','ان ابراهيم لحليم اواه منيب');
INSERT INTO "imlaei_simple" VALUES(11,76,'11:76','يا ابراهيم اعرض عن هذا انه قد جاء امر ربك وانهم اتيهم عذاب غير مردود');
INSERT INTO "imlaei_simple" VALUES(11,77,'11:77','ولما جاءت رسلنا لوطا سيء بهم وضاق بهم ذرعا وقال هذا يوم عصيب');
INSERT INTO "imlaei_simple" VALUES(11,78,'11:78','وجاءه قومه يهرعون اليه ومن قبل كانوا يعملون السيئات قال يا قوم هؤلاء بناتي هن اطهر لكم فاتقوا الله ولا تخزون في ضيفي اليس منكم رجل رشيد');
INSERT INTO "imlaei_simple" VALUES(11,79,'11:79','قالوا لقد علمت ما لنا في بناتك من حق وانك لتعلم ما نريد');
INSERT INTO "imlaei_simple" VALUES(11,80,'11:80','قال لو ان لي بكم قوة او اوي الى ركن شديد');
INSERT INTO "imlaei_simple" VALUES(11,81,'11:81','قالوا يا لوط انا رسل ربك لن يصلوا اليك فاسر باهلك بقطع من الليل ولا يلتفت منكم احد الا امراتك انه مصيبها ما اصابهم ان موعدهم الصبح اليس الصبح بقريب');
INSERT INTO "imlaei_simple" VALUES(11,82,'11:82','فلما جاء امرنا جعلنا عاليها سافلها وامطرنا عليها حجارة من سجيل منضود');
INSERT INTO "imlaei_simple" VALUES(11,83,'11:83','مسومة عند ربك وما هي من الظالمين ببعيد');
INSERT INTO "imlaei_simple" VALUES(11,84,'11:84','والى مدين اخاهم شعيبا قال يا قوم اعبدوا الله ما لكم من اله غيره ولا تنقصوا المكيال والميزان اني اراكم بخير واني اخاف عليكم عذاب يوم محيط');
INSERT INTO "imlaei_simple" VALUES(11,85,'11:85','ويا قوم اوفوا المكيال والميزان بالقسط ولا تبخسوا الناس اشياءهم ولا تعثوا في الارض مفسدين');
INSERT INTO "imlaei_simple" VALUES(11,86,'11:86','بقيت الله خير لكم ان كنتم مؤمنين وما انا عليكم بحفيظ');
INSERT INTO "imlaei_simple" VALUES(11,87,'11:87','قالوا يا شعيب اصلاتك تامرك ان نترك ما يعبد اباؤنا او ان نفعل في اموالنا ما نشاء انك لانت الحليم الرشيد');
INSERT INTO "imlaei_simple" VALUES(11,88,'11:88','قال يا قوم ارايتم ان كنت على بينة من ربي ورزقني منه رزقا حسنا وما اريد ان اخالفكم الى ما انهاكم عنه ان اريد الا الاصلاح ما استطعت وما توفيقي الا بالله عليه توكلت واليه انيب');
INSERT INTO "imlaei_simple" VALUES(11,89,'11:89','ويا قوم لا يجرمنكم شقاقي ان يصيبكم مثل ما اصاب قوم نوح او قوم هود او قوم صالح وما قوم لوط منكم ببعيد');
INSERT INTO "imlaei_simple" VALUES(11,90,'11:90','واستغفروا ربكم ثم توبوا اليه ان ربي رحيم ودود');
INSERT INTO "imlaei_simple" VALUES(11,91,'11:91','قالوا يا شعيب ما نفقه كثيرا مما تقول وانا لنراك فينا ضعيفا ولولا رهطك لرجمناك وما انت علينا بعزيز');
INSERT INTO "imlaei_simple" VALUES(11,92,'11:92','قال يا قوم ارهطي اعز عليكم من الله واتخذتموه وراءكم ظهريا ان ربي بما تعملون محيط');
INSERT INTO "imlaei_simple" VALUES(11,93,'11:93','ويا قوم اعملوا على مكانتكم اني عامل سوف تعلمون من ياتيه عذاب يخزيه ومن هو كاذب وارتقبوا اني معكم رقيب');
INSERT INTO "imlaei_simple" VALUES(11,94,'11:94','ولما جاء امرنا نجينا شعيبا والذين امنوا معه برحمة منا واخذت الذين ظلموا الصيحة فاصبحوا في ديارهم جاثمين');
INSERT INTO "imlaei_simple" VALUES(11,95,'11:95','كان لم يغنوا فيها الا بعدا لمدين كما بعدت ثمود');
INSERT INTO "imlaei_simple" VALUES(11,96,'11:96','ولقد ارسلنا موسى باياتنا وسلطان مبين');
INSERT INTO "imlaei_simple" VALUES(11,97,'11:97','الى فرعون وملئه فاتبعوا امر فرعون وما امر فرعون برشيد');
INSERT INTO "imlaei_simple" VALUES(11,98,'11:98','يقدم قومه يوم القيامة فاوردهم النار وبئس الورد المورود');
INSERT INTO "imlaei_simple" VALUES(11,99,'11:99','واتبعوا في هذه لعنة ويوم القيامة بئس الرفد المرفود');
INSERT INTO "imlaei_simple" VALUES(11,100,'11:100','ذلك من انباء القرى نقصه عليك منها قائم وحصيد');
INSERT INTO "imlaei_simple" VALUES(11,101,'11:101','وما ظلمناهم ولكن ظلموا انفسهم فما اغنت عنهم الهتهم التي يدعون من دون الله من شيء لما جاء امر ربك وما زادوهم غير تتبيب');
INSERT INTO "imlaei_simple" VALUES(11,102,'11:102','وكذلك اخذ ربك اذا اخذ القرى وهي ظالمة ان اخذه اليم شديد');
INSERT INTO "imlaei_simple" VALUES(11,103,'11:103','ان في ذلك لاية لمن خاف عذاب الاخرة ذلك يوم مجموع له الناس وذلك يوم مشهود');
INSERT INTO "imlaei_simple" VALUES(11,104,'11:104','وما نؤخره الا لاجل معدود');
INSERT INTO "imlaei_simple" VALUES(11,105,'11:105','يوم يات لا تكلم نفس الا باذنه فمنهم شقي وسعيد');
INSERT INTO "imlaei_simple" VALUES(11,106,'11:106','فاما الذين شقوا ففي النار لهم فيها زفير وشهيق');
INSERT INTO "imlaei_simple" VALUES(11,107,'11:107','خالدين فيها ما دامت السماوات والارض الا ما شاء ربك ان ربك فعال لما يريد');
INSERT INTO "imlaei_simple" VALUES(11,108,'11:108','واما الذين سعدوا ففي الجنة خالدين فيها ما دامت السماوات والارض الا ما شاء ربك عطاء غير مجذوذ');
INSERT INTO "imlaei_simple" VALUES(11,109,'11:109','فلا تك في مرية مما يعبد هؤلاء ما يعبدون الا كما يعبد اباؤهم من قبل وانا لموفوهم نصيبهم غير منقوص');
INSERT INTO "imlaei_simple" VALUES(11,110,'11:110','ولقد اتينا موسى الكتاب فاختلف فيه ولولا كلمة سبقت من ربك لقضي بينهم وانهم لفي شك منه مريب');
INSERT INTO "imlaei_simple" VALUES(11,111,'11:111','وان كلا لما ليوفينهم ربك اعمالهم انه بما يعملون خبير');
INSERT INTO "imlaei_simple" VALUES(11,112,'11:112','فاستقم كما امرت ومن تاب معك ولا تطغوا انه بما تعملون بصير');
INSERT INTO "imlaei_simple" VALUES(11,113,'11:113','ولا تركنوا الى الذين ظلموا فتمسكم النار وما لكم من دون الله من اولياء ثم لا تنصرون');
INSERT INTO "imlaei_simple" VALUES(11,114,'11:114','واقم الصلاة طرفي النهار وزلفا من الليل ان الحسنات يذهبن السيئات ذلك ذكرى للذاكرين');
INSERT INTO "imlaei_simple" VALUES(11,115,'11:115','واصبر فان الله لا يضيع اجر المحسنين');
INSERT INTO "imlaei_simple" VALUES(11,116,'11:116','فلولا كان من القرون من قبلكم اولو بقية ينهون عن الفساد في الارض الا قليلا ممن انجينا منهم واتبع الذين ظلموا ما اترفوا فيه وكانوا مجرمين');
INSERT INTO "imlaei_simple" VALUES(11,117,'11:117','وما كان ربك ليهلك القرى بظلم واهلها مصلحون');
INSERT INTO "imlaei_simple" VALUES(11,118,'11:118','ولو شاء ربك لجعل الناس امة واحدة ولا يزالون مختلفين');
INSERT INTO "imlaei_simple" VALUES(11,119,'11:119','الا من رحم ربك ولذلك خلقهم وتمت كلمة ربك لاملان جهنم من الجنة والناس اجمعين');
INSERT INTO "imlaei_simple" VALUES(11,120,'11:120','وكلا نقص عليك من انباء الرسل ما نثبت به فؤادك وجاءك في هذه الحق وموعظة وذكرى للمؤمنين');
INSERT INTO "imlaei_simple" VALUES(11,121,'11:121','وقل للذين لا يؤمنون اعملوا على مكانتكم انا عاملون');
INSERT INTO "imlaei_simple" VALUES(11,122,'11:122','وانتظروا انا منتظرون');
INSERT INTO "imlaei_simple" VALUES(11,123,'11:123','ولله غيب السماوات والارض واليه يرجع الامر كله فاعبده وتوكل عليه وما ربك بغافل عما تعملون');
INSERT INTO "imlaei_simple" VALUES(12,1,'12:1','الر تلك ايات الكتاب المبين');
INSERT INTO "imlaei_simple" VALUES(12,2,'12:2','انا انزلناه قرانا عربيا لعلكم تعقلون');
INSERT INTO "imlaei_simple" VALUES(12,3,'12:3','نحن نقص عليك احسن القصص بما اوحينا اليك هذا القران وان كنت من قبله لمن الغافلين');
INSERT INTO "imlaei_simple" VALUES(12,4,'12:4','اذ قال يوسف لابيه يا ابت اني رايت احد عشر كوكبا والشمس والقمر رايتهم لي ساجدين');
INSERT INTO "imlaei_simple" VALUES(12,5,'12:5','قال يا بني لا تقصص رؤياك على اخوتك فيكيدوا لك كيدا ان الشيطان للانسان عدو مبين');
INSERT INTO "imlaei_simple" VALUES(12,6,'12:6','وكذلك يجتبيك ربك ويعلمك من تاويل الاحاديث ويتم نعمته عليك وعلى ال يعقوب كما اتمها على ابويك من قبل ابراهيم واسحاق ان ربك عليم حكيم');
INSERT INTO "imlaei_simple" VALUES(12,7,'12:7','لقد كان في يوسف واخوته ايات للسائلين');
INSERT INTO "imlaei_simple" VALUES(12,8,'12:8','اذ قالوا ليوسف واخوه احب الى ابينا منا ونحن عصبة ان ابانا لفي ضلال مبين');
INSERT INTO "imlaei_simple" VALUES(12,9,'12:9','اقتلوا يوسف او اطرحوه ارضا يخل لكم وجه ابيكم وتكونوا من بعده قوما صالحين');
INSERT INTO "imlaei_simple" VALUES(12,10,'12:10','قال قائل منهم لا تقتلوا يوسف والقوه في غيابت الجب يلتقطه بعض السيارة ان كنتم فاعلين');
INSERT INTO "imlaei_simple" VALUES(12,11,'12:11','قالوا يا ابانا ما لك لا تامنا على يوسف وانا له لناصحون');
INSERT INTO "imlaei_simple" VALUES(12,12,'12:12','ارسله معنا غدا يرتع ويلعب وانا له لحافظون');
INSERT INTO "imlaei_simple" VALUES(12,13,'12:13','قال اني ليحزنني ان تذهبوا به واخاف ان ياكله الذئب وانتم عنه غافلون');
INSERT INTO "imlaei_simple" VALUES(12,14,'12:14','قالوا لئن اكله الذئب ونحن عصبة انا اذا لخاسرون');
INSERT INTO "imlaei_simple" VALUES(12,15,'12:15','فلما ذهبوا به واجمعوا ان يجعلوه في غيابت الجب واوحينا اليه لتنبئنهم بامرهم هذا وهم لا يشعرون');
INSERT INTO "imlaei_simple" VALUES(12,16,'12:16','وجاءوا اباهم عشاء يبكون');
INSERT INTO "imlaei_simple" VALUES(12,17,'12:17','قالوا يا ابانا انا ذهبنا نستبق وتركنا يوسف عند متاعنا فاكله الذئب وما انت بمؤمن لنا ولو كنا صادقين');
INSERT INTO "imlaei_simple" VALUES(12,18,'12:18','وجاءوا على قميصه بدم كذب قال بل سولت لكم انفسكم امرا فصبر جميل والله المستعان على ما تصفون');
INSERT INTO "imlaei_simple" VALUES(12,19,'12:19','وجاءت سيارة فارسلوا واردهم فادلى دلوه قال يا بشرى هذا غلام واسروه بضاعة والله عليم بما يعملون');
INSERT INTO "imlaei_simple" VALUES(12,20,'12:20','وشروه بثمن بخس دراهم معدودة وكانوا فيه من الزاهدين');
INSERT INTO "imlaei_simple" VALUES(12,21,'12:21','وقال الذي اشتراه من مصر لامراته اكرمي مثواه عسى ان ينفعنا او نتخذه ولدا وكذلك مكنا ليوسف في الارض ولنعلمه من تاويل الاحاديث والله غالب على امره ولكن اكثر الناس لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(12,22,'12:22','ولما بلغ اشده اتيناه حكما وعلما وكذلك نجزي المحسنين');
INSERT INTO "imlaei_simple" VALUES(12,23,'12:23','وراودته التي هو في بيتها عن نفسه وغلقت الابواب وقالت هيت لك قال معاذ الله انه ربي احسن مثواي انه لا يفلح الظالمون');
INSERT INTO "imlaei_simple" VALUES(12,24,'12:24','ولقد همت به وهم بها لولا ان راى برهان ربه كذلك لنصرف عنه السوء والفحشاء انه من عبادنا المخلصين');
INSERT INTO "imlaei_simple" VALUES(12,25,'12:25','واستبقا الباب وقدت قميصه من دبر والفيا سيدها لدى الباب قالت ما جزاء من اراد باهلك سوءا الا ان يسجن او عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(12,26,'12:26','قال هي راودتني عن نفسي وشهد شاهد من اهلها ان كان قميصه قد من قبل فصدقت وهو من الكاذبين');
INSERT INTO "imlaei_simple" VALUES(12,27,'12:27','وان كان قميصه قد من دبر فكذبت وهو من الصادقين');
INSERT INTO "imlaei_simple" VALUES(12,28,'12:28','فلما راى قميصه قد من دبر قال انه من كيدكن ان كيدكن عظيم');
INSERT INTO "imlaei_simple" VALUES(12,29,'12:29','يوسف اعرض عن هذا واستغفري لذنبك انك كنت من الخاطئين');
INSERT INTO "imlaei_simple" VALUES(12,30,'12:30','وقال نسوة في المدينة امرات العزيز تراود فتاها عن نفسه قد شغفها حبا انا لنراها في ضلال مبين');
INSERT INTO "imlaei_simple" VALUES(12,31,'12:31','فلما سمعت بمكرهن ارسلت اليهن واعتدت لهن متكا واتت كل واحدة منهن سكينا وقالت اخرج عليهن فلما راينه اكبرنه وقطعن ايديهن وقلن حاش لله ما هذا بشرا ان هذا الا ملك كريم');
INSERT INTO "imlaei_simple" VALUES(12,32,'12:32','قالت فذلكن الذي لمتنني فيه ولقد راودته عن نفسه فاستعصم ولئن لم يفعل ما امره ليسجنن وليكونا من الصاغرين');
INSERT INTO "imlaei_simple" VALUES(12,33,'12:33','قال رب السجن احب الي مما يدعونني اليه والا تصرف عني كيدهن اصب اليهن واكن من الجاهلين');
INSERT INTO "imlaei_simple" VALUES(12,34,'12:34','فاستجاب له ربه فصرف عنه كيدهن انه هو السميع العليم');
INSERT INTO "imlaei_simple" VALUES(12,35,'12:35','ثم بدا لهم من بعد ما راوا الايات ليسجننه حتى حين');
INSERT INTO "imlaei_simple" VALUES(12,36,'12:36','ودخل معه السجن فتيان قال احدهما اني اراني اعصر خمرا وقال الاخر اني اراني احمل فوق راسي خبزا تاكل الطير منه نبئنا بتاويله انا نراك من المحسنين');
INSERT INTO "imlaei_simple" VALUES(12,37,'12:37','قال لا ياتيكما طعام ترزقانه الا نباتكما بتاويله قبل ان ياتيكما ذلكما مما علمني ربي اني تركت ملة قوم لا يؤمنون بالله وهم بالاخرة هم كافرون');
INSERT INTO "imlaei_simple" VALUES(12,38,'12:38','واتبعت ملة ابائي ابراهيم واسحاق ويعقوب ما كان لنا ان نشرك بالله من شيء ذلك من فضل الله علينا وعلى الناس ولكن اكثر الناس لا يشكرون');
INSERT INTO "imlaei_simple" VALUES(12,39,'12:39','يا صاحبي السجن اارباب متفرقون خير ام الله الواحد القهار');
INSERT INTO "imlaei_simple" VALUES(12,40,'12:40','ما تعبدون من دونه الا اسماء سميتموها انتم واباؤكم ما انزل الله بها من سلطان ان الحكم الا لله امر الا تعبدوا الا اياه ذلك الدين القيم ولكن اكثر الناس لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(12,41,'12:41','يا صاحبي السجن اما احدكما فيسقي ربه خمرا واما الاخر فيصلب فتاكل الطير من راسه قضي الامر الذي فيه تستفتيان');
INSERT INTO "imlaei_simple" VALUES(12,42,'12:42','وقال للذي ظن انه ناج منهما اذكرني عند ربك فانساه الشيطان ذكر ربه فلبث في السجن بضع سنين');
INSERT INTO "imlaei_simple" VALUES(12,43,'12:43','وقال الملك اني ارى سبع بقرات سمان ياكلهن سبع عجاف وسبع سنبلات خضر واخر يابسات يا ايها الملا افتوني في رؤياي ان كنتم للرؤيا تعبرون');
INSERT INTO "imlaei_simple" VALUES(12,44,'12:44','قالوا اضغاث احلام وما نحن بتاويل الاحلام بعالمين');
INSERT INTO "imlaei_simple" VALUES(12,45,'12:45','وقال الذي نجا منهما وادكر بعد امة انا انبئكم بتاويله فارسلون');
INSERT INTO "imlaei_simple" VALUES(12,46,'12:46','يوسف ايها الصديق افتنا في سبع بقرات سمان ياكلهن سبع عجاف وسبع سنبلات خضر واخر يابسات لعلي ارجع الى الناس لعلهم يعلمون');
INSERT INTO "imlaei_simple" VALUES(12,47,'12:47','قال تزرعون سبع سنين دابا فما حصدتم فذروه في سنبله الا قليلا مما تاكلون');
INSERT INTO "imlaei_simple" VALUES(12,48,'12:48','ثم ياتي من بعد ذلك سبع شداد ياكلن ما قدمتم لهن الا قليلا مما تحصنون');
INSERT INTO "imlaei_simple" VALUES(12,49,'12:49','ثم ياتي من بعد ذلك عام فيه يغاث الناس وفيه يعصرون');
INSERT INTO "imlaei_simple" VALUES(12,50,'12:50','وقال الملك ائتوني به فلما جاءه الرسول قال ارجع الى ربك فاساله ما بال النسوة اللاتي قطعن ايديهن ان ربي بكيدهن عليم');
INSERT INTO "imlaei_simple" VALUES(12,51,'12:51','قال ما خطبكن اذ راودتن يوسف عن نفسه قلن حاش لله ما علمنا عليه من سوء قالت امرات العزيز الان حصحص الحق انا راودته عن نفسه وانه لمن الصادقين');
INSERT INTO "imlaei_simple" VALUES(12,52,'12:52','ذلك ليعلم اني لم اخنه بالغيب وان الله لا يهدي كيد الخائنين');
INSERT INTO "imlaei_simple" VALUES(12,53,'12:53','وما ابرئ نفسي ان النفس لامارة بالسوء الا ما رحم ربي ان ربي غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(12,54,'12:54','وقال الملك ائتوني به استخلصه لنفسي فلما كلمه قال انك اليوم لدينا مكين امين');
INSERT INTO "imlaei_simple" VALUES(12,55,'12:55','قال اجعلني على خزائن الارض اني حفيظ عليم');
INSERT INTO "imlaei_simple" VALUES(12,56,'12:56','وكذلك مكنا ليوسف في الارض يتبوا منها حيث يشاء نصيب برحمتنا من نشاء ولا نضيع اجر المحسنين');
INSERT INTO "imlaei_simple" VALUES(12,57,'12:57','ولاجر الاخرة خير للذين امنوا وكانوا يتقون');
INSERT INTO "imlaei_simple" VALUES(12,58,'12:58','وجاء اخوة يوسف فدخلوا عليه فعرفهم وهم له منكرون');
INSERT INTO "imlaei_simple" VALUES(12,59,'12:59','ولما جهزهم بجهازهم قال ائتوني باخ لكم من ابيكم الا ترون اني اوفي الكيل وانا خير المنزلين');
INSERT INTO "imlaei_simple" VALUES(12,60,'12:60','فان لم تاتوني به فلا كيل لكم عندي ولا تقربون');
INSERT INTO "imlaei_simple" VALUES(12,61,'12:61','قالوا سنراود عنه اباه وانا لفاعلون');
INSERT INTO "imlaei_simple" VALUES(12,62,'12:62','وقال لفتيانه اجعلوا بضاعتهم في رحالهم لعلهم يعرفونها اذا انقلبوا الى اهلهم لعلهم يرجعون');
INSERT INTO "imlaei_simple" VALUES(12,63,'12:63','فلما رجعوا الى ابيهم قالوا يا ابانا منع منا الكيل فارسل معنا اخانا نكتل وانا له لحافظون');
INSERT INTO "imlaei_simple" VALUES(12,64,'12:64','قال هل امنكم عليه الا كما امنتكم على اخيه من قبل فالله خير حافظا وهو ارحم الراحمين');
INSERT INTO "imlaei_simple" VALUES(12,65,'12:65','ولما فتحوا متاعهم وجدوا بضاعتهم ردت اليهم قالوا يا ابانا ما نبغي هذه بضاعتنا ردت الينا ونمير اهلنا ونحفظ اخانا ونزداد كيل بعير ذلك كيل يسير');
INSERT INTO "imlaei_simple" VALUES(12,66,'12:66','قال لن ارسله معكم حتى تؤتون موثقا من الله لتاتنني به الا ان يحاط بكم فلما اتوه موثقهم قال الله على ما نقول وكيل');
INSERT INTO "imlaei_simple" VALUES(12,67,'12:67','وقال يا بني لا تدخلوا من باب واحد وادخلوا من ابواب متفرقة وما اغني عنكم من الله من شيء ان الحكم الا لله عليه توكلت وعليه فليتوكل المتوكلون');
INSERT INTO "imlaei_simple" VALUES(12,68,'12:68','ولما دخلوا من حيث امرهم ابوهم ما كان يغني عنهم من الله من شيء الا حاجة في نفس يعقوب قضاها وانه لذو علم لما علمناه ولكن اكثر الناس لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(12,69,'12:69','ولما دخلوا على يوسف اوى اليه اخاه قال اني انا اخوك فلا تبتئس بما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(12,70,'12:70','فلما جهزهم بجهازهم جعل السقاية في رحل اخيه ثم اذن مؤذن ايتها العير انكم لسارقون');
INSERT INTO "imlaei_simple" VALUES(12,71,'12:71','قالوا واقبلوا عليهم ماذا تفقدون');
INSERT INTO "imlaei_simple" VALUES(12,72,'12:72','قالوا نفقد صواع الملك ولمن جاء به حمل بعير وانا به زعيم');
INSERT INTO "imlaei_simple" VALUES(12,73,'12:73','قالوا تالله لقد علمتم ما جئنا لنفسد في الارض وما كنا سارقين');
INSERT INTO "imlaei_simple" VALUES(12,74,'12:74','قالوا فما جزاؤه ان كنتم كاذبين');
INSERT INTO "imlaei_simple" VALUES(12,75,'12:75','قالوا جزاؤه من وجد في رحله فهو جزاؤه كذلك نجزي الظالمين');
INSERT INTO "imlaei_simple" VALUES(12,76,'12:76','فبدا باوعيتهم قبل وعاء اخيه ثم استخرجها من وعاء اخيه كذلك كدنا ليوسف ما كان لياخذ اخاه في دين الملك الا ان يشاء الله نرفع درجات من نشاء وفوق كل ذي علم عليم');
INSERT INTO "imlaei_simple" VALUES(12,77,'12:77','قالوا ان يسرق فقد سرق اخ له من قبل فاسرها يوسف في نفسه ولم يبدها لهم قال انتم شر مكانا والله اعلم بما تصفون');
INSERT INTO "imlaei_simple" VALUES(12,78,'12:78','قالوا يا ايها العزيز ان له ابا شيخا كبيرا فخذ احدنا مكانه انا نراك من المحسنين');
INSERT INTO "imlaei_simple" VALUES(12,79,'12:79','قال معاذ الله ان ناخذ الا من وجدنا متاعنا عنده انا اذا لظالمون');
INSERT INTO "imlaei_simple" VALUES(12,80,'12:80','فلما استياسوا منه خلصوا نجيا قال كبيرهم الم تعلموا ان اباكم قد اخذ عليكم موثقا من الله ومن قبل ما فرطتم في يوسف فلن ابرح الارض حتى ياذن لي ابي او يحكم الله لي وهو خير الحاكمين');
INSERT INTO "imlaei_simple" VALUES(12,81,'12:81','ارجعوا الى ابيكم فقولوا يا ابانا ان ابنك سرق وما شهدنا الا بما علمنا وما كنا للغيب حافظين');
INSERT INTO "imlaei_simple" VALUES(12,82,'12:82','واسال القرية التي كنا فيها والعير التي اقبلنا فيها وانا لصادقون');
INSERT INTO "imlaei_simple" VALUES(12,83,'12:83','قال بل سولت لكم انفسكم امرا فصبر جميل عسى الله ان ياتيني بهم جميعا انه هو العليم الحكيم');
INSERT INTO "imlaei_simple" VALUES(12,84,'12:84','وتولى عنهم وقال يا اسفى على يوسف وابيضت عيناه من الحزن فهو كظيم');
INSERT INTO "imlaei_simple" VALUES(12,85,'12:85','قالوا تالله تفتا تذكر يوسف حتى تكون حرضا او تكون من الهالكين');
INSERT INTO "imlaei_simple" VALUES(12,86,'12:86','قال انما اشكو بثي وحزني الى الله واعلم من الله ما لا تعلمون');
INSERT INTO "imlaei_simple" VALUES(12,87,'12:87','يا بني اذهبوا فتحسسوا من يوسف واخيه ولا تياسوا من روح الله انه لا يياس من روح الله الا القوم الكافرون');
INSERT INTO "imlaei_simple" VALUES(12,88,'12:88','فلما دخلوا عليه قالوا يا ايها العزيز مسنا واهلنا الضر وجئنا ببضاعة مزجاة فاوف لنا الكيل وتصدق علينا ان الله يجزي المتصدقين');
INSERT INTO "imlaei_simple" VALUES(12,89,'12:89','قال هل علمتم ما فعلتم بيوسف واخيه اذ انتم جاهلون');
INSERT INTO "imlaei_simple" VALUES(12,90,'12:90','قالوا اانك لانت يوسف قال انا يوسف وهذا اخي قد من الله علينا انه من يتق ويصبر فان الله لا يضيع اجر المحسنين');
INSERT INTO "imlaei_simple" VALUES(12,91,'12:91','قالوا تالله لقد اثرك الله علينا وان كنا لخاطئين');
INSERT INTO "imlaei_simple" VALUES(12,92,'12:92','قال لا تثريب عليكم اليوم يغفر الله لكم وهو ارحم الراحمين');
INSERT INTO "imlaei_simple" VALUES(12,93,'12:93','اذهبوا بقميصي هذا فالقوه على وجه ابي يات بصيرا واتوني باهلكم اجمعين');
INSERT INTO "imlaei_simple" VALUES(12,94,'12:94','ولما فصلت العير قال ابوهم اني لاجد ريح يوسف لولا ان تفندون');
INSERT INTO "imlaei_simple" VALUES(12,95,'12:95','قالوا تالله انك لفي ضلالك القديم');
INSERT INTO "imlaei_simple" VALUES(12,96,'12:96','فلما ان جاء البشير القاه على وجهه فارتد بصيرا قال الم اقل لكم اني اعلم من الله ما لا تعلمون');
INSERT INTO "imlaei_simple" VALUES(12,97,'12:97','قالوا يا ابانا استغفر لنا ذنوبنا انا كنا خاطئين');
INSERT INTO "imlaei_simple" VALUES(12,98,'12:98','قال سوف استغفر لكم ربي انه هو الغفور الرحيم');
INSERT INTO "imlaei_simple" VALUES(12,99,'12:99','فلما دخلوا على يوسف اوى اليه ابويه وقال ادخلوا مصر ان شاء الله امنين');
INSERT INTO "imlaei_simple" VALUES(12,100,'12:100','ورفع ابويه على العرش وخروا له سجدا وقال يا ابت هذا تاويل رؤياي من قبل قد جعلها ربي حقا وقد احسن بي اذ اخرجني من السجن وجاء بكم من البدو من بعد ان نزغ الشيطان بيني وبين اخوتي ان ربي لطيف لما يشاء انه هو العليم الحكيم');
INSERT INTO "imlaei_simple" VALUES(12,101,'12:101','رب قد اتيتني من الملك وعلمتني من تاويل الاحاديث فاطر السماوات والارض انت وليي في الدنيا والاخرة توفني مسلما والحقني بالصالحين');
INSERT INTO "imlaei_simple" VALUES(12,102,'12:102','ذلك من انباء الغيب نوحيه اليك وما كنت لديهم اذ اجمعوا امرهم وهم يمكرون');
INSERT INTO "imlaei_simple" VALUES(12,103,'12:103','وما اكثر الناس ولو حرصت بمؤمنين');
INSERT INTO "imlaei_simple" VALUES(12,104,'12:104','وما تسالهم عليه من اجر ان هو الا ذكر للعالمين');
INSERT INTO "imlaei_simple" VALUES(12,105,'12:105','وكاين من اية في السماوات والارض يمرون عليها وهم عنها معرضون');
INSERT INTO "imlaei_simple" VALUES(12,106,'12:106','وما يؤمن اكثرهم بالله الا وهم مشركون');
INSERT INTO "imlaei_simple" VALUES(12,107,'12:107','افامنوا ان تاتيهم غاشية من عذاب الله او تاتيهم الساعة بغتة وهم لا يشعرون');
INSERT INTO "imlaei_simple" VALUES(12,108,'12:108','قل هذه سبيلي ادعو الى الله على بصيرة انا ومن اتبعني وسبحان الله وما انا من المشركين');
INSERT INTO "imlaei_simple" VALUES(12,109,'12:109','وما ارسلنا من قبلك الا رجالا نوحي اليهم من اهل القرى افلم يسيروا في الارض فينظروا كيف كان عاقبة الذين من قبلهم ولدار الاخرة خير للذين اتقوا افلا تعقلون');
INSERT INTO "imlaei_simple" VALUES(12,110,'12:110','حتى اذا استياس الرسل وظنوا انهم قد كذبوا جاءهم نصرنا فنجي من نشاء ولا يرد باسنا عن القوم المجرمين');
INSERT INTO "imlaei_simple" VALUES(12,111,'12:111','لقد كان في قصصهم عبرة لاولي الالباب ما كان حديثا يفترى ولكن تصديق الذي بين يديه وتفصيل كل شيء وهدى ورحمة لقوم يؤمنون');
INSERT INTO "imlaei_simple" VALUES(13,1,'13:1','المر تلك ايات الكتاب والذي انزل اليك من ربك الحق ولكن اكثر الناس لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(13,2,'13:2','الله الذي رفع السماوات بغير عمد ترونها ثم استوى على العرش وسخر الشمس والقمر كل يجري لاجل مسمى يدبر الامر يفصل الايات لعلكم بلقاء ربكم توقنون');
INSERT INTO "imlaei_simple" VALUES(13,3,'13:3','وهو الذي مد الارض وجعل فيها رواسي وانهارا ومن كل الثمرات جعل فيها زوجين اثنين يغشي الليل النهار ان في ذلك لايات لقوم يتفكرون');
INSERT INTO "imlaei_simple" VALUES(13,4,'13:4','وفي الارض قطع متجاورات وجنات من اعناب وزرع ونخيل صنوان وغير صنوان يسقى بماء واحد ونفضل بعضها على بعض في الاكل ان في ذلك لايات لقوم يعقلون');
INSERT INTO "imlaei_simple" VALUES(13,5,'13:5','وان تعجب فعجب قولهم ااذا كنا ترابا اانا لفي خلق جديد اولئك الذين كفروا بربهم واولئك الاغلال في اعناقهم واولئك اصحاب النار هم فيها خالدون');
INSERT INTO "imlaei_simple" VALUES(13,6,'13:6','ويستعجلونك بالسيئة قبل الحسنة وقد خلت من قبلهم المثلات وان ربك لذو مغفرة للناس على ظلمهم وان ربك لشديد العقاب');
INSERT INTO "imlaei_simple" VALUES(13,7,'13:7','ويقول الذين كفروا لولا انزل عليه اية من ربه انما انت منذر ولكل قوم هاد');
INSERT INTO "imlaei_simple" VALUES(13,8,'13:8','الله يعلم ما تحمل كل انثى وما تغيض الارحام وما تزداد وكل شيء عنده بمقدار');
INSERT INTO "imlaei_simple" VALUES(13,9,'13:9','عالم الغيب والشهادة الكبير المتعال');
INSERT INTO "imlaei_simple" VALUES(13,10,'13:10','سواء منكم من اسر القول ومن جهر به ومن هو مستخف بالليل وسارب بالنهار');
INSERT INTO "imlaei_simple" VALUES(13,11,'13:11','له معقبات من بين يديه ومن خلفه يحفظونه من امر الله ان الله لا يغير ما بقوم حتى يغيروا ما بانفسهم واذا اراد الله بقوم سوءا فلا مرد له وما لهم من دونه من وال');
INSERT INTO "imlaei_simple" VALUES(13,12,'13:12','هو الذي يريكم البرق خوفا وطمعا وينشئ السحاب الثقال');
INSERT INTO "imlaei_simple" VALUES(13,13,'13:13','ويسبح الرعد بحمده والملائكة من خيفته ويرسل الصواعق فيصيب بها من يشاء وهم يجادلون في الله وهو شديد المحال');
INSERT INTO "imlaei_simple" VALUES(13,14,'13:14','له دعوة الحق والذين يدعون من دونه لا يستجيبون لهم بشيء الا كباسط كفيه الى الماء ليبلغ فاه وما هو ببالغه وما دعاء الكافرين الا في ضلال');
INSERT INTO "imlaei_simple" VALUES(13,15,'13:15','ولله يسجد من في السماوات والارض طوعا وكرها وظلالهم بالغدو والاصال');
INSERT INTO "imlaei_simple" VALUES(13,16,'13:16','قل من رب السماوات والارض قل الله قل افاتخذتم من دونه اولياء لا يملكون لانفسهم نفعا ولا ضرا قل هل يستوي الاعمى والبصير ام هل تستوي الظلمات والنور ام جعلوا لله شركاء خلقوا كخلقه فتشابه الخلق عليهم قل الله خالق كل شيء وهو الواحد القهار');
INSERT INTO "imlaei_simple" VALUES(13,17,'13:17','انزل من السماء ماء فسالت اودية بقدرها فاحتمل السيل زبدا رابيا ومما يوقدون عليه في النار ابتغاء حلية او متاع زبد مثله كذلك يضرب الله الحق والباطل فاما الزبد فيذهب جفاء واما ما ينفع الناس فيمكث في الارض كذلك يضرب الله الامثال');
INSERT INTO "imlaei_simple" VALUES(13,18,'13:18','للذين استجابوا لربهم الحسنى والذين لم يستجيبوا له لو ان لهم ما في الارض جميعا ومثله معه لافتدوا به اولئك لهم سوء الحساب وماواهم جهنم وبئس المهاد');
INSERT INTO "imlaei_simple" VALUES(13,19,'13:19','افمن يعلم انما انزل اليك من ربك الحق كمن هو اعمى انما يتذكر اولو الالباب');
INSERT INTO "imlaei_simple" VALUES(13,20,'13:20','الذين يوفون بعهد الله ولا ينقضون الميثاق');
INSERT INTO "imlaei_simple" VALUES(13,21,'13:21','والذين يصلون ما امر الله به ان يوصل ويخشون ربهم ويخافون سوء الحساب');
INSERT INTO "imlaei_simple" VALUES(13,22,'13:22','والذين صبروا ابتغاء وجه ربهم واقاموا الصلاة وانفقوا مما رزقناهم سرا وعلانية ويدرءون بالحسنة السيئة اولئك لهم عقبى الدار');
INSERT INTO "imlaei_simple" VALUES(13,23,'13:23','جنات عدن يدخلونها ومن صلح من ابائهم وازواجهم وذرياتهم والملائكة يدخلون عليهم من كل باب');
INSERT INTO "imlaei_simple" VALUES(13,24,'13:24','سلام عليكم بما صبرتم فنعم عقبى الدار');
INSERT INTO "imlaei_simple" VALUES(13,25,'13:25','والذين ينقضون عهد الله من بعد ميثاقه ويقطعون ما امر الله به ان يوصل ويفسدون في الارض اولئك لهم اللعنة ولهم سوء الدار');
INSERT INTO "imlaei_simple" VALUES(13,26,'13:26','الله يبسط الرزق لمن يشاء ويقدر وفرحوا بالحياة الدنيا وما الحياة الدنيا في الاخرة الا متاع');
INSERT INTO "imlaei_simple" VALUES(13,27,'13:27','ويقول الذين كفروا لولا انزل عليه اية من ربه قل ان الله يضل من يشاء ويهدي اليه من اناب');
INSERT INTO "imlaei_simple" VALUES(13,28,'13:28','الذين امنوا وتطمئن قلوبهم بذكر الله الا بذكر الله تطمئن القلوب');
INSERT INTO "imlaei_simple" VALUES(13,29,'13:29','الذين امنوا وعملوا الصالحات طوبى لهم وحسن ماب');
INSERT INTO "imlaei_simple" VALUES(13,30,'13:30','كذلك ارسلناك في امة قد خلت من قبلها امم لتتلو عليهم الذي اوحينا اليك وهم يكفرون بالرحمن قل هو ربي لا اله الا هو عليه توكلت واليه متاب');
INSERT INTO "imlaei_simple" VALUES(13,31,'13:31','ولو ان قرانا سيرت به الجبال او قطعت به الارض او كلم به الموتى بل لله الامر جميعا افلم يياس الذين امنوا ان لو يشاء الله لهدى الناس جميعا ولا يزال الذين كفروا تصيبهم بما صنعوا قارعة او تحل قريبا من دارهم حتى ياتي وعد الله ان الله لا يخلف الميعاد');
INSERT INTO "imlaei_simple" VALUES(13,32,'13:32','ولقد استهزئ برسل من قبلك فامليت للذين كفروا ثم اخذتهم فكيف كان عقاب');
INSERT INTO "imlaei_simple" VALUES(13,33,'13:33','افمن هو قائم على كل نفس بما كسبت وجعلوا لله شركاء قل سموهم ام تنبئونه بما لا يعلم في الارض ام بظاهر من القول بل زين للذين كفروا مكرهم وصدوا عن السبيل ومن يضلل الله فما له من هاد');
INSERT INTO "imlaei_simple" VALUES(13,34,'13:34','لهم عذاب في الحياة الدنيا ولعذاب الاخرة اشق وما لهم من الله من واق');
INSERT INTO "imlaei_simple" VALUES(13,35,'13:35','مثل الجنة التي وعد المتقون تجري من تحتها الانهار اكلها دائم وظلها تلك عقبى الذين اتقوا وعقبى الكافرين النار');
INSERT INTO "imlaei_simple" VALUES(13,36,'13:36','والذين اتيناهم الكتاب يفرحون بما انزل اليك ومن الاحزاب من ينكر بعضه قل انما امرت ان اعبد الله ولا اشرك به اليه ادعو واليه ماب');
INSERT INTO "imlaei_simple" VALUES(13,37,'13:37','وكذلك انزلناه حكما عربيا ولئن اتبعت اهواءهم بعدما جاءك من العلم ما لك من الله من ولي ولا واق');
INSERT INTO "imlaei_simple" VALUES(13,38,'13:38','ولقد ارسلنا رسلا من قبلك وجعلنا لهم ازواجا وذرية وما كان لرسول ان ياتي باية الا باذن الله لكل اجل كتاب');
INSERT INTO "imlaei_simple" VALUES(13,39,'13:39','يمحو الله ما يشاء ويثبت وعنده ام الكتاب');
INSERT INTO "imlaei_simple" VALUES(13,40,'13:40','وان ما نرينك بعض الذي نعدهم او نتوفينك فانما عليك البلاغ وعلينا الحساب');
INSERT INTO "imlaei_simple" VALUES(13,41,'13:41','اولم يروا انا ناتي الارض ننقصها من اطرافها والله يحكم لا معقب لحكمه وهو سريع الحساب');
INSERT INTO "imlaei_simple" VALUES(13,42,'13:42','وقد مكر الذين من قبلهم فلله المكر جميعا يعلم ما تكسب كل نفس وسيعلم الكفار لمن عقبى الدار');
INSERT INTO "imlaei_simple" VALUES(13,43,'13:43','ويقول الذين كفروا لست مرسلا قل كفى بالله شهيدا بيني وبينكم ومن عنده علم الكتاب');
INSERT INTO "imlaei_simple" VALUES(14,1,'14:1','الر كتاب انزلناه اليك لتخرج الناس من الظلمات الى النور باذن ربهم الى صراط العزيز الحميد');
INSERT INTO "imlaei_simple" VALUES(14,2,'14:2','الله الذي له ما في السماوات وما في الارض وويل للكافرين من عذاب شديد');
INSERT INTO "imlaei_simple" VALUES(14,3,'14:3','الذين يستحبون الحياة الدنيا على الاخرة ويصدون عن سبيل الله ويبغونها عوجا اولئك في ضلال بعيد');
INSERT INTO "imlaei_simple" VALUES(14,4,'14:4','وما ارسلنا من رسول الا بلسان قومه ليبين لهم فيضل الله من يشاء ويهدي من يشاء وهو العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(14,5,'14:5','ولقد ارسلنا موسى باياتنا ان اخرج قومك من الظلمات الى النور وذكرهم بايام الله ان في ذلك لايات لكل صبار شكور');
INSERT INTO "imlaei_simple" VALUES(14,6,'14:6','واذ قال موسى لقومه اذكروا نعمة الله عليكم اذ انجاكم من ال فرعون يسومونكم سوء العذاب ويذبحون ابناءكم ويستحيون نساءكم وفي ذلكم بلاء من ربكم عظيم');
INSERT INTO "imlaei_simple" VALUES(14,7,'14:7','واذ تاذن ربكم لئن شكرتم لازيدنكم ولئن كفرتم ان عذابي لشديد');
INSERT INTO "imlaei_simple" VALUES(14,8,'14:8','وقال موسى ان تكفروا انتم ومن في الارض جميعا فان الله لغني حميد');
INSERT INTO "imlaei_simple" VALUES(14,9,'14:9','الم ياتكم نبا الذين من قبلكم قوم نوح وعاد وثمود والذين من بعدهم لا يعلمهم الا الله جاءتهم رسلهم بالبينات فردوا ايديهم في افواههم وقالوا انا كفرنا بما ارسلتم به وانا لفي شك مما تدعوننا اليه مريب');
INSERT INTO "imlaei_simple" VALUES(14,10,'14:10','قالت رسلهم افي الله شك فاطر السماوات والارض يدعوكم ليغفر لكم من ذنوبكم ويؤخركم الى اجل مسمى قالوا ان انتم الا بشر مثلنا تريدون ان تصدونا عما كان يعبد اباؤنا فاتونا بسلطان مبين');
INSERT INTO "imlaei_simple" VALUES(14,11,'14:11','قالت لهم رسلهم ان نحن الا بشر مثلكم ولكن الله يمن على من يشاء من عباده وما كان لنا ان ناتيكم بسلطان الا باذن الله وعلى الله فليتوكل المؤمنون');
INSERT INTO "imlaei_simple" VALUES(14,12,'14:12','وما لنا الا نتوكل على الله وقد هدانا سبلنا ولنصبرن على ما اذيتمونا وعلى الله فليتوكل المتوكلون');
INSERT INTO "imlaei_simple" VALUES(14,13,'14:13','وقال الذين كفروا لرسلهم لنخرجنكم من ارضنا او لتعودن في ملتنا فاوحى اليهم ربهم لنهلكن الظالمين');
INSERT INTO "imlaei_simple" VALUES(14,14,'14:14','ولنسكننكم الارض من بعدهم ذلك لمن خاف مقامي وخاف وعيد');
INSERT INTO "imlaei_simple" VALUES(14,15,'14:15','واستفتحوا وخاب كل جبار عنيد');
INSERT INTO "imlaei_simple" VALUES(14,16,'14:16','من ورائه جهنم ويسقى من ماء صديد');
INSERT INTO "imlaei_simple" VALUES(14,17,'14:17','يتجرعه ولا يكاد يسيغه وياتيه الموت من كل مكان وما هو بميت ومن ورائه عذاب غليظ');
INSERT INTO "imlaei_simple" VALUES(14,18,'14:18','مثل الذين كفروا بربهم اعمالهم كرماد اشتدت به الريح في يوم عاصف لا يقدرون مما كسبوا على شيء ذلك هو الضلال البعيد');
INSERT INTO "imlaei_simple" VALUES(14,19,'14:19','الم تر ان الله خلق السماوات والارض بالحق ان يشا يذهبكم ويات بخلق جديد');
INSERT INTO "imlaei_simple" VALUES(14,20,'14:20','وما ذلك على الله بعزيز');
INSERT INTO "imlaei_simple" VALUES(14,21,'14:21','وبرزوا لله جميعا فقال الضعفاء للذين استكبروا انا كنا لكم تبعا فهل انتم مغنون عنا من عذاب الله من شيء قالوا لو هدانا الله لهديناكم سواء علينا اجزعنا ام صبرنا ما لنا من محيص');
INSERT INTO "imlaei_simple" VALUES(14,22,'14:22','وقال الشيطان لما قضي الامر ان الله وعدكم وعد الحق ووعدتكم فاخلفتكم وما كان لي عليكم من سلطان الا ان دعوتكم فاستجبتم لي فلا تلوموني ولوموا انفسكم ما انا بمصرخكم وما انتم بمصرخي اني كفرت بما اشركتمون من قبل ان الظالمين لهم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(14,23,'14:23','وادخل الذين امنوا وعملوا الصالحات جنات تجري من تحتها الانهار خالدين فيها باذن ربهم تحيتهم فيها سلام');
INSERT INTO "imlaei_simple" VALUES(14,24,'14:24','الم تر كيف ضرب الله مثلا كلمة طيبة كشجرة طيبة اصلها ثابت وفرعها في السماء');
INSERT INTO "imlaei_simple" VALUES(14,25,'14:25','تؤتي اكلها كل حين باذن ربها ويضرب الله الامثال للناس لعلهم يتذكرون');
INSERT INTO "imlaei_simple" VALUES(14,26,'14:26','ومثل كلمة خبيثة كشجرة خبيثة اجتثت من فوق الارض ما لها من قرار');
INSERT INTO "imlaei_simple" VALUES(14,27,'14:27','يثبت الله الذين امنوا بالقول الثابت في الحياة الدنيا وفي الاخرة ويضل الله الظالمين ويفعل الله ما يشاء');
INSERT INTO "imlaei_simple" VALUES(14,28,'14:28','الم تر الى الذين بدلوا نعمت الله كفرا واحلوا قومهم دار البوار');
INSERT INTO "imlaei_simple" VALUES(14,29,'14:29','جهنم يصلونها وبئس القرار');
INSERT INTO "imlaei_simple" VALUES(14,30,'14:30','وجعلوا لله اندادا ليضلوا عن سبيله قل تمتعوا فان مصيركم الى النار');
INSERT INTO "imlaei_simple" VALUES(14,31,'14:31','قل لعبادي الذين امنوا يقيموا الصلاة وينفقوا مما رزقناهم سرا وعلانية من قبل ان ياتي يوم لا بيع فيه ولا خلال');
INSERT INTO "imlaei_simple" VALUES(14,32,'14:32','الله الذي خلق السماوات والارض وانزل من السماء ماء فاخرج به من الثمرات رزقا لكم وسخر لكم الفلك لتجري في البحر بامره وسخر لكم الانهار');
INSERT INTO "imlaei_simple" VALUES(14,33,'14:33','وسخر لكم الشمس والقمر دائبين وسخر لكم الليل والنهار');
INSERT INTO "imlaei_simple" VALUES(14,34,'14:34','واتاكم من كل ما سالتموه وان تعدوا نعمت الله لا تحصوها ان الانسان لظلوم كفار');
INSERT INTO "imlaei_simple" VALUES(14,35,'14:35','واذ قال ابراهيم رب اجعل هذا البلد امنا واجنبني وبني ان نعبد الاصنام');
INSERT INTO "imlaei_simple" VALUES(14,36,'14:36','رب انهن اضللن كثيرا من الناس فمن تبعني فانه مني ومن عصاني فانك غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(14,37,'14:37','ربنا اني اسكنت من ذريتي بواد غير ذي زرع عند بيتك المحرم ربنا ليقيموا الصلاة فاجعل افئدة من الناس تهوي اليهم وارزقهم من الثمرات لعلهم يشكرون');
INSERT INTO "imlaei_simple" VALUES(14,38,'14:38','ربنا انك تعلم ما نخفي وما نعلن وما يخفى على الله من شيء في الارض ولا في السماء');
INSERT INTO "imlaei_simple" VALUES(14,39,'14:39','الحمد لله الذي وهب لي على الكبر اسماعيل واسحاق ان ربي لسميع الدعاء');
INSERT INTO "imlaei_simple" VALUES(14,40,'14:40','رب اجعلني مقيم الصلاة ومن ذريتي ربنا وتقبل دعاء');
INSERT INTO "imlaei_simple" VALUES(14,41,'14:41','ربنا اغفر لي ولوالدي وللمؤمنين يوم يقوم الحساب');
INSERT INTO "imlaei_simple" VALUES(14,42,'14:42','ولا تحسبن الله غافلا عما يعمل الظالمون انما يؤخرهم ليوم تشخص فيه الابصار');
INSERT INTO "imlaei_simple" VALUES(14,43,'14:43','مهطعين مقنعي رءوسهم لا يرتد اليهم طرفهم وافئدتهم هواء');
INSERT INTO "imlaei_simple" VALUES(14,44,'14:44','وانذر الناس يوم ياتيهم العذاب فيقول الذين ظلموا ربنا اخرنا الى اجل قريب نجب دعوتك ونتبع الرسل اولم تكونوا اقسمتم من قبل ما لكم من زوال');
INSERT INTO "imlaei_simple" VALUES(14,45,'14:45','وسكنتم في مساكن الذين ظلموا انفسهم وتبين لكم كيف فعلنا بهم وضربنا لكم الامثال');
INSERT INTO "imlaei_simple" VALUES(14,46,'14:46','وقد مكروا مكرهم وعند الله مكرهم وان كان مكرهم لتزول منه الجبال');
INSERT INTO "imlaei_simple" VALUES(14,47,'14:47','فلا تحسبن الله مخلف وعده رسله ان الله عزيز ذو انتقام');
INSERT INTO "imlaei_simple" VALUES(14,48,'14:48','يوم تبدل الارض غير الارض والسماوات وبرزوا لله الواحد القهار');
INSERT INTO "imlaei_simple" VALUES(14,49,'14:49','وترى المجرمين يومئذ مقرنين في الاصفاد');
INSERT INTO "imlaei_simple" VALUES(14,50,'14:50','سرابيلهم من قطران وتغشى وجوههم النار');
INSERT INTO "imlaei_simple" VALUES(14,51,'14:51','ليجزي الله كل نفس ما كسبت ان الله سريع الحساب');
INSERT INTO "imlaei_simple" VALUES(14,52,'14:52','هذا بلاغ للناس ولينذروا به وليعلموا انما هو اله واحد وليذكر اولو الالباب');
INSERT INTO "imlaei_simple" VALUES(15,1,'15:1','الر تلك ايات الكتاب وقران مبين');
INSERT INTO "imlaei_simple" VALUES(15,2,'15:2','ربما يود الذين كفروا لو كانوا مسلمين');
INSERT INTO "imlaei_simple" VALUES(15,3,'15:3','ذرهم ياكلوا ويتمتعوا ويلههم الامل فسوف يعلمون');
INSERT INTO "imlaei_simple" VALUES(15,4,'15:4','وما اهلكنا من قرية الا ولها كتاب معلوم');
INSERT INTO "imlaei_simple" VALUES(15,5,'15:5','ما تسبق من امة اجلها وما يستاخرون');
INSERT INTO "imlaei_simple" VALUES(15,6,'15:6','وقالوا يا ايها الذي نزل عليه الذكر انك لمجنون');
INSERT INTO "imlaei_simple" VALUES(15,7,'15:7','لو ما تاتينا بالملائكة ان كنت من الصادقين');
INSERT INTO "imlaei_simple" VALUES(15,8,'15:8','ما ننزل الملائكة الا بالحق وما كانوا اذا منظرين');
INSERT INTO "imlaei_simple" VALUES(15,9,'15:9','انا نحن نزلنا الذكر وانا له لحافظون');
INSERT INTO "imlaei_simple" VALUES(15,10,'15:10','ولقد ارسلنا من قبلك في شيع الاولين');
INSERT INTO "imlaei_simple" VALUES(15,11,'15:11','وما ياتيهم من رسول الا كانوا به يستهزئون');
INSERT INTO "imlaei_simple" VALUES(15,12,'15:12','كذلك نسلكه في قلوب المجرمين');
INSERT INTO "imlaei_simple" VALUES(15,13,'15:13','لا يؤمنون به وقد خلت سنة الاولين');
INSERT INTO "imlaei_simple" VALUES(15,14,'15:14','ولو فتحنا عليهم بابا من السماء فظلوا فيه يعرجون');
INSERT INTO "imlaei_simple" VALUES(15,15,'15:15','لقالوا انما سكرت ابصارنا بل نحن قوم مسحورون');
INSERT INTO "imlaei_simple" VALUES(15,16,'15:16','ولقد جعلنا في السماء بروجا وزيناها للناظرين');
INSERT INTO "imlaei_simple" VALUES(15,17,'15:17','وحفظناها من كل شيطان رجيم');
INSERT INTO "imlaei_simple" VALUES(15,18,'15:18','الا من استرق السمع فاتبعه شهاب مبين');
INSERT INTO "imlaei_simple" VALUES(15,19,'15:19','والارض مددناها والقينا فيها رواسي وانبتنا فيها من كل شيء موزون');
INSERT INTO "imlaei_simple" VALUES(15,20,'15:20','وجعلنا لكم فيها معايش ومن لستم له برازقين');
INSERT INTO "imlaei_simple" VALUES(15,21,'15:21','وان من شيء الا عندنا خزائنه وما ننزله الا بقدر معلوم');
INSERT INTO "imlaei_simple" VALUES(15,22,'15:22','وارسلنا الرياح لواقح فانزلنا من السماء ماء فاسقيناكموه وما انتم له بخازنين');
INSERT INTO "imlaei_simple" VALUES(15,23,'15:23','وانا لنحن نحيي ونميت ونحن الوارثون');
INSERT INTO "imlaei_simple" VALUES(15,24,'15:24','ولقد علمنا المستقدمين منكم ولقد علمنا المستاخرين');
INSERT INTO "imlaei_simple" VALUES(15,25,'15:25','وان ربك هو يحشرهم انه حكيم عليم');
INSERT INTO "imlaei_simple" VALUES(15,26,'15:26','ولقد خلقنا الانسان من صلصال من حما مسنون');
INSERT INTO "imlaei_simple" VALUES(15,27,'15:27','والجان خلقناه من قبل من نار السموم');
INSERT INTO "imlaei_simple" VALUES(15,28,'15:28','واذ قال ربك للملائكة اني خالق بشرا من صلصال من حما مسنون');
INSERT INTO "imlaei_simple" VALUES(15,29,'15:29','فاذا سويته ونفخت فيه من روحي فقعوا له ساجدين');
INSERT INTO "imlaei_simple" VALUES(15,30,'15:30','فسجد الملائكة كلهم اجمعون');
INSERT INTO "imlaei_simple" VALUES(15,31,'15:31','الا ابليس ابى ان يكون مع الساجدين');
INSERT INTO "imlaei_simple" VALUES(15,32,'15:32','قال يا ابليس ما لك الا تكون مع الساجدين');
INSERT INTO "imlaei_simple" VALUES(15,33,'15:33','قال لم اكن لاسجد لبشر خلقته من صلصال من حما مسنون');
INSERT INTO "imlaei_simple" VALUES(15,34,'15:34','قال فاخرج منها فانك رجيم');
INSERT INTO "imlaei_simple" VALUES(15,35,'15:35','وان عليك اللعنة الى يوم الدين');
INSERT INTO "imlaei_simple" VALUES(15,36,'15:36','قال رب فانظرني الى يوم يبعثون');
INSERT INTO "imlaei_simple" VALUES(15,37,'15:37','قال فانك من المنظرين');
INSERT INTO "imlaei_simple" VALUES(15,38,'15:38','الى يوم الوقت المعلوم');
INSERT INTO "imlaei_simple" VALUES(15,39,'15:39','قال رب بما اغويتني لازينن لهم في الارض ولاغوينهم اجمعين');
INSERT INTO "imlaei_simple" VALUES(15,40,'15:40','الا عبادك منهم المخلصين');
INSERT INTO "imlaei_simple" VALUES(15,41,'15:41','قال هذا صراط علي مستقيم');
INSERT INTO "imlaei_simple" VALUES(15,42,'15:42','ان عبادي ليس لك عليهم سلطان الا من اتبعك من الغاوين');
INSERT INTO "imlaei_simple" VALUES(15,43,'15:43','وان جهنم لموعدهم اجمعين');
INSERT INTO "imlaei_simple" VALUES(15,44,'15:44','لها سبعة ابواب لكل باب منهم جزء مقسوم');
INSERT INTO "imlaei_simple" VALUES(15,45,'15:45','ان المتقين في جنات وعيون');
INSERT INTO "imlaei_simple" VALUES(15,46,'15:46','ادخلوها بسلام امنين');
INSERT INTO "imlaei_simple" VALUES(15,47,'15:47','ونزعنا ما في صدورهم من غل اخوانا على سرر متقابلين');
INSERT INTO "imlaei_simple" VALUES(15,48,'15:48','لا يمسهم فيها نصب وما هم منها بمخرجين');
INSERT INTO "imlaei_simple" VALUES(15,49,'15:49','نبئ عبادي اني انا الغفور الرحيم');
INSERT INTO "imlaei_simple" VALUES(15,50,'15:50','وان عذابي هو العذاب الاليم');
INSERT INTO "imlaei_simple" VALUES(15,51,'15:51','ونبئهم عن ضيف ابراهيم');
INSERT INTO "imlaei_simple" VALUES(15,52,'15:52','اذ دخلوا عليه فقالوا سلاما قال انا منكم وجلون');
INSERT INTO "imlaei_simple" VALUES(15,53,'15:53','قالوا لا توجل انا نبشرك بغلام عليم');
INSERT INTO "imlaei_simple" VALUES(15,54,'15:54','قال ابشرتموني على ان مسني الكبر فبم تبشرون');
INSERT INTO "imlaei_simple" VALUES(15,55,'15:55','قالوا بشرناك بالحق فلا تكن من القانطين');
INSERT INTO "imlaei_simple" VALUES(15,56,'15:56','قال ومن يقنط من رحمة ربه الا الضالون');
INSERT INTO "imlaei_simple" VALUES(15,57,'15:57','قال فما خطبكم ايها المرسلون');
INSERT INTO "imlaei_simple" VALUES(15,58,'15:58','قالوا انا ارسلنا الى قوم مجرمين');
INSERT INTO "imlaei_simple" VALUES(15,59,'15:59','الا ال لوط انا لمنجوهم اجمعين');
INSERT INTO "imlaei_simple" VALUES(15,60,'15:60','الا امراته قدرنا انها لمن الغابرين');
INSERT INTO "imlaei_simple" VALUES(15,61,'15:61','فلما جاء ال لوط المرسلون');
INSERT INTO "imlaei_simple" VALUES(15,62,'15:62','قال انكم قوم منكرون');
INSERT INTO "imlaei_simple" VALUES(15,63,'15:63','قالوا بل جئناك بما كانوا فيه يمترون');
INSERT INTO "imlaei_simple" VALUES(15,64,'15:64','واتيناك بالحق وانا لصادقون');
INSERT INTO "imlaei_simple" VALUES(15,65,'15:65','فاسر باهلك بقطع من الليل واتبع ادبارهم ولا يلتفت منكم احد وامضوا حيث تؤمرون');
INSERT INTO "imlaei_simple" VALUES(15,66,'15:66','وقضينا اليه ذلك الامر ان دابر هؤلاء مقطوع مصبحين');
INSERT INTO "imlaei_simple" VALUES(15,67,'15:67','وجاء اهل المدينة يستبشرون');
INSERT INTO "imlaei_simple" VALUES(15,68,'15:68','قال ان هؤلاء ضيفي فلا تفضحون');
INSERT INTO "imlaei_simple" VALUES(15,69,'15:69','واتقوا الله ولا تخزون');
INSERT INTO "imlaei_simple" VALUES(15,70,'15:70','قالوا اولم ننهك عن العالمين');
INSERT INTO "imlaei_simple" VALUES(15,71,'15:71','قال هؤلاء بناتي ان كنتم فاعلين');
INSERT INTO "imlaei_simple" VALUES(15,72,'15:72','لعمرك انهم لفي سكرتهم يعمهون');
INSERT INTO "imlaei_simple" VALUES(15,73,'15:73','فاخذتهم الصيحة مشرقين');
INSERT INTO "imlaei_simple" VALUES(15,74,'15:74','فجعلنا عاليها سافلها وامطرنا عليهم حجارة من سجيل');
INSERT INTO "imlaei_simple" VALUES(15,75,'15:75','ان في ذلك لايات للمتوسمين');
INSERT INTO "imlaei_simple" VALUES(15,76,'15:76','وانها لبسبيل مقيم');
INSERT INTO "imlaei_simple" VALUES(15,77,'15:77','ان في ذلك لاية للمؤمنين');
INSERT INTO "imlaei_simple" VALUES(15,78,'15:78','وان كان اصحاب الايكة لظالمين');
INSERT INTO "imlaei_simple" VALUES(15,79,'15:79','فانتقمنا منهم وانهما لبامام مبين');
INSERT INTO "imlaei_simple" VALUES(15,80,'15:80','ولقد كذب اصحاب الحجر المرسلين');
INSERT INTO "imlaei_simple" VALUES(15,81,'15:81','واتيناهم اياتنا فكانوا عنها معرضين');
INSERT INTO "imlaei_simple" VALUES(15,82,'15:82','وكانوا ينحتون من الجبال بيوتا امنين');
INSERT INTO "imlaei_simple" VALUES(15,83,'15:83','فاخذتهم الصيحة مصبحين');
INSERT INTO "imlaei_simple" VALUES(15,84,'15:84','فما اغنى عنهم ما كانوا يكسبون');
INSERT INTO "imlaei_simple" VALUES(15,85,'15:85','وما خلقنا السماوات والارض وما بينهما الا بالحق وان الساعة لاتية فاصفح الصفح الجميل');
INSERT INTO "imlaei_simple" VALUES(15,86,'15:86','ان ربك هو الخلاق العليم');
INSERT INTO "imlaei_simple" VALUES(15,87,'15:87','ولقد اتيناك سبعا من المثاني والقران العظيم');
INSERT INTO "imlaei_simple" VALUES(15,88,'15:88','لا تمدن عينيك الى ما متعنا به ازواجا منهم ولا تحزن عليهم واخفض جناحك للمؤمنين');
INSERT INTO "imlaei_simple" VALUES(15,89,'15:89','وقل اني انا النذير المبين');
INSERT INTO "imlaei_simple" VALUES(15,90,'15:90','كما انزلنا على المقتسمين');
INSERT INTO "imlaei_simple" VALUES(15,91,'15:91','الذين جعلوا القران عضين');
INSERT INTO "imlaei_simple" VALUES(15,92,'15:92','فوربك لنسالنهم اجمعين');
INSERT INTO "imlaei_simple" VALUES(15,93,'15:93','عما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(15,94,'15:94','فاصدع بما تؤمر واعرض عن المشركين');
INSERT INTO "imlaei_simple" VALUES(15,95,'15:95','انا كفيناك المستهزئين');
INSERT INTO "imlaei_simple" VALUES(15,96,'15:96','الذين يجعلون مع الله الها اخر فسوف يعلمون');
INSERT INTO "imlaei_simple" VALUES(15,97,'15:97','ولقد نعلم انك يضيق صدرك بما يقولون');
INSERT INTO "imlaei_simple" VALUES(15,98,'15:98','فسبح بحمد ربك وكن من الساجدين');
INSERT INTO "imlaei_simple" VALUES(15,99,'15:99','واعبد ربك حتى ياتيك اليقين');
INSERT INTO "imlaei_simple" VALUES(16,1,'16:1','اتى امر الله فلا تستعجلوه سبحانه وتعالى عما يشركون');
INSERT INTO "imlaei_simple" VALUES(16,2,'16:2','ينزل الملائكة بالروح من امره على من يشاء من عباده ان انذروا انه لا اله الا انا فاتقون');
INSERT INTO "imlaei_simple" VALUES(16,3,'16:3','خلق السماوات والارض بالحق تعالى عما يشركون');
INSERT INTO "imlaei_simple" VALUES(16,4,'16:4','خلق الانسان من نطفة فاذا هو خصيم مبين');
INSERT INTO "imlaei_simple" VALUES(16,5,'16:5','والانعام خلقها لكم فيها دفء ومنافع ومنها تاكلون');
INSERT INTO "imlaei_simple" VALUES(16,6,'16:6','ولكم فيها جمال حين تريحون وحين تسرحون');
INSERT INTO "imlaei_simple" VALUES(16,7,'16:7','وتحمل اثقالكم الى بلد لم تكونوا بالغيه الا بشق الانفس ان ربكم لرءوف رحيم');
INSERT INTO "imlaei_simple" VALUES(16,8,'16:8','والخيل والبغال والحمير لتركبوها وزينة ويخلق ما لا تعلمون');
INSERT INTO "imlaei_simple" VALUES(16,9,'16:9','وعلى الله قصد السبيل ومنها جائر ولو شاء لهداكم اجمعين');
INSERT INTO "imlaei_simple" VALUES(16,10,'16:10','هو الذي انزل من السماء ماء لكم منه شراب ومنه شجر فيه تسيمون');
INSERT INTO "imlaei_simple" VALUES(16,11,'16:11','ينبت لكم به الزرع والزيتون والنخيل والاعناب ومن كل الثمرات ان في ذلك لاية لقوم يتفكرون');
INSERT INTO "imlaei_simple" VALUES(16,12,'16:12','وسخر لكم الليل والنهار والشمس والقمر والنجوم مسخرات بامره ان في ذلك لايات لقوم يعقلون');
INSERT INTO "imlaei_simple" VALUES(16,13,'16:13','وما ذرا لكم في الارض مختلفا الوانه ان في ذلك لاية لقوم يذكرون');
INSERT INTO "imlaei_simple" VALUES(16,14,'16:14','وهو الذي سخر البحر لتاكلوا منه لحما طريا وتستخرجوا منه حلية تلبسونها وترى الفلك مواخر فيه ولتبتغوا من فضله ولعلكم تشكرون');
INSERT INTO "imlaei_simple" VALUES(16,15,'16:15','والقى في الارض رواسي ان تميد بكم وانهارا وسبلا لعلكم تهتدون');
INSERT INTO "imlaei_simple" VALUES(16,16,'16:16','وعلامات وبالنجم هم يهتدون');
INSERT INTO "imlaei_simple" VALUES(16,17,'16:17','افمن يخلق كمن لا يخلق افلا تذكرون');
INSERT INTO "imlaei_simple" VALUES(16,18,'16:18','وان تعدوا نعمة الله لا تحصوها ان الله لغفور رحيم');
INSERT INTO "imlaei_simple" VALUES(16,19,'16:19','والله يعلم ما تسرون وما تعلنون');
INSERT INTO "imlaei_simple" VALUES(16,20,'16:20','والذين يدعون من دون الله لا يخلقون شيئا وهم يخلقون');
INSERT INTO "imlaei_simple" VALUES(16,21,'16:21','اموات غير احياء وما يشعرون ايان يبعثون');
INSERT INTO "imlaei_simple" VALUES(16,22,'16:22','الهكم اله واحد فالذين لا يؤمنون بالاخرة قلوبهم منكرة وهم مستكبرون');
INSERT INTO "imlaei_simple" VALUES(16,23,'16:23','لا جرم ان الله يعلم ما يسرون وما يعلنون انه لا يحب المستكبرين');
INSERT INTO "imlaei_simple" VALUES(16,24,'16:24','واذا قيل لهم ماذا انزل ربكم قالوا اساطير الاولين');
INSERT INTO "imlaei_simple" VALUES(16,25,'16:25','ليحملوا اوزارهم كاملة يوم القيامة ومن اوزار الذين يضلونهم بغير علم الا ساء ما يزرون');
INSERT INTO "imlaei_simple" VALUES(16,26,'16:26','قد مكر الذين من قبلهم فاتى الله بنيانهم من القواعد فخر عليهم السقف من فوقهم واتاهم العذاب من حيث لا يشعرون');
INSERT INTO "imlaei_simple" VALUES(16,27,'16:27','ثم يوم القيامة يخزيهم ويقول اين شركائي الذين كنتم تشاقون فيهم قال الذين اوتوا العلم ان الخزي اليوم والسوء على الكافرين');
INSERT INTO "imlaei_simple" VALUES(16,28,'16:28','الذين تتوفاهم الملائكة ظالمي انفسهم فالقوا السلم ما كنا نعمل من سوء بلى ان الله عليم بما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(16,29,'16:29','فادخلوا ابواب جهنم خالدين فيها فلبئس مثوى المتكبرين');
INSERT INTO "imlaei_simple" VALUES(16,30,'16:30','وقيل للذين اتقوا ماذا انزل ربكم قالوا خيرا للذين احسنوا في هذه الدنيا حسنة ولدار الاخرة خير ولنعم دار المتقين');
INSERT INTO "imlaei_simple" VALUES(16,31,'16:31','جنات عدن يدخلونها تجري من تحتها الانهار لهم فيها ما يشاءون كذلك يجزي الله المتقين');
INSERT INTO "imlaei_simple" VALUES(16,32,'16:32','الذين تتوفاهم الملائكة طيبين يقولون سلام عليكم ادخلوا الجنة بما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(16,33,'16:33','هل ينظرون الا ان تاتيهم الملائكة او ياتي امر ربك كذلك فعل الذين من قبلهم وما ظلمهم الله ولكن كانوا انفسهم يظلمون');
INSERT INTO "imlaei_simple" VALUES(16,34,'16:34','فاصابهم سيئات ما عملوا وحاق بهم ما كانوا به يستهزئون');
INSERT INTO "imlaei_simple" VALUES(16,35,'16:35','وقال الذين اشركوا لو شاء الله ما عبدنا من دونه من شيء نحن ولا اباؤنا ولا حرمنا من دونه من شيء كذلك فعل الذين من قبلهم فهل على الرسل الا البلاغ المبين');
INSERT INTO "imlaei_simple" VALUES(16,36,'16:36','ولقد بعثنا في كل امة رسولا ان اعبدوا الله واجتنبوا الطاغوت فمنهم من هدى الله ومنهم من حقت عليه الضلالة فسيروا في الارض فانظروا كيف كان عاقبة المكذبين');
INSERT INTO "imlaei_simple" VALUES(16,37,'16:37','ان تحرص على هداهم فان الله لا يهدي من يضل وما لهم من ناصرين');
INSERT INTO "imlaei_simple" VALUES(16,38,'16:38','واقسموا بالله جهد ايمانهم لا يبعث الله من يموت بلى وعدا عليه حقا ولكن اكثر الناس لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(16,39,'16:39','ليبين لهم الذي يختلفون فيه وليعلم الذين كفروا انهم كانوا كاذبين');
INSERT INTO "imlaei_simple" VALUES(16,40,'16:40','انما قولنا لشيء اذا اردناه ان نقول له كن فيكون');
INSERT INTO "imlaei_simple" VALUES(16,41,'16:41','والذين هاجروا في الله من بعد ما ظلموا لنبوئنهم في الدنيا حسنة ولاجر الاخرة اكبر لو كانوا يعلمون');
INSERT INTO "imlaei_simple" VALUES(16,42,'16:42','الذين صبروا وعلى ربهم يتوكلون');
INSERT INTO "imlaei_simple" VALUES(16,43,'16:43','وما ارسلنا من قبلك الا رجالا نوحي اليهم فاسالوا اهل الذكر ان كنتم لا تعلمون');
INSERT INTO "imlaei_simple" VALUES(16,44,'16:44','بالبينات والزبر وانزلنا اليك الذكر لتبين للناس ما نزل اليهم ولعلهم يتفكرون');
INSERT INTO "imlaei_simple" VALUES(16,45,'16:45','افامن الذين مكروا السيئات ان يخسف الله بهم الارض او ياتيهم العذاب من حيث لا يشعرون');
INSERT INTO "imlaei_simple" VALUES(16,46,'16:46','او ياخذهم في تقلبهم فما هم بمعجزين');
INSERT INTO "imlaei_simple" VALUES(16,47,'16:47','او ياخذهم على تخوف فان ربكم لرءوف رحيم');
INSERT INTO "imlaei_simple" VALUES(16,48,'16:48','اولم يروا الى ما خلق الله من شيء يتفيا ظلاله عن اليمين والشمائل سجدا لله وهم داخرون');
INSERT INTO "imlaei_simple" VALUES(16,49,'16:49','ولله يسجد ما في السماوات وما في الارض من دابة والملائكة وهم لا يستكبرون');
INSERT INTO "imlaei_simple" VALUES(16,50,'16:50','يخافون ربهم من فوقهم ويفعلون ما يؤمرون');
INSERT INTO "imlaei_simple" VALUES(16,51,'16:51','وقال الله لا تتخذوا الهين اثنين انما هو اله واحد فاياي فارهبون');
INSERT INTO "imlaei_simple" VALUES(16,52,'16:52','وله ما في السماوات والارض وله الدين واصبا افغير الله تتقون');
INSERT INTO "imlaei_simple" VALUES(16,53,'16:53','وما بكم من نعمة فمن الله ثم اذا مسكم الضر فاليه تجارون');
INSERT INTO "imlaei_simple" VALUES(16,54,'16:54','ثم اذا كشف الضر عنكم اذا فريق منكم بربهم يشركون');
INSERT INTO "imlaei_simple" VALUES(16,55,'16:55','ليكفروا بما اتيناهم فتمتعوا فسوف تعلمون');
INSERT INTO "imlaei_simple" VALUES(16,56,'16:56','ويجعلون لما لا يعلمون نصيبا مما رزقناهم تالله لتسالن عما كنتم تفترون');
INSERT INTO "imlaei_simple" VALUES(16,57,'16:57','ويجعلون لله البنات سبحانه ولهم ما يشتهون');
INSERT INTO "imlaei_simple" VALUES(16,58,'16:58','واذا بشر احدهم بالانثى ظل وجهه مسودا وهو كظيم');
INSERT INTO "imlaei_simple" VALUES(16,59,'16:59','يتوارى من القوم من سوء ما بشر به ايمسكه على هون ام يدسه في التراب الا ساء ما يحكمون');
INSERT INTO "imlaei_simple" VALUES(16,60,'16:60','للذين لا يؤمنون بالاخرة مثل السوء ولله المثل الاعلى وهو العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(16,61,'16:61','ولو يؤاخذ الله الناس بظلمهم ما ترك عليها من دابة ولكن يؤخرهم الى اجل مسمى فاذا جاء اجلهم لا يستاخرون ساعة ولا يستقدمون');
INSERT INTO "imlaei_simple" VALUES(16,62,'16:62','ويجعلون لله ما يكرهون وتصف السنتهم الكذب ان لهم الحسنى لا جرم ان لهم النار وانهم مفرطون');
INSERT INTO "imlaei_simple" VALUES(16,63,'16:63','تالله لقد ارسلنا الى امم من قبلك فزين لهم الشيطان اعمالهم فهو وليهم اليوم ولهم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(16,64,'16:64','وما انزلنا عليك الكتاب الا لتبين لهم الذي اختلفوا فيه وهدى ورحمة لقوم يؤمنون');
INSERT INTO "imlaei_simple" VALUES(16,65,'16:65','والله انزل من السماء ماء فاحيا به الارض بعد موتها ان في ذلك لاية لقوم يسمعون');
INSERT INTO "imlaei_simple" VALUES(16,66,'16:66','وان لكم في الانعام لعبرة نسقيكم مما في بطونه من بين فرث ودم لبنا خالصا سائغا للشاربين');
INSERT INTO "imlaei_simple" VALUES(16,67,'16:67','ومن ثمرات النخيل والاعناب تتخذون منه سكرا ورزقا حسنا ان في ذلك لاية لقوم يعقلون');
INSERT INTO "imlaei_simple" VALUES(16,68,'16:68','واوحى ربك الى النحل ان اتخذي من الجبال بيوتا ومن الشجر ومما يعرشون');
INSERT INTO "imlaei_simple" VALUES(16,69,'16:69','ثم كلي من كل الثمرات فاسلكي سبل ربك ذللا يخرج من بطونها شراب مختلف الوانه فيه شفاء للناس ان في ذلك لاية لقوم يتفكرون');
INSERT INTO "imlaei_simple" VALUES(16,70,'16:70','والله خلقكم ثم يتوفاكم ومنكم من يرد الى ارذل العمر لكي لا يعلم بعد علم شيئا ان الله عليم قدير');
INSERT INTO "imlaei_simple" VALUES(16,71,'16:71','والله فضل بعضكم على بعض في الرزق فما الذين فضلوا برادي رزقهم على ما ملكت ايمانهم فهم فيه سواء افبنعمة الله يجحدون');
INSERT INTO "imlaei_simple" VALUES(16,72,'16:72','والله جعل لكم من انفسكم ازواجا وجعل لكم من ازواجكم بنين وحفدة ورزقكم من الطيبات افبالباطل يؤمنون وبنعمت الله هم يكفرون');
INSERT INTO "imlaei_simple" VALUES(16,73,'16:73','ويعبدون من دون الله ما لا يملك لهم رزقا من السماوات والارض شيئا ولا يستطيعون');
INSERT INTO "imlaei_simple" VALUES(16,74,'16:74','فلا تضربوا لله الامثال ان الله يعلم وانتم لا تعلمون');
INSERT INTO "imlaei_simple" VALUES(16,75,'16:75','ضرب الله مثلا عبدا مملوكا لا يقدر على شيء ومن رزقناه منا رزقا حسنا فهو ينفق منه سرا وجهرا هل يستوون الحمد لله بل اكثرهم لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(16,76,'16:76','وضرب الله مثلا رجلين احدهما ابكم لا يقدر على شيء وهو كل على مولاه اينما يوجهه لا يات بخير هل يستوي هو ومن يامر بالعدل وهو على صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(16,77,'16:77','ولله غيب السماوات والارض وما امر الساعة الا كلمح البصر او هو اقرب ان الله على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(16,78,'16:78','والله اخرجكم من بطون امهاتكم لا تعلمون شيئا وجعل لكم السمع والابصار والافئدة لعلكم تشكرون');
INSERT INTO "imlaei_simple" VALUES(16,79,'16:79','الم يروا الى الطير مسخرات في جو السماء ما يمسكهن الا الله ان في ذلك لايات لقوم يؤمنون');
INSERT INTO "imlaei_simple" VALUES(16,80,'16:80','والله جعل لكم من بيوتكم سكنا وجعل لكم من جلود الانعام بيوتا تستخفونها يوم ظعنكم ويوم اقامتكم ومن اصوافها واوبارها واشعارها اثاثا ومتاعا الى حين');
INSERT INTO "imlaei_simple" VALUES(16,81,'16:81','والله جعل لكم مما خلق ظلالا وجعل لكم من الجبال اكنانا وجعل لكم سرابيل تقيكم الحر وسرابيل تقيكم باسكم كذلك يتم نعمته عليكم لعلكم تسلمون');
INSERT INTO "imlaei_simple" VALUES(16,82,'16:82','فان تولوا فانما عليك البلاغ المبين');
INSERT INTO "imlaei_simple" VALUES(16,83,'16:83','يعرفون نعمت الله ثم ينكرونها واكثرهم الكافرون');
INSERT INTO "imlaei_simple" VALUES(16,84,'16:84','ويوم نبعث من كل امة شهيدا ثم لا يؤذن للذين كفروا ولا هم يستعتبون');
INSERT INTO "imlaei_simple" VALUES(16,85,'16:85','واذا راى الذين ظلموا العذاب فلا يخفف عنهم ولا هم ينظرون');
INSERT INTO "imlaei_simple" VALUES(16,86,'16:86','واذا راى الذين اشركوا شركاءهم قالوا ربنا هؤلاء شركاؤنا الذين كنا ندعو من دونك فالقوا اليهم القول انكم لكاذبون');
INSERT INTO "imlaei_simple" VALUES(16,87,'16:87','والقوا الى الله يومئذ السلم وضل عنهم ما كانوا يفترون');
INSERT INTO "imlaei_simple" VALUES(16,88,'16:88','الذين كفروا وصدوا عن سبيل الله زدناهم عذابا فوق العذاب بما كانوا يفسدون');
INSERT INTO "imlaei_simple" VALUES(16,89,'16:89','ويوم نبعث في كل امة شهيدا عليهم من انفسهم وجئنا بك شهيدا على هؤلاء ونزلنا عليك الكتاب تبيانا لكل شيء وهدى ورحمة وبشرى للمسلمين');
INSERT INTO "imlaei_simple" VALUES(16,90,'16:90','ان الله يامر بالعدل والاحسان وايتاء ذي القربى وينهى عن الفحشاء والمنكر والبغي يعظكم لعلكم تذكرون');
INSERT INTO "imlaei_simple" VALUES(16,91,'16:91','واوفوا بعهد الله اذا عاهدتم ولا تنقضوا الايمان بعد توكيدها وقد جعلتم الله عليكم كفيلا ان الله يعلم ما تفعلون');
INSERT INTO "imlaei_simple" VALUES(16,92,'16:92','ولا تكونوا كالتي نقضت غزلها من بعد قوة انكاثا تتخذون ايمانكم دخلا بينكم ان تكون امة هي اربى من امة انما يبلوكم الله به وليبينن لكم يوم القيامة ما كنتم فيه تختلفون');
INSERT INTO "imlaei_simple" VALUES(16,93,'16:93','ولو شاء الله لجعلكم امة واحدة ولكن يضل من يشاء ويهدي من يشاء ولتسالن عما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(16,94,'16:94','ولا تتخذوا ايمانكم دخلا بينكم فتزل قدم بعد ثبوتها وتذوقوا السوء بما صددتم عن سبيل الله ولكم عذاب عظيم');
INSERT INTO "imlaei_simple" VALUES(16,95,'16:95','ولا تشتروا بعهد الله ثمنا قليلا انما عند الله هو خير لكم ان كنتم تعلمون');
INSERT INTO "imlaei_simple" VALUES(16,96,'16:96','ما عندكم ينفد وما عند الله باق ولنجزين الذين صبروا اجرهم باحسن ما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(16,97,'16:97','من عمل صالحا من ذكر او انثى وهو مؤمن فلنحيينه حياة طيبة ولنجزينهم اجرهم باحسن ما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(16,98,'16:98','فاذا قرات القران فاستعذ بالله من الشيطان الرجيم');
INSERT INTO "imlaei_simple" VALUES(16,99,'16:99','انه ليس له سلطان على الذين امنوا وعلى ربهم يتوكلون');
INSERT INTO "imlaei_simple" VALUES(16,100,'16:100','انما سلطانه على الذين يتولونه والذين هم به مشركون');
INSERT INTO "imlaei_simple" VALUES(16,101,'16:101','واذا بدلنا اية مكان اية والله اعلم بما ينزل قالوا انما انت مفتر بل اكثرهم لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(16,102,'16:102','قل نزله روح القدس من ربك بالحق ليثبت الذين امنوا وهدى وبشرى للمسلمين');
INSERT INTO "imlaei_simple" VALUES(16,103,'16:103','ولقد نعلم انهم يقولون انما يعلمه بشر لسان الذي يلحدون اليه اعجمي وهذا لسان عربي مبين');
INSERT INTO "imlaei_simple" VALUES(16,104,'16:104','ان الذين لا يؤمنون بايات الله لا يهديهم الله ولهم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(16,105,'16:105','انما يفتري الكذب الذين لا يؤمنون بايات الله واولئك هم الكاذبون');
INSERT INTO "imlaei_simple" VALUES(16,106,'16:106','من كفر بالله من بعد ايمانه الا من اكره وقلبه مطمئن بالايمان ولكن من شرح بالكفر صدرا فعليهم غضب من الله ولهم عذاب عظيم');
INSERT INTO "imlaei_simple" VALUES(16,107,'16:107','ذلك بانهم استحبوا الحياة الدنيا على الاخرة وان الله لا يهدي القوم الكافرين');
INSERT INTO "imlaei_simple" VALUES(16,108,'16:108','اولئك الذين طبع الله على قلوبهم وسمعهم وابصارهم واولئك هم الغافلون');
INSERT INTO "imlaei_simple" VALUES(16,109,'16:109','لا جرم انهم في الاخرة هم الخاسرون');
INSERT INTO "imlaei_simple" VALUES(16,110,'16:110','ثم ان ربك للذين هاجروا من بعد ما فتنوا ثم جاهدوا وصبروا ان ربك من بعدها لغفور رحيم');
INSERT INTO "imlaei_simple" VALUES(16,111,'16:111','يوم تاتي كل نفس تجادل عن نفسها وتوفى كل نفس ما عملت وهم لا يظلمون');
INSERT INTO "imlaei_simple" VALUES(16,112,'16:112','وضرب الله مثلا قرية كانت امنة مطمئنة ياتيها رزقها رغدا من كل مكان فكفرت بانعم الله فاذاقها الله لباس الجوع والخوف بما كانوا يصنعون');
INSERT INTO "imlaei_simple" VALUES(16,113,'16:113','ولقد جاءهم رسول منهم فكذبوه فاخذهم العذاب وهم ظالمون');
INSERT INTO "imlaei_simple" VALUES(16,114,'16:114','فكلوا مما رزقكم الله حلالا طيبا واشكروا نعمت الله ان كنتم اياه تعبدون');
INSERT INTO "imlaei_simple" VALUES(16,115,'16:115','انما حرم عليكم الميتة والدم ولحم الخنزير وما اهل لغير الله به فمن اضطر غير باغ ولا عاد فان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(16,116,'16:116','ولا تقولوا لما تصف السنتكم الكذب هذا حلال وهذا حرام لتفتروا على الله الكذب ان الذين يفترون على الله الكذب لا يفلحون');
INSERT INTO "imlaei_simple" VALUES(16,117,'16:117','متاع قليل ولهم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(16,118,'16:118','وعلى الذين هادوا حرمنا ما قصصنا عليك من قبل وما ظلمناهم ولكن كانوا انفسهم يظلمون');
INSERT INTO "imlaei_simple" VALUES(16,119,'16:119','ثم ان ربك للذين عملوا السوء بجهالة ثم تابوا من بعد ذلك واصلحوا ان ربك من بعدها لغفور رحيم');
INSERT INTO "imlaei_simple" VALUES(16,120,'16:120','ان ابراهيم كان امة قانتا لله حنيفا ولم يك من المشركين');
INSERT INTO "imlaei_simple" VALUES(16,121,'16:121','شاكرا لانعمه اجتباه وهداه الى صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(16,122,'16:122','واتيناه في الدنيا حسنة وانه في الاخرة لمن الصالحين');
INSERT INTO "imlaei_simple" VALUES(16,123,'16:123','ثم اوحينا اليك ان اتبع ملة ابراهيم حنيفا وما كان من المشركين');
INSERT INTO "imlaei_simple" VALUES(16,124,'16:124','انما جعل السبت على الذين اختلفوا فيه وان ربك ليحكم بينهم يوم القيامة فيما كانوا فيه يختلفون');
INSERT INTO "imlaei_simple" VALUES(16,125,'16:125','ادع الى سبيل ربك بالحكمة والموعظة الحسنة وجادلهم بالتي هي احسن ان ربك هو اعلم بمن ضل عن سبيله وهو اعلم بالمهتدين');
INSERT INTO "imlaei_simple" VALUES(16,126,'16:126','وان عاقبتم فعاقبوا بمثل ما عوقبتم به ولئن صبرتم لهو خير للصابرين');
INSERT INTO "imlaei_simple" VALUES(16,127,'16:127','واصبر وما صبرك الا بالله ولا تحزن عليهم ولا تك في ضيق مما يمكرون');
INSERT INTO "imlaei_simple" VALUES(16,128,'16:128','ان الله مع الذين اتقوا والذين هم محسنون');
INSERT INTO "imlaei_simple" VALUES(17,1,'17:1','سبحان الذي اسرى بعبده ليلا من المسجد الحرام الى المسجد الاقصى الذي باركنا حوله لنريه من اياتنا انه هو السميع البصير');
INSERT INTO "imlaei_simple" VALUES(17,2,'17:2','واتينا موسى الكتاب وجعلناه هدى لبني اسرائيل الا تتخذوا من دوني وكيلا');
INSERT INTO "imlaei_simple" VALUES(17,3,'17:3','ذرية من حملنا مع نوح انه كان عبدا شكورا');
INSERT INTO "imlaei_simple" VALUES(17,4,'17:4','وقضينا الى بني اسرائيل في الكتاب لتفسدن في الارض مرتين ولتعلن علوا كبيرا');
INSERT INTO "imlaei_simple" VALUES(17,5,'17:5','فاذا جاء وعد اولاهما بعثنا عليكم عبادا لنا اولي باس شديد فجاسوا خلال الديار وكان وعدا مفعولا');
INSERT INTO "imlaei_simple" VALUES(17,6,'17:6','ثم رددنا لكم الكرة عليهم وامددناكم باموال وبنين وجعلناكم اكثر نفيرا');
INSERT INTO "imlaei_simple" VALUES(17,7,'17:7','ان احسنتم احسنتم لانفسكم وان اساتم فلها فاذا جاء وعد الاخرة ليسوءوا وجوهكم وليدخلوا المسجد كما دخلوه اول مرة وليتبروا ما علوا تتبيرا');
INSERT INTO "imlaei_simple" VALUES(17,8,'17:8','عسى ربكم ان يرحمكم وان عدتم عدنا وجعلنا جهنم للكافرين حصيرا');
INSERT INTO "imlaei_simple" VALUES(17,9,'17:9','ان هذا القران يهدي للتي هي اقوم ويبشر المؤمنين الذين يعملون الصالحات ان لهم اجرا كبيرا');
INSERT INTO "imlaei_simple" VALUES(17,10,'17:10','وان الذين لا يؤمنون بالاخرة اعتدنا لهم عذابا اليما');
INSERT INTO "imlaei_simple" VALUES(17,11,'17:11','ويدع الانسان بالشر دعاءه بالخير وكان الانسان عجولا');
INSERT INTO "imlaei_simple" VALUES(17,12,'17:12','وجعلنا الليل والنهار ايتين فمحونا اية الليل وجعلنا اية النهار مبصرة لتبتغوا فضلا من ربكم ولتعلموا عدد السنين والحساب وكل شيء فصلناه تفصيلا');
INSERT INTO "imlaei_simple" VALUES(17,13,'17:13','وكل انسان الزمناه طائره في عنقه ونخرج له يوم القيامة كتابا يلقاه منشورا');
INSERT INTO "imlaei_simple" VALUES(17,14,'17:14','اقرا كتابك كفى بنفسك اليوم عليك حسيبا');
INSERT INTO "imlaei_simple" VALUES(17,15,'17:15','من اهتدى فانما يهتدي لنفسه ومن ضل فانما يضل عليها ولا تزر وازرة وزر اخرى وما كنا معذبين حتى نبعث رسولا');
INSERT INTO "imlaei_simple" VALUES(17,16,'17:16','واذا اردنا ان نهلك قرية امرنا مترفيها ففسقوا فيها فحق عليها القول فدمرناها تدميرا');
INSERT INTO "imlaei_simple" VALUES(17,17,'17:17','وكم اهلكنا من القرون من بعد نوح وكفى بربك بذنوب عباده خبيرا بصيرا');
INSERT INTO "imlaei_simple" VALUES(17,18,'17:18','من كان يريد العاجلة عجلنا له فيها ما نشاء لمن نريد ثم جعلنا له جهنم يصلاها مذموما مدحورا');
INSERT INTO "imlaei_simple" VALUES(17,19,'17:19','ومن اراد الاخرة وسعى لها سعيها وهو مؤمن فاولئك كان سعيهم مشكورا');
INSERT INTO "imlaei_simple" VALUES(17,20,'17:20','كلا نمد هؤلاء وهؤلاء من عطاء ربك وما كان عطاء ربك محظورا');
INSERT INTO "imlaei_simple" VALUES(17,21,'17:21','انظر كيف فضلنا بعضهم على بعض وللاخرة اكبر درجات واكبر تفضيلا');
INSERT INTO "imlaei_simple" VALUES(17,22,'17:22','لا تجعل مع الله الها اخر فتقعد مذموما مخذولا');
INSERT INTO "imlaei_simple" VALUES(17,23,'17:23','وقضى ربك الا تعبدوا الا اياه وبالوالدين احسانا اما يبلغن عندك الكبر احدهما او كلاهما فلا تقل لهما اف ولا تنهرهما وقل لهما قولا كريما');
INSERT INTO "imlaei_simple" VALUES(17,24,'17:24','واخفض لهما جناح الذل من الرحمة وقل رب ارحمهما كما ربياني صغيرا');
INSERT INTO "imlaei_simple" VALUES(17,25,'17:25','ربكم اعلم بما في نفوسكم ان تكونوا صالحين فانه كان للاوابين غفورا');
INSERT INTO "imlaei_simple" VALUES(17,26,'17:26','وات ذا القربى حقه والمسكين وابن السبيل ولا تبذر تبذيرا');
INSERT INTO "imlaei_simple" VALUES(17,27,'17:27','ان المبذرين كانوا اخوان الشياطين وكان الشيطان لربه كفورا');
INSERT INTO "imlaei_simple" VALUES(17,28,'17:28','واما تعرضن عنهم ابتغاء رحمة من ربك ترجوها فقل لهم قولا ميسورا');
INSERT INTO "imlaei_simple" VALUES(17,29,'17:29','ولا تجعل يدك مغلولة الى عنقك ولا تبسطها كل البسط فتقعد ملوما محسورا');
INSERT INTO "imlaei_simple" VALUES(17,30,'17:30','ان ربك يبسط الرزق لمن يشاء ويقدر انه كان بعباده خبيرا بصيرا');
INSERT INTO "imlaei_simple" VALUES(17,31,'17:31','ولا تقتلوا اولادكم خشية املاق نحن نرزقهم واياكم ان قتلهم كان خطئا كبيرا');
INSERT INTO "imlaei_simple" VALUES(17,32,'17:32','ولا تقربوا الزنا انه كان فاحشة وساء سبيلا');
INSERT INTO "imlaei_simple" VALUES(17,33,'17:33','ولا تقتلوا النفس التي حرم الله الا بالحق ومن قتل مظلوما فقد جعلنا لوليه سلطانا فلا يسرف في القتل انه كان منصورا');
INSERT INTO "imlaei_simple" VALUES(17,34,'17:34','ولا تقربوا مال اليتيم الا بالتي هي احسن حتى يبلغ اشده واوفوا بالعهد ان العهد كان مسئولا');
INSERT INTO "imlaei_simple" VALUES(17,35,'17:35','واوفوا الكيل اذا كلتم وزنوا بالقسطاس المستقيم ذلك خير واحسن تاويلا');
INSERT INTO "imlaei_simple" VALUES(17,36,'17:36','ولا تقف ما ليس لك به علم ان السمع والبصر والفؤاد كل اولئك كان عنه مسئولا');
INSERT INTO "imlaei_simple" VALUES(17,37,'17:37','ولا تمش في الارض مرحا انك لن تخرق الارض ولن تبلغ الجبال طولا');
INSERT INTO "imlaei_simple" VALUES(17,38,'17:38','كل ذلك كان سيئه عند ربك مكروها');
INSERT INTO "imlaei_simple" VALUES(17,39,'17:39','ذلك مما اوحى اليك ربك من الحكمة ولا تجعل مع الله الها اخر فتلقى في جهنم ملوما مدحورا');
INSERT INTO "imlaei_simple" VALUES(17,40,'17:40','افاصفاكم ربكم بالبنين واتخذ من الملائكة اناثا انكم لتقولون قولا عظيما');
INSERT INTO "imlaei_simple" VALUES(17,41,'17:41','ولقد صرفنا في هذا القران ليذكروا وما يزيدهم الا نفورا');
INSERT INTO "imlaei_simple" VALUES(17,42,'17:42','قل لو كان معه الهة كما يقولون اذا لابتغوا الى ذي العرش سبيلا');
INSERT INTO "imlaei_simple" VALUES(17,43,'17:43','سبحانه وتعالى عما يقولون علوا كبيرا');
INSERT INTO "imlaei_simple" VALUES(17,44,'17:44','تسبح له السماوات السبع والارض ومن فيهن وان من شيء الا يسبح بحمده ولكن لا تفقهون تسبيحهم انه كان حليما غفورا');
INSERT INTO "imlaei_simple" VALUES(17,45,'17:45','واذا قرات القران جعلنا بينك وبين الذين لا يؤمنون بالاخرة حجابا مستورا');
INSERT INTO "imlaei_simple" VALUES(17,46,'17:46','وجعلنا على قلوبهم اكنة ان يفقهوه وفي اذانهم وقرا واذا ذكرت ربك في القران وحده ولوا على ادبارهم نفورا');
INSERT INTO "imlaei_simple" VALUES(17,47,'17:47','نحن اعلم بما يستمعون به اذ يستمعون اليك واذ هم نجوى اذ يقول الظالمون ان تتبعون الا رجلا مسحورا');
INSERT INTO "imlaei_simple" VALUES(17,48,'17:48','انظر كيف ضربوا لك الامثال فضلوا فلا يستطيعون سبيلا');
INSERT INTO "imlaei_simple" VALUES(17,49,'17:49','وقالوا ااذا كنا عظاما ورفاتا اانا لمبعوثون خلقا جديدا');
INSERT INTO "imlaei_simple" VALUES(17,50,'17:50','قل كونوا حجارة او حديدا');
INSERT INTO "imlaei_simple" VALUES(17,51,'17:51','او خلقا مما يكبر في صدوركم فسيقولون من يعيدنا قل الذي فطركم اول مرة فسينغضون اليك رءوسهم ويقولون متى هو قل عسى ان يكون قريبا');
INSERT INTO "imlaei_simple" VALUES(17,52,'17:52','يوم يدعوكم فتستجيبون بحمده وتظنون ان لبثتم الا قليلا');
INSERT INTO "imlaei_simple" VALUES(17,53,'17:53','وقل لعبادي يقولوا التي هي احسن ان الشيطان ينزغ بينهم ان الشيطان كان للانسان عدوا مبينا');
INSERT INTO "imlaei_simple" VALUES(17,54,'17:54','ربكم اعلم بكم ان يشا يرحمكم او ان يشا يعذبكم وما ارسلناك عليهم وكيلا');
INSERT INTO "imlaei_simple" VALUES(17,55,'17:55','وربك اعلم بمن في السماوات والارض ولقد فضلنا بعض النبيين على بعض واتينا داوود زبورا');
INSERT INTO "imlaei_simple" VALUES(17,56,'17:56','قل ادعوا الذين زعمتم من دونه فلا يملكون كشف الضر عنكم ولا تحويلا');
INSERT INTO "imlaei_simple" VALUES(17,57,'17:57','اولئك الذين يدعون يبتغون الى ربهم الوسيلة ايهم اقرب ويرجون رحمته ويخافون عذابه ان عذاب ربك كان محذورا');
INSERT INTO "imlaei_simple" VALUES(17,58,'17:58','وان من قرية الا نحن مهلكوها قبل يوم القيامة او معذبوها عذابا شديدا كان ذلك في الكتاب مسطورا');
INSERT INTO "imlaei_simple" VALUES(17,59,'17:59','وما منعنا ان نرسل بالايات الا ان كذب بها الاولون واتينا ثمود الناقة مبصرة فظلموا بها وما نرسل بالايات الا تخويفا');
INSERT INTO "imlaei_simple" VALUES(17,60,'17:60','واذ قلنا لك ان ربك احاط بالناس وما جعلنا الرؤيا التي اريناك الا فتنة للناس والشجرة الملعونة في القران ونخوفهم فما يزيدهم الا طغيانا كبيرا');
INSERT INTO "imlaei_simple" VALUES(17,61,'17:61','واذ قلنا للملائكة اسجدوا لادم فسجدوا الا ابليس قال ااسجد لمن خلقت طينا');
INSERT INTO "imlaei_simple" VALUES(17,62,'17:62','قال ارايتك هذا الذي كرمت علي لئن اخرتن الى يوم القيامة لاحتنكن ذريته الا قليلا');
INSERT INTO "imlaei_simple" VALUES(17,63,'17:63','قال اذهب فمن تبعك منهم فان جهنم جزاؤكم جزاء موفورا');
INSERT INTO "imlaei_simple" VALUES(17,64,'17:64','واستفزز من استطعت منهم بصوتك واجلب عليهم بخيلك ورجلك وشاركهم في الاموال والاولاد وعدهم وما يعدهم الشيطان الا غرورا');
INSERT INTO "imlaei_simple" VALUES(17,65,'17:65','ان عبادي ليس لك عليهم سلطان وكفى بربك وكيلا');
INSERT INTO "imlaei_simple" VALUES(17,66,'17:66','ربكم الذي يزجي لكم الفلك في البحر لتبتغوا من فضله انه كان بكم رحيما');
INSERT INTO "imlaei_simple" VALUES(17,67,'17:67','واذا مسكم الضر في البحر ضل من تدعون الا اياه فلما نجاكم الى البر اعرضتم وكان الانسان كفورا');
INSERT INTO "imlaei_simple" VALUES(17,68,'17:68','افامنتم ان يخسف بكم جانب البر او يرسل عليكم حاصبا ثم لا تجدوا لكم وكيلا');
INSERT INTO "imlaei_simple" VALUES(17,69,'17:69','ام امنتم ان يعيدكم فيه تارة اخرى فيرسل عليكم قاصفا من الريح فيغرقكم بما كفرتم ثم لا تجدوا لكم علينا به تبيعا');
INSERT INTO "imlaei_simple" VALUES(17,70,'17:70','ولقد كرمنا بني ادم وحملناهم في البر والبحر ورزقناهم من الطيبات وفضلناهم على كثير ممن خلقنا تفضيلا');
INSERT INTO "imlaei_simple" VALUES(17,71,'17:71','يوم ندعو كل اناس بامامهم فمن اوتي كتابه بيمينه فاولئك يقرءون كتابهم ولا يظلمون فتيلا');
INSERT INTO "imlaei_simple" VALUES(17,72,'17:72','ومن كان في هذه اعمى فهو في الاخرة اعمى واضل سبيلا');
INSERT INTO "imlaei_simple" VALUES(17,73,'17:73','وان كادوا ليفتنونك عن الذي اوحينا اليك لتفتري علينا غيره واذا لاتخذوك خليلا');
INSERT INTO "imlaei_simple" VALUES(17,74,'17:74','ولولا ان ثبتناك لقد كدت تركن اليهم شيئا قليلا');
INSERT INTO "imlaei_simple" VALUES(17,75,'17:75','اذا لاذقناك ضعف الحياة وضعف الممات ثم لا تجد لك علينا نصيرا');
INSERT INTO "imlaei_simple" VALUES(17,76,'17:76','وان كادوا ليستفزونك من الارض ليخرجوك منها واذا لا يلبثون خلافك الا قليلا');
INSERT INTO "imlaei_simple" VALUES(17,77,'17:77','سنة من قد ارسلنا قبلك من رسلنا ولا تجد لسنتنا تحويلا');
INSERT INTO "imlaei_simple" VALUES(17,78,'17:78','اقم الصلاة لدلوك الشمس الى غسق الليل وقران الفجر ان قران الفجر كان مشهودا');
INSERT INTO "imlaei_simple" VALUES(17,79,'17:79','ومن الليل فتهجد به نافلة لك عسى ان يبعثك ربك مقاما محمودا');
INSERT INTO "imlaei_simple" VALUES(17,80,'17:80','وقل رب ادخلني مدخل صدق واخرجني مخرج صدق واجعل لي من لدنك سلطانا نصيرا');
INSERT INTO "imlaei_simple" VALUES(17,81,'17:81','وقل جاء الحق وزهق الباطل ان الباطل كان زهوقا');
INSERT INTO "imlaei_simple" VALUES(17,82,'17:82','وننزل من القران ما هو شفاء ورحمة للمؤمنين ولا يزيد الظالمين الا خسارا');
INSERT INTO "imlaei_simple" VALUES(17,83,'17:83','واذا انعمنا على الانسان اعرض وناى بجانبه واذا مسه الشر كان يئوسا');
INSERT INTO "imlaei_simple" VALUES(17,84,'17:84','قل كل يعمل على شاكلته فربكم اعلم بمن هو اهدى سبيلا');
INSERT INTO "imlaei_simple" VALUES(17,85,'17:85','ويسالونك عن الروح قل الروح من امر ربي وما اوتيتم من العلم الا قليلا');
INSERT INTO "imlaei_simple" VALUES(17,86,'17:86','ولئن شئنا لنذهبن بالذي اوحينا اليك ثم لا تجد لك به علينا وكيلا');
INSERT INTO "imlaei_simple" VALUES(17,87,'17:87','الا رحمة من ربك ان فضله كان عليك كبيرا');
INSERT INTO "imlaei_simple" VALUES(17,88,'17:88','قل لئن اجتمعت الانس والجن على ان ياتوا بمثل هذا القران لا ياتون بمثله ولو كان بعضهم لبعض ظهيرا');
INSERT INTO "imlaei_simple" VALUES(17,89,'17:89','ولقد صرفنا للناس في هذا القران من كل مثل فابى اكثر الناس الا كفورا');
INSERT INTO "imlaei_simple" VALUES(17,90,'17:90','وقالوا لن نؤمن لك حتى تفجر لنا من الارض ينبوعا');
INSERT INTO "imlaei_simple" VALUES(17,91,'17:91','او تكون لك جنة من نخيل وعنب فتفجر الانهار خلالها تفجيرا');
INSERT INTO "imlaei_simple" VALUES(17,92,'17:92','او تسقط السماء كما زعمت علينا كسفا او تاتي بالله والملائكة قبيلا');
INSERT INTO "imlaei_simple" VALUES(17,93,'17:93','او يكون لك بيت من زخرف او ترقى في السماء ولن نؤمن لرقيك حتى تنزل علينا كتابا نقرؤه قل سبحان ربي هل كنت الا بشرا رسولا');
INSERT INTO "imlaei_simple" VALUES(17,94,'17:94','وما منع الناس ان يؤمنوا اذ جاءهم الهدى الا ان قالوا ابعث الله بشرا رسولا');
INSERT INTO "imlaei_simple" VALUES(17,95,'17:95','قل لو كان في الارض ملائكة يمشون مطمئنين لنزلنا عليهم من السماء ملكا رسولا');
INSERT INTO "imlaei_simple" VALUES(17,96,'17:96','قل كفى بالله شهيدا بيني وبينكم انه كان بعباده خبيرا بصيرا');
INSERT INTO "imlaei_simple" VALUES(17,97,'17:97','ومن يهد الله فهو المهتد ومن يضلل فلن تجد لهم اولياء من دونه ونحشرهم يوم القيامة على وجوههم عميا وبكما وصما ماواهم جهنم كلما خبت زدناهم سعيرا');
INSERT INTO "imlaei_simple" VALUES(17,98,'17:98','ذلك جزاؤهم بانهم كفروا باياتنا وقالوا ااذا كنا عظاما ورفاتا اانا لمبعوثون خلقا جديدا');
INSERT INTO "imlaei_simple" VALUES(17,99,'17:99','اولم يروا ان الله الذي خلق السماوات والارض قادر على ان يخلق مثلهم وجعل لهم اجلا لا ريب فيه فابى الظالمون الا كفورا');
INSERT INTO "imlaei_simple" VALUES(17,100,'17:100','قل لو انتم تملكون خزائن رحمة ربي اذا لامسكتم خشية الانفاق وكان الانسان قتورا');
INSERT INTO "imlaei_simple" VALUES(17,101,'17:101','ولقد اتينا موسى تسع ايات بينات فاسال بني اسرائيل اذ جاءهم فقال له فرعون اني لاظنك يا موسى مسحورا');
INSERT INTO "imlaei_simple" VALUES(17,102,'17:102','قال لقد علمت ما انزل هؤلاء الا رب السماوات والارض بصائر واني لاظنك يا فرعون مثبورا');
INSERT INTO "imlaei_simple" VALUES(17,103,'17:103','فاراد ان يستفزهم من الارض فاغرقناه ومن معه جميعا');
INSERT INTO "imlaei_simple" VALUES(17,104,'17:104','وقلنا من بعده لبني اسرائيل اسكنوا الارض فاذا جاء وعد الاخرة جئنا بكم لفيفا');
INSERT INTO "imlaei_simple" VALUES(17,105,'17:105','وبالحق انزلناه وبالحق نزل وما ارسلناك الا مبشرا ونذيرا');
INSERT INTO "imlaei_simple" VALUES(17,106,'17:106','وقرانا فرقناه لتقراه على الناس على مكث ونزلناه تنزيلا');
INSERT INTO "imlaei_simple" VALUES(17,107,'17:107','قل امنوا به او لا تؤمنوا ان الذين اوتوا العلم من قبله اذا يتلى عليهم يخرون للاذقان سجدا');
INSERT INTO "imlaei_simple" VALUES(17,108,'17:108','ويقولون سبحان ربنا ان كان وعد ربنا لمفعولا');
INSERT INTO "imlaei_simple" VALUES(17,109,'17:109','ويخرون للاذقان يبكون ويزيدهم خشوعا');
INSERT INTO "imlaei_simple" VALUES(17,110,'17:110','قل ادعوا الله او ادعوا الرحمن ايا ما تدعوا فله الاسماء الحسنى ولا تجهر بصلاتك ولا تخافت بها وابتغ بين ذلك سبيلا');
INSERT INTO "imlaei_simple" VALUES(17,111,'17:111','وقل الحمد لله الذي لم يتخذ ولدا ولم يكن له شريك في الملك ولم يكن له ولي من الذل وكبره تكبيرا');
INSERT INTO "imlaei_simple" VALUES(18,1,'18:1','الحمد لله الذي انزل على عبده الكتاب ولم يجعل له عوجا');
INSERT INTO "imlaei_simple" VALUES(18,2,'18:2','قيما لينذر باسا شديدا من لدنه ويبشر المؤمنين الذين يعملون الصالحات ان لهم اجرا حسنا');
INSERT INTO "imlaei_simple" VALUES(18,3,'18:3','ماكثين فيه ابدا');
INSERT INTO "imlaei_simple" VALUES(18,4,'18:4','وينذر الذين قالوا اتخذ الله ولدا');
INSERT INTO "imlaei_simple" VALUES(18,5,'18:5','ما لهم به من علم ولا لابائهم كبرت كلمة تخرج من افواههم ان يقولون الا كذبا');
INSERT INTO "imlaei_simple" VALUES(18,6,'18:6','فلعلك باخع نفسك على اثارهم ان لم يؤمنوا بهذا الحديث اسفا');
INSERT INTO "imlaei_simple" VALUES(18,7,'18:7','انا جعلنا ما على الارض زينة لها لنبلوهم ايهم احسن عملا');
INSERT INTO "imlaei_simple" VALUES(18,8,'18:8','وانا لجاعلون ما عليها صعيدا جرزا');
INSERT INTO "imlaei_simple" VALUES(18,9,'18:9','ام حسبت ان اصحاب الكهف والرقيم كانوا من اياتنا عجبا');
INSERT INTO "imlaei_simple" VALUES(18,10,'18:10','اذ اوى الفتية الى الكهف فقالوا ربنا اتنا من لدنك رحمة وهيئ لنا من امرنا رشدا');
INSERT INTO "imlaei_simple" VALUES(18,11,'18:11','فضربنا على اذانهم في الكهف سنين عددا');
INSERT INTO "imlaei_simple" VALUES(18,12,'18:12','ثم بعثناهم لنعلم اي الحزبين احصى لما لبثوا امدا');
INSERT INTO "imlaei_simple" VALUES(18,13,'18:13','نحن نقص عليك نباهم بالحق انهم فتية امنوا بربهم وزدناهم هدى');
INSERT INTO "imlaei_simple" VALUES(18,14,'18:14','وربطنا على قلوبهم اذ قاموا فقالوا ربنا رب السماوات والارض لن ندعو من دونه الها لقد قلنا اذا شططا');
INSERT INTO "imlaei_simple" VALUES(18,15,'18:15','هؤلاء قومنا اتخذوا من دونه الهة لولا ياتون عليهم بسلطان بين فمن اظلم ممن افترى على الله كذبا');
INSERT INTO "imlaei_simple" VALUES(18,16,'18:16','واذ اعتزلتموهم وما يعبدون الا الله فاووا الى الكهف ينشر لكم ربكم من رحمته ويهيئ لكم من امركم مرفقا');
INSERT INTO "imlaei_simple" VALUES(18,17,'18:17','وترى الشمس اذا طلعت تزاور عن كهفهم ذات اليمين واذا غربت تقرضهم ذات الشمال وهم في فجوة منه ذلك من ايات الله من يهد الله فهو المهتد ومن يضلل فلن تجد له وليا مرشدا');
INSERT INTO "imlaei_simple" VALUES(18,18,'18:18','وتحسبهم ايقاظا وهم رقود ونقلبهم ذات اليمين وذات الشمال وكلبهم باسط ذراعيه بالوصيد لو اطلعت عليهم لوليت منهم فرارا ولملئت منهم رعبا');
INSERT INTO "imlaei_simple" VALUES(18,19,'18:19','وكذلك بعثناهم ليتساءلوا بينهم قال قائل منهم كم لبثتم قالوا لبثنا يوما او بعض يوم قالوا ربكم اعلم بما لبثتم فابعثوا احدكم بورقكم هذه الى المدينة فلينظر ايها ازكى طعاما فلياتكم برزق منه وليتلطف ولا يشعرن بكم احدا');
INSERT INTO "imlaei_simple" VALUES(18,20,'18:20','انهم ان يظهروا عليكم يرجموكم او يعيدوكم في ملتهم ولن تفلحوا اذا ابدا');
INSERT INTO "imlaei_simple" VALUES(18,21,'18:21','وكذلك اعثرنا عليهم ليعلموا ان وعد الله حق وان الساعة لا ريب فيها اذ يتنازعون بينهم امرهم فقالوا ابنوا عليهم بنيانا ربهم اعلم بهم قال الذين غلبوا على امرهم لنتخذن عليهم مسجدا');
INSERT INTO "imlaei_simple" VALUES(18,22,'18:22','سيقولون ثلاثة رابعهم كلبهم ويقولون خمسة سادسهم كلبهم رجما بالغيب ويقولون سبعة وثامنهم كلبهم قل ربي اعلم بعدتهم ما يعلمهم الا قليل فلا تمار فيهم الا مراء ظاهرا ولا تستفت فيهم منهم احدا');
INSERT INTO "imlaei_simple" VALUES(18,23,'18:23','ولا تقولن لشيء اني فاعل ذلك غدا');
INSERT INTO "imlaei_simple" VALUES(18,24,'18:24','الا ان يشاء الله واذكر ربك اذا نسيت وقل عسى ان يهدين ربي لاقرب من هذا رشدا');
INSERT INTO "imlaei_simple" VALUES(18,25,'18:25','ولبثوا في كهفهم ثلاث مائة سنين وازدادوا تسعا');
INSERT INTO "imlaei_simple" VALUES(18,26,'18:26','قل الله اعلم بما لبثوا له غيب السماوات والارض ابصر به واسمع ما لهم من دونه من ولي ولا يشرك في حكمه احدا');
INSERT INTO "imlaei_simple" VALUES(18,27,'18:27','واتل ما اوحي اليك من كتاب ربك لا مبدل لكلماته ولن تجد من دونه ملتحدا');
INSERT INTO "imlaei_simple" VALUES(18,28,'18:28','واصبر نفسك مع الذين يدعون ربهم بالغداة والعشي يريدون وجهه ولا تعد عيناك عنهم تريد زينة الحياة الدنيا ولا تطع من اغفلنا قلبه عن ذكرنا واتبع هواه وكان امره فرطا');
INSERT INTO "imlaei_simple" VALUES(18,29,'18:29','وقل الحق من ربكم فمن شاء فليؤمن ومن شاء فليكفر انا اعتدنا للظالمين نارا احاط بهم سرادقها وان يستغيثوا يغاثوا بماء كالمهل يشوي الوجوه بئس الشراب وساءت مرتفقا');
INSERT INTO "imlaei_simple" VALUES(18,30,'18:30','ان الذين امنوا وعملوا الصالحات انا لا نضيع اجر من احسن عملا');
INSERT INTO "imlaei_simple" VALUES(18,31,'18:31','اولئك لهم جنات عدن تجري من تحتهم الانهار يحلون فيها من اساور من ذهب ويلبسون ثيابا خضرا من سندس واستبرق متكئين فيها على الارائك نعم الثواب وحسنت مرتفقا');
INSERT INTO "imlaei_simple" VALUES(18,32,'18:32','واضرب لهم مثلا رجلين جعلنا لاحدهما جنتين من اعناب وحففناهما بنخل وجعلنا بينهما زرعا');
INSERT INTO "imlaei_simple" VALUES(18,33,'18:33','كلتا الجنتين اتت اكلها ولم تظلم منه شيئا وفجرنا خلالهما نهرا');
INSERT INTO "imlaei_simple" VALUES(18,34,'18:34','وكان له ثمر فقال لصاحبه وهو يحاوره انا اكثر منك مالا واعز نفرا');
INSERT INTO "imlaei_simple" VALUES(18,35,'18:35','ودخل جنته وهو ظالم لنفسه قال ما اظن ان تبيد هذه ابدا');
INSERT INTO "imlaei_simple" VALUES(18,36,'18:36','وما اظن الساعة قائمة ولئن رددت الى ربي لاجدن خيرا منها منقلبا');
INSERT INTO "imlaei_simple" VALUES(18,37,'18:37','قال له صاحبه وهو يحاوره اكفرت بالذي خلقك من تراب ثم من نطفة ثم سواك رجلا');
INSERT INTO "imlaei_simple" VALUES(18,38,'18:38','لكنا هو الله ربي ولا اشرك بربي احدا');
INSERT INTO "imlaei_simple" VALUES(18,39,'18:39','ولولا اذ دخلت جنتك قلت ما شاء الله لا قوة الا بالله ان ترن انا اقل منك مالا وولدا');
INSERT INTO "imlaei_simple" VALUES(18,40,'18:40','فعسى ربي ان يؤتين خيرا من جنتك ويرسل عليها حسبانا من السماء فتصبح صعيدا زلقا');
INSERT INTO "imlaei_simple" VALUES(18,41,'18:41','او يصبح ماؤها غورا فلن تستطيع له طلبا');
INSERT INTO "imlaei_simple" VALUES(18,42,'18:42','واحيط بثمره فاصبح يقلب كفيه على ما انفق فيها وهي خاوية على عروشها ويقول يا ليتني لم اشرك بربي احدا');
INSERT INTO "imlaei_simple" VALUES(18,43,'18:43','ولم تكن له فئة ينصرونه من دون الله وما كان منتصرا');
INSERT INTO "imlaei_simple" VALUES(18,44,'18:44','هنالك الولاية لله الحق هو خير ثوابا وخير عقبا');
INSERT INTO "imlaei_simple" VALUES(18,45,'18:45','واضرب لهم مثل الحياة الدنيا كماء انزلناه من السماء فاختلط به نبات الارض فاصبح هشيما تذروه الرياح وكان الله على كل شيء مقتدرا');
INSERT INTO "imlaei_simple" VALUES(18,46,'18:46','المال والبنون زينة الحياة الدنيا والباقيات الصالحات خير عند ربك ثوابا وخير املا');
INSERT INTO "imlaei_simple" VALUES(18,47,'18:47','ويوم نسير الجبال وترى الارض بارزة وحشرناهم فلم نغادر منهم احدا');
INSERT INTO "imlaei_simple" VALUES(18,48,'18:48','وعرضوا على ربك صفا لقد جئتمونا كما خلقناكم اول مرة بل زعمتم الن نجعل لكم موعدا');
INSERT INTO "imlaei_simple" VALUES(18,49,'18:49','ووضع الكتاب فترى المجرمين مشفقين مما فيه ويقولون يا ويلتنا مال هذا الكتاب لا يغادر صغيرة ولا كبيرة الا احصاها ووجدوا ما عملوا حاضرا ولا يظلم ربك احدا');
INSERT INTO "imlaei_simple" VALUES(18,50,'18:50','واذ قلنا للملائكة اسجدوا لادم فسجدوا الا ابليس كان من الجن ففسق عن امر ربه افتتخذونه وذريته اولياء من دوني وهم لكم عدو بئس للظالمين بدلا');
INSERT INTO "imlaei_simple" VALUES(18,51,'18:51','ما اشهدتهم خلق السماوات والارض ولا خلق انفسهم وما كنت متخذ المضلين عضدا');
INSERT INTO "imlaei_simple" VALUES(18,52,'18:52','ويوم يقول نادوا شركائي الذين زعمتم فدعوهم فلم يستجيبوا لهم وجعلنا بينهم موبقا');
INSERT INTO "imlaei_simple" VALUES(18,53,'18:53','وراى المجرمون النار فظنوا انهم مواقعوها ولم يجدوا عنها مصرفا');
INSERT INTO "imlaei_simple" VALUES(18,54,'18:54','ولقد صرفنا في هذا القران للناس من كل مثل وكان الانسان اكثر شيء جدلا');
INSERT INTO "imlaei_simple" VALUES(18,55,'18:55','وما منع الناس ان يؤمنوا اذ جاءهم الهدى ويستغفروا ربهم الا ان تاتيهم سنة الاولين او ياتيهم العذاب قبلا');
INSERT INTO "imlaei_simple" VALUES(18,56,'18:56','وما نرسل المرسلين الا مبشرين ومنذرين ويجادل الذين كفروا بالباطل ليدحضوا به الحق واتخذوا اياتي وما انذروا هزوا');
INSERT INTO "imlaei_simple" VALUES(18,57,'18:57','ومن اظلم ممن ذكر بايات ربه فاعرض عنها ونسي ما قدمت يداه انا جعلنا على قلوبهم اكنة ان يفقهوه وفي اذانهم وقرا وان تدعهم الى الهدى فلن يهتدوا اذا ابدا');
INSERT INTO "imlaei_simple" VALUES(18,58,'18:58','وربك الغفور ذو الرحمة لو يؤاخذهم بما كسبوا لعجل لهم العذاب بل لهم موعد لن يجدوا من دونه موئلا');
INSERT INTO "imlaei_simple" VALUES(18,59,'18:59','وتلك القرى اهلكناهم لما ظلموا وجعلنا لمهلكهم موعدا');
INSERT INTO "imlaei_simple" VALUES(18,60,'18:60','واذ قال موسى لفتاه لا ابرح حتى ابلغ مجمع البحرين او امضي حقبا');
INSERT INTO "imlaei_simple" VALUES(18,61,'18:61','فلما بلغا مجمع بينهما نسيا حوتهما فاتخذ سبيله في البحر سربا');
INSERT INTO "imlaei_simple" VALUES(18,62,'18:62','فلما جاوزا قال لفتاه اتنا غداءنا لقد لقينا من سفرنا هذا نصبا');
INSERT INTO "imlaei_simple" VALUES(18,63,'18:63','قال ارايت اذ اوينا الى الصخرة فاني نسيت الحوت وما انسانيه الا الشيطان ان اذكره واتخذ سبيله في البحر عجبا');
INSERT INTO "imlaei_simple" VALUES(18,64,'18:64','قال ذلك ما كنا نبغ فارتدا على اثارهما قصصا');
INSERT INTO "imlaei_simple" VALUES(18,65,'18:65','فوجدا عبدا من عبادنا اتيناه رحمة من عندنا وعلمناه من لدنا علما');
INSERT INTO "imlaei_simple" VALUES(18,66,'18:66','قال له موسى هل اتبعك على ان تعلمن مما علمت رشدا');
INSERT INTO "imlaei_simple" VALUES(18,67,'18:67','قال انك لن تستطيع معي صبرا');
INSERT INTO "imlaei_simple" VALUES(18,68,'18:68','وكيف تصبر على ما لم تحط به خبرا');
INSERT INTO "imlaei_simple" VALUES(18,69,'18:69','قال ستجدني ان شاء الله صابرا ولا اعصي لك امرا');
INSERT INTO "imlaei_simple" VALUES(18,70,'18:70','قال فان اتبعتني فلا تسالني عن شيء حتى احدث لك منه ذكرا');
INSERT INTO "imlaei_simple" VALUES(18,71,'18:71','فانطلقا حتى اذا ركبا في السفينة خرقها قال اخرقتها لتغرق اهلها لقد جئت شيئا امرا');
INSERT INTO "imlaei_simple" VALUES(18,72,'18:72','قال الم اقل انك لن تستطيع معي صبرا');
INSERT INTO "imlaei_simple" VALUES(18,73,'18:73','قال لا تؤاخذني بما نسيت ولا ترهقني من امري عسرا');
INSERT INTO "imlaei_simple" VALUES(18,74,'18:74','فانطلقا حتى اذا لقيا غلاما فقتله قال اقتلت نفسا زكية بغير نفس لقد جئت شيئا نكرا');
INSERT INTO "imlaei_simple" VALUES(18,75,'18:75','قال الم اقل لك انك لن تستطيع معي صبرا');
INSERT INTO "imlaei_simple" VALUES(18,76,'18:76','قال ان سالتك عن شيء بعدها فلا تصاحبني قد بلغت من لدني عذرا');
INSERT INTO "imlaei_simple" VALUES(18,77,'18:77','فانطلقا حتى اذا اتيا اهل قرية استطعما اهلها فابوا ان يضيفوهما فوجدا فيها جدارا يريد ان ينقض فاقامه قال لو شئت لاتخذت عليه اجرا');
INSERT INTO "imlaei_simple" VALUES(18,78,'18:78','قال هذا فراق بيني وبينك سانبئك بتاويل ما لم تستطع عليه صبرا');
INSERT INTO "imlaei_simple" VALUES(18,79,'18:79','اما السفينة فكانت لمساكين يعملون في البحر فاردت ان اعيبها وكان وراءهم ملك ياخذ كل سفينة غصبا');
INSERT INTO "imlaei_simple" VALUES(18,80,'18:80','واما الغلام فكان ابواه مؤمنين فخشينا ان يرهقهما طغيانا وكفرا');
INSERT INTO "imlaei_simple" VALUES(18,81,'18:81','فاردنا ان يبدلهما ربهما خيرا منه زكاة واقرب رحما');
INSERT INTO "imlaei_simple" VALUES(18,82,'18:82','واما الجدار فكان لغلامين يتيمين في المدينة وكان تحته كنز لهما وكان ابوهما صالحا فاراد ربك ان يبلغا اشدهما ويستخرجا كنزهما رحمة من ربك وما فعلته عن امري ذلك تاويل ما لم تسطع عليه صبرا');
INSERT INTO "imlaei_simple" VALUES(18,83,'18:83','ويسالونك عن ذي القرنين قل ساتلو عليكم منه ذكرا');
INSERT INTO "imlaei_simple" VALUES(18,84,'18:84','انا مكنا له في الارض واتيناه من كل شيء سببا');
INSERT INTO "imlaei_simple" VALUES(18,85,'18:85','فاتبع سببا');
INSERT INTO "imlaei_simple" VALUES(18,86,'18:86','حتى اذا بلغ مغرب الشمس وجدها تغرب في عين حمئة ووجد عندها قوما قلنا يا ذا القرنين اما ان تعذب واما ان تتخذ فيهم حسنا');
INSERT INTO "imlaei_simple" VALUES(18,87,'18:87','قال اما من ظلم فسوف نعذبه ثم يرد الى ربه فيعذبه عذابا نكرا');
INSERT INTO "imlaei_simple" VALUES(18,88,'18:88','واما من امن وعمل صالحا فله جزاء الحسنى وسنقول له من امرنا يسرا');
INSERT INTO "imlaei_simple" VALUES(18,89,'18:89','ثم اتبع سببا');
INSERT INTO "imlaei_simple" VALUES(18,90,'18:90','حتى اذا بلغ مطلع الشمس وجدها تطلع على قوم لم نجعل لهم من دونها سترا');
INSERT INTO "imlaei_simple" VALUES(18,91,'18:91','كذلك وقد احطنا بما لديه خبرا');
INSERT INTO "imlaei_simple" VALUES(18,92,'18:92','ثم اتبع سببا');
INSERT INTO "imlaei_simple" VALUES(18,93,'18:93','حتى اذا بلغ بين السدين وجد من دونهما قوما لا يكادون يفقهون قولا');
INSERT INTO "imlaei_simple" VALUES(18,94,'18:94','قالوا يا ذا القرنين ان ياجوج وماجوج مفسدون في الارض فهل نجعل لك خرجا على ان تجعل بيننا وبينهم سدا');
INSERT INTO "imlaei_simple" VALUES(18,95,'18:95','قال ما مكني فيه ربي خير فاعينوني بقوة اجعل بينكم وبينهم ردما');
INSERT INTO "imlaei_simple" VALUES(18,96,'18:96','اتوني زبر الحديد حتى اذا ساوى بين الصدفين قال انفخوا حتى اذا جعله نارا قال اتوني افرغ عليه قطرا');
INSERT INTO "imlaei_simple" VALUES(18,97,'18:97','فما اسطاعوا ان يظهروه وما استطاعوا له نقبا');
INSERT INTO "imlaei_simple" VALUES(18,98,'18:98','قال هذا رحمة من ربي فاذا جاء وعد ربي جعله دكاء وكان وعد ربي حقا');
INSERT INTO "imlaei_simple" VALUES(18,99,'18:99','وتركنا بعضهم يومئذ يموج في بعض ونفخ في الصور فجمعناهم جمعا');
INSERT INTO "imlaei_simple" VALUES(18,100,'18:100','وعرضنا جهنم يومئذ للكافرين عرضا');
INSERT INTO "imlaei_simple" VALUES(18,101,'18:101','الذين كانت اعينهم في غطاء عن ذكري وكانوا لا يستطيعون سمعا');
INSERT INTO "imlaei_simple" VALUES(18,102,'18:102','افحسب الذين كفروا ان يتخذوا عبادي من دوني اولياء انا اعتدنا جهنم للكافرين نزلا');
INSERT INTO "imlaei_simple" VALUES(18,103,'18:103','قل هل ننبئكم بالاخسرين اعمالا');
INSERT INTO "imlaei_simple" VALUES(18,104,'18:104','الذين ضل سعيهم في الحياة الدنيا وهم يحسبون انهم يحسنون صنعا');
INSERT INTO "imlaei_simple" VALUES(18,105,'18:105','اولئك الذين كفروا بايات ربهم ولقائه فحبطت اعمالهم فلا نقيم لهم يوم القيامة وزنا');
INSERT INTO "imlaei_simple" VALUES(18,106,'18:106','ذلك جزاؤهم جهنم بما كفروا واتخذوا اياتي ورسلي هزوا');
INSERT INTO "imlaei_simple" VALUES(18,107,'18:107','ان الذين امنوا وعملوا الصالحات كانت لهم جنات الفردوس نزلا');
INSERT INTO "imlaei_simple" VALUES(18,108,'18:108','خالدين فيها لا يبغون عنها حولا');
INSERT INTO "imlaei_simple" VALUES(18,109,'18:109','قل لو كان البحر مدادا لكلمات ربي لنفد البحر قبل ان تنفد كلمات ربي ولو جئنا بمثله مددا');
INSERT INTO "imlaei_simple" VALUES(18,110,'18:110','قل انما انا بشر مثلكم يوحى الي انما الهكم اله واحد فمن كان يرجو لقاء ربه فليعمل عملا صالحا ولا يشرك بعبادة ربه احدا');
INSERT INTO "imlaei_simple" VALUES(19,1,'19:1','كهيعص');
INSERT INTO "imlaei_simple" VALUES(19,2,'19:2','ذكر رحمت ربك عبده زكريا');
INSERT INTO "imlaei_simple" VALUES(19,3,'19:3','اذ نادى ربه نداء خفيا');
INSERT INTO "imlaei_simple" VALUES(19,4,'19:4','قال رب اني وهن العظم مني واشتعل الراس شيبا ولم اكن بدعائك رب شقيا');
INSERT INTO "imlaei_simple" VALUES(19,5,'19:5','واني خفت الموالي من ورائي وكانت امراتي عاقرا فهب لي من لدنك وليا');
INSERT INTO "imlaei_simple" VALUES(19,6,'19:6','يرثني ويرث من ال يعقوب واجعله رب رضيا');
INSERT INTO "imlaei_simple" VALUES(19,7,'19:7','يا زكريا انا نبشرك بغلام اسمه يحيى لم نجعل له من قبل سميا');
INSERT INTO "imlaei_simple" VALUES(19,8,'19:8','قال رب انى يكون لي غلام وكانت امراتي عاقرا وقد بلغت من الكبر عتيا');
INSERT INTO "imlaei_simple" VALUES(19,9,'19:9','قال كذلك قال ربك هو علي هين وقد خلقتك من قبل ولم تك شيئا');
INSERT INTO "imlaei_simple" VALUES(19,10,'19:10','قال رب اجعل لي اية قال ايتك الا تكلم الناس ثلاث ليال سويا');
INSERT INTO "imlaei_simple" VALUES(19,11,'19:11','فخرج على قومه من المحراب فاوحى اليهم ان سبحوا بكرة وعشيا');
INSERT INTO "imlaei_simple" VALUES(19,12,'19:12','يا يحيى خذ الكتاب بقوة واتيناه الحكم صبيا');
INSERT INTO "imlaei_simple" VALUES(19,13,'19:13','وحنانا من لدنا وزكاة وكان تقيا');
INSERT INTO "imlaei_simple" VALUES(19,14,'19:14','وبرا بوالديه ولم يكن جبارا عصيا');
INSERT INTO "imlaei_simple" VALUES(19,15,'19:15','وسلام عليه يوم ولد ويوم يموت ويوم يبعث حيا');
INSERT INTO "imlaei_simple" VALUES(19,16,'19:16','واذكر في الكتاب مريم اذ انتبذت من اهلها مكانا شرقيا');
INSERT INTO "imlaei_simple" VALUES(19,17,'19:17','فاتخذت من دونهم حجابا فارسلنا اليها روحنا فتمثل لها بشرا سويا');
INSERT INTO "imlaei_simple" VALUES(19,18,'19:18','قالت اني اعوذ بالرحمن منك ان كنت تقيا');
INSERT INTO "imlaei_simple" VALUES(19,19,'19:19','قال انما انا رسول ربك لاهب لك غلاما زكيا');
INSERT INTO "imlaei_simple" VALUES(19,20,'19:20','قالت انى يكون لي غلام ولم يمسسني بشر ولم اك بغيا');
INSERT INTO "imlaei_simple" VALUES(19,21,'19:21','قال كذلك قال ربك هو علي هين ولنجعله اية للناس ورحمة منا وكان امرا مقضيا');
INSERT INTO "imlaei_simple" VALUES(19,22,'19:22','فحملته فانتبذت به مكانا قصيا');
INSERT INTO "imlaei_simple" VALUES(19,23,'19:23','فاجاءها المخاض الى جذع النخلة قالت يا ليتني مت قبل هذا وكنت نسيا منسيا');
INSERT INTO "imlaei_simple" VALUES(19,24,'19:24','فناداها من تحتها الا تحزني قد جعل ربك تحتك سريا');
INSERT INTO "imlaei_simple" VALUES(19,25,'19:25','وهزي اليك بجذع النخلة تساقط عليك رطبا جنيا');
INSERT INTO "imlaei_simple" VALUES(19,26,'19:26','فكلي واشربي وقري عينا فاما ترين من البشر احدا فقولي اني نذرت للرحمن صوما فلن اكلم اليوم انسيا');
INSERT INTO "imlaei_simple" VALUES(19,27,'19:27','فاتت به قومها تحمله قالوا يا مريم لقد جئت شيئا فريا');
INSERT INTO "imlaei_simple" VALUES(19,28,'19:28','يا اخت هارون ما كان ابوك امرا سوء وما كانت امك بغيا');
INSERT INTO "imlaei_simple" VALUES(19,29,'19:29','فاشارت اليه قالوا كيف نكلم من كان في المهد صبيا');
INSERT INTO "imlaei_simple" VALUES(19,30,'19:30','قال اني عبد الله اتاني الكتاب وجعلني نبيا');
INSERT INTO "imlaei_simple" VALUES(19,31,'19:31','وجعلني مباركا اين ما كنت واوصاني بالصلاة والزكاة ما دمت حيا');
INSERT INTO "imlaei_simple" VALUES(19,32,'19:32','وبرا بوالدتي ولم يجعلني جبارا شقيا');
INSERT INTO "imlaei_simple" VALUES(19,33,'19:33','والسلام علي يوم ولدت ويوم اموت ويوم ابعث حيا');
INSERT INTO "imlaei_simple" VALUES(19,34,'19:34','ذلك عيسى ابن مريم قول الحق الذي فيه يمترون');
INSERT INTO "imlaei_simple" VALUES(19,35,'19:35','ما كان لله ان يتخذ من ولد سبحانه اذا قضى امرا فانما يقول له كن فيكون');
INSERT INTO "imlaei_simple" VALUES(19,36,'19:36','وان الله ربي وربكم فاعبدوه هذا صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(19,37,'19:37','فاختلف الاحزاب من بينهم فويل للذين كفروا من مشهد يوم عظيم');
INSERT INTO "imlaei_simple" VALUES(19,38,'19:38','اسمع بهم وابصر يوم ياتوننا لكن الظالمون اليوم في ضلال مبين');
INSERT INTO "imlaei_simple" VALUES(19,39,'19:39','وانذرهم يوم الحسرة اذ قضي الامر وهم في غفلة وهم لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(19,40,'19:40','انا نحن نرث الارض ومن عليها والينا يرجعون');
INSERT INTO "imlaei_simple" VALUES(19,41,'19:41','واذكر في الكتاب ابراهيم انه كان صديقا نبيا');
INSERT INTO "imlaei_simple" VALUES(19,42,'19:42','اذ قال لابيه يا ابت لم تعبد ما لا يسمع ولا يبصر ولا يغني عنك شيئا');
INSERT INTO "imlaei_simple" VALUES(19,43,'19:43','يا ابت اني قد جاءني من العلم ما لم ياتك فاتبعني اهدك صراطا سويا');
INSERT INTO "imlaei_simple" VALUES(19,44,'19:44','يا ابت لا تعبد الشيطان ان الشيطان كان للرحمن عصيا');
INSERT INTO "imlaei_simple" VALUES(19,45,'19:45','يا ابت اني اخاف ان يمسك عذاب من الرحمن فتكون للشيطان وليا');
INSERT INTO "imlaei_simple" VALUES(19,46,'19:46','قال اراغب انت عن الهتي يا ابراهيم لئن لم تنته لارجمنك واهجرني مليا');
INSERT INTO "imlaei_simple" VALUES(19,47,'19:47','قال سلام عليك ساستغفر لك ربي انه كان بي حفيا');
INSERT INTO "imlaei_simple" VALUES(19,48,'19:48','واعتزلكم وما تدعون من دون الله وادعو ربي عسى الا اكون بدعاء ربي شقيا');
INSERT INTO "imlaei_simple" VALUES(19,49,'19:49','فلما اعتزلهم وما يعبدون من دون الله وهبنا له اسحاق ويعقوب وكلا جعلنا نبيا');
INSERT INTO "imlaei_simple" VALUES(19,50,'19:50','ووهبنا لهم من رحمتنا وجعلنا لهم لسان صدق عليا');
INSERT INTO "imlaei_simple" VALUES(19,51,'19:51','واذكر في الكتاب موسى انه كان مخلصا وكان رسولا نبيا');
INSERT INTO "imlaei_simple" VALUES(19,52,'19:52','وناديناه من جانب الطور الايمن وقربناه نجيا');
INSERT INTO "imlaei_simple" VALUES(19,53,'19:53','ووهبنا له من رحمتنا اخاه هارون نبيا');
INSERT INTO "imlaei_simple" VALUES(19,54,'19:54','واذكر في الكتاب اسماعيل انه كان صادق الوعد وكان رسولا نبيا');
INSERT INTO "imlaei_simple" VALUES(19,55,'19:55','وكان يامر اهله بالصلاة والزكاة وكان عند ربه مرضيا');
INSERT INTO "imlaei_simple" VALUES(19,56,'19:56','واذكر في الكتاب ادريس انه كان صديقا نبيا');
INSERT INTO "imlaei_simple" VALUES(19,57,'19:57','ورفعناه مكانا عليا');
INSERT INTO "imlaei_simple" VALUES(19,58,'19:58','اولئك الذين انعم الله عليهم من النبيين من ذرية ادم وممن حملنا مع نوح ومن ذرية ابراهيم واسرائيل وممن هدينا واجتبينا اذا تتلى عليهم ايات الرحمن خروا سجدا وبكيا');
INSERT INTO "imlaei_simple" VALUES(19,59,'19:59','فخلف من بعدهم خلف اضاعوا الصلاة واتبعوا الشهوات فسوف يلقون غيا');
INSERT INTO "imlaei_simple" VALUES(19,60,'19:60','الا من تاب وامن وعمل صالحا فاولئك يدخلون الجنة ولا يظلمون شيئا');
INSERT INTO "imlaei_simple" VALUES(19,61,'19:61','جنات عدن التي وعد الرحمن عباده بالغيب انه كان وعده ماتيا');
INSERT INTO "imlaei_simple" VALUES(19,62,'19:62','لا يسمعون فيها لغوا الا سلاما ولهم رزقهم فيها بكرة وعشيا');
INSERT INTO "imlaei_simple" VALUES(19,63,'19:63','تلك الجنة التي نورث من عبادنا من كان تقيا');
INSERT INTO "imlaei_simple" VALUES(19,64,'19:64','وما نتنزل الا بامر ربك له ما بين ايدينا وما خلفنا وما بين ذلك وما كان ربك نسيا');
INSERT INTO "imlaei_simple" VALUES(19,65,'19:65','رب السماوات والارض وما بينهما فاعبده واصطبر لعبادته هل تعلم له سميا');
INSERT INTO "imlaei_simple" VALUES(19,66,'19:66','ويقول الانسان ااذا ما مت لسوف اخرج حيا');
INSERT INTO "imlaei_simple" VALUES(19,67,'19:67','اولا يذكر الانسان انا خلقناه من قبل ولم يك شيئا');
INSERT INTO "imlaei_simple" VALUES(19,68,'19:68','فوربك لنحشرنهم والشياطين ثم لنحضرنهم حول جهنم جثيا');
INSERT INTO "imlaei_simple" VALUES(19,69,'19:69','ثم لننزعن من كل شيعة ايهم اشد على الرحمن عتيا');
INSERT INTO "imlaei_simple" VALUES(19,70,'19:70','ثم لنحن اعلم بالذين هم اولى بها صليا');
INSERT INTO "imlaei_simple" VALUES(19,71,'19:71','وان منكم الا واردها كان على ربك حتما مقضيا');
INSERT INTO "imlaei_simple" VALUES(19,72,'19:72','ثم ننجي الذين اتقوا ونذر الظالمين فيها جثيا');
INSERT INTO "imlaei_simple" VALUES(19,73,'19:73','واذا تتلى عليهم اياتنا بينات قال الذين كفروا للذين امنوا اي الفريقين خير مقاما واحسن نديا');
INSERT INTO "imlaei_simple" VALUES(19,74,'19:74','وكم اهلكنا قبلهم من قرن هم احسن اثاثا ورئيا');
INSERT INTO "imlaei_simple" VALUES(19,75,'19:75','قل من كان في الضلالة فليمدد له الرحمن مدا حتى اذا راوا ما يوعدون اما العذاب واما الساعة فسيعلمون من هو شر مكانا واضعف جندا');
INSERT INTO "imlaei_simple" VALUES(19,76,'19:76','ويزيد الله الذين اهتدوا هدى والباقيات الصالحات خير عند ربك ثوابا وخير مردا');
INSERT INTO "imlaei_simple" VALUES(19,77,'19:77','افرايت الذي كفر باياتنا وقال لاوتين مالا وولدا');
INSERT INTO "imlaei_simple" VALUES(19,78,'19:78','اطلع الغيب ام اتخذ عند الرحمن عهدا');
INSERT INTO "imlaei_simple" VALUES(19,79,'19:79','كلا سنكتب ما يقول ونمد له من العذاب مدا');
INSERT INTO "imlaei_simple" VALUES(19,80,'19:80','ونرثه ما يقول وياتينا فردا');
INSERT INTO "imlaei_simple" VALUES(19,81,'19:81','واتخذوا من دون الله الهة ليكونوا لهم عزا');
INSERT INTO "imlaei_simple" VALUES(19,82,'19:82','كلا سيكفرون بعبادتهم ويكونون عليهم ضدا');
INSERT INTO "imlaei_simple" VALUES(19,83,'19:83','الم تر انا ارسلنا الشياطين على الكافرين تؤزهم ازا');
INSERT INTO "imlaei_simple" VALUES(19,84,'19:84','فلا تعجل عليهم انما نعد لهم عدا');
INSERT INTO "imlaei_simple" VALUES(19,85,'19:85','يوم نحشر المتقين الى الرحمن وفدا');
INSERT INTO "imlaei_simple" VALUES(19,86,'19:86','ونسوق المجرمين الى جهنم وردا');
INSERT INTO "imlaei_simple" VALUES(19,87,'19:87','لا يملكون الشفاعة الا من اتخذ عند الرحمن عهدا');
INSERT INTO "imlaei_simple" VALUES(19,88,'19:88','وقالوا اتخذ الرحمن ولدا');
INSERT INTO "imlaei_simple" VALUES(19,89,'19:89','لقد جئتم شيئا ادا');
INSERT INTO "imlaei_simple" VALUES(19,90,'19:90','تكاد السماوات يتفطرن منه وتنشق الارض وتخر الجبال هدا');
INSERT INTO "imlaei_simple" VALUES(19,91,'19:91','ان دعوا للرحمن ولدا');
INSERT INTO "imlaei_simple" VALUES(19,92,'19:92','وما ينبغي للرحمن ان يتخذ ولدا');
INSERT INTO "imlaei_simple" VALUES(19,93,'19:93','ان كل من في السماوات والارض الا اتي الرحمن عبدا');
INSERT INTO "imlaei_simple" VALUES(19,94,'19:94','لقد احصاهم وعدهم عدا');
INSERT INTO "imlaei_simple" VALUES(19,95,'19:95','وكلهم اتيه يوم القيامة فردا');
INSERT INTO "imlaei_simple" VALUES(19,96,'19:96','ان الذين امنوا وعملوا الصالحات سيجعل لهم الرحمن ودا');
INSERT INTO "imlaei_simple" VALUES(19,97,'19:97','فانما يسرناه بلسانك لتبشر به المتقين وتنذر به قوما لدا');
INSERT INTO "imlaei_simple" VALUES(19,98,'19:98','وكم اهلكنا قبلهم من قرن هل تحس منهم من احد او تسمع لهم ركزا');
INSERT INTO "imlaei_simple" VALUES(20,1,'20:1','طه');
INSERT INTO "imlaei_simple" VALUES(20,2,'20:2','ما انزلنا عليك القران لتشقى');
INSERT INTO "imlaei_simple" VALUES(20,3,'20:3','الا تذكرة لمن يخشى');
INSERT INTO "imlaei_simple" VALUES(20,4,'20:4','تنزيلا ممن خلق الارض والسماوات العلى');
INSERT INTO "imlaei_simple" VALUES(20,5,'20:5','الرحمن على العرش استوى');
INSERT INTO "imlaei_simple" VALUES(20,6,'20:6','له ما في السماوات وما في الارض وما بينهما وما تحت الثرى');
INSERT INTO "imlaei_simple" VALUES(20,7,'20:7','وان تجهر بالقول فانه يعلم السر واخفى');
INSERT INTO "imlaei_simple" VALUES(20,8,'20:8','الله لا اله الا هو له الاسماء الحسنى');
INSERT INTO "imlaei_simple" VALUES(20,9,'20:9','وهل اتاك حديث موسى');
INSERT INTO "imlaei_simple" VALUES(20,10,'20:10','اذ راى نارا فقال لاهله امكثوا اني انست نارا لعلي اتيكم منها بقبس او اجد على النار هدى');
INSERT INTO "imlaei_simple" VALUES(20,11,'20:11','فلما اتاها نودي يا موسى');
INSERT INTO "imlaei_simple" VALUES(20,12,'20:12','اني انا ربك فاخلع نعليك انك بالواد المقدس طوى');
INSERT INTO "imlaei_simple" VALUES(20,13,'20:13','وانا اخترتك فاستمع لما يوحى');
INSERT INTO "imlaei_simple" VALUES(20,14,'20:14','انني انا الله لا اله الا انا فاعبدني واقم الصلاة لذكري');
INSERT INTO "imlaei_simple" VALUES(20,15,'20:15','ان الساعة اتية اكاد اخفيها لتجزى كل نفس بما تسعى');
INSERT INTO "imlaei_simple" VALUES(20,16,'20:16','فلا يصدنك عنها من لا يؤمن بها واتبع هواه فتردى');
INSERT INTO "imlaei_simple" VALUES(20,17,'20:17','وما تلك بيمينك يا موسى');
INSERT INTO "imlaei_simple" VALUES(20,18,'20:18','قال هي عصاي اتوكا عليها واهش بها على غنمي ولي فيها مارب اخرى');
INSERT INTO "imlaei_simple" VALUES(20,19,'20:19','قال القها يا موسى');
INSERT INTO "imlaei_simple" VALUES(20,20,'20:20','فالقاها فاذا هي حية تسعى');
INSERT INTO "imlaei_simple" VALUES(20,21,'20:21','قال خذها ولا تخف سنعيدها سيرتها الاولى');
INSERT INTO "imlaei_simple" VALUES(20,22,'20:22','واضمم يدك الى جناحك تخرج بيضاء من غير سوء اية اخرى');
INSERT INTO "imlaei_simple" VALUES(20,23,'20:23','لنريك من اياتنا الكبرى');
INSERT INTO "imlaei_simple" VALUES(20,24,'20:24','اذهب الى فرعون انه طغى');
INSERT INTO "imlaei_simple" VALUES(20,25,'20:25','قال رب اشرح لي صدري');
INSERT INTO "imlaei_simple" VALUES(20,26,'20:26','ويسر لي امري');
INSERT INTO "imlaei_simple" VALUES(20,27,'20:27','واحلل عقدة من لساني');
INSERT INTO "imlaei_simple" VALUES(20,28,'20:28','يفقهوا قولي');
INSERT INTO "imlaei_simple" VALUES(20,29,'20:29','واجعل لي وزيرا من اهلي');
INSERT INTO "imlaei_simple" VALUES(20,30,'20:30','هارون اخي');
INSERT INTO "imlaei_simple" VALUES(20,31,'20:31','اشدد به ازري');
INSERT INTO "imlaei_simple" VALUES(20,32,'20:32','واشركه في امري');
INSERT INTO "imlaei_simple" VALUES(20,33,'20:33','كي نسبحك كثيرا');
INSERT INTO "imlaei_simple" VALUES(20,34,'20:34','ونذكرك كثيرا');
INSERT INTO "imlaei_simple" VALUES(20,35,'20:35','انك كنت بنا بصيرا');
INSERT INTO "imlaei_simple" VALUES(20,36,'20:36','قال قد اوتيت سؤلك يا موسى');
INSERT INTO "imlaei_simple" VALUES(20,37,'20:37','ولقد مننا عليك مرة اخرى');
INSERT INTO "imlaei_simple" VALUES(20,38,'20:38','اذ اوحينا الى امك ما يوحى');
INSERT INTO "imlaei_simple" VALUES(20,39,'20:39','ان اقذفيه في التابوت فاقذفيه في اليم فليلقه اليم بالساحل ياخذه عدو لي وعدو له والقيت عليك محبة مني ولتصنع على عيني');
INSERT INTO "imlaei_simple" VALUES(20,40,'20:40','اذ تمشي اختك فتقول هل ادلكم على من يكفله فرجعناك الى امك كي تقر عينها ولا تحزن وقتلت نفسا فنجيناك من الغم وفتناك فتونا فلبثت سنين في اهل مدين ثم جئت على قدر يا موسى');
INSERT INTO "imlaei_simple" VALUES(20,41,'20:41','واصطنعتك لنفسي');
INSERT INTO "imlaei_simple" VALUES(20,42,'20:42','اذهب انت واخوك باياتي ولا تنيا في ذكري');
INSERT INTO "imlaei_simple" VALUES(20,43,'20:43','اذهبا الى فرعون انه طغى');
INSERT INTO "imlaei_simple" VALUES(20,44,'20:44','فقولا له قولا لينا لعله يتذكر او يخشى');
INSERT INTO "imlaei_simple" VALUES(20,45,'20:45','قالا ربنا اننا نخاف ان يفرط علينا او ان يطغى');
INSERT INTO "imlaei_simple" VALUES(20,46,'20:46','قال لا تخافا انني معكما اسمع وارى');
INSERT INTO "imlaei_simple" VALUES(20,47,'20:47','فاتياه فقولا انا رسولا ربك فارسل معنا بني اسرائيل ولا تعذبهم قد جئناك باية من ربك والسلام على من اتبع الهدى');
INSERT INTO "imlaei_simple" VALUES(20,48,'20:48','انا قد اوحي الينا ان العذاب على من كذب وتولى');
INSERT INTO "imlaei_simple" VALUES(20,49,'20:49','قال فمن ربكما يا موسى');
INSERT INTO "imlaei_simple" VALUES(20,50,'20:50','قال ربنا الذي اعطى كل شيء خلقه ثم هدى');
INSERT INTO "imlaei_simple" VALUES(20,51,'20:51','قال فما بال القرون الاولى');
INSERT INTO "imlaei_simple" VALUES(20,52,'20:52','قال علمها عند ربي في كتاب لا يضل ربي ولا ينسى');
INSERT INTO "imlaei_simple" VALUES(20,53,'20:53','الذي جعل لكم الارض مهدا وسلك لكم فيها سبلا وانزل من السماء ماء فاخرجنا به ازواجا من نبات شتى');
INSERT INTO "imlaei_simple" VALUES(20,54,'20:54','كلوا وارعوا انعامكم ان في ذلك لايات لاولي النهى');
INSERT INTO "imlaei_simple" VALUES(20,55,'20:55','منها خلقناكم وفيها نعيدكم ومنها نخرجكم تارة اخرى');
INSERT INTO "imlaei_simple" VALUES(20,56,'20:56','ولقد اريناه اياتنا كلها فكذب وابى');
INSERT INTO "imlaei_simple" VALUES(20,57,'20:57','قال اجئتنا لتخرجنا من ارضنا بسحرك يا موسى');
INSERT INTO "imlaei_simple" VALUES(20,58,'20:58','فلناتينك بسحر مثله فاجعل بيننا وبينك موعدا لا نخلفه نحن ولا انت مكانا سوى');
INSERT INTO "imlaei_simple" VALUES(20,59,'20:59','قال موعدكم يوم الزينة وان يحشر الناس ضحى');
INSERT INTO "imlaei_simple" VALUES(20,60,'20:60','فتولى فرعون فجمع كيده ثم اتى');
INSERT INTO "imlaei_simple" VALUES(20,61,'20:61','قال لهم موسى ويلكم لا تفتروا على الله كذبا فيسحتكم بعذاب وقد خاب من افترى');
INSERT INTO "imlaei_simple" VALUES(20,62,'20:62','فتنازعوا امرهم بينهم واسروا النجوى');
INSERT INTO "imlaei_simple" VALUES(20,63,'20:63','قالوا ان هذان لساحران يريدان ان يخرجاكم من ارضكم بسحرهما ويذهبا بطريقتكم المثلى');
INSERT INTO "imlaei_simple" VALUES(20,64,'20:64','فاجمعوا كيدكم ثم ائتوا صفا وقد افلح اليوم من استعلى');
INSERT INTO "imlaei_simple" VALUES(20,65,'20:65','قالوا يا موسى اما ان تلقي واما ان نكون اول من القى');
INSERT INTO "imlaei_simple" VALUES(20,66,'20:66','قال بل القوا فاذا حبالهم وعصيهم يخيل اليه من سحرهم انها تسعى');
INSERT INTO "imlaei_simple" VALUES(20,67,'20:67','فاوجس في نفسه خيفة موسى');
INSERT INTO "imlaei_simple" VALUES(20,68,'20:68','قلنا لا تخف انك انت الاعلى');
INSERT INTO "imlaei_simple" VALUES(20,69,'20:69','والق ما في يمينك تلقف ما صنعوا انما صنعوا كيد ساحر ولا يفلح الساحر حيث اتى');
INSERT INTO "imlaei_simple" VALUES(20,70,'20:70','فالقي السحرة سجدا قالوا امنا برب هارون وموسى');
INSERT INTO "imlaei_simple" VALUES(20,71,'20:71','قال امنتم له قبل ان اذن لكم انه لكبيركم الذي علمكم السحر فلاقطعن ايديكم وارجلكم من خلاف ولاصلبنكم في جذوع النخل ولتعلمن اينا اشد عذابا وابقى');
INSERT INTO "imlaei_simple" VALUES(20,72,'20:72','قالوا لن نؤثرك على ما جاءنا من البينات والذي فطرنا فاقض ما انت قاض انما تقضي هذه الحياة الدنيا');
INSERT INTO "imlaei_simple" VALUES(20,73,'20:73','انا امنا بربنا ليغفر لنا خطايانا وما اكرهتنا عليه من السحر والله خير وابقى');
INSERT INTO "imlaei_simple" VALUES(20,74,'20:74','انه من يات ربه مجرما فان له جهنم لا يموت فيها ولا يحيى');
INSERT INTO "imlaei_simple" VALUES(20,75,'20:75','ومن ياته مؤمنا قد عمل الصالحات فاولئك لهم الدرجات العلى');
INSERT INTO "imlaei_simple" VALUES(20,76,'20:76','جنات عدن تجري من تحتها الانهار خالدين فيها وذلك جزاء من تزكى');
INSERT INTO "imlaei_simple" VALUES(20,77,'20:77','ولقد اوحينا الى موسى ان اسر بعبادي فاضرب لهم طريقا في البحر يبسا لا تخاف دركا ولا تخشى');
INSERT INTO "imlaei_simple" VALUES(20,78,'20:78','فاتبعهم فرعون بجنوده فغشيهم من اليم ما غشيهم');
INSERT INTO "imlaei_simple" VALUES(20,79,'20:79','واضل فرعون قومه وما هدى');
INSERT INTO "imlaei_simple" VALUES(20,80,'20:80','يا بني اسرائيل قد انجيناكم من عدوكم وواعدناكم جانب الطور الايمن ونزلنا عليكم المن والسلوى');
INSERT INTO "imlaei_simple" VALUES(20,81,'20:81','كلوا من طيبات ما رزقناكم ولا تطغوا فيه فيحل عليكم غضبي ومن يحلل عليه غضبي فقد هوى');
INSERT INTO "imlaei_simple" VALUES(20,82,'20:82','واني لغفار لمن تاب وامن وعمل صالحا ثم اهتدى');
INSERT INTO "imlaei_simple" VALUES(20,83,'20:83','وما اعجلك عن قومك يا موسى');
INSERT INTO "imlaei_simple" VALUES(20,84,'20:84','قال هم اولاء على اثري وعجلت اليك رب لترضى');
INSERT INTO "imlaei_simple" VALUES(20,85,'20:85','قال فانا قد فتنا قومك من بعدك واضلهم السامري');
INSERT INTO "imlaei_simple" VALUES(20,86,'20:86','فرجع موسى الى قومه غضبان اسفا قال يا قوم الم يعدكم ربكم وعدا حسنا افطال عليكم العهد ام اردتم ان يحل عليكم غضب من ربكم فاخلفتم موعدي');
INSERT INTO "imlaei_simple" VALUES(20,87,'20:87','قالوا ما اخلفنا موعدك بملكنا ولكنا حملنا اوزارا من زينة القوم فقذفناها فكذلك القى السامري');
INSERT INTO "imlaei_simple" VALUES(20,88,'20:88','فاخرج لهم عجلا جسدا له خوار فقالوا هذا الهكم واله موسى فنسي');
INSERT INTO "imlaei_simple" VALUES(20,89,'20:89','افلا يرون الا يرجع اليهم قولا ولا يملك لهم ضرا ولا نفعا');
INSERT INTO "imlaei_simple" VALUES(20,90,'20:90','ولقد قال لهم هارون من قبل يا قوم انما فتنتم به وان ربكم الرحمن فاتبعوني واطيعوا امري');
INSERT INTO "imlaei_simple" VALUES(20,91,'20:91','قالوا لن نبرح عليه عاكفين حتى يرجع الينا موسى');
INSERT INTO "imlaei_simple" VALUES(20,92,'20:92','قال يا هارون ما منعك اذ رايتهم ضلوا');
INSERT INTO "imlaei_simple" VALUES(20,93,'20:93','الا تتبعن افعصيت امري');
INSERT INTO "imlaei_simple" VALUES(20,94,'20:94','قال يا ابن ام لا تاخذ بلحيتي ولا براسي اني خشيت ان تقول فرقت بين بني اسرائيل ولم ترقب قولي');
INSERT INTO "imlaei_simple" VALUES(20,95,'20:95','قال فما خطبك يا سامري');
INSERT INTO "imlaei_simple" VALUES(20,96,'20:96','قال بصرت بما لم يبصروا به فقبضت قبضة من اثر الرسول فنبذتها وكذلك سولت لي نفسي');
INSERT INTO "imlaei_simple" VALUES(20,97,'20:97','قال فاذهب فان لك في الحياة ان تقول لا مساس وان لك موعدا لن تخلفه وانظر الى الهك الذي ظلت عليه عاكفا لنحرقنه ثم لننسفنه في اليم نسفا');
INSERT INTO "imlaei_simple" VALUES(20,98,'20:98','انما الهكم الله الذي لا اله الا هو وسع كل شيء علما');
INSERT INTO "imlaei_simple" VALUES(20,99,'20:99','كذلك نقص عليك من انباء ما قد سبق وقد اتيناك من لدنا ذكرا');
INSERT INTO "imlaei_simple" VALUES(20,100,'20:100','من اعرض عنه فانه يحمل يوم القيامة وزرا');
INSERT INTO "imlaei_simple" VALUES(20,101,'20:101','خالدين فيه وساء لهم يوم القيامة حملا');
INSERT INTO "imlaei_simple" VALUES(20,102,'20:102','يوم ينفخ في الصور ونحشر المجرمين يومئذ زرقا');
INSERT INTO "imlaei_simple" VALUES(20,103,'20:103','يتخافتون بينهم ان لبثتم الا عشرا');
INSERT INTO "imlaei_simple" VALUES(20,104,'20:104','نحن اعلم بما يقولون اذ يقول امثلهم طريقة ان لبثتم الا يوما');
INSERT INTO "imlaei_simple" VALUES(20,105,'20:105','ويسالونك عن الجبال فقل ينسفها ربي نسفا');
INSERT INTO "imlaei_simple" VALUES(20,106,'20:106','فيذرها قاعا صفصفا');
INSERT INTO "imlaei_simple" VALUES(20,107,'20:107','لا ترى فيها عوجا ولا امتا');
INSERT INTO "imlaei_simple" VALUES(20,108,'20:108','يومئذ يتبعون الداعي لا عوج له وخشعت الاصوات للرحمن فلا تسمع الا همسا');
INSERT INTO "imlaei_simple" VALUES(20,109,'20:109','يومئذ لا تنفع الشفاعة الا من اذن له الرحمن ورضي له قولا');
INSERT INTO "imlaei_simple" VALUES(20,110,'20:110','يعلم ما بين ايديهم وما خلفهم ولا يحيطون به علما');
INSERT INTO "imlaei_simple" VALUES(20,111,'20:111','وعنت الوجوه للحي القيوم وقد خاب من حمل ظلما');
INSERT INTO "imlaei_simple" VALUES(20,112,'20:112','ومن يعمل من الصالحات وهو مؤمن فلا يخاف ظلما ولا هضما');
INSERT INTO "imlaei_simple" VALUES(20,113,'20:113','وكذلك انزلناه قرانا عربيا وصرفنا فيه من الوعيد لعلهم يتقون او يحدث لهم ذكرا');
INSERT INTO "imlaei_simple" VALUES(20,114,'20:114','فتعالى الله الملك الحق ولا تعجل بالقران من قبل ان يقضى اليك وحيه وقل رب زدني علما');
INSERT INTO "imlaei_simple" VALUES(20,115,'20:115','ولقد عهدنا الى ادم من قبل فنسي ولم نجد له عزما');
INSERT INTO "imlaei_simple" VALUES(20,116,'20:116','واذ قلنا للملائكة اسجدوا لادم فسجدوا الا ابليس ابى');
INSERT INTO "imlaei_simple" VALUES(20,117,'20:117','فقلنا يا ادم ان هذا عدو لك ولزوجك فلا يخرجنكما من الجنة فتشقى');
INSERT INTO "imlaei_simple" VALUES(20,118,'20:118','ان لك الا تجوع فيها ولا تعرى');
INSERT INTO "imlaei_simple" VALUES(20,119,'20:119','وانك لا تظما فيها ولا تضحى');
INSERT INTO "imlaei_simple" VALUES(20,120,'20:120','فوسوس اليه الشيطان قال يا ادم هل ادلك على شجرة الخلد وملك لا يبلى');
INSERT INTO "imlaei_simple" VALUES(20,121,'20:121','فاكلا منها فبدت لهما سواتهما وطفقا يخصفان عليهما من ورق الجنة وعصى ادم ربه فغوى');
INSERT INTO "imlaei_simple" VALUES(20,122,'20:122','ثم اجتباه ربه فتاب عليه وهدى');
INSERT INTO "imlaei_simple" VALUES(20,123,'20:123','قال اهبطا منها جميعا بعضكم لبعض عدو فاما ياتينكم مني هدى فمن اتبع هداي فلا يضل ولا يشقى');
INSERT INTO "imlaei_simple" VALUES(20,124,'20:124','ومن اعرض عن ذكري فان له معيشة ضنكا ونحشره يوم القيامة اعمى');
INSERT INTO "imlaei_simple" VALUES(20,125,'20:125','قال رب لم حشرتني اعمى وقد كنت بصيرا');
INSERT INTO "imlaei_simple" VALUES(20,126,'20:126','قال كذلك اتتك اياتنا فنسيتها وكذلك اليوم تنسى');
INSERT INTO "imlaei_simple" VALUES(20,127,'20:127','وكذلك نجزي من اسرف ولم يؤمن بايات ربه ولعذاب الاخرة اشد وابقى');
INSERT INTO "imlaei_simple" VALUES(20,128,'20:128','افلم يهد لهم كم اهلكنا قبلهم من القرون يمشون في مساكنهم ان في ذلك لايات لاولي النهى');
INSERT INTO "imlaei_simple" VALUES(20,129,'20:129','ولولا كلمة سبقت من ربك لكان لزاما واجل مسمى');
INSERT INTO "imlaei_simple" VALUES(20,130,'20:130','فاصبر على ما يقولون وسبح بحمد ربك قبل طلوع الشمس وقبل غروبها ومن اناء الليل فسبح واطراف النهار لعلك ترضى');
INSERT INTO "imlaei_simple" VALUES(20,131,'20:131','ولا تمدن عينيك الى ما متعنا به ازواجا منهم زهرة الحياة الدنيا لنفتنهم فيه ورزق ربك خير وابقى');
INSERT INTO "imlaei_simple" VALUES(20,132,'20:132','وامر اهلك بالصلاة واصطبر عليها لا نسالك رزقا نحن نرزقك والعاقبة للتقوى');
INSERT INTO "imlaei_simple" VALUES(20,133,'20:133','وقالوا لولا ياتينا باية من ربه اولم تاتهم بينة ما في الصحف الاولى');
INSERT INTO "imlaei_simple" VALUES(20,134,'20:134','ولو انا اهلكناهم بعذاب من قبله لقالوا ربنا لولا ارسلت الينا رسولا فنتبع اياتك من قبل ان نذل ونخزى');
INSERT INTO "imlaei_simple" VALUES(20,135,'20:135','قل كل متربص فتربصوا فستعلمون من اصحاب الصراط السوي ومن اهتدى');
INSERT INTO "imlaei_simple" VALUES(21,1,'21:1','اقترب للناس حسابهم وهم في غفلة معرضون');
INSERT INTO "imlaei_simple" VALUES(21,2,'21:2','ما ياتيهم من ذكر من ربهم محدث الا استمعوه وهم يلعبون');
INSERT INTO "imlaei_simple" VALUES(21,3,'21:3','لاهية قلوبهم واسروا النجوى الذين ظلموا هل هذا الا بشر مثلكم افتاتون السحر وانتم تبصرون');
INSERT INTO "imlaei_simple" VALUES(21,4,'21:4','قال ربي يعلم القول في السماء والارض وهو السميع العليم');
INSERT INTO "imlaei_simple" VALUES(21,5,'21:5','بل قالوا اضغاث احلام بل افتراه بل هو شاعر فلياتنا باية كما ارسل الاولون');
INSERT INTO "imlaei_simple" VALUES(21,6,'21:6','ما امنت قبلهم من قرية اهلكناها افهم يؤمنون');
INSERT INTO "imlaei_simple" VALUES(21,7,'21:7','وما ارسلنا قبلك الا رجالا نوحي اليهم فاسالوا اهل الذكر ان كنتم لا تعلمون');
INSERT INTO "imlaei_simple" VALUES(21,8,'21:8','وما جعلناهم جسدا لا ياكلون الطعام وما كانوا خالدين');
INSERT INTO "imlaei_simple" VALUES(21,9,'21:9','ثم صدقناهم الوعد فانجيناهم ومن نشاء واهلكنا المسرفين');
INSERT INTO "imlaei_simple" VALUES(21,10,'21:10','لقد انزلنا اليكم كتابا فيه ذكركم افلا تعقلون');
INSERT INTO "imlaei_simple" VALUES(21,11,'21:11','وكم قصمنا من قرية كانت ظالمة وانشانا بعدها قوما اخرين');
INSERT INTO "imlaei_simple" VALUES(21,12,'21:12','فلما احسوا باسنا اذا هم منها يركضون');
INSERT INTO "imlaei_simple" VALUES(21,13,'21:13','لا تركضوا وارجعوا الى ما اترفتم فيه ومساكنكم لعلكم تسالون');
INSERT INTO "imlaei_simple" VALUES(21,14,'21:14','قالوا يا ويلنا انا كنا ظالمين');
INSERT INTO "imlaei_simple" VALUES(21,15,'21:15','فما زالت تلك دعواهم حتى جعلناهم حصيدا خامدين');
INSERT INTO "imlaei_simple" VALUES(21,16,'21:16','وما خلقنا السماء والارض وما بينهما لاعبين');
INSERT INTO "imlaei_simple" VALUES(21,17,'21:17','لو اردنا ان نتخذ لهوا لاتخذناه من لدنا ان كنا فاعلين');
INSERT INTO "imlaei_simple" VALUES(21,18,'21:18','بل نقذف بالحق على الباطل فيدمغه فاذا هو زاهق ولكم الويل مما تصفون');
INSERT INTO "imlaei_simple" VALUES(21,19,'21:19','وله من في السماوات والارض ومن عنده لا يستكبرون عن عبادته ولا يستحسرون');
INSERT INTO "imlaei_simple" VALUES(21,20,'21:20','يسبحون الليل والنهار لا يفترون');
INSERT INTO "imlaei_simple" VALUES(21,21,'21:21','ام اتخذوا الهة من الارض هم ينشرون');
INSERT INTO "imlaei_simple" VALUES(21,22,'21:22','لو كان فيهما الهة الا الله لفسدتا فسبحان الله رب العرش عما يصفون');
INSERT INTO "imlaei_simple" VALUES(21,23,'21:23','لا يسال عما يفعل وهم يسالون');
INSERT INTO "imlaei_simple" VALUES(21,24,'21:24','ام اتخذوا من دونه الهة قل هاتوا برهانكم هذا ذكر من معي وذكر من قبلي بل اكثرهم لا يعلمون الحق فهم معرضون');
INSERT INTO "imlaei_simple" VALUES(21,25,'21:25','وما ارسلنا من قبلك من رسول الا نوحي اليه انه لا اله الا انا فاعبدون');
INSERT INTO "imlaei_simple" VALUES(21,26,'21:26','وقالوا اتخذ الرحمن ولدا سبحانه بل عباد مكرمون');
INSERT INTO "imlaei_simple" VALUES(21,27,'21:27','لا يسبقونه بالقول وهم بامره يعملون');
INSERT INTO "imlaei_simple" VALUES(21,28,'21:28','يعلم ما بين ايديهم وما خلفهم ولا يشفعون الا لمن ارتضى وهم من خشيته مشفقون');
INSERT INTO "imlaei_simple" VALUES(21,29,'21:29','ومن يقل منهم اني اله من دونه فذلك نجزيه جهنم كذلك نجزي الظالمين');
INSERT INTO "imlaei_simple" VALUES(21,30,'21:30','اولم ير الذين كفروا ان السماوات والارض كانتا رتقا ففتقناهما وجعلنا من الماء كل شيء حي افلا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(21,31,'21:31','وجعلنا في الارض رواسي ان تميد بهم وجعلنا فيها فجاجا سبلا لعلهم يهتدون');
INSERT INTO "imlaei_simple" VALUES(21,32,'21:32','وجعلنا السماء سقفا محفوظا وهم عن اياتها معرضون');
INSERT INTO "imlaei_simple" VALUES(21,33,'21:33','وهو الذي خلق الليل والنهار والشمس والقمر كل في فلك يسبحون');
INSERT INTO "imlaei_simple" VALUES(21,34,'21:34','وما جعلنا لبشر من قبلك الخلد افان مت فهم الخالدون');
INSERT INTO "imlaei_simple" VALUES(21,35,'21:35','كل نفس ذائقة الموت ونبلوكم بالشر والخير فتنة والينا ترجعون');
INSERT INTO "imlaei_simple" VALUES(21,36,'21:36','واذا راك الذين كفروا ان يتخذونك الا هزوا اهذا الذي يذكر الهتكم وهم بذكر الرحمن هم كافرون');
INSERT INTO "imlaei_simple" VALUES(21,37,'21:37','خلق الانسان من عجل ساريكم اياتي فلا تستعجلون');
INSERT INTO "imlaei_simple" VALUES(21,38,'21:38','ويقولون متى هذا الوعد ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(21,39,'21:39','لو يعلم الذين كفروا حين لا يكفون عن وجوههم النار ولا عن ظهورهم ولا هم ينصرون');
INSERT INTO "imlaei_simple" VALUES(21,40,'21:40','بل تاتيهم بغتة فتبهتهم فلا يستطيعون ردها ولا هم ينظرون');
INSERT INTO "imlaei_simple" VALUES(21,41,'21:41','ولقد استهزئ برسل من قبلك فحاق بالذين سخروا منهم ما كانوا به يستهزئون');
INSERT INTO "imlaei_simple" VALUES(21,42,'21:42','قل من يكلؤكم بالليل والنهار من الرحمن بل هم عن ذكر ربهم معرضون');
INSERT INTO "imlaei_simple" VALUES(21,43,'21:43','ام لهم الهة تمنعهم من دوننا لا يستطيعون نصر انفسهم ولا هم منا يصحبون');
INSERT INTO "imlaei_simple" VALUES(21,44,'21:44','بل متعنا هؤلاء واباءهم حتى طال عليهم العمر افلا يرون انا ناتي الارض ننقصها من اطرافها افهم الغالبون');
INSERT INTO "imlaei_simple" VALUES(21,45,'21:45','قل انما انذركم بالوحي ولا يسمع الصم الدعاء اذا ما ينذرون');
INSERT INTO "imlaei_simple" VALUES(21,46,'21:46','ولئن مستهم نفحة من عذاب ربك ليقولن يا ويلنا انا كنا ظالمين');
INSERT INTO "imlaei_simple" VALUES(21,47,'21:47','ونضع الموازين القسط ليوم القيامة فلا تظلم نفس شيئا وان كان مثقال حبة من خردل اتينا بها وكفى بنا حاسبين');
INSERT INTO "imlaei_simple" VALUES(21,48,'21:48','ولقد اتينا موسى وهارون الفرقان وضياء وذكرا للمتقين');
INSERT INTO "imlaei_simple" VALUES(21,49,'21:49','الذين يخشون ربهم بالغيب وهم من الساعة مشفقون');
INSERT INTO "imlaei_simple" VALUES(21,50,'21:50','وهذا ذكر مبارك انزلناه افانتم له منكرون');
INSERT INTO "imlaei_simple" VALUES(21,51,'21:51','ولقد اتينا ابراهيم رشده من قبل وكنا به عالمين');
INSERT INTO "imlaei_simple" VALUES(21,52,'21:52','اذ قال لابيه وقومه ما هذه التماثيل التي انتم لها عاكفون');
INSERT INTO "imlaei_simple" VALUES(21,53,'21:53','قالوا وجدنا اباءنا لها عابدين');
INSERT INTO "imlaei_simple" VALUES(21,54,'21:54','قال لقد كنتم انتم واباؤكم في ضلال مبين');
INSERT INTO "imlaei_simple" VALUES(21,55,'21:55','قالوا اجئتنا بالحق ام انت من اللاعبين');
INSERT INTO "imlaei_simple" VALUES(21,56,'21:56','قال بل ربكم رب السماوات والارض الذي فطرهن وانا على ذلكم من الشاهدين');
INSERT INTO "imlaei_simple" VALUES(21,57,'21:57','وتالله لاكيدن اصنامكم بعد ان تولوا مدبرين');
INSERT INTO "imlaei_simple" VALUES(21,58,'21:58','فجعلهم جذاذا الا كبيرا لهم لعلهم اليه يرجعون');
INSERT INTO "imlaei_simple" VALUES(21,59,'21:59','قالوا من فعل هذا بالهتنا انه لمن الظالمين');
INSERT INTO "imlaei_simple" VALUES(21,60,'21:60','قالوا سمعنا فتى يذكرهم يقال له ابراهيم');
INSERT INTO "imlaei_simple" VALUES(21,61,'21:61','قالوا فاتوا به على اعين الناس لعلهم يشهدون');
INSERT INTO "imlaei_simple" VALUES(21,62,'21:62','قالوا اانت فعلت هذا بالهتنا يا ابراهيم');
INSERT INTO "imlaei_simple" VALUES(21,63,'21:63','قال بل فعله كبيرهم هذا فاسالوهم ان كانوا ينطقون');
INSERT INTO "imlaei_simple" VALUES(21,64,'21:64','فرجعوا الى انفسهم فقالوا انكم انتم الظالمون');
INSERT INTO "imlaei_simple" VALUES(21,65,'21:65','ثم نكسوا على رءوسهم لقد علمت ما هؤلاء ينطقون');
INSERT INTO "imlaei_simple" VALUES(21,66,'21:66','قال افتعبدون من دون الله ما لا ينفعكم شيئا ولا يضركم');
INSERT INTO "imlaei_simple" VALUES(21,67,'21:67','اف لكم ولما تعبدون من دون الله افلا تعقلون');
INSERT INTO "imlaei_simple" VALUES(21,68,'21:68','قالوا حرقوه وانصروا الهتكم ان كنتم فاعلين');
INSERT INTO "imlaei_simple" VALUES(21,69,'21:69','قلنا يا نار كوني بردا وسلاما على ابراهيم');
INSERT INTO "imlaei_simple" VALUES(21,70,'21:70','وارادوا به كيدا فجعلناهم الاخسرين');
INSERT INTO "imlaei_simple" VALUES(21,71,'21:71','ونجيناه ولوطا الى الارض التي باركنا فيها للعالمين');
INSERT INTO "imlaei_simple" VALUES(21,72,'21:72','ووهبنا له اسحاق ويعقوب نافلة وكلا جعلنا صالحين');
INSERT INTO "imlaei_simple" VALUES(21,73,'21:73','وجعلناهم ائمة يهدون بامرنا واوحينا اليهم فعل الخيرات واقام الصلاة وايتاء الزكاة وكانوا لنا عابدين');
INSERT INTO "imlaei_simple" VALUES(21,74,'21:74','ولوطا اتيناه حكما وعلما ونجيناه من القرية التي كانت تعمل الخبائث انهم كانوا قوم سوء فاسقين');
INSERT INTO "imlaei_simple" VALUES(21,75,'21:75','وادخلناه في رحمتنا انه من الصالحين');
INSERT INTO "imlaei_simple" VALUES(21,76,'21:76','ونوحا اذ نادى من قبل فاستجبنا له فنجيناه واهله من الكرب العظيم');
INSERT INTO "imlaei_simple" VALUES(21,77,'21:77','ونصرناه من القوم الذين كذبوا باياتنا انهم كانوا قوم سوء فاغرقناهم اجمعين');
INSERT INTO "imlaei_simple" VALUES(21,78,'21:78','وداوود وسليمان اذ يحكمان في الحرث اذ نفشت فيه غنم القوم وكنا لحكمهم شاهدين');
INSERT INTO "imlaei_simple" VALUES(21,79,'21:79','ففهمناها سليمان وكلا اتينا حكما وعلما وسخرنا مع داوود الجبال يسبحن والطير وكنا فاعلين');
INSERT INTO "imlaei_simple" VALUES(21,80,'21:80','وعلمناه صنعة لبوس لكم لتحصنكم من باسكم فهل انتم شاكرون');
INSERT INTO "imlaei_simple" VALUES(21,81,'21:81','ولسليمان الريح عاصفة تجري بامره الى الارض التي باركنا فيها وكنا بكل شيء عالمين');
INSERT INTO "imlaei_simple" VALUES(21,82,'21:82','ومن الشياطين من يغوصون له ويعملون عملا دون ذلك وكنا لهم حافظين');
INSERT INTO "imlaei_simple" VALUES(21,83,'21:83','وايوب اذ نادى ربه اني مسني الضر وانت ارحم الراحمين');
INSERT INTO "imlaei_simple" VALUES(21,84,'21:84','فاستجبنا له فكشفنا ما به من ضر واتيناه اهله ومثلهم معهم رحمة من عندنا وذكرى للعابدين');
INSERT INTO "imlaei_simple" VALUES(21,85,'21:85','واسماعيل وادريس وذا الكفل كل من الصابرين');
INSERT INTO "imlaei_simple" VALUES(21,86,'21:86','وادخلناهم في رحمتنا انهم من الصالحين');
INSERT INTO "imlaei_simple" VALUES(21,87,'21:87','وذا النون اذ ذهب مغاضبا فظن ان لن نقدر عليه فنادى في الظلمات ان لا اله الا انت سبحانك اني كنت من الظالمين');
INSERT INTO "imlaei_simple" VALUES(21,88,'21:88','فاستجبنا له ونجيناه من الغم وكذلك ننجي المؤمنين');
INSERT INTO "imlaei_simple" VALUES(21,89,'21:89','وزكريا اذ نادى ربه رب لا تذرني فردا وانت خير الوارثين');
INSERT INTO "imlaei_simple" VALUES(21,90,'21:90','فاستجبنا له ووهبنا له يحيى واصلحنا له زوجه انهم كانوا يسارعون في الخيرات ويدعوننا رغبا ورهبا وكانوا لنا خاشعين');
INSERT INTO "imlaei_simple" VALUES(21,91,'21:91','والتي احصنت فرجها فنفخنا فيها من روحنا وجعلناها وابنها اية للعالمين');
INSERT INTO "imlaei_simple" VALUES(21,92,'21:92','ان هذه امتكم امة واحدة وانا ربكم فاعبدون');
INSERT INTO "imlaei_simple" VALUES(21,93,'21:93','وتقطعوا امرهم بينهم كل الينا راجعون');
INSERT INTO "imlaei_simple" VALUES(21,94,'21:94','فمن يعمل من الصالحات وهو مؤمن فلا كفران لسعيه وانا له كاتبون');
INSERT INTO "imlaei_simple" VALUES(21,95,'21:95','وحرام على قرية اهلكناها انهم لا يرجعون');
INSERT INTO "imlaei_simple" VALUES(21,96,'21:96','حتى اذا فتحت ياجوج وماجوج وهم من كل حدب ينسلون');
INSERT INTO "imlaei_simple" VALUES(21,97,'21:97','واقترب الوعد الحق فاذا هي شاخصة ابصار الذين كفروا يا ويلنا قد كنا في غفلة من هذا بل كنا ظالمين');
INSERT INTO "imlaei_simple" VALUES(21,98,'21:98','انكم وما تعبدون من دون الله حصب جهنم انتم لها واردون');
INSERT INTO "imlaei_simple" VALUES(21,99,'21:99','لو كان هؤلاء الهة ما وردوها وكل فيها خالدون');
INSERT INTO "imlaei_simple" VALUES(21,100,'21:100','لهم فيها زفير وهم فيها لا يسمعون');
INSERT INTO "imlaei_simple" VALUES(21,101,'21:101','ان الذين سبقت لهم منا الحسنى اولئك عنها مبعدون');
INSERT INTO "imlaei_simple" VALUES(21,102,'21:102','لا يسمعون حسيسها وهم في ما اشتهت انفسهم خالدون');
INSERT INTO "imlaei_simple" VALUES(21,103,'21:103','لا يحزنهم الفزع الاكبر وتتلقاهم الملائكة هذا يومكم الذي كنتم توعدون');
INSERT INTO "imlaei_simple" VALUES(21,104,'21:104','يوم نطوي السماء كطي السجل للكتب كما بدانا اول خلق نعيده وعدا علينا انا كنا فاعلين');
INSERT INTO "imlaei_simple" VALUES(21,105,'21:105','ولقد كتبنا في الزبور من بعد الذكر ان الارض يرثها عبادي الصالحون');
INSERT INTO "imlaei_simple" VALUES(21,106,'21:106','ان في هذا لبلاغا لقوم عابدين');
INSERT INTO "imlaei_simple" VALUES(21,107,'21:107','وما ارسلناك الا رحمة للعالمين');
INSERT INTO "imlaei_simple" VALUES(21,108,'21:108','قل انما يوحى الي انما الهكم اله واحد فهل انتم مسلمون');
INSERT INTO "imlaei_simple" VALUES(21,109,'21:109','فان تولوا فقل اذنتكم على سواء وان ادري اقريب ام بعيد ما توعدون');
INSERT INTO "imlaei_simple" VALUES(21,110,'21:110','انه يعلم الجهر من القول ويعلم ما تكتمون');
INSERT INTO "imlaei_simple" VALUES(21,111,'21:111','وان ادري لعله فتنة لكم ومتاع الى حين');
INSERT INTO "imlaei_simple" VALUES(21,112,'21:112','قال رب احكم بالحق وربنا الرحمن المستعان على ما تصفون');
INSERT INTO "imlaei_simple" VALUES(22,1,'22:1','يا ايها الناس اتقوا ربكم ان زلزلة الساعة شيء عظيم');
INSERT INTO "imlaei_simple" VALUES(22,2,'22:2','يوم ترونها تذهل كل مرضعة عما ارضعت وتضع كل ذات حمل حملها وترى الناس سكارى وما هم بسكارى ولكن عذاب الله شديد');
INSERT INTO "imlaei_simple" VALUES(22,3,'22:3','ومن الناس من يجادل في الله بغير علم ويتبع كل شيطان مريد');
INSERT INTO "imlaei_simple" VALUES(22,4,'22:4','كتب عليه انه من تولاه فانه يضله ويهديه الى عذاب السعير');
INSERT INTO "imlaei_simple" VALUES(22,5,'22:5','يا ايها الناس ان كنتم في ريب من البعث فانا خلقناكم من تراب ثم من نطفة ثم من علقة ثم من مضغة مخلقة وغير مخلقة لنبين لكم ونقر في الارحام ما نشاء الى اجل مسمى ثم نخرجكم طفلا ثم لتبلغوا اشدكم ومنكم من يتوفى ومنكم من يرد الى ارذل العمر لكيلا يعلم من بعد علم شيئا وترى الارض هامدة فاذا انزلنا عليها الماء اهتزت وربت وانبتت من كل زوج بهيج');
INSERT INTO "imlaei_simple" VALUES(22,6,'22:6','ذلك بان الله هو الحق وانه يحيي الموتى وانه على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(22,7,'22:7','وان الساعة اتية لا ريب فيها وان الله يبعث من في القبور');
INSERT INTO "imlaei_simple" VALUES(22,8,'22:8','ومن الناس من يجادل في الله بغير علم ولا هدى ولا كتاب منير');
INSERT INTO "imlaei_simple" VALUES(22,9,'22:9','ثاني عطفه ليضل عن سبيل الله له في الدنيا خزي ونذيقه يوم القيامة عذاب الحريق');
INSERT INTO "imlaei_simple" VALUES(22,10,'22:10','ذلك بما قدمت يداك وان الله ليس بظلام للعبيد');
INSERT INTO "imlaei_simple" VALUES(22,11,'22:11','ومن الناس من يعبد الله على حرف فان اصابه خير اطمان به وان اصابته فتنة انقلب على وجهه خسر الدنيا والاخرة ذلك هو الخسران المبين');
INSERT INTO "imlaei_simple" VALUES(22,12,'22:12','يدعو من دون الله ما لا يضره وما لا ينفعه ذلك هو الضلال البعيد');
INSERT INTO "imlaei_simple" VALUES(22,13,'22:13','يدعو لمن ضره اقرب من نفعه لبئس المولى ولبئس العشير');
INSERT INTO "imlaei_simple" VALUES(22,14,'22:14','ان الله يدخل الذين امنوا وعملوا الصالحات جنات تجري من تحتها الانهار ان الله يفعل ما يريد');
INSERT INTO "imlaei_simple" VALUES(22,15,'22:15','من كان يظن ان لن ينصره الله في الدنيا والاخرة فليمدد بسبب الى السماء ثم ليقطع فلينظر هل يذهبن كيده ما يغيظ');
INSERT INTO "imlaei_simple" VALUES(22,16,'22:16','وكذلك انزلناه ايات بينات وان الله يهدي من يريد');
INSERT INTO "imlaei_simple" VALUES(22,17,'22:17','ان الذين امنوا والذين هادوا والصابئين والنصارى والمجوس والذين اشركوا ان الله يفصل بينهم يوم القيامة ان الله على كل شيء شهيد');
INSERT INTO "imlaei_simple" VALUES(22,18,'22:18','الم تر ان الله يسجد له من في السماوات ومن في الارض والشمس والقمر والنجوم والجبال والشجر والدواب وكثير من الناس وكثير حق عليه العذاب ومن يهن الله فما له من مكرم ان الله يفعل ما يشاء');
INSERT INTO "imlaei_simple" VALUES(22,19,'22:19','هذان خصمان اختصموا في ربهم فالذين كفروا قطعت لهم ثياب من نار يصب من فوق رءوسهم الحميم');
INSERT INTO "imlaei_simple" VALUES(22,20,'22:20','يصهر به ما في بطونهم والجلود');
INSERT INTO "imlaei_simple" VALUES(22,21,'22:21','ولهم مقامع من حديد');
INSERT INTO "imlaei_simple" VALUES(22,22,'22:22','كلما ارادوا ان يخرجوا منها من غم اعيدوا فيها وذوقوا عذاب الحريق');
INSERT INTO "imlaei_simple" VALUES(22,23,'22:23','ان الله يدخل الذين امنوا وعملوا الصالحات جنات تجري من تحتها الانهار يحلون فيها من اساور من ذهب ولؤلؤا ولباسهم فيها حرير');
INSERT INTO "imlaei_simple" VALUES(22,24,'22:24','وهدوا الى الطيب من القول وهدوا الى صراط الحميد');
INSERT INTO "imlaei_simple" VALUES(22,25,'22:25','ان الذين كفروا ويصدون عن سبيل الله والمسجد الحرام الذي جعلناه للناس سواء العاكف فيه والباد ومن يرد فيه بالحاد بظلم نذقه من عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(22,26,'22:26','واذ بوانا لابراهيم مكان البيت ان لا تشرك بي شيئا وطهر بيتي للطائفين والقائمين والركع السجود');
INSERT INTO "imlaei_simple" VALUES(22,27,'22:27','واذن في الناس بالحج ياتوك رجالا وعلى كل ضامر ياتين من كل فج عميق');
INSERT INTO "imlaei_simple" VALUES(22,28,'22:28','ليشهدوا منافع لهم ويذكروا اسم الله في ايام معلومات على ما رزقهم من بهيمة الانعام فكلوا منها واطعموا البائس الفقير');
INSERT INTO "imlaei_simple" VALUES(22,29,'22:29','ثم ليقضوا تفثهم وليوفوا نذورهم وليطوفوا بالبيت العتيق');
INSERT INTO "imlaei_simple" VALUES(22,30,'22:30','ذلك ومن يعظم حرمات الله فهو خير له عند ربه واحلت لكم الانعام الا ما يتلى عليكم فاجتنبوا الرجس من الاوثان واجتنبوا قول الزور');
INSERT INTO "imlaei_simple" VALUES(22,31,'22:31','حنفاء لله غير مشركين به ومن يشرك بالله فكانما خر من السماء فتخطفه الطير او تهوي به الريح في مكان سحيق');
INSERT INTO "imlaei_simple" VALUES(22,32,'22:32','ذلك ومن يعظم شعائر الله فانها من تقوى القلوب');
INSERT INTO "imlaei_simple" VALUES(22,33,'22:33','لكم فيها منافع الى اجل مسمى ثم محلها الى البيت العتيق');
INSERT INTO "imlaei_simple" VALUES(22,34,'22:34','ولكل امة جعلنا منسكا ليذكروا اسم الله على ما رزقهم من بهيمة الانعام فالهكم اله واحد فله اسلموا وبشر المخبتين');
INSERT INTO "imlaei_simple" VALUES(22,35,'22:35','الذين اذا ذكر الله وجلت قلوبهم والصابرين على ما اصابهم والمقيمي الصلاة ومما رزقناهم ينفقون');
INSERT INTO "imlaei_simple" VALUES(22,36,'22:36','والبدن جعلناها لكم من شعائر الله لكم فيها خير فاذكروا اسم الله عليها صواف فاذا وجبت جنوبها فكلوا منها واطعموا القانع والمعتر كذلك سخرناها لكم لعلكم تشكرون');
INSERT INTO "imlaei_simple" VALUES(22,37,'22:37','لن ينال الله لحومها ولا دماؤها ولكن يناله التقوى منكم كذلك سخرها لكم لتكبروا الله على ما هداكم وبشر المحسنين');
INSERT INTO "imlaei_simple" VALUES(22,38,'22:38','ان الله يدافع عن الذين امنوا ان الله لا يحب كل خوان كفور');
INSERT INTO "imlaei_simple" VALUES(22,39,'22:39','اذن للذين يقاتلون بانهم ظلموا وان الله على نصرهم لقدير');
INSERT INTO "imlaei_simple" VALUES(22,40,'22:40','الذين اخرجوا من ديارهم بغير حق الا ان يقولوا ربنا الله ولولا دفع الله الناس بعضهم ببعض لهدمت صوامع وبيع وصلوات ومساجد يذكر فيها اسم الله كثيرا ولينصرن الله من ينصره ان الله لقوي عزيز');
INSERT INTO "imlaei_simple" VALUES(22,41,'22:41','الذين ان مكناهم في الارض اقاموا الصلاة واتوا الزكاة وامروا بالمعروف ونهوا عن المنكر ولله عاقبة الامور');
INSERT INTO "imlaei_simple" VALUES(22,42,'22:42','وان يكذبوك فقد كذبت قبلهم قوم نوح وعاد وثمود');
INSERT INTO "imlaei_simple" VALUES(22,43,'22:43','وقوم ابراهيم وقوم لوط');
INSERT INTO "imlaei_simple" VALUES(22,44,'22:44','واصحاب مدين وكذب موسى فامليت للكافرين ثم اخذتهم فكيف كان نكير');
INSERT INTO "imlaei_simple" VALUES(22,45,'22:45','فكاين من قرية اهلكناها وهي ظالمة فهي خاوية على عروشها وبئر معطلة وقصر مشيد');
INSERT INTO "imlaei_simple" VALUES(22,46,'22:46','افلم يسيروا في الارض فتكون لهم قلوب يعقلون بها او اذان يسمعون بها فانها لا تعمى الابصار ولكن تعمى القلوب التي في الصدور');
INSERT INTO "imlaei_simple" VALUES(22,47,'22:47','ويستعجلونك بالعذاب ولن يخلف الله وعده وان يوما عند ربك كالف سنة مما تعدون');
INSERT INTO "imlaei_simple" VALUES(22,48,'22:48','وكاين من قرية امليت لها وهي ظالمة ثم اخذتها والي المصير');
INSERT INTO "imlaei_simple" VALUES(22,49,'22:49','قل يا ايها الناس انما انا لكم نذير مبين');
INSERT INTO "imlaei_simple" VALUES(22,50,'22:50','فالذين امنوا وعملوا الصالحات لهم مغفرة ورزق كريم');
INSERT INTO "imlaei_simple" VALUES(22,51,'22:51','والذين سعوا في اياتنا معاجزين اولئك اصحاب الجحيم');
INSERT INTO "imlaei_simple" VALUES(22,52,'22:52','وما ارسلنا من قبلك من رسول ولا نبي الا اذا تمنى القى الشيطان في امنيته فينسخ الله ما يلقي الشيطان ثم يحكم الله اياته والله عليم حكيم');
INSERT INTO "imlaei_simple" VALUES(22,53,'22:53','ليجعل ما يلقي الشيطان فتنة للذين في قلوبهم مرض والقاسية قلوبهم وان الظالمين لفي شقاق بعيد');
INSERT INTO "imlaei_simple" VALUES(22,54,'22:54','وليعلم الذين اوتوا العلم انه الحق من ربك فيؤمنوا به فتخبت له قلوبهم وان الله لهاد الذين امنوا الى صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(22,55,'22:55','ولا يزال الذين كفروا في مرية منه حتى تاتيهم الساعة بغتة او ياتيهم عذاب يوم عقيم');
INSERT INTO "imlaei_simple" VALUES(22,56,'22:56','الملك يومئذ لله يحكم بينهم فالذين امنوا وعملوا الصالحات في جنات النعيم');
INSERT INTO "imlaei_simple" VALUES(22,57,'22:57','والذين كفروا وكذبوا باياتنا فاولئك لهم عذاب مهين');
INSERT INTO "imlaei_simple" VALUES(22,58,'22:58','والذين هاجروا في سبيل الله ثم قتلوا او ماتوا ليرزقنهم الله رزقا حسنا وان الله لهو خير الرازقين');
INSERT INTO "imlaei_simple" VALUES(22,59,'22:59','ليدخلنهم مدخلا يرضونه وان الله لعليم حليم');
INSERT INTO "imlaei_simple" VALUES(22,60,'22:60','ذلك ومن عاقب بمثل ما عوقب به ثم بغي عليه لينصرنه الله ان الله لعفو غفور');
INSERT INTO "imlaei_simple" VALUES(22,61,'22:61','ذلك بان الله يولج الليل في النهار ويولج النهار في الليل وان الله سميع بصير');
INSERT INTO "imlaei_simple" VALUES(22,62,'22:62','ذلك بان الله هو الحق وان ما يدعون من دونه هو الباطل وان الله هو العلي الكبير');
INSERT INTO "imlaei_simple" VALUES(22,63,'22:63','الم تر ان الله انزل من السماء ماء فتصبح الارض مخضرة ان الله لطيف خبير');
INSERT INTO "imlaei_simple" VALUES(22,64,'22:64','له ما في السماوات وما في الارض وان الله لهو الغني الحميد');
INSERT INTO "imlaei_simple" VALUES(22,65,'22:65','الم تر ان الله سخر لكم ما في الارض والفلك تجري في البحر بامره ويمسك السماء ان تقع على الارض الا باذنه ان الله بالناس لرءوف رحيم');
INSERT INTO "imlaei_simple" VALUES(22,66,'22:66','وهو الذي احياكم ثم يميتكم ثم يحييكم ان الانسان لكفور');
INSERT INTO "imlaei_simple" VALUES(22,67,'22:67','لكل امة جعلنا منسكا هم ناسكوه فلا ينازعنك في الامر وادع الى ربك انك لعلى هدى مستقيم');
INSERT INTO "imlaei_simple" VALUES(22,68,'22:68','وان جادلوك فقل الله اعلم بما تعملون');
INSERT INTO "imlaei_simple" VALUES(22,69,'22:69','الله يحكم بينكم يوم القيامة فيما كنتم فيه تختلفون');
INSERT INTO "imlaei_simple" VALUES(22,70,'22:70','الم تعلم ان الله يعلم ما في السماء والارض ان ذلك في كتاب ان ذلك على الله يسير');
INSERT INTO "imlaei_simple" VALUES(22,71,'22:71','ويعبدون من دون الله ما لم ينزل به سلطانا وما ليس لهم به علم وما للظالمين من نصير');
INSERT INTO "imlaei_simple" VALUES(22,72,'22:72','واذا تتلى عليهم اياتنا بينات تعرف في وجوه الذين كفروا المنكر يكادون يسطون بالذين يتلون عليهم اياتنا قل افانبئكم بشر من ذلكم النار وعدها الله الذين كفروا وبئس المصير');
INSERT INTO "imlaei_simple" VALUES(22,73,'22:73','يا ايها الناس ضرب مثل فاستمعوا له ان الذين تدعون من دون الله لن يخلقوا ذبابا ولو اجتمعوا له وان يسلبهم الذباب شيئا لا يستنقذوه منه ضعف الطالب والمطلوب');
INSERT INTO "imlaei_simple" VALUES(22,74,'22:74','ما قدروا الله حق قدره ان الله لقوي عزيز');
INSERT INTO "imlaei_simple" VALUES(22,75,'22:75','الله يصطفي من الملائكة رسلا ومن الناس ان الله سميع بصير');
INSERT INTO "imlaei_simple" VALUES(22,76,'22:76','يعلم ما بين ايديهم وما خلفهم والى الله ترجع الامور');
INSERT INTO "imlaei_simple" VALUES(22,77,'22:77','يا ايها الذين امنوا اركعوا واسجدوا واعبدوا ربكم وافعلوا الخير لعلكم تفلحون');
INSERT INTO "imlaei_simple" VALUES(22,78,'22:78','وجاهدوا في الله حق جهاده هو اجتباكم وما جعل عليكم في الدين من حرج ملة ابيكم ابراهيم هو سماكم المسلمين من قبل وفي هذا ليكون الرسول شهيدا عليكم وتكونوا شهداء على الناس فاقيموا الصلاة واتوا الزكاة واعتصموا بالله هو مولاكم فنعم المولى ونعم النصير');
INSERT INTO "imlaei_simple" VALUES(23,1,'23:1','قد افلح المؤمنون');
INSERT INTO "imlaei_simple" VALUES(23,2,'23:2','الذين هم في صلاتهم خاشعون');
INSERT INTO "imlaei_simple" VALUES(23,3,'23:3','والذين هم عن اللغو معرضون');
INSERT INTO "imlaei_simple" VALUES(23,4,'23:4','والذين هم للزكاة فاعلون');
INSERT INTO "imlaei_simple" VALUES(23,5,'23:5','والذين هم لفروجهم حافظون');
INSERT INTO "imlaei_simple" VALUES(23,6,'23:6','الا على ازواجهم او ما ملكت ايمانهم فانهم غير ملومين');
INSERT INTO "imlaei_simple" VALUES(23,7,'23:7','فمن ابتغى وراء ذلك فاولئك هم العادون');
INSERT INTO "imlaei_simple" VALUES(23,8,'23:8','والذين هم لاماناتهم وعهدهم راعون');
INSERT INTO "imlaei_simple" VALUES(23,9,'23:9','والذين هم على صلواتهم يحافظون');
INSERT INTO "imlaei_simple" VALUES(23,10,'23:10','اولئك هم الوارثون');
INSERT INTO "imlaei_simple" VALUES(23,11,'23:11','الذين يرثون الفردوس هم فيها خالدون');
INSERT INTO "imlaei_simple" VALUES(23,12,'23:12','ولقد خلقنا الانسان من سلالة من طين');
INSERT INTO "imlaei_simple" VALUES(23,13,'23:13','ثم جعلناه نطفة في قرار مكين');
INSERT INTO "imlaei_simple" VALUES(23,14,'23:14','ثم خلقنا النطفة علقة فخلقنا العلقة مضغة فخلقنا المضغة عظاما فكسونا العظام لحما ثم انشاناه خلقا اخر فتبارك الله احسن الخالقين');
INSERT INTO "imlaei_simple" VALUES(23,15,'23:15','ثم انكم بعد ذلك لميتون');
INSERT INTO "imlaei_simple" VALUES(23,16,'23:16','ثم انكم يوم القيامة تبعثون');
INSERT INTO "imlaei_simple" VALUES(23,17,'23:17','ولقد خلقنا فوقكم سبع طرائق وما كنا عن الخلق غافلين');
INSERT INTO "imlaei_simple" VALUES(23,18,'23:18','وانزلنا من السماء ماء بقدر فاسكناه في الارض وانا على ذهاب به لقادرون');
INSERT INTO "imlaei_simple" VALUES(23,19,'23:19','فانشانا لكم به جنات من نخيل واعناب لكم فيها فواكه كثيرة ومنها تاكلون');
INSERT INTO "imlaei_simple" VALUES(23,20,'23:20','وشجرة تخرج من طور سيناء تنبت بالدهن وصبغ للاكلين');
INSERT INTO "imlaei_simple" VALUES(23,21,'23:21','وان لكم في الانعام لعبرة نسقيكم مما في بطونها ولكم فيها منافع كثيرة ومنها تاكلون');
INSERT INTO "imlaei_simple" VALUES(23,22,'23:22','وعليها وعلى الفلك تحملون');
INSERT INTO "imlaei_simple" VALUES(23,23,'23:23','ولقد ارسلنا نوحا الى قومه فقال يا قوم اعبدوا الله ما لكم من اله غيره افلا تتقون');
INSERT INTO "imlaei_simple" VALUES(23,24,'23:24','فقال الملا الذين كفروا من قومه ما هذا الا بشر مثلكم يريد ان يتفضل عليكم ولو شاء الله لانزل ملائكة ما سمعنا بهذا في ابائنا الاولين');
INSERT INTO "imlaei_simple" VALUES(23,25,'23:25','ان هو الا رجل به جنة فتربصوا به حتى حين');
INSERT INTO "imlaei_simple" VALUES(23,26,'23:26','قال رب انصرني بما كذبون');
INSERT INTO "imlaei_simple" VALUES(23,27,'23:27','فاوحينا اليه ان اصنع الفلك باعيننا ووحينا فاذا جاء امرنا وفار التنور فاسلك فيها من كل زوجين اثنين واهلك الا من سبق عليه القول منهم ولا تخاطبني في الذين ظلموا انهم مغرقون');
INSERT INTO "imlaei_simple" VALUES(23,28,'23:28','فاذا استويت انت ومن معك على الفلك فقل الحمد لله الذي نجانا من القوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(23,29,'23:29','وقل رب انزلني منزلا مباركا وانت خير المنزلين');
INSERT INTO "imlaei_simple" VALUES(23,30,'23:30','ان في ذلك لايات وان كنا لمبتلين');
INSERT INTO "imlaei_simple" VALUES(23,31,'23:31','ثم انشانا من بعدهم قرنا اخرين');
INSERT INTO "imlaei_simple" VALUES(23,32,'23:32','فارسلنا فيهم رسولا منهم ان اعبدوا الله ما لكم من اله غيره افلا تتقون');
INSERT INTO "imlaei_simple" VALUES(23,33,'23:33','وقال الملا من قومه الذين كفروا وكذبوا بلقاء الاخرة واترفناهم في الحياة الدنيا ما هذا الا بشر مثلكم ياكل مما تاكلون منه ويشرب مما تشربون');
INSERT INTO "imlaei_simple" VALUES(23,34,'23:34','ولئن اطعتم بشرا مثلكم انكم اذا لخاسرون');
INSERT INTO "imlaei_simple" VALUES(23,35,'23:35','ايعدكم انكم اذا متم وكنتم ترابا وعظاما انكم مخرجون');
INSERT INTO "imlaei_simple" VALUES(23,36,'23:36','هيهات هيهات لما توعدون');
INSERT INTO "imlaei_simple" VALUES(23,37,'23:37','ان هي الا حياتنا الدنيا نموت ونحيا وما نحن بمبعوثين');
INSERT INTO "imlaei_simple" VALUES(23,38,'23:38','ان هو الا رجل افترى على الله كذبا وما نحن له بمؤمنين');
INSERT INTO "imlaei_simple" VALUES(23,39,'23:39','قال رب انصرني بما كذبون');
INSERT INTO "imlaei_simple" VALUES(23,40,'23:40','قال عما قليل ليصبحن نادمين');
INSERT INTO "imlaei_simple" VALUES(23,41,'23:41','فاخذتهم الصيحة بالحق فجعلناهم غثاء فبعدا للقوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(23,42,'23:42','ثم انشانا من بعدهم قرونا اخرين');
INSERT INTO "imlaei_simple" VALUES(23,43,'23:43','ما تسبق من امة اجلها وما يستاخرون');
INSERT INTO "imlaei_simple" VALUES(23,44,'23:44','ثم ارسلنا رسلنا تترى كل ما جاء امة رسولها كذبوه فاتبعنا بعضهم بعضا وجعلناهم احاديث فبعدا لقوم لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(23,45,'23:45','ثم ارسلنا موسى واخاه هارون باياتنا وسلطان مبين');
INSERT INTO "imlaei_simple" VALUES(23,46,'23:46','الى فرعون وملئه فاستكبروا وكانوا قوما عالين');
INSERT INTO "imlaei_simple" VALUES(23,47,'23:47','فقالوا انؤمن لبشرين مثلنا وقومهما لنا عابدون');
INSERT INTO "imlaei_simple" VALUES(23,48,'23:48','فكذبوهما فكانوا من المهلكين');
INSERT INTO "imlaei_simple" VALUES(23,49,'23:49','ولقد اتينا موسى الكتاب لعلهم يهتدون');
INSERT INTO "imlaei_simple" VALUES(23,50,'23:50','وجعلنا ابن مريم وامه اية واويناهما الى ربوة ذات قرار ومعين');
INSERT INTO "imlaei_simple" VALUES(23,51,'23:51','يا ايها الرسل كلوا من الطيبات واعملوا صالحا اني بما تعملون عليم');
INSERT INTO "imlaei_simple" VALUES(23,52,'23:52','وان هذه امتكم امة واحدة وانا ربكم فاتقون');
INSERT INTO "imlaei_simple" VALUES(23,53,'23:53','فتقطعوا امرهم بينهم زبرا كل حزب بما لديهم فرحون');
INSERT INTO "imlaei_simple" VALUES(23,54,'23:54','فذرهم في غمرتهم حتى حين');
INSERT INTO "imlaei_simple" VALUES(23,55,'23:55','ايحسبون انما نمدهم به من مال وبنين');
INSERT INTO "imlaei_simple" VALUES(23,56,'23:56','نسارع لهم في الخيرات بل لا يشعرون');
INSERT INTO "imlaei_simple" VALUES(23,57,'23:57','ان الذين هم من خشية ربهم مشفقون');
INSERT INTO "imlaei_simple" VALUES(23,58,'23:58','والذين هم بايات ربهم يؤمنون');
INSERT INTO "imlaei_simple" VALUES(23,59,'23:59','والذين هم بربهم لا يشركون');
INSERT INTO "imlaei_simple" VALUES(23,60,'23:60','والذين يؤتون ما اتوا وقلوبهم وجلة انهم الى ربهم راجعون');
INSERT INTO "imlaei_simple" VALUES(23,61,'23:61','اولئك يسارعون في الخيرات وهم لها سابقون');
INSERT INTO "imlaei_simple" VALUES(23,62,'23:62','ولا نكلف نفسا الا وسعها ولدينا كتاب ينطق بالحق وهم لا يظلمون');
INSERT INTO "imlaei_simple" VALUES(23,63,'23:63','بل قلوبهم في غمرة من هذا ولهم اعمال من دون ذلك هم لها عاملون');
INSERT INTO "imlaei_simple" VALUES(23,64,'23:64','حتى اذا اخذنا مترفيهم بالعذاب اذا هم يجارون');
INSERT INTO "imlaei_simple" VALUES(23,65,'23:65','لا تجاروا اليوم انكم منا لا تنصرون');
INSERT INTO "imlaei_simple" VALUES(23,66,'23:66','قد كانت اياتي تتلى عليكم فكنتم على اعقابكم تنكصون');
INSERT INTO "imlaei_simple" VALUES(23,67,'23:67','مستكبرين به سامرا تهجرون');
INSERT INTO "imlaei_simple" VALUES(23,68,'23:68','افلم يدبروا القول ام جاءهم ما لم يات اباءهم الاولين');
INSERT INTO "imlaei_simple" VALUES(23,69,'23:69','ام لم يعرفوا رسولهم فهم له منكرون');
INSERT INTO "imlaei_simple" VALUES(23,70,'23:70','ام يقولون به جنة بل جاءهم بالحق واكثرهم للحق كارهون');
INSERT INTO "imlaei_simple" VALUES(23,71,'23:71','ولو اتبع الحق اهواءهم لفسدت السماوات والارض ومن فيهن بل اتيناهم بذكرهم فهم عن ذكرهم معرضون');
INSERT INTO "imlaei_simple" VALUES(23,72,'23:72','ام تسالهم خرجا فخراج ربك خير وهو خير الرازقين');
INSERT INTO "imlaei_simple" VALUES(23,73,'23:73','وانك لتدعوهم الى صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(23,74,'23:74','وان الذين لا يؤمنون بالاخرة عن الصراط لناكبون');
INSERT INTO "imlaei_simple" VALUES(23,75,'23:75','ولو رحمناهم وكشفنا ما بهم من ضر للجوا في طغيانهم يعمهون');
INSERT INTO "imlaei_simple" VALUES(23,76,'23:76','ولقد اخذناهم بالعذاب فما استكانوا لربهم وما يتضرعون');
INSERT INTO "imlaei_simple" VALUES(23,77,'23:77','حتى اذا فتحنا عليهم بابا ذا عذاب شديد اذا هم فيه مبلسون');
INSERT INTO "imlaei_simple" VALUES(23,78,'23:78','وهو الذي انشا لكم السمع والابصار والافئدة قليلا ما تشكرون');
INSERT INTO "imlaei_simple" VALUES(23,79,'23:79','وهو الذي ذراكم في الارض واليه تحشرون');
INSERT INTO "imlaei_simple" VALUES(23,80,'23:80','وهو الذي يحيي ويميت وله اختلاف الليل والنهار افلا تعقلون');
INSERT INTO "imlaei_simple" VALUES(23,81,'23:81','بل قالوا مثل ما قال الاولون');
INSERT INTO "imlaei_simple" VALUES(23,82,'23:82','قالوا ااذا متنا وكنا ترابا وعظاما اانا لمبعوثون');
INSERT INTO "imlaei_simple" VALUES(23,83,'23:83','لقد وعدنا نحن واباؤنا هذا من قبل ان هذا الا اساطير الاولين');
INSERT INTO "imlaei_simple" VALUES(23,84,'23:84','قل لمن الارض ومن فيها ان كنتم تعلمون');
INSERT INTO "imlaei_simple" VALUES(23,85,'23:85','سيقولون لله قل افلا تذكرون');
INSERT INTO "imlaei_simple" VALUES(23,86,'23:86','قل من رب السماوات السبع ورب العرش العظيم');
INSERT INTO "imlaei_simple" VALUES(23,87,'23:87','سيقولون لله قل افلا تتقون');
INSERT INTO "imlaei_simple" VALUES(23,88,'23:88','قل من بيده ملكوت كل شيء وهو يجير ولا يجار عليه ان كنتم تعلمون');
INSERT INTO "imlaei_simple" VALUES(23,89,'23:89','سيقولون لله قل فانى تسحرون');
INSERT INTO "imlaei_simple" VALUES(23,90,'23:90','بل اتيناهم بالحق وانهم لكاذبون');
INSERT INTO "imlaei_simple" VALUES(23,91,'23:91','ما اتخذ الله من ولد وما كان معه من اله اذا لذهب كل اله بما خلق ولعلا بعضهم على بعض سبحان الله عما يصفون');
INSERT INTO "imlaei_simple" VALUES(23,92,'23:92','عالم الغيب والشهادة فتعالى عما يشركون');
INSERT INTO "imlaei_simple" VALUES(23,93,'23:93','قل رب اما تريني ما يوعدون');
INSERT INTO "imlaei_simple" VALUES(23,94,'23:94','رب فلا تجعلني في القوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(23,95,'23:95','وانا على ان نريك ما نعدهم لقادرون');
INSERT INTO "imlaei_simple" VALUES(23,96,'23:96','ادفع بالتي هي احسن السيئة نحن اعلم بما يصفون');
INSERT INTO "imlaei_simple" VALUES(23,97,'23:97','وقل رب اعوذ بك من همزات الشياطين');
INSERT INTO "imlaei_simple" VALUES(23,98,'23:98','واعوذ بك رب ان يحضرون');
INSERT INTO "imlaei_simple" VALUES(23,99,'23:99','حتى اذا جاء احدهم الموت قال رب ارجعون');
INSERT INTO "imlaei_simple" VALUES(23,100,'23:100','لعلي اعمل صالحا فيما تركت كلا انها كلمة هو قائلها ومن ورائهم برزخ الى يوم يبعثون');
INSERT INTO "imlaei_simple" VALUES(23,101,'23:101','فاذا نفخ في الصور فلا انساب بينهم يومئذ ولا يتساءلون');
INSERT INTO "imlaei_simple" VALUES(23,102,'23:102','فمن ثقلت موازينه فاولئك هم المفلحون');
INSERT INTO "imlaei_simple" VALUES(23,103,'23:103','ومن خفت موازينه فاولئك الذين خسروا انفسهم في جهنم خالدون');
INSERT INTO "imlaei_simple" VALUES(23,104,'23:104','تلفح وجوههم النار وهم فيها كالحون');
INSERT INTO "imlaei_simple" VALUES(23,105,'23:105','الم تكن اياتي تتلى عليكم فكنتم بها تكذبون');
INSERT INTO "imlaei_simple" VALUES(23,106,'23:106','قالوا ربنا غلبت علينا شقوتنا وكنا قوما ضالين');
INSERT INTO "imlaei_simple" VALUES(23,107,'23:107','ربنا اخرجنا منها فان عدنا فانا ظالمون');
INSERT INTO "imlaei_simple" VALUES(23,108,'23:108','قال اخسئوا فيها ولا تكلمون');
INSERT INTO "imlaei_simple" VALUES(23,109,'23:109','انه كان فريق من عبادي يقولون ربنا امنا فاغفر لنا وارحمنا وانت خير الراحمين');
INSERT INTO "imlaei_simple" VALUES(23,110,'23:110','فاتخذتموهم سخريا حتى انسوكم ذكري وكنتم منهم تضحكون');
INSERT INTO "imlaei_simple" VALUES(23,111,'23:111','اني جزيتهم اليوم بما صبروا انهم هم الفائزون');
INSERT INTO "imlaei_simple" VALUES(23,112,'23:112','قال كم لبثتم في الارض عدد سنين');
INSERT INTO "imlaei_simple" VALUES(23,113,'23:113','قالوا لبثنا يوما او بعض يوم فاسال العادين');
INSERT INTO "imlaei_simple" VALUES(23,114,'23:114','قال ان لبثتم الا قليلا لو انكم كنتم تعلمون');
INSERT INTO "imlaei_simple" VALUES(23,115,'23:115','افحسبتم انما خلقناكم عبثا وانكم الينا لا ترجعون');
INSERT INTO "imlaei_simple" VALUES(23,116,'23:116','فتعالى الله الملك الحق لا اله الا هو رب العرش الكريم');
INSERT INTO "imlaei_simple" VALUES(23,117,'23:117','ومن يدع مع الله الها اخر لا برهان له به فانما حسابه عند ربه انه لا يفلح الكافرون');
INSERT INTO "imlaei_simple" VALUES(23,118,'23:118','وقل رب اغفر وارحم وانت خير الراحمين');
INSERT INTO "imlaei_simple" VALUES(24,1,'24:1','سورة انزلناها وفرضناها وانزلنا فيها ايات بينات لعلكم تذكرون');
INSERT INTO "imlaei_simple" VALUES(24,2,'24:2','الزانية والزاني فاجلدوا كل واحد منهما مائة جلدة ولا تاخذكم بهما رافة في دين الله ان كنتم تؤمنون بالله واليوم الاخر وليشهد عذابهما طائفة من المؤمنين');
INSERT INTO "imlaei_simple" VALUES(24,3,'24:3','الزاني لا ينكح الا زانية او مشركة والزانية لا ينكحها الا زان او مشرك وحرم ذلك على المؤمنين');
INSERT INTO "imlaei_simple" VALUES(24,4,'24:4','والذين يرمون المحصنات ثم لم ياتوا باربعة شهداء فاجلدوهم ثمانين جلدة ولا تقبلوا لهم شهادة ابدا واولئك هم الفاسقون');
INSERT INTO "imlaei_simple" VALUES(24,5,'24:5','الا الذين تابوا من بعد ذلك واصلحوا فان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(24,6,'24:6','والذين يرمون ازواجهم ولم يكن لهم شهداء الا انفسهم فشهادة احدهم اربع شهادات بالله انه لمن الصادقين');
INSERT INTO "imlaei_simple" VALUES(24,7,'24:7','والخامسة ان لعنت الله عليه ان كان من الكاذبين');
INSERT INTO "imlaei_simple" VALUES(24,8,'24:8','ويدرا عنها العذاب ان تشهد اربع شهادات بالله انه لمن الكاذبين');
INSERT INTO "imlaei_simple" VALUES(24,9,'24:9','والخامسة ان غضب الله عليها ان كان من الصادقين');
INSERT INTO "imlaei_simple" VALUES(24,10,'24:10','ولولا فضل الله عليكم ورحمته وان الله تواب حكيم');
INSERT INTO "imlaei_simple" VALUES(24,11,'24:11','ان الذين جاءوا بالافك عصبة منكم لا تحسبوه شرا لكم بل هو خير لكم لكل امرئ منهم ما اكتسب من الاثم والذي تولى كبره منهم له عذاب عظيم');
INSERT INTO "imlaei_simple" VALUES(24,12,'24:12','لولا اذ سمعتموه ظن المؤمنون والمؤمنات بانفسهم خيرا وقالوا هذا افك مبين');
INSERT INTO "imlaei_simple" VALUES(24,13,'24:13','لولا جاءوا عليه باربعة شهداء فاذ لم ياتوا بالشهداء فاولئك عند الله هم الكاذبون');
INSERT INTO "imlaei_simple" VALUES(24,14,'24:14','ولولا فضل الله عليكم ورحمته في الدنيا والاخرة لمسكم في ما افضتم فيه عذاب عظيم');
INSERT INTO "imlaei_simple" VALUES(24,15,'24:15','اذ تلقونه بالسنتكم وتقولون بافواهكم ما ليس لكم به علم وتحسبونه هينا وهو عند الله عظيم');
INSERT INTO "imlaei_simple" VALUES(24,16,'24:16','ولولا اذ سمعتموه قلتم ما يكون لنا ان نتكلم بهذا سبحانك هذا بهتان عظيم');
INSERT INTO "imlaei_simple" VALUES(24,17,'24:17','يعظكم الله ان تعودوا لمثله ابدا ان كنتم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(24,18,'24:18','ويبين الله لكم الايات والله عليم حكيم');
INSERT INTO "imlaei_simple" VALUES(24,19,'24:19','ان الذين يحبون ان تشيع الفاحشة في الذين امنوا لهم عذاب اليم في الدنيا والاخرة والله يعلم وانتم لا تعلمون');
INSERT INTO "imlaei_simple" VALUES(24,20,'24:20','ولولا فضل الله عليكم ورحمته وان الله رءوف رحيم');
INSERT INTO "imlaei_simple" VALUES(24,21,'24:21','يا ايها الذين امنوا لا تتبعوا خطوات الشيطان ومن يتبع خطوات الشيطان فانه يامر بالفحشاء والمنكر ولولا فضل الله عليكم ورحمته ما زكى منكم من احد ابدا ولكن الله يزكي من يشاء والله سميع عليم');
INSERT INTO "imlaei_simple" VALUES(24,22,'24:22','ولا ياتل اولو الفضل منكم والسعة ان يؤتوا اولي القربى والمساكين والمهاجرين في سبيل الله وليعفوا وليصفحوا الا تحبون ان يغفر الله لكم والله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(24,23,'24:23','ان الذين يرمون المحصنات الغافلات المؤمنات لعنوا في الدنيا والاخرة ولهم عذاب عظيم');
INSERT INTO "imlaei_simple" VALUES(24,24,'24:24','يوم تشهد عليهم السنتهم وايديهم وارجلهم بما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(24,25,'24:25','يومئذ يوفيهم الله دينهم الحق ويعلمون ان الله هو الحق المبين');
INSERT INTO "imlaei_simple" VALUES(24,26,'24:26','الخبيثات للخبيثين والخبيثون للخبيثات والطيبات للطيبين والطيبون للطيبات اولئك مبرءون مما يقولون لهم مغفرة ورزق كريم');
INSERT INTO "imlaei_simple" VALUES(24,27,'24:27','يا ايها الذين امنوا لا تدخلوا بيوتا غير بيوتكم حتى تستانسوا وتسلموا على اهلها ذلكم خير لكم لعلكم تذكرون');
INSERT INTO "imlaei_simple" VALUES(24,28,'24:28','فان لم تجدوا فيها احدا فلا تدخلوها حتى يؤذن لكم وان قيل لكم ارجعوا فارجعوا هو ازكى لكم والله بما تعملون عليم');
INSERT INTO "imlaei_simple" VALUES(24,29,'24:29','ليس عليكم جناح ان تدخلوا بيوتا غير مسكونة فيها متاع لكم والله يعلم ما تبدون وما تكتمون');
INSERT INTO "imlaei_simple" VALUES(24,30,'24:30','قل للمؤمنين يغضوا من ابصارهم ويحفظوا فروجهم ذلك ازكى لهم ان الله خبير بما يصنعون');
INSERT INTO "imlaei_simple" VALUES(24,31,'24:31','وقل للمؤمنات يغضضن من ابصارهن ويحفظن فروجهن ولا يبدين زينتهن الا ما ظهر منها وليضربن بخمرهن على جيوبهن ولا يبدين زينتهن الا لبعولتهن او ابائهن او اباء بعولتهن او ابنائهن او ابناء بعولتهن او اخوانهن او بني اخوانهن او بني اخواتهن او نسائهن او ما ملكت ايمانهن او التابعين غير اولي الاربة من الرجال او الطفل الذين لم يظهروا على عورات النساء ولا يضربن بارجلهن ليعلم ما يخفين من زينتهن وتوبوا الى الله جميعا ايه المؤمنون لعلكم تفلحون');
INSERT INTO "imlaei_simple" VALUES(24,32,'24:32','وانكحوا الايامى منكم والصالحين من عبادكم وامائكم ان يكونوا فقراء يغنهم الله من فضله والله واسع عليم');
INSERT INTO "imlaei_simple" VALUES(24,33,'24:33','وليستعفف الذين لا يجدون نكاحا حتى يغنيهم الله من فضله والذين يبتغون الكتاب مما ملكت ايمانكم فكاتبوهم ان علمتم فيهم خيرا واتوهم من مال الله الذي اتاكم ولا تكرهوا فتياتكم على البغاء ان اردن تحصنا لتبتغوا عرض الحياة الدنيا ومن يكرههن فان الله من بعد اكراههن غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(24,34,'24:34','ولقد انزلنا اليكم ايات مبينات ومثلا من الذين خلوا من قبلكم وموعظة للمتقين');
INSERT INTO "imlaei_simple" VALUES(24,35,'24:35','الله نور السماوات والارض مثل نوره كمشكاة فيها مصباح المصباح في زجاجة الزجاجة كانها كوكب دري يوقد من شجرة مباركة زيتونة لا شرقية ولا غربية يكاد زيتها يضيء ولو لم تمسسه نار نور على نور يهدي الله لنوره من يشاء ويضرب الله الامثال للناس والله بكل شيء عليم');
INSERT INTO "imlaei_simple" VALUES(24,36,'24:36','في بيوت اذن الله ان ترفع ويذكر فيها اسمه يسبح له فيها بالغدو والاصال');
INSERT INTO "imlaei_simple" VALUES(24,37,'24:37','رجال لا تلهيهم تجارة ولا بيع عن ذكر الله واقام الصلاة وايتاء الزكاة يخافون يوما تتقلب فيه القلوب والابصار');
INSERT INTO "imlaei_simple" VALUES(24,38,'24:38','ليجزيهم الله احسن ما عملوا ويزيدهم من فضله والله يرزق من يشاء بغير حساب');
INSERT INTO "imlaei_simple" VALUES(24,39,'24:39','والذين كفروا اعمالهم كسراب بقيعة يحسبه الظمان ماء حتى اذا جاءه لم يجده شيئا ووجد الله عنده فوفاه حسابه والله سريع الحساب');
INSERT INTO "imlaei_simple" VALUES(24,40,'24:40','او كظلمات في بحر لجي يغشاه موج من فوقه موج من فوقه سحاب ظلمات بعضها فوق بعض اذا اخرج يده لم يكد يراها ومن لم يجعل الله له نورا فما له من نور');
INSERT INTO "imlaei_simple" VALUES(24,41,'24:41','الم تر ان الله يسبح له من في السماوات والارض والطير صافات كل قد علم صلاته وتسبيحه والله عليم بما يفعلون');
INSERT INTO "imlaei_simple" VALUES(24,42,'24:42','ولله ملك السماوات والارض والى الله المصير');
INSERT INTO "imlaei_simple" VALUES(24,43,'24:43','الم تر ان الله يزجي سحابا ثم يؤلف بينه ثم يجعله ركاما فترى الودق يخرج من خلاله وينزل من السماء من جبال فيها من برد فيصيب به من يشاء ويصرفه عن من يشاء يكاد سنا برقه يذهب بالابصار');
INSERT INTO "imlaei_simple" VALUES(24,44,'24:44','يقلب الله الليل والنهار ان في ذلك لعبرة لاولي الابصار');
INSERT INTO "imlaei_simple" VALUES(24,45,'24:45','والله خلق كل دابة من ماء فمنهم من يمشي على بطنه ومنهم من يمشي على رجلين ومنهم من يمشي على اربع يخلق الله ما يشاء ان الله على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(24,46,'24:46','لقد انزلنا ايات مبينات والله يهدي من يشاء الى صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(24,47,'24:47','ويقولون امنا بالله وبالرسول واطعنا ثم يتولى فريق منهم من بعد ذلك وما اولئك بالمؤمنين');
INSERT INTO "imlaei_simple" VALUES(24,48,'24:48','واذا دعوا الى الله ورسوله ليحكم بينهم اذا فريق منهم معرضون');
INSERT INTO "imlaei_simple" VALUES(24,49,'24:49','وان يكن لهم الحق ياتوا اليه مذعنين');
INSERT INTO "imlaei_simple" VALUES(24,50,'24:50','افي قلوبهم مرض ام ارتابوا ام يخافون ان يحيف الله عليهم ورسوله بل اولئك هم الظالمون');
INSERT INTO "imlaei_simple" VALUES(24,51,'24:51','انما كان قول المؤمنين اذا دعوا الى الله ورسوله ليحكم بينهم ان يقولوا سمعنا واطعنا واولئك هم المفلحون');
INSERT INTO "imlaei_simple" VALUES(24,52,'24:52','ومن يطع الله ورسوله ويخش الله ويتقه فاولئك هم الفائزون');
INSERT INTO "imlaei_simple" VALUES(24,53,'24:53','واقسموا بالله جهد ايمانهم لئن امرتهم ليخرجن قل لا تقسموا طاعة معروفة ان الله خبير بما تعملون');
INSERT INTO "imlaei_simple" VALUES(24,54,'24:54','قل اطيعوا الله واطيعوا الرسول فان تولوا فانما عليه ما حمل وعليكم ما حملتم وان تطيعوه تهتدوا وما على الرسول الا البلاغ المبين');
INSERT INTO "imlaei_simple" VALUES(24,55,'24:55','وعد الله الذين امنوا منكم وعملوا الصالحات ليستخلفنهم في الارض كما استخلف الذين من قبلهم وليمكنن لهم دينهم الذي ارتضى لهم وليبدلنهم من بعد خوفهم امنا يعبدونني لا يشركون بي شيئا ومن كفر بعد ذلك فاولئك هم الفاسقون');
INSERT INTO "imlaei_simple" VALUES(24,56,'24:56','واقيموا الصلاة واتوا الزكاة واطيعوا الرسول لعلكم ترحمون');
INSERT INTO "imlaei_simple" VALUES(24,57,'24:57','لا تحسبن الذين كفروا معجزين في الارض وماواهم النار ولبئس المصير');
INSERT INTO "imlaei_simple" VALUES(24,58,'24:58','يا ايها الذين امنوا ليستاذنكم الذين ملكت ايمانكم والذين لم يبلغوا الحلم منكم ثلاث مرات من قبل صلاة الفجر وحين تضعون ثيابكم من الظهيرة ومن بعد صلاة العشاء ثلاث عورات لكم ليس عليكم ولا عليهم جناح بعدهن طوافون عليكم بعضكم على بعض كذلك يبين الله لكم الايات والله عليم حكيم');
INSERT INTO "imlaei_simple" VALUES(24,59,'24:59','واذا بلغ الاطفال منكم الحلم فليستاذنوا كما استاذن الذين من قبلهم كذلك يبين الله لكم اياته والله عليم حكيم');
INSERT INTO "imlaei_simple" VALUES(24,60,'24:60','والقواعد من النساء اللاتي لا يرجون نكاحا فليس عليهن جناح ان يضعن ثيابهن غير متبرجات بزينة وان يستعففن خير لهن والله سميع عليم');
INSERT INTO "imlaei_simple" VALUES(24,61,'24:61','ليس على الاعمى حرج ولا على الاعرج حرج ولا على المريض حرج ولا على انفسكم ان تاكلوا من بيوتكم او بيوت ابائكم او بيوت امهاتكم او بيوت اخوانكم او بيوت اخواتكم او بيوت اعمامكم او بيوت عماتكم او بيوت اخوالكم او بيوت خالاتكم او ما ملكتم مفاتحه او صديقكم ليس عليكم جناح ان تاكلوا جميعا او اشتاتا فاذا دخلتم بيوتا فسلموا على انفسكم تحية من عند الله مباركة طيبة كذلك يبين الله لكم الايات لعلكم تعقلون');
INSERT INTO "imlaei_simple" VALUES(24,62,'24:62','انما المؤمنون الذين امنوا بالله ورسوله واذا كانوا معه على امر جامع لم يذهبوا حتى يستاذنوه ان الذين يستاذنونك اولئك الذين يؤمنون بالله ورسوله فاذا استاذنوك لبعض شانهم فاذن لمن شئت منهم واستغفر لهم الله ان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(24,63,'24:63','لا تجعلوا دعاء الرسول بينكم كدعاء بعضكم بعضا قد يعلم الله الذين يتسللون منكم لواذا فليحذر الذين يخالفون عن امره ان تصيبهم فتنة او يصيبهم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(24,64,'24:64','الا ان لله ما في السماوات والارض قد يعلم ما انتم عليه ويوم يرجعون اليه فينبئهم بما عملوا والله بكل شيء عليم');
INSERT INTO "imlaei_simple" VALUES(25,1,'25:1','تبارك الذي نزل الفرقان على عبده ليكون للعالمين نذيرا');
INSERT INTO "imlaei_simple" VALUES(25,2,'25:2','الذي له ملك السماوات والارض ولم يتخذ ولدا ولم يكن له شريك في الملك وخلق كل شيء فقدره تقديرا');
INSERT INTO "imlaei_simple" VALUES(25,3,'25:3','واتخذوا من دونه الهة لا يخلقون شيئا وهم يخلقون ولا يملكون لانفسهم ضرا ولا نفعا ولا يملكون موتا ولا حياة ولا نشورا');
INSERT INTO "imlaei_simple" VALUES(25,4,'25:4','وقال الذين كفروا ان هذا الا افك افتراه واعانه عليه قوم اخرون فقد جاءوا ظلما وزورا');
INSERT INTO "imlaei_simple" VALUES(25,5,'25:5','وقالوا اساطير الاولين اكتتبها فهي تملى عليه بكرة واصيلا');
INSERT INTO "imlaei_simple" VALUES(25,6,'25:6','قل انزله الذي يعلم السر في السماوات والارض انه كان غفورا رحيما');
INSERT INTO "imlaei_simple" VALUES(25,7,'25:7','وقالوا مال هذا الرسول ياكل الطعام ويمشي في الاسواق لولا انزل اليه ملك فيكون معه نذيرا');
INSERT INTO "imlaei_simple" VALUES(25,8,'25:8','او يلقى اليه كنز او تكون له جنة ياكل منها وقال الظالمون ان تتبعون الا رجلا مسحورا');
INSERT INTO "imlaei_simple" VALUES(25,9,'25:9','انظر كيف ضربوا لك الامثال فضلوا فلا يستطيعون سبيلا');
INSERT INTO "imlaei_simple" VALUES(25,10,'25:10','تبارك الذي ان شاء جعل لك خيرا من ذلك جنات تجري من تحتها الانهار ويجعل لك قصورا');
INSERT INTO "imlaei_simple" VALUES(25,11,'25:11','بل كذبوا بالساعة واعتدنا لمن كذب بالساعة سعيرا');
INSERT INTO "imlaei_simple" VALUES(25,12,'25:12','اذا راتهم من مكان بعيد سمعوا لها تغيظا وزفيرا');
INSERT INTO "imlaei_simple" VALUES(25,13,'25:13','واذا القوا منها مكانا ضيقا مقرنين دعوا هنالك ثبورا');
INSERT INTO "imlaei_simple" VALUES(25,14,'25:14','لا تدعوا اليوم ثبورا واحدا وادعوا ثبورا كثيرا');
INSERT INTO "imlaei_simple" VALUES(25,15,'25:15','قل اذلك خير ام جنة الخلد التي وعد المتقون كانت لهم جزاء ومصيرا');
INSERT INTO "imlaei_simple" VALUES(25,16,'25:16','لهم فيها ما يشاءون خالدين كان على ربك وعدا مسئولا');
INSERT INTO "imlaei_simple" VALUES(25,17,'25:17','ويوم يحشرهم وما يعبدون من دون الله فيقول اانتم اضللتم عبادي هؤلاء ام هم ضلوا السبيل');
INSERT INTO "imlaei_simple" VALUES(25,18,'25:18','قالوا سبحانك ما كان ينبغي لنا ان نتخذ من دونك من اولياء ولكن متعتهم واباءهم حتى نسوا الذكر وكانوا قوما بورا');
INSERT INTO "imlaei_simple" VALUES(25,19,'25:19','فقد كذبوكم بما تقولون فما تستطيعون صرفا ولا نصرا ومن يظلم منكم نذقه عذابا كبيرا');
INSERT INTO "imlaei_simple" VALUES(25,20,'25:20','وما ارسلنا قبلك من المرسلين الا انهم لياكلون الطعام ويمشون في الاسواق وجعلنا بعضكم لبعض فتنة اتصبرون وكان ربك بصيرا');
INSERT INTO "imlaei_simple" VALUES(25,21,'25:21','وقال الذين لا يرجون لقاءنا لولا انزل علينا الملائكة او نرى ربنا لقد استكبروا في انفسهم وعتوا عتوا كبيرا');
INSERT INTO "imlaei_simple" VALUES(25,22,'25:22','يوم يرون الملائكة لا بشرى يومئذ للمجرمين ويقولون حجرا محجورا');
INSERT INTO "imlaei_simple" VALUES(25,23,'25:23','وقدمنا الى ما عملوا من عمل فجعلناه هباء منثورا');
INSERT INTO "imlaei_simple" VALUES(25,24,'25:24','اصحاب الجنة يومئذ خير مستقرا واحسن مقيلا');
INSERT INTO "imlaei_simple" VALUES(25,25,'25:25','ويوم تشقق السماء بالغمام ونزل الملائكة تنزيلا');
INSERT INTO "imlaei_simple" VALUES(25,26,'25:26','الملك يومئذ الحق للرحمن وكان يوما على الكافرين عسيرا');
INSERT INTO "imlaei_simple" VALUES(25,27,'25:27','ويوم يعض الظالم على يديه يقول يا ليتني اتخذت مع الرسول سبيلا');
INSERT INTO "imlaei_simple" VALUES(25,28,'25:28','يا ويلتى ليتني لم اتخذ فلانا خليلا');
INSERT INTO "imlaei_simple" VALUES(25,29,'25:29','لقد اضلني عن الذكر بعد اذ جاءني وكان الشيطان للانسان خذولا');
INSERT INTO "imlaei_simple" VALUES(25,30,'25:30','وقال الرسول يا رب ان قومي اتخذوا هذا القران مهجورا');
INSERT INTO "imlaei_simple" VALUES(25,31,'25:31','وكذلك جعلنا لكل نبي عدوا من المجرمين وكفى بربك هاديا ونصيرا');
INSERT INTO "imlaei_simple" VALUES(25,32,'25:32','وقال الذين كفروا لولا نزل عليه القران جملة واحدة كذلك لنثبت به فؤادك ورتلناه ترتيلا');
INSERT INTO "imlaei_simple" VALUES(25,33,'25:33','ولا ياتونك بمثل الا جئناك بالحق واحسن تفسيرا');
INSERT INTO "imlaei_simple" VALUES(25,34,'25:34','الذين يحشرون على وجوههم الى جهنم اولئك شر مكانا واضل سبيلا');
INSERT INTO "imlaei_simple" VALUES(25,35,'25:35','ولقد اتينا موسى الكتاب وجعلنا معه اخاه هارون وزيرا');
INSERT INTO "imlaei_simple" VALUES(25,36,'25:36','فقلنا اذهبا الى القوم الذين كذبوا باياتنا فدمرناهم تدميرا');
INSERT INTO "imlaei_simple" VALUES(25,37,'25:37','وقوم نوح لما كذبوا الرسل اغرقناهم وجعلناهم للناس اية واعتدنا للظالمين عذابا اليما');
INSERT INTO "imlaei_simple" VALUES(25,38,'25:38','وعادا وثمود واصحاب الرس وقرونا بين ذلك كثيرا');
INSERT INTO "imlaei_simple" VALUES(25,39,'25:39','وكلا ضربنا له الامثال وكلا تبرنا تتبيرا');
INSERT INTO "imlaei_simple" VALUES(25,40,'25:40','ولقد اتوا على القرية التي امطرت مطر السوء افلم يكونوا يرونها بل كانوا لا يرجون نشورا');
INSERT INTO "imlaei_simple" VALUES(25,41,'25:41','واذا راوك ان يتخذونك الا هزوا اهذا الذي بعث الله رسولا');
INSERT INTO "imlaei_simple" VALUES(25,42,'25:42','ان كاد ليضلنا عن الهتنا لولا ان صبرنا عليها وسوف يعلمون حين يرون العذاب من اضل سبيلا');
INSERT INTO "imlaei_simple" VALUES(25,43,'25:43','ارايت من اتخذ الهه هواه افانت تكون عليه وكيلا');
INSERT INTO "imlaei_simple" VALUES(25,44,'25:44','ام تحسب ان اكثرهم يسمعون او يعقلون ان هم الا كالانعام بل هم اضل سبيلا');
INSERT INTO "imlaei_simple" VALUES(25,45,'25:45','الم تر الى ربك كيف مد الظل ولو شاء لجعله ساكنا ثم جعلنا الشمس عليه دليلا');
INSERT INTO "imlaei_simple" VALUES(25,46,'25:46','ثم قبضناه الينا قبضا يسيرا');
INSERT INTO "imlaei_simple" VALUES(25,47,'25:47','وهو الذي جعل لكم الليل لباسا والنوم سباتا وجعل النهار نشورا');
INSERT INTO "imlaei_simple" VALUES(25,48,'25:48','وهو الذي ارسل الرياح بشرا بين يدي رحمته وانزلنا من السماء ماء طهورا');
INSERT INTO "imlaei_simple" VALUES(25,49,'25:49','لنحيي به بلدة ميتا ونسقيه مما خلقنا انعاما واناسي كثيرا');
INSERT INTO "imlaei_simple" VALUES(25,50,'25:50','ولقد صرفناه بينهم ليذكروا فابى اكثر الناس الا كفورا');
INSERT INTO "imlaei_simple" VALUES(25,51,'25:51','ولو شئنا لبعثنا في كل قرية نذيرا');
INSERT INTO "imlaei_simple" VALUES(25,52,'25:52','فلا تطع الكافرين وجاهدهم به جهادا كبيرا');
INSERT INTO "imlaei_simple" VALUES(25,53,'25:53','وهو الذي مرج البحرين هذا عذب فرات وهذا ملح اجاج وجعل بينهما برزخا وحجرا محجورا');
INSERT INTO "imlaei_simple" VALUES(25,54,'25:54','وهو الذي خلق من الماء بشرا فجعله نسبا وصهرا وكان ربك قديرا');
INSERT INTO "imlaei_simple" VALUES(25,55,'25:55','ويعبدون من دون الله ما لا ينفعهم ولا يضرهم وكان الكافر على ربه ظهيرا');
INSERT INTO "imlaei_simple" VALUES(25,56,'25:56','وما ارسلناك الا مبشرا ونذيرا');
INSERT INTO "imlaei_simple" VALUES(25,57,'25:57','قل ما اسالكم عليه من اجر الا من شاء ان يتخذ الى ربه سبيلا');
INSERT INTO "imlaei_simple" VALUES(25,58,'25:58','وتوكل على الحي الذي لا يموت وسبح بحمده وكفى به بذنوب عباده خبيرا');
INSERT INTO "imlaei_simple" VALUES(25,59,'25:59','الذي خلق السماوات والارض وما بينهما في ستة ايام ثم استوى على العرش الرحمن فاسال به خبيرا');
INSERT INTO "imlaei_simple" VALUES(25,60,'25:60','واذا قيل لهم اسجدوا للرحمن قالوا وما الرحمن انسجد لما تامرنا وزادهم نفورا');
INSERT INTO "imlaei_simple" VALUES(25,61,'25:61','تبارك الذي جعل في السماء بروجا وجعل فيها سراجا وقمرا منيرا');
INSERT INTO "imlaei_simple" VALUES(25,62,'25:62','وهو الذي جعل الليل والنهار خلفة لمن اراد ان يذكر او اراد شكورا');
INSERT INTO "imlaei_simple" VALUES(25,63,'25:63','وعباد الرحمن الذين يمشون على الارض هونا واذا خاطبهم الجاهلون قالوا سلاما');
INSERT INTO "imlaei_simple" VALUES(25,64,'25:64','والذين يبيتون لربهم سجدا وقياما');
INSERT INTO "imlaei_simple" VALUES(25,65,'25:65','والذين يقولون ربنا اصرف عنا عذاب جهنم ان عذابها كان غراما');
INSERT INTO "imlaei_simple" VALUES(25,66,'25:66','انها ساءت مستقرا ومقاما');
INSERT INTO "imlaei_simple" VALUES(25,67,'25:67','والذين اذا انفقوا لم يسرفوا ولم يقتروا وكان بين ذلك قواما');
INSERT INTO "imlaei_simple" VALUES(25,68,'25:68','والذين لا يدعون مع الله الها اخر ولا يقتلون النفس التي حرم الله الا بالحق ولا يزنون ومن يفعل ذلك يلق اثاما');
INSERT INTO "imlaei_simple" VALUES(25,69,'25:69','يضاعف له العذاب يوم القيامة ويخلد فيه مهانا');
INSERT INTO "imlaei_simple" VALUES(25,70,'25:70','الا من تاب وامن وعمل عملا صالحا فاولئك يبدل الله سيئاتهم حسنات وكان الله غفورا رحيما');
INSERT INTO "imlaei_simple" VALUES(25,71,'25:71','ومن تاب وعمل صالحا فانه يتوب الى الله متابا');
INSERT INTO "imlaei_simple" VALUES(25,72,'25:72','والذين لا يشهدون الزور واذا مروا باللغو مروا كراما');
INSERT INTO "imlaei_simple" VALUES(25,73,'25:73','والذين اذا ذكروا بايات ربهم لم يخروا عليها صما وعميانا');
INSERT INTO "imlaei_simple" VALUES(25,74,'25:74','والذين يقولون ربنا هب لنا من ازواجنا وذرياتنا قرة اعين واجعلنا للمتقين اماما');
INSERT INTO "imlaei_simple" VALUES(25,75,'25:75','اولئك يجزون الغرفة بما صبروا ويلقون فيها تحية وسلاما');
INSERT INTO "imlaei_simple" VALUES(25,76,'25:76','خالدين فيها حسنت مستقرا ومقاما');
INSERT INTO "imlaei_simple" VALUES(25,77,'25:77','قل ما يعبا بكم ربي لولا دعاؤكم فقد كذبتم فسوف يكون لزاما');
INSERT INTO "imlaei_simple" VALUES(26,1,'26:1','طسم');
INSERT INTO "imlaei_simple" VALUES(26,2,'26:2','تلك ايات الكتاب المبين');
INSERT INTO "imlaei_simple" VALUES(26,3,'26:3','لعلك باخع نفسك الا يكونوا مؤمنين');
INSERT INTO "imlaei_simple" VALUES(26,4,'26:4','ان نشا ننزل عليهم من السماء اية فظلت اعناقهم لها خاضعين');
INSERT INTO "imlaei_simple" VALUES(26,5,'26:5','وما ياتيهم من ذكر من الرحمن محدث الا كانوا عنه معرضين');
INSERT INTO "imlaei_simple" VALUES(26,6,'26:6','فقد كذبوا فسياتيهم انباء ما كانوا به يستهزئون');
INSERT INTO "imlaei_simple" VALUES(26,7,'26:7','اولم يروا الى الارض كم انبتنا فيها من كل زوج كريم');
INSERT INTO "imlaei_simple" VALUES(26,8,'26:8','ان في ذلك لاية وما كان اكثرهم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(26,9,'26:9','وان ربك لهو العزيز الرحيم');
INSERT INTO "imlaei_simple" VALUES(26,10,'26:10','واذ نادى ربك موسى ان ائت القوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(26,11,'26:11','قوم فرعون الا يتقون');
INSERT INTO "imlaei_simple" VALUES(26,12,'26:12','قال رب اني اخاف ان يكذبون');
INSERT INTO "imlaei_simple" VALUES(26,13,'26:13','ويضيق صدري ولا ينطلق لساني فارسل الى هارون');
INSERT INTO "imlaei_simple" VALUES(26,14,'26:14','ولهم علي ذنب فاخاف ان يقتلون');
INSERT INTO "imlaei_simple" VALUES(26,15,'26:15','قال كلا فاذهبا باياتنا انا معكم مستمعون');
INSERT INTO "imlaei_simple" VALUES(26,16,'26:16','فاتيا فرعون فقولا انا رسول رب العالمين');
INSERT INTO "imlaei_simple" VALUES(26,17,'26:17','ان ارسل معنا بني اسرائيل');
INSERT INTO "imlaei_simple" VALUES(26,18,'26:18','قال الم نربك فينا وليدا ولبثت فينا من عمرك سنين');
INSERT INTO "imlaei_simple" VALUES(26,19,'26:19','وفعلت فعلتك التي فعلت وانت من الكافرين');
INSERT INTO "imlaei_simple" VALUES(26,20,'26:20','قال فعلتها اذا وانا من الضالين');
INSERT INTO "imlaei_simple" VALUES(26,21,'26:21','ففررت منكم لما خفتكم فوهب لي ربي حكما وجعلني من المرسلين');
INSERT INTO "imlaei_simple" VALUES(26,22,'26:22','وتلك نعمة تمنها علي ان عبدت بني اسرائيل');
INSERT INTO "imlaei_simple" VALUES(26,23,'26:23','قال فرعون وما رب العالمين');
INSERT INTO "imlaei_simple" VALUES(26,24,'26:24','قال رب السماوات والارض وما بينهما ان كنتم موقنين');
INSERT INTO "imlaei_simple" VALUES(26,25,'26:25','قال لمن حوله الا تستمعون');
INSERT INTO "imlaei_simple" VALUES(26,26,'26:26','قال ربكم ورب ابائكم الاولين');
INSERT INTO "imlaei_simple" VALUES(26,27,'26:27','قال ان رسولكم الذي ارسل اليكم لمجنون');
INSERT INTO "imlaei_simple" VALUES(26,28,'26:28','قال رب المشرق والمغرب وما بينهما ان كنتم تعقلون');
INSERT INTO "imlaei_simple" VALUES(26,29,'26:29','قال لئن اتخذت الها غيري لاجعلنك من المسجونين');
INSERT INTO "imlaei_simple" VALUES(26,30,'26:30','قال اولو جئتك بشيء مبين');
INSERT INTO "imlaei_simple" VALUES(26,31,'26:31','قال فات به ان كنت من الصادقين');
INSERT INTO "imlaei_simple" VALUES(26,32,'26:32','فالقى عصاه فاذا هي ثعبان مبين');
INSERT INTO "imlaei_simple" VALUES(26,33,'26:33','ونزع يده فاذا هي بيضاء للناظرين');
INSERT INTO "imlaei_simple" VALUES(26,34,'26:34','قال للملا حوله ان هذا لساحر عليم');
INSERT INTO "imlaei_simple" VALUES(26,35,'26:35','يريد ان يخرجكم من ارضكم بسحره فماذا تامرون');
INSERT INTO "imlaei_simple" VALUES(26,36,'26:36','قالوا ارجه واخاه وابعث في المدائن حاشرين');
INSERT INTO "imlaei_simple" VALUES(26,37,'26:37','ياتوك بكل سحار عليم');
INSERT INTO "imlaei_simple" VALUES(26,38,'26:38','فجمع السحرة لميقات يوم معلوم');
INSERT INTO "imlaei_simple" VALUES(26,39,'26:39','وقيل للناس هل انتم مجتمعون');
INSERT INTO "imlaei_simple" VALUES(26,40,'26:40','لعلنا نتبع السحرة ان كانوا هم الغالبين');
INSERT INTO "imlaei_simple" VALUES(26,41,'26:41','فلما جاء السحرة قالوا لفرعون ائن لنا لاجرا ان كنا نحن الغالبين');
INSERT INTO "imlaei_simple" VALUES(26,42,'26:42','قال نعم وانكم اذا لمن المقربين');
INSERT INTO "imlaei_simple" VALUES(26,43,'26:43','قال لهم موسى القوا ما انتم ملقون');
INSERT INTO "imlaei_simple" VALUES(26,44,'26:44','فالقوا حبالهم وعصيهم وقالوا بعزة فرعون انا لنحن الغالبون');
INSERT INTO "imlaei_simple" VALUES(26,45,'26:45','فالقى موسى عصاه فاذا هي تلقف ما يافكون');
INSERT INTO "imlaei_simple" VALUES(26,46,'26:46','فالقي السحرة ساجدين');
INSERT INTO "imlaei_simple" VALUES(26,47,'26:47','قالوا امنا برب العالمين');
INSERT INTO "imlaei_simple" VALUES(26,48,'26:48','رب موسى وهارون');
INSERT INTO "imlaei_simple" VALUES(26,49,'26:49','قال امنتم له قبل ان اذن لكم انه لكبيركم الذي علمكم السحر فلسوف تعلمون لاقطعن ايديكم وارجلكم من خلاف ولاصلبنكم اجمعين');
INSERT INTO "imlaei_simple" VALUES(26,50,'26:50','قالوا لا ضير انا الى ربنا منقلبون');
INSERT INTO "imlaei_simple" VALUES(26,51,'26:51','انا نطمع ان يغفر لنا ربنا خطايانا ان كنا اول المؤمنين');
INSERT INTO "imlaei_simple" VALUES(26,52,'26:52','واوحينا الى موسى ان اسر بعبادي انكم متبعون');
INSERT INTO "imlaei_simple" VALUES(26,53,'26:53','فارسل فرعون في المدائن حاشرين');
INSERT INTO "imlaei_simple" VALUES(26,54,'26:54','ان هؤلاء لشرذمة قليلون');
INSERT INTO "imlaei_simple" VALUES(26,55,'26:55','وانهم لنا لغائظون');
INSERT INTO "imlaei_simple" VALUES(26,56,'26:56','وانا لجميع حاذرون');
INSERT INTO "imlaei_simple" VALUES(26,57,'26:57','فاخرجناهم من جنات وعيون');
INSERT INTO "imlaei_simple" VALUES(26,58,'26:58','وكنوز ومقام كريم');
INSERT INTO "imlaei_simple" VALUES(26,59,'26:59','كذلك واورثناها بني اسرائيل');
INSERT INTO "imlaei_simple" VALUES(26,60,'26:60','فاتبعوهم مشرقين');
INSERT INTO "imlaei_simple" VALUES(26,61,'26:61','فلما تراءى الجمعان قال اصحاب موسى انا لمدركون');
INSERT INTO "imlaei_simple" VALUES(26,62,'26:62','قال كلا ان معي ربي سيهدين');
INSERT INTO "imlaei_simple" VALUES(26,63,'26:63','فاوحينا الى موسى ان اضرب بعصاك البحر فانفلق فكان كل فرق كالطود العظيم');
INSERT INTO "imlaei_simple" VALUES(26,64,'26:64','وازلفنا ثم الاخرين');
INSERT INTO "imlaei_simple" VALUES(26,65,'26:65','وانجينا موسى ومن معه اجمعين');
INSERT INTO "imlaei_simple" VALUES(26,66,'26:66','ثم اغرقنا الاخرين');
INSERT INTO "imlaei_simple" VALUES(26,67,'26:67','ان في ذلك لاية وما كان اكثرهم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(26,68,'26:68','وان ربك لهو العزيز الرحيم');
INSERT INTO "imlaei_simple" VALUES(26,69,'26:69','واتل عليهم نبا ابراهيم');
INSERT INTO "imlaei_simple" VALUES(26,70,'26:70','اذ قال لابيه وقومه ما تعبدون');
INSERT INTO "imlaei_simple" VALUES(26,71,'26:71','قالوا نعبد اصناما فنظل لها عاكفين');
INSERT INTO "imlaei_simple" VALUES(26,72,'26:72','قال هل يسمعونكم اذ تدعون');
INSERT INTO "imlaei_simple" VALUES(26,73,'26:73','او ينفعونكم او يضرون');
INSERT INTO "imlaei_simple" VALUES(26,74,'26:74','قالوا بل وجدنا اباءنا كذلك يفعلون');
INSERT INTO "imlaei_simple" VALUES(26,75,'26:75','قال افرايتم ما كنتم تعبدون');
INSERT INTO "imlaei_simple" VALUES(26,76,'26:76','انتم واباؤكم الاقدمون');
INSERT INTO "imlaei_simple" VALUES(26,77,'26:77','فانهم عدو لي الا رب العالمين');
INSERT INTO "imlaei_simple" VALUES(26,78,'26:78','الذي خلقني فهو يهدين');
INSERT INTO "imlaei_simple" VALUES(26,79,'26:79','والذي هو يطعمني ويسقين');
INSERT INTO "imlaei_simple" VALUES(26,80,'26:80','واذا مرضت فهو يشفين');
INSERT INTO "imlaei_simple" VALUES(26,81,'26:81','والذي يميتني ثم يحيين');
INSERT INTO "imlaei_simple" VALUES(26,82,'26:82','والذي اطمع ان يغفر لي خطيئتي يوم الدين');
INSERT INTO "imlaei_simple" VALUES(26,83,'26:83','رب هب لي حكما والحقني بالصالحين');
INSERT INTO "imlaei_simple" VALUES(26,84,'26:84','واجعل لي لسان صدق في الاخرين');
INSERT INTO "imlaei_simple" VALUES(26,85,'26:85','واجعلني من ورثة جنة النعيم');
INSERT INTO "imlaei_simple" VALUES(26,86,'26:86','واغفر لابي انه كان من الضالين');
INSERT INTO "imlaei_simple" VALUES(26,87,'26:87','ولا تخزني يوم يبعثون');
INSERT INTO "imlaei_simple" VALUES(26,88,'26:88','يوم لا ينفع مال ولا بنون');
INSERT INTO "imlaei_simple" VALUES(26,89,'26:89','الا من اتى الله بقلب سليم');
INSERT INTO "imlaei_simple" VALUES(26,90,'26:90','وازلفت الجنة للمتقين');
INSERT INTO "imlaei_simple" VALUES(26,91,'26:91','وبرزت الجحيم للغاوين');
INSERT INTO "imlaei_simple" VALUES(26,92,'26:92','وقيل لهم اين ما كنتم تعبدون');
INSERT INTO "imlaei_simple" VALUES(26,93,'26:93','من دون الله هل ينصرونكم او ينتصرون');
INSERT INTO "imlaei_simple" VALUES(26,94,'26:94','فكبكبوا فيها هم والغاوون');
INSERT INTO "imlaei_simple" VALUES(26,95,'26:95','وجنود ابليس اجمعون');
INSERT INTO "imlaei_simple" VALUES(26,96,'26:96','قالوا وهم فيها يختصمون');
INSERT INTO "imlaei_simple" VALUES(26,97,'26:97','تالله ان كنا لفي ضلال مبين');
INSERT INTO "imlaei_simple" VALUES(26,98,'26:98','اذ نسويكم برب العالمين');
INSERT INTO "imlaei_simple" VALUES(26,99,'26:99','وما اضلنا الا المجرمون');
INSERT INTO "imlaei_simple" VALUES(26,100,'26:100','فما لنا من شافعين');
INSERT INTO "imlaei_simple" VALUES(26,101,'26:101','ولا صديق حميم');
INSERT INTO "imlaei_simple" VALUES(26,102,'26:102','فلو ان لنا كرة فنكون من المؤمنين');
INSERT INTO "imlaei_simple" VALUES(26,103,'26:103','ان في ذلك لاية وما كان اكثرهم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(26,104,'26:104','وان ربك لهو العزيز الرحيم');
INSERT INTO "imlaei_simple" VALUES(26,105,'26:105','كذبت قوم نوح المرسلين');
INSERT INTO "imlaei_simple" VALUES(26,106,'26:106','اذ قال لهم اخوهم نوح الا تتقون');
INSERT INTO "imlaei_simple" VALUES(26,107,'26:107','اني لكم رسول امين');
INSERT INTO "imlaei_simple" VALUES(26,108,'26:108','فاتقوا الله واطيعون');
INSERT INTO "imlaei_simple" VALUES(26,109,'26:109','وما اسالكم عليه من اجر ان اجري الا على رب العالمين');
INSERT INTO "imlaei_simple" VALUES(26,110,'26:110','فاتقوا الله واطيعون');
INSERT INTO "imlaei_simple" VALUES(26,111,'26:111','قالوا انؤمن لك واتبعك الارذلون');
INSERT INTO "imlaei_simple" VALUES(26,112,'26:112','قال وما علمي بما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(26,113,'26:113','ان حسابهم الا على ربي لو تشعرون');
INSERT INTO "imlaei_simple" VALUES(26,114,'26:114','وما انا بطارد المؤمنين');
INSERT INTO "imlaei_simple" VALUES(26,115,'26:115','ان انا الا نذير مبين');
INSERT INTO "imlaei_simple" VALUES(26,116,'26:116','قالوا لئن لم تنته يا نوح لتكونن من المرجومين');
INSERT INTO "imlaei_simple" VALUES(26,117,'26:117','قال رب ان قومي كذبون');
INSERT INTO "imlaei_simple" VALUES(26,118,'26:118','فافتح بيني وبينهم فتحا ونجني ومن معي من المؤمنين');
INSERT INTO "imlaei_simple" VALUES(26,119,'26:119','فانجيناه ومن معه في الفلك المشحون');
INSERT INTO "imlaei_simple" VALUES(26,120,'26:120','ثم اغرقنا بعد الباقين');
INSERT INTO "imlaei_simple" VALUES(26,121,'26:121','ان في ذلك لاية وما كان اكثرهم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(26,122,'26:122','وان ربك لهو العزيز الرحيم');
INSERT INTO "imlaei_simple" VALUES(26,123,'26:123','كذبت عاد المرسلين');
INSERT INTO "imlaei_simple" VALUES(26,124,'26:124','اذ قال لهم اخوهم هود الا تتقون');
INSERT INTO "imlaei_simple" VALUES(26,125,'26:125','اني لكم رسول امين');
INSERT INTO "imlaei_simple" VALUES(26,126,'26:126','فاتقوا الله واطيعون');
INSERT INTO "imlaei_simple" VALUES(26,127,'26:127','وما اسالكم عليه من اجر ان اجري الا على رب العالمين');
INSERT INTO "imlaei_simple" VALUES(26,128,'26:128','اتبنون بكل ريع اية تعبثون');
INSERT INTO "imlaei_simple" VALUES(26,129,'26:129','وتتخذون مصانع لعلكم تخلدون');
INSERT INTO "imlaei_simple" VALUES(26,130,'26:130','واذا بطشتم بطشتم جبارين');
INSERT INTO "imlaei_simple" VALUES(26,131,'26:131','فاتقوا الله واطيعون');
INSERT INTO "imlaei_simple" VALUES(26,132,'26:132','واتقوا الذي امدكم بما تعلمون');
INSERT INTO "imlaei_simple" VALUES(26,133,'26:133','امدكم بانعام وبنين');
INSERT INTO "imlaei_simple" VALUES(26,134,'26:134','وجنات وعيون');
INSERT INTO "imlaei_simple" VALUES(26,135,'26:135','اني اخاف عليكم عذاب يوم عظيم');
INSERT INTO "imlaei_simple" VALUES(26,136,'26:136','قالوا سواء علينا اوعظت ام لم تكن من الواعظين');
INSERT INTO "imlaei_simple" VALUES(26,137,'26:137','ان هذا الا خلق الاولين');
INSERT INTO "imlaei_simple" VALUES(26,138,'26:138','وما نحن بمعذبين');
INSERT INTO "imlaei_simple" VALUES(26,139,'26:139','فكذبوه فاهلكناهم ان في ذلك لاية وما كان اكثرهم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(26,140,'26:140','وان ربك لهو العزيز الرحيم');
INSERT INTO "imlaei_simple" VALUES(26,141,'26:141','كذبت ثمود المرسلين');
INSERT INTO "imlaei_simple" VALUES(26,142,'26:142','اذ قال لهم اخوهم صالح الا تتقون');
INSERT INTO "imlaei_simple" VALUES(26,143,'26:143','اني لكم رسول امين');
INSERT INTO "imlaei_simple" VALUES(26,144,'26:144','فاتقوا الله واطيعون');
INSERT INTO "imlaei_simple" VALUES(26,145,'26:145','وما اسالكم عليه من اجر ان اجري الا على رب العالمين');
INSERT INTO "imlaei_simple" VALUES(26,146,'26:146','اتتركون في ما هاهنا امنين');
INSERT INTO "imlaei_simple" VALUES(26,147,'26:147','في جنات وعيون');
INSERT INTO "imlaei_simple" VALUES(26,148,'26:148','وزروع ونخل طلعها هضيم');
INSERT INTO "imlaei_simple" VALUES(26,149,'26:149','وتنحتون من الجبال بيوتا فارهين');
INSERT INTO "imlaei_simple" VALUES(26,150,'26:150','فاتقوا الله واطيعون');
INSERT INTO "imlaei_simple" VALUES(26,151,'26:151','ولا تطيعوا امر المسرفين');
INSERT INTO "imlaei_simple" VALUES(26,152,'26:152','الذين يفسدون في الارض ولا يصلحون');
INSERT INTO "imlaei_simple" VALUES(26,153,'26:153','قالوا انما انت من المسحرين');
INSERT INTO "imlaei_simple" VALUES(26,154,'26:154','ما انت الا بشر مثلنا فات باية ان كنت من الصادقين');
INSERT INTO "imlaei_simple" VALUES(26,155,'26:155','قال هذه ناقة لها شرب ولكم شرب يوم معلوم');
INSERT INTO "imlaei_simple" VALUES(26,156,'26:156','ولا تمسوها بسوء فياخذكم عذاب يوم عظيم');
INSERT INTO "imlaei_simple" VALUES(26,157,'26:157','فعقروها فاصبحوا نادمين');
INSERT INTO "imlaei_simple" VALUES(26,158,'26:158','فاخذهم العذاب ان في ذلك لاية وما كان اكثرهم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(26,159,'26:159','وان ربك لهو العزيز الرحيم');
INSERT INTO "imlaei_simple" VALUES(26,160,'26:160','كذبت قوم لوط المرسلين');
INSERT INTO "imlaei_simple" VALUES(26,161,'26:161','اذ قال لهم اخوهم لوط الا تتقون');
INSERT INTO "imlaei_simple" VALUES(26,162,'26:162','اني لكم رسول امين');
INSERT INTO "imlaei_simple" VALUES(26,163,'26:163','فاتقوا الله واطيعون');
INSERT INTO "imlaei_simple" VALUES(26,164,'26:164','وما اسالكم عليه من اجر ان اجري الا على رب العالمين');
INSERT INTO "imlaei_simple" VALUES(26,165,'26:165','اتاتون الذكران من العالمين');
INSERT INTO "imlaei_simple" VALUES(26,166,'26:166','وتذرون ما خلق لكم ربكم من ازواجكم بل انتم قوم عادون');
INSERT INTO "imlaei_simple" VALUES(26,167,'26:167','قالوا لئن لم تنته يا لوط لتكونن من المخرجين');
INSERT INTO "imlaei_simple" VALUES(26,168,'26:168','قال اني لعملكم من القالين');
INSERT INTO "imlaei_simple" VALUES(26,169,'26:169','رب نجني واهلي مما يعملون');
INSERT INTO "imlaei_simple" VALUES(26,170,'26:170','فنجيناه واهله اجمعين');
INSERT INTO "imlaei_simple" VALUES(26,171,'26:171','الا عجوزا في الغابرين');
INSERT INTO "imlaei_simple" VALUES(26,172,'26:172','ثم دمرنا الاخرين');
INSERT INTO "imlaei_simple" VALUES(26,173,'26:173','وامطرنا عليهم مطرا فساء مطر المنذرين');
INSERT INTO "imlaei_simple" VALUES(26,174,'26:174','ان في ذلك لاية وما كان اكثرهم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(26,175,'26:175','وان ربك لهو العزيز الرحيم');
INSERT INTO "imlaei_simple" VALUES(26,176,'26:176','كذب اصحاب الايكة المرسلين');
INSERT INTO "imlaei_simple" VALUES(26,177,'26:177','اذ قال لهم شعيب الا تتقون');
INSERT INTO "imlaei_simple" VALUES(26,178,'26:178','اني لكم رسول امين');
INSERT INTO "imlaei_simple" VALUES(26,179,'26:179','فاتقوا الله واطيعون');
INSERT INTO "imlaei_simple" VALUES(26,180,'26:180','وما اسالكم عليه من اجر ان اجري الا على رب العالمين');
INSERT INTO "imlaei_simple" VALUES(26,181,'26:181','اوفوا الكيل ولا تكونوا من المخسرين');
INSERT INTO "imlaei_simple" VALUES(26,182,'26:182','وزنوا بالقسطاس المستقيم');
INSERT INTO "imlaei_simple" VALUES(26,183,'26:183','ولا تبخسوا الناس اشياءهم ولا تعثوا في الارض مفسدين');
INSERT INTO "imlaei_simple" VALUES(26,184,'26:184','واتقوا الذي خلقكم والجبلة الاولين');
INSERT INTO "imlaei_simple" VALUES(26,185,'26:185','قالوا انما انت من المسحرين');
INSERT INTO "imlaei_simple" VALUES(26,186,'26:186','وما انت الا بشر مثلنا وان نظنك لمن الكاذبين');
INSERT INTO "imlaei_simple" VALUES(26,187,'26:187','فاسقط علينا كسفا من السماء ان كنت من الصادقين');
INSERT INTO "imlaei_simple" VALUES(26,188,'26:188','قال ربي اعلم بما تعملون');
INSERT INTO "imlaei_simple" VALUES(26,189,'26:189','فكذبوه فاخذهم عذاب يوم الظلة انه كان عذاب يوم عظيم');
INSERT INTO "imlaei_simple" VALUES(26,190,'26:190','ان في ذلك لاية وما كان اكثرهم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(26,191,'26:191','وان ربك لهو العزيز الرحيم');
INSERT INTO "imlaei_simple" VALUES(26,192,'26:192','وانه لتنزيل رب العالمين');
INSERT INTO "imlaei_simple" VALUES(26,193,'26:193','نزل به الروح الامين');
INSERT INTO "imlaei_simple" VALUES(26,194,'26:194','على قلبك لتكون من المنذرين');
INSERT INTO "imlaei_simple" VALUES(26,195,'26:195','بلسان عربي مبين');
INSERT INTO "imlaei_simple" VALUES(26,196,'26:196','وانه لفي زبر الاولين');
INSERT INTO "imlaei_simple" VALUES(26,197,'26:197','اولم يكن لهم اية ان يعلمه علماء بني اسرائيل');
INSERT INTO "imlaei_simple" VALUES(26,198,'26:198','ولو نزلناه على بعض الاعجمين');
INSERT INTO "imlaei_simple" VALUES(26,199,'26:199','فقراه عليهم ما كانوا به مؤمنين');
INSERT INTO "imlaei_simple" VALUES(26,200,'26:200','كذلك سلكناه في قلوب المجرمين');
INSERT INTO "imlaei_simple" VALUES(26,201,'26:201','لا يؤمنون به حتى يروا العذاب الاليم');
INSERT INTO "imlaei_simple" VALUES(26,202,'26:202','فياتيهم بغتة وهم لا يشعرون');
INSERT INTO "imlaei_simple" VALUES(26,203,'26:203','فيقولوا هل نحن منظرون');
INSERT INTO "imlaei_simple" VALUES(26,204,'26:204','افبعذابنا يستعجلون');
INSERT INTO "imlaei_simple" VALUES(26,205,'26:205','افرايت ان متعناهم سنين');
INSERT INTO "imlaei_simple" VALUES(26,206,'26:206','ثم جاءهم ما كانوا يوعدون');
INSERT INTO "imlaei_simple" VALUES(26,207,'26:207','ما اغنى عنهم ما كانوا يمتعون');
INSERT INTO "imlaei_simple" VALUES(26,208,'26:208','وما اهلكنا من قرية الا لها منذرون');
INSERT INTO "imlaei_simple" VALUES(26,209,'26:209','ذكرى وما كنا ظالمين');
INSERT INTO "imlaei_simple" VALUES(26,210,'26:210','وما تنزلت به الشياطين');
INSERT INTO "imlaei_simple" VALUES(26,211,'26:211','وما ينبغي لهم وما يستطيعون');
INSERT INTO "imlaei_simple" VALUES(26,212,'26:212','انهم عن السمع لمعزولون');
INSERT INTO "imlaei_simple" VALUES(26,213,'26:213','فلا تدع مع الله الها اخر فتكون من المعذبين');
INSERT INTO "imlaei_simple" VALUES(26,214,'26:214','وانذر عشيرتك الاقربين');
INSERT INTO "imlaei_simple" VALUES(26,215,'26:215','واخفض جناحك لمن اتبعك من المؤمنين');
INSERT INTO "imlaei_simple" VALUES(26,216,'26:216','فان عصوك فقل اني بريء مما تعملون');
INSERT INTO "imlaei_simple" VALUES(26,217,'26:217','وتوكل على العزيز الرحيم');
INSERT INTO "imlaei_simple" VALUES(26,218,'26:218','الذي يراك حين تقوم');
INSERT INTO "imlaei_simple" VALUES(26,219,'26:219','وتقلبك في الساجدين');
INSERT INTO "imlaei_simple" VALUES(26,220,'26:220','انه هو السميع العليم');
INSERT INTO "imlaei_simple" VALUES(26,221,'26:221','هل انبئكم على من تنزل الشياطين');
INSERT INTO "imlaei_simple" VALUES(26,222,'26:222','تنزل على كل افاك اثيم');
INSERT INTO "imlaei_simple" VALUES(26,223,'26:223','يلقون السمع واكثرهم كاذبون');
INSERT INTO "imlaei_simple" VALUES(26,224,'26:224','والشعراء يتبعهم الغاوون');
INSERT INTO "imlaei_simple" VALUES(26,225,'26:225','الم تر انهم في كل واد يهيمون');
INSERT INTO "imlaei_simple" VALUES(26,226,'26:226','وانهم يقولون ما لا يفعلون');
INSERT INTO "imlaei_simple" VALUES(26,227,'26:227','الا الذين امنوا وعملوا الصالحات وذكروا الله كثيرا وانتصروا من بعد ما ظلموا وسيعلم الذين ظلموا اي منقلب ينقلبون');
INSERT INTO "imlaei_simple" VALUES(27,1,'27:1','طس تلك ايات القران وكتاب مبين');
INSERT INTO "imlaei_simple" VALUES(27,2,'27:2','هدى وبشرى للمؤمنين');
INSERT INTO "imlaei_simple" VALUES(27,3,'27:3','الذين يقيمون الصلاة ويؤتون الزكاة وهم بالاخرة هم يوقنون');
INSERT INTO "imlaei_simple" VALUES(27,4,'27:4','ان الذين لا يؤمنون بالاخرة زينا لهم اعمالهم فهم يعمهون');
INSERT INTO "imlaei_simple" VALUES(27,5,'27:5','اولئك الذين لهم سوء العذاب وهم في الاخرة هم الاخسرون');
INSERT INTO "imlaei_simple" VALUES(27,6,'27:6','وانك لتلقى القران من لدن حكيم عليم');
INSERT INTO "imlaei_simple" VALUES(27,7,'27:7','اذ قال موسى لاهله اني انست نارا ساتيكم منها بخبر او اتيكم بشهاب قبس لعلكم تصطلون');
INSERT INTO "imlaei_simple" VALUES(27,8,'27:8','فلما جاءها نودي ان بورك من في النار ومن حولها وسبحان الله رب العالمين');
INSERT INTO "imlaei_simple" VALUES(27,9,'27:9','يا موسى انه انا الله العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(27,10,'27:10','والق عصاك فلما راها تهتز كانها جان ولى مدبرا ولم يعقب يا موسى لا تخف اني لا يخاف لدي المرسلون');
INSERT INTO "imlaei_simple" VALUES(27,11,'27:11','الا من ظلم ثم بدل حسنا بعد سوء فاني غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(27,12,'27:12','وادخل يدك في جيبك تخرج بيضاء من غير سوء في تسع ايات الى فرعون وقومه انهم كانوا قوما فاسقين');
INSERT INTO "imlaei_simple" VALUES(27,13,'27:13','فلما جاءتهم اياتنا مبصرة قالوا هذا سحر مبين');
INSERT INTO "imlaei_simple" VALUES(27,14,'27:14','وجحدوا بها واستيقنتها انفسهم ظلما وعلوا فانظر كيف كان عاقبة المفسدين');
INSERT INTO "imlaei_simple" VALUES(27,15,'27:15','ولقد اتينا داوود وسليمان علما وقالا الحمد لله الذي فضلنا على كثير من عباده المؤمنين');
INSERT INTO "imlaei_simple" VALUES(27,16,'27:16','وورث سليمان داوود وقال يا ايها الناس علمنا منطق الطير واوتينا من كل شيء ان هذا لهو الفضل المبين');
INSERT INTO "imlaei_simple" VALUES(27,17,'27:17','وحشر لسليمان جنوده من الجن والانس والطير فهم يوزعون');
INSERT INTO "imlaei_simple" VALUES(27,18,'27:18','حتى اذا اتوا على واد النمل قالت نملة يا ايها النمل ادخلوا مساكنكم لا يحطمنكم سليمان وجنوده وهم لا يشعرون');
INSERT INTO "imlaei_simple" VALUES(27,19,'27:19','فتبسم ضاحكا من قولها وقال رب اوزعني ان اشكر نعمتك التي انعمت علي وعلى والدي وان اعمل صالحا ترضاه وادخلني برحمتك في عبادك الصالحين');
INSERT INTO "imlaei_simple" VALUES(27,20,'27:20','وتفقد الطير فقال ما لي لا ارى الهدهد ام كان من الغائبين');
INSERT INTO "imlaei_simple" VALUES(27,21,'27:21','لاعذبنه عذابا شديدا او لاذبحنه او لياتيني بسلطان مبين');
INSERT INTO "imlaei_simple" VALUES(27,22,'27:22','فمكث غير بعيد فقال احطت بما لم تحط به وجئتك من سبا بنبا يقين');
INSERT INTO "imlaei_simple" VALUES(27,23,'27:23','اني وجدت امراة تملكهم واوتيت من كل شيء ولها عرش عظيم');
INSERT INTO "imlaei_simple" VALUES(27,24,'27:24','وجدتها وقومها يسجدون للشمس من دون الله وزين لهم الشيطان اعمالهم فصدهم عن السبيل فهم لا يهتدون');
INSERT INTO "imlaei_simple" VALUES(27,25,'27:25','الا يسجدوا لله الذي يخرج الخبء في السماوات والارض ويعلم ما تخفون وما تعلنون');
INSERT INTO "imlaei_simple" VALUES(27,26,'27:26','الله لا اله الا هو رب العرش العظيم');
INSERT INTO "imlaei_simple" VALUES(27,27,'27:27','قال سننظر اصدقت ام كنت من الكاذبين');
INSERT INTO "imlaei_simple" VALUES(27,28,'27:28','اذهب بكتابي هذا فالقه اليهم ثم تول عنهم فانظر ماذا يرجعون');
INSERT INTO "imlaei_simple" VALUES(27,29,'27:29','قالت يا ايها الملا اني القي الي كتاب كريم');
INSERT INTO "imlaei_simple" VALUES(27,30,'27:30','انه من سليمان وانه بسم الله الرحمن الرحيم');
INSERT INTO "imlaei_simple" VALUES(27,31,'27:31','الا تعلوا علي واتوني مسلمين');
INSERT INTO "imlaei_simple" VALUES(27,32,'27:32','قالت يا ايها الملا افتوني في امري ما كنت قاطعة امرا حتى تشهدون');
INSERT INTO "imlaei_simple" VALUES(27,33,'27:33','قالوا نحن اولو قوة واولو باس شديد والامر اليك فانظري ماذا تامرين');
INSERT INTO "imlaei_simple" VALUES(27,34,'27:34','قالت ان الملوك اذا دخلوا قرية افسدوها وجعلوا اعزة اهلها اذلة وكذلك يفعلون');
INSERT INTO "imlaei_simple" VALUES(27,35,'27:35','واني مرسلة اليهم بهدية فناظرة بم يرجع المرسلون');
INSERT INTO "imlaei_simple" VALUES(27,36,'27:36','فلما جاء سليمان قال اتمدونن بمال فما اتاني الله خير مما اتاكم بل انتم بهديتكم تفرحون');
INSERT INTO "imlaei_simple" VALUES(27,37,'27:37','ارجع اليهم فلناتينهم بجنود لا قبل لهم بها ولنخرجنهم منها اذلة وهم صاغرون');
INSERT INTO "imlaei_simple" VALUES(27,38,'27:38','قال يا ايها الملا ايكم ياتيني بعرشها قبل ان ياتوني مسلمين');
INSERT INTO "imlaei_simple" VALUES(27,39,'27:39','قال عفريت من الجن انا اتيك به قبل ان تقوم من مقامك واني عليه لقوي امين');
INSERT INTO "imlaei_simple" VALUES(27,40,'27:40','قال الذي عنده علم من الكتاب انا اتيك به قبل ان يرتد اليك طرفك فلما راه مستقرا عنده قال هذا من فضل ربي ليبلوني ااشكر ام اكفر ومن شكر فانما يشكر لنفسه ومن كفر فان ربي غني كريم');
INSERT INTO "imlaei_simple" VALUES(27,41,'27:41','قال نكروا لها عرشها ننظر اتهتدي ام تكون من الذين لا يهتدون');
INSERT INTO "imlaei_simple" VALUES(27,42,'27:42','فلما جاءت قيل اهكذا عرشك قالت كانه هو واوتينا العلم من قبلها وكنا مسلمين');
INSERT INTO "imlaei_simple" VALUES(27,43,'27:43','وصدها ما كانت تعبد من دون الله انها كانت من قوم كافرين');
INSERT INTO "imlaei_simple" VALUES(27,44,'27:44','قيل لها ادخلي الصرح فلما راته حسبته لجة وكشفت عن ساقيها قال انه صرح ممرد من قوارير قالت رب اني ظلمت نفسي واسلمت مع سليمان لله رب العالمين');
INSERT INTO "imlaei_simple" VALUES(27,45,'27:45','ولقد ارسلنا الى ثمود اخاهم صالحا ان اعبدوا الله فاذا هم فريقان يختصمون');
INSERT INTO "imlaei_simple" VALUES(27,46,'27:46','قال يا قوم لم تستعجلون بالسيئة قبل الحسنة لولا تستغفرون الله لعلكم ترحمون');
INSERT INTO "imlaei_simple" VALUES(27,47,'27:47','قالوا اطيرنا بك وبمن معك قال طائركم عند الله بل انتم قوم تفتنون');
INSERT INTO "imlaei_simple" VALUES(27,48,'27:48','وكان في المدينة تسعة رهط يفسدون في الارض ولا يصلحون');
INSERT INTO "imlaei_simple" VALUES(27,49,'27:49','قالوا تقاسموا بالله لنبيتنه واهله ثم لنقولن لوليه ما شهدنا مهلك اهله وانا لصادقون');
INSERT INTO "imlaei_simple" VALUES(27,50,'27:50','ومكروا مكرا ومكرنا مكرا وهم لا يشعرون');
INSERT INTO "imlaei_simple" VALUES(27,51,'27:51','فانظر كيف كان عاقبة مكرهم انا دمرناهم وقومهم اجمعين');
INSERT INTO "imlaei_simple" VALUES(27,52,'27:52','فتلك بيوتهم خاوية بما ظلموا ان في ذلك لاية لقوم يعلمون');
INSERT INTO "imlaei_simple" VALUES(27,53,'27:53','وانجينا الذين امنوا وكانوا يتقون');
INSERT INTO "imlaei_simple" VALUES(27,54,'27:54','ولوطا اذ قال لقومه اتاتون الفاحشة وانتم تبصرون');
INSERT INTO "imlaei_simple" VALUES(27,55,'27:55','ائنكم لتاتون الرجال شهوة من دون النساء بل انتم قوم تجهلون');
INSERT INTO "imlaei_simple" VALUES(27,56,'27:56','فما كان جواب قومه الا ان قالوا اخرجوا ال لوط من قريتكم انهم اناس يتطهرون');
INSERT INTO "imlaei_simple" VALUES(27,57,'27:57','فانجيناه واهله الا امراته قدرناها من الغابرين');
INSERT INTO "imlaei_simple" VALUES(27,58,'27:58','وامطرنا عليهم مطرا فساء مطر المنذرين');
INSERT INTO "imlaei_simple" VALUES(27,59,'27:59','قل الحمد لله وسلام على عباده الذين اصطفى الله خير اما يشركون');
INSERT INTO "imlaei_simple" VALUES(27,60,'27:60','امن خلق السماوات والارض وانزل لكم من السماء ماء فانبتنا به حدائق ذات بهجة ما كان لكم ان تنبتوا شجرها االه مع الله بل هم قوم يعدلون');
INSERT INTO "imlaei_simple" VALUES(27,61,'27:61','امن جعل الارض قرارا وجعل خلالها انهارا وجعل لها رواسي وجعل بين البحرين حاجزا االه مع الله بل اكثرهم لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(27,62,'27:62','امن يجيب المضطر اذا دعاه ويكشف السوء ويجعلكم خلفاء الارض االه مع الله قليلا ما تذكرون');
INSERT INTO "imlaei_simple" VALUES(27,63,'27:63','امن يهديكم في ظلمات البر والبحر ومن يرسل الرياح بشرا بين يدي رحمته االه مع الله تعالى الله عما يشركون');
INSERT INTO "imlaei_simple" VALUES(27,64,'27:64','امن يبدا الخلق ثم يعيده ومن يرزقكم من السماء والارض االه مع الله قل هاتوا برهانكم ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(27,65,'27:65','قل لا يعلم من في السماوات والارض الغيب الا الله وما يشعرون ايان يبعثون');
INSERT INTO "imlaei_simple" VALUES(27,66,'27:66','بل ادارك علمهم في الاخرة بل هم في شك منها بل هم منها عمون');
INSERT INTO "imlaei_simple" VALUES(27,67,'27:67','وقال الذين كفروا ااذا كنا ترابا واباؤنا ائنا لمخرجون');
INSERT INTO "imlaei_simple" VALUES(27,68,'27:68','لقد وعدنا هذا نحن واباؤنا من قبل ان هذا الا اساطير الاولين');
INSERT INTO "imlaei_simple" VALUES(27,69,'27:69','قل سيروا في الارض فانظروا كيف كان عاقبة المجرمين');
INSERT INTO "imlaei_simple" VALUES(27,70,'27:70','ولا تحزن عليهم ولا تكن في ضيق مما يمكرون');
INSERT INTO "imlaei_simple" VALUES(27,71,'27:71','ويقولون متى هذا الوعد ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(27,72,'27:72','قل عسى ان يكون ردف لكم بعض الذي تستعجلون');
INSERT INTO "imlaei_simple" VALUES(27,73,'27:73','وان ربك لذو فضل على الناس ولكن اكثرهم لا يشكرون');
INSERT INTO "imlaei_simple" VALUES(27,74,'27:74','وان ربك ليعلم ما تكن صدورهم وما يعلنون');
INSERT INTO "imlaei_simple" VALUES(27,75,'27:75','وما من غائبة في السماء والارض الا في كتاب مبين');
INSERT INTO "imlaei_simple" VALUES(27,76,'27:76','ان هذا القران يقص على بني اسرائيل اكثر الذي هم فيه يختلفون');
INSERT INTO "imlaei_simple" VALUES(27,77,'27:77','وانه لهدى ورحمة للمؤمنين');
INSERT INTO "imlaei_simple" VALUES(27,78,'27:78','ان ربك يقضي بينهم بحكمه وهو العزيز العليم');
INSERT INTO "imlaei_simple" VALUES(27,79,'27:79','فتوكل على الله انك على الحق المبين');
INSERT INTO "imlaei_simple" VALUES(27,80,'27:80','انك لا تسمع الموتى ولا تسمع الصم الدعاء اذا ولوا مدبرين');
INSERT INTO "imlaei_simple" VALUES(27,81,'27:81','وما انت بهادي العمي عن ضلالتهم ان تسمع الا من يؤمن باياتنا فهم مسلمون');
INSERT INTO "imlaei_simple" VALUES(27,82,'27:82','واذا وقع القول عليهم اخرجنا لهم دابة من الارض تكلمهم ان الناس كانوا باياتنا لا يوقنون');
INSERT INTO "imlaei_simple" VALUES(27,83,'27:83','ويوم نحشر من كل امة فوجا ممن يكذب باياتنا فهم يوزعون');
INSERT INTO "imlaei_simple" VALUES(27,84,'27:84','حتى اذا جاءوا قال اكذبتم باياتي ولم تحيطوا بها علما اماذا كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(27,85,'27:85','ووقع القول عليهم بما ظلموا فهم لا ينطقون');
INSERT INTO "imlaei_simple" VALUES(27,86,'27:86','الم يروا انا جعلنا الليل ليسكنوا فيه والنهار مبصرا ان في ذلك لايات لقوم يؤمنون');
INSERT INTO "imlaei_simple" VALUES(27,87,'27:87','ويوم ينفخ في الصور ففزع من في السماوات ومن في الارض الا من شاء الله وكل اتوه داخرين');
INSERT INTO "imlaei_simple" VALUES(27,88,'27:88','وترى الجبال تحسبها جامدة وهي تمر مر السحاب صنع الله الذي اتقن كل شيء انه خبير بما تفعلون');
INSERT INTO "imlaei_simple" VALUES(27,89,'27:89','من جاء بالحسنة فله خير منها وهم من فزع يومئذ امنون');
INSERT INTO "imlaei_simple" VALUES(27,90,'27:90','ومن جاء بالسيئة فكبت وجوههم في النار هل تجزون الا ما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(27,91,'27:91','انما امرت ان اعبد رب هذه البلدة الذي حرمها وله كل شيء وامرت ان اكون من المسلمين');
INSERT INTO "imlaei_simple" VALUES(27,92,'27:92','وان اتلو القران فمن اهتدى فانما يهتدي لنفسه ومن ضل فقل انما انا من المنذرين');
INSERT INTO "imlaei_simple" VALUES(27,93,'27:93','وقل الحمد لله سيريكم اياته فتعرفونها وما ربك بغافل عما تعملون');
INSERT INTO "imlaei_simple" VALUES(28,1,'28:1','طسم');
INSERT INTO "imlaei_simple" VALUES(28,2,'28:2','تلك ايات الكتاب المبين');
INSERT INTO "imlaei_simple" VALUES(28,3,'28:3','نتلو عليك من نبا موسى وفرعون بالحق لقوم يؤمنون');
INSERT INTO "imlaei_simple" VALUES(28,4,'28:4','ان فرعون علا في الارض وجعل اهلها شيعا يستضعف طائفة منهم يذبح ابناءهم ويستحيي نساءهم انه كان من المفسدين');
INSERT INTO "imlaei_simple" VALUES(28,5,'28:5','ونريد ان نمن على الذين استضعفوا في الارض ونجعلهم ائمة ونجعلهم الوارثين');
INSERT INTO "imlaei_simple" VALUES(28,6,'28:6','ونمكن لهم في الارض ونري فرعون وهامان وجنودهما منهم ما كانوا يحذرون');
INSERT INTO "imlaei_simple" VALUES(28,7,'28:7','واوحينا الى ام موسى ان ارضعيه فاذا خفت عليه فالقيه في اليم ولا تخافي ولا تحزني انا رادوه اليك وجاعلوه من المرسلين');
INSERT INTO "imlaei_simple" VALUES(28,8,'28:8','فالتقطه ال فرعون ليكون لهم عدوا وحزنا ان فرعون وهامان وجنودهما كانوا خاطئين');
INSERT INTO "imlaei_simple" VALUES(28,9,'28:9','وقالت امرات فرعون قرت عين لي ولك لا تقتلوه عسى ان ينفعنا او نتخذه ولدا وهم لا يشعرون');
INSERT INTO "imlaei_simple" VALUES(28,10,'28:10','واصبح فؤاد ام موسى فارغا ان كادت لتبدي به لولا ان ربطنا على قلبها لتكون من المؤمنين');
INSERT INTO "imlaei_simple" VALUES(28,11,'28:11','وقالت لاخته قصيه فبصرت به عن جنب وهم لا يشعرون');
INSERT INTO "imlaei_simple" VALUES(28,12,'28:12','وحرمنا عليه المراضع من قبل فقالت هل ادلكم على اهل بيت يكفلونه لكم وهم له ناصحون');
INSERT INTO "imlaei_simple" VALUES(28,13,'28:13','فرددناه الى امه كي تقر عينها ولا تحزن ولتعلم ان وعد الله حق ولكن اكثرهم لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(28,14,'28:14','ولما بلغ اشده واستوى اتيناه حكما وعلما وكذلك نجزي المحسنين');
INSERT INTO "imlaei_simple" VALUES(28,15,'28:15','ودخل المدينة على حين غفلة من اهلها فوجد فيها رجلين يقتتلان هذا من شيعته وهذا من عدوه فاستغاثه الذي من شيعته على الذي من عدوه فوكزه موسى فقضى عليه قال هذا من عمل الشيطان انه عدو مضل مبين');
INSERT INTO "imlaei_simple" VALUES(28,16,'28:16','قال رب اني ظلمت نفسي فاغفر لي فغفر له انه هو الغفور الرحيم');
INSERT INTO "imlaei_simple" VALUES(28,17,'28:17','قال رب بما انعمت علي فلن اكون ظهيرا للمجرمين');
INSERT INTO "imlaei_simple" VALUES(28,18,'28:18','فاصبح في المدينة خائفا يترقب فاذا الذي استنصره بالامس يستصرخه قال له موسى انك لغوي مبين');
INSERT INTO "imlaei_simple" VALUES(28,19,'28:19','فلما ان اراد ان يبطش بالذي هو عدو لهما قال يا موسى اتريد ان تقتلني كما قتلت نفسا بالامس ان تريد الا ان تكون جبارا في الارض وما تريد ان تكون من المصلحين');
INSERT INTO "imlaei_simple" VALUES(28,20,'28:20','وجاء رجل من اقصى المدينة يسعى قال يا موسى ان الملا ياتمرون بك ليقتلوك فاخرج اني لك من الناصحين');
INSERT INTO "imlaei_simple" VALUES(28,21,'28:21','فخرج منها خائفا يترقب قال رب نجني من القوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(28,22,'28:22','ولما توجه تلقاء مدين قال عسى ربي ان يهديني سواء السبيل');
INSERT INTO "imlaei_simple" VALUES(28,23,'28:23','ولما ورد ماء مدين وجد عليه امة من الناس يسقون ووجد من دونهم امراتين تذودان قال ما خطبكما قالتا لا نسقي حتى يصدر الرعاء وابونا شيخ كبير');
INSERT INTO "imlaei_simple" VALUES(28,24,'28:24','فسقى لهما ثم تولى الى الظل فقال رب اني لما انزلت الي من خير فقير');
INSERT INTO "imlaei_simple" VALUES(28,25,'28:25','فجاءته احداهما تمشي على استحياء قالت ان ابي يدعوك ليجزيك اجر ما سقيت لنا فلما جاءه وقص عليه القصص قال لا تخف نجوت من القوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(28,26,'28:26','قالت احداهما يا ابت استاجره ان خير من استاجرت القوي الامين');
INSERT INTO "imlaei_simple" VALUES(28,27,'28:27','قال اني اريد ان انكحك احدى ابنتي هاتين على ان تاجرني ثماني حجج فان اتممت عشرا فمن عندك وما اريد ان اشق عليك ستجدني ان شاء الله من الصالحين');
INSERT INTO "imlaei_simple" VALUES(28,28,'28:28','قال ذلك بيني وبينك ايما الاجلين قضيت فلا عدوان علي والله على ما نقول وكيل');
INSERT INTO "imlaei_simple" VALUES(28,29,'28:29','فلما قضى موسى الاجل وسار باهله انس من جانب الطور نارا قال لاهله امكثوا اني انست نارا لعلي اتيكم منها بخبر او جذوة من النار لعلكم تصطلون');
INSERT INTO "imlaei_simple" VALUES(28,30,'28:30','فلما اتاها نودي من شاطئ الواد الايمن في البقعة المباركة من الشجرة ان يا موسى اني انا الله رب العالمين');
INSERT INTO "imlaei_simple" VALUES(28,31,'28:31','وان الق عصاك فلما راها تهتز كانها جان ولى مدبرا ولم يعقب يا موسى اقبل ولا تخف انك من الامنين');
INSERT INTO "imlaei_simple" VALUES(28,32,'28:32','اسلك يدك في جيبك تخرج بيضاء من غير سوء واضمم اليك جناحك من الرهب فذانك برهانان من ربك الى فرعون وملئه انهم كانوا قوما فاسقين');
INSERT INTO "imlaei_simple" VALUES(28,33,'28:33','قال رب اني قتلت منهم نفسا فاخاف ان يقتلون');
INSERT INTO "imlaei_simple" VALUES(28,34,'28:34','واخي هارون هو افصح مني لسانا فارسله معي ردءا يصدقني اني اخاف ان يكذبون');
INSERT INTO "imlaei_simple" VALUES(28,35,'28:35','قال سنشد عضدك باخيك ونجعل لكما سلطانا فلا يصلون اليكما باياتنا انتما ومن اتبعكما الغالبون');
INSERT INTO "imlaei_simple" VALUES(28,36,'28:36','فلما جاءهم موسى باياتنا بينات قالوا ما هذا الا سحر مفترى وما سمعنا بهذا في ابائنا الاولين');
INSERT INTO "imlaei_simple" VALUES(28,37,'28:37','وقال موسى ربي اعلم بمن جاء بالهدى من عنده ومن تكون له عاقبة الدار انه لا يفلح الظالمون');
INSERT INTO "imlaei_simple" VALUES(28,38,'28:38','وقال فرعون يا ايها الملا ما علمت لكم من اله غيري فاوقد لي يا هامان على الطين فاجعل لي صرحا لعلي اطلع الى اله موسى واني لاظنه من الكاذبين');
INSERT INTO "imlaei_simple" VALUES(28,39,'28:39','واستكبر هو وجنوده في الارض بغير الحق وظنوا انهم الينا لا يرجعون');
INSERT INTO "imlaei_simple" VALUES(28,40,'28:40','فاخذناه وجنوده فنبذناهم في اليم فانظر كيف كان عاقبة الظالمين');
INSERT INTO "imlaei_simple" VALUES(28,41,'28:41','وجعلناهم ائمة يدعون الى النار ويوم القيامة لا ينصرون');
INSERT INTO "imlaei_simple" VALUES(28,42,'28:42','واتبعناهم في هذه الدنيا لعنة ويوم القيامة هم من المقبوحين');
INSERT INTO "imlaei_simple" VALUES(28,43,'28:43','ولقد اتينا موسى الكتاب من بعد ما اهلكنا القرون الاولى بصائر للناس وهدى ورحمة لعلهم يتذكرون');
INSERT INTO "imlaei_simple" VALUES(28,44,'28:44','وما كنت بجانب الغربي اذ قضينا الى موسى الامر وما كنت من الشاهدين');
INSERT INTO "imlaei_simple" VALUES(28,45,'28:45','ولكنا انشانا قرونا فتطاول عليهم العمر وما كنت ثاويا في اهل مدين تتلو عليهم اياتنا ولكنا كنا مرسلين');
INSERT INTO "imlaei_simple" VALUES(28,46,'28:46','وما كنت بجانب الطور اذ نادينا ولكن رحمة من ربك لتنذر قوما ما اتاهم من نذير من قبلك لعلهم يتذكرون');
INSERT INTO "imlaei_simple" VALUES(28,47,'28:47','ولولا ان تصيبهم مصيبة بما قدمت ايديهم فيقولوا ربنا لولا ارسلت الينا رسولا فنتبع اياتك ونكون من المؤمنين');
INSERT INTO "imlaei_simple" VALUES(28,48,'28:48','فلما جاءهم الحق من عندنا قالوا لولا اوتي مثل ما اوتي موسى اولم يكفروا بما اوتي موسى من قبل قالوا سحران تظاهرا وقالوا انا بكل كافرون');
INSERT INTO "imlaei_simple" VALUES(28,49,'28:49','قل فاتوا بكتاب من عند الله هو اهدى منهما اتبعه ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(28,50,'28:50','فان لم يستجيبوا لك فاعلم انما يتبعون اهواءهم ومن اضل ممن اتبع هواه بغير هدى من الله ان الله لا يهدي القوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(28,51,'28:51','ولقد وصلنا لهم القول لعلهم يتذكرون');
INSERT INTO "imlaei_simple" VALUES(28,52,'28:52','الذين اتيناهم الكتاب من قبله هم به يؤمنون');
INSERT INTO "imlaei_simple" VALUES(28,53,'28:53','واذا يتلى عليهم قالوا امنا به انه الحق من ربنا انا كنا من قبله مسلمين');
INSERT INTO "imlaei_simple" VALUES(28,54,'28:54','اولئك يؤتون اجرهم مرتين بما صبروا ويدرءون بالحسنة السيئة ومما رزقناهم ينفقون');
INSERT INTO "imlaei_simple" VALUES(28,55,'28:55','واذا سمعوا اللغو اعرضوا عنه وقالوا لنا اعمالنا ولكم اعمالكم سلام عليكم لا نبتغي الجاهلين');
INSERT INTO "imlaei_simple" VALUES(28,56,'28:56','انك لا تهدي من احببت ولكن الله يهدي من يشاء وهو اعلم بالمهتدين');
INSERT INTO "imlaei_simple" VALUES(28,57,'28:57','وقالوا ان نتبع الهدى معك نتخطف من ارضنا اولم نمكن لهم حرما امنا يجبى اليه ثمرات كل شيء رزقا من لدنا ولكن اكثرهم لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(28,58,'28:58','وكم اهلكنا من قرية بطرت معيشتها فتلك مساكنهم لم تسكن من بعدهم الا قليلا وكنا نحن الوارثين');
INSERT INTO "imlaei_simple" VALUES(28,59,'28:59','وما كان ربك مهلك القرى حتى يبعث في امها رسولا يتلو عليهم اياتنا وما كنا مهلكي القرى الا واهلها ظالمون');
INSERT INTO "imlaei_simple" VALUES(28,60,'28:60','وما اوتيتم من شيء فمتاع الحياة الدنيا وزينتها وما عند الله خير وابقى افلا تعقلون');
INSERT INTO "imlaei_simple" VALUES(28,61,'28:61','افمن وعدناه وعدا حسنا فهو لاقيه كمن متعناه متاع الحياة الدنيا ثم هو يوم القيامة من المحضرين');
INSERT INTO "imlaei_simple" VALUES(28,62,'28:62','ويوم يناديهم فيقول اين شركائي الذين كنتم تزعمون');
INSERT INTO "imlaei_simple" VALUES(28,63,'28:63','قال الذين حق عليهم القول ربنا هؤلاء الذين اغوينا اغويناهم كما غوينا تبرانا اليك ما كانوا ايانا يعبدون');
INSERT INTO "imlaei_simple" VALUES(28,64,'28:64','وقيل ادعوا شركاءكم فدعوهم فلم يستجيبوا لهم وراوا العذاب لو انهم كانوا يهتدون');
INSERT INTO "imlaei_simple" VALUES(28,65,'28:65','ويوم يناديهم فيقول ماذا اجبتم المرسلين');
INSERT INTO "imlaei_simple" VALUES(28,66,'28:66','فعميت عليهم الانباء يومئذ فهم لا يتساءلون');
INSERT INTO "imlaei_simple" VALUES(28,67,'28:67','فاما من تاب وامن وعمل صالحا فعسى ان يكون من المفلحين');
INSERT INTO "imlaei_simple" VALUES(28,68,'28:68','وربك يخلق ما يشاء ويختار ما كان لهم الخيرة سبحان الله وتعالى عما يشركون');
INSERT INTO "imlaei_simple" VALUES(28,69,'28:69','وربك يعلم ما تكن صدورهم وما يعلنون');
INSERT INTO "imlaei_simple" VALUES(28,70,'28:70','وهو الله لا اله الا هو له الحمد في الاولى والاخرة وله الحكم واليه ترجعون');
INSERT INTO "imlaei_simple" VALUES(28,71,'28:71','قل ارايتم ان جعل الله عليكم الليل سرمدا الى يوم القيامة من اله غير الله ياتيكم بضياء افلا تسمعون');
INSERT INTO "imlaei_simple" VALUES(28,72,'28:72','قل ارايتم ان جعل الله عليكم النهار سرمدا الى يوم القيامة من اله غير الله ياتيكم بليل تسكنون فيه افلا تبصرون');
INSERT INTO "imlaei_simple" VALUES(28,73,'28:73','ومن رحمته جعل لكم الليل والنهار لتسكنوا فيه ولتبتغوا من فضله ولعلكم تشكرون');
INSERT INTO "imlaei_simple" VALUES(28,74,'28:74','ويوم يناديهم فيقول اين شركائي الذين كنتم تزعمون');
INSERT INTO "imlaei_simple" VALUES(28,75,'28:75','ونزعنا من كل امة شهيدا فقلنا هاتوا برهانكم فعلموا ان الحق لله وضل عنهم ما كانوا يفترون');
INSERT INTO "imlaei_simple" VALUES(28,76,'28:76','ان قارون كان من قوم موسى فبغى عليهم واتيناه من الكنوز ما ان مفاتحه لتنوء بالعصبة اولي القوة اذ قال له قومه لا تفرح ان الله لا يحب الفرحين');
INSERT INTO "imlaei_simple" VALUES(28,77,'28:77','وابتغ فيما اتاك الله الدار الاخرة ولا تنس نصيبك من الدنيا واحسن كما احسن الله اليك ولا تبغ الفساد في الارض ان الله لا يحب المفسدين');
INSERT INTO "imlaei_simple" VALUES(28,78,'28:78','قال انما اوتيته على علم عندي اولم يعلم ان الله قد اهلك من قبله من القرون من هو اشد منه قوة واكثر جمعا ولا يسال عن ذنوبهم المجرمون');
INSERT INTO "imlaei_simple" VALUES(28,79,'28:79','فخرج على قومه في زينته قال الذين يريدون الحياة الدنيا يا ليت لنا مثل ما اوتي قارون انه لذو حظ عظيم');
INSERT INTO "imlaei_simple" VALUES(28,80,'28:80','وقال الذين اوتوا العلم ويلكم ثواب الله خير لمن امن وعمل صالحا ولا يلقاها الا الصابرون');
INSERT INTO "imlaei_simple" VALUES(28,81,'28:81','فخسفنا به وبداره الارض فما كان له من فئة ينصرونه من دون الله وما كان من المنتصرين');
INSERT INTO "imlaei_simple" VALUES(28,82,'28:82','واصبح الذين تمنوا مكانه بالامس يقولون ويكان الله يبسط الرزق لمن يشاء من عباده ويقدر لولا ان من الله علينا لخسف بنا ويكانه لا يفلح الكافرون');
INSERT INTO "imlaei_simple" VALUES(28,83,'28:83','تلك الدار الاخرة نجعلها للذين لا يريدون علوا في الارض ولا فسادا والعاقبة للمتقين');
INSERT INTO "imlaei_simple" VALUES(28,84,'28:84','من جاء بالحسنة فله خير منها ومن جاء بالسيئة فلا يجزى الذين عملوا السيئات الا ما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(28,85,'28:85','ان الذي فرض عليك القران لرادك الى معاد قل ربي اعلم من جاء بالهدى ومن هو في ضلال مبين');
INSERT INTO "imlaei_simple" VALUES(28,86,'28:86','وما كنت ترجو ان يلقى اليك الكتاب الا رحمة من ربك فلا تكونن ظهيرا للكافرين');
INSERT INTO "imlaei_simple" VALUES(28,87,'28:87','ولا يصدنك عن ايات الله بعد اذ انزلت اليك وادع الى ربك ولا تكونن من المشركين');
INSERT INTO "imlaei_simple" VALUES(28,88,'28:88','ولا تدع مع الله الها اخر لا اله الا هو كل شيء هالك الا وجهه له الحكم واليه ترجعون');
INSERT INTO "imlaei_simple" VALUES(29,1,'29:1','الم');
INSERT INTO "imlaei_simple" VALUES(29,2,'29:2','احسب الناس ان يتركوا ان يقولوا امنا وهم لا يفتنون');
INSERT INTO "imlaei_simple" VALUES(29,3,'29:3','ولقد فتنا الذين من قبلهم فليعلمن الله الذين صدقوا وليعلمن الكاذبين');
INSERT INTO "imlaei_simple" VALUES(29,4,'29:4','ام حسب الذين يعملون السيئات ان يسبقونا ساء ما يحكمون');
INSERT INTO "imlaei_simple" VALUES(29,5,'29:5','من كان يرجو لقاء الله فان اجل الله لات وهو السميع العليم');
INSERT INTO "imlaei_simple" VALUES(29,6,'29:6','ومن جاهد فانما يجاهد لنفسه ان الله لغني عن العالمين');
INSERT INTO "imlaei_simple" VALUES(29,7,'29:7','والذين امنوا وعملوا الصالحات لنكفرن عنهم سيئاتهم ولنجزينهم احسن الذي كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(29,8,'29:8','ووصينا الانسان بوالديه حسنا وان جاهداك لتشرك بي ما ليس لك به علم فلا تطعهما الي مرجعكم فانبئكم بما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(29,9,'29:9','والذين امنوا وعملوا الصالحات لندخلنهم في الصالحين');
INSERT INTO "imlaei_simple" VALUES(29,10,'29:10','ومن الناس من يقول امنا بالله فاذا اوذي في الله جعل فتنة الناس كعذاب الله ولئن جاء نصر من ربك ليقولن انا كنا معكم اوليس الله باعلم بما في صدور العالمين');
INSERT INTO "imlaei_simple" VALUES(29,11,'29:11','وليعلمن الله الذين امنوا وليعلمن المنافقين');
INSERT INTO "imlaei_simple" VALUES(29,12,'29:12','وقال الذين كفروا للذين امنوا اتبعوا سبيلنا ولنحمل خطاياكم وما هم بحاملين من خطاياهم من شيء انهم لكاذبون');
INSERT INTO "imlaei_simple" VALUES(29,13,'29:13','وليحملن اثقالهم واثقالا مع اثقالهم وليسالن يوم القيامة عما كانوا يفترون');
INSERT INTO "imlaei_simple" VALUES(29,14,'29:14','ولقد ارسلنا نوحا الى قومه فلبث فيهم الف سنة الا خمسين عاما فاخذهم الطوفان وهم ظالمون');
INSERT INTO "imlaei_simple" VALUES(29,15,'29:15','فانجيناه واصحاب السفينة وجعلناها اية للعالمين');
INSERT INTO "imlaei_simple" VALUES(29,16,'29:16','وابراهيم اذ قال لقومه اعبدوا الله واتقوه ذلكم خير لكم ان كنتم تعلمون');
INSERT INTO "imlaei_simple" VALUES(29,17,'29:17','انما تعبدون من دون الله اوثانا وتخلقون افكا ان الذين تعبدون من دون الله لا يملكون لكم رزقا فابتغوا عند الله الرزق واعبدوه واشكروا له اليه ترجعون');
INSERT INTO "imlaei_simple" VALUES(29,18,'29:18','وان تكذبوا فقد كذب امم من قبلكم وما على الرسول الا البلاغ المبين');
INSERT INTO "imlaei_simple" VALUES(29,19,'29:19','اولم يروا كيف يبدئ الله الخلق ثم يعيده ان ذلك على الله يسير');
INSERT INTO "imlaei_simple" VALUES(29,20,'29:20','قل سيروا في الارض فانظروا كيف بدا الخلق ثم الله ينشئ النشاة الاخرة ان الله على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(29,21,'29:21','يعذب من يشاء ويرحم من يشاء واليه تقلبون');
INSERT INTO "imlaei_simple" VALUES(29,22,'29:22','وما انتم بمعجزين في الارض ولا في السماء وما لكم من دون الله من ولي ولا نصير');
INSERT INTO "imlaei_simple" VALUES(29,23,'29:23','والذين كفروا بايات الله ولقائه اولئك يئسوا من رحمتي واولئك لهم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(29,24,'29:24','فما كان جواب قومه الا ان قالوا اقتلوه او حرقوه فانجاه الله من النار ان في ذلك لايات لقوم يؤمنون');
INSERT INTO "imlaei_simple" VALUES(29,25,'29:25','وقال انما اتخذتم من دون الله اوثانا مودة بينكم في الحياة الدنيا ثم يوم القيامة يكفر بعضكم ببعض ويلعن بعضكم بعضا وماواكم النار وما لكم من ناصرين');
INSERT INTO "imlaei_simple" VALUES(29,26,'29:26','فامن له لوط وقال اني مهاجر الى ربي انه هو العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(29,27,'29:27','ووهبنا له اسحاق ويعقوب وجعلنا في ذريته النبوة والكتاب واتيناه اجره في الدنيا وانه في الاخرة لمن الصالحين');
INSERT INTO "imlaei_simple" VALUES(29,28,'29:28','ولوطا اذ قال لقومه انكم لتاتون الفاحشة ما سبقكم بها من احد من العالمين');
INSERT INTO "imlaei_simple" VALUES(29,29,'29:29','ائنكم لتاتون الرجال وتقطعون السبيل وتاتون في ناديكم المنكر فما كان جواب قومه الا ان قالوا ائتنا بعذاب الله ان كنت من الصادقين');
INSERT INTO "imlaei_simple" VALUES(29,30,'29:30','قال رب انصرني على القوم المفسدين');
INSERT INTO "imlaei_simple" VALUES(29,31,'29:31','ولما جاءت رسلنا ابراهيم بالبشرى قالوا انا مهلكو اهل هذه القرية ان اهلها كانوا ظالمين');
INSERT INTO "imlaei_simple" VALUES(29,32,'29:32','قال ان فيها لوطا قالوا نحن اعلم بمن فيها لننجينه واهله الا امراته كانت من الغابرين');
INSERT INTO "imlaei_simple" VALUES(29,33,'29:33','ولما ان جاءت رسلنا لوطا سيء بهم وضاق بهم ذرعا وقالوا لا تخف ولا تحزن انا منجوك واهلك الا امراتك كانت من الغابرين');
INSERT INTO "imlaei_simple" VALUES(29,34,'29:34','انا منزلون على اهل هذه القرية رجزا من السماء بما كانوا يفسقون');
INSERT INTO "imlaei_simple" VALUES(29,35,'29:35','ولقد تركنا منها اية بينة لقوم يعقلون');
INSERT INTO "imlaei_simple" VALUES(29,36,'29:36','والى مدين اخاهم شعيبا فقال يا قوم اعبدوا الله وارجوا اليوم الاخر ولا تعثوا في الارض مفسدين');
INSERT INTO "imlaei_simple" VALUES(29,37,'29:37','فكذبوه فاخذتهم الرجفة فاصبحوا في دارهم جاثمين');
INSERT INTO "imlaei_simple" VALUES(29,38,'29:38','وعادا وثمود وقد تبين لكم من مساكنهم وزين لهم الشيطان اعمالهم فصدهم عن السبيل وكانوا مستبصرين');
INSERT INTO "imlaei_simple" VALUES(29,39,'29:39','وقارون وفرعون وهامان ولقد جاءهم موسى بالبينات فاستكبروا في الارض وما كانوا سابقين');
INSERT INTO "imlaei_simple" VALUES(29,40,'29:40','فكلا اخذنا بذنبه فمنهم من ارسلنا عليه حاصبا ومنهم من اخذته الصيحة ومنهم من خسفنا به الارض ومنهم من اغرقنا وما كان الله ليظلمهم ولكن كانوا انفسهم يظلمون');
INSERT INTO "imlaei_simple" VALUES(29,41,'29:41','مثل الذين اتخذوا من دون الله اولياء كمثل العنكبوت اتخذت بيتا وان اوهن البيوت لبيت العنكبوت لو كانوا يعلمون');
INSERT INTO "imlaei_simple" VALUES(29,42,'29:42','ان الله يعلم ما يدعون من دونه من شيء وهو العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(29,43,'29:43','وتلك الامثال نضربها للناس وما يعقلها الا العالمون');
INSERT INTO "imlaei_simple" VALUES(29,44,'29:44','خلق الله السماوات والارض بالحق ان في ذلك لاية للمؤمنين');
INSERT INTO "imlaei_simple" VALUES(29,45,'29:45','اتل ما اوحي اليك من الكتاب واقم الصلاة ان الصلاة تنهى عن الفحشاء والمنكر ولذكر الله اكبر والله يعلم ما تصنعون');
INSERT INTO "imlaei_simple" VALUES(29,46,'29:46','ولا تجادلوا اهل الكتاب الا بالتي هي احسن الا الذين ظلموا منهم وقولوا امنا بالذي انزل الينا وانزل اليكم والهنا والهكم واحد ونحن له مسلمون');
INSERT INTO "imlaei_simple" VALUES(29,47,'29:47','وكذلك انزلنا اليك الكتاب فالذين اتيناهم الكتاب يؤمنون به ومن هؤلاء من يؤمن به وما يجحد باياتنا الا الكافرون');
INSERT INTO "imlaei_simple" VALUES(29,48,'29:48','وما كنت تتلو من قبله من كتاب ولا تخطه بيمينك اذا لارتاب المبطلون');
INSERT INTO "imlaei_simple" VALUES(29,49,'29:49','بل هو ايات بينات في صدور الذين اوتوا العلم وما يجحد باياتنا الا الظالمون');
INSERT INTO "imlaei_simple" VALUES(29,50,'29:50','وقالوا لولا انزل عليه ايات من ربه قل انما الايات عند الله وانما انا نذير مبين');
INSERT INTO "imlaei_simple" VALUES(29,51,'29:51','اولم يكفهم انا انزلنا عليك الكتاب يتلى عليهم ان في ذلك لرحمة وذكرى لقوم يؤمنون');
INSERT INTO "imlaei_simple" VALUES(29,52,'29:52','قل كفى بالله بيني وبينكم شهيدا يعلم ما في السماوات والارض والذين امنوا بالباطل وكفروا بالله اولئك هم الخاسرون');
INSERT INTO "imlaei_simple" VALUES(29,53,'29:53','ويستعجلونك بالعذاب ولولا اجل مسمى لجاءهم العذاب ولياتينهم بغتة وهم لا يشعرون');
INSERT INTO "imlaei_simple" VALUES(29,54,'29:54','يستعجلونك بالعذاب وان جهنم لمحيطة بالكافرين');
INSERT INTO "imlaei_simple" VALUES(29,55,'29:55','يوم يغشاهم العذاب من فوقهم ومن تحت ارجلهم ويقول ذوقوا ما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(29,56,'29:56','يا عبادي الذين امنوا ان ارضي واسعة فاياي فاعبدون');
INSERT INTO "imlaei_simple" VALUES(29,57,'29:57','كل نفس ذائقة الموت ثم الينا ترجعون');
INSERT INTO "imlaei_simple" VALUES(29,58,'29:58','والذين امنوا وعملوا الصالحات لنبوئنهم من الجنة غرفا تجري من تحتها الانهار خالدين فيها نعم اجر العاملين');
INSERT INTO "imlaei_simple" VALUES(29,59,'29:59','الذين صبروا وعلى ربهم يتوكلون');
INSERT INTO "imlaei_simple" VALUES(29,60,'29:60','وكاين من دابة لا تحمل رزقها الله يرزقها واياكم وهو السميع العليم');
INSERT INTO "imlaei_simple" VALUES(29,61,'29:61','ولئن سالتهم من خلق السماوات والارض وسخر الشمس والقمر ليقولن الله فانى يؤفكون');
INSERT INTO "imlaei_simple" VALUES(29,62,'29:62','الله يبسط الرزق لمن يشاء من عباده ويقدر له ان الله بكل شيء عليم');
INSERT INTO "imlaei_simple" VALUES(29,63,'29:63','ولئن سالتهم من نزل من السماء ماء فاحيا به الارض من بعد موتها ليقولن الله قل الحمد لله بل اكثرهم لا يعقلون');
INSERT INTO "imlaei_simple" VALUES(29,64,'29:64','وما هذه الحياة الدنيا الا لهو ولعب وان الدار الاخرة لهي الحيوان لو كانوا يعلمون');
INSERT INTO "imlaei_simple" VALUES(29,65,'29:65','فاذا ركبوا في الفلك دعوا الله مخلصين له الدين فلما نجاهم الى البر اذا هم يشركون');
INSERT INTO "imlaei_simple" VALUES(29,66,'29:66','ليكفروا بما اتيناهم وليتمتعوا فسوف يعلمون');
INSERT INTO "imlaei_simple" VALUES(29,67,'29:67','اولم يروا انا جعلنا حرما امنا ويتخطف الناس من حولهم افبالباطل يؤمنون وبنعمة الله يكفرون');
INSERT INTO "imlaei_simple" VALUES(29,68,'29:68','ومن اظلم ممن افترى على الله كذبا او كذب بالحق لما جاءه اليس في جهنم مثوى للكافرين');
INSERT INTO "imlaei_simple" VALUES(29,69,'29:69','والذين جاهدوا فينا لنهدينهم سبلنا وان الله لمع المحسنين');
INSERT INTO "imlaei_simple" VALUES(30,1,'30:1','الم');
INSERT INTO "imlaei_simple" VALUES(30,2,'30:2','غلبت الروم');
INSERT INTO "imlaei_simple" VALUES(30,3,'30:3','في ادنى الارض وهم من بعد غلبهم سيغلبون');
INSERT INTO "imlaei_simple" VALUES(30,4,'30:4','في بضع سنين لله الامر من قبل ومن بعد ويومئذ يفرح المؤمنون');
INSERT INTO "imlaei_simple" VALUES(30,5,'30:5','بنصر الله ينصر من يشاء وهو العزيز الرحيم');
INSERT INTO "imlaei_simple" VALUES(30,6,'30:6','وعد الله لا يخلف الله وعده ولكن اكثر الناس لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(30,7,'30:7','يعلمون ظاهرا من الحياة الدنيا وهم عن الاخرة هم غافلون');
INSERT INTO "imlaei_simple" VALUES(30,8,'30:8','اولم يتفكروا في انفسهم ما خلق الله السماوات والارض وما بينهما الا بالحق واجل مسمى وان كثيرا من الناس بلقاء ربهم لكافرون');
INSERT INTO "imlaei_simple" VALUES(30,9,'30:9','اولم يسيروا في الارض فينظروا كيف كان عاقبة الذين من قبلهم كانوا اشد منهم قوة واثاروا الارض وعمروها اكثر مما عمروها وجاءتهم رسلهم بالبينات فما كان الله ليظلمهم ولكن كانوا انفسهم يظلمون');
INSERT INTO "imlaei_simple" VALUES(30,10,'30:10','ثم كان عاقبة الذين اساءوا السواى ان كذبوا بايات الله وكانوا بها يستهزئون');
INSERT INTO "imlaei_simple" VALUES(30,11,'30:11','الله يبدا الخلق ثم يعيده ثم اليه ترجعون');
INSERT INTO "imlaei_simple" VALUES(30,12,'30:12','ويوم تقوم الساعة يبلس المجرمون');
INSERT INTO "imlaei_simple" VALUES(30,13,'30:13','ولم يكن لهم من شركائهم شفعاء وكانوا بشركائهم كافرين');
INSERT INTO "imlaei_simple" VALUES(30,14,'30:14','ويوم تقوم الساعة يومئذ يتفرقون');
INSERT INTO "imlaei_simple" VALUES(30,15,'30:15','فاما الذين امنوا وعملوا الصالحات فهم في روضة يحبرون');
INSERT INTO "imlaei_simple" VALUES(30,16,'30:16','واما الذين كفروا وكذبوا باياتنا ولقاء الاخرة فاولئك في العذاب محضرون');
INSERT INTO "imlaei_simple" VALUES(30,17,'30:17','فسبحان الله حين تمسون وحين تصبحون');
INSERT INTO "imlaei_simple" VALUES(30,18,'30:18','وله الحمد في السماوات والارض وعشيا وحين تظهرون');
INSERT INTO "imlaei_simple" VALUES(30,19,'30:19','يخرج الحي من الميت ويخرج الميت من الحي ويحيي الارض بعد موتها وكذلك تخرجون');
INSERT INTO "imlaei_simple" VALUES(30,20,'30:20','ومن اياته ان خلقكم من تراب ثم اذا انتم بشر تنتشرون');
INSERT INTO "imlaei_simple" VALUES(30,21,'30:21','ومن اياته ان خلق لكم من انفسكم ازواجا لتسكنوا اليها وجعل بينكم مودة ورحمة ان في ذلك لايات لقوم يتفكرون');
INSERT INTO "imlaei_simple" VALUES(30,22,'30:22','ومن اياته خلق السماوات والارض واختلاف السنتكم والوانكم ان في ذلك لايات للعالمين');
INSERT INTO "imlaei_simple" VALUES(30,23,'30:23','ومن اياته منامكم بالليل والنهار وابتغاؤكم من فضله ان في ذلك لايات لقوم يسمعون');
INSERT INTO "imlaei_simple" VALUES(30,24,'30:24','ومن اياته يريكم البرق خوفا وطمعا وينزل من السماء ماء فيحيي به الارض بعد موتها ان في ذلك لايات لقوم يعقلون');
INSERT INTO "imlaei_simple" VALUES(30,25,'30:25','ومن اياته ان تقوم السماء والارض بامره ثم اذا دعاكم دعوة من الارض اذا انتم تخرجون');
INSERT INTO "imlaei_simple" VALUES(30,26,'30:26','وله من في السماوات والارض كل له قانتون');
INSERT INTO "imlaei_simple" VALUES(30,27,'30:27','وهو الذي يبدا الخلق ثم يعيده وهو اهون عليه وله المثل الاعلى في السماوات والارض وهو العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(30,28,'30:28','ضرب لكم مثلا من انفسكم هل لكم من ما ملكت ايمانكم من شركاء في ما رزقناكم فانتم فيه سواء تخافونهم كخيفتكم انفسكم كذلك نفصل الايات لقوم يعقلون');
INSERT INTO "imlaei_simple" VALUES(30,29,'30:29','بل اتبع الذين ظلموا اهواءهم بغير علم فمن يهدي من اضل الله وما لهم من ناصرين');
INSERT INTO "imlaei_simple" VALUES(30,30,'30:30','فاقم وجهك للدين حنيفا فطرت الله التي فطر الناس عليها لا تبديل لخلق الله ذلك الدين القيم ولكن اكثر الناس لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(30,31,'30:31','منيبين اليه واتقوه واقيموا الصلاة ولا تكونوا من المشركين');
INSERT INTO "imlaei_simple" VALUES(30,32,'30:32','من الذين فرقوا دينهم وكانوا شيعا كل حزب بما لديهم فرحون');
INSERT INTO "imlaei_simple" VALUES(30,33,'30:33','واذا مس الناس ضر دعوا ربهم منيبين اليه ثم اذا اذاقهم منه رحمة اذا فريق منهم بربهم يشركون');
INSERT INTO "imlaei_simple" VALUES(30,34,'30:34','ليكفروا بما اتيناهم فتمتعوا فسوف تعلمون');
INSERT INTO "imlaei_simple" VALUES(30,35,'30:35','ام انزلنا عليهم سلطانا فهو يتكلم بما كانوا به يشركون');
INSERT INTO "imlaei_simple" VALUES(30,36,'30:36','واذا اذقنا الناس رحمة فرحوا بها وان تصبهم سيئة بما قدمت ايديهم اذا هم يقنطون');
INSERT INTO "imlaei_simple" VALUES(30,37,'30:37','اولم يروا ان الله يبسط الرزق لمن يشاء ويقدر ان في ذلك لايات لقوم يؤمنون');
INSERT INTO "imlaei_simple" VALUES(30,38,'30:38','فات ذا القربى حقه والمسكين وابن السبيل ذلك خير للذين يريدون وجه الله واولئك هم المفلحون');
INSERT INTO "imlaei_simple" VALUES(30,39,'30:39','وما اتيتم من ربا ليربو في اموال الناس فلا يربو عند الله وما اتيتم من زكاة تريدون وجه الله فاولئك هم المضعفون');
INSERT INTO "imlaei_simple" VALUES(30,40,'30:40','الله الذي خلقكم ثم رزقكم ثم يميتكم ثم يحييكم هل من شركائكم من يفعل من ذلكم من شيء سبحانه وتعالى عما يشركون');
INSERT INTO "imlaei_simple" VALUES(30,41,'30:41','ظهر الفساد في البر والبحر بما كسبت ايدي الناس ليذيقهم بعض الذي عملوا لعلهم يرجعون');
INSERT INTO "imlaei_simple" VALUES(30,42,'30:42','قل سيروا في الارض فانظروا كيف كان عاقبة الذين من قبل كان اكثرهم مشركين');
INSERT INTO "imlaei_simple" VALUES(30,43,'30:43','فاقم وجهك للدين القيم من قبل ان ياتي يوم لا مرد له من الله يومئذ يصدعون');
INSERT INTO "imlaei_simple" VALUES(30,44,'30:44','من كفر فعليه كفره ومن عمل صالحا فلانفسهم يمهدون');
INSERT INTO "imlaei_simple" VALUES(30,45,'30:45','ليجزي الذين امنوا وعملوا الصالحات من فضله انه لا يحب الكافرين');
INSERT INTO "imlaei_simple" VALUES(30,46,'30:46','ومن اياته ان يرسل الرياح مبشرات وليذيقكم من رحمته ولتجري الفلك بامره ولتبتغوا من فضله ولعلكم تشكرون');
INSERT INTO "imlaei_simple" VALUES(30,47,'30:47','ولقد ارسلنا من قبلك رسلا الى قومهم فجاءوهم بالبينات فانتقمنا من الذين اجرموا وكان حقا علينا نصر المؤمنين');
INSERT INTO "imlaei_simple" VALUES(30,48,'30:48','الله الذي يرسل الرياح فتثير سحابا فيبسطه في السماء كيف يشاء ويجعله كسفا فترى الودق يخرج من خلاله فاذا اصاب به من يشاء من عباده اذا هم يستبشرون');
INSERT INTO "imlaei_simple" VALUES(30,49,'30:49','وان كانوا من قبل ان ينزل عليهم من قبله لمبلسين');
INSERT INTO "imlaei_simple" VALUES(30,50,'30:50','فانظر الى اثار رحمت الله كيف يحيي الارض بعد موتها ان ذلك لمحيي الموتى وهو على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(30,51,'30:51','ولئن ارسلنا ريحا فراوه مصفرا لظلوا من بعده يكفرون');
INSERT INTO "imlaei_simple" VALUES(30,52,'30:52','فانك لا تسمع الموتى ولا تسمع الصم الدعاء اذا ولوا مدبرين');
INSERT INTO "imlaei_simple" VALUES(30,53,'30:53','وما انت بهاد العمي عن ضلالتهم ان تسمع الا من يؤمن باياتنا فهم مسلمون');
INSERT INTO "imlaei_simple" VALUES(30,54,'30:54','الله الذي خلقكم من ضعف ثم جعل من بعد ضعف قوة ثم جعل من بعد قوة ضعفا وشيبة يخلق ما يشاء وهو العليم القدير');
INSERT INTO "imlaei_simple" VALUES(30,55,'30:55','ويوم تقوم الساعة يقسم المجرمون ما لبثوا غير ساعة كذلك كانوا يؤفكون');
INSERT INTO "imlaei_simple" VALUES(30,56,'30:56','وقال الذين اوتوا العلم والايمان لقد لبثتم في كتاب الله الى يوم البعث فهذا يوم البعث ولكنكم كنتم لا تعلمون');
INSERT INTO "imlaei_simple" VALUES(30,57,'30:57','فيومئذ لا ينفع الذين ظلموا معذرتهم ولا هم يستعتبون');
INSERT INTO "imlaei_simple" VALUES(30,58,'30:58','ولقد ضربنا للناس في هذا القران من كل مثل ولئن جئتهم باية ليقولن الذين كفروا ان انتم الا مبطلون');
INSERT INTO "imlaei_simple" VALUES(30,59,'30:59','كذلك يطبع الله على قلوب الذين لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(30,60,'30:60','فاصبر ان وعد الله حق ولا يستخفنك الذين لا يوقنون');
INSERT INTO "imlaei_simple" VALUES(31,1,'31:1','الم');
INSERT INTO "imlaei_simple" VALUES(31,2,'31:2','تلك ايات الكتاب الحكيم');
INSERT INTO "imlaei_simple" VALUES(31,3,'31:3','هدى ورحمة للمحسنين');
INSERT INTO "imlaei_simple" VALUES(31,4,'31:4','الذين يقيمون الصلاة ويؤتون الزكاة وهم بالاخرة هم يوقنون');
INSERT INTO "imlaei_simple" VALUES(31,5,'31:5','اولئك على هدى من ربهم واولئك هم المفلحون');
INSERT INTO "imlaei_simple" VALUES(31,6,'31:6','ومن الناس من يشتري لهو الحديث ليضل عن سبيل الله بغير علم ويتخذها هزوا اولئك لهم عذاب مهين');
INSERT INTO "imlaei_simple" VALUES(31,7,'31:7','واذا تتلى عليه اياتنا ولى مستكبرا كان لم يسمعها كان في اذنيه وقرا فبشره بعذاب اليم');
INSERT INTO "imlaei_simple" VALUES(31,8,'31:8','ان الذين امنوا وعملوا الصالحات لهم جنات النعيم');
INSERT INTO "imlaei_simple" VALUES(31,9,'31:9','خالدين فيها وعد الله حقا وهو العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(31,10,'31:10','خلق السماوات بغير عمد ترونها والقى في الارض رواسي ان تميد بكم وبث فيها من كل دابة وانزلنا من السماء ماء فانبتنا فيها من كل زوج كريم');
INSERT INTO "imlaei_simple" VALUES(31,11,'31:11','هذا خلق الله فاروني ماذا خلق الذين من دونه بل الظالمون في ضلال مبين');
INSERT INTO "imlaei_simple" VALUES(31,12,'31:12','ولقد اتينا لقمان الحكمة ان اشكر لله ومن يشكر فانما يشكر لنفسه ومن كفر فان الله غني حميد');
INSERT INTO "imlaei_simple" VALUES(31,13,'31:13','واذ قال لقمان لابنه وهو يعظه يا بني لا تشرك بالله ان الشرك لظلم عظيم');
INSERT INTO "imlaei_simple" VALUES(31,14,'31:14','ووصينا الانسان بوالديه حملته امه وهنا على وهن وفصاله في عامين ان اشكر لي ولوالديك الي المصير');
INSERT INTO "imlaei_simple" VALUES(31,15,'31:15','وان جاهداك على ان تشرك بي ما ليس لك به علم فلا تطعهما وصاحبهما في الدنيا معروفا واتبع سبيل من اناب الي ثم الي مرجعكم فانبئكم بما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(31,16,'31:16','يا بني انها ان تك مثقال حبة من خردل فتكن في صخرة او في السماوات او في الارض يات بها الله ان الله لطيف خبير');
INSERT INTO "imlaei_simple" VALUES(31,17,'31:17','يا بني اقم الصلاة وامر بالمعروف وانه عن المنكر واصبر على ما اصابك ان ذلك من عزم الامور');
INSERT INTO "imlaei_simple" VALUES(31,18,'31:18','ولا تصعر خدك للناس ولا تمش في الارض مرحا ان الله لا يحب كل مختال فخور');
INSERT INTO "imlaei_simple" VALUES(31,19,'31:19','واقصد في مشيك واغضض من صوتك ان انكر الاصوات لصوت الحمير');
INSERT INTO "imlaei_simple" VALUES(31,20,'31:20','الم تروا ان الله سخر لكم ما في السماوات وما في الارض واسبغ عليكم نعمه ظاهرة وباطنة ومن الناس من يجادل في الله بغير علم ولا هدى ولا كتاب منير');
INSERT INTO "imlaei_simple" VALUES(31,21,'31:21','واذا قيل لهم اتبعوا ما انزل الله قالوا بل نتبع ما وجدنا عليه اباءنا اولو كان الشيطان يدعوهم الى عذاب السعير');
INSERT INTO "imlaei_simple" VALUES(31,22,'31:22','ومن يسلم وجهه الى الله وهو محسن فقد استمسك بالعروة الوثقى والى الله عاقبة الامور');
INSERT INTO "imlaei_simple" VALUES(31,23,'31:23','ومن كفر فلا يحزنك كفره الينا مرجعهم فننبئهم بما عملوا ان الله عليم بذات الصدور');
INSERT INTO "imlaei_simple" VALUES(31,24,'31:24','نمتعهم قليلا ثم نضطرهم الى عذاب غليظ');
INSERT INTO "imlaei_simple" VALUES(31,25,'31:25','ولئن سالتهم من خلق السماوات والارض ليقولن الله قل الحمد لله بل اكثرهم لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(31,26,'31:26','لله ما في السماوات والارض ان الله هو الغني الحميد');
INSERT INTO "imlaei_simple" VALUES(31,27,'31:27','ولو انما في الارض من شجرة اقلام والبحر يمده من بعده سبعة ابحر ما نفدت كلمات الله ان الله عزيز حكيم');
INSERT INTO "imlaei_simple" VALUES(31,28,'31:28','ما خلقكم ولا بعثكم الا كنفس واحدة ان الله سميع بصير');
INSERT INTO "imlaei_simple" VALUES(31,29,'31:29','الم تر ان الله يولج الليل في النهار ويولج النهار في الليل وسخر الشمس والقمر كل يجري الى اجل مسمى وان الله بما تعملون خبير');
INSERT INTO "imlaei_simple" VALUES(31,30,'31:30','ذلك بان الله هو الحق وان ما يدعون من دونه الباطل وان الله هو العلي الكبير');
INSERT INTO "imlaei_simple" VALUES(31,31,'31:31','الم تر ان الفلك تجري في البحر بنعمت الله ليريكم من اياته ان في ذلك لايات لكل صبار شكور');
INSERT INTO "imlaei_simple" VALUES(31,32,'31:32','واذا غشيهم موج كالظلل دعوا الله مخلصين له الدين فلما نجاهم الى البر فمنهم مقتصد وما يجحد باياتنا الا كل ختار كفور');
INSERT INTO "imlaei_simple" VALUES(31,33,'31:33','يا ايها الناس اتقوا ربكم واخشوا يوما لا يجزي والد عن ولده ولا مولود هو جاز عن والده شيئا ان وعد الله حق فلا تغرنكم الحياة الدنيا ولا يغرنكم بالله الغرور');
INSERT INTO "imlaei_simple" VALUES(31,34,'31:34','ان الله عنده علم الساعة وينزل الغيث ويعلم ما في الارحام وما تدري نفس ماذا تكسب غدا وما تدري نفس باي ارض تموت ان الله عليم خبير');
INSERT INTO "imlaei_simple" VALUES(32,1,'32:1','الم');
INSERT INTO "imlaei_simple" VALUES(32,2,'32:2','تنزيل الكتاب لا ريب فيه من رب العالمين');
INSERT INTO "imlaei_simple" VALUES(32,3,'32:3','ام يقولون افتراه بل هو الحق من ربك لتنذر قوما ما اتاهم من نذير من قبلك لعلهم يهتدون');
INSERT INTO "imlaei_simple" VALUES(32,4,'32:4','الله الذي خلق السماوات والارض وما بينهما في ستة ايام ثم استوى على العرش ما لكم من دونه من ولي ولا شفيع افلا تتذكرون');
INSERT INTO "imlaei_simple" VALUES(32,5,'32:5','يدبر الامر من السماء الى الارض ثم يعرج اليه في يوم كان مقداره الف سنة مما تعدون');
INSERT INTO "imlaei_simple" VALUES(32,6,'32:6','ذلك عالم الغيب والشهادة العزيز الرحيم');
INSERT INTO "imlaei_simple" VALUES(32,7,'32:7','الذي احسن كل شيء خلقه وبدا خلق الانسان من طين');
INSERT INTO "imlaei_simple" VALUES(32,8,'32:8','ثم جعل نسله من سلالة من ماء مهين');
INSERT INTO "imlaei_simple" VALUES(32,9,'32:9','ثم سواه ونفخ فيه من روحه وجعل لكم السمع والابصار والافئدة قليلا ما تشكرون');
INSERT INTO "imlaei_simple" VALUES(32,10,'32:10','وقالوا ااذا ضللنا في الارض اانا لفي خلق جديد بل هم بلقاء ربهم كافرون');
INSERT INTO "imlaei_simple" VALUES(32,11,'32:11','قل يتوفاكم ملك الموت الذي وكل بكم ثم الى ربكم ترجعون');
INSERT INTO "imlaei_simple" VALUES(32,12,'32:12','ولو ترى اذ المجرمون ناكسو رءوسهم عند ربهم ربنا ابصرنا وسمعنا فارجعنا نعمل صالحا انا موقنون');
INSERT INTO "imlaei_simple" VALUES(32,13,'32:13','ولو شئنا لاتينا كل نفس هداها ولكن حق القول مني لاملان جهنم من الجنة والناس اجمعين');
INSERT INTO "imlaei_simple" VALUES(32,14,'32:14','فذوقوا بما نسيتم لقاء يومكم هذا انا نسيناكم وذوقوا عذاب الخلد بما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(32,15,'32:15','انما يؤمن باياتنا الذين اذا ذكروا بها خروا سجدا وسبحوا بحمد ربهم وهم لا يستكبرون');
INSERT INTO "imlaei_simple" VALUES(32,16,'32:16','تتجافى جنوبهم عن المضاجع يدعون ربهم خوفا وطمعا ومما رزقناهم ينفقون');
INSERT INTO "imlaei_simple" VALUES(32,17,'32:17','فلا تعلم نفس ما اخفي لهم من قرة اعين جزاء بما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(32,18,'32:18','افمن كان مؤمنا كمن كان فاسقا لا يستوون');
INSERT INTO "imlaei_simple" VALUES(32,19,'32:19','اما الذين امنوا وعملوا الصالحات فلهم جنات الماوى نزلا بما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(32,20,'32:20','واما الذين فسقوا فماواهم النار كلما ارادوا ان يخرجوا منها اعيدوا فيها وقيل لهم ذوقوا عذاب النار الذي كنتم به تكذبون');
INSERT INTO "imlaei_simple" VALUES(32,21,'32:21','ولنذيقنهم من العذاب الادنى دون العذاب الاكبر لعلهم يرجعون');
INSERT INTO "imlaei_simple" VALUES(32,22,'32:22','ومن اظلم ممن ذكر بايات ربه ثم اعرض عنها انا من المجرمين منتقمون');
INSERT INTO "imlaei_simple" VALUES(32,23,'32:23','ولقد اتينا موسى الكتاب فلا تكن في مرية من لقائه وجعلناه هدى لبني اسرائيل');
INSERT INTO "imlaei_simple" VALUES(32,24,'32:24','وجعلنا منهم ائمة يهدون بامرنا لما صبروا وكانوا باياتنا يوقنون');
INSERT INTO "imlaei_simple" VALUES(32,25,'32:25','ان ربك هو يفصل بينهم يوم القيامة فيما كانوا فيه يختلفون');
INSERT INTO "imlaei_simple" VALUES(32,26,'32:26','اولم يهد لهم كم اهلكنا من قبلهم من القرون يمشون في مساكنهم ان في ذلك لايات افلا يسمعون');
INSERT INTO "imlaei_simple" VALUES(32,27,'32:27','اولم يروا انا نسوق الماء الى الارض الجرز فنخرج به زرعا تاكل منه انعامهم وانفسهم افلا يبصرون');
INSERT INTO "imlaei_simple" VALUES(32,28,'32:28','ويقولون متى هذا الفتح ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(32,29,'32:29','قل يوم الفتح لا ينفع الذين كفروا ايمانهم ولا هم ينظرون');
INSERT INTO "imlaei_simple" VALUES(32,30,'32:30','فاعرض عنهم وانتظر انهم منتظرون');
INSERT INTO "imlaei_simple" VALUES(33,1,'33:1','يا ايها النبي اتق الله ولا تطع الكافرين والمنافقين ان الله كان عليما حكيما');
INSERT INTO "imlaei_simple" VALUES(33,2,'33:2','واتبع ما يوحى اليك من ربك ان الله كان بما تعملون خبيرا');
INSERT INTO "imlaei_simple" VALUES(33,3,'33:3','وتوكل على الله وكفى بالله وكيلا');
INSERT INTO "imlaei_simple" VALUES(33,4,'33:4','ما جعل الله لرجل من قلبين في جوفه وما جعل ازواجكم اللائي تظاهرون منهن امهاتكم وما جعل ادعياءكم ابناءكم ذلكم قولكم بافواهكم والله يقول الحق وهو يهدي السبيل');
INSERT INTO "imlaei_simple" VALUES(33,5,'33:5','ادعوهم لابائهم هو اقسط عند الله فان لم تعلموا اباءهم فاخوانكم في الدين ومواليكم وليس عليكم جناح فيما اخطاتم به ولكن ما تعمدت قلوبكم وكان الله غفورا رحيما');
INSERT INTO "imlaei_simple" VALUES(33,6,'33:6','النبي اولى بالمؤمنين من انفسهم وازواجه امهاتهم واولو الارحام بعضهم اولى ببعض في كتاب الله من المؤمنين والمهاجرين الا ان تفعلوا الى اوليائكم معروفا كان ذلك في الكتاب مسطورا');
INSERT INTO "imlaei_simple" VALUES(33,7,'33:7','واذ اخذنا من النبيين ميثاقهم ومنك ومن نوح وابراهيم وموسى وعيسى ابن مريم واخذنا منهم ميثاقا غليظا');
INSERT INTO "imlaei_simple" VALUES(33,8,'33:8','ليسال الصادقين عن صدقهم واعد للكافرين عذابا اليما');
INSERT INTO "imlaei_simple" VALUES(33,9,'33:9','يا ايها الذين امنوا اذكروا نعمة الله عليكم اذ جاءتكم جنود فارسلنا عليهم ريحا وجنودا لم تروها وكان الله بما تعملون بصيرا');
INSERT INTO "imlaei_simple" VALUES(33,10,'33:10','اذ جاءوكم من فوقكم ومن اسفل منكم واذ زاغت الابصار وبلغت القلوب الحناجر وتظنون بالله الظنونا');
INSERT INTO "imlaei_simple" VALUES(33,11,'33:11','هنالك ابتلي المؤمنون وزلزلوا زلزالا شديدا');
INSERT INTO "imlaei_simple" VALUES(33,12,'33:12','واذ يقول المنافقون والذين في قلوبهم مرض ما وعدنا الله ورسوله الا غرورا');
INSERT INTO "imlaei_simple" VALUES(33,13,'33:13','واذ قالت طائفة منهم يا اهل يثرب لا مقام لكم فارجعوا ويستاذن فريق منهم النبي يقولون ان بيوتنا عورة وما هي بعورة ان يريدون الا فرارا');
INSERT INTO "imlaei_simple" VALUES(33,14,'33:14','ولو دخلت عليهم من اقطارها ثم سئلوا الفتنة لاتوها وما تلبثوا بها الا يسيرا');
INSERT INTO "imlaei_simple" VALUES(33,15,'33:15','ولقد كانوا عاهدوا الله من قبل لا يولون الادبار وكان عهد الله مسئولا');
INSERT INTO "imlaei_simple" VALUES(33,16,'33:16','قل لن ينفعكم الفرار ان فررتم من الموت او القتل واذا لا تمتعون الا قليلا');
INSERT INTO "imlaei_simple" VALUES(33,17,'33:17','قل من ذا الذي يعصمكم من الله ان اراد بكم سوءا او اراد بكم رحمة ولا يجدون لهم من دون الله وليا ولا نصيرا');
INSERT INTO "imlaei_simple" VALUES(33,18,'33:18','قد يعلم الله المعوقين منكم والقائلين لاخوانهم هلم الينا ولا ياتون الباس الا قليلا');
INSERT INTO "imlaei_simple" VALUES(33,19,'33:19','اشحة عليكم فاذا جاء الخوف رايتهم ينظرون اليك تدور اعينهم كالذي يغشى عليه من الموت فاذا ذهب الخوف سلقوكم بالسنة حداد اشحة على الخير اولئك لم يؤمنوا فاحبط الله اعمالهم وكان ذلك على الله يسيرا');
INSERT INTO "imlaei_simple" VALUES(33,20,'33:20','يحسبون الاحزاب لم يذهبوا وان يات الاحزاب يودوا لو انهم بادون في الاعراب يسالون عن انبائكم ولو كانوا فيكم ما قاتلوا الا قليلا');
INSERT INTO "imlaei_simple" VALUES(33,21,'33:21','لقد كان لكم في رسول الله اسوة حسنة لمن كان يرجو الله واليوم الاخر وذكر الله كثيرا');
INSERT INTO "imlaei_simple" VALUES(33,22,'33:22','ولما راى المؤمنون الاحزاب قالوا هذا ما وعدنا الله ورسوله وصدق الله ورسوله وما زادهم الا ايمانا وتسليما');
INSERT INTO "imlaei_simple" VALUES(33,23,'33:23','من المؤمنين رجال صدقوا ما عاهدوا الله عليه فمنهم من قضى نحبه ومنهم من ينتظر وما بدلوا تبديلا');
INSERT INTO "imlaei_simple" VALUES(33,24,'33:24','ليجزي الله الصادقين بصدقهم ويعذب المنافقين ان شاء او يتوب عليهم ان الله كان غفورا رحيما');
INSERT INTO "imlaei_simple" VALUES(33,25,'33:25','ورد الله الذين كفروا بغيظهم لم ينالوا خيرا وكفى الله المؤمنين القتال وكان الله قويا عزيزا');
INSERT INTO "imlaei_simple" VALUES(33,26,'33:26','وانزل الذين ظاهروهم من اهل الكتاب من صياصيهم وقذف في قلوبهم الرعب فريقا تقتلون وتاسرون فريقا');
INSERT INTO "imlaei_simple" VALUES(33,27,'33:27','واورثكم ارضهم وديارهم واموالهم وارضا لم تطئوها وكان الله على كل شيء قديرا');
INSERT INTO "imlaei_simple" VALUES(33,28,'33:28','يا ايها النبي قل لازواجك ان كنتن تردن الحياة الدنيا وزينتها فتعالين امتعكن واسرحكن سراحا جميلا');
INSERT INTO "imlaei_simple" VALUES(33,29,'33:29','وان كنتن تردن الله ورسوله والدار الاخرة فان الله اعد للمحسنات منكن اجرا عظيما');
INSERT INTO "imlaei_simple" VALUES(33,30,'33:30','يا نساء النبي من يات منكن بفاحشة مبينة يضاعف لها العذاب ضعفين وكان ذلك على الله يسيرا');
INSERT INTO "imlaei_simple" VALUES(33,31,'33:31','ومن يقنت منكن لله ورسوله وتعمل صالحا نؤتها اجرها مرتين واعتدنا لها رزقا كريما');
INSERT INTO "imlaei_simple" VALUES(33,32,'33:32','يا نساء النبي لستن كاحد من النساء ان اتقيتن فلا تخضعن بالقول فيطمع الذي في قلبه مرض وقلن قولا معروفا');
INSERT INTO "imlaei_simple" VALUES(33,33,'33:33','وقرن في بيوتكن ولا تبرجن تبرج الجاهلية الاولى واقمن الصلاة واتين الزكاة واطعن الله ورسوله انما يريد الله ليذهب عنكم الرجس اهل البيت ويطهركم تطهيرا');
INSERT INTO "imlaei_simple" VALUES(33,34,'33:34','واذكرن ما يتلى في بيوتكن من ايات الله والحكمة ان الله كان لطيفا خبيرا');
INSERT INTO "imlaei_simple" VALUES(33,35,'33:35','ان المسلمين والمسلمات والمؤمنين والمؤمنات والقانتين والقانتات والصادقين والصادقات والصابرين والصابرات والخاشعين والخاشعات والمتصدقين والمتصدقات والصائمين والصائمات والحافظين فروجهم والحافظات والذاكرين الله كثيرا والذاكرات اعد الله لهم مغفرة واجرا عظيما');
INSERT INTO "imlaei_simple" VALUES(33,36,'33:36','وما كان لمؤمن ولا مؤمنة اذا قضى الله ورسوله امرا ان يكون لهم الخيرة من امرهم ومن يعص الله ورسوله فقد ضل ضلالا مبينا');
INSERT INTO "imlaei_simple" VALUES(33,37,'33:37','واذ تقول للذي انعم الله عليه وانعمت عليه امسك عليك زوجك واتق الله وتخفي في نفسك ما الله مبديه وتخشى الناس والله احق ان تخشاه فلما قضى زيد منها وطرا زوجناكها لكي لا يكون على المؤمنين حرج في ازواج ادعيائهم اذا قضوا منهن وطرا وكان امر الله مفعولا');
INSERT INTO "imlaei_simple" VALUES(33,38,'33:38','ما كان على النبي من حرج فيما فرض الله له سنة الله في الذين خلوا من قبل وكان امر الله قدرا مقدورا');
INSERT INTO "imlaei_simple" VALUES(33,39,'33:39','الذين يبلغون رسالات الله ويخشونه ولا يخشون احدا الا الله وكفى بالله حسيبا');
INSERT INTO "imlaei_simple" VALUES(33,40,'33:40','ما كان محمد ابا احد من رجالكم ولكن رسول الله وخاتم النبيين وكان الله بكل شيء عليما');
INSERT INTO "imlaei_simple" VALUES(33,41,'33:41','يا ايها الذين امنوا اذكروا الله ذكرا كثيرا');
INSERT INTO "imlaei_simple" VALUES(33,42,'33:42','وسبحوه بكرة واصيلا');
INSERT INTO "imlaei_simple" VALUES(33,43,'33:43','هو الذي يصلي عليكم وملائكته ليخرجكم من الظلمات الى النور وكان بالمؤمنين رحيما');
INSERT INTO "imlaei_simple" VALUES(33,44,'33:44','تحيتهم يوم يلقونه سلام واعد لهم اجرا كريما');
INSERT INTO "imlaei_simple" VALUES(33,45,'33:45','يا ايها النبي انا ارسلناك شاهدا ومبشرا ونذيرا');
INSERT INTO "imlaei_simple" VALUES(33,46,'33:46','وداعيا الى الله باذنه وسراجا منيرا');
INSERT INTO "imlaei_simple" VALUES(33,47,'33:47','وبشر المؤمنين بان لهم من الله فضلا كبيرا');
INSERT INTO "imlaei_simple" VALUES(33,48,'33:48','ولا تطع الكافرين والمنافقين ودع اذاهم وتوكل على الله وكفى بالله وكيلا');
INSERT INTO "imlaei_simple" VALUES(33,49,'33:49','يا ايها الذين امنوا اذا نكحتم المؤمنات ثم طلقتموهن من قبل ان تمسوهن فما لكم عليهن من عدة تعتدونها فمتعوهن وسرحوهن سراحا جميلا');
INSERT INTO "imlaei_simple" VALUES(33,50,'33:50','يا ايها النبي انا احللنا لك ازواجك اللاتي اتيت اجورهن وما ملكت يمينك مما افاء الله عليك وبنات عمك وبنات عماتك وبنات خالك وبنات خالاتك اللاتي هاجرن معك وامراة مؤمنة ان وهبت نفسها للنبي ان اراد النبي ان يستنكحها خالصة لك من دون المؤمنين قد علمنا ما فرضنا عليهم في ازواجهم وما ملكت ايمانهم لكيلا يكون عليك حرج وكان الله غفورا رحيما');
INSERT INTO "imlaei_simple" VALUES(33,51,'33:51','ترجي من تشاء منهن وتؤوي اليك من تشاء ومن ابتغيت ممن عزلت فلا جناح عليك ذلك ادنى ان تقر اعينهن ولا يحزن ويرضين بما اتيتهن كلهن والله يعلم ما في قلوبكم وكان الله عليما حليما');
INSERT INTO "imlaei_simple" VALUES(33,52,'33:52','لا يحل لك النساء من بعد ولا ان تبدل بهن من ازواج ولو اعجبك حسنهن الا ما ملكت يمينك وكان الله على كل شيء رقيبا');
INSERT INTO "imlaei_simple" VALUES(33,53,'33:53','يا ايها الذين امنوا لا تدخلوا بيوت النبي الا ان يؤذن لكم الى طعام غير ناظرين اناه ولكن اذا دعيتم فادخلوا فاذا طعمتم فانتشروا ولا مستانسين لحديث ان ذلكم كان يؤذي النبي فيستحيي منكم والله لا يستحيي من الحق واذا سالتموهن متاعا فاسالوهن من وراء حجاب ذلكم اطهر لقلوبكم وقلوبهن وما كان لكم ان تؤذوا رسول الله ولا ان تنكحوا ازواجه من بعده ابدا ان ذلكم كان عند الله عظيما');
INSERT INTO "imlaei_simple" VALUES(33,54,'33:54','ان تبدوا شيئا او تخفوه فان الله كان بكل شيء عليما');
INSERT INTO "imlaei_simple" VALUES(33,55,'33:55','لا جناح عليهن في ابائهن ولا ابنائهن ولا اخوانهن ولا ابناء اخوانهن ولا ابناء اخواتهن ولا نسائهن ولا ما ملكت ايمانهن واتقين الله ان الله كان على كل شيء شهيدا');
INSERT INTO "imlaei_simple" VALUES(33,56,'33:56','ان الله وملائكته يصلون على النبي يا ايها الذين امنوا صلوا عليه وسلموا تسليما');
INSERT INTO "imlaei_simple" VALUES(33,57,'33:57','ان الذين يؤذون الله ورسوله لعنهم الله في الدنيا والاخرة واعد لهم عذابا مهينا');
INSERT INTO "imlaei_simple" VALUES(33,58,'33:58','والذين يؤذون المؤمنين والمؤمنات بغير ما اكتسبوا فقد احتملوا بهتانا واثما مبينا');
INSERT INTO "imlaei_simple" VALUES(33,59,'33:59','يا ايها النبي قل لازواجك وبناتك ونساء المؤمنين يدنين عليهن من جلابيبهن ذلك ادنى ان يعرفن فلا يؤذين وكان الله غفورا رحيما');
INSERT INTO "imlaei_simple" VALUES(33,60,'33:60','لئن لم ينته المنافقون والذين في قلوبهم مرض والمرجفون في المدينة لنغرينك بهم ثم لا يجاورونك فيها الا قليلا');
INSERT INTO "imlaei_simple" VALUES(33,61,'33:61','ملعونين اينما ثقفوا اخذوا وقتلوا تقتيلا');
INSERT INTO "imlaei_simple" VALUES(33,62,'33:62','سنة الله في الذين خلوا من قبل ولن تجد لسنة الله تبديلا');
INSERT INTO "imlaei_simple" VALUES(33,63,'33:63','يسالك الناس عن الساعة قل انما علمها عند الله وما يدريك لعل الساعة تكون قريبا');
INSERT INTO "imlaei_simple" VALUES(33,64,'33:64','ان الله لعن الكافرين واعد لهم سعيرا');
INSERT INTO "imlaei_simple" VALUES(33,65,'33:65','خالدين فيها ابدا لا يجدون وليا ولا نصيرا');
INSERT INTO "imlaei_simple" VALUES(33,66,'33:66','يوم تقلب وجوههم في النار يقولون يا ليتنا اطعنا الله واطعنا الرسولا');
INSERT INTO "imlaei_simple" VALUES(33,67,'33:67','وقالوا ربنا انا اطعنا سادتنا وكبراءنا فاضلونا السبيلا');
INSERT INTO "imlaei_simple" VALUES(33,68,'33:68','ربنا اتهم ضعفين من العذاب والعنهم لعنا كبيرا');
INSERT INTO "imlaei_simple" VALUES(33,69,'33:69','يا ايها الذين امنوا لا تكونوا كالذين اذوا موسى فبراه الله مما قالوا وكان عند الله وجيها');
INSERT INTO "imlaei_simple" VALUES(33,70,'33:70','يا ايها الذين امنوا اتقوا الله وقولوا قولا سديدا');
INSERT INTO "imlaei_simple" VALUES(33,71,'33:71','يصلح لكم اعمالكم ويغفر لكم ذنوبكم ومن يطع الله ورسوله فقد فاز فوزا عظيما');
INSERT INTO "imlaei_simple" VALUES(33,72,'33:72','انا عرضنا الامانة على السماوات والارض والجبال فابين ان يحملنها واشفقن منها وحملها الانسان انه كان ظلوما جهولا');
INSERT INTO "imlaei_simple" VALUES(33,73,'33:73','ليعذب الله المنافقين والمنافقات والمشركين والمشركات ويتوب الله على المؤمنين والمؤمنات وكان الله غفورا رحيما');
INSERT INTO "imlaei_simple" VALUES(34,1,'34:1','الحمد لله الذي له ما في السماوات وما في الارض وله الحمد في الاخرة وهو الحكيم الخبير');
INSERT INTO "imlaei_simple" VALUES(34,2,'34:2','يعلم ما يلج في الارض وما يخرج منها وما ينزل من السماء وما يعرج فيها وهو الرحيم الغفور');
INSERT INTO "imlaei_simple" VALUES(34,3,'34:3','وقال الذين كفروا لا تاتينا الساعة قل بلى وربي لتاتينكم عالم الغيب لا يعزب عنه مثقال ذرة في السماوات ولا في الارض ولا اصغر من ذلك ولا اكبر الا في كتاب مبين');
INSERT INTO "imlaei_simple" VALUES(34,4,'34:4','ليجزي الذين امنوا وعملوا الصالحات اولئك لهم مغفرة ورزق كريم');
INSERT INTO "imlaei_simple" VALUES(34,5,'34:5','والذين سعوا في اياتنا معاجزين اولئك لهم عذاب من رجز اليم');
INSERT INTO "imlaei_simple" VALUES(34,6,'34:6','ويرى الذين اوتوا العلم الذي انزل اليك من ربك هو الحق ويهدي الى صراط العزيز الحميد');
INSERT INTO "imlaei_simple" VALUES(34,7,'34:7','وقال الذين كفروا هل ندلكم على رجل ينبئكم اذا مزقتم كل ممزق انكم لفي خلق جديد');
INSERT INTO "imlaei_simple" VALUES(34,8,'34:8','افترى على الله كذبا ام به جنة بل الذين لا يؤمنون بالاخرة في العذاب والضلال البعيد');
INSERT INTO "imlaei_simple" VALUES(34,9,'34:9','افلم يروا الى ما بين ايديهم وما خلفهم من السماء والارض ان نشا نخسف بهم الارض او نسقط عليهم كسفا من السماء ان في ذلك لاية لكل عبد منيب');
INSERT INTO "imlaei_simple" VALUES(34,10,'34:10','ولقد اتينا داوود منا فضلا يا جبال اوبي معه والطير والنا له الحديد');
INSERT INTO "imlaei_simple" VALUES(34,11,'34:11','ان اعمل سابغات وقدر في السرد واعملوا صالحا اني بما تعملون بصير');
INSERT INTO "imlaei_simple" VALUES(34,12,'34:12','ولسليمان الريح غدوها شهر ورواحها شهر واسلنا له عين القطر ومن الجن من يعمل بين يديه باذن ربه ومن يزغ منهم عن امرنا نذقه من عذاب السعير');
INSERT INTO "imlaei_simple" VALUES(34,13,'34:13','يعملون له ما يشاء من محاريب وتماثيل وجفان كالجواب وقدور راسيات اعملوا ال داوود شكرا وقليل من عبادي الشكور');
INSERT INTO "imlaei_simple" VALUES(34,14,'34:14','فلما قضينا عليه الموت ما دلهم على موته الا دابة الارض تاكل منساته فلما خر تبينت الجن ان لو كانوا يعلمون الغيب ما لبثوا في العذاب المهين');
INSERT INTO "imlaei_simple" VALUES(34,15,'34:15','لقد كان لسبا في مسكنهم اية جنتان عن يمين وشمال كلوا من رزق ربكم واشكروا له بلدة طيبة ورب غفور');
INSERT INTO "imlaei_simple" VALUES(34,16,'34:16','فاعرضوا فارسلنا عليهم سيل العرم وبدلناهم بجنتيهم جنتين ذواتي اكل خمط واثل وشيء من سدر قليل');
INSERT INTO "imlaei_simple" VALUES(34,17,'34:17','ذلك جزيناهم بما كفروا وهل نجازي الا الكفور');
INSERT INTO "imlaei_simple" VALUES(34,18,'34:18','وجعلنا بينهم وبين القرى التي باركنا فيها قرى ظاهرة وقدرنا فيها السير سيروا فيها ليالي واياما امنين');
INSERT INTO "imlaei_simple" VALUES(34,19,'34:19','فقالوا ربنا باعد بين اسفارنا وظلموا انفسهم فجعلناهم احاديث ومزقناهم كل ممزق ان في ذلك لايات لكل صبار شكور');
INSERT INTO "imlaei_simple" VALUES(34,20,'34:20','ولقد صدق عليهم ابليس ظنه فاتبعوه الا فريقا من المؤمنين');
INSERT INTO "imlaei_simple" VALUES(34,21,'34:21','وما كان له عليهم من سلطان الا لنعلم من يؤمن بالاخرة ممن هو منها في شك وربك على كل شيء حفيظ');
INSERT INTO "imlaei_simple" VALUES(34,22,'34:22','قل ادعوا الذين زعمتم من دون الله لا يملكون مثقال ذرة في السماوات ولا في الارض وما لهم فيهما من شرك وما له منهم من ظهير');
INSERT INTO "imlaei_simple" VALUES(34,23,'34:23','ولا تنفع الشفاعة عنده الا لمن اذن له حتى اذا فزع عن قلوبهم قالوا ماذا قال ربكم قالوا الحق وهو العلي الكبير');
INSERT INTO "imlaei_simple" VALUES(34,24,'34:24','قل من يرزقكم من السماوات والارض قل الله وانا او اياكم لعلى هدى او في ضلال مبين');
INSERT INTO "imlaei_simple" VALUES(34,25,'34:25','قل لا تسالون عما اجرمنا ولا نسال عما تعملون');
INSERT INTO "imlaei_simple" VALUES(34,26,'34:26','قل يجمع بيننا ربنا ثم يفتح بيننا بالحق وهو الفتاح العليم');
INSERT INTO "imlaei_simple" VALUES(34,27,'34:27','قل اروني الذين الحقتم به شركاء كلا بل هو الله العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(34,28,'34:28','وما ارسلناك الا كافة للناس بشيرا ونذيرا ولكن اكثر الناس لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(34,29,'34:29','ويقولون متى هذا الوعد ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(34,30,'34:30','قل لكم ميعاد يوم لا تستاخرون عنه ساعة ولا تستقدمون');
INSERT INTO "imlaei_simple" VALUES(34,31,'34:31','وقال الذين كفروا لن نؤمن بهذا القران ولا بالذي بين يديه ولو ترى اذ الظالمون موقوفون عند ربهم يرجع بعضهم الى بعض القول يقول الذين استضعفوا للذين استكبروا لولا انتم لكنا مؤمنين');
INSERT INTO "imlaei_simple" VALUES(34,32,'34:32','قال الذين استكبروا للذين استضعفوا انحن صددناكم عن الهدى بعد اذ جاءكم بل كنتم مجرمين');
INSERT INTO "imlaei_simple" VALUES(34,33,'34:33','وقال الذين استضعفوا للذين استكبروا بل مكر الليل والنهار اذ تامروننا ان نكفر بالله ونجعل له اندادا واسروا الندامة لما راوا العذاب وجعلنا الاغلال في اعناق الذين كفروا هل يجزون الا ما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(34,34,'34:34','وما ارسلنا في قرية من نذير الا قال مترفوها انا بما ارسلتم به كافرون');
INSERT INTO "imlaei_simple" VALUES(34,35,'34:35','وقالوا نحن اكثر اموالا واولادا وما نحن بمعذبين');
INSERT INTO "imlaei_simple" VALUES(34,36,'34:36','قل ان ربي يبسط الرزق لمن يشاء ويقدر ولكن اكثر الناس لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(34,37,'34:37','وما اموالكم ولا اولادكم بالتي تقربكم عندنا زلفى الا من امن وعمل صالحا فاولئك لهم جزاء الضعف بما عملوا وهم في الغرفات امنون');
INSERT INTO "imlaei_simple" VALUES(34,38,'34:38','والذين يسعون في اياتنا معاجزين اولئك في العذاب محضرون');
INSERT INTO "imlaei_simple" VALUES(34,39,'34:39','قل ان ربي يبسط الرزق لمن يشاء من عباده ويقدر له وما انفقتم من شيء فهو يخلفه وهو خير الرازقين');
INSERT INTO "imlaei_simple" VALUES(34,40,'34:40','ويوم يحشرهم جميعا ثم يقول للملائكة اهؤلاء اياكم كانوا يعبدون');
INSERT INTO "imlaei_simple" VALUES(34,41,'34:41','قالوا سبحانك انت ولينا من دونهم بل كانوا يعبدون الجن اكثرهم بهم مؤمنون');
INSERT INTO "imlaei_simple" VALUES(34,42,'34:42','فاليوم لا يملك بعضكم لبعض نفعا ولا ضرا ونقول للذين ظلموا ذوقوا عذاب النار التي كنتم بها تكذبون');
INSERT INTO "imlaei_simple" VALUES(34,43,'34:43','واذا تتلى عليهم اياتنا بينات قالوا ما هذا الا رجل يريد ان يصدكم عما كان يعبد اباؤكم وقالوا ما هذا الا افك مفترى وقال الذين كفروا للحق لما جاءهم ان هذا الا سحر مبين');
INSERT INTO "imlaei_simple" VALUES(34,44,'34:44','وما اتيناهم من كتب يدرسونها وما ارسلنا اليهم قبلك من نذير');
INSERT INTO "imlaei_simple" VALUES(34,45,'34:45','وكذب الذين من قبلهم وما بلغوا معشار ما اتيناهم فكذبوا رسلي فكيف كان نكير');
INSERT INTO "imlaei_simple" VALUES(34,46,'34:46','قل انما اعظكم بواحدة ان تقوموا لله مثنى وفرادى ثم تتفكروا ما بصاحبكم من جنة ان هو الا نذير لكم بين يدي عذاب شديد');
INSERT INTO "imlaei_simple" VALUES(34,47,'34:47','قل ما سالتكم من اجر فهو لكم ان اجري الا على الله وهو على كل شيء شهيد');
INSERT INTO "imlaei_simple" VALUES(34,48,'34:48','قل ان ربي يقذف بالحق علام الغيوب');
INSERT INTO "imlaei_simple" VALUES(34,49,'34:49','قل جاء الحق وما يبدئ الباطل وما يعيد');
INSERT INTO "imlaei_simple" VALUES(34,50,'34:50','قل ان ضللت فانما اضل على نفسي وان اهتديت فبما يوحي الي ربي انه سميع قريب');
INSERT INTO "imlaei_simple" VALUES(34,51,'34:51','ولو ترى اذ فزعوا فلا فوت واخذوا من مكان قريب');
INSERT INTO "imlaei_simple" VALUES(34,52,'34:52','وقالوا امنا به وانى لهم التناوش من مكان بعيد');
INSERT INTO "imlaei_simple" VALUES(34,53,'34:53','وقد كفروا به من قبل ويقذفون بالغيب من مكان بعيد');
INSERT INTO "imlaei_simple" VALUES(34,54,'34:54','وحيل بينهم وبين ما يشتهون كما فعل باشياعهم من قبل انهم كانوا في شك مريب');
INSERT INTO "imlaei_simple" VALUES(35,1,'35:1','الحمد لله فاطر السماوات والارض جاعل الملائكة رسلا اولي اجنحة مثنى وثلاث ورباع يزيد في الخلق ما يشاء ان الله على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(35,2,'35:2','ما يفتح الله للناس من رحمة فلا ممسك لها وما يمسك فلا مرسل له من بعده وهو العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(35,3,'35:3','يا ايها الناس اذكروا نعمت الله عليكم هل من خالق غير الله يرزقكم من السماء والارض لا اله الا هو فانى تؤفكون');
INSERT INTO "imlaei_simple" VALUES(35,4,'35:4','وان يكذبوك فقد كذبت رسل من قبلك والى الله ترجع الامور');
INSERT INTO "imlaei_simple" VALUES(35,5,'35:5','يا ايها الناس ان وعد الله حق فلا تغرنكم الحياة الدنيا ولا يغرنكم بالله الغرور');
INSERT INTO "imlaei_simple" VALUES(35,6,'35:6','ان الشيطان لكم عدو فاتخذوه عدوا انما يدعو حزبه ليكونوا من اصحاب السعير');
INSERT INTO "imlaei_simple" VALUES(35,7,'35:7','الذين كفروا لهم عذاب شديد والذين امنوا وعملوا الصالحات لهم مغفرة واجر كبير');
INSERT INTO "imlaei_simple" VALUES(35,8,'35:8','افمن زين له سوء عمله فراه حسنا فان الله يضل من يشاء ويهدي من يشاء فلا تذهب نفسك عليهم حسرات ان الله عليم بما يصنعون');
INSERT INTO "imlaei_simple" VALUES(35,9,'35:9','والله الذي ارسل الرياح فتثير سحابا فسقناه الى بلد ميت فاحيينا به الارض بعد موتها كذلك النشور');
INSERT INTO "imlaei_simple" VALUES(35,10,'35:10','من كان يريد العزة فلله العزة جميعا اليه يصعد الكلم الطيب والعمل الصالح يرفعه والذين يمكرون السيئات لهم عذاب شديد ومكر اولئك هو يبور');
INSERT INTO "imlaei_simple" VALUES(35,11,'35:11','والله خلقكم من تراب ثم من نطفة ثم جعلكم ازواجا وما تحمل من انثى ولا تضع الا بعلمه وما يعمر من معمر ولا ينقص من عمره الا في كتاب ان ذلك على الله يسير');
INSERT INTO "imlaei_simple" VALUES(35,12,'35:12','وما يستوي البحران هذا عذب فرات سائغ شرابه وهذا ملح اجاج ومن كل تاكلون لحما طريا وتستخرجون حلية تلبسونها وترى الفلك فيه مواخر لتبتغوا من فضله ولعلكم تشكرون');
INSERT INTO "imlaei_simple" VALUES(35,13,'35:13','يولج الليل في النهار ويولج النهار في الليل وسخر الشمس والقمر كل يجري لاجل مسمى ذلكم الله ربكم له الملك والذين تدعون من دونه ما يملكون من قطمير');
INSERT INTO "imlaei_simple" VALUES(35,14,'35:14','ان تدعوهم لا يسمعوا دعاءكم ولو سمعوا ما استجابوا لكم ويوم القيامة يكفرون بشرككم ولا ينبئك مثل خبير');
INSERT INTO "imlaei_simple" VALUES(35,15,'35:15','يا ايها الناس انتم الفقراء الى الله والله هو الغني الحميد');
INSERT INTO "imlaei_simple" VALUES(35,16,'35:16','ان يشا يذهبكم ويات بخلق جديد');
INSERT INTO "imlaei_simple" VALUES(35,17,'35:17','وما ذلك على الله بعزيز');
INSERT INTO "imlaei_simple" VALUES(35,18,'35:18','ولا تزر وازرة وزر اخرى وان تدع مثقلة الى حملها لا يحمل منه شيء ولو كان ذا قربى انما تنذر الذين يخشون ربهم بالغيب واقاموا الصلاة ومن تزكى فانما يتزكى لنفسه والى الله المصير');
INSERT INTO "imlaei_simple" VALUES(35,19,'35:19','وما يستوي الاعمى والبصير');
INSERT INTO "imlaei_simple" VALUES(35,20,'35:20','ولا الظلمات ولا النور');
INSERT INTO "imlaei_simple" VALUES(35,21,'35:21','ولا الظل ولا الحرور');
INSERT INTO "imlaei_simple" VALUES(35,22,'35:22','وما يستوي الاحياء ولا الاموات ان الله يسمع من يشاء وما انت بمسمع من في القبور');
INSERT INTO "imlaei_simple" VALUES(35,23,'35:23','ان انت الا نذير');
INSERT INTO "imlaei_simple" VALUES(35,24,'35:24','انا ارسلناك بالحق بشيرا ونذيرا وان من امة الا خلا فيها نذير');
INSERT INTO "imlaei_simple" VALUES(35,25,'35:25','وان يكذبوك فقد كذب الذين من قبلهم جاءتهم رسلهم بالبينات وبالزبر وبالكتاب المنير');
INSERT INTO "imlaei_simple" VALUES(35,26,'35:26','ثم اخذت الذين كفروا فكيف كان نكير');
INSERT INTO "imlaei_simple" VALUES(35,27,'35:27','الم تر ان الله انزل من السماء ماء فاخرجنا به ثمرات مختلفا الوانها ومن الجبال جدد بيض وحمر مختلف الوانها وغرابيب سود');
INSERT INTO "imlaei_simple" VALUES(35,28,'35:28','ومن الناس والدواب والانعام مختلف الوانه كذلك انما يخشى الله من عباده العلماء ان الله عزيز غفور');
INSERT INTO "imlaei_simple" VALUES(35,29,'35:29','ان الذين يتلون كتاب الله واقاموا الصلاة وانفقوا مما رزقناهم سرا وعلانية يرجون تجارة لن تبور');
INSERT INTO "imlaei_simple" VALUES(35,30,'35:30','ليوفيهم اجورهم ويزيدهم من فضله انه غفور شكور');
INSERT INTO "imlaei_simple" VALUES(35,31,'35:31','والذي اوحينا اليك من الكتاب هو الحق مصدقا لما بين يديه ان الله بعباده لخبير بصير');
INSERT INTO "imlaei_simple" VALUES(35,32,'35:32','ثم اورثنا الكتاب الذين اصطفينا من عبادنا فمنهم ظالم لنفسه ومنهم مقتصد ومنهم سابق بالخيرات باذن الله ذلك هو الفضل الكبير');
INSERT INTO "imlaei_simple" VALUES(35,33,'35:33','جنات عدن يدخلونها يحلون فيها من اساور من ذهب ولؤلؤا ولباسهم فيها حرير');
INSERT INTO "imlaei_simple" VALUES(35,34,'35:34','وقالوا الحمد لله الذي اذهب عنا الحزن ان ربنا لغفور شكور');
INSERT INTO "imlaei_simple" VALUES(35,35,'35:35','الذي احلنا دار المقامة من فضله لا يمسنا فيها نصب ولا يمسنا فيها لغوب');
INSERT INTO "imlaei_simple" VALUES(35,36,'35:36','والذين كفروا لهم نار جهنم لا يقضى عليهم فيموتوا ولا يخفف عنهم من عذابها كذلك نجزي كل كفور');
INSERT INTO "imlaei_simple" VALUES(35,37,'35:37','وهم يصطرخون فيها ربنا اخرجنا نعمل صالحا غير الذي كنا نعمل اولم نعمركم ما يتذكر فيه من تذكر وجاءكم النذير فذوقوا فما للظالمين من نصير');
INSERT INTO "imlaei_simple" VALUES(35,38,'35:38','ان الله عالم غيب السماوات والارض انه عليم بذات الصدور');
INSERT INTO "imlaei_simple" VALUES(35,39,'35:39','هو الذي جعلكم خلائف في الارض فمن كفر فعليه كفره ولا يزيد الكافرين كفرهم عند ربهم الا مقتا ولا يزيد الكافرين كفرهم الا خسارا');
INSERT INTO "imlaei_simple" VALUES(35,40,'35:40','قل ارايتم شركاءكم الذين تدعون من دون الله اروني ماذا خلقوا من الارض ام لهم شرك في السماوات ام اتيناهم كتابا فهم على بينت منه بل ان يعد الظالمون بعضهم بعضا الا غرورا');
INSERT INTO "imlaei_simple" VALUES(35,41,'35:41','ان الله يمسك السماوات والارض ان تزولا ولئن زالتا ان امسكهما من احد من بعده انه كان حليما غفورا');
INSERT INTO "imlaei_simple" VALUES(35,42,'35:42','واقسموا بالله جهد ايمانهم لئن جاءهم نذير ليكونن اهدى من احدى الامم فلما جاءهم نذير ما زادهم الا نفورا');
INSERT INTO "imlaei_simple" VALUES(35,43,'35:43','استكبارا في الارض ومكر السيئ ولا يحيق المكر السيئ الا باهله فهل ينظرون الا سنت الاولين فلن تجد لسنت الله تبديلا ولن تجد لسنت الله تحويلا');
INSERT INTO "imlaei_simple" VALUES(35,44,'35:44','اولم يسيروا في الارض فينظروا كيف كان عاقبة الذين من قبلهم وكانوا اشد منهم قوة وما كان الله ليعجزه من شيء في السماوات ولا في الارض انه كان عليما قديرا');
INSERT INTO "imlaei_simple" VALUES(35,45,'35:45','ولو يؤاخذ الله الناس بما كسبوا ما ترك على ظهرها من دابة ولكن يؤخرهم الى اجل مسمى فاذا جاء اجلهم فان الله كان بعباده بصيرا');
INSERT INTO "imlaei_simple" VALUES(36,1,'36:1','يس');
INSERT INTO "imlaei_simple" VALUES(36,2,'36:2','والقران الحكيم');
INSERT INTO "imlaei_simple" VALUES(36,3,'36:3','انك لمن المرسلين');
INSERT INTO "imlaei_simple" VALUES(36,4,'36:4','على صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(36,5,'36:5','تنزيل العزيز الرحيم');
INSERT INTO "imlaei_simple" VALUES(36,6,'36:6','لتنذر قوما ما انذر اباؤهم فهم غافلون');
INSERT INTO "imlaei_simple" VALUES(36,7,'36:7','لقد حق القول على اكثرهم فهم لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(36,8,'36:8','انا جعلنا في اعناقهم اغلالا فهي الى الاذقان فهم مقمحون');
INSERT INTO "imlaei_simple" VALUES(36,9,'36:9','وجعلنا من بين ايديهم سدا ومن خلفهم سدا فاغشيناهم فهم لا يبصرون');
INSERT INTO "imlaei_simple" VALUES(36,10,'36:10','وسواء عليهم اانذرتهم ام لم تنذرهم لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(36,11,'36:11','انما تنذر من اتبع الذكر وخشي الرحمن بالغيب فبشره بمغفرة واجر كريم');
INSERT INTO "imlaei_simple" VALUES(36,12,'36:12','انا نحن نحيي الموتى ونكتب ما قدموا واثارهم وكل شيء احصيناه في امام مبين');
INSERT INTO "imlaei_simple" VALUES(36,13,'36:13','واضرب لهم مثلا اصحاب القرية اذ جاءها المرسلون');
INSERT INTO "imlaei_simple" VALUES(36,14,'36:14','اذ ارسلنا اليهم اثنين فكذبوهما فعززنا بثالث فقالوا انا اليكم مرسلون');
INSERT INTO "imlaei_simple" VALUES(36,15,'36:15','قالوا ما انتم الا بشر مثلنا وما انزل الرحمن من شيء ان انتم الا تكذبون');
INSERT INTO "imlaei_simple" VALUES(36,16,'36:16','قالوا ربنا يعلم انا اليكم لمرسلون');
INSERT INTO "imlaei_simple" VALUES(36,17,'36:17','وما علينا الا البلاغ المبين');
INSERT INTO "imlaei_simple" VALUES(36,18,'36:18','قالوا انا تطيرنا بكم لئن لم تنتهوا لنرجمنكم وليمسنكم منا عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(36,19,'36:19','قالوا طائركم معكم ائن ذكرتم بل انتم قوم مسرفون');
INSERT INTO "imlaei_simple" VALUES(36,20,'36:20','وجاء من اقصى المدينة رجل يسعى قال يا قوم اتبعوا المرسلين');
INSERT INTO "imlaei_simple" VALUES(36,21,'36:21','اتبعوا من لا يسالكم اجرا وهم مهتدون');
INSERT INTO "imlaei_simple" VALUES(36,22,'36:22','وما لي لا اعبد الذي فطرني واليه ترجعون');
INSERT INTO "imlaei_simple" VALUES(36,23,'36:23','ااتخذ من دونه الهة ان يردن الرحمن بضر لا تغن عني شفاعتهم شيئا ولا ينقذون');
INSERT INTO "imlaei_simple" VALUES(36,24,'36:24','اني اذا لفي ضلال مبين');
INSERT INTO "imlaei_simple" VALUES(36,25,'36:25','اني امنت بربكم فاسمعون');
INSERT INTO "imlaei_simple" VALUES(36,26,'36:26','قيل ادخل الجنة قال يا ليت قومي يعلمون');
INSERT INTO "imlaei_simple" VALUES(36,27,'36:27','بما غفر لي ربي وجعلني من المكرمين');
INSERT INTO "imlaei_simple" VALUES(36,28,'36:28','وما انزلنا على قومه من بعده من جند من السماء وما كنا منزلين');
INSERT INTO "imlaei_simple" VALUES(36,29,'36:29','ان كانت الا صيحة واحدة فاذا هم خامدون');
INSERT INTO "imlaei_simple" VALUES(36,30,'36:30','يا حسرة على العباد ما ياتيهم من رسول الا كانوا به يستهزئون');
INSERT INTO "imlaei_simple" VALUES(36,31,'36:31','الم يروا كم اهلكنا قبلهم من القرون انهم اليهم لا يرجعون');
INSERT INTO "imlaei_simple" VALUES(36,32,'36:32','وان كل لما جميع لدينا محضرون');
INSERT INTO "imlaei_simple" VALUES(36,33,'36:33','واية لهم الارض الميتة احييناها واخرجنا منها حبا فمنه ياكلون');
INSERT INTO "imlaei_simple" VALUES(36,34,'36:34','وجعلنا فيها جنات من نخيل واعناب وفجرنا فيها من العيون');
INSERT INTO "imlaei_simple" VALUES(36,35,'36:35','لياكلوا من ثمره وما عملته ايديهم افلا يشكرون');
INSERT INTO "imlaei_simple" VALUES(36,36,'36:36','سبحان الذي خلق الازواج كلها مما تنبت الارض ومن انفسهم ومما لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(36,37,'36:37','واية لهم الليل نسلخ منه النهار فاذا هم مظلمون');
INSERT INTO "imlaei_simple" VALUES(36,38,'36:38','والشمس تجري لمستقر لها ذلك تقدير العزيز العليم');
INSERT INTO "imlaei_simple" VALUES(36,39,'36:39','والقمر قدرناه منازل حتى عاد كالعرجون القديم');
INSERT INTO "imlaei_simple" VALUES(36,40,'36:40','لا الشمس ينبغي لها ان تدرك القمر ولا الليل سابق النهار وكل في فلك يسبحون');
INSERT INTO "imlaei_simple" VALUES(36,41,'36:41','واية لهم انا حملنا ذريتهم في الفلك المشحون');
INSERT INTO "imlaei_simple" VALUES(36,42,'36:42','وخلقنا لهم من مثله ما يركبون');
INSERT INTO "imlaei_simple" VALUES(36,43,'36:43','وان نشا نغرقهم فلا صريخ لهم ولا هم ينقذون');
INSERT INTO "imlaei_simple" VALUES(36,44,'36:44','الا رحمة منا ومتاعا الى حين');
INSERT INTO "imlaei_simple" VALUES(36,45,'36:45','واذا قيل لهم اتقوا ما بين ايديكم وما خلفكم لعلكم ترحمون');
INSERT INTO "imlaei_simple" VALUES(36,46,'36:46','وما تاتيهم من اية من ايات ربهم الا كانوا عنها معرضين');
INSERT INTO "imlaei_simple" VALUES(36,47,'36:47','واذا قيل لهم انفقوا مما رزقكم الله قال الذين كفروا للذين امنوا انطعم من لو يشاء الله اطعمه ان انتم الا في ضلال مبين');
INSERT INTO "imlaei_simple" VALUES(36,48,'36:48','ويقولون متى هذا الوعد ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(36,49,'36:49','ما ينظرون الا صيحة واحدة تاخذهم وهم يخصمون');
INSERT INTO "imlaei_simple" VALUES(36,50,'36:50','فلا يستطيعون توصية ولا الى اهلهم يرجعون');
INSERT INTO "imlaei_simple" VALUES(36,51,'36:51','ونفخ في الصور فاذا هم من الاجداث الى ربهم ينسلون');
INSERT INTO "imlaei_simple" VALUES(36,52,'36:52','قالوا يا ويلنا من بعثنا من مرقدنا هذا ما وعد الرحمن وصدق المرسلون');
INSERT INTO "imlaei_simple" VALUES(36,53,'36:53','ان كانت الا صيحة واحدة فاذا هم جميع لدينا محضرون');
INSERT INTO "imlaei_simple" VALUES(36,54,'36:54','فاليوم لا تظلم نفس شيئا ولا تجزون الا ما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(36,55,'36:55','ان اصحاب الجنة اليوم في شغل فاكهون');
INSERT INTO "imlaei_simple" VALUES(36,56,'36:56','هم وازواجهم في ظلال على الارائك متكئون');
INSERT INTO "imlaei_simple" VALUES(36,57,'36:57','لهم فيها فاكهة ولهم ما يدعون');
INSERT INTO "imlaei_simple" VALUES(36,58,'36:58','سلام قولا من رب رحيم');
INSERT INTO "imlaei_simple" VALUES(36,59,'36:59','وامتازوا اليوم ايها المجرمون');
INSERT INTO "imlaei_simple" VALUES(36,60,'36:60','الم اعهد اليكم يا بني ادم ان لا تعبدوا الشيطان انه لكم عدو مبين');
INSERT INTO "imlaei_simple" VALUES(36,61,'36:61','وان اعبدوني هذا صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(36,62,'36:62','ولقد اضل منكم جبلا كثيرا افلم تكونوا تعقلون');
INSERT INTO "imlaei_simple" VALUES(36,63,'36:63','هذه جهنم التي كنتم توعدون');
INSERT INTO "imlaei_simple" VALUES(36,64,'36:64','اصلوها اليوم بما كنتم تكفرون');
INSERT INTO "imlaei_simple" VALUES(36,65,'36:65','اليوم نختم على افواههم وتكلمنا ايديهم وتشهد ارجلهم بما كانوا يكسبون');
INSERT INTO "imlaei_simple" VALUES(36,66,'36:66','ولو نشاء لطمسنا على اعينهم فاستبقوا الصراط فانى يبصرون');
INSERT INTO "imlaei_simple" VALUES(36,67,'36:67','ولو نشاء لمسخناهم على مكانتهم فما استطاعوا مضيا ولا يرجعون');
INSERT INTO "imlaei_simple" VALUES(36,68,'36:68','ومن نعمره ننكسه في الخلق افلا يعقلون');
INSERT INTO "imlaei_simple" VALUES(36,69,'36:69','وما علمناه الشعر وما ينبغي له ان هو الا ذكر وقران مبين');
INSERT INTO "imlaei_simple" VALUES(36,70,'36:70','لينذر من كان حيا ويحق القول على الكافرين');
INSERT INTO "imlaei_simple" VALUES(36,71,'36:71','اولم يروا انا خلقنا لهم مما عملت ايدينا انعاما فهم لها مالكون');
INSERT INTO "imlaei_simple" VALUES(36,72,'36:72','وذللناها لهم فمنها ركوبهم ومنها ياكلون');
INSERT INTO "imlaei_simple" VALUES(36,73,'36:73','ولهم فيها منافع ومشارب افلا يشكرون');
INSERT INTO "imlaei_simple" VALUES(36,74,'36:74','واتخذوا من دون الله الهة لعلهم ينصرون');
INSERT INTO "imlaei_simple" VALUES(36,75,'36:75','لا يستطيعون نصرهم وهم لهم جند محضرون');
INSERT INTO "imlaei_simple" VALUES(36,76,'36:76','فلا يحزنك قولهم انا نعلم ما يسرون وما يعلنون');
INSERT INTO "imlaei_simple" VALUES(36,77,'36:77','اولم ير الانسان انا خلقناه من نطفة فاذا هو خصيم مبين');
INSERT INTO "imlaei_simple" VALUES(36,78,'36:78','وضرب لنا مثلا ونسي خلقه قال من يحيي العظام وهي رميم');
INSERT INTO "imlaei_simple" VALUES(36,79,'36:79','قل يحييها الذي انشاها اول مرة وهو بكل خلق عليم');
INSERT INTO "imlaei_simple" VALUES(36,80,'36:80','الذي جعل لكم من الشجر الاخضر نارا فاذا انتم منه توقدون');
INSERT INTO "imlaei_simple" VALUES(36,81,'36:81','اوليس الذي خلق السماوات والارض بقادر على ان يخلق مثلهم بلى وهو الخلاق العليم');
INSERT INTO "imlaei_simple" VALUES(36,82,'36:82','انما امره اذا اراد شيئا ان يقول له كن فيكون');
INSERT INTO "imlaei_simple" VALUES(36,83,'36:83','فسبحان الذي بيده ملكوت كل شيء واليه ترجعون');
INSERT INTO "imlaei_simple" VALUES(37,1,'37:1','والصافات صفا');
INSERT INTO "imlaei_simple" VALUES(37,2,'37:2','فالزاجرات زجرا');
INSERT INTO "imlaei_simple" VALUES(37,3,'37:3','فالتاليات ذكرا');
INSERT INTO "imlaei_simple" VALUES(37,4,'37:4','ان الهكم لواحد');
INSERT INTO "imlaei_simple" VALUES(37,5,'37:5','رب السماوات والارض وما بينهما ورب المشارق');
INSERT INTO "imlaei_simple" VALUES(37,6,'37:6','انا زينا السماء الدنيا بزينة الكواكب');
INSERT INTO "imlaei_simple" VALUES(37,7,'37:7','وحفظا من كل شيطان مارد');
INSERT INTO "imlaei_simple" VALUES(37,8,'37:8','لا يسمعون الى الملا الاعلى ويقذفون من كل جانب');
INSERT INTO "imlaei_simple" VALUES(37,9,'37:9','دحورا ولهم عذاب واصب');
INSERT INTO "imlaei_simple" VALUES(37,10,'37:10','الا من خطف الخطفة فاتبعه شهاب ثاقب');
INSERT INTO "imlaei_simple" VALUES(37,11,'37:11','فاستفتهم اهم اشد خلقا ام من خلقنا انا خلقناهم من طين لازب');
INSERT INTO "imlaei_simple" VALUES(37,12,'37:12','بل عجبت ويسخرون');
INSERT INTO "imlaei_simple" VALUES(37,13,'37:13','واذا ذكروا لا يذكرون');
INSERT INTO "imlaei_simple" VALUES(37,14,'37:14','واذا راوا اية يستسخرون');
INSERT INTO "imlaei_simple" VALUES(37,15,'37:15','وقالوا ان هذا الا سحر مبين');
INSERT INTO "imlaei_simple" VALUES(37,16,'37:16','ااذا متنا وكنا ترابا وعظاما اانا لمبعوثون');
INSERT INTO "imlaei_simple" VALUES(37,17,'37:17','اواباؤنا الاولون');
INSERT INTO "imlaei_simple" VALUES(37,18,'37:18','قل نعم وانتم داخرون');
INSERT INTO "imlaei_simple" VALUES(37,19,'37:19','فانما هي زجرة واحدة فاذا هم ينظرون');
INSERT INTO "imlaei_simple" VALUES(37,20,'37:20','وقالوا يا ويلنا هذا يوم الدين');
INSERT INTO "imlaei_simple" VALUES(37,21,'37:21','هذا يوم الفصل الذي كنتم به تكذبون');
INSERT INTO "imlaei_simple" VALUES(37,22,'37:22','احشروا الذين ظلموا وازواجهم وما كانوا يعبدون');
INSERT INTO "imlaei_simple" VALUES(37,23,'37:23','من دون الله فاهدوهم الى صراط الجحيم');
INSERT INTO "imlaei_simple" VALUES(37,24,'37:24','وقفوهم انهم مسئولون');
INSERT INTO "imlaei_simple" VALUES(37,25,'37:25','ما لكم لا تناصرون');
INSERT INTO "imlaei_simple" VALUES(37,26,'37:26','بل هم اليوم مستسلمون');
INSERT INTO "imlaei_simple" VALUES(37,27,'37:27','واقبل بعضهم على بعض يتساءلون');
INSERT INTO "imlaei_simple" VALUES(37,28,'37:28','قالوا انكم كنتم تاتوننا عن اليمين');
INSERT INTO "imlaei_simple" VALUES(37,29,'37:29','قالوا بل لم تكونوا مؤمنين');
INSERT INTO "imlaei_simple" VALUES(37,30,'37:30','وما كان لنا عليكم من سلطان بل كنتم قوما طاغين');
INSERT INTO "imlaei_simple" VALUES(37,31,'37:31','فحق علينا قول ربنا انا لذائقون');
INSERT INTO "imlaei_simple" VALUES(37,32,'37:32','فاغويناكم انا كنا غاوين');
INSERT INTO "imlaei_simple" VALUES(37,33,'37:33','فانهم يومئذ في العذاب مشتركون');
INSERT INTO "imlaei_simple" VALUES(37,34,'37:34','انا كذلك نفعل بالمجرمين');
INSERT INTO "imlaei_simple" VALUES(37,35,'37:35','انهم كانوا اذا قيل لهم لا اله الا الله يستكبرون');
INSERT INTO "imlaei_simple" VALUES(37,36,'37:36','ويقولون ائنا لتاركو الهتنا لشاعر مجنون');
INSERT INTO "imlaei_simple" VALUES(37,37,'37:37','بل جاء بالحق وصدق المرسلين');
INSERT INTO "imlaei_simple" VALUES(37,38,'37:38','انكم لذائقو العذاب الاليم');
INSERT INTO "imlaei_simple" VALUES(37,39,'37:39','وما تجزون الا ما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(37,40,'37:40','الا عباد الله المخلصين');
INSERT INTO "imlaei_simple" VALUES(37,41,'37:41','اولئك لهم رزق معلوم');
INSERT INTO "imlaei_simple" VALUES(37,42,'37:42','فواكه وهم مكرمون');
INSERT INTO "imlaei_simple" VALUES(37,43,'37:43','في جنات النعيم');
INSERT INTO "imlaei_simple" VALUES(37,44,'37:44','على سرر متقابلين');
INSERT INTO "imlaei_simple" VALUES(37,45,'37:45','يطاف عليهم بكاس من معين');
INSERT INTO "imlaei_simple" VALUES(37,46,'37:46','بيضاء لذة للشاربين');
INSERT INTO "imlaei_simple" VALUES(37,47,'37:47','لا فيها غول ولا هم عنها ينزفون');
INSERT INTO "imlaei_simple" VALUES(37,48,'37:48','وعندهم قاصرات الطرف عين');
INSERT INTO "imlaei_simple" VALUES(37,49,'37:49','كانهن بيض مكنون');
INSERT INTO "imlaei_simple" VALUES(37,50,'37:50','فاقبل بعضهم على بعض يتساءلون');
INSERT INTO "imlaei_simple" VALUES(37,51,'37:51','قال قائل منهم اني كان لي قرين');
INSERT INTO "imlaei_simple" VALUES(37,52,'37:52','يقول اانك لمن المصدقين');
INSERT INTO "imlaei_simple" VALUES(37,53,'37:53','ااذا متنا وكنا ترابا وعظاما اانا لمدينون');
INSERT INTO "imlaei_simple" VALUES(37,54,'37:54','قال هل انتم مطلعون');
INSERT INTO "imlaei_simple" VALUES(37,55,'37:55','فاطلع فراه في سواء الجحيم');
INSERT INTO "imlaei_simple" VALUES(37,56,'37:56','قال تالله ان كدت لتردين');
INSERT INTO "imlaei_simple" VALUES(37,57,'37:57','ولولا نعمة ربي لكنت من المحضرين');
INSERT INTO "imlaei_simple" VALUES(37,58,'37:58','افما نحن بميتين');
INSERT INTO "imlaei_simple" VALUES(37,59,'37:59','الا موتتنا الاولى وما نحن بمعذبين');
INSERT INTO "imlaei_simple" VALUES(37,60,'37:60','ان هذا لهو الفوز العظيم');
INSERT INTO "imlaei_simple" VALUES(37,61,'37:61','لمثل هذا فليعمل العاملون');
INSERT INTO "imlaei_simple" VALUES(37,62,'37:62','اذلك خير نزلا ام شجرة الزقوم');
INSERT INTO "imlaei_simple" VALUES(37,63,'37:63','انا جعلناها فتنة للظالمين');
INSERT INTO "imlaei_simple" VALUES(37,64,'37:64','انها شجرة تخرج في اصل الجحيم');
INSERT INTO "imlaei_simple" VALUES(37,65,'37:65','طلعها كانه رءوس الشياطين');
INSERT INTO "imlaei_simple" VALUES(37,66,'37:66','فانهم لاكلون منها فمالئون منها البطون');
INSERT INTO "imlaei_simple" VALUES(37,67,'37:67','ثم ان لهم عليها لشوبا من حميم');
INSERT INTO "imlaei_simple" VALUES(37,68,'37:68','ثم ان مرجعهم لالى الجحيم');
INSERT INTO "imlaei_simple" VALUES(37,69,'37:69','انهم الفوا اباءهم ضالين');
INSERT INTO "imlaei_simple" VALUES(37,70,'37:70','فهم على اثارهم يهرعون');
INSERT INTO "imlaei_simple" VALUES(37,71,'37:71','ولقد ضل قبلهم اكثر الاولين');
INSERT INTO "imlaei_simple" VALUES(37,72,'37:72','ولقد ارسلنا فيهم منذرين');
INSERT INTO "imlaei_simple" VALUES(37,73,'37:73','فانظر كيف كان عاقبة المنذرين');
INSERT INTO "imlaei_simple" VALUES(37,74,'37:74','الا عباد الله المخلصين');
INSERT INTO "imlaei_simple" VALUES(37,75,'37:75','ولقد نادانا نوح فلنعم المجيبون');
INSERT INTO "imlaei_simple" VALUES(37,76,'37:76','ونجيناه واهله من الكرب العظيم');
INSERT INTO "imlaei_simple" VALUES(37,77,'37:77','وجعلنا ذريته هم الباقين');
INSERT INTO "imlaei_simple" VALUES(37,78,'37:78','وتركنا عليه في الاخرين');
INSERT INTO "imlaei_simple" VALUES(37,79,'37:79','سلام على نوح في العالمين');
INSERT INTO "imlaei_simple" VALUES(37,80,'37:80','انا كذلك نجزي المحسنين');
INSERT INTO "imlaei_simple" VALUES(37,81,'37:81','انه من عبادنا المؤمنين');
INSERT INTO "imlaei_simple" VALUES(37,82,'37:82','ثم اغرقنا الاخرين');
INSERT INTO "imlaei_simple" VALUES(37,83,'37:83','وان من شيعته لابراهيم');
INSERT INTO "imlaei_simple" VALUES(37,84,'37:84','اذ جاء ربه بقلب سليم');
INSERT INTO "imlaei_simple" VALUES(37,85,'37:85','اذ قال لابيه وقومه ماذا تعبدون');
INSERT INTO "imlaei_simple" VALUES(37,86,'37:86','ائفكا الهة دون الله تريدون');
INSERT INTO "imlaei_simple" VALUES(37,87,'37:87','فما ظنكم برب العالمين');
INSERT INTO "imlaei_simple" VALUES(37,88,'37:88','فنظر نظرة في النجوم');
INSERT INTO "imlaei_simple" VALUES(37,89,'37:89','فقال اني سقيم');
INSERT INTO "imlaei_simple" VALUES(37,90,'37:90','فتولوا عنه مدبرين');
INSERT INTO "imlaei_simple" VALUES(37,91,'37:91','فراغ الى الهتهم فقال الا تاكلون');
INSERT INTO "imlaei_simple" VALUES(37,92,'37:92','ما لكم لا تنطقون');
INSERT INTO "imlaei_simple" VALUES(37,93,'37:93','فراغ عليهم ضربا باليمين');
INSERT INTO "imlaei_simple" VALUES(37,94,'37:94','فاقبلوا اليه يزفون');
INSERT INTO "imlaei_simple" VALUES(37,95,'37:95','قال اتعبدون ما تنحتون');
INSERT INTO "imlaei_simple" VALUES(37,96,'37:96','والله خلقكم وما تعملون');
INSERT INTO "imlaei_simple" VALUES(37,97,'37:97','قالوا ابنوا له بنيانا فالقوه في الجحيم');
INSERT INTO "imlaei_simple" VALUES(37,98,'37:98','فارادوا به كيدا فجعلناهم الاسفلين');
INSERT INTO "imlaei_simple" VALUES(37,99,'37:99','وقال اني ذاهب الى ربي سيهدين');
INSERT INTO "imlaei_simple" VALUES(37,100,'37:100','رب هب لي من الصالحين');
INSERT INTO "imlaei_simple" VALUES(37,101,'37:101','فبشرناه بغلام حليم');
INSERT INTO "imlaei_simple" VALUES(37,102,'37:102','فلما بلغ معه السعي قال يا بني اني ارى في المنام اني اذبحك فانظر ماذا ترى قال يا ابت افعل ما تؤمر ستجدني ان شاء الله من الصابرين');
INSERT INTO "imlaei_simple" VALUES(37,103,'37:103','فلما اسلما وتله للجبين');
INSERT INTO "imlaei_simple" VALUES(37,104,'37:104','وناديناه ان يا ابراهيم');
INSERT INTO "imlaei_simple" VALUES(37,105,'37:105','قد صدقت الرؤيا انا كذلك نجزي المحسنين');
INSERT INTO "imlaei_simple" VALUES(37,106,'37:106','ان هذا لهو البلاء المبين');
INSERT INTO "imlaei_simple" VALUES(37,107,'37:107','وفديناه بذبح عظيم');
INSERT INTO "imlaei_simple" VALUES(37,108,'37:108','وتركنا عليه في الاخرين');
INSERT INTO "imlaei_simple" VALUES(37,109,'37:109','سلام على ابراهيم');
INSERT INTO "imlaei_simple" VALUES(37,110,'37:110','كذلك نجزي المحسنين');
INSERT INTO "imlaei_simple" VALUES(37,111,'37:111','انه من عبادنا المؤمنين');
INSERT INTO "imlaei_simple" VALUES(37,112,'37:112','وبشرناه باسحاق نبيا من الصالحين');
INSERT INTO "imlaei_simple" VALUES(37,113,'37:113','وباركنا عليه وعلى اسحاق ومن ذريتهما محسن وظالم لنفسه مبين');
INSERT INTO "imlaei_simple" VALUES(37,114,'37:114','ولقد مننا على موسى وهارون');
INSERT INTO "imlaei_simple" VALUES(37,115,'37:115','ونجيناهما وقومهما من الكرب العظيم');
INSERT INTO "imlaei_simple" VALUES(37,116,'37:116','ونصرناهم فكانوا هم الغالبين');
INSERT INTO "imlaei_simple" VALUES(37,117,'37:117','واتيناهما الكتاب المستبين');
INSERT INTO "imlaei_simple" VALUES(37,118,'37:118','وهديناهما الصراط المستقيم');
INSERT INTO "imlaei_simple" VALUES(37,119,'37:119','وتركنا عليهما في الاخرين');
INSERT INTO "imlaei_simple" VALUES(37,120,'37:120','سلام على موسى وهارون');
INSERT INTO "imlaei_simple" VALUES(37,121,'37:121','انا كذلك نجزي المحسنين');
INSERT INTO "imlaei_simple" VALUES(37,122,'37:122','انهما من عبادنا المؤمنين');
INSERT INTO "imlaei_simple" VALUES(37,123,'37:123','وان الياس لمن المرسلين');
INSERT INTO "imlaei_simple" VALUES(37,124,'37:124','اذ قال لقومه الا تتقون');
INSERT INTO "imlaei_simple" VALUES(37,125,'37:125','اتدعون بعلا وتذرون احسن الخالقين');
INSERT INTO "imlaei_simple" VALUES(37,126,'37:126','الله ربكم ورب ابائكم الاولين');
INSERT INTO "imlaei_simple" VALUES(37,127,'37:127','فكذبوه فانهم لمحضرون');
INSERT INTO "imlaei_simple" VALUES(37,128,'37:128','الا عباد الله المخلصين');
INSERT INTO "imlaei_simple" VALUES(37,129,'37:129','وتركنا عليه في الاخرين');
INSERT INTO "imlaei_simple" VALUES(37,130,'37:130','سلام على ال ياسين');
INSERT INTO "imlaei_simple" VALUES(37,131,'37:131','انا كذلك نجزي المحسنين');
INSERT INTO "imlaei_simple" VALUES(37,132,'37:132','انه من عبادنا المؤمنين');
INSERT INTO "imlaei_simple" VALUES(37,133,'37:133','وان لوطا لمن المرسلين');
INSERT INTO "imlaei_simple" VALUES(37,134,'37:134','اذ نجيناه واهله اجمعين');
INSERT INTO "imlaei_simple" VALUES(37,135,'37:135','الا عجوزا في الغابرين');
INSERT INTO "imlaei_simple" VALUES(37,136,'37:136','ثم دمرنا الاخرين');
INSERT INTO "imlaei_simple" VALUES(37,137,'37:137','وانكم لتمرون عليهم مصبحين');
INSERT INTO "imlaei_simple" VALUES(37,138,'37:138','وبالليل افلا تعقلون');
INSERT INTO "imlaei_simple" VALUES(37,139,'37:139','وان يونس لمن المرسلين');
INSERT INTO "imlaei_simple" VALUES(37,140,'37:140','اذ ابق الى الفلك المشحون');
INSERT INTO "imlaei_simple" VALUES(37,141,'37:141','فساهم فكان من المدحضين');
INSERT INTO "imlaei_simple" VALUES(37,142,'37:142','فالتقمه الحوت وهو مليم');
INSERT INTO "imlaei_simple" VALUES(37,143,'37:143','فلولا انه كان من المسبحين');
INSERT INTO "imlaei_simple" VALUES(37,144,'37:144','للبث في بطنه الى يوم يبعثون');
INSERT INTO "imlaei_simple" VALUES(37,145,'37:145','فنبذناه بالعراء وهو سقيم');
INSERT INTO "imlaei_simple" VALUES(37,146,'37:146','وانبتنا عليه شجرة من يقطين');
INSERT INTO "imlaei_simple" VALUES(37,147,'37:147','وارسلناه الى مائة الف او يزيدون');
INSERT INTO "imlaei_simple" VALUES(37,148,'37:148','فامنوا فمتعناهم الى حين');
INSERT INTO "imlaei_simple" VALUES(37,149,'37:149','فاستفتهم الربك البنات ولهم البنون');
INSERT INTO "imlaei_simple" VALUES(37,150,'37:150','ام خلقنا الملائكة اناثا وهم شاهدون');
INSERT INTO "imlaei_simple" VALUES(37,151,'37:151','الا انهم من افكهم ليقولون');
INSERT INTO "imlaei_simple" VALUES(37,152,'37:152','ولد الله وانهم لكاذبون');
INSERT INTO "imlaei_simple" VALUES(37,153,'37:153','اصطفى البنات على البنين');
INSERT INTO "imlaei_simple" VALUES(37,154,'37:154','ما لكم كيف تحكمون');
INSERT INTO "imlaei_simple" VALUES(37,155,'37:155','افلا تذكرون');
INSERT INTO "imlaei_simple" VALUES(37,156,'37:156','ام لكم سلطان مبين');
INSERT INTO "imlaei_simple" VALUES(37,157,'37:157','فاتوا بكتابكم ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(37,158,'37:158','وجعلوا بينه وبين الجنة نسبا ولقد علمت الجنة انهم لمحضرون');
INSERT INTO "imlaei_simple" VALUES(37,159,'37:159','سبحان الله عما يصفون');
INSERT INTO "imlaei_simple" VALUES(37,160,'37:160','الا عباد الله المخلصين');
INSERT INTO "imlaei_simple" VALUES(37,161,'37:161','فانكم وما تعبدون');
INSERT INTO "imlaei_simple" VALUES(37,162,'37:162','ما انتم عليه بفاتنين');
INSERT INTO "imlaei_simple" VALUES(37,163,'37:163','الا من هو صال الجحيم');
INSERT INTO "imlaei_simple" VALUES(37,164,'37:164','وما منا الا له مقام معلوم');
INSERT INTO "imlaei_simple" VALUES(37,165,'37:165','وانا لنحن الصافون');
INSERT INTO "imlaei_simple" VALUES(37,166,'37:166','وانا لنحن المسبحون');
INSERT INTO "imlaei_simple" VALUES(37,167,'37:167','وان كانوا ليقولون');
INSERT INTO "imlaei_simple" VALUES(37,168,'37:168','لو ان عندنا ذكرا من الاولين');
INSERT INTO "imlaei_simple" VALUES(37,169,'37:169','لكنا عباد الله المخلصين');
INSERT INTO "imlaei_simple" VALUES(37,170,'37:170','فكفروا به فسوف يعلمون');
INSERT INTO "imlaei_simple" VALUES(37,171,'37:171','ولقد سبقت كلمتنا لعبادنا المرسلين');
INSERT INTO "imlaei_simple" VALUES(37,172,'37:172','انهم لهم المنصورون');
INSERT INTO "imlaei_simple" VALUES(37,173,'37:173','وان جندنا لهم الغالبون');
INSERT INTO "imlaei_simple" VALUES(37,174,'37:174','فتول عنهم حتى حين');
INSERT INTO "imlaei_simple" VALUES(37,175,'37:175','وابصرهم فسوف يبصرون');
INSERT INTO "imlaei_simple" VALUES(37,176,'37:176','افبعذابنا يستعجلون');
INSERT INTO "imlaei_simple" VALUES(37,177,'37:177','فاذا نزل بساحتهم فساء صباح المنذرين');
INSERT INTO "imlaei_simple" VALUES(37,178,'37:178','وتول عنهم حتى حين');
INSERT INTO "imlaei_simple" VALUES(37,179,'37:179','وابصر فسوف يبصرون');
INSERT INTO "imlaei_simple" VALUES(37,180,'37:180','سبحان ربك رب العزة عما يصفون');
INSERT INTO "imlaei_simple" VALUES(37,181,'37:181','وسلام على المرسلين');
INSERT INTO "imlaei_simple" VALUES(37,182,'37:182','والحمد لله رب العالمين');
INSERT INTO "imlaei_simple" VALUES(38,1,'38:1','ص والقران ذي الذكر');
INSERT INTO "imlaei_simple" VALUES(38,2,'38:2','بل الذين كفروا في عزة وشقاق');
INSERT INTO "imlaei_simple" VALUES(38,3,'38:3','كم اهلكنا من قبلهم من قرن فنادوا ولات حين مناص');
INSERT INTO "imlaei_simple" VALUES(38,4,'38:4','وعجبوا ان جاءهم منذر منهم وقال الكافرون هذا ساحر كذاب');
INSERT INTO "imlaei_simple" VALUES(38,5,'38:5','اجعل الالهة الها واحدا ان هذا لشيء عجاب');
INSERT INTO "imlaei_simple" VALUES(38,6,'38:6','وانطلق الملا منهم ان امشوا واصبروا على الهتكم ان هذا لشيء يراد');
INSERT INTO "imlaei_simple" VALUES(38,7,'38:7','ما سمعنا بهذا في الملة الاخرة ان هذا الا اختلاق');
INSERT INTO "imlaei_simple" VALUES(38,8,'38:8','اانزل عليه الذكر من بيننا بل هم في شك من ذكري بل لما يذوقوا عذاب');
INSERT INTO "imlaei_simple" VALUES(38,9,'38:9','ام عندهم خزائن رحمة ربك العزيز الوهاب');
INSERT INTO "imlaei_simple" VALUES(38,10,'38:10','ام لهم ملك السماوات والارض وما بينهما فليرتقوا في الاسباب');
INSERT INTO "imlaei_simple" VALUES(38,11,'38:11','جند ما هنالك مهزوم من الاحزاب');
INSERT INTO "imlaei_simple" VALUES(38,12,'38:12','كذبت قبلهم قوم نوح وعاد وفرعون ذو الاوتاد');
INSERT INTO "imlaei_simple" VALUES(38,13,'38:13','وثمود وقوم لوط واصحاب الايكة اولئك الاحزاب');
INSERT INTO "imlaei_simple" VALUES(38,14,'38:14','ان كل الا كذب الرسل فحق عقاب');
INSERT INTO "imlaei_simple" VALUES(38,15,'38:15','وما ينظر هؤلاء الا صيحة واحدة ما لها من فواق');
INSERT INTO "imlaei_simple" VALUES(38,16,'38:16','وقالوا ربنا عجل لنا قطنا قبل يوم الحساب');
INSERT INTO "imlaei_simple" VALUES(38,17,'38:17','اصبر على ما يقولون واذكر عبدنا داوود ذا الايد انه اواب');
INSERT INTO "imlaei_simple" VALUES(38,18,'38:18','انا سخرنا الجبال معه يسبحن بالعشي والاشراق');
INSERT INTO "imlaei_simple" VALUES(38,19,'38:19','والطير محشورة كل له اواب');
INSERT INTO "imlaei_simple" VALUES(38,20,'38:20','وشددنا ملكه واتيناه الحكمة وفصل الخطاب');
INSERT INTO "imlaei_simple" VALUES(38,21,'38:21','وهل اتاك نبا الخصم اذ تسوروا المحراب');
INSERT INTO "imlaei_simple" VALUES(38,22,'38:22','اذ دخلوا على داوود ففزع منهم قالوا لا تخف خصمان بغى بعضنا على بعض فاحكم بيننا بالحق ولا تشطط واهدنا الى سواء الصراط');
INSERT INTO "imlaei_simple" VALUES(38,23,'38:23','ان هذا اخي له تسع وتسعون نعجة ولي نعجة واحدة فقال اكفلنيها وعزني في الخطاب');
INSERT INTO "imlaei_simple" VALUES(38,24,'38:24','قال لقد ظلمك بسؤال نعجتك الى نعاجه وان كثيرا من الخلطاء ليبغي بعضهم على بعض الا الذين امنوا وعملوا الصالحات وقليل ما هم وظن داوود انما فتناه فاستغفر ربه وخر راكعا واناب');
INSERT INTO "imlaei_simple" VALUES(38,25,'38:25','فغفرنا له ذلك وان له عندنا لزلفى وحسن ماب');
INSERT INTO "imlaei_simple" VALUES(38,26,'38:26','يا داوود انا جعلناك خليفة في الارض فاحكم بين الناس بالحق ولا تتبع الهوى فيضلك عن سبيل الله ان الذين يضلون عن سبيل الله لهم عذاب شديد بما نسوا يوم الحساب');
INSERT INTO "imlaei_simple" VALUES(38,27,'38:27','وما خلقنا السماء والارض وما بينهما باطلا ذلك ظن الذين كفروا فويل للذين كفروا من النار');
INSERT INTO "imlaei_simple" VALUES(38,28,'38:28','ام نجعل الذين امنوا وعملوا الصالحات كالمفسدين في الارض ام نجعل المتقين كالفجار');
INSERT INTO "imlaei_simple" VALUES(38,29,'38:29','كتاب انزلناه اليك مبارك ليدبروا اياته وليتذكر اولو الالباب');
INSERT INTO "imlaei_simple" VALUES(38,30,'38:30','ووهبنا لداوود سليمان نعم العبد انه اواب');
INSERT INTO "imlaei_simple" VALUES(38,31,'38:31','اذ عرض عليه بالعشي الصافنات الجياد');
INSERT INTO "imlaei_simple" VALUES(38,32,'38:32','فقال اني احببت حب الخير عن ذكر ربي حتى توارت بالحجاب');
INSERT INTO "imlaei_simple" VALUES(38,33,'38:33','ردوها علي فطفق مسحا بالسوق والاعناق');
INSERT INTO "imlaei_simple" VALUES(38,34,'38:34','ولقد فتنا سليمان والقينا على كرسيه جسدا ثم اناب');
INSERT INTO "imlaei_simple" VALUES(38,35,'38:35','قال رب اغفر لي وهب لي ملكا لا ينبغي لاحد من بعدي انك انت الوهاب');
INSERT INTO "imlaei_simple" VALUES(38,36,'38:36','فسخرنا له الريح تجري بامره رخاء حيث اصاب');
INSERT INTO "imlaei_simple" VALUES(38,37,'38:37','والشياطين كل بناء وغواص');
INSERT INTO "imlaei_simple" VALUES(38,38,'38:38','واخرين مقرنين في الاصفاد');
INSERT INTO "imlaei_simple" VALUES(38,39,'38:39','هذا عطاؤنا فامنن او امسك بغير حساب');
INSERT INTO "imlaei_simple" VALUES(38,40,'38:40','وان له عندنا لزلفى وحسن ماب');
INSERT INTO "imlaei_simple" VALUES(38,41,'38:41','واذكر عبدنا ايوب اذ نادى ربه اني مسني الشيطان بنصب وعذاب');
INSERT INTO "imlaei_simple" VALUES(38,42,'38:42','اركض برجلك هذا مغتسل بارد وشراب');
INSERT INTO "imlaei_simple" VALUES(38,43,'38:43','ووهبنا له اهله ومثلهم معهم رحمة منا وذكرى لاولي الالباب');
INSERT INTO "imlaei_simple" VALUES(38,44,'38:44','وخذ بيدك ضغثا فاضرب به ولا تحنث انا وجدناه صابرا نعم العبد انه اواب');
INSERT INTO "imlaei_simple" VALUES(38,45,'38:45','واذكر عبادنا ابراهيم واسحاق ويعقوب اولي الايدي والابصار');
INSERT INTO "imlaei_simple" VALUES(38,46,'38:46','انا اخلصناهم بخالصة ذكرى الدار');
INSERT INTO "imlaei_simple" VALUES(38,47,'38:47','وانهم عندنا لمن المصطفين الاخيار');
INSERT INTO "imlaei_simple" VALUES(38,48,'38:48','واذكر اسماعيل واليسع وذا الكفل وكل من الاخيار');
INSERT INTO "imlaei_simple" VALUES(38,49,'38:49','هذا ذكر وان للمتقين لحسن ماب');
INSERT INTO "imlaei_simple" VALUES(38,50,'38:50','جنات عدن مفتحة لهم الابواب');
INSERT INTO "imlaei_simple" VALUES(38,51,'38:51','متكئين فيها يدعون فيها بفاكهة كثيرة وشراب');
INSERT INTO "imlaei_simple" VALUES(38,52,'38:52','وعندهم قاصرات الطرف اتراب');
INSERT INTO "imlaei_simple" VALUES(38,53,'38:53','هذا ما توعدون ليوم الحساب');
INSERT INTO "imlaei_simple" VALUES(38,54,'38:54','ان هذا لرزقنا ما له من نفاد');
INSERT INTO "imlaei_simple" VALUES(38,55,'38:55','هذا وان للطاغين لشر ماب');
INSERT INTO "imlaei_simple" VALUES(38,56,'38:56','جهنم يصلونها فبئس المهاد');
INSERT INTO "imlaei_simple" VALUES(38,57,'38:57','هذا فليذوقوه حميم وغساق');
INSERT INTO "imlaei_simple" VALUES(38,58,'38:58','واخر من شكله ازواج');
INSERT INTO "imlaei_simple" VALUES(38,59,'38:59','هذا فوج مقتحم معكم لا مرحبا بهم انهم صالو النار');
INSERT INTO "imlaei_simple" VALUES(38,60,'38:60','قالوا بل انتم لا مرحبا بكم انتم قدمتموه لنا فبئس القرار');
INSERT INTO "imlaei_simple" VALUES(38,61,'38:61','قالوا ربنا من قدم لنا هذا فزده عذابا ضعفا في النار');
INSERT INTO "imlaei_simple" VALUES(38,62,'38:62','وقالوا ما لنا لا نرى رجالا كنا نعدهم من الاشرار');
INSERT INTO "imlaei_simple" VALUES(38,63,'38:63','اتخذناهم سخريا ام زاغت عنهم الابصار');
INSERT INTO "imlaei_simple" VALUES(38,64,'38:64','ان ذلك لحق تخاصم اهل النار');
INSERT INTO "imlaei_simple" VALUES(38,65,'38:65','قل انما انا منذر وما من اله الا الله الواحد القهار');
INSERT INTO "imlaei_simple" VALUES(38,66,'38:66','رب السماوات والارض وما بينهما العزيز الغفار');
INSERT INTO "imlaei_simple" VALUES(38,67,'38:67','قل هو نبا عظيم');
INSERT INTO "imlaei_simple" VALUES(38,68,'38:68','انتم عنه معرضون');
INSERT INTO "imlaei_simple" VALUES(38,69,'38:69','ما كان لي من علم بالملا الاعلى اذ يختصمون');
INSERT INTO "imlaei_simple" VALUES(38,70,'38:70','ان يوحى الي الا انما انا نذير مبين');
INSERT INTO "imlaei_simple" VALUES(38,71,'38:71','اذ قال ربك للملائكة اني خالق بشرا من طين');
INSERT INTO "imlaei_simple" VALUES(38,72,'38:72','فاذا سويته ونفخت فيه من روحي فقعوا له ساجدين');
INSERT INTO "imlaei_simple" VALUES(38,73,'38:73','فسجد الملائكة كلهم اجمعون');
INSERT INTO "imlaei_simple" VALUES(38,74,'38:74','الا ابليس استكبر وكان من الكافرين');
INSERT INTO "imlaei_simple" VALUES(38,75,'38:75','قال يا ابليس ما منعك ان تسجد لما خلقت بيدي استكبرت ام كنت من العالين');
INSERT INTO "imlaei_simple" VALUES(38,76,'38:76','قال انا خير منه خلقتني من نار وخلقته من طين');
INSERT INTO "imlaei_simple" VALUES(38,77,'38:77','قال فاخرج منها فانك رجيم');
INSERT INTO "imlaei_simple" VALUES(38,78,'38:78','وان عليك لعنتي الى يوم الدين');
INSERT INTO "imlaei_simple" VALUES(38,79,'38:79','قال رب فانظرني الى يوم يبعثون');
INSERT INTO "imlaei_simple" VALUES(38,80,'38:80','قال فانك من المنظرين');
INSERT INTO "imlaei_simple" VALUES(38,81,'38:81','الى يوم الوقت المعلوم');
INSERT INTO "imlaei_simple" VALUES(38,82,'38:82','قال فبعزتك لاغوينهم اجمعين');
INSERT INTO "imlaei_simple" VALUES(38,83,'38:83','الا عبادك منهم المخلصين');
INSERT INTO "imlaei_simple" VALUES(38,84,'38:84','قال فالحق والحق اقول');
INSERT INTO "imlaei_simple" VALUES(38,85,'38:85','لاملان جهنم منك وممن تبعك منهم اجمعين');
INSERT INTO "imlaei_simple" VALUES(38,86,'38:86','قل ما اسالكم عليه من اجر وما انا من المتكلفين');
INSERT INTO "imlaei_simple" VALUES(38,87,'38:87','ان هو الا ذكر للعالمين');
INSERT INTO "imlaei_simple" VALUES(38,88,'38:88','ولتعلمن نباه بعد حين');
INSERT INTO "imlaei_simple" VALUES(39,1,'39:1','تنزيل الكتاب من الله العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(39,2,'39:2','انا انزلنا اليك الكتاب بالحق فاعبد الله مخلصا له الدين');
INSERT INTO "imlaei_simple" VALUES(39,3,'39:3','الا لله الدين الخالص والذين اتخذوا من دونه اولياء ما نعبدهم الا ليقربونا الى الله زلفى ان الله يحكم بينهم في ما هم فيه يختلفون ان الله لا يهدي من هو كاذب كفار');
INSERT INTO "imlaei_simple" VALUES(39,4,'39:4','لو اراد الله ان يتخذ ولدا لاصطفى مما يخلق ما يشاء سبحانه هو الله الواحد القهار');
INSERT INTO "imlaei_simple" VALUES(39,5,'39:5','خلق السماوات والارض بالحق يكور الليل على النهار ويكور النهار على الليل وسخر الشمس والقمر كل يجري لاجل مسمى الا هو العزيز الغفار');
INSERT INTO "imlaei_simple" VALUES(39,6,'39:6','خلقكم من نفس واحدة ثم جعل منها زوجها وانزل لكم من الانعام ثمانية ازواج يخلقكم في بطون امهاتكم خلقا من بعد خلق في ظلمات ثلاث ذلكم الله ربكم له الملك لا اله الا هو فانى تصرفون');
INSERT INTO "imlaei_simple" VALUES(39,7,'39:7','ان تكفروا فان الله غني عنكم ولا يرضى لعباده الكفر وان تشكروا يرضه لكم ولا تزر وازرة وزر اخرى ثم الى ربكم مرجعكم فينبئكم بما كنتم تعملون انه عليم بذات الصدور');
INSERT INTO "imlaei_simple" VALUES(39,8,'39:8','واذا مس الانسان ضر دعا ربه منيبا اليه ثم اذا خوله نعمة منه نسي ما كان يدعو اليه من قبل وجعل لله اندادا ليضل عن سبيله قل تمتع بكفرك قليلا انك من اصحاب النار');
INSERT INTO "imlaei_simple" VALUES(39,9,'39:9','امن هو قانت اناء الليل ساجدا وقائما يحذر الاخرة ويرجو رحمة ربه قل هل يستوي الذين يعلمون والذين لا يعلمون انما يتذكر اولو الالباب');
INSERT INTO "imlaei_simple" VALUES(39,10,'39:10','قل يا عباد الذين امنوا اتقوا ربكم للذين احسنوا في هذه الدنيا حسنة وارض الله واسعة انما يوفى الصابرون اجرهم بغير حساب');
INSERT INTO "imlaei_simple" VALUES(39,11,'39:11','قل اني امرت ان اعبد الله مخلصا له الدين');
INSERT INTO "imlaei_simple" VALUES(39,12,'39:12','وامرت لان اكون اول المسلمين');
INSERT INTO "imlaei_simple" VALUES(39,13,'39:13','قل اني اخاف ان عصيت ربي عذاب يوم عظيم');
INSERT INTO "imlaei_simple" VALUES(39,14,'39:14','قل الله اعبد مخلصا له ديني');
INSERT INTO "imlaei_simple" VALUES(39,15,'39:15','فاعبدوا ما شئتم من دونه قل ان الخاسرين الذين خسروا انفسهم واهليهم يوم القيامة الا ذلك هو الخسران المبين');
INSERT INTO "imlaei_simple" VALUES(39,16,'39:16','لهم من فوقهم ظلل من النار ومن تحتهم ظلل ذلك يخوف الله به عباده يا عباد فاتقون');
INSERT INTO "imlaei_simple" VALUES(39,17,'39:17','والذين اجتنبوا الطاغوت ان يعبدوها وانابوا الى الله لهم البشرى فبشر عباد');
INSERT INTO "imlaei_simple" VALUES(39,18,'39:18','الذين يستمعون القول فيتبعون احسنه اولئك الذين هداهم الله واولئك هم اولو الالباب');
INSERT INTO "imlaei_simple" VALUES(39,19,'39:19','افمن حق عليه كلمة العذاب افانت تنقذ من في النار');
INSERT INTO "imlaei_simple" VALUES(39,20,'39:20','لكن الذين اتقوا ربهم لهم غرف من فوقها غرف مبنية تجري من تحتها الانهار وعد الله لا يخلف الله الميعاد');
INSERT INTO "imlaei_simple" VALUES(39,21,'39:21','الم تر ان الله انزل من السماء ماء فسلكه ينابيع في الارض ثم يخرج به زرعا مختلفا الوانه ثم يهيج فتراه مصفرا ثم يجعله حطاما ان في ذلك لذكرى لاولي الالباب');
INSERT INTO "imlaei_simple" VALUES(39,22,'39:22','افمن شرح الله صدره للاسلام فهو على نور من ربه فويل للقاسية قلوبهم من ذكر الله اولئك في ضلال مبين');
INSERT INTO "imlaei_simple" VALUES(39,23,'39:23','الله نزل احسن الحديث كتابا متشابها مثاني تقشعر منه جلود الذين يخشون ربهم ثم تلين جلودهم وقلوبهم الى ذكر الله ذلك هدى الله يهدي به من يشاء ومن يضلل الله فما له من هاد');
INSERT INTO "imlaei_simple" VALUES(39,24,'39:24','افمن يتقي بوجهه سوء العذاب يوم القيامة وقيل للظالمين ذوقوا ما كنتم تكسبون');
INSERT INTO "imlaei_simple" VALUES(39,25,'39:25','كذب الذين من قبلهم فاتاهم العذاب من حيث لا يشعرون');
INSERT INTO "imlaei_simple" VALUES(39,26,'39:26','فاذاقهم الله الخزي في الحياة الدنيا ولعذاب الاخرة اكبر لو كانوا يعلمون');
INSERT INTO "imlaei_simple" VALUES(39,27,'39:27','ولقد ضربنا للناس في هذا القران من كل مثل لعلهم يتذكرون');
INSERT INTO "imlaei_simple" VALUES(39,28,'39:28','قرانا عربيا غير ذي عوج لعلهم يتقون');
INSERT INTO "imlaei_simple" VALUES(39,29,'39:29','ضرب الله مثلا رجلا فيه شركاء متشاكسون ورجلا سلما لرجل هل يستويان مثلا الحمد لله بل اكثرهم لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(39,30,'39:30','انك ميت وانهم ميتون');
INSERT INTO "imlaei_simple" VALUES(39,31,'39:31','ثم انكم يوم القيامة عند ربكم تختصمون');
INSERT INTO "imlaei_simple" VALUES(39,32,'39:32','فمن اظلم ممن كذب على الله وكذب بالصدق اذ جاءه اليس في جهنم مثوى للكافرين');
INSERT INTO "imlaei_simple" VALUES(39,33,'39:33','والذي جاء بالصدق وصدق به اولئك هم المتقون');
INSERT INTO "imlaei_simple" VALUES(39,34,'39:34','لهم ما يشاءون عند ربهم ذلك جزاء المحسنين');
INSERT INTO "imlaei_simple" VALUES(39,35,'39:35','ليكفر الله عنهم اسوا الذي عملوا ويجزيهم اجرهم باحسن الذي كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(39,36,'39:36','اليس الله بكاف عبده ويخوفونك بالذين من دونه ومن يضلل الله فما له من هاد');
INSERT INTO "imlaei_simple" VALUES(39,37,'39:37','ومن يهد الله فما له من مضل اليس الله بعزيز ذي انتقام');
INSERT INTO "imlaei_simple" VALUES(39,38,'39:38','ولئن سالتهم من خلق السماوات والارض ليقولن الله قل افرايتم ما تدعون من دون الله ان ارادني الله بضر هل هن كاشفات ضره او ارادني برحمة هل هن ممسكات رحمته قل حسبي الله عليه يتوكل المتوكلون');
INSERT INTO "imlaei_simple" VALUES(39,39,'39:39','قل يا قوم اعملوا على مكانتكم اني عامل فسوف تعلمون');
INSERT INTO "imlaei_simple" VALUES(39,40,'39:40','من ياتيه عذاب يخزيه ويحل عليه عذاب مقيم');
INSERT INTO "imlaei_simple" VALUES(39,41,'39:41','انا انزلنا عليك الكتاب للناس بالحق فمن اهتدى فلنفسه ومن ضل فانما يضل عليها وما انت عليهم بوكيل');
INSERT INTO "imlaei_simple" VALUES(39,42,'39:42','الله يتوفى الانفس حين موتها والتي لم تمت في منامها فيمسك التي قضى عليها الموت ويرسل الاخرى الى اجل مسمى ان في ذلك لايات لقوم يتفكرون');
INSERT INTO "imlaei_simple" VALUES(39,43,'39:43','ام اتخذوا من دون الله شفعاء قل اولو كانوا لا يملكون شيئا ولا يعقلون');
INSERT INTO "imlaei_simple" VALUES(39,44,'39:44','قل لله الشفاعة جميعا له ملك السماوات والارض ثم اليه ترجعون');
INSERT INTO "imlaei_simple" VALUES(39,45,'39:45','واذا ذكر الله وحده اشمازت قلوب الذين لا يؤمنون بالاخرة واذا ذكر الذين من دونه اذا هم يستبشرون');
INSERT INTO "imlaei_simple" VALUES(39,46,'39:46','قل اللهم فاطر السماوات والارض عالم الغيب والشهادة انت تحكم بين عبادك في ما كانوا فيه يختلفون');
INSERT INTO "imlaei_simple" VALUES(39,47,'39:47','ولو ان للذين ظلموا ما في الارض جميعا ومثله معه لافتدوا به من سوء العذاب يوم القيامة وبدا لهم من الله ما لم يكونوا يحتسبون');
INSERT INTO "imlaei_simple" VALUES(39,48,'39:48','وبدا لهم سيئات ما كسبوا وحاق بهم ما كانوا به يستهزئون');
INSERT INTO "imlaei_simple" VALUES(39,49,'39:49','فاذا مس الانسان ضر دعانا ثم اذا خولناه نعمة منا قال انما اوتيته على علم بل هي فتنة ولكن اكثرهم لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(39,50,'39:50','قد قالها الذين من قبلهم فما اغنى عنهم ما كانوا يكسبون');
INSERT INTO "imlaei_simple" VALUES(39,51,'39:51','فاصابهم سيئات ما كسبوا والذين ظلموا من هؤلاء سيصيبهم سيئات ما كسبوا وما هم بمعجزين');
INSERT INTO "imlaei_simple" VALUES(39,52,'39:52','اولم يعلموا ان الله يبسط الرزق لمن يشاء ويقدر ان في ذلك لايات لقوم يؤمنون');
INSERT INTO "imlaei_simple" VALUES(39,53,'39:53','قل يا عبادي الذين اسرفوا على انفسهم لا تقنطوا من رحمة الله ان الله يغفر الذنوب جميعا انه هو الغفور الرحيم');
INSERT INTO "imlaei_simple" VALUES(39,54,'39:54','وانيبوا الى ربكم واسلموا له من قبل ان ياتيكم العذاب ثم لا تنصرون');
INSERT INTO "imlaei_simple" VALUES(39,55,'39:55','واتبعوا احسن ما انزل اليكم من ربكم من قبل ان ياتيكم العذاب بغتة وانتم لا تشعرون');
INSERT INTO "imlaei_simple" VALUES(39,56,'39:56','ان تقول نفس يا حسرتا على ما فرطت في جنب الله وان كنت لمن الساخرين');
INSERT INTO "imlaei_simple" VALUES(39,57,'39:57','او تقول لو ان الله هداني لكنت من المتقين');
INSERT INTO "imlaei_simple" VALUES(39,58,'39:58','او تقول حين ترى العذاب لو ان لي كرة فاكون من المحسنين');
INSERT INTO "imlaei_simple" VALUES(39,59,'39:59','بلى قد جاءتك اياتي فكذبت بها واستكبرت وكنت من الكافرين');
INSERT INTO "imlaei_simple" VALUES(39,60,'39:60','ويوم القيامة ترى الذين كذبوا على الله وجوههم مسودة اليس في جهنم مثوى للمتكبرين');
INSERT INTO "imlaei_simple" VALUES(39,61,'39:61','وينجي الله الذين اتقوا بمفازتهم لا يمسهم السوء ولا هم يحزنون');
INSERT INTO "imlaei_simple" VALUES(39,62,'39:62','الله خالق كل شيء وهو على كل شيء وكيل');
INSERT INTO "imlaei_simple" VALUES(39,63,'39:63','له مقاليد السماوات والارض والذين كفروا بايات الله اولئك هم الخاسرون');
INSERT INTO "imlaei_simple" VALUES(39,64,'39:64','قل افغير الله تامروني اعبد ايها الجاهلون');
INSERT INTO "imlaei_simple" VALUES(39,65,'39:65','ولقد اوحي اليك والى الذين من قبلك لئن اشركت ليحبطن عملك ولتكونن من الخاسرين');
INSERT INTO "imlaei_simple" VALUES(39,66,'39:66','بل الله فاعبد وكن من الشاكرين');
INSERT INTO "imlaei_simple" VALUES(39,67,'39:67','وما قدروا الله حق قدره والارض جميعا قبضته يوم القيامة والسماوات مطويات بيمينه سبحانه وتعالى عما يشركون');
INSERT INTO "imlaei_simple" VALUES(39,68,'39:68','ونفخ في الصور فصعق من في السماوات ومن في الارض الا من شاء الله ثم نفخ فيه اخرى فاذا هم قيام ينظرون');
INSERT INTO "imlaei_simple" VALUES(39,69,'39:69','واشرقت الارض بنور ربها ووضع الكتاب وجيء بالنبيين والشهداء وقضي بينهم بالحق وهم لا يظلمون');
INSERT INTO "imlaei_simple" VALUES(39,70,'39:70','ووفيت كل نفس ما عملت وهو اعلم بما يفعلون');
INSERT INTO "imlaei_simple" VALUES(39,71,'39:71','وسيق الذين كفروا الى جهنم زمرا حتى اذا جاءوها فتحت ابوابها وقال لهم خزنتها الم ياتكم رسل منكم يتلون عليكم ايات ربكم وينذرونكم لقاء يومكم هذا قالوا بلى ولكن حقت كلمة العذاب على الكافرين');
INSERT INTO "imlaei_simple" VALUES(39,72,'39:72','قيل ادخلوا ابواب جهنم خالدين فيها فبئس مثوى المتكبرين');
INSERT INTO "imlaei_simple" VALUES(39,73,'39:73','وسيق الذين اتقوا ربهم الى الجنة زمرا حتى اذا جاءوها وفتحت ابوابها وقال لهم خزنتها سلام عليكم طبتم فادخلوها خالدين');
INSERT INTO "imlaei_simple" VALUES(39,74,'39:74','وقالوا الحمد لله الذي صدقنا وعده واورثنا الارض نتبوا من الجنة حيث نشاء فنعم اجر العاملين');
INSERT INTO "imlaei_simple" VALUES(39,75,'39:75','وترى الملائكة حافين من حول العرش يسبحون بحمد ربهم وقضي بينهم بالحق وقيل الحمد لله رب العالمين');
INSERT INTO "imlaei_simple" VALUES(40,1,'40:1','حم');
INSERT INTO "imlaei_simple" VALUES(40,2,'40:2','تنزيل الكتاب من الله العزيز العليم');
INSERT INTO "imlaei_simple" VALUES(40,3,'40:3','غافر الذنب وقابل التوب شديد العقاب ذي الطول لا اله الا هو اليه المصير');
INSERT INTO "imlaei_simple" VALUES(40,4,'40:4','ما يجادل في ايات الله الا الذين كفروا فلا يغررك تقلبهم في البلاد');
INSERT INTO "imlaei_simple" VALUES(40,5,'40:5','كذبت قبلهم قوم نوح والاحزاب من بعدهم وهمت كل امة برسولهم لياخذوه وجادلوا بالباطل ليدحضوا به الحق فاخذتهم فكيف كان عقاب');
INSERT INTO "imlaei_simple" VALUES(40,6,'40:6','وكذلك حقت كلمت ربك على الذين كفروا انهم اصحاب النار');
INSERT INTO "imlaei_simple" VALUES(40,7,'40:7','الذين يحملون العرش ومن حوله يسبحون بحمد ربهم ويؤمنون به ويستغفرون للذين امنوا ربنا وسعت كل شيء رحمة وعلما فاغفر للذين تابوا واتبعوا سبيلك وقهم عذاب الجحيم');
INSERT INTO "imlaei_simple" VALUES(40,8,'40:8','ربنا وادخلهم جنات عدن التي وعدتهم ومن صلح من ابائهم وازواجهم وذرياتهم انك انت العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(40,9,'40:9','وقهم السيئات ومن تق السيئات يومئذ فقد رحمته وذلك هو الفوز العظيم');
INSERT INTO "imlaei_simple" VALUES(40,10,'40:10','ان الذين كفروا ينادون لمقت الله اكبر من مقتكم انفسكم اذ تدعون الى الايمان فتكفرون');
INSERT INTO "imlaei_simple" VALUES(40,11,'40:11','قالوا ربنا امتنا اثنتين واحييتنا اثنتين فاعترفنا بذنوبنا فهل الى خروج من سبيل');
INSERT INTO "imlaei_simple" VALUES(40,12,'40:12','ذلكم بانه اذا دعي الله وحده كفرتم وان يشرك به تؤمنوا فالحكم لله العلي الكبير');
INSERT INTO "imlaei_simple" VALUES(40,13,'40:13','هو الذي يريكم اياته وينزل لكم من السماء رزقا وما يتذكر الا من ينيب');
INSERT INTO "imlaei_simple" VALUES(40,14,'40:14','فادعوا الله مخلصين له الدين ولو كره الكافرون');
INSERT INTO "imlaei_simple" VALUES(40,15,'40:15','رفيع الدرجات ذو العرش يلقي الروح من امره على من يشاء من عباده لينذر يوم التلاق');
INSERT INTO "imlaei_simple" VALUES(40,16,'40:16','يوم هم بارزون لا يخفى على الله منهم شيء لمن الملك اليوم لله الواحد القهار');
INSERT INTO "imlaei_simple" VALUES(40,17,'40:17','اليوم تجزى كل نفس بما كسبت لا ظلم اليوم ان الله سريع الحساب');
INSERT INTO "imlaei_simple" VALUES(40,18,'40:18','وانذرهم يوم الازفة اذ القلوب لدى الحناجر كاظمين ما للظالمين من حميم ولا شفيع يطاع');
INSERT INTO "imlaei_simple" VALUES(40,19,'40:19','يعلم خائنة الاعين وما تخفي الصدور');
INSERT INTO "imlaei_simple" VALUES(40,20,'40:20','والله يقضي بالحق والذين يدعون من دونه لا يقضون بشيء ان الله هو السميع البصير');
INSERT INTO "imlaei_simple" VALUES(40,21,'40:21','اولم يسيروا في الارض فينظروا كيف كان عاقبة الذين كانوا من قبلهم كانوا هم اشد منهم قوة واثارا في الارض فاخذهم الله بذنوبهم وما كان لهم من الله من واق');
INSERT INTO "imlaei_simple" VALUES(40,22,'40:22','ذلك بانهم كانت تاتيهم رسلهم بالبينات فكفروا فاخذهم الله انه قوي شديد العقاب');
INSERT INTO "imlaei_simple" VALUES(40,23,'40:23','ولقد ارسلنا موسى باياتنا وسلطان مبين');
INSERT INTO "imlaei_simple" VALUES(40,24,'40:24','الى فرعون وهامان وقارون فقالوا ساحر كذاب');
INSERT INTO "imlaei_simple" VALUES(40,25,'40:25','فلما جاءهم بالحق من عندنا قالوا اقتلوا ابناء الذين امنوا معه واستحيوا نساءهم وما كيد الكافرين الا في ضلال');
INSERT INTO "imlaei_simple" VALUES(40,26,'40:26','وقال فرعون ذروني اقتل موسى وليدع ربه اني اخاف ان يبدل دينكم او ان يظهر في الارض الفساد');
INSERT INTO "imlaei_simple" VALUES(40,27,'40:27','وقال موسى اني عذت بربي وربكم من كل متكبر لا يؤمن بيوم الحساب');
INSERT INTO "imlaei_simple" VALUES(40,28,'40:28','وقال رجل مؤمن من ال فرعون يكتم ايمانه اتقتلون رجلا ان يقول ربي الله وقد جاءكم بالبينات من ربكم وان يك كاذبا فعليه كذبه وان يك صادقا يصبكم بعض الذي يعدكم ان الله لا يهدي من هو مسرف كذاب');
INSERT INTO "imlaei_simple" VALUES(40,29,'40:29','يا قوم لكم الملك اليوم ظاهرين في الارض فمن ينصرنا من باس الله ان جاءنا قال فرعون ما اريكم الا ما ارى وما اهديكم الا سبيل الرشاد');
INSERT INTO "imlaei_simple" VALUES(40,30,'40:30','وقال الذي امن يا قوم اني اخاف عليكم مثل يوم الاحزاب');
INSERT INTO "imlaei_simple" VALUES(40,31,'40:31','مثل داب قوم نوح وعاد وثمود والذين من بعدهم وما الله يريد ظلما للعباد');
INSERT INTO "imlaei_simple" VALUES(40,32,'40:32','ويا قوم اني اخاف عليكم يوم التناد');
INSERT INTO "imlaei_simple" VALUES(40,33,'40:33','يوم تولون مدبرين ما لكم من الله من عاصم ومن يضلل الله فما له من هاد');
INSERT INTO "imlaei_simple" VALUES(40,34,'40:34','ولقد جاءكم يوسف من قبل بالبينات فما زلتم في شك مما جاءكم به حتى اذا هلك قلتم لن يبعث الله من بعده رسولا كذلك يضل الله من هو مسرف مرتاب');
INSERT INTO "imlaei_simple" VALUES(40,35,'40:35','الذين يجادلون في ايات الله بغير سلطان اتاهم كبر مقتا عند الله وعند الذين امنوا كذلك يطبع الله على كل قلب متكبر جبار');
INSERT INTO "imlaei_simple" VALUES(40,36,'40:36','وقال فرعون يا هامان ابن لي صرحا لعلي ابلغ الاسباب');
INSERT INTO "imlaei_simple" VALUES(40,37,'40:37','اسباب السماوات فاطلع الى اله موسى واني لاظنه كاذبا وكذلك زين لفرعون سوء عمله وصد عن السبيل وما كيد فرعون الا في تباب');
INSERT INTO "imlaei_simple" VALUES(40,38,'40:38','وقال الذي امن يا قوم اتبعون اهدكم سبيل الرشاد');
INSERT INTO "imlaei_simple" VALUES(40,39,'40:39','يا قوم انما هذه الحياة الدنيا متاع وان الاخرة هي دار القرار');
INSERT INTO "imlaei_simple" VALUES(40,40,'40:40','من عمل سيئة فلا يجزى الا مثلها ومن عمل صالحا من ذكر او انثى وهو مؤمن فاولئك يدخلون الجنة يرزقون فيها بغير حساب');
INSERT INTO "imlaei_simple" VALUES(40,41,'40:41','ويا قوم ما لي ادعوكم الى النجاة وتدعونني الى النار');
INSERT INTO "imlaei_simple" VALUES(40,42,'40:42','تدعونني لاكفر بالله واشرك به ما ليس لي به علم وانا ادعوكم الى العزيز الغفار');
INSERT INTO "imlaei_simple" VALUES(40,43,'40:43','لا جرم انما تدعونني اليه ليس له دعوة في الدنيا ولا في الاخرة وان مردنا الى الله وان المسرفين هم اصحاب النار');
INSERT INTO "imlaei_simple" VALUES(40,44,'40:44','فستذكرون ما اقول لكم وافوض امري الى الله ان الله بصير بالعباد');
INSERT INTO "imlaei_simple" VALUES(40,45,'40:45','فوقاه الله سيئات ما مكروا وحاق بال فرعون سوء العذاب');
INSERT INTO "imlaei_simple" VALUES(40,46,'40:46','النار يعرضون عليها غدوا وعشيا ويوم تقوم الساعة ادخلوا ال فرعون اشد العذاب');
INSERT INTO "imlaei_simple" VALUES(40,47,'40:47','واذ يتحاجون في النار فيقول الضعفاء للذين استكبروا انا كنا لكم تبعا فهل انتم مغنون عنا نصيبا من النار');
INSERT INTO "imlaei_simple" VALUES(40,48,'40:48','قال الذين استكبروا انا كل فيها ان الله قد حكم بين العباد');
INSERT INTO "imlaei_simple" VALUES(40,49,'40:49','وقال الذين في النار لخزنة جهنم ادعوا ربكم يخفف عنا يوما من العذاب');
INSERT INTO "imlaei_simple" VALUES(40,50,'40:50','قالوا اولم تك تاتيكم رسلكم بالبينات قالوا بلى قالوا فادعوا وما دعاء الكافرين الا في ضلال');
INSERT INTO "imlaei_simple" VALUES(40,51,'40:51','انا لننصر رسلنا والذين امنوا في الحياة الدنيا ويوم يقوم الاشهاد');
INSERT INTO "imlaei_simple" VALUES(40,52,'40:52','يوم لا ينفع الظالمين معذرتهم ولهم اللعنة ولهم سوء الدار');
INSERT INTO "imlaei_simple" VALUES(40,53,'40:53','ولقد اتينا موسى الهدى واورثنا بني اسرائيل الكتاب');
INSERT INTO "imlaei_simple" VALUES(40,54,'40:54','هدى وذكرى لاولي الالباب');
INSERT INTO "imlaei_simple" VALUES(40,55,'40:55','فاصبر ان وعد الله حق واستغفر لذنبك وسبح بحمد ربك بالعشي والابكار');
INSERT INTO "imlaei_simple" VALUES(40,56,'40:56','ان الذين يجادلون في ايات الله بغير سلطان اتاهم ان في صدورهم الا كبر ما هم ببالغيه فاستعذ بالله انه هو السميع البصير');
INSERT INTO "imlaei_simple" VALUES(40,57,'40:57','لخلق السماوات والارض اكبر من خلق الناس ولكن اكثر الناس لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(40,58,'40:58','وما يستوي الاعمى والبصير والذين امنوا وعملوا الصالحات ولا المسيء قليلا ما تتذكرون');
INSERT INTO "imlaei_simple" VALUES(40,59,'40:59','ان الساعة لاتية لا ريب فيها ولكن اكثر الناس لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(40,60,'40:60','وقال ربكم ادعوني استجب لكم ان الذين يستكبرون عن عبادتي سيدخلون جهنم داخرين');
INSERT INTO "imlaei_simple" VALUES(40,61,'40:61','الله الذي جعل لكم الليل لتسكنوا فيه والنهار مبصرا ان الله لذو فضل على الناس ولكن اكثر الناس لا يشكرون');
INSERT INTO "imlaei_simple" VALUES(40,62,'40:62','ذلكم الله ربكم خالق كل شيء لا اله الا هو فانى تؤفكون');
INSERT INTO "imlaei_simple" VALUES(40,63,'40:63','كذلك يؤفك الذين كانوا بايات الله يجحدون');
INSERT INTO "imlaei_simple" VALUES(40,64,'40:64','الله الذي جعل لكم الارض قرارا والسماء بناء وصوركم فاحسن صوركم ورزقكم من الطيبات ذلكم الله ربكم فتبارك الله رب العالمين');
INSERT INTO "imlaei_simple" VALUES(40,65,'40:65','هو الحي لا اله الا هو فادعوه مخلصين له الدين الحمد لله رب العالمين');
INSERT INTO "imlaei_simple" VALUES(40,66,'40:66','قل اني نهيت ان اعبد الذين تدعون من دون الله لما جاءني البينات من ربي وامرت ان اسلم لرب العالمين');
INSERT INTO "imlaei_simple" VALUES(40,67,'40:67','هو الذي خلقكم من تراب ثم من نطفة ثم من علقة ثم يخرجكم طفلا ثم لتبلغوا اشدكم ثم لتكونوا شيوخا ومنكم من يتوفى من قبل ولتبلغوا اجلا مسمى ولعلكم تعقلون');
INSERT INTO "imlaei_simple" VALUES(40,68,'40:68','هو الذي يحيي ويميت فاذا قضى امرا فانما يقول له كن فيكون');
INSERT INTO "imlaei_simple" VALUES(40,69,'40:69','الم تر الى الذين يجادلون في ايات الله انى يصرفون');
INSERT INTO "imlaei_simple" VALUES(40,70,'40:70','الذين كذبوا بالكتاب وبما ارسلنا به رسلنا فسوف يعلمون');
INSERT INTO "imlaei_simple" VALUES(40,71,'40:71','اذ الاغلال في اعناقهم والسلاسل يسحبون');
INSERT INTO "imlaei_simple" VALUES(40,72,'40:72','في الحميم ثم في النار يسجرون');
INSERT INTO "imlaei_simple" VALUES(40,73,'40:73','ثم قيل لهم اين ما كنتم تشركون');
INSERT INTO "imlaei_simple" VALUES(40,74,'40:74','من دون الله قالوا ضلوا عنا بل لم نكن ندعو من قبل شيئا كذلك يضل الله الكافرين');
INSERT INTO "imlaei_simple" VALUES(40,75,'40:75','ذلكم بما كنتم تفرحون في الارض بغير الحق وبما كنتم تمرحون');
INSERT INTO "imlaei_simple" VALUES(40,76,'40:76','ادخلوا ابواب جهنم خالدين فيها فبئس مثوى المتكبرين');
INSERT INTO "imlaei_simple" VALUES(40,77,'40:77','فاصبر ان وعد الله حق فاما نرينك بعض الذي نعدهم او نتوفينك فالينا يرجعون');
INSERT INTO "imlaei_simple" VALUES(40,78,'40:78','ولقد ارسلنا رسلا من قبلك منهم من قصصنا عليك ومنهم من لم نقصص عليك وما كان لرسول ان ياتي باية الا باذن الله فاذا جاء امر الله قضي بالحق وخسر هنالك المبطلون');
INSERT INTO "imlaei_simple" VALUES(40,79,'40:79','الله الذي جعل لكم الانعام لتركبوا منها ومنها تاكلون');
INSERT INTO "imlaei_simple" VALUES(40,80,'40:80','ولكم فيها منافع ولتبلغوا عليها حاجة في صدوركم وعليها وعلى الفلك تحملون');
INSERT INTO "imlaei_simple" VALUES(40,81,'40:81','ويريكم اياته فاي ايات الله تنكرون');
INSERT INTO "imlaei_simple" VALUES(40,82,'40:82','افلم يسيروا في الارض فينظروا كيف كان عاقبة الذين من قبلهم كانوا اكثر منهم واشد قوة واثارا في الارض فما اغنى عنهم ما كانوا يكسبون');
INSERT INTO "imlaei_simple" VALUES(40,83,'40:83','فلما جاءتهم رسلهم بالبينات فرحوا بما عندهم من العلم وحاق بهم ما كانوا به يستهزئون');
INSERT INTO "imlaei_simple" VALUES(40,84,'40:84','فلما راوا باسنا قالوا امنا بالله وحده وكفرنا بما كنا به مشركين');
INSERT INTO "imlaei_simple" VALUES(40,85,'40:85','فلم يك ينفعهم ايمانهم لما راوا باسنا سنت الله التي قد خلت في عباده وخسر هنالك الكافرون');
INSERT INTO "imlaei_simple" VALUES(41,1,'41:1','حم');
INSERT INTO "imlaei_simple" VALUES(41,2,'41:2','تنزيل من الرحمن الرحيم');
INSERT INTO "imlaei_simple" VALUES(41,3,'41:3','كتاب فصلت اياته قرانا عربيا لقوم يعلمون');
INSERT INTO "imlaei_simple" VALUES(41,4,'41:4','بشيرا ونذيرا فاعرض اكثرهم فهم لا يسمعون');
INSERT INTO "imlaei_simple" VALUES(41,5,'41:5','وقالوا قلوبنا في اكنة مما تدعونا اليه وفي اذاننا وقر ومن بيننا وبينك حجاب فاعمل اننا عاملون');
INSERT INTO "imlaei_simple" VALUES(41,6,'41:6','قل انما انا بشر مثلكم يوحى الي انما الهكم اله واحد فاستقيموا اليه واستغفروه وويل للمشركين');
INSERT INTO "imlaei_simple" VALUES(41,7,'41:7','الذين لا يؤتون الزكاة وهم بالاخرة هم كافرون');
INSERT INTO "imlaei_simple" VALUES(41,8,'41:8','ان الذين امنوا وعملوا الصالحات لهم اجر غير ممنون');
INSERT INTO "imlaei_simple" VALUES(41,9,'41:9','قل ائنكم لتكفرون بالذي خلق الارض في يومين وتجعلون له اندادا ذلك رب العالمين');
INSERT INTO "imlaei_simple" VALUES(41,10,'41:10','وجعل فيها رواسي من فوقها وبارك فيها وقدر فيها اقواتها في اربعة ايام سواء للسائلين');
INSERT INTO "imlaei_simple" VALUES(41,11,'41:11','ثم استوى الى السماء وهي دخان فقال لها وللارض ائتيا طوعا او كرها قالتا اتينا طائعين');
INSERT INTO "imlaei_simple" VALUES(41,12,'41:12','فقضاهن سبع سماوات في يومين واوحى في كل سماء امرها وزينا السماء الدنيا بمصابيح وحفظا ذلك تقدير العزيز العليم');
INSERT INTO "imlaei_simple" VALUES(41,13,'41:13','فان اعرضوا فقل انذرتكم صاعقة مثل صاعقة عاد وثمود');
INSERT INTO "imlaei_simple" VALUES(41,14,'41:14','اذ جاءتهم الرسل من بين ايديهم ومن خلفهم الا تعبدوا الا الله قالوا لو شاء ربنا لانزل ملائكة فانا بما ارسلتم به كافرون');
INSERT INTO "imlaei_simple" VALUES(41,15,'41:15','فاما عاد فاستكبروا في الارض بغير الحق وقالوا من اشد منا قوة اولم يروا ان الله الذي خلقهم هو اشد منهم قوة وكانوا باياتنا يجحدون');
INSERT INTO "imlaei_simple" VALUES(41,16,'41:16','فارسلنا عليهم ريحا صرصرا في ايام نحسات لنذيقهم عذاب الخزي في الحياة الدنيا ولعذاب الاخرة اخزى وهم لا ينصرون');
INSERT INTO "imlaei_simple" VALUES(41,17,'41:17','واما ثمود فهديناهم فاستحبوا العمى على الهدى فاخذتهم صاعقة العذاب الهون بما كانوا يكسبون');
INSERT INTO "imlaei_simple" VALUES(41,18,'41:18','ونجينا الذين امنوا وكانوا يتقون');
INSERT INTO "imlaei_simple" VALUES(41,19,'41:19','ويوم يحشر اعداء الله الى النار فهم يوزعون');
INSERT INTO "imlaei_simple" VALUES(41,20,'41:20','حتى اذا ما جاءوها شهد عليهم سمعهم وابصارهم وجلودهم بما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(41,21,'41:21','وقالوا لجلودهم لم شهدتم علينا قالوا انطقنا الله الذي انطق كل شيء وهو خلقكم اول مرة واليه ترجعون');
INSERT INTO "imlaei_simple" VALUES(41,22,'41:22','وما كنتم تستترون ان يشهد عليكم سمعكم ولا ابصاركم ولا جلودكم ولكن ظننتم ان الله لا يعلم كثيرا مما تعملون');
INSERT INTO "imlaei_simple" VALUES(41,23,'41:23','وذلكم ظنكم الذي ظننتم بربكم ارداكم فاصبحتم من الخاسرين');
INSERT INTO "imlaei_simple" VALUES(41,24,'41:24','فان يصبروا فالنار مثوى لهم وان يستعتبوا فما هم من المعتبين');
INSERT INTO "imlaei_simple" VALUES(41,25,'41:25','وقيضنا لهم قرناء فزينوا لهم ما بين ايديهم وما خلفهم وحق عليهم القول في امم قد خلت من قبلهم من الجن والانس انهم كانوا خاسرين');
INSERT INTO "imlaei_simple" VALUES(41,26,'41:26','وقال الذين كفروا لا تسمعوا لهذا القران والغوا فيه لعلكم تغلبون');
INSERT INTO "imlaei_simple" VALUES(41,27,'41:27','فلنذيقن الذين كفروا عذابا شديدا ولنجزينهم اسوا الذي كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(41,28,'41:28','ذلك جزاء اعداء الله النار لهم فيها دار الخلد جزاء بما كانوا باياتنا يجحدون');
INSERT INTO "imlaei_simple" VALUES(41,29,'41:29','وقال الذين كفروا ربنا ارنا اللذين اضلانا من الجن والانس نجعلهما تحت اقدامنا ليكونا من الاسفلين');
INSERT INTO "imlaei_simple" VALUES(41,30,'41:30','ان الذين قالوا ربنا الله ثم استقاموا تتنزل عليهم الملائكة الا تخافوا ولا تحزنوا وابشروا بالجنة التي كنتم توعدون');
INSERT INTO "imlaei_simple" VALUES(41,31,'41:31','نحن اولياؤكم في الحياة الدنيا وفي الاخرة ولكم فيها ما تشتهي انفسكم ولكم فيها ما تدعون');
INSERT INTO "imlaei_simple" VALUES(41,32,'41:32','نزلا من غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(41,33,'41:33','ومن احسن قولا ممن دعا الى الله وعمل صالحا وقال انني من المسلمين');
INSERT INTO "imlaei_simple" VALUES(41,34,'41:34','ولا تستوي الحسنة ولا السيئة ادفع بالتي هي احسن فاذا الذي بينك وبينه عداوة كانه ولي حميم');
INSERT INTO "imlaei_simple" VALUES(41,35,'41:35','وما يلقاها الا الذين صبروا وما يلقاها الا ذو حظ عظيم');
INSERT INTO "imlaei_simple" VALUES(41,36,'41:36','واما ينزغنك من الشيطان نزغ فاستعذ بالله انه هو السميع العليم');
INSERT INTO "imlaei_simple" VALUES(41,37,'41:37','ومن اياته الليل والنهار والشمس والقمر لا تسجدوا للشمس ولا للقمر واسجدوا لله الذي خلقهن ان كنتم اياه تعبدون');
INSERT INTO "imlaei_simple" VALUES(41,38,'41:38','فان استكبروا فالذين عند ربك يسبحون له بالليل والنهار وهم لا يسامون');
INSERT INTO "imlaei_simple" VALUES(41,39,'41:39','ومن اياته انك ترى الارض خاشعة فاذا انزلنا عليها الماء اهتزت وربت ان الذي احياها لمحيي الموتى انه على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(41,40,'41:40','ان الذين يلحدون في اياتنا لا يخفون علينا افمن يلقى في النار خير ام من ياتي امنا يوم القيامة اعملوا ما شئتم انه بما تعملون بصير');
INSERT INTO "imlaei_simple" VALUES(41,41,'41:41','ان الذين كفروا بالذكر لما جاءهم وانه لكتاب عزيز');
INSERT INTO "imlaei_simple" VALUES(41,42,'41:42','لا ياتيه الباطل من بين يديه ولا من خلفه تنزيل من حكيم حميد');
INSERT INTO "imlaei_simple" VALUES(41,43,'41:43','ما يقال لك الا ما قد قيل للرسل من قبلك ان ربك لذو مغفرة وذو عقاب اليم');
INSERT INTO "imlaei_simple" VALUES(41,44,'41:44','ولو جعلناه قرانا اعجميا لقالوا لولا فصلت اياته ااعجمي وعربي قل هو للذين امنوا هدى وشفاء والذين لا يؤمنون في اذانهم وقر وهو عليهم عمى اولئك ينادون من مكان بعيد');
INSERT INTO "imlaei_simple" VALUES(41,45,'41:45','ولقد اتينا موسى الكتاب فاختلف فيه ولولا كلمة سبقت من ربك لقضي بينهم وانهم لفي شك منه مريب');
INSERT INTO "imlaei_simple" VALUES(41,46,'41:46','من عمل صالحا فلنفسه ومن اساء فعليها وما ربك بظلام للعبيد');
INSERT INTO "imlaei_simple" VALUES(41,47,'41:47','اليه يرد علم الساعة وما تخرج من ثمرات من اكمامها وما تحمل من انثى ولا تضع الا بعلمه ويوم يناديهم اين شركائي قالوا اذناك ما منا من شهيد');
INSERT INTO "imlaei_simple" VALUES(41,48,'41:48','وضل عنهم ما كانوا يدعون من قبل وظنوا ما لهم من محيص');
INSERT INTO "imlaei_simple" VALUES(41,49,'41:49','لا يسام الانسان من دعاء الخير وان مسه الشر فيئوس قنوط');
INSERT INTO "imlaei_simple" VALUES(41,50,'41:50','ولئن اذقناه رحمة منا من بعد ضراء مسته ليقولن هذا لي وما اظن الساعة قائمة ولئن رجعت الى ربي ان لي عنده للحسنى فلننبئن الذين كفروا بما عملوا ولنذيقنهم من عذاب غليظ');
INSERT INTO "imlaei_simple" VALUES(41,51,'41:51','واذا انعمنا على الانسان اعرض وناى بجانبه واذا مسه الشر فذو دعاء عريض');
INSERT INTO "imlaei_simple" VALUES(41,52,'41:52','قل ارايتم ان كان من عند الله ثم كفرتم به من اضل ممن هو في شقاق بعيد');
INSERT INTO "imlaei_simple" VALUES(41,53,'41:53','سنريهم اياتنا في الافاق وفي انفسهم حتى يتبين لهم انه الحق اولم يكف بربك انه على كل شيء شهيد');
INSERT INTO "imlaei_simple" VALUES(41,54,'41:54','الا انهم في مرية من لقاء ربهم الا انه بكل شيء محيط');
INSERT INTO "imlaei_simple" VALUES(42,1,'42:1','حم');
INSERT INTO "imlaei_simple" VALUES(42,2,'42:2','عسق');
INSERT INTO "imlaei_simple" VALUES(42,3,'42:3','كذلك يوحي اليك والى الذين من قبلك الله العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(42,4,'42:4','له ما في السماوات وما في الارض وهو العلي العظيم');
INSERT INTO "imlaei_simple" VALUES(42,5,'42:5','تكاد السماوات يتفطرن من فوقهن والملائكة يسبحون بحمد ربهم ويستغفرون لمن في الارض الا ان الله هو الغفور الرحيم');
INSERT INTO "imlaei_simple" VALUES(42,6,'42:6','والذين اتخذوا من دونه اولياء الله حفيظ عليهم وما انت عليهم بوكيل');
INSERT INTO "imlaei_simple" VALUES(42,7,'42:7','وكذلك اوحينا اليك قرانا عربيا لتنذر ام القرى ومن حولها وتنذر يوم الجمع لا ريب فيه فريق في الجنة وفريق في السعير');
INSERT INTO "imlaei_simple" VALUES(42,8,'42:8','ولو شاء الله لجعلهم امة واحدة ولكن يدخل من يشاء في رحمته والظالمون ما لهم من ولي ولا نصير');
INSERT INTO "imlaei_simple" VALUES(42,9,'42:9','ام اتخذوا من دونه اولياء فالله هو الولي وهو يحيي الموتى وهو على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(42,10,'42:10','وما اختلفتم فيه من شيء فحكمه الى الله ذلكم الله ربي عليه توكلت واليه انيب');
INSERT INTO "imlaei_simple" VALUES(42,11,'42:11','فاطر السماوات والارض جعل لكم من انفسكم ازواجا ومن الانعام ازواجا يذرؤكم فيه ليس كمثله شيء وهو السميع البصير');
INSERT INTO "imlaei_simple" VALUES(42,12,'42:12','له مقاليد السماوات والارض يبسط الرزق لمن يشاء ويقدر انه بكل شيء عليم');
INSERT INTO "imlaei_simple" VALUES(42,13,'42:13','شرع لكم من الدين ما وصى به نوحا والذي اوحينا اليك وما وصينا به ابراهيم وموسى وعيسى ان اقيموا الدين ولا تتفرقوا فيه كبر على المشركين ما تدعوهم اليه الله يجتبي اليه من يشاء ويهدي اليه من ينيب');
INSERT INTO "imlaei_simple" VALUES(42,14,'42:14','وما تفرقوا الا من بعد ما جاءهم العلم بغيا بينهم ولولا كلمة سبقت من ربك الى اجل مسمى لقضي بينهم وان الذين اورثوا الكتاب من بعدهم لفي شك منه مريب');
INSERT INTO "imlaei_simple" VALUES(42,15,'42:15','فلذلك فادع واستقم كما امرت ولا تتبع اهواءهم وقل امنت بما انزل الله من كتاب وامرت لاعدل بينكم الله ربنا وربكم لنا اعمالنا ولكم اعمالكم لا حجة بيننا وبينكم الله يجمع بيننا واليه المصير');
INSERT INTO "imlaei_simple" VALUES(42,16,'42:16','والذين يحاجون في الله من بعد ما استجيب له حجتهم داحضة عند ربهم وعليهم غضب ولهم عذاب شديد');
INSERT INTO "imlaei_simple" VALUES(42,17,'42:17','الله الذي انزل الكتاب بالحق والميزان وما يدريك لعل الساعة قريب');
INSERT INTO "imlaei_simple" VALUES(42,18,'42:18','يستعجل بها الذين لا يؤمنون بها والذين امنوا مشفقون منها ويعلمون انها الحق الا ان الذين يمارون في الساعة لفي ضلال بعيد');
INSERT INTO "imlaei_simple" VALUES(42,19,'42:19','الله لطيف بعباده يرزق من يشاء وهو القوي العزيز');
INSERT INTO "imlaei_simple" VALUES(42,20,'42:20','من كان يريد حرث الاخرة نزد له في حرثه ومن كان يريد حرث الدنيا نؤته منها وما له في الاخرة من نصيب');
INSERT INTO "imlaei_simple" VALUES(42,21,'42:21','ام لهم شركاء شرعوا لهم من الدين ما لم ياذن به الله ولولا كلمة الفصل لقضي بينهم وان الظالمين لهم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(42,22,'42:22','ترى الظالمين مشفقين مما كسبوا وهو واقع بهم والذين امنوا وعملوا الصالحات في روضات الجنات لهم ما يشاءون عند ربهم ذلك هو الفضل الكبير');
INSERT INTO "imlaei_simple" VALUES(42,23,'42:23','ذلك الذي يبشر الله عباده الذين امنوا وعملوا الصالحات قل لا اسالكم عليه اجرا الا المودة في القربى ومن يقترف حسنة نزد له فيها حسنا ان الله غفور شكور');
INSERT INTO "imlaei_simple" VALUES(42,24,'42:24','ام يقولون افترى على الله كذبا فان يشا الله يختم على قلبك ويمح الله الباطل ويحق الحق بكلماته انه عليم بذات الصدور');
INSERT INTO "imlaei_simple" VALUES(42,25,'42:25','وهو الذي يقبل التوبة عن عباده ويعفو عن السيئات ويعلم ما تفعلون');
INSERT INTO "imlaei_simple" VALUES(42,26,'42:26','ويستجيب الذين امنوا وعملوا الصالحات ويزيدهم من فضله والكافرون لهم عذاب شديد');
INSERT INTO "imlaei_simple" VALUES(42,27,'42:27','ولو بسط الله الرزق لعباده لبغوا في الارض ولكن ينزل بقدر ما يشاء انه بعباده خبير بصير');
INSERT INTO "imlaei_simple" VALUES(42,28,'42:28','وهو الذي ينزل الغيث من بعد ما قنطوا وينشر رحمته وهو الولي الحميد');
INSERT INTO "imlaei_simple" VALUES(42,29,'42:29','ومن اياته خلق السماوات والارض وما بث فيهما من دابة وهو على جمعهم اذا يشاء قدير');
INSERT INTO "imlaei_simple" VALUES(42,30,'42:30','وما اصابكم من مصيبة فبما كسبت ايديكم ويعفو عن كثير');
INSERT INTO "imlaei_simple" VALUES(42,31,'42:31','وما انتم بمعجزين في الارض وما لكم من دون الله من ولي ولا نصير');
INSERT INTO "imlaei_simple" VALUES(42,32,'42:32','ومن اياته الجوار في البحر كالاعلام');
INSERT INTO "imlaei_simple" VALUES(42,33,'42:33','ان يشا يسكن الريح فيظللن رواكد على ظهره ان في ذلك لايات لكل صبار شكور');
INSERT INTO "imlaei_simple" VALUES(42,34,'42:34','او يوبقهن بما كسبوا ويعف عن كثير');
INSERT INTO "imlaei_simple" VALUES(42,35,'42:35','ويعلم الذين يجادلون في اياتنا ما لهم من محيص');
INSERT INTO "imlaei_simple" VALUES(42,36,'42:36','فما اوتيتم من شيء فمتاع الحياة الدنيا وما عند الله خير وابقى للذين امنوا وعلى ربهم يتوكلون');
INSERT INTO "imlaei_simple" VALUES(42,37,'42:37','والذين يجتنبون كبائر الاثم والفواحش واذا ما غضبوا هم يغفرون');
INSERT INTO "imlaei_simple" VALUES(42,38,'42:38','والذين استجابوا لربهم واقاموا الصلاة وامرهم شورى بينهم ومما رزقناهم ينفقون');
INSERT INTO "imlaei_simple" VALUES(42,39,'42:39','والذين اذا اصابهم البغي هم ينتصرون');
INSERT INTO "imlaei_simple" VALUES(42,40,'42:40','وجزاء سيئة سيئة مثلها فمن عفا واصلح فاجره على الله انه لا يحب الظالمين');
INSERT INTO "imlaei_simple" VALUES(42,41,'42:41','ولمن انتصر بعد ظلمه فاولئك ما عليهم من سبيل');
INSERT INTO "imlaei_simple" VALUES(42,42,'42:42','انما السبيل على الذين يظلمون الناس ويبغون في الارض بغير الحق اولئك لهم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(42,43,'42:43','ولمن صبر وغفر ان ذلك لمن عزم الامور');
INSERT INTO "imlaei_simple" VALUES(42,44,'42:44','ومن يضلل الله فما له من ولي من بعده وترى الظالمين لما راوا العذاب يقولون هل الى مرد من سبيل');
INSERT INTO "imlaei_simple" VALUES(42,45,'42:45','وتراهم يعرضون عليها خاشعين من الذل ينظرون من طرف خفي وقال الذين امنوا ان الخاسرين الذين خسروا انفسهم واهليهم يوم القيامة الا ان الظالمين في عذاب مقيم');
INSERT INTO "imlaei_simple" VALUES(42,46,'42:46','وما كان لهم من اولياء ينصرونهم من دون الله ومن يضلل الله فما له من سبيل');
INSERT INTO "imlaei_simple" VALUES(42,47,'42:47','استجيبوا لربكم من قبل ان ياتي يوم لا مرد له من الله ما لكم من ملجا يومئذ وما لكم من نكير');
INSERT INTO "imlaei_simple" VALUES(42,48,'42:48','فان اعرضوا فما ارسلناك عليهم حفيظا ان عليك الا البلاغ وانا اذا اذقنا الانسان منا رحمة فرح بها وان تصبهم سيئة بما قدمت ايديهم فان الانسان كفور');
INSERT INTO "imlaei_simple" VALUES(42,49,'42:49','لله ملك السماوات والارض يخلق ما يشاء يهب لمن يشاء اناثا ويهب لمن يشاء الذكور');
INSERT INTO "imlaei_simple" VALUES(42,50,'42:50','او يزوجهم ذكرانا واناثا ويجعل من يشاء عقيما انه عليم قدير');
INSERT INTO "imlaei_simple" VALUES(42,51,'42:51','وما كان لبشر ان يكلمه الله الا وحيا او من وراء حجاب او يرسل رسولا فيوحي باذنه ما يشاء انه علي حكيم');
INSERT INTO "imlaei_simple" VALUES(42,52,'42:52','وكذلك اوحينا اليك روحا من امرنا ما كنت تدري ما الكتاب ولا الايمان ولكن جعلناه نورا نهدي به من نشاء من عبادنا وانك لتهدي الى صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(42,53,'42:53','صراط الله الذي له ما في السماوات وما في الارض الا الى الله تصير الامور');
INSERT INTO "imlaei_simple" VALUES(43,1,'43:1','حم');
INSERT INTO "imlaei_simple" VALUES(43,2,'43:2','والكتاب المبين');
INSERT INTO "imlaei_simple" VALUES(43,3,'43:3','انا جعلناه قرانا عربيا لعلكم تعقلون');
INSERT INTO "imlaei_simple" VALUES(43,4,'43:4','وانه في ام الكتاب لدينا لعلي حكيم');
INSERT INTO "imlaei_simple" VALUES(43,5,'43:5','افنضرب عنكم الذكر صفحا ان كنتم قوما مسرفين');
INSERT INTO "imlaei_simple" VALUES(43,6,'43:6','وكم ارسلنا من نبي في الاولين');
INSERT INTO "imlaei_simple" VALUES(43,7,'43:7','وما ياتيهم من نبي الا كانوا به يستهزئون');
INSERT INTO "imlaei_simple" VALUES(43,8,'43:8','فاهلكنا اشد منهم بطشا ومضى مثل الاولين');
INSERT INTO "imlaei_simple" VALUES(43,9,'43:9','ولئن سالتهم من خلق السماوات والارض ليقولن خلقهن العزيز العليم');
INSERT INTO "imlaei_simple" VALUES(43,10,'43:10','الذي جعل لكم الارض مهدا وجعل لكم فيها سبلا لعلكم تهتدون');
INSERT INTO "imlaei_simple" VALUES(43,11,'43:11','والذي نزل من السماء ماء بقدر فانشرنا به بلدة ميتا كذلك تخرجون');
INSERT INTO "imlaei_simple" VALUES(43,12,'43:12','والذي خلق الازواج كلها وجعل لكم من الفلك والانعام ما تركبون');
INSERT INTO "imlaei_simple" VALUES(43,13,'43:13','لتستووا على ظهوره ثم تذكروا نعمة ربكم اذا استويتم عليه وتقولوا سبحان الذي سخر لنا هذا وما كنا له مقرنين');
INSERT INTO "imlaei_simple" VALUES(43,14,'43:14','وانا الى ربنا لمنقلبون');
INSERT INTO "imlaei_simple" VALUES(43,15,'43:15','وجعلوا له من عباده جزءا ان الانسان لكفور مبين');
INSERT INTO "imlaei_simple" VALUES(43,16,'43:16','ام اتخذ مما يخلق بنات واصفاكم بالبنين');
INSERT INTO "imlaei_simple" VALUES(43,17,'43:17','واذا بشر احدهم بما ضرب للرحمن مثلا ظل وجهه مسودا وهو كظيم');
INSERT INTO "imlaei_simple" VALUES(43,18,'43:18','اومن ينشا في الحلية وهو في الخصام غير مبين');
INSERT INTO "imlaei_simple" VALUES(43,19,'43:19','وجعلوا الملائكة الذين هم عباد الرحمن اناثا اشهدوا خلقهم ستكتب شهادتهم ويسالون');
INSERT INTO "imlaei_simple" VALUES(43,20,'43:20','وقالوا لو شاء الرحمن ما عبدناهم ما لهم بذلك من علم ان هم الا يخرصون');
INSERT INTO "imlaei_simple" VALUES(43,21,'43:21','ام اتيناهم كتابا من قبله فهم به مستمسكون');
INSERT INTO "imlaei_simple" VALUES(43,22,'43:22','بل قالوا انا وجدنا اباءنا على امة وانا على اثارهم مهتدون');
INSERT INTO "imlaei_simple" VALUES(43,23,'43:23','وكذلك ما ارسلنا من قبلك في قرية من نذير الا قال مترفوها انا وجدنا اباءنا على امة وانا على اثارهم مقتدون');
INSERT INTO "imlaei_simple" VALUES(43,24,'43:24','قال اولو جئتكم باهدى مما وجدتم عليه اباءكم قالوا انا بما ارسلتم به كافرون');
INSERT INTO "imlaei_simple" VALUES(43,25,'43:25','فانتقمنا منهم فانظر كيف كان عاقبة المكذبين');
INSERT INTO "imlaei_simple" VALUES(43,26,'43:26','واذ قال ابراهيم لابيه وقومه انني براء مما تعبدون');
INSERT INTO "imlaei_simple" VALUES(43,27,'43:27','الا الذي فطرني فانه سيهدين');
INSERT INTO "imlaei_simple" VALUES(43,28,'43:28','وجعلها كلمة باقية في عقبه لعلهم يرجعون');
INSERT INTO "imlaei_simple" VALUES(43,29,'43:29','بل متعت هؤلاء واباءهم حتى جاءهم الحق ورسول مبين');
INSERT INTO "imlaei_simple" VALUES(43,30,'43:30','ولما جاءهم الحق قالوا هذا سحر وانا به كافرون');
INSERT INTO "imlaei_simple" VALUES(43,31,'43:31','وقالوا لولا نزل هذا القران على رجل من القريتين عظيم');
INSERT INTO "imlaei_simple" VALUES(43,32,'43:32','اهم يقسمون رحمت ربك نحن قسمنا بينهم معيشتهم في الحياة الدنيا ورفعنا بعضهم فوق بعض درجات ليتخذ بعضهم بعضا سخريا ورحمت ربك خير مما يجمعون');
INSERT INTO "imlaei_simple" VALUES(43,33,'43:33','ولولا ان يكون الناس امة واحدة لجعلنا لمن يكفر بالرحمن لبيوتهم سقفا من فضة ومعارج عليها يظهرون');
INSERT INTO "imlaei_simple" VALUES(43,34,'43:34','ولبيوتهم ابوابا وسررا عليها يتكئون');
INSERT INTO "imlaei_simple" VALUES(43,35,'43:35','وزخرفا وان كل ذلك لما متاع الحياة الدنيا والاخرة عند ربك للمتقين');
INSERT INTO "imlaei_simple" VALUES(43,36,'43:36','ومن يعش عن ذكر الرحمن نقيض له شيطانا فهو له قرين');
INSERT INTO "imlaei_simple" VALUES(43,37,'43:37','وانهم ليصدونهم عن السبيل ويحسبون انهم مهتدون');
INSERT INTO "imlaei_simple" VALUES(43,38,'43:38','حتى اذا جاءنا قال يا ليت بيني وبينك بعد المشرقين فبئس القرين');
INSERT INTO "imlaei_simple" VALUES(43,39,'43:39','ولن ينفعكم اليوم اذ ظلمتم انكم في العذاب مشتركون');
INSERT INTO "imlaei_simple" VALUES(43,40,'43:40','افانت تسمع الصم او تهدي العمي ومن كان في ضلال مبين');
INSERT INTO "imlaei_simple" VALUES(43,41,'43:41','فاما نذهبن بك فانا منهم منتقمون');
INSERT INTO "imlaei_simple" VALUES(43,42,'43:42','او نرينك الذي وعدناهم فانا عليهم مقتدرون');
INSERT INTO "imlaei_simple" VALUES(43,43,'43:43','فاستمسك بالذي اوحي اليك انك على صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(43,44,'43:44','وانه لذكر لك ولقومك وسوف تسالون');
INSERT INTO "imlaei_simple" VALUES(43,45,'43:45','واسال من ارسلنا من قبلك من رسلنا اجعلنا من دون الرحمن الهة يعبدون');
INSERT INTO "imlaei_simple" VALUES(43,46,'43:46','ولقد ارسلنا موسى باياتنا الى فرعون وملئه فقال اني رسول رب العالمين');
INSERT INTO "imlaei_simple" VALUES(43,47,'43:47','فلما جاءهم باياتنا اذا هم منها يضحكون');
INSERT INTO "imlaei_simple" VALUES(43,48,'43:48','وما نريهم من اية الا هي اكبر من اختها واخذناهم بالعذاب لعلهم يرجعون');
INSERT INTO "imlaei_simple" VALUES(43,49,'43:49','وقالوا يا ايه الساحر ادع لنا ربك بما عهد عندك اننا لمهتدون');
INSERT INTO "imlaei_simple" VALUES(43,50,'43:50','فلما كشفنا عنهم العذاب اذا هم ينكثون');
INSERT INTO "imlaei_simple" VALUES(43,51,'43:51','ونادى فرعون في قومه قال يا قوم اليس لي ملك مصر وهذه الانهار تجري من تحتي افلا تبصرون');
INSERT INTO "imlaei_simple" VALUES(43,52,'43:52','ام انا خير من هذا الذي هو مهين ولا يكاد يبين');
INSERT INTO "imlaei_simple" VALUES(43,53,'43:53','فلولا القي عليه اسورة من ذهب او جاء معه الملائكة مقترنين');
INSERT INTO "imlaei_simple" VALUES(43,54,'43:54','فاستخف قومه فاطاعوه انهم كانوا قوما فاسقين');
INSERT INTO "imlaei_simple" VALUES(43,55,'43:55','فلما اسفونا انتقمنا منهم فاغرقناهم اجمعين');
INSERT INTO "imlaei_simple" VALUES(43,56,'43:56','فجعلناهم سلفا ومثلا للاخرين');
INSERT INTO "imlaei_simple" VALUES(43,57,'43:57','ولما ضرب ابن مريم مثلا اذا قومك منه يصدون');
INSERT INTO "imlaei_simple" VALUES(43,58,'43:58','وقالوا االهتنا خير ام هو ما ضربوه لك الا جدلا بل هم قوم خصمون');
INSERT INTO "imlaei_simple" VALUES(43,59,'43:59','ان هو الا عبد انعمنا عليه وجعلناه مثلا لبني اسرائيل');
INSERT INTO "imlaei_simple" VALUES(43,60,'43:60','ولو نشاء لجعلنا منكم ملائكة في الارض يخلفون');
INSERT INTO "imlaei_simple" VALUES(43,61,'43:61','وانه لعلم للساعة فلا تمترن بها واتبعون هذا صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(43,62,'43:62','ولا يصدنكم الشيطان انه لكم عدو مبين');
INSERT INTO "imlaei_simple" VALUES(43,63,'43:63','ولما جاء عيسى بالبينات قال قد جئتكم بالحكمة ولابين لكم بعض الذي تختلفون فيه فاتقوا الله واطيعون');
INSERT INTO "imlaei_simple" VALUES(43,64,'43:64','ان الله هو ربي وربكم فاعبدوه هذا صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(43,65,'43:65','فاختلف الاحزاب من بينهم فويل للذين ظلموا من عذاب يوم اليم');
INSERT INTO "imlaei_simple" VALUES(43,66,'43:66','هل ينظرون الا الساعة ان تاتيهم بغتة وهم لا يشعرون');
INSERT INTO "imlaei_simple" VALUES(43,67,'43:67','الاخلاء يومئذ بعضهم لبعض عدو الا المتقين');
INSERT INTO "imlaei_simple" VALUES(43,68,'43:68','يا عباد لا خوف عليكم اليوم ولا انتم تحزنون');
INSERT INTO "imlaei_simple" VALUES(43,69,'43:69','الذين امنوا باياتنا وكانوا مسلمين');
INSERT INTO "imlaei_simple" VALUES(43,70,'43:70','ادخلوا الجنة انتم وازواجكم تحبرون');
INSERT INTO "imlaei_simple" VALUES(43,71,'43:71','يطاف عليهم بصحاف من ذهب واكواب وفيها ما تشتهيه الانفس وتلذ الاعين وانتم فيها خالدون');
INSERT INTO "imlaei_simple" VALUES(43,72,'43:72','وتلك الجنة التي اورثتموها بما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(43,73,'43:73','لكم فيها فاكهة كثيرة منها تاكلون');
INSERT INTO "imlaei_simple" VALUES(43,74,'43:74','ان المجرمين في عذاب جهنم خالدون');
INSERT INTO "imlaei_simple" VALUES(43,75,'43:75','لا يفتر عنهم وهم فيه مبلسون');
INSERT INTO "imlaei_simple" VALUES(43,76,'43:76','وما ظلمناهم ولكن كانوا هم الظالمين');
INSERT INTO "imlaei_simple" VALUES(43,77,'43:77','ونادوا يا مالك ليقض علينا ربك قال انكم ماكثون');
INSERT INTO "imlaei_simple" VALUES(43,78,'43:78','لقد جئناكم بالحق ولكن اكثركم للحق كارهون');
INSERT INTO "imlaei_simple" VALUES(43,79,'43:79','ام ابرموا امرا فانا مبرمون');
INSERT INTO "imlaei_simple" VALUES(43,80,'43:80','ام يحسبون انا لا نسمع سرهم ونجواهم بلى ورسلنا لديهم يكتبون');
INSERT INTO "imlaei_simple" VALUES(43,81,'43:81','قل ان كان للرحمن ولد فانا اول العابدين');
INSERT INTO "imlaei_simple" VALUES(43,82,'43:82','سبحان رب السماوات والارض رب العرش عما يصفون');
INSERT INTO "imlaei_simple" VALUES(43,83,'43:83','فذرهم يخوضوا ويلعبوا حتى يلاقوا يومهم الذي يوعدون');
INSERT INTO "imlaei_simple" VALUES(43,84,'43:84','وهو الذي في السماء اله وفي الارض اله وهو الحكيم العليم');
INSERT INTO "imlaei_simple" VALUES(43,85,'43:85','وتبارك الذي له ملك السماوات والارض وما بينهما وعنده علم الساعة واليه ترجعون');
INSERT INTO "imlaei_simple" VALUES(43,86,'43:86','ولا يملك الذين يدعون من دونه الشفاعة الا من شهد بالحق وهم يعلمون');
INSERT INTO "imlaei_simple" VALUES(43,87,'43:87','ولئن سالتهم من خلقهم ليقولن الله فانى يؤفكون');
INSERT INTO "imlaei_simple" VALUES(43,88,'43:88','وقيله يا رب ان هؤلاء قوم لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(43,89,'43:89','فاصفح عنهم وقل سلام فسوف يعلمون');
INSERT INTO "imlaei_simple" VALUES(44,1,'44:1','حم');
INSERT INTO "imlaei_simple" VALUES(44,2,'44:2','والكتاب المبين');
INSERT INTO "imlaei_simple" VALUES(44,3,'44:3','انا انزلناه في ليلة مباركة انا كنا منذرين');
INSERT INTO "imlaei_simple" VALUES(44,4,'44:4','فيها يفرق كل امر حكيم');
INSERT INTO "imlaei_simple" VALUES(44,5,'44:5','امرا من عندنا انا كنا مرسلين');
INSERT INTO "imlaei_simple" VALUES(44,6,'44:6','رحمة من ربك انه هو السميع العليم');
INSERT INTO "imlaei_simple" VALUES(44,7,'44:7','رب السماوات والارض وما بينهما ان كنتم موقنين');
INSERT INTO "imlaei_simple" VALUES(44,8,'44:8','لا اله الا هو يحيي ويميت ربكم ورب ابائكم الاولين');
INSERT INTO "imlaei_simple" VALUES(44,9,'44:9','بل هم في شك يلعبون');
INSERT INTO "imlaei_simple" VALUES(44,10,'44:10','فارتقب يوم تاتي السماء بدخان مبين');
INSERT INTO "imlaei_simple" VALUES(44,11,'44:11','يغشى الناس هذا عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(44,12,'44:12','ربنا اكشف عنا العذاب انا مؤمنون');
INSERT INTO "imlaei_simple" VALUES(44,13,'44:13','انى لهم الذكرى وقد جاءهم رسول مبين');
INSERT INTO "imlaei_simple" VALUES(44,14,'44:14','ثم تولوا عنه وقالوا معلم مجنون');
INSERT INTO "imlaei_simple" VALUES(44,15,'44:15','انا كاشفو العذاب قليلا انكم عائدون');
INSERT INTO "imlaei_simple" VALUES(44,16,'44:16','يوم نبطش البطشة الكبرى انا منتقمون');
INSERT INTO "imlaei_simple" VALUES(44,17,'44:17','ولقد فتنا قبلهم قوم فرعون وجاءهم رسول كريم');
INSERT INTO "imlaei_simple" VALUES(44,18,'44:18','ان ادوا الي عباد الله اني لكم رسول امين');
INSERT INTO "imlaei_simple" VALUES(44,19,'44:19','وان لا تعلوا على الله اني اتيكم بسلطان مبين');
INSERT INTO "imlaei_simple" VALUES(44,20,'44:20','واني عذت بربي وربكم ان ترجمون');
INSERT INTO "imlaei_simple" VALUES(44,21,'44:21','وان لم تؤمنوا لي فاعتزلون');
INSERT INTO "imlaei_simple" VALUES(44,22,'44:22','فدعا ربه ان هؤلاء قوم مجرمون');
INSERT INTO "imlaei_simple" VALUES(44,23,'44:23','فاسر بعبادي ليلا انكم متبعون');
INSERT INTO "imlaei_simple" VALUES(44,24,'44:24','واترك البحر رهوا انهم جند مغرقون');
INSERT INTO "imlaei_simple" VALUES(44,25,'44:25','كم تركوا من جنات وعيون');
INSERT INTO "imlaei_simple" VALUES(44,26,'44:26','وزروع ومقام كريم');
INSERT INTO "imlaei_simple" VALUES(44,27,'44:27','ونعمة كانوا فيها فاكهين');
INSERT INTO "imlaei_simple" VALUES(44,28,'44:28','كذلك واورثناها قوما اخرين');
INSERT INTO "imlaei_simple" VALUES(44,29,'44:29','فما بكت عليهم السماء والارض وما كانوا منظرين');
INSERT INTO "imlaei_simple" VALUES(44,30,'44:30','ولقد نجينا بني اسرائيل من العذاب المهين');
INSERT INTO "imlaei_simple" VALUES(44,31,'44:31','من فرعون انه كان عاليا من المسرفين');
INSERT INTO "imlaei_simple" VALUES(44,32,'44:32','ولقد اخترناهم على علم على العالمين');
INSERT INTO "imlaei_simple" VALUES(44,33,'44:33','واتيناهم من الايات ما فيه بلاء مبين');
INSERT INTO "imlaei_simple" VALUES(44,34,'44:34','ان هؤلاء ليقولون');
INSERT INTO "imlaei_simple" VALUES(44,35,'44:35','ان هي الا موتتنا الاولى وما نحن بمنشرين');
INSERT INTO "imlaei_simple" VALUES(44,36,'44:36','فاتوا بابائنا ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(44,37,'44:37','اهم خير ام قوم تبع والذين من قبلهم اهلكناهم انهم كانوا مجرمين');
INSERT INTO "imlaei_simple" VALUES(44,38,'44:38','وما خلقنا السماوات والارض وما بينهما لاعبين');
INSERT INTO "imlaei_simple" VALUES(44,39,'44:39','ما خلقناهما الا بالحق ولكن اكثرهم لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(44,40,'44:40','ان يوم الفصل ميقاتهم اجمعين');
INSERT INTO "imlaei_simple" VALUES(44,41,'44:41','يوم لا يغني مولى عن مولى شيئا ولا هم ينصرون');
INSERT INTO "imlaei_simple" VALUES(44,42,'44:42','الا من رحم الله انه هو العزيز الرحيم');
INSERT INTO "imlaei_simple" VALUES(44,43,'44:43','ان شجرت الزقوم');
INSERT INTO "imlaei_simple" VALUES(44,44,'44:44','طعام الاثيم');
INSERT INTO "imlaei_simple" VALUES(44,45,'44:45','كالمهل يغلي في البطون');
INSERT INTO "imlaei_simple" VALUES(44,46,'44:46','كغلي الحميم');
INSERT INTO "imlaei_simple" VALUES(44,47,'44:47','خذوه فاعتلوه الى سواء الجحيم');
INSERT INTO "imlaei_simple" VALUES(44,48,'44:48','ثم صبوا فوق راسه من عذاب الحميم');
INSERT INTO "imlaei_simple" VALUES(44,49,'44:49','ذق انك انت العزيز الكريم');
INSERT INTO "imlaei_simple" VALUES(44,50,'44:50','ان هذا ما كنتم به تمترون');
INSERT INTO "imlaei_simple" VALUES(44,51,'44:51','ان المتقين في مقام امين');
INSERT INTO "imlaei_simple" VALUES(44,52,'44:52','في جنات وعيون');
INSERT INTO "imlaei_simple" VALUES(44,53,'44:53','يلبسون من سندس واستبرق متقابلين');
INSERT INTO "imlaei_simple" VALUES(44,54,'44:54','كذلك وزوجناهم بحور عين');
INSERT INTO "imlaei_simple" VALUES(44,55,'44:55','يدعون فيها بكل فاكهة امنين');
INSERT INTO "imlaei_simple" VALUES(44,56,'44:56','لا يذوقون فيها الموت الا الموتة الاولى ووقاهم عذاب الجحيم');
INSERT INTO "imlaei_simple" VALUES(44,57,'44:57','فضلا من ربك ذلك هو الفوز العظيم');
INSERT INTO "imlaei_simple" VALUES(44,58,'44:58','فانما يسرناه بلسانك لعلهم يتذكرون');
INSERT INTO "imlaei_simple" VALUES(44,59,'44:59','فارتقب انهم مرتقبون');
INSERT INTO "imlaei_simple" VALUES(45,1,'45:1','حم');
INSERT INTO "imlaei_simple" VALUES(45,2,'45:2','تنزيل الكتاب من الله العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(45,3,'45:3','ان في السماوات والارض لايات للمؤمنين');
INSERT INTO "imlaei_simple" VALUES(45,4,'45:4','وفي خلقكم وما يبث من دابة ايات لقوم يوقنون');
INSERT INTO "imlaei_simple" VALUES(45,5,'45:5','واختلاف الليل والنهار وما انزل الله من السماء من رزق فاحيا به الارض بعد موتها وتصريف الرياح ايات لقوم يعقلون');
INSERT INTO "imlaei_simple" VALUES(45,6,'45:6','تلك ايات الله نتلوها عليك بالحق فباي حديث بعد الله واياته يؤمنون');
INSERT INTO "imlaei_simple" VALUES(45,7,'45:7','ويل لكل افاك اثيم');
INSERT INTO "imlaei_simple" VALUES(45,8,'45:8','يسمع ايات الله تتلى عليه ثم يصر مستكبرا كان لم يسمعها فبشره بعذاب اليم');
INSERT INTO "imlaei_simple" VALUES(45,9,'45:9','واذا علم من اياتنا شيئا اتخذها هزوا اولئك لهم عذاب مهين');
INSERT INTO "imlaei_simple" VALUES(45,10,'45:10','من ورائهم جهنم ولا يغني عنهم ما كسبوا شيئا ولا ما اتخذوا من دون الله اولياء ولهم عذاب عظيم');
INSERT INTO "imlaei_simple" VALUES(45,11,'45:11','هذا هدى والذين كفروا بايات ربهم لهم عذاب من رجز اليم');
INSERT INTO "imlaei_simple" VALUES(45,12,'45:12','الله الذي سخر لكم البحر لتجري الفلك فيه بامره ولتبتغوا من فضله ولعلكم تشكرون');
INSERT INTO "imlaei_simple" VALUES(45,13,'45:13','وسخر لكم ما في السماوات وما في الارض جميعا منه ان في ذلك لايات لقوم يتفكرون');
INSERT INTO "imlaei_simple" VALUES(45,14,'45:14','قل للذين امنوا يغفروا للذين لا يرجون ايام الله ليجزي قوما بما كانوا يكسبون');
INSERT INTO "imlaei_simple" VALUES(45,15,'45:15','من عمل صالحا فلنفسه ومن اساء فعليها ثم الى ربكم ترجعون');
INSERT INTO "imlaei_simple" VALUES(45,16,'45:16','ولقد اتينا بني اسرائيل الكتاب والحكم والنبوة ورزقناهم من الطيبات وفضلناهم على العالمين');
INSERT INTO "imlaei_simple" VALUES(45,17,'45:17','واتيناهم بينات من الامر فما اختلفوا الا من بعد ما جاءهم العلم بغيا بينهم ان ربك يقضي بينهم يوم القيامة فيما كانوا فيه يختلفون');
INSERT INTO "imlaei_simple" VALUES(45,18,'45:18','ثم جعلناك على شريعة من الامر فاتبعها ولا تتبع اهواء الذين لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(45,19,'45:19','انهم لن يغنوا عنك من الله شيئا وان الظالمين بعضهم اولياء بعض والله ولي المتقين');
INSERT INTO "imlaei_simple" VALUES(45,20,'45:20','هذا بصائر للناس وهدى ورحمة لقوم يوقنون');
INSERT INTO "imlaei_simple" VALUES(45,21,'45:21','ام حسب الذين اجترحوا السيئات ان نجعلهم كالذين امنوا وعملوا الصالحات سواء محياهم ومماتهم ساء ما يحكمون');
INSERT INTO "imlaei_simple" VALUES(45,22,'45:22','وخلق الله السماوات والارض بالحق ولتجزى كل نفس بما كسبت وهم لا يظلمون');
INSERT INTO "imlaei_simple" VALUES(45,23,'45:23','افرايت من اتخذ الهه هواه واضله الله على علم وختم على سمعه وقلبه وجعل على بصره غشاوة فمن يهديه من بعد الله افلا تذكرون');
INSERT INTO "imlaei_simple" VALUES(45,24,'45:24','وقالوا ما هي الا حياتنا الدنيا نموت ونحيا وما يهلكنا الا الدهر وما لهم بذلك من علم ان هم الا يظنون');
INSERT INTO "imlaei_simple" VALUES(45,25,'45:25','واذا تتلى عليهم اياتنا بينات ما كان حجتهم الا ان قالوا ائتوا بابائنا ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(45,26,'45:26','قل الله يحييكم ثم يميتكم ثم يجمعكم الى يوم القيامة لا ريب فيه ولكن اكثر الناس لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(45,27,'45:27','ولله ملك السماوات والارض ويوم تقوم الساعة يومئذ يخسر المبطلون');
INSERT INTO "imlaei_simple" VALUES(45,28,'45:28','وترى كل امة جاثية كل امة تدعى الى كتابها اليوم تجزون ما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(45,29,'45:29','هذا كتابنا ينطق عليكم بالحق انا كنا نستنسخ ما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(45,30,'45:30','فاما الذين امنوا وعملوا الصالحات فيدخلهم ربهم في رحمته ذلك هو الفوز المبين');
INSERT INTO "imlaei_simple" VALUES(45,31,'45:31','واما الذين كفروا افلم تكن اياتي تتلى عليكم فاستكبرتم وكنتم قوما مجرمين');
INSERT INTO "imlaei_simple" VALUES(45,32,'45:32','واذا قيل ان وعد الله حق والساعة لا ريب فيها قلتم ما ندري ما الساعة ان نظن الا ظنا وما نحن بمستيقنين');
INSERT INTO "imlaei_simple" VALUES(45,33,'45:33','وبدا لهم سيئات ما عملوا وحاق بهم ما كانوا به يستهزئون');
INSERT INTO "imlaei_simple" VALUES(45,34,'45:34','وقيل اليوم ننساكم كما نسيتم لقاء يومكم هذا وماواكم النار وما لكم من ناصرين');
INSERT INTO "imlaei_simple" VALUES(45,35,'45:35','ذلكم بانكم اتخذتم ايات الله هزوا وغرتكم الحياة الدنيا فاليوم لا يخرجون منها ولا هم يستعتبون');
INSERT INTO "imlaei_simple" VALUES(45,36,'45:36','فلله الحمد رب السماوات ورب الارض رب العالمين');
INSERT INTO "imlaei_simple" VALUES(45,37,'45:37','وله الكبرياء في السماوات والارض وهو العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(46,1,'46:1','حم');
INSERT INTO "imlaei_simple" VALUES(46,2,'46:2','تنزيل الكتاب من الله العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(46,3,'46:3','ما خلقنا السماوات والارض وما بينهما الا بالحق واجل مسمى والذين كفروا عما انذروا معرضون');
INSERT INTO "imlaei_simple" VALUES(46,4,'46:4','قل ارايتم ما تدعون من دون الله اروني ماذا خلقوا من الارض ام لهم شرك في السماوات ائتوني بكتاب من قبل هذا او اثارة من علم ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(46,5,'46:5','ومن اضل ممن يدعو من دون الله من لا يستجيب له الى يوم القيامة وهم عن دعائهم غافلون');
INSERT INTO "imlaei_simple" VALUES(46,6,'46:6','واذا حشر الناس كانوا لهم اعداء وكانوا بعبادتهم كافرين');
INSERT INTO "imlaei_simple" VALUES(46,7,'46:7','واذا تتلى عليهم اياتنا بينات قال الذين كفروا للحق لما جاءهم هذا سحر مبين');
INSERT INTO "imlaei_simple" VALUES(46,8,'46:8','ام يقولون افتراه قل ان افتريته فلا تملكون لي من الله شيئا هو اعلم بما تفيضون فيه كفى به شهيدا بيني وبينكم وهو الغفور الرحيم');
INSERT INTO "imlaei_simple" VALUES(46,9,'46:9','قل ما كنت بدعا من الرسل وما ادري ما يفعل بي ولا بكم ان اتبع الا ما يوحى الي وما انا الا نذير مبين');
INSERT INTO "imlaei_simple" VALUES(46,10,'46:10','قل ارايتم ان كان من عند الله وكفرتم به وشهد شاهد من بني اسرائيل على مثله فامن واستكبرتم ان الله لا يهدي القوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(46,11,'46:11','وقال الذين كفروا للذين امنوا لو كان خيرا ما سبقونا اليه واذ لم يهتدوا به فسيقولون هذا افك قديم');
INSERT INTO "imlaei_simple" VALUES(46,12,'46:12','ومن قبله كتاب موسى اماما ورحمة وهذا كتاب مصدق لسانا عربيا لينذر الذين ظلموا وبشرى للمحسنين');
INSERT INTO "imlaei_simple" VALUES(46,13,'46:13','ان الذين قالوا ربنا الله ثم استقاموا فلا خوف عليهم ولا هم يحزنون');
INSERT INTO "imlaei_simple" VALUES(46,14,'46:14','اولئك اصحاب الجنة خالدين فيها جزاء بما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(46,15,'46:15','ووصينا الانسان بوالديه احسانا حملته امه كرها ووضعته كرها وحمله وفصاله ثلاثون شهرا حتى اذا بلغ اشده وبلغ اربعين سنة قال رب اوزعني ان اشكر نعمتك التي انعمت علي وعلى والدي وان اعمل صالحا ترضاه واصلح لي في ذريتي اني تبت اليك واني من المسلمين');
INSERT INTO "imlaei_simple" VALUES(46,16,'46:16','اولئك الذين نتقبل عنهم احسن ما عملوا ونتجاوز عن سيئاتهم في اصحاب الجنة وعد الصدق الذي كانوا يوعدون');
INSERT INTO "imlaei_simple" VALUES(46,17,'46:17','والذي قال لوالديه اف لكما اتعدانني ان اخرج وقد خلت القرون من قبلي وهما يستغيثان الله ويلك امن ان وعد الله حق فيقول ما هذا الا اساطير الاولين');
INSERT INTO "imlaei_simple" VALUES(46,18,'46:18','اولئك الذين حق عليهم القول في امم قد خلت من قبلهم من الجن والانس انهم كانوا خاسرين');
INSERT INTO "imlaei_simple" VALUES(46,19,'46:19','ولكل درجات مما عملوا وليوفيهم اعمالهم وهم لا يظلمون');
INSERT INTO "imlaei_simple" VALUES(46,20,'46:20','ويوم يعرض الذين كفروا على النار اذهبتم طيباتكم في حياتكم الدنيا واستمتعتم بها فاليوم تجزون عذاب الهون بما كنتم تستكبرون في الارض بغير الحق وبما كنتم تفسقون');
INSERT INTO "imlaei_simple" VALUES(46,21,'46:21','واذكر اخا عاد اذ انذر قومه بالاحقاف وقد خلت النذر من بين يديه ومن خلفه الا تعبدوا الا الله اني اخاف عليكم عذاب يوم عظيم');
INSERT INTO "imlaei_simple" VALUES(46,22,'46:22','قالوا اجئتنا لتافكنا عن الهتنا فاتنا بما تعدنا ان كنت من الصادقين');
INSERT INTO "imlaei_simple" VALUES(46,23,'46:23','قال انما العلم عند الله وابلغكم ما ارسلت به ولكني اراكم قوما تجهلون');
INSERT INTO "imlaei_simple" VALUES(46,24,'46:24','فلما راوه عارضا مستقبل اوديتهم قالوا هذا عارض ممطرنا بل هو ما استعجلتم به ريح فيها عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(46,25,'46:25','تدمر كل شيء بامر ربها فاصبحوا لا يرى الا مساكنهم كذلك نجزي القوم المجرمين');
INSERT INTO "imlaei_simple" VALUES(46,26,'46:26','ولقد مكناهم فيما ان مكناكم فيه وجعلنا لهم سمعا وابصارا وافئدة فما اغنى عنهم سمعهم ولا ابصارهم ولا افئدتهم من شيء اذ كانوا يجحدون بايات الله وحاق بهم ما كانوا به يستهزئون');
INSERT INTO "imlaei_simple" VALUES(46,27,'46:27','ولقد اهلكنا ما حولكم من القرى وصرفنا الايات لعلهم يرجعون');
INSERT INTO "imlaei_simple" VALUES(46,28,'46:28','فلولا نصرهم الذين اتخذوا من دون الله قربانا الهة بل ضلوا عنهم وذلك افكهم وما كانوا يفترون');
INSERT INTO "imlaei_simple" VALUES(46,29,'46:29','واذ صرفنا اليك نفرا من الجن يستمعون القران فلما حضروه قالوا انصتوا فلما قضي ولوا الى قومهم منذرين');
INSERT INTO "imlaei_simple" VALUES(46,30,'46:30','قالوا يا قومنا انا سمعنا كتابا انزل من بعد موسى مصدقا لما بين يديه يهدي الى الحق والى طريق مستقيم');
INSERT INTO "imlaei_simple" VALUES(46,31,'46:31','يا قومنا اجيبوا داعي الله وامنوا به يغفر لكم من ذنوبكم ويجركم من عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(46,32,'46:32','ومن لا يجب داعي الله فليس بمعجز في الارض وليس له من دونه اولياء اولئك في ضلال مبين');
INSERT INTO "imlaei_simple" VALUES(46,33,'46:33','اولم يروا ان الله الذي خلق السماوات والارض ولم يعي بخلقهن بقادر على ان يحيي الموتى بلى انه على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(46,34,'46:34','ويوم يعرض الذين كفروا على النار اليس هذا بالحق قالوا بلى وربنا قال فذوقوا العذاب بما كنتم تكفرون');
INSERT INTO "imlaei_simple" VALUES(46,35,'46:35','فاصبر كما صبر اولو العزم من الرسل ولا تستعجل لهم كانهم يوم يرون ما يوعدون لم يلبثوا الا ساعة من نهار بلاغ فهل يهلك الا القوم الفاسقون');
INSERT INTO "imlaei_simple" VALUES(47,1,'47:1','الذين كفروا وصدوا عن سبيل الله اضل اعمالهم');
INSERT INTO "imlaei_simple" VALUES(47,2,'47:2','والذين امنوا وعملوا الصالحات وامنوا بما نزل على محمد وهو الحق من ربهم كفر عنهم سيئاتهم واصلح بالهم');
INSERT INTO "imlaei_simple" VALUES(47,3,'47:3','ذلك بان الذين كفروا اتبعوا الباطل وان الذين امنوا اتبعوا الحق من ربهم كذلك يضرب الله للناس امثالهم');
INSERT INTO "imlaei_simple" VALUES(47,4,'47:4','فاذا لقيتم الذين كفروا فضرب الرقاب حتى اذا اثخنتموهم فشدوا الوثاق فاما منا بعد واما فداء حتى تضع الحرب اوزارها ذلك ولو يشاء الله لانتصر منهم ولكن ليبلو بعضكم ببعض والذين قتلوا في سبيل الله فلن يضل اعمالهم');
INSERT INTO "imlaei_simple" VALUES(47,5,'47:5','سيهديهم ويصلح بالهم');
INSERT INTO "imlaei_simple" VALUES(47,6,'47:6','ويدخلهم الجنة عرفها لهم');
INSERT INTO "imlaei_simple" VALUES(47,7,'47:7','يا ايها الذين امنوا ان تنصروا الله ينصركم ويثبت اقدامكم');
INSERT INTO "imlaei_simple" VALUES(47,8,'47:8','والذين كفروا فتعسا لهم واضل اعمالهم');
INSERT INTO "imlaei_simple" VALUES(47,9,'47:9','ذلك بانهم كرهوا ما انزل الله فاحبط اعمالهم');
INSERT INTO "imlaei_simple" VALUES(47,10,'47:10','افلم يسيروا في الارض فينظروا كيف كان عاقبة الذين من قبلهم دمر الله عليهم وللكافرين امثالها');
INSERT INTO "imlaei_simple" VALUES(47,11,'47:11','ذلك بان الله مولى الذين امنوا وان الكافرين لا مولى لهم');
INSERT INTO "imlaei_simple" VALUES(47,12,'47:12','ان الله يدخل الذين امنوا وعملوا الصالحات جنات تجري من تحتها الانهار والذين كفروا يتمتعون وياكلون كما تاكل الانعام والنار مثوى لهم');
INSERT INTO "imlaei_simple" VALUES(47,13,'47:13','وكاين من قرية هي اشد قوة من قريتك التي اخرجتك اهلكناهم فلا ناصر لهم');
INSERT INTO "imlaei_simple" VALUES(47,14,'47:14','افمن كان على بينة من ربه كمن زين له سوء عمله واتبعوا اهواءهم');
INSERT INTO "imlaei_simple" VALUES(47,15,'47:15','مثل الجنة التي وعد المتقون فيها انهار من ماء غير اسن وانهار من لبن لم يتغير طعمه وانهار من خمر لذة للشاربين وانهار من عسل مصفى ولهم فيها من كل الثمرات ومغفرة من ربهم كمن هو خالد في النار وسقوا ماء حميما فقطع امعاءهم');
INSERT INTO "imlaei_simple" VALUES(47,16,'47:16','ومنهم من يستمع اليك حتى اذا خرجوا من عندك قالوا للذين اوتوا العلم ماذا قال انفا اولئك الذين طبع الله على قلوبهم واتبعوا اهواءهم');
INSERT INTO "imlaei_simple" VALUES(47,17,'47:17','والذين اهتدوا زادهم هدى واتاهم تقواهم');
INSERT INTO "imlaei_simple" VALUES(47,18,'47:18','فهل ينظرون الا الساعة ان تاتيهم بغتة فقد جاء اشراطها فانى لهم اذا جاءتهم ذكراهم');
INSERT INTO "imlaei_simple" VALUES(47,19,'47:19','فاعلم انه لا اله الا الله واستغفر لذنبك وللمؤمنين والمؤمنات والله يعلم متقلبكم ومثواكم');
INSERT INTO "imlaei_simple" VALUES(47,20,'47:20','ويقول الذين امنوا لولا نزلت سورة فاذا انزلت سورة محكمة وذكر فيها القتال رايت الذين في قلوبهم مرض ينظرون اليك نظر المغشي عليه من الموت فاولى لهم');
INSERT INTO "imlaei_simple" VALUES(47,21,'47:21','طاعة وقول معروف فاذا عزم الامر فلو صدقوا الله لكان خيرا لهم');
INSERT INTO "imlaei_simple" VALUES(47,22,'47:22','فهل عسيتم ان توليتم ان تفسدوا في الارض وتقطعوا ارحامكم');
INSERT INTO "imlaei_simple" VALUES(47,23,'47:23','اولئك الذين لعنهم الله فاصمهم واعمى ابصارهم');
INSERT INTO "imlaei_simple" VALUES(47,24,'47:24','افلا يتدبرون القران ام على قلوب اقفالها');
INSERT INTO "imlaei_simple" VALUES(47,25,'47:25','ان الذين ارتدوا على ادبارهم من بعد ما تبين لهم الهدى الشيطان سول لهم واملى لهم');
INSERT INTO "imlaei_simple" VALUES(47,26,'47:26','ذلك بانهم قالوا للذين كرهوا ما نزل الله سنطيعكم في بعض الامر والله يعلم اسرارهم');
INSERT INTO "imlaei_simple" VALUES(47,27,'47:27','فكيف اذا توفتهم الملائكة يضربون وجوههم وادبارهم');
INSERT INTO "imlaei_simple" VALUES(47,28,'47:28','ذلك بانهم اتبعوا ما اسخط الله وكرهوا رضوانه فاحبط اعمالهم');
INSERT INTO "imlaei_simple" VALUES(47,29,'47:29','ام حسب الذين في قلوبهم مرض ان لن يخرج الله اضغانهم');
INSERT INTO "imlaei_simple" VALUES(47,30,'47:30','ولو نشاء لاريناكهم فلعرفتهم بسيماهم ولتعرفنهم في لحن القول والله يعلم اعمالكم');
INSERT INTO "imlaei_simple" VALUES(47,31,'47:31','ولنبلونكم حتى نعلم المجاهدين منكم والصابرين ونبلو اخباركم');
INSERT INTO "imlaei_simple" VALUES(47,32,'47:32','ان الذين كفروا وصدوا عن سبيل الله وشاقوا الرسول من بعد ما تبين لهم الهدى لن يضروا الله شيئا وسيحبط اعمالهم');
INSERT INTO "imlaei_simple" VALUES(47,33,'47:33','يا ايها الذين امنوا اطيعوا الله واطيعوا الرسول ولا تبطلوا اعمالكم');
INSERT INTO "imlaei_simple" VALUES(47,34,'47:34','ان الذين كفروا وصدوا عن سبيل الله ثم ماتوا وهم كفار فلن يغفر الله لهم');
INSERT INTO "imlaei_simple" VALUES(47,35,'47:35','فلا تهنوا وتدعوا الى السلم وانتم الاعلون والله معكم ولن يتركم اعمالكم');
INSERT INTO "imlaei_simple" VALUES(47,36,'47:36','انما الحياة الدنيا لعب ولهو وان تؤمنوا وتتقوا يؤتكم اجوركم ولا يسالكم اموالكم');
INSERT INTO "imlaei_simple" VALUES(47,37,'47:37','ان يسالكموها فيحفكم تبخلوا ويخرج اضغانكم');
INSERT INTO "imlaei_simple" VALUES(47,38,'47:38','ها انتم هؤلاء تدعون لتنفقوا في سبيل الله فمنكم من يبخل ومن يبخل فانما يبخل عن نفسه والله الغني وانتم الفقراء وان تتولوا يستبدل قوما غيركم ثم لا يكونوا امثالكم');
INSERT INTO "imlaei_simple" VALUES(48,1,'48:1','انا فتحنا لك فتحا مبينا');
INSERT INTO "imlaei_simple" VALUES(48,2,'48:2','ليغفر لك الله ما تقدم من ذنبك وما تاخر ويتم نعمته عليك ويهديك صراطا مستقيما');
INSERT INTO "imlaei_simple" VALUES(48,3,'48:3','وينصرك الله نصرا عزيزا');
INSERT INTO "imlaei_simple" VALUES(48,4,'48:4','هو الذي انزل السكينة في قلوب المؤمنين ليزدادوا ايمانا مع ايمانهم ولله جنود السماوات والارض وكان الله عليما حكيما');
INSERT INTO "imlaei_simple" VALUES(48,5,'48:5','ليدخل المؤمنين والمؤمنات جنات تجري من تحتها الانهار خالدين فيها ويكفر عنهم سيئاتهم وكان ذلك عند الله فوزا عظيما');
INSERT INTO "imlaei_simple" VALUES(48,6,'48:6','ويعذب المنافقين والمنافقات والمشركين والمشركات الظانين بالله ظن السوء عليهم دائرة السوء وغضب الله عليهم ولعنهم واعد لهم جهنم وساءت مصيرا');
INSERT INTO "imlaei_simple" VALUES(48,7,'48:7','ولله جنود السماوات والارض وكان الله عزيزا حكيما');
INSERT INTO "imlaei_simple" VALUES(48,8,'48:8','انا ارسلناك شاهدا ومبشرا ونذيرا');
INSERT INTO "imlaei_simple" VALUES(48,9,'48:9','لتؤمنوا بالله ورسوله وتعزروه وتوقروه وتسبحوه بكرة واصيلا');
INSERT INTO "imlaei_simple" VALUES(48,10,'48:10','ان الذين يبايعونك انما يبايعون الله يد الله فوق ايديهم فمن نكث فانما ينكث على نفسه ومن اوفى بما عاهد عليه الله فسيؤتيه اجرا عظيما');
INSERT INTO "imlaei_simple" VALUES(48,11,'48:11','سيقول لك المخلفون من الاعراب شغلتنا اموالنا واهلونا فاستغفر لنا يقولون بالسنتهم ما ليس في قلوبهم قل فمن يملك لكم من الله شيئا ان اراد بكم ضرا او اراد بكم نفعا بل كان الله بما تعملون خبيرا');
INSERT INTO "imlaei_simple" VALUES(48,12,'48:12','بل ظننتم ان لن ينقلب الرسول والمؤمنون الى اهليهم ابدا وزين ذلك في قلوبكم وظننتم ظن السوء وكنتم قوما بورا');
INSERT INTO "imlaei_simple" VALUES(48,13,'48:13','ومن لم يؤمن بالله ورسوله فانا اعتدنا للكافرين سعيرا');
INSERT INTO "imlaei_simple" VALUES(48,14,'48:14','ولله ملك السماوات والارض يغفر لمن يشاء ويعذب من يشاء وكان الله غفورا رحيما');
INSERT INTO "imlaei_simple" VALUES(48,15,'48:15','سيقول المخلفون اذا انطلقتم الى مغانم لتاخذوها ذرونا نتبعكم يريدون ان يبدلوا كلام الله قل لن تتبعونا كذلكم قال الله من قبل فسيقولون بل تحسدوننا بل كانوا لا يفقهون الا قليلا');
INSERT INTO "imlaei_simple" VALUES(48,16,'48:16','قل للمخلفين من الاعراب ستدعون الى قوم اولي باس شديد تقاتلونهم او يسلمون فان تطيعوا يؤتكم الله اجرا حسنا وان تتولوا كما توليتم من قبل يعذبكم عذابا اليما');
INSERT INTO "imlaei_simple" VALUES(48,17,'48:17','ليس على الاعمى حرج ولا على الاعرج حرج ولا على المريض حرج ومن يطع الله ورسوله يدخله جنات تجري من تحتها الانهار ومن يتول يعذبه عذابا اليما');
INSERT INTO "imlaei_simple" VALUES(48,18,'48:18','لقد رضي الله عن المؤمنين اذ يبايعونك تحت الشجرة فعلم ما في قلوبهم فانزل السكينة عليهم واثابهم فتحا قريبا');
INSERT INTO "imlaei_simple" VALUES(48,19,'48:19','ومغانم كثيرة ياخذونها وكان الله عزيزا حكيما');
INSERT INTO "imlaei_simple" VALUES(48,20,'48:20','وعدكم الله مغانم كثيرة تاخذونها فعجل لكم هذه وكف ايدي الناس عنكم ولتكون اية للمؤمنين ويهديكم صراطا مستقيما');
INSERT INTO "imlaei_simple" VALUES(48,21,'48:21','واخرى لم تقدروا عليها قد احاط الله بها وكان الله على كل شيء قديرا');
INSERT INTO "imlaei_simple" VALUES(48,22,'48:22','ولو قاتلكم الذين كفروا لولوا الادبار ثم لا يجدون وليا ولا نصيرا');
INSERT INTO "imlaei_simple" VALUES(48,23,'48:23','سنة الله التي قد خلت من قبل ولن تجد لسنة الله تبديلا');
INSERT INTO "imlaei_simple" VALUES(48,24,'48:24','وهو الذي كف ايديهم عنكم وايديكم عنهم ببطن مكة من بعد ان اظفركم عليهم وكان الله بما تعملون بصيرا');
INSERT INTO "imlaei_simple" VALUES(48,25,'48:25','هم الذين كفروا وصدوكم عن المسجد الحرام والهدي معكوفا ان يبلغ محله ولولا رجال مؤمنون ونساء مؤمنات لم تعلموهم ان تطئوهم فتصيبكم منهم معرة بغير علم ليدخل الله في رحمته من يشاء لو تزيلوا لعذبنا الذين كفروا منهم عذابا اليما');
INSERT INTO "imlaei_simple" VALUES(48,26,'48:26','اذ جعل الذين كفروا في قلوبهم الحمية حمية الجاهلية فانزل الله سكينته على رسوله وعلى المؤمنين والزمهم كلمة التقوى وكانوا احق بها واهلها وكان الله بكل شيء عليما');
INSERT INTO "imlaei_simple" VALUES(48,27,'48:27','لقد صدق الله رسوله الرؤيا بالحق لتدخلن المسجد الحرام ان شاء الله امنين محلقين رءوسكم ومقصرين لا تخافون فعلم ما لم تعلموا فجعل من دون ذلك فتحا قريبا');
INSERT INTO "imlaei_simple" VALUES(48,28,'48:28','هو الذي ارسل رسوله بالهدى ودين الحق ليظهره على الدين كله وكفى بالله شهيدا');
INSERT INTO "imlaei_simple" VALUES(48,29,'48:29','محمد رسول الله والذين معه اشداء على الكفار رحماء بينهم تراهم ركعا سجدا يبتغون فضلا من الله ورضوانا سيماهم في وجوههم من اثر السجود ذلك مثلهم في التوراة ومثلهم في الانجيل كزرع اخرج شطاه فازره فاستغلظ فاستوى على سوقه يعجب الزراع ليغيظ بهم الكفار وعد الله الذين امنوا وعملوا الصالحات منهم مغفرة واجرا عظيما');
INSERT INTO "imlaei_simple" VALUES(49,1,'49:1','يا ايها الذين امنوا لا تقدموا بين يدي الله ورسوله واتقوا الله ان الله سميع عليم');
INSERT INTO "imlaei_simple" VALUES(49,2,'49:2','يا ايها الذين امنوا لا ترفعوا اصواتكم فوق صوت النبي ولا تجهروا له بالقول كجهر بعضكم لبعض ان تحبط اعمالكم وانتم لا تشعرون');
INSERT INTO "imlaei_simple" VALUES(49,3,'49:3','ان الذين يغضون اصواتهم عند رسول الله اولئك الذين امتحن الله قلوبهم للتقوى لهم مغفرة واجر عظيم');
INSERT INTO "imlaei_simple" VALUES(49,4,'49:4','ان الذين ينادونك من وراء الحجرات اكثرهم لا يعقلون');
INSERT INTO "imlaei_simple" VALUES(49,5,'49:5','ولو انهم صبروا حتى تخرج اليهم لكان خيرا لهم والله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(49,6,'49:6','يا ايها الذين امنوا ان جاءكم فاسق بنبا فتبينوا ان تصيبوا قوما بجهالة فتصبحوا على ما فعلتم نادمين');
INSERT INTO "imlaei_simple" VALUES(49,7,'49:7','واعلموا ان فيكم رسول الله لو يطيعكم في كثير من الامر لعنتم ولكن الله حبب اليكم الايمان وزينه في قلوبكم وكره اليكم الكفر والفسوق والعصيان اولئك هم الراشدون');
INSERT INTO "imlaei_simple" VALUES(49,8,'49:8','فضلا من الله ونعمة والله عليم حكيم');
INSERT INTO "imlaei_simple" VALUES(49,9,'49:9','وان طائفتان من المؤمنين اقتتلوا فاصلحوا بينهما فان بغت احداهما على الاخرى فقاتلوا التي تبغي حتى تفيء الى امر الله فان فاءت فاصلحوا بينهما بالعدل واقسطوا ان الله يحب المقسطين');
INSERT INTO "imlaei_simple" VALUES(49,10,'49:10','انما المؤمنون اخوة فاصلحوا بين اخويكم واتقوا الله لعلكم ترحمون');
INSERT INTO "imlaei_simple" VALUES(49,11,'49:11','يا ايها الذين امنوا لا يسخر قوم من قوم عسى ان يكونوا خيرا منهم ولا نساء من نساء عسى ان يكن خيرا منهن ولا تلمزوا انفسكم ولا تنابزوا بالالقاب بئس الاسم الفسوق بعد الايمان ومن لم يتب فاولئك هم الظالمون');
INSERT INTO "imlaei_simple" VALUES(49,12,'49:12','يا ايها الذين امنوا اجتنبوا كثيرا من الظن ان بعض الظن اثم ولا تجسسوا ولا يغتب بعضكم بعضا ايحب احدكم ان ياكل لحم اخيه ميتا فكرهتموه واتقوا الله ان الله تواب رحيم');
INSERT INTO "imlaei_simple" VALUES(49,13,'49:13','يا ايها الناس انا خلقناكم من ذكر وانثى وجعلناكم شعوبا وقبائل لتعارفوا ان اكرمكم عند الله اتقاكم ان الله عليم خبير');
INSERT INTO "imlaei_simple" VALUES(49,14,'49:14','قالت الاعراب امنا قل لم تؤمنوا ولكن قولوا اسلمنا ولما يدخل الايمان في قلوبكم وان تطيعوا الله ورسوله لا يلتكم من اعمالكم شيئا ان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(49,15,'49:15','انما المؤمنون الذين امنوا بالله ورسوله ثم لم يرتابوا وجاهدوا باموالهم وانفسهم في سبيل الله اولئك هم الصادقون');
INSERT INTO "imlaei_simple" VALUES(49,16,'49:16','قل اتعلمون الله بدينكم والله يعلم ما في السماوات وما في الارض والله بكل شيء عليم');
INSERT INTO "imlaei_simple" VALUES(49,17,'49:17','يمنون عليك ان اسلموا قل لا تمنوا علي اسلامكم بل الله يمن عليكم ان هداكم للايمان ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(49,18,'49:18','ان الله يعلم غيب السماوات والارض والله بصير بما تعملون');
INSERT INTO "imlaei_simple" VALUES(50,1,'50:1','ق والقران المجيد');
INSERT INTO "imlaei_simple" VALUES(50,2,'50:2','بل عجبوا ان جاءهم منذر منهم فقال الكافرون هذا شيء عجيب');
INSERT INTO "imlaei_simple" VALUES(50,3,'50:3','ااذا متنا وكنا ترابا ذلك رجع بعيد');
INSERT INTO "imlaei_simple" VALUES(50,4,'50:4','قد علمنا ما تنقص الارض منهم وعندنا كتاب حفيظ');
INSERT INTO "imlaei_simple" VALUES(50,5,'50:5','بل كذبوا بالحق لما جاءهم فهم في امر مريج');
INSERT INTO "imlaei_simple" VALUES(50,6,'50:6','افلم ينظروا الى السماء فوقهم كيف بنيناها وزيناها وما لها من فروج');
INSERT INTO "imlaei_simple" VALUES(50,7,'50:7','والارض مددناها والقينا فيها رواسي وانبتنا فيها من كل زوج بهيج');
INSERT INTO "imlaei_simple" VALUES(50,8,'50:8','تبصرة وذكرى لكل عبد منيب');
INSERT INTO "imlaei_simple" VALUES(50,9,'50:9','ونزلنا من السماء ماء مباركا فانبتنا به جنات وحب الحصيد');
INSERT INTO "imlaei_simple" VALUES(50,10,'50:10','والنخل باسقات لها طلع نضيد');
INSERT INTO "imlaei_simple" VALUES(50,11,'50:11','رزقا للعباد واحيينا به بلدة ميتا كذلك الخروج');
INSERT INTO "imlaei_simple" VALUES(50,12,'50:12','كذبت قبلهم قوم نوح واصحاب الرس وثمود');
INSERT INTO "imlaei_simple" VALUES(50,13,'50:13','وعاد وفرعون واخوان لوط');
INSERT INTO "imlaei_simple" VALUES(50,14,'50:14','واصحاب الايكة وقوم تبع كل كذب الرسل فحق وعيد');
INSERT INTO "imlaei_simple" VALUES(50,15,'50:15','افعيينا بالخلق الاول بل هم في لبس من خلق جديد');
INSERT INTO "imlaei_simple" VALUES(50,16,'50:16','ولقد خلقنا الانسان ونعلم ما توسوس به نفسه ونحن اقرب اليه من حبل الوريد');
INSERT INTO "imlaei_simple" VALUES(50,17,'50:17','اذ يتلقى المتلقيان عن اليمين وعن الشمال قعيد');
INSERT INTO "imlaei_simple" VALUES(50,18,'50:18','ما يلفظ من قول الا لديه رقيب عتيد');
INSERT INTO "imlaei_simple" VALUES(50,19,'50:19','وجاءت سكرة الموت بالحق ذلك ما كنت منه تحيد');
INSERT INTO "imlaei_simple" VALUES(50,20,'50:20','ونفخ في الصور ذلك يوم الوعيد');
INSERT INTO "imlaei_simple" VALUES(50,21,'50:21','وجاءت كل نفس معها سائق وشهيد');
INSERT INTO "imlaei_simple" VALUES(50,22,'50:22','لقد كنت في غفلة من هذا فكشفنا عنك غطاءك فبصرك اليوم حديد');
INSERT INTO "imlaei_simple" VALUES(50,23,'50:23','وقال قرينه هذا ما لدي عتيد');
INSERT INTO "imlaei_simple" VALUES(50,24,'50:24','القيا في جهنم كل كفار عنيد');
INSERT INTO "imlaei_simple" VALUES(50,25,'50:25','مناع للخير معتد مريب');
INSERT INTO "imlaei_simple" VALUES(50,26,'50:26','الذي جعل مع الله الها اخر فالقياه في العذاب الشديد');
INSERT INTO "imlaei_simple" VALUES(50,27,'50:27','قال قرينه ربنا ما اطغيته ولكن كان في ضلال بعيد');
INSERT INTO "imlaei_simple" VALUES(50,28,'50:28','قال لا تختصموا لدي وقد قدمت اليكم بالوعيد');
INSERT INTO "imlaei_simple" VALUES(50,29,'50:29','ما يبدل القول لدي وما انا بظلام للعبيد');
INSERT INTO "imlaei_simple" VALUES(50,30,'50:30','يوم نقول لجهنم هل امتلات وتقول هل من مزيد');
INSERT INTO "imlaei_simple" VALUES(50,31,'50:31','وازلفت الجنة للمتقين غير بعيد');
INSERT INTO "imlaei_simple" VALUES(50,32,'50:32','هذا ما توعدون لكل اواب حفيظ');
INSERT INTO "imlaei_simple" VALUES(50,33,'50:33','من خشي الرحمن بالغيب وجاء بقلب منيب');
INSERT INTO "imlaei_simple" VALUES(50,34,'50:34','ادخلوها بسلام ذلك يوم الخلود');
INSERT INTO "imlaei_simple" VALUES(50,35,'50:35','لهم ما يشاءون فيها ولدينا مزيد');
INSERT INTO "imlaei_simple" VALUES(50,36,'50:36','وكم اهلكنا قبلهم من قرن هم اشد منهم بطشا فنقبوا في البلاد هل من محيص');
INSERT INTO "imlaei_simple" VALUES(50,37,'50:37','ان في ذلك لذكرى لمن كان له قلب او القى السمع وهو شهيد');
INSERT INTO "imlaei_simple" VALUES(50,38,'50:38','ولقد خلقنا السماوات والارض وما بينهما في ستة ايام وما مسنا من لغوب');
INSERT INTO "imlaei_simple" VALUES(50,39,'50:39','فاصبر على ما يقولون وسبح بحمد ربك قبل طلوع الشمس وقبل الغروب');
INSERT INTO "imlaei_simple" VALUES(50,40,'50:40','ومن الليل فسبحه وادبار السجود');
INSERT INTO "imlaei_simple" VALUES(50,41,'50:41','واستمع يوم يناد المناد من مكان قريب');
INSERT INTO "imlaei_simple" VALUES(50,42,'50:42','يوم يسمعون الصيحة بالحق ذلك يوم الخروج');
INSERT INTO "imlaei_simple" VALUES(50,43,'50:43','انا نحن نحيي ونميت والينا المصير');
INSERT INTO "imlaei_simple" VALUES(50,44,'50:44','يوم تشقق الارض عنهم سراعا ذلك حشر علينا يسير');
INSERT INTO "imlaei_simple" VALUES(50,45,'50:45','نحن اعلم بما يقولون وما انت عليهم بجبار فذكر بالقران من يخاف وعيد');
INSERT INTO "imlaei_simple" VALUES(51,1,'51:1','والذاريات ذروا');
INSERT INTO "imlaei_simple" VALUES(51,2,'51:2','فالحاملات وقرا');
INSERT INTO "imlaei_simple" VALUES(51,3,'51:3','فالجاريات يسرا');
INSERT INTO "imlaei_simple" VALUES(51,4,'51:4','فالمقسمات امرا');
INSERT INTO "imlaei_simple" VALUES(51,5,'51:5','انما توعدون لصادق');
INSERT INTO "imlaei_simple" VALUES(51,6,'51:6','وان الدين لواقع');
INSERT INTO "imlaei_simple" VALUES(51,7,'51:7','والسماء ذات الحبك');
INSERT INTO "imlaei_simple" VALUES(51,8,'51:8','انكم لفي قول مختلف');
INSERT INTO "imlaei_simple" VALUES(51,9,'51:9','يؤفك عنه من افك');
INSERT INTO "imlaei_simple" VALUES(51,10,'51:10','قتل الخراصون');
INSERT INTO "imlaei_simple" VALUES(51,11,'51:11','الذين هم في غمرة ساهون');
INSERT INTO "imlaei_simple" VALUES(51,12,'51:12','يسالون ايان يوم الدين');
INSERT INTO "imlaei_simple" VALUES(51,13,'51:13','يوم هم على النار يفتنون');
INSERT INTO "imlaei_simple" VALUES(51,14,'51:14','ذوقوا فتنتكم هذا الذي كنتم به تستعجلون');
INSERT INTO "imlaei_simple" VALUES(51,15,'51:15','ان المتقين في جنات وعيون');
INSERT INTO "imlaei_simple" VALUES(51,16,'51:16','اخذين ما اتاهم ربهم انهم كانوا قبل ذلك محسنين');
INSERT INTO "imlaei_simple" VALUES(51,17,'51:17','كانوا قليلا من الليل ما يهجعون');
INSERT INTO "imlaei_simple" VALUES(51,18,'51:18','وبالاسحار هم يستغفرون');
INSERT INTO "imlaei_simple" VALUES(51,19,'51:19','وفي اموالهم حق للسائل والمحروم');
INSERT INTO "imlaei_simple" VALUES(51,20,'51:20','وفي الارض ايات للموقنين');
INSERT INTO "imlaei_simple" VALUES(51,21,'51:21','وفي انفسكم افلا تبصرون');
INSERT INTO "imlaei_simple" VALUES(51,22,'51:22','وفي السماء رزقكم وما توعدون');
INSERT INTO "imlaei_simple" VALUES(51,23,'51:23','فورب السماء والارض انه لحق مثل ما انكم تنطقون');
INSERT INTO "imlaei_simple" VALUES(51,24,'51:24','هل اتاك حديث ضيف ابراهيم المكرمين');
INSERT INTO "imlaei_simple" VALUES(51,25,'51:25','اذ دخلوا عليه فقالوا سلاما قال سلام قوم منكرون');
INSERT INTO "imlaei_simple" VALUES(51,26,'51:26','فراغ الى اهله فجاء بعجل سمين');
INSERT INTO "imlaei_simple" VALUES(51,27,'51:27','فقربه اليهم قال الا تاكلون');
INSERT INTO "imlaei_simple" VALUES(51,28,'51:28','فاوجس منهم خيفة قالوا لا تخف وبشروه بغلام عليم');
INSERT INTO "imlaei_simple" VALUES(51,29,'51:29','فاقبلت امراته في صرة فصكت وجهها وقالت عجوز عقيم');
INSERT INTO "imlaei_simple" VALUES(51,30,'51:30','قالوا كذلك قال ربك انه هو الحكيم العليم');
INSERT INTO "imlaei_simple" VALUES(51,31,'51:31','قال فما خطبكم ايها المرسلون');
INSERT INTO "imlaei_simple" VALUES(51,32,'51:32','قالوا انا ارسلنا الى قوم مجرمين');
INSERT INTO "imlaei_simple" VALUES(51,33,'51:33','لنرسل عليهم حجارة من طين');
INSERT INTO "imlaei_simple" VALUES(51,34,'51:34','مسومة عند ربك للمسرفين');
INSERT INTO "imlaei_simple" VALUES(51,35,'51:35','فاخرجنا من كان فيها من المؤمنين');
INSERT INTO "imlaei_simple" VALUES(51,36,'51:36','فما وجدنا فيها غير بيت من المسلمين');
INSERT INTO "imlaei_simple" VALUES(51,37,'51:37','وتركنا فيها اية للذين يخافون العذاب الاليم');
INSERT INTO "imlaei_simple" VALUES(51,38,'51:38','وفي موسى اذ ارسلناه الى فرعون بسلطان مبين');
INSERT INTO "imlaei_simple" VALUES(51,39,'51:39','فتولى بركنه وقال ساحر او مجنون');
INSERT INTO "imlaei_simple" VALUES(51,40,'51:40','فاخذناه وجنوده فنبذناهم في اليم وهو مليم');
INSERT INTO "imlaei_simple" VALUES(51,41,'51:41','وفي عاد اذ ارسلنا عليهم الريح العقيم');
INSERT INTO "imlaei_simple" VALUES(51,42,'51:42','ما تذر من شيء اتت عليه الا جعلته كالرميم');
INSERT INTO "imlaei_simple" VALUES(51,43,'51:43','وفي ثمود اذ قيل لهم تمتعوا حتى حين');
INSERT INTO "imlaei_simple" VALUES(51,44,'51:44','فعتوا عن امر ربهم فاخذتهم الصاعقة وهم ينظرون');
INSERT INTO "imlaei_simple" VALUES(51,45,'51:45','فما استطاعوا من قيام وما كانوا منتصرين');
INSERT INTO "imlaei_simple" VALUES(51,46,'51:46','وقوم نوح من قبل انهم كانوا قوما فاسقين');
INSERT INTO "imlaei_simple" VALUES(51,47,'51:47','والسماء بنيناها بايد وانا لموسعون');
INSERT INTO "imlaei_simple" VALUES(51,48,'51:48','والارض فرشناها فنعم الماهدون');
INSERT INTO "imlaei_simple" VALUES(51,49,'51:49','ومن كل شيء خلقنا زوجين لعلكم تذكرون');
INSERT INTO "imlaei_simple" VALUES(51,50,'51:50','ففروا الى الله اني لكم منه نذير مبين');
INSERT INTO "imlaei_simple" VALUES(51,51,'51:51','ولا تجعلوا مع الله الها اخر اني لكم منه نذير مبين');
INSERT INTO "imlaei_simple" VALUES(51,52,'51:52','كذلك ما اتى الذين من قبلهم من رسول الا قالوا ساحر او مجنون');
INSERT INTO "imlaei_simple" VALUES(51,53,'51:53','اتواصوا به بل هم قوم طاغون');
INSERT INTO "imlaei_simple" VALUES(51,54,'51:54','فتول عنهم فما انت بملوم');
INSERT INTO "imlaei_simple" VALUES(51,55,'51:55','وذكر فان الذكرى تنفع المؤمنين');
INSERT INTO "imlaei_simple" VALUES(51,56,'51:56','وما خلقت الجن والانس الا ليعبدون');
INSERT INTO "imlaei_simple" VALUES(51,57,'51:57','ما اريد منهم من رزق وما اريد ان يطعمون');
INSERT INTO "imlaei_simple" VALUES(51,58,'51:58','ان الله هو الرزاق ذو القوة المتين');
INSERT INTO "imlaei_simple" VALUES(51,59,'51:59','فان للذين ظلموا ذنوبا مثل ذنوب اصحابهم فلا يستعجلون');
INSERT INTO "imlaei_simple" VALUES(51,60,'51:60','فويل للذين كفروا من يومهم الذي يوعدون');
INSERT INTO "imlaei_simple" VALUES(52,1,'52:1','والطور');
INSERT INTO "imlaei_simple" VALUES(52,2,'52:2','وكتاب مسطور');
INSERT INTO "imlaei_simple" VALUES(52,3,'52:3','في رق منشور');
INSERT INTO "imlaei_simple" VALUES(52,4,'52:4','والبيت المعمور');
INSERT INTO "imlaei_simple" VALUES(52,5,'52:5','والسقف المرفوع');
INSERT INTO "imlaei_simple" VALUES(52,6,'52:6','والبحر المسجور');
INSERT INTO "imlaei_simple" VALUES(52,7,'52:7','ان عذاب ربك لواقع');
INSERT INTO "imlaei_simple" VALUES(52,8,'52:8','ما له من دافع');
INSERT INTO "imlaei_simple" VALUES(52,9,'52:9','يوم تمور السماء مورا');
INSERT INTO "imlaei_simple" VALUES(52,10,'52:10','وتسير الجبال سيرا');
INSERT INTO "imlaei_simple" VALUES(52,11,'52:11','فويل يومئذ للمكذبين');
INSERT INTO "imlaei_simple" VALUES(52,12,'52:12','الذين هم في خوض يلعبون');
INSERT INTO "imlaei_simple" VALUES(52,13,'52:13','يوم يدعون الى نار جهنم دعا');
INSERT INTO "imlaei_simple" VALUES(52,14,'52:14','هذه النار التي كنتم بها تكذبون');
INSERT INTO "imlaei_simple" VALUES(52,15,'52:15','افسحر هذا ام انتم لا تبصرون');
INSERT INTO "imlaei_simple" VALUES(52,16,'52:16','اصلوها فاصبروا او لا تصبروا سواء عليكم انما تجزون ما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(52,17,'52:17','ان المتقين في جنات ونعيم');
INSERT INTO "imlaei_simple" VALUES(52,18,'52:18','فاكهين بما اتاهم ربهم ووقاهم ربهم عذاب الجحيم');
INSERT INTO "imlaei_simple" VALUES(52,19,'52:19','كلوا واشربوا هنيئا بما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(52,20,'52:20','متكئين على سرر مصفوفة وزوجناهم بحور عين');
INSERT INTO "imlaei_simple" VALUES(52,21,'52:21','والذين امنوا واتبعتهم ذريتهم بايمان الحقنا بهم ذريتهم وما التناهم من عملهم من شيء كل امرئ بما كسب رهين');
INSERT INTO "imlaei_simple" VALUES(52,22,'52:22','وامددناهم بفاكهة ولحم مما يشتهون');
INSERT INTO "imlaei_simple" VALUES(52,23,'52:23','يتنازعون فيها كاسا لا لغو فيها ولا تاثيم');
INSERT INTO "imlaei_simple" VALUES(52,24,'52:24','ويطوف عليهم غلمان لهم كانهم لؤلؤ مكنون');
INSERT INTO "imlaei_simple" VALUES(52,25,'52:25','واقبل بعضهم على بعض يتساءلون');
INSERT INTO "imlaei_simple" VALUES(52,26,'52:26','قالوا انا كنا قبل في اهلنا مشفقين');
INSERT INTO "imlaei_simple" VALUES(52,27,'52:27','فمن الله علينا ووقانا عذاب السموم');
INSERT INTO "imlaei_simple" VALUES(52,28,'52:28','انا كنا من قبل ندعوه انه هو البر الرحيم');
INSERT INTO "imlaei_simple" VALUES(52,29,'52:29','فذكر فما انت بنعمت ربك بكاهن ولا مجنون');
INSERT INTO "imlaei_simple" VALUES(52,30,'52:30','ام يقولون شاعر نتربص به ريب المنون');
INSERT INTO "imlaei_simple" VALUES(52,31,'52:31','قل تربصوا فاني معكم من المتربصين');
INSERT INTO "imlaei_simple" VALUES(52,32,'52:32','ام تامرهم احلامهم بهذا ام هم قوم طاغون');
INSERT INTO "imlaei_simple" VALUES(52,33,'52:33','ام يقولون تقوله بل لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(52,34,'52:34','فلياتوا بحديث مثله ان كانوا صادقين');
INSERT INTO "imlaei_simple" VALUES(52,35,'52:35','ام خلقوا من غير شيء ام هم الخالقون');
INSERT INTO "imlaei_simple" VALUES(52,36,'52:36','ام خلقوا السماوات والارض بل لا يوقنون');
INSERT INTO "imlaei_simple" VALUES(52,37,'52:37','ام عندهم خزائن ربك ام هم المصيطرون');
INSERT INTO "imlaei_simple" VALUES(52,38,'52:38','ام لهم سلم يستمعون فيه فليات مستمعهم بسلطان مبين');
INSERT INTO "imlaei_simple" VALUES(52,39,'52:39','ام له البنات ولكم البنون');
INSERT INTO "imlaei_simple" VALUES(52,40,'52:40','ام تسالهم اجرا فهم من مغرم مثقلون');
INSERT INTO "imlaei_simple" VALUES(52,41,'52:41','ام عندهم الغيب فهم يكتبون');
INSERT INTO "imlaei_simple" VALUES(52,42,'52:42','ام يريدون كيدا فالذين كفروا هم المكيدون');
INSERT INTO "imlaei_simple" VALUES(52,43,'52:43','ام لهم اله غير الله سبحان الله عما يشركون');
INSERT INTO "imlaei_simple" VALUES(52,44,'52:44','وان يروا كسفا من السماء ساقطا يقولوا سحاب مركوم');
INSERT INTO "imlaei_simple" VALUES(52,45,'52:45','فذرهم حتى يلاقوا يومهم الذي فيه يصعقون');
INSERT INTO "imlaei_simple" VALUES(52,46,'52:46','يوم لا يغني عنهم كيدهم شيئا ولا هم ينصرون');
INSERT INTO "imlaei_simple" VALUES(52,47,'52:47','وان للذين ظلموا عذابا دون ذلك ولكن اكثرهم لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(52,48,'52:48','واصبر لحكم ربك فانك باعيننا وسبح بحمد ربك حين تقوم');
INSERT INTO "imlaei_simple" VALUES(52,49,'52:49','ومن الليل فسبحه وادبار النجوم');
INSERT INTO "imlaei_simple" VALUES(53,1,'53:1','والنجم اذا هوى');
INSERT INTO "imlaei_simple" VALUES(53,2,'53:2','ما ضل صاحبكم وما غوى');
INSERT INTO "imlaei_simple" VALUES(53,3,'53:3','وما ينطق عن الهوى');
INSERT INTO "imlaei_simple" VALUES(53,4,'53:4','ان هو الا وحي يوحى');
INSERT INTO "imlaei_simple" VALUES(53,5,'53:5','علمه شديد القوى');
INSERT INTO "imlaei_simple" VALUES(53,6,'53:6','ذو مرة فاستوى');
INSERT INTO "imlaei_simple" VALUES(53,7,'53:7','وهو بالافق الاعلى');
INSERT INTO "imlaei_simple" VALUES(53,8,'53:8','ثم دنا فتدلى');
INSERT INTO "imlaei_simple" VALUES(53,9,'53:9','فكان قاب قوسين او ادنى');
INSERT INTO "imlaei_simple" VALUES(53,10,'53:10','فاوحى الى عبده ما اوحى');
INSERT INTO "imlaei_simple" VALUES(53,11,'53:11','ما كذب الفؤاد ما راى');
INSERT INTO "imlaei_simple" VALUES(53,12,'53:12','افتمارونه على ما يرى');
INSERT INTO "imlaei_simple" VALUES(53,13,'53:13','ولقد راه نزلة اخرى');
INSERT INTO "imlaei_simple" VALUES(53,14,'53:14','عند سدرة المنتهى');
INSERT INTO "imlaei_simple" VALUES(53,15,'53:15','عندها جنة الماوى');
INSERT INTO "imlaei_simple" VALUES(53,16,'53:16','اذ يغشى السدرة ما يغشى');
INSERT INTO "imlaei_simple" VALUES(53,17,'53:17','ما زاغ البصر وما طغى');
INSERT INTO "imlaei_simple" VALUES(53,18,'53:18','لقد راى من ايات ربه الكبرى');
INSERT INTO "imlaei_simple" VALUES(53,19,'53:19','افرايتم اللات والعزى');
INSERT INTO "imlaei_simple" VALUES(53,20,'53:20','ومناة الثالثة الاخرى');
INSERT INTO "imlaei_simple" VALUES(53,21,'53:21','الكم الذكر وله الانثى');
INSERT INTO "imlaei_simple" VALUES(53,22,'53:22','تلك اذا قسمة ضيزى');
INSERT INTO "imlaei_simple" VALUES(53,23,'53:23','ان هي الا اسماء سميتموها انتم واباؤكم ما انزل الله بها من سلطان ان يتبعون الا الظن وما تهوى الانفس ولقد جاءهم من ربهم الهدى');
INSERT INTO "imlaei_simple" VALUES(53,24,'53:24','ام للانسان ما تمنى');
INSERT INTO "imlaei_simple" VALUES(53,25,'53:25','فلله الاخرة والاولى');
INSERT INTO "imlaei_simple" VALUES(53,26,'53:26','وكم من ملك في السماوات لا تغني شفاعتهم شيئا الا من بعد ان ياذن الله لمن يشاء ويرضى');
INSERT INTO "imlaei_simple" VALUES(53,27,'53:27','ان الذين لا يؤمنون بالاخرة ليسمون الملائكة تسمية الانثى');
INSERT INTO "imlaei_simple" VALUES(53,28,'53:28','وما لهم به من علم ان يتبعون الا الظن وان الظن لا يغني من الحق شيئا');
INSERT INTO "imlaei_simple" VALUES(53,29,'53:29','فاعرض عن من تولى عن ذكرنا ولم يرد الا الحياة الدنيا');
INSERT INTO "imlaei_simple" VALUES(53,30,'53:30','ذلك مبلغهم من العلم ان ربك هو اعلم بمن ضل عن سبيله وهو اعلم بمن اهتدى');
INSERT INTO "imlaei_simple" VALUES(53,31,'53:31','ولله ما في السماوات وما في الارض ليجزي الذين اساءوا بما عملوا ويجزي الذين احسنوا بالحسنى');
INSERT INTO "imlaei_simple" VALUES(53,32,'53:32','الذين يجتنبون كبائر الاثم والفواحش الا اللمم ان ربك واسع المغفرة هو اعلم بكم اذ انشاكم من الارض واذ انتم اجنة في بطون امهاتكم فلا تزكوا انفسكم هو اعلم بمن اتقى');
INSERT INTO "imlaei_simple" VALUES(53,33,'53:33','افرايت الذي تولى');
INSERT INTO "imlaei_simple" VALUES(53,34,'53:34','واعطى قليلا واكدى');
INSERT INTO "imlaei_simple" VALUES(53,35,'53:35','اعنده علم الغيب فهو يرى');
INSERT INTO "imlaei_simple" VALUES(53,36,'53:36','ام لم ينبا بما في صحف موسى');
INSERT INTO "imlaei_simple" VALUES(53,37,'53:37','وابراهيم الذي وفى');
INSERT INTO "imlaei_simple" VALUES(53,38,'53:38','الا تزر وازرة وزر اخرى');
INSERT INTO "imlaei_simple" VALUES(53,39,'53:39','وان ليس للانسان الا ما سعى');
INSERT INTO "imlaei_simple" VALUES(53,40,'53:40','وان سعيه سوف يرى');
INSERT INTO "imlaei_simple" VALUES(53,41,'53:41','ثم يجزاه الجزاء الاوفى');
INSERT INTO "imlaei_simple" VALUES(53,42,'53:42','وان الى ربك المنتهى');
INSERT INTO "imlaei_simple" VALUES(53,43,'53:43','وانه هو اضحك وابكى');
INSERT INTO "imlaei_simple" VALUES(53,44,'53:44','وانه هو امات واحيا');
INSERT INTO "imlaei_simple" VALUES(53,45,'53:45','وانه خلق الزوجين الذكر والانثى');
INSERT INTO "imlaei_simple" VALUES(53,46,'53:46','من نطفة اذا تمنى');
INSERT INTO "imlaei_simple" VALUES(53,47,'53:47','وان عليه النشاة الاخرى');
INSERT INTO "imlaei_simple" VALUES(53,48,'53:48','وانه هو اغنى واقنى');
INSERT INTO "imlaei_simple" VALUES(53,49,'53:49','وانه هو رب الشعرى');
INSERT INTO "imlaei_simple" VALUES(53,50,'53:50','وانه اهلك عادا الاولى');
INSERT INTO "imlaei_simple" VALUES(53,51,'53:51','وثمود فما ابقى');
INSERT INTO "imlaei_simple" VALUES(53,52,'53:52','وقوم نوح من قبل انهم كانوا هم اظلم واطغى');
INSERT INTO "imlaei_simple" VALUES(53,53,'53:53','والمؤتفكة اهوى');
INSERT INTO "imlaei_simple" VALUES(53,54,'53:54','فغشاها ما غشى');
INSERT INTO "imlaei_simple" VALUES(53,55,'53:55','فباي الاء ربك تتمارى');
INSERT INTO "imlaei_simple" VALUES(53,56,'53:56','هذا نذير من النذر الاولى');
INSERT INTO "imlaei_simple" VALUES(53,57,'53:57','ازفت الازفة');
INSERT INTO "imlaei_simple" VALUES(53,58,'53:58','ليس لها من دون الله كاشفة');
INSERT INTO "imlaei_simple" VALUES(53,59,'53:59','افمن هذا الحديث تعجبون');
INSERT INTO "imlaei_simple" VALUES(53,60,'53:60','وتضحكون ولا تبكون');
INSERT INTO "imlaei_simple" VALUES(53,61,'53:61','وانتم سامدون');
INSERT INTO "imlaei_simple" VALUES(53,62,'53:62','فاسجدوا لله واعبدوا');
INSERT INTO "imlaei_simple" VALUES(54,1,'54:1','اقتربت الساعة وانشق القمر');
INSERT INTO "imlaei_simple" VALUES(54,2,'54:2','وان يروا اية يعرضوا ويقولوا سحر مستمر');
INSERT INTO "imlaei_simple" VALUES(54,3,'54:3','وكذبوا واتبعوا اهواءهم وكل امر مستقر');
INSERT INTO "imlaei_simple" VALUES(54,4,'54:4','ولقد جاءهم من الانباء ما فيه مزدجر');
INSERT INTO "imlaei_simple" VALUES(54,5,'54:5','حكمة بالغة فما تغن النذر');
INSERT INTO "imlaei_simple" VALUES(54,6,'54:6','فتول عنهم يوم يدع الداع الى شيء نكر');
INSERT INTO "imlaei_simple" VALUES(54,7,'54:7','خشعا ابصارهم يخرجون من الاجداث كانهم جراد منتشر');
INSERT INTO "imlaei_simple" VALUES(54,8,'54:8','مهطعين الى الداع يقول الكافرون هذا يوم عسر');
INSERT INTO "imlaei_simple" VALUES(54,9,'54:9','كذبت قبلهم قوم نوح فكذبوا عبدنا وقالوا مجنون وازدجر');
INSERT INTO "imlaei_simple" VALUES(54,10,'54:10','فدعا ربه اني مغلوب فانتصر');
INSERT INTO "imlaei_simple" VALUES(54,11,'54:11','ففتحنا ابواب السماء بماء منهمر');
INSERT INTO "imlaei_simple" VALUES(54,12,'54:12','وفجرنا الارض عيونا فالتقى الماء على امر قد قدر');
INSERT INTO "imlaei_simple" VALUES(54,13,'54:13','وحملناه على ذات الواح ودسر');
INSERT INTO "imlaei_simple" VALUES(54,14,'54:14','تجري باعيننا جزاء لمن كان كفر');
INSERT INTO "imlaei_simple" VALUES(54,15,'54:15','ولقد تركناها اية فهل من مدكر');
INSERT INTO "imlaei_simple" VALUES(54,16,'54:16','فكيف كان عذابي ونذر');
INSERT INTO "imlaei_simple" VALUES(54,17,'54:17','ولقد يسرنا القران للذكر فهل من مدكر');
INSERT INTO "imlaei_simple" VALUES(54,18,'54:18','كذبت عاد فكيف كان عذابي ونذر');
INSERT INTO "imlaei_simple" VALUES(54,19,'54:19','انا ارسلنا عليهم ريحا صرصرا في يوم نحس مستمر');
INSERT INTO "imlaei_simple" VALUES(54,20,'54:20','تنزع الناس كانهم اعجاز نخل منقعر');
INSERT INTO "imlaei_simple" VALUES(54,21,'54:21','فكيف كان عذابي ونذر');
INSERT INTO "imlaei_simple" VALUES(54,22,'54:22','ولقد يسرنا القران للذكر فهل من مدكر');
INSERT INTO "imlaei_simple" VALUES(54,23,'54:23','كذبت ثمود بالنذر');
INSERT INTO "imlaei_simple" VALUES(54,24,'54:24','فقالوا ابشرا منا واحدا نتبعه انا اذا لفي ضلال وسعر');
INSERT INTO "imlaei_simple" VALUES(54,25,'54:25','االقي الذكر عليه من بيننا بل هو كذاب اشر');
INSERT INTO "imlaei_simple" VALUES(54,26,'54:26','سيعلمون غدا من الكذاب الاشر');
INSERT INTO "imlaei_simple" VALUES(54,27,'54:27','انا مرسلو الناقة فتنة لهم فارتقبهم واصطبر');
INSERT INTO "imlaei_simple" VALUES(54,28,'54:28','ونبئهم ان الماء قسمة بينهم كل شرب محتضر');
INSERT INTO "imlaei_simple" VALUES(54,29,'54:29','فنادوا صاحبهم فتعاطى فعقر');
INSERT INTO "imlaei_simple" VALUES(54,30,'54:30','فكيف كان عذابي ونذر');
INSERT INTO "imlaei_simple" VALUES(54,31,'54:31','انا ارسلنا عليهم صيحة واحدة فكانوا كهشيم المحتظر');
INSERT INTO "imlaei_simple" VALUES(54,32,'54:32','ولقد يسرنا القران للذكر فهل من مدكر');
INSERT INTO "imlaei_simple" VALUES(54,33,'54:33','كذبت قوم لوط بالنذر');
INSERT INTO "imlaei_simple" VALUES(54,34,'54:34','انا ارسلنا عليهم حاصبا الا ال لوط نجيناهم بسحر');
INSERT INTO "imlaei_simple" VALUES(54,35,'54:35','نعمة من عندنا كذلك نجزي من شكر');
INSERT INTO "imlaei_simple" VALUES(54,36,'54:36','ولقد انذرهم بطشتنا فتماروا بالنذر');
INSERT INTO "imlaei_simple" VALUES(54,37,'54:37','ولقد راودوه عن ضيفه فطمسنا اعينهم فذوقوا عذابي ونذر');
INSERT INTO "imlaei_simple" VALUES(54,38,'54:38','ولقد صبحهم بكرة عذاب مستقر');
INSERT INTO "imlaei_simple" VALUES(54,39,'54:39','فذوقوا عذابي ونذر');
INSERT INTO "imlaei_simple" VALUES(54,40,'54:40','ولقد يسرنا القران للذكر فهل من مدكر');
INSERT INTO "imlaei_simple" VALUES(54,41,'54:41','ولقد جاء ال فرعون النذر');
INSERT INTO "imlaei_simple" VALUES(54,42,'54:42','كذبوا باياتنا كلها فاخذناهم اخذ عزيز مقتدر');
INSERT INTO "imlaei_simple" VALUES(54,43,'54:43','اكفاركم خير من اولئكم ام لكم براءة في الزبر');
INSERT INTO "imlaei_simple" VALUES(54,44,'54:44','ام يقولون نحن جميع منتصر');
INSERT INTO "imlaei_simple" VALUES(54,45,'54:45','سيهزم الجمع ويولون الدبر');
INSERT INTO "imlaei_simple" VALUES(54,46,'54:46','بل الساعة موعدهم والساعة ادهى وامر');
INSERT INTO "imlaei_simple" VALUES(54,47,'54:47','ان المجرمين في ضلال وسعر');
INSERT INTO "imlaei_simple" VALUES(54,48,'54:48','يوم يسحبون في النار على وجوههم ذوقوا مس سقر');
INSERT INTO "imlaei_simple" VALUES(54,49,'54:49','انا كل شيء خلقناه بقدر');
INSERT INTO "imlaei_simple" VALUES(54,50,'54:50','وما امرنا الا واحدة كلمح بالبصر');
INSERT INTO "imlaei_simple" VALUES(54,51,'54:51','ولقد اهلكنا اشياعكم فهل من مدكر');
INSERT INTO "imlaei_simple" VALUES(54,52,'54:52','وكل شيء فعلوه في الزبر');
INSERT INTO "imlaei_simple" VALUES(54,53,'54:53','وكل صغير وكبير مستطر');
INSERT INTO "imlaei_simple" VALUES(54,54,'54:54','ان المتقين في جنات ونهر');
INSERT INTO "imlaei_simple" VALUES(54,55,'54:55','في مقعد صدق عند مليك مقتدر');
INSERT INTO "imlaei_simple" VALUES(55,1,'55:1','الرحمن');
INSERT INTO "imlaei_simple" VALUES(55,2,'55:2','علم القران');
INSERT INTO "imlaei_simple" VALUES(55,3,'55:3','خلق الانسان');
INSERT INTO "imlaei_simple" VALUES(55,4,'55:4','علمه البيان');
INSERT INTO "imlaei_simple" VALUES(55,5,'55:5','الشمس والقمر بحسبان');
INSERT INTO "imlaei_simple" VALUES(55,6,'55:6','والنجم والشجر يسجدان');
INSERT INTO "imlaei_simple" VALUES(55,7,'55:7','والسماء رفعها ووضع الميزان');
INSERT INTO "imlaei_simple" VALUES(55,8,'55:8','الا تطغوا في الميزان');
INSERT INTO "imlaei_simple" VALUES(55,9,'55:9','واقيموا الوزن بالقسط ولا تخسروا الميزان');
INSERT INTO "imlaei_simple" VALUES(55,10,'55:10','والارض وضعها للانام');
INSERT INTO "imlaei_simple" VALUES(55,11,'55:11','فيها فاكهة والنخل ذات الاكمام');
INSERT INTO "imlaei_simple" VALUES(55,12,'55:12','والحب ذو العصف والريحان');
INSERT INTO "imlaei_simple" VALUES(55,13,'55:13','فباي الاء ربكما تكذبان');
INSERT INTO "imlaei_simple" VALUES(55,14,'55:14','خلق الانسان من صلصال كالفخار');
INSERT INTO "imlaei_simple" VALUES(55,15,'55:15','وخلق الجان من مارج من نار');
INSERT INTO "imlaei_simple" VALUES(55,16,'55:16','فباي الاء ربكما تكذبان');
