INSERT INTO "imlaei_simple" VALUES(57,3,'57:3','هو الاول والاخر والظاهر والباطن وهو بكل شيء عليم');
INSERT INTO "imlaei_simple" VALUES(57,4,'57:4','هو الذي خلق السماوات والارض في ستة ايام ثم استوى على العرش يعلم ما يلج في الارض وما يخرج منها وما ينزل من السماء وما يعرج فيها وهو معكم اين ما كنتم والله بما تعملون بصير');
INSERT INTO "imlaei_simple" VALUES(57,5,'57:5','له ملك السماوات والارض والى الله ترجع الامور');
INSERT INTO "imlaei_simple" VALUES(57,6,'57:6','يولج الليل في النهار ويولج النهار في الليل وهو عليم بذات الصدور');
INSERT INTO "imlaei_simple" VALUES(57,7,'57:7','امنوا بالله ورسوله وانفقوا مما جعلكم مستخلفين فيه فالذين امنوا منكم وانفقوا لهم اجر كبير');
INSERT INTO "imlaei_simple" VALUES(57,8,'57:8','وما لكم لا تؤمنون بالله والرسول يدعوكم لتؤمنوا بربكم وقد اخذ ميثاقكم ان كنتم مؤمنين');
INSERT INTO "imlaei_simple" VALUES(57,9,'57:9','هو الذي ينزل على عبده ايات بينات ليخرجكم من الظلمات الى النور وان الله بكم لرءوف رحيم');
INSERT INTO "imlaei_simple" VALUES(57,10,'57:10','وما لكم الا تنفقوا في سبيل الله ولله ميراث السماوات والارض لا يستوي منكم من انفق من قبل الفتح وقاتل اولئك اعظم درجة من الذين انفقوا من بعد وقاتلوا وكلا وعد الله الحسنى والله بما تعملون خبير');
INSERT INTO "imlaei_simple" VALUES(57,11,'57:11','من ذا الذي يقرض الله قرضا حسنا فيضاعفه له وله اجر كريم');
INSERT INTO "imlaei_simple" VALUES(57,12,'57:12','يوم ترى المؤمنين والمؤمنات يسعى نورهم بين ايديهم وبايمانهم بشراكم اليوم جنات تجري من تحتها الانهار خالدين فيها ذلك هو الفوز العظيم');
INSERT INTO "imlaei_simple" VALUES(57,13,'57:13','يوم يقول المنافقون والمنافقات للذين امنوا انظرونا نقتبس من نوركم قيل ارجعوا وراءكم فالتمسوا نورا فضرب بينهم بسور له باب باطنه فيه الرحمة وظاهره من قبله العذاب');
INSERT INTO "imlaei_simple" VALUES(57,14,'57:14','ينادونهم الم نكن معكم قالوا بلى ولكنكم فتنتم انفسكم وتربصتم وارتبتم وغرتكم الاماني حتى جاء امر الله وغركم بالله الغرور');
INSERT INTO "imlaei_simple" VALUES(57,15,'57:15','فاليوم لا يؤخذ منكم فدية ولا من الذين كفروا ماواكم النار هي مولاكم وبئس المصير');
INSERT INTO "imlaei_simple" VALUES(57,16,'57:16','الم يان للذين امنوا ان تخشع قلوبهم لذكر الله وما نزل من الحق ولا يكونوا كالذين اوتوا الكتاب من قبل فطال عليهم الامد فقست قلوبهم وكثير منهم فاسقون');
INSERT INTO "imlaei_simple" VALUES(57,17,'57:17','اعلموا ان الله يحيي الارض بعد موتها قد بينا لكم الايات لعلكم تعقلون');
INSERT INTO "imlaei_simple" VALUES(57,18,'57:18','ان المصدقين والمصدقات واقرضوا الله قرضا حسنا يضاعف لهم ولهم اجر كريم');
INSERT INTO "imlaei_simple" VALUES(57,19,'57:19','والذين امنوا بالله ورسله اولئك هم الصديقون والشهداء عند ربهم لهم اجرهم ونورهم والذين كفروا وكذبوا باياتنا اولئك اصحاب الجحيم');
INSERT INTO "imlaei_simple" VALUES(57,20,'57:20','اعلموا انما الحياة الدنيا لعب ولهو وزينة وتفاخر بينكم وتكاثر في الاموال والاولاد كمثل غيث اعجب الكفار نباته ثم يهيج فتراه مصفرا ثم يكون حطاما وفي الاخرة عذاب شديد ومغفرة من الله ورضوان وما الحياة الدنيا الا متاع الغرور');
INSERT INTO "imlaei_simple" VALUES(57,21,'57:21','سابقوا الى مغفرة من ربكم وجنة عرضها كعرض السماء والارض اعدت للذين امنوا بالله ورسله ذلك فضل الله يؤتيه من يشاء والله ذو الفضل العظيم');
INSERT INTO "imlaei_simple" VALUES(57,22,'57:22','ما اصاب من مصيبة في الارض ولا في انفسكم الا في كتاب من قبل ان نبراها ان ذلك على الله يسير');
INSERT INTO "imlaei_simple" VALUES(57,23,'57:23','لكيلا تاسوا على ما فاتكم ولا تفرحوا بما اتاكم والله لا يحب كل مختال فخور');
INSERT INTO "imlaei_simple" VALUES(57,24,'57:24','الذين يبخلون ويامرون الناس بالبخل ومن يتول فان الله هو الغني الحميد');
INSERT INTO "imlaei_simple" VALUES(57,25,'57:25','لقد ارسلنا رسلنا بالبينات وانزلنا معهم الكتاب والميزان ليقوم الناس بالقسط وانزلنا الحديد فيه باس شديد ومنافع للناس وليعلم الله من ينصره ورسله بالغيب ان الله قوي عزيز');
INSERT INTO "imlaei_simple" VALUES(57,26,'57:26','ولقد ارسلنا نوحا وابراهيم وجعلنا في ذريتهما النبوة والكتاب فمنهم مهتد وكثير منهم فاسقون');
INSERT INTO "imlaei_simple" VALUES(57,27,'57:27','ثم قفينا على اثارهم برسلنا وقفينا بعيسى ابن مريم واتيناه الانجيل وجعلنا في قلوب الذين اتبعوه رافة ورحمة ورهبانية ابتدعوها ما كتبناها عليهم الا ابتغاء رضوان الله فما رعوها حق رعايتها فاتينا الذين امنوا منهم اجرهم وكثير منهم فاسقون');
INSERT INTO "imlaei_simple" VALUES(57,28,'57:28','يا ايها الذين امنوا اتقوا الله وامنوا برسوله يؤتكم كفلين من رحمته ويجعل لكم نورا تمشون به ويغفر لكم والله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(57,29,'57:29','لئلا يعلم اهل الكتاب الا يقدرون على شيء من فضل الله وان الفضل بيد الله يؤتيه من يشاء والله ذو الفضل العظيم');
INSERT INTO "imlaei_simple" VALUES(58,1,'58:1','قد سمع الله قول التي تجادلك في زوجها وتشتكي الى الله والله يسمع تحاوركما ان الله سميع بصير');
INSERT INTO "imlaei_simple" VALUES(58,2,'58:2','الذين يظاهرون منكم من نسائهم ما هن امهاتهم ان امهاتهم الا اللائي ولدنهم وانهم ليقولون منكرا من القول وزورا وان الله لعفو غفور');
INSERT INTO "imlaei_simple" VALUES(58,3,'58:3','والذين يظاهرون من نسائهم ثم يعودون لما قالوا فتحرير رقبة من قبل ان يتماسا ذلكم توعظون به والله بما تعملون خبير');
INSERT INTO "imlaei_simple" VALUES(58,4,'58:4','فمن لم يجد فصيام شهرين متتابعين من قبل ان يتماسا فمن لم يستطع فاطعام ستين مسكينا ذلك لتؤمنوا بالله ورسوله وتلك حدود الله وللكافرين عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(58,5,'58:5','ان الذين يحادون الله ورسوله كبتوا كما كبت الذين من قبلهم وقد انزلنا ايات بينات وللكافرين عذاب مهين');
INSERT INTO "imlaei_simple" VALUES(58,6,'58:6','يوم يبعثهم الله جميعا فينبئهم بما عملوا احصاه الله ونسوه والله على كل شيء شهيد');
INSERT INTO "imlaei_simple" VALUES(58,7,'58:7','الم تر ان الله يعلم ما في السماوات وما في الارض ما يكون من نجوى ثلاثة الا هو رابعهم ولا خمسة الا هو سادسهم ولا ادنى من ذلك ولا اكثر الا هو معهم اين ما كانوا ثم ينبئهم بما عملوا يوم القيامة ان الله بكل شيء عليم');
INSERT INTO "imlaei_simple" VALUES(58,8,'58:8','الم تر الى الذين نهوا عن النجوى ثم يعودون لما نهوا عنه ويتناجون بالاثم والعدوان ومعصيت الرسول واذا جاءوك حيوك بما لم يحيك به الله ويقولون في انفسهم لولا يعذبنا الله بما نقول حسبهم جهنم يصلونها فبئس المصير');
INSERT INTO "imlaei_simple" VALUES(58,9,'58:9','يا ايها الذين امنوا اذا تناجيتم فلا تتناجوا بالاثم والعدوان ومعصيت الرسول وتناجوا بالبر والتقوى واتقوا الله الذي اليه تحشرون');
INSERT INTO "imlaei_simple" VALUES(58,10,'58:10','انما النجوى من الشيطان ليحزن الذين امنوا وليس بضارهم شيئا الا باذن الله وعلى الله فليتوكل المؤمنون');
INSERT INTO "imlaei_simple" VALUES(58,11,'58:11','يا ايها الذين امنوا اذا قيل لكم تفسحوا في المجالس فافسحوا يفسح الله لكم واذا قيل انشزوا فانشزوا يرفع الله الذين امنوا منكم والذين اوتوا العلم درجات والله بما تعملون خبير');
INSERT INTO "imlaei_simple" VALUES(58,12,'58:12','يا ايها الذين امنوا اذا ناجيتم الرسول فقدموا بين يدي نجواكم صدقة ذلك خير لكم واطهر فان لم تجدوا فان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(58,13,'58:13','ااشفقتم ان تقدموا بين يدي نجواكم صدقات فاذ لم تفعلوا وتاب الله عليكم فاقيموا الصلاة واتوا الزكاة واطيعوا الله ورسوله والله خبير بما تعملون');
INSERT INTO "imlaei_simple" VALUES(58,14,'58:14','الم تر الى الذين تولوا قوما غضب الله عليهم ما هم منكم ولا منهم ويحلفون على الكذب وهم يعلمون');
INSERT INTO "imlaei_simple" VALUES(58,15,'58:15','اعد الله لهم عذابا شديدا انهم ساء ما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(58,16,'58:16','اتخذوا ايمانهم جنة فصدوا عن سبيل الله فلهم عذاب مهين');
INSERT INTO "imlaei_simple" VALUES(58,17,'58:17','لن تغني عنهم اموالهم ولا اولادهم من الله شيئا اولئك اصحاب النار هم فيها خالدون');
INSERT INTO "imlaei_simple" VALUES(58,18,'58:18','يوم يبعثهم الله جميعا فيحلفون له كما يحلفون لكم ويحسبون انهم على شيء الا انهم هم الكاذبون');
INSERT INTO "imlaei_simple" VALUES(58,19,'58:19','استحوذ عليهم الشيطان فانساهم ذكر الله اولئك حزب الشيطان الا ان حزب الشيطان هم الخاسرون');
INSERT INTO "imlaei_simple" VALUES(58,20,'58:20','ان الذين يحادون الله ورسوله اولئك في الاذلين');
INSERT INTO "imlaei_simple" VALUES(58,21,'58:21','كتب الله لاغلبن انا ورسلي ان الله قوي عزيز');
INSERT INTO "imlaei_simple" VALUES(58,22,'58:22','لا تجد قوما يؤمنون بالله واليوم الاخر يوادون من حاد الله ورسوله ولو كانوا اباءهم او ابناءهم او اخوانهم او عشيرتهم اولئك كتب في قلوبهم الايمان وايدهم بروح منه ويدخلهم جنات تجري من تحتها الانهار خالدين فيها رضي الله عنهم ورضوا عنه اولئك حزب الله الا ان حزب الله هم المفلحون');
INSERT INTO "imlaei_simple" VALUES(59,1,'59:1','سبح لله ما في السماوات وما في الارض وهو العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(59,2,'59:2','هو الذي اخرج الذين كفروا من اهل الكتاب من ديارهم لاول الحشر ما ظننتم ان يخرجوا وظنوا انهم مانعتهم حصونهم من الله فاتاهم الله من حيث لم يحتسبوا وقذف في قلوبهم الرعب يخربون بيوتهم بايديهم وايدي المؤمنين فاعتبروا يا اولي الابصار');
INSERT INTO "imlaei_simple" VALUES(59,3,'59:3','ولولا ان كتب الله عليهم الجلاء لعذبهم في الدنيا ولهم في الاخرة عذاب النار');
INSERT INTO "imlaei_simple" VALUES(59,4,'59:4','ذلك بانهم شاقوا الله ورسوله ومن يشاق الله فان الله شديد العقاب');
INSERT INTO "imlaei_simple" VALUES(59,5,'59:5','ما قطعتم من لينة او تركتموها قائمة على اصولها فباذن الله وليخزي الفاسقين');
INSERT INTO "imlaei_simple" VALUES(59,6,'59:6','وما افاء الله على رسوله منهم فما اوجفتم عليه من خيل ولا ركاب ولكن الله يسلط رسله على من يشاء والله على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(59,7,'59:7','ما افاء الله على رسوله من اهل القرى فلله وللرسول ولذي القربى واليتامى والمساكين وابن السبيل كي لا يكون دولة بين الاغنياء منكم وما اتاكم الرسول فخذوه وما نهاكم عنه فانتهوا واتقوا الله ان الله شديد العقاب');
INSERT INTO "imlaei_simple" VALUES(59,8,'59:8','للفقراء المهاجرين الذين اخرجوا من ديارهم واموالهم يبتغون فضلا من الله ورضوانا وينصرون الله ورسوله اولئك هم الصادقون');
INSERT INTO "imlaei_simple" VALUES(59,9,'59:9','والذين تبوءوا الدار والايمان من قبلهم يحبون من هاجر اليهم ولا يجدون في صدورهم حاجة مما اوتوا ويؤثرون على انفسهم ولو كان بهم خصاصة ومن يوق شح نفسه فاولئك هم المفلحون');
INSERT INTO "imlaei_simple" VALUES(59,10,'59:10','والذين جاءوا من بعدهم يقولون ربنا اغفر لنا ولاخواننا الذين سبقونا بالايمان ولا تجعل في قلوبنا غلا للذين امنوا ربنا انك رءوف رحيم');
INSERT INTO "imlaei_simple" VALUES(59,11,'59:11','الم تر الى الذين نافقوا يقولون لاخوانهم الذين كفروا من اهل الكتاب لئن اخرجتم لنخرجن معكم ولا نطيع فيكم احدا ابدا وان قوتلتم لننصرنكم والله يشهد انهم لكاذبون');
INSERT INTO "imlaei_simple" VALUES(59,12,'59:12','لئن اخرجوا لا يخرجون معهم ولئن قوتلوا لا ينصرونهم ولئن نصروهم ليولن الادبار ثم لا ينصرون');
INSERT INTO "imlaei_simple" VALUES(59,13,'59:13','لانتم اشد رهبة في صدورهم من الله ذلك بانهم قوم لا يفقهون');
INSERT INTO "imlaei_simple" VALUES(59,14,'59:14','لا يقاتلونكم جميعا الا في قرى محصنة او من وراء جدر باسهم بينهم شديد تحسبهم جميعا وقلوبهم شتى ذلك بانهم قوم لا يعقلون');
INSERT INTO "imlaei_simple" VALUES(59,15,'59:15','كمثل الذين من قبلهم قريبا ذاقوا وبال امرهم ولهم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(59,16,'59:16','كمثل الشيطان اذ قال للانسان اكفر فلما كفر قال اني بريء منك اني اخاف الله رب العالمين');
INSERT INTO "imlaei_simple" VALUES(59,17,'59:17','فكان عاقبتهما انهما في النار خالدين فيها وذلك جزاء الظالمين');
INSERT INTO "imlaei_simple" VALUES(59,18,'59:18','يا ايها الذين امنوا اتقوا الله ولتنظر نفس ما قدمت لغد واتقوا الله ان الله خبير بما تعملون');
INSERT INTO "imlaei_simple" VALUES(59,19,'59:19','ولا تكونوا كالذين نسوا الله فانساهم انفسهم اولئك هم الفاسقون');
INSERT INTO "imlaei_simple" VALUES(59,20,'59:20','لا يستوي اصحاب النار واصحاب الجنة اصحاب الجنة هم الفائزون');
INSERT INTO "imlaei_simple" VALUES(59,21,'59:21','لو انزلنا هذا القران على جبل لرايته خاشعا متصدعا من خشية الله وتلك الامثال نضربها للناس لعلهم يتفكرون');
INSERT INTO "imlaei_simple" VALUES(59,22,'59:22','هو الله الذي لا اله الا هو عالم الغيب والشهادة هو الرحمن الرحيم');
INSERT INTO "imlaei_simple" VALUES(59,23,'59:23','هو الله الذي لا اله الا هو الملك القدوس السلام المؤمن المهيمن العزيز الجبار المتكبر سبحان الله عما يشركون');
INSERT INTO "imlaei_simple" VALUES(59,24,'59:24','هو الله الخالق البارئ المصور له الاسماء الحسنى يسبح له ما في السماوات والارض وهو العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(60,1,'60:1','يا ايها الذين امنوا لا تتخذوا عدوي وعدوكم اولياء تلقون اليهم بالمودة وقد كفروا بما جاءكم من الحق يخرجون الرسول واياكم ان تؤمنوا بالله ربكم ان كنتم خرجتم جهادا في سبيلي وابتغاء مرضاتي تسرون اليهم بالمودة وانا اعلم بما اخفيتم وما اعلنتم ومن يفعله منكم فقد ضل سواء السبيل');
INSERT INTO "imlaei_simple" VALUES(60,2,'60:2','ان يثقفوكم يكونوا لكم اعداء ويبسطوا اليكم ايديهم والسنتهم بالسوء وودوا لو تكفرون');
INSERT INTO "imlaei_simple" VALUES(60,3,'60:3','لن تنفعكم ارحامكم ولا اولادكم يوم القيامة يفصل بينكم والله بما تعملون بصير');
INSERT INTO "imlaei_simple" VALUES(60,4,'60:4','قد كانت لكم اسوة حسنة في ابراهيم والذين معه اذ قالوا لقومهم انا براء منكم ومما تعبدون من دون الله كفرنا بكم وبدا بيننا وبينكم العداوة والبغضاء ابدا حتى تؤمنوا بالله وحده الا قول ابراهيم لابيه لاستغفرن لك وما املك لك من الله من شيء ربنا عليك توكلنا واليك انبنا واليك المصير');
INSERT INTO "imlaei_simple" VALUES(60,5,'60:5','ربنا لا تجعلنا فتنة للذين كفروا واغفر لنا ربنا انك انت العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(60,6,'60:6','لقد كان لكم فيهم اسوة حسنة لمن كان يرجو الله واليوم الاخر ومن يتول فان الله هو الغني الحميد');
INSERT INTO "imlaei_simple" VALUES(60,7,'60:7','عسى الله ان يجعل بينكم وبين الذين عاديتم منهم مودة والله قدير والله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(60,8,'60:8','لا ينهاكم الله عن الذين لم يقاتلوكم في الدين ولم يخرجوكم من دياركم ان تبروهم وتقسطوا اليهم ان الله يحب المقسطين');
INSERT INTO "imlaei_simple" VALUES(60,9,'60:9','انما ينهاكم الله عن الذين قاتلوكم في الدين واخرجوكم من دياركم وظاهروا على اخراجكم ان تولوهم ومن يتولهم فاولئك هم الظالمون');
INSERT INTO "imlaei_simple" VALUES(60,10,'60:10','يا ايها الذين امنوا اذا جاءكم المؤمنات مهاجرات فامتحنوهن الله اعلم بايمانهن فان علمتموهن مؤمنات فلا ترجعوهن الى الكفار لا هن حل لهم ولا هم يحلون لهن واتوهم ما انفقوا ولا جناح عليكم ان تنكحوهن اذا اتيتموهن اجورهن ولا تمسكوا بعصم الكوافر واسالوا ما انفقتم وليسالوا ما انفقوا ذلكم حكم الله يحكم بينكم والله عليم حكيم');
INSERT INTO "imlaei_simple" VALUES(60,11,'60:11','وان فاتكم شيء من ازواجكم الى الكفار فعاقبتم فاتوا الذين ذهبت ازواجهم مثل ما انفقوا واتقوا الله الذي انتم به مؤمنون');
INSERT INTO "imlaei_simple" VALUES(60,12,'60:12','يا ايها النبي اذا جاءك المؤمنات يبايعنك على ان لا يشركن بالله شيئا ولا يسرقن ولا يزنين ولا يقتلن اولادهن ولا ياتين ببهتان يفترينه بين ايديهن وارجلهن ولا يعصينك في معروف فبايعهن واستغفر لهن الله ان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(60,13,'60:13','يا ايها الذين امنوا لا تتولوا قوما غضب الله عليهم قد يئسوا من الاخرة كما يئس الكفار من اصحاب القبور');
INSERT INTO "imlaei_simple" VALUES(61,1,'61:1','سبح لله ما في السماوات وما في الارض وهو العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(61,2,'61:2','يا ايها الذين امنوا لم تقولون ما لا تفعلون');
INSERT INTO "imlaei_simple" VALUES(61,3,'61:3','كبر مقتا عند الله ان تقولوا ما لا تفعلون');
INSERT INTO "imlaei_simple" VALUES(61,4,'61:4','ان الله يحب الذين يقاتلون في سبيله صفا كانهم بنيان مرصوص');
INSERT INTO "imlaei_simple" VALUES(61,5,'61:5','واذ قال موسى لقومه يا قوم لم تؤذونني وقد تعلمون اني رسول الله اليكم فلما زاغوا ازاغ الله قلوبهم والله لا يهدي القوم الفاسقين');
INSERT INTO "imlaei_simple" VALUES(61,6,'61:6','واذ قال عيسى ابن مريم يا بني اسرائيل اني رسول الله اليكم مصدقا لما بين يدي من التوراة ومبشرا برسول ياتي من بعدي اسمه احمد فلما جاءهم بالبينات قالوا هذا سحر مبين');
INSERT INTO "imlaei_simple" VALUES(61,7,'61:7','ومن اظلم ممن افترى على الله الكذب وهو يدعى الى الاسلام والله لا يهدي القوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(61,8,'61:8','يريدون ليطفئوا نور الله بافواههم والله متم نوره ولو كره الكافرون');
INSERT INTO "imlaei_simple" VALUES(61,9,'61:9','هو الذي ارسل رسوله بالهدى ودين الحق ليظهره على الدين كله ولو كره المشركون');
INSERT INTO "imlaei_simple" VALUES(61,10,'61:10','يا ايها الذين امنوا هل ادلكم على تجارة تنجيكم من عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(61,11,'61:11','تؤمنون بالله ورسوله وتجاهدون في سبيل الله باموالكم وانفسكم ذلكم خير لكم ان كنتم تعلمون');
INSERT INTO "imlaei_simple" VALUES(61,12,'61:12','يغفر لكم ذنوبكم ويدخلكم جنات تجري من تحتها الانهار ومساكن طيبة في جنات عدن ذلك الفوز العظيم');
INSERT INTO "imlaei_simple" VALUES(61,13,'61:13','واخرى تحبونها نصر من الله وفتح قريب وبشر المؤمنين');
INSERT INTO "imlaei_simple" VALUES(61,14,'61:14','يا ايها الذين امنوا كونوا انصار الله كما قال عيسى ابن مريم للحواريين من انصاري الى الله قال الحواريون نحن انصار الله فامنت طائفة من بني اسرائيل وكفرت طائفة فايدنا الذين امنوا على عدوهم فاصبحوا ظاهرين');
INSERT INTO "imlaei_simple" VALUES(62,1,'62:1','يسبح لله ما في السماوات وما في الارض الملك القدوس العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(62,2,'62:2','هو الذي بعث في الاميين رسولا منهم يتلو عليهم اياته ويزكيهم ويعلمهم الكتاب والحكمة وان كانوا من قبل لفي ضلال مبين');
INSERT INTO "imlaei_simple" VALUES(62,3,'62:3','واخرين منهم لما يلحقوا بهم وهو العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(62,4,'62:4','ذلك فضل الله يؤتيه من يشاء والله ذو الفضل العظيم');
INSERT INTO "imlaei_simple" VALUES(62,5,'62:5','مثل الذين حملوا التوراة ثم لم يحملوها كمثل الحمار يحمل اسفارا بئس مثل القوم الذين كذبوا بايات الله والله لا يهدي القوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(62,6,'62:6','قل يا ايها الذين هادوا ان زعمتم انكم اولياء لله من دون الناس فتمنوا الموت ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(62,7,'62:7','ولا يتمنونه ابدا بما قدمت ايديهم والله عليم بالظالمين');
INSERT INTO "imlaei_simple" VALUES(62,8,'62:8','قل ان الموت الذي تفرون منه فانه ملاقيكم ثم تردون الى عالم الغيب والشهادة فينبئكم بما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(62,9,'62:9','يا ايها الذين امنوا اذا نودي للصلاة من يوم الجمعة فاسعوا الى ذكر الله وذروا البيع ذلكم خير لكم ان كنتم تعلمون');
INSERT INTO "imlaei_simple" VALUES(62,10,'62:10','فاذا قضيت الصلاة فانتشروا في الارض وابتغوا من فضل الله واذكروا الله كثيرا لعلكم تفلحون');
INSERT INTO "imlaei_simple" VALUES(62,11,'62:11','واذا راوا تجارة او لهوا انفضوا اليها وتركوك قائما قل ما عند الله خير من اللهو ومن التجارة والله خير الرازقين');
INSERT INTO "imlaei_simple" VALUES(63,1,'63:1','اذا جاءك المنافقون قالوا نشهد انك لرسول الله والله يعلم انك لرسوله والله يشهد ان المنافقين لكاذبون');
INSERT INTO "imlaei_simple" VALUES(63,2,'63:2','اتخذوا ايمانهم جنة فصدوا عن سبيل الله انهم ساء ما كانوا يعملون');
INSERT INTO "imlaei_simple" VALUES(63,3,'63:3','ذلك بانهم امنوا ثم كفروا فطبع على قلوبهم فهم لا يفقهون');
INSERT INTO "imlaei_simple" VALUES(63,4,'63:4','واذا رايتهم تعجبك اجسامهم وان يقولوا تسمع لقولهم كانهم خشب مسندة يحسبون كل صيحة عليهم هم العدو فاحذرهم قاتلهم الله انى يؤفكون');
INSERT INTO "imlaei_simple" VALUES(63,5,'63:5','واذا قيل لهم تعالوا يستغفر لكم رسول الله لووا رءوسهم ورايتهم يصدون وهم مستكبرون');
INSERT INTO "imlaei_simple" VALUES(63,6,'63:6','سواء عليهم استغفرت لهم ام لم تستغفر لهم لن يغفر الله لهم ان الله لا يهدي القوم الفاسقين');
INSERT INTO "imlaei_simple" VALUES(63,7,'63:7','هم الذين يقولون لا تنفقوا على من عند رسول الله حتى ينفضوا ولله خزائن السماوات والارض ولكن المنافقين لا يفقهون');
INSERT INTO "imlaei_simple" VALUES(63,8,'63:8','يقولون لئن رجعنا الى المدينة ليخرجن الاعز منها الاذل ولله العزة ولرسوله وللمؤمنين ولكن المنافقين لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(63,9,'63:9','يا ايها الذين امنوا لا تلهكم اموالكم ولا اولادكم عن ذكر الله ومن يفعل ذلك فاولئك هم الخاسرون');
INSERT INTO "imlaei_simple" VALUES(63,10,'63:10','وانفقوا من ما رزقناكم من قبل ان ياتي احدكم الموت فيقول رب لولا اخرتني الى اجل قريب فاصدق واكن من الصالحين');
INSERT INTO "imlaei_simple" VALUES(63,11,'63:11','ولن يؤخر الله نفسا اذا جاء اجلها والله خبير بما تعملون');
INSERT INTO "imlaei_simple" VALUES(64,1,'64:1','يسبح لله ما في السماوات وما في الارض له الملك وله الحمد وهو على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(64,2,'64:2','هو الذي خلقكم فمنكم كافر ومنكم مؤمن والله بما تعملون بصير');
INSERT INTO "imlaei_simple" VALUES(64,3,'64:3','خلق السماوات والارض بالحق وصوركم فاحسن صوركم واليه المصير');
INSERT INTO "imlaei_simple" VALUES(64,4,'64:4','يعلم ما في السماوات والارض ويعلم ما تسرون وما تعلنون والله عليم بذات الصدور');
INSERT INTO "imlaei_simple" VALUES(64,5,'64:5','الم ياتكم نبا الذين كفروا من قبل فذاقوا وبال امرهم ولهم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(64,6,'64:6','ذلك بانه كانت تاتيهم رسلهم بالبينات فقالوا ابشر يهدوننا فكفروا وتولوا واستغنى الله والله غني حميد');
INSERT INTO "imlaei_simple" VALUES(64,7,'64:7','زعم الذين كفروا ان لن يبعثوا قل بلى وربي لتبعثن ثم لتنبؤن بما عملتم وذلك على الله يسير');
INSERT INTO "imlaei_simple" VALUES(64,8,'64:8','فامنوا بالله ورسوله والنور الذي انزلنا والله بما تعملون خبير');
INSERT INTO "imlaei_simple" VALUES(64,9,'64:9','يوم يجمعكم ليوم الجمع ذلك يوم التغابن ومن يؤمن بالله ويعمل صالحا يكفر عنه سيئاته ويدخله جنات تجري من تحتها الانهار خالدين فيها ابدا ذلك الفوز العظيم');
INSERT INTO "imlaei_simple" VALUES(64,10,'64:10','والذين كفروا وكذبوا باياتنا اولئك اصحاب النار خالدين فيها وبئس المصير');
INSERT INTO "imlaei_simple" VALUES(64,11,'64:11','ما اصاب من مصيبة الا باذن الله ومن يؤمن بالله يهد قلبه والله بكل شيء عليم');
INSERT INTO "imlaei_simple" VALUES(64,12,'64:12','واطيعوا الله واطيعوا الرسول فان توليتم فانما على رسولنا البلاغ المبين');
INSERT INTO "imlaei_simple" VALUES(64,13,'64:13','الله لا اله الا هو وعلى الله فليتوكل المؤمنون');
INSERT INTO "imlaei_simple" VALUES(64,14,'64:14','يا ايها الذين امنوا ان من ازواجكم واولادكم عدوا لكم فاحذروهم وان تعفوا وتصفحوا وتغفروا فان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(64,15,'64:15','انما اموالكم واولادكم فتنة والله عنده اجر عظيم');
INSERT INTO "imlaei_simple" VALUES(64,16,'64:16','فاتقوا الله ما استطعتم واسمعوا واطيعوا وانفقوا خيرا لانفسكم ومن يوق شح نفسه فاولئك هم المفلحون');
INSERT INTO "imlaei_simple" VALUES(64,17,'64:17','ان تقرضوا الله قرضا حسنا يضاعفه لكم ويغفر لكم والله شكور حليم');
INSERT INTO "imlaei_simple" VALUES(64,18,'64:18','عالم الغيب والشهادة العزيز الحكيم');
INSERT INTO "imlaei_simple" VALUES(65,1,'65:1','يا ايها النبي اذا طلقتم النساء فطلقوهن لعدتهن واحصوا العدة واتقوا الله ربكم لا تخرجوهن من بيوتهن ولا يخرجن الا ان ياتين بفاحشة مبينة وتلك حدود الله ومن يتعد حدود الله فقد ظلم نفسه لا تدري لعل الله يحدث بعد ذلك امرا');
INSERT INTO "imlaei_simple" VALUES(65,2,'65:2','فاذا بلغن اجلهن فامسكوهن بمعروف او فارقوهن بمعروف واشهدوا ذوي عدل منكم واقيموا الشهادة لله ذلكم يوعظ به من كان يؤمن بالله واليوم الاخر ومن يتق الله يجعل له مخرجا');
INSERT INTO "imlaei_simple" VALUES(65,3,'65:3','ويرزقه من حيث لا يحتسب ومن يتوكل على الله فهو حسبه ان الله بالغ امره قد جعل الله لكل شيء قدرا');
INSERT INTO "imlaei_simple" VALUES(65,4,'65:4','واللائي يئسن من المحيض من نسائكم ان ارتبتم فعدتهن ثلاثة اشهر واللائي لم يحضن واولات الاحمال اجلهن ان يضعن حملهن ومن يتق الله يجعل له من امره يسرا');
INSERT INTO "imlaei_simple" VALUES(65,5,'65:5','ذلك امر الله انزله اليكم ومن يتق الله يكفر عنه سيئاته ويعظم له اجرا');
INSERT INTO "imlaei_simple" VALUES(65,6,'65:6','اسكنوهن من حيث سكنتم من وجدكم ولا تضاروهن لتضيقوا عليهن وان كن اولات حمل فانفقوا عليهن حتى يضعن حملهن فان ارضعن لكم فاتوهن اجورهن واتمروا بينكم بمعروف وان تعاسرتم فسترضع له اخرى');
INSERT INTO "imlaei_simple" VALUES(65,7,'65:7','لينفق ذو سعة من سعته ومن قدر عليه رزقه فلينفق مما اتاه الله لا يكلف الله نفسا الا ما اتاها سيجعل الله بعد عسر يسرا');
INSERT INTO "imlaei_simple" VALUES(65,8,'65:8','وكاين من قرية عتت عن امر ربها ورسله فحاسبناها حسابا شديدا وعذبناها عذابا نكرا');
INSERT INTO "imlaei_simple" VALUES(65,9,'65:9','فذاقت وبال امرها وكان عاقبة امرها خسرا');
INSERT INTO "imlaei_simple" VALUES(65,10,'65:10','اعد الله لهم عذابا شديدا فاتقوا الله يا اولي الالباب الذين امنوا قد انزل الله اليكم ذكرا');
INSERT INTO "imlaei_simple" VALUES(65,11,'65:11','رسولا يتلو عليكم ايات الله مبينات ليخرج الذين امنوا وعملوا الصالحات من الظلمات الى النور ومن يؤمن بالله ويعمل صالحا يدخله جنات تجري من تحتها الانهار خالدين فيها ابدا قد احسن الله له رزقا');
INSERT INTO "imlaei_simple" VALUES(65,12,'65:12','الله الذي خلق سبع سماوات ومن الارض مثلهن يتنزل الامر بينهن لتعلموا ان الله على كل شيء قدير وان الله قد احاط بكل شيء علما');
INSERT INTO "imlaei_simple" VALUES(66,1,'66:1','يا ايها النبي لم تحرم ما احل الله لك تبتغي مرضات ازواجك والله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(66,2,'66:2','قد فرض الله لكم تحلة ايمانكم والله مولاكم وهو العليم الحكيم');
INSERT INTO "imlaei_simple" VALUES(66,3,'66:3','واذ اسر النبي الى بعض ازواجه حديثا فلما نبات به واظهره الله عليه عرف بعضه واعرض عن بعض فلما نباها به قالت من انباك هذا قال نباني العليم الخبير');
INSERT INTO "imlaei_simple" VALUES(66,4,'66:4','ان تتوبا الى الله فقد صغت قلوبكما وان تظاهرا عليه فان الله هو مولاه وجبريل وصالح المؤمنين والملائكة بعد ذلك ظهير');
INSERT INTO "imlaei_simple" VALUES(66,5,'66:5','عسى ربه ان طلقكن ان يبدله ازواجا خيرا منكن مسلمات مؤمنات قانتات تائبات عابدات سائحات ثيبات وابكارا');
INSERT INTO "imlaei_simple" VALUES(66,6,'66:6','يا ايها الذين امنوا قوا انفسكم واهليكم نارا وقودها الناس والحجارة عليها ملائكة غلاظ شداد لا يعصون الله ما امرهم ويفعلون ما يؤمرون');
INSERT INTO "imlaei_simple" VALUES(66,7,'66:7','يا ايها الذين كفروا لا تعتذروا اليوم انما تجزون ما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(66,8,'66:8','يا ايها الذين امنوا توبوا الى الله توبة نصوحا عسى ربكم ان يكفر عنكم سيئاتكم ويدخلكم جنات تجري من تحتها الانهار يوم لا يخزي الله النبي والذين امنوا معه نورهم يسعى بين ايديهم وبايمانهم يقولون ربنا اتمم لنا نورنا واغفر لنا انك على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(66,9,'66:9','يا ايها النبي جاهد الكفار والمنافقين واغلظ عليهم وماواهم جهنم وبئس المصير');
INSERT INTO "imlaei_simple" VALUES(66,10,'66:10','ضرب الله مثلا للذين كفروا امرات نوح وامرات لوط كانتا تحت عبدين من عبادنا صالحين فخانتاهما فلم يغنيا عنهما من الله شيئا وقيل ادخلا النار مع الداخلين');
INSERT INTO "imlaei_simple" VALUES(66,11,'66:11','وضرب الله مثلا للذين امنوا امرات فرعون اذ قالت رب ابن لي عندك بيتا في الجنة ونجني من فرعون وعمله ونجني من القوم الظالمين');
INSERT INTO "imlaei_simple" VALUES(66,12,'66:12','ومريم ابنت عمران التي احصنت فرجها فنفخنا فيه من روحنا وصدقت بكلمات ربها وكتبه وكانت من القانتين');
INSERT INTO "imlaei_simple" VALUES(67,1,'67:1','تبارك الذي بيده الملك وهو على كل شيء قدير');
INSERT INTO "imlaei_simple" VALUES(67,2,'67:2','الذي خلق الموت والحياة ليبلوكم ايكم احسن عملا وهو العزيز الغفور');
INSERT INTO "imlaei_simple" VALUES(67,3,'67:3','الذي خلق سبع سماوات طباقا ما ترى في خلق الرحمن من تفاوت فارجع البصر هل ترى من فطور');
INSERT INTO "imlaei_simple" VALUES(67,4,'67:4','ثم ارجع البصر كرتين ينقلب اليك البصر خاسئا وهو حسير');
INSERT INTO "imlaei_simple" VALUES(67,5,'67:5','ولقد زينا السماء الدنيا بمصابيح وجعلناها رجوما للشياطين واعتدنا لهم عذاب السعير');
INSERT INTO "imlaei_simple" VALUES(67,6,'67:6','وللذين كفروا بربهم عذاب جهنم وبئس المصير');
INSERT INTO "imlaei_simple" VALUES(67,7,'67:7','اذا القوا فيها سمعوا لها شهيقا وهي تفور');
INSERT INTO "imlaei_simple" VALUES(67,8,'67:8','تكاد تميز من الغيظ كلما القي فيها فوج سالهم خزنتها الم ياتكم نذير');
INSERT INTO "imlaei_simple" VALUES(67,9,'67:9','قالوا بلى قد جاءنا نذير فكذبنا وقلنا ما نزل الله من شيء ان انتم الا في ضلال كبير');
INSERT INTO "imlaei_simple" VALUES(67,10,'67:10','وقالوا لو كنا نسمع او نعقل ما كنا في اصحاب السعير');
INSERT INTO "imlaei_simple" VALUES(67,11,'67:11','فاعترفوا بذنبهم فسحقا لاصحاب السعير');
INSERT INTO "imlaei_simple" VALUES(67,12,'67:12','ان الذين يخشون ربهم بالغيب لهم مغفرة واجر كبير');
INSERT INTO "imlaei_simple" VALUES(67,13,'67:13','واسروا قولكم او اجهروا به انه عليم بذات الصدور');
INSERT INTO "imlaei_simple" VALUES(67,14,'67:14','الا يعلم من خلق وهو اللطيف الخبير');
INSERT INTO "imlaei_simple" VALUES(67,15,'67:15','هو الذي جعل لكم الارض ذلولا فامشوا في مناكبها وكلوا من رزقه واليه النشور');
INSERT INTO "imlaei_simple" VALUES(67,16,'67:16','اامنتم من في السماء ان يخسف بكم الارض فاذا هي تمور');
INSERT INTO "imlaei_simple" VALUES(67,17,'67:17','ام امنتم من في السماء ان يرسل عليكم حاصبا فستعلمون كيف نذير');
INSERT INTO "imlaei_simple" VALUES(67,18,'67:18','ولقد كذب الذين من قبلهم فكيف كان نكير');
INSERT INTO "imlaei_simple" VALUES(67,19,'67:19','اولم يروا الى الطير فوقهم صافات ويقبضن ما يمسكهن الا الرحمن انه بكل شيء بصير');
INSERT INTO "imlaei_simple" VALUES(67,20,'67:20','امن هذا الذي هو جند لكم ينصركم من دون الرحمن ان الكافرون الا في غرور');
INSERT INTO "imlaei_simple" VALUES(67,21,'67:21','امن هذا الذي يرزقكم ان امسك رزقه بل لجوا في عتو ونفور');
INSERT INTO "imlaei_simple" VALUES(67,22,'67:22','افمن يمشي مكبا على وجهه اهدى امن يمشي سويا على صراط مستقيم');
INSERT INTO "imlaei_simple" VALUES(67,23,'67:23','قل هو الذي انشاكم وجعل لكم السمع والابصار والافئدة قليلا ما تشكرون');
INSERT INTO "imlaei_simple" VALUES(67,24,'67:24','قل هو الذي ذراكم في الارض واليه تحشرون');
INSERT INTO "imlaei_simple" VALUES(67,25,'67:25','ويقولون متى هذا الوعد ان كنتم صادقين');
INSERT INTO "imlaei_simple" VALUES(67,26,'67:26','قل انما العلم عند الله وانما انا نذير مبين');
INSERT INTO "imlaei_simple" VALUES(67,27,'67:27','فلما راوه زلفة سيئت وجوه الذين كفروا وقيل هذا الذي كنتم به تدعون');
INSERT INTO "imlaei_simple" VALUES(67,28,'67:28','قل ارايتم ان اهلكني الله ومن معي او رحمنا فمن يجير الكافرين من عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(67,29,'67:29','قل هو الرحمن امنا به وعليه توكلنا فستعلمون من هو في ضلال مبين');
INSERT INTO "imlaei_simple" VALUES(67,30,'67:30','قل ارايتم ان اصبح ماؤكم غورا فمن ياتيكم بماء معين');
INSERT INTO "imlaei_simple" VALUES(68,1,'68:1','ن والقلم وما يسطرون');
INSERT INTO "imlaei_simple" VALUES(68,2,'68:2','ما انت بنعمة ربك بمجنون');
INSERT INTO "imlaei_simple" VALUES(68,3,'68:3','وان لك لاجرا غير ممنون');
INSERT INTO "imlaei_simple" VALUES(68,4,'68:4','وانك لعلى خلق عظيم');
INSERT INTO "imlaei_simple" VALUES(68,5,'68:5','فستبصر ويبصرون');
INSERT INTO "imlaei_simple" VALUES(68,6,'68:6','باييكم المفتون');
INSERT INTO "imlaei_simple" VALUES(68,7,'68:7','ان ربك هو اعلم بمن ضل عن سبيله وهو اعلم بالمهتدين');
INSERT INTO "imlaei_simple" VALUES(68,8,'68:8','فلا تطع المكذبين');
INSERT INTO "imlaei_simple" VALUES(68,9,'68:9','ودوا لو تدهن فيدهنون');
INSERT INTO "imlaei_simple" VALUES(68,10,'68:10','ولا تطع كل حلاف مهين');
INSERT INTO "imlaei_simple" VALUES(68,11,'68:11','هماز مشاء بنميم');
INSERT INTO "imlaei_simple" VALUES(68,12,'68:12','مناع للخير معتد اثيم');
INSERT INTO "imlaei_simple" VALUES(68,13,'68:13','عتل بعد ذلك زنيم');
INSERT INTO "imlaei_simple" VALUES(68,14,'68:14','ان كان ذا مال وبنين');
INSERT INTO "imlaei_simple" VALUES(68,15,'68:15','اذا تتلى عليه اياتنا قال اساطير الاولين');
INSERT INTO "imlaei_simple" VALUES(68,16,'68:16','سنسمه على الخرطوم');
INSERT INTO "imlaei_simple" VALUES(68,17,'68:17','انا بلوناهم كما بلونا اصحاب الجنة اذ اقسموا ليصرمنها مصبحين');
INSERT INTO "imlaei_simple" VALUES(68,18,'68:18','ولا يستثنون');
INSERT INTO "imlaei_simple" VALUES(68,19,'68:19','فطاف عليها طائف من ربك وهم نائمون');
INSERT INTO "imlaei_simple" VALUES(68,20,'68:20','فاصبحت كالصريم');
INSERT INTO "imlaei_simple" VALUES(68,21,'68:21','فتنادوا مصبحين');
INSERT INTO "imlaei_simple" VALUES(68,22,'68:22','ان اغدوا على حرثكم ان كنتم صارمين');
INSERT INTO "imlaei_simple" VALUES(68,23,'68:23','فانطلقوا وهم يتخافتون');
INSERT INTO "imlaei_simple" VALUES(68,24,'68:24','ان لا يدخلنها اليوم عليكم مسكين');
INSERT INTO "imlaei_simple" VALUES(68,25,'68:25','وغدوا على حرد قادرين');
INSERT INTO "imlaei_simple" VALUES(68,26,'68:26','فلما راوها قالوا انا لضالون');
INSERT INTO "imlaei_simple" VALUES(68,27,'68:27','بل نحن محرومون');
INSERT INTO "imlaei_simple" VALUES(68,28,'68:28','قال اوسطهم الم اقل لكم لولا تسبحون');
INSERT INTO "imlaei_simple" VALUES(68,29,'68:29','قالوا سبحان ربنا انا كنا ظالمين');
INSERT INTO "imlaei_simple" VALUES(68,30,'68:30','فاقبل بعضهم على بعض يتلاومون');
INSERT INTO "imlaei_simple" VALUES(68,31,'68:31','قالوا يا ويلنا انا كنا طاغين');
INSERT INTO "imlaei_simple" VALUES(68,32,'68:32','عسى ربنا ان يبدلنا خيرا منها انا الى ربنا راغبون');
INSERT INTO "imlaei_simple" VALUES(68,33,'68:33','كذلك العذاب ولعذاب الاخرة اكبر لو كانوا يعلمون');
INSERT INTO "imlaei_simple" VALUES(68,34,'68:34','ان للمتقين عند ربهم جنات النعيم');
INSERT INTO "imlaei_simple" VALUES(68,35,'68:35','افنجعل المسلمين كالمجرمين');
INSERT INTO "imlaei_simple" VALUES(68,36,'68:36','ما لكم كيف تحكمون');
INSERT INTO "imlaei_simple" VALUES(68,37,'68:37','ام لكم كتاب فيه تدرسون');
INSERT INTO "imlaei_simple" VALUES(68,38,'68:38','ان لكم فيه لما تخيرون');
INSERT INTO "imlaei_simple" VALUES(68,39,'68:39','ام لكم ايمان علينا بالغة الى يوم القيامة ان لكم لما تحكمون');
INSERT INTO "imlaei_simple" VALUES(68,40,'68:40','سلهم ايهم بذلك زعيم');
INSERT INTO "imlaei_simple" VALUES(68,41,'68:41','ام لهم شركاء فلياتوا بشركائهم ان كانوا صادقين');
INSERT INTO "imlaei_simple" VALUES(68,42,'68:42','يوم يكشف عن ساق ويدعون الى السجود فلا يستطيعون');
INSERT INTO "imlaei_simple" VALUES(68,43,'68:43','خاشعة ابصارهم ترهقهم ذلة وقد كانوا يدعون الى السجود وهم سالمون');
INSERT INTO "imlaei_simple" VALUES(68,44,'68:44','فذرني ومن يكذب بهذا الحديث سنستدرجهم من حيث لا يعلمون');
INSERT INTO "imlaei_simple" VALUES(68,45,'68:45','واملي لهم ان كيدي متين');
INSERT INTO "imlaei_simple" VALUES(68,46,'68:46','ام تسالهم اجرا فهم من مغرم مثقلون');
INSERT INTO "imlaei_simple" VALUES(68,47,'68:47','ام عندهم الغيب فهم يكتبون');
INSERT INTO "imlaei_simple" VALUES(68,48,'68:48','فاصبر لحكم ربك ولا تكن كصاحب الحوت اذ نادى وهو مكظوم');
INSERT INTO "imlaei_simple" VALUES(68,49,'68:49','لولا ان تداركه نعمة من ربه لنبذ بالعراء وهو مذموم');
INSERT INTO "imlaei_simple" VALUES(68,50,'68:50','فاجتباه ربه فجعله من الصالحين');
INSERT INTO "imlaei_simple" VALUES(68,51,'68:51','وان يكاد الذين كفروا ليزلقونك بابصارهم لما سمعوا الذكر ويقولون انه لمجنون');
INSERT INTO "imlaei_simple" VALUES(68,52,'68:52','وما هو الا ذكر للعالمين');
INSERT INTO "imlaei_simple" VALUES(69,1,'69:1','الحاقة');
INSERT INTO "imlaei_simple" VALUES(69,2,'69:2','ما الحاقة');
INSERT INTO "imlaei_simple" VALUES(69,3,'69:3','وما ادراك ما الحاقة');
INSERT INTO "imlaei_simple" VALUES(69,4,'69:4','كذبت ثمود وعاد بالقارعة');
INSERT INTO "imlaei_simple" VALUES(69,5,'69:5','فاما ثمود فاهلكوا بالطاغية');
INSERT INTO "imlaei_simple" VALUES(69,6,'69:6','واما عاد فاهلكوا بريح صرصر عاتية');
INSERT INTO "imlaei_simple" VALUES(69,7,'69:7','سخرها عليهم سبع ليال وثمانية ايام حسوما فترى القوم فيها صرعى كانهم اعجاز نخل خاوية');
INSERT INTO "imlaei_simple" VALUES(69,8,'69:8','فهل ترى لهم من باقية');
INSERT INTO "imlaei_simple" VALUES(69,9,'69:9','وجاء فرعون ومن قبله والمؤتفكات بالخاطئة');
INSERT INTO "imlaei_simple" VALUES(69,10,'69:10','فعصوا رسول ربهم فاخذهم اخذة رابية');
INSERT INTO "imlaei_simple" VALUES(69,11,'69:11','انا لما طغى الماء حملناكم في الجارية');
INSERT INTO "imlaei_simple" VALUES(69,12,'69:12','لنجعلها لكم تذكرة وتعيها اذن واعية');
INSERT INTO "imlaei_simple" VALUES(69,13,'69:13','فاذا نفخ في الصور نفخة واحدة');
INSERT INTO "imlaei_simple" VALUES(69,14,'69:14','وحملت الارض والجبال فدكتا دكة واحدة');
INSERT INTO "imlaei_simple" VALUES(69,15,'69:15','فيومئذ وقعت الواقعة');
INSERT INTO "imlaei_simple" VALUES(69,16,'69:16','وانشقت السماء فهي يومئذ واهية');
INSERT INTO "imlaei_simple" VALUES(69,17,'69:17','والملك على ارجائها ويحمل عرش ربك فوقهم يومئذ ثمانية');
INSERT INTO "imlaei_simple" VALUES(69,18,'69:18','يومئذ تعرضون لا تخفى منكم خافية');
INSERT INTO "imlaei_simple" VALUES(69,19,'69:19','فاما من اوتي كتابه بيمينه فيقول هاؤم اقرءوا كتابيه');
INSERT INTO "imlaei_simple" VALUES(69,20,'69:20','اني ظننت اني ملاق حسابيه');
INSERT INTO "imlaei_simple" VALUES(69,21,'69:21','فهو في عيشة راضية');
INSERT INTO "imlaei_simple" VALUES(69,22,'69:22','في جنة عالية');
INSERT INTO "imlaei_simple" VALUES(69,23,'69:23','قطوفها دانية');
INSERT INTO "imlaei_simple" VALUES(69,24,'69:24','كلوا واشربوا هنيئا بما اسلفتم في الايام الخالية');
INSERT INTO "imlaei_simple" VALUES(69,25,'69:25','واما من اوتي كتابه بشماله فيقول يا ليتني لم اوت كتابيه');
INSERT INTO "imlaei_simple" VALUES(69,26,'69:26','ولم ادر ما حسابيه');
INSERT INTO "imlaei_simple" VALUES(69,27,'69:27','يا ليتها كانت القاضية');
INSERT INTO "imlaei_simple" VALUES(69,28,'69:28','ما اغنى عني ماليه');
INSERT INTO "imlaei_simple" VALUES(69,29,'69:29','هلك عني سلطانيه');
INSERT INTO "imlaei_simple" VALUES(69,30,'69:30','خذوه فغلوه');
INSERT INTO "imlaei_simple" VALUES(69,31,'69:31','ثم الجحيم صلوه');
INSERT INTO "imlaei_simple" VALUES(69,32,'69:32','ثم في سلسلة ذرعها سبعون ذراعا فاسلكوه');
INSERT INTO "imlaei_simple" VALUES(69,33,'69:33','انه كان لا يؤمن بالله العظيم');
INSERT INTO "imlaei_simple" VALUES(69,34,'69:34','ولا يحض على طعام المسكين');
INSERT INTO "imlaei_simple" VALUES(69,35,'69:35','فليس له اليوم هاهنا حميم');
INSERT INTO "imlaei_simple" VALUES(69,36,'69:36','ولا طعام الا من غسلين');
INSERT INTO "imlaei_simple" VALUES(69,37,'69:37','لا ياكله الا الخاطئون');
INSERT INTO "imlaei_simple" VALUES(69,38,'69:38','فلا اقسم بما تبصرون');
INSERT INTO "imlaei_simple" VALUES(69,39,'69:39','وما لا تبصرون');
INSERT INTO "imlaei_simple" VALUES(69,40,'69:40','انه لقول رسول كريم');
INSERT INTO "imlaei_simple" VALUES(69,41,'69:41','وما هو بقول شاعر قليلا ما تؤمنون');
INSERT INTO "imlaei_simple" VALUES(69,42,'69:42','ولا بقول كاهن قليلا ما تذكرون');
INSERT INTO "imlaei_simple" VALUES(69,43,'69:43','تنزيل من رب العالمين');
INSERT INTO "imlaei_simple" VALUES(69,44,'69:44','ولو تقول علينا بعض الاقاويل');
INSERT INTO "imlaei_simple" VALUES(69,45,'69:45','لاخذنا منه باليمين');
INSERT INTO "imlaei_simple" VALUES(69,46,'69:46','ثم لقطعنا منه الوتين');
INSERT INTO "imlaei_simple" VALUES(69,47,'69:47','فما منكم من احد عنه حاجزين');
INSERT INTO "imlaei_simple" VALUES(69,48,'69:48','وانه لتذكرة للمتقين');
INSERT INTO "imlaei_simple" VALUES(69,49,'69:49','وانا لنعلم ان منكم مكذبين');
INSERT INTO "imlaei_simple" VALUES(69,50,'69:50','وانه لحسرة على الكافرين');
INSERT INTO "imlaei_simple" VALUES(69,51,'69:51','وانه لحق اليقين');
INSERT INTO "imlaei_simple" VALUES(69,52,'69:52','فسبح باسم ربك العظيم');
INSERT INTO "imlaei_simple" VALUES(70,1,'70:1','سال سائل بعذاب واقع');
INSERT INTO "imlaei_simple" VALUES(70,2,'70:2','للكافرين ليس له دافع');
INSERT INTO "imlaei_simple" VALUES(70,3,'70:3','من الله ذي المعارج');
INSERT INTO "imlaei_simple" VALUES(70,4,'70:4','تعرج الملائكة والروح اليه في يوم كان مقداره خمسين الف سنة');
INSERT INTO "imlaei_simple" VALUES(70,5,'70:5','فاصبر صبرا جميلا');
INSERT INTO "imlaei_simple" VALUES(70,6,'70:6','انهم يرونه بعيدا');
INSERT INTO "imlaei_simple" VALUES(70,7,'70:7','ونراه قريبا');
INSERT INTO "imlaei_simple" VALUES(70,8,'70:8','يوم تكون السماء كالمهل');
INSERT INTO "imlaei_simple" VALUES(70,9,'70:9','وتكون الجبال كالعهن');
INSERT INTO "imlaei_simple" VALUES(70,10,'70:10','ولا يسال حميم حميما');
INSERT INTO "imlaei_simple" VALUES(70,11,'70:11','يبصرونهم يود المجرم لو يفتدي من عذاب يومئذ ببنيه');
INSERT INTO "imlaei_simple" VALUES(70,12,'70:12','وصاحبته واخيه');
INSERT INTO "imlaei_simple" VALUES(70,13,'70:13','وفصيلته التي تؤويه');
INSERT INTO "imlaei_simple" VALUES(70,14,'70:14','ومن في الارض جميعا ثم ينجيه');
INSERT INTO "imlaei_simple" VALUES(70,15,'70:15','كلا انها لظى');
INSERT INTO "imlaei_simple" VALUES(70,16,'70:16','نزاعة للشوى');
INSERT INTO "imlaei_simple" VALUES(70,17,'70:17','تدعو من ادبر وتولى');
INSERT INTO "imlaei_simple" VALUES(70,18,'70:18','وجمع فاوعى');
INSERT INTO "imlaei_simple" VALUES(70,19,'70:19','ان الانسان خلق هلوعا');
INSERT INTO "imlaei_simple" VALUES(70,20,'70:20','اذا مسه الشر جزوعا');
INSERT INTO "imlaei_simple" VALUES(70,21,'70:21','واذا مسه الخير منوعا');
INSERT INTO "imlaei_simple" VALUES(70,22,'70:22','الا المصلين');
INSERT INTO "imlaei_simple" VALUES(70,23,'70:23','الذين هم على صلاتهم دائمون');
INSERT INTO "imlaei_simple" VALUES(70,24,'70:24','والذين في اموالهم حق معلوم');
INSERT INTO "imlaei_simple" VALUES(70,25,'70:25','للسائل والمحروم');
INSERT INTO "imlaei_simple" VALUES(70,26,'70:26','والذين يصدقون بيوم الدين');
INSERT INTO "imlaei_simple" VALUES(70,27,'70:27','والذين هم من عذاب ربهم مشفقون');
INSERT INTO "imlaei_simple" VALUES(70,28,'70:28','ان عذاب ربهم غير مامون');
INSERT INTO "imlaei_simple" VALUES(70,29,'70:29','والذين هم لفروجهم حافظون');
INSERT INTO "imlaei_simple" VALUES(70,30,'70:30','الا على ازواجهم او ما ملكت ايمانهم فانهم غير ملومين');
INSERT INTO "imlaei_simple" VALUES(70,31,'70:31','فمن ابتغى وراء ذلك فاولئك هم العادون');
INSERT INTO "imlaei_simple" VALUES(70,32,'70:32','والذين هم لاماناتهم وعهدهم راعون');
INSERT INTO "imlaei_simple" VALUES(70,33,'70:33','والذين هم بشهاداتهم قائمون');
INSERT INTO "imlaei_simple" VALUES(70,34,'70:34','والذين هم على صلاتهم يحافظون');
INSERT INTO "imlaei_simple" VALUES(70,35,'70:35','اولئك في جنات مكرمون');
INSERT INTO "imlaei_simple" VALUES(70,36,'70:36','فمال الذين كفروا قبلك مهطعين');
INSERT INTO "imlaei_simple" VALUES(70,37,'70:37','عن اليمين وعن الشمال عزين');
INSERT INTO "imlaei_simple" VALUES(70,38,'70:38','ايطمع كل امرئ منهم ان يدخل جنة نعيم');
INSERT INTO "imlaei_simple" VALUES(70,39,'70:39','كلا انا خلقناهم مما يعلمون');
INSERT INTO "imlaei_simple" VALUES(70,40,'70:40','فلا اقسم برب المشارق والمغارب انا لقادرون');
INSERT INTO "imlaei_simple" VALUES(70,41,'70:41','على ان نبدل خيرا منهم وما نحن بمسبوقين');
INSERT INTO "imlaei_simple" VALUES(70,42,'70:42','فذرهم يخوضوا ويلعبوا حتى يلاقوا يومهم الذي يوعدون');
INSERT INTO "imlaei_simple" VALUES(70,43,'70:43','يوم يخرجون من الاجداث سراعا كانهم الى نصب يوفضون');
INSERT INTO "imlaei_simple" VALUES(70,44,'70:44','خاشعة ابصارهم ترهقهم ذلة ذلك اليوم الذي كانوا يوعدون');
INSERT INTO "imlaei_simple" VALUES(71,1,'71:1','انا ارسلنا نوحا الى قومه ان انذر قومك من قبل ان ياتيهم عذاب اليم');
INSERT INTO "imlaei_simple" VALUES(71,2,'71:2','قال يا قوم اني لكم نذير مبين');
INSERT INTO "imlaei_simple" VALUES(71,3,'71:3','ان اعبدوا الله واتقوه واطيعون');
INSERT INTO "imlaei_simple" VALUES(71,4,'71:4','يغفر لكم من ذنوبكم ويؤخركم الى اجل مسمى ان اجل الله اذا جاء لا يؤخر لو كنتم تعلمون');
INSERT INTO "imlaei_simple" VALUES(71,5,'71:5','قال رب اني دعوت قومي ليلا ونهارا');
INSERT INTO "imlaei_simple" VALUES(71,6,'71:6','فلم يزدهم دعائي الا فرارا');
INSERT INTO "imlaei_simple" VALUES(71,7,'71:7','واني كلما دعوتهم لتغفر لهم جعلوا اصابعهم في اذانهم واستغشوا ثيابهم واصروا واستكبروا استكبارا');
INSERT INTO "imlaei_simple" VALUES(71,8,'71:8','ثم اني دعوتهم جهارا');
INSERT INTO "imlaei_simple" VALUES(71,9,'71:9','ثم اني اعلنت لهم واسررت لهم اسرارا');
INSERT INTO "imlaei_simple" VALUES(71,10,'71:10','فقلت استغفروا ربكم انه كان غفارا');
INSERT INTO "imlaei_simple" VALUES(71,11,'71:11','يرسل السماء عليكم مدرارا');
INSERT INTO "imlaei_simple" VALUES(71,12,'71:12','ويمددكم باموال وبنين ويجعل لكم جنات ويجعل لكم انهارا');
INSERT INTO "imlaei_simple" VALUES(71,13,'71:13','ما لكم لا ترجون لله وقارا');
INSERT INTO "imlaei_simple" VALUES(71,14,'71:14','وقد خلقكم اطوارا');
INSERT INTO "imlaei_simple" VALUES(71,15,'71:15','الم تروا كيف خلق الله سبع سماوات طباقا');
INSERT INTO "imlaei_simple" VALUES(71,16,'71:16','وجعل القمر فيهن نورا وجعل الشمس سراجا');
INSERT INTO "imlaei_simple" VALUES(71,17,'71:17','والله انبتكم من الارض نباتا');
INSERT INTO "imlaei_simple" VALUES(71,18,'71:18','ثم يعيدكم فيها ويخرجكم اخراجا');
INSERT INTO "imlaei_simple" VALUES(71,19,'71:19','والله جعل لكم الارض بساطا');
INSERT INTO "imlaei_simple" VALUES(71,20,'71:20','لتسلكوا منها سبلا فجاجا');
INSERT INTO "imlaei_simple" VALUES(71,21,'71:21','قال نوح رب انهم عصوني واتبعوا من لم يزده ماله وولده الا خسارا');
INSERT INTO "imlaei_simple" VALUES(71,22,'71:22','ومكروا مكرا كبارا');
INSERT INTO "imlaei_simple" VALUES(71,23,'71:23','وقالوا لا تذرن الهتكم ولا تذرن ودا ولا سواعا ولا يغوث ويعوق ونسرا');
INSERT INTO "imlaei_simple" VALUES(71,24,'71:24','وقد اضلوا كثيرا ولا تزد الظالمين الا ضلالا');
INSERT INTO "imlaei_simple" VALUES(71,25,'71:25','مما خطيئاتهم اغرقوا فادخلوا نارا فلم يجدوا لهم من دون الله انصارا');
INSERT INTO "imlaei_simple" VALUES(71,26,'71:26','وقال نوح رب لا تذر على الارض من الكافرين ديارا');
INSERT INTO "imlaei_simple" VALUES(71,27,'71:27','انك ان تذرهم يضلوا عبادك ولا يلدوا الا فاجرا كفارا');
INSERT INTO "imlaei_simple" VALUES(71,28,'71:28','رب اغفر لي ولوالدي ولمن دخل بيتي مؤمنا وللمؤمنين والمؤمنات ولا تزد الظالمين الا تبارا');
INSERT INTO "imlaei_simple" VALUES(72,1,'72:1','قل اوحي الي انه استمع نفر من الجن فقالوا انا سمعنا قرانا عجبا');
INSERT INTO "imlaei_simple" VALUES(72,2,'72:2','يهدي الى الرشد فامنا به ولن نشرك بربنا احدا');
INSERT INTO "imlaei_simple" VALUES(72,3,'72:3','وانه تعالى جد ربنا ما اتخذ صاحبة ولا ولدا');
INSERT INTO "imlaei_simple" VALUES(72,4,'72:4','وانه كان يقول سفيهنا على الله شططا');
INSERT INTO "imlaei_simple" VALUES(72,5,'72:5','وانا ظننا ان لن تقول الانس والجن على الله كذبا');
INSERT INTO "imlaei_simple" VALUES(72,6,'72:6','وانه كان رجال من الانس يعوذون برجال من الجن فزادوهم رهقا');
INSERT INTO "imlaei_simple" VALUES(72,7,'72:7','وانهم ظنوا كما ظننتم ان لن يبعث الله احدا');
INSERT INTO "imlaei_simple" VALUES(72,8,'72:8','وانا لمسنا السماء فوجدناها ملئت حرسا شديدا وشهبا');
INSERT INTO "imlaei_simple" VALUES(72,9,'72:9','وانا كنا نقعد منها مقاعد للسمع فمن يستمع الان يجد له شهابا رصدا');
INSERT INTO "imlaei_simple" VALUES(72,10,'72:10','وانا لا ندري اشر اريد بمن في الارض ام اراد بهم ربهم رشدا');
INSERT INTO "imlaei_simple" VALUES(72,11,'72:11','وانا منا الصالحون ومنا دون ذلك كنا طرائق قددا');
INSERT INTO "imlaei_simple" VALUES(72,12,'72:12','وانا ظننا ان لن نعجز الله في الارض ولن نعجزه هربا');
INSERT INTO "imlaei_simple" VALUES(72,13,'72:13','وانا لما سمعنا الهدى امنا به فمن يؤمن بربه فلا يخاف بخسا ولا رهقا');
INSERT INTO "imlaei_simple" VALUES(72,14,'72:14','وانا منا المسلمون ومنا القاسطون فمن اسلم فاولئك تحروا رشدا');
INSERT INTO "imlaei_simple" VALUES(72,15,'72:15','واما القاسطون فكانوا لجهنم حطبا');
INSERT INTO "imlaei_simple" VALUES(72,16,'72:16','وان لو استقاموا على الطريقة لاسقيناهم ماء غدقا');
INSERT INTO "imlaei_simple" VALUES(72,17,'72:17','لنفتنهم فيه ومن يعرض عن ذكر ربه يسلكه عذابا صعدا');
INSERT INTO "imlaei_simple" VALUES(72,18,'72:18','وان المساجد لله فلا تدعوا مع الله احدا');
INSERT INTO "imlaei_simple" VALUES(72,19,'72:19','وانه لما قام عبد الله يدعوه كادوا يكونون عليه لبدا');
INSERT INTO "imlaei_simple" VALUES(72,20,'72:20','قل انما ادعو ربي ولا اشرك به احدا');
INSERT INTO "imlaei_simple" VALUES(72,21,'72:21','قل اني لا املك لكم ضرا ولا رشدا');
INSERT INTO "imlaei_simple" VALUES(72,22,'72:22','قل اني لن يجيرني من الله احد ولن اجد من دونه ملتحدا');
INSERT INTO "imlaei_simple" VALUES(72,23,'72:23','الا بلاغا من الله ورسالاته ومن يعص الله ورسوله فان له نار جهنم خالدين فيها ابدا');
INSERT INTO "imlaei_simple" VALUES(72,24,'72:24','حتى اذا راوا ما يوعدون فسيعلمون من اضعف ناصرا واقل عددا');
INSERT INTO "imlaei_simple" VALUES(72,25,'72:25','قل ان ادري اقريب ما توعدون ام يجعل له ربي امدا');
INSERT INTO "imlaei_simple" VALUES(72,26,'72:26','عالم الغيب فلا يظهر على غيبه احدا');
INSERT INTO "imlaei_simple" VALUES(72,27,'72:27','الا من ارتضى من رسول فانه يسلك من بين يديه ومن خلفه رصدا');
INSERT INTO "imlaei_simple" VALUES(72,28,'72:28','ليعلم ان قد ابلغوا رسالات ربهم واحاط بما لديهم واحصى كل شيء عددا');
INSERT INTO "imlaei_simple" VALUES(73,1,'73:1','يا ايها المزمل');
INSERT INTO "imlaei_simple" VALUES(73,2,'73:2','قم الليل الا قليلا');
INSERT INTO "imlaei_simple" VALUES(73,3,'73:3','نصفه او انقص منه قليلا');
INSERT INTO "imlaei_simple" VALUES(73,4,'73:4','او زد عليه ورتل القران ترتيلا');
INSERT INTO "imlaei_simple" VALUES(73,5,'73:5','انا سنلقي عليك قولا ثقيلا');
INSERT INTO "imlaei_simple" VALUES(73,6,'73:6','ان ناشئة الليل هي اشد وطئا واقوم قيلا');
INSERT INTO "imlaei_simple" VALUES(73,7,'73:7','ان لك في النهار سبحا طويلا');
INSERT INTO "imlaei_simple" VALUES(73,8,'73:8','واذكر اسم ربك وتبتل اليه تبتيلا');
INSERT INTO "imlaei_simple" VALUES(73,9,'73:9','رب المشرق والمغرب لا اله الا هو فاتخذه وكيلا');
INSERT INTO "imlaei_simple" VALUES(73,10,'73:10','واصبر على ما يقولون واهجرهم هجرا جميلا');
INSERT INTO "imlaei_simple" VALUES(73,11,'73:11','وذرني والمكذبين اولي النعمة ومهلهم قليلا');
INSERT INTO "imlaei_simple" VALUES(73,12,'73:12','ان لدينا انكالا وجحيما');
INSERT INTO "imlaei_simple" VALUES(73,13,'73:13','وطعاما ذا غصة وعذابا اليما');
INSERT INTO "imlaei_simple" VALUES(73,14,'73:14','يوم ترجف الارض والجبال وكانت الجبال كثيبا مهيلا');
INSERT INTO "imlaei_simple" VALUES(73,15,'73:15','انا ارسلنا اليكم رسولا شاهدا عليكم كما ارسلنا الى فرعون رسولا');
INSERT INTO "imlaei_simple" VALUES(73,16,'73:16','فعصى فرعون الرسول فاخذناه اخذا وبيلا');
INSERT INTO "imlaei_simple" VALUES(73,17,'73:17','فكيف تتقون ان كفرتم يوما يجعل الولدان شيبا');
INSERT INTO "imlaei_simple" VALUES(73,18,'73:18','السماء منفطر به كان وعده مفعولا');
INSERT INTO "imlaei_simple" VALUES(73,19,'73:19','ان هذه تذكرة فمن شاء اتخذ الى ربه سبيلا');
INSERT INTO "imlaei_simple" VALUES(73,20,'73:20','ان ربك يعلم انك تقوم ادنى من ثلثي الليل ونصفه وثلثه وطائفة من الذين معك والله يقدر الليل والنهار علم ان لن تحصوه فتاب عليكم فاقرءوا ما تيسر من القران علم ان سيكون منكم مرضى واخرون يضربون في الارض يبتغون من فضل الله واخرون يقاتلون في سبيل الله فاقرءوا ما تيسر منه واقيموا الصلاة واتوا الزكاة واقرضوا الله قرضا حسنا وما تقدموا لانفسكم من خير تجدوه عند الله هو خيرا واعظم اجرا واستغفروا الله ان الله غفور رحيم');
INSERT INTO "imlaei_simple" VALUES(74,1,'74:1','يا ايها المدثر');
INSERT INTO "imlaei_simple" VALUES(74,2,'74:2','قم فانذر');
INSERT INTO "imlaei_simple" VALUES(74,3,'74:3','وربك فكبر');
INSERT INTO "imlaei_simple" VALUES(74,4,'74:4','وثيابك فطهر');
INSERT INTO "imlaei_simple" VALUES(74,5,'74:5','والرجز فاهجر');
INSERT INTO "imlaei_simple" VALUES(74,6,'74:6','ولا تمنن تستكثر');
INSERT INTO "imlaei_simple" VALUES(74,7,'74:7','ولربك فاصبر');
INSERT INTO "imlaei_simple" VALUES(74,8,'74:8','فاذا نقر في الناقور');
INSERT INTO "imlaei_simple" VALUES(74,9,'74:9','فذلك يومئذ يوم عسير');
INSERT INTO "imlaei_simple" VALUES(74,10,'74:10','على الكافرين غير يسير');
INSERT INTO "imlaei_simple" VALUES(74,11,'74:11','ذرني ومن خلقت وحيدا');
INSERT INTO "imlaei_simple" VALUES(74,12,'74:12','وجعلت له مالا ممدودا');
INSERT INTO "imlaei_simple" VALUES(74,13,'74:13','وبنين شهودا');
INSERT INTO "imlaei_simple" VALUES(74,14,'74:14','ومهدت له تمهيدا');
INSERT INTO "imlaei_simple" VALUES(74,15,'74:15','ثم يطمع ان ازيد');
INSERT INTO "imlaei_simple" VALUES(74,16,'74:16','كلا انه كان لاياتنا عنيدا');
INSERT INTO "imlaei_simple" VALUES(74,17,'74:17','سارهقه صعودا');
INSERT INTO "imlaei_simple" VALUES(74,18,'74:18','انه فكر وقدر');
INSERT INTO "imlaei_simple" VALUES(74,19,'74:19','فقتل كيف قدر');
INSERT INTO "imlaei_simple" VALUES(74,20,'74:20','ثم قتل كيف قدر');
INSERT INTO "imlaei_simple" VALUES(74,21,'74:21','ثم نظر');
INSERT INTO "imlaei_simple" VALUES(74,22,'74:22','ثم عبس وبسر');
INSERT INTO "imlaei_simple" VALUES(74,23,'74:23','ثم ادبر واستكبر');
INSERT INTO "imlaei_simple" VALUES(74,24,'74:24','فقال ان هذا الا سحر يؤثر');
INSERT INTO "imlaei_simple" VALUES(74,25,'74:25','ان هذا الا قول البشر');
INSERT INTO "imlaei_simple" VALUES(74,26,'74:26','ساصليه سقر');
INSERT INTO "imlaei_simple" VALUES(74,27,'74:27','وما ادراك ما سقر');
INSERT INTO "imlaei_simple" VALUES(74,28,'74:28','لا تبقي ولا تذر');
INSERT INTO "imlaei_simple" VALUES(74,29,'74:29','لواحة للبشر');
INSERT INTO "imlaei_simple" VALUES(74,30,'74:30','عليها تسعة عشر');
INSERT INTO "imlaei_simple" VALUES(74,31,'74:31','وما جعلنا اصحاب النار الا ملائكة وما جعلنا عدتهم الا فتنة للذين كفروا ليستيقن الذين اوتوا الكتاب ويزداد الذين امنوا ايمانا ولا يرتاب الذين اوتوا الكتاب والمؤمنون وليقول الذين في قلوبهم مرض والكافرون ماذا اراد الله بهذا مثلا كذلك يضل الله من يشاء ويهدي من يشاء وما يعلم جنود ربك الا هو وما هي الا ذكرى للبشر');
INSERT INTO "imlaei_simple" VALUES(74,32,'74:32','كلا والقمر');
INSERT INTO "imlaei_simple" VALUES(74,33,'74:33','والليل اذ ادبر');
INSERT INTO "imlaei_simple" VALUES(74,34,'74:34','والصبح اذا اسفر');
INSERT INTO "imlaei_simple" VALUES(74,35,'74:35','انها لاحدى الكبر');
INSERT INTO "imlaei_simple" VALUES(74,36,'74:36','نذيرا للبشر');
INSERT INTO "imlaei_simple" VALUES(74,37,'74:37','لمن شاء منكم ان يتقدم او يتاخر');
INSERT INTO "imlaei_simple" VALUES(74,38,'74:38','كل نفس بما كسبت رهينة');
INSERT INTO "imlaei_simple" VALUES(74,39,'74:39','الا اصحاب اليمين');
INSERT INTO "imlaei_simple" VALUES(74,40,'74:40','في جنات يتساءلون');
INSERT INTO "imlaei_simple" VALUES(74,41,'74:41','عن المجرمين');
INSERT INTO "imlaei_simple" VALUES(74,42,'74:42','ما سلككم في سقر');
INSERT INTO "imlaei_simple" VALUES(74,43,'74:43','قالوا لم نك من المصلين');
INSERT INTO "imlaei_simple" VALUES(74,44,'74:44','ولم نك نطعم المسكين');
INSERT INTO "imlaei_simple" VALUES(74,45,'74:45','وكنا نخوض مع الخائضين');
INSERT INTO "imlaei_simple" VALUES(74,46,'74:46','وكنا نكذب بيوم الدين');
INSERT INTO "imlaei_simple" VALUES(74,47,'74:47','حتى اتانا اليقين');
INSERT INTO "imlaei_simple" VALUES(74,48,'74:48','فما تنفعهم شفاعة الشافعين');
INSERT INTO "imlaei_simple" VALUES(74,49,'74:49','فما لهم عن التذكرة معرضين');
INSERT INTO "imlaei_simple" VALUES(74,50,'74:50','كانهم حمر مستنفرة');
INSERT INTO "imlaei_simple" VALUES(74,51,'74:51','فرت من قسورة');
INSERT INTO "imlaei_simple" VALUES(74,52,'74:52','بل يريد كل امرئ منهم ان يؤتى صحفا منشرة');
INSERT INTO "imlaei_simple" VALUES(74,53,'74:53','كلا بل لا يخافون الاخرة');
INSERT INTO "imlaei_simple" VALUES(74,54,'74:54','كلا انه تذكرة');
INSERT INTO "imlaei_simple" VALUES(74,55,'74:55','فمن شاء ذكره');
INSERT INTO "imlaei_simple" VALUES(74,56,'74:56','وما يذكرون الا ان يشاء الله هو اهل التقوى واهل المغفرة');
INSERT INTO "imlaei_simple" VALUES(75,1,'75:1','لا اقسم بيوم القيامة');
INSERT INTO "imlaei_simple" VALUES(75,2,'75:2','ولا اقسم بالنفس اللوامة');
INSERT INTO "imlaei_simple" VALUES(75,3,'75:3','ايحسب الانسان الن نجمع عظامه');
INSERT INTO "imlaei_simple" VALUES(75,4,'75:4','بلى قادرين على ان نسوي بنانه');
INSERT INTO "imlaei_simple" VALUES(75,5,'75:5','بل يريد الانسان ليفجر امامه');
INSERT INTO "imlaei_simple" VALUES(75,6,'75:6','يسال ايان يوم القيامة');
INSERT INTO "imlaei_simple" VALUES(75,7,'75:7','فاذا برق البصر');
INSERT INTO "imlaei_simple" VALUES(75,8,'75:8','وخسف القمر');
INSERT INTO "imlaei_simple" VALUES(75,9,'75:9','وجمع الشمس والقمر');
INSERT INTO "imlaei_simple" VALUES(75,10,'75:10','يقول الانسان يومئذ اين المفر');
INSERT INTO "imlaei_simple" VALUES(75,11,'75:11','كلا لا وزر');
INSERT INTO "imlaei_simple" VALUES(75,12,'75:12','الى ربك يومئذ المستقر');
INSERT INTO "imlaei_simple" VALUES(75,13,'75:13','ينبا الانسان يومئذ بما قدم واخر');
INSERT INTO "imlaei_simple" VALUES(75,14,'75:14','بل الانسان على نفسه بصيرة');
INSERT INTO "imlaei_simple" VALUES(75,15,'75:15','ولو القى معاذيره');
INSERT INTO "imlaei_simple" VALUES(75,16,'75:16','لا تحرك به لسانك لتعجل به');
INSERT INTO "imlaei_simple" VALUES(75,17,'75:17','ان علينا جمعه وقرانه');
INSERT INTO "imlaei_simple" VALUES(75,18,'75:18','فاذا قراناه فاتبع قرانه');
INSERT INTO "imlaei_simple" VALUES(75,19,'75:19','ثم ان علينا بيانه');
INSERT INTO "imlaei_simple" VALUES(75,20,'75:20','كلا بل تحبون العاجلة');
INSERT INTO "imlaei_simple" VALUES(75,21,'75:21','وتذرون الاخرة');
INSERT INTO "imlaei_simple" VALUES(75,22,'75:22','وجوه يومئذ ناضرة');
INSERT INTO "imlaei_simple" VALUES(75,23,'75:23','الى ربها ناظرة');
INSERT INTO "imlaei_simple" VALUES(75,24,'75:24','ووجوه يومئذ باسرة');
INSERT INTO "imlaei_simple" VALUES(75,25,'75:25','تظن ان يفعل بها فاقرة');
INSERT INTO "imlaei_simple" VALUES(75,26,'75:26','كلا اذا بلغت التراقي');
INSERT INTO "imlaei_simple" VALUES(75,27,'75:27','وقيل من راق');
INSERT INTO "imlaei_simple" VALUES(75,28,'75:28','وظن انه الفراق');
INSERT INTO "imlaei_simple" VALUES(75,29,'75:29','والتفت الساق بالساق');
INSERT INTO "imlaei_simple" VALUES(75,30,'75:30','الى ربك يومئذ المساق');
INSERT INTO "imlaei_simple" VALUES(75,31,'75:31','فلا صدق ولا صلى');
INSERT INTO "imlaei_simple" VALUES(75,32,'75:32','ولكن كذب وتولى');
INSERT INTO "imlaei_simple" VALUES(75,33,'75:33','ثم ذهب الى اهله يتمطى');
INSERT INTO "imlaei_simple" VALUES(75,34,'75:34','اولى لك فاولى');
INSERT INTO "imlaei_simple" VALUES(75,35,'75:35','ثم اولى لك فاولى');
INSERT INTO "imlaei_simple" VALUES(75,36,'75:36','ايحسب الانسان ان يترك سدى');
INSERT INTO "imlaei_simple" VALUES(75,37,'75:37','الم يك نطفة من مني يمنى');
INSERT INTO "imlaei_simple" VALUES(75,38,'75:38','ثم كان علقة فخلق فسوى');
INSERT INTO "imlaei_simple" VALUES(75,39,'75:39','فجعل منه الزوجين الذكر والانثى');
INSERT INTO "imlaei_simple" VALUES(75,40,'75:40','اليس ذلك بقادر على ان يحيي الموتى');
INSERT INTO "imlaei_simple" VALUES(76,1,'76:1','هل اتى على الانسان حين من الدهر لم يكن شيئا مذكورا');
INSERT INTO "imlaei_simple" VALUES(76,2,'76:2','انا خلقنا الانسان من نطفة امشاج نبتليه فجعلناه سميعا بصيرا');
INSERT INTO "imlaei_simple" VALUES(76,3,'76:3','انا هديناه السبيل اما شاكرا واما كفورا');
INSERT INTO "imlaei_simple" VALUES(76,4,'76:4','انا اعتدنا للكافرين سلاسل واغلالا وسعيرا');
INSERT INTO "imlaei_simple" VALUES(76,5,'76:5','ان الابرار يشربون من كاس كان مزاجها كافورا');
INSERT INTO "imlaei_simple" VALUES(76,6,'76:6','عينا يشرب بها عباد الله يفجرونها تفجيرا');
INSERT INTO "imlaei_simple" VALUES(76,7,'76:7','يوفون بالنذر ويخافون يوما كان شره مستطيرا');
INSERT INTO "imlaei_simple" VALUES(76,8,'76:8','ويطعمون الطعام على حبه مسكينا ويتيما واسيرا');
INSERT INTO "imlaei_simple" VALUES(76,9,'76:9','انما نطعمكم لوجه الله لا نريد منكم جزاء ولا شكورا');
INSERT INTO "imlaei_simple" VALUES(76,10,'76:10','انا نخاف من ربنا يوما عبوسا قمطريرا');
INSERT INTO "imlaei_simple" VALUES(76,11,'76:11','فوقاهم الله شر ذلك اليوم ولقاهم نضرة وسرورا');
INSERT INTO "imlaei_simple" VALUES(76,12,'76:12','وجزاهم بما صبروا جنة وحريرا');
INSERT INTO "imlaei_simple" VALUES(76,13,'76:13','متكئين فيها على الارائك لا يرون فيها شمسا ولا زمهريرا');
INSERT INTO "imlaei_simple" VALUES(76,14,'76:14','ودانية عليهم ظلالها وذللت قطوفها تذليلا');
INSERT INTO "imlaei_simple" VALUES(76,15,'76:15','ويطاف عليهم بانية من فضة واكواب كانت قواريرا');
INSERT INTO "imlaei_simple" VALUES(76,16,'76:16','قوارير من فضة قدروها تقديرا');
INSERT INTO "imlaei_simple" VALUES(76,17,'76:17','ويسقون فيها كاسا كان مزاجها زنجبيلا');
INSERT INTO "imlaei_simple" VALUES(76,18,'76:18','عينا فيها تسمى سلسبيلا');
INSERT INTO "imlaei_simple" VALUES(76,19,'76:19','ويطوف عليهم ولدان مخلدون اذا رايتهم حسبتهم لؤلؤا منثورا');
INSERT INTO "imlaei_simple" VALUES(76,20,'76:20','واذا رايت ثم رايت نعيما وملكا كبيرا');
INSERT INTO "imlaei_simple" VALUES(76,21,'76:21','عاليهم ثياب سندس خضر واستبرق وحلوا اساور من فضة وسقاهم ربهم شرابا طهورا');
INSERT INTO "imlaei_simple" VALUES(76,22,'76:22','ان هذا كان لكم جزاء وكان سعيكم مشكورا');
INSERT INTO "imlaei_simple" VALUES(76,23,'76:23','انا نحن نزلنا عليك القران تنزيلا');
INSERT INTO "imlaei_simple" VALUES(76,24,'76:24','فاصبر لحكم ربك ولا تطع منهم اثما او كفورا');
INSERT INTO "imlaei_simple" VALUES(76,25,'76:25','واذكر اسم ربك بكرة واصيلا');
INSERT INTO "imlaei_simple" VALUES(76,26,'76:26','ومن الليل فاسجد له وسبحه ليلا طويلا');
INSERT INTO "imlaei_simple" VALUES(76,27,'76:27','ان هؤلاء يحبون العاجلة ويذرون وراءهم يوما ثقيلا');
INSERT INTO "imlaei_simple" VALUES(76,28,'76:28','نحن خلقناهم وشددنا اسرهم واذا شئنا بدلنا امثالهم تبديلا');
INSERT INTO "imlaei_simple" VALUES(76,29,'76:29','ان هذه تذكرة فمن شاء اتخذ الى ربه سبيلا');
INSERT INTO "imlaei_simple" VALUES(76,30,'76:30','وما تشاءون الا ان يشاء الله ان الله كان عليما حكيما');
INSERT INTO "imlaei_simple" VALUES(76,31,'76:31','يدخل من يشاء في رحمته والظالمين اعد لهم عذابا اليما');
INSERT INTO "imlaei_simple" VALUES(77,1,'77:1','والمرسلات عرفا');
INSERT INTO "imlaei_simple" VALUES(77,2,'77:2','فالعاصفات عصفا');
INSERT INTO "imlaei_simple" VALUES(77,3,'77:3','والناشرات نشرا');
INSERT INTO "imlaei_simple" VALUES(77,4,'77:4','فالفارقات فرقا');
INSERT INTO "imlaei_simple" VALUES(77,5,'77:5','فالملقيات ذكرا');
INSERT INTO "imlaei_simple" VALUES(77,6,'77:6','عذرا او نذرا');
INSERT INTO "imlaei_simple" VALUES(77,7,'77:7','انما توعدون لواقع');
INSERT INTO "imlaei_simple" VALUES(77,8,'77:8','فاذا النجوم طمست');
INSERT INTO "imlaei_simple" VALUES(77,9,'77:9','واذا السماء فرجت');
INSERT INTO "imlaei_simple" VALUES(77,10,'77:10','واذا الجبال نسفت');
INSERT INTO "imlaei_simple" VALUES(77,11,'77:11','واذا الرسل اقتت');
INSERT INTO "imlaei_simple" VALUES(77,12,'77:12','لاي يوم اجلت');
INSERT INTO "imlaei_simple" VALUES(77,13,'77:13','ليوم الفصل');
INSERT INTO "imlaei_simple" VALUES(77,14,'77:14','وما ادراك ما يوم الفصل');
INSERT INTO "imlaei_simple" VALUES(77,15,'77:15','ويل يومئذ للمكذبين');
INSERT INTO "imlaei_simple" VALUES(77,16,'77:16','الم نهلك الاولين');
INSERT INTO "imlaei_simple" VALUES(77,17,'77:17','ثم نتبعهم الاخرين');
INSERT INTO "imlaei_simple" VALUES(77,18,'77:18','كذلك نفعل بالمجرمين');
INSERT INTO "imlaei_simple" VALUES(77,19,'77:19','ويل يومئذ للمكذبين');
INSERT INTO "imlaei_simple" VALUES(77,20,'77:20','الم نخلقكم من ماء مهين');
INSERT INTO "imlaei_simple" VALUES(77,21,'77:21','فجعلناه في قرار مكين');
INSERT INTO "imlaei_simple" VALUES(77,22,'77:22','الى قدر معلوم');
INSERT INTO "imlaei_simple" VALUES(77,23,'77:23','فقدرنا فنعم القادرون');
INSERT INTO "imlaei_simple" VALUES(77,24,'77:24','ويل يومئذ للمكذبين');
INSERT INTO "imlaei_simple" VALUES(77,25,'77:25','الم نجعل الارض كفاتا');
INSERT INTO "imlaei_simple" VALUES(77,26,'77:26','احياء وامواتا');
INSERT INTO "imlaei_simple" VALUES(77,27,'77:27','وجعلنا فيها رواسي شامخات واسقيناكم ماء فراتا');
INSERT INTO "imlaei_simple" VALUES(77,28,'77:28','ويل يومئذ للمكذبين');
INSERT INTO "imlaei_simple" VALUES(77,29,'77:29','انطلقوا الى ما كنتم به تكذبون');
INSERT INTO "imlaei_simple" VALUES(77,30,'77:30','انطلقوا الى ظل ذي ثلاث شعب');
INSERT INTO "imlaei_simple" VALUES(77,31,'77:31','لا ظليل ولا يغني من اللهب');
INSERT INTO "imlaei_simple" VALUES(77,32,'77:32','انها ترمي بشرر كالقصر');
INSERT INTO "imlaei_simple" VALUES(77,33,'77:33','كانه جمالت صفر');
INSERT INTO "imlaei_simple" VALUES(77,34,'77:34','ويل يومئذ للمكذبين');
INSERT INTO "imlaei_simple" VALUES(77,35,'77:35','هذا يوم لا ينطقون');
INSERT INTO "imlaei_simple" VALUES(77,36,'77:36','ولا يؤذن لهم فيعتذرون');
INSERT INTO "imlaei_simple" VALUES(77,37,'77:37','ويل يومئذ للمكذبين');
INSERT INTO "imlaei_simple" VALUES(77,38,'77:38','هذا يوم الفصل جمعناكم والاولين');
INSERT INTO "imlaei_simple" VALUES(77,39,'77:39','فان كان لكم كيد فكيدون');
INSERT INTO "imlaei_simple" VALUES(77,40,'77:40','ويل يومئذ للمكذبين');
INSERT INTO "imlaei_simple" VALUES(77,41,'77:41','ان المتقين في ظلال وعيون');
INSERT INTO "imlaei_simple" VALUES(77,42,'77:42','وفواكه مما يشتهون');
INSERT INTO "imlaei_simple" VALUES(77,43,'77:43','كلوا واشربوا هنيئا بما كنتم تعملون');
INSERT INTO "imlaei_simple" VALUES(77,44,'77:44','انا كذلك نجزي المحسنين');
INSERT INTO "imlaei_simple" VALUES(77,45,'77:45','ويل يومئذ للمكذبين');
INSERT INTO "imlaei_simple" VALUES(77,46,'77:46','كلوا وتمتعوا قليلا انكم مجرمون');
INSERT INTO "imlaei_simple" VALUES(77,47,'77:47','ويل يومئذ للمكذبين');
INSERT INTO "imlaei_simple" VALUES(77,48,'77:48','واذا قيل لهم اركعوا لا يركعون');
INSERT INTO "imlaei_simple" VALUES(77,49,'77:49','ويل يومئذ للمكذبين');
INSERT INTO "imlaei_simple" VALUES(77,50,'77:50','فباي حديث بعده يؤمنون');
INSERT INTO "imlaei_simple" VALUES(78,1,'78:1','عم يتساءلون');
INSERT INTO "imlaei_simple" VALUES(78,2,'78:2','عن النبا العظيم');
INSERT INTO "imlaei_simple" VALUES(78,3,'78:3','الذي هم فيه مختلفون');
INSERT INTO "imlaei_simple" VALUES(78,4,'78:4','كلا سيعلمون');
INSERT INTO "imlaei_simple" VALUES(78,5,'78:5','ثم كلا سيعلمون');
INSERT INTO "imlaei_simple" VALUES(78,6,'78:6','الم نجعل الارض مهادا');
INSERT INTO "imlaei_simple" VALUES(78,7,'78:7','والجبال اوتادا');
INSERT INTO "imlaei_simple" VALUES(78,8,'78:8','وخلقناكم ازواجا');
INSERT INTO "imlaei_simple" VALUES(78,9,'78:9','وجعلنا نومكم سباتا');
INSERT INTO "imlaei_simple" VALUES(78,10,'78:10','وجعلنا الليل لباسا');
INSERT INTO "imlaei_simple" VALUES(78,11,'78:11','وجعلنا النهار معاشا');
INSERT INTO "imlaei_simple" VALUES(78,12,'78:12','وبنينا فوقكم سبعا شدادا');
INSERT INTO "imlaei_simple" VALUES(78,13,'78:13','وجعلنا سراجا وهاجا');
INSERT INTO "imlaei_simple" VALUES(78,14,'78:14','وانزلنا من المعصرات ماء ثجاجا');
INSERT INTO "imlaei_simple" VALUES(78,15,'78:15','لنخرج به حبا ونباتا');
INSERT INTO "imlaei_simple" VALUES(78,16,'78:16','وجنات الفافا');
INSERT INTO "imlaei_simple" VALUES(78,17,'78:17','ان يوم الفصل كان ميقاتا');
INSERT INTO "imlaei_simple" VALUES(78,18,'78:18','يوم ينفخ في الصور فتاتون افواجا');
INSERT INTO "imlaei_simple" VALUES(78,19,'78:19','وفتحت السماء فكانت ابوابا');
INSERT INTO "imlaei_simple" VALUES(78,20,'78:20','وسيرت الجبال فكانت سرابا');
INSERT INTO "imlaei_simple" VALUES(78,21,'78:21','ان جهنم كانت مرصادا');
INSERT INTO "imlaei_simple" VALUES(78,22,'78:22','للطاغين مابا');
INSERT INTO "imlaei_simple" VALUES(78,23,'78:23','لابثين فيها احقابا');
INSERT INTO "imlaei_simple" VALUES(78,24,'78:24','لا يذوقون فيها بردا ولا شرابا');
INSERT INTO "imlaei_simple" VALUES(78,25,'78:25','الا حميما وغساقا');
INSERT INTO "imlaei_simple" VALUES(78,26,'78:26','جزاء وفاقا');
INSERT INTO "imlaei_simple" VALUES(78,27,'78:27','انهم كانوا لا يرجون حسابا');
INSERT INTO "imlaei_simple" VALUES(78,28,'78:28','وكذبوا باياتنا كذابا');
INSERT INTO "imlaei_simple" VALUES(78,29,'78:29','وكل شيء احصيناه كتابا');
INSERT INTO "imlaei_simple" VALUES(78,30,'78:30','فذوقوا فلن نزيدكم الا عذابا');
INSERT INTO "imlaei_simple" VALUES(78,31,'78:31','ان للمتقين مفازا');
INSERT INTO "imlaei_simple" VALUES(78,32,'78:32','حدائق واعنابا');
INSERT INTO "imlaei_simple" VALUES(78,33,'78:33','وكواعب اترابا');
INSERT INTO "imlaei_simple" VALUES(78,34,'78:34','وكاسا دهاقا');
INSERT INTO "imlaei_simple" VALUES(78,35,'78:35','لا يسمعون فيها لغوا ولا كذابا');
INSERT INTO "imlaei_simple" VALUES(78,36,'78:36','جزاء من ربك عطاء حسابا');
INSERT INTO "imlaei_simple" VALUES(78,37,'78:37','رب السماوات والارض وما بينهما الرحمن لا يملكون منه خطابا');
INSERT INTO "imlaei_simple" VALUES(78,38,'78:38','يوم يقوم الروح والملائكة صفا لا يتكلمون الا من اذن له الرحمن وقال صوابا');
INSERT INTO "imlaei_simple" VALUES(78,39,'78:39','ذلك اليوم الحق فمن شاء اتخذ الى ربه مابا');
INSERT INTO "imlaei_simple" VALUES(78,40,'78:40','انا انذرناكم عذابا قريبا يوم ينظر المرء ما قدمت يداه ويقول الكافر يا ليتني كنت ترابا');
INSERT INTO "imlaei_simple" VALUES(79,1,'79:1','والنازعات غرقا');
INSERT INTO "imlaei_simple" VALUES(79,2,'79:2','والناشطات نشطا');
INSERT INTO "imlaei_simple" VALUES(79,3,'79:3','والسابحات سبحا');
INSERT INTO "imlaei_simple" VALUES(79,4,'79:4','فالسابقات سبقا');
INSERT INTO "imlaei_simple" VALUES(79,5,'79:5','فالمدبرات امرا');
INSERT INTO "imlaei_simple" VALUES(79,6,'79:6','يوم ترجف الراجفة');
INSERT INTO "imlaei_simple" VALUES(79,7,'79:7','تتبعها الرادفة');
INSERT INTO "imlaei_simple" VALUES(79,8,'79:8','قلوب يومئذ واجفة');
INSERT INTO "imlaei_simple" VALUES(79,9,'79:9','ابصارها خاشعة');
INSERT INTO "imlaei_simple" VALUES(79,10,'79:10','يقولون اانا لمردودون في الحافرة');
INSERT INTO "imlaei_simple" VALUES(79,11,'79:11','ااذا كنا عظاما نخرة');
INSERT INTO "imlaei_simple" VALUES(79,12,'79:12','قالوا تلك اذا كرة خاسرة');
INSERT INTO "imlaei_simple" VALUES(79,13,'79:13','فانما هي زجرة واحدة');
INSERT INTO "imlaei_simple" VALUES(79,14,'79:14','فاذا هم بالساهرة');
INSERT INTO "imlaei_simple" VALUES(79,15,'79:15','هل اتاك حديث موسى');
INSERT INTO "imlaei_simple" VALUES(79,16,'79:16','اذ ناداه ربه بالواد المقدس طوى');
INSERT INTO "imlaei_simple" VALUES(79,17,'79:17','اذهب الى فرعون انه طغى');
INSERT INTO "imlaei_simple" VALUES(79,18,'79:18','فقل هل لك الى ان تزكى');
INSERT INTO "imlaei_simple" VALUES(79,19,'79:19','واهديك الى ربك فتخشى');
INSERT INTO "imlaei_simple" VALUES(79,20,'79:20','فاراه الاية الكبرى');
INSERT INTO "imlaei_simple" VALUES(79,21,'79:21','فكذب وعصى');
INSERT INTO "imlaei_simple" VALUES(79,22,'79:22','ثم ادبر يسعى');
INSERT INTO "imlaei_simple" VALUES(79,23,'79:23','فحشر فنادى');
INSERT INTO "imlaei_simple" VALUES(79,24,'79:24','فقال انا ربكم الاعلى');
INSERT INTO "imlaei_simple" VALUES(79,25,'79:25','فاخذه الله نكال الاخرة والاولى');
INSERT INTO "imlaei_simple" VALUES(79,26,'79:26','ان في ذلك لعبرة لمن يخشى');
INSERT INTO "imlaei_simple" VALUES(79,27,'79:27','اانتم اشد خلقا ام السماء بناها');
INSERT INTO "imlaei_simple" VALUES(79,28,'79:28','رفع سمكها فسواها');
INSERT INTO "imlaei_simple" VALUES(79,29,'79:29','واغطش ليلها واخرج ضحاها');
INSERT INTO "imlaei_simple" VALUES(79,30,'79:30','والارض بعد ذلك دحاها');
INSERT INTO "imlaei_simple" VALUES(79,31,'79:31','اخرج منها ماءها ومرعاها');
INSERT INTO "imlaei_simple" VALUES(79,32,'79:32','والجبال ارساها');
INSERT INTO "imlaei_simple" VALUES(79,33,'79:33','متاعا لكم ولانعامكم');
INSERT INTO "imlaei_simple" VALUES(79,34,'79:34','فاذا جاءت الطامة الكبرى');
INSERT INTO "imlaei_simple" VALUES(79,35,'79:35','يوم يتذكر الانسان ما سعى');
INSERT INTO "imlaei_simple" VALUES(79,36,'79:36','وبرزت الجحيم لمن يرى');
INSERT INTO "imlaei_simple" VALUES(79,37,'79:37','فاما من طغى');
INSERT INTO "imlaei_simple" VALUES(79,38,'79:38','واثر الحياة الدنيا');
INSERT INTO "imlaei_simple" VALUES(79,39,'79:39','فان الجحيم هي الماوى');
INSERT INTO "imlaei_simple" VALUES(79,40,'79:40','واما من خاف مقام ربه ونهى النفس عن الهوى');
INSERT INTO "imlaei_simple" VALUES(79,41,'79:41','فان الجنة هي الماوى');
INSERT INTO "imlaei_simple" VALUES(79,42,'79:42','يسالونك عن الساعة ايان مرساها');
INSERT INTO "imlaei_simple" VALUES(79,43,'79:43','فيم انت من ذكراها');
INSERT INTO "imlaei_simple" VALUES(79,44,'79:44','الى ربك منتهاها');
INSERT INTO "imlaei_simple" VALUES(79,45,'79:45','انما انت منذر من يخشاها');
INSERT INTO "imlaei_simple" VALUES(79,46,'79:46','كانهم يوم يرونها لم يلبثوا الا عشية او ضحاها');
INSERT INTO "imlaei_simple" VALUES(80,1,'80:1','عبس وتولى');
INSERT INTO "imlaei_simple" VALUES(80,2,'80:2','ان جاءه الاعمى');
INSERT INTO "imlaei_simple" VALUES(80,3,'80:3','وما يدريك لعله يزكى');
INSERT INTO "imlaei_simple" VALUES(80,4,'80:4','او يذكر فتنفعه الذكرى');
INSERT INTO "imlaei_simple" VALUES(80,5,'80:5','اما من استغنى');
INSERT INTO "imlaei_simple" VALUES(80,6,'80:6','فانت له تصدى');
INSERT INTO "imlaei_simple" VALUES(80,7,'80:7','وما عليك الا يزكى');
INSERT INTO "imlaei_simple" VALUES(80,8,'80:8','واما من جاءك يسعى');
INSERT INTO "imlaei_simple" VALUES(80,9,'80:9','وهو يخشى');
INSERT INTO "imlaei_simple" VALUES(80,10,'80:10','فانت عنه تلهى');
INSERT INTO "imlaei_simple" VALUES(80,11,'80:11','كلا انها تذكرة');
INSERT INTO "imlaei_simple" VALUES(80,12,'80:12','فمن شاء ذكره');
INSERT INTO "imlaei_simple" VALUES(80,13,'80:13','في صحف مكرمة');
INSERT INTO "imlaei_simple" VALUES(80,14,'80:14','مرفوعة مطهرة');
INSERT INTO "imlaei_simple" VALUES(80,15,'80:15','بايدي سفرة');
INSERT INTO "imlaei_simple" VALUES(80,16,'80:16','كرام بررة');
INSERT INTO "imlaei_simple" VALUES(80,17,'80:17','قتل الانسان ما اكفره');
INSERT INTO "imlaei_simple" VALUES(80,18,'80:18','من اي شيء خلقه');
INSERT INTO "imlaei_simple" VALUES(80,19,'80:19','من نطفة خلقه فقدره');
INSERT INTO "imlaei_simple" VALUES(80,20,'80:20','ثم السبيل يسره');
INSERT INTO "imlaei_simple" VALUES(80,21,'80:21','ثم اماته فاقبره');
INSERT INTO "imlaei_simple" VALUES(80,22,'80:22','ثم اذا شاء انشره');
INSERT INTO "imlaei_simple" VALUES(80,23,'80:23','كلا لما يقض ما امره');
INSERT INTO "imlaei_simple" VALUES(80,24,'80:24','فلينظر الانسان الى طعامه');
INSERT INTO "imlaei_simple" VALUES(80,25,'80:25','انا صببنا الماء صبا');
INSERT INTO "imlaei_simple" VALUES(80,26,'80:26','ثم شققنا الارض شقا');
INSERT INTO "imlaei_simple" VALUES(80,27,'80:27','فانبتنا فيها حبا');
INSERT INTO "imlaei_simple" VALUES(80,28,'80:28','وعنبا وقضبا');
INSERT INTO "imlaei_simple" VALUES(80,29,'80:29','وزيتونا ونخلا');
INSERT INTO "imlaei_simple" VALUES(80,30,'80:30','وحدائق غلبا');
INSERT INTO "imlaei_simple" VALUES(80,31,'80:31','وفاكهة وابا');
INSERT INTO "imlaei_simple" VALUES(80,32,'80:32','متاعا لكم ولانعامكم');
INSERT INTO "imlaei_simple" VALUES(80,33,'80:33','فاذا جاءت الصاخة');
INSERT INTO "imlaei_simple" VALUES(80,34,'80:34','يوم يفر المرء من اخيه');
INSERT INTO "imlaei_simple" VALUES(80,35,'80:35','وامه وابيه');
INSERT INTO "imlaei_simple" VALUES(80,36,'80:36','وصاحبته وبنيه');
INSERT INTO "imlaei_simple" VALUES(80,37,'80:37','لكل امرئ منهم يومئذ شان يغنيه');
INSERT INTO "imlaei_simple" VALUES(80,38,'80:38','وجوه يومئذ مسفرة');
INSERT INTO "imlaei_simple" VALUES(80,39,'80:39','ضاحكة مستبشرة');
INSERT INTO "imlaei_simple" VALUES(80,40,'80:40','ووجوه يومئذ عليها غبرة');
INSERT INTO "imlaei_simple" VALUES(80,41,'80:41','ترهقها قترة');
INSERT INTO "imlaei_simple" VALUES(80,42,'80:42','اولئك هم الكفرة الفجرة');
INSERT INTO "imlaei_simple" VALUES(81,1,'81:1','اذا الشمس كورت');
INSERT INTO "imlaei_simple" VALUES(81,2,'81:2','واذا النجوم انكدرت');
INSERT INTO "imlaei_simple" VALUES(81,3,'81:3','واذا الجبال سيرت');
INSERT INTO "imlaei_simple" VALUES(81,4,'81:4','واذا العشار عطلت');
INSERT INTO "imlaei_simple" VALUES(81,5,'81:5','واذا الوحوش حشرت');
INSERT INTO "imlaei_simple" VALUES(81,6,'81:6','واذا البحار سجرت');
INSERT INTO "imlaei_simple" VALUES(81,7,'81:7','واذا النفوس زوجت');
INSERT INTO "imlaei_simple" VALUES(81,8,'81:8','واذا الموءودة سئلت');
INSERT INTO "imlaei_simple" VALUES(81,9,'81:9','باي ذنب قتلت');
INSERT INTO "imlaei_simple" VALUES(81,10,'81:10','واذا الصحف نشرت');
INSERT INTO "imlaei_simple" VALUES(81,11,'81:11','واذا السماء كشطت');
INSERT INTO "imlaei_simple" VALUES(81,12,'81:12','واذا الجحيم سعرت');
INSERT INTO "imlaei_simple" VALUES(81,13,'81:13','واذا الجنة ازلفت');
INSERT INTO "imlaei_simple" VALUES(81,14,'81:14','علمت نفس ما احضرت');
INSERT INTO "imlaei_simple" VALUES(81,15,'81:15','فلا اقسم بالخنس');
INSERT INTO "imlaei_simple" VALUES(81,16,'81:16','الجوار الكنس');
INSERT INTO "imlaei_simple" VALUES(81,17,'81:17','والليل اذا عسعس');
INSERT INTO "imlaei_simple" VALUES(81,18,'81:18','والصبح اذا تنفس');
INSERT INTO "imlaei_simple" VALUES(81,19,'81:19','انه لقول رسول كريم');
INSERT INTO "imlaei_simple" VALUES(81,20,'81:20','ذي قوة عند ذي العرش مكين');
INSERT INTO "imlaei_simple" VALUES(81,21,'81:21','مطاع ثم امين');
INSERT INTO "imlaei_simple" VALUES(81,22,'81:22','وما صاحبكم بمجنون');
INSERT INTO "imlaei_simple" VALUES(81,23,'81:23','ولقد راه بالافق المبين');
INSERT INTO "imlaei_simple" VALUES(81,24,'81:24','وما هو على الغيب بضنين');
INSERT INTO "imlaei_simple" VALUES(81,25,'81:25','وما هو بقول شيطان رجيم');
INSERT INTO "imlaei_simple" VALUES(81,26,'81:26','فاين تذهبون');
INSERT INTO "imlaei_simple" VALUES(81,27,'81:27','ان هو الا ذكر للعالمين');
INSERT INTO "imlaei_simple" VALUES(81,28,'81:28','لمن شاء منكم ان يستقيم');
INSERT INTO "imlaei_simple" VALUES(81,29,'81:29','وما تشاءون الا ان يشاء الله رب العالمين');
INSERT INTO "imlaei_simple" VALUES(82,1,'82:1','اذا السماء انفطرت');
INSERT INTO "imlaei_simple" VALUES(82,2,'82:2','واذا الكواكب انتثرت');
INSERT INTO "imlaei_simple" VALUES(82,3,'82:3','واذا البحار فجرت');
INSERT INTO "imlaei_simple" VALUES(82,4,'82:4','واذا القبور بعثرت');
INSERT INTO "imlaei_simple" VALUES(82,5,'82:5','علمت نفس ما قدمت واخرت');
INSERT INTO "imlaei_simple" VALUES(82,6,'82:6','يا ايها الانسان ما غرك بربك الكريم');
INSERT INTO "imlaei_simple" VALUES(82,7,'82:7','الذي خلقك فسواك فعدلك');
INSERT INTO "imlaei_simple" VALUES(82,8,'82:8','في اي صورة ما شاء ركبك');
INSERT INTO "imlaei_simple" VALUES(82,9,'82:9','كلا بل تكذبون بالدين');
INSERT INTO "imlaei_simple" VALUES(82,10,'82:10','وان عليكم لحافظين');
INSERT INTO "imlaei_simple" VALUES(82,11,'82:11','كراما كاتبين');
INSERT INTO "imlaei_simple" VALUES(82,12,'82:12','يعلمون ما تفعلون');
INSERT INTO "imlaei_simple" VALUES(82,13,'82:13','ان الابرار لفي نعيم');
INSERT INTO "imlaei_simple" VALUES(82,14,'82:14','وان الفجار لفي جحيم');
INSERT INTO "imlaei_simple" VALUES(82,15,'82:15','يصلونها يوم الدين');
INSERT INTO "imlaei_simple" VALUES(82,16,'82:16','وما هم عنها بغائبين');
INSERT INTO "imlaei_simple" VALUES(82,17,'82:17','وما ادراك ما يوم الدين');
INSERT INTO "imlaei_simple" VALUES(82,18,'82:18','ثم ما ادراك ما يوم الدين');
INSERT INTO "imlaei_simple" VALUES(82,19,'82:19','يوم لا تملك نفس لنفس شيئا والامر يومئذ لله');
INSERT INTO "imlaei_simple" VALUES(83,1,'83:1','ويل للمطففين');
INSERT INTO "imlaei_simple" VALUES(83,2,'83:2','الذين اذا اكتالوا على الناس يستوفون');
INSERT INTO "imlaei_simple" VALUES(83,3,'83:3','واذا كالوهم او وزنوهم يخسرون');
INSERT INTO "imlaei_simple" VALUES(83,4,'83:4','الا يظن اولئك انهم مبعوثون');
INSERT INTO "imlaei_simple" VALUES(83,5,'83:5','ليوم عظيم');
INSERT INTO "imlaei_simple" VALUES(83,6,'83:6','يوم يقوم الناس لرب العالمين');
INSERT INTO "imlaei_simple" VALUES(83,7,'83:7','كلا ان كتاب الفجار لفي سجين');
INSERT INTO "imlaei_simple" VALUES(83,8,'83:8','وما ادراك ما سجين');
INSERT INTO "imlaei_simple" VALUES(83,9,'83:9','كتاب مرقوم');
INSERT INTO "imlaei_simple" VALUES(83,10,'83:10','ويل يومئذ للمكذبين');
INSERT INTO "imlaei_simple" VALUES(83,11,'83:11','الذين يكذبون بيوم الدين');
INSERT INTO "imlaei_simple" VALUES(83,12,'83:12','وما يكذب به الا كل معتد اثيم');
INSERT INTO "imlaei_simple" VALUES(83,13,'83:13','اذا تتلى عليه اياتنا قال اساطير الاولين');
INSERT INTO "imlaei_simple" VALUES(83,14,'83:14','كلا بل ران على قلوبهم ما كانوا يكسبون');
INSERT INTO "imlaei_simple" VALUES(83,15,'83:15','كلا انهم عن ربهم يومئذ لمحجوبون');
INSERT INTO "imlaei_simple" VALUES(83,16,'83:16','ثم انهم لصالو الجحيم');
INSERT INTO "imlaei_simple" VALUES(83,17,'83:17','ثم يقال هذا الذي كنتم به تكذبون');
INSERT INTO "imlaei_simple" VALUES(83,18,'83:18','كلا ان كتاب الابرار لفي عليين');
INSERT INTO "imlaei_simple" VALUES(83,19,'83:19','وما ادراك ما عليون');
INSERT INTO "imlaei_simple" VALUES(83,20,'83:20','كتاب مرقوم');
INSERT INTO "imlaei_simple" VALUES(83,21,'83:21','يشهده المقربون');
INSERT INTO "imlaei_simple" VALUES(83,22,'83:22','ان الابرار لفي نعيم');
INSERT INTO "imlaei_simple" VALUES(83,23,'83:23','على الارائك ينظرون');
INSERT INTO "imlaei_simple" VALUES(83,24,'83:24','تعرف في وجوههم نضرة النعيم');
INSERT INTO "imlaei_simple" VALUES(83,25,'83:25','يسقون من رحيق مختوم');
INSERT INTO "imlaei_simple" VALUES(83,26,'83:26','ختامه مسك وفي ذلك فليتنافس المتنافسون');
INSERT INTO "imlaei_simple" VALUES(83,27,'83:27','ومزاجه من تسنيم');
INSERT INTO "imlaei_simple" VALUES(83,28,'83:28','عينا يشرب بها المقربون');
INSERT INTO "imlaei_simple" VALUES(83,29,'83:29','ان الذين اجرموا كانوا من الذين امنوا يضحكون');
INSERT INTO "imlaei_simple" VALUES(83,30,'83:30','واذا مروا بهم يتغامزون');
INSERT INTO "imlaei_simple" VALUES(83,31,'83:31','واذا انقلبوا الى اهلهم انقلبوا فكهين');
INSERT INTO "imlaei_simple" VALUES(83,32,'83:32','واذا راوهم قالوا ان هؤلاء لضالون');
INSERT INTO "imlaei_simple" VALUES(83,33,'83:33','وما ارسلوا عليهم حافظين');
INSERT INTO "imlaei_simple" VALUES(83,34,'83:34','فاليوم الذين امنوا من الكفار يضحكون');
INSERT INTO "imlaei_simple" VALUES(83,35,'83:35','على الارائك ينظرون');
INSERT INTO "imlaei_simple" VALUES(83,36,'83:36','هل ثوب الكفار ما كانوا يفعلون');
INSERT INTO "imlaei_simple" VALUES(84,1,'84:1','اذا السماء انشقت');
INSERT INTO "imlaei_simple" VALUES(84,2,'84:2','واذنت لربها وحقت');
INSERT INTO "imlaei_simple" VALUES(84,3,'84:3','واذا الارض مدت');
INSERT INTO "imlaei_simple" VALUES(84,4,'84:4','والقت ما فيها وتخلت');
INSERT INTO "imlaei_simple" VALUES(84,5,'84:5','واذنت لربها وحقت');
INSERT INTO "imlaei_simple" VALUES(84,6,'84:6','يا ايها الانسان انك كادح الى ربك كدحا فملاقيه');
INSERT INTO "imlaei_simple" VALUES(84,7,'84:7','فاما من اوتي كتابه بيمينه');
INSERT INTO "imlaei_simple" VALUES(84,8,'84:8','فسوف يحاسب حسابا يسيرا');
INSERT INTO "imlaei_simple" VALUES(84,9,'84:9','وينقلب الى اهله مسرورا');
INSERT INTO "imlaei_simple" VALUES(84,10,'84:10','واما من اوتي كتابه وراء ظهره');
INSERT INTO "imlaei_simple" VALUES(84,11,'84:11','فسوف يدعو ثبورا');
INSERT INTO "imlaei_simple" VALUES(84,12,'84:12','ويصلى سعيرا');
INSERT INTO "imlaei_simple" VALUES(84,13,'84:13','انه كان في اهله مسرورا');
INSERT INTO "imlaei_simple" VALUES(84,14,'84:14','انه ظن ان لن يحور');
INSERT INTO "imlaei_simple" VALUES(84,15,'84:15','بلى ان ربه كان به بصيرا');
INSERT INTO "imlaei_simple" VALUES(84,16,'84:16','فلا اقسم بالشفق');
INSERT INTO "imlaei_simple" VALUES(84,17,'84:17','والليل وما وسق');
INSERT INTO "imlaei_simple" VALUES(84,18,'84:18','والقمر اذا اتسق');
INSERT INTO "imlaei_simple" VALUES(84,19,'84:19','لتركبن طبقا عن طبق');
INSERT INTO "imlaei_simple" VALUES(84,20,'84:20','فما لهم لا يؤمنون');
INSERT INTO "imlaei_simple" VALUES(84,21,'84:21','واذا قرئ عليهم القران لا يسجدون');
INSERT INTO "imlaei_simple" VALUES(84,22,'84:22','بل الذين كفروا يكذبون');
INSERT INTO "imlaei_simple" VALUES(84,23,'84:23','والله اعلم بما يوعون');
INSERT INTO "imlaei_simple" VALUES(84,24,'84:24','فبشرهم بعذاب اليم');
INSERT INTO "imlaei_simple" VALUES(84,25,'84:25','الا الذين امنوا وعملوا الصالحات لهم اجر غير ممنون');
INSERT INTO "imlaei_simple" VALUES(85,1,'85:1','والسماء ذات البروج');
INSERT INTO "imlaei_simple" VALUES(85,2,'85:2','واليوم الموعود');
INSERT INTO "imlaei_simple" VALUES(85,3,'85:3','وشاهد ومشهود');
INSERT INTO "imlaei_simple" VALUES(85,4,'85:4','قتل اصحاب الاخدود');
INSERT INTO "imlaei_simple" VALUES(85,5,'85:5','النار ذات الوقود');
INSERT INTO "imlaei_simple" VALUES(85,6,'85:6','اذ هم عليها قعود');
INSERT INTO "imlaei_simple" VALUES(85,7,'85:7','وهم على ما يفعلون بالمؤمنين شهود');
INSERT INTO "imlaei_simple" VALUES(85,8,'85:8','وما نقموا منهم الا ان يؤمنوا بالله العزيز الحميد');
INSERT INTO "imlaei_simple" VALUES(85,9,'85:9','الذي له ملك السماوات والارض والله على كل شيء شهيد');
INSERT INTO "imlaei_simple" VALUES(85,10,'85:10','ان الذين فتنوا المؤمنين والمؤمنات ثم لم يتوبوا فلهم عذاب جهنم ولهم عذاب الحريق');
INSERT INTO "imlaei_simple" VALUES(85,11,'85:11','ان الذين امنوا وعملوا الصالحات لهم جنات تجري من تحتها الانهار ذلك الفوز الكبير');
INSERT INTO "imlaei_simple" VALUES(85,12,'85:12','ان بطش ربك لشديد');
INSERT INTO "imlaei_simple" VALUES(85,13,'85:13','انه هو يبدئ ويعيد');
INSERT INTO "imlaei_simple" VALUES(85,14,'85:14','وهو الغفور الودود');
INSERT INTO "imlaei_simple" VALUES(85,15,'85:15','ذو العرش المجيد');
INSERT INTO "imlaei_simple" VALUES(85,16,'85:16','فعال لما يريد');
INSERT INTO "imlaei_simple" VALUES(85,17,'85:17','هل اتاك حديث الجنود');
INSERT INTO "imlaei_simple" VALUES(85,18,'85:18','فرعون وثمود');
INSERT INTO "imlaei_simple" VALUES(85,19,'85:19','بل الذين كفروا في تكذيب');
INSERT INTO "imlaei_simple" VALUES(85,20,'85:20','والله من ورائهم محيط');
INSERT INTO "imlaei_simple" VALUES(85,21,'85:21','بل هو قران مجيد');
INSERT INTO "imlaei_simple" VALUES(85,22,'85:22','في لوح محفوظ');
INSERT INTO "imlaei_simple" VALUES(86,1,'86:1','والسماء والطارق');
INSERT INTO "imlaei_simple" VALUES(86,2,'86:2','وما ادراك ما الطارق');
INSERT INTO "imlaei_simple" VALUES(86,3,'86:3','النجم الثاقب');
INSERT INTO "imlaei_simple" VALUES(86,4,'86:4','ان كل نفس لما عليها حافظ');
INSERT INTO "imlaei_simple" VALUES(86,5,'86:5','فلينظر الانسان مم خلق');
INSERT INTO "imlaei_simple" VALUES(86,6,'86:6','خلق من ماء دافق');
INSERT INTO "imlaei_simple" VALUES(86,7,'86:7','يخرج من بين الصلب والترائب');
INSERT INTO "imlaei_simple" VALUES(86,8,'86:8','انه على رجعه لقادر');
INSERT INTO "imlaei_simple" VALUES(86,9,'86:9','يوم تبلى السرائر');
INSERT INTO "imlaei_simple" VALUES(86,10,'86:10','فما له من قوة ولا ناصر');
INSERT INTO "imlaei_simple" VALUES(86,11,'86:11','والسماء ذات الرجع');
INSERT INTO "imlaei_simple" VALUES(86,12,'86:12','والارض ذات الصدع');
INSERT INTO "imlaei_simple" VALUES(86,13,'86:13','انه لقول فصل');
INSERT INTO "imlaei_simple" VALUES(86,14,'86:14','وما هو بالهزل');
INSERT INTO "imlaei_simple" VALUES(86,15,'86:15','انهم يكيدون كيدا');
INSERT INTO "imlaei_simple" VALUES(86,16,'86:16','واكيد كيدا');
INSERT INTO "imlaei_simple" VALUES(86,17,'86:17','فمهل الكافرين امهلهم رويدا');
INSERT INTO "imlaei_simple" VALUES(87,1,'87:1','سبح اسم ربك الاعلى');
INSERT INTO "imlaei_simple" VALUES(87,2,'87:2','الذي خلق فسوى');
INSERT INTO "imlaei_simple" VALUES(87,3,'87:3','والذي قدر فهدى');
INSERT INTO "imlaei_simple" VALUES(87,4,'87:4','والذي اخرج المرعى');
INSERT INTO "imlaei_simple" VALUES(87,5,'87:5','فجعله غثاء احوى');
INSERT INTO "imlaei_simple" VALUES(87,6,'87:6','سنقرئك فلا تنسى');
INSERT INTO "imlaei_simple" VALUES(87,7,'87:7','الا ما شاء الله انه يعلم الجهر وما يخفى');
INSERT INTO "imlaei_simple" VALUES(87,8,'87:8','ونيسرك لليسرى');
INSERT INTO "imlaei_simple" VALUES(87,9,'87:9','فذكر ان نفعت الذكرى');
INSERT INTO "imlaei_simple" VALUES(87,10,'87:10','سيذكر من يخشى');
INSERT INTO "imlaei_simple" VALUES(87,11,'87:11','ويتجنبها الاشقى');
INSERT INTO "imlaei_simple" VALUES(87,12,'87:12','الذي يصلى النار الكبرى');
INSERT INTO "imlaei_simple" VALUES(87,13,'87:13','ثم لا يموت فيها ولا يحيى');
INSERT INTO "imlaei_simple" VALUES(87,14,'87:14','قد افلح من تزكى');
INSERT INTO "imlaei_simple" VALUES(87,15,'87:15','وذكر اسم ربه فصلى');
INSERT INTO "imlaei_simple" VALUES(87,16,'87:16','بل تؤثرون الحياة الدنيا');
INSERT INTO "imlaei_simple" VALUES(87,17,'87:17','والاخرة خير وابقى');
INSERT INTO "imlaei_simple" VALUES(87,18,'87:18','ان هذا لفي الصحف الاولى');
INSERT INTO "imlaei_simple" VALUES(87,19,'87:19','صحف ابراهيم وموسى');
INSERT INTO "imlaei_simple" VALUES(88,1,'88:1','هل اتاك حديث الغاشية');
INSERT INTO "imlaei_simple" VALUES(88,2,'88:2','وجوه يومئذ خاشعة');
INSERT INTO "imlaei_simple" VALUES(88,3,'88:3','عاملة ناصبة');
INSERT INTO "imlaei_simple" VALUES(88,4,'88:4','تصلى نارا حامية');
INSERT INTO "imlaei_simple" VALUES(88,5,'88:5','تسقى من عين انية');
INSERT INTO "imlaei_simple" VALUES(88,6,'88:6','ليس لهم طعام الا من ضريع');
INSERT INTO "imlaei_simple" VALUES(88,7,'88:7','لا يسمن ولا يغني من جوع');
INSERT INTO "imlaei_simple" VALUES(88,8,'88:8','وجوه يومئذ ناعمة');
INSERT INTO "imlaei_simple" VALUES(88,9,'88:9','لسعيها راضية');
INSERT INTO "imlaei_simple" VALUES(88,10,'88:10','في جنة عالية');
INSERT INTO "imlaei_simple" VALUES(88,11,'88:11','لا تسمع فيها لاغية');
INSERT INTO "imlaei_simple" VALUES(88,12,'88:12','فيها عين جارية');
INSERT INTO "imlaei_simple" VALUES(88,13,'88:13','فيها سرر مرفوعة');
INSERT INTO "imlaei_simple" VALUES(88,14,'88:14','واكواب موضوعة');
INSERT INTO "imlaei_simple" VALUES(88,15,'88:15','ونمارق مصفوفة');
INSERT INTO "imlaei_simple" VALUES(88,16,'88:16','وزرابي مبثوثة');
INSERT INTO "imlaei_simple" VALUES(88,17,'88:17','افلا ينظرون الى الابل كيف خلقت');
INSERT INTO "imlaei_simple" VALUES(88,18,'88:18','والى السماء كيف رفعت');
INSERT INTO "imlaei_simple" VALUES(88,19,'88:19','والى الجبال كيف نصبت');
INSERT INTO "imlaei_simple" VALUES(88,20,'88:20','والى الارض كيف سطحت');
INSERT INTO "imlaei_simple" VALUES(88,21,'88:21','فذكر انما انت مذكر');
INSERT INTO "imlaei_simple" VALUES(88,22,'88:22','لست عليهم بمصيطر');
INSERT INTO "imlaei_simple" VALUES(88,23,'88:23','الا من تولى وكفر');
INSERT INTO "imlaei_simple" VALUES(88,24,'88:24','فيعذبه الله العذاب الاكبر');
INSERT INTO "imlaei_simple" VALUES(88,25,'88:25','ان الينا ايابهم');
INSERT INTO "imlaei_simple" VALUES(88,26,'88:26','ثم ان علينا حسابهم');
INSERT INTO "imlaei_simple" VALUES(89,1,'89:1','والفجر');
INSERT INTO "imlaei_simple" VALUES(89,2,'89:2','وليال عشر');
INSERT INTO "imlaei_simple" VALUES(89,3,'89:3','والشفع والوتر');
INSERT INTO "imlaei_simple" VALUES(89,4,'89:4','والليل اذا يسر');
INSERT INTO "imlaei_simple" VALUES(89,5,'89:5','هل في ذلك قسم لذي حجر');
INSERT INTO "imlaei_simple" VALUES(89,6,'89:6','الم تر كيف فعل ربك بعاد');
INSERT INTO "imlaei_simple" VALUES(89,7,'89:7','ارم ذات العماد');
INSERT INTO "imlaei_simple" VALUES(89,8,'89:8','التي لم يخلق مثلها في البلاد');
INSERT INTO "imlaei_simple" VALUES(89,9,'89:9','وثمود الذين جابوا الصخر بالواد');
INSERT INTO "imlaei_simple" VALUES(89,10,'89:10','وفرعون ذي الاوتاد');
INSERT INTO "imlaei_simple" VALUES(89,11,'89:11','الذين طغوا في البلاد');
INSERT INTO "imlaei_simple" VALUES(89,12,'89:12','فاكثروا فيها الفساد');
INSERT INTO "imlaei_simple" VALUES(89,13,'89:13','فصب عليهم ربك سوط عذاب');
INSERT INTO "imlaei_simple" VALUES(89,14,'89:14','ان ربك لبالمرصاد');
INSERT INTO "imlaei_simple" VALUES(89,15,'89:15','فاما الانسان اذا ما ابتلاه ربه فاكرمه ونعمه فيقول ربي اكرمن');
INSERT INTO "imlaei_simple" VALUES(89,16,'89:16','واما اذا ما ابتلاه فقدر عليه رزقه فيقول ربي اهانن');
INSERT INTO "imlaei_simple" VALUES(89,17,'89:17','كلا بل لا تكرمون اليتيم');
INSERT INTO "imlaei_simple" VALUES(89,18,'89:18','ولا تحاضون على طعام المسكين');
INSERT INTO "imlaei_simple" VALUES(89,19,'89:19','وتاكلون التراث اكلا لما');
INSERT INTO "imlaei_simple" VALUES(89,20,'89:20','وتحبون المال حبا جما');
INSERT INTO "imlaei_simple" VALUES(89,21,'89:21','كلا اذا دكت الارض دكا دكا');
INSERT INTO "imlaei_simple" VALUES(89,22,'89:22','وجاء ربك والملك صفا صفا');
INSERT INTO "imlaei_simple" VALUES(89,23,'89:23','وجيء يومئذ بجهنم يومئذ يتذكر الانسان وانى له الذكرى');
INSERT INTO "imlaei_simple" VALUES(89,24,'89:24','يقول يا ليتني قدمت لحياتي');
INSERT INTO "imlaei_simple" VALUES(89,25,'89:25','فيومئذ لا يعذب عذابه احد');
INSERT INTO "imlaei_simple" VALUES(89,26,'89:26','ولا يوثق وثاقه احد');
INSERT INTO "imlaei_simple" VALUES(89,27,'89:27','يا ايتها النفس المطمئنة');
INSERT INTO "imlaei_simple" VALUES(89,28,'89:28','ارجعي الى ربك راضية مرضية');
INSERT INTO "imlaei_simple" VALUES(89,29,'89:29','فادخلي في عبادي');
INSERT INTO "imlaei_simple" VALUES(89,30,'89:30','وادخلي جنتي');
INSERT INTO "imlaei_simple" VALUES(90,1,'90:1','لا اقسم بهذا البلد');
INSERT INTO "imlaei_simple" VALUES(90,2,'90:2','وانت حل بهذا البلد');
INSERT INTO "imlaei_simple" VALUES(90,3,'90:3','ووالد وما ولد');
INSERT INTO "imlaei_simple" VALUES(90,4,'90:4','لقد خلقنا الانسان في كبد');
INSERT INTO "imlaei_simple" VALUES(90,5,'90:5','ايحسب ان لن يقدر عليه احد');
INSERT INTO "imlaei_simple" VALUES(90,6,'90:6','يقول اهلكت مالا لبدا');
INSERT INTO "imlaei_simple" VALUES(90,7,'90:7','ايحسب ان لم يره احد');
INSERT INTO "imlaei_simple" VALUES(90,8,'90:8','الم نجعل له عينين');
INSERT INTO "imlaei_simple" VALUES(90,9,'90:9','ولسانا وشفتين');
INSERT INTO "imlaei_simple" VALUES(90,10,'90:10','وهديناه النجدين');
INSERT INTO "imlaei_simple" VALUES(90,11,'90:11','فلا اقتحم العقبة');
INSERT INTO "imlaei_simple" VALUES(90,12,'90:12','وما ادراك ما العقبة');
INSERT INTO "imlaei_simple" VALUES(90,13,'90:13','فك رقبة');
INSERT INTO "imlaei_simple" VALUES(90,14,'90:14','او اطعام في يوم ذي مسغبة');
INSERT INTO "imlaei_simple" VALUES(90,15,'90:15','يتيما ذا مقربة');
INSERT INTO "imlaei_simple" VALUES(90,16,'90:16','او مسكينا ذا متربة');
