INSERT INTO "islamhouse" VALUES(1,1,'1:1','به نام الله بخشندۀ مهربان');
INSERT INTO "islamhouse" VALUES(1,2,'1:2','تمام ستایش‌ها [و سپاس‌ها] از آنِ الله ـ پروردگار جهانیان‌ ـ است؛');
INSERT INTO "islamhouse" VALUES(1,3,'1:3','بخشندۀ مهربان است؛');
INSERT INTO "islamhouse" VALUES(1,4,'1:4','مالک روزِ جزاست.');
INSERT INTO "islamhouse" VALUES(1,5,'1:5','[پروردگارا] تنها تو را عبادت می‌کنیم و تنها از تو یاری [و مدد] می‌جوییم.');
INSERT INTO "islamhouse" VALUES(1,6,'1:6','ما را به راه راست هدایت فرما [و همواره در این راه، ثابت‌قدم بدار]؛');
INSERT INTO "islamhouse" VALUES(1,7,'1:7','راه کسانی [همچون پیامبران، شهیدان، راستگویان و نیکوکاران] ‌که به آنان نعمت داده‌ای، نه [راه] کسانی که بر آنان خشم گرفته شده است [= یهود] و نه [راه] گمراهان [= نصاری].');
INSERT INTO "islamhouse" VALUES(2,1,'2:1','الم [= الف. لام. میم].');
INSERT INTO "islamhouse" VALUES(2,2,'2:2','این کتابی است که هیچ تردیدی در [الهی بودنِ] آن نیست و مایۀ هدایت پرهیزگاران است؛');
INSERT INTO "islamhouse" VALUES(2,3,'2:3','همان کسانی که به غیب ایمان دارند و نماز را برپا می‌دارند و از آنچه به آنان روزی‌ داده‌ایم انفاق می‌کنند؛');
INSERT INTO "islamhouse" VALUES(2,4,'2:4','و آن کسانی که به آنچه بر تو و [بر پیامبران] پیش از تو نازل شده است ایمان می‌آورند و به روز رستاخیز یقین دارند.');
INSERT INTO "islamhouse" VALUES(2,5,'2:5','اینان از جانب پروردگارشان از هدایت برخوردارند و رستگاران [واقعی] همین‌ها هستند.');
INSERT INTO "islamhouse" VALUES(2,6,'2:6','قطعاً برای کسانی که [آگاهانه و از روی دشمنی] کفر می‌ورزند، تفاوتی ندارد که آنان را بیم دهی یا بیم ندهی؛ [در هر حال] ایمان نمی‌آورند.');
INSERT INTO "islamhouse" VALUES(2,7,'2:7','[چون] الله بر دل‌ها و گوش‌هایشان مُهر [غفلت] زده و بر چشم‌هایشان پرده‌ای است و عذاب بزرگی [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(2,8,'2:8','برخی از مردم [= منافقان] می‌گویند: «به الله و روز قیامت ایمان آورده‌ایم»؛ در حالی که ایمان ندارند.');
INSERT INTO "islamhouse" VALUES(2,9,'2:9','آنان [گمان می‌کنند] الله و مؤمنان را فریب می‌دهند؛ در حالی که فقط خود را فریب می‌دهند؛ ولی نمی‌فهمند.');
INSERT INTO "islamhouse" VALUES(2,10,'2:10','در دل‌هایشان بیماری [= شکّ و نفاق] است و الله بر بیماری آنان افزوده است و به خاطر دروغ‌هایی که می‌گفتند عذاب دردناکی [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(2,11,'2:11','هر گاه به آنان گفته شود: «در زمین فساد نکنید» [به دروغ] می‌گویند: «ما خود اصلاح‌گریم».');
INSERT INTO "islamhouse" VALUES(2,12,'2:12','آگاه باشید! که آنان [تبهکار و] مفسد هستند؛ ولی نمی‌فهمند.');
INSERT INTO "islamhouse" VALUES(2,13,'2:13','و هنگامی که به آنان گفته شود: «شما نیز همچون مردم [منصِف و واقع‌بین] ایمان بیاورید»، می‌گویند: «آیا [انتظار دارید] همچون کم‌خردان ایمان بیاوریم؟» آگاه باشید! خودشان کم‌خرد هستند؛ ولی [این را] نمی‌دانند.');
INSERT INTO "islamhouse" VALUES(2,14,'2:14','و هنگام ملاقات با مؤمنان می‌گویند: «ما [نیز همچون شما] ایمان آورده‌ایم» و هنگامی که با بزرگان شیطان‌صفتِ خود خلوت می‌کنند می‌گویند: «ما با شما هستیم و فقط [آنان را] مسخره می‌کنیم».');
INSERT INTO "islamhouse" VALUES(2,15,'2:15','الله آنان را مسخره مى‌كند و مهلت‌شان می‌دهد [تا] در طُغیانشان سرگشته بمانند.');
INSERT INTO "islamhouse" VALUES(2,16,'2:16','آنان کسانی هستند که گمراهی را به [بهایِ] هدایت خریدند؛ پس تجارتشان سودی نداد و هدایت‌یافته نبودند.');
INSERT INTO "islamhouse" VALUES(2,17,'2:17','داستان اینان [= منافقان] همانند داستان کسانی است که آتشی بیفروزند؛ و چون آتش، اطراف آنان را روشن سازد، الله نورشان را بگیرد و در تاریکی‌هایی که نمی‌بینند رهایشان کند.');
INSERT INTO "islamhouse" VALUES(2,18,'2:18','[در برابر حقیقت] کر و لال و کورند و [از گمراهىِ خود] بازنمى‌گردند.');
INSERT INTO "islamhouse" VALUES(2,19,'2:19','یا [داستان آنان] همچون [داستان گرفتارشدگان در] بارانِ ‌شدیدی است که از آسمان [فرو می‌ریزد] و دارای تاریکی‌ها و رعد و برق است و آنان از ترسِ مرگ، انگشتان خویش را در گوش‌هایشان فرو می‌برند تا صدای صاعقه را نشنوند؛ و الله به کافران احاطه دارد [و آنان در قبضه قدرت الله متعال هستند].');
INSERT INTO "islamhouse" VALUES(2,20,'2:20','نزدیک است که برق، چشمانشان را برباید؛ هرگاه كه [برق مى‌زند و راه را] بر آنان روشن مى‌سازد، گامى در [پرتوِ] آن برمى‌دارند و چون تاریكى آنان را فراگیرد، [حیرت‌زده بر جای خود] بازمی‌ایستند؛ و اگر الله بخواهد، شنوایی و بینایی‌شان را از بین می‌برد؛ زیرا الله بر هر کاری تواناست.');
INSERT INTO "islamhouse" VALUES(2,21,'2:21','ای مردم! پروردگار خویش را عبادت کنید؛ همان ذاتی که شما و پیشینیانتان را آفرید؛ باشد که پرهیزگار شوید.');
INSERT INTO "islamhouse" VALUES(2,22,'2:22','همان ذاتی که زمین را برای شما گستراند و آسمان را [همچون سقفی] بالای سرتان قرار داد و از آسمان، آبی فروفرستاد و به وسیلۀ آن، انواع میوه‌ها را به وجود آورد تا روزیِ شما باشد؛ پس همتایانی برای الله قرار ندهید، در حالی ‌که [حقیقت را] می‌دانید.');
INSERT INTO "islamhouse" VALUES(2,23,'2:23','و اگر دربارۀ آنچه بر بندۀ خود [محمد] نازل کرده‌ایم در شک و تردید هستید، اگر راست می‌گویید، سوره‌ای همانند آن بیاورید و گواهان [و یاوران] خود را ـ غیر از الله ـ فراخوانید.');
INSERT INTO "islamhouse" VALUES(2,24,'2:24','پس اگر این کار را نکردید ـ و هرگز قادر بر انجام این کار نخواهید بود ـ از آتشی بترسید که هیزمِ آن، مردم و سنگ‌ها هستند و برای کافران آماده شده است.');
INSERT INTO "islamhouse" VALUES(2,25,'2:25','[ای پیامبر!] به کسانی که ایمان آورده و کارهای شایسته انجام داده‌اند، مژده بده که باغ‌هایی [از بهشت] برایشان [در پیش] است که از زیر [درختان] آن جویبارها جاری است. هر زمان که میوه‌ای از آن [باغ‌ها] به آنان داده شود، می‌گویند: «این همان است که پیش از این روزیِ ما شده بود»؛ در حالی که شبیهِ آن به آنان داده شده بود؛ و [در آنجا] همسرانی پاک دارند و جاودانه در آن خواهند ماند.');
INSERT INTO "islamhouse" VALUES(2,26,'2:26','الله از اینکه به پشه‌ای ـ یا بالاتر از آن [از نظر کوچکی] ـ مثال بزند، شرم نمی‌کند. کسانی که ایمان آورده‌اند، می‌دانند که آن [مَثَل] حق و از جانب پروردگارشان است [و از آن پند مى‌گیرند]؛ و اما کافران می‌گویند: «هدف الله از این مَثَل چیست؟» [الله با بیان این مَثَل‌ها] گروه بسیاری را [كه در آنها تدبر نمی‌کنند، حیران و] گمراه و گروه بسیاری را هدایت می‌کند؛ و تنها [منافقان و] فاسقان را با آن گمراه می‌سازد.');
INSERT INTO "islamhouse" VALUES(2,27,'2:27','[همان] کسانی که پیمان الله را ـ پس از محکم ساختنش ـ می‌شکنند و روابط [نیكوى خویشاوندى] را كه الله به برقرارىِ آن فرمان داده است قطع مى‌نمایند و در زمین فساد برپا می‌کنند. اینان [در دنیا و آخرت] زیانکارانند.');
INSERT INTO "islamhouse" VALUES(2,28,'2:28','چگونه [یگانگیِ] الله را انکار می‌کنید در حالى كه وجودی نداشتید و او به شما زندگی بخشید؛ سپس شما را دستخوش مرگ مى‌گردانَد و دوباره زنده خواهد كرد؛ آنگاه [برای حساب و جزا] به پیشگاهش بازگردانده مى‌شوید؟');
INSERT INTO "islamhouse" VALUES(2,29,'2:29','اوست که همۀ آنچه را که در زمین است برایتان آفرید؛ آنگاه به [آفرینش] آسمان پرداخت و آنها را به صورت هفت آسمان سامان داد و او به همه چیز داناست.');
INSERT INTO "islamhouse" VALUES(2,30,'2:30','و [یاد کن] هنگامی که پروردگارت به فرشتگان فرمود: «من در زمین جانشینى خواهم گماشت». [فرشتگان] گفتند: آیا كسى را در آنجا مى‌گمارى كه تبهكارى و خونریزی به راه اندازد؛ در حالی که ما تو را با سپاس به پاکی می‌ستاییم و مقدس می‌داریم؟ الله فرمود: «من [دربارۀ هدف و مصلحتِ آفرینشِ او] چیزهایی مى‌دانم كه شما نمى‌دانید».');
INSERT INTO "islamhouse" VALUES(2,31,'2:31','آنگاه [الله] همۀ نام‌ها[ی اشیا و موجودات] را به آدم آموخت؛ سپس آنها را به فرشتگان عرضه داشت و فرمود: «اگر راست مى‌گویید، از نام‌های اینها به من خبر دهید».');
INSERT INTO "islamhouse" VALUES(2,32,'2:32','[فرشتگان] گفتند: «منزّهی تو! ما چیزی جز آنچه [خود] به ما آموخته‌ای نمی‌دانیم. تو دانای حکیمی».');
INSERT INTO "islamhouse" VALUES(2,33,'2:33','فرمود: «ای آدم، آنان را از نام‌هایشان آگاه کن». هنگامی که وی [آدم] آنان را آگاه کرد، [الله خطاب به فرشتگان] فرمود: «آیا به شما نگفتم که من اسرار آسمان‌ها و زمین را می‌دانم و [نیز] از آنچه که آشکار می‌کنید و پنهان می‌داشتید آگاهم؟»');
INSERT INTO "islamhouse" VALUES(2,34,'2:34','[یاد کن] هنگامی که به فرشتگان گفتیم: «برای [بزرگداشت و احترام، بر] آدم سجده کنید»؛ پس [همگی] سجده کردند جز ابلیس که خودداری کرد و تکبر ورزید و از کافران شد.');
INSERT INTO "islamhouse" VALUES(2,35,'2:35','و گفتیم: «ای آدم! تو و همسرت [حوّا] در بهشت سکونت کنید و از [نعمت‌های] آن ـ از هر جا که می‌خواهید ـ گوارا بخورید؛ اما به این درخت نزدیک نشوید که از ستمکاران خواهید شد».');
INSERT INTO "islamhouse" VALUES(2,36,'2:36','اما شیطان موجب لغزش [و سرپیچیِ] آن دو شد؛ سپس الله آنان را از آنچه در آن بودند [= بهشت] اخراج کرد؛ و [به آن دو و شیطان] گفتیم: «همگی [به زمین] فرود آیید در حالی که دشمن یکدیگر خواهید بود؛ و برایتان در زمین، تا مدتی معیّن قرارگاه و وسیلۀ بهره‌برداری وجود خواهد داشت».');
INSERT INTO "islamhouse" VALUES(2,37,'2:37','آنگاه آدم از پروردگارش کلماتی فراگرفت [و درخواستِ بخشایش نمود] و الله توبۀ او را پذیرفت؛ زیرا الله، توبه‌پذیرِ مهربان است.');
INSERT INTO "islamhouse" VALUES(2,38,'2:38','گفتیم: «همگی از آن [= بهشت] فرود آیید و هر ‌گاه از جانبِ من رهنمودی برایتان آمد، کسانی که از آن [هدایت] پیروی کنند، نه ترسی خواهند داشت و نه اندوهگین می‌شوند».');
INSERT INTO "islamhouse" VALUES(2,39,'2:39','و[لی] کسانی که کافر شدند و نشانه‌های [قدرت و یگانگیِ] ما را دروغ پنداشتند، آنان اهل دوزخند و همیشه در آن خواهند بود.');
INSERT INTO "islamhouse" VALUES(2,40,'2:40','ای بنی‌اسرائیل [= فرزندانِ یعقوب] نعمت‌هایی را که به شما ارزانی داشتم به یاد آورید و به پیمانی که با من بسته‌اید وفا کنید تا من نیز به پیمانِ [خود با] شما [دربارۀ نعمت‌های دنیا و آخرت] وفا کنم؛ و تنها از من بترسید.');
INSERT INTO "islamhouse" VALUES(2,41,'2:41','و به آنچه نازل کرده‌ام [= قرآن] ایمان بیاورید که تصدیق‌کنندۀ همان چیزی است که با شماست؛ و نخستین کافر به آن نباشید و آیات مرا به بهای ناچیز نفروشید و تنها از من بترسید.');
INSERT INTO "islamhouse" VALUES(2,42,'2:42','و در حالی که [حقیقت را] می‌دانید، حق را با باطل نیامیزید و آن را کتمان نکنید.');
INSERT INTO "islamhouse" VALUES(2,43,'2:43','و نماز برپا دارید و زکات بپردازید و با نمازگزاران نماز بخوانید.');
INSERT INTO "islamhouse" VALUES(2,44,'2:44','آیا در حالی که کتاب [تورات] را می‌خوانید، مردم را به نیکی فرمان می‌دهید و خود را فراموش می‌کنید؟ آیا نمی‌اندیشید؟');
INSERT INTO "islamhouse" VALUES(2,45,'2:45','و از شکیبایی و نماز یاری جویید؛ و به راستی که آن [= نماز] جز بر فروتنان، دشوار و گران است.');
INSERT INTO "islamhouse" VALUES(2,46,'2:46','همان کسانی که یقین دارند پروردگار خویش را [در آخرت] ملاقات خواهند کرد، و به پیشگاه او بازمی‌گردند.');
INSERT INTO "islamhouse" VALUES(2,47,'2:47','ای بنی‌اسرائیل، نعمت‌هایی را که به شما ارزانی داشتم به یاد آورید و [نیز] اینکه شما را [با برخورداری از موهبتِ هدایت] بر جهانیان [زمان خودتان] برتری دادم.');
INSERT INTO "islamhouse" VALUES(2,48,'2:48','و از روزی بترسید که کسی ذره‌ای [از عذاب الله] را از دیگری دفع نمى‌كند و از او نه شفاعتی پذیرفته می‌شود و نه غرامت و فِدیه‌ای گرفته می‌شود و نه یاری می‌شوند.');
INSERT INTO "islamhouse" VALUES(2,49,'2:49','و [نیز به یاد آورید] هنگامی که شما را از چنگال فرعونیان نجات دادیم. [آنان] بدترین [و خفت‌بارترین] عذاب را بر سرتان می‌آوردند: پسرانتان را می‌کشتند و زنانتان را [برای کنیزی] زنده نگه می‌داشتند؛ و در این امر، آزمایش بزرگی از جانب پروردگارتان برای شما بود.');
INSERT INTO "islamhouse" VALUES(2,50,'2:50','و [به یاد آورید] هنگامی که دریا را برایتان شکافتیم و شما را نجات دادیم و فرعونیان را ـ در حالی که [عذابِ آنان را به چشمِ خود] می‌نگریستیدـ غرق ساختیم.');
INSERT INTO "islamhouse" VALUES(2,51,'2:51','و [به یاد آورید] هنگامی را که [برای نزولِ تورات] چهل شب با موسی وعده گذاشتیم؛ آنگاه شما در غیابِ او، گوساله را [به عبادت] گرفتید، در حالی که [نسبت به خویشتن] ستمکار بودید.');
INSERT INTO "islamhouse" VALUES(2,52,'2:52','سپس شما را بعد از آن [گناه] بخشیدیم؛ باشد که سپاسگزاری کنید.');
INSERT INTO "islamhouse" VALUES(2,53,'2:53','و [نیز به یاد آورید] هنگامی که به موسی کتاب [تورات] را که جداکنندۀ [حق از باطل] است عطا کردیم؛ باشد که هدایت شوید.');
INSERT INTO "islamhouse" VALUES(2,54,'2:54','و [به یاد آورید] زمانی را که موسی به قوم خود گفت: «ای قوم من! شما با انتخاب گوساله [برای عبادت]، بر خويشتن ستم کرده‌اید؛ پس به سوی آفریدگار خود بازگردید و توبه کنید و [خطاکارانِ] خود را بکشید. این کار، در پیشگاه پروردگارتان برای شما بهتر است». آنگاه الله توبۀ شما را پذیرفت. به راستی که او توبه‌پذیرِ مهربان است.');
INSERT INTO "islamhouse" VALUES(2,55,'2:55','و [به یاد آورید] هنگامی را که گفتید: «ای موسی، ما هرگز به تو ایمان نمی‌آوریم مگر اینکه الله را آشکارا ببینیم»؛ پس صاعقه شما را در حالی گرفت که [عذابِ خود را به چشمِ خویش] می‌نگریستید.');
INSERT INTO "islamhouse" VALUES(2,56,'2:56','سپس شما را پس از مرگتان برانگیختیم؛ باشد که سپاسگزاری کنید.');
INSERT INTO "islamhouse" VALUES(2,57,'2:57','و ابر را بر شما سایبان قرار دادیم و تَرَنجَبین و بِلدرچین برایتان فروفرستادیم [و گفتیم]: «از نعمت‌های پاکیز‌ه‌ای که به شما روزی داده‌ایم بخورید» [ولی کفر ورزیدید]. آنان [با ناسپاسى و نافرمانى] به ما ستم نکردند؛ بلکه به خود ستم روا می‌داشتند.');
INSERT INTO "islamhouse" VALUES(2,58,'2:58','و [به یاد آورید] آنگاه که گفتیم: «به این شهر [= بیت‌المقدس] وارد شوید و از [نعمت‌های] آن ـ از هر جا [و هر وقت] که خواستید ـ بخورید و با فروتنی از دروازه وارد شوید و بگویید: «[پروردگارا، گناهانمان را] فرو ریز»؛ تا گناهانتان را بیامرزیم و [یقیناً] بر [پاداش] نیكوكاران خواهیم افزود.');
INSERT INTO "islamhouse" VALUES(2,59,'2:59','امّا ستمگران این سخن را که به آنها گفته شده بود تغییر دادند [و به جای آن، کلمات تمسخرآمیزی گفتند]؛ پس [ما نیز] به سزایِ این نافرمانی‌ ستمگران، عذابی از آسمان بر آنان نازل کردیم.');
INSERT INTO "islamhouse" VALUES(2,60,'2:60','و [به یاد آورید] هنگامی را که موسی برای قومش آب خواست؛ [به او] گفتیم: «عصایت را بر سنگ بزن»؛ آنگاه دوازده چشمه از آن جوشید و هر گروهی آبشخورِ خود را دانست [و گفتیم]: «از روزیِ الله بخورید و بیاشامید و[لی] در زمین به فساد [و تباهی] نکوشید».');
INSERT INTO "islamhouse" VALUES(2,61,'2:61','و [به یاد آورید] زمانی را که گفتید: «ای موسی، ما هرگز تابِ تحملِ یک [نوع] غذا را نداریم؛ از پروردگار خویش برای ما بخواه تا از روییدنی‌های زمین ـ از سبزی و خیار و گندم و عدس و پیاز ـ برایمان برویانَد». [موسی] گفت: «آیا چیزی را که پست‌تر است جایگزین چیز بهتر می‌کنید؟ پس به شهری فرود آیید که آنچه خواستید در آنجا برایتان وجود دارد»؛ و [اینچنین مُهرِ] ذلت و بینوایی بر آنان زده شد و سزاوار خشمِ الله گردیدند؛ چرا که آنان به آیات الله کفر می‌ورزیدند و پیامبران را به ناحق می‌کشتند. اين [كفرورزى و كشتن پيامبران‌] به این سبب بود كه [از من‌] نافرمانی کردند و پيوسته [از حدود حق‌] تجاوز مى‌نمودند.');
INSERT INTO "islamhouse" VALUES(2,62,'2:62','در حقیقت، کسانی که [به رسالت محمد صلی الله علیه وسلم] ایمان آورده‌ و نیز کسانی [از امت‌های پیشین، قبل از بعثت محمد صلی الله علیه وسلم از] یهود و نصاری و صابئان [= پیروان برخی پیامبران]؛ هر کس [از آنان] به الله و روز قیامت ایمان آورند و کارهای شایسته انجام دهند، پاداش آنان نزد پروردگارشان است؛ نه ترسی خواهند داشت و نه اندوهگین می‌شوند.');
INSERT INTO "islamhouse" VALUES(2,63,'2:63','و [به یاد آورید] زمانی را که از شما پیمان گرفتیم و [کوه] طور را بالای سرتان برافراشتیم [و گفتیم]: «آنچه [از احكام تورات] به شما داده‌ایم با جدیت بگیرید و آنچه را که در آن است به یاد داشته باشید [و به آن عمل کنید]؛ باشد که پرهیزگار شوید».');
INSERT INTO "islamhouse" VALUES(2,64,'2:64','ولى بعد [از آن همه عهد و پیمان] از این [فرمان] روى برتافتید و اگر فضل و بخشش الله بر شما نبود، یقیناً در زمرۀ زیانکاران بودید.');
INSERT INTO "islamhouse" VALUES(2,65,'2:65','مسلماً از [شرح حال] كسانى از خودتان آگاه شده‌اید كه در [مورد حكمِ حرمتِ صید ماهى در روز] شنبه تجاوز كردند؛ پس به آنها گفتیم: «بوزینه‌هایی خوار و رانده‌شده باشید».');
INSERT INTO "islamhouse" VALUES(2,66,'2:66','پس آن [کیفر] را عبرتی برای مردم آن زمان و نسل‌های پس از آنان و [همچنین] پند و اندرزی برای پرهیزگاران قرار دادیم.');
INSERT INTO "islamhouse" VALUES(2,67,'2:67','و [به یاد آورید] هنگامی که موسی به قوم خود گفت: «الله به شما دستور می‌دهد که گاوی را ذبح کنید». آنان گفتند: «آیا ما را مسخره می‌کنی؟» [موسی] گفت: «[تمسخر كار جاهلان است و] به الله پناه می‌برم از اینکه در زمرۀ جاهلان باشم».');
INSERT INTO "islamhouse" VALUES(2,68,'2:68','گفتند: «از پروردگارت بخواه تا برای ما روشن کند که آن چگونه [گاوی] است؟» [موسی] گفت: «[الله] می‌فرماید: "آن گاوی است نه پیر و نه جوان؛ بلکه میانسالی بین این دو است. پس [اکنون] آنچه را که دستور یافته‌اید انجام دهید"».');
INSERT INTO "islamhouse" VALUES(2,69,'2:69','گفتند: «از پروردگارت بخواه تا برای ما روشن سازد که رنگش چگونه است؟» [موسی] گفت: «[الله] می‌فرماید: "آن گاوِ زرد پررنگی است که [رنگش] بینندگان را خوش آید"».');
INSERT INTO "islamhouse" VALUES(2,70,'2:70','گفتند: «از پروردگارت [باز هم] بخواه تا چگونگیِ آن گاو را [از هر جهت] برای ما روشن سازد؛ زیرا [ویژگی‌های] این گاو [هنوز] بر ما مبهم است، و ان شاء الله [با توضیحاتِ تو] هدایت خواهیم شد».');
INSERT INTO "islamhouse" VALUES(2,71,'2:71','[موسی] گفت: «الله می‌فرماید: "آن گاوی است نه چنان رام كه زمین را شخم بزند و یا كشتزار آب بدهد؛ [اما] بی‌عیب و یکرنگ است، هيچ لكه‌اى در آن نيست"». آنان گفتند: «اینک سخن درستی آوردی»؛ سپس [چنان گاوی را پیدا کردند و] آن را سر بریدند و [البته] نزدیک بود این کار را انجام ندهند.');
INSERT INTO "islamhouse" VALUES(2,72,'2:72','و [به یاد آورید] هنگامی که شخصی را کشتید و دربارۀ [قاتلِ] او به نزاع پرداختید و الله چیزی را که پنهان می‌کردید آشکار نمود.');
INSERT INTO "islamhouse" VALUES(2,73,'2:73','پس گفتیم: «قسمتی از آن [گاو سر بریده شده] را به آن [مقتول] بزنید[تا زنده شود و قاتل خویش را معرفى كند]. الله این گونه مردگان را زنده می‌کند و نشانه‌های [آشکار قدرتِ] خویش را به شما نشان می‌دهد؛ باشد که بیندیشید».');
INSERT INTO "islamhouse" VALUES(2,74,'2:74','پس از آن، [با وجود این معجزه بزرگ] دل‌های شما سخت شد؛ همچون سنگ، یا سخت‌تر؛ زیرا [حال دل‌هایتان هرگز تغییر نمی‌کند؛ اما سنگ‌ها دچار تغییر و تحول می‌شوند‌؛ چنان که] برخی از سنگ‌ها می‌شکافند و جویبارهایی از آنها جاری می‌شود و برخی می‌شکافند و [چشمه‌هاى] آب از آنها مى‌جوشد و برخی از ترسِ الله [از فرازِ کوهها] فرو می‌افتند؛ و الله از آنچه انجام می‌دهید غافل نیست.');
INSERT INTO "islamhouse" VALUES(2,75,'2:75','[ای مؤمنان! حال که حقیقت دشمنیِ یهود برایتان آشکار گردید،] آیا امید دارید که به [آیین] شما ایمان بیاورند، با اینکه گروهی از [علمای] آنان سخنان الله را [در تورات] می‌شنیدند و پس از فهمیدنش [الفاظ و معانیِ] آن را تحریف می‌کردند؛ حال آنکه [از بزرگیِ گناه خویش] اطلاع داشتند؟!');
INSERT INTO "islamhouse" VALUES(2,76,'2:76','و [یک مورد از فریب‌کاری‌هایشان این است که] وقتی با مؤمنان روبرو می‌شوند می‌گویند: «[ما هم] ایمان آورده‌ایم»؛ ولی هنگامی که با یکدیگر خلوت می‌کنند می‌گویند: «چرا مطالبی را که [دربارۀ ویژگی‌های پیامبر و حقانیتِ اسلام در تورات آمده و] الله برای شما بیان کرده است به مسلمانان بازگو می‌کنید تا [روز قیامت] در پیشگاه پروردگارتان علیه شما به آن استدلال کنند؟ آیا نمی‌فهمید؟»');
INSERT INTO "islamhouse" VALUES(2,77,'2:77','آیا نمی‌دانند که الله [تمام] آنچه را پنهان می‌دارند و آشکار می‌سازند [همه را] می‌داند؟');
INSERT INTO "islamhouse" VALUES(2,78,'2:78','و برخی از آنان بی‌سوادانی هستند که [از] کتابِ [الله، تورات] جز [مُشتی از] دروغ‌های خود بافته، چیزی نمی‌دانند و تنها به پندارهای خویش [که از بزرگانشان فراگرفته‌اند] دل بسته‌اند.');
INSERT INTO "islamhouse" VALUES(2,79,'2:79','پس وای بر کسانی که [کتابی تحریف‌شده] با دست خود می‌نویسند، سپس می‌گویند: «این از جانب الله است» تا آن را به بهای اندک بفروشند! پس وای بر آنها از آنچه دست‌هایشان نوشته است و وای بر آنان از آنچه [از این راه] به دست می‌آورند!');
INSERT INTO "islamhouse" VALUES(2,80,'2:80','و [یهود] گفتند: «آتش دوزخ جز چند روز محدودی، هرگز به ما نخواهد رسید». بگو: «آیا از نزد الله پیمانی گرفته‌اید؟! - و الله هرگز از پیمانش تخلف نمی‌ورزد- یا چیزی را که نمی‌دانید به الله نسبت می‌دهید؟!»');
INSERT INTO "islamhouse" VALUES(2,81,'2:81','آری، هر‌ کس مرتکب گناه [= كفر و شرک] شود و گناهانش او را احاطه کند، آنان اهل آتشند و جاودانه در آن خواهند بود.');
INSERT INTO "islamhouse" VALUES(2,82,'2:82','و کسانی که ایمان آورند و کارهای شایسته انجام دهند، آنان اهل بهشتند و جاودانه در آن خواهند ماند.');
INSERT INTO "islamhouse" VALUES(2,83,'2:83','و [به یاد آورید] هنگامی که از بنی‌اسرائیل پیمان گرفتیم که: «جز الله یگانه را عبادت نکنید و به پدر و مادر و نزدیکان و یتیمان و بینوایان نیکی کنید و به مردم [سخن] نیک بگویید و نماز برپا دارید و زکات بپردازید»؛ ولی همۀ شما ـ جز عدۀ کمی ـ پشت کردید و رویگردان شدید.');
INSERT INTO "islamhouse" VALUES(2,84,'2:84','و [به یاد آورید] هنگامی که از شما پیمان گرفتیم که خون یکدیگر را نریزید و همدیگر را از سرزمینتان بیرون نکنید؛ سپس شما اقرار کردید و خود [بر آن پیمان] گواهی می‌دهید.');
INSERT INTO "islamhouse" VALUES(2,85,'2:85','[امّا] باز همین شما هستید که یکدیگر را می‌کشید و گروهی از خودتان را از سرزمینشان بیرون می‌کنید و به گناه و ستم علیه آنها به یکدیگر کمک می‌کنید؛ و اگر [بعضی از آنان در جنگ با هم‌پیمانانِ عربِ شما گرفتار شوند و به صورت] اسیر نزدتان بیایند، [برای این‌که آنها از بنی‌اسرائیل و هم‌دینِ شما هستند، در برابرِ آزادی‌شان می‌کوشید و] فدیه می‌دهید؛ حال آنکه آواره‌کردن شان بر شما حرام بود. آیا به بخشی از [دستورات] کتاب [تورات در مورد فدیه دادن] ایمان می‌آورید و به بخشی [که شما را از کشتن و آواره‌کردن هم‌دینانتان منع می‌کند] کافر می‌شوید؟! پس جزای هر کس از شما که چنین [تبعیضی در آیات الله ایجاد] کند، جز رسوایی و خواری در زندگی دنیا نخواهد بود و در روز قیامت به سخت‌ترین عذاب گرفتار می‌شوند؛ و [یقین بدانید که] الله از آنچه انجام می‌دهید غافل نیست.');
INSERT INTO "islamhouse" VALUES(2,86,'2:86','اینان کسانی هستند که زندگانی دنیا را به بهای آخرت خریدند؛ پس عذاب آنها تخفیف داده نمی‌شود و کسی یاری‌شان نخواهد کرد.');
INSERT INTO "islamhouse" VALUES(2,87,'2:87','همانا به موسی کتاب [= تورات] دادیم و پس از او پیامبرانی پی در پی فرستادیم؛ و به عیسی ـ پسر مریم ـ معجزات و دلایل روشن دادیم و او را به وسیلۀ روح‌القُدُس [= جبرئیل] تقویت کردیم. آیا چنین نیست که هر گاه پیامبری چیزی بر خلافِ هوای نفستان آورد، در برابرش تکبر نمودید و گروهی [از پیامبران] را تکذیب کردید و گروهی را کشتید؟');
INSERT INTO "islamhouse" VALUES(2,88,'2:88','و [یهود برای پیروی نکردن از محمد صلی الله علیه وسلم، از روی تمسخر] گفتند: «دل‌های ما پوشیده و در غلاف است»؛ [چنین نیست؛] بلكه الله آنان را به سزاى كفرشان لعنت كرده است؛ پس [به همین سبب است که] بسیار كم ایمان مى‌آورند.');
INSERT INTO "islamhouse" VALUES(2,89,'2:89','و هنگامی که از جانب الله کتابی [= قرآن] برای آنان آمد که تصدیق‌کنندۀ مطالبی [= تورات] بود که با خود داشتند و [به انتظار رسیدن این كتاب] از قبل به خود نوید پیروزی بر کافران را می‌دادند، با این همه، وقتی آن کسی را که از پیش شناخته بودند [= محمد صلی الله علیه وسلم] نزدشان آمد، انکارش کردند. پس لعنت الله بر کافران باد!');
INSERT INTO "islamhouse" VALUES(2,90,'2:90','به چه بهای بدی خود را فروختند که از روی حسد [و برتری‌خواهی] به آنچه الله فرستاده بود کافر شدند که چرا الله از فضل خویش بر هر ‌کس از بندگانش که می‌خواهد [وحی] نازل می‌کند. پس [به خاطرِ این حسادت] بیش از پیش به خشم [الهی] گرفتار شدند؛ و عذابی خفت‌بار برای کافران [در پیش] است.');
INSERT INTO "islamhouse" VALUES(2,91,'2:91','و چون به آنان گفته شود: «به آنچه الله نازل کرده است ایمان بیاورید»، می‌گویند: «ما به چیزی ایمان می‌آوریم که بر خودِ ما نازل شده است»، و به غیرِ آن، کافر می‌شوند، در حالی که آن [قرآن، سخنِ] حق و تصدیق‌کنندۀ مطالبی است که با خود دارند. [ای پیامبر، به آنان] بگو: «اگر [راست می‌گویید] و مؤمن هستید، پس چرا پیامبرانِ الله را پیش از این می‌کشتید؟».');
INSERT INTO "islamhouse" VALUES(2,92,'2:92','و به راستی موسی معجزه‌ها و نشانه‌های روشنی برایتان آورد؛ اما شما پس از وی، در حالی گوساله را [برای عبادت] برگزیدید که [به خاطر این شرک‌ورزی، نسبت به خود] ستمکار بودید.');
INSERT INTO "islamhouse" VALUES(2,93,'2:93','و [به یاد آورید] هنگامی را که از شما پیمان گرفتیم [که از اوامر الهی و رهنمودهای موسی پیروی کنید] و [کوه] طور را بالای سرتان برافراشتیم [و گفتیم]: «آنچه [از احکام تورات] به شما داده‌ایم محکم بگیرید و [به كلام الله] گوش فرادهید». گفتند: «[با گوش‌هایمان] شنیدیم و[لی با رفتارمان] نافرمانی کردیم»؛ و به سبب کفرشان، [محبتِ] گوساله در دل‌هایشان جای گرفت. [ای پیامبر! به آنان] بگو: «اگر [واقعاً] مؤمن هستید، ایمانتان چه فرمان بدی به شما می‌دهد!».');
INSERT INTO "islamhouse" VALUES(2,94,'2:94','بگو: «[ای قوم یهود!] اگر نزد الله، سرای آخرت ویژۀ شماست نه سایر مردم، پس اگر راست می‌گویید، آرزوی مرگ کنید [تا هر چه زودتر به این جایگاه دست یابید]».');
INSERT INTO "islamhouse" VALUES(2,95,'2:95','ولی آنان به خاطر آنچه [که از کفر و تکذیب و تحریف] پیش فرستاده‌اند هرگز آرزوی مرگ نخواهند کرد؛ و الله به [احوال] ستمکاران داناست.');
INSERT INTO "islamhouse" VALUES(2,96,'2:96','[ای پیامبر!] قطعاً اینان [= یهود] را حریص‌ترین مردم بر زندگی خواهی یافت؛ [حتی] حریص‌تر از مشرکان. هر یک از آنان دوست دارد هزار سال عمر کند؛ در حالی که [حتی] اگر این عمر [دراز] به او داده شود، [باز هم طول عمرش] او را از عذاب دور نمی‌کند؛ و الله به آنچه انجام می‌دهند بیناست.');
INSERT INTO "islamhouse" VALUES(2,97,'2:97','بگو: «کسی که دشمن جبرئیل باشد، [در حقیقت، دشمنِ الله است؛ زیرا] او به فرمان الله قرآن را بر قلب تو نازل کرده است که [کتاب‌های آسمانی] پیش از خود را تصدیق می‌کند و برای مؤمنان، هدایت و بشارت است».');
INSERT INTO "islamhouse" VALUES(2,98,'2:98','هر کس دشمنِ الله و فرشتگان او و پیامبرانش و جبرئیل و میکائیل باشد، [بداند که] الله دشمنِ کافران است.');
INSERT INTO "islamhouse" VALUES(2,99,'2:99','[ای پیامبر،] به راستی که ما نشانه‌های روشنی بر تو نازل کردیم و جز منحرفان [از دین،] کسی به آنها کفر نمی‌ورزد.');
INSERT INTO "islamhouse" VALUES(2,100,'2:100','آیا چنین نیست که هر گاه [یهود] پیمانی [با الله و رسول] بستند، گروهی از آنان آن را دور افکندند [و پیمان شکستند]؟ آری، بیشترشان ایمان نمی‌آورند.');
INSERT INTO "islamhouse" VALUES(2,101,'2:101','و وقتی فرستاده‌ای از جانب الله برایشان آمد -که آنچه را با آنان بود تصديق می‌کرد-، گروهی از کسانی که به آنها کتاب [= تورات] داده شده بود، کتابِ الله را پشت سر افکندند؛ گویی [از آن هیچ] نمی‌دانند.');
INSERT INTO "islamhouse" VALUES(2,102,'2:102','و [یهود] از آنچه شیاطین در [عهدِ] فرمانروایی سلیمان [از علم سحر بر مردم] می‌خواندند پیروی کردند [و مدعی شدند که سلیمان پادشاهی خویش را با سحر استوار کرده است]؛ و [حال آنکه] سلیمان [هرگز دست به سحر نیالود و] کافر نشد؛ ولی شیاطین [که] کفر ورزیدند، به مردم سحر می‌آموختند؛ و [نیز] از آنچه بر دو فرشته «هاروت» و «ماروت» در بابِل نازل شده بود [پیروی کردند]؛ و[لی آن دو فرشته] به هیچ کس چیزی نمی‌آموختند مگر اینکه [قبلاً از روی خیرخواهی به او] می‌گفتند: «ما وسیلۀ آزمایش هستیم؛ پس [با به کار بستن سحر] کافر نشو». [یهودیان] از آن دو [فرشته] چیزهایی می‌آموختند که [بتوانند] توسط آن میان مرد و همسرش جدایی افکنند؛ هر چند بدون اجازۀ الله نمی‌توانستند به وسیلۀ آن به کسی زیان برسانند؛ و چیزی می‌آموختند که [فقط] به آنان زیان می‌رساند و [هیچ] سودی به آنان نمی‌بخشید؛ و قطعاً [یهودیان] می‌دانستند که هر کس [مشتاق و] خریدارِ آن [جادوها] باشد، در آخرت بهره‌ای نخواهد داشت. اگر [حقیقت را] می‌دانستند، [متوجه می‌شدند که] چه بد و زشت بود آنچه خود را به آن فروختند!');
INSERT INTO "islamhouse" VALUES(2,103,'2:103','و اگر آنها ایمان می‌آوردند و پرهیزگاری می‌کردند، اگر آگاهی داشتند [درمی‌یافتند که] پاداشی که نزد الله وجود دارد برایشان بهتر است.');
INSERT INTO "islamhouse" VALUES(2,104,'2:104','ای کسانی که ایمان آورده‌اید، [هنگامی که از پیامبر برای درک آیات قرآن درخواست مهلت می‌کنید] نگویید: «راعِنا» [زیرا علاوه بر معنای اصلی‌اش، مفهوم توهین‌آمیز «نادانی» را می‌رساند] بلکه بگویید: «اُنظُرنا» [= کمی به ما فرصت بده] و [آنگاه به آیات قرآن] گوش کنید و [یقین بدانید که] کافران [و تمسخركنندگانِ پیامبر،] عذابى دردناک [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(2,105,'2:105','کافران ـ چه اهل کتاب و چه مشرکان ـ دوست ندارند که از جانب پروردگارتان خیر و برکتی بر شما نازل گردد؛ در حالی که الله، رحمت خود را به هر ‌کس که بخواهد اختصاص می‌دهد؛ و الله دارای فضل بزرگ است.');
INSERT INTO "islamhouse" VALUES(2,106,'2:106','هر آیه‌ای را نسخ کنیم یا [از دل مردم بزداییم و] از یادها ببریم، بهتر از آن یا همانندش را می‌آوریم. [ای پیامبر،] آیا نمی‌دانی که الله بر هر چیزی تواناست؟');
INSERT INTO "islamhouse" VALUES(2,107,'2:107','آیا نمی‌دانی که فرمانرواییِ آسمان‌ها و زمین از آنِ الله است و شما جز الله [هیچ] دوست و یاوری ندارید [که کارسازتان باشد]؟');
INSERT INTO "islamhouse" VALUES(2,108,'2:108','[ای مؤمنان،] آیا می‌خواهید از پیامبر خود [موارد نامعقولی را] درخواست کنید که پیش از این از موسی خواسته شد؟ کسی که کفر را به [جای] ایمان برگزیند، مسلماً راهِ راست را گم کرده است.');
INSERT INTO "islamhouse" VALUES(2,109,'2:109','بسیاری از اهل کتاب پس از اینکه حق برایشان آشکار گردید، به خاطرِ حسادتی که در وجودشان است آرزو دارند که شما را بعد از ایمانتان به حالِ کفر بازگردانند؛ پس شما عفو کنید و [از آنان] درگذرید تا الله فرمان خویش را [برای جهاد با آنان] صادر نماید. همانا الله بر هر چیزی تواناست.');
INSERT INTO "islamhouse" VALUES(2,110,'2:110','و نماز برپا دارید و زکات بپردازید؛ و [بدانید که] هر کار نیکی که برای خود پیش می‌فرستید، [پاداشِ] آن را نزد الله خواهید یافت. یقیناً الله به آنچه انجام می‌دهید بیناست.');
INSERT INTO "islamhouse" VALUES(2,111,'2:111','[اهل کتاب] گفتند: «جز یهودی یا نصرانی، [هیچ کس] هرگز وارد بهشت نخواهد شد. این آرزو‌های [واهی و باطلِ] آنهاست. [ای پیامبر، به آنان] بگو: «اگر راست می‌گویید، دلیل خود را بیاورید».');
INSERT INTO "islamhouse" VALUES(2,112,'2:112','آری، کسی ‌که روی خود را مخلصانه تسلیم الله کند و نیکوکار باشد، پاداش او نزد پروردگارش [محفوظ] است و [چنین کسانی] نه ترسی خواهند داشت و نه اندوهگین می‌شوند.');
INSERT INTO "islamhouse" VALUES(2,113,'2:113','یهود گفتند: «نصاری بر حق نیستند [و آیین صحیحی ندارند]» و نصاری [نیز] گفتند: «یهود بر حق نیستند [و آیین صحیحی ندارند]»؛ در حالی که [هر دو گروهِ] آنان کتاب [آسمانی] را می‌خوانند؛ همچنین کسانی [مانند مشرکان] که [از پیام الهی هیچ] نمی‌دانند، [سخنی] همانند آنان گفتند. پس الله روز قیامت، در مورد آنچه با هم اختلاف داشتند، میانشان داوری خواهد کرد.');
INSERT INTO "islamhouse" VALUES(2,114,'2:114','کیست ستمکارتر از آن کس که از بردنِ نام الله در مساجد جلوگیری کند و در ویرانی آنها بکوشد؟ شایسته نیست که آنان جز با حالِ وحشت و ترس وارد این مساجد شوند. بهرۀ آنان در دنیا، خواری و رسوایی است و در آخرت [نیز] عذاب بزرگی [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(2,115,'2:115','مشرق و مغرب، از آنِ الله است؛ پس به هر سو رو کنید، آنجا وجه الله [= قبله] است. همانا الله گشایشگرِ داناست.');
INSERT INTO "islamhouse" VALUES(2,116,'2:116','[اهل کتاب و مشرکان] گفتند: «الله [برای خود] فرزندی برگزیده است». او [از چنین نسبت‌هایی] منزّه است. [الله نه تنها نیازی به فرزند ندارد] بلکه [هر] آنچه در آسمان‌ها و زمین است متعلق به اوست [و] همه فرمانبردارِ او هستند.');
INSERT INTO "islamhouse" VALUES(2,117,'2:117','[او] پدیدآورندۀ آسمان‌ها و زمین است و چون به کاری اراده فرماید، فقط به آن می‌گوید: «باش»؛ پس [بی‌درنگ] به وجود می‌آید.');
INSERT INTO "islamhouse" VALUES(2,118,'2:118','کسانی که [از اهل کتاب و مشرکان هستند و از آیین الهی چیزی] نمی‌دانند گفتند: «چرا الله با ما سخن نمی‌گوید؟ یا [چرا] معجزه و نشانه‌ای برای ما نمی‌آید؟» کسانی که پیش از اینان بودند [نیز] مانند همین سخن را می‌گفتند. [افکار و] دل‌هایشان، همانندِ یکدیگر است. بی‌تردید، ما نشانه‌ها[ی یگانگی و قدرتِ خویش] را برای گروهی که یقین دارند، آشکار و روشن ساخته‌ایم.');
INSERT INTO "islamhouse" VALUES(2,119,'2:119','[ای پیامبر،] ما تو را به‌حق فرستادیم تا بشارت‌بخش و بیم‌دهنده باشی و [تو هرگز در آخرت] دربارۀ [کفر و بی‌دینیِ] دوزخیان بازخواست نخواهی شد.');
INSERT INTO "islamhouse" VALUES(2,120,'2:120','یهود و نصاری هرگز از تو راضی نخواهند شد، مگر اینکه از آیین آنان پیروی کنی. بگو: «بی‌تردید، هدایت [راستین]، هدایتِ الله است»؛ و [یقین بدان] اگر پس از دانشی که به تو رسیده است از هوس‌های آنان پیروی کنی، در برابرِ الله هیچ دوست و یاوری نخواهی داشت.');
INSERT INTO "islamhouse" VALUES(2,121,'2:121','کسانی که کتاب آسمانی [= تورات و انجیل] به آنها داده‌ایم [و] آن را چنان که شایسته است می‌خوانند، به [مطالبِ] آن [که دربارۀ حقانیت محمد صلی الله علیه وسلم و رسالتش است] ایمان می‌آورند؛ و کسانی که به او [و آیینش] کافر شوند آنانند که زیانکارند.');
INSERT INTO "islamhouse" VALUES(2,122,'2:122','ای بنی‌اسرائیل، نعمتم را که بر شما ارزانی داشتم به یاد آورید و اینکه شما را [به دلیل برخورداری از پیامبران متعدد و حقِ فرمانروایی،] بر مردم [زمان خودتان] برتری بخشیدم.');
INSERT INTO "islamhouse" VALUES(2,123,'2:123','از روزی بترسید که هیچ کس از دیگری دفاع نمی‌کند و هیچ تاوان و فدیه‌ای از او پذیرفته نمی‌شود و هیچ شفاعتی سودی برایش ندارد و [انسان‌ها] یاری نمی‌شوند.');
INSERT INTO "islamhouse" VALUES(2,124,'2:124','و [ای پیامبر، یاد کن از] هنگامی که الله ابراهیم را با سخنانی [مشتمل بر احکام و تکالیف شرعی] آزمود و او همه را به خوبی به انجام رساند. [الله به وی] فرمود: «من تو را پیشوای مردم قرار می‌دهم». [ابراهیم] گفت: «[پروردگارا] از فرزندانم [نیز پیشوایانی قرار بده تا مردم به آنان اقتدا کنند]». [الله] فرمود: «عهد و پیمانِ من [با تو در مورد پیشواییِ دین]، به ستمکاران نمی‌رسد».');
INSERT INTO "islamhouse" VALUES(2,125,'2:125','و [یاد کن از] هنگامی که خانۀ [کعبه] را محلِ بازگشت [حاجیان مشتاق] و [مکانِ] امنی برای مردم قرار دادیم و [مقرر کردیم که] مقام ابراهیم را نمازگاهی برای خویش انتخاب کنید؛ و ما به ابراهیم و اسماعیل وحی کردیم که: «خانۀ مرا برای طواف‌کنندگان و مُعتکفان و رکوع و سجده‌کنندگان [= نمازگزاران] پاک و پاکیزه کنید».');
INSERT INTO "islamhouse" VALUES(2,126,'2:126','و [یاد کن از] هنگامی که ابراهیم گفت: «پروردگارا، این [سرزمین مکه] را شهری امن بگردان و اهل آن را ـ [یعنی] کسانی از ایشان را که به الله و روز قیامت ایمان آورده‌اند ـ از میوه‌های [گوناگون] روزی ده». [الله] فرمود: «هر کس کافر شود، اندکی بهره‌مندش می‌سازم، سپس او را به عذابِ آتش [دوزخ] می‌کشانم؛ و چه بد سرانجامی است!».');
INSERT INTO "islamhouse" VALUES(2,127,'2:127','و [یاد کن از] هنگامی که ابراهیم و اسماعیل پایه‌های خانه [کعبه] را بالا می‌بردند [و می‌گفتند]: «پروردگارا، [اعمالِ نیک ما ـ از جمله بنای این خانۀ مقدس را] از ما بپذیر که بی‌تردید، تو شنوای دانایی.');
INSERT INTO "islamhouse" VALUES(2,128,'2:128','پروردگارا! ما را [تسلیم و] فرمانبردارِ خود قرار بده و از نسل ما [نیز] امتی فرمانبردارِ خود [پدید آور] و آداب عبادتمان را به ما نشان بده و توبۀ ما را بپذیر. حقا که تو توبه‌پذیرِ مهربانی.');
INSERT INTO "islamhouse" VALUES(2,129,'2:129','پروردگارا، در میان آنان [= مردم مکه] پیامبری از خودشان [از نسلِ اسماعیل] برانگیز تا آیاتِ تو را بر آنان بخوانَد و کتاب و حکمت به آنان بیاموزد و [از شرک و نادانی] پاکیزه‌شان گردانَد. همانا که تو پیروزمندِ حکیمی».');
INSERT INTO "islamhouse" VALUES(2,130,'2:130','و چه کسی جز آن که خود را به [سبکسری و] نادانی می‌اندازد از آیین ابراهیم رویگردان می‌گردد؟ و به راستی که ما او را در دنیا برگزیدیم و بی‌تردید، در آخرت [نیز] از شایستگان خواهد بود.');
INSERT INTO "islamhouse" VALUES(2,131,'2:131','آنگاه که پروردگارش به او فرمود: «[خالصانه] تسلیم شو» [و او در پاسخ] گفت: «تسلیمِ پروردگار جهانیان شدم».');
INSERT INTO "islamhouse" VALUES(2,132,'2:132','و ابراهیم و یعقوب فرزندانشان را به این [آیین] سفارش کردند [و هر یک به آنان می‌گفتند]: «ای فرزندان من، الله این آیین را برای شما برگزیده است [پس به آن بگروید] و از دنیا نروید مگر اینکه تسلیم [فرمانِ حق] باشید».');
INSERT INTO "islamhouse" VALUES(2,133,'2:133','آیا هنگامی که مرگِ یعقوب فرا رسید، شما حاضر بودید؟ آنگاه که به فرزندانش گفت: «بعد از من چه چیز را عبادت می‌کنید؟» گفتند: «معبودِ تو و معبودِ پدرانت ـ ابراهیم و اسماعیل و اسحاق ـ الله یکتا را عبادت می‌کنیم و ما تسلیم او هستیم».');
INSERT INTO "islamhouse" VALUES(2,134,'2:134','آنان امتی بودند که درگذشتند؛ آنچه که [از نتیجۀ اعمالشان] کسب کرده‌اند، از آنِ خودشان است و آنچه شما کسب کرده‌اید از آنِ خودتان است؛ و شما دربارۀ آنچه [آنان] انجام می‌دادند بازخواست نخواهید شد.');
INSERT INTO "islamhouse" VALUES(2,135,'2:135','و [اهل کتاب] گفتند: «یهودی یا نصرانی شوید تا هدایت یابید». [ای پیامبر، به آنان] بگو: «[نه از آیین شما] بلکه از آیین ابراهیم [پیروی می‌کنیم] که حقگرا بود و هرگز در زمرۀ مشرکان نبود».');
INSERT INTO "islamhouse" VALUES(2,136,'2:136','[ای مؤمنان، به اهل کتاب] بگویید: «ما به الله ایمان آورده‌ایم و به آنچه بر ما نازل شده است و به آنچه که بر ابراهیم و اسماعیل و اسحاق و یعقوب و نوادگان [یعقوب] نازل گردید و [همچنین] به آنچه که به موسی و عیسی داده شده است و به آنچه که به پیامبران [دیگر] از طرف پروردگارشان داده شده است؛ [آری به همۀ آنان ایمان می‌آوریم] و میان هیچ یک از ایشان تفاوت نمی‌گذاریم و ما [همگی] تسلیمِ الله هستیم».');
INSERT INTO "islamhouse" VALUES(2,137,'2:137','پس اگر آنان [نیز] به آنچه شما ایمان آورده‌اید ایمان آوردند، قطعاً هدایت یافته‌اند؛ و اگر پشت کنند، پس جز این نیست که قطعاً سرِ ستیز [و جدایی] دارند؛ پس [ای پیامبر، پروردگار یکتا برای حمایتِ تو کافی است و] به زودی الله [شرِّ] آنها را از تو دفع خواهد نمود و او شنوای داناست.');
INSERT INTO "islamhouse" VALUES(2,138,'2:138','رنگِ الهی [و دینِ او را بپذیرید و به آن پایبند باشید] و چه کسی خوش‌نگارتر از الله است؟ و ما تنها او را عبادت می‌کنیم.');
INSERT INTO "islamhouse" VALUES(2,139,'2:139','[ای پیامبر، به اهل کتاب] بگو: «آیا دربارۀ الله با ما مجادله می‌کنید؟! در حالی که او پروردگارِ ما و شماست و کردار ما از آنِ ما و کردار شما از آنِ شماست؛ و ما او را با اخلاص عبادت می‌کنیم».');
INSERT INTO "islamhouse" VALUES(2,140,'2:140','[آیا] می‌گویید: «ابراهیم و اسماعیل و اسحاق و یعقوب و نوادگان [یعقوب] یهودی یا نصرانی بودند [در حالى كه یهودیّت و نصرانيّت پس از این پیامبران به‌وجود آمده‌اند]؟» بگو: «شما داناترید یا الله؟» و کیست ستمکارتر از آن کس که شهادتی را که از [جانب] الله [در کتاب‌های آسمانی دربارۀ این پیامبران آمده و] نزد اوست کتمان می‌کند؟ و [یقین بدانید که] الله از آنچه می‌کنید غافل نیست.');
INSERT INTO "islamhouse" VALUES(2,141,'2:141','[به هر حال،] آنان امتی بودند که درگذشتند؛ آنچه که [از نتیجۀ اعمالشان] کسب کرده‌اند، از آنِ خودشان است و آنچه شما کسب کرده‌اید از آنِ خودتان است؛ و شما دربارۀ آنچه [آنان] انجام می‌دادند بازخواست نخواهید شد.');
INSERT INTO "islamhouse" VALUES(2,142,'2:142','به زودی گروهی از مردمِ کم‌خرد [یهودی] خواهند گفت: «چه چیزی آنها [=مسلمانان] را از قبله‌شان [= بیت‌المقدس] که بر آن بودند [به سوی کعبه] بازگردانْد؟» [ای پیامبر،] بگو: «مشرق و مغرب از آنِ الله است؛ هرکس را بخواهد، به راه راست هدایت می‌کند».');
INSERT INTO "islamhouse" VALUES(2,143,'2:143','همان‌گونه [که قبله‌ای پسندیده برای شما برگزیدیم] شما را نیز، امتی میانه[رو و اهل اعتدال] قرار دادیم تا [در روز قیامت] بر مردم گواه باشید [که پیامبران الهی پیامشان را به درستی ابلاغ کردند]؛ و پیامبر بر شما گواه باشد [که کلام الله را به شما رسانده است]؛ و ما قبله‌اى را كه پیشتر بر آن بودى [= بیت‌المقدس، قبله] قرار ندادیم، مگر به این هدف تا كسى را كه از پیامبر پیروى مى‌كند، از كسى كه به عقب بازمى‌گردد [و از اطاعت الله و رسولش رویگردان می‌شود] مشخص کنیم. مسلّماً این [حکم،] جز بر کسانی ‌که الله هدایتشان کرده باشد، سنگین و دشوار است. الله هرگز ایمان [و نماز] شما را ضایع نمی‌گردانَد. به راستی که الله نسبت به مردم، دلسوز و مهربان است.');
INSERT INTO "islamhouse" VALUES(2,144,'2:144','[ای پیامبر،] به راستی که ما گرداندنِ رویت به [سوی] آسمان را [که در انتظار وحی الهی برای تغییر قبله هستی] می‌بینیم. اکنون تو را به [سوی] قبله‌ای بازمی‌گردانیم که به آن راضی ‌شوی؛ پس روی خود را به سوی مسجدالحرام کن و [شما نیز ای مؤمنان، برای ادای نماز] هر جا بودید، روی خود را به سوی آن بگردانید. کسانی که کتاب [آسمانی] به آنها داده شد، به خوبی می‌دانند که این [تغییر قبله، فرمانی] حق [و] از جانب پروردگارشان است؛ و الله از آنچه می‌کنند غافل نیست.');
INSERT INTO "islamhouse" VALUES(2,145,'2:145','اگر برای کسانی که کتاب [آسمانی] به آنان داده شده است هر گونه نشانه [و دلیلی برای حقانیتِ تغییر قبله] بیاوری، [باز هم] از قبلۀ تو پیروی نمی‌کنند و تو [نیز] از قبلۀ آنان پیروی نخواهی کرد و برخی از آنان نیز از قبلۀ برخی دیگر پیروی نمی‌کنند؛ و اگر پس از آنکه علم و دانش به تو رسیده است، از خواسته‌های [نفسانیِ] آنان [در مورد قبله و سایر احکام] پیروی کنی، مسلماً از ستمکاران خواهی بود.');
INSERT INTO "islamhouse" VALUES(2,146,'2:146','کسانی که به آنان کتاب [آسمانی] داده‌ایم، او [= محمد صلی الله علیه وسلم] را چنان می‌شناسند که فرزندان خود را می‌شناسند [و می‌دانند که سخنش برحق است]؛ و[لی] گروهی از آنان ـ در حالی که خود نیز می‌دانند ـ حق را کتمان می‌کنند.');
INSERT INTO "islamhouse" VALUES(2,147,'2:147','[ای پیامبر، آنچه بر تو نازل می‌گردد، حکمِ] حق از جانب پروردگارِ توست؛ پس هرگز از تردید‌کنندگان مباش.');
INSERT INTO "islamhouse" VALUES(2,148,'2:148','هر [امتی] جهتی دارد که [هنگام نماز] به آن رو می‌کند؛ پس [شما ای مؤمنان] در نیکی‌ها [بر یکدیگر] سبقت بگیرید. هر جا که باشید، الله [در روزِ قیامت،] همۀ شما را حاضر می‌کند. همانا الله بر هر چیزی تواناست.');
INSERT INTO "islamhouse" VALUES(2,149,'2:149','[ای پیامبر،] از هر مکانی که [به قصدِ سفر] خارج شدی، [هنگام نماز] روی خود را به سوی مسجدالحرام کن؛ و به درستی که این [قبله] حق [و] از جانب پروردگارت است؛ و الله از آنچه می‌کنید غافل نیست.');
INSERT INTO "islamhouse" VALUES(2,150,'2:150','و از هر مکانی که بیرون رفتی، [هنگام نماز] روی خود را به سوی مسجدالحرام کن و [شما ای مؤمنان،] هر جا که بودید، روی خود را به سوی آن [مسجد] کنید تا مردم علیه شما حجتی نداشته باشند [تا با استناد به آنها بهانه‌تراشی کنند]؛ مگر کسانی از آنان که ستم کردند [و همواره دشمنتان خواهند بود]؛ پس از آنان نترسید و از من بترسید [این تغییر قبله به این هدف بود] که نعمتم را بر شما تمام کنم [و شما را متمایز سازم]؛ باشد که هدایت شوید.');
INSERT INTO "islamhouse" VALUES(2,151,'2:151','همچنان که [نعمت دیگری نیز به شما ارزانی داشتیم و] پیامبری از خودتان به میانتان فرستادیم که آیات ما را بر شما می‌خوانَد و شما را [با امر به معروف و نهی از منکر، از پلیدی‌ها] پاک می‌گردانَد و کتاب [= قرآن] و حکمت به شما می‌آموزد و آنچه را [که از امور دین و دنیایتان که] نمی‌دانستید به شما آموزش می‌دهد.');
INSERT INTO "islamhouse" VALUES(2,152,'2:152','پس مرا یاد کنید تا شما را یاد کنم و شکر مرا به جای آورید و از من ناسپاسی نکنید.');
INSERT INTO "islamhouse" VALUES(2,153,'2:153','ای کسانی که ایمان آورده‌اید، از شکیبایی و نماز یاری بجویید [که] بی‌تردید، الله با شکیبایان است.');
INSERT INTO "islamhouse" VALUES(2,154,'2:154','و به کسانی که در راه الله کشته می‌شوند، مُرده نگویید؛ بلکه آنان [نزد پروردگارشان] زنده‌اند؛ ولی شما نمی‌فهمید.');
INSERT INTO "islamhouse" VALUES(2,155,'2:155','و قطعاً شما را با چیزی از [قبیل] ترس و گرسنگی [= قحطی] و زیان مالی [= نابودی یا دشواریِ کسبِ آن] و جانی [= مرگ و میر یا شهادت] و زیان محصولات [کشاورزی] می‌آزماییم؛ و [ای پیامبر،] شکیبایان را [به آنچه در دنیا و آخرت برایشان فراهم می‌آید] بشارت بده؛');
INSERT INTO "islamhouse" VALUES(2,156,'2:156','همان کسانی که هر گاه مصیبتی به ایشان می‌رسد [از روی رضایت و تسلیم] می‌گویند: «به راستی که ما از آنِ الله هستیم و به سوی او بازمی‌گردیم».');
INSERT INTO "islamhouse" VALUES(2,157,'2:157','اینها هستند که درودها و رحمتی از پروردگارشان برایشان است و همینان هدایت‌یافتگانند.');
INSERT INTO "islamhouse" VALUES(2,158,'2:158','«صفا» و «مروه» از نشانه‌های [ظاهریِ شریعتِ] الله است؛ پس هر کس که حج خانه [کعبه] یا عمره به جای می‌آورد بر او گناهی نیست که طواف [= سعی] بین آن دو را [نیز] انجام دهد [و این کار، از رفتارهای جاهلیت تلقی نمی‌گردد]؛ و کسی که به میلِ خود کارِ نیکی انجام دهد، [بداند که] بی‌تردید، الله سپاس‌گزار و داناست.');
INSERT INTO "islamhouse" VALUES(2,159,'2:159','کسانی ‌که دلایل روشن و هدایتی را که نازل کرده‌ایم ـ پس از آنکه در کتاب [= تورات و انجیل] برای مردم بیان نمودیم ـ پنهان می‌دارند، الله آنان را لعنت می‌کند و لعنت‌کنندگان [ـ اعم از فرشتگان و مؤمنان ‌ـ نیز] بر آنان لعنت می‌کنند؛');
INSERT INTO "islamhouse" VALUES(2,160,'2:160','مگر کسانی ‌که توبه کنند و [کارهای ناشایستِ خود را] اصلاح نمایند و [حقیقت را] آشکار سازند؛ اینان هستند که توبه‌شان را می‌پذیرم و من توبه‌پذیرِ مهربانم.');
INSERT INTO "islamhouse" VALUES(2,161,'2:161','کسانی ‌که کفر ورزیدند [و به انکار حق کوشیدند] و در حال کفر مُردند، لعنتِ الله و فرشتگان و همۀ مردم بر آنها خواهد بود.');
INSERT INTO "islamhouse" VALUES(2,162,'2:162','در آن [لعنت و عذاب] جاودانه باقی می‌مانند و نه از عذابشان کاسته می‌شود و نه مهلت [عذرخواهی] می‌یابند.');
INSERT INTO "islamhouse" VALUES(2,163,'2:163','[ای مردم،] معبودِ شما معبودی یگانه است [که] جز او [هیچ] معبودی [به‌حق] نیست؛ [او] بخشندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(2,164,'2:164','به راستی، در آفرینش آسمان‌ها و زمین و گذرِ شب و روز و کشتی‌هایی که برای بهره‌برداریِ مردم در دریا روانند و آبی که الله از آسمان نازل نموده است که با آن زمین را پس از مردنش‌ [و نابود شدن گياه آن] زنده کرده و انواع جنبندگان را در آن پراکنده است و [همچنین] در تغییر مسیرِ بادها و ابرهایی که در میان آسمان و زمین به خدمت [انسان] گماشته شده‌اند، برای کسانی که می‌اندیشند، نشانه‌هایی [واضح از قدرت پروردگار] است.');
INSERT INTO "islamhouse" VALUES(2,165,'2:165','[با وجود این آیات روشن،] برخی از مردم، همتایانی غیر از الله را [برای عبادت] برمی‌گزینند و آنها را همچون الله دوست می‌دارند؛ و[لی] کسانی ‌که ایمان آورده‌اند الله را بیشتر دوست ‌دارند؛ و کسانی که [با ارتکاب شرک و گناهان، بر خود] ستم کردند، هنگامی که عذاب [قیامت] را ببینند، [خواهند دانست] که تمام قدرت از آنِ الله است و اینکه الله سخت‌کیفر است.');
INSERT INTO "islamhouse" VALUES(2,166,'2:166','آنگاه ‌که رهبران [گمراهگر،] از پیروان خود بیزاری می‌جویند و عذاب را می‌بینند و اسباب [و تمام روابطی که موجب نجاتشان می‌شد، همگی] قطع شود.');
INSERT INTO "islamhouse" VALUES(2,167,'2:167','و [در آن هنگام،] کسانی که [در دنیا از رهبران گمراهگر] پیروی کرده بودند می‌گویند: «کاش بازگشتی [به دنیا] برای ما بود تا همان گونه که آنان از ما بیزاری جستند [ما نیز] از آنها بیزاری می‌جستیم». الله اینچنین رفتارشان را که مایۀ حسرتشان است به آنان نشان می‌دهد و [کافران] هرگز از آتش [دوزخ] بیرون نخواهند آمد.');
INSERT INTO "islamhouse" VALUES(2,168,'2:168','ای مردم، از آنچه در زمین است حلال و پاکیزه بخورید و از [راه‌ها و] گام‌های شیطان [که به وسیلۀ آنها به تدریج شما را به سوی عذاب می‌کشد] پیروی نکنید که به راستی او دشمن آشکارِ شماست.');
INSERT INTO "islamhouse" VALUES(2,169,'2:169','[او] شما را فقط به بدی و زشتی فرمان می‌دهد و اینکه [بر آن می‌دارد تا] آنچه را که نمی‌دانید به الله نسبت دهید.');
INSERT INTO "islamhouse" VALUES(2,170,'2:170','و هنگامی که به آنان [= کافران] گفته شود: «از آنچه الله نازل کرده است پیروی کنید»، می‌گویند: [نه،] بلکه ما از چیزی که پدران خود را بر آن یافتیم پیروی می‌کنیم. آیا اگر پدرانشان [از حقیقت] چیزی نفهمیده و هدایت نیافته باشند [باز هم کورکورانه از آنان پیروی می‌کنند]؟');
INSERT INTO "islamhouse" VALUES(2,171,'2:171','مَثَل [دعوت تو ـ ای محمد ـ برای هدایتِ این] کافران، همچون کسی است که چهارپایی را صدا می‌زند ولی آن [چهارپا] چیزی جز صدا و آوایی نمی‌شنود؛ [و حقیقت آن را درک نمی‌کند. این کافران نیز چنین‌اند که در مواجهه با حق،] کر و لال و کور هستند و از این رو نمی‌اندیشند.');
INSERT INTO "islamhouse" VALUES(2,172,'2:172','ای کسانی که ایمان آورده‌اید، از نعمت‌های پاکیزه‌ای که روزیِ شما کرده‌ایم بخورید و شکرِ الله را به جای آورید اگر تنها او را عبادت می‌کنید.');
INSERT INTO "islamhouse" VALUES(2,173,'2:173','به تحقیق، [الله] مُردار و خون و گوشت خوک و آنچه را که [هنگام ذبح،] نامِ غیر الله بر آن برده شود بر شما حرام کرده است؛ پس هر كس [برای حفظ جان خود، به خوردن آنها] ناچار شود، در صورتی که سركش و زیاده‌خواه نباشد، بر او گناهی نیست. به راستی که الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(2,174,'2:174','کسانی ‌که آنچه را الله در کتاب [تورات دربارۀ نبوت محمد صلی الله علیه وسلم] نازل کرده است کتمان می‌کنند و آن را [برای کسبِ مال و مقامِ دنیا] به بهای اندکی می‌فروشند، [در حقیقت] جز آتش در شکم‌هایشان فرو نمی‌برند و الله روز قیامت با آنان سخن نمی‌گوید و [از پلیدیِ گناه] پاکشان نمی‌کند و عذاب دردناکی خواهند داشت.');
INSERT INTO "islamhouse" VALUES(2,175,'2:175','اینان همان کسانی هستند که گمراهی را به [بهای] هدایت و عذاب را به [بهای] آمرزش خریدند؛ پس بر [تحمّل] آتش [دوزخ] چه شکیبا هستند!');
INSERT INTO "islamhouse" VALUES(2,176,'2:176','این [مجازات بر کتمان حق،] به خاطر آن است که الله کتاب[های آسمانی] را به‌حق نازل کرده است و کسانی ‌که در آن اختلاف کردند، دشمنی بسیار دور و درازی [با حقیقت] دارند.');
INSERT INTO "islamhouse" VALUES(2,177,'2:177','نیکی [فقط] این نیست که [برای نماز] روی خود را به سوی مشرق و [یا] مغرب بگردانید؛ بلکه نيكى آن است كه كسى به الله و روز قیامت و فرشتگان و کتاب [آسمانی] و پیامبران ایمان آورد و مال [خود] را ـ با وجود دوست داشتنش‌ـ به خویشاوندان و یتیمان و مستمندان و در راه‌‌ماندگان و درخواست‌کنندگان [= گدایان] و در [راه آزادیِ] بردگان ببخشد و نماز برپا ‌دارد و زکات ‌بپردازد؛ و [نیز] کسانی که چون عهد می‌بندند به پیمانشان وفا می‌کنند و [کسانی که] در فقر و سختی و زیان و بیماری و به هنگام [شدت گرفتنِ] نبرد، [پایداری و] شکیبایی می‌کنند؛ آنان [که به این ویژگی‌ها آراسته‌اند] کسانی هستند که راست گفتند و همانان پرهیزگارانند.');
INSERT INTO "islamhouse" VALUES(2,178,'2:178','ای کسانی که ایمان آورده‌اید، در [مورد] کشتگان[ی که از روی عمد به قتل رسیده‌اند، حکمِ] قصاص بر شما مقرر گشته است: آزاد در برابرِ آزاد و برده در برابر برده و زن در برابر زن. پس اگر کسی از جانب برادر [دينى‌]اش [يعنى ولىّ مقتول‌]، چيزى [از حق قصاص‌] به او گذشت شود، [از سوی عفو کننده] باید خوشرفتاری شود [و سختگیری و بدرفتاری نشود، و از سوی قاتل نیز به ولی مقتول] پرداختِ [دیه] با نیکی انجام گیرد [و در آن کم و کاست نباشد]. این [حکم،] تخفیف و رحمتی است از سوی پروردگارتان؛ پس اگر کسی بعد از آن [گذشت و قبول دیه] تجاوز کند، عذابی دردناک [در انتظار] دارد.');
INSERT INTO "islamhouse" VALUES(2,179,'2:179','ای خردمندان، در قصاص برای شما [تداومِ] زندگی است؛ باشد که تقوا پیشه کنید.');
INSERT INTO "islamhouse" VALUES(2,180,'2:180','بر شما مقرر شده است که هر گاه زمانِ مرگِ یکی از شما فرا رسید، اگر مالی از خود به جای گذاشته است، برای پدر و مادر و خویشاوندان، به شیوه‌ای شایسته وصیت کند. این [کار،] حقی است بر پرهیزگاران [که باید انجام دهند].');
INSERT INTO "islamhouse" VALUES(2,181,'2:181','پس هر کس آن [وصیت] را پس از شنیدنش تغییر دهد، گناهش تنها بر [عهدۀ] کسانی است که آن را تغییر می‌دهند. به راستی که الله شنوای داناست.');
INSERT INTO "islamhouse" VALUES(2,182,'2:182','اما کسی ‌که از بی‌عدالتی [و تمایل بی‌جای] وصیت‌کننده [نسبت به بعضی ورثه] یا از گناه او [در وصیت نامشروع] بترسد و [با اصلاحِ وصیت] میان آنان [= وارثان]، سازش برقرار کند، بر او گناهی نیست [و مشمولِ حکم تبدیلِ وصیّت نمی‌باشد]. همانا الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(2,183,'2:183','ای کسانی که ایمان آورده‌اید، روزه بر شما واجب شده است همان‌گونه که بر کسانی‌ که پیش از شما بودند [نیز] واجب شده بود؛ باشد که پرهیزگار شوید.');
INSERT INTO "islamhouse" VALUES(2,184,'2:184','روزهایی معین [را روزه‌دارى كنید]؛ و هر ‌کس از شما که بیمار یا مسافر باشد، [به‌ اندازۀ آن روزهایی که نتوانسته است روزه بگیرد] تعدادی از روزهای دیگر [را روزه بگیرد]؛ و بر کسانی که توانایی آن را ندارند، لازم است فدیه بدهند [و آن، به ازای هر روز،] خوراک‌دادن به یک مسکین است؛ و هر کس به میلِ خود [بیشتر] نیکویی کند، آن [افزون‌بخشی، به سودِ اوست و] برایش بهتر است؛ و اگر [فضیلت روزه‌داری را] بدانید، روزه برایتان بهتر [از افطار و پرداختِ فدیه] است.');
INSERT INTO "islamhouse" VALUES(2,185,'2:185','ماه رمضان، [ماهی] است که قرآن در آن نازل شده است؛ [کتابی] که راهنمای مردم است و دلایل آشکاری از هدایت [با خود دارد] و جداکننده [حق از باطل] است. پس هر یک از شما [که مُقیم و سالم است و] این ماه را دریابد، باید آن را روزه بدارد و هر‌ کس که بیمار یا در سفر باشد، [باید به تعداد روزهایی که روزه نگرفته است] روزهای دیگری [را روزه بگیرد]. الله [با بیان این احکام،] برایتان آسانی می‌خواهد و دشواری نمی‌خواهد تا [بتوانید روزه ماه رمضان] را کامل کنید و الله را برای آنکه شما را هدایت کرده است، به بزرگی یاد کنید؛ باشد که شکر گزارید.');
INSERT INTO "islamhouse" VALUES(2,186,'2:186','و [ای پیامبر،] هرگاه بندگانم دربارۀ من از تو بپرسند، [به آنان بگو] من نزدیکم و دعای دعاکننده را ـ هنگامی که مرا بخوانَد ـ اجابت می‌کنم؛ پس [آنان فقط] باید دعوت مرا بپذیرند و به من ایمان بیاورند؛ باشد که راه ‌یابند.');
INSERT INTO "islamhouse" VALUES(2,187,'2:187','[ای مؤمنان،] آمیزش با همسرانتان در شب[های] روزه، بر شما حلال گردیده است. آنان لباس شما هستند و شما لباس آنان [= مایه آرامش و سبب پاکدامنی یکدیگر] هستید. الله می‌دانست که شما به خود خیانت می‌کردید [و با آنکه در اوایل اسلام، آمیزش پس از نماز عشاء در شب روزه حرام بود، شما به آن بی‌توجه بودید]؛ پس توبه‌تان را پذیرفت و از شما درگذشت؛ پس اکنون [در شب‌های روزه می‌توانید] با آنان آمیزش کنید و آنچه را که الله برایتان مقرر داشته است [از ایشان] طلب کنید. بخورید و بیاشامید تا رشتۀ سپیدِ صبح [= فجرِ صادق] از رشتۀ سیاه [شب] برایتان آشکار گردد؛ آنگاه روزه را تا شب به پایان برسانید؛ و در حالی ‌که در مساجد معتکف هستید با زنان آمیزش نکنید. این مرزهای [احکامِ] الهی است؛ پس به آنها نزدیک نشوید. الله اینچنین آیات [و احکامِ] خود را برای مردم روشن می‌سازد؛ باشد که پرهیزگار شوند.');
INSERT INTO "islamhouse" VALUES(2,188,'2:188','و اموالتان را بین خود به باطل [و ناحق] نخورید و برای آنکه بخشی از مال مردم را به گناه بخورید، قضیه را به حاکمان ارجاع ندهید، در حالی که خودتان می‌دانید [که این کار، گناه است].');
INSERT INTO "islamhouse" VALUES(2,189,'2:189','[ای پیامبر،] از تو دربارۀ هلال‌ها[ی ماه] می‌پرسند؛ بگو: «آنها بیان اوقات [و تقویم طبیعی] برای مردم و [وسیلۀ تعیینِ وقتِ] حج هستند». نیکوکاری آن نیست که [مانند دوران جاهلیت، در حال احرام،] از پشت خانه‌ها وارد شوید؛ بلكه نيكى آن است كه شخص تقوا پيشه كند؛ [پس پرهیزگار باشید] و به خانه‌ها از درهایشان وارد شوید و از الله بترسید؛ باشد که رستگار شوید.');
INSERT INTO "islamhouse" VALUES(2,190,'2:190','و در راه الله با کسانی ‌که با شما می‌جنگند، بجنگید و[لی با مُثله‌کردن و کشتن کودکان و زنان و کهنسالان] از حد تجاوز نکنید [که] به راستی، الله تجاوزگران را دوست ندارد.');
INSERT INTO "islamhouse" VALUES(2,191,'2:191','و آنان [= مشرکان] را هر کجا یافتید، بکشید و از آنجا [= مکه] که شما را بیرون کرده‌اند اخراجشان کنید؛ زیرا فتنه [کفر و شرک و آزار رساندن به مسلمانان،] بدتر از قتل است؛ و [با وجود این،] در کنار مسجدالحرام با آنان پیکار نکنید مگر اینکه در آنجا با شما بجنگند؛ پس اگر [در آنجا] با شما جنگیدند، آنان را بکشید [که] سزای کافران چنین است.');
INSERT INTO "islamhouse" VALUES(2,192,'2:192','پس اگر [از کفرشان] دست برداشتند [از نبرد دست بکشید که] بی‌گمان، الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(2,193,'2:193','و با آنها بجنگید تا فتنه [= شرک] باقی نمانَد و دین، [خالصانه] از آنِ الله گردد. پس اگر دست برداشتند، [ستم نکنید که] ستم، جز بر ستمکاران روا نیست.');
INSERT INTO "islamhouse" VALUES(2,194,'2:194','[این] ماه حرام [(سال هفتم هجری) که امکان عمره‌گزاردن یافتید]، در برابر ماه حرام [سال گذشته] است [که مشرکان شما را از ادای حج بازداشتند] و [هتکِ] حُرمتها[ی سرزمین و ماه حرام] قصاص دارد؛ پس هر‌کس‌ که دشمنی کرد، همان گونه که بر شما دشمنی کرده است، با او دشمنی نمایید و از الله پروا کنید؛ و بدانید که الله با پرهیزگاران است.');
INSERT INTO "islamhouse" VALUES(2,195,'2:195','و در راه الله انفاق کنید و [با فرار از میدان جهاد و ترک انفاق،] خود را با دست خویشتن به هلاکت نیفکنید؛ و نیکی کنید [که] به راستی الله نیکوکاران را دوست ‌دارد.');
INSERT INTO "islamhouse" VALUES(2,196,'2:196','و حج و عمره را برای الله به پایان رسانید و اگر [به سبب مانعی مانند ترس از دشمن یا ابتلا به بیماری از این کار] بازماندید‌، آنچه از قربانی [اعم از شتر، گاو یا گوسفند] میسر شد [ذبح کنید و آنگاه از احرام خارج شوید] و سرهایتان را نتراشید تا قربانی به جایگاهش [در قربانگاه] برسد؛ و اگر کسی از شما بیمار بود و یا ناراحتی‌ای در سر داشت [که ناچار بود به سبب آن، در حال احرام سرش را بتراشد،] فدیه بدهد؛ از [قبیل: سه روز] روزه یا صدقه [= خوراک دادن به شش مسکینِ حرم] یا قربانی کردن گوسفندی [که میان فقرای حرم تقسیم شود]. پس هنگامی‌ که در امان بودید، هر‌ کس از عمره بهره‌مند گردید و سپس [اعمال] حج را آغاز کرد، آنچه از قربانی [برایش] میسر شد [ذبح ‌کند]؛ و هر ‌کس [قربانی] نیافت، [باید] سه روز در [ایام] حج و هفت روز [دیگر] هنگامی ‌که [به خانه] بازگشتید روزه بگیرد. این، ده [روزِ] کامل است. این [حکم] برای کسی است که خانواده‌اش ساکن مسجدالحرام [= مکه و اطراف آن] نباشد؛ و از الله پروا کنید و بدانید که الله سخت‌کیفر است.');
INSERT INTO "islamhouse" VALUES(2,197,'2:197','[موسمِ] حج ماه‌های مشخصی است [از آغاز شوال تا دهم ذیحجه]؛ پس کسی‌ که در این ماه‌ها حج را [بر خود] واجب گردانَد، [باید بداند که] در حج، آمیزش جنسی و [ارتکابِ] گناه و درگیری [و جدال، روا] نیست؛ و هر کار نیکی که انجام دهید، الله آن را می‌داند؛ و [خوراک و پوشاک مورد نیاز حج را برای خود فراهم کنید و] توشه برگیرید [ولی به یاد داشته باشید که] به راستی، بهترین توشه، پرهیزگاری است؛ و ای خردمندان، از من پروا کنید.');
INSERT INTO "islamhouse" VALUES(2,198,'2:198','گناهی بر شما نیست که [در ایام حج، با خرید و فروش] از فضل پروردگارتان [منفعت و روزی] طلب کنید؛ و هنگامی‌ که [روز نهم، پس از وقوف] از عرفات کوچ کردید، الله را در «مَشعَر الحرام» یاد کنید؛ و او را یاد کنید، زیرا شما را که پیشتر از گمراهان بودید [به ادای مناسک حج و دیگر اعمال عبادی] هدایت کرد.');
INSERT INTO "islamhouse" VALUES(2,199,'2:199','سپس از همانجا که مردم [= پیروان ابراهیم] روانه می‌شوند [شما نیز از عرفات به سوی مِنی] حرکت کنید و از الله آمرزش بخواهید [زیرا] بی‌تردید، الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(2,200,'2:200','پس چون مناسک [حج] خود را به جای آوردید، همان گونه که پدرانتان را [با فخر و غرور] یاد می‌کردید ـ بلکه [بهتر و] بیشتر از آن ـ الله را یاد کنید؛ و برخی از مردم [کافرانی] هستند که می‌گویند: «پروردگارا، در دنیا [مال و فرزند] به ما عطا کن». این شخص در آخرت بهره‌ای [از نعمت‌های الهی] ندارد.');
INSERT INTO "islamhouse" VALUES(2,201,'2:201','و برخی [دیگر] از ایشان [مؤمنانی] هستند که می‌گویند: «پروردگارا، در دنیا نیکی [= روزی، سلامتی، دانش و ایمان] به ما عطا کن و در آخرت [نیز] نیکی [عطا فرما] و ما را از عذاب آتش نگه دار».');
INSERT INTO "islamhouse" VALUES(2,202,'2:202','اینانند که از دستاوردشان بهره‌ای دارند؛ و الله در حسابرسى، سریع است.');
INSERT INTO "islamhouse" VALUES(2,203,'2:203','الله را در روزهای معیّن [= سه روز ایام تَشریق (یازدهم تا سیزدهم ذی‌حجه)] یاد کنید؛ پس کسی ‌که شتاب کند و طی دو روز [اعمال را انجام دهد،] گناهی بر او نیست؛ و کسی که تأخیر کند [و اعمال را در سه روز انجام دهد،] گناهی بر او نیست. [این حق اختیار،] برای کسی‌ است که تقوا پیشه کند؛ و [ای مؤمنان،] از الله پروا کنید و بدانید که به سوی او محشور خواهید شد.');
INSERT INTO "islamhouse" VALUES(2,204,'2:204','و [ای پیامبر،] از مردم کسی هست که گفتارش در [مورد] زندگی دنیا تو را به شگفت می‌اندازد و الله را بر [ایمان قلبی و] آنچه در دلش دارد گواه می‌گیرد [تا جایی که او را راستگو می‌پنداری] و حال آنکه او [در زمرۀ] سرسخت‌ترینِ دشمنان [و بدخواهانِ اسلام و مسلمین] است.');
INSERT INTO "islamhouse" VALUES(2,205,'2:205','او هنگامی ‌که [از تو] روی برمی‌گردانَد و [از نزدت] می‌رود [یا به ریاستی می‌رسد،] در راهِ فساد در زمین می‌کوشد و [محصولات] کشاورزی و چهارپایان را نابود می‌سازد؛ و الله فساد و [تباهکاری] را دوست ندارد.');
INSERT INTO "islamhouse" VALUES(2,206,'2:206','و هنگامی که به او گفته می‌شود: «از الله بترس»، خودبزرگ‌بینی [و غرور] او را به [استمرار بر انجام] گناه می‌کشانَد؛ پس [آتش] جهنم برایش کافی است و چه بد جایگاهی است!');
INSERT INTO "islamhouse" VALUES(2,207,'2:207','و از مردم کسی [نیز] هست که جان خود را برای کسبِ خشنودی الله می‌فروشد؛ و الله نسبت به بندگان مهربان است.');
INSERT INTO "islamhouse" VALUES(2,208,'2:208','ای کسانی که ایمان آورده‌اید، همگی به اسلام [و اطاعتِ الله] درآیید و از [راه‌ها و] گام‌های شیطان پیروی نکنید. به راستی که او دشمن آشکار شماست.');
INSERT INTO "islamhouse" VALUES(2,209,'2:209','و اگر پس از آنکه نشانه‌های روشن و آشکار برایتان آمد، لغزش کردید و [منحرف شدید]، بدانید که الله شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(2,210,'2:210','آیا انتظارِ [پیروان شیطان] غیر از این است که الله [در روز قیامت، برای داوری] در سایبان‌هایی از ابر به سویشان بیاید و فرشتگان [نیز برای اجرای عذاب الهی آنان را احاطه کنند] و کار یکسره شود؟ و [آگاه باشید كه همۀ] کارها به سوی الله بازگردانده می‌شود.');
INSERT INTO "islamhouse" VALUES(2,211,'2:211','[ای پیامبر،] از بنی‌اسرائیل بپرس [که] چه اندازه نشانه‌های آشکار به آنها داده‌ایم؛ و کسی‌ که نعمتِ الله را ـ پس از آنکه نصیبش شدـ [به کفر و ناسپاسی] تبدیل کند، [عذاب سختی خواهد داشت و باید بداند که] الله سخت‌کیفر است.');
INSERT INTO "islamhouse" VALUES(2,212,'2:212','زندگی دنیا برای کافران آراسته شده است و [آنان] مؤمنان را مسخره می‌کنند؛ و [حال آنکه] پرهیزگاران در روز قیامت بالاتر از آنان هستند؛ [زیرا در بهشت جاودانند] و الله هر ‌کس را بخواهد، بی‌شمار روزی می‌دهد.');
INSERT INTO "islamhouse" VALUES(2,213,'2:213','[در آغاز،] مردم یک امت [و پیرو یک دین] بودند [اما پس از آنکه اختلاف کردند]؛ آنگاه الله پیامبران را بشارت‌بخش و بیم‌دهنده برانگیخت و همراه با آنان کتاب [آسمانی] را به‌حق نازل کرد تا میان مردم در آنچه اختلاف داشتند داوری کنند؛ و فقط کسانی‌ که [تورات] به آنان داده شده بود ـ پس از آنکه نشانه‌های روشن به آنها رسیدـ به خاطر ستم و حسادتی که بینشان بود، در مورد آن اختلاف کردند؛ پس الله به فرمانِ خویش کسانی را که ایمان آورده بودند، به حقیقتی که در آن اختلاف داشتند هدایت نمود؛ و الله هر کس را بخواهد، به راه راست هدایت می‌کند.');
INSERT INTO "islamhouse" VALUES(2,214,'2:214','[ای مؤمنان،] آیا پنداشته‌اید که وارد بهشت می‌شوید و حال آنکه هنوز مانند آنچه بر [سرِ] پیشینیان شما آمد، بر [سرِ] شما نیامده است؟ دشواری و زیان به آنان رسید و [چنان سختی کشیدند و] به لرزه افتادند که پیامبر و کسانی که با او ایمان آورده بودند گفتند: «یاریِ الله کی فرا خواهد رسید؟» آگاه باشید! بی‌تردید، یاریِ الله نزدیک است.');
INSERT INTO "islamhouse" VALUES(2,215,'2:215','[ای پیامبر، یارانت] از تو می‌پرسند چه چیزی انفاق کنند؛ بگو: «هر مالی که انفاق کردید، باید به پدر و مادر و نزدیکان و یتیمان و نیازمندان و درراه‌ماندگان باشد؛ و هر کار نیکی که می‌کنید، الله به آن داناست».');
INSERT INTO "islamhouse" VALUES(2,216,'2:216','[ای مؤمنان،] جهاد [در راه الله] بر شما واجب شده است، در حالی که [به سبب هزینۀ مالی و جانی] برایتان ناخوشایند است. چه بسا از چیزی خوشتان نیاید، حال آنکه برایتان بهتر است و چه بسا چیزی را دوست داشته باشید، حال آنکه برایتان شرّ است؛ و الله [خیر و شرِّ امور را] می‌داند و شما نمی‌دانید.');
INSERT INTO "islamhouse" VALUES(2,217,'2:217','[ای پیامبر،] دربارۀ [حکم] جنگ در ماه حرام [= محرم، رجب، ذی‌قعده و ذی‌حجه] از تو می‌پرسند؛ بگو: «جنگ در این [ماه‌ها، گناهی] بزرگ است و بازداشتن از راه الله و کفر ورزیدن به او و [جلوگیری از ورود مردم به] مسجدالحرام و بیرون راندن اهلش از آنجا، نزد الله [گناهی] بزرگ‌تر است؛ و فتنه[ی شرک] از قتل بزرگ‌تر است». و [ای مؤمنان، مشرکان] پیوسته با شما می‌جنگند تا اگر بتوانند، شما را از دینتان برگردانند؛ و کسانی از شما که از دینشان باز‌گردند و در حال کفر بمیرند، اعمالشان در دنیا و آخرت تباه می‌گردد و اینان اهلِ آتشند و همیشه در آن خواهند بود.');
INSERT INTO "islamhouse" VALUES(2,218,'2:218','کسانی که ایمان آوردند و کسانی که [برای برپاییِ دین حق] هجرت نمودند و در راه الله جهاد کردند، آنان به رحمتِ الله امیدوارند و الله [نسبت به اینان] آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(2,219,'2:219','[ای پیامبر، اصحابت] دربارۀ شراب و قمار، از تو می‌پرسند؛ بگو: «در آن دو، گناهی بزرگ و [نیز] سودهایی برای مردم وجود دارد؛ ولی گناهِ آن دو، از سودشان [بیشتر و] بزرگ‌تر است»؛ و از تو می‌پرسند: چه چیزی انفاق کنند؟ بگو: «مازاد [از نیازمندی خود را]». الله اینچنین آیات [خود] را برای شما روشن می‌سازد؛ باشد که بیندیشید.');
INSERT INTO "islamhouse" VALUES(2,220,'2:220','دربارۀ دنیا و آخرت [بیندیشید]؛ و [ای پیامبر،] از تو در مورد [نحوۀ سرپرستی‌شان بر] یتیمان می‌پرسند؛ بگو: «اصلاحِ [کار و پرهیز از درآمیختن اموالِ] آنان بهتر است؛ و اگر با [تجمیع اموال آنان با داراییِ خود، در هزینه‌های زندگی و مسکن با] آنان همزیستی ‌کنید، [ایرادی در این کار نیست؛ زیرا] آنها برادرانِ [دینی] شما هستند». و الله تبهکار را از درستکار می‌شناسد؛ و اگر الله می‌خواست، [در مورد یتیمان سخت‌گیری می‌کرد و] شما را به زحمت می‌انداخت. به راستی که الله شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(2,221,'2:221','و [ای مؤمنان] با زنان مشرک تا ایمان نیاورده‌اند ازدواج نکنید؛ و بی‌گمان، کنیزِ مؤمن، از زنِ [آزادِ] مشرک بهتر است؛ گرچه [زیبایی یا دارایی‌اش] مورد پسندتان واقع گردد؛ و [زنان مسلمان را] به ازدواج مردان مشرک درنیاورید، مگر آنکه ایمان بیاورند؛ و قطعاً بردۀ مؤمن، از مردِ [آزاد] مشرک بهتر است؛ گرچه شیفتۀ [دارایی و موقعیتِ] او شده باشید. آنان به سوی آتش [دوزخ] فرا می‌خوانند و الله به فرمان [و توفیق] خویش به سوی بهشت و آمرزش دعوت می‌کند و آیات [و احکام] خویش را برای مردم روشن می‌سازد؛ باشد که پند گیرند.');
INSERT INTO "islamhouse" VALUES(2,222,'2:222','و [ای پیامبر،] از تو دربارۀ حیض [= عادت ماهانۀ زنان] می‌پرسند؛ بگو: «آن پلید و زیانبار است؛ پس در حالت قاعدگی از [آمیزش با] زنان کناره‌گیری کنید و با آنان نزدیکی نکنید تا پاک شوند. پس هنگامی که پاک شدند [و غسل کردند،] از همان جا که الله به شما فرمان داده است [یعنی از راه قُبُل و نه از راه دُبُر] با آنان آمیزش کنید. به راستی که الله توبه‌کنندگان را دوست دارد و پاکیزگان را [نیز] دوست ‌دارد».');
INSERT INTO "islamhouse" VALUES(2,223,'2:223','زنان شما، [در حُکمِ] کشتزاری برای شما هستند؛ پس هر گونه که می‌خواهید به کشتزار [= رَحِمِ همسرِ] خود درآیید، و [توشه‌ای همچون فرزند صالح] برای خود پیش بفرستید و از الله پروا کنید و بدانید که او را ملاقات خواهید کرد، و [ای پیامبر، دربارۀ نعمت‌های الهی] به مؤمنان بشارت بده.');
INSERT INTO "islamhouse" VALUES(2,224,'2:224','و الله را دستاویز سوگندهایتان قرار ندهید تا [به این بهانه] از نیکوکاری و پرهیزگاری و آشتی‌دادن میان مردم [دست بردارید]؛ و [بدانید که] الله شنوای داناست.');
INSERT INTO "islamhouse" VALUES(2,225,'2:225','الله شما را به خاطر سوگند‌هایی که بیهوده و بدون قصد یاد می‌کنید، مؤاخذه نمی‌کند؛ ولی به خاطر آنچه دل‌هایتان [عمداً] کسب کرده است [و سوگندهای دروغی که آگاهانه بر زبان آورده‌اید] بازخواست می‌کند؛ و [اگر توبه کنید،] الله آمرزندۀ بردبار است.');
INSERT INTO "islamhouse" VALUES(2,226,'2:226','براى كسانى كه [به رسم جاهلیت] بر ترک آمیزش با زنان خود سوگند یاد كنند [= ایلاء می‌کنند] چهار ماه انتظار [و مهلت] است. پس اگر [در مدت چهار ماه یا کمتر، پشیمان شدند و به زنانشان] بازگشتند، [بدانند که] الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(2,227,'2:227','و اگر [بر ترک زنانشان پافشاری کردند و] تصمیم به طلاق گرفتند، [بدانند که] همانا الله شنوای داناست.');
INSERT INTO "islamhouse" VALUES(2,228,'2:228','زنان مطلّقه [پیش از ازدواج مجدد] باید به مدت سه بار پاکی [= دیدن سه بار عادت ماهیانه] انتظار بکشند؛ و اگر به الله و روز قیامت ایمان دارند، بر آنان روا نیست که آنچه را الله در رحِم‌هایشان آفریده است کتمان کنند؛ و اگر شوهرانشان خواهان آشتی هستند، برای بازگرداندن آنان [به زندگی زناشویی] در این [مدت، از دیگران] سزاوارترند؛ و زنان حقوق شایسته‌ای [بر عهده مردان] دارند همانند [و همسنگِ وظایفی] که بر عهده دارند و مردان [در سرپرستی و امرِ طلاق] بر آنان برتری دارند؛ و الله شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(2,229,'2:229','طلاق [رجعی] دو بار است؛ پس [از آن، مرد یا باید همسرش را] به طرزی شایسته نگاه دارد یا به نیکی رها کند؛ و [ای مردان مسلمان،] برایتان حلال نیست که [ذره‌ای] از آنچه را به آنان داده‌اید [= مهریه و مانند آن] پس بگیرید؛ مگر آنکه [زن و شوهر] بترسند که نتوانند [حقوق زناشویی و] حدود الهی را بر پا دارند؛ پس [حل اختلاف را به خویشاوندان یا دوستان خود می‌سپارند؛ و شما ای مؤمنان] اگر ترسیدید که [ممکن است زوجین] حدود الهی را برپا ندارند، گناهی بر آن دو نیست که زن [برای کسب رضایت مرد به طلاق و آزاد کردن خود، مهریه‌اش را ببخشد یا به او] فدیه‌ای بپردازد [و طلاق خُلع بگیرد]. اینها حدودِ [احکامِ] الهی است؛ پس از آنها تجاوز نکنید؛ و هر‌ کس از حدود الله تجاوز کند، ستمکار است.');
INSERT INTO "islamhouse" VALUES(2,230,'2:230','اگر [شوهر برای بار سوم] او را طلاق داد، پس از آن، [زن] برایش حلال نیست؛ مگر اینکه [به قصد ازدواج حقیقی و نه برای حلال‌شدن] با شوهری غیر از او ازدواج کند [و با وی همبستر شود. در این صورت،] اگر [شوهر دوم فوت کرد یا] او را طلاق داد، اگر [آن دو زوجِ سابق] می‌پندارند که حدود [احکامِ] الله را برپا می‌دارند، گناهی بر آن دو نیست که [با ازدواج مجدد،] به یکدیگر بازگردند. اینها حدود الله است که آنها را برای مردمی که می‌دانند [و به اسلام ایمان دارند] بیان می‌کند.');
INSERT INTO "islamhouse" VALUES(2,231,'2:231','و هنگامی که زنان را طلاق [رجعی] دادید و به پایان [روزهای] عدۀ خویش رسیدند، آنان را به شایستگی نگه دارید یا به نیکی و خوبی رهایشان سازید؛ و [همچون دوران جاهلیت،] آنان را به قصد [آزار و] زیان‌رساندن نگه ندارید تا [به حقوقشان] تجاوز کنید؛ و هر کس چنین کند، قطعاً بر خویشتن ستم کرده است؛ و آیات الله را به مسخره نگیرید و نعمتِ الله را بر خود و آنچه را که از کتاب [= قرآن] و حکمت بر شما نازل کرده است و شما را با آن پند می‌دهد به یاد بیاورید و از الله پروا داشته باشید و بدانید که الله به همه چیز داناست.');
INSERT INTO "islamhouse" VALUES(2,232,'2:232','و هنگامی که زنان را طلاق دادید و [مدت] عدّۀ خود را به پایان رساندند، [شما ای سرپرستان،] آنان را بازندارید که اگر [بر اساس موازین شرعی] به طرز پسندیده‌ای بین خویش به توافق رسیده بودند، با شوهران [سابق] خود ازدواج کنند. این [دستوری است] که هر یک از شما که به الله و روز قیامت ایمان دارد، به آن [حکم] پند داده می‌شود؛ و این [دستور،] برایتان پربرکت‌تر و پاکیزه‌تر است؛ و الله [خیر و صلاح شما را] می‌داند و شما نمی‌دانید.');
INSERT INTO "islamhouse" VALUES(2,233,'2:233','و مادران [باید] فرزندان خود را دو سال تمام شیر دهند. [این دستور،] برای کسی است که بخواهد دوران شیرخوارگی [فرزندش] را تکمیل کند؛ و بر صاحبِ فرزند [= پدر] لازم است خوراک و پوشاک آنان [= مادران] را [در مدت شیردادن] به طور شایسته‌ای [فراهم کند]. هیچ کس جز به‌ اندازۀ توانایی‌اش مکلف نمی‌شود؛ نه مادر باید به خاطر فرزندش زیان ببیند و نه پدر به سبب فرزندش. [اگر پدر کودک فوت کرده بود و مالی هم نداشت] مانند همین [تعهدات پدر] بر وارث [نیز] لازم است؛ پس اگر آن دو [= پدر و مادر] با رضایت و مشورت یکدیگر بخواهند [کودک را زودتر] از شیر بازگیرند، گناهی بر آنها نیست؛ و اگر خواستید که برای فرزندانتان دایه‌ای بگیرید، گناهی بر شما نیست؛ به شرط اینکه آنچه [از حقوق و دستمزد] مقرر کرده‌اید، به طور شایسته بپردازید؛ و از الله پروا کنید و بدانید که الله به آنچه انجام می‌دهید بیناست.');
INSERT INTO "islamhouse" VALUES(2,234,'2:234','و افرادی از شما که می‌میرند و همسرانی [غیرباردار از خود] باقی می‌گذارند، [آن زنان برای ازدواج مجدد] باید چهار ماه و ده روز انتظار بکشند [و عده نگه دارند] و هنگامی که مدت عدۀ خود را به پایان رساندند، در مورد آنچه آنان به طور شایسته [و مشروع] دربارۀ [زندگیِ] خود انجام دهند، گناهی بر شما [سرپرستان] نیست؛ و الله به آنچه می‌کنید آگاه است.');
INSERT INTO "islamhouse" VALUES(2,235,'2:235','و گناهی بر شما نیست که به طور کنایه، از زنانی [که در عده وفات یا عده طلاق بائن قرار دارند] خواستگاری کنید و یا در دل [بر این کار] تصمیم بگیرید [بدون اینکه آن را بیان کنید]. الله می‌دانست که شما به زودی به یاد آنان خواهید افتاد؛ ولی پنهانی به آنها وعدۀ [زناشویی] ندهید مگر اینکه [به کنایه و اشاره] سخنی پسندیده بگویید و [همچنین] تصمیم به عقد ازدواج نگیرید تا مدت مقرر [= عدّه] به سرآید؛ و بدانید که الله آنچه را که در دل‌های شماست می‌داند؛ پس از [مخالفت با] او پروا کنید و بدانید که به راستی الله آمرزندۀ بردبار است.');
INSERT INTO "islamhouse" VALUES(2,236,'2:236','اگر زنان را قبل از آمیزش و قبل از تعیین مهریه، [به دلایلی] طلاق دادید، گناهی بر شما نیست و [در این صورت، دلجویی کنید و] آنان را [با اهدای هدیه‌ای مناسب] بهره‌مند سازید؛ توانگر به‌ اندازۀ توانش و تنگدست به ‌اندازۀ توانش. [اجرای این حُکم،] بر نیکوکاران [حقی ثابت و] الزامی است.');
INSERT INTO "islamhouse" VALUES(2,237,'2:237','اگر پیش از نزدیکی با آنان، خواستید طلاقشان بدهید و مهریه‌ای برایشان تعیین کرده بودید، نصف آنچه را که تعیین کرده‌اید [به آنان بپردازید]؛ مگر اینکه [حق خود را] ببخشند یا [شوهر، یعنی] کسی ‌که [تصمیم‌گیری در مورد] پیوند ازدواج با اوست، گذشت کند [و تمام مهریه را بپردازد]. گذشتِ شما [و بخشیدن تمام مهریه به زنان] به پرهیزگاری نزدیک‌تر است. [ای مؤمنان، هنگام پرداخت حقوق، آسان‌گیری و] نیکی را میان خود فراموش نکنید [و بدانید که] به راستی الله به آنچه می‌کنید بیناست.');
INSERT INTO "islamhouse" VALUES(2,238,'2:238','[ای مسمانان،] بر [ادای] همه نمازها و [به خصوص] نماز عصر کوشا باشید و با فروتنی و فرمانبرداری برای الله به پا خیزید.');
INSERT INTO "islamhouse" VALUES(2,239,'2:239','اگر [از خطر] ترسیدید، در حال پیاده یا سواره [یا حتی با ایما و اشاره نماز را به جای آورید] و چون ایمن شدید، الله را [چنان که به شما آموخته است] یاد کنید [و شکر او تعالی را به جای آورید] که آنچه را نمی‌دانستید به شما یاد داد.');
INSERT INTO "islamhouse" VALUES(2,240,'2:240','کسانی از شما که مرگشان فرامی‌رسد و همسرانی [از خود] به جا می‌گذارند، باید به نفع همسرانشان وصیت کنند که [وارثان] تا یک سال آنان را [با پرداختن هزینۀ زندگی] بهره‌مند سازند، بدون آنکه [آنان را از خانۀ شوهر] بیرون کنند. اما اگر [به میل خود، پیش از پایان یک سال، خانه را ترک کنند و] بیرون بروند، دربارۀ آنچه که نسبت به [ظاهر و زندگی] خود به طور شایسته [و مشروع] انجام می‌دهند [از قبیل زینت‌کردن و ازدواج مجدد] گناهی بر شما نیست؛ و الله شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(2,241,'2:241','و برای زنان مطلّقه، هدیه‌ای مناسب و شایسته لازم است [که از طرف شوهر پرداخت شود تا دلشکستگی‌اش جبران گردد. اجرای این حکم،] حقی است بر مردان پرهیزگار.');
INSERT INTO "islamhouse" VALUES(2,242,'2:242','[ای مؤمنان،] الله اینچنین آیات خویش را [که مشتمل بر حدود و احکام است] به روشنی برایتان بیان می‌کند؛ باشد که بیندیشید.');
INSERT INTO "islamhouse" VALUES(2,243,'2:243','[ای پیامبر،] آیا نمی‌دانی [داستانِ] افرادی [از بنی‌اسرائیل] را که هزاران نفر بودند و از ترسِ [بیماری و] مرگ از خانه‌هایشان فرار کردند؟ آنگاه الله به آنان فرمود: بمیرید؛ [پس مُردند.] سپس الله آنها را زنده کرد [تا برایشان روشن گرداند که تمام کارها به دست اوست]. به راستی که الله نسبت به بندگانش صاحب بخشایش است؛ ولی بیشتر مردم شُکر [نعمت‌هایش را] به جای نمی‌آورند.');
INSERT INTO "islamhouse" VALUES(2,244,'2:244','و [ای مؤمنان،] در راه الله بجنگید و بدانید که بی‌تردید، الله شنوای داناست.');
INSERT INTO "islamhouse" VALUES(2,245,'2:245','کیست که به الله قرض دهد؛ قرضی نیکو [و از اموالش در راه خشنودیِ پروردگار انفاق کند] تا [الله] آن را برایش چندین برابر کند؟ و الله است که [از روی حکمت و عدالت، در روزیِ بندگانش] تنگی و گشایش پدید می‌آورد؛ و [همگی در آخرت] به سوی او بازگردانده می‌شوید.');
INSERT INTO "islamhouse" VALUES(2,246,'2:246','[ای پیامبر،] آیا [از داستان] گروهی از [اشراف و بزرگان] بنی‌اسرائیل پس از موسی خبر داری ‌که به پیامبر خود گفتند: «فرمانروایی برایمان انتخاب کن تا [به دستورش] در راه الله بجنگیم»؟ [پیامبرشان] گفت: «شاید اگر جهاد بر شما مقرر شود، [سرپیچی کنید و] جهاد نکنید»؛ گفتند: «چرا در راه الله جهاد نکنیم، در حالی که از خانه‌هایمان رانده شده‌ایم و پسرانمان [را به اسارت برده‌ا‌ند]؟» اما هنگامی ‌که جنگ بر آنان مقرر شد ـ جز عدۀ کمی از آنان ـ [همگی] سرپیچی کردند؛ و الله به [حالِ پیمان‌شکنان و] ستمکاران داناست.');
INSERT INTO "islamhouse" VALUES(2,247,'2:247','پیامبرشان به آنان گفت: «الله طالوت را به فروانرواییِ شما برگزیده است». [آنان] گفتند: «چگونه او بر ما فرمانروایی کند در حالی که ما به فرمانروایی از او سزاوارتریم و از مال [دنیا نیز] بهرۀ چندانی [به وی] داده نشده است؟» [پیامبرشان] گفت: «به راستی که الله او را بر شما برگزیده و دانش و نیروى جسمى‌اش را فزونی بخشیده است؛ و الله فرمانرواییِ خود را به هر ‌کس بخواهد می‌دهد و الله گشایشگرِ داناست».');
INSERT INTO "islamhouse" VALUES(2,248,'2:248','پیامبرشان به آنها گفت: «نشانۀ [راستی و درستیِ] فرمانرواییِ او، این است که صندوق [عهد] به شما مى‌رسد که مایۀ آرامشی از سوی پروردگارتان است و بازماندۀ میراث خاندان ‌موسی و ‌هارون [همچون عصا و برخی الواح تورات] در آن قرار دارد و در حالى [مى‌رسد] که فرشتگان آن را حمل می‌کنند. اگر مؤمن باشید، مسلماً در این [امر،] نشانه‌ای [در مورد حقانیتِ طالوت] برایتان وجود دارد».');
INSERT INTO "islamhouse" VALUES(2,249,'2:249','پس هنگامی که طالوت با لشکریان رهسپار شد، به آنان گفت: «الله [شکیباییِ] شما را به وسیلۀ یک نهر [آب] آزمایش می‌کند؛ هر کس از آن بنوشد، از [یاران] من نیست و هر‌ کس از آن ننوشد، قطعاً از [یاران] من است؛ مگر كسى كه با دست خود مُشتی [از آن آب] برگیرد [و به اندازۀ كف دستى بیاشامد]». پس جز عدۀ کمی، همگی از آن [آب] نوشیدند؛ و هنگامی که او و کسانی که با او ایمان آورده بودند از آن [نهر] گذشتند [از کمیِ افراد خود ناراحت شدند و برخی] گفتند: «امروز ما توانایی [مقابله] با جالوت و سپاهیانش را نداریم». [در این هنگام،] کسانی که به دیدار الله [در آخرت] یقین داشتند گفتند: «چه بسا گروه کوچکی که به فرمان الله بر گروهی بسیار پیروز شدند و [بدانید که همواره] الله همراه شکیبایان است».');
INSERT INTO "islamhouse" VALUES(2,250,'2:250','و هنگامی ‌که [طالوت و همراهانش] برای [مبارزه] با جالوت و سپاهیانش به میدان آمدند، گفتند: «پروردگارا، بر [دل‌های] ما صبر و شکیبایی فرو ریز و گام‌هایمان را استوار بدار و ما را بر گروه کافران پیروز گردان».');
INSERT INTO "islamhouse" VALUES(2,251,'2:251','پس به فرمان الله، آنها را شکست دادند و داود [= جوان نیرومند و شجاعی که در لشکر طالوت بود] جالوت را کشت و الله فرمانروایی و نبوت را به او بخشید و از آنچه می‌خواست به او آموخت؛ و اگر الله [فتنۀ] برخى از مردم را توسط برخى دیگر دفع نمی‌کرد، قطعاً زمین تباه می‌گردید؛ ولی الله نسبت به جهانیان فضل [و بخشش] دارد.');
INSERT INTO "islamhouse" VALUES(2,252,'2:252','[ای پیامبر،] اینها، آیات الله است که آن را به‌حق بر تو می‌خوانیم و به راستی، تو از پیامبران هستی.');
INSERT INTO "islamhouse" VALUES(2,253,'2:253','این پیامبران [که نام برخی از آنان گذشت] برخی از آنان را [در دریافت وحی و تعداد پیروان] بر برخی دیگر برتری دادیم. از [میان] اینان کسی [مانند موسی] بود که الله با او سخن گفت و درجات برخی [دیگر] از آنان را بالا برد؛ و به عیسی - پسر مریم‌ـ نشانه‌های روشنی [بر اثبات نبوتش] دادیم و او را توسط روح ‌القُدُس [= جبرئیل] تأیید [و تقویت] کردیم؛ و اگر الله می‌خواست، کسانی‌ که بعد از آنها آمدند پس از آن [همه] نشانه‌های روشنی که برایشان آمد، با یکدیگر نمی‌جنگیدند؛ ولی [با هم] اختلاف کردند و برخی از آنان ایمان آوردند و برخی کفر ورزیدند؛ و اگر الله می‌خواست، [هرگز] با هم نمی‌جنگیدند؛ ولی الله آنچه را می‌خواهد، انجام می‌دهد.');
INSERT INTO "islamhouse" VALUES(2,254,'2:254','ای کسانی ‌که ایمان آورده‌اید، از آنچه به شما روزی داده‌ایم انفاق کنید، پیش از آنکه روزی فرارسد که در آن نه خرید و فروشی است و نه دوستی و شفاعتی؛ و کافران [به سببِ کفرشان] همان ستمکارانند.');
INSERT INTO "islamhouse" VALUES(2,255,'2:255','الله [معبودِ راستین است؛] هیچ معبودی [به‌حق] جز او نیست؛ زندۀ پاینده [و قائم به ذات] است؛ نه خوابی سبک او را فرا می‌گیرد و نه خوابی سنگین؛ [و لحظه‌ای از تدبیر جهان هستی، غافل نمی‌ماند]؛ آنچه در آسمان‌ها و زمین است، از آنِ اوست. کیست که نزد او جز به فرمانش شفاعت کند؟ گذشته و آیندۀ آنان [= بندگان] را می‌داند و [آنان] به چیزی از علم او احاطه [و آگاهی] نمی‌یابند، مگر آنچه خود [الله] بخواهد. کرسیِ او آسمان‌ها و زمین را در بر گرفته است و نگهداشتنِ آنها بر او [سنگین و] دشوار نیست و او بلندمرتبه [و] بزرگ است.');
INSERT INTO "islamhouse" VALUES(2,256,'2:256','در [پذیرش] دین، هیچ اجباری نیست؛ به راستی که راه راست [و هدایت] از راه انحراف [و گمراهی] روشن شده است؛ پس هر ‌کس به طاغوت [= هر آنچه به جای الله عبادت می‌شود] کفر بورزد و به الله ایمان بیاورد، قطعاً به محكم‌ترین دستاویز چنگ زده است كه هرگز نخواهد گسست؛ و الله شنواى داناست.');
INSERT INTO "islamhouse" VALUES(2,257,'2:257','الله یاور [و کارسازِ] کسانی است که ایمان آورده‌اند؛ آنان را از تاریکی‌ها به سوی نور بیرون می‌بَرد؛ و کسانی ‌که کافر شدند، [کارساز و] یاورشان طاغوت است [که] آنان را از نور [ایمان و علم] به سوی تاریکی‌ها[ی کفر و نادانی] بیرون می‌بَرد. آنان اهل آتش هستند [و] در آن جاودانند.');
INSERT INTO "islamhouse" VALUES(2,258,'2:258','[ای پیامبر،] آیا [داستان] آن شخص [= نمرود] را ندانسته‌ای که از [سرمستیِ] آنکه الله به او فرمانروایی بخشیده بود با ابراهیم دربارۀ پروردگارش مجادله می‌کرد؟ هنگامی که ابراهیم گفت: «پروردگار من ذاتی است که زنده می‌کند و می‌میرانَد». [نمرود] گفت: «من [نیز] زنده می‌کنم و می‌میرانم». ابراهیم گفت: «الله خورشید را از مشرق [بیرون] می‌آورَد؛ پس تو آن را از مغرب بیاور». [آنگاه] آن کس که کفر ورزیده بود، مبهوت شد [و از ارائه دلیل ناتوان ماند]؛ و الله ستمکاران را هدایت نمی‌کند.');
INSERT INTO "islamhouse" VALUES(2,259,'2:259','یا همچون آن کس که از شهری [ویران و متروک] که سقف‌ها و دیوارهایش فروریخته بود گذشت، [و با خود‌] گفت: «چگونه الله [اهالی‌] این [دیار] را پس از مرگشان زنده می‌کند؟» پس الله او را [به مدت‌] صد سال میراند سپس او را زنده کرد [و به او] فرمود: «چه مدت [در این حال‌] مانده‌ای؟» گفت: «یک روز یا بخشی از یک روز [در این حال‌] مانده‌ام». فرمود: «چنین نیست؛ صد سال [در چنین حالی‌] مانده‌ای. به خوردنی و نوشیدنی‌ات بنگر که [رنگ و طعم آن] با گذشت زمان دگرگون نشده است و به اُلاغِ خود بنگر [که چگونه متلاشی شده است. ما چنین کردیم تا به سؤالت پاسخ گوییم] و تو را برای مردم نشانه‌ای [در مورد معاد] قرار دهیم. [اکنون] به استخوان‌های آن [مرکب سواری خود] نگاه کن که چگونه آنها را برداشته و به هم پیوند می‌دهیم و سپس گوشت بر آن می‌پوشانیم». پس هنگامی ‌که [قدرت الهی بر زنده کردن مردگان] برایش آشکار شد، گفت: «[به یقین]می‌دانم که الله بر هر کاری تواناست».');
INSERT INTO "islamhouse" VALUES(2,260,'2:260','و [ای پیامبر، یاد کن از] هنگامی که ابراهیم گفت: «پروردگارا، به من نشان بده چگونه مردگان را زنده می‌کنی؟» [الله] فرمود: «مگر ایمان نیاورده‌ای؟» گفت: «بله؛ ولی [می‌خواهم] قلبم آرام بگیرد». فرمود: «پس چهار پرنده بگیر [و آنها را ذبح‌] و ریزه‌ریزه كن [و درهم بیامیز]، سپس بر هر كوهى پاره‌اى از آنها را بگذار [و] آنگاه آنها را [به سوی خود] فراخوان؛ [خواهی دید که زنده می‌شوند و] شتابان به سوی تو می‌آیند؛ و [ای ابراهیم،] بدان که الله شکست‌ناپذیرِ حکیم است».');
INSERT INTO "islamhouse" VALUES(2,261,'2:261','مَثَل [اجر و ثواب] کسانی‌ که اموال خود را در راه الله انفاق می‌کنند، همانند دانه‌ای است که هفت خوشه می‌رویانَد که در هر خوشه یکصد دانه است؛ و الله برای هر ‌کس بخواهد [پاداش او را] چند برابر می‌کند و الله گشایشگرِ داناست.');
INSERT INTO "islamhouse" VALUES(2,262,'2:262','کسانی که اموالشان را در راه الله می‌بخشند و در پیِ بخشش خود، منّت و آزار روا نمی‌دارند، پاداش آنان نزد پروردگارشان [محفوظ] است؛ نه ترسی خواهند داشت و نه اندوهگین می‌شوند.');
INSERT INTO "islamhouse" VALUES(2,263,'2:263','سخنی پسندیده [در برخورد با نیازمندان] و گذشت [از اصرار و تندیِ آنان]، بهتر از صدقه‌ای است که آزاری در پی دارد؛ و الله بی‌نیازِ بردبار است.');
INSERT INTO "islamhouse" VALUES(2,264,'2:264','ای کسانی که ایمان آورده‌اید، صدقه‌های خود را با منت و آزار باطل نکنید همچون کسی که مال خود را برای نمایش دادن به مردم انفاق می‌کند و به الله و روز قیامت ایمان ندارد؛ پس مَثَل او همچون مَثل سنگ صافی است که بر روی آن [اندکی] خاک نشسته است؛ آنگاه بارانِ تندی بر آن می‌زند [و همۀ خاک‌ها را از سنگ می‌شوید] و آن را صاف [و بدون خاک] رها می‌کند. آنان [= ریاکاران] از آنچه که انجام داده‌اند، چیزی به دست نمی‌آورند؛ و الله گروه کافران را هدایت نمی‌کند.');
INSERT INTO "islamhouse" VALUES(2,265,'2:265','و مَثَل کسانی‌ که اموال خود را در طلب رضایت الله و به خاطر باوری که در دل‌هایشان [به پاداشِ الله] دارند انفاق می‌کنند، همچون باغی بر بالای تپه‌اى است كه باران فراوانی بر آن ببارد و حاصلی دو چندان دهد؛ و اگر باران فراوانی هم به آن نرسد، بارانی سبک [نیز برایش کافی است و زمین پاکش را پرثمر می‌سازد]؛ و الله از آنچه مى‌كنید آگاه است.');
INSERT INTO "islamhouse" VALUES(2,266,'2:266','آیا کسی از شما دوست دارد که باغی از درختان خرما و انگور داشته باشد که از زیر آنها جویبارها[ی گوارا] روان است و هر گونه میوه برایش به بار می‌آورَد و[لی] در حالی که به سن پیری رسیده و فرزندانی [خردسال] و ناتوان دارد، ناگهان گرد‌بادی آتشین در آن زنَد و [همۀ باغ] بسوزد؟ [حالِ کسانی‌ که صدقه‌های خود را با منت و آزار و ریا باطل می‌کنند نیز چنین است]. الله بدین گونه آیات [خود] را برای شما روشن می‌سازد؛ باشد که بیندیشید.');
INSERT INTO "islamhouse" VALUES(2,267,'2:267','ای کسانی ‌که ایمان آورده‌اید، از چیزهای پاکیزه‌ای که به دست آورده‌اید و از آنچه از زمین برای شما برآورده‌ایم، انفاق کنید؛ و به سراغ [مالِ] ناپاک نروید که از آن انفاق نمایید؛ در حالی که خودتان حاضر به پذیرفتنش نیستید، مگر با چشم‌پوشى [و بی‌میلی] نسبت به آن؛ [پس چگونه چیزی را که برای خود نمی‌پسندید، برای الله روا می‌دارید؟] و بدانید كه الله بى‌نیاز [و در ذات و صفات و افعالش] ستوده است.');
INSERT INTO "islamhouse" VALUES(2,268,'2:268','شیطان، شما را [به هنگام انفاق می‌ترساند و] وعدۀ فقر [و تنگدستی] می‌دهد و شما را به زشتی‌ها [= ارتکاب گناه] امر می‌کند؛ ولی الله از جانب خود به شما وعدۀ آمرزش و افزونی [نعمت] می‌دهد و الله گشایشگرِ داناست.');
INSERT INTO "islamhouse" VALUES(2,269,'2:269','[الله] به هر ‌کس بخواهد حکمت [= راستی در گفتار و کردار] می‌دهد و بی‌تردید، به هر ‌کس حکمت داده شود، يقيناً خیرِ فراوانی [به او] عطا شده است؛ و جز خردمندان، کسی [از آیات الهی] پند نمی‌گیرد.');
INSERT INTO "islamhouse" VALUES(2,270,'2:270','هر نفقه‌ای [کم یا زیاد] انفاق کنید یا هر نذری که [با خود] عهد کنید، قطعاً الله آن را می‌داند. [هر ‌کس از اجرای اوامر الهی جلوگیری کند، ستمکار است] و ستمکاران [در آخرت،] هیچ یاوری ندارند.');
INSERT INTO "islamhouse" VALUES(2,271,'2:271','اگر صدقه‌ها را آشکار کنید، چه نیکوست! و اگر آنها را پنهان دارید و به نیازمندان بدهید، برایتان بهتر است و [این گونه صدقۀ خالصانه و پنهانی، بخشی] از گناهانتان را می‌زداید؛ و [بدانید که در هر حال،] الله به آنچه می‌کنید آگاه است.');
INSERT INTO "islamhouse" VALUES(2,272,'2:272','[ای پیامبر،] هدایتِ آنان بر عهدۀ تو نیست؛ بلکه الله هر ‌کس را بخواهد هدایت می‌کند؛ و [ای مؤمنان،] آنچه از مال [خود] انفاق می‌کنید، به سودِ خودتان است و [به یاد داشته باشید که] جز برای رضایت الله انفاق نکنید؛ و آنچه از مال انفاق می‌کنید، [پاداش آن] به طور کامل و تمام به شما داده می‌شود و [هرگز] به شما ستم نخواهد شد.');
INSERT INTO "islamhouse" VALUES(2,273,'2:273','[صدقات شما] برای افراد تنگدستی است که خود را وقف [جهاد] در راه الله کرده‌اند و نمی‌توانند [برای تأمین هزینه‌های زندگی] در زمین سفر کنند. از شدت [نجابت و] خویشتنداری، افرادِ بی‌اطلاع [از حالشان]، آنان را بی‌نیاز می‌پندارند. اینان را از نشانه‌هایشان [که در لباس و وضعیت ظاهری‌شان آشکار است، به وضوح] می‌شناسی [و آنان هرگز] با اصرار [چیزی] از مردم نمی‌خواهند؛ و آنچه از مال انفاق می‌کنید، قطعاً الله از آن آگاه است.');
INSERT INTO "islamhouse" VALUES(2,274,'2:274','کسانی ‌که اموال خود را شب و روز، پنهان و آشکار انفاق می‌کنند، پاداش آنان نزد پروردگارشان [محفوظ] است؛ نه ترسی خواهند داشت و نه اندوهگین می‌شوند.');
INSERT INTO "islamhouse" VALUES(2,275,'2:275','کسانی که ربا می‌خورند [روز قیامت از قبر] برنمی‌خیزند؛ مگر مانند برخاستن کسی‌ که بر اثر تماس شیطان، دیوانه شده است [و نمی‌تواند تعادل خود را حفظ کند]. این، به خاطر آن است که آنان [= رباخواران] گفتند: «داد و ستد نیز مانند رباست [و هر دو، مال را افزون می‌کند]»؛ در حالی که الله، داد و ستد را حلال و ربا را حرام نموده است. پس هر کس که از [جانب] پروردگارش پندی به او برسد و [از رباخواری] دست بردارد، آنچه گذشته و [سودهایی که قبل از نزولِ حکمِ ربا دریافت کرده است] از آنِ اوست و [در مورد آینده نیز] کارش به الله واگذار می‌شود؛ و[لی] کسانی که [به رباخواری] بازگردند، اهل آتش [دوزخ] هستند و در آن جاودانند [= مدت طولانی در آن خواهند بود].');
INSERT INTO "islamhouse" VALUES(2,276,'2:276','الله ربا را نابود [و بی‌برکت] می‌گردانَد و صدقات را [با افزودن بر ثوابش] افزایش و [برکت] می‌دهد؛ و الله هیچ کافرِ لجوج و گناهکاری را دوست ندارد.');
INSERT INTO "islamhouse" VALUES(2,277,'2:277','کسانی ‌که ایمان آورده‌اند‌ و کارهای شایسته انجام داده‌اند و نماز برپا داشته‌اند و زکات پرداخته‌اند، پاداش آنان نزد پروردگارشان [محفوظ] است؛ نه ترسی خواهند داشت و نه اندوهگین می‌شوند.');
INSERT INTO "islamhouse" VALUES(2,278,'2:278','ای کسانی ‌که ایمان آورده‌اید، از الله پروا کنید و اگر مؤمن هستید، آنچه را که از [مطالبات] ربا باقی مانده است رها کنید.');
INSERT INTO "islamhouse" VALUES(2,279,'2:279','اگر [چنین] نکردید، بدانید که الله و رسولش با شما پیکار خواهند کرد؛ و اگر توبه کردید، اصلِ سرمایه‌هایتان از آنِ شماست؛ نه [با گرفتنِ سودِ سرمایه‌تان به کسی] ستم می‌کنید و نه [با چشم‌پوشی از آن سودِ حرام] بر شما ستم می‌رود.');
INSERT INTO "islamhouse" VALUES(2,280,'2:280','و اگر [شخصِ بدهکار،] تنگدست بود، تا [هنگام] گشایش [و تواناییِ بازپرداخت، به او] مهلت دهید و [اگر توانایی پرداخت نداشت،] بخشیدن آن [بدهی] برایتان بهتر است، اگر [ثواب این کار را] بدانید.');
INSERT INTO "islamhouse" VALUES(2,281,'2:281','و از [عذابِ] روزی بترسید که در آن به سوی الله بازگردانده می‌شوید؛ سپس به هر ‌کس [پاداش] آنچه انجام داده است به طور کامل داده می‌شود و به آنان ستم نخواهد شد.');
INSERT INTO "islamhouse" VALUES(2,282,'2:282','ای کسانی که ایمان آورده‌اید، اگر به یکدیگر وامی [پرداختید یا معاملۀ نسیه‌ای انجام] دادید که سررسیدِ معیّنی داشت، آن را بنویسید؛ و باید نویسنده‌ای [قراردادِ این کار را] بر اساسِ عدالت بین شما بنویسد و هیچ نویسنده‌ای نباید از نوشتنش خودداری کند، همان‌گونه [و به شکرانۀ آن] که الله به او آموزش داده است؛ و کسی که وام بر عهدۀ اوست [= بدهکار] باید املا [و اقرار] کند و او [= نویسنده] بنویسد؛ و باید از الله، که پروردگارِ اوست، بترسد و از [مبلغ و مدتِ] آن [وام] چیزی نکاهد. اگر کسی که وام بر عهدۀ اوست سَفیه [=کم‌خرد]، یا [به سبب خردسالی یا بیماری یا پیری] ناتوان است، یا خود نمی‌تواند املا کند، باید قَیِّمِ او با [رعایت] عدالت املا کند و دو شاهد از مردانتان را [بر آن قرارداد] گواه بگیرید. اگر دو مرد نبودند، یک مرد و دو زن از میان گواهانی که [به عدالت آنان] رضایت دارید [گواه بگیرید] تا اگر یکی از آن دو [زن] فراموش کرد، دیگری به او یادآوری کند؛ و هنگامی ‌که شاهدان [برای ادای شهادت] دعوت شدند، نباید [از آمدن] خودداری کنند. و از نوشتن [بدهی] تا سررسیدش [سستی نکنید و] دلگیر نشوید خواه [مبلغ آن] کم باشد خواه زیاد. این [کار،] در نزد الله به عدالت نزدیک‌تر و برای گواهی‌دادن، استوارتر است و برای آنکه [در مقدار و مدتِ وام] دچار تردید نشوید، [به احتیاط] نزدیک‌تر است؛ مگر اینکه داد و ستدی نقدی باشد که بین خود دست به دست می‌کنید. در این صورت، گناهی بر شما نیست که آن را ننویسید؛ و هنگامی که داد و ستد [نقدی] کردید، گواه بگیرید و [در این میان] نباید به نویسنده و گواه [هیچ] رنج و زیانی برسد؛ و اگر [چنین] کنید بی‌شک [بیانگر] نافرمانیِ شما خواهد بود؛ و [ای مؤمنان،] از الله پروا کنید [و معاملات را به همان شکلی که امر می‌کند انجام دهید]. الله [آنچه را که برای دنیا و آخرتتان سودمند است] به شما آموزش می‌دهد و الله به همه چیز داناست.');
INSERT INTO "islamhouse" VALUES(2,283,'2:283','اگر در سفر بودید و نویسنده‌ای نیافتید [که سندِ وام را بنویسد، چیزی] گرو بگیرید و اگر یکی از شما دیگری را امین دانست [و چیزی گرو نگرفت و نوشته و گواه نیز نداشت]، آن که امین شمرده شده است، باید امانت [و بدهی] خود را بازپس دهد و باید از الله، که پروردگار اوست، پروا کند؛ و شهادت را کتمان نکنید و [بدانید که] هر ‌کس آن را کتمان کند، بی‌شک، قلبش گناهکار است؛ و الله به آنچه می‌کنید، داناست.');
INSERT INTO "islamhouse" VALUES(2,284,'2:284','آنچه در آسمان‌ها و زمین است، از آنِ الله است. اگر آنچه را در دل خود دارید، آشکار کنید یا پنهانش نمایید، الله شما را بر [اساسِ] آن محاسبه می‌کند؛ آنگاه [بر اساس بخشش و رحمت خویش] هر‌ کس را بخواهد می‌بخشد و [از روی عدالت و حکمتش نیز] هر کس را بخواهد عذاب می‌کند؛ و الله بر [انجام] هر کاری تواناست.');
INSERT INTO "islamhouse" VALUES(2,285,'2:285','رسول [الله] به آنچه از [سوی] پروردگارش بر او نازل شده ایمان آورده است و مؤمنان [نیز] همگی به الله و فرشتگان و کتاب‌ها و پیامبرانش ایمان آورده‌اند؛ [و رسول و مؤمنان گفتند]: «میان هیچ‌یک از پیامبرانش فرق نمی‌گذاریم [و به همه شان ایمان داریم]. و گفتند: «شنیدیم و اطاعت کردیم. پروردگارا، آمرزش تو را [خواهانیم] و بازگشت [تمام امور] به سوی توست».');
INSERT INTO "islamhouse" VALUES(2,286,'2:286','الله هیچ‌ کس را جز به اندازۀ توانش مکلّف نمی‌کند. آنچه [از خوبی] به دست آورده است به سودِ اوست و آنچه [بدی] کسب کرده است به زیان اوست. [پیامبران و مؤمنان گفتند:] «پروردگارا، اگر فراموش یا خطا کردیم، ما را بازخواست نکن. پروردگارا، بارِ گران [و تکلیف سنگین] بر [دوش] ما مگذار؛ چنان که آن را [به مجازات گناه و سرکشی،] بر [دوش] کسانی که پیش از ما بودند [= یهوديان] نهادی. پروردگارا، آنچه که تاب تحملش را نداریم بر [دوش] ما مگذار و از ما درگذر و ما را بیامرز و به ما رحم کن. تو یار [و کارسازِ] مایی؛ پس ما را بر گروه کافران پیروز گردان».');
INSERT INTO "islamhouse" VALUES(3,1,'3:1','الم [= الف لام میم].');
INSERT INTO "islamhouse" VALUES(3,2,'3:2','الله [معبودِ راستین است؛] هیچ معبودی [به‌حق] جز او نیست؛ زندۀ پاینده [و قائم به ذات] است.');
INSERT INTO "islamhouse" VALUES(3,3,'3:3','این کتاب [= قرآن] را که تأییدکنندۀ کتاب‌های [آسمانی] پیشین است، به حق و راستی بر تو نازل کرد و تورات و انجیل را');
INSERT INTO "islamhouse" VALUES(3,4,'3:4','پیش از آن برای هدایت مردم نازل کرد؛ و [نیز] فرقان را [که جداکنندۀ حق از باطل است] نازل نمود. کسانی‌ که به آیات الله کافر شدند، عذاب سختی [در پیش] دارند؛ و الله شکست‌ناپذیر [و] انتقام‌گیرنده است.');
INSERT INTO "islamhouse" VALUES(3,5,'3:5','بی‌تردید، هیچ چیز ـ [نه] در زمین و نه در آسمان ‌ـ از الله پوشیده نمی‌مانَد.');
INSERT INTO "islamhouse" VALUES(3,6,'3:6','اوست که شما را هر گونه که بخواهد در رحِم‌ها شکل می‌دهد. هیچ معبودی [به‌حق] جز آن ذاتِ شکست‌ناپذیرِ حکیم نیست.');
INSERT INTO "islamhouse" VALUES(3,7,'3:7','[ای پیامبر،] اوست که کتاب [= قرآن] را بر تو نازل کرد. بخشی از آن، آیات محکم [صریح و روشن] است که آنها اساسِ کتابند و [بخشی] دیگر متشابهات است [که تأویل‌پذیرند]؛ اما کسانی ‌که در دل‌هایشان انحراف است، برای فتنه‌جویی [و گمراه کردن مردم] و به خاطرِ تأویل آن [به دلخواه خود،] از متشابهِ آن پیروی می‌کنند؛ حال آنکه تأویلش را [کسی] جز الله نمی‌داند؛ و راسخان در علم [= علمای دین] می‌گویند: «ما به همۀ آن [آیات ـ چه محکم و چه متشابه‌ـ] ایمان آوردیم؛ همه از طرف پروردگارِ ماست»؛ و جز خردمندان کسی پند نمی‌گیرد.');
INSERT INTO "islamhouse" VALUES(3,8,'3:8','آنان می‌گویند: «پروردگارا، بعد از آنکه ما را هدایت فرمودی، دل‌هایمان را [از راه حق] منحرف مگردان و از سوی خود، رحمتی به ما ارزانی دار [که] بی‌تردید، تو بسیار بخشنده‌ای.');
INSERT INTO "islamhouse" VALUES(3,9,'3:9','پروردگارا، بی‌شک، تو مردم را برای روزی ‌که در [آمدنِ] آن تردیدی نیست جمع خواهی کرد. قطعاً الله در وعدۀ [خود] خلاف نمی‌کند».');
INSERT INTO "islamhouse" VALUES(3,10,'3:10','در حقیقت، کسانی‌ که کفر ورزیدند، اموال و فرزندانشان چیزی از [عذاب] الله را از آنان دور نخواهد کرد و آنان خود هیزم آتش [دوزخ] هستند.');
INSERT INTO "islamhouse" VALUES(3,11,'3:11','[شیوۀ این کافران] همچون شیوۀ خاندان ‌فرعون و [روشِ] کسانی است که پیش از آنها بودند: آیات ما را تکذیب کردند؛ پس الله آنان را به [سزای] گناهانشان عذاب کرد؛ و الله [نسبت به کافران] سخت‌کیفر است.');
INSERT INTO "islamhouse" VALUES(3,12,'3:12','به کسانی که کفر ورزیدند بگو: «به زودی [از مؤمنان] شکست خواهید خورد و [سپس در قیامت] به سوی جهنم محشور می‌شوید؛ و چه بد جایگاهی است!».');
INSERT INTO "islamhouse" VALUES(3,13,'3:13','قطعاً در [رویاروییِ] دو گروهی که [در بدر] با هم روبرو شدند، [عبرت و] نشانه‌ای برای شما بود؛ یک گروه در راه الله می‌جنگید و [گروهی] دیگر کافر بودند. [مؤمنان] با چشم خود، آنان [=کافران] را دوبرابر خود [و کمتر از تعداد واقعی‌شان] می‌دیدند؛ و الله هر کس را بخواهد با یاری خود تأیید می‌کند. به راستی در این [ماجرا] برای اهل بینش، عبرتی است.');
INSERT INTO "islamhouse" VALUES(3,14,'3:14','دلبستگی به خواستنی‌های [مادی،] از [جمله] زنان و فرزندان و اموال هنگفت ـ از طلا و نقره و اسب‌های نشاندار و دام‌ها و کشتزار[ها]ـ برای مردم آراسته شده است. [امّا تمام] این[ها، لذت‌ها و] کالای [زودگذرِ] زندگی دنیاست و [حال آنکه] سرانجام نیک، نزد الله است.');
INSERT INTO "islamhouse" VALUES(3,15,'3:15','[ای پیامبر، به آنان] بگو: «آیا [می‌خواهید] شما را از چیزی آگاه کنم که از این [دارایی‌ها و لذت‌های مادی] بهتر باشد؟» کسانی که تقوا پیشه کرده‌اند، نزد پروردگارشان باغ‌هایی دارند که از زیر [درختان] آن جویبارها روان است؛ جاودانه در آن خواهند بود و همسرانی پاکیزه [و بی‌عیب دارند] و [همچنین از] خشنودی الله [برخوردارند]؛ و الله به [احوالِ] بندگان بیناست.');
INSERT INTO "islamhouse" VALUES(3,16,'3:16','[این بهشتیان] کسانی‌ [هستند] که می‌گویند: «پروردگارا، به راستی که ما ایمان آورده‌ایم؛ پس گناهانمان را بیامرز و ما را از عذاب آتش نگه دار».');
INSERT INTO "islamhouse" VALUES(3,17,'3:17','آنان [در برابر مشکلات و در راه اطاعت و ترک گناه] شکیبایی می‌ورزند و [در گفتار و رفتارشان] راستگو هستند و [در برابر اوامرِ الهی] فرمانبردارند و [برای خشنودی باری تعالی] انفاق می‌کنند و در سحرگاهان آمرزش می‌طلبند.');
INSERT INTO "islamhouse" VALUES(3,18,'3:18','الله، ‌که [در جهان هستی] برپادارندۀ عدل است، گواهی داده است که معبودی [به‌حق] جز او نیست و فرشتگان و علما [نیز بر یگانگی و عدالتِ الهی گواهی می‌دهند]. معبودی [به‌حق] جز او نیست [که] شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(3,19,'3:19','در حقیقت، دین [پسندیده] نزد الله همان اسلام است؛ و كسانى كه كتاب [آسمانى‌] به آنان داده شده است، با یكدیگر به اختلاف نپرداختند مگر پس از آنكه علم برایشان [حاصل‌] آمد [و حجت بر آنان تمام شد]. آن [اختلاف و تفرقه] هم به خاطر سرکشی و حسدى [بود] كه میانشان وجود داشت؛ و هر‌ کس به آیات الله کفر بورزد، [بداند که] الله در حسابرسی سریع است.');
INSERT INTO "islamhouse" VALUES(3,20,'3:20','[ای پیامبر،] اگر [دربارۀ اسلام و توحید] با تو مجادله کردند، بگو: «من در برابر الله [و اوامر و احکامش] تسلیم شده‌ام و پیروانم [نیز چنین هستند]»؛ و به اهل کتاب [= یهود و نصاری] و مشرکان بگو: «آیا شما هم تسلیم شده‌اید؟» اگر تسلیم شوند، قطعاً هدایت یافته‌اند و اگر رویگردان شدند [و سرپیچی کردند، اندوه مدار؛ زیرا] تو وظیفه‌ای جز رساندن [وحی] نداری؛ و الله به [احوال] بندگان بیناست.');
INSERT INTO "islamhouse" VALUES(3,21,'3:21','به راستی، کسانی ‌که به آیات الله کفر می‌ورزند و پیامبران را به ناحق [و فقط از روی ستم و دشمنی] می‌کشند و [نیز] کسانی را که به عدالت امر می‌کنند، به قتل می‌رسانند، آنان را به عذابی دردناک بشارت ده!');
INSERT INTO "islamhouse" VALUES(3,22,'3:22','آنان کسانی هستند که اعمالشان در دنیا و آخرت تباه شده است و [در آخرت] هیچ یاوری ندارند.');
INSERT INTO "islamhouse" VALUES(3,23,'3:23','[ای پیامبر،] آیا داستان كسانى را كه بهره‌اى از كتاب [تورات‌] یافته‌اند ندانسته‌اى كه چون به [بازگشت به] سوى كتاب الله فراخوانده مى‌شوند تا میانشان حكم كند، گروهى از [علما و بزرگان] آنان [که حقیقت را خلاف میل و منفعت خود می‌بینند] به حال سرپیچی روى برمى‌تابند؟');
INSERT INTO "islamhouse" VALUES(3,24,'3:24','این [رویگردانی از حق،] به سبب آن است که [به خیال خام خود] گفتند: «آتش [دوزخ] جز چند روزی به ما نخواهد رسید»؛ و این [افتراها] و دروغ‌هایی که می‌بافتند آنان را در دینشان فریفته ساخت.');
INSERT INTO "islamhouse" VALUES(3,25,'3:25','پس [حال پشیمانی‌شان] چگونه خواهد بود؟ [آری، در بدترین حال خواهد بود] آنگاه که آنان را برای روزی ‌که [هیچ] تردیدی در [وقوع] آن نیست گرد آوریم و به هر ‌کس [پاداش] آنچه که فراهم کرده است به تمام و کمال داده شود و به آنان ستم نخواهد شد.');
INSERT INTO "islamhouse" VALUES(3,26,'3:26','[ای پیامبر،] بگو: «بار الها، ای فرمانروایِ هستی، هر ‌کس را بخواهی حکومت می‌بخشی و از هر ‌کس بخواهی حکومت را بازپس می‌گیری؛ و به هر ‌کس بخواهی عزت [و قدرت] می‌دهی و هر ‌کس را بخواهی خوار می‌داری. [پروردگارا،] خوبی به دست توست و بی‌گمان، تو بر هر چیزی توانایی.');
INSERT INTO "islamhouse" VALUES(3,27,'3:27','شب را به روز در مى‌آورى و روز را به شب در مى‌آورى [و از طولِ یکی می‌کاهی و بر دیگری می‌افزایی] و [موجودِ] زنده را از [مادۀ] بی‌جان بیرون مى‌آورى [= رویشِ گیاه از دانه] و [مادۀ] بی‌جان را از [موجود] زنده خارج مى‌سازى [= بیرون آمدنِ تخم از پرندگان] و به هر ‌کس بخواهی، بی‌شمار روزی می‌دهی».');
INSERT INTO "islamhouse" VALUES(3,28,'3:28','مؤمنان نباید کافران را به جای مؤمنان، دوست خود بگیرند؛ و هر کس چنین کند، هیچ پیوندی با الله ندارد [و الله از او بیزار است]؛ مگر آنکه [ضعیف باشید و ناچار شوید با نرمی در گفتار و رفتار،] خویشتن را از [آزارِ] آنان در امان دارید [و به خاطر حفظ جانتان احتیاط کنید]؛ و الله شما را از [نافرمانی] خویش برحذر می‌دارد؛ و بازگشت [همه] به سوی الله است.');
INSERT INTO "islamhouse" VALUES(3,29,'3:29','[ای پیامبر، به مؤمنان] بگو: «اگر هر آنچه [از دوستی و محبتِ کافران که] در دل‌هایتان است، پنهان دارید یا آشکارش کنید، الله آن را می‌داند [و نیز] آنچه را که در آسمان‌ها و زمین است می‌داند؛ و الله بر هر چیزی تواناست».');
INSERT INTO "islamhouse" VALUES(3,30,'3:30','[روز قیامت،] روزی [است] که هر کس آنچه را از کار نیک [و بد] انجام داده است حاضر می‌بیند و آرزو می‌کند میان او و اعمال بدی که انجام داده است فاصلۀ زیادی باشد؛ و الله شما را از [نافرمانی و کیفر] خود بر حذر می‌دارد؛ و الله نسبت به بندگان [خود] دلسوز است.');
INSERT INTO "islamhouse" VALUES(3,31,'3:31','[ای پیامبر،] بگو: «اگر الله را دوست ‌دارید، از من پیروی کنید تا الله شما را دوست بدارد و گناهانتان را بیامرزد؛ و الله آمرزندۀ مهربان است»');
INSERT INTO "islamhouse" VALUES(3,32,'3:32','[ای پیامبر،] بگو: «از الله و پیامبر [او] اطاعت کنید»؛ پس اگر پشت کردند [و سرپیچی نمودند]، قطعاً الله کافران را دوست ندارد.');
INSERT INTO "islamhouse" VALUES(3,33,'3:33','به راستی که الله، آدم و نوح و خاندان ‌ابراهیم و خاندان ‌عمران را بر جهانیان برگزید.');
INSERT INTO "islamhouse" VALUES(3,34,'3:34','فرزندانی که برخی از آنان، از [نسلِ] برخی دیگرند؛ [توحید، اعمال صالح، و اخلاق والا را از یکدیگر به میراث برده‌اند]؛ و الله شنوای داناست.');
INSERT INTO "islamhouse" VALUES(3,35,'3:35','[ای پیامبر، یاد کن از] هنگامی‌ که همسر عمران گفت: «پروردگارا، من آنچه را در شکم دارم برای تو نذر کردم که آزاد [از همه چیز، در خدمت بیت‌المقدس و عبادت‌کننده تو] باشد؛ پس از من بپذیر که تو خود شنوای دانایی».');
INSERT INTO "islamhouse" VALUES(3,36,'3:36','پس هنگامی‌ که او را به دنیا آورد، گفت: «پروردگارا، من او را دختر زاده‌ام ـ ولی الله به آنچه او به دنیا آورده بود، داناتر بود ـ و پسر همانند دختر نیست، و من او را مریم نام نهادم و او و فرزندانش را از [شرِ] شیطانِ رانده‌شده، در پناه تو قرار مى‌دهم».');
INSERT INTO "islamhouse" VALUES(3,37,'3:37','آنگاه پروردگارش او [= مریم] را به نیکویی پذیرفت و [نهالِ وجودش را] به شیوۀ شایسته‌ای پرورش داد و سرپرستی‌اش را به زکریا سپرد. هر گاه زکریا وارد عبادتگاه [او] می‌شد، نزدش غذایی می‌یافت [پس با شگفتی] می‌گفت: «ای مریم، این [غذا] از کجا برایت آمده است؟» [مریم] می‌گفت: «این [غذا] از جانب الله است. بی‌گمان، الله به هر ‌کس بخواهد، بی‌شمار روزی می‌بخشد».');
INSERT INTO "islamhouse" VALUES(3,38,'3:38','آنجا [بود که] زکریا پروردگارش را خواند [و] گفت: «پروردگارا، از جانب خویش فرزندى پاک و پسندیده به من عطا كن [که] بی‌تردید، تو شنوندۀ دعایی».');
INSERT INTO "islamhouse" VALUES(3,39,'3:39','پس در حالی ‌که او [= زکریا] ایستاده در عبادتگاه [خود] نماز می‌خواند، فرشتگان او را ندا دادند که: «الله تو را به [ولادتِ] یحیی بشارت می‌دهد [که] تصدیق‌کنندۀ کلمۀ الله [= عیسی] است و [در علم و عبادت، بر قوم خویش] سرور است و پارساست [= به شهوات توجهی ندارد] و پیامبری از شایستگان است».');
INSERT INTO "islamhouse" VALUES(3,40,'3:40','[زکریا] گفت: «پروردگارا، چگونه فرزندی خواهم داشت، حال آنکه پیری به سراغم آمده و همسرم [نیز] نازاست؟» [الله] فرمود: «[امر آفرینش] چنین است [که] الله هر چه بخواهد، انجام مى‌دهد».');
INSERT INTO "islamhouse" VALUES(3,41,'3:41','[زکریا] گفت: «پروردگارا، برایم نشانه‌ای قرار بده»؛ [الله] فرمود: «نشانه‌ات این است که سه [شبانه] روز، [بدون اینکه بیمار باشی] جز با اشاره، قادر به سخن گفتن با مردم نیستی [پس صبور باش] و پروردگارت را بسیار یاد کن و شامگاهان و صبحگاهان [او را] تسبیح گوی».');
INSERT INTO "islamhouse" VALUES(3,42,'3:42','و [ای پیامبر، یاد کن از] هنگامی که فرشتگان گفتند: «ای مریم، الله تو را برگزیده و پاک ساخته و تو را [در عصرِ خودت،] بر زنان جهان برتری داده است.');
INSERT INTO "islamhouse" VALUES(3,43,'3:43','ای مریم، [همواره] عبادتگرِ پروردگار خویش باش و [برای او] سجده بگزار و همراه رکوع‌کنندگان رکوع کن».');
INSERT INTO "islamhouse" VALUES(3,44,'3:44','[ای پیامبر،] این [خبر،] از خبرهای غیبی است که به تو وحی می‌کنیم. و تو هنگامی که [علمای مذهبی یهود] قلم‌هایشان را [برای قرعه‌کشی به آب] می‌افکندند تا [تعیین کنند] کدام‌یک سرپرستی مریم را به عهده گیرد، نزدشان نبودی؛ و [نیز] وقتی که [بر سر این موضوع] با یکدیگر کشمکش می‌کردند، کنارشان حضور نداشتی.');
INSERT INTO "islamhouse" VALUES(3,45,'3:45','و [ای پیامبر، یاد کن از] هنگامی که فرشتگان گفتند: «ای مریم، الله تو را به کلمه‌ای از جانب خویش بشارت می‌دهد که نامش مسیح ـ عیسی پسر مریم ـ است؛ در حالی ‌که [او] در دنیا و آخرت آبرومند و از نزدیکان [درگاه الهی] است.');
INSERT INTO "islamhouse" VALUES(3,46,'3:46','او در گهواره [به اعجاز،] و در میانسالی [به وحی الهی‌] با مردم سخن می‌گوید و از شایستگان است».');
INSERT INTO "islamhouse" VALUES(3,47,'3:47','[مریم] گفت: «پروردگارا، چگونه فرزندی خواهم داشت در حالی که [هیچ] بشری به من دست نزده است؟» [فرشته] گفت: «الله بدین‌سان هر چه را بخواهد می‌آفریند [و] هر گاه چیزی را اراده کند، فقط به آن می‌گوید: "باش" آنگاه آن [چیز بی‌درنگ] موجود می‌شود».');
INSERT INTO "islamhouse" VALUES(3,48,'3:48','و [نیز بشارت داد كه الله] به او نوشتن و حکمت و تورات و انجیل می‌آموزد.');
INSERT INTO "islamhouse" VALUES(3,49,'3:49','و پیامبری به سوی بنی‌اسرائیل است که [به آنان می‌گوید:] «من از سوی پروردگارتان برای شما نشانه‌ای آورده‌ام: من از گِل برایتان [چیزی] به شکل پرنده می‌سازم؛ آنگاه در آن می‌دَمم و به فرمان الله [آن گِلِ بی‌جان، تبدیل به] پرنده‌ای می‌شود؛ و [همچنین] به فرمان الله، کورِ مادرزاد و مبتلایان به پیسی را بهبودی می‌بخشم و مردگان را زنده می‌کنم و از آنچه می‌خورید و در خانه‌هایتان ذخیره می‌کنید به شما خبر می‌دهم. مسلماً اگر ایمان داشته باشید، در این [معجزات] نشانه‌ای برای شماست [که نبوّتم را باور کنید].');
INSERT INTO "islamhouse" VALUES(3,50,'3:50','و [مى‌گوید: «آمده‌ام تا] تصدیق‌كنندۀ تورات باشم كه پیش از من [نازل شده‌] است؛ و [فرستاده شده‌ام] تا برخی از چیزهایی را که بر شما حرام شده بود، حلال کنم؛ و نشانه‌ای از طرف پروردگارتان برای شما آورده‌ام؛ پس از الله پروا نمایید و مرا اطاعت کنید.');
INSERT INTO "islamhouse" VALUES(3,51,'3:51','به راستی که الله، پروردگار من و پروردگار شماست؛ پس او را عبادت کنید [که] راه راست این است».');
INSERT INTO "islamhouse" VALUES(3,52,'3:52','سپس چون عیسی به کفر آنان پی برد گفت: «چه کسانی یاوران من [در راه دعوت] به سوی الله هستند؟» حواریون گفتند: «ما یاوران [دینِ] الله هستیم؛ به الله ایمان آورده‌ایم و [تو نیز ای عیسی،] گواه باش که ما تسلیم [او] هستیم.');
INSERT INTO "islamhouse" VALUES(3,53,'3:53','پروردگارا، به آنچه نازل کرده‌ای ایمان آوردیم و از فرستادۀ [تو] پیروی نمودیم؛ پس ما را در زمرۀ گواهان [به حق و مؤمنان به پیامبرت] بنویس».');
INSERT INTO "islamhouse" VALUES(3,54,'3:54','[پس از مدتی کافران بنی‌اسرائیل برای کشتن عیسی] مکر ورزیدند و الله [نیز در پاسخشان] تدبیر نمود و الله بهترینِ تدبیرکنندگان است.');
INSERT INTO "islamhouse" VALUES(3,55,'3:55','[یاد کن از] هنگامی که الله [به عیسی] فرمود: «ای عیسی، من تو را [از زمین] برمی‌گیرم و به سوی خویش بالا می‌برم و تو را از [آلایش و تهمتِ] کسانی ‌که کفر ورزیدند، پاک می‌گردانم و تا روز رستاخیز، کسانی را که از تو پیروی کردند [و به محمد ایمان آوردند]، برتر از کسانی قرار می‌دهم که کفر ورزیدند. سپس بازگشتتان به سوی من است؛ آنگاه در [مورد] آنچه در آن اختلاف می‌کردید، میانتان داوری می‌کنم.');
INSERT INTO "islamhouse" VALUES(3,56,'3:56','اما کسانی را ‌که کفر ورزیدند، در دنیا و آخرت به عذابی سخت مجازات می‌کنم و یاورانی نخواهند داشت.');
INSERT INTO "islamhouse" VALUES(3,57,'3:57','و اما کسانی‌ که ایمان آوردند و کارهای شایسته انجام دادند، [الله] پاداششان را به طور کامل به آنان خواهد داد؛ و الله ستمکاران را دوست ندارد.');
INSERT INTO "islamhouse" VALUES(3,58,'3:58','[ای پیامبر،] اینها که [از ماجرای عیسی] بر تو می‌خوانیم، از نشانه‌ها[ی روشن] است [که بر راستگوییِ تو] و [درستیِ این] اندرز حکمت‌آمیز [= قرآن] دلالت دارد.');
INSERT INTO "islamhouse" VALUES(3,59,'3:59','در حقیقت، مَثَل عیسی نزد الله، همچون مَثل [خلقتِ] آدم است [که] او را از خاک آفرید سپس به او فرمود: «باش»؛ پس [بی‌درنگ] موجود شد.');
INSERT INTO "islamhouse" VALUES(3,60,'3:60','[آنچه دربارۀ عیسی بیان کردیم،] حقیقتی از جانب پروردگار توست؛ پس هرگز از تردید‌کنندگان مباش.');
INSERT INTO "islamhouse" VALUES(3,61,'3:61','پس [ای پیامبر،] هر کس [از نصرانی‌های نَجْران] پس از آن دانشى كه دربارۀ او [= عیسی] برایت [حاصل‌] آمده است با تو بحث و جدل ‌كند، [به آنها] بگو: «بیایید [همگی را] فرابخوانیم: پسران ما و پسران شما و زنان ما و زنان شما و خود ما و شما [نیز حاضر شویم]؛ سپس به [درگاه الهی تضرّع و] دعا كنیم و لعنتِ الله را بر دروغگویان قرار دهیم».');
INSERT INTO "islamhouse" VALUES(3,62,'3:62','این همان داستان حقیقی [مسیح] است، و هیچ معبودی [به‌حق] جز الله نیست و بی‌گمان، الله ذات شکست‌ناپذیر [و] حکیم است.');
INSERT INTO "islamhouse" VALUES(3,63,'3:63','پس اگر [از سخن حق] روی گردانند، [بدان که] الله از [حال] مفسدان آگاه است.');
INSERT INTO "islamhouse" VALUES(3,64,'3:64','[ای پیامبر،] بگو: «ای اهل کتاب، به سوی سخنی بیایید که میان ما و شما عادلانه است [و انحرافی از حق در آن نیست:] که جز الله را عبادت نکنیم و چیزی را [در قدرت و تدبیر] با او شریک نسازیم و برخی از ما برخی دیگر را به جای الله، معبود نگیرند». اگر آنان [از این دعوت] روی گرداندند، پس [ای مؤمنان، به آنان] بگویید: «گواه باشید كه ما تسلیم [اوامر الهی] هستیم».');
INSERT INTO "islamhouse" VALUES(3,65,'3:65','ای اهل کتاب، چرا دربارۀ ابراهیم مجادله می‌کنید؛ در حالی که تورات و انجیل بعد از او نازل شده است؟ آیا نمی‌اندیشید؟');
INSERT INTO "islamhouse" VALUES(3,66,'3:66','هان! شما [اهل کتاب] کسانی هستید که دربارۀ آنچه نسبت به آن آگاهی و علم داشتید، بحث و ستیز می‌کردید؛ پس چرا [اینک] در مورد چیزی که به آن علم ندارید [و در کتاب‌هایتان نیامده است] بحث و ستیز می‌کنید؟ در حالی که الله [حقایق امور را] می‌داند و شما نمی‌دانید.');
INSERT INTO "islamhouse" VALUES(3,67,'3:67','ابراهیم نه یهودی بود و نه نصرانی؛ بلکه حق‌گرایی مسلمان بود و هرگز از زمرۀ مشرکان نبود.');
INSERT INTO "islamhouse" VALUES(3,68,'3:68','به راستی، سزاوارترین مردم به ابراهیم همان کسانی هستند که از او پیروی کرده‌اند؛ و [از جمله] این پیامبر [شما محمد] و کسانی‌ که [مسلمانند و به او] ایمان آورده‌اند؛ و الله [یار و] کارسازِ مؤمنان است.');
INSERT INTO "islamhouse" VALUES(3,69,'3:69','[ای مؤمنان،] گروهی از اهل کتاب آرزو می‌کردند کاش شما را گمراه می‌کردند؛ در حالی‌ که جز خودشان [کسی] را گمراه نمی‌کنند و[لی] نمی‌فهمند.');
INSERT INTO "islamhouse" VALUES(3,70,'3:70','ای اهل کتاب، چرا به آیات الله کافر می‌شوید، در حالی که خود [به درستیِ آن] گواهی می‌دهید؟');
INSERT INTO "islamhouse" VALUES(3,71,'3:71','ای اهل کتاب، چرا حق را به باطل می‌آمیزید و حقیقت [= پیامبریِ محمد] را پنهان می‌کنید در حالی که خود می‌دانید؟');
INSERT INTO "islamhouse" VALUES(3,72,'3:72','گروهی از [علمای] اهل کتاب [به یکدیگر] گفتند: «در آغاز روز به آنچه بر مؤمنان نازل شده است ایمان بیاورید و در پایان [روز] کافر شوید؛ شاید آنان [نیز تردید کنند و از اسلام] برگردند.');
INSERT INTO "islamhouse" VALUES(3,73,'3:73','و [اینکه] ایمان نیاورید مگر به کسی‌ که از دین شما پیروی می‌کند». [ای پیامبر، به آنان] بگو: «در حقیقت، هدایت [راستین]، هدایتِ الله است. [دوباره گفتند: «هرگز چیزی به مسلمانان نیاموزید و اجازه ندهید] به کسی نظیر آنچه به شما داده شده است، داده شود یا [دلیلی در اختیارشان نگذارید که بتوانند] در پیشگاه پروردگارتان با شما بحث کنند». [ای پیامبر،] بگو: «بی‌تردید، فضل [و برتری] به دست الله است؛ آن را به هر ‌کس بخواهد می‌بخشد؛ و الله گشایشگرِ داناست.');
INSERT INTO "islamhouse" VALUES(3,74,'3:74','رحمت خویش را به هر ‌کس بخواهد اختصاص می‌دهد؛ و الله دارای بخشش بزرگ [و بی‌اندازه] است».');
INSERT INTO "islamhouse" VALUES(3,75,'3:75','از اهل کتاب کسی هست که اگر مال فراوانی به او امانت دهی، آن را به تو بازمی‌گرداند؛ و از [میان] آنان کسی هست که اگر او را بر دیناری امین بشماری، آن را به تو بازنمی‌گردانَد؛ مگر آنکه پیوسته بر [دریافتِ] آن ایستادگى كنى. این به سبب آن است که آنان می‌گویند: «در مورد [خوردن اموالِ] امّیها [= غیر یهود]، گناهی بر ما نیست»؛ و [بدین گونه، آنان] در حالی ‌که [حقیقت را] می‌دانند، بر الله دروغ می‌بندند.');
INSERT INTO "islamhouse" VALUES(3,76,'3:76','آری، [این کار، گناه است؛ اما] هر ‌کس به پیمان خود وفا کند و پرهیزگاری نماید، [بداند که] بی‌تردید، الله پرهیزگاران را دوست دارد.');
INSERT INTO "islamhouse" VALUES(3,77,'3:77','کسانی که پیمان الله [دربارۀ اجرای احکام] و سوگند‌های خود [دربارۀ پیروی از محمد] را به بهای ناچیزی می‌فروشند، یقیناً بهره‌ای در آخرت نخواهند داشت و الله روز قیامت با آنان سخن نمی‌گوید و به آنان نمی‌نگرد و آنها را [از گناه] پاک نمی‌سازد و عذاب دردناکی [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(3,78,'3:78','و گروهی از آنان [= یهود] هستند که زبان خود را به [هنگام تلاوت] کتاب [تورات] چنان می‌گردانند [و آن را تحریف می‌کنند] که گمان کنید [آنچه می‌خوانند واقعاً] از [مطالبِ آن] کتاب [آسمانی] است، در حالی که از آن کتاب نیست؛ و [باز به دروغ] می‌گویند: «آن [جملات] از جانب الله است»؛ در صورتی که از جانب الله نیست و بر الله دروغ می‌بندند، در حالی که خود [نیز حقیقت را] می‌دانند.');
INSERT INTO "islamhouse" VALUES(3,79,'3:79','برای هیچ بشری سزاوار نیست که الله به او کتاب و حُکم [= دانش و فهم] و پیامبری دهد، سپس او به مردم بگوید: «به جای الله، بندگان من باشید». بلکه [سزاوار است که پیامبران به مردم بگویند:] «به خاطر آنکه کتاب [تورات] را آموزش می‌دادید و از آنجا که [عقاید و احکام آن را] فرا می‌گرفتید، [فقیهان و دانشمندان] الهی باشید».');
INSERT INTO "islamhouse" VALUES(3,80,'3:80','و نه اینکه به شما فرمان دهد که فرشتگان و پیامبران را [به جای الله،] صاحب‌اختیار [معبود] بگیرید. آیا [این شخص] شما را پس از آنکه مسلمان شدید، به کفر فرمان می‌دهد؟');
INSERT INTO "islamhouse" VALUES(3,81,'3:81','و [ای پیامبر، یاد کن از] هنگامى كه الله از پیامبران پیمان گرفت [و فرمود:] «هر گاه به شما كتاب و حكمتى دادم و آنگاه فرستاده‌اى [= محمد] به سویتان آمد كه آنچه را با شماست تصدیق كرد، باید به او ایمان آورید و یاری‌اش کنید». [آنگاه] فرمود: «آیا اقرار کردید و پیمان محکم مرا در این باره پذیرفتید؟» [آنان] گفتند: «اقرار کردیم». [الله] فرمود: «پس [بر خود و امت‌هایتان] گواه باشید و من [نیز] با شما از گواهانم».');
INSERT INTO "islamhouse" VALUES(3,82,'3:82','پس هر ‌کس بعد از این [پیمان، از عهد خویش] روی بگردانَد، آنانند که فاسقند».');
INSERT INTO "islamhouse" VALUES(3,83,'3:83','آیا [کافران، آیینی] جز دین الله را می‌جویند؟ حال آنکه هر ‌کس [و هر چه] در آسمان‌ها و زمین است ـ خواه ناخواه ـ سر به فرمان او نهاده است و [همگی] به سوی او بازگردانده می‌شوند.');
INSERT INTO "islamhouse" VALUES(3,84,'3:84','[ای پیامبر،] بگو: «به الله و آنچه بر ما نازل شده است ایمان آوردیم و [همچنین به] آنچه بر ابراهیم و اسماعیل و اسحاق و یعقوب و [پیامبرانی که از] نوادگان [یعقوب بودند] نازل گردیده است و آنچه به موسی و عیسی و [دیگر] پیامبران از جانب پروردگارشان داده شده است؛ میان هیچ‌یک از آنان فرقی نمی‌گذاریم و تسلیمِ او هستیم».');
INSERT INTO "islamhouse" VALUES(3,85,'3:85','هر ‌کس دینی غیر از اسلام برگزیند، هرگز از او پذیرفته نخواهد شد و در آخرت از زیانکاران است.');
INSERT INTO "islamhouse" VALUES(3,86,'3:86','چگونه الله قومی را هدایت می‌کند که بعد از ایمانشان و [پس از آنکه] گواهی دادند به اینکه پیامبر [اسلام] بر حق است و برایشان دلایل روشن آمد، کافر شدند؟ و قطعاً الله قوم ستمکار را هدایت نمی‌کند.');
INSERT INTO "islamhouse" VALUES(3,87,'3:87','جَزایشان این است که لعنتِ الله و فرشتگان و مردم، همگی بر آنهاست.');
INSERT INTO "islamhouse" VALUES(3,88,'3:88','در آن [لعنت،] جاودانه می‌مانند؛ نه عذاب از اینان کاسته می‌شود و نه مهلت [عذر و توبه] می‌یابند.');
INSERT INTO "islamhouse" VALUES(3,89,'3:89','مگر کسانی‌ که پس از آن توبه کردند و [با درستکاری، رفتارشان را] اصلاح نمودند؛ که بی‌گمان، الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(3,90,'3:90','به راستی، کسانی که پس از ایمان خود کافر شدند و بر کفر [خویش] افزودند، هرگز توبۀ آنان پذیرفته نخواهد شد و آنان قطعاً گمراهند.');
INSERT INTO "islamhouse" VALUES(3,91,'3:91','کسانی که کفر ورزیدند و در حال کفر از دنیا رفتند، اگر زمین پر از طلا باشد و [بتوانند] آن را به عنوان فدیه [و کفّارۀ اعمال بدِ خویش] بپردازند، هرگز از هیچ‌یک از آنها پذیرفته نخواهد شد و مجازاتی دردناک [در پیش] دارند و یاورانی نخواهند داشت.');
INSERT INTO "islamhouse" VALUES(3,92,'3:92','[ای مؤمنان،] هرگز به [منزلت و ثواب] نیکوکاری دست نمی‌یابید مگر آنکه از آنچه دوست دارید انفاق کنید؛ و بی‌تردید، الله به آنچه انفاق می‌کنید داناست.');
INSERT INTO "islamhouse" VALUES(3,93,'3:93','همۀ غذاها برای بنی‌اسرائیل حلال بود، مگر آنچه یعقوب پیش از نزول تورات بر خود حرام کرده بود. [ای پیامبر، به یهود] بگو: «اگر راست می‌گویید [و سخنم را باور ندارید]، تورات را بیاورید و آن را بخوانید».');
INSERT INTO "islamhouse" VALUES(3,94,'3:94','پس هر ‌کس بعد از این، بر الله دروغ بندد، آنانند که ستمکارند.');
INSERT INTO "islamhouse" VALUES(3,95,'3:95','[ای پیامبر،] بگو: «الله [در مورد یعقوب و هر چه نازل فرموده است] راست گفت؛ پس از آیین ابراهیم پیروی کنید که حق‌گرا بود و هرگز در زمرۀ مشرکان نبود».');
INSERT INTO "islamhouse" VALUES(3,96,'3:96','در حقیقت، نخستین خانه‌ای که برای [عبادت] مردم قرار داده شد، همانی است که در مکه قرار دارد که پربرکت و [مایۀ] هدایت جهانیان است.');
INSERT INTO "islamhouse" VALUES(3,97,'3:97','در آن [خانه،] نشانه‌های روشن، [از جمله] مقام ابراهیم است و [دیگر اینکه] هر ‌کس وارد آن [حرم] شود، در امان خواهد بود؛ و بر مردم [واجب] است كه براى [عبادتِ] الله قصد آن خانه كنند [البته برای] کسی که توانایی [مالی و جسمی] رفتن به سوی آن را دارد؛ و هر ‌کس [به فریضۀ حج] کفر ورزد، بی‌تردید، الله از جهانیان بی‌نیاز است.');
INSERT INTO "islamhouse" VALUES(3,98,'3:98','[ای پیامبر،] بگو: «ای اهل کتاب، چرا به آیات الله کفر می‌ورزید؟ حال آنکه الله بر آنچه می‌کنید، گواه است».');
INSERT INTO "islamhouse" VALUES(3,99,'3:99','بگو: «ای اهل کتاب، چرا کسی را که ایمان آورده ‌است از راهِ الله باز می‌دارید و آن را منحرف می‌خواهید؟ در حالی که شما [خود به درستیِ آن] گواهید؛ و الله از آنچه می‌کنید غافل نیست».');
INSERT INTO "islamhouse" VALUES(3,100,'3:100','ای کسانی‌ که ایمان آورده‌اید، اگر از گروهی از اهل کتاب اطاعت کنید، شما را پس از ایمانتان به کفر بازمی‌گردانند.');
INSERT INTO "islamhouse" VALUES(3,101,'3:101','و چگونه شما کافر مى‌شوید در حالی که آیات الله بر شما خوانده می‌شود و پیامبرش میان شماست؟ و هر ‌کس به [کتاب] الله [و سنت پیامبرش] تمسّک جوید، قطعاً به راه راست هدایت شده ‌است.');
INSERT INTO "islamhouse" VALUES(3,102,'3:102','ای کسانی‌ که ایمان آورده‌اید، از الله ـ‌آن گونه که شایستۀ پروا کردن از اوست‌ـ پروا کنید و جز در حال مسلمانی نمیرید.');
INSERT INTO "islamhouse" VALUES(3,103,'3:103','و همگی به ریسمان الله [= دین الله] چنگ زنید و پراکنده نشوید؛ و نعمت [بزرگِ] الله را بر خود به یاد آورید که چگونه دشمن یکدیگر بودید و او میان دل‌هایتان الفت ایجاد کرد و به [لطف و برکتِ] نعمتش برادر [یکدیگر] شدید؛ در حالی‌که [پیش از آن، به خاطر کفرتان،] بر لبِ پرتگاه آتش قرار داشتید [که] الله شما را از آن نجات داد. الله اینچنین آیات خود را برایتان آشکار می‌سازد؛ باشد که هدایت یابید.');
INSERT INTO "islamhouse" VALUES(3,104,'3:104','و [ای مؤمنان،] باید گروهی از شما باشند که [مردم را] به نیکی دعوت کنند و به [کارهای] شایسته فرمان دهند و از [امور] ناپسند بازدارند؛ و آنانند که رستگارند.');
INSERT INTO "islamhouse" VALUES(3,105,'3:105','و مانند کسانی نباشید که پس از آنکه دلایل روشن برایشان آمد، پراکنده شدند و اختلاف کردند. اینان عذابی سهمگین [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(3,106,'3:106','روزی‌ که چهره‌هایی سپید و چهره‌هایی سیاه می‌گردد؛ اما [به] کسانی که چهره‌هایشان سیاه شده است [گفته می‌شود:] «آیا بعد از ایمانتان کافر شدید؟ پس به سبب آنچه کفر می‌ورزیدید، عذاب را بچشید».');
INSERT INTO "islamhouse" VALUES(3,107,'3:107','و اما کسانی که چهره‌هایشان سپید گشته است، در رحمتِ الله [در بهشتِ برین] جای می‌گیرند و در آن جاودانند.');
INSERT INTO "islamhouse" VALUES(3,108,'3:108','[ای پیامبر،] اینها آیات الله است که آن را به‌حق بر تو می‌خوانیم؛ و الله [هیچ گاه] ستمی برای جهانیان نمی‌خواهد.');
INSERT INTO "islamhouse" VALUES(3,109,'3:109','و هر چه در آسمان‌ها و زمین است از آنِ الله است و [همۀ] کارها به سوی الله بازگردانده می‌شود.');
INSERT INTO "islamhouse" VALUES(3,110,'3:110','[ای امت محمد،] شما بهترین امتی هستید که برای مردم پدیدار شده‌اید: به کار نیک فرمان می‌دهید و از کارِ ناپسند بازمی‌دارید و به الله ایمان دارید؛ و اگر اهل کتاب [نیز] ایمان می‌آوردند، قطعاً برایشان بهتر بود؛ برخی از آنان مؤمنند و[لی] بیشتر آنان [فاسق و] نافرمانند.');
INSERT INTO "islamhouse" VALUES(3,111,'3:111','[فاسقان اهل کتاب] جز آزاری اندک [همچون طعنه و تمسخر] هرگز زیانی به شما نخواهند رساند و اگر با شما بجنگند، [شکست می‌خورند و] به شما پشت می‌کنند؛ آنگاه [نیز از سوی هیچ کس] یاری نمی‌شوند.');
INSERT INTO "islamhouse" VALUES(3,112,'3:112','[یهود] هر کجا یافت شوند، [داغِ ننگ و] خواری بر آنان زده شده است؛ جز در پناه الله یا پناه مردم، [امنیت نمی‌یابند]؛ و آنان به خشمی از الله گرفتار شده‌اند و [داغِ] درماندگی [و بدبختی] بر آنان زده شده است. این به سبب آن بود که به آیات الله کفر می‌ورزیدند و پیامبران را به ناحق می‌کشتند؛ [و نیز] این [کیفر] به خاطر آن بود که نافرمانی کردند و از حدود [الهی] تجاوز می‌نمودند.');
INSERT INTO "islamhouse" VALUES(3,113,'3:113','[ولی همۀ آنان] یکسان نیستند. گروهی از اهل کتاب هستند که درستکارند [و] در دل شب، در حالی که به نماز ایستاده‌اند، آیات الله را می‌خوانند،');
INSERT INTO "islamhouse" VALUES(3,114,'3:114','[برخی از] آنان به الله و روز قیامت ایمان دارند و [مردم را] به نیکی امر می‌کنند و از بدی باز می‌دارند و در [انجام] کارهای نیک می‌شتابند و آنان از زمرۀ شایستگانند.');
INSERT INTO "islamhouse" VALUES(3,115,'3:115','و هر کار نیکی که انجام دهند، هرگز دربارۀ آن ناسپاسی نخواهند دید [=نیکی‌هایشان نزد الله محفوظ است]؛ و الله به [حال] پرهیزگاران داناست.');
INSERT INTO "islamhouse" VALUES(3,116,'3:116','بی‌گمان، کسانی که کفر ورزیدند، هرگز اموال و فرزندانشان چیزی از [عذاب] الله را از آنان دفع نخواهد کرد و آنان اهل آتشند [و] جاودانه در آن می‌مانند.');
INSERT INTO "islamhouse" VALUES(3,117,'3:117','مَثَل آنچه [کافران] در این زندگی دنیا انفاق می‌کنند، همانند بادی است که سرمای سختی با خود دارد و به کشتزار قومی که بر خود ستم کرده‌اند می‌رسد و نابودش می‌سازد. الله به آنان ستم نکرده است، بلکه آنان خود بر خویشتن ستم کرده‌اند.');
INSERT INTO "islamhouse" VALUES(3,118,'3:118','ای کسانی‌ که ایمان آورده‌اید، از غیر خودتان [دوست و] همراز نگیرید. آنان از هیچ نابکاری [و شرّی] در حق شما کوتاهی نمی‌کنند [و] دوست دارند که شما در رنج و زحمت باشید. در حقیقت، [نشانه‌های] دشمنی، از گفتارشان آشکار ‌است و آنچه در دل‌هایشان پنهان می‌دارند بزرگ‌تر است. اگر بیندیشید، قطعاً [خواهید دانست که] ما نشانه‌ها[ی پندآموز و راه سعادت دنیا و آخرت] را برایتان به روشنی بیان کرده‌ایم.');
INSERT INTO "islamhouse" VALUES(3,119,'3:119','هان! شما [ای مؤمنان،] کسانی هستید که آنان را دوست ‌دارید در حالی‌ که آنها شما را دوست ندارند و شما به همۀ کتاب[های آسمانی] ایمان دارید [ولی آنان به قرآن ایمان ندارند]؛ و چون با شما ملاقات می‌کنند می‌گویند: «ایمان آورده‌ایم» و چون تنها می‌شوند، از شدت خشم بر شما، سرانگشتان [خود] را به دندان می‌گزند. [ای پیامبر، به آنان] بگو: «به خشمِ خود بمیرید». بی‌گمان، الله از [راز] درون سینه‌ها آگاه است.');
INSERT INTO "islamhouse" VALUES(3,120,'3:120','[ای مؤمنان،] اگر به شما نیکی [و خوشی] رسد، آنان را اندوهگین می‌سازد و اگر به شما بدی [و گزندی] رسد، به آن شادمان می‌شوند؛ و اگر [بر تقدیر الهی] صبر و پرهیزگاری نمایید، مکر و نیرنگشان هیچ زیانی به شما نمی‌رساند؛ [چرا که] بی‌تردید، الله به آنچه می‌کنند احاطه دارد.');
INSERT INTO "islamhouse" VALUES(3,121,'3:121','و [ای پیامبر، به یاد آور] زمانی که [در جنگ احد،] بامدادان از خانه و کاشانه‌ات بیرون آمدی [و] مؤمنان را برای جنگیدن در سنگرهای نظامی مستقر می‌کردی؛ و الله شنوای داناست.');
INSERT INTO "islamhouse" VALUES(3,122,'3:122','و [به یاد آور] هنگامی که دو گروه از شما [= بنی‌سَلَمه و بنی‌حارثه] خواستند سستی ورزند [و همراه منافقان به مدینه بازگردند]؛ ولی الله یاور [و نگهدار] آنان بود؛ و مؤمنان باید تنها بر الله توکل کنند.');
INSERT INTO "islamhouse" VALUES(3,123,'3:123','و [ای مؤمنان،] بی‌گمان، الله شما را در [جنگ] بدر در حالی یاری کرد که [به سبب کمبود افراد و اسلحه] ناتوان بودید؛ پس از الله پروا کنید؛ باشد که شکر [نعمت‌هایش را] به جای آورید.');
INSERT INTO "islamhouse" VALUES(3,124,'3:124','[ای پیامبر، به یاد آور] آنگاه که به مؤمنان می‌گفتی: «آیا برایتان کافی نیست که پروردگارتان شما را با سه هزار فرشته که [از آسمان] فرود می‌آیند یاری کند؟»');
INSERT INTO "islamhouse" VALUES(3,125,'3:125','آرى، اگر شكیبایى و پارسایى كنید و دشمنان در این [هیجان و] شتاب خویش بر شما بتازند، پروردگارتان شما را با پنج هزار فرشتۀ نشاندار یارى خواهد كرد.');
INSERT INTO "islamhouse" VALUES(3,126,'3:126','و الله آن [وعده یاری] را جز بشارتی برای [پیروزی] شما قرار نداد تا [به وسیله آن] دل‌هایتان به آن آرامش گیرد؛ و [گرنه] پیروزی جز از جانب الله شکست‌ناپذیرِ حکیم نیست.');
INSERT INTO "islamhouse" VALUES(3,127,'3:127','[این پیروزی برای آن بوده]تا [الله] گروهی از کسانی ‌را که کفر ورزیدند، هلاک سازد یا خوار و مغلوبشان نماید تا مأیوس [و خوار به مکه] بازگردند.');
INSERT INTO "islamhouse" VALUES(3,128,'3:128','[ای پیامبر،] هیچ چیزی از کار [بندگان] در دست تو نیست؛ یا [الله] توبۀ آنان [= مشرکان] را می‌پذیرد [و از گناهشان درمی‌گذرد] یا عذابشان می‌کند؛ زیرا آنان ستمکارند.');
INSERT INTO "islamhouse" VALUES(3,129,'3:129','آنچه در آسمان‌ها و زمین است، از آنِ الله است. او تعالی هر کس را که بخواهد [به رحمت] می‌آمرزد و هر کس را بخواهد [به عدالت] عذاب می‌کند؛ و الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(3,130,'3:130','ای کسانی که ایمان آورده‌اید، ربا را [با سودِ] چندبرابر نخورید و از الله پروا کنید؛ باشد که رستگار شوید.');
INSERT INTO "islamhouse" VALUES(3,131,'3:131','و از آتشی که برای کافران آماده شده است بپرهیزید.');
INSERT INTO "islamhouse" VALUES(3,132,'3:132','و از الله و پیامبر اطاعت کنید؛ باشد که مورد رحمت [الله] قرار گیرید.');
INSERT INTO "islamhouse" VALUES(3,133,'3:133','و به سوی آمرزش پروردگارتان و بهشتی بشتابید که گسترۀ آن [به اندازۀ] آسمان‌ها و زمین است [و] برای پرهیزگاران مهیا شده‌ است.');
INSERT INTO "islamhouse" VALUES(3,134,'3:134','آن کسانی ‌که در [هنگام] توانگری و تنگدستی انفاق می‌کنند و خشم خود را فرومی‌برند و از مردم درمی‌گذرند؛ و الله نیکوکاران را دوست دارد.');
INSERT INTO "islamhouse" VALUES(3,135,'3:135','و [همان] کسانی که چون مرتکب کار زشتی شدند یا بر خود ستم کردند، الله را یاد می‌کنند و برای گناهانشان آمرزش می‌خواهند ـ و جز الله چه کسی گناهان را می‌آمرزد؟ـ و بر آنچه کرده‌اند پافشاری نمی‌کنند در حالی ‌که می‌دانند [گناهکارند و الله بخشاینده و توبه‌پذیر است].');
INSERT INTO "islamhouse" VALUES(3,136,'3:136','پاداش اینان، آمرزشی از [جانب] پروردگارشان است و باغ‌هایی که از زیر [درختان] آن جویبار‌ها جاری است؛ جاودانه در آن می‌مانند؛ و پاداش [نیکوکاران و] اهل عمل چه نیکوست!');
INSERT INTO "islamhouse" VALUES(3,137,'3:137','[ای آسیب‌دیدگانِ اُحُد،] بی‌تردید، پیش از شما [نیز مؤمنانی بوده‌اند که رنج کشیدند و] سنت‌هایی گذشته است؛ پس در زمین بگردید و بنگرید که سرانجامِ تکذیب‌کنندگان [آیات الهی] چگونه بوده است.');
INSERT INTO "islamhouse" VALUES(3,138,'3:138','این [قرآن،] بیانی آشکار برای مردم و هدایت و اندرزی برای پرهیزگاران است.');
INSERT INTO "islamhouse" VALUES(3,139,'3:139','و [ای مؤمنان، از جهاد در راه الله] سست [و ضعیف] نشوید و اندوهگین نگردید؛ و [بدانید که] اگر ایمان داشته باشید، شما برترید.');
INSERT INTO "islamhouse" VALUES(3,140,'3:140','اگر [در اُحد] به شما جراحت [و آسیبی] رسید، به دشمنان شما نیز [در جنگ بدر به همان شکل] آسیب رسید [پس مقاومت کنید]. ما این روزها[ى شكست و پیروزى‌] را میان مردم مى‌گردانیم تا الله، مؤمنان [واقعى‌] را معلوم کند و از میان شما [مؤمنان]، شهیدانی برگیرد؛ و الله ستمکاران را دوست ندارد.');
INSERT INTO "islamhouse" VALUES(3,141,'3:141','و [ماجرای اُحُد وسیلۀ امتحان بود] تا الله کسانی را که ایمان آورده‌اند [از گناهانشان پاک کند] و خالص گرداند و کافران را نابود سازد.');
INSERT INTO "islamhouse" VALUES(3,142,'3:142','[ای اصحاب محمد،] آیا پنداشته‌اید که [تنها با ادعای ایمان،] به بهشت می‌روید، حال آنکه الله هنوز جهادگران و شکیبایان شما را معلوم نکرده است؟');
INSERT INTO "islamhouse" VALUES(3,143,'3:143','و [ای مؤمنان،] به راستی که پیش از روبه‌روشدن با مرگ [در غزوۀ اُحُد]، آرزوی شهادت داشتید. سرانجام آن [معرکۀ جنگ‌] را دیدید و [اکنون پیش روی شماست و] به آن می‌نگرید.');
INSERT INTO "islamhouse" VALUES(3,144,'3:144','و محمد جز فرستاده‌ای نیست که پیش از او [نیز] رسولانی [بودند و] گذشتند. آیا اگر او بمیرد یا کشته شود، به عقب بازمی‌گردید [و دین خود را رها می‌کنید]؟ و هر ‌کس [از عقیدۀ راستین خود] بازگردد، [بداند که‌] هرگز به الله زیانی نمی‌رساند و [به زودی] الله سپاسگزاران را پاداش خواهد داد.');
INSERT INTO "islamhouse" VALUES(3,145,'3:145','و هیچ کسی جز به فرمان الله نمی‌میرد [چرا که مرگ،] سرنوشتی است زماندار [و معین]؛ و هر ‌کس پاداش این دنیا را بخواهد، نصیبش می‌کنیم و هر ‌کس پاداش آخرت را بخواهد [نیز] نصیبش خواهیم کرد و [به زودی] سپاسگزاران را پاداش خواهیم داد.');
INSERT INTO "islamhouse" VALUES(3,146,'3:146','و چه بسیار پیامبرانی که توده‌های انبوهی همراهشان نبرد کردند؛ آنان هیچ‎گاه در برابر [سختی‌ها و] آنچه که در راه الله به ایشان رسید سستی نورزیدند و [در مقابل دشمن] درمانده نشدند؛ و الله شکیبایان را دوست دارد.');
INSERT INTO "islamhouse" VALUES(3,147,'3:147','و سخنشان تنها این بود که: «پروردگارا، گناهانمان را بیامرز و از زیاده‌رویِ ما در کارمان [درگذر] و گام‌هایمان را استوار بدار و ما را بر گروه کافران پیروز گردان».');
INSERT INTO "islamhouse" VALUES(3,148,'3:148','پس الله، پاداش این دنیا و پاداش نیک آخرت را به آنان عطا کرد؛ و الله نیکوکاران را دوست دارد.');
INSERT INTO "islamhouse" VALUES(3,149,'3:149','ای کسانی‌ که ایمان آورده‌اید، اگر از کسانی ‌که کفر ورزیدند اطاعت کنید، شما را [به کفر] بازمی‌گردانند، آنگاه [در دنیا و آخرت] زیانکار می‌گردید.');
INSERT INTO "islamhouse" VALUES(3,150,'3:150','[بر کافران تکیه نکنید] بلکه، الله [یار و] کارسازِ شماست و او بهترینِ یاریگران است.');
INSERT INTO "islamhouse" VALUES(3,151,'3:151','به زودی در دل‌های کسانی که کفر ورزیدند بیم و هراس خواهیم افکند؛ به خاطر اینکه چیزی را شریک الله قرار داده‌اند که هیچ دلیلی بر [حقانیتِ] آن نازل نکرده ‌است؛ و جایگاهشان آتش است و جایگاه ستمکاران چه بد است!');
INSERT INTO "islamhouse" VALUES(3,152,'3:152','و آنگاه که در [آغاز جنگ احد،] به فرمان او، آنان [= کافران] را می‌کشتید، به راستی الله وعدۀ خود با شما را تحقق بخشید تا [اینکه در حفظ موقعیتِ خود] سست شدید و در کار [جنگ و جمع‌آوری غنیمت] با یکدیگر به نزاع پرداختید؛ و پس از آنکه [الله] آنچه را دوست می‌داشتید [= پیروزی] به شما نشان داد، [از دستور پیامبر] سرپیچی کردید؛ برخی از شما خواهان دنیا بودند و برخی خواهان آخرت. سپس [الله] برای آنکه شما را بیازماید، از [تعقیب] آنان منصرفتان کرد [و پیروزیتان به شکست انجامید] و [چون از گناه و کوتاهی خود در نافرمانی از رسول پشیمان شدید] از شما درگذشت؛ و الله نسبت به مؤمنان بخشش [بسیار] دارد.');
INSERT INTO "islamhouse" VALUES(3,153,'3:153','و [ای مؤمنان، به یاد آورید] هنگامی که [از میدان جنگ] مى‌گريختيد و [چنان ترسیده بودید که] به هیچ کس توجه نمی‌کردید و پیامبر [که در میدان جنگ مقاومت می‌کرد] از پشت سرتان شما را صدا می‌زد [که: «ای بندگان الله، به سوی من بشتابید»؛ ولی شما توجه نمی‌کردید]؛ پس [الله نیز] با اندوهی در پی اندوهی [دیگر] شما را مجازات کرد. این به سبب آن بود که بر آنچه از دست داده‌اید [= پیروزی و غنیمت] و آنچه بر سرتان آمده است [= شکست و ترس] اندوهگین نشوید و [بدانید که] الله به آنچه می‌کنید آگاه است.');
INSERT INTO "islamhouse" VALUES(3,154,'3:154','سپس [الله] بعد از آن اندوه، آرامشی بر شما فروفرستاد [به گونه‌اى] كه گروهى از شما را [به سبب اطمینان و آرامشی که در دل‌هایشان بود] خوابى سبک فراگرفت و گروهی دیگر [= منافقان] تنها در فکر جان خود بودند و دربارۀ الله گمان‌های ناروا [و باطلی] همچون گمان‌های [دوران] جاهلیت داشتند. آنان می‌گفتند: «آیا ما در این کار اختیاری داریم؟» [ای پیامبر، به آنان] بگو: «همۀ کارها [و شکست و پیروزی] به دست الله است». آنها چیزی را در دل‌هایشان پنهان می‌دارند که برای تو آشکار نمی‌سازند [و] می‌گویند: «اگر در این کار اختیار داشتیم، در اینجا کشته نمی‌شدیم». بگو: «اگر شما در خانه‌های خود نیز بودید، کسانی ‌که کشته‌شدن بر آنان مقرر شده بود، قطعاً [با پاى خود] به سوى قتلگاه‌های خویش مى‌رفتند». و [این رویدادها] برای آن است که الله آنچه را در سینه‌هایتان [پنهان] ‌دارید [در عمل] بیازماید و آنچه را در دل‌هایتان است پاک گردانَد؛ و الله از [راز] درون سینه‌ها آگاه است.');
INSERT INTO "islamhouse" VALUES(3,155,'3:155','[ای اصحاب پیامبر،] افرادی از شما که در روزِ برخورد دو گروه [مسلمانان و کافران در جنگ احد] فرار کردند، در حقیقت، شیطان آنان را به سبب بعضی از کردار [و گناهانی] که مرتکب شده بودند دچار لغزش نمود و قطعاً الله از آنان درگذشت. بی‌گمان، الله آمرزندۀ بردبار است.');
INSERT INTO "islamhouse" VALUES(3,156,'3:156','ای کسانی‌ که ایمان آورده‌اید، مانند افرادی نباشید که کفر ورزیدند و دربارۀ برادرانشان ـ هنگامی که [در پیِ کسبِ روزی] به سفر رفتند [و مُردند] یا در جنگ شرکت کردند [و کشته شدند]ـ گفتند: «اگر آنان نزد ما بودند، نمی‌مردند و کشته نمی‌شدند». [این اعتقاد، در وجودشان نهاده شد] تا [این گونه،] الله [اندوه و] حسرتی در دل‌هایشان قرار دهد؛ و الله [است که] زنده می‌کند و می‌میراند؛ و الله به آنچه می‌کنید، بیناست.‌');
INSERT INTO "islamhouse" VALUES(3,157,'3:157','و اگر در راه الله کشته شوید یا بمیرید، قطعاً آمرزش و رحمت الله بهتر است از [تمام] آنچه آنان [= دنیادوستان] جمع‌آوری می‌کنند.');
INSERT INTO "islamhouse" VALUES(3,158,'3:158','و اگر بمیرید یا [در عرصۀ جهاد] کشته شوید، یقیناً به سوی الله محشور می‌شوید.');
INSERT INTO "islamhouse" VALUES(3,159,'3:159','[ای پیامبر،] به [برکتِ] رحمت الهی است که تو با آنان [= اصحاب،] نرم‌خو [و مهربان] شدی؛ و اگر تندخو و سنگدل بودی، قطعاً از اطرافت پراکنده می‌شدند؛ پس از آنان درگذر و برایشان آمرزش بخواه و در کارها با آنان مشورت کن و آنگاه که تصمیم [بر انجام کاری] گرفتی، بر الله توکل کن؛ [چرا که] بی‌تردید، الله توکل‌کنندگان را دوست ‌دارد.');
INSERT INTO "islamhouse" VALUES(3,160,'3:160','اگر الله شما را یاری کند، هیچ کس بر شما پیروز نخواهد شد و[لی] اگر شما را به خود واگذارد، کیست که پس از او یاری‌تان کند؟ و مؤمنان باید تنها بر الله توکل کنند.');
INSERT INTO "islamhouse" VALUES(3,161,'3:161','و سزاوار نیست هیچ پیامبری [با گرفتن سهمی اضافی از غنایم، به اصحابش] خیانت کند؛ و هر کس خیانت کند، روز قیامت، با آنچه [در آن] خیانت کرده است می‌آید؛ سپس به هر ‌کس [جزای] آنچه [از نیک و بد] کرده است به طور کامل داده می‌شود و به آنان ستم نخواهد شد.');
INSERT INTO "islamhouse" VALUES(3,162,'3:162','آیا کسی‌ که در پیِ رضایتِ الله است، همانند کسی است که دچار خشمی از جانب الله گشته و جایگاهش دوزخ است؟ و [دوزخ] چه بد جایگاهی است!');
INSERT INTO "islamhouse" VALUES(3,163,'3:163','[هر یک از] اینان، درجاتی [متفاوت] نزد الله دارند و الله به آنچه می‌کنند بیناست.');
INSERT INTO "islamhouse" VALUES(3,164,'3:164','به راستی، الله بر مؤمنان منت نهاد ‌که میان آنان پیامبری از خودشان برانگیخت تا آیات او [= قرآن] را برایشان بخوانَد و [از گناه] پاکشان گردانَد و به آنان کتاب [= قرآن] و حکمت [= سنت] بیا‌موزد؛ و قطعاً پیش از آن، در گمراهی آشکاری بودند.');
INSERT INTO "islamhouse" VALUES(3,165,'3:165','[ای مؤمنان،] آیا چون [در جنگ اُحُد] به شما مصیبتى رسید ـ [با آنكه در جنگ بدر] دو برابرش را [به دشمنان خود] رساندید ـ گفتید: «این [مصیبت‌] از كجا [به ما] رسید؟» [ای پیامبر،] بگو: «آن [شکست] از جانب خودتان [و نتیجۀ نافرمانی از دستور پیامبر] است». بی‌گمان، الله بر هر کاری تواناست.');
INSERT INTO "islamhouse" VALUES(3,166,'3:166','و روزی‌ که [در جنگ احد] دو گروه [مؤمنان و کافران] با هم برخورد کردند، آنچه به شما رسید، به فرمانِ الله بود [تا شما را بیازماید] و مؤمنان را معلوم نمايد.');
INSERT INTO "islamhouse" VALUES(3,167,'3:167','و کسانی‌ را که نفاق ورزیدند [نیز] معلوم نمايد که [چون] به ایشان گفته شد: «بیایید در راه الله بجنگید یا [از خانواده و قبیله‌تان] دفاع کنید». گفتند: «اگر می‌دانستیم جنگی روی خواهد داد، حتماً از شما پیروی می‌کردیم». آنان در آن هنگام به کفر نزدیک‌تر بودند تا ایمان؛ به زبان خویش چیزی می‌گویند که در دل‌هایشان نیست و الله به آنچه [در دل] پنهان می‌کنند، آگاه‌تر است.');
INSERT INTO "islamhouse" VALUES(3,168,'3:168','کسانی‌ که [خود در خانه] نشستند [از جهاد سر باز زدند] و دربارۀ برادران شان گفتند: «اگر آنها از ما پیروی می‌کردند، کشته نمی‌شدند». [ای پیامبر، به این منافقان] بگو: «اگر راست می‌گویید، پس مرگ را از خود دور سازید».');
INSERT INTO "islamhouse" VALUES(3,169,'3:169','هرگز کسانی را که در راه الله کشته شده‌اند مُرده مپندار؛ بلکه زنده‌اند [و] نزد پروردگارشان روزی داده می‌شوند.');
INSERT INTO "islamhouse" VALUES(3,170,'3:170','آنان به آنچه الله از فضل خود برایشان داده ‌است شادمانند و براى [سرانجامِ نیک] كسانى كه [راه] آنان را پى گرفته‌اند و [هنوز] به آنان نپیوسته‌اند شادى مى‌كنند؛ چرا كه [هیچ یک از] اینان ترس و ‌اندوهى نخواهند داشت.');
INSERT INTO "islamhouse" VALUES(3,171,'3:171','[همچنین] به نعمت و فضل الله شادمانند و اینکه [می‌بینند] الله پاداش مؤمنان را تباه نمی‌کند.');
INSERT INTO "islamhouse" VALUES(3,172,'3:172','کسانی ‌که دعوت الله و پیامبر را ـ پس از آنکه [در غزوۀ احد] به آنان جراحت [و سختی بسیار] رسید ـ اجابت کردند [و برای تعقیب مشرکان به سوی «حَمراء الاسد» رفتند]، برای [آن عده از] اینان که نیکی و پرهیزگاری کردند، پاداش بزرگی [در پیش] است.');
INSERT INTO "islamhouse" VALUES(3,173,'3:173','همان کسانی‌ که برخی [از مشرکان] به آنان گفتند: «مردم [مکه] برای [جنگ با] شما گِرد آمده‌اند؛ پس از آنها بترسید» و[لی این سخن] بر ایمانشان افزود و گفتند: «الله برای [محافظت از] ما بس است و [او] بهترین [مراقب و] کارساز است».');
INSERT INTO "islamhouse" VALUES(3,174,'3:174','پس به نعمت و فضل الله [از حمراء الاسد به مدینه] بازگشتند در حالی که هیچ آسیبی به آنان نرسیده بود و همچنان در پیِ [کسبِ] رضایتِ الله بودند؛ و الله فضل و بخشش بزرگی دارد.');
INSERT INTO "islamhouse" VALUES(3,175,'3:175','در حقیقت، این شیطان است که [شما را از هیبت پوشالیِ] دوستانش می‌ترساند؛ پس اگر ایمان دارید، از آنان [= مشرکان] نترسید و از من بترسید.');
INSERT INTO "islamhouse" VALUES(3,176,'3:176','و [ای پیامبر،] کسانی‌ که در [یاری کردنِ] کفر می‌شتابند، تو را اندوهگین نسازند. یقیناً آنان هرگز زیانی به الله نمی‌رسانند. الله می‌خواهد که بهره‌ای برایشان در آخرت قرار ندهد و عذابی بزرگ [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(3,177,'3:177','به راستی، کسانی ‌که کفر را به [بهای] ایمان خریدند، هرگز به الله زیانی نمی‌رسانند و عذابی دردناک [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(3,178,'3:178','و کسانی ‌که کافر شدند، مپندارند که چون [با زندگی طولانی] به آنان مهلت می‌دهیم به سودشان است. جز این نیست که به آنان مهلت می‌دهیم تا بر گناه [خود] بیفزایند و عذابی خفت‌بار [در پیش‌] دارند.');
INSERT INTO "islamhouse" VALUES(3,179,'3:179','[ای مؤمنان،] چنین نبود [و از حکمت الهی نیست] که الله، مؤمنان را بر این حال كه شما هستید [واگذارد و مؤمن و منافق را در کنار یکدیگر] رها سازد [بلكه شما را با تکالیف و آزمایش‌های گوناگون می‌آزماید] تا پلید را از پاک متمایز كند؛ و چنین نبود که الله [برای مشخص‌نمودن مؤمنان از منافقان،] شما را از [اسرارِ] غیب آگاه کند؛ ولی الله از میان رسولانش هر کس را بخواهد برمی‌گزیند [و او را بر بخشی از غیب آگاه می‌کند]؛ پس به الله و رسولانش ایمان بیاورید؛ و اگر ایمان بیاورید و تقوا پیشه کنید، پاداش بزرگی برایتان [در پیش] است.');
INSERT INTO "islamhouse" VALUES(3,180,'3:180','و کسانی‌ که به آنچه الله از فضل خویش به آنان داده است بخل می‌ورزند، گمان نکنند که آن [بخل] برایشان خیر است؛ بلکه برایشان شر است. در روز قیامت، آنچه به آن بخل می‌ورزیدند، طوق گردنشان خواهد شد [و با آن عذاب می‌شوند]؛ و میراث آسمان‌ها و زمین از آنِ الله است و الله به آنچه می‌کنید آگاه است.');
INSERT INTO "islamhouse" VALUES(3,181,'3:181','بی‌تردید، الله سخن کسانی را شنید که گفتند: «الله فقیر است و [از ما قرض می‌خواهد و] ما بی‌نیازیم». [ما این] گفتارشان و [نیز] قتل ناحق پیامبران را خواهیم نوشت و [روز قیامت به [آنان] می‌گوییم: «عذاب سوزان [دوزخ] را بچشید».');
INSERT INTO "islamhouse" VALUES(3,182,'3:182','[ای یهودیان،] این [مجازات،] به خاطر کردار پیشین شماست؛ و [گرنه] الله هرگز به بندگان [خود] ستمکار نیست.');
INSERT INTO "islamhouse" VALUES(3,183,'3:183','[این یهودیان، همان] کسانی‌ هستند که گفتند: «الله به ما سفارش کرده که به هیچ پیامبری ایمان نیاوریم تا [پیامبری همراه خود] برایمان قربانی‌ای بیاورد که آتش [ناشی از صاعقه،] آن را [به نشانۀ پذیرش بسوزانَد و] ببلعد». [ای پیامبر، به آنان] بگو: «قطعاً پیش از من پیامبرانى بودند كه دلایل آشكار را همراه آنچه گفتید برایتان آوردند. اگر راست مى‌گویید، پس چرا آنان را كشتید؟»');
INSERT INTO "islamhouse" VALUES(3,184,'3:184','پس اگر [یهود و کافران] تو را تکذیب کردند، [غمگین مباش و بدان] پیامبرانی که پیش از تو بودند و معجزات و نوشته‌ها و کتاب‌های روشنگر آورده بودند [نیز] تکذیب شدند.');
INSERT INTO "islamhouse" VALUES(3,185,'3:185','هر جانداری چشندۀ [طعم‌] مرگ است و یقیناً روز قیامت پاداش‌هایتان را به صورت کامل دریافت خواهید کرد؛ پس هر كه را از آتش دور دارند و در بهشت درآورند، قطعاً رستگار شده ‌است؛ و زندگی دنیا چیزی جز مایۀ فریب نیست.');
INSERT INTO "islamhouse" VALUES(3,186,'3:186','[ای مؤمنان،] بی‌تردید شما در [مورد بذلِ] اموال و جان‌هایتان آزمایش می‌شوید و [همچنین] از کسانی که پیش از شما به آنان کتاب [آسمانی] داده شده است [= یهود و نصاری] و [نیز] از کسانی‌ که شرک ورزیدند سخنان آزاردهندۀ بسیاری خواهید شنید؛ و[لی] اگر صبر کنید و تقوا پیشه سازید، [شایسته‌تر است؛ زیرا] این [ایستادگى‌] حاكى از عزم استوار [شما] در كارهاست.');
INSERT INTO "islamhouse" VALUES(3,187,'3:187','و [ای پیامبر، یاد کن] هنگامی که الله از کسانی‌ که کتاب [آسمانی] به آنان داده شده بود پیمان گرفت که: «حتماً [آموزه‌های هدایت‌بخش] آن را برای مردم بیان نمایید و [آنچه را که از نبوت محمد صلی الله علیه وسلم خبر می‌دهد] کتمانش نکنید»؛ و[لی] آن [عهد] را پشت سر خویش انداختند [به آن توجهی نکردند] و به بهای اندکی [همچون مال و مقام] فروختند. چه بد است آنچه می‌خرند!');
INSERT INTO "islamhouse" VALUES(3,188,'3:188','کسانی که از اعمال [زشت] خود خشنود می‌گردند و دوست دارند در ازای کار [نیکی] که انجام نداده‌اند مورد ستایش قرار گیرند، گمان مبر که از عذاب [الهی] برکنارند؛ [بلکه] برای آنان عذاب دردناکی [در پیش] است.');
INSERT INTO "islamhouse" VALUES(3,189,'3:189','فرمانرواییِ آسمان‌ها و زمین از آنِ الله است و الله بر هر چیزی تواناست.');
INSERT INTO "islamhouse" VALUES(3,190,'3:190','به راستی در آفرینش آسمان‌ها و زمین و آمد و شدِ شب و روز، نشانه‌های [آشکاری] برای خردمندان است.');
INSERT INTO "islamhouse" VALUES(3,191,'3:191','[همان] کسانی‌ که الله را [در همۀ احوال‌] ایستاده و نشسته و بر پهلو آرمیده یاد می‌کنند و در آفرینش آسمان‌ها و زمین می‌اندیشند [و می‌گویند:] «پروردگارا، اینها را بیهوده نیافریده‌ای؛ منزّهی تو؛ پس ما را از عذاب آتش [دوزخ] نگاه دار.');
INSERT INTO "islamhouse" VALUES(3,192,'3:192','پروردگارا، کسی را که تو به آتش [دوزخ] درآوری، یقیناً خوار و رسوایش کرده‌ای و ستمکاران [هیچ] یاوری ندارند.');
INSERT INTO "islamhouse" VALUES(3,193,'3:193','پروردگارا، ما [ندای] دعوتگری [= پیامبرت محمد صلی الله علیه وسلم] را شنیدیم که به ایمان فرامی‌خوانَد که: "به پروردگار خود ایمان آورید"؛ پس ما ایمان آوردیم. پروردگارا، پس گناهانمان را ببخش و بدی‌هایمان را بپوشان و ما را در زمرۀ نیکان بمیران.');
INSERT INTO "islamhouse" VALUES(3,194,'3:194','پروردگارا، آنچه را که به وسیلۀ رسولانت به ما وعده داده‌ای به ما عطا کن و در روز قیامت رسوایمان مگردان. بی‌تردید، تو خلاف وعده نمی‌کنی».');
INSERT INTO "islamhouse" VALUES(3,195,'3:195','پس پروردگارشان دعاى آنان را اجابت كرد [و فرمود:] «من [پاداش] عمل هیچ صاحبْ‌عملى از شما را ـ چه مرد و چه زن ـ كه همنوعِ یكدیگرید تباه نمى‌كنم؛ پس كسانى كه [از وطن خود] هجرت كرده‌اند و [یا] از خانه‌هاى خود رانده شده‌اند و در راه من آزار دیده‌اند و جنگیده‌اند و كشته شده‌اند، قطعاً بدی‌هایشان را از آنان مى‌زدایم و [به عنوان] پاداشى از جانب الله، آنان را در باغ‌هایی درمى‌آورم كه از زیرِ [درختان‌] آن جویبارها جاری است؛ و الله است که پاداش نیكو نزد اوست.');
INSERT INTO "islamhouse" VALUES(3,196,'3:196','[ای پیامبر،] رفت و آمدِ کافران [برای تجارت] در شهرها [و مال و مقام آنان] تو را نفریبد.');
INSERT INTO "islamhouse" VALUES(3,197,'3:197','[این] بهره‌مندی ناچیزی است؛ سپس جایگاهشان دوزخ است و چه بد جایگاهی است!');
INSERT INTO "islamhouse" VALUES(3,198,'3:198','اما کسانی ‌که از پروردگارشان پروا کرده‌اند، [در بهشت] باغ‌هایی دارند که از زیر [درختان] آن جویبارها جاری است [و] جاودانه در آن می‌مانند. [این] محلّ پذیرایی از جانب الله [برای آنان مهیّا شده] است و آنچه نزد الله است، برای نیکوکاران بهتر است.');
INSERT INTO "islamhouse" VALUES(3,199,'3:199','و کسانی از اهل کتاب هستند که به الله و آنچه بر شما نازل شده و آنچه بر خودشان نازل شده است ایمان دارند؛ در برابر الله فروتن هستند و آیات الله را به بهای ناچیزی نمی‌فروشند. اینانند که پاداششان نزد پروردگارشان [محفوظ] است. به راستی که الله در حسابرسی سریع است.');
INSERT INTO "islamhouse" VALUES(3,200,'3:200','ای کسانی که ایمان آورده‌اید، [بر تکالیف دینی و رنج‌های دنیوی] شکیبایی ورزید و [در برابر دشمنان] پایدار باشید و مرزها[ی سرزمین اسلامی] را نگهبانى كنيد و پرهیزگاری پیشه کنید؛ باشد که رستگار شوید.');
INSERT INTO "islamhouse" VALUES(4,1,'4:1','ای مردم، از پروردگارتان پروا کنید [همان ذاتی] که شما را از یک تن آفرید و همسرش را [نیز] از او آفرید و از [نسلِ] آن دو، مردان و زنان بسیاری پراکند؛ و از الله که به [نام] او از یکدیگر درخواست می‌کنید پروا نمایید و از [گسستن] پیوند خویشاوندی بپرهیزید. بی‌گمان، الله همواره مراقب [و نگهبان] شماست.');
INSERT INTO "islamhouse" VALUES(4,2,'4:2','و [ای سرپرستان،] اموال یتیمان را [پس از بلوغ و رشد] به آنان بازگردانید و [مال کم‌ارزش و] نامرغوب [خود] را با [مال ارزشمند و‌] مرغوب [آنان] عوض نكنید و اموالشان را همراه اموال خود نخورید. به راستی که این [کار] گناه بزرگی است.');
INSERT INTO "islamhouse" VALUES(4,3,'4:3','و اگر ترسیدید که [به خاطر کاستن از مهریه یا بدرفتاری] نتوانید عدالت را در حق دختران یتیم رعایت نمایید، [از ازدواج با آنان صرف‌نظر کنید و] از زنانی [دیگر] که مورد پسند شماست، [یک یا] دو و [یا] سه و [یا] چهار تن را به همسری درآورید. پس اگر بیم دارید که نتوانید عدالت کنید، به یک [زن] یا به آنچه [از کنیزان] که مالک آنها هستید [اکتفا نمایید]. این [کار] نزدیک‌تر است به اینکه ستم نکنید.');
INSERT INTO "islamhouse" VALUES(4,4,'4:4','و مهریه‌های زنان را به عنوان هدیه [و فریضه‌ای الهی] با خوشدلی به آنان بدهید. پس اگر آنان چیزی از آن را با رضایت خاطر به شما بخشیدند، آن را نوشین و گوارا بخورید.');
INSERT INTO "islamhouse" VALUES(4,5,'4:5','و اموالتان را که الله مایه قِوام [زندگی] شما قرار داده است به سفیهان [و کم‌خردان] نسپارید و[لی] از [درآمدِ] آن [اموال] به آنان خوراک و پوشاک بدهید و به شایستگی با آنان سخن بگویید.');
INSERT INTO "islamhouse" VALUES(4,6,'4:6','و یتیمان را [از لحاظ توانایی بر مراقبت از اموالشان] بیازمایید تا وقتی به [سن] ازدواج برسند؛ پس اگر رشد [فکریِ کافی] در آنان یافتید، اموالشان را به آنان بدهید و آن را [از بیم آنکه مبادا] بزرگ شوند [و از شما پس بگیرند] به اسراف و شتاب مخورید؛ و هر کس بی‌نیاز است، باید [از گرفتن اجرتِ سرپرستی] خودداری کند و هر کس نیازمند است، باید به طرز شایسته [و مطابقِ عُرف از آن] بخورد. پس هر گاه اموالشان را به آنان بازگرداندید، بر آنان گواه بگیرید و [بدانید] الله برای حسابرسی کافی است.');
INSERT INTO "islamhouse" VALUES(4,7,'4:7','مردان از آنچه پدر و مادر و خویشاوندان [شان به عنوان ارث] بر جای گذاشته‌اند، سهمی دارند و زنان [نیز] از آنچه پدر و مادر و خویشاوندان بر جای گذاشته‌اند، سهمی دارند ـ خواه آن [مال]کم باشد یا زیاد. [چنین] بهره‌ای [از جانب الله] معیّن [و مقرر شده] است‌.');
INSERT INTO "islamhouse" VALUES(4,8,'4:8','و هر گاه خویشاوندان [غیر وارث] و یتیمان و مستمندان در تقسیم [میراث] حاضر شدند، [پیش از تقسیم، چیزی] از آن [اموال را از باب استحباب] برایشان بدهید و به شایستگی با آنان سخن بگویید.');
INSERT INTO "islamhouse" VALUES(4,9,'4:9','و کسانی كه اگر فرزندان ناتوانى از خود بر جاى بگذارند، بر [آیندۀ‌] آنان بیم دارند، باید [از ستم دربارۀ یتیمان مردم نیز] بترسند؛ پس باید از الله پروا کنند و سخنی [سنجیده و] درست بگویند.');
INSERT INTO "islamhouse" VALUES(4,10,'4:10','در حقیقت، کسانی‌ که اموال یتیمان را به ستم می‌خورند، جز این نیست که آتش در شکم خویش فرومی‌برند و به زودی به آتشی برافروخته درمی‌آیند.');
INSERT INTO "islamhouse" VALUES(4,11,'4:11','الله دربارۀ فرزندانتان به شما سفارش می‌کند: سهم پسر، مانند سهم دو دختر است؛ و اگر [وارثان، دو دختر یا] بیشتر از دو دختر باشند، سهم آنان دو سومِ تَرَکه [= میراث] است؛ و اگر فقط یک دختر باشد، سهم او نصف ترکه است. اگر [متوفی‌] فرزند [چه پسر و چه دختر] داشته باشد، برای هر یک از پدر و مادر او [= متوفَّی]، یک‌ششمِ ترکه است و اگر فرزند نداشته باشد و [تنها] پدر و مادرش از او ارث ببرند، در این صورت، برای مادرش یک‌سوم‌ است [و بقیه که دو سوم می‌شود سهم پدر است‌]؛ و اگر او [= متوفی] برادرانی داشته باشد، سهم مادرش یک‌ششم است. [این تقسیم میراث،‌] پس از اجرای وصیتی که [میت] بدان سفارش نموده یا [پرداخت] بِدِهی‌[ها و قَرض‌هایی که دارد انجام می‌گیرد]. شما نمی‌دانید که پدرانتان و [یا] فرزندانتان، کدام‌یک برایتان سودمندترند. [این احکام،] فریضه‌ای [معیّن و مقرر] از جانب الله است. به راستی که الله دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(4,12,'4:12','و [ای شوهران،] اگر همسرانتان فرزندی نداشته باشند، نیمی از تَرَکه برای شماست و اگر فرزندی داشته باشند، یک‌چهارمِ ترکه از آنِ شماست؛ [البته] پس از [عمل به‌] وصیتی که کرده‌اند یا [پرداخت‌] بِدِهی‌[ها و قَرض‌هایی] که دارند؛ و اگر شما فرزندی نداشته باشید، یک‌چهارمِ ترکۀ شما برای زنانتان است و اگر فرزندی داشته باشید، یک‌هشتم ترکۀ شما از آنِ ایشان است؛ [البته] پس از عمل به وصیتی که کرده‌اید و [پرداخت] بِدِهی[ها و قَرض‌هایی که دارید]؛ و اگر مرد یا زنی که از او ارث می‌برند، کَلاله [= بدون پدر و مادر و بدون فرزند] باشد و یک برادر [مادری] یا یک خواهری [مادری] داشته باشد، برای هر یک از آنان یک‌ششم [تَرَکه] است؛ و اگر [برادران و خواهران مادری میت] بیش از این [دو نفر] باشند، در این صورت، در یک‌سوم [از ترکه] شریکند؛ [البته‌] پس از انجام وصیتى كه به آن سفارش شده یا بِدِهی[ها و قَرض‌هایی] كه [باید پرداخت گردد؛ به شرط آنكه از این طریق،‌] زیانى [به ورثه‌] نرساند. [این حُکم،] سفارش الله است و الله دانای بردبار است.');
INSERT INTO "islamhouse" VALUES(4,13,'4:13','اینها حدود الهی است و هر ‌کس از الله و پیامبرش اطاعت کند، وی را به باغ‌هایی درمی‌آورد که از زیر [درختان] آن جویبارها جاری است؛ در آن جاودانه‌اند و این همان کامیابی بزرگ است.');
INSERT INTO "islamhouse" VALUES(4,14,'4:14','و هر ‌کس از الله و پیامبرش نافرمانی نماید و از حدود او تجاوز کند، وی را در آتشی وارد می‌کند که جاودانه در آن خواهد ماند و برایش عذابی خفت‌بار [در پیش] است.');
INSERT INTO "islamhouse" VALUES(4,15,'4:15','و کسانی از زنان شما که مرتکب زنا می‌شوند، چهار نفر [مرد عادل] از خودتان را بر آنان گواه گیرید؛ پس اگر گواهی دادند، آن زنان را در خانه‌ها نگه دارید تا مرگشان فرارسد یا الله راهی برایشان قرار دهد.');
INSERT INTO "islamhouse" VALUES(4,16,'4:16','از میان شما، آن مرد و زنی که مرتکب آن [عمل زشت] می‌شوند، [با تنبیه و توبیخ] آزارشان دهید؛ پس اگر توبه کردند و درستکار شدند، از آنان صرف‌نظر کنید؛ [چرا که] بی‌تردید، الله توبه‌پذیر مهربان است.');
INSERT INTO "islamhouse" VALUES(4,17,'4:17','[پذیرش] توبه از سوی الله، تنها برای کسانی است که از روی نادانی [و سفاهت] کار ناشایستی انجام می‌دهند، آنگاه به زودی [و پیش از فرارسیدنِ مرگ] توبه می‌کنند. اینانند که الله توبه‌شان را می‌پذیرد و الله دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(4,18,'4:18','و برای کسانی که [تا پایان عمر همواره] مرتکب کارهای ناشایست می‌شوند و هنگامی که مرگ یکی از آنان فرامی‌رسد می‌گوید: «اکنون توبه کردم» [هیچ] توبه [و بخششی در کار] نیست و نه برای کسانی‌ که در حال کفر می‌میرند. اینان هستند که برایشان عذاب دردناکی مهیّا ساخته‌ایم.');
INSERT INTO "islamhouse" VALUES(4,19,'4:19','ای کسانی‌ که ایمان آورده‌اید، برای شما حلال نیست که زنان [پدرانتان] را بر خلاف میلشان به ارث برید [یا مجبور به ازدواج با دیگران کنید] و [در مورد همسران خودتان نیز حکم این است که] آنان را زیر فشار قرار ندهید تا برخی از آنچه را که [به عنوان مهریه] به آنان داده‌اید پس بگیرید؛ مگر اینکه مرتکب عمل ناشایستِ آشکاری [مانند زنا] شوند؛ [اما در مورد زنان پاکدامنِ خود نیکوگفتار باشید] و با آنها به طور شایسته رفتار کنید و اگر از آنان [خوشتان نیامد و] کراهت داشتید، پس [شکیبایی کنید؛] چه بسا از چیزی خوشتان نمی‌آید و الله خیر بسیاری در آن قرار می‌دهد.');
INSERT INTO "islamhouse" VALUES(4,20,'4:20','اگر خواستید همسری [دیگر] به جای همسر [پیشین خود] برگزینید و به یکی از آنان مال فراوانی [به عنوان مهریه] پرداخته‌اید، چیزی از آن را پس نگیرید. آیا می‌خواهید آن [مال] را به ناحق و گناهِ آشکار بازپس ‌بگیرید؟');
INSERT INTO "islamhouse" VALUES(4,21,'4:21','و چگونه آن [مهریه] را بازپس می‌گیرید در حالی‌ که از یکدیگر بهره‌مند شده‌اید [و از اسرار یکدیگر آگاهید] و آنان [هنگام عقد ازدواج] از شما پیمانی محکم و استوار گرفته‌اند؟');
INSERT INTO "islamhouse" VALUES(4,22,'4:22','و با زنانی که پدرانتان [با آنها] ازدواج کرده‌اند، [پس از مرگ پدر] ازدواج نکنید؛ مگر آنچه در گذشته [از این نوع ازدواج، پیش از اعلام این حکم] رخ داده است؛ [چرا که] بی‌تردید، این کار، عملی بسیار زشت و مبغوض [الله] و راهی نادرست است.');
INSERT INTO "islamhouse" VALUES(4,23,'4:23','[ازدواج با این زنان] بر شما حرام شده ‌است: مادر و دختر و خواهر و عمه و خاله و دختر برادر و دختر خواهر و مادری که به شما شیر داده‌ است و خواهر رِضاعی و مادر زن و دخترِ همسری که [آن دختر] در خانۀ شما پرورش یافته است و با آن همسر همبستر شده‌اید ـ ولی اگر با آن زن آمیزش نکرده‌اید، گناهی نیست [که با دخترش ازدواج کنید]ـ همچنین همسرِ پسرتان که از پشت خودتان است و ازدواج با دو خواهر [در یک زمان، نیز بر شما حرام است]؛ مگر آنچه در گذشته [از این نوع ازدواج، پیش از اعلام این حکم] رخ داده است [چرا که] بی‌تردید، الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(4,24,'4:24','و زنان شوهردار [نیز بر شما حرامند]؛ مگر زنانی که [کنیزتان هستند و شما از راه اسارت] مالک آنها شده‌اید. [این] فریضۀ الهی است که بر شما مقرر داشته است و غیر از اینها [که گفته شد،] برایتان حلال است که ـ در حالی‌ که پاکدامنی می‌ورزید و زناکار نیستید ـ با اموال خود [زنان دیگر را] طلب کنید. پس آن زنانی را که [به ازدواج درآوردید و] از آنان کام گرفتید، باید مهریه‌شان را به عنوان فریضه‌ای به آنان بدهید و در مورد آنچه بعد از تعیین مهریه [در مورد بیش و کمِ آن] با یکدیگر توافق می‌کنید، گناهی بر شما نیست. بی‌گمان، الله دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(4,25,'4:25','و هر یک از شما از لحاظ مالی توانایی ازدواج با زنان آزادۀ مؤمن را ندارد، با کنیزکان باایمانی که در اختیار شما [یا دیگر مسلمانان] هستند [ازدواج کند]؛ و الله به ایمانتان داناتر است. [شما و آنان] همگی از جنس یكدیگر [و با یکدیگر برابر] هستید؛ پس با اجازۀ سرپرستشان با آنان ازدواج کنید و مهریه‌هایشان را به طور پسندیده به آنان بدهید [به شرط آنکه] پاکدامن باشند نه زناکار، و نه [در زمرۀ] کسانی که پنهانی [برای خود] دوست می‌گیرند. آنگاه چون ازدواج کردند، اگر مرتکب زنا شدند، مجازاتشان [پنجاه تازیانه، یعنی] نصف مجازات زنان آزاد است. این [جواز ازدواج با کنیزان،] برای کسی از شماست که از مشقت [غریزه جنسی و دچار شدن به زنا] بترسد؛ و[لی در مورد ازدواج با کنیزان] اگر شکیبایی [و پاکدامنی] پیشه کنید، برایتان بهتر است؛ و الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(4,26,'4:26','الله می‌خواهد که [احکام خویش را] برایتان روشن سازد و شما را به راه و روش کسانی ‌که پیش از شما بودند راهنمایی کند و توبه تان را بپذیرد؛ و الله دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(4,27,'4:27','و الله می‌خواهد توبۀ شما را بپذیرد [و از آلودگیِ گناه پاکتان نماید]؛ و[لی] کسانی ‌که پیرو شهوات هستند، می‌خواهند که شما دستخوش انحراف بزرگی شوید.');
INSERT INTO "islamhouse" VALUES(4,28,'4:28','الله [با تشریع این احکام] می‌خواهد بر شما آسان بگیرد؛ و انسان ناتوان آفریده شده‌ است.');
INSERT INTO "islamhouse" VALUES(4,29,'4:29','ای کسانی‌ که ایمان آورده‌اید، اموال یکدیگر را به باطل [و از راه‌های نامشروعی همچون غصب و دزدی و رشوه] نخورید؛ مگر اینکه تجارتی با رضایتِ شما [انجام گرفته] باشد؛ و [همچنین] یکدیگر را نکشید و خودکشی نکنید؛ همانا الله [نسبت] به شما مهربان است [و شما را از این کارها بازمی‌دارد].');
INSERT INTO "islamhouse" VALUES(4,30,'4:30','و هر‌ کس [آگاهانه و] از روی تجاوز و ستم چنین کند، به زودی او را در آتشی [سوزان] درمی‌آوریم و این [کار] برای الله آسان است.');
INSERT INTO "islamhouse" VALUES(4,31,'4:31','[ای مؤمنان،] اگر از گناهان بزرگی که از آن نهی شده‌اید دوری کنید، گناهان [صغیره]تان را از شما می‌زداییم و شما را در جایگاهی شایسته [= بهشت] وارد می‌کنیم.');
INSERT INTO "islamhouse" VALUES(4,32,'4:32','[ای مؤمنان،] آنچه را که الله به وسیلۀ آن، برخی از شما را بر برخی دیگر برتری داده‌ است، آرزو مکنید [تا دچار حسد نشوید]. مردان از آنچه به دست آورده‌اند [پاداش و] بهره‌ای دارند و زنان [نیز] از آنچه به دست آورده‌اند [اجر و] بهره‌ای دارند؛ و از بخشش الله [چیزی] بخواهید. بی‌تردید، الله به هر چیزی داناست.');
INSERT INTO "islamhouse" VALUES(4,33,'4:33','ما برای هر‌ کس وارثانی قرار داده‌ایم که از آنچه پدر و مادر و خویشاوندان بر جای گذاشته‌اند ارث ببرند؛ و [نیز] به کسانی که [با آنان] پیمان [برادری و یاری] بسته‌اید، سهمشان را [از ارث] بدهید. بی‌گمان، الله بر هر چیزی گواه است.');
INSERT INTO "islamhouse" VALUES(4,34,'4:34','مردان بر زنان سرپرست و نگهبانند، به دلیل آنکه الله برخی از اینان را بر برخی برتری داده است و [نیز] به دلیل آنکه از اموال خویش [برای زنان] خرج می‌کنند. پس زنانِ صالح، [آنانند که هم از الله و هم از شوهرانشان] فرمانبردارند [و] به پاس آنچه الله [برایشان] حفظ کرده است [اسرار و حقوق شوهران خود را] در غیابِ [آنان] حفظ می‌کنند؛ و [ای شوهران،] به زنانی که از نافرمانی‌شان بیم دارید، پند و اندرز‌ دهید؛ [اگر فرمانبردار نشدند،] در بستر از آنان دوری کنید [و اگر تأثیر نکرد،] آنان را [طوری که آزار نبینند] بزنید؛ پس اگر از شما اطاعت کردند، هیچ راهی برای [بهانه‌جویی و سرزنش] بر آنها مجویید [و بدانید که] به راستی الله بلندمرتبۀ بزرگ است.');
INSERT INTO "islamhouse" VALUES(4,35,'4:35','و [شما ای اولیای زن و مرد،] اگر از اختلاف و جدایی میان آن دو [= زن و شوهر] بیم داشتید، یک داور از خانوادۀ شوهر و یک داور از خانوادۀ زن [تعیین کنید و برای تصمیم‌گیری] بفرستید. اگر این دو [داور] قصد اصلاح [و آشتی بین زوجین] داشته باشند، الله میان آن دو [زن و شوهر] سازگاری خواهد داد. بی‌گمان، الله دانای آگاه است.');
INSERT INTO "islamhouse" VALUES(4,36,'4:36','و الله را عبادت کنید و چیزی را با او شریک نگردانید و به پدر و مادر نیکی کنید و [نیز] به خویشاوندان و یتیمان و مستمندان و همسایۀ خویشاوند و همسایۀ غیرخویشاوند و [دوست و] همنشین و [مسافرِ] درراه‌مانده و [کنیزان و] بردگانی که مالک آنها هستید [نیکی کنید و بدانید که] یقیناً الله کسی را که متکبر و فخرفروش باشد دوست ندارد.');
INSERT INTO "islamhouse" VALUES(4,37,'4:37','[همان] کسانی ‌که بخل می‌ورزند و مردم را به بخل وامی‌دارند و آنچه [از ثروت و دانش] را که الله از فضل خویش به آنان بخشیده است پنهان می‌کنند؛ و ما برای کافران [و ناسپاسان،] عذابی خفت‌بار مهیّا کرده‌ایم.');
INSERT INTO "islamhouse" VALUES(4,38,'4:38','و [نیز برای] کسانی ‌که اموالشان را برای [خودنمایی و] نشان‌دادن به مردم انفاق می‌کنند و به الله و روز آخرت ایمان ندارند؛ [چنین شخصی یارِ شیطان است] و کسی ‌که شیطان همدمش باشد، چه بد همدمی دارد!');
INSERT INTO "islamhouse" VALUES(4,39,'4:39','و برای آنان چه زیانی داشت اگر به الله و روز آخرت ایمان می‌آوردند و از آنچه الله به آنان روزی داده است انفاق می‌کردند؟ و الله به [حال و اعمال] اینان داناست.');
INSERT INTO "islamhouse" VALUES(4,40,'4:40','بی‌گمان، الله به ‌اندازۀ ذره‌ای [به بندگانش] ستم نمی‌کند و اگر [ذره‌ای] کار نیک باشد، آن را چندین برابر می‌کند و از پیشگاه خود [به آنان] پاداش بزرگی عطا می‌کند.');
INSERT INTO "islamhouse" VALUES(4,41,'4:41','و [حال و روزِ ناباوران] چگونه باشد آنگاه که از هر امتی شاهدی را [که پیامبر همان امت باشد] به میان ‌آوریم و تو را [نیز ای پیامبر، دربارۀ ابلاغ پیام الهی] بر اینان گواه ‌آوریم‌؟');
INSERT INTO "islamhouse" VALUES(4,42,'4:42','آن روز کسانی ‌که کافر شدند و از پیامبر نافرمانی کردند، آرزو می‌کنند که ای کاش با خاک یکسان می‌شدند و هیچ سخنی را نمی‌توانند از الله پنهان کنند.');
INSERT INTO "islamhouse" VALUES(4,43,'4:43','ای کسانی‌ که ایمان آورده‌اید، در حال مستی به نماز نزديک نشويد تا زمانی که بدانید چه می‌گویید و [همچنین] هنگامی که جُنُب هستید، تا غسل نکرده‌اید، به نماز [و مسجد] نزدیک نشوید مگر آنکه رهگذر باشید؛ و اگر بیمار یا مسافر بودید یا یکی از شما از مکان قضای حاجت آمد یا با زنان آمیزش کردید و [برای وضو یا غسل] آب نیافته‌اید، پس [باید] با خاکی پاک تیمم کنید؛ [به این روش که] چهره و دست‌هایتان را [با خاک] مسح نمایید. بی‌گمان، الله بخشایندۀ آمرزگار ‌است.');
INSERT INTO "islamhouse" VALUES(4,44,'4:44','[ای پیامبر،] آیا کسانی [از یهود] را که بهره‌ای از کتاب به آنها داده شده ‌است ندیده‌ای [که چگونه] گمراهی را می‌خرند و می‌خواهند شما [مؤمنان نیز مانند آنان] گمراه شوید؟');
INSERT INTO "islamhouse" VALUES(4,45,'4:45','و [ای مؤمنان،] الله به دشمنان شما داناتر است و کافی است که الله یار [و کارسازتان] باشد و کافی است که الله یاور [و مددکارتان] باشد.');
INSERT INTO "islamhouse" VALUES(4,46,'4:46','برخی از یهود، کلمات [الله] را از جای خود تحریف می‌کنند و با پیچاندن زبان خود و به قصد طعنه‌زدن در دین [با درآمیختن زبان عبرى با عربى، به پیامبر‌] مى‌گویند: «شنیدیم و نافرمانى كردیم» و «بشنو كه [كاش‌] ناشنوا گردى!» و [نیز به تمسخر مى‌گویند:] «راعِنا» [که در عبری مفهوم توهین‌آمیزی دارد؛] ولی اگر آنان می‌گفتند: «شنیدیم و اطاعت کردیم» و «[سخنان ما را] بشنو و به ما مهلت بده»، قطعاً برایشان بهتر و درست‌تر بود؛ ولی الله آنان را به خاطر کفر‌شان لعنت کرده‌ است؛ پس جز [گروهی] اندک ایمان نمی‌آورند.');
INSERT INTO "islamhouse" VALUES(4,47,'4:47','ای اهل کتاب، به آنچه که [بر محمد] نازل کردیم و کتاب آسمانی شما را تصدیق می‌کند ایمان بیاورید، پیش از آنکه [اندام‌های حسّی] چهره‌ها را محو کنیم و آنها را [همچون] پشت سرشان [صاف و زشت] بگردانیدم؛ یا آنان را همچون اصحاب سَبت [= یهودیان نافرمانی که روز شنبه ماهی گرفتند] لعنت کنیم [و به شکل میمون درآوریم]؛ و [بدانید که] فرمان الله قطعاً به وقوع می‌پیوندد.');
INSERT INTO "islamhouse" VALUES(4,48,'4:48','بی‌گمان، الله این گناه را که به او شرک آورده شود نمی‌بخشد و غیر از آن را ـ برای هر ‌کس بخواهد ـ می‌بخشد؛ و هر ‌کس به الله شرک ورزد، یقیناً گناهی بزرگ بربافته است.');
INSERT INTO "islamhouse" VALUES(4,49,'4:49','[ای پیامبر،] آیا ندیدی کسانی را که خویشتن را پاک می‌شمارند [و خودستایی می‌کنند؟ چنین نیست؛] بلکه الله هر ‌کس را بخواهد پاک می‌گردانَد، و به آنان به اندازۀ ذره‌ای ناچیز [حتی به اندازۀ نخ روی هستۀ خرما] ستم نمی‌شود.');
INSERT INTO "islamhouse" VALUES(4,50,'4:50','[ای پیامبر،] بنگر که چگونه بر الله دروغ می‌بندند! و همین [عمل به عنوان] گناهی آشکار کافی است.');
INSERT INTO "islamhouse" VALUES(4,51,'4:51','آیا افرادی [از قوم یهود] را که بهره‌ای از کتاب [آسمانی] به آنان داده شده است ندیده‌ای [که چگونه] به جِبت [= سحر] و طاغوت [= معبودان باطل] ایمان می‌آورند؟ [این افراد از روی دوستی و مدارا] دربارۀ کسانی‌ که کفر ورزیده‌اند می‌گویند: «اینان از کسانی‌ که ایمان آورده‌اند هدایت‌یافته‌ترند».');
INSERT INTO "islamhouse" VALUES(4,52,'4:52','آنان کسانی هستند که الله لعنتشان کرده است و هر ‌که را الله لعنت کند، هرگز برایش یاوری نخواهی یافت.');
INSERT INTO "islamhouse" VALUES(4,53,'4:53','آیا آنان بهره‌ای از حكومت [= فرمانروایی] دارند؟ [اگر هم داشتند،] به قدر نقطه پشت هسته خرما [چيزى‌] به مردم نمى‌دادند.');
INSERT INTO "islamhouse" VALUES(4,54,'4:54','آیا به مردم [= محمد و یارانش] به خاطر آنچه الله از فضل خویش به آنان بخشیده است حسد می‌ورزند؟ در حقیقت، ما [پیشتر نیز] به خاندان ابراهیم کتاب و حکمت دادیم و به آنان فرمانرواییِ بزرگی بخشیدیم.');
INSERT INTO "islamhouse" VALUES(4,55,'4:55','پس برخی از آنان به وی ایمان آوردند و برخی از آنان از او روی گرداندند [پس نسبت به دعوت محمد نیز چنین کردند] و [برای این کافران] آتش افروختۀ دوزخ کافی است.');
INSERT INTO "islamhouse" VALUES(4,56,'4:56','بی‌گمان، کسانی‌ را که به آیات ما کفر ورزیدند، در آتشی [سوزان] وارد خواهیم کرد که هر چه پوستشان بریان گردد، پوست‌های دیگری به جایش قرار می‌دهیم تا [طعم] عذاب را بچشند. به راستی که الله، شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(4,57,'4:57','و کسانی‌ را که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، به باغ‌هایی [از بهشت] وارد خواهیم نمود که از زیر [درختان] آن جویبارها جاری است؛ همواره در آن خواهند ماند و در آنجا همسران پاکیزه دارند و آنان را زیر سایه‌هایی گسترده [و انبوه] جای می‌دهیم.');
INSERT INTO "islamhouse" VALUES(4,58,'4:58','الله به شما فرمان می‌دهد که امانت‌ها را [به طور کامل] به صاحبانشان بازگردانید و هنگامی ‌که میان مردم داوری می‌کنید، به عدالت داوری کنید. الله شما را به امری نیک پند می‌دهد. به راستی که الله شنوای بیناست.');
INSERT INTO "islamhouse" VALUES(4,59,'4:59','ای کسانی‌ که ایمان آورده‌اید، از الله و پیامبر و کارگزاران [و فرماندهان مسلمانِ] خود اطاعت کنید و اگر در چیزی اختلاف کردید، آن را به [کتاب] الله و [سنت] پیامبر بازگردانید. اگر به الله و روز قیامت ایمان دارید، [یقین داشته باشید که] این [بازگشت به قرآن و سنت، برایتان] بهتر و خوش‌فرجام‌تر است.');
INSERT INTO "islamhouse" VALUES(4,60,'4:60','[ای پیامبر،] آیا ندیدی افرادی را که گمان می‌کنند به آنچه بر تو نازل شده و [به] آنچه پیش از تو نازل شده،‌ ایمان آورده‌اند، و[لی] می‌خواهند برای داوری [به جای شریعت الهی] نزد طاغوت بروند؟ [و حُکم باطل را به جای حُکم الله بپذیرند] با آنکه به آنان دستور داده شده است که به طاغوت کفر ورزند؛ و[لی] شیطان می‌خواهد آنان را در گمراهی دور و درازی قرار دهد.');
INSERT INTO "islamhouse" VALUES(4,61,'4:61','و چون به آنان گفته شود: «به سوی آنچه الله نازل کرده است [= قرآن] و به سوی پیامبر [او] بیایید» منافقان را می‌بینی که از تو سخت روی می‌گردانند.');
INSERT INTO "islamhouse" VALUES(4,62,'4:62','اما چگونه است که وقتی به [سزای] کارهای بدشان مصیبتی به آنان می‌رسد، نزد تو می‌آیند و به الله سوگند یاد می‌کنند که: «منظور ما [از دادخواهی نزد دیگران، چیزی] جز نیکی و توافق [میان طرفین] نبوده ‌است»؟');
INSERT INTO "islamhouse" VALUES(4,63,'4:63','اینان همان کسانی هستند که الله می‌داند چه در دل دارند؛ پس [ای پیامبر،] از آنان روی بگردان و[لی] اندرزشان بده و با بیانی رسا [نتایجِ] کردار‌شان را بر آنان گوشزد کن.');
INSERT INTO "islamhouse" VALUES(4,64,'4:64','و هیچ پیامبری را نفرستادیم مگر به این هدف که به فرمان الله [مردم از وی] اطاعت کنند؛ و اگر هنگامی‌ که آنان [با گناه و نافرمانی] بر خویشتن ستم می‌کردند، [در حالی‌که زنده هستی] نزد تو می‌آمدند و از الله آمرزش می‌خواستند، و پیامبر هم برایشان آمرزش می‌طلبید؛ بی‌تردید، الله را توبه‌پذیرِ مهربان می‌یافتند.');
INSERT INTO "islamhouse" VALUES(4,65,'4:65','نه؛ سوگند به پروردگارت [ای محمد] که آنان ایمان نمی‌آورند مگر اینکه در اختلافاتشان تو را داور قرار دهند و از داوری تو در دلِ خویش احساس ناراحتی نکنند و کاملاً تسلیم [حکم تو] باشند.');
INSERT INTO "islamhouse" VALUES(4,66,'4:66','و اگر [مانند بنی‌اسرائیل] بر آنان مقرر می‌کردیم که «[نافرمانانِ] خود را بکشید یا از خانه و شهرتان بیرون بروید»، جز اندکی از آنان [به آن دستورها] عمل نمی‌کردند؛ و اگر به آنچه اندرز داده شدند عمل می‌کردند، بی‌تردید برایشان بهتر و در پایداری‌شان [بر دین] مؤثرتر بود.');
INSERT INTO "islamhouse" VALUES(4,67,'4:67','و در این صورت، از نزد خود پاداش بزرگی به آنان می‌دادیم،');
INSERT INTO "islamhouse" VALUES(4,68,'4:68','و به راه راست هدایتشان می‌کردیم.');
INSERT INTO "islamhouse" VALUES(4,69,'4:69','و کسی‌ که الله و پیامبر را اطاعت کند [روز قیامت] همنشین کسانی خواهد بود که الله آنان را گرامی داشته است؛ [یعنی] با پیامبران و صدّیقان [= تصدیق‌کنندگان پیامبر] و شهیدان و صالحان؛ و اینان چه نیکو رفیقانی هستند!');
INSERT INTO "islamhouse" VALUES(4,70,'4:70','این فضل و بخشش از جانب الله است و همین بس که الله [به احوال بندگان] داناست.');
INSERT INTO "islamhouse" VALUES(4,71,'4:71','ای کسانی‌ که ایمان آورده‌اید، سلاح خود را برگیرید؛ سپس گروه‌گروه یا یکپارچه [برای جهاد با دشمن] رهسپار گردید.');
INSERT INTO "islamhouse" VALUES(4,72,'4:72','و [ای مسلمانان،] بی‌تردید، از [میان] شما کسی هست که [به خاطر ترس، از شرکت در میدان جهاد] سستی می‌ورزد و اگر مصیبتی به شما برسد می‌گوید: «الله به من لطف کرد که همراه آنان [در جنگ] حاضر نبودم».');
INSERT INTO "islamhouse" VALUES(4,73,'4:73','و[لی] اگر بخشش و غنیمتی از جانب الله به شما رسد ـ چنان که گویا هرگز میان شما و او [رابطۀ] دوستی نبوده است ـ [با حسرت] می‌گوید: «ای کاش من نیز همراه آنان بودم و به کامیابی بزرگی می‌رسیدم».');
INSERT INTO "islamhouse" VALUES(4,74,'4:74','پس کسانی‌ که زندگی دنیا را به آخرت فروخته‌اند، باید در راه الله پیکار کنند؛ و به هر کسی‌ که در راه الله پیکار کند ـ خواه کشته شود یا پیروز گردد ـ به زودی پاداش بزرگی خواهیم داد.');
INSERT INTO "islamhouse" VALUES(4,75,'4:75','و [ای مؤمنان،] شما را چه شده ‌است که در راه الله و [برای یاریِ] مردان و زنان و کودکان ستمدیده پیکار نمی‌کنید؟ همان کسانی که می‌گویند: «پروردگارا، ما را از این شهر [مکه] که اهالی‌اش ستمکارند بیرون ببر و از سوی خویش برای ما یار و کارسازی قرار بده و از جانب خود یاور و مددکاری برایمان بگمار».');
INSERT INTO "islamhouse" VALUES(4,76,'4:76','کسانی ‌که ایمان آورده‌اند، در راه الله پیکار می‌کنند و کافران، در راه طاغوت [و حاکمان خودکامه] می‌جنگند؛ پس با یاران شیطان بجنگید که قطعاً نیرنگ [و نقشۀ] شیطان ضعیف است.');
INSERT INTO "islamhouse" VALUES(4,77,'4:77','[ای پیامبر،] آیا کسانی را ندیدی که [در مکه] به آنها گفته شد: «[اکنون] دست [از جنگ] بازدارید و نماز برپا دارید و زکات بپردازید» و[لی] چون [در مدینه] جهاد بر آنان مقرر شد، آنگاه گروهی از آنان از مردم [مشرکِ مکه] ‌ترسیدند ـ مانند ترس از الله یا [حتی] بیشتر ـ و گفتند: «پروردگارا، چرا جهاد را بر ما مقرر نمودی؟ چرا تا مدتی کوتاه به ما مهلت ندادی [تا از نعمت‌های دنیوی بهره‌مند گردیم]؟» [به آنان] بگو: «برخورداری [از لذت‌های این] دنیا ناچیز است و برای کسی‌ که پرهیزگار باشد سرای آخرت بهتر است و [در آنجا] به قدر نخ هسته خرمايى به شما ستم نخواهد شد.');
INSERT INTO "islamhouse" VALUES(4,78,'4:78','هر کجا باشید، مرگ شما را درمی‌یابد؛ هر چند که در برج‌های استوار باشید؛ و اگر به آنان [= منافقان] خیری [همچون فرزند و مال فراوان] برسد، می‌گویند: «این از جانب الله است» و اگر شری [همچون مرگ و زیان مالی] به ایشان برسد [رسول الله را بَدْیُمن می‌دانند و به او] می‌گویند: «این از جانب توست». [ای پیامبر، به آنان] بگو: «همه [چیز] از جانب الله است». چرا این قوم حاضر نیستند سخنی را درک کنند؟');
INSERT INTO "islamhouse" VALUES(4,79,'4:79','[ای انسان،] آنچه از نیکی‌ها به تو می‌رسد، از جانب الله است و آنچه از بدی‌ها به تو می‌رسد، از جانب خود توست؛ و [ای پیامبر،] ما تو را برای [هدایتِ] مردم فرستادیم و گواهی الله [در این ‌باره] کافی است.');
INSERT INTO "islamhouse" VALUES(4,80,'4:80','هر‌ کس از پیامبر اطاعت کند، در حقیقت، از الله اطاعت کرده‌ است؛ و هر‌ کس رویگردان شود، [از او اندوهگین نباش ای پیامبر؛ زیرا ما هرگز] تو را بر آنان نگهبان [و مراقب] نفرستادیم.');
INSERT INTO "islamhouse" VALUES(4,81,'4:81','آنان [نزد تو] می‌گویند: «فرمانبرداریم»، ولی چون از نزد تو بیرون می‌روند، گروهی از آنان شبانه چیزی جز آنچه تو گفته‌ای نقشه مى‌كشند؛ و الله هر چه را كه شبانه، تدبیر می‌کنند [در پرونده اعمالشان] می‌نویسد. پس از آنان روی بگردان و بر الله توکل کن و همین بس که الله [مراقب و] کارساز [تو] باشد.');
INSERT INTO "islamhouse" VALUES(4,82,'4:82','آیا در قرآن نمی‌اندیشند که اگر از سوی [کسی] غیر از الله بود، قطعاً اختلاف [و تناقض] بسیاری در آن می‌یافتند؟');
INSERT INTO "islamhouse" VALUES(4,83,'4:83','هنگامی ‌که خبری [حاکی] از ایمنی [و پیروزی] یا ترس [و شکست] به آنان می‌رسد، [بدون هیچ تحقیقی] آن را فاش می‌سازند؛ و[لی] اگر آن را به پیامبر و فرماندهانشان باز‌گردانند، افرادی از آنان که اهل [فهم و] استنباط هستند قطعاً [مصلحتِ اعلام یا کتمان اخبار را بهتر] می‌دانند؛ و [ای مؤمنان،] اگر فضل و رحمت الله بر شما نبود، جز اندکی، [همگی] از شیطان پیروی می‌کردید.');
INSERT INTO "islamhouse" VALUES(4,84,'4:84','پس [ای پیامبر،] در راه الله پیکار کن. تو عهده‌دارِ کسی جز خود نیستی و[لی] مؤمنان را [نیز به جهاد] تشویق کن. امید است که الله آسیب کافران را از شما بازدارد؛ و الله قدرتش، بیشتر و کیفرش سخت‌تر است.');
INSERT INTO "islamhouse" VALUES(4,85,'4:85','کسی‌ که شفاعت [= میانجی‌گریِ] پسندیده‌ای کند، از [خیرِ] آن نصیبی خواهد داشت و کسی ‌که شفاعت ناپسندی کند، از [شرِ] آن سهمی خواهد داشت؛ و الله همواره بر هر چیزی شاهد و مراقب است.');
INSERT INTO "islamhouse" VALUES(4,86,'4:86','[ای مؤمنان،] هر گاه با درودی صمیمانه به شما سلام گویند، شما پاسخی بهتر ـ و یا همانند آن‌ ـ بگویید؛ [چرا که] بی‌تردید، الله همواره بر هر چیزی حسابگر است.');
INSERT INTO "islamhouse" VALUES(4,87,'4:87','الله [که معبودِ راستین است و] هیچ معبودی [به‌حق] جز او نیست، یقیناً همگی شما را روز قیامت ـ که تردیدی در [وقوعِ] آن نیست ـ جمع می‌کند؛ و کیست که از الله راستگوتر باشد؟');
INSERT INTO "islamhouse" VALUES(4,88,'4:88','[ای مؤمنان،] شما را چه شده است که در مورد [نحوۀ رفتار با] منافقان دو گروه شده‌اید، حال ‌آنکه الله آنان را به خاطر آنچه انجام داده‌اند سرنگون ساخته [و به کفر و گمراهی بازگردانده] ‌است؟ آیا می‌خواهید کسی را که الله گمراه کرده‌ است هدایت کنید؟ و[لی] هر کس که الله او را گمراه کند، راهی [به سوی هدایت] برایش نخواهی یافت.');
INSERT INTO "islamhouse" VALUES(4,89,'4:89','آنان [= منافقان] آرزو می‌کنند که شما نیز همچون خودشان کفر بورزید تا با هم برابر شوید؛ پس هیچ‌یک از آنان را به دوستی نگیرید، مگر آنکه [مسلمان شوند و] در راه الله هجرت کنند؛ پس اگر از [اسلام و هجرت] روی برتافتند [و رفتارشان را ادامه دادند]، آنها را هر جا که یافتید، دستگیر کنید و بکشید و دوست و یاوری [از میان] آنان اختیار نکنید.');
INSERT INTO "islamhouse" VALUES(4,90,'4:90','مگر آنهایی که با هم‌پیمانان شما پیمان بسته‌اند یا آنهایی که نزدتان می‌آیند در حالی که از جنگ با شما یا قوم خود به تنگ آمده‌اند؛ و اگر الله می‌خواست، آنان را بر شما مسلط می‌کرد که با شما بجنگند؛ پس اگر از شما کناره‌گیری کردند و با شما نجنگیدند و تسلیم [و فرمانبردارِ] شما شدند، [بدانید که دیگر] الله برای شما راهی [برای تجاوز] بر آنان قرار نداده ‌است.');
INSERT INTO "islamhouse" VALUES(4,91,'4:91','[ای مؤمنان،] به زودی [گروه] دیگری [از منافقان] را خواهید یافت که [ظاهراً] می‌خواهند از شما و قومِ خود در امان باشند؛ و[لی] هر گاه به فتنه [شرک] بازگردانده شوند، در آن فرو می‌روند. پس اگر از شما کناره نگرفتند و از درِ صلح و آشتی وارد نشدند و دست‌ از [جنگ با] شما برنداشتند، آنگاه آنان را هر کجا یافتید، دستگیر کنید و بکشید. آنان هستند كه ما براى شما علیه آنان دلیلی آشكار [برای دستگیری و قتلشان] قرار داده‌ایم.');
INSERT INTO "islamhouse" VALUES(4,92,'4:92','هیچ مؤمنی مجاز نیست که مؤمنی را بکشد، مگر به اشتباه [و غیرعمد]؛ و کسی‌ که مؤمنی را به اشتباه بکشد، باید یک بردۀ مؤمن آزاد کند و خون‌بهایی به خانوادۀ او [= به وارثان مقتول] بپردازد؛ مگر اینکه آنها گذشت کنند؛ و اگر [مقتول] از قومی باشد که دشمنان شما هستند و [خودِ] او مؤمن است، آزاد کردنِ یک بردۀ مؤمن [توسط قاتل به عنوان کفاره کافی است و پرداخت خون‌بها به وارثانش لازم نیست] و اگر [مقتول مؤمن نباشد ولی] از قومی باشد که میان شما و آنان پیمانی برقرار است، باید خون‌بهایی به خانوادۀ او بپردازد و یک بردۀ مؤمن را [نیز] آزاد کند؛ و هر کس [توان مالی نداشت یا برده‌ای برای آزاد کردن] نیافت، [باید] به عنوان توبه‌ای به درگاه الله، دو ماه پیاپی روزه بگیرد؛ و الله همواره دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(4,93,'4:93','و هر کس مؤمنی را عمداً به قتل برساند، کیفرش دوزخ است [که] در آن جاودانه می‌مانَد و الله بر وی خشم می‌گیرد و او را از رحمت خویش دور می‌سازد و عذاب بزرگی برایش آماده ساخته است.');
INSERT INTO "islamhouse" VALUES(4,94,'4:94','ای کسانی‌ که ایمان آورده‌اید، هنگامی ‌که در راه الله [برای جهاد] رهسپار شدید، [جوانب امر را به دقت] بررسی کنید و به کسی‌ که اظهار صلح و اسلام نمود، نگویید: «تو مؤمن نیستی» تا [به این بهانه بتوانید او را بکشید و غنیمت و] سرمایۀ ناپایدار زندگی دنیا را به دست آورید؛ زیرا غنیمت‌های بسیاری [برای شما] نزد الله است. شما نیز قبلاً اینچنین بودید [و ایمانتان را پنهان می‌کردید]؛ سپس الله بر شما منت نهاد [و هدایت شدید]. بنابراین به درستی بررسی کنید؛ [چرا که] بی‌تردید، الله به آنچه انجام می‌دهید آگاه است.');
INSERT INTO "islamhouse" VALUES(4,95,'4:95','آن مؤمنان [خانه‌نشینی] که بدون هیچ بیماری و رنجی از جهاد بازنشستند، با مجاهدانی که در راه الله با مال و جان خود جهاد کردند [هرگز] یکسان نیستند. الله کسانی را که با مال و جانشان جهاد کردند بر خانه‌نشینان به مرتبه‌ای [بزرگ] برتری بخشیده است و الله به هر یک [مجاهدان یا خانه‌نشینانِ معذور] وعدۀ [پاداش] نیکو داده ‌است و[لی] الله مجاهدان را بر خانه‌نشینان با پاداشی بزرگ برتری داده ‌است.');
INSERT INTO "islamhouse" VALUES(4,96,'4:96','[پاداشی كه‌] به عنوان درجات و آمرزش و رحمتى از [پیشگاه] او [نصیبشان مى‌گردد] و الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(4,97,'4:97','کسانی كه [با ماندن در سرزمین کفر] نسبت به خویش ستمكار بوده‌اند، [وقتى‌] فرشتگان جانشان را مى‌گیرند [به آنان] مى‌گویند: «در چه [حال‌] بودید؟» پاسخ مى‌دهند: «ما در زمین از ستمدیدگان بودیم». [فرشتگان] مى‌گویند: «آیا زمینِ الله گسترده نبود تا در آن مهاجرت كنید [و ایمانتان را حفظ نمایید]؟» پس جایگاهشان دوزخ است و چه بد سرانجامى است!');
INSERT INTO "islamhouse" VALUES(4,98,'4:98','مگر آن مردان و زنان و کودکان ستم‌دیده‌ای که نمی‌توانند چاره‌ای بیندیشند و راه به جایی نمی‌برند.');
INSERT INTO "islamhouse" VALUES(4,99,'4:99','پس آنان [كه عذرى دارند] امید است الله از اینان درگذرد و الله همواره بخشایندۀ آمرزگار ‌است.');
INSERT INTO "islamhouse" VALUES(4,100,'4:100','و هر کس در راه الله هجرت کند، در زمین سرپناه بسیار و گشایش[ی در مصالح دین و دنیا] می‌یابد و کسی‌ که به قصد هجرت به سوی الله و پیامبرش از خانه‌اش خارج شود و آنگاه مرگش فرا رسد، قطعاً پاداش او بر [عهدۀ] الله است و الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(4,101,'4:101','و [ای مؤمنان،] هر گاه در زمین سفر ‌کردید، اگر ترسیدید کسانی که کفر ورزیدند زیانی به شما برسانند، گناهی بر شما نیست که از [رکعات] نماز بکاهید [و آن را شکسته بخوانید]؛ زیرا کافران همواره برایتان دشمن آشکاری هستند.');
INSERT INTO "islamhouse" VALUES(4,102,'4:102','و [ای پیامبر،] چون [وقت جنگ] در میان آنان بودى و برایشان نماز برپا داشتى، باید گروهى از آنان با تو [به نماز] بایستند و [همچنین] باید جنگ‌افزارهایشان را با خود برگیرند [و یک رکعت را همراه تو بخوانند و نماز را به صورت فُرادی ادامه دهند] و چون سجده کردند [و نمازشان تمام شد]، باید پشت سرتان قرار گیرند و آن دسته دیگری که نماز نخوانده‌اند باید بیایند و [یک رکعت] با تو نماز بخوانند [و پس از اینکه نمازت به پایان رسید، ادامۀ نمازشان را کامل کنند] و [آنان نیز] باید احتیاط کنند و جنگ‌افزارهایشان را [در نماز با خود] برگیرند؛ [زیرا] کافران آرزو دارند که شما از جنگ‌افزارها و تجهیزات خود غافل شوید تا یکباره بر شما بتازند؛ و اگر از باران [یا موانع دیگر] در رنج بودید و [یا] بیمار [و مجروح] بودید، گناهی بر شما نیست که جنگ‌افزارهاى خود را [بر زمین] بگذارید؛ ولی جانب احتیاط را رعایت کنید. به راستی که الله برای کافران عذابی خفت‌بار مهیّا کرده ‌است.');
INSERT INTO "islamhouse" VALUES(4,103,'4:103','چون نماز را به پایان رساندید، الله را [در همه حال،] ایستاده و نشسته و بر پهلو خفته [= خوابیده] یاد کنید؛ و هر گاه آرامش یافتید [و ترس از بین رفت]، نماز را [کامل] بر پا دارید [چرا که] بی‌تردید، نماز [فریضه‌ای است که] در اوقات معیّنی بر مؤمنان واجب شده ‌است.');
INSERT INTO "islamhouse" VALUES(4,104,'4:104','و [ای مؤمنان،] در تعقیب آن گروه [دشمن] سستی نکنید. اگر شما [از خستگی و جراحت] رنج می‌برید، آنان نیز چنان که شما رنج می‌برید رنج برده‌اند؛ ولی شما از الله چیزی را امید دارید که آنان ندارند؛ و الله دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(4,105,'4:105','[ای پیامبر،] ما [این] کتاب را به‌حق بر تو نازل کردیم تا به [موجبِ] آنچه الله به تو آموخته است در میان مردم داوری کنی؛ و مدافع خیانتکاران مباش.');
INSERT INTO "islamhouse" VALUES(4,106,'4:106','و از الله آمرزش بخواه. بی‌تردید، الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(4,107,'4:107','و از کسانی ‌که [با ارتکاب گناه] به خود خیانت می‌کنند دفاع نکن [زیرا] بی‌گمان، الله هر ‌کس را که خیانتکار [و] گناهکار باشد دوست ندارد.');
INSERT INTO "islamhouse" VALUES(4,108,'4:108','آنها [زشتکاری‌های خود را] از مردم پنهان می‌دارند، ولی نمی‌توانند چیزی را از الله پنهان کنند؛ و [حال آنکه] او تعالی همراهشان است آنگاه که شبانه چاره‌اندیشی می‌کنند و [از سرِ دروغ و افترا] سخنانی می‌گویند که او نمی‌پسندد؛ و الله همواره به آنچه می‌کنند احاطه دارد.');
INSERT INTO "islamhouse" VALUES(4,109,'4:109','هان! شما کسانی هستید که در زندگی دنیا از آنان دفاع کردید؛ پس کیست که روز قیامت در برابر الله از آنان دفاع کند؟ یا چه کسی است که [مراقب و‌] کارسازشان باشد؟');
INSERT INTO "islamhouse" VALUES(4,110,'4:110','و هر کس عمل ناشایستی مرتکب شود یا [با نافرمانی از دستور الله و رسولش] به خود ستم کند [و] سپس از الله آمرزش بخواهد، الله را آمرزندۀ مهربان خواهد یافت.');
INSERT INTO "islamhouse" VALUES(4,111,'4:111','و هر کس گناهی مرتکب شود، تنها به زیان خویش [چنین] می‌کند؛ و الله همواره دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(4,112,'4:112','و هر کس خطا یا گناهی مرتکب شود، سپس آن را بر گردن بی‌گناهی بیندازد، قطعاً [بارِ] بهتان و گناه آشکاری را بر دوش گرفته است.');
INSERT INTO "islamhouse" VALUES(4,113,'4:113','و [ای پیامبر،] اگر فضلِ الله و رحمتش بر تو نبود، گروهی از آنان قصد داشتند که تو را گمراه کنند [تا ناعادلانه قضاوت کنی]؛ و[لى‌] جز خودشان [كسى‌] را گمراه نمى‌سازند و هیچ زیانی به تو نمی‌رسانند؛ و الله کتاب [= قرآن] و حکمت [= سنت] را بر تو نازل کرد و آنچه را نمی‌دانستی به تو آموخت؛ و همواره فضلِ الله بر تو بزرگ [و بسیار] بوده ‌است.');
INSERT INTO "islamhouse" VALUES(4,114,'4:114','در بسیاری از [سخنان درگوشی و] نجواهای آنان خیری نیست، مگر [در گفتارِ] كسى كه به [دادنِ] صدقه‌ای یا كاری پسندیده یا سازشى میان مردم فرمان دهد؛ و هر کس برای رضایت الله چنین کند، به زودی پاداش بزرگی به وی خواهیم داد.');
INSERT INTO "islamhouse" VALUES(4,115,'4:115','و هر کس ‌پس از آنکه [راه حق و] هدایت برایش روشن شد با پیامبر مخالفت ورزد و از راهی جز راه مؤمنان پیروی کند، او را به آنچه [برای خود برگزیده و از آن] پیروی کرده است وامی‌گذاریم و به دوزخ می‌کشانیم؛ و [حقا که دوزخ] چه بد جایگاهی است!');
INSERT INTO "islamhouse" VALUES(4,116,'4:116','الله این [گناه] را كه به او شرک آورده شود، قطعاً نمى‌آمرزد و غیر آن [هر گناهی] را برای هر ‌کس که بخواهد می‌آمرزد؛ و هر ‌کس به الله شرک آورَد، بی‌تردید در گمراهی دور و درازی افتاده‌ است.');
INSERT INTO "islamhouse" VALUES(4,117,'4:117','[مشرکان] به جای الله بُت‌های مادینه‌ای [همچون لات و عُزّی و منات] را می‌خوانند و [در حقیقت،] جز شیطان سرکش را نمی‌خوانند.');
INSERT INTO "islamhouse" VALUES(4,118,'4:118','[همان که] الله او را از رحمت خویش دور ساخت و او گفت: «از بندگان تو، سهمی معیّن برخواهم گرفت.');
INSERT INTO "islamhouse" VALUES(4,119,'4:119','و مسلماً آنان را گمراه می‌کنم و به آرزوهای باطل می‌افکنم؛ و به آنان دستور می‌دهم که گوش چهارپایان را بشکافند و وادارشان می‌کنم تا آفرینش الله را تغییر دهند»؛ و هر ‌کس شیطان را به جای الله دوست [و کارساز] خود گیرد، قطعاً زیانی آشکار کرده ‌است.');
INSERT INTO "islamhouse" VALUES(4,120,'4:120','[آرى،] شیطان به آنان وعده مى‌دهد و آنها را در [دامِ] آرزوها می‌اندازد و به آنان وعده‌ای جز فریب نمى‌دهد.');
INSERT INTO "islamhouse" VALUES(4,121,'4:121','آنان [= پیروان شیطان] جایگاهشان دوزخ است و هیچ راه فراری از آن نمی‌یابند.');
INSERT INTO "islamhouse" VALUES(4,122,'4:122','و کسانی‌ که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، به زودی آنان را در باغ‌هایی [از بهشت] وارد می‌کنیم که جویبارها از زیر [درختان] آن جاری است؛ جاودانه در آن خواهند ماند؛ وعدۀ الله حق است و کیست که در گفتار [و وعده‌هایش] از الله راستگوتر باشد؟');
INSERT INTO "islamhouse" VALUES(4,123,'4:123','[ای مسلمانان، نجات و رستگاری وابسته] به آرزوهای شما و آرزوهای اهل کتاب نیست. هر‌ کس کار ناشایستی انجام دهد، به کیفرش خواهد رسید و دوست و یاوری جز الله برای خود نخواهد یافت.');
INSERT INTO "islamhouse" VALUES(4,124,'4:124','و هر کس ـ چه مرد و چه زن ـ که کارهای شایسته کند و مؤمن باشد، [همۀ] آنان وارد بهشت می‌شوند و به اندازۀ ذرۀ ناچیزی [حتی به اندازۀ شکافِ پشت هستۀ خرمایی نیز] به آنان ستم نخواهد شد.');
INSERT INTO "islamhouse" VALUES(4,125,'4:125','و دین چه کسی بهتر از [دین] کسی است که خالصانه خود را تسلیم الله كرده و نیكوكار است و از آیین ابراهیمِ حق‌گرا پیروى نموده است؟ و الله ابراهیم را به دوستی خالصانه خود برگزید.');
INSERT INTO "islamhouse" VALUES(4,126,'4:126','و آنچه در آسمان‌ها و زمین است از آنِ الله است و الله همواره به هر چیزی احاطه دارد.');
INSERT INTO "islamhouse" VALUES(4,127,'4:127','و [ای پیامبر، مسلمانان] از تو دربارۀ زنان نظرخواهی می‌کنند؛ بگو: «الله درباره آنان فتوا می‌دهد و در آنچه از کتاب [الله] بر شما خوانده می‌شود [نیز] آمده است که [حکمِ او] دربارۀ دختران یتیمی که حقِ مقررشان [از مهریه و ارث] را نمی‌پردازید و به ازدواج با آنان تمایل ندارید [و از روی طمع در اموالشان، آنان را از ازدواج با دیگران نیز منع می‌کنید]، و [همچنین دستور او تعالی در مورد] کودکان ناتوان [این است] که: «در حق یتیمان به عدل و انصاف بکوشید»؛ و هر آنچه از نیکی‌ها که انجام ‌دهید، الله به آن آگاه است.');
INSERT INTO "islamhouse" VALUES(4,128,'4:128','و اگر زنی از ناسازگاری یا روی‌گردانیِ شوهرش بیم داشت، بر آن دو گناهی نیست که [با صرف‌نظر از برخی حقوق و وظایف] با یکدیگر آشتی کنند؛ و آشتی بهتر [از طلاق] است و[لی طمع و] بُخل، نفْس‌ها را فراگرفته است [و هیچ کس از حق خویش نمی‌گذرد]؛ و اگر نیکی کنید و پرهیزگاری پیشه سازید، قطعاً الله به آنچه انجام می‌‌دهید آگاه است.');
INSERT INTO "islamhouse" VALUES(4,129,'4:129','و [شما مردان] هر چند بکوشید هرگز نمی‌توانید [از نظر محبت قلبی] میان زنان عدالت برقرار کنید؛ پس یکسره [به یکی میل نکنید و] از یكى روى نگردانید که او را بلاتکلیف رها کنید؛ و اگر سازش نمایید و پرهیزگاری کنید، بی‌تردید الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(4,130,'4:130','و اگر آن دو [= زن و شوهر] از یکدیگر جدا شوند، الله هر یک از آنان را از گشایش [و بخشش] خویش بی‌نیاز می‌سازد؛ و الله همواره گشایشگرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(4,131,'4:131','و آنچه در آسمان‌ها و زمین است از آنِ الله است؛ و ما به کسانی که پیش از شما به آنان کتاب [آسمانی] داده شد و [همچنین] به شما سفارش کردیم که: «از الله پروا کنید»، و اگر کافر شدید، [بدانید که] آنچه در آسمان‌ها و زمین است از آنِ الله است و الله بی‌نیازِ ستوده ‌است.');
INSERT INTO "islamhouse" VALUES(4,132,'4:132','و آنچه در آسمان‌ها و زمین است، از آنِ الله است و همین بس که الله [مراقب و] کارگزار [بندگان] باشد.');
INSERT INTO "islamhouse" VALUES(4,133,'4:133','ای مردم، اگر او تعالی بخواهد، شما را از میان می‌برد و دیگرانی را می‌آورد؛ و الله همواره بر این [کار] تواناست.');
INSERT INTO "islamhouse" VALUES(4,134,'4:134','هر‌ کس [در قبال کارهایش] پاداش دنیا را می‌خواهد، [بداند که] پاداش دنیا و آخرت نزد الله است و الله همواره شنوای بیناست.');
INSERT INTO "islamhouse" VALUES(4,135,'4:135','ای کسانی که ایمان آورده‌اید، برپادارندۀ عدالت باشید [و] برای الله شهادت دهید؛ هر چند به زیان خودتان یا پدر و مادر و نزدیکان [شما] باشد. اگر [یكى از دو طرف دعوا] توانگر یا نیازمند باشد، [باز هم] الله [از شما] به آن دو نفر سزاوارتر است؛ پس از هوای نفس پیروی نکنید که [از حق] منحرف می‌شوید؛ و اگر زبان را بگردانید [و حق را تحریف کنید] یا [از اظهار آن] خودداری نمایید، مسلّماً الله به آنچه انجام می‌دهید آگاه است.');
INSERT INTO "islamhouse" VALUES(4,136,'4:136','ای کسانی ‌که ایمان آورده‌اید، به الله و پیامبرش و کتابی که بر او نازل کرده است و [به هر] کتابی که پیش از این فرستاده ‌است ایمان بیاورید [و بر آن پایدار باشید]؛ و هر ‌کس به الله و فرشتگانِ او و کتاب‌ها و پیامبرانش و روز قیامت کفر بورزد، بی‌تردید، در گمراهیِ دور و درازی افتاده ‌است.');
INSERT INTO "islamhouse" VALUES(4,137,'4:137','بی‌تردید، کسانی‌ که ایمان آوردند، سپس کافر شدند و پس از آن ایمان آوردند و باز کافر شدند و آنگاه بر کفر [خود] افزودند، الله هرگز بر آن نیست که آنان را ببخشاید و به راهی [راست که به سعادت ابدی ختم می‌گردد] هدایتشان کند.');
INSERT INTO "islamhouse" VALUES(4,138,'4:138','[ای پیامبر،] به منافقان خبر بده که عذابی دردناک برایشان [در پیش] است.');
INSERT INTO "islamhouse" VALUES(4,139,'4:139','[همان] کسانی که کافران را به جای مؤمنان به دوستی برمی‌گزینند. آیا سربلندی را نزد آنان می‌جویند؟ [باید بدانند که] همۀ سربلندی از آنِ الله است.');
INSERT INTO "islamhouse" VALUES(4,140,'4:140','و [ای مؤمنان،] مسلماً [الله این حُکم را] در این کتاب [= قرآن] بر شما نازل کرده است که: «چون شنیدید [افرادی] آیات الله را انکار می‌کنند و آن را به تمسخر می‌گیرند، با آنان ننشینید تا به سخنی دیگر بپردازند. [اگر با آنان بنشینید،] در این صورت، قطعاً شما [نیز] همانند آنان خواهید بود». بی‌تردید، الله همۀ منافقان و کافران را در دوزخ گِردِ هم می‌آورد.');
INSERT INTO "islamhouse" VALUES(4,141,'4:141','[همان] کسانی ‌که [از سرِ بدخواهی، همواره] شما را زیر نظر دارند [و منتظر هستند که چه چیزی به شما می‌رسد]؛ اگر پیروزی و گشایشی از جانب الله نصیبتان گردد، می‌گویند: «مگر ما با شما نبودیم؟» و اگر بهره‌ای نصیب کافران گردد، [به آنان] می‌گویند: «مگر ما پشتیبانتان نبودیم و شما را از [گزندِ] مؤمنان باز نمی‌نداشتیم؟»؛ پس الله روز قیامت میان شما داوری می‌کند؛ و الله هرگز برای کافران راهی [برای تسلط] بر مؤمنان قرار نداده ‌است.');
INSERT INTO "islamhouse" VALUES(4,142,'4:142','بی‌تردید، منافقان [با دروغگویی و تظاهرشان] با الله فریبکاری می‌کنند [و حال آنکه به سزای رفتارشان] الله فریب‌دهندۀ آنان است؛ و چون به نماز برخیزند، با سستی و‌ کاهلی برمی‌خیزند [و] با مردم ریاکاری می‌کنند و الله را جز اندکی یاد نمی‌کنند.');
INSERT INTO "islamhouse" VALUES(4,143,'4:143','[منافقان] بین این دو [گروه مؤمنان و کافران] متحیّر و سرگردانند؛ نه با اینانند و نه با آنان؛ و [ای پیامبر،] هر‌ کس که الله او را گمراه کند، راهی برای [هدایتِ] او نخواهی یافت.');
INSERT INTO "islamhouse" VALUES(4,144,'4:144','ای کسانی که ایمان آورده‌اید، کافران را به جای مؤمنان، دوستان [خود] نگیرید. آیا می‌خواهید برای الله دلیل آشکاری به زیانِ خود [برای عذاب] پدید آورید؟');
INSERT INTO "islamhouse" VALUES(4,145,'4:145','همانا منافقان در پایین‌ترین درجاتِ دوزخند و هرگز یاوری برایشان نخواهی یافت.');
INSERT INTO "islamhouse" VALUES(4,146,'4:146','مگر کسانی که توبه کردند و [باطنِ خویش را] اصلاح نمودند و به [کتابِ] الله تمسک جستند و دین خود را برای الله خالص گرداندند؛ که اینان با مؤمنانند و الله پاداش بزرگی به مؤمنان خواهد داد.');
INSERT INTO "islamhouse" VALUES(4,147,'4:147','اگر شکر کنید و ایمان آورید، الله چه نیازی به مجازات شما دارد؟ و الله همواره قدردانِ داناست.');
INSERT INTO "islamhouse" VALUES(4,148,'4:148','الله بدگوییِ آشکار را دوست ندارد، مگر از جانب کسی که به او ستم شده است [و ناچار زبان به شکایت و نفرین می‌گشاید]؛ و الله همواره شنوای داناست.');
INSERT INTO "islamhouse" VALUES(4,149,'4:149','اگر کار نیک را آشکار کنید یا آن را پنهان دارید یا از بدی [و ستمی که به شما رسیده است] درگذرید، [بدانید که] بی‌تردید، الله همواره آمرزندۀ تواناست.');
INSERT INTO "islamhouse" VALUES(4,150,'4:150','كسانى كه به الله و پیامبرانش كفر مى‌ورزند و مى‌خواهند میان الله و پیامبرانش جدایى اندازند و مى‌گویند: «ما به بعضى ایمان داریم و بعضى را انكار مى‌كنیم» و مى‌خواهند در این [میان، بین کفر و ایمان برای خود] راهى برگزینند.');
INSERT INTO "islamhouse" VALUES(4,151,'4:151','آنان به راستی كافرند و ما براى كافران عذابى خفت‌بار مهیا كرده‌ایم.');
INSERT INTO "islamhouse" VALUES(4,152,'4:152','و كسانى كه به الله و پیامبرانش ایمان آورده‌اند و میان هیچ یک از آنان تفاوت نمى‌گذارند، به زودى [الله] پاداششان را عطا مى‌كند و الله همواره آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(4,153,'4:153','[ای پیامبر،] اهل کتاب از تو می‌خواهند که [به نشانۀ راستگویی، یکباره] کتابی از آسمان برایشان نازل کنی. [این درخواستشان را بزرگ مشمار؛ زیرا آنان] از موسی [چیزی بزرگ‌تر و] عجیب‌تر از این خواستند و گفتند: «الله را آشکارا به ما نشان بده»؛ به خاطر درخواستِ ناحقشان، صاعقه‌[ی مرگ‌] آنان را فرو گرفت؛ آنگاه [چون الله آنان را دوباره زنده کرد] پس از آنکه نشانه‌های آشکار برایشان آمد، گوساله را [به عنوان معبود] گرفتند و[لی ما پس از آنکه توبه کردند] از آن [گناهشان نیز] درگذشتیم و به موسی دلیلی آشکار بخشیدیم.‌');
INSERT INTO "islamhouse" VALUES(4,154,'4:154','و به سببِ پیمان [گرفتن از] آنان [کوه] طور را بالای سرشان برافراشتیم و [نیز] به آنان گفتیم: «با فروتنی از دروازۀ [بیت‌المقدس] وارد شوید» و [همچنین] به آنان گفتیم: «حرمتِ شنبه را نشکنید [و از صید ماهی در این روز دست بکشید]» و از آنان پیمانی سخت و استوار گرفتیم.');
INSERT INTO "islamhouse" VALUES(4,155,'4:155','[آنان را لعنت کردیم] به خاطر اینکه پیمانشان را شکستند و به آیاتِ الله کفر ورزیدند و پیامبران را به ناحق کشتند و [نیز از روی تمسخر] گفتند كه: «بر دل‌های ما، پرده [انداخته شده] است [که سخن حق را درک نمی‌کنیم]»؛ بلكه الله به خاطر کفرشان بر دل‌هایشان مُهر زده است؛ بنابراین جز اندکی [از آنان] ایمان نمى‌آورند.');
INSERT INTO "islamhouse" VALUES(4,156,'4:156','و [نیز آنان را لعنت کردیم] به سبب كفرشان و آن تهمت بزرگى كه به مریم زدند؛');
INSERT INTO "islamhouse" VALUES(4,157,'4:157','و [همچنان آنان را لعنت کردیم به خاطر این] سخنشان که: «ما مسیح ـ عیسی پسر مریم ‌ـ پیامبر الله را کشتیم»؛ در حالی که نه او را کشتند و نه به دار آویختند؛ بلکه [امر] بر آنان مُشتَبَه شد [و مردی را که شبیه عیسی بود به دار کشیدند] و كسانى كه دربارۀ او اختلاف كردند، قطعاً راجع به وی دچار تردیدند و هیچ علمى به آن ندارند، مگر آنكه از گمان پیروى مى‌كنند؛ و یقیناً او را نکشته‌اند.');
INSERT INTO "islamhouse" VALUES(4,158,'4:158','بلکه الله او را به سوی خویش بالا بُرد؛ و الله شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(4,159,'4:159','و [کسی] از اهل کتاب نیست، مگر اینکه پیش از مرگش [= مرگ عیسی]، حتماً به او ایمان می‌آورَد و روز قیامت [نیز] وی بر [کفر و ایمانِ] آنان گواه خواهد بود.');
INSERT INTO "islamhouse" VALUES(4,160,'4:160','پس به سزاى ستمى كه از یهود سر زد و به سبب اینکه [مردم را] بسیار از راه الله بازداشتند، ما چیزهای پاکیزه‌ای را که برایشان حلال بود، بر آنان حرام کردیم.');
INSERT INTO "islamhouse" VALUES(4,161,'4:161','و [نیز به سزای‌] ربا گرفتن شان ـ با آنكه از آن نهى شده بودند ـ و اینکه مالِ مردم را به ناروا می‌خوردند؛ و ما براى كافرانشان عذابى دردناک مهیّا كرده‌ایم.');
INSERT INTO "islamhouse" VALUES(4,162,'4:162','ولی از میان آنان راسخان در علم [= علمای یهود] و مؤمنان [یعنی کسانی که] به آنچه بر تو نازل شده است و [به] آنچه که پیش از تو نازل شده است ایمان می‌آورند؛ و [همچنین] نمازگزاران و زکات‌دهندگان و ایمان‌آورندگان به الله و روز بازپسین؛ به [همۀ] اینها پاداش بزرگی خواهیم داد.');
INSERT INTO "islamhouse" VALUES(4,163,'4:163','[ای پیامبر،] بی‌تردید، ما به تو وحی فرستادیم؛ همان گونه که به نوح و پیامبران بعد از او وحی فرستادیم و [نیز] به ابراهیم و اسماعیل و اسحاق و یعقوب و نوادگان [یعقوب] و عیسی و ایوب و یونس و هارون و سلیمان وحی فرستادیم؛ و به داود زبور دادیم.');
INSERT INTO "islamhouse" VALUES(4,164,'4:164','و پیامبرانى [فرستادیم‌] كه در حقیقت، [سرگذشت‌] آنان را پیش از این برای تو حكایت نمودیم و پیامبرانى [را نیز برانگیخته‌ایم‌] كه [داستان] آنان را برایت بیان نكرده‌ایم؛ و الله با موسى آشكارا [و بی‌واسطه] سخن گفت.');
INSERT INTO "islamhouse" VALUES(4,165,'4:165','پیامبرانى [فرستادیم] كه بشارت‌بخش و بیم‌دهنده بودند تا براى مردم پس از [بعثت‌] پیامبران، در مقابل الله [هیچ بهانه و] حجتى نباشد؛ و الله همواره شکست‌ناپذیزِ حكیم است.');
INSERT INTO "islamhouse" VALUES(4,166,'4:166','[ای پیامبر، کافران پیامت را انکار می‌کنند] ولی الله به [درستیِ] آنچه بر تو نازل کرده است گواهی می‌دهد؛ آن را به علم خویش نازل کرده است و فرشتگان نیز گواهی می‌دهند؛ و [برای حقانیتِ قرآن،] کافی است که الله گواه باشد.');
INSERT INTO "islamhouse" VALUES(4,167,'4:167','بى‌تردید، كسانى كه كفر ورزیدند و [مردم را] از راه الله بازداشتند، به گمراهىِ دور و درازى افتاده‌اند.');
INSERT INTO "islamhouse" VALUES(4,168,'4:168','بى‌تردید، كسانى كه كفر ورزیدند و [با این کار بر خود] ستم كردند، الله هرگز بر آن نیست که آنان را بیامرزد و آنان را به راهى [درست نیز] هدایت نمی‌کند.');
INSERT INTO "islamhouse" VALUES(4,169,'4:169','مگر راه جهنم که جاودانه در آن می‌مانند؛ و این [کار] بر الله آسان است.');
INSERT INTO "islamhouse" VALUES(4,170,'4:170','ای مردم، این پیامبر، از جانب پروردگارتان [هدایت و دینِ] حق را برایتان آورده است؛ پس ایمان بیاورید که به خیر [و صلاحِ‌] شماست؛ و اگر [سخنش را نپذیرید و] کفر بورزید، [بدانید که] آنچه در آسمان‌ها و زمین است از آنِ الله است [و کفر شما زیانی به او نمی‌رساند] و الله همواره دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(4,171,'4:171','ای اهل کتاب، در دین خود غلو نکنید و دربارۀ الله جز [سخنِ] حق نگویید. بی‌تردید، مسیح ـ عیسی پسر مریم ـ فرستادۀ الله و کلمۀ اوست که [امر کرد: «باش» که بی‌درنگ موجود شد و به این صورت، به امرِ الله، جبرئیل] وی را به مریم القا کرد و [عیسی] روحی از جانب اوست؛ پس به الله و پیامبرانش ایمان بیاورید و نگویید: «[معبود] سه‌گانه است». [از این سخن] بازایستید که برای شما بهتر است. الله تنها معبود یگانه است [و] از اینکه فرزندی داشته باشد، پاک [و منزه] است. آنچه در آسمان‌ها و زمین است از آنِ اوست و همین بس که الله کارگزار [و مراقبِ مخلوقات] باشد.');
INSERT INTO "islamhouse" VALUES(4,172,'4:172','مسیح و فرشتگان مقرّب هرگز اِبا ندارند از اینکه بندۀ الله باشند؛ و هر کس از عبادت او تعالی سرپیچی کند و تکبر ورزد، همۀ آنان را [برای حسابرسی‌] نزد خویش جمع خواهد کرد.');
INSERT INTO "islamhouse" VALUES(4,173,'4:173','اما کسانی که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، [الله] پاداش‌هایشان را به طور کامل می‌دهد و از فضل خویش بر آنها می‌افزاید؛ و اما کسانی را که اِبا کردند و تکبر ورزیدند، به عذابی دردناک عذاب می‌کند و دوست و یاوری غیر از الله برای خود نمی‌یابند.');
INSERT INTO "islamhouse" VALUES(4,174,'4:174','ای مردم، به راستی که برای شما از جانب پروردگارتان دلیلی روشن [= محمد صلی الله علیه وسلم] آمد و نوری آشکار [= قرآن] را به سویتان نازل کردیم.');
INSERT INTO "islamhouse" VALUES(4,175,'4:175','اما کسانی را که به الله ایمان آوردند و به آن [= قرآن] تمسک جستند، به زودی [الله] آنان را در رحمت و فضل خویش وارد خواهد کرد و به راهی راست به سوی خویش هدایت خواهد نمود.');
INSERT INTO "islamhouse" VALUES(4,176,'4:176','[ای پیامبر، دربارۀ نحوۀ تقسیم ارث بین برادر و خواهرِ متوفایی که پدر و مادر و فرزند ندارد] از تو می‌پرسند. بگو: «الله در [مورد] کـَـلاله برایتان حکم صادر می‌کند: اگر مردی که [پدر و مادر و] فرزندی ندارد فوت کند و خواهری داشته باشد، نصف تَرَکه از آنِ اوست؛ [و اگر زنی فوت کند و برادری داشته باشد] در صورتی که [متوفی] فرزند نداشته باشد، آن مرد [همۀ ترکه را] از او به ارث می‌برد؛ و اگر [فقط] دو خواهر [یا بیشتر از متوفی] باقی بماند، دو سومِ تَرَکه برای آنهاست؛ و اگر [چند] برادر و خواهر [با هم] باشند، هر مردی [معادل] سهم دو زن را دارد. الله [این احکام را] برایتان روشن می‌سازد تا گمراه نشوید [و در تقسیم ارث، به راه خطا نروید]؛ و الله به هر چیزی داناست».');
INSERT INTO "islamhouse" VALUES(5,1,'5:1','ای کسانی که ایمان آورده‌اید، به پیمان‌ها[یتان] وفا کنید. [خوردن گوشت‌] چهارپایان بر شما حلال است، مگر آنچه [حکمش] بر شما خوانده شود و [به شرط آنکه‌] در حال احرام [حج یا عمره] شکار را حلال نشمارید. همانا الله هر چه بخواهد حکم می‌کند.');
INSERT INTO "islamhouse" VALUES(5,2,'5:2','ای کسانی که ایمان آورده‌اید، [حرمت] شعایر الهی و ماه حرام [را نگه دارید] و [همچنین حرمتِ] قربانى بى‌نشان و قربانی‌های قلاده‌دار و راهیان بیت ‌الحرام كه فضل و رضایتِ پروردگارشان را مى‌طلبند؛ و چون از احرام [و سرزمین حرم] خارج شدید، [می‌توانید آزادانه] به شکار بپردازید؛ و [مبادا کینه‌توزی و] دشمنی گروهی که شما را از مسجد الحرام بازداشتند، شما را به تعدّی و تجاوز وادار کند؛ و در نیكوكارى و پرهیزگاری به یكدیگر یاری رسانید و در گناه و تجاوز دستیار هم نشوید و از الله پروا كنید [چرا كه] بی‌تردید، الله سخت‌كیفر است.');
INSERT INTO "islamhouse" VALUES(5,3,'5:3','[اینها همه] بر شما حرام شده است: [گوشت] مُردار و خون و گوشت خوک و آنچه [هنگام ذبح] نام غیر الله بر آن برده شود و [حیوانات حلال‌گوشتِ‌] خفه‌شده و به ضرب [چوب و سنگ‌ و...] مُرده و از بلندی افتاده [و مُرده‌] و [آنها که از حیوانی دیگر] شاخ‌خورده [و مُرده‌اند] و نیم‌خوردۀ [حیواناتِ] درنده ـ مگر آنچه [پیش از مردن] ذبح [شرعی] کرده باشید ـ و [همچنین] آنچه در برابر نشانه‌های معبودان باطل ذبح می‌شود و [همچنان بر شما حرام است که] باتیر‌های بخت‌آزمایی تصمیم‌گیری و اقدام کنید؛ و [به یقین بدانید که روی آوردن به تمام] اینها نافرمانی [از الله] است. امروز کافران از دین شما [و بازگشتتان به کفر] ناامید شده‌اند؛ پس از آنان نترسید و از من بترسید. امروز دینتان را کامل کردم و نعمتم را بر شما تمام نمودم و اسلام را [به عنوان بهترین] دین برایتان برگزیدم. اما هر کس [برای حفظ جان خود و] بدون آنكه به گناه متمایل باشد، [به خوردن گوشت‌های ممنوعه] ناچار شود [گناهی بر او نیست]. به راستی که الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(5,4,'5:4','[ای پیامبر، اصحابت] از تو می‌پرسند چه چیز برایشان حلال شده است. بگو: «چیزهاى پاكیزه و [شکارِ] حیوانات شکاری که از آنچه الله به شما تعلیم داده، به آنها آموخته‌ايد، برایتان حلال شده است. پس، از آنچه [این حیوانات] برای شما گرفته‌اند [و خود از آن نخورده‌اند] بخورید. و [به هنگام ذبح یا فرستادن حیوانات شکاری] نام الله را بر آن ببرید و از الله پروا کنید. بی‌گمان الله، حسابرسی سریع است».');
INSERT INTO "islamhouse" VALUES(5,5,'5:5','[ای مسلمانان،] امروز خوراکی‌های پاکیزه‌ برایتان حلال شده است و [ذبایح و] غذای اهل کتاب برای شما حلال است و غذای شما [نیز] برای آنان حلال است؛ و [همچنین ازدواج با] زنان پاکدامن مسلمان و زنان پاکدامن از [اهل کتاب، یعنی] کسانی‌ که پیش از شما به آنان کتاب [آسمانی] داده شده است [حلال است]؛ به شرط آنکه مهریه‌های‌ آنان را بپردازید، در حالی ‌که خود پاکدامن باشید، نه زناكار باشید و نه [زنان را] پنهانى به دوستی بگیرید؛ و هر‌ کس به [ارکان] ایمان کفر بورزد، قطعاً عملش تباه شده است و در آخرت از زیانکاران خواهد بود.');
INSERT INTO "islamhouse" VALUES(5,6,'5:6','ای کسانی‌ که ایمان آورده‌اید، چون به [قصدِ] نماز برخاستید، صورت و دست‌هایتان را تا آرنج بشویید و سرتان را مسح کنید و پاهایتان را تا دو قوزک [بشویید] و اگر جُنُب بودید، خود را [با غسل کردن] پاک سازید؛ و اگر بیمار یا مسافرید یا هر یک از شما از محل قضای حاجت آمده است یا با زنان آمیزش کرده‌اید و آب نمی‌یابید، با خاکی پاک تیمم کنید و از آن بر صورت و دست‌هایتان بکشید. الله نمی‌خواهد شما را به سختی بیندازد؛ بلکه می‌خواهد شما را پاک سازد و نعمت خویش را بر شما تمام کند؛ باشد که شکر به جای آورید.');
INSERT INTO "islamhouse" VALUES(5,7,'5:7','و به یاد آورید نعمت الله را بر خویش و [نیز] پیمانى كه شما را به [انجام‌] آن متعهد گردانده است آنگاه كه [دربارۀ دعوت به فرمانبرداری از پیامبر] گفتید: «شنیدیم و اطاعت کردیم»؛ و از الله پروا کنید [که] بی‌تردید، الله به آنچه درون سینه‌هاست داناست.');
INSERT INTO "islamhouse" VALUES(5,8,'5:8','اى كسانى كه ایمان آورده‌اید، براى الله به عدل برخیزید [و] به عدالت گواهی دهید؛ و البته نباید دشمنى یک گروه، شما را بر آن دارد كه عدالت نكنید. عدالت كنید كه این [کار] به پرهیزگاری نزدیک‌تر است؛ و از الله پروا دارید [كه] بی‌تردید، الله به آنچه انجام مى‌دهید، آگاه است.');
INSERT INTO "islamhouse" VALUES(5,9,'5:9','الله به کسانی که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، وعدۀ آمرزش و پاداش بزرگی داده است.');
INSERT INTO "islamhouse" VALUES(5,10,'5:10','و کسانی ‌که کفر ورزیدند و آیات ما را تکذیب کردند، آنان اهل دوزخند.');
INSERT INTO "islamhouse" VALUES(5,11,'5:11','ای کسانی ‌که ایمان آورده‌اید، نعمتِ الله را بر خود به یاد آورید، آنگاه که گروهی [از دشمنان] قصد کردند به سویتان دست‌درازی کنند، اما [الله] دستشان را از شما بازداشت؛ و از الله پروا کنید؛ و مؤمنان باید تنها بر الله توکل کنند.');
INSERT INTO "islamhouse" VALUES(5,12,'5:12','و بی‌تردید، الله از بنی‌اسرائیل پیمان گرفت و از میان آنان [به تعداد قبایلشان] دوازده سالار برگماشتیم و الله فرمود: «من با شما هستم. اگر نماز برپا دارید و زکات بپردازید و به پیامبران من ایمان آورید و آنان را یاری کنید و در راه الله قرضی نیکو دهید، گناهانتان را می‌زدایم و شما را به باغ‌هایی [از بهشت] وارد می‌کنم که جویبارها از زیر [درختان] آن جاری است. اما هر‌ یک از شما که بعد از این کافر شود، مسلماً راه راست را گم کرده است».');
INSERT INTO "islamhouse" VALUES(5,13,'5:13','پس به [سزای] پیمان‌شکنی‌شان، آنان را از رحمت خویش دور ساختیم و دل‌هایشان را سخت گرداندیم؛ [زیرا با تغییرِ لفظ و تأویلِ معنا] سخنان [الله] را از جایگاهش تحریف می‌کنند و بخشى از آنچه را به آن پند داده شدند فراموش کردند؛ و [ای پیامبر،] تو پیوسته دربارۀ خیانتى از سوی آنان آگاه مى‌شوى، مگر [شمارى‌] اندک از اینان [كه خیانتكار نیستند]؛ پس از آنان درگذر و چشم‌پوشى كن [كه] بی‌تردید، الله نیكوكاران را دوست ‌دارد.');
INSERT INTO "islamhouse" VALUES(5,14,'5:14','و [همچنین] از کسانی ‌که گفتند: «ما نصرانی هستیم» پیمان گرفتیم، [که پیرو قرآن و رسول الله باشند] و[لى دینشان را تحریف نمودند و‌] بخشى از آنچه را که به آن پند داده شدند به فراموشی سپردند و ما [نیز‌] تا روز قیامت میانشان دشمنى و كینه افكندیم؛ و [در آن روز] الله آنان را [از نتیجۀ‌] آنچه کرده‌اند آگاه خواهد ساخت.');
INSERT INTO "islamhouse" VALUES(5,15,'5:15','ای اهل کتاب، همانا پیامبر ما به سوى شما آمده است كه بسیارى از [مطالب و آموزه‌های] كتاب [آسمانى خود] را كه پنهان می‌کردید، برایتان به روشنی بیان ‌كند و از [بیان موارد] بسیاری در‌گذرد. قطعاً از جانب الله برایتان نور و كتابى روشنگر آمده است.');
INSERT INTO "islamhouse" VALUES(5,16,'5:16','الله به وسیلۀ این [کتاب،] کسانی را که به دنبال رضایتش باشند به راه‌های [امنیت و] سلامت هدایت می‌کند و به فرمان خویش آنان را از تاریکی‌ها به سوی روشنایی می‌بَرد و به راهی راست هدایتشان می‌کند.');
INSERT INTO "islamhouse" VALUES(5,17,'5:17','کسانی که گفتند: «الله همان مسیح ـ پسر مریم ـ است» یقیناً کافر شدند. [ای پیامبر، به آنان] بگو: «اگر الله بخواهد که مسیح ـ پسر مریم ـ و مادرش و همۀ کسانی را که روی زمین هستند نابود کند، چه كسى در برابر الله اختیارى دارد [كه مانع شود]؟ فرمانرواییِ آسمان‌ها و زمین و آنچه میان آن دو قرار دارد از آنِ الله است؛ هر چه بخواهد می‌آفریند؛ و الله بر همه چیز تواناست».');
INSERT INTO "islamhouse" VALUES(5,18,'5:18','یهود و نصاری گفتند: «ما پسران و دوستان الله هستیم». [ای پیامبر، در پاسخ به آنان] بگو: «پس چرا او شما را به کیفر گناهانتان عذاب می‌کند؟! [هرگز چنین نیست] بلکه شما [نیز] بشری هستید از جمله افرادی که آفریده است؛ هر ‌کس را بخواهد می‌آمرزد و هر ‌کس را بخواهد عذاب می‌کند؛ و فرمانروایی آسمان‌ها و زمین و آنچه میان آن دو قرار دارد، از آنِ الله است و بازگشت [همه] به سوی اوست».');
INSERT INTO "islamhouse" VALUES(5,19,'5:19','ای اهل کتاب، پیامبر ما، پس از [انقطاع وحی، و] فترتی از پیامبران [=فاصله بین بعثت عیسی و محمد علیهما السلام]، به سوی شما آمد که [احکام و آموزه‌های الهی را] به روشنی برایتان بیان کند تا مبادا [روز قیامت] بگویید: «نه بشارت‌دهنده‌ای به سراغمان آمد، نه بیم‌دهنده‌ای». یقیناً [اینک] پیامبر بشارت‌دهنده و بیم‌دهنده به سویتان آمده است؛ و الله بر همه چیز تواناست.');
INSERT INTO "islamhouse" VALUES(5,20,'5:20','و [ای پیامبر، یاد کن از] هنگامی که موسی به قومش گفت: «ای قوم من، نعمت الله را بر خود به یاد آورید، آنگاه که پیامبرانی میان شما برانگیخت و [پس از بردگیِ فرعون، افرادی از] شما را فرمانروایان [خودتان] ساخت و چیزهایی به شما بخشید که به هیچ یک از جهانیان [معاصرتان] نداده بود.');
INSERT INTO "islamhouse" VALUES(5,21,'5:21','ای قوم من، به سرزمین مقدسی که الله برایتان مقرر نموده است وارد شوید و به عقب بازنگردید [و از دینتان دست نکشید] که زیانکار خواهید شد».');
INSERT INTO "islamhouse" VALUES(5,22,'5:22','[آنان] گفتند: «ای موسی، بی‌گمان آنجا قومی زورمند هستند [و] تا آنان از آنجا بیرون نروند، ما هرگز در آن وارد نمى‌شویم؛ اگر از آنجا بیرون بروند، ما وارد خواهیم شد».');
INSERT INTO "islamhouse" VALUES(5,23,'5:23','دو تن از مردانی که [از نافرمانی و عذاب الهی] می‌ترسیدند و الله به آنان نعمت [اطاعت و بندگی] داده بود گفتند: «از آن دروازه بر آنان [هجوم برید و] وارد شوید كه اگر از آن درآمدید، قطعاً پیروز خواهید شد؛ و اگر مؤمنید، بر الله توكل كنید».');
INSERT INTO "islamhouse" VALUES(5,24,'5:24','[بنی‌اسرائیل] گفتند: «ای موسی، تا زمانی که آنان در آن [شهر] هستند ما هرگز وارد نمی‌شویم. تو و پروردگارت بروید و بجنگید. ما همینجا نشسته‌ایم».');
INSERT INTO "islamhouse" VALUES(5,25,'5:25','[موسی] گفت: «پروردگارا، من جز اختیار خودم و برادرم [اختیار کس دیگری] را ندارم؛ پس میان ما و این گروهِ نافرمان جدایی بینداز».');
INSERT INTO "islamhouse" VALUES(5,26,'5:26','[الله] فرمود: «در این صورت، [ورود به] آن [سرزمین مقدس] تا چهل سال بر آنان حرام شده است؛ [و پیوسته] در زمین سرگردان خواهند بود؛ پس بر این گروهِ نافرمان افسوس مخور».');
INSERT INTO "islamhouse" VALUES(5,27,'5:27','و [ای پیامبر،] داستان دو پسر آدم [= هابیل و قابیل] را به‌حق برایشان بخوان: هنگامی ‌که [هر کدام] برای نزدیکی‌جستن [به درگاه الله] یک قربانی تقدیم کردند؛ اما از یکی پذیرفته شد و از دیگری پذیرفته نشد. [قابیل به هابیل] گفت: «قطعاً تو را خواهم کشت». [هابیل] گفت: «الله [قربانی را] فقط از پرهیزگاران می‌پذیرد.');
INSERT INTO "islamhouse" VALUES(5,28,'5:28','اگر تو دستت را برای کشتن من دراز کنی، من هرگز دستم را برای کشتن به سویت دراز نخواهم کرد؛ [زیرا] بی‌تردید، من از الله ـ پروردگار جهانیان‌ـ می‌ترسم.');
INSERT INTO "islamhouse" VALUES(5,29,'5:29','[آنگاه برای آنکه او را بترساند گفت:] من می‌خواهم که تو با گناه [قتلِ] من و گناه [اعمال] خودت [به سوی الله] بازگردی و از اهل دوزخ باشی؛ و این است سزای ستمکاران».');
INSERT INTO "islamhouse" VALUES(5,30,'5:30','پس نفسِ [سرکشِ] او کشتن برادرش را برایش آراست [و موجّه و آسان جلوه داد]؛ پس او را کشت و از زیانکاران شد.');
INSERT INTO "islamhouse" VALUES(5,31,'5:31','آنگاه الله کلاغی را فرستاد که زمین را می‌کاوید تا [کلاغ مرده‌ای را دفن کند و این گونه] به وی نشان دهد که چگونه جسد برادرش را بپوشاند. [قابیل] گفت: «وای بر من! آیا ناتوان بودم از اینکه همچون این کلاغ باشم و جسد برادرم را بپوشانم؟» آنگاه [به شدّت از کارِ زشت خویش] پشیمان شد.');
INSERT INTO "islamhouse" VALUES(5,32,'5:32','به همین سبب، بر بنی‌اسرائیل مقرر داشتیم که هر کس انسانی را جز برای [قصاص] کسی یا [به کیفر] فسادی [که] در زمین [مرتکب شده است] به قتل برسانَد، چنان است که گویی همۀ مردم را کشته است و هر ‌کس [با خودداری از قتل‌های خودسرانه و بیهوده،] انسانی را زنده بدارد، چنان است که همۀ مردم را زنده کرده است؛ و بی‌گمان، پیامبران ما با دلایل روشن نزد آنان [= بنی‌اسرائیل] آمدند، [ولی] باز هم بسیاری از آنان پس از آن [با ارتکاب گناه] در زمین [پا از حدود الهی فراتر گذاشتند و] زیاده‌روی کردند.');
INSERT INTO "islamhouse" VALUES(5,33,'5:33','سزای کسانی ‌که با الله و پیامبرش می‌جنگند و [با قتل و دزدی و فحشا] در زمین به فساد می‌کوشند، جز این نیست که کشته شوند یا به دار آویخته شوند یا دست [راست] و پای [چپ] آنها در خلاف [جهت یکدیگر] قطع شود یا از سرزمین [خود] تبعید گردند. این [مجازات، مایۀ] رسوایی آنها در دنیاست و در آخرت [نیز] عذابی بزرگ [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(5,34,'5:34','مگر کسانی‌ که [شما حاکمان] پیش از آنکه بر آنان دست یابید توبه کنند؛ پس [اگر چنین کردند، از آنان درگذرید و] بدانید که الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(5,35,'5:35','ای کسانی که ایمان آورده‌اید، از الله پروا کنید و [با انجام کارهای نیک، برای تقرب] به سوی او وسیله‌ بجویید و در راهش جهاد کنید؛ باشد که رستگار شوید.');
INSERT INTO "islamhouse" VALUES(5,36,'5:36','در حقیقت، كسانى كه كفر ورزیدند، اگر تمام آنچه در زمین است براى آنان باشد و همانندش [نیز] با آن [در اختیار شان باشد و بخواهند] به وسیله آن خود را از عذاب روز قیامت بازخرند، از آنان پذیرفته نمى‌شود و عذابى دردناک [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(5,37,'5:37','[این کافران] می‌خواهند از آتش [دوزخ] بیرون روند؛ و[لی] از آن [عذاب] خارج‌شدنی نیستند و برایشان عذابی پایدار [مهیا] است.');
INSERT INTO "islamhouse" VALUES(5,38,'5:38','دست مرد و زن دزد را به سزای عملی که مرتکب شده‌اند، به عنوان مجازاتی از جانب الله قطع کنید و [بدانید که] الله شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(5,39,'5:39','اما هر کس بعد از [گناه و] ستم‌کردنش توبه کند و [اعمال خود را جبران و] اصلاح نماید، بی‌تردید الله توبه‌اش را می‌پذیرد. به راستی که الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(5,40,'5:40','آیا نمی‌دانی که فرمانروایی آسمان‌ها و زمین از آنِ الله است [و] هر کس را بخواهد عذاب می‌کند و هر کس را بخواهد می‌بخشد؟ و الله بر هر چیزی تواناست.');
INSERT INTO "islamhouse" VALUES(5,41,'5:41','ای پیامبر، کسانی که در [راه] کفر شتاب می‌کنند؛ تو را غمگین نسازند ـ [خواه] از کسانی که به زبانشان می‌گویند: «ایمان آوردیم» و قلبشان ایمان نیاورده است و [خواه‌] از یهودیانی باشند که به سخنان دروغ گوش مى‌سپارند و از گروهی که خودشان [از روی کبر و حسد] نزد تو نیامده‌اند، می‌شنوند [و از آنها فرمان می‌برند]. آنان سخنان [الله] را [بر اساس میل و منفعتِ خود] از جایگاهشان تحریف می‌کنند و [به یکدیگر] می‌گویند: «اگر این [حکم از سوی محمد] به شما داده شد، [از او] بپذیرید و اگر آن [چیزی که دل‌خواهتان است] به شما داده نشد، [از او] دوری کنید». و [ای پیامبر،] هر کس که الله گمراهی‌اش را خواسته باشد، تو هرگز در برابر الله برایش اختیاری نداری [و نمی‌توانی از او دفاع کنی]. آنان [= یهود و منافقان] کسانی هستند که الله نخواسته است دل‌هایشان را [از پلیدیِ کفر] پاک کند. آنان در دنیا رسوایی [و خواری در انتظار] دارند و در آخرت برایشان عذاب بزرگی [در پیش] است.');
INSERT INTO "islamhouse" VALUES(5,42,'5:42','آنان [= یهود] بسیار به دروغ گوش می‌سپارند [و] مال حرام فراوان می‌خورند؛ پس [ای پیامبر،] اگر [برای داوری] نزدت آمدند، میانشان داوری کن یا از آنان روی بگردان؛ و [یقین بدان که] اگر از آنان روی بگردانی، هیچ زیانی به تو نمی‌رسانند؛ و[لی] اگر داوری کردی، با عدالت میانشان داوری کن. بی‌تردید، الله دادگران را دوست دارد.');
INSERT INTO "islamhouse" VALUES(5,43,'5:43','و آنان چگونه تو را به داوری می‌طلبند در حالی که تورات نزدشان است [و] حکم الله در آن وجود دارد؟ سپس بعد از آن [داوری، اگر سخنی خلاف میلشان بگویی، از دستور تورات و حکمِ تو] روی می‌گردانند؛ و اینان مؤمن نیستند.');
INSERT INTO "islamhouse" VALUES(5,44,'5:44','به راستی ما تورات را نازل کردیم که در آن هدایت و نور است. پیامبرانی که [در برابر فرمان الله] تسلیم بودند، بر اساس [آموزه‌های] آن برای یهود داوری می‌کردند و [نیز] مصلحان و دانشمندان [یهود] که پاسداری از كتاب الله [و جلوگیری از تحریف تورات] به آنان سپرده شده بود و بر [حقانیتِ] آن گواه بودند [و بنا به دستورهایش حکم می‌کردند]؛ پس [ای یهود، در اجرای احکام الهی] از مردم نترسید و از من بترسید و آیاتم را به بهایی ناچیز نفروشید؛ و هر کس بر [اساس قوانین و] احکامی که الله نازل کرده است داوری [و حکم] نکند، کافر است.');
INSERT INTO "islamhouse" VALUES(5,45,'5:45','و در آن [= تورات] بر آنان [= بنی‌اسرائیل] مقرر داشتیم که: «جان در برابر جان و چشم در برابر چشم و بینی در برابر بینی و گوش در برابر گوش و دندان در برابر دندان است؛ و [حتی] زخم‌ها قصاص دارند». اما هر کس از آن [قصاص] گذشت نماید، آن [بخشش،] کفارۀ [گناهان] اوست؛ و هر کس بر [اساس قوانین و] احکامی که الله نازل کرده است داوری [و حکم] نکند، ستمکار است.');
INSERT INTO "islamhouse" VALUES(5,46,'5:46','و به دنبال آنان [= پیامبران بنی‌اسرائیل] عیسی پسر مریم را فرستادیم که تصدیق‌کنندۀ تورات بود که پیش از او فرستاده بودیم؛ و انجیل را که در آن هدایت و نور بود به او دادیم که تصدیق‌کنندۀ توراتِ پیش از وی بود و هدایت و اندرزی برای پرهیزگاران بود.');
INSERT INTO "islamhouse" VALUES(5,47,'5:47','و پیروان انجیل باید به آنچه الله در آن [کتاب] نازل کرده است حکم کنند؛ و هر کس بر [اساس قوانین و] احکامی که الله نازل کرده است داوری [و حکم] نکند، فاسق است.');
INSERT INTO "islamhouse" VALUES(5,48,'5:48','و [ای پیامبر، ما این] کتاب را به‌حق بر تو نازل کردیم [که] تصدیق‌کنندۀ کتاب‌های پیشین و نگهبان و گواه بر آنهاست. پس بین آنان بر اساس آنچه الله نازل کرده است حکم کن و به جای حقیقتی که [از جانب قرآن] به تو رسیده است، از هوس‌های آنان پیروی نکن. [ای مردم،] ما برای هر یک از شما [امت‌ها] آیین و راهی روشن قرار دادیم؛ و اگر الله می‌خواست، همۀ شما را امتی واحد قرار می‌داد؛ ولی [الله می‌خواهد] شما را در [مورد] آنچه به شما بخشیده است بیازماید؛ پس در نیکی‌ها بر یکدیگر پیشی گیرید. بازگشت همگی شما به سوی الله است؛ آنگاه او تعالی شما را از آنچه در آن اختلاف می‌کردید، آگاه می‌سازد.');
INSERT INTO "islamhouse" VALUES(5,49,'5:49','و [ای پیامبر،] میان آنان [= یهود] بر اساس آنچه الله [در قرآن] نازل کرده است حکم کن و از هوس‌هایشان پیروی مکن و بر حذر باش از اینکه تو را از برخی از آنچه الله بر تو نازل کرده است منحرف کنند؛ پس اگر [از پذیرشِ داوریِ تو] روی گردانند، بدان که الله می‌خواهد آنان را به [خاطر] برخی از گناهانشان مجازات کند؛ و به راستی که بسیاری از مردم نافرمانند.');
INSERT INTO "islamhouse" VALUES(5,50,'5:50','آیا آنان حکم [و داوری بر اساس موازین] جاهلیت را می‌خواهند؟ و براى اهل یقین، حکم [و داورىِ] چه كسى بهتر از الله است؟');
INSERT INTO "islamhouse" VALUES(5,51,'5:51','ای کسانی‌ که ایمان آورده‌اید، یهود و نصاری را به دوستی نگیرید. آنان دوستان یکدیگرند؛ و هر یک‌ از شما که با آنان دوستی [و همکاری] کنند، حتماً از آنهاست. بی‌تردید، الله گروه ستمکاران را [به خاطر دوستی‌ با کافران] هدایت نمی‌کند.');
INSERT INTO "islamhouse" VALUES(5,52,'5:52','پس [ای پیامبر،] کسانی را که در دل‌هایشان بیماری [نفاق] است می‌بینی که در [دوستی با] آنان می‌شتابند [و] می‌گویند: «می‌ترسیم که [اگر پیروز شوند، از جانب آنان] آسیبی به ما برسد». امید است که الله [برای مسلمانان] از جانب خود فتح [و پیروزی] یا امر [دیگری] را پیش آورَد، آنگاه [این منافقان] از آنچه در دل خود پنهان می‌داشتند پشیمان شوند.');
INSERT INTO "islamhouse" VALUES(5,53,'5:53','و کسانی که ایمان آورده‌اند می‌گویند: «آیا اینها [= منافقان] همان کسانی هستند که با بیشترین تأکید به الله سوگند مى‌خوردند كه با شما هستند؟» اعمالشان تباه گشت و زیانکار شدند.');
INSERT INTO "islamhouse" VALUES(5,54,'5:54','ای کسانی ‌که ایمان آورده‌اید، هر یک از شما که از دین خود برگردد [به الله زیانی نمی‌رساند]، الله به زودی گروهی را می‌آورد که آنان را دوست دارد و آنها [نیز] او تعالی را دوست دارند، [آنان] نسبت به مؤمنان فروتن [و مهربان] و در برابر کافران سرسخت هستند، در راه الله جهاد می‌کنند و از سرزنش هیچ ملامتگری نمی‌هراسند. این فضل الله است که به هر کس بخواهد می‌بخشد؛ و الله گشایشگرِ داناست.');
INSERT INTO "islamhouse" VALUES(5,55,'5:55','[ای مؤمنان،] بی‌تردید، [یاور و] دوست شما فقط الله و پیامبرش و [همچنین] افرادی هستند که ایمان آورده‌اند؛ [همان] کسانی که نماز برپا می‌دارند و با فروتنی [و رضایت کامل] زکات می‌پردازند.');
INSERT INTO "islamhouse" VALUES(5,56,'5:56','و هر کس الله و پیامبرش و مؤمنان را به دوستی بگیرد، [از گروه الله است و] یقیناً گروه الله پیروزند.');
INSERT INTO "islamhouse" VALUES(5,57,'5:57','ای کسانی که ایمان آورده‌اید، کسانی را که دینتان را به تمسخر و بازی گرفته‌اند دوستان [خود] مگیرید؛ [خواه] از كسانى [باشند] كه پیش از شما به آنان كتاب داده شده است و [خواه از] كافران؛ و اگر ایمان دارید، از الله پروا کنید.');
INSERT INTO "islamhouse" VALUES(5,58,'5:58','و [این افراد کسانی هستند که] وقتی [توسط اذان، مردم را] به نماز مى‌خوانید، آن را به مسخره و بازى مى‌گیرند؛ زیرا آنان گروهی هستند كه نمى‌اندیشند.');
INSERT INTO "islamhouse" VALUES(5,59,'5:59','[ای پیامبر،] بگو: «ای اهل کتاب، آیا [علت اینكه‌] بر ما خرده مى‌گیرید جز این است که ما به الله و آنچه بر ما نازل شده و [به] آنچه پیش از ما نازل گردیده است ایمان آورده‌ایم و [همچنین بر این باوریم] که اغلب شما نافرمانید؟»');
INSERT INTO "islamhouse" VALUES(5,60,'5:60','بگو: «آیا [می‌خواهید] شما را از [حال] کسانی آگاه کنم که کیفری از این بدتر نزد الله دارند؟ [آنان‌] کسانی [هستند] که الله لعنتشان کرد و بر آنان خشم گرفت و برخى از آنان را [مسخ نمود و به شکل] بوزینه و خوک درآورد و [نیز] كسانى كه [به جاى الله،] طاغوت‌ [=معبودان باطل] را عبادت [و بندگی] كردند. اینان [از همه] بدمقام‌تر و از راه راست گمگشته‌ترند.');
INSERT INTO "islamhouse" VALUES(5,61,'5:61','و [ای پیامبر،] وقتی [یهودیان منافق] نزدتان می‌آیند، می‌گویند: «ایمان آورده‌ایم»؛ حال آنکه با کفر وارد شدند و قطعاً با همان [کفر] بیرون رفته‌اند؛ و الله به آنچه پنهان می‌کردند داناتر است.');
INSERT INTO "islamhouse" VALUES(5,62,'5:62','و بسیاری از آنان را می‌بینی که در گناه و تجاوز [به حقوق دیگران] و حرام‌خواری شتاب می‌کنند. چه بد است آنچه می‌کنند.');
INSERT INTO "islamhouse" VALUES(5,63,'5:63','چرا دانشمندان و فقهای [یهود] آنان را از گفتارِ گناه‌[آلود] و حرام‌خوارگی‌شان بازنمى‌دارند؟ راستى چه بد است آنچه انجام مى‌دهند!');
INSERT INTO "islamhouse" VALUES(5,64,'5:64','و یهود [هنگام سختی و تنگدستی] گفتند: «دست الله [از خیر و بخشش] بسته است». دست‌هایشان بسته باد و به سزاى آنچه گفتند لعنت بر آنان باد! [هرگز چنین نیست؛] بلکه هر دو دستِ او گشاده است [و] هر گونه که بخواهد، می‌بخشد. [ای پیامبر،] این آیات که از طرف پروردگارت بر تو نازل شده است، بر سرکشی و کفرِ بسیاری از آنان می‌افزاید. ما در میان آنان تا روز قیامت دشمنی و کینه افکندیم. هر زمان آتش جنگ برافروختند، الله آن را خاموش کرد. آنان [پیوسته] در زمین به تبهکاری [و فساد] می‌کوشند؛ و الله تبهکاران را دوست ندارد.');
INSERT INTO "islamhouse" VALUES(5,65,'5:65','و اگر اهل کتاب ایمان می‌آوردند و پرهیزگاری می‌کردند، یقیناً گناهانشان را می‌زدودیم و آنان را به باغ‌های پرنعمت [بهشت] وارد می‌کردیم.');
INSERT INTO "islamhouse" VALUES(5,66,'5:66','و اگر آنان به تورات و انجیل و آنچه از سوی پروردگارشان بر آنها نازل شده است [= قرآن] عمل می‌کردند، قطعاً از [برکات آسمانِ] بالای سرشان و از [نعمت‌های زمینِ] زیر پایشان برخوردار می‌شدند. برخی از آنان میانه‌رو [و بر حق، پایدار] هستند و[لی] بسیاری از آنان [به سبب بی‌ایمانی] کارهای بدی مرتکب می‌شوند.');
INSERT INTO "islamhouse" VALUES(5,67,'5:67','ای پیامبر، آنچه را از طرف پروردگارت بر تو نازل شده است ابلاغ کن؛ و اگر [چنین] نکنی، رسالت او را [به انجام] نرسانده‌ای؛ و الله تو را از [گزندِ] مردم حفظ می‌کند. بی‌تردید، الله گروه کافران را هدایت نمی‌کند.');
INSERT INTO "islamhouse" VALUES(5,68,'5:68','بگو: «ای اهل کتاب، شما بر هیچ [آیین درستی] نیستید؛ مگر اینکه تورات و انجیل و آنچه را که از طرف پروردگارتان بر شما نازل شده است برپا دارید». و [ای پیامبر،] بی‌تردید آنچه از سوی پروردگارت بر تو نازل شده است، بر سرکشی و کفرِ بسیاری از آنان می‌افزاید؛ پس بر [این] گروه کافران، افسوس مخور.');
INSERT INTO "islamhouse" VALUES(5,69,'5:69','در حقیقت، کسانی که [به رسالت محمد صلی الله علیه وسلم] ایمان آورده‌اند‌ و نیز کسانی [از امت‌های پیشین، قبل از بعثت محمد صلی الله علیه وسلم از] یهود و صابئان [= پیروان برخی پیامبران]، و نصاری؛ هر کس [از آنان] به الله و روز قیامت ایمان آورند و کارهای شایسته انجام دهند، پاداش آنان نزد پروردگارشان است؛ نه ترسی خواهند داشت و نه اندوهگین می‌شوند.');
INSERT INTO "islamhouse" VALUES(5,70,'5:70','یقیناً ما از بنی‌اسرائیل [در مورد اطاعت از فرمان حق] پیمان گرفتیم و پیامبرانی به سویشان فرستادیم؛ هر گاه پیامبری چیزی آورد که هوای نفسشان نمی‌پسندید، گروهی را دروغگو خواندند و گروهی را کشتند.');
INSERT INTO "islamhouse" VALUES(5,71,'5:71','[آری، یهود پیمان شکستند] و پنداشتند که [عذاب و] مجازاتی در کار نیست؛ پس کور گشتند [و حق را ندیدند] و کر شدند [و حق را نشنیدند]؛ آنگاه [توبه کردند و] الله توبه‌شان را پذیرفت. سپس بسیاری از آنان [باز هم در برابرِ هدایت] کور و کر شدند؛ و الله به آنچه انجام می‌دهند بیناست.');
INSERT INTO "islamhouse" VALUES(5,72,'5:72','کسانی که گفتند: «الله همان مسیح پسر مریم است» یقیناً کافر شدند؛ در حالی ‌که [خودِ] مسیح گفت: «ای بنی‌اسرائیل، الله را عبادت کنید که پروردگار من و پروردگار شماست. بی‌تردید، هر ‌کس به الله شرک ورزد، الله بهشت را بر او حرام کرده است و جایگاهش دوزخ است؛ و ستمکاران هیچ یاوری ندارند».');
INSERT INTO "islamhouse" VALUES(5,73,'5:73','كسانى كه [به تثلیث قائل شدند و معبود حقیقی را مجموعۀ پدر، پسر و روح القُدُس دانستند و] گفتند: «الله، یکی از [این] سه تاست [پدر، پسر، روح‌القدس]» قطعاً کافر شدند. معبودی [به‌حق] جز معبود یگانه نیست؛ و اگر از آنچه می‌گویند بازنایستند، قطعاً به افرادی از آنان که کفر ورزیدند عذاب دردناکی خواهد رسید.');
INSERT INTO "islamhouse" VALUES(5,74,'5:74','آیا به سوی الله بازنمی‌گردند و از او طلب آمرزش نمی‌کنند؟ حال آنکه الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(5,75,'5:75','مسیح پسر مریم، جز پیامبری نیست که پیش از او [نیز] پیامبران دیگری بودند و مادرش زن بسیار راستگو [و درستکار] بود؛ هر دو [مانند انسان‌های دیگر] غذا می‌خوردند. [ای پیامبر] بنگر که چگونه آیات [خود] را برایشان روشن می‌سازیم و باز بنگر که چگونه [از حقیقت] رویگردان می‌شوند.');
INSERT INTO "islamhouse" VALUES(5,76,'5:76','[ای پیامبر،] بگو: «آیا چیزی را به جای الله عبادت می‌کنید که سود و زیانی برایتان ندارد؟ و [حال آنكه] الله شنوای داناست».');
INSERT INTO "islamhouse" VALUES(5,77,'5:77','بگو: «ای اهل کتاب، در دین خود به ناحق غلو [و زیاده‌روی] نکنید و از هوس‌های گروهی پیروی نکنید که پیشتر گمراه شدند و بسیاری [از پیروانشان] را گمراه کردند و [خودشان] از راه راست منحرف گشتند».');
INSERT INTO "islamhouse" VALUES(5,78,'5:78','[افرادی] از بنی‌اسرائیل كه کفر ورزیدند، از زبان داوود [در زبور] و [از قول] عیسی پسر مریم [در انجیل] مورد لعن قرار گرفتند. این [نفرین،] به خاطر آن بود که نافرمانی ‌کردند و [از حدود الهی] تجاوز ‌می‌نمودند.');
INSERT INTO "islamhouse" VALUES(5,79,'5:79','آنان یکدیگر را از [گناهان و] کار[های] زشتی که انجام می‌دادند نهی نمی‌کردند. به راستى، چه بد بود آنچه انجام می‌دادند!');
INSERT INTO "islamhouse" VALUES(5,80,'5:80','[ای پیامبر،] بسیاری از آنان را می‌بینی که با کافران دوستی [و همکاری] می‌کنند. به راستى چه زشت است آنچه براى خود پیش فرستادند كه [در نتیجۀ آن نیز] الله بر آنان خشم گرفت و جاودانه در عذاب مى‌مانند.');
INSERT INTO "islamhouse" VALUES(5,81,'5:81','و اگر [یهود] به الله و پیامبر و آنچه بر او نازل شده است ایمان می‌آوردند، آنان [= کافران] را به دوستی نمی‌گرفتند؛ ولی بسیاری از آنان نافرمانند.');
INSERT INTO "islamhouse" VALUES(5,82,'5:82','[ای پیامبر،] یقیناً یهود و كسانى را كه شرک ورزیده‌اند، دشمن‌ترین مردم نسبت به مؤمنان خواهى یافت؛ و نزدیک‌ترین دوستان به مؤمنان را کسانی خواهى یافت که می‌گویند: «ما نَصرانی هستیم». این به سبب آن است که در میان آنان کشیشان و راهبانی [حق‌پرست‌] هستند و [نیز] از آن روست که تکبر نمی‌ورزند.');
INSERT INTO "islamhouse" VALUES(5,83,'5:83','و چون آیاتی را که بر پیامبر نازل شده است می‌شنوند، مى‌بینى بر اثر آنچه از حق شناخته‌اند، چشمانشان اشک می‌بارد و می‌گویند: «پروردگارا، ایمان آوردیم؛ پس ما را در زمرۀ [مسلمانان و] گواهان بنویس.');
INSERT INTO "islamhouse" VALUES(5,84,'5:84','چرا به الله و آنچه از حق به ما رسیده است ایمان نیاوریم؛ حال آنكه امید داریم پروردگارمان ما را با گروه شایستگان [به بهشت‌] درآورد؟»');
INSERT INTO "islamhouse" VALUES(5,85,'5:85','پس الله به پاس آنچه گفتند، به آنان باغ‌هایی [از بهشت] پاداش داد که از زیر [درختان] آن جویبارها جاری است و جاودانه در آن خواهند ماند؛ و این است جزای نیکوکاران.');
INSERT INTO "islamhouse" VALUES(5,86,'5:86','و کسانی که کفر ورزیدند و آیات ما را تکذیب کردند، آنان اهل دوزخند.');
INSERT INTO "islamhouse" VALUES(5,87,'5:87','ای کسانی که ایمان آورده‌اید، چیزهای پاکیزه‌ای را که الله برای [استفادۀ‌] شما حلال کرده است، [به بهانۀ زهد و دینداری بر خود] حرام نکنید و [از حد] تجاوز ننمایید. به راستی که الله متجاوزان [از حدود الهی] را دوست ندارد.');
INSERT INTO "islamhouse" VALUES(5,88,'5:88','و از چیزهای حلال [و] پاکیزه‌ای که الله به شما روزی داده است بخورید، و از الله که به او ایمان دارید پروا کنید.');
INSERT INTO "islamhouse" VALUES(5,89,'5:89','الله شما را به خاطر سوگندهای بیهوده [و بی‌اختیارتان] بازخواست نمی‌کند؛ ولی در سوگندهایی که [آگاهانه‌] می‌خورید [و می‌شکنید] مؤاخذه می‌کند. پس کفارۀ آن، خوراک‌ دادن به ده مستمند از غذاهای [معمولی و] متوسطِ مردم شهرتان است یا لباس ‌دادن به ده نفر [از مستمندان] یا آزاد کردن یک برده. اگر کسی [توانایی مالی ندارد یا هیچ یک از اینها را] نمی‌یابد، [باید] سه روز روزه [بگیرد]. این، کفارۀ سوگندهای شماست [برای] هر‌ زمان که سوگند یاد کردید [و آن را شکستید]؛ و سوگندهایتان را حفظ کنید [و نشکنید]. الله اینچنین آیات خود را برای شما بیان می‌کند؛ باشد که شکر به جای آورید.');
INSERT INTO "islamhouse" VALUES(5,90,'5:90','ای کسانی که ایمان آورده‌اید، بی‌تردید، شراب و قمار و نشانه‌های معبودان باطل و تیرهای قرعه‌کشی [برای فالگیری]، پلید [و] از کار[های] شیطان هستند؛ پس از آنها دوری کنید؛ باشد که رستگار شوید.');
INSERT INTO "islamhouse" VALUES(5,91,'5:91','در حقیقت، شیطان می‌خواهد با شراب و قمار، در میان شما دشمنی و کینه ایجاد کند و شما را از یاد الله و از نماز بازدارد؛ پس [ای مؤمنان، حال که حقیقت را دانستید،] آیا از آنها دست برمی‌دارید؟');
INSERT INTO "islamhouse" VALUES(5,92,'5:92','و از الله اطاعت کنید و فرمانبرِ پیامبر باشید و [از گناه و نافرمانی] پرهیز کنید. اما اگر روی گرداندید، بدانید که وظیفۀ پیامبر ما [چیزی] جز ابلاغ آشکار نیست.');
INSERT INTO "islamhouse" VALUES(5,93,'5:93','بر کسانی‌ که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، [ایراد و] گناهی در آنچه [پیش از حکم تحریم شراب] خورده‌اند نیست؛ [البته] در صورتى كه [از محرّمات] پروا كنند و ایمان بیاورند و كارهاى شایسته انجام دهند؛ سپس پرهیزگاری كنند و ایمان بیاورند؛ آنگاه [بیش از پیش] پرهیزگاری كنند و نیکی نمایند؛ و الله نیکوکاران را دوست ‌دارد.');
INSERT INTO "islamhouse" VALUES(5,94,'5:94','ای کسانی که ایمان آورده‌اید، الله شما را [در حالی که مُحرِم هستید] به چیزى از شكار [صحرایی] كه در دسترس شما و نیزه‌هایتان باشد می‌آزماید تا معلوم دارد چه كسى در نهان از او مى‌ترسد. پس هر کس بعد از این [آزمون] از حد درگذرد [و در حال حج یا عمره شکار کند]، عذابی دردناک [در پیش‌] دارد.');
INSERT INTO "islamhouse" VALUES(5,95,'5:95','ای کسانی که ایمان آورده‌اید، در حال احرام، شکار را نکشید؛ و هر ‌یک از شما که به عمد آن را بکشد، باید همانند آنچه که کشته است، کفاره‌ای از چهارپایان بدهد؛ [به شرطی] که دو نفر عادل از شما [برابریِ ارزشِ] آن را تأیید کنند و [آنگاه آن کفاره را به صورت] قربانی به [مستمندان پیرامون] کعبه برساند؛ یا [معادلِ بهای آن را] به مستمندان [حرم] غذا دهد یا [اگر توان مالى نداشت] معادل آن را [به ازای هر فقیر، یک روز] روزه بگیرد تا کیفر عمل خود را بچشد. الله آنچه را که [پیش از این تحریم] گذشته است عفو نمود؛ و[لی] هر کس [به این گناه] بازگردد، [بداند که] الله از او انتقام می‌گیرد؛ و الله شکست‌ناپذیرِ انتقام‌گیرنده است.');
INSERT INTO "islamhouse" VALUES(5,96,'5:96','[ای مسلمانان، در حال احرام] صید دریایی و خوراک آن برای شما حلال شده است تا شما [که در مکه مقیم هستید] و کاروانیان [که مسافرند، همگی‌] از آن برخوردار شوید؛ و[لی] تا زمانی که مُحرِم هستید، صید صحرایی بر شما حرام است؛ و از الله که [روز قیامت] نزد او جمع می‌شوید، پروا کنید.');
INSERT INTO "islamhouse" VALUES(5,97,'5:97','الله کعبۀ بیت ‌الحرام را وسیله‌ای برای استواری [و سامان بخشیدن به کار] مردم قرار داد و [همچنین حرمتِ جنگ و درگیری در] ماه حرام و [کشتنِ] قربانی [بی‌نشان] و قربانی قلاده‌دار [را برای آسایش مردم وضع کرد]. این [گونه احکام،] برای آن است که بدانید الله آنچه را که در آسمان‌ها و زمین است می‌داند و اینکه الله به همه چیز داناست.');
INSERT INTO "islamhouse" VALUES(5,98,'5:98','[ای مردم،] بدانید که الله سخت‌کیفر است و [در عین حال،] الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(5,99,'5:99','پیامبر وظیفه‌ای جز رساندن [وحی] ندارد؛ و الله آنچه را که آشکار می‌کنید و پنهان می‌دارید [همه را] می‌داند.');
INSERT INTO "islamhouse" VALUES(5,100,'5:100','[ای پیامبر،] بگو: «پلید و پاک یكسان نیستند؛ هر چند که فراوانیِ پلید[ی] تو را به شگفت آورَد. پس اى خردمندان، از الله پروا كنید؛ باشد كه رستگار شوید».');
INSERT INTO "islamhouse" VALUES(5,101,'5:101','ای کسانی که ایمان آورده‌اید، از چیزهایی نپرسید که اگر [پاسخِ حقیقی‌اش] برایتان آشکار گردد، شما را اندوهگین می‌کند و اگر زمانی که قرآن نازل می‌گردد پرس‌وجو کنید، [حکم و پاسخِ پرسش‌هایتان] بر شما آشکار می‌گردد. الله از آن [سوالات نابجای قبلی و پاسخ به آنها] درگذشت؛ و الله آمرزندۀ بردبار است‌.');
INSERT INTO "islamhouse" VALUES(5,102,'5:102','در حقیقت، گروهی [که] پیش از شما [بودند نیز] از این [گونه] سؤال‌ها پرسیدند؛ سپس [وقتی که جوابی ناگوار برایشان آمد،] به آن [عمل نکردند و] کفر ورزیدند.');
INSERT INTO "islamhouse" VALUES(5,103,'5:103','الله هیچ گونه «بَحیره» و «سائِبه» و «وَصیله» و «حام» [= انواع شترهای مختلفی که مشرکان وقف معبود‌هایشان می‌کردند] را [برای خود] قرار نداده است؛ ولی کسانی ‌که کفر ورزیدند، [با ادعای حرمتِ این حیوانات] بر الله دروغ می‌بندند؛ و بیشتر آنان نمی‌اندیشند.');
INSERT INTO "islamhouse" VALUES(5,104,'5:104','و هنگامی ‌که به آنان گفته شود: «به سوی آنچه الله نازل کرده است و به سوی پیامبر بیایید» می‌گویند: «آنچه نیاکانمان را بر آن یافته‌ایم، برایمان کافی است». آیا اگر نیاکانشان چیزی نمی‌دانستند و هدایت نیافته بودند [باز هم از آنان پیروی می‌کنند]؟');
INSERT INTO "islamhouse" VALUES(5,105,'5:105','ای کسانی که ایمان آورده‌اید، مراقب [ایمان و طاعت] خود باشید. اگر هدایت یافته باشید، گمراهیِ کسانی‌ که گمراه شده‌اند [چنانچه به امر به معروف و نهی از منکر پایبند باشید] زیانی به شما نمی‌رساند. بازگشت همۀ شما به سوی الله است؛ آنگاه شما را از آنچه می‌کردید، آگاه می‌سازد.');
INSERT INTO "islamhouse" VALUES(5,106,'5:106','ای کسانی که ایمان آورده‌اید، هنگامی که [نشانه‌های] مرگ یکی از شما فرا رسید، در هنگام وصیت، باید از میان خودتان دو نفر عادل را گواه بگیرید یا اگر به سفر رفتید و پیشامدِ مرگ به شما رسید [و شاهد مسلمان نیافتید،] دو نفر از غیر خود [= غیرمسلمانان] را به گواهی بطلبید؛ و اگر [در راستگویی آنها] تردید داشتید، آن دو را بعد از نماز نگاه دارید تا به الله سوگند بخورند که: «ما حاضر نیستیم آن [وصیت] را به چیزی بفروشیم، اگرچه [به سودِ] خویشانمان باشد؛ و گواهیِ الله را کتمان نمی‌کنیم که در این صورت قطعاً از گناهکاران خواهیم بود».');
INSERT INTO "islamhouse" VALUES(5,107,'5:107','پس اگر مشخص شد که آن دو مرتکب گناه [خیانت و دروغگویی] شده‌اند، دو تن از کسانی که مورد جفای گواهان قرار گرفته‌اند و از نزدیکان [متوفی‌] هستند، به جای آنان برخیزند و به الله سوگند بخورند که: «گواهیِ ما از گواهیِ آن دو درست‌تر است و ما [از حق] تجاوز نمی‌کنیم و در غیر این صورت، از ستمکاران خواهیم بود».');
INSERT INTO "islamhouse" VALUES(5,108,'5:108','این [روش،] نزدیک‌تر است به آنکه شهادت به صورت درست آن ادا شود یا شاهدان بترسند که پس از سوگند آنان [= ورثه‌] سوگندهایشان رد شود [و خیانتشان آشکار گردد]؛ و از الله پروا کنید و [این پندها را] بشنوید و [بدانید که‌] الله مردم [بدکار و] نافرمان را هدایت نمی‌کند.');
INSERT INTO "islamhouse" VALUES(5,109,'5:109','روزی [خواهد رسید] که الله پیامبران را گرد می‌آورد و می‌پرسد: «[امت‌هایتان] چه پاسخی به [دعوت] شما دادند؟» آنان گویند: «ما دانشی [به حقیقتِ امر] نداریم. بی‌تردید، تویی که دانای رازهای نهانی»‌.');
INSERT INTO "islamhouse" VALUES(5,110,'5:110','[ای پیامبر، یاد کن از] هنگامى كه الله فرمود: «اى عیسى پسر مریم، نعمت مرا بر خود و بر مادرت به یاد آور؛ آنگاه كه تو را با روح ‌القُدُس [= جبرئیل] تأیید [و یاری] كردم كه در گهواره [به اعجاز،] و در میانسالی [به وحى‌] با مردم سخن می‌گفتى؛ و آنگاه كه به تو نوشتن و حكمت و تورات و انجیل ‌آموختم؛ و آنگاه كه به فرمان من، از گِل، [چیزی] به شكل پرنده مى‌ساختى و در آن مى‌دمیدى و به فرمان من پرنده‌اى مى‌شد؛ و كورِ مادرزاد و بیمار مبتلا به پیسی را به فرمان من شفا مى‌دادى؛ و آنگاه كه مردگان را به فرمان من [زنده از قبر] بیرون مى‌آوردى؛ و آنگاه كه [آزارِ‌] بنى‌اسرائیل را ـ وقتی معجزات آشکار برایشان آورده بودى ـ از تو بازداشتم. پس كسانى از آنان كه كفر ورزیده بودند گفتند: «این‌ [کارها چیزى‌] نیست مگر افسونى آشكار».');
INSERT INTO "islamhouse" VALUES(5,111,'5:111','و [ای عیسی، به یاد آور] زمانی که به حواریون وحی فرستادم که: «به من و به فرستاده‌ام ایمان بیاورید». آنان گفتند: «ایمان آوردیم و گواه باش که ما تسلیم [فرمانت] شده‌ایم».');
INSERT INTO "islamhouse" VALUES(5,112,'5:112','و [یاد کن از] آنگاه که حواریون گفتند: «ای عیسی پسر مریم، آیا پروردگارت می‌تواند سفره‌ای [پر از غذا] از آسمان بر ما نازل کند؟». [عیسی در پاسخ] گفت: «اگر مؤمن هستید، از الله پروا کنید».');
INSERT INTO "islamhouse" VALUES(5,113,'5:113','آنان گفتند: «می‌خواهیم از آن بخوریم و دل‌هایمان [به رسالت تو] اطمینان یابد و بدانیم که به ما راست گفته‌ای و خود از گواهانِ آن باشیم».');
INSERT INTO "islamhouse" VALUES(5,114,'5:114','عیسی ـ پسر مریم ـ [دعا کرد و] گفت: «بار الها، ای پروردگار ما، سفره‌ای [پرغذا] از آسمان بر ما بفرست تا عیدی برای امروزیان و آیندگانمان باشد و [همچنین] معجزه‌ای از جانب تو باشد؛ و به ما روزی ده که تو بهترین روزی‌دهنده‌ای».');
INSERT INTO "islamhouse" VALUES(5,115,'5:115','الله [دعای او را اجابت کرد و] فرمود: «من آن [سفره] را بر شما نازل می‌کنم؛ اما هر یک از شما که پس از آن کفر بورزد، او را به چنان عذابی [سخت] دچار می‌کنم که هیچ یک از جهانیان را آنچنان، عذاب نکرده باشم».');
INSERT INTO "islamhouse" VALUES(5,116,'5:116','و [یاد کن از] هنگامی که [روز قیامت] الله می‌فرماید: «ای عیسی ـ پسر مریم، آیا تو به مردم گفتی که: «من و مادرم را [همچون] دو معبود به جای الله برگزینید؟» [عیسی] می‌گوید: «[بار الها،] تو منزّهی. شایسته نیست كه من [دربارۀ خویشتن‌] چیزى كه حقم نیست بگویم. اگر چنین [سخنی] گفته باشم، بی‌گمان، تو می‌دانی. تو آنچه را که در نفْس من است مى‌دانى و[لی] من آنچه را که در ذات [پاکِ] توست نمى‌دانم. بی‌تردید، تو خود داناى رازهاى نهانى.');
INSERT INTO "islamhouse" VALUES(5,117,'5:117','جز آنچه مرا به آن فرمان دادی چیزی به آنان نگفتم. [بلکه گفتم که:] «الله را عبادت کنید که پروردگار من و شماست»؛ و تا زمانی که در میان آنان به سر می‌بردم، بر [سخنان] آنان گواه بودم؛ و چون مرا [به سوی خویش] برگرفتی، تو خود مراقبشان بودی؛ و تو بر هر چیز گواهی.');
INSERT INTO "islamhouse" VALUES(5,118,'5:118','اگر آنان را [با عدالت خویش] عذاب کنی، بندگان تو هستند؛ و اگر آنان را [با رحمت خود] ببخشایی، تویی که شکست‌ناپذیرِ حکیمی».');
INSERT INTO "islamhouse" VALUES(5,119,'5:119','الله می‌فرماید: «امروز روزی است که راستیِ راستگویان به آنان سود می‌بخشد. آنان باغ‌هایی [در بهشت] دارند که جویبارها از زیر [درختان] آن جاری است و تا ابد جاودانه در آن می‌مانند. الله از [اعمال] آنان راضی است و آنان [نیز] از [پاداشِ] او راضی هستند. این همان رستگاری بزرگ است».');
INSERT INTO "islamhouse" VALUES(5,120,'5:120','فرمانرواییِ آسمان‌ها و زمین و آنچه در آنهاست از آنِ الله است و او بر هر چیزی تواناست.');
INSERT INTO "islamhouse" VALUES(6,1,'6:1','ستایش الله را كه آسمان‌ها و زمین را آفرید و تاریکی‌ها و روشنایى [= شب و روز] را پدید آورد. با این همه، كسانى كه كفر ورزیده‌اند، [دیگران را] با پروردگار خود برابر مى‌دانند.');
INSERT INTO "islamhouse" VALUES(6,2,'6:2','اوست كه شما را از گِل آفرید، آنگاه مدتى را [براى حیات شما] مقرر داشت؛ و اَجلِ حتمى نزد اوست. با این همه، شما [در قدرت او برای برپایی قیامت] تردید دارید.');
INSERT INTO "islamhouse" VALUES(6,3,'6:3','و اوست الله [معبود حقیقی و یگانه] در آسمان‌ها و زمین؛ نهان و آشكارتان را مى‌داند و آنچه را به دست مى‌آورید [و انجام می‌دهید نیز] مى‌داند.');
INSERT INTO "islamhouse" VALUES(6,4,'6:4','و هیچ یک از نشانه‌های پروردگارشان برای آنان [= کافران] نمی‌آید، مگر اینکه از آن رویگردان می‌شوند.');
INSERT INTO "islamhouse" VALUES(6,5,'6:5','همانا آنان حق را هنگامى كه به سویشان آمد دروغ شمردند؛ پس به زودى [حقیقت] خبرهاى آنچه را كه مسخره‌اش می‌کردند، به آنان خواهد رسید.');
INSERT INTO "islamhouse" VALUES(6,6,'6:6','آیا [کافران] ندیده‌اند که پیش از آنان چه [بسیار] گروه‌هایی از مردم را نابود کردیم؟ [امت‌هایی] که در زمین به آنان چنان اقتدار [و نیرویی] داده بودیم که [هرگز] به شما چنان امکاناتی نداده‌ایم؛ و باران آسمان را [فراوان و] پیاپى بر آنان فرستادیم و نهرها پدید آوردیم كه زیر [خانه‌های] آنان جارى بود. اما [هنگامی که ناسپاسی کردند ما] آنان را به [سزاى‌] گناهانشان نابود كردیم و پس از آنان گروهی دیگر پدید آوردیم.');
INSERT INTO "islamhouse" VALUES(6,7,'6:7','و [ای پیامبر، حتی] اگر کتابی [نوشته] بر کاغذ بر تو نازل می‌کردیم و اینان آن [کتاب] را با دستان خویش لمس می‌کردند، قطعاً کسانی ‌که کفر ورزیدند می‌گفتند: «این [کتاب، چیزی] نیست مگر جادویی آشکار».');
INSERT INTO "islamhouse" VALUES(6,8,'6:8','و [کافران] گفتند: «چرا فرشته‌ای بر او نازل نشده است [تا گواه راستگویی‌اش باشد]؟» و اگر فرشته‌ای می‌فرستادیم [و ایمان نمی‌آوردند]، قطعاً کار به پایان می‌رسید و مهلتی [برای توبه] نمی‌یافتند [و هلاک می‌شدند].');
INSERT INTO "islamhouse" VALUES(6,9,'6:9','و اگر آن [فرستاده] را فرشته‌ای قرار می‌دادیم، حتماً وى را به شکل مردى درمی‌آوردیم [تا بتوانند او را ببینند و سخنانش را بفهمند] و یقیناً آنان را دچار همان اشتباهی می‌کردیم که قبلاً در آن بودند [زیرا آن فرشته نیز صورت انسان داشت].');
INSERT INTO "islamhouse" VALUES(6,10,'6:10','[ای پیامبر،] قطعاً پیامبرانِ پیش از تو [نیز از سوی کافران] مورد تمسخر واقع شدند؛ پس آنچه که مسخره‌اش می‌کردند دامانشان را ‌گرفت [و گرفتار عذاب شدند].');
INSERT INTO "islamhouse" VALUES(6,11,'6:11','[ای پیامبر، به کافران] بگو: «در زمین بگردید و بنگرید که سرانجامِ تکذیب‌کنندگان [آیات الهی] چگونه بوده است».');
INSERT INTO "islamhouse" VALUES(6,12,'6:12','بگو: «آنچه در آسمان‌ها و زمین است از آنِ کیست؟» بگو: «از آنِ الله است که رحمت را بر خویش واجب گردانیده است [و] قطعاً شما را در روز قیامت ـ که در [وقوع] آن شکی نیست ـ [نزد خود] جمع خواهد کرد؛ و کسانی که خویش را تباه کرده‌اند، ایمان نمی‌آورند.');
INSERT INTO "islamhouse" VALUES(6,13,'6:13','و هر چه در شب و روز، آرام [و قرار] دارد از آنِ اوست؛ و او شنواى داناست».');
INSERT INTO "islamhouse" VALUES(6,14,'6:14','بگو: «آیا غیر الله را [برای خود دوست و] کارساز گیرم؟ [پروردگاری] که پدیدآورندۀ آسمان‌ها و زمین است و اوست که روزی می‌دهد و خود روزی داده نمی‌شود». بگو: «من فرمان یافته‌ام که نخستین کسی [از این امت] باشم که تسلیم [اوامر الهی] شده است و [الله به من فرمان داد که:] "هرگز از مشرکان نباش"».');
INSERT INTO "islamhouse" VALUES(6,15,'6:15','بگو: «اگر من از [حُکمِ] پروردگارم نافرمانی کنم، بی‌تردید از عذاب روز بزرگ می‌ترسم».');
INSERT INTO "islamhouse" VALUES(6,16,'6:16','هر کس که در آن روز به او عذاب نرسد، قطعاً [الله] به وی رحم کرده است و این همان پیروزی آشکار است.');
INSERT INTO "islamhouse" VALUES(6,17,'6:17','[ای انسان،] اگر الله [بخواهد] زیانی به تو رسانَد، هیچ کس جز او برطرف‌کنندۀ آن نیست؛ و اگر [اراده کند که] خیری به تو رسانَد، [کسی بازدارندۀ آن نیست] پس او بر همه چیز تواناست.');
INSERT INTO "islamhouse" VALUES(6,18,'6:18','و او بر بندگانش چیره و فوق [آنان] است؛ و او حکیمِ آگاه است.');
INSERT INTO "islamhouse" VALUES(6,19,'6:19','[ای پیامبر، به مشرکان] بگو: «گواهى چه كسى از همه برتر است؟» بگو: «الله [که] میان من و شما گواه است؛ و این قرآن [از جانب او] بر من وحی شده است تا به وسیلۀ آن به شما و هر کس [این پیام به او] می‌رسد هشدار دهم. آیا شما به راستی گواهی می‌دهید که با الله معبودان دیگری هست؟» بگو: «من [هرگز به چنین چیزی] گواهی نمی‌دهم». بگو: «یقیناً او تنها معبود یگانه است و من از آنچه [با او] شریک قرار می‌دهید بیزارم».');
INSERT INTO "islamhouse" VALUES(6,20,'6:20','كسانى كه به آنان كتاب [آسمانى‌] داده‌ایم، همان گونه كه پسران خود را می‌شناسند، او [= پیامبر] را مى‌شناسد. کسانی ‌که [با پیروی از هوای نفس] به خویش زیان رسانده‌اند، ایمان نمی‌آورند.');
INSERT INTO "islamhouse" VALUES(6,21,'6:21','و کیست ستمکارتر از آن کس که بر الله دروغ می‌بندد [و برایش شریک می‌پندارد] یا آیاتش را دروغ می‌انگارد؟ یقیناً ستمکاران [مشرک] رستگار نمی‌شوند.');
INSERT INTO "islamhouse" VALUES(6,22,'6:22','و [یاد کن از قیامت:] روزی ‌که همۀ آنان را جمع می‌کنیم و به کسانی‌ که شرک ورزیده‌اند می‌گوییم: «معبودانتان که [شریک الله] می‌پنداشتید کجا هستند؟»');
INSERT INTO "islamhouse" VALUES(6,23,'6:23','آنگاه [پاسخ و] عذرشان جز این نیست که می‌گویند: «به الله ـ پروردگارمان ـ سوگند که ما مشرک نبودیم».');
INSERT INTO "islamhouse" VALUES(6,24,'6:24','[ای پیامبر،] بنگر چگونه بر خود دروغ ‌بستند و آنچه [در مورد شفاعت معبودانشان] به دروغ می‌گفتند، [همه] محو و نابود شد.');
INSERT INTO "islamhouse" VALUES(6,25,'6:25','و افرادی از آنان [= مشرکان] هستند که [هنگام تلاوت قرآن] به تو گوش می‌سپارند و[لی] ما بر دل‌هایشان پرده‌ای افکنده‌ایم تا آن را نفهمند و در گوش‌هایشان سنگینی [قرار داده‌ایم]؛ و اگر تمام معجزات [و دلایل توحید] را ببینند، [باز هم] به آن ایمان نمی‌آورند تا نزدت بیایند و با تو مجادله کنند. کسانی‌ که کفر ورزیدند می‌گویند: «این [قرآن، چیزی] نیست مگر افسانه‌های [خیالی] پیشینیان».');
INSERT INTO "islamhouse" VALUES(6,26,'6:26','و آنان [مردم] را از پیروی او [= پیامبر] بازمی‌دارند و خود [نیز] از او دور می‌شوند و [کسی] جز خویشتن را هلاک نمی‌کنند و[لی] نمی‌دانند.');
INSERT INTO "islamhouse" VALUES(6,27,'6:27','و اگر [آنان را] هنگامی‌ که در برابر آتش [دوزخ] نگه داشته‌ شده‌اند ببینی، [از حالشان شگفت‌زده می‌شوی که] می‌گویند: «ای کاش [به دنیا] بازگردانده می‌شدیم و آیات پروردگارمان را تکذیب نمی‌کردیم و از مؤمنان ‌بودیم!»');
INSERT INTO "islamhouse" VALUES(6,28,'6:28','[چنین نیست که می‌گویند؛] بلکه آنچه پیش از این پنهان می‌کردند برایشان آشکار شده است و اگر [هم به دنیا] بازگردانده ‌شوند، بی‌تردید، به آنچه از آن نهی شده بودند بازمی‌گردند و اینان دروغگو هستند.');
INSERT INTO "islamhouse" VALUES(6,29,'6:29','و [مشرکان] گفتند: «جز زندگى ما در دنیا هیچ [زندگى دیگرى‌] نیست و هرگز برانگیخته نخواهیم شد».');
INSERT INTO "islamhouse" VALUES(6,30,'6:30','و اگر آنان را هنگامی که در پیشگاه پروردگارشان نگه داشته شده‌اند ببینی، [شگفت‌زده می‌شوی. الله به آنان] می‌فرماید: «آیا این [زندگی پس از مرگ] حق نیست؟» آنان می‌گویند: «بله، قسم به پروردگارمان [که حق است]. [الله] می‌فرماید: «پس به سزای آنکه کفر می‌ورزیدید، این عذاب را بچشید».');
INSERT INTO "islamhouse" VALUES(6,31,'6:31','کسانی که دیدار پروردگار را تکذیب کردند، قطعاً زیان دیدند؛ [پس] هنگامی ‌که ناگهان قیامت بر آنان فرا رسد، در حالى كه بار [سنگین] گناهان خویش را بر پشتشان حمل مى‌كنند می‌گویند: «اى دریغ بر ما از آنچه دربارۀ آن [در دنیا] كوتاهى كردیم!». هان! چه بد است باری که[بر دوش] می‌کشند!');
INSERT INTO "islamhouse" VALUES(6,32,'6:32','زندگی دنیا [چیزی] جز بازیچه و سرگرمی نیست؛ و قطعاً سرای آخرت برای کسانی ‌که پروا پیشه می‌کنند بهتر است. آیا نمی‌اندیشید؟');
INSERT INTO "islamhouse" VALUES(6,33,'6:33','[ای پیامبر،] به یقین ما مى‌دانیم كه آنچه [مشرکان] مى‌گویند، تو را سخت غمگین مى‌كند. در واقع، آنان [در دل،] تو را تكذیب نمى‌كنند؛ بلکه ستمكاران [با آنکه حقیقت را می‌دانند] آیاتِ الله را انكار مى‌كنند.');
INSERT INTO "islamhouse" VALUES(6,34,'6:34','و پیش از تو نیز پیامبرانى تكذیب شدند؛ ولى بر آنچه تكذیب شدند و آزار دیدند، شكیبایى كردند تا [سرانجام] یارى ما به آنان فرارسید؛ و سخنان الله [در مورد پیروزی پیامبرانش] را هیچ تغییردهنده‌ای نیست [که مانع گردد]؛ و مسلّماً اخبار پیامبران [پیشین و داستان شکست دشمنانشان] به تو رسیده است.');
INSERT INTO "islamhouse" VALUES(6,35,'6:35','و [ای پیامبر،] اگر رویگردانیِ آنان [از قرآن،‌] بر تو ناگوار [و دشوار] است، اگر مى‌توانى حفره‌ای در زمین یا نردبانى در آسمان بجویى تا معجزه‌اى [دیگر] برایشان بیاورى [پس چنان كن. ولی سودی ندارد‌]؛ و اگر الله مى‌خواست، قطعاً آنان را بر [مسیر] هدایت جمع می‌کرد؛ پس هرگز از نادانان مباش.');
INSERT INTO "islamhouse" VALUES(6,36,'6:36','به راستی، تنها كسانى [دعوت حق را] می‌پذیرند كه می‌شنوند [و درک می‌کنند]؛ و الله مردگان را [روز قیامت‌] برمی‌انگیزد سپس به سوى او بازگردانده مى‌شوند.');
INSERT INTO "islamhouse" VALUES(6,37,'6:37','و [مشرکان] گفتند: «چرا نشانه‌ای [دربارۀ حقّانیتِ محمد] از طرف پروردگارش بر او نازل نشده است؟» [ای پیامبر،] بگو: «بی‌گمان، الله تواناست که نشانه‌ای [مطابق خواست آنان] نازل کند»؛ ولی بیشتر آنان نمی‌دانند [که نزول وحی، بر اساس حکمت و تدبیر است].');
INSERT INTO "islamhouse" VALUES(6,38,'6:38','و هیچ جنبنده‌اى در زمین نیست و نه هیچ پرنده‌اى كه با دو بال‌ خود پرواز مى‌كند، مگر آنكه آنها [نیز در آفرینش و زندگی،] گروه‌هایی مانند شما هستند. ما هیچ چیزى را در كتاب [= لوح محفوظ] فروگذار نكرده‌ایم؛ سپس [همه، روز قیامت‌] نزد پروردگارشان گرد آورده می‌شوند.');
INSERT INTO "islamhouse" VALUES(6,39,'6:39','و کسانی ‌که آیات ما را تکذیب کردند، کر و گُنگ و در تاریکی‌ها[ى جهل و کفر، سرگشته] هستند. الله هر کس را بخواهد گمراه می‌کند و هر ‌کس را بخواهد بر راه راست قرار می‌دهد.');
INSERT INTO "islamhouse" VALUES(6,40,'6:40','[ای پیامبر، به مشرکان] بگو: «اگر راست می‌گویید، به من خبر دهید که اگر عذابِ الله به سراغتان بیاید یا [ناگهان] قیامت برپا شود، آیا غیر از الله را [به یاری] می‌خوانید؟».');
INSERT INTO "islamhouse" VALUES(6,41,'6:41','آری، تنها او را [به دعا] می‌خوانید؛ و اگر او بخواهد، رنج و بلا را که به خاطر آن او را فرامی‌خوانید از بین می‌برد و شما [نیز از ترس عذاب،] شریکی را که برایش قائل شده‌اید، از یاد می‌برید.');
INSERT INTO "islamhouse" VALUES(6,42,'6:42','[ای پیامبر،] بی‌تردید، پیش از تو [نیز پیامبرانی را] به سوی امت‌ها[ی پیشین‌] فرستادیم و آنان [= امت‌ها] را به تنگدستی و ناخوشی دچار ساختیم؛ باشد که [به درگاه الهی فروتنی و] تضرع کنند.');
INSERT INTO "islamhouse" VALUES(6,43,'6:43','پس چرا هنگامی‌ که عذاب ما به آنان رسید تضرع نکردند؟ ولى [واقعیت این است که] دل‌هایشان سخت شده است و شیطان آنچه را انجام مى‌دادند برایشان آراسته است.');
INSERT INTO "islamhouse" VALUES(6,44,'6:44','وقتی [مشرکان] آنچه را كه به آن پند داده شده بودند فراموش كردند، درهاى هر چیزى [از نعمت‌های الهی] را بر آنان گشودیم تا وقتی که به آنچه داده شدند شاد گشتند؛ ناگهان آنان را [به کیفر] گرفتیم؛ پس همان دَم [همگی از نجات و رحمت] مأیوس شدند.');
INSERT INTO "islamhouse" VALUES(6,45,'6:45','بنابر این، ریشۀ آن گروهى كه ستم كردند بركنده شد؛ و ستایش مخصوص الله ـ پروردگار جهانیان‌ـ است.');
INSERT INTO "islamhouse" VALUES(6,46,'6:46','[ای پیامبر، به مشرکان] بگو: «به من خبر دهید اگر الله گوش و چشم‌هایتان را بگیرد و بر دل‌هایتان مُهر بزند، کدام معبود جز الله [می‌تواند] آنها را به شما بازگرداند؟» ببین چگونه آیات را [به شیوه‌های] گوناگون [بیان] می‌کنیم آنگاه آنان روی می‌گردانند.');
INSERT INTO "islamhouse" VALUES(6,47,'6:47','بگو: «بیندیشید اگر عذاب الهی به ناگاه یا آشکارا به سراغ شما بیاید، آیا جز گروه ستمکاران [كسى‌] نابود می‌شود؟»');
INSERT INTO "islamhouse" VALUES(6,48,'6:48','و ما پیامبران را جز بشارت‌گر و بیم‌دهنده نمی‌فرستیم؛ پس هر‌ کس ایمان بیاورد و [خویشتن را] اصلاح کند، نه ترسی بر آنان است و نه اندوهگین می‌شوند.');
INSERT INTO "islamhouse" VALUES(6,49,'6:49','و كسانى كه آیات ما را دروغ شمردند، به [سزاى‌] آنكه نافرمانى مى‌كردند، عذاب [الهی] دامنگیرشان خواهد شد.');
INSERT INTO "islamhouse" VALUES(6,50,'6:50','[ای پیامبر، به مشرکان] بگو: «من نمی‌گویم گنجینه‌های الله نزد من است و غیب [نیز] نمی‌دانم و به شما نمی‌گویم که من فرشته‌ام. من از چیزی پیروی نمی‌کنم مگر از آنچه که به من وحی می‌گردد». بگو: «آیا نابینا و بینا [= کافر و مؤمن] یکسانند؟ آیا نمی‌اندیشید؟»');
INSERT INTO "islamhouse" VALUES(6,51,'6:51','و [ای پیامبر،] با این [قرآن،‌] به آنان كه می‌ترسند به سوى پروردگارشان [برانگیخته] و گرد آورده شوند هشدار ده؛ [چرا] كه جز او نه [دوست و] کارسازی برایشان هست و نه شفاعتگری؛ باشد كه پروا كنند.');
INSERT INTO "islamhouse" VALUES(6,52,'6:52','و کسانی را که [فقیرند و] سحرگاهان و شامگاهان پروردگارشان را می‌خوانند [و] خشنودی او را می‌طلبند [از مجالس خود] طرد نکن [تا به بزرگان مشرکان توجه کنی]؛ نه چیزی از حساب آنها بر عهدۀ توست و نه چیزی از حساب تو بر عهدۀ آنهاست که [بخواهی] آنها را [از پیرامونت] طرد کنی و از ستمکاران شوی.');
INSERT INTO "islamhouse" VALUES(6,53,'6:53','و اینچنین برخى از آنان را با برخى دیگر آزمودیم تا [کافران ثروتمند دربارۀ مؤمنان مستمند] بگویند: «آیا از میان ما اینها هستند كه الله [با بخشش توفیق ایمان] بر آنان منت نهاده است؟» آیا الله به [حال‌] سپاسگزاران داناتر نیست؟');
INSERT INTO "islamhouse" VALUES(6,54,'6:54','و [ای پیامبر،] هر گاه كسانى كه به آیات ما ایمان دارند نزد تو ‌آیند، بگو: «سلام بر شما. پروردگارتان [در حق شما بخشایش و] رحمت را بر خود مقرر داشته است. هر یک از شما که از روی نادانى كار بدى انجام دهد سپس توبه کند و [گذشتۀ خویش را] اصلاح نماید، [بداند که] یقیناً اوتعالی آمرزندۀ مهربان است».');
INSERT INTO "islamhouse" VALUES(6,55,'6:55','و اینچنین آیات [خود] را شرح می‌دهیم تا راه گناهكاران روشن شود [و شما از آن دوری کنید].');
INSERT INTO "islamhouse" VALUES(6,56,'6:56','[ای پیامبر، به مشرکان] بگو: «من نهی شده‌ام از اینکه کسانی‌ را عبادت کنم که شما به جای الله می‌خوانید». بگو: «من از هوس‌هایتان پیروی نمی‌کنم که [اگر چنین کنم،] قطعاً گمراه شده‌ام و از هدایت‌یافتگان نخواهم بود».');
INSERT INTO "islamhouse" VALUES(6,57,'6:57','بگو: «من دلیل روشنی از پروردگارم دارم و[لی] شما آن را دروغ پنداشتید؛ و آنچه به شتاب خواستارش هستید [= عذاب الهی] نزد من نیست. فرمان، تنها از آنِ الله است [که] حق را بیان می‌کند و او بهترین جداکننده[ی حق از باطل] است».');
INSERT INTO "islamhouse" VALUES(6,58,'6:58','[ای پیامبر، به مشرکان] بگو: «اگر آنچه که به شتاب خواستارش هستید نزد من بود، [بر سرتان فرود می‌آوردم و] کار میان من و شما به پایان می‌رسید؛ و[لی] الله به [حال] ستمکاران داناتر است [و به آنان مهلت می‌دهد]».');
INSERT INTO "islamhouse" VALUES(6,59,'6:59','و کلیدهای [گنجینه‌های‌] نهان نزد اوست [و] جز او [هیچ کس] آن را نمی‌داند و [اوست که] آنچه را در خشکی و دریاست می‌داند و هیچ برگی [از درخت‌] نمی‌افتد مگر اینکه آن را می‌داند؛ و هیچ دانه‌ای در تاریکی‌های زمین و هیچ تر و خشکی نیست مگر اینكه در كتابى روشن [= لوح محفوظ] قرار دارد.');
INSERT INTO "islamhouse" VALUES(6,60,'6:60','و اوست كه شبانگاه روحتان را [هنگام خواب‌] مى‌گیرد و آنچه را در روز به دست آورده‌اید مى‌داند؛ سپس شما را در آن [= روز] بیدار مى‌كند تا آنکه هنگامى معیّن سپری گردد؛ آنگاه [که وفات کردید، در قیامت] بازگشتتان به سوى اوست؛ سپس شما را از آنچه انجام مى‌دادید، آگاه می‌کند.');
INSERT INTO "islamhouse" VALUES(6,61,'6:61','و او بر بندگانش چیره [و فوق آنان] است و نگهبانانی [از فرشتگان] بر شما می‌فرستد تا زمانی که مرگ یکی از شما فرارسد فرستادگان ما [با همراهی فرشتۀ مرگ] جانش را می‌گیرند و آنان [در مأموریت خود] کوتاهی نمی‌کنند.');
INSERT INTO "islamhouse" VALUES(6,62,'6:62','آنگاه به سوی الله که [یار و] کارسازِ راستینشان است بازگردانده می‌شوند. آگاه باشید! حکم از آنِ اوست و او سریع‌ترین حسابرس است‌.');
INSERT INTO "islamhouse" VALUES(6,63,'6:63','[ای پیامبر، به مشرکان] بگو: «کیست که شما را از تاریکی‌ها [و سختی‌های] خشکی و دریا نجات می‌دهد؟ [در حالی که] او را به زاری و پنهانی می‌خوانید [و می‌گویید:] "اگر ما را از این [ورطۀ مرگ‌] نجات دهد، قطعاً از سپاسگزاران خواهیم بود"».');
INSERT INTO "islamhouse" VALUES(6,64,'6:64','بگو: «الله شما را از این [تاریکی‌ها] و از تمام سختی‌ها نجات می‌دهد؛ سپس شما [باز هم] شرک می‌ورزید».');
INSERT INTO "islamhouse" VALUES(6,65,'6:65','بگو: «او تواناست كه بر شما عذابى [همچون صاعقه و طوفان] از بالاى سرتان یا [همچون خشکسالی و زلزله] از زیر پاهایتان بفرستد یا شما را به صورت گروه‌هایی پراکنده [و جدا از هم] درآورَد و عذاب برخی از شما را به برخی [دیگر] بچشاند». بنگر چگونه آیات [خود] را گوناگون بیان مى‌كنیم؛ باشد كه دریابند.');
INSERT INTO "islamhouse" VALUES(6,66,'6:66','[ای پیامبر،] قوم تو آن [= قرآن‌] را دروغ شمردند، در حالى كه آن [کتاب،] حق است. [به کافرانِ قومت] بگو: «من [مراقب و] کارگزارِ شما نیستم.');
INSERT INTO "islamhouse" VALUES(6,67,'6:67','هر خبرى سررسیدی [برای وقوع] دارد؛ و [به زودی] خواهید دانست [که سرانجامتان چگونه است]».');
INSERT INTO "islamhouse" VALUES(6,68,'6:68','و هر گاه کسانی را دیدی که در آیات ما [با دشمنی و تمسخر] گفتگو می‌کنند، از آنان روی بگردان تا به سخن دیگری بپردازند؛ و اگر شیطان تو را به فراموشی انداخت، پس از آنکه به یاد آوردی، دیگر با قوم ستمکار [مشرک‌] منشین‌.');
INSERT INTO "islamhouse" VALUES(6,69,'6:69','و [هر چند] چیزی از حساب [و گناه] آنان بر [عهدۀ] پرهیزگاران نیست، ولی یادآورى [و ارشاد، لازم‌] است؛ باشد که پروا کنند.');
INSERT INTO "islamhouse" VALUES(6,70,'6:70','و كسانى را كه دینشان را به بازى و سرگرمى گرفته‌اند و زندگى دنیا آنان را فریفته است رها كن؛ و [مردم را] با این [قرآن‌] اندرز ده تا مبادا كسى به [كیفر] آنچه به دست آورده است به هلاكت بیفتد در حالى كه در برابر الله برای [رهایی از عذابِ] آن نه [دوست و] کارسازی دارد و نه شفاعتگرى؛ و [حتی] اگر [بخواهد براى نجات خویش] هر فدیه‌اى دهد، از او پذیرفته نمی‌شود. اینان هستند كه به [کیفر‌] آنچه به دست آورده‌اند، هلاک گشته‌اند و به [سزای] آنكه كفر مى‌ورزیدند، شرابى از آبِ جوشان و عذابى پردرد [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(6,71,'6:71','[ای پیامبر،] بگو: «آیا به جای الله چیزی را بخوانیم که نه به ما سود می‌دهد و نه زیان می‌رساند؟ و آیا پس از آنکه الله ما را هدایت کرده است [به سوی گمراهی] به عقب بازگردیم؟ همچون کسی ‌که شیاطین او را [فریب داده و از راه راست] به در بُرده‌اند و او در زمین حیران و سرگشته مانده است، در حالی‌ که او یارانی [دلسوز] دارد که به هدایت دعوتش می‌کنند [و می‌گویند:] "به سوی ما بیا"». بگو: «بی‌تردید، هدایت [راستین]، هدایت الله است و ما فرمان یافته‌ایم که تسلیم پروردگار جهانیان باشیم؛');
INSERT INTO "islamhouse" VALUES(6,72,'6:72','و اینكه نماز برپا دارید و از او پروا کنید؛ و اوست كه به پیشگاهش محشور خواهید شد».');
INSERT INTO "islamhouse" VALUES(6,73,'6:73','و اوست که آسمان‌ها و زمین را به‌حق آفرید و روزی که [الله به هر چیزی] بگوید: «موجود شو»، بی‌درنگ موجود می‌شود. سخن او حق است؛ و روزی که در صور دمیده می‌شود، فرمانروایی از آنِ اوست. او دانای پنهان و پیداست و اوست که حکیمِ آگاه است.‌');
INSERT INTO "islamhouse" VALUES(6,74,'6:74','و [یاد کن از] هنگامی که ابراهیم به پدرش ـ آزرـ گفت: «آیا بُت‌ها را معبود [خویش] می‌گیری؟ به راستی که من تو و قومت را در گمراهیِ آشکاری می‌بینم».');
INSERT INTO "islamhouse" VALUES(6,75,'6:75','و این گونه فرماندهیِ [شکوهمندِ] آسمان‌ها و زمین را به ابراهیم نشان دادیم تا [یگانگی الله و گمراهیِ آزر و قومش را دریابد و] از اهل یقین باشد.');
INSERT INTO "islamhouse" VALUES(6,76,'6:76','پس هنگامی‌ که [تاریکی] شب او را در بر گرفت، ستاره‌ای دید [و] گفت: «این پروردگارِ من است» و چون غروب کرد [و ناپدید شد] گفت: «غروب‌کنندگان را دوست ندارم».');
INSERT INTO "islamhouse" VALUES(6,77,'6:77','پس هنگامی‌ که ماه را تابنده دید گفت: «این پروردگارِ من است» و چون غروب کرد گفت: «اگر پروردگارم مرا هدایت نكرده بود، قطعاً از گمراهان بودم».');
INSERT INTO "islamhouse" VALUES(6,78,'6:78','پس هنگامی ‌که خورشید را درخشنده دید گفت: «این پروردگارِ من است. این بزرگ‌تر است»؛ اما وقتی که غروب کرد گفت: «ای قوم، من از آنچه [برای الله] شریک قرار می‌دهید بیزارم».');
INSERT INTO "islamhouse" VALUES(6,79,'6:79','به راستی که من حقگرایانه دین [و عبادتِ] خویش را برای کسی خالص کرده‌ام که آسمان‌ها و زمین را پدید آورده است و هرگز در زمرۀ مشرکان نیستم».');
INSERT INTO "islamhouse" VALUES(6,80,'6:80','و قوم او با وی به مجادله پرداختند [و او را از خشم معبودها ترساندند. ابراهیم] گفت: «آیا دربارۀ الله با من مجادله می‌کنید در حالی که او مرا [به راهِ راست] هدایت کرده است و از چیزهایی که با او شریک می‌پندارید هیچ نمی‌ترسم، مگر آنکه پروردگارم چیزی [از سود و زیان برایم] خواسته باشد. علمِ پروردگارم همه چیز را فرا گرفته است. آیا پند نمی‌گیرید؟');
INSERT INTO "islamhouse" VALUES(6,81,'6:81','و چگونه از چیزهایی که شریک [الله] می‌پندارید بترسم با آنكه شما خود نمى‌هراسید از اینكه چیزى را شریک الله ساخته‌اید كه او تعالی [هیچ] دلیلى دربارۀ [حقانیتِ] آن بر شما نازل نكرده است؟ پس اگر مى‌دانید، [بگویید] كدام یک از این دو گروه [موحد و مشرک] به امنیت [و دوری از عذاب] سزاوارتر است؟');
INSERT INTO "islamhouse" VALUES(6,82,'6:82','کسانی که ایمان آورده‌اند و ایمانشان را به [هیچ] شرکی نیالوده‌اند، آنانند که امنیت [و آرامش‌] دارند و رهیافتگانند».');
INSERT INTO "islamhouse" VALUES(6,83,'6:83','و این [استدلال،] حجت ما بود كه به ابراهیم در برابر قومش دادیم. [در دنیا و آخرت، مقام] هر کس را كه بخواهیم به درجاتی بالا مى‌بریم. [ای پیامبر،] بی‌تردید، پروردگارت حكیم داناست.');
INSERT INTO "islamhouse" VALUES(6,84,'6:84','و به او [= ابراهیم] اسحاق و یعقوب را بخشیدیم و همه را [به راه راست] هدایت کردیم؛ و پیشتر نوح را [نیز] هدایت کرده بودیم و از نسل او داود و سلیمان و ایوب و یوسف و موسى و هارون را [به راه حق رهنمون ساختیم‌] و نیكوكاران را این گونه پاداش مى‌دهیم.');
INSERT INTO "islamhouse" VALUES(6,85,'6:85','و [همچنین] زکریا و یحیی و عیسی و الیاس [را راه نمودیم که] همه از شایستگان بودند.');
INSERT INTO "islamhouse" VALUES(6,86,'6:86','و اسماعیل و یَسَع و یونس و لوط [را نیز هدایت کردیم] و جملگى را بر جهانیان برترى دادیم.');
INSERT INTO "islamhouse" VALUES(6,87,'6:87','و [نیز] از پدران‌ و فرزندان‌ و برادرانشان [برخی را برتری دادیم و] آنان را برگزیدیم و به راه راست هدایت کردیم.');
INSERT INTO "islamhouse" VALUES(6,88,'6:88','این هدایتِ الله است که هر یک از بندگانش را که بخواهد، با آن هدایت می‌کند؛ و اگر شرک می‌ورزیدند، آنچه [از اعمال نیک] انجام داده بودند تباه می‌شد.');
INSERT INTO "islamhouse" VALUES(6,89,'6:89','اینان کسانی هستند که به ایشان کتاب و حکمت و نبوت بخشیدیم. پس [ای پیامبر،] اگر اینان [= مشركان‌] به این [پیامبران و کتاب‌های الهی] كفر بورزند، قطعاً گروهى [= مهاجران و انصار و تابعین] را بر [حفظ و نگهبانیِ] آن [اصول و عقاید] می‌گماریم كه نسبت به آن كافر نباشند.');
INSERT INTO "islamhouse" VALUES(6,90,'6:90','آنان [= پیامبران] کسانی هستند که الله هدایتشان کرده است؛ پس [ای پیامبر،] از هدایت [و راه راستِ] ایشان پیروی کن [و به قومت] بگو: «من در برابر این [رسالت،] پاداشی از شما نمی‌خواهم. این [قرآن، چیزی] نیست مگر [یادآوری و] پندی برای جهانیان».');
INSERT INTO "islamhouse" VALUES(6,91,'6:91','و [مشرکان، بزرگی و مقام] الله را چنان که سزاوار اوست نشناختند؛ چرا که گفتند: «الله بر [هیچ] بشری چیزی نازل نکرده است». [ای پیامبر،] بگو: «آن کتابی را که موسی آورد چه کسی نازل کرده است؟ [همان كتابى كه‌] براى مردم روشنایى و رهنمودی بود [و شما یهودیان] آن را به صورت [پراکنده بر] طومارها درمى‌آورید [و هر چه را که می‌خواهید] از آن آشكار می‌سازید و بسیارى را پنهان مى‌كنید؛ و [اکنون ای قوم عرب،] چیزهایی [از آیات و اخبار قرآن] به شما آموخته شد كه نه خود مى‌دانستید و نه پدرانتان». [ای پیامبر،] بگو: «الله [آن را نازل کرده است]»؛ آنگاه [در جهل و گمراهی] رهایشان کن تا در ژرفاى [عقاید باطل‌] خود سرگرم بازى باشند.');
INSERT INTO "islamhouse" VALUES(6,92,'6:92','و این [قرآن] کتاب مبارکی است که آن را نازل کردیم [تا] تصدیق‌کنندۀ چیزی باشد که پیش از آن نازل شده است و [همچنین] به این منظور است كه به [ساکنانِ] اُمّ القُری [= مكه] و [مردم] پیرامونش هشدار دهى؛ و كسانى كه به آخرت ایمان مى‌آورند، به این [کتاب] نیز ایمان مى‌آورند و بر نماز خویش مواظبت می‌كنند.');
INSERT INTO "islamhouse" VALUES(6,93,'6:93','و کیست ستمکارتر از آن کس که‌ دروغی به الله ببندد یا بگوید: «به من [نیز] وحی شده است»؛ در حالی که هیچ وحیی به او نرسیده است و [یا] کسی ‌که بگوید: «من [هم] مانند آنچه را که الله نازل کرده است [برایتان] نازل می‌کنم»؟ و [ای پیامبر، صحنۀ هولناکی است] اگر هنگامی را ببینی ‌که [این] ستمکاران، در [ترس و] سختی‌های مرگ فرورفته‌اند و فرشتگان [قبضِ روح،‌] دست‌هایشان را [به سوی آنان] گشوده‌اند [و فریاد مى‌زنند:] «جان‌هایتان را [از بدن] بیرون دهید. امروز به [سزاى‌] آنچه به ناحق بر الله دروغ مى‌بستید و در برابر آیاتش تكبر مى‌كردید، به عذابی خفت‌بار مجازات می‌شوید».');
INSERT INTO "islamhouse" VALUES(6,94,'6:94','و [روز قیامت الله می‌فرماید:] «همان گونه كه شما را نخستین بار [بدون مال و مقام] آفریدیم، [اكنون نیز] قطعاً تنها به سوى ما آمده‌اید و آنچه [از متاع دنیا] را که به شما ارزانی كرده بودیم، پشت سر خود [در دنیا] واگذاشتید و [معبودان و] شفاعتگرانی را كه در [امور] خودتان شریكان [الله] مى‌پنداشتید، همراه شما نمى‌بینیم. به راستی پیوند میان شما گسسته است و آنچه [شریک ما و شفیع خود] مى‌پنداشتید، گم گشته است».');
INSERT INTO "islamhouse" VALUES(6,95,'6:95','بی‌تردید، الله شکافندۀ دانه[ی گیاهان] و هسته[ی خرما در دل زمین] است؛ [موجود] زنده را از [مادۀ] بی‌جان بیرون مى‌آورد [= رویشِ گیاه از دانه] و [مادۀ به ظاهر] بی‌جان را از [موجود] زنده خارج مى‌سازد [= بیرون آمدنِ تخم یا نطفه از حیوانات]. این الله است [که چنین قدرت مطلقی دارد]؛ پس چگونه [از راه بندگى او] منحرف مى‌شوید؟');
INSERT INTO "islamhouse" VALUES(6,96,'6:96','[او] شکافندۀ سپیده‌دم است و شب را [مایۀ] آرامش قرار داد و خورشید و ماه را [نیز] با حساب [و نظمی دقیق پدید آورد]. تقدیرِ [پروردگار] شکست‌ناپذیرِ دانا چنین است‌.');
INSERT INTO "islamhouse" VALUES(6,97,'6:97','و [ای فرزندان آدم،] اوست که ستارگان را برایتان قرار داد تا در تاریکی‌های خشکی و دریا به [کمک] آنها راه یابید. به راستی که ما نشانه‌های [توان و تدبیر خویش] را برای مردمی که می‌دانند به تفصیل بیان كرده‌ایم.');
INSERT INTO "islamhouse" VALUES(6,98,'6:98','و اوست که شما را از یک تن آفرید؛ پس برای شما [در رحِمِ مادر] قرارگاهی است و [در پشتِ پدر] امانتگاهی. ما آیات [خود] را برای مردمی که [کلام الهی را] می‌فهمند به تفصیل بیان كرده‌ایم.');
INSERT INTO "islamhouse" VALUES(6,99,'6:99','و اوست که از آسمان، آبی فرو ریخت و از آن [باران] هر گونه روییدنی برآوردیم و از آن [نیز هر کِشت و گیاه] سبزی را رویاندیم [که] از آن [گیاهان نیز برای تغذیۀ شما] دانه‌هاى متراكمى [همچون گندم و جو] پدید می‌آوریم؛ و [همچنین] از شکوفۀ نخل، خوشه‌هایی نزدیک به یکدیگر [رویاندیم] و باغ‌هایی از انگور و زیتون و انار [آفریدیم؛ میوه‌هایی که] برخی همانند و برخی ناهمانند هستند. وقتی به بار نشست، به میوه‌هاى آن و [نحوۀ] رسیدنش بنگرید. در این [آفرینش] برای مردمی که ایمان دارند، نشانه‌هایی [روشن از قدرت الهی] است‌.');
INSERT INTO "islamhouse" VALUES(6,100,'6:100','و [مشرکان‌] شریکانی از جن برای الله قایل شده‌اند؛ حال آنکه [الله خود] آنها را آفریده است؛ و [همچنین] از روی نادانی برایش [فرزندان‌] پسر و دختر می‌سازند [و به او تعالی نسبت می‌دهند]. او از آنچه توصیف مى‌كنند پاک و برتر است.');
INSERT INTO "islamhouse" VALUES(6,101,'6:101','[او] پدید آورندۀ آسمان‌ها و زمین است؛ چگونه ممکن است فرزندی داشته باشد در حالی که همسری ندارد؟ و همه چیز را آفریده و او به همه چیز داناست.');
INSERT INTO "islamhouse" VALUES(6,102,'6:102','این است الله، پروردگار شما؛ هیچ معبودی [به‌حق] جز او نیست؛ آفرینندۀ همه چیز است؛ پس او را عبادت کنید و [بدانید که] او [مراقب و] کارگزار همه چیز است.');
INSERT INTO "islamhouse" VALUES(6,103,'6:103','چشم‌ها او را درنمی‌یابند و[لی] او چشم‌ها را درمی‌یابد و اوست که باریک‌بین و آگاه است.');
INSERT INTO "islamhouse" VALUES(6,104,'6:104','[ای پیامبر، به مشرکان بگو:] «به راستى دلایل آشکاری از جانب پروردگارتان براى شما آمده است؛ پس هر کس [با بصیرت و منطق دربارۀ آن] بیندیشد به سود اوست و هر کس چشم [بر حقیقت] ببندد، به زیانِ اوست؛ و من نگهبان شما نیستم».');
INSERT INTO "islamhouse" VALUES(6,105,'6:105','و اینچنین نشانه‌ها [و دلایل قدرت الهی] را گوناگون بیان می‌کنیم تا مبادا [مشرکان] بگویند: «[آنها را از اهل کتاب] آموخته‌ای»؛ و ما آنها را برای گروهی که می‌دانند [و می‌فهمند] به روشنی بیان می‌کنیم.');
INSERT INTO "islamhouse" VALUES(6,106,'6:106','[ای پیامبر،] از آنچه که از جانب پروردگارت بر تو وحی شده است پیروی کن ـ هیچ معبودی [به‌حق] جز او نیست‌ـ و از مشرکان روی بگردان.');
INSERT INTO "islamhouse" VALUES(6,107,'6:107','و اگر الله می‌خواست، [آنان هرگز] شرک نمی‌ورزیدند؛ و ما تو را بر آنان نگهبان قرار نداده‌ایم و تو [هرگز مراقب و] کارگزارشان نیستی.');
INSERT INTO "islamhouse" VALUES(6,108,'6:108','و [ای مؤمنان، هرگز] به آنها [=معبودهای باطل] که [مشرکان] به جای الله [به نیایش] مى‌خوانند دشنام ندهید [چرا كه] آنان نیز از روى دشمنى [و] نادانى به الله دشنام می‌دهند. این گونه براى هر امتى كردارشان را آراستیم؛ سپس بازگشت آنان [در قیامت] به سوى پروردگارشان است و آنان را از آنچه انجام مى‌دادند آگاه می‌سازد.');
INSERT INTO "islamhouse" VALUES(6,109,'6:109','و [مشرکان] با سخت‌ترین سوگندهایشان به الله سوگند خوردند كه اگر معجزه‌اى [که خواسته‌اند] برایشان بیاید، حتماً ایمان می‌آورند. [ای پیامبر،] بگو: «معجزات فقط در اختیار الله است» و شما [ای مؤمنان،] چه مى‌دانید كه اگر [معجزه‌ای هم‌] بیاید، ایمان نمى‌آورند.');
INSERT INTO "islamhouse" VALUES(6,110,'6:110','و ما دل‌ها و دیدگانشان را [از درک حقایق] برمى‌گردانیم، [در نتيجه، به معجزات ما ايمان نمى‌آورند] چنانكه نخستین بار به آن ایمان نیاوردند. و آنان را در [نافرمانی و] طغیانشان سرگشته رها می‌کنیم.');
INSERT INTO "islamhouse" VALUES(6,111,'6:111','و اگر ما فرشتگان را به سوى آنان مى‌فرستادیم و [حتی] اگر مردگان با آنان به سخن مى‌آمدند و هر چیزى را دسته‌دسته در برابرشان گرد مى‌آوردیم [تا راستگویی‌ پیامبر را گواهی دهند]، باز هم ایمان نمى‌آوردند ـ مگر اینكه الله بخواهد ـ ولى بیشتر آنان نادانی می‌کنند.');
INSERT INTO "islamhouse" VALUES(6,112,'6:112','و [ای پیامبر محمد، تو را با دشمنیِ مشرکان آزمودیم و] همچنین برای هر پیامبری، دشمنی از شیاطین انس و جن قرار دادیم که برخی از آنان در نهان، به برخی دیگر سخنان فریبنده [و بی‌اساس] القا می‌کنند و اگر پروردگارت می‌خواست، [هرگز] چنین نمی‌کردند؛ پس آنان را با دروغ‌هایی که می‌بافند واگذار.');
INSERT INTO "islamhouse" VALUES(6,113,'6:113','و [چنین کردیم تا آنان را بیازماییم، خواستیم] تا دل‌های كسانى كه به آخرت ایمان نمى‌آورند، به آن [سخن فریبنده‌] بگراید و آن را بپسندد و هر گناهی که می‌خواهند مرتکب شوند.');
INSERT INTO "islamhouse" VALUES(6,114,'6:114','[ای پیامبر به مشرکان بگو:] «آیا [سزاست که برای قضاوت دربارۀ اختلاف من و شما] داورى جز الله بجویم؟ و حال آنکه اوست ذاتی كه این كتاب را به تفصیل به سوى شما نازل كرده است»؛ و کسانی كه كتاب [آسمانى‌] به ایشان داده‌ایم مى‌دانند كه آن [قرآن] از جانب پروردگارت به‌حق نازل شده است؛ پس هرگز از تردیدكنندگان نباش.');
INSERT INTO "islamhouse" VALUES(6,115,'6:115','و سخن پروردگارت [= قرآن] با راستى [در گفتار] و عدل [در احکام] به حد تمام و کمال رسید. هیچ تغییردهنده‌اى براى سخنانش نیست؛ و او شنواى داناست.');
INSERT INTO "islamhouse" VALUES(6,116,'6:116','و اگر از بیشتر مردم زمین اطاعت كنى، تو را از راه الله گمراه مى‌كنند. آنان جز گمان [بی‌اساسِ خود از چیزی] پیروى نمى‌كنند و جز دروغ نمی‌بافند.');
INSERT INTO "islamhouse" VALUES(6,117,'6:117','بی‌تردید، پروردگارت به [حال‌] كسى كه از راه او منحرف مى‌شود داناتر است؛ و او به [حال‌] راه‌یافتگان [نیز] داناتر است.');
INSERT INTO "islamhouse" VALUES(6,118,'6:118','پس [ای مردم،] اگر به آیاتش ایمان دارید، [فقط] از [گوشتِ] آنچه نام الله [هنگام ذبح‌] بر آن برده شده است بخورید.');
INSERT INTO "islamhouse" VALUES(6,119,'6:119','و [ای مسلمانان،] شما را چه شده است كه از آنچه [از گوشت‌هایی که] نام الله بر آن برده شده است نمى‌خورید؟ حال آنکه [الله] آنچه را بر شما حرام کرده است به تفصیل برایتان بیان داشته است [پس از آنها دوری کنید؛] مگر چیزی که به [خوردن‌] آن ناچار شوید؛ و بی‌تردید، بسیارى [از مردم، دیگران را] از روى نادانى با هوس‌های خویش گمراه مى‌كنند. [ای پیامبر،] یقیناً پروردگارت به [حال‌] تجاوزكاران [از حدود الهی] داناتر است.');
INSERT INTO "islamhouse" VALUES(6,120,'6:120','و [ای مردم،] گناه آشكار و پنهان را رها كنید؛ [چرا که] بی‌تردید، كسانى كه مرتكب گناه مى‌شوند، به زودى در ازای آنچه می‌کردند مجازات خواهند شد.');
INSERT INTO "islamhouse" VALUES(6,121,'6:121','و [ای مؤمنان،] از آنچه [هنگام ذبح] نام الله بر آن برده نشده است نخورید؛ چرا كه آن [کار] قطعاً نافرمانى است؛ و در حقیقت، شیاطین به دوستان خود القا مى‌كنند که با شما [در این مورد] مجادله نمایند؛ و اگر از آنان اطاعت كنید، قطعاً شما [نیز در زمرۀ] مشرکان هستید.');
INSERT INTO "islamhouse" VALUES(6,122,'6:122','آیا كسى كه [دلش بر اثر جهل و شرک] مرده‌ بود و ما [با توحید و ایمان] زنده‌اش گرداندیم و نورى برایش پدید آوردیم تا در پرتوی [هدایتِ] آن، میان مردم گام بردارد، همچون كسى است كه در تاریکی‌ها [گرفتار] است و از آن بیرون‌آمدنى نیست؟ آنچه كافران می‌کردند، این گونه برایشان آراسته شده است.');
INSERT INTO "islamhouse" VALUES(6,123,'6:123','و این گونه در هر شهرى بزرگان گناهکارش را مى‌گماریم تا در آن به نیرنگ بپردازند [و مردم را از راه الله بازدارند]؛ و[لى‌] آنان جز به خود نیرنگ نمى‌زنند و [این حقیقت را] درک نمى‌كنند.');
INSERT INTO "islamhouse" VALUES(6,124,'6:124','و [مشرکان] چون آیه‌ای [از قرآن] برایشان می‌آید می‌گویند: «هرگز به آن ایمان نمی‌آوریم، مگر آنکه همانند آنچه به پیامبران الهی داده شده است، به ما نیز داده شود». الله بهتر می‌داند رسالت خویش را کجا [و بر عهدۀ چه کسی‌] قرار دهد. به زودی بر کسانی که مرتکب [نافرمانی و] گناه شدند، به سزای نیرنگی که ورزیده بودند، از جانب الله، خواری و عذابی سخت خواهد رسید.');
INSERT INTO "islamhouse" VALUES(6,125,'6:125','پس هر کس که الله بخواهد او را هدایت کند، دلش را برای [پذیرش] اسلام می‌گشاید؛ و هر کس را بخواهد در گمراهی واگذارد، دلش را بسیار تنگ [و بسته] می‌سازد [و پذیرش حق را چنان برایش محال می‌گردانَد که] گویی می‌خواهد از آسمان بالا رود [ولی نمی‌تواند]. الله اینچنین عذاب را بر کسانی که ایمان نمی‌آورند، می‌گمارد.');
INSERT INTO "islamhouse" VALUES(6,126,'6:126','و [ای پیامبر،] این [دین اسلام] راهِ راستِ پروردگار توست. به راستی که ما آیات [خود] را برای گروهی که پند می‌گیرند، به تفصیل بیان کردیم.');
INSERT INTO "islamhouse" VALUES(6,127,'6:127','برای آنان نزد پروردگارشان سرای سلامت و امنیت است و او به [پاداش‌] کارهایی که می‌کردند، [دوست و] کارسازشان است.‌');
INSERT INTO "islamhouse" VALUES(6,128,'6:128','و [یاد كن از قیامت؛‌] روزى كه [الله] همه آنان [= انس و جن] را گرد مى‌آورَد [و مى‌فرماید:] «اى گروه جنّیان، از آدمیان [بسیاری را گمراه کردید و پیروان‌] فراوان یافتید»؛ و دوستان [مشرکِ] آنان از [میان] انسان‌ها مى‌گویند: «پروردگارا، برخى از ما از برخى دیگر بهره‌بردارى كردند [و در کفر و گناه به یکدیگر یاری رساندند] و [اینک] ما به پایانى كه برایمان مقرر كرده بودى رسیده‌ایم». [الله] مى‌فرماید: «جایگاهتان آتش است [و] در آن جاودان خواهید بود؛ مگر آنچه را الله بخواهد [از آن بکاهد یا ببخشاید]. بی‌تردید، پروردگارت حكیم داناست».');
INSERT INTO "islamhouse" VALUES(6,129,'6:129','و اینچنین بعضی از ستمکاران را بر بعضی دیگر به [سزای] آنچه می‌کردند، مسلط می‌گردانیم.‌');
INSERT INTO "islamhouse" VALUES(6,130,'6:130','ای گروه جن و انس، آیا رسولانی از میان خود شما برایتان نیامدند كه آیات مرا بر شما بخوانند و از دیدار این روز به شما هشدار دهند؟ آنان می‌گویند: «[آری،] ما علیه خویش گواهى می‌دهیم [كه آمدند]»؛ و زندگى دنیا آنان [= کافران] را فریب داد و به زیان خود گواهى دادند كه كافر بوده‌اند.');
INSERT INTO "islamhouse" VALUES(6,131,'6:131','این [اتمام حجت‌] به خاطر آن است كه پروردگارت هیچ‌گاه شهرها را ـ در حالی که مردمش [از پیام الهی بی‌خبر و] غافلند ـ به ستم نابود نكرده است.');
INSERT INTO "islamhouse" VALUES(6,132,'6:132','و هر یک [از بندگان‌] در برابر آنچه انجام داده‌اند، [نزد الله] درجات [و مراتبی از ثواب و عذاب] دارند، و پروردگارت از آنچه انجام می‌دهند غافل نیست.');
INSERT INTO "islamhouse" VALUES(6,133,'6:133','و پروردگارت بى‌نیاز و دارای رحمت [بیکران] است. [ای بندگان گنهکار، الله] اگر بخواهد، شما را [با عذاب از میان] مى‌بَرد و پس از شما هر کس را بخواهد جانشین می‌سازد؛ همچنان كه شما را نیز از نسل گروهى دیگر آفرید.');
INSERT INTO "islamhouse" VALUES(6,134,'6:134','[ای کافران،] آنچه به شما وعده داده می‌شود، یقیناً خواهد آمد و شما نمی‌توانید [الله را] ناتوان سازید [و از کیفرش بگریزید].');
INSERT INTO "islamhouse" VALUES(6,135,'6:135','بگو: «ای قوم من، به شیوۀ خود عمل کنید [و بر کفر و گمراهی خود بمانید که] بی‌تردید، من [نیز به ایمان خویش] عمل می‌کنم؛ پس به زودی خواهید دانست که [پیروزی دنیا و] فرجامِ [نیکوی] آن سرا از آنِ کیست. بی‌گمان، ستمکاران [مشرک] رستگار نخواهند شد».');
INSERT INTO "islamhouse" VALUES(6,136,'6:136','و [مشركان] براى الله از آنچه آفریده است ‌ـ از كِشت و چهارپایان ـ سهمى تعیین کردند و به گمان خود گفتند: «این مخصوصِ الله است و این مخصوصِ شریکان ما [= معبودان]». آنچه برای شریکانشان بود به الله نمى‌رسد؛ و[لى‌] آنچه برای الله بود، به شریکان آنها مى‌رسد. چه بد داورى مى‌كنند!');
INSERT INTO "islamhouse" VALUES(6,137,'6:137','و این گونه بود که شریکان آنها [اعم از شیاطین و...،] در نظر بسیاری از مشرکان [که فقیر بودند] قتل فرزندانشان را آراستند [و زیبا جلوه دادند] تا هلاكشان كنند و آیینشان را بر آنان [آشفته و] مشتبه سازند؛ و اگر الله می‌خواست، چنین نمی‌کردند؛ پس [ای پیامبر، تو نیز] آنان را با [تهمت و] دروغ‌هایشان واگذار.');
INSERT INTO "islamhouse" VALUES(6,138,'6:138','و به پندار خویش گفتند: «[استفاده از] این چهارپایان و كشتزارها[یی که وقف معبود‌ها شده] ممنوع است و هیچ‌کس از آن نمی‌خورد مگر آن [گروه از خادمان معبود‌ها] كه ما بخواهیم؛ و چهارپایانی [نیز] هستند كه [سوارشدن بر] پشتشان حرام شده است»؛ و دام‌هایی [داشتند] كه [هنگام ذبح‌] نام الله را بر آنها نمى‌بردند. [اینها همه باورهایی بود که] به دروغ به الله نسبت می‌دادند [و الله] به زودی جزای دروغ‌هایی را که می‌بافتند به آنان خواهد داد.');
INSERT INTO "islamhouse" VALUES(6,139,'6:139','و گفتند: «آنچه در شكمِ این چهارپایان است [اگر زنده متولد شود،] به مردان ما اختصاص دارد و بر همسرانمان حرام شده است؛ و[لی] اگر [جنین‌] مرده باشد، همۀ آنان [از زن و مرد] در آن شریكند». به زودى [الله] آنان را به سزای این توصیفشان کیفر خواهد داد. به راستی که او حكیم داناست.');
INSERT INTO "islamhouse" VALUES(6,140,'6:140','كسانى كه از روى [جهل و] بى‌خردى، فرزندان خود را [از ترس فقر] كشتند و با دروغ بستن به الله، آنچه را که الله روزی‌شان كرده بود [بر خود] حرام دانستند، سخت زیان كردند. بی‌تردید، آنان گمراه شدند و هدایت‌یافته نبودند.');
INSERT INTO "islamhouse" VALUES(6,141,'6:141','اوست که باغ‌هایی [با بوته‌های‌] داربست‌دار [مانند انگور] و [درختان] بی‌داربست [مانند خرما] پدید آورد و [نیز] درخت خرما و زراعتی که میوه‌هایش گوناگون است و درخت زیتون و انار که [برگ‌هایشان] همانند و [میوه‌هایشان] متفاوت است. [ای مردم،] از میوۀ آن - چون به بار نشست‌ـ بخورید و روزِ چیدنش، حقّ [زکاتِ بینوایان از] آن را بپردازید و[لی] اسراف نکنید [چرا که] بی‌تردید، او تعالی اسرافکاران را دوست ندارد.');
INSERT INTO "islamhouse" VALUES(6,142,'6:142','و [نیز] از چهارپایان، [حیوانات] باربر و [حیوانات] غیرباری [برای منافع دیگر، آفرید]. از آنچه الله به شما روزی داده است بخورید و از گام‌های شیطان پیروی نکنید. بی‌گمان، او دشمن آشکاری برای شماست.');
INSERT INTO "islamhouse" VALUES(6,143,'6:143','هشت [رأس] نر و ماده [برای استفادۀ ‌شما آفرید:] از گوسفند یک جفت و از بز یک جفت. [ای پیامبر، به مشرکان] بگو: «آیا [الله] آن دو نر را حرام کرده است یا ماده‌ها را؟ یا آنچه که رحِم‌های [این] دو ماده در بر دارد؟ اگر راست می‌گویید [و بر تحریم آنها دلیلی دارید]، از روی علم [و منطق] به من خبر دهید.');
INSERT INTO "islamhouse" VALUES(6,144,'6:144','و از شتر یک جفت و از گاو یک جفت [آفرید]. بگو: «آیا [الله] آن دو نر را حرام کرده است یا دو ماده را؟ یا آنچه که رحِم‌های [این] دو ماده در بر دارد؟ آیا وقتى الله شما را به این [تحریم‌] سفارش می‌كرد [آنجا] حاضر بودید؟ پس كیست ستمكارتر از آن کس كه بر الله دروغ می‌بندد تا بدون هیچ [استناد و] علمی مردم را گمراه كند؟ به راستی که الله گروه ستمكاران را هدایت نمى‌كند».');
INSERT INTO "islamhouse" VALUES(6,145,'6:145','بگو: «در آنچه [از احکام الهی که] به من وحى شده است، [غذای] حرامی را که خوردنش برای خورنده‌ای حرام باشد نمی‌یابم؛ مگر [گوشتِ] مردار یا خونِ ریخته یا گوشت خوک ـ که قطعاً پلید [و حرام] است ـ یا [قربانی‌اى كه‌ هنگام ذبح] از روى نافرمانى، نام غیرِ الله را بر آن بُرده باشند». اما هر کس [برای حفظ جان خود، به خوردن آنها] ناچار شود ـ بدون آنکه سركش و زیاده‌خواه باشد، [بر او گناهی نیست]. به راستی که پروردگارت آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(6,146,'6:146','و هر [حیوان‌] چنگال‌دارى را بر یهود حرام كردیم و از گاو و گوسفند، چربی‌های آن دو را بر آنان حرام كردیم؛ مگر چربی‌هایی كه بر پشت آنها یا دورِ روده‌هاست یا چربی‌هایی که با استخوان درآمیخته است. این گونه آنان را به سزاى سرکشی‌شان مجازات کردیم و بی‌تردید، ما راستگوییم.');
INSERT INTO "islamhouse" VALUES(6,147,'6:147','پس [ای پیامبر،] اگر تو را تكذیب كردند، بگو: «پروردگارتان رحمتى گسترده دارد؛ و[لی] عذابش از گروه گناهکاران بازگردانده نخواهد شد».');
INSERT INTO "islamhouse" VALUES(6,148,'6:148','كسانى كه شرک ورزیدند خواهند گفت: «اگر الله مى‌خواست، نه ما شرک می‌آوردیم و نه پدرانمان و [همچنین] چیزى را [خودسرانه‌] تحریم نمى‌كردیم». كسانى كه پیش از آنان بودند نیز همین گونه [پیامبران خود را] تكذیب كردند تا عذاب ما را چشیدند. [ای پیامبر، به این مشرکان] بگو: «آیا [دانش و] دلیلی نزدتان هست كه آن را براى ما آشکار كنید؟ [در حقیقت،] شما جز پندار [از چیزی] پیروی نمی‌کنید و جز دروغ نمى‌گویید».');
INSERT INTO "islamhouse" VALUES(6,149,'6:149','بگو: «دلیل رسا و روشن از آنِ الله است و اگر [او تعالی] می‌خواست، همۀ شما را هدایت می‌کرد».');
INSERT INTO "islamhouse" VALUES(6,150,'6:150','بگو: «گواهانتان را كه گواهى مى‌دهند الله اینها را حرام كرده است بیاورید». پس [حتی] اگر گواهی دادند، تو با آنان [به چنین دروغی] گواهی نده و از هوس‌های كسانى كه آیات ما را دروغ شمردند و كسانى كه به آخرت ایمان نمى‌آورند و [معبودان باطل را] با پروردگارشان همتا قرار مى‌دهند، پیروى نكن.');
INSERT INTO "islamhouse" VALUES(6,151,'6:151','بگو: «بیایید آنچه را که پروردگارتان بر شما حرام كرده است برایتان بخوانم: اینکه چیزى را با او شریک قرار ندهید و به پدر و مادر نیکی كنید و فرزندان خود را از [ترس] تنگدستى نكشید،‌ ‌ما به شما و آنان روزى مى‌دهیم و به كارهاى زشت ـ چه آشکار و چه پنهان‌ـ نزدیک نشوید و کسی را كه الله [قتلش را] حرام کرده است جز به‌حق نكشید. این است [آنچه الله] شما را به [پیروی از] آن سفارش كرده است؛ باشد كه بیندیشید.');
INSERT INTO "islamhouse" VALUES(6,152,'6:152','و به مال یتیم نزدیک نشوید، مگر به بهترین شکل [که به سود و صلاحِ او باشد] تا به سن [بلوغ و] رشد خود برسد؛ و [سنجشِ] پیمانه و ترازو را به عدالت کامل کنید ـ ما هیچ کس را جز به ‌اندازۀ توانش مکلّف نمی‌کنیم‌ـ و هنگامی که سخن می‌گویید [یا گواهی می‌دهید] عدالت را رعایت کنید؛ هر چند [دربارۀ] خویشاوند[تان] باشد و به عهد و پیمان الله وفا کنید. این است [آنچه الله] شما را به [پیروی از] آن سفارش كرده است؛ باشد که پند گیرید.');
INSERT INTO "islamhouse" VALUES(6,153,'6:153','و [بدانید] که این راهِ راست من است؛ پس از آن پیروى كنید و از [دیگر] راه‌ها پیروى نكنید [چرا که] شما را از راه او جدا مى‌سازد. این است [آنچه الله] شما را به [پیروی از] آن سفارش كرده است؛ باشد که پروا کنید.');
INSERT INTO "islamhouse" VALUES(6,154,'6:154','سپس به موسى كتاب [تورات] دادیم تا بر كسى كه نیكى كرده است نعمت را تمام كنیم و به این هدف که [حکمِ] هر چیزی را به تفصیل بیان نماییم و رهنمود و رحمتى گردد؛ باشد كه به دیدار پروردگارشان ایمان بیاورند.');
INSERT INTO "islamhouse" VALUES(6,155,'6:155','و این [قرآن] که آن را نازل کردیم کتابی پُربرکت است؛ از آن پیروی کنید و پروا نمایید؛ باشد که مورد رحمت [الله] قرار گیرید.');
INSERT INTO "islamhouse" VALUES(6,156,'6:156','[آن را نازل کردیم] تا نگویید: «کتاب [آسمانی] تنها بر دو طایفۀ پیش از ما [= یهود و نصاری] نازل شده بود و ما از آموختن [کتاب‌های] آنان بی‌خبر بودیم».');
INSERT INTO "islamhouse" VALUES(6,157,'6:157','یا نگویید: «اگر کتاب [آسمانی] بر ما نازل می‌شد، از آنان راه‌یافته‌تر بودیم». پس بی‌تردید [اینک] از جانب پروردگارتان دلیل روشن و هدایت و رحمتی برایتان آمده است. پس کیست ستمکارتر از آن کس که آیات الله را دروغ انگارد و از آن روی بگرداند؟ به زودی کسانی را که از آیات ما روی می‌گردانند، به سزای آنکه [به کلام الهی] پشت می‌کردند، به عذابی سخت [و خفت‌بار] مجازات خواهیم کرد.');
INSERT INTO "islamhouse" VALUES(6,158,'6:158','آیا [کافران] انتظاری جز این دارند که فرشتگان [مرگ] نزد‌شان بیایند یا پروردگارت [خود برای داوری] بیاید یا برخی از نشانه‌های پروردگارت [در مورد قیامت] فرارسد؟ روزی ‌که برخی از نشانه‌های پروردگارت آشکار گردد، ایمان آوردن کسی که پیشتر ایمان نیاورده یا با ایمانش خیری حاصل نکرده است، سودی به حالش ندارد. [ای پیامبر، به مشرکان] بگو: «منتظر باشید. بی‌گمان، ما [نیز] منتظریم».');
INSERT INTO "islamhouse" VALUES(6,159,'6:159','[ای پیامبر،] كسانى كه دین خود را پراكنده ساختند و فرقه‌فرقه شدند، بی‌تردید، تو را با آنان کاری نیست؛ آنان كارشان فقط با الله است؛ آنگاه [الله در روز قیامت] آنان را از آنچه انجام می‌دادند، آگاه می‌سازد.');
INSERT INTO "islamhouse" VALUES(6,160,'6:160','[در آن روز،] هر [مؤمنی] كه كار نیكى بیاورد، ده برابر آن [پاداش‌] خواهد داشت و هر کس كار بدى بیاورد، جز مانند آن کیفر نخواهد دید؛ و به آنان ستم نخواهد شد.');
INSERT INTO "islamhouse" VALUES(6,161,'6:161','[ای پیامبر، به مشرکان] بگو: «بی‌تردید، پروردگارم مرا به راه راست هدایت کرده است: دین پایدار [و] آیینِ ابراهیم که حقگرا بود و هرگز در زمرۀ مشرکان نبود».');
INSERT INTO "islamhouse" VALUES(6,162,'6:162','بگو: «بی‌تردید، نمازم و قربانی‌های من و زندگی و مرگم همه برای الله ـ پروردگار جهانیان‌ـ است.');
INSERT INTO "islamhouse" VALUES(6,163,'6:163','[همان ذاتی كه‌] شریكى ندارد؛ و به این [عبادت الله به یگانگی و خالصانه] دستور یافته‌ام و من نخستین مسلمان [از این قوم] هستم».');
INSERT INTO "islamhouse" VALUES(6,164,'6:164','بگو: «آیا به جز الله پروردگاری را بجویم و حال آنکه او پروردگار همه چیز است؟ و هیچ کس [کار ناشایستی] انجام نمی‌دهد، مگر آنکه [بارِ گناهش] بر خود اوست؛ و هیچ گناهکاری بارِ [گناه] دیگری را به دوش نمی‌کشد؛ سپس بازگشت [همۀ] شما به سوی پروردگارتان است؛ سرانجام [همه] شما را [در روز قیامت، از حقیقت‌] آنچه در آن اختلاف داشتید، آگاه می‌سازد.');
INSERT INTO "islamhouse" VALUES(6,165,'6:165','و اوست که شما را جانشین [پیشینیان در] زمین قرار داد و درجات برخی از شما را [در آفرینش و ثروت] بر برخی دیگر برتری داد تا شما را [به وسیلۀ] آنچه به شما داده است بیازماید. بی‌تردید، پروردگارت زودكیفر است و یقیناً او بسیار آمرزنده [و] مهربان است.');
INSERT INTO "islamhouse" VALUES(7,1,'7:1','المص [= الف. لام. میم. صاد].');
INSERT INTO "islamhouse" VALUES(7,2,'7:2','[ای پیامبر، این قرآن،] کتابی است که بر تو نازل شده است؛ پس نباید در سینه‌ات نسبت به آن تردیدی باشد؛ [نازل شده] تا با آن [مردم را] بیم دهی و برای مؤمنان پند [و تذکری] باشد.');
INSERT INTO "islamhouse" VALUES(7,3,'7:3','[ای مردم،] از آنچه از جانب پروردگارتان بر شما نازل شده است پیروی کنید و از [دوستان و] کارسازانِ دیگر به جز او پیروی نکنید؛ چه اندک پند می‌پذیرید!');
INSERT INTO "islamhouse" VALUES(7,4,'7:4','چه بسیار [ساکنانِ روستاها و] شهر‌ها که نابودشان کردیم و عذاب ما شب‌هنگام [که در خواب بودند] یا در حالی که به خواب نیمروزی فرورفته بودند به سراغشان آمد.');
INSERT INTO "islamhouse" VALUES(7,5,'7:5','و چون عذاب ما به سراغشان آمد، سخن[و دعای]شان جز این نبود که گفتند: «به راستی که ما [کافر و] ستمکار بودیم [و پشیمانیم]».');
INSERT INTO "islamhouse" VALUES(7,6,'7:6','مسلّماً ما از کسانی ‌که [پیامبرانمان] به سویشان فرستاده شدند، [دربارۀ پذیرش پیام حق] سؤال خواهیم کرد؛ و قطعاً از پیامبران [نیز دربارۀ پاسخ امت‌هایشان به دعوت آنان] خواهیم پرسید.');
INSERT INTO "islamhouse" VALUES(7,7,'7:7','یقیناً [کردار بندگان را] با علم [خود] برایشان بیان می‌کنیم؛ و ما هرگز غایب [و غافل از احوال آنان‌] نبوده‌ایم‌.');
INSERT INTO "islamhouse" VALUES(7,8,'7:8','در آن روز، وزن [و معیار سنجشِ اعمال، عادلانه و] درست است؛ پس هر‌ کس كفه ميزان [اعمال‌] وی سنگین باشد، چنین کسانی رستگارند.');
INSERT INTO "islamhouse" VALUES(7,9,'7:9','و هر کس كفه ميزان [اعمال‌] وی سبک باشد، اینانند که به خود زیان رسانده‌اند؛ چرا که [با انکار قرآن،] به آیاتِ ما ستم می‌کردند.');
INSERT INTO "islamhouse" VALUES(7,10,'7:10','و [ای مردم،] به راستی که ما شما را در زمین جای دادیم و وسایل [و امکاناتِ] زندگی را در آن برایتان نهادیم. چه اندک شکر می‌گزارید!');
INSERT INTO "islamhouse" VALUES(7,11,'7:11','و بی‌تردید، ما [نسلِ] شما را [در زمین] پدید آوردیم، سپس شما را [به بهترین حالت] شکل دادیم، آنگاه به فرشتگان گفتیم: «برای [بزرگداشت و احترام، به] آدم سجده کنید»؛ پس [همگی] سجده کردند، مگر ابلیس که از سجده‌کنندگان نبود.');
INSERT INTO "islamhouse" VALUES(7,12,'7:12','[الله] فرمود: «وقتی که تو را [به سجده] فرمان دادم، چه چیز تو را بازداشت از اینکه سجده کنی؟» [ابلیس] گفت: «من از او بهترم: مرا از آتش آفریدی و[لی] او را از گِل پدید آوردی».');
INSERT INTO "islamhouse" VALUES(7,13,'7:13','[الله] فرمود: «از این [بهشت برین] فرود آی [زیرا] حق تو نیست که در آن [مکان، گردنکشی و] تکبر کنی؛ پس بیرون رو [که] بی‌تردید، تو از خوارشدگانی».');
INSERT INTO "islamhouse" VALUES(7,14,'7:14','[ابلیس] گفت: «تا روزی‌ که [انسان‌ها] برانگیخته ‌شوند، مرا مهلت بده».');
INSERT INTO "islamhouse" VALUES(7,15,'7:15','[الله] فرمود: «قطعاً تو از مهلت‌یافتگانی».');
INSERT INTO "islamhouse" VALUES(7,16,'7:16','[ابلیس] گفت: «پس به خاطر آنکه مرا در گمراهی [و هلاکت] افکندی، من نیز بر سر راه مستقیم تو برای [فریب] آنان می‌نشینم،');
INSERT INTO "islamhouse" VALUES(7,17,'7:17','سپس بر آنان ـ از روبرو و از پشت سر و از طرف راست و چپشان‌ـ درمى‌آیم و [چنان می‌کنم که] بیشتر آنان را شكرگزار [نعمت‌های خود] نیابی».');
INSERT INTO "islamhouse" VALUES(7,18,'7:18','[الله] فرمود: «از آنجا [= بهشت] نکوهیده و رانده‌شده بیرون رو. هر یک از آنان که از تو پیروی کند، [در سپاهِ توست و روز قیامت،] قطعاً دوزخ را از همۀ شما پر خواهم کرد.');
INSERT INTO "islamhouse" VALUES(7,19,'7:19','و ای آدم، تو و همسرت در بهشت ساکن شوید و از [خوردنی‌های آن از] هر جا که خواستید بخورید؛ و[لی] به این درخت نزدیک نشوید که از ستمکاران [نافرمان] خواهید بود».');
INSERT INTO "islamhouse" VALUES(7,20,'7:20','پس شیطان آن دو را وسوسه کرد تا آنچه از شرمگاهشان بر آنان پوشیده مانده بود، برایشان آشکار سازد [پس حیله‌ای اندیشید] و گفت: «پروردگارتان شما را از [خوردن میوۀ] این درخت منع نكرد مگر [براى‌] آنكه [مبادا تبدیل به] دو فرشته گردید یا از جاودانان شوید».');
INSERT INTO "islamhouse" VALUES(7,21,'7:21','و برای آن‌ دو سوگند یاد کرد که: «من قطعاً خیرخواهتان هستم»‌.');
INSERT INTO "islamhouse" VALUES(7,22,'7:22','پس آنها را با فریب [از مقام و منزلشان] فرود آورد و چون از [میوۀ] آن درخت [ممنوعه] چشیدند، شرمگاهشان بر آنان نمایان شد؛ و شروع کردند به قرار دادن برگ[های درختانِ] بهشت بر [شرمگاه] خود [تا آن را بپوشانند]؛ و پروردگارشان [اینچنین] به آنها ندا داد: «آیا شما را از [خوردنِ میوۀ] آن درخت منع نکردم و به شما نگفتم که بی‌تردید، شیطان دشمن آشکار شماست؟».');
INSERT INTO "islamhouse" VALUES(7,23,'7:23','[آن دو] گفتند: «پروردگارا، ما [با سرپیچی از دستور تو] به خویشتن ستم کردیم و اگر ما را نیامرزی و بر ما رحم نکنی، قطعاً از زیانکاران خواهیم بود».');
INSERT INTO "islamhouse" VALUES(7,24,'7:24','[الله] فرمود: «[از بهشت بر زمین] فرود آیید. [در حالی که زین پس] برخی از شما دشمن برخی دیگرید و در زمین تا مدتی [معیّن] قرارگاه و بهره‌مندی برایتان خواهد بود».');
INSERT INTO "islamhouse" VALUES(7,25,'7:25','[و] فرمود: «در آن زندگی می‌کنید و در همان[جا] می‌میرید و [روز قیامت نیز] از آن بیرون آورده می‌شوید».');
INSERT INTO "islamhouse" VALUES(7,26,'7:26','ای فرزندان آدم، همانا لباسی برایتان فرو فرستادیم که شرمگاهتان را می‌پوشانَد و [مایۀ] زینت شماست؛ و [برای مؤمن،] لباس پرهیزگاری بهتر است. این از نشانه‌های [قدرتِ] الله است؛ باشد که [انسان‌ها] پند گیرند.');
INSERT INTO "islamhouse" VALUES(7,27,'7:27','ای فرزندان آدم، [آگاه باشید که] شیطان شما را نفریبد چنان که پدر و مادرتان را از بهشت بیرون کرد و لباس آن دو را از تنشان [بیرون] کشید تا شرمگاهشان را به آنان نشان دهد. بی‌تردید، او و گروهش، از جایی که آنها را نمی‌بینید، شما را می‌بینند. به راستی که ما شیاطین‌ را دوستان کسانی قرار داده‌ایم که ایمان نمی‌آورند.');
INSERT INTO "islamhouse" VALUES(7,28,'7:28','و چون [مشرکان] کار‌‌ زشتی [از قبیل شرک یا طواف در حال برهنگی] انجام می‌دهند، می‌گویند: «ما نیاکان خود را بر این [کار] یافته‌ایم و الله ما را به [انجامِ] آن فرمان داده است». بگو: «الله هرگز به زشتکاری فرمان نمی‌دهد. آیا سخنی را که نمی‌دانید به الله نسبت می‌دهید؟».');
INSERT INTO "islamhouse" VALUES(7,29,'7:29','[ای پیامبر، به مشرکان] بگو: «پروردگارم به عدالت فرمان داده است و [اینکه همه جا، به ویژه] در هر مسجدی [خالصانه] به سوی [قبلۀ او] روی آورید و او را در حالی [به عبادت و دعا] بخوانید که دین [خود] را برایش خالص نموده‌اید. [و بدانید] همان گونه که شما را در آغاز پدید آورْد، [پس از مرگ نیز بار دیگر به سویش] بازمی‌گردید.');
INSERT INTO "islamhouse" VALUES(7,30,'7:30','[الله] گروهی را هدایت کرد و بر گروهی [دیگر] گمراهی مقرر گشته است؛ زیرا آنان به جای الله، شیاطین را [به عنوان] دوستان [خویش] برگزیدند و گمان می‌کنند که هدایت‌یافته‌اند».');
INSERT INTO "islamhouse" VALUES(7,31,'7:31','ای فرزندان آدم، هنگام رفتن به مسجد [و در هر نمازی] زینت خود را برگیرید [و عورت‌هایتان را بپوشانید و لباس شایسته بپوشید] و [در زندگی، از خوراکی‌های پاکیزه] بخورید و بیاشامید؛ و[لی] اسراف نکنید [که] بی‌تردید، الله اسرافکاران را دوست ندارد.');
INSERT INTO "islamhouse" VALUES(7,32,'7:32','[ای پیامبر، به مشرکان] بگو: «لباس [و زینت‌هایی] را که الله برای بندگانش پدید آورده و [همچنین] روزی‌های پاکیزه را چه کسی حرام کرده است؟». بگو: «این [نعمت‌ها] در زندگی دنیا برای کسانی است که ایمان آورده‌اند [و اگر چه کافران نیز از آنها بهره‌مندند،] روز قیامت، مخصوص [مؤمنان] خواهد بود. ما اینچنین آیات [خود] را برای گروهی که می‌دانند، به تفصیل بیان می‌کنیم.');
INSERT INTO "islamhouse" VALUES(7,33,'7:33','بگو: «پروردگارم زشتکاری‌ها را ـ چه آشكار [باشد] و چه پنهان‌ـ و گناه و ستم ناحق را حرام نموده است و اینكه کسی [یا چیزى] را شریک الله بدانید كه دلیلى بر [حقانیت‌] آن نازل نكرده است و اینكه چیزى را كه نمى‌دانید به الله نسبت دهید.');
INSERT INTO "islamhouse" VALUES(7,34,'7:34','هر امتی، اَجَلی دارد؛ و هنگامی‌ که اجلشان به سر آید، نه لحظه‌ای [از آن] تأخیر ‌می‌کنند و نه [بر آن] پیشی می‌گیرند.');
INSERT INTO "islamhouse" VALUES(7,35,'7:35','ای فرزندان آدم، اگر پیامبرانی از خود شما [نزدتان] بیایند که آیات مرا برایتان بخوانند، [از آنان پیروی کنید]؛ پس کسانی که پرهیزگاری نمایند و [رفتارهای گذشته‌ را] اصلاح کنند، نه ترسی خواهند داشت و نه اندوهگین می‌شوند.');
INSERT INTO "islamhouse" VALUES(7,36,'7:36','و کسانی‌ که آیات ما را دروغ می‌پندارند و در برابر آنها [گردنکشی و] تکبر می‌ورزند، اهل دوزخند و جاودانه در آن خواهند ماند.');
INSERT INTO "islamhouse" VALUES(7,37,'7:37','پس کیست ستمکارتر از آن کس که بر الله دروغ می‌بندد یا آیاتش را دروغ می‌پندارد؟ آنان کسانی هستند که نصیبشان از آنچه [در لوح محفوظ برایشان] مقرر شده است، به آنان خواهد رسید تا آنگاه که فرستادگان ما به سراغشان می‌آیند که جانشان را بگیرند [به آنان] می‌گویند: «آنهایی که به جای الله [به یاری و دعا] می‌خواندید کجا هستند؟» [آنان] می‌گویند: «از [نزد] ما گم شدند»؛ و [این گونه] علیه خود گواهی می‌دهند که کافر بوده‌اند.');
INSERT INTO "islamhouse" VALUES(7,38,'7:38','[فرشتگان] به آنها می‌گویند: «در بین گروه‌هایی از جن و انس که پیش از شما [کافر و گمراه] بوده‌اند، در آتش [دوزخ] وارد شوید». هر گاه گروهی وارد می‌شوند، گروه دیگر را لعنت می‌کنند تا هنگامی‌ که [سرانجام] همگی در آنجا به هم می‌رسند. [آنگاه] پیروان [فریب‌خوردۀ] آنها دربارۀ رهبرانشان می‌گویند: «پروردگارا، اینان بودند که ما را گمراه کردند؛ پس عذابی چند برابر از آتش به آنان بده». [الله] می‌فرماید: «برای هر کدام [از شما عذابی] چند برابر است؛ ولی نمی‌دانید».');
INSERT INTO "islamhouse" VALUES(7,39,'7:39','رهبران آنها به پیروانشان می‌گویند: «شما هیچ امتیازی بر ما ندارید؛ پس به [کیفر] آنچه می‌کردید، عذاب را بچشید».');
INSERT INTO "islamhouse" VALUES(7,40,'7:40','کسانی‌ که آیات ما را تکذیب کردند و در برابر آنها [گردنکشی و] تکبر نمودند، درهای آسمان هرگز برایشان گشوده نمی‌شود و به بهشت وارد نخواهند شد، مگر آنکه شتر از سوراخ سوزن وارد شود؛ و گناهکاران را اینچنین کیفر می‌دهیم.');
INSERT INTO "islamhouse" VALUES(7,41,'7:41','بستری از [آتش] دوزخ برایشان [مهیّاست] و [رواندازشان نیز] پوشش‌هایی [از آتش است]؛ و ستمکاران را اینچنین کیفر می‌دهیم.');
INSERT INTO "islamhouse" VALUES(7,42,'7:42','کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، [هر اندازه عمل نیک كرده‌اند می‌پذیریم؛ چرا كه‌] بر هیچ کس جز به اندازۀ توانش تکلیف نمی‌کنیم‌. اینان بهشتی‌اند و در آن جاودان خواهند بود.');
INSERT INTO "islamhouse" VALUES(7,43,'7:43','و هر كینه‌اى را از سینه‌هایشان مى‌زداییم؛ از زیر [قصرهای] آنان جویبارها جاری است و می‌گویند: «سپاس مخصوص الله است که ما را [توفیقِ عبادت داد و] به این [بهشت] هدایت نمود؛ و اگر الله ما را هدایت نمی‌کرد، هرگز راه نمی‌یافتیم. بی‌تردید، رسولان پروردگارمان، حق را آوردند». [آنگاه] به آنان نِدا داده می‌شود که: «این بهشت، به پاداش آنچه می‌کردید به شما رسیده است».');
INSERT INTO "islamhouse" VALUES(7,44,'7:44','بهشتیان به دوزخیان ندا مى‌دهند كه: «ما آنچه را که پروردگارمان به ما وعده داده بود درست یافتیم. آیا شما [نیز] آنچه را که پروردگارتان وعده داده بود درست یافتید؟» آنان مى‌گویند: «آرى». آنگاه ندادهنده‌ای در میان آنان ندا درمى‌دهد كه: «لعنتِ الله بر ستمكاران [کافر] باد!»');
INSERT INTO "islamhouse" VALUES(7,45,'7:45','[همان] کسانی‌ که [مردم را] از راه الله باز می‌داشتند و آن را [با القاى شُبهات‌] منحرف مى‌خواستند و آخرت را باور نداشتند».');
INSERT INTO "islamhouse" VALUES(7,46,'7:46','در میان آن دو [گروه بهشتی و دوزخی،] مانعی است و بر اَعراف [= بلندی‌های آن مانع] مردانی هستند که هر یک [از آن دو گروه] را از [نشانه‌های] چهره‌شان می‌شناسند و [از بلندای اعراف،] اهل بهشت را ندا می‌دهند که: «سلام بر شما باد». اینان امید [ورود به] بهشت دارند و هنوز به آن وارد نشده‌اند.');
INSERT INTO "islamhouse" VALUES(7,47,'7:47','و هنگامی که چشمانشان متوجه اهل دوزخ می‌شود، می‌گویند: «پروردگارا، ما را با گروه ستمکاران [در یکجا] قرار نده».');
INSERT INTO "islamhouse" VALUES(7,48,'7:48','اهل اعراف، مردانی [از دوزخیان را] که آنان را از چهره‌هایشان می‌شناسند، صدا می‌زنند [و] می‌گویند: «جمعیت [و مال‌اندوزیِ] شما و آن [همه‌] گردنكشى [و تکبر] كه مى‌كردید، سودی به حالتان نداشت».');
INSERT INTO "islamhouse" VALUES(7,49,'7:49','آیا اینها [= بهشتیان] همان کسانی هستند که سوگند یاد می‌کردید که الله [هرگز] رحمتی به آنان نمی‌رسانَد؟ [سپس الله می‌فرماید: ای مؤمنان،] به بهشت وارد شوید [که در آنجا] نه ترسی خواهید داشت و نه اندوهگین می‌شوید.');
INSERT INTO "islamhouse" VALUES(7,50,'7:50','اهل دوزخ، به بهشتیان ندا می‌دهند که: «از آن آب یا آنچه الله روزی‌تان کرده است اندکی ما را بهره‌مند سازید». [آنان در پاسخ] می‌گویند: «الله هر دو را بر کافران حرام کرده است».');
INSERT INTO "islamhouse" VALUES(7,51,'7:51','[همان] کسانی كه دین خود را به سرگرمى و بازى گرفتند و زندگى دنیا فریبشان داد. پس چنان كه آنان دیدار امروزشان را از یاد بردند و آیات ما را انكار ‌كردند، ما [نیز‌] امروز آنان را از یاد مى‌بریم [و در عذاب رها می‌کنیم].');
INSERT INTO "islamhouse" VALUES(7,52,'7:52','به راستی، ما کتابی برایشان آوردیم كه آن را بر [اساسِ] علم [بیکران الهی] به تفصیل بیان کرده‌ایم [و نازل کردیم] تا براى گروهى كه ایمان مى‌آورند، هدایت و رحمتى باشد.');
INSERT INTO "islamhouse" VALUES(7,53,'7:53','آیا آنان [= کافران] جز تأویل آیات [و فرا رسیدنِ قیامت] انتظار [دیگری] دارند؟ روزی ‌که تأویلش [= قیامت] فرارسد، کسانی‌ که آن را پیشتر فراموش کرده بودند می‌گویند: «یقیناً رسولانِ پروردگارمان حق را آوردند؛ پس آیا [امروز] شفاعتگرانی داریم که شفاعت ما را بكنند یا [می‌توانیم به دنیا] بازگردیم تا کارهایی غیر از آنچه می‌کردیم انجام دهیم؟» بی‌تردید، [آنان‌] به خود زیان زدند و آنچه را به دروغ [معبودِ خود] مى‌ساختند [همه] محو و نابود شد.');
INSERT INTO "islamhouse" VALUES(7,54,'7:54','[ای مردم،] بی‌گمان، پروردگار شما الله است که آسمان‌ها و زمین را در شش روز آفرید؛ سپس [آن گونه كه شايسته جلال و عظمت اوست] بر عرش قرار گرفت. [تاریکیِ] شب را با [روشناییِ] روز می‌پوشانَد که [هر یک،] آن [دیگری] را شتابان [و پیوسته] می‌جوید و خورشید و ماه و ستارگان را [پدید آورد] كه [همگی] به فرمانِ او به خدمت [انسان] گماشته شده‌اند. آگاه باشید! آفرینش و فرمانروایی، از آنِ اوست. پربرکت [و بزرگ] است الله که پروردگار جهانیان است.');
INSERT INTO "islamhouse" VALUES(7,55,'7:55','[ای مؤمنان،] پروردگارتان را به زاری و پنهانی بخوانید که او متجاوزان [از حدود الهی] را دوست ندارد.');
INSERT INTO "islamhouse" VALUES(7,56,'7:56','و در زمین ـ پس از آنکه [با بعثت پیامبران و دینداریِ مؤمنان] اصلاح گشته است ـ فتنه و فساد نکنید و او [= الله] را با بیم و امید بخوانید [که] بی‌تردید، رحمت الله به نیکوکاران نزدیک است.');
INSERT INTO "islamhouse" VALUES(7,57,'7:57','او ذاتی است که پیشاپیشِ [بارانِ] رحمتش، بادها را بشارت‌بخش می‌فرستد تا ابرهای سنگین‌بار [از آب] را [بر دوش] کشند [و] آن را به سوی سرزمینی [خشک و] مرده می‌فرستیم؛ آنگاه بر آن [سرزمین،] آب فرو می‌باریم و با [برکتِ] آن [آب] هر گونه میوه‌اى برمی‌آوریم. این‌گونه، مردگان را [نیز از قبر] خارج مى‌سازیم؛ باشد كه پند گیرید.');
INSERT INTO "islamhouse" VALUES(7,58,'7:58','سرزمین پاکیزه [و آماده،] گیاهش به فرمان پروردگارش [فراوان و پُربار] می‌روید و از آن [شوره‌زاری] که ناپاک است، جز [گیاهی] اندک و بی‌ارزش نمی‌روید. این گونه آیات [خود] را برای گروهی که سپاس می‌گزارند، گوناگون بیان می‌کنیم.');
INSERT INTO "islamhouse" VALUES(7,59,'7:59','بی‌تردید، ما نوح را به سوی قومش فرستادیم، پس گفت: «ای قوم من، الله را عبادت کنید که جز او معبودی [به‌حق] ندارید. [اگر او را عبادت نکنید،] من بر شما از عذاب روزی بزرگ می‌ترسم».');
INSERT INTO "islamhouse" VALUES(7,60,'7:60','اشراف [و بزرگان] قومش گفتند: «به راستی که ما تو را در گمراهیِ آشکار می‌بینیم».');
INSERT INTO "islamhouse" VALUES(7,61,'7:61','او گفت: «ای قوم من، هیچ گونه گمراهی [و انحرافی] در من نیست؛ بلکه من فرستاده‌ای از [جانب] پروردگار جهانیان هستم.');
INSERT INTO "islamhouse" VALUES(7,62,'7:62','پیام‌های پروردگارم را به شما می‌رسانم و برایتان خیرخواهی می‌کنم و از [شریعتِ] الله چیزهایی می‌دانم که شما نمی‌دانید.');
INSERT INTO "islamhouse" VALUES(7,63,'7:63','آیا تعجب كرد‌ه‌اید كه پندى از جانب پروردگارتان توسط مردى از خودِ شما برایتان آمده است تا شما را [از عذاب قیامت] بیم دهد که پروا پیشه کنید و باشد که مشمول رحمت گردید؟»');
INSERT INTO "islamhouse" VALUES(7,64,'7:64','اما وی را دروغگو پنداشتند؛ آنگاه ما او و کسانی را که همراهش در کشتی بودند نجات دادیم و کسانی ‌که آیات ما را تکذیب کرده بودند غرق کردیم. به راستی که آنان [در قبال حق،] گروهی نابینا بودند.');
INSERT INTO "islamhouse" VALUES(7,65,'7:65','و به [سوی قوم] عاد، برادرشان ـ هودـ را [فرستادیم]. او گفت: «ای قوم من، الله را عبادت کنید که جز او معبودی [به‌حق] ندارید. آیا پروا نمی‌کنید؟»');
INSERT INTO "islamhouse" VALUES(7,66,'7:66','اشراف [و بزرگان] قوم او که کافر بودند گفتند: «بی‌گمان، ما تو را در نادانی می‌بینیم؛ و ما قطعاً تو را از دروغگویان می‌پنداریم».');
INSERT INTO "islamhouse" VALUES(7,67,'7:67','گفت: «ای قوم من، هیچ‌ گونه نادانی‌ای در من نیست؛ بلکه من فرستاده‌ای از [جانب] پروردگار جهانیان هستم.');
INSERT INTO "islamhouse" VALUES(7,68,'7:68','پیام‌های پروردگارم را به شما می‌رسانم و من خیرخواهی امین برایتان هستم.');
INSERT INTO "islamhouse" VALUES(7,69,'7:69','آیا تعجب كرد‌ه‌اید كه پندى از جانب پروردگارتان توسط مردى از خودِ شما برایتان آمده است تا شما را [از عذاب قیامت] بیم دهد؟ و به یاد آورید هنگامی ‌که [الله] پس از قوم نوح، شما را جانشینان [آنان] قرار داد و به شما در آفرینش، فزونی [و قدرت] داد. پس نعمت‌های الله را به یاد آورید؛ باشد که رستگار شوید».');
INSERT INTO "islamhouse" VALUES(7,70,'7:70','[کافران] گفتند: «آیا به سوی ما آمده‌ای که فقط الله را عبادت کنیم و آنچه را پدرانمان عبادت می‌کردند رها کنیم؟ پس اگر از راستگویانی، آنچه [از عذاب الهی که] به ما وعده می‌دهی، بیاور».');
INSERT INTO "islamhouse" VALUES(7,71,'7:71','[هود] گفت: «بی‌تردید، عذاب و خشمی [سخت] از [سوی] پروردگارتان بر شما واقع شده [و شما را فراگرفته] است. آیا دربارۀ اسم‌هایی که خودِ شما و پدرانتان آنها را [معبود] نام نهاده‌اید و الله هیچ دلیلی بر [حقانیتِ] آنها نازل نکرده است با من مجادله می‌کنید؟ پس منتظر باشید [که] بی‌گمان، من [نیز] با شما از منتظرانم».');
INSERT INTO "islamhouse" VALUES(7,72,'7:72','پس او و کسانی را که همراهش بودند به رحمت [و لطفی] از [جانب] خویش نجات دادیم و ریشۀ کسانی که آیات ما را تکذیب کردند و از مؤمنان نبودند، برانداختیم.');
INSERT INTO "islamhouse" VALUES(7,73,'7:73','و به [سوی قوم] ثَمود، برادرشان ـ صالح ـ را [فرستادیم]. او گفت: «ای قوم من، الله را عبادت کنید که جز او معبودی [به‌حق] ندارید. بی‌تردید، دلیل روشنی از [سوی] پروردگارتان برای شما آمده است. این ماده‌شترِ الله [که از دلِ صخره‌ها بیرون آمده است] معجزه‌ای برایتان است؛ پس او را [به حال خود] واگذارید تا در زمین الله [بچرد و علف] بخورد و به او آزار [و آسیبی] نرسانید که شما را عذابی دردناک فرا خواهد گرفت.');
INSERT INTO "islamhouse" VALUES(7,74,'7:74','و به یاد آورید هنگامی ‌که [الله] شما را پس از [قوم] عاد، جانشینان [آنان] قرار داد و در زمین مستقر ساخت که در دشت‌هایش قصرهایی بنا می‌کنید و در کوه‌ها خانه‌هایی می‌تراشید. پس نعمت‌های الله را به یاد آورید و در زمین به فساد [و تباهی] نکوشید».');
INSERT INTO "islamhouse" VALUES(7,75,'7:75','اشراف [و بزرگان] قومِ او که گردنکشی می‌کردند، به مستضعفان ـ به [همان] کسانی از آنان که ایمان آورده بودندـ گفتند: «آیا [به یقین] می‌دانید که صالح از [طرف] پروردگارش فرستاده شده است؟» [آنان] گفتند: «ما به آنچه او با آن فرستاده شده است ایمان داریم».');
INSERT INTO "islamhouse" VALUES(7,76,'7:76','کسانی که گردنکشی می‌کردند گفتند: «ما به آنچه شما به آن ایمان آورده‌اید، باور نداریم».');
INSERT INTO "islamhouse" VALUES(7,77,'7:77','پس ماده‌شتر را پِی کردند و از فرمان پروردگارشان سرپیچی نمودند و گفتند: «ای صالح، اگر از پیامبران [الهی] هستی، آنچه از عذاب که به ما وعده می‌دهی [بر سرمان] بیاور».');
INSERT INTO "islamhouse" VALUES(7,78,'7:78','سرانجام زمین‌لرزه [شدیدی] آنان را فراگرفت؛ و در خانه‌های خود به روی افتادند [و هلاک شدند].');
INSERT INTO "islamhouse" VALUES(7,79,'7:79','پس [صالح] از آنان روی گردانْد و گفت: «ای قوم من، من پیام پروردگارم را به شما رساندم و برایتان خیرخواهی کردم؛ ولى شما [خیرخواهان و] نصیحت‌گران را دوست ندارید».');
INSERT INTO "islamhouse" VALUES(7,80,'7:80','و لوط را [به پیامبری فرستادیم‌] ‌که به قوم خود گفت: «آیا کار بسیار زشتی را انجام می‌دهید که پیش از شما هیچ یک از جهانیان انجام نداده است؟');
INSERT INTO "islamhouse" VALUES(7,81,'7:81','شما از روى شهوت، به جاى زنان، با مردان مى‌آمیزید. آرى، شما [از حدود الهی فراتر رفته‌اید و] گروهى تجاوزكارید».');
INSERT INTO "islamhouse" VALUES(7,82,'7:82','و[لى] پاسخ قومش جز این نبود كه گفتند: «آنان را از شهرتان بیرون كنید؛ [چرا که] بی‌تردید، اینها افرادی پاکدامن [و مقدس‌مَآب] هستند».');
INSERT INTO "islamhouse" VALUES(7,83,'7:83','پس ما او و خاندانش را نجات دادیم؛ مگر همسرش که از بازماندگان [در عذاب] بود.');
INSERT INTO "islamhouse" VALUES(7,84,'7:84','و بارانی [از سنگ] بر آنان باریدیم. پس بنگر که سرانجام گناهکاران چگونه بود.');
INSERT INTO "islamhouse" VALUES(7,85,'7:85','و به سوی [مردم] مَدیَن، برادرشان شُعیب را [فرستادیم]. او گفت: «ای قوم من، الله را عبادت کنید که جز او معبودی [به‌حق] ندارید. یقیناً از [سوی] پروردگارتان دلیلی روشن برای شما آمده است؛ پس [حقِ] پیمانه و ترازو را کامل [و درست] ادا کنید و از اجناس مردم نکاهید و در زمین ـ پس از اصلاحش ـ فساد نکنید. اگر ایمان دارید، این برای شما بهتر است؛');
INSERT INTO "islamhouse" VALUES(7,86,'7:86','و بر سر هر راهى ننشینید كه [با زورگیری و دزدی و تهدید به قتل، مردم را] بترسانید و هر کس را که ایمان دارد از راه الله بازدارید و راهش را منحرف بخواهید [که به هدایت دست نیابد]؛ و به یاد آورید هنگامى را كه اندک بودید، سپس الله [تعداد] شما را فزونی بخشید؛ و بنگرید که سرانجامِ مفسدان [تبهکار] چگونه بود.');
INSERT INTO "islamhouse" VALUES(7,87,'7:87','اگر گروهى از شما به آنچه با آن فرستاده شده‌ام ایمان آوردند و گروه دیگر ایمان نیاوردند، صبر كنید تا الله میان ما داورى كند و او بهترین داور است».');
INSERT INTO "islamhouse" VALUES(7,88,'7:88','اشراف [و بزرگان] قومش كه تكبر مى‌ورزیدند گفتند: «ای شعیب، قطعاً تو و کسانی را که همراهت ایمان آورده‌اند از شهرمان بیرون می‌کنیم؛ [پس یا بروید] یا به آیین ما باز‌گردید». [شعیب] گفت: «آیا [بازگردیم حتی] اگر از آن کراهت داشته [و بیزار] باشیم؟');
INSERT INTO "islamhouse" VALUES(7,89,'7:89','اگر به آیین شما برگردیم پس از آنكه الله ما را از آن نجات بخشید، در حقیقت، به الله دروغ بسته‌ایم؛ و سزاوار ما نیست كه به آن بازگردیم، مگر آنكه الله ـ پروردگار ما ـ بخواهد. علم پروردگارمان همه چیز را فراگرفته است؛ بر الله توكل كرده‌ایم. پروردگارا، میان ما و قوممان به حق داورى كن و تو بهترین داورى».');
INSERT INTO "islamhouse" VALUES(7,90,'7:90','و اشراف [و بزرگان] قومش که کافر بودند گفتند: «اگر از شعیب پیروی کنید، قطعاً زیانکار خواهید بود».');
INSERT INTO "islamhouse" VALUES(7,91,'7:91','آنگاه زلزلۀ [شدیدی] آنان را فراگرفت؛ و در خانه‌های خود به رویْ درافتادند [و هلاک شدند].');
INSERT INTO "islamhouse" VALUES(7,92,'7:92','کسانی که شعیب را دروغگو انگاشتند [چنان از بین رفتند که] گویی هرگز در آن [دیار] نبوده‌اند؛ و آنان که شعیب را دروغگو انگاشتند، خود زیانکار بودند.');
INSERT INTO "islamhouse" VALUES(7,93,'7:93','پس [شعیب‌] از آنان روى برگرداند و گفت: «اى قوم من، بی‌تردید، پیام‌های پروردگارم را به شما رساندم و پندتان دادم. پس چگونه برای گروهى كافر [لجوج] اندوهگین باشم؟»');
INSERT INTO "islamhouse" VALUES(7,94,'7:94','و ما در هیچ شهر [و دیاری] پیامبری نفرستادیم، مگر اینکه ساکنانش را به رنج‌ها و سختی‌ها گرفتار ساختیم؛ باشد که [به درگاه الهی، فروتنی و] تضرع کنند.');
INSERT INTO "islamhouse" VALUES(7,95,'7:95','سپس به جای ناخوشی [و بدی،] نیکی [و خوشی] آوردیم تا افزون شدند و گفتند: «بی‌تردید، به پدران ما [نیز به حكم طبیعت،‌] سختی و راحتی مى‌رسید». پس در حالى كه بی‌خبر بودند، ناگهان آنان را [به عذاب سخت] گرفتیم.');
INSERT INTO "islamhouse" VALUES(7,96,'7:96','و اگر اهل آن شهرها ایمان می‌آوردند و پروا می‌کردند، [درهای‌] برکات آسمان و زمین را بر آنان می‌گشودیم؛ ولی [آیات و پیام‌های ما را] تکذیب کردند؛ پس به [كیفرِ] آنچه می‌کردند، آنان را [به عذاب] فروگرفتیم‌.');
INSERT INTO "islamhouse" VALUES(7,97,'7:97','آیا ساکنان [این] شهرها در امانند از اینکه عذاب ما [ناگهان] شب‌هنگام‌ که خفته‌اند به سراغشان بیاید؟');
INSERT INTO "islamhouse" VALUES(7,98,'7:98','و آیا ساکنان [این] شهرها در امانند از اینکه عذاب ما نیمروز ‌که به بازی [و امور بیهوده] سرگرمند، به سراغشان بیاید؟');
INSERT INTO "islamhouse" VALUES(7,99,'7:99','آیا خود را از مكر [و تدبیر] الله ایمن دانسته‌اند؟ [حال آنكه‌] جز مردمِ زیانكار، [كسى‌] خود را از مكرِ الله در امان نمى‌داند.');
INSERT INTO "islamhouse" VALUES(7,100,'7:100','مگر براى كسانى كه زمین را پس از ساكنان [پیشین‌] آن به ارث مى‌برند، روشن نشده است كه اگر می‌خواستیم، آنان را به [كیفر] گناهانشان نابود می‌کردیم و بر دل‌هایشان مُهر مى‌نهادیم تا دیگر [ندای حق را] نشنوند؟');
INSERT INTO "islamhouse" VALUES(7,101,'7:101','[ای پیامبر،] اینها شهرهایی است كه برخى از خبرهایشان را بر تو حكایت مى‌كنیم. بی‌تردید، پیامبران آنها دلایل روشن را برایشان آوردند؛ ولی آنان نمی‌خواستند به چیزی که پیشتر دروغ شمرده بودند ایمان بیاورند. الله بر دل‌های کافران، این گونه مُهر [غفلت] می‌نهد.');
INSERT INTO "islamhouse" VALUES(7,102,'7:102','و در بیشتر آنان وفای به عهد نیافتیم و اکثرشان را جداً نافرمان یافتیم.‌');
INSERT INTO "islamhouse" VALUES(7,103,'7:103','سپس بعد از آنها موسی را با آیات خویش به سوی فرعون و بزرگان قومش فرستادیم؛ ولى آنها به آن [آیات‌] كفر ورزیدند؛ پس بنگر که سرانجامِ تبهکاران چگونه بوده است‌.');
INSERT INTO "islamhouse" VALUES(7,104,'7:104','و موسی گفت: «ای فرعون، من فرستاده‌ای از [سوی] پروردگار جهانیانم.');
INSERT INTO "islamhouse" VALUES(7,105,'7:105','سزاوار است كه در مورد الله جز [به راستى و] حق سخن نگویم. بی‌تردید، من دلیل [و معجزه‌ای روشن] از پروردگارتان برای شما آورده‌ام؛ پس بنی‌اسرائیل را همراه من بفرست».');
INSERT INTO "islamhouse" VALUES(7,106,'7:106','[فرعون] گفت: «اگر معجزه‌ای آورده‌ای [و] اگر راست می‌گویى، آن را بیاور».');
INSERT INTO "islamhouse" VALUES(7,107,'7:107','پس [موسی] عصایش را انداخت و ناگهان [تبدیل به] اژدهایی آشکار [و بزرگ تبدیل] شد.');
INSERT INTO "islamhouse" VALUES(7,108,'7:108','و دستش را [از گریبان‌] بیرون آورد و ناگهان براى تماشاگران سپید [و درخشان‌] می‌نمود.');
INSERT INTO "islamhouse" VALUES(7,109,'7:109','اشراف [و بزرگان] قوم فرعون گفتند: «حقا که این [مرد] جادوگری داناست».');
INSERT INTO "islamhouse" VALUES(7,110,'7:110','[فرعون گفت:] «او می‌خواهد شما را از سرزمینتان بیرون کند؛ پس نظرتان چیست؟»');
INSERT INTO "islamhouse" VALUES(7,111,'7:111','آنان گفتند: «کارِ او و برادرش را به تأخیر بینداز و مأمورانِ جمع‌آوری‌ را به [همۀ] شهرها بفرست؛');
INSERT INTO "islamhouse" VALUES(7,112,'7:112','تا همۀ جادوگران دانا [و ماهر] را نزدت بیاورند».');
INSERT INTO "islamhouse" VALUES(7,113,'7:113','و [سرانجام‌،] جادوگران نزد فرعون آمدند [و] گفتند: «آیا اگر ما پیروز شدیم، [نزد تو] پاداشی خواهیم داشت؟»');
INSERT INTO "islamhouse" VALUES(7,114,'7:114','[فرعون] گفت: «آری، قطعاً شما از مقرّبان [درگاهم] خواهید بود».');
INSERT INTO "islamhouse" VALUES(7,115,'7:115','[در روز موعود، جادوگران مغرورانه] گفتند: «ای موسی، یا تو نخست [عصایت را] بیفکن یا ما [ابزار خود را] می‌افکنیم».');
INSERT INTO "islamhouse" VALUES(7,116,'7:116','[موسی] گفت: «شما بیفکنید». هنگامی که [ابزارشان را] افکندند، چشمان مردم را افسون کردند و آنان را سخت ترساندند و جادویی بزرگ [پدید] آوردند.');
INSERT INTO "islamhouse" VALUES(7,117,'7:117','و [ما] به موسی وحی کردیم که: «عصایت را بینداز». ناگهان آن [عصا، به مار بزرگی تبدیل شد و] آنچه را به دروغ ساخته بودند، فرو ‌بلعید.');
INSERT INTO "islamhouse" VALUES(7,118,'7:118','پس حقیقت آشكار گردید و آنچه مى‌كردند باطل شد.');
INSERT INTO "islamhouse" VALUES(7,119,'7:119','و [فرعون و فرعونیان همگی در] آنجا شکست خوردند و خوار گردیدند،');
INSERT INTO "islamhouse" VALUES(7,120,'7:120','و جادوگران به سجده افتادند.');
INSERT INTO "islamhouse" VALUES(7,121,'7:121','گفتند: «به پروردگار جهانیان ایمان آوردیم؛');
INSERT INTO "islamhouse" VALUES(7,122,'7:122','پروردگار موسی و هارون».');
INSERT INTO "islamhouse" VALUES(7,123,'7:123','فرعون گفت: «پیش از آنکه به شما اجازه دهم به او ایمان آوردید؟ قطعاً این نیرنگى است كه در شهر به راه انداخته‌اید تا ساکنانش را از آن بیرون كنید. پس به زودى [نتیجۀ کارتان را] خواهید دانست.');
INSERT INTO "islamhouse" VALUES(7,124,'7:124','یقیناً دست و پایتان را بر خلافِ یكدیگر [از چپ و راست] قطع مى‌كنم و همگی شما را به دار مى‌آویزم».');
INSERT INTO "islamhouse" VALUES(7,125,'7:125','آنان گفتند: «بی‌تردید، ما [پس از مرگ] به سوی پروردگارمان بازمی‌گردیم');
INSERT INTO "islamhouse" VALUES(7,126,'7:126','و [ای فرعون،] تو از ما ایراد نمى‌گیری جز به این سبب كه به معجزات پروردگارمان ـ آنگاه که به ما رسید ـ ایمان آوردیم. پروردگارا، بر ما شکیبایی [و استقامت] ببار و ما را در حالی بمیران که تسلیم [فرمان تو] هستیم».');
INSERT INTO "islamhouse" VALUES(7,127,'7:127','و بزرگان قوم فرعون [به او] گفتند: «آیا موسی و قومش را رها می‌کنی تا در این سرزمین فساد کنند و تو و معبودانت را رها كنند؟». [فرعون] گفت: «[هرگز؛ بلکه] پسرانشان را خواهیم کشت و زنانشان را [برای کنیزی‌] زنده خواهیم گذاشت؛ و ما بر آنان چیره‌ایم».');
INSERT INTO "islamhouse" VALUES(7,128,'7:128','موسی به قومش گفت: «از الله یاری بجویید و شکیبایی [و استقامت] پیشه کنید. بی‌تردید، زمین از آنِ الله است [و] آن را به هر‌ یک از بندگانش که بخواهد، به میراث می‌دهد؛ و سرانجام نیک، از آنِ پرهیزگاران است».');
INSERT INTO "islamhouse" VALUES(7,129,'7:129','[بنی‌اسرائیل] گفتند: «پیش از آنکه نزد ما بیایی و [حتی] پس از آنکه نزد ما آمدی، [از ستم‌های فرعون] آزار و اذیت دیده‌ایم». [موسی] گفت: «امید است که پروردگار شما دشمنتان را هلاک کند و شما را در زمین جانشین [آنان] سازد؛ آنگاه بنگرد که چگونه رفتار می‌کنید».');
INSERT INTO "islamhouse" VALUES(7,130,'7:130','و بی‌تردید، ما فرعونیان را به خشكسالى و كمبود محصولات دچار كردیم؛ باشد كه پند گیرند [و توبه کنند].');
INSERT INTO "islamhouse" VALUES(7,131,'7:131','اما وقتی نیکی [و فراوانیِ نعمت] به آنان می‌رسید، می‌گفتند: «این به سبب [شایستگىِ‌] خود ماست» و اگر بدی [و خشکسالی] به آنان می‌رسید، به موسی و کسانی که همراهش بودند، شگونِ بد می‌زدند. آگاه باشید! سرنوشتِ شوم آنان، تنها نزد الله [و به قضا و قدر او] است؛ اما بیشتر آنان نمی‌دانند.');
INSERT INTO "islamhouse" VALUES(7,132,'7:132','و [پیروان فرعون به او] گفتند: «هر [معجزه و] نشانه‌ای برایمان بیاوری تا ما را با آن جادو کنی، [هرگز] به تو ایمان نمی‌آوریم».');
INSERT INTO "islamhouse" VALUES(7,133,'7:133','پس [بلاهایی همچون] طوفان و ملخ و شته و قورباغه‌ها و [رودِ پر] خون بر آنان فرستادیم [که] نشانه‌هایی جدا [از هم و پیاپی] بودند؛ اما [باز هم] گردنکشی کردند و گروهی گناهکار بودند.');
INSERT INTO "islamhouse" VALUES(7,134,'7:134','و هنگامی که عذاب بر آنان واقع ‌شد، ‌گفتند: «ای موسی، از پروردگارت برای ما بخواه، بنا به پیمانی که با تو دارد [تا این عذاب و بلا را از ما دور کند]. اگر این عذاب را از ما برداری، قطعاً به تو ایمان می‌آوریم و بنى‌اسرائیل را همراه تو روانه می‌کنیم».');
INSERT INTO "islamhouse" VALUES(7,135,'7:135','اما چون عذاب را تا مدت معینی که [پیش از هلاکت] فرصت داشتند از آنان دور کردیم، باز هم پیمان‌شکنی کردند.');
INSERT INTO "islamhouse" VALUES(7,136,'7:136','پس از آنان انتقام گرفتیم و در دریا غرقشان ساختیم؛ زیرا آیات [و معجزات] ما را دروغ می‌انگاشتند و از آن غافل بودند.');
INSERT INTO "islamhouse" VALUES(7,137,'7:137','و به آن گروهى كه همواره مورد ستم واقع مى‌شدند، [بخش‌های] شرقی و غربیِ سرزمین [شام‌] را ـ كه به آن بركت بخشیده بودیم ـ به میراث عطا كردیم و به پاداش آنكه شکیبایی كردند، وعدۀ نیكوى پروردگارت به بنی‌اسرائیل [= یاریِ ستمدیدگان] تحقق یافت و آنچه را كه فرعون و قومش می‌ساختند و برمی‌افراشتند [یکسره] ویران كردیم.');
INSERT INTO "islamhouse" VALUES(7,138,'7:138','و بنی‌اسرائیل را از دریا گذراندیم و [در راه] به قومى رسیدند كه برای [عبادت‌] بُت‌هایشان گرد آمده بودند؛[در این هنگام بنی­اسرائیل] گفتند: «اى موسى، همان گونه كه آنان معبودانی دارند، براى ما [نیز] معبودی تعیین کن». [موسی] گفت: «بی‌تردید، شما گروهی هستید که جهالت [و نادانى] مى‌كنید [و عظمتِ الله را نمی‌دانید].');
INSERT INTO "islamhouse" VALUES(7,139,'7:139','یقیناً آیین [مشرکانۀ] اینها نابودشدنى است و آنچه می‌کنند [نیز] باطل است».');
INSERT INTO "islamhouse" VALUES(7,140,'7:140','[سپس] گفت: «آیا به جز الله معبودی [دیگر] برایتان بجویم در حالی‌ که او شما را بر جهانیان [هم‌عصرتان] برتری داده است؟»');
INSERT INTO "islamhouse" VALUES(7,141,'7:141','و [به یاد آورید] زمانی که شما را از [آسیبِ] فرعونیان نجات دادیم [که] بدترین [و خفت‌بارترین] عذاب را بر سرتان می‌آوردند: پسرانتان را می‌کشتند و زنانتان را [برای کنیزی] زنده نگه می‌داشتند؛ و در این [عذاب و نجات،] آزمایشی بزرگ از جانب پروردگارتان برای شما بود.');
INSERT INTO "islamhouse" VALUES(7,142,'7:142','و با موسی سی شب [برای مناجات] وعده نهادیم و آن را با ده شب [دیگر] کامل کردیم؛ به این ترتیب وعدۀ پروردگارش [با او] در چهل شب تمام شد. و موسی [هنگام رفتن به كوه طور] به برادرش هارون گفت: «جانشین من در میان قومم باش و [امورشان را] اصلاح کن و از راهِ مفسدان پیروی نکن».');
INSERT INTO "islamhouse" VALUES(7,143,'7:143','و هنگامی‌ که موسی به وعده‌گاهِ ما آمد و پروردگارش [بی‌واسطه] با او سخن گفت. [موسی] عرض کرد: «پروردگارا، خود را به من نشان بده تا به تو بنگرم». [الله] فرمود: «[در دنیا] هرگز مرا نخواهی دید؛ ولی به این کوه بنگر؛ اگر در جای خود برقرار باقی ماند، تو [نیز] مرا خواهی دید». پس چون پروردگارش بر [آن] کوه تجلی کرد، آن را متلاشی [و پراکنده] ساخت و موسی مدهوش افتاد و چون به هوش آمد، عرض کرد: «[بار الها،] تو پاک و منزّهی. به درگاهت توبه كردم و بی‌تردید، من [میان قومِ خود] نخستین مؤمن هستم».');
INSERT INTO "islamhouse" VALUES(7,144,'7:144','[الله] فرمود: «ای موسی، [بدان که‌] من تو را به پیام‌ها و کلام [بی‌واسطۀ] خویش، بر مردمان برگزیدم. آنچه به تو می‌بخشم بگیر و سپاسگزار باش»‌.');
INSERT INTO "islamhouse" VALUES(7,145,'7:145','و در الواح [تورات‌] براى او در هر موردى پندى [آوردیم] و براى هر امری شرحی نگاشتیم [و به موسی فرمودیم:] «آن را با جدیّت بگیر و به قومت دستور بده که نیکوترین آنها را فراگیرند [و به آن عمل کنند]؛ و به زودی [جایگاه و] سرای نافرمانان را به شما نشان خواهم داد».');
INSERT INTO "islamhouse" VALUES(7,146,'7:146','به زودی کسانی را که به ناحق در زمین [برتری‌جویی و] تکبر می‌کنند، از [فهمِ] آیات خویش باز خواهم داشت [چنان كه‌] اگر هر نشانه‌اى [از قدرتم‌] را بنگرند، به آن ایمان نیاورند و اگر راهِ حق را ببینند، آن را در پیش نگیرند و اگر راهِ گمراهی را ببینند آن را بر‌گزینند. این [مجازات،] برای آن است که آیات ما را تکذیب کردند و از آنها غافل بودند.');
INSERT INTO "islamhouse" VALUES(7,147,'7:147','و کسانی‌ که آیات ما و دیدار آخرت را تکذیب کردند، کارهایشان تباه گشته است. آیا جز [در برابر] آنچه مى‌كردند مجازات می‌شوند؟');
INSERT INTO "islamhouse" VALUES(7,148,'7:148','و قوم موسی پس از [رفتنِ] او [به کوه طور،] از زیورهایشان مجسمۀ گوساله‌ای را ساختند [و عبادت کردند] که صدایی [همچون صدای گوساله] داشت. آیا نمی‌دیدند که [آن پیکرِ بی‌جان] با آنان سخن نمی‌گوید و آنان را به راهی هدایت نمی‌کند؟ آن را [معبود] گرفتند و [به خاطر این شرک، نسبت به خویش] ستمكار بودند.');
INSERT INTO "islamhouse" VALUES(7,149,'7:149','و چون [از کردۀ خویش] پشیمان شدند و دیدند که به راستی گمراه شده‌اند، گفتند: «اگر پروردگارمان به ما رحم نکند و ما را نیامرزد، مسلّماً زیانکار خواهیم بود».');
INSERT INTO "islamhouse" VALUES(7,150,'7:150','و چون موسی به سوی قومش بازگشت [و آنان را بر شرک و گمراهی یافت،] با خشم و اندوه گفت: «پس از من، چه بد جانشینی کردید! آیا از حکم پروردگارتان پیشی گرفتید؟» و [آنگاه] الواح را انداخت و [ریش و موی] سرِ برادر خود را گرفت و به سوی خود کشید. [هارون] گفت: «ای پسرِ مادرم، به راستی این قوم، مرا ناتوان پنداشتند [و تحقیر کردند] و نزدیک بود مرا بکشند؛ پس دشمنان را با [سرزنش] من ‌شادمان نکن و مرا در زمرۀ گروه ستمکار [و نافرمان] قرار نده».');
INSERT INTO "islamhouse" VALUES(7,151,'7:151','[موسی] گفت: «پروردگارا، من و برادرم را بیامرز و ما را در [پناه‌] رحمت خویش درآور؛ و تو مهربان‌ترین مهربانانی».');
INSERT INTO "islamhouse" VALUES(7,152,'7:152','بی‌تردید، کسانی ‌که گوساله را [معبود] گرفتند، به زودی خشمی از پروردگارشان و ذلّتی در زندگی دنیا به آنان خواهد رسید؛ و ما دروغ‌پردازان را اینچنین کیفر می‌دهیم.');
INSERT INTO "islamhouse" VALUES(7,153,'7:153','و کسانی که مرتکب کارهای ناشایست [= کفر و گناه] شدند، سپس بعد از آن توبه نمودند و ایمان آوردند، بی‌گمان، پروردگارت از آن پس، آمرزندۀ مهربان است‌.');
INSERT INTO "islamhouse" VALUES(7,154,'7:154','و چون خشم موسی فرونشست، الواح [تورات] را برگرفت و در نوشته‌هایش برای کسانی که از پروردگارشان می‌ترسند، هدایت و رحمتی [بزرگ] بود.');
INSERT INTO "islamhouse" VALUES(7,155,'7:155','و موسی از قومش، هفتاد مرد را برای وعده‌گاهِ ما [در طور سینا] برگزید [تا به خاطر شرک و گناه قومشان به درگاه الهی توبه کنند]. هنگامی‌ که [به دلیل نافرمانی و درخواستِ رؤیت الله،] زلزله [شدید] آنان را فراگرفت [و همگی مردند، موسی] گفت: «پروردگارا، اگر می‌خواستی، آنان و مرا پیش از این هلاک می‌کردی. آیا ما را به خاطر آنچه افراد بی‌خبر ما کرده‌اند هلاک می‌کنی؟ این [عذاب، چیزی] نیست، مگر آزمایش تو؛ به [وسیلۀ] آن هر کس را بخواهی، گمراه می‌سازی و هر کس را بخواهی، هدایت می‌کنی. تو [دوست و] کارسازِ مایی؛ پس ما را بیامرز و بر ما رحم آور؛ و تو بهترین آمرزنده‌ای.');
INSERT INTO "islamhouse" VALUES(7,156,'7:156','و در این دنیا و در سرای آخرت، نیکی برایمان مقرر فرما [که] بی‌تردید، ما به سوی تو بازگشتیم». [الله] فرمود: «عذابم را به هر کس بخواهم می‌رسانم و رحمتم همه چیز را فراگرفته است؛ پس آن را برای کسانی‌ مقرر خواهم داشت که پروا پیشه می‌کنند و زکات می‌دهند و [همچنین برای] کسانی ‌که به آیات ما ایمان می‌آورند.');
INSERT INTO "islamhouse" VALUES(7,157,'7:157','همان کسانی که از فرستاده و پیامبرِ درس‌ناخوانده [= اُمّی] پیروی می‌کنند؛ [همان] که نام [و نشانه‌های‌] او را نزد خود در تورات و انجیل نوشته‌شده می‌یابند؛ [همو] که آنان را به نیکی‌ها فرمان می‌دهد و از زشتی‌ها [و کارهای ناپسند] باز می‌دارد و پاکیزه‌ها را برایشان حلال می‌گردانَد و پلیدی‌ها را بر آنان حرام می‌کند و اعمال مشقت‌بار و قید و بندهایی را که بدان مکلف شده بودند از [عهدۀ] آنان برمى‌دارد. پس کسانی‌ که به او ایمان آوردند و گرامی‌اش داشتند و یاری‌اش کردند و از نوری که با او نازل شده است پیروی نمودند، آنانند که رستگارند».');
INSERT INTO "islamhouse" VALUES(7,158,'7:158','بگو: «ای مردم، من فرستادۀ الله به سوی همۀ شما هستم؛ همان [پروردگاری] که فرمانرواییِ آسمان‌ها و زمین از آنِ اوست؛ معبودی [به‌حق] جز او نیست؛ زنده می‌کند و می‌میراند؛ پس به الله و فرستاده‌اش ـ‌آن پیامبرِ درس‌ناخوانده‌ای که به الله و سخنانش باور داردـ ایمان بیاورید و از او پیروی کنید؛ باشد که هدایت شوید».');
INSERT INTO "islamhouse" VALUES(7,159,'7:159','و گروهی از قوم موسی هستند که [مردم را] به [راه] حق هدایت می‌کنند و بر اساس آن [نیز قضاوت و] عدالت می‌کنند.');
INSERT INTO "islamhouse" VALUES(7,160,'7:160','و آنان را به دوازده قبیله [که هر کدام] شاخه‌ای از نوادگان [یعقوب] بودند تقسیم کردیم؛ و به موسی ـ آنگاه که قومش از او آب خواستند ـ وحی کردیم که: «عصایت را بر سنگ بزن»؛ به‌ناگاه دوازده چشمه از آن جوشید [و] هر گروهی آبشخورِ خود را شناخت؛ و ابر را بر [سرِ] آنان سایه‌گستر نمودیم و تَرَنجَبین و بلدرچین بر آنان فروفرستادیم [و گفتیم:] «از چیزهای پاکیزه‌ای که روزی‌تان کرده‌ایم بخورید». آنان [با نافرمانی‌ و مخالفت، در حقیقت] به ما ستم نکردند؛ بلکه به خویشتن ستم می‌نمودند.');
INSERT INTO "islamhouse" VALUES(7,161,'7:161','و [یاد کن از] هنگامی که به آنان گفته شد: «در این شهر [= بیت‌المقدس] ساکن شوید و [از نعمت‌های] آن ـ از هر جا [و هر وقت] که - خواستید- بخورید و [برای عذرخواهی‌] بگویید: «[پروردگارا،] گناهانمان را فرو ریز» و با فروتنی از دروازه[ی شهر] وارد شوید تا گناهانتان را بیامرزیم؛ [و پاداش دنیا و آخرتِ] نیکوکاران را خواهیم افزود».');
INSERT INTO "islamhouse" VALUES(7,162,'7:162','اما افرادی از آنها که [نافرمان و] ستمکار بودند، سخن را به چیزی غیر از آنچه كه به آنان گفته شده بود تبدیل کردند [و به جای طلب آمرزش، کلمات تمسخرآمیزی گفتند]؛ پس به [کیفرِ] آنکه ‌ستم می‌کردند، عذابی از آسمان بر آنان فروفرستادیم.');
INSERT INTO "islamhouse" VALUES(7,163,'7:163','و [ای پیامبر،] از آنان دربارۀ [ساکنان] شهری که نزدیک دریا بود بپرس؛ هنگامی ‌که آنان از [حکمِ تحریمِ صید در روز] شنبه تجاوز می‌کردند، و ماهی‌هایشان در روزهای شنبه [که روز تعطیل و عبادت بود] روی آب ظاهر می‌شدند و روزهای غیرشنبه به سوی آنان نمی‌آمد. آنان را به [خاطر] آنکه نافرمانی می‌کردند، اینچنین آزمودیم.');
INSERT INTO "islamhouse" VALUES(7,164,'7:164','و [یاد کن از] هنگامی که گروهی از آنان [به گروه دیگر] گفتند: «چرا قومی را پند می‌دهید [که سرانجام،] الله آنان را هلاک خواهد کرد یا به عذابی شدید گرفتار خواهد نمود؟» [ناصحان] گفتند: «این [پند و نهی، در حُکمِ‌] معذرتی به درگاه پروردگار شماست و باشد که پروا کنند».');
INSERT INTO "islamhouse" VALUES(7,165,'7:165','اما چون آنچه را به آنان پند داده بودند به فراموشی سپردند، كسانى را كه از [كارِ] بد بازمى‌داشتند نجات دادیم و كسانى را كه [با نادیده گرفتن حکمِ الهی به خود] ستم کرده بودند ـ به سزاى آنكه نافرمانى مى‌كردند ـ به عذابى شدید گرفتار نمودیم.');
INSERT INTO "islamhouse" VALUES(7,166,'7:166','و چون در برابر آنچه از آن نهى شده بودند [لجاجت و] سرپیچى كردند، به آنان گفتیم: «به صورت بوزینه‌هایی [خوار و] رانده‌شده درآیید».');
INSERT INTO "islamhouse" VALUES(7,167,'7:167','و [یاد کن از] هنگامی ‌که پروردگارت فرمود یقیناً تا روز قیامت، کسی را بر آنان [=یهود] خواهد گماشت که همواره بدترین [و خفت‌بارترین] عذاب را بر سرشان ‌بیاورد. بی‌گمان، پروردگارت زودکیفر است، و[لی] او تعالی قطعاً آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(7,168,'7:168','و آنان را در زمین به صورت گروه‌هایی [مختلف] پراکنده ساختیم. [برخی] از آنان نیکوکار اند و [برخی میانه‌رو هستند و گروهی نیز] غیر از اینند؛ و آنان را با خوشی‌ها و ناخوشی‌ها آزمودیم؛ باشد که [به راه حق‌] بازگردند.');
INSERT INTO "islamhouse" VALUES(7,169,'7:169','و پس از آنان، جانشینان [ناشایستی‌] آمدند که کتاب [تورات] را به ارث بردند. آنان [ثروت و] کالای این [دنیای] پست را می‌گیرند [که سخنان الله را تحریف و به ناحق حکم کنند] و می‌گویند: «بخشیده خواهیم شد»؛ و اگر کالایی [دیگر] همانند آن برایشان بیاید، آن را [نیز] می‌گیرند. آیا در کتاب [آسمانی] از آنان پیمان گرفته نشده است که دربارۀ الله جز [سخن] حق نگویند؟ [پس نافرمانی‌شان از روی نادانی نیست؛ زیرا بارها تورات را خوانده‌اند] و آنچه را که در آن است آموخته‌اند؛ و سرای آخرت برای کسانی که پروا پیشه کنند بهتر است. آیا نمی‌اندیشید؟');
INSERT INTO "islamhouse" VALUES(7,170,'7:170','و کسانی که به کتاب [الله] تمسّک می‌جویند و نماز را برپا می‌دارند، به یقین [بدانند که] ما پاداش نیکوکاران را تباه نمی‌کنیم.');
INSERT INTO "islamhouse" VALUES(7,171,'7:171','و [یاد کن از] هنگامی ‌که کوه [طور] را [از جای برکندیم و] همچون سایبانی بر فرازشان بر‌افراشتیم و پنداشتند که بر آنان فرود می‌آید [و در این حالت، از آنان عهد گرفتیم و گفتیم:] «آنچه را که به شما داده‌ایم، با نیرو [و جدیّت] بگیرید و آنچه را که در آن است به یاد داشته باشید [و به آن عمل نمایید]؛ باشد که پروا کنید.');
INSERT INTO "islamhouse" VALUES(7,172,'7:172','و [یاد کن از] هنگامی ‌که پروردگارت از پشت فرزندان آدم، نسلشان را برگرفت و آنان را بر خودشان گواه گرفت [و فرمود:] «آیا من پروردگار شما نیستم؟» آنان گفتند: «آری؛ [بر این حقیقت] گواهی می‌دهیم». [الله فرمود: «از شما اقرار گرفتم] تا مبادا روز قیامت بگویید که "ما قطعاً از این [امر] بی‌خبر بودیم".');
INSERT INTO "islamhouse" VALUES(7,173,'7:173','یا [مبادا] بگویید که پدرانمان پیشتر شرک آورده بودند و ما نیز فرزندانی پس از آنان بودیم. آیا ما را به [سزای] آنچه [کافران و] باطل‌اندیشان انجام دادند، هلاک می‌کنی؟»');
INSERT INTO "islamhouse" VALUES(7,174,'7:174','و اینچنین آیات [خود] را به تفصیل بیان می‌کنیم [تا آنها را بفهمند] و باشد که [به توحید] بازگردند.');
INSERT INTO "islamhouse" VALUES(7,175,'7:175','و [ای پیامبر،] سرگذشت کسی را بر آنان حکایت کن که آیات [و نشانه‌های] خویش را به او دادیم و [او حقانیت این آیات را درک کرد؛ ولی] از آنها جدا [و عاری] گشت؛ پس شیطان به دنبالش افتاد [و بر وی مسلط گردید] و او از گمراهان شد.');
INSERT INTO "islamhouse" VALUES(7,176,'7:176','و اگر می‌خواستیم، [مقام و منزلت] او را به خاطر [پایبندی و عمل به] آن [آیات] بالا می‌بردیم؛ ولی او به دنیا [و پستی‌] گرایید و از هوای نفس خویش پیروی کرد. پس مَثَل او [در دنیادوستی] همچون مَثل سگ است که اگر به او حمله کنی [و او را برانی]، زبان از دهان بیرون می‌آورد و اگر او را [به حال خود] واگذاری، [باز هم] زبان از دهان بیرون می‌آورد [چنین شخصی را نیز چه پند بدهی یا پند ندهی، تفاوتی ندارد]. این، مَثَلِ گروهی است که آیات ما را تکذیب کردند. پس این داستان را [برایشان‌] حكایت كن؛ باشد كه بیندیشند.');
INSERT INTO "islamhouse" VALUES(7,177,'7:177','چه زشت است داستان گروهى كه آیات ما را تکذیب کردند و به خویش ستم ‌نمودند!');
INSERT INTO "islamhouse" VALUES(7,178,'7:178','کسی که الله او را هدایت کند، هدایت‌یافته [واقعی] است؛ و کسی که الله او را گمراه سازد، آنانند که زیانکارند.');
INSERT INTO "islamhouse" VALUES(7,179,'7:179','و بی‌تردید، بسیارى از جنّیان و آدمیان را براى دوزخ آفریده‌ایم؛ [زیرا‌] دل‌هایی دارند كه با آنها [حقایق را] درک نمى‌كنند و چشمانى دارند كه با آنها [نشانه‌های توان و تدبیر الهی را] نمى‌بینند و گوش‌هایی دارند كه با آنها [سخنان حق را] نمى‌شنوند. آنان همچون چهارپایانند، بلكه [از آنها هم] گمراه‌تر. [آرى،] آنانند که [از آخرت] غافلند.');
INSERT INTO "islamhouse" VALUES(7,180,'7:180','و الله زیباترین نام‌ها را دارد؛ پس او را به آن [نام‌ها] بخوانید و کسانی را که در [مورد] نام‌هایش بيراهه می‌روند [و آنها را تحریف می‌کنند] رها کنید. آنان به [سزاى‌] آنچه مى‌کردند، مجازات خواهند شد.');
INSERT INTO "islamhouse" VALUES(7,181,'7:181','و از [میان] کسانی که آفریده‌ایم، گروهی هستند که [مردم را] به حق هدایت می‌نمایند و بر [اساسِ] آن، به عدالت رفتار می‌کنند.');
INSERT INTO "islamhouse" VALUES(7,182,'7:182','و کسانی ‌که آیات ما را تکذیب کردند، [درهای روزی را به رویشان می‌گشاییم تا بیشتر در گناه غرق شوند، و این‌گونه] آنان را به تدریج از جایى كه نمى‌دانند [به كیفر خود] گرفتار خواهیم ساخت.');
INSERT INTO "islamhouse" VALUES(7,183,'7:183','و به آنان مهلت می‌دهم. به راستی که تدبیرِ من، استوار [و حساب‌شده] است.');
INSERT INTO "islamhouse" VALUES(7,184,'7:184','آیا نیندیشیده‌اند که همنشینِ آنان [= رسول الله] هیچ جنونی ندارد؟ او جز بیم‌دهنده‌ای آشکار نیست.');
INSERT INTO "islamhouse" VALUES(7,185,'7:185','آیا در فرمانرواییِ آسمان‌ها و زمین و آنچه الله آفریده است نمی‌نگرند و اینکه چه بسا هنگام مرگشان نزدیک شده باشد؟ پس بعد از این [قرآن] به کدام سخن ایمان می‌آورند؟');
INSERT INTO "islamhouse" VALUES(7,186,'7:186','هر‌ کس که الله او را گمراه کند، [هیچ] راهنمایی برایش نیست و آنان را در سرکشیِ خویش سرگردان رها می‌کند.');
INSERT INTO "islamhouse" VALUES(7,187,'7:187','[ای پیامبر، کافران مکه] دربارۀ قیامت از تو می‌پرسند [که] چه زمانی واقع می‌شود؟ بگو: «بی‌تردید، دانش آن فقط نزد پروردگار من است و هیچ‌ کس جز او تعالی [نمی‌تواند] آن را در وقت معیّنش پدیدار سازد؛ این امر [= ظهور قیامت] بر اهل آسمان‌ها و زمین دشوار است و جز به طور ناگهانی به سویتان نمی‌آید». [آنان چنان] از تو می‌پرسند که گویی تو خود مشتاق [دانستن زمان] آن هستی [حال آنکه تو دربارۀ زمان وقوعش هیچ نمی‌پرسی]. بگو: «دانش آن فقط نزد الله است؛ ولی بیشتر مردم نمی‌دانند».');
INSERT INTO "islamhouse" VALUES(7,188,'7:188','[ای پیامبر،] بگو: «من مالکِ سود و زیان خویشتن نیستم، مگر آنچه را که الله بخواهد؛ و اگر غیب می‌دانستم، خیر [و سود] بسیاری [برای خود] فراهم می‌ساختم و هیچ بدی [و زیانی] به من نمی‌رسید. من [کسی] نیستم، مگر بیم‌دهنده و بشارت‌بخشی [دلسوز] برای گروهی که ایمان دارند.');
INSERT INTO "islamhouse" VALUES(7,189,'7:189','[ای مردم،] او ذاتی است که شما را از یک تن آفرید و همسرش را از او پدید آورد تا در کنارش آرام گیرد. پس [نظام تولد و تناسل آغاز شد؛] چون شوهری با همسرش آمیزش نمود، زن به حملی سبک باردار شد، و [نخستین ماه‌های بارداری‌ را] گذرانید؛ و چون سنگین‌بار شد، زن و شوهر، الله ـ پروردگار خود- را [به دعا] ‌خواندند كه: «اگر [فرزندى تندرست و‌] شایسته به ما عطا كنى، قطعاً سپاسگزار خواهیم بود».');
INSERT INTO "islamhouse" VALUES(7,190,'7:190','اما هنگامی که [الله، فرزندى تندرست و‌] شایسته به آنان داد، در این نعمت که به آنها بخشیده بود، شریکانی برایش قرار دادند؛ و الله از آنچه که شریک [و همتای] او می‌سازند برتر و منزه است.');
INSERT INTO "islamhouse" VALUES(7,191,'7:191','آیا چیزهایی را شریک [الله] قرار می‌دهند که چیزی نمی‌آفرینند و خود آفریده می‌شوند؟');
INSERT INTO "islamhouse" VALUES(7,192,'7:192','و نه می‌توانند آنان را یاری کنند و نه به خویشتن یاری می‌رسانند.');
INSERT INTO "islamhouse" VALUES(7,193,'7:193','و [ای مشرکان،] اگر آنها [= معبودها] را به [راه] هدایت دعوت کنید، از شما پیروی نمی‌کنند. چه آنها را دعوت کنید و چه خاموش باشید، برایتان یکسان است [و پاسخی نخواهید شنید].');
INSERT INTO "islamhouse" VALUES(7,194,'7:194','بی‌گمان، آنهایی که به جای الله [به یاری و دعا] می‌خوانید، بندگان[و آفریدگان]ی همچون خودتان هستند؛ پس آنها را [در سختی‌ها به یاری و دعا] بخوانید. اگر راست مى‌گویید، باید پاسخ شما را بدهند.');
INSERT INTO "islamhouse" VALUES(7,195,'7:195','آیا آنها پاهایی دارند که با آنها راه بروند یا دست‌هایی دارند که با آنها [یاری‌تان دهند و از شما] دفاع کنند؟ آیا چشمانی دارند که با آنها ببینند یا گوش‌هایی دارند که با آنها بشنوند؟ [ای پیامبر، به مشرکان] بگو: «شریکانتان را بخوانید، سپس [علیه من] تدبیر [و نیرنگ] کنید و به من مهلت ندهید.');
INSERT INTO "islamhouse" VALUES(7,196,'7:196','بی‌تردید، [دوست و] کارسازِ من الله است که [این] کتاب را نازل کرده است و او برای شایستگان [دوستی و] کارسازی می‌کند.');
INSERT INTO "islamhouse" VALUES(7,197,'7:197','و کسانی را که [شما مشرکان] به جای او [به یاری و دعا] می‌خوانید، نه می‌توانند شما را یاری کنند و نه به خویشتن یاری رسانند.');
INSERT INTO "islamhouse" VALUES(7,198,'7:198','و اگر آنان را به [راه] هدایت دعوت کنید، [سخنانتان را] نمی‌شنوند؛ و آنان را می‌بینی که [ظاهراً] به تو نگاه می‌کنند، در حالی ‌که نمی‌بینند».');
INSERT INTO "islamhouse" VALUES(7,199,'7:199','[ای پیامبر، با آنان] مدارا کن و به نیکی فرمان بده و از نادانان روی بگردان.');
INSERT INTO "islamhouse" VALUES(7,200,'7:200','و اگر وسوسه‌ای از سوی شیطان به تو رسید، به الله پناه ببر [که] بی‌تردید، او شنوای داناست.');
INSERT INTO "islamhouse" VALUES(7,201,'7:201','در حقیقت، كسانى كه [از الله] پروا می‌کنند، چون وسوسه‌اى از جانب شیطان به آنان برسد، [الله را] به یاد می‌آورند و آنگاه [نسبت به حقیقت] بینا می‌شوند.');
INSERT INTO "islamhouse" VALUES(7,202,'7:202','و برادرانشان [= برادران كافران و مشركان که شیاطین هستند] آنان را به گمراهی می‌کشانند و [هرگز در این کار] کوتاهی نمی‌کنند.');
INSERT INTO "islamhouse" VALUES(7,203,'7:203','و [ای پیامبر،] هنگامی که [نزول وحی به تأخیر می‌افتد و] تو آیه‌ای برایشان نمی‌آوری، می‌گویند: «چرا تو خود آن را نمی‌سازی؟». بگو: «من فقط از چیزی پیروی می‌کنم که از سوی پروردگارم به من وحی می‌شود. این [قرآن، شامل] روشنگری‌هایی از سوی پروردگارتان است و برای گروهی که ایمان می‌آورند، هدایت و رحمت است.');
INSERT INTO "islamhouse" VALUES(7,204,'7:204','و هنگامی که قرآن خوانده می‌شود، به آن گوش فرادهید و خاموش باشید؛ باشد که مشمول رحمت شوید».');
INSERT INTO "islamhouse" VALUES(7,205,'7:205','و پروردگارت را در دل خویش با تضرّع و ترس، بدون آواى بلند [در] بامدادان و شامگاهان یاد كن و [از یاد الله] غافل نباش.');
INSERT INTO "islamhouse" VALUES(7,206,'7:206','[ای پیامبر،] كسانى كه نزد پروردگار تو هستند [= فرشتگان] یقیناً از عبادت وی [سرکشی و] تكبر نمى‌ورزند و او را به پاكى مى‌ستایند و برایش سجده مى‌كنند.');
INSERT INTO "islamhouse" VALUES(8,1,'8:1','[ای پیامبر،] از تو دربارۀ [تقسیمِ] انفال [= غنایم جنگی] می‌پرسند؛ بگو: «انفال از آنِ الله و پیامبر است؛ پس از الله پروا کنید و با یكدیگر سازش نمایید و اگر ایمان دارید، از الله و پیامبرش اطاعت کنید.');
INSERT INTO "islamhouse" VALUES(8,2,'8:2','بی‌تردید، مؤمنان فقط کسانی هستند که وقتی الله یاد می‌شود، دل‌هایشان هراسان می‌گردد و هرگاه آیاتش بر آنان خوانده می‌شود، بر ایمانشان می‌افزاید و بر پروردگارشان توکل می‌کنند.');
INSERT INTO "islamhouse" VALUES(8,3,'8:3','همان کسانی‌ که نماز برپا می‌دارند و از آنچه روزی‌شان کرده‌ایم انفاق می‌کنند.');
INSERT INTO "islamhouse" VALUES(8,4,'8:4','آنان هستند كه به راستی مؤمنند. براى آنان نزد پروردگارشان، درجاتی [والا] و آمرزش و روزىِ نیكو خواهد بود.');
INSERT INTO "islamhouse" VALUES(8,5,'8:5','[ای پیامبر، اختیار کاملِ تو بر غنایم، موجب ناخشنودی برخی افراد است؛ درست] همان‌ طور که پروردگارت تو را به‌حق از خانه‌ات [در مدینه به سوی میدان بدر] بیرون آورد و قطعاً گروهی از مؤمنان [از جنگیدن] ناخشنود بودند.');
INSERT INTO "islamhouse" VALUES(8,6,'8:6','آنان پس از آنکه حقیقت آشکار شد، دربارۀ حق [= جهاد] با تو مجادله می‌کنند؛ گویى آنان را به سوى مرگ مى‌رانند در حالی که خود [به مُردنشان‌] مى‌نگرند.');
INSERT INTO "islamhouse" VALUES(8,7,'8:7','و [ای مجاهدان، به یاد آورید] هنگامی که الله به شما وعده داد که [غنایمِ] یکی از دو گروه [= کاروان تجاری یا لشکر] نصیبتان خواهد شد و شما دوست ‌داشتید که کاروانِ غیرنظامی [= تجاری] برای شما باشد؛ و[لی] الله می‌خواست با سخنان خویش [دربارۀ کشتار کفار]، حق [= اسلام] را پایدار گردانَد و ریشۀ کافران را قطع کند.');
INSERT INTO "islamhouse" VALUES(8,8,'8:8','[الله اسلام را پیروز کرد] تا حق را ثابت نماید و باطل را از میان بردارد؛ هر چند که گناهکاران [مشرک] نا‌خشنود باشند.');
INSERT INTO "islamhouse" VALUES(8,9,'8:9','[روز بدر را به یاد آورید؛] آنگاه كه از پروردگارتان فریادرسى [و یارى‌] مى‌خواستید و او [درخواستِ] شما را پذیرفت [و فرمود:] «بی‌گمان، شما را با هزار فرشته که پیاپی فرود می‌آیند یاری می‌کنم».');
INSERT INTO "islamhouse" VALUES(8,10,'8:10','و الله این [یاری] را جز مژده‌ای [برای شما] قرار نداد تا دل‌هایتان با آن آرام گیرد؛ و [بدانید که] پیروزی جز از جانب الله نیست. به راستی که الله شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(8,11,'8:11','[و به یاد آورید] هنگامی که [الله آن] خواب سبک را که [امنیت و] آرامشی از سوی او تعالی بود، بر شما افکند و آبی از آسمان برایتان فروفرستاد تا شما را با آن پاک کند و پلیدی [= وسوسه‌های] شیطان را از شما دور سازد و دل‌هایتان را محکم بدارد و گام‌هایتان را با آن استوار نماید.');
INSERT INTO "islamhouse" VALUES(8,12,'8:12','[و ای پیامبر، به یاد آور] هنگامی‌ که پروردگارت به فرشتگان وحی کرد: «من با شما هستم؛ پس کسانی را که ایمان آورده‌اند، ثابت‌قدم دارید. به زودی در دل کسانی که کفر ورزیدند، هراس می‌افکنم؛ پس [ای مجاهدان، شمشیر خویش را] بر فرازِ گردن‌هایشان بزنید و همۀ انگشتانشان را قطع کنید [تا سلاح برنگیرند].');
INSERT INTO "islamhouse" VALUES(8,13,'8:13','این [کیفر] به خاطر آن است که آنان با الله و رسولش مخالفت کردند؛ و هر کس با الله و رسولش مخالفت کند، [بداند که] الله سخت‌کیفر است.');
INSERT INTO "islamhouse" VALUES(8,14,'8:14','[ای کافران،] این [عذاب دنیا] را بچشید و [بدانید که] برای کافران [در قیامت،] عذاب آتش است.');
INSERT INTO "islamhouse" VALUES(8,15,'8:15','اى كسانى كه ایمان آورده‌اید، هر گاه [در میدان جنگ] با انبوهِ كافران روبرو شدید، به آنان پشت نكنید [و نگریزید].');
INSERT INTO "islamhouse" VALUES(8,16,'8:16','و هر کس در آن هنگام به آنان پشت كند ـ مگر آنكه [انگیزه‌اش فریب دشمن یا] كناره‌گیرى براى نبردى [دوباره] یا پیوستن به گروهی [دیگر از سپاه مسلمانان‌] باشد ـ قطعاً به خشم الله گرفتار می‌گردد و جایگاهش دوزخ است؛ و چه بد سرانجامى است!');
INSERT INTO "islamhouse" VALUES(8,17,'8:17','پس [ای مؤمنان، در روز نبرد با مشرکان در بدر] شما [با نیروی خود] آنان را نکشتید؛ بلکه الله آنان را کشت؛ و [ای پیامبر،] هنگامی که [به سویشان خاک و سنگ] می‌انداختی، [در حقیقت،] تو نینداختی؛ بلکه الله انداخت تا مؤمنان را به آزمایشی نیکو از جانب خود بیازماید. بی‌تردید، الله شنوای داناست.');
INSERT INTO "islamhouse" VALUES(8,18,'8:18','این [شکست و فرارِ مشرکان و سرافرازیِ مؤمنان، همه از جانب الله] بود و الله نیرنگِ كافران را سست [و بی‌اثر] مى‌گردانَد.');
INSERT INTO "islamhouse" VALUES(8,19,'8:19','[اى مشركان،‌] اگر خواهان پیروزی [بر مسلمانان و فرارسیدنِ عذاب الهی هستید]، اینک پیروزى [اسلام، که بزرگ‌ترین رنج و عذاب شماست] به سراغتان آمد؛ و اگر [از کفر و دشمنى با پیامبر‌] دست بردارید، آن [کار] برایتان بهتر است؛ و[لی] اگر [به جنگ با مسلمانان‌] برگردید، ما نیز برمى‌گردیم؛ و [این را بدانید] كه گروه شما ـ هر چند زیاد باشدـ هرگز نمی‌تواند شما را از چیزی بی‌نیاز کند و [دیگر] اینكه [همواره] الله با مؤمنان است.');
INSERT INTO "islamhouse" VALUES(8,20,'8:20','ای کسانی که ایمان آورده‌اید، از الله و رسولش اطاعت کنید و از [احکام و فرمان‌های] او روی نگردانید؛ در حالی که [آیات قرآن و سخنان پیامبر را] می‌شنوید.');
INSERT INTO "islamhouse" VALUES(8,21,'8:21','و مانند کسانی نباشید که گفتند: «[آیات الهی و سخن رسولان را] شنیدیم»؛ حال آنکه نمی‌شنیدند.');
INSERT INTO "islamhouse" VALUES(8,22,'8:22','بی‌گمان، بدترین جنبندگان نزد الله، آن [کسانی هستند که در برابر حق] كر و گُنگ هستند [و حقا] که نمی‌اندیشند.');
INSERT INTO "islamhouse" VALUES(8,23,'8:23','و اگر الله خیری در آنان سراغ داشت، [در برابر کلام حق] شنوایشان می‌نمود؛ و[لی حتی] اگر آنان را شنوا می‌ساخت، باز هم پشت می‌کردند و رویگردان می‌شدند.');
INSERT INTO "islamhouse" VALUES(8,24,'8:24','اى كسانى كه ایمان آورده‌اید، هر گاه الله و پیامبر، شما را به چیزى فراخواندند كه به شما زندگی مى‌بخشد، [فرمان] آنان را اجابت كنید و بدانید كه الله میان انسان و دلش حایل می‌شود [و از اسرار آگاه است] و اینکه [در قیامت، همگی] نزد او گِرد آورده می‌شوید.');
INSERT INTO "islamhouse" VALUES(8,25,'8:25','و [ای مؤمنان،] از عذابی بپرهیزید كه تنها دامن ستمکارانتان را نمی‌گیرد؛ و بدانید که الله سخت‌کیفر است.');
INSERT INTO "islamhouse" VALUES(8,26,'8:26','و به یاد آورید زمانی را که شما در سرزمین [مکه] اندک و ناتوان بودید [و] می‌ترسیدید که مردمان [مشرک،] شما را بربایند؛ ولی الله شما را [در مدینه] پناه داد و با یاری خویش نیرومندتان ساخت و از چیزهای پاکیزه به شما روزی بخشید؛ باشد که سپاسگزاری کنید.');
INSERT INTO "islamhouse" VALUES(8,27,'8:27','ای کسانی که ایمان آورده‌اید، [با سرپیچی از اوامر و نَواهی،] به الله و پیامبر خیانت نکنید و [همچنین] در امانت‌هایتان خیانت نکنید، در حالی ‌که می‌دانید [این کار، گناه است].');
INSERT INTO "islamhouse" VALUES(8,28,'8:28','و بدانید که بی‌تردید، اموال و فرزندانتان [وسیلۀ] آزمایش [شما] هستند و پاداشی بزرگ نزد الله است.');
INSERT INTO "islamhouse" VALUES(8,29,'8:29','ای کسانی ‌که ایمان آورده‌اید، اگر از الله پروا کنید، براى شما [نیروى‌] تشخیص [حق و باطل‌] قرار مى‌دهد و گناهانتان را از شما مى‌زداید و شما را مى‌آمرزد؛ و الله داراى بخشش بزرگ است.');
INSERT INTO "islamhouse" VALUES(8,30,'8:30','و [ای پیامبر، به یاد آور] آنگاه که کافران درباره تو دسیسه می‌کردند که تو را به زندان بیفکنند یا بکشند یا تو را [از مکه] بیرون کنند. آنان مکر می‌اندیشیدند و الله [نیز تدبیر و] مکر می‌کرد؛ و الله بهترینِ مکرانگیزان است.');
INSERT INTO "islamhouse" VALUES(8,31,'8:31','و چون آیات ما بر آنان خوانده شود، می‌گویند: «به خوبى شنیدیم. اگر مى‌خواستیم، ما نیز قطعاً همانند این [سخنان] را مى‌گفتیم. این [قرآن، چیزی] نیست، مگر افسانه‌هاى [خیالی] پیشینیان».');
INSERT INTO "islamhouse" VALUES(8,32,'8:32','و [به یاد آور] آنگاه که گفتند: «پروردگارا، اگر این [قرآن] به راستی، حق و از جانب توست، سنگ‌هایی [ویرانگر] از آسمان بر ما ببار یا عذابی دردناک بر ما بفرست».');
INSERT INTO "islamhouse" VALUES(8,33,'8:33','و[لى‌ ای محمد،] تا تو در میان آنان هستى، الله بر آن نیست كه عذابشان كند و تا زمانی که [از درگاه الهی] آمرزش می‌طلبند، الله عذاب‌كنندۀ آنان نیست.');
INSERT INTO "islamhouse" VALUES(8,34,'8:34','و چرا الله آنان را [در آخرت‌] عذاب نكند، با اینكه آنان [مردم را] از [طواف و عبادت در] مسجدالحرام بازمى‌دارند؟ و [مشرکان] هرگز دوستان الله نبوده‌اند؛ [زیرا] دوستان الله کسی جز پرهیزگاران نیستند؛ ولى بیشتر آنان نمى‌دانند.');
INSERT INTO "islamhouse" VALUES(8,35,'8:35','و نمازشان نزد خانه [کعبه] چیزی جز سوت‌کشیدن و کف‌زدن نبود؛ پس به سزای آنکه کفر می‌ورزیدید، عذاب [شکست و اسارت در بدر] را بچشید.');
INSERT INTO "islamhouse" VALUES(8,36,'8:36','کسانی‌ که کفر ورزیدند، اموالشان را خرج می‌کنند تا [مردم را] از راه الله بازدارند؛ پس به زودی همه را خرج می‌کنند، آنگاه [این کار، مایۀ] حسرت و پیشمانی‌شان می‌گردد و سپس شکست می‌خورند؛ و [سرانجام،] کسانی ‌که کفر ورزیدند، به سوی [آتش] دوزخ کشانده می‌شوند.');
INSERT INTO "islamhouse" VALUES(8,37,'8:37','تا الله، پلید را از پاکیزه جدا کند و پلیدان را روى يكديگر نهد و همگی را متراكم سازد، سپس [یکجا] در دوزخ قرار دهد. اینان همان زیانکاران [واقعی] هستند.');
INSERT INTO "islamhouse" VALUES(8,38,'8:38','[ای پیامبر،] به کسانی‌ که کفر ورزیدند بگو [که] اگر [از شرک و دشمنی] دست بردارند، گذشته‌هایشان بخشوده می‌شود و[لی] اگر [به رفتار گذشتۀ خود] بازگردند، سنت [و عذاب الهى كه در مورد] پیشینیان گذشت [تكرار خواهد شد].');
INSERT INTO "islamhouse" VALUES(8,39,'8:39','و [ای مؤمنان،] با آنان پیکار کنید تا فتنه [ی شرک] ریشه‌کن شود و [عبادت و] دین، همه از آنِ الله گردد. پس اگر آنان [از شرک و آزار] دست برداشتند، [رهایشان کنید؛ چرا که] بی‌تردید، الله به آنچه می‌کنند بیناست.');
INSERT INTO "islamhouse" VALUES(8,40,'8:40','و اگر [از اوامر الهی] سرپیچی کردند، بدانید که الله [دوست و] کارسازِ شماست. چه نیکو کارساز و چه نیک یاوری است!');
INSERT INTO "islamhouse" VALUES(8,41,'8:41','و بدانید هر غنیمتی که به دست آوردید، یک‌پنجم آن برای الله و پیامبر و برای خویشاوندان [پیامبر، از بنی‌هاشم و بنی‌مطّلب] و یتیمان [نابالغ] و مستمندان و درراه‌ماندگان است. اگر به الله و آنچه که بر بندۀ خود در روز جدایی [حق از باطل]، روز برخورد دو گروه [مؤمنان و مشرکان] نازل کردیم، ایمان دارید [به این احکام عمل کنید] و [بدانید که] الله بر هر چیز تواناست.');
INSERT INTO "islamhouse" VALUES(8,42,'8:42','آنگاه كه شما [مؤمنان] بر دامنۀ نزدیک‌تر [دره، به سمت مدینه] بودید و آنان در دامنۀ دورتر [دره، به سمت مکه] قرار داشتند و کاروان [تجاری قریش‌] پایین‌تر از [میدان جنگ و دور از تیررسِ‌] شما بود و اگر [شما و مشرکان] با یكدیگر [در مورد محل درگیری] وعده گذاشته بودید، قطعاً در وعده‌گاهِ خود اختلاف مى‌كردید؛ ولى [به ارادۀ الهی در بدر جمع شدید] تا الله كارى را كه قطعاً [باید] به وقوع می‌پیوست انجام دهد تا هر [کافری] كه هلاک می‌شود [با وجود برتری نظامی و عددی، در شرک و گمراهى] هلاک گردد و هر [مؤمنی] که زنده می‌ماند [به هدایت] زندگى ‌یابد [و این مرگ و زندگی] بر اساس دلیلی روشن باشد؛ و به راستی که الله شنوای داناست‌.');
INSERT INTO "islamhouse" VALUES(8,43,'8:43','[به یاد آور] آنگاه که الله [تعداد] آنان را در خوابت به تو اندک نشان داد؛ و اگر آنان را بسیار نشان می‌داد، مسلماً سست [و بددل] می‌شدید و در کار [جنگ] اختلاف می‌کردید؛ ولی الله [شما را از سستی و اختلاف] در امان نگاه داشت. بی‌گمان، او تعالی به آنچه در سینه‌هاست، داناست.');
INSERT INTO "islamhouse" VALUES(8,44,'8:44','و [به یاد آور] هنگامی که با هم روبرو شدید، آنان را به چشم شما کم‌تعداد نمود و شما را [نیز] به چشم آنان اندک جلوه داد، تا الله كارى را كه قطعاً [باید] به وقوع می‌پیوست انجام دهد؛ و [بدانید که همۀ] کارها به الله بازگردانده می‌شود.');
INSERT INTO "islamhouse" VALUES(8,45,'8:45','اى كسانى كه ایمان آورده‌اید، هنگامی ‌که با گروهی [از دشمنان] روبرو شدید، پایداری نمایید و الله را بسیار یاد کنید؛ باشد که رستگار شوید.');
INSERT INTO "islamhouse" VALUES(8,46,'8:46','و از الله و پیامبرش اطاعت کنید و [در تصمیم‌گیری] با یکدیگر کشمکش نکنید که سست می‌شوید و [توان و] اقتدارتان از بین می‌رود؛ و شکیبایی کنید که بی‌گمان، الله با شکیبایان است.');
INSERT INTO "islamhouse" VALUES(8,47,'8:47','و مانند کسانی نباشید که با سرکشی و خودنمایی در برابر مردم، از سرزمین خود [= مکه] خارج شدند و [مردم را] از راه الله باز‌داشتند؛ و الله به آنچه می‌کنند احاطه دارد.');
INSERT INTO "islamhouse" VALUES(8,48,'8:48','و [ای مؤمنان، به یاد آورید] هنگامی که شیطان، اعمال آنان را در نظرشان آراست و گفت: «امروز هیچ کس بر شما پیروز نمی‌شود و من [یاور و] پناهتان هستم»؛ اما هنگامی ‌که دو گروه [با هم] روبرو شدند، [شیطان] به عقب بازگشت و گفت: «به راستی که من از شما بیزارم و چیزی [= فرشتگان یاریگر] را می‌بینم که شما نمی‌بینید. من از الله می‌ترسم و [از هلاکت بیم دارم؛ چرا که] الله سخت‌کیفر است».');
INSERT INTO "islamhouse" VALUES(8,49,'8:49','و [به یاد آورید] هنگامی که منافقان و كسانى كه در دل‌هایشان بیمارى بود می‌گفتند: «اینان [= مؤمنان] را دینشان فریفته است»؛ و هر ‌کس بر الله توکل کند، پس [آسوده‌خاطر باشد؛ چرا که] بی‌تردید، الله شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(8,50,'8:50','و اگر مى‌دیدى آنگاه كه فرشتگان جان كافران را مى‌ستانند، بر صورت و پشتشان مى‌زنند و [می‌گویند:] «عذاب سوزان را بچشید.');
INSERT INTO "islamhouse" VALUES(8,51,'8:51','این [جان دادنِ خفت‌بار و عذاب دردناک] در مقابل کارهایی است که از پیش فرستاده‌اید؛ و [گرنه‌] الله نسبت به بندگان [خود] ستمكار نیست».');
INSERT INTO "islamhouse" VALUES(8,52,'8:52','[عذاب این کافران حق‌ستیز،] همانند شیوۀ [مجازات] فرعونیان و [دیگر] کسانی است‌ که پیش از آنان بودند [که همگی] به آیات الله کفر ورزیدند و الله آنان را به [سزاى‌] گناهانشان گرفتار كرد. بی‌تردید، الله نیرومندِ سخت‌کیفر است.');
INSERT INTO "islamhouse" VALUES(8,53,'8:53','این [كیفر سخت،] از آن روست كه الله هرگز بر آن نبوده است كه نعمتى را كه بر مردمى ارزانى داشته است دگرگون كند؛ مگر آنکه آنان [رفتار موحدانۀ] خود را تغییر دهند [و کفر و ناسپاسی بورزند]؛ و بی‌تردید، الله شنوای داناست.');
INSERT INTO "islamhouse" VALUES(8,54,'8:54','[کافران رفتارى دارند‌] همچون رفتار فرعونیان و كسانى كه پیش از آنان بودند كه آیات پروردگارشان را دروغ می‌انگاشتند؛ پس [ما نیز] آنان را به کیفر گناهانشان هلاک کردیم و فرعونیان را غرق نمودیم و همگی [مشرک و] ستمکار بودند.');
INSERT INTO "islamhouse" VALUES(8,55,'8:55','یقیناً بدترین جنبندگان نزد الله کسانی هستند که کفر می‌ورزند و ایمان نمی‌آورند.');
INSERT INTO "islamhouse" VALUES(8,56,'8:56','[همان] کسانی‌ که با آنان پیمان بستی؛ ولى هر بار پیمانشان را مى‌شكنند و [از الله] پروا نمى‌کنند.');
INSERT INTO "islamhouse" VALUES(8,57,'8:57','و [ای پیامبر،] اگر در جنگ بر آنان دست یافتی، چنان آنان را در هم بکوب که مایۀ عبرت دیگران گردد؛ باشد که [دیگران] پند گیرند.');
INSERT INTO "islamhouse" VALUES(8,58,'8:58','و اگر از گروهى بیم خیانت دارى، [پیمانشان را] به آنان بازگردان [و از لغو پیمان، آنان را آگاه کن تا آگاهی طرفین از نقض پیمان] به طور یكسان [باشد]. بی‌تردید، الله خائنان را دوست ندارد.');
INSERT INTO "islamhouse" VALUES(8,59,'8:59','و کسانی که کفر ورزیدند، هرگز مپندارند که جان به در برده‌اند [و نجات یافته‌اند]؛ آنان هرگز [از مرگ و عذاب،] راهِ گریزی ندارند.');
INSERT INTO "islamhouse" VALUES(8,60,'8:60','و [ای مؤمنان،] هر چه در توان دارید ـ از نیرو و اسب‌های آماده [و جنگ‌افزار]ـ بسیج كنید تا با این [تداركات‌]، دشمن الله و دشمن خویش را بترسانید، همچنین [با این تجهیزات، دشمنان‌] دیگرى را غیر از اینان که شما آنان را نمی‌شناسید و[لی] الله آنان را می‌شناسد [به وحشت بیندازید]؛ و هر چه در راه الله انفاق کنید، پاداش آن به شما بازگردانده مى‌شود و بر شما ستم نمی‌رود.');
INSERT INTO "islamhouse" VALUES(8,61,'8:61','و اگر [کافران] به صلح مایل شدند، پس تو [نیز ای پیامبر،] از درِ صلح درآی و بر الله توکل کن. بی‌گمان، اوست که شنوای داناست.');
INSERT INTO "islamhouse" VALUES(8,62,'8:62','و اگر [کافران با تظاهر به آشتی] بخواهند تو را فریب دهند، الله برایت کافی است. او تعالی همان ذاتی است که با یاری خود و مؤمنان، تو را تأیید [و تقویت] نمود.');
INSERT INTO "islamhouse" VALUES(8,63,'8:63','و بین دل‌هایشان [مهربانی و] اُلفَت برقرار نمود. [ای پیامبر،] اگر تو تمام آنچه را که در روی زمین است خرج می‌کردی، [هرگز] نمی‌توانستی بین دل‌هایشان الفت برقرار کنی؛ ولی الله میان آنان الفت انداخت. به راستی که او شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(8,64,'8:64','ای پیامبر، [حمایتِ] الله برای تو و [نیز] برای مؤمنانی که از تو پیروی کرده‌اند، كافى است.');
INSERT INTO "islamhouse" VALUES(8,65,'8:65','ای پیامبر، مؤمنان را به جنگ تشویق کن. اگر از [میان] شما [مؤمنان] بیست نفر [بر جهاد] شکیبا باشند، بر دویست نفر [از کافران] غلبه می‌کنند و اگر صد نفر از شما [اینچنین] باشند، بر هزار نفر از کسانی ‌که کفر ورزیدند غلبه می‌کنند؛ زیرا آنان گروهی هستند که [ارزش حقیقیِ جهاد را] نمی‌فهمند.');
INSERT INTO "islamhouse" VALUES(8,66,'8:66','اکنون الله به شما تخفیف داد و دانست که [بر اثر جنگ،] ضعفی در وجودتان است. پس اگر از [میان] شما صد نفر شکیبا باشند، بر دویست نفر [از کافران] پیروز می‌شوند و اگر از شما هزار نفر باشند، به فرمان الله، بر دو هزار نفر پیروز خواهند شد؛ و الله با شکیبایان است.');
INSERT INTO "islamhouse" VALUES(8,67,'8:67','برای هیچ پیامبری سزاوار نیست که اسیران [جنگی] داشته باشد [تا آنان را در مقابل دریافت فدیه رها کند، مگر] تا [زمانی‌که با ریختن خون آنها] در زمین کشتار بسیار به راه اندازد [و به این وسیله، دشمن را به وحشت بیندازد]. شما [مؤمنان، با گرفتن فدیه از اسیران بدر، ثروت و] کالای دنیا را می‌خواهید و الله آخرت را [برایتان] می‌خواهد؛ و الله شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(8,68,'8:68','اگر در آنچه [از غنایم و فدیه‌ها که] گرفتید، حکم پیشین الهی وجود نداشت [و آن را برایتان حلال نکرده بود]، قطعاً عذابی بزرگ به شما می‌رسید.');
INSERT INTO "islamhouse" VALUES(8,69,'8:69','پس [اکنون] از آنچه غنیمت گرفته‌اید، حلال و پاکیزه بخورید و از الله پروا کنید. به راستی که الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(8,70,'8:70','ای پیامبر، به اسیرانی که در دست شما هستند بگو: «اگر الله خیری در دل‌هایتان سراغ داشته باشد، بهتر از [فدیه و] آنچه را که از شما گرفته شده است به شما می‌دهد و شما را می‌آمرزد؛ و الله آمرزندۀ مهربان است».');
INSERT INTO "islamhouse" VALUES(8,71,'8:71','و [ای پیامبر،] اگر بخواهند به تو خیانت کنند، [بدان که] آنان قطعاً پیش از این [نیز] به الله خیانت کرده‌اند و الله [تو را] بر آنان پیروز نمود؛ و الله دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(8,72,'8:72','کسانی ‌که ایمان آوردند و هجرت کردند و با اموال و جان‌هایشان در راه الله جهاد نمودند و کسانی ‌که [مهاجران را] پناه دادند و یاری نمودند، اینان [همه] دوستان یکدیگرند؛ و کسانی‌ که ایمان آوردند و[لی از سرزمین کفر] هجرت نکردند، شما هیچ دوستی [و پیوندی] با آنان ندارید؛ مگر آنکه هجرت کنند؛ و اگر در [کار] دین از شما یاری خواستند، بر شما [واجب] است که [به آنان] یاری رسانید، مگر [اینکه این یاری‌خواهی] علیه گروهی باشد که میان شما و آنان پیمانی برقرار است؛ و الله به آنچه می‌کنید بیناست.');
INSERT INTO "islamhouse" VALUES(8,73,'8:73','و کسانی که کفر ورزیدند، [آنان نیز] دوستان یکدیگرند. اگر [شما مؤمنان به این سفارش دربارۀ دوست و دشمن] عمل نکنید، فتنه و فسادی بزرگ در زمین بر پا می‌گردد.');
INSERT INTO "islamhouse" VALUES(8,74,'8:74','و کسانی‌ که ایمان آوردند و هجرت نمودند و در راه الله جهاد کردند و کسانی ‌که [این مهاجران را] پناه دادند و یاری نمودند، اینان مؤمنان حقیقی هستند. برای آنان [در بهشت] آمرزش و روزیِ شایسته‌ای خواهد بود.');
INSERT INTO "islamhouse" VALUES(8,75,'8:75','و کسانی‌ که پس از آنان ایمان آوردند و هجرت نمودند و به همراه شما [مؤمنان] جهاد کردند، آنان [نیز] از شما هستند؛ و در [حکمِ] کتابِ الله، خویشاوندان [در میراث،] نسبت به یکدیگر سزاوارترند. به راستی که الله به همه چیز داناست.');
INSERT INTO "islamhouse" VALUES(9,1,'9:1','[این آیات،‌] اعلام بیزارى [و پایان تعهد] از طرف الله و پیامبرش نسبت به مشركانى است كه [شما مسلمانان] با آنان پیمان بسته‌اید.');
INSERT INTO "islamhouse" VALUES(9,2,'9:2','پس [اى مشركان، فقط] چهار ماه [فرصت دارید که با امنیت كامل‌] در زمین بگردید و بدانید كه [هرگز] نمى‌توانید الله را ناتوان سازید [و از عذابش بگریزید] و الله رسواكنندۀ كافران است.');
INSERT INTO "islamhouse" VALUES(9,3,'9:3','و [این،] اعلامی است از جانب الله و پیامبرش به مردم در روز حج اکبر [= عید قربان] که: «الله و پیامبرش از مشرکان بیزارند؛ پس اگر توبه کنید، برای شما بهتر است و اگر روی بگردانید [و سرپیچی کنید] بدانید که شما [هرگز] نمی‌توانید الله را ناتوان سازید [و از عذابش بگریزید]؛ و [تو ای پیامبر،] کسانی را ‌که کفر ورزیدند، به عذاب دردناکی بشارت بده.');
INSERT INTO "islamhouse" VALUES(9,4,'9:4','مگر کسانی از مشرکان که با آنان پیمان بسته‌اید و چیزى از [تعهدات خود نسبت به‌] شما فروگذار نكرده‌اند و کسی را علیه شما پشتیبانی نکردند؛ پیمان آنان را تا پایان مدتشان به پایان برسانید [که] بی‌تردید، الله پرهیزگاران را دوست دارد.');
INSERT INTO "islamhouse" VALUES(9,5,'9:5','وقتی ماه‌های حرام سپرى شد، مشركان را هر كجا یافتید، بکُشید و آنان را دستگیر كنید و در محاصره بگذارید و در هر گذرگاهی به كمین آنان بنشینید؛ پس اگر توبه كردند و نماز برپا داشتند و زكات پرداختند، رهایشان کنید [چرا که] بی‌تردید الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(9,6,'9:6','و [ای پیامبر،] اگر یکی از مشرکان از تو پناه خواست، پناهش بده تا سخن الله را بشنود؛ سپس او را به محل امنش برسان. این [امان‌دادن به مشرکان،] به سبب آن است که آنان گروهی نادانند.');
INSERT INTO "islamhouse" VALUES(9,7,'9:7','چگونه مشرکان می‌توانند نزد الله و پیامبرش [عهد و] پیمانی داشته باشند؟ مگر [در مورد] کسانی که با آنان نزدیک مسجدالحرام [در حدیبیه‌] پیمان بسته‌اید. پس تا [مشرکان در مورد آن عهد،] با شما پایدارند، شما [نیز] با آنان پایدار باشید؛ [چرا که] بی‌تردید، الله پرهیزگاران را دوست ‌دارد.');
INSERT INTO "islamhouse" VALUES(9,8,'9:8','چگونه [پیمان مشرکان ارزش دارد] با اینكه اگر بر شما پیروز [و مسلط] شوند، دربارۀ شما نه [حقِ] خویشاوندى را مراعات مى‌كنند و نه هیچ پیمانی را؟ آنان شما را با زبان خود راضی می‌کنند و حال آنكه دل‌هایشان امتناع مى‌ورزد و بیشتر آنان نافرمانند.');
INSERT INTO "islamhouse" VALUES(9,9,'9:9','آیات الله را به بهای ناچیزی فروختند و [مردم را] از راهِ او بازداشتند. به راستى چه بد اعمالى انجام مى‌دادند!');
INSERT INTO "islamhouse" VALUES(9,10,'9:10','اینان در حق هیچ مؤمنی، نه [حق] خویشاوندی را مراعات می‌کنند و نه پیمانی را؛ و اینانند که تجاوزکارند.');
INSERT INTO "islamhouse" VALUES(9,11,'9:11','پس [با این حال،] اگر توبه کردند و نماز برپا داشتند و زکات پرداختند، برادران دینیِ شما [مسلمانان] هستند؛ و ما آیات خود را برای گروهی که می‌دانند، به تفصیل بیان می‌کنیم.');
INSERT INTO "islamhouse" VALUES(9,12,'9:12','و اگر [تعهد و] سوگندشان را پس از پیمان خویش شکستند و در دینتان طعنه زدند، با پیشوایان کفر بجنگید؛ زیرا آنان را [نسبت به پیمان‌هایشان] هیچ تعهدی نیست؛ باشد که [از رفتار خود] دست بردارند.');
INSERT INTO "islamhouse" VALUES(9,13,'9:13','[ای مؤمنان،] چرا با گروهى نمى‌جنگید كه سوگند [و پیمان] خود را شكستند و در بیرون راندنِ پیامبر [از مکه] کوشیدند؟ آنان بودند كه نخستین ‌بار [با یاری کردن قبیلۀ بَکْر در برابر خُزاعه، جنگ را] با شما آغاز كردند. آیا از آنان مى‌ترسید؟ در حالی که اگر ایمان دارید، الله سزاوارتر است که از او بترسید.');
INSERT INTO "islamhouse" VALUES(9,14,'9:14','با آنان بجنگید تا الله آنان را به دست شما عذاب كند و خوار سازد و شما را بر آنان پیروز گرداند و دل‌های مؤمنان را آرام بخشد.');
INSERT INTO "islamhouse" VALUES(9,15,'9:15','و [با شکست کافران،] خشم دل‌های آنان [= مؤمنان] را برطرف کند؛ و الله از هر کس بخواهد درمی‌گذرد؛ و الله دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(9,16,'9:16','آیا پنداشته‌اید كه [بدون امتحان الهی] به حال خود رها مى‌شوید؟ در حالى كه الله هنوز كسانى از شما را كه جهاد كرده‌اند و جز الله و پیامبر و مؤمنان [کسی را به دوستی و] همرازى نگرفته‌اند معلوم نکرده است؟ و الله به آنچه می‌کنید آگاه است.');
INSERT INTO "islamhouse" VALUES(9,17,'9:17','سزاوار نیست که مشرکان، مساجدِ الله را آباد کنند، در حالی ‌که آنان [عملاً] به کفرِ خویش گواهند [و آن را اظهار می‌کنند]. آنانند كه اعمالشان تباه گشته است و خود در آتش [دوزخ] جاودانند.');
INSERT INTO "islamhouse" VALUES(9,18,'9:18','مساجدِ الله را فقط کسانی [باید] آباد کنند که به الله و روز آخرت ایمان آورده‌اند و نماز برپا می‌دارند و زکات می‌پردازند و از غیر الله نمی‌ترسند. امید است که اینان از هدایت‌یافتگان باشند.');
INSERT INTO "islamhouse" VALUES(9,19,'9:19','[ای مشرکان،] آیا آب‌دادن به حاجیان و آباد‌کردن مسجد الحرام را با [ایمان و عملِ] كسى كه به الله و روز واپسین ایمان آورده و در راه الله جهاد كرده است برابر می‌دانید؟ [این دو هرگز] نزد الله برابر نیستند؛ و الله ستمکاران [مشرک] را هدایت نمى‌كند.');
INSERT INTO "islamhouse" VALUES(9,20,'9:20','كسانى كه ایمان آوردند و هجرت كردند و در راه الله با اموال و جان‌های خویش جهاد کردند، نزد الله مقام برتری دارند؛ و اینان همان رستگارانند.');
INSERT INTO "islamhouse" VALUES(9,21,'9:21','پروردگارشان آنان را از جانب خویش به رحمت و خشنودى و باغ‌هایی [در بهشت‌] مژده مى‌دهد كه در آنها نعمت‌هایی پایدار دارند.');
INSERT INTO "islamhouse" VALUES(9,22,'9:22','همواره در آن جاودانند. به راستی که پاداش بزرگ [فقط] نزد الله است.');
INSERT INTO "islamhouse" VALUES(9,23,'9:23','ای کسانی ‌که ایمان آورده‌اید، اگر پدران و برادرانتان كفر را بر ایمان ترجیح ‌دهند، [آنان را] به دوستى نگیرید؛ و هر کس از شما که آنان را به دوستى بگیرد، آنان همان ستمكارانند.');
INSERT INTO "islamhouse" VALUES(9,24,'9:24','[ای پیامبر، به مؤمنان] بگو: «اگر پدران‌ و فرزندان و برادران و همسران و خویشاوندانتان و اموالی که به دست آورده‌اید و تجارتی که از [بی‌رونقی و] کسادی‌اش می‌ترسید و خانه‌هایی که به آن دلخوش هستید، نزد شما از الله و پیامبرش و جهاد در راه او محبوب‌ترند، منتظر باشید تا الله فرمان [عذاب] خویش را نازل کند؛ و الله گروه نافرمان را هدایت نمی‌کند».');
INSERT INTO "islamhouse" VALUES(9,25,'9:25','بی‌تردید، الله شما [مؤمنان] را در موقعیت‌های زیادی یاری کرده است و نیز در جنگ حُنَین، آنگاه که فراوانی تعدادتان، شما را [شاد و] شگفت‌زده کرد؛ ولی هیچ سودی به حالتان نداشت و زمین ـ با همۀ گستردگی‌ ـ بر شما تنگ آمد؛ آنگاه [به دشمن‌] پشت کردید [و گریختید].');
INSERT INTO "islamhouse" VALUES(9,26,'9:26','سپس الله آرامش خویش را بر پیامبرش و بر مؤمنان فرو فرستاد و سپاهیانی را که شما نمی‌دیدید [به یاری‌تان] فرستاد و کسانی را که کفر ورزیده بودند عذاب کرد؛ و جزای کافران چنین است‌.');
INSERT INTO "islamhouse" VALUES(9,27,'9:27','سپس الله بعد از آن [عذاب خفت‌بار،] توبۀ‌ هر ‌کس را بخواهد می‌پذیرد؛ و الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(9,28,'9:28','ای کسانی‌ که ایمان آورده‌اید، مشرکان قطعاً پلید‌ند؛ پس نباید بعد از امسال به مسجدالحرام نزدیک شوند؛ و اگر [به خاطر قطع تجارت با آنان] از تنگدستی بیم دارید، [بدانید که] اگر الله بخواهد، شما را از بخشش خویش بی‌نیاز می‌سازد. بی‌تردید، الله دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(9,29,'9:29','با کسانی [از اهل کتاب] که به الله و روز قیامت ایمان نمی‌آورند و آنچه را الله و پیامبرش حرام کرده‌اند حرام نمی‌شمارند و دین حق را نمی‌پذیرند بجنگید تا آنگاه که با شکست و خواری به دست خویش جِزیه دهند.');
INSERT INTO "islamhouse" VALUES(9,30,'9:30','و یهود گفتند: «عُزَیر پسر الله است» و نصاری گفتند: «مسیح پسر الله است». این سخن [باطلى‌] است كه بر زبان مى‌آورند و به گفتار كسانى كه پیش از این كفر ورزیدند شباهت دارد. الله آنان را نابود کند! چگونه [از حق] منحرف می‌شوند؟');
INSERT INTO "islamhouse" VALUES(9,31,'9:31','[این مشرکان،] علما و راهبان خود و [همچنین] مسیح ـ پسر مریم ـ را به جای الله [به عنوان] معبودان خویش تلقی کردند؛ حال آنكه دستوری جز این نداشتند که فقط الله یگانه را عبادت کنند كه معبودى [به‌حق] جز او نیست. او تعالی از آنچه [با وی] شریک قرار می‌دهند، منزه است.');
INSERT INTO "islamhouse" VALUES(9,32,'9:32','آنان مى‌خواهند نورِ الله را با سخنان خویش خاموش كنند؛ ولی الله جز این نمی‌خواهد که نورِ خویش را کامل کند؛ هر چند کافران خوش نداشته باشند.');
INSERT INTO "islamhouse" VALUES(9,33,'9:33','اوست که پیامبرش را با هدایت و دین حق فرستاده است تا آن را بر همۀ ادیان پیروز گرداند؛ هر چند مشركان دوست نداشته باشند.');
INSERT INTO "islamhouse" VALUES(9,34,'9:34','ای کسانی که ایمان آورده‌اید، بی‌تردید، بسیارى از علما[ی یهودی] و راهبان، اموال مردم را به ناحق مى‌خورند و [آنان را] از راه الله باز مى‌دارند؛ و كسانى كه زر و سیم مى‌اندوزند و آن را در راه الله انفاق نمى‌كنند، آنان را به عذابی دردناک بشارت بده.');
INSERT INTO "islamhouse" VALUES(9,35,'9:35','روزى كه آن [گنجینه‌ها] را در آتش دوزخ سرخ كنند و پیشانى و پهلو و پشتشان را با آن داغ نهند [و بگویند:] «این است آنچه براى خویش مى‌اندوختید؛ پس بچشید آنچه را كه مى‌اندوختید».');
INSERT INTO "islamhouse" VALUES(9,36,'9:36','شمار ماه‌ها نزد الله، [از] روزی که آسمان‌ها و زمین را آفریده است، در کتاب الله [= لوح محفوظ] دوازده ماه است که [جنگ و درگیری در] چهار ماه از آن، حرام است؛ [ذوالقعده، ذوالحجه، محرم و رجب]. این است آیینِ استوار [و راهِ حق]؛ پس در این [چهار ماه،] بر خود ستم نكنید و با همۀ [انواع] مشركان [و کافران] بجنگید، چنان كه آنان با همۀ شما مى‌جنگند؛ و بدانید كه الله همراه پرهیزگاران است.');
INSERT INTO "islamhouse" VALUES(9,37,'9:37','بی‌تردید، به تأخیر‌ انداختن [ماه‌های حرام و بر هم زدن ترتیب اصلىِ آنها، موجب] افزایش کفر است [و] کسانی که کفر ورزیدند با این کار، به گمراهی کشیده می‌شوند. آن [ماه] را یک سال حلال مى‌شمارند و یک سالِ [دیگر] حرام مى‌دانند تا با تعداد ماه‌هایی كه الله حرام كرده است یکسان سازند؛ پس [به این سبب،‌] آنچه را که الله حرام كرده است [بر خود] حلال می‌کنند؛ زشتىِ اعمال آنان برایشان آراسته شده است؛ و الله گروه كافران را هدایت نمى‌كند.');
INSERT INTO "islamhouse" VALUES(9,38,'9:38','اى كسانى كه ایمان آورده‌اید، شما را چه شده است كه چون به شما گفته مى‌شود: «در راه الله رهسپار [جهاد] شوید»، سستی به خرج مى‌دهید [و در خانه‌هایتان می‌نشینید]؟ آیا به جاى آخرت، به زندگى دنیا دل خوش كرده‌اید؟ [پس بدانید که] کالای زندگى دنیا در برابر آخرت، جز اندكى نیست.');
INSERT INTO "islamhouse" VALUES(9,39,'9:39','[ای مؤمنان،] اگر رهسپار [جهاد] نگردید، [الله] شما را به عذابى دردناک گرفتار مى‌كند و به جاى شما گروهى دیگر را مى‌آورَد و [شما با نافرمانی، هرگز] به او تعالی زیانى نمی‌رسانید؛ و الله بر هر چیزى تواناست.');
INSERT INTO "islamhouse" VALUES(9,40,'9:40','[ای اصحاب پیامبر،] اگر شما او را یارى نكنید، قطعاً الله [او را ياری می‌كند، همان گونه كه قبلاً] یارى‌اش نمود؛ هنگامى كه کافران او را [از مكه‌] بیرون كردند، در حالی که او دوّمین نفر بود [و یک نفر بیشتر همراه نداشت]؛ آنگاه که آن دو در غار [ثور پناه گرفته] بودند، به یار خود [= ابوبکر] ‌گفت: «غم مخور [كه] بی‌تردید الله با ماست»؛ پس الله آرامشِ خود را بر او فرو فرستاد و او را با سپاهیانى كه آنها را نمى‌دیدید، تأیید [و تقویت] كرد و سخن كسانى را كه كفر ورزیدند پست‌تر گردانْد؛ و سخن الله است كه برتر است؛ و الله شكست‌ناپذیرِ حكیم است.');
INSERT INTO "islamhouse" VALUES(9,41,'9:41','سبكبار و گرانبار [به هر حالتی، برای جهاد] رهسپار شوید و با اموال و جان‌هایتان در راه الله جهاد كنید. اگر بدانید، این براى شما بهتر است.');
INSERT INTO "islamhouse" VALUES(9,42,'9:42','[ای پیامبر،] اگر مالی در دسترس و سفری [آسان و] کوتاه در پیش بود، قطعاً [منافقان] به دنبال تو مى‌آمدند؛ ولى آن راهِ پر مشقت [= شام] بر آنان دور [و دشوار] نمود؛ و به زودى به الله سوگند خواهند خورد كه: «اگر مى‌توانستیم، حتماً با شما رهسپار می‌شدیم». [آنان با نافرمانی و سوگند دروغ‌،] خود را به هلاكت افکندند و الله مى‌داند كه آنان یقیناً دروغگو هستند.');
INSERT INTO "islamhouse" VALUES(9,43,'9:43','[ای پیامبر،] الله از تو درگذرد! چرا پیش از آنكه [حال‌] راستگویان بر تو روشن گردد و دروغگویان را بازشناسى، به آنان اجازه دادى [که در جهاد شركت نكنند]؟');
INSERT INTO "islamhouse" VALUES(9,44,'9:44','كسانى كه به الله و روز واپسین ایمان دارند، در جهاد با اموال و جان‌هایشان از تو اجازه [معافیت] نمى‌خواهند؛ و الله به [حال‌] پرهیزگاران داناست.');
INSERT INTO "islamhouse" VALUES(9,45,'9:45','تنها كسانى از تو اجازه [معافیت از جهاد] مى‌خواهند كه به الله و روز واپسین ایمان ندارند و دل‌هایشان به تردید آلوده است و در تردید خود سرگردانند.');
INSERT INTO "islamhouse" VALUES(9,46,'9:46','و اگر [به راستى‌] ارادۀ رهسپاری [به قصد جهاد] داشتند، قطعاً ساز و برگی برایش فراهم مى‌کردند؛ ولى الله رهسپاریِ آنان را [برای جهاد در رکاب تو] نپسندید؛ پس آنان را منصرف نمود و [به آنان‌] گفته شد: «با خانه‌نشینان بمانید».');
INSERT INTO "islamhouse" VALUES(9,47,'9:47','[ای مؤمنان،] اگر [منافقان] همراه شما رهسپار می‌شدند، [به سبب بزدلی و فرار از میدان جنگ،] جز فتنه و فساد برایتان به بار نمی‌آوردند و در میانتان نفوذ مى‌نمودند و در حق شما فتنه‌جویی می‌کردند و در جمع شما جاسوسانی دارند؛ و الله به [حال‌] ستمکاران آگاه است‌.');
INSERT INTO "islamhouse" VALUES(9,48,'9:48','یقیناً پیش از این [نیز منافقان، بسیار] فتنه‌جویى کردند و كارها را بر تو [وارونه و] آشفته جلوه دادند تا آنکه حق آمد و فرمان الله آشكار گردید، در حالى كه آنان کراهت داشتند.');
INSERT INTO "islamhouse" VALUES(9,49,'9:49','و از میان آنان کسی هست که می‌گوید: «به من اجازه بده [که جهاد نکنم] و مرا در فتنه نینداز». آگاه باشید! [منافقان] هم اکنون نیز در فتنه افتاده‌اند و بی‌تردید، دوزخ بر کافران احاطه دارد.');
INSERT INTO "islamhouse" VALUES(9,50,'9:50','[ای پیامبر،] اگر به تو نیکی برسد، آنان را ناراحت می‌کند و اگر مصیبتی به تو رسد، می‌گویند: «ما پیش از این چارۀ کار خود را اندیشیده‌ایم» و شادمان [نزد خانواده‌هایشان] بازمی‌گردند.');
INSERT INTO "islamhouse" VALUES(9,51,'9:51','بگو: «هرگز [مصیبتی] به ما نمی‌رسد، جز آنچه الله برایمان مقرر کرده است. او [دوست و] کارسازِ ماست و مؤمنان باید بر الله توکل کنند».');
INSERT INTO "islamhouse" VALUES(9,52,'9:52','[به منافقان] بگو: «آیا برای ما جز یکی از دو نیکی [پیروزی یا شهادت] را انتظار دارید؟ در حالى كه ما انتظار مى‌كشیم الله از جانب خویش یا به دست ما، شما را به عذابى [سخت] گرفتار کند. پس انتظار بكشید كه بی‌تردید، ما [نیز] با شما منتظریم».');
INSERT INTO "islamhouse" VALUES(9,53,'9:53','[به منافقان] بگو: «چه با رغبت چه با کراهت انفاق كنید، هرگز از شما پذیرفته نمی‌شود [چرا که] بی‌تردید، شما گروهى نافرمان بوده‌اید».');
INSERT INTO "islamhouse" VALUES(9,54,'9:54','و هیچ چیز مانع قبول انفاق‌هایشان نشد، جز اینكه آنان به الله و پیامبرش كفر ورزیدند و جز با کسالت [و سستی] نماز نمى‌گزارند و جز به كراهت [و بی‌میلی] انفاق نمى‌كنند.');
INSERT INTO "islamhouse" VALUES(9,55,'9:55','پس [ای پیامبر،] اموال و فرزندانشان تو را شگفت‌زده نکند. بی‌تردید، الله مى‌خواهد آنان را در زندگى دنیا به این وسیله عذاب كند و در حالی که کافرند جانشان برآید.');
INSERT INTO "islamhouse" VALUES(9,56,'9:56','و آنان [= منافقان] به الله سوگند یاد می‌کنند که از شما [مؤمنان] هستند، در حالی‌ که از شما نیستند؛ بلکه آنان گروهی هستند که می‌ترسند [و به این سبب سوگند می‌خورند].');
INSERT INTO "islamhouse" VALUES(9,57,'9:57','اگر پناهگاه یا غار یا گریزگاهى پیدا ‌كنند، شتابان به سوى آن روى مى‌آورند.');
INSERT INTO "islamhouse" VALUES(9,58,'9:58','و برخی از آنان در [تقسیم] صدقات [و غنایم] بر تو خرده می‌گیرند؛ اگر از آن [اموال‌] به آنان داده شود راضی مى‌گردند و اگر چیزی از آن به آنان داده نشود، خشمگین می‌شوند.');
INSERT INTO "islamhouse" VALUES(9,59,'9:59','و [قطعاً برایشان بهتر بود] اگر به آنچه الله و پیامبرش به آنان داده‌اند راضی مى‌گشتند و مى‌گفتند: «الله برایمان کافی است؛ به زودى الله و پیامبرش از فضل و بخشش خود به ما مى‌دهند و ما به الله [امیدوار و] مشتاقیم».');
INSERT INTO "islamhouse" VALUES(9,60,'9:60','زکات‌ اختصاص دارد به فقیران و مستمندان و کارگزاران [جمع‌آوری و توزیعِ] آن و دلجویی‌شدگان [تازه مسلمان] و برای [آزاد کردن] بردگان و [پرداختِ وام] بدهکاران و [جهادگران] در راهِ الله و [کمک به] درراه‌ماندگان. [این] فریضه‌ای [مقررشده] از جانب الله است؛ و الله دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(9,61,'9:61','و برخی از آنان [= منافقان] هستند که پیامبر را می‌آزارند و می‌گویند: «او زود‌باور [و سراپا گوش] است»؛ [ای پیامبر،] بگو: «او براى شما [فقط] شنوندۀ سخنِ خیر است. [وی] به الله ایمان دارد و [سخن‌] مؤمنان را باور دارد و براى كسانى از شما كه ایمان آورده‌اند رحمتى است»؛ و كسانى كه رسول الله را می‌آزارند، عذابى دردناک [در پیش‌] دارند.');
INSERT INTO "islamhouse" VALUES(9,62,'9:62','[ای مؤمنان، منافقان] برایتان به الله سوگند یاد می‌کنند تا شما را راضی سازند؛ در حالی‌ که اگر ایمان داشتند، شایسته‌تر [این] بود که الله و پیامبرش را راضی سازند.');
INSERT INTO "islamhouse" VALUES(9,63,'9:63','آیا نمی‌دانند هر‌ کس با الله و رسولش مخالفت [و دشمنی] کند، مسلماً آتش جهنم برای اوست [و] جاودانه در آن خواهد بود؟ این همان رسوایی بزرگ است.');
INSERT INTO "islamhouse" VALUES(9,64,'9:64','منافقان بیم دارند كه [مبادا] سوره‌اى دربارۀ آنان [بر پیامبر] نازل شود و آنان [= مؤمنان] را از آنچه در دل‌های آنان [= منافقان] است آگاه سازد. [ای پیامبر، به آنان] بگو: «[هر چه می‌خواهید،] مسخره كنید [که] بى‌تردید، الله آنچه را كه [از آن‌ می‌پرهیزید و] هراس دارید، افشا خواهد كرد».');
INSERT INTO "islamhouse" VALUES(9,65,'9:65','و اگر از آنان بپرسی: [«چرا تمسخر می‌کردید؟»] مسلّماً می‌گویند: «ما جدی نمی‌گفتیم و [شوخی و] بازی می‌کردیم». بگو: «آیا الله و آیات او و پیامبرش را مسخره می‌کردید؟»');
INSERT INTO "islamhouse" VALUES(9,66,'9:66','عذر نیاورید. به راستی که شما پس از ایمانتان کافر شده‌اید. اگر گروهی از شما را [به خاطر توبه‌شان] ببخشیم، گروهی دیگر را [به سببِ سرکشی و ترک انفاق] عذاب خواهیم کرد؛ چرا که بی‌تردید، آنان تبهکار بودند.');
INSERT INTO "islamhouse" VALUES(9,67,'9:67','مردان و زنان منافق، [مانند یکدیگر و] از یک گروهند: به کارهای زشت فرمان می‌دهند و از نیکی‌ها بازمی‌دارند و دست‌های خویش را [از انفاق‌] فرومى‌بندند [و] الله را فراموش کردند؛ الله [نیز با بی‌تفاوتی] آنان را به فراموشی سپرد [= رحمت و توفیقش را از آنان سلب نمود]. در حقيقت، اين منافقانند كه فاسق [و سرکش و گناهکار] هستند.');
INSERT INTO "islamhouse" VALUES(9,68,'9:68','الله به مردان و زنان منافق و [همچنین به] کافران، آتش دوزخ را وعده داده است؛ جاودانه در آن خواهند ماند. همان [آتش‌] برایشان كافى است و الله لعنتشان كرده است و عذابى پایدار [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(9,69,'9:69','[شما منافقان] همانند کسانی‌ [هستید] که پیش از شما بودند [بلکه] آنان از شما نیرومندتر بودند و اموال و فرزندان بیشتری داشتند. پس، از بهرۀ خویش [در دنیا] برخوردار شدند؛ و شما [نیز] از بهرۀ خود برخوردار شدید چنانکه کسانی که پیش از شما بودند [نیز] از بهرۀ خویش برخوردار شدند؛ و شما [در گناه تکذیب حق و توهین به پیامبر خود] فرو رفتید، همان طور كه آنان فرو رفتند. آنان اعمالشان در دنیا و آخرت تباه گشت و آنانند که زیانكارند.');
INSERT INTO "islamhouse" VALUES(9,70,'9:70','آیا خبر کسانی‌ که پیش از آنان بودند به آنان نرسیده است؟ [کسانی همچون] قوم نوح و عاد و ثمود و قوم ابراهیم و اصحاب مَدیَن [= قوم شعیب] و شهرهای زیر و رو شده [قوم لوط] که پیامبران آنان دلایل روشن برایشان آوردند [ولی آنان نپذیرفتند]؛ پس الله بر آن نبود که [با نزولِ عذاب] به آنان ستم کند؛ بلکه خود بر خویشتن ستم می‌کردند.');
INSERT INTO "islamhouse" VALUES(9,71,'9:71','و مردان و زنان مؤمن، دوستان یکدیگرند؛ [مردم را] به نیکی فرمان می‌دهند و از کارهای ناپسند بازمی‌دارند و نماز برپا می‌دارند و زکات می‌پردازند و از الله و پیامبرش اطاعت می‌نمایند. اینانند که الله آنان را مورد رحمت قرار خواهد داد. بی‌تردید، الله شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(9,72,'9:72','الله به مردان و زنان مؤمن، باغ‌هایی [از بهشت] وعده داده است که جویبارها از زیر [درختان] آن جاری است؛ جاودانه در آن خواهند ماند؛ و [نیز] خانه‌های پاکیزه‌ای در بهشت‌های جاودان [به آنان وعده داده است‌]؛ و رضایت الله [از همۀ اینها] برتر است. این همان [رستگاری و] کامیابیِ بزرگ است.');
INSERT INTO "islamhouse" VALUES(9,73,'9:73','ای پیامبر، با کافران و منافقان جهاد کن و بر آنان سختگیر باش؛ و جایگاهشان دوزخ است و چه بد سرانجامی است!');
INSERT INTO "islamhouse" VALUES(9,74,'9:74','[منافقان] به الله سوگند یاد می‌کنند که [سخنان ناپسندی دربارۀ الله و پیامبرش] نگفته‌اند و به راستی که سخن کفر گفته‌اند و پس از اسلام آوردنشان [باز] کافر شدند [و با دسیسه برای قتل پیامبر،] تصمیم به کاری گرفتند كه در آن موفق نشدند. و به عیب‌جویی [و مخالفت] برنخاستند مگر پس از آنکه الله و پیامبرش [با بخشیدنِ غنایم،] آنان را از فضل خویش بی‌نیاز کردند؛ پس اگر توبه کنند، برایشان بهتر است و اگر روی بگردانند، الله آنان را در دنیا و آخرت، به عذابی دردناک دچار می‌سازد و در زمین هیچ کارساز و یاوری نخواهند داشت.');
INSERT INTO "islamhouse" VALUES(9,75,'9:75','بعضی از آنان با الله [عهد و] پیمان بستند که: «اگر [الله] از فضل خود [نصیبی] به ما دهد، قطعاً صدقه [و زکات] خواهیم داد و از نیکوکاران خواهیم بود».');
INSERT INTO "islamhouse" VALUES(9,76,'9:76','اما وقتی [الله] از فضل خود به آنان [نصیبی] بخشید، نسبت به آن بُخل ورزیدند و [از آن پیمان] روی گرداندند و [از حق] سرپیچی کردند.');
INSERT INTO "islamhouse" VALUES(9,77,'9:77','پس به سزاى آنكه با الله خُلفِ وعده كردند و به خاطر آن كه دروغ مى‌گفتند، [الله نیز] تا روزى كه او را [در قیامت] ملاقات می‌کنند، در دل‌هایشان داغ [و ننگِ] نفاق نهاد.');
INSERT INTO "islamhouse" VALUES(9,78,'9:78','آیا ندانسته‌اند كه الله راز و نجوایشان را [که در محافل خویش بیان می‌شود، به خوبی] مى‌داند و الله دانای رازهای نهان است؟');
INSERT INTO "islamhouse" VALUES(9,79,'9:79','[منافقان] كسانى [هستند] كه بر مؤمنانى عیب مى‌گیرند كه داوطلبانه صدقات [مستحبی زیادی، علاوه بر صدقه واجب] مى‌دهند و [همچنین فقرا و‌] افرادی را كه بیش از توان مالی خویش چیزی [برای صدقه] نمى‌یابند مسخره می‌کنند. الله آنان را به تمسخر مى‌گیرد و برایشان عذابى دردناک [در پیش] خواهد بود.');
INSERT INTO "islamhouse" VALUES(9,80,'9:80','[ای پیامبر،] چه برای آنان آمرزش بخواهی، چه آمرزش نخواهی، [سودی به حالشان ندارد. حتی] اگر هفتاد بار برایشان آمرزش بخواهی، الله هرگز آنان را نمی‌آمرزد؛ چرا که به الله و پیامبرش کفر ورزیدند، و الله گروه نافرمان را هدایت نمی‌کند.');
INSERT INTO "islamhouse" VALUES(9,81,'9:81','واگذاشته‌شدگان [از غزوۀ تبوک]پس از [مخالفت با] رسول الله، از خانه‌نشینیِ خود شادمان شدند و از اینكه با اموال و جان‌هایشان در راه الله جهاد كنند كراهت داشتند و [به افراد سست‌ایمان] می‌گفتند: «در این گرما، رهسپار [جهاد] نشوید». [ای پیامبر، به آنان] بگو: «آتش دوزخ [از این بسیار] سوزان‌تر است»، اگر می‌فهمیدند».');
INSERT INTO "islamhouse" VALUES(9,82,'9:82','به سزای آنچه می‌کردند، باید [در دنیا] كم بخندند و [در آخرت] بسیار بگریند.');
INSERT INTO "islamhouse" VALUES(9,83,'9:83','و اگر الله تو را به سوى گروهى از آنها بازگردانْد و از تو براى همراهی [در جهادی دیگر‌] اجازه خواستند بگو: «[از این پس،] هرگز با من رهسپار [جهاد‌] نخواهید شد و هرگز همراه من با هیچ دشمنى نبرد نخواهید كرد؛ زیرا شما نخستین بار به خانه‌نشینی رضایت دادید؛ پس [اکنون نیز] با بازماندگان [از جهاد] بنشینید.');
INSERT INTO "islamhouse" VALUES(9,84,'9:84','و هرگز بر هیچ‌ مُرده‌ای از آنان نماز نخوان و [برای دعا خواندن و طلب آمرزش] بر سر قبرش نایست؛ [چرا که] بی‌تردید، آنان به الله و پیامبرش کفر ورزیده‌اند و در حالی مُردند که نافرمان بودند.');
INSERT INTO "islamhouse" VALUES(9,85,'9:85','و [ای پیامبر، افزونیِ] اموال و فرزندانشان تو را شگفت‌زده نکند. به راستی جز این نیست که الله می‌خواهد به این [وسیله] آنان را در دنیا عذاب کند و در حالی ‌که کافرند جانشان برآید.');
INSERT INTO "islamhouse" VALUES(9,86,'9:86','و هنگامی ‌که سوره‌ای نازل شود [و امر می‌کند] که «به الله ایمان بیاورید و همراه پیامبرش جهاد کنید»، ثروتمندانشان [برای نرفتن به جهاد] از تو اجازه می‌خواهند و می‌گویند: «بگذار ما با خانه‌نشینان باشیم».');
INSERT INTO "islamhouse" VALUES(9,87,'9:87','آنان راضی شدند که با خانه‌نشینان باشند و بر دل‌هایشان مُهر [نفاق‌] نهاده شده است؛ پس [خیر و صلاح خویش را] درنمی‌یابند.');
INSERT INTO "islamhouse" VALUES(9,88,'9:88','اما پیامبر و کسانی‌ که با او ایمان آورده‌اند، با اموال و جان‌هایشان [در راه الله] جهاد کردند. آنانند كه همۀ نیکی‌ها برایشان است و آنان همان رستگارانند.');
INSERT INTO "islamhouse" VALUES(9,89,'9:89','الله برایشان باغ‌هایی [از بهشت] مهیّا کرده است که جویبارها از زیر [درختان] آن جاری است [و] جاودانه در آن می‌مانند. این [همان رستگاری و] کامیابی بزرگ است.');
INSERT INTO "islamhouse" VALUES(9,90,'9:90','و بهانه‌جویان [و عذرتراشانِ] بادیه‌نشین [نزد تو] آمدند تا به آنان اجازه [معافیت از رفتن به جهاد‌] داده شود و كسانى كه [در ادعاى ایمان خود] به الله و پیامبرش دروغ مى‌گفتند [بدون هیچ عذر و اجازه‌اى، به جای جهاد،] خانه‌نشین شدند. به زودی به کسانی از آنان که کفر ورزیدند، عذابی دردناک خواهد رسید.');
INSERT INTO "islamhouse" VALUES(9,91,'9:91','بر ناتوانان [= زنان و کودکان] و بیماران و بر كسانى كه چیزى نمى‌یابند [تا در راه جهاد] هزینه كنند ـ اگر براى الله و پیامبرش خیرخواهى كنندـ گناهی نیست [که در جهاد شرکت نکنند]. بر نیكوكاران[شان نیز] هیچ راهی [برای سرزنش و مجازات] نیست؛ و الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(9,92,'9:92','و [همچنین] گناهى نیست بر كسانى كه چون نزدت آمدند تا آنان را [برای رفتن به جهاد، بر چهارپایی] سوار كنى، [به آنان] گفتى: «چیزى پیدا نمى‌كنم که شما را بر آن بنشانم». آنان بازگشتند در حالی که چشمانشان از این اندوه، اشکبار بود كه چیزى نمى‌یابند تا [در راه جهاد] هزینه كنند.');
INSERT INTO "islamhouse" VALUES(9,93,'9:93','راه [سرزنش و مجازات] تنها بر كسانى [گشوده] است كه با آنکه توانگرند [و امکان جهاد دارند]، از تو اجازه مى‌خواهند [که در جهاد شركت نكنند]. آنان به این رضایت داده‌اند كه با خانه‌نشینان باشند و الله بر دل‌هایشان مُهر [نفاق] زده است؛ پس [خیر و صلاح خویش را] درنمی‌یابند.');
INSERT INTO "islamhouse" VALUES(9,94,'9:94','وقتی [از غزوۀ تبوک] به سویشان بازگردید، برایتان عذر مى‌آورند. [ای پیامبر،] بگو: «عذر نیاورید؛ ما هرگز [سخن] شما را باور نمی‌کنیم. در حقیقت، الله ما را از اخبار شما آگاه کرده است و به زودی الله و پیامبرش رفتارتان را خواهند دید [که آیا توبه می‌کنید یا بر نفاق می‌مانید]. آنگاه به سوی [الله] دانای پنهان و آشکار بازگردانده می‌شوید و او شما را از آنچه می‌کردید آگاه می‌کند».');
INSERT INTO "islamhouse" VALUES(9,95,'9:95','[ای مؤمنان، پس از جنگ] وقتی به سوی آنان [= منافقان] بازگردید، برایتان به الله سوگند یاد می‌کنند تا از [گناهِ] آنان چشم‌پوشی کنید. پس از آنان روی بگردانید؛ [چرا كه] بی‌تردید، آنان پلیدند و به [سزاى‌] آنچه می‌کردند، جایگاهشان دوزخ است.');
INSERT INTO "islamhouse" VALUES(9,96,'9:96','برایتان سوگند یاد می‌کنند تا از آنان راضی شوید. [حتی] اگر شما از آنان راضی شوید، الله هرگز از گروه نافرمان راضی نمی‌گردد.');
INSERT INTO "islamhouse" VALUES(9,97,'9:97','کفر و نفاق اعراب [بادیه‌نشین، از دیگران] شدیدتر است و سزاوارترند كه حدودِ آنچه را كه الله بر پیامبرش نازل كرده است ندانند؛ و الله دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(9,98,'9:98','و برخی از اعراب [بادیه‌نشین] هستند كه آنچه را [در راه الله] هزینه مى‌كنند، خسارتى [براى خود] مى‌دانند و پیشامدهای بد را برایتان انتظار می‌کشند [تا از شما رهایی یابند]. پیشامدِ بد بر خودشان باد؛ و الله شنواى داناست.');
INSERT INTO "islamhouse" VALUES(9,99,'9:99','و برخی [دیگر] از اعراب [بادیه‌نشین] هستند كه به الله و روز واپسین ایمان دارند و آنچه را که انفاق مى‌كنند باعث نزدیکی به الله و [نتیجه] دعاهاى پیامبر [در حق خویش] مى‌دانند. آگاه باشید! [این انفاق،] مایۀ نزدیکی آنهاست. الله [در قیامت] آنان را در جوارِ رحمتِ خویش درخواهد آورد. بی‌تردید، الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(9,100,'9:100','و پیشگامان نخستین ـ از مهاجران و انصارـ و کسانی ‌که به نیکی از آنان پیروی کردند، الله از آنان راضی گشت و آنان [نیز] از او راضی شدند؛ و باغ‌هایی [در بهشت] برایشان مهیّا کرده است که جویبارها از زیر [درختان] آن جاری است؛ جاودانه در آن خواهند ماند. این همان [رستگاری و] کامیابی بزرگ است.');
INSERT INTO "islamhouse" VALUES(9,101,'9:101','و برخی از اعراب [بادیه‌نشین مدینه] که پیرامون شما هستند، [گروهی] منافقند و از اهل مدینه [نیز] گروهی به نفاق خو گرفته‌اند. [ای پیامبر،] تو آنان را نمی‌شناسی [ولی] ما آنان را می‌شناسیم. آنان را دو بار عذاب خواهیم کرد، سپس به سوی عذابی بزرگ [در قیامت] فرستاده می‌شوند.');
INSERT INTO "islamhouse" VALUES(9,102,'9:102','و گروهی دیگر به گناهان خود [دربارۀ فرار از جهاد] اعتراف کرده‌اند. [آنان‌] كار شایسته و ناشایست را به هم آمیخته‌اند. امید است که الله توبۀ آنان را بپذیرد؛ [چرا كه] بى‌تردید، الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(9,103,'9:103','از اموال آنان [= توبه‌کاران] صدقه [و زکات] بگیر، تا به وسیله‌ آن، آنان را [از گناه] پاک گردانی و درجاتشان را بالا ببری؛ و برایشان دعا کن [چرا که] یقیناً دعای تو [رحمتی برایشان و] مایه آرامش برای آنان است؛ و الله شنوای داناست.');
INSERT INTO "islamhouse" VALUES(9,104,'9:104','آیا نمی‌دانند الله است که توبه را از بندگانش می‌پذیرد و صدقات را می‌ستانَد؛ و بی‌تردید، الله است که توبه‌پذیرِ مهربان است.');
INSERT INTO "islamhouse" VALUES(9,105,'9:105','و [ای پیامبر، به متخلفان از جهاد و توبه‌کاران] بگو: «به عمل بكوشید [و آنچه را که از دست داده‌اید جبران کنید] که الله و پیامبرش و مؤمنان، کارهای شما را خواهند دید؛ و به زودی به سوی [الله] دانای پنهان و آشکار بازگردانده می‌شوید، سپس او [در قیامت] شما را از آنچه انجام می‌دادید آگاه می‌سازد».');
INSERT INTO "islamhouse" VALUES(9,106,'9:106','و گروهی دیگر [از متخلفانِ تبوک هستند که سرنوشت آنان] به فرمان الله موکول است: یا آنان را عذاب مى‌كند و یا توبه‌شان را مى‌پذیرد؛ و الله داناى حکیم است.');
INSERT INTO "islamhouse" VALUES(9,107,'9:107','و کسانی‌ [از منافقان هستند] که به هدف زیان رساندن [به مسلمانان] و [تقویت] کفر و تفرقه‌افکنی میان مؤمنان، مسجدی ساختند [تا] کمینگاهی باشد برای کسانی‌ که با الله و پیامبرش از پیش در جنگ بودند؛ و البته آنان سوگند یاد می‌کنند که قصدی جز نیکی [و خدمت] نداشته‌ایم؛ و[لی] الله گواهی می‌دهد که آنان قطعاً دروغگو هستند.');
INSERT INTO "islamhouse" VALUES(9,108,'9:108','[ای پیامبر،] هرگز در آن مسجد [به نماز] نایست؛ [زیرا] شایسته‌تر است در مسجدی [به نماز] بایستی که از روز نخست، بر پایه پرهیزگاری بنا شده است. [آری، مسجد قُباست] که در آن، مردانی هستند که دوست دارند [از گناه و آلودگی] پاک و پاکیزه گردند؛ و الله پاکیزگان را دوست دارد.');
INSERT INTO "islamhouse" VALUES(9,109,'9:109','آیا كسى كه بنیان [كار] خویش را بر پایه پرهیزگاری و رضایت الله بنا نهاده بهتر است یا كسى كه بنیان خویش را بر لبۀ پرتگاهى در شرف ریزش [و سقوط] بنا نهاده است و با آن در آتش دوزخ سرنگون مى‌گردد؟ و الله گروه ستمکاران را [به سبب کفر و نفاقشان] هدایت نمی‌کند.');
INSERT INTO "islamhouse" VALUES(9,110,'9:110','آن بنایی که آنان بنیان نهاده‌اند، پیوسته در دل‌هایشان مایۀ تردید [و نفاق] خواهد بود مگر آنکه دل‌هایشان پاره‌پاره شود [و بمیرند]؛ و الله دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(9,111,'9:111','بی‌تردید، الله از مؤمنان، جان‌ها و اموالشان را به [بهاى‌] اینكه بهشت براى آنان باشد، خریده است؛ همان كسانى كه در راه الله مى‌جنگند و [کافران را] مى‌كُشند و [یا خود به دست آنها] كشته مى‌شوند. [این‌ پاداش،] به عنوان وعده‌ای راست و درست در تورات و انجیل و قرآن بر عهدۀ اوست؛ و کیست که از الله به پیمان خویش وفادارتر است؟ پس [ای مؤمنان،] به معامله‌اى كه با او كرده‌اید شادمان باشید؛ و این همان [رستگاری و] كامیابى بزرگ است.');
INSERT INTO "islamhouse" VALUES(9,112,'9:112','[کسانی که چنین پاداشی دارند، همان] توبه‌کنندگان، عبادت‌کنندگان، سپاسگزاران، روزه‌داران، رکوع‌کنندگان، سجده‌کنندگان، فرمان‌دهندگان به کارهای پسندیده و بازدارندگان از کارهای ناپسند و پاسدارانِ حدود [و احکام] الهی هستند؛ و آن مؤمنان را [که چنین هستند، به بهشت] بشارت بده.');
INSERT INTO "islamhouse" VALUES(9,113,'9:113','برای پیامبر و کسانی‌ که ایمان آوردند سزاوار نیست که برای مشرکان ـ پس از اینکه برایشان آشکار شد که آنان اهل دوزخند ـ آمرزش بخواهند؛ هر چند از نزدیکان [شان] باشند.');
INSERT INTO "islamhouse" VALUES(9,114,'9:114','آمرزش‌خواهیِ ابراهیم براى پدرش [آزر]، جز به خاطر وعده‌اى كه به او داده بود صورت نگرفت؛ اما وقتی برایش آشکار شد كه وى دشمن الله است، از او بیزارى جست [و برایش دعا نکرد]. به راستی که ابراهیم، بسى نیایشگر و بردبار بود.');
INSERT INTO "islamhouse" VALUES(9,115,'9:115','و الله هرگز بر آن نیست كه گروهی را پس از آنکه هدایتشان کرده است گمراه بگذارد؛ مگر آنکه چیزی را که باید [از آن] پرهیز کنند برایشان روشن کرده باشد [و آنان به آن عمل نکرده باشند]. بی‌تردید، الله به هر چیزی داناست.‌');
INSERT INTO "islamhouse" VALUES(9,116,'9:116','در حقیقت، فرمانرواییِ آسمان‌ها و زمین از آنِ الله است. [اوست که] زنده می‌کند و می‌میرانَد؛ و شما به جز الله هیچ کارساز و یاوری ندارید.');
INSERT INTO "islamhouse" VALUES(9,117,'9:117','بی‌گمان، الله بر پیامبر و مهاجران و انصار كه در هنگام دشواری [غزوۀ تبوک] از او پیروى كردند رحمت آورد؛ بعد از آنکه نزدیک بود دل‌های گروهی از آنان بلغزد [و به سبب سختی‌های فراوان، جهاد را ترک كنند] سپس [باز هم] توبۀ آنان را پذیرفت. بی‌تردید، او تعالی [نسبت] به آنان دلسوز [و] مهربان است.');
INSERT INTO "islamhouse" VALUES(9,118,'9:118','و [همچنین توبه] آن سه نفری [را قبول کرد] که امرشان به تأخير انداخته شد؛ آنگاه که [مسلمانان از آنان بریدند و] زمین با همۀ فراخی‌اش بر آنان تنگ شد و از خود [نیز] به تنگ آمدند و دانستند که از الله، جز به سوی خودِ او پناهگاهى نیست؛ پس [الله با بخشایشِ خویش] به آنان بازگشت تا توبه کنند [و از آنان درگذشت]. یقیناً الله است که توبه‌پذیرِ مهربان است.');
INSERT INTO "islamhouse" VALUES(9,119,'9:119','ای کسانی ‌که ایمان آورده‌اید، از الله پروا کنید و با راستگویان باشید.');
INSERT INTO "islamhouse" VALUES(9,120,'9:120','سزاوار نیست که اهل مدینه و اعراب [بادیه‌نشینِ] پیرامونِ آنان، از [همراهى با] رسول الله بازمانند و حفظ جان خویش را بر او مقدّم شمارند؛ زیرا در راه الله هیچ تشنگى و رنج و گرسنگی‌ای به آنان نمى‌رسد و در هیچ مكانى كه كافران را به خشم مى‌آورد قدم نمى‌گذارند و به دشمن هیچ [گزند و] دستبردی نمی‌رسانند، مگر اینكه به [پاداشِ] آن، کاری شایسته برای آنان [در نامه‌های اعمالشان] ثبت مى‌شود. به راستی که الله پاداش نیكوكاران را ضایع نمى‌گردانَد.');
INSERT INTO "islamhouse" VALUES(9,121,'9:121','و هیچ مال کوچک یا بزرگی را [در راه الله] انفاق نمی‌کنند و [در راه جهاد] از هیچ سرزمینی نمی‌گذرند مگر اینکه [پاداش آن] برایشان ثبت می‌شود تا الله بر اساس نیکوترین [طاعت و] کردارشان به آنان پاداش دهد.');
INSERT INTO "islamhouse" VALUES(9,122,'9:122','و سزاوار نیست که مؤمنان همگی رهسپار [جهاد] شوند. پس چرا از هر گروهی از آنان، عده‌ای رهسپار نمی‌شوند [و عده‌ای نیز همراه پیامبر نمی‌مانند] تا [به سبب آنچه از او فرامی‌گیرند] در دین آگاهی بیابند و قوم خود را ـ وقتی به سویشان بازگشتندـ بیم دهند؟ باشد كه آنان [از كیفر الهى بترسند و] بپرهیزند.');
INSERT INTO "islamhouse" VALUES(9,123,'9:123','ای کسانی که ایمان آورده‌اید، با کافرانی که نزدیک [محل زندگی] شما هستند بجنگید [تا خطرشان را دفع نمایید]؛ و آنان باید در شما شدت و خشونت بیابند؛ و بدانید که الله با پرهیزگاران است.');
INSERT INTO "islamhouse" VALUES(9,124,'9:124','و هنگامی‌ که سوره‌ای نازل می‌گردد، بعضی از آنان [به دیگران] می‌گوید: «این [سوره]، به ایمانِ کدام‌یک از شما افزود؟». اما کسانی که ایمان آورده‌اند، بر ایمانشان می‌افزاید و [به آن] شادمان می‌گردند.');
INSERT INTO "islamhouse" VALUES(9,125,'9:125','و اما کسانی ‌که در دل‌هایشان بیماری [شک و نفاق] است، پلیدی‌ای بر پلیدی‌شان می‌افزاید و در حالی که کافرند می‌میرند.');
INSERT INTO "islamhouse" VALUES(9,126,'9:126','آیا آنان نمی‌بینند که هر سال، یک یا دو بار آزمایش می‌شوند؟ اما نه توبه می‌کنند و نه پند می‌پذیرند.');
INSERT INTO "islamhouse" VALUES(9,127,'9:127','و هنگامی‌ که سوره‌ای نازل می‌شود [که احوال منافقان را بیان نموده است]، بعضی از آنان به بعضی دیگر نگاه می‌کنند [و می‌گویند:] «آیا کسی شما را می‌بیند؟» سپس [مخفیانه از مجلس پیامبر] بازمی‌گردند. الله دل‌هایشان را [از خیر و هدایت] بازگردانده است؛ زیرا آنان گروهی‌ هستند که نمی‌فهمند.');
INSERT INTO "islamhouse" VALUES(9,128,'9:128','یقیناً پیامبری از [میان] خود شما به سویتان آمد که رنج‌دیدنتان بر او [گران و] دشوار است و بر [هدایت] شما سخت اصرار دارد و [نسبت] به مؤمنان، دلسوز [و] مهربان است.');
INSERT INTO "islamhouse" VALUES(9,129,'9:129','پس [ای پیامبر،] اگر [مشرکان و منافقان از حق] رویگردان شدند، بگو: «الله برای من کافی است. هیچ معبودی [به‌حق] جز او نیست؛ تنها بر او توکل کردم و او پروردگارِ عرش بزرگ است».');
INSERT INTO "islamhouse" VALUES(10,1,'10:1','الر [= الف. لام. را]. این آیات کتاب پرحکمت است.');
INSERT INTO "islamhouse" VALUES(10,2,'10:2','آیا برای مردم موجب شگفتی است که به مردی از خودشان وحی کرده‌ایم که: «به مردم [دربارۀ عذاب آخرت] هشدار بده و به کسانی‌ که ایمان آورده‌اند بشارت بده که برای آنان نزد پروردگار‌شان پاداشی نیکوست»؟ کافران گفتند: «این [مرد] قطعاً جادوگری آشکار است».');
INSERT INTO "islamhouse" VALUES(10,3,'10:3','بی‌گمان، پروردگار شما الله است که آسمان‌ها و زمین را در شش روز آفرید؛ سپس [آن گونه كه شايسته جلال و مقام اوست] بر عرش قرار گرفت. او کار [جهان] را تدبیر می‌کند [و در روز قیامت] هیچ شفاعتگری نیست مگر پس از اجازۀ او. این الله است، پروردگارتان؛ پس او را عبادت کنید. آیا پند نمی‌گیرید؟');
INSERT INTO "islamhouse" VALUES(10,4,'10:4','بازگشتِ همۀ شما به سوی اوست. وعدۀ الله [در مورد قیامت] حق است. اوست که آفرینش را آغاز می‌کند، آنگاه [پس از مرگ] آن را بازمی‌گردانَد تا کسانی را که ایمان آورده‌اند و کارهای شایسته کرده‌اند، به عدالت پاداش دهد؛ و کسانی ‌که کفر ورزیدند ـ به سزای آنکه کافر بودند ـ شربتی از آبِ جوشان و عذابی دردناک [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(10,5,'10:5','اوست که خورشید را درخشان نمود و ماه را تابان کرد و جایگاه‌ها[ی مشخصی] برایش قرار داد تا [با تغییر وضع ماه] شمارشِ سال‌ها و حساب [زندگی خود] را بدانید. الله اینها را جز به‌حق نیافریده است. او نشانه‌[های خود] را برای گروهی که می‌دانند، به تفصیل بیان می‌کند.');
INSERT INTO "islamhouse" VALUES(10,6,'10:6','بی‌گمان، در آمد و شدِ شب و روز و آنچه الله در آسمان‌ها و زمین آفریده است برای کسانی که پروا می‌کنند، نشانه‌ها[ی قدرت الهی] است.');
INSERT INTO "islamhouse" VALUES(10,7,'10:7','کسانی که به دیدارِ ما امید [و باور] ندارند و به زندگانی دنیا راضی شدند و به آن دل بسته‌اند و کسانی که از آیات ما غافلند،');
INSERT INTO "islamhouse" VALUES(10,8,'10:8','اینان به سزای آنچه انجام می‌دادند، جایگاهشان آتش [دوزخ] است.');
INSERT INTO "islamhouse" VALUES(10,9,'10:9','بی‌تردید، کسانی ‌که ایمان آورده‌اند و کارهای شایسته کرده‌اند، پروردگارشان آنان را به پاسِ ایمانشان، به باغ‌های [بهشتِ پُر]نعمت هدایت می‌کند که جویبارها از زیر [درختان] آن جاری است.');
INSERT INTO "islamhouse" VALUES(10,10,'10:10','دعایشان در آنجا [این است:] «الهی، تو پاک و منزّهی» و درودشان [در آنجا] «سلام» است و پایان دعایشان این است که: «حمد و سپاس، مخصوص الله ـ پروردگار جهانیان ـ است».');
INSERT INTO "islamhouse" VALUES(10,11,'10:11','و اگر [الله] براى مردم، بدی را با همان شتاب می‌خواست که آنان نیکی را می‌خواهند [و نفرین‌هایی را که در حق خود و اموال و فرزندانشان می‌کنند مستجاب می‌کرد]، قطعاً اَجلشان فرا ‌رسیده بود. اما كسانى را كه به دیدار ما امید [و باور] ندارند، وامی‌گذاریم تا در [گمراهی و] سرکشی‌شان سرگردان بمانند.');
INSERT INTO "islamhouse" VALUES(10,12,'10:12','و چون به انسان گزندی می‌رسد، [در هر حال که باشد] به پهلو خوابیده یا نشسته یا ایستاده ما را می‌خواند. اما همین ‌که آن گزند را از وی برطرف کردیم، چنان می‌رود که گویی هرگز ما را برای دفع گزندی که به او رسیده بود نخوانده است. به همین صورت، برای اسرافکاران [نیز] آنچه می‌کردند [در نظرشان] آراسته شده است.');
INSERT INTO "islamhouse" VALUES(10,13,'10:13','و [ای مشرکان،] بی‌تردید، ما امت‌های پیش از شما را هنگامی‌ که ظلم کردند هلاک کردیم، در حالی ‌که پیامبرانشان با دلایل روشن به سویشان آمدند و[لى] آنان بر آن نبودند كه ایمان بیاورند. ما گروه مجرمان را این گونه کیفر می‌دهیم.');
INSERT INTO "islamhouse" VALUES(10,14,'10:14','سپس بعد از [نابودی] آنان، شما [مردم] را جانشینان آنها [در] زمین قرار دادیم تا بنگریم که چگونه رفتار می‌کنید.');
INSERT INTO "islamhouse" VALUES(10,15,'10:15','و هنگامی که آیات روشن ما بر آنان خوانده می‌شود، کسانی که به دیدارِ ما [در قیامت] امید ندارند می‌گویند: «[ای محمد،] قرآنی غیر از این بیاور [که مطابق میل ما باشد] یا آن را تغییر بده». [تو ای پیامبر، به آنان] بگو: «من حق ندارم که آن را از پیش خود تغییر دهم و پیروی نمی‌کنم مگر از چیزی که بر من وحی می‌شود و اگر از پروردگارم نافرمانی کنم، از عذاب روز بزرگ می‌ترسم».');
INSERT INTO "islamhouse" VALUES(10,16,'10:16','بگو: «اگر الله می‌خواست، نه من آن را بر شما می‌خواندم و نه او [= الله] شما را از آن آگاه می‌کرد. به راستی که پیش از این [دعوت،] عمری را در میان شما گذرانده‌ام. آیا نمی‌اندیشید؟»');
INSERT INTO "islamhouse" VALUES(10,17,'10:17','پس کیست ستمکارتر از آن کس که بر الله دروغ ‌بندد یا آیاتش را تکذیب کند؟ بی‌گمان، مجرمان رستگار نخواهند شد.');
INSERT INTO "islamhouse" VALUES(10,18,'10:18','و [مشرکان] به جای الله چیزهایی را عبادت می‌کنند که نه به آنان زیانی می‌رساند و نه سودی به آنان می‌بخشد و می‌گویند: «اینها، [واسطه‌ها و] شفاعتگرانِ ما نزد الله هستند». [ای پیامبر،] بگو: «آیا [به گمان خود،] الله را از چیزی [= شریکی] آگاه می‌سازید که در آسمان‌ها و زمین [برایش قائلید و خود] نمی‌داند؟» او تعالی منزّه است و از چیزهایی که با وی شریک می‌سازند، برتر است.');
INSERT INTO "islamhouse" VALUES(10,19,'10:19','[در آغاز،] مردم جز امتی واحد [که الله را به یگانگی عبادت می‌کردند] نبودند، سپس اختلاف کردند؛ و اگر پیشتر [در مورد مهلت به گناهکاران و حسابرسی در قیامت] سخنی از جانب پروردگارت بیان نشده بود، یقیناً [در همین دنیا] دربارۀ آنچه با هم اختلاف مى‌كنند، میانشان داورى مى‌شد.');
INSERT INTO "islamhouse" VALUES(10,20,'10:20','و [مشرکان] می‌گویند «چرا [معجزه و] نشانه‌ای از [جانب] پروردگارش بر او نازل نشده است؟». [ای پیامبر،] بگو: «[آگاهی از] غیب، تنها از آنِ الله است؛ پس انتظار بکشید که من [نیز] با شما از منتظرانم».');
INSERT INTO "islamhouse" VALUES(10,21,'10:21','و هنگامی که به مردم ـ پس از [زیان و] رنجی که به آنان رسیده است ـ [نعمت و] رحمتی بچشانیم، به‌ناگاه در آیات ما [تمسخر و] نیرنگ می‌کنند. بگو: «الله در نیرنگ‌زدن سریع‌تر است». بی‌تردید، فرستادگان [= فرشتگان] ما هر چه را نیرنگ می‌کنید، می‌نویسند.');
INSERT INTO "islamhouse" VALUES(10,22,'10:22','[ای مردم،] او همان ذاتی است که شما را در خشکی و دریا به حرکت درمی‌آورَد تا آنگاه که در [دریا بر] کشتی قرار می‌گیرید و [کشتی‌ها] با بادی دلپذیر آنان را [به سوی مقصد] حرکت می‌دهند و [سرنشینان] به آن شادمان می‌شوند. [ناگهان] طوفان شدیدی می‌وزد و موج از هر طرف [به سویشان] می‌آید و گمان می‌کنند که راهِ گریزی ندارند [و هلاک خواهند شد؛ آنگاه] الله را از روی اخلاص می‌خوانند که: «اگر ما را از این [گرفتاری] نجات دهی، قطعاً از سپاسگزاران خواهیم شد».');
INSERT INTO "islamhouse" VALUES(10,23,'10:23','اما وقتی [الله] آنان را نجات می‌دهد، به ناحق در زمین سرکشی [و ستم] می‌کنند. ای مردم، سرکشی [و ستمِ] شما تنها به زیان خودتان است؛ [چرا که فقط] بهره[ی ناچیزی از] زندگى دنیا [مى‌برید و] سپس بازگشتتان به سوى ما خواهد بود؛ آنگاه شما را از آنچه [در دنیا] انجام می‌دادید، باخبر می‌سازیم.');
INSERT INTO "islamhouse" VALUES(10,24,'10:24','جز این نیست که مثال زندگى دنیا همچون آبى است كه از آسمان فروریختیم؛ پس گیاهان [گوناگونِ] زمین ـ از آنچه مردم و دام‌ها مى‌خورند ـ با آن درآمیخت [و رویید] تا آنگاه كه زمین پیرایۀ خود را [با گل‌ها و گیاهان] برگرفت و آراسته شد و ساکنانش پنداشتند كه آنان بر آن توانایی دارند [تا از محصولاتش بهره‌مند گردند؛ ولی ناگهان] شبى یا روزى، فرمان [ویرانىِ‌] ما آمد و آن [کِشت و میوه] را چنان درو كردیم كه گویى دیروز هیچ نبوده است. ما نشانه‌ها[ى خود] را براى مردمى كه می‌اندیشند، اینچنین به روشنى بیان مى‌كنیم.');
INSERT INTO "islamhouse" VALUES(10,25,'10:25','و الله [شما را] به سراى سلامت [= بهشت] فرامى‌خوانَد و هر کس را بخواهد به راه راست هدایت مى‌كند.');
INSERT INTO "islamhouse" VALUES(10,26,'10:26','برای کسانی که نیکی کرده‌اند، پاداش نیک [= بهشت] است و افزون بر آن، [رؤیت باری تعالی است؛] و تیرگی و خواری، چهره‌هایشان را نخواهد پوشاند. اینان اهل بهشتند [و] جاودانه در آن خواهند ماند.');
INSERT INTO "islamhouse" VALUES(10,27,'10:27','و کسانی‌ که مرتکب بدی‌ها شدند، [بدانند] سزای [هر] بدی، همانند آن است؛ و خواری [چهره‌های] آنان را می‌پوشانَد و در برابر [عذاب] الله هیچ [مانع و] محافظی ندارند. [چنان می‌شوند که] گویی چهره‌هایشان با پاره‌ای از شبِ تاریک پوشانده شده است. اینان اهل آتشند و جاودانه در آن خواهند ماند.');
INSERT INTO "islamhouse" VALUES(10,28,'10:28','[و ای پیامبر، یاد کن از] روزى كه همۀ آنان را گرد مى‌آوریم؛ آنگاه به كسانى كه شرک ورزیده‌اند مى‌گوییم: «شما و شریكانتان [= معبودانتان] بر جاى خود بمانید»؛ سپس میان آنها جدایى مى‌افكنیم و شریكانشان مى‌گویند: «در حقیقت، شما ما را عبادت نمی‌کردید،');
INSERT INTO "islamhouse" VALUES(10,29,'10:29','و کافی است که الله میان ما و شما گواهى دهد [که ما به شرک‌ورزیِ شما راضی نبودیم و] از عبادت شما کاملاً بى‌خبر بودیم».');
INSERT INTO "islamhouse" VALUES(10,30,'10:30','آنجاست كه هر كسى آنچه را از پیش فرستاده است [بررسی کرده و] مى‌آزماید و همگی به سوی الله ـ کارسازِ حقیقی‌شان‌ـ بازگردانده می‌شوند و دروغ‌هایی که [دربارۀ شفاعت معبود‌ها] می‌بافتند همه محو و نابود می‌گردد.');
INSERT INTO "islamhouse" VALUES(10,31,'10:31','[ای پیامبر، به مشرکان] بگو: «كیست كه از آسمان و زمین به شما روزى مى‌بخشد؟ یا كیست كه بر گوش‌ها و دیدگان حاكم است؟ و كیست كه [موجودِ] زنده را از [مادۀ] بی‌جان بیرون مى‌آورد و [مادۀ] بی‌جان مرده را از [موجودِ] زنده خارج مى‌سازد؟ و كیست كه كار [جهان و جهانیان‌] را تدبیر مى‌كند؟» خواهند گفت: «الله». پس بگو: «آیا پروا نمى‌كنید؟»');
INSERT INTO "islamhouse" VALUES(10,32,'10:32','پس آن الله [یکتا]، پروردگار حقیقی شماست و بعد از حقیقت، جز گمراهى چیست؟ پس چگونه [از حق‌] بازگردانده مى‌شوید؟');
INSERT INTO "islamhouse" VALUES(10,33,'10:33','این گونه سخن پروردگارت دربارۀ كسانى كه [از فرمان او] نافرمانى كردند به حقیقت پیوست كه «آنان ایمان نمى‌آورند».');
INSERT INTO "islamhouse" VALUES(10,34,'10:34','[ای پیامبر، به مشرکان] بگو: «آیا از شریکان[=معبودانِ]تان كسى هست كه آفرینش را آغاز كند و سپس آن را [پس از مرگ] بازگردانَد؟» بگو: «الله است كه آفرینش را آغاز مى‌كند سپس آن را بازمى‌گردانَد؛ پس به كجا منحرف مى‌شوید؟»');
INSERT INTO "islamhouse" VALUES(10,35,'10:35','بگو: «آیا از معبودانتان كسى هست كه به سوى حق رهبرى كند؟» بگو: «[تنها] الله است كه به سوى حق رهبرى مى‌كند؛ پس آیا كسى كه به سوى حق رهبرى مى‌كند سزاوارتر است پیروى شود یا كسى كه خود راه نمى‌یابد مگر آنكه هدایتش كنند؟ پس شما را چه شده است؟ چگونه داوری مى‌كنید؟»');
INSERT INTO "islamhouse" VALUES(10,36,'10:36','و بیشتر آنان جز از گمان پیروى نمى‌كنند؛ [در حالى كه‌] گمان هرگز [انسان را] از [شناخت] حق بی‌نیاز نمی‌گرداند. بی‌تردید، الله به آنچه مى‌كنند داناست.');
INSERT INTO "islamhouse" VALUES(10,37,'10:37','و نشاید كه این قرآن از جانب غیرِ الله [و] به دروغ ساخته شده باشد؛ بلكه تصدیق‌کننده کتاب‌هایی است كه پیش از آن آمده است و شرح [احکام] آنهاست و تردیدى در آن نیست [و] از [جانبِ] پروردگار جهانیان [نازل شده] است.');
INSERT INTO "islamhouse" VALUES(10,38,'10:38','یا می‌گویند: «[محمد] آن [سخنان] را به دروغ [به الله] نسبت داده است؟» بگو: «اگر راست می‌گویید، یک سوره همانند آن بیاورید و به جز الله، هر‌ کس را می‌توانید [به یاری] بخوانید».');
INSERT INTO "islamhouse" VALUES(10,39,'10:39','[چنین نیست] بلكه چیزى را تکذیب کردند كه به شناختش احاطه نداشتند و هنوز واقعیتِ آن [و حقیقت اخبارش] برایشان روشن نشده است. كسانى كه پیش از آنان بودند نیز [پیامبران و آیات الله را] همین گونه تکذیب کردند. پس [ای پیامبر،] بنگر که عاقبت ستمکاران چگونه بود.');
INSERT INTO "islamhouse" VALUES(10,40,'10:40','و از میان آنان [= مشرکان] کسی هست که به آن [= قرآن] ایمان می‌آورد و از آنان کسی هست که [هرگز] به آن ایمان نمی‌آورد؛ و پروردگارت به [حال] مفسدان آگاه‌تر است.');
INSERT INTO "islamhouse" VALUES(10,41,'10:41','و اگر [کافران] تو را دروغگو انگاشتند، پس بگو: «[نتیجۀ] عمل من برای خودم و [سزای] عمل شما برای خودتان است. شما از آنچه من می‌کنم بیزارید و من [نیز] از آنچه [شما] می‌کنید بیزارم».');
INSERT INTO "islamhouse" VALUES(10,42,'10:42','و برخى از آنها [در ظاهر] به تو گوش فرامى‌دهند؛ اما مگر تو مى‌توانى ناشنوایان را [که از شنیدنِ کلام حق عاجزند] شنوا كنى، هر چند خردورزی نکنند؟');
INSERT INTO "islamhouse" VALUES(10,43,'10:43','و از میان آنان کسانی هستند که [با چشم ظاهری] تو را می‌نگرند؛ اما آیا تو می‌توانی نابینایان را هدایت کنی، هر چند كه اهل‌بصیرت نباشند؟');
INSERT INTO "islamhouse" VALUES(10,44,'10:44','یقیناً الله هیچ ستمی به مردم نمی‌کند؛ ولی مردم [هستند که با شرک و نافرمانی] به خویشتن ستم می‌کنند.');
INSERT INTO "islamhouse" VALUES(10,45,'10:45','و روزی ‌که [الله] آنان را [در پیشگاه خویش] گرد می‌آورد، [چنان احساس می‌کنند که] گویی جز ساعتی از روز [در دنیا] درنگ نکرده‌اند [و در آنجا] یکدیگر را می‌شناسند. بی‌تردید، کسانی که دیدارِ الله را دروغ می‌انگاشتند، زیان دیدند و هدایت نیافتند.');
INSERT INTO "islamhouse" VALUES(10,46,'10:46','و [ای پیامبر،] اگر بخشی از آنچه [از عذاب] را که به آنان وعده داده‌ایم به تو نشان دهیم یا جان تو را [پیش از عذابِ آنان‌] بگیریم، [در هر حال،] بازگشتشان به سوی ماست؛ آنگاه الله بر آنچه می‌کنند گواه است.‌');
INSERT INTO "islamhouse" VALUES(10,47,'10:47','هر امتی پیامبری دارد؛ هنگامی که پیامبرشان آمد [برخى مؤمن و برخى كافر شدند؛ و روز قیامت] میان آنان به عدالت داوری می‌شود و [هرگز] به آنان ستم نخواهد شد.');
INSERT INTO "islamhouse" VALUES(10,48,'10:48','[مشرکان به پیامبر] می‌گویند: «اگر [تو و پیروانت] راست می‌گویید، این وعدۀ [عذاب] چه زمانی خواهد بود؟»');
INSERT INTO "islamhouse" VALUES(10,49,'10:49','بگو: «من مالک هیچ زیان و سودی برای خویش نیستم، مگر آنچه الله بخواهد. هر امتى، اَجَلی دارد و آنگاه كه اجلشان به سر آید، نه ساعتى [از آن‌] تأخیر می‌كنند و نه پیشى می‌گیرند».');
INSERT INTO "islamhouse" VALUES(10,50,'10:50','[ای پیامبر، به کافران] بگو: «به من بگویید اگر عذاب او تعالی شبانه یا در [وقتِ] روز بر شما نازل گردد، [چه می‌کنید و] گناهکاران چه چیزی را از آن [عذاب] به شتاب می‌خواهند؟»');
INSERT INTO "islamhouse" VALUES(10,51,'10:51','آیا پس از آنکه [عذاب] واقع شد آن را باور می‌کنید؟ آیا اکنون [ايمان می‌آورید]؟ و حال آنکه [پیشتر] آن [=عذاب] را به شتاب می‌خواستید.');
INSERT INTO "islamhouse" VALUES(10,52,'10:52','سپس به کسانی ‌که ستم کردند گفته می‌شود: «عذاب جاودان را بچشید. آیا جز در برابر آنچه می‌کردید مجازات می‌شوید؟»');
INSERT INTO "islamhouse" VALUES(10,53,'10:53','و [ای پیامبر، مشرکان] از تو خبر مى‌گیرند [که]: «آیا آن [عذاب] حقیقت دارد؟» بگو: «آرى، سوگند به پروردگارم كه قطعاً حقیقت دارد و شما نمى‌توانید [الله را از وقوع معاد و عذاب] درمانده كنید».');
INSERT INTO "islamhouse" VALUES(10,54,'10:54','و هر کس [با ارتکاب شرک و گناه] ستم كرده است، اگر [تمام] آنچه را در زمین وجود دارد [در اختیار] داشته باشد، قطعاً آن را براى [رهایی و] بازخرید [خود از عذاب] خود مى‌دهد و [مشرکان] وقتی عذاب را ببینند، پشیمانى خود را پنهان می‌کنند و به عدالت در میانشان داورى می‌شود و بر آنان ستم نمی‌رود.');
INSERT INTO "islamhouse" VALUES(10,55,'10:55','آگاه باشید! در حقیقت، آنچه در آسمان‌ها و زمین است [همه] از آنِ الله است. آگاه باشید! بی‌تردید، وعدۀ الله [در مورد کیفرِ کافران] راست است؛ ولى بیشتر آنان نمى‌دانند.');
INSERT INTO "islamhouse" VALUES(10,56,'10:56','اوست که زنده می‌کند و می‌میرانَد و [همگی] به سوی او بازگردانده می‌شوید.');
INSERT INTO "islamhouse" VALUES(10,57,'10:57','اى مردم، به راستى براى شما از جانب پروردگارتان موعظه‌اى آمده است و شفایى براى آنچه در دل‌هاست و هدایت و رحمتى براى مؤمنان.');
INSERT INTO "islamhouse" VALUES(10,58,'10:58','بگو: «[مؤمنان] باید به بخشش و رحمت الله شادمان شوند كه این [نعمت] از آنچه مى‌اندوزند بهتر است».');
INSERT INTO "islamhouse" VALUES(10,59,'10:59','بگو: «به من خبر دهید که آنچه از [نعمت و] روزى كه الله برایتان فرستاده است [چرا] بخشى از آن را حرام و [بخشی دیگر را] حلال تلقی کرده‌اید؟» بگو: «آیا الله چنین دستوری به شما داده است یا بر الله دروغ مى‌بندید؟»');
INSERT INTO "islamhouse" VALUES(10,60,'10:60','و کسانی‌ که بر الله دروغ می‌بندند، [دربارۀ] روز قیامت چه می‌پندارند؟ بی‌گمان، الله بر [همۀ] مردم فضل [و بخشش] دارد؛ ولی بیشتر آنان سپاس نمی‌گزارند.');
INSERT INTO "islamhouse" VALUES(10,61,'10:61','و [ای پیامبر،] تو مشغول هیچ كارى نیستی و هیچ [آیه‌اى از‌] آن [= قرآن] را نمی‌خوانى؛ و [شما نیز ای مردم،] هیچ كارى نمی‌كنید، مگر اینكه آنگاه كه به آن می‌پردازید، ما بر [کارهای] شما گواهیم؛ و هم‌وزنِ ذرّه‌اى ـ‌ [نه] در زمین و نه در آسمان‌ـ از پروردگارت پنهان نیست؛ و [حتی] كوچک‌تر یا بزرگ‌تر از آن [نیز] چیزى نیست، مگر اینكه در كتابى روشن [ثبت شده‌] است.');
INSERT INTO "islamhouse" VALUES(10,62,'10:62','آگاه باشید! دوستان الله نه ترسی خواهند داشت و نه اندوهگین می‌شوند.');
INSERT INTO "islamhouse" VALUES(10,63,'10:63','[همان] کسانی‌ که ایمان آوردند و پرهیزگاری می‌کردند.');
INSERT INTO "islamhouse" VALUES(10,64,'10:64','برای آنان در زندگی دنیا و در آخرت بشارت است. سخنان الله [دربارۀ پیروزی و پاداشِ دو جهان،] تغییرناپذیر است. این همان [رستگاری و] کامیابیِ بزرگ است.');
INSERT INTO "islamhouse" VALUES(10,65,'10:65','[ای پیامبر،] سخن آنان تو را غمگین نکند. بی‌تردید، تمام عزت [و پیروزمندی] از آنِ الله است [و] او شنوای داناست.');
INSERT INTO "islamhouse" VALUES(10,66,'10:66','آگاه باشید! هر کس [و هر چه‌] در آسمان‌ها و زمین است، از آنِ الله است؛ پس كسانى كه به جای الله شریكانى را مى‌خوانند، از چه پیروى مى‌كنند؟ اینان جز از [تردید و] گمان پیروى نمى‌كنند و جز دروغ نمی‌گویند.');
INSERT INTO "islamhouse" VALUES(10,67,'10:67','او ذاتی است كه شب را برایتان پدید آورد تا در آن بیارامید و روز را روشن ساخت [تا در پرتوی نورش به کار بپردازید]. بی‌تردید، در این [امور] براى گروهی كه [سخنِ حق را] مى‌شنوند نشانه‌هاست.');
INSERT INTO "islamhouse" VALUES(10,68,'10:68','[مشرکان] گفتند: «الله فرزندى براى خود اختیار كرده است». منزّه است او تعالی [و] بى‌نیاز است. آنچه در آسمان‌ها و زمین است، از آنِ اوست. [ای مشرکان،] شما بر این [ادعا] دلیلی ندارید. آیا چیزى را كه نمى‌دانید به دروغ به الله نسبت می‌دهید؟');
INSERT INTO "islamhouse" VALUES(10,69,'10:69','بگو: «کسانی‌ که به الله نسبت دروغ می‌دهند، هرگز رستگار نمی‌شوند».');
INSERT INTO "islamhouse" VALUES(10,70,'10:70','آنان بهره‌اى [اندک‌] در دنیا [دارند]؛ سپس بازگشتشان به سوى ماست. آنگاه به [سزاى‌] آنكه كفر مى‌ورزیدند، عذابی سخت به آنان مى‌چشانیم.');
INSERT INTO "islamhouse" VALUES(10,71,'10:71','[ای پیامبر،] داستان نوح را بر آنان بازگو؛ آنگاه كه به قومش گفت: «اى قوم من، اگر ماندنِ من [در میان شما] و اندرز دادن من به آیات الله، بر شما گران آمده است، [بدانید كه من‌] بر الله توكل كرده‌ام؛ پس [در توطئه و] كارتان با شریكان خود، [علیه من] همدست شوید و در کارتان پرده‌پوشی نکنید؛ آنگاه [هر چه در دل دارید و می‌توانید] علیه من به كار بندید و به من مهلت ندهید.');
INSERT INTO "islamhouse" VALUES(10,72,'10:72','اگر [از دعوتِ حق] رویگردان شدید، [بدانید که] من پاداشی از شما نخواسته‌ام؛ [چرا که] پاداشم تنها بر الله است و من دستور یافته‌ام که از تسلیم‌شدگان [اوامر الهی] باشم».');
INSERT INTO "islamhouse" VALUES(10,73,'10:73','اما [قومش] او را دروغگو انگاشتند و ما او و کسانی را كه در كشتى همراهش بودند نجات دادیم و آنان را جانشین [غرق‌شدگان] ساختیم و كسانى را كه آیات ما را دروغ می‌انگاشتند غرق کردیم. پس بنگر که سرانجامِ هشداریافتگان چگونه بود.');
INSERT INTO "islamhouse" VALUES(10,74,'10:74','سپس، بعد از او [= نوح] پیامبرانی به سوی قومشان فرستادیم و معجزات روشن [برایشان] آوردند؛ ولی آنها برآن نبودند که به چیزی ایمان بیاورند که از پیش، آن را دروغ شمرده بودند. بدین‌سان، بر دل‌های تجاوزکاران [از حدود الهی] مُهر می‌نهیم.‌');
INSERT INTO "islamhouse" VALUES(10,75,'10:75','سپس بعد از آنان، موسی و هارون را با معجزات خویش به سوی فرعون و بزرگان قومش فرستادیم؛ و[لی آنان] گردنكشى کردند و قومی گناهکار بودند.');
INSERT INTO "islamhouse" VALUES(10,76,'10:76','اما هنگامی که حق از نزد ما به سویشان آمد، گفتند: «این قطعاً جادویی آشکار است».');
INSERT INTO "islamhouse" VALUES(10,77,'10:77','موسى گفت: «آیا وقتى حق به سویتان آمد، مى‌گویید [جادوست]؟ آیا این جادوست؟ و حال آنكه جادوگران رستگار نمى‌شوند».');
INSERT INTO "islamhouse" VALUES(10,78,'10:78','[فرعونیان] گفتند: «آیا به سوى ما آمده‌اى تا ما را از شیوه‌اى كه پدرانمان را بر آن یافته‌ایم بازگردانى و [عزت و] بزرگى در این سرزمین براى شما دو تن باشد؟ ما هرگز به شما دو نفر ایمان نمی‌آوریم».');
INSERT INTO "islamhouse" VALUES(10,79,'10:79','و فرعون گفت: «تمام جادوگرانِ [ماهر و] دانا را نزد من بیاورید».');
INSERT INTO "islamhouse" VALUES(10,80,'10:80','وقتی جادوگران آمدند، موسی به آنان گفت: «آنچه [از وسایل جادو] می‌خواهید بیفکنید، [اکنون] بیفکنید».');
INSERT INTO "islamhouse" VALUES(10,81,'10:81','هنگامی که افکندند، موسی گفت: «آنچه آورده‌اید، جادوست. یقیناً الله آن را باطل خواهد کرد. بی‌تردید، الله کارِ مفسدان را اصلاح نمی‌کند.');
INSERT INTO "islamhouse" VALUES(10,82,'10:82','و الله با سخنان خویش حق را [ثابت و] پایدار می‌کند؛ هر چند که مجرمان نپسندند».');
INSERT INTO "islamhouse" VALUES(10,83,'10:83','پس هیچ کس به موسی ایمان نیاورد، مگر گروهی از از قومش، [آن هم] با ترسی [شدید] از فرعون و اطرافیانش که [مبادا] آنان را مورد [شکنجه] و آزار قرار دهند؛ و بی‌شک، فرعون در زمین سرکش [و جاه‌طلب] بود و به راستی که او از اسرافکاران [در کفر و جنایت] بود.');
INSERT INTO "islamhouse" VALUES(10,84,'10:84','و موسی گفت: «ای قوم من، اگر به الله ایمان آورده‌اید و اگر اهل تسلیم هستید، پس بر او توکل کنید».');
INSERT INTO "islamhouse" VALUES(10,85,'10:85','آنان گفتند: «بر الله توکل کرده‌ایم. پروردگارا، ما را دستخوشِ فتنه [و شکنجه و آزارِ] گروه ستمکاران قرار نده؛');
INSERT INTO "islamhouse" VALUES(10,86,'10:86','و به [فضل و] رحمت خویش ما را از [شر] گروه کافران رهایی بخش».');
INSERT INTO "islamhouse" VALUES(10,87,'10:87','و به موسی و برادرش [هارون] وحی کردیم که: «برای قوم خود، خانه‌هایی در [سرزمین] مصر برگیرید و خانه‌هایتان را عبادتگاه قرار دهید و نماز را برپا دارید؛ و [ای موسی،] مؤمنان را [به پیروزی و بهشت] بشارت بده».');
INSERT INTO "islamhouse" VALUES(10,88,'10:88','موسی گفت: «پروردگارا، بی‌گمان، تو به فرعون و اطرافیانش زینت و اموالی [فراوان] در زندگی دنیا داده‌ای؛ پروردگارا، تا [به وسیله آن، مردم را] از راهت گمراه کنند. پروردگارا، اموالشان را نابود کن و دل‌هایشان را سخت بگردان که ایمان نیاورند تا [وقتی که] عذاب دردناک را ببینند».');
INSERT INTO "islamhouse" VALUES(10,89,'10:89','[الله] فرمود: «قطعاً دعای شما دو نفر پذیرفته شد؛ پس استقامت کنید و از راه کسانی ‌که نمی‌دانند پیروی نکنید».');
INSERT INTO "islamhouse" VALUES(10,90,'10:90','و [سرانجام] بنی‌اسرائیل را از دریا گذراندیم؛ آنگاه فرعون و سپاهیانش از روی ستم و تجاوز، در پی آنان رفتند تا هنگامی که غرقاب، او [= فرعون] را فراگرفت [با ناامیدی] گفت: «ایمان آوردم که هیچ معبودی [به‌حق] نیست مگر کسی‌ که بنی‌اسرائیل به او ایمان آورده‌اند و من از تسلیم‌شدگانم».');
INSERT INTO "islamhouse" VALUES(10,91,'10:91','[به او گفته شد:] «اینک [ایمان می‌آوری‌]؟ و حال آنکه پیشتر سرپیچی می‌کردی و از مفسدان بودی؟');
INSERT INTO "islamhouse" VALUES(10,92,'10:92','[بدان که] امروز پیکر [بی‌جانِ] تو را بر بلندی [ساحل] می‌اندازیم‌ تا برای آیندگانت مایۀ عبرت باشد؛ و به راستی، بسیاری از مردم از نشانه‌های [توان و تدبیرِ] ما غافلند.');
INSERT INTO "islamhouse" VALUES(10,93,'10:93','و بی‌تردید، ما بنى‌اسرائیل را در جایگاهى شایسته [در سرزمین شام و مصر] جاى دادیم و از نعمت‌های پاكیزه روزی‌شان كردیم. پس آنها اختلاف نكردند، مگر پس از آنكه علم [به حقانیت قرآن] به سویشان آمد. [ای پیامبر،] پروردگارت روز قیامت دربارۀ آنچه اختلاف مى‌كردند، قطعاً در میانشان داوری می‌کند.');
INSERT INTO "islamhouse" VALUES(10,94,'10:94','پس اگر در آنچه بر تو نازل کرده‌ایم تردیدی داری، از کسانی‌ که پیش از تو کتاب [تورات و انجیل] را می‌خواندند سؤال کن. یقیناً حق از طرف پروردگارت به سوی تو آمده است؛ پس هرگز از تردیدکنندگان نباش.');
INSERT INTO "islamhouse" VALUES(10,95,'10:95','و [همچنین] از کسانی نباش که آیات الله را تکذیب کردند که از زیانکاران خواهی بود.');
INSERT INTO "islamhouse" VALUES(10,96,'10:96','بی‌تردید، كسانى كه سخن پروردگارت دربارۀ [کفر در دنیا بر] آنان تحقق یافته است، ایمان نمى‌آورند؛');
INSERT INTO "islamhouse" VALUES(10,97,'10:97','هر چند تمام نشانه‌ها [و پندها] برایشان بیاید؛ [مگر] وقتى كه عذاب دردناک را ببینند.');
INSERT INTO "islamhouse" VALUES(10,98,'10:98','هیچ شهرى نبود كه [اهلش هنگام مشاهده عذاب،] ایمان بیاورد و ایمانش به حال آن سود بخشد، مگر قوم یونس كه چون [به موقع] ایمان آوردند، عذابِ خواری [و رسوایی] در زندگىِ دنیا را از آنان برطرف نمودیم و آنان را تا مدتی [که زنده بودند از نعمت‌های دنیوی] برخوردار ساختیم.');
INSERT INTO "islamhouse" VALUES(10,99,'10:99','و [ای پیامبر،] اگر پروردگارت می‌خواست، قطعاً تمام کسانی که در زمین هستند، ایمان می‌آوردند. پس آیا تو مردم را وادار می‌کنی که مؤمن شوند؟');
INSERT INTO "islamhouse" VALUES(10,100,'10:100','و هیچ کس نمی‌تواند [به ارادۀ خویش] ایمان بیاورد مگر به فرمان الله؛ و [الله] پلیدى [عذاب و خواری] را بر كسانى قرار مى‌دهد كه نمى‌اندیشند.');
INSERT INTO "islamhouse" VALUES(10,101,'10:101','[ای پیامبر، به مشرکان] بگو: «بنگرید كه در آسمان‌ها و زمین چه [نشانه‌ها] است» و[لى همۀ‌] نشانه‌ها و هشدارها، برای گروهى كه ایمان نمى‌آورند، سودی نمى‌بخشد.');
INSERT INTO "islamhouse" VALUES(10,102,'10:102','آیا آنها [چیزی] جز همانند روزگار پیشینیان [و عذاب‌ها و مجازات‌هایشان] را انتظار می‌کشند؟ بگو: «منتظر باشید كه من [نیز] با شما از منتظرانم».');
INSERT INTO "islamhouse" VALUES(10,103,'10:103','سپس [هنگام نزول عذاب‌،] پیامبرانمان و كسانى را كه [به الله] ایمان آورده‌اند نجات مى‌دهیم؛ زیرا حقی بر عهدۀ ماست كه مؤمنان را نجات دهیم.');
INSERT INTO "islamhouse" VALUES(10,104,'10:104','بگو: «ای مردم، اگر در دینِ من تردید دارید، پس [آگاه باشید كه من‌] كسانى را كه شما به جاى الله عبادت می‌کنید، عبادت نمی‌کنم؛ بلكه آن الله را عبادت می‌کنم كه جانتان را می‌گیرد و فرمان یافته‌ام كه از مؤمنان باشم».');
INSERT INTO "islamhouse" VALUES(10,105,'10:105','و [به من امر شده است‌] كه: «حقگرایانه به این دین روى آور و هرگز در زمرۀ مشركان نباش.');
INSERT INTO "islamhouse" VALUES(10,106,'10:106','و به جای الله، چیزى را كه سود و زیانى به تو نمى‌رساند [به عبادت و نیازخواهی] نخوان كه اگر چنین كنى، قطعاً از ستمكاران [مشرک] خواهی بود.');
INSERT INTO "islamhouse" VALUES(10,107,'10:107','و اگر الله زیانی به تو برساند، هیچ بازدارنده‌ای جز او تعالی برایش نیست؛ و اگر برایت ارادۀ خیری نماید، برای فضل [و بخششِ] او هیچ مانعی نیست. [او بخشش خویش را] به هر یک از بندگانش که بخواهد، می‌رساند و او آمرزندۀ مهربان است».');
INSERT INTO "islamhouse" VALUES(10,108,'10:108','بگو: «اى مردم، از جانب پروردگارتان حق برای شما آمده است؛ پس هر کس هدایت یابد، به سود خویش هدایت مى‌یابد و هر کس گمراه گردد، به زیان خویش گمراه مى‌گردد؛ و من [مراقب و] کارگزارِ شما نیستم».');
INSERT INTO "islamhouse" VALUES(10,109,'10:109','و [ای پیامبر،] از آنچه بر تو وحى مى‌شود پیروى كن و شكیبا باش تا الله [بین تو و کافران‌] داورى كند؛ و او تعالی بهترین داور است.');
INSERT INTO "islamhouse" VALUES(11,1,'11:1','الر [= الف. لام. را]. این [قرآن،] كتابى است كه آیات آن استحكام یافته [که هیچ خلل و نقصانی در آن وجود ندارد]، سپس از جانب [ذات] حكیمى آگاه، به روشنى بیان شده است.');
INSERT INTO "islamhouse" VALUES(11,2,'11:2','[پیام پیامبر این است] که جز الله را عبادت نکنید [که] بی‌تردید، من از سوی او برایتان بیم‌دهنده و بشارتگر هستم.');
INSERT INTO "islamhouse" VALUES(11,3,'11:3','و اینکه از پروردگارتان آمرزش بخواهید، سپس به درگاهش توبه کنید تا شما را [در زندگی دنیا‌] تا سرآمدی معیّن، به خیر و خوبی بهره‌مند سازد و به هر صاحب فضیلتی [که الله را به یگانگی عبادت نموده و نیکوکاری پیشه کرده است، پاداش و] فزونی بخشد؛ و اگر [از ایمان] روی بگردانید، [بدانید که من] به راستی از عذاب روزی سهمگین برایتان می‌ترسم‌.');
INSERT INTO "islamhouse" VALUES(11,4,'11:4','[شما ای مردم،] بازگشتتان به سوى الله است و او بر هر چیزى تواناست.');
INSERT INTO "islamhouse" VALUES(11,5,'11:5','آگاه باشید! آنها [کفر را] در سینه‌هایشان نهان می‌کنند تا آن را از او [= الله] پنهان دارند. آگاه باشید! آنگاه كه آنان جامه‌هایشان را بر سر مى‌كشند، الله آنچه پنهان می‌سازند و آنچه آشكار مى‌دارند، [همه را] مى‌داند. بی‌تردید، او به اسرارِ سینه‌ها داناست.');
INSERT INTO "islamhouse" VALUES(11,6,'11:6','و هیچ جنبنده‌اى در زمین نیست، مگر [اینكه‌] روزی‌اش بر عهدۀ الله است و [او] قرارگاه و محل مرگش را مى‌داند. همه [اینها] در كتابى روشن [ثبت‌] است.');
INSERT INTO "islamhouse" VALUES(11,7,'11:7','و اوست كه آسمان‌ها و زمین را در شش روز آفرید و [پیش از آن،] عرشِ او بر آب بود، تا شما را بیازماید كه كدامتان نیكوكارترید. و اگر [به مشرکان] بگویى: «شما پس از مرگ برانگیخته مى‌شوید»، كسانى كه كفر ورزیدند می‌گویند: «این [قرآن چیزی] نیست مگر جادویی آشكار».');
INSERT INTO "islamhouse" VALUES(11,8,'11:8','و اگر ما عذابشان را تا مدت محدودی به تأخیر بیندازیم، البته [از روی تمسخر] می‌گویند: «چه چیزی مانع [وقوعِ] آن شده است؟» آگاه باشید! روزی‌ که [عذاب] به سراغشان بیاید، از آنان بازگردانده نخواهد شد و آنچه را به مسخره می‌گرفتند، دامنگیرشان خواهد شد.');
INSERT INTO "islamhouse" VALUES(11,9,'11:9','و اگر از جانب خویش رحمتی به انسان بچشانیم، سپس آن را از او بگیریم، قطعاً دلسرد [و] ناسپاس می‌گردد.');
INSERT INTO "islamhouse" VALUES(11,10,'11:10','و اگر پس از رنجی كه به او رسیده است نعمتى به او بچشانیم قطعاً می‌گوید: «سختی‌ها [و ناگواری‌ها] از من دور شد». حقا که او [از روی ناسپاسی] سَرخوش و [به داشتن نعمت‌ها] فخرفروش است.');
INSERT INTO "islamhouse" VALUES(11,11,'11:11','مگر کسانی ‌که شکیبایی نمودند و کارهای نیکو انجام دادند [که] برای اینان آمرزش و پاداش بزرگی [در پیش] است.');
INSERT INTO "islamhouse" VALUES(11,12,'11:12','و [ای پیامبر،] مبادا [ابلاغِ] برخى از آنچه را كه به سویت وحى مى‌شود ترک کنی و سینه‌ات از آن به تنگ آید که [مشرکان] مى‌گویند: «چرا گنجى بر او نازل نگشته است یا فرشته‌اى با او نیامده است؟». جز این نیست که تو فقط بیم‌دهنده‌اى و الله [مراقب و] کارگزارِ هر چیزى است.');
INSERT INTO "islamhouse" VALUES(11,13,'11:13','یا می‌گویند: «[محمد] آن [کتاب‌] را برساخته است». بگو: «اگر راست می‌گویید، ده سورۀ برساخته همانند آن بیاورید و جز الله هر ‌کس را می‌توانید به یاری بخوانید».');
INSERT INTO "islamhouse" VALUES(11,14,'11:14','پس اگر آنان دعوتتان را نپذیرفتند، بدانید که [قرآن] فقط به علم الله نازل شده است و اینکه هیچ معبودی [به‌حق] جز او نیست؛ پس آیا تسلیم [امرِ الله و پیامبر] مى‌شوید؟');
INSERT INTO "islamhouse" VALUES(11,15,'11:15','کسانی که زندگی دنیا و زیور آن را می‌خواهند، [پاداش] کارهایشان را در این [دنیا] به تمامی به آنان می‌دهیم و در آن [پاداش، هیچ] کم و کاستی نخواهند دید.');
INSERT INTO "islamhouse" VALUES(11,16,'11:16','اینان کسانی هستند که در آخرت جز آتش [دوزخ، هیچ نصیبی] نخواهند داشت و آنچه در دنیا انجام داده‌اند، بر باد رفته است و آنچه می‌کردند، تباه است.');
INSERT INTO "islamhouse" VALUES(11,17,'11:17','آیا کسی که از سوی پروردگارش بر دلیل روشنی [از بصیرت و بینش] متکی باشد و [قرآن، به عنوان] گواهی از جانب او [تعالی] به دنبال آن آید [و آن را تصدیق نماید]، و قبل از آن [= قرآن]، کتاب موسی که پیشوا و مایۀ رحمت بود [بر حقانیّت آن گواهی می‌دهد، همانند کافران گمراه است که چنین دلایلی ندارند؟!] اینان [= مسلمانان] به آن [کتاب] ایمان دارند؛ و هر کسی از گروه‌ها[ی مختلف مشرکان] که به آن کفر بورزد [و آن را انکار کند] آتش دوزخ وعده‌گاهِ اوست. پس [ای پیامبر،] از [قرآن و حقیقتِ وعده‌های‌] آن در تردید مباش. بی‌تردید، آن [کتابی] راست و درست از سوی پروردگار توست؛ ولی بیشتر مردم ایمان نمی‌آورند.');
INSERT INTO "islamhouse" VALUES(11,18,'11:18','و کیست ستمکارتر از آن کس که [با نسبت دادن فرزند یا شریک،] بر الله دروغ می‌بندد؟ آنان [روز قیامت] بر پروردگارشان عرضه می‌شوند و گواهان [= پیامبران و فرشتگان] می‌گویند: «اینها همان کسانی هستند که بر پروردگارشان دروغ می‌بستند». هان! لعنت الله بر ستمکاران [مشرک] باد!');
INSERT INTO "islamhouse" VALUES(11,19,'11:19','[همان] کسانی ‌که [مردم را] از راه الله بازمی‌دارند و آن را منحرف می‌خواهند؛ و همان‌ها آخرت را باور ندارند.');
INSERT INTO "islamhouse" VALUES(11,20,'11:20','آنان در زمین ناتوان‌کننده[ی الله از وقوع عذاب خویش] نیستند و جز الله، دوستانى ندارند. عذاب برایشان چندین برابر مى‌شود [چرا که] آنان نه توان شنیدن [حق را] داشتند و نه [حقایق را] مى‌دیدند.');
INSERT INTO "islamhouse" VALUES(11,21,'11:21','آنان کسانی هستند كه به خویش زیان زدند و دروغ‌هایی که می‌بافتند، [همه] محو و نابود شد.');
INSERT INTO "islamhouse" VALUES(11,22,'11:22','ناگزیر، آنان در آخرت از همه زیانکارترند.');
INSERT INTO "islamhouse" VALUES(11,23,'11:23','کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند و در برابر پروردگارشان فروتنی نموده‌اند، اینان یقیناً بهشتی‌اند [و] هم اینان در آن جاودانند.');
INSERT INTO "islamhouse" VALUES(11,24,'11:24','مَثَل این دو گروه [کافر و مؤمن]، همانند نابینا و ناشنوا [از یک سو] و بینا و شنوا [از سوی دیگر] است. آیا با یکدیگر برابرند؟ آیا پند نمی‌گیرید؟');
INSERT INTO "islamhouse" VALUES(11,25,'11:25','و به راستی ما نوح را به سوی قومش فرستادیم [با این پیام] که: «به راستی که من بیم‌دهنده‌ای‌ آشکار برایتان هستم.');
INSERT INTO "islamhouse" VALUES(11,26,'11:26','جز الله را عبادت نکنید [که] بی‌تردید، من از عذاب روزی دردناک بر شما می‌ترسم».');
INSERT INTO "islamhouse" VALUES(11,27,'11:27','بزرگانی از قوم او که کفر ورزیده بودند، [در پاسخ] گفتند: «تو را جز بشری همانند خویش نمی‌بینیم و جز فرومایگان ما ـ [آن هم ندانسته و‌] نسنجیده‌‌ـ [کسی دیگر را] نمی‌بینیم که از تو پیروی کرده باشد؛ و برایتان [هیچ] برتری‌ای بر خود نمی‌بینیم؛ بلکه شما را دروغگو می‌پنداریم»‌.');
INSERT INTO "islamhouse" VALUES(11,28,'11:28','[نوح] گفت: «ای قوم من، به من بگویید [که] اگر از سوی پروردگارم دلیل [و معجزۀ] آشکاری داشته باشم و او از جانب خویش رحمتی [= نبوت] به من بخشیده باشد و [این موهبت] از دید شما پوشیده باشد، آیا ما می‌توانیم شما را به [پذیرشِ] آن وادار کنیم و حال آنکه کراهت دارید؟');
INSERT INTO "islamhouse" VALUES(11,29,'11:29','و ای قوم من، برای این [رسالت‌، من هیچ ثروت و] مالى از شما درخواست نمى‌كنم. پاداش من جز بر [عهدۀ] الله نیست؛ و كسانى را كه ایمان آورده‌اند از خود نمى‌رانم. بی‌تردید، [روز قیامت] آنان ملاقات‌کنندۀ پروردگارِ خویشند؛ ولی من شما را گروهی مى‌بینم که نادانی می‌کنید.');
INSERT INTO "islamhouse" VALUES(11,30,'11:30','و ای قوم من، اگر من آنان[= مومنان] را طرد کنم، کیست که مرا در برابر [کیفرِ] الله یاری رساند؟ پس آیا پند نمی‌گیرید؟');
INSERT INTO "islamhouse" VALUES(11,31,'11:31','و به شما نمى‌گویم كه گنجینه‌هاى الله نزد من است و غیب [نیز] نمى‌دانم و نمى‌گویم كه من فرشته‌ام و دربارۀ كسانى كه به چشم شما حقیر مى‌آیند، نمى‌گویم كه الله خیرى نصیبشان نخواهد كرد. الله به آنچه در دل‌هایشان وجود دارد آگاه‌تر است. [اگر چنین بگویم،‌] در آن صورت، قطعاً از ستمكاران خواهم بود».');
INSERT INTO "islamhouse" VALUES(11,32,'11:32','[قومش] گفتند: «ای نوح، به راستی که با ما جدال کردی و بسیار [هم] جدال کردی؛ پس اگر راست می‌گویی، آنچه [از عذاب] را که به ما وعده می‌دهی بیاور».');
INSERT INTO "islamhouse" VALUES(11,33,'11:33','[نوح] گفت: «تنها الله است كه اگر بخواهد، آن را بر [سرِ] شما مى‌آورَد و شما ناتوان‌کننده [او از عذاب کردن] نیستید.');
INSERT INTO "islamhouse" VALUES(11,34,'11:34','و اگر من بخواهم شما را نصیحت کنم، نصیحت کردنِ من سودی برایتان ندارد اگر الله بخواهد [که نابود و] گمراهتان سازد. او پروردگار شماست و [همگی] به سویش بازگردانده می‌شوید».');
INSERT INTO "islamhouse" VALUES(11,35,'11:35','آیا [مشرکان] می‌گویند: «او [= محمد] این سخنان را به دروغ به الله نسبت داده است؟ بگو: «اگر من اینها را از پیش خود ساخته باشم و به او نسبت دهم، گناهش بر [عهدۀ] من است و[لی] من از گناهان شما بیزارم».');
INSERT INTO "islamhouse" VALUES(11,36,'11:36','و به نوح وحی شد که: «از قوم تو ـ‌ جز کسانی که ایمان آورده‌اندـ دیگر هیچ کس ایمان نمی‌آورد؛ پس بر آنچه می‌کردند غم مخور.');
INSERT INTO "islamhouse" VALUES(11,37,'11:37','و [اکنون] کِشتی را زیر نظر ما و به وحی [و راهنماییِ] ما بساز و دربارۀ [گذشت از] كسانى كه ستم كردند با من سخن مگو [چرا که] آنها قطعاً غرق‌شدنى هستند».');
INSERT INTO "islamhouse" VALUES(11,38,'11:38','و [نوح‌] کشتی را می‌ساخت و هر گاه بزرگانی از قومش بر او می‌گذشتند، مسخره‌اش می‌کردند. [او] می‌گفت: «اگر ما را مسخره کنید، ما [نیز] به همان صورت که ما را مسخره می‌کنید، شما را مسخره خواهیم کرد.');
INSERT INTO "islamhouse" VALUES(11,39,'11:39','به زودى خواهید دانست كه عذاب [الهى‌] سراغ چه كسى مى‌آید كه خوارش سازد و عذابى پایدار بر او فرود آید».');
INSERT INTO "islamhouse" VALUES(11,40,'11:40','[این حالت همچنان ادامه داشت] تا زمانی که فرمان [عذاب] فرارسید و [آب از] تنور فوران كرد. [به نوح] گفتیم: «از هر [صنف حیوان] یک جفت [نر و ماده] در آن [کشتی] سوار کن و [همچنین] خاندان خود را؛ مگر کسی‌ که وعدۀ [هلاکت] قبلاً بر او مقرر شده است و [نیز] کسانی‌ را که ایمان آورده‌اند [سوار کن]»؛ و جز [افراد] اندکی همراه او ایمان نیاورده بودند.');
INSERT INTO "islamhouse" VALUES(11,41,'11:41','[نوح] گفت: «در آن [کشتی] سوار شوید. حرکت و توقفش با نام [و خواستِ] الله است. بی‌تردید، پروردگارم آمرزندۀ مهربان است».');
INSERT INTO "islamhouse" VALUES(11,42,'11:42','و آن [كشتى‌،] آنان را در میان امواجى کوه‌پیکر [پیش] مى‌بُرد و نوح پسرش را كه در كنارى [ایستاده] بود صدا زد: «پسرم، با ما سوار شو و همراه كافران نباش».');
INSERT INTO "islamhouse" VALUES(11,43,'11:43','[پسرِ نوح] گفت: «به کوهی پناه خواهم ‌برد که مرا از آب محافظت ‌کند». [نوح] گفت: «امروز هیچ نگهدارنده‌ای در برابر فرمان الله نیست، مگر کسی ‌که [الله] به او رحم کند» و[لی ناگاه] موج در میان آن دو فاصله انداخت؛ پس [پسر نوح نیز] از غرق‌شدگان گردید.');
INSERT INTO "islamhouse" VALUES(11,44,'11:44','و گفته شد: «ای زمین، آبت را فرو بَر و ای آسمان، [از باریدن] باز ایست؛ و آب فرونشست و کار به انجام رسید و [کشتی] بر [کوه] جودی قرار گرفت؛ و گفته شد: «دوری [از رحمت الهی] بر گروه ستمکاران باد!»');
INSERT INTO "islamhouse" VALUES(11,45,'11:45','و نوح پروردگارش را ندا داد و گفت: «پروردگارا، پسرم از خاندان من است و بی‌گمان، وعدۀ تو [دربارۀ نجات خاندانم] حق است؛ و تو بهترین داوری».');
INSERT INTO "islamhouse" VALUES(11,46,'11:46','[الله] فرمود: «ای نوح، در حقیقت، او از خاندان تو نیست. [کردارِ] او، کرداری ناشایست است. پس چیزی را که به آن دانش نداری از من نخواه. من به تو پند می‌دهم که مبادا از جاهلان باشی».');
INSERT INTO "islamhouse" VALUES(11,47,'11:47','[نوح] گفت: «پروردگارا، من به تو پناه می‌برم از اینکه چیزی را که به آن دانشی ندارم از تو بخواهم و اگر مرا نبخشی و بر من رحم نکنی، از زیانکاران خواهم بود».');
INSERT INTO "islamhouse" VALUES(11,48,'11:48','گفته شد: «ای نوح، با [امنیت و] سلامتی از جانب ما و [نیز همراه با] برکت‌های [فراوان] بر تو و بر گروه‌هایی که همراهت هستند، [بر زمین] فرود آی؛ و گروه‌هایی [نیز] هستند که به زودی آنان را [از نعمت‌ها] بهره‌مند می‌سازیم، سپس عذاب دردناکی از سوی ما به آنان خواهد رسید».');
INSERT INTO "islamhouse" VALUES(11,49,'11:49','[ای پیامبر،] اینها از خبرهای غیب است که آن را به تو وحی می‌کنیم. نه تو آنها را پیش از این می‌دانستی و نه قومت؛ پس [در برابر آزارشان] شکیبایی کن. یقیناً عاقبت [نیکو] از آنِ پرهیزگاران است.');
INSERT INTO "islamhouse" VALUES(11,50,'11:50','و به سوی قوم عاد، برادرشان هود را [فرستادیم که] گفت: «ای قوم من، الله را عبادت کنید که معبودی [به‌حق] جز او ندارید [و] شما [در شرک به الله] دروغ‌بافانی بیش نیستید.');
INSERT INTO "islamhouse" VALUES(11,51,'11:51','«ای قوم من، برای [رساندن] این [دعوت] از شما پاداشی نمی‌طلبم. پاداش من تنها بر [عهدۀ] ذاتی است که مرا آفرید. آیا نمی‌اندیشید؟');
INSERT INTO "islamhouse" VALUES(11,52,'11:52','و ای قوم من، از پروردگارتان طلب آمرزش کنید، سپس به سوی او بازگردید [و توبه کنید] تا [باران] آسمان را پیاپی بر شما بفرستد و نیرویی بر نیرویتان بیفزاید و گنهکارانه [از حق] روی نگردانید».');
INSERT INTO "islamhouse" VALUES(11,53,'11:53','آنان گفتند: «ای هود، دلیل روشنی برایمان نیاورده‌ای و ما به [خاطر] گفتار تو، معبودانمان را رها نمی‌کنیم و به تو ایمان نمی‌آوریم.');
INSERT INTO "islamhouse" VALUES(11,54,'11:54','[ما چیزی] جز این نمی‌گوییم که برخی از معبودانمان به تو گزندی رسانده‌اند [و دیوانه شده‌ای]». [هود] گفت: «الله را گواه می‌گیرم و شما [نیز] گواه باشید که من از آنچه [با الله] شریک قرار می‌دهید بیزارم.');
INSERT INTO "islamhouse" VALUES(11,55,'11:55','[آری،] به جز او [تعالی، از هر معبودی بیزارم]. پس همگى [در باره] من نقشه بکشید و مهلتم ندهید.');
INSERT INTO "islamhouse" VALUES(11,56,'11:56','من بر الله که پروردگار من و شماست توکل کرده‌ام. هیچ جنبنده‌ای نیست، مگر اینکه در حیطۀ قدرت و فرمانروایىِ اوست. بی‌تردید، پروردگارم بر راه راست است.');
INSERT INTO "islamhouse" VALUES(11,57,'11:57','پس اگر روی بگردانید، [بدانید که‌] من یقیناً آنچه را كه به خاطر آن به سوى شما فرستاده شده‌ام به شما رساندم؛ و پروردگار من، قومی غیر از شما را جانشین [تان] می‌کند و شما نمی‌توانید هیچ زیانی به او برسانید [چرا که] بی‌تردید، پروردگارم بر همه چیز، نگهبان است».');
INSERT INTO "islamhouse" VALUES(11,58,'11:58','و هنگامی‌ که فرمان [عذاب] ما فرارسید، هود و کسانی را ‌که همراهش ایمان آورده بودند، به رحمتی از [جانب] خویش نجات دادیم و آنان را از عذاب سخت رهاندیم.');
INSERT INTO "islamhouse" VALUES(11,59,'11:59','و این [قوم] عاد بودند که آیات پروردگارشان را انکار کردند و از پیامبرانش نافرمانی نمودند و از فرمان هر سرکشِ ستیزه‌گری پیروی کردند.');
INSERT INTO "islamhouse" VALUES(11,60,'11:60','و در این دنیا و روز قیامت [نیز] لعنتی به دنبالشان است. آگاه باشید! قوم عاد به پروردگارشان کفر ورزیدند. هان! عاد ـ قوم هودـ [از رحمت الهی] دور باد!');
INSERT INTO "islamhouse" VALUES(11,61,'11:61','و به سوی [قوم] ثمود، برادرشان ـ صالح ـ را [فرستادیم]. او گفت: «ای قوم من، الله را عبادت کنید که جز او معبودی [به‌حق] ندارید. او شما را از زمین پدید آورد و آبادانىِ آن را به شما واگذاشت؛ پس از او آمرزش بطلبید سپس به سویش بازگردید [و توبه کنید]. بی‌تردید، پروردگارم نزدیک [و] اجابتگر است».');
INSERT INTO "islamhouse" VALUES(11,62,'11:62','آنان گفتند: «ای صالح، پیش از این، تو میان ما مایۀ امیدوارى بودى. آیا ما را از عبادت آنچه پدرانمان عبادت می‌کردند بازمی‌داری؟ و به راستی، ما از آنچه که ما را به سوی آن دعوت می‌کنی، سخت تردید داریم».');
INSERT INTO "islamhouse" VALUES(11,63,'11:63','[صالح] گفت: «ای قوم من، به من بگویید [که] اگر از سوی پروردگارم دلیل [و معجزۀ] آشکاری داشته باشم و او از جانب خویش رحمتی [= نبوت] به من بخشیده باشد، اگر از او نافرمانی کنم، چه کسی مرا در برابرِ الله یاری می‌کند؟ [آنگاه] شما جز [گمراهی و] زیانکاری، [چیزی] بر من نمی‌افزایید.');
INSERT INTO "islamhouse" VALUES(11,64,'11:64','ای قوم من، این ماده‌شترِ الله است که برای شما نشانه‌ای است [بر درستیِ دعوتم]؛ پس بگذارید تا در زمینِ الله بچرد و آزاری به او نرسانید که [در غیر این صورت،] عذابی زودرس شما را فرامی‌گیرد».');
INSERT INTO "islamhouse" VALUES(11,65,'11:65','اما آن [ماده شتر] را پِی کردند؛ آنگاه [صالح به آنان] گفت: «سه روز در خانه‌هایتان [از نعمت‌های دنیا] بهره‌مند گردید [و پس از آن، عذاب خواهد آمد]. این وعده‌ای است که دروغ نخواهد بود».');
INSERT INTO "islamhouse" VALUES(11,66,'11:66','پس چون فرمان [عذاب] ما فرارسید، صالح و کسانی‌ را که همراهش ایمان آورده بودند به رحمتی از [جانب] خویش از [عذاب و] رسوایی آن روز نجات دادیم. بی‌گمان، پروردگارت همان توانمندِ شکست‌ناپذیر است.');
INSERT INTO "islamhouse" VALUES(11,67,'11:67','و کسانی را که ستم کرده بودند، بانگی [مرگبار] فروگرفت؛ و در جای خویش به روی افتادند و هلاک شدند.');
INSERT INTO "islamhouse" VALUES(11,68,'11:68','چنان که گویی هرگز در آن [دیار] نبوده‌اند. آگاه باشید! [قوم] ثمود به پروردگارشان کفر ‌ورزیدند. هان! ثمود [از رحمت الهی] دور باد!');
INSERT INTO "islamhouse" VALUES(11,69,'11:69','به راستی، فرستادگان ما با بشارت [تولد فرزند] نزد ابراهیم آمدند [و] گفتند: «سلام [بر تو]». [او نیز در پاسخ] گفت: «سلام [بر شما]» و دیری نپایید که [ابراهیم برایشان] گوساله‌ای بریان آورد.');
INSERT INTO "islamhouse" VALUES(11,70,'11:70','اما چون دید به سوی آن [غذا] دست نمی‌برند [و چیزی نمی‌خورند]، با آنان احساس بیگانگی کرد و [از بیم آنکه قصد جانش را داشته باشند،] ترسی از آنان به دل گرفت. [فرشتگان] گفتند: «نترس؛ ما به سوی قوم لوط فرستاده شده‌ایم».');
INSERT INTO "islamhouse" VALUES(11,71,'11:71','و همسرش [ساره که پشتِ پرده] ایستاده بود، [از شنیدنِ مژده تولد فرزند] خندید؛ آنگاه او را به اسحاق و پس از او به یعقوب بشارت دادیم.');
INSERT INTO "islamhouse" VALUES(11,72,'11:72','[ساره] گفت: «ای وای بر من! آیا در حالی که خودم پیرزنم و این شوهرم پیرمردی [فرتوت‌] است، فرزند می‌زایم؟ به راستی، این امری شگفت‌آور است‌».');
INSERT INTO "islamhouse" VALUES(11,73,'11:73','[فرشتگان] گفتند: «آیا از فرمان الله تعجب می‌کنی؟ رحمت الله و برکاتش بر شما اهل خانه [نبوت] باد. بی‌تردید، او [تعالی در ذات و صفات و افعالش] ستوده‌ای بزرگوار است».');
INSERT INTO "islamhouse" VALUES(11,74,'11:74','هنگامی که ترس از [دل] ابراهیم [بیرون] رفت و بشارت [تولد فرزند] به او رسید، دربارۀ [تأخیرِ عذاب یا نجات مؤمنانِ] قوم لوط با ما به بحث [و چون و چرا] پرداخت.');
INSERT INTO "islamhouse" VALUES(11,75,'11:75','به راستی که ابراهیم بردبار و بسیار نیایشگر و بازآینده [به درگاه ما] بود.');
INSERT INTO "islamhouse" VALUES(11,76,'11:76','[فرشتگان گفتند:] «ای ابراهیم، از این [سخن] بگذر؛ [چرا که] به راستی فرمان [عذاب] پروردگارت فرارسیده است و مسلماً عذابی بازگشت‌ناپذیر برایشان خواهد آمد».');
INSERT INTO "islamhouse" VALUES(11,77,'11:77','و چون فرستادگان ما نزد لوط آمدند، از [تصور بی‌شرمىِ قومش نسبت به] آنان نگران و دلتنگ شد و گفت: «امروز، روز بسیار سختی است».');
INSERT INTO "islamhouse" VALUES(11,78,'11:78','و قوم او شتابان [به قصد کامجویی از مهمانان لوط‌] به سوى [خانۀ] او هجوم آوردند، در حالى كه پیش از آن [نیز‌] مرتكب کارهای زشتى مى‌شدند. [لوط] گفت: «ای قوم من، اینان دختران [= زنانِ امتِ] من هستند؛ [با آنها ازدواج کنید.] آنان برای شما پاکیزه‌ترند؛ پس از الله بترسید و مرا در [مقابل] مهمانانم شرمسار نکنید. آیا بین شما مرد خردمندی نیست [که شما را از این کار بازدارد]؟».');
INSERT INTO "islamhouse" VALUES(11,79,'11:79','آنان گفتند: «تو خود می‌دانی که ما به دختران [= زنانِ امتِ] تو نیازی نداریم و خوب می‌دانی که ما چه می‌خواهیم».');
INSERT INTO "islamhouse" VALUES(11,80,'11:80','[لوط] گفت: «کاش در برابر شما قدرتی داشتم یا می‌توانستم به تکیه‌گاهی استوار [همچون خانواده و قبیله‌ام] پناه ‌ببرم».');
INSERT INTO "islamhouse" VALUES(11,81,'11:81','[فرشتگان] گفتند: «ای لوط، ما فرستادگان پروردگارت هستیم. آنان هرگز به تو دست نمی‌یابند؛ خانواده‌ات را در پاسی از شب،[از این شهر بیرون] ببر و هیچ یک از شما به پشت سر نگاه نکند؛ مگر همسرت که آنچه [از عذاب] که به آنان می‌رسد، قطعاً به او [نیز] خواهد رسید. در حقیقت، وعده‌گاهشان صبح است. آیا صبح نزدیک نیست؟»');
INSERT INTO "islamhouse" VALUES(11,82,'11:82','هنگامی که فرمان ما فرارسید، آن [سرزمین] را زیر و رو کردیم و بر آنان سنگ‌گِل‌هایی پیاپی باریدیم.');
INSERT INTO "islamhouse" VALUES(11,83,'11:83','[سنگ‌هایی که] نزد پروردگارت نشاندار بود؛ و این [عذاب دردناک،] از ستمکاران [دیگر‌] دور نیست.');
INSERT INTO "islamhouse" VALUES(11,84,'11:84','و به سوی [مردم] مَدیَن، برادرشان شعیب را [فرستادیم]. او گفت: «ای قوم من، الله را عبادت کنید که معبودی [به‌حق] جز او ندارید؛ و پیمانه و میزان را کم نکنید. به راستی، شما را در نعمت می‌بینم؛ و[لی] از عذاب روز فراگیر بر شما می‌ترسم.');
INSERT INTO "islamhouse" VALUES(11,85,'11:85','و ای قوم من، پیمانه و میزان را با عدالت، تمام [و کامل] بپردازید و کالاهای مردم را کم ندهید و در زمین به فساد [و گناه] نکوشید.');
INSERT INTO "islamhouse" VALUES(11,86,'11:86','اگر ایمان داشته باشید، آنچه [از كسب حلال که‌] الله [پس از تحویل کامل پیمانه و ترازو]، برایتان باقى می‌گذارد بهتر است؛ و من بر شما نگهبان نیستم».');
INSERT INTO "islamhouse" VALUES(11,87,'11:87','آنان گفتند: «ای شعیب، آیا نمازت تو را بر آن می‌دارد که [به ما امر کنی تا‌] دست از آنچه پدرانمان عبادت می‌کردند برداریم یا در اموالمان هر کاری که می‌خواهیم نکنیم؟ تو که بردبار [و] خردمندی [پس چرا چنین می‌گویی؟]».');
INSERT INTO "islamhouse" VALUES(11,88,'11:88','[شعیب] گفت: «ای قوم من، به من بگویید [که] اگر از سوی پروردگارم دلیل [و معجزۀ] آشکاری داشته باشم و از جانب خویش روزیِ نیکویی به من عطا کرده باشد، [آنگاه چه می‌کنید؟] و من نمی‌خواهم در آنچه شما را از آن بازمی‌دارم با شما مخالفت کنم [و خودم انجامش دهم‌]. تا آنجا که در توان دارم، جز اصلاح نمی‌خواهم؛ و توفیق من، جز به [لطف و خواستِ] الله نیست؛ بر او توکل کرده‌ام و به سوی او بازمی‌گردم.');
INSERT INTO "islamhouse" VALUES(11,89,'11:89','و ای قوم من، دشمنی [و مخالفت] با من سبب نشود که همانند آنچه که به قوم نوح یا قوم هود یا قوم صالح رسید، به شما [نیز] برسد؛ و [به خصوص عذاب] قوم لوط [که از لحاظ زمان و مکان] چندان از شما دور نیست.');
INSERT INTO "islamhouse" VALUES(11,90,'11:90','و از پروردگارتان آمرزش بخواهید؛ سپس به سوی او بازگردید [و توبه کنید که] بی‌تردید، پروردگارم مهربان [و] دوستدار [توبه‌کنندگان‌] است».');
INSERT INTO "islamhouse" VALUES(11,91,'11:91','آنان گفتند: «ای شعیب، بسیاری از آنچه را که می‌گویی نمی‌فهمیم و یقیناً تو را در میان خود ناتوان می‌بینیم؛ و اگر [احترام] قبیله‌ات نبود، مسلماً سنگسارت می‌کردیم و تو نزد ما توانمند [و گرانقدر] نیستی».');
INSERT INTO "islamhouse" VALUES(11,92,'11:92','[شعیب] گفت: «ای قوم من، آیا قبیله‌ام نزد شما گرامی‌تر از الله است که [فرمان] او را [فراموش کردید و] پشت سر خویش انداخته‌اید. بی‌تردید، پروردگارم به آنچه انجام می‌دهید احاطه دارد.');
INSERT INTO "islamhouse" VALUES(11,93,'11:93','و ای قوم من، شما به روش خود عمل کنید، من نیز [به روش خود] عمل می‌کنم. به زودى خواهید دانست كه عذاب رسواكننده بر چه كسى فرود مى‌آید و دروغگو كیست. شما منتظر باشید و من [نیز] با شما در انتظارم».');
INSERT INTO "islamhouse" VALUES(11,94,'11:94','و چون فرمان ما فرارسید، شعیب و کسانی را که همراهش ایمان آورده بودند، به رحمتی از [جانب] خویش نجات دادیم و کسانی را که ستم کردند، بانگی [مرگبار] فروگرفت، که در جای خود به روی افتادند و هلاک شدند؛');
INSERT INTO "islamhouse" VALUES(11,95,'11:95','چنان [هلاک شدند] که گویی هرگز در آن [دیار] نبودند. هان! [قوم] مَدیَن [از رحمت الهی] دور باد! چنان که [قوم] ثمود دور شدند.');
INSERT INTO "islamhouse" VALUES(11,96,'11:96','و به راستی موسی را با آیات خود و دلیلی آشکار فرستادیم.');
INSERT INTO "islamhouse" VALUES(11,97,'11:97','به سوی فرعون و بزرگان [و اشرافِ قومِ] او؛ اما آنان از فرمان فرعون پیروی کردند و فرمان فرعون درست [و بخردانه] نبود.');
INSERT INTO "islamhouse" VALUES(11,98,'11:98','[فرعون] روز قیامت پیشاپیش قومش مى‌رود و آنان را به آتش [دوزخ] وارد مى‌كند؛ و چه بد جایگاهی است که به آن وارد می‌شوند!');
INSERT INTO "islamhouse" VALUES(11,99,'11:99','و در این [دنیا] و روز قیامت، نفرین به دنبال دارند و چه بد ارمغانی به آنان مى‌بخشند!');
INSERT INTO "islamhouse" VALUES(11,100,'11:100','[ای پیامبر،] این [برخی] از خبرهای آن [سرزمین‌ها و] شهرهاست که آنها را برایت بازگو می‌کنیم؛ برخی از آنها [هنوز] پابرجاست و [برخی دیگر] ویران [گشته است].');
INSERT INTO "islamhouse" VALUES(11,101,'11:101','و ما به آنان ستم نکردیم؛ بلکه آنان [خود] به خویشتن ستم کردند؛ و هنگامی ‌که فرمان پروردگارت [برای عذابشان] فرارسید، معبودانشان که [آنها را] به جای الله می‌خواندند، سودی برایشان نرساندند [و یاری‌شان ندادند] و بر آنان جز هلاکت [و تباهی] نیفزودند.');
INSERT INTO "islamhouse" VALUES(11,102,'11:102','و چنین است بازخواستِ پروردگارت که اهالی شهرهایی را که ستمکار [و مشرک‌] بودند فرومی‌گرفت. بی‌گمان، بازخواستِ او دردناک و سخت است‌.');
INSERT INTO "islamhouse" VALUES(11,103,'11:103','یقیناً در این [داستان‌ها، پند و] نشانه‌ای است برای کسی‌ که از عذاب آخرت می‌ترسد. آن [روز،] روزی است که مردم را برای [حسابرسی در] آن جمع می‌کنند و آن [روز،] روزی است که [تمام اهل محشر] آن را می‌بینند.');
INSERT INTO "islamhouse" VALUES(11,104,'11:104','و ما آن [روز] را جز تا مدتی معیّن به تأخیر نمی‌اندازیم.');
INSERT INTO "islamhouse" VALUES(11,105,'11:105','وقتی این روز فرا رسد هیچ کس جز به اجازۀ او [= الله] سخن نمی‌گوید؛ [در آن روز] برخی از آنان بدبختند و [برخی دیگر] نیکبخت.');
INSERT INTO "islamhouse" VALUES(11,106,'11:106','اما کسانی‌ که بدبخت شدند، [دوزخی‌اند و] در آتش [با هر دم و بازدمی] ضجّه و ناله‌ای برایشان است.');
INSERT INTO "islamhouse" VALUES(11,107,'11:107','تا آسمان‌ها و زمین [باقی] است، در آن [عذاب] جاودانند؛ مگر آنکه پروردگارت بخواهد [که موحدان گناهکار را از دوزخ خارج سازد]. بی‌تردید، پروردگارت آنچه را که بخواهد انجام می‌دهد.');
INSERT INTO "islamhouse" VALUES(11,108,'11:108','و اما کسانی ‌که نیکبخت [و سعادتمند] شدند، تا آسمان‌ها و زمین [باقی] است، جاودانه در بهشتند؛ مگر آنکه پروردگارت بخواهد [موحدان گناهکار را پیش از ورود به بهشت، در دوزخ مجازات کند. این نعمت،] بخششی بی‌پایان است.');
INSERT INTO "islamhouse" VALUES(11,109,'11:109','پس، از [باطل بودنِ] آنچه اینان [= مشرکان] عبادت می‌کنند در تردید نباش. آنان این معبودان را عبادت نمی‌کنند مگر به [تقلید و] همان‌ شکلی که پدرانشان [نیز] پیشتر عبادت می‌کردند؛ و ما سهمشان را [از عذاب] بی‌کم‌وکاست به آنان خواهیم داد.');
INSERT INTO "islamhouse" VALUES(11,110,'11:110','و به راستی [ما] به موسی کتاب دادیم؛ سپس در آن اختلاف شد؛ و اگر پیشتر [در مورد مهلت به گناهکاران و حسابرسی در قیامت] سخنی از جانب پروردگارت بیان نشده بود، یقیناً [در همین دنیا] در میانشان داورى مى‌شد [و عذاب نازل می‌گشت]؛ و به راستی که آنان [= یهود و مشرکان] دربارۀ [حقانیتِ] این [قرآن] سخت در تردیدند.');
INSERT INTO "islamhouse" VALUES(11,111,'11:111','و پروردگارت [پاداش] اعمال همگان را قطعاً بی‌کم‌وکاست خواهد داد. بی‌تردید، او به آنچه می‌کنند آگاه است.');
INSERT INTO "islamhouse" VALUES(11,112,'11:112','پس [ای پیامبر،] چنان که فرمان یافته‌ای، [در برابر مشرکان] استقامت کن و کسی‌ که همراه تو [به پیشگاه الهی] روی آورده است [نیز باید در دینداری استقامت کند] و [از حدود الهی] تجازو نکنید. بی‌تردید، او به آنچه می‌کنید بیناست.');
INSERT INTO "islamhouse" VALUES(11,113,'11:113','به کسانی‌ که ستم کردند [= کافران‌] گرایش نیابید که [اگر چنین کنید،] آتش [دوزخ] به شما نیز خواهد رسید و در برابر الله [هیچ] دوستی ندارید و آنگاه یاری نمی‌شوید.');
INSERT INTO "islamhouse" VALUES(11,114,'11:114','و در دو طرف روز [= آغاز و پایان روز] و ساعاتی از شب، نماز بگزار. بی‌تردید، نیکی‌ها بدی‌ها را از بین می‌برد. این [سخن،] پندی است برای پندپذیران.');
INSERT INTO "islamhouse" VALUES(11,115,'11:115','و شکیبایی کن [که] بی‌تردید، الله پاداش نیکوکاران را تباه نمی‌کند.');
INSERT INTO "islamhouse" VALUES(11,116,'11:116','پس چرا در میان امت‌های پیش از شما ـ جز اندکی از کسانی که آنان را [از عذاب] نجات دادیم ـ [مؤمنان و] خردمندانی نبودند که [مردم را] از فساد در زمین بازدارند؟ و كسانى كه ستم كردند، به دنبال ناز و نعمتی که یافته بودند رفتند و گناهکار بودند.');
INSERT INTO "islamhouse" VALUES(11,117,'11:117','و [سنتِ] پروردگارت [هرگز] چنین نبوده است که شهرها را ـ در حالی‌ که ساکنانش [افرادی] نیکوکار بودند ـ به ستم نابود کند.');
INSERT INTO "islamhouse" VALUES(11,118,'11:118','و اگر پروردگارت می‌خواست، [همۀ] مردم را یک امت [موحد و درستکار] قرار می‌داد؛ ولی [آنان] همواره مختلفند.');
INSERT INTO "islamhouse" VALUES(11,119,'11:119','مگر كسانى كه پروردگارت به آنان رحم کرده باشد و به همین هدف [آزمودن به وسیله اختلاف] آنان را آفریده است؛ و وعدۀ پروردگارت [چنین‌] تحقق پذیرفته است [كه:] «قطعاً دوزخ را از جن و انسان [گناهکار] انباشته خواهم ساخت».');
INSERT INTO "islamhouse" VALUES(11,120,'11:120','و [ای محمد،] از اخبار پیامبران این همه را بر تو حكایت مى‌كنیم تا دلت را با [بیان] آنها ثبات [و آرامش] ‌بخشیم؛ و در این [سرگذشت‌ها] حقیقت برایت آمده است و [این داستان‌ها] پندی است [برای کافران] و تذکری است براى مؤمنان.');
INSERT INTO "islamhouse" VALUES(11,121,'11:121','و به کسانی که ایمان نمی‌آورند بگو: «شما به روش خود عمل کنید، ما [نیز به شیوۀ خود] عمل می‌کنیم.');
INSERT INTO "islamhouse" VALUES(11,122,'11:122','و منتظر باشید [كه] بی‌تردید، ما [نیز] منتظریم».');
INSERT INTO "islamhouse" VALUES(11,123,'11:123','و [آگاهی از] غیبِ آسمان‌ها و زمین، از آنِ الله است و همۀ کارها به سوی او بازگردانده می‌شود؛ پس او را عبادت کن و بر او توکل نما؛ و پروردگارت از آنچه می‌کنید [هرگز] غافل نیست.');
INSERT INTO "islamhouse" VALUES(12,1,'12:1','الر [= الف. لام. را]. این آیاتِ کتاب روشنگر است.');
INSERT INTO "islamhouse" VALUES(12,2,'12:2','ما آن را قرآنی [به زبان] عربی نازل کردیم؛ باشد که [در مفاهیمش] بیندیشید.');
INSERT INTO "islamhouse" VALUES(12,3,'12:3','[ای پیامبر،] ما بهترین داستان‌ها را با وحی کردنِ این قرآن بر تو حکایت می‌کنیم؛ و مسلّماً پیشتر تو [از این داستان‌ها] بی‌خبر بودی.');
INSERT INTO "islamhouse" VALUES(12,4,'12:4','[یاد کن از] هنگامی که یوسف به پدرش [یعقوب] گفت: «پدر جان، من [در خواب] یازده ستاره و خورشید و ماه دیدم. آنها را دیدم که برایم سجده می‌کردند».');
INSERT INTO "islamhouse" VALUES(12,5,'12:5','[یعقوب] گفت: «پسرم، خوابت را به برادران خویش باز‌گو نکن كه نیرنگى برایت مى‌اندیشند؛ [چرا که] بی‌تردید، شیطان دشمنِ آشكاری برای انسان است.');
INSERT INTO "islamhouse" VALUES(12,6,'12:6','پروردگارت تو را این گونه برمی‌گزیند و [علمِ] تعبیر خواب‌ها را به تو می‌آموزد و نعمتش را بر تو و بر خاندان یعقوب تمام می‌گردانَد؛ چنان که پیش از این [نیز] بر اجدادت ـ ابراهیم و اسحاق ـ تمام کرد. بی‌تردید، پروردگارت دانای حکیم است».');
INSERT INTO "islamhouse" VALUES(12,7,'12:7','به راستی که در [داستان] یوسف و برادرانش، برای پُرسشگران [دربارۀ یوسف، عبرت‌ها و] نشانه‌هاست.');
INSERT INTO "islamhouse" VALUES(12,8,'12:8','آنگاه ‌که [برادرانش به یکدیگر] گفتند: «یوسف و برادرش [بنیامین] نزد پدر‌مان از ما محبوب‌ترند؛ در حالی‌ که ما یک گروه [نیرومند] هستیم. حقا که پدرمان [با این مهرورزیِ بی‌دلیل] در گمراهیِ آشکاری است.');
INSERT INTO "islamhouse" VALUES(12,9,'12:9','[یکی از برادران گفت:] یوسف را بکُشید یا او را به سرزمینی [دوردست] بیفکنید تا توجه پدر‌تان فقط به [سوی] شما باشد و پس از آن [توبه کنید و] افرادی شایسته باشید».');
INSERT INTO "islamhouse" VALUES(12,10,'12:10','یکی [دیگر] از آنان گفت: «یوسف را نکشید و اگر [می‌خواهید] کاری انجام دهید، او را در قعر چاه بیندازید تا برخی از مسافران [کاروان] او را برگیرند [و با خود به جایی دور ببرند]».');
INSERT INTO "islamhouse" VALUES(12,11,'12:11','[سپس نزد یعقوب رفتند] و گفتند: «پدر جان، چرا ما را بر یوسف امین نمی‌شماری؛ حال‌ آنکه ما خیرخواهش هستیم؟');
INSERT INTO "islamhouse" VALUES(12,12,'12:12','فردا او را با ما [به دشت] بفرست تا [در چمنزارها و گلزارها] بگردد و بازی کند؛ و ما به خوبی مراقب او هستیم».');
INSERT INTO "islamhouse" VALUES(12,13,'12:13','[یعقوب] گفت: «اینکه او را ببرید، مرا اندوهگین می‌کند و از این می‌ترسم كه گرگ او را بخورد و شما از او غافل باشید».');
INSERT INTO "islamhouse" VALUES(12,14,'12:14','آنان گفتند: «با وجود اینکه ما یک گروه [نیرومند] هستیم، اگر [اجازه دهیم که] گرگ او را بخورد، قطعاً زیانکار خواهیم بود».');
INSERT INTO "islamhouse" VALUES(12,15,'12:15','هنگامی که او را [همراه خود‌] بردند و همدست شدند که او را در قعر چاه بیندازند، به او وحی کردیم که: «[سرانجام‌] قطعاً آنان را در حالی که هیچ [درباره‌ات] نمی‌دانند، از [چند و چون] این کارشان آگاه خواهی ساخت»‌.');
INSERT INTO "islamhouse" VALUES(12,16,'12:16','و شبانگاه، گریه‌کنان نزد پدر‌شان آمدند.');
INSERT INTO "islamhouse" VALUES(12,17,'12:17','گفتند: «پدرجان، ما [دورتر] رفتیم که مسابقه دهیم و یوسف را نزد اثاث خود گذاشتیم و گرگ او را خورد؛ و هر چند که راستگو باشیم، تو [هرگز سخن] ما را باور نخواهی کرد».');
INSERT INTO "islamhouse" VALUES(12,18,'12:18','و پیراهن او را [آغشته] با خونی دروغین [برای یعقوب] آوردند. [او] گفت: «[چنین نیست؛] بلکه [هوای] نفس شما کاری [ناشایست] را برایتان آراسته است؛ پس صبرى نیكو [برایم بهتر است‌] و الله در [مورد] آنچه می‌گویید یاریگرِ [من‌] است».');
INSERT INTO "islamhouse" VALUES(12,19,'12:19','و کاروانی آمد و آنان آب‌آورِ خود را فرستادند؛ او دَلوش را [در چاه‌] انداخت [و چون یوسف را بالا کشید] گفت: «مژده دهید! این یک نوجوان است»؛ و [پس از نجاتش] او را همچون یک کالا[ی ارزشمند از دیگران] پنهان داشتند؛ و[لی] الله به آنچه می‌کردند آگاه بود.');
INSERT INTO "islamhouse" VALUES(12,20,'12:20','و [سرانجام] او را به بهایی اندک ـ چند درهم ـ فروختند و در[بارۀ نگه داشتنِ] او بی‌میل بودند.');
INSERT INTO "islamhouse" VALUES(12,21,'12:21','و آن شخص از [مردم] مصر [= عزیز] که او را خریده بود، به همسرش گفت: «مقامش را گرامی بدار. امید است که برایمان سودمند باشد یا او را به فرزندی بگیریم». و اینچنین بود که یوسف را در [آن] سرزمین، ارجمند [و مقتدر] گرداندیم تا تعبیر خواب به او بیاموزیم؛ و الله بر کار خویش [توانا و] چیره است؛ ولی بیشتر مردم نمی‌دانند.');
INSERT INTO "islamhouse" VALUES(12,22,'12:22','و چون [یوسف به اوج] نیروی جوانی رسید، حکمت و دانش به او عطا کردیم؛ و نیکو‌کاران را اینچنین پاداش می‌دهیم.');
INSERT INTO "islamhouse" VALUES(12,23,'12:23','و آن زنی که او [= یوسف] در خانه‌اش بود، از وی تقاضای کامجویی کرد و در‌ها را بست و گفت: «بیا [که] در اختیارِ تو [هستم]». [یوسف] گفت: «پناه بر الله! آن مرد، [شوهرِ تو و] سرورِ من است [و] جایگاهم را گرامی داشته است [پس چگونه به او خیانت کنم؟] یقیناً ستمکاران رستگار نمی‌شوند».');
INSERT INTO "islamhouse" VALUES(12,24,'12:24','و در حقیقت، [آن زن] قصد او کرد و او [= یوسف نیز] ـ اگر بُرهان پروردگارش را ندیده بودـ قصد آن زن می‌کرد. ما اینچنین [کردیم] تا بدی و زشتکاری را از او دور سازیم. به راستی که او از بندگان مخلصِ ما بود.');
INSERT INTO "islamhouse" VALUES(12,25,'12:25','و هر‌ دو به طرف در شتافتند و [همسرِ عزیزِ مصر] پیراهن او[= یوسف] را از پشت پاره کرد و [در این هنگام] شوهرش را در آستانۀ در یافتند. [آن] زن گفت: «کیفر کسی ‌که قصد بدی به خانوادۀ تو داشته باشد چیست؛ جز اینکه زندانی شود یا عذابی دردناک [ببیند]؟»');
INSERT INTO "islamhouse" VALUES(12,26,'12:26','[یوسف] گفت: «او از من کامجویی کرد؛» و [در آن هنگام] شاهدی از بستگان آن زن، [چنین] شهادت داد: «اگر پیراهنش از جلو پاره شده باشد، این زن راست می‌گوید و آن مرد [= یوسف] دروغگوست؛');
INSERT INTO "islamhouse" VALUES(12,27,'12:27','و[لی] اگر پیراهنش از پشت پاره شده باشد، این زن دروغ می‌گوید و آن مرد راستگوست».');
INSERT INTO "islamhouse" VALUES(12,28,'12:28','چون [عزیز مصر] دید که پیراهن او از پشت پاره شده است، [به حقیقت پی بُرد و] گفت: «این از نیرنگِ شما [زنان] است. به راستی که نیرنگ شما بزرگ است.');
INSERT INTO "islamhouse" VALUES(12,29,'12:29','ای یوسف، از این [ماجرا] درگذر و [تو ای زن،] برای گناهت آمرزش بخواه که بی‌تردید خطا‌کار بوده‌ای».');
INSERT INTO "islamhouse" VALUES(12,30,'12:30','[هنگامی که این خبر منتشر شد] برخی از زنان در شهر گفتند: «همسر عزیز [مصر]، از غلام [جوان] خویش تقاضای کامجویی می‌کند [و] سخت شیفته‌اش گشته است. ما او را واقعاً در گمراهی آشکاری می‌بینیم».');
INSERT INTO "islamhouse" VALUES(12,31,'12:31','وقتی [همسرِ عزیز] نیرنگ [و بدگوییِ] آنان را شنید، [شخصی را برای دعوت] به سراغشان فرستاد و مجلسی برایشان ترتیب داد و به هر یک از آنان [برای بریدن غذا] چاقویی داد و به یوسف گفت: «بر [جمعِ] آنان وارد شو». [زنان] چون او را دیدند، [او را] بسیار بزرگ[وار و زیبا] شمردند و [از شدت شگفتی] دست‌هایشان را زخمی کردند و گفتند: «پناه بر الله! این بشر نیست؛ این جز فرشته‌ای بزرگوار نیست».');
INSERT INTO "islamhouse" VALUES(12,32,'12:32','[همسر عزیز] گفت: «این [جوانِ زیباروی،] همان است که مرا دربارۀ او سرزنش می‌کردید. [آری،] در حقیقت، من از او تقاضای کامجویی نمودم و[لی] او خویشتنداری کرد؛ و [اینک] اگر آنچه را به او دستور می‌دهم انجام ندهد، قطعاً زندانی می‌شود و مسلماً از خوارشدگان خواهد بود».');
INSERT INTO "islamhouse" VALUES(12,33,'12:33','[یوسف] گفت: «پروردگارا، زندان برای من از آنچه [اینها] مرا به سویش می‌خوانند محبوب‌تر است؛ و اگر نیرنگشان را از من نگردانی، به آنان گرایش می‌یابم و [اگر چنین کنم،] نادان خواهم بود».');
INSERT INTO "islamhouse" VALUES(12,34,'12:34','پروردگارش [دعای] او را اجابت کرد و نیرنگشان را از او بازگردانْد. بی‌تردید، او شنوای داناست.');
INSERT INTO "islamhouse" VALUES(12,35,'12:35','آنگاه پس از آنکه [عزیز مصر و اطرافیانش] نشانه‌ها[ی پاکدامنیِ یوسف] را دیدند، تصمیم گرفتند [برای سرپوش گذاشتن بر این بی‌آبرویی،] او را تا مدتی زندانی کنند.');
INSERT INTO "islamhouse" VALUES(12,36,'12:36','و دو جوان همراه او وارد زندان شدند. [روزى‌] یکی از آن دو گفت: «من [در خواب،] خود را دیدم که [انگور برای] شراب می‌فشارم» و دیگری گفت: «من خواب دیده‌ام که بر سر خویش [ظرف] نانی می‌برم [و] پرندگان از آن می‌خورند. ما را از تعبیر آن آگاه کن [که] قطعاً تو را از نیکو‌کاران می‌بینیم».');
INSERT INTO "islamhouse" VALUES(12,37,'12:37','[یوسف] گفت: «هیچ غذایی برای خوردن نزدتان نمی‌رسد، مگر آنکه پیش از رسیدنش، شما را از [کیفیت و] حقیقت آن [= خواب‌ها] آگاه خواهم ساخت. این [اخبار غیبی و تعبیر خواب،] از چیزهایی است که پروردگارم به من آموخته است. در حقیقت، من آیینِ قومی را که به الله ایمان نمی‌آورند و به [سرای] آخرت [نیز] کفر می‌ورزند، ترک کرده‌ام.');
INSERT INTO "islamhouse" VALUES(12,38,'12:38','و از آیین پدرانم ـ ابراهیم و اسحاق و یعقوب ـ پیروی نموده‌ام. برای ما سزاوار نیست که چیزی را شریک الله قرار دهیم. این [عبادت الله به یگانگی،] از فضل الله بر ما و بر [همۀ] مردم است؛ ولی بیشتر مردم سپاس نمی‌گزارند.');
INSERT INTO "islamhouse" VALUES(12,39,'12:39','ای رفقای زندانی من، آیا معبودان پراکنده [و متعدد] بهترند، یا الله یگانۀ پیروزمند؟');
INSERT INTO "islamhouse" VALUES(12,40,'12:40','شما به جای او [معبودی را] عبادت نمی‌کنید، مگر نام‌هایی [بی‌معنی] که خود و پدرانتان بر آنها نهاده‌اید. الله هیچ دلیلی بر [حقانیت] آنها نازل نکرده است. فرمانروایی، تنها از آن الله است [و] فرمان داده است که جز او را عبادت نکنید. این همان دین راست و استوار است؛ ولی بیشتر مردم نمی‌دانند.');
INSERT INTO "islamhouse" VALUES(12,41,'12:41','ای رفقای زندانی من، یکی از شما [آزاد می‌شود] و به سَروَر خویش شراب خواهد نوشاند؛ و اما دیگری به دار آویخته می‌شود و پرندگان از [مغز] سرش خواهند خورد. امری که در [مورد] آن از من نظر خواستید، [چنین] مقدر شده است».');
INSERT INTO "islamhouse" VALUES(12,42,'12:42','و [یوسف] به یکی از آن دو [جوان] که دانست رها می‌شود، گفت: «نزد سرورت [= پادشاه] از من یاد کن»؛ و[لی] شیطان یاد کردن [از یوسف نزد] سَرورش را از خاطرِ وی [= ساقی پادشاه] بُرد و او [= یوسف] چندین سال [دیگر نیز] در زندان باقی ماند.');
INSERT INTO "islamhouse" VALUES(12,43,'12:43','و [روزی] پادشاه گفت: «من [در خواب] دیدم که هفت گاو چاق را هفت [گاو] لاغر می‌خورند؛ و هفت خوشۀ سبز و [هفت خوشۀ] دیگر را خشک [دیدم]. ای بزرگان، اگر تعبیر خواب می‌کنید، در[بارۀ] خوابم نظر دهید».');
INSERT INTO "islamhouse" VALUES(12,44,'12:44','آنان گفتند: «[اینها] خواب‌هایی پریشان است و ما به تعبیر این [گونه] خواب‌ها دانا نیستیم».');
INSERT INTO "islamhouse" VALUES(12,45,'12:45','و یکی از آن دو [زندانی] که نجات یافته بود، پس از مدت‌ها [یوسف] را به یاد آورد [و] گفت: «مرا به [زندان‌] بفرستید تا دربارۀ تعبیر آن [خواب] به شما خبر دهم».');
INSERT INTO "islamhouse" VALUES(12,46,'12:46','[او به زندان رفت و گفت:] «یوسف، ای [مرد] راستگو، در[بارۀ این خواب که] هفت گاو چاق را هفت گاو [لاغر] می‌خورند و هفت خوشۀ سبز و [هفت خوشۀ‌] خشکیدۀ دیگر، برایمان نظر بده [و آن را تعبیر كن] تا نزد مردم برگردم؛ شاید آنها [تعبیر این خواب و توانایی‌های تو را‌] بدانند.');
INSERT INTO "islamhouse" VALUES(12,47,'12:47','[یوسف] گفت: «هفت سال پیاپی [با جدیّت] کشاورزی کنید و آنچه را که درو می‌کنید ـ جز اندکی که می‌خورید ـ با خوشه‌اش [کنار] بگذارید.');
INSERT INTO "islamhouse" VALUES(12,48,'12:48','سپس بعد از آن [دورانِ رونق]، هفت [سال قحطیِ] سخت می‌آید که آنچه را برای آن سال‌ها [کنار] گذاشته‌اید می‌خورید، جز اندکی که آن را [برای بذر] ذخیره می‌کنید.');
INSERT INTO "islamhouse" VALUES(12,49,'12:49','سپس بعد از آن هفت [سال،] سالی فرامی‌رسد كه باران فراوانی نصیب مردم مى‌شود و در آن سال، [بر اثر فراوانى، از میوه‌ها و دانه‌های روغنی] عصاره مى‌گیرند».');
INSERT INTO "islamhouse" VALUES(12,50,'12:50','پادشاه [چون این تعبیر را شنید] گفت: «او را نزد من بیاورید». اما وقتی فرستادۀ [پادشاه] نزد او آمد، [یوسف] گفت: «نزد سرورت بازگرد و از او بپرس که ماجرای زنانی که دست‌های خود را بریدند چه بود؟ قطعاً پروردگارم به نیرنگِ آنان آگاه است».');
INSERT INTO "islamhouse" VALUES(12,51,'12:51','[پادشاه آن زنان را خواست و] گفت: «هنگامی‌ که یوسف را به سوی خویش دعوت کردید، جریان کارتان چه بود؟» گفتند: «پناه بر الله! ما هیچ گناهی در او سراغ نداریم». [در این هنگام] همسر عزیز گفت: «اکنون حق آشکار شد: من [بودم که] از وی تقاضای کامجویی کردم [و او پاکدامنی ورزید] و یقیناً راستگوست.');
INSERT INTO "islamhouse" VALUES(12,52,'12:52','این [اعتراف] برای آن است که [عزیز مصر] بداند من در پنهان به او خیانت نکرده‌ام و اینکه الله نیرنگِ خائنان را به جایى نمى‌رسانَد.');
INSERT INTO "islamhouse" VALUES(12,53,'12:53','من هرگز خود را بیگناه نمی‌شمارم. بی‌شک، نفس [وسوسه‌گرِ انسان، او را] پیوسته به بدی فرمان می‌دهد مگر نفس [کسی] که پروردگارم به او رحم كند. بی‌تردید، پروردگارم آمرزندۀ مهربان است».');
INSERT INTO "islamhouse" VALUES(12,54,'12:54','و پادشاه گفت: «او [= یوسف] را نزدم بیاورید [تا مشاورِ] ویژۀ خود گردانم». پس چون [یوسف نزدش آمد و] با وی صحبت کرد، [پادشاه] گفت: «تو امروز نزد ما ارجمند [و] امین هستی».');
INSERT INTO "islamhouse" VALUES(12,55,'12:55','[یوسف‌] گفت: «مرا بر [سرپرستىِ] خزانه‌هاى [مالی و غذاییِ این‌] سرزمین بگمار [كه] بی‌تردید، من نگهبانى دانا هستم».');
INSERT INTO "islamhouse" VALUES(12,56,'12:56','و این گونه به یوسف در سرزمین [مصر] منزلت [و قدرت] دادیم [آنچنان] كه [می‌توانست به هر جا برود و] در هر جایی از آن که مى‌خواست، اقامت مى‌گزید. ما رحمت خود را به هر کس که بخواهیم می‌رسانیم و پاداش نیکوکاران را تباه نمی‌کنیم.');
INSERT INTO "islamhouse" VALUES(12,57,'12:57','و یقیناً پاداش آخرت برای کسانی ‌که ایمان آورده‌اند و پرهیزگاری ‌کرده‌اند، بهتر است.');
INSERT INTO "islamhouse" VALUES(12,58,'12:58','و [چون سرزمین کنعان را خشکسالی فراگرفت] برادران یوسف [برای تهیۀ‌ گندم به مصر] آمدند و بر او وارد شدند. [او] آنها را شناخت در حالی‌ که آنان وی را نشناختند.');
INSERT INTO "islamhouse" VALUES(12,59,'12:59','هنگامی که بار‌هایشان را آماده کرد، [به آنان] گفت: «[نوبت آینده،] برادری را که از پدرتان دارید، نزد من بیاورید. آیا نمی‌بینید که من پیمانه را تمام می‌دهم و بهترین میزبانم؟');
INSERT INTO "islamhouse" VALUES(12,60,'12:60','و اگر او را نزدم نیاوردید، پیمانه‌ای نزد من نخواهید داشت و به من نزدیک نشوید».');
INSERT INTO "islamhouse" VALUES(12,61,'12:61','[برادران] گفتند: «او را از پدرش خواهیم خواست و حتماً این کار را می‌کنیم».');
INSERT INTO "islamhouse" VALUES(12,62,'12:62','و [یوسف] به غلامانش گفت: «سرمایه‌ها‌یشان را [که برای خرید غله آورده‌اند، مخفیانه] در بار‌هایشان بگذارید؛ شاید هنگامی ‌که به سوی خانوادۀ خود بازگشتند، آن را [ببینند و] بشناسند. امید است که بازآیند».');
INSERT INTO "islamhouse" VALUES(12,63,'12:63','هنگامی که به سوی پدر‌شان بازگشتند، گفتند: «پدر جان، پیمانه [و سهمِ غلّۀ نوبت آینده] را از ما بازداشتند؛ پس برادرمان [بنیامین] را با ما بفرست تا سهمی [از غلّه] بگیریم و ما حتماً مراقبش خواهیم بود».');
INSERT INTO "islamhouse" VALUES(12,64,'12:64','[یعقوب] گفت: «آیا همان‌ گونه که پیشتر شما را نسبت به برادرش [یوسف] امین داشتم، [این بار هم] بر او امین بدانم؟ الله بهترین نگهبان است و او مهربان‌ترینِ مهربانان است».');
INSERT INTO "islamhouse" VALUES(12,65,'12:65','و چون بار خود را گشودند، سرمایه‌شان را یافتند که به آنان بازگردانده شده بود. گفتند: «پدر جان، [دیگر] چه می‌خواهیم؟ این سرمایۀ ماست که به ما بازگردانده شده است؛ [به این وسیله] براى خانوادۀ خود آذوقه [فراوانی] مى‌آوریم و از برادرمان [نیز] محافظت مى‌كنیم و [با بردنِ بنیامین] یک بارِ شتر اضافه می‌گیریم. این پیمانه [برای عزیز مصر، کار] آسانی است».');
INSERT INTO "islamhouse" VALUES(12,66,'12:66','[یعقوب] گفت: «هرگز او را با شما نخواهم فرستاد تا با من به نام الله پیمان استوارى ببندید كه قطعاً او را نزد من بازمی‌آورید، مگر آنكه گرفتار [حادثه‌ای] شوید [و هیچ‌ یک نجات نیابید]». وقتی [عهد و] پیمان استوار خویش را به او دادند، [یعقوب] گفت: «الله بر آنچه می‌گوییم [گواه و] نگهبان است».');
INSERT INTO "islamhouse" VALUES(12,67,'12:67','و [همچنین] گفت: «ای پسرانم، [برای آنکه جلب‌توجه نکنید، همگی] از یک دروازه وارد [شهر] نشوید؛ بلکه از دروازه‌های مختلف وارد شوید و [البته من با این سفارش] نمی‌توانم چیزی از [قضا و قدَرِ] الله را از شما دور کنم. حُکم، جز برای الله نیست؛ بر او توکل کرده‌ام و توکل‌کنندگان باید بر او توکل ‌کنند.');
INSERT INTO "islamhouse" VALUES(12,68,'12:68','و وقتی همان گونه که پدرشان به آنان دستور داده بود وارد شدند، [این شیوۀ ورود،] آنان را از [قضای] الله بازنداشت؛ ولی [هر چه بود،] خواسته قلبی یعقوب را برآورده کرد. او از [بركتِ‌] آنچه به او آموخته بودیم، داراى دانشى [فراوان‌] بود؛ ولى بیشتر مردم نمى‌دانند.');
INSERT INTO "islamhouse" VALUES(12,69,'12:69','و چون [برادران] بر یوسف وارد شدند، برادرش [بنیامین] را نزد خود جای داد [و به آهستگی به او] گفت: «من برادرت هستم؛ و از کارهایی که [برادرانمان] می‌کردند اندوهگین نباش».');
INSERT INTO "islamhouse" VALUES(12,70,'12:70','هنگامی‌ که بارهایشان را آماده کرد، جام [پادشاه] را در بارِ برادرش [بنیامین] گذاشت؛ آنگاه ندادهنده‌ای بانگ برآورد [که]: «ای کاروانیان، قطعاً شما دزد هستید».');
INSERT INTO "islamhouse" VALUES(12,71,'12:71','[برادرانِ یوسف] رو به آنان ‌کردند و گفتند: «چه گم کرده‌اید؟»');
INSERT INTO "islamhouse" VALUES(12,72,'12:72','[کارگزاران] گفتند: «پیمانۀ پادشاه را گم کرده‌ایم و [آن که بانگ برآورده بود گفت:] هر کس آن را بیاورد، یک بار شتر [جایزه] دارد و من ضامن این [پاداش] هستم».');
INSERT INTO "islamhouse" VALUES(12,73,'12:73','[برادران یوسف] گفتند: «به الله سوگند، شما می‌دانید که ما نیامده‌ایم تا در این سرزمین فساد کنیم و ما [هرگز] دزد نبوده‌ایم».');
INSERT INTO "islamhouse" VALUES(12,74,'12:74','[آنان] گفتند: «اگر دروغگو باشید، کیفرش چیست؟»');
INSERT INTO "islamhouse" VALUES(12,75,'12:75','گفتند: «کیفر کسی ‌که [آن پیمانه] در بارش پیدا شود، [این است که] خودش کیفر آن باشد [و به بردگیِ شما درآید]. ما ستمکاران را این گونه مجازات می‌کنیم».');
INSERT INTO "islamhouse" VALUES(12,76,'12:76','آنگاه [یوسف] پیش از بار برادرش، شروع به [جست‌وجوی] بار‌های آنان کرد؛ سپس آن [پیمانه] را از بار برادرش بیرون کشید. این گونه برای یوسف چاره‌اندیشی کردیم؛ [تا بتواند بنیامین را نزد خود نگه دارد؛ زیرا او] طبق آیین پادشاه [مصر] نمى‌توانست برادرش را بازداشت كند، مگر اینكه الله بخواهد. ما هر که را بخواهیم [به] درجاتی بالا مى‌بریم؛ و [بدانید که] فراتر از هر صاحب‌دانشى، دانشورى است.');
INSERT INTO "islamhouse" VALUES(12,77,'12:77','[برادران] گفتند: «اگر او دزدی کرده است [شگفت نیست؛ چرا که] برادرش [نیز] پیش از این دزدی کرده بود. یوسف آن را در دل خویش پنهان داشت و [ناراحتی‌ را] بر آنان آشکار نکرد [و با خود] گفت: «شما از نظر جایگاه، [از آنچه وانمود مى‌كنید] بد‌ترید و الله به آنچه بیان می‌کنید، داناتر است».');
INSERT INTO "islamhouse" VALUES(12,78,'12:78','گفتند: «ای عزیز، او پدر پیری دارد [که طاقت دوری‌ فرزندش را ندارد]؛ پس یکی از ما را به جای او بگیر. به راستی که ما تو را از نیکوکاران می‌بینیم».');
INSERT INTO "islamhouse" VALUES(12,79,'12:79','[یوسف] گفت: «پناه بر الله از اینکه جز آن كسی را كه كالایمان را نزدش یافته‌ایم بازداشت كنیم! در این صورت [اگر کس دیگری را به جای او بازداشت کنیم،] قطعاً ستمكار خواهیم بود».');
INSERT INTO "islamhouse" VALUES(12,80,'12:80','هنگامی که [برادران] از او ناامید شدند، نجواکنان [برای مشورت] به گوشه‌ای رفتند. [برادر] بزرگشان گفت: «مگر نمى‌دانید كه پدرتان با نام الله پیمانى استوار از شما گرفته است و پیشتر [نیز‌] در مورد یوسف چه کوتاهی‌ای كردید؟ من هرگز از این سرزمین نمى‌روم تا پدرم به من اجازه [بازگشت] دهد یا [آنکه] الله درباره‌ام داورى كند [که] بی‌تردید، او بهترین داور است.');
INSERT INTO "islamhouse" VALUES(12,81,'12:81','[شما] نزد پدر‌تان بازگردید و بگویید: ای پدر، پسرت دزدی کرد و ما جز به آنچه می‌دانستیم گواهی ندادیم و از غیب آگاه نبودیم [که بدانیم دزدی می‌کند].');
INSERT INTO "islamhouse" VALUES(12,82,'12:82','و از [مردم] شهری که در آن بودیم و از کاروانی که با آن آمدیم بپرس؛ و ما قطعاً راستگوییم».');
INSERT INTO "islamhouse" VALUES(12,83,'12:83','[یعقوب] گفت: «[چنین نیست؛] بلکه [هوای] نفس شما کاری [ناشایست] را برایتان آراسته است؛ پس [چارۀ کار،‌] صبرى نیكوست. امید است که الله همۀ آنان را به من برساند. بی‌تردید، او دانای حکیم است».');
INSERT INTO "islamhouse" VALUES(12,84,'12:84','و از آنان روی برگرداند و گفت: «دریغ از یوسف!» و چشمانش از شدت اندوه [و گریستن برای فرزندش] سفید شد و همچنان اندوه خود را فرومى‌خورد.');
INSERT INTO "islamhouse" VALUES(12,85,'12:85','[پسرانش] گفتند: «به الله سوگند، تو [آنقدر] پیوسته از یوسف یاد می‌کنی تا [عاقبت،] به سختی بیمار شوی یا بمیری».');
INSERT INTO "islamhouse" VALUES(12,86,'12:86','[یعقوب] گفت: «من از درد و اندوه خویش به [درگاه] الله می‌نالم و از [لطف و بزرگیِ] الله چیزهایی می‌دانم که شما نمی‌دانید؛');
INSERT INTO "islamhouse" VALUES(12,87,'12:87','ای پسرانم، بروید و دربارۀ یوسف و برادرش جستجو کنید [و خبر بگیرید] و از رحمتِ الله مأیوس نشوید؛ [چرا که] جز گروه کافران، کسی از رحمت الله مأیوس نمی‌گردد».');
INSERT INTO "islamhouse" VALUES(12,88,'12:88','وقتی [پسران یعقوب به مصر رفتند و] بر او [= یوسف] وارد شدند، گفتند: «ای عزیز، به ما و خاندانمان [در اثر فقر و خشکسالی] سختی فراوانی رسیده است و [اینک] کالای ناچیز [و مال اندکی برای خرید آذوقه] با خود آورده‌ایم؛ پس پیمانه را برایمان کامل کن و بر ما صدقه [و بخشش] کن. بی‌تردید، الله بخشندگان را پاداش می‌دهد».');
INSERT INTO "islamhouse" VALUES(12,89,'12:89','[یوسف‌] گفت: «آیا دانستید که وقتی [از عاقبتِ کارتان] بی‌خبر بودید، در حق یوسف و برادرش چه کردید؟»');
INSERT INTO "islamhouse" VALUES(12,90,'12:90','[آنان] گفتند: «آیا به راستی تو [همان] یوسفی؟» او گفت: «[آری،] من یوسفم و این برادر من است. یقیناً الله بر ما منّت نهاد. به راستی، هر کس پرهیزگاری و شکیبایی کند، [بداند که] بی‌تردید، الله پاداش نیکو‌کاران را تباه نمی‌کند».');
INSERT INTO "islamhouse" VALUES(12,91,'12:91','[برادرانش] گفتند: «به الله سوگند [که] یقیناً الله تو را بر ما برتری داده است و ما قطعاً خطاکار بودیم».');
INSERT INTO "islamhouse" VALUES(12,92,'12:92','[یوسف] گفت: «امروز هیچ سرزنشی بر شما نیست. الله شما را می‌آمرزد و او مهربان‌ترینِ مهربانان است.');
INSERT INTO "islamhouse" VALUES(12,93,'12:93','[اکنون] این پیراهن مرا ببرید و بر صورت پدرم بیندازید تا بینا گردد و [سپس] همۀ خانواده‌ خود را نزد من بیاورید».');
INSERT INTO "islamhouse" VALUES(12,94,'12:94','و چون کاروان [از مصر به سوی کنعان] به راه افتاد، پدر‌شان [به اطرافیانش] گفت: «اگر مرا کم‌خرد نشمارید، [می‌گویم که] یقیناً بوی یوسف را احساس می‌کنم».');
INSERT INTO "islamhouse" VALUES(12,95,'12:95','[آنان] گفتند: «به الله سوگند که حتماً [باز هم] در اشتباه دیرینه‌ات هستی».');
INSERT INTO "islamhouse" VALUES(12,96,'12:96','هنگامی که مژده‌رسان آمد و آن [پیراهن] را بر چهرۀ‌ او افکند، ناگهان بینا شد [و] گفت: «آیا به شما نگفتم که من از [لطف و بزرگی] الله چیزهایی می‌دانم که شما نمی‌دانید؟»');
INSERT INTO "islamhouse" VALUES(12,97,'12:97','گفتند: «ای پدر، برای ما [به خاطرِ] گناهانمان آمرزش بخواه [چرا که] یقیناً ما گناهکار بودیم».');
INSERT INTO "islamhouse" VALUES(12,98,'12:98','[یعقوب] گفت: «به زودی از پروردگارم برایتان آمرزش می‌طلبم. بی‌تردید، او آمرزندۀ مهربان است».');
INSERT INTO "islamhouse" VALUES(12,99,'12:99','[همگی به مصر رفتند و] چون بر یوسف وارد شدند، او پدر و مادرش را [در آغوش گرفت و] نزد خود جای داد و گفت: «[همگی] به مصر درآیید که ان شاء الله [از هر گونه آزاری] ایمن خواهید بود».');
INSERT INTO "islamhouse" VALUES(12,100,'12:100','و پدر و مادرش را بر تخت نشاند و [همگی] در برابرش به سجده افتادند و [یوسف] گفت: «پدر جان، این تعبیر [همان] خوابم است که پیشتر دیده بودم. پروردگارم آن را راست گردانْد [و تحقق بخشید]. یقیناً او به من نیکی کرد ‌که مرا از زندان بیرون آورد و ـ پس از آنکه شیطان میان من و برادرانم فتنه برپا کرد ـ شما را از بیابان [کنعان به مصر] آورد. بی‌تردید، پروردگارم در آنچه می‌خواهد، باریک‌بین است. به راستی که او دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(12,101,'12:101','پروردگارا، مرا از فرمانروایی [بهره‌ای] عطا کردی و از علم تعبیر خواب‌ها به من آموختی. ای پدیدآورندۀ آسمان‌ها و زمین، تویی که در دنیا و آخرت [دوست و] کارسازِ منی. مرا مسلمان [= فرمانبردار] بمیران و به شایستگان ملحق فرما».');
INSERT INTO "islamhouse" VALUES(12,102,'12:102','[ای پیامبر،] این [داستان،] از خبرهای غیب است که به تو وحی می‌کنیم؛ و هنگامی ‌که [برادران یوسف علیه وی] بداندیشی می‌کردند و نیرنگ می‌زدند، تو نزدشان نبودی.');
INSERT INTO "islamhouse" VALUES(12,103,'12:103','و بیشتر مردم مؤمن نخواهند شد؛ حتی اگر [برای ایمان آوردنشان] حرص بورزی‌.');
INSERT INTO "islamhouse" VALUES(12,104,'12:104','تو از آنان برای این [رسالت‌] پاداشی نمی‌خواهی. این [قرآن، چیزی] نیست، مگر پندی برای جهانیان.');
INSERT INTO "islamhouse" VALUES(12,105,'12:105','و چه بسیار نشانه‌ها در آسمان‌ها و زمین وجود دارد كه بر آنها مى‌گذرند و[لی] از آنها روى برمى‌گردانند [و توجه نمى‌كنند].');
INSERT INTO "islamhouse" VALUES(12,106,'12:106','و بیشتر آنان [در ظاهر] به الله ایمان نمی‌آورند، مگر آنکه [در عمل، به نوعی‌] مشرکند.');
INSERT INTO "islamhouse" VALUES(12,107,'12:107','آیا [مشرکان] ایمن هستند از اینکه عذابی فراگیر از جانب الله بر آنان فرود آید یا در حالی که غافلند، ناگهان قیامت آنان را فرا گیرد؟');
INSERT INTO "islamhouse" VALUES(12,108,'12:108','[ای پیامبر، به مردم] بگو: «این راه من است. من با دلیلی آشکار به سوی الله دعوت می‌کنم و کسانی‌ که از من پیروی کردند [نیز چنین می‌کنند]؛ و الله پاک و منزّه است و من از مشرکان نیستم».');
INSERT INTO "islamhouse" VALUES(12,109,'12:109','و ما پیش از تو جز مردانی از اهل [همین‌] شهرها [را به پیامبری] نفرستاده‌ایم که به آنان وحی می‌کردیم. آیا در زمین نگشته‌اند تا ببینند سرانجامِ کسانی که پیش از آنان بودند چگونه بوده است؟ و یقیناً سرای آخرت برای پرهیزگاران بهتر است. آیا نمی‌اندیشید؟');
INSERT INTO "islamhouse" VALUES(12,110,'12:110','[ما همچنان به کافران مهلت می‌دادیم] تا آنگاه که پیامبران [از ایمان‌آوردن اقوام‌شان] مأیوس شدند و [مردم] پنداشتند [در آنچه رسولان از جانب الله آورده‌اند،] به آنان دروغ گفته شده است. [در این هنگام] یاریِ ما به سراغشان آمد. پس هر کس را که خواستیم، نجات یافت؛ و[لى‌] عذاب سختِ ما از گروه مجرمان بازگردانده نمی‌شود.');
INSERT INTO "islamhouse" VALUES(12,111,'12:111','یقیناً در داستان آنان برای خردمندان عبرتی است. این [قرآن] سخنى نیست كه به دروغ ساخته شده باشد؛ بلکه تصدیق‌کنندۀ آنچه [از کتاب‌هایی] است كه پیش از آن [آمده] است و روشنگرِ همه چیز است و رهنمود و رحمتی است برای گروهی که ایمان می‌آورند.');
INSERT INTO "islamhouse" VALUES(13,1,'13:1','المر [= الف. لام. میم. را]. این آیاتِ کتاب [آسمانی] و آنچه از [سوی] پروردگارت بر تو نازل شده است، حق است؛ ولی بیشتر مردم ایمان نمی‌آورند.');
INSERT INTO "islamhouse" VALUES(13,2,'13:2','الله است که آسمان‌ها را بدون ستون‌هایی که آنها را ببینید برافراشت، آنگاه [آن گونه كه شايسته جلال و مقام اوست] بر عرش قرار گرفت؛ و خورشید و ماه را به خدمت [انسان] گماشته است که هر یک تا زمانی معیّن در [مدار خود] در حرکت باشند. [الله] کار [جهان‌] را تدبیر می‌کند و آیات [خویش‌] را به روشنی بیان می‌دارد؛ باشد که به دیدارِ پروردگارتان یقین پیدا کنید.');
INSERT INTO "islamhouse" VALUES(13,3,'13:3','و او همان ذاتی است كه زمین را بگسترد و در آن، کوه‌های استوار و جویبارها پدید آورد و در آن از هر گونه میوه‌اى دو گونه آفرید؛ شب را به روز [و روز را به شب‌] می‌پوشانَد. قطعاً در این [امور] براى مردمى كه می‌اندیشند نشانه‌هایی [از قدرت پروردگار] است.');
INSERT INTO "islamhouse" VALUES(13,4,'13:4','و در زمین، قطعاتی است در کنار هم [که با یکدیگر متفاوتند]؛ و [نیز] باغ‌هایی از انگور و کشتزارها و درختان خرما که [نر و ماده‌اند و] گاه از یک ریشه می‌رویند و گاه از ریشه‌های متفاوتند و [همۀ آنها] با یک آب سیراب می‌شوند. و [با این حال،] بعضی از آنها را در [طعم و خواص] میوه بر دیگری برتری می‌دهیم. بی‌تردید، در این [امر نیز] برای مردمی که خرد می‌ورزند، نشانه‌های [روشنى‌] است.');
INSERT INTO "islamhouse" VALUES(13,5,'13:5','و [ای پیامبر،] اگر [از بی‌ایمانیِ کافران] تعجب می‌کنی، عجیب[تر] گفتار آنان است كه [دربارۀ معاد می‌گویند:] «آیا وقتى خاک شدیم، واقعاً [زنده می‌شویم و] در آفرینش جدیدی خواهیم بود؟» اینانند كه به پروردگارشان كفر ورزیده‌اند و در گردن‌هایشان [از آتش،] زنجیرهاست و آنان اهل آتشند [و] در آن جاودانند.');
INSERT INTO "islamhouse" VALUES(13,6,'13:6','و [ای پیامبر، مشرکان] پیش از نیکی [= رحمت] به شتاب از تو بدی [= عذاب] را درخواست می‌کنند و حال آنكه پیش از اینان [نیز بر كافران‌] عقوبت‌های عبرت‌آموز رفته است؛ و به راستى، پروردگارت نسبت به مردم ـ با وجود ستمکاری‌شان ‌ـ آمرزنده است؛ و بی‌تردید، پروردگارت سخت‌كیفر است.');
INSERT INTO "islamhouse" VALUES(13,7,'13:7','و کسانی‌ که کفر ورزیدند می‌گویند: «چرا نشانه‌ها [و معجزاتی همانند موسی و عیسی] از پروردگارش بر او نازل نشده است؟» [ای پیامبر،] بی‌تردید، تو فقط بیم‌دهنده‌ای؛ و هر قومی، [پیامبرِ] هدایتگری دارد.');
INSERT INTO "islamhouse" VALUES(13,8,'13:8','الله مى‌داند آنچه را كه هر ماده‌اى [در رحِم‌] بار مى‌گیرد و [نیز] آنچه را كه رحِم‌ها مى‌كاهند و آنچه را مى‌افزایند؛ و هر چیزى نزد او به اندازه [و دقیق] است.');
INSERT INTO "islamhouse" VALUES(13,9,'13:9','[او] دانای نهان و آشکار [و] بزرگِ بلندمرتبه است.');
INSERT INTO "islamhouse" VALUES(13,10,'13:10','[ای مردم،] هر یک از شما که سخن [خود] را پنهان کند یا آن را آشكارا بیان سازد و هر کس در [تاریکیِ] شب پنهان گردد یا در روز [به دنبال انجام کارهایش] برود، [در علم الهی] یکسان است [و او همه را می‌داند].');
INSERT INTO "islamhouse" VALUES(13,11,'13:11','برای او [= انسان] فرشتگانی است که همواره از پیش رو و از پشت سرش، او را به فرمان الله حفاظت می‌کنند. بی‌گمان، الله حال [و سرنوشت نیکوی] هیچ قومی را تغییر نمی‌دهد، مگر آنکه آنان آنچه را که در دل‌هایشان دارند دگرگون سازند [و مرتکب گناه شوند]؛ و هنگامی ‌که الله برای قومی [گرفتاری و] بدی بخواهد، هیچ چیز مانع آن نخواهد شد و آنان جز [ذات پاکِ] او هیچ کارسازی ندارند.');
INSERT INTO "islamhouse" VALUES(13,12,'13:12','اوست که برق را برای بیم[از صاعقه و آتش‌] و امید [به باران‌] به شما می‌نمایانَد و ابرهای گرانبار را پدید می‌آورَد.');
INSERT INTO "islamhouse" VALUES(13,13,'13:13','رعد به ستایش او و [تمام] فرشتگان از خوف و خشیت، او را تسبیح می‌گویند؛ و [اوست که‌] صاعقه‌ها را می‌فرستد؛ و آن [صاعقۀ مرگبار] به هر کس که او بخواهد برخورد می‌کند؛ و آنان [= کافران] دربارۀ [یگانگیِ] الله مجادله می‌کنند و او تعالی سختگیر [و سخت‌کیفر] است.‌');
INSERT INTO "islamhouse" VALUES(13,14,'13:14','دعای حقیقی [و عبادت به یگانگی] فقط از آنِ او [= الله] است؛ و کسانی را که [مشرکان] به جای او [به دعا و یاری] می‌خوانند، به هیچ وجه دعایشان را اجابت نمی‌کنند؛ مگر مانند كسى كه دو دستش را به سوى آب بگشاید تا [آب‌] به دهانش برساند، در حالى كه [هیچ آبی‌] به [دهان‌] او نمی‌رسد؛ و دعای کافران [در برابر معبودهایشان] جز در گمراهی نیست.');
INSERT INTO "islamhouse" VALUES(13,15,'13:15','و هر کس [و هر چه] در آسمان‌ها و زمین است ـ خواه و ناخواه ـ و [حتی] سایه‌هایشان، بامداد و شامگاه براى الله سجده [و فروتنی] مى‌كنند.');
INSERT INTO "islamhouse" VALUES(13,16,'13:16','[ای پیامبر، به مشرکان] بگو: «پروردگار آسمان‌ها و زمین کیست؟» بگو: «الله». [سپس] بگو: «آیا به جای او [دوستان و] کارسازانی برگزیده‌اید که [حتی] مالک سود و زیان خود نیستند؟». بگو: «آیا نابینا و بینا برابرند؟ یا تاریکی‌ها و نور یکسانند؟ یا آنان شریکانی برای الله قرار داده‌اند که همچون آفرینش او آفریده‌اند و [در نتیجه، امرِ] آفرینش بر آنان مُشتبه شده است؟» بگو: «الله آفرینندۀ همه چیز است و او یگانۀ پیروزمند است.');
INSERT INTO "islamhouse" VALUES(13,17,'13:17','[الله] از آسمان آبی فرو بارید؛ آنگاه رودها هر یک به اندازۀ [گنجایش] خود جاری شدند و سیلاب، کف روی خود را برآورد؛ و از آنچه بر آتش می‌افروزند [و می‌گدازند] تا زیور یا کالایی به دست آورند [نیز] کفی همانند آن [آب‌ برمی‌آید]. الله حق و باطل را اینچنین مثال می‌زند. و اما کف، به کناری می‌رود و نیست می‌گردد؛ ولی آنچه به مردم سود می‌رسانَد بر زمین [باقی] می‌مانَد. الله مَثَل‌ها را اینچنین بیان می‌کند.');
INSERT INTO "islamhouse" VALUES(13,18,'13:18','برای کسانی‌ که [دعوت] پروردگارشان را اجابت کردند، پاداش نیک [= بهشت در پیش] است؛ و کسانی‌ که [دعوت] او را اجابت نکردند، اگر تمام آنچه در زمین است و [نیز] همانندش را با آن داشتند، یقیناً [حاضر بودند که همه را] برای رهاییِ خویش [از عذاب] بپردازند، [ولی از آنان پذیرفته نخواهد شد]. آنان حسابی بد [و بازخواستی بسیار سخت] دارند و جایگاهشان دوزخ است و چه بد جایگاهی است!');
INSERT INTO "islamhouse" VALUES(13,19,'13:19','آیا کسی ‌که می‌داند آنچه از [طرف] پروردگارت بر تو نازل شده حق است، مانند کسی است که [نسبت به حق] نابیناست؟ تنها خردمندان پند می‌گیرند.');
INSERT INTO "islamhouse" VALUES(13,20,'13:20','[همان] کسانی‌ که به پیمان الله وفا می‌کنند و عهد نمی‌شکنند.');
INSERT INTO "islamhouse" VALUES(13,21,'13:21','و كسانى كه هر آنچه را الله به پیوند کردنِ آن فرمان داده است مى‌پیوندند [= صلۀ رحِم برقرار می‌کنند] و از پروردگارشان مى‌ترسند و از سختیِ حساب می‌هراسند.');
INSERT INTO "islamhouse" VALUES(13,22,'13:22','و کسانی‌ که برای کسب خشنودی پروردگار‌شان شکیبایی کرده‌اند و نماز بر پا داشته‌اند و از آنچه به آنان روزی داده‌ایم، پنهان و آشکارا انفاق کرده‌اند و بدی را با نیکی می‌زدایند. آنانند که فَرجامی نیک‌ خواهند داشت.');
INSERT INTO "islamhouse" VALUES(13,23,'13:23','[پاداششان] باغ‌های جاودان [بهشت است] که آنان و هر یک از پدران و همسران و فرزندانشان که نیکوکار بوده‌اند، به آن وارد می‌شوند و فرشتگان از هر دری بر آنان درمی‌آیند.');
INSERT INTO "islamhouse" VALUES(13,24,'13:24','[و به آنان می‌گویند:] «سلام بر شما به [پاداشِ] آنچه [بر ایمانتان] شکیبایی کرده‌اید. پس چه نیکوست فرجامِ آن سرای!»');
INSERT INTO "islamhouse" VALUES(13,25,'13:25','و کسانی‌ که پیمان الله را پس از استوارکردنش می‌شکنند و آنچه را که الله به پیوستنش فرمان داده است [= صلۀ رحِم] می‌گسلند و در زمین [فتنه و] فساد می‌کنند، اینانند که [در آخرت] لعنت و فَرجامی بد دارند.');
INSERT INTO "islamhouse" VALUES(13,26,'13:26','الله [نعمت و] روزی را بر هر کس که بخواهد گسترده می‌دارد و یا تنگ می‌کند؛ و [کافران] به زندگی دنیا شاد شدند و[لی] زندگی دنیا در [برابر] آخرت، جز بهره‌ای [ناچیز] نیست.');
INSERT INTO "islamhouse" VALUES(13,27,'13:27','و کسانی ‌که کفر ورزیدند می‌گویند: «چرا نشانه‌ای از [سوی] پروردگارش بر او نازل نشده است؟» بگو: «بی‌تردید، الله هر کس را که بخواهد گمراه می‌کند؛ و هر کس را که توبه نماید به سوی خویش هدایت می‌کند».');
INSERT INTO "islamhouse" VALUES(13,28,'13:28','[همان‌] کسانی که ایمان آورده‌اند و دل‌هایشان به یاد الله آرام می‌گیرد. آگاه باشید که تنها با یاد الله دل‌ها آرام می‌گیرد.');
INSERT INTO "islamhouse" VALUES(13,29,'13:29','کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، خوشا بر آنان! و سرانجامی نیک دارند.');
INSERT INTO "islamhouse" VALUES(13,30,'13:30','[ای پیامبر،] این‌گونه، تو را بین امتی فرستادیم که پیش از آنها امت‌هایی [دیگر] آمده [و رفته‌] بودند تا آنچه بر تو وحی کرده‌ایم بر آنان بخوانی و حال آنکه آنان به [پروردگار] رحمان کفر می‌ورزند. بگو: «او پروردگار من است [و] معبودی [به‌حق] جز او نیست؛ بر او توکل کرده‌ام و بازگشتم به سوی اوست».‌');
INSERT INTO "islamhouse" VALUES(13,31,'13:31','و [حتی] اگر قرآنی بود که کوه‌ها از [هیبت‌] آن به حرکت درمی‌آمد و زمین پاره‌پاره می‌شکافت یا مردگان از [تلاوتِ] آن به سخن درمی‌آمدند [باز هم کافران ایمان نمی‌آوردند]. امر [تدبیر جهان و نزول معجزات،] از آنِ الله است. آیا کسانی که ایمان آورده‌اند نمی‌دانند که اگر الله می‌خواست قطعاً همۀ مردم را هدایت می‌کرد؟ و كسانى كه كفر ورزیدند، پیوسته به [سزاى‌] آنچه كرده‌اند گرفتار مصیبت كوبنده‌اى مى‌شوند یا [عذاب الهی] نزدیک خانه‌هایشان فرود مى‌آید تا [سرانجام] وعدۀ الله فرارسد. بی‌تردید، الله خُلف وعده نمى‌كند.');
INSERT INTO "islamhouse" VALUES(13,32,'13:32','[ای پیامبر،] یقیناً پیامبرانی که پیش از تو بودند [نیز] مورد تمسخر واقع شدند، آنگاه به کسانی که کفر ورزیدند مهلت دادم، سپس آنان را [به عذاب] فروگرفتم. پس [بنگر که‌] کیفرِ من چگونه بود.‌');
INSERT INTO "islamhouse" VALUES(13,33,'13:33','آیا آن [پروردگاری] که حاکم بر هر کس و [ناظر بر] رفتار و کردارِ اوست [به عبادت سزاوارتر است یا معبودهای باطل و بی‌خبر]؟ و [کافران] شریکانی براى الله قرار دادند. بگو: «آنان را نام ببرید». آیا [می‌خواهید به] او تعالی دربارۀ آنچه در زمین است و او نمى‌داند خبر ‌دهید، یا فقط سخنى سطحى [و بی‌اساس‌] مى‌گویید؟ [چنین نیست‌؛] بلكه براى كسانى كه كفر ورزیده‌اند، نیرنگشان آراسته شده است و از راه [حق‌] بازداشته شده‌اند؛ و هر کس که الله او را گمراه گذارد، هدایتگری نخواهد داشت.');
INSERT INTO "islamhouse" VALUES(13,34,'13:34','آنان در زندگی دنیا عذاب [درد‌ناکی] دارند و قطعاً عذاب آخرت سخت‌تر است؛ و در برابر [عذابِ] الله، هیچ [یار و] محافظی ندارند.');
INSERT INTO "islamhouse" VALUES(13,35,'13:35','ویژگی بهشتی که به پرهیزگاران وعده داده شده [چنین است که:] جویبارها از زیر [درختان] آن جاری است، میوه‌هایش همیشگی و سایه‌اش [نیز دائمی است]. این، سرانجامِ کسانی است ‌که پرهیزگاری کرده‌اند؛ و سرانجامِ کافران، آتش [دوزخ] است.');
INSERT INTO "islamhouse" VALUES(13,36,'13:36','و كسانى كه به آنان كتاب آسمانى داده‌ایم، از آنچه بر تو نازل شده است شادمان مى‌شوند و برخى از گروه‌ها[ی اهل کتاب]، بخشى از آن را انكار مى‌كنند. [ای پیامبر، به آنان] بگو: «جز این نیست كه من دستور یافته‌ام الله را عبادت کنم و به او شرک نورزم؛ به سوى او دعوت مى‌دهم و بازگشتم به سوى اوست».');
INSERT INTO "islamhouse" VALUES(13,37,'13:37','و این‌گونه، این [قرآن] را فرمانى به زبان عربى [فصیح] نازل كردیم؛ و اگر پس از دانشى كه [از جانب پروردگار] برایت آمده است، از هوس‌های آنان [= مشرکان] پیروى كنى، در برابر [عذابِ] الله هیچ کارساز و نگهدارنده‌ای نخواهى داشت.');
INSERT INTO "islamhouse" VALUES(13,38,'13:38','و بی‌تردید، پیش از تو [نیز] پیامبرانی فرستادیم و برای آنان [مانند دیگر انسان‌ها] همسران و فرزندانی قرار دادیم؛ و برای هیچ پیامبری سزاوار نیست که جز به فرمان الله [معجزه و] آیه‌ای بیاورد. هر امری، [تقدیر و] نوشته‌ای [معلوم] دارد.');
INSERT INTO "islamhouse" VALUES(13,39,'13:39','الله هر چه را که بخواهد از میان می‌برد و [آنچه را بخواهد] استوار می‌دارد؛ و اُمّ الکتاب [= لوح محفوظ] نزد اوست.');
INSERT INTO "islamhouse" VALUES(13,40,'13:40','و [ای پیامبر،] اگر برخی از آنچه را که به آنان وعده می‌دهیم به تو نشان دهیم یا [پیش از فرارسیدنِ عذاب،] تو را بمیرانیم، [در هر حال] فقط تبلیغ [رسالت] بر [عهدۀ] توست و حساب [و مجازاتشان] بر ماست.');
INSERT INTO "islamhouse" VALUES(13,41,'13:41','آیا [کافران] ندیده‌اند که ما [با فتوحات لشکرِ اسلام،] از دامنه‌های سرزمین [کفر] می‌کاهیم [و بر قلمروی اسلام می‌افزاییم‌]؟ و الله حکم می‌کند و [هیچ] بازدارنده‌ای برای حُکمش نیست و او در حسابرسی سریع است‌.');
INSERT INTO "islamhouse" VALUES(13,42,'13:42','و به راستی، کسانی ‌که پیش از آنان بودند [برای مقابله با پیامبرانشان] مکر ورزیدند؛ ولی تمام مکرها [و تدبیرها] از آنِ الله است. او تعالی می‌داند که هر ‌کسی چه کاری انجام می‌دهد؛ و به زودی کافران خواهند دانست که فرجامِ [نیکوی] سرای [آخرت] از آنِ کیست.');
INSERT INTO "islamhouse" VALUES(13,43,'13:43','و کسانی‌ که کفر ورزیدند می‌گویند: «[ای محمد،] تو رسول [الله] نیستی». بگو: «کافی است که الله و کسانی‌ که دانش [کتاب‌های آسمانیِ پیشین] نزدشان است، میان من و شما گواه باشند».');
INSERT INTO "islamhouse" VALUES(14,1,'14:1','الر [= الف. لام. را]. این [قرآن] کتابی است که بر تو نازل کردیم تا مردم را به فرمان پروردگارشان، از تاریکی‌ها[ی شرک و جهل] به روشنایی [ایمان و دانش] بیرون آوری؛ به راه [پروردگارِ] شکست‌ناپذیرِ ستوده.');
INSERT INTO "islamhouse" VALUES(14,2,'14:2','[همان] الله که آنچه در آسمان‌ها و زمین است از آنِ اوست؛ و وای بر کافران از عذاب سخت!');
INSERT INTO "islamhouse" VALUES(14,3,'14:3','کسانی‌ که زندگی دنیا را بر آخرت ترجیح می‌دهند و [مردم را] از راه الله بازمی‌دارند و آن راه را منحرف [و سازگار با هوای نفس‌شان] می‌خواهند، اینان در گمراهی دور و درازی هستند.');
INSERT INTO "islamhouse" VALUES(14,4,'14:4','ما هیچ پیامبری را جز به زبان قومش نفرستادیم تا [بتواند پیام الهی را] برایشان بیان کند. الله هر ‌کس را که بخواهد [به عدالتِ خویش] گمراه می‌سازد و [به فضل خویش] هر ‌کس را که بخواهد هدایت می‌کند و او شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(14,5,'14:5','و به راستی، ما موسی را با نشانه‌های روشنِ خود فرستادیم [و به او دستور دادیم] که: «قومت را از تاریکی‌ها[ی کفر] به روشنایی [توحید] بیرون آور و روزهای الله را به آنان یادآوری کن. بی‌تردید، در این [یادآوری]، برای هر شکیبای شکرگزار، نشانه‌هایی [از یگانگی و بزرگیِ الله] است.');
INSERT INTO "islamhouse" VALUES(14,6,'14:6','و [ای پیامبر، یاد کن از] هنگامی‌ که موسی به قومش گفت: «نعمت الله را بر خود به یاد آورید، آنگاه که شما را از [چنگال] فرعونیان نجات داد، [همان کسانی] که بدترین [و خفت‌بارترین] عذاب را بر سرتان می‌آوردند: پسرانتان را می‌کشتند و زنانتان را [برای کنیزی] زنده می‌گذاشتند؛ و در این [امر]، آزمایشی بزرگ از جانب پروردگارتان برای شما بود [تا شکیبایی‌تان را بیازماید]».');
INSERT INTO "islamhouse" VALUES(14,7,'14:7','و [یاد کنید از] هنگامی که پروردگارتان اعلام کرد: «اگر شکر گزارید، یقیناً [نعمتم را] بر شما می‌افزایم و اگر ناسپاسی کنید، یقیناً عذاب من سخت است».');
INSERT INTO "islamhouse" VALUES(14,8,'14:8','و موسی به [آنان] گفت: «اگر شما و همۀ مردم روی زمین کافر شوید، [هیچ زیانی به الله نمی‌رسد؛ چرا که] بی‌تردید، الله بی‌نیازِ ستوده است».');
INSERT INTO "islamhouse" VALUES(14,9,'14:9','[ای کافران،] آیا خبر [هلاکتِ] کسانی ‌که پیش از شما بودند، ـ [مانند] قوم نوح و عاد و ثمود ـ و [نیز] کسانی كه بعد از آنان بودند [و] جز الله كسى از آنان خبر ندارد، به شما نرسیده است؟ پیامبرانشان با دلایل [و معجزات] روشن به سویشان آمدند؛ ولى آنان [به نشانۀ مخالفت] دست‌های خود را بر دهان‌هایشان نهادند و [انگشت به دندان گزیدند و] گفتند: «ما آنچه را که شما برای [ابلاغِ] آن فرستاده شده‌اید انکار می‌کنیم و دربارۀ آنچه ما را به سویش مى‌خوانید، سخت در تردید هستیم».');
INSERT INTO "islamhouse" VALUES(14,10,'14:10','پیامبرانشان گفتند: «آیا در [یگانگیِ] الله ـ آفرینندۀ آسمان‌ها و زمین ـ تردیدی هست؟ او شما را [به ایمان] فرامی‌خوانَد تا گناهانتان را ببخشاید و تا مدتی معیّن به شما مهلت دهد». آنان گفتند: «شما جز بشری همانند ما نیستید [و] می‌خواهید ما را از آنچه که پدرانمان عبادت می‌کردند بازدارید. پس دلیلی آشکار [در مورد راستگویی‌تان] برایمان بیاورید».');
INSERT INTO "islamhouse" VALUES(14,11,'14:11','پیامبرانشان به آنان گفتند: «[آری،‌] ما جز انسان‌هایی مانند شما نیستیم؛ ولی الله بر هر یک از بندگانش که بخواهد [با نعمتِ نبوت] منت می‌نهد؛ و سزاوار نیست که ما جز به اجازۀ الله دلیلی برای شما بیاوریم؛ پس مؤمنان باید بر الله توکل کنند.');
INSERT INTO "islamhouse" VALUES(14,12,'14:12','و چرا بر الله توكل نكنیم، و حال آنكه او ما را به راه‌های [سعادت]مان هدایت نمود؟ و ما بر آزارى كه شما [در راه رسالت] به ما می‌رسانید، قطعاً شکیبایی می‌کنیم؛ و توكل‌کنندگان باید فقط بر الله توكل كنند».');
INSERT INTO "islamhouse" VALUES(14,13,'14:13','و کسانی ‌که کفر ورزیدند به پیامبرانشان گفتند: «یا شما را از سرزمینمان بیرون می‌کنیم یا به آیین ما بازگردید». پروردگارشان به آنان وحی کرد که: «یقیناً ستمکاران را نابود می‌کنیم.');
INSERT INTO "islamhouse" VALUES(14,14,'14:14','و قطعاً شما را پس از آنان در زمین جای خواهیم داد. این [عذاب و پاداش] برای کسی است که از ایستادن [به هنگام حسابرسی‌] در پیشگاهِ من بترسد و از تهدیدم بهراسد».');
INSERT INTO "islamhouse" VALUES(14,15,'14:15','و [پیامبران از الله] درخواست پیروزی [بر کافران را] کردند و [سرانجام] هر گردنکشِ ستیزه‌جویی، ناکام [و نابود] شد.');
INSERT INTO "islamhouse" VALUES(14,16,'14:16','[همان کسی كه‌] دوزخ پیش روى اوست و آبى از چرک خون‌آلود [دوزخیان] به وی می‌نوشانند.');
INSERT INTO "islamhouse" VALUES(14,17,'14:17','[آبی] که آن را جرعه‌جرعه می‌آشامد و [از فرطِ تعفن و تلخی،] فروبردنش آسان و گوارا نیست و مرگ از هر طرف به سویش رو می‌آورَد ولی نمی‌میرد و عذابی سخت [و سهمگین] پیش رویِ اوست.');
INSERT INTO "islamhouse" VALUES(14,18,'14:18','مَثل کسانی که به پروردگارشان کفر ورزیده‌اند، [این است که] اعمالشان همچون خاکستری است که در روز طوفانی باد شدیدی بر آن می‌وزد [و] از آنچه کرده‌اند، چیزی در دست ندارند. این، همان گمراهیِ دور و دراز است‌.');
INSERT INTO "islamhouse" VALUES(14,19,'14:19','[ای انسان،] آیا ندیدی که الله آسمان‌ها و زمین را به‌حق آفریده است؟ اگر بخواهد، شما را [از میان] می‌بَرد و خلق جدیدی [به جای شما پدید] می‌آورد.');
INSERT INTO "islamhouse" VALUES(14,20,'14:20','و این [کار] بر الله دشوار نیست.');
INSERT INTO "islamhouse" VALUES(14,21,'14:21','و [روز قیامت] همگی در پیشگاه الله حاضر [و آشکار] می‌شوند؛ [در این هنگام] ستمدیدگان به کسانی که گردنکشی می‌کردند می‌گویند: «ما [در دنیا] پیرو شما بودیم؛ آیا [امروز] مى‌توانید چیزى از عذاب الله را از ما بازدارید؟» آنان می‌گویند: «اگر الله ما را [در دنیا به سوی رستگاری] راهنمایی کرده بود، ما نیز راه را به شما نشان می‌دادیم. چه بی‌تابی کنیم چه شکیبایی نماییم، برایمان یکسان است؛ [هیچ] گریزگاهی نداریم».');
INSERT INTO "islamhouse" VALUES(14,22,'14:22','و چون كار [داورى] انجام شد، شیطان مى‌گوید: «بی‌تردید، الله به شما وعده داد؛ وعده‌ای راست و درست؛ و من به شما وعده [دروغین] دادم و با شما خُلفِ وعده کردم؛ و من هیچ تسلطى بر شما نداشتم جز اینكه شما را دعوت دادم و شما [نیز دعوت] مرا اجابت کردید؛ پس مرا سرزنش نكنید و خودتان را سرزنش كنید. [امروز] نه من فریادرسِ شما هستم و نه شما فریادرسِ من هستید. من بیزارم از اینكه پیش از این مرا [با الله] شریک می‌گرفتید. ستمكاران [مشرک] عذابى پردرد خواهند داشت.');
INSERT INTO "islamhouse" VALUES(14,23,'14:23','و کسانی ‌که ایمان آوردند و کارهای شایسته انجام دادند، به باغ‌های [بهشتی] درآورده می‌شوند که جویبارها از زیر [درختان] آن جاری است؛ به فرمان پروردگارشان در آن جاودانند و درودشان در آنجا «سلام» است‌.');
INSERT INTO "islamhouse" VALUES(14,24,'14:24','[ای پیامبر،] آیا ندیدی که الله چگونه مَثل زده است؟ کلمۀ پاک [لا اله الا الله] همچون درخت [نخل تنومند و] پاکی است که ریشه‌اش [در اعماق زمین‌] استوار است و شاخه‌اش بر آسمان [سر برآورده] است.');
INSERT INTO "islamhouse" VALUES(14,25,'14:25','میوه‌اش را هر لحظه به فرمان پروردگارش مى‌دهد؛ و الله برای [هدایت مردم] مَثل‌ها مى‌زند؛ باشد که پند گیرند.');
INSERT INTO "islamhouse" VALUES(14,26,'14:26','و مَثل کلمۀ پلید [شرک]، همچون درخت پلیدی است که از زمین برکنده شده [و هیچ] قرار و ثباتی ندارد.');
INSERT INTO "islamhouse" VALUES(14,27,'14:27','الله كسانى را كه ایمان آورده‌اند در زندگى دنیا و در آخرت، با سخن استوار [توحید] ثابت مى‌گردانَد و ستمگران [مشرک] را گمراه مى‌كند؛ و الله هر کاری بخواهد انجام می‌دهد.');
INSERT INTO "islamhouse" VALUES(14,28,'14:28','آیا ندیدی آن کسانی [از قریش] را که [سپاس‌] نعمتِ الله [= حرم امن و وجود پیامبر] را به [ناسپاسی و] کفر تبدیل کردند و قومشان را به سرای هلاکت درآوردند؟');
INSERT INTO "islamhouse" VALUES(14,29,'14:29','[آن سراى هلاکت،‌] دوزخ است [که] در آن وارد مى‌شوند و چه بد جایگاهی است!');
INSERT INTO "islamhouse" VALUES(14,30,'14:30','[مشرکان] همتایانی برای الله قرار دادند تا [مردم را] از راه او گمراه کنند. بگو: «[چند روزی از نعمت‌های دنیا] بهره گیرید؛ که یقیناً بازگشتتان به سوی آتش [دوزخ] است».');
INSERT INTO "islamhouse" VALUES(14,31,'14:31','[ای پیامبر،] به بندگان من که ایمان آورده‌اند بگو نماز را برپا دارند و از آنچه روزی‌شان کرده‌ایم پنهانی و آشکارا انفاق کنند؛ پیش از آنکه روزی فرارسد که در آن نه خرید و فروشی هست و نه دوستی‌ای.');
INSERT INTO "islamhouse" VALUES(14,32,'14:32','الله است که آسمان‌ها و زمین را آفرید و آبی از آسمان فروفرستاد و با آن، [انواع] میوه‌ها را برای روزیِ شما پدید آورد؛ و کشتی‌ها را به خدمتتان گماشت تا به فرمانش در دریا در حرکت باشند و جویبارها را [نیز] در خدمت شما نهاد؛');
INSERT INTO "islamhouse" VALUES(14,33,'14:33','و خورشید و ماه را که پیوسته [در مدارِ خود] در حرکتند و [همچنین] شب و روز را به خدمتتان گماشت.');
INSERT INTO "islamhouse" VALUES(14,34,'14:34','و از هر آنچه از او خواستید به شما بخشید؛ و اگر نعمت الله را بشمارید، [چنان فراوان است که] نمی‌توانید آن را شمارش کنید. به راستی که انسان، [در حق خویش،] ستمکار [و نسبت به الله،] ناسپاس است‌.');
INSERT INTO "islamhouse" VALUES(14,35,'14:35','و [یاد کن از] ابراهیم هنگامی که گفت: «پروردگارا، این شهر [= مکه] را امن بگردان و من و فرزندانم را از عبادت بُت‌ها دور بدار.');
INSERT INTO "islamhouse" VALUES(14,36,'14:36','پروردگارا، به راستی که آنها [=معبود‌ها] بسیاری از مردم را [با تصور شفاعت] گمراه ساخته‌اند؛ پس هر کس [در عبادت الله به یگانگی] از من پیروی کند، یقیناً از من است و هر‌ کس از من نافرمانی کند، بی‌تردید، تو آمرزندۀ مهربانی.');
INSERT INTO "islamhouse" VALUES(14,37,'14:37','پروردگارا، من [یکی] از فرزندانم را در تنگه‌ای بى‌آب و ‌كِشت، نزد خانۀ محترم [و شکوهمندِ] تو جای دادم. پروردگارا، [چنین کردم] تا نماز را برپا دارند؛ پس دل‌های برخى از مردم را چنان كن که به سوی آنان [و این سرزمین] متمایل گردد و از محصولات [مورد نیازشان‌] به آنان روزى‌ ببخش؛ باشد كه سپاس گزارند.');
INSERT INTO "islamhouse" VALUES(14,38,'14:38','پروردگارا، تو آنچه را که پنهان می‌کنیم و آشکار می‌سازیم، [همه را] می‌دانی؛ و چیزی در زمین و آسمان از الله پوشیده نیست.');
INSERT INTO "islamhouse" VALUES(14,39,'14:39','ستایش از آن الله است که در کهنسالی، اسماعیل و اسحاق را به من عطا فرمود. بی‌تردید، پروردگارم شنوندۀ دعاست.');
INSERT INTO "islamhouse" VALUES(14,40,'14:40','پروردگارا، مرا برپادارندۀ نماز قرار بده و از فرزندانم [نیز چنین کن]. پروردگارا، دعایم را بپذیر.');
INSERT INTO "islamhouse" VALUES(14,41,'14:41','پروردگارا، من و پدر و مادرم و [همۀ] مؤمنان را در روزی‌ که حساب برپا می‌شود بیامرز».');
INSERT INTO "islamhouse" VALUES(14,42,'14:42','و [ای پیامبر، هرگز] مپندار که الله از آنچه ستمکاران می‌کنند غافل است؛ [نه، بلکه کیفر] آنان را برای روزی‌ به تأخیر می‌افکنَد که چشم‌ها در آن [از وحشت] خیره می‌شوند،.');
INSERT INTO "islamhouse" VALUES(14,43,'14:43','در حالى كه [به سوى منادى محشر] شتابانند و سرها را بالا گرفته‌اند [و با بی‌قراری به آسمان می‌نگرند] و چشم بر هم نمى‌زنند و دل‌هایشان [از وحشت‌] تهى گشته است.');
INSERT INTO "islamhouse" VALUES(14,44,'14:44','و [ای پیامبر،] مردم را از روزى بترسان كه عذاب الهى به سراغشان مى‌آید؛ و کسانی كه [با کفر و شرک به خویش] ستم كرده‌اند مى‌گویند: «پروردگارا، اندکی به ما مهلت بده تا [به دنیا بازگردیم و] دعوتت را پاسخ گوییم و از پیامبران پیروى كنیم. [به آنان گفته می‌شود: «مگر شما نبودید كه پیشتر سوگند مى‌خوردید شما را فنایى نیست؟»');
INSERT INTO "islamhouse" VALUES(14,45,'14:45','و [مگر نه این است كه] در خانه‌های کسانی ‌که [با شرک و ناسپاسی] به خویشتن ستم کرده بودند [= قوم هود و صالح] ساکن شدید و برایتان روشن شد که [چون کفر ورزیدند] با آنان چگونه رفتار کردیم و [از سرگذشت پشینیان] برایتان مَثَل‌ها زدیم [ولی باز هم پند نگرفتید].');
INSERT INTO "islamhouse" VALUES(14,46,'14:46','و آنان نهایت نیرنگ خود را [برای مخالفت با پیامبر و کشتنِ او] به كار بردند، و مكرشان نزد الله [آشکار‌] است؛ هر چند نیرنگشان [در حدی نیست که] به سبب آن کوه‌ها از جایش کنده شود.');
INSERT INTO "islamhouse" VALUES(14,47,'14:47','پس هرگز مپندار كه الله وعدۀ خویش را [دربارۀ پیروزی حق] با پیامبرانش خلاف مى‌كند. بی‌تردید، الله شكست‌ناپذیرِ انتقام‌گیرنده است.');
INSERT INTO "islamhouse" VALUES(14,48,'14:48','در آن روز، [این] زمین، به زمینی دیگر و آسمان‌ها [به آسمان‌های دیگر] تبدیل می‌شوند و [مردم‌] در مقابل الله یگانۀ پیروزمند ظاهر می‌گردند.');
INSERT INTO "islamhouse" VALUES(14,49,'14:49','و در آن روز گناهكاران را مى‌بینى كه در زنجیرها بسته شده‌اند.');
INSERT INTO "islamhouse" VALUES(14,50,'14:50','لباس‌هایشان از قیرِ گداخته است و آتش صورت‌هایشان را مى‌پوشانَد.');
INSERT INTO "islamhouse" VALUES(14,51,'14:51','تا الله هر‌ کس را به [سزای] آنچه کرده است کیفر دهد. به راستی که الله در حسابرسی سریع است.');
INSERT INTO "islamhouse" VALUES(14,52,'14:52','این [قرآن‌] پیامی رسا براى مردم است [تا به وسیلۀ آن هدایت شوند] و با آن هشدار یابند و بدانند كه او تعالی معبودى یکتاست؛ و تا خردمندان پند گیرند.');
INSERT INTO "islamhouse" VALUES(15,1,'15:1','الر [= الف. لام. را]. این آیات كتاب [الهى] و قرآنِ روشنگر است.');
INSERT INTO "islamhouse" VALUES(15,2,'15:2','چه بسا کافران [هنگام مواجهه با عذاب قیامت] آرزو ‌کنند که کاش مسلمان بودند.');
INSERT INTO "islamhouse" VALUES(15,3,'15:3','بگذار بخورند و [از لذت‌های دنیا] بهره‌مند شوند و آرزوها[ی مادّی،] سرگرمشان سازد؛ پس به زودی خواهند دانست.');
INSERT INTO "islamhouse" VALUES(15,4,'15:4','ما [ساکنان] هیچ [شهر و] دیاری را هلاک نکردیم؛ مگر اینکه [مهلت و] اَجلی معیّن داشتند.');
INSERT INTO "islamhouse" VALUES(15,5,'15:5','هیچ امتى از اَجلِ [مقررِ] خود نه پیشى می‌گیرد و نه [از آن] عقب می‌مانَد.');
INSERT INTO "islamhouse" VALUES(15,6,'15:6','[کافران] گفتند: «ای کسی‌ که [ادعا می‌کنی] قرآن بر تو نازل شده است، یقیناً تو دیوانه‌ای.');
INSERT INTO "islamhouse" VALUES(15,7,'15:7','اگر راست می‌گویی، چرا فرشتگان را نزدمان نمی‌آوری [تا دربارۀ پیامبری‌ات گواهی دهند]؟»');
INSERT INTO "islamhouse" VALUES(15,8,'15:8','[باید بدانند که‌] ما فرشتگان را به‌حق [و فقط در هنگام عذاب کافران] نازل می‌کنیم؛ و [اگر برای عذابشان نازل شوند] دیگر مهلتی نمی‌یابند.');
INSERT INTO "islamhouse" VALUES(15,9,'15:9','به راستی که ما خود، قرآن را نازل کردیم و قطعاً خودمان نیز نگهبان آن [از هر گونه تبدیل و تحریفی] هستیم.');
INSERT INTO "islamhouse" VALUES(15,10,'15:10','[ای پیامبر،] پیش از تو نیز [رسولان خود را] میان گروه‌های پیشین فرستادیم.');
INSERT INTO "islamhouse" VALUES(15,11,'15:11','هیچ پیامبری نزدشان نمی‌آمد، مگر آنکه مسخره‌اش می‌کردند.');
INSERT INTO "islamhouse" VALUES(15,12,'15:12','اینچنین آن [تکذیب و تمسخر] را در دل‌های گناهکاران [= کافران] راه می‌دهیم.');
INSERT INTO "islamhouse" VALUES(15,13,'15:13','آنان به آن [قرآن] ایمان نمی‌آورند؛ و شیوۀ [الله در مجازات] پیشینیان [نیز چنین] رفته است [كه آنان را با عذابی دردناک هلاک نماید].');
INSERT INTO "islamhouse" VALUES(15,14,'15:14','و [حتی] اگر دری از آسمان به روی آنان می‌گشودیم تا پیوسته در آن بالا روند [و شگفتی‌های ملکوت را ببینند]،');
INSERT INTO "islamhouse" VALUES(15,15,'15:15','یقیناً باز هم می‌گفتند: «بی‌تردید، ما را چشم‌بندی کرده‌اند؛ بلکه ما گروهی جادو‌شده‌ایم».');
INSERT INTO "islamhouse" VALUES(15,16,'15:16','و به راستی، ما برجهایی در آسمان قرار داده‌ایم و آن را برای تماشاگران آراسته‌ایم.');
INSERT INTO "islamhouse" VALUES(15,17,'15:17','و آن [= آسمان] را از [ورود و دستبردِ] هر شیطانِ رانده‌شده‌ای حفظ کردیم.');
INSERT INTO "islamhouse" VALUES(15,18,'15:18','مگر آن کس که دزدانه سخنی [از ملکوتیان] بشنود که [در آن صورت،] شهابی روشن او را دنبال می‌کند [و می‌سوزاند].');
INSERT INTO "islamhouse" VALUES(15,19,'15:19','و زمین را گستردیم و کوه‌های استواری در آن افکندیم و از هر گیاه سنجیده [و مناسبی] در آن رویاندیم.');
INSERT INTO "islamhouse" VALUES(15,20,'15:20','و براى شما و کسانی كه روزى‌دهنده آنان نیستید اسباب زندگی در آن قرار دادیم.');
INSERT INTO "islamhouse" VALUES(15,21,'15:21','و هیچ چیز نیست، مگر آنکه گنجینه‌هایش نزد ماست و ما آن را جز به ‌اندازه‌ای معیّن فرو نمی‌فرستیم.');
INSERT INTO "islamhouse" VALUES(15,22,'15:22','و بادها را بارورکننده[ی ابرها] فرستادیم، سپس آبی از آسمان‌ها نازل کردیم و شما را با آن سیراب ساختیم؛ در حالى كه شما ذخیره‌كننده‌اش نیستید.');
INSERT INTO "islamhouse" VALUES(15,23,'15:23','و یقیناً ماییم که زنده می‌کنیم و می‌میرانیم، و ما [بازماندۀ جاوید و] وارث [همۀ جهانیان] هستیم.');
INSERT INTO "islamhouse" VALUES(15,24,'15:24','و بی‌تردید، [تولد و مرگ] گذشتگان شما را دانسته‌ایم و یقیناً [تولد و مرگ] آیندگان را [نیز به خوبی] می‌دانیم.');
INSERT INTO "islamhouse" VALUES(15,25,'15:25','و [ای پیامبر،] بی‌گمان، پروردگارت [همۀ] آنان را [در قیامت] محشور می‌کند. به راستی که او حکیم داناست.');
INSERT INTO "islamhouse" VALUES(15,26,'15:26','و در حقیقت، ما [نخستین] انسان [= آدم] را از گِلِ خشک [بازمانده] از لجنی بدبو آفریدیم‌.');
INSERT INTO "islamhouse" VALUES(15,27,'15:27','و پیش از آن، جن را از آتشی سوزان و بى‌دود آفریدیم.');
INSERT INTO "islamhouse" VALUES(15,28,'15:28','و [یاد کن] از هنگامی که پروردگارت به فرشتگان فرمود: «همانا من بشری را از گِلِ خشک [بازمانده] از لجنی بدبو خواهم آفرید.');
INSERT INTO "islamhouse" VALUES(15,29,'15:29','پس چون [او را آفریدم و] به او سامان دادم و از روح خویش در وی دمیدم، [به نشانۀ اطاعت از فرمان من، همگی] بر او سجده کنید».');
INSERT INTO "islamhouse" VALUES(15,30,'15:30','فرشتگان همگی سجده کردند،');
INSERT INTO "islamhouse" VALUES(15,31,'15:31','مگر ابلیس که سرپیچی کرد از اینکه با سجده‌کنندگان باشد.');
INSERT INTO "islamhouse" VALUES(15,32,'15:32','[الله] فرمود: «ای ابلیس، چرا تو با سجده‌کنندگان [همراه] نیستی؟»');
INSERT INTO "islamhouse" VALUES(15,33,'15:33','[ابلیس] گفت: «من آن نیستم که برای بشری که او را از گِلِ خشک [بازمانده] از لجنی بدبو آفریده‌ای سجده ‌کنم».');
INSERT INTO "islamhouse" VALUES(15,34,'15:34','[الله] فرمود: «از آنجا [= بهشت] بیرون شو [که] بی‌تردید، تو [از بارگاهِ ما] رانده‌شده‌ای.');
INSERT INTO "islamhouse" VALUES(15,35,'15:35','و یقیناً تا روز قیامت بر تو لعنت خواهد بود».');
INSERT INTO "islamhouse" VALUES(15,36,'15:36','[ابلیس] گفت: «پروردگارا، تا روزی ‌که [مردم] برانگیخته می‌شوند به من مهلت بده [و مرا نمیران]».');
INSERT INTO "islamhouse" VALUES(15,37,'15:37','[الله] فرمود: «[برو که] یقیناً تو از مهلت‌یافتگانی،');
INSERT INTO "islamhouse" VALUES(15,38,'15:38','تا روزِ آن وقتِ معلوم [که تمام مخلوقات با نفخۀ نخست می‌میرند]».');
INSERT INTO "islamhouse" VALUES(15,39,'15:39','[او] گفت: «پروردگارا، به سببِ آنکه مرا گمراه ساختی، [من نیز] در زمین [بدی‌ها را] در نظرشان نیک جلوه می‌دهم و همۀ آنان را به گمراهی می‌کشانم؛');
INSERT INTO "islamhouse" VALUES(15,40,'15:40','مگر بندگان خالصِ تو از [میان] آنان، [که هدایتشان کرده‌ای]».');
INSERT INTO "islamhouse" VALUES(15,41,'15:41','[الله] فرمود: «این [اخلاص،] راه مستقیمی است [که به من می‌رسد].');
INSERT INTO "islamhouse" VALUES(15,42,'15:42','یقیناً تو بر بندگانم تسلطی نداری، مگر [بر آن مشرکان و] گمراهانی که از تو پیروی می‌کنند.');
INSERT INTO "islamhouse" VALUES(15,43,'15:43','و بی‌تردید، دوزخ وعده‌گاه همۀ آنان است.');
INSERT INTO "islamhouse" VALUES(15,44,'15:44','[دوزخ] هفت در دارد [و] برای هر دری، گروهی از آنان تقسیم [و مشخص] شده‌اند».');
INSERT INTO "islamhouse" VALUES(15,45,'15:45','یقیناً پرهیزگاران در باغ‌ها[ی بهشت] و در [کنار] چشمه‌ها هستند.');
INSERT INTO "islamhouse" VALUES(15,46,'15:46','[هنگام ورود به آنان گفته می‌شود:] «به سلامت و ایمنی به آن [باغ‌ها] وارد شوید».');
INSERT INTO "islamhouse" VALUES(15,47,'15:47','آنچه از کینه [و حسد که] در دل‌هایشان است برمی‌کنیم؛ برادرانه بر تخت‌ها مقابل یکدیگرند.');
INSERT INTO "islamhouse" VALUES(15,48,'15:48','در آنجا نه رنجى به آنان مى‌رسد و نه از آنجا بیرون رانده مى‌شوند.');
INSERT INTO "islamhouse" VALUES(15,49,'15:49','[ای پیامبر،] به بندگانم خبر بده که من آمرزندۀ مهربانم.');
INSERT INTO "islamhouse" VALUES(15,50,'15:50','و اینکه عذاب من، عذابی دردناک است.');
INSERT INTO "islamhouse" VALUES(15,51,'15:51','و از مهمانان ابراهیم [نیز] به آنان خبر بده.');
INSERT INTO "islamhouse" VALUES(15,52,'15:52','هنگامی که بر او وارد شدند، سلام گفتند؛ [اما چون دست به غذا نبردند، ابراهیم‌] گفت: «ما از شما هراسانیم».');
INSERT INTO "islamhouse" VALUES(15,53,'15:53','آنان گفتند: «نترس؛ ما تو را به پسری دانا مژده می‌دهیم».');
INSERT INTO "islamhouse" VALUES(15,54,'15:54','[ابراهیم] گفت: «آیا با اینكه سالخورده شده‌ام مژده[ی تولد فرزند] به من مى‌دهید؟ به چه [دلیل اینچنین] بشارت مى‌دهید؟»');
INSERT INTO "islamhouse" VALUES(15,55,'15:55','آنان گفتند: «ما تو را به درستی بشارت دادیم؛ پس ناامید نباش».');
INSERT INTO "islamhouse" VALUES(15,56,'15:56','[ابراهیم] گفت: «و جز گمراهان، چه کسی از رحمتِ پروردگارش ناامید می‌گردد؟»');
INSERT INTO "islamhouse" VALUES(15,57,'15:57','[آنگاه به فرشتگان] گفت: «اى فرستادگان، كار [و مأموریت دیگر] شما چیست؟»');
INSERT INTO "islamhouse" VALUES(15,58,'15:58','آنان گفتند: «در حقیقت، ما به سوی قوم گنهکار [لوط] فرستاده شده‌ایم [تا هلاکشان کنیم].');
INSERT INTO "islamhouse" VALUES(15,59,'15:59','مگر خانوادۀ لوط که ما یقیناً همۀ آنان را نجات خواهیم داد.');
INSERT INTO "islamhouse" VALUES(15,60,'15:60','جز همسرش که مقدر کرده‌ایم از بازماندگان [در عذاب] باشد».');
INSERT INTO "islamhouse" VALUES(15,61,'15:61','هنگامی‌ که فرستادگان [الله به شکل انسان] نزد خاندان لوط آمدند،');
INSERT INTO "islamhouse" VALUES(15,62,'15:62','[لوط] گفت: «شما گروهی ناشناس هستید».');
INSERT INTO "islamhouse" VALUES(15,63,'15:63','[فرشتگان] گفتند: «بلکه ما همان چیزی را برایت آورده‌ایم كه [این مردم گناهکار] درباره‌اش تردید مى‌كردند [= نزول عذاب].');
INSERT INTO "islamhouse" VALUES(15,64,'15:64','و ما برایت [خبرِ حقیقی و] راست آورده‌ایم و قطعاً ما راستگوییم.');
INSERT INTO "islamhouse" VALUES(15,65,'15:65','پس پاسی از شب [که گذشت] خانواده‌ات را [از این جا] ببر و [خودت] به دنبالشان برو؛ و هیچ‌یک از شما [نباید] پشت سرش را نگاه نکند و به جایی که به شما فرمان می‌دهند بروید».');
INSERT INTO "islamhouse" VALUES(15,66,'15:66','و ما او [= لوط] را از این امر آگاه كردیم که صبحگاهان ریشۀ آنان برکنده خواهد شد.');
INSERT INTO "islamhouse" VALUES(15,67,'15:67','و اهالی شهر، در حالی که [دربارۀ آمدن افراد جدید] با شادی‌ [به یکدیگر] خبر می‌دادند [به سوی خانۀ لوط] آمدند.');
INSERT INTO "islamhouse" VALUES(15,68,'15:68','[لوط] گفت: «اینها مهمانان من هستند؛ پس مرا رسوا [و شرمندۀ مهمانانم] نکنید،');
INSERT INTO "islamhouse" VALUES(15,69,'15:69','و از الله بترسید و سرشکسته‌ام نسازید».');
INSERT INTO "islamhouse" VALUES(15,70,'15:70','[آنان] گفتند: «مگر ما تو را از [میزبانیِ] افراد [بیگانه] نهی نکردیم؟»');
INSERT INTO "islamhouse" VALUES(15,71,'15:71','[لوط] گفت: «اگر مى‌خواهید [كارى حلال‌] انجام دهید، اینها دختران [قوم] من هستند؛ [با آنها ازدواج كنید]».');
INSERT INTO "islamhouse" VALUES(15,72,'15:72','سوگند به جان تو [ای پیامبر] که آنان در مستیِ خویش سرگشته بودند.');
INSERT INTO "islamhouse" VALUES(15,73,'15:73','پس هنگام برآمدنِ آفتاب، بانگ [مرگبار] آنان را فراگرفت.');
INSERT INTO "islamhouse" VALUES(15,74,'15:74','آنگاه آن [سرزمین] را زیر و رو کردیم و سنگ‌گل‌هایی پیاپی بر آنان باریدیم.');
INSERT INTO "islamhouse" VALUES(15,75,'15:75','به یقین، در این [كیفر،] براى هوشیاران عبرت‌هاست.');
INSERT INTO "islamhouse" VALUES(15,76,'15:76','و [آثار] آن [شهرهای ویرانه، هنوز] بر سر راه [مسافران] پابرجاست.');
INSERT INTO "islamhouse" VALUES(15,77,'15:77','یقیناً در این [عذاب،] نشانه‌ای برای [عبرت گرفتنِ] مؤمنان است.');
INSERT INTO "islamhouse" VALUES(15,78,'15:78','و اصحاب اَیکه [= قوم شعیب] قطعاً ستمکار بودند.');
INSERT INTO "islamhouse" VALUES(15,79,'15:79','[ما] از آنان انتقام گرفتیم و [شهرهای ویران‌شدۀ] این دو [قوم،] بر سر راه آشکار است.');
INSERT INTO "islamhouse" VALUES(15,80,'15:80','و بی‌تردید، اهل حِجر [= قوم ثمود] پیامبران را دروغگو انگاشتند.');
INSERT INTO "islamhouse" VALUES(15,81,'15:81','و [ما معجزات و] نشانه‌های خود را به آنان دادیم، و[لی] از آنها رویگردان شدند.');
INSERT INTO "islamhouse" VALUES(15,82,'15:82','و از کوه‌ها خانه‌هایی [برای خود] می‌تراشیدند که در امان بمانند.');
INSERT INTO "islamhouse" VALUES(15,83,'15:83','پس صبحگاهان بانگ [مرگبار] آنان را فراگرفت.');
INSERT INTO "islamhouse" VALUES(15,84,'15:84','و آنچه [از اموال و خانه‌های محکم] به دست آورده بودند، سودی به حالشان نداشت [و عذاب الهی را دفع نکرد].');
INSERT INTO "islamhouse" VALUES(15,85,'15:85','و ما آسمان‌ها و زمین و آنچه را که میان آنهاست جز به‌حق نیافریده‌ایم؛ و قیامت قطعاً آمدنی است؛ پس [ای پیامبر، از کافران‌] به نیکویی گذشت کن.‌');
INSERT INTO "islamhouse" VALUES(15,86,'15:86','بی‌تردید، پروردگارت همان آفریدگارِ داناست. ‌');
INSERT INTO "islamhouse" VALUES(15,87,'15:87','و به راستی، ما به تو «سبعُ المثانی» [= سورۀ فاتحه] و قرآن بزرگ را دادیم.');
INSERT INTO "islamhouse" VALUES(15,88,'15:88','[بنابراین،] هرگز به آنچه [از نعمت‌های دنیوی] که گروه‌هایی از آنان [= کافران] را بهره‌مند کرده‌ایم چشم ندوز و به خاطر [ناسپاسی و شرکِ] آنان اندوهگین نباش و با مؤمنان فروتنی کن.');
INSERT INTO "islamhouse" VALUES(15,89,'15:89','و بگو: «به راستی که من همان بیم‌دهندۀ آشکارم».');
INSERT INTO "islamhouse" VALUES(15,90,'15:90','[و آنان را از عذاب بترسان] همان گونه که [آن را] بر تقسیم‌کنندگان [آیات الهی] فرستادیم؛');
INSERT INTO "islamhouse" VALUES(15,91,'15:91','همان کسانی كه قرآن را بخش بخش کردند [و آن را شعر، سحر، کذب، و اساطیر ‌نامیدند].');
INSERT INTO "islamhouse" VALUES(15,92,'15:92','سوگند به پروردگارت، قطعاً [در قیامت] از همۀ آنان سؤال خواهیم کرد،');
INSERT INTO "islamhouse" VALUES(15,93,'15:93','از آنچه می‌کردند.');
INSERT INTO "islamhouse" VALUES(15,94,'15:94','پس [ای پیامبر،] آنچه [را که از سوی الله] دستور یافته‌ای، آشکار کن و از مشرکان روی بگردان‌.');
INSERT INTO "islamhouse" VALUES(15,95,'15:95','بی‌تردید، ما [شرّ] مسخره‌کنندگان را از تو دور می‌کنیم.');
INSERT INTO "islamhouse" VALUES(15,96,'15:96','همان کسانی که [برای خود،] با الله، معبود دیگری قرار می‌دهند؛ پس به زودی [نتیجۀ شرکِ خویش را] خواهند دانست.');
INSERT INTO "islamhouse" VALUES(15,97,'15:97','و [ای پیامبر،] یقیناً ما می‌دانیم که تو از آنچه آنان [= کافران] می‌گویند آزرده‌خاطر می‌گردی.');
INSERT INTO "islamhouse" VALUES(15,98,'15:98','پس به ستایش پروردگارت تسبیح بگو و از سجده‌کنندگان باش.');
INSERT INTO "islamhouse" VALUES(15,99,'15:99','و تا هنگامی‌ که مرگت فرامی‌رسد پروردگارت را عبادت کن.');
INSERT INTO "islamhouse" VALUES(16,1,'16:1','[ای کافران، لحظۀ وقوع قیامت و] فرمانِ الله [برای عذابتان] نزدیک شده است؛ پس برای [رسیدنِ] آن شتاب نکنید. او منزّه است و از چیزهایی که با وی شریک می‌سازند برتر است.');
INSERT INTO "islamhouse" VALUES(16,2,'16:2','[الله] به فرمان خویش فرشتگان را با وحی بر هر ‌یک از بندگانش که بخواهد نازل می‌کند [با این پیام] که: «[ای پیامبران، مردم را از شرک به الله] بیم دهید [و به آنان بگویید که] معبودی [به‌حق] جز من نیست؛ پس [ای مردم،] از من پروا کنید».');
INSERT INTO "islamhouse" VALUES(16,3,'16:3','او تعالی آسمان‌ها و زمین را به‌حق [و هدفمند] آفرید [و] از چیزهایی که [با وى‌] شریک مى‌سازند برتر است.');
INSERT INTO "islamhouse" VALUES(16,4,'16:4','[الله] انسان را از نطفه‌ای آفرید و ناگهان او ستیزه‌گری سرسخت و آشکار شد.');
INSERT INTO "islamhouse" VALUES(16,5,'16:5','و چهارپایان را برایتان آفرید که در آنها پوشش [گرم] و منافع [دیگری] است و از [گوشت برخی از] آنها می‌خورید.');
INSERT INTO "islamhouse" VALUES(16,6,'16:6','و در آنها زیبایى [و شكوه‌] برایتان است، آنگاه كه [آنها را شامگاهان] از چَرا بازمى‌آورید و [بامدادان] برای چَرا [به صحرا] مى‌فرستید.');
INSERT INTO "islamhouse" VALUES(16,7,'16:7','[آنها] بارهاى سنگین شما را [از شهری] به شهرى [دیگر] مى‌بَرند كه [اگر چنین نمی‌کردند،] جز با زحمتی جانکاه نمى‌توانستید به آنجا برسید. به راستی که پروردگارتان دلسوز و مهربان است.');
INSERT INTO "islamhouse" VALUES(16,8,'16:8','و اسبان و قاطِران و خران را [نیز آفرید] تا بر آنها سوار شوید و [نیز برایتان مایۀ] تجمل [است] و چیزهایی می‌آفریند که نمی‌دانید.');
INSERT INTO "islamhouse" VALUES(16,9,'16:9','بر الله است که راهِ راست [= اسلام] را [به بند‌گانش بنمایاند] و برخی از آن [راه‌ها] بیراهه است؛ و اگر [الله] می‌خواست، همۀ شما را هدایت می‌کرد.');
INSERT INTO "islamhouse" VALUES(16,10,'16:10','او همان ذاتی است که آبی از آسمان فروفرستاد که آشامیدنیِ شما از آن است و [گیاه و] درختی كه [چهارپایان خود را] در آن مى‌چرانید [نیز] از آن [آب] است.');
INSERT INTO "islamhouse" VALUES(16,11,'16:11','[الله] به وسیله آن، كِشت و زیتون و درختان خرما و انگور و همه نوع محصولات [و میوه‌های دیگر] برایتان مى‌رویانَد. به راستی که در اینها براى مردمی که می‌اندیشند نشانه‌اى [از قدرت پروردگار] است.');
INSERT INTO "islamhouse" VALUES(16,12,'16:12','و شب و روز و خورشید و ماه را به خدمتتان گماشت؛ و ستارگان [نیز] به فرمانش در خدمت [انسان] هستند. بی‌تردید، در این [آفرینش،] برای مردم كه خردورزی می‌کنند نشانه‌ای [از قدرت پروردگار] است.');
INSERT INTO "islamhouse" VALUES(16,13,'16:13','و آنچه را که در زمین به رنگ‌های گوناگون برایتان آفرید [نیز به خدمت شما گماشت‌]. بى‌تردید، در این [امور،] براى مردمى كه پند مى‌گیرند، نشانه‌اى [از قدرت پروردگار] است.');
INSERT INTO "islamhouse" VALUES(16,14,'16:14','اوست كه دریا را به خدمتتان گماشت تا از [صیدِ] آن، گوشت تازه بخورید و زیورى [همچون مروارید و مرجان] از آن بیرون مى‌آورید که می‌پوشید؛ و کشتی‌ها را در آن [دریا] مى‌بینى كه [امواج را] مى‌شكافند تا شما از فضل [و بخشش] الله بهره‌مند گردید و باشد كه سپاس گزارید.');
INSERT INTO "islamhouse" VALUES(16,15,'16:15','و کوه‌های استوار [و محکمی] در زمین درافکند که مبادا شما را بلرزاند و جویبارها و راه‌ها[یی پدید آورد] تا [بدون گم‌شدن،] راه خود را بیابید.');
INSERT INTO "islamhouse" VALUES(16,16,'16:16','و نشانه‌هایى [برای یافتن مسیرها در طول روز آفرید] و توسط ستاره[ها نیز در شب] راه‌یابی مى‌كنند.');
INSERT INTO "islamhouse" VALUES(16,17,'16:17','آیا آن ذاتی که می‌آفریند همچون کسی است که [هیچ] نمی‌آفریند؟ آیا پند نمی‌گیرید؟');
INSERT INTO "islamhouse" VALUES(16,18,'16:18','اگر [بخواهید] نعمت[های] الله را بشمارید، نمی‌توانید آنها را به شمارش درآورید. بی‌تردید، الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(16,19,'16:19','و الله آنچه پنهان می‌کنید و آشکار می‌سازید، [همه را] می‌داند.');
INSERT INTO "islamhouse" VALUES(16,20,'16:20','و آنهایی را که [مشرکان] به جای الله [به یاری و دعا] می‌خوانند، چیزی را نمی‌آفرینند و خود آفریده می‌شوند.');
INSERT INTO "islamhouse" VALUES(16,21,'16:21','[این معبود‌ها در حقیقت،] مُرده‌اند نه زنده؛ و خود نمی‌دانند که چه هنگام برانگیخته می‌شوند.');
INSERT INTO "islamhouse" VALUES(16,22,'16:22','معبود شما، معبودی یگانه است؛ و کسانی‌ که به آخرت ایمان ندارند، دل‌هایشان انکارکننده[ی حق] است و خود متکبرند.');
INSERT INTO "islamhouse" VALUES(16,23,'16:23','قطعاً الله آنچه را پنهان می‌کنند و آشکار می‌سازند [همه را] می‌داند. بی‌تردید، او متکبران را دوست ندارد.');
INSERT INTO "islamhouse" VALUES(16,24,'16:24','هنگامی که به آنان گفته می‌شود: «پروردگارتان چه چیزی نازل کرده است؟» می‌گویند: «افسانه‌های [خیالی] پیشینیان».');
INSERT INTO "islamhouse" VALUES(16,25,'16:25','[همچنان در کفر می‌مانند] تا روز قیامت، [هم] بارِ گناهان خود را به تمامی بر دوش کشند و [هم بخشی] از بارِ گناهان کسانی را که از روی نادانی [و تقلید] گمراهشان کرده‌اند. هان! چه بد است آنچه بر دوش می‌کشند!');
INSERT INTO "islamhouse" VALUES(16,26,'16:26','در حقیقت، کسانی که پیش از آنان بودند [نیز] نیرنگ [و بداندیشی] کردند؛ اما فرمان الله دررسید که بنیادشان را از اساس برانداخت و سقف بر سرشان فرو ریخت و از [همان] جایی که گمان نمی‌کردند عذاب دامنگیرشان شد.');
INSERT INTO "islamhouse" VALUES(16,27,'16:27','سپس روز قیامت، [الله با عذابی دردناک،] آنان را خوار می‌سازد و می‌فرماید: «کجا هستند شریکان من که دربارۀ آنان [با پیامبران و مؤمنان] ستیز می‌کردید؟» کسانی که به آنان علم داده شده است [= علمای ربانی] می‌گویند: «امروز خواری و عذاب، نصیب کافران است».');
INSERT INTO "islamhouse" VALUES(16,28,'16:28','[همان] کسانی ‌که فرشتگان [عذاب،] در حالی‌ جانشان را می‌گیرند که [به سبب شرک در دنیا] به خود ستم کرده بودند. [آنان در هنگام مرگ] سرِ تسلیم فرود می‌آورند [و می‌گویند:] «ما هیچ [کارِ] بدی نمی‌کردیم. آری، مسلماً الله به آنچه می‌کردید داناست.');
INSERT INTO "islamhouse" VALUES(16,29,'16:29','پس، از دروازه‌های دوزخ وارد شوید [که] جاودانه در آن خواهید بود؛ و به راستی، جایگاه گردنکشان چه بد است‌!');
INSERT INTO "islamhouse" VALUES(16,30,'16:30','و به کسانی‌ که پرهیزگاری کردند گفته می‌شود: «پروردگارتان [بر پیامبر] چه چیز نازل کرده است؟» آنان می‌گویند: «خیر [و نیکوییِ بسیار]». براى كسانى كه در این دنیا نیكى كرده‌اند، [پاداش‌] نیكویى است و قطعاً سراى آخرت بهتر است؛ و به راستی، چه نیكوست سراى پرهیزگاران!');
INSERT INTO "islamhouse" VALUES(16,31,'16:31','[جایگاهشان،] باغ‌هایی [از بهشت] جاودان است که به آن وارد می‌شوند. از زیر [درختان] آن جویبارها جاری است [و] در آنجا هر چه بخواهند برایشان [فراهم] است. الله پرهیزگاران را اینچنین پاداش می‌دهد.');
INSERT INTO "islamhouse" VALUES(16,32,'16:32','[همان] كسانى كه فرشتگان در حالى جانشان را مى‌ستانند كه [از آلودگیِ کفر و گناه] پاكند [و به آنان‌] مى‌گویند: «درود بر شما. به [پاداش‌] آنچه می‌کردید [اینک] به بهشت درآیید».');
INSERT INTO "islamhouse" VALUES(16,33,'16:33','آیا [کافران] جز این انتظار دارند که فرشتگان [برای قبض روح] به سراغشان بیایند یا فرمان پروردگارت [برای عذابشان] بیاید؟ کسانی ‌که پیش از آنان بودند [نیز] چنین می‌کردند و الله به آنان ستم نکرد؛ بلکه آنان خود به خویشتن ستم می‌کردند.');
INSERT INTO "islamhouse" VALUES(16,34,'16:34','پس [كیفر] بدی‌هایی كه مرتکب شده بودند گریبانگیرشان شد و آنچه که آن را مسخره مى‌كردند آنان را فروگرفت.');
INSERT INTO "islamhouse" VALUES(16,35,'16:35','و کسانی‌ که شرک ورزیدند گفتند: «اگر الله مى‌خواست، نه ما و نه پدرانمان چیزى را غیر از او عبادت نمی‌کردیم و چیزى را جز به [خواست و فرمان] او حرام نمى‌كردیم». كسانى كه پیش از آنان بودند نیز چنین کردند؛ پس آیا پیامبران وظیفه‌ای جز رساندن آشكار [وحی] دارند؟');
INSERT INTO "islamhouse" VALUES(16,36,'16:36','یقیناً ما در هر امتی، پیامبری را [با این پیام] برانگیختیم که: «الله یکتا را عبادت کنید و از طاغوت [= معبودان غیر الله] دوری کنید». الله برخی از آنان را هدایت كرده است و برخی از آنان نیز سزاوار گمراهى شدند. پس در زمین بگردید و بنگرید که سرانجامِ تكذیب‌كنندگان [آیات الهی] چگونه بوده است.');
INSERT INTO "islamhouse" VALUES(16,37,'16:37','[ای پیامبر،] هر اندازه بر هدایت آنان مشتاق باشی [سودی ندارد؛ زیرا] الله کسی را که گمراه نماید، هدایت نخواهد کرد و آنان [هیچ] یاوری ندارند.');
INSERT INTO "islamhouse" VALUES(16,38,'16:38','[مشرکان] با سخت‌ترین سوگندهایشان به الله سوگند خوردند که: «الله کسی را که می‌میرد برنمی‌انگیزد». آری، این [برانگیختنِ مردگان]، وعده‌ای است که به‌حق بر عهدۀ اوست؛ ولی بیشتر مردم نمی‌دانند.');
INSERT INTO "islamhouse" VALUES(16,39,'16:39','[او مردگان را زنده می‌کند] تا سرانجام [حقیقتِ] آنچه را که در آن اختلاف می‌کنند برایشان روشن سازد و تا کسانی ‌که کفر ورزیدند بدانند که دروغگو بوده‌اند.');
INSERT INTO "islamhouse" VALUES(16,40,'16:40','سخن ما دربارۀ چیزی که آن را اراده کرده‌ایم فقط این است که به آن می‌گوییم: «موجود شو»، پس بی‌درنگ موجود می‌شود.');
INSERT INTO "islamhouse" VALUES(16,41,'16:41','و کسانی که در [راه] الله ـ پس از آنكه مورد ستم واقع شدندـ هجرت کردند، قطعاً در دنیا [در سرزمین‌ و] مکانی نیکو آنان را جای می‌دهیم؛ و یقیناً پاداش آخرت بزرگ‌تر است‌. اگر [کسانی که از هجرت امتناع کردند، پاداش این کار را] می‌دانستند، [هرگز از هجرت بازنمی‌ماندند].');
INSERT INTO "islamhouse" VALUES(16,42,'16:42','[مهاجران در راه الله،] کسانی‌ [هستند] که شکیبایی نمودند و بر پروردگار‌شان توکل می‌کنند.');
INSERT INTO "islamhouse" VALUES(16,43,'16:43','و [ای پیامبر،] ما پیش از تو جز مردانی که به آنان وحی می‌کردیم [کسی را به رسالت] نفرستادیم؛ پس [ای مشرکان،] اگر نمی‌دانید، از اهل ذکر [= دانشمندان اهل کتاب] بپرسید.');
INSERT INTO "islamhouse" VALUES(16,44,'16:44','[آن پیامبران را] با دلایل روشن و کتاب‌ها[ی آسمانی فرستادیم]؛ و [ما این] قرآن را بر تو نازل کردیم تا برای مردم آنچه را که برایشان نازل شده است روشن سازی؛ باشد که [در مفاهیمش] بیندیشند.');
INSERT INTO "islamhouse" VALUES(16,45,'16:45','آیا کسانی‌ که بداندیشی [و توطئه] کردند، ایمن هستند [از این] که الله [همۀ] آنان را در زمین فروبَرد یا عذاب [الهی] از جایی که نمی‌دانند [به سراغشان] بیاید؟');
INSERT INTO "islamhouse" VALUES(16,46,'16:46','یا [آنکه] به هنگام رفت و آمدشان [گریبان] آنان را بگیرد؟ پس آنان مانع [رسیدن عذاب] نیستند.');
INSERT INTO "islamhouse" VALUES(16,47,'16:47','یا [آنکه عذاب الهی] در حالی که [از آن] می‌ترسند گریبانگیرشان شود؟ پس بی‌تردید، پروردگارتان دلسوزِ مهربان است [که در مجازات شما شتاب نمی‌کند].');
INSERT INTO "islamhouse" VALUES(16,48,'16:48','آیا آنان به چیزهایی [همچون درختان و کوه‌ها] که الله آفریده است نمی‌نگرند که [چگونه با حرکت خورشید و ماه] سایه‌هایشان از راست و چپ می‌گسترد و در حالی برای الله سجده می‌کنند که [در برابر عظمتش] خاکسارند؟');
INSERT INTO "islamhouse" VALUES(16,49,'16:49','و هر چه در آسمان‌ها و زمین است ـ از جنبندگان و فرشتگان‌ـ [همگی] برای الله سجده می‌آورند و گردنکشی نمی‌کنند.');
INSERT INTO "islamhouse" VALUES(16,50,'16:50','[فرشتگان] از پروردگار خویش كه [به ذات و غلبه و قدرتش] بالای آنان است مى‌ترسند و آنچه را فرمان داده مى‌شوند انجام می‌دهند.');
INSERT INTO "islamhouse" VALUES(16,51,'16:51','و الله فرمود: «دو معبود [را براى خود به عبادت] نگیرید. جز این نیست كه [معبود شما] همان الله یکتاست؛ پس تنها از من پروا کنید».');
INSERT INTO "islamhouse" VALUES(16,52,'16:52','و آنچه در آسمان‌ها و زمین است، از آنِ اوست و عبادت و فرمانبردارى [نیز] همیشه و پیوسته براى اوست. آیا از غیر الله پروا می‌کنید؟');
INSERT INTO "islamhouse" VALUES(16,53,'16:53','و [ای مردم،] هر نعمتی که دارید، از سوی الله است؛ به علاوه، وقتی گزندی به شما می‌رسد، به پیشگاه او ناله و زاری می‌کنید.');
INSERT INTO "islamhouse" VALUES(16,54,'16:54','سپس وقتی آن گزند را از شما برطرف ساخت، گروهی از شما به پروردگارشان شرک می‌ورزند.');
INSERT INTO "islamhouse" VALUES(16,55,'16:55','تا سرانجام به آنچه به آنان داده‌ایم ناسپاسی کنند. پس [چند روزی از نعمت‌های دنیا] برخوردار شوید که به زودی [نتایج کردارتان را] خواهید دانست.');
INSERT INTO "islamhouse" VALUES(16,56,'16:56','و [مشرکان‌] از آنچه روزی‌شان کرده‌ایم، برای معبودانی که [چیزی] نمی‌دانند سهمی قرار می‌دهند. به الله سوگند که دربارۀ آنچه افترا می‌بستید، قطعاً بازخواست خواهید شد.');
INSERT INTO "islamhouse" VALUES(16,57,'16:57','و [آنان فرشتگان را] دخترانی برای الله قرار می‌دهند ـ او تعالی منزّه است‌ ـ و آنچه را میل دارند [= پسران] برای خودشان [قرار می‌دهند].');
INSERT INTO "islamhouse" VALUES(16,58,'16:58','و هر گاه یکی از آنان را به [تولد] دختری بشارت دهند، در حالی ‌که خشمش را فرومی‌خورد، چهره‌اش [از اندوه] تیره می‌شود.');
INSERT INTO "islamhouse" VALUES(16,59,'16:59','از بدی [و ناراحتیِ] آنچه به او مژده داده‌اند، از قوم [خویش] پنهان می‌شود [و با خود می‌اندیشد که] آیا او را با سرافکندگی نگه دارد یا در خاک پنهانش کند. هان! چه بد داورى مى‌كنند!');
INSERT INTO "islamhouse" VALUES(16,60,'16:60','صفت زشت [جهل و کفر] براى كسانى است كه به آخرت ایمان ندارند و صفتِ برتر، از آنِ الله است و او شکست‌ناپذیرِ حكیم است.');
INSERT INTO "islamhouse" VALUES(16,61,'16:61','و اگر الله مردم را به خاطر ظلمشان بازخواست مى‌كرد، جنبنده‌اى روى زمین باقى نمى‌گذاشت؛ ولی [كیفرِ] آنان را تا زمانی معیّن به تأخیر مى‌اندازد؛ و چون اَجَلشان به سر آید، نه ساعتى [از آن‌] تأخیر می‌كنند و نه پیشى می‌گیرند.');
INSERT INTO "islamhouse" VALUES(16,62,'16:62','و [مشرکان] آنچه را نمی‌پسندند برای الله قرار می‌دهند و زبانشان [چنین] دروغ مى‌سازد كه [رستگاری و] نیكویی از آنِ آنان است. حقا که آتش نصیبشان است و آنان قطعاً در [دوزخ] رها [و فراموش] می‌شوند.');
INSERT INTO "islamhouse" VALUES(16,63,'16:63','[ای پیامبر،] سوگند به الله كه به سوى امت‌هایی که پیش از تو بودند [نیز پیامبرانی‌] فرستادیم؛ اما شیطان کارهایشان را برایشان آراست؛ از این رو، امروز [نیز‌ دوست و] کارسازشان همو[= شیطان] است و برایشان عذابى دردناک [در پیش] است.');
INSERT INTO "islamhouse" VALUES(16,64,'16:64','و [ای پیامبر،] این كتاب را بر تو نازل نكردیم مگر برای اینكه [حقیقتِ] آنچه را که [مردم] در آن اختلاف دارند برایشان بیان كنى و هدایت و رحمتى باشد براى گروهی كه ایمان مى‌آورند.');
INSERT INTO "islamhouse" VALUES(16,65,'16:65','و الله آبی از آسمان فرو فرستاد و با آن، زمین را پس از خزان و خشکی‌اش زنده كرد. قطعاً در این [امر] براى مردمى كه [سخن حق را] می‌شنوند، نشانه‌ای [از قدرت پروردگار] است.');
INSERT INTO "islamhouse" VALUES(16,66,'16:66','و [ای مردم،] قطعاً در [آفرینش] چهارپایان عبرتى برای شماست: از آنچه در شكمِ آنهاست، از میان غذاهای هضم‌شده و خون، شیرِ خالصی به شما مى‌نوشانیم كه براى نوشندگان گواراست.');
INSERT INTO "islamhouse" VALUES(16,67,'16:67','و [نیز] از میوه‌های درختان خرما و انگور، هم شراب [مستی‌آور] و هم روزیِ نیکو [و پاکیزه] به دست می‌آورید. بی‌گمان، در این [امر]، برای کسانی که خرد می‌ورزند، نشانه‌ای [از لطف و قدرتِ پروردگار] است.');
INSERT INTO "islamhouse" VALUES(16,68,'16:68','پروردگارت به زنبور عسل الهام كرد كه: «از کوه‌ها و درختان و از آنچه [مردم‌] بنا می‌کنند [برای خود] خانه‌هایی برگیر.');
INSERT INTO "islamhouse" VALUES(16,69,'16:69','آنگاه از [شیرۀ] تمام میوه‌ها [و گل‌ها] بخور و راه‌های پروردگارت را فروتنانه طی کن». از شكم آنها شهدی رنگارنگ بیرون مى‌آید كه شفاى مردم در آن است. به راستى، در این [ویژگی و کار زنبور عسل،] براى مردمى كه می‌اندیشند، نشانه‌ای [از علم و قدرتِ پروردگار] است.');
INSERT INTO "islamhouse" VALUES(16,70,'16:70','و الله شما را آفریده است سپس جانتان را می‌گیرد؛ و برخی از شما به فروترین [دوران] عمر [= کُهنسالی] می‌رسد چنان که پس از دانستن [بسیاری چیزها، دیگر] چیزی نمی‌داند [و همه را فراموش می‌کند]. بی‌تردید، الله دانای تواناست‌.');
INSERT INTO "islamhouse" VALUES(16,71,'16:71','و الله برخی از شما را بر برخی [دیگر] در [نعمت و] روزی برتری داده است؛ ولی کسانی ‌که برتری داده شده‌اند، حاضر نیستند از روزی خود به بردگانشان بدهند، تا همگی در آن [نعمت و دارایی] برابر شوند. [اگر خود از شریک بیزارند، چرا برای الله شریک قرار می‌دهند؟] پس آیا [با این شرک‌ورزی،] نعمت الله را انكار مى‌كنند؟');
INSERT INTO "islamhouse" VALUES(16,72,'16:72','و الله از [جنس] خودتان همسرانی برای شما قرار داد و از همسرانتان فرزندان و نوادگانی برای شما آفرید و از چیزهای پاكیزه به شما روزى بخشید. پس آیا [باز هم‌] به باطل ایمان می‌آورند و نعمت الله را ناسپاسی می‌کنند؟');
INSERT INTO "islamhouse" VALUES(16,73,'16:73','و [مشرکان] به جاى الله چیزهایى را عبادت می‌کنند كه مالک هیچ روزى‌ای در آسمان‌ها و زمین برایشان نیستند و توانایی [هیچ کاری] ندارند.');
INSERT INTO "islamhouse" VALUES(16,74,'16:74','پس براى الله امثال [و شریک‌ها] قائل نشوید. بی‌تردید، الله مى‌داند و شما نمى‌دانید.');
INSERT INTO "islamhouse" VALUES(16,75,'16:75','الله مثالی زده است: بردۀ مملوکی که قادر به هیچ کاری نیست و [در مقابل،] کسی که از جانب خویش به او روزیِ نیکویی بخشیده‌ایم و او از همان [نعمت‌های الهی] پنهان و آشکار انفاق می‌کند. آیا این دو برابرند؟ [پس شما مشرکان چگونه مالک مطلق را با معبودهای ناتوان یکسان می‌دانید؟] ستایش مخصوص الله است؛ اما بیشتر آنان [حقِ یگانگی‌اش را] نمی‌دانند.');
INSERT INTO "islamhouse" VALUES(16,76,'16:76','و الله مَثل [دیگری] زده است: دو مرد را [فرض کنید] که یکی از آنها گنگ است و هیچ کاری از وی برنمی‌آید و سربارِ سرور خود می‌باشد، هر کجا او را می‌فرستد، هیچ خیری به همراه نمی‌آورد [و وظایفش را به درستی انجام نمی‌دهد]؛ آیا او با کسی ‌که به عدالت فرمان می‌دهد و خود بر راهی مستقیم قرار دارد برابر است؟ [پس شما مشرکان چگونه قادر مطلق را با مردگان و بُت‌های بی‌احساسِ ناتوان یکسان می‌دانید؟]');
INSERT INTO "islamhouse" VALUES(16,77,'16:77','[علم] غیب آسمان‌ها و زمین، تنها از آنِ الله است و امرِ [برپاییِ] قیامت، جز [به سرعت‌] یک چشم بر هم زدن نیست؛ یا [حتی] سریع‌تر. بی‌تردید، الله بر هر چیزی تواناست.');
INSERT INTO "islamhouse" VALUES(16,78,'16:78','[ای مردم،] الله شما را از شکمِ مادرانتان خارج نمود در حالی ‌که هیچ چیز نمی‌دانستید؛ و گوش و چشم و دل [= عقل] برایتان پدید آورد [تا قدرت‌های الهی را درک کنید]؛ باشد که سپاس گزارید.');
INSERT INTO "islamhouse" VALUES(16,79,'16:79','آیا [مشرکان] پرندگان را ندیده‌اند که در فضای آسمان رام شده‌اند [و] کسی جز الله آنها را [در آن حال‌] نگه نمی‌دارد؟ به راستی، در این [امر‌]، براى مردمى كه ایمان دارند، نشانه‌هایى [از قدرت پروردگار] است.');
INSERT INTO "islamhouse" VALUES(16,80,'16:80','الله از خانه‌هایتان محلِ سکونت [و آرامش] برای شما قرار داد و از پوست چهارپایان [نیز] خانه‌هایی [= خیمه‌هایی] برایتان قرار داد تا روز کوچ کردن‌ سبکبار باشید و روز اقامتتان [نیز برپایی‌اش آسان باشد]؛ و از پشم و کُرک و مو‌هایشان تا مدتی معیّن اثاث [خانه] و اسباب [و وسایل زندگی، برایتان] فراهم کرد.');
INSERT INTO "islamhouse" VALUES(16,81,'16:81','الله از آنچه [مانند درختان] آفرید، سایه‌هایی برایتان فراهم آورده است و از کوه‌ها [نیز راه‌ها و] پناهگاه‌هایی برایتان قرار داده است و جامه‌هایى برایتان پدید آورده است كه شما را از [سرما] و گرما نگاه می‌دارد و [زِره‌ها و] تن‌پوشهایی كه در جنگ[های]تان از شما محافظت مى‌کند. او اینچنین نعمتش را بر شما تمام مى‌گردانَد؛ باشد که [در برابرِ توحید] تسلیم شوید [و شرک نورزید].');
INSERT INTO "islamhouse" VALUES(16,82,'16:82','پس [ای پیامبر،] اگر [مشرکان] روی گرداندند، جز این نیست که وظیفۀ تو [فقط] پیام‌رسانیِ آشکار است.');
INSERT INTO "islamhouse" VALUES(16,83,'16:83','[آنان] نعمت الله را می‌شناسند؛ اما [باز هم] آن را انکار می‌کنند و بیشتر آنان ناسپاس و کافرند.');
INSERT INTO "islamhouse" VALUES(16,84,'16:84','و [یاد کن از] روزی‌ که از هر امتی، گواهی [بر آنان] برمی‌انگیزیم؛ آنگاه به کسانی‌ که کفر ورزیدند نه اجازۀ [سخن گفتن] داده می‌شود و نه از آنان خواسته می‌شود که پوزش بخواهند.');
INSERT INTO "islamhouse" VALUES(16,85,'16:85','و کسانی‌ که ستم کردند، هنگامی ‌که عذاب [آخرت] را می‌بینند؛ نه عذابشان سبک می‌شود و نه مهلت داده می‌شوند.');
INSERT INTO "islamhouse" VALUES(16,86,'16:86','کسانی كه شرک ورزیدند، وقتی شریكان خود را می‌بینند مى‌گویند: «پروردگارا، اینها بودند آن شریكانى كه ما به جاى تو مى‌خواندیم» و[لى شریكان‌،] سخنِ آنان را [اینگونه] رد مى‌كنند كه: «یقیناً شما دروغگویید».');
INSERT INTO "islamhouse" VALUES(16,87,'16:87','در آن روز، در برابرِ الله، سرِ تسلیم می‌افكنند و دروغ‌هایی که [دربارۀ شفاعتِ غیر الله] می‌بافتند همه محو و نابود می‌گردد.');
INSERT INTO "islamhouse" VALUES(16,88,'16:88','کسانی‌ که کفر ورزیدند و [مردم را] از راهِ الله بازداشتند، به [کیفر] آنکه فساد می‌کردند، عذابی بر عذابشان می‌افزاییم.');
INSERT INTO "islamhouse" VALUES(16,89,'16:89','و روزی [را یاد کن] که از هر امتی، گواهی از خودشان بر آنان می‌گماریم [تا به کفر و ایمانشان گواهی دهد] و تو را [نیز ای محمد،] بر اینان گواه می‌گیریم؛ و ما این کتاب [= قرآن‌] را بر تو نازل کردیم که روشنگرِ همه چیز است و برای مسلمانان، رهنمود و رحمت و بشارت است.‌');
INSERT INTO "islamhouse" VALUES(16,90,'16:90','در حقیقت، الله [شما را] به عدالت و نیکوکاری و بخشش به خویشاوندان فرمان مى‌دهد و از فحشا و زشت‌کاری و سرکشی نهى مى‌كند. او شما را پند مى‌دهد؛ باشد كه پند گیرید.');
INSERT INTO "islamhouse" VALUES(16,91,'16:91','و چون با الله [یا با مردم] پیمان بستید، به آن وفا كنید؛ و سوگندها[ى خود] را پس از محكم ساختنشان نشكنید، در حالى كه الله را بر [وفاداریِ] خویش گواه قرار داده‌اید. بى‌تردید، الله می‌داند که چه مى‌كنید.');
INSERT INTO "islamhouse" VALUES(16,92,'16:92','و مانند آن زن [نادانی] نباشید که رشتۀ [پشمی تابیدۀ] خویش را پس از محکم‌بودن از هم باز‌گشود [و گسست؛ پس مبادا] سوگندهاى خود را وسیلۀ فریب یکدیگر قرار دهید [آن هم تنها] به سبب این كه [ثروت و] جمعیت گروهی افزون‌تر از دیگرى است. جز این نیست که الله شما را با این [کار] می‌آزماید و یقیناً روز قیامت، آنچه را که در آن اختلاف می‌کردید، برایتان روشن می‌سازد.');
INSERT INTO "islamhouse" VALUES(16,93,'16:93','و اگر الله می‌خواست، همۀ شما را یک امت قرار می‌داد؛ ولی [با عدالت خویش] هر کس را که بخواهد گمراه می‌کند و [با فضل خویش] هر کس را که بخواهد هدایت می‌نماید؛ و شما [روز قیامت] قطعاً از آنچه می‌کردید، بازخواست خواهید شد.');
INSERT INTO "islamhouse" VALUES(16,94,'16:94','و سوگندهایتان را وسیلۀ نیرنگ [و فریبکاری] میان خویش قرار ندهید، تا مبادا [ایمانتان به خطر بیفتد و] گامى پس از استواری‌اش بلغزد و به سزاى آنكه [مردم را] از راه الله بازداشته‌اید، [کیفرِ] ناگواری بچشید و عذابی بزرگ برایتان [در پیش‌] باشد‌.');
INSERT INTO "islamhouse" VALUES(16,95,'16:95','و پیمان الله را به بهای اندک نفروشید. اگر بدانید، یقیناً آنچه نزد الله است، برایتان بهتر است.');
INSERT INTO "islamhouse" VALUES(16,96,'16:96','آنچه نزد شماست، پایان می‌پذیرد و[لی] آنچه نزد الله است، باقی [و جاوید] است؛ و قطعاً به كسانى كه شكیبایى كرده‌اند، اجرشان را بر اساس نیکوترین [طاعت و] كارى كه مى‌كردند خواهیم داد.');
INSERT INTO "islamhouse" VALUES(16,97,'16:97','هر کس ـ مرد یا زن ـ که کار نیک کند و مؤمن باشد، قطعاً او را [در دنیا] به زندگانی پاک و پسندیده‌ای زنده می‌داریم و [در آخرت نیز] بر اساس نیکوترین [طاعت و] كارى كه مى‌كردند، به آنان پاداش می‌دهیم.');
INSERT INTO "islamhouse" VALUES(16,98,'16:98','پس [ای مؤمن،] هنگامی ‌که قرآن می‌خوانی، از [شرّ] شیطانِ رانده‌شده [از رحمت الهی]، به الله پناه ببر.');
INSERT INTO "islamhouse" VALUES(16,99,'16:99','بی‌تردید، او بر کسانی ‌که ایمان آورده‌اند و بر پروردگار‌شان توکل می‌کنند [هیچ] تسلطی ندارد.');
INSERT INTO "islamhouse" VALUES(16,100,'16:100','تسلط او تنها بر کسانی است که او را [دوست و] کارساز خود گرفته‌اند و [همچنین بر] کسانی ‌که به سبب [اغوای] شیطان، [به الله] شرک می‌ورزند.');
INSERT INTO "islamhouse" VALUES(16,101,'16:101','و هنگامی که [حكمِ] آیه‌اى را جایگزین آیه‌ای [دیگر] می‌کنیم ـ حال آنكه الله به آنچه نازل مى‌كند داناتر است ـ [کافران] مى‌گویند: «[ای محمد،] جز این نیست كه تو دروغ‌بافی». [چنین نیست؛] بلکه بیشتر آنان [از احکام شرع و حمکت الهی چیزی] نمى‌دانند.');
INSERT INTO "islamhouse" VALUES(16,102,'16:102','بگو: «روح القُدُس [= جبرئیل] آن را از جانب پروردگارت به‌حق نازل کرده است تا کسانی را که ایمان آورده‌اند ثابت‌قدم گرداند و هدایت و بشارتی برای مسلمانان باشد».');
INSERT INTO "islamhouse" VALUES(16,103,'16:103','و یقیناً می‌دانیم که آنان می‌گویند: «حتماً بشری [این آیات را] به او آموزش می‌دهد». [نه، چنین نیست؛ زیرا] زبان كسى كه [این‌] نسبت را به او مى‌دهند غیرعربى است؛ حال آنکه این [قرآن‌،] به زبان عربىِ روشن [و با بلاغت و فصاحت عالی نازل شده] است.');
INSERT INTO "islamhouse" VALUES(16,104,'16:104','یقیناً کسانی ‌که به آیات الله ایمان نمی‌آورند، الله آنان را هدایت نمی‌کند و عذاب دردناکی برای آنان [در پیش] است.');
INSERT INTO "islamhouse" VALUES(16,105,'16:105','[محمد صلی الله علیه وسلم دروغگو نیست؛ بلکه] کسانی دروغ می‌بافند که به آیات الله ایمان ندارند و اینان دروغگویان [واقعی] هستند.');
INSERT INTO "islamhouse" VALUES(16,106,'16:106','هر کس بعد از ایمانش به الله کفر بورزد، [گرفتار عذاب الهی خواهد شد]؛ مگر کسی که [به این کار،] وادار شده باشد و[لی] دلش به ایمان، آرام [و استوار] باشد؛ اما هر کس سینه‌اش را به کفر بگشاید، خشم الله بر آنان است و عذابی سهمگین [در پیش‌] دارند.');
INSERT INTO "islamhouse" VALUES(16,107,'16:107','این [مجازات،] از آن روست که زندگی دنیا را بر آخرت ترجیح دادند؛ و [نیز] اینکه الله گروه کافران را هدایت نمی‌کند.');
INSERT INTO "islamhouse" VALUES(16,108,'16:108','اینان کسانی هستند که [بر اثر گناهانشان] الله بر دل‌ها و گوش‌ها و چشمانشان مُهر نهاده است؛ و اینان غافلان [واقعی] هستند.');
INSERT INTO "islamhouse" VALUES(16,109,'16:109','یقیناً آنان در آخرت زیانکارند.');
INSERT INTO "islamhouse" VALUES(16,110,'16:110','سپس پروردگارت نسبت به كسانى كه پس از زجر کشیدن [از آزار مشرکان] هجرت كردند سپس به جهاد برخاستند و شکیبایی نمودند، پس از آن [همه سختی‌ها‌] قطعاً آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(16,111,'16:111','[ای پیامبر، یاد کن از] روزى كه هر كس در حالی [به پیشگاه عدل الهی] مى‌آید که [تنها] از خود دفاع مى‌كند؛ و هر كس در برابر آنچه كرده است، بى‌كم‌وكاست پاداش مى‌گیرد و بر آنان ستم نمى‌رود.');
INSERT INTO "islamhouse" VALUES(16,112,'16:112','و الله شهری [همچون مکه] را مثال می‌زند که امن و آرام بود [و] روزی‌اش از هر سو به آسانی و فراوانی می‌رسید؛ ولی [مردمانش] نسبت به نعمت‌های الهی ناسپاسی نمودند و آنگاه الله [نیز] به سزاى آنچه مرتکب می‌شدند، طعم گرسنگى و ترس را به [اهالی‌] آن چشانید.');
INSERT INTO "islamhouse" VALUES(16,113,'16:113','و پیامبری از خودشان به سوی آنان آمد، اما او را دروغگو انگاشتند؛ و در حالی ‌که ستمکار [و مشرک] بودند، عذاب [الهی] آنان را فروگرفت.');
INSERT INTO "islamhouse" VALUES(16,114,'16:114','پس، از آنچه الله روزی‌تان کرده است حلال [و] پاکیزه بخورید و اگر فقط او را عبادت می‌کنید [و شرک نمی‌ورزید]، بر نعمت الهی شکر گزارید.');
INSERT INTO "islamhouse" VALUES(16,115,'16:115','جز این نیست که [الله] فقط مُردار و خون و گوشت خوک و آنچه را که [هنگام ذبح،] نامِ غیر الله بر آن برده شده است بر شما حرام کرده است؛ اما هر کس [برای حفظ جانش به خوردن آنها] ناچار شود [و] سركش و زیاده‌خواه نباشد، [بر او گناهی نیست؛ چرا که] بی‌تردید، الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(16,116,'16:116','و [ای مشرکان،] با دروغی که بر زبانتان جاری می‌شود نگویید: «این حلال است و آن حرام» تا بر الله دروغ بندید. كسانى كه بر الله دروغ مى‌بندند، هرگز رستگار نمى‌شوند.');
INSERT INTO "islamhouse" VALUES(16,117,'16:117','[نتیجۀ دنیادوستی‌شان] بهره‌ای اندک است و [در آخرت] عذابى دردناک [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(16,118,'16:118','و بر یهود آنچه را پیش‌تر بر تو حكایت کردیم حرام نمودیم و ما به آنان ستم نكردیم؛ بلكه آنها خود [بودند که در نتیجۀ نافرمانی،] به خویشتن ستم مى‌كردند.');
INSERT INTO "islamhouse" VALUES(16,119,'16:119','با وجود این، پروردگارت نسبت به كسانى كه از روى نادانی گناه كردند و سپس از آن [اشتباهات] توبه نمودند و درستكارى كردند، قطعاً پس از آن، آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(16,120,'16:120','بی‌گمان، ابراهیم پیشوایی فروتن در برابر الله [و] حقگرا بود و [هرگز] در زمرۀ مشرکان نبود.');
INSERT INTO "islamhouse" VALUES(16,121,'16:121','[او] شکرگزارِ نعمت‌های الله بود. [الله] وی را برگزید و به راه راست هدایت نمود.');
INSERT INTO "islamhouse" VALUES(16,122,'16:122','[ما] در دنیا به او نیکی عطا کردیم و قطعاً او در آخرت [نیز] از شایستگان است.');
INSERT INTO "islamhouse" VALUES(16,123,'16:123','سپس [ای پیامبر،] به تو وحی کردیم که از آیین ابراهیم پیروی کن که حقگرا بود و [هرگز] در زمرۀ مشرکان نبود.');
INSERT INTO "islamhouse" VALUES(16,124,'16:124','[وجوبِ بزرگداشتِ روزهای] شنبه، تنها بر کسانی‌ قرار داده شد که در آن اختلاف کردند؛ و قطعاً پروردگارت روز قیامت، در مورد آنچه اختلاف می‌کردند، میان آنان داوری خواهد کرد.');
INSERT INTO "islamhouse" VALUES(16,125,'16:125','[ای پیامبر، مردم را] با حکمت و پند نیکو به راه پروردگارت دعوت نما و با روشی که نیکو‌تر است با آنان [بحث و] مناظره کن. بی‌تردید، پروردگارت به [حال] کسی‌ که از راه او گمراه می‌شود دانا‌تر است و او به [حال] هدایت‌یافتگان [نیز] دانا‌تر است.');
INSERT INTO "islamhouse" VALUES(16,126,'16:126','و اگر [شما مؤمنان] خواستید [دشمنانتان را] مجازات کنید، چنان مجازات کنید که [آنان] شما را مجازات کرده‌اند؛ و اگر [خویشتنداری و‌] شکیبایی کنید، [بدانید که] قطعاً این [کار] برای شکیبایان بهتر است‌.');
INSERT INTO "islamhouse" VALUES(16,127,'16:127','و [ای پیامبر،] صبر پیشه کن و صبر تو جز به [یاری و] توفیق الله نیست؛ و بر آنان [= کافران] اندوه مخور و از نیرنگی که می‌ورزند دلتنگ مباش‌.');
INSERT INTO "islamhouse" VALUES(16,128,'16:128','بی‌تردید، الله با كسانى است كه پرهیزگاری کردند و كسانى كه نیكوكارند.');
INSERT INTO "islamhouse" VALUES(17,1,'17:1','پاک و منزّه است آن [پروردگاری‌] كه بنده‌اش [محمد] را شبانگاه از مسجد الحرام به سوى مسجد الاقصى ـ كه پیرامونش را بركت داده‌ایم ـ سِیر داد تا برخی از نشانه‌هاى [قدرت] خویش را به او بنمایانیم. بی‌تردید، او همان شنواى بیناست.');
INSERT INTO "islamhouse" VALUES(17,2,'17:2','و ما به موسی کتاب [تورات] دادیم و آن را [مایۀ] هدایتِ بنی‌اسرائیل ساختیم [و فرمودیم] که: «کارگزاری جز من [برای خود] برنگزینید».');
INSERT INTO "islamhouse" VALUES(17,3,'17:3','ای فرزندانِ کسانی‌ که آنان را با نوح [در کشتی] سوار کردیم، به راستی او [= نوح] بنده‌ای شکرگزار بود.');
INSERT INTO "islamhouse" VALUES(17,4,'17:4','و در کتاب [تورات] به بنی‌اسرائیل وحی کردیم که قطعاً دو بار در زمین فساد خواهید کرد و قطعاً سرکشی [و طغیانِ] بزرگی خواهید نمود.');
INSERT INTO "islamhouse" VALUES(17,5,'17:5','پس هر گاه وعدۀ نخستین از آن دو [بار] فرارسید، گروهی از بندگانِ بسیار نیرومند [و پیکارجویِ] خود را بر شما برمی‌انگیزیم تا میان خانه‌ها[یتان براى قتل و غارتِ شما] به جستجو درآیند؛ و این وعده‌ای انجام‌شدنی [و قطعی] بود.');
INSERT INTO "islamhouse" VALUES(17,6,'17:6','آنگاه [پس از مدتی] شما را بر آنان مسلط می‌گردانیم و با اموال و فرزندان یاری‌تان می‌کنیم، و [تعداد] نفرات شما را بیشتر [از دشمن] می‌گردانیم.');
INSERT INTO "islamhouse" VALUES(17,7,'17:7','اگر [در گفتار و رفتار] نیکی کنید، به خود نیکی کرده‌اید؛ و اگر بدی کنید، [باز هم به] خود کرده‌اید. پس هنگامی‌ که وعدۀ دیگر فرارسید، [دشمن آنچنان شما را خوار می‌سازد] که آثار غم و اندوه در چهره‌هایتان ظاهر می‌شود و همان گونه که بارِ نخست وارد شدند، [این بار نیز حمله می‌آورند] تا به مسجد[الاقصی] داخل ‌شوند و تا بر هر چه دست یابند [آن را] یکسره نابود کنند.');
INSERT INTO "islamhouse" VALUES(17,8,'17:8','[ای بنی‌اسرائیل، اگر توبه كنید] امید است كه پروردگارتان بر شما رحمت آورد و اگر [به تباهی] بازگشتید، ما نیز [به عذاب‌کردن شما] بازمى‌گردیم و دوزخ را زندان [ابدیِ] كافران کرده‌ایم.');
INSERT INTO "islamhouse" VALUES(17,9,'17:9','به راستی که این قرآن به استوارترین راه هدایت مى‌کند و به مؤمنانى كه كارهاى شایسته مى‌كنند مژده مى‌دهد كه پاداشى بزرگ برایشان [در پیش] است.');
INSERT INTO "islamhouse" VALUES(17,10,'17:10','و براى كسانى كه به آخرت ایمان نمى‌آورند، عذابى دردناک مهیّا كرده‌ایم.');
INSERT INTO "islamhouse" VALUES(17,11,'17:11','انسان [در هنگام خشم، از روی نادانی و شتاب، نفرین می‌کند و] بدى را مى‌طلبد، همان گونه كه نیکی را درخواست می‌کند؛ و انسان همواره شتابزده است‌.');
INSERT INTO "islamhouse" VALUES(17,12,'17:12','شب و روز را دو نشانه[ی قدرت و یگانگی خویش] قرار دادیم؛ و نشانۀ شب را محو [و تیره‌] كردیم و نشانۀ روز را روشنى‌بخش قرار دادیم تا از پروردگارتان روزى بجویید و شمار سال‌ها و حساب [وقایع] را بدانید؛ و هر چیزى را به تفصیل بیان كردیم.');
INSERT INTO "islamhouse" VALUES(17,13,'17:13','و [سرنوشتِ‌] رفتار هر انسانى را [تا لحظۀ حسابرسی] در گردنش بسته‌ایم و روز قیامت نوشته‌اى برایش بیرون می‌آوریم كه آن را گشوده می‌بیند.');
INSERT INTO "islamhouse" VALUES(17,14,'17:14','[به او می‌گوییم:] «نامه‌ات را بخوان. کافی است که امروز خود حسابگرِ خویش باشی».');
INSERT INTO "islamhouse" VALUES(17,15,'17:15','هر کس راه [راست‌] یافت، جز این نیست كه به سود خویش راه یافته است و هر کس گمراه شد، یقیناً به زیان خود گمراه شده است؛ و هیچ گناهکاری، بارِ گناه دیگری را برنمی‌دارد؛ و ما هرگز [قومی را] مجازات نخواهیم کرد، مگر آنکه [میانشان] پیامبری مبعوث کرده باشیم [تا وظایفشان را بیان کند].');
INSERT INTO "islamhouse" VALUES(17,16,'17:16','و هر گاه بخواهیم [مردم] شهری را نابود کنیم، [نخست] به ثروتمندانِ سرکشِ آنجا فرمان [اطاعت از الله و پیروی از پیامبران را] می‌دهیم، سپس وقتی در آنجا به فساد [و مخالفت] برخاستند، وعده[ی عذاب الهی بر] آنجا محقق می‌گردد؛ آنگاه آنجا را به شدت در هم می‌کوبیم [و ساکنانش را از بین می‌بریم].');
INSERT INTO "islamhouse" VALUES(17,17,'17:17','و چه بسیار نسل‌هایی را که پس از نوح [زندگی می‌کردند] هلاک کردیم و [همین] کافی است که پروردگارت از گناهان بندگانش آگاه [و نسبت به آنان] بیناست.');
INSERT INTO "islamhouse" VALUES(17,18,'17:18','هر کس خواهان [دنیاى‌] زودگذر است، هر چه بخواهیم [و] به هر كه بخواهیم [بهره‌ای] از آن مى‌دهیم؛ آنگاه دوزخ را نصیبش می‌کنیم كه در آنجا خوار و رانده‌شده [از رحمت الله،] وارد می‌گردد.');
INSERT INTO "islamhouse" VALUES(17,19,'17:19','و هر کس [زندگی] آخرت را بخواهد و برای [رسیدن به] آن با جدیّت بکوشد و مؤمن باشد، اینان کسانی هستند که از تلاششان قدردانی خواهد شد.');
INSERT INTO "islamhouse" VALUES(17,20,'17:20','همه را ـ [خواه] این گروه [بدکار] و [یا] آن گروه [نیکوکار]ـ از بخشش [و نعمت‌های دنیویِ] پروردگارت بهره‌مند می‌سازیم و بخششِ پروردگارت [از کسی] منع نشده است.');
INSERT INTO "islamhouse" VALUES(17,21,'17:21','بنگر که چگونه برخی از آنان را [از لحاظ مال و مقام] بر برخی دیگر برترى داده‌ایم؛ و قطعاً آخرت [در مقایسه با دنیا] از نظرِ درجات، بزرگ‌تر و از لحاظ فضیلت، برتر است [پس مومن باید به آن اشتیاق ورزد].');
INSERT INTO "islamhouse" VALUES(17,22,'17:22','[ای انسان، هرگز هیچ] معبود دیگری را با الله [شریک] قرار نده که نکوهیده و بی‌یاور خواهی ماند.');
INSERT INTO "islamhouse" VALUES(17,23,'17:23','و پروردگارت چنین فرمان داده است که: «جز او را عبادت نکنید و به پدر و مادر [تان] نیکی کنید. هر گاه یکی از آن دو یا هر دوی آنها در کنارِ تو به سن پیری رسیدند، حتی کمترین اهانتی به ایشان نکن و بر سرشان فریاد نزن و به نیکی [و مهربانی] با آنان سخن بگو.');
INSERT INTO "islamhouse" VALUES(17,24,'17:24','و از روی مهربانی، در برابرشان فروتن باش و بگو: «پروردگارا، همان‌ گونه که [بر من رحمت آوردند و] مرا در کودکی پرورش دادند، [تو نیز] به آنان رحمت آور».');
INSERT INTO "islamhouse" VALUES(17,25,'17:25','پروردگارِ شما، به آنچه در دل‌هایتان است آگاه‌تر است. اگر شایسته باشید، یقیناً او در حقِ توبه‌کاران آمرزنده است.');
INSERT INTO "islamhouse" VALUES(17,26,'17:26','و حق خویشاوند را به او بده [= صلۀ رحِم به جای آور] و نیازمند و درراه‌مانده [را یاری كن‌] و ولخرجى و اسراف نكن.');
INSERT INTO "islamhouse" VALUES(17,27,'17:27','در حقیقت، اسرافكاران برادرانِ شیاطین هستند و شیطان نسبت به پروردگارش همواره ناسپاس بوده است.');
INSERT INTO "islamhouse" VALUES(17,28,'17:28','و اگر به امید رحمت و [گشایش روزی] از جانب پروردگارت [به ناچار] از آنان روی می‌گردانی، سخنی نرم به آنان بگو.');
INSERT INTO "islamhouse" VALUES(17,29,'17:29','و [هرگز] دست از انفاق نبند و [نیز] گشاده‌دستیِ بسیار نکن که ملامت‌شده و حسرت‌زده می‌گردی.');
INSERT INTO "islamhouse" VALUES(17,30,'17:30','بی‌گمان، پروردگارت، [نعمت و] روزی را بر هر کس که بخواهد گسترده می‌دارد و یا تنگ می‌گرداند. بی‌تردید، او همواره به [احوالِ] بندگانش آگاه و بیناست‌.');
INSERT INTO "islamhouse" VALUES(17,31,'17:31','و فرزندانتان را از بیم تنگدستى نكشید. ما به آنان و شما روزى مى‌بخشیم. یقیناً كشتنِ آنها همواره خطایى بزرگ است.');
INSERT INTO "islamhouse" VALUES(17,32,'17:32','و نزدیک زنا نشوید. بی‌تردید، آن [کار،] بسیار زشت و راهی ناپسند است.');
INSERT INTO "islamhouse" VALUES(17,33,'17:33','و كسى را كه الله [كشتنش را] حرام كرده است جز به‌حق نكشید؛ و هر کس به ستم كشته شود، به قیّمِ او قدرت [و حق قصاص] داده‌ایم؛ او نیز نباید در [کیفرِ‌] قتل زیاده‌روى كند. بی‌تردید، او [= ولیّ دَم] تحت حمایت [الله و شریعت او تعالی] قرار دارد.');
INSERT INTO "islamhouse" VALUES(17,34,'17:34','و به مال یتیم ـ جز به شیوه‌ای که نیکوتر است‌ـ نزدیک نشوید تا آنگاه که به حدِ [رشد و] بلوغش برسد و به پیمان [خویش] وفا کنید؛ چرا که [از شما] دربارۀ پیمان پرسیده خواهد شد.');
INSERT INTO "islamhouse" VALUES(17,35,'17:35','و هنگامی ‌که [در خرید و فروش، اجناس را] پیمانه می‌کنید، پیمانه را کامل [و تمام] بدهید و [آنها را] با ترازوی درست وزن کنید. این [کار] برای شما بهتر است و سرانجام نیکو‌تری دارد.');
INSERT INTO "islamhouse" VALUES(17,36,'17:36','و از آنچه دانشی دربارۀ آن نداری پیروی نکن. بی‌تردید [در قیامت،] گوش و چشم و دل، از همۀ اینها بازخواست خواهد شد.');
INSERT INTO "islamhouse" VALUES(17,37,'17:37','و در زمین با تکبر راه نرو. تو هرگز نمی‌توانی [با راه رفتنِ غرورآمیزت] زمین را بشکافی و در بلندی [و گردن‌فرازی نیز] به کوه‌ها نمی‌رسی.');
INSERT INTO "islamhouse" VALUES(17,38,'17:38','همۀ این [کارها]، گناهش نزد پروردگارت ناپسند است.');
INSERT INTO "islamhouse" VALUES(17,39,'17:39','این [سفارش‌ها] از آن حکمت[هایی] است که از جانب پروردگارت به تو وحی شده است؛ و معبودِ دیگری با الله قرار نده که نکوهیده و رانده‌شده [از رحمت الله] به دوزخ خواهی افتاد.');
INSERT INTO "islamhouse" VALUES(17,40,'17:40','آیا [شما مشرکان گمان می‌کنید كه‌] پروردگارتان [داشتن‌] پسران را به شما اختصاص داده است و خود از [میانِ] فرشتگان، دخترانى برگرفته است؟ به راستی که سخنى بسیار بزرگ [و ناروا] مى‌گویید.');
INSERT INTO "islamhouse" VALUES(17,41,'17:41','و بی‌تردید، ما در این قرآن [احکام و اندرزها را با شیوه‌های روشن و] گونا‌گون بیان كردیم تا [مردم] پند بگیرند؛ و[لى‌ این سخنان،] جز دوری [از حق] بر آنان نمى‌افزاید.');
INSERT INTO "islamhouse" VALUES(17,42,'17:42','[ای پیامبر،] بگو: «اگر [بر فرضِ محال،] چنان که [مشرکان] می‌گویند، با او [= الله] معبودانی [دیگر] بود، در این صورت، آنان [برای تقرّب و کسبِ خشنودی پروردگار] به سوی [اللهِ] صاحب‌عرش راهی می‌جستند».');
INSERT INTO "islamhouse" VALUES(17,43,'17:43','او تعالی پاک و منزّه است و از آنچه آنان می‌گویند، بسی برتر است.');
INSERT INTO "islamhouse" VALUES(17,44,'17:44','آسمان‌های هفتگانه و زمین و هر ‌کس در آنهاست او را به پاکی می‌ستایند؛ و هیچ چیز نیست مگر آنکه او را به پاکی یاد می‌کند؛ ولی شما تسبیح‌ آنان را درنمی‌یابید. بی‌تردید، او همواره بردبار [و] آمرزنده است.');
INSERT INTO "islamhouse" VALUES(17,45,'17:45','و [ای پیامبر،] هنگامی‌ که قرآن می‌خوانی، میان تو و کسانی ‌که به آخرت ایمان ندارند، پرده‌ای پوشیده قرار می‌دهیم.');
INSERT INTO "islamhouse" VALUES(17,46,'17:46','و پوششی بر دل‌هایشان قرار دادیم تا [پیام] آن را درنیابند و در گوش‌هایشان سنگینى [نهادیم تا حق را نشنوند‌]؛ و چون پروردگارت را در قرآن به یگانگى یاد می‌كنى، با نفرت پشت مى‌كنند [و می‌گریزند].');
INSERT INTO "islamhouse" VALUES(17,47,'17:47','و آنگاه كه [کافران] به تو گوش می‌سپارند، ما بهتر مى‌دانیم که به چه [منظور و چگونه] گوش مى‌دهند و [نیز] آنگاه كه نجوا مى‌کنند [ما از سخنانشان آگاهیم]؛ آنگاه که ستمکاران [مشرک به مردم] می‌گویند: «شما جز از مردى جادوشده پیروى نمى‌كنید».');
INSERT INTO "islamhouse" VALUES(17,48,'17:48','بنگر که چگونه برایت مثال‌ها زدند، پس گمراه شدند و در نتیجه، نمی‌توانند راهی [به سوی حق] بیابند.');
INSERT INTO "islamhouse" VALUES(17,49,'17:49','و [مشرکان] گفتند: «آیا هنگامی‌ که استخوان‌هایی [پوسیده و] پراکنده شدیم، دوباره با آفرینشی جدید برانگیخته می‌شویم؟»');
INSERT INTO "islamhouse" VALUES(17,50,'17:50','بگو: «[حتی اگر] سنگ باشید یا آهن');
INSERT INTO "islamhouse" VALUES(17,51,'17:51','یا [هر] آفریده‌ای که در خاطرتان بزرگ می‌نماید، [باز هم الله می‌تواند شما را زنده کند]. پس خواهند گفت: «چه کسی ما را بازمی‌گردانَد؟» بگو: «[همان پروردگاری] ‌که نخستین بار شما را آفرید». آنگاه سرهایشان را [به نشانۀ ناباوری و تمسخر] در برابر تو تکان می‌دهند و مى‌گویند: «آن [حادثه] كى خواهد بود؟» بگو: «چه بسا نزدیک باشد.');
INSERT INTO "islamhouse" VALUES(17,52,'17:52','روزى كه [الله] شما را [به صحرای محشر] فرامى‌خوانَد و شما با سپاس و ستایش اجابتش مى‌کنید و مى‌پندارید كه جز اندكى [در دنیا] به سر نبرده‌اید».');
INSERT INTO "islamhouse" VALUES(17,53,'17:53','و [ای پیامبر،] به بندگانم بگو با بهترین [الفاظ با یكدیگر] صحبت كنند [و از بددهانی بپرهیزند؛ چرا که] بی‌تردید، شیطان میان آنان دشمنی می‌افکنَد. به راستی که شیطان برای انسان، دشمنی آشکار است‌.');
INSERT INTO "islamhouse" VALUES(17,54,'17:54','[ای مردم،] پروردگارتان به [احوال‌] شما داناتر است؛ اگر بخواهد، بر شما رحمت می‌آورد یا اگر بخواهد، عذابتان می‌کند؛ و [ای پیامبر،] ما تو را [برای آنان، نگهبان و] کارگزار نفرستاده‌ایم‌.');
INSERT INTO "islamhouse" VALUES(17,55,'17:55','پروردگارت به [هر چه و] هر ‌کس در آسمان‌ها و زمین است داناتر است؛ و به راستی [ما] برخی پیامبران را [با افزونیِ پیروان و اعطای کتاب،] بر برخی [دیگر] برتری بخشیدیم و به داوود زَبور دادیم.');
INSERT INTO "islamhouse" VALUES(17,56,'17:56','[ای پیامبر، به مشرکان] بگو: «كسانى را كه به جاى او [معبود و کارسازِ خویش] پنداشته‌اید فرابخوانید؛ [خواهید دید که آنها] نه مى‌توانند گزندی را از شما دور کنند و نه قادرند آن را [به سوی دیگری] بگردانند».');
INSERT INTO "islamhouse" VALUES(17,57,'17:57','کسانی را که آنان [به یاری و دعا] می‌خوانند، خود [با عبادت و عمل صالح] وسیله‌ای [برای تقرّب] به پروردگارشان می‌جویند [و در رقابتند تا بدانند] كدام‌یک از آنان [به عبادت الله] نزدیک‌ترند و به رحمتش امیدوارند و از عذابش می‌ترسند؛ [چرا که] بی‌تردید، عذاب پروردگارت همواره شایستۀ پرهیز است.');
INSERT INTO "islamhouse" VALUES(17,58,'17:58','و هیچ شهرى نیست مگر اینكه [به سبب کفر و نافرمانى، مردمِ] آن را پیش از روز قیامت نابود می‌کنیم یا به عذابى سخت گرفتار مى‌سازیم. این [کیفر] در کتاب الهی [= لوح محفوظ] ثبت است.');
INSERT INTO "islamhouse" VALUES(17,59,'17:59','[چیزى‌] ما را از فرستادن معجزات [درخواستى مشركان‌] بازنداشت، مگر اینكه پیشینیان آن را دروغ پنداشتند؛ و ما به ثمود [قوم صالح] آن ماده‌شتر را به عنوان نشانه‌ای روشن دادیم؛ اما به آن ستم كردند و ما نشانه‌ها [و معجزات] را جز براى بیم‌دادن [مردم] نمى‌فرستیم.');
INSERT INTO "islamhouse" VALUES(17,60,'17:60','و [به یاد آور‌] هنگامى كه به تو گفتیم: «یقیناً پروردگارت بر مردم احاطه دارد [و در قبضه قدرت او هستند]»؛ و آن رؤیایى را كه [در شب معراج] به تو نمایاندیم؛ و [همچنین] آن درختِ لعنت‌شده [زَقّوم] در قرآن را جز براى آزمایش مردم قرار ندادیم؛ و ما آنان را بیم مى‌دهیم، ولى جز بر سرکشیِ بیشتر آنان نمى‌افزاید.');
INSERT INTO "islamhouse" VALUES(17,61,'17:61','و [یاد کن از] هنگامی که به فرشتگان گفتیم: «برای آدم سجده کنید»؛ پس [همگی] سجده کردند، جز ابلیس که [از روی تکبر سر باز زد و] گفت: «آیا برای کسی سجده کنم که [او را] از گِل آفریده‌ای؟»');
INSERT INTO "islamhouse" VALUES(17,62,'17:62','[و] گفت: «به من خبر بده آیا این همان کسی است که او را بر من [برتری دادی و] گرامی داشتی؟ اگر تا روز قیامت به من مهلت دهی، فرزندانش را ـ جز عدۀ کمی ـ [مهار زده و] ریشه‌کن خواهم کرد».');
INSERT INTO "islamhouse" VALUES(17,63,'17:63','[الله] فرمود: «برو که هر یک از آنان که از تو پیروی کند، یقیناً دوزخ پاداشتان خواهد بود كه پاداشی کامل است.');
INSERT INTO "islamhouse" VALUES(17,64,'17:64','و هر کس از آنان را توانستى به آواى خود برانگیز [و به سوی گناه بکشان] و با سواران و پیادگانت بر آنان بانگ بزن [تا آلودۀ گناه گردند] و در اموال و فرزندان، شریکشان شو و به آنان وعده[های واهی] بده؛ و[لى] شیطان به آنان وعده نمى‌دهد مگر به فریب.');
INSERT INTO "islamhouse" VALUES(17,65,'17:65','[ای شیطان،] تو هرگز تسلطی بر بندگان من نخواهی داشت»؛ و [ای پیامبر،] همین بس که پروردگارت [کارگزار و] نگهبانِ آنان [= مؤمنان] باشد.');
INSERT INTO "islamhouse" VALUES(17,66,'17:66','[ای مردم،] پروردگار شما کسی است که کشتی را در دریا برایتان به حرکت درمی‌آورد تا از فضلش بهره‌مند گردید. بی‌تردید، او همواره نسبت به شما مهربان است.');
INSERT INTO "islamhouse" VALUES(17,67,'17:67','و آنگاه که در دریا گزندی به شما می‌رسد، هر کس را [به حاجت‌خواهی] مى‌خوانید، ناپدید [و فراموش‌] مى‌گردد مگر [ذات پاکِ] او؛ اما وقتی شما را [از خطر مرگ] رهاند و به خشکی ‌رساند، [از عبادت الله به یگانگی] رویگردان مى‌شوید؛ و انسان همواره ناسپاس است.');
INSERT INTO "islamhouse" VALUES(17,68,'17:68','آیا [می‌پندارید] از اینکه [الله] شما را در کنارۀ خشکی [در زمین] فرو بَرد یا طوفان شن به سوی شما بفرستد [و سنگبارانتان کند] ایمن شده‌اید؟ آنگاه برای خود هیچ [کارگزار و] نگهبانی نمی‌یابید.');
INSERT INTO "islamhouse" VALUES(17,69,'17:69','یا ایمن شده‌اید از اینكه بار دیگر شما را به دریا بازگردانَد و تُندبادى کوبنده به سویتان بفرستد و به سزاى كفرتان شما را غرق كند؟ آنگاه در برابر ما هیچ بازپرسی برای خود نمی‌یابید [که دلیل کارمان را جویا شود].');
INSERT INTO "islamhouse" VALUES(17,70,'17:70','و به راستی، ما فرزندان آدم را گرامی داشتیم و آنان را در خشکی و دریا [بر وسایل سفر] سوار کردیم و از انواع [نعمت‌های] پاکیزه به آنان روزی دادیم و آنان را بر بسیاری از موجوداتی که آفریده‌ایم، برتری بسیار بخشیدیم.');
INSERT INTO "islamhouse" VALUES(17,71,'17:71','[یاد کن از] روزی که هر گروهی را با پیشوایشان فرامی‌خوانیم؛ آنگاه کسانی که نامه[ی اعمال]شان به [دست] راستشان داده می‌شود، اینان نامۀ خود را [با شادمانی] می‌خوانند و [کوچکترین] ستمی[حتی] به اندازۀ رشتۀ میان هستۀ خرما نمی‌بینند.');
INSERT INTO "islamhouse" VALUES(17,72,'17:72','و[لی] کسی‌ که در این [دنیا از دیدن حق] نابینا بوده است، در آخرت [نیز] نابینا و گمراه‌تر است.');
INSERT INTO "islamhouse" VALUES(17,73,'17:73','[ای پیامبر،] نزدیک بود آنها تو را [با وسوسه‌های خود] از آنچه بر تو وحی کرده‌ایم بفریبند تا [الفاظی] غیر از آن را [که وحی کرده‌ایم] به دروغ به ما نسبت دهی و آنگاه تو را به دوستی بگیرند.');
INSERT INTO "islamhouse" VALUES(17,74,'17:74','و اگر [ما] تو را ثابت‌قدم نمی‌کردیم، به راستی نزدیک بود اندکی به آنان متمایل شوی [و در آن‌چه که بر تو پیشنهاد داده‌اند و بخاطر اشتیاق زیاد بر ایمان آوردنشان با آن‌ها موافقت کنی].');
INSERT INTO "islamhouse" VALUES(17,75,'17:75','در آن صورت، قطعاً چند برابر عذاب زندگی در دنیا و چند برابر عذاب مرگ در آخرت به تو می‌چشاندیم و آنگاه در برابر ما یاورى برای خویش نمى‌یافتى.');
INSERT INTO "islamhouse" VALUES(17,76,'17:76','و نزدیک بود [که کافران با نیرنگ و دشمنی،] تو را از سرزمین [مکه] برکَنند تا از آنجا بیرونت کنند و [اگر چنین می‌کردند] در آن صورت، آنان [نیز‌] پس از تو جز [زمان‌] اندكى نمى‌ماندند [و گرفتار عذاب می‌شدند].');
INSERT INTO "islamhouse" VALUES(17,77,'17:77','این [شیوۀ برخورد با ملت‌های ناسپاس،] سنتِ [ما دربارۀ] پیامبرانی است که پیش از تو فرستادیم؛ و [هرگز] در سنت ما [دگرگونی و] تغییری نمی‌یابی.');
INSERT INTO "islamhouse" VALUES(17,78,'17:78','نماز را از زوال خورشید [= هنگام ظهر] تا نهایت تاریکی شب [= نیمه شب‌] برپا دار و [به ویژه] نماز صبح‌ را. به راستی که نماز صبح [و تلاوت قرآن در این وقت،] مورد مشاهده [و در حضورِ فرشتگانِ روز و شب‌] است.');
INSERT INTO "islamhouse" VALUES(17,79,'17:79','و [ای پیامبر،] پاسی از شب را [از خواب] برخیز و نماز بگزار. قطعاً [این نماز شب،‌] برای افزونی مقام و مرتبۀ توست [تا درجاتت را برتری بخشد]. امید است پروردگارت تو را به مقامی در خور ستایشِ [همگان] برانگیزد! [= مقام شفاعت کبری را به تو عطا فرماید].');
INSERT INTO "islamhouse" VALUES(17,80,'17:80','و بگو: «پروردگارا، مرا [در هر كارى‌] به درستی داخل نما و به درستی خارج ساز [چنان که موردِ رضای توست]؛ و از جانب خویش حجتی یاری‌کننده برای من [در برابر دشمنانم‌] قرار بده».');
INSERT INTO "islamhouse" VALUES(17,81,'17:81','و بگو: «حق [= اسلام] آمد و باطل [= شرک] نابود شد. به راستی که باطل نابودشدنی است».');
INSERT INTO "islamhouse" VALUES(17,82,'17:82','و از قرآن، آنچه درمان [شرک و کفر] و رحمتی برای مؤمنان است نازل می‌کنیم و [این کتاب،‌] بر ستمکاران [مشرک‌] جز زیان نمی‌افزاید.');
INSERT INTO "islamhouse" VALUES(17,83,'17:83','و چون به انسان نعمت [ثروت و سلامتی] می‌دهیم، روى مى‌گردانَد و [از شکرگزاری و عبادت] دورى مى‌کند و چون بدی [و گزندی] به او می‌رسد، [از رحمت الهی] ناامید مى‌گردد.');
INSERT INTO "islamhouse" VALUES(17,84,'17:84','[ای پیامبر،] بگو: «هر کس طبق روش خویش [و متناسب با هدایت یا گمراهی‌اش] رفتار می‌کند؛ و پروردگارتان بهتر مى‌داند که چه کسی‌ هدایت‌یافته‌تر است».');
INSERT INTO "islamhouse" VALUES(17,85,'17:85','و [ای پیامبر، کافران] از تو دربارۀ روح می‌پرسند؛ بگو: «روح از [جمله امور غیبی و ناشی از] فرمانِ پروردگارِ من است و جز اندکی از دانش به شما نداده‌اند».');
INSERT INTO "islamhouse" VALUES(17,86,'17:86','و اگر مى‌خواستیم، قطعاً آنچه را به تو وحى كرده‌ایم [از یادت] مى‌بردیم؛ آنگاه هیچ [کارگزار و] نگهبانی در برابر ما برای خود نمی‌یافتی [که مانع فراموشی‌ات گردد].');
INSERT INTO "islamhouse" VALUES(17,87,'17:87','ولی [چنین نکردیم و این لطف، به خاطرِ] رحمتی از جانب پروردگارِ توست. بی‌تردید، همواره بخشش او بر تو بسیار است.');
INSERT INTO "islamhouse" VALUES(17,88,'17:88','[ای پیامبر،] بگو: «اگر انس و جن [همگی] جمع شوند تا همانند این قرآن را بیاورند، [هرگز] همانندش را نخواهند آورد؛ هر چند آنان پشتیبان یکدیگر باشند».');
INSERT INTO "islamhouse" VALUES(17,89,'17:89','و به راستى ما در این قرآن از هر گونه مثال [و اندرز و داستان] برای مردم آوردیم؛ ولى بیشتر مردم [از پذیرشِ آن] خودداری کردند [و] جز سرِ انكار نداشتند.');
INSERT INTO "islamhouse" VALUES(17,90,'17:90','و گفتند: «ما هرگز به تو ایمان نمی‌آوریم، مگر اینکه از زمین [مکه] چشمه‌هایی برایمان جاری کنی');
INSERT INTO "islamhouse" VALUES(17,91,'17:91','یا باغی از نخل و انگور داشته باشی و میان [درختان‌] آن، جوی‌ها روان گردانی‌.');
INSERT INTO "islamhouse" VALUES(17,92,'17:92','یا چنان كه ادعا کردی، یا آسمان را پاره پاره بر [سر] ما بیفکنی یا الله و فرشتگان را در برابرمان حاضر کنی [تا به راستگویی‌ات گواهی دهند]');
INSERT INTO "islamhouse" VALUES(17,93,'17:93','یا خانه‌ای [آراسته] از طلا داشته باشی یا در آسمان بالا روی؛ و [البته] هرگز بالا رفتنت را باور نمی‌کنیم مگر آنکه نوشته‌ای بر ما فرود آوری که آن را بخوانیم». بگو: «پروردگارم منزّه است. آیا من جز بشری فرستاده‌شده هستم؟»');
INSERT INTO "islamhouse" VALUES(17,94,'17:94','و [چیزى‌] مانع ایمان‌آوردنِ مردم نشد ـ آنگاه كه هدایت برایشان آمد ـ جز اینكه گفتند: «آیا الله یک انسان را به پیامبری برانگیخته است؟»');
INSERT INTO "islamhouse" VALUES(17,95,'17:95','[ای پیامبر،] بگو: «اگر در زمین [به جای انسان،] فرشتگانی بودند که [همچون شما] با آرامش راه می‌رفتند، قطعاً از آسمان فرشته‌ای را [از جنس خودشان به عنوان] پیامبر بر آنان نازل می‌کردیم».');
INSERT INTO "islamhouse" VALUES(17,96,'17:96','بگو: «کافی است که الله میان من و شما گواه باشد. بی‌تردید، او همواره [نسبت] به بندگانش آگاه [و] بیناست.');
INSERT INTO "islamhouse" VALUES(17,97,'17:97','و هر کس که الله او را هدایت کند، هدایت‌یافته[ی واقعی] است و هر کس را که گمراه سازد، هرگز جز او [= الله] دوستانی برایشان نخواهی یافت؛ و آنان را در قیامت، نابینا و گنگ و ناشنوا محشور می‌گردانیم [در حالی که] بر صورت‌هایشان [کشیده می‌شوند]؛ جایگاهشان دوزخ است [که] هر گاه شعله‌اش فرونشیند، شراره‌ای [تازه] بر آنان مى‌افزاییم‌.');
INSERT INTO "islamhouse" VALUES(17,98,'17:98','این جزای آنان است؛ چرا كه آیات ما را انكار كردند و گفتند: «آیا هنگامی‌ که استخوان‌هایی [پوسیده و] پراکنده شدیم، دوباره با آفرینشی جدید‌ برانگیخته می‌شویم؟»');
INSERT INTO "islamhouse" VALUES(17,99,'17:99','آیا ندانستند الله كه آسمان‌ها و زمین را آفریده است، می‌تواند همانند آنان را بیافریند و اَجَلی برایشان تعیین کرده است كه هیچ تردیدی در آن نیست؟ و[لى‌] ستمکاران جز انكار [چیزى را] نپذیرفتند.');
INSERT INTO "islamhouse" VALUES(17,100,'17:100','[ای پیامبر، به مشرکان] بگو: «اگر شما مالک گنجینه‌های [بخشش و] رحمتِ پروردگارم بودید، [باز هم] از بیم خرج کردن [و تنگدستی] از انفاق خوداری می‌کردید؛ و انسان همواره بخیل [و تنگ‌نظر] است».');
INSERT INTO "islamhouse" VALUES(17,101,'17:101','و به راستی، ما نُه معجزۀ روشن [را به عنوان نشانه‌های پیامبری] به موسی دادیم؛ پس از بنی‌اسرائیل بپرس آنگاه که [موسی‌] نزدشان آمد و فرعون به او گفت: «ای موسی، یقیناً تو را افسون‌شده می‌پندارم».');
INSERT INTO "islamhouse" VALUES(17,102,'17:102','[موسی] گفت: «تو قطعاً مى‌دانى كه اینها [= معجزات] را [کسی] جز پروردگار آسمان‌ها و زمین نفرستاده است. [این معجزات،] دلایلی روشن [دربارۀ قدرت و یگانگی الله و راستگوییِ پیامبرش هستند]؛ و اى فرعون، به راستى که تو را هلاک‌شده [و زیان‌دیده] مى‌پندارم».');
INSERT INTO "islamhouse" VALUES(17,103,'17:103','[فرعون] خواست که آنان [= بنی‌اسرائیل] را از آن سرزمین [مصر] بیرون کند؛ و[لی ما] او و تمام کسانی را که همراهش بودند غرق کردیم.');
INSERT INTO "islamhouse" VALUES(17,104,'17:104','و پس از [نابودیِ] او به بنی‌اسرائیل گفتیم: «در سرزمین [شام] ساکن شوید و هنگامی‌ که وعدۀ آخرت فرارسید، همۀ شما را با هم [به صحرای محشر] می‌آوریم».');
INSERT INTO "islamhouse" VALUES(17,105,'17:105','و این [قرآن] را به‌حق نازل کردیم و به‌حق [و بدون هیچ تحریف و تغییری] نازل شد؛ و [ای محمد،] تو را جز بشارتگر و بیم‌دهنده نفرستادیم.');
INSERT INTO "islamhouse" VALUES(17,106,'17:106','و قرآنى [گرانقدر] را با بیان استوار [و به مرور زمان] نازل كردیم تا آن را با درنگ [و آهستگی] برای مردم بخوانى؛ و آن را به تدریج [و بر حسب رویدادهای مختلف] نازل كردیم.');
INSERT INTO "islamhouse" VALUES(17,107,'17:107','[ای پیامبر، به کافران] بگو: «[چه‌] به آن ایمان بیاورید یا ایمان نیاورید [فرقی به حالتان ندارد]. بی‌تردید، كسانى كه قبل از [نزول‌] آن، دانش [کتاب‌های آسمانیِ پیشین را] یافته‌اند، چون [آیات قرآن‌] بر آنان خوانده شود، سجده‌كنان بر [زمین] مى‌افتند».');
INSERT INTO "islamhouse" VALUES(17,108,'17:108','و می‌گویند: «پروردگارمان پاک و منزّه است. یقیناً وعدۀ پروردگارمان انجام‌شدنی است».');
INSERT INTO "islamhouse" VALUES(17,109,'17:109','و [آنگاه] گریه‌کنان بر [زمین] مى‌افتند و [تلاوتِ قرآن و تدبر در آیاتش] بر فروتنی‌شان می‌افزاید.');
INSERT INTO "islamhouse" VALUES(17,110,'17:110','بگو: «الله» را بخوانید یا «رحمان» را بخوانید؛ هر كدام را که بخوانید، نیكوترین نام‌ها براى اوست». و [ای پیامبر،] نمازت را [آنچنان] به صدای بلند نخوان [که مشرکان بشنوند] و آن را [آنقدر نیز] آهسته نخوان [که حتی اصحابت نشوند]؛ و میان این [دو حالت،] راهى [میانه‌] بجوى.');
INSERT INTO "islamhouse" VALUES(17,111,'17:111','و بگو: «ستایش مخصوص الله است که فرزندی [برای خویش] برنگزیده است و در فرمانروایی، هیچ شریکی ندارد و هرگز خوار و ناتوان نمی‌گردد که [نیاز به دوست و] کارساز داشته باشد» و او را بزرگ بدار؛ بزرگداشتی شایسته.');
INSERT INTO "islamhouse" VALUES(18,1,'18:1','حمد و سپاس مخصوص الله است که این کتاب [= قرآن] را بر بنده‌اش [محمد] نازل کرد و هیچ گونه انحرافی در آن قرار نداد.');
INSERT INTO "islamhouse" VALUES(18,2,'18:2','[کتابی] استوار تا [الله، کافران را] از عذابی سخت از جانبِ خود بیم دهد و به مؤمنانی که کارهای شایسته می‌کنند مژده دهد که پاداش نیکویی برایشان [در پیش] است.');
INSERT INTO "islamhouse" VALUES(18,3,'18:3','[همان بهشتی] که جاودانه در آن خواهند ماند.');
INSERT INTO "islamhouse" VALUES(18,4,'18:4','و [نیز] کسانی‌ را که گفتند: «الله فرزندی [برای خود] برگزیده است» بیم دهد.');
INSERT INTO "islamhouse" VALUES(18,5,'18:5','نه خودشان به این [ادعای باطل،] علم [و یقین‌] دارند نه پدرانشان. سخن بسیار ناپسندی است که از دهانشان بیرون می‌آید [و] جز دروغ نمی‌گویند.');
INSERT INTO "islamhouse" VALUES(18,6,'18:6','پس [ای پیامبر،] نزدیک است اگر [قومت] به این سخن [= قرآن] ایمان نیاورند، در پی [بی‌ایمانیِ] آنان، خود را [از تأسف و اندوه] هلاک کنی.');
INSERT INTO "islamhouse" VALUES(18,7,'18:7','در حقیقت، ما آنچه را که روی زمین است، زیوری برای آن قرار داده‌ایم تا آنان را بیازماییم که کدامشان نیکوکارترند.');
INSERT INTO "islamhouse" VALUES(18,8,'18:8','و ما [پس از پایان حیات،] آنچه را که روی زمین است، به بیابانی خشک و بی‌گیاه تبدیل خواهیم کرد.');
INSERT INTO "islamhouse" VALUES(18,9,'18:9','آیا پنداشته‌ای که [ماجرای] اصحاب کهف و آن سنگ‌نوشته [که نامشان بر آن حک شده بود]، از جمله نشانه‌های شگفت‌انگیز [و غیرممکنِ] ما بوده ‌است؟');
INSERT INTO "islamhouse" VALUES(18,10,'18:10','هنگامی که آن جوانمردان به غار پناه بردند و گفتند: «پروردگارا، رحمتی از جانب خویش بر ما ارزانی دار و در كارمان برای ما هدایت و تعالى فراهم ساز».');
INSERT INTO "islamhouse" VALUES(18,11,'18:11','در آن غار، تا سالیان دراز [پرده‌ای از سکوت و آرامش] بر گوش‌هایشان زدیم [تا به خواب روند]‌.');
INSERT INTO "islamhouse" VALUES(18,12,'18:12','سپس بیدارشان کردیم تا معلوم بداریم کدام یک از آن دو گروه مدتی را که [در خواب] مانده‌اند درست‌تر حساب کرده است.');
INSERT INTO "islamhouse" VALUES(18,13,'18:13','[ای پیامبر،] ما داستان آنان را به درستی برایت بازگو می‌کنیم. آنان جوانانی [شایسته] بودند که به پروردگارشان ایمان آورده بودند و ما [نیز] بر هدایتشان افزودیم.');
INSERT INTO "islamhouse" VALUES(18,14,'18:14','و دل‌هایشان را [بر ایمان] استوار ساختیم، آنگاه ‌که [در برابر پادشاه کافر] قیام کردند و گفتند: «پروردگارِ ما، پروردگار آسمان‌ها و زمین است؛ و ما هرگز جز او معبودی را عبادت نمی‌کنیم که آنگاه [اگر چنین کنیم،] سخنی گزاف و ناروا گفته‌ایم».');
INSERT INTO "islamhouse" VALUES(18,15,'18:15','[سپس به همفکرانشان گفتند:] «این قوم ما [که] به جای او [= الله] معبودانی [دیگر] برگزیده‌اند‌، چرا دلیل آشکاری بر [حقانیتِ] آنان نمی‌آورند؟ پس کیست ستمکار‌تر از آن کس که بر الله دروغ بندد [و برایش شریک قائل شود]؟');
INSERT INTO "islamhouse" VALUES(18,16,'18:16','و چون از آنان و از آنچه به جای الله عبادت می‌کنند کناره‌گیری کردید، به غار پناه ببرید تا پروردگارتان از [سایۀ] رحمت خویش بر شما بگسترانَد و در کارتان [آسایش و] گشایشی پدید آورَد.');
INSERT INTO "islamhouse" VALUES(18,17,'18:17','و [ای محمد، اگر آنجا بودی،] خورشید را می‌دیدی که وقتی طلوع می‌کند به سمت راست [غار‌] متمایل می‌گردد و وقتی غروب می‌کند، از سمت چپ آنان دور می‌شد [و گرما آزارشان نمی‌داد]؛ و آنان در محلی گسترده [و گشاده] از آن [غار] بودند. این، از نشانه‌های [قدرتِ] الله است. هر کس که الله او را هدایت کند، او هدایت‌یافته[ی واقعی] است و هر ‌کس را که گمراه کند، هرگز دوست [و] راهنمایی برایش نمی‌یابی.');
INSERT INTO "islamhouse" VALUES(18,18,'18:18','و [اگر به آنها نگاه می‌کردی،] می‌پنداشتی بیدارند؛ در حالی‌ که [با چشمانِ باز] خفته بودند؛ و ما آنان را به پهلوی راست و چپ می‌گرداندیم [تا بدنشان آسیب نبیند]؛ و سگشان بر آستانۀ غار [نشسته و به حالت پاسبانی] دست‌هایش را گشوده بود. اگر [به آنان نگاه می‌کردی و] از حالشان آگاه می‌شدی، قطعاً [از وحشت] فرار می‌کردی و دلت از ترس آنان آکنده می‌شد.');
INSERT INTO "islamhouse" VALUES(18,19,'18:19','و همان گونه [كه آنان را در خواب فرو بردیم] آنان را [از خواب] بیدار نمودیم تا بین خود از یکدیگر سوال کنند. یکی از آنان گفت: «چه مدت [در خواب] ماندید؟» [بقیه] گفتند: «یک روز یا بخشی از یک روز». [سرانجام] گفتند: «پروردگارتان داناتر است که چقدر [در این حالت] مانده‌اید. [اکنون] یک نفر از خودتان را با این سکه[‌های]تان به شهر بفرستید تا ببیند کدام‌ یک از [فروشندگانِ آنجا، کاسبی و] غذا‌یش پاکیزه‌تر است و از [نزد] او غذایی برایتان بیاورد؛ و باید [در رفت‌وآمد و خرید، پنهان‌کاری و] زیرکی نماید و هیچ کس را از [حالِ] شما آگاه نسازد.');
INSERT INTO "islamhouse" VALUES(18,20,'18:20','بی‌تردید، اگر آنان [از مکان شما آگاه شوند و] بر شما دست یابند، سنگسار‌تان می‌کنند یا شما را به آیین خویش بازمی‌گردانند و در آن صورت، هرگز رستگار نخواهید شد».');
INSERT INTO "islamhouse" VALUES(18,21,'18:21','و این‌گونه، [مردم آن سرزمین] را از حال آنان آگاه کردیم تا بدانند وعدۀ الله [دربارۀ یاریِ مؤمنان و برانگیختنِ مردگان] راست و درست است و اینکه در [فرارسیدنِ] قیامت تردیدی نیست. هنگامی ‌که [مردم شهر] بین خود [دربارۀ آن جوانمردان متوفی] دچار اختلاف شدند، گروهی گفتند: «دیواری بر [ورودیِ] آن [غار] بسازید. پروردگارشان به حال آنان آگاه‌تر است». برخی دیگر که [ناآگاه بودند و نفوذ و] قدرت داشتند گفتند: «[به نشانۀ بزرگداشت،] بر [غارِ] آنان عبادتگاهی می‌سازیم».');
INSERT INTO "islamhouse" VALUES(18,22,'18:22','[دربارۀ تعدادشان] خواهند گفت: «سه نفر بودند [و] چهارمینِ آنان سگشان بود» و [گروهی] می‌گویند: «پنج نفر بودند [و] ششمینِ آنان سگشان بود» ـ [که البته] همه از روی حدس و گمان است ـ و [گروهی] می‌گویند: «هفت نفر بودند و هشتمینِ آنان سگشان بود». بگو: «پروردگارم از [تعداد] آنان آگاه‌تر است [و] جز گروهی اندک، کسی از تعدادشان آگاه نیست. پس دربارۀ آنان گفتگویی مختصر [و بحثی آرام و گذرا] داشته باش و دربارۀ آنان از هیچ کس سؤال نکن.');
INSERT INTO "islamhouse" VALUES(18,23,'18:23','و هرگز در مورد کاری نگو: «فردا آن را انجام می‌دهم»،');
INSERT INTO "islamhouse" VALUES(18,24,'18:24','مگر اینکه [بگویی:] «ان شاء الله»؛ و هر گاه فراموش کردی، [با گفتن این لفظ] پروردگارت را یاد کن و بگو: «امیدوارم پروردگارم مرا به راهی رهنمون گردد که از این [راه]، به هدایت [و صلاح] نزدیک‌تر باشد».');
INSERT INTO "islamhouse" VALUES(18,25,'18:25','و آنان [= اصحاب کهف] سیصد سال در غار‌شان [در حالت خواب] ماندند و نُه سال [نیز] بر آن افزودند.');
INSERT INTO "islamhouse" VALUES(18,26,'18:26','[ای پیامبر،] بگو: «الله از [مدت] ماندنشان [در غار] آگاه‌تر است؛ غیب آسمان‌ها و زمین از آنِ اوست. [به راستی که] او تعالی چه بینا و چه شنواست! آنان [= جهانیان] هیچ [دوست و] کارسازی جز او ندارند و او هیچ کس را در حکم [و فرمان] خویش شریک نمی‌سازد».');
INSERT INTO "islamhouse" VALUES(18,27,'18:27','و [ای پیامبر،] از کتاب پروردگارت آنچه را به تو وحی شده است تلاوت کن. هیچ کس تغییردهندۀ سخنان او [= الله] نیست و هرگز پناهگاهی جز او نمی‌یابی.');
INSERT INTO "islamhouse" VALUES(18,28,'18:28','و خود را [بر همنشینی] با کسانی همراه [و شکیبا] گردان که بامداد و شامگاه پروردگارشان را می‌خوانند [و با آنکه فقیرند، تنها] رضایتِ او را می‌خواهند؛ و مبادا چشمانت از آنان [به سوی ثروتمندان] برگردد تا زیورهای دنیا [و همنشینی با اشراف و قدرتمندان] را بخواهی و [همچنین] از کسی پیروی نکن ‌که دلش را از یاد خویش غافل ساخته‌ایم و از هوای نفس خود پیروی کرده و کارش تباه است.');
INSERT INTO "islamhouse" VALUES(18,29,'18:29','[ای پیامبر، به مشرکان] بگو: «[این قرآن، سخنِ] حق از جانب پروردگارتان است؛ پس هر‌ کس می‌خواهد، ایمان بیاورد و هر کس می‌خواهد، کافر شود». ما برای ستمکاران آتشی آماده کرده‌ایم که سراپرده‌ها‌یش آنان را از هر سو فرامی‌گیرد؛ و اگر [در طلبِ آب،] فریادرسى بجویند، با آبی همچون مسِ گداخته به فریادشان می‌رسند؛ [آبی که حرارتش] چهره‌ها را بریان می‌کند. چه بد نوشیدنی و چه بد جایگاهی است!');
INSERT INTO "islamhouse" VALUES(18,30,'18:30','کسانی‌ که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، [به یقین بدانند که] ما پاداش کسی را که کار نیکو کرده باشد هرگز تباه نمی‌کنیم.');
INSERT INTO "islamhouse" VALUES(18,31,'18:31','آنان کسانی هستند که باغ‌های جاودانی [در بهشت] دارند که از زیر [قصرها] شان جویبارها جاری است. در آنجا با دستبندهای زرین آراسته می‌شوند و جامه‌های [فاخر] سبزرنگ از حریر نازک و حریر ضخیم بر تن می‌کنند، در حالی که بر تخت‌ها تکیه زده‌اند. چه پاداش شایسته و چه نیکو جایگاهی است!');
INSERT INTO "islamhouse" VALUES(18,32,'18:32','و [ای پیامبر،] مثالی برای آنان بزن: دو مرد [کافر و مؤمن] که برای یکی از آنان [که کافر است] دو باغ انگور قرار دادیم و پیرامونِ آن دو [باغ] را با درختان نخل پوشاندیم و میان آن، کشتزاری [پُربار] نهادیم.');
INSERT INTO "islamhouse" VALUES(18,33,'18:33','هر دو باغ، میوه خود را [فراوان] می‌داد و چیزی از آن نمی‌کاست؛ و میان آن دو [باغ] نهری بزرگ جاری کردیم.');
INSERT INTO "islamhouse" VALUES(18,34,'18:34','او [= صاحب باغ] میوه‌هایی [بسیار و درآمدی فراوان] داشت؛ پس به دوست خود‌ که با او گفتگو می‌کرد گفت: «ثروتِ من از تو بیشتر است و از لحاظ خانواده [و نفرات] از تو توانمندترم».');
INSERT INTO "islamhouse" VALUES(18,35,'18:35','و او در حالی به باغ خویش داخل شد ‌که [به خاطر کفر و غرور،] در حق خویش ستمکار بود؛ [پس رو به دوستش] گفت: «گمان نمی‌کنم که این [باغ] هرگز نابود گردد.');
INSERT INTO "islamhouse" VALUES(18,36,'18:36','و گمان نمی‌کنم که قیامت بر پا گردد؛ و اگر [قیامتی در کار باشد و] به سوی پروردگارم بازگردانده شوم، قطعاً جایگاهی بهتر از این می‌یابم».');
INSERT INTO "islamhouse" VALUES(18,37,'18:37','دوستش که با وی گفتگو می‌کرد به او گفت: «آیا به آن ذاتی‌ که تو را از خاک [و] سپس از نطفه آفرید و سپس تو را مردی [کامل] سامان داد کافر شده‌ای؟');
INSERT INTO "islamhouse" VALUES(18,38,'18:38','اما من [می‌گویم:] او الله پروردگارِ من است و هیچ کس را با پروردگارم شریک نمی‌دانم.');
INSERT INTO "islamhouse" VALUES(18,39,'18:39','وقتی وارد باغت شدی، چرا نگفتی هر چه الله بخواهد [همان می‌شود] و هیچ نیرویى جز به [تأیید] الله نیست؟ اگر می‌بینی من از نظر مال و فرزند از تو کمترم، [مهم نیست].');
INSERT INTO "islamhouse" VALUES(18,40,'18:40','چه بسا پروردگارم [چیزی] بهتر از باغ تو به من بدهد و از آسمان چنان عذابی [آتشبار] بر باغت فروبارد که به زمین صاف و بی‌گیاهِ لغزنده‌ای تبدیل شود.');
INSERT INTO "islamhouse" VALUES(18,41,'18:41','یا آبش در [اعماق زمین چنان] فرو رود که هرگز نتوانی به آن دست بیابی».');
INSERT INTO "islamhouse" VALUES(18,42,'18:42','[سرانجام، عذاب الهی فرارسید] و تمام میوه‌هایش را در بر گرفت [و نابود شد]. و او به خاطر هزینه‌هایی که صرف آنجا کرده بود، پیوسته دست‌هایش را [از پشیمانی و اندوه] بر هم می‌زد و در حالی‌ که داربستهایش فروریخته بود می‌گفت: «ای کاش [در عبادت،] کسی را شریک پروردگارم قرار نداده بودم».');
INSERT INTO "islamhouse" VALUES(18,43,'18:43','و او [که به خاندان خود می‌بالید، اینک] گروهی نداشت که در برابرِ [عذابِ] الله یاری‌اش کنند و خود [نیز] یاری‌کنندۀ خویش نبود.');
INSERT INTO "islamhouse" VALUES(18,44,'18:44','آنجا [ثابت شد که یاری و] کارسازی از آنِ [پروردگارِ] حق است. او پاداش [عبادتش] بهتر است و سرانجام [بندگان مؤمنش] نیکوتر.');
INSERT INTO "islamhouse" VALUES(18,45,'18:45','و [ای پیامبر، فناپذیریِ] زندگی دنیا را به آبی مَثل بزن که از آسمان فروفرستادیم و به وسیلۀ آن، گیاه زمین [سرسبز شد و] در هم ‌آمیخت؛ سپس [چنان‌] خشک و شكسته شد كه بادها [به هر سو] پراكنده‌اش مى‌گرداند؛ و الله بر هر چیزی تواناست.');
INSERT INTO "islamhouse" VALUES(18,46,'18:46','مال و فرزندان، زینت زندگی دنیاست و نیکی‌های پایدار [= رفتار و گفتار شایسته] نزد پروردگارت، پاداشش بهتر است و امیدداشتن [به ثوابش] نیکوتر.');
INSERT INTO "islamhouse" VALUES(18,47,'18:47','و [یاد کن از] روزی که کوه‌ها را به حرکت درمی‌آوریم و زمین را آشکار [و هموار] می‌بینی و همه [مخلوقات] را جمع می‌کنیم و هیچ‌یک از آنان را [به حال خود] رها نمی‌کنیم.');
INSERT INTO "islamhouse" VALUES(18,48,'18:48','و [همگی] صف‌کشیده بر پروردگارت عرضه می‌شوند [و به آنان می‌گوید:] «بی‌تردید، چنان که نخستین بار شما را آفریدیم، [امروز نیز تنها و پابرهنه] نزد ما آمدید؛ بلکه می‌پنداشتید که ما هرگز وعده‌گاهی برایتان قرار نمی‌دهیم».');
INSERT INTO "islamhouse" VALUES(18,49,'18:49','و نامه[ی اعمال در میان] نهاده می‌شود؛ آنگاه گناهکاران را می‌بینی که از آنچه در آن [نامه] است ترسان و نگرانند و می‌گویند: «ای وای بر ما! این چه نامه‌ای است که هیچ [گفتار و رفتارِ] کوچک و بزرگی را رها نکرده است، مگر اینکه آن را در شمار آورده است؟» و هر چه کرده‌اند حاضر می‌یابند و پروردگارت به هیچ کسی ستم نمی‌کند.');
INSERT INTO "islamhouse" VALUES(18,50,'18:50','و [یاد کن از] هنگامی که به فرشتگان گفتیم: «برای آدم سجده کنید»؛ آنگاه [همه] سجده کردند، مگر ابلیس ‌که از جن بود و از فرمان پروردگارش سرپیچید. پس [ای مردم،] آیا به جای من، او و فرزندانش را دوستان [خود] می‌گیرید، حال آنکه آنان دشمنتان هستند؟ [اطاعت از ابلیس و نسلش به جای اطاعت الله] چه جایگزینی بدی است برای ستمکاران!');
INSERT INTO "islamhouse" VALUES(18,51,'18:51','من آنان را [که مشرکانه به عبادت می‌گیرید] نه هنگام آفرینش آسمان و زمین به گواهی گرفتم و نه هنگام آفرینش خودشان؛ و من هرگز گمراه‌کنندگان را یار و مددکار [خویش] نگرفته‌ام.');
INSERT INTO "islamhouse" VALUES(18,52,'18:52','و [یاد کن از] روزی که الله [به مشرکان] می‌فرماید: «شریکانی را که برای من می‌پنداشتید، فرابخوانید [تا شما را از عذاب برهانند]؛ آنها را می‌خوانند، ولی پاسخشان را نمی‌دهند؛ و ما در میان این دو گروه، مهلکه‌ای [از آتشِ دوزخ] قرار داده‌ایم.');
INSERT INTO "islamhouse" VALUES(18,53,'18:53','و گناهکاران آتش [دوزخ] را می‌بینند و به یقین درمی‌یابند که در آن خواهند افتاد و هیچ راه بازگشتی از آنجا ندارند.');
INSERT INTO "islamhouse" VALUES(18,54,'18:54','و به راستی در این قرآن، برای مردم از هر گونه مثلی آوردیم؛ و[لی] انسان بیش از هر چیزی به ستیز [و چون‌وچرا] می‌پردازد.');
INSERT INTO "islamhouse" VALUES(18,55,'18:55','و چیزی مردم را بازنداشت که وقتی هدایت [= پیامبر و قرآن] به سویشان آمد ایمان بیاورند و از پروردگارشان آمرزش بخواهند، مگر اینکه [از روی لجاجت می‌خواستند] سنت [الله در مورد عذابِ] پیشینیان، بر [سرِ] آنان [نیز] بیاید یا عذاب رو در رو به آنان برسد.');
INSERT INTO "islamhouse" VALUES(18,56,'18:56','و ما پیامبران را جز بشارتگر و بیم‌دهنده نمی‌فرستیم و کسانی ‌که کفر ورزیدند، به [سخنان بیهوده و] باطل ستیز می‌کنند تا به وسیلۀ آن، حق [= قرآن] را پایمال کنند و [معجزات و] آیات من و آنچه را که به آن بیم داده شده‌اند، به تمسخر گرفتند.');
INSERT INTO "islamhouse" VALUES(18,57,'18:57','کیست ستمکار‌تر از آن کس که به آیات پروردگارش پند داده شود ولی از آن روی بگردانَد و کارهای [زشت] گذشته‌اش را فراموش کند؟ ما بر دل‌هایشان پرده‌هایی افکنده‌ایم تا [پیامِ حق] را درنیابند و در گوش‌هایشان سنگینی [نهاده‌ایم تا سخن حق را نشنوند]؛ و اگر آنان را به سوی هدایت بخوانی، هرگز هدایت نمی‌شوند.');
INSERT INTO "islamhouse" VALUES(18,58,'18:58','[ای پیامبر،] پروردگارت آمرزنده و دارای رحمت [فراوان] است. اگر آنان [= کافران] را به [سزاى] آنچه کرده‌اند بازخواست می‌كرد، قطعاً در عذابشان شتاب می‌نمود؛ [اما چنین نمی‌کند؛] بلکه آنان موعدی دارند که هرگز از آن گریزی نخواهند داشت.');
INSERT INTO "islamhouse" VALUES(18,59,'18:59','ما [ساکنانِ] آن شهر‌ها را هنگامی‌ هلاک نمودیم که [به خاطر کفر و گناه، به خود] ستم کردند و برای هلاکتشان موعدی تعیین کردیم.');
INSERT INTO "islamhouse" VALUES(18,60,'18:60','[یاد کن از] هنگامی که موسی به [خدمتکارِ] جوان خود [یوشَع بن نون] گفت: «همچنان خواهم رفت تا به محل بر‌خورد دو دریا برسم یا برای مدت طولانی راه بپیمایم [تا بندۀ درستکاری بیابم و نادانسته‌هایم را از او بیاموزم]».');
INSERT INTO "islamhouse" VALUES(18,61,'18:61','وقتی به محل برخورد دو دریا رسیدند، ماهیِ خود را [که برای خوردن همراه داشتند] فراموش کردند [و الله آن را زنده کرد و به آب انداخت] و ماهی، راهِ خود را در دریا [مانند تونلی] در پیش گرفت [و رفت].');
INSERT INTO "islamhouse" VALUES(18,62,'18:62','هنگامی‌ که [به راه افتادند و از آنجا] گذشتند، [موسی] به [خدمتکار] جوان خود گفت: «غذایمان را بیاور که از این سفر بسیار خسته شده‌ایم».');
INSERT INTO "islamhouse" VALUES(18,63,'18:63','[یوشع] گفت: «به یاد داری هنگامی ‌که [برای استراحت] به کنار آن تخته‌سنگ جای گرفتیم، من [در آنجا داستان زنده شدن] ماهی را فراموش کردم [که به تو بگویم] و جز شیطان، [کسی] مرا از یادآورىِ آن [داستان] به فراموشى نینداخت و ماهی به طرزی شگفت‌آور راهش را در دریا پیش گرفت».');
INSERT INTO "islamhouse" VALUES(18,64,'18:64','[موسی] گفت: «آن همان چیزی است که می‌خواستیم [و جایگاهِ آن بندۀ نیکوکار، همان‌جاست]». پس جستجوکنان ردّ پای خود را گرفتند [و از همان راه] بازگشتند.');
INSERT INTO "islamhouse" VALUES(18,65,'18:65','[در آنجا] بنده‌ای از بندگان ما [= خَضِر] را یافتند که از جانب خویش به او رحمتی داده بودیم و از نزد خود به وی دانشی [که این قصه آن را در بر دارد] آموخته بودیم.');
INSERT INTO "islamhouse" VALUES(18,66,'18:66','موسی به او گفت: «آیا [اجازه می‌دهی] همراهت بیایم تا از آنچه به تو آموخته شده و مایۀ هدایت و ارشاد است به من بیاموزی؟»');
INSERT INTO "islamhouse" VALUES(18,67,'18:67','[خَضِر] گفت: «تو هرگز نمی‌توانی [در اطاعت و همراهی] با من شکیبایی کنی.');
INSERT INTO "islamhouse" VALUES(18,68,'18:68','و چگونه بر [انجام] کاری که به [راز] آن آگاه نیستی شکیبایی می‌کنی؟»');
INSERT INTO "islamhouse" VALUES(18,69,'18:69','[موسی] گفت: «ان شاء الله شکیبا خواهم بود و در هیچ کاری از تو نافرمانی نمی‌کنم».');
INSERT INTO "islamhouse" VALUES(18,70,'18:70','[خَضِر] گفت: «پس اگر با من همراه شدی، دربارۀ هیچ چیز از من سؤال نکن تا خود دربارۀ آن سخنی با تو بگویم».');
INSERT INTO "islamhouse" VALUES(18,71,'18:71','آن دو به راه افتادند تا آنکه سوار کشتی شدند. [خَضِر] کشتی را سوراخ کرد. [موسی] گفت: «آن را سوراخ کردی تا سرنشینانش را غرق کنی؟ راستی چه کار ناروایی کردی!»');
INSERT INTO "islamhouse" VALUES(18,72,'18:72','[خَضِر] گفت: «آیا نگفتم تو هرگز نمی‌توانی با من شکیبایی کنی؟»');
INSERT INTO "islamhouse" VALUES(18,73,'18:73','[موسی] گفت: «به خاطر آنچه فراموش کردم سرزنشم نکن و در کارم بر من سخت نگیر».');
INSERT INTO "islamhouse" VALUES(18,74,'18:74','پس به راه افتادند تا هنگامی ‌که به نوجوانی رسیدند و [خَضِر] او را کشت. [موسی] گفت: «شخص [بی‌گناه و] پاکی را کُشتی [آن هم] بدون اینکه کسی را کشته باشد؟ واقعاً کار ناپسندی کردی!»');
INSERT INTO "islamhouse" VALUES(18,75,'18:75','[خَضِر] گفت: «به تو نگفتم که هرگز نمی‌توانی با من شکیبایی کنی؟»');
INSERT INTO "islamhouse" VALUES(18,76,'18:76','[موسی] گفت: «اگر از این پس چیزى از تو پرسیدم، دیگر با من همراهى نكن [و] از جانب من قطعاً معذور خواهى بود».');
INSERT INTO "islamhouse" VALUES(18,77,'18:77','پس به راه افتادند تا به اهالی شهری رسیدند [و] از آنان غذا خواستند؛ [اما آنان] از مهمان‌کردنشان خودداری کردند. سپس در آنجا دیواری یافتند که می‌خواست فرو بریزد و [خضر] آن را راست و استوار کرد. [موسی] گفت: «اگر می‌خواستی، [می‌توانستی] برای آن [کار، از آنان] مزدی بگیری».');
INSERT INTO "islamhouse" VALUES(18,78,'18:78','[خَضِر] گفت: «اینک زمان جدایی من و تو فرارسیده است. به زودی تو را از توضیح [و دلیل] آنچه که نتوانستی در برابرش شکیبایی کنی آگاه خواهم ساخت.');
INSERT INTO "islamhouse" VALUES(18,79,'18:79','اما آن کشتی، از آنِ بینوایانی بود که در دریا کار می‌کردند؛ خواستم معیوبش کنم؛ [زیرا] پیش راهشان [در دریا] پادشاهی [ستمکار] بود که هر کشتی [سالمی] را به زور می‌گرفت.');
INSERT INTO "islamhouse" VALUES(18,80,'18:80','و اما آن نوجوان، پدر و مادرش مؤمن بودند؛ پس ترسیدیم که آن دو را به سرکشی و کفر وادارد.');
INSERT INTO "islamhouse" VALUES(18,81,'18:81','از این رو، خواستیم که پروردگارشان به جای او [فرزند] پاک‌تر و مهربان‌تری به آنها عطا فرماید.');
INSERT INTO "islamhouse" VALUES(18,82,'18:82','و اما آن دیوار، از دو پسربچۀ یتیم در آن شهر بود و گنجی زیر آن بود که به آن دو تعلق داشت و پدرشان [نیز] مردی درستکار بود؛ پس پروردگارت خواست که [آن دو یتیم] به حد بلوغ برسند و گنج خویش را [از زیر آن دیوار] بیرون آورند. این رحمتی از جانب پروردگار تو بود و من این [کار‌ها] را خودسرانه نکردم. این بود توضیح [و دلیل] کارهایی که نتوانستی در برابرش شکیبایی کنی».');
INSERT INTO "islamhouse" VALUES(18,83,'18:83','و [ای پیامبر، مشرکان و یهود] دربارۀ «ذوالقَرنین» از تو می‌پرسند؛ بگو: «به زودی چیزی از سرگذشت او برایتان خواهم خواند».');
INSERT INTO "islamhouse" VALUES(18,84,'18:84','ما در زمین به او قدرت و حکومت دادیم و وسیلۀ [رسیدن به] هر چیزی را در اختیارش نهادیم.');
INSERT INTO "islamhouse" VALUES(18,85,'18:85','او [از این] وسایل [برای رسیدن به اهدافش] استفاده کرد.');
INSERT INTO "islamhouse" VALUES(18,86,'18:86','[روزی در زمین به راه افتاد] تا به محل غروب خورشید رسید. [چنین به نظرش رسید] که خورشید در چشمه‌ای گرم و گِل‌آلود غروب می‌کند و در [نزدیکیِ] آنجا قومی [کافر] یافت. [به او] گفتیم: «ای ذوالقرنین، یا [آنان را] مجازات می‌کنی یا در میانشان [رفتار] نیکویی پیش می‌گیری».');
INSERT INTO "islamhouse" VALUES(18,87,'18:87','[ذوالقرنین] گفت: «اما کسی‌ را که ستم کرده [و شرک ورزیده] است مجازات خواهیم کرد، سپس به سوی پروردگارش بازگردانده می‌شود، آنگاه او را به عذابی بسیار سخت مجازات خواهد کرد.');
INSERT INTO "islamhouse" VALUES(18,88,'18:88','و اما کسی که ایمان آورد و کار شایسته انجام دهد، [در آخرت،] نیکوترین پاداش [= بهشت] را خواهد داشت و با ملایمت [و مهربانی] با وی سخن خواهیم گفت».');
INSERT INTO "islamhouse" VALUES(18,89,'18:89','سپس [ذوالقرنین] از وسایل [خود] استفاده کرد [و به سوی مشرق رفت].');
INSERT INTO "islamhouse" VALUES(18,90,'18:90','تا آنکه به جایگاه برآمدن خورشید رسید. [در آنجا خورشید را] دید که بر مردمانی طلوع می‌کند که در برابر [نور و گرمای آفتاب] پوششی برایشان قرار نداده بودیم [نه سرپناهی، نه سایۀ درختی].');
INSERT INTO "islamhouse" VALUES(18,91,'18:91','[کار ذوالقرنین] اینچنین بود و ما به آنچه او در اختیار داشت [و انجام می‌داد] کاملاً آگاه بودیم.');
INSERT INTO "islamhouse" VALUES(18,92,'18:92','سپس [ذوالقرنین] از امکانات [خود] استفاده کرد [و به جایی بین شرق و غرب رفت].');
INSERT INTO "islamhouse" VALUES(18,93,'18:93','[او همچنان رفت] تا به [دره‌ای] میان دو کوه رسید و در کنار آن دو [کوه،] مردمی را یافت که سخنی [جز زبان خود را] نمی‌فهمیدند.');
INSERT INTO "islamhouse" VALUES(18,94,'18:94','[آنان] گفتند: «ای ذوالقرنین، یأجوج و مأجوج در این سرزمین [تبهکاری و] فساد می‌کنند. آیا [ممکن است] هزینه‌ای در اختیارت قرار دهیم تا میان ما و آنان سدی بسازی؟»');
INSERT INTO "islamhouse" VALUES(18,95,'18:95','او گفت: «آنچه [از قدرت و ثروت] که پروردگارم در آن به من توانایی داده است [از پیشنهاد مالی شما] بهتر است؛ پس مرا با نیرویى [انسانى‌] یارى كنید [تا] میان شما و آنان سدّى محکم استوار سازم.');
INSERT INTO "islamhouse" VALUES(18,96,'18:96','قطعات آهن برایم بیاورید [و روی هم بچینید]» تا وقتی ‌کاملاً میان دو کوه را برابر ساخت [و پوشاند. سپس] گفت: «[در اطراف دیوارۀ آهنین آتش بیفروزید و] در آن بدمید». [آنان چنین کردند] تا وقتی آن [قطعات آهن] را گداخته کرد؛ [سپس] گفت: «[اکنون] مسِ مُذاب برایم بیاورید تا روی آن [دیوار] بریزم».');
INSERT INTO "islamhouse" VALUES(18,97,'18:97','[یأجوج و مأجوج] دیگر نتوانستند از آن [سد] بالا روند و [حتی] نتوانستند سوراخی در آن ایجاد کنند.');
INSERT INTO "islamhouse" VALUES(18,98,'18:98','[آنگاه ذوالقرنین] گفت: «این [سد،] از رحمت پروردگار من است [و تا الله بخواهد باقی می‌مانَد]؛ اما هنگامی که وعدۀ پروردگارم فرارسد، آن را در هم می‌کوبد و وعدۀ پروردگارم حق است».');
INSERT INTO "islamhouse" VALUES(18,99,'18:99','و در آن روز [که جهان پایان می‌پذیرد] آنان را [چنان] رها می‌کنیم که [از فراوانیِ جمعیت،] در هم می‌آمیزند و [همین که] در صور دمیده ‌شد، همه را [در یک جا] جمع می‌کنیم.');
INSERT INTO "islamhouse" VALUES(18,100,'18:100','و آن روز، دوزخ را آشکارا بر کافران عرضه می‌داریم.');
INSERT INTO "islamhouse" VALUES(18,101,'18:101','[همان] کسانی‌ که چشمانشان از یاد من در پرده‌[ی غفلت] بود و توان شنیدن [آیات الهی را] نداشتند.');
INSERT INTO "islamhouse" VALUES(18,102,'18:102','آیا کسانی‌ که کفر ورزیدند، پنداشته‌اند كه [مى‌توانند] به جاى من، بندگانم را کارساز [و دوستِ خود] بگیرند؟ بی‌تردید، ما دوزخ را برای پذیرایی كافران آماده كرده‌ايم.');
INSERT INTO "islamhouse" VALUES(18,103,'18:103','[ای پیامبر،] بگو: «[می‌خواهید] شما را از زیانکارترین [مردم] در کارها آگاه سازم؟');
INSERT INTO "islamhouse" VALUES(18,104,'18:104','[آنان] کسانی هستند‌ که تلاششان در زندگی دنیا تباه گشته است و خود می‌پندارند که کار نیکو می‌کنند».');
INSERT INTO "islamhouse" VALUES(18,105,'18:105','آنان کسانی هستند که به آیات پروردگارشان و دیدارِ او [در آخرت] کافر شدند؛ پس کارهایشان تباه گشت و روز قیامت [هیچ قدر و] ارزشى برایشان قائل نمی‌شویم.');
INSERT INTO "islamhouse" VALUES(18,106,'18:106','اینچنین است که به خاطر کفری که ورزیده‌اند و آیات و پیامبرانِ مرا به تمسخر گرفته‌اند مجازات‌شان دوزخ است‌.');
INSERT INTO "islamhouse" VALUES(18,107,'18:107','بی‌تردید، کسانی‌ که ایمان آورده‌ و کار‌های شایسته انجام داده‌اند، باغ‌های [بهشت] برین، محلّ پذیرایی آنها خواهد بود.');
INSERT INTO "islamhouse" VALUES(18,108,'18:108','جاودانه در آن خواهند ماند و هرگز از آنجا به جای دیگری گرایش ندارند.');
INSERT INTO "islamhouse" VALUES(18,109,'18:109','[ای پیامبر،] بگو: «اگر دریا برای [نگارش] کلمات پروردگارم جوهر شود، پیش از آنکه کلمات پروردگارم پایان یابد، قطعاً دریا به پایان می‌رسد؛ هر چند [دریاهایی دیگر] همانند آن [دریا را نیز] به کمک [آن] بیاوریم».');
INSERT INTO "islamhouse" VALUES(18,110,'18:110','[ای پیامبر،] بگو: «من فقط بشری [عادی] همچون شما هستم. به من وحی می‌شود که تنها معبودتان، اللهِ یکتاست؛ پس هر‌ کس به دیدارِ پروردگارش [در آخرت] امید دارد، باید کار شایسته انجام دهد و هیچ کس را در عبادتِ پروردگارش شریک نسازد».');
INSERT INTO "islamhouse" VALUES(19,1,'19:1','کهیعص [= کاف. ها. یا. عین. صاد].');
INSERT INTO "islamhouse" VALUES(19,2,'19:2','[این] بیان رحمت پروردگارت [نسبت] به بنده‌اش زکریاست.');
INSERT INTO "islamhouse" VALUES(19,3,'19:3','آنگاه که پروردگارش را به ندایی پنهان [به دعا] خواند.');
INSERT INTO "islamhouse" VALUES(19,4,'19:4','گفت: «پروردگارا، به راستی که استخوانم سست شده و [موی] سرم از پیری سپید گشته و ای پروردگارم، [هرگز] در دعای تو [از اجابت] بی‌بهره نبوده‌ام.');
INSERT INTO "islamhouse" VALUES(19,5,'19:5','و من پس از [مرگ] خود از خویشاوندانم [نسبت به حفظِ دین تو] بیمناکم و همسرم [نیز] نازاست؛ پس به لطف خویش [فرزندی به عنوان وارث و] یاور به من عطا فرما.');
INSERT INTO "islamhouse" VALUES(19,6,'19:6','[پسری که] وارث من و وارث خاندانِ یعقوب باشد؛ و پروردگارا، او را [در علم و دین، انسانی] پسندیده گردان».');
INSERT INTO "islamhouse" VALUES(19,7,'19:7','[الله دعایش را اجابت کرد و فرمود:] «ای زکریا، ما تو را به [تولدِ] پسری بشارت می‌دهیم که نامش یحیی است [و] پیش از این، همنامی برایش قرار نداده‌ایم».');
INSERT INTO "islamhouse" VALUES(19,8,'19:8','[زکریا] گفت: «پروردگارا، چگونه پسری خواهم داشت در حالی ‌که همسرم نازاست و من [نیز] از شدت پیری، به ناتوانی رسیده‌ام؟»');
INSERT INTO "islamhouse" VALUES(19,9,'19:9','[فرشته] گفت: «این گونه است [که تو می‌گویی؛ ولی] پروردگارت فرموده است: "این [امر،] برای من آسان است و به راستی پیش از این [نیز] تو را آفریدم، در حالی ‌که چیزی نبودی"».');
INSERT INTO "islamhouse" VALUES(19,10,'19:10','[زکریا] گفت: «پروردگارا، نشانه‌ای [بر تحققِ این بشارت] برایم قرار بده». [الله] فرمود: «نشانۀ تو این است که سه شبانه [روز] در حالی‌ که سالم [و تندرست] هستی، با مردم سخن نمی‌گویی».');
INSERT INTO "islamhouse" VALUES(19,11,'19:11','او از محراب [= جایگاه عبادتش] به سوی قومش رفت و به آنان اشاره کرد که: «بامداد و شامگاه، [پروردگار را] به پاکی یاد کنید».');
INSERT INTO "islamhouse" VALUES(19,12,'19:12','[الله فرمود:] «ای یحیی، کتاب [تورات] را با قوّت [و جدیت] بگیر» و در کودکی به او دانش [و حکمت] دادیم.');
INSERT INTO "islamhouse" VALUES(19,13,'19:13','و از جانب خویش [به او] محبت و پاکی [از گناهان عطا کردیم] و او پرهیزگار بود.');
INSERT INTO "islamhouse" VALUES(19,14,'19:14','و نسبت به پدر و مادرش نیکوکار بود و سرکش [و] نافرمان نبود.');
INSERT INTO "islamhouse" VALUES(19,15,'19:15','و سلام [و امنیتِ الله] بر او باد، روزی‌ که متولد شد و روزی ‌که می‌میرد و روزی ‌که زنده برانگیخته می‌شود.');
INSERT INTO "islamhouse" VALUES(19,16,'19:16','و در [این] کتاب [= قرآن] از مریم یاد کن، آنگاه ‌که از خانواده‌اش کناره گرفت [و در] ناحیۀ شرقی [از آنان] مستقر شد.');
INSERT INTO "islamhouse" VALUES(19,17,'19:17','و میان خود و آنها پرده‌اى افكند [تا خلوتگاهِ عبادتش باشد]. آنگاه [ما] روحِ خود [= جبرئیل] را به سویش فرستادیم و به [شكل] انسانی خوش‌اندام بر وی نمایان گشت.');
INSERT INTO "islamhouse" VALUES(19,18,'19:18','[مریم] گفت: «من از [شرّ] تو، به [الله] رحمان پناه می‌برم. اگر پرهیزگاری [به من آسیب نرسان]».');
INSERT INTO "islamhouse" VALUES(19,19,'19:19','[جبرئیل] گفت: «یقیناً من فرستادۀ پروردگارت هستم تا پسرِ پاکیزه‌ای به تو بخشم».');
INSERT INTO "islamhouse" VALUES(19,20,'19:20','[مریم] گفت: «چگونه ممکن است پسری داشته باشم؛ حال آنكه دست هیچ انسانی به من نرسیده است و بدكار [نیز] نبوده‌ام؟»');
INSERT INTO "islamhouse" VALUES(19,21,'19:21','[جبرئیل] گفت: «[دستور‌] چنین است. پروردگارت فرمود: "این [کار] بر من آسان است و او را برای مردم نشانه‌ای [از قدرتِ خویش] قرار می‌دهیم و [نیز] رحمتی از سوی ماست؛ و این [شیوۀ تولد،] امری انجام‌یافته [و حتمی] است"».');
INSERT INTO "islamhouse" VALUES(19,22,'19:22','پس [مریم] او [= عیسی] را باردار شد. و با او به جایی دوردست [رفت و از مردم] کناره گرفت.');
INSERT INTO "islamhouse" VALUES(19,23,'19:23','درد زایمان او را به سمت یک تنۀ‌ نخل کشاند [و او در حالی که درد می‌کشید] گفت: «ای کاش پیش از این می‌مُردم و به کلی از یادها می‌رفتم [تا کسی درباره‌ام خیال باطل نکند]».');
INSERT INTO "islamhouse" VALUES(19,24,'19:24','ناگهان از زیر [پای] او، [جبرئیل] ندایش داد که: «غمگین مباش. پروردگارت زیر [پاى‌] تو چشمۀ آبى روان ساخته است.');
INSERT INTO "islamhouse" VALUES(19,25,'19:25','و تنۀ نخل را به طرف خود تکان بده [تا] خرمای تازه بر تو فرو‌ریزد.');
INSERT INTO "islamhouse" VALUES(19,26,'19:26','آنگاه [از آن خرما] بخور و [از آب نهر] بنوش و دیدگان را [به ‌این فرزند] روشن دار [و شاد باش]؛ و اگر هر یک از مردم را دیدی [با اشاره] بگو: "من برای [الله] رحمان روزه[ی سکوت] نذر کرده‌ام و امروز با هیچ انسانی سخن نخواهم گفت"».');
INSERT INTO "islamhouse" VALUES(19,27,'19:27','[مریم] در حالی‌ که او [= عیسی] را برداشته بود نزد قومش آمد. آنان گفتند: «ای مریم، به راستی دروغ بزرگی می‌گویی [که این نوزاد بدون پدر متولد شده است].');
INSERT INTO "islamhouse" VALUES(19,28,'19:28','ای خواهر هارون، نه پدرت مرد بدکاری بود و نه مادرت [زن] بدکاره‌ای».');
INSERT INTO "islamhouse" VALUES(19,29,'19:29','آنگاه مریم به او [= عیسی] اشاره کرد. آنان گفتند: «چگونه با ‌کودکی در گهواره سخن بگوییم؟»');
INSERT INTO "islamhouse" VALUES(19,30,'19:30','[عیسی به سخن آمد و] گفت: «من بندۀ الله هستم. او به من کتاب داده و مرا پیامبر ساخته است.');
INSERT INTO "islamhouse" VALUES(19,31,'19:31','و مرا ـ هر جا که باشم ـ پربرکت نموده و تا زنده‌ هستم مرا به نماز و زکات سفارش کرده است.');
INSERT INTO "islamhouse" VALUES(19,32,'19:32','و نسبت به مادرم نیکوکار [گردانده] و مرا [در برابر پروردگارم] سرکش [و] نافرمان قرار نداده است.');
INSERT INTO "islamhouse" VALUES(19,33,'19:33','و سلام بر من، روزی‌ که متولد شدم و روزی‌ که می‌میرم و روزی‌ که زنده برانگیخته می‌شوم».');
INSERT INTO "islamhouse" VALUES(19,34,'19:34','این است [حکایت] عیسی پسر مریم؛ [همان] گفتار راستی که [گمراهان] درباره‌اش تردید [و اختلاف] می‌کنند.');
INSERT INTO "islamhouse" VALUES(19,35,'19:35','[هرگز] سزاوار نیست که الله [برای خویش] فرزندی بگیرد. او [پاک و] منزّه است. [الله] هر گاه کاری را اراده کند، فقط به آن می‌گوید: «موجود شو»؛ آنگاه [بی‌درنگ] موجود می‌شود.');
INSERT INTO "islamhouse" VALUES(19,36,'19:36','و [عیسی به قومش گفت:] «بی‌تردید، الله پروردگار من و شماست؛ پس او را عبادت کنید [که] راهِ راست همین است».');
INSERT INTO "islamhouse" VALUES(19,37,'19:37','آنگاه گروه‌ها[ی مختلفِ قومش] در میان خود دچار اختلاف شدند؛ پس وای بر کسانی که کفر ورزیدند از مشاهدۀ روز بزرگ [قیامت]!');
INSERT INTO "islamhouse" VALUES(19,38,'19:38','آن روز ‌که نزد ما می‌آیند، چه [خوب] شنوا و بینا هستند! اما امروز [در زندگی دنیا] ستمکاران در گمراهیِ آشکارند.');
INSERT INTO "islamhouse" VALUES(19,39,'19:39','و [ای پیامبر،] به آنان دربارۀ روز [پشیمانی و] حسرت هشدار بده؛ آنگاه كه کار [داورى] انجام می‌گیرد؛ و [امروز‌] آنان در غفلتند و ایمان نمی‌آورند.');
INSERT INTO "islamhouse" VALUES(19,40,'19:40','یقیناً ما زمین و هر ‌کس را بر آن است به ارث می‌بریم و [همه] به سوی ما بازگردانده می‌شوند.');
INSERT INTO "islamhouse" VALUES(19,41,'19:41','و [ای پیامبر،] در این کتاب از ابراهیم یاد کن. بی‌تردید، او پیامبری بسیار راستگو بود.');
INSERT INTO "islamhouse" VALUES(19,42,'19:42','هنگامی‌ که به پدرش [آزر] گفت: «پدر جان، چرا چیزی را عبادت می‌کنی که نه [دعایت را] می‌شنود و نه [عبادتت را] می‌بیند و نه گزندی را از تو دور می‌سازد؟');
INSERT INTO "islamhouse" VALUES(19,43,'19:43','پدر جان، یقیناً از دانش [وحی] چیزی به من رسیده است که [هرگز] به تو نرسیده است؛ پس از من پیروی کن تا تو را به راهی راست هدایت کنم.');
INSERT INTO "islamhouse" VALUES(19,44,'19:44','پدر جان، از شیطان پیروی نکن [چرا که] بی‌تردید، شیطان نسبت به [الله] رحمان نافرمان بود.');
INSERT INTO "islamhouse" VALUES(19,45,'19:45','پدر جان، من می‌ترسم که از [جانب الله] رحمان عذابی به تو رسد و [در دوزخ] از دوستان [و همنشینانِ] شیطان باشی».');
INSERT INTO "islamhouse" VALUES(19,46,'19:46','[آزر] گفت: «ای ابراهیم، آیا از معبود‌های من رویگردانی؟ اگر [از عقیده‌ات] دست برنداری، قطعاً سنگسارت می‌کنم؛ [برو] و مدت زیادى از من دور شو».');
INSERT INTO "islamhouse" VALUES(19,47,'19:47','[ابراهیم] گفت: «سلام [و امنیت از جانب من] بر تو. به زودی از پروردگارم برایت آمرزش خواهم خواست. بی‌تردید، او نسبت به من بسیار مهربان است.');
INSERT INTO "islamhouse" VALUES(19,48,'19:48','و از شما [کافران] و آنچه به جای الله می‌خوانید کناره‌گیری می‌کنم و پروردگارم را می‌خوانم. امید است كه در خواندنِ پروردگارم [از اجابت] بى‌بهره نباشم».');
INSERT INTO "islamhouse" VALUES(19,49,'19:49','هنگامی که از آنان و آنچه به جای الله عبادت می‌کردند کناره‌گیری کرد، [پسرش] اسحاق و [نوه‌اش] یعقوب را به او بخشیدیم و همه [آنان] را پیامبر نمودیم.');
INSERT INTO "islamhouse" VALUES(19,50,'19:50','و ایشان را از رحمت خویش بهره‌مند ساختیم و [در میان مردم آوازۀ بلند و] نام نیکو برایشان نهادیم.');
INSERT INTO "islamhouse" VALUES(19,51,'19:51','و در این کتاب، از موسی یاد کن. بی‌تردید، او [برگزیده‌ای] مخلص و فرستاده‌ای پیامبر بود.');
INSERT INTO "islamhouse" VALUES(19,52,'19:52','و [ما] او [=موسى] را از كناره [كوه] طور و سمت راست [خودش] ندا دادیم و او را مناجات‌کنان [به خویش] نزدیک ساختیم.');
INSERT INTO "islamhouse" VALUES(19,53,'19:53','و از رحمت خود، برادرش هارون را به عنوان پیامبر به وی عطا کردیم.');
INSERT INTO "islamhouse" VALUES(19,54,'19:54','و در این کتاب، از اسماعیل یاد کن. بی‌تردید، او راست‌وعده و فرستاده‌ای پیامبر بود.');
INSERT INTO "islamhouse" VALUES(19,55,'19:55','و [همواره] خانواده‌اش را به نماز و زکات فرمان می‌داد و نزد پروردگارش پسندیده بود.');
INSERT INTO "islamhouse" VALUES(19,56,'19:56','و در این کتاب، از ادریس یاد کن. بی‌تردید، او راست‌گفتار پیامبری بود.');
INSERT INTO "islamhouse" VALUES(19,57,'19:57','و [ما] او را به مقامی بلند برکشیدیم.');
INSERT INTO "islamhouse" VALUES(19,58,'19:58','اینان کسانی از پیامبران هستند که الله نعمت [هدایت] به آنان داد؛ [افرادی] از فرزندان آدم و از كسانى كه همراه نوح [بر كشتى‌] سوار كردیم و از فرزندان ابراهیم و یعقوب و از [جمله] افرادی كه هدایت کردیم و برگزیدیم [و] هر گاه آیات [پرودگار‌] رحمان بر آنان خوانده مى‌شد، سجده‌كنان و گریان به خاک مى‌افتادند.');
INSERT INTO "islamhouse" VALUES(19,59,'19:59','پس از آنان، جانشینانى گمراه و ناشایست آمدند كه نماز را تباه ساختند [و در آن سهل‌انگارى نمودند] و از هوس‌ها [و شهوات] پیروى كردند؛ و به زودى [کیفرِ] گمراهى [خویش] را خواهند دید.');
INSERT INTO "islamhouse" VALUES(19,60,'19:60','مگر کسی که توبه نمود و ایمان آورد و کاری شایسته کرد، اینان به بهشت داخل می‌شوند و هیچ ستمی بر آنان نمی‌رود.');
INSERT INTO "islamhouse" VALUES(19,61,'19:61','باغ‌هایی جاودان که [الله] رحمان از [عالم] غیب [و نادیده] به بندگانش وعده داده است. یقیناً وعدۀ او آمدنی است.');
INSERT INTO "islamhouse" VALUES(19,62,'19:62','در آنجا سخن بیهوده‌ای نمی‌شنوند [و گفتارشان] جز سلام [و تحیت نیست]؛ و در آنجا برایشان [هر] صبح و شام روزی‌ [بی‌حساب فراهم] است.');
INSERT INTO "islamhouse" VALUES(19,63,'19:63','این همان بهشتی است که به هر كس از بندگانمان که [در دنیا] پرهیزگار باشد به میراث می‌دهیم.');
INSERT INTO "islamhouse" VALUES(19,64,'19:64','و [ای جبرئیل، به محمد بگو:‌] «[ما فرشتگان] جز به فرمان پروردگارت فرود نمی‌آییم. او حاکم بر آینده و گذشته و حالِ ماست؛ و پروردگارت فراموشکار نیست‌.');
INSERT INTO "islamhouse" VALUES(19,65,'19:65','[همان] پروردگار آسمان‌ها و زمین و آنچه میان آنهاست؛ پس او را عبادت کن و بر عبادتش شکیبا [و پایدار] باش. آیا [همانند و] همنامی برایش می‌شناسی؟»');
INSERT INTO "islamhouse" VALUES(19,66,'19:66','و انسان [کافر] می‌گوید: «آیا هنگامی که بمیرم، به راستی مرا زنده [از گور] بیرون خواهند ‌آورد؟»');
INSERT INTO "islamhouse" VALUES(19,67,'19:67','آیا انسان به یاد نمی‌آورد که ما پیش از این او را آفریدیم، در حالی ‌که هیچ نبود؟');
INSERT INTO "islamhouse" VALUES(19,68,'19:68','پس سوگند به پروردگارت که یقیناً آنان [= منکران قیامت] را با شیاطین محشور خواهیم کرد؛ سپس همه را به‌زانودرآمده گرداگرد دوزخ حاضر می‌کنیم.');
INSERT INTO "islamhouse" VALUES(19,69,'19:69','سپس از هر گروهی، کسانی را که در برابر [فرمان الله] رحمان سرکش‌تر بوده‌اند بیرون می‌کشیم.');
INSERT INTO "islamhouse" VALUES(19,70,'19:70','آنگاه یقیناً ما به [حال] کسانی ‌که برای [درافتادن به] آن [آتش مهیب] سزاوارترند، داناتریم.');
INSERT INTO "islamhouse" VALUES(19,71,'19:71','و هیچ یک از شما [مردم] نیست، مگر آنکه وارد آن می‌گردد؛ [مؤمنان برای عبور و دیدن، کافران برای ورود و ماندن]. این [امر،] همواره بر پروردگارت حکمی حتمی است.');
INSERT INTO "islamhouse" VALUES(19,72,'19:72','سپس كسانى را كه پرهیزگاری كرده‌اند [از آتش] نجات مى‌دهیم و ظالمان را به‌زانودرآمده در آن رها مى‌كنیم.');
INSERT INTO "islamhouse" VALUES(19,73,'19:73','و هنگامی‌ که آیات روشن ما بر آنان تلاوت می‌شود، کسانی ‌که کفر ورزیده‌اند به افرادی‌ که ایمان آورده‌اند می‌گویند: «كدام یک از [ما] دو گروه، جایگاهی بهتر و مجلسی نیکوتر دارد؟»');
INSERT INTO "islamhouse" VALUES(19,74,'19:74','چه بسیار نسل‌ها را پیش از آنان نابود کردیم که ثروتی بیشتر و ظاهری آراسته‌تر داشتند.');
INSERT INTO "islamhouse" VALUES(19,75,'19:75','[ای پیامبر،] بگو: «هر کس که در گمراهى است، [الله‌] رحمان به او مهلت مى‌دهد [تا گمراهی‌اش بیشتر شود] تا زمانی که وعدۀ الهی را [به چشم خود] ببینند: یا عذاب [دنیا] یا [کیفرِ] قیامت. پس به زودى خواهند دانست که چه کسی جایگاهش بدتر و سپاهش ناتوان‌تر است».');
INSERT INTO "islamhouse" VALUES(19,76,'19:76','و [اما] کسانی ‌که راه‌یافته‌اند، الله بر هدایتشان می‌افزاید؛ و نیکی‌های پایدار [= رفتار و گفتار شایسته] در پیشگاه پروردگارت، پاداشی بهتر و سرانجامی نیکوتر دارد.');
INSERT INTO "islamhouse" VALUES(19,77,'19:77','آیا دیدی آن کسی را که به آیات ما کافر شد و گفت: «قطعاً مال و فرزند [فراوانی در آخرت] به من داده خواهد شد»؟');
INSERT INTO "islamhouse" VALUES(19,78,'19:78','آیا او بر غیب آگاهی یافته یا از [الله] رحمان پیمانی گرفته است؟');
INSERT INTO "islamhouse" VALUES(19,79,'19:79','[هرگز] چنین نیست؛ گفته‌اش را می‌نویسیم و بر عذابش می‌افزاییم‌.');
INSERT INTO "islamhouse" VALUES(19,80,'19:80','و آنچه [از اموال و فرزندان] که می‌گوید از او به ارث می‌بریم و [روز قیامت، نیازمند و] تنها نزد ما می‌آید.');
INSERT INTO "islamhouse" VALUES(19,81,'19:81','و [مشرکان] به جای الله معبودانی برگزیده‌اند تا [یاور و] پشتیبانشان باشند.');
INSERT INTO "islamhouse" VALUES(19,82,'19:82','چنین نیست؛ [بلکه آن معبودها در قیامت] عبادت آنان را انکار خواهند کرد و دشمنشان خواهند بود.');
INSERT INTO "islamhouse" VALUES(19,83,'19:83','[ای پیامبر،] آیا ندیدی كه ما شیاطین را بر كافران فرستادیم تا آنان را [با وسوسه‌های خود] تحریک كنند [و از راهِ الله بازدارند]؟');
INSERT INTO "islamhouse" VALUES(19,84,'19:84','پس دربارۀ [نابودیِ] آنان شتاب نکن. جز این نیست که ما [روزهای مرگ و فرارسیدنِ عذاب را] به دقت برایشان می‌شماریم.');
INSERT INTO "islamhouse" VALUES(19,85,'19:85','[یاد كن از‌] روزى كه پرهیزگاران را [با عزت و احترام] به مهمانی [الله] رحمان گِرد می‌آوریم.');
INSERT INTO "islamhouse" VALUES(19,86,'19:86','و مجرمان را تشنه‌کام به سوی جهنم می‌رانیم.');
INSERT INTO "islamhouse" VALUES(19,87,'19:87','آنان هرگز [توان] شفاعت ندارند؛ مگر کسی ‌که [با ایمان و عمل شایسته‌اش،] از [الله] رحمان پیمانی گرفته باشد.');
INSERT INTO "islamhouse" VALUES(19,88,'19:88','و [مشرکان] گفتند: «[الله] رحمان، فرزندی [برای خویش] برگزیده است».');
INSERT INTO "islamhouse" VALUES(19,89,'19:89','به راستی [با این ادعا] چیزی بسیار زشت [به میان] آوردید!');
INSERT INTO "islamhouse" VALUES(19,90,'19:90','از این [سخن کفرآمیز،] نزدیک است آسمان‌ها از هم بپاشد و زمین بشکافد و کوه‌ها در هم بشکنند و فروریزند.');
INSERT INTO "islamhouse" VALUES(19,91,'19:91','[چرا] که برای [الله] رحمان فرزندی ادعا کرده‌اند.');
INSERT INTO "islamhouse" VALUES(19,92,'19:92','و هرگز سزاوار [الله] رحمان نیست که فرزندی برگزیند.');
INSERT INTO "islamhouse" VALUES(19,93,'19:93','در آسمان‌ها و زمین هیچ كسى نیست، مگر آنكه [در روز قیامت، خاکسار و] بنده‌وار نزد الله رحمان مى‌آید.');
INSERT INTO "islamhouse" VALUES(19,94,'19:94','یقیناً [الله همۀ] آنان را در شمار آورده و درست برشمرده است.');
INSERT INTO "islamhouse" VALUES(19,95,'19:95','و همگی‌شان روز قیامت، تنها [و تهیدست] نزد او حاضر می‌شوند.');
INSERT INTO "islamhouse" VALUES(19,96,'19:96','کسانی‌ که ایمان آورده‌اند و کارهای شایسته کرده‌اند یقیناً [الله] رحمان [در دل‌های بندگانش] محبتی برایشان خواهد نهاد.');
INSERT INTO "islamhouse" VALUES(19,97,'19:97','[ای پیامبر،] بی‌تردید، ما این [قرآن] را بر زبان تو آسان نمودیم تا پرهیزگاران را با آن بشارت دهی و ستیزه‌گران [سرسخت] را بیم دهی.');
INSERT INTO "islamhouse" VALUES(19,98,'19:98','و چه بسیار نسل‌های گذشته را پیش از آنان نابود کردیم. آیا [وجود] هیچ‌یک از آنان را احساس می‌کنی یا کمترین صدایی از آنان می‌شنوی؟');
INSERT INTO "islamhouse" VALUES(20,1,'20:1','طه [= طا. ها].');
INSERT INTO "islamhouse" VALUES(20,2,'20:2','[ای پیامبر، ما] قرآن را بر تو نازل نکردیم تا در رنج بیفتی.');
INSERT INTO "islamhouse" VALUES(20,3,'20:3','[آن را نفرستادیم] مگر آنکه پندی باشد برای هر کس که [از عذاب الهی] می‌ترسد.');
INSERT INTO "islamhouse" VALUES(20,4,'20:4','[این قرآن،] فروفرستاده‌ای است از جانب آن ذاتی که زمین و آسمان‌های بلند را آفریده است.');
INSERT INTO "islamhouse" VALUES(20,5,'20:5','[پروردگار] رحمان [آن گونه كه شايسته جلال و مقام اوست] بر عرش قرار گرفت.');
INSERT INTO "islamhouse" VALUES(20,6,'20:6','آنچه در آسمان‌ها و زمین و آنچه میان آنهاست و آنچه زیر خاک [پنهان] است [همگی] از آنِ اوست.');
INSERT INTO "islamhouse" VALUES(20,7,'20:7','و اگر سخن آشکار بگویی [یا آن را پنهان داری،] یقیناً او [راز] نهان و [حتی] نهان‌تر [از آن] را نیز می‌داند.');
INSERT INTO "islamhouse" VALUES(20,8,'20:8','الله است که هیچ معبودی [به‌حق] جز او نیست [و] نیکوترین نام‌ها از آنِ اوست.');
INSERT INTO "islamhouse" VALUES(20,9,'20:9','[ای پیامبر،] آیا خبر موسی به تو رسیده است؟');
INSERT INTO "islamhouse" VALUES(20,10,'20:10','آنگاه‌ که آتشی را [از دور] دید و به خانواده‌ا‌ش گفت: «[لحظه‌ای] درنگ کنید [که] من آتشی دیده‌ام. شاید شعله‌ای از آن برایتان بیاورم یا کنار آن آتش راهنمایی بیابم».');
INSERT INTO "islamhouse" VALUES(20,11,'20:11','وقتی نزد آن [آتش] آمد، نِدا داده شد که: «ای موسی،');
INSERT INTO "islamhouse" VALUES(20,12,'20:12','به راستی، من پروردگارت هستم؛ پس [برای مناجات با پروردگار] کفش‌هایت را درآور [که] در حقیقت، تو در سرزمین مقدس «طُوی» هستی.');
INSERT INTO "islamhouse" VALUES(20,13,'20:13','و من تو را [به پیامبری] برگزیدم؛ پس به آنچه وحی می‌شود گوش فرا دار:');
INSERT INTO "islamhouse" VALUES(20,14,'20:14','بی‌تردید، منم «الله» که هیچ معبودی [به‌حق] جز من نیست؛ پس مرا عبادت کن و برای یاد من نماز برپا دار.');
INSERT INTO "islamhouse" VALUES(20,15,'20:15','یقیناً قیامت آمدنی است؛ [ولی] می‌خواهم [وقت] آن را پنهان دارم تا به هر کسی در برابر [کار و] تلاشی که می‌کند پاداش داده شود.');
INSERT INTO "islamhouse" VALUES(20,16,'20:16','پس مبادا کسی ‌که به آن ایمان ندارد و از هوس‌هایش پیروی می‌کند، تو را از [نیکوکاری و آمادگی برای] آن بازدارد که هلاک می‌شوی.');
INSERT INTO "islamhouse" VALUES(20,17,'20:17','و ای موسی، آن چیست به [دست] راستت؟»');
INSERT INTO "islamhouse" VALUES(20,18,'20:18','او گفت: «این عصای من است؛ بر آن تکیه می‌کنم و با آن برای گوسفندانم [برگ درختان را] فرومی‌ریزم و کارهای دیگری [نیز] با آن دارم [که برآورده می‌کنم].');
INSERT INTO "islamhouse" VALUES(20,19,'20:19','[الله] فرمود: «ای موسی، آن را بیفکن».');
INSERT INTO "islamhouse" VALUES(20,20,'20:20','[موسی] آن را [بر زمین] افکند و ناگهان [تبدیل به] ماری شد که به سرعت می‌خزید.');
INSERT INTO "islamhouse" VALUES(20,21,'20:21','[الله] فرمود: «آن را بگیر و نترس. به زودی آن را به شکل اولش بازمی‌گردانیم.');
INSERT INTO "islamhouse" VALUES(20,22,'20:22','و دستت را در بغل كن تا بدون ‌هیچ گزندى [به عنوان‌] معجزه‌اى دیگر، سفید [و درخشان‌] بیرون آید.');
INSERT INTO "islamhouse" VALUES(20,23,'20:23','[هدف از ارایه این دو معجزه، این بود] که برخی از نشانه‌های بزرگ خویش را به تو نشان دهیم.');
INSERT INTO "islamhouse" VALUES(20,24,'20:24','[اینک] به سوی فرعون برو [که] بی‌تردید، [کفر ورزیده و] سرکشی کرده است».');
INSERT INTO "islamhouse" VALUES(20,25,'20:25','[موسی] گفت: «پروردگارا، بر [حوصله و] شکیبایی‌ام بیفزای');
INSERT INTO "islamhouse" VALUES(20,26,'20:26','و کارم را برایم آسان گردان');
INSERT INTO "islamhouse" VALUES(20,27,'20:27','و گره از زبانم باز کن');
INSERT INTO "islamhouse" VALUES(20,28,'20:28','تا سخنم را دریابند.');
INSERT INTO "islamhouse" VALUES(20,29,'20:29','و یاوری از [افراد] خاندانم برای من قرار بده؛');
INSERT INTO "islamhouse" VALUES(20,30,'20:30','هارون، برادرم را.');
INSERT INTO "islamhouse" VALUES(20,31,'20:31','پشتم را با او محکم کن؛');
INSERT INTO "islamhouse" VALUES(20,32,'20:32','و او را در کار [رسالت] من شریک کن');
INSERT INTO "islamhouse" VALUES(20,33,'20:33','تا تو را بسیار به پاكى بستاییم');
INSERT INTO "islamhouse" VALUES(20,34,'20:34','و بسیار یادت کنیم.');
INSERT INTO "islamhouse" VALUES(20,35,'20:35','بی‌تردید، تو به [حال] ما بینایی».');
INSERT INTO "islamhouse" VALUES(20,36,'20:36','[الله] فرمود: «ای موسی، خواسته‌ات به تو داده شد.');
INSERT INTO "islamhouse" VALUES(20,37,'20:37','در حقیقت، ما [پیشتر] یک بار دیگر [نیز] بر تو منت نهاده بودیم.');
INSERT INTO "islamhouse" VALUES(20,38,'20:38','آنگاه كه آنچه را كه [باید دربارۀ حفاظت از تو] الهام مى‌شد به مادرت الهام كردیم:');
INSERT INTO "islamhouse" VALUES(20,39,'20:39','که: «او را در صندوقی بگذار و آن [صندوق] را به دریا [= رود نیل‌] بینداز تا دریا او را به ساحل بیفکنَد [و] دشمن من و دشمن او، آن را [از آب] بگیرد»؛ و از جانب خویش مهرى [در دل‌ها] بر تو افكندم و [تو را حمایت کردم] تا تحت نظرم پرورش یابى.');
INSERT INTO "islamhouse" VALUES(20,40,'20:40','آنگاه که خواهرت [در پی صندوقِ تو] راه می‌رفت و [به کسانی که تو را از آب گرفتند] ‌گفت: «می‌خواهید شخصی را به شما نشان دهم که سرپرستی‌ او را به عهده بگیرد؟» پس تو را به مادرت بازگرداندیم تا چشمش [به دیدارت] روشن گردد و اندوهگین نباشد؛ و [بعدها] تو شخصی [قِبطی] را کُشتی و ما از اندوه [و مجازاتِ قتل] نجاتت دادیم و تو را بار‌ها آزمودیم. پس [از آن،] چند سال بین مردم مَدیَن به سر بردی؛ آنگاه ای موسی، بر [طبق] تقدیر [الهی و در زمان مقرر به اینجا] آمدی.');
INSERT INTO "islamhouse" VALUES(20,41,'20:41','و تو را برای خودم برگزیدم [تا پیامم را به مردم برسانی].');
INSERT INTO "islamhouse" VALUES(20,42,'20:42','[اینک] تو و برادرت با نشانه‌های [قدرت و یگانگیِ] من روانه شوید و در [دعوت حق و] یاد کردنِ من سستی نورزید.');
INSERT INTO "islamhouse" VALUES(20,43,'20:43','به سوی فرعون بروید که او [کفر ورزیده و] سرکشی کرده است.');
INSERT INTO "islamhouse" VALUES(20,44,'20:44','پس به نرمی با او سخن بگویید؛ باشد که پند گیرد یا [از عذاب الهی] بترسد [و توبه کند].');
INSERT INTO "islamhouse" VALUES(20,45,'20:45','[موسی و هارون] گفتند: «پروردگارا، ما بیمناکیم که بر ما پیشدستی نماید [و پیش از پایان دعوت، مجازاتمان کند] یا [در برابر سخن حق] سرکشی کند».');
INSERT INTO "islamhouse" VALUES(20,46,'20:46','[الله] فرمود: «نترسید [که] یقیناً من همراهتان هستم [و همه چیز را] می‌شنوم و می‌بینم.');
INSERT INTO "islamhouse" VALUES(20,47,'20:47','پس به سوی او بروید و بگویید: «ما رسولان پروردگارت هستیم. بنی‌اسرائیل را همراه ما بفرست و آنان را [تحقیر و] آزار نکن. به راستی که ما نشانه‌های روشنی از جانب پروردگارت برای تو آورده‌ایم؛ و سلامتی [از عذاب الهی] بر کسی است که از هدایت پیروی کند.');
INSERT INTO "islamhouse" VALUES(20,48,'20:48','در حقیقت، به ما وحی شده است که عذاب [دوزخ] بر کسی است که [آیات و معجزات را] تکذیب کند و [از دعوت پیامبرش] روی گرداند».');
INSERT INTO "islamhouse" VALUES(20,49,'20:49','[فرعون] گفت: «ای موسی، پروردگار شما کیست؟»');
INSERT INTO "islamhouse" VALUES(20,50,'20:50','[موسی] گفت: «پروردگار ما [همان] ذاتی است که به هر چیزی آفرینشِ [= ظاهر و ویژگی‌های مناسب] او را بخشیده و سپس [آن را] هدایت نموده است».');
INSERT INTO "islamhouse" VALUES(20,51,'20:51','[فرعون] گفت: «پس حال [و سرنوشت] نسل‌های گذشته [که کافر بوده‌اند] چیست؟»');
INSERT INTO "islamhouse" VALUES(20,52,'20:52','[موسی] گفت: «دانشِ آن در کتابی [= لوح محفوظ] نزد پروردگار من [ثبت] است. پروردگارم نه اشتباه می‌کند و نه فراموش می‌نماید.');
INSERT INTO "islamhouse" VALUES(20,53,'20:53','آن [معبودی] که زمین را برایتان [همچون] گهواره‌ای [محل آسایش] قرار داد و راه‌هایی در آن ایجاد کرد و از آسمان آبی فروفرستاد و به وسیلۀ آن، انواع گوناگون گیاهان را [از زمین] بیرون آوردیم.');
INSERT INTO "islamhouse" VALUES(20,54,'20:54','[هم خود از آن] بخورید و [هم] چهارپایانتان را [در آن] بچرانید. بی‌تردید، در این [امور،] نشانه‌هایی [از قدرت الهی] برای خردمندان است.');
INSERT INTO "islamhouse" VALUES(20,55,'20:55','شما را از آن [خاک] آفریدیم و به آن بازمی‌گردانیم و بار دیگر [در قیامت] از آن بیرون می‌آوریم.');
INSERT INTO "islamhouse" VALUES(20,56,'20:56','و به راستی [ما] همۀ نشانه‌های [قدرت] خود [و صداقت رسالت موسی] را به او نشان دادیم؛ ولی [همه را] دروغ پنداشت و [دعوت حق را] نپذیرفت.');
INSERT INTO "islamhouse" VALUES(20,57,'20:57','[آنگاه] گفت: «ای موسی، آیا نزد ما آمده‌ای که با جادویَت ما را از سرزمینمان بیرون کنی؟');
INSERT INTO "islamhouse" VALUES(20,58,'20:58','یقیناً ما نیز جادویی مانند آن برایت می‌آوریم؛ پس در مکانی که [فاصله‌اش از هر دو طرف] یکسان باشد، وعده‌گاهی میان ما و خود تعیین کن که نه ما از آن خلاف کنیم و نه تو.');
INSERT INTO "islamhouse" VALUES(20,59,'20:59','[موسی] گفت: «وعده‌گاه شما روز عید باشد که [همۀ] مردم پیش از ظهر جمع می‌شوند».');
INSERT INTO "islamhouse" VALUES(20,60,'20:60','آنگاه فرعون بازگشت و [تمام] نیرنگ و حیلۀ خویش را جمع کرد و [همراه جادوگرانش به محل مورد نظر] آمد.');
INSERT INTO "islamhouse" VALUES(20,61,'20:61','موسی به آنان گفت: «وای بر شما! بر الله دروغ نبندید که با عذابی [سخت] نابودتان می‌سازد؛ و یقیناً هر ‌کس [با فریب‌دادنِ مردم بر الله] دروغ بست، زیانکار گشت».');
INSERT INTO "islamhouse" VALUES(20,62,'20:62','[جادوگران پس از هشدار موسی‌] در کار‌شان میان خود اختلاف ورزیدند و پنهانی گفتگو کردند.');
INSERT INTO "islamhouse" VALUES(20,63,'20:63','[برخی از آنان] گفتند: «این دو [نفر] قطعاً جادوگرند [و] با جادوی خویش می‌خواهند شما را از سرزمینتان بیرون کنند و آیین [و راه و رسمِ] برتر شما را از میان بردارند.');
INSERT INTO "islamhouse" VALUES(20,64,'20:64','پس [همۀ] نیرنگ خویش را فراهم کنید [و به کار بندید]؛ سپس در یک صف [برای مبارزه] به پیش آیید. بی‌تردید، هرکس امروز [بر دشمن] چیره شود، پیروز [و کامیاب] گشته است.');
INSERT INTO "islamhouse" VALUES(20,65,'20:65','[جادوگران] گفتند: «اى موسى، آیا تو [آنچه را با خود داری] مى‌افكنى یا [ما] نخستین كسی باشیم كه مى‌افکنَد؟»');
INSERT INTO "islamhouse" VALUES(20,66,'20:66','[موسی] گفت: «[نه،] بلکه، شما بیفکنید». ناگاه ریسمان‌ها و چوبدستی‌های آنان از جادویشان چنان به نظرش رسید كه گویی به شتاب مى‌خزند.');
INSERT INTO "islamhouse" VALUES(20,67,'20:67','پس موسى در دلش ترسی احساس كرد.');
INSERT INTO "islamhouse" VALUES(20,68,'20:68','گفتیم: «نترس [که] تو قطعاً [پیروز و] برتری.');
INSERT INTO "islamhouse" VALUES(20,69,'20:69','و آنچه را که در دست راست خود داری بیفکن تا [تمام] آنچه را که ساخته‌اند ببلعد. بی‌تردید، آنچه آنان ساخته‌اند، فقط نیرنگِ جادوگر است و جادوگر هر جا که باشد [و هر چه کند]، پیروز نمی‌گردد».');
INSERT INTO "islamhouse" VALUES(20,70,'20:70','[هنگامی‌که موسی عصایش را بر زمین انداخت، به مار بزرگی تبدیل شد و ساخته‌های آنان را بلعید] آنگاه جادوگران به سجده افتادند [و] گفتند: «به پروردگار هارون و موسی ایمان آوردیم».');
INSERT INTO "islamhouse" VALUES(20,71,'20:71','[فرعون] گفت: «آیا پیش از آنکه به شما اجازه دهم به او ایمان آوردید؟ قطعاً او بزرگِ شماست كه به شما جادوگری آموخته است. پس قطعاً دست و پاى شما را بر عكس یكدیگر [از چپ و راست] قطع مى‌كنم و همگی‌تان را از تنه‌هاى نخل به دار مى‌آویزم و آنگاه خوب خواهید دانست که عذاب [و شکنجۀ] کدام ‌یک از ما سخت‌تر و پاینده‌تر است؛ [من یا پروردگارِ موسی]».');
INSERT INTO "islamhouse" VALUES(20,72,'20:72','[جادوگران] گفتند: «هرگز [پیروی از] تو را بر این دلایل روشنی که برایمان آمده است و [همچنین] بر آن که ما را آفریده است ترجیح نمی‌دهیم؛ پس به هر حکمی که می‌خواهی، حکم کن. تو فقط می‌توانی در زندگیِ این دنیا حکم کنی.');
INSERT INTO "islamhouse" VALUES(20,73,'20:73','بی‌تردید، ما به پروردگارمان ایمان آوردیم تا گناهانمان و آنچه را از جادوگری بر ما تحمیل کردی ببخشاید؛ و الله بهتر و پاینده‌تر است».');
INSERT INTO "islamhouse" VALUES(20,74,'20:74','هرکس در پیشگاهِ پروردگارش، گناهکار حاضر شود، یقیناً آتش دوزخ برای اوست؛ در آنجا نه می‌میرد [که رهایی یابد] و نه [به خوشى‌] زندگى كند.');
INSERT INTO "islamhouse" VALUES(20,75,'20:75','و هرکس مؤمن نزد او تعالی بیاید در حالی که کارهای شایسته انجام داده باشد، اینان درجات عالی دارند.');
INSERT INTO "islamhouse" VALUES(20,76,'20:76','باغ‌های جاودان [بهشت] که جویبارها زیر [درختان] آن جاری است. همیشه در آن خواهند بود؛ و این است پاداش کسی ‌که خود را [از آلودگی‌های شرک و گناه] پاک کرده است.');
INSERT INTO "islamhouse" VALUES(20,77,'20:77','و به راستی [ما] به موسی وحی کردیم که: «بندگانم را شبانه [از مصر بیرون] ببر، سپس در دریا راهی خشک برایشان بگشا، که نه از تعقیب [دشمنان] خواهی ترسید و نه [از غرق شدن در دریا] بیم داری.');
INSERT INTO "islamhouse" VALUES(20,78,'20:78','آنگاه فرعون با سپاهیانش آنان را دنبال کرد و آب دریا آنان را فروپوشاند؛ چنان پوشاندنی [که حقیقتش را کسی جز الله نمی‌داند].');
INSERT INTO "islamhouse" VALUES(20,79,'20:79','و [بدین‌سان‌] فرعون قوم خود را گمراه نمود و هدایت نکرد.');
INSERT INTO "islamhouse" VALUES(20,80,'20:80','ای بنی‌اسرائیل، به راستی [ما] شما را از [چنگ] دشمنانتان نجات دادیم و در سمت راست [کوه] طور با شما وعده گذاشتیم [تا تورات را نازل کنیم] و بر شما تَرَنجَبین و بلدرچین فروفرستادیم.');
INSERT INTO "islamhouse" VALUES(20,81,'20:81','از نعمت‌های پاکیزه‌ای که به شما روزی داده‌ایم بخورید و در آن زیاده‌روی نکنید که خشم من بر شما فرود می‌آید؛ و هرکس که خشم من بر او فرود ‌آید، قطعاً هلاک می‌گردد.');
INSERT INTO "islamhouse" VALUES(20,82,'20:82','و یقیناً من آمرزندۀ كسى هستم كه توبه كند و ایمان آورد و كاری شایسته کند و آنگاه بر هدایت [خود باقى] بماند.');
INSERT INTO "islamhouse" VALUES(20,83,'20:83','ای موسی، چه چیز موجب شد که جلوتر از قومت [به سوی وعده‌گاهِ طور‌] بشتابى؟');
INSERT INTO "islamhouse" VALUES(20,84,'20:84','[موسی] گفت: «پروردگارا، آنان به دنبالم می‌آیند و من به سویت شتافتم تا [از من] راضی گردی».');
INSERT INTO "islamhouse" VALUES(20,85,'20:85','[الله] فرمود: «پس [بدان که] ما قومت را پس از [رفتنِ] تو آزمودیم و سامری گمراهشان کرد».');
INSERT INTO "islamhouse" VALUES(20,86,'20:86','آنگاه موسی خشمگین [و] اندوهناک به سوی قومش بازگشت [و] گفت: «ای قوم من، مگر پروردگارتان [دربارۀ نزول تورات] وعدۀ نیکو به شما نداده بود؟ آیا بر پیمان شما [با الله] زمانی طولانی گذشت [و همه را از یاد بردید] یا خواستید خشمی از [سوی] پروردگارتان بر شما فرود آید که وعدۀ مرا خلاف کردید؟»');
INSERT INTO "islamhouse" VALUES(20,87,'20:87','آنان گفتند: «[ما] به اختیار خود پیمان تو را نشکستیم؛ ولی بارهای سنگینی از زر و زیورهای قوم [فرعون] را که بر ما نهاده بودند [در آتش] انداختیم و سامری نیز [آنچه را که ادعا می‌کرد خاکِ سُمِ اسب جبرئیل است، در آتش] انداخت».');
INSERT INTO "islamhouse" VALUES(20,88,'20:88','سپس مجسمۀ گوساله‌ای برایشان ساخت که صدایی [همچون صدای گوساله] داشت. [سامری و پیروانش به مردم] گفتند: «این معبود شما و معبود موسی است که آن را فراموش [و اینجا رها] کرده است».');
INSERT INTO "islamhouse" VALUES(20,89,'20:89','آیا نمی‌دیدند که [این گوساله] هیچ پاسخی به آنها نمی‌دهد و هیچ زیان و سودی برایشان ندارد؟');
INSERT INTO "islamhouse" VALUES(20,90,'20:90','و در حقیقت، پیش از آن [که موسی بازگردد،] هارون نیز به آنها گفته بود: «اى قوم من، جز این نیست كه شما با این [گوساله] امتحان شده‌اید و بى‌تردید، پروردگار [حقیقىِ] شما [الله] رحمان است؛ پس پیرو من باشید و از فرمانم اطاعت كنید».');
INSERT INTO "islamhouse" VALUES(20,91,'20:91','[آنان] گفتند: «ما پیوسته بر این [عبادت گوساله] مى‌مانیم تا موسى نزدمان بازگردد».');
INSERT INTO "islamhouse" VALUES(20,92,'20:92','[وقتی موسی بازآمد] گفت: «ای هارون، هنگامی ‌که دیدی آنان گمراه شده‌اند، چه چیزی تو را بازداشت');
INSERT INTO "islamhouse" VALUES(20,93,'20:93','از پیرویِ من [و چرا آنان را ترک نکردی]؟ آیا از فرمانم سرپیچی کردی؟»');
INSERT INTO "islamhouse" VALUES(20,94,'20:94','[هارون] گفت: «ای پسر مادرم، ریش و [موی] سرم را نگیر. در حقیقت، ترسیدم که بگویی میان بنی‌اسرائیل تفرقه ‌انداختی و سخن [و سفارش] مرا به کار نبستی».');
INSERT INTO "islamhouse" VALUES(20,95,'20:95','[موسی] گفت: «ای سامری، این چه کاری است که کردی؟»');
INSERT INTO "islamhouse" VALUES(20,96,'20:96','او گفت: «من چیزی را دیدم که آنان ندیدند و مُشتی از خاک پای [اسبِ] فرستاده [= جبرئیل‌] را برداشتم و [در پیكر گوساله‌] انداختم و [هوای] نفسم [این کار را] اینچنین برایم آراست».');
INSERT INTO "islamhouse" VALUES(20,97,'20:97','[موسی] گفت: «پس برو. بی‌تردید، بهرۀ تو در زندگى این است كه [چنان مطرود شوی که به مردم] بگویى: "[به من‌] دست نزنید"؛ و وعده‌گاهی [برای عذاب] خواهی داشت كه هرگز از آن تخلف نخواهد شد؛ و [اکنون‌] به آن معبودی كه پیوسته در عبادتش بودی بنگر؛ آن را مى‌سوزانیم، آنگاه [که کاملاً خاكستر شد،] در دریا پراکنده‌اش می‌سازیم».');
INSERT INTO "islamhouse" VALUES(20,98,'20:98','[ای مردم،] معبود شما الله است که هیچ معبودی [به‌حق] جز او نیست و علمش همه چیز را فراگرفته است.');
INSERT INTO "islamhouse" VALUES(20,99,'20:99','[ای پیامبر،] این گونه از اخبار [و داستان‌های پیامبران] گذشته بر تو حکایت می‌کنیم؛ و به راستی که [ما] از جانب خویش به تو قرآنی [پندآموز] داده‌ایم.');
INSERT INTO "islamhouse" VALUES(20,100,'20:100','هر‌کس از آن رویگردان شود، یقیناً روز قیامت بارِ سنگینی [از گناه] بر دوش خواهد کشید.');
INSERT INTO "islamhouse" VALUES(20,101,'20:101','جاودانه در آن [عذاب] خواهند ماند؛ و چه بد است باری که [کافران] در قیامت خواهند کشید!');
INSERT INTO "islamhouse" VALUES(20,102,'20:102','روزی ‌که در «صور» دمیده می‌شود و در آن روز، مجرمان را [که بسیار ترسیده‌اند، با چشم و بدنِ] کبود جمع می‌کنیم.');
INSERT INTO "islamhouse" VALUES(20,103,'20:103','بین خود آهسته سخن می‌گویند: «[شما] بیش از ده روز [در دنیا] به سر نبرده‌اید».');
INSERT INTO "islamhouse" VALUES(20,104,'20:104','ما به آنچه آنان می‌گویند داناتریم؛ آنگاه که خردمندترینشان می‌گوید: «[شما] تنها یک روز به سر برده‌اید».');
INSERT INTO "islamhouse" VALUES(20,105,'20:105','و [ای پیامبر،] از تو دربارۀ کوه‌ها [در قیامت] می‌پرسند؛ بگو: «پروردگارم آنان را سخت متلاشی [و پراکنده] می‌کند؛');
INSERT INTO "islamhouse" VALUES(20,106,'20:106','و آنها را صاف و هموار خواهد ‌نمود؛');
INSERT INTO "islamhouse" VALUES(20,107,'20:107','[همچون زمینی] که در آن هیچ پستی و بلندی‌ای نمی‌بینی».');
INSERT INTO "islamhouse" VALUES(20,108,'20:108','در آن روز، [همۀ مردم‌] بدون هیچ مخالفتی از دعوت‌کنندۀ [الهى‌] پیروی می‌کنند و صداها در مقابل [الله] رحمان به خاموشی می‌گراید و جز صدایی آهسته [و زیر لب،‌ چیزى] نمى‌شنوى.');
INSERT INTO "islamhouse" VALUES(20,109,'20:109','در آن روز، شفاعت [هیچ کس] سودی ندارد، مگر کسی ‌که [الله] رحمان به او اجازه دهد و سخنش را بپسندد.');
INSERT INTO "islamhouse" VALUES(20,110,'20:110','[الله] آنچه را که انسان‌ها [در آخرت] در پیش دارند و آنچه را که [در دنیا] پشت سر گذاشته‌اند [همه را] مى‌داند؛ ولی دانش آنان او را فرانمی‌گیرد.');
INSERT INTO "islamhouse" VALUES(20,111,'20:111','و چهره‌ها در برابر [پروردگارِ] زندۀ پایدار، خوار می‌گردد و هرکس [بارِ] ستم برداشت، زیان دید.');
INSERT INTO "islamhouse" VALUES(20,112,'20:112','و کسی که از كارهاى شایسته انجام دهد، و مؤمن باشد، نه از هیچ ستمى مى‌هراسد و نه از كاستن [پاداش اعمال نیکش‌].');
INSERT INTO "islamhouse" VALUES(20,113,'20:113','و اینچنین آن را قرآنی [به زبان] عربی نازل کردیم و هشدارهای گوناگون در آن بیان نمودیم؛ باشد که [از عذاب الهی] بپرهیزند یا [این کتاب] برایشان پندی پدید آورَد.');
INSERT INTO "islamhouse" VALUES(20,114,'20:114','پس بلند‌مرتبه است الله که فرمانروای حق است. و در [خواندنِ‌] قرآن ـ پیش از آنكه وحىِ آن بر تو پایان یابد ـ شتاب مكن؛ و بگو: «پروردگارا، بر دانشم بیفزاى».');
INSERT INTO "islamhouse" VALUES(20,115,'20:115','به راستی، پیش از این با آدم پیمان بستیم [که از میوۀ درخت ممنوعه نخورَد]؛ ولی [پیمانش را] فراموش کرد و عزمی [استوار] در او نیافتیم.');
INSERT INTO "islamhouse" VALUES(20,116,'20:116','و [یاد کن از] هنگامی که به فرشتگان گفتیم: «برای آدم سجده کنید»؛ پس [همگی] سجده کردند، مگر ابلیس که سرپیچی نمود.');
INSERT INTO "islamhouse" VALUES(20,117,'20:117','گفتیم: «ای آدم، این [ابلیس] دشمنی برای تو و همسرت است؛ مبادا شما را از بهشت بیرون کند که به سختی می‌افتی.');
INSERT INTO "islamhouse" VALUES(20,118,'20:118','در حقیقت، تو این [نعمت را در بهشت] داری که نه گرسنه می‌شوی و نه برهنه می‌مانی.');
INSERT INTO "islamhouse" VALUES(20,119,'20:119','و اینكه در آنجا نه تشنه مى‌شوى و نه آفتاب‌زده.');
INSERT INTO "islamhouse" VALUES(20,120,'20:120','اما شیطان او را وسوسه کرد [و] گفت: «ای آدم، آیا [می‌خواهی] درخت جاودانگی و فرمانرواییِ فناناپذیر را به تو نشان دهم؟»');
INSERT INTO "islamhouse" VALUES(20,121,'20:121','آنگاه از آن [درخت ممنوعه] خوردند و شرمگاهشان بر آنان نمایان شد و به قرار‌دادن برگ [درختان] بهشت بر خود شروع کردند [تا عورتشان را بپوشانند]؛ و [اینچنین بود که] آدم از پروردگارش سرپیچی كرد و به بیراهه رفت.');
INSERT INTO "islamhouse" VALUES(20,122,'20:122','سپس پروردگارش او را برگزید و توبه‌اش را پذیرفت و هدایتش کرد.');
INSERT INTO "islamhouse" VALUES(20,123,'20:123','فرمود: «[شما دو تن همراه ابلیس] همگی از بهشت فرود آیید، در حالی‌ که دشمن یکدیگرید. اگر از [جانب] من رهنمودی برایتان رسید، [بدانید] هرکس از هدایتم پیروی کند، نه گمراه می‌شود و نه [از عذابِ دوزخ] به رنج می‌افتد.');
INSERT INTO "islamhouse" VALUES(20,124,'20:124','و کسی‌ که از یاد من رویگردان شود، یقیناً زندگی سختی خواهد داشت و روز قیامت او را نابینا برمی‌انگیزیم.');
INSERT INTO "islamhouse" VALUES(20,125,'20:125','وی می‌گوید: «پروردگارا، چرا مرا نابینا برانگیختی؛ حال آنکه [در دنیا] بینا بودم؟»');
INSERT INTO "islamhouse" VALUES(20,126,'20:126','[الله] می‌فرماید: «همان طور كه آیات ما بر تو آمد و آنها را به فراموشى سپردى، امروز تو [نیز] به همان صورت فراموش مى‌شوى».');
INSERT INTO "islamhouse" VALUES(20,127,'20:127','و کسی را که [در گمراهی و گناه] زیاده‌روی کند و به آیات پروردگارش ایمان نیاورد، این گونه کیفر می‌دهیم؛ و قطعاً عذاب آخرت شدیدتر و پایدارتر است.');
INSERT INTO "islamhouse" VALUES(20,128,'20:128','آیا براى هدایتشان كافى نیست كه [بدانند] چه نسل‌هایی را پیش از آنان نابود كردیم كه [اکنون این مشرکان] در خانه‌های آنان [= نسل‌های گذشته] راه مى‌روند؟ به راستى در این [نکته،] نشانه‌هایى [عبرت‌انگیز] برای خردمندان است.');
INSERT INTO "islamhouse" VALUES(20,129,'20:129','و اگر از سوی پروردگارت سخنى [دربارۀ اتمام حجت با مردم] نگذشته بود و سررسیدى معین در كار نبود، قطعاً [عذاب الهی در همین دنیا بر آنان] لازم مى‌شد.');
INSERT INTO "islamhouse" VALUES(20,130,'20:130','پس [ای پیامبر،] بر آنچه می‌گویند شکیبا باش و پروردگارت را پیش از طلوع آفتاب [در نماز صبح] و پیش از غروب [در نماز عصر] به پاکی ستایش کن و ساعاتی از شب [در نماز عشاء] و کناره‌های روز [در نماز ظهر و مغرب نیز به نیایش بپرداز]؛ باشد که [از پاداش الهی] راضی شوی.');
INSERT INTO "islamhouse" VALUES(20,131,'20:131','و هرگز به [نعمت‌های دنیوی و] آنچه که گروه‌هایی از آنان [= مشرکان] را از آنها بهره‌مند ساخته‌ایم چشم ندوز. اینها [فقط] زینتِ زندگی دنیاست تا آنان را به آن بیازماییم؛ و روزیِ پروردگارت بهتر و پایدارتر است.');
INSERT INTO "islamhouse" VALUES(20,132,'20:132','و خانواده‌ات را به نماز فرمان بده و [خود] بر [انجام] آن شکیبا باش. [ما] از تو روزی نمی‌خواهیم، [بلکه] خود به تو روزی می‌دهیم؛ و سرانجامِ نیک، برای [اهل] پرهیز است.');
INSERT INTO "islamhouse" VALUES(20,133,'20:133','[مشرکان] گفتند: «چرا [محمد] معجزه‌ای از جانب پروردگارش برای ما نمی‌آورد؟» بگو: «آیا [قرآن به عنوان] دلیل روشن [و تصدیق‌کننده] آنچه در کتاب‌های پیشین است برایشان نیامده است؟»');
INSERT INTO "islamhouse" VALUES(20,134,'20:134','اگر ما آنان را پیش از آن [که پیامبر و کتاب آسمانی بیاید] با عذابی هلاک می‌کردیم، قطعاً [روز قیامت مشرکان] می‌گفتند: «پروردگارا، چرا پیامبری به سویمان نفرستادی تا پیش از آنکه خوار و رسوا شویم از آیات تو پیروی کنیم؟»');
INSERT INTO "islamhouse" VALUES(20,135,'20:135','[ای پیامبر، به آنان] بگو: «همه منتظرند؛ پس شما [نیز] منتظر باشید. به زودی خواهید دانست که رهروانِ راه راست چه کسانی هستند و هدایت‌یافته کیست».');
INSERT INTO "islamhouse" VALUES(21,1,'21:1','[زمان] حساب مردم نزدیک شده است و آنان در ناآگاهی [از بازخواستِ آخرت] رویگردانند.');
INSERT INTO "islamhouse" VALUES(21,2,'21:2','هیچ پند تازه‌ای از جانب پروردگارشان برای آنان نمی‌آید، مگر آنکه بازی‌کنان به آن گوش می‌دهند.');
INSERT INTO "islamhouse" VALUES(21,3,'21:3','[پیام الهی را در حالی می‌شنوند که] دل‌هایشان غافل است؛ و کسانی ‌که [کافر و] ستمکار بودند، نجواهایشان را پنهان کردند [و گفتند:] «آیا این [مدعى پیامبرى،] جز بشرى همچون شماست؟ آیا در حالی ‌که [حقیقت را] می‌بینید، به جادو روی می‌آورید؟»');
INSERT INTO "islamhouse" VALUES(21,4,'21:4','[پیامبر] گفت: «پروردگارم هر سخنی را که در آسمان و زمین باشد می‌داند و او شنوای داناست».');
INSERT INTO "islamhouse" VALUES(21,5,'21:5','بلکه [کافران در ستیز با قرآن] گفتند: «[آنچه محمد می‌گوید] خواب‌هایی آشفته است؛ بلکه آن را به دروغ [به الله] نسبت می‌دهد؛ [نه،] بلکه او شاعر است. پس [اگر راست می‌گوید،] معجزه‌ای برایمان بیاورد، چنان که پیامبران پیشین [نیز با معجزات] فرستاده شدند».');
INSERT INTO "islamhouse" VALUES(21,6,'21:6','پیش از آنان [نیز ساکنان هر] شهری که نابودش می‌کردیم [به آیات و معجزاتِ ما] ایمان نیاورده بودند. پس آیا [اینها] ایمان می‌آورند؟');
INSERT INTO "islamhouse" VALUES(21,7,'21:7','و [ای پیامبر،] پیش از تو جز مردانی که به آنان وحی می‌کردیم [برای هدایت مردم] نفرستادیم؛ پس [ای کافران،] اگر نمی‌دانید، از اهل کتاب بپرسید.');
INSERT INTO "islamhouse" VALUES(21,8,'21:8','و [ما] آنان را پیکرهایی [مخالف سرشت و طبیعت بشری] که غذا نخورند قرار ندادیم و [در دنیا نیز] جاودان نبودند.');
INSERT INTO "islamhouse" VALUES(21,9,'21:9','سپس وعده‌ای را که [دربارۀ پیروزی و نجات] به آنان داده بودیم تحقق بخشیدیم و آنان را به همراه هرکس که خواستیم نجات دادیم و اسرافکاران [کافر] را نابود کردیم.');
INSERT INTO "islamhouse" VALUES(21,10,'21:10','بی‌تردید، کتابی به سویتان نازل کردیم که عزت [و شرف دنیا و آخرتِ] شما در آن است. آیا نمی‌اندیشید؟');
INSERT INTO "islamhouse" VALUES(21,11,'21:11','چه بسیار شهرهایی را که [ساکنانش] ستمکار بودند در هم شکستیم و پس از آنان گروهی دیگر پدید آوردیم.');
INSERT INTO "islamhouse" VALUES(21,12,'21:12','هنگامی‌که عذاب ما را احساس می‌کردند [و به چشم می‌دیدند،] ناگاه از آنجا می‌گریختند.');
INSERT INTO "islamhouse" VALUES(21,13,'21:13','[به تمسخر گفتیم:] «فرار نکنید. به سوی خانه‌هایتان و نعمت‌هایی که در آن آسوده بودید بازگردید؛ باشد که [فقط دربارۀ امور دنیوی] بازخواست شوید».');
INSERT INTO "islamhouse" VALUES(21,14,'21:14','گفتند: «وای بر ما [که] قطعاً ستمکار بوده‌ایم!»');
INSERT INTO "islamhouse" VALUES(21,15,'21:15','پس گفتارشان پیوسته همین بود تا آنکه آنان را [چون گیاهی] درو شده [و] بی‌جان ساختیم.');
INSERT INTO "islamhouse" VALUES(21,16,'21:16','و ما آسمان و زمین و آنچه را که میان آنهاست به بازیچه نیافریده‌ایم.');
INSERT INTO "islamhouse" VALUES(21,17,'21:17','اگر [به فرض] می‌خواستیم که سرگرمی‌ای [مانند فرزند یا هم‌صحبت برای خود] برگزینیم، آن را [نه از معبود‌های شما بلکه] از نزد خویش برمی‌گزیدیم.');
INSERT INTO "islamhouse" VALUES(21,18,'21:18','بلکه ما حق را بر باطل می‌افکنیم؛ که آن را در هم می‌شکند و ناگاه آن [باطل] نابود می‌شود؛ و [ای کافران،] وای بر شما از آنچه توصیف می‌کنید!');
INSERT INTO "islamhouse" VALUES(21,19,'21:19','هر کس [و هر چه] در آسمان‌ها و زمین است از آنِ اوست و کسانی که نزد او هستند [= فرشتگان] از عبادتش سرکشی نمی‌کنند و خسته نمی‌شوند.');
INSERT INTO "islamhouse" VALUES(21,20,'21:20','آنان شب و روز [الله را] به پاکی می‌ستایند و سستی نمی‌ورزند.');
INSERT INTO "islamhouse" VALUES(21,21,'21:21','آیا [این مشرکان] معبودانی از زمین برگزیده‌اند که [مردگان را] زنده می‌کنند؟');
INSERT INTO "islamhouse" VALUES(21,22,'21:22','اگر در آنها [= آسمان و زمین] معبودانی جز الله وجود داشت، [در اثر ناسازگاریِ آنها] قطعاً هر دو تباه مى‌شدند؛ پس الله ـ پروردگار عرش ـ از توصیفی که [مشرکان] می‌کنند پاک و منزه است.');
INSERT INTO "islamhouse" VALUES(21,23,'21:23','[الله] به خاطر آنچه می‌کند بازخواست نمی‌شود؛ [ولی] آنان [در برابر رفتارشان] مسئولند.');
INSERT INTO "islamhouse" VALUES(21,24,'21:24','آیا [مشرکان] معبودانی جز او برگزیده‌اند؟ [به آنان] بگو: «دلیلتان را بیاورید. این [قرآن] کتابی است که همراه من است و [اینها نیز] کتاب‌هایی است [متعلق به پیامبرانی] که پیش از من بودند؛ [و در هیچ کدام دلیلی بر حقانیتِ شما وجود ندارد]»؛ بلکه بیشتر آنان [به جهل و تقلید استناد می‌کنند و] حق را نمی‌دانند و [از آن] رویگردانند.');
INSERT INTO "islamhouse" VALUES(21,25,'21:25','و ما پیش از تو هیچ پیامبری را نفرستادیم، مگر آنکه به او وحی کردیم که [به مردم بگوید]: «معبودی [به‌حق] جز من [= الله] نیست؛ پس تنها مرا عبادت کنید».');
INSERT INTO "islamhouse" VALUES(21,26,'21:26','[مشرکان با این تصور که فرشتگان دخترانِ الله هستند] گفتند: «[الله] رحمان فرزندی [برای خویش] برگزیده است». او منزّه است؛ [چنین نیست که می‌پندارند؛] بلکه [فرشتگان] بندگان گرامی‌[اش] هستند.');
INSERT INTO "islamhouse" VALUES(21,27,'21:27','آنان هرگز در سخن بر او پیشی نمی‌گیرند و [پیوسته] به فرمانش عمل می‌کنند.');
INSERT INTO "islamhouse" VALUES(21,28,'21:28','[الله] گذشته و آیندۀ آنان را مى‌داند و آنان جز براى كسى كه [الله] بپسندد [و از او راضی باشد] شفاعت نمى‌كنند و خود از بیم او هراسانند.');
INSERT INTO "islamhouse" VALUES(21,29,'21:29','و هر یک از آنان بگوید: «جز او، من [نیز] معبود هستم»، وی را به دوزخ كیفر مى‌دهیم. [آرى‌،] ستمكاران را اینچنین کیفر مى‌دهیم.');
INSERT INTO "islamhouse" VALUES(21,30,'21:30','آیا کسانی که کفر ورزیدند، ندانسته‌اند که آسمان‌ها و زمین به هم چسبیده بودند؛ [نه بارانی می‌بارید و نه گیاهی می‌رویید؛] سپس [ما] آن دو را [به قدرت خویش] از هم جدا کردیم و هر چیز زنده‌ای را از آب پدید آوردیم؟ پس آیا [باز هم] ایمان نمی‌آورند؟');
INSERT INTO "islamhouse" VALUES(21,31,'21:31','و کوه‌هایی استوار در زمین نهادیم تا مبادا آنان را بلرزانَد و راه‌هایی [وسیع] در آن پدید آوردیم؛ باشد كه [در سفرهایشان، به مقصد] راه یابند.');
INSERT INTO "islamhouse" VALUES(21,32,'21:32','و آسمان را سقفی محفوظ قرار دادیم و[لی] آنان [= مشرکان] از [تفکر در] نشانه‌هایش رویگردانند.');
INSERT INTO "islamhouse" VALUES(21,33,'21:33','اوست که شب و روز و خورشید و ماه را آفرید [و چنان منظم ساخت که] هر یک [از اینها] در مداری [معیّن] شناورند.');
INSERT INTO "islamhouse" VALUES(21,34,'21:34','[ای پیامبر،] ما پیش از تو برای هیچ بشری جاودانگی [در دنیا] قرار ندادیم. آیا اگر تو بمیری، آنان جاودانند؟');
INSERT INTO "islamhouse" VALUES(21,35,'21:35','هر کسی چشندۀ [طعم‌] مرگ است؛ و [ای مردم،] شما را با بدی و نیکی می‌آزماییم و [سرانجام] به سوی ما بازگردانده می‌شوید.');
INSERT INTO "islamhouse" VALUES(21,36,'21:36','کسانی که کفر ورزیدند، هر گاه که تو را می‌بینند، فقط به مسخره‌ات می‌گیرند [و می گویند:] «آیا این همان کسی است که از معبودانتان [به بدی] یاد می‌کند؟» و حال آنکه آنان خود یاد [الله] رحمان را انکار می‌کنند.');
INSERT INTO "islamhouse" VALUES(21,37,'21:37','انسان [ذاتاً] عجول آفریده شده است [و همه چیز را پیش از زمان مقررش می‌خواهد]. به زودی نشانه‌های [عذابِ] خویش را به شما نشان خواهیم داد؛ پس [نزولِ عذاب را] به شتاب نخواهید.');
INSERT INTO "islamhouse" VALUES(21,38,'21:38','[کافران] می‌گویند: «اگر راستگویید، این وعده‌[ی قیامت] کی خواهد بود؟»');
INSERT INTO "islamhouse" VALUES(21,39,'21:39','کسانی‌ که کفر ورزیدند اگر می‌دانستند زمانی که [به دوزخ می‌افتند] نمی‌توانند [شعله‌های] آتش را از چهره‌ها و پشت‌هایشان بازدارند و [از سوی هیچ کس] یاری نمی‌شوند، [هرگز این گونه شتابان عذاب را نمی‌خواستند].');
INSERT INTO "islamhouse" VALUES(21,40,'21:40','بلكه [آتش، چنان‌] ناگهانى به آنان مى‌رسد و آنان را [چنان سرگشته و] بهت‌زده مى‌كند كه نه مى‌توانند آن را [از خود] دفع کنند و نه مهلت [توبه] به آنان داده مى‌شود.');
INSERT INTO "islamhouse" VALUES(21,41,'21:41','یقیناً پیامبران پیش از تو [نیز] مورد تمسخر قرار گرفتند؛ آنگاه بر سرِ کسانی که آنها [= عذاب‌های الهی] را به ریشخند می‌گرفتند، همان چیزی آمد که آن را مسخره می‌کردند.');
INSERT INTO "islamhouse" VALUES(21,42,'21:42','[ای پیامبر، به کافران] بگو: «چه کسی شما را در شب و روز از [عذاب الله] رحمان محافظت می‌کند؟ بلکه [حق این است که] آنان از یاد پروردگارشان رویگردانند.');
INSERT INTO "islamhouse" VALUES(21,43,'21:43','آیا آنان معبودانی دارند که [می‌توانند] در برابر ما از آنان دفاع کنند؟ [هرگز!] آنان نه می‌توانند خود را یاری دهند و نه از [عذاب] ما در امانند.');
INSERT INTO "islamhouse" VALUES(21,44,'21:44','بلکه [ما] آنان و پدرانشان را [از نعمت‌های دنیوی] بهره‌مند ساختیم تا عمرشان به درازا کشید [و مغرور شدند]. آیا نمى‌بینند كه ما [همواره] به سراغ زمین مى‌آییم و آن را از هر طرف مى‌كاهیم [و مردمش را نابود می‌کنیم]؟ پس آیا آنان [بر قدرتِ الله] پیروزند؟');
INSERT INTO "islamhouse" VALUES(21,45,'21:45','بگو: «من فقط شما را به وسیلۀ وحی [از عذاب الهی] بیم می‌دهم» و[لى] چون به كران بیم داده ‌شود، دعوت [حق] را نمى‌شنوند.');
INSERT INTO "islamhouse" VALUES(21,46,'21:46','اگر اندکی از عذاب پروردگارت به آنان برسد، قطعاً می‌گویند: «ای وای بر ما [که] یقیناً ستمکار بوده‌ایم!».');
INSERT INTO "islamhouse" VALUES(21,47,'21:47','روز قیامت، ما ترازوهای عدل را [برای حسابرسی در میان] می‌نهیم؛ آنگاه به هیچ کس [کمترین] ستمی نمی‌شود؛ و اگر [کردارشان] همسنگِ دانۀ خَردَلی باشد، آن را [به حساب] می‌آوریم؛ و کافی است که ما حسابرس باشیم.');
INSERT INTO "islamhouse" VALUES(21,48,'21:48','به راستی، ما به موسی و هارون فُرقان [= تورات] دادیم و [این کتاب] برای پرهیزگاران، روشنایی و اندرزی است.');
INSERT INTO "islamhouse" VALUES(21,49,'21:49','[همان] کسانی ‌که در نهان از پروردگارشان می‌ترسند و از قیامت بیمناکند.');
INSERT INTO "islamhouse" VALUES(21,50,'21:50','و این [قرآن،] پند مبارکی است که آن را نازل کردیم. آیا [باز هم] انکارش می‌کنید؟');
INSERT INTO "islamhouse" VALUES(21,51,'21:51','و بی‌تردید، پیش از آن، ما به ابراهیم [رشد فکری و] هدایتش را عطا کردیم و به [شایستگی‌های] او دانا بودیم؛');
INSERT INTO "islamhouse" VALUES(21,52,'21:52','آنگاه که به پدر خود و قومش گفت: «این مجسمه‌ها چیست که به عبادت آنها ایستاده‌اید؟»');
INSERT INTO "islamhouse" VALUES(21,53,'21:53','آنان گفتند: «ما پدرانمان را دیدیم که آنها را عبادت می‌کردند».');
INSERT INTO "islamhouse" VALUES(21,54,'21:54','[ابراهیم] گفت: «قطعاً شما و پدرانتان در گمراهیِ آشکار بوده‌اید».');
INSERT INTO "islamhouse" VALUES(21,55,'21:55','آنان گفتند: «آیا [سخن] حق برایمان آورده‌ای یا شوخی می‌کنی؟»');
INSERT INTO "islamhouse" VALUES(21,56,'21:56','[ابراهیم] گفت: «[نه،] بلکه [سخنم حق است:] پروردگارتان، پروردگار آسمان‌ها و زمین است؛ همان ذاتی که آنها را پدید آورده است و من بر این [سخن] گواهم».');
INSERT INTO "islamhouse" VALUES(21,57,'21:57','و [با خود گفت:] «به الله سوگند، پس از آنکه پشت کردید و [به جشن] رفتید، برای [نابودی] بُت‌هایتان تدبیری می‌اندیشم».');
INSERT INTO "islamhouse" VALUES(21,58,'21:58','و [در غیابِ بت‌پرستان،] همۀ آنها جز [بُت] بزرگشان را خُرد کرد؛ شاید به سراغ آن بروند [تا حقایق را برایشان بازگو کند].');
INSERT INTO "islamhouse" VALUES(21,59,'21:59','[وقتی آنان معبودان خویش را چنین دیدند] گفتند: «چه کسی با معبودان ما چنین کرده است؟ قطعاً او ستمکار است».');
INSERT INTO "islamhouse" VALUES(21,60,'21:60','[برخی] گفتند: «ما شنیدیم جوانی که او را ابراهیم می‌نامند از آنها [به بدی] یاد می‌کند».');
INSERT INTO "islamhouse" VALUES(21,61,'21:61','گفتند: «پس او را در برابر دیدگان مردم بیاورید؛ باشد که [بر اعترافش به گناه] گواه باشند».');
INSERT INTO "islamhouse" VALUES(21,62,'21:62','[وقتی ابراهیم را آوردند] گفتند: «ای ابراهیم، آیا تو این [کار] را با معبودان ما کرده‌ای؟»');
INSERT INTO "islamhouse" VALUES(21,63,'21:63','[او] گفت: «بلکه بزرگشان چنین کرده است. اگر سخن می‌گویند، از آنها بپرسید».');
INSERT INTO "islamhouse" VALUES(21,64,'21:64','آنگاه [آنان] به خود آمدند و [به یکدیگر] گفتند: «واقعاً شما [مشرک و] ستمکارید».');
INSERT INTO "islamhouse" VALUES(21,65,'21:65','سپس [باز هم] سر به دشمنی و انکار برداشتند [و گفتند:] «تو خوب می‌دانی که اینها سخن نمی‌گویند».');
INSERT INTO "islamhouse" VALUES(21,66,'21:66','[ابراهیم] گفت: «آیا به جای الله چیزهایی را عبادت می‌کنید که نه سودی برایتان دارد و نه زیانی به شما می‌رساند؟');
INSERT INTO "islamhouse" VALUES(21,67,'21:67','اُف بر شما و بر آنچه به جای الله عبادت می‌کنید! آیا نمی‌اندیشید؟»');
INSERT INTO "islamhouse" VALUES(21,68,'21:68','آنان گفتند: «اگر مى‌خواهید [برای مجازاتش] كارى كنید، او را بسوزانید و معبودانتان را یاری دهید».');
INSERT INTO "islamhouse" VALUES(21,69,'21:69','[سرانجام او را در آتش انداختند؛ ولی ما] گفتیم: «ای آتش، بر ابراهیم سرد و سلامت باش».');
INSERT INTO "islamhouse" VALUES(21,70,'21:70','و خواستند برای [نابودی] او نیرنگی به کار برند؛ ولی ما آنان را زیانکارترین [مردم] ساختیم؛');
INSERT INTO "islamhouse" VALUES(21,71,'21:71','و او و لوط را [با هجرت‌] به سرزمینی که برای جهانیان در آن برکت نهاده‌ایم [= شام] نجات دادیم؛');
INSERT INTO "islamhouse" VALUES(21,72,'21:72','و اسحاق و [نوه‌اش] یعقوب را [نیز به عنوان بخشش] افزونی به او بخشیدیم و همگی‌شان را [افرادی] شایسته گرداندیم؛');
INSERT INTO "islamhouse" VALUES(21,73,'21:73','و آنان را پیشوایانی قرار دادیم که به فرمان ما [مردم را] هدایت می‌کردند و انجام کارهای نیک و برپاداشتن نماز و ادای زکات را به آنان وحی کردیم؛ و آنان [همه] عبادت‌کننده ما بودند.');
INSERT INTO "islamhouse" VALUES(21,74,'21:74','ما به لوط، [اجازۀ] قضاوت و علم [دین] دادیم و او را از شهری که [ساکنانش] اعمال زشت [و پلید] مرتکب می‌شدند نجات بخشیدیم. به راستی که آنان گروهی پلید و نافرمان بودند.');
INSERT INTO "islamhouse" VALUES(21,75,'21:75','و او را در رحمت خود وارد کردیم. بی‌گمان، او از شایستگان بود.');
INSERT INTO "islamhouse" VALUES(21,76,'21:76','و نوح را [یاد کن] هنگامی ‌که پیشتر [از سایر پیامبران، ما را] ندا داد؛ ما [نیز دعای] وی را اجابت کردیم آنگاه او و خاندانش را از اندوهِ بزرگ نجات دادیم.');
INSERT INTO "islamhouse" VALUES(21,77,'21:77','و در برابر قومی ‌که آیات ما را تکذیب کرده بودند یاری‌اش کردیم. بی‌تردید، آنان گروه بدکاری بودند؛ پس همگی‌شان را غرق کردیم.');
INSERT INTO "islamhouse" VALUES(21,78,'21:78','و [یاد کن از] داود و سلیمان، آنگاه ‌که دربارۀ کشتزاری داوری می‌کردند که گوسفندان قوم، شبانه در آن چریده بودند و ما بر داوری آنان گواه بودیم.');
INSERT INTO "islamhouse" VALUES(21,79,'21:79','آن [حقیقت] را به سلیمان فهماندیم و به هر یک از آنان پیامبری و دانش [عمل به احکام دین] عطا کردیم؛ و کوه‌ها و پرندگان را در خدمت داود گماشتیم که [همراه با او، پروردگار را] تسبیح می‌گفتند و ما بودیم که این [همه] را انجام دادیم.');
INSERT INTO "islamhouse" VALUES(21,80,'21:80','و ساختن زره را به او [= داود] آموختیم تا شما را از [آسیب] جنگ‌هایتان حفظ کند؛ پس آیا شکر می‌گزارید؟');
INSERT INTO "islamhouse" VALUES(21,81,'21:81','و تندباد را برای سلیمان [به خدمت گماشتیم] که به فرمانِ او به سوی سرزمینی که در آن برکت نهاده‌ایم [= شام] روان بود؛ و ما همه چیز را می‌دانستیم.');
INSERT INTO "islamhouse" VALUES(21,82,'21:82','و [نیز] از شیاطین کسانی را [گماشتیم] که برایش غواصی می‌کردند [و جواهر استخراج می‌نمودند] و کارهایی غیر از این [نیز] انجام می‌دادند؛ و ما مراقب [رفتار و تعداد] آنان بودیم.');
INSERT INTO "islamhouse" VALUES(21,83,'21:83','و ایوب را [یاد کن] آنگاه‌ که پروردگارش را [چنین] ندا داد: «رنج [و بیماری] به من رسیده است و تو مهربان‌ترینِ مهربانانی».');
INSERT INTO "islamhouse" VALUES(21,84,'21:84','ما [دعای] او را اجابت کردیم و رنجی را که به او رسیده بود برطرف ساختیم و خانواده‌اش و [نیز تعدادی] همانندشان را به همراه آنان به وی بازگرداندیم [تا] رحمتی از جانب ما و پندی برای عبادت‌کنندگان باشد.');
INSERT INTO "islamhouse" VALUES(21,85,'21:85','و [یاد کن از] اسماعیل و ادریس و ذوالکِفل که همگی از شکیبایان بودند.');
INSERT INTO "islamhouse" VALUES(21,86,'21:86','و [ما] آنان را در رحمت خویش وارد کردیم. بی‌تردید، آنان از شایستگان بودند.');
INSERT INTO "islamhouse" VALUES(21,87,'21:87','و [یاد کن از] ذوالنون [= یونس] آنگاه که خشمگین [از میان قومش] رفت و چنین پنداشت که ما هرگز بر او سخت نمی‌گیریم [و به خاطر این کار مجازاتش نمی‌کنیم]؛ [وقتی ماهی او را بلعید] در تاریکی‌ها ندا داد که: «[پروردگارا،] هیچ معبودی [به‌حق] جز تو نیست. تو منزّهی. بی‌تردید، من از ستمکاران بوده‌ام».');
INSERT INTO "islamhouse" VALUES(21,88,'21:88','دعایش را اجابت کردیم و او را از اندوه نجات دادیم و مؤمنان را [نیز] اینچنین نجات می‌دهیم.');
INSERT INTO "islamhouse" VALUES(21,89,'21:89','و [یاد کن از] زکریا، آنگاه ‌که پروردگارش را [این گونه] ندا داد: «پروردگارا، مرا تنها [و بی‌فرزند] مگذار و تو بهترین وارثانی».');
INSERT INTO "islamhouse" VALUES(21,90,'21:90','دعایش را اجابت کردیم و یحیی را به او بخشیدیم و همسرش را [که نازا بود] برایش شایسته [و آمادۀ بارداری] نمودیم. آنان همواره در کارهای خیر می‌شتافتند و در حال بیم و امید ما را می‌خواندند و پیوسته برای ما [فرمانبردار و] فروتن بودند.');
INSERT INTO "islamhouse" VALUES(21,91,'21:91','و [یاد کن از مریم،] زنی که دامانِ خود را پاک نگاه داشت و [ما] از روح[ی از جانب] خویش در [وجودِ] او دمیدیم و او و پسرش را برای جهانیان نشانه‌ای [از قدرت الهی] قرار دادیم.');
INSERT INTO "islamhouse" VALUES(21,92,'21:92','[ای مردم،] به راستی، این آیین شما، آیینی یگانه [و مبتنی بر یکتاپرستی] است و من پروردگارتان هستم؛ پس مرا عبادت کنید.');
INSERT INTO "islamhouse" VALUES(21,93,'21:93','و[لی مردم] میان خود در امر [دین]شان فرقه‌فرقه شدند؛ [ولی سرانجام،] همگی به سوی ما بازمی‌گردند.');
INSERT INTO "islamhouse" VALUES(21,94,'21:94','هر کس کارهای شایسته انجام دهد و مؤمن باشد، در برابر کوشش او ناسپاسی نخواهد شد و ما کارهایش را می‌نویسیم‌.');
INSERT INTO "islamhouse" VALUES(21,95,'21:95','و [مردم] هر شهری را که [بر اثر کفر و گناه] نابودش کردیم، محال است که [بتوانند به قصدِ توبه به دنیا] بازگردند.');
INSERT INTO "islamhouse" VALUES(21,96,'21:96','تا زمانی‌ که [سدّ] یأجوج و مأجوج گشوده شود و آنان از هر بلندی [و تپه‌ای] شتابان سرازیر گردند.');
INSERT INTO "islamhouse" VALUES(21,97,'21:97','و وعدۀ حق [= قیامت] نزدیک گردد؛ در آن هنگام، چشم‌های کسانی ‌که کفر ورزیدند [از شدت وحشت] خیره می‌مانَد [و می‌گویند:] «ای وای بر ما! قطعاً ما از این [حادثه] در غفلت بودیم! بلکه [کافر و] ستمکار بودیم».');
INSERT INTO "islamhouse" VALUES(21,98,'21:98','[به آنان گفته می‌شود:] «قطعاً شما و آنچه که به جای الله عبادت می‌کنید، هیزمِ دوزخید [و همگی] در آن وارد خواهید شد».');
INSERT INTO "islamhouse" VALUES(21,99,'21:99','اگر اینها معبودانی [به‌حق] بودند، هرگز وارد آن نمی‌شدند؛ و حال آنكه همگی در آن [عذاب سوزان] جاودانند.');
INSERT INTO "islamhouse" VALUES(21,100,'21:100','در آنجا ناله‌هایى [دردناک‌] دارند و [از شدت درد و ترس] چیزى نمى‌شنوند.');
INSERT INTO "islamhouse" VALUES(21,101,'21:101','[امّا] کسانی ‌که پیشتر، وعدۀ نیکو [= بهشت] از جانب ما به آنان داده شده است، یقیناً از آن [آتش جهنم] دور نگه داشته می‌شوند.');
INSERT INTO "islamhouse" VALUES(21,102,'21:102','آنان صدای آن [آتش هولناک و فریادهای دوزخیان] را نمی‌شنوند و در آنچه میل داشته باشند، جاودانه [در آسایش] هستند.');
INSERT INTO "islamhouse" VALUES(21,103,'21:103','وحشت بزرگ [قیامت] آنان را اندوهگین نمی‌سازد و فرشتگان به استقبالشان می‌آیند [و به آنان می‌گویند:] «این همان روزی است که به شما وعده داده می‌شد».');
INSERT INTO "islamhouse" VALUES(21,104,'21:104','روزی که آسمان را در هم می‌پیچیم همانگونه که طومار نوشته‌ها را درهم می‌پیچد، [و] همان ‌گونه که نخستین [بار] آفرینش را آغاز کردیم، [بار دیگر] آن را بازمی‌گردانیم. [این] وعده‌ای است بر عهدۀ ما که قطعاً انجامش خواهیم داد.');
INSERT INTO "islamhouse" VALUES(21,105,'21:105','و به راستی پس [از اینکه در] لوح محفوظ [ثبت شده بود]، در [تمام] کتاب‌های [آسمانی] نوشتیم که: «زمین را بندگان شایسته‌ام به ارث می‌برند».');
INSERT INTO "islamhouse" VALUES(21,106,'21:106','بی‌گمان، در این [سخن، عبرت و] پیام روشنی برای عبادت‌کنندگان است.');
INSERT INTO "islamhouse" VALUES(21,107,'21:107','و [ای محمد،] تو را جز رحمتی برای جهانیان نفرستادیم.');
INSERT INTO "islamhouse" VALUES(21,108,'21:108','بگو: «در حقیقت به من وحی می‌شود که: "بی‌تردید، معبود شما معبودی یگانه است؛ پس آیا تسلیم [دعوتِ حق] می‌شوید؟"»');
INSERT INTO "islamhouse" VALUES(21,109,'21:109','اگر رویگردان شدند، بگو: «من [همۀ] شما را به طور یکسان آگاه کردم [و دربارۀ عذاب الهی هشدار دادم] و نمی‌دانم آنچه که به شما وعده داده می‌شود نزدیک است یا دور.');
INSERT INTO "islamhouse" VALUES(21,110,'21:110','یقیناً او تعالی سخن آشکار را می‌داند و [نیز] آنچه را [در دل خویش] پنهان می‌کنید می‌داند.');
INSERT INTO "islamhouse" VALUES(21,111,'21:111','و نمی‌دانم؛ شاید این [تأخیر عذاب، مایۀ] آزمایش و بهره‌مندیِ شما تا مدتی [معیّن] باشد».');
INSERT INTO "islamhouse" VALUES(21,112,'21:112','[پیامبر] گفت: «پروردگارا، [بین ما و کافران] به حق داوری کن؛ و پروردگار ما، [الله] رحمان است که در برابر آنچه [از کفر و شرک] می‌گویید از او یاری می‌خواهیم».');
INSERT INTO "islamhouse" VALUES(22,1,'22:1','ای مردم، از پروردگارتان پروا کنید. بی‌تردید، زلزلۀ قیامت امر بزرگی است.');
INSERT INTO "islamhouse" VALUES(22,2,'22:2','روزی که آن را ‌می‌بینید، [آنچنان وحشتناک است که] هر مادرِ شیرده‌ای [فرزندِ] شیرخوارش را فراموش می‌کند و هر [موجودِ] بارداری جنین خود را [بر زمین] می‌نهد و مردم را مست می‌بینی، در حالی که مست نیستند؛ بلکه عذاب الله شدید است.');
INSERT INTO "islamhouse" VALUES(22,3,'22:3','و از [میان] مردم کسی هست که بدون [هیچ] دانشی، دربارۀ الله مجادله می‌کند و از هر شیطان سرکشی پیروی می‌نماید.');
INSERT INTO "islamhouse" VALUES(22,4,'22:4','[در قضای الهی بر شیطان‌] مقرر شده است که هر کس او را به دوستى بگیرد، قطعاً گمراهش مى‌سازد و به عذاب آتش مى‌كشاند.');
INSERT INTO "islamhouse" VALUES(22,5,'22:5','ای مردم، اگر دربارۀ برانگیخته شدن [در قیامت] تردید دارید، [بدانید که] بی‌تردید، ما شما را از خاک آفریدیم، سپس از نطفه و آنگاه از خون بسته‌شده، سپس از پاره‌گوشتی دارای خلقت کامل و [بعضاً] خلقت ناقص؛ [چنین کردیم] تا [قدرت خویش را] برایتان آشکار سازیم؛ و هر چه را که بخواهیم، تا مدتی معیّن در رَحِم [مادران] نگه می‌داریم، آنگاه شما را به صورت نوزادی بیرون می‌آوریم، سپس [پرورش می‌یابید] تا به حد رشد [و بلوغ] خود برسید؛ و از شما کسی هست که [در دوران جوانی] می‌میرد و کسی نیز هست [که آنقدر عمر می‌کند تا] به نهایت فرتوتی [و کهنسالی] می‌رسد، چنان که پس از [آن همه] دانش، [همه را فراموش می‌کند و] چیزی نمی‌داند؛ و [اما مثالی دیگر:] زمین را خشکیده می‌بینی؛ ولی چون آب [باران] بر آن فرو می‌فرستیم، به جنبش درمی‌آید و رشد می‌کند و از هر نوع [گیاه] زیبایی می‌رویاند.');
INSERT INTO "islamhouse" VALUES(22,6,'22:6','این [شگفتی‌های خلقت،] از آن روست که [بدانید] الله حق است و اوست که مردگان را زنده می‌کند و او بر هر کاری تواناست.');
INSERT INTO "islamhouse" VALUES(22,7,'22:7','و اینکه قیامت آمدنی است [و] تردیدی در آن نیست و اینکه الله [همۀ] کسانی ‌را که در گور‌ها هستند، برمی‌انگیزد.');
INSERT INTO "islamhouse" VALUES(22,8,'22:8','و از مردم کسی هست که بدون هیچ دانش و هدایت و کتاب روشنی‌بخشی، دربارۀ الله مجادله می‌کند.');
INSERT INTO "islamhouse" VALUES(22,9,'22:9','[از روی تکبر] سر خویش را برمی‌گرداند تا [مردم را] از راه الله گمراه سازد. براى او در این دنیا رسوایی و خوارى است و روز قیامت [نیز] عذاب آتش سوزان را به او مى‌چشانیم.');
INSERT INTO "islamhouse" VALUES(22,10,'22:10','این [کیفر،] به سزای کارهایی است که از پیش فرستاده است و الله هرگز نسبت به بندگان [خود] ستمکار نیست.');
INSERT INTO "islamhouse" VALUES(22,11,'22:11','و از [میان] مردم کسی هست که الله را با تردید عبادت می‌کند [و ایمانش ضعیف است]؛ پس اگر خیری به او برسد، [دلش] به آن آرام می‌گیرد [و بر ایمانش باقی می‌ماند] و اگر بلایی [برای آزمایش] به او برسد، روی می‌گرداند [و به کفر بازمی‌گردد]. او در دنیا و آخرت زیان کرده است. این همان زیان آشکار است.');
INSERT INTO "islamhouse" VALUES(22,12,'22:12','[انسان کافر،] به جای الله چیزی را می‌خوانَد که نه به او زیانی می‌رساند و نه سودی می‌بخشد. این همان گمراهی دور و دراز است.');
INSERT INTO "islamhouse" VALUES(22,13,'22:13','او کسی را می‌خوانَد که قطعاً زیانش نزدیک‌تر از سودش می‌باشد؛ و چه بد دوست و چه بد همدمی است!');
INSERT INTO "islamhouse" VALUES(22,14,'22:14','بی‌تردید، الله کسانی را که ایمان آورده‌اند و کارهای شایسته انجام داد‌ه‌اند، به باغ‌هایی [از بهشت] وارد می‌کند که جویبارها از زیر [درختان] آن جاری است. به راستی، الله آنچه را که می‌خواهد، انجام می‌دهد.');
INSERT INTO "islamhouse" VALUES(22,15,'22:15','کسی‌ که گمان می‌کند الله او [= پیامبر] را در دنیا و آخرت هرگز یاری نخواهد کرد [و اکنون حمایت الله را می‌بیند و خشمگین است]، باید ریسمانی به سقف [خانه‌اش] بیاویزد و [خود را دار بزند تا راه نفَس] را قطع کند [و تا سر حد مرگ پیش رود]؛ آنگاه بنگرد که آیا [این ترفند و] نیرنگ او، خشمش را از میان می‌بَرد.');
INSERT INTO "islamhouse" VALUES(22,16,'22:16','و این‌گونه، ما این [قرآن] را [به صورت] آیاتی روشن نازل کردیم و الله هر کس را بخواهد، هدایت می‌کند.');
INSERT INTO "islamhouse" VALUES(22,17,'22:17','کسانی ‌که ایمان آورده‌اند و کسانی که یهودی شدند و صابئان [= پیروان برخی پیامبران] و نصاری و زرتشتیان و کسانی ‌که شرک ورزیدند، یقیناً روز قیامت الله میانشان داوری می‌کند. بی‌گمان، الله بر هر چیزی گواه است.');
INSERT INTO "islamhouse" VALUES(22,18,'22:18','آیا ندیدی که هر ‌کس در آسمان‌ها و هر ‌کس در زمین است و خورشید و ماه و ستارگان و کوه‌ها و درختان و جنبندگان و بسیاری از مردم برای الله سجده می‌کنند؟ و بسیاری نیز هستند که [در نتیجۀ سرپیچی از عبادت، فرمانِ] عذاب بر آنان تحقق یافته است؛ و هر کس که الله او را خوار سازد، کسی گرامی‌اش نمی‌دارد. بی‌تردید، الله هر چه بخواهد انجام می‌دهد.');
INSERT INTO "islamhouse" VALUES(22,19,'22:19','این دو [گروه مؤمن و کافر،] دشمنان یکدیگرند و دربارۀ پروردگارشان ستیز می‌کنند. پس کسانی ‌که کفر ورزیدند، برایشان جامه‌هایی از آتش بریده‌اند [و] از بالای سرشان [بر آنان] آب جوشان می‌ریزند.');
INSERT INTO "islamhouse" VALUES(22,20,'22:20','آنچه در درونشان هست و [نیز] پوست‌هایشان با آن گداخته می‌شود.');
INSERT INTO "islamhouse" VALUES(22,21,'22:21','و گرز‌های آهنین برایشان [مهیّا] است.');
INSERT INTO "islamhouse" VALUES(22,22,'22:22','هرگاه بخواهند از [شدت] اندوه از آنجا [= دوزخ] خارج شوند، به آن بازگردانده می‌شوند و [به آنان گفته می‌شود:] «عذاب سوزان را بچشید».');
INSERT INTO "islamhouse" VALUES(22,23,'22:23','بی‌تردید، الله کسانی را که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، به باغ‌هایی [از بهشت] وارد می‌کند که از زیر [درختان] آن جویبارها جاری است. در آنجا به دستبند‌هایی از طلا و مروارید آراسته می‌شوند و لباسشان در آنجا ابریشم است.');
INSERT INTO "islamhouse" VALUES(22,24,'22:24','و [در دنیا] به سوی گفتار پاک [توحید و تکبیر و تحمید] هدایت می‌شوند و به راهِ ستوده [اسلام] راهنمایی می‌گردند.');
INSERT INTO "islamhouse" VALUES(22,25,'22:25','بی‌گمان، كسانى كه كفر ورزیدند و [مانند ماجرای حدیبیه، مردم را] از راه الله بازمى‌دارند [عذابی دردناک در پیش دارند]؛ و مسجد الحرام كه آن را براى مردم [قبله و عبادتگاه] قرار دادیم، مقیم و مسافر در آن یكسانند؛ و هر کس در آنجا با ستمکاری [و گناه،] انحراف بخواهد، از عذابى دردناک به او می‌چشانیم.');
INSERT INTO "islamhouse" VALUES(22,26,'22:26','و [یاد کن از] آنگاه که محلِ خانه [کعبه] را برای ابراهیم تعیین کردیم [و به او گفتیم] که: «چیزی را شریک من قرار نده و خانه‌ام را برای طواف‌کنندگان و قیام‌کنندگان و رکوع‌کنندگان [و] سجده‌کنندگان پاک گردان.');
INSERT INTO "islamhouse" VALUES(22,27,'22:27','و بین مردم برای [ادای] حج ندا بده تا پیاده و [سوار] بر هر [وسیلۀ سفر یا] شتر لاغری، از هر راه دوری به سویت بیایند.');
INSERT INTO "islamhouse" VALUES(22,28,'22:28','تا شاهد منافع [گوناگون] خویش باشند و در روزهایی معیّن، [به هنگام قربانی] نام الله را بر چهارپایانی که به آنان روزی داده‌ایم، یاد کنند؛ پس [خودتان] از [گوشتِ] آن بخورید و به درماندۀ فقیر [نیز] غذا بدهید.');
INSERT INTO "islamhouse" VALUES(22,29,'22:29','سپس باید آلودگی‌هایشان را برطرف سازند و به نذرهای خویش وفا کنند و [گرداگردِ] این خانۀ کهن [= کعبه] طواف کنند».');
INSERT INTO "islamhouse" VALUES(22,30,'22:30','این است [حکمِ الله]؛ و هر ‌کس [مُحرّمات و] مقررات الهی را بزرگ بدارد، این [کار] نزد پروردگارش برای او بهتر است؛ و [ای مردم،] چهارپایانی برای [مصرف] شما حلال شده است، مگر آنچه [حُکمش] بر شما خوانده می‌شود؛ پس از پلیدیِ بُت‌ها [و معبودان باطل] دوری کنید و از گفتار باطل [و دروغ] اجتناب نمایید.');
INSERT INTO "islamhouse" VALUES(22,31,'22:31','در حالی ‌که [بندۀ مخلصِ الله و] حقگرا باشید و به او شرک نورزید؛ و هر کس به الله شرک ورزد، چنان است که گویى از آسمان افتاده و مرغان [شكارى‌] وی را بربایند یا باد او را به جایى دورافتاده بیندازد.');
INSERT INTO "islamhouse" VALUES(22,32,'22:32','این است [فرمان الله]، و هر کس دستورهای دینی و الهی را بزرگ بشمارد، یقیناً این [بزرگداشت، نشانه‌ای] از پرهیزگاری دل‌هاست.');
INSERT INTO "islamhouse" VALUES(22,33,'22:33','در آن [چهارپایانِ ویژۀ قربانی،] تا زمان معیّن [= هنگام ذبح] برای شما منافع [و بهره‌هایی] است. سپس قربانگاه آنها خانۀ قدیمی و گرامی [کعبه] است.');
INSERT INTO "islamhouse" VALUES(22,34,'22:34','و برای هر امتی آیینی [در قربانی کردن] قرار دادیم تا نام الله را [وقت ذبح] بر چهارپایانی که روزی‌شان کرده است ذکر کنند. معبود شما، معبود یگانه است. [همه] در برابر او تسلیم باشید؛ و [ای پیامبر، خیر دنیا و آخرت را] به فروتنان بشارت بده.');
INSERT INTO "islamhouse" VALUES(22,35,'22:35','[همان] کسانی ‌که چون نام الله برده شود، دل‌هایشان می‌ترسد و [کسانی که] در برابر مصیبت‌هایی که به آنان می‌رسد شکیبا هستند و کسانی که نمازگزارند و از آنچه روزی‌شان کرده‌ایم انفاق می‌کنند.');
INSERT INTO "islamhouse" VALUES(22,36,'22:36','[قربانی کردنِ] شتر [و گاو در موسمِ حج] را برای شما از شعایر دین [و نشانه‌های عبادت الله] قرار دادیم. در این [کار،] خیر [و برکت] برایتان است. [هنگام قربانی،] در حالی‌ که [شتران] بر پا ایستاده‌اند، نام الله را بر آنها ببرید [و نحرشان کنید]. آنگاه چون بر خاک افتادند، از [گوشت] آنها بخورید و نیازمندان آبرومند و گدایان را نیز اطعام کنید. آنها [=چهارپایان] را اینچنین به خدمت شما گماشتیم؛ باشد که شکر به جای آورید.');
INSERT INTO "islamhouse" VALUES(22,37,'22:37','گوشت و خون این [قربانی‌ها] هرگز به الله نمی‌رسد؛ بلکه پرهیزگاری شماست که به او می‌رسد. او تعالی آنها را اینچنین به خدمت شما گماشت تا الله را به [شکرانۀ] آنکه شما را هدایت کرده است، به بزرگی یاد کنید؛ و نیکوکاران را [به خیر و رستگاری] بشارت بده.');
INSERT INTO "islamhouse" VALUES(22,38,'22:38','الله از کسانی ‌که ایمان آورده‌اند قطعاً [در برابر دشمنانشان] دفاع می‌کند. بی‌تردید، الله هیچ خیانتکارِ ناسپاسی را دوست ندارد.');
INSERT INTO "islamhouse" VALUES(22,39,'22:39','به کسانی‌ که [مشرکان] با آنان جنگیده‌اند اجازۀ [جهاد] داده شده است؛ چرا که ستم دیده‌اند؛ و یقیناً الله بر یاری‌شان تواناست.');
INSERT INTO "islamhouse" VALUES(22,40,'22:40','[همان] کسانی که به ناحق از دیار‌شان رانده شدند [و گناهی نداشتند] جز اینکه می‌گفتند: «پروردگارِ ما الله است». و اگر الله [ظلم و کفرِ] برخی از مردم را به وسیلۀ برخی دیگر دفع نمی‌کرد، صومعه‌ها[ی راهبان] و کلیساهای [نصاری] و عبادتگاه‌ها[ی یهود] و مساجدی که نام الله در آنان بسیار برده می‌شود حتماً ویران می‌گردید؛ و الله کسی را ‌که [دین] او را یاری دهد، یقیناً یاری می‌کند. بی‌تردید، الله قدرتمندِ شکست‌ناپذیر است.');
INSERT INTO "islamhouse" VALUES(22,41,'22:41','[همان] کسانی ‌که هر گاه در زمین به آنان قدرت [و حکومت] ببخشیم، نماز برپا می‌دارند و زکات می‌پردازند و [مردم را] به نیکی فرمان می‌دهند و از کارهای ناپسند بازمی‌دارند؛ و سرانجامِ کارها از آنِ الله است.');
INSERT INTO "islamhouse" VALUES(22,42,'22:42','[ای پیامبر،] اگر [کافران،] تو را دروغگو می‌انگارند، [شکیبا باش.] قطعاً پیش از آنان، قوم نوح و عاد و ثمود [نیز پیامبرانشان را] دروغگو می‌انگاشتند.');
INSERT INTO "islamhouse" VALUES(22,43,'22:43','و [همچنین] قوم ابراهیم و قوم لوط.');
INSERT INTO "islamhouse" VALUES(22,44,'22:44','و [نیز] اهل مَدیَن [شعیب را دروغگو می‌دانستند] و موسی [نیز توسط فرعونیان] دروغگو خوانده شد. به کافران مهلت دادم؛ آنگاه آنان را [به عذاب] فروگرفتم. پس [بنگر که نتیجۀ] انکار [آیات و مجازات] من چگونه بود.');
INSERT INTO "islamhouse" VALUES(22,45,'22:45','چه بسیار شهرهایی را که [چون مردمش کافر و‌] ستمکار بودند نابودشان کردیم و [اینک] سقف‌هایش فروریخته است؛ و چه بسیار چاه[های] متروک و قصر[های] استوار [و باشکوه که ساکنانش را از عذاب الهی نجات نداد].‌');
INSERT INTO "islamhouse" VALUES(22,46,'22:46','آیا آنان در زمین گردش نکرده‌اند، تا دل‌هایی داشته باشند که [حقیقت را] با آن دریابند یا گوش‌هایی که [اخبار گذشتگان را] با آن بشنوند؟ به راستی، تنها چشم‌ها نیست که نابینا می‌گردد؛ بلکه دل‌هایی که در سینه‌هاست [بی‌بصیرت گشته و برای دیدنِ حق،] نابینا می‌شود.');
INSERT INTO "islamhouse" VALUES(22,47,'22:47','[ای پیامبر، کافران] به شتاب از تو درخواستِ عذاب می‌کنند و الله هرگز وعده‌اش را خلاف نمی‌کند؛ و در واقع، یک روز [از عذاب آخرت] نزد پروردگارت، با هزار سال كه شما مى‌شمارید یكسان است.');
INSERT INTO "islamhouse" VALUES(22,48,'22:48','و چه بسیار [اهالی] شهرهایی که به آنان مهلت دادم، در حالی ‌که ستمکار بودند [اما باز هم سرپیچی کردند]؛ پس آنان را [به عذاب] فروگرفتم و بازگشت [همه] به سوی من است.');
INSERT INTO "islamhouse" VALUES(22,49,'22:49','بگو: «ای مردم، جز این نیست که من برای شما بیم‌دهنده‌ای آشکارم».');
INSERT INTO "islamhouse" VALUES(22,50,'22:50','کسانی ‌که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، آمرزش و روزیِ نیک [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(22,51,'22:51','و كسانى كه در [راهِ انكار و تکذیب‌] آیات ما می‌كوشند تا [به پندار خویش‌] ما را درمانده کنند، آنان اهل دوزخند.');
INSERT INTO "islamhouse" VALUES(22,52,'22:52','و [ما] هیچ رسول و پیامبری را پیش از تو نفرستادیم، مگر اینکه چون [آیات الهی را] تلاوت می‌کرد، شیطان در تلاوتش [کلمات اشتباه] القا می‌کرد؛ آنگاه الله آنچه را که شیطان القا نموده بود از میان می‌بُرد و سپس آیات خویش را استوار می‌ساخت؛ و الله دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(22,53,'22:53','تا [بدین وسیله، الله] آنچه را که شیطان القا می‌کند، برای کسانی که در دل‌هایشان بیماری [شک و نفاق] است و [همچنین برای] سنگدلان، وسیلۀ آزمایش قرار دهد؛ و یقیناً ستمکاران [مشرک] در دشمنیِ دور و درازی [با الله و پیامبرش] هستند.');
INSERT INTO "islamhouse" VALUES(22,54,'22:54','و تا کسانی كه دانش یافته‌اند، بدانند كه این [قرآن‌] حق است [و] از جانب پروردگار توست و به آن ایمان بیاورند و دل‌هایشان برایش [نرم و] فروتن گردد؛ و بی‌تردید، الله كسانى را كه ایمان آورده‌اند، به راهى راست هدایت می‌کند.');
INSERT INTO "islamhouse" VALUES(22,55,'22:55','کسانی که کفر ورزیدند همواره در مورد آن[چه در قرآن نازل کرده‌ایم] تردید دارند تا آنگاه که ناگهان قیامت فرارسد یا عذاب روزی بی‌خیر و رحمت بر آنان [فرود] آید.');
INSERT INTO "islamhouse" VALUES(22,56,'22:56','در آن روز، فرمانروایی از آنِ الله است [و] میان آنان داوری می‌کند. کسانی‌ که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، در باغ‌های پرنعمت [بهشت] هستند.');
INSERT INTO "islamhouse" VALUES(22,57,'22:57','و کسانی‌ که کفر ورزیدند و آیات ما را تکذیب کردند، عذاب خوارکننده‌ای [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(22,58,'22:58','و کسانی‌ که در راه الله هجرت کردند، سپس کشته شدند یا وفات کردند، یقیناً الله به آنان روزیِ نیکویی می‌دهد. به راستی که الله بهترین روزی‌دهنده است.');
INSERT INTO "islamhouse" VALUES(22,59,'22:59','بی‌تردید، آنان را به جایگاهی وارد می‌کند که از آن راضی باشند؛ و به راستی که الله دانای بردبار است.');
INSERT INTO "islamhouse" VALUES(22,60,'22:60','این است [حكم الهی دربارۀ كافر و مؤمن‌]؛ و هر کس به همان مقدار که به او ستم شده است مجازات کند، سپس [باز] مورد ستم قرار گیرد، الله یاری‌اش خواهد کرد. بی‌تردید، الله بخشندۀ آمرزنده است.');
INSERT INTO "islamhouse" VALUES(22,61,'22:61','این [یاریِ مظلومان‌] از آن روست که الله [بر هر کاری تواناست؛ چنان که] شب را در روز، و روز را در شب داخل می‌کند؛ [و از یکی می‌کاهد و بر دیگری می‌افزاید]؛ و بی‌تردید، الله شنوای بیناست.');
INSERT INTO "islamhouse" VALUES(22,62,'22:62','این به سبب آن است که الله حق است و آنچه را که [مشرکان] جز او می‌خوانند [همه] باطل است و الله بلندمرتبۀ بزرگ است.');
INSERT INTO "islamhouse" VALUES(22,63,'22:63','آیا ندیدی که الله آبی از آسمان فروفرستاد و زمین [بر اثر آن، خرم و] سرسبز می‌گردد؟ بی‌تردید، الله باریک‌بینِ آگاه است.');
INSERT INTO "islamhouse" VALUES(22,64,'22:64','آنچه در آسمان‌ها و زمین است از آنِ اوست؛ و بی‌تردید، الله بی‌نیازِ ستوده است.');
INSERT INTO "islamhouse" VALUES(22,65,'22:65','آیا ندیدی که الله آنچه را در زمین است به خدمت شما گماشته است و کشتی‌ها به فرمانش در دریا در حرکتند و آسمان را نگه می‌دارد که جز به اجازۀ او بر زمین نیفتد؟ بی‌تردید، الله [نسبت] به مردم، دلسوزِ مهربان است.');
INSERT INTO "islamhouse" VALUES(22,66,'22:66','و او تعالی ذاتی است که شما را زنده کرد، سپس می‌میرانَد، و آنگاه [بارِ دیگر] زنده می‌کند. به راستی که انسان [نسبت به نعمت‌های الهی] بسیار ناسپاس است.');
INSERT INTO "islamhouse" VALUES(22,67,'22:67','برای هر امتی آیینی مقرر کرده‌ایم كه به آن عمل ‌كنند؛ پس [ای پیامبر، پیروان سایر ادیان] نباید با تو در این امر ستیز کنند. به راه پروردگارت دعوت كن؛ [چرا که] تو یقیناً بر [آیینی درست و] راهی راست قرار دارى.');
INSERT INTO "islamhouse" VALUES(22,68,'22:68','و اگر [دربارۀ حقانیت دین] با تو مجادله کردند، بگو: «الله به آنچه انجام می‌دهید داناتر است.');
INSERT INTO "islamhouse" VALUES(22,69,'22:69','الله روز قیامت در مورد آنچه درباره‌اش اختلاف می‌کردید، میان شما داوری می‌کند».');
INSERT INTO "islamhouse" VALUES(22,70,'22:70','آیا ندانسته‌ای که الله آنچه را در آسمان و زمین است می‌داند؟ بی‌گمان [همۀ] اینها در کتابی= [لوح محفوظ، ثبت] است. مسلماً این [کار،] بر الله آسان است.');
INSERT INTO "islamhouse" VALUES(22,71,'22:71','و [مشرکان] به جای الله چیزی را عبادت می‌کنند که هیچ دلیلی بر [حقانیت] آن نازل نکرده است و به آن آگاهی [و شناختی نیز] ندارند؛ و ستمکاران [در برابر عذاب الهی] هیچ یاریگری ندارند.');
INSERT INTO "islamhouse" VALUES(22,72,'22:72','و هنگامی‌ که آیات روشن ما بر آنان خوانده می‌شود، در چهرۀ کسانی ‌که کفر ورزیده‌اند [آثار کراهت و] ناخوشایندی می‌بینی. نزدیک است به کسانی‌ که آیات ما را برایشان می‌خوانند حمله‌ور شوند. [ای پیامبر، به کافران] بگو: «آیا شما را به بدتر از این [که در انتظارتان است] خبر دهم؟ آتش [دوزخ] است که الله آن را به کسانی ‌که کفر ورزیدند وعده داده است؛ و چه بد سرانجامی است!»');
INSERT INTO "islamhouse" VALUES(22,73,'22:73','ای مردم، [دربارۀ ناتوانیِ معبودان باطل] مَثَلی زده شده است؛ پس به آن گوش دهید: کسانی را که به جای الله [به عبادت و یاری] می‌خوانید، هرگز نمی‌توانند [حتی] مگسی را بیافرینند، حتی اگر [همگی] برای این [کار] جمع شوند؛ و اگر آن مگس چیزی از آنان برُباید، نمی‌توانند آن را بازپس گیرند. [آری،] طالب و مطلوب [= معبود باطل و مگس یا عابد و معبود باطل] هر دو ناتوانند.');
INSERT INTO "islamhouse" VALUES(22,74,'22:74','[آنان] الله را چنان که سزاوارِ [بزرگداشتِ] اوست نشناخته‌اند. بی‌تردید، الله نیرومندِ شکست‌ناپذیر است.');
INSERT INTO "islamhouse" VALUES(22,75,'22:75','الله از میان فرشتگان، فرستادگانی [برای ابلاغ وحی به پیامبران] برمی‌گزیند و از میان انسان‌ها نیز [پیامبرانی برای رساندنِ پیام الهی به مردم انتخاب می‌کند]. بی‌تردید، الله شنوای بیناست.');
INSERT INTO "islamhouse" VALUES(22,76,'22:76','او تعالی آینده و گذشتۀ آنان را می‌داند و [همۀ] کارها به الله بازگردانده می‌شود.');
INSERT INTO "islamhouse" VALUES(22,77,'22:77','ای کسانی ‌که ایمان آورده‌اید، رکوع کنید و سجده گزارید و پروردگارتان را عبادت کنید و کار نیک انجام دهید؛ باشد که رستگار شوید.');
INSERT INTO "islamhouse" VALUES(22,78,'22:78','و در راه الله ـ چنان که سزاوارِ جهاد [در راه] اوست ـ جهاد کنید. او شما را برگزید و هیچ سختی [و تنگنایی] در دین برایتان قرار نداد. [این آیین سهل و آسان،] آیین پدرتان ابراهیم [است]. او [= الله] پیش از این [در کتاب‌های آسمانیِ گذشته] و در این [قرآن] شما را مسلمان نامید تا پیامبر بر [ابلاغِ پیامش به] شما گواه باشد و شما [نیز] بر مردم گواه باشید [که همۀ پیامبران، پیام الهی را به قومِ خود رسانده‌اند]. پس نماز برپا دارید و زکات بپردازید و به الله پناه ببرید [و بر او توکل کنید] که او [دوست و] کارسازِ شماست؛ و چه نیکو کارساز و چه نیک یاوری است!');
INSERT INTO "islamhouse" VALUES(23,1,'23:1','به راستی که مؤمنان رستگار شدند؛');
INSERT INTO "islamhouse" VALUES(23,2,'23:2','همان کسانی ‌که در نمازشان فروتن هستند؛');
INSERT INTO "islamhouse" VALUES(23,3,'23:3','و آنان ‌که از [گفتار و رفتار] بیهوده رویگردانند؛');
INSERT INTO "islamhouse" VALUES(23,4,'23:4','و آنان ‌که زکات می‌پردازند؛');
INSERT INTO "islamhouse" VALUES(23,5,'23:5','و آنان که شرمگاهشان را حفظ می‌کنند؛');
INSERT INTO "islamhouse" VALUES(23,6,'23:6','مگر در مورد همسران‌ یا کنیزانشان که [در بهره‌گیری جنسی از آنها] نکوهشی بر آنان نیست.');
INSERT INTO "islamhouse" VALUES(23,7,'23:7','اما کسانی که فراتر از این بخواهند، آنان کسانی هستند که از حد درگذشته‌اند.');
INSERT INTO "islamhouse" VALUES(23,8,'23:8','و [در زمرۀ مؤمنانند] کسانی‌ که امانت‌ها و عهد خود را رعایت می‌کنند،');
INSERT INTO "islamhouse" VALUES(23,9,'23:9','و کسانی‌ که بر [ادای] نمازهایشان مراقبت می‌کنند.');
INSERT INTO "islamhouse" VALUES(23,10,'23:10','اینانند که وارثان هستند.');
INSERT INTO "islamhouse" VALUES(23,11,'23:11','[همان] کسانی‌ که بهشت برین را به ارث می‌برند [و] در آن جاودانند.');
INSERT INTO "islamhouse" VALUES(23,12,'23:12','و در حقیقت، انسان را از چکیده‌ای از گِل آفریدیم.');
INSERT INTO "islamhouse" VALUES(23,13,'23:13','سپس او را [به شکل] نطفه‌ای در قرارگاهی استوار [= رحِم] نهادیم.');
INSERT INTO "islamhouse" VALUES(23,14,'23:14','سپس نطفه را [به شکل] خونِ بسته ساختیم و آنگاه خونِ بسته را [به صورت] پاره‌گوشتی ساختیم؛ سپس پاره‌گوشت را [به شکل] استخوان درآوردیم و آنگاه بر استخوان‌ها گوشت پوشاندیم؛ سپس آن [جنین] را آفرینشی دیگر بخشیدیم. [پربرکت و] بزرگوار است الله که نیکوترین آفریننده است.');
INSERT INTO "islamhouse" VALUES(23,15,'23:15','سپس شما [پس از طی مراحل زندگی] قطعاً خواهید مُرد.');
INSERT INTO "islamhouse" VALUES(23,16,'23:16','آنگاه بی‌تردید، در روز قیامت برانگیخته می‌شوید.');
INSERT INTO "islamhouse" VALUES(23,17,'23:17','و به یقین، ما بر فرازتان هفت آسمان آفریدیم و [هرگز] از آفرینش غافل نبوده‌ایم.');
INSERT INTO "islamhouse" VALUES(23,18,'23:18','و از آسمان به ‌اندازه‌ای معیّن [برای نیاز مخلوقات] آبی فرستادیم و آن را در زمین جای دادیم؛ و قطعاً برای از بین بردنش [نیز] کاملاً تواناییم.');
INSERT INTO "islamhouse" VALUES(23,19,'23:19','سپس توسط آن [آب] باغ‌هایی از درختان خرما و انگور برایتان پدید آوردیم که در آن [باغ‌ها،] میوه‌های بسیاری برای شماست و از آن [محصولات] می‌خورید.');
INSERT INTO "islamhouse" VALUES(23,20,'23:20','و درختى [آفریدیم] كه از طور سینا برمى‌آید و روغن [زیتون‌] مى‌دهد و خورشى براى خورندگان است.');
INSERT INTO "islamhouse" VALUES(23,21,'23:21','و بی‌تردید، در [وجود] چهارپایان عبرتى [از قدرت الله] برایتان است: از آنچه در شكم آنهاست به شما [شیر] مى‌نوشانیم و در [وجود] آنها سودهای بسیاری برایتان وجود دارد و از [گوشت] آنها مى‌خورید.');
INSERT INTO "islamhouse" VALUES(23,22,'23:22','[در خشكى] بر آنها و [در آب] بر کشتی‌ها سوار می‌شوید.');
INSERT INTO "islamhouse" VALUES(23,23,'23:23','و بی‌تردید، ما نوح را به سوی قومش فرستادیم؛ [به آنان] گفت: «ای قوم من، الله را عبادت کنید [که] جز او معبودی [به‌حق] ندارید. آیا پروا نمی‌کنید؟»');
INSERT INTO "islamhouse" VALUES(23,24,'23:24','اما اشراف و بزرگان قومش ‌که کفر ورزیده بودند گفتند: «این [مرد کسی] نیست مگر بشری همچون شما [که] می‌خواهد بر شما برتری جوید. اگر الله می‌خواست [رسولی بفرستد]، فرشتگانی [به سویتان] نازل می‌کرد. ما این [سخن] را [که نوح می‌گوید] در میان نیاکان خود نشنیده‌ایم.');
INSERT INTO "islamhouse" VALUES(23,25,'23:25','او مرد دیوانه‌ای بیش نیست؛ پس تا مدتی منتظر باشید [که یا دروغش آشکار گردد و دست از دعوتش بردارد یا مرگش فرارسد]».');
INSERT INTO "islamhouse" VALUES(23,26,'23:26','[نوح] گفت: «پروردگارا، مرا در برابر تکذیب آنان یاری کن».');
INSERT INTO "islamhouse" VALUES(23,27,'23:27','به او وحی کردیدم: «کشتی را تحت نظرِ ما و به وحی ما بساز و هنگامی‌ که فرمان [عذاب] ما فرارسید و [به نشانۀ رسیدنِ طوفان، آب از] تنور جوشید، از هر نوع [حیوان،] یک جفت [نر و ماده] در کشتی سوار کن و خانواده‌ات را [نیز همراه خود ببر] مگر کسانی که پیشتر وعده[ی هلاکت] بر آنان مقرر شده است و دربارۀ [نجاتِ] کسانی ‌که ستم کرده‌اند با من سخن نگو؛ [چرا که] آنان حتماً غرق خواهند شد.');
INSERT INTO "islamhouse" VALUES(23,28,'23:28','هنگامی ‌که تو و همراهانت بر کشتی سوار شدید بگو: "سپاس مخصوص الله است که ما را از [چنگ] گروه ستمکار نجات داد"».');
INSERT INTO "islamhouse" VALUES(23,29,'23:29','و [نیز] بگو: «پروردگارا، ما را در منزل‌گاهی پربرکت فرود آور که تو بهترین فرودآورنده‌ای».');
INSERT INTO "islamhouse" VALUES(23,30,'23:30','بی‌تردید در این [ماجرا] نشانه‌هایی [از قدرت الله در نجات مؤمنان و عذاب کافران] است و یقیناً ما آزمایشگر [ایمان و کفرِ مردم] بوده‌ایم؛');
INSERT INTO "islamhouse" VALUES(23,31,'23:31','آنگاه گروه دیگری را پس از آنان پدید آوردیم؛');
INSERT INTO "islamhouse" VALUES(23,32,'23:32','و پیامبری از خودشان در [میان] آنان فرستادیم [تا بگوید]: «الله را عبادت کنید [که] جز او معبودی [به‌حق] ندارید. آیا پروا نمی‌کنید؟»');
INSERT INTO "islamhouse" VALUES(23,33,'23:33','گروهی از اشراف و بزرگان قومش که کفر ورزیده بودند و دیدار آخرت را دروغ می‌انگاشتند و در زندگی دنیا نعمت و آسایش به آنان داده بودیم، گفتند: «این [مرد کسی] نیست مگر بشری همچون شما؛ از آنچه [شما] می‌خورید [او نیز] می‌خورد و از آنچه که شما می‌نوشید [او نیز] می‌نوشد [پس مزیّتی ندارد که پیامبر باشد].');
INSERT INTO "islamhouse" VALUES(23,34,'23:34','و اگر از بشری همچون خود اطاعت کنید، در آن صورت، یقیناً زیانکارید.');
INSERT INTO "islamhouse" VALUES(23,35,'23:35','آیا او به شما وعده می‌دهد که وقتی مردید و خاک و استخوان شدید، [در قیامت از گور] بیرون آورده می‌شوید؟');
INSERT INTO "islamhouse" VALUES(23,36,'23:36','آنچه به شما وعده می‌دهد، چه دور است، چه دور!');
INSERT INTO "islamhouse" VALUES(23,37,'23:37','[زندگانی] جز همین زندگی دنیای ما نیست؛ و [گروهى‌] مى‌میریم و [و گروهى به جاى آنها] زنده مى‌شویم و هرگز [پس از مرگ] برانگیخته نخواهیم شد.');
INSERT INTO "islamhouse" VALUES(23,38,'23:38','او [کسی] نیست مگر مردی که بر الله دروغ می‌بندد و ما به او ایمان نمی‌آوریم».');
INSERT INTO "islamhouse" VALUES(23,39,'23:39','[پیامبرشان] گفت: «پروردگارا، مرا در برابر تکذیب آنان یاری کن».');
INSERT INTO "islamhouse" VALUES(23,40,'23:40','[الله] فرمود: «پس از اندک زمانی، [از کار خود] سخت پشیمان می‌شوند».');
INSERT INTO "islamhouse" VALUES(23,41,'23:41','پس بانگی [مرگبار] آنان را به‌حق فروگرفت و آنان را [همچون] خس و خاشاک [روی سیل] نمودیم؛ پس قوم ستمکار [از رحمت الله] دور باد!');
INSERT INTO "islamhouse" VALUES(23,42,'23:42','سپس بعد از آنان نسل‌هایی دیگر پدید آوردیم.');
INSERT INTO "islamhouse" VALUES(23,43,'23:43','هیچ امتی نه از اجل [معینِ] خود پیشی می‌گیرد و نه تأخیر می‌کند.');
INSERT INTO "islamhouse" VALUES(23,44,'23:44','سپس پیامبران خود را پیاپی فرستادیم. هر گاه پیامبری برای [هدایت] امتی می‌آمد، او را دروغگو می‌انگاشتند؛ [ما نیز] آنان را یکی پس از دیگری هلاک کردیم و آنان را داستان‌ها[ی عبرت‌آموزی برای آیندگان] قرار دادیم. پس نابود باد قومی که ایمان نمی‌آورند!');
INSERT INTO "islamhouse" VALUES(23,45,'23:45','سپس موسی و برادرش هارون را با آیات [نُه‌گانۀ] خویش و دلایل آشکار فرستادیم');
INSERT INTO "islamhouse" VALUES(23,46,'23:46','به سوی فرعون و اشراف [قوم] او؛ و[لی] آنان گردنکشی کردند و گروهی برتری‌خواه بودند.');
INSERT INTO "islamhouse" VALUES(23,47,'23:47','و گفتند: «آیا به دو انسان مانند خود ایمان بیاوریم، حال‌ آنکه قومشان بردگان ما هستند؟»');
INSERT INTO "islamhouse" VALUES(23,48,'23:48','پس آن دو را دروغگو انگاشتند [و] سرانجام هلاک شدند.');
INSERT INTO "islamhouse" VALUES(23,49,'23:49','و بی‌تردید، به موسی کتاب [تورات] دادیم؛ باشد که [بنی‌اسرائیل] هدایت شوند.');
INSERT INTO "islamhouse" VALUES(23,50,'23:50','و پسر مریم و مادرش را نشانه‌ای [از قدرت الهی] قرار دادیم و آنان را در مکان بلند و همواری که دارای امنیت و آبی [زلال و] جاری بود جای دادیم.');
INSERT INTO "islamhouse" VALUES(23,51,'23:51','ای پیامبران، از [غذاهای] پاکیزه بخورید و کار شایسته انجام دهید. یقیناً من از آنچه انجام می‌دهید آگاهم.');
INSERT INTO "islamhouse" VALUES(23,52,'23:52','و در حقیقت، این دین شما، دینی واحد [به نام اسلام] است و من پروردگارتان هستم؛ پس از من پروا کنید.');
INSERT INTO "islamhouse" VALUES(23,53,'23:53','اما [پیروانشان اختلاف کردند و] در امر [دین]شان میان خود پراکنده و گروه‌گروه شدند [و] هر گروهی به آنچه نزدشان است دلخوشند [چرا که خود را بر حق و دیگران را بر باطل می‌دانند].');
INSERT INTO "islamhouse" VALUES(23,54,'23:54','[ای پیامبر،] آنان را تا وقتی [که عذاب فرامی‌رسد] در جهل و نادانی‌شان رها کن.');
INSERT INTO "islamhouse" VALUES(23,55,'23:55','آیا گمان می‌کنند اموال و فرزندانی که به آنان می‌دهیم [به این هدف است که،]');
INSERT INTO "islamhouse" VALUES(23,56,'23:56','شتابان نیکی‌ها را به آنان برسانیم؟ [نه، هرگز چنین نیست؛] بلکه آنان نمی‌فهمند [که این وسیله امتحانشان است].');
INSERT INTO "islamhouse" VALUES(23,57,'23:57','کسانی ‌که از بیم پروردگارشان ترسانند');
INSERT INTO "islamhouse" VALUES(23,58,'23:58','و کسانی‌ که به آیات پروردگارشان ایمان می‌آورند');
INSERT INTO "islamhouse" VALUES(23,59,'23:59','و کسانی‌ که به پروردگارشان شرک نمی‌ورزند');
INSERT INTO "islamhouse" VALUES(23,60,'23:60','و کسانی ‌که در انجام کارهای خیر می‌کوشند و از اینکه به سوی پروردگار‌ خویش بازمی‌گردند دل‌هایشان هراسان است [از اینکه مبادا کارهای نیکشان قبول نشود].');
INSERT INTO "islamhouse" VALUES(23,61,'23:61','اینان در نیکی‌ها شتاب می‌کنند و در انجام آن [از یکدیگر] پیشی می‌گیرند.');
INSERT INTO "islamhouse" VALUES(23,62,'23:62','و [ما] به هیچ کس جز به ‌اندازۀ توانش تکلیف نمی‌کنیم و نزد ما کتابی است که [دربارۀ اعمال بندگان] به‌حق سخن می‌گوید و به آنان ستم نمی‌شود.');
INSERT INTO "islamhouse" VALUES(23,63,'23:63','[نه،] بلكه دل‌هایشان از این [کتاب‌] در غفلت است و آنان جز این [گناه کفر،] کارهایی [دیگر نیز] انجام مى‌دهند.');
INSERT INTO "islamhouse" VALUES(23,64,'23:64','تا هنگامی ‌که ثروتمندانشان را به عذاب گرفتار کنیم، ناگهان فریادِ زاری سر می‌دهند.');
INSERT INTO "islamhouse" VALUES(23,65,'23:65','[به آنان گفته می‌شود:] «امروز ناله و زاری نکنید [که] هرگز از سوی ما یاری نخواهید شد.');
INSERT INTO "islamhouse" VALUES(23,66,'23:66','بی‌تردید، آیات من پیوسته بر شما خوانده می‌شد؛ ولی [از حق] روی می‌گرداندید.');
INSERT INTO "islamhouse" VALUES(23,67,'23:67','در حالی‌ که در برابرش تکبر می‌کردید [و در مجالس شبانۀ خود] به بدگویی می‌پرداختید.');
INSERT INTO "islamhouse" VALUES(23,68,'23:68','آیا آنان در این گفتار [= قرآن] نیندیشیده‌اند یا [مگر] چیزی برایشان آمده که برای نیاکانشان نیامده است؟');
INSERT INTO "islamhouse" VALUES(23,69,'23:69','یا پیامبرشان را [به خوبی] نشناخته‌اند و [از این روست که] او را انکار می‌کنند؟');
INSERT INTO "islamhouse" VALUES(23,70,'23:70','یا می‌گویند او جنون دارد؟ [نه، چنین نیست؛] بلکه [او دینِ] حق را برایشان آورده است؛ [ولی] بیشتر آنان [از روی حسادت و تعصبی که به باطل خویش دارند] حق را نمی‌پسندند [و از آن به خشم می‌آیند].');
INSERT INTO "islamhouse" VALUES(23,71,'23:71','اگر الله از هوس‌های آنان پیروی کند [و امورِ هستی به میلِ کافران اداره شود]، آسمان‌ها و زمین و هر ‌کس [و هر چه] در آنهاست تباه می‌گردد؛ بلکه پندشان [= قرآن] را برای آنان آورده‌ایم [که مایۀ عزت و شرفشان است]؛ ولی آنان از پندشان رویگردانند.');
INSERT INTO "islamhouse" VALUES(23,72,'23:72','[ای پیامبر،] آیا تو از آنان [برای تبلیغ دین] پاداشی می‌طلبی؟ و حال آنکه پاداش پروردگارت [بسی] بهتر است و او بهترین روزی‌دهنده است.');
INSERT INTO "islamhouse" VALUES(23,73,'23:73','و بی‌تردید، تو آنان را به راهی راست دعوت می‌کنی.');
INSERT INTO "islamhouse" VALUES(23,74,'23:74','کسانی ‌که به آخرت ایمان ندارند قطعاً از این راه [راست،] منحرف شده‌اند.');
INSERT INTO "islamhouse" VALUES(23,75,'23:75','اگر به آنان رحم کنیم و بلا و رنجی را که [از قحطی و گرسنگی] دیده‌اند از آنان بر‌طرف سازیم، بر سرکشی [و گمراهیِ] خویش پافشاری می‌کنند [و همچنان] به بیراهه می‌روند.');
INSERT INTO "islamhouse" VALUES(23,76,'23:76','در حقیقت، [ما] آنان را به عذاب [و بلا] گرفتار ساختیم [تا بیدار شوند]؛ اما در برابر پروردگارشان فروتنی ننمودند و [به درگاهش تضرع و] زاری نکردند.');
INSERT INTO "islamhouse" VALUES(23,77,'23:77','[آنان پیوسته چنین هستند] تا آنگاه‌ که دری از عذابِ سخت بر آنان می‌گشاییم [و چنان گرفتار ‌شوند] که [از همه جا] مأیوس گردند.');
INSERT INTO "islamhouse" VALUES(23,78,'23:78','[ای منکرانِ قیامت،] الله ذاتی است که گوش و چشم و دل برایتان پدید آورد. چه اندک سپاس می‌گزارید!');
INSERT INTO "islamhouse" VALUES(23,79,'23:79','و او تعالی است که شما را در زمین [آفرید و] پراکنده کرد و [روز قیامت] به سوی او جمع می‌شوید.');
INSERT INTO "islamhouse" VALUES(23,80,'23:80','و اوست که زنده می‌کند و می‌میراند و گردشِ شب و روز از [تدبیرِ] اوست. آیا نمی‌اندیشید؟');
INSERT INTO "islamhouse" VALUES(23,81,'23:81','[چنین نکردند؛] بلكه آنان نیز همان [سخنانی را] گفتند كه پیشینیان مى‌گفتند.');
INSERT INTO "islamhouse" VALUES(23,82,'23:82','گفتند: «آیا هنگامی که مردیم و خاک و استخوان شدیم، برانگیخته خواهیم شد؟');
INSERT INTO "islamhouse" VALUES(23,83,'23:83','بی‌تردید، این وعده را پیش از این نیز به ما و نیاکانمان داده‌اند. این [سخن، چیزی] نیست مگر [دروغ‌ها و] افسانه‌های [خیالی] پیشینیان».');
INSERT INTO "islamhouse" VALUES(23,84,'23:84','[ای پیامبر، به منکرانِ معاد] بگو: «اگر می‌دانید، [بگویید] زمین و هر ‌کس در آن است از آنِ کیست؟»');
INSERT INTO "islamhouse" VALUES(23,85,'23:85','خواهند گفت: «[همه] از آنِ الله است». بگو: «پس آیا پند نمی‌گیرید؟»');
INSERT INTO "islamhouse" VALUES(23,86,'23:86','بگو: «پروردگارِ هفت ‌آسمان و پروردگار عرش بزرگ کیست؟»');
INSERT INTO "islamhouse" VALUES(23,87,'23:87','خواهند گفت: «[همه] از آنِ الله است». بگو: «پس آیا [از الله] پروا نمی‌کنید؟»');
INSERT INTO "islamhouse" VALUES(23,88,'23:88','بگو: «اگر می‌دانید، [بگویید] فرمانرواییِ همه چیز به دست کیست؟ و [کیست که] پناه می‌دهد و کسی در برابر [عذابِ] او پناه‌داده نمی‌شود؟');
INSERT INTO "islamhouse" VALUES(23,89,'23:89','خواهند گفت: «[فرمانرواییِ همه چیز،] از آنِ الله است». بگو: «پس چگونه افسون می‌شوید [و نمی‌اندیشید]؟»');
INSERT INTO "islamhouse" VALUES(23,90,'23:90','[چنین نیست که کافران گمان می‌کنند] بلکه ما حق را برایشان آوردیم و یقیناً آنان دروغگو هستند.');
INSERT INTO "islamhouse" VALUES(23,91,'23:91','الله هرگز فرزندی برنگزیده است و هیچ معبودی با او نیست. [اگر چنین بود،] آنگاه هر معبودی آنچه را آفریده بود [با خود] می‌بُرد و مسلماً برخی بر برخی دیگر برتری می‌جستند [و نظامِ هستی تباه می‌گشت]. الله از آنچه [مشرکان] توصیف می‌کنند، منزّه است.');
INSERT INTO "islamhouse" VALUES(23,92,'23:92','او دانای نهان و آشکار است و از چیزهایی که با [وی] شریک می‌سازند برتر است.');
INSERT INTO "islamhouse" VALUES(23,93,'23:93','بگو: «پروردگارا، اگر آن [عذابی] را که به آنان وعده داده می‌شود به من نشان می‌دهی [که شاهدش باشم]،');
INSERT INTO "islamhouse" VALUES(23,94,'23:94','مرا در این [عذاب‌ها، در کنارِ این] گروه ستمکار قرار نده».');
INSERT INTO "islamhouse" VALUES(23,95,'23:95','و قطعاً ما تواناییم که آنچه را به آنان وعده می‌دهیم، به تو نشان دهیم.');
INSERT INTO "islamhouse" VALUES(23,96,'23:96','[ای پیامبر،] بدی را به شیوه‌ای که بهتر است، دفع کن [و ببخش و شکیبا باش]. ما به آنچه [مشرکان دربارۀ تو] توصیف می‌کنند، داناتریم.');
INSERT INTO "islamhouse" VALUES(23,97,'23:97','و بگو: «پروردگارا، من از وسوسه‌های شیاطین به تو پناه می‌برم.');
INSERT INTO "islamhouse" VALUES(23,98,'23:98','و پروردگارا، از اینکه آنها نزد من حاضر شوند [نیز] به تو پناه می‌برم».');
INSERT INTO "islamhouse" VALUES(23,99,'23:99','[کافران پیوسته بر عقیدۀ باطلشان هستند] تا هنگامی ‌که مرگ یکی از آنان فرارسد [آنگاه در حالی که] نزد پروردگار [خود ناله و زاری می‌کند، به فرشتگان] می‌گوید: «مرا [به دنیا] بازگردانید.');
INSERT INTO "islamhouse" VALUES(23,100,'23:100','شاید در آنچه [کوتاهی نموده و] ترک کرده‌ام، کار شایسته‌ای انجام دهم». هرگز چنین نیست. بی‌تردید، این فقط سخنی است که او به زبان می‌گوید [و هرگز رفتارش تغییر نمی‌کند]؛ و تا روزی ‌که برانگیخته شوند، پشت سرشان برزخی است.');
INSERT INTO "islamhouse" VALUES(23,101,'23:101','روزی که در صور دمیده می‌شود، هیچ ‌یک از [افتخارات خانوادگی و] پیوندهای خویشاوندی میانشان [برقرار] نخواهد بود و [از وحشتِ عذاب] از [حال] یکدیگر نمی‌پرسند.');
INSERT INTO "islamhouse" VALUES(23,102,'23:102','و هر کس كفه ميزان [اعمال‌] وی سنگین باشد، آنان رستگارانند.');
INSERT INTO "islamhouse" VALUES(23,103,'23:103','و هر ‌کس كفه ميزان [اعمال‌] وی سبک باشد، آنانند که به خویش زیان زده‌اند [و] در دوزخ جاودانند.');
INSERT INTO "islamhouse" VALUES(23,104,'23:104','آتش چهره‌هایشان را می‌سوزانَد و در آنجا عبوس و زشت‌منظرند.');
INSERT INTO "islamhouse" VALUES(23,105,'23:105','[به آنان گفته می‌شود:] «مگر نه این بود كه آیاتم بر شما خوانده مى‌شد و آنها را دروغ می‌انگاشتید؟»');
INSERT INTO "islamhouse" VALUES(23,106,'23:106','[در پاسخ] می‌گویند: «پروردگارا، بدبختی بر ما چیره شد و گروهی گمراه بودیم.');
INSERT INTO "islamhouse" VALUES(23,107,'23:107','پروردگارا، ما را از این [دوزخ] بیرون آور. اگر [به کفر و نافرمانی] بازگشتیم، قطعاً ستمکاریم».');
INSERT INTO "islamhouse" VALUES(23,108,'23:108','[الله] می‌فرماید: «در آن [آتش] گم شوید و با من سخن نگویید.');
INSERT INTO "islamhouse" VALUES(23,109,'23:109','گروهی از بندگانم می‌گفتند: "پروردگارا، ایمان آوردیم؛ پس ما را ببخشای و بر ما رحم کن [که] بی‌تردید، تو بهترین بخشاینده‌ای"؛');
INSERT INTO "islamhouse" VALUES(23,110,'23:110','ولی شما آنان را مسخره می‌کردید تا جایی که [سرگرم‌شدن به تمسخرِ آنان] ذکر [و عبادتِ] مرا از یادتان برد و همچنان به آنان می‌خندیدید.');
INSERT INTO "islamhouse" VALUES(23,111,'23:111','امروز من آنان را از آن رو كه شكیبایى كردند پاداش مى‌دهم. آرى، ایشانند كه رستگارانند».');
INSERT INTO "islamhouse" VALUES(23,112,'23:112','[الله به دوزخیان] می‌فرماید: «چند سال در زمین به سر بردید؟»');
INSERT INTO "islamhouse" VALUES(23,113,'23:113','می‌گویند: «یک روز، یا بخشی از یک روز را به سر بردیم. از حسابگران [که حساب روز و سال را نگه می‌دارند] بپرس».');
INSERT INTO "islamhouse" VALUES(23,114,'23:114','[الله] می‌فرماید: «[آری،] اگر می‌دانستید، جز اندکی [در دنیا] به سر نبردید».');
INSERT INTO "islamhouse" VALUES(23,115,'23:115','آیا پنداشته‌اید که شما را بیهوده آفریده‌ایم و به سوی ما بازگردانده نمی‌شوید؟');
INSERT INTO "islamhouse" VALUES(23,116,'23:116','بلندمرتبه [و بزرگ] است الله [که] فرمانروای حق است. هیچ معبودی [به‌حق] جز او نیست [و] پروردگارِ عرشِ گرانقدر است.');
INSERT INTO "islamhouse" VALUES(23,117,'23:117','و هرکس معبود دیگری با الله بخواند، یقیناً هیچ دلیلی بر [حقانیتِ] آن نخواهد داشت؛ و حسابِ او با پروردگارش خواهد بود. بی‌تردید، کافران رستگار نمی‌شوند.');
INSERT INTO "islamhouse" VALUES(23,118,'23:118','و [ای پیامبر،] بگو: «پروردگارا، [گناهانم را] ببخشای و رحم کن و تو بهترین بخشنده‌ای».');
INSERT INTO "islamhouse" VALUES(24,1,'24:1','[این] سوره‌ای است که آن را نازل کرده‌ایم و [عمل به احکام] آن را واجب نموده‌ایم و آیات روشنی در آن نازل کرده‌ایم؛ باشد که پند گیرید.');
INSERT INTO "islamhouse" VALUES(24,2,'24:2','هر یک از زن و مرد زناکار را صد تازیانه بزنید و اگر به الله و روز آخرت ایمان دارید، نباید در [اجرای احکام] دین الله نسبت به آن دو [نفر] دلسوزی [و مهربانیِ بی‌دلیل] داشته باشید؛ و باید گروهی از مؤمنان بر مجازاتشان گواه باشند.');
INSERT INTO "islamhouse" VALUES(24,3,'24:3','مرد زناکار جز با زن زناکار یا مشرک ازدواج نمی‌کند و زن زناکار را [نیز] جز مرد زناکار یا مشرک به ازدواج خود درنمی‌آورد؛ و این [ازدواج] بر مؤمنان حرام است.');
INSERT INTO "islamhouse" VALUES(24,4,'24:4','و کسانی ‌که [مردان و] زنان پاکدامن را [به زنا] متهم می‌کنند و چهار شاهد [بر ادعای خود] نمی‌آورند، آنان را هشتاد تازیانه بزنید و هرگز شهادتشان را [در هیچ موردی] نپذیرید؛ و اینانند که نافرمانند.');
INSERT INTO "islamhouse" VALUES(24,5,'24:5','مگر کسانی ‌که بعد از آن [تهمت،] توبه کنند و [رفتار گذشتۀ خود را] اصلاح [و جبران] نمایند؛ بی‌تردید، الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(24,6,'24:6','و کسانی‌ که همسران خود را [به زنا] متهم می‌کنند و شاهدی جز خویشتن [بر این ادعا] ندارند، هر یک از آنان باید چهار بار به الله سوگند یاد کند که قطعاً راست می‌گوید.');
INSERT INTO "islamhouse" VALUES(24,7,'24:7','و پنجمین بار [شوهر بگوید] که لعنت الله بر او باد اگر دروغگو باشد.');
INSERT INTO "islamhouse" VALUES(24,8,'24:8','و کیفرِ زن در صورتی برداشته می‌شود که چهار بار به الله سوگند یاد کند که آن مرد قطعاً دروغگوست.');
INSERT INTO "islamhouse" VALUES(24,9,'24:9','و پنجمین بار [بگوید] که خشم الله بر او باد اگر [شوهرش] راست گفته باشد.');
INSERT INTO "islamhouse" VALUES(24,10,'24:10','و [ای مردم،] اگر بخشش و رحمت الله بر شما نبود و اینکه الله توبه‌پذیرِ حکیم است، [قطعاً گرفتار عذاب می‌شدید].');
INSERT INTO "islamhouse" VALUES(24,11,'24:11','[ای مؤمنان،] بی‌تردید کسانی ‌که آن تهمت بزرگ را [دربارۀ مادر مؤمنان، عایشه] مطرح کردند، گروهی از خود شما هستند. گمان نکنید که این [ماجرا] برای شما شرّ است؛ بلکه خیری برایتان در آن [نهفته] است. برای هر یک از آنان [که در این شایعه‌پراکنی نقش داشته] کیفر گناهی است که مرتکب شده است؛ و برای آن کسی از آنان که بخش بزرگ آن [تهمت] را به عهده دارد [= عبدالله بن اُبَیّ بن سلول]، عذاب بزرگی [در پیش] است.');
INSERT INTO "islamhouse" VALUES(24,12,'24:12','چرا هنگامی‌ که این [بهتان] را شنیدید، مردان و زنان مؤمن گمان نیک به خود نبردند و نگفتند: «این تهمتی [بزرگ و] آشکار [به همسرِ پیامبر] است»؟');
INSERT INTO "islamhouse" VALUES(24,13,'24:13','چرا چهار شاهد بر آن نیاوردند؟ پس چون شاهدان را نیاوردند، آنانند که نزد الله دروغگو هستند.');
INSERT INTO "islamhouse" VALUES(24,14,'24:14','و اگر بخشش و رحمت الله در دنیا و آخرت نبود، قطعاً به کیفرِ آنچه به آن پرداختید، به عذابی [سخت و] بزرگ گرفتار می‌شدید.');
INSERT INTO "islamhouse" VALUES(24,15,'24:15','آنگاه ‌که آن [شایعه] را از زبان یکدیگر می‌شنیدید و سخنی را ‌دهان‌به‌دهان نقل می‌کردید که هیچ آگاهی [و یقینی] دربارۀ آن نداشتید و آن را ساده [و کوچک] می‌پنداشتید؛ در حالی‌ که آن [تهمت،] نزد الله بسیار بزرگ است.');
INSERT INTO "islamhouse" VALUES(24,16,'24:16','چرا هنگامی‌ که آن را شنیدید نگفتید: «سزاوار نیست که این [سخن وقیح] را بر زبان آوریم. [پروردگارا،] تو منزّهی؛ این دروغ بزرگی است؟»');
INSERT INTO "islamhouse" VALUES(24,17,'24:17','الله شما را پند می‌دهد که اگر مؤمن هستید، هرگز این [کار] را تکرار نکنید.');
INSERT INTO "islamhouse" VALUES(24,18,'24:18','و الله آیات [خود] را برای شما بیان می‌کند و الله دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(24,19,'24:19','کسانی ‌که دوست دارند زشتکاری در میان مؤمنان رایج گردد، قطعاً در دنیا و آخرت عذابی دردناک [در پیش] دارند؛ و الله [حقایق و مصالح را] می‌داند و شما نمی‌دانید.');
INSERT INTO "islamhouse" VALUES(24,20,'24:20','و اگر بخشش و رحمت الله بر شما نبود و اینکه الله دلسوز مهربان است، [قطعاً مجازات سختی در انتظارتان بود].');
INSERT INTO "islamhouse" VALUES(24,21,'24:21','ای کسانی‌ که ایمان آورده‌اید، در راه‌های شیطان گام نگذارید؛ و هرکس راه‌های شیطان را می‌پوید، [بداند که] او تنها به زشتکاری و اعمال ناپسند فرمان می‌دهد؛ و اگر بخشش و رحمت الله بر شما نبود، هرگز هیچ یک از شما [از گناهان] پاک نمی‌شد؛ ولی الله هر کس را که بخواهد پاک می‌کند؛ و الله شنوای داناست.');
INSERT INTO "islamhouse" VALUES(24,22,'24:22','افرادی از شما که برتری [ایمانی] و وسعت [مالی] دارند، نباید سوگند یاد کنند که به خویشاوندان و نیازمندان و مهاجران در راه الله [که مرتکب گناه شده‌اند] چیزی ندهند. آنان باید عفو کنند و [از گناه دیگران] چشم بپوشند. آیا دوست ندارید که الله [نیز] شما را ببخشد؟ و الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(24,23,'24:23','کسانی ‌که زنان پاکدامن بی‌خبر [از فحشا و] مؤمن را [به زنا] متهم می‌کنند، در دنیا و آخرت از رحمت الله دور گشته‌اند؛ و [در آخرت] عذابی بزرگ [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(24,24,'24:24','روزی‌ که زبان‌ها و دست‌ها و پاهایشان دربارۀ آنچه می‌کردند [و می‌گفتند] علیه آنان گواهی می‌دهند.');
INSERT INTO "islamhouse" VALUES(24,25,'24:25','آن روز، الله جزای واقعی آنان را بی‌کم‌و‌کاست می‌دهد و خواهند دانست که الله همان حق آشکار است.');
INSERT INTO "islamhouse" VALUES(24,26,'24:26','زنان پلید برای مردانِ پلیدند و مردان پلید [نیز] برای زنانِ پلیدند؛ و زنان پاک از آنِ مردان پاکند و مردان پاک [نیز] از آنِ زنان پاکند. اینان از آنچه [ناپاکان درباره‌شان] می‌گویند، مبرّا هستند [و] آمرزش [الهی] و روزیِ ارزشمندی [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(24,27,'24:27','ای کسانی ‌که ایمان آورده‌اید، به خانه‌هایی جز خانه‌های خود وارد نشوید؛ مگر اینکه اجازه بگیرید و بر اهل آن [خانه] سلام کنید. این [کار،] برای شما بهتر است؛ باشد که پند گیرید.');
INSERT INTO "islamhouse" VALUES(24,28,'24:28','اگر کسی را در آن [خانه] نیافتید، وارد نشوید تا به شما اجازه داده شود؛ و اگر به شما گفته شد: «برگردید»، پس برگردید؛ [چرا که] این [کسب اجازه،] برای شما شایسته‌تر است؛ و الله به آنچه می‌کنید داناست.');
INSERT INTO "islamhouse" VALUES(24,29,'24:29','بر شما گناهی نیست که به خانه‌های غیرمسکونی [و مکان‌های عمومی] که در آنجا [احتیاج یا] بهره‌ای دارید وارد شوید؛ و الله آنچه آشکار می‌سازید و آنچه پنهان می‌دارید [همه را] می‌داند.');
INSERT INTO "islamhouse" VALUES(24,30,'24:30','[ای پیامبر،] به مردان مؤمن بگو چشمان خود را [از نگاه به نامحرم] فروگیرند و پاکدامنی پیشه کنند. این [کار] برای آنان پاکیز‌ه‌تر است. بی‌تردید، الله به آنچه انجام می‌دهند آگاه است.');
INSERT INTO "islamhouse" VALUES(24,31,'24:31','و به زنان مؤمن [نیز] بگو چشمان خود را [از نگاه به نامحرم] فروگیرند و پاکدامنی ورزند و زیور [و آرایشِ] خود را آشکار نکنند؛ مگر آنچه [به صورت طبیعی] نمایان است؛ و باید روسری خود را بر سینه بیفکنند [تا موها و چهره‌ها و سینه‌هایشان پوشیده گردد] و زینت خود را آشکار نسازند، مگر برای شوهر یا پدر یا پدرشوهر یا پسرِ خود یا پسر شوهرشان یا برادر یا پسر برادر یا پسر خواهر یا زنان [همکیش] خود یا کنیزان و بردگانشان یا خویشاوندانی که [دیگر،] نیاز و تمایلی به زنان ندارند یا کودکانی‌ که از مسایل جنسیِ زنان آگاهی نیافته‌اند؛ همچنین [هنگام راه رفتن] پاها‌ی خود را [چنان بر زمین] نکوبند که [صدای] زینت‌هایی که [زیر لباس] پنهان کرده‌اند [= خلخال] آشکار شود؛ و ای مؤمنان، همگی به سوی الله توبه کنید؛ باشد که رستگار گردید.');
INSERT INTO "islamhouse" VALUES(24,32,'24:32','[ای مؤمنان،] مردان و زنان مجردِ خود را همسر دهید و غلامان و کنیزانتان را که شایستگی دارند [نیز همسر دهید]. اگر آنان فقیر [و تنگدست] باشند، الله از بخشش [و لطفِ] خویش بی‌نیازشان می‌سازد؛ و الله گشایشگرِ آگاه است.');
INSERT INTO "islamhouse" VALUES(24,33,'24:33','کسانی ‌که [شرایط و امکانات] ازدواج ندارند، باید پاکدامنی پیشه کنند تا الله آنان را از بخششِ خویش بی‌نیاز نماید؛ و بردگانی که خواستار بازخرید [و آزادی خویش] هستند، اگر [توانایى پرداخت مال و‌] شایستگی در آنان مى‌بینید، [درخواستشان را] بپذیرید و از ثروتی که الله به شما عطا کرده است، [مقداری] به آنان ببخشید [تا به آزادى‌شان کمک کنید]. همچنین کنیزانتان را که می‌خواهند پاکدامن باشند، به زنا وادار نکنید تا بهرۀ [ناچیز] زندگی دنیا را به دست آورید؛ و اگر کسی ‌آنان را [به این کار] وادار نماید، الله پس از مجبورشدنشان قطعاً [نسبت به آنان] آمرزنده [و] مهربان است.');
INSERT INTO "islamhouse" VALUES(24,34,'24:34','و به راستی، آیاتی روشنگر و داستانی از زندگیِ پیشینیانتان و پندی برای پرهیزگاران به سوی شما نازل کرده‌ایم.');
INSERT INTO "islamhouse" VALUES(24,35,'24:35','الله نور آسمان‌ها و زمین است. مَثلِ نور او همانند چراغدانی است که درونش چراغی [افروخته] است [و] آن چراغ در شیشه‌ای قرار دارد [و] آن شیشه گویی ستاره‌ای درخشان [و روشنایی‌بخش] است. این چراغ، با [روغن] درخت پربرکت زیتون افروخته می‌شود که نه شرقی است و نه غربی [و خورشید همواره بر آن می‌تابد]. روغنش [چنان زلال است که] نزدیک است بدون تماس با آتش شعله‌ور گردد. نوری است افزون بر نور. [قلب مؤمن نیز با نور هدایت این‌گونه روشن می‌شود] و الله هر ‌کس را بخواهد به نور خویش هدایت می‌کند؛ و الله برای مردم مَثل‌ها می‌زند؛ و الله به همه چیز داناست.');
INSERT INTO "islamhouse" VALUES(24,36,'24:36','[این چراغ هدایت،] در مساجدی است که الله امر کرده است تا [قدر و منزلتشان] گرامی داشته شود و نامش در آنها برده شود [و] در آنجا بامدادان و شامگاهان او را به پاکی بستایند.');
INSERT INTO "islamhouse" VALUES(24,37,'24:37','مردانی‌ که هیچ تجارت و خرید و فروشی آنان را از یاد الله و برپا داشتن نماز و پرداخت زکات غافل نمی‌کند [و] از روزی که دل‌ها و چشم‌ها در آن دگرگون می‌شود بیم دارند.');
INSERT INTO "islamhouse" VALUES(24,38,'24:38','تا الله بر اساس نیکوترین کردارشان به آنان پاداش دهد و از فضل خود [نیز] بر پاداششان بیفزاید و الله به هر ‌کس که بخواهد، بی‌حساب روزی می‌دهد.');
INSERT INTO "islamhouse" VALUES(24,39,'24:39','و کسانی ‌که کفر ورزیدند، اعمالشان همچون سرابی در بیابانی هموار است که [از دور، انسانِ] تشنه آن را آب می‌پندارد تا آنکه نزدیکش می‌شود [و] چیزی نمی‌یابد و [اعمال کافر نیز سرابی است که سودی برایش ندارد و در عوض،] الله را نزد خویش می‌یابد که حسابش را به طور کامل می‌دهد؛ و الله در حسابرسی سریع است.');
INSERT INTO "islamhouse" VALUES(24,40,'24:40','یا [اعمالش] همچون تاریکی‌هایی در دریای ژرف است که موجی آن را می‌پوشانَد و بر فرازِ آن، موجی دیگر است [و] بر فرازش ابری [تاریک] قرار دارد. تاریکی‌هایی است یکی بر فراز دیگری؛ [چنان که] اگر [شخصِ گم‌گشته] دست خود را بیرون آورد، ممکن نیست آن را ببیند. و کسی که الله نوری [از هدایت] برایش قرار نداده باشد، [هیچ هدایت و] نوری برایش نیست.');
INSERT INTO "islamhouse" VALUES(24,41,'24:41','آیا ندیدی هر كه [و هر چه‌] در آسمان‌ها و زمین است و [حتی] پرندگانِ پرگشوده [در آسمان، همگی] الله را به پاکی می‌ستایند. هر یک از آنان نیایش و ستایش خویش را می‌داند و الله به آنچه می‌کنند آگاه است.');
INSERT INTO "islamhouse" VALUES(24,42,'24:42','و فرمانرواییِ آسمان‌ها و زمین از آنِ الله است و بازگشت [همه] به سوی الله است.');
INSERT INTO "islamhouse" VALUES(24,43,'24:43','آیا ندیدی که الله ابرهایی را به آرامی می‌رانَد، سپس بین [اجزای] آن پیوند می‌دهد، آنگاه تودۀ انبوهى پدید مى‌آورَد، پس قطره‌های باران را می‌بینی که از میانش تراوش می‌کند؛ و از کوه‌هایی [از انبوه ابرها] كه در آسمان است دانه‌های تگرگ فرومی‌بارد؛ آنگاه آن را به هر کس که بخواهد فرومی‌ریزد و از هر کس ‌که بخواهد بازمی‌دارد. نزدیک است درخشندگیِ برقش چشم‌ها را [از بین] ببرد.');
INSERT INTO "islamhouse" VALUES(24,44,'24:44','الله شب و روز را می‌گردانَد [و پیاپی می‌آورد]. یقیناً در این [گردش،] عبرتی برای اهل بصیرت است.');
INSERT INTO "islamhouse" VALUES(24,45,'24:45','و الله هر موجود زنده‌ای را [ابتدا] از آب آفرید؛ برخی از آنها بر شکم خود می‌روند و برخی بر دو پا راه می‌روند و برخی از آنها بر چهار [پا] راه می‌روند. الله هر چه بخواهد می‌آفریند. بی‌تردید، الله بر همه چیز تواناست.');
INSERT INTO "islamhouse" VALUES(24,46,'24:46','به راستی که [ما] آیات روشنگری نازل کردیم و الله هر ‌کس را بخواهد به راه راست هدایت می‌کند.');
INSERT INTO "islamhouse" VALUES(24,47,'24:47','و [منافقان] می‌گویند: «به الله و پیامبر [او] ایمان آوردیم و [از فرمانشان] اطاعت کردیم»؛ اما پس از این [ادعا]، گروهی از آنان رویگردان می‌شوند و اینان [در حقیقت] مؤمن نیستند.');
INSERT INTO "islamhouse" VALUES(24,48,'24:48','و هنگامی ‌که به سوی الله و پیامبرش فرا‌خوانده می‌شوند تا در میانشان داوری کند، ناگاه گروهی از آنان رویگردان می‌شوند.');
INSERT INTO "islamhouse" VALUES(24,49,'24:49','و اگر حق با آنان باشد، مطیعانه [و فروتنانه] به سوی او می‌آیند.');
INSERT INTO "islamhouse" VALUES(24,50,'24:50','آیا در دل‌هایشان بیماری [نفاق] است یا [دربارۀ رسالتش] تردید دارند؟ یا می‌ترسند که الله و پیامبرش بر آنان ستم کنند؟ [نه،] بلکه آنان خود ستمکارند.');
INSERT INTO "islamhouse" VALUES(24,51,'24:51','سخن مؤمنان ـ هنگامی ‌که به سوی الله و پیامبرش فراخوانده می‌شوند تا میانشان داوری کند ـ فقط این است که می‌گویند: «شنیدیم و اطاعت کردیم»؛ و اینان همان رستگارانند.');
INSERT INTO "islamhouse" VALUES(24,52,'24:52','و هر ‌کس از الله و پیامبرش اطاعت کند و از الله بترسد و از [نافرمانیِ] او بپرهیزد، اینان کامیابند.');
INSERT INTO "islamhouse" VALUES(24,53,'24:53','[منافقان] با سخت‌ترین سوگندهایشان به الله سوگند یاد کردند که اگر به آنان فرمان دهی، [برای جهاد] رهسپار می‌شوند. بگو: «سوگند یاد نکنید. اطاعت [و سخنان دروغتان] شناخته‌شده است. بی‌تردید، الله از آنچه می‌کنید آگاه است».');
INSERT INTO "islamhouse" VALUES(24,54,'24:54','بگو: «از الله و پیامبر اطاعت کنید؛ اگر سرپیچی کردید، پیامبر مسئول چیزى است كه بر او تكلیف شده [= ابلاغِ وحی] و شما نیز مسئول چیزى هستید كه به آن تكلیف شده‌اید؛ اما اگر از او اطاعت كنید، هدایت می‌شوید؛ و پیامبر وظیفه‌ای جز رساندن آشكار [وحی] ندارد».');
INSERT INTO "islamhouse" VALUES(24,55,'24:55','الله به افرادی از شما که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، وعده داده است که قطعاً آنان را در زمین جانشین [مشرکان] می‌سازد؛ همان ‌گونه که کسانی را که پیش از آنان بودند [نیز] جانشین [کافران و ستمگران] نمود؛ و [فرموده است] دینشان را که برای آنان پسندیده است، حتما برایشان استوار [و پیروزمند] می‌سازد و یقینا ترسشان را به آرامش و امنیت تبدیل می‌کند؛ [چرا که] تنها مرا عبادت می‌کنند و چیزی را با من شریک نمی‌سازند؛ و هر کس از این پس کفر بورزد، اینانند که نافرمانند.');
INSERT INTO "islamhouse" VALUES(24,56,'24:56','و نماز برپا دارید و زکات بپردازید و از پیامبر اطاعت کنید؛ باشد که مورد رحمت قرار گیرید.');
INSERT INTO "islamhouse" VALUES(24,57,'24:57','[ای پیامبر،] هرگز مپندار کسانی که کفر ورزیدند، ناتوان‌کننده[ی الله] در زمینند [و می‌توانند از مجازات الهی بگریزند]. جایگاه اینان آتش است و چه بد بازگشتگاهی است!');
INSERT INTO "islamhouse" VALUES(24,58,'24:58','ای کسانی ‌که ایمان آورده‌اید، بردگانتان و کسانی از [کودکان] شما که به [سن] بلوغ نرسیده‌اند، باید در سه وقت از شما اجازه [ورود] بگیرند: پیش از نماز صبح و نیمروز ـ هنگامی که لباس‌هایتان را بیرون می‌آورید ـ و پس از نماز عشاء. [این] سه [وقت،] هنگام خلوتِ شماست. جز این [سه وقت،] بر شما و بر آنان هیچ گناهی نیست [که بدون اجازه وارد شوند؛ چرا که] آنان پیرامونتان در رفت و آمدند [و] با یکدیگر [معاشرت می‌کنید]. الله اینچنین آیات [خود] را برایتان بیان می‌کند؛ و الله دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(24,59,'24:59','و هنگامی ‌که کودکانتان به [سن] بلوغ رسیدند، باید [برای ورود به خانه‌های دیگران] اجازه بگیرند؛ همانگونه که اشخاصی که پیش از آنان [به سنّ بلوغ رسیده بودند]، اجازه می‌گرفتند. الله اینچنین آیاتش را برایتان بیان می‌کند؛ و الله دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(24,60,'24:60','و بر زنان [یائسه و] از کار افتاده‌ای که [دیگر میل و] امید زناشویی ندارند، گناهی نیست که پوشش [سر و صورتِ] خویش را کنار گذارند، [به شرط آنکه آرایش و] زینتی را آشکار نکنند؛ و اگر عفت بورزند [و خود را بپوشانند] برایشان بهتر است؛ و الله شنوای داناست.');
INSERT INTO "islamhouse" VALUES(24,61,'24:61','بر [افراد] نابینا و لنگ و بیمار [که از تکالیف اجتماعی ـ همچون جهاد ـ معاف شده‌اند] گناهی نیست و [همچنین] بر خود شما گناهی نیست که از خانه‌های خود [یا فرزندان و همسرانتان، غذایی] بخورید یا از خانه‌های پدرانتان یا خانه‌های مادرانتان یا خانه‌های برادرانتان یا خانه‌های خواهرانتان یا خانه‌های عمو‌هایتان یا خانه‌های عمه‌هایتان یا خانه‌های دایی‌هایتان یا خانه‌های خاله‌هایتان یا خانه‌ای که کلیدش در اختیار شماست یا [از خانه‌های] دوستانتان. [نیز] بر شما گناهی نیست که دسته‌جمعی یا جداگانه [غذا] بخورید؛ و هنگامی‌ که به خانه‌ها وارد می‌شوید، به یکدیگر سلام کنید که درودی مبارک [و] پسندیده از سوی الله است. الله این گونه آیات [خویش] را برایتان به روشنی بیان می‌کند؛ باشد که بیندیشید.');
INSERT INTO "islamhouse" VALUES(24,62,'24:62','مؤمنان [واقعی] کسانی هستند که به الله و پیامبرش ایمان آورده‌اند و هنگامی‌ که برای [اجرای یک] امر اجتماعی با او [= پیامبر] همراه هستند، تا از وی اجازه نگرفته‌اند، [از جمع] خارج نمی‌شوند. بی‌تردید، کسانی ‌که از تو اجازه می‌گیرند، آنان کسانی هستند که به الله و پیامبرش ایمان آورده‌اند. پس اگر برای انجام برخی کارهای [مهم] خود از تو اجازه می‌خواهند، به هر ‌یک از آنان که می‌خواهی اجازه بده و از الله برایشان آمرزش بخواه. به راستی که الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(24,63,'24:63','[ای مؤمنان] آنگونه که یکدیگر را [با نام خودتان یا با نام پدرانتان] صدا می‌زنید، پیامبر را [با نام خودش یا با نام پدرش] صدا نزنید [نگویید: ای محمد، ای پسر عبدالله، بلکه بگویید: ای رسول الله، ای نبی الله]. یقیناً الله افرادی از شما را که پشت دیگران پنهان می‌شوند و مخفیانه [از نزد پیامبر] می‌گریزند [به خوبی] می‌شناسد. پس کسانی ‌که از فرمانش سرپیچی می‌کنند، باید بترسند از اینکه اندوه و بلایی بر سرشان بیاید یا به عذابی دردناک گرفتار شوند.');
INSERT INTO "islamhouse" VALUES(24,64,'24:64','آگاه باشید! بی‌تردید، آنچه در آسمان‌ها و زمین است از آنِ الله است و یقیناً او می‌داند که شما [بر چه کار و] در چه حالى هستید؛ و روزی‌ که [بندگان] به سویش بازگردانده ‌می‌شوند، آنان را از آنچه [در دنیا] کرده‌اند آگاه می‌سازد؛ و الله به همه چیز داناست.');
INSERT INTO "islamhouse" VALUES(25,1,'25:1','پربرکت [و بزرگوار] است ذاتی‌ که قرآن را [که جداکنندۀ حق و باطل است] بر بنده‌اش نازل کرد تا بیم‌دهندۀ جهانیان باشد.');
INSERT INTO "islamhouse" VALUES(25,2,'25:2','همان ذاتی که فرمانروایی آسمان‌ها و زمین از آنِ اوست و فرزندی [برای خویش] برنگزیده است و در فرمانروایی، هیچ شریکی ندارد و همه چیز را آفریده است و اندازۀ هر چیز را ـ چنان که مناسبش می‌باشدـ تدبیر نموده است.');
INSERT INTO "islamhouse" VALUES(25,3,'25:3','و [مشرکان] به جای او [= الله] معبودانی برگزیده‌اند که چیزی نمی‌آفرینند و خود‌ آفریده می‌شوند و مالک هیچ زیان و سودی برای خود نیستند و [نیز] مالک مرگ و زندگی و [قادر به] برانگیختن [مردگان] نیستند.');
INSERT INTO "islamhouse" VALUES(25,4,'25:4','و کسانی که کفر ورزیدند گفتند: «این [قرآن] جز دروغی نیست که [محمد خودش] بافته است و گروهی دیگر او را بر این [کار] یاری داده‌اند». حقا که آنان [با این سخن‌،] به ستم و دروغ بزرگی روی آورده‌اند.');
INSERT INTO "islamhouse" VALUES(25,5,'25:5','و [کافران] گفتند: «[این قرآن، همان] افسانه‌های [خیالی] پیشینیان است که [محمد] آن را رو‌نویسی کرده است و هر بامداد و شامگاه بر او املا می‌شود».');
INSERT INTO "islamhouse" VALUES(25,6,'25:6','بگو: «ذاتی آن را نازل کرده است که راز آسمان‌ها و زمین را می‌داند. بی‌تردید، او همواره آمرزنده [و] مهربان است.');
INSERT INTO "islamhouse" VALUES(25,7,'25:7','و [مشرکان] گفتند: «این چگونه پیامبری است که [همچون مردم عادی] غذا می‌خورد و در بازار‌ها راه می‌رود؟ چرا فرشته‌ای بر او نازل نشده است تا [یاری‌اش کند و] همراه او هشدار‌دهنده باشد؟');
INSERT INTO "islamhouse" VALUES(25,8,'25:8','یا چرا گنجی [از آسمان] بر او فرود نمی‌آید و یا چرا باغی ندارد که از [میوه‌های] آن بخورد؟» ستمکاران [مشرک] گفتند: «شما جز از مردى جادوشده پیروى نمى‌كنید».');
INSERT INTO "islamhouse" VALUES(25,9,'25:9','بنگر که چگونه برایت مَثل‌ها زدند و گمراه شدند! چنان که نمی‌توانند راهی [به سوی حقیقت] بیابند.');
INSERT INTO "islamhouse" VALUES(25,10,'25:10','پربرکت [و بزرگوار] است ذاتی که اگر بخواهد، برایت بهتر از این قرار می‌دهد: باغ‌هایی که جویبارها از زیر [درختان] آن جاری است و [حتی] کاخ‌هایی [باشکوه] برایت پدید می‌آورد.');
INSERT INTO "islamhouse" VALUES(25,11,'25:11','[مشرکان با این سخنان در پیِ حق نرفتند؛] بلکه قیامت را تکذیب کردند و [ما] برای هر کس که قیامت را دروغ انگارد، آتش سوزان [دوزخ را] مهیّا کرده‌ایم.');
INSERT INTO "islamhouse" VALUES(25,12,'25:12','هنگامی که [دوزخ] آنان را از دوردست می‌بیند آوای خشم و خروشی [وحشتناک] از آن می‌شنوند.');
INSERT INTO "islamhouse" VALUES(25,13,'25:13','و چون دست‌وپابسته [با غُل و زنجیر] در جاى تنگى از آن [= دوزخ]، انداخته ‌شوند، آنجاست كه مرگ [خود] را مى‌خواهند.');
INSERT INTO "islamhouse" VALUES(25,14,'25:14','[به آنان گفته می‌شود:] «امروز نه یک بار، بلكه بارها درخواست مرگ كنید».');
INSERT INTO "islamhouse" VALUES(25,15,'25:15','[ای پیامبر، به کافران] بگو: «این [عذاب دردناک] بهتر است یا بهشت جاودانى كه به پرهیزکاران وعده داده شده و پاداش و سرانجام آنان است؟»');
INSERT INTO "islamhouse" VALUES(25,16,'25:16','در آنجا هر چه بخواهند، برایشان [فراهم] است [و] در آن جاودانند. این وعده‌ای است بر [عهدۀ] پروردگارت که [بندگان پرهیزگار، انجامش را] از او خواسته‌اند.');
INSERT INTO "islamhouse" VALUES(25,17,'25:17','و روزی که [همۀ] آنان و آنچه را که به جای الله عبادت می‌کردند جمع می‌کند و [به معبودان باطل] می‌فرماید: «آیا شما این بندگانم را گمراه کردید یا خود، راه را گم کردند؟»');
INSERT INTO "islamhouse" VALUES(25,18,'25:18','آنان می‌گویند: «تو منزّهی؛ شایستۀ ما نبود که به جای تو [دوستان و] کارسازانی برگزینیم؛ ولی تو آنان و پدرانشان را [از نعمت‌ها] بهره‌مند ساختی تا اینکه یاد [تو] را فراموش کردند و [به این خاطر،] گروهی هلاک‌شده گشتند».');
INSERT INTO "islamhouse" VALUES(25,19,'25:19','[الله به مشرکان می‌فرماید:] «بی‌تردید، [معبودانتان] ادعای شما را دروغ شمردند. [اکنون] نه می‌توانید [عذاب را از خود] دور کنید و نه [از کسی] یاری بجویید؛ و هر ‌یک از شما که [شرک بورزد و به خود] ستم كند، عذابی بزرگ به وی می‌چشانیم».');
INSERT INTO "islamhouse" VALUES(25,20,'25:20','[ای پیامبر،] پیش از تو پیامبران را نفرستادیم، مگر اینکه آنان [نیز] غذا می‌خوردند و [همچون سایر انسان‌ها] در بازارها راه می‌رفتند؛ و ما برخی از شما را وسیلۀ آزمایش برخی دیگر ساختیم [تا روشن شود] آیا شکیبایی می‌کنید؛ و پروردگارت همواره بیناست.');
INSERT INTO "islamhouse" VALUES(25,21,'25:21','کسانی ‌که به دیدار ما [در آخرت] امید ندارند گفتند: «چرا فرشتگانی بر ما نازل نشده‌اند یا [چرا] پروردگار‌مان را نمی‌بینیم؟» بی‌تردید، آنان خود را بزرگ پنداشتند و سرکشیِ بزرگی کردند.');
INSERT INTO "islamhouse" VALUES(25,22,'25:22','روزی ‌که [کافران] فرشتگان را می‌بینند، هیچ بشارتی برای مجرمان نخواهد بود [بلکه روز کیفر و عذاب است]؛ و [فرشتگان] می‌گویند: «[بهشت] بر شما ممنوع [و حرام] است».');
INSERT INTO "islamhouse" VALUES(25,23,'25:23','و به [بررسى] کارهایشان مى‌پردازیم و همه را [همچون] غبارى پراكنده مى‌سازیم.');
INSERT INTO "islamhouse" VALUES(25,24,'25:24','در آن روز، بهشتیان بهترین جایگاه و نیكوترین استراحتگاه را دارند.');
INSERT INTO "islamhouse" VALUES(25,25,'25:25','و [یاد کن از] روزی که آسمان با ابرهای سپید شکافته می‌شود و فرشتگان [پیاپی به سرزمین محشر] فروفرستاده می‌شوند.');
INSERT INTO "islamhouse" VALUES(25,26,'25:26','در آن روز، فرمانروایی [راستین] از آنِ [الله] رحمان است و برای کافران روزی دشوار است.');
INSERT INTO "islamhouse" VALUES(25,27,'25:27','و [یاد کن از] روزی که ستمکار، دستانش را [از حسرت] می‌گزد و می‌گوید: «ای کاش من راهی [هماهنگ] با پیامبر برگزیده بودم!');
INSERT INTO "islamhouse" VALUES(25,28,'25:28','ای وای بر من! ای کاش فلان [کافر] را به دوستی نمی‌گرفتم!');
INSERT INTO "islamhouse" VALUES(25,29,'25:29','بی‌تردید، پس از آنکه قرآن [و دعوت پیامبر] به من رسید، او [بود که] مرا به گمراهی کشاند و شیطان همواره انسان را تنها [و خوار] وامی‌گذارد».');
INSERT INTO "islamhouse" VALUES(25,30,'25:30','و پیامبر گفت: «پروردگارا، قوم من این قرآن را رها کرده‌اند».');
INSERT INTO "islamhouse" VALUES(25,31,'25:31','و اینچنین برای هر پیامبری دشمنی از بدکاران [قومش] قرار دادیم؛ و کافی است که پروردگارت راهنما و یاورت باشد.');
INSERT INTO "islamhouse" VALUES(25,32,'25:32','و کسانی ‌که کفر ورزیدند گفتند: «چرا قرآن یکباره بر او نازل نشده است؟» ما این‌گونه [کلام الهی را به مرور نازل] کرده‌ایم تا دلت را با آن [آرام و] استوار گردانیم و آن را به تدریج [بر تو] خوانده‌ا‌یم [تا درک و حفظش آسان باشد].');
INSERT INTO "islamhouse" VALUES(25,33,'25:33','و [کافران] هیچ [اعتراض و] مَثلی برایت نمی‌آورند، مگر آنکه ما [نیز پاسخی‌] برحق و خوش‌بیان‌تر برایت ‌می‌آوریم.');
INSERT INTO "islamhouse" VALUES(25,34,'25:34','کسانی که به‌رودرافتاده به دوزخ کشانده می‌شوند، اینان بدجایگاه‌تر و گمراه‌ترند.');
INSERT INTO "islamhouse" VALUES(25,35,'25:35','و در حقیقت، ما به موسی کتاب [تورات] دادیم و برادرش هارون را همراهش دستیار ساختیم.');
INSERT INTO "islamhouse" VALUES(25,36,'25:36','و گفتیم: «شما دو نفر به سوی قومی بروید که آیات ما را تکذیب کردند»؛ پس [چون فرعونیان سرپیچی نمودند] آنان را به شدت نابود ساختیم.');
INSERT INTO "islamhouse" VALUES(25,37,'25:37','و قوم نوح را ـ آنگاه که پیامبران را دروغگو انگاشتند ـ غرق کردیم و آنان را [نشانه و] عبرتی برای مردم قرار دادیم؛ و عذابی دردناک برای ستمکاران مهیّا کرده‌ایم.');
INSERT INTO "islamhouse" VALUES(25,38,'25:38','و [همچنین] قوم عاد و ثمود و اهالی «رَسّ» [= چاه] و نسل‌های بسیاری را که بین آنان بودند [نابود ساختیم].');
INSERT INTO "islamhouse" VALUES(25,39,'25:39','و برای هر یک از آنها مَثَلها زدیم و [چون پند نگرفتند،] همه را به شدت از میان بردیم.');
INSERT INTO "islamhouse" VALUES(25,40,'25:40','و [مشرکان] قطعاً از [سرزمین قوم لوط، همان] شهری که باران بلا بر آن باریده بود گذر کرده‌اند. آیا آن [ویرانه‌های باقی‌مانده از عذاب] را ندیده‌اند؟ [چنین نیست؛] بلکه به زنده ‌شدن [پس از مرگ] امیدی ندارند.');
INSERT INTO "islamhouse" VALUES(25,41,'25:41','و هنگامی‌ که تو را می‌بینند، فقط به تمسخر می‌گیرند [و می‌گویند:] «آیا این همان کسی است که الله او را به پیامبری فرستاده است؟');
INSERT INTO "islamhouse" VALUES(25,42,'25:42','اگر بر [عبادتِ] معبودانمان [شکیبایی و] پایداری نمى‌كردیم، نزدیک بود [محمد] ما را از [راهِ] آنها گمراه كند؛ و[لی] آنگاه که عذاب [الهی] را ببینند، خواهند دانست چه کسی گمراه‌تر [بوده] است.');
INSERT INTO "islamhouse" VALUES(25,43,'25:43','آیا کسی را که هوای نفسش را معبود خود کرده است دیده‌ای؟ آیا تو [می‌توانی] نگهبانِ [ایمانِ] او باشی؟');
INSERT INTO "islamhouse" VALUES(25,44,'25:44','آیا می‌پنداری بیشتر آنان می‌شنوند یا می‌فهمند؟ آنان جز همانند چهارپایان نیستند؛ بلکه گمراه‌ترند.');
INSERT INTO "islamhouse" VALUES(25,45,'25:45','آیا [آفرینش] پروردگارت را ندیده‌ای که چگونه سایه را گسترانده است و اگر می‌خواست، آن را ثابت می‌نمود؟ آنگاه [تغییر جایگاه] خورشید [نسبت به زمین] را دلیل [گسترش و حركت] آن [سایه] قرار دادیم.');
INSERT INTO "islamhouse" VALUES(25,46,'25:46','سپس [با تغییر ارتفاع خورشید، سایه را کوتاه می‌کنیم و] آن را اندک‌اندک به سوی خویش بازمی‌گیریم.');
INSERT INTO "islamhouse" VALUES(25,47,'25:47','و او تعالی است که شب را [با تاریکیِ فراگیرش] پوششی برای شما قرار داد و خواب را مایۀ آسایش و روز را هنگام برخاستن [و کار و تلاش] ساخت.');
INSERT INTO "islamhouse" VALUES(25,48,'25:48','و اوست كه پیش از [باران] رحمتش بادها را بشارت‌بخش فرستاد و از آسمان آبی پاک [و پاک‌کننده] فروفرستادیم.');
INSERT INTO "islamhouse" VALUES(25,49,'25:49','تا با آن [آب]، سرزمین [خشک و] مُرده را زنده گردانیم و چهارپایان و انسان‌های بسیاری را که آفریده‌ایم سیراب سازیم.');
INSERT INTO "islamhouse" VALUES(25,50,'25:50','و به راستی، آن [دلایل و نشانه‌های قدرت الهی] را به صورت‌های گوناگون بیان کردیم تا پند گیرند؛ ولی بیشتر مردم جز [انکار و] ناسپاسی نکردند.');
INSERT INTO "islamhouse" VALUES(25,51,'25:51','و اگر می‌خواستیم، در هر دیاری بیم‌دهنده‌ای برمى‌انگیختیم.');
INSERT INTO "islamhouse" VALUES(25,52,'25:52','پس [ای پیامبر،] از کافران اطاعت نکن و به وسیلۀ قرآن به جهادی بزرگ با آنان برخیز [و در این راه شکیبا باش].');
INSERT INTO "islamhouse" VALUES(25,53,'25:53','و او همان ذاتی است که دو دریا را به هم آمیخت: این یکی خوشگوار و شیرین است و آن یکی بسیار شور و تلخ؛ و در میان آن دو مانعی نفوذناپذیر قرار داد [تا با هم نیامیزند].');
INSERT INTO "islamhouse" VALUES(25,54,'25:54','و اوست که انسان را از آب [منی] آفرید و او را دارای [پیوند] نسبی و سببی گردانید؛ و پروردگارت همواره تواناست.');
INSERT INTO "islamhouse" VALUES(25,55,'25:55','و [مشرکان] به جای الله چیزی را عبادت می‌کنند که نه به آنان سودی می‌بخشد و نه زیانی می‌رساند؛ و کافر در برابر پروردگارش، پیوسته پشتیبان [شیطان] است.');
INSERT INTO "islamhouse" VALUES(25,56,'25:56','و [ای پیامبر،] ما تو را جز بشارت‌بخش و بیم‌دهنده نفرستادیم.');
INSERT INTO "islamhouse" VALUES(25,57,'25:57','بگو: «من [در برابر رسالتم] هیچ‌ پاداشی از شما نمی‌خواهم؛ مگر کسی ‌که بخواهد راهی به سوی پروردگارش برگزیند [و همین برایم کافی است]».');
INSERT INTO "islamhouse" VALUES(25,58,'25:58','و بر [پروردگارِ] زنده‌ا‌ی که هرگز نمی‌میرد توکل کن و او را با ستایش به پاکی یاد کن؛ و همین بس که او به گناهان بندگانش آگاه است.');
INSERT INTO "islamhouse" VALUES(25,59,'25:59','[همان] ذاتی ‌که آسمان‌ها و زمین و آنچه را میان آنهاست در شش روز آفرید، سپس [آن گونه كه شايسته جلال و مقام اوست] بر عرش قرار گرفت. [او الله] رحمان است؛ پس در مورد او از کسی كه آگاه است بپرس [و او نیز کسی جز الله نیست].');
INSERT INTO "islamhouse" VALUES(25,60,'25:60','و هنگامی‌ که به آنان گفته شود: «برای [الله] رحمان سجده کنید»، می‌گویند: «رحمان کیست؟ آیا [قرار است] بر هر چه تو امر می‌كنى سجده بریم؟» و [این دعوتِ تو، دوری از حق و] نفرتشان را می‌افزاید.');
INSERT INTO "islamhouse" VALUES(25,61,'25:61','پربرکت [و بزرگوار] است آن ذاتی که در آسمان، منزلگاه‌هایی [برای ستارگان] قرار داد و در [میان] آنها خورشیدی [درخشان و روشنایی‌بخش] و ماهی تابان پدید آورد.');
INSERT INTO "islamhouse" VALUES(25,62,'25:62','و او تعالی است که شب و روز را در پی یکدیگر قرار داد، برای هر ‌کس که بخواهد [از تدبیر الهی] پند گیرد یا سپاسگزار باشد.');
INSERT INTO "islamhouse" VALUES(25,63,'25:63','و بندگان [مؤمن و شایستۀ] رحمان، کسانی هستند که با فروتنی [و متانت] بر زمین راه می‌روند و هنگامی ‌که جاهلان [با سخنان ناپسند] آنان را مورد خطاب قرار ‌دهند، [به جای تلافی، به نیکی پاسخ دهند و] گویند: سلام [و با بی‌اعتنایی درگذرند].');
INSERT INTO "islamhouse" VALUES(25,64,'25:64','و کسانی که برای پروردگارشان، در حال سجده‌ و قیام، شب‌زنده‌داری می‌کنند.');
INSERT INTO "islamhouse" VALUES(25,65,'25:65','و کسانی ‌که می‌گویند: «پروردگارا، عذاب دوزخ را از ما بازگردان [که] بی‌تردید، عذابش [سخت و] پایدار است.');
INSERT INTO "islamhouse" VALUES(25,66,'25:66','حقا که دوزخ جایگاه و اقامتگاه بدی است».');
INSERT INTO "islamhouse" VALUES(25,67,'25:67','و [مؤمنان] کسانی هستند ‌که چون انفاق می‌نمایند، نه زیاده‌روی می‌کنند و نه سختگیری؛ و بین این دو [حالت، راهِ] اعتدال پیش می‌گیرند.');
INSERT INTO "islamhouse" VALUES(25,68,'25:68','و کسانی که معبودی دیگر با الله نمی‌خوانند و کسی که الله [کشتنش را] حرام کرده است، جز به‌حق نمی‌کشند و زنا نمی‌کنند؛ و هر کس این [گناهان] را مرتکب شود، کیفرِ گناه [خویش] را می‌بیند.');
INSERT INTO "islamhouse" VALUES(25,69,'25:69','روز قیامت، عذابش چندین برابر می‌گردد و با خواری در آن [عذاب] جاودان می‌ماند.');
INSERT INTO "islamhouse" VALUES(25,70,'25:70','مگر کسی‌ که توبه کند و ایمان آورد و کاری شایسته انجام دهد؛ اینانند که الله بدی‌هایشان را به نیکی‌ها تبدیل می‌کند و الله همواره آمرزگاری مهربان است.');
INSERT INTO "islamhouse" VALUES(25,71,'25:71','و کسی ‌که توبه کند و کاری شایسته انجام دهد، یقیناً به درگاه الله ـ چنان که باید و شاید ـ توبه کرده است‌.');
INSERT INTO "islamhouse" VALUES(25,72,'25:72','و کسانی ‌[مؤمن هستند] که گواهیِ دروغ نمی‌دهند [و در مجالس باطل حضور نمی‌یابند] و چون به [کار ناپسند یا] بیهوده‌ای برخورد می‌کنند، بزرگوارانه [از کنارش] می‌گذرند [و خود را آلوده نمى‌سازند].');
INSERT INTO "islamhouse" VALUES(25,73,'25:73','و کسانی‌ که چون به آیات پروردگارشان پند داده می‌شوند، [از آن غفلت نمی‌کنند و در برابر قرآن،] چون کران و کوران نیستند.');
INSERT INTO "islamhouse" VALUES(25,74,'25:74','و کسانی که می‌گویند: «پروردگارا، از همسران و فرزندانمان [کسانی را] به ما عطا فرما که [مایۀ شادیِ دل‌ها و] روشنیِ چشم‌ها باشند و ما را پیشوای پرهیزگاران قرار بده».');
INSERT INTO "islamhouse" VALUES(25,75,'25:75','[آری،] اینان به پاس آنکه شکیبایی کرده‌اند، منازل [عالی بهشت] را پاداش می‌گیرند و در آنجا با درود و سلام [فرشتگان] روبرو می‌گردند.');
INSERT INTO "islamhouse" VALUES(25,76,'25:76','در آنجا جاودانند. چه نیکو جایگاه و [چه نیک] اقامتگاهی است!');
INSERT INTO "islamhouse" VALUES(25,77,'25:77','[به کافران] بگو: «اگر عبادت و دعایتان نباشد، پروردگارم اعتنایی به شما ندارد [و ارزشی برایتان قائل نیست]. به راستی، شما [دعوت حق را] تکذیب کردید؛ به زودی [کیفرِ این کار،] گریبان‌گیرتان خواهد شد».');
INSERT INTO "islamhouse" VALUES(26,1,'26:1','طسم [= طا. سین. میم].');
INSERT INTO "islamhouse" VALUES(26,2,'26:2','این آیاتِ کتابِ روشنگر است.');
INSERT INTO "islamhouse" VALUES(26,3,'26:3','گویی می‌خواهی از [اندوهِ] اینکه [مشرکان] ایمان نمی‌آورند، خود را هلاک کنی.');
INSERT INTO "islamhouse" VALUES(26,4,'26:4','اگر بخواهیم، از آسمان نشانه‌اى بر آنان نازل مى‌كنیم كه در برابرش با فروتنى سر فرود آورند.');
INSERT INTO "islamhouse" VALUES(26,5,'26:5','و هیچ پند تازه‌ای از سوی [الله] رحمان برایشان نمی‌آید، مگر اینکه از آن رویگردانند.');
INSERT INTO "islamhouse" VALUES(26,6,'26:6','به راستی آنان [دعوت پیامبران و عذاب قیامت را] تکذیب کردند؛ به زودی اخبار آنچه مسخره می‌کردند به آنان خواهد رسید.');
INSERT INTO "islamhouse" VALUES(26,7,'26:7','آیا به زمین نمى‌نگرند كه چه بسیار از هر گونه [گیاه] زیبا [و سودمندى] در آن رویانده‌ایم؟');
INSERT INTO "islamhouse" VALUES(26,8,'26:8','بی‌تردید، در این [آفرینش،] نشانه‌ای [از قدرت الله بر معاد] است؛ و[لی] بیشتر آنان ایمان نمی‌آورند.');
INSERT INTO "islamhouse" VALUES(26,9,'26:9','و بی‌تردید، پروردگارت شکست‌ناپذیرِ مهربان است.');
INSERT INTO "islamhouse" VALUES(26,10,'26:10','و [یاد کن از] هنگامی که پروردگارت موسی را ندا داد که: «به سوی قوم ستمکار برو');
INSERT INTO "islamhouse" VALUES(26,11,'26:11','ـ قوم فرعون ـ [و به آنان بگو] آیا [از کفر و سرکشی] نمی‌پرهیزند؟»');
INSERT INTO "islamhouse" VALUES(26,12,'26:12','[موسی] گفت: «پروردگارا، به‌راستی می‌ترسم که مرا تکذیب کنند،');
INSERT INTO "islamhouse" VALUES(26,13,'26:13','و دلم [از تکذیب و کفرشان] تنگ می‌گردد و زبانم [به قدر کافی گویا و] روان نیست؛ پس [جبرئیل را] به سوی هارون بفرست [تا مرا یاری کند].');
INSERT INTO "islamhouse" VALUES(26,14,'26:14','و آنان [به گمان خویش، قصاصِ] گناهی بر [گردن] من دارند؛ می‌ترسم که مرا بکشند».');
INSERT INTO "islamhouse" VALUES(26,15,'26:15','[الله] فرمود: «هرگز! [چنین نیست که بتوانند تو را به قتل برسانند. پس تو و هارون] با آیات [و معجزات] ما بروید. یقیناً [با یاری و تایید] با شماییم و [گفتگویتان را] مى‌شنویم.');
INSERT INTO "islamhouse" VALUES(26,16,'26:16','پس نزد فرعون بروید و بگویید: "ما فرستادۀ پروردگار جهانیان هستیم؛');
INSERT INTO "islamhouse" VALUES(26,17,'26:17','که بنی‌اسرائیل را همراه ما بفرست"».');
INSERT INTO "islamhouse" VALUES(26,18,'26:18','[فرعون] گفت: «آیا تو را در كودكى میان خویش پرورش ندادیم و سال‌هایی از عمرت را بین ما نبودی؟');
INSERT INTO "islamhouse" VALUES(26,19,'26:19','و كار خود را ـ چنان که خواستی‌ـ كردى [و برای یاری مردی از قومت آن مرد قِبطی را کشتی]؛ و تو [نسبت به نعمت‌های من] ناسپاسی».');
INSERT INTO "islamhouse" VALUES(26,20,'26:20','[موسی] گفت: «من آن [کار] را زمانی انجام دادم که از بی‌خبران بودم [نمى‌دانستم مشتى سبب قتل می‌گردد].');
INSERT INTO "islamhouse" VALUES(26,21,'26:21','آنگاه چون از شما ترسیدم، فرار کردم. آنگاه پروردگارم به من دانش بخشید و مرا از پیامبران قرار داد.');
INSERT INTO "islamhouse" VALUES(26,22,'26:22','و این [چه] نعمتی است که تو بر من منت می‌گذاری که بنی‌اسرائیل را بردۀ خود ساخته‌ای؟»');
INSERT INTO "islamhouse" VALUES(26,23,'26:23','فرعون گفت: «پروردگار جهانیان کیست؟»');
INSERT INTO "islamhouse" VALUES(26,24,'26:24','[موسی] گفت: «پروردگار آسمان‌ها و زمین و آنچه میان آنهاست. اگر اهل یقین هستید [تنها او را عبادت کنید]».');
INSERT INTO "islamhouse" VALUES(26,25,'26:25','[فرعون] به کسانی که در اطرافش بودند [با تمسخر] گفت: «آیا می‌شنوید [چه می‌گوید]؟»');
INSERT INTO "islamhouse" VALUES(26,26,'26:26','[موسی] گفت: «[الله] پروردگار شما و پروردگارِ نیاکان شماست».');
INSERT INTO "islamhouse" VALUES(26,27,'26:27','[فرعون] گفت: «این پیامبرتان که به سوی شما فرستاده شده، یقیناً دیوانه است».');
INSERT INTO "islamhouse" VALUES(26,28,'26:28','[موسی] گفت: «اگر بیندیشید، [خواهید دانست که او] پروردگار مشرق و مغرب و همۀ چیزهایی است که میان آن دو قرار دارد».');
INSERT INTO "islamhouse" VALUES(26,29,'26:29','[فرعون پس از ناتوانی از استدلال با موسی] گفت: «اگر معبودی جز من برگزینی، قطعاً تو را زندانی می‌کنم».');
INSERT INTO "islamhouse" VALUES(26,30,'26:30','[موسی] گفت: «حتی اگر نشانۀ آشکاری [در مورد رسالتم] برایت آورده باشم؟»');
INSERT INTO "islamhouse" VALUES(26,31,'26:31','[فرعون] گفت: «اگر راست می‌گویی، آن را بیاور».');
INSERT INTO "islamhouse" VALUES(26,32,'26:32','آنگاه [موسی] عصایش را افکند که ناگهان [به] اژد‌هایی آشکار [تبدیل] شد.');
INSERT INTO "islamhouse" VALUES(26,33,'26:33','و دستش را [از گریبان] بیرون آورد و ناگاه در نظرِ بینندگان سپید [و روشن] بود.');
INSERT INTO "islamhouse" VALUES(26,34,'26:34','[فرعون] به بزرگان پیرامونش گفت: «حقا که این [مرد،] جادوگری داناست.');
INSERT INTO "islamhouse" VALUES(26,35,'26:35','[او] می‌خواهد شما را با جادویش از سرزمینتان بیرون کند؛ نظرتان چیست؟»');
INSERT INTO "islamhouse" VALUES(26,36,'26:36','آنان گفتند: «[تصمیم دربارۀ مجازاتِ] او و برادرش را به تأخیر انداز و مأمورانِ جمع‌آوری را به [همۀ] شهر‌ها بفرست');
INSERT INTO "islamhouse" VALUES(26,37,'26:37','تا هر جادوگر ماهر [و] دانایی را نزدت بیاورند».');
INSERT INTO "islamhouse" VALUES(26,38,'26:38','[چنین کردند، و سر‌انجام] جادوگران برای وعده‌گاهِ روز معیّن گرد آورده شدند.');
INSERT INTO "islamhouse" VALUES(26,39,'26:39','و به مردم گفته شد: «آیا شما [نیز] جمع می‌شوید تا [ببینیم برنده کیست]؟');
INSERT INTO "islamhouse" VALUES(26,40,'26:40','امید که اگر جادوگران پیروز شدند، [همگی] از آنان پیروی کنیم».');
INSERT INTO "islamhouse" VALUES(26,41,'26:41','وقتی جادوگران آمدند، به فرعون گفتند: «اگر ما پیروز شویم، آیا پاداشی خواهیم داشت؟»');
INSERT INTO "islamhouse" VALUES(26,42,'26:42','او گفت: «آری؛ در آن صورت، قطعاً از نزدیکانم خواهید بود».');
INSERT INTO "islamhouse" VALUES(26,43,'26:43','موسی به آنان گفت: «هر چه [می‌خواهید] بیفکنید، [اکنون] بیفکنید».');
INSERT INTO "islamhouse" VALUES(26,44,'26:44','آنان ریسمان‌ها و عصا‌هایشان را افکندند و گفتند: «به عزت فرعون سوگند که ما حتماً پیروزیم».');
INSERT INTO "islamhouse" VALUES(26,45,'26:45','سپس موسی عصایش را افکند؛ ناگهان [به اژدهایی تبدیل شد و] آنچه را به دروغ ساخته بودند بلعید.');
INSERT INTO "islamhouse" VALUES(26,46,'26:46','آنگاه جادوگران به سجده درافتادند.');
INSERT INTO "islamhouse" VALUES(26,47,'26:47','[و] گفتند: «ما به پروردگار جهانیان ایمان آوردیم؛');
INSERT INTO "islamhouse" VALUES(26,48,'26:48','پروردگار موسی و هارون».');
INSERT INTO "islamhouse" VALUES(26,49,'26:49','[فرعون] گفت: «آیا پیش از آنکه به شما اجازه دهم به او ایمان آوردید؟ بی‌تردید، او همان بزرگِ شماست که به شما جادو آموخته است. به زودی خواهید دانست [که مجازاتتان چیست]. یقیناً دست‌ها و پاهایتان را برعكس [یكدیگر از چپ و راست] قطع مى‌كنم و همگی‌تان را به دار مى‌آویزم».');
INSERT INTO "islamhouse" VALUES(26,50,'26:50','آنان گفتند: «[در این شکنجه و عذاب] هیچ زیانی نیست. بی‌تردید، ما به سوی پروردگارمان بازمی‌گردیم.');
INSERT INTO "islamhouse" VALUES(26,51,'26:51','امیدواریم که پروردگارمان گناهان ما را ببخشد که ما نخستین كسانى هستیم كه [از این قوم، به پروردگارِ موسی] ایمان آورده‌ایم».');
INSERT INTO "islamhouse" VALUES(26,52,'26:52','و به موسی وحی کردیم که: «بندگانم را شبانه [از مصر] بیرون ببر؛ [زیرا سپاه فرعون] قطعاً در پیِ شما خواهد آمد».');
INSERT INTO "islamhouse" VALUES(26,53,'26:53','فرعون [چون از ماجرا آگاه گردید] مأمورانِ جمع‌آوری [نیرو] را به شهرها فرستاد.');
INSERT INTO "islamhouse" VALUES(26,54,'26:54','[و اعلام کرد:] اینها [= بنی‌اسرائیل] عده‌ای ناچیزند');
INSERT INTO "islamhouse" VALUES(26,55,'26:55','و جداً ما را به خشم آورده‌اند');
INSERT INTO "islamhouse" VALUES(26,56,'26:56','و ما همگی کاملاً [هوشیار و] آمادۀ پیکاریم».');
INSERT INTO "islamhouse" VALUES(26,57,'26:57','[سرانجام] آنان را از باغ‌ها و چشمه‌ها[ی سرزمین مصر] بیرون آوردیم');
INSERT INTO "islamhouse" VALUES(26,58,'26:58','و از گنج‌ها و خانه‌های مجلّل [دور ساختیم].');
INSERT INTO "islamhouse" VALUES(26,59,'26:59','اینچنین [کردیم] و بنی‌اسرائیل را وارث آن [ثروت و داراییِ] نمودیم.');
INSERT INTO "islamhouse" VALUES(26,60,'26:60','[سپاه فرعون] هنگام طلوع آفتاب به تعقیب آنان پرداختند.');
INSERT INTO "islamhouse" VALUES(26,61,'26:61','و چون دو گروه یکدیگر را دیدند، یاران موسی گفتند: «قطعاً [در چنگال فرعونیان] گرفتاریم».');
INSERT INTO "islamhouse" VALUES(26,62,'26:62','[موسی] گفت: «هرگز! بی‌تردید، پروردگارم با من است و مرا [به راه نجات] هدایت خواهد کرد».');
INSERT INTO "islamhouse" VALUES(26,63,'26:63','به موسی وحی کردیم: «عصایت را به دریا بزن»؛ [هنگامی که عصایش را زد، نیل] شکافته شد و هر بخشی [از آن،] همچون کوهی بزرگ گشت.');
INSERT INTO "islamhouse" VALUES(26,64,'26:64','و آن گروه دیگر [= فرعون و سپاهش] را به آنجا كشاندیم.');
INSERT INTO "islamhouse" VALUES(26,65,'26:65','و موسی و همۀ کسانی را که همراهش بودند نجات دادیم.');
INSERT INTO "islamhouse" VALUES(26,66,'26:66','و دیگران را غرق نمودیم.');
INSERT INTO "islamhouse" VALUES(26,67,'26:67','قطعاً در این [ماجرا] نشانه‌ای [برای عبرت گرفتن] است؛ و[لی] بیشتر آنان مؤمن نبودند.');
INSERT INTO "islamhouse" VALUES(26,68,'26:68','و بی‌تردید، پرورگارت همان شکست‌ناپذیرِ مهربان است.');
INSERT INTO "islamhouse" VALUES(26,69,'26:69','[ای پیامبر،] داستان ابراهیم را بر آنان بازگو.');
INSERT INTO "islamhouse" VALUES(26,70,'26:70','هنگامى كه به پدر و قومش گفت: «چه چیز را عبادت می‌کنید؟»');
INSERT INTO "islamhouse" VALUES(26,71,'26:71','آنان گفتند: «بُت‌هایی را عبادت می‌کنیم و پیوسته سر بر آستانشان داریم».');
INSERT INTO "islamhouse" VALUES(26,72,'26:72','[ابراهیم] گفت: «آیا وقتی آنها را می‌خوانید، [صدای] شما را می‌شنوند؟');
INSERT INTO "islamhouse" VALUES(26,73,'26:73','یا به شما سود و زیان می‌رسانند؟»');
INSERT INTO "islamhouse" VALUES(26,74,'26:74','آنان گفتند: «[نه،] ولی پدرانمان را یافتیم که چنین می‌کردند [و از آن‌ها تقلید می‌کنیم]».');
INSERT INTO "islamhouse" VALUES(26,75,'26:75','[ابراهیم] گفت: «آیا دیدید [و دانستید] چیزهایی را که همواره عبادت می‌کردید');
INSERT INTO "islamhouse" VALUES(26,76,'26:76','ـ [هم] شما و [هم] پدرانتان‌ـ');
INSERT INTO "islamhouse" VALUES(26,77,'26:77','یقیناً [همۀ] آنها [= معبودان باطل] دشمنِ [عقیدۀ توحیدیِ] من هستند؟ مگر پروردگار جهانیان.');
INSERT INTO "islamhouse" VALUES(26,78,'26:78','آن [معبودی] ‌که مرا آفریده و هدایتم می‌کند');
INSERT INTO "islamhouse" VALUES(26,79,'26:79','و همان ذاتی ‌که به من غذا می‌دهد و سیرابم می‌سازد');
INSERT INTO "islamhouse" VALUES(26,80,'26:80','و هنگامی که بیمار می‌شوم، اوست که شفایم می‌دهد');
INSERT INTO "islamhouse" VALUES(26,81,'26:81','و ذاتی ‌که مرا می‌میرانَد و [دوباره] زنده‌ام می‌کند');
INSERT INTO "islamhouse" VALUES(26,82,'26:82','و ذاتی ‌که امیدوارم روز جزا گناهانم را ببخشد.');
INSERT INTO "islamhouse" VALUES(26,83,'26:83','پروردگارا، به من دانشی [در دین] ببخش و مرا به شایستگان ملحق کن.');
INSERT INTO "islamhouse" VALUES(26,84,'26:84','و در آیندگان آوازه‌ای نیک برایم قرار بده.');
INSERT INTO "islamhouse" VALUES(26,85,'26:85','و مرا از وارثان بهشتِ پرنعمت بگردان.');
INSERT INTO "islamhouse" VALUES(26,86,'26:86','و پدرم را بیامرز [که] بی‌تردید در زمرۀ گمراهان بود.');
INSERT INTO "islamhouse" VALUES(26,87,'26:87','و روزی ‌که [مردم] برانگیخته می‌شوند، مرا رسوا [و شرمنده] نساز.');
INSERT INTO "islamhouse" VALUES(26,88,'26:88','همان روزی ‌که مال و فرزندان سودی نمی‌بخشد؛');
INSERT INTO "islamhouse" VALUES(26,89,'26:89','مگر کسی‌که قلبی پاک [و خالی از شرک و نفاق و کینه و ریا] به پیشگاه الله بیاورد».');
INSERT INTO "islamhouse" VALUES(26,90,'26:90','و [آن روز،] بهشت برای پرهیزگاران نزدیک می‌گردد');
INSERT INTO "islamhouse" VALUES(26,91,'26:91','و دوزخ برای گمراهان آشکار می‌شود.');
INSERT INTO "islamhouse" VALUES(26,92,'26:92','و به آنان گفته می‌شود: «[اکنون] کجا هستندآنچه عبادت می‌کردید');
INSERT INTO "islamhouse" VALUES(26,93,'26:93','به جای الله؟ آیا [آنها در برابر عذاب الهی] شما را یاری می‌کنند یا [حتی خود] را یاری می‌دهند؟»');
INSERT INTO "islamhouse" VALUES(26,94,'26:94','پس، آنها [= معبودان باطل] همراه گمراهان در آن [آتش مهیب] افکنده می‌شوند.');
INSERT INTO "islamhouse" VALUES(26,95,'26:95','و [همراه] تمام لشکریان ابلیس.');
INSERT INTO "islamhouse" VALUES(26,96,'26:96','در آنجا [مشرکان] در حال ستیز [با معبودان باطل] می‌گویند:');
INSERT INTO "islamhouse" VALUES(26,97,'26:97','«به الله سوگند که ما در گمراهی آشکاری بودیم.');
INSERT INTO "islamhouse" VALUES(26,98,'26:98','آنگاه که شما را با پروردگار جهانیان برابر می‌دانستیم.');
INSERT INTO "islamhouse" VALUES(26,99,'26:99','و ما را کسی جز مجرمان گمراه نکرد.');
INSERT INTO "islamhouse" VALUES(26,100,'26:100','[اکنون] نه هیچ شفاعتگری داریم');
INSERT INTO "islamhouse" VALUES(26,101,'26:101','و نه هیچ دوست مهربانی.');
INSERT INTO "islamhouse" VALUES(26,102,'26:102','كاش بازگشتی [به دنیا] داشتیم و در زمرۀ مؤمنان قرار مى‌گرفتیم».');
INSERT INTO "islamhouse" VALUES(26,103,'26:103','قطعاً در این [ماجرا] نشانه‌ای [برای عبرت گرفتن] است؛ ولی بیشتر آنان مؤمن نبودند.');
INSERT INTO "islamhouse" VALUES(26,104,'26:104','و بی‌تردید، پروردگارت همان شکست‌ناپذیرِ مهربان است.');
INSERT INTO "islamhouse" VALUES(26,105,'26:105','قوم نوح، پیامبران را دروغگو انگاشتند.');
INSERT INTO "islamhouse" VALUES(26,106,'26:106','آنگاه‌ که برادرشان نوح به آنان گفت: «آیا [از الله‌] پروا نمی‌کنید؟');
INSERT INTO "islamhouse" VALUES(26,107,'26:107','به راستی، من پیامبری امین برایتان هستم؛');
INSERT INTO "islamhouse" VALUES(26,108,'26:108','پس، از الله پروا کنید و مطیع [دستورهای] من باشید.');
INSERT INTO "islamhouse" VALUES(26,109,'26:109','و من [در برابر رسالتم] هیچ پاداشی از شما نمی‌خواهم. پاداش من، تنها بر [عهدۀ] پروردگار جهانیان است؛');
INSERT INTO "islamhouse" VALUES(26,110,'26:110','پس، از الله پروا کنید و مطیع [دستورهای] من باشید».');
INSERT INTO "islamhouse" VALUES(26,111,'26:111','آنان گفتند: «آیا به تو ایمان بیاوریم در حالی كه [فقط] فرومایگان از تو پیروى كرده‌اند؟»');
INSERT INTO "islamhouse" VALUES(26,112,'26:112','[نوح] گفت: «من به آنچه آنان [در گذشته] مى‌كردند چه دانشی دارم؟');
INSERT INTO "islamhouse" VALUES(26,113,'26:113','حساب آنان تنها با پروردگار من است. اگر می‌فهمید [چنین سخن گزافی نگویید].');
INSERT INTO "islamhouse" VALUES(26,114,'26:114','و من [هرگز] مؤمنان را طرد نمی‌کنم.');
INSERT INTO "islamhouse" VALUES(26,115,'26:115','من جز بیم‌دهنده‌ای آشکار نیستم».');
INSERT INTO "islamhouse" VALUES(26,116,'26:116','آنان گفتند: «ای نوح، اگر [از دعوتت] دست برنداری، قطعاً سنگسار می‌شوی».');
INSERT INTO "islamhouse" VALUES(26,117,'26:117','او گفت: «پروردگارا، قومم مرا دروغگو انگاشتند؛');
INSERT INTO "islamhouse" VALUES(26,118,'26:118','پس میان من و آنان داوری کن و من و مؤمنانی را که همراهم هستند نجات بده».');
INSERT INTO "islamhouse" VALUES(26,119,'26:119','او و افرادی را که همراهش بودند، در [آن] کشتیِ آکنده [از انسان و حیوان و کالا] نجات دادیم');
INSERT INTO "islamhouse" VALUES(26,120,'26:120','و باقی‌ماندگان را غرق کردیم.');
INSERT INTO "islamhouse" VALUES(26,121,'26:121','قطعاً در این [ماجرا] نشانه‌ای [برای عبرت گرفتن] است؛ و[لی] بیشتر آنان مؤمن نبودند.');
INSERT INTO "islamhouse" VALUES(26,122,'26:122','و بی‌تردید، پروردگارت همان شکست‌ناپذیر مهربان است.');
INSERT INTO "islamhouse" VALUES(26,123,'26:123','[قوم] عاد [نیز] پیامبران را دروغگو انگاشتند.');
INSERT INTO "islamhouse" VALUES(26,124,'26:124','آنگاه‌ که برادر‌شان هود به آنان گفت: «آیا [از الله] پروا نمی‌کنید؟»');
INSERT INTO "islamhouse" VALUES(26,125,'26:125','به راستی، من پیامبری امین برای شما هستم.');
INSERT INTO "islamhouse" VALUES(26,126,'26:126','پس، از الله پروا کنید و مطیع [دستورهای] من باشید.');
INSERT INTO "islamhouse" VALUES(26,127,'26:127','و من [در برابر رسالتم] هیچ پاداشی از شما نمی‌خواهم. پاداش من، تنها بر [عهدۀ] پروردگار جهانیان است.');
INSERT INTO "islamhouse" VALUES(26,128,'26:128','آیا بر هر مکان بلندی، بنایی برافراشته می‌سازید [و در آن] بيهوده‌كارى می‌کنید؟!');
INSERT INTO "islamhouse" VALUES(26,129,'26:129','و قصر‌ها و قلعه‌هایی محکم و استوار بنا می‌کنید؛ گویی [در دنیا] جاودانید.');
INSERT INTO "islamhouse" VALUES(26,130,'26:130','و چون [به کسی] حمله‌ور می‌شوید، [بی‌رحم و] ظالمانه حمله می‌کنید.');
INSERT INTO "islamhouse" VALUES(26,131,'26:131','از الله پروا کنید و مطیع [دستورهای] من باشید.');
INSERT INTO "islamhouse" VALUES(26,132,'26:132','و از ذاتی پروا کنید كه [نعمت‌هایی چنان فراوان] به شما عطا کرده است كه خود، [آنها را بهتر] مى‌دانید.');
INSERT INTO "islamhouse" VALUES(26,133,'26:133','او به شما چهارپایان و فرزندان [بسیار] عطا کرده است');
INSERT INTO "islamhouse" VALUES(26,134,'26:134','و باغ‌ها و چشمه‌سار‌ها.');
INSERT INTO "islamhouse" VALUES(26,135,'26:135','به راستی که من از عذاب روزی بزرگ بر شما می‌ترسم».');
INSERT INTO "islamhouse" VALUES(26,136,'26:136','آنان گفتند: «چه ما را پند بدهی یا ندهی، برایمان یکسان است [و از تو اطاعت نمی‌کنیم].');
INSERT INTO "islamhouse" VALUES(26,137,'26:137','این [آیینِ ما، همان] روش پیشینیان است');
INSERT INTO "islamhouse" VALUES(26,138,'26:138','و ما [هرگز] عذاب نخواهیم شد».');
INSERT INTO "islamhouse" VALUES(26,139,'26:139','آنان هود را دروغگو انگاشتند؛ ما [نیز] هلاکشان کردیم. قطعاً در این [ماجرا] نشانه‌ای [برای عبرت گرفتن] است؛ و بیشتر آنان مؤمن نبودند.');
INSERT INTO "islamhouse" VALUES(26,140,'26:140','و بی‌تردید، پروردگارت همان شکست‌ناپذیرِ مهربان است.');
INSERT INTO "islamhouse" VALUES(26,141,'26:141','[قوم] ثمود پیامبران را دروغگو انگاشتند.');
INSERT INTO "islamhouse" VALUES(26,142,'26:142','آنگاه ‌که برادر‌شان صالح به آنان گفت: «آیا [از الله] پروا نمی‌کنید؟');
INSERT INTO "islamhouse" VALUES(26,143,'26:143','به راستی، من پیامبری امین برایتان هستم.');
INSERT INTO "islamhouse" VALUES(26,144,'26:144','پس، از الله پروا کنید و مطیع [دستورهای] من باشید.');
INSERT INTO "islamhouse" VALUES(26,145,'26:145','و من [در برابر رسالتم] هیچ پاداشی از شما نمی‌خواهم. پاداش من، تنها بر [عهدۀ] پروردگار جهانیان است.');
INSERT INTO "islamhouse" VALUES(26,146,'26:146','آیا [مى‌پندارید كه‌] شما را در این نعمت‌هایی كه اینجاست آسوده رها مى‌كنند؟');
INSERT INTO "islamhouse" VALUES(26,147,'26:147','در [این] باغ‌ها و چشمه‌سارها');
INSERT INTO "islamhouse" VALUES(26,148,'26:148','و [در کنار این] کشتزارها و نخل‌هایی که میوه‌هایش نرم و رسیده است؟');
INSERT INTO "islamhouse" VALUES(26,149,'26:149','و [سرمست و] ماهرانه از کوه‌ها [برای خود] خانه‌هایی می‌تراشید.');
INSERT INTO "islamhouse" VALUES(26,150,'26:150','از الله پروا کنید و مطیع [دستورهای] من باشید.');
INSERT INTO "islamhouse" VALUES(26,151,'26:151','و از اسرافکاران [گناهکار] پیروی نکنید.');
INSERT INTO "islamhouse" VALUES(26,152,'26:152','[همان] کسانی که در زمین به فساد می‌پردازند و [خویشتن را] اصلاح نمی‌کنند».');
INSERT INTO "islamhouse" VALUES(26,153,'26:153','آنان گفتند: «[ای صالح،] جز این نیست که تو جادوشده‌ای.');
INSERT INTO "islamhouse" VALUES(26,154,'26:154','تو جز بشری همانند ما نیستی؛ پس اگر راست می‌گویی، نشانه‌ای [بر پیامبری‌ات] بیاور».');
INSERT INTO "islamhouse" VALUES(26,155,'26:155','[صالح] گفت: «این ماده شتری است كه سهمى از آب [چشمه] دارد و [روزهای معیّنى نیز] شما سهمی از آب دارید.');
INSERT INTO "islamhouse" VALUES(26,156,'26:156','پس [کمترین] آزاری به آن نرسانید که عذابِ روزی بزرگ شما را فرامی‌گیرد».');
INSERT INTO "islamhouse" VALUES(26,157,'26:157','اما آن را پَى كردند و [از کردۀ خود] پشیمان شدند.');
INSERT INTO "islamhouse" VALUES(26,158,'26:158','پس عذاب [الهی] آنان را فروگرفت. بی‌گمان، در این [ماجرا] نشانه‌ای [برای عبرت گرفتن] است؛ و بیشتر آنان مؤمن نبودند.');
INSERT INTO "islamhouse" VALUES(26,159,'26:159','و بی‌تردید، پروردگارت همان شکست‌ناپذیرِ مهربان است.');
INSERT INTO "islamhouse" VALUES(26,160,'26:160','قوم لوط [نیز] پیامبران را دروغگو انگاشتند.');
INSERT INTO "islamhouse" VALUES(26,161,'26:161','آنگاه ‌که برادر‌شان لوط به آنان گفت: «آیا [از الله] پروا نمی‌کنید؟');
INSERT INTO "islamhouse" VALUES(26,162,'26:162','به راستی، من پیامبری امین برای شما هستم.');
INSERT INTO "islamhouse" VALUES(26,163,'26:163','پس، از الله پروا کنید و مطیع [دستورهای] من باشید.');
INSERT INTO "islamhouse" VALUES(26,164,'26:164','و من [در برابر رسالتم] هیچ پاداشی از شما نمی‌خواهم. پاداش من، تنها بر [عهدۀ] پروردگار جهانیان است.');
INSERT INTO "islamhouse" VALUES(26,165,'26:165','آیا از میان جهانیان، با مردان می‌آمیزید،');
INSERT INTO "islamhouse" VALUES(26,166,'26:166','و همسرانی را که الله برایتان آفریده است رها می‌کنید؟! بلکه شما گروهی متجاوزید.');
INSERT INTO "islamhouse" VALUES(26,167,'26:167','آنان گفتند: «ای لوط، اگر [از این امر و نهی] دست بر نداری، قطعاً [از شهر] اخراج می‌شوی».');
INSERT INTO "islamhouse" VALUES(26,168,'26:168','[لوط] گفت: «من دشمن [سرسخت] این کارتان هستم.');
INSERT INTO "islamhouse" VALUES(26,169,'26:169','پروردگارا، من و خانواده‌ام را از [عاقبت] آنچه [اینها] انجام می‌دهند نجات ده».');
INSERT INTO "islamhouse" VALUES(26,170,'26:170','ما او و همۀ خانواده‌اش را نجات دادیم');
INSERT INTO "islamhouse" VALUES(26,171,'26:171','مگر پیرزنی که در میان بازماندگان [در عذاب] بود.');
INSERT INTO "islamhouse" VALUES(26,172,'26:172','سپس دیگران را نابود کردیم');
INSERT INTO "islamhouse" VALUES(26,173,'26:173','و بارانی [از سنگ] بر آنان باراندیم؛ و بارانِ بیم‌داده‌شدگان چه بد بود!');
INSERT INTO "islamhouse" VALUES(26,174,'26:174','بی‌گمان، در این [ماجرا] نشانه‌ای [برای عبرت گرفتن] است؛ و بیشتر آنان مؤمن نبودند.');
INSERT INTO "islamhouse" VALUES(26,175,'26:175','و بی‌تردید، پروردگارت همان شکست‌ناپذیرِ مهربان است.');
INSERT INTO "islamhouse" VALUES(26,176,'26:176','اصحاب اَیکه [نیز] پیامبران را دروغگو انگاشتند.');
INSERT INTO "islamhouse" VALUES(26,177,'26:177','آنگاه که شعیب به آنان گفت: «آیا [از الله] پروا نمی‌کنید؟');
INSERT INTO "islamhouse" VALUES(26,178,'26:178','به‌راستی، من پیامبری امین برای شما هستم.');
INSERT INTO "islamhouse" VALUES(26,179,'26:179','پس از الله پروا کنید و مطیع [دستورهای] من باشید.');
INSERT INTO "islamhouse" VALUES(26,180,'26:180','و من [در برابر رسالتم] هیچ پاداشی از شما نمی‌خواهم. پاداش من، تنها بر [عهدۀ] پروردگار جهانیان است.');
INSERT INTO "islamhouse" VALUES(26,181,'26:181','پیمانه را تمام بدهید و کم‌فروشی نکنید.');
INSERT INTO "islamhouse" VALUES(26,182,'26:182','و با ترازوی درست وزن کنید.');
INSERT INTO "islamhouse" VALUES(26,183,'26:183','و کالای مردم را کم ندهید [و حقشان را ضایع نکنید] و در زمین به فساد [و تباهی] نکوشید.');
INSERT INTO "islamhouse" VALUES(26,184,'26:184','و از ذاتی‌ که شما و امت‌های پیشین را آفرید پروا کنید».');
INSERT INTO "islamhouse" VALUES(26,185,'26:185','آنان گفتند: «[ای شعیب،] جز این نیست که تو جادوشده‌ای.');
INSERT INTO "islamhouse" VALUES(26,186,'26:186','تو جز بشری همانند ما نیستی و ما تو را دروغگو می‌پنداریم.');
INSERT INTO "islamhouse" VALUES(26,187,'26:187','اگر راست می‌گویی، پاره‌های از آسمان را بر [سر] ما بینداز».');
INSERT INTO "islamhouse" VALUES(26,188,'26:188','[شعیب] گفت: «پروردگارم به آنچه می‌کنید داناتر است».');
INSERT INTO "islamhouse" VALUES(26,189,'26:189','او را دروغگو انگاشتند و عذابِ روزِ ابر [آتشبار] آنان را فراگرفت. به راستى که آن [بارانِ آتش،] عذاب روزى هولناک بود.');
INSERT INTO "islamhouse" VALUES(26,190,'26:190','بی‌گمان، در این [ماجرا] نشانه‌ای [برای عبرت گرفتن] است؛ و بیشتر آنان مؤمن نبودند.');
INSERT INTO "islamhouse" VALUES(26,191,'26:191','و بی‌تردید، پروردگارت همان شکست‌ناپذیرِ مهربان است.');
INSERT INTO "islamhouse" VALUES(26,192,'26:192','و به راستی [این] قرآن فروفرستادۀ پروردگار جهانیان است.');
INSERT INTO "islamhouse" VALUES(26,193,'26:193','روح‌ الامین [= جبرئیل] آن را فرود آورده است.');
INSERT INTO "islamhouse" VALUES(26,194,'26:194','بر قلب تو [نازل کرده است] تا بیم‌دهنده باشی.');
INSERT INTO "islamhouse" VALUES(26,195,'26:195','[قرآن را] به زبان عربی روشن [نازل نمود].');
INSERT INTO "islamhouse" VALUES(26,196,'26:196','و بی‌تردید، [بشارت نزولِ] آن، در کتاب‌های پیشینیان [نیز آمده] است.');
INSERT INTO "islamhouse" VALUES(26,197,'26:197','آیا همین نشانه برایشان کافی نیست که علمای بنی‌اسرائیل از [حقیقتِ] قرآن آگاهند؟');
INSERT INTO "islamhouse" VALUES(26,198,'26:198','و اگر آن را بر بعضی از غیر عرب‌ها نازل می‌کردیم');
INSERT INTO "islamhouse" VALUES(26,199,'26:199','و [پیامبر،] آن را برایشان می‌خواند، به آن ایمان نمی‌آوردند.');
INSERT INTO "islamhouse" VALUES(26,200,'26:200','[آری،] این گونه آن [کفر و تکذیب] را در دل‌های گناهکاران جای دادیم.');
INSERT INTO "islamhouse" VALUES(26,201,'26:201','اما به آن ایمان نمی‌آورند تا آنگاه که عذاب دردناک را ببینند.');
INSERT INTO "islamhouse" VALUES(26,202,'26:202','ناگهان در حالی ‌که بی‌خبرند، به سراغشان می‌آید.');
INSERT INTO "islamhouse" VALUES(26,203,'26:203','[در آن حال] می‌گویند: «آیا مهلتی [برای توبه] خواهیم داشت؟»');
INSERT INTO "islamhouse" VALUES(26,204,'26:204','آیا آنان عذاب ما را به شتاب خواستارند؟');
INSERT INTO "islamhouse" VALUES(26,205,'26:205','آیا دانستی که اگر آنان را سال‌ها [از زندگی دنیا] بهره‌مند سازیم');
INSERT INTO "islamhouse" VALUES(26,206,'26:206','سپس آنچه که به آنان وعده داده شده است به سراغشان بیاید');
INSERT INTO "islamhouse" VALUES(26,207,'26:207','این بهره‌مندی‌شان [از دنیا، در هنگام عذاب] سودی برایشان نخواهد داشت؟');
INSERT INTO "islamhouse" VALUES(26,208,'26:208','و ما [ساکنان] هیچ شهری را هلاک نکردیم مگر اینکه هشداردهندگانی [از پیامبران] داشتند.');
INSERT INTO "islamhouse" VALUES(26,209,'26:209','تا آنان را پند دهند؛ و ما هرگز ستمکار نبوده‌ایم [که اتمام حجت نکنیم].');
INSERT INTO "islamhouse" VALUES(26,210,'26:210','و این قرآن را شیاطین نازل نکرده‌اند.');
INSERT INTO "islamhouse" VALUES(26,211,'26:211','آنان نه درخورِ این كارند و نه توانش را دارند.');
INSERT INTO "islamhouse" VALUES(26,212,'26:212','بى‌تردید، آنان از شنیدن [وحى‌] منع شده‌اند.');
INSERT INTO "islamhouse" VALUES(26,213,'26:213','پس [ای پیامبر،] هیچ معبودی را با الله مخوان، که عذاب خواهی شد.');
INSERT INTO "islamhouse" VALUES(26,214,'26:214','و خویشاوندان نزدیکت را [از عذاب الهی] بیم ده.');
INSERT INTO "islamhouse" VALUES(26,215,'26:215','و با مؤمنانی ‌که از تو پیروی کرده‌اند، مهربان [و فروتن] باش.');
INSERT INTO "islamhouse" VALUES(26,216,'26:216','اگر از [فرمان] تو سرپیچی کردند، بگو: «به راستی، من از آنچه می‌کنید بیزارم».');
INSERT INTO "islamhouse" VALUES(26,217,'26:217','و بر [الله] شکست‌ناپذیرِ مهربان توکل کن.');
INSERT INTO "islamhouse" VALUES(26,218,'26:218','همان ذاتی ‌که چون [برای عبادت] برمی‌خیزی، تو را می‌بیند.');
INSERT INTO "islamhouse" VALUES(26,219,'26:219','و حرکت [و نشست و برخاستِ] تو را میان سجده‌کنندگان [می‌نگرد].');
INSERT INTO "islamhouse" VALUES(26,220,'26:220','بی‌تردید، او همان شنوای داناست.');
INSERT INTO "islamhouse" VALUES(26,221,'26:221','آیا به شما خبر دهم که شیاطین بر چه کسی نازل می‌شوند؟');
INSERT INTO "islamhouse" VALUES(26,222,'26:222','بر هر دروغ‌پرداز گنه‌پیشه‌ نازل می‌شوند.');
INSERT INTO "islamhouse" VALUES(26,223,'26:223','آنان شنیده‌ها[یی را که دزدانه از ملکوت گوش داده‌اند، به جادوگران و فال‌گیران] القا مى‌كنند و بیشتر آنان دروغگو هستند.');
INSERT INTO "islamhouse" VALUES(26,224,'26:224','و [محمد شاعر نیست؛ زیرا‌] شاعران را گمراهان پیروی می‌کنند.');
INSERT INTO "islamhouse" VALUES(26,225,'26:225','آیا ندیدی که آنان در هر لغو و بیهوده‌ای [حیران و] سرگشته‌اند؟');
INSERT INTO "islamhouse" VALUES(26,226,'26:226','و چیزی را می‌گویند که [خود به آن اعتقاد ندارند و] عمل نمی‌کنند؟');
INSERT INTO "islamhouse" VALUES(26,227,'26:227','مگر کسانی ‌که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند و الله را بسیار یاد کرده‌اند و پس از آنکه ستم دیدند، انتقام گرفتند [و با شعر خود از اسلام و رسول الله و مسلمانان دفاع نمودند]؛ و کسانی ‌که ستم کردند، به زودی خواهند دانست به چه بازگشتگاهی بازمی‌گردند.');
INSERT INTO "islamhouse" VALUES(27,1,'27:1','طس [= طا. سین]. این، آیات قرآن و کتابی روشنگر است.');
INSERT INTO "islamhouse" VALUES(27,2,'27:2','[که] هدایت و بشارت برای مؤمنان است.');
INSERT INTO "islamhouse" VALUES(27,3,'27:3','[همان] کسانی‌ که نماز برپا می‌دارند و زکات می‌پردازند و به آخرت یقین دارند.');
INSERT INTO "islamhouse" VALUES(27,4,'27:4','در حقیقت، کسانی ‌که به آخرت ایمان نمی‌آورند، اعمال آنان را برایشان آراسته‌ایم؛ پس [کوردل و] سرگشته‌اند.');
INSERT INTO "islamhouse" VALUES(27,5,'27:5','آنان کسانی هستند که عذاب دردناک [در پیش] دارند و در آخرت زیانکارترین [مردم] هستند.');
INSERT INTO "islamhouse" VALUES(27,6,'27:6','و [ای پیامبر،] یقیناً تو قرآن را از پیشگاهِ [پروردگار] حکیم دانا دریافت می‌کنی.');
INSERT INTO "islamhouse" VALUES(27,7,'27:7','[یاد کن از] هنگامی که موسی به خانواده‌اش گفت: «[بایستید؛] من آتشی [از دور] دیدم. به زودی خبری از آن برایتان می‌آورم یا شعله‌ آتشی می‌آورم تا گرم شوید».');
INSERT INTO "islamhouse" VALUES(27,8,'27:8','چون به آن [آتش] رسید، ندا داده شد که: «مبارک [و خجسته] باد آن ‌که در آتش است و هر [فرشته‌ای] ‌که پیرامون آن است؛ و پاک و منزّه است الله ـ پروردگار جهانیان.');
INSERT INTO "islamhouse" VALUES(27,9,'27:9','ای موسی، به راستی که منم الله شکست‌ناپذیرِ حکیم.');
INSERT INTO "islamhouse" VALUES(27,10,'27:10','و عصایت را بیفکن». چون [موسی] آن را دید که همچون ماری [با سرعت] می‌خزد، [از ترس] پشت کرد و گریخت و بازنگشت. [ندا آمد که:] «ای موسی، نترس. پیامبران در پیشگاهِ من هیچ ترسی ندارند.');
INSERT INTO "islamhouse" VALUES(27,11,'27:11','اما کسی ‌که ستم کند، آنگاه [پس از توبه،] بدی را به نیکی تبدیل نماید [بداند که] بی‌تردید، من آمرزندۀ مهربانم.');
INSERT INTO "islamhouse" VALUES(27,12,'27:12','و دستت را در گریبانت فرو بر [تا] سپید و بدون عیب [و بدون بیماری پیسی] خارج ‌شود. [این معجزه‌ای است] در زمرۀ معجزات نُه‌گانه [که باید] به سوی فرعون و قومش [ببری؛ چرا که] بی‌تردید، آنان گروهی نافرمانند».');
INSERT INTO "islamhouse" VALUES(27,13,'27:13','اما وقتی آیات [و معجزات] ما روشن [و واضح] به سویشان آمد، گفتند: «این، جادویی آشکار است».');
INSERT INTO "islamhouse" VALUES(27,14,'27:14','و آنها را از روی ستم و سرکشی انکار کردند در حالی که دل‌هایشان به آنها یقین داشتند؛ پس بنگر که سرانجام مفسدان چگونه بود.');
INSERT INTO "islamhouse" VALUES(27,15,'27:15','و به راستی، به داود و سلیمان دانشی [عظیم] دادیم و آنان گفتند: «ستایش از آنِ الله است که ما را بر بسیاری از بندگان مؤمن خود برتری بخشید».');
INSERT INTO "islamhouse" VALUES(27,16,'27:16','و سلیمان از داود [علم و نبوت و پادشاهی را] میراث برد و گفت: «ای مردم، [فهمِ] زبان پرندگان به ما آموخته شده و از هر چیز به ما [بهره‌ای] داده‌اند. بی‌تردید، این فضلی آشکار [از جانب پروردگار] است».');
INSERT INTO "islamhouse" VALUES(27,17,'27:17','و سپاهیان سلیمان ـ از جن و انس و پرندگان ـ نزدش گِرد آمدند؛ آنگاه منظم [و پیوسته در کنار یکدیگر] می‌رفتند.');
INSERT INTO "islamhouse" VALUES(27,18,'27:18','[آنان به راه خود ادامه دادند] تا وقتی به سرزمین مورچگان [در شام] رسیدند. مورچه‌ای گفت: «ای مورچگان، به لانه‌های خود بروید تا سلیمان و لشکریانش شما را [ندیده و] ندانسته پایمال نکنند».');
INSERT INTO "islamhouse" VALUES(27,19,'27:19','[سلیمان] از سخن آن [مورچه] خندید و گفت: «پروردگارا، به من توفیق بده تا شکر نعمتی را که بر من و بر پدر و مادرم ارزانی داشته‌ای به جای آورم و [یاری‌ام کن تا] کاری شایسته‌ انجام دهم که مایۀ رضایتت گردد و به رحمت خویش مرا در جمعِ بندگان شایسته‌ات وارد کن».');
INSERT INTO "islamhouse" VALUES(27,20,'27:20','و [سلیمان، حالِ] پرندگان را جویا شد و گفت: «چه شده است که هدهد را نمی‌بینم؟ [آیا جایی پنهان شده] یا غایب است؟');
INSERT INTO "islamhouse" VALUES(27,21,'27:21','[چون او را نیافت گفت:] قطعاً او را به سختی مجازات می‌کنم یا سرش را می‌بُرم، مگر اینکه دلیل روشنی [برای غیبتش] بیاورد».');
INSERT INTO "islamhouse" VALUES(27,22,'27:22','دیری نپایید [که هدهد آمد] و گفت: «از چیزی آگاهی یافته‌ام که تو به آن آگاهی نیافته‌ای و از [سرزمین] سبأ خبری موثق برایت آورده‌ام.');
INSERT INTO "islamhouse" VALUES(27,23,'27:23','من [آنجا] زنی را یافته‌ام که بر آنان فرمانروایی می‌کند و [به او] از هر گونه نعمتی داده‌اند و تخت بزرگی دارد.');
INSERT INTO "islamhouse" VALUES(27,24,'27:24','او و قومش را دیدم که به جای الله، برای خورشید سجده می‌کنند، و شیطان کارهای [باطل و شرکِ] آنان را برایشان آراسته و آنان را از راه [راست] بازداشته است؛ از این رو هدایت نمی‌شوند».');
INSERT INTO "islamhouse" VALUES(27,25,'27:25','[شیطان فریبشان داده است] تا براى الله سجده نكنند؛ [همان معبودی] كه نهان آسمان‌ها و زمین را آشکار می‌کند و آنچه را که پنهان می‌کنید و آشكار مى‌سازید، [همه را] مى‌داند.');
INSERT INTO "islamhouse" VALUES(27,26,'27:26','الله، که هیچ معبودی [به‌حق] جز او نیست، پروردگار عرش بزرگ است.');
INSERT INTO "islamhouse" VALUES(27,27,'27:27','[سلیمان] گفت: «خواهیم دید که آیا راست گفته‌ای یا دروغگویی.');
INSERT INTO "islamhouse" VALUES(27,28,'27:28','این نامۀ مرا ببر و به سوی آنان بیفکن؛ آنگاه دور شو و [در گوشه‌ای بایست و] بنگر که [دربارۀ آن] چه می‌گویند».');
INSERT INTO "islamhouse" VALUES(27,29,'27:29','[ملکۀ سبا] گفت: «ای بزرگان، نامۀ ارجمندی به سوی من افکنده شده است.');
INSERT INTO "islamhouse" VALUES(27,30,'27:30','این [نامه] از جانب سلیمان است و چنین است: "به نام الله بخشندۀ مهربان.');
INSERT INTO "islamhouse" VALUES(27,31,'27:31','بر من برتری مجویید و در حالی که [از شرک دست برداشته و] تسلیم [حق و توحید] شده‌اید، نزد من بیایید"».');
INSERT INTO "islamhouse" VALUES(27,32,'27:32','[ملکه] گفت: «ای بزرگان، نظرتان را در این مورد بگویید؛ [چرا که] من هرگز بدون حضور [و مشورتِ] شما دربارۀ کاری تصمیم نگرفته‌ام».');
INSERT INTO "islamhouse" VALUES(27,33,'27:33','آنان گفتند: «ما نیروی بسیار و جنگجویانِ قدرتمندی داریم؛ ولی اختیار [و تصمیم نهایی] با توست؛ پس بررسی کن که چه دستوری بدهی».');
INSERT INTO "islamhouse" VALUES(27,34,'27:34','[ملکه] گفت: «بی‌تردید، وقتی پادشاهان [به عنوان فاتح] وارد شهری می‌شوند، آن را ویران [و تباه] می‌سازند و بزرگانش را خوار و ذلیل می‌کنند و رفتارشان همواره چنین بوده است.');
INSERT INTO "islamhouse" VALUES(27,35,'27:35','و من هدیه‌ای برایشان می‌فرستم و منتظر می‌مانم تا ببینم فرستادگان چه پاسخی [برایم] می‌آورند».');
INSERT INTO "islamhouse" VALUES(27,36,'27:36','هنگامی که [فرستادۀ ملکه] نزد سلیمان آمد، [سلیمان] گفت: «آیا [می‌خواهید] با مال [و ثروت] به من کمک کنید؟ آنچه الله [از پیامبری و دانش و فرمانروایی] به من بخشیده، از آن چیزی که به شما عطا کرده بهتر است؛ بلکه شمایید که به هدیۀ خویش [و ثروت‌های دنیا] شادمان می‌شوید.');
INSERT INTO "islamhouse" VALUES(27,37,'27:37','نزد آنان بازگرد [و بگو که] قطعاً با لشکریانی به سویشان خواهیم آمد که توان مقابله با آن را نداشته باشند و بی‌تردید، آنان را از آن [سرزمین] با خفت و خواری بیرون می‌کنیم».');
INSERT INTO "islamhouse" VALUES(27,38,'27:38','[آنگاه] گفت: «ای بزرگان، کدام‌یک از شما ـ پیش از آنکه آنان تسلیم‌شده نزد من بیایند ـ تخت او را برایم می‌آورد؟»');
INSERT INTO "islamhouse" VALUES(27,39,'27:39','یكى از جنّیانِ تنومند گفت: «پیش از آنکه از جایت برخیزی، من آن را نزدت می‌آورم و قطعاً بر انجام این کار، نیرومند و امینم».');
INSERT INTO "islamhouse" VALUES(27,40,'27:40','کسی‌ که دانشی از کتاب [الهی] داشت گفت: «پیش از آنکه چشم بر هم بزنی، آن را برایت می‌آورم». وقتی [سلیمان] آن [تخت] را نزد خود مستقر دید، گفت: «این از فضل پروردگار من است تا مرا بیازماید که آیا سپاس می‌گزارم یا ناسپاسی می‌کنم؛ پس هر کس سپاس گزارد، تنها به سود خویش سپاس می‌گزارد و هر کس ناسپاسی کند، [به زیانِ خود ناسپاسی کرده است؛ چرا که] بی‌تردید، پروردگارم بی‌نیاز کریم است.');
INSERT INTO "islamhouse" VALUES(27,41,'27:41','[سلیمان] گفت: «تختش را [تغییر شكل دهید و] برایش ناشناس کنید تا ببینیم آیا [آن را] تشخیص می‌دهد یا از کسانی است که [اشیا و اموال خود را] نمی‌شناسند.');
INSERT INTO "islamhouse" VALUES(27,42,'27:42','هنگامی که [ملکۀ سبأ] آمد، [به او] گفته شد: «آیا تخت تو این گونه است؟» [ملکه] گفت: «گویی همان است» و [سلیمان گفت:] «این دانش [و توانایی] پیش از این به ما داده شده است و همواره فرمانبردار بوده‌ایم».');
INSERT INTO "islamhouse" VALUES(27,43,'27:43','و آنچه [به تقلید از قومش] به جای الله عبادت می‌کرد، او را [از ایمان و توحید] بازداشته بود. بی‌تردید، او [= ملکه] از جملۀ کافران بود.');
INSERT INTO "islamhouse" VALUES(27,44,'27:44','به وی گفته شد: «به صحن [قصر] وارد شو». هنگامی که آن را دید، پنداشت که حوضی است و دو ساق [پای] خویش را برهنه کرد [تا از آب بگذرد. سلیمان] گفت: «این [آب نیست؛ بلکه] کاخی است که با [قطعات] بلور فرش ‌شده است». [آنگاه او را به توحید دعوت داد. ملکه سبأ] گفت: «پروردگارا، من به خود ستم کرده‌ام و [اینک] همراه سلیمان، تسلیم الله ـ پروردگار جهانیان ـ شدم».');
INSERT INTO "islamhouse" VALUES(27,45,'27:45','و به سوی قوم ثمود، برادر‌شان صالح را فرستادیم که [بگوید:] «الله یگانه را عبادت کنید»؛ ولی آنان به دو گروه [مؤمن و کافر] تقسیم شدند و به ستیز پرداختند.');
INSERT INTO "islamhouse" VALUES(27,46,'27:46','[صالح] گفت: «ای قوم من، چرا به جای رحمت، ‌شتابزده خواهان عذاب الهی هستید؟ چرا از الله آمرزش نمی‌خواهید؟ باشد که مورد رحمت قرار گیرید»');
INSERT INTO "islamhouse" VALUES(27,47,'27:47','آنان گفتند: «ما تو و کسانی را ‌که همراهت هستند به فال بد گرفته‌ایم». [صالح] گفت: «فالِ بد شما [و دانشِ آن] نزد الله است؛ بلکه شما گروهی هستید که آزمایش می‌شوید».');
INSERT INTO "islamhouse" VALUES(27,48,'27:48','و در شهر [حِجر] نُه نفر [سردستۀ گروه] بودند که در آن سرزمین به فساد می‌پرداختند و [هیچ]‌ کارِ شایسته‌ای نمی‌کردند.');
INSERT INTO "islamhouse" VALUES(27,49,'27:49','[آنان به یکدیگر] گفتند: «برای همدیگر به الله سوگند یاد کنید که بر او [= صالح] و خانواده‌اش شبیخون بزنیم [و آنان را بکشیم]؛ آنگاه به خونخواهش بگوییم: "ما هنگام کشتارِ خانواده‌اش حاضر نبودیم و قطعاً راست می‌گوییم"».');
INSERT INTO "islamhouse" VALUES(27,50,'27:50','آنان [برای قتل صالح و پیروانش] نیرنگی به کار بردند و ما [نیز برای نابودی کافران] نیرنگی اندیشیدیم؛ در حالی‌ که آنان نمی‌دانستند.');
INSERT INTO "islamhouse" VALUES(27,51,'27:51','بنگر عاقبت نیرنگشان چه شد: ما همۀ آن افراد و قومشان را نابود کردیم.');
INSERT INTO "islamhouse" VALUES(27,52,'27:52','اینک خانه‌هایشان به سزای ستمی که کردند [ویران و] تهی مانده است. بی‌گمان، در این [کیفر،] برای کسانی که می‌دانند [و ایمان دارند] نشانه‌ای [برای عبرت گرفتن] است.');
INSERT INTO "islamhouse" VALUES(27,53,'27:53','و کسانی را که ایمان آورده و پرهیزگار بودند، [همراه صالح] نجات دادیم.');
INSERT INTO "islamhouse" VALUES(27,54,'27:54','و [یاد کن از] لوط آنگاه ‌که به قومش گفت: «با آنكه [زشتى و گناهِ لواط را] می‌دانید، آیا [باز هم] مرتکب این عمل بی‌شرمانه مى‌شوید؟');
INSERT INTO "islamhouse" VALUES(27,55,'27:55','آیا از روى شهوت، به جاى زنان، با مردان درمى‌آمیزید؟ بلکه شما گروهى نادانید».');
INSERT INTO "islamhouse" VALUES(27,56,'27:56','امّا پاسخ قومش جز این نبود که [به یکدیگر] گفتند: «خانوادۀ لوط را از شهر خود بیرون کنید [چرا که] بی‌تردید، اینها افرادی پاکدامن [و مقدس‌مَآب] هستند».');
INSERT INTO "islamhouse" VALUES(27,57,'27:57','آنگاه او و خانوده‌اش را نجات دادیم؛ مگر همسرش را که مقدر کردیم از بازماندگان [در عذاب] باشد.');
INSERT INTO "islamhouse" VALUES(27,58,'27:58','و بارانی [از سنگ] بر آنان باراندیم؛ و بارانِ بیم‌داده‌شدگان چه بد بود!');
INSERT INTO "islamhouse" VALUES(27,59,'27:59','بگو: «ستایش و سپاس از آنِ الله است و سلام بر بندگانش‌ که [آنان را] برگزیده است». آیا الله بهتر است یا آنچه [با او] شریک مى‌سازند؟');
INSERT INTO "islamhouse" VALUES(27,60,'27:60','[آیا معبودان شما بهترند] یا ذاتی که آسمان‌ها و زمین را آفرید و آبی از آسمان برایتان نازل کرد و با آن باران، بوستان‌هایی خرم [و زیبا] رویاندیم که هرگز توان رویاندن درختانش را نداشتید؟ آیا معبود دیگری با الله است؟ [هرگز؛] بلکه آنان مردم منحرف هستند [که مخلوقات را هم‌طرازِ الله قرار می‌دهند].');
INSERT INTO "islamhouse" VALUES(27,61,'27:61','[آیا معبودان شما بهترند] یا ذاتی ‌که زمین را قرارگاه [امنی] ساخت و جویبارهایی میان آن جاری ساخت و کوه‌هایی [استوار] برایش پدید آورد و میان دو دریا مانعی قرار داد [تا آب شور و شیرین با هم نیامیزند]؟ آیا معبود دیگری با الله وجود دارد؟ [هرگز؛] اما بیشتر آنان نمی‌دانند.');
INSERT INTO "islamhouse" VALUES(27,62,'27:62','[آیا معبودان شما بهترند] یا ذاتی ‌که [دعای] درمانده را ـ وقتی او را بخواند- اجابت می‌کند و گرفتاری را برطرف می‌سازد و شما را جانشینان [پیشینیان در] زمین قرار می‌دهد؟ آیا معبود دیگری با الله وجود دارد؟ چه اندک پند می‌گیرید!');
INSERT INTO "islamhouse" VALUES(27,63,'27:63','[آیا معبودان شما بهترند] یا ذاتی ‌که شما را در تاریکی‌های بیابان و دریا [به وسیلۀ ماه و ستارگان] هدایت می‌کند و آن ‌ذاتی که پیش از [باران] رحمتش، باد‌ها را به مژده می‌فرستد؟ آیا معبود دیگری با الله وجود دارد؟ الله از چیزهایی که [برایش] شریک قرار می‌دهند برتر است.');
INSERT INTO "islamhouse" VALUES(27,64,'27:64','[آیا معبودان شما بهترند] یا ذاتی‌ که آفرینش را آغاز کرد و سپس آن را بازمی‌گردانَد و [یا] آن ذاتی ‌که از آسمان و زمین به شما روزی می‌دهد؟ آیا معبود دیگری با الله وجود دارد؟ بگو: «اگر راستگویید، دلیلتان را بیاورید».');
INSERT INTO "islamhouse" VALUES(27,65,'27:65','بگو: «کسانی که در آسمان‌ها و زمین هستند از غیب آگاه نیستند، مگر الله؛ و نمی‌دانند چه زمانی برانگیخته می‌شوند.');
INSERT INTO "islamhouse" VALUES(27,66,'27:66','آیا علمشان دربارۀ آخرت به کمال رسیده است [و درباره آن یقین دارند؟ هرگز؛] بلکه آنان در این مورد، در شک [و حیرانی] هستند؛ بلکه آنان نسبت به [فهمِ] آن [بی‌بصیرت و] کوردلند.');
INSERT INTO "islamhouse" VALUES(27,67,'27:67','و کسانی ‌که کفر ورزیدند گفتند: «آیا هنگامی ‌که ما و پدرانمان خاک شدیم، ما را [زنده می‌کنند و از گور] بیرون می‌آورند؟');
INSERT INTO "islamhouse" VALUES(27,68,'27:68','در حقیقت، این را به ما و پیش از این به پدرانمان [نیز] وعده داده‌اند. این [سخن، چیزی] نیست، مگر افسانه‌های [خیالی] پیشینیان».');
INSERT INTO "islamhouse" VALUES(27,69,'27:69','[ای پیامبر، به کافران] بگو: «در زمین بگردید و بنگرید که سرانجامِ گناهکاران چگونه بوده است».');
INSERT INTO "islamhouse" VALUES(27,70,'27:70','و از [رویگردانیِ] آنان غمگین نباش و از نیرنگ‌هایی که می‌ورزند، دلتنگ نشو.');
INSERT INTO "islamhouse" VALUES(27,71,'27:71','و [کافران] می‌گویند: «اگر راستگویید، این وعده [عذاب] کی خواهد بود؟»');
INSERT INTO "islamhouse" VALUES(27,72,'27:72','بگو: «چه بسا برخى از آنچه به شتاب مى‌خواهید، به شما نزدیک ‌باشد».');
INSERT INTO "islamhouse" VALUES(27,73,'27:73','و بی‌گمان، پروردگارت نسبت به مردم بخشش [و رحمت] دارد؛ ولی بیشتر آنان سپاس نمی‌گزارند.');
INSERT INTO "islamhouse" VALUES(27,74,'27:74','و بی‌تردید، پروردگارت آنچه را در سینه‌هایشان پنهان می‌کنند و آنچه را آشکار می‌سازند، به خوبی می‌داند.');
INSERT INTO "islamhouse" VALUES(27,75,'27:75','و هیچ پنهانی در آسمان و زمین نیست، مگر آنکه در کتابی روشن [= لوح محفوظ] ثبت است.');
INSERT INTO "islamhouse" VALUES(27,76,'27:76','بی‌گمان، این قرآن بیشتر آنچه را بنی‌اسرائیل درباره‌اش اختلاف دارند، برایشان بیان می‌کند.');
INSERT INTO "islamhouse" VALUES(27,77,'27:77','و حقا که رهنمود و رحمتی برای مؤمنان است.');
INSERT INTO "islamhouse" VALUES(27,78,'27:78','مسلماً پروردگارت [در قیامت] به حکم خویش در میان آنان داوری می‌کند و او شکست‌ناپذیرِ داناست.');
INSERT INTO "islamhouse" VALUES(27,79,'27:79','پس بر الله توکل کن. به راستی که تو بر حقیقتی آشکار هستی.');
INSERT INTO "islamhouse" VALUES(27,80,'27:80','و تو هرگز نمی‌توانی مرده‌دلان را شنوا سازی و [ندای] دعوت [حق را] را به گوش كران برسانى؛ آنگاه که آنان پشت می‌کنند و روی می‌گردانند.');
INSERT INTO "islamhouse" VALUES(27,81,'27:81','و تو نمی‌توانی کوردلان را از گمراهی‌شان [بازگردانی و] هدایت کنی. تو [می‌توانی پیام حق را تنها] به گوش کسانی برسانی که به آیات ما ایمان دارند و [در برابر حق،] تسلیمند.');
INSERT INTO "islamhouse" VALUES(27,82,'27:82','و آنگاه که [در آستانۀ قیامت،] فرمان [عذاب] بر آنان واجب گردد، جنبنده‌ای را برایشان از زمین بیرون می‌آوریم که با آنان [در این مورد] سخن بگوید که مردم به آیات ما یقین نداشتند.');
INSERT INTO "islamhouse" VALUES(27,83,'27:83','و [یاد کن از] روزی که از هر امتی، گروهی از آنان که آیات ما را دروغ می‌انگاشتند جمع می‌کنیم؛ آنگاه [برای حسابرسی، به صف] نگاه داشته می‌شوند.');
INSERT INTO "islamhouse" VALUES(27,84,'27:84','تا هنگامی‌ که [به جایگاه حسابرسی] برسند؛ [در آنجا الله] می‌فرماید: «آیا در حالی که نسبت به [حقیقتِ] آیاتم دانشی نداشتید، آنها را دروغ می‌انگاشتید؟ این چه کاری بود که می‌کردید؟»');
INSERT INTO "islamhouse" VALUES(27,85,'27:85','و به خاطر ستمی که مرتکب شدند، فرمان [عذاب] بر آنان واقع می‌گردد و هیچ سخنی [در دفاع از خود] نمی‌گویند.');
INSERT INTO "islamhouse" VALUES(27,86,'27:86','آیا ندیدند که شب را پدید آورده‌ایم تا در آن آرامش یابند و روز را روشنی‌بخش [ساخته‌ایم تا به کار و تلاش بپردازند]؟ بی‌تردید، در این [آفرینش،] برای کسانی که ایمان می‌آورند، نشانه‌هایی [روشن از قدرتِ الله] است.');
INSERT INTO "islamhouse" VALUES(27,87,'27:87','و [یاد کن از] روزی که در صور دمیده می‌شود و هر کس در آسمان‌ها و زمین است وحشت می‌کند، مگر آن کس که الله بخواهد؛ و همگی با خواری [و ذلت] به پیشگاه الله می‌آیند.');
INSERT INTO "islamhouse" VALUES(27,88,'27:88','و کوه‌ها را می‌بینی [و] آنها را بی‌حرکت می‌پنداری؛ حال آنکه مانند گذرِ ابر‌ها در حرکتند. این، آفرینشِ الهی است که هر چیزی را محکم [و استوار] پدید آورده است. بی‌تردید، او به آنچه می‌کنید آگاه است.');
INSERT INTO "islamhouse" VALUES(27,89,'27:89','هر کس [در روز قیامت] نیکی بیاورد، [پاداشی] بهتر از آن خواهد داشت و از وحشت آن روز در امانند.');
INSERT INTO "islamhouse" VALUES(27,90,'27:90','و هر کس بدی بیاورد، با صورت در آتش [دوزخ] نگونسار می‌شوند [و به آنان گفته می‌شود:] «آیا جز در برابر آنچه كرده‌اید مجازات مى‌شوید؟»');
INSERT INTO "islamhouse" VALUES(27,91,'27:91','[ای پیامبر، به کافران بگو:] «من فقط مأمورم که پروردگار این شهر [= مکه] را عبادت کنم؛ همان ذاتی ‌که به اینجا حرمت بخشیده و همه چیز از آنِ اوست؛ و مأمورم که تسلیم [فرمانش] باشم.');
INSERT INTO "islamhouse" VALUES(27,92,'27:92','و اینکه قرآن را تلاوت کنم؛ پس هر‌ کس هدایت شود، به سود خویش هدایت یافته است، و [به] هر ‌کس که گمراه می‌گردد بگو: «من فقط بیم‌دهنده‌ام».');
INSERT INTO "islamhouse" VALUES(27,93,'27:93','و بگو: «ستایش از آنِ الله است. به زودى نشانه‌های [قدرتِ] خویش را به شما نشان خواهد داد و آنها را خواهید شناخت»؛ و پروردگارت از آنچه می‌کنید غافل نیست.');
INSERT INTO "islamhouse" VALUES(28,1,'28:1','طسم [= طا. سین. میم].');
INSERT INTO "islamhouse" VALUES(28,2,'28:2','این آیاتِ کتابِ روشنگر است.');
INSERT INTO "islamhouse" VALUES(28,3,'28:3','[بخش‌هایی] از داستان موسى و فرعون را براى کسانی که ایمان آورده‌اند، به درستى بر تو تلاوت مى‌كنیم.');
INSERT INTO "islamhouse" VALUES(28,4,'28:4','بی‌تردید، فرعون در سرزمین [مصر] برتری‌خواهی کرد و مردمش را به گروه‌هایی تقسیم نمود؛ گروهی از آنان را به ناتوانی [و خواری] می‌کشاند [چنان که] پسرانشان را می‌کشت و زنانشان را [برای کنیزی] زنده نگه می‌داشت. به راستی که او از تبهکاران بود.');
INSERT INTO "islamhouse" VALUES(28,5,'28:5','و می‌خواستیم بر بنی‌اسرائیل، ‌که در آن سرزمین به ناتوانی کشیده شده بودند، [با نابود کردنِ دشمنشان] منت بگذاریم و آنان را پیشوایان [مردم] قرار دهیم و [پس از نابودی فرعون] وارثان [سرزمین شام] نماییم.');
INSERT INTO "islamhouse" VALUES(28,6,'28:6','و در آن سرزمین به آنان قدرت و تمکین بخشیم و [از طرفی] به فرعون و [وزیرش] هامان و سپاهیانشان، آنچه [= سقوط حکومتشان] را که همواره از جانب آنان [=بنی‌اسرائیل] مى‌ترسیدند، نشان دهیم.');
INSERT INTO "islamhouse" VALUES(28,7,'28:7','و به مادر موسی الهام کردیم که: «او را شیر بده و هنگامی‌ که [از آسیب فرعونیان] بر او ترسیدى، او را [در جعبه‌ای بگذار و] به رود [نیل] بینداز و نترس و اندوهگین نشو [زیرا] قطعاً او را به تو بازمی‌گردانیم و از پیامبران قرار می‌دهیم».');
INSERT INTO "islamhouse" VALUES(28,8,'28:8','[پس از اینکه مادر موسی چنین کرد،] خاندان فرعون او را [از آب] گرفتند، تا سرانجام [سال‌ها بعد، این فرزند بنی‌اسرائیل] دشمن و [مایۀ] اندوهشان گردد. به راستی که فرعون و هامان و سپاهیانشان گناهکار بودند.');
INSERT INTO "islamhouse" VALUES(28,9,'28:9','و [چون فرعون دستور قتل موسی را داد، آسیه] همسر فرعون گفت: «[این پسر] نور چشم [و مایه خوشحالی] من و توست. او را نکشید؛ چه بسا سودی به ما ببخشد یا او را به فرزندی بگیریم»؛ حال آنکه [عاقبتِ کار را] نمی‌دانستند.');
INSERT INTO "islamhouse" VALUES(28,10,'28:10','و دل مادر موسی [از همه چیز جز یاد پسرش] تهی گشت و اگر قلبش را مطمئن نكرده بودیم که [به وعدۀ ما] ایمان داشته باشد، نزدیک بود آن [راز] را فاش كند [و آشکارا بگوید که آن نوزاد، پسر اوست].');
INSERT INTO "islamhouse" VALUES(28,11,'28:11','و [مادر موسی پس از آنکه او را در نیل رها کرد] به خواهر او گفت: «به دنبالش برو»؛ [خواهرش] از دور به او می‌نگریست، در حالی ‌که آنان ‌نمی‌دانستند [که آن دختر، خواهرِ این کودک است].');
INSERT INTO "islamhouse" VALUES(28,12,'28:12','و ما [شیرِ] همۀ زنان شیرده را از قبل بر او حرام كردیم [چنان كه سینۀ هیچ یک را نپذیرفت]. آنگاه [خواهرش] گفت: «می‌خواهید خانواده‌ای را به شما نشان دهم که او را برایتان سرپرستی کنند و خیرخواهش باشند؟»');
INSERT INTO "islamhouse" VALUES(28,13,'28:13','[آنان پذیرفتند و به این صورت،] او را به مادرش بازگرداندیم تا چشمش [به او] روشن گردد و غمگین نباشد و بداند که وعدۀ الله حق است؛ ولی بیشتر آنان نمی‌دانند.');
INSERT INTO "islamhouse" VALUES(28,14,'28:14','و چون موسی به رشد و كمال خویش رسید و برومند شد، به او حکمت و دانش [آیین بنی‌اسرائیل را] عطا كردیم و نیكوكاران را اینچنین پاداش مى‌دهیم.');
INSERT INTO "islamhouse" VALUES(28,15,'28:15','هنگامی که مردم [مشغول استراحت و] بی‌خبر بودند، موسی [مخفیانه] وارد شهر شد و دو مرد را دید که با یکدیگر درگیر شده‌اند. یکی از آنان [از قوم بنی‌اسرائیل و] از دوستان موسی بود و دیگری [قِبطی بود و] از دشمنانش. کسی که از دوستان او بود، علیه دشمنش از موسی یاری خواست؛ [موسی] مشت [محکمی] به وی زد و او را از پای درآورد [آنگاه] گفت: «این کار شیطان بود. حقا که او دشمن [انسان و] گمراه‌کنندۀ آشکاری است».');
INSERT INTO "islamhouse" VALUES(28,16,'28:16','[سپس] گفت: «پروردگارا، من به خود ستم کرده‌ام؛ مرا ببخش» و [الله] او را بخشید. بی‌تردید، او تعالی آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(28,17,'28:17','[آنگاه] گفت: «پروردگارا، به شکرانۀ آنکه بر من نعمت [قدرت و دانش] ارزانی داشته‌ای، هرگز پشتیبان مجرمان نخواهم بود».');
INSERT INTO "islamhouse" VALUES(28,18,'28:18','[موسی] شب را با ترس و نگرانى در شهر به صبح رساند كه ناگاه مردى كه دیروز از او یارى خواسته بود، [بار دیگر] به فریاد از او یاری خواست. موسی به او گفت: «واقعاً آشکار است که تو [ماجراجو و] گمراهی».');
INSERT INTO "islamhouse" VALUES(28,19,'28:19','هنگامی ‌که [موسی] خواست به کسی ‌که دشمن هر دوی آنان بود حمله کند، [آن مرد فرياد خواه به تصور آنکه موسی قصد وی را دارد] گفت: «ای موسی، آیا می‌خواهی همان گونه که دیروز کسی را کشتی، [امروز نیز] مرا بکشی؟ قصدِ تو فقط زورگویی در این سرزمین است و نمی‌خواهی از اصلاح‌گران [بین افراد] باشی».');
INSERT INTO "islamhouse" VALUES(28,20,'28:20','و [در این هنگام] مردی [که از دوستان موسی بود] از دورترین نقطۀ شهر شتابان آمد [و] گفت: «ای موسی، بزرگان [قوم فرعون] دربارۀ کشتنت به مشورت نشسته‌اند؛ پس بی‌درنگ [از شهر] خارج شو [که] من قطعاً خیرخواهت هستم».');
INSERT INTO "islamhouse" VALUES(28,21,'28:21','موسی با ترس و نگرانی از شهر خارج شد [و] گفت: «پروردگارا، مرا از [آسیبِ] ستمکاران نجات بده».');
INSERT INTO "islamhouse" VALUES(28,22,'28:22','و چون به سوی [شهر] مَدیَن روی آورد گفت: «امید است پروردگارم مرا به راه راست هدایت کند».');
INSERT INTO "islamhouse" VALUES(28,23,'28:23','و هنگامی که به [چاه] آب مدین رسید، بر سرِ چاه، گروهی از مردم را دید که [چهارپایان خود را] آب می‌دهند و نزدیک آنان دو زن را دید که [گوسفندانشان را] کنار می‌کشیدند [و از دیگران فاصله می‌گرفتند. [موسی] گفت: «شما چه می‌کنید؟ [چرا گوسفند‌ان خود را آب نمی‌دهید؟]» آنان گفتند: «ما [به گلۀ خود] آب نمی‌دهیم تا این چوپان‌ها [گوسفندانشان را از آب] برگردانند [و چاه آب خلوت ‌شود]. و پدرمان پیرمردی کهنسال است [و توانایی این کار را ندارد]».');
INSERT INTO "islamhouse" VALUES(28,24,'28:24','موسی به [گوسفندان] آن دو زن آب داد؛ آنگاه به سوی سایه بازگشت و گفت: «پروردگارا، به هر خیری که برایم بفرستی، سخت نیازمندم».');
INSERT INTO "islamhouse" VALUES(28,25,'28:25','آنگاه یکی از آن دو [زن] ـ‌ در حالی ‌که با شرم و حیا گام برمی‌داشت‌ـ نزدش آمد و گفت: «پدرم تو را دعوت می‌کند [که نزدش بروی] تا مزد آنکه [گوسفندان] را برایمان آب دادی به تو بپردازد». چون [موسی] نزد او رفت و سرگذشت خویش را برایش حکایت کرد، وی گفت: «نترس که از گروه ستمکار نجات یافته‌ای».');
INSERT INTO "islamhouse" VALUES(28,26,'28:26','یکی از آن دو [دختر] گفت: «پدر جان، او را استخدام کن [زیرا] بی‌تردید، بهترین کسی که می‌توانی به کار بگیری، آن است که نیرومند و امانتدار باشد [و او چنین است]».');
INSERT INTO "islamhouse" VALUES(28,27,'28:27','[پیرمرد به موسی] گفت: «می‌خواهم یکی از دو دخترم را به همسری‌ات درآورم؛ به این [شرط] ‌که هشت سال برایم کار کنی؛ و اگر [این کار را] تا ده سال ادامه دهی، لطف و محبتی از سوی توست و من نمی‌خواهم بر تو سخت بگیرم. ان شاء الله مرا شایسته [و نیکوکار] خواهی یافت».');
INSERT INTO "islamhouse" VALUES(28,28,'28:28','[موسی] گفت: «این [قرارداد] میان من و تو باشد که هر کدام از این دو مدت [هشت یا ده سال] را که انجام دادم [به عهدِ خود وفا کرده‌ام]؛ پس ستمی بر من نباشد [و نخواهی که بیشتر بمانم]؛ و الله بر آنچه می‌گوییم گواه است».');
INSERT INTO "islamhouse" VALUES(28,29,'28:29','هنگامی ‌که موسی [آن] مدت [معیّن] را به پایان رساند و همراه خانواده‌اش [از مدین به سوی مصر] حرکت کرد، [ناگهان در تاریکیِ شب،] از سوی [کوه] طور آتشی دید؛ به خانواده‌اش گفت: «[اینجا] بایستید كه من آتشى [از دور] دیدم. شاید خبری از آن برایتان بیاورم یا شعلۀ آتشی [بیاورم] تا گرم شوید».');
INSERT INTO "islamhouse" VALUES(28,30,'28:30','وقتی به آن آتش نزدیک شد، در کنارۀ راست آن سرزمین، در منطقۀ پربرکت [طور]، از [میان] یک درخت ندا داده شد که: «ای موسی، بی‌تردید من الله، پروردگار جهانیانم.');
INSERT INTO "islamhouse" VALUES(28,31,'28:31','و عصایت را بیفکن». وقتی [موسی عصایش را افکند و] آن را دید که همچون ماری مى‌جنبد، [از ترس] پشت کرد و گریخت و بازنگشت. [ندا آمد:] «ای موسی، پیش آی و نترس. قطعاً تو از [آسیب خطرات] ایمن شده‌ای.');
INSERT INTO "islamhouse" VALUES(28,32,'28:32','و دستت را در گریبانت فرو بر [تا] سپید و بدون عیب [و بدون بیماری پیسی] خارج ‌شود؛ و [براى رهایى] از این ترس، دستانت را به سمت خود جمع كن [و به سینه‌ات بچسبان]. این دو [معجزۀ عصا و ید بَیضا] دو دلیل [روشن] از سوی پروردگارت برای فرعون و بزرگانِ [قومِ] اوست. به راستی که آنان گروهی نافرمانند».');
INSERT INTO "islamhouse" VALUES(28,33,'28:33','[موسی] گفت: «پروردگارا، من یکی از آنان را [بدون عمد] کشته‌ام؛ می‌ترسم که [به قصاص آن قتل،] مرا بکشند.');
INSERT INTO "islamhouse" VALUES(28,34,'28:34','و برادرم ـ هارون‌ـ زبانش از من شیواتر است؛ پس او را همراهم بفرست تا یاورم باشد و راستگویی‌ام را تأیید کند؛ زیرا می‌ترسم مرا تكذیب کنند».');
INSERT INTO "islamhouse" VALUES(28,35,'28:35','[الله] فرمود: «تو را با [پیوستنِ] برادرت تقویت خواهیم نمود و سلطه و برتری برایتان قرار می‌دهیم که [فرعونیان هرگز] به شما دست نیابند؛ و به [برکتِ] معجزاتمان [که در اختیار دارید] شما و هر کس ‌که از شما پیروی کند، پیروزید».');
INSERT INTO "islamhouse" VALUES(28,36,'28:36','هنگامی که موسی با معجزات روشن ما به سویشان آمد، گفتند: «این [چیزی] نیست مگر جادویی ساختگی؛ و ما هرگز این [ادعای عبادت الله به یگانگی] را از پدرانمان نشنیده‌ایم».');
INSERT INTO "islamhouse" VALUES(28,37,'28:37','و موسی گفت: «پروردگارم بهتر مى‌داند چه كسى هدایت را از نزد او آورده است و [آگاه‌تر است که] عاقبت نیکِ آن سرا براى کیست. بی‌تردید، ستمکاران رستگار نمی‌شوند».');
INSERT INTO "islamhouse" VALUES(28,38,'28:38','فرعون گفت: «ای بزرگان، معبودی جز خود برایتان نمی‌شناسم؛ پس ای هامان، برایم آتشی بر گِل برافروز [و آجر بپز] آنگاه برج بلندی بساز؛ باشد که از معبودِ موسی باخبر شوم؛ هر چند که قطعاً او را دروغگو می‌پندارم».');
INSERT INTO "islamhouse" VALUES(28,39,'28:39','و او [= فرعون] و لشکریانش به ناحق در سرزمین [مصر] سر‌کشی کردند و پنداشتند که به سوی ما بازگردانده نمی‌شوند.');
INSERT INTO "islamhouse" VALUES(28,40,'28:40','[ما] او و لشکریانش را [به عذاب] فروگرفتیم و آنان را به دریا انداختیم؛ پس بنگر که عاقبت ستمکاران چگونه بود.');
INSERT INTO "islamhouse" VALUES(28,41,'28:41','و آنان را پیشوایانی قرار دادیم که [پیروانشان را] به آتش [دوزخ] دعوت می‌کردند؛ و روز قیامت یاری نمی‌شوند.');
INSERT INTO "islamhouse" VALUES(28,42,'28:42','در این دنیا، به دنبالشان لعنت روانه کردیم و روز قیامت [نیز] زشت‌منظرند.');
INSERT INTO "islamhouse" VALUES(28,43,'28:43','و به راستی، پس از آنکه امت‌های پیشین را نابود نمودیم، به موسی کتاب [تورات] دادیم که [دارای] روشنگری‌ها و هدایت و رحمتی برای مردم بود؛ باشد که پند پذیرند.');
INSERT INTO "islamhouse" VALUES(28,44,'28:44','و تو [ای پیامبر،] آنگاه که فرمان [نبوت] را به موسی می‌دادیم در دامنۀ غربی [کوه طور] حضور نداشتی و از گواهان [نیز] نبودی.');
INSERT INTO "islamhouse" VALUES(28,45,'28:45','و [ما پس از موسی] نسل‌هایی آفریدیم و سال‌های طولانی بر آنان گذشت [و پیمان الهی را فراموش کردند]؛ و تو در میان اهل مَدیَن اقامت نداشتی [كه از داستان موسی باخبر باشی و] آیات ما را بر آنان [= مردم مکه] بخوانى؛ بلکه ما بودیم که [این آیات را به سویت] می‌فرستادیم.');
INSERT INTO "islamhouse" VALUES(28,46,'28:46','و آنگاه كه [به موسى] ندا دادیم، تو در كنار كوه طور نبودى؛ ولى به عنوان رحمتى از سوی پروردگارت [به تو وحى نمودیم] تا گروهى را كه پیش از تو بیم‌دهنده‌اى به سراغشان نیامده است [با این آیات] بیم دهى؛ باشد که پند پذیرند.');
INSERT INTO "islamhouse" VALUES(28,47,'28:47','[با کافران اتمام حجت کردیم] تا وقتی به کیفر کارهایی كه در گذشته مرتكب شده‌اند مصیبتى به آنها می‌رسد نگویند: «پروردگارا، چرا پیامبری بر ما نفرستادی تا از آیاتت پیروی کنیم و از مؤمنان باشیم؟»');
INSERT INTO "islamhouse" VALUES(28,48,'28:48','ولى هنگامى كه [پیامبرِ] حق، از جانب ما به سراغشان آمد، [یهود از سرِ لجاجت] گفتند: «چرا معجزاتی همانند موسى به او داده نشده است؟» [در پاسخشان بگو:] «مگر [یهود] در گذشته معجزات موسى را انكار نكردند؟» آنان گفتند: «[قرآن و تورات] دو جادو هستند که یکدیگر را تأیید می‌کنند» و گفتند: «ما تمام [مطالب] آنها را انكار مى‌كنیم».');
INSERT INTO "islamhouse" VALUES(28,49,'28:49','بگو: «اگر راستگویید، از جانب الله، كتابى هدایت‌بخش‌تر از این دو [کتاب] بیاورید تا من نیز از آن پیروى كنم».');
INSERT INTO "islamhouse" VALUES(28,50,'28:50','اگر [قریش] به درخواستت پاسخی ندادند، بدان که آنان فقط از هوای نفسشان پیروی می‌کنند؛ و کیست گمراه‌تر از آن کس که بدون رهنمودی از سوی الله، از هوای نفسش پیروی می‌کند؟ بی‌تردید، الله گروه ستمکار را هدایت نمی‌کند.');
INSERT INTO "islamhouse" VALUES(28,51,'28:51','و به راستی، ما این آیات [و حکایات] را یکی پس از دیگری برای آنان [= مشرکان و یهود] نازل کردیم؛ باشد که پند گیرند.');
INSERT INTO "islamhouse" VALUES(28,52,'28:52','کسانی ‌که پیش از این [آیات الهی] کتاب [تورات] به آنان داده‌ایم، به این [قرآن] نیز ایمان می‌آورند');
INSERT INTO "islamhouse" VALUES(28,53,'28:53','و چون [آیاتش] بر آنان خوانده می‌شود، می‌گویند: «به آن ایمان آوردیم. یقیناً سخن حقی از جانب پروردگارمان است. به راستی که ما پیش از [نزول] آن نیز تسلیم [فرمان پروردگار] بوده‌ایم».');
INSERT INTO "islamhouse" VALUES(28,54,'28:54','اینانند که به پاسِ آنکه شکیبایی ورزیدند، پاداششان را دو بار می‌گیرند و با کارهای نیک، [گناهان و] بدی‌ها را دفع می‌کنند و از آنچه به آنان روزی داده‌ایم انفاق می‌نمایند.');
INSERT INTO "islamhouse" VALUES(28,55,'28:55','و هر گاه سخنی بیهوده [و ناشایست از اهل کتاب] می‌شنوند، [با بی‌توجهی] از آن روی ‌می‌گردانند و می‌گویند: «[پاداش] کارهای ما برای ماست و [کیفرِ] کارهای شما از آنِ شماست؛ [شما از آزار و ناسزایِ ما] در امانید. ما به دنبالِ [هم‌صحبتی با] جاهلان نیستیم».');
INSERT INTO "islamhouse" VALUES(28,56,'28:56','[ای پیامبر،] بی‌تردید، تو نمی‌توانی هر کس را دوست داشته باشی [به اجبار به راه راست] هدایت ‌کنی؛ بلکه الله است که هر کس را بخواهد هدایت می‌کند و او به [حال] راه‌یافتگان داناتر است‌.');
INSERT INTO "islamhouse" VALUES(28,57,'28:57','و [مشرکان] گفتند: «اگر همراه تو از هدایت پیروی کنیم، ما را از سرزمینمان می‌ربایند». آیا ما آنان را در حرم امنی جای ندادیم که انواع محصولات ـ به عنوان روزی‌ای از جانب ما ـ به سویشان سرازیر می‌گردد؟ ولی بیشتر آنان [قدر نعمت‌های الهی را] نمی‌دانند.');
INSERT INTO "islamhouse" VALUES(28,58,'28:58','و چه بسیار [مردم] شهرهایی را نابود ساختیم که [از شدّت رفاه‌] در زندگی خویش، طغیان و سرکشی کرده بودند. این خانه‌های [ویران‌شدۀ] آنان است که پس از آنان ـ‌ جز اندکی‌ـ [کسی] در آنها سکونت نکرده است و ما وارث آنان بودیم.');
INSERT INTO "islamhouse" VALUES(28,59,'28:59','و پروردگارت هرگز [مردم] شهرها را نابود نمی‌کند، مگر آنکه در شهر بزرگی از آنها [همچون مکه] پیامبری بفرستد که آیات ما را بر آنان بخواند؛ و ما هرگز شهرها را نابود نکردیم، مگر آنکه ساکنانش [کافر و] ستمکار بوده‌اند.');
INSERT INTO "islamhouse" VALUES(28,60,'28:60','و آنچه به شما داده شده است، بهره[ی گذرا] و زینتِ زندگی دنیاست؛ و آنچه نزد الله است، بهتر و پایدار‌تر است. آیا نمی‌اندیشید؟');
INSERT INTO "islamhouse" VALUES(28,61,'28:61','آیا کسی که به او وعدۀ نیکو داده‌ایم و به آن خواهد رسید، همچون کسی است که او را از بهرۀ زندگی دنیا برخوردار ساخته‌ایم و روز قیامت از [جمله] احضارشدگان [در آتش دوزخ] است؟');
INSERT INTO "islamhouse" VALUES(28,62,'28:62','و [یاد کن از] روزى كه الله به آنان ندا مى‌دهد و مى‌فرماید‌: افرادى كه شریکانِ من [در عبادت] می‌پنداشتید كجا هستند؟»');
INSERT INTO "islamhouse" VALUES(28,63,'28:63','کسانی [از سران کفار] که عذاب برایشان حتمی است می‌گویند: «پروردگارا، اینها کسانی هستند که ما گمراهشان کردیم. [آری،] آنان را گمراه کردیم، چنان که [خود نیز] گمراه شدیم. از [شرک و توسل] آنان به سویت بیزاری می‌جوییم. [در حقیقت،] آنان ما را عبادت نمی‌کردند [بلکه شیطان را عبادت می‌کردند]».');
INSERT INTO "islamhouse" VALUES(28,64,'28:64','و [به مشرکان] گفته می‌شود: «شریکانتان را [که به جای الله عبادت می‌کردید] فرابخوانید [تا یاری‌تان کنند]»؛ آنگاه آنان را می‌خوانند، ولی پاسخی به اینان نمی‌دهند و عذاب [دوزخ] را می‌بینند [و آرزو می‌کنند] کاش هدایت‌یافته بودند.');
INSERT INTO "islamhouse" VALUES(28,65,'28:65','و روزی‌ که [الله] به آنان ندا می‌دهد و می‌فرماید: «به [دعوتِ] پیامبران چه پاسخی دادید؟»');
INSERT INTO "islamhouse" VALUES(28,66,'28:66','در آن روز، همۀ دلایل [و بهانه‌هایی که برای توجیه دارند] از آنان پنهان می‌گردد و از [شدت عذاب، از حالِ] یکدیگر نمی‌پرسند.');
INSERT INTO "islamhouse" VALUES(28,67,'28:67','اما کسی ‌که توبه کند و ایمان آورَد و کار شایسته انجام دهد، امید است که از رستگاران باشد.');
INSERT INTO "islamhouse" VALUES(28,68,'28:68','و پروردگارت هر چه بخواهد می‌آفریند و [هر کس را بخواهد به عبادت و پیامبری] برمی‌گزیند. مشرکان اختیاری ندارند [که در این مورد اعتراض کنند]. الله از چیزهایی که با او شریک می‌دانند منزه و برتر است.');
INSERT INTO "islamhouse" VALUES(28,69,'28:69','و پروردگارت آنچه در سینه‌هایشان پنهان می‌کنند و آنچه آشکار می‌سازند، [همه را] می‌داند.');
INSERT INTO "islamhouse" VALUES(28,70,'28:70','و او الله است که هیچ معبودی [به‌حق] جز او نیست. ستایش در دنیا و آخرت برای اوست و حُکم [و فرمانروایی نیز] از آنِ اوست و [همگی] به سوی او بازگردانده می‌شوید.');
INSERT INTO "islamhouse" VALUES(28,71,'28:71','[به مشرکان] بگو: «به من بگویید اگر الله [تاریکیِ] شب را تا روز قیامت بر شما پاینده گردانَد، كدام معبود به جز الله برایتان روشنى مى‌آورَد؟ آیا [سخن حق را] نمی‌شنوید؟»');
INSERT INTO "islamhouse" VALUES(28,72,'28:72','بگو: «به من بگویید اگر الله [روشناییِ] روز را تا روز قیامت بر شما پاینده گردانَد، كدام معبود جز الله شبی برایتان می‌آورد که در آن آرام بگیرید؟ آیا [این نشانه‌های قدرت الهی را] نمی‌بینید؟');
INSERT INTO "islamhouse" VALUES(28,73,'28:73','و الله از رحمت خویش شب و روز را برایتان قرار داد تا در شب آرام گیرید و در روز [تلاش کنید و] از بخشش او [روزی] بجویید و باشد که سپاس گزارید.');
INSERT INTO "islamhouse" VALUES(28,74,'28:74','و [یاد کن از] روزی‌ که الله به آنان ندا می‌دهد و می‌فرماید: «کجا هستند شریکانی که برای من می‌پنداشتید؟»');
INSERT INTO "islamhouse" VALUES(28,75,'28:75','و از هر امتی، گواهی برمی‌گزینیم [که پیامبر آنان است و در مورد کفرشان گواهی می‌دهد]؛ سپس به آنان می‌گوییم: «دلیل [کفر و شرکِ] خویش را بیاورید»؛ پس می‌دانند که حق از آنِ الله است و دروغ‌هایی که می‌بافتند [و آنچه مشرکانه عبادت می‌کردند] محو و نابود گشته است.');
INSERT INTO "islamhouse" VALUES(28,76,'28:76','قارون از قوم موسی بود؛ اما بر آنان فخرفروشی نمود؛ و از گنجینه‌ها، آنقدر به وی داده بودیم که [حتی] حمل کلید‌هایش برای گروهی نیرومند مشکل بود. هنگامی ‌که [مردم] قومش به او گفتند: «[به خاطر ثروتت، چنین مغرور و] سرمست نباش. بی‌تردید، الله سرمستان را دوست ندارد.');
INSERT INTO "islamhouse" VALUES(28,77,'28:77','و با آنچه الله به تو بخشیده است، [ثوابِ] سرای آخرت را بجوی و [در عین حال،] بهره‌ات را از [زندگی] دنیا نیز فراموش نکن؛ و چنان که الله به تو نیکی کرده است، تو نیز [به بندگانش] نیکی کن و هرگز در پیِ تبهکاری [و فساد] در زمین نباش. بی‌تردید، الله مفسدان را دوست ندارد».');
INSERT INTO "islamhouse" VALUES(28,78,'28:78','[قارون] گفت: «آنچه به من داده شده، در نتیجۀ [قدرت و] دانشی است که نزد من است». آیا نمی‌دانست که الله نسل‌هایی را پیش از وی نابود کرده بود که از او نیرومندتر و ثروتمندتر بودند؟ و [روز قیامت، اینگونه] مجرمان از گناهانشان پرسيده نمى‌شوند [و بى‌حساب دوزخ مى‌روند].');
INSERT INTO "islamhouse" VALUES(28,79,'28:79','قارون با [تجملات و] زینت‌هایش در برابر قوم خویش ظاهر شد. افرادی ‌[از اطرافیان او] که خواستار زندگی دنیا بودند، گفتند: «ای کاش همانند آنچه به قارون داده شده است ما [نیز] داشتیم. واقعاً او بهرۀ بزرگی [از مال دنیا] دارد».');
INSERT INTO "islamhouse" VALUES(28,80,'28:80','اما کسانی که دانش [حقیقی] یافته بودند، گفتند: «وای بر شما! برای کسی ‌که ایمان آورده است و کارهای شایسته می‌کند، ثواب الهی بهتر است؛ و این [سخن] را کسانی جز شکیبایان نمی‌پذیرند».');
INSERT INTO "islamhouse" VALUES(28,81,'28:81','سپس قارون‌ را همراه خانه‌اش در زمین فروبردیم؛ آنگاه هیچ گروهى نداشت كه او را در برابر [عذاب‌] الله یاری كنند و [خود نیز] نتوانست به خویشتن یاری رساند.');
INSERT INTO "islamhouse" VALUES(28,82,'28:82','و کسانی ‌که دیروز آرزو می‌کردند به جای او باشند، بامداد [روز بعد] می‌گفتند: «وای بر ما! گویی الله است که [نعمت و] روزی را بر هر کس که بخواهد گسترده می‌دارد و [یا] تنگ می‌گرداند. اگر الله بر ما منّت ننهاده بود، قطعاً ما را [نیز به سزای سخنانمان به ژرفای زمین] فرومی‌بُرد، ای وای! گویی کافران هرگز رستگار نمی‌شوند.');
INSERT INTO "islamhouse" VALUES(28,83,'28:83','این سرای آخرت را [فقط] برای کسانی قرار می‌دهیم که خواهان گردنکشی و فساد در زمین نیستند؛ و سرانجامِ [نیک]، از آنِ پرهیزگاران است.');
INSERT INTO "islamhouse" VALUES(28,84,'28:84','[روز قیامت،] هر ‌کس نیکی بیاورد، [پاداشی] بهتر از آن خواهد داشت و هر ‌کس بدی بیاورد، [بداند] کسانی ‌که کارهای ناشایست کرده‌اند، جز [در برابر] آنچه می‌کردند کیفر نمی‌بینند.');
INSERT INTO "islamhouse" VALUES(28,85,'28:85','همان [پروردگاری] ‌که قرآن را [نازل نمود و تبلیغ آن را] بر تو واجب کرد، قطعاً تو را به جایگاهت [در مکه] بازمی‌گردانَد. بگو: «پروردگارم بهتر می‌داند چه کسی هدایت آورده است و چه کسی در گمراهیِ آشکار است».');
INSERT INTO "islamhouse" VALUES(28,86,'28:86','و [پیش از بعثت،] تو انتظار نداشتی که این کتاب [آسمانی] بر تو نازل شود. [این نبود،] مگر رحمتى از جانب پروردگارت. پس هرگز پشتیبان کافران نباش.');
INSERT INTO "islamhouse" VALUES(28,87,'28:87','و مبادا [کافران] تو را از [تبلیغ‌] آیات الهی ـ پس از آنكه بر تو نازل شد ـ بازدارند! و [مردم را] به سوی پروردگارت دعوت کن و هرگز از مشرکان نباش.');
INSERT INTO "islamhouse" VALUES(28,88,'28:88','و معبود دیگری با الله [به نیایش] نخوان. هیچ معبودی [به‌حق] جز او نیست. همه چیز فناپذیر است؛ مگر [ذات جاوید و] روی او. فرمانروایی [تنها] از آنِ اوست و [روز قیامت، همگی] به پیشگاه او تعالی بازگردانده می‌شوید.');
INSERT INTO "islamhouse" VALUES(29,1,'29:1','الم [= الف. لام. میم].');
INSERT INTO "islamhouse" VALUES(29,2,'29:2','آیا مردم پنداشته‌اند همین که [به زبان] بگویند: «ایمان آوردیم»، آنان را [به حال خود] وامی‌گذارند و آزموده نمی‌شوند؟');
INSERT INTO "islamhouse" VALUES(29,3,'29:3','و به راستی کسانی را که پیش از آنان بودند نیز آزمودیم و تا الله کسانی را که [دربارۀ ایمانشان] راست گفته‌اند جدا سازد و دروغگویان را [نیز] معلوم گردانَد.');
INSERT INTO "islamhouse" VALUES(29,4,'29:4','آیا کسانی که کارهای ناشایسته می‌کنند، پنداشته‌اند که می‌توانند ما را مغلوب نمایند [و از عذابمان بگریزند]؟ چه بد داوری می‌کنند!');
INSERT INTO "islamhouse" VALUES(29,5,'29:5','هر کس به دیدار الله امید دارد، [بداند که] وعدۀ الله قطعاً خواهد آمد و او تعالی شنوای داناست.');
INSERT INTO "islamhouse" VALUES(29,6,'29:6','و هر کس [در راه حق‌] بکوشد، به سود خویش کوشیده است. بی‌تردید، الله از جهانیان بی‌نیاز است‌.');
INSERT INTO "islamhouse" VALUES(29,7,'29:7','و کسانی ‌که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، یقیناً گناهانشان را می‌زداییم و آنان را بر اساس نیکوترین [طاعت و] کاری که انجام داده‌اند پاداش می‌دهیم‌.');
INSERT INTO "islamhouse" VALUES(29,8,'29:8','و به انسان سفارش کردیم که به پدر و مادرش نیکی نماید؛ و [گفتیم:] «اگر آن دو کوشیدند تا چیزی را که تو به آن دانشی نداری شریک من سازی، از آنان اطاعت نکن. بازگشتتان به سوی من است، آنگاه شما را از [حقیقت] آنچه می‌کردید، آگاه خواهم کرد.');
INSERT INTO "islamhouse" VALUES(29,9,'29:9','و کسانی ‌که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، البته آنان را در [زمرۀ] شایستگان درمی‌آوریم.');
INSERT INTO "islamhouse" VALUES(29,10,'29:10','و برخی از مردم هستند که می‌گویند: «به الله ایمان آورده‌ایم»؛ اما هنگامی که در [راه] الله آزار می‌بینند، آزار مردم را همچون عذاب الله [سخت و جانکاه] می‌دانند [و از ایمانشان دست می‌کشند]؛ و اگر از سوی پروردگارت پیروزی [و گشایشی برایتان] پیش آید، می‌گویند: «بی‌تردید، ما [نیز] با شما همراه بودیم». آیا الله به آنچه در دل‌های جهانیان است داناتر نیست؟');
INSERT INTO "islamhouse" VALUES(29,11,'29:11','و به راستی، الله کسانی را که ایمان آورده‌اند به خوبی می‌شناسد و یقیناً منافقان را [نیز] می‌شناسد.');
INSERT INTO "islamhouse" VALUES(29,12,'29:12','و کسانی ‌که کفر ورزیدند به کسانی ‌که ایمان آوردند می‌گفتند: «از راه ما پیروی کنید، و قطعاً بار گناهانتان را به عهده می‌گیریم»؛ و[لی] آنان هرگز چیزی از گناهانشان را بر عهده نخواهند گرفت. به راستی که آنان دروغگو هستند.');
INSERT INTO "islamhouse" VALUES(29,13,'29:13','و قطعاً بارِ سنگین [گناه] خود و بارهای سنگین [گناه پیروانشان] را همراه بارِ سنگین خود به دوش خواهند کشید؛ و یقیناً روز قیامت، در مورد دروغ‌هایی كه مى‌بافتند بازخواست خواهند شد.');
INSERT INTO "islamhouse" VALUES(29,14,'29:14','و ما نوح را به سوی قومش فرستادیم و او نهصد و پنجاه سال در میان آنان بود. آنگاه در حالی ‌که ستمکار [و کافر] بودند طوفان [سهمگین] آنان را فراگرفت.');
INSERT INTO "islamhouse" VALUES(29,15,'29:15','او [= نوح] و سرنشینان کشتی را نجات دادیم و آن [کشتی] را نشانه‌ای برای [عبرت گرفتنِ] جهانیان قرار دادیم.');
INSERT INTO "islamhouse" VALUES(29,16,'29:16','و [یاد کن از] ابراهیم آنگاه ‌که به قومش گفت: «الله را عبادت کنید و از او پروا کنید [که] اگر بدانید، این کار برایتان بهتر است.');
INSERT INTO "islamhouse" VALUES(29,17,'29:17','جز این نیست که به جای الله، فقط بُت‌هایی را عبادت می‌کنید و [با این کار، به الله] دروغ می‌بندید. در حقیقت، کسانی را که شما به جای الله عبادت می‌کنید، اختیار روزی‌تان را ندارند؛ پس [نعمت و] روزی را تنها از پیشگاهِ الله طلب کنید و او را عبادت کنید و شکر او را به جای آورید [که] به سوی او بازگردانده می‌شوید.');
INSERT INTO "islamhouse" VALUES(29,18,'29:18','و [ای مشرکان،] اگر شما [محمد را] دروغگو بدانید، [عجیب نیست.] به راستی که برخی از امت‌های پیش از شما [نیز، پیامبران الهی را] تکذیب کردند؛ و پیامبر وظیفه‌ای جز رساندن آشكار [وحی] ندارد».');
INSERT INTO "islamhouse" VALUES(29,19,'29:19','آیا ندیدند که الله چگونه آفرینش را آغاز می‌کند سپس آن را [پس از مرگش] بازمی‌گرداند؟ قطعاً این کار برای الله آسان است.');
INSERT INTO "islamhouse" VALUES(29,20,'29:20','[ای پیامبر، به کافران] بگو: «در زمین بگردید و بنگرید که الله چگونه آفرینش را آغاز کرد؛ [به همین صورت نیز] زندگی دیگر را پدید می‌آورَد. بی‌تردید، الله بر هر کاری تواناست.');
INSERT INTO "islamhouse" VALUES(29,21,'29:21','او تعالی هر ‌کس را بخواهد، عذاب می‌کند و بر هر ‌کس که بخواهد، رحمت می‌آورد؛ و [همگی] به سویش بازگردانده می‌شوید.');
INSERT INTO "islamhouse" VALUES(29,22,'29:22','شما ـ [نه] در زمین و نه در آسمان ـ هرگز ناتوان‌کنندۀ او تعالی نیستید و جز الله هیچ کارساز و یاوری ندارید».');
INSERT INTO "islamhouse" VALUES(29,23,'29:23','و کسانی که به آیاتِ الله و دیدار او کفر ورزیدند، آنانند که از رحمت من مأیوسند و عذاب دردناکی برایشان [در پیش] است.');
INSERT INTO "islamhouse" VALUES(29,24,'29:24','اما پاسخ قوم او [= ابراهیم] جز این نبود که گفتند: «او را بکشید یا بسوزانید»؛ پس الله او را از آتش نجات داد. بی‌تردید، در این [رهایی از آتش،] برای گروهی ‌که ایمان می‌آورند نشانه‌هایی [برای عبرت گرفتن] است.');
INSERT INTO "islamhouse" VALUES(29,25,'29:25','و [ابراهیم] گفت: «شما به جای الله بت‌هایی برای خود برگزیده‌اید که [مایۀ دوستی و] محبت میان شما در زندگی دنیا باشد. آنگاه روز قیامت، از یکدیگر بیزاری می‌جویید و همدیگر را لعنت می‌کنید و جایگاهتان آتش است و هیچ یاریگری نخواهید داشت».');
INSERT INTO "islamhouse" VALUES(29,26,'29:26','لوط به او ایمان آورد و [ابراهیم] گفت: «من به سوی پروردگارم [به سرزمین شام] هجرت می‌کنم. بی‌تردید، او شکست‌ناپذیرِ حکیم است».');
INSERT INTO "islamhouse" VALUES(29,27,'29:27','و اسحاق و یعقوب را به او بخشیدیم و میان فرزندانش، نبوت و کتاب [آسمانی] قرار دادیم و پاداشش را در این دنیا دادیم؛ و یقیناً او در آخرت [نیز] از شایستگان خواهد بود.');
INSERT INTO "islamhouse" VALUES(29,28,'29:28','و [یاد کن از] لوط هنگامی که به قومش گفت: «واقعاً کار بسیار زشتی می‌کنید که پیش از شما هیچ‌یک از [مردم] جهان، آن را انجام نداده است.');
INSERT INTO "islamhouse" VALUES(29,29,'29:29','آیا با مردان می‌آمیزید و [به قصد تجاوز به مسافران،] راه را می‌بندید و در محافل عمومی خود مرتکب کارهای ناپسند می‌شوید؟» پاسخ قومش جز این نبود که گفتند: «اگر راستگویی، عذاب الهی را پیش آور».');
INSERT INTO "islamhouse" VALUES(29,30,'29:30','[لوط] گفت: «پروردگارا، مرا در برابر مفسدان یاری کن».');
INSERT INTO "islamhouse" VALUES(29,31,'29:31','و هنگامی ‌که فرستادگان ما به ابراهیم بشارت [تولد پسرش اسحاق را] دادند، به او گفتند: «ما ساکنان شهر [سَدوم] را قطعاً نابود خواهیم کرد؛ [چرا که] بی‌تردید، مردم آنجا ستمکارند».');
INSERT INTO "islamhouse" VALUES(29,32,'29:32','[ابراهیم] گفت: «لوط [نیز] در آنجاست». فرشتگان گفتند: «ما بهتر مى‌دانیم چه كسانى در آنجا هستند. او و خانواده‌اش را حتماً نجات خواهیم داد؛ مگر همسرش، که از بازماندگان [در عذاب] خواهد بود».');
INSERT INTO "islamhouse" VALUES(29,33,'29:33','و چون فرستادگان ما نزد لوط آمدند، از [تصور بی‌شرمیِ قومش نسبت به] آنان نگران و دلتنگ شد؛ و [فرشتگان] گفتند: «نترس و اندوهگین نباش. ما تو و خانواده‌ات را حتماً نجات می‌دهیم؛ مگر همسرت را ‌که از بازماندگان [در عذاب] است.');
INSERT INTO "islamhouse" VALUES(29,34,'29:34','ما بر ساکنان این شهر ـ به کیفر آنکه نافرمانی [و گناه] ‌کرده‌اندـ عذابی از آسمان فرود می‌آوریم».');
INSERT INTO "islamhouse" VALUES(29,35,'29:35','و در حقیقت، ما از آن شهر [ویران‌شده]، نشانه [و عبرت] آشکاری، برای کسانی که می‌اندیشند باقی گذاشتیم.');
INSERT INTO "islamhouse" VALUES(29,36,'29:36','و به سوی [ساکنان] مَدیَن، برادرشان شعیب [را فرستادیم]. او گفت: «ای قوم من، الله را عبادت کنید و به روز بازپسین امیدوار باشید و در زمین به فساد [و تباهی] نکوشید».');
INSERT INTO "islamhouse" VALUES(29,37,'29:37','ولی او را دروغگو انگاشتند و زلزله سختی آنان را فراگرفت؛ آنگاه در خانه‌های خود به روی افتادند و هلاک شدند.');
INSERT INTO "islamhouse" VALUES(29,38,'29:38','و [قوم] عاد و ثمود را [نیز هلاک کردیم] و یقیناً برخی از خانه‌های [ویران‌شدۀ] آنان برایتان نمایان است؛ و شیطان کارهایشان را در نظرشان بیاراست و آنان را از راه [حق] بازداشت؛ در حالی ‌که [نسبت به حق و باطل] بینا بودند.');
INSERT INTO "islamhouse" VALUES(29,39,'29:39','و قارون و فرعون و هامان را [نیز نابود کردیم]؛ و به راستی، موسی دلایل روشن برایشان آورد؛ اما آنان در سرزمین [مصر] سرکشی کردند؛ ولی [از عذاب الهی] پیشی نگرفتند [و راه فرار نداشتند].');
INSERT INTO "islamhouse" VALUES(29,40,'29:40','هر یک از آنان را به [کیفر] گناهشان عذاب کردیم؛ بر برخی از آنان باران سنگ فرستادیم و برخی دیگر را بانگ مرگبار فروگرفت و برخی را در زمین فروبردیم و برخی را غرق کردیم. الله هرگز به آنان ستم نکرد؛ بلکه آنان [خود] به خویشتن ستم ‌می‌کردند.');
INSERT INTO "islamhouse" VALUES(29,41,'29:41','مَثَل کسانی ‌که به جای الله دوستانی [از سنگ و چوب و مردگان را به عنوان شفاعتگر] برای خود برگزیده‌اند، همچون مَثل عنکبوت است که خانه‌ای [بی‌دوام را به عنوان پناهگاه خویش] برگزیده است؛ و بی‌تردید، سست‌ترین خانه‌ها، خانۀ عنکبوت است. اگر [مشرکان این حقیقت را] می‌دانستند، [معبودان باطل را به عبادت نمی‌گرفتند].');
INSERT INTO "islamhouse" VALUES(29,42,'29:42','یقیناً الله هر آنچه را به جای او [به عبادت] می‌خوانند می‌داند؛ و او تعالی شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(29,43,'29:43','این مَثل‌ها را برای مردم می‌زنیم و جز دانایان کسی آنها را درنمی‌یابد.');
INSERT INTO "islamhouse" VALUES(29,44,'29:44','الله آسمان‌ها و زمین را به‌حق آفرید. بی‌تردید، در این [آفرینش،] نشانه‌ای [از قدرت الهی] برای مومنان است.');
INSERT INTO "islamhouse" VALUES(29,45,'29:45','[ای پیامبر،] آنچه را از این کتاب بر تو وحی شده است تلاوت کن و نماز برپا دار. به راستی که نماز، انسان را از گناه و زشتکاری بازمی‌دارد؛ و البته یاد الله [از هر کاری] بالا‌تر است؛ و الله می‌داند که چه می‌کنید.');
INSERT INTO "islamhouse" VALUES(29,46,'29:46','[ای مؤمنان،] با اهل کتاب جز به بهترین شیوه بحث و مناظره نكنید ـ مگر با کسانی از آنان که ستم کردند [و با شما به جنگ برخاستند] ـ و بگویید: «ما به تمام آنچه [از سوی الله] بر ما و شما نازل شده است ایمان آورده‌ایم و معبود ما و شما یکی است و ما تسلیمِ [فرمانِ] او هستیم».');
INSERT INTO "islamhouse" VALUES(29,47,'29:47','و همان گونه [كه به پيامبران پيشين كتاب آسمانی فرو فرستاديم‌] اين كتاب را [نیز] بر تو نازل کردیم؛ کسانی ‌که [پیش از تو] به آنان کتاب [آسمانی] دادیم به این [قرآن نیز] ایمان می‌آورند و از اینان [= مشرکان قریش] کسانی هستند که به آن ایمان می‌آورند؛ و جز کافران [کسی] آیات ما را انکار نمی‌کند.');
INSERT INTO "islamhouse" VALUES(29,48,'29:48','و پیش از [نزول] قرآن، تو [هرگز] هیچ کتابی نمی‌خواندی و با دست خود چیزی نمی‌نوشتی؛ وگرنه، قطعاً باطل‌اندیشان [دربارۀ صحتِ پیامبری‌ات] تردید می‌کردند.');
INSERT INTO "islamhouse" VALUES(29,49,'29:49','بلکه آن [کتاب، شامل] آیات روشنی است که در سینۀ دانشوران [مؤمن] جای دارد؛ و جز ستمکاران [مشرک، کسی] آیات ما را انکار نمی‌کند.');
INSERT INTO "islamhouse" VALUES(29,50,'29:50','و [مشرکان] گفتند: «چرا معجزاتی از سوی پروردگارش بر او نازل نشده است؟» بگو: «معجزات نزد الله است [و به فرمان او نازل می‌شود] و من فقط بیم‌دهنده‌ای آشکارم».');
INSERT INTO "islamhouse" VALUES(29,51,'29:51','آیا برایشان کافی نیست که ما قرآن را بر تو نازل کردیم که [پیوسته] بر آنان خوانده می‌شود؟ بی‌گمان، در این [قرآن] برای مردمی که ایمان می‌آورند، رحمت و پند است.');
INSERT INTO "islamhouse" VALUES(29,52,'29:52','بگو: «همین بس که الله میان من و شما گواه است. او تعالی آنچه را که در آسمان‌ها و زمین است می‌داند؛ و کسانی ‌که به باطل گرویده‌اند و به الله کفر ورزیده‌اند، زیانکارانند».');
INSERT INTO "islamhouse" VALUES(29,53,'29:53','و [مشرکان] به شتاب از تو درخواستِ عذاب می‌کنند؛ و اگر موعد مقرری [برای عذابشان] نبود، یقیناً عذاب [الهی] به [سراغ] آنان می‌آمد و [سرانجام] در حالی ‌که غافلند، ناگهان به سراغشان خواهد آمد.');
INSERT INTO "islamhouse" VALUES(29,54,'29:54','و [مشرکان] به شتاب از تو درخواستِ عذاب می‌کنند، و یقیناً دوزخ بر کافران احاطه دارد.');
INSERT INTO "islamhouse" VALUES(29,55,'29:55','روزی ‌که عذاب، از بالای سر و زیر پایشان آنان را فرامی‌گیرد و [الله به آنان] می‌فرماید: «[کیفر] آنچه را انجام می‌دادید بچشید».');
INSERT INTO "islamhouse" VALUES(29,56,'29:56','«ای بندگان من که ایمان آورده‌اید، بی‌گمان، زمین من گسترده است؛ [پس برای رهایی از فشار مشرکان، هجرت کنید] و تنها مرا عبادت کنید».');
INSERT INTO "islamhouse" VALUES(29,57,'29:57','هر کسی چشندۀ [طعم‌] مرگ است؛ آنگاه به سوی ما بازگردانده می‌شوید.');
INSERT INTO "islamhouse" VALUES(29,58,'29:58','و کسانی که ایمان آورده‌اند و کار‌های شایسته انجام داده‌اند، قطعاً آنان را در غرفه‌هایی از بهشت جای می‌دهیم که جویبارها از زیر آن جاری است. جاودانه در آن خواهند ماند. چه نیکوست پاداش عمل‌کنندگان!');
INSERT INTO "islamhouse" VALUES(29,59,'29:59','[همان] کسانی‌ که شکیبایی ورزیدند و تنها بر پروردگارشان توکل می‌کنند.');
INSERT INTO "islamhouse" VALUES(29,60,'29:60','و چه بسیار جاندارانى كه [نمى‌توانند] روزى خود را حمل [یا ذخیره‌] كنند. الله به آنها و شما روزی می‌دهد و او شنوای داناست.');
INSERT INTO "islamhouse" VALUES(29,61,'29:61','و اگر از آنان [= مشرکان] بپرسی: «چه کسی آسمان‌ها و زمین را آفریده و خورشید و ماه را به خدمت [انسان] گماشته است؟» مسلّماً می‌گویند: «الله»؛ پس چگونه [از حق] رویگردان می‌شوند؟');
INSERT INTO "islamhouse" VALUES(29,62,'29:62','الله [نعمت و] روزی را برای هر کس که بخواهد می‌گشاید و [یا] تنگ می‌گرداند. بی‌تردید، الله به هر چیزی داناست.');
INSERT INTO "islamhouse" VALUES(29,63,'29:63','و اگر از آنان بپرسی: «چه کسی از آسمان باران فرستاد و به وسیلۀ آن، زمین را ـ پس از خشکی و خزانش ـ زنده کرد؟»، مسلّماً می‌گویند: «الله». بگو: «حمد و ستایش مخصوص الله است»؛ بلکه بیشتر آنان [در این نشانه‌ها] نمی‌اندیشند.');
INSERT INTO "islamhouse" VALUES(29,64,'29:64','و این زندگی دنیا [چیزی] جز سرگرمی و بازیچه نیست؛ و بی‌تردید، سرای آخرت زندگانی [راستین و همیشگی] است. اگر [کافران این حقیقت را] می‌دانستند [دل به دنیا نبسته بودند].');
INSERT INTO "islamhouse" VALUES(29,65,'29:65','وقتی [مشرکان] در کشتی سوار می‌شوند، الله را با اخلاص می‌خوانند؛ اما چون آنان را به خشکی می‌رسانَد و [از خطرِ غرق‌شدن] می‌رهانَد، آنگاه [باز هم] شرک می‌ورزند.');
INSERT INTO "islamhouse" VALUES(29,66,'29:66','[چنین می‌کنند] تا نسبت به نعمت‌هایی که به آنان داده‌ایم ناسپاسی کنند و [از لذت‌های زندگی دنیا] بهره‌مند باشند. به زودی [سرانجامِ دردناکشان را] خواهند دانست.');
INSERT INTO "islamhouse" VALUES(29,67,'29:67','آیا ندیده‌اند در حالی که مردم پیرامونشان [گرفتارِ قتل و غارت هستند و] مورد دستبرد قرار می‌گیرند، ما حرم امنی [برای مردم مکه] پدید آورده‌ایم؟ آیا [باز هم] به [معبودهای] باطل ایمان می‌آورند و نسبت به نعمت الله ناسپاسی می‌کنند؟');
INSERT INTO "islamhouse" VALUES(29,68,'29:68','و کیست ستمکار‌تر از آن ‌که بر الله دروغ می‌بندد یا حق را ـ آنگاه که به او می‌رسدـ دروغ می‌انگارد؟ آیا در دوزخ جایگاهی برای کافران نیست؟');
INSERT INTO "islamhouse" VALUES(29,69,'29:69','کسانی ‌که در راه [خشنودیِ] ما می‌کوشند، قطعاً آنان را به راه‌های خویش هدایت می‌کنیم و بی‌تردید، الله با نیکوکاران است.');
INSERT INTO "islamhouse" VALUES(30,1,'30:1','الم [= الف. لام. میم].');
INSERT INTO "islamhouse" VALUES(30,2,'30:2','رومیان [در جنگ با فارس] شكست خوردند.');
INSERT INTO "islamhouse" VALUES(30,3,'30:3','در نزدیک‌ترین سرزمین [مرزی شام با فارس]؛ و آنان پس از شکستشان، [در نبردی دیگر] پیروز خواهند شد؛');
INSERT INTO "islamhouse" VALUES(30,4,'30:4','در چند سال [آینده]. چه قبل و چه بعد از این [پیروزی، سررشتۀ] کار در اختیار الله است؛ و در آن روز، مؤمنان شادمان می‌گردند.');
INSERT INTO "islamhouse" VALUES(30,5,'30:5','[این شادی،] به خاطرِ یاری رساندنِ الله [به اهل کتاب] است؛ و او تعالی هر کس را که بخواهد یاری می‌کند و او شکست‌ناپذیرِ مهربان است.');
INSERT INTO "islamhouse" VALUES(30,6,'30:6','[این پیروزی] وعدۀ الله است [و] الله هرگز از وعدۀ خود تخلف نمی‌کند؛ ولی بیشتر مردم نمی‌دانند.');
INSERT INTO "islamhouse" VALUES(30,7,'30:7','آنان فقط ظاهری از زندگی دنیا را می‌دانند و از آخرت غافلند.');
INSERT INTO "islamhouse" VALUES(30,8,'30:8','آیا با خود نمی‌اندیشند که الله آسمان‌ها و زمین و آنچه را که میان آنهاست، جز به‌حق و برای مدت معیّنی نیافریده است؟ بی‌تردید، بسیاری از مردم به دیدار پروردگارشان [در قیامت] باور ندارند.');
INSERT INTO "islamhouse" VALUES(30,9,'30:9','آیا [مشرکان] در زمین گشت و گذار نکرده‌اند تا ببینند سرانجامِ کسانی که پیش از آنان بودند چگونه بوده است؟ آنها بسیار نیرومندتر از اینان بودند و زمین را [برای کشاورزی و عمران،] بیش از اینان دگرگون ساختند و آباد کردند؛ و پیامبرانشان با دلایل روشن به سراغشان آمدند [اما آنان انکار کردند و نابود شدند]. الله هرگز بر آن نبود که به آنان ستم کند؛ بلکه آنان خود به خویشتن ستم می‌کردند.');
INSERT INTO "islamhouse" VALUES(30,10,'30:10','آنگاه سرانجامِ کسانی‌ که بدی کرده بودند، بسیار بدتر شد؛ چرا که آیات الله را دروغ می‌انگاشتند و آنها را به تمسخر می‌گرفتند.');
INSERT INTO "islamhouse" VALUES(30,11,'30:11','الله آفرینش مخلوقات را آغاز می‌کند؛ سپس آنان را [پس از مرگشان] بازمی‌گردانَد؛ آنگاه [در قیامت، همگی] به سویش بازگردانده می‌شوید.');
INSERT INTO "islamhouse" VALUES(30,12,'30:12','روزی که قیامت بر پا گردد، گناهکاران [از رحمت الهی] ناامید می‌شوند.');
INSERT INTO "islamhouse" VALUES(30,13,'30:13','[در آن روز،] مشرکان از بین معبودان خود [که عبادت می‌کردند] هیچ شفاعتگری نخواهند داشت و نسبت به معبودانشان [که آنها را همتای الله قرار داده بودند] کفر می‌ورزند.');
INSERT INTO "islamhouse" VALUES(30,14,'30:14','روزی که قیامت برپا گردد، [مردم بر اساس کارهایشان] از یکدیگر جدا می‌شوند.');
INSERT INTO "islamhouse" VALUES(30,15,'30:15','اما کسانی که ایمان آورده‌اند و کار‌های شایسته انجام داده‌اند، در بوستانی [از بهشت] شادمان می‌گردند.');
INSERT INTO "islamhouse" VALUES(30,16,'30:16','و اما کسانی ‌که کفر ورزیدند و آیات ما و دیدارمان در آخرت را تکذیب کردند، در عذاب [دوزخ] احضار می‌شوند.');
INSERT INTO "islamhouse" VALUES(30,17,'30:17','پس الله را به پاکی ستایش کنید، آنگاه که به شب درمی‌آیید و آن دَم که به صبح می‌رسید');
INSERT INTO "islamhouse" VALUES(30,18,'30:18','و عصرگاهان و هنگامی ‌که به نیمروز می‌رسید؛ و حمد و ستایش در آسمان‌ها و زمین از آنِ اوست.');
INSERT INTO "islamhouse" VALUES(30,19,'30:19','او تعالی [موجود] زنده را از [مادّۀ] بی‌جان [= نطفه] پدید می‌آورد و [مادّۀ] بی‌جان را از [موجود] زنده خارج می‌سازد؛ و زمین را ـ پس از خشکی و خزانش ـ زنده می‌سازد؛ و [شما نیز روز قیامت،] اینچنین برانگیخته می‌شوید.');
INSERT INTO "islamhouse" VALUES(30,20,'30:20','و [یکی دیگر] از نشانه‌های او تعالی [این] است که شما را از خاک آفرید، سپس انسان‌هایی شدید [که تولید مثل کردید و در زمین] پراکنده گشتید.');
INSERT INTO "islamhouse" VALUES(30,21,'30:21','و از [دیگر] نشانه‌های او [این] است که از جنس خود شما همسرانی برایتان آفرید تا در کنارشان آرام گیرید و در میانتان محبت و مهربانی قرار داد. بی‌گمان، در این [نعمت،] نشانه‌هایی است [از قدرت پروردگار] برای مردمی که تفکر می‌کنند.');
INSERT INTO "islamhouse" VALUES(30,22,'30:22','و از [دیگر] نشانه‌های او تعالی، آفرینش آسمان‌ها و زمین و اختلاف زبان‌ها و رنگ‌های شماست. بی‌تردید، در این [امور،] نشانه‌هایی [از قدرت پروردگار] برای دانشوران است.');
INSERT INTO "islamhouse" VALUES(30,23,'30:23','و [یکی دیگر] از نشانه‌های او، خواب شما در شب و روز و تلاشتان برای بهره‌گیری از بخشش پروردگار [و تأمین معاش] است. بی‌گمان، در این امور، برای مردمی که گوش شنوا دارند، نشانه‌هایی [از قدرتِ پروردگار] است.');
INSERT INTO "islamhouse" VALUES(30,24,'30:24','و از [دیگر] نشانه‌های او [این] است که برق را برای ترس [از صاعقه] و امید [به باران]، به شما نشان می‌دهد و آبی از آسمان می‌فرستد و زمین را ـ پس از خشکی و خزانش ـ با آن زنده می‌سازد. بی‌گمان، در این امور، برای مردمی که می‌اندیشند، نشانه‌هایی [از قدرتِ پروردگار] است.');
INSERT INTO "islamhouse" VALUES(30,25,'30:25','و از نشانه‌های او [این] است که آسمان‌ها و زمین به فرمانش برپاست و [روز قیامت] هنگامی که با ندایی شما را از زمین فرامی‌خوانَد، ناگهان [همگی از گورها] بیرون می‌آیید.');
INSERT INTO "islamhouse" VALUES(30,26,'30:26','هر کس [و هر چه] در آسمان‌ها و زمین است از آنِ الله است و همگی [تسلیم و] فرمانبردارِ او هستند.');
INSERT INTO "islamhouse" VALUES(30,27,'30:27','او همان ذاتی است که آفرینش مخلوقات را آغاز می‌کند، سپس آنان را [پس از مرگشان] بازمی‌گردانَد و این کار برایش آسان‌تر [از آفرینش نخستین] است؛ و برترین صفت در آسمان‌ها و زمین برای اوست و او تعالی شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(30,28,'30:28','[ای مشرکان،] الله برای شما از خودتان مثالی می‌آورد: آیا از بردگانتان کسی هست که در اموالی که به شما داده‌ایم شریکتان ‌باشد؛ چنان که هر دو [در برخورداری از آن اموال،] یکسان باشید و همان طور که [به خاطر تصرف مال] از یکدیگر بیم دارید، از آنان نیز بیم داشته باشید؟ [حال که چنین نیست، پس چرا بندگان و مخلوقات الله را با او شریک می‌دانید؟] ما آیات خویش را برای کسانی که می‌اندیشند، اینچنین به روشنی بیان می‌کنیم.');
INSERT INTO "islamhouse" VALUES(30,29,'30:29','[دلیل شرک، کمبود دلایل توحیدی نیست؛] بلکه ستمکاران بدون علم و آگاهی، از هوی و هوس‌ خود پیروی می‌کنند؛ پس کسی را که الله گمراه کرده است چه کسی هدایت می‌کند؟ و آنان [در برابر عذاب الهی] هیچ یاوری نخواهند داشت.');
INSERT INTO "islamhouse" VALUES(30,30,'30:30','[ای پیامبر،] حقگرایانه و خالصانه به این دین روی آور. [این آیین پاک،] اسلام است که الله فطرت مردم را بر اساس آن سرشته است. هیچ تغییری در آفرینشِ الله نیست. این است [راه راست و] دین استوار؛ ولی بیشتر مردم [حقیقت را] نمی‌دانند.');
INSERT INTO "islamhouse" VALUES(30,31,'30:31','[ای مؤمنان، به درگاه پروردگار توبه کنید و] به سوی او بازگردید و از او پروا کنید و نماز برپا دارید و از مشرکان نباشید.');
INSERT INTO "islamhouse" VALUES(30,32,'30:32','از کسانی [نباشید] که در دینشان تفرقه ایجاد کردند و دسته‌دسته شدند و هر گروهی به آنچه نزد خویش دارند خشنودند.');
INSERT INTO "islamhouse" VALUES(30,33,'30:33','هنگامی که به مردم [رنج و] زیانی برسد، زاری‌کنان پروردگار‌شان را می‌خوانند؛ سپس چون [الله] رحمتی از جانب خویش به آنان بچشاند [و گره از کارشان بگشاید]، آنگاه گروهی از آنان به پروردگارشان شرک می‌آورند');
INSERT INTO "islamhouse" VALUES(30,34,'30:34','تا به آنچه که به آنان داده‌ایم ناسپاسی کنند. [از لذت‌های دنیوی] بهره بگیرید که به زودی [نتیجۀ کردارتان را] خواهید دانست.');
INSERT INTO "islamhouse" VALUES(30,35,'30:35','آیا [در کتاب‌های آسمانی] دلیل محکمی بر آنان نازل کرده‌ایم که از [درستیِ] آنچه به او [= الله] شرک ‌ورزیده‌اند سخن می‌گوید [و این کار را تأیید می‌کند]؟');
INSERT INTO "islamhouse" VALUES(30,36,'30:36','وقتی رحمتی به مردم بچشانیم، از آن [بیش از حد] شاد می‌گردند [و تکبر می‌ورزند] و اگر به سزای کارهایی که می‌کردند رنج و مصیبتی به آنان برسد، [از رحمت الهی و دفع بلا] ناامید می‌شوند.');
INSERT INTO "islamhouse" VALUES(30,37,'30:37','آیا ندیدند که الله [نعمت و] روزی را بر هر کس که بخواهد می‌گشاید و [یا] تنگ می‌گرداند؟ بی‌گمان، در این [گشایش و سختی،] نشانه‌هایی [از لطف و رحمت پروردگار،] برای مؤمنان است.');
INSERT INTO "islamhouse" VALUES(30,38,'30:38','حقِ [صلۀ رَحِم] خویشاوند و [صدقه و زکاتِ] مستمند و درراه‌مانده را ادا کن. این [کار] برای کسانی‌ که [خشنودی] الله را می‌طلبند بهتر است و آنان همان رستگارانند.');
INSERT INTO "islamhouse" VALUES(30,39,'30:39','آنچه از اموال [خود، که به قصد] ربا به مردم می‌دهید تا [با پس گرفتنِ آن به میزان بیشتری بر اموالتان] افزوده گردد، نزد الله افزایش نمی‌یابد و[لی] آنچه برای خشنودی الله [به عنوان] زکات می‌پردازید، [برکت و افزایش می‌یابد] و [کسانی که چنین می‌کنند] اینانند که پاداششان [نزد الله] چند برابر می‌گردد.');
INSERT INTO "islamhouse" VALUES(30,40,'30:40','الله است که شما را ‌آفرید و روزی ‌داد؛ آنگاه شما را می‌میراند و باز [در قیامت] زنده می‌کند. آيا در ميان شريكانتان كسى هست که چیزی از این [کار‌ها] را انجام دهد؟ او تعالی منزّه است و از چیزهایی که [با وی] شریک می‌سازند برتر است.');
INSERT INTO "islamhouse" VALUES(30,41,'30:41','به سبب گناهانی که مردم مرتکب شده‌اند، فساد [و تباهی] در خشکی و دریا آشکار گشته است تا [الله] جزای برخی از اعمالشان را به آنان بچشاند؛ باشد که [به سوی حق] بازگردند.');
INSERT INTO "islamhouse" VALUES(30,42,'30:42','[ای پیامبر، به مشرکان] بگو: «در زمین بگردید تا ببینید سرانجامِ کسانی که پیش از شما بودند چگونه بوده است؛ بیشتر آنان مشرک بودند [و به همین سبب هلاک شدند]».');
INSERT INTO "islamhouse" VALUES(30,43,'30:43','به سوى دین مستقیم [و پایدار] روی آور پیش از آنكه روزى از جانب الله فرارسد كه برگشت‌ناپذير است [و قطعاً واقع می‌گردد]. در آن روز، [مردم] پراکنده می‌شوند [سپس گروهی به بهشت می‌روند و گروهی به دوزخ].');
INSERT INTO "islamhouse" VALUES(30,44,'30:44','هر کس کفر بورزد، کفرش به زیان [خودِ] اوست و هر کس کاری شایسته انجام دهد، [بهشت را] برای خود آماده می‌سازند.');
INSERT INTO "islamhouse" VALUES(30,45,'30:45','تا [الله] کسانی را که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، از فضل خود پاداش دهد. بی‌تردید، او تعالی کافران را دوست ندارد.');
INSERT INTO "islamhouse" VALUES(30,46,'30:46','از نشانه‌های [قدرتِ] او این است که باد‌های بشارت‌بخش را [با مژدۀ باران] می‌فرستد تا از رحمت خویش به شما بچشاند و کشتی‌ها به فرمانش حرکت کنند تا از بخشش او [روزی] بجویید؛ باشد که شکر به جای آورید.');
INSERT INTO "islamhouse" VALUES(30,47,'30:47','و در حقیقت، پیش از تو [نیز] پیامبرانی را به سوی قومشان فرستادیم و آنان با دلایل روشن نزد‌شان آمدند [ولی آن دلایل را تکذیب کردند]؛ سپس از کسانی که مرتکب گناه شده بودند انتقام گرفتیم [و مؤمنان را نجات دادیم] و یاری‌دادن مؤمنان [حقی] بر عهدۀ ما است.');
INSERT INTO "islamhouse" VALUES(30,48,'30:48','الله است که باد‌ها را روانه می‌کند و [آنها نیز تودۀ] ابری را به حرکت درمی‌آورند و [الله] آنها را چنان که می‌خواهد در [پهنۀ] آسمان می‌گسترانَد و به صورت پاره‌هایی درمی‌آورَد و باران را می‌بینی که از میان آن [ابرها] بیرون می‌آید؛ و چون [پروردگار مهربان] این [بارانِ حیات‌بخش] را به هر یک از بندگانش که بخواهد فرو‌بارَد، آنگاه است که شادمان می‌شوند.');
INSERT INTO "islamhouse" VALUES(30,49,'30:49','و قطعاً پیش از آنکه بر آنان ببارد، [آری] پیش از آن، ناامید بودند.');
INSERT INTO "islamhouse" VALUES(30,50,'30:50','پس به آثار رحمت الهی بنگر که چگونه زمین را پس از خشکی و خزانش زنده می‌کند. به راستی، اوست که یقیناً زندگی‌بخشِ مردگان است و او تعالی بر هر چیزی تواناست.');
INSERT INTO "islamhouse" VALUES(30,51,'30:51','اگر بادی [سوزان] بفرستیم و آن [کشتزارهایشان] را زرد [و پژمرده] ببینند، قطعاً پس از آن، ناسپاسی می‌کنند.');
INSERT INTO "islamhouse" VALUES(30,52,'30:52','[ای پیامبر،] تو هرگز نمی‌توانی مردگان را شنوا سازی و آواى دعوت [حق را] را به گوش كران برسانى؛ آنگاه که آنان پشت می‌کنند و روی می‌گردانند.');
INSERT INTO "islamhouse" VALUES(30,53,'30:53','و تو راهنمای کوردلان از گمراهی‌شان نیستی. تو تنها [سخن حق را] به گوش کسانی می‌رسانی که به آیات ما ایمان دارند و [در برابر فرمان الله] تسلیمند.');
INSERT INTO "islamhouse" VALUES(30,54,'30:54','الله است که شما را از [آب] بی‌ارزشی آفرید؛ سپس بعد از ناتوانى [دوران خردسالی،] نیرومندى بخشید؛ آنگاه پس از نیرومندى، ضعف و کهنسالی پدید آورد. او هر چه بخواهد می‌آفریند و او دانای تواناست.');
INSERT INTO "islamhouse" VALUES(30,55,'30:55','و روزی که قیامت برپا گردد، گناهکاران سوگند یاد می‌کنند که جز ساعتی [در گور] درنگ نکرده‌اند. آنان [در دنیا نیز] این گونه به انحراف كشیده مى‌شدند.');
INSERT INTO "islamhouse" VALUES(30,56,'30:56','و کسانی که به آنان دانش و ایمان داده شده است [به گناهکاران] می‌گویند: «بی‌تردید، شما [بر اساس آنچه] در كتاب الله [مقدر بوده است] تا روز رستاخیز درنگ کرده‌اید. پس این روز رستاخیز است؛ اما شما نمی‌دانستید [و آن را انکار می‌کردید]».');
INSERT INTO "islamhouse" VALUES(30,57,'30:57','در آن روز، عذرخواهیِ کسانی که ستم کرده‌اند سودی ندارد و از آنان خواسته نمی‌شود که [برای به دست آوردن خشنودی الله] عذرخواهی کنند.');
INSERT INTO "islamhouse" VALUES(30,58,'30:58','به راستی که در این قرآن،از هر گونه مَثَلی برای مردم آوردیم؛ و اگر نشانه‌ای [در مورد راستگویی‌ات] برایشان بیاوری، کسانی که کفر ورزیدند می‌گویند: «شما باطل‌اندیشانی بیش نیستید».');
INSERT INTO "islamhouse" VALUES(30,59,'30:59','الله بر دل‌های کسانی که [سخن حق را] نمی‌فهمند، این گونه مُهر [غفلت] می‌نهد.');
INSERT INTO "islamhouse" VALUES(30,60,'30:60','[ای پیامبر، بر آزار و تکذیبِ مشرکان] شکیبا باش [که] قطعاً وعدۀ [یاریِ] الله، حق است؛ و مبادا کسانی که [به رستاخیز] باور ندارند، تو را به [بی‌صبری و] سبكسرى وادار کنند.');
INSERT INTO "islamhouse" VALUES(31,1,'31:1','الم [= الف. لام. میم].');
INSERT INTO "islamhouse" VALUES(31,2,'31:2','این آیات کتاب حکیم است.');
INSERT INTO "islamhouse" VALUES(31,3,'31:3','[که] هدایت و رحمتی برای نیکوکاران است.');
INSERT INTO "islamhouse" VALUES(31,4,'31:4','همان کسانی ‌که نماز برپا می‌دارند و زکات می‌پردازند و به آخرت یقین دارند.');
INSERT INTO "islamhouse" VALUES(31,5,'31:5','آنان از جانب پروردگارشان از هدایتی برخوردارند و آنان قطعاً رستگارند.');
INSERT INTO "islamhouse" VALUES(31,6,'31:6','و در میان مردم کسی هست که در پیِ سخن یاوه است تا [دیگران را] از راهِ الله گمراه سازد و آن [آیات روشنگر] را به تمسخر می‌گیرد. آنان [که رفتارشان چنین است] عذابی خفت‌بار [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(31,7,'31:7','و هنگامی که آیات ما بر او خوانده می‌شود، متکبرانه روی می‌گردانَد؛ گویی آن را نشنیده [و] گویی در گوش‌هایش سنگینی است؛ او را به عذابی درد‌ناک بشارت ده.');
INSERT INTO "islamhouse" VALUES(31,8,'31:8','بی‌تردید، کسانی ‌که ایمان آورده‌اند و کار‌های شایسته انجام داده‌اند، باغ‌های پرنعمت [بهشت] برایشان است.');
INSERT INTO "islamhouse" VALUES(31,9,'31:9','در آنجا جاودانند. وعدۀ الله راست است و او تعالی شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(31,10,'31:10','چنان که می‌بینید، [الله] آسمان‌ها را بدون ستون آفرید و کوه‌های استوار [و محکمی] در زمین پِی افکند تا [زمین] شما را نلرزانَد و از هر گونه جنبنده‌ای در آن پراکنده ساخت و آبی از آسمان فرستادیم و از هر گونه [گیاه زیبا و] سودمندى در آن رویاندیم.');
INSERT INTO "islamhouse" VALUES(31,11,'31:11','این آفرینشِ الله است؛ اینک [ای مشرکان،] به من نشان دهید کسانی ‌که غیر از او هستند [و شما آنها را عبادت می‌کنید] چه آفریده‌اند؟ بلکه ستمکاران [مشرک] در گمراهی آشکارند.');
INSERT INTO "islamhouse" VALUES(31,12,'31:12','به راستی، ما دانش [فهم دین و تدبیر زندگی] به لقمان بخشیدیم [و به او گفتیم]: «شکر الله را به جای آور؛ و هر ‌کس شکر گزارد، تنها به سود خویش شکر گزارده است؛ و هر‌ کس ناسپاسی کند، [بداند که] بی‌تردید، الله بی‌نیازِ ستوده است.');
INSERT INTO "islamhouse" VALUES(31,13,'31:13','و [یاد کن از] آنگاه که لقمان به پسرش [چنین] پند می‌داد: «پسرم، به الله شرک نیاور [و کسی یا چیزی را همتایش قرار نده؛ چرا که] یقیناً شرک، ستم بزرگی است».');
INSERT INTO "islamhouse" VALUES(31,14,'31:14','ما انسان را [دربارۀ نیکویی] به پدر و مادرش سفارش کردیم [از آن رو که] مادرش او را با سستی [و ضعفِ] روزافزون باردار شد و [دوران شیرخوارگی و] بازگرفتنش از شیر دو سال به طول ‌می‌انجامد. [به او گفتیم] که: «شُکرِ من و پدر و مادرت را به جای آور [که] باز‌گشت [همه] به سوی من است.');
INSERT INTO "islamhouse" VALUES(31,15,'31:15','و اگر آن دو [مشرک باشند و] تلاش کنند تو چیزی را شریک من قرار دهی که نسبت به آن دانشی نداری، [در این صورت] از آن‌ها اطاعت نکن و[لی] در دنیا با آنان به شایستگی [و نیکویی] رفتار کن و از راه کسی پیروی کن که [موحدانه] به سوی من بازآمده است؛ آنگاه بازگشتتان به سوی من است و شما را از آنچه انجام می‌دادید آگاه می‌کنم».');
INSERT INTO "islamhouse" VALUES(31,16,'31:16','[لقمان گفت:] «پسرم، اگر [عمل نیک یا بد] همسنگِ دانۀ خردل باشد و درون تخته‌سنگی یا در [گوشه‌ای از] آسمان‌ها یا در زمین [نهفته] باشد، الله آن را [روز قیامت به حساب] می‌آورد. بی‌تردید، الله باریک‌بینِ آگاه است.');
INSERT INTO "islamhouse" VALUES(31,17,'31:17','پسرم، نماز برپا دار و [مردم را] به نیکی فرمان بده و از کار ناپسند بازدار و بر آنچه [که از سختی‌های این کار] به تو می‌رسد، شکیبا باش. بی‌تردید، این از [جمله کارهایی است که نیازمند ارادۀ قوی و] عزم استوار است.');
INSERT INTO "islamhouse" VALUES(31,18,'31:18','و [از روی غرور و تکبر] از مردم روی نگردان و مغرورانه در زمین راه نرو. به راستی که الله هیچ متکبر فخرفروشی را دوست ندارد.');
INSERT INTO "islamhouse" VALUES(31,19,'31:19','و [در منش و رفتارت] میانه‌رو باش و به آرامی سخن بگو [نه با صدای بلند؛ چرا که] قطعاً زشت‌ترین صدا، صدای خران است.');
INSERT INTO "islamhouse" VALUES(31,20,'31:20','[ای مردم،] آیا ندیده‌اید که الله آنچه را در آسمان‌ها و زمین است، به خدمتتان گماشته و [بخششِ] نعمت‌های آشکار و پنهان خویش را بر شما تمام کرد؟ [با این حال،] از مردم کسی هست که بدون هیچ دانش و هدایت و کتابِ روشنگری، در [مورد] الله مجادله می‌کند.');
INSERT INTO "islamhouse" VALUES(31,21,'31:21','و هنگامی که به آنان گفته می‌شود: «از آنچه الله نازل کرده است پیروی کنید» می‌گویند: «[نه،] بلکه از چیزی پیروی می‌کنیم که پدرانمان را بر آن یافتیم». آیا اگر شیطان آنان را به سوی عذاب [آتش] سوزان دعوت کند [باز هم از پدرانشان پیروی می‌کنند؟]');
INSERT INTO "islamhouse" VALUES(31,22,'31:22','هر کس [خالصانه] به الله رو کند و نیکوکار باشد، به راستی که به محكم‌ترين دستاويز چنگ زده است؛ و سرانجامِ کار‌ها به سوی الله است.');
INSERT INTO "islamhouse" VALUES(31,23,'31:23','هر ‌کس کفر ورزید، کفرش تو را غمگین نسازد. بازگشت آنان به سوی ماست؛ آنگاه آنان را از آنچه کرده‌اند آگاه می‌سازیم. بی‌تردید، الله از [رازِ] دل‌ها آگاه است.');
INSERT INTO "islamhouse" VALUES(31,24,'31:24','آنان [= کافران] را اندکی [از نعمت‌های دنیا] بهره‌مند می‌سازیم؛ آنگاه آنان را به [تحمل] عذابی سخت دچار می‌کنیم.');
INSERT INTO "islamhouse" VALUES(31,25,'31:25','و اگر از مشرکان بپرسی: «چه کسی آسمان‌ها و زمین را آفریده است؟» قطعاً می‌گویند: «الله». بگو: «سپاس [و ستایش] مخصوصِ الله است [که شما را از این حقیقت، آگاه نمود]»؛ بلکه بیشتر آنان نمی‌دانند.');
INSERT INTO "islamhouse" VALUES(31,26,'31:26','آنچه در آسمان‌ها و زمین است، از آنِ الله است. بی‌تردید، الله بی‌نیازِ ستوده است.');
INSERT INTO "islamhouse" VALUES(31,27,'31:27','اگر [همۀ] درختان روی زمین قلم شود و دریا [مرکب گردد] و هفت دریای دیگر به یاری‌اش بیاید [تا دانش الهی را بنویسند]، سخنان الله پایان نمی‌یابد. بی‌تردید، الله شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(31,28,'31:28','[ای مردم،] آفرینش و برانگیختن شما [در قیامت] همچون [آفرینش] یک نفر بیش نیست. بی‌تردید، الله شنوای بیناست.');
INSERT INTO "islamhouse" VALUES(31,29,'31:29','آیا ندیدی که الله از شب می‌کاهد و بر روز می‌افزاید و از روز می‌کاهد و بر شب می‌افزاید؛ و خورشید و ماه را به خدمت [انسان] گماشته است ‌که هر یک تا زمانی معیّن در [مدار خود] در حرکت باشد؟ بی‌تردید، الله از آنچه می‌کنید آگاه است.');
INSERT INTO "islamhouse" VALUES(31,30,'31:30','این [تقدیر و تدبیر،] از آن روست که الله حق است و آنچه جز او [به یاری و دعا] می‌خوانند [همه] باطل است و الله است که بلندمرتبه [و] بزرگ است.');
INSERT INTO "islamhouse" VALUES(31,31,'31:31','آیا ندیدی که کشتی‌ها به [فرمان و] نعمت الله در دریا در حرکتند تا برخی از نشانه‌های خویش را به شما بنمایاند؟ بی‌تردید، در این امر، برای هر شکیبای سپاسگزار، نشانه‌هایی [از قدرت و لطف پروردگار] است.');
INSERT INTO "islamhouse" VALUES(31,32,'31:32','و آنگاه‌ که موجی کوه‌پیکر آنان را احاطه می‌کند، الله را در حالی خالصانه می‌خوانند که دین [و عبادت] را تنها ویژۀ او می‌دانند؛ اما چون آنان را [از مرگ] ‌رهاند و به خشکی ‌رساند، برخی از آنان میانه‌رو [می‌شوند، چنان که باید، سپاس نمی‌گزارند؛ در حالی ‌که برخی دیگر ناسپاسند]؛ و آیات ما را کسی جز پیمان‌شکنانِ ناسپاس انکار نمی‌کند.');
INSERT INTO "islamhouse" VALUES(31,33,'31:33','ای مردم، از پروردگار‌تان پروا کنید و از روزی بترسید که نه هیچ پدری برای فرزندش سودی دارد و نه هیچ فرزندی سودرسانِ پدر است. یقیناً وعدۀ الله [در مورد قیامت] حقیقت دارد؛ پس [هوشیار باشید که لذت‌های] زندگی دنیا شما را نفریبد و شیطان شما را نسبت به [تأخیر عذاب و شکیباییِ] الله فریب ندهد.');
INSERT INTO "islamhouse" VALUES(31,34,'31:34','بی‌تردید، آگاهی [از زمان وقوع] قیامت، [تنها] نزد الله است و [اوست که] باران نازل می‌کند و آنچه را که در رَحِم‌هاست می‌داند؛ و ‌کسی نمی‌داند فردا چه به دست می‌آورد و هیچ‌ ‌کس آگاه نیست که در کدام سرزمین می‌میرد. به راستی که الله دانای آگاه است.');
INSERT INTO "islamhouse" VALUES(32,1,'32:1','الم [= الف. لام. میم].');
INSERT INTO "islamhouse" VALUES(32,2,'32:2','نزول این کتاب که در [حقانیت] آن هیچ شکی نیست، از جانب پروردگار جهانیان است.');
INSERT INTO "islamhouse" VALUES(32,3,'32:3','[کافران] می‌گویند: «[محمد] آن را به دروغ [به پروردگارش] نسبت می‌دهد». [هرگز چنین نیست؛] بلکه آن [آیات، سخنِ] حقی از جانب پروردگار توست تا به قومی که پیش از تو [هیچ] بیم‌دهنده‌ای برایشان نیامده است، هشدار دهی؛ باشد که هدایت شوند.');
INSERT INTO "islamhouse" VALUES(32,4,'32:4','الله همان ذاتی است که آسمان‌ها و زمین و آنچه را که میان آنهاست در شش روز آفرید؛ آنگاه [آن گونه كه شايسته جلال و مقام اوست] بر عرش قرار گرفت. شما هیچ کارساز و شفاعتگری جز او ندارید. آیا پند نمی‌گیرید؟');
INSERT INTO "islamhouse" VALUES(32,5,'32:5','کار [جهان هستی] را از آسمان تا زمین تدبیر می‌کند. آنگاه در روزی که بلندی‌اش، به حساب شما [انسان‌ها] هزار سال است، [این کارها] به سوی او بالا می‌رود.');
INSERT INTO "islamhouse" VALUES(32,6,'32:6','این [پروردگار شماست:] دانای پنهان و آشکار، شکست‌ناپذیرِ مهربان.');
INSERT INTO "islamhouse" VALUES(32,7,'32:7','[همان] ذاتی ‌که هر چه آفرید، به نیکو‌ترین [شکل] آفرید و آفرینش [نخستین] انسان را از گِل آغاز کرد.');
INSERT INTO "islamhouse" VALUES(32,8,'32:8','سپس [تداوم‌] نسل او را از چکیدۀ آبی پست [و بی‌قدر] مقرّر کرد.');
INSERT INTO "islamhouse" VALUES(32,9,'32:9','آنگاه [اندام] او را سامان داد و از روح خویش [= روحی که خود آفرید] در وی دمید؛ و [نعمتِ] گوش و چشم‌ و دل برایتان قرار داد. چه اندک سپاس می‌گزارید!');
INSERT INTO "islamhouse" VALUES(32,10,'32:10','[مشرکان] می‌گویند: «آیا وقتی [مُردیم و] در زمین [تبدیل به خاک و] ناپدید شدیم، آفرینش جدیدی خواهیم یافت؟» بلکه آنان دیدار پروردگارشان را منکرند.');
INSERT INTO "islamhouse" VALUES(32,11,'32:11','[ای پیامبر،] بگو: «فرشتۀ مرگ که بر شما گماشته شده است، جانتان را می‌گیرد؛ آنگاه به سوی پروردگارتان بازگردانده می‌شوید».');
INSERT INTO "islamhouse" VALUES(32,12,'32:12','و چون [در قیامت] گناهکاران را در پیشگاه پروردگارشان ببینی سر به زیر افکنده‌اند [و می‌گویند:] «پروردگارا، [اکنون حقیقت را] دیدیم و شنیدیم؛ پس ما را بازگردان تا کاری شایسته انجام دهیم. بی‌تردید، ما [به قیامت] یقین داریم».');
INSERT INTO "islamhouse" VALUES(32,13,'32:13','اگر می‌خواستیم، به هر‌ کسی [توفیق و] هدایتش را ارزانی می‌داشتیم؛ ليكن اين سخن از جانب من حتمى شده است كه: «دوزخ را از همۀ جنیان و آدمیان [کافر] پر خواهم کرد».');
INSERT INTO "islamhouse" VALUES(32,14,'32:14','[به آنان گفته می‌شود:] «به سزای آنکه دیدار امروزتان را فراموش ‌کردید، [عذاب سوزان را] بچشید. یقیناً ما [نیز] شما را [در آتش] رها کرده‌ایم و [اکنون] به سزای آنچه می‌کردید، عذاب جاودان را بچشید.');
INSERT INTO "islamhouse" VALUES(32,15,'32:15','فقط کسانی به آیات ما ایمان می‌آورند که چون به آنها پند داده شوند، به سجده می‌افتند و پروردگارشان را به پاکی می‌ستایند و تکبر نمی‌ورزند.');
INSERT INTO "islamhouse" VALUES(32,16,'32:16','[در دل شب،] از بسترها برمی‌خیزند و پروردگار‌شان را با بیم و امید [به نیایش] می‌خوانند و از آنچه به آنان روزی داده‌ایم انفاق می‌کنند.');
INSERT INTO "islamhouse" VALUES(32,17,'32:17','هیچ کس نمی‌داند به [پاس] آنچه [این مؤمنان در دنیا] انجام می‌دهند، چه [بسیار پاداش‌ها که مایۀ شادمانی و] روشنی چشم‌هاست، برایشان نهفته است.');
INSERT INTO "islamhouse" VALUES(32,18,'32:18','آیا [پاداش] کسی که مؤمن است همچون کسی است که نافرمان است؟ هرگز برابر نیستند.');
INSERT INTO "islamhouse" VALUES(32,19,'32:19','اما کسانی که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، برایشان باغ‌ها[ی بهشتی] است؛ [این به عنوان] محلّ پذیرایی از [آنها] است به [پاس] آنچه می‌کردند.');
INSERT INTO "islamhouse" VALUES(32,20,'32:20','و اما کسانی که نافرمانی کردند، جایگاهشان آتش [دوزخ] است. هر گاه بخواهند از آن خارج شوند، به آن بازگردانده می‌شوند و به آنان گفته می‌شود: «بچشید عذاب آتشی که همواره آن را انکار می‌کردید».');
INSERT INTO "islamhouse" VALUES(32,21,'32:21','یقیناً پیش از عذاب بزرگ [آخرت]، از عذاب نزدیک‌ [و سختی‌های این دنیا] به آنان می‌چشانیم؛ باشد که [به درگاه الهی] بازگردند.');
INSERT INTO "islamhouse" VALUES(32,22,'32:22','کیست ستمکار‌تر از آن ‌کس که به آیات پروردگارش پند داده می‌شود، سپس از آن روی می‌گرداند؟ بی‌تردید، ما از گناهکاران انتقام می‌گیریم.');
INSERT INTO "islamhouse" VALUES(32,23,'32:23','ما به موسی کتاب [تورات] دادیم ـ پس [ای پیامبر،] در مورد دیدار [خودت با] او [در شب اسراء و معراج] در تردید نباش‌ـ و آن [= تورات] را رهنمود بنی‌اسرائیل گرداندیم');
INSERT INTO "islamhouse" VALUES(32,24,'32:24','و چون آنان [بر عبادت و دوری از گناه] شکیبایی می‌کردند و به آیات ما یقین داشتند، پیشوایانی از میان آنان برگماشتیم که به فرمان ما [مردم را] هدایت می‌کردند.');
INSERT INTO "islamhouse" VALUES(32,25,'32:25','مسلماً پروردگار توست که روز قیامت در مورد آنچه اختلاف داشتند، میان آنان داوری می‌کند.');
INSERT INTO "islamhouse" VALUES(32,26,'32:26','آیا برایشان روشن نشده است كه چه بسیار نسل‌هایی را پیش از آنان نابود كردیم كه [اکنون این کافران] در خانه‌های آنها راه مى‌روند؟ به راستی که در این [عذاب و نابودی] عبرت‌هاست. آیا [باز هم سخن حق را] نمی‌شنوند؟');
INSERT INTO "islamhouse" VALUES(32,27,'32:27','آیا ندیدند که ما باران را به سوی زمین خشک [و بی‌گیاه] می‌فرستیم و به وسیلۀ آن، کشتزاری پدید می‌آوریم که خود و چهارپایانشان از آن می‌خورند؟ آیا [نشانه‌های قدرتِ الله بر رستاخیز را] نمی‌بینند؟');
INSERT INTO "islamhouse" VALUES(32,28,'32:28','و [کافران] می‌گویند: «اگر راستگویید، این داوری [میان ما و شما] کی فرامی‌رسد؟»');
INSERT INTO "islamhouse" VALUES(32,29,'32:29','بگو: «در روز داوری [که جدایی حق و باطل است]، ایمان آوردنِ کسانی که کفر ورزیدند سودی ندارد و به آنان مهلت [توبه] داده نمی‌شود».');
INSERT INTO "islamhouse" VALUES(32,30,'32:30','پس [ای پیامبر، اکنون که بر شرک پافشاری می‌کنند] از آنان روی بگردان و منتظر [عذابشان] باش [که] بی‌تردید، آنان [نیز] منتظر [وقوع حوادث ناگوار بر شما] هستند.');
INSERT INTO "islamhouse" VALUES(33,1,'33:1','ای پیامبر، تقوای الهی پیشه کن و از کافران و منافقان اطاعت نکن. بی‌تردید، الله همواره دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(33,2,'33:2','و از آنچه از جانب پروردگارت بر تو وحی می‌شود پیروی کن. بی‌تردید، الله همواره به آنچه می‌کنید آگاه است.');
INSERT INTO "islamhouse" VALUES(33,3,'33:3','و بر الله توکل كن و همین بس كه الله کارسازِ [تو] باشد.');
INSERT INTO "islamhouse" VALUES(33,4,'33:4','الله درون هیچ ‌مردی دو قلب برایش ننهاده است و [به همین صورت،] هرگز همسرانتان را که مورد ظِهار [و تحریم جنسی] قرار می‌دهید، [همچون] مادرانتان نگردانده است؛ و [همچنین] پسرخوانده‌هایتان را پسران [حقیقی] شما قرار نداده است. این سخنی [نادرست] است که بر زبان می‌آورید و الله حق را می‌گوید و اوست که به راه راست هدایت می‌کند.');
INSERT INTO "islamhouse" VALUES(33,5,'33:5','آنان [= پسرخوانده‌ها] را به پدرانشان نسبت دهید [که] نزد الله عادلانه‌تر است. اگر پدرانشان را نمی‌شناسید، برادران دینی و بردگان آزادشده [و در حکمِ دوستان] شما هستند. در مورد آنچه [قبلاً] اشتباه کرده‌اید، گناهی بر شما نیست؛ ولی [در مورد] آنچه دل‌هایتان قصد آن را دارد [و عمداً بر زبان می‌آورید، مسؤول هستید و گناهکار می‌شوید؛ پس اگر توبه کنید،] همواره الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(33,6,'33:6','پیامبر [در تمام امور] نسبت به مؤمنان، از خودشان سزاوار‌تر است [و نظر و خواستش اولویت دارد] و همسران او [از لحاظ حرمت ازدواج و رعایت احترام، در حکمِ] مادرانشان هستند؛ و در کتاب الله، خویشاوندان [در ارث بردن از یكدیگر،] نسبت به مؤمنان و مهاجران اولویت دارند [و مِلاک ارث، خویشاوندی است نه ایمان و هجرت]؛ مگر اینکه [بخواهید] به دوستانتان نیکی کنید [و بخشی از اموالتان را به آنان بدهید]. این [حکم،] در آن کتاب [= لوح محفوظ] نوشته شده است.');
INSERT INTO "islamhouse" VALUES(33,7,'33:7','و [ای پیامبر، یاد کن از] هنگامی که از پیامبران پیمان [عبادت الله به یگانگی و نبوت] گرفتیم و [به ویژه] از تو و نوح و ابراهیم و موسی و عیسی ـ پسر مریم؛ و از [همۀ] آنان پیمان محکمی [دربارۀ تبلیغ دین] گرفتیم.');
INSERT INTO "islamhouse" VALUES(33,8,'33:8','[الله این پیمان را از رسولان گرفت] تا [با کافران اتمام حجت کرده باشد و] از راستگویان [= پیامبران] دربارۀ راستی‌شان بپرسد و برای کافران عذابی دردناک مهیّا کرده است.');
INSERT INTO "islamhouse" VALUES(33,9,'33:9','ای کسانی که ایمان آورده‌اید، نعمت الله را بر خود به یاد آورید؛ آنگاه که سپاهیانی [از قبایل عرب] به سویتان آمدند. آنگاه ما تندبادی [سخت] و لشکری [از فرشتگان] که آنها را نمی‌دیدید، بر آنان فرستادیم [و به سختی شکستشان دادیم]؛ و همواره الله به آنچه می‌کنید بیناست.');
INSERT INTO "islamhouse" VALUES(33,10,'33:10','وقتی [در غزوۀ خندق] دشمنان از بالا و پایین [و از شرق و غربِ شهر] به سویتان آمدند [و مدینه را محاصره کردند]؛ آنگاه که چشم‌ها [به فراوانیِ دشمن] خیره گشته و جان‌ها [از شدت وحشت] به گلوگاه رسیده بود و شما گمان‌هایی [نابجا] به الله می‌بردید.');
INSERT INTO "islamhouse" VALUES(33,11,'33:11','آنجا بود که مؤمنان آزمایش شدند و شدیداً به وحشت و اضطراب افتادند.');
INSERT INTO "islamhouse" VALUES(33,12,'33:12','و [نیز به یاد آورید] هنگامی که منافقان و کسانی که در دل‌هایشان بیماری [شک و دورویی] بود می‌گفتند: «آنچه الله و پیامبرش [دربارۀ پیروزى بر کافران] وعده داده‌اند، جز فریبى بیش نیست».');
INSERT INTO "islamhouse" VALUES(33,13,'33:13','و [نیز] هنگامی که گروهی از آنان گفتند: «ای اهل یثرب، اینجا جای ماندن شما نیست؛ پس [به خانه‌هایتان] بازگردید»؛ و برخی از آنان از پیامبر اجازۀ [بازگشت] می‌خواستند و می‌گفتند: «خانه‌های ما بی‌حفاظ است»؛ در حالی که بی‌حفاظ نبود. آنان چیزی جز فرار [از جهاد] نمی‌خواستند.');
INSERT INTO "islamhouse" VALUES(33,14,'33:14','اگر [لشکر دشمنان] از اطراف آن [= مدینه] بر آنان وارد می‌شدند [و خانه‌هایشان را محاصره می‌کردند] و از آنان خواسته می‌شد به کفر و شرک بازگردند، قطعاً چنین [تعهدی به دشمن] می‌دادند و جز تعدادی اندک [در برابر این درخواست] مقاومت نمی‌کردند.');
INSERT INTO "islamhouse" VALUES(33,15,'33:15','و پیش از این [وقتی همین منافقان در جنگ احد از مقابل کافران گریختند] با الله پیمان بستند که [هرگز به دشمن] پشت نکنند [و از میدان نگریزند]؛ و همواره پیمان الهی بازخواست دارد.');
INSERT INTO "islamhouse" VALUES(33,16,'33:16','[ای پیامبر، به منافقان] بگو: «اگر از مرگ یا کشته شدن فرار کنید، هرگز سودی به حالتان ندارد؛ و در آن صورت نیز [از زندگی دنیا] جز اندکی بهره‌مند نمی‌شوید».');
INSERT INTO "islamhouse" VALUES(33,17,'33:17','بگو: «اگر الله مصیبت یا رحمتی برایتان خواسته باشد، کیست که شما را در برابرِ [اجرای فرمان] او حفظ ‌کند؟» و آنان هیچ کارساز و یاریگری برای خویش جز الله نمی‌یابند.');
INSERT INTO "islamhouse" VALUES(33,18,'33:18','الله کسانی از شما را که بازدارنده[ی مردم از جهاد] هستند و به برادرانشان می‌گویند: «به ما بپیوندید» به خوبی می‌شناسد. [آنان سست‌باورند] و جز اندکی به میدان جنگ نمی‌آیند.');
INSERT INTO "islamhouse" VALUES(33,19,'33:19','[منافقان در همه چیز] بر شما بخل می‌ورزند؛ و هنگامی که بیم [جنگ پیش] می‌آید، در حالی که چشم‌هایشان [از وحشت در حدقه] می‌چرخد، همچون کسی که از [سختیِ] مرگ بیهوش شده باشد تو را نگاه می‌کنند؛ و چون [بحران و] ترس برطرف شد، به خاطر حرص و طمعی که به غنایم دارند، با زبان‌های تند و نیشدار شما را می‌رنجانند. آنان [هرگز] ایمان نیاورده‌اند؛ پس الله اعمالشان را تباه می‌گردانَد و این [کار] همواره بر الله آسان است.');
INSERT INTO "islamhouse" VALUES(33,20,'33:20','[منافقان] مى‌پندارند [که لشکریان] احزاب [هنوز از اطراف مدینه] نرفته‌اند؛ و اگر احزاب بازگردند، [این سست‌باوران] آرزو می‌کنند که میان بادیه‌نشینان [پنهان] باشند [و از همانجا] اخبارِ [درگیرىِ] شما را جویا شوند؛ و [حتی] اگر بین [سپاه] شما بودند، جز اندکی نمی‌جنگیدند.');
INSERT INTO "islamhouse" VALUES(33,21,'33:21','بی‌تردید، در [رفتار و گفتار] رسول الله، سرمشق نیکویی برایتان است؛ برای هر کس که به الله و روز آخرت امید دارد و الله را بسیار یاد می‌کند.');
INSERT INTO "islamhouse" VALUES(33,22,'33:22','و چون مؤمنان [لشکر] احزاب را دیدند، گفتند: «این [امتحان الهی و پیروزی نهایی‌اش] همان چیزی است که الله و پیامبرش به ما وعده داده‌اند و [حقا که] الله و پیامبرش راست گفتند» و [مشاهدۀ انبوه دشمن] جز بر ایمان و تسلیمشان [در مقابل پروردگار] نیفزود.');
INSERT INTO "islamhouse" VALUES(33,23,'33:23','در میان مؤمنان، مردانی هستند که به عهد و پیمانی که با الله بسته بودند [صادقانه] وفا کردند؛ برخی از آنان تا آخر بر عهد و پیمان خود ایستادند [و در این راه درگذشتند یا شهید شدند] و برخی دیگر هنوز چشم‌انتظار [شهادت] هستند و هرگز [پیمان خود را] دگرگون نساخته‌اند.');
INSERT INTO "islamhouse" VALUES(33,24,'33:24','[این رویدادها پیش می‌آید] تا الله راستگویان را به [پاسِ پایمردی و] راستگویی‌شان پاداش دهد و منافقان را ـ اگر بخواهد ـ عذاب کند یا توبه‌شان را بپذیرد. بی‌تردید، الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(33,25,'33:25','و الله [سپاه] کسانی را که کفر ورزیده‌اند، بى‌آنكه به غنیمی دست یابند، با [تمام حسرت و] خشمشان [از اطراف مدینه] برگرداند و الله مؤمنان را از جنگ بی‌نیاز ساخت؛ و الله همواره توانای شکست‌ناپذیر است.');
INSERT INTO "islamhouse" VALUES(33,26,'33:26','[الله] گروهی از اهل کتاب [= یهود بنی‌قُریظه] را که از آنان [= مشرکان] پشتیبانی کرده بودند، از قلعه‌هایشان پایین کشید و در دل‌هایشان وحشت انداخت؛ [چنان که شما] گروهی [از آنان] را می‌کشتید و گروهی را به اسارت می‌گرفتید.');
INSERT INTO "islamhouse" VALUES(33,27,'33:27','و زمین‌های کشاورزی و خانه‌ها و اموالشان و [همچنین] سرزمینی را که هرگز به آنجا پا نگذاشته بودید [= خیبر] در اختیار شما نهاد؛ و الله بر هر چیزی تواناست.');
INSERT INTO "islamhouse" VALUES(33,28,'33:28','ای پیامبر، به همسرانت [که نفقۀ بیشتری می‌خواهند] بگو: «اگر زندگی دنیا و زینت‌هایش را می‌خواهید، بیایید تا شما را [با هدیه‌ای مناسب] بهره‌مند سازم و [بدون هیچ آزاری] به نیکویی رهایتان کنم.');
INSERT INTO "islamhouse" VALUES(33,29,'33:29','و اگر مشتاق الله و پیامبرش و سرای آخرت هستید، [بدانید که] بی‌تردید، الله پاداش بزرگی برای نیکوکارانتان فراهم کرده است».');
INSERT INTO "islamhouse" VALUES(33,30,'33:30','ای زنان پیامبر، هر یک از شما که [گناه] ناشایستِ آشکاری مرتکب شود، عذابش دو چندان می‌گردد؛ و این [کار] همواره برای الله آسان است.');
INSERT INTO "islamhouse" VALUES(33,31,'33:31','و هر‌یک از شما که پیوسته فرمانبردارِ الله و پیامبرش باشد و کاری شایسته کند، پاداش او را دو برابر می‌دهیم و [در بهشت] روزی ارزشمندی برایش فراهم آورده‌ایم.');
INSERT INTO "islamhouse" VALUES(33,32,'33:32','ای زنان پیامبر، اگر پرهیزگاری کنید، [در برتری و شرافت] مانند هیچ یک از زنان [دیگر] نخواهید بود؛ پس در هنگام سخن گفتن [با مردان]، ناز و نرمش به کار نبرید تا [مبادا] کسی که در دلش بیماری است [در شما] طمع كند و به شیوه‌ای شایسته سخن بگویید.');
INSERT INTO "islamhouse" VALUES(33,33,'33:33','و در خانه‌هایتان بمانید و همچون جلوه‌گری [زنان در دوران] جاهلیت پیشین، جلوه‌گری [و خودنمایی] نکنید و نماز برپا دارید و زکات بپردازید و از الله و پیامبرش اطاعت کنید. ای اهل خانه[ی پیامبر]، جز این نیست الله می‌خواهد پلیدی [و آزارِ بیگانگان] را از شما دور نماید و شما را کاملاً پاک گردانَد.');
INSERT INTO "islamhouse" VALUES(33,34,'33:34','و هر آنچه را که از آیات الهی و سنت پیامبر در خانه‌هایتان خوانده می‌شود، به یاد داشته باشید. بی‌تردید، الله همواره باریک‌بین آگاه است.');
INSERT INTO "islamhouse" VALUES(33,35,'33:35','الله براى مردان مسلمان و زنان مسلمان و مردان مؤمن و زنان مؤمن و مردان فرمانبردار و زنان فرمانبردار و مردان راستگو و زنان راستگو و مردان شکیبا و زنان شکیبا و مردان فروتن و زنان فروتن و مردان انفاقگر و زنان انفاقگر و مردان روزه‌دار و زنان روزه‌دار و مردان پاکدامن و زنان پاکدامن و مردان و زنانی که الله را بسیار یاد می‌کنند، برای [همگی] آنان آمرزش و پاداشی بزرگ فراهم نموده است.');
INSERT INTO "islamhouse" VALUES(33,36,'33:36','براى هیچ مرد و زن مؤمنى شایسته نیست كه وقتى الله و رسولش به كارى فرمان می‌دهند، [در اجرای آن فرمان،] اختیارى داشته باشد؛ و هر کس از الله و رسولش نافرمانى كند، حقا که در گمراهىِ آشكارى گرفتار شده است.');
INSERT INTO "islamhouse" VALUES(33,37,'33:37','[به یاد آور] وقتی به [زید بن‌حارثه] ـ كسى كه الله به او نعمت [اسلام] داده بود و تو نعمت [آزادى] به او بخشیده بودى‌ـ [به نصیحت] مى‌گفتى: «همسرت را نزد خویش نگه دار و از الله پروا کن» و در دل خویش چیزی را [در مورد ازدواج با زینب پس از طلاقش] پنهان می‌داشتی که الله آشکارکنندۀ آن بود و [چون این ازدواج، با سنت‌های جاهلى مغایر بود] از [سرزنشِ] مردم می‌ترسیدی؛ حال آنکه الله سزاوارتر است که از او بترسی. پس هنگامی که زید نیازش را از وی به پایان بُرد [و طلاقش داد]، او را به ازدواج تو درآوردیم تا براى مؤمنان در مورد ازدواج با همسرانِ پسرخوانده‌هایشان كه از آنان كام گرفته‌اند [و طلاقشان داده‌اند]، هیچ گناهى نباشد؛ و فرمان الله همواره انجام‌پذیر است.');
INSERT INTO "islamhouse" VALUES(33,38,'33:38','بر پیامبر، در مورد آنچه الله برایش مقرر [و حلال] داشته است، هیچ گونه گناهی وجود ندارد. [این کار،] سنت الهی است [و] دربارۀ کسانی ‌[= پیامبرانی] که پیش از این بوده‌اند [نیز صدق می‌کند]؛ و فرمان الله همواره دقیق و حساب‌شده است.');
INSERT INTO "islamhouse" VALUES(33,39,'33:39','[همان] کسانی که پیام‌های الله را [به مردم] می‌رسانند و تنها از او می‌ترسند و از هیچ کس جز الله ترسی ندارند؛ و الله برای حسابرسی [کارهای بندگانش] کافی است.');
INSERT INTO "islamhouse" VALUES(33,40,'33:40','محمد هرگز پدر هیچ‌یک از مردان شما [از جمله زید] نیست؛ بلکه رسول الله و خاتم پیامبران است؛ و الله همواره به همه چیز داناست.');
INSERT INTO "islamhouse" VALUES(33,41,'33:41','ای کسانی که ایمان آورده‌اید، الله را بسیار یاد کنید');
INSERT INTO "islamhouse" VALUES(33,42,'33:42','و بامداد و شامگاه او را به پاکی بستایید.');
INSERT INTO "islamhouse" VALUES(33,43,'33:43','اوست که بر شما درود [و رحمت] می‌فرستد و فرشتگانش [نیز دعایتان می‌کنند] تا شما را از تاریکی‌ها به سوی نور بیرون آورد و [الله] همواره نسبت به مؤمنان مهربان است.');
INSERT INTO "islamhouse" VALUES(33,44,'33:44','روزی ‌که با او دیدار می‌کنند، درودشان «سلام» است و [الله] پاداشی ارجمند برایشان فراهم ساخته است.');
INSERT INTO "islamhouse" VALUES(33,45,'33:45','ای پیامبر، ما تو را گواه و بشارت‌بخش و بیم‌دهنده فرستاده‌ایم');
INSERT INTO "islamhouse" VALUES(33,46,'33:46','و [تو را] دعوتگری به سوی الله به فرمان او و چراغی روشنی‌بخش [قرار دادیم].');
INSERT INTO "islamhouse" VALUES(33,47,'33:47','و به مؤمنان بشارت بده که از جانب الله بخششی بزرگی برایشان [در پیش] است.');
INSERT INTO "islamhouse" VALUES(33,48,'33:48','و از کافران و منافقان اطاعت نکن و به آزار‌شان بی‌توجه باش و بر الله توکل کن؛ و همین بس که الله [یاور و] کارگزار تو باشد.');
INSERT INTO "islamhouse" VALUES(33,49,'33:49','ای کسانی که ایمان آورده‌اید، هرگاه با زنان مؤمن ازدواج کردید، سپس پیش از اینکه با آنان همبستر شوید طلاقشان دادید، از جانب شما هیچ‌‌ عدّه‌اى بر عهدۀ آنان نیست که حسابش را نگاه دارید؛ آنان را [با هدیه‌ای مناسب] بهره‌مند سازید و به نیکوترین وجه رهایشان سازید.');
INSERT INTO "islamhouse" VALUES(33,50,'33:50','ای پیامبر، ما همسرانت که مهر‌شان را پرداخته‌ای و [نیز] کنیزانی را که الله [از بهرۀ جنگ] به تو ارزانی داشته است برایت حلال کرده‌ایم و [نیز ازدواج با] دخترعموهایت و دخترعمه‌هایت و دختردایی‌هایت و دخترخاله‌هایت که با تو هجرت کرده‌اند و [همچنین] اگر زن مؤمنی خود را [بدون مهریه] به پیامبر ببخشد، چنانچه پیامبر بخواهد [می‌تواند] با وی ازدواج کند. [این حکم،] ویژۀ توست، نه دیگر مؤمنان. بی‌تردید، ما می‌دانیم در مورد همسران و کنیزانشان چه حکمی برای آنان تعیین کرده‌ایم. [این حکمِ ویژه را برقرار کردیم] تا هیچ تنگنایی [در راه انجام وظیفه] بر تو نباشد؛ و الله همواره آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(33,51,'33:51','[نوبت] هر یک از آنان [= همسرانت] را که بخواهی [می‌توانی] به تأخیر اندازی و هر کدام را بخواهی، [می‌توانی] نزد خویش جای دهی؛ و اگر هر یک از آنان را که از او کناره گرفته‌ای [و نوبتش به تأخیر افتاده است، دوباره] طلب کنی، [در هر حال] هیچ گناهی بر تو نیست. این [اختیار در انتخاب،] نزدیک‌تر است به اینكه چشمشان روشن شود و ‌اندوهگین نباشند و همگی‌شان از آنچه به آنان بخشیده‌ای راضی گردند؛ و الله می‌داند که شما [مردان دربارۀ محبتِ بیشتر به برخی زنانتان] چه در دل دارید؛ و الله همواره دانای بردبار است.');
INSERT INTO "islamhouse" VALUES(33,52,'33:52','از این پس، [دیگر ازدواج با] هیچ زنی برایت حلال نیست و نباید [برخی از همسرانت را طلاق دهی تا] همسرانی را جانشین آنان کنی؛ حتی اگر زیباییِ آنان [= زنان جدید] تو را به شگفت آورَد؛ مگر آن که [به صورت کنیز] در اختیارت باشد؛ و الله همواره بر هر چیزى نگهبان است.');
INSERT INTO "islamhouse" VALUES(33,53,'33:53','ای کسانی که ایمان آورده‌اید، به خانه‌های پیامبر داخل نشوید؛ مگر آنکه برای [صرف] غذایی به شما اجازه داده شود؛ [آن هم] بى‌آنكه [زودتر از وقت غذا بروید و] منتظر آماده شدنش باشید؛ ولی هنگامى كه شما را فراخواندند وارد شوید و چون غذا خوردید پراكنده گردید و سرگرم [بحث و] گفتگو نشوید. این [رفتارتان] پیامبر را مى‌آزارد و او از [ابراز این موضوع در برابر] شما شرم دارد؛ و[لى] الله از [بیان] حق شرم ندارد. و هنگامى كه از آنان [= همسران پیامبر] چیزی از وسایل زندگی را [به عاریت] خواستید، [نیازتان را] از پشت پرده‌ از آنان بخواهید [چرا که] این [کار] برای دل‌های شما و آنان پاک‌تر است؛ و [همچنین] برایتان سزاوار نیست که رسول الله را بیازارید و هرگز شایستۀ شما نیست که پس از [وفات] او با همسرانش ازدواج کنید. به راستی که این [کار] نزد الله [گناهی] بزرگ است.');
INSERT INTO "islamhouse" VALUES(33,54,'33:54','اگر چیزی را آشکار کنید یا پنهان دارید، [بدانید که] بی‌تردید، الله از همه چیز آگاه است.');
INSERT INTO "islamhouse" VALUES(33,55,'33:55','بر آنان [= زنان مسلمان] گناهی نیست [در نداشتن حجاب] نزد پدرانشان و پسرانشان و برادرانشان و پسران برادرانشان و پسران خواهرانشان و زنان [هم‌دین]شان و بردگانشان؛ و [ای زنان مسلمان،] از الله پروا کنید. بی‌تردید، الله بر همه چیز گواه است.');
INSERT INTO "islamhouse" VALUES(33,56,'33:56','به راستی، الله و فرشتگانش بر پیامبر درود می‌فرستند؛ [پس] ای کسانی که ایمان آورده‌اید، [شما نیز] بر او درود فرستید و به شایستگی سلام گویید.');
INSERT INTO "islamhouse" VALUES(33,57,'33:57','کسانی که الله و پیامبرش را می‌آزارند، الله در دنیا و آخرت آنان را از رحمت خویش دور می‌کند و عذاب خفت‌باری برایشان آماده کرده است.');
INSERT INTO "islamhouse" VALUES(33,58,'33:58','و کسانی که مردان و زنان مؤمن را بدون اینکه مرتكب [عمل زشتى‌] شده باشند [با اتهامات ناروا] می‌آزارند، بارِ دروغ و گناهی آشکار را به دوش می‌کشند.');
INSERT INTO "islamhouse" VALUES(33,59,'33:59','ای پیامبر، به همسران و دخترانت و سایر زنان مؤمن بگو که چادر‌هایشان را به شایستگی بر [سر و صورت و سینۀ] خویش بیفکنند. این [گونه پوشش] مناسب‌تر است به اینكه [در جامعه به متانت و وقار] شناخته ‌شوند و [در نتیجه،] مورد آزار [افراد هرزه] قرار نگیرند؛ و الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(33,60,'33:60','اگر منافقان و کسانی که بیماری [نفاق] در دل‌هایشان است و شایعه‌پراکنان در مدینه، [از دروغگویی و شایعه‌سازی] دست برندارند، یقیناً تو را بر آنان مسلط می‌گردانیم. در این صورت، جز مدت کوتاهی نمی‌توانند در این شهر و در کنار تو بمانند.');
INSERT INTO "islamhouse" VALUES(33,61,'33:61','لعنت‌شدگانند و هر جا که یافت شوند، باید بی‌محابا دستگیر و کشته شوند.');
INSERT INTO "islamhouse" VALUES(33,62,'33:62','[این] سنت الله، در مورد افرادی [از منافقان] که پیشتر بوده‌اند نیز جاری گشته است؛ و هرگز در سنت الله دگرگونی [و تغییری] نمی‌یابی.');
INSERT INTO "islamhouse" VALUES(33,63,'33:63','[ای پیامبر،] مردم دربارۀ [زمان وقوعِ] قیامت از تو می‌پرسند؛ بگو: «دانش آن تنها نزد الله است»؛ و تو چه می‌دانی؟ شاید‌ قیامت نزدیک باشد.');
INSERT INTO "islamhouse" VALUES(33,64,'33:64','بی‌تردید، الله کافران را از رحمت خویش دور کرده و آتشی افروخته و سوزان برایشان مهیّا نموده است.');
INSERT INTO "islamhouse" VALUES(33,65,'33:65','[آتشی] که جاودانه در آن خواهند ماند [و برای دفاع از خود،] هیچ کارساز و یاریگری نمی‌یابند.');
INSERT INTO "islamhouse" VALUES(33,66,'33:66','روزى كه چهره‌هایشان در آتش دوزخ دگرگون مى‌شود، [با پشیمانی] می‌گویند: «ای کاش از الله و پیامبر اطاعت کرده بودیم!»');
INSERT INTO "islamhouse" VALUES(33,67,'33:67','[آنان بهانه می‌آورند و] می‌گویند: «پروردگارا، ما از رهبران و بزرگان خود اطاعت [و تقلید] کردیم و آنان [بودند که] ما را گمراه کردند.');
INSERT INTO "islamhouse" VALUES(33,68,'33:68','پروردگارا، عذابشان را دو چندان کن و به لعنتی بزرگ [و سخت] لعنت فرما».');
INSERT INTO "islamhouse" VALUES(33,69,'33:69','ای کسانی که ایمان آورده‌اید، [پیامبر را نیازارید و] مانند کسانی نباشید که موسی را [با تهمت‌های ناروا] آزردند، سپس الله او را [از آنچه در حقش می‌گفتند] تبرئه نمود؛ و [موسی] همواره نزد الله آبرومند است.');
INSERT INTO "islamhouse" VALUES(33,70,'33:70','ای کسانی که ایمان آورده‌اید، از الله پروا کنید و سخنی راست و درست بگویید.');
INSERT INTO "islamhouse" VALUES(33,71,'33:71','[پرهیزگار و راستگو باشید] تا الله کارهایتا‌ن را اصلاح کند و گناهانتان را بیامرزد و [بدانید] هر کس از الله و پیامبرش اطاعت کند، یقیناً به کامیابی بزرگی دست یافته است.');
INSERT INTO "islamhouse" VALUES(33,72,'33:72','ما امانت [انجام تکالیف شرعی] را بر آسمان‌ها و زمین و کوهساران عرضه داشتیم و آنها از تحمل آن سر باز زدند و از [احتمال خیانت در] آن ترسیدند و[لی] انسان آن را پذیرفت. حقا که او [در حق خویش] همواره ستمکار و نادان است.');
INSERT INTO "islamhouse" VALUES(33,73,'33:73','[انسان چنین کرد] تا الله مردان و زنان منافق و مردان و زنان مشرک را عذاب کند و توبۀ مردان و زنان مؤمن را [که امانتدارانِ شایسته‌ای بوده‌اند] بپذیرد؛ و الله همواره آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(34,1,'34:1','ستایش مخصوص الله است که آنچه در آسمان‌ها و زمین است از آنِ اوست و در آخرت [نیز] ستایش از آنِ اوست و او تعالی حکیم و آگاه است.');
INSERT INTO "islamhouse" VALUES(34,2,'34:2','هر چه در زمین فرو می‌رود و هر چه از آن برمی‌آید و آنچه از آسمان فرود می‌آید و آنچه به سوی آن بالا می‌رود [همه را الله] می‌داند و او مهربانِ آمرزنده است.');
INSERT INTO "islamhouse" VALUES(34,3,'34:3','کسانی که کفر ورزیدند می‌گویند: «قیامت هرگز به سراغ ما نخواهد آمد». بگو: «آری، سوگند به پروردگارم كه دانای غیب است، قطعاً به سراغتان مى‌آید. همسنگِ ذره‌اى در آسمان‌ها و زمین از او پوشیده نیست و كوچک‌تر و بزرگ‌تر از آن ذره چیزى نیست، مگر آنكه در كتابی آشکار [= لوح محفوظ] ثبت است.');
INSERT INTO "islamhouse" VALUES(34,4,'34:4','تا کسانی را که ایمان آورده‌اند و کار‌های شایسته انجام داده‌اند پاداش دهد. اینانند که بخشایش و روزیِ ارزشمندی [در پیش] دارند».');
INSERT INTO "islamhouse" VALUES(34,5,'34:5','و كسانى كه در [راهِ انكار و تکذیب‌] آیات ما کوشیدند تا [به پندار خویش‌] ما را درمانده کنند، عذابی سخت و دردناک [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(34,6,'34:6','اهل دانش [= دانشمندان صحابه و علمای مؤمن اهل کتاب] می‌دانند آنچه که از سوی پروردگارت بر تو نازل شده، حق است و به راه [الله] شکست‌ناپذیرِ ستوده هدایت می‌کند.');
INSERT INTO "islamhouse" VALUES(34,7,'34:7','کسانی که کفر ورزیدند [با تمسخر به یکدیگر] می‌گویند: «می‌خواهید مردی را به شما نشان دهیم که ادعا می‌کند وقتی [پس از مرگ] کاملاً متلاشی شدید، [بارِ دیگر] در آفرینشی نو درخواهید آمد؟');
INSERT INTO "islamhouse" VALUES(34,8,'34:8','آیا [این مرد] بر الله دروغ می‌بندد یا دیوانه است؟» [هرگز چنین نیست؛] بلکه کسانی که به آخرت ایمان ندارند، در عذاب [شدید آخرت] و گمراهی دور و دراز [دنیا] گرفتارند.');
INSERT INTO "islamhouse" VALUES(34,9,'34:9','آیا [کافران] به آسمان و زمینی که پیش رو و پشت سرشان است ننگریسته‌اند؟ اگر بخواهیم، آنان را [مانند قارون] در زمین فرومی‌بریم یا [همچون قوم شعیب] پاره‌های از آسمان را بر سرشان می‌افکنیم. بی‌گمان، در این [هشدار،] برای هر بندۀ توبه‌کاری، نشانه‌ای [از قدرتِ الله] است.');
INSERT INTO "islamhouse" VALUES(34,10,'34:10','ما از جانب خویش فضیلتی [= حُکمرانی و نبوت] به داود بخشیدیم [و گفتیم:] «ای کوهساران و ای پرندگان، [در تسبیح و نیایشِ الله] با او هم‌آواز شوید». و آهن را برایش نرم کردیم [تا هر چه می‌خواهد بسازد].');
INSERT INTO "islamhouse" VALUES(34,11,'34:11','[به او دستور دادیم] که: «زره‌های بلند [و فراخ] بساز و در ‌بافتن [و پیوستنِ حلقه‌های زره] سنجیده و دقیق کار کن و [شما ای مردم،] نیکوکاری کنید [که] بی‌تردید، من به آنچه می‌کنید بینا هستم».');
INSERT INTO "islamhouse" VALUES(34,12,'34:12','و برای سلیمان، باد را [به خدمت گماشتیم] که [در وزشِ] صبحگاهان، مسیر یک ماهه را طی می‌کرد و شامگاهان [نیز] مسیر یک ماهه را می‌پیمود؛ و چشمۀ مس [گداخته] را برایش روان ساختیم و گروهی از جنیان، به فرمان پروردگارش، نزد او کار می‌کردند و هر یک از آنان که از فرمان ما سرپیچی می‌کرد، از عذاب سوزان به او می‌چشاندیم.');
INSERT INTO "islamhouse" VALUES(34,13,'34:13','آنها هر چه می‌خواست برایش می‌ساختند: از [کاخ و] عبادتگاه و مجسمه و کاسه‌هایی [که از بزرگی] همچون حوض [بودند] و دیگ‌هایی بسیار بزرگ و ثابت. [به آنان گفتیم:] «ای آل داود، سپاس [نعمت‌های مرا] به جای آوردید» و[لی] اندکی از بندگانم سپاسگزارند.');
INSERT INTO "islamhouse" VALUES(34,14,'34:14','هنگامی که مرگ را بر سلیمان مقرر داشتیم، [هیچ کس] آنان را از مرگش آگاه نساخت، مگر موریانه‌ای که عصایش را [به تدریج] ‌خورد. وقتی [پیکر سلیمان] فروافتاد، جنیان دریافتند که اگر از غیب آگاه بودند، هرگز در آن عذاب خفت‌بار [و کارِ طاقت‌فرسا] باقی نمی‌ماندند.');
INSERT INTO "islamhouse" VALUES(34,15,'34:15','در محل سكونتِ [قوم] «سبأ» دو باغ [بزرگ] از راست و چپ [قرار داشت] كه نشانه‌اى [از قدرت و بخشش الهى] بود. [به آنان گفتیم:] «از روزی پروردگار‌تان بخورید و شُکرگزارش باشید [که] سرزمینی پاک و پروردگاری آمرزنده [دارید]».');
INSERT INTO "islamhouse" VALUES(34,16,'34:16','اما آنان [از شُکر و ایمان] روی گرداندند؛ پس ما سیلی ویرانگر به سویشان روانه کردیم و دو باغ [پرمحصول] آنان را به دو باغ [بی‌ارزش] تبدیل نمودیم [که تنها] میوه‌های تلخ و شوره‌گز و اندکی سِدر داشت.');
INSERT INTO "islamhouse" VALUES(34,17,'34:17','ما آنان را به سزای ناسپاسی‌شان اینچنین کیفر دادیم و آیا جز ناسپاس را کیفر می‌دهیم؟');
INSERT INTO "islamhouse" VALUES(34,18,'34:18','و [همچنین] میان آنها [= قوم سبأ در یمن] و سرزمین‌هایی که به آنها برکت داده بودیم [= شام] شهرهایی [خرّم و] به‌هم‌پیوسته قرار دادیم و سیر و سفری [آسان] در آن مقرر نمودیم [و به آنان گفتیم:] «شب و روز بین این [راه]ها ایمن [و آسوده] سفر کنید».');
INSERT INTO "islamhouse" VALUES(34,19,'34:19','اما آنان [با ناسپاسی] گفتند: «پروردگارا، میان سفرهای ما فاصلۀ بیشتری ایجاد کن [تا ارزش اسب‌ها و شترهایمان نمایان گردد و بینوایان نتوانند همچون ثروتمندان به راحتى سفر كنند]». آنان به خویشتن ستم کردند؛ ما نیز سخت آنها را متفرق و پراکنده ساختیم و [سرگذشت] آنان را داستانی [عبرت‌آموز] برای دیگران قرار دادیم. به راستی که در این [ماجرا] برای هر شکیبای سپاسگزاری، نشانه‌هایی [از قدرت الهی] است.');
INSERT INTO "islamhouse" VALUES(34,20,'34:20','و یقیناً ابلیس گمان خود را در مورد آنان به تحقق رساند؛ پس جز گروهی [اندک] از مؤمنان، [همگی] از او پیروی کردند.');
INSERT INTO "islamhouse" VALUES(34,21,'34:21','[شیطان] بر آنان هیچ تسلطی نداشت؛ بلکه [ما می‌خواستیم] کسی را که به آخرت ایمان دارد، از کسی که دربارۀ آن تردید دارد مشخص کنیم؛ و پروردگارت بر هر چیزی نگهبان است.');
INSERT INTO "islamhouse" VALUES(34,22,'34:22','[ای پیامبر، به مشرکان] بگو: «کسانی را که به جای الله [یاور و شفاعتگرِ خود] می‌پندارید، [به فریاد] بخوانید. آنان حتی همسنگِ ذره‌ای در آسمان‌ها و زمین مالک چیزی نیستند و نه در [آفرینش و تدبیر] آن دو [= زمین و آسمان] شرکتی دارند و نه او [= الله] از جانب آنان هیچ پشتیبانی دارد.');
INSERT INTO "islamhouse" VALUES(34,23,'34:23','شفاعت [هیچ کس] نزد او [= الله] سودی ندارد؛ مگر برای آن کس که [الله] به او اجازه داده باشد. [در آن روز فرشتگان از خوف و هیبت الهی ترسانند] تا آنگاه که اضطراب [و تشویش] از دل‌هایشان برطرف می‌گردد، [گروهی از آنان به گروه دیگر] می‌گویند: «پروردگار‌تان چه گفت؟» [آنان در پاسخ] می‌گویند: «حق را [فرمود] و او بلندمرتبۀ بزرگ است»');
INSERT INTO "islamhouse" VALUES(34,24,'34:24','[ای پیامبر، به مشرکان] بگو: «چه کسی از آسمان‌ها و زمین به شما روزی می‌دهد؟» [در پاسخ] بگو: «الله؛ و یقیناً [یک گروه از بین] ما و شما یا بر [راهِ] هدایت است یا در گمراهى آشكار».');
INSERT INTO "islamhouse" VALUES(34,25,'34:25','بگو: «[روز قیامت] شما از گناهی که ما مرتکب شده‌ایم بازخواست نمی‌شوید و ما را [نیز] به خاطر آنچه شما انجام می‌دهید بازخواست نمی‌کنند».');
INSERT INTO "islamhouse" VALUES(34,26,'34:26','بگو: «[آن روز] پروردگارمان [همۀ] ما را گرد می‌آورد؛ سپس میان ما به‌حق [و عدالت] داوری می‌کند و او داورِ داناست».');
INSERT INTO "islamhouse" VALUES(34,27,'34:27','بگو: «کسانی را که [در قدرت و تدبیر] شریک او [= الله] ساخته‌اید به من نشان دهید. هرگز [چنین نیست که می‌پندارید]؛ بلکه او الله شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(34,28,'34:28','[ای پیامبر،] ما تو را جز بشارت‌بخش و بیم‌دهنده‌ای برای همۀ مردم نفرستادیم؛ اما بیشتر مردم [این حقیقت را] نمی‌دانند.');
INSERT INTO "islamhouse" VALUES(34,29,'34:29','و [کافران] می‌گویند: «اگر راستگویید، این وعدۀ [قیامت] کی خواهد بود؟»');
INSERT INTO "islamhouse" VALUES(34,30,'34:30','بگو: «وعده‌گاهتان روزی است که نه ساعتی از آن تأخیر می‌کنید و نه [بر آن] پیشی می‌گیرید».');
INSERT INTO "islamhouse" VALUES(34,31,'34:31','و کسانی که کفر ورزیدند می‌گویند: «ما هرگز به این قرآن و به آن [کتاب‌هایی] که پیش از آن بوده است ایمان نمی‌آوریم». كاش مى‌دیدى زمانی را كه این ستمکاران در پیشگاه پروردگارشان [برای حسابرسی] نگاه داشته مى‌شوند [و] سخن یكدیگر را رد مى‌كنند [و هر یک گناه را به گردن دیگری می‌اندازد]. کسانی که [در دنیا] به ناتوانی کشیده شده بودند به کسانی که گردنکشی می‌کردند می‌گویند: «اگر شما نبودید، ما قطعاً ایمان مى‌آوردیم».');
INSERT INTO "islamhouse" VALUES(34,32,'34:32','کسانی که گردنکشی می‌کردند [در پاسخ به] کسانی که به ناتوانی کشیده شده بودند می‌گویند: «آیا ما [بودیم که] شما را از هدایت ـ پس از آنکه به سویتان آمدـ بازداشتیم؟ [هرگز؛] بلکه شما خود گناهکار [و اهل فساد] بودید».');
INSERT INTO "islamhouse" VALUES(34,33,'34:33','کسانی که به ناتوانی کشیده شده بودند به کسانی که گردنکشی می‌کردند می‌گویند: «[چنین نیست؛] بلکه نیرنگ شب و ‌روز [شما سبب شد که از هدایت بازمانیم]؛ آنگاه ‌که به ما دستور می‌دادید به الله کافر شویم و [در قدرت و عبادت] همتایانی برایش قرار دهیم». آنان هنگامی که عذاب [الهی] را می‌بینند، پشیمانی خود را [در دل] پنهان می‌کنند و ما غُل و زنجیرها را در گردن‌ کسانی که کفر ورزیده‌اند می‌اندازیم. آیا جز [به کیفرِ] آنچه می‌کردند مجازات می‌شوند؟');
INSERT INTO "islamhouse" VALUES(34,34,'34:34','و در هیچ شهری بیم‌دهند‌ای نفرستادیم، مگر اینکه ثروتمندانِ سرکشِ آنجا گفتند: «پیامى را كه مأمور به [ابلاغ] آن شده‌اید، قطعاً انكار مى‌كنیم».');
INSERT INTO "islamhouse" VALUES(34,35,'34:35','آنان می‌گفتند: «اموال و فرزندان ما [از شما] بیشتر است و هرگز مجازات نخواهیم شد».');
INSERT INTO "islamhouse" VALUES(34,36,'34:36','[ای پیامبر،] بگو: «بی‌تردید، پروردگارم [برای آزمایش مردم، نعمت و] روزی را بر هر کس که بخواهد، می‌گشاید و [یا] تنگ می‌گرداند؛ ولی بیشتر مردم نمی‌دانند».');
INSERT INTO "islamhouse" VALUES(34,37,'34:37','و اموال‌ و فرزندانتان [فضیلتی] نیست که شما را به پیشگاهِ ما نزدیک سازد، مگر کسی ‌که ایمان آورده‌ و کاری شایسته انجام داده‌ است؛ که آنان در برابر آنچه کرده‌اند، پاداش چند برابر دارند و در خانه‌های بلند بهشت، در امن و آسایشند».');
INSERT INTO "islamhouse" VALUES(34,38,'34:38','و كسانى كه در [دور کردنِ مردم از] آیات ما می‌كوشند تا [به پندار خویش‌] ما را درمانده کنند، آنان قطعاً در عذاب [دوزخ] احضار می‌شوند.');
INSERT INTO "islamhouse" VALUES(34,39,'34:39','[ای پیامبر،] بگو: «بی‌تردید، پروردگارم [نعمت و] روزی را بر هر کس که بخواهد، می‌گشاید و [یا] تنگ می‌گرداند؛ و هر چیزی را [که در راه الله] انفاق کنید، او به جای آن [مال ـ و بهتر از آن ـ به شما] بازپس می‌دهد؛ و او تعالی بهترین روزی‌دهنده است».');
INSERT INTO "islamhouse" VALUES(34,40,'34:40','و [یاد کن از] روزی که الله همۀ آنان را برمی‌انگیزد، آنگاه به فرشتگان می‌گوید: «آیا اینها شما را عبادت می‌کردند؟»');
INSERT INTO "islamhouse" VALUES(34,41,'34:41','[فرشتگان] می‌گویند: «تو منزّهی؛ [دوست و] کارسازِ ما تو هستی، نه آنان؛ بلکه [این مشرکان] شیاطین را عبادت می‌کردند و بیشتر آنان به آنها ایمان داشتند».');
INSERT INTO "islamhouse" VALUES(34,42,'34:42','امروز هیچ یک از شما سود و زیانی برای دیگری ندارد؛ و به کسانی که [با شرک ورزیدن] به خود ستم می‌کردند می‌گوییم: «عذاب آتشی را بچشید که آن را تکذیب می‌کردید».');
INSERT INTO "islamhouse" VALUES(34,43,'34:43','و هنگامی که آیات روشن ما بر آنان [= کافران] خوانده می‌شود، می‌گویند: «این جز مردی نیست که می‌خواهد شما را از آنچه پدرانتان عبادت می‌کردند بازدارد»؛ و [نیز] می‌گویند: «این [قرآن] جز دروغی بربافته نیست»؛ و کسانی که کفر ورزیدند، دربارۀ حق ـ وقتی به سویشان آمد ـ گفتند: «این [قرآن، چیزی] نیست، مگر جادویی آشکار».');
INSERT INTO "islamhouse" VALUES(34,44,'34:44','ما [پیش از این قرآن] هیچ کتابی به آنان [= مشرکان] نداده‌ایم که آن را بخوانند [و با استناد به مطالبش تو را دروغگو بدانند] و پیش از تو هیچ بیم‌دهنده‌ای به سویشان نفرستاده‌ایم.');
INSERT INTO "islamhouse" VALUES(34,45,'34:45','کسانی که پیش از آنان بودند [نیز پیامبران الهی را] دروغگو انگاشتند و [این در حالی است که مشرکانِ قوم تو، حتی] به یک‌دهمِ آنچه [از قدرت و ثروت] به آنان داده بودیم نرسیده‌اند ولی [آن کافران] پیامبرانم را دروغگو انگاشتند؛ [بنگر که] کیفر من چگونه بود.');
INSERT INTO "islamhouse" VALUES(34,46,'34:46','[ای پیامبر، به مشرکان] بگو: «من شما را فقط به یک [سخن] پند می‌دهم و [آن] اینکه: دو نفر دو نفر یا یک نفر یک نفر [= گروهی یا انفرادی] برای الله برخیزید، سپس [در احوال و زندگی پیامبر] بیندیشید [تا بدانید که] این همنشین شما [محمد] دیوانه نیست؛ او فقط بیم‌دهنده‌ای است که پیش از [فرارسیدن] عذابی سخت، برای [هدایت] شما آمده است».');
INSERT INTO "islamhouse" VALUES(34,47,'34:47','بگو: «هر پاداشی که [برای رسالتم] از شما خواسته‌ام، برای خودتان باشد. پاداش من جز بر [عهدۀ] الله نیست و او تعالی بر هر چیزی گواه است».');
INSERT INTO "islamhouse" VALUES(34,48,'34:48','بگو: «یقیناً پروردگارم، که دانای رازهای نهان است، حق را بر باطل فرومی‌افکنَد [و آن را نابود می‌سازد]».');
INSERT INTO "islamhouse" VALUES(34,49,'34:49','بگو: «حق [= اسلام] فرارسید و باطل [دیگر] نمی‌تواند [تأثیر و قدرت جدیدى] آغاز كند و نه [نفوذ گذشته‌اش را] بازگردانَد».');
INSERT INTO "islamhouse" VALUES(34,50,'34:50','بگو: «اگر [من با رساندن این پیام] گمراه شده باشم، جز این نیست که به زیان خویش گمراه ‌شده‌ام و اگر هدایت یافته باشم، به سببِ آن [سخنی] است که پروردگارم بر من وحی می‌کند. بی‌تردید، او شنوا [و] نزدیک است».');
INSERT INTO "islamhouse" VALUES(34,51,'34:51','[ای پیامبر!] اگر ببینی هنگامی را که [کافران از مشاهدۀ عذاب] وحشت‌زده می‌شوند و [هیچ راهِ] گریزی نیست و آنان را از جای نزدیکی [که انتظارش را ندارند] دستگیر می‌کنند.');
INSERT INTO "islamhouse" VALUES(34,52,'34:52','و [در این حال] می‌گویند: «به این [قیامت] ایمان آوردیم»؛ و [اکنون که مرده‌اند] چگونه از جایی [چنین] دور، دسترسی [به ایمان] برایشان ممکن است؟');
INSERT INTO "islamhouse" VALUES(34,53,'34:53','حال آنکه آنان در دنیا این [قیامت] را انکار می‌کردند و از دور [و بدون دانش و آگاهی،] نسبت‌های ناروا [به پیامبر] می‌دادند.');
INSERT INTO "islamhouse" VALUES(34,54,'34:54','و [سرانجام،] میان آنان و آنچه [از لذت‌های دنیوی] که میل دارند جدایى افتاد؛ چنان كه پیش از این [نیز] با امثال [و همفكرانِ] اینان نیز چنین رفتاری شد [چرا که] یقیناً آنان [نیز نسبت به قیامت] سخت در تردید بودند.');
INSERT INTO "islamhouse" VALUES(35,1,'35:1','سپاس [و ستایش] از آنِ الله - آفرینندۀ آسمان‌ها و زمین- است؛ [پروردگاری که] فرشتگان را با بال‌های دوگانه و سه‌گانه و چهارگانه، فرستادگان [خویش به سوی مردم] قرار داد [و] هر چه بخواهد در آفرینش می‌افزاید. بی‌تردید، الله بر هر چیزی تواناست.');
INSERT INTO "islamhouse" VALUES(35,2,'35:2','هر [بخشایش و] رحمتی که الله برای مردمان بگشاید، هیچ بازدارنده‌ای برایش نیست و هر چه را بازگیرد، فرستنده‌ای پس از او وجود ندارد؛ و او تعالی شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(35,3,'35:3','ای مردم، نعمت الله را بر خود به یاد آورید. آیا آفریننده‌ای جز الله هست که شما را از آسمان و زمین روزی دهد؟ هیچ معبودی جز او نیست؛ پس چگونه [از حق] منحرف می‌شوید؟');
INSERT INTO "islamhouse" VALUES(35,4,'35:4','[ای پیامبر،] اگر مشرکان تو را دروغگو می‌انگارند [غمگین نباش؛ زیرا] پیامبران پیش از تو را [نیز] دروغگو انگاشتند و [سرانجام، همۀ] کار‌ها به الله بازگردانده می‌شود.');
INSERT INTO "islamhouse" VALUES(35,5,'35:5','ای مردم، یقیناً وعدۀ الله [در مورد قیامت] حق است؛ پس [هوشیار باشید که لذت‌های] زندگی دنیا شما را نفریبد و [شيطانِ‌] فريبنده شما را نسبت به [تأخیر در عذاب و شکیباییِ] الله فریب ندهد.');
INSERT INTO "islamhouse" VALUES(35,6,'35:6','بی‌تردید، شیطان دشمن شماست؛ شما نیز او را دشمن [خود] گیرید. جز این نیست که او پیروانش را [به کفر] می‌خواند تا از اهل آتش باشند.');
INSERT INTO "islamhouse" VALUES(35,7,'35:7','کسانی که کفر ورزیده‌اند عذابی سخت [در پیش] دارند و کسانی که ایمان آورده‌اند و کار‌های شایسته انجام داده‌اند آمرزش و پاداشی بزرگ [در انتظار] دارند.');
INSERT INTO "islamhouse" VALUES(35,8,'35:8','آیا کسی که کردار زشتش در نظرش آراسته گشته است و آن را نیکو می‌بیند [همچون مؤمن حقیقت‌بین است]؟ بی‌تردید، الله هر کس را که بخواهد گمراه می‌سازد و هر کس را که بخواهد هدایت می‌کند؛ پس [ای پیامبر،] جانت به خاطر حسرت‌هایی که بر [گمراهیِ] آنان می‌خوری، از بین نرود. بی‌تردید، الله به آنچه انجام می‌دهند آگاه است.');
INSERT INTO "islamhouse" VALUES(35,9,'35:9','الله است که باد‌ها را روانه می‌کند و [آنها نیز] ابری را برمی‌انگیزند؛ سپس ما آن [ابر]‌ را به سوی سرزمینی [خشک و] مرده می‌فرستیم و به [وسیلۀ] آن، زمین را ـ پس از خشکی و خزانش‌ ـ زنده می‌کنیم. برانگیختن [مردگان از قبرها نیز] چنین است.');
INSERT INTO "islamhouse" VALUES(35,10,'35:10','هر کس سربلندی [دنیا و آخرت را] می‌خواهد، [بداند که] سربلندی همه از آنِ الله است. گفتار پاک [و ذکر الهی] به سویش اوج می‌گیرد و کردار شایسته، آن [گفتار] را بالا می‌برد؛ و کسانی که زشتی‌ها [و گناهان] را دسیسه‌چینی می‌کنند، عذاب سختی [در پیش] دارند و [نقشه و] نیرنگشان نابود می‌گردد.');
INSERT INTO "islamhouse" VALUES(35,11,'35:11','و الله شما را از خاکی آفرید، سپس از نطفه‌ای؛ سپس شما را جفت‌ها[ی یکدیگر] قرار داد. هیچ [جاندارِ] ماده‌ای باردار نمی‌شود و زایمان نمی‌کند، مگر با دانش [و آگاهیِ] او تعالی؛ و هیچ [جاندارِ] کهنسالی عمر طولانی نمی‌کند و از عمرش کاسته نمی‌گردد، مگر آنکه در کتابی [= لوح محفوظ] ثبت است. به راستی که این کار‌ برای الله آسان است.');
INSERT INTO "islamhouse" VALUES(35,12,'35:12','و آن دو دریا یکسان نیستند: این یکی شیرین و خوشگوار است و نوشیدنش گواراست و آن یکی شور و تلخ است؛ و از [صیدِ] هر یک گوشتی تازه می‌خورید و زیورى [همچون مروارید و مرجان از اعماقش] بیرون مى‌آورید که آنها را بر تن می‌کنید؛ و کشتی‌ها را در آن دریا مى‌بینى كه [سینه امواج را] مى‌شكافند تا از بخشایش او [روزی خود را] بجویید؛ باشد كه شکر به جای آورید.');
INSERT INTO "islamhouse" VALUES(35,13,'35:13','[الله] شب را در روز درمی‌آورد و روز را در شب درمی‌آورد [و اینچنین بر طول هر یک می‌افزاید] و خورشید و ماه را به خدمت [انسان] گماشت که هر یک تا زمانی معیّن [در مدار خود] در حرکت باشند. این است الله ـ پروردگار شما؛ فرمانروایی از آنِ اوست و کسانی را که به جای او [به یاری و دعا] می‌خوانید، [در برابر او تعالی چنان فقیرند که حتی] مالکِ پوست [نازک روی] هستۀ خرمایی نیستند.');
INSERT INTO "islamhouse" VALUES(35,14,'35:14','اگر آنان را [به یاری و دعا] بخوانید، صدایتان را نمی‌شنوند و [به فرض محال] اگر بشنوند، پاسختان را نمی‌دهند و روز قیامت از شرک شما بیزاری می‌جویند؛ و [ای پیامبر،] هیچ کس همچون [پروردگارِ] آگاه، تو را [از حقایق] خبردار نمی‌سازد.');
INSERT INTO "islamhouse" VALUES(35,15,'35:15','ای مردم، [همۀ] شما به الله نیازمندید و [تنها] الله است که بی‌نیازِ ستوده است.');
INSERT INTO "islamhouse" VALUES(35,16,'35:16','اگر بخواهد، شما را [از میان] می‌بَرد و آفرینشی جدید [به جایتان پدید] می‌آورد.');
INSERT INTO "islamhouse" VALUES(35,17,'35:17','و [انجام] این کار برای الله دشوار نیست.');
INSERT INTO "islamhouse" VALUES(35,18,'35:18','و هیچ گناهکاری بارِ گناه دیگری را به دوش نمی‌گیرد و اگر [شخص] گرانباری، [دیگری را] برای حمل بار [گناهان] خویش فرا بخواند، [چیزی از آن بار] برداشته نمی‌شود؛ هر چند که او خویشاوند [نزدیکش] باشد. [ای پیامبر،] جز این نیست که تو فقط به کسانی هشدار می‌دهی که در نهان از [مجازات] پروردگارشان می‌ترسند و نماز برپا می‌دارند. هر ‌کس [از گناهان] پاک شود، یقیناً تنها به سود خویش پاک گشته است و بازگشت [همه] به سوی الله است.');
INSERT INTO "islamhouse" VALUES(35,19,'35:19','و [کافر و مؤمن برابر نیستند؛ چنان که] نابینا و بینا یکسان نیستند؛');
INSERT INTO "islamhouse" VALUES(35,20,'35:20','و نه تاریکی‌ها ‌ و نور؛');
INSERT INTO "islamhouse" VALUES(35,21,'35:21','و نه سایه و باد سوزان؛');
INSERT INTO "islamhouse" VALUES(35,22,'35:22','و زندگان و مردگان یکسان نیستند. بی‌تردید، الله [ندای حق را] به گوش هر کس بخواهد می‌رساند و تو [ای پیامبر،] نمی‌توانی کسانی را که در گورها هستند شنوا سازی.');
INSERT INTO "islamhouse" VALUES(35,23,'35:23','تو جز بیم‌دهنده‌ای [بیش] نیستی.');
INSERT INTO "islamhouse" VALUES(35,24,'35:24','به راستی، ما تو را به‌حق بشارت‌بخش و بیم‌دهنده فرستادیم و [در نسل‌های گذشته نیز] هیچ امتی نبود، مگر آنکه میانشان بیم‌دهنده‌ای گذشته است.');
INSERT INTO "islamhouse" VALUES(35,25,'35:25','و اگر [مشرکان] تو را دروغگو می‌انگارند، [عجیب نیست؛ چرا که] بی‌تردید، کسانی ‌که پیش از آنان بودند [نیز] پیامبرانشان را که با دلایل روشن و کتاب‌های آسمانی و کتاب روشنگر به سراغشان آمدند دروغگو انگاشتند.');
INSERT INTO "islamhouse" VALUES(35,26,'35:26','آنگاه [من نیز] کسانی را که کفر ورزیده بودند نابود ساختم؛ پس [بنگر که] کیفر من چگونه بود.');
INSERT INTO "islamhouse" VALUES(35,27,'35:27','آیا ندیده‌ای که الله آبی از آسمان نازل کرد و به [وسیلۀ] آن، [از درختان مختلف] میوه‌هایی پدید ‌آوردیم که رنگهایش گوناگون است و از کوه‌ها [نیز رگه‌ها و] راه‌هایی سفید و قرمز و كاملاً سیاه [آفریدیم] که رنگهایش گوناگون است؟');
INSERT INTO "islamhouse" VALUES(35,28,'35:28','و همچنین از انسان‌ها و جنبندگان و چهارپایان [نیز مخلوقاتی آفریدیم که] رنگهایش گوناگون است. به راستی، از [میانِ] بندگان الله، تنها دانایان [هستند که به صفات و احکام الهی آگاهی دارند و] از او می‌ترسند. بی‌تردید، الله شکست‌ناپذیرِ آمرزنده است.');
INSERT INTO "islamhouse" VALUES(35,29,'35:29','در حقیقت، کسانی که کتاب الله را می‌خوانند و نماز برپا می‌دارند و از آنچه به آنان روزی داده‌ایم، پنهان و آشکار انفاق می‌کنند، به تجارتی [پرسود] امید دارند که هرگز زیان [و کسادی] ندارد.');
INSERT INTO "islamhouse" VALUES(35,30,'35:30','تا [الله] پاداششان را به صورت کامل بدهد و از فضل خویش، بر [ثواب] آنان بیفزاید. بی‌تردید، او تعالی آمرزندۀ سپاسگزار است.');
INSERT INTO "islamhouse" VALUES(35,31,'35:31','[ای پیامبر،] آنچه از این کتاب به تو وحی کردیم حق است و آن [کتاب‌های آسمانی] را که پیشتر بوده است تصدیق می‌کند. بی‌تردید، الله به [حال] بندگانش آگاه و بیناست.');
INSERT INTO "islamhouse" VALUES(35,32,'35:32','آنگاه این کتاب [= قرآن] را به آن بندگانمان که [آنان را] برگزیدیم [= امت محمد] به میراث دادیم؛ [برخی] از آنان در حق خویش ستمکارند و [برخی دیگر] از آنان میانه‌رو هستند و [برخی] از آنان به فرمان الله در نیکی‌ها پیشتازند. این [مسلمانی و بهره‌مندی از قرآن، فضیلت و] بخشایش بزرگ است.');
INSERT INTO "islamhouse" VALUES(35,33,'35:33','[پاداششان] باغ‌های جاودان [بهشتی] است که به آن وارد می‌شوند. در آنجا به دستبند‌هایی از طلا و مروارید آراسته می‌گردند و لباسشان از حریر است.');
INSERT INTO "islamhouse" VALUES(35,34,'35:34','[آنان پس از ورود به بهشت] می‌گویند: «سپاس [و ستایش] از آنِ الله است که اندوه را از ما دور کرد. به راستی که پروردگارمان آمرزندۀ سپاسگزار است.');
INSERT INTO "islamhouse" VALUES(35,35,'35:35','آن [معبودی] که از فضل خویش ما را در این سرای جاوید جای داد؛ در آنجا رنجی به ما نمی‌رسد و دچار خستگی [و کسالت نیز] نمی‌شویم.');
INSERT INTO "islamhouse" VALUES(35,36,'35:36','و برای کسانی که کفر ورزیدند آتش دوزخ [در پیش] است؛ نه فرمان [مرگ] بر آنان جارى مى‌شود تا بمیرند و نه [چیزی] از عذابش از آنان کاسته مى‌گردد. ما هر ناسپاسى را اینچنین جزا مى‌دهیم.');
INSERT INTO "islamhouse" VALUES(35,37,'35:37','[دوزخیان] در آن [آتش] فریاد می‌زنند: «پروردگارا، ما را [از این عذاب] بیرون آور تا غیر از آنچه [پیشتر] می‌کردیم، کار شایسته‌ای انجام دهیم». [به آنان گفته می‌شود:] «آیا به شما [آنقدر فرصت و] عمر ندادیم تا هر کس که پندپذیر است در آن [مدت] پند گیرد؟ و [پیامبر نیز به عنوان] بیم‌دهنده نزدتان آمد؛ پس [حال که اطاعت نکردید، طعم عذاب را] بچشید که ستمکاران هیچ یاریگری ندارند.');
INSERT INTO "islamhouse" VALUES(35,38,'35:38','بی‌تردید، الله دانای نهانِ آسمان‌ها و زمین است [و] اوست که به رازِ دل‌ها داناست.');
INSERT INTO "islamhouse" VALUES(35,39,'35:39','او همان ذاتی است که شما را در زمین جانشین [پیشینیان] قرار داد؛ هر کس کفر بورزد کفرش به زیان خودِ اوست؛ و کافران را کفرشان نزد پروردگارشان جز خشم [و بیزاری] نمی‌افزاید؛ و کافران را کفرشان جز زیان [و گناه، چیزی] نمی‌افزاید.');
INSERT INTO "islamhouse" VALUES(35,40,'35:40','[ای پیامبر، به مشرکان] بگو: «آن معبودانی که به جای الله [به یاری و دعا] می‌خوانید، به من نشان دهید که چه چیزی از زمین را آفریده‌اند؟ یا [مگر همراه الله] در آفرینش آسمان‌ها شرکت داشته‌اند؟ یا به آنان [= مشرکان] کتابی داده‌ایم و از [مطالبِ] آن، نشانۀ روشنی [بر حقانیت خود] دارند؟» [چنین نیست؛] بلکه ستمکاران [مشرک] جز فریب، به یکدیگر وعده‌ای نمی‌دهند.');
INSERT INTO "islamhouse" VALUES(35,41,'35:41','به راستی، الله است که آسمان‌ها و زمین را نگه مى‌دارد تا [از مسیر و مدارِ خود] منحرف نشوند؛ و اگر انحراف یابند، پس از او كسی آنها را نگه نمی‌دارد. بی‌تردید، الله همواره بردبارِ آمرزنده است.');
INSERT INTO "islamhouse" VALUES(35,42,'35:42','[کافران] با سخت‌ترین سوگندهایشان به الله سوگند یاد کردند که اگر [پیامبرِ] بیم‌دهنده‌ای برایشان بیاید، از هر امتی راه‌یافته ‌ترند؛ اما چون [محمد به عنوان] بیم‌دهنده‌ای نزدشان آمد، جز دوری [و نفرت از حق چیزی] بر آنان نیفزود.');
INSERT INTO "islamhouse" VALUES(35,43,'35:43','[سوگندهایشان صادقانه نبود؛ بلکه] از روی گردنكشى در زمین و نیرنگ زشت [بود]؛ و نیرنگ‌ زشت، جز دامنگیر صاحبش نمی‌گردد. آیا [کافران، چیزی] جز سنت [الهی و کیفرِ] پیشینیان را انتظار دارند؟ هرگز در سنت الله تبدیلی نمی‌یابی و هرگز در سنت الله تغییری نمی‌یابی.');
INSERT INTO "islamhouse" VALUES(35,44,'35:44','آیا [کافران] در زمین نگشته‌اند تا ببینند سرانجامِ کسانی که پیش از آنان ـ و [حتی] نیرومندتر از آنان بودند ـ چگونه شد؟ هیچ چیزی در آسمان‌ها و زمین نیست که الله را ناتوان سازد [و از حوزۀ قدرتش بیرون رود]. بی‌تردید، او تعالی همواره دانای تواناست.');
INSERT INTO "islamhouse" VALUES(35,45,'35:45','و اگر الله مردم را به سبب آنچه کرده‌اند مجازات می‌کرد، هیچ جنبنده‌ای را بر پشت زمین باقی نمی‌گذاشت؛ اما [مهلت می‌دهد و کیفرِ] آنان را تا سرآمدی معیّن به تأخیر می‌اندازد. هنگامی که اجلشان [در قیامت] فرارسد، یقیناً الله به حال بندگانش بیناست.');
INSERT INTO "islamhouse" VALUES(36,1,'36:1','یس [= یا. سین]');
INSERT INTO "islamhouse" VALUES(36,2,'36:2','سوگند به قرآن حکیم!');
INSERT INTO "islamhouse" VALUES(36,3,'36:3','[که] قطعاً تو از پیامبران [ما] هستی');
INSERT INTO "islamhouse" VALUES(36,4,'36:4','[و] بر راه راست [اسلام قرار داری]');
INSERT INTO "islamhouse" VALUES(36,5,'36:5','[این قرآن] فروفرستادۀ [پروردگار] شکست‌ناپذیر مهربان است');
INSERT INTO "islamhouse" VALUES(36,6,'36:6','تا مردمی را [دربارۀ عذاب قیامت] بیم دهى كه پدرانشان هشدار نیافته‌اند و [به این سبب] آنها نیز غافلند.');
INSERT INTO "islamhouse" VALUES(36,7,'36:7','یقیناً فرمان [عذاب الهی] بر بیشتر آنان تحقق یافته است؛ پس [لجاجت می‌کنند و] ایمان نمی‌آورند.');
INSERT INTO "islamhouse" VALUES(36,8,'36:8','[مثال گمراهیِ‌ کافران، چنان است که گویی] ما زنجیرهایی بر گردن‌هایشان نهاده‌ایم که تا زیر چانه‌هایشان [پیچیده شده] است، چنان كه سرهایشان بالا مانده است [و از انجام هر خیری عاجزند و راه هدایت را نمی‌یابند].');
INSERT INTO "islamhouse" VALUES(36,9,'36:9','و از پیش رویشان دیواری و از پشت سر‌شان نیز دیواری نهاده‌ایم و چشمانشان را با پرده‌ای پوشانده‌ایم؛ از این روست که [حقیقت را] نمی‌بینند.');
INSERT INTO "islamhouse" VALUES(36,10,'36:10','[ای پیامبر،] برایشان یکسان است که آنان را بیم دهی یا بیم ندهی؛ [در هر حال،] ایمان نمی‌آورند.');
INSERT INTO "islamhouse" VALUES(36,11,'36:11','جز این نیست که تو به كسى بیم می‌دهى [و این کار برای کسی سود دارد] كه پیرو قرآن باشد و در نهان از [پروردگار] رحمان بترسد؛ پس او را [که چنین است] به آمرزش و پاداشى ارجمند بشارت بده.');
INSERT INTO "islamhouse" VALUES(36,12,'36:12','یقیناً ماییم که مردگان را زنده می‌کنیم و آنچه [از اعمال خیر و شر] را که از پیش فرستاده‌اند و آثار [به جای‌مانده از] آنان را می‌نویسیم؛ و همه چیز را در کتابی روشن [= لوح محفوظ] به شمار آورده‌ایم.');
INSERT INTO "islamhouse" VALUES(36,13,'36:13','[ای پیامبر، ماجرای] مردم آن شهر را برایشان مثال بزن، آنگاه که پیامبران نزدشان رفتند.');
INSERT INTO "islamhouse" VALUES(36,14,'36:14','وقتی دو نفر از پیامبران را به سویشان فرستادیم؛ اما [مردم] آنان را تکذیب کردند؛ سپس آن دو را با فرستادن پیامبر سوم، [تأیید و] تقویت کردیم. آنان [به اهالی شهر] می‌گفتند: «ما رسولان [الهی] به سوی شما هستیم».');
INSERT INTO "islamhouse" VALUES(36,15,'36:15','[اهالی شهر در پاسخ] گفتند: «شما جز بشری همانند ما نیستید و [پروردگار] رحمان، چیزی [از وحی] نازل نکرده است. شما جز دروغ نمی‌گویید».');
INSERT INTO "islamhouse" VALUES(36,16,'36:16','[پیامبران] گفتند: «پروردگارمان می‌داند که قطعاً به سوی شما فرستاده شده‌ایم.');
INSERT INTO "islamhouse" VALUES(36,17,'36:17','و ما وظیفه‌ای جز رساندن آشکار [وحی] نداریم».');
INSERT INTO "islamhouse" VALUES(36,18,'36:18','[مردم] گفتند: «ما شما را به شگون بد گرفته‌ايم. اگر [از تبلیغ] دست برندارید، حتماً سنگسارتان مى‌كنیم و بی‌تردید، عذابی دردناک از [جانب] ما به شما خواهد رسید».');
INSERT INTO "islamhouse" VALUES(36,19,'36:19','[پیامبران] گفتند: «شومیِ شما با خودتان [و ناشی از کفر و گناه] است. آیا اگر پندتان دهند [آن را شوم می‌پندارید؟] بلکه [حقیقت این است که شما در ارتکاب گناه،] مردمی اسرافکارید».');
INSERT INTO "islamhouse" VALUES(36,20,'36:20','و مردی از دور‌ترین نقطۀ شهر شتابان آمد [و] گفت: «ای قوم من، از این پیامبران پیروی کنید.');
INSERT INTO "islamhouse" VALUES(36,21,'36:21','پیرو کسانی باشید که از شما پاداشی نمی‌خواهند و خود هدایت‌یافته‌اند.');
INSERT INTO "islamhouse" VALUES(36,22,'36:22','و چرا معبودی را عبادت نکنم که مرا آفریده است و [شما نیز] به سویش بازگردانده می‌شوید؟');
INSERT INTO "islamhouse" VALUES(36,23,'36:23','آیا به جاى او معبودانى برگزینم كه اگر [پروردگار] رحمان بخواهد گزندی به من برساند، نه شفاعتشان برایم سودی دارد و نه مى‌توانند مرا [از عذاب الهی] نجات دهند؟');
INSERT INTO "islamhouse" VALUES(36,24,'36:24','در آن صورت، یقیناً در گمراهی آشکاری خواهم بود.');
INSERT INTO "islamhouse" VALUES(36,25,'36:25','من به پروردگارتان ایمان آورده‌ام [و از مرگ هراسی ندارم؛] پس سخنانم را بشنوید.');
INSERT INTO "islamhouse" VALUES(36,26,'36:26','[پس از اینکه آن مرد به دست قومش به شهادت رسید،] به وی گفته شد: «وارد بهشت شو». او گفت: «ای کاش قوم من می‌دانستند');
INSERT INTO "islamhouse" VALUES(36,27,'36:27','که پروردگارم مرا آمرزیده و گرامی داشته است».');
INSERT INTO "islamhouse" VALUES(36,28,'36:28','پس از [شهادت] او، هیچ سپاهى از آسمان بر قومش نفرستادیم و [پیش از این نیز] فروفرستنده نبودیم.');
INSERT INTO "islamhouse" VALUES(36,29,'36:29','[کیفرشان] جز یک بانگ مرگبار نبود؛ به‌ناگاه همگی [مرده و] خاموش گشتند.');
INSERT INTO "islamhouse" VALUES(36,30,'36:30','ای افسوس بر این بندگان! هیچ پیامبری به سویشان نیامد، مگر آنکه او را مسخره می‌کردند.');
INSERT INTO "islamhouse" VALUES(36,31,'36:31','آیا ندیده‌اند [و نمى‌دانند] كه قبل از آنان چه بسیار نسل‌ها را هلاک كرده‌ایم كه دیگر نزد اینها [به دنیا] بازنمى‌گردند؟');
INSERT INTO "islamhouse" VALUES(36,32,'36:32','و [روز قیامت] هیچ کس نیست، مگر آنکه همگی [برای حسابرسی و جزادهی] نزد ما احضار می‌شوند.');
INSERT INTO "islamhouse" VALUES(36,33,'36:33','برای آنان [که قیامت را باور ندارند،] زمین خشک نشانه‌ای [عبرت‌آموز] است: ما آن [زمین] را زنده کردیم و از [خاک] آن دانه‌هایی ‌رویاندیم که [مخلوقات الهی] از آن می‌خورند.');
INSERT INTO "islamhouse" VALUES(36,34,'36:34','و در آن [زمین] باغ‌هایی از نخل و انگور پدید ‌آوردیم و چشمه‌ها در آن جاری ساختیم.');
INSERT INTO "islamhouse" VALUES(36,35,'36:35','تا [مردم] از میوه‌اش بخورند در حالی که این [محصولات، حاصل تلاش آنها نیست و آنها] را با دستان خود به عمل نیاورده‌اند [بلکه به رحمتی از جانب الله از آنها برخوردار شده‌اند]. پس آیا شکر به جای نمی‌آورند؟');
INSERT INTO "islamhouse" VALUES(36,36,'36:36','پاک و منزه است آن [پروردگاری] که همۀ گونه‌ها را آفریده است؛ از آنچه زمین می‌رویانَد [= گیاهان و درختان] و از خودشان [=انسان‌ها] و از آنچه نمی‌دانند.');
INSERT INTO "islamhouse" VALUES(36,37,'36:37','و شب نشانه‌ای [دیگر از قدرت الهی] برایشان است [كه] روز را از آن بركنار مى‌كنیم و ناگهان [همه] در تاریكى فرومى‌روند.');
INSERT INTO "islamhouse" VALUES(36,38,'36:38','و خورشید [نیز پیوسته] به سوی قرار‌گاهش در حرکت است. این تقدیرِ [پروردگارِ] شکست‌ناپذیرِ داناست.');
INSERT INTO "islamhouse" VALUES(36,39,'36:39','و براى ماه [نیز] منزلگاه‌هایی مقرر نموده‌ایم تا [سرانجام از قرص كامل به هلال و] به شکل شاخۀ خشک خرما بازگردد.');
INSERT INTO "islamhouse" VALUES(36,40,'36:40','نه خورشید را سزاوار است که [در سیر خود] به ماه برسد و نه شب بر روز پیشی می‌گیرد؛ و هر یک از [کُرات و سیارات] در مداری شناورند.');
INSERT INTO "islamhouse" VALUES(36,41,'36:41','نشانه‌اى [دیگر از قدرت پروردگار] برای آنان، این است كه پدرانشان را در كشتی انباشته [از سرنشین و بار] حمل کردیم.');
INSERT INTO "islamhouse" VALUES(36,42,'36:42','همچنین، چیزی همانند آن [كشتى] برایشان آفریده‌ایم که بر آن سوار شوند.');
INSERT INTO "islamhouse" VALUES(36,43,'36:43','اگر بخواهیم، غرقشان می‌کنیم؛ در این صورت، نه فریادرسی دارند و نه [از تقدیر الهی] رهایی می‌یابند؛');
INSERT INTO "islamhouse" VALUES(36,44,'36:44','مگر اینکه رحمتی از جانب ما [بینند] و بهره‌مندی [از نعمت‌های دنیا] تا زمانی [معیّن شامل حالشان شود].');
INSERT INTO "islamhouse" VALUES(36,45,'36:45','و چون به آنان [= مشرکان] گفته می‌شود: «از آنچه [عذاب‌ و سختی دنیا و آخرت] پیش رو و پشت سر دارید، پروا كنید؛ باشد که مورد رحمت قرار گیرید»، [هیچ اعتنا نمی‌کنند].');
INSERT INTO "islamhouse" VALUES(36,46,'36:46','هیچ یک از نشانه‌های [قدرت و بزرگی] پروردگارشان‌ بر آنان عرضه نمی‌گردد، مگر اینکه از آن روی می‌گردانند.');
INSERT INTO "islamhouse" VALUES(36,47,'36:47','و چون به آنان گفته می‌شود: «از آنچه الله روزی‌تان کرده است انفاق کنید»، کسانی که کفر ورزیده‌اند به کسانی که ایمان آورده‌اند می‌گویند: «آیا به کسی غذا بدهیم که اگر الله می‌خواست، [خود] به او غذا می‌داد؟ شما جز در گمراهی آشکار نیستید».');
INSERT INTO "islamhouse" VALUES(36,48,'36:48','و [با تمسخر به مؤمنان] می‌گویند: «اگر راستگویید، این وعده [قیامت] کی خواهد بود؟»');
INSERT INTO "islamhouse" VALUES(36,49,'36:49','[این منکران قیامت] جز در انتظار یک بانگ مرگبار نیستند كه ناگهان، در حالی که سرگرم جدال [در امور دنیا] هستند، آنان را فراگیرد.');
INSERT INTO "islamhouse" VALUES(36,50,'36:50','[در آن هنگام] نه می‌توانند [به کسی] وصیت کنند و نه به سوی خانوادۀ خود بازگردند.');
INSERT INTO "islamhouse" VALUES(36,51,'36:51','و [هنگامی که برای دومین بار] در صور دمیده شود و ناگهان از قبرهایشان [بیرون می‌آیند و] به سوی پروردگارشان می‌شتابند.');
INSERT INTO "islamhouse" VALUES(36,52,'36:52','[و با ناباوری] می‌گویند: «وای بر ما! چه کسی ما را از قبرهایمان برانگیخت؟» [به آنان گفته می‌شود:] «این همان [روزی] است که [پروردگار] رحمان وعده داده بود؛ و پیامبران [در این باره] راست می‌گفتند.');
INSERT INTO "islamhouse" VALUES(36,53,'36:53','[برانگیختن مردگان از گور،] جز یک بانگ هولناک [= دومین دمیدنِ صور] نیست و ناگهان همه را نزد ما حاضر می‌کنند.');
INSERT INTO "islamhouse" VALUES(36,54,'36:54','[بدانید که] امروز، به هیچ کس ستمی نمی‌شود و جز در برابرِ آنچه [در دنیا] ‌کرده‌اید، جزا نمی‌بینید.');
INSERT INTO "islamhouse" VALUES(36,55,'36:55','بی‌تردید، اهل بهشت امروز با شادمانی سرگرم [نعمت‌های الهی] هستند.');
INSERT INTO "islamhouse" VALUES(36,56,'36:56','آنان و همسرانشان در سایه‌ها[ی گستردۀ بهشتی] بر تخت‌ها تکیه زده‌اند.');
INSERT INTO "islamhouse" VALUES(36,57,'36:57','در آنجا [هر گونه] میوه و هر چه بخواهند برایشان [فراهم] است.');
INSERT INTO "islamhouse" VALUES(36,58,'36:58','[برتر از این نعمت‌ها] سلامی است که سخن پروردگار مهربان است.');
INSERT INTO "islamhouse" VALUES(36,59,'36:59','[آنگاه به مشرکان خطاب می‌شود:] «ای گناهکاران، امروز [از مؤمنان] جدا شوید.');
INSERT INTO "islamhouse" VALUES(36,60,'36:60','ای فرزندان آدم، آیا با شما پیمان نبستم [و فرمان ندادم] که شیطان را عبادت نکنید [که] قطعاً او دشمن آشکارِ شماست؟');
INSERT INTO "islamhouse" VALUES(36,61,'36:61','و اینكه [تنها] مرا عبادت کنید [كه] راه راست همین است؟');
INSERT INTO "islamhouse" VALUES(36,62,'36:62','و بی‌تردید، شیطان بسیاری از شما را گمراه ساخت. آیا اندیشه نمی‌کردید؟');
INSERT INTO "islamhouse" VALUES(36,63,'36:63','این [همان] دوزخی است که به شما وعده داده می‌شد.');
INSERT INTO "islamhouse" VALUES(36,64,'36:64','امروز به سزای آنکه کفر می‌ورزیدید به آن [آتش] وارد شوید».');
INSERT INTO "islamhouse" VALUES(36,65,'36:65','امروز بر دهان‌هایشان مُهر می‌نهیم و دست‌هایشان با ما سخن می‌گویند و پا‌هایشان به آنچه می‌کردند گواهی می‌دهند.');
INSERT INTO "islamhouse" VALUES(36,66,'36:66','و اگر بخواهیم، بى‌تردید چشمانشان را محو مى‌كنیم، آنگاه [با چشم بسته‌] به سوی [پل] صراط [از یکدیگر] پیشی مى‌گیرند [تا به بهشت راه یابند]؛ اما مگر كجا را مى‌بینند؟');
INSERT INTO "islamhouse" VALUES(36,67,'36:67','و اگر بخواهیم، [ظاهرشان را تغییر می‌دهیم و چنان] آنان را در جای خود مسخ [و زمینگیر] می‌کنیم که نه بتوانند پیش بروند و نه به عقب بازگردند.');
INSERT INTO "islamhouse" VALUES(36,68,'36:68','و ما به هر کس که عمر طولانی بدهیم، در آفرینش دگرگونش می‌کنیم [و او را به ناتوانی ذهنی و بدنیِ دوران کودکی برمی‌گردانیم]. پس آیا [دربارۀ قدرت پروردگار] نمی‌اندیشند؟');
INSERT INTO "islamhouse" VALUES(36,69,'36:69','و ما به او [= محمد] شعر نیاموخته‌ایم و [شاعری،] شایستۀ او نیست. این [که آموخته‌ایم، چیزی] نیست مگر اندرز و قرآنی روشن.');
INSERT INTO "islamhouse" VALUES(36,70,'36:70','تا هر کس را که زنده[‌دل] باشد، بیم دهد و فرمان [عذاب الهی] بر کافران محقَّق گردد.');
INSERT INTO "islamhouse" VALUES(36,71,'36:71','آیا ندیده‌اند که ما از آنچه دست‌هایمان ساخته است، چهارپایانی برایشان آفریده‌ایم که مالک آنها هستند؟');
INSERT INTO "islamhouse" VALUES(36,72,'36:72','و آنها را برایشان رام کردیم تا بر برخی سوار ‌شوند و از [گوشت] برخی تغدیه کنند.');
INSERT INTO "islamhouse" VALUES(36,73,'36:73','و از آن [حیوانات،] بهره‌های دیگر و نوشیدنی‌هایی [= شیر] دارند. پس آیا شکر به جای نمی‌آورند؟');
INSERT INTO "islamhouse" VALUES(36,74,'36:74','[با این همه نعمت، مشرکان] به جای الله معبودانی برگزیده‌اند؛ به این امید که [از سوی آنها] یاری شوند.');
INSERT INTO "islamhouse" VALUES(36,75,'36:75','آن [معبودهای باطل] قادر به یاری‌شان نیستند و این [مشرکان نیز در قیامت] لشکری برای آنها [= معبودهای باطل] خواهند بود که [همگی به آتش دوزخ] فراخوانده می‌شوند.');
INSERT INTO "islamhouse" VALUES(36,76,'36:76','[ای پیامبر،] سخن آنان تو را غمگین نسازد. بی‌تردید، ما آنچه را پنهان می‌کنند و آنچه را آشکار می‌سازند، [به خوبی] می‌دانیم.');
INSERT INTO "islamhouse" VALUES(36,77,'36:77','آیا انسان ندیده است که ما او را از نطفه‌ای آفریده‌ایم؛ [آنگاه مراحل رشد را طی کرد و] سپس دشمنی آشکار شد؟');
INSERT INTO "islamhouse" VALUES(36,78,'36:78','و برای ما مثالی زد و آفرینش خویش را فراموش کرد [و] گفت: «چه کسی این استخوان‌ها را [دوباره] زنده می‌کند در حالی که پوسیده است؟»');
INSERT INTO "islamhouse" VALUES(36,79,'36:79','بگو: «همان ذاتی زنده‌اش می‌کند که نخستین بار آن را پدید آورد و او به هر آفرینشی دانا‌ست.');
INSERT INTO "islamhouse" VALUES(36,80,'36:80','همان ذاتی که از درخت سبز، آتشی برایتان پدید آورد و شما [در صورت نیاز،] خود از آن [درخت] آتش می‌افروزید».');
INSERT INTO "islamhouse" VALUES(36,81,'36:81','آیا ذاتی که آسمان‌ها و زمین را آفریده است، نمی‌تواند همانند اینها را [پس از مرگشان دوباره] بیافریند؟ آری [می‌تواند] و او آفرینندۀ داناست.');
INSERT INTO "islamhouse" VALUES(36,82,'36:82','فرمان او چون چیزی را اراده کند تنها این است که به آن می‌گوید: «موجود شو»؛ پس [بی‌درنگ] موجود می‌شود.');
INSERT INTO "islamhouse" VALUES(36,83,'36:83','پس پاک و منزه است پروردگاری که [حاکمیت و] اختیارِ همه چیز به دست اوست و [همگی] به سوی او بازگردانده می‌شوید.');
INSERT INTO "islamhouse" VALUES(37,1,'37:1','سوگند به فرشتگان صف‌به‌صف [که برای عبادت پروردگار ایستاده‌اند]');
INSERT INTO "islamhouse" VALUES(37,2,'37:2','و سوگند به فرشتگانی که ابرها را می‌رانند');
INSERT INTO "islamhouse" VALUES(37,3,'37:3','و سوگند به فرشتگانی که قرآن تلاوت می‌کنند');
INSERT INTO "islamhouse" VALUES(37,4,'37:4','[که] بی‌تردید، معبود شما یکتاست.');
INSERT INTO "islamhouse" VALUES(37,5,'37:5','[همان] پروردگار آسمان‌ها و زمین و آنچه میان آنهاست و پروردگار [خورشید در غروب‌گاه‌ها و] طلوع‌گاه‌ها.');
INSERT INTO "islamhouse" VALUES(37,6,'37:6','به راستی که ما آسمان دنیا را با زیورِ ستارگان آراستیم');
INSERT INTO "islamhouse" VALUES(37,7,'37:7','و [آن را] از [دسترس] هر شیطان سرکشی حفظ کردیم.');
INSERT INTO "islamhouse" VALUES(37,8,'37:8','[آن شیاطین، هرگز] نمی‌توانند [سخنان فرشتگان] عالم بالا را بشنوند و از هر سو [با شهاب‌های آتشین] تیرباران می‌شوند.');
INSERT INTO "islamhouse" VALUES(37,9,'37:9','[از ورود آنان، به عالم فرشتگان جلوگیری می‌شود و همواره به] عقب رانده می‌شوند و عذابی جاودان [در پیش] دارند؛');
INSERT INTO "islamhouse" VALUES(37,10,'37:10','مگر کسی [از شیاطین] که ناگهان چیزی [از گفتگوی فرشتگان را] برباید که [در این صورت،] شهابی درخشان او را دنبال می‌کند.');
INSERT INTO "islamhouse" VALUES(37,11,'37:11','پس از آنان [= کافران] بپرس که آیا آفرینش [پیکرهای] آنان سخت‌تر است یا آنچه که ما [از زمین و آسمان‌ها و جهان بیکران] آفریده‌ایم؛ [در حالی که] ما آنها از گِلى [بی‌ارزش و] چسبنده پدید آوردیم.');
INSERT INTO "islamhouse" VALUES(37,12,'37:12','تو [از انکار آنان] در شگفتی و آنها [نیز سخنانت را دربارۀ قیامت] مسخره می‌کنند.');
INSERT INTO "islamhouse" VALUES(37,13,'37:13','و چون پند داده ‌شوند، نمی‌پذیرند.');
INSERT INTO "islamhouse" VALUES(37,14,'37:14','و چون نشانه‌ای [در مورد راستگویی پیامبر] می‌بینند، [آن را] به تمسخر می‌گیرند');
INSERT INTO "islamhouse" VALUES(37,15,'37:15','و می‌گویند: «این [قرآن، چیزی] نیست، مگر جادویی آشکار.');
INSERT INTO "islamhouse" VALUES(37,16,'37:16','آیا هنگامی که مردیم و خاک و استخوان شدیم، برانگیخته خواهیم شد؟');
INSERT INTO "islamhouse" VALUES(37,17,'37:17','آیا پدران ما [نیز برانگیخته می‌شوند]؟»');
INSERT INTO "islamhouse" VALUES(37,18,'37:18','بگو: «آری؛ شما در حالی که خوار هستید [زنده می‌شوید]».');
INSERT INTO "islamhouse" VALUES(37,19,'37:19','[وقوع قیامت،] تنها با یک بانگ [سهمناک] است و ناگهان [همه از قبرها بیرون می‌آیند و در انتظار دستور پروردگار، وحشت قیامت را] نظاره می‌کنند.');
INSERT INTO "islamhouse" VALUES(37,20,'37:20','و می‌گویند: «وای بر ما! این [همان] روز جزاست».');
INSERT INTO "islamhouse" VALUES(37,21,'37:21','[گفته می‌شود: «آری،] این همان روز داوری است که آن را انکار می‌کردید».');
INSERT INTO "islamhouse" VALUES(37,22,'37:22','[به فرشتگان گفته می‌شود:] «گرد آورید کسانی را که [با شرک به خویش] ستم کرده‌اند با [همفکران و] همانندهایشان و آنچه عبادت می‌کردند');
INSERT INTO "islamhouse" VALUES(37,23,'37:23','به جاى الله؛ و آنان را به راه دوزخ هدایت کنید.');
INSERT INTO "islamhouse" VALUES(37,24,'37:24','و آنان را نگه دارید که قطعاً [پیش از رفتن به دوزخ] بازخواست خواهند شد».');
INSERT INTO "islamhouse" VALUES(37,25,'37:25','[از دوزخیان می‌پرسند:] «چرا [مانند دنیا] از یكدیگر حمایت نمى‌كنید؟»');
INSERT INTO "islamhouse" VALUES(37,26,'37:26','بلکه امروز [همگی] تسلیم [امرِ پروردگار] هستند.');
INSERT INTO "islamhouse" VALUES(37,27,'37:27','و سرزنش‌کنان به یکدیگر رو می‌کنند');
INSERT INTO "islamhouse" VALUES(37,28,'37:28','[و به رهبران و بزرگان خود] می‌گویند: «شما [برای به کفر کشاندنِ ما، با ادعای دین راستین و] از موضع حق به سراغمان می‌آمدید».');
INSERT INTO "islamhouse" VALUES(37,29,'37:29','[رهبرانشان] می‌گویند: «[ما مقصر نیستیم؛] بلکه شما خود ایمان نداشتید؛');
INSERT INTO "islamhouse" VALUES(37,30,'37:30','و ما هیچ تسلطى بر شما نداشتیم؛ بلکه شما خود گروهى سرکش بودید.');
INSERT INTO "islamhouse" VALUES(37,31,'37:31','[اکنون] فرمان مجازاتِ پروردگارمان بر ما محقق شده و به راستی، ما [همگی عذاب دوزخ را] خواهیم چشید.');
INSERT INTO "islamhouse" VALUES(37,32,'37:32','شما را گمراه نمودیم [چرا که] در حقیقت، خود نیز گمراه بودیم».');
INSERT INTO "islamhouse" VALUES(37,33,'37:33','یقیناً آن روز [همۀ آنان] در عذاب شریکند.');
INSERT INTO "islamhouse" VALUES(37,34,'37:34','بی‌تردید، ما با گناهکاران اینچنین [رفتار] می‌کنیم.');
INSERT INTO "islamhouse" VALUES(37,35,'37:35','آنان بودند كه وقتی به آنان گفته مى‌شد: «معبودی [به‌حق] جز الله نیست» سرکشی [و تکبر] می‌کردند.');
INSERT INTO "islamhouse" VALUES(37,36,'37:36','و می‌گفتند: «آیا ما معبودانمان را به خاطر [سخن] شاعری دیوانه رها کنیم؟»');
INSERT INTO "islamhouse" VALUES(37,37,'37:37','[چنین نیست] بلکه او حق [= توحید] را آورده و پیامبران [پیشین] را تصدیق کرده است.');
INSERT INTO "islamhouse" VALUES(37,38,'37:38','شما [کافران] قطعاً عذاب دردناک [دوزخ] را می‌چشید.');
INSERT INTO "islamhouse" VALUES(37,39,'37:39','و جز [در برابر] آنچه می‌کردید مجازات نمی‌شوید.');
INSERT INTO "islamhouse" VALUES(37,40,'37:40','[همه عذاب می‌بینند؛] مگر بندگان مخلص الله.');
INSERT INTO "islamhouse" VALUES(37,41,'37:41','آنان [در پیشگاهِ پروردگار،] روزیِ مشخصی دارند:');
INSERT INTO "islamhouse" VALUES(37,42,'37:42','[انواع] میوه‌ها [را در اختیار خواهند داشت] و گرامی داشته می‌شوند.');
INSERT INTO "islamhouse" VALUES(37,43,'37:43','در باغ‌های پرنعمت [بهشت]؛');
INSERT INTO "islamhouse" VALUES(37,44,'37:44','در حالی که مقابل یکدیگر بر تخت‌ها [تکیه زده‌اند]؛');
INSERT INTO "islamhouse" VALUES(37,45,'37:45','جامی از شراب ناب [و زلال] پیرامونشان می‌گردانند؛');
INSERT INTO "islamhouse" VALUES(37,46,'37:46','[شرابی روشن و] سپید که برای نوشندگانش لذت‌بخش است؛');
INSERT INTO "islamhouse" VALUES(37,47,'37:47','[شرابی كه خماری و] سردردى در نوشیدنش نیست و از آن مست نمی‌گردند.');
INSERT INTO "islamhouse" VALUES(37,48,'37:48','و نزدشان زنانی زیباچشم است که تنها [به شوهران خود] نظر دارند [و تنها به آنها عشق می‌ورزند].');
INSERT INTO "islamhouse" VALUES(37,49,'37:49','[زنانی که] گویی پردۀ سفید داخلی تخم [پرندگان] هستند که [از دسترس همگان] پنهان مانده‌اند.');
INSERT INTO "islamhouse" VALUES(37,50,'37:50','[بهشتیان دربارۀ رخدادهای دنیا صحبت می‌کنند و] برخی از آنان پرسش‌کنان رو به برخی دیگر می‌کنند.');
INSERT INTO "islamhouse" VALUES(37,51,'37:51','یکی از آنان می‌گوید: «من [در دنیا] همنشینی داشتم.');
INSERT INTO "islamhouse" VALUES(37,52,'37:52','[که پیوسته به من] می‌گفت: آیا تو واقعاً [به قیامت] باور داری؟');
INSERT INTO "islamhouse" VALUES(37,53,'37:53','آیا هنگامی که مُردیم و خاک و استخوان شدیم، آیا حقیقتاً [زنده می‌شویم و در قبال کارهایمان مورد محاسبه قرار می‌گیریم و] به ما کیفر می‌دهند؟!»');
INSERT INTO "islamhouse" VALUES(37,54,'37:54','[سپس به دوستان بهشتی‌اش] می‌گوید: «آیا [شما نیز همراه من] سر می‌کشید [تا بدانیم او کجاست]؟»');
INSERT INTO "islamhouse" VALUES(37,55,'37:55','آنگاه خود سر می‌کشد و او را در میان دوزخ مى‌بیند.');
INSERT INTO "islamhouse" VALUES(37,56,'37:56','به وی می‌گوید: «به الله سوگند که نزدیک بود مرا [با وسوسه‌های کفرآمیزت] نابود سازی.');
INSERT INTO "islamhouse" VALUES(37,57,'37:57','اگر نعمتِ [هدایتِ] پروردگارم نبود، من نیز از حاضرشدگان [در دوزخ] بودم».');
INSERT INTO "islamhouse" VALUES(37,58,'37:58','[آنگاه به بهشتیان می‌گوید:] «آیا ما مرگ دیگری نخواهیم داشت');
INSERT INTO "islamhouse" VALUES(37,59,'37:59','جز مرگ نخستین، و عذاب نمی‌شویم؟');
INSERT INTO "islamhouse" VALUES(37,60,'37:60','به راستی که این همان کامیابی بزرگ است».');
INSERT INTO "islamhouse" VALUES(37,61,'37:61','اهل عمل، باید براى چنین [پاداشی] بکوشند.');
INSERT INTO "islamhouse" VALUES(37,62,'37:62','آیا این [نعمت بهشت، کریمانه‌تر و] براى پذیرایى بهتر است یا درختِ زَقّوم [در دوزخ]؟');
INSERT INTO "islamhouse" VALUES(37,63,'37:63','بی‌تردید، ما آن درخت را مایۀ آزمایش ستمکاران [مشرک] قرار داده‌ایم [چرا که می‌گویند: «هیچ درختی در آتش نمی‌روید»].');
INSERT INTO "islamhouse" VALUES(37,64,'37:64','در حقیقت، آن [مایۀ عذاب،] درختی است که از ژرفای دوزخ می‌روید.');
INSERT INTO "islamhouse" VALUES(37,65,'37:65','میوه‌اش [چنان بدمنظر است که] گویى سرهاى شیاطین است.');
INSERT INTO "islamhouse" VALUES(37,66,'37:66','آنان [= دوزخیان] از [میوه‌های تلخ] آن مى‌خورند و شکم‌ها را از آنها انباشته مى‌كنند.');
INSERT INTO "islamhouse" VALUES(37,67,'37:67','آنگاه بر روی این [غذای ناپاک، به جای نوشیدنی] آمیزه‌ای از آب جوشان دارند.');
INSERT INTO "islamhouse" VALUES(37,68,'37:68','سپس یقیناً باز‌گشتشان به دوزخ است.');
INSERT INTO "islamhouse" VALUES(37,69,'37:69','آنان پدران خود را گمراه یافتند؛');
INSERT INTO "islamhouse" VALUES(37,70,'37:70','و [همچنان] در پیِ آنان می‌شتافتند.');
INSERT INTO "islamhouse" VALUES(37,71,'37:71','و قطعاً قبل از آنان نیز بیشتر پیشینیان گمراه شدند.');
INSERT INTO "islamhouse" VALUES(37,72,'37:72','و بی‌تردید، ما هشدار‌دهندگان [بسیاری] میان آنان فرستادیم.');
INSERT INTO "islamhouse" VALUES(37,73,'37:73','پس بنگر که سرانجامِ هشداریافتگان چگونه بود.');
INSERT INTO "islamhouse" VALUES(37,74,'37:74','[همه گرفتار عذاب شدند] مگر بندگان مخلص الله.');
INSERT INTO "islamhouse" VALUES(37,75,'37:75','نوح به درگاه ما دعا کرد و [ما] چه نیکو اجابت‌کننده بودیم!');
INSERT INTO "islamhouse" VALUES(37,76,'37:76','و او و خانواده‌اش را [به همراه مؤمنان] از بلای بزرگ [طوفان] نجات دادیم');
INSERT INTO "islamhouse" VALUES(37,77,'37:77','و [پس از طوفان، فقط پیروان مؤمن و] خانوادۀ او را [در زمین] باقی گذاشتیم');
INSERT INTO "islamhouse" VALUES(37,78,'37:78','و در میان آیندگان [نام نیک] برایش به جا نهادیم.');
INSERT INTO "islamhouse" VALUES(37,79,'37:79','سلام [و ستایش] جهانیان بر نوح باد.');
INSERT INTO "islamhouse" VALUES(37,80,'37:80','به راستی، ما نیکوکاران را اینچنین پاداش می‌دهیم.');
INSERT INTO "islamhouse" VALUES(37,81,'37:81','بی‌تردید، او از بندگان مؤمن ما بود.');
INSERT INTO "islamhouse" VALUES(37,82,'37:82','آنگاه دیگران را [که در طوفان مانده بودند] غرق کردیم.');
INSERT INTO "islamhouse" VALUES(37,83,'37:83','و قطعاً ابراهیم از پیروان او [= نوح] بود.');
INSERT INTO "islamhouse" VALUES(37,84,'37:84','[یاد کن از] هنگامی که وی با دلی پاک [و خالی از شرک] به [درگاه] پروردگارش آمد.');
INSERT INTO "islamhouse" VALUES(37,85,'37:85','آنگاه که به پدر و قومش گفت: «چه چیزی عبادت می‌کنید؟');
INSERT INTO "islamhouse" VALUES(37,86,'37:86','آیا به جای الله، این معبودان دروغین را می‌خواهید؟');
INSERT INTO "islamhouse" VALUES(37,87,'37:87','گمانتان دربارۀ پروردگار جهانیان چیست؟»');
INSERT INTO "islamhouse" VALUES(37,88,'37:88','سپس نگاهی به ستارگان افکند [و اندیشید].');
INSERT INTO "islamhouse" VALUES(37,89,'37:89','و گفت: «من بیمارم [و در جشن شرکت نمی‌کنم]».');
INSERT INTO "islamhouse" VALUES(37,90,'37:90','پس [مردم] او را ترک کردند و رفتند.');
INSERT INTO "islamhouse" VALUES(37,91,'37:91','آنگاه پنهانی نزد معبودهایشان رفت و [با تمسخر] گفت: «چیزی نمی‌خورید؟');
INSERT INTO "islamhouse" VALUES(37,92,'37:92','شما را چه شده است که سخن نمی‌گویید؟»');
INSERT INTO "islamhouse" VALUES(37,93,'37:93','سپس با دست راست خود ضربه‌ای [محکم] بر آنها وارد آورد [و جز بُت‌ بزرگ، همه را شکست].');
INSERT INTO "islamhouse" VALUES(37,94,'37:94','قومش شتابان به سوی او آمدند.');
INSERT INTO "islamhouse" VALUES(37,95,'37:95','[ابراهیم] گفت: «آیا چیزی را که خود می‌تراشید عبادت می‌کنید؟');
INSERT INTO "islamhouse" VALUES(37,96,'37:96','و حال آنکه الله، [هم] شما و [هم] آنچه را انجام می‌دهید آفریده است».');
INSERT INTO "islamhouse" VALUES(37,97,'37:97','[وقتی بُت‌پرستان از پاسخ درماندند،] گفتند: «بنایی [بلند] برایش بسازید و او را در آتش بیفکنید».');
INSERT INTO "islamhouse" VALUES(37,98,'37:98','براى [نابودى‌] او نیرنگی خواستند و[لى] ما آنان را پست و [مغلوب‌] ساختیم [و آتش بر ابراهیم سرد و بی‌اثر شد].');
INSERT INTO "islamhouse" VALUES(37,99,'37:99','و گفت: «من به سوى [سرزمین دیگرى براى عبادتِ] پروردگارم می‌روم [و هجرت می‌کنم] و او مرا هدایت خواهد كرد.');
INSERT INTO "islamhouse" VALUES(37,100,'37:100','پروردگارا، [فرزندی] از شایستگان به من عطا کن».');
INSERT INTO "islamhouse" VALUES(37,101,'37:101','ما [دعایش را مستجاب کردیم و] او را به تولد پسری بردبار بشارت دادیم.');
INSERT INTO "islamhouse" VALUES(37,102,'37:102','و چون [اسماعیل] به [سن نوجوانى و] حد تلاش [و همكارى] با او [= ابراهیم] رسید، به وی گفت: پسرم، در خواب [به من وحی می‌شود و] مى‌بینم كه تو را ذبح مى‌كنم. نظرت در این باره چیست؟» [اسماعیل] گفت: «پدرجان، آنچه را به تو امر شده است انجام بده. ان شاء الله مرا [در برابر اجرای این فرمان،] شكیبا خواهی یافت».');
INSERT INTO "islamhouse" VALUES(37,103,'37:103','هنگامی که هر دو تسلیم [فرمان پروردگار] شدند و [ابراهیم] او را به پیشانی [بر خاک] نهاد.');
INSERT INTO "islamhouse" VALUES(37,104,'37:104','به او ندا دادیم که: «ای ابراهیم،');
INSERT INTO "islamhouse" VALUES(37,105,'37:105','یقیناً که خوابت را تحقق بخشیدی». ما نیکوکاران را اینچنین پاداش می‌دهیم.');
INSERT INTO "islamhouse" VALUES(37,106,'37:106','بی‌تردید، این [خواب،] آزمایشی آشکار بود [و ابراهیم از آن سربلند بیرون آمد].');
INSERT INTO "islamhouse" VALUES(37,107,'37:107','و [جان] او را در ازای قربانی بزرگی بازخریدیم');
INSERT INTO "islamhouse" VALUES(37,108,'37:108','و در میان آیندگان، [نام نیک] برایش بر جای نهادیم.');
INSERT INTO "islamhouse" VALUES(37,109,'37:109','سلام بر ابراهیم.');
INSERT INTO "islamhouse" VALUES(37,110,'37:110','ما نیکوکاران را اینچنین پاداش می‌دهیم.');
INSERT INTO "islamhouse" VALUES(37,111,'37:111','بی‌تردید، او از بندگان مؤمن ما بود.');
INSERT INTO "islamhouse" VALUES(37,112,'37:112','و او را به [تولد] اسحاق که پیامبری از شایستگان بود بشارت دادیم.');
INSERT INTO "islamhouse" VALUES(37,113,'37:113','[ما] به او و اسحاق برکت دادیم؛ و [برخى] از افرادِ دودمانشان نیكوكار بودند و [برخى دیگر] آشكارا نسبت به خویشتن ستمکار بودند.');
INSERT INTO "islamhouse" VALUES(37,114,'37:114','و به راستی، بر موسی و هارون منت نهادیم');
INSERT INTO "islamhouse" VALUES(37,115,'37:115','و آن دو را همراه قومشان از اندوه بزرگ [= ستم فرعون و خطر غرق شدن در نیل] نجات دادیم');
INSERT INTO "islamhouse" VALUES(37,116,'37:116','و یاری‌شان کردیم تا پیروز شدند');
INSERT INTO "islamhouse" VALUES(37,117,'37:117','و به آن دو، کتاب روشنگر [= تورات] دادیم');
INSERT INTO "islamhouse" VALUES(37,118,'37:118','و آن دو را به راه راست هدایت کردیم');
INSERT INTO "islamhouse" VALUES(37,119,'37:119','و در میان آیندگان [نام نیک] برایشان بر جای نهادیم.');
INSERT INTO "islamhouse" VALUES(37,120,'37:120','سلام بر موسی و هارون.');
INSERT INTO "islamhouse" VALUES(37,121,'37:121','ما نیکوکاران را اینچنین پاداش می‌دهیم.');
INSERT INTO "islamhouse" VALUES(37,122,'37:122','بی‌تردید، آن دو از بندگان مؤمن ما بودند.');
INSERT INTO "islamhouse" VALUES(37,123,'37:123','و قطعاً الیاس [نیز] یکی از پیامبران [ما] بود.');
INSERT INTO "islamhouse" VALUES(37,124,'37:124','[یاد کن از] هنگامی که او به قومش گفت: «آیا [از الله] پروا نمی‌کنید؟');
INSERT INTO "islamhouse" VALUES(37,125,'37:125','آیا [نماد یا همان بتِ] «بَعل» را [به عبادت] می‌خوانید و بهترین آفریننده را رها می‌کنید؟');
INSERT INTO "islamhouse" VALUES(37,126,'37:126','الله [را ترک می‌کنید] که پروردگار شما و پروردگار پدرانتان است».');
INSERT INTO "islamhouse" VALUES(37,127,'37:127','اما او را دروغگو انگاشتند و مسلّماً [برای عذاب] احضار می‌شوند.');
INSERT INTO "islamhouse" VALUES(37,128,'37:128','مگر بندگان مخلص الله.');
INSERT INTO "islamhouse" VALUES(37,129,'37:129','ما در میان آیندگان [نام نیک] برایش بر جای نهادیم.');
INSERT INTO "islamhouse" VALUES(37,130,'37:130','سلام بر الیاس.');
INSERT INTO "islamhouse" VALUES(37,131,'37:131','به راستی، ما نیکوکاران را اینچنین پاداش می‌دهیم.');
INSERT INTO "islamhouse" VALUES(37,132,'37:132','بی‌تردید، او از بندگان مؤمن ما بود.');
INSERT INTO "islamhouse" VALUES(37,133,'37:133','و قطعاً لوط [نیز] یکی از پیامبران [ما] بود.');
INSERT INTO "islamhouse" VALUES(37,134,'37:134','[یاد کن از] هنگامی که او و همۀ خانواده‌اش را نجات دادیم.');
INSERT INTO "islamhouse" VALUES(37,135,'37:135','مگر پیرزنی که از باقی‌ماندگان [در عذاب] بود.');
INSERT INTO "islamhouse" VALUES(37,136,'37:136','سپس دیگران را هلاک کردیم.');
INSERT INTO "islamhouse" VALUES(37,137,'37:137','و شما [در سفر به سرزمین شام] صبحگاهان بر [ویرانه‌های شهرهای] آنان می‌گذرید.');
INSERT INTO "islamhouse" VALUES(37,138,'37:138','آیا نمی‌اندیشید [و عبرت نمی‌گیرید]؟');
INSERT INTO "islamhouse" VALUES(37,139,'37:139','و قطعاً یونس [نیز] یکی از پیامبران [ما] بود.');
INSERT INTO "islamhouse" VALUES(37,140,'37:140','[یاد کن از] هنگامی که [خشمگین از کفر قومش، بدون اجازۀ پروردگار] به کشتیِ انباشته [از مسافر و بار] گریخت.');
INSERT INTO "islamhouse" VALUES(37,141,'37:141','[چون کشتی در حال غرق شدن بود، یونس با سرنشینان کشتی برای بیرون انداختنِ برخی افراد] قرعه انداخت و خود از باختگان شد [و او را به دریا انداختند].');
INSERT INTO "islamhouse" VALUES(37,142,'37:142','سپس ماهی [بزرگی] او را بلعید؛ در حالى كه [خود را به خاطر ترک وظیفه‌] سرزنش مى‌كرد.');
INSERT INTO "islamhouse" VALUES(37,143,'37:143','اگر در زمرۀ تسبیح‌گویان [پروردگار] نبود،');
INSERT INTO "islamhouse" VALUES(37,144,'37:144','یقیناً تا روزی که [مردم] برانگیخته می‌شوند، در شکم آن [ماهی] باقی می‌ماند.');
INSERT INTO "islamhouse" VALUES(37,145,'37:145','آنگاه او را در حالی که [ضعیف و] بیمار بود به سرزمینی خشک [و ‌بی‌گیاه] افکندیم');
INSERT INTO "islamhouse" VALUES(37,146,'37:146','و بوتۀ کدو بر [سرِ] او رویاندیم [تا از سایه و میوه‌اش بهره گیرد]؛');
INSERT INTO "islamhouse" VALUES(37,147,'37:147','و [دوباره] او را به سوی صد هزار نفر یا بیشتر [به رسالت] فرستادیم.');
INSERT INTO "islamhouse" VALUES(37,148,'37:148','آنان ایمان آوردند و ما نیز آنان را تا مدتی [از نعمت‌های زندگی] بهره‌مند ساختیم.');
INSERT INTO "islamhouse" VALUES(37,149,'37:149','از آنان [= مشرکان] بپرس آیا دخترها برای پروردگارِ تو و پسرها برای آنهاست؟');
INSERT INTO "islamhouse" VALUES(37,150,'37:150','آیا ما فرشتگان را دختر آفریدیم و آنان حضور داشتند؟');
INSERT INTO "islamhouse" VALUES(37,151,'37:151','آگاه باش! آنان از [روی تهمت و] دروغگویی‌ می‌گویند:');
INSERT INTO "islamhouse" VALUES(37,152,'37:152','«الله فرزند دارد» و یقیناً دروغ می‌گویند.');
INSERT INTO "islamhouse" VALUES(37,153,'37:153','آیا [الله] دختران را بر پسران ترجیح داده است؟');
INSERT INTO "islamhouse" VALUES(37,154,'37:154','شما را چه شده است؟ چگونه حکم می‌کنید؟');
INSERT INTO "islamhouse" VALUES(37,155,'37:155','آیا [نمی‌اندیشید و] پند نمی‌گیرید؟');
INSERT INTO "islamhouse" VALUES(37,156,'37:156','آیا [بر ادعای باطل خویش] دلیل روشنی دارید؟');
INSERT INTO "islamhouse" VALUES(37,157,'37:157','اگر راستگویید، کتابتان را بیاورید.');
INSERT INTO "islamhouse" VALUES(37,158,'37:158','[مشرکان] بین او [= الله] و فرشتگان، خویشاوندى قائل شده‌اند؛ در حالى كه فرشتگان به یقین می‌دانند كه [مشرکان روز قیامت، براى حسابرسی] احضار می‌شوند.');
INSERT INTO "islamhouse" VALUES(37,159,'37:159','الله از آنچه [مشرکان] توصیف می‌کنند منزّه است.');
INSERT INTO "islamhouse" VALUES(37,160,'37:160','[مردم در توصیف پروردگار، سخن گزاف مى‌گویند؛] مگر بندگان مخلص الله.');
INSERT INTO "islamhouse" VALUES(37,161,'37:161','شما [مشرکان] و آنچه عبادت می‌کنید');
INSERT INTO "islamhouse" VALUES(37,162,'37:162','[هرگز] گمراهگر [کسی از دین حق] نیستید؛');
INSERT INTO "islamhouse" VALUES(37,163,'37:163','مگر کسی که [به تقدیرِ الهی،] اهل دوزخ باشد.');
INSERT INTO "islamhouse" VALUES(37,164,'37:164','و [فرشتگان می‌گویند:] «هیچ‌یک از ما نیست، مگر آنکه مقام مشخصی [در پیشگاه الهی] دارد.');
INSERT INTO "islamhouse" VALUES(37,165,'37:165','ماییم که [برای عبادت و نیایش به درگاه پروردگار] به صف ایستاده‌ایم');
INSERT INTO "islamhouse" VALUES(37,166,'37:166','و بی‌تردید، ماییم که تسبیح‌گوی [او] هستیم».');
INSERT INTO "islamhouse" VALUES(37,167,'37:167','و [کافران، پیش از بعثت] همواره می‌گفتند:');
INSERT INTO "islamhouse" VALUES(37,168,'37:168','«اگر کتابی از [کتاب‌های آسمانیِ] پیشینیان نزدمان بود،');
INSERT INTO "islamhouse" VALUES(37,169,'37:169','یقیناً از بندگان مخلصِ الله می‌شدیم».');
INSERT INTO "islamhouse" VALUES(37,170,'37:170','اما [هنگامی که پیامبر با قرآن به سویشان آمد، حقانیتِ] آن را انکار کردند؛ به زودی [نتیجۀ انکارشان را] خواهند دانست.');
INSERT INTO "islamhouse" VALUES(37,171,'37:171','و قطعاً سخن ما دربارۀ بندگان فرستاده‌شده‌مان از پیش صادر شده است [که]:');
INSERT INTO "islamhouse" VALUES(37,172,'37:172','آنان قطعاً [از جانب ما] یاری‌شده‌اند.');
INSERT INTO "islamhouse" VALUES(37,173,'37:173','و بی‌تردید، لشکر ما پیروزند.');
INSERT INTO "islamhouse" VALUES(37,174,'37:174','پس [ای پیامبر،] تا مدتی از آنان [= مشرکان] روی بگردان.');
INSERT INTO "islamhouse" VALUES(37,175,'37:175','و آنان را [در هنگام نزول عذاب] بنگر؛ به زودی خود [نیز نتیجۀ کفرشان را] خواهند دید.');
INSERT INTO "islamhouse" VALUES(37,176,'37:176','آیا عذاب ما را به شتاب خواستارند؟');
INSERT INTO "islamhouse" VALUES(37,177,'37:177','آنگاه كه [عذاب الهی] بر خانه‌هایشان فرود آید، چه بد است بامداد هشداریافتگان!');
INSERT INTO "islamhouse" VALUES(37,178,'37:178','و تا مدتی از آنان روی بگردان،');
INSERT INTO "islamhouse" VALUES(37,179,'37:179','و آنان را [در هنگام نزول عذاب] بنگر؛ به زودی خود [نیز نتیجۀ کفرشان را] خواهند دید.');
INSERT INTO "islamhouse" VALUES(37,180,'37:180','پروردگارت ـ پروردگار عزت و قدرت ـ از آنچه [مشرکان به دروغ] توصیف می‌کنند پاک و منزه است.');
INSERT INTO "islamhouse" VALUES(37,181,'37:181','و سلام بر پیامبران');
INSERT INTO "islamhouse" VALUES(37,182,'37:182','و سپاس [و ستایش] از آنِ الله ـ پروردگار جهانیان ـ است.');
INSERT INTO "islamhouse" VALUES(38,1,'38:1','ص [= صاد]. سوگند به قرآن پندآموز [که الله هیچ شریکی ندارد]');
INSERT INTO "islamhouse" VALUES(38,2,'38:2','بلکه کسانی که کفر ورزیدند در سرکشی و ستیزند.');
INSERT INTO "islamhouse" VALUES(38,3,'38:3','چه بسیار نسل‌ها پیش از آنان نابود کردیم که [در هنگام نزول عذاب] فریاد [توبه] برمی‌آوردند؛ و[لی آن زمان، دیگر] وقت [توبه و] رهایی نبود.');
INSERT INTO "islamhouse" VALUES(38,4,'38:4','و [مشرکان] از اینکه بیم‌دهنده‌اى از میان خودشان به سراغشان آمده است تعجب کردند و گفتند: «این مرد، جادوگرى دروغگوست.');
INSERT INTO "islamhouse" VALUES(38,5,'38:5','آیا معبودان [مختلف و متعدد] را [تبدیل به] معبودی یگانه کرده است؟ این واقعاً چیز عجیبی است».');
INSERT INTO "islamhouse" VALUES(38,6,'38:6','بزرگانشان [= سران قریش] به پا خاستند [و به پیروان خود گفتند]: «بروید و بر [عبادت و حفظ] معبودانتان شکیبا [و پایدار] باشید؛ زیرا این [دعوت به عبادت معبودی یگانه] چیزی است که [برنامه‌ریزی و] خواسته شده است [تا محمد بر شما برتری یابد].');
INSERT INTO "islamhouse" VALUES(38,7,'38:7','ما در آیین اخیر [= نصرانیت] نیز چنین [ادعایی دربارۀ توحید] نشنیده‌ایم. این جز دروغ [چیزی] نیست.');
INSERT INTO "islamhouse" VALUES(38,8,'38:8','آیا از میان [همۀ] ما، قرآن بر او نازل شده است؟» [چنین نیست که می‌گویند؛] بلکه آنان دربارۀ وحی من [بر تو] تردید دارند؛ بلکه هنوز عذاب [الهی] را نچشیده‌اند [که چنین گستاخند].');
INSERT INTO "islamhouse" VALUES(38,9,'38:9','آیا گنجینه‌های رحمت پروردگار شکست‌ناپذیر بخشنده‌ات نزدشان است؟');
INSERT INTO "islamhouse" VALUES(38,10,'38:10','یا فرمانروایی آسمان‌ها و زمین و آنچه بین آنهاست از آنِ آنان است؟ [اگر چنین است،] باید با وسایل [و امكانات خود به آسمان] اوج بگیرند [و مانع یا باعث نزول نعمت‌ها گردند].');
INSERT INTO "islamhouse" VALUES(38,11,'38:11','[این کافران] لشکری شکست‌خورده از جمله گروه‌ها[و فرقه‌های مخالف پیامبران] هستند.');
INSERT INTO "islamhouse" VALUES(38,12,'38:12','[پیش از آنان نیز] قوم نوح و عاد و فرعونِ صاحب‌سپاه [نیز پیامبران ما را] دروغگو انگاشتند.');
INSERT INTO "islamhouse" VALUES(38,13,'38:13','و قوم ثمود و قوم لوط و اصحاب اَیکه [= قوم شعیب] نیز [از] آن گروه‌ها بودند [که به تکذیب پیامبران پرداختند].');
INSERT INTO "islamhouse" VALUES(38,14,'38:14','هر یک [از این کافران] پیامبران را دروغگو انگاشتند و كیفر من [در موردشان] تحقق یافت.');
INSERT INTO "islamhouse" VALUES(38,15,'38:15','اینان [= مشرکان] جز یک بانگ [سهمگین] را انتظار نمی‌کشند؛ [بانگی] که هیچ بازگشتی ندارد.');
INSERT INTO "islamhouse" VALUES(38,16,'38:16','و [با تمسخر] ‌گفتند: «پروردگارا، پیش از فرارسیدن روز حساب، بهرۀ ما را [از عذاب] به شتاب بده».');
INSERT INTO "islamhouse" VALUES(38,17,'38:17','[ای پیامبر،] در برابر گفتارشان شكیبا باش و از بندۀ ما ـ داود توانمند ـ یاد کن [که] بی‌تردید، بسیار توبه‌کار بود.');
INSERT INTO "islamhouse" VALUES(38,18,'38:18','به راستی، ما کوه‌ها را همراهش به خدمت گماشتیم که [هم‌نوا با او] شامگاهان و بامدادان [پروردگار را] به پاکی می‌ستودند.');
INSERT INTO "islamhouse" VALUES(38,19,'38:19','و پرندگان را نیز گِردآمده [در آسمان، به خدمت گماشتیم و] همگی به پیروی از وی تسبیح‌گوی بودند.');
INSERT INTO "islamhouse" VALUES(38,20,'38:20','و فرمانروایی‌اش را استوار نمودیم و پیامبری و کلامی فیصله‌بخش به او عطا کردیم.');
INSERT INTO "islamhouse" VALUES(38,21,'38:21','و آیا خبر آن شاکیان به تو رسیده است، آنگاه که [برای دادخواهی نزد داود] از دیوار عبادتگاه [او] بالا رفتند؟');
INSERT INTO "islamhouse" VALUES(38,22,'38:22','چون [بدون اطلاع] بر داود وارد شده بودند، از آنان ترسید؛ گفتند: «نترس؛ ما دو نفر شاکی هستیم که یکی بر دیگری ستم کرده است؛ به عدالت در میان ما داوری کن و ستم روا مدار و ما را به راه راست هدایت کن».');
INSERT INTO "islamhouse" VALUES(38,23,'38:23','[یکی از آن دو گفت:] «این [مرد،] برادرم است. او نود و نُه میش دارد و من یک میش دارم و [به اصرار] می‌گوید: "آن [یک میش] را هم به من واگذار"؛ و در بحث بر من پیروز شده است».');
INSERT INTO "islamhouse" VALUES(38,24,'38:24','[داود] گفت: «بی‌تردید، او با خواستنِ میش تو [براى افزودن] به میش‌های خودش، بر تو ستم كرده است؛ و البته بسیاری از شریکان [و همكاران] در حق یكدیگر ستم مى‌كنند، مگر کسانی که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند كه آنها [نیز بسیار] اندکند»؛ و داود [كه بدون توضیح خواستن از طرف دیگر، حكم صادر كرده بود] دریافت كه قطعاً او را امتحان كرده‌ایم؛ بنابر این، از پروردگار خویش آمرزش خواست و سجده‌کنان بر زمین افتاد و [به درگاه الهی] توبه کرد.');
INSERT INTO "islamhouse" VALUES(38,25,'38:25','آن [اشتباه] را بر او بخشیدیم؛ و به راستی، او نزد ما مقامی والا و [در آخرت] بازگشتی نیکو دارد.');
INSERT INTO "islamhouse" VALUES(38,26,'38:26','ای داود، ما تو را جانشین [خود] در زمین قرار دادیم؛ پس به حق میان مردم داوری کن و پیرو هوای [نفس] نباش که تو را از راه الله گمراه می‌کند. بی‌تردید، کسانی که از راه الله گمراه می‌شوند، به سزای آنکه روز حساب را فراموش کرده‌اند، عذاب شدیدی [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(38,27,'38:27','ما آسمان و زمین و آنچه را که میان آنهاست، بیهوده نیافریده‌ایم. این، گمان کسانی است که کفر ورزیده‌اند. پس وای بر کسانی که کفر ورزیدند از آتش [دوزخ]!');
INSERT INTO "islamhouse" VALUES(38,28,'38:28','آیا کسانی را که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند مانند مفسدان در زمین به حساب می‌آوریم؟ آیا پرهیزگاران را مانند بدکاران به حساب می‌آوریم؟');
INSERT INTO "islamhouse" VALUES(38,29,'38:29','[ای محمد، این قرآن] کتابی است پربرکت که بر تو نازل کردیم تا [مردم] در آیاتش به دقت بیندیشند و خردمندان پند گیرند.');
INSERT INTO "islamhouse" VALUES(38,30,'38:30','و سلیمان را به داود عطا کردیم؛ نیکو بنده‌ای بود [و] بی‌تردید، بسیار توبه‌کار بود.');
INSERT INTO "islamhouse" VALUES(38,31,'38:31','[یاد کن از] هنگامی که عصرگاهان، اسب‌های [اصیل و] تیزرو را بر او عرضه کردند [و این اسب‌ها پیوسته تا غروب خورشید بر او عرضه می‌شد].');
INSERT INTO "islamhouse" VALUES(38,32,'38:32','[سلیمان سرگرم تماشای اسب‌ها شد و وقت عبادت گذشت؛] گفت: «به راستی که دوست‌داشتن مال [و تماشای اسب‌ها] مرا از یاد پروردگارم غافل کرد تا [آنکه خورشید] در پرده [مغرب] پنهان گشت.');
INSERT INTO "islamhouse" VALUES(38,33,'38:33','[بار دیگر] آنها را نزدم بازگردانید». آنگاه [به نشانۀ وقف در راهِ الله] به ساق‌ها و گردن‌ها[ی آن اسب‌ها] دست کشید.');
INSERT INTO "islamhouse" VALUES(38,34,'38:34','و بی‌تردید، سلیمان را آزمودیم و جسد [نوزاد]ى را [كه ـ بدون گفتن ان‌شاءالله- مشتاقانه در انتظار تولدش بود، مُرده] بر تختش افكندیم [تا دریابد که ادارۀ امور، به دست الله است]؛ سپس [توبه کرد و به درگاه الهی] بازگشت.');
INSERT INTO "islamhouse" VALUES(38,35,'38:35','گفت: «پروردگارا، مرا بیامرز و حكومتى به من ارزانى دار كه پس از من بى‌نظیر باشد [كه] بی‌تردید، تو بسیار بخشاینده‌ای».');
INSERT INTO "islamhouse" VALUES(38,36,'38:36','[ما] باد را در خدمتش گماشتیم که هر کجا اراده می‌کرد به فرمانش به آرامى روانه مى‌شد');
INSERT INTO "islamhouse" VALUES(38,37,'38:37','و هر بنّا و غواصی از جنیان را [که می‌خواست، به فرمانش درآوردیم]');
INSERT INTO "islamhouse" VALUES(38,38,'38:38','و گروه دیگری [از جنیان] را [که همگی] در غل و زنجیر بودند [تحت سلطه‌اش قرار دادیم].');
INSERT INTO "islamhouse" VALUES(38,39,'38:39','[به او گفتیم:] «این عطای ماست؛ بی‌شمار [به هر که می‌خواهی] ببخش یا [از هر کس كه می‌خواهى] بازدار».');
INSERT INTO "islamhouse" VALUES(38,40,'38:40','و به راستی، او نزد ما مقامی والا و [در آخرت] بازگشتی نیکو دارد.');
INSERT INTO "islamhouse" VALUES(38,41,'38:41','[ای پیامبر،] از بندۀ ما ـ ایوب ـ یاد کن؛ آنگاه که پروردگارش را ندا داد که: «شیطان مرا به رنج و درد آورده است».');
INSERT INTO "islamhouse" VALUES(38,42,'38:42','[به او فرمان دادیم:] «پایت را بر زمین بکوب [تا چشمه‌ای بجوشد]. این، چشمه‌ساری خنک برای شستشو و آشامیدن است».');
INSERT INTO "islamhouse" VALUES(38,43,'38:43','و خانواده‌اش و مانند آنها [= نوادگان] را همراه آنان به وی بخشیدیم تا [نشانۀ] رحمتى از جانب ما و اندرزی براى خردمندان باشد.');
INSERT INTO "islamhouse" VALUES(38,44,'38:44','و [به او فرمان دادیم:] «دسته‌ای گیاه برگیر و [همسرت را] با آن بزن و سوگندت را [که در هنگام خشم یاد کرده بودی] نشکن. به راستی، ما او را شکیبا یافتیم. نیکو بنده‌ای بود [و] بی‌تردید، بسیار توبه‌کار بود.');
INSERT INTO "islamhouse" VALUES(38,45,'38:45','و [ای پیامبر،] از بندگان ما ابراهیم و اسحاق و یعقوب یاد کن که [در عبادت] توانمند و [در شناخت حق] بابصیرت بودند.');
INSERT INTO "islamhouse" VALUES(38,46,'38:46','ما آنان را با خصلتی [ممتاز] ویژه ساختیم که [همانا] یاد آخرت بود.');
INSERT INTO "islamhouse" VALUES(38,47,'38:47','و آنان نزد ما از برگزیدگان و نیکانند.');
INSERT INTO "islamhouse" VALUES(38,48,'38:48','و از اسماعیل و یَسَع و ذوالکِفل یاد کن [که] همگی از نیکانند.');
INSERT INTO "islamhouse" VALUES(38,49,'38:49','این [قرآن] پندی است و یقیناً پرهیزگاران [در آخرت] بازگشتی نیکو دارند.');
INSERT INTO "islamhouse" VALUES(38,50,'38:50','باغ‌هایی جاودان که در‌ها[یش] برای آنان گشوده است.');
INSERT INTO "islamhouse" VALUES(38,51,'38:51','در حالى كه [بر تخت‌ها] تكیه زده‌اند، [از خدمتکارانشان] میوه‌هاى فراوان و نوشیدنی مى‌خواهند.');
INSERT INTO "islamhouse" VALUES(38,52,'38:52','و زنانی نزدشان است که تنها [به شوهران خود] نظر دارند و [همگی] هم‌سن و سال ‌هستند.');
INSERT INTO "islamhouse" VALUES(38,53,'38:53','این است آنچه براى روز حساب به شما وعده داده مى‌شد.');
INSERT INTO "islamhouse" VALUES(38,54,'38:54','این روزی [و لطف] ماست که پایانی ندارد.');
INSERT INTO "islamhouse" VALUES(38,55,'38:55','این [پاداش پرهیزگاران] است؛ و بی‌تردید، سرکشان بدترین بازگشتگاه را دارند.');
INSERT INTO "islamhouse" VALUES(38,56,'38:56','[همان] دوزخ که در آن وارد می‌شوند؛ و چه بد قرارگاهی است!');
INSERT INTO "islamhouse" VALUES(38,57,'38:57','این [مجازات،] آب جوشان و خونابۀ چرک‌آلودی است که باید آن را بچشند.');
INSERT INTO "islamhouse" VALUES(38,58,'38:58','و از همين گونه، انواع ديگری [عذاب‌ها را خواهند دید].');
INSERT INTO "islamhouse" VALUES(38,59,'38:59','[هنگام ورود دوزخیان به آتش، برخی از آنان به دیگران می‌گویند:] «این گروهی است که همراه شما [به آتش] داخل می‌شوند». [آنان پاسخ مى‌دهند:] «خوشامد بر آنان مباد! به راستی که به آتش درمى‌آیند».');
INSERT INTO "islamhouse" VALUES(38,60,'38:60','[آنان به رهبران خود] می‌گویند: «بلکه بر شما خوشامد مباد؛ [زیرا] شما بودید که این [عذاب] را پیش پای ما نهادید. پس [دوزخ] چه بد قرارگاهی است!»');
INSERT INTO "islamhouse" VALUES(38,61,'38:61','[آنگاه] می‌گویند: «پروردگارا، هر کس این [عذاب] را برای ما فراهم کرده است، عذابش را در آتش چندین برابر بیفزای».');
INSERT INTO "islamhouse" VALUES(38,62,'38:62','[دوزخیان متکبر] می‌گویند: «چرا کسانی را که [در دنیا] از اشرار مى‌پنداشتیم، [در دوزخ] نمى‌بینیم؟');
INSERT INTO "islamhouse" VALUES(38,63,'38:63','آیا [در دنیا، بى‌جهت] آنان را به تمسخر می‌گرفتیم یا [اینجا هستند و] چشمان [ما] به آنان نمی‌افتد؟»');
INSERT INTO "islamhouse" VALUES(38,64,'38:64','بی‌تردید، این ستیز [و مجادله] اهل آتش، راست است [و قطعاً رخ می‌دهد].');
INSERT INTO "islamhouse" VALUES(38,65,'38:65','[ای پیامبر، به کافران] بگو: «من فقط بیم‌دهنده‌ام و معبودی [به‌حق] نیست، مگر الله یگانۀ پیروزمند.');
INSERT INTO "islamhouse" VALUES(38,66,'38:66','پروردگار آسمان‌ها و زمین و آنچه میان آنهاست، [آن ذاتِ] شکست‌ناپذیرِ آمرزنده».');
INSERT INTO "islamhouse" VALUES(38,67,'38:67','[به کافران] بگو: «این [قرآن،] خبرى بزرگ است.');
INSERT INTO "islamhouse" VALUES(38,68,'38:68','[ولی] شما از آن رویگردانید.');
INSERT INTO "islamhouse" VALUES(38,69,'38:69','[اگر به من وحى نمى‌شد، هرگز] از عالم بالاتر ـ آنگاه كه [فرشتگان در مورد آفرینشِ بشر] جدال مى‌كردندـ اطلاعى نداشتم.');
INSERT INTO "islamhouse" VALUES(38,70,'38:70','به من وحی نمی‌شود، مگر از آن روى كه من بیم‌دهنده‌اى آشكارم».');
INSERT INTO "islamhouse" VALUES(38,71,'38:71','[یاد کن از] هنگامی که پروردگارت به فرشتگان گفت: «من بشری از گِل می‌آفرینم.');
INSERT INTO "islamhouse" VALUES(38,72,'38:72','آنگاه كه او را سامان دادم و از روح خویش در او دمیدم، [به نشانۀ بزرگداشتِ الله] برایش به سجده بیفتید».');
INSERT INTO "islamhouse" VALUES(38,73,'38:73','[چون آدم پدید آمد،] فرشتگان همگی سجده کردند');
INSERT INTO "islamhouse" VALUES(38,74,'38:74','مگر ابلیس [که] تکبر نمود و کافر شد.');
INSERT INTO "islamhouse" VALUES(38,75,'38:75','[الله] فرمود: «اى ابلیس، چه چیز مانع تو شد از اینکه در برابر آنچه به دستان خویش آفریده‌ام سجده كنى؟ آیا تکبر ورزیدی یا برتری‌جویی کردی؟»');
INSERT INTO "islamhouse" VALUES(38,76,'38:76','او گفت: «من از او برترم: مرا از آتش آفریده‌ای و او را از گِل».');
INSERT INTO "islamhouse" VALUES(38,77,'38:77','[الله] فرمود: «از آن [بهشت برین] بیرون شو [که] بی‌تردید، تو [منفور و] رانده‌شده‌ای.');
INSERT INTO "islamhouse" VALUES(38,78,'38:78','و لعنت من تا روز جزا [= قیامت] بر تو خواهد بود».');
INSERT INTO "islamhouse" VALUES(38,79,'38:79','[ابلیس] گفت: «پروردگارا، پس تا روزی که [بندگانت] برانگیخته می‌شوند، به من مهلت بده».');
INSERT INTO "islamhouse" VALUES(38,80,'38:80','[الله] فرمود: «مهلت خواهی داشت،');
INSERT INTO "islamhouse" VALUES(38,81,'38:81','تا روز [قیامت،] آن وقت معیّن».');
INSERT INTO "islamhouse" VALUES(38,82,'38:82','[ابلیس] گفت: «پس به عزت تو سوگند که قطعاً همۀ آنان را گمراه می‌کنم');
INSERT INTO "islamhouse" VALUES(38,83,'38:83','مگر بندگان مخلصت را از میان آنان».');
INSERT INTO "islamhouse" VALUES(38,84,'38:84','[الله] فرمود: «[سخنِ من] حق است و حق را مى‌گویم.');
INSERT INTO "islamhouse" VALUES(38,85,'38:85','و یقیناً جهنم را از تو و از هر یک از آنان که از تو پیروی کند ـ از همه ـ انباشته خواهم ساخت».');
INSERT INTO "islamhouse" VALUES(38,86,'38:86','[ای پیامبر، به مشرکان] بگو: «من [در برابر رسالتم] هیچ پاداشی از شما نمی‌خواهم و فریبکار نیستم [و به دروغ ادعای نبوت نمی‌کنم]».');
INSERT INTO "islamhouse" VALUES(38,87,'38:87','این [قرآن] جز پندی برای جهانیان [= انسان و جن] نیست.');
INSERT INTO "islamhouse" VALUES(38,88,'38:88','و قطعاً پس از چندی [که مرگ به سراغتان آمد] از خبرِ [راستیِ] آن آگاه خواهید شد.');
INSERT INTO "islamhouse" VALUES(39,1,'39:1','نزول این کتاب، از سوی الله شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(39,2,'39:2','این کتاب را به‌حق بر تو نازل کردیم؛ پس الله را با خلوص اعتقاد عبادت کن.');
INSERT INTO "islamhouse" VALUES(39,3,'39:3','آگاه باشید که دین خالص [و خالی از شرک]، از آنِ الله است و افرادى كه غیر الله را [دوست و] كارساز خود انتخاب كرده‌اند [پندارشان این است كه:] «آنها را فقط به این دلیل عبادت می‌کنیم كه ما را به الله نزدیک ‌گردانند [و منزلتمان را بیشتر کنند]. الله در مواردی که با هم اختلاف دارند میان آنان [و موحدانِ راستین] داورى خواهد كرد. بی‌تردید، الله كسى را كه دروغگوی ناسپاس باشد، هدایت نخواهد كرد.');
INSERT INTO "islamhouse" VALUES(39,4,'39:4','اگر الله مى‌خواست فرزندى برگزیند، از میان آفریدگان خود هرچه مى‌خواست برمى‌گزید. او تعالی [از این نسبت‌ها] منزه است، و اوست الله یگانۀ پیروزمند.');
INSERT INTO "islamhouse" VALUES(39,5,'39:5','آسمان‌ها و زمین را به‌حق [و با هدف] آفرید. شب را بر روز مى‌گسترانَد و روز را بر شب؛ و خورشید و ماه را در خدمت [شما] گماشت که هر یک تا سرآمدِ معیّنى در حرکت است. آگاه باشید! او شکست‌ناپذیرِ آمرزگار است.');
INSERT INTO "islamhouse" VALUES(39,6,'39:6','او شما را از یک تن آفرید، آنگاه همسرش را نیز از وی پدید آورد؛ و براى [استفادۀ] شما، هشت نوع از چهارپایان [= نر و مادۀ شتر، گاو، گوسفند و بز] را آفرید. شما را در شكم مادرتان طى مراحل مختلف در نهانگاه‌های سه‌گانه [پردۀ مَشیمه، رحِم و شكم] مى‌آفریند. این است الله، پروردگار شما كه فرمانروایى، خاص اوست. معبودى [به‌حق] جز او نیست؛ پس چگونه [از عبادت الله] به بیراهه می‌روید؟');
INSERT INTO "islamhouse" VALUES(39,7,'39:7','اگر ناسپاسی کنید، [بدانید که] الله از شما بی‌نیاز است و هرگز ناسپاسیِ بندگانش را نمی‌پسندد؛ و اگر شکر او را به جای آورید، این کار را برایتان می‌پسندد؛ و هیچ گناهکاری، بارِ گناه دیگری را بر دوش نمی‌کشد؛ بازگشتتان به سوی پروردگارتان است؛ آنگاه او شما را از آنچه انجام می‌دادید، آگاه می‌کند. یقیناً او از رازِ دل‌ها آگاه است.');
INSERT INTO "islamhouse" VALUES(39,8,'39:8','هر گاه انسان دچار [رنج و] زیانی می‌شود، پروردگارش را [با تضرّع و زاری] می‌خواند و به درگاهش بازمی‌گردد؛ اما هنگامی که نعمتی از جانب خود به وی می‌بخشیم، مشکلی را که قبلاً برایش دعا می‌کرد از یاد می‌بَرد و برای الله [در قدرت،] همتایانی قائل می‌شود تا [دیگران را نیز] از راه او گمراه کند. [ای پیامبر، به چنین کسی] بگو: «چند صباحی با ناسپاسی [از نعمت‌های دنیا] بهره‌مند شو که قطعاً دوزخی خواهی بود».');
INSERT INTO "islamhouse" VALUES(39,9,'39:9','آیا [چنین مشرکی بهتر است یا] کسی که در طول شب، در حال سجده و قیام، به عبادت [الله] مشغول است و از [عذاب] آخرت ترسان است و به رحمت پروردگارش امید دارد؟ بگو: «آیا کسانی که [حق بندگی پروردگار را] می‌دانند و کسانی که نمی‌دانند، یکسانند؟ تنها خردمندان پند می‌پذیرند».');
INSERT INTO "islamhouse" VALUES(39,10,'39:10','[ای پیامبر،] بگو: «ای بندگان باایمانِ من، از پروردگارتان پروا کنید. افرادى كه نیكویى مى‌كنند، [علاوه بر آخرت، در این دنیا نیز] پاداش نیكو دارند. زمین الله گسترده است [پس اگر براى حفظ عقیدۀ خود دچار مشكل شدید، هجرت كنید]. قطعاً پاداش پایداران، کامل و بى‌دریغ عطا خواهد شد».');
INSERT INTO "islamhouse" VALUES(39,11,'39:11','بگو: «من مأمورم با خلوصِ اعتقاد، الله را عبادت کنم،');
INSERT INTO "islamhouse" VALUES(39,12,'39:12','و مأمور شده‌ام نخستین كسى [در این امت] باشم كه تسلیم [محض در مقابل پروردگار] است».');
INSERT INTO "islamhouse" VALUES(39,13,'39:13','بگو: «اگر در برابر پروردگارم نافرمان باشم، از عذاب روز بزرگ [قیامت] مى‌ترسم».');
INSERT INTO "islamhouse" VALUES(39,14,'39:14','[ای پیامبر] بگو: «من الله را با خلوصِ اعتقاد خود عبادت می‌کنم؛');
INSERT INTO "islamhouse" VALUES(39,15,'39:15','شما نیز غیر از الله، هر چه را می‌خواهید عبادت کنید». بگو: «زیانكاران [واقعى] كسانى هستند كه [به طمع دنیا] خود و خانواده‌هایشان را در رستاخیز به زیان انداختند. آگاه باشید كه این، همان زیان آشکار است.');
INSERT INTO "islamhouse" VALUES(39,16,'39:16','بر بالای سر و زیر پایشان طبقاتى از آتش است. این است آنچه الله بندگانش را از آن مى‌ترساند. اى بندگان من، از [نافرمانى] من پروا كنید.');
INSERT INTO "islamhouse" VALUES(39,17,'39:17','بشارت براى كسانى است كه از عبادت طاغوت [= معبودان غیر الله] اجتناب ورزیدند و [توبه‌کنان] به درگاه الهی بازگشتند؛ پس [ای پیامبر،] [بهشت را] به بندگانم بشارت بده.');
INSERT INTO "islamhouse" VALUES(39,18,'39:18','[همان] کسانی که سخن[های مختلف] را می‌شنوند و از بهترین آن پیروى مى‌كنند. آنان كسانى هستند كه الله هدایتشان کرده و خردمندند.');
INSERT INTO "islamhouse" VALUES(39,19,'39:19','آیا كسى را كه فرمان مجازات بر او قطعی شده است [می‌توان هدایت نمود]؟ آیا تو مى‌توانى کسی را كه در آتش است، نجات دهی؟');
INSERT INTO "islamhouse" VALUES(39,20,'39:20','اما كسانى كه از [عذاب] پروردگارشان پروا دارند، برای آنان [در بهشت] غرفه‌هایی است که بر فراز آنها غرفه‌های دیگری ساخته شده‌ [و] جویبارها از زیر آنها جاری است. این وعده را الله داده است و الله در وعده خلاف نمی‌کند.');
INSERT INTO "islamhouse" VALUES(39,21,'39:21','آیا ندیده‌ای كه الله بارانى از آسمان فرستاد و آن را [به صورت] چشمه‌هایى در زمین جاری ساخت؛ آنگاه به وسیلۀ آن، كشتزارى با رنگ‌های گوناگون پدید مى‌آورد؛ سپس پژمرده مى‌گردد و مى‌بینى كه زرد شده است؛ آنگاه آن را خُرد و خاشاک مى‌سازد؟ بی‌تردید، در این [دگرگونی‌ها] پندی براى خردمندان است.');
INSERT INTO "islamhouse" VALUES(39,22,'39:22','پس آیا كسى كه الله دلش را پذیراى اسلام كرده و از جانب پروردگارش، از نور [هدایت و بصیرت] برخوردار گشته، [همانند سختدلان است]؟ واى بر آنان که در برابرِ ذكر الله سنگدل شده‌اند! آنان در گمراهىِ آشكارند.');
INSERT INTO "islamhouse" VALUES(39,23,'39:23','الله بهترین سخن را به صورت كتابى نازل كرده كه [برخی از آیاتش‌] همانند یكدیگر و [برخی نیز] مکرر است. كسانى كه از [مجازات] پروردگارشان بیم دارند، [با شنیدن این آیات،] مو بر تنشان راست می‌شود و سپس دل و جانشان با یاد الله آرام می‌گیرد. این هدایتِ الله است كه هر کس را بخواهد، به آن راه می‌نماید؛ و هر کس که الله او را گمراه سازد، هیچ هدایتگری نخواهد داشت.');
INSERT INTO "islamhouse" VALUES(39,24,'39:24','پس آیا كسی كه در روز قیامت [دستانش بسته است و ناچار‌] با صورتش آن عذاب سخت را دفع مى‌كند، [همچون کسی است که در آسایش بهشت قرار دارد]؟ به ستمکاران [مشرک‌] گفته می‌شود: «[نتیجۀ‌] دستاوردتان را بچشید».');
INSERT INTO "islamhouse" VALUES(39,25,'39:25','کسانی که پیش از آنان بودند [نیز آیات الله را] دروغ شمردند و از جایى كه گمان نمى‌كردند، عذاب دامنگیرشان شد.');
INSERT INTO "islamhouse" VALUES(39,26,'39:26','الله [طعم] رسوایی و خواری را در زندگی دنیا به آنان چشاند؛ و مسلّماً عذاب آخرت بزرگ‌تر [و سخت‌تر] است. اگر مشرکان می‌دانستند [که رفتارشان چه پیامدی دارد، عبرت می‌گرفتند].');
INSERT INTO "islamhouse" VALUES(39,27,'39:27','در این قرآن، از هر [گونه] مَثلى براى مردم آورده‌ایم؛ باشد که پند پذیرند.');
INSERT INTO "islamhouse" VALUES(39,28,'39:28','قرآنى به زبان فصیح عربی و بدون هیچ گونه ابهام و انحراف [نازل كردیم]؛ باشد که [تحت تعلیماتش، از پروردگار یکتا] پروا کنند.');
INSERT INTO "islamhouse" VALUES(39,29,'39:29','الله [براى مقایسۀ مشرک و موحد،] مردى را مثال می‌زند كه چند شریک دربارۀ [مالکیتِ] او پیوسته با هم اختلاف و مشاجره دارند؛ و در مقابل آن، مردى كه تحت فرمان یک نفر قرار دارد. آیا این دو نفر، [از نظرِ موقعیت] یكسانند؟ ستایش مخصوص الله است؛ ولى بیشتر آنان نمى‌دانند.');
INSERT INTO "islamhouse" VALUES(39,30,'39:30','[ای پیامبر،] قطعاً تو خواهی مُرد و آنان [نیز] خواهند مُرد.');
INSERT INTO "islamhouse" VALUES(39,31,'39:31','آنگاه [شما مردم] در روز قیامت، نزد پروردگارتان [دربارۀ مسایل مورد اختلاف،] مجادله خواهید کرد.');
INSERT INTO "islamhouse" VALUES(39,32,'39:32','و كیست ستمكارتر از کسی كه به الله، دروغ نسبت می‌دهد و سخن راستی را كه به وی رسیده است، دروغ می‌شمارد؟ آیا در دوزخ، جایگاهى براى کافران نیست؟');
INSERT INTO "islamhouse" VALUES(39,33,'39:33','اما کسانی که پیام راستین بیاورند [و دیگران را به پذیرش آن دعوت کنند] و خود نیز آن را تصدیق نمایند، به راستی پرهیزگارند.');
INSERT INTO "islamhouse" VALUES(39,34,'39:34','هر چه بخواهند، نزد پروردگارشان دارند. پاداش نیكوكاران همین است.');
INSERT INTO "islamhouse" VALUES(39,35,'39:35','تا الله، بدترین رفتارشان را از [كارنامۀ] آنان بزداید و بر اساس بهترین اعمالشان، به آنان پاداش دهد.');
INSERT INTO "islamhouse" VALUES(39,36,'39:36','آیا الله براى دفاع از بنده‌اش كافى نیست؟ اما [مشرکان] تو را از معبودان[باطل]شان مى‌ترسانند؛ و هر کس كه الله او را گمراه کند، هیچ هدایتگری نخواهد داشت.');
INSERT INTO "islamhouse" VALUES(39,37,'39:37','و هر کس که الله او را هدایت کند، هیچ کس او را گمراه نخواهد ساخت. آیا الله شکست‌ناپذیر و انتقام‌گیرنده نیست؟');
INSERT INTO "islamhouse" VALUES(39,38,'39:38','[ای پیامبر،] اگر از مشرکان بپرسی: «چه کسی آسمان‌ها و زمین را آفریده است؟»، مسلماً می‌گویند: «الله». به آنان بگو: «پس به نظر شما، اگر الله ناخوشی و رنجی برایم بخواهد، آیا آنچه به جاى الله [به نیایش] مى‌خوانید، می‌توانند آسیب او را برطرف کنند؟ یا اگر الله رحمتى براى من بخواهد، آیا مى‌توانند مانع او شوند؟» بگو: [پشتیبانیِ] الله برایم کافی است. اهل توکل، تنها بر او توكل مى‌كنند».');
INSERT INTO "islamhouse" VALUES(39,39,'39:39','بگو: «اى قوم من، شما بر روش خود عمل کنید، من نیز [به وظایف خود] عمل مى‌كنم.');
INSERT INTO "islamhouse" VALUES(39,40,'39:40','به زودى خواهید دانست كه كیست آنكه [در دنیا] گرفتارِ عذاب رسواگر می‌شود و [در آخرت،] عذاب پاینده بر سرش می‌بارد».');
INSERT INTO "islamhouse" VALUES(39,41,'39:41','این كتاب آسمانی را براى [هدایت] مردم، به‌حق بر تو نازل كردیم؛ هر کس هدایت شود، به سودِ خود اوست و هر کس گمراه گشت، به زیان خویش گمراه شده است؛ و تو [نگهبان و] کارسازشان نیستى [که آنان را به هدایت اجبار کنی].');
INSERT INTO "islamhouse" VALUES(39,42,'39:42','الله جان‌ها را در لحظۀ مرگ مى‌گیرد و [نیز] جان کسی را كه نمرده است، به هنگام خوابش [مى‌گیرد]؛ آنگاه جان‌هایی را كه حكم مرگ بر آنها رانده است، نگه مى‌دارد و جانِ دیگران را تا هنگام معین [مرگ] بازپس می‌فرستد. در این [امر] براى هر کس که بیندیشد، نشانه‌هایى [از قیامت] وجود دارد.');
INSERT INTO "islamhouse" VALUES(39,43,'39:43','آیا مشرکان غیر از الله، شفیعانی [برای خود از میان معبودان باطلشان] انتخاب کرده‌اند؟ [ای پیامبر، به آنان] بگو: «حتی اگر آنها مالک چیزی نباشند و [چیزی را] درک نکنند [باز هم آنها را به شفاعت می‌طلبید]؟»');
INSERT INTO "islamhouse" VALUES(39,44,'39:44','بگو: «تمام شفاعت‌ها، فقط از آنِ الله است. فرمانروایی آسمان‌ها و زمین [نیز] از آن اوست؛ سپس [در روز قیامت، همگی] به پیشگاهِ او بازگردانده می‌شوید».');
INSERT INTO "islamhouse" VALUES(39,45,'39:45','هنگامی که الله به یکتایی [و توحید] یاد می‌شود، دل‌های کسانی که به آخرت ایمان ندارند، متنفر [و بیزار] می‌گردد؛ و چون از معبودانی غیر از الله یاد شود، آنگاه است که مشرکان شادمان می‌گردند.');
INSERT INTO "islamhouse" VALUES(39,46,'39:46','بگو: «بار الها، [ای] آفرینندۀ آسمان‌ها و زمین، ای دانای ‌نهان و آشکار، تو [به تنهایی] میان بندگانت راجع به آنچه [در دنیا] در آن اختلاف می‌ورزیدند داوری خواهی کرد».');
INSERT INTO "islamhouse" VALUES(39,47,'39:47','اگر دو برابر آنچه در زمین است، در اختیار ستمکاران [مشرک] باشد، همه را براى نجات خود از سختىِ عذابِ روز قیامت فدا مى‌كنند؛ [ولى پذیرفتنى نیست]؛ و اموری از جانب الله برایشان آشكار مى‌گردد كه تصورش را هم نمى‌كردند.');
INSERT INTO "islamhouse" VALUES(39,48,'39:48','[در آن روز،] دستاورد زشتشان بر آنان آشكار مى‌شود و عذابى كه مسخره مى‌كردند، دامنگیرشان خواهد شد.');
INSERT INTO "islamhouse" VALUES(39,49,'39:49','هر گاه انسان دچار [رنج و] زیانی شود، ما را [به نیایش] مى‌خواند؛ و چون از جانب خویش نعمتى به وی ارزانى‌ داریم، می‌گوید: «فقط [لیاقت و] داناییِ‌ خودم موجب این نعمت شده است. [نه،] بلكه این آزمایش [الهی] است؛ ولى بیشتر مردم نمى‌دانند.');
INSERT INTO "islamhouse" VALUES(39,50,'39:50','پیشینیان آنان نیز همین سخن را گفتند؛ ولى [به هنگام عذاب،] دستاوردشان سودى به حالشان نداشت.');
INSERT INTO "islamhouse" VALUES(39,51,'39:51','و [عقوبت] دستاورد زشت آنان دامنگیرشان شد. این ستمکاران [مشرک] نیز به عقوبت دستاوردشان گرفتار خواهند شد و [پروردگار متعال را] به تنگ نخواهند آورد.');
INSERT INTO "islamhouse" VALUES(39,52,'39:52','آیا مشرکان نمی‌دانند که الله [نعمت و] روزی را بر هر کس که بخواهد، گسترده می‌دارد و یا تنگ می‌گرداند؟ بی‌گمان در این امر، نشانه‌هایی [از تدبیر و حکمت الهی] برای مومنان است.');
INSERT INTO "islamhouse" VALUES(39,53,'39:53','[ای پیامبر،] به بندگانم که [در ارتکاب به شرک و گناه] زیاده‌روی کرده‌اند، بگو: «از رحمت الهی مأیوس نباشید؛ چرا که [اگر توبه کنید،] الله همۀ گناهانتان را می‌بخشد. به راستی که او تعالی بسیار آمرزنده و مهربان است.');
INSERT INTO "islamhouse" VALUES(39,54,'39:54','پیش از آنکه عذاب به سراغتان بیاید و هیچ کس نتواند یاری‌تان کند، به سوی پروردگار‌تان بازگردید و تسلیم [فرمانِ] او شوید.');
INSERT INTO "islamhouse" VALUES(39,55,'39:55','و پیش از آنكه ناگهان ـ در حالی که غافلید ـ عذاب به سراغتان بیاید، از بهترین دستورهایى كه از جانب پروردگارتان بر شما نازل شده است، پیروى کنید.');
INSERT INTO "islamhouse" VALUES(39,56,'39:56','[هم‌اکنون بر حذر باشید] تا کسی [در روز قیامت] نگوید: «ای دریغ بر من که در حقِ الله کوتاهی نمودم و [مؤمنان را] مسخره می‌کردم!»');
INSERT INTO "islamhouse" VALUES(39,57,'39:57','یا [گناه خود را بر گردن تقدیر نیندازد و] نگوید: «اگر الله مرا هدایت کرده بود، قطعاً پرهیزگار بودم»');
INSERT INTO "islamhouse" VALUES(39,58,'39:58','یا هنگامی که عذاب را می‌بیند، نگوید: «ای کاش بازگشتی [به دنیا] داشتم تا در زمرۀ نیکوکاران قرار می‌گرفتم».');
INSERT INTO "islamhouse" VALUES(39,59,'39:59','[الله می‌فرماید:] «آری، آیات من برایت آمد و تو آنها را دروغ پنداشتی و تکبر نمودی و کفر ورزیدی».');
INSERT INTO "islamhouse" VALUES(39,60,'39:60','و روز قیامت کسانی را که بر الله دروغ بسته‌اند، سیه‌روی می‌بینی. آیا در دوزخ جایگاهی برای متکبران نیست؟');
INSERT INTO "islamhouse" VALUES(39,61,'39:61','الله، پرهیزگاران را با درآوردن به مکان کامیابی‌شان، [از عذاب] نجات می‌دهد. [در بهشت،] نه رنجی به آنان می‌رسد و نه اندوهگین می‌شوند.');
INSERT INTO "islamhouse" VALUES(39,62,'39:62','الله آفریدگار هر چیزی است و او تعالی بر همه چیز [نگهبان و] کارساز است.');
INSERT INTO "islamhouse" VALUES(39,63,'39:63','کلید گنجینه‌های آسمان‌ها و زمین از آنِ اوست؛ و کسانی که به آیات الهی کافر شدند، قطعاً زیانکارند.');
INSERT INTO "islamhouse" VALUES(39,64,'39:64','[ای پیامبر،] بگو: «ای بی‌خبران، آیا به من فرمان می‌دهید که [معبودی] غیر از الله را عبادت کنم؟»');
INSERT INTO "islamhouse" VALUES(39,65,'39:65','به تو و پیامبرانِ پیش از تو وحى شده است كه: «اگر شرک بورزى، [پاداشِ] کارهایت قطعاً بر باد خواهد رفت و زیانكار خواهى شد؛');
INSERT INTO "islamhouse" VALUES(39,66,'39:66','بلکه تنها الله را عبادت کن و سپاسگزار باش».');
INSERT INTO "islamhouse" VALUES(39,67,'39:67','مشرکان، الله را چنان كه سزاوار اوست، ارج ننهادند؛ حال آنکه در روز قیامت، زمین یكسره در قبضۀ اوست؛ و آسمان‌ها به دست وی درهم‌پیچیده خواهد شد. پاک و منزه است الله؛ و از چیزهایی که با [وی] شریک می‌سازند، برتر است.');
INSERT INTO "islamhouse" VALUES(39,68,'39:68','[در آستانۀ قیامت،] در «صور» دمیده می‌شود؛ آنگاه هر کس که در آسمان‌ها و در زمین است، مدهوشِ مرگ خواهد شد ـ مگر كسى كه الله بخواهد؛ آنگاه بار دیگر در آن دمیده می‌شود و ناگهان [همه از مرگ] برمی‌خیزند و چشم‌به‌راه [فرمان پروردگار] هستند.');
INSERT INTO "islamhouse" VALUES(39,69,'39:69','[در روز قیامت،] زمین به نورِ پروردگارش روشن خواهد شد و نامۀ [اعمال] نهاده می‌شود و پیامبران و گواهان را می‌آورند و به حق و عدالت میان مردم داوری می‌شود و هرگز به آنان ستم نخواهد شد.');
INSERT INTO "islamhouse" VALUES(39,70,'39:70','و جزای عملکردِ هر کس، به تمام و کمال داده می‌شود؛ و الله نسبت به اعمالشان دانا‌تر است.');
INSERT INTO "islamhouse" VALUES(39,71,'39:71','کافران گروه‌گروه به سوى دوزخ رانده مى‌شوند. همین كه به آنجا می‌رسند، درهایش [به روی آنان] گشوده می‌گردد و نگهبانانش می‌گویند: «آیا پیامبرانی از میان خود شما به سراغتان نیامدند كه آیات پروردگارتان را برای شما مى‌خواندند و دربارۀ دیدار این روز هشدار مى‌دادند؟» آنان [در پاسخ] مى‌گویند: «آری؛ ولى [ما آنان را انکار كردیم و] فرمان مجازات، سزاى کافران است».');
INSERT INTO "islamhouse" VALUES(39,72,'39:72','[به آنان] گفته مى‌شود: «از درهاى دوزخ وارد شوید و جاودانه در آن به سر ‌بَرید؛ و [به راستى] جایگاه متكبران، چه بد [جایگاهى] است!»');
INSERT INTO "islamhouse" VALUES(39,73,'39:73','و كسانى كه از پروردگارشان پروا داشتند، گروه‌گروه به سوى بهشت هدایت مى‌شوند؛ همین كه به آنجا می‌رسند ـ در حالی كه درهاى بهشت گشوده شده است ـ [با عزت و احترام وارد می‌شوند] و نگهبانان آنجا می‌گویند: «سلام بر شما. خوش آمدید. [و] برای همیشه [در آن بمانید]».');
INSERT INTO "islamhouse" VALUES(39,74,'39:74','بهشتیان مى‌گویند: «ستایش و سپاس مخصوص الله است كه وعدۀ خویش را در مورد ما تحقق بخشید و سرزمین [پهناور بهشت] را به ما میراث داد كه در هر جاى آن بخواهیم، منزل می‌گیریم؛ و [به راستى] پاداش اهل عمل، چه نیكوست!»');
INSERT INTO "islamhouse" VALUES(39,75,'39:75','[در آن روز،] فرشتگان را خواهی دید كه پیرامون عرش حلقه زده‌اند و پروردگارشان را به پاکی می‌ستایند؛ و به حق و عدالت میان مردم داورى خواهد شد و گفته مى‌شود: «ستایش و سپاس، مخصوص الله - پروردگار جهانیان- است».');
INSERT INTO "islamhouse" VALUES(40,1,'40:1','حم [حا. میم]');
INSERT INTO "islamhouse" VALUES(40,2,'40:2','نزول [این] کتاب، از سوی الله شکست‌ناپذیرِ داناست.');
INSERT INTO "islamhouse" VALUES(40,3,'40:3','[پروردگاری که] آمرزندۀ گناه و توبه‌پذیر، سخت‌کیفر و نعمت‌بخش است؛ هیچ معبودی [به‌حق] جز او نیست و بازگشت [همه] به سوی اوست.');
INSERT INTO "islamhouse" VALUES(40,4,'40:4','تنها کافران هستند که دربارۀ آیات الهی مجادله مى‌كنند؛ پس مبادا رفت و آمدشان در شهرها [برای تجارت و بهره‌مندی‌شان از نعمت‌های دنیا] تو را بفریبد.');
INSERT INTO "islamhouse" VALUES(40,5,'40:5','پیش از آنان قوم نوح، و پس از آنان نیز گروه‌های دیگری [همچون قوم عاد و ثمود و لوط نیز پیامبرانشان را] دروغگو شمردند و هر قومی خواست پیامبرش را دستگیر كند [و به قتل برساند]. آنان به باطل مجادله كردند تا به وسیله آن، حق را از میان بردارند؛ من نیز آنان را عذاب کردم؛ [پس بنگرید که] عذابِ من چگونه بود.');
INSERT INTO "islamhouse" VALUES(40,6,'40:6','و فرمان عذابِ پروردگارت درباره کافران اینچنین به تحقق پیوست كه آنان اهل آتشند.');
INSERT INTO "islamhouse" VALUES(40,7,'40:7','فرشتگانى كه عرش را حمل مى‌كنند و فرشتگانى كه گردِ آن حلقه زده‌اند، پروردگارشان را به پاکی می‌ستایند و به او ایمان دارند و [این گونه] براى اهل ایمان آمرزش مى‌خواهند: «پروردگارا، رحمت و دانشت، هر چیزى را فراگرفته است؛ پس كسانى را كه توبه كردند و پیرو راه تو شدند، بیامرز و از عذاب دوزخ دور نگاه دار».');
INSERT INTO "islamhouse" VALUES(40,8,'40:8','پروردگارا، آنان و پدران [و مادران] و همسران و فرزندان صالحشان را در باغ‌های جاودان بهشت كه وعده داده‌اى، داخل كن، كه بی‌تردید، تو شکست‌ناپذیر و حکیمی.');
INSERT INTO "islamhouse" VALUES(40,9,'40:9','[بار الها،] آنان را از [كیفرِ] بدی‌ها حفظ كن؛ و قطعاً هر کس را که در آن روز از [كیفرِ] بدی‌ها نگاه دارى، مشمول رحمتت قرار داده‌اى. این، همان كامیابى بزرگ است».');
INSERT INTO "islamhouse" VALUES(40,10,'40:10','[در دوزخ] به کافران ندا مى‌دهند كه: «دشمنى و خشم الله، سخت‌تر از دشمنى و خشم شما نسبت به خودتان است؛ چرا كه به ایمان دعوت مى‌شدید، ولى [با دشمنى نسبت به خود، عقیدۀ توحیدی را] انكار مى‌كردید».');
INSERT INTO "islamhouse" VALUES(40,11,'40:11','آنان [در پاسخ] می‌گویند: «پروردگارا، ما را دوبار میراندی و دو بار زنده کردی؛ پس [اکنون که] به گناهان خود اعتراف کردیم، آیا راهی برای خروج [از دوزخ] وجود دارد؟»');
INSERT INTO "islamhouse" VALUES(40,12,'40:12','[به آنان گفته می‌شود:] «این [عذاب] به سبب آن است که چون الله به یگانگی خوانده می‌شد، انکار می‌کردید؛ ولی اگر به او شرک آورده می‌شد ایمان می‌آوردید. [اکنون] داوری از آن الله بلندمرتبۀ بزرگ است.');
INSERT INTO "islamhouse" VALUES(40,13,'40:13','او همان ذاتی است که نشانه‌های [توان و تدبیر] خود را به شما ارائه می‌دهد و [با نزول باران] از آسمان روزى برایتان مى‌فرستد؛ ولى تنها كسانى پند مى‌گیرند كه [خالصانه به درگاه الهی] توبه ‌کنند.');
INSERT INTO "islamhouse" VALUES(40,14,'40:14','[ای مردم! تنها] الله را با خلوصِ اعتقاد بخوانید؛ هر چند کافران خوششان نیاید.');
INSERT INTO "islamhouse" VALUES(40,15,'40:15','او دارای درجات رفیع و صاحبِ عرش است؛ و به سوی هر یک از بندگانش كه بخواهد، به فرمان خویش وحى مى‌فرستد تا دربارۀ روز ملاقات [به آنان] هشدار دهد.');
INSERT INTO "islamhouse" VALUES(40,16,'40:16','روزی كه مردم حاضر و نمایان می‌شوند و چیزی از آنان بر الله پوشیده نخواهد ماند، [الله می‌فرماید:] «امروز فرمانروایی از آنِ کیست؟» [آنگاه خود می‌فرماید:] «از آنِ الله یگانه پیروزمند».');
INSERT INTO "islamhouse" VALUES(40,17,'40:17','امروز هر کس در برابر آنچه کرده است، پاداش داده می‌شود و هیچ ستمی در میان نیست. بی‌گمان، الله در حسابرسی سریع است.');
INSERT INTO "islamhouse" VALUES(40,18,'40:18','در مورد روز قیامت [که بسیار نزدیک است] به آنان هشدار بده؛ آنگاه كه جان‌ها [از شدت وحشت] به گلوگاه مى‌رسد، در حالى كه سرشار از‌ اندوه شده‌اند. [در آن روز،] ستمگران نه دوستى مهربان دارند و نه شفیعی كه سخنش پذیرفته شود.');
INSERT INTO "islamhouse" VALUES(40,19,'40:19','[الله] حركات شرورانۀ چشم‌ها و آنچه را که در دل‌های افراد نهفته است، [به خوبی] مى‌داند.');
INSERT INTO "islamhouse" VALUES(40,20,'40:20','الله به عدالت داوری مى‌كند؛ و معبودانى كه [مشركان] به جاى الله عبادت می‌کنند، هیچ گونه [حق] داوری ندارند. به راستی که الله شنوای بیناست.');
INSERT INTO "islamhouse" VALUES(40,21,'40:21','آیا مشرکان در زمین نگشته‌اند تا ببینند سرانجامِ کسانی که پیش از آنان بودند، چگونه بوده است؟ آن اقوام گذشته، نیرومندتر از اینها بودند و آثار و بناهای [استوارتر] بیشتری در زمین پدید آوردند؛ اما الله آنان را به [سبب] گناهانشان عذاب کرد، در حالی که در برابرِ [عذاب] الله، هیچ [یار و] مدافعی نداشتند.');
INSERT INTO "islamhouse" VALUES(40,22,'40:22','این [كیفر] به سبب آن بود كه پیامبرانشان نشانه‌هاى روشن برایشان ارائه مى‌كردند، ولى آنان انكار می‌ورزیدند و الله [نیز] آنان را عذاب کرد. بی‌گمان، او توانمندى سخت‌كیفر است.');
INSERT INTO "islamhouse" VALUES(40,23,'40:23','ما موسى را با معجزاتِ خود و دلیلى قاطع فرستادیم.');
INSERT INTO "islamhouse" VALUES(40,24,'40:24','به سوی فرعون و هامان و قارون؛ ولی [آنان] گفتند: «[او] جادوگری دروغگوست».');
INSERT INTO "islamhouse" VALUES(40,25,'40:25','اما هنگامی که موسی، معجزات الهی و دلایل محکم را از جانب ما بر آنان ارائه كرد، [فرعون و یارانش سرکشی کردند و] گفتند: «پسران كسانى را كه به موسى ایمان آورده‌اند، بكشید و زنانشان را [براى کنیزی] زنده نگاه دارید»؛ و[لى] نیرنگ کافران، محكوم به فناست.');
INSERT INTO "islamhouse" VALUES(40,26,'40:26','فرعون گفت: «بگذارید موسى را بکشم؛ و [چون كسى را ندارد،] ناگزیر است پروردگارش را به یاری بخواند [و من از وی هراسی ندارم]. مى‌ترسم که او دینتان را دگرگون سازد یا در این سرزمین تباهى به بار آورد».');
INSERT INTO "islamhouse" VALUES(40,27,'40:27','موسی گفت: «من از [شر] هر متکبری که به روز حساب باور ندارد، به پروردگار خویش و پروردگار شما پناه می‌برم».');
INSERT INTO "islamhouse" VALUES(40,28,'40:28','و مرد مؤمنی از خانوادۀ فرعون که ایمانش را پنهان می‌داشت، گفت: «آیا می‌خواهید این مرد را [فقط به این جرم] بکشید که می‌گوید: "پروردگارم الله است" و [در تأیید سخنش نیز] دلایل روشنی از سوی پروردگار‌تان برای شما آورده است؟ اگر دروغگو باشد، [گناه] دروغش بر عهدۀ خودِ اوست؛ و اگر راست گفته باشد، [دست‌کم] برخى از عذاب‌هایی كه وعده مى‌دهد، به شما خواهد رسید. بی‌تردید، الله، کسی را که اسرافکار و دروغگوست، هدایت نمی‌کند.');
INSERT INTO "islamhouse" VALUES(40,29,'40:29','ای قوم من، امروز فرمانروایى در اختیار شماست [و] در این سرزمین سُلطه دارید؛ ولى اگر سختگیرىِ الله دامنگیرمان شود، چه كسى ما را یارى خواهد كرد؟» فرعون گفت: «من فقط آنچه را صلاح می‌دانم می‌گویم و شما را به راه راست هدایت مى‌كنم».');
INSERT INTO "islamhouse" VALUES(40,30,'40:30','آن مرد مؤمن گفت: «ای قوم من، می‌ترسم عذابی همچون عذاب اقوام پیشین [که از پیامبرانشان سرپیچی کردند] بر سرتان بیاید؛');
INSERT INTO "islamhouse" VALUES(40,31,'40:31','مانند شیوۀ [عذاب] قوم نوح و عاد و ثمود و کسانی که بعد از آنان بودند؛ در حالی که الله ستمی برای بندگانش نمی‌خواهد.');
INSERT INTO "islamhouse" VALUES(40,32,'40:32','و ای قوم من، از روزی بر شما می‌ترسم که مردم به فریاد از یکدیگر یاری‌ می‌خواهند.');
INSERT INTO "islamhouse" VALUES(40,33,'40:33','[همان] روزى كه همگی [از ترس] به عقب بازمى‌گردید و در برابر [مجازاتِ] الله، هیچ محافظى ندارید؛ و هر کس که الله او را گمراه كند، هیچ هدایتگری نخواهد داشت.');
INSERT INTO "islamhouse" VALUES(40,34,'40:34','پیش از این نیز یوسف با دلایل روشن نزدتان آمد؛ اما شما از آنچه برایتان آورده بود پیوسته در شک بودید، تا زمانی که از دنیا رفت [و بر تردیدتان افزوده شد]؛ آنگاه گفتید: «الله هرگز پس از وی پیامبری را مبعوث نخواهد کرد». الله، اسرافکارِ بدگمان را این‌گونه گمراه می‌سازد.');
INSERT INTO "islamhouse" VALUES(40,35,'40:35','كسانى كه در مورد آیات الهی ـ بدون اینکه دلیلى داشته باشند ـ به مجادله برمى‌خیزند، [رفتارشان] نزد الله و مؤمنین، سخت ناپسند است. الله این‌گونه بر دل هر متكبرِ خودكامه‌ای مهر [غفلت] مى‌نهد».');
INSERT INTO "islamhouse" VALUES(40,36,'40:36','فرعون [به وزیرش] گفت: «ای هامان، بنای بلندی برایم بساز؛ شاید به راه‌هایی برسم؛');
INSERT INTO "islamhouse" VALUES(40,37,'40:37','راه‌هایی در‌ آسمان‌ها [که مرا تا عرش الهی ببرد] تا به معبود موسی بنگرم [و از او آگاه شوم]؛ هر چند که من قطعاً او را دروغگو می‌پندارم». این‌گونه، زشتیِ کردارِ فرعون، [در نظرش] آراسته گشت و از راه [حق] بازماند؛ و نیرنگ فرعون، به زیان و تباهی انجامید.');
INSERT INTO "islamhouse" VALUES(40,38,'40:38','آن مرد مؤمن گفت: «ای قوم من، از من پیروی کنید تا شما را به راه راست هدایت کنم.');
INSERT INTO "islamhouse" VALUES(40,39,'40:39','ای قوم من، این زندگی دنیا، بهره‌ای [اندک و زودگذر] است؛ و آخرت، سرای جاودان است.');
INSERT INTO "islamhouse" VALUES(40,40,'40:40','هر ‌کس کار بدی مرتکب شود، به تناسبِ آن کیفر خواهد دید؛ و مردان و زنان مؤمن و نیکوکار، به بهشت وارد می‌شوند و در آنجا از نعمتِ بی‌شمار برخوردار خواهند شد.');
INSERT INTO "islamhouse" VALUES(40,41,'40:41','ای قوم من، چه شده كه من شما را به راه نجات دعوت مى‌كنم، و[لى] شما مرا به آتش فرامى‌خوانید؟');
INSERT INTO "islamhouse" VALUES(40,42,'40:42','مرا دعوت مى‌كنید كه الله را انكار كنم و چیزى را [در قدرت] با الله شریک گردانم كه هیچ اطلاعى از آن ندارم؛ در حالى كه من شما را به سوى [پروردگار] شکست‌ناپذیر و آمرزگار فرامى‌خوانم.');
INSERT INTO "islamhouse" VALUES(40,43,'40:43','قطعاً، آنچه مرا به آن دعوت مى‌دهید، در دنیا و آخرت [توان اجابت] دعایى را ندارد؛ و بازگشت ما به پیشگاه الله است، و اسرافكاران [در کفر و گناه،] دوزخى هستند.');
INSERT INTO "islamhouse" VALUES(40,44,'40:44','به زودى سخنان مرا به یاد خواهید آورد؛ و من كار خود را به الله واگذار مى‌كنم؛ زیرا الله نسبت به [کردارِ] بندگان بیناست».');
INSERT INTO "islamhouse" VALUES(40,45,'40:45','الله او را از آسیب دسیسۀ آنان حفظ نمود و عذاب سخت، دامنگیر فرعونیان شد.');
INSERT INTO "islamhouse" VALUES(40,46,'40:46','همان آتشی كه [در قبرهایشان] بامداد و شامگاه بر آن عرضه مى‌شوند؛ و روزی که قیامت برپا گردد، [فرمان می‌رسد که:] «فرعونیان را در سخت‌ترین عذاب درآورید».');
INSERT INTO "islamhouse" VALUES(40,47,'40:47','آنگاه كه در آتش دوزخ کشمکش [و مجادله] می‌کنند، مردمانِ زیردست به رهبران گمراهگر می‌گویند: «ما پیرو شما بودیم؛ آیا ممكن است بخشى از عذاب آتش را از ما دور کنید؟»');
INSERT INTO "islamhouse" VALUES(40,48,'40:48','رهبران گمراهگر می‌گویند: «بی‌تردید، همۀ ما در آتش خواهیم بود. الله میان بندگان [خود] داورى كرده است».');
INSERT INTO "islamhouse" VALUES(40,49,'40:49','و كسانى كه در آتشند، به نگهبانان دوزخ مى‌گویند: «از پروردگارتان بخواهید كه برای یک روز، این عذاب را از ما بردارد».');
INSERT INTO "islamhouse" VALUES(40,50,'40:50','آنان پاسخ مى‌دهند: «مگر پیامبرانتان نشانه‌هاى روشن بر شما ارائه نكردند؟ دوزخیان مى‌گویند: «بله»؛ و مأموران مى‌گویند: «پس [بى‌نتیجه] درخواست كنید که درخواست کافران، [مردود و] تباه است».');
INSERT INTO "islamhouse" VALUES(40,51,'40:51','بی‌گمان، ما پیامبران خویش و اهل ایمان را در زندگى دنیا و روزى كه گواهان [به شهادت] برمی‌خیزند، یارى مى‌كنیم.');
INSERT INTO "islamhouse" VALUES(40,52,'40:52','روزى كه عذرخواهىِ ستمگران سودى به حالشان نخواهد داشت؛ آنان از رحمت الهی به دورند و دشوارى عذاب آخرت را [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(40,53,'40:53','ما [مقام] هدایت به موسی بخشیدیم و بنی‌اسرائیل را وارثان تورات قرار دادیم؛');
INSERT INTO "islamhouse" VALUES(40,54,'40:54','[کتابی‌که] برای خردمندان، هدایت و پند است.');
INSERT INTO "islamhouse" VALUES(40,55,'40:55','پس [ای پیامبر، بر آزارِ مشرکان] شكیبا باش [كه] قطعاً وعدۀ الهی راست است و براى گناهت آمرزش بخواه و پروردگارت را شامگاه و بامداد، به پاکی ستایش کن.');
INSERT INTO "islamhouse" VALUES(40,56,'40:56','كسانى كه در مورد آیات الهی ـ بدون اینکه دلیلى در اختیارشان باشد ـ به مجادله برمى‌خیزند، در دل‌هایشان [غرور و] تکبری است كه هرگز به آن نخواهند رسید؛ پس [از شرِ آنان] به الله پناه ببر كه به راستی، او شنوای بیناست.');
INSERT INTO "islamhouse" VALUES(40,57,'40:57','مسلماً آفرینش آسمان‌ها و زمین بزرگ‌تر از آفرینشِ انسان‌هاست؛ [بنابر این برپاییِ قیامت، برای الله دشوار نیست]؛ ولی بیشتر مردم نمی‌دانند.');
INSERT INTO "islamhouse" VALUES(40,58,'40:58','نابینا و بینا هرگز برابر نیستند، و مؤمنان نیكوكار با بدكاران یكسان نخواهند بود؛ [ولى] عدۀ كمى از شما پند مى‌پذیرید.');
INSERT INTO "islamhouse" VALUES(40,59,'40:59','هیچ تردیدی در این نیست که روز قیامت فرامی‌رسد؛ ولی بیشتر مردم ایمان نمی‌آورند.');
INSERT INTO "islamhouse" VALUES(40,60,'40:60','پروردگارتان فرمود: «مرا به دعا بخوانید، تا [دعای] شما را اجابت کنم. کسانی که از عبادت من سرکشی می‌کنند، به زودی با خواری به جهنم وارد می‌شوند».');
INSERT INTO "islamhouse" VALUES(40,61,'40:61','الله است كه شب را برایتان پدید آورد تا در آن آرامش یابید و روز را روشنى‌بخش [قرار داد تا به تلاش پردازید]. به راستى، الله نسبت به مردم [لطف و] بخشش دارد؛ ولى بیشتر مردم سپاس نمی‌گزارند.');
INSERT INTO "islamhouse" VALUES(40,62,'40:62','این است الله، پروردگار شما [که] آفرینندۀ هر موجودی [است]. هیچ معبودی [به‌حق] جز او نیست؛ پس چگونه [از عبادت او] منحرف می‌شوید؟');
INSERT INTO "islamhouse" VALUES(40,63,'40:63','به همین صورت، كسانى كه آیات الهی را انكار مى‌كنند نیز [از حق] منحرف می‌گردند.');
INSERT INTO "islamhouse" VALUES(40,64,'40:64','الله است که زمین را قرارگاه و آسمان را سرپناهتان مقرر داشت و شما را صورتگرى كرد و نیكو صورتگرى كرد و از نعمت‌های پاكیزه به شما روزى داد؛ این است الله، پروردگار شما؛ پس پر برکت و بزرگوار است الله که پروردگار جهانیان است.');
INSERT INTO "islamhouse" VALUES(40,65,'40:65','الله زنده [و پاینده] است و معبودى [به‌حق] جز او نیست؛ پس او را با خلوص اعتقاد بخوانید. ستایش و سپاس مخصوص الله، پروردگار جهانیان است.');
INSERT INTO "islamhouse" VALUES(40,66,'40:66','[ای پیامبر،] بگو: «من از عبادت افرادى كه شما به جاى الله [به نیایش] مى‌خوانید، نهى شده‌ام؛ زیرا نشانه‌هاى روشنی از جانب پروردگارم به من رسیده است و فرمان یافته‌ام كه [تنها] در برابر پروردگار جهانیان تسلیم باشم».');
INSERT INTO "islamhouse" VALUES(40,67,'40:67','او تعالی ذاتی است كه شما را از خاک، و سپس [طى مراحل مختلف] از نطفه و آنگاه خون بسته [= جنین ابتدایى] آفرید؛ سپس شما را [به صورت] كودكى [به دنیا] مى‌آورد، و امكان مى‌دهد تا به حد رشد [و کمال نیرو] برسید؛ سپس [سال‌ها زندگی می‌کنید تا] سالخورده می‌شوید. برخی از شما دچار مرگِ زودرس خواهند شد و [به گروهى دیگر امكان می‌دهد] تا به اجل معیّن برسید؛ باشد که بیندیشید.');
INSERT INTO "islamhouse" VALUES(40,68,'40:68','او ذاتی است که زنده می‌کند و می‌میرانَد و هنگامی که چیزی را می‌خواهد، تنها به آن می‌گوید: «موجود شو»؛ پس [بی‌درنگ] موجود می‌شود.');
INSERT INTO "islamhouse" VALUES(40,69,'40:69','آیا ندیدی کسانی که دربارۀ آیات الهی مجادله می‌کنند، چگونه [از راه حق] منحرف می‌گردند؟');
INSERT INTO "islamhouse" VALUES(40,70,'40:70','کسانی که كتاب [آسمانى] و پیام پیامبران ما را دروغ شمردند، به زودى [نتیجۀ کردارشان را] خواهند دانست.');
INSERT INTO "islamhouse" VALUES(40,71,'40:71','آنگاه كه غل و زنجیر بر گردنشان قرار گرفته و كشیده می‌شوند.');
INSERT INTO "islamhouse" VALUES(40,72,'40:72','[ابتدا] در آب داغ [و] سپس در آتش دوزخ افروخته مى‌شوند.');
INSERT INTO "islamhouse" VALUES(40,73,'40:73','سپس به آنان گفته می‌شود: «چیزهایی که شریک [الله] قرار مى‌دادید، کجا هستند؟');
INSERT INTO "islamhouse" VALUES(40,74,'40:74','[همان معبودانی که آنها را] به جای الله [عبادت می‌کردید]. آنان گویند: «از [نظر] ما ناپدید شدند؛ گویی ما هرگز پیش از این چیزی را [به عبادت] نمی‌خواندیم». این‌گونه الله کافران را گمراه می‌کند.');
INSERT INTO "islamhouse" VALUES(40,75,'40:75','این [عذاب] از آن جهت است كه به ناحق در زمین شادمانى و سرمستى مى‌كردید و فخر مى‌فروختید.');
INSERT INTO "islamhouse" VALUES(40,76,'40:76','[اکنون] از در‌های جهنم وارد شوید و جاودانه در آنجا بمانید. و [به راستى که] جایگاه متكبران چه بد [جایگاهى] است!');
INSERT INTO "islamhouse" VALUES(40,77,'40:77','[ای پیامبر، بر آزار مشرکان] شکیبا باش که وعدۀ الهی حق است؛ و اگر برخى از مجازات‌هایی را كه به آنان وعده داده‌ایم، [در زندگی] به تو بنماییم یا [پیش از نزول عذاب،] جان تو را بگیریم، در هر حال، به پیشگاه ما بازگردانده مى‌شوند [و راه گریزی از عذاب ندارند].');
INSERT INTO "islamhouse" VALUES(40,78,'40:78','پیش از تو نیز پیامبرانی فرستاده‌ایم كه [سرگذشت] برخى از آنان را برایت حكایت كرده‌ایم و [سرگذشت] برخى دیگر را حكایت نكرده‌ایم؛ و هیچ رسولى نمی‌توانست بدون فرمان الهی معجزه‌اى ارائه كند؛ و آنگاه كه فرمان الله [براى پاداش و كیفر] فرارسد، به حق و عدالت داورى خواهد شد و باطل‌اندیشان زیانكار خواهند بود.');
INSERT INTO "islamhouse" VALUES(40,79,'40:79','الله است که چهارپایان را برایتان آفرید تا از برخى براى سوارى و از برخى براى تغذیه استفاده كنید.');
INSERT INTO "islamhouse" VALUES(40,80,'40:80','و سودهای دیگری نیز در آنها برایتان است؛ و [نیز] برای آنکه سوار بر آنها به مقصدی که در دل دارید برسید؛ و بر آنها و بر کشتی‌ها [به هر جا که می‌خواهید] منتقل می‌شوید.');
INSERT INTO "islamhouse" VALUES(40,81,'40:81','الله نشانه‌های [قدرت و تدبیرِ] خود را به شما نشان می‌دهد؛ پس کدام یک از نشانه‌های الهی را انکار می‌کنید؟');
INSERT INTO "islamhouse" VALUES(40,82,'40:82','آیا مشرکان در زمین نگشته‌اند تا ببینند سرانجامِ کسانی که پیش از آنان بودند، چگونه بوده است؟ آن اقوام گذشته، نفراتشان از اینها افزون‌تر بود و آثار و بناهای بیشتری در زمین پدید آورده بودند؛ اما [در هنگام نزول عذاب،] دستاورد آنان سودى به حالشان نداشت.');
INSERT INTO "islamhouse" VALUES(40,83,'40:83','هنگامى كه پیامبرانشان با نشانه‌هاى روشن به سراغشان آمدند، به دانش و كاردانىِ خود سرمست [و از توحید، رویگردان] شدند و همان عذابى كه آن را مسخره مى‌كردند، دامنگیرشان شد.');
INSERT INTO "islamhouse" VALUES(40,84,'40:84','هنگامی که عذاب ما را دیدند، گفتند: «تنها به الله ایمان آوردیم و معبودانی را که شریک او قرار داده بودیم، انکار می‌کنیم».');
INSERT INTO "islamhouse" VALUES(40,85,'40:85','اما ایمان‌آوردنشان به هنگام مشاهدۀ عذاب، هیچ سودی نداشت. این سنت الهی [که در هنگام عذاب، توبۀ کسی پذیرفته نمی‌گردد، همواره] در مورد بندگانِ الله جارى بوده است؛ و در این هنگام، کافران زیانكار شده‌اند.');
INSERT INTO "islamhouse" VALUES(41,1,'41:1','حم [حا. میم]');
INSERT INTO "islamhouse" VALUES(41,2,'41:2','[این قرآن،] از سوی الله بخشندۀ مهربان نازل شده است.');
INSERT INTO "islamhouse" VALUES(41,3,'41:3','کتابی است که آیاتش به زبان عربی، برای مردمی که می‌دانند، به شیوایی و روشنی بیان شده است.');
INSERT INTO "islamhouse" VALUES(41,4,'41:4','[کتابی] بشارت‌دهنده و بیم‌دهنده است؛ ولی بیشتر مردم رویگردانند و [حقایق را] نمی‌شنوند.');
INSERT INTO "islamhouse" VALUES(41,5,'41:5','مشرکان گفتند: «دل‌های ما نسبت به آنچه ما را دعوت می‌کنی در پوششی [از بى‌تفاوتى] قرار دارد و در گوش‌هایمان سنگینی است و میان ما و تو فاصله است؛ پس تو به کارِ خود مشغول باش و ما نیز به کارِ خود».');
INSERT INTO "islamhouse" VALUES(41,6,'41:6','[ای پیامبر، به آنان] بگو: «من فقط بشرى همچون شما هستم [با این تفاوت] كه به من وحى مى‌شود: «معبودِ شما، معبودى است یگانه؛ پس به او روى آورید و از او آمرزش بخواهید؛ و واى به حال مشركان!»');
INSERT INTO "islamhouse" VALUES(41,7,'41:7','همان کسانی که زكات نمى‌پردازند و آخرت را انكار مى‌كنند.');
INSERT INTO "islamhouse" VALUES(41,8,'41:8','[ولى] مؤمنانِ نیكوكار پاداشى بى‌پایان دارند.');
INSERT INTO "islamhouse" VALUES(41,9,'41:9','بگو: آیا به آن [خالقِ توانمندی] كه زمین را در دو روز آفرید، کفر می‌ورزید و همتایانى برایش قائل مى‌شوید؟ این [آفریدگار یکتاست که] پروردگار جهانیان است.');
INSERT INTO "islamhouse" VALUES(41,10,'41:10','او طى چهار روز، کوه‌های استواری بر روى زمین پدید آورد و خیر و بركتِ فراوان در آن نهاد و رزق و روزیِ آن را درست به ‌اندازۀ نیازِ تقاضا‌كنندگان مقدّر نمود.');
INSERT INTO "islamhouse" VALUES(41,11,'41:11','آنگاه به آسمان پرداخت، در حالى كه [هنوز به صورت] دودی بود؛ و به آسمان و زمین گفت: «خواه یا ناخواه، از درِ تسلیم درآیید». آن دو گفتند: «با میل، از درِ تسلیم درآمدیم».');
INSERT INTO "islamhouse" VALUES(41,12,'41:12','سپس آن تودۀ گاز را به صورت هفت آسمان، در دو روز آفرید و به هر آسمانى برنامه‌اش را وحى كرد؛ و آسمان دنیا را با چراغ‌هایى [از ستارگان] آراستیم و [آن را از شرِ شیاطین] حفظ نمودیم؛ این است تقدیر [پروردگارِ] شکست‌ناپذیرِ دانا.');
INSERT INTO "islamhouse" VALUES(41,13,'41:13','[ای پیامبر،] اگر آنان [از پذیرشِ اسلام] روی گرداندند، بگو: «من شما را از صاعقه‌اى مانند صاعقه [هلاکت قوم] عاد و ثمود هشدار مى‌دهم».');
INSERT INTO "islamhouse" VALUES(41,14,'41:14','آنگاه كه پیامبران الهی پیاپی به سراغشان می‌آمدند [با این پیام] كه: «جز الله را عبادت نکنید»، آنان [در پاسخ] می‌گفتند: «اگر پروردگارمان مى‌خواست [پیامِ هدایتگری بفرستد،] فرشتگانى نازل مى‌كرد. ما پیامى را كه شما مأمور [ابلاغ] آن هستید باور نمى‌كنیم».');
INSERT INTO "islamhouse" VALUES(41,15,'41:15','اما [قوم] عاد به ناحق در زمین تکبر می‌ورزیدند و می‌گفتند: «چه كسى نیرومندتر از ماست؟» آیا نمى‌دانستند الله [آن خالق یکتایی] كه آنان را آفریده، نیرومندتر از آنان است؟ ولى [با لجاجت] آیات ما را انكار مى‌كردند.');
INSERT INTO "islamhouse" VALUES(41,16,'41:16','سرانجام تندبادى سرد و سخت در روزهایى شوم بر آنان فرستادیم تا عذابِ رسوایى [و خفت] را در زندگى دنیا به آنان بچشانیم؛ ولى عذاب آخرت رسواكننده‌تر است و [از هیچ سو] یارى نخواهند شد.');
INSERT INTO "islamhouse" VALUES(41,17,'41:17','و اما قوم ثمود را نیز هدایت كردیم؛ ولى آنان كوردلى را به هدایت ترجیح دادند و به [سزاى] دستاوردشان، صاعقۀ عذابِ خفت‌بار، آنان را فراگرفت؛');
INSERT INTO "islamhouse" VALUES(41,18,'41:18','و كسانى را كه ایمان آورده و [از گناه و نافرمانی] پروا مى‌كردند، نجات دادیم.');
INSERT INTO "islamhouse" VALUES(41,19,'41:19','[ای پیامبر، یاد کن از] روزى كه دشمنان الله را به سوى آتشِ دوزخ احضار مى‌كنند و [از پراكندگى] بازمی‌دارند،');
INSERT INTO "islamhouse" VALUES(41,20,'41:20','تا هنگامی که به دوزخ می‌رسند، گوش و چشم و پوستشان به کارهایی که انجام می‌داده‌اند بر ضدّشان گواهى می‌دهند.');
INSERT INTO "islamhouse" VALUES(41,21,'41:21','آنان به پوست خود می‌گویند: «چرا علیه ما گواهی دادید؟» [در جواب] می‌گویند: «الله [همان خالق توانمندی] كه همه موجودات را گویا كرده، ما را نیز به سخن درآورده است؛ و اوست كه نخستین بار شما را آفرید و [سرانجام، همگی] به پیشگاه او بازگردانده مى‌شوید».');
INSERT INTO "islamhouse" VALUES(41,22,'41:22','شما كه [گناهانتان را] پنهان نمى‌كردید، نه از آن بابت بود كه تصور مى‌كردید گوش و چشم‌ و پوستتان علیه شما گواهى نمى‌دهند؛ بلكه گمان می‌کردید الله بر بسیارى از اعمالى كه مى‌كنید، آگاه نیست.');
INSERT INTO "islamhouse" VALUES(41,23,'41:23','این پندار شما دربارۀ پروردگارتان بود كه موجب هلاكت شما گردید و [سرانجام] زیانكار شدید.');
INSERT INTO "islamhouse" VALUES(41,24,'41:24','اگر شكیبایى و تحمل كنند [یا نكنند]، آتش جایگاهشان است؛ و اگر طلب رضایت [و آمرزش] كنند، پذیرفته نخواهد شد.');
INSERT INTO "islamhouse" VALUES(41,25,'41:25','ما همنشینانى [از شیاطین] بر آنان گماشتیم که [کارهای] گذشته و [آرزوهاى] آینده را در نظرشان آراستند؛ و [این‌گونه،] فرمان [عذاب] در مورد آنان تحقق یافت و به عذاب اقوام گمراهی از جن و انس که پیش از آنان بودند گرفتار شدند. بى‌گمان، آنان زیانكار بودند.');
INSERT INTO "islamhouse" VALUES(41,26,'41:26','کافران [که در برابر حق عاجز گشته بودند] گفتند: «به این قرآن گوش نکنید و در [هنگام تلاوت] آن، جنجال به راه اندازید [و سخنان بیهوده بگویید]؛ باشد که پیروز گردید».');
INSERT INTO "islamhouse" VALUES(41,27,'41:27','یقیناً عذاب سختی به کافران می‌چشانیم و آنان را بر اساس [جزای] بدترین کارهایشان کیفر می‌دهیم.');
INSERT INTO "islamhouse" VALUES(41,28,'41:28','این است سزاى دشمنان الله: آتشی كه در آن سكونتِ جاودان دارند؛ به كیفر آنكه آیات ما را انكار مى‌كردند.');
INSERT INTO "islamhouse" VALUES(41,29,'41:29','کافران [به هنگام عذاب] می‌گویند: «پروردگارا، [افرادى] از دو گروه جن و انس را كه عامل گمراهى‌مان بودند، به ما نشان بده که لگدمالشان كنیم تا از همه ذلیل‌تر شوند».');
INSERT INTO "islamhouse" VALUES(41,30,'41:30','[اما] كسانى كه گفتند: «پروردگار ما الله است» سپس [از عبادت و بندگى غیر او سر باز زدند و بر اعتقاد خود] پایداری کردند، [هنگام فرا رسیدن مرگشان] فرشتگان بر آنان فرود مى‌آیند، [و مى‌گویند:] «نترسید و ‌اندوه نداشته باشید؛ و شما را بشارت باد به بهشتی که به آن وعده داده می‌شدید!');
INSERT INTO "islamhouse" VALUES(41,31,'41:31','ما در زندگى دنیا و آخرت دوستدار شماییم، و در بهشت هرچه دلتان بخواهد و درخواست كنید، در اختیار شماست.');
INSERT INTO "islamhouse" VALUES(41,32,'41:32','به عنوان پذیرایى از جانب آمرزگارى مهربان».');
INSERT INTO "islamhouse" VALUES(41,33,'41:33','کیست خوش‌گفتارتر از آن کس كه به سوى الله دعوت می‌دهد و به شایستگى عمل می‌نماید و می‌گوید: «تسلیم [اوامر الهی] هستم؟»');
INSERT INTO "islamhouse" VALUES(41,34,'41:34','[هرگز] نیکی و بدی یکسان نیست. همواره به شیوه‌اى كه بهتر است، پاسخ بده كه در آن صورت، [خواهی دید] همان كسى كه بین تو و او دشمنى بود، همچون دوستى مهربان [و دلسوز] گشته است.');
INSERT INTO "islamhouse" VALUES(41,35,'41:35','تنها شكیبایان و کسانی كه بهرۀ بزرگی [از ایمان و اخلاق] دارند، به چنین مقامى مى‌رسند.');
INSERT INTO "islamhouse" VALUES(41,36,'41:36','و هر گاه وسوسه‌ای از جانب شیطان تو را فراگرفت، به الله پناه ببر كه یقیناً او شنوای داناست.');
INSERT INTO "islamhouse" VALUES(41,37,'41:37','از نشانه‌هاى [توان و تدبیر] او، شب و روز و خورشید و ماه است. در برابر خورشید و ماه سجده نكنید؛ بلكه اگر تنها او را عبادت می‌کنید، بر الله كه آنها را آفریده است سجده کنید.');
INSERT INTO "islamhouse" VALUES(41,38,'41:38','و اگر [گروه‌هایی از نیایش او تعالی] تكبر ورزیدند، [فرشتگان و] مقربان درگاه پروردگارت، شب و روز، بدون خستگى او را به پاکی می‌ستایند.');
INSERT INTO "islamhouse" VALUES(41,39,'41:39','از جمله نشانه‌هاى [قدرت] الهى این است كه زمین خشک و فرسوده را مى‌بینى که چون باران بر آن فرومی‌ریزیم، به جنبش درمی‌آید [و گیاه می‌رویاند] و [گیاهانش] رشد می‌كند؛ آن [پروردگار توانمندی] كه زمین را زنده مى‌كند، مردگان را نیز زنده خواهد كرد. به راستی كه او تعالی بر هر كارى تواناست.');
INSERT INTO "islamhouse" VALUES(41,40,'41:40','[گفتار و رفتارِ] كسانى كه در مورد آیات ما كجروى [و آن را تحریف] مى‌كنند بر ما پوشیده نخواهند بود. آیا كسی كه به آتش [دوزخ] ‌می‌افتد بهتر است یا کسی كه در روز رستاخیز [از عذاب] ایمن است؟ هر چه مى‌خواهید انجام دهید كه بی‌تردید، الله به آنچه مى‌كنید بیناست.');
INSERT INTO "islamhouse" VALUES(41,41,'41:41','کسانی كه این قرآن را كه براى [هدایت]شان آمده است، انكار كرده‌اند، [در قیامت عذاب خواهند شد]. این [کلامِ الهی،] كتابى است شكست‌ناپذیر.');
INSERT INTO "islamhouse" VALUES(41,42,'41:42','سخن باطل، نه از پیش رو و نه از پشت سرش [= از هیچ جهت و از هیچ نظری] به آن راه نمی‌یابد و از جانب [پروردگارِ] حکیمِ ستوده نازل شده است.');
INSERT INTO "islamhouse" VALUES(41,43,'41:43','[ای پیامبر، کافران] همان [سخنان باطلی] را به تو می‌گویند که به پیامبران پیش از تو نیز می‌گفتند. بى‌گمان، پروردگارت آمرزش و [در عین حال،] كیفرى دردناک دارد.');
INSERT INTO "islamhouse" VALUES(41,44,'41:44','اگر قرآن را [به زبانِ] غیرعربی نازل كرده بودیم، کافران مى‌گفتند: «چرا آیاتش [عربی نیست و] به روشنی بیان نشده است؟ کتابی غیرعربی و پیامبری عرب؟» [ای پیامبر، به آنان] بگو: «این كتاب، براى مؤمنان مایۀ هدایت و شفاست و کسانی كه ایمان نمی‌آورند، در گوش‌هایشان سنگینى است و قرآن برایشان نامفهوم است؛ [گویى] آنان را از راهى دور صدا می‌زنند».');
INSERT INTO "islamhouse" VALUES(41,45,'41:45','ما به موسى كتاب [تورات] دادیم؛ اما در [مورد] آن اختلاف[نظر] پدید آمد؛ و اگر فرمانى از پروردگارت [در موردِ مهلت و آزمایش] از پیش مقرر نشده بود، [بى‌درنگ] میانشان داورى مى‌شد. کافران [نیز] در موردِ قرآن در تردید و بدگمانى‌اند.');
INSERT INTO "islamhouse" VALUES(41,46,'41:46','هر کس کاری شایسته كند، به سود خودِ اوست؛ و هر کس بدى كند، به زیان خویش بد كرده است؛ و پروردگار تو هرگز به بندگان ستم نمی‌کند.');
INSERT INTO "islamhouse" VALUES(41,47,'41:47','آگاهى از [لحظۀ وقوع] قیامت، تنها به الله مربوط است؛ و خروجِ هر میوه‌اى از شكوفه‌اش و باردارى و وضع حملِ هر ماده‌اى، با آگاهىِ اوست؛ و روزی كه [الله] به مشركان ندا مى‌دهد: «افرادى كه [در قدرت،] شریک من تصور مى‌كردید [و به آنان توسل می‌جستید] كجا هستند؟» آنان می‌گویند: «اکنون در برابر تو اعتراف مى‌كنیم كه هیچ یک از ما [بر درستىِ اعتقاد مشرکانۀ خود] گواه نیست».');
INSERT INTO "islamhouse" VALUES(41,48,'41:48','آنچه در گذشته [تكیه‌گاه خود] مى‌خواندند، از نظرشان ناپدید می‌شود و درمى‌یابند كه هیچ راه گریزی [از آتش عذاب الهی] نخواهند داشت.');
INSERT INTO "islamhouse" VALUES(41,49,'41:49','انسان در تمناى خیر، خستگی‌ناپذیر است، و[لى] اگر بدى به او برسد، مأیوس و دلسرد خواهد شد.');
INSERT INTO "islamhouse" VALUES(41,50,'41:50','و اگر پس از رنجی كه به انسان مى‌رسد، رحمتى از جانب خویش به او برسانیم، [مغرورانه] مى‌گوید: «این حق من است، و گمان نمى‌كنم قیامت برپا شود، و اگر [هم واقع شود و] به سوى پروردگارم بازگردانده شوم، [ثروت و پاداش‌های] نیکویی نزدش خواهم داشت». مسلّماً کافران را از عملكردشان آگاه خواهیم ساخت و عذابى سخت به آنان می‌چشانیم.');
INSERT INTO "islamhouse" VALUES(41,51,'41:51','هرگاه نعمتی به انسان می‌بخشیم، [با سرمستى] روى مى‌گرداند [و شکر نعمت را به جای نمی‌آورد] و متکبرانه [از حق] دور می‌شود؛ و هنگامى كه رنجی [همچون فقر و بیماری] به سراغش می‌آید، [به درگاه الهی] دعا و درخواست فراوان دارد.');
INSERT INTO "islamhouse" VALUES(41,52,'41:52','[ای پیامبر، به کافران] بگو: «به من بگویید اگر [قرآن] از جانب الله باشد و شما به آن کافر شوید، کیست گمراه‌تر از کسی كه [با وجود آشکار بودنِ حق،] در ستیزی بى‌پایان [با آن] قرار دارد؟»');
INSERT INTO "islamhouse" VALUES(41,53,'41:53','[در این دنیا و در هنگامۀ قیامت،] نشانه‌هاى [توان و تدبیر] خود را در آفاق [جهان] و در [وجود] خودشان به آنان نشان خواهیم داد تا برایشان روشن شود كه قرآن حق است. آیا كافى نیست كه پروردگارت بر هر چیزی گواه است؟');
INSERT INTO "islamhouse" VALUES(41,54,'41:54','آگاه باشید كه آنان در مورد دیدار پروردگارشان [در قیامت] تردید دارند! [و] آگاه باشید كه الله بر همه چیز احاطه دارد!');
INSERT INTO "islamhouse" VALUES(42,1,'42:1','حم [حا. میم].');
INSERT INTO "islamhouse" VALUES(42,2,'42:2','عسق [عین. سین. قاف].');
INSERT INTO "islamhouse" VALUES(42,3,'42:3','الله شکست‌ناپذیر حکیم، بر تو و پیامبران پیش از تو چنین وحى مى‌فرستد.');
INSERT INTO "islamhouse" VALUES(42,4,'42:4','هرچه در آسمان‌ها و زمین است متعلق به اوست؛ و او بلندمرتبه و بزرگ است.');
INSERT INTO "islamhouse" VALUES(42,5,'42:5','نزدیک است آسمان‌ها [از شکوه و عظمت الهی] از فراز یکدیگر بشكافند، و فرشتگان، پروردگارشان را با ستایش تقدیس مى‌كنند و براى ساكنان زمین [كه توبه كرده‌اند] آمرزش مى‌طلبند. آگاه باشید كه الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(42,6,'42:6','[ای پیامبر،] الله خود مراقبِ [رفتارِ] كسانى است كه غیر او را [دوست و] کارسازِ خود برگزیده‌اند و تو كارگزار [و مسئول رفتار] آنان نیستى.');
INSERT INTO "islamhouse" VALUES(42,7,'42:7','ما قرآن را این گونه به زبان عربی بر تو وحى كردیم تا اهل مکه و ساكنان اطرافش را بیم دهى و در مورد روز محشر ـ که تردیدی در وقوعش نیست ـ به آنان هشدار دهى. [آن روز،] گروهى بهشتى‌اند و گروهى در آتش سوزان.');
INSERT INTO "islamhouse" VALUES(42,8,'42:8','اگر الله مى‌خواست، همۀ مردم را امتی واحد [که الله را به یگانگی عبادت کنند] قرار مى‌داد؛ ولى [به انسان اختیار داد؛ او تعالی] هر کس را بخواهد، به رحمت خویش درمی‌آورد؛ و مشرکان هیچ کارساز و یاورى نخواهند داشت.');
INSERT INTO "islamhouse" VALUES(42,9,'42:9','آیا آنان به جای او کسی [دیگر را به عنوان دوست و] کارساز برگزیده‌اند؟ حال آنكه كارساز [جهانیان] الله است و اوست كه مردگان را زنده مى‌كند و بر هر كارى تواناست.');
INSERT INTO "islamhouse" VALUES(42,10,'42:10','[بگو: «ای مردم،] هر اختلافى بین شماست، داورى‌اش با الله است. این است الله، پروردگار من؛ بر او توکل كرده‌ام و به پیشگاهش بازمی‌آیم».');
INSERT INTO "islamhouse" VALUES(42,11,'42:11','او آفرینندۀ آسمان‌ها و زمین است؛ همسرانى از [جنسِ] خود شما برایتان قرار داد و از چهارپایان نیز جفت آفرید؛ به این صورت، تعداد شما را افزایش مى‌دهد. هیچ چیز همانند الله نیست و همو شنوا و بیناست.');
INSERT INTO "islamhouse" VALUES(42,12,'42:12','کلید‌ [گنجینه‌های] آسمان‌ها و زمین از آنِ اوست؛ [نعمت و] روزی را بر هر کس که بخواهد، گسترده می‌دارد و یا تنگ می‌گرداند. به راستی که او بر هر چیزی داناست.');
INSERT INTO "islamhouse" VALUES(42,13,'42:13','از دین همان را براى شما مقرر كرد كه به نوح امر كرده بود؛ و نیز آنچه به تو وحى كردیم و آنچه به ابراهیم و موسى و عیسى امر كردیم؛ اینكه: «دین را برپا نگاه دارید و در مورد آن فرقه‌فرقه نشوید [و اختلاف نورزید]». [پذیرشِ] دعوت تو بر مشركان گران آمده است. الله است كه هر كس را بخواهد [توفیقِ عبادت می‌دهد و] به سوى خویش برمى‌گزیند؛ و هر کس را [که توبه کند و به درگاهش] بازآید، به سوى خویش هدایت خواهد كرد.');
INSERT INTO "islamhouse" VALUES(42,14,'42:14','هنگامی که دلایل محکمِ نبوتِ پیامبر بر کافران عرضه شد، آنان از روی برترى‌خواهى [و حسادتی] كه بینشان بود، راه تفرقه پیمودند؛ و اگر فرمان پروردگارت [در موردِ مهلت و تأخیرِ عذاب] تا زمانى معین، از پیش مقرر نشده بود، [بی‌درنگ] میانشان داورى مى‌شد؛ و اما پس از کافران [و به دلیل انكار و اختلاف آنان] وارثان كتاب [تورات و انجیل نیز] در مورد قرآن، سخت در تردید و بدگمانى‌ هستند.');
INSERT INTO "islamhouse" VALUES(42,15,'42:15','[ای پیامبر، مردم را] به سوی این [آیین الهی] دعوت کن و چنان كه فرمان یافته‌اى پایداری كن و از هوس‌های آنان پیروى نكن و بگو: «به همۀ کتاب‌هایی كه الله نازل كرده است ایمان دارم و فرمان یافته‌ام كه در برابر شما به عدالت رفتار كنم. الله پروردگار ما و شماست. [نتیجۀ] اعمال ما براى ماست و [نتیجۀ] اعمال شما براى شما؛ هیچ [دشمنی و] جدالی بین ما و شما نیست. الله همۀ ما را [در قیامت] جمع خواهد كرد و بازگشت [همه] به سوی اوست».');
INSERT INTO "islamhouse" VALUES(42,16,'42:16','كسانى كه دربارۀ الله [و دین او] پس از آن كه [دعوت او از سوی مردم منصف] پذيرفته شد، به مجادله مى‌پردازند، حجّتشان نزد پروردگارشان [باطل و] بى‌پایه است؛ خشم [الهی] گریبانگیرشان مى‌شود و عذابى سخت [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(42,17,'42:17','الله است که قرآن و مقیاس عدالت را به‌حق نازل كرده است؛ و تو چه می‌دانی؟ شاید قیامت نزدیک باشد.');
INSERT INTO "islamhouse" VALUES(42,18,'42:18','كسانى كه قیامت را باور ندارند، [با تمسخر و] به شتاب آن را مى‌خواهند؛ و كسانى كه [به روزِ جزا] باور دارند و مى‌دانند كه حق است، از آن هراسانند. آگاه باشید! کسانی كه در مورد قیامت، جدل و انكار مى‌كنند، در گمراهىِ بى‌پایانى قرار دارند.');
INSERT INTO "islamhouse" VALUES(42,19,'42:19','الله نسبت به بندگانش باریک‌بین [و مهربان] است؛ به هر کس بخواهد، روزی می‌بخشد؛ و او توانای شکست‌ناپذیر است.');
INSERT INTO "islamhouse" VALUES(42,20,'42:20','کسی كه كِشت آخرت را بخواهد، به كِشته‌اش بركت مى‌بخشیم؛ و هر کس [تنها] كِشت دنیا را بخواهد، در اختیار او قرار مى‌دهیم؛ و[لى] در آخرت بهره‌اى نخواهد داشت.');
INSERT INTO "islamhouse" VALUES(42,21,'42:21','آیا [مشرکان] معبودانى دارند كه بدون اجازۀ الله، [قوانین] دینى برایشان مقرر كرده‌اند؟ اگر حُكمِ قطعى [براى مهلت دادن به گناهکاران] در میان نبود، [بى‌درنگ] میانشان داورى مى‌شد؛ و ستمکاران عذابی دردناک [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(42,22,'42:22','[در روز قیامت،] ستمکاران را از دستاوردشان هراسان مى‌بینى و [خواهی دید که كیفر آن کارها] دامنگیرشان است؛ ولى مؤمنان نیكوكار در باغ‌های بهشتند؛ هر چه بخواهند، نزد پروردگارشان دارند؛ فضل [و بخششِ] بزرگ همین است.');
INSERT INTO "islamhouse" VALUES(42,23,'42:23','این همان [فضیلتی] است كه الله به بندگان مؤمن و نیكوكارش بشارت مى‌دهد. [ای پیامبر،] بگو: «در برابر این رسالت، پاداشى از شما نمى‌خواهم؛ جز [رعایتِ دوستی و] محبتِ خویشاوندى؛ و هر کس [در این راستا] اقدام نیكویى كند، بر پاداشش در آن [مورد] مى‌افزاییم. به راستی كه الله آمرزگارى قدرشناس است.');
INSERT INTO "islamhouse" VALUES(42,24,'42:24','آیا مشرکان مى‌گویند: «[محمد] به الله دروغ نسبت داده است؟» اگر الله بخواهد، بر دل تو مُهر مى‌نهد؛ و[لى] الله باطل را محو مى‌كند و حق را با سخنان [و تمهیداتِ] خویش به كرسى مى‌نشانَد؛ كه او به راز دل‌ها داناست.');
INSERT INTO "islamhouse" VALUES(42,25,'42:25','اوست که توبۀ بندگانش را می‌پذیرد و از بدی‌ها درمى‌گذرد و آنچه مى‌كنید مى‌داند.');
INSERT INTO "islamhouse" VALUES(42,26,'42:26','او [درخواستِ] مؤمنانِ نیكوكار را مى‌پذیرد و از فضلِ خویش بر [پاداش] آنان مى‌افزاید؛ و کافران عذابى سخت [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(42,27,'42:27','اگر الله روزىِ [برخى از] بندگانش را گشایش مى‌داد، در زمین ستم و سركشى مى‌كردند؛ ولى به ‌اندازه‌اى كه بخواهد مقرر مى‌دارد؛ زیرا او قطعاً به [حال] بندگانش آگاه و بیناست.');
INSERT INTO "islamhouse" VALUES(42,28,'42:28','او ذاتی است كه باران را پس از نومیدى [مردم] مى‌فرستد و رحمت خویش را مى‌گسترد؛ و او تعالی كارساز و ستوده است.');
INSERT INTO "islamhouse" VALUES(42,29,'42:29','از نشانه‌هاى [توان و تدبیر] او، آفرینش آسمان‌ها و زمین و موجودات زنده‌اى است كه در آنها پراكنده كرده است؛ و هر گاه اراده كند، بر جمع‌آورى آنها نیز تواناست.');
INSERT INTO "islamhouse" VALUES(42,30,'42:30','هر مصیبتی که به شما رسد، به خاطر کارهایی است که انجام داده‌اید؛ و [الله] از بسیاری [از گناهانتان] گذشت می‌کند.');
INSERT INTO "islamhouse" VALUES(42,31,'42:31','شما هرگز در زمین [و آسمان، الله را] به تنگ نمى‌آورید؛ و در برابر الله هیچ كارساز و یاورى نخواهید داشت.');
INSERT INTO "islamhouse" VALUES(42,32,'42:32','از نشانه‌های [توان و تدبیرِ] الهی، کشتی‌های کوه‌پیکری است که در دریاها در حرکتند.');
INSERT INTO "islamhouse" VALUES(42,33,'42:33','اگر الله بخواهد، باد را ساكن مى‌گردانَد تا [کشتی‌ها] بر پهنۀ دریا بى‌حركت بمانند. به راستى در این [امر،] براى هر شكیباى سپاسگزاری، نشانه‌هایى است.');
INSERT INTO "islamhouse" VALUES(42,34,'42:34','یا [اگر بخواهد، سرنشینانِ] آن کشتی‌ها را به کیفر اعمالشان نابود می‌سازد؛ در حالی که او از بسیاری [از گناهان] ‌گذشت می‌کند.');
INSERT INTO "islamhouse" VALUES(42,35,'42:35','[تا موجب عبرت باشد] و كسانى كه در مورد آیات ما به مجادله برمى‌خیزند، بدانند كه [اگر عذاب الهی فرارسد،] راه گریزى نخواهند داشت.');
INSERT INTO "islamhouse" VALUES(42,36,'42:36','[ای مردم،] آنچه به شما داده شده است، نعمت‌های [گذراىِ] زندگى دنیاست؛ و آنچه نزد الله است، براى مؤمنانى كه بر پروردگارشان توکل مى‌كنند، بهتر و پاینده‌تر است.');
INSERT INTO "islamhouse" VALUES(42,37,'42:37','و [نیز براى] کسانی كه از گناهان بزرگ و بی‌حیایی‌ها اجتناب می‌ورزند و آنگاه كه خشمگین می‌شوند، گذشت مى‌كنند.');
INSERT INTO "islamhouse" VALUES(42,38,'42:38','و کسانی كه [دعوت] پروردگارشان را پذیرا مى‌شوند و نماز برپا مى‌دارند و كارشان در ميانشان [بر پايه‌] مشورت است و از آنچه روزى‌شان كرده‌ایم، انفاق مى‌كنند.');
INSERT INTO "islamhouse" VALUES(42,39,'42:39','و کسانی که هر گاه ستمی به آنان برسد، [به دفاع و] انتقام برمی‌خیزند.');
INSERT INTO "islamhouse" VALUES(42,40,'42:40','و جزای هر بدی، کیفری همانند آن بدی است [نه بیشتر]؛ اما هر کس گذشت کند و کار را به اصلاح [و آشتی] آورَد، پاداشش بر عهدۀ الله است. بی‌گمان، او ستمکاران را دوست ندارد.');
INSERT INTO "islamhouse" VALUES(42,41,'42:41','البته کسانی كه پس از ستمى كه دیده‌اند، انتقام می‌گیرند، هیچ [بازخواست و] سرزنشی بر آنان نیست.');
INSERT INTO "islamhouse" VALUES(42,42,'42:42','راه [بازخواست و] سرزنش، تنها بر ستمکارانى گشوده است كه به ناحق در زمین برترى‌جویی مى‌كنند. آنان عذابى دردناک [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(42,43,'42:43','اما هر کس شكیبایى و گذشت كند، نشان از قدرت ارادۀ [او] دارد.');
INSERT INTO "islamhouse" VALUES(42,44,'42:44','هر کس که الله او را گمراه كند، پس از الله هیچ [یاور و] كارسازى نخواهد داشت؛ و [در روز قیامت،] ستمکاران را مى‌بینى كه چون عذاب را مشاهده كنند، مى‌گویند: «آیا راه بازگشتى [به دنیا] هست؟»');
INSERT INTO "islamhouse" VALUES(42,45,'42:45','[در آن روز،] ستمگران را مشاهده خواهى كرد كه بر آتش عرضه مى‌شوند؛ و در حالى كه از [شدت] خواری، فروتن شده‌اند، زیرچشمى مى‌نگرند؛ و [در آن حال] مؤمنان مى‌گویند: «زیانكاران [واقعى] كسانى هستند كه [به طمع دنیا] خود و خانواده‌شان را در قیامت به زیان‌انداخته‌اند». آگاه باشید! ستمکاران، گرفتارِ عذابى پایدارند.');
INSERT INTO "islamhouse" VALUES(42,46,'42:46','و آنان در برابر الله، كارسازى نخواهند داشت كه یارى‌شان كند؛ و هر کس كه الله او را گمراه كند، راهى [براى نجات] نخواهد داشت.');
INSERT INTO "islamhouse" VALUES(42,47,'42:47','[ای مردم،] پیش از آنكه روزى فرارسد كه بازگشتى از جانب الله ندارد، دعوت پروردگارتان را بپذیرید. در آن روز، نه پناهگاهى خواهید داشت، نه [فرصتی برای] انكار.');
INSERT INTO "islamhouse" VALUES(42,48,'42:48','[ای پیامبر] اگر [مشرکان از دعوتِ تو] روى می‌گردانند، [توفیق را از خود سلب می‌كنند]. ما تو را نگهبان آنان نفرستادیم و تو وظیفه‌اى جز رساندن [وحی] نداری؛ و چون رحمتى از جانب خود به انسان بچشانیم، به آن سرمست مى‌شود؛ و اگر به سزاى رفتار گذشتۀ خود دچار مصیبتى شود، [به جاى بیدارى، ناسپاسی مى‌کند]. به راستی که انسان بسیار ناسپاس است.');
INSERT INTO "islamhouse" VALUES(42,49,'42:49','فرمانروایى آسمان‌ها و زمین، از آنِ الله است. او هر چه بخواهد، مى‌آفریند؛ به هر کس که بخواهد، [فرزندِ] دختر مى‌بخشد و به هر کس كه بخواهد، [فرزندِ] پسر.');
INSERT INTO "islamhouse" VALUES(42,50,'42:50','یا پسر و دختر را ـ هر دو ـ با هم می‌دهد؛ و هر کس را که بخواهد، نازا مى‌گرداند كه او دانای تواناست.');
INSERT INTO "islamhouse" VALUES(42,51,'42:51','درخورِ هیچ بشرى نیست كه الله با او سخن بگوید؛ مگر با وحى یا از فراسویِ حجابى یا [اینکه الله] فرشته‌اى بفرستد كه به فرمانش هر چه بخواهد، وحى كند. بی‌تردید، او تعالی بلندمرتبۀ حکیم است.');
INSERT INTO "islamhouse" VALUES(42,52,'42:52','[ای پیامبر] این‌گونه، قرآن را [توسط جبرئیل و] به فرمان خویش بر تو وحى كردیم. [پیش از آن] تو آگاهى نداشتى كه كتاب و ایمان چیست؛ ولى ما آن را نورى ساختیم كه هر یک از بندگان خویش را كه بخواهیم، به آن هدایت كنیم؛ و مسلّماً تو به راهى راست [= اسلام] هدایت مى‌كنى.');
INSERT INTO "islamhouse" VALUES(42,53,'42:53','راه الله که آنچه در آسمان‌ها و زمین است از آنِ اوست. آگاه باشید! [همۀ] امور به سوی الله بازمی‌گردد.');
INSERT INTO "islamhouse" VALUES(43,1,'43:1','حم [حا. میم].');
INSERT INTO "islamhouse" VALUES(43,2,'43:2','سوگند به کتاب روشنگر.');
INSERT INTO "islamhouse" VALUES(43,3,'43:3','ما آن را قرآنى به زبان عربی قرار دادیم، باشد که خردورزى كنید.');
INSERT INTO "islamhouse" VALUES(43,4,'43:4','این كتاب، كه بلندمرتبه و حكمت‌آموز است، در لوح محفوظ نزد ما ثبت شده است [و دستخوش تغییر نخواهد شد].');
INSERT INTO "islamhouse" VALUES(43,5,'43:5','آیا به صِرف اینكه شما [مشرکان،] گروهی اسرافکارید [و پیام الهی را انكار مى‌كنید، نزول] قرآن را از شما بازداریم؟');
INSERT INTO "islamhouse" VALUES(43,6,'43:6','چه بسیار پیامبرانى كه در میان پیشینیان فرستادیم.');
INSERT INTO "islamhouse" VALUES(43,7,'43:7','و[لی] هر پیامبری به سوی آنان می‌آمد، مسخره‌اش می‌کردند.');
INSERT INTO "islamhouse" VALUES(43,8,'43:8','ما افرادی نیرومند‌تر از مشرکان مکه را هلاک کرده‌ایم؛ و داستان [عبرت‌انگیز] پیشینیان گذشت.');
INSERT INTO "islamhouse" VALUES(43,9,'43:9','[ای پیامبر] اگر از مشرکان بپرسی: «آسمان‌ها و زمین را چه کسی آفریده است؟»، قطعاً می‌گویند: «[الله] شکست‌ناپذیر دانا».');
INSERT INTO "islamhouse" VALUES(43,10,'43:10','همان [پروردگاری] كه زمین را محل آسایش شما قرار داد و راه‌هایی در آن برایتان پدید آورد تا راه یابید.');
INSERT INTO "islamhouse" VALUES(43,11,'43:11','و همان ذاتی كه باران را از آسمان به مقدار معینى فرستاد؛ آنگاه سرزمین خزان‌زده و خشک را به وسیلۀ آن حیات بخشیدیم؛ شما نیز همین گونه [از گورها] بیرون آورده مى‌شوید.');
INSERT INTO "islamhouse" VALUES(43,12,'43:12','پروردگاری كه تمام مخلوقات را آفرید و از کشتی‌ها و چهارپایان، وسیلۀ سوارى برایتان پدید آورد؛');
INSERT INTO "islamhouse" VALUES(43,13,'43:13','تا بر پشت آنها قرار گیرید و آنگاه كه بر آنها قرار گرفتید، نعمت پروردگارتان را یاد كنید و بگویید: «پاک و منزه است ذاتی كه این [وسیله] را در خدمت ما گماشت و[گرنه] ما خود توان این کار را نداشتیم؛');
INSERT INTO "islamhouse" VALUES(43,14,'43:14','و ما به پیشگاهِ پروردگار‌مان بازمی‌گردیم».');
INSERT INTO "islamhouse" VALUES(43,15,'43:15','مشرکان از میان بندگان الله، سهمى [از فرزند] برایش قرار دادند. به راستى كه انسان آشكارا ناسپاس است.');
INSERT INTO "islamhouse" VALUES(43,16,'43:16','آیا [می‌پندارید] الله از میان آفریدگانش، دختران را انتخاب كرده و پسران را به شما اختصاص داده است؟');
INSERT INTO "islamhouse" VALUES(43,17,'43:17','اما هر گاه به یكى از آنان، همان چیزی را بشارت دهند که براى [الله] رحمان قائل هستند [= تولد دختر]، در حالى كه خشم خود را فرومى‌خورد، چهره‌اش [از اندوه] تیره مى‌گردد.');
INSERT INTO "islamhouse" VALUES(43,18,'43:18','آیا کسی را که در زیور [و راحتی] پرورش می‌یابد و در [هنگام] جدال [قادر به] تبیین مقصودش نیست [به الله نسبت می‌دهید؟]');
INSERT INTO "islamhouse" VALUES(43,19,'43:19','[مشرکان]، فرشتگان را كه بندگانِ [الله] رحمانند، مؤنث پنداشتند. آیا به هنگام آفرینش آنان حضور داشتند؟ سخنانشان ثبت می‌گردد و [در روز قیامت،] بازخواست خواهند شد.');
INSERT INTO "islamhouse" VALUES(43,20,'43:20','[آنان برای توجیهِ شرکِ خود] می‌گویند: «اگر [الله] رحمان می‌خواست، ما فرشتگان را عبادت نمی‌کردیم». آنان در مورد این [سخن] بى‌اطلاعند [و] جز سخن دروغ نمى‌گویند.');
INSERT INTO "islamhouse" VALUES(43,21,'43:21','آیا پیش از قرآن كتابى به آنان داده‌ایم كه به آن استدلال كنند؟');
INSERT INTO "islamhouse" VALUES(43,22,'43:22','بلکه می‌گویند: «نیاکانمان نیز همین آیین را داشتند و ما [نیز] راه آنان را دنبال می‌کنیم».');
INSERT INTO "islamhouse" VALUES(43,23,'43:23','پیش از تو نیز هر [پیامبرِ] هشداردهنده‌ای را به هر شهری فرستادیم، ثروتمندانِ سرکشِ آن شهر می‌گفتند: «نیاکانمان نیز همین آیین را داشتند و ما [نیز] راه آنان را دنبال می‌کنیم».');
INSERT INTO "islamhouse" VALUES(43,24,'43:24','[پیامبر‌شان] می‌گفت: «اگر آیینى هدایت‌بخش‌تر از آنچه نیاکانتان داشتند آورده باشم [باز هم از آنان پیروی می‌کنید]؟» آنان [در پاسخ] می‌گفتند: «[آری،] پیامى را كه مأمور [ابلاغ] آن شده‌اید، انكار مى‌كنیم».');
INSERT INTO "islamhouse" VALUES(43,25,'43:25','ما نیز از آنان انتقام گرفتیم؛ پس بنگر که سرانجامِ تكذیب‌كنندگان چگونه بود.');
INSERT INTO "islamhouse" VALUES(43,26,'43:26','[یاد کن] هنگامی را ‌که ابراهیم به پدر و قومش گفت: «از آنچه شما عبادت می‌کنید، بیزارم؛');
INSERT INTO "islamhouse" VALUES(43,27,'43:27','مگر از ذاتی كه مرا آفریده است كه او هدایتم خواهد كرد».');
INSERT INTO "islamhouse" VALUES(43,28,'43:28','ابراهیم، یكتاپرستى را در میانِ نسل‌های پس از خود، سخنى ماندگار قرار داد؛ باشد که [به پیشگاه الهی] بازگردند.');
INSERT INTO "islamhouse" VALUES(43,29,'43:29','بلکه اینان [که در مکه آلوده به شرک هستند] و پدرانشان را [از نعمت‌های دنیا] برخوردار كردم تا آنكه حق [= قرآن] و پیامبری روشنگر به سراغشان آمد؛');
INSERT INTO "islamhouse" VALUES(43,30,'43:30','و آنگاه كه حق [= قرآن] به سویشان آمد، گفتند: «این جادوست و آن را انكار مى‌كنیم»؛');
INSERT INTO "islamhouse" VALUES(43,31,'43:31','و گفتند: «چرا این قرآن بر مردى بزرگ از دو شهرِ [مکه و طائف] نازل نشده است؟»');
INSERT INTO "islamhouse" VALUES(43,32,'43:32','آیا آنان رحمت پروردگارت را تقسیم مى‌كنند؟ ماییم كه روزی [و معیشت] آنان را در زندگى دنیا میانشان تقسیم كرده‌ایم و آنان را نسبت به هم، به درجاتى برتری بخشیده‌ایم تا از خدماتِ [متقابلِ] یكدیگر استفاده كنند؛ و رحمت پروردگارت از اموالى كه جمع مى‌كنند بهتر است.');
INSERT INTO "islamhouse" VALUES(43,33,'43:33','اگر [نگرانى از آن] نبود كه همۀ مردم [در دنیاطلبى و انكار الله] یك گروه ‌شوند، سقف خانۀ كسانى را كه به الله رحمان ایمان ندارند، از نقره قرار مى‌دادیم و نیز پله‌هایى [از نقره در آن می‌نهادیم] كه بتوانند از [طبقاتِ] آن [خانه] بالا روند؛');
INSERT INTO "islamhouse" VALUES(43,34,'43:34','و برای خانه‌هایشان در‌هایی [از طلا] می‌گذاشتیم و تخت‌هایی [قرار می‌دادیم] که بر آن تکیه زنند؛');
INSERT INTO "islamhouse" VALUES(43,35,'43:35','و تجمل [و زینت‌های] فراوان [برایشان فراهم می‌کردیم] و تمام اینها بهرۀ [ناچیز] زندگی دنیاست؛ و آخرت نزد پروردگارت برای پرهیزگاران است.');
INSERT INTO "islamhouse" VALUES(43,36,'43:36','هر کس از یاد [الله] رحمان دل بگرداند، شیطانى بر او مى‌گماریم تا [یار و] همنشینش باشد [و همواره او را فریب دهد].');
INSERT INTO "islamhouse" VALUES(43,37,'43:37','شیاطین، این گروه را از راه [الله] بازمی‌دارند؛ و[لی] آنان گمان می‌کنند که هدایت‌یافته‌اند.');
INSERT INTO "islamhouse" VALUES(43,38,'43:38','تا آنگاه كه [در قیامت] نزد ما بیاید، [به شیطانِ همنشینش] می‌گوید: ای كاش میان من و تو، فاصله‌اى به دورى شرق و غرب بود! چه همنشین بدی بودى!».');
INSERT INTO "islamhouse" VALUES(43,39,'43:39','[الله می‌فرماید:] «امروز [پشیمانى] سودى به حالتان نخواهد داشت؛ زیرا ستم كرده‌اید و همگی در عذاب شریكید».');
INSERT INTO "islamhouse" VALUES(43,40,'43:40','[ای پیامبر،] آیا می‌توانی [پیام خود را] به گوش ناشنوایان برسانى یا نابینایان و گمراهانِ آشكار را [به راه راست] هدایت كنى؟');
INSERT INTO "islamhouse" VALUES(43,41,'43:41','پس اگر تو را [پیش از عذاب آنان از دنیا] ببریم، [در هر حال،] از آنان انتقام خواهیم گرفت.');
INSERT INTO "islamhouse" VALUES(43,42,'43:42','یا عذابى را كه به آنان وعده داده‌ایم، [در دوران زندگی‌ات] به تو نشان خواهیم داد؛ زیرا ما بر آنان اقتدار [کامل] داریم.');
INSERT INTO "islamhouse" VALUES(43,43,'43:43','به آنچه بر تو وحی می‌شود تمسّک بجوی که بی‌گمان، بر راه راست قرار داری.');
INSERT INTO "islamhouse" VALUES(43,44,'43:44','تردیدی نیست که قرآن برای تو و قومت مایۀ اندرز [و سربلندی] است و [روز قیامت، دربارۀ ایمان و عمل به آن] بازخواست خواهید شد.');
INSERT INTO "islamhouse" VALUES(43,45,'43:45','از پیامبرانی که پیش از تو فرستادیم بپرس که آیا غیر از [الله] رحمان، معبودانی برای عبادت قرار داده‌ایم [مسلّماً چنین نیست].');
INSERT INTO "islamhouse" VALUES(43,46,'43:46','ما موسى را با معجزات خویش به سوى فرعون و سران قومش فرستادیم و به آنان گفت: «من فرستادۀ پروردگار جهانیانم»؛');
INSERT INTO "islamhouse" VALUES(43,47,'43:47','و هنگامی که آیات [و معجزات] ما را به آنان ارائه كرد، همگی به آنها خندیدند.');
INSERT INTO "islamhouse" VALUES(43,48,'43:48','هر معجزه‌اى به آنان ارائه دادیم، یكى از دیگرى بزرگ‌تر بود و [چون همه را تكذیب كردند،] آنان را به عذاب گرفتار كردیم؛ باشد که [از کفر] بازگردند.');
INSERT INTO "islamhouse" VALUES(43,49,'43:49','[پس هنگامی که گوشه‌ای از عذاب الهی را چشیدند،] گفتند: «اى جادوگر! از پروردگارت ـ بنا به عهدى كه با تو دارد ـ بخواه [تا عذاب را از ما دور كند] كه ما [دیگر] راه‌یافته‌ایم».');
INSERT INTO "islamhouse" VALUES(43,50,'43:50','اما هنگامی که عذاب را از آنان برداشتیم، پیمان شكستند [و بر کفر اصرار ورزیدند].');
INSERT INTO "islamhouse" VALUES(43,51,'43:51','فرعون در میان قوم خود ندا سر داد و گفت: «اى قوم من، آیا فرمانروایىِ مصر در اختیار من نیست و این نهرها از دامنۀ قصرِ من جریان ندارد؟ آیا [قدرت و عظمتم را] نمى‌بینید؟');
INSERT INTO "islamhouse" VALUES(43,52,'43:52','من از این مردِ بى‌مقدار که [حتی] نمى‌تواند به روشنى سخن بگوید، شایسته‌ترم.');
INSERT INTO "islamhouse" VALUES(43,53,'43:53','چرا [به نشانۀ سروَرى،] دستبند‌هاى زرین [از آسمان] بر او افكنده نشده است [تا ثابت شود که پیامبر است]؟ یا چرا فرشتگان همراه او نیامده‌اند؟»');
INSERT INTO "islamhouse" VALUES(43,54,'43:54','او [= فرعون] قوم خود را به [بی‌خردی و] سبُكسَرى كشاند و همگی مُطیع او شدند. حقا که آنان [نسبت به اوامر الهی] نافرمان بودند.');
INSERT INTO "islamhouse" VALUES(43,55,'43:55','پس چون ما را به خشم آوردند، از آنان انتقام گرفتیم و همگی را [در دریا] غرق کردیم.');
INSERT INTO "islamhouse" VALUES(43,56,'43:56','آنگاه آنان را در شمارِ پیشگامان [کفر و نافرمانی] و عبرتی [برای آیندگان] قرار دادیم.');
INSERT INTO "islamhouse" VALUES(43,57,'43:57','و هنگامی که [داستانِ تولد] پسر مریم مطرح شد، قومِ تو [که گمان ‌کردند می‌توانند مانند نصاری چند معبود داشته باشند] به ناگاه، شادی و هیاهو به راه‌ انداختند.');
INSERT INTO "islamhouse" VALUES(43,58,'43:58','و گفتند: «معبودان ما بهترند یا او [= عیسی]؟» آنان این سخن را تنها براى جدال با تو عنوان كردند؛ به راستی که گروهى ستیزه‌جو هستند.');
INSERT INTO "islamhouse" VALUES(43,59,'43:59','عیسى فقط بنده‌اى است كه نعمت [رسالت] بر او ارزانى داشتیم و او را نمونه‌ای [از قدرت الهی] براى بنی‌اسرائیل قرار دادیم.');
INSERT INTO "islamhouse" VALUES(43,60,'43:60','اگر مى‌خواستیم، [همۀ شما انسان‌ها را نابود می‌کردیم و] به جای شما، فرشتگانى پدید مى‌آوردیم كه در زمین جانشین [شما] شوند.');
INSERT INTO "islamhouse" VALUES(43,61,'43:61','قطعاً [نزول] او [= عیسی] نشانه [نزدیکی وقوع] قیامت است؛ پس هرگز در مورد قیامت شک نكنید و پیرو من باشید كه راه راست [به سوی بهشت،] همین است.');
INSERT INTO "islamhouse" VALUES(43,62,'43:62','[آگاه باشید تا] شیطان شما را [از راه الله] بازندارد؛ چرا كه او دشمن آشكار شماست.');
INSERT INTO "islamhouse" VALUES(43,63,'43:63','و چون عیسى [معجزات و] نشانه‌هاى روشن به آنان ارائه كرد، گفت: «من حکمت برایتان آورده‌ام تا [به آن راه یابید] و برخى از موارد اختلافتان را روشن كنم؛ پس از الله پروا کنید و مطیع [دستورهای] من باشید.');
INSERT INTO "islamhouse" VALUES(43,64,'43:64','الله پروردگار من و شماست؛ پس او را عبادت کنید که راهِ راست همین است».');
INSERT INTO "islamhouse" VALUES(43,65,'43:65','آنگاه گروه‌های [مختلف] بنی‌اسرائیل میان خود [دربارۀ عیسی] دچار اختلاف شدند؛ پس وای بر ستمکاران از عذاب روز دردناک!');
INSERT INTO "islamhouse" VALUES(43,66,'43:66','آیا جز این انتظار دارند كه قیامت ـ در حالی که غافلند ـ ناگهان بر آنان فرا رسد؟');
INSERT INTO "islamhouse" VALUES(43,67,'43:67','دوستان [دنیایى،] در آن روز، دشمن یكدیگرند؛ مگر پرهیزگاران.');
INSERT INTO "islamhouse" VALUES(43,68,'43:68','[الله به آنان می‌فرماید:] «ای بندگانِ [پرهیزكارِ] من، امروز ترس و ‌اندوهى نخواهید داشت.');
INSERT INTO "islamhouse" VALUES(43,69,'43:69','همان بندگانى كه به آیات ما ایمان آوردند و تسلیم [فرمانِ ما] بودند.');
INSERT INTO "islamhouse" VALUES(43,70,'43:70','شما و دیگر مؤمنانی که همچون شما هستند، به بهشت درآیید تا [از نعمت‌های جاودانش بهره‌مند گردید و] شادمان شوید.');
INSERT INTO "islamhouse" VALUES(43,71,'43:71','ظرف‌ها و جام‌های زرین را پیرامونشان مى‌گردانند؛ و هر چه دل‌ها بخواهد و دیده‌ها از آن لذت بَرَد، در آنجا موجود است؛ و شما در بهشت جاودانه‌اید.');
INSERT INTO "islamhouse" VALUES(43,72,'43:72','این همان بهشتى است كه در برابر اعمالتان به شما داده‌اند.');
INSERT INTO "islamhouse" VALUES(43,73,'43:73','میوه‌هاى فراوانی آنجا در اختیار دارید كه از آنها مى‌خورید.');
INSERT INTO "islamhouse" VALUES(43,74,'43:74','بی‌تردید، مجرمان در عذاب دوزخ جاودانند.');
INSERT INTO "islamhouse" VALUES(43,75,'43:75','[در حالى كه عذاب] از آنان كاسته نمی‌شود و در یأس و نومیدی فرورفته‌اند.');
INSERT INTO "islamhouse" VALUES(43,76,'43:76','ما به آنان ستم نكردیم؛ بلکه آنها خود به خویشتن ستم می‌کردند.');
INSERT INTO "islamhouse" VALUES(43,77,'43:77','[دوزخیان، نگهبان آتش را صدا می‌زنند و] فریاد برمی‌آرند: «اى مالک [كاش] پروردگارت كار ما را یكسره كند [و ما را بمیراند]». او می‌گوید: «نه، شما [در این عذاب] ماندگارید».');
INSERT INTO "islamhouse" VALUES(43,78,'43:78','به راستی ما حق را برای شما آوردیم؛ ولی بیشتر شما از آن کراهت داشتید.');
INSERT INTO "islamhouse" VALUES(43,79,'43:79','بلکه آنان عزمِ [خود را برای توطئه علیه تو] جزم كردند؛ ما نیز ارادۀ محکمی [بر ناکام گذاشتنِ آنان] داریم.');
INSERT INTO "islamhouse" VALUES(43,80,'43:80','آیا مى‌پندارند كه ما سخنان سِرّى و نجوایشان را نمى‌شنویم؟ آرى، فرشتگان ما در كنارشان [هستند و همه چیز را] ثبت مى‌كنند.');
INSERT INTO "islamhouse" VALUES(43,81,'43:81','[ای پیامبر، به مشرکان] بگو: «اگر [به فرض محال، الله] رحمان فرزندى داشت، من نخستین عبادت‌کننده [او] بودم».');
INSERT INTO "islamhouse" VALUES(43,82,'43:82','[معبود یگانه‌ای كه] پروردگار آسمان‌ها و زمین و عرش است، از توصیفِ [جاهلانه] آنان منزه است.');
INSERT INTO "islamhouse" VALUES(43,83,'43:83','پس [ای پیامبر،] آنان را [به حال خود] بگذار تا یاوه‌گویی کنند و به بازى سرگرم باشند تا سرانجام، با روز موعود [= قیامت] مواجه شوند [و سزای اعمالشان را ببینند].');
INSERT INTO "islamhouse" VALUES(43,84,'43:84','او تعالی است که در آسمان و زمین معبود است؛ و او حکیم داناست.');
INSERT INTO "islamhouse" VALUES(43,85,'43:85','پربرکت و بزرگوار است ذاتی که فرمانروایی آسمان‌ها و زمین و آنچه میان آنهاست، از آنِ اوست؛ و آگاهى از [لحظۀ برپایىِ] رستاخیز تنها در اختیار اوست و [همگی] به پیشگاه او بازگردانده مى‌شوید.');
INSERT INTO "islamhouse" VALUES(43,86,'43:86','كسانی كه [مشركان] به جاى الله [به نیایش] مى‌خوانند، قادر به شفاعت نیستند؛ مگر افرادى كه به [کلمۀ توحید و] حق گواهى مى‌دهند.');
INSERT INTO "islamhouse" VALUES(43,87,'43:87','و اگر از مشرکان بپرسی چه کسی آنان را آفریده است، مسلّماً می‌گویند: «الله»؛ پس چگونه [از عبادت الله] بازگردانیده می‌شوند');
INSERT INTO "islamhouse" VALUES(43,88,'43:88','[شکایت و] سخن پیامبر [این بود:] «پروردگارا، اینان قومی هستند که ایمان نمی‌آورند».');
INSERT INTO "islamhouse" VALUES(43,89,'43:89','[ای پیامبر،] از [آزار و اذیت] آنان درگذر و [برای رهایی از شرّشان] بگو: «سلام بر شما»؛ به زودى خواهند دانست [که عذابشان چگونه است].');
INSERT INTO "islamhouse" VALUES(44,1,'44:1','حم [حا. میم].');
INSERT INTO "islamhouse" VALUES(44,2,'44:2','و سوگند به کتاب روشنگر.');
INSERT INTO "islamhouse" VALUES(44,3,'44:3','[که] ما قرآن را در شب مبارک [و فرخندۀ قدر] نازل کردیم [زیرا] که ما [همواره] هشداردهنده بوده‌ایم.');
INSERT INTO "islamhouse" VALUES(44,4,'44:4','در آن [شب،] هر امر مهمى فیصله مى‌یابد [و تدبیر می‌شود].');
INSERT INTO "islamhouse" VALUES(44,5,'44:5','[این‌] كارى است [كه‌] از جانب ما [صورت مى‌گیرد] بى‌گمان، ما فرستنده[ی پیامبران] بوده‌ایم.');
INSERT INTO "islamhouse" VALUES(44,6,'44:6','به عنوان رحمتى از پروردگارت. بی‌تردید، او شنوای داناست.');
INSERT INTO "islamhouse" VALUES(44,7,'44:7','پروردگار آسمان‌ها و زمین و آنچه میان آنهاست. اگر [به این حقیقت] یقین دارید [به الله و پیامبرش ایمان بیاورید].');
INSERT INTO "islamhouse" VALUES(44,8,'44:8','هیچ معبودی [به‌حق] جز او نیست؛ زندگی می‌بخشد و می‌میراند؛ پروردگار شما و نیاکان شماست؛');
INSERT INTO "islamhouse" VALUES(44,9,'44:9','ولى مشرکان تردید دارند و سرگرم [لذت‌های دنیا] و بازى هستند.');
INSERT INTO "islamhouse" VALUES(44,10,'44:10','پس [ای پیامبر] منتظر روزی باش که آسمان، دودی آشکار [پدید] آورد.');
INSERT INTO "islamhouse" VALUES(44,11,'44:11','که [تمام] مردم را فرامی‌گیرد. این عذابی دردناک است.');
INSERT INTO "islamhouse" VALUES(44,12,'44:12','[آنگاه کافران می‌گویند:] «پروردگارا، [این] عذاب را از ما برطرف کن که ایمان آورده‌ایم».');
INSERT INTO "islamhouse" VALUES(44,13,'44:13','چگونه [و از کجا] پند می‌پذیرند در حالی که [پیشتر،] پیامبر روشنگر به سویشان آمده بود؟');
INSERT INTO "islamhouse" VALUES(44,14,'44:14','اما از او روی گرداندند و گفتند: «[او] آموزش‌یافته‌ای دیوانه است».');
INSERT INTO "islamhouse" VALUES(44,15,'44:15','[براى آزمایش شما] زمانى ‌اندک عذاب را برمى‌داریم؛ و[لى] شما [به کفرِ خویش] بازمى‌گردید.');
INSERT INTO "islamhouse" VALUES(44,16,'44:16','روزی که سخت بر آنان می‌تازیم. [آری،] ما [از کافران به خاطر کفر و تکذیبشان،] انتقام‌ می‌گیریم.');
INSERT INTO "islamhouse" VALUES(44,17,'44:17','پیش از آنان نیز قوم فرعون را آزمودیم و پیامبری بزرگوار به سویشان آمد');
INSERT INTO "islamhouse" VALUES(44,18,'44:18','[که به آنان گفت:] «بندگان الله [= بنی‌اسرائیل‌] را به من بسپارید که من پیامبری امین برایتان هستم.');
INSERT INTO "islamhouse" VALUES(44,19,'44:19','و [نیز] بر الله برتری مجویید که من دلیل روشنی برایتان آورده‌ام؛');
INSERT INTO "islamhouse" VALUES(44,20,'44:20','و از اینکه سنگسارم کنید به پروردگار خود و پروردگار شما پناه می‌برم.');
INSERT INTO "islamhouse" VALUES(44,21,'44:21','اگر به من ایمان نمی‌آورید، از من کناره بگیرید [و رهایم کنید]».');
INSERT INTO "islamhouse" VALUES(44,22,'44:22','آنگاه به درگاه پروردگارش دعا کرد که: «[بار الها،] اینان گروهی تبهکارند».');
INSERT INTO "islamhouse" VALUES(44,23,'44:23','[به موسی وحی کردیم که:] «بندگانم را شبانه [از مصر] بیرون ببر؛ زیرا [سپاه فرعون] شما را تعقیب خواهند کرد؛');
INSERT INTO "islamhouse" VALUES(44,24,'44:24','و دریا را آرام پشت سر بگذار كه آنان سپاهى هستند كه غرق خواهند شد.');
INSERT INTO "islamhouse" VALUES(44,25,'44:25','چه بسیار باغ‌ها و چشمه‌سارها كه رها كردند [و رفتند]،');
INSERT INTO "islamhouse" VALUES(44,26,'44:26','و کشتزارها و قصر‌های نیکو [و زیبا]،');
INSERT INTO "islamhouse" VALUES(44,27,'44:27','و نعمتی که در آن شادمان [و بهر‌ه‌مند] بودند.');
INSERT INTO "islamhouse" VALUES(44,28,'44:28','[سرنوشتشان] این گونه [بود] و [تمامى] آن نعمت‌ها را به گروهى دیگر واگذاشتیم.');
INSERT INTO "islamhouse" VALUES(44,29,'44:29','آسمان و زمین قطرۀ اشکی بر [نابودى] آنان نریخت و مهلتى [برای توبه] نیز نیافتند.');
INSERT INTO "islamhouse" VALUES(44,30,'44:30','بی‌تردید، بنی‌اسرائیل را از عذاب خفت‌بار نجات دادیم؛');
INSERT INTO "islamhouse" VALUES(44,31,'44:31','از فرعون که [رهبر] سرکشی در زمرۀ اسرافکاران بود.');
INSERT INTO "islamhouse" VALUES(44,32,'44:32','ما با دانشِ خود بنی‌اسرائیل را بر مردم زمانه برترى دادیم،');
INSERT INTO "islamhouse" VALUES(44,33,'44:33','و نشانه‌هایى [از قدرت خویش] در اختیارشان گذاشتیم كه آزمایشى روشن در آن بود.');
INSERT INTO "islamhouse" VALUES(44,34,'44:34','این [کافران] مى‌گویند:');
INSERT INTO "islamhouse" VALUES(44,35,'44:35','«جز مرگِ نخستینِ ما، هیچ [زندگیِ دیگری] نیست و هرگز برانگیخته نخواهیم شد؛');
INSERT INTO "islamhouse" VALUES(44,36,'44:36','و اگر راست می‌گویید، نیاکانمان را [زنده کنید و] بیاورید».');
INSERT INTO "islamhouse" VALUES(44,37,'44:37','آیا اینان بهترند یا قوم [نیرومندِ] «تُبَّع» و پیشینیان آنها كه چون بزهكار بودند آنان را نابود كردیم؟');
INSERT INTO "islamhouse" VALUES(44,38,'44:38','ما آسمان‌ها و زمین و آنچه را که میان آنهاست به بازی نیافریده‌ایم؛');
INSERT INTO "islamhouse" VALUES(44,39,'44:39','آنها را به‌حق [و با هدف] آفریده‌ایم؛ ولى بیشتر آنان نمى‌دانند.');
INSERT INTO "islamhouse" VALUES(44,40,'44:40','بی‌تردید، روزِ داوری [= قیامت] وعده‌گاه همۀ آنان است.');
INSERT INTO "islamhouse" VALUES(44,41,'44:41','روزى كه هیچ دوستى براى دوست خود سودمند نیست و [از هیچ سو] یارى نمی‌شوند؛');
INSERT INTO "islamhouse" VALUES(44,42,'44:42','مگر كسى كه الله [بر او] رحم كند كه او تعالی شکست‌ناپذیرِ مهربان است.');
INSERT INTO "islamhouse" VALUES(44,43,'44:43','بی‌گمان، درخت زَقّوم،');
INSERT INTO "islamhouse" VALUES(44,44,'44:44','غذای گناهکاران است.');
INSERT INTO "islamhouse" VALUES(44,45,'44:45','[میوه‌اش، به سبب گرمای زیاد،] همچون مسِ گداخته در شکم‌ها می‌جوشد؛');
INSERT INTO "islamhouse" VALUES(44,46,'44:46','مانند جوششِ آب داغ.');
INSERT INTO "islamhouse" VALUES(44,47,'44:47','[خطاب مى‌رسد:] «آن [ستمکار] را بگیرید و به میان دوزخ درافكنید؛');
INSERT INTO "islamhouse" VALUES(44,48,'44:48','آنگاه از عذاب آب داغ بر سرش فروریزید».');
INSERT INTO "islamhouse" VALUES(44,49,'44:49','[در آن حال، به او گفته می‌شود:] «بچش که تو [در میان قوم خود] عزتمند و بزرگواری.');
INSERT INTO "islamhouse" VALUES(44,50,'44:50','این همان [سرنوشت دردناکی] است که در موردش تردید داشتید».');
INSERT INTO "islamhouse" VALUES(44,51,'44:51','یقیناً پرهیزگاران در جایگاه امنی هستند؛');
INSERT INTO "islamhouse" VALUES(44,52,'44:52','در [میان] باغ‌ها و [کنار] چشمه‌سار‌های بهشت.');
INSERT INTO "islamhouse" VALUES(44,53,'44:53','لباس‌هایی از دیبای نازک و دیبای ضخیم می‌پوشند، در حالى كه رویاروى یكدیگر [به صحبت] نشسته‌اند.');
INSERT INTO "islamhouse" VALUES(44,54,'44:54','چنین است [وضع آنان]؛ و [نیز] حور العین را به همسری آنان درمی‌آوریم.');
INSERT INTO "islamhouse" VALUES(44,55,'44:55','در آنجا هر میوه‌اى كه اراده کنند، بى دغدغه در اختیارشان است.');
INSERT INTO "islamhouse" VALUES(44,56,'44:56','در بهشت طعم مرگ را نخواهند چشید؛ جز همان مرگ نخستین [در دنیا]؛ و [پروردگار مهربان] آنان را از مجازات دوزخ محفوظ می‌دارد.');
INSERT INTO "islamhouse" VALUES(44,57,'44:57','[این] بخششی است از [جانب] پروردگارت؛ و این همان کامیابی بزرگ است.');
INSERT INTO "islamhouse" VALUES(44,58,'44:58','[ای پیامبر،] ما قرآن را به زبان تو روان [و آسان] ساختیم؛ باشد که [مشرکان] پند گیرند.');
INSERT INTO "islamhouse" VALUES(44,59,'44:59','پس در انتظار [وعده‌های الهی] باش که بی‌گمان، آنان [نیز] منتظر [شکست و مرگ تو] هستند.');
INSERT INTO "islamhouse" VALUES(45,1,'45:1','حم [حا. میم].');
INSERT INTO "islamhouse" VALUES(45,2,'45:2','نزول [این] کتاب از جانب الله شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(45,3,'45:3','به راستى كه در آسمان‌ها و زمین، نشانه‌هایى [از قدرت الهى] براى مؤمنان [پدیدار] است.');
INSERT INTO "islamhouse" VALUES(45,4,'45:4','و [نیز] در آفرینش خودتان و موجودات زنده‌اى كه [در زمین] پراكنده مى‌سازد، نشانه‌هایى [از توان و تدبیر الله] برای اهل یقین [آشکار] است.');
INSERT INTO "islamhouse" VALUES(45,5,'45:5','و در گردش پیاپى شب و روز و رِزقى كه الله [به صورت باران] از آسمان مى‌فرستد و با آن، زمین را پس از خزان و خشكى‌اش زنده می‌سازد؛ و [نیز] در گردش بادها، نشانه‌هایى [از قدرت پروردگار،] برای خردورزان است.');
INSERT INTO "islamhouse" VALUES(45,6,'45:6','[ای پیامبر،] اینها آیات و [نشانه‌های] الهی است كه به‌حق بر تو مى‌خوانیم. [به راستی، مشرکان] پس از [سخن] الله و آیاتش، كدام سخن را باور مى‌كنند؟');
INSERT INTO "islamhouse" VALUES(45,7,'45:7','وای بر هر دروغگوی گنهکاری!');
INSERT INTO "islamhouse" VALUES(45,8,'45:8','[آن کافری] كه آیات الهی را كه بر او خوانده مى‌شود، مى‌شنود؛ ولى متكبرانه بر مخالفت اصرار دارد؛ گویى آنها را نشنیده است؛ پس او را به عذابى دردناک بشارت بده.');
INSERT INTO "islamhouse" VALUES(45,9,'45:9','[همان کسی که] چون بر گوشه‌اى از آیات ما واقف شود، آن را به سُخره مى‌گیرد. آنان عذابى خفت‌بار [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(45,10,'45:10','پیش رویشان دوزخ است؛ و دستاوردشان و [یاوران و] كارسازانى كه به جاى الله برگزیده‌اند، هیچ سودی به حالشان نخواهند داشت؛ و عذابى سهمگین [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(45,11,'45:11','این [قرآن، موجبِ] هدایت است؛ و كسانى كه آیات پروردگار‌شان را انكار می‌كنند، عذابی از سخت‌ترین عذاب‌های دردناک [دوزخ را در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(45,12,'45:12','الله همان ذاتی است كه دریاها را در خدمت شما گماشت تا کشتی‌ها به فرمانش در آن حرکت کنند و [شما بتوانید] از فضل او [روزى] بجویید؛ باشد که شکر به جای آورید.');
INSERT INTO "islamhouse" VALUES(45,13,'45:13','آنچه را در آسمان‌ها و زمین است، از [رحمت] خویش در خدمت شما گماشت؛ كه در این [امر،] نشانه‌هایی [از لطف و قدرت الهی] برای اندیشمندان است.');
INSERT INTO "islamhouse" VALUES(45,14,'45:14','[ای پیامبر] به مؤمنان بگو: «از كسانى كه به روزهاى الله [= روزهای پیروزی بر کفر و گرفتن انتقام از مشرکان] امیدى ندارند، درگذرند تا [الله در زمان مشخص، هر] گروهى را در برابر آنچه که انجام داده‌اند سزا دهد».');
INSERT INTO "islamhouse" VALUES(45,15,'45:15','هر کس کار شایسته‌ای انجام دهد، به سود خود و هر کس کار بدى كند، به زیان خود كرده است؛ آنگاه [براى محاسبه و جزا] به پیشگاه پروردگارتان بازگردانده مى‌شوید.');
INSERT INTO "islamhouse" VALUES(45,16,'45:16','به بنی‌اسرائیل، کتاب [تورات و انجیل] و [علمِ] داوری [بین مردم] و نبوت بخشیدیم و از نعمت‌های پاكیزه، روزی‌شان دادیم و آنان را [به موهبت توحید] بر مردم زمانه برترى بخشیدیم؛');
INSERT INTO "islamhouse" VALUES(45,17,'45:17','و در امر [بازشناسی حق از باطل]، آیات روشنى در اختیارشان قرار دادیم؛ پس اختلاف نکردند مگر بعد از اینکه علم [و آگاهی] برایشان حاصل شد [و حجت بر آنان اقامه گشت، آنگاه] به سبب برتری‌جویی [و حسادتی] كه بینشان بود، اختلاف ورزیدند؛ قطعاً، پروردگارت روز قیامت دربارۀ آنچه [در دنیا] راجع به آن اختلاف می‌ورزیدند میان آنان داورى خواهد كرد.');
INSERT INTO "islamhouse" VALUES(45,18,'45:18','آنگاه تو را بر روشی [روشن و كامل] از امر [دین] قرار دادیم؛ پس پیرو همان راه باش و از هوس‌های نادانان پیروى نكن.');
INSERT INTO "islamhouse" VALUES(45,19,'45:19','آنان در برابر [عذاب] الله هیچ نفعی براى تو ندارند؛ ستمکاران [یاور و] كارساز یكدیگرند؛ و الله كارسازِ پرهیزگاران است.');
INSERT INTO "islamhouse" VALUES(45,20,'45:20','این [قرآن] مایۀ بصیرت مردم است و هدایت و رحمتی برای اهل یقین است.');
INSERT INTO "islamhouse" VALUES(45,21,'45:21','آیا كسانى كه مرتكب کارهای ناشایست شدند، پنداشته‌اند كه آنان را مانند کسانی قرار می‌دهیم که ایمان آورده و کارهای نیک انجام داده‌اند، به گونه‌اى كه زندگى و مرگشان [با آنان] یكسان باشد؟ چه بد داوری می‌کنند!');
INSERT INTO "islamhouse" VALUES(45,22,'45:22','الله آسمان‌ها و زمین را [هدفمند و] به‌حق آفرید تا [همه آزمایش شوند] و جزاى دستاوردهاى همه [بندگان] داده شود؛ بدون آنكه ظلمی به آنان شود.');
INSERT INTO "islamhouse" VALUES(45,23,'45:23','آیا آن كسی را دیده‌ای كه آگاهانه هواى نفسش را به جاى معبود خود نشانده [و تسلیم آن شده] است؟ الله نیز او را گمراه ساخت و بر گوش و دلش مُهر [غفلت] نهاد و بر دیده‌اش پرده‌اى كشید؛ [اگر الله هدایتش نکند،] پس از الله چه كسى هدایتش خواهد كرد؟ آیا پند نمى‌گیرید؟');
INSERT INTO "islamhouse" VALUES(45,24,'45:24','[مشرکان] می‌گویند: «جز زندگى دنیاى ما هیچ [خبرى] نیست؛ زندگى مى‌كنیم و مى‌میریم؛ و این گردشِ روزگار است كه ما را به كامِ مرگ مى‌برد». آنان در مورد این [امر،] ناآگاه هستند و فقط از حدس و گمان پیروی می‌کنند.');
INSERT INTO "islamhouse" VALUES(45,25,'45:25','و چون آیات روشنگر ما [در مورد قیامت] بر آنان خوانده می‌شود، دلیل [انكار]شان فقط این است كه مى‌گویند: «اگر راست مى‌گویید، نیاکانمان را [زنده کنید و] بازگردانید».');
INSERT INTO "islamhouse" VALUES(45,26,'45:26','[ای پیامبر، به آنان] بگو: «الله است كه شما را حیات مى‌بخشد و مرگ مى‌دهد؛ آنگاه شما را در روز قیامت ـ که هیچ تردیدی در [وقوع] آن نیست ـ جمع مى‌كند؛ ولى بیشتر مردم [این حقیقت را] نمى‌دانند».');
INSERT INTO "islamhouse" VALUES(45,27,'45:27','فرمانروایی آسمان‌ها و زمین از آنِ الله است و روزی که قیامت برپا شود، اهل باطل زیانکار خواهند بود.');
INSERT INTO "islamhouse" VALUES(45,28,'45:28','[در آن روز] هر گروهى را [از وحشت حساب و جزا، خوار و] به‌زانودرآمده مى‌بینى؛ همه را براى [گرفتن] نامۀ اعمالشان فرامی‌خوانند [و به آنان خطاب می‌شود:] «امروز شما در برابر آنچه كرده‌اید، جزا مى‌بینید.');
INSERT INTO "islamhouse" VALUES(45,29,'45:29','این نامۀ [اعمالِ شما نزد] ماست كه به‌حق بر شما گواهى مى‌دهد. ما [تمام] اعمالی را که انجام می‌دادید [دقیقاً] ثبت مى‌كردیم.');
INSERT INTO "islamhouse" VALUES(45,30,'45:30','مؤمنان نیكوكار را پروردگارشان مشمول رحمت خویش قرار خواهد داد. این همان كامیابى آشكار است».');
INSERT INTO "islamhouse" VALUES(45,31,'45:31','ولى [به] کافران [خطاب می‌شود:] «مگر نه این بود كه در برابر آیات من كه بر شما عرضه مى‌شد، گردنكشى مى‌كردید و گروهى بزهكار بودید؟');
INSERT INTO "islamhouse" VALUES(45,32,'45:32','و چون گفته می‌شد: "وعدۀ الله حق است و [در وقوع] قیامت هیچ تردیدی نیست"، می‌گفتید: "نمی‌دانیم قیامت چیست؛ [آن را] توهّمی بیش نمی‌دانیم و به یقین نرسیده‌ایم"».');
INSERT INTO "islamhouse" VALUES(45,33,'45:33','رفتار زشتشان بر آنان آشكار مى‌شود و عذابى كه آن را مسخره مى‌كردند، دامنگیرشان خواهد شد.');
INSERT INTO "islamhouse" VALUES(45,34,'45:34','[به آنان] گفته می‌شود: «امروز شما را [در عذاب دوزخ] رها می‌کنیم؛ چنان كه شما [نیز در دنیا نسبت] به دیدار امروز بی‌اعتنا بودید؛ و جایگاهتان آتش است و هیچ یاورى نخواهید داشت.');
INSERT INTO "islamhouse" VALUES(45,35,'45:35','این [گرفتارى] از آن روست كه آیات الهی را مسخره می‌کردید و زندگى دنیا فریبتان داده بود». امروز [کافران را] از آتش بیرون نخواهند آورد و از آنان خواسته نمی‌شود که [با توبه و طاعت، پروردگارشان را از خود] خشنود سازند.');
INSERT INTO "islamhouse" VALUES(45,36,'45:36','ستایش مخصوص الله است؛ پروردگار آسمان‌ها و زمین و تمامیِ جهانیان.');
INSERT INTO "islamhouse" VALUES(45,37,'45:37','بزرگی در آسمان‌ها و زمین از آنِ اوست؛ و او تعالی شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(46,1,'46:1','حم [حا. میم].');
INSERT INTO "islamhouse" VALUES(46,2,'46:2','نزول [این] کتاب از جانب الله شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(46,3,'46:3','آسمان‌ها و زمین و آنچه را که بین آنهاست به‌حق [و هدفمند] و براى مدتى معین آفریده‌ایم؛ و[لى] کافران از آنچه به آنان هشدار داده شده، رویگردانند.');
INSERT INTO "islamhouse" VALUES(46,4,'46:4','[ای پیامبر، به مشرکان] بگو: «آیا به چیزهایی که به جاى الله [به نیایش] مى‌خوانید توجه كرده‌اید؟ به من نشان دهید چه چیز از [اجزاى] زمین را آفریده‌اند. آیا در [آفرینش] آسمان‌ها شركت داشته‌اند؟ اگر راست مى‌گویید، از کتاب‌های آسمانى پیشین یا بازمانده‌ای از دانش [گذشتگان، دلیل] بیاورید».');
INSERT INTO "islamhouse" VALUES(46,5,'46:5','كیست گمراه‌تر از کسی كه به جاى الله، افرادى را به دعا مى‌خواند كه تا روز قیامت به او پاسخ نمى‌دهند و آنان [= معبود‌های باطل، اساساً] از درخواست آنها [= عبادتگزارانشان] بى‌خبرند؟');
INSERT INTO "islamhouse" VALUES(46,6,'46:6','و آنگاه که [در قیامت] مردم احضار شوند، آن [معبودان باطل] دشمنانشان خواهند بود و عبادتشان را انکار خواهند کرد.');
INSERT INTO "islamhouse" VALUES(46,7,'46:7','و چون آیاتِ روشنگر ما بر کافران خوانده می‌شود، آنان در مورد سخن حقى كه به آنها رسیده است می‌گویند: «این جادویى آشكار است»؛');
INSERT INTO "islamhouse" VALUES(46,8,'46:8','یا می‌گویند: «[محمد] این [قرآن] را خود جعل كرده [و به الله نسبت داده] است». [ای پیامبر، به آنان] بگو: «اگر من آن را جعل کرده [و به الله نسبت داده] باشم، شما نمی‌توانید در برابر [عذاب و خشم] الله از من دفاع کنید. الله به آنچه شما دربارۀ او [= قرآن، با طعنه] گفتگو می‌کنید، آگاه‌تر است، و به عنوان گواه بین من و شما كافى است؛ و او آمرزگارِ مهربان است».');
INSERT INTO "islamhouse" VALUES(46,9,'46:9','بگو: «من پدیده‌اى نوظهور در میان پیامبران نیستم و نمى‌دانم سرنوشت من و شما چه خواهد شد؛ تنها از آنچه به من وحى مى‌شود پیروى مى‌كنم و فقط هشداردهنده‌اى آشكارم».');
INSERT INTO "islamhouse" VALUES(46,10,'46:10','بگو: «به من بگویید: اگر قرآن از جانب الله باشد و شما به آن کفر ورزید و شاهدى از بنی‌اسرائیل، به مشابهت آن [با تورات و با تکیه بر آنچه در تورات در مورد آن آمده بر حقانیّت آن] گواهى داده و ایمان آورده باشد و شما در برابرش [انكار و] تكبر ورزید، [آیا گمراه و ستمگر نیستید]؟ بى‌گمان، الله ستمکاران را هدایت نخواهد كرد».');
INSERT INTO "islamhouse" VALUES(46,11,'46:11','و کافران به مؤمنان گفتند: «اگر این [قرآن] خوب بود، کسانی [كه بردگان و فقیرانی ناچیزند] در پذیرش آن بر ما سبقت نمى‌گرفتند»؛ و چون به وسیله آن هدایت نیافته‌اند، مى‌گویند: «این [قرآن]، دروغى قدیمی است».');
INSERT INTO "islamhouse" VALUES(46,12,'46:12','و [در حالى كه] پیش از آن [= قرآن]، كتاب موسى راهنما و رحمت بود؛ و این [قرآن،] كتابى است كه [تورات را] به زبان [شیوای] عربی تصدیق مى‌كند تا به ستمکاران هشدار دهد و براى نیكوكاران بشارت باشد.');
INSERT INTO "islamhouse" VALUES(46,13,'46:13','كسانى كه گفتند: «پروردگارِ ما الله است»؛ آنگاه [از بندگىِ غیرِ او سر باز زدند و بر اعتقاد خود] پاى فشردند، ترس و ‌اندوهى نخواهند داشت.');
INSERT INTO "islamhouse" VALUES(46,14,'46:14','آنان اهل بهشتند و جاودانه در آنجا خواهند بود. این، پاداش اعمالی است که انجام ‌داده‌اند.');
INSERT INTO "islamhouse" VALUES(46,15,'46:15','و انسان را به نيكى كردن به پدر و مادرش سفارش كرديم؛ [به ویژه] مادر كه به دشوارى بارِ حمل او را بر عهده دارد و با دشوارى وضع حمل می‌كند؛ و [دوران] باردارى و شیرخوارگى‌اش [مجموعاً] سى ماه است؛ [او پرورش می‌یابد] تا به حد رشد برسد و در چهل سالگى [كه توان جسمی و ذهنی‌اش کامل است] می‌گوید: «پروردگارا، به من توفیق بده تا شکرِ نعمتی را كه به من و پدر و مادرم ارزانى داشته‌اى، به جای آورم و كار شایسته‌اى انجام دهم كه از آن راضی گردى؛ و فرزندانم را شایسته گردان؛ به درگاهت [توبه‌ نموده و] بازگشته‌ام و تسلیم [فرمانت] هستم».');
INSERT INTO "islamhouse" VALUES(46,16,'46:16','آنان کسانی هستند که ما نیکوترین کارهایشان را می‌پذیریم و از گناهانشان درمی‌گذریم و در زمرۀ اهل بهشتند. این وعدۀ راستی است که به آن نوید داده‌ شده‌اند.');
INSERT INTO "islamhouse" VALUES(46,17,'46:17','و کسی كه به پدر و مادرش گفت: «اف بر شما! چگونه به من وعده مى‌دهید كه [از گور] بیرون آورده خواهم شد؛ در حالى كه نسل‌های فراوانی پیش از من رفتند [و بازنگشتند]؟» پدر و مادرش به درگاه الهی زاری مى‌كنند [که فرزندشان را هدایت کند و به او مى‌گویند:] «واى بر تو! ایمان بیاور كه وعدۀ الهی راست است»؛ [اما] او پاسخ مى‌دهد: «این [وعده‌ها] فقط افسانه‌هاى [خیالی] پیشینیان است».');
INSERT INTO "islamhouse" VALUES(46,18,'46:18','این افراد، در زمرۀ گروه‌هایی از جن و انس هستند كه پیشتر، فرمان عذاب الهی در موردشان تحقق یافته است. بى‌گمان، آنان زیانكار بودند.');
INSERT INTO "islamhouse" VALUES(46,19,'46:19','هر گروه [بهشتی یا دوزخی]، درجاتى بر حسب اعمالشان دارند [تا حقى ضایع نگردد]؛ و الله اعمالشان را به تمام و کمال کیفر می‌دهد؛ بى آنكه در حقشان ستم شود.');
INSERT INTO "islamhouse" VALUES(46,20,'46:20','روزى كه کافران بر آتش عرضه مى‌شوند، [به آنان خطاب مى‌شود:] «نعمت‌های پاكیزۀ خود را در زندگی دنیا از بین بردید و از آنها بهره‌مند شدید؛ پس امروز به خاطر برتری‌جوییِ ناحق و انحرافی كه در زمین داشتید، با عذابى خفت‌بار مجازات مى‌شوید».');
INSERT INTO "islamhouse" VALUES(46,21,'46:21','[ای پیامبر، سرگذشت هود،] برادر قوم عاد را یاد کن؛ آنگاه كه در سرزمین «احقاف» [= ریگزاری در جنوب شبه جزیرۀ عربستان] قومش را هشدار مى‌داد ـ در حالى كه هشداردهندگان [دیگرى نیز] در گذشته‌هاى دور و نزدیک آمده بودند ـ [و چنین می‌گفت] كه: «تنها الله را عبادت کنید كه من از عذاب روزى بزرگ بر شما مى‌ترسم».');
INSERT INTO "islamhouse" VALUES(46,22,'46:22','آنان گفتند: «آیا آمده‌اى كه ما را از [عبادت] معبودانمان بازگردانى؟ اگر راست مى‌گویى، عذابى كه به ما وعده مى‌دهى پیش آور».');
INSERT INTO "islamhouse" VALUES(46,23,'46:23','[هود] گفت: «[وقت نزول عذاب را] فقط الله مى‌داند؛ و من پیامى را كه مأمور [ابلاغ] آن شده‌ام به شما مى‌رسانم؛ ولى شما را گروهى نادان مى‌بینم».');
INSERT INTO "islamhouse" VALUES(46,24,'46:24','و چون آن [عذاب] را كه به صورت ابرى در جهت [دشت و] دره‌ها[ى حاصلخیز]شان پیش مى‌آمد، مشاهده كردند، [با شادمانی] گفتند: «این ابرى است كه [رحمت] بر ما مى‌بارد»؛ [هود گفت: «نه،] بلكه همان [عذابى] است كه براى رسیدنش شتاب داشتید؛ تندبادى است که عذابى دردناک با خود دارد.');
INSERT INTO "islamhouse" VALUES(46,25,'46:25','[طوفانی است که] هر چیزى را به فرمان پروردگارش در هم مى‌كوبد»؛ آنگاه چنان [هلاک] شدند كه جز خانه‌هاى [ویران] آنان، اثرى به چشم نمى‌خورد. ما بزهكاران را این گونه كیفر مى‌دهیم.');
INSERT INTO "islamhouse" VALUES(46,26,'46:26','یقیناً به آنها [= قوم عاد]، چنان امکاناتی داده بودیم كه به شما [اهل مکه، چنان] امکاناتی نداده‌ایم؛ و برای آنان چشم و گوش و دل [نیرومندتر از دیگران] قرار داده بودیم، و[لی] گوش و چشم و دل‌هایشان سودى به حالشان نداشت؛ زیرا آیات الهی را انكار مى‌كردند و [سرانجام،] عذابی كه آن را مسخره مى‌كردند، دامنگیرشان شد.');
INSERT INTO "islamhouse" VALUES(46,27,'46:27','[بسیارى از مردم] شهرهاى پیرامون شما [مكّیان] را هلاک كردیم؛ و[لى قبلاً] نشانه‌ها [و دلایل توحید] را به شکل‌های مختلف در میان آوردیم؛ باشد که [از کفر و گمراهى] بازگردند.');
INSERT INTO "islamhouse" VALUES(46,28,'46:28','پس چرا کسانی كه [مشرکان آنان را] براى تقرب [به الله،] معبودان خود گرفتند، [به هنگام بلا] یاری‌شان نكردند؟ بلكه از نظرشان ناپدید شدند؛ و [سرانجامِ] دروغ‌ها و تهمت‌هایشان همین بود.');
INSERT INTO "islamhouse" VALUES(46,29,'46:29','[ای پیامبر، به یاد آور] هنگامی‌ که گروهى از جن را به سوی تو فرستادیم كه قرآن را بشنوند؛ هنگامی که نزد پیامبر حضور یافتند، [به یكدیگر] گفتند: «خاموش باشید [تا به آیات قرآن گوش فرادهیم] و چون [تلاوتش] به پایان رسید، نزد قوم خود بازگشتند تا به آنان [در مورد عواقبِ سرپیچی از فرمان حق] هشدار دهند.');
INSERT INTO "islamhouse" VALUES(46,30,'46:30','گفتند: «اى قوم، ما [آیاتِ] كتابى را شنیدیم كه پس از موسى نازل شده است و کتاب‌های [آسمانیِ] پیش از خود را تصدیق می‌کند و به [سوى] حق و راه راست هدایت مى‌نماید.');
INSERT INTO "islamhouse" VALUES(46,31,'46:31','ای قوم، [سخنان توحیدیِ] این دعوتگر را که به سوی الله فرامی‌خوانَد بپذیرید و به او ایمان آورید تا [الله] برخى از گناهانتان را ببخشد و شما را از عذابى دردناک در امان بدارد.');
INSERT INTO "islamhouse" VALUES(46,32,'46:32','و هر کس [سخنان] دعوتگری را که به سوی الله فرامی‌خوانَد نپذیرد [و به وی ایمان نیاورد، هرگز الله را] در زمین به تنگ نمى‌آورَد؛ و در برابر الله [یاور و] كارسازى نخواهد داشت. چنین کسانی در گمراهىِ آشكارند».');
INSERT INTO "islamhouse" VALUES(46,33,'46:33','آیا مشرکان ندیده‌اند [و نمی‌دانند] الله که آسمان‌ها و زمین را آفرید و از آفرینش آنها درمانده نشد، قادر است مردگان را [نیز] زنده كند؟ آرى، او بر هر كارى تواناست.');
INSERT INTO "islamhouse" VALUES(46,34,'46:34','روزى كه کافران بر آتش عرضه مى‌شوند، [از آنان پرسیده مى‌شود:] «آیا این آتش حق نیست [و حقیقت ندارد]؟» آنان می‌گویند: «آری؛ سوگند به پروردگارمان [که چنین است]»؛ [آنگاه الله] می‌فرماید: «پس به سزاى آنكه [قیامت را] انكار مى‌کردید، عذاب را بچشید».');
INSERT INTO "islamhouse" VALUES(46,35,'46:35','[ای پیامبر،] تو نیز همچون پیامبران اولوالعزم [كه همواره ثابت‌قدم بودند، در برابر آزار و تکذیبِ قومت] شکیبا باش و در مورد [درخواست عذابِ] آنان شتاب نكن. روزى كه [این کافران] عذابِ موعودشان را [در جهان آخرت] ببینند، [کیفرشان چنان طولانی است که] گویى فقط بخشی از روز را [در دنیا] به سر برده‌اند. این قرآن، پیامى [عمومى برای جن و انس] است. آیا جز نافرمانان هلاک مى‌شوند؟');
INSERT INTO "islamhouse" VALUES(47,1,'47:1','کسانی که کافر شدند و [مردم را] از راه الله بازمى‌دارند، [الله] اعمال آنان را تباه می‌كند.');
INSERT INTO "islamhouse" VALUES(47,2,'47:2','و کسانی كه ایمان آورده و کارهای شایسته کرده‌اند و به آنچه بر محمد نازل شده- که آن حق و از جانب پروردگارشان است- ایمان آورده‌اند، [الله نیز]، بدی‌هایشان را می‌زداید و امور [دنیا و دین]شان را سامان می‌بخشد [و اصلاح می‌کند].');
INSERT INTO "islamhouse" VALUES(47,3,'47:3','این [پاداش و جزا،] به سبب آن است که کافران پیرو باطل شدند و مؤمنان، پیرو [آیات] حقِ پروردگارشان. الله مَثَل‌های مردم را اینچنین برایشان بیان مى‌كند.');
INSERT INTO "islamhouse" VALUES(47,4,'47:4','[ای مومنان!] هر گاه با کافران روبرو شدید، آنان را گردن بزنید تا آنكه [بسیاری از آنان را] بکشید؛ آنگاه [اسیران را] محکم به بند كشید؛ سپس یا بر آنان منت گذارید [و بدون هیچ عوضی آزادشان کنید] یا [از آنان] فدیه بگیرید [و رهایشان کنید] تا سنگینى جنگ فروكش كند. این [حکم الهی] است؛ و اگر الله مى‌خواست، خود از آنان انتقام مى‌گرفت؛ ولى [فرمان جنگ داد] تا شما را در برابر یكدیگر آزمایش كند؛ و کسانی كه در راه الله كشته شدند، [الله هرگز] نتیجه تلاششان را تباه نمى‌كند.');
INSERT INTO "islamhouse" VALUES(47,5,'47:5','الله آنان را [به راه راست] هدایت خواهد كرد و امورشان را سامان خواهد بخشید؛');
INSERT INTO "islamhouse" VALUES(47,6,'47:6','و آنان را به بهشتی که [اوصافش را] برایشان بیان کرده است وارد خواهد نمود.');
INSERT INTO "islamhouse" VALUES(47,7,'47:7','ای کسانی که ایمان آورده‌اید، اگر [دین و پیامبر] الله را یاری کنید، او نیز شما را یاری می‌کند و گام‌هایتان را [ثابت و] استوار می‌دارد.');
INSERT INTO "islamhouse" VALUES(47,8,'47:8','و بر کافران، نگونساری و هلاکت باد! [الله] اعمالشان را تباه می‌گردانَد؛');
INSERT INTO "islamhouse" VALUES(47,9,'47:9','زیرا آنچه را که الله نازل كرده است نپسندیدند؛ [الله] نیز اعمالشان را تباه نمود.');
INSERT INTO "islamhouse" VALUES(47,10,'47:10','آیا در زمین گشت و گذار نکرده‌اند تا ببینند سرانجامِ کسانی که پیش از آنان بودند، چگونه بوده است؟ الله [خانه‌هایشان را] بر سر‌شان ویران کرد [و آنان را نابود ساخت] و این کافران [نیز کیفری] همانند آن [در پیش] خواهند داشت.');
INSERT INTO "islamhouse" VALUES(47,11,'47:11','زیرا الله [یاور و] كارسازِ مؤمنان است؛ و[لى] کافران هیچ كارسازى ندارند.');
INSERT INTO "islamhouse" VALUES(47,12,'47:12','الله مؤمنان نیكوكار را به باغ‌هایی [از بهشت] وارد می‌کند که جویبارها از زیر [درختان] آن جاری است؛ و کافران [تنها در دنیا] بهره‌مند مى‌شوند و همچون چهارپایان مى‌خورند و آتش [دوزخ] جایگاهشان است.');
INSERT INTO "islamhouse" VALUES(47,13,'47:13','و چه بسیار شهرهایی که ساکنانش از [مردم] شَهرت که تو را از آنجا بیرون کردند، توانمند‌تر بودند؛ ما آنان را نابود کرده‌ایم و هیچ یاوری [در برابر عذاب الهی] نداشتند.');
INSERT INTO "islamhouse" VALUES(47,14,'47:14','آیا كسى كه بر شیوۀ روشنى از جانب پروردگار خویش است، همچون كسانى است كه زشتىِ رفتارشان در نظرشان آراسته شده و از هوس‌های خویش پیروى مى‌كنند؟');
INSERT INTO "islamhouse" VALUES(47,15,'47:15','توصیف بهشتى كه به پرهیزگاران وعده داده شده، [چنین است كه:] در آن، جویبارهایی از آبی [خالص و زلال] که بو و مزه‌اش تغییر نکرده؛ و جویبارهایی از شیری كه طعمش دگرگون نگشته؛ و جویبارهایی از شرابی كه مایۀ لذتِ نوشندگان است؛ و جویبارهایی از عسل ناب است؛ و همۀ [انواع] میوه‌ها، در آنجا در دسترسشان است و [از همه برتر، از] آمرزشِ پروردگارشان [برخوردارند]. [آیا بهشتیان] همانند ساكنان جاودان دوزخند كه آبی چنان جوشان به آنان می‌نوشانند كه روده‌هایشان را متلاشى می‌كند؟');
INSERT INTO "islamhouse" VALUES(47,16,'47:16','[ای پیامبر،] برخی از منافقان به [گفتار] تو گوش فرامى‌دهند اما هنگامی که از نزد تو خارج مى‌شوند، به اهل دانش [با تمسخر] مى‌گویند: «[فهمیدید] لحظاتى پیش چه مى‌گفت؟» الله بر دل‌هایشان مُهر [غفلت] نهاده است و آنان از هوس‌های خویش پیروى كرده‌اند.');
INSERT INTO "islamhouse" VALUES(47,17,'47:17','الله بر هدایتِ پرهیزگاران مى‌افزاید و پرهیزكاری‌شان [را قوّت] مى‌بخشد.');
INSERT INTO "islamhouse" VALUES(47,18,'47:18','آیا [کافران] جز این انتظار دارند که قیامت ناگهان بر آنان فرارسد؟ همانا نشانه‌هاى آن آشكار شده است. آنگاه كه قیامت فرارسد پندگرفتنشان چه سودی خواهد داشت؟');
INSERT INTO "islamhouse" VALUES(47,19,'47:19','[ای پیامبر،] بدان که معبودی [به‌حق] جز الله نیست؛ و برای گناه خویش و [گناهِ] مردان و زنان مؤمن آمرزش بخواه؛ و [به یاد داشته باش که] الله، محل رفت‌وآمدِ [روزانه] و آرمیدن [شبانۀ] شما را مى‌داند.');
INSERT INTO "islamhouse" VALUES(47,20,'47:20','مؤمنان می‌گویند: «چرا سوره‌ای [دربارۀ اجازۀ جهاد] نازل نمی‌شود؟»؛ اما هنگامى كه آیات صریحی در مورد جنگ نازل مى‌گردد، [منافقان و] بیماردلان را مى‌بینى كه همچون كسى كه از [سختى] مرگ دستخوش بیهوشى شده باشد به تو مى‌نگرند؛ [مرگ و عذاب الهی] بر آنان شایسته‌تر است.');
INSERT INTO "islamhouse" VALUES(47,21,'47:21','اطاعت و گفتار شایسته [بهتر است]؛ و چون تصمیم قطعی [جهاد] گرفته شد، اگر [در تعهد خود با] الله صادق باشند، [از نفاق و سرکشی]، برایشان بهتر است.');
INSERT INTO "islamhouse" VALUES(47,22,'47:22','پس [اى منافقان،‌] آیا [جز این‌] انتظار ‌دارید كه اگر [از قرآن و سنت پیامبرش] روی بگردانید، در زمین تباهى كنید و از خویشاوندانتان ببُرید؟ [چنان‌که در جاهلیت این حالت را داشتید.]');
INSERT INTO "islamhouse" VALUES(47,23,'47:23','اینان کسانی هستند که الله [با دور ساختن از رحمتِ خویش] لعنتشان کرده است؛ پس [گوشِ دلِ] آنان را کر، و چشم [دلِ] آنان را کور ساخته است.');
INSERT INTO "islamhouse" VALUES(47,24,'47:24','آیا در قرآن تدبّر نمی‌کنند یا قفل‌ها[ی غفلت] بر دل‌هایشان نهاده شده است؟');
INSERT INTO "islamhouse" VALUES(47,25,'47:25','کسانی که پس از روشن شدن [راه] هدایت، به راه پیشینِ خویش بازگشتند [و مرتد شدند،] شیطان [اعمال زشتشان را] برایشان آراسته و آنان را [با آرزوهای دراز] فریب داده است.');
INSERT INTO "islamhouse" VALUES(47,26,'47:26','این [جهل و گمراهی،] بدان خاطر است که منافقان به مشرکانی که از وحی الهی کراهت داشتند گفتند: «ما در برخی از امور از شما پیروی می‌کنیم»؛ و الله پنهانکاری‌شان را می‌داند.');
INSERT INTO "islamhouse" VALUES(47,27,'47:27','آنگاه كه فرشتگان الهی بر چهره و پشتشان مى‌زنند و جانشان را مى‌گیرند، [حال این منافقان] چگونه خواهد بود؟');
INSERT INTO "islamhouse" VALUES(47,28,'47:28','این [کیفر،] از آن روست که آنان پیرو راهی شدند که الله را به خشم می‌آورَد و از انجام کارهایی که موجب خشنودی الهی می‌شود کراهت داشتند؛ پس [الله نیز نتیجۀ] کارهایشان را تباه ساخت.');
INSERT INTO "islamhouse" VALUES(47,29,'47:29','آیا بیماردلان [منافق] پنداشته‌اند كه الله هرگز كینه‌هایشان را آشكار نخواهد ساخت؟');
INSERT INTO "islamhouse" VALUES(47,30,'47:30','[ای پیامبر،] اگر مى‌خواستیم، آنان را به تو نشان می‌دادیم كه [باطن] آنان را از ظاهرشان بازشناسى؛ و مسلّماً از شیوه سخنشان به خوبى آنان را خواهى شناخت؛ و [بدانید که] الله از کارهایتان آگاه است.');
INSERT INTO "islamhouse" VALUES(47,31,'47:31','قطعاً [همۀ] شما را آزمایش می‌کنیم تا مجاهدان و شکیبایانتان را بازشناسیم و احوال شما را می‌آزماییم [تا راستگویان مشخص گردند].');
INSERT INTO "islamhouse" VALUES(47,32,'47:32','كسانى كه پس از روشن شدن راه هدایت بر آنان، کفر ورزیدند و [مردم را] از راه الله بازداشتند و به مخالفت با پیامبر برخاستند، هرگز زیانى به الله نخواهند رساند؛ و [الله نیز نتیجۀ] اعمالشان را تباه خواهد ساخت.');
INSERT INTO "islamhouse" VALUES(47,33,'47:33','ای مؤمنان، از الله و پیامبر اطاعت كنید و [با کفر و ریا] اعمال [شایستۀ] خویش را تباه نكنید.');
INSERT INTO "islamhouse" VALUES(47,34,'47:34','کسانی که کفر ورزیدند و [دیگران را نیز] از راه الله بازداشتند و در حال کفر مردند، الله هرگز آنان را نخواهد آمرزید.');
INSERT INTO "islamhouse" VALUES(47,35,'47:35','پس [ای مؤمنان، هنگام رویارویی با دشمنان] سستی نورزید و [از ترس جنگ، آنان را] به صلح و آشتی دعوت نکنید؛ در حالى كه شما برترید و الله با شماست و هرگز از [پاداش] اعمالتان نخواهد کاست.');
INSERT INTO "islamhouse" VALUES(47,36,'47:36','به راستی که زندگی دنیا، بازیچه و سرگرمى است؛ و اگر ایمان بیاورید و پرهیزگاری کنید، [الله] پاداشتان را [بی‌کم‌و‌کاست] خواهد داد؛ و [تمام] اموالتان را نمی‌خواهد [بلکه می‌فرماید که زکاتش را بپردازید].');
INSERT INTO "islamhouse" VALUES(47,37,'47:37','اگر [تمام] آن را از شما بخواهد و [بر این دستور] اصرار ‌کند، بخل مى‌ورزید و [این کار،] کینه‌هایتان را آشکار می‌سازد.');
INSERT INTO "islamhouse" VALUES(47,38,'47:38','آگاه باشید! شما كسانى هستید كه براى انفاق در راه الله دعوت مى‌شوید؛ ولى برخی از شما بخل مى‌ورزند؛ و هر کس بخل بورزد، به زیان خویش بخل ورزیده است؛ و الله بى‌نیاز است و [این شما هستید كه] نیازمندید؛ و اگر [از اسلام به سوی کفر] بازگردید، الله گروهى دیگر را جایگزین شما خواهد كرد كه همچون شما [به بخل مبتلا] نباشند.');
INSERT INTO "islamhouse" VALUES(48,1,'48:1','[ای پیامبر،] به راستی که ما پیروزىِ آشكارى را [در صلح حدیبیه] برایت مقدر كردیم');
INSERT INTO "islamhouse" VALUES(48,2,'48:2','تا الله گناهِ گذشته و آینده‌ات را بیامرزد و نعمتش را بر تو تمام گردانَد و تو را به راه راست هدایت نماید؛');
INSERT INTO "islamhouse" VALUES(48,3,'48:3','و الله به پیروزیِ شکست‌ناپذیری یاری‌ات کند.');
INSERT INTO "islamhouse" VALUES(48,4,'48:4','اوست که بر دل‌های مؤمنان آرامش [و ثبات] نازل کرد تا بر ایمانشان بیفزایند؛ و سپاهیان آسمان‌ها و زمین از آنِ الله است؛ و الله دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(48,5,'48:5','[آری، چنین کرد] تا مردان و زنان مؤمن را به باغ‌هایی [بهشتی] وارد کند که جویبارها از زیر [درختان] آن جاری است؛ جاودانه در آنجا خواهند بود؛ و گناهانشان را بزداید [و بیامرزد]؛ و این نزد الله كامیابى بزرگى [برای مومنان] است؛');
INSERT INTO "islamhouse" VALUES(48,6,'48:6','و [نیز به این هدف که] منافقان و مشرکان را ـ اعم از زن و مرد ـ كه نسبت به الله بدگمانند مجازات كند؛ ناگواری [و عذاب الهی] بر آنان باد! الله بر آنان خشم گرفته و آنان را از رحمت خویش دور کرده و دوزخ را برایشان آماده نموده است؛ و [به راستی که دورخ] چه بد جایگاهی است!');
INSERT INTO "islamhouse" VALUES(48,7,'48:7','سپاهیان آسمان‌ها و زمین از آنِ الله است؛ و الله شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(48,8,'48:8','[ای پیامبر،] ما تو را گواه و مژده‌دهنده و هشدار‌دهنده فرستادیم');
INSERT INTO "islamhouse" VALUES(48,9,'48:9','تا [شما مردم] به الله و پیامبرش ایمان بیاورید و [دین] او را یاری کنید و او را بزرگ دارید و بامداد و شامگاه، الله را تسبیح گویید.');
INSERT INTO "islamhouse" VALUES(48,10,'48:10','بی‌تردید، كسانى كه با تو بیعت مى‌كنند، در حقیقت، با الله بیعت مى‌كنند؛ دست الله بالای دست‌های آنان است. هر کس [پس از بیعت] پیمان‌شكنى كند، به زیان خویش پیمان شكسته است؛ و هر کس به پیمانش [مبنی بر یاری دین الله و پیامبرش] وفا كند، به زودى [الله] پاداش بزرگی به او خواهد داد.');
INSERT INTO "islamhouse" VALUES(48,11,'48:11','بادیه‌نشینان متخلف [كه از شركت در سفر مکه و صلح حدیبیه خودداری کرده‌اند، براى توجیهِ کارِ خود] خواهند گفت: «[محافظت] اموال و خانواده‌مان ما را گرفتار كرد؛ برایمان آمرزش بخواه». آنان چیزى كه در دل‌هایشان نیست بر زبان مى‌آورند. بگو: «اگر الله اراده كند که به شما زیان یا سودی برسد، كیست كه در مقابل [خواستِ] الله از شما دفاع کند؟ آرى، الله از آنچه مى‌كنید آگاه است».');
INSERT INTO "islamhouse" VALUES(48,12,'48:12','[عذرتان این نبود؛] بلكه گمان می‌کردید كه پیامبر و مؤمنان [همراهش، از این سفرِ خطرناک] هرگز نزد خانوادۀ خویش بازنخواهند گشت؛ و این [توهم] در دل‌هایتان آراسته شد و گمانِ بد بردید [که الله دین و پیامبرش را یاری نخواهد کرد؛ پس به خطا رفتید] و هلاک شدید.');
INSERT INTO "islamhouse" VALUES(48,13,'48:13','هر کس به الله و پیامبرش ایمان نیاورد، [بداند که] ما آتشی سوزان برای کافران آماده کرده‌ایم.');
INSERT INTO "islamhouse" VALUES(48,14,'48:14','فرمانرواییِ آسمان‌ها و زمین از آنِ الله است؛ هر کس را که بخواهد می‌آمرزد و هر کس را که بخواهد عذاب می‌کند؛ و الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(48,15,'48:15','[ای مؤمنان،] هنگامى كه عازم [خیبر و] گرفتن غنیمت مى‌شوید، متخلفان [از سفر مکه] خواهند گفت: «اجازه دهید ما [نیز] به دنبال شما بیاییم». آنان مى‌خواهند كلام الهی را تغییر دهند. [ای پیامبر،] به آنان بگو: «شما هرگز به دنبال ما نخواهید آمد [زیرا این غنیمت‌ها مخصوص کسانی است که در حدیبیه حضور داشتند و دربارۀ این حُکم،] الله قبلاً چنین فرموده است»؛ آنگاه خواهند گفت: «[نه،] بلكه شما نسبت به ما حسد مى‌ورزید». [هرگز چنین نیست؛] بلكه جز عدۀ كمى [از آنان، اوامر الهی را] درنمى‌یابند.');
INSERT INTO "islamhouse" VALUES(48,16,'48:16','[ای پیامبر،] به بادیه‌نشینان متخلف بگو: «به زودى به مقابله با گروهى جنگاور فراخوانده خواهید شد که آنان را از پای درآورید، مگر اینكه تسلیم شوند؛ اگر اطاعت كنید، الله پاداش نیكویى به شما خواهد داد؛ و اگر همچون گذشته سرپیچی نمایید، شما را با عذابى دردناک مجازات خواهد كرد».');
INSERT INTO "islamhouse" VALUES(48,17,'48:17','بر نابینا و لَنگ و بیمار گناهى نیست [كه در جنگ شركت نكنند]؛ و هر کس از الله و پیامبرش اطاعت كند، [الله] او را به باغ‌هایی [از بهشت] وارد می‌کند كه جویبارها از زیر [درختان] آن جاری است؛ و هر کس را که سرپیچی کند، به عذابى دردناک مجازات خواهد كرد.');
INSERT INTO "islamhouse" VALUES(48,18,'48:18','الله از مؤمنانى كه در كنار آن درخت [در حدیبیه] با تو بیعت كردند، راضی شد و دانست كه در دلشان چه مى‌گذرد؛ از این روى آرامش [و ثبات] بر آنان فروفرستاد و به پیروزى نزدیک [در خیبر] پاداششان داد؛');
INSERT INTO "islamhouse" VALUES(48,19,'48:19','و [همچنین] غنیمت‌های فراوانی که [از خیبر] به دست می‌آورند؛ و الله همواره شکست‌ناپذیر [و] حکیم است.');
INSERT INTO "islamhouse" VALUES(48,20,'48:20','الله غنیمت‌های فراوانى را به شما وعده داد كه [در فتوحات اسلامی آینده] به آنها دست خواهید یافت؛ این [پیروزى و غنیمت‌های خیبر] را براى شما زودتر فراهم ساخت و دست [تعدی و تجاوز] دشمن [= یهودیان] را از شما کوتاه نمود تا نشانه‌اى [از لطف الهی] برای مومنان باشد و شما را به راهى راست هدایت كند؛');
INSERT INTO "islamhouse" VALUES(48,21,'48:21','و [همچنین غنیمت‌های] دیگرى [در انتظار شماست] كه هنوز به آنها دست نیافته‌اید [و] الله بر آنها احاطه دارد [و از آنها آگاه است]. به راستی که الله بر هر كارى تواناست.');
INSERT INTO "islamhouse" VALUES(48,22,'48:22','اگر کافران به جنگ شما بیایند، [از توکل و صلابت شما] پشت كرده و مى‌گریزند؛ سپس هیچ كارساز و یاورى نخواهند یافت.');
INSERT INTO "islamhouse" VALUES(48,23,'48:23','این سنت الهی است که پیشتر نیز جاری بوده است؛ و هرگز در سنت الهی تغییر و تبدیلی نخواهی یافت‌.');
INSERT INTO "islamhouse" VALUES(48,24,'48:24','اوست که در دل مکه [= در حدیبیه] دست آنان را از شما کوتاه نمود [و مانع از آن شد که شما را به جنگ وادارند]، و پس از پيروزساختنِ شما بر آنان، دست‌های شما را از آنان [بازداشت که آنان را بعد از اسارت، نکشید و آزادشان کنید]؛ و الله همواره به آنچه مى‌كنید بیناست.');
INSERT INTO "islamhouse" VALUES(48,25,'48:25','آنان كسانى بودند كه کافر شدند و شما را از [ورود به] مسجدالحرام بازداشتند و نیز نگذاشتند قربانی‌هایی که با خود داشتید به قربانگاهش برسد؛ و اگر مردان و زنان مؤمنی [بین مردم مکه] نبودند که [در صورت بروز درگیری، ممکن بود] آنان را ندانسته پایمال کنید و ناخواسته مرتکب گناه شوید، [فرمان حمله به مکه می‌دادیم؛ ولى الله چنین کرد] تا [از اهل مکه] هر کس را که بخواهد، مشمول رحمت خویش قرار دهد؛ و اگر [مؤمنان و کافران در مکه] از یکدیگر جدا بودند، کافرانشان را با عذابى دردناک مجازات مى‌كردیم.');
INSERT INTO "islamhouse" VALUES(48,26,'48:26','[به یاد آور] آنگاه كه کافران [مکه در حدیبیه] قلباً دستخوش تعصب، [آن هم] تعصب جاهلیت شده بودند، الله آرامش [خاطرى از جانب] خویش بر پیامبرش و بر مؤمنان فروفرستاد و آنان را به اصل [و اساس توحید و] پرهیزگاری [= لا اله الا الله] پایبند ساخت؛ چرا كه [نسبت به مشرکان،] به آن سزاوارتر و شایسته‌تر بودند؛ و الله به هر چیزى داناست.');
INSERT INTO "islamhouse" VALUES(48,27,'48:27','الله رؤیاى رسولش را به درستی تحقق بخشید كه به خواست الهی، در حالى كه سرهایتان را تراشیده و [یا] موهایتان را كوتاه كرده‌اید، در ایمنی و بدون ترس [از دشمن]، به مسجدالحرام وارد خواهید شد. [الله] چیزهایى را مى‌دانست كه شما نمى‌دانستید؛ و علاوه بر فتح مکه، پیروزى نزدیک [خیبر] را [نیز برایتان] مقرر نمود.');
INSERT INTO "islamhouse" VALUES(48,28,'48:28','اوست كه پیامبرش را با [کتابِ] هدایت و آیین حق فرستاد تا [حقانیت برتر] آن را بر همۀ ادیان آشکار گردانَد؛ و الله به عنوان گواه كافى است.');
INSERT INTO "islamhouse" VALUES(48,29,'48:29','محمد رسول الله و [صحابۀ مؤمن و فداکارش، همان] کسانی که همواره با او هستند، در برابر كافران [ستیزه‌جو]، محکم و سختگیرند و با یكدیگر مهربان؛ آنان را در حال رکوع و سجده می‌بینی كه پیوسته فضل و رضایت الله را مى‌جویند؛ نشان [ایمان و پرهیزگاری] آنان در چهره‌هایشان از اثر سجده و عبادت هویداست؛ این وصف آنان در تورات است؛ و توصیفشان در انجیل [نیز] همچون گیاهی‌ است كه جوانه‌اش را برمی‌آورد و آن را نیرو می‌بخشد تا محکم گردد و بر ساقه‌هایش استوار بایستد؛ چنان كه موجب شگفتى كشاورزان شود؛ در نتیجه، [الله] با [تقویت و پیشرفتِ] مؤمنان، کافران را به خشم می‌آورد. الله به مؤمنان نیكوكار [= صحابه و پیروانشان] وعدۀ آمرزش و پاداشى بزرگ داده است.');
INSERT INTO "islamhouse" VALUES(49,1,'49:1','ای مؤمنان، در برابر الله و پیامبرش [در هیچ كارى‌] پیشى نگیرید؛ و از الله پروا کنید كه الله شنواى داناست.');
INSERT INTO "islamhouse" VALUES(49,2,'49:2','ای مؤمنان، صدایتان را از صداى پیامبر بلندتر نكنید؛ و همچنان كه برخى از شما با برخى دیگر بلند سخن مى‌گویید، با او به صداى بلند سخن نگویید. مبادا بى‌آنكه بدانید، اعمالتان تباه گردد.');
INSERT INTO "islamhouse" VALUES(49,3,'49:3','کسانی که صدای خود را نزد رسول الله پایین می‌آورند، الله دل‌هایشان را برای پرهیزگاری آزموده [و خالص گردانده] است. آمرزش و پاداش بزرگی برایشان [در پیش] است.');
INSERT INTO "islamhouse" VALUES(49,4,'49:4','[ولى] کسانی كه از پشت اطاق‌ها[ی همسرانت] تو را صدا مى‌زنند، بیشتر آنان بى‌خردند.');
INSERT INTO "islamhouse" VALUES(49,5,'49:5','و اگر صبر مى‌كردند تا خود براى ملاقاتشان خارج شوى، برایشان بهتر بود؛ و الله آمرزگار مهربان است.');
INSERT INTO "islamhouse" VALUES(49,6,'49:6','ای مؤمنان، اگر شخص منحرفى خبرى برایتان آورد، تحقیق كنید تا مبادا ندانسته به گروهى [بی‌گناه] آسیب برسانید و از کردۀ خود پشیمان شوید.');
INSERT INTO "islamhouse" VALUES(49,7,'49:7','بدانید كه رسول الله میان شماست [و صلاحتان را بهتر می‌داند]؛ اگر در بسیارى از امور از [میل و نظرِ] شما پیروی كند، قطعاً دچار رنج و زحمت می‌شوید؛ ولى الله ایمان را محبوب شما نمود و آن را در دل‌هایتان بیاراست [تا اسلام بیاورید] و کفر و نافرمانى و سرکشی را در نظرتان ناخوشایند گرداند. آنان [كه آراسته به این اوصافند] پویندگانِ راه هدایت و کمالند.');
INSERT INTO "islamhouse" VALUES(49,8,'49:8','[این عنایات،] به عنوان فضیلت و نعمتى از جانب الله [مقرر شده است]؛ و الله دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(49,9,'49:9','اگر دو گروه از مؤمنان با هم درگیر شدند، میانشان آشتى برقرار كنید؛ و اگر یک گروه بر دیگرى تجاوز كرد، با گروه متجاوز بجنگید تا به فرمان الله بازآید؛ اگر بازگشت، صلحى عادلانه در میان آنان برقرار كنید و به عدالت رفتار نمایید كه الله عادلان را دوست دارد.');
INSERT INTO "islamhouse" VALUES(49,10,'49:10','بی‌تردید مؤمنان برادر یكدیگرند؛ پس میان برادران خویش آشتى برقرار كنید و از الله پروا كنید؛ باشد که مورد رحمت قرار گیرید.');
INSERT INTO "islamhouse" VALUES(49,11,'49:11','هرگز نباید گروهى [از شما] گروهی دیگر را مسخره کند؛ چه بسا اینان از آنان بهتر باشند؛ و زنان نیز نباید زنان دیگر را مسخره نمایند؛ چه بسا اینان از آنان برتر باشند؛ و از یكدیگر عیبجویى نكنید؛ و یكدیگر را با لقب‌های [زشت] خطاب نكنید [که این کار، مصداقِ نافرمانی از اوامر الهی است]؛ و بدترین ویژگی [برای یک مسلمان] این است که پس از ایمان‌آوردن، [فِسق و] نافرمانی کند؛ و کسانی كه توبه نكنند، [نسبت به نفس خویش] ستمکارند.');
INSERT INTO "islamhouse" VALUES(49,12,'49:12','ای مؤمنان، از بسیارى از گمان‌ها بپرهیزید؛ چرا كه برخى از گمان‌ها گناه است؛ و دربارۀ یكدیگر تجسس و غیبت نكنید؛ آیا هیچ یک از شما دوست دارد كه گوشت برادرِ مرده‌اش را بخورد؟ [حتماً] از آن نفرت دارید، [غیبت نیز به همین زشتى است]؛ از الله پروا كنید كه الله توبه‌پذیرِ مهربان است.');
INSERT INTO "islamhouse" VALUES(49,13,'49:13','ای مردم، ما شما را از [آمیزش] مرد و زنى آفریدیم و به تیره‌ها و قبیله‌های مختلف تقسیم كردیم تا یكدیگر را بازشناسید. بی‌تردید، بزرگوارترین شما نزد الله پرهیزگار‌ترینِ شماست؛ و الله [به آنچه می‌کنید] دانا و آگاه است.');
INSERT INTO "islamhouse" VALUES(49,14,'49:14','اعراب [بادیه‌نشین، در حضور پیامبر] گفتند: «ایمان آورده‌ایم» [ای پیامبر، به آنان] بگو: «شما ایمان نیاورده‌اید؛ ولى بگویید: "اسلام آورده‌ایم"؛ و هنوز ایمان در قلب‌هایتان نفوذ نكرده است. اگر از الله و پیامبرش اطاعت كنید، از [پاداش] اعمال شما ذره‌ای نخواهد كاست. به راستی كه الله آمرزگارى مهربان است».');
INSERT INTO "islamhouse" VALUES(49,15,'49:15','مؤمنان [حقیقی] كسانى هستند كه به الله و پیامبرش ایمان آورده‌اند و [لحظه‌ای در ایمانشان] تردید نكردند و با مال و جانشان در راه الله به مبارزه برخاستند؛ اینانند كه [در اعتقاد و عمل] صداقت دارند.');
INSERT INTO "islamhouse" VALUES(49,16,'49:16','[ای پیامبر، به بادیه‌نشینان] بگو: «آیا می‌خواهید الله را از دین خویش آگاه كنید [که از عمق قلب ایمان آورده‌اید؟!] در حالى كه الله هر چه را که در آسمان‌ها و زمین است مى‌داند؛ و الله به هر چیزى آگاه است».');
INSERT INTO "islamhouse" VALUES(49,17,'49:17','آنان بر تو منت مى‌نهند كه اسلام آورده‌اند؛ بگو: «منتِ اسلامِ خویش را بر من نگذارید؛ بلكه اگر [در ابراز ایمان] صداقت دارید، این الله است كه بر شما منت دارد كه شما را به سوى ایمان هدایت نموده است».');
INSERT INTO "islamhouse" VALUES(49,18,'49:18','بی‌تردید، الله اسرار آسمان‌ها و زمین را مى‌داند و به آنچه مى‌كنید بیناست.');
INSERT INTO "islamhouse" VALUES(50,1,'50:1','ق [قاف]؛ سوگند به قرآن ارجمند [كه رستاخیز آمدنى است].');
INSERT INTO "islamhouse" VALUES(50,2,'50:2','مشرکان از اینكه هشداردهنده‌اى از میان خودشان به سراغشان آمده است، ابراز شگفتى می‌کنند و می‌گویند: «ماجرای عجیبى است.');
INSERT INTO "islamhouse" VALUES(50,3,'50:3','آیا هنگامی که مُردیم و خاک شدیم، [دوباره برانگیخته خواهیم شد؟] این بازگشتی بعید است».');
INSERT INTO "islamhouse" VALUES(50,4,'50:4','بی‌تردید، ذراتى كه زمین از بدن آنان مى‌كاهد [همه را] مى‌دانیم؛ و كتابى نگاهبان در اختیار داریم [که هر چه بر آنان مقدر گشته، در آن ثبت است].');
INSERT INTO "islamhouse" VALUES(50,5,'50:5','وقتى كه [قرآن، یعنی كلام] حق برایشان آمد، آن را تکذیب كردند؛ پس در حالت سرگردانی [و پریشانی] هستند [و دربارۀ قرآن حیرانند].');
INSERT INTO "islamhouse" VALUES(50,6,'50:6','آیا کافران، آسمان را برفرازشان نمى‌نگرند كه چگونه آن را برافراشتیم و [به زیور ستارگان] آراستیم و عارى از [هرگونه خلل و] شکافی است؟');
INSERT INTO "islamhouse" VALUES(50,7,'50:7','و زمین را گستردیم و کوه‌های ثابت در آن پى افكندیم و از هر نوع گیاه زیبا [و شادی‌انگیزی] در آن رویاندیم.');
INSERT INTO "islamhouse" VALUES(50,8,'50:8','[آنها را آفریدیم] تا برای هر بندۀ توبه‌کاری، موجب بینش و پند باشد؛');
INSERT INTO "islamhouse" VALUES(50,9,'50:9','و بارانى پربركت از آسمان فرستادیم و با آن، باغ‌ها[ى میوه] و [مزارعِ] دانه[هاى] دروكردنى رویاندیم،');
INSERT INTO "islamhouse" VALUES(50,10,'50:10','و درختان سركشیدۀ خرما با خوشه‌هاى برهم‌نشسته.');
INSERT INTO "islamhouse" VALUES(50,11,'50:11','براى روزى بندگان؛ به وسیلۀ آن [باران،] سرزمین خزان‌زده و خشک را حیات بخشیدیم؛ خروج از قبرها [در قیامت] نیز همین گونه است.');
INSERT INTO "islamhouse" VALUES(50,12,'50:12','پیش از آنان نیز قوم نوح و اهالى «رَسّ» [= چاه] و قوم ثمود [پیامبرانشان را] دروغگو شمردند؛');
INSERT INTO "islamhouse" VALUES(50,13,'50:13','و [همچنین قوم] عاد و فرعون و قوم لوط؛');
INSERT INTO "islamhouse" VALUES(50,14,'50:14','و اهالی «اَیکه» [= قوم شعیب] و قوم «تُبَّع»؛ هر یک [از آنان] پیامبران [الهی] را دروغگو شمردند؛ پس وعدۀ عذاب من [بر آنان] تحقّق یافت.');
INSERT INTO "islamhouse" VALUES(50,15,'50:15','آیا ما در آفرینش نخستین فروماندیم [تا از بازآفرینیِ آن عاجز باشیم]؟ [هرگز،] بلكه آنان از آفرینش جدید در [حیرت و] تردیدند.');
INSERT INTO "islamhouse" VALUES(50,16,'50:16','ما انسان را آفریدیم و از وسوسه‌هاى ضمیرش آگاهیم؛ و از شاهرگ [وی] به او نزدیكتریم.');
INSERT INTO "islamhouse" VALUES(50,17,'50:17','وقتی دو فرشته که بر سمت راست و چپ مراقب نشسته‌اند [رفتارش را] ثبت مى‌كنند؛');
INSERT INTO "islamhouse" VALUES(50,18,'50:18','هر سخنى که بر زبان مى‌آورد، [فرشتۀ] مراقبى در کنار او آماده است [و آن سخن را می‌نویسد].');
INSERT INTO "islamhouse" VALUES(50,19,'50:19','سختی‌های مرگ، به راستى دررسید؛ [ای انسان غافل،] این همان چیزى است كه از آن مى‌گریختى؛');
INSERT INTO "islamhouse" VALUES(50,20,'50:20','و [برای دومین بار] در «صور» دمیده می‌شود [و قیامت برپا می‌گردد]. آن روز، [روز تحققِ] وعدۀ [عذاب] است.');
INSERT INTO "islamhouse" VALUES(50,21,'50:21','و هر کس در حالی [که دو فرشته او را همراهی می‌کنند به عرصۀ محشر] می‌آید؛ یکی [او را] سوق می‌دهد و دیگری [بر اعمالش] گواه است.');
INSERT INTO "islamhouse" VALUES(50,22,'50:22','[به او گفته می‌شود:] «تو از این [روز] در غفلت بودى و ما [با عذاب دوزخ،] پرده[ی غفلت] را از مقابل [چشم‌های] تو برداشتیم؛ اكنون دیده‌ات تیزبین شده است.');
INSERT INTO "islamhouse" VALUES(50,23,'50:23','[فرشتۀ] همنشین وى مى‌گوید: «این همان [نامۀ اعمال او] است كه [آن را ثبت کرده‌ام و] نزدم آماده است».');
INSERT INTO "islamhouse" VALUES(50,24,'50:24','[الله خطاب به آن دو فرشته مى‌فرماید:] «هر ستیزه‌جوی حق‌ستیزی را در دوزخ بیفكنید؛');
INSERT INTO "islamhouse" VALUES(50,25,'50:25','[هر] مانع خیر و متجاوز و بدگمان را؛');
INSERT INTO "islamhouse" VALUES(50,26,'50:26','همان كسى كه در كنار الله، معبود دیگرى قرار مى‌داد. او را در عذابى سخت بیفكنید».');
INSERT INTO "islamhouse" VALUES(50,27,'50:27','[شیطانِ] همنشینش مى‌گوید: «پروردگارا، من او را به طغیان نکشیدم؛ بلکه او خود در گمراهیِ بى‌پایانی بود».');
INSERT INTO "islamhouse" VALUES(50,28,'50:28','[الله] می‌فرماید: «در پیشگاه من ستیز نکنید كه پیش از این به شما هشدار داده بودم.');
INSERT INTO "islamhouse" VALUES(50,29,'50:29','فرمانِ مجازات، نزد من تغییرپذیر نیست؛ و الله [در کیفر و پاداش] به بندگانش ستم نمی‌کند».');
INSERT INTO "islamhouse" VALUES(50,30,'50:30','روزى كه به دوزخ مى‌گوییم: «آیا پُر شده‌ای؟» [و او] مى‌گوید: «آیا بیش از این هم هست؟».');
INSERT INTO "islamhouse" VALUES(50,31,'50:31','[در آن روز] بهشت را برای پرهیزگاران نزدیک می‌کنند، و فاصله‌ای از آنان ندارد [تا نعمت‌هایش را از نزدیک ببینند].');
INSERT INTO "islamhouse" VALUES(50,32,'50:32','[به آنان گفته می‌شود:] «این همان پاداشى است كه به شما وعده داده مى‌شد که برای هر توبه‌کاری است که [به سوی پروردگارش بازمی‌گردد و به پیمان و احکام الهی] پایبند است.');
INSERT INTO "islamhouse" VALUES(50,33,'50:33','همان كسی كه در نهان از [خشمِ الله] رحمان می‌ترسد و با قلبی توبه‌کار [به پیشگاهش] بازمی‌گردد.');
INSERT INTO "islamhouse" VALUES(50,34,'50:34','با سلامت به بهشت درآیید؛ این است روز جاودانگى».');
INSERT INTO "islamhouse" VALUES(50,35,'50:35','در آنجا هرچه بخواهند، در اختیار دارند و حتی بیشتر از آن [نیز] در اختیارِ ماست.');
INSERT INTO "islamhouse" VALUES(50,36,'50:36','و پیش از اینان [= مشرکان مکه]، امت‌های زیادی را نابود کردیم که آنان [به مراتب] از اینها قوی‌تر بودند؛ و در گوشه و کنار شهرها به جستجو پرداختند [که ببینند آیا] هیچ پناه و گریزگاهی [از عذاب و هلاکت، برایشان] وجود دارد.');
INSERT INTO "islamhouse" VALUES(50,37,'50:37','بی‌گمان در این [سخن] برای صاحبدلان یا کسانی که با حضور [قلب به ندای توحید] گوش فرامی‌دهند و هوشیارند، اندرز است.');
INSERT INTO "islamhouse" VALUES(50,38,'50:38','ما آسمان‌ها و زمین و آنچه را که میان آنهاست، در شش روز آفریدیم و [هرگز] رنج و خستگى به ما نرسید.');
INSERT INTO "islamhouse" VALUES(50,39,'50:39','[ای پیامبر،] در برابر سخنان مشرکان شکیبا باش؛ و پیش از طلوع آفتاب و قبل از غروب آن، پروردگارت را به پاکی ستایش كن [و نماز بگزار].');
INSERT INTO "islamhouse" VALUES(50,40,'50:40','و نیز [در پاسى] از شب و پس از هر نماز، او را به پاکی ستایش کن.');
INSERT INTO "islamhouse" VALUES(50,41,'50:41','و [ای پیامبر،] آنگاه که منادی [قیامت] از مکانی نزدیک ندا می‌دهد، گوش فرادار.');
INSERT INTO "islamhouse" VALUES(50,42,'50:42','روزی که همگان بانگِ هولناک [رستاخیز] را به‌حق می‌شنوند، [آن روز،] روزِ خروج [از گورها] است.');
INSERT INTO "islamhouse" VALUES(50,43,'50:43','ماییم كه حیات مى‌بخشیم و مرگ مى‌دهیم؛ و بازگشت [همگان] به سوی ماست.');
INSERT INTO "islamhouse" VALUES(50,44,'50:44','روزى كه زمین از [فراز] آنان شكافته مى‌شود، انسان‌ها شتابان [بیرون می‌آیند]. این احضارى است كه براى ما آسان است.');
INSERT INTO "islamhouse" VALUES(50,45,'50:45','ما به آنچه [کافران] می‌گویند داناتریم؛ و تو [ای پیامبر،] بر آنان مسلط نیستی [که بخواهی آنان را به ایمان آوردن وادار کنی]؛ کسی را که از عذاب [و هشدار] من می‌ترسد، به [وسیلۀ] قرآن اندرز بده.');
INSERT INTO "islamhouse" VALUES(51,1,'51:1','سوگند به بادهاى خاک [و باران] ‌افشان');
INSERT INTO "islamhouse" VALUES(51,2,'51:2','و سوگند به ابرهای سنگین‌بار [از باران]');
INSERT INTO "islamhouse" VALUES(51,3,'51:3','و سوگند به کشتی‌های سبک‌سیر');
INSERT INTO "islamhouse" VALUES(51,4,'51:4','و سوگند به فرشتگانی که کارها را [به امر الله] تقسیم می‌کنند');
INSERT INTO "islamhouse" VALUES(51,5,'51:5','که آنچه [در مورد پاداش و عذاب] به شما وعده داده شده حقیقت دارد؛');
INSERT INTO "islamhouse" VALUES(51,6,'51:6','و [روز] جزا قطعاً به وقوع مى‌پیوندد.');
INSERT INTO "islamhouse" VALUES(51,7,'51:7','سوگند به آسمان که [با ستارگان آراسته شده، و گویی] دارای راه‌هاست،');
INSERT INTO "islamhouse" VALUES(51,8,'51:8','كه شما [در مورد پیامبر و قرآن] اختلاف‌نظر دارید.');
INSERT INTO "islamhouse" VALUES(51,9,'51:9','هر کس [از راه حق] بازگردانده شود، از [ایمان به] پیامبر و قرآن نیز بازگردانده خواهد شد.');
INSERT INTO "islamhouse" VALUES(51,10,'51:10','مرگ [و لعنت] بر دروغگویان!');
INSERT INTO "islamhouse" VALUES(51,11,'51:11','همان كسانى كه در ورطۀ جهل، غافلند.');
INSERT INTO "islamhouse" VALUES(51,12,'51:12','آنان [پیوسته و از روى انكار] مى‌پرسند: «روز جزا چه زمانى است؟»');
INSERT INTO "islamhouse" VALUES(51,13,'51:13','همان روزى است كه به آتش مجازات مى‌شوند؛');
INSERT INTO "islamhouse" VALUES(51,14,'51:14','[و به آنان گفته می‌شود:] «عذاب خود را بچشید؛ این همان آتشى است كه به شتاب مى‌خواستید».');
INSERT INTO "islamhouse" VALUES(51,15,'51:15','[در روز قیامت،] مسلماً پرهیزگاران در باغ‌هایی [از بهشت] و [كنار] چشمه‌سارها به سر مى‌برند؛');
INSERT INTO "islamhouse" VALUES(51,16,'51:16','آنچه پروردگارشان به آنان عطا كرده است دریافت مى‌دارند؛ زیرا در [زندگیِ] گذشته نیكوكار بوده‌اند.');
INSERT INTO "islamhouse" VALUES(51,17,'51:17','آنان ‌اندكى از شب را مى‌خفتند [و بقیه را به نماز و نیایش مشغول بودند]');
INSERT INTO "islamhouse" VALUES(51,18,'51:18','و سحرگاهان [از الله] آمرزش مى‌خواستند');
INSERT INTO "islamhouse" VALUES(51,19,'51:19','و در اموالشان براى سائل و محروم، حقى [معیّن از صدقه و زکات] بود.');
INSERT INTO "islamhouse" VALUES(51,20,'51:20','و در زمین نشانه‌ها[ى قدرت الهى] براى اهل یقین پدیدار است؛');
INSERT INTO "islamhouse" VALUES(51,21,'51:21','و در وجود خودتان، آیا نمى‌بینید؟');
INSERT INTO "islamhouse" VALUES(51,22,'51:22','و رِزق و روزی‌تان و آنچه [از کیفر و پاداش و خیر و شر که] به شما وعده داده می‌شود، در آسمان است.');
INSERT INTO "islamhouse" VALUES(51,23,'51:23','سوگند به پروردگار آسمان و زمین كه وعده[ی قیامت و پاداش و عذاب] همچون سخن گفتنتان، حقیقی و قطعى است.');
INSERT INTO "islamhouse" VALUES(51,24,'51:24','[ای پیامبر] آیا داستان میهمانان گرامى ابراهیم به تو رسیده است؟');
INSERT INTO "islamhouse" VALUES(51,25,'51:25','آنگاه که بر او وارد شدند و سلام گفتند، او گفت: «سلام» [و زیر لب گفت:] «گروهى ناشناسند».');
INSERT INTO "islamhouse" VALUES(51,26,'51:26','آنگاه آهسته نزد خانوادۀ خود رفت و [گوشتِ بریانِ] گوساله‌اى فربه [براى پذیرایى] آورد.');
INSERT INTO "islamhouse" VALUES(51,27,'51:27','[او غذا را] نزد آنان گذاشت [و چند لحظه بعد] گفت: «آیا نمی‌خورید؟»');
INSERT INTO "islamhouse" VALUES(51,28,'51:28','[چون دست به غذا نزدند] از آنان احساس ترس كرد؛ گفتند: نترس [ما فرشته‌ایم]؛ و او را به [تولد] پسرى دانا بشارت دادند.');
INSERT INTO "islamhouse" VALUES(51,29,'51:29','همسرش با فریاد [و شگفتى] پیش آمد و در حالى كه به صورت خود مى‌زد گفت: «پیرزنى نازا [فرزند می‌زاید]؟!»');
INSERT INTO "islamhouse" VALUES(51,30,'51:30','[فرشتگان] گفتند: «پروردگارت اینچنین فرموده است؛ و بی‌گمان، او حکیمِ داناست».');
INSERT INTO "islamhouse" VALUES(51,31,'51:31','[ابراهیم] گفت: «ای فرستادگان [الله،] کار و مأموریت شما چیست؟»');
INSERT INTO "islamhouse" VALUES(51,32,'51:32','[فرشتگان] گفتند: «ما برای [مجازات] قومی مجرم [و گناهکار] فرستاده شده‌ایم');
INSERT INTO "islamhouse" VALUES(51,33,'51:33','تا سنگ‌هایی از گلِ [سخت‌شده] بر سرشان فروباریم.');
INSERT INTO "islamhouse" VALUES(51,34,'51:34','[سنگ‌هایی] که نزد پروردگارت، برای اسرافکاران، نشان گذاشته شده است».');
INSERT INTO "islamhouse" VALUES(51,35,'51:35','مؤمنانی را که در آن [شهرهای قوم لوط] بودند، [پیش از نزول عذاب] بیرون آوردیم؛');
INSERT INTO "islamhouse" VALUES(51,36,'51:36','ولى در آنجا فقط یک خانواده را یافتیم كه [نسبت به اوامر الهی] تسلیم بودند.');
INSERT INTO "islamhouse" VALUES(51,37,'51:37','و در آن [شهرها] براى مردمى كه از عذاب دردناک [الهی] ترس دارند، نشانه‌اى روشن بر جاى گذاشتیم.');
INSERT INTO "islamhouse" VALUES(51,38,'51:38','در [داستان] موسی [نیز نشانه و عبرتی است]؛ آنگاه که او را با دلیلی روشن نزد فرعون فرستادیم.');
INSERT INTO "islamhouse" VALUES(51,39,'51:39','ولى [فرعون] با تكیه بر قدرتش، روی برگرداند [و حق را نپذیرفت] و گفت: «[این مرد، یا] جادوگر است یا دیوانه».');
INSERT INTO "islamhouse" VALUES(51,40,'51:40','او را همراه سپاهیانش گرفتیم و به دریا افكندیم [و هلاک کردیم]؛ در حالى كه [در لحظۀ مرگ، خود را به خاطر نافرمانی و کفرش] سرزنش مى‌كرد.');
INSERT INTO "islamhouse" VALUES(51,41,'51:41','در [داستان قوم] عاد [نیز درس عبرتی است]؛ آنگاه که تندبادى بی‌خیر و برکت بر آنان فرستادیم.');
INSERT INTO "islamhouse" VALUES(51,42,'51:42','[تندبادی] که بر هر چه وزید، آن را خاک و خاشاک كرد.');
INSERT INTO "islamhouse" VALUES(51,43,'51:43','در [داستان قوم] ثمود [نیز درس عبرتی است] آنگاه که [توسط پیامبرشان صالح] به آنان گفته شد: «اندکی [از لذت‌های زندگی] بهره‌مند شوید [که تنها سه روز فرصت دارید]».');
INSERT INTO "islamhouse" VALUES(51,44,'51:44','آنان از فرمان پروردگارشان سرپیچی کردند؛ پس در حالی که چشم به راه بودند، بانگ عذاب آنان را فراگرفت.');
INSERT INTO "islamhouse" VALUES(51,45,'51:45','[چنان بر زمین افتادند که] نه توان برخاستن [و گریز] داشتند و نه یاراى دفاع.');
INSERT INTO "islamhouse" VALUES(51,46,'51:46','پیش از آن، قوم نوح را كه گروهى نافرمان بودند [هلاک كردیم].');
INSERT INTO "islamhouse" VALUES(51,47,'51:47','ما آسمان را به توان [خود] بنا نهادیم و همواره آن را وسعت می‌بخشیم؛');
INSERT INTO "islamhouse" VALUES(51,48,'51:48','و زمین را [برای زندگی] گستردیم و چه نیک گستراننده‌ای هستیم!');
INSERT INTO "islamhouse" VALUES(51,49,'51:49','و همه چیز را به صورت زوج [= نر و ماده] آفریدیم؛ باشد که [توجه كنید و] پند گیرید.');
INSERT INTO "islamhouse" VALUES(51,50,'51:50','[ای پیامبر، به مردم بگو:] «به سوى الله بشتابید كه من هشداردهنده‌ای آشکار از جانب او برای شما هستم؛');
INSERT INTO "islamhouse" VALUES(51,51,'51:51','و در كنار الله معبود دیگرى قرار ندهید كه من از [جانب] او هشداردهنده‌اى آشکار برایتان هستم».');
INSERT INTO "islamhouse" VALUES(51,52,'51:52','هر پیامبری که بر پیشینیانشان مبعوث شد، همین گونه گفتند: «[او یا] جادوگر است یا دیوانه».');
INSERT INTO "islamhouse" VALUES(51,53,'51:53','آیا یكدیگر را به این [کفر و تکذیب] سفارش كرده بودند؟ نه، بلكه مردمی سرکش بودند.');
INSERT INTO "islamhouse" VALUES(51,54,'51:54','پس [ای پیامبر،] از آنان روی بگردان که تو [به خاطر کفر آنان] هرگز سزاوار سرزنش نیستی.');
INSERT INTO "islamhouse" VALUES(51,55,'51:55','و [پیوسته به آنان] تذكر بده كه قطعاً تذكر براى مؤمنان سودبخش است.');
INSERT INTO "islamhouse" VALUES(51,56,'51:56','و جن و انس را فقط برای این آفریده‌ام که مرا عبادت كنند [و از بندگىِ دیگران سر باز زنند].');
INSERT INTO "islamhouse" VALUES(51,57,'51:57','نه از آنان روزى مى‌خواهم، نه اینكه مرا خوراک دهند؛');
INSERT INTO "islamhouse" VALUES(51,58,'51:58','[زیرا] الله است که روزی‌رسانِ نیرومند و برقرار است.');
INSERT INTO "islamhouse" VALUES(51,59,'51:59','ستمکاران [مشرک نیز] همچون یاران [گذشتۀ] خویش، سهمى از عذاب دارند؛ پس به شتاب [آن را] از من نخواهند.');
INSERT INTO "islamhouse" VALUES(51,60,'51:60','واى بر کافران از [عذاب] روزی که به آن وعده داده می‌شوند!');
INSERT INTO "islamhouse" VALUES(52,1,'52:1','سوگند به کوه طور');
INSERT INTO "islamhouse" VALUES(52,2,'52:2','و سوگند به کتاب نوشته‌شده');
INSERT INTO "islamhouse" VALUES(52,3,'52:3','در صحیفه‌ای گشوده');
INSERT INTO "islamhouse" VALUES(52,4,'52:4','و سوگند به «بیت المَعمور» [که فرشتگان در آن، به عبادت پروردگار مشغولند]');
INSERT INTO "islamhouse" VALUES(52,5,'52:5','و سوگند به [آسمان بلند، همان] سقف برافراشته [بر زمین]');
INSERT INTO "islamhouse" VALUES(52,6,'52:6','و سوگند به دریای آکنده [از آب]');
INSERT INTO "islamhouse" VALUES(52,7,'52:7','که بی‌تردید، عذاب پروردگارت به وقوع خواهد پیوست؛');
INSERT INTO "islamhouse" VALUES(52,8,'52:8','و هیچ بازدارنده‌اى ندارد.');
INSERT INTO "islamhouse" VALUES(52,9,'52:9','روزى كه آسمان به سختى در جنبش و اضطراب افتد');
INSERT INTO "islamhouse" VALUES(52,10,'52:10','و کوه‌ها با حركتى شدید روان شوند.');
INSERT INTO "islamhouse" VALUES(52,11,'52:11','در آن روز، واى به حال تكذیب‌كنندگان!');
INSERT INTO "islamhouse" VALUES(52,12,'52:12','همان کسانی كه در بیهوده‌گویى، سرگرم بازى‌اند.');
INSERT INTO "islamhouse" VALUES(52,13,'52:13','روزى كه با خشونت به سوى آتش دوزخ رانده مى‌شوند.');
INSERT INTO "islamhouse" VALUES(52,14,'52:14','[به آنان گفته می‌شود:] «این [همان] آتشی است که آن را دروغ می‌پنداشتید.');
INSERT INTO "islamhouse" VALUES(52,15,'52:15','آیا این [آتش و عذاب] جادوست یا [هنوز هم آن را] نمی‌بینید؟');
INSERT INTO "islamhouse" VALUES(52,16,'52:16','به آتش درآیید [و بسوزید] كه شکیبایی و ناشکیباییِ شما [در برابر شعله‌های سوزانش] برایتان یكسان است؛ تنها در برابر آنچه كرده‌اید، مجازات مى‌شوید».');
INSERT INTO "islamhouse" VALUES(52,17,'52:17','به راستی که پرهیزگاران در باغ‌های بهشت در ناز و نعمتند؛');
INSERT INTO "islamhouse" VALUES(52,18,'52:18','و از بخشش‌های [بی‌دریغِ] پروردگارشان و از اینكه از عذاب دوزخ نگاهشان داشته است شادمانند.');
INSERT INTO "islamhouse" VALUES(52,19,'52:19','[به آنان گفته می‌شود:] «به پاداشِ کردارتان [در دنیا، اینک] گوارا بخورید و بیاشامید».');
INSERT INTO "islamhouse" VALUES(52,20,'52:20','در حالى كه بر تخت‌هایى ردیف‌شده تكیه زده‌اند و حور العین را به همسریِ آنان درمی‌آوریم.');
INSERT INTO "islamhouse" VALUES(52,21,'52:21','مؤمنانى كه فرزندانشان در ایمان پیرو آنان بودند، فرزندانشان را [نیز در بهشت] به آنان ملحق خواهیم كرد و از [پاداش] عملشان ذره‌اى نمى‌كاهیم؛ چرا که [سرنوشتِ] هر كس در گرو دستاورد خودِ اوست [و گناه دیگری را به دوش نمی‌کشد].');
INSERT INTO "islamhouse" VALUES(52,22,'52:22','[در بهشت، از] هر نوع میوه و گوشتى كه بخواهند، پى‌درپى در دسترسشان قرار مى‌دهیم.');
INSERT INTO "islamhouse" VALUES(52,23,'52:23','در آنجا جام‌های شرابى از دست یکدیگر مى‌گیرند كه نه موجب بیهوده‌گویى است و نه گناه؛');
INSERT INTO "islamhouse" VALUES(52,24,'52:24','و نوجوانانى [که براى خدمت] پیرامونشان در رفت و آمدند، گویی [از زیبایی و سپیدرویی] همچون مرواریدى پنهان [در صدف] هستند.');
INSERT INTO "islamhouse" VALUES(52,25,'52:25','[بهشتیان] به یکدیگر روی می‌آورند [و] از [دنیا و احوال گذشتۀ] خود سؤال می‌کنند.');
INSERT INTO "islamhouse" VALUES(52,26,'52:26','می‌گویند: «ما در دنیا، در خانوادۀ خود [از عذاب الهی] می‌ترسیدیم.');
INSERT INTO "islamhouse" VALUES(52,27,'52:27','الله [با نعمت اسلام] بر ما منت نهاد و ما را از عذاب سوزان [دوزخ] محافظت کرد.');
INSERT INTO "islamhouse" VALUES(52,28,'52:28','ما در زندگی دنیا، همواره او را [عبادت می‌کردیم و به ربوبیت] می‌خواندیم. بی‌تردید، او نیکوکار مهربان است».');
INSERT INTO "islamhouse" VALUES(52,29,'52:29','[ای پیامبر، با آیات قرآن به مردم] اندرز بده که به لطف پروردگارت [تو برخلاف ادعای کافران،] نه كاهنى و نه مجنون.');
INSERT INTO "islamhouse" VALUES(52,30,'52:30','آیا می‌گویند: «محمد شاعری است که منتظریم تا مرگش فرارسد [و از سخنانش رهایی یابیم]؟»');
INSERT INTO "islamhouse" VALUES(52,31,'52:31','بگو: «منتظر [مرگِ من] باشید؛ من [نیز] با شما منتظر خواهم ماند [تا عذابتان را به چشم ببینم]».');
INSERT INTO "islamhouse" VALUES(52,32,'52:32','آیا عقل‌هایشان به [بیان] این [سخنان باطل] حُكم مى‌كند یا [اساساً] مردمی سرکش هستند؟');
INSERT INTO "islamhouse" VALUES(52,33,'52:33','آیا می‌گویند: «[محمد] قرآن را جعل کرده است؟» [هرگز چنین نیست؛] بلکه [غرق در تکبرند که] ایمان نمی‌آورند.');
INSERT INTO "islamhouse" VALUES(52,34,'52:34','اگر راست مى‌گویند، سخنى مانند آن بیاورند.');
INSERT INTO "islamhouse" VALUES(52,35,'52:35','آیا آنان از هیچ آفریده شده‌اند یا خود آفرینندۀ خویشند؟');
INSERT INTO "islamhouse" VALUES(52,36,'52:36','آیا آنان آسمان‌ها و زمین را آفریده‌اند؟ [نه،] بلکه آنان یقین ندارند [که الله آفرینندۀ مطلق است].');
INSERT INTO "islamhouse" VALUES(52,37,'52:37','آیا گنجینه‌های [نعمت و هدایت] پروردگارت نزد آنان است؟ آیا [بر همه چیز] تسلط دارند؟');
INSERT INTO "islamhouse" VALUES(52,38,'52:38','آیا نردبانى دارند كه [گفتار فرشتگان وحى را] به وسیلۀ آن مى‌شنوند؟ هر کس شنیده است، باید دلیلى روشن بیاورد.');
INSERT INTO "islamhouse" VALUES(52,39,'52:39','آیا الله فرزندان دختر دارد و شما پسر؟');
INSERT INTO "islamhouse" VALUES(52,40,'52:40','[ای پیامبر،] آیا پاداشی از آنان درخواست می‌کنی که [که از پرداخت آن در رنجند و هزینه‌اش] برایشان [گران و] سنگین است؟');
INSERT INTO "islamhouse" VALUES(52,41,'52:41','آیا از عالم غیب آگاهند و [خبرهاى نهان را] می‌نویسند؟');
INSERT INTO "islamhouse" VALUES(52,42,'52:42','آیا قصد نیرنگ [و نقشۀ پلیدی برایت] دارند؟ ولى کافران خود گرفتار نیرنگ [و تدبیر الهی] هستند.');
INSERT INTO "islamhouse" VALUES(52,43,'52:43','آیا آنان معبودی غیر از الله دارند؟ الله از چیزهایی که [با او] شریک قرار می‌دهند پاک و منزه است.');
INSERT INTO "islamhouse" VALUES(52,44,'52:44','[این کافران، حتی] اگر پاره‌هاى از آسمان را نیز در حال سقوط ببینند، [باز هم ایمان نمى‌آورند و] مى‌گویند: «ابرى متراكم است».');
INSERT INTO "islamhouse" VALUES(52,45,'52:45','پس آنان را [به حال خود] رها كن تا با روزِ موعودشان كه [با نخستین دمیدنِ صور] مدهوش مرگ می‌گردند، مواجه شوند.');
INSERT INTO "islamhouse" VALUES(52,46,'52:46','روزى كه نیرنگشان سودمند نخواهد بود و [از هیچ سو] یارى نمی‌شوند.');
INSERT INTO "islamhouse" VALUES(52,47,'52:47','ستمکاران [در دنیا نیز به دست مؤمنان] به عذاب دیگرى گرفتار خواهند شد؛ ولى بیشتر آنان نمى‌دانند.');
INSERT INTO "islamhouse" VALUES(52,48,'52:48','[ای پیامبر،] در برابر حکم پروردگارت شکیبا باش؛ زیرا تو تحت نظر و حفاظت مایی؛ و هنگامی که [از خواب یا به قصد عبادت] برمی‌خیزی، پروردگارت را به پاکی ستایش کن.');
INSERT INTO "islamhouse" VALUES(52,49,'52:49','و [نیز در پاسى] از شب او را تسبیح بگوی [و نماز بگزار] و به هنگام افول ستارگان [در سپیده‌دم نیز نماز صبح بگزار].');
INSERT INTO "islamhouse" VALUES(53,1,'53:1','سوگند به ستاره، آنگاه كه افول كند');
INSERT INTO "islamhouse" VALUES(53,2,'53:2','که یار شما [= محمد صلی الله علیه وسلم] هرگز گمراه نشده و به راه تباهى نیفتاده است؛');
INSERT INTO "islamhouse" VALUES(53,3,'53:3','و از روی هوای نفس سخن نمی‌گوید.');
INSERT INTO "islamhouse" VALUES(53,4,'53:4','[آنچه می‌گوید] نیست جز وحی [الهی] که به [او] وحی می‌شود.');
INSERT INTO "islamhouse" VALUES(53,5,'53:5','[جبرئیل که فرشته‌اى است] پُرتوان، به او آموزش داده است.');
INSERT INTO "islamhouse" VALUES(53,6,'53:6','[همان فرشتۀ] خوش‌منظری كه [با چهرۀ حقیقی‌اش در برابر پیامبر] ایستاد.');
INSERT INTO "islamhouse" VALUES(53,7,'53:7','در حالی که او در افق بالا [در آسمان] قرار داشت.');
INSERT INTO "islamhouse" VALUES(53,8,'53:8','سپس [جبرئیل] نزدیک شد؛ و نزدیک‌تر؛');
INSERT INTO "islamhouse" VALUES(53,9,'53:9','تا آنکه [فاصلۀ او با پیامبر] به اندازۀ طول دو کمان یا کمتر بود.');
INSERT INTO "islamhouse" VALUES(53,10,'53:10','آنگاه [آن فرشتۀ بزرگوار] آنچه را که باید به بندۀ الله وحی می‌کرد، وحی نمود.');
INSERT INTO "islamhouse" VALUES(53,11,'53:11','آنچه را که [پیامبر] دید، قلبش دروغ نپنداشت.');
INSERT INTO "islamhouse" VALUES(53,12,'53:12','آیا با او دربارۀ آنچه می‌بیند مجادله می‌کنید؟');
INSERT INTO "islamhouse" VALUES(53,13,'53:13','در حقیقت، پیامبر یک بار دیگر نیز آن فرشته را [به صورت كامل] مشاهده كرده بود.');
INSERT INTO "islamhouse" VALUES(53,14,'53:14','در كنارِ [درخت بزرگ] «سِدرةُ المُنتَهی» [در آسمان هفتم]');
INSERT INTO "islamhouse" VALUES(53,15,'53:15','که آرامشگاه بهشت، همان‌جاست.');
INSERT INTO "islamhouse" VALUES(53,16,'53:16','آنگاه كه هاله‌اى آن درخت را پوشانده بود.');
INSERT INTO "islamhouse" VALUES(53,17,'53:17','چشم [پیامبر، به چپ و راست] منحرف نشد و [از حد] تجاوز نكرد.');
INSERT INTO "islamhouse" VALUES(53,18,'53:18','به راستی که او برخی از بزرگ‌ترین نشانه‌هاى پروردگارش را [در آنجا] مشاهده كرد.');
INSERT INTO "islamhouse" VALUES(53,19,'53:19','[ای مشرکان،] آیا «لات» و «عُزّی» را دیده‌اید؟');
INSERT INTO "islamhouse" VALUES(53,20,'53:20','و «مَنات» آن سومین [نمادِ بی‌ارزش] را؟ [آیا هیچ سود و زیانی به شما می‌رسانند؟]');
INSERT INTO "islamhouse" VALUES(53,21,'53:21','آیا [ادعا می‌کنید كه] فرزند پسر برای شماست و دختر برای الله؟');
INSERT INTO "islamhouse" VALUES(53,22,'53:22','در این صورت، تقسیمی ناعادلانه است.');
INSERT INTO "islamhouse" VALUES(53,23,'53:23','این[نماد]ها فقط اسم‌هایی [بى‌معنا] هستند كه خود و پدرانتان با آنها [معبودهاى دروغین خود را] نام نهاده‌اید و الله هیچ دلیلی [بر حقانیت این کار] نازل نکرده است. [مشرکان، در اعتقاداتشان] فقط پیرو گمان و خواسته‌های دلِ خویشند؛ حال آنكه [موجبات] هدایت، از جانب پروردگارشان بر آنان رسیده است.');
INSERT INTO "islamhouse" VALUES(53,24,'53:24','آیا انسان هر چه [از شفاعت این معبودان باطل] آرزو کند برایش میسر است؟');
INSERT INTO "islamhouse" VALUES(53,25,'53:25','دنیا و آخرت از آنِ الله است [و به هر کس، هر چه را صلاح بداند می‌بخشد].');
INSERT INTO "islamhouse" VALUES(53,26,'53:26','چه بسیار فرشتگانی در آسمان‌ها هستند كه شفاعتشان به هیچ وجه سودمند نخواهد بود؛ مگر پس از آنكه الله، براى هر کس که بخواهد و راضی باشد، اجازه دهد.');
INSERT INTO "islamhouse" VALUES(53,27,'53:27','كسانى كه به آخرت باور ندارند، بر فرشتگان الهی نام دختر می‌گذارند [و آنان را دختران الله مى‌دانند].');
INSERT INTO "islamhouse" VALUES(53,28,'53:28','آنان در موردِ این امر، [کاملاً] بى‌اطلاعند [و] فقط از حدس و گمان پیروی می‌کنند؛ در حالى كه براى [شناخت] حقیقت، گمان كافى نیست.');
INSERT INTO "islamhouse" VALUES(53,29,'53:29','پس [ای پیامبر،] تو نیز از كسى كه از یاد ما روى گردانده و فقط [لذت‌های] زندگى دنیا را مى‌خواهد، روى برتاب.');
INSERT INTO "islamhouse" VALUES(53,30,'53:30','نهایت درک و فهمشان همین است. بی‌تردید، پروردگارت به حال كسانى كه از راه او منحرف شده‌اند آگاه‌تر است؛ و نیز به حال راه‌یافتگان.');
INSERT INTO "islamhouse" VALUES(53,31,'53:31','هر چه در آسمان‌ها و زمین است، از آنِ الله است؛ [راه خیر و شر را به انسان ارائه كرد] تا بدكاران را در برابر رفتارشان كیفر دهد و نیكوكاران را [با نعمت‌های بهشت] پاداش نیكو عطا نماید.');
INSERT INTO "islamhouse" VALUES(53,32,'53:32','و [نیز براى] کسانی كه از گناهان بزرگ و بی‌حیایی‌ها اجتناب می‌ورزند؛ مگر لغزش‌های كم‌اهمیت [و گناهان صغیره که با توبه و عبادت بخشوده می‌شوند] كه آمرزشِ پروردگارت گسترده است؛ از همان دَم كه شما را از زمین پدید آورد و آنگاه كه به صورت جنین در شكم مادرانتان بودید، به [طبیعتِ] شما آگاه‌تر [از خودتان] است؛ پس خودستایى نكنید، كه او تعالی به حال پرهیزگاران داناتر است.');
INSERT INTO "islamhouse" VALUES(53,33,'53:33','آیا آن کسی را که [از حق] روی برتافت دیده‌ای؟');
INSERT INTO "islamhouse" VALUES(53,34,'53:34','و‌ اندكى انفاق كرد و [سپس] خوددارى نمود.');
INSERT INTO "islamhouse" VALUES(53,35,'53:35','آیا علم غیب دارد و [آینده را] مى‌بیند؟');
INSERT INTO "islamhouse" VALUES(53,36,'53:36','آیا از مطالب كتاب آسمانى موسى باخبر نشده است؟');
INSERT INTO "islamhouse" VALUES(53,37,'53:37','و [از تعلیمات] ابراهیم، كه حق [رسالت و بندگى ما] را به تمامى ادا كرد،');
INSERT INTO "islamhouse" VALUES(53,38,'53:38','که هیچ ‌گناهکاری بارِ گناه دیگری را بر دوش نخواهد کشید؛');
INSERT INTO "islamhouse" VALUES(53,39,'53:39','و اینكه انسان فقط ثمرۀ تلاش [و نیت] خود را خواهد داشت؛');
INSERT INTO "islamhouse" VALUES(53,40,'53:40','و اینكه بى‌گمان، تلاش او [در محاسبۀ اعمالش در روز قیامت،] منظور خواهد شد؛');
INSERT INTO "islamhouse" VALUES(53,41,'53:41','آنگاه پاداشش را به تمامى خواهند داد،');
INSERT INTO "islamhouse" VALUES(53,42,'53:42','و اینکه بازگشت [همۀ امور] به سوی پروردگار توست؛');
INSERT INTO "islamhouse" VALUES(53,43,'53:43','و اوست که می‌خندانَد و می‌گریانَد؛');
INSERT INTO "islamhouse" VALUES(53,44,'53:44','و اوست که می‌میرانَد و [در آخرت] زنده می‌کند؛');
INSERT INTO "islamhouse" VALUES(53,45,'53:45','و هموست كه زوج نر و ماده را می‌آفریند؛');
INSERT INTO "islamhouse" VALUES(53,46,'53:46','از نطفه‌اى كه [در رحِم] ریخته می‌شود؛');
INSERT INTO "islamhouse" VALUES(53,47,'53:47','و پدیدآوردن دوبارۀ آنها [در قیامت نیز بر عهدۀ] الله است.');
INSERT INTO "islamhouse" VALUES(53,48,'53:48','و اوست که [شما را] بى‌نیاز می‌کند و سرمایه می‌بخشد؛');
INSERT INTO "islamhouse" VALUES(53,49,'53:49','و اوست که پروردگار [ستارۀ] «شِعْرىٰ» است؛');
INSERT INTO "islamhouse" VALUES(53,50,'53:50','و اوست که قوم عاد را در گذشته هلاک کرد؛');
INSERT INTO "islamhouse" VALUES(53,51,'53:51','و قوم ثمود را [نیز نابود کرد] و [هیچ یک از آنان را] باقی نگذاشت.');
INSERT INTO "islamhouse" VALUES(53,52,'53:52','و قوم نوح را كه ستمكارتر و سركش‌تر بودند نیز پیش از آن [هلاک نمود]؛');
INSERT INTO "islamhouse" VALUES(53,53,'53:53','و شهرهاى زیر و روشده [قوم لوط] را [بالا برد و] درهم كوبید؛');
INSERT INTO "islamhouse" VALUES(53,54,'53:54','و آنان را [با باران سنگ] ـ چنان كه باید ـ فروپوشانید؛');
INSERT INTO "islamhouse" VALUES(53,55,'53:55','حال [اى انسان،] در مورد كدام یک از نعمت‌های پروردگارت تردید روا مى‌دارى [و مجادله می‌کنی]؟');
INSERT INTO "islamhouse" VALUES(53,56,'53:56','این [پیامبر نیز] هشداردهنده‌اى است از [گروه] هشداردهندگان پیشین.');
INSERT INTO "islamhouse" VALUES(53,57,'53:57','قیامت نزدیک شد؛');
INSERT INTO "islamhouse" VALUES(53,58,'53:58','و هیچ کس ـ جز الله ـ نمی‌تواند آن را آشکار ‌کند [و سختی‌هایش را برطرف سازد].');
INSERT INTO "islamhouse" VALUES(53,59,'53:59','[ای مشرکان،] آیا از این سخن در شگفتید؟');
INSERT INTO "islamhouse" VALUES(53,60,'53:60','و [به آیات قرآن] مى‌خندید و [از شنیدنِ هشدارهایش نمی‌ترسید و] اشک نمى‌ریزید؟');
INSERT INTO "islamhouse" VALUES(53,61,'53:61','و در حال غفلت و غرور هستید؟');
INSERT INTO "islamhouse" VALUES(53,62,'53:62','پس [همگی] برای الله سجده کنید و [تنها] او را عبادت کنید.');
INSERT INTO "islamhouse" VALUES(54,1,'54:1','قیامت نزدیک شد و ماه [از هم] شکافت.');
INSERT INTO "islamhouse" VALUES(54,2,'54:2','اگر [کافران] معجزه‌ای ببینند، روی می‌گردانند و می‌گویند: «[این] جادویی [باطل و] زودگذر است».');
INSERT INTO "islamhouse" VALUES(54,3,'54:3','آنان [آیات و نشانه‌های الهی را] دروغ می‌پندارند و از هوای نفس خویش پیروی می‌کنند؛ و هر امرى [از پاداش و کیفرهای قیامت] در زمان مخصوص به خود قرار دارد.');
INSERT INTO "islamhouse" VALUES(54,4,'54:4','و مطمئناً [ماجرای عذاب اقوام گذشته و] اخبارى كه باعث دست کشیدن از گناه می‌شود، به مشرکان رسیده است.');
INSERT INTO "islamhouse" VALUES(54,5,'54:5','این [آیات الهى، دانش و] حکمتی رساست؛ ولى [برای افراد لجوج،] هشدارها سودی ندارد؛');
INSERT INTO "islamhouse" VALUES(54,6,'54:6','پس [ای پیامبر،] از آنان روى برتاب [و منتظر] روزى [باش] كه [فرشتۀ] دعوت‌كننده [با دمیدن در صور] آنان را به امرى بس دشوار [و وحشتناک] فرا‌بخوانَد.');
INSERT INTO "islamhouse" VALUES(54,7,'54:7','با دیدگانِ فروافتاده [از وحشت] از گورها خارج می‌شوند؛ [حرکتشان به سوی دادگاه الهی، چنان است که] گویى ملخ‌هایی پراكنده‌اند.');
INSERT INTO "islamhouse" VALUES(54,8,'54:8','[هراسناک] به سوى مُنادىِ [محشر] می‌شتابند و کافران مى‌گویند: «امروز، روز دشوارى است».');
INSERT INTO "islamhouse" VALUES(54,9,'54:9','پیش از آنان، قوم نوح [نیز پیامبرشان را] تكذیب کردند و بندۀ ما را دروغگو خواندند و گفتند: «دیوانه است»؛ و او را سخت آزردند؛');
INSERT INTO "islamhouse" VALUES(54,10,'54:10','تا آنجا که او پروردگارش را خواند [و گفت]: «من مغلوب شده‌ام؛ پس یاری‌ام فرما [و از آنان انتقام بگیر]».');
INSERT INTO "islamhouse" VALUES(54,11,'54:11','ما نیز درهاى آسمان را براى [ریزشِ] رگبارى سیل‌آسا گشودیم.');
INSERT INTO "islamhouse" VALUES(54,12,'54:12','و از زمین نیز چشمه‌ها جارى ساختیم و آبها [از زمین و آسمان] براى طوفانى كه مقدر شده بود، به هم پیوست.');
INSERT INTO "islamhouse" VALUES(54,13,'54:13','و نوح را بر [كشتىِ] ساخته و پرداخته از تخته‌ها و میخ‌ها سوار كردیم');
INSERT INTO "islamhouse" VALUES(54,14,'54:14','كه تحت نظر [و حفاظتِ] ما حركت مى‌كرد. این، پاداش كسى بود كه [قومش] او را انکار کرده بودند.');
INSERT INTO "islamhouse" VALUES(54,15,'54:15','ما آن [کیفر و نجات] را نشانه‌اى [از قدرتِ خود] بر جاى گذاشتیم؛ آیا پندپذیرى هست؟');
INSERT INTO "islamhouse" VALUES(54,16,'54:16','[بنگرید] که عذاب و هشدارهای من چگونه بود.');
INSERT INTO "islamhouse" VALUES(54,17,'54:17','به راستی که قرآن را براى پند گرفتن آسان ساختیم؛ آیا پندپذیرى هست؟');
INSERT INTO "islamhouse" VALUES(54,18,'54:18','قوم عاد [نیز پیامبرشان را] دروغگو پنداشتند؛ [ای اهل مکه، بنگرید که] عذاب و هشدار‌های من چگونه بود.');
INSERT INTO "islamhouse" VALUES(54,19,'54:19','ما تندبادى سرد و سخت را در یک روز شوم و طولانی بر آنان فرستادیم.');
INSERT INTO "islamhouse" VALUES(54,20,'54:20','[طوفانی وحشتناک] كه مردم را چنان از جاى مى‌كَنْد که گویى تنه‌های نخلِ ریشه‌كن‌شده بودند.');
INSERT INTO "islamhouse" VALUES(54,21,'54:21','[ای اهل مکه، بنگرید که] عذاب و هشدار‌های من چگونه بود.');
INSERT INTO "islamhouse" VALUES(54,22,'54:22','به راستی که قرآن را براى پندگرفتن آسان ساختیم؛ آیا پندپذیرى هست؟');
INSERT INTO "islamhouse" VALUES(54,23,'54:23','قوم ثمود [نیز] هشداردهندگان را دروغگو پنداشتند.');
INSERT INTO "islamhouse" VALUES(54,24,'54:24','آنان گفتند: «آیا [سزاوار است] بشرى از میان خودمان را پیروى كنیم؟ در این صورت، در گمراهى و سردرگمى خواهیم بود.');
INSERT INTO "islamhouse" VALUES(54,25,'54:25','آیا از میان همۀ ما، تنها بر او وحی نازل شده است؟ [چنین نیست؛] بلکه او دروغگویی خودپسند است».');
INSERT INTO "islamhouse" VALUES(54,26,'54:26','فردا خواهند دانست که دروغگوی خودپسند کیست.');
INSERT INTO "islamhouse" VALUES(54,27,'54:27','[به صالح گفتیم:] «براى آزمایشِ آنان، ماده‌شترى [را كه درخواست كرده‌اند، از دلِ کوه به سویشان] خواهیم فرستاد؛ پس مراقبِ رفتارشان باش و [بر آزارشان] شكیبایى كن.');
INSERT INTO "islamhouse" VALUES(54,28,'54:28','و به آنان اعلام كن كه آب [چاه،] بین آنان [و آن ماده‌شتر] سهمیه‌بندی شده است و هر کس باید در نوبت خود حاضر شود».');
INSERT INTO "islamhouse" VALUES(54,29,'54:29','ولى آنان همدستشان را ندا دادند؛ او نیز دست به كار شد و [شتر را] به قتل رساند.');
INSERT INTO "islamhouse" VALUES(54,30,'54:30','[ای اهل مکه، بنگرید که] عذاب و هشدار‌های من چگونه بود.');
INSERT INTO "islamhouse" VALUES(54,31,'54:31','ما بانگ مرگباری بر آنان فرستادیم؛ به‌ناگاه [همگی] همچون گیاه خشكیده[ی بازمانده از كومه‌ها و حصارهای ساخته‌شده با نی و علف،] خرد و ریز شدند.');
INSERT INTO "islamhouse" VALUES(54,32,'54:32','به راستی که قرآن را براى پندگرفتن آسان ساختیم؛ آیا پندپذیرى هست؟');
INSERT INTO "islamhouse" VALUES(54,33,'54:33','قوم لوط [نیز] هشداردهندگان را دروغگو پنداشتند.');
INSERT INTO "islamhouse" VALUES(54,34,'54:34','[ما نیز] طوفانى ریگ‌بار بر سرشان فرستادیم [كه همگی را هلاک کرد]؛ مگر خانوادۀ لوط كه سحرگاهان آنان را نجات دادیم.');
INSERT INTO "islamhouse" VALUES(54,35,'54:35','[این نجات،] نعمتی از جانب ما بود؛ و به هر کس سپاسگزارى كند، این گونه پاداش مى‌دهیم.');
INSERT INTO "islamhouse" VALUES(54,36,'54:36','لوط آنان را از کیفر [سخت] ما بیم داد؛ ولی آنان در برابر هشدار‌ها به مجادله [و ستیز] برخاستند.');
INSERT INTO "islamhouse" VALUES(54,37,'54:37','آنان از لوط خواستند که مهمانانش را [برای کام‌جویی] در اختیار‌شان بگذارد؛ ما نیز چشمانشان را کور کردیم [و گفتیم:] «طعم عذاب و [نتیجۀ] هشدار‌هایم را بچشید».');
INSERT INTO "islamhouse" VALUES(54,38,'54:38','و صبحگاهان عذابى پایدار آنان را فراگرفت.');
INSERT INTO "islamhouse" VALUES(54,39,'54:39','[و گفتیم:] «طعم عذاب و [نتیجۀ] هشدار‌هایم را بچشید».');
INSERT INTO "islamhouse" VALUES(54,40,'54:40','به راستی که قرآن را براى پندگرفتن آسان ساختیم؛ آیا پندپذیرى هست؟');
INSERT INTO "islamhouse" VALUES(54,41,'54:41','هشداردهندگان به سراغ فرعونیان [نیز] رفتند.');
INSERT INTO "islamhouse" VALUES(54,42,'54:42','[ولى] آنان تمامى آیات ما را دروغ شمردند؛ ما نیز چنان [که شایستۀ پروردگار] شکست‌ناپذیرِ مقتدر [است،] آنان را عذاب کردیم.');
INSERT INTO "islamhouse" VALUES(54,43,'54:43','[ای اهل مکه،] آیا کافران شما از آنان بهترند یا برای شما امان‌نامه‌ای در کتاب‌های [آسمانی پیشین نوشته شده] است؟');
INSERT INTO "islamhouse" VALUES(54,44,'54:44','یا می‌گویند: «ما جماعتی متحد و انتقام‌جو هستیم».');
INSERT INTO "islamhouse" VALUES(54,45,'54:45','به زودى [در جنگ بدر] جمعشان شکست می‌خورد و فرارى خواهند شد.');
INSERT INTO "islamhouse" VALUES(54,46,'54:46','وعده‌گاهشان قیامت است كه [نسبت به میدان بدر، روزگارى بسیار] سخت‌تر و تلخ‌تر است.');
INSERT INTO "islamhouse" VALUES(54,47,'54:47','گناهکاران در گمراهی و عذابند.');
INSERT INTO "islamhouse" VALUES(54,48,'54:48','روزی‌که با چهره‌هایشان در آتش کشیده می‌شوند [و به آنان می‌گویند:] «آتش دوزخ را بچشید».');
INSERT INTO "islamhouse" VALUES(54,49,'54:49','بی‌تردید، ما هر چیزی را به ‌اندازه آفریده‌ایم؛');
INSERT INTO "islamhouse" VALUES(54,50,'54:50','و [چون چیزی را اراده نماییم] فرمان ما فقط یک [کلمه یعنی «کُن»] است؛ به سرعتِ یک چشم ‌بر هم ‌زدن [یا كمتر، انجام می‌پذیرد].');
INSERT INTO "islamhouse" VALUES(54,51,'54:51','به تحقیق که ما [در گذشته] امثال شما [کافران] را نابود کرده‌ایم؛ پس آیا پندپذیرى هست؟ [که از این امر عبرت بگیرد و از کفر باز ایستد]؟');
INSERT INTO "islamhouse" VALUES(54,52,'54:52','و تمام کارهایی که [بندگان] انجام داده‌اند در نامه‌هاى اعمال[شان] ثبت شده است؛');
INSERT INTO "islamhouse" VALUES(54,53,'54:53','و هر [عمل] کوچک و بزرگی [در آن] نوشته شده است.');
INSERT INTO "islamhouse" VALUES(54,54,'54:54','پرهیزگاران در باغ‌ها و [کنار] جویبارهای [بهشتی] جای دارند؛');
INSERT INTO "islamhouse" VALUES(54,55,'54:55','در مجلسى سرشار از راستی [و شایستگی] و نزد فرمانروایی مقتدر.');
INSERT INTO "islamhouse" VALUES(55,1,'55:1','[الله] رحمان');
INSERT INTO "islamhouse" VALUES(55,2,'55:2','قرآن را آموزش داد؛');
INSERT INTO "islamhouse" VALUES(55,3,'55:3','انسان را آفرید؛');
INSERT INTO "islamhouse" VALUES(55,4,'55:4','[و] به او سخن‌گفتن آموخت.');
INSERT INTO "islamhouse" VALUES(55,5,'55:5','خورشید و ماه، با حسابی منظم [و دقیق] در گردِشَند؛');
INSERT INTO "islamhouse" VALUES(55,6,'55:6','و گیاه و درخت [برایش] سجده می‌کنند.');
INSERT INTO "islamhouse" VALUES(55,7,'55:7','و آسمان را برافراشت و میزان [عدالت را در زمین] برقرار نمود.');
INSERT INTO "islamhouse" VALUES(55,8,'55:8','[ای مردم، چنین کرد] تا در وزن و پیمانه، [به یکدیگر] ستم نکنید؛');
INSERT INTO "islamhouse" VALUES(55,9,'55:9','و سنجش [حقوق دیگران] را به عدالت برآورد كنید و در سنجش [چیزی] مکاهید [و کم‌فروشی نکنید].');
INSERT INTO "islamhouse" VALUES(55,10,'55:10','و زمین را براى [زندگى] انسان‌ها مقرر داشت،');
INSERT INTO "islamhouse" VALUES(55,11,'55:11','که [انواع] میوه‌ها و نخل‌های خوشه‌دار در آن است.');
INSERT INTO "islamhouse" VALUES(55,12,'55:12','و [همچنین] حبوباتِ برگ‌دار و گیاهانِ خوشبو؛');
INSERT INTO "islamhouse" VALUES(55,13,'55:13','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,14,'55:14','او انسان را از گِل خشکی همچون سفال آفرید؛');
INSERT INTO "islamhouse" VALUES(55,15,'55:15','و جن را از شعله‌ای از آتش خلق کرد؛');
INSERT INTO "islamhouse" VALUES(55,16,'55:16','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,17,'55:17','[او] پروردگار مشرق‌ها و مغرب‌هاست؛');
INSERT INTO "islamhouse" VALUES(55,18,'55:18','پس [ای گروه انس و جن] کدام یک از نعمت‌های پروردگارتان را تکذیب می‌کنید؟');
INSERT INTO "islamhouse" VALUES(55,19,'55:19','او دو دریا[ی شور و شیرین] را روان کرد تا [به یکدیگر] برسند؛');
INSERT INTO "islamhouse" VALUES(55,20,'55:20','[در عین حال،] مانعی میان آن دو است که با هم نمی‌آمیزند؛');
INSERT INTO "islamhouse" VALUES(55,21,'55:21','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,22,'55:22','از آن دو [دریا] مروارید و مرجان به دست می‌آید؛');
INSERT INTO "islamhouse" VALUES(55,23,'55:23','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,24,'55:24','کشتی‌های كوه‌پیكرِ بادبان‌برافراشته [و جاری] در دریا برای اوست؛');
INSERT INTO "islamhouse" VALUES(55,25,'55:25','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,26,'55:26','هر چه روی زمین وجود دارد، فناپذیر است');
INSERT INTO "islamhouse" VALUES(55,27,'55:27','و [تنها ذات جاوید و] روی پروردگار باشکوه و ارجمندت باقی خواهد ماند؛');
INSERT INTO "islamhouse" VALUES(55,28,'55:28','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,29,'55:29','[تمام] کسانی که در آسمان‌ها و زمین هستند از او درخواستِ نیاز می‌کنند و او هر روز [و هر لحظه] در کاری است؛');
INSERT INTO "islamhouse" VALUES(55,30,'55:30','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,31,'55:31','ای گروه جن و انس، به زودی به حساب شما می‌پردازیم؛');
INSERT INTO "islamhouse" VALUES(55,32,'55:32','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,33,'55:33','ای گروه جن و انس، اگر مى‌توانید از كرانه‌هاى آسمان و زمین گذر كنید [تا از مجازات الهی برهید]، پس بگذرید؛ قادر [به این کار] نخواهید بود، مگر با قدرت [و بَیّنه فراوان که شما آن را ندارید]؛');
INSERT INTO "islamhouse" VALUES(55,34,'55:34','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,35,'55:35','شراره‌اى از آتش و مِس گداخته بر شما باریده مى‌شود كه [در برابر آن] قادر به دفاع [از خود] نیستید؛');
INSERT INTO "islamhouse" VALUES(55,36,'55:36','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,37,'55:37','آنگاه كه آسمان بشكافد و همچون روغن گداخته، سرخ‌فام گردد [قیامت آغاز خواهد شد].');
INSERT INTO "islamhouse" VALUES(55,38,'55:38','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,39,'55:39','آن روز، [به علت روشن‌بودن همۀ امور،] از انس و جن [دربارۀ گناهانشان] سؤال نخواهد شد؛');
INSERT INTO "islamhouse" VALUES(55,40,'55:40','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,41,'55:41','گناهکاران از سيمايشان شناخته می‌شوند؛ آنگاه [آنان را] از موی پیشانی و پاهایشان می‌گیرند [و به دوزخ می‌اندازند]؛');
INSERT INTO "islamhouse" VALUES(55,42,'55:42','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,43,'55:43','این [همان] جهنمی است که گناهکاران انکارش می‌کردند؛');
INSERT INTO "islamhouse" VALUES(55,44,'55:44','[در آنجا] میان آتش و آب بی‌نهایت داغ در حركتند؛');
INSERT INTO "islamhouse" VALUES(55,45,'55:45','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,46,'55:46','هر کس از ایستادن در پیشگاهِ پروردگارش [براى حساب] مى‌ترسد، دو باغ [در بهشت] دارد؛');
INSERT INTO "islamhouse" VALUES(55,47,'55:47','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,48,'55:48','[با درختانى] پر از شاخسارِ [تر و تازه و ثمردهنده]؛');
INSERT INTO "islamhouse" VALUES(55,49,'55:49','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,50,'55:50','در آن دو [باغ]، دو چشمه جاری است؛');
INSERT INTO "islamhouse" VALUES(55,51,'55:51','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,52,'55:52','در آن دو [باغ]، از هر میوه‌ای دو نوع وجود دارد؛');
INSERT INTO "islamhouse" VALUES(55,53,'55:53','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,54,'55:54','[بهشتیان] بر بسترهایی كه آسترش از دیباى ضخیم است تكیه می‌زنند و میوه‌هاى آن باغ‌ها در دسترسشان است؛');
INSERT INTO "islamhouse" VALUES(55,55,'55:55','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,56,'55:56','در آن باغ‌ها، حوریانی هستند که تنها [به شوهران خود] نظر دارند [و فقط به آنها عشق می‌ورزند]، و دستِ احدى از انس و جن قبلاً به آنان نرسیده است؛');
INSERT INTO "islamhouse" VALUES(55,57,'55:57','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,58,'55:58','گویی آن حوریان، یاقوت و مرجانند؛');
INSERT INTO "islamhouse" VALUES(55,59,'55:59','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,60,'55:60','آیا پاداشِ نیکی جز نیکی است؟');
INSERT INTO "islamhouse" VALUES(55,61,'55:61','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,62,'55:62','در کنار آن باغ‌ها، دو باغ [بهشتیِ] دیگر است؛');
INSERT INTO "islamhouse" VALUES(55,63,'55:63','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,64,'55:64','[آن دو باغ] كه [درختانش] در نهایت سبزى [و خرمى] است؛');
INSERT INTO "islamhouse" VALUES(55,65,'55:65','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,66,'55:66','در آن دو [باغ بهشتی،] دو چشمۀ همیشه جوشان جاری است.');
INSERT INTO "islamhouse" VALUES(55,67,'55:67','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,68,'55:68','و در آنها [درختان] میوه و درختان خرما و انار هست؛');
INSERT INTO "islamhouse" VALUES(55,69,'55:69','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,70,'55:70','در میان آن باغ‌ها، زنان نیک‌سیرت و نیکوروی، حضور دارند؛');
INSERT INTO "islamhouse" VALUES(55,71,'55:71','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,72,'55:72','حوریانی که در خیمه‌های بهشتی [از چشم بیگانگان] مستورند؛');
INSERT INTO "islamhouse" VALUES(55,73,'55:73','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,74,'55:74','[دوشیزگانی که] دست هیچ انس و جنی قبلاً به آنان نرسیده است؛');
INSERT INTO "islamhouse" VALUES(55,75,'55:75','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,76,'55:76','[بهشتیان] بر بالشت‌های سبز و بسترهایى نفیس و زیبا آرمیده‌اند؛');
INSERT INTO "islamhouse" VALUES(55,77,'55:77','پس [ای جن و انس] كدام یک از نعمت‌های پروردگارتان را انكار مى‌كنید؟');
INSERT INTO "islamhouse" VALUES(55,78,'55:78','نامِ پروردگارِ باشکوه و ارجمند تو، بابرکت و فرخنده است.');
INSERT INTO "islamhouse" VALUES(56,1,'56:1','هنگامی که واقعۀ [قیامت] رخ دهد،');
INSERT INTO "islamhouse" VALUES(56,2,'56:2','[خواهید دید كه‌] در وقوع آن دروغى نیست؛');
INSERT INTO "islamhouse" VALUES(56,3,'56:3','[گروهى را] خوار مى‌كند [و گروهى را] رفعتِ مقام مى‌بخشد.');
INSERT INTO "islamhouse" VALUES(56,4,'56:4','آنگاه كه زمین به سختى لرزانده شود');
INSERT INTO "islamhouse" VALUES(56,5,'56:5','و کوه‌ها به شدت متلاشى شوند');
INSERT INTO "islamhouse" VALUES(56,6,'56:6','و [به صورت] غبارى پراكنده درآیند');
INSERT INTO "islamhouse" VALUES(56,7,'56:7','و شما به سه گروه تقسیم شوید:');
INSERT INTO "islamhouse" VALUES(56,8,'56:8','[گروه نخست:] سعادتمندان [هستند]؛ چه سعادتمندانی [و چه نیکو جایگاهی]!');
INSERT INTO "islamhouse" VALUES(56,9,'56:9','و [گروه دیگر:] تیره‌بختان [هستند]؛ چه تیره‌بختانی [و چه بد جایگاهی]!');
INSERT INTO "islamhouse" VALUES(56,10,'56:10','و [سومین گروه:] پیشگامان که [در نیکی‌ها] سبقت گرفتند.');
INSERT INTO "islamhouse" VALUES(56,11,'56:11','آنان مقرّبان [درگاه الهی] هستند.');
INSERT INTO "islamhouse" VALUES(56,12,'56:12','در باغ‌های پرنعمت [بهشت جای دارند].');
INSERT INTO "islamhouse" VALUES(56,13,'56:13','گروه بسیاری از آنان، از پیشینیان هستند [خواه از امت اسلام یا از امت‌های گذشته]؛');
INSERT INTO "islamhouse" VALUES(56,14,'56:14','و اندکی از آیندگان [و متأخران] هستند.');
INSERT INTO "islamhouse" VALUES(56,15,'56:15','كه بر تخت‌های گوهرنشان،');
INSERT INTO "islamhouse" VALUES(56,16,'56:16','روبروى هم تكیه زده‌اند.');
INSERT INTO "islamhouse" VALUES(56,17,'56:17','نوجوانانى جاودان [با طراوت،] همواره گردِ آنان [به خدمت] مى‌گردند.');
INSERT INTO "islamhouse" VALUES(56,18,'56:18','با جام‌ها و کوزه‌ها و پیاله‌هایی از شراب جارى؛');
INSERT INTO "islamhouse" VALUES(56,19,'56:19','[شرابی] كه از نوشیدنش سردرد نمی‌گیرند و دستخوشِ مستى نمی‌شوند.');
INSERT INTO "islamhouse" VALUES(56,20,'56:20','و از هر میوه‌اى كه بخواهند [در اختیار دارند]؛');
INSERT INTO "islamhouse" VALUES(56,21,'56:21','و از هر گوشتِ پرنده‌اى که میل داشته باشند؛');
INSERT INTO "islamhouse" VALUES(56,22,'56:22','و [همسرانی از] حور العین؛');
INSERT INTO "islamhouse" VALUES(56,23,'56:23','همچون مروارید پنهان در صدف؛');
INSERT INTO "islamhouse" VALUES(56,24,'56:24','[اینها همه،] پاداش اعمالشان است.');
INSERT INTO "islamhouse" VALUES(56,25,'56:25','در آنجا [سخن] یاوه و گناه‌آلودی نخواهند شنید؛');
INSERT INTO "islamhouse" VALUES(56,26,'56:26','مگر سلام و درود [فرشتگان و بهشتیان].');
INSERT INTO "islamhouse" VALUES(56,27,'56:27','و [اما] سعادتمندان؛ چه سعادتمندانی!');
INSERT INTO "islamhouse" VALUES(56,28,'56:28','در کنار درختان بی‌خارِ سدر [هستند]؛');
INSERT INTO "islamhouse" VALUES(56,29,'56:29','و درختان موز با خوشه‌هاى برهم‌نشسته');
INSERT INTO "islamhouse" VALUES(56,30,'56:30','با سایه‌اى گسترده');
INSERT INTO "islamhouse" VALUES(56,31,'56:31','و آبِ همواره روان؛');
INSERT INTO "islamhouse" VALUES(56,32,'56:32','و میوه‌هاى فراوان');
INSERT INTO "islamhouse" VALUES(56,33,'56:33','كه نه تمام می‌شود و نه ممنوع می‌گردد،');
INSERT INTO "islamhouse" VALUES(56,34,'56:34','و بسترهای برافراشته [و ارزشمند]');
INSERT INTO "islamhouse" VALUES(56,35,'56:35','و همسرانى که با آفرینشى ویژه پدید آوردیم [که زیبایی و جوانیِ جاودان دارند]؛');
INSERT INTO "islamhouse" VALUES(56,36,'56:36','همواره دوشیزه‌اند');
INSERT INTO "islamhouse" VALUES(56,37,'56:37','و شوهردوستانی هم‌سن‌وسال هستند.');
INSERT INTO "islamhouse" VALUES(56,38,'56:38','[همۀ این نعمت‌ها،] براى سعادتمندان است');
INSERT INTO "islamhouse" VALUES(56,39,'56:39','كه برخی از آنان از پیشینیان');
INSERT INTO "islamhouse" VALUES(56,40,'56:40','و برخی دیگر از متأخران هستند.');
INSERT INTO "islamhouse" VALUES(56,41,'56:41','و [اما] تیره‌بختان؛ چه تیره‌بختانی!');
INSERT INTO "islamhouse" VALUES(56,42,'56:42','در میان باد سوزان و آب جوشان قرار دارند');
INSERT INTO "islamhouse" VALUES(56,43,'56:43','و سایه‌هایى از دود سیاه');
INSERT INTO "islamhouse" VALUES(56,44,'56:44','كه نه خنک است و نه خوش.');
INSERT INTO "islamhouse" VALUES(56,45,'56:45','آنان در دنیا ثروتمند و سرکش بودند');
INSERT INTO "islamhouse" VALUES(56,46,'56:46','و بر گناه بزرگِ [شرک] اصرار می‌ورزیدند؛');
INSERT INTO "islamhouse" VALUES(56,47,'56:47','و می‌گفتند: «آیا هنگامی که مردیم و خاک و استخوان شدیم، برانگیخته خواهیم شد؟');
INSERT INTO "islamhouse" VALUES(56,48,'56:48','آیا نیاکان ما [نیز برانگیخته می‌شوند]؟»');
INSERT INTO "islamhouse" VALUES(56,49,'56:49','[ای پیامبر، به کافران] بگو: «بی‌تردید، گذشتگان و آیندگان،');
INSERT INTO "islamhouse" VALUES(56,50,'56:50','همه در وعده‌گاهِ قیامت جمع خواهند شد».');
INSERT INTO "islamhouse" VALUES(56,51,'56:51','آنگاه شما، اى گمراهان تكذیب‌كننده،');
INSERT INTO "islamhouse" VALUES(56,52,'56:52','قطعاً از [میوۀ] درخت زَقّوم خواهید خورد؛');
INSERT INTO "islamhouse" VALUES(56,53,'56:53','و شکم‌ها را از آن انباشته مى‌كنید؛');
INSERT INTO "islamhouse" VALUES(56,54,'56:54','آنگاه روی آن، آب جوشان مى‌نوشید؛');
INSERT INTO "islamhouse" VALUES(56,55,'56:55','همچون شترانِ عطش‌زده.');
INSERT INTO "islamhouse" VALUES(56,56,'56:56','این پذیرایىِ آنان در روز جزاست.');
INSERT INTO "islamhouse" VALUES(56,57,'56:57','ماییم كه شما را [از هیچ] آفریده‌ایم؛ پس چرا [آفرینشِ دوباره را] باور ندارید؟');
INSERT INTO "islamhouse" VALUES(56,58,'56:58','آیا به نطفه‌ای که [در رحِم همسرانتان] می‌ریزید، توجه كرده‌اید؟');
INSERT INTO "islamhouse" VALUES(56,59,'56:59','آیا شما آن را مى‌آفرینید یا ما آفریننده‌اش هستیم؟');
INSERT INTO "islamhouse" VALUES(56,60,'56:60','ما مرگ را در میان شما مقدر کردیم و ناتوان نیستیم');
INSERT INTO "islamhouse" VALUES(56,61,'56:61','كه امثال شما را جایگزین [خودتان] كنیم و شما را به گونه‌اى كه از آن بى‌خبرید، [در آفرینشى نو] پدید آوریم.');
INSERT INTO "islamhouse" VALUES(56,62,'56:62','شما كه از آفرینش نخستین به روشنى آگاه شده‌اید، چرا [به یاد آخرت نمی‌افتید و] پند نمی‌گیرید؟');
INSERT INTO "islamhouse" VALUES(56,63,'56:63','آیا به دانه‌هایى كه مى‌كارید، توجه كرده‌اید؟');
INSERT INTO "islamhouse" VALUES(56,64,'56:64','آیا شما هستید كه آن را مى‌رویانید یا ما رویاننده‌ایم؟');
INSERT INTO "islamhouse" VALUES(56,65,'56:65','اگر مى‌خواستیم، آن را خاشاک مى‌كردیم؛ چنان كه شگفت‌زده شوید.');
INSERT INTO "islamhouse" VALUES(56,66,'56:66','[و چنان آن را نابود می‌کردیم که بگویید:] «ما زیان کرده‌ایم؛');
INSERT INTO "islamhouse" VALUES(56,67,'56:67','بلكه همه چیز را از دست داده‌ایم».');
INSERT INTO "islamhouse" VALUES(56,68,'56:68','آیا به آبى كه مى‌نوشید توجه كرده‌اید؟');
INSERT INTO "islamhouse" VALUES(56,69,'56:69','آیا شما آن را از ابر فروباریدید یا ما فرودآورندۀ آن هستیم.');
INSERT INTO "islamhouse" VALUES(56,70,'56:70','اگر مى‌خواستیم، آن را بسیار شور و تلخ مى‌کردیم؛ پس چرا شکر به جای نمی‌آورید؟');
INSERT INTO "islamhouse" VALUES(56,71,'56:71','آیا به آتشى كه مى‌افروزید توجه كرده‌اید؟');
INSERT INTO "islamhouse" VALUES(56,72,'56:72','آیا شما درختش را آفریده‌اید یا ما آفریده‌ایم؟');
INSERT INTO "islamhouse" VALUES(56,73,'56:73','ما این آتش را وسیلۀ یادآورى [عذاب دوزخ] و مایۀ برخوردارى [و راحتی] مسافران قرار داده‌ایم.');
INSERT INTO "islamhouse" VALUES(56,74,'56:74','پس [ای پیامبر،] به نام پروردگارِ بزرگت تسبیح گوى [و او را به پاکی یاد کن].');
INSERT INTO "islamhouse" VALUES(56,75,'56:75','سوگند به جایگاه ستارگان');
INSERT INTO "islamhouse" VALUES(56,76,'56:76','که اگر بدانید، سوگند بزرگی است.');
INSERT INTO "islamhouse" VALUES(56,77,'56:77','كه این [گفتار]، قرآنى گرانقدر است؛');
INSERT INTO "islamhouse" VALUES(56,78,'56:78','در لوح محفوظ قرار دارد.');
INSERT INTO "islamhouse" VALUES(56,79,'56:79','جز فرشتگان پاک‌سیرت، کسی [آن را نمی‌بیند و] به آن دسترسی ندارد.');
INSERT INTO "islamhouse" VALUES(56,80,'56:80','از سوی پروردگار جهانیان نازل شده است.');
INSERT INTO "islamhouse" VALUES(56,81,'56:81','آیا این سخن را سبک [و غیر قابل اعتنا] مى‌دانید؟!');
INSERT INTO "islamhouse" VALUES(56,82,'56:82','و به جای شکرِ روزی‌هایی که به شما داده شده است، آن را دروغ می‌پندارید؟');
INSERT INTO "islamhouse" VALUES(56,83,'56:83','آنگاه كه [جان نزدیكانتان] به گلوگاه مى‌رسد');
INSERT INTO "islamhouse" VALUES(56,84,'56:84','و شما در آن هنگام نظاره مى‌كنید');
INSERT INTO "islamhouse" VALUES(56,85,'56:85','و ما از شما به او نزدیک‌تریم؛ ولى نمى‌بینید.');
INSERT INTO "islamhouse" VALUES(56,86,'56:86','اگر هرگز در برابر اعمالتان جزا داده نمی‌شوید [و قیامتی نیست]،');
INSERT INTO "islamhouse" VALUES(56,87,'56:87','و اگر راست مى‌گویید، چرا جانش را بازنمى‌گردانید؟');
INSERT INTO "islamhouse" VALUES(56,88,'56:88','اما اگر او در زمرۀ مقرّبان باشد،');
INSERT INTO "islamhouse" VALUES(56,89,'56:89','در آرامش و گشایش و بهشت پرنعمت است؛');
INSERT INTO "islamhouse" VALUES(56,90,'56:90','و اگر در زمرۀ سعادتمندان باشد،');
INSERT INTO "islamhouse" VALUES(56,91,'56:91','[به او گفته می‌شود:] «سلامت و امنیت بر تو باد، که از سعادتمندان هستی»؛');
INSERT INTO "islamhouse" VALUES(56,92,'56:92','اما اگر از تکذیب‌کنندگان گمراه [و در زمرۀ تیره‌بختان] باشد،');
INSERT INTO "islamhouse" VALUES(56,93,'56:93','با آب جوشان [از او] پذیرایی می‌شود');
INSERT INTO "islamhouse" VALUES(56,94,'56:94','و به [آتش] دوزخ در‌می‌آید.');
INSERT INTO "islamhouse" VALUES(56,95,'56:95','این [وعدۀ پاداش و عذاب،] یقیناً حقیقت دارد [و تردیدی در وقوع آن نیست].');
INSERT INTO "islamhouse" VALUES(56,96,'56:96','پس [ای پیامبر،] با [ذکرِ] نامِ پروردگار بزرگت [او را] تسبیح گوی.');
INSERT INTO "islamhouse" VALUES(57,1,'57:1','آنچه در آسمان‌ها و زمین است برای الله تسبیح می‌گویند؛ و او تعالی پیروزمند حکیم است.');
INSERT INTO "islamhouse" VALUES(57,2,'57:2','فرمانروایی آسمان‌ها و زمین از آنِ اوست؛ حیات مى‌بخشد و مرگ مى‌دهد؛ و بر هر كارى تواناست.');
INSERT INTO "islamhouse" VALUES(57,3,'57:3','او ازلى و ابدى است و پیدا و ناپیدا؛ و به هر چیزى داناست.');
INSERT INTO "islamhouse" VALUES(57,4,'57:4','اوست كه آسمان‌ها و زمین را در شش روز آفرید؛ آنگاه [آن گونه كه شايسته جلال و مقام اوست] بر عرش قرار گرفت. هر چه در زمین نفوذ می‌كند یا از آن برمی‌آید و هر چه از آسمان فرود می‌آید یا در آن صعود می‌كند، [همه را] مى‌داند؛ و هر جا كه باشید، همراه شماست؛ و الله به آنچه مى‌كنید، بیناست.');
INSERT INTO "islamhouse" VALUES(57,5,'57:5','فرمانروایی آسمان‌ها و زمین از آنِ اوست؛ و همۀ امور تنها به او بازگردانده مى‌شود.');
INSERT INTO "islamhouse" VALUES(57,6,'57:6','[با تغییر تدریجى فصول] شب را در روز مى‌كشانَد و روز را در شب؛ و او به راز دل‌ها داناست.');
INSERT INTO "islamhouse" VALUES(57,7,'57:7','به الله و پیامبرش ایمان آورید و از اموالى كه شما را جانشین [و نمایندۀ خود در استفاده از آنها] قرار داده است انفاق كنید؛ زیرا افرادی از شما كه ایمان بیاورند و انفاق كنند، پاداش بزرگی [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(57,8,'57:8','چرا به الله ایمان نمى‌آورید؛ حال آنكه پیامبر، شما را فرامى‌خوانَد که به پروردگارتان ایمان بیاورید؟ اگر باور دارید، [به یاد داشته باشید که الله در این مورد] از شما پیمان گرفته است.');
INSERT INTO "islamhouse" VALUES(57,9,'57:9','اوست که آیاتی روشنگر بر بنده‌اش نازل می‌کند تا شما را از تاریکی‌های [کفر و شرک] به سوی نور [ایمان] رهسپار گردانَد؛ [زیرا] الله نسبت به شما دلسوز و مهربان است.');
INSERT INTO "islamhouse" VALUES(57,10,'57:10','چرا در راه الله انفاق نمی‌كنید، حال آنكه میراثِ آسمان‌ها و زمین از آنِ الله است؟ كسانى كه پیش از فتح [مكه] انفاق كردند و جنگیدند، [با دیگران] برابر نیستند؛ درجۀ آنان والاتر از كسانى است كه پس از فتح، انفاق كردند و جنگیدند؛ ولى الله به هر دو گروه، نیکوترین [پاداش‌ها] را وعده داده است؛ و الله به آنچه انجام می‌دهید، آگاه است.');
INSERT INTO "islamhouse" VALUES(57,11,'57:11','كیست كه به الله قرضی نیكو دهد [و انفاق کند] تا الله نیز آن را برایش چندین برابر سازد و [در آخرت] پاداشى فاخر داشته باشد؟');
INSERT INTO "islamhouse" VALUES(57,12,'57:12','[این پاداش بزرگ، در] روزى است كه مردان و زنان مؤمن را مى‌بینی كه نورشان، پیش رو و در سمت راستشان به شتاب حرکت می‌کند؛ [و به آنان خطاب مى‌شود:] «امروز بشارتِ باغ‌هایی [از بهشت] بر شما باد كه جویبارها از زیر [درختان] آن جاری است و جاودانه در آن به سر خواهید برد؛ این است همان كامیابى بزرگ».');
INSERT INTO "islamhouse" VALUES(57,13,'57:13','روزى كه مردان و زنانِ منافق به مؤمنان مى‌گویند: «منتظرمان بمانید تا پرتویی از نورتان برگیریم»؛ به آنان گفته مى‌شود: به عقب بازگردید و كسبِ نور كنید؛ آنگاه دیواری میانشان قرار می‌گیرد كه درى دارد؛ درون آن [كه به سوی مؤمنان است،] رو به رحمت است و بیرون آن [كه به سمت منافقان است] رو به عذاب است.');
INSERT INTO "islamhouse" VALUES(57,14,'57:14','[منافقان] به مؤمنان می‌گویند: «مگر ما [در دینداری و عبادت] همراه شما نبودیم؟» آنان پاسخ مى‌دهند: «بله، ولى شما [با دورویی و تزویر،] خود را به فتنه انداختید [و هلاک کردید]؛ و در انتظار [شکست مؤمنان و بازگشت به شرک و کفر] بودید و [دربارۀ یاری رساندنِ الله به پیامبر و مؤمنان] تردید داشتید و آرزوها[ی دروغین] شما را فریب داد تا آنکه فرمان [مرگتان از جانب] الله در حالی فرارسید كه شیطان شما را در برابرِ او تعالی گستاخ کرده بود».');
INSERT INTO "islamhouse" VALUES(57,15,'57:15','امروز نه از شما [منافقان] غرامت مى‌پذیرند و نه از کافران؛ جایگاهتان آتش است كه [به راستی] شایستۀ شماست؛ و چه بد سرانجامى است!');
INSERT INTO "islamhouse" VALUES(57,16,'57:16','آیا وقت آن فرا نرسیده است كه دل‌های مؤمنان به یاد الله و كلام حقى كه نازل شده است [نرم و] فروتن گردد و همچون كسانى نباشند كه قبلاً اهل كتاب بودند و چون [از بعثت پیامبرانشان] زمانى طولانى بر آنان گذشت، دل‌هایشان سخت شد و بسیارى از آنان [از دینشان] منحرف شدند؟');
INSERT INTO "islamhouse" VALUES(57,17,'57:17','آگاه باشید كه الله زمین را پس از خزان و خشكى‌اش زنده می‌سازد. [ای مردم،] مسلّماً ما نشانه‌ها[ى توان و تدبیر خود] را به روشنی برایتان بیان كرده‌ایم؛ باشد که بیندیشید.');
INSERT INTO "islamhouse" VALUES(57,18,'57:18','بى‌گمان، مردان و زنانى كه انفاق مى‌كنند و قرضی نیكو به الله مى‌دهند، ثوابشان چندین برابر می‌گردد و [در آخرت،] پاداشى فاخر [و نیک] خواهند داشت.');
INSERT INTO "islamhouse" VALUES(57,19,'57:19','كسانى كه به الله و پیامبرانش ایمان آوردند، راستگویان [راست‌کردار] هستند؛ و شهیدان نزد پروردگارشان به سر می‌برند و [در آخرت،] پاداش و نورشان محفوظ است؛ و كسانى كه کفر ورزیدند و آیات ما را دروغ شمردند، دوزخى‌اند.');
INSERT INTO "islamhouse" VALUES(57,20,'57:20','بدانید که زندگى دنیا، بازیچه و سرگرمى و زیور، و فخرفروشى در برابر یكدیگر و افزون‌طلبى در اموال و فرزندان است؛ [این زندگى،] همچون بارانى است كه گیاهان [روییده از] آن، كشاورزان را به شگفت می‌آورد؛ سپس [این گیاه شاداب،] پژمرده شود؛ آنگاه مى‌بینى كه زرد می‌شود و سپس خاشاک مى‌گردد؛ و در آخرت، [نصیبِ منافقان و کافران] عذابی شدید است؛ و [نصیبِ مؤمنان،] آمرزش و رضایت الهی است. [در هر حال] زندگى دنیا چیزی جز [لذت و] برخوردارىِ فانی [و فریبنده] نیست.');
INSERT INTO "islamhouse" VALUES(57,21,'57:21','[ای مردم،] براى رسیدن به آمرزشِ پروردگارتان و بهشتى كه پهنۀ آن، همچون پهنۀ آسمان و زمین است، از یکدیگر پیشی بگیرید. [این بهشت،] براى كسانى آماده شده است كه به الله و پیامبرانش ایمان آورده‌اند. این [پاداش، ناشی از] فضل الهی است؛ آن را به هر کس که بخواهد [و شایسته بداند] عطا مى‌كند که الله فضل و بخشش بى‌حد دارد.');
INSERT INTO "islamhouse" VALUES(57,22,'57:22','هیچ آسیبى در زمین و یا در وجود شما روى نخواهد داد، مگر پیش از آن که آن را پديد آوريم، در دفتر [علم الهی] رقم خورده است. به راستی كه این کار، بر الله آسان است.');
INSERT INTO "islamhouse" VALUES(57,23,'57:23','[این نكته را تذکر دادیم] تا بر آنچه از دستتان می‌رود افسوس نخورید و به آنچه به دست می‌آورید سرمست نگردید كه الله هیچ متكبرِ فخرفروشى را دوست ندارد.');
INSERT INTO "islamhouse" VALUES(57,24,'57:24','همان كسانى كه بخل مى‌ورزند و مردم را به بخل وامی‌دارند؛ و هر کس [از انفاق] روى بگرداندَ، [بداند كه] الله بى‌نیازِ ستوده است.');
INSERT INTO "islamhouse" VALUES(57,25,'57:25','در حقیقت، ما پیامبران خویش را با نشانه‌هاى روشن فرستادیم و كتاب و میزانِ [تشخیص حق از باطل،] با آنان نازل كردیم تا مردم به عدالت برخیزند؛ و آهن [و سایر فلزات] را پدید آوردیم كه در آن، هم نیرویی شدید وجود دارد و هم منافعى براى مردم؛ [چنین کردیم] تا الله [برای بندگانش] معلوم بدارد چه كسانى بدون آنکه پروردگارشان را ببینند، [با جنگ‌افزارهای آهنین،] او و پیامبرانش را [در میدان‌های جنگ] یارى مى‌كنند. بی‌تردید، الله نیرومندِ شکست‌ناپذیر است.');
INSERT INTO "islamhouse" VALUES(57,26,'57:26','ما نوح و ابراهیم را [به رسالت] فرستادیم و در میان فرزندانشان [موهبتِ] پیامبرى و كتاب مقرر داشتیم؛ برخى از آنان راه یافتند و بسیارى از آنان نافرمانی کردند.');
INSERT INTO "islamhouse" VALUES(57,27,'57:27','سپس به دنبال آنان، پیامبران دیگرمان را [مبعوث کردیم]؛ و سپس عیسى پسر مریم را فرستادیم و انجیل را به او عطا نمودیم و در دلِ پیروانش، رأفت و مهربانى نهادیم؛ ولی [دنیاگریزی و] رُهبانیتی را که بدعت نهادند، ما بر آنان مقرر نداشتیم؛ و هر چند هدفشان جلب رضایت الهی بود، ولی حقِ آن را [چنان که باید،] رعایت نکردند؛ از این رو، ما به افرادی از آنان که ایمان آوردند، پاداششان را عطا كردیم؛ ولى بسیارى از آنان [به بیراهه رفتند و] نافرمان بودند.');
INSERT INTO "islamhouse" VALUES(57,28,'57:28','اى مؤمنان، از الله پروا كنید و به پیامبرش [خالصانه] ایمان بیاورید تا الله از رحمت خویش، بهرۀ دوچندان به شما عطا نماید و نوری برایتان پدید آورَد كه در پرتوی آن گام بردارید و شما را ببخشاید. به راستی كه الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(57,29,'57:29','[این را بیان کردیم] تا اهل كتاب بدانند كه به فضل و بخشش الهی دسترسى ندارند [و نمی‌توانند آن را به کسی اختصاص دهند یا از کسی بازدارند]؛ و اینكه تمامى برترى به دست الله است و به هر کس که بخواهد [و شایسته بداند] عطا مى‌كند. بی‌گمان، الله فضل و بخشش بى‌حد دارد.');
INSERT INTO "islamhouse" VALUES(58,1,'58:1','الله سخن آن زن [= خَوله بنت ثَعلَبه] را شنید که دربارۀ شوهرش با تو صحبت می‌کرد و به الله شکایت می‌بُرد. الله گفتگوی شما را می‌شنود؛ چرا که الله شنوای بیناست.');
INSERT INTO "islamhouse" VALUES(58,2,'58:2','كسانى از شما كه همسران خویش را [از لحاظ حرمتِ همبستری، همچون] مادر خود تلقی مى‌كنند، [بدانند كه] آن زنان هرگز مادرانشان نخواهند بود؛ مادرانشان فقط زنانى هستند كه آنها را به دنیا آورده‌اند. آنان سخنى ناپسند و ناحق مى‌گویند؛ و [اگر توبه كنند] الله بخشایندۀ آمرزگار است.');
INSERT INTO "islamhouse" VALUES(58,3,'58:3','کسانی ‌که زنانشان را ظِهار می‌کنند، سپس از آنچه گفته‌اند باز می‌گردند، باید پیش از آمیزش جنسی با هم، برده‌ای را آزاد کنند. این حکمی است که به آن [پند و] اندرز داده می‌شوید؛ و الله به آنچه می‌کنید آگاه است.');
INSERT INTO "islamhouse" VALUES(58,4,'58:4','اگر کسی [برده‌ای را] نیابد، [باید] پیش از آمیزش جنسی، دو ماه پیاپی روزه بگیرد؛ و کسی ‌که نمی‌تواند [روزه بگیرد، در آن صورت،] باید شصت مسکین را اِطعام کند. این [کفّاره،] برای آن است که به الله و رسولش ایمان بیاورید و اینها حدود [احکام] الهی است؛ و برای کافران عذاب دردناکی [در پیش] است.');
INSERT INTO "islamhouse" VALUES(58,5,'58:5','همانا کسانی ‌که با الله و رسولش دشمنی می‌کنند، خوار [و ذلیل] می‌شوند؛ همان‌ گونه که پیشینیانِ آنها نیز خوار [و ذلیل] شدند؛ و به راستی ما آیات روشنی نازل کردیم، و برای کافران عذابی خفت‌بار [در پیش] است.');
INSERT INTO "islamhouse" VALUES(58,6,'58:6','روزی‌ که الله همۀ آنها را [از گور] برمی‌انگیزد، سپس آنها را از آنچه کرده‌اند باخبر می‌سازد؛ [همان اعمالی که] الله حساب آن را نگه داشته، اما آنها فراموشش کرده‌اند؛ و الله بر همه چیز، گواه [و ناظر] است.');
INSERT INTO "islamhouse" VALUES(58,7,'58:7','مگر ندیده‌ای که الله آنچه را که در آسمان‌ها و آنچه را که در زمین است می‌داند؟ هیچ نجوایی میان سه نفر نیست، مگر آنکه او [با علم خویش] چهارمینِ آنهاست؛ و نه میان پنج نفر، مگر آنکه او [با علم خویش] ششمینِ آنهاست؛ و نه کمتر از این [عدد] و نه بیشتر؛ مگر آنکه هر کجا باشند، او [با علم خویش] همراهِ آنهاست. سپس روز قیامت آنها را از آنچه کرده‌اند باخبر می‌سازد. بی‌گمان، الله به همه چیز داناست.');
INSERT INTO "islamhouse" VALUES(58,8,'58:8','[ای پیامبر،] مگر ندیده‌ای کسانی [= یهودیانی] را که از نجوا نهی شدند، ولی به آنچه از آن نهی شده بودند باز می‌گردند، و به [قصدِ] گناه و تجاوز و نافرمانی از پیامبر، با هم نجوا می‌کنند، و هنگامی که نزد تو می‌آیند، به نحوی به تو درود می‌فرستند که الله با آن [کلمات،] به تو درود و سلام نگفته است [و به جای السلام عليك، السَّامُ عَلَيكَ یعنی مرگ بر تو می‌گفتند]؛ و [آنها] در دل خود می‌گویند: «چرا الله ما را به [کیفر] آنچه می‌گوییم عذاب نمی‌کند؟» جهنم برایشان کافی است [که] به آن وارد می‌شوند؛ و چه بد [سرانجام و] جایگاهی است!');
INSERT INTO "islamhouse" VALUES(58,9,'58:9','ای کسانی که ایمان آورده‌اید، هنگامی که با یکدیگر نجوا می‌کنید، به [قصدِ] گناه و تجاوز و نافرمانی از رسول نجوا نکنید، و به نیکی و پرهیزگاری نجوا کنید؛ و از الله بترسید، که به سوی او محشور خواهید شد.');
INSERT INTO "islamhouse" VALUES(58,10,'58:10','همانا سخن پنهانی [که موجب گناه و دشمنی می‌گردد] از سوی شیطان است تا کسانی را که ایمان آورده‌اند [با آنها گرفتار کند و در نتیجه،] اندوهگین سازد؛ و [حال آنکه او] جز به فرمانِ الله، نمی‌تواند هیچ ضرری به آنان برساند؛ پس مؤمنان باید بر الله توکل کنند.');
INSERT INTO "islamhouse" VALUES(58,11,'58:11','ای کسانی ‌که ایمان آورده‌اید، هنگامی ‌که به شما گفته شود: «در مجالس جا باز کنید»، [جا] باز کنید [که با این کار،] الله برای شما گشایش می‌آورد؛ و هنگامی ‌که گفته شود: «برخیزید» برخیزید. الله مقام [و درجاتِ] کسانی از شما را که ایمان آورده‌اند و کسانی را که علم به آنان عطا شده‌است، بالا می‌برد؛ و الله به آنچه می‌کنید آگاه است.');
INSERT INTO "islamhouse" VALUES(58,12,'58:12','ای کسانی ‌که ایمان آورده‌اید، هنگامی که می‌خواهید با رسول الله نجوا کنید، پیش از نجوایتان، صدقه‌ای [در راه الله] بدهید. این [کار،] برای شما بهتر و پاکیزه‌تر است. اما اگر چیزی نیافتید، بی‌گمان، الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(58,13,'58:13','آیا [از فقر] ترسیدید که پیش از نجوایتان صدقاتی دهید؟ حال که چنین نکردید و الله از شما درگذشت، پس نماز برپا دارید و زکات بپردازید، و از الله و پیامبرش اطاعت کنید؛ و الله به آنچه می‌کنید آگاه است.');
INSERT INTO "islamhouse" VALUES(58,14,'58:14','[ای پیامبر،] آیا ندیدی کسانی [=منافقان] را که با قومی دوستی کردند که الله بر آنان خشم [و غضب] کرده است؛ آنها [=منافقان] نه از شما هستند و نه از آنان [= یهود]؛ و به دروغ سوگند یاد می‌کنند [که مسلمانند]، در حالی که خود می‌دانند [که منافقند].');
INSERT INTO "islamhouse" VALUES(58,15,'58:15','الله عذاب سختی برای آنها آماده کرده است؛ چرا که بی‌گمان، اعمال بدی انجام می‌دادند.');
INSERT INTO "islamhouse" VALUES(58,16,'58:16','آنها سوگندهای خود را سپر [بلا] قرار دادند و [مردم را] از راهِ الله بازداشتند؛ پس عذاب خفت‌باری [در پیش] دارند.');
INSERT INTO "islamhouse" VALUES(58,17,'58:17','هرگز اموال‌ و فرزندانشان چیزی از عذاب الله را از آنها دفع نخواهد کرد. آنها اهل [آتش] دوزخند و جاودانه در آن می‌مانند.');
INSERT INTO "islamhouse" VALUES(58,18,'58:18','روزی‌ که الله همگی آنها را برمی‌انگیزد، آنگاه آنها برای او [نیز] سوگند یاد می‌کنند، همان‌گونه که [امروز در دنیا] برای شما سوگند یاد می‌کنند و گمان می‌کنند که بر چیزی [سودمند] هستند. آگاه باشید [و بدانید] که آنها دروغگویانند.');
INSERT INTO "islamhouse" VALUES(58,19,'58:19','شیطان بر آنها چیره شده و یاد الله را از خاطرشان برده است. آنها حزب شیطان هستند؛ و آگاه باشید [و بدانید] که حزب شیطان زیانکارانند.');
INSERT INTO "islamhouse" VALUES(58,20,'58:20','بی‌گمان، کسانی که با الله و پیامبرش مخالفت [و دشمنی] می‌کنند، در زمرۀ خوارترین [افراد]ند.');
INSERT INTO "islamhouse" VALUES(58,21,'58:21','الله مقرر داشته است که «یقیناً من و رسولانم پیروز می‌شویم». بی‌گمان، الله نیرومندِ شکست‌ناپذیر است.');
INSERT INTO "islamhouse" VALUES(58,22,'58:22','[ای پیامبر،] هیچ قومی را نمی‌یابی که به الله و روز قیامت ایمان داشته باشند [و در عین حال،] با کسانی که با الله و رسولش [دشمنی و] مخالفت می‌ورزند، دوستی کنند؛ اگر چه پدرانشان یا فرزندانشان یا برادرانشان یا خویشاوندانشان باشند. آنها کسانی هستند که [الله] ایمان را در [صفحۀ] دل‌هایشان نوشته است و به روحی از جانب خود، آنها را تقویت [و تأیید] نموده است؛ و آنها را به باغ‌هایی [از بهشت] وارد می‌کند که از زیر [درختان] آن جویبارها جاری است؛ جاودانه در آن می‌مانند. الله از آنها راضی است و آنها [نیز] از الله راضیَند. آنها حزبِ الله هستند. آگاه باشید! بی‌تردید، حزبِ الله رستگارند.');
INSERT INTO "islamhouse" VALUES(59,1,'59:1','آنچه در آسمان‌ها و آنچه در زمین است، برای الله تسبیح می‌گویند؛ و او پیروزمندِ حکیم است.');
INSERT INTO "islamhouse" VALUES(59,2,'59:2','اوست که کسانی از اهل کتاب [= بنی نضیر] را که کافر شدند، با نخستین برخورد [و گرد‌آوریِ لشکر] از دیار‌شان [به سوی شام] بیرون راند. گمان نمی‌کردید که آنها خارج شوند، و خودشان [نیز] گمان می‌کردند که دژهای [محکم]شان آنها را از [عذاب] الله مانع می‌شود؛ اما [عذاب] الله از جایی که گمان نمی‌کردند به سراغشان آمد و در دل‌هایشان ترس و وحشت افکند، [به گونه‌ای که] خانه‌های خود را با دست خود و با دست مؤمنان ویران می‌کردند؛ پس ای صاحبان بینش، عبرت گیرید.');
INSERT INTO "islamhouse" VALUES(59,3,'59:3','و اگر الله ترک وطن [و آوارگی] را بر آنان مقرر نداشته بود، یقیناً آنان را در [همین] دنیا عذاب می‌کرد و در آخرت، عذاب آتش [جهنم] برایشان است.');
INSERT INTO "islamhouse" VALUES(59,4,'59:4','این [آوارگی و عذاب،] به خاطر آن است که آنها با الله و پیامبرش مخالفت [و دشمنی] کردند؛ و هر‌ کس با الله مخالفت [و دشمنی] کند، بی‌گمان، الله سخت‌کیفر است.');
INSERT INTO "islamhouse" VALUES(59,5,'59:5','هر درخت خرمایی که بریدید و یا آن را ایستاده بر ریشه‌اش باقی گذاشتید، به فرمان الله بود، تا [الله،] فاسقان را خوار [و رسوا] سازد.');
INSERT INTO "islamhouse" VALUES(59,6,'59:6','و آنچه الله از [اموال] آنها [= بنی نضیر] به پیامبرش بازگردانده [و بخشیده] است، نه اسبی برای آن [اموال] تاختید و نه شتری؛ ولی الله رسولانش را بر هر ‌کس که بخواهد، چیره می‌گردانَد؛ و الله بر همه چیز تواناست.');
INSERT INTO "islamhouse" VALUES(59,7,'59:7','و آنچه الله از [اموالِ] اهل آبادی‌ها [بدون پیکار] به پیامبرش بازگردانده [و بخشیده] است، از آنِ الله و رسول و خویشاوندان [او] و یتیمان و بینوایان و در راه‌ماندگان است. تا [این اموال،] در میان ثروتمندانِ شما دست به دست نشود؛ و آنچه که رسول الله به شما داد، بگیرید و از آنچه که شما را از آن نهی کرد دست بردارید؛ و از الله پروا کنید که الله سخت‌کیفر است.');
INSERT INTO "islamhouse" VALUES(59,8,'59:8','[بخشی از این اموال،] برای فقرای مهاجری است که از خانه و اموالشان بیرون رانده شدند، [همان کسانی که] فضل و رضامندی الله را می‌طلبند و الله و پیامبرش را یاری می‌کنند. آنها راستگویانند.');
INSERT INTO "islamhouse" VALUES(59,9,'59:9','و [نیز انصار، همان] کسانی ‌که پیش از آنان، [در مدینه] وطن گزیده و ایمان آورده‌اند، افرادی را که به سویشان هجرت می‌کنند دوست ‌دارند، و در دل‌های خود، از آنچه [به مهاجران] داده شده احساس حسادت نمی‌کنند، و آنان را بر خود مقدّم می‌دارند، حتی اگر خود [سخت] نیازمند باشند؛ و کسانی که از بُخل [و حرصِ] نفسِ خویش در امان بمانند [و در راه الله انفاق کنند]، آنان رستگارند.');
INSERT INTO "islamhouse" VALUES(59,10,'59:10','و [نیز] کسانی ‌که پس از آنها [= مهاجران و انصار] آمده‌اند، می‌گویند: «پروردگارا، ما و برادرانمان را که در ایمان بر ما پیشی گرفتند بیامرز و در دل‌هایمان نسبت به کسانی‌ که ایمان آورده‌اند کینه‌ای قرار مده. پروردگارا، بی‌گمان، تو دلسوزِ مهربانی».');
INSERT INTO "islamhouse" VALUES(59,11,'59:11','آیا منافقان را ندیدی که پیوسته به برادران اهل کتابشان که کفر ورزیده‌اند می‌گویند: «اگر شما را [از سرزمینتان] بیرون کنند، ما [نیز] با شما بیرون خواهیم آمد و هرگز [سخن] کسی را در مورد شما اطاعت نخواهیم کرد؛ و اگر با شما جنگ شود، البته یاری‌تان خواهیم کرد»؟ الله گواهی می‌دهد که آنها دروغگو هستند.');
INSERT INTO "islamhouse" VALUES(59,12,'59:12','اگر آنها [= یهود] [از وطنشان] اخراج شوند، اینان [=منافقان] همراه آنان بیرون نمی‌روند؛ و اگر با آنها جنگ شود، یاری‌شان نخواهند کرد؛ و اگر [هم] یاری‌شان کنند، قطعاً پشت [به میدان] کرده و فرار می‌کنند، و دیگر یاری نمی‌شوند.');
INSERT INTO "islamhouse" VALUES(59,13,'59:13','[ای مؤمنان،] شما قطعاً در دل‌های آنان بیش از الله مایۀ هراسید؛ زیرا آنان مردمانی هستند که نمی‌فهمند.');
INSERT INTO "islamhouse" VALUES(59,14,'59:14','آنها [= یهود] هرگز دسته‌جمعی با شما نمی‌جنگند، مگر در روستاها [و دژهای] محکم یا از پشت دیوارها. جنگشان در میان خودشان سخت است. تو آنها را متحد می‌پنداری؛ در حالی‌ که دل‌هایشان پراکنده است؛ زیرا آنها قومی هستند که نمی‌اندیشند.');
INSERT INTO "islamhouse" VALUES(59,15,'59:15','[داستان این یهودیان،] مانند [داستان] کسانی است که اندکی پیش از آنان بودند [و در واقعۀ بدر،] سزایِ کار [بدِ] خود را چشیدند؛ و عذابِ دردناکی برایشان [در پیش] است.');
INSERT INTO "islamhouse" VALUES(59,16,'59:16','[داستان منافقان با یهودیان] همچون [داستان] شیطان است، هنگامی‌ که به انسان گفت: «کافر شو»؛ اما هنگامی که کافر شد، [به او] گفت: «من از تو بیزارم؛ [چرا که] من از الله ـ پروردگار جهانیان ـ می‌ترسم».');
INSERT INTO "islamhouse" VALUES(59,17,'59:17','سرانجامِ [کار] آنها این شد که آنها هر دو در آتش [دوزخ] خواهند بود. جاودانه در آن می‌مانند و این است کیفرِ ستمکاران.');
INSERT INTO "islamhouse" VALUES(59,18,'59:18','ای کسانی‌ که ایمان آورده‌اید، از الله پروا کنید؛ و هر ‌کس باید بنگرد که برای فردا چه پیش فرستاده است؛ و از الله بترسید. بی‌گمان، الله به آنچه انجام می‌دهید آگاه است.');
INSERT INTO "islamhouse" VALUES(59,19,'59:19','و مانند کسانی نباشید که الله را فراموش کردند، و الله [نیز] آنان را دچار خودفراموشی ساخت. آنها [بدکار و] نافرمانند.');
INSERT INTO "islamhouse" VALUES(59,20,'59:20','هرگز اهل جهنم و اهل بهشت یکسان نیستند؛ اهل بهشت رستگارانند.');
INSERT INTO "islamhouse" VALUES(59,21,'59:21','اگر این قرآن را بر کوهی نازل می‌کردیم، یقیناً آن را از ترس الله خاکسار [و] از هم‌ ‌پاشیده می‌دیدی؛ و این مَثل‌ها را برای مردم می‌زنیم، باشد که بیندیشند.');
INSERT INTO "islamhouse" VALUES(59,22,'59:22','او الله است که معبودی [راستین] جز او نیست؛ دانای غیب و آشکار است؛ او بخشندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(59,23,'59:23','او الله است که معبودی [به‌حق] جز او نیست؛ [اوست] فرمانروا، منزّه، بی‌عیب و نقص، تصدیق‌کنندۀ پیامبرانش، مراقب [اعمال بندگانش]، قدرتمندِ شکست‌ناپذیر، شکوهمند و شکست‌دهندۀ مطلق، والامقام و شایستۀ عظمت. الله از چیزهایی که [برای او به عنوان] شریک می‌آورند، پاک و منزّه است.');
INSERT INTO "islamhouse" VALUES(59,24,'59:24','او الله است: خالقِ هستی، آفریدگارِ آن از نیستی [و] شکل‌دهندۀ مخلوقات؛ نام‌هایی نیک دارد. آنچه در آسمان‌ها و زمین است تسبیحِ او را می‌گویند، و او قدرتمندِ شکست‌ناپذیر [و] حکیم است.');
INSERT INTO "islamhouse" VALUES(60,1,'60:1','ای کسانی که ایمان آورده‌اید، اگر برای جهاد در راه من و طلبِ رضایت من از [وطن خود] بیرون آمده‌اید، دشمن من و دشمن خودتان را دوست مگیرید که با آنها مهربانی کنید؛ و حال آنکه آنان به دین حقی که برای شما آمده است، کفر و انکار ورزیده‌اند و پیامبر و شما را آواره کرده‌اند؛ چرا که به الله ـ پروردگارتان ـ ایمان دارید. شما پنهانی با آنها پیوند دوستی برقرار می‌کنید، در حالی که من به آنچه که پنهان می‌دارید و آنچه که آشکار می‌سازید داناترم؛ و هر کس از شما که چنین [کاری] کند، یقیناً راهِ راست را گم کرده است.');
INSERT INTO "islamhouse" VALUES(60,2,'60:2','اگر آنها بر شما دست یابند، دشمنانتان خواهند بود و به آزارتان دست و زبان می‌گشایند و دوست دارند که شما [نیز] کافر گردید.');
INSERT INTO "islamhouse" VALUES(60,3,'60:3','روز قیامت، خویشاوندانتان و فرزندانتان هرگز سودی به حالتان نخواهند داشت. الله در میان شما جدایی می‌اندازد و الله به آنچه انجام می‌دهید بیناست.');
INSERT INTO "islamhouse" VALUES(60,4,'60:4','یقیناً در [زندگیِ] ابراهیم و کسانی ‌که با او بودند سرمشق خوبی برایتان وجود دارد؛ آنگاه که به قوم خود گفتند: «ما از شما و از آنچه غیر از الله عبادت می‌کنید بیزاریم؛ به شما کافر شده‌ایم، و میان ما و شما دشمنی و کینۀ همیشگی پدید آمده است، تا وقتی که به الله یگانه ایمان آورید»؛ مگر این سخن ابراهیم [که قابل پیروی نیست، آنگاه] که به پدرش [آزر] گفت: «من برایت آمرزش طلب می‌کنم و در برابر الله برای تو اختیارِ چیزی را ندارم». پروردگارا، بر تو توکل کردیم و به سوی تو روی آوردیم و بازگشت [همه] به سوی توست.');
INSERT INTO "islamhouse" VALUES(60,5,'60:5','پروردگارا، ما را دستخوشِ [فتنۀ] کافران قرار مده و ما را بیامرز ای پروردگار ما؛ همانا تو پیروزمندِ حکیمی».');
INSERT INTO "islamhouse" VALUES(60,6,'60:6','مسلماً در [زندگی] آنها [= ابراهیم و یارانش] سرمشق خوبی برای شماست، برای کسانی‌ که به الله و روز قیامت امید دارند؛ و هر ‌کس رویگردان شود [بداند که] الله بی‌نیازِ ستوده است.');
INSERT INTO "islamhouse" VALUES(60,7,'60:7','امید است که الله میان شما و کسانی [از مشرکان] که با آن‌ها دشمنی داشته‌اید [با اسلام آوردن‌شان، پیوند] دوستی [و محبت] برقرار کند؛ و الله تواناست؛ و الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(60,8,'60:8','الله شما را از نیکی ‌کردن و رعایت عدالت نسبت به کسانی که در [امر] دین، با شما نجنگیده‌اند و شما را از دیارتان بیرون نکرده‌اند، نهی نمی‌کند. بی‌گمان، الله عدالت‌پیشگان را دوست دارد.');
INSERT INTO "islamhouse" VALUES(60,9,'60:9','[او] تنها شما را از دوستی با کسانی نهی می‌کند که در [امر] دین با شما جنگیده‌اند و شما را از دیارتان بیرون کرده‌اند و بر بیرون ‌راندنِ شما [به دیگران] کمک کرده‌اند؛ و هر ‌کس با آنان دوستی کند، آنان همان ستمکارانند.');
INSERT INTO "islamhouse" VALUES(60,10,'60:10','ای کسانی ‌که ایمان آورده‌اید، هنگامی ‌که زنان مؤمنِ مهاجر نزد شما می‌آیند، آنها را بیازمایید [تا از صحت ایمانشان آگاه گردید]؛ الله به ایمانشان داناتر است. اگر آنان را [زنانی] مؤمن یافتید، آنها را به سوی کافران بازنگردانید. نه این [زنان مؤمن] بر آنان [= کافران] حلالند، و نه آن [مردان‌ کافر] بر این [زنان] حلالند؛ و هر چه [شوهران کافر به عنوان مهریه] خرج [این زنان] کرده‌اند، به آنها بدهید؛ و بر شما گناهی نیست که پس از آنکه مهریه‌هایشان را پرداختید [و پس از انقضای عده،] با آنان ازدواج کنید؛ و به [نگه‌داشتن پیوند زناشویی با] زنان کافر، پایبند نباشید؛ [و اگر زنان شما مرتد شدند و نزدِ کافران رفتند، رهایشان کنید] و آنچه را [به عنوان مهریه] خرج کرده‌اید [از کافران] مطالبه کنید؛ و [کافران نیز] آنچه را خرج کرده‌اند [از شما] مطالبه کنند. این حکمِ الله است که در میان شما داوری می‌کند؛ و الله دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(60,11,'60:11','اگر یکی از همسران شما به سوی کافران رفت [و مهریه‌اش را از کافران طلب کردید و آن‌ها پرداخت نکردند]، آنگاه شما [در جنگ بر آنان پیروز شدید و] و از آنان غنیمت گرفتید، به کسانی ‌که همسرانشان [به سوی کفار] رفته‌اند، همانند آنچه را که خرج کرده‌اند [از غنایم] بپردازید؛ و از الله که شما به او ایمان دارید پروا کنید.');
INSERT INTO "islamhouse" VALUES(60,12,'60:12','ای پیامبر، هنگامی‌ که زنان مؤمن نزد تو آمدند تا با تو بیعت کنند که چیزی را با الله شریک نسازند و دزدی نکنند و مرتکب زنا نشوند و فرزندان خود را نکشند و فرزندی را به دروغ به شوهرانشان نسبت ندهند و در کارهای نیک از تو نافرمانی نکنند، با آنان بیعت کن و از الله برایشان آمرزش بخواه. بی‌گمان، الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(60,13,'60:13','ای کسانی ‌که ایمان آورده‌اید، با قومی که الله بر آنان خشم [و غضب] گرفته است دوستی نکنید. آنها از آخرت نومید هستند، همان ‌گونه که کافران، [به سبب کفر به رستاخیز] از [دوباره زنده شدن] گورخفتگان [در قیامت] نومیدند.');
INSERT INTO "islamhouse" VALUES(61,1,'61:1','آنچه در آسمان‌ها و آنچه در زمین است، تسبیح الله را می‌گویند؛ و او پیروزمندِ حکیم است.');
INSERT INTO "islamhouse" VALUES(61,2,'61:2','ای کسانی که ایمان آورده‌اید، چرا چیزی را می‌گویید که [به آن] عمل نمی‌کنید؟');
INSERT INTO "islamhouse" VALUES(61,3,'61:3','نزد الله سخت ناپسند [و موجب خشم] است که چیزی را بگویید که [به آن] عمل نمی‌کنید.');
INSERT INTO "islamhouse" VALUES(61,4,'61:4','بی‌گمان، الله کسانی را که چون بنیانی آهنین، صف ‌بسته [و] در راه او پیکار می‌کنند دوست دارد.');
INSERT INTO "islamhouse" VALUES(61,5,'61:5','و [ای پیامبر، به یاد آور] هنگامی را که موسی به قومش گفت: «ای قوم من، چرا به من آزار [و اذیت] می‌رسانید، در حالی ‌که می‌دانید بی‌گمان، من فرستادۀ الله به سوی شما هستم؟» آنان چون [از حق] منحرف شدند، الله دل‌هایشان را منحرف ساخت؛ و الله قومِ نافرمان را هدایت نمی‌کند.');
INSERT INTO "islamhouse" VALUES(61,6,'61:6','و [به یاد آور] هنگامی‌ که عیسی ـ پسر مریم ـ گفت: «ای بنی‌اسرائیل، بی‌گمان، من فرستادۀ الله به سوی شما هستم. آنچه از تورات را که پیش از من بوده تصدیق می‌کنم و [نیز] مژده‌دهنده به رسولی هستم که بعد از من می‌آید و نامش "احمد" است». اما هنگامی‌ که او [= احمد] با معجزه‌ها [و دلایل روشن] به سوی آنان آمد، گفتند: «این جادوی آشکاری است».');
INSERT INTO "islamhouse" VALUES(61,7,'61:7','چه کسی ستمکارتر از آن ‌کسی است که بر الله دروغ ببندد، در حالی ‌که به [دین] اسلام دعوت می‌شود؟ و الله گروه ستمکاران را هدایت نمی‌کند.');
INSERT INTO "islamhouse" VALUES(61,8,'61:8','آنها می‌خواهند نورِ الله را با دهان خود خاموش کنند؛ و[لی] الله کامل‌کنندۀ نور خویش است؛ هر چند کافران خوششان نیاید.');
INSERT INTO "islamhouse" VALUES(61,9,'61:9','او ذاتی است که فرستادۀ خود را با هدایت و دین حق فرستاد، تا آن را بر همۀ ادیان پیروز گردانَد؛ هر چند مشرکان خوششان نیاید.');
INSERT INTO "islamhouse" VALUES(61,10,'61:10','ای کسانی که ایمان آورده‌اید، آیا [می‌خواهید] شما را به تجارتی راهنمایی کنم که شما را از عذاب دردناک نجات ‌دهد؟');
INSERT INTO "islamhouse" VALUES(61,11,'61:11','به الله و پیامبرش ایمان بیاورید و با اموال و جان‌هایتان در راهِ الله جهاد کنید. اگر بدانید، این [کار] برای شما بهتر است.');
INSERT INTO "islamhouse" VALUES(61,12,'61:12','[اگر چنین کردید، [الله] گناهانتان را می‌بخشد و شما را در باغ‌هایی [از بهشت] وارد می‌کند که جویبارها از زیر [درختان] آن جاری است و در خانه‌های پاکیزه در بهشت جاویدان [جای می‌دهد]. این کامیابی بزرگی است.');
INSERT INTO "islamhouse" VALUES(61,13,'61:13','و [نعمت] دیگری که آن را دوست دارید [نیز به شما عنایت می‌فرماید که آن،] یاری از سوی الله و پیروزیِ نزدیک است؛ و مؤمنان را [به این پیروزی] بشارت ده.');
INSERT INTO "islamhouse" VALUES(61,14,'61:14','ای کسانی‌ که ایمان آورده‌اید، یاورانِ [دین] الله باشید؛ همان ‌گونه که عیسی ـ پسر مریم‌ـ به حواریون گفت: «چه کسانی یاوران من [در دعوت] به سوی الله هستند؟» حواریون گفتند: «ما یاوران [دینِ] الله هستیم». سپس گروهی از بنی‌اسرائیل ایمان آوردند و گروهی کافر شدند. آنگاه ما کسانی را که ایمان آورده بودند بر دشمنانشان نیرو [و توانایی] دادیم، و سرانجام پیروز شدند.');
INSERT INTO "islamhouse" VALUES(62,1,'62:1','آنچه در آسمان‌ها و آنچه در زمین است، تسبیحِ الله را می‌گویند؛ [الله] که فرمانروا، منزّه [و] پیروزمندِ حکیم است.');
INSERT INTO "islamhouse" VALUES(62,2,'62:2','و ذاتی است که از میان بی‌سوادان، پیامبری از میان خودشان برانگیخت که آیاتش را بر آنها بخوانَد و آنان را پاک گردانَد و کتاب [= قرآن] و حکمت [= سنت] به آنان بیاموزاند؛ و به راستی که آنان پیش از این[که پیامبر به سویشان فرستاده شود]، در گمراهیِ آشکاری بودند.');
INSERT INTO "islamhouse" VALUES(62,3,'62:3','و [این پیامبر نیز] به سوی قومی دیگر از ایشان که هنوز به اینان ملحق نشده‌اند [و بعدها به دنیا خواهند آمد، مبعوث شده است]؛ و او تعالی شکست‌ناپذیرِ حکیم است.');
INSERT INTO "islamhouse" VALUES(62,4,'62:4','این فضلِ الله است که آن را به هر‌ کس که بخواهد می‌بخشد؛ و الله دارای فضل عظیم است.');
INSERT INTO "islamhouse" VALUES(62,5,'62:5','مَثَل کسانی که [عمل به] تورات به آنان تکلیف شد ولی آن را [چنان که باید و شاید] رعایت نکردند، مانند خری است که کتاب‌هایی را حمل می‌کند، [اما چیزی از آن نمی‌فهمد]. چه بد است مَثَل گروهی که آیات الهی را تکذیب کردند! و الله گروه ستمکاران را هدایت نمی‌کند.');
INSERT INTO "islamhouse" VALUES(62,6,'62:6','بگو: «ای یهود، اگر گمان دارید که [تنها] شما دوستان الله هستید نه مردم دیگر، اگر راست می‌گویید، مرگ را آرزو کنید».');
INSERT INTO "islamhouse" VALUES(62,7,'62:7','ولی آنها به خاطر آنچه از پیش فرستاده‌اند، هرگز آن آرزو را نخواهند کرد؛ و الله [از حالِ] ستمکاران آگاه است.');
INSERT INTO "islamhouse" VALUES(62,8,'62:8','[ای پیامبر،] بگو: «آن مرگی که از آن فرار می‌کنید، یقیناً به شما خواهد رسید؛ سپس به سوی [الله، آن] دانای غیب و آشکار بازگردانده می‌شوید؛ آنگاه شما را از آنچه انجام می‌دادید آگاه می‌سازد».');
INSERT INTO "islamhouse" VALUES(62,9,'62:9','ای کسانی که ایمان آورده‌اید، هنگامی ‌که برای نماز روز جمعه، اذان گفته شد، به سوی [نماز و] ذکرِ الله بشتابید [و درنگ نکنید]، و خرید و فروش را رها کنید. اگر بدانید، این [کاری که به شما دستور داده می‌شود] برایتان بهتر است.');
INSERT INTO "islamhouse" VALUES(62,10,'62:10','و هنگامی ‌که نماز پایان یافت، [برای کسب رزق و روزی] در زمین پراکنده شوید و از فضلِ الله طلب کنید؛ و الله را بسیار یاد کنید تا رستگار شوید.');
INSERT INTO "islamhouse" VALUES(62,11,'62:11','[ای پیامبر، برخی از این مردم] هنگامی ‌که تجارت یا سرگرمی‌ای ببینند، [از اطرافِ تو] به سوی آن پراکنده می‌شوند و تو را ایستاده [بر منبر] رها می‌کنند. بگو: «آنچه در نزد الله است، بهتر از سرگرمی و تجارت است؛ و الله بهترین روزی‌دهنده است».');
INSERT INTO "islamhouse" VALUES(63,1,'63:1','هنگامی که منافقان نزد تو آیند، [سوگند می‌خورند و] می‌گویند: «ما گواهی می‌دهیم که یقیناً تو فرستادۀ الله هستی»؛ و الله می‌داند که بی‌گمان، تو فرستادۀ او هستی؛ و الله گواهی می‌دهد که منافقان یقیناً دروغگو هستند.');
INSERT INTO "islamhouse" VALUES(63,2,'63:2','آنها سوگندهای خود را سپر ساخته‌اند، در نتیجه [مردم را] از راه الله بازداشته‌اند. به راستی که آنان چه بد می‌کنند!');
INSERT INTO "islamhouse" VALUES(63,3,'63:3','این به خاطر آن است که آنها ایمان آوردند، سپس کافر شدند؛ آنگاه بر دل‌هایشان مُهر نهاده شده، پس درنمی‌یابند.');
INSERT INTO "islamhouse" VALUES(63,4,'63:4','و هنگامی‌ که آنها را ببینی، جسم [و ظاهر] شان [از آراستگی و وقار] تو را به شگفت آورَد؛ و اگر [سخن] گویند، [به علت شیرینی و جذابیّت کلام‌شان] به سخنانشان گوش می‌دهی. [ای پیامبر] گویی آنها [در مجلس تو] چوب‌های تکیه‌داده به دیوارند، [که هیچ چیزی نمی‌فهمند و درک نمی‌کنند، و به سبب بزدلی] هر بانگی را علیه خود می‌پندارند. آنان دشمن [حقیقی] هستند؛ پس از آنان بر حذر باش. الله آنها را بکشد! چگونه [از حق] منحرف می‌شوند؟');
INSERT INTO "islamhouse" VALUES(63,5,'63:5','و هنگامی که به آنها گفته شود: «بیایید تا رسول الله برای شما آمرزش بخواهد» [از روی تمسخر و استهزا] سرهایشان را تکان می‌دهند، و آنها را می‌بینی که [از سخنان تو] رویگردان می‌شوند و تکبر می‌ورزند.');
INSERT INTO "islamhouse" VALUES(63,6,'63:6','برای آنها یکسان است که برایشان آمرزش بخواهی یا نخواهی؛ الله هرگز آنان را نمی‌بخشد. بی‌گمان، الله قومِ نافرمان را هدایت نمی‌کند.');
INSERT INTO "islamhouse" VALUES(63,7,'63:7','آنها کسانی هستند که می‌گویند: «به کسانی که نزد رسول الله هستند [چیزی] انفاق نکنید تا [از گِرد او] پراکنده شوند؛ حال آنکه گنجینه‌های آسمان‌ها و زمین، از آنِ الله است؛ ولی منافقان درنمی‌یابند.');
INSERT INTO "islamhouse" VALUES(63,8,'63:8','آنها می‌گویند: «اگر به مدینه بازگردیم، یقیناً صاحبان عزت، فرومایگان را از آنجا بیرون می‌کنند»؛ در حالی که عزت، از آنِ الله و فرستادۀ او و مؤمنان است؛ ولی منافقان نمی‌دانند.');
INSERT INTO "islamhouse" VALUES(63,9,'63:9','ای کسانی که ایمان آورده‌اید، اموال و فرزندانتان شما را از یادِ الله غافل نکند؛ و هر ‌کس که چنین کند، آنان زیانکارانند.');
INSERT INTO "islamhouse" VALUES(63,10,'63:10','و از آنچه به شما روزی داده‌ایم، [در راه الله] انفاق کنید، پیش از آنکه مرگ به سراغ یکی از شما آید و بگوید: «پروردگارا، ای کاش [مرگ] مرا مدت کمی به تأخیر می‌انداختی تا [در راه تو] صدقه دهم و از نیکوکاران باشم».');
INSERT INTO "islamhouse" VALUES(63,11,'63:11','و[لى‌] هر كس اجلش فرارسد، هرگز الله [آن را] به تأخیر نمى‌اندازد، و الله از آنچه مى‌كنید آگاه است.');
INSERT INTO "islamhouse" VALUES(64,1,'64:1','آنچه در آسمان‌ها و آنچه در زمین است همه تسبیح الله را می‌گویند. فرمانروایی از آنِ اوست و سپاس [و ستایش نیز] از آنِ اوست؛ و او بر همه چیز تواناست.');
INSERT INTO "islamhouse" VALUES(64,2,'64:2','او ذاتی است که شما را آفرید؛ [گروهی] از شما کافرند و [گروهی] از شما مؤمن هستند؛ و الله از آنچه می‌کنید آگاه است.');
INSERT INTO "islamhouse" VALUES(64,3,'64:3','آسمان‌ها و زمین را به حق آفرید و شما را [در رحِم مادرانتان] شکل و صورت بخشید. و شکل و صورتتان را نیکو [و زیبا] گرداند، و بازگشت [همه] به سوی اوست.');
INSERT INTO "islamhouse" VALUES(64,4,'64:4','آنچه را که در آسمان‌ها و زمین است می‌داند و آنچه را که پنهان می‌دارید و آنچه را که آشکار می‌کنید [نیز] می‌داند؛ و الله از آنچه در سینه‌هاست آگاه است.');
INSERT INTO "islamhouse" VALUES(64,5,'64:5','آیا خبر کسانی‌ که پیش از این کافر شدند، به شما نرسیده است؟ آنها [طعم تلخ] کیفر کار خود را چشیدند و عذاب دردناکی خواهند داشت.');
INSERT INTO "islamhouse" VALUES(64,6,'64:6','این [کیفر] به سبب آن است که پیامبرانشان با معجزات [و دلایل روشن] به سوی آنان آمدند، اما [آنها از روی تکبر] گفتند: «آیا بشری [مانند خودمان] می‌خواهد ما را هدایت کند؟». آنگاه کافر شدند و [از حق] روی گرداندند و الله [از آنان و ایمانشان] بی‌نیاز بود؛ و الله بی‌نیازِ ستوده است.');
INSERT INTO "islamhouse" VALUES(64,7,'64:7','کسانی ‌که کافر شدند، گمان بردند که هرگز برانگیخته نخواهند شد. بگو: «آری؛ به پروردگارم سوگند، یقیناً [همه] برانگیخته خواهید شد؛ آنگاه از آنچه می‌کردید به شما خبر خواهند داد؛ و این [کار] بر الله آسان است».');
INSERT INTO "islamhouse" VALUES(64,8,'64:8','پس به الله و پیامبرش و نوری که نازل کرده‌ایم ایمان بیاورید؛ و الله از آنچه انجام می‌دهید آگاه است.');
INSERT INTO "islamhouse" VALUES(64,9,'64:9','روزی ‌که [همۀ] شما را [در محشر] گرد می‌آورَد. آن روز، روز زیانمندی [کافران، و سودمندی مؤمنان] است؛ و هر کس به الله ایمان آورد و کارهای شایسته انجام دهد، گناهان او را می‌بخشد و او را به باغ‌هایی [از بهشت] وارد می‌کند که جویبارها زیر [درختان] آن جاری است؛ جاودانه در آن می‌مانند. این کامیابی بزرگ است.');
INSERT INTO "islamhouse" VALUES(64,10,'64:10','و کسانی ‌که کافر شدند و آیات ما را تکذیب کردند، آنان اهل آتش [دوزخ]ند [و] جاودانه در آن می‌مانند؛ و چه بد [سرانجام و] جایگاهی است!');
INSERT INTO "islamhouse" VALUES(64,11,'64:11','هیچ مصیبتی [به انسان] نمی‌رسد، مگر به حکم [و قضا و قدر] الله؛ و هر ‌کس به الله ایمان آورَد، [الله] قلبش را هدایت می‌کند؛ و الله به همه چیز داناست.');
INSERT INTO "islamhouse" VALUES(64,12,'64:12','از الله اطاعت کنید و رسول را [نیز] اطاعت کنید. اگر [از اطاعت] روی بگردانید، [بدانید که گناه این رویگردانی بر خودتان است و] فرستادۀ ما وظیفه‌ای جز ابلاغِ آشکار ندارد.');
INSERT INTO "islamhouse" VALUES(64,13,'64:13','الله است که هیچ معبودی [به‌حق] جز او نیست؛ پس مؤمنان باید بر الله توکل کنند.');
INSERT INTO "islamhouse" VALUES(64,14,'64:14','ای کسانی‌ که ایمان آورده‌اید، به راستی که [بعضی] از همسرانتان و فرزندانتان دشمنان شما هستند؛ [چون شما را از یاد الله و جهاد بازمی‌دارند]؛ پس از آنها بر حذر باشید؛ و اگر عفو کنید و [از لغزشهایشان] چشم بپوشید و ببخشید، بی‌گمان، الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(64,15,'64:15','اموال و فرزندانتان فقط وسیلۀ آزمایش [شما] هستند؛ و الله است که پاداش بزرگ نزد اوست.');
INSERT INTO "islamhouse" VALUES(64,16,'64:16','تا آنجا که می‌توانید، از الله پروا کنید و [حق را] بشنوید و فرمان برید و مالی برای [مصلحت] خودتان [در راه الله] انفاق کنید؛ و کسانی ‌که از بُخل و حرصِ نفس خویش در امان بمانند، آنانند که رستگارند.');
INSERT INTO "islamhouse" VALUES(64,17,'64:17','اگر به الله قرض نیکو دهید، آن را برای شما چندین برابر می‌کند و شما را می‌آمرزد؛ و الله قدرشناسِ بردبار است.');
INSERT INTO "islamhouse" VALUES(64,18,'64:18','[او] دانای پنهان و آشکار [و] پیروزمندِ حکیم است.');
INSERT INTO "islamhouse" VALUES(65,1,'65:1','ای پیامبر، هرگاه خواستید زنان را طلاق دهید، آنها را در زمان [آغاز] عده طلاق دهید [یعنی پس از پاک شدن از حیض و پیش از آمیزش]، و حساب عدّه را نگه دارید، و از الله که پروردگار شماست پروا کنید. نه شما آنها را [تا پایان عده] از خانه‌هایشان بیرون کنید و نه آنها بیرون روند؛ مگر آنکه کار زشتِ آشکاری [مانند زنا] مرتکب شوند؛ و این حدود [احکام] الله است؛ و هر ‌کس از حدود الله تجاوز کند، مسلماً به خود ستم کرده است. تو نمی‌دانی، شاید الله پس از آن، وضع تازه‌ای فراهم آورَد [و رغبتی در دل زوجین بیندازد که به صلح و آشتی منجر شود].');
INSERT INTO "islamhouse" VALUES(65,2,'65:2','و هنگامی که [آنها را طلاق دادید و] عده‌شان به سر رسید، آنان را به طرز شایسته‌ای نگه‌ دارید یا به طرز شایسته‌ای از آنها جدا شوید؛ و دو مرد عادل از خودتان را گواه بگیرید و شهادت را برای الله برپا دارید. این [حکمی است] که هر کس به الله و روز قیامت ایمان دارد، به آن پند و اندرز داده می‌شود؛ و هر‌ کس که از الله بترسد، راه نجاتی برای او قرار می‌دهد');
INSERT INTO "islamhouse" VALUES(65,3,'65:3','و او را از جایی که گمان ندارد، روزی می‌دهد؛ و هر کس بر الله توکل کند، او تعالی برایش کافی است. بی‌گمان، الله فرمان خود را به انجام می‌رسانَد. مسلّماً الله برای هر چیزی اندازه‌ای قرار داده است.');
INSERT INTO "islamhouse" VALUES(65,4,'65:4','و کسانی از زنانتان که [به سبب سن زیاد و ...] از عادت ماهانه مأیوس شده‌اند، اگر [در چگونگی عده آنان] شک کردید، عدّۀ آنان سه ماه است، و زنانی که عادت ماهانه نديده‌اند [نيز عده‌شان سه ماه است‌]. و عدّۀ زنان باردار این است که وضع حمل کنند؛ و هر کس از الله بترسد، [الله] کارش را برایش آسان می‌سازد.');
INSERT INTO "islamhouse" VALUES(65,5,'65:5','این، فرمان الله است که بر شما نازل کرده است؛ و هر کس از الله پروا کند، الله گناهانش را از او می‌زداید [و می‌بخشد] و پاداش او را بزرگ می‌گردانَد.');
INSERT INTO "islamhouse" VALUES(65,6,'65:6','آنان [= زنان مطلقه] را در حد توان خود، هر جا که خودتان سکونت دارید [و در تواناییِ شماست] سکونت دهید، و به آنها زیان نرسانید تا [عرصه را] بر آنان تنگ کنید [و پیش از پایان عده، به ترک منزل مجبور شوند]؛ و اگر باردار هستند، نفقۀ آنها را بدهید تا وضع حمل کنند. اگر [فرزندتان را] برای شما شیر می‌دهند، مزدشان را بپردازید و [این کار را] به نیکی و با مشورت یکدیگر انجام دهید؛ و اگر به توافق نرسیدید، زن دیگری [به درخواست شوهر،] او را شیر دهد.');
INSERT INTO "islamhouse" VALUES(65,7,'65:7','کسانی که دارا [و ثروتمند] هستند، باید از دارایی [و ثروت] خود [بر زن مطلقه و فرزندش] خرج کنند، و کسی‌ که تنگدست است، باید از آنچه که الله به او داده [و در توانش است] خرج کند. الله هیچ کس را جز به ‌آن اندازه که به او [توان] داده است مکلّف نمی‌کند. الله به زودی بعد از سختی [و تنگدستی،] آسانی [و گشایش] قرار می‌دهد.');
INSERT INTO "islamhouse" VALUES(65,8,'65:8','چه بسیار [اهالی] شهرها که از فرمان پروردگارشان و [فرمان] پیامبرانش سرپیچی کردند؛ ما به شدت از آنها حساب کشیدیم و آنها را به عذابی سخت [و سهمناک] عذاب کردیم.');
INSERT INTO "islamhouse" VALUES(65,9,'65:9','پس آنان کیفر کار خود را چشیدند، و سرانجامِ کارشان زیان بود.');
INSERT INTO "islamhouse" VALUES(65,10,'65:10','الله عذاب سختی برایشان مهیّا کرده است؛ پس ای خردمندانی که ایمان آورده‌اید، از الله پروا کنید. همانا الله ذکر [= قرآن] را به سوی شما نازل کرده است.');
INSERT INTO "islamhouse" VALUES(65,11,'65:11','[و نیز] پیامبری [فرستاده] که آیات روشن الله را بر شما می‌خوانَد، تا کسانی را که ایمان آورده‌ و کارهای شایسته انجام داده‌اند، از تاریکی‌ها به سوی نور بیرون آورَد؛ و هر کس به الله ایمان آورَد و کار شایسته انجام دهد، [الله] او را به باغ‌هایی [از بهشت] وارد می‌کند که از زیر [درختان] آن جویبارها جاری است؛ جاودانه در آن می‌مانند. به راستی الله رزق و روزی‌اش را نیکو گردانیده است.');
INSERT INTO "islamhouse" VALUES(65,12,'65:12','الله است که هفت آسمان را آفرید و از زمین [نیز] همانند آنها را [آفرید]؛ و فرمان [الله] پیوسته در میان آنها نازل می‌شود، تا بدانید که الله بر همه چیز تواناست، و اینکه علم الله بر همه چیز احاطه دارد.');
INSERT INTO "islamhouse" VALUES(66,1,'66:1','ای پیامبر، چرا چیزی را که الله بر تو حلال کرده است، به خاطر راضی‌کردن همسرانت [بر خود] حرام می‌کنی؟ و [بدان که در همه حال،] الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(66,2,'66:2','به راستی که الله شکستن سوگندهایتان را [با دادن کفّاره‌] بر شما روا داشته است؛ و الله دوستدار [و کارساز] شماست و او دانای حکیم است.');
INSERT INTO "islamhouse" VALUES(66,3,'66:3','و [به یاد آور] آنگاه ‌که پیامبر با یکی از همسرانش رازی را در میان نهاد، و [آن همسر] آن راز را [به همسر دیگر] خبر داد و الله او [= پیامبر] را از آن [افشای راز] آگاه کرد، [پیامبر] قسمتی از آن [رازگویی] را [برای همسر رازگویش] بازگو کرد و از [بازگویی] قسمتی [دیگر] خودداری نمود؛ هنگامی ‌که [پیامبر] او را از آن[چه که ذکر کرده بود] باخبر ساخت، [آن زن] گفت: «چه کسی تو را از این امر باخبر کرده است؟» [پیامبر] گفت: «[الله] دانای آگاه مرا باخبر ساخته است».');
INSERT INTO "islamhouse" VALUES(66,4,'66:4','[شما ای دو همسر پیامبر،] اگر به درگاه الله توبه کنید [به سود شماست]؛ بی‌گمان، دل‌هایتان [از حفظ سرّ که پیغمبر دوست می‌داشت] منحرف گشته است، و اگر شما دو نفر علیه او همدست [و متفق] شوید، [بدانید] که الله دوست و یاورِ اوست و [نیز] جبرئیل و مردمان شایسته ـ از مؤمنان و فرشتگان‌ـ [نیز] بعد از آن، پشتیبان [و مددکار او] هستند.');
INSERT INTO "islamhouse" VALUES(66,5,'66:5','چه بسا اگر شما را طلاق دهد، پروردگارش همسرانی اعم از بیوه و دوشیزه بهتر از شما برایش جایگزین کند [که] زنانی مسلمان، مؤمن، فرمانبردار، توبه‌کار، عبادتگر [و] روزه‌دار باشند.');
INSERT INTO "islamhouse" VALUES(66,6,'66:6','ای کسانی ‌که ایمان آورده‌اید، خود و خانواده‌تان را از آتشی که هیزمِ آن مردم و سنگ‌هاست نگه دارید؛ [آتشی که] فرشتگانِ خشن و سختگیر بر آن، [گمارده شده‌اند] که هرگز از الله در [مورد] آنچه به آنان فرمان داده، نافرمانی نمی‌کنند، و هر چه فرمان می‌یابند انجام می‌دهند.');
INSERT INTO "islamhouse" VALUES(66,7,'66:7','[و در روز قیامت به کافران گفته می‌شود:] ای کسانی‌ که کافر شده‌اید، امروز عذرخواهی نکنید. فقط در برابر آنچه [در دنیا] می‌کردید جزا داده می‌شوید.');
INSERT INTO "islamhouse" VALUES(66,8,'66:8','ای کسانی ‌که ایمان آورده‌اید، به سوی الله توبه کنید؛ توبه‌ای خالصانه. امید است پروردگارتان گناهانتان را از شما بزداید و شما را به باغ‌هایی [از بهشت] وارد کند که جویبارها از زیر [درختان] آن جاری است؛ [در] روزی‌ که الله، پیامبر و کسانی را که همراه او ایمان آورده‌اند، خوار نمی‌کند. نورشان پیشاپیش آنان و سمت راستشان [بر روی صراط] در حرکت است؛ می‌گویند: «پروردگارا، نور ما را به تمام [و کمال] برسان [که وارد بهشت شویم] و ما را بیامرز. بی‌گمان، تو بر هر چیز توانایی».');
INSERT INTO "islamhouse" VALUES(66,9,'66:9','ای پیامبر، با کافران و منافقان جهاد کن و بر آنها سخت بگیر، و جایگاهشان جهنم است؛ و چه بد جایگاهی است!');
INSERT INTO "islamhouse" VALUES(66,10,'66:10','الله برای کسانی‌ که کافر شده‌اند، همسر نوح و همسر لوط را مَثَل زده است که آن دو در نکاح دو تن از بندگان صالح ما بودند؛ و به آن دو خیانت کردند، و [آن دو بندۀ صالح] نتوانستند چیزی از [عذاب] الله را از آن دو [زن] دفع کنند؛ و [به آنها] گفته شد: «همراه واردشوندگانِ به آتش [جهنم، شما نیز] وارد شوید».');
INSERT INTO "islamhouse" VALUES(66,11,'66:11','الله برای کسانی ‌که ایمان آورده‌اند، همسر فرعون [= آسیه] را مَثَل زده است؛ هنگامی که گفت: «پروردگارا، نزد خود در بهشت خانه‌ای برایم بساز و مرا از فرعون و کردارش نجات بده، و مرا از قوم ستمگر رهایی بخش».');
INSERT INTO "islamhouse" VALUES(66,12,'66:12','و [نیز] مریم ـ دختر عمران‌ـ را [مَثَل زده است] که شرمگاه خویش را [پاک] نگه داشت؛ و ما از روح[ی از جانب] خود در آن دمیدیم و [او] کلمات پروردگارش و کتاب‌های او را تصدیق کرد و از فرمانبرداران بود.');
INSERT INTO "islamhouse" VALUES(67,1,'67:1','پربرکت [و بزرگوار] است کسی‌ که فرمانرواییِ جهانِ هستی به دست اوست، و او بر همه چیز تواناست.');
INSERT INTO "islamhouse" VALUES(67,2,'67:2','[همان] ذاتی که مرگ و زندگی را آفرید، تا شما را بیازماید که کدام یک از شما نیکوکار‌تر است؛ و او پیروزمندِ بخشنده است.');
INSERT INTO "islamhouse" VALUES(67,3,'67:3','آن ذاتی که هفت آسمان را بر فرازِ یکدیگر آفرید. در آفرینشِ [الله] رحمان هیچ بی‌نظمی و خللی نمی‌بینی. بار دیگر نگاه کن؛ آیا هیچ شکاف [و نقصی] می‌بینی؟');
INSERT INTO "islamhouse" VALUES(67,4,'67:4','بار دیگر [به عالم هستی] نگاه کن؛ [خلل و نقصی نخواهی دید و] چشمت در حالی ‌که خسته و ناتوان است به سوی تو بازمی‌گردد.');
INSERT INTO "islamhouse" VALUES(67,5,'67:5','و به راستی ما آسمان دنیا را با چراغ‌ها آراستیم و آنها را برای راندن شیاطین قرار دادیم، و عذاب [آتش] سوزان را برایشان آماده ساختیم.');
INSERT INTO "islamhouse" VALUES(67,6,'67:6','و برای کسانی ‌که به پروردگار‌شان کافر شدند، عذاب جهنم است؛ و چه بد جایگاهی است!');
INSERT INTO "islamhouse" VALUES(67,7,'67:7','هنگامی ‌که در آن انداخته شوند، نعره‌ای [وحشتناک] از آن می‌شنوند، در حالی‌ که می‌جوشد.');
INSERT INTO "islamhouse" VALUES(67,8,'67:8','از شدت خشم، نزدیک است [جهنم] پاره‌پاره شود. هر گاه گروهی در آن انداخته می‌شوند، نگهبانانش از آنان می‌پرسند: «آیا بیم‌دهنده‌ای به سراغتان نیامد؟»');
INSERT INTO "islamhouse" VALUES(67,9,'67:9','می‌گویند: «بله؛ بیم‌دهنده به سوی ما آمد، اما ما او را تکذیب کردیم و گفتیم: «هرگز الله چیزی نازل نکرده است. شما جز در گمراهی بزرگ نیستید».');
INSERT INTO "islamhouse" VALUES(67,10,'67:10','و [باز] می‌گویند: «اگر ما می‌شنیدیم یا عقلمان را به کار می‌بردیم، در [زمرۀ] دوزخیان نبودیم».');
INSERT INTO "islamhouse" VALUES(67,11,'67:11','اینجاست که به گناه خود اعتراف می‌کنند. پس دوری [از رحمت الله] بهرۀ دوزخیان باد!');
INSERT INTO "islamhouse" VALUES(67,12,'67:12','بی‌گمان، کسانی ‌که در نهان از پروردگار‌شان می‌ترسند، آمرزش و پاداش بزرگی دارند.');
INSERT INTO "islamhouse" VALUES(67,13,'67:13','[خواه] گفتار خود را پنهان دارید یا آن را آشکار سازید، [به هر حال،] او به آنچه در دل‌هاست آگاه است.');
INSERT INTO "islamhouse" VALUES(67,14,'67:14','آیا کسی‌ که [همۀ موجودات را] آفریده است، [حالِ بندگان را] نمی‌داند؟ در حالی ‌که او باریک‌بین و آگاه است.');
INSERT INTO "islamhouse" VALUES(67,15,'67:15','او ذاتی است که زمین را برای شما رام [و هموار] گردانید، پس در گوشه و کنارِ آن راه بروید و از روزیِ او بخورید؛ و بازگشت [همه] به سوی اوست.');
INSERT INTO "islamhouse" VALUES(67,16,'67:16','آیا خود را از [عذاب] ذاتی که در آسمان است، در امان می‌دانید، که [فرمان دهد] زمین شما را فرو بَرد و ناگهان به لرزش درآید؟');
INSERT INTO "islamhouse" VALUES(67,17,'67:17','یا خود را از ذاتی ‌که در آسمان است در امان می‌دانید، که طوفان شن بر شما فرستد؟ آنگاه به زودی خواهید دانست که هشدار من چگونه است!');
INSERT INTO "islamhouse" VALUES(67,18,'67:18','همانا کسانی ‌که پیش از آنان بودند [نیز دعوت پیامبران را] تکذیب کردند؛ پس [بنگر که] عقوبت من چگونه بود!');
INSERT INTO "islamhouse" VALUES(67,19,'67:19','آیا به پرندگانی که بر فرازِ سرشان است نگاه نکردند که [گاهی] بال‌هایشان را می‌گشایند و [گاهی] فرومی‌بندند؟ جز [الله] رحمان [کسی] آنها را [بر فرازِ آسمان] نگاه نمی‌دارد؛ بی‌گمان او به همه چیز بیناست.');
INSERT INTO "islamhouse" VALUES(67,20,'67:20','آیا این کسی که لشکر شماست، می‌تواند شما را در برابر الله یاری دهد؟ کافران جز در فریب [و غرور‌شان] نیستند.');
INSERT INTO "islamhouse" VALUES(67,21,'67:21','یا آن ذاتی که شما را روزی می‌دهد‌، اگر روزی‌اش را بازدارد، [چه کسی می‌تواند نیاز شما را تأمین کند؟] ولی آنها در سرکشی و فرار از حقیقت، لجاجت می‌ورزند.');
INSERT INTO "islamhouse" VALUES(67,22,'67:22','آیا کسی ‌که به رو افتاده [و نگونسار] حرکت می‌کند، به هدایت نزدیک‌تر است، یا کسی ‌که راست و استوار بر راهِ راست گام برمی‌دارد؟');
INSERT INTO "islamhouse" VALUES(67,23,'67:23','بگو: «او ذاتی است که شما را آفرید و گوش و چشمان و دل برایتان قرار داد؛ [اما شما] اندکی سپاسگزاری می‌کنید».');
INSERT INTO "islamhouse" VALUES(67,24,'67:24','بگو: «او ذاتی است که شما را در زمین پراکند، و به سوی او محشور می‌شوید».');
INSERT INTO "islamhouse" VALUES(67,25,'67:25','و می‌گویند: «اگر راست می‌گویید، این وعده [قیامت] چه زمانی است؟»');
INSERT INTO "islamhouse" VALUES(67,26,'67:26','بگو: «علم [قیامت] تنها نزد الله است و من فقط بیم‌دهنده‌ای آشکارم».');
INSERT INTO "islamhouse" VALUES(67,27,'67:27','هنگامی که آن [وعده و عذاب الهی] را از نزدیک ببینند، چهرۀ کسانی‌ که کافر شدند، سیاه و اندوهگین می‌گردد و [به آنان] گفته می‌شود: «این همان چیزی است که درخواست می‌کردید».');
INSERT INTO "islamhouse" VALUES(67,28,'67:28','[ای پیامبر،] بگو: «به من خبر دهید، اگر الله مرا و کسانی را‌ که با من هستند، نابود کند یا بر ما رحمت آورد، کیست که کافران را از عذاب دردناک پناه ‌دهد؟»');
INSERT INTO "islamhouse" VALUES(67,29,'67:29','بگو: «او [الله] رحمان است؛ به او ایمان آورده‌ایم و بر او توکل کرده‌ایم؛ [ای کافران،] به زودی خواهید دانست که چه کسی در گمراهیِ آشکار است».');
INSERT INTO "islamhouse" VALUES(67,30,'67:30','[ای پیامبر،] بگو: «به من خبر دهید اگر آب [آشامیدنیِ] شما [در زمین] فرو رود، چه کسی می‌تواند آب روان [و گوارا] برایتان بیاورد؟»');
INSERT INTO "islamhouse" VALUES(68,1,'68:1','نون. سوگند به قلم و آنچه می‌نویسند،');
INSERT INTO "islamhouse" VALUES(68,2,'68:2','که تو [ای پیامبر،] به نعمت [و فضلِ] پروردگارت، دیوانه نیستی،');
INSERT INTO "islamhouse" VALUES(68,3,'68:3','و پاداشی بی‌پایان برای تو [درپیش] است؛');
INSERT INTO "islamhouse" VALUES(68,4,'68:4','و یقیناً تو اخلاقِ بسیار نیکو و والایی داری.');
INSERT INTO "islamhouse" VALUES(68,5,'68:5','به زودی خواهی دید و آنها [نیز] خواهند دید');
INSERT INTO "islamhouse" VALUES(68,6,'68:6','که کدام یک از شما دیوانه است.');
INSERT INTO "islamhouse" VALUES(68,7,'68:7','پروردگارت بهتر می‌داند چه کسی از راه او گمراه گشته، و او به هدایت‌یافتگان [نیز] دانا‌تر است.');
INSERT INTO "islamhouse" VALUES(68,8,'68:8','پس از تکذیب‌کنندگان اطاعت مکن.');
INSERT INTO "islamhouse" VALUES(68,9,'68:9','آنها دوست دارند [و می‌خواهند] که نرمی [و مدارا] کنی، تا آنان [نیز] نرمی [و مدارا] کنند.');
INSERT INTO "islamhouse" VALUES(68,10,'68:10','و از هر فرومایه‌ای که بسیار سوگند [دروغ] یاد می‌کند، اطاعت مکن.');
INSERT INTO "islamhouse" VALUES(68,11,'68:11','[همان کسی که] بسیار عیبجوست و به سخن‌چینی آمد و شد می‌کند.');
INSERT INTO "islamhouse" VALUES(68,12,'68:12','و بسیار مانع کار خیر، و متجاوز و گناهکار است.');
INSERT INTO "islamhouse" VALUES(68,13,'68:13','به علاوه، خشن و بی‌تبار و بدنام است.');
INSERT INTO "islamhouse" VALUES(68,14,'68:14','به خاطر آن که صاحب مال و فرزندان بسیار است.');
INSERT INTO "islamhouse" VALUES(68,15,'68:15','هنگامی ‌که آیات ما بر او خوانده می‌شود، می‌گوید: «[اینها] افسانه‌های [خیالی] گذشتگان است».');
INSERT INTO "islamhouse" VALUES(68,16,'68:16','به زودی داغ [ننگ و رسوایی] بر بینی او می‌نهیم.');
INSERT INTO "islamhouse" VALUES(68,17,'68:17','ما آنها را آزمایش کردیم، همان‌گونه که صاحبان باغ را آزمودیم؛ هنگامی که سوگند یاد کردند که صبحگاهان [که مستمندان نباشند،] میوه‌های باغ را بچینند.');
INSERT INTO "islamhouse" VALUES(68,18,'68:18','و[لی] «ان شاء الله» نگفتند.');
INSERT INTO "islamhouse" VALUES(68,19,'68:19','عذابی [فرا‌گیر] از سوی پروردگارت بر [باغِ] آنها فرود آمد، در حالی ‌که همه در خواب بودند.');
INSERT INTO "islamhouse" VALUES(68,20,'68:20','و آن باغ [سبز و خرم، پس از آتش‌سوزی] همچون شبِ سیاه شد.');
INSERT INTO "islamhouse" VALUES(68,21,'68:21','آنگاه صبحگاهان یکدیگر را ندا دادند');
INSERT INTO "islamhouse" VALUES(68,22,'68:22','که اگر می‌خواهید میوه بچینید، به سوی کشتزار [و باغ] خویش بروید.');
INSERT INTO "islamhouse" VALUES(68,23,'68:23','آنگاه [به سوی کشتزار] حرکت کردند، در حالی ‌که آهسته با هم می‌گفتند:');
INSERT INTO "islamhouse" VALUES(68,24,'68:24','«[مراقب باشید] که امروز هیچ [نیازمند و] بینوایی در آن [باغ] وارد نشود!»');
INSERT INTO "islamhouse" VALUES(68,25,'68:25','و صبحگاهان با تصمیم جدی بر جلوگیری [از ورود مستمندان، به سمتِ باغ] بیرون شدند.');
INSERT INTO "islamhouse" VALUES(68,26,'68:26','اما هنگامی ‌که آن [باغ] را دیدند، گفتند: «یقیناً ما [اشتباه آمده‌ایم و] راه را گم کرده‌ایم؛');
INSERT INTO "islamhouse" VALUES(68,27,'68:27','[نه؛] بلکه ما [از حاصلِ آن] بی‌بهره و محروم هستیم».');
INSERT INTO "islamhouse" VALUES(68,28,'68:28','بهترین [و خردمندترین] آنان گفت: «آیا به شما نگفتم چرا [شکر و] تسبیح [الله را] نمی‌گویید؟».');
INSERT INTO "islamhouse" VALUES(68,29,'68:29','گفتند: «پروردگار ما پاک و منزّه است؛ مسلّماً ما ستمکار بودیم».');
INSERT INTO "islamhouse" VALUES(68,30,'68:30','آنگاه ملامت‌کنان به یکدیگر روی آوردند');
INSERT INTO "islamhouse" VALUES(68,31,'68:31','[و] گفتند: «وای بر ما که طغیانگر [و سرکش] بودیم!');
INSERT INTO "islamhouse" VALUES(68,32,'68:32','امیدواریم که پروردگارمان [ما را ببخشد و] در عوض، چیزی بهتر از آن [باغ] به ما دهد؛ [چرا که] بی‌گمان، ما به سوی پروردگار خود روی آورده‌ایم».');
INSERT INTO "islamhouse" VALUES(68,33,'68:33','عذاب [الله در دنیا] اینچنین است؛ و اگر می‌دانستند، عذاب آخرت مسلماً بزرگ‌تر [و بیشتر] است.');
INSERT INTO "islamhouse" VALUES(68,34,'68:34','به راستی برای پرهیزگاران نزد پروردگارشان باغ‌های پرنعمت است.');
INSERT INTO "islamhouse" VALUES(68,35,'68:35','آیا ما مسلمانان را همچون مجرمان [و گناهکاران] قرار می‌دهیم؟');
INSERT INTO "islamhouse" VALUES(68,36,'68:36','شما را چه شده است؟ چگونه داوری می‌کنید؟');
INSERT INTO "islamhouse" VALUES(68,37,'68:37','آیا شما کتابی [آسمانی] دارید که در آن [می‌خوانید و] فرامی‌گیرید؟');
INSERT INTO "islamhouse" VALUES(68,38,'68:38','و [آیا] آنچه را انتخاب می‌کنید، در آن [کتاب] برای شما [نوشته شده] است؟');
INSERT INTO "islamhouse" VALUES(68,39,'68:39','یا اینکه بر [عهدۀ] ما عهد و پیمان استواری تا روز قیامت دارید که هر چه را حکم کنید، [حق] برای شما باشد؟');
INSERT INTO "islamhouse" VALUES(68,40,'68:40','[ای پیامبر،] از آنها بپرس کدام‌یک از آنان در برابر این [ادعا‌ها] متعهد است؟');
INSERT INTO "islamhouse" VALUES(68,41,'68:41','و یا اینکه [معبودان و] شریکانی دارند؟ اگر راست می‌گویند، باید شریکان [و معبودان] خویش را بیاورند.');
INSERT INTO "islamhouse" VALUES(68,42,'68:42','روزی ‌که [الله متعال برای داوری میان خلایق بیاید،] ساق [او تعالی که هیچ چیز مانند آن نیست] آشکار می‌گردد و [مردم] به سجده فراخوانده می‌شوند؛ ولی [کافران و منافقان] نمی‌توانند [سجده کنند].');
INSERT INTO "islamhouse" VALUES(68,43,'68:43','[در حالی‌ که] دیدگانشان [از ترس و ندامت] فرو افتاده و ذلت و خواری وجود‌شان را فراگرفته است؛ و یقیناً پیش از این [در دنیا] در حالی ‌که تندرست بودند، به سجده فراخوانده می‌شدند.');
INSERT INTO "islamhouse" VALUES(68,44,'68:44','[ای پیامبر،] مرا با آن کسی ‌که این سخن [= قرآن] را تکذیب می‌کند واگذار. ما آنان را اندک‌اندک [و] به گونه‌ای که درنیابند [و از جایی‌که متوجه نگردند]، به سوی عذاب خواهیم کشاند.');
INSERT INTO "islamhouse" VALUES(68,45,'68:45','و به آنها مهلت می‌دهم. بی‌گمان، مکر [و تدبیرِ] من استوار [و محکم] است.');
INSERT INTO "islamhouse" VALUES(68,46,'68:46','[ای پیامبر،] مگر تو از آنان مزدی درخواست می‌کنی که [از ادایِ آن در رنجند و] برایشان سنگین است؟');
INSERT INTO "islamhouse" VALUES(68,47,'68:47','یا [اسرار] غیب نزد آنهاست و آنان [آنچه را می‌گویند از روی آن] می‌نویسند.');
INSERT INTO "islamhouse" VALUES(68,48,'68:48','[ای پیامبر،] در برابر فرمان [و حکم] پروردگارت صبر کن، و مانند صاحبِ ماهی [= یونس] مباش ‌که با دلی سرشار از خشم و اندوه، [الله را] ندا داد.');
INSERT INTO "islamhouse" VALUES(68,49,'68:49','اگر نعمت [و رحمتی] از سوی پروردگارش به یاری‌ او نیامده بود، یقیناً در حالی ‌که نکوهیده بود، [از شکم ماهی،] به صحرای خشکیده بیرون افکنده می‌شد.');
INSERT INTO "islamhouse" VALUES(68,50,'68:50','آنگاه پروردگارش او را برگُزید و از صالحان قرار داد.');
INSERT INTO "islamhouse" VALUES(68,51,'68:51','و [ای پیامبر،] نزدیک است کسانی ‌که کافر شدند، هنگامی‌ که آیات قرآن را می‌شنوند، تو را چشم‌زخم زنند؛ و [به خاطر خشم و حسدشان] می‌گویند: «قطعاً او دیوانه است»؛');
INSERT INTO "islamhouse" VALUES(68,52,'68:52','در حالی ‌که این [قرآن] جز پندی برای جهانیان نیست.');
INSERT INTO "islamhouse" VALUES(69,1,'69:1','[روزِ] واقع‌شدنیِ [رستاخیز]');
INSERT INTO "islamhouse" VALUES(69,2,'69:2','آن واقع‌شدنی چیست؟');
INSERT INTO "islamhouse" VALUES(69,3,'69:3','و تو چه دانی آن واقع‌شدنی چیست [و چگونه است]؟');
INSERT INTO "islamhouse" VALUES(69,4,'69:4','[قوم] ثمود و [قوم] عاد، [قیامتِ] درهم‌کوبنده را تکذیب کردند.');
INSERT INTO "islamhouse" VALUES(69,5,'69:5','و اما [قوم] ثمود، به بانگِ سهمگین هلاک شدند.');
INSERT INTO "islamhouse" VALUES(69,6,'69:6','و اما [قوم] عاد، با تندبادی سرکش و سرد به هلاکت رسیدند.');
INSERT INTO "islamhouse" VALUES(69,7,'69:7','[الله] هفت شب و هشت روز پیاپی آن [عذاب] را بر آنها مسلط نمود؛ پس [اگر آنجا ‌بودی، آن] قوم را [در اثرِ آن تندباد،] مانند تنه‌های پوسیده و توخالی درختان خرما، بر زمین‌افتاده [و هلاک‌شده] می‌دیدی.');
INSERT INTO "islamhouse" VALUES(69,8,'69:8','آیا کسی از آنها را می‌بینی که باقی مانده باشد؟');
INSERT INTO "islamhouse" VALUES(69,9,'69:9','و فرعون و کسانی ‌که پیش از او بودند و [مردم] شهر‌های ویران شده [= قوم لوط،] مرتکب گناهان [بزرگ] شدند.');
INSERT INTO "islamhouse" VALUES(69,10,'69:10','آنها از [فرمان] فرستادۀ پروردگار‌شان سرپیچی کردند، آنگاه [الله] آنان را به عذاب بی‌نهایت سختی گرفتار کرد.');
INSERT INTO "islamhouse" VALUES(69,11,'69:11','هنگامی ‌که آب طغیان کرد، ما شما را در کشتی سوار کردیم،');
INSERT INTO "islamhouse" VALUES(69,12,'69:12','تا پندی برای شما قرار دهیم و گوش‌های شنوا آن را بشنوند و به یاد بسپارند.');
INSERT INTO "islamhouse" VALUES(69,13,'69:13','آنگاه ‌که یک بار در صور دمیده شود،');
INSERT INTO "islamhouse" VALUES(69,14,'69:14','و زمین و کوه‌ها از جا کنده شده و یکباره درهم‌کوبیده [و متلاشی] شوند.');
INSERT INTO "islamhouse" VALUES(69,15,'69:15','در آن روز، واقعۀ [عظیمِ] قیامت به وقوع می‌پیوندد');
INSERT INTO "islamhouse" VALUES(69,16,'69:16','و آسمان شکافته می‌شود و در آن روز، سست می‌گردد [و فرو می‌ریزد].');
INSERT INTO "islamhouse" VALUES(69,17,'69:17','و فرشتگان بر کناره‌های آن [= آسمان] قرار می‌گیرند؛ و آن روز، هشت [فرشته] عرش پروردگارت را بر سرِ خود حمل می‌کنند.');
INSERT INTO "islamhouse" VALUES(69,18,'69:18','در آن روز، شما [همگی برای حسابرسی، به پیشگاه الله] عرضه می‌شوید و هیچ چیز از [کار‌های] شما پنهان نخواهد ماند.');
INSERT INTO "islamhouse" VALUES(69,19,'69:19','اما کسی‌ که نامۀ [اعمالش] را به دست راستش دهند، می‌گوید: «بیایید نامۀ [اعمال] مرا بخوانید.');
INSERT INTO "islamhouse" VALUES(69,20,'69:20','من یقین داشتم که با حساب [اعمال] خود مواجه خواهم شد».');
INSERT INTO "islamhouse" VALUES(69,21,'69:21','پس او در یک زندگی پسندیده [و رضایت‌بخش] خواهد بود؛');
INSERT INTO "islamhouse" VALUES(69,22,'69:22','در بهشتی برین');
INSERT INTO "islamhouse" VALUES(69,23,'69:23','که میوه‌هایش [برای چیدن] در دسترس است.');
INSERT INTO "islamhouse" VALUES(69,24,'69:24','[و به آنها گفته می‌شود: «به [پاداش] آنچه در ایام گذشته پیش فرستاده‌اید، بخورید و بیاشامید؛ گوارایتان باد».');
INSERT INTO "islamhouse" VALUES(69,25,'69:25','و اما کسی ‌که نامۀ [اعمالش] را به دست چپش دهند، می‌گوید: «ای کاش هرگز نامه[ی اعمالم] را به من نمی‌دادند،');
INSERT INTO "islamhouse" VALUES(69,26,'69:26','و نمی‌دانستم حسابم چیست!');
INSERT INTO "islamhouse" VALUES(69,27,'69:27','ای کاش [با مرگ،] همه چیز تمام می‌شد [و پایان کار بود]!');
INSERT INTO "islamhouse" VALUES(69,28,'69:28','مال و دارایی‌ام مرا بی‌نیاز نکرد [و سودی نبخشید].');
INSERT INTO "islamhouse" VALUES(69,29,'69:29','قدرت و فرمانروایی‌ام [نیز] از [دست] من رفت [و به کلی نابود شد]».');
INSERT INTO "islamhouse" VALUES(69,30,'69:30','[به فرشتگان گفته می‌شود:] «او را بگیرید و در بند و زنجیر کنید.');
INSERT INTO "islamhouse" VALUES(69,31,'69:31','سپس او را به [آتش] دوزخ بیفکنید.');
INSERT INTO "islamhouse" VALUES(69,32,'69:32','آنگاه وی را در زنجیری ببندید که طولش هفتاد ذِراع است.');
INSERT INTO "islamhouse" VALUES(69,33,'69:33','بی‌گمان، او هرگز به الله بزرگ ایمان نمی‌آورد،');
INSERT INTO "islamhouse" VALUES(69,34,'69:34','و هرگز [مردم را] بر اطعام بینوا[یان] تشویق نمی‌کرد.');
INSERT INTO "islamhouse" VALUES(69,35,'69:35','بنابر این، امروز دوست صمیمی [و مهربانی] در اینجا ندارد [که یاری‌اش کند]،');
INSERT INTO "islamhouse" VALUES(69,36,'69:36','و غذایی جز چرک و خون [جهنمیان] ندارد؛');
INSERT INTO "islamhouse" VALUES(69,37,'69:37','[غذایی ‌که] جز گناهکاران آن را نمی‌خورند».');
INSERT INTO "islamhouse" VALUES(69,38,'69:38','سوگند به آنچه می‌بینید');
INSERT INTO "islamhouse" VALUES(69,39,'69:39','و به آنچه نمی‌بینید.');
INSERT INTO "islamhouse" VALUES(69,40,'69:40','بی‌تردید، این [قرآن، سخن الله است که ابلاغش به پیامبر، بر عهدۀ] فرستاده‌ای بزرگوار [=جبرئیل] است.');
INSERT INTO "islamhouse" VALUES(69,41,'69:41','و اینها گفتار شاعری نیست؛ [اما شما کافران] کمتر ایمان می‌آورید.');
INSERT INTO "islamhouse" VALUES(69,42,'69:42','و [نیز] گفتار کاهنی نیست؛ [اما شما] کمتر پند می‌گیرید.');
INSERT INTO "islamhouse" VALUES(69,43,'69:43','[این قرآن،] از سوی پروردگار جهانیان نازل شده است.');
INSERT INTO "islamhouse" VALUES(69,44,'69:44','اگر [پیامبر] سخنانی [به دروغ] بر ما می‌بست،');
INSERT INTO "islamhouse" VALUES(69,45,'69:45','مسلّماً ما دست راست او را می‌گرفتیم،');
INSERT INTO "islamhouse" VALUES(69,46,'69:46','سپس [شاه]رگِ قلبش را قطع می‌کردیم،');
INSERT INTO "islamhouse" VALUES(69,47,'69:47','و هیچ‌یک از شما نمی‌توانست مانع [عذابِ] او گردد.');
INSERT INTO "islamhouse" VALUES(69,48,'69:48','یقیناً این [قرآن،] پند و تذکری برای پرهیزگاران است.');
INSERT INTO "islamhouse" VALUES(69,49,'69:49','و ما یقیناً می‌دانیم که برخی از شما، تکذیب‌کنندگان [این کلام الهی] هستید.');
INSERT INTO "islamhouse" VALUES(69,50,'69:50','و همانا این [قرآن،] بر کافران مایۀ حسرت و ندامت است.');
INSERT INTO "islamhouse" VALUES(69,51,'69:51','و بی‌گمان، این [قرآن،] حقیقتِ راستین است.');
INSERT INTO "islamhouse" VALUES(69,52,'69:52','پس [ای پیامبر،] با [ذکرِ] نامِ پروردگار بزرگت [او را] تسبیح گوی.');
INSERT INTO "islamhouse" VALUES(70,1,'70:1','درخواست‌کننده‌ای [به تمسخر] تقاضای عذابی را کرد که وقوعش [در قیامت] حتمی است،');
INSERT INTO "islamhouse" VALUES(70,2,'70:2','[این عذاب،] برای کافران است [و] هیچ دفع‌کننده‌ای ندارد.');
INSERT INTO "islamhouse" VALUES(70,3,'70:3','از سوی الله [به وقوع می‌پیوندد که دارای عُلُو و] صاحبِ مراتب و درجات والاست.');
INSERT INTO "islamhouse" VALUES(70,4,'70:4','فرشتگان و روح [= جبرئیل] به سوی او عروج می‌کنند، در روزی ‌که مقدارش [در مقیاس بشری،] پنجاه‌ هزار سال است.');
INSERT INTO "islamhouse" VALUES(70,5,'70:5','پس تو [ای پیامبر، در برابر تمسخرشان] صبرِ نیکو پیشه کن.');
INSERT INTO "islamhouse" VALUES(70,6,'70:6','بی‌گمان، آنها آن [روز] را دور می‌بینند');
INSERT INTO "islamhouse" VALUES(70,7,'70:7','و ما آن را نزدیک می‌بینیم.');
INSERT INTO "islamhouse" VALUES(70,8,'70:8','[همان] روزی ‌که آسمان چون مسِ گداخته شود،');
INSERT INTO "islamhouse" VALUES(70,9,'70:9','و کوه‌ها مانند پشم رنگین [متلاشی و پراکنده] شود.');
INSERT INTO "islamhouse" VALUES(70,10,'70:10','و هیچ دوست صمیمی [و خویشاوندی] از [حال] دوست صمیمی [و خویشاوندش] نمی‌پرسد.');
INSERT INTO "islamhouse" VALUES(70,11,'70:11','[در حالی ‌که] آنان به یکدیگر نشان داده می‌شوند، گناهکار [دوست دارد و] آرزو می‌کند که فرزندان خود را در ازای [رهایی] از عذاب آن روز بدهد،');
INSERT INTO "islamhouse" VALUES(70,12,'70:12','و [همچنین] زنش و برادرش را');
INSERT INTO "islamhouse" VALUES(70,13,'70:13','و قبیله‌اش را که [همیشه] به او جا و پناه می‌داد.');
INSERT INTO "islamhouse" VALUES(70,14,'70:14','و [همچنین] همۀ کسانی را که روی زمین هستند [فدا کند] تا نجات یابد.');
INSERT INTO "islamhouse" VALUES(70,15,'70:15','[اما] هرگز چنین نیست [که برایش راه نجاتی باشد]! بی‌گمان، آن شعله‌های [آتشِ] سوزان است');
INSERT INTO "islamhouse" VALUES(70,16,'70:16','[که] پوست از سر برمی‌کَند.');
INSERT INTO "islamhouse" VALUES(70,17,'70:17','کسانی را که [به فرمان الله] پشت کردند و [از حق] روی گرداندند، [به سوی خود] فرامی‌خوانَد.');
INSERT INTO "islamhouse" VALUES(70,18,'70:18','و [نیز کسانی را که] اموال را جمع‌آوری و ذخیره کردند [و حق آن را نپرداختند].');
INSERT INTO "islamhouse" VALUES(70,19,'70:19','یقیناً انسان، کم‌طاقت [و حریص] آفریده شده است.');
INSERT INTO "islamhouse" VALUES(70,20,'70:20','هر گاه بدی [و مصیبتی] به او رسد، بی‌تابی می‌کند،');
INSERT INTO "islamhouse" VALUES(70,21,'70:21','و هر گاه خوبی به او رسد، بازدارنده [و بخیل] می‌گردد.');
INSERT INTO "islamhouse" VALUES(70,22,'70:22','مگر نمازگزاران؛');
INSERT INTO "islamhouse" VALUES(70,23,'70:23','همان کسانی ‌که بر نمازهایشان همیشه پایداری می‌کنند.');
INSERT INTO "islamhouse" VALUES(70,24,'70:24','و کسانی‌ که در اموالشان، حق [معیّن و] معلومی است');
INSERT INTO "islamhouse" VALUES(70,25,'70:25','برای مستمند و محروم؛');
INSERT INTO "islamhouse" VALUES(70,26,'70:26','و [همان] کسانی ‌که روز جزا را تصدیق می‌کنند؛');
INSERT INTO "islamhouse" VALUES(70,27,'70:27','و کسانی‌ که از عذاب پروردگارشان می‌ترسند.');
INSERT INTO "islamhouse" VALUES(70,28,'70:28','[چرا که] بی‌گمان، از عذاب پروردگارشان نمی‌توان ایمن بود.');
INSERT INTO "islamhouse" VALUES(70,29,'70:29','و کسانی ‌که شرمگاه‌هایشان را نگه می‌دارند؛');
INSERT INTO "islamhouse" VALUES(70,30,'70:30','مگر با همسران و کنیزانی که مالک آنها هستند، که [در این صورت،] سرزنشی بر آنها نیست.');
INSERT INTO "islamhouse" VALUES(70,31,'70:31','اما هر کس فراتر از اینها را بخواهد، آنان متجاوزان هستند.');
INSERT INTO "islamhouse" VALUES(70,32,'70:32','و کسانی‌ که امانت‌ها و پیمان‌هایشان را رعایت می‌کنند،');
INSERT INTO "islamhouse" VALUES(70,33,'70:33','و کسانی ‌که به شهادت‌های خود متعهدند،');
INSERT INTO "islamhouse" VALUES(70,34,'70:34','و کسانی ‌که بر نمازشان پایداری می‌کنند.');
INSERT INTO "islamhouse" VALUES(70,35,'70:35','اینان در باغ‌های [بهشت] گرامی داشته می‌شوند.');
INSERT INTO "islamhouse" VALUES(70,36,'70:36','پس [ای پیامبر،] کافران را چه شده که به سوی تو می‌شتابند؟');
INSERT INTO "islamhouse" VALUES(70,37,'70:37','[و] از راست و چپ، گروه‌گروه [بر مخالفت تو جمع می‌شوند و بر تو هجوم می‌آورند؟]');
INSERT INTO "islamhouse" VALUES(70,38,'70:38','آیا هر کدام از آنها [با آن اعمال ناپسندش] طمع دارد که در بهشت پرنعمت [الهی] درآورده شود؟');
INSERT INTO "islamhouse" VALUES(70,39,'70:39','هرگز چنین نیست! بی‌گمان، ما آنها را از آنچه [خود‌] می‌دانند آفریده‌ایم.');
INSERT INTO "islamhouse" VALUES(70,40,'70:40','به پروردگار مشرق‌ها و مغرب‌ها سوگند می‌خورم که قطعاً ما تواناییم');
INSERT INTO "islamhouse" VALUES(70,41,'70:41','بر اینکه [کسان دیگری] بهتر از اینان را جایگزین کنیم؛ و ما [برای انجام این کار،] درمانده و ناتوان نیستیم.');
INSERT INTO "islamhouse" VALUES(70,42,'70:42','پس [ای پیامبر،] آنان را به حال خود واگذار تا در باطل خود فروروند و بازی کنند، تا به روزی برسند که به آنان وعده داده شده است.');
INSERT INTO "islamhouse" VALUES(70,43,'70:43','[همان] روزی ‌که شتابان از قبرها خارج می‌شوند؛ گویی به سوی بت‌ها [و معبودانشان] می‌دوند.');
INSERT INTO "islamhouse" VALUES(70,44,'70:44','[در حالی که] چشم‌هایشان [از شرم و وحشت] به زیر افتاده و ذلت [و خواری،] آنان را پوشانده است. این همان روزی است که به آنان وعده داده می‌شد.');
INSERT INTO "islamhouse" VALUES(71,1,'71:1','بی‌گمان، ما نوح را به سوی قومش فرستادیم [و گفتیم]: «قوم خود را ـ پیش از آنکه عذاب دردناک به سراغشان آید ـ هشدار ده [و بترسان].');
INSERT INTO "islamhouse" VALUES(71,2,'71:2','[نوح] گفت: «ای قوم، من بیم‌دهنده‌ای آشکار برایتان هستم،');
INSERT INTO "islamhouse" VALUES(71,3,'71:3','که الله را عبادت کنید، و از او بترسید، و مطیع [دستورهای] من باشید.');
INSERT INTO "islamhouse" VALUES(71,4,'71:4','[که اگر چنین کردید، الله] گناهانتان را می‌آمرزد و تا زمان معیّنی به شما مهلت می‌دهد؛ زیرا هنگامی که اجل الهی فرا رسد، تأخیر نخواهد داشت، اگر می‌دانستید».');
INSERT INTO "islamhouse" VALUES(71,5,'71:5','و نوح [پس از نصیحت‌ و دعوت] گفت: «پروردگارا، به راستی که من قومِ خود را شب و روز [به ایمان] دعوت کردم؛');
INSERT INTO "islamhouse" VALUES(71,6,'71:6','ولی دعوت من، جز بر گریزشان [از حق] نیفزود');
INSERT INTO "islamhouse" VALUES(71,7,'71:7','و من هر چه دعوتشان کردم تا سرانجام آنان را بیامرزی، انگشتان‌ خود را در گوش‌هایشان فرو کردند و لباس‌هایشان را [به خود] پیچیدند و [در مخالفت] اصرار [و پا‌فشاری] کردند و به شدت، گردنکشی و تکبر نمودند.');
INSERT INTO "islamhouse" VALUES(71,8,'71:8','سپس آنها را با صدای بلند [و آشکار] دعوت کردم.');
INSERT INTO "islamhouse" VALUES(71,9,'71:9','به علاوه، آشکارا برایشان گفتم و در نهان برای [هرکدام از] آنان بیان کردم.');
INSERT INTO "islamhouse" VALUES(71,10,'71:10','گفتم: «از پروردگار خویش آمرزش بخواهید [که] بی‌گمان، او بسیار آمرزنده است.');
INSERT INTO "islamhouse" VALUES(71,11,'71:11','تا از آسمان [باران] پیاپی بر شما بفرستد.');
INSERT INTO "islamhouse" VALUES(71,12,'71:12','و شما را با اموال و فرزندانِ بسیار یاری کند، به شما باغ‌های [سرسبز] بدهد و جویبارها[ی جاری] برایتان قرار دهد.');
INSERT INTO "islamhouse" VALUES(71,13,'71:13','شما را چه شده است که از عظمت [و شکوه] الله نمی‌ترسید؟');
INSERT INTO "islamhouse" VALUES(71,14,'71:14','در حالی ‌که شما را طی مراحل گوناگون آفرید.');
INSERT INTO "islamhouse" VALUES(71,15,'71:15','آیا نمی‌بینید که الله چگونه هفت آسمان را بر فراز یکدیگر آفرید؟');
INSERT INTO "islamhouse" VALUES(71,16,'71:16','و ماه را در میان آسمان‌ها مایۀ روشنایی قرار داد، و خورشید را چراغ [فروزانی] گردانید؟');
INSERT INTO "islamhouse" VALUES(71,17,'71:17','و الله شما را همچون گیاهی از زمین رویانید.');
INSERT INTO "islamhouse" VALUES(71,18,'71:18','سپس شما را به آن [زمین] بازمی‌گرداند و بار دیگر شما را بیرون می‌آورَد.');
INSERT INTO "islamhouse" VALUES(71,19,'71:19','و الله زمین را برای شما گسترده ساخت.');
INSERT INTO "islamhouse" VALUES(71,20,'71:20','تا از راه‌های پهناورِ آن [به سوی مقصد خود] رفت و آمد کنید».');
INSERT INTO "islamhouse" VALUES(71,21,'71:21','نوح گفت: «پروردگارا، آنها از من نافرمانی نمودند و از کسی پیروی کردند که مال و فرزندش، چیزی جز زیانکاری بر او نیفزوده است.');
INSERT INTO "islamhouse" VALUES(71,22,'71:22','و [آن پیشوایان گمراه،] نیرنگِ بزرگی به کار بردند');
INSERT INTO "islamhouse" VALUES(71,23,'71:23','و گفتند: معبودان خود را رها نکنید؛ و [به خصوص] «وَدّ» و «سَواع» و «یَغوث» و «یَعوق» و «نَسر» را [رها نکنید].');
INSERT INTO "islamhouse" VALUES(71,24,'71:24','به راستی آنها بسیاری را گمراه کردند؛ و [تو ای پروردگار،] ستمکاران را جز گمراهی میفزای».');
INSERT INTO "islamhouse" VALUES(71,25,'71:25','[سرانجام، آنان] به کیفرِ گناهانشان [همگی] غرق شدند؛ آنگاه در آتش [دوزخ] درآورده شدند و در برابر [عذاب] الهی، یاور [و مددکاری] برای خود نیافتند.');
INSERT INTO "islamhouse" VALUES(71,26,'71:26','نوح گفت: «پروردگارا، هیچ‌یک از کافران را بر روی زمین باقی مگذار.');
INSERT INTO "islamhouse" VALUES(71,27,'71:27','چرا که اگر آنها را باقی گذاری، بندگانت را گمراه می‌کنند و جز [نسلی] بدکار و کافر به وجود نمی‌آورند.');
INSERT INTO "islamhouse" VALUES(71,28,'71:28','پروردگارا، مرا و پدر و مادرم و هر کسی را که با ایمان وارد خانۀ من شود، و [تمام] مردان مؤمن و زنان مؤمن را بیامرز؛ و ستمکاران را جز هلاکت [و نابودی] میفزای».');
INSERT INTO "islamhouse" VALUES(72,1,'72:1','[ای پیامبر،] بگو: «به من وحی شده است که گروهی از جنیان [به این قرآن] گوش فرا دادند، و [پس از بازگشت به میان قوم خویش، به آنان] گفتند: بی‌گمان ما قرآنی شگفت‌آور شنیدیم');
INSERT INTO "islamhouse" VALUES(72,2,'72:2','که به راه راست هدایت می‌کند؛ پس ما به آن ایمان آوردیم و هرگز کسی را با پروردگارمان شریک قرار نمی‌دهیم؛');
INSERT INTO "islamhouse" VALUES(72,3,'72:3','و اینکه شأن و عظمت پروردگارمان، بس [عظیم و] بلند است. او [هرگز برای خود] همسر و فرزندی انتخاب نکرده است؛');
INSERT INTO "islamhouse" VALUES(72,4,'72:4','و اینکه سفیه [و نادانِ] ما [= شیطان] دربارۀ الله سخنان ناروایی می‌گفت؛');
INSERT INTO "islamhouse" VALUES(72,5,'72:5','و ما [چنین] گمان می‌کردیم که هرگز انس و جن بر الله دروغ نمی‌بندند؛');
INSERT INTO "islamhouse" VALUES(72,6,'72:6','و اینکه مردانی از آدمیان، به مردانی از جنّیان پناه می‌بردند و [این‌گونه] بر گمراهی و سرکشیِ‌ آنان می‌افزودند؛');
INSERT INTO "islamhouse" VALUES(72,7,'72:7','و اینکه آنان [= کافران] گمان می‌کردند ـ همان ‌گونه که شما [جنیان] می‌پنداشتید ـ که الله هیچ کس را [دوباره] زنده نمی‌گردانَد؛');
INSERT INTO "islamhouse" VALUES(72,8,'72:8','و اینکه ما آسمان را [در پی شنیدن خبرها] جستجو کردیم؛ و آن را پر از محافظان نیرومند و تیرهای شهاب یافتیم.');
INSERT INTO "islamhouse" VALUES(72,9,'72:9','و اینکه در [گذشته] جاهایی [در آسمان‌] براى شنیدن، به كمین مى‌نشستیم؛ [اما] اكنون هر کس بخواهد گوش دهد، تیر شهابى در كمین خود مى‌یابد؛');
INSERT INTO "islamhouse" VALUES(72,10,'72:10','و اینکه ما نمی‌دانیم آیا برای اهل زمین شرّی اراده شده، یا پروردگارشان خیر و هدایتِ آنان را خواسته است؛');
INSERT INTO "islamhouse" VALUES(72,11,'72:11','و اینکه در میان ما افرادی صالح و افرادی نادرستند، ما گروه‌هایی متفاوت [و گوناگون] هستیم.');
INSERT INTO "islamhouse" VALUES(72,12,'72:12','و اینکه ما یقین داریم که هرگز نمی‌توانیم در روی زمین، الله را عاجز و ناتوان کنیم؛ و هرگز با گریز نمی‌توانیم او را ناتوان سازیم [و راه فراری از عذابش نداریم].');
INSERT INTO "islamhouse" VALUES(72,13,'72:13','و اینکه ما هنگامی که [سخن] هدایت را شنیدیم، به آن ایمان آوردیم؛ پس هر کس که به پروردگارش ایمان آورَد، نه از نُقصان می‌هراسد و نه از ستم [بیم دارد].');
INSERT INTO "islamhouse" VALUES(72,14,'72:14','و اینکه گروهی از ما، مسلمان، و گروهی، کافر [و ظالم] هستند؛ هر کس که اسلام آورده، آنانند که راه رستگاری را جستجو کرده [و برگزیده]اند.');
INSERT INTO "islamhouse" VALUES(72,15,'72:15','و اما کافران [و ستمکاران] هیزم [آتش] جهنم خواهند بود».');
INSERT INTO "islamhouse" VALUES(72,16,'72:16','و اینکه اگر آنها بر راه راست [اسلام]، پایداری کنند، البته با آبِ فراوان سیرابشان می‌کنیم');
INSERT INTO "islamhouse" VALUES(72,17,'72:17','تا آنها را با آن [نعمت‌ها] بیازماییم؛ و هر کس از یاد [و ذکر] پروردگارش روی برتابد، [الله] او را به عذابی سخت گرفتار می‌سازد؛');
INSERT INTO "islamhouse" VALUES(72,18,'72:18','و اینکه مساجد از آنِ الله است؛ پس کسی را [در مساجد] با الله [به عبادت] نخوانید؛');
INSERT INTO "islamhouse" VALUES(72,19,'72:19','و اینکه هنگامی ‌که بندۀ الله [محمد صلی الله علیه وسلم به عبادت] برخاست تا او را بخوانَد، نزدیک بود که [از ازدحام بر گِرد او] بر سرِ هم فروریزند».');
INSERT INTO "islamhouse" VALUES(72,20,'72:20','[ای پیامبر، به آنها] بگو: «من تنها پروردگارم را می‌خوانم و کسی را با او شریک نمی‌سازم».');
INSERT INTO "islamhouse" VALUES(72,21,'72:21','بگو: «بی‌گمان، من نه می‌توانم زیانی به شما برسانم و نه خیر و سودی».');
INSERT INTO "islamhouse" VALUES(72,22,'72:22','بگو: «[اگر من نیز بر خلاف فرمانش رفتار کنم] هیچ کس مرا در برابرِ او حمایت نمی‌کند و پناهگاهی جز او نمی‌یابم.');
INSERT INTO "islamhouse" VALUES(72,23,'72:23','[وظیفۀ من‌،] تنها ابلاغی از جانبِ الله و [رساندن‌] پیام‌های اوست. و هر کس از الله و پیامبرش نافرمانی کند، بی‌گمان، آتش جهنم از آنِ اوست؛ جاودانه در آن خواهد ماند».');
INSERT INTO "islamhouse" VALUES(72,24,'72:24','[کار کفار همچنان ادامه دارد] تا زمانی که آنچه را به آنها وعده داده شده، ببینند. آنگاه خواهند دانست که چه کسی یاورش ناتوان‌تر، و جمیعتش کمتر است.');
INSERT INTO "islamhouse" VALUES(72,25,'72:25','[ای پیامبر،] بگو: «من نمی‌دانم آیا آنچه که به شما وعده داده شده نزدیک است یا پروردگارم زمانی [دور] برای آن قرار می‌دهد».');
INSERT INTO "islamhouse" VALUES(72,26,'72:26','[الله] دانای غیب است و هیچ کس را بر [اسرارِ] غیبِ خود آگاه نمی‌سازد؛');
INSERT INTO "islamhouse" VALUES(72,27,'72:27','مگر رسولانی که آنان را پسندیده [و برگزیده] است؛ که بی‌گمان، از پیش روی او و پشت سرش، نگهبانی [از فرشتگان برای محافظت از وی] می‌گمارد،');
INSERT INTO "islamhouse" VALUES(72,28,'72:28','تا [پیامبر] بداند که [پیامبران پیش از او نیز] رسالت‌های پروردگار‌شان را ابلاغ کرده‌اند؛ و [الله] به آنچه نزد آنهاست احاطه دارد؛ و هر چیزی را به عدد برشمرده است.');
INSERT INTO "islamhouse" VALUES(73,1,'73:1','ای جامه‌به‌خودپیچیده،');
INSERT INTO "islamhouse" VALUES(73,2,'73:2','شب را ـ جز اندکی‌ـ به پا خیز [و عبادت کن].');
INSERT INTO "islamhouse" VALUES(73,3,'73:3','نیمه‌ای از آن را، یا اندکی از آن کم کن.');
INSERT INTO "islamhouse" VALUES(73,4,'73:4','یا اندکی بر [نصف] آن بیفزای، و قرآن را با ترتیل [و تأمل] بخوان.');
INSERT INTO "islamhouse" VALUES(73,5,'73:5','یقیناً ما به زودی سخنی سنگین بر تو نازل خواهیم کرد.');
INSERT INTO "islamhouse" VALUES(73,6,'73:6','بی‌گمان، شب‌زنده‌داری [بر نفس] دشوار است، و برای گفتار [و نیایش،] مناسب‌تر و استوارتر است.');
INSERT INTO "islamhouse" VALUES(73,7,'73:7','و یقیناً تو در روز، مشغلۀ بسیار داری.');
INSERT INTO "islamhouse" VALUES(73,8,'73:8','و نام پروردگارت را یاد کن و [با خالص نمودن عبادت] تنها به او دل ببند.');
INSERT INTO "islamhouse" VALUES(73,9,'73:9','[همان] پروردگار مشرق و مغرب که معبودی [به‌حق] جز او نیست؛ پس او را کارساز [و نگهبان خود] برگُزین.');
INSERT INTO "islamhouse" VALUES(73,10,'73:10','و بر آنچه [مشرکان] می‌گویند، شکیبا باش، و به شیوۀ نیکو [و شایسته] از آنان دوری کن،');
INSERT INTO "islamhouse" VALUES(73,11,'73:11','و مرا با این تکذیب‌کنندگان مرفّه واگذار و کمی به آنان مهلت ده.');
INSERT INTO "islamhouse" VALUES(73,12,'73:12','بی‌گمان، نزد ما قید [و بندهایی] سنگین و [آتش] دوزخ است،');
INSERT INTO "islamhouse" VALUES(73,13,'73:13','و غذایی [ناگوار و] گلوگیر و عذابی دردناک.');
INSERT INTO "islamhouse" VALUES(73,14,'73:14','روزی ‌که زمین و کوه‌ها به لرزه درآید و کوه‌ها [متلاشی ‌شوند و] همچون تودۀ شنِ نرم [و روان] ‌گردند.');
INSERT INTO "islamhouse" VALUES(73,15,'73:15','یقیناً ما پیامبری به سوی شما فرستادیم که بر شما شاهد [و گواه] است؛ همان‌گونه که به سوی فرعون پیامبری فرستادیم.');
INSERT INTO "islamhouse" VALUES(73,16,'73:16','آنگاه فرعون از آن پیامبر نافرمانی [و با او مخالفت] کرد، و ما او را به شدّت مجازات کردیم.');
INSERT INTO "islamhouse" VALUES(73,17,'73:17','پس اگر کافر شوید، چگونه خود را [از عذاب] در امان می‌دارید [در] روزی‌ که [از هول و سختی] کودکان را پیر می‌گرداند؟');
INSERT INTO "islamhouse" VALUES(73,18,'73:18','[در آن روز،] آسمان شکافته می‌شود [و] وعدۀ او قطعاً به وقوع می‌پیوندد.');
INSERT INTO "islamhouse" VALUES(73,19,'73:19','بی‌گمان، این [آیات،] پند [و هشداری] است تا هر کس بخواهد، به سوى پروردگار خود راهى در پیش گیرد.');
INSERT INTO "islamhouse" VALUES(73,20,'73:20','[ای پیامبر،] یقیناً پروردگارت می‌داند که تو و گروهی از کسانی که با تو هستند، نزدیک دو سوم شب و [یا] نصف [یا] یک‌سومِ آن را به [نماز] برمی‌خیزید؛ و الله شب و روز را اندازه‌گیری می‌کند. [او] می‌داند که شما نمی‌توانید شمارشِ آن را [دقیق] محاسبه کنید؛ و شما را بخشید. پس اکنون هر چه از قرآن [برای شما] میسّر باشد، بخوانید. [الله] می‌داند که برخی از شما بیمار خواهند شد، و گروهی [دیگر] برای به دست ‌آوردن فضل و نعمتِ الله، در زمین سفر می‌کنند، و گروهی [نیز] در راه الله جهاد می‌کنند؛ پس آنچه از آن میسر باشد بخوانید و نماز بر پا دارید و زکات دهید و به الله قرض نیکو دهید؛ و [بدانید که] آنچه از کارهای خیر پیشاپیش برای خود می‌فرستید، [آن را] نزد الله به بهترین وجه و بزرگ‌ترین پاداش خواهید یافت؛ و از الله آمرزش بخواهید، که همانا الله آمرزندۀ مهربان است.');
INSERT INTO "islamhouse" VALUES(74,1,'74:1','ای جامه‌برسرکشیده،');
INSERT INTO "islamhouse" VALUES(74,2,'74:2','برخیز و بیم ده،');
INSERT INTO "islamhouse" VALUES(74,3,'74:3','و پروردگارت را بزرگ شمار،');
INSERT INTO "islamhouse" VALUES(74,4,'74:4','و جامه‌ات را پاکیزه بدار،');
INSERT INTO "islamhouse" VALUES(74,5,'74:5','و از پلیدی [عبادت معبودان باطل] دوری کن.');
INSERT INTO "islamhouse" VALUES(74,6,'74:6','به این هدف بذل و بخشش مکن که افزون‌طلبی کنی؛');
INSERT INTO "islamhouse" VALUES(74,7,'74:7','و برای [رضایت] پروردگارت شکیبا باش.');
INSERT INTO "islamhouse" VALUES(74,8,'74:8','هنگامی‌ که در «صور» دمیده شود.');
INSERT INTO "islamhouse" VALUES(74,9,'74:9','آن روز، روزِ سخت [و دشواری] است.');
INSERT INTO "islamhouse" VALUES(74,10,'74:10','بر کافران آسان نیست.');
INSERT INTO "islamhouse" VALUES(74,11,'74:11','[ای پیامبر،] مرا با کسی ‌که او را تنها [و بدون اموال و اولاد] آفریده‌ام واگذار؛');
INSERT INTO "islamhouse" VALUES(74,12,'74:12','[همان کسی که] مال فراوانی برایش قرار دادم،');
INSERT INTO "islamhouse" VALUES(74,13,'74:13','و فرزندانی که همیشه [در خدمت او و] با او هستند.');
INSERT INTO "islamhouse" VALUES(74,14,'74:14','و همۀ وسایل [و امکانات] زندگی را در اختیارش قرار دادم.');
INSERT INTO "islamhouse" VALUES(74,15,'74:15','باز [هم] طمع دارد که [بر آن] بیفزایم.');
INSERT INTO "islamhouse" VALUES(74,16,'74:16','هرگز، [چنین نخواهد شد.] بی‌گمان، او نسبت به آیات ما دشمنی دارد،');
INSERT INTO "islamhouse" VALUES(74,17,'74:17','و به زودی او را به مشقّت و سختی می‌اندازم؛');
INSERT INTO "islamhouse" VALUES(74,18,'74:18','زیرا او [برای مبارزه با قرآن] اندیشید و محاسبه کرد.');
INSERT INTO "islamhouse" VALUES(74,19,'74:19','پس مرگ بر او باد! چگونه اندازه [و محاسبه] کرد؟');
INSERT INTO "islamhouse" VALUES(74,20,'74:20','باز [هم] مرگ بر او باد! چگونه اندازه [و محاسبه] کرد؟');
INSERT INTO "islamhouse" VALUES(74,21,'74:21','سپس نظر افکند؛');
INSERT INTO "islamhouse" VALUES(74,22,'74:22','آنگاه چهره در هم کشید و روی ترش کرد،');
INSERT INTO "islamhouse" VALUES(74,23,'74:23','سپس [به حق] پشت نمود و تکبر ورزید؛');
INSERT INTO "islamhouse" VALUES(74,24,'74:24','آنگاه گفت: «این [قرآن،] چیزی جز جادویی که [از جادوگران پیشین] نقل شده، نیست؛');
INSERT INTO "islamhouse" VALUES(74,25,'74:25','و این [چیزی] جز گفتار بشر نیست».');
INSERT INTO "islamhouse" VALUES(74,26,'74:26','به زودی او را به دوزخ درخواهم آورد.');
INSERT INTO "islamhouse" VALUES(74,27,'74:27','و تو چه ‌دانی که دوزخ چگونه است؟');
INSERT INTO "islamhouse" VALUES(74,28,'74:28','[آتش سوزانی است که] نه [چیزی را] باقی می‌گذارد و نه [چیزی را] رها می‌کند.');
INSERT INTO "islamhouse" VALUES(74,29,'74:29','پوست را [دگرگون کرده و] می‌سوزانَد.');
INSERT INTO "islamhouse" VALUES(74,30,'74:30','بر آن [آتش،] نوزده [فرشته] گمارده شده است.');
INSERT INTO "islamhouse" VALUES(74,31,'74:31','ما مأموران دوزخ را فقط از فرشتگان قرار دادیم، و شمار آنها را تنها برای آزمایش کافران معیّن کرده‌ایم تا اهل کتاب یقین کنند و کسانی ‌که ایمان آورده‌اند بر ایمان خود بیفزایند و اهل کتاب و مؤمنان، گرفتار شک و تردید نشوند؛ و تا کسانی‌ که در دل‌هایشان مرض [نفاق] است و [نیز] کافران بگویند: «هدف الله از این مَثل [و توصیف] چیست؟» این‌گونه، الله هر ‌کس را بخواهد، گمراه می‌سازد و هر کس را بخواهد، هدایت می‌کند؛ و [شمارِ] لشکریان پروردگارت را [کسی] جز [خود] او تعالی نمی‌داند؛ و این جز پند و اندرزی برای انسان‌ها نیست.');
INSERT INTO "islamhouse" VALUES(74,32,'74:32','آری؛ سوگند به ماه [که هرگز اینچنین نیست که آنان تصور می‌کنند].');
INSERT INTO "islamhouse" VALUES(74,33,'74:33','و [سوگند به] شب، هنگامی ‌که پشت کند.');
INSERT INTO "islamhouse" VALUES(74,34,'74:34','و به صبح، هنگامی ‌که روشن شود.');
INSERT INTO "islamhouse" VALUES(74,35,'74:35','که آن [آتش دوزخ،] یکی از [عذاب‌های] بزرگ [و هولناک] است.');
INSERT INTO "islamhouse" VALUES(74,36,'74:36','برای انسان، بیم‌دهنده است.');
INSERT INTO "islamhouse" VALUES(74,37,'74:37','برای کسی از شما که بخواهد [با انجام کارهای نیکو] پیش افتد، یا [با ارتکاب گناه] عقب بماند.');
INSERT INTO "islamhouse" VALUES(74,38,'74:38','هر کس در گروِ اعمال خویش است؛');
INSERT INTO "islamhouse" VALUES(74,39,'74:39','مگر اصحاب یمین [= مؤمنان]');
INSERT INTO "islamhouse" VALUES(74,40,'74:40','که در باغ‌های [بهشت] هستند و می‌پرسند');
INSERT INTO "islamhouse" VALUES(74,41,'74:41','از گناهکاران:');
INSERT INTO "islamhouse" VALUES(74,42,'74:42','چه چیز شما را به دوزخ درآورد؟');
INSERT INTO "islamhouse" VALUES(74,43,'74:43','[دوزخیان] می‌گویند: «ما از نمازگزاران نبودیم');
INSERT INTO "islamhouse" VALUES(74,44,'74:44','و بینوا[یان] را اطعام نمی‌کردیم');
INSERT INTO "islamhouse" VALUES(74,45,'74:45','و پیوسته همراه یاوه‌گویان [و اهل باطل] هم‌صدا می‌شدیم.');
INSERT INTO "islamhouse" VALUES(74,46,'74:46','و همواره روزِ جزا را تکذیب می‌کردیم،');
INSERT INTO "islamhouse" VALUES(74,47,'74:47','تا زمانی که مرگ به سراغمان آمد».');
INSERT INTO "islamhouse" VALUES(74,48,'74:48','پس شفاعتِ شفاعت‌کنندگان سودی به آنها نمی‌بخشد.');
INSERT INTO "islamhouse" VALUES(74,49,'74:49','آنها را چه شده است که از پند [و تذکر] روی گردانند؟');
INSERT INTO "islamhouse" VALUES(74,50,'74:50','گویى كه گورخرانى رمیده‌اند');
INSERT INTO "islamhouse" VALUES(74,51,'74:51','که از [مقابل] شیر گریخته‌اند.');
INSERT INTO "islamhouse" VALUES(74,52,'74:52','بلکه هر کدام از آنها [توقع دارد و] می‌خواهد که [به او] نامه‌های گشاده [و جداگانه از سوی الله] داده شود.');
INSERT INTO "islamhouse" VALUES(74,53,'74:53','هرگز چنین نیست [که آنان می‌پندارند]! بلکه آنها از [عذاب] آخرت نمی‌ترسند.');
INSERT INTO "islamhouse" VALUES(74,54,'74:54','نه، چنین نیست كه آنها مى‌گویند؛ آن [قرآن] یک تذكر و یادآورى است.');
INSERT INTO "islamhouse" VALUES(74,55,'74:55','پس هر ‌کس که بخواهد، [می‌تواند] از آن پند گیرد.');
INSERT INTO "islamhouse" VALUES(74,56,'74:56','و یاد نمی‌کنند [و پند نمی‌گیرند] مگر اینکه الله بخواهد. اوست که سزاوار پروا کردن و آمرزیدن است.');
INSERT INTO "islamhouse" VALUES(75,1,'75:1','به روز قیامت سوگند می‌خورم.');
INSERT INTO "islamhouse" VALUES(75,2,'75:2','و به نفسِ ملامت‌گر سوگند می‌خورم.');
INSERT INTO "islamhouse" VALUES(75,3,'75:3','آیا انسان می‌پندارد که [پس از مرگ] هرگز استخوان‌هایش را جمع نخواهیم کرد؟');
INSERT INTO "islamhouse" VALUES(75,4,'75:4','آری، ما قادریم که [حتی خطوط سر] انگشتانش را یکسان و مرتب کنیم.');
INSERT INTO "islamhouse" VALUES(75,5,'75:5','بلکه انسان می‌خواهد که [آزادانه و بدون ترس از آخرت،] در گناه و بدکاری مداومت کند.');
INSERT INTO "islamhouse" VALUES(75,6,'75:6','[از این رو] می‌پرسد: «روز قیامت کی خواهد بود؟»');
INSERT INTO "islamhouse" VALUES(75,7,'75:7','هنگامی ‌که چشم [وحشت‌زده و] خیره شود');
INSERT INTO "islamhouse" VALUES(75,8,'75:8','و ماه تیره [و بی‌نور] گردد');
INSERT INTO "islamhouse" VALUES(75,9,'75:9','و خورشید و ماه، یکجا جمع شوند،');
INSERT INTO "islamhouse" VALUES(75,10,'75:10','آن روز، انسان می‌گوید: «راهِ گریز کجاست؟»');
INSERT INTO "islamhouse" VALUES(75,11,'75:11','هرگز! هیچ گریزگاهی نیست.');
INSERT INTO "islamhouse" VALUES(75,12,'75:12','آن روز، قرارگاه [نهایی،] به سوی پروردگار توست.');
INSERT INTO "islamhouse" VALUES(75,13,'75:13','در آن روز، انسان را از تمام كارهایى كه پیشاپیش فرستاده و کارهایی که به تاخیر انداخته [و انجام نداده است]، آگاه مى‌كنند.');
INSERT INTO "islamhouse" VALUES(75,14,'75:14','بلکه انسان به خوبی بر خویشتن آگاه است.');
INSERT INTO "islamhouse" VALUES(75,15,'75:15','اگر چه [در دفاع از خود،] عذرهایش را در میان آورد.');
INSERT INTO "islamhouse" VALUES(75,16,'75:16','[ای پیامبر، هنگام نزول قرآن،] زبانت را برای [تکرار و خواندنِ] آن، شتابزده حرکت مده.');
INSERT INTO "islamhouse" VALUES(75,17,'75:17','مسلماً جمع‌آوری و خواندنِ آن، بر [عهدۀ] ماست.');
INSERT INTO "islamhouse" VALUES(75,18,'75:18','پس هر گاه [توسط جبرئیل] آن را [بر تو] خواندیم، از خواندنِ او پیروی کن.');
INSERT INTO "islamhouse" VALUES(75,19,'75:19','سپس بیان‌کردنش بر [عهدۀ] ماست.');
INSERT INTO "islamhouse" VALUES(75,20,'75:20','هرگز چنین نیست [که شما مشرکان می‌پندارید]! بلکه شما دنیا[ی زودگذر] را دوست دارید');
INSERT INTO "islamhouse" VALUES(75,21,'75:21','و آخرت را رها می‌کنید.');
INSERT INTO "islamhouse" VALUES(75,22,'75:22','آن روز، [اهل ایمان دارای] چهره‌هایی تازه [و شاداب] هستند.');
INSERT INTO "islamhouse" VALUES(75,23,'75:23','به سوی پروردگارشان می‌نگرند.');
INSERT INTO "islamhouse" VALUES(75,24,'75:24','و در آن روز، [کافران و بدبختان دارای] چهره‌هایی عبوس [و درهم‌کشیده] هستند.');
INSERT INTO "islamhouse" VALUES(75,25,'75:25','زیرا یقین دارند که آسیبی کمر‌شکن به آنها می‌رسد.');
INSERT INTO "islamhouse" VALUES(75,26,'75:26','آری، چون [جان] به گلوگاهش برسد.');
INSERT INTO "islamhouse" VALUES(75,27,'75:27','و گفته شود: چه کسی است که رُقیه بخواند؟ [شاید شفا یابد].');
INSERT INTO "islamhouse" VALUES(75,28,'75:28','و یقین کند که زمان جدایی [از دنیا فرا رسیده] است.');
INSERT INTO "islamhouse" VALUES(75,29,'75:29','و [کار بالا گیرد و] ساق‌ها[ی پایش از سختیِ جان دادن] به هم بپیچد.');
INSERT INTO "islamhouse" VALUES(75,30,'75:30','در آن روز، مسیرِ همه به سوی پروردگارت خواهد بود.');
INSERT INTO "islamhouse" VALUES(75,31,'75:31','[منکرِ معاد،] نه تصدیق کرده و نه نماز گزارده است؛');
INSERT INTO "islamhouse" VALUES(75,32,'75:32','بلکه [قرآن را] تکذیب کرد و [از ایمان] روی گرداند.');
INSERT INTO "islamhouse" VALUES(75,33,'75:33','سپس خرامان به سوی خانواده‌اش بازگشت.');
INSERT INTO "islamhouse" VALUES(75,34,'75:34','وای بر تو! پس وای بر تو!');
INSERT INTO "islamhouse" VALUES(75,35,'75:35','باز هم وای بر تو! پس وای بر تو!');
INSERT INTO "islamhouse" VALUES(75,36,'75:36','آیا انسان گمان می‌کند که [بی‌هدف و بدون حساب و جزا] به خود رها می‌شود؟');
INSERT INTO "islamhouse" VALUES(75,37,'75:37','آیا [او] نطفه‌ای از منی نبود که [در رحم] ریخته می‌شود؟');
INSERT INTO "islamhouse" VALUES(75,38,'75:38','سپس به صورت خونِ بسته درآمد و [الله او را] آفرید و درست و استوار ساخت،');
INSERT INTO "islamhouse" VALUES(75,39,'75:39','سپس دو زوج نر و ماده از او پدید آورد.');
INSERT INTO "islamhouse" VALUES(75,40,'75:40','آیا [چنین آفریدگاری] قادر نیست که مردگان را [دوباره] زنده کند؟');
INSERT INTO "islamhouse" VALUES(76,1,'76:1','یقیناً زمانی طولانی بر انسان گذشت که چیزی [مهم و] قابل ذکری نبود.');
INSERT INTO "islamhouse" VALUES(76,2,'76:2','به راستی ما انسان را از نطفۀ مختلطی آفریدیم، او را می‌آزماییم؛ از این رو] او را شنوای بینا قرار دادیم.');
INSERT INTO "islamhouse" VALUES(76,3,'76:3','همانا ما راه را به او نشان دادیم؛ خواه سپاسگزار باشد یا ناسپاس.');
INSERT INTO "islamhouse" VALUES(76,4,'76:4','به راستی که ما زنجیرها و بند‌ها و آتش سوزان برای کافران مهیا کرده‌ایم.');
INSERT INTO "islamhouse" VALUES(76,5,'76:5','بی‌گمان، نیکوکاران [در بهشت] از جامی می‌نوشند که آمیخته به کافور است.');
INSERT INTO "islamhouse" VALUES(76,6,'76:6','[از] چشمه‌ای که بندگان [خاص] الله از آن می‌نوشند؛ [هر وقت و] هر جا بخواهند آن [چشمه] را جاری می‌سازند.');
INSERT INTO "islamhouse" VALUES(76,7,'76:7','[بندگان نیکوکار] به نذر وفا می‌کنند و از روزی می‌ترسند که [عذاب و] شرّ آن فراگیر است.');
INSERT INTO "islamhouse" VALUES(76,8,'76:8','و غذا را با اینکه [نیاز و] دوست دارند به مستمند و یتیم و اسیر می‌بخشند.');
INSERT INTO "islamhouse" VALUES(76,9,'76:9','[و در دل می‌گویند:] «ما فقط به خاطر الله به شما غذا می‌دهیم؛ نه از شما پاداشی می‌خواهیم و نه سپاسی.');
INSERT INTO "islamhouse" VALUES(76,10,'76:10','همانا ما از پروردگارمان می‌ترسیم، [از عذابِ] روزی ‌که تُرشروی و دشوار است».');
INSERT INTO "islamhouse" VALUES(76,11,'76:11','پس الله آنان را از [سختی و] شرِ آن روز نگه داشت و خرّمی و شادمانی به آنان بخشید.');
INSERT INTO "islamhouse" VALUES(76,12,'76:12','و به [پاداش] صبری که کردند، بهشت و [لباس‌های] حریر [بهشتی] به آنان پاداش داد.');
INSERT INTO "islamhouse" VALUES(76,13,'76:13','در آنجا بر تخت‌ها[ی زیبا] تکیه کرده‌اند؛ نه [حرارتِ] آفتابی در آنجا می‌بینند و نه [سوزِ] سرمایی،');
INSERT INTO "islamhouse" VALUES(76,14,'76:14','و سایه‌هایش بر آنان فروافتاده و میوه‌هایش [برای چیدن] در دسترس [و آسان] است.');
INSERT INTO "islamhouse" VALUES(76,15,'76:15','و ظرف‌های سیمین [غذا‌] و کاسه‌های بلورین [نوشیدنی]، در گِرداگرد آنان، گردانده می‌شود.');
INSERT INTO "islamhouse" VALUES(76,16,'76:16','[کاسه‌های] بلورینی از نقره که آنها را به ‌اندازۀ مناسب، [لبریز و] آماده کرده‌اند؛');
INSERT INTO "islamhouse" VALUES(76,17,'76:17','و در آنجا از جام‌هایی سیراب می‌شوند که آمیزه‌اش زَنجبیل است.');
INSERT INTO "islamhouse" VALUES(76,18,'76:18','[این جام‌ها، از] چشمه‌ای در آنجا که سَلسَبیل نامیده می‌شود [پر می‌شوند]؛');
INSERT INTO "islamhouse" VALUES(76,19,'76:19','و همواره نوجوانانی جاودانه بر گِردشان می‌چرخند که هر گاه آنها را ببینی، گمان می‌کنی مرواریدِ پراکنده هستند.');
INSERT INTO "islamhouse" VALUES(76,20,'76:20','و چون [به هر سَمتی] بنگری، نعمت بسیار و فرمانرواییِ عظیمی آنجا می‌بینی.');
INSERT INTO "islamhouse" VALUES(76,21,'76:21','بر آنان [= بهشتیان] لباس‌هایی سبزرنگ از دیبای نازک و دیبای ضخیم [پوشانده شده] است، و با دستبند‌هایی از نقره آراسته شده‌اند، و پروردگارشان شراب پاک به آنان می‌نوشانَد.');
INSERT INTO "islamhouse" VALUES(76,22,'76:22','[و به آنها گفته می‌شود:] «این [نعمت‌ها،] پاداش شماست، و [بدانید که] از سعی و کوشش شما قدردانی شده است».');
INSERT INTO "islamhouse" VALUES(76,23,'76:23','[ای پیامبر،] یقیناً ما قرآن را به صورت تدریجی بر تو نازل کردیم؛');
INSERT INTO "islamhouse" VALUES(76,24,'76:24','پس بر حُکم پروردگارت شکیبا باش و از هیچ یک از گناهکاران و بی‌دینان آنان فرمان مبر.');
INSERT INTO "islamhouse" VALUES(76,25,'76:25','و صبح و شام، نامِ پروردگارت را یاد کن.');
INSERT INTO "islamhouse" VALUES(76,26,'76:26','و [نیز] بخشی از شب برایش سجده کن [و نماز بگزار]، و در بخش بلندی از شب، او را تسبیح گوی.');
INSERT INTO "islamhouse" VALUES(76,27,'76:27','بی‌گمان، اینان [= کافران] دنیای زودگذر را دوست دارند و روز سختی [را که در پیش دارند] پشت سر خود رها می‌کنند [و به آن اهمیت نمی‌دهند].');
INSERT INTO "islamhouse" VALUES(76,28,'76:28','ما آنها را آفریدیم و پیوند وجودشان را محکم کردیم، و هر زمان که بخواهیم، جای آنان را به [گروه دیگری] همانندشان می‌دهیم.');
INSERT INTO "islamhouse" VALUES(76,29,'76:29','بی‌گمان، این یادآوری [و پند] است؛ پس هر ‌کس که بخواهد، راهی به سوی پروردگارش بر‌گزیند.');
INSERT INTO "islamhouse" VALUES(76,30,'76:30','و تا الله اراده نکند، [شما چیزی را] نخواهید خواست. بی‌گمان، الله دانای حکیم است؛');
INSERT INTO "islamhouse" VALUES(76,31,'76:31','هر کس را که بخواهد، در رحمت خود وارد می‌کند؛ و عذاب دردناکی برای ستمکاران آماده کرده است.');
INSERT INTO "islamhouse" VALUES(77,1,'77:1','سوگند به فرشتگانی که پیاپی فرستاده می‌شوند،');
INSERT INTO "islamhouse" VALUES(77,2,'77:2','و سوگند به فرشتگانی که همچون تندباد می‌روند،');
INSERT INTO "islamhouse" VALUES(77,3,'77:3','و سوگند به فرشتگانی که [ابرها را] پراکنده می‌کنند،');
INSERT INTO "islamhouse" VALUES(77,4,'77:4','و سوگند به فرشتگانی که جداکننده[ی حق از باطل] هستند');
INSERT INTO "islamhouse" VALUES(77,5,'77:5','و سوگند به فرشتگانی که وحی [الهی] را [به پیامبران] القا می‌کنند،');
INSERT INTO "islamhouse" VALUES(77,6,'77:6','برای اتمامِ‌حجت یا برای بیم و هشدار.');
INSERT INTO "islamhouse" VALUES(77,7,'77:7','بی‌گمان، آنچه به شما وعده داده می‌شود، واقع خواهد شد؛');
INSERT INTO "islamhouse" VALUES(77,8,'77:8','آنگاه ‌که ستارگان تیره [و محو] شود');
INSERT INTO "islamhouse" VALUES(77,9,'77:9','و آنگاه‌ که آسمان شکافته شود،');
INSERT INTO "islamhouse" VALUES(77,10,'77:10','و آنگاه ‌که کوه‌ها [از جا کنده و] پراکنده شود،');
INSERT INTO "islamhouse" VALUES(77,11,'77:11','و آنگاه ‌که برای پیامبران [جهت گواهی دادن در مورد امت‌ها]، وقت تعیین گردد،');
INSERT INTO "islamhouse" VALUES(77,12,'77:12','این [امر،] برای چه روزی به تأخیر افتاده است؟');
INSERT INTO "islamhouse" VALUES(77,13,'77:13','برای روز جدایی [و داوری].');
INSERT INTO "islamhouse" VALUES(77,14,'77:14','و تو چه دانی روز جدایی [و داوری] چیست؟');
INSERT INTO "islamhouse" VALUES(77,15,'77:15','در آن روز، [= قیامت] وای بر تکذیب‌کنندگان!');
INSERT INTO "islamhouse" VALUES(77,16,'77:16','آیا ما پیشینیان [مجرم] را نابود نکردیم؟');
INSERT INTO "islamhouse" VALUES(77,17,'77:17','سپس دیگران را در پی آنها خواهیم آورد.');
INSERT INTO "islamhouse" VALUES(77,18,'77:18','اینچنین با گنهکاران رفتار می‌کنیم.');
INSERT INTO "islamhouse" VALUES(77,19,'77:19','در آن روز، وای بر تکذیب‌کنندگان!');
INSERT INTO "islamhouse" VALUES(77,20,'77:20','آیا شما را از آبی پست [و ناچیز] نیافریدیم؟');
INSERT INTO "islamhouse" VALUES(77,21,'77:21','سپس آن را در قرارگاهی محفوظ [= رحِم] قرار دادیم');
INSERT INTO "islamhouse" VALUES(77,22,'77:22','تا زمانی معین.');
INSERT INTO "islamhouse" VALUES(77,23,'77:23','ما [بر این کار] توانا بودیم و چه نیک توانا [و قدرتمند] هستیم.');
INSERT INTO "islamhouse" VALUES(77,24,'77:24','در آن روز، وای بر تکذیب‌کنندگان!');
INSERT INTO "islamhouse" VALUES(77,25,'77:25','آیا زمین را جایگاه [تجمع مردم] قرار ندادیم؟');
INSERT INTO "islamhouse" VALUES(77,26,'77:26','هم در حال حیات و هم مرگشان؟');
INSERT INTO "islamhouse" VALUES(77,27,'77:27','و کوه‌های بسیار بلند [و استوار] در آن قرار دادیم و آبی شیرین [و گوارا] به شما نوشاندیم.');
INSERT INTO "islamhouse" VALUES(77,28,'77:28','در آن روز، وای بر تکذیب‌کنندگان!');
INSERT INTO "islamhouse" VALUES(77,29,'77:29','[به آنها گفته می‌شود:] «به سوی همان چیزی بروید که پیوسته آن را تکذیب می‌کردید.');
INSERT INTO "islamhouse" VALUES(77,30,'77:30','بروید به سوی سایۀ [دودهای آتش] سه‌شاخه،');
INSERT INTO "islamhouse" VALUES(77,31,'77:31','نه سایه‌افکن [و خنک] است و نه از [گرمیِ] شعله‌های آتش جلوگیری می‌کند».');
INSERT INTO "islamhouse" VALUES(77,32,'77:32','همانا [دوزخ،‌] شراره‌هایی چون كاخى [بلند] مى‌افكنَد.');
INSERT INTO "islamhouse" VALUES(77,33,'77:33','گویی که آن [شراره‌ها] شتران زردرنگ هستند.');
INSERT INTO "islamhouse" VALUES(77,34,'77:34','در آن روز، وای بر تکذیب‌کنندگان!');
INSERT INTO "islamhouse" VALUES(77,35,'77:35','این [همان] روزی است که سخن نمی‌گویند.');
INSERT INTO "islamhouse" VALUES(77,36,'77:36','و به آنها اجازه داده نمی‌شود تا عذرخواهی کنند.');
INSERT INTO "islamhouse" VALUES(77,37,'77:37','در آن روز، وای بر تکذیب‌کنندگان!');
INSERT INTO "islamhouse" VALUES(77,38,'77:38','این [همان] روز جدایی [و داوری] است که شما و گذشتگان را گرد آورده‌ایم.');
INSERT INTO "islamhouse" VALUES(77,39,'77:39','پس اگر [حیله و] نیرنگی دارید، آن را در حق من به کار گیرید.');
INSERT INTO "islamhouse" VALUES(77,40,'77:40','در آن روز، وای بر تکذیب‌کنندگان!');
INSERT INTO "islamhouse" VALUES(77,41,'77:41','به راستی که [در آن روز،] پرهیزگاران در سایه‌ها و [کنار] چشمه‌ها قرار دارند،');
INSERT INTO "islamhouse" VALUES(77,42,'77:42','و میوه‌هایی که میل داشته باشند.');
INSERT INTO "islamhouse" VALUES(77,43,'77:43','[به آنها گفته می‌شود:] «به پاداش آنچه می‌کردید، گوارا بخورید و بیاشامید.');
INSERT INTO "islamhouse" VALUES(77,44,'77:44','ما این گونه نیکوکاران را پاداش می‌دهیم».');
INSERT INTO "islamhouse" VALUES(77,45,'77:45','در آن روز، وای بر تکذیب‌کنندگان!');
INSERT INTO "islamhouse" VALUES(77,46,'77:46','«[ای کافران، در این دنیا] اندکی بخورید و بهره گیرید؛ زیرا شما گناهکارید».');
INSERT INTO "islamhouse" VALUES(77,47,'77:47','در آن روز، وای بر تکذیب‌کنندگان!');
INSERT INTO "islamhouse" VALUES(77,48,'77:48','و هنگامی‌ که به آنها گفته شود: [نماز بگزارید و] رکوع کنید، [نماز نمی‌گزارند و] رکوع نمی‌کنند.');
INSERT INTO "islamhouse" VALUES(77,49,'77:49','در آن روز، وای بر تکذیب‌کنندگان!');
INSERT INTO "islamhouse" VALUES(77,50,'77:50','[وقتی مردم به این قرآن که از جانب پروردگارشان نازل شده است ایمان نمی‌آورند،] پس به کدام سخن دیگری غیر از آن ایمان خواهند آورد؟');
INSERT INTO "islamhouse" VALUES(78,1,'78:1','[کافران] دربارۀ چه چیزی از یکدیگر سؤال می‌کنند؟');
INSERT INTO "islamhouse" VALUES(78,2,'78:2','از خبری بزرگ.');
INSERT INTO "islamhouse" VALUES(78,3,'78:3','همان [خبری] که در مورد آن با هم اختلاف دارند.');
INSERT INTO "islamhouse" VALUES(78,4,'78:4','چنین نیست [که آنها می‌اندیشند]؛ به زودی خواهند دانست.');
INSERT INTO "islamhouse" VALUES(78,5,'78:5','باز هم چنین نیست؛ به زودی خواهند دانست.');
INSERT INTO "islamhouse" VALUES(78,6,'78:6','آیا زمین را بستری [برای آسایش شما] قرار ندادیم؟');
INSERT INTO "islamhouse" VALUES(78,7,'78:7','و کوه‌ها را میخ‌ها[ی آن قرار ندادیم؟]');
INSERT INTO "islamhouse" VALUES(78,8,'78:8','و شما را جفت [= نر و ماده] آفریدیم.');
INSERT INTO "islamhouse" VALUES(78,9,'78:9','و خواب شما را [مایۀ] آرامشتان قرار دادیم.');
INSERT INTO "islamhouse" VALUES(78,10,'78:10','و شب را پوششی [برایتان] قرار دادیم.');
INSERT INTO "islamhouse" VALUES(78,11,'78:11','و روز را [وسیلۀ] زندگی و [امرارِ معاش] قرار دادیم،');
INSERT INTO "islamhouse" VALUES(78,12,'78:12','و بر فراز شما هفت [آسمان] محکم بنا کردیم،');
INSERT INTO "islamhouse" VALUES(78,13,'78:13','و [خورشید را] چراغی درخشان [و روشنایی‌بخش] آفریدیم،');
INSERT INTO "islamhouse" VALUES(78,14,'78:14','و از ابر‌های باران‌زا، آبی فراوان فرو‌فرستادیم');
INSERT INTO "islamhouse" VALUES(78,15,'78:15','تا دانه و گیاه بسیار با آن برویانیم.');
INSERT INTO "islamhouse" VALUES(78,16,'78:16','و باغ‌هایی پردرخت [با آن پرورش دهیم].');
INSERT INTO "islamhouse" VALUES(78,17,'78:17','بی‌گمان، روز داورى [میان مخلوقات]، وعده‌گاه [ما با شما] است.');
INSERT INTO "islamhouse" VALUES(78,18,'78:18','روزی ‌که در «صور» دمیده می‌شود و شما گروه‌گروه [به محشر] می‌آیید؛');
INSERT INTO "islamhouse" VALUES(78,19,'78:19','و آسمان گشوده می‌شود و به صورت درهای متعدد درمی‌آید؛');
INSERT INTO "islamhouse" VALUES(78,20,'78:20','و کوه‌ها به حرکت درمی‌آید و به صورت سرابی می‌شود؛');
INSERT INTO "islamhouse" VALUES(78,21,'78:21','و بی‌گمان، جهنم کمینگاهی است');
INSERT INTO "islamhouse" VALUES(78,22,'78:22','[و] محل بازگشتی برای طغیانگران.');
INSERT INTO "islamhouse" VALUES(78,23,'78:23','مدت زمانی بی‌پایان در آنجا می‌مانند.');
INSERT INTO "islamhouse" VALUES(78,24,'78:24','در آنجا نه [چیز] خنکی می‌چشند و نه آشامیدنی [گوارایی خواهند داشت].');
INSERT INTO "islamhouse" VALUES(78,25,'78:25','جز آبی سوزان و [مایعی که] چرک و خون [جهنمیان است].');
INSERT INTO "islamhouse" VALUES(78,26,'78:26','[این] کیفری است مناسب و درخور [گناهانشان]؛');
INSERT INTO "islamhouse" VALUES(78,27,'78:27','همانا آنان از حساب [و کتاب روز قیامت] نمی‌ترسیدند');
INSERT INTO "islamhouse" VALUES(78,28,'78:28','و آیات ما را به شدت تکذیب کردند؛');
INSERT INTO "islamhouse" VALUES(78,29,'78:29','و ما همه چیز را [در لوح محفوظ] شمارش و ثبت کرده‌ایم.');
INSERT INTO "islamhouse" VALUES(78,30,'78:30','پس [کیفرِ اعمال خود را] بچشید که [چیزی] جز عذاب بر شما نمی‌افزاییم.');
INSERT INTO "islamhouse" VALUES(78,31,'78:31','بی‌گمان، برای پرهیزگاران کامیابی [بزرگی] است.');
INSERT INTO "islamhouse" VALUES(78,32,'78:32','باغ‌های [میوه] و تاکستان‌ها.');
INSERT INTO "islamhouse" VALUES(78,33,'78:33','و حوریانی نوجوان و هم‌سن و سال.');
INSERT INTO "islamhouse" VALUES(78,34,'78:34','و جام‌هایی لبریز و پیاپی [از شراب پاکیزۀ بهشت].');
INSERT INTO "islamhouse" VALUES(78,35,'78:35','در آنجا نه سخن بیهوده‌ای می‌شنوند و نه دروغی.');
INSERT INTO "islamhouse" VALUES(78,36,'78:36','[این] پاداش، از جانب پروردگار توست، و عطایی از روی حساب');
INSERT INTO "islamhouse" VALUES(78,37,'78:37','[همان] پروردگار آسمان‌ها و زمین و آنچه در میان آنهاست؛ [همان الله] رحمان [که] هیچ‌ کس [در آن روز] یارای سخن گفتن با او را ندارد.');
INSERT INTO "islamhouse" VALUES(78,38,'78:38','روزی ‌که روح [= جبرئیل] و فرشتگان به صف می‌ایستند، [و] هیچ‌ کس سخن نمی‌گوید، جز کسی ‌که [الله] رحمان به او اجازه داده باشد و [او] سخن درست [و صواب] گوید.');
INSERT INTO "islamhouse" VALUES(78,39,'78:39','آن [روز]، روزِ حق است [و بدون تردید واقع می‌شود]؛ پس هر کس كه بخواهد، [می‌تواند] راه بازگشتى به سوى پروردگار خود بجوید.');
INSERT INTO "islamhouse" VALUES(78,40,'78:40','به راستی، ما شما را از عذابی نزدیک بیم دادیم: روزی ‌که انسان آنچه را از قبل با دست‌های خود فرستاده است می‌بیند، و کافر می‌گوید: «ای کاش من خاک بودم [و برای حساب برانگیخته نمی‌شدم»]!');
INSERT INTO "islamhouse" VALUES(79,1,'79:1','سوگند به فرشتگانی که جان [کافران] را به شدت بیرون می‌کشند.');
INSERT INTO "islamhouse" VALUES(79,2,'79:2','و سوگند به فرشتگانی که جان [مؤمنان] را به نرمی و آسانی می‌گیرند.');
INSERT INTO "islamhouse" VALUES(79,3,'79:3','و سوگند به فرشتگانی که [به امرِ الله، از آسمان به سوی زمین و بالعکس] شناورند.');
INSERT INTO "islamhouse" VALUES(79,4,'79:4','و سوگند به فرشتگانی که [در اجرای اوامر الهی] بر یکدیگر سبقت می‌گیرند.');
INSERT INTO "islamhouse" VALUES(79,5,'79:5','و سوگند به فرشتگانی [که به امر الهی] کارها را تدبیر می‌کنند.');
INSERT INTO "islamhouse" VALUES(79,6,'79:6','روزی‌ که [پس از اولین دمیدن در صور] زمین [و کوه‌ها و همه چیز] به لرزه درآید');
INSERT INTO "islamhouse" VALUES(79,7,'79:7','[و] در پی آن، [دمیدن دوم حشر] بیاید،');
INSERT INTO "islamhouse" VALUES(79,8,'79:8','در آن روز، دل‌هایی سخت مضطرب [و ترسان] هستند.');
INSERT INTO "islamhouse" VALUES(79,9,'79:9','و چشم‌های آنان [از ترس و شرمساری] فرو افتاده است.');
INSERT INTO "islamhouse" VALUES(79,10,'79:10','[کافران در دنیا] می‌گفتند: «آیا ما [پس از مرگ، دوباره] به حال اول خود باز‌گردانده می‌شویم؟');
INSERT INTO "islamhouse" VALUES(79,11,'79:11','آیا هنگامی‌ که استخوان‌هایی پوسیده شدیم [و به خاک تبدیل گشتیم، باز هم زنده می‌شویم]»؟');
INSERT INTO "islamhouse" VALUES(79,12,'79:12','می‌گفتند: «[اگر چنین وعده‌ای درست باشد،] آنگاه آن بازگشتی زیانبار است».');
INSERT INTO "islamhouse" VALUES(79,13,'79:13','آن [بازگشت،] تنها [به] یک بانگِ مهیب است [و بس].');
INSERT INTO "islamhouse" VALUES(79,14,'79:14','و ناگهان همگی بر عرصۀ زمین [محشر] ظاهر می‌شوند.');
INSERT INTO "islamhouse" VALUES(79,15,'79:15','[ای پیامبر،] آیا داستان موسی به تو رسیده است؟');
INSERT INTO "islamhouse" VALUES(79,16,'79:16','آنگاه که پروردگارش او را در سرزمین مقدس «طُوی» ندا داد [و فرمود:]');
INSERT INTO "islamhouse" VALUES(79,17,'79:17','«به سوی فرعون برو که او طغیان کرده است،');
INSERT INTO "islamhouse" VALUES(79,18,'79:18','و به او بگو: «آیا می‌خواهی [از کفر و گناه] پاک شوی؟');
INSERT INTO "islamhouse" VALUES(79,19,'79:19','و من تو را به سوی پروردگارت هدایت کنم تا [از او] بترسی [و فرمانبردار شوی]؟');
INSERT INTO "islamhouse" VALUES(79,20,'79:20','پس [موسی] معجزۀ بزرگ را به او نشان داد.');
INSERT INTO "islamhouse" VALUES(79,21,'79:21','اما او تکذیب و سرکشی کرد.');
INSERT INTO "islamhouse" VALUES(79,22,'79:22','سپس پشت کرد و به کوشش و تلاش [علیه او] پرداخت.');
INSERT INTO "islamhouse" VALUES(79,23,'79:23','آنگاه [قومش را] جمع کرد و ندا داد');
INSERT INTO "islamhouse" VALUES(79,24,'79:24','و گفت: «من پروردگارِ برتر شما هستم».');
INSERT INTO "islamhouse" VALUES(79,25,'79:25','الله او را به عذاب آخرت و دنیا گرفتار کرد.');
INSERT INTO "islamhouse" VALUES(79,26,'79:26','بی‌گمان، در این [واقعه،] برای هر کس ‌که [از الله] می‌ترسد، عبرتی است.');
INSERT INTO "islamhouse" VALUES(79,27,'79:27','آیا آفرینش شما [بعد از مرگ] سخت‌تر است یا آسمانی که [الله] آن را بنا کرد؟');
INSERT INTO "islamhouse" VALUES(79,28,'79:28','سقف آن را بر افراشت و به آن شکل و نظم داد،');
INSERT INTO "islamhouse" VALUES(79,29,'79:29','و شبش را تاریک و روزش را روشن گردانید،');
INSERT INTO "islamhouse" VALUES(79,30,'79:30','و زمین را بعد از آن گسترانید،');
INSERT INTO "islamhouse" VALUES(79,31,'79:31','و از آن، آب [چشمه‌ها و چاه‌ها و] چراگاهش را بیرون آورد،');
INSERT INTO "islamhouse" VALUES(79,32,'79:32','و کوه‌ها را محکم و استوار [بر روی زمین] قرار داد.');
INSERT INTO "islamhouse" VALUES(79,33,'79:33','[همۀ اینها] برای بهره‌گیری شما و چهارپایانتان است.');
INSERT INTO "islamhouse" VALUES(79,34,'79:34','هنگامی‌ که [آن] حادثۀ بزرگ [قیامت] فرا رسد،');
INSERT INTO "islamhouse" VALUES(79,35,'79:35','در آن روز، انسان [تمام اعمال و] تلاش‌های خود را به یاد می‌آورد.');
INSERT INTO "islamhouse" VALUES(79,36,'79:36','و جهنم برای هر بیننده‌ای آشکار می‌شود.');
INSERT INTO "islamhouse" VALUES(79,37,'79:37','اما آن کس که طغیان [و سرکشی] کرده باشد');
INSERT INTO "islamhouse" VALUES(79,38,'79:38','و زندگی دنیا را [بر آخرت] ترجیح داده باشد،');
INSERT INTO "islamhouse" VALUES(79,39,'79:39','بی‌تردید، جهنم جایگاه اوست.');
INSERT INTO "islamhouse" VALUES(79,40,'79:40','و اما کسی‌ که از ایستادن در حضور پروردگارش بیمناک بوده و نفس را از هوی [و هوس] بازداشته باشد،');
INSERT INTO "islamhouse" VALUES(79,41,'79:41','قطعاً بهشت جایگاه اوست.');
INSERT INTO "islamhouse" VALUES(79,42,'79:42','[ای پیامبر،] دربارۀ قیامت از تو می‌پرسند که در چه زمانی واقع می‌شود؟');
INSERT INTO "islamhouse" VALUES(79,43,'79:43','تو را با یادآوری این سخن چه کار است؟');
INSERT INTO "islamhouse" VALUES(79,44,'79:44','[سرانجام و] منتهای [علمِ] آن، نزد پروردگار توست.');
INSERT INTO "islamhouse" VALUES(79,45,'79:45','تو فقط بیم‌دهندۀ کسانی هستی که از آن [= قیامت] می‌ترسند.');
INSERT INTO "islamhouse" VALUES(79,46,'79:46','روزی‌ که [کافران قیامت را] ببینند، [چنین احساس می‌کنند که] گویی [در دنیا] جز یک شامگاه یا بامداد به سر نبرده‌اند.');
INSERT INTO "islamhouse" VALUES(80,1,'80:1','[پیامبر] چهره در هم کشید و روی برگردانْد');
INSERT INTO "islamhouse" VALUES(80,2,'80:2','از اینکه آن نابینا [= عبدالله بن اُمّ مَکتوم] نزدش آمد.');
INSERT INTO "islamhouse" VALUES(80,3,'80:3','و [ای پیامبر،] تو چه می‌دانی؟ چه بسا او [از گناهانش] پاک ‌شود،');
INSERT INTO "islamhouse" VALUES(80,4,'80:4','یا پند گیرد و این پند به سودش باشد.');
INSERT INTO "islamhouse" VALUES(80,5,'80:5','اما آن کس که [از ایمان] بی‌نیازی می‌ورزد');
INSERT INTO "islamhouse" VALUES(80,6,'80:6','تو به او روی می‌آوری؛');
INSERT INTO "islamhouse" VALUES(80,7,'80:7','در حالی که اگر او خود را [از کفر] پاک نسازد، ایرادی بر تو نیست؛');
INSERT INTO "islamhouse" VALUES(80,8,'80:8','اما کسی که شتابان به سراغ تو می‌آید');
INSERT INTO "islamhouse" VALUES(80,9,'80:9','و [از الله] می‌ترسد،');
INSERT INTO "islamhouse" VALUES(80,10,'80:10','تو از او غافل می‌شوی [و به دیگران می‌پردازی].');
INSERT INTO "islamhouse" VALUES(80,11,'80:11','هرگز چنین نیست؛ بی‌گمان، این [آیات برای] تذکر و یادآوری است.');
INSERT INTO "islamhouse" VALUES(80,12,'80:12','پس هر کس که بخواهد، [می‌تواند] از آن پند گیرد.');
INSERT INTO "islamhouse" VALUES(80,13,'80:13','در صحیفه‌های ارجمندی [ثبت] است.');
INSERT INTO "islamhouse" VALUES(80,14,'80:14','[در جایگاهی] بلندپایه و پاکیزه [از پلیدی]');
INSERT INTO "islamhouse" VALUES(80,15,'80:15','در دست سفیران [وحی] است');
INSERT INTO "islamhouse" VALUES(80,16,'80:16','[که] بزرگوار و نیکوکارند.');
INSERT INTO "islamhouse" VALUES(80,17,'80:17','مرگ بر انسان [کافر، که] چقدر ناسپاس است!');
INSERT INTO "islamhouse" VALUES(80,18,'80:18','[الله] او را از چه چیز آفریده است؟');
INSERT INTO "islamhouse" VALUES(80,19,'80:19','او را از نطفۀ [ناچیزی] آفرید، سپس او را موزون ساخت.');
INSERT INTO "islamhouse" VALUES(80,20,'80:20','سپس راه را برایش آسان نمود.');
INSERT INTO "islamhouse" VALUES(80,21,'80:21','آنگاه [پس از پایان عمر،] او را میرانْد و در قبر [پنهان] نمود.');
INSERT INTO "islamhouse" VALUES(80,22,'80:22','سپس هر گاه بخواهد، او را [زنده می‌کند و] برمی‌انگیزد.');
INSERT INTO "islamhouse" VALUES(80,23,'80:23','هرگز چنین نیست [که او می‌پندارد]. او هنوز آنچه را که [الله] فرمان داده به جای نیاورده است.');
INSERT INTO "islamhouse" VALUES(80,24,'80:24','انسان باید به غذای خویش [و آفرینشِ آن] بنگرد:');
INSERT INTO "islamhouse" VALUES(80,25,'80:25','ما آب فراوان [از آسمان] فروریختیم،');
INSERT INTO "islamhouse" VALUES(80,26,'80:26','سپس زمین را از هم شکافتیم،');
INSERT INTO "islamhouse" VALUES(80,27,'80:27','آنگاه دانه‌[های فراون] در آن رویاندیم،');
INSERT INTO "islamhouse" VALUES(80,28,'80:28','و انگور و سبزی [خوراکی بسیار]،');
INSERT INTO "islamhouse" VALUES(80,29,'80:29','و زیتون و نخل،');
INSERT INTO "islamhouse" VALUES(80,30,'80:30','و باغ‌هایی [انبوه و] پردرخت،');
INSERT INTO "islamhouse" VALUES(80,31,'80:31','و [انواع] میوه و علوفه [پدید آوردیم].');
INSERT INTO "islamhouse" VALUES(80,32,'80:32','[همۀ اینها] برای بهره‌گیری شما و چهارپایانتان است.');
INSERT INTO "islamhouse" VALUES(80,33,'80:33','هنگامی ‌که [آن] صدای مهیب [قیامت] فرارسد،');
INSERT INTO "islamhouse" VALUES(80,34,'80:34','روزی ‌که انسان از برادرش می‌گریزد');
INSERT INTO "islamhouse" VALUES(80,35,'80:35','و از مادرش و از پدرش');
INSERT INTO "islamhouse" VALUES(80,36,'80:36','و از زنش و پسرانش [نیز می‌گریزد]،');
INSERT INTO "islamhouse" VALUES(80,37,'80:37','در آن روز، هر کس را کاری است که او را به خودش سرگرم می‌دارد [و از دیگری بازمی‌دارد].');
INSERT INTO "islamhouse" VALUES(80,38,'80:38','چهره‌هایی در آن روز، گشاده و روشن است.');
INSERT INTO "islamhouse" VALUES(80,39,'80:39','[به خاطر نعمت و رحمتِ الله] خندان [و] شاد است.');
INSERT INTO "islamhouse" VALUES(80,40,'80:40','و چهره‌هایی در آن روز، غبارآلود است.');
INSERT INTO "islamhouse" VALUES(80,41,'80:41','[و] سیاهی [و تاریکی] آنها را پوشانده است.');
INSERT INTO "islamhouse" VALUES(80,42,'80:42','اینان همان کافرانِ بدکردارند.');
INSERT INTO "islamhouse" VALUES(81,1,'81:1','آنگاه ‌که خورشید درهم پیچیده [و تاریک] گردد،');
INSERT INTO "islamhouse" VALUES(81,2,'81:2','و آنگاه ‌که ستارگان بی‌فروغ شوند،');
INSERT INTO "islamhouse" VALUES(81,3,'81:3','و آنگاه ‌که کوه‌ها به حرکت درآید،');
INSERT INTO "islamhouse" VALUES(81,4,'81:4','و آنگاه ‌که ماده‌شترانِ باردار رها شوند،');
INSERT INTO "islamhouse" VALUES(81,5,'81:5','و آنگاه ‌که حیوانات وحشی [در کنار انسان] گرد آورده شوند،');
INSERT INTO "islamhouse" VALUES(81,6,'81:6','و آنگاه ‌که دریا‌ها [جوشان و] برافروخته شود،');
INSERT INTO "islamhouse" VALUES(81,7,'81:7','و آنگاه ‌که هر کس با همسانِ خود قرین گردد،');
INSERT INTO "islamhouse" VALUES(81,8,'81:8','و آنگاه ‌که از دختر زنده‌به‌گورشده پرسیده شود');
INSERT INTO "islamhouse" VALUES(81,9,'81:9','[که] به کدامین گناه کشته شده است،');
INSERT INTO "islamhouse" VALUES(81,10,'81:10','و آنگاه ‌که نامه‌های [اعمال] گشوده شود،');
INSERT INTO "islamhouse" VALUES(81,11,'81:11','و آنگاه ‌که پرده از روی آسمان برگرفته شود،');
INSERT INTO "islamhouse" VALUES(81,12,'81:12','و آنگاه ‌که دوزخ، افروخته [و شعله‌ور] گردد،');
INSERT INTO "islamhouse" VALUES(81,13,'81:13','و آنگاه ‌که بهشت [برای پرواپیشگان] نزدیک آورده شود.');
INSERT INTO "islamhouse" VALUES(81,14,'81:14','[در آن هنگام،] هر کس خواهد دانست که چه چیزی [برای آخرت خود] آماده کرده است.');
INSERT INTO "islamhouse" VALUES(81,15,'81:15','سوگند به ستارگانی که [به جایگاه خود] بازمی‌گردند،');
INSERT INTO "islamhouse" VALUES(81,16,'81:16','[شتابان] حرکت می‌کنند [و از نظر] پنهان می‌شوند؛');
INSERT INTO "islamhouse" VALUES(81,17,'81:17','و سوگند به شب، هنگامی ‌که [از تاریکی‌اش کاسته شود و] پشت کند؛');
INSERT INTO "islamhouse" VALUES(81,18,'81:18','و سوگند به صبح، هنگامی که بدمد؛');
INSERT INTO "islamhouse" VALUES(81,19,'81:19','بی‌تردید، این [قرآن، سخن الله است که ابلاغش به پیامبر، بر عهدۀ] فرستاده‌ای بزرگوار [=جبرئیل] است؛');
INSERT INTO "islamhouse" VALUES(81,20,'81:20','[همان فرشته‌ای] که [مقتدر و] نیرومند است و نزد [اللهِ] صاحب‌عرش، مقام والایی دارد؛');
INSERT INTO "islamhouse" VALUES(81,21,'81:21','[در ملکوت اعلی و عالم فرشتگان] از او فرمان می‌برند و امین [وحی] است.');
INSERT INTO "islamhouse" VALUES(81,22,'81:22','[ای مردم،] همنشین شما [= محمد] دیوانه نیست؛');
INSERT INTO "islamhouse" VALUES(81,23,'81:23','و بی‌تردید، [محمد]، او [= جبرئیل] را در افق روشن دیده است.');
INSERT INTO "islamhouse" VALUES(81,24,'81:24','و او بر [ابلاغِ آنچه از طریق] غیب [به وی وحی می‌گردد] بخیل نیست [و همانند کاهنان نیز مزد نمی‌گیرد].');
INSERT INTO "islamhouse" VALUES(81,25,'81:25','و این [قرآن] گفتۀ شیطان رانده‌شده نیست.');
INSERT INTO "islamhouse" VALUES(81,26,'81:26','پس به کجا می‌روید؟');
INSERT INTO "islamhouse" VALUES(81,27,'81:27','این [قرآن] چیزی جز پند و اندرز برای جهانیان نیست.');
INSERT INTO "islamhouse" VALUES(81,28,'81:28','برای هر یک از شما که بخواهد راه راست [حق را] در پیش گیرد.');
INSERT INTO "islamhouse" VALUES(81,29,'81:29','و شما [چیزی را] نمی‌خواهید، مگر آنکه پروردگار جهانیان [اراده کند و] بخواهد.');
INSERT INTO "islamhouse" VALUES(82,1,'82:1','آنگاه ‌که آسمان شکافته شود،');
INSERT INTO "islamhouse" VALUES(82,2,'82:2','آنگاه ‌که ستارگان پراکنده شوند [و فرو ریزند]،');
INSERT INTO "islamhouse" VALUES(82,3,'82:3','و آنگاه ‌که دریا‌ها [به هم آمیزند و لبریز و] روان گردند،');
INSERT INTO "islamhouse" VALUES(82,4,'82:4','و آنگاه ‌که قبرها [برای براگیختن مردگان] زیر و رو شود،');
INSERT INTO "islamhouse" VALUES(82,5,'82:5','[در آن هنگام،] هر کس می‌داند چه چیزهایی را پیشاپیش فرستاده و چه چیزهایی را واپس نهاده [و بر جای گذاشته است].');
INSERT INTO "islamhouse" VALUES(82,6,'82:6','ای انسان، چه چیزی تو را [نسبت] به پروردگار بزرگوارت مغرور ساخته است؟');
INSERT INTO "islamhouse" VALUES(82,7,'82:7','آن [پروردگاری] که تو را آفرید، سپس به تو سر و سامان داد و آنگاه تو را متعادل و متناسب کرد.');
INSERT INTO "islamhouse" VALUES(82,8,'82:8','و به هر شکلی [و صورتی] که خواست، تو را ترکیب نمود.');
INSERT INTO "islamhouse" VALUES(82,9,'82:9','هرگز چنین نیست [که شما گمان می‌کنید]؛ بلکه شما [روز] جزا را تکذیب می‌کنید.');
INSERT INTO "islamhouse" VALUES(82,10,'82:10','و بی‌گمان، نگهبانانی [از فرشتگان] بر شما گمارده شده‌اند.');
INSERT INTO "islamhouse" VALUES(82,11,'82:11','که نویسندگانی بزرگوارند؛');
INSERT INTO "islamhouse" VALUES(82,12,'82:12','آنچه را انجام می‌دهید، می‌دانند [و اعمال نیک و بد شما را ثبت می‌کنند].');
INSERT INTO "islamhouse" VALUES(82,13,'82:13','مسلماً نیکوکاران در نعمت[های بهشت] هستند.');
INSERT INTO "islamhouse" VALUES(82,14,'82:14','و یقیناً بدکاران در [آتش] جهنمند.');
INSERT INTO "islamhouse" VALUES(82,15,'82:15','روز جزا به آن درمی‌آیند [و می‌سوزند]');
INSERT INTO "islamhouse" VALUES(82,16,'82:16','و هرگز آنان از آنجا به دور و [در امان] نباشند.');
INSERT INTO "islamhouse" VALUES(82,17,'82:17','و تو چه ‌دانی که روز جزا چیست؟');
INSERT INTO "islamhouse" VALUES(82,18,'82:18','باز تو چه می‌دانی که روز جزا چیست؟');
INSERT INTO "islamhouse" VALUES(82,19,'82:19','روزی‌که هیچ کس قادر به انجام کاری برای دیگری نیست؛ و در آن روز، حکم [و فرمان،] از آنِ الله است.');
INSERT INTO "islamhouse" VALUES(83,1,'83:1','وای بر کم‌فروشان!');
INSERT INTO "islamhouse" VALUES(83,2,'83:2','[همان] کسانی‌ که چون [برای خود] از مردم پیمانه می‌کنند، حق خود را کامل می‌گیرند،');
INSERT INTO "islamhouse" VALUES(83,3,'83:3','و هنگامی ‌که [می‌خواهند] برای آنان پیمانه یا وزن کنند، کم می‌گذارند.');
INSERT INTO "islamhouse" VALUES(83,4,'83:4','آیا آنها گمان نمی‌کنند که [قیامت فرا می‌رسد و از قبرها] برانگیخته می‌شوند،');
INSERT INTO "islamhouse" VALUES(83,5,'83:5','در روزی بزرگ؟');
INSERT INTO "islamhouse" VALUES(83,6,'83:6','[همان] روزی‌ که مردم در پیشگاه پروردگار جهانیان می‌ایستند.');
INSERT INTO "islamhouse" VALUES(83,7,'83:7','هرگز چنین نیست [که کافران پنداشته‌اند]! به راستی که نامۀ [اعمال] بدکاران در سِجّین است.');
INSERT INTO "islamhouse" VALUES(83,8,'83:8','و تو چه دانی که سِجّین چیست؟');
INSERT INTO "islamhouse" VALUES(83,9,'83:9','کتابی است که [اعمال بدکاران در آن] نوشته شده است.');
INSERT INTO "islamhouse" VALUES(83,10,'83:10','در آن روز، وای بر تکذیب‌کنندگان!');
INSERT INTO "islamhouse" VALUES(83,11,'83:11','همان کسانی که روز جزا را [انکار و] تکذیب می‌کنند؛');
INSERT INTO "islamhouse" VALUES(83,12,'83:12','و جز ستمکارانِ گنهکار، [کسی] آن [روز] را تکذیب نمی‌کند.');
INSERT INTO "islamhouse" VALUES(83,13,'83:13','هنگامی که آیات ما بر او تلاوت می‌شود، می‌گوید: «[این] افسانه‌های [خیالی] گذشتگان است».');
INSERT INTO "islamhouse" VALUES(83,14,'83:14','هرگز چنین نیست [که آنها گمان می‌کنند]؛ بلکه [به سبب] آنچه کرده‌اند، بر دل‌هایشان زنگار بسته است.');
INSERT INTO "islamhouse" VALUES(83,15,'83:15','هرگز چنین نیست [که آنها می‌پندارند]. بی‌گمان، آنان در آن روز از [دیدار] پروردگار‌شان محرومند.');
INSERT INTO "islamhouse" VALUES(83,16,'83:16','و [بعد از حساب،] مسلماً وارد دوزخ می‌شوند.');
INSERT INTO "islamhouse" VALUES(83,17,'83:17','آنگاه به آنان گفته می‌شود: «این [آتش،] همان چیزی است که آن را تکذیب می‌کردید».');
INSERT INTO "islamhouse" VALUES(83,18,'83:18','هرگز چنین نیست [که آنها می‌پندارند]؛ بی‌گمان، نامۀ [اعمال] نیکوکاران در «عِلّیین» است.');
INSERT INTO "islamhouse" VALUES(83,19,'83:19','و تو چه دانی که «عِلّیین» چیست؟');
INSERT INTO "islamhouse" VALUES(83,20,'83:20','کتابی است که [اعمال نیکوکاران در آن] نوشته شده است،');
INSERT INTO "islamhouse" VALUES(83,21,'83:21','که مقرّبان [درگاه الهی] بر آن حاضر می‌شوند [و گواهی می‌دهند].');
INSERT INTO "islamhouse" VALUES(83,22,'83:22','همانا نیکوکاران در نعمت[های بهشت] هستند.');
INSERT INTO "islamhouse" VALUES(83,23,'83:23','بر تخت‌ها [تکیه زده و] می‌نگرند.');
INSERT INTO "islamhouse" VALUES(83,24,'83:24','خرمی و نشاطِ نعمت [بهشت] را در چهره‌هایشان [می بینی و] می‌شناسی.');
INSERT INTO "islamhouse" VALUES(83,25,'83:25','آنها از شراب نابِ مُهرشده، [نوشانیده و] سیراب می‌شوند.');
INSERT INTO "islamhouse" VALUES(83,26,'83:26','مُهری که بر آن نهاده شده، از مُشک است، و مشتاقان [رحمت]، در این [شراب و دیگر نعمت‌های بهشتی] باید بر یکدیگر پیشی گیرند.');
INSERT INTO "islamhouse" VALUES(83,27,'83:27','و آمیزه‌اش از [چشمه] تسنیم است؛');
INSERT INTO "islamhouse" VALUES(83,28,'83:28','[همان] چشمه‌ای که مقربان [درگاه الهی] از آن می‌نوشند.');
INSERT INTO "islamhouse" VALUES(83,29,'83:29','همانا کسانی که جرم و گناه مرتکب شدند، پیوسته [در دنیا] بر کسانی‌ که ایمان آورده بودند می‌خندیدند.');
INSERT INTO "islamhouse" VALUES(83,30,'83:30','و هر گاه [مؤمنان] از کنار‌شان می‌گذشتند، با چشم و ابرو به هم اشاره می‌کردند [و آنان را به سُخره می‌گرفتند]؛');
INSERT INTO "islamhouse" VALUES(83,31,'83:31','و چون به سوی خانوادۀ خود بازمی‌گشتند، [به خاطر تمسخرِ مؤمنان] شادمان و خندان بودند؛');
INSERT INTO "islamhouse" VALUES(83,32,'83:32','و هنگامی که مؤمنان را می‌دیدند می‌گفتند: «بی‌گمان، اینان گمراهانند»؛');
INSERT INTO "islamhouse" VALUES(83,33,'83:33','در حالی ‌که آنان برای مراقبت [و نگهبانی،] بر مؤمنان فرستاده نشده بودند.');
INSERT INTO "islamhouse" VALUES(83,34,'83:34','ولی امروز کسانی ‌که ایمان آورده‌اند، به کافران می‌خندند.');
INSERT INTO "islamhouse" VALUES(83,35,'83:35','بر تخت‌های [آراسته نشسته و به نعمت‌های الهی که برترین آنها، رؤیت وجه گرامی الله است] می‌نگرند.');
INSERT INTO "islamhouse" VALUES(83,36,'83:36','آیا کافران [با چشیدن عذاب،] جزای آنچه را که می‌کردند دریافت نموده‌اند؟');
INSERT INTO "islamhouse" VALUES(84,1,'84:1','آنگاه ‌که آسمان شکافته شود،');
INSERT INTO "islamhouse" VALUES(84,2,'84:2','و به [فرمان] پروردگارش گوش فرا دهد [و تسلیم شود]؛ و سزاوار است [که چنین کند]،');
INSERT INTO "islamhouse" VALUES(84,3,'84:3','و آنگاه ‌که زمین گسترده [و هموار] شود،');
INSERT INTO "islamhouse" VALUES(84,4,'84:4','و هر چه را درون خود دارد، بیرون ریزد و تهی گردد،');
INSERT INTO "islamhouse" VALUES(84,5,'84:5','و به [فرمان] پروردگارش گوش دهد [و تسلیم شود]؛ و سزاوار است [که چنین کند].');
INSERT INTO "islamhouse" VALUES(84,6,'84:6','ای انسان، بی‌گمان، تو در راه [رسیدن به] پروردگارت سخت تلاش و کوشش می‌کنی؛ پس او را ملاقات خواهی کرد.');
INSERT INTO "islamhouse" VALUES(84,7,'84:7','و اما کسی ‌که نامۀ [اعمال او] به دست راستش داده شود،');
INSERT INTO "islamhouse" VALUES(84,8,'84:8','به زودی به حسابی آسان، محاسبه می‌شود.');
INSERT INTO "islamhouse" VALUES(84,9,'84:9','و شادمان به سوی خانواده‌اش بازمی‌گردد؛');
INSERT INTO "islamhouse" VALUES(84,10,'84:10','و اما کسی ‌که نامۀ [اعمال او] از پشت سرش به او داده شود.');
INSERT INTO "islamhouse" VALUES(84,11,'84:11','به زودی نابودیِ خود را خواهد ‌طلبید');
INSERT INTO "islamhouse" VALUES(84,12,'84:12','و به [آتش] جهنم شعله‌ور در خواهد آمد.');
INSERT INTO "islamhouse" VALUES(84,13,'84:13','بی‌گمان، او در [دنیا به خاطر گناهانش،] در میان خانوادۀ خود شادمان بود.');
INSERT INTO "islamhouse" VALUES(84,14,'84:14','او گمان می‌کرد که هرگز [به نزد الله] بازنخواهد گشت.');
INSERT INTO "islamhouse" VALUES(84,15,'84:15','آری؛ همانا پروردگارش از حال وی آگاه بود.');
INSERT INTO "islamhouse" VALUES(84,16,'84:16','به شفق سوگند می‌خورم،');
INSERT INTO "islamhouse" VALUES(84,17,'84:17','و سوگند به شب و آنچه را فرو می‌پوشد');
INSERT INTO "islamhouse" VALUES(84,18,'84:18','و سوگند به ماه چون [فروغش] کامل شود');
INSERT INTO "islamhouse" VALUES(84,19,'84:19','که قطعاً مراتب و حالات مختلفی را یکی پس از دیگری [در مسیر زندگی دنیوی] طی خواهید کرد.');
INSERT INTO "islamhouse" VALUES(84,20,'84:20','آنان را چه شده که ایمان نمی‌آورند؟');
INSERT INTO "islamhouse" VALUES(84,21,'84:21','و [چرا] هنگامی‌ که قرآن بر آنان تلاوت می‌شود سجده نمی‌کنند؟');
INSERT INTO "islamhouse" VALUES(84,22,'84:22','بلکه کسانی‌ که کافر شدند، پیوسته [آیات الهی را] تکذیب می‌کنند.');
INSERT INTO "islamhouse" VALUES(84,23,'84:23','و الله به آنچه در دل پنهان می‌دارند، دانا‌تر است.');
INSERT INTO "islamhouse" VALUES(84,24,'84:24','پس [ای پیامبر،] آنان را به عذابی دردناک بشارت ده؛');
INSERT INTO "islamhouse" VALUES(84,25,'84:25','مگر کسانی که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، که پاداشی بی‌منّت و پیوسته برایشان است.');
INSERT INTO "islamhouse" VALUES(85,1,'85:1','سوگند به آسمان که دارندۀ بُرج‌هاست،');
INSERT INTO "islamhouse" VALUES(85,2,'85:2','و سوگند به روز موعود [قیامت]،');
INSERT INTO "islamhouse" VALUES(85,3,'85:3','و سوگند به هر کس که گواهی دهد و به هر کس که مورد گواهی قرار گیرد.');
INSERT INTO "islamhouse" VALUES(85,4,'85:4','مرگ بر آدم‌سوزان خندق!');
INSERT INTO "islamhouse" VALUES(85,5,'85:5','[همان آتشِ] افروخته از هیزم‌های بسیار [که مؤمنان را در آن می‌سوزاندند]؛');
INSERT INTO "islamhouse" VALUES(85,6,'85:6','هنگامی ‌که بر [کنارۀ] آن نشسته بودند');
INSERT INTO "islamhouse" VALUES(85,7,'85:7','و آنچه را با مؤمنان انجام می‌دادند، تماشا می‌کردند؛');
INSERT INTO "islamhouse" VALUES(85,8,'85:8','و هیچ ایرادی از آنان [=مؤمنان] نگرفتند، جز اینکه به الله پیروزمندِ ستوده ایمان آورده بودند.');
INSERT INTO "islamhouse" VALUES(85,9,'85:9','[همان] ذاتی که فرمانروایی آسمان‌ها و زمین از آنِ اوست؛ و الله بر همه چیز گواه است.');
INSERT INTO "islamhouse" VALUES(85,10,'85:10','به راستی کسانی که مردان و زنان مؤمن را شکنجه [و آزار] دادند و پس [از انجام آن کار،] توبه نکردند، عذاب جهنم برایشان [مهیّا] است و عذاب آتش [در پیش دارند].');
INSERT INTO "islamhouse" VALUES(85,11,'85:11','همانا برای کسانی ‌که ایمان آوردند و کار‌های شایسته کردند، باغ‌هایی [از بهشت] است که جویبارها زیر [درختان] آن جاری است؛ و این است کامیابی بزرگ.');
INSERT INTO "islamhouse" VALUES(85,12,'85:12','بی‌گمان، [مجازات و] سختگیریِ پروردگارت بسیار شدید است.');
INSERT INTO "islamhouse" VALUES(85,13,'85:13','در حقیقت، اوست که [آفرینش را] آغاز می‌کند و دوباره [بعد از مرگ] بازمی‌گرداند؛');
INSERT INTO "islamhouse" VALUES(85,14,'85:14','و همو آمرزنده [و] دوستدار [مؤمنان] است.');
INSERT INTO "islamhouse" VALUES(85,15,'85:15','[الله] صاحب‌عرش [و] بلندمرتبه [و شکوهمند] است');
INSERT INTO "islamhouse" VALUES(85,16,'85:16','[و] آنچه را بخواهد، انجام می‌دهد.');
INSERT INTO "islamhouse" VALUES(85,17,'85:17','[ای پیامبر،] آیا داستان لشکر‌یان [حق‌ستیز] به تو رسیده است؟');
INSERT INTO "islamhouse" VALUES(85,18,'85:18','[لشکر] فرعون و [قوم] ثمود.');
INSERT INTO "islamhouse" VALUES(85,19,'85:19','بلکه کسانی ‌که کافر شدند، پیوسته در تکذیب [حق] هستند،');
INSERT INTO "islamhouse" VALUES(85,20,'85:20','و الله از هر سو بر آنان احاطه دارد.');
INSERT INTO "islamhouse" VALUES(85,21,'85:21','آری، این [آیات، شعر و سحر نیست؛ بلکه] قرآن شکوهمند است.');
INSERT INTO "islamhouse" VALUES(85,22,'85:22','در لوح محفوظ [از هر گونه تحریف و تبدیل، در امان] است.');
INSERT INTO "islamhouse" VALUES(86,1,'86:1','سوگند به آسمان و سوگند به ستارۀ شبگرد.');
INSERT INTO "islamhouse" VALUES(86,2,'86:2','و تو چه می‌دانی که ستارۀ شبگرد چیست؟');
INSERT INTO "islamhouse" VALUES(86,3,'86:3','[همان] ستارۀ فروزان است.');
INSERT INTO "islamhouse" VALUES(86,4,'86:4','هیچ کس نیست، مگر اینکه نگهبانی [از فرشتگان] بر او[گماشته شده] است.');
INSERT INTO "islamhouse" VALUES(86,5,'86:5','انسان باید بنگرد که از چه چیز آفریده شده است:');
INSERT INTO "islamhouse" VALUES(86,6,'86:6','از یک آب جهنده آفریده شده');
INSERT INTO "islamhouse" VALUES(86,7,'86:7','که از میان [استخوانِ] پشت [مرد] و سینۀ [زن] بیرون می‌آید.');
INSERT INTO "islamhouse" VALUES(86,8,'86:8','بی‌گمان، الله بر بازگرداندن او [پس از مرگ] تواناست.');
INSERT INTO "islamhouse" VALUES(86,9,'86:9','روزی ‌که [تمام] رازها فاش شود،');
INSERT INTO "islamhouse" VALUES(86,10,'86:10','[در آن روز،] انسان هیچ توان و یاوری ندارد.');
INSERT INTO "islamhouse" VALUES(86,11,'86:11','سوگند به آسمان پرباران');
INSERT INTO "islamhouse" VALUES(86,12,'86:12','و سوگند به زمینِ پرشکاف [که گیاهان از آن سر برمی‌آورند].');
INSERT INTO "islamhouse" VALUES(86,13,'86:13','[که] بی‌گمان، این قرآن، سخن جداکنندۀ حق از باطل است');
INSERT INTO "islamhouse" VALUES(86,14,'86:14','و [سخنِ] هزل و بیهوده نیست.');
INSERT INTO "islamhouse" VALUES(86,15,'86:15','آنان [= کافران] پیوسته حیله و نیرنگ می‌کنند');
INSERT INTO "islamhouse" VALUES(86,16,'86:16','و من [نیز] حیله [و تدبیر] می‌کنم؛');
INSERT INTO "islamhouse" VALUES(86,17,'86:17','پس به کافران مهلت بده [و] اندکی رهایشان کن [تا غرق گناه باشند].');
INSERT INTO "islamhouse" VALUES(87,1,'87:1','نام پروردگار بلند مرتبه‌ات را به پاکی یاد کن.');
INSERT INTO "islamhouse" VALUES(87,2,'87:2','[همان ذاتی] که [انسان را] آفرید و هماهنگ و متعادل ساخت.');
INSERT INTO "islamhouse" VALUES(87,3,'87:3','و [همان] ذاتی‌ که اندازه‌گیری کرد و [به شایستگی آفرید] و سپس هدایت نمود.');
INSERT INTO "islamhouse" VALUES(87,4,'87:4','و [آن] ذاتی‌ که چراگاه را [برای تغذیۀ جانداران] رویانید.');
INSERT INTO "islamhouse" VALUES(87,5,'87:5','سپس آن را خشک و سیاه گردانید.');
INSERT INTO "islamhouse" VALUES(87,6,'87:6','ما به زودی [قرآن را] بر تو می‌خوانیم؛ پس تو هرگز [آن را] فراموش نخواهی کرد؛');
INSERT INTO "islamhouse" VALUES(87,7,'87:7','مگر آنچه را که الله بخواهد؛ همانا او آشکار و نهان را می‌داند.');
INSERT INTO "islamhouse" VALUES(87,8,'87:8','و آسان‌ترین [راه‌] را برایت فراهم مى‌گردانیم.');
INSERT INTO "islamhouse" VALUES(87,9,'87:9','اگر پند مفید باشد، [مردم را با آیات قرآن] پند ده.');
INSERT INTO "islamhouse" VALUES(87,10,'87:10','به زودی کسی‌ که [از الله] می‌ترسد، پند می‌پذیرد.');
INSERT INTO "islamhouse" VALUES(87,11,'87:11','و بدبخت‌ترین [مردم،] از آن دوری می‌گزیند؛');
INSERT INTO "islamhouse" VALUES(87,12,'87:12','[همان] کسی‌ که در آتش بزرگ [جهنم] درخواهد آمد،');
INSERT INTO "islamhouse" VALUES(87,13,'87:13','و در آن [آتش] نه می‌میرد و نه زنده می‌مانَد.');
INSERT INTO "islamhouse" VALUES(87,14,'87:14','یقیناً کسی ‌که خود را [از کفرو گناه] پاک کند، رستگار خواهد شد؛');
INSERT INTO "islamhouse" VALUES(87,15,'87:15','و [نیز کسی که] نام پروردگارش را یاد کند و نماز بگزارد.');
INSERT INTO "islamhouse" VALUES(87,16,'87:16','ولی شما [مردم،] زندگی دنیا را [بر آخرت] ترجیح می‌دهید؛');
INSERT INTO "islamhouse" VALUES(87,17,'87:17','در حالی‌ که آخرت، بهتر و پاینده‌تر است.');
INSERT INTO "islamhouse" VALUES(87,18,'87:18','به راستی این [پندها] در کتاب‌های آسمانی پیشین [نیز] بود؛');
INSERT INTO "islamhouse" VALUES(87,19,'87:19','کتاب‌های ابراهیم و موسی.');
INSERT INTO "islamhouse" VALUES(88,1,'88:1','[ای پیامبر،] آیا خبرِ قیامت هولناکِ فراگیر به تو رسیده است؟');
INSERT INTO "islamhouse" VALUES(88,2,'88:2','در آن روز، چهره‌هایی خوار و ذلیل خواهند بود.');
INSERT INTO "islamhouse" VALUES(88,3,'88:3','[در دنیا] پیوسته تلاش کرده [و] خسته شده [و چون در راه حق نبوده، نتیجه‌ای ندیده‌اند].');
INSERT INTO "islamhouse" VALUES(88,4,'88:4','در آتش سوزان درمی‌آیند،');
INSERT INTO "islamhouse" VALUES(88,5,'88:5','از چشمۀ بسیار داغ به آنها نوشانیده می‌شود.');
INSERT INTO "islamhouse" VALUES(88,6,'88:6','غذایی جز خارِ خشک و تلخ ندارند');
INSERT INTO "islamhouse" VALUES(88,7,'88:7','که نه [آنان را] فربه کند و نه گرسنگی را دفع می‌نماید.');
INSERT INTO "islamhouse" VALUES(88,8,'88:8','در آن روز، چهره‌هایی شاداب و تازه هستند');
INSERT INTO "islamhouse" VALUES(88,9,'88:9','و از سعی [و تلاش] خود راضیند؛');
INSERT INTO "islamhouse" VALUES(88,10,'88:10','در بهشت برین هستند.');
INSERT INTO "islamhouse" VALUES(88,11,'88:11','در آنجا هیچ سخن بیهوده‌ای نمی‌شنوند.');
INSERT INTO "islamhouse" VALUES(88,12,'88:12','در آن، چشمه[های آب] روان است.');
INSERT INTO "islamhouse" VALUES(88,13,'88:13','تخت‌های [زیبای] بلند [پایه] در آن است.');
INSERT INTO "islamhouse" VALUES(88,14,'88:14','و جام‌هایی [که در کنار چشمه‌ها] نهاده شده.');
INSERT INTO "islamhouse" VALUES(88,15,'88:15','و بالشت‌های [منظم] چیده‌شده،');
INSERT INTO "islamhouse" VALUES(88,16,'88:16','و [نیز] فرش‌های گرانبها گسترده شده است.');
INSERT INTO "islamhouse" VALUES(88,17,'88:17','آیا [کافران] به شتر نمی‌نگرند که چگونه آفریده شده است؟');
INSERT INTO "islamhouse" VALUES(88,18,'88:18','و به آسمان [نمی‌نگرند] که چگونه برافراشته شده است؟');
INSERT INTO "islamhouse" VALUES(88,19,'88:19','و به کوه‌ها [نمی‌نگرند] که چگونه [محکم بر زمین] نصب شده است؟');
INSERT INTO "islamhouse" VALUES(88,20,'88:20','و به زمین [نمی‌نگرند] که چگونه گسترده شده است؟');
INSERT INTO "islamhouse" VALUES(88,21,'88:21','[ای پیامبر،] پند بده، که تو فقط پنددهنده‌ای.');
INSERT INTO "islamhouse" VALUES(88,22,'88:22','تو بر آنان مسلط [و چیره] نیستی [که آنان را بر ایمان‌آوردن وادار کنی].');
INSERT INTO "islamhouse" VALUES(88,23,'88:23','اما کسی‌ که روی بگردانَد و کافر شود');
INSERT INTO "islamhouse" VALUES(88,24,'88:24','الله او را با بزرگ‌ترین عذاب، مجازات می‌کند.');
INSERT INTO "islamhouse" VALUES(88,25,'88:25','همانا بازگشت آنها به سوی ماست.');
INSERT INTO "islamhouse" VALUES(88,26,'88:26','سپس بی‌گمان، حسابشان [نیز] با ماست.');
INSERT INTO "islamhouse" VALUES(89,1,'89:1','سوگند به سپيده‌دم [صبحگاهان]');
INSERT INTO "islamhouse" VALUES(89,2,'89:2','و به شب‌های دهگانه [آغاز ذی‌حجه]');
INSERT INTO "islamhouse" VALUES(89,3,'89:3','و به زوج و فرد [اشیاء]');
INSERT INTO "islamhouse" VALUES(89,4,'89:4','و سوگند به شب، هنگامی‌ که [حرکت می‌کند و] می‌رود.');
INSERT INTO "islamhouse" VALUES(89,5,'89:5','آیا در این [چیزها] سوگندی برای صاحب‌خرد نیست [که او را قانع نماید]؟');
INSERT INTO "islamhouse" VALUES(89,6,'89:6','[ای پیامبر،] آیا ندیدی که پروردگارت با [قوم] عاد چه کرد؟');
INSERT INTO "islamhouse" VALUES(89,7,'89:7','[همان قبیلۀ] اِرَم که دارای [کاخ‌هایی با] ستونهای بلند بودند');
INSERT INTO "islamhouse" VALUES(89,8,'89:8','که همانند آن در شهرها آفریده نشده بود؟');
INSERT INTO "islamhouse" VALUES(89,9,'89:9','و [نیز قوم] ثَمود، کسانی که صخره‌های سخت را از [کنار] وادی می‌تراشیدند [و خانه برای خودشان می‌ساختند]؟');
INSERT INTO "islamhouse" VALUES(89,10,'89:10','و فرعون، صاحب سپاه [بزرگ و قدرتمند].');
INSERT INTO "islamhouse" VALUES(89,11,'89:11','[همان] کسانی که در شهر‌ها طغیان [و سرکشی] کردند');
INSERT INTO "islamhouse" VALUES(89,12,'89:12','و فسادِ بسیار در آنها به بار آوردند.');
INSERT INTO "islamhouse" VALUES(89,13,'89:13','آنگاه پروردگارت تازیانۀ عذاب را بر آنان فرود آورد.');
INSERT INTO "islamhouse" VALUES(89,14,'89:14','یقیناً پروردگار تو در کمینگاه است.');
INSERT INTO "islamhouse" VALUES(89,15,'89:15','و اما انسان هنگامی که پروردگارش او را بیازماید و گرامی دارد و به او نعمت بخشد، [مغرور می‌گردد و گمان می‌کند که این امر به خاطر کرامتی است که نزد الله دارد]، پس می‌گوید: «پروردگارم مرا گرامی داشته است»؛');
INSERT INTO "islamhouse" VALUES(89,16,'89:16','و اما هنگامی که او را بیازماید و روزی‌اش را بر او تنگ سازد، [دلسرد ‌شده و گمان می‌کند که این امر بدان سبب است که نزد الله، خوار و ذلیل است؛] پس می‌گوید: «پروردگارم مرا خوار کرده است».');
INSERT INTO "islamhouse" VALUES(89,17,'89:17','هرگز چنین نیست [که شما می‌پندارید]؛ بلکه شما یتیم را گرامی نمی‌دارید');
INSERT INTO "islamhouse" VALUES(89,18,'89:18','و یکدیگر را بر اطعام مستمندان ترغیب نمی‌کنید');
INSERT INTO "islamhouse" VALUES(89,19,'89:19','و میراث را حریصانه می‌خورید [و حق ضعیفان را نمی‌دهید]');
INSERT INTO "islamhouse" VALUES(89,20,'89:20','و مال و ثروت [دنیا] را بسیار دوست دارید.');
INSERT INTO "islamhouse" VALUES(89,21,'89:21','هرگز چنین نیست [که شما گمان می‌کنید]؛ هنگامی ‌که زمین سخت در هم کوبیده شود [و هموار گردد]');
INSERT INTO "islamhouse" VALUES(89,22,'89:22','و پروردگارت [برای دادرسی] بیاید و [نیز] فرشتگان صف‌درصف [بایستند]');
INSERT INTO "islamhouse" VALUES(89,23,'89:23','و در آن روز جهنم آورده شود، آن روز است که انسان [کافر] پند می‌گیرد [و توبه می‌کند]؛ و [اما] این پند گرفتن، چه سودی برایش دارد؟');
INSERT INTO "islamhouse" VALUES(89,24,'89:24','وی می‌گوید: «ای کاش [در دنیا] برای [این] زندگی‌ام [اعمال نیک، از] پیش فرستاده بودم!');
INSERT INTO "islamhouse" VALUES(89,25,'89:25','در آن روز، هیچ کس همانند عذاب او [= الله] عذاب نمی‌کند،');
INSERT INTO "islamhouse" VALUES(89,26,'89:26','و هیچ کس همچون بند کشیدن او [= الله]، کسی را به بند نمی‌کشد.');
INSERT INTO "islamhouse" VALUES(89,27,'89:27','[اما به مؤمن ندا می‌شود:] «ای روحِ آرام‌یافته،');
INSERT INTO "islamhouse" VALUES(89,28,'89:28','به سوی پروردگارت بازگرد، در حالی که تو [از پاداشِ الله] راضی هستی، و او از [اعمال] تو راضی است.');
INSERT INTO "islamhouse" VALUES(89,29,'89:29','پس در زمرۀ بندگان [خاصّ] من در آی');
INSERT INTO "islamhouse" VALUES(89,30,'89:30','و به بهشت من وارد شو».');
INSERT INTO "islamhouse" VALUES(90,1,'90:1','سوگند به این شهر [مکه].');
INSERT INTO "islamhouse" VALUES(90,2,'90:2','در حالی که [ای پیامبر!] تو در این شهر ساکنی [و جنگ در این شهر برایت حلال است]');
INSERT INTO "islamhouse" VALUES(90,3,'90:3','و سوگند به پدر [= آدم] و فرزندانش.');
INSERT INTO "islamhouse" VALUES(90,4,'90:4','یقیناً ما انسان را در رنج [و دشواری‌های دنیوی] آفریدیم.');
INSERT INTO "islamhouse" VALUES(90,5,'90:5','آیا او گمان می‌کند هیچ کس بر [مجازاتِ] وی توانا نیست؟');
INSERT INTO "islamhouse" VALUES(90,6,'90:6','می‌گوید: «[به خاطرِ انفاق،] مال زیادی را تباه کرده‌ام».');
INSERT INTO "islamhouse" VALUES(90,7,'90:7','آیا می‌پندارد که هیچ کس او را ندیده است؟');
INSERT INTO "islamhouse" VALUES(90,8,'90:8','آیا دو چشم برایش قرار ندادیم؟');
INSERT INTO "islamhouse" VALUES(90,9,'90:9','و یک زبان و دو لب؟');
INSERT INTO "islamhouse" VALUES(90,10,'90:10','و او را به دو راه [خیر و شر] راهنمایی کردیم.');
INSERT INTO "islamhouse" VALUES(90,11,'90:11','اما به گردنۀ [سخت] قدم نگذاشت [و نیامد].');
INSERT INTO "islamhouse" VALUES(90,12,'90:12','و تو چه می‌دانی که آن گردنۀ [سخت] چیست؟');
INSERT INTO "islamhouse" VALUES(90,13,'90:13','آزاد کردن برده‌ای است.');
INSERT INTO "islamhouse" VALUES(90,14,'90:14','یا اطعام در روز قحطی [و گرسنگی]،');
INSERT INTO "islamhouse" VALUES(90,15,'90:15','[خواه] یتیمی از خویشاوندان [باشد]');
INSERT INTO "islamhouse" VALUES(90,16,'90:16','یا مستمندی خاک نشین؛');
INSERT INTO "islamhouse" VALUES(90,17,'90:17','به علاوه، از کسانی باشد که ایمان آورده‌اند و یکدیگر را به صبر [بر عبادت و دوری از گناه] و مهربانی [با بندگان] توصیه نموده‌اند.');
INSERT INTO "islamhouse" VALUES(90,18,'90:18','اینان اهل سعادتند [که نامۀ اعمال آنان به دست راستشان داده می‌شود و اهل بهشتند].');
INSERT INTO "islamhouse" VALUES(90,19,'90:19','اما کسانی ‌که به آیات ما کفر ورزیدند، آنان اهل شقاوتند [که نامۀ اعمال به دست چپشان داده می‌شود و به دوزخ می‌روند].');
INSERT INTO "islamhouse" VALUES(90,20,'90:20','[از هر سو] آتش سرپوشید‌ه‌ای بر آنان است [که راه فرار ندارند].');
INSERT INTO "islamhouse" VALUES(91,1,'91:1','سوگند به خورشید و روشنیِ آن [به هنگام بامداد]،');
INSERT INTO "islamhouse" VALUES(91,2,'91:2','و سوگند به ماه، هنگامی که بعد از آن [= خورشید] در‌آید،');
INSERT INTO "islamhouse" VALUES(91,3,'91:3','و سوگند به روز، هنگامی‌ که آن [= خورشید] را روشن [و جلوه‌گر] کند،');
INSERT INTO "islamhouse" VALUES(91,4,'91:4','و سوگند به شب، هنگامی که آن را بپوشانَد،');
INSERT INTO "islamhouse" VALUES(91,5,'91:5','و سوگند به آسمان و به ذاتی که آن را بنا کرد،');
INSERT INTO "islamhouse" VALUES(91,6,'91:6','و سوگند به زمین و به ذاتی که آن را گسترانید،');
INSERT INTO "islamhouse" VALUES(91,7,'91:7','و سوگند به جانِ [انسان] و ذاتی که آن را [آفرید و] نیکو گردانید،');
INSERT INTO "islamhouse" VALUES(91,8,'91:8','سپس نافرمانی و پرهیزگاری‌اش را [به او] الهام کرد.');
INSERT INTO "islamhouse" VALUES(91,9,'91:9','بی‌تردید، هر کس نفس خود را [از گناهان] پاک کند، رستگار می‌شود.');
INSERT INTO "islamhouse" VALUES(91,10,'91:10','و هر کس آن را [با گناه] آلوده سازد، یقیناً زیانکار می‌شود.');
INSERT INTO "islamhouse" VALUES(91,11,'91:11','[قوم] ثمود از روی سرکشی، [پیامبر‌شان را] تکذیب کردند،');
INSERT INTO "islamhouse" VALUES(91,12,'91:12','آنگاه که بدکار‌ترین آنان [برای اقدام به جنایت] برخاست.');
INSERT INTO "islamhouse" VALUES(91,13,'91:13','پیامبر الله [= صالح] به آنان گفت: «ماده‌شترِ الله و [نوبت‌] آب‌خوردنش را [حرمت نهید]».');
INSERT INTO "islamhouse" VALUES(91,14,'91:14','[ولی آنان] او را تکذیب کردند و آن [ماده‌شتر] را کشتند؛ پس پروردگار آنان به سبب گناهانشان بر سر‌شان عذاب آورد و همگی آنان را با خاک یکسان کرد.');
INSERT INTO "islamhouse" VALUES(91,15,'91:15','و [الله] از سرانجام آن [کار،] بیم ندارد.');
INSERT INTO "islamhouse" VALUES(92,1,'92:1','سوگند به شب، هنگامی ‌که [همه چیز را] بپوشانَد،');
INSERT INTO "islamhouse" VALUES(92,2,'92:2','و سوگند به روز، هنگامی‌ که آشکار شود،');
INSERT INTO "islamhouse" VALUES(92,3,'92:3','و سوگند به ذاتی که نر و ماده را آفرید،');
INSERT INTO "islamhouse" VALUES(92,4,'92:4','که یقیناً سعی [و تلاش] شما مختلف [و نیک و بد] است.');
INSERT INTO "islamhouse" VALUES(92,5,'92:5','و اما کسی‌ که [در راه الله] ببخشد و پرهیزگاری نماید');
INSERT INTO "islamhouse" VALUES(92,6,'92:6','و [وعدۀ الله و سخنان] نیک را تصدیق کند،');
INSERT INTO "islamhouse" VALUES(92,7,'92:7','به زودی انجام [کار خیر و] آسانی [= اعمال نیکو و انفاق در راه الله] را برایش آسان می‌کنیم.');
INSERT INTO "islamhouse" VALUES(92,8,'92:8','و اما کسی‌که بخل ورزد و بی‌نیازی بجوید،');
INSERT INTO "islamhouse" VALUES(92,9,'92:9','و [وعدۀ الله و سخنانِ] نیک را تکذیب کند،');
INSERT INTO "islamhouse" VALUES(92,10,'92:10','به زودی انجام دشواری [= اعمال شر و پلید] را برایش آسان می‌سازیم [و انجام کار خیر را بر او سخت می‌گردانیم]؛');
INSERT INTO "islamhouse" VALUES(92,11,'92:11','و در هنگام [مرگ و] ورود به دوزخ، دارایی او سودی به حالش نخواهد داشت.');
INSERT INTO "islamhouse" VALUES(92,12,'92:12','مسلماً هدایت [بندگان] بر [عهدۀ] ماست.');
INSERT INTO "islamhouse" VALUES(92,13,'92:13','و محققاً آخرت و دنیا [نیز] از آنِ ماست.');
INSERT INTO "islamhouse" VALUES(92,14,'92:14','من شما را از آتشی شعله‌ور بیم دادم');
INSERT INTO "islamhouse" VALUES(92,15,'92:15','که جز بدبخت‌ترین [مردم] در آن نیفتد؛');
INSERT INTO "islamhouse" VALUES(92,16,'92:16','[همان] کسی ‌که [آیات ما را] تکذیب کرد و روی گرداند');
INSERT INTO "islamhouse" VALUES(92,17,'92:17','و به زودی پرهیزگارترین [مردم] از آن دور نگه داشته می‌شود.');
INSERT INTO "islamhouse" VALUES(92,18,'92:18','[همان] کسی‌که مال خود را [در راه الله] می‌بخشد تا [از گناه] پاک شود.');
INSERT INTO "islamhouse" VALUES(92,19,'92:19','و هیچ كس را به قصد پاداش‌یافتن نعمت نمى‌بخشد؛');
INSERT INTO "islamhouse" VALUES(92,20,'92:20','مگر [برای] کسبِ رضا [و خشنودی] پروردگار برترش.');
INSERT INTO "islamhouse" VALUES(92,21,'92:21','و به راستی که [با پاداشِ الله] راضی خواهد شد.');
INSERT INTO "islamhouse" VALUES(93,1,'93:1','سوگند به آغاز روز،');
INSERT INTO "islamhouse" VALUES(93,2,'93:2','و سوگند به شب، هنگامی‌ که آرام گیرد');
INSERT INTO "islamhouse" VALUES(93,3,'93:3','[که] پروردگارت تو را وانگذاشته و [بر تو] خشم نگرفته است.');
INSERT INTO "islamhouse" VALUES(93,4,'93:4','و مسلماً آخرت برای تو از دنیا بهتر است.');
INSERT INTO "islamhouse" VALUES(93,5,'93:5','و به زودی پروردگارت به تو [و امت تو آنقدر] عطا خواهد کرد که راضی گردی.');
INSERT INTO "islamhouse" VALUES(93,6,'93:6','آیا تو را یتیم نیافت، و پناه داد؟');
INSERT INTO "islamhouse" VALUES(93,7,'93:7','و تو را [راه‌نایافته و] سرگشته یافت، و هدایت کرد.');
INSERT INTO "islamhouse" VALUES(93,8,'93:8','و تو را فقیر یافت، و بی‌نیاز نمود.');
INSERT INTO "islamhouse" VALUES(93,9,'93:9','پس [تو نیز] یتیم را میازار،');
INSERT INTO "islamhouse" VALUES(93,10,'93:10','و [مستمندِ] نیازخواه را مران [و با او خشونت مکن]،');
INSERT INTO "islamhouse" VALUES(93,11,'93:11','و نعمت‌های پروردگارت را بازگو کن [و سپاس گزار].');
INSERT INTO "islamhouse" VALUES(94,1,'94:1','[ای پیامبر،] آیا ما سینه‌ات را [برای نزول وحی] برایت نگشودیم؟');
INSERT INTO "islamhouse" VALUES(94,2,'94:2','و سنگینی [بارِ گناه و دشواریِ رسالت] را از [دوشِ] تو برنداشتیم؟');
INSERT INTO "islamhouse" VALUES(94,3,'94:3','همان [باری] که بر پشتت سنگینی می‌کرد.');
INSERT INTO "islamhouse" VALUES(94,4,'94:4','و [نام و] آوازۀ تو را بلند ساختیم.');
INSERT INTO "islamhouse" VALUES(94,5,'94:5','مسلماً با [هر] دشواری، آسانی است.');
INSERT INTO "islamhouse" VALUES(94,6,'94:6','[آری؛] مسلماً با [هر] دشواری، آسانی است.');
INSERT INTO "islamhouse" VALUES(94,7,'94:7','پس هنگامی‌ که [از کار و امور دنیا] فارغ شدی، [به عبادت پروردگارت] بکوش');
INSERT INTO "islamhouse" VALUES(94,8,'94:8','و به سوی پروردگارت راغب [و مشتاق] شو.');
INSERT INTO "islamhouse" VALUES(95,1,'95:1','سوگند به انجیر و زیتون');
INSERT INTO "islamhouse" VALUES(95,2,'95:2','و سوگند به طورِ سینا');
INSERT INTO "islamhouse" VALUES(95,3,'95:3','و سوگند به این شهر امن [مکه]');
INSERT INTO "islamhouse" VALUES(95,4,'95:4','[که] یقیناً ما انسان را در بهترین صورت [و با فطرت پاک] آفریدیم.');
INSERT INTO "islamhouse" VALUES(95,5,'95:5','سپس او را [که انحراف یافت،] به پست‌ترین [مراحلِ] پَستی برگرداندیم.');
INSERT INTO "islamhouse" VALUES(95,6,'95:6','مگر کسانی که ایمان آوردند و کار‌های شایسته انجام دادند؛ که پاداشی پایدار برایشان [در پیش] است.');
INSERT INTO "islamhouse" VALUES(95,7,'95:7','پس [ای انسان،] چه چیزی بعد [از این همه دلایل روشن،] تو را به تکذیب [روز] جزا وا می‌دارد؟');
INSERT INTO "islamhouse" VALUES(95,8,'95:8','آیا الله بهترین داور [و حاکم مطلق] نیست؟');
INSERT INTO "islamhouse" VALUES(96,1,'96:1','بخوان به نام پروردگارت که [هستی را] آفرید؛');
INSERT INTO "islamhouse" VALUES(96,2,'96:2','[همان پروردگاری که] انسان را از خونِ بسته آفرید.');
INSERT INTO "islamhouse" VALUES(96,3,'96:3','بخوان و پروردگارت [از همه] بزرگوار‌تر است.');
INSERT INTO "islamhouse" VALUES(96,4,'96:4','[همان] ذاتی ‌که به وسیلۀ قلم [نوشتن] آموخت.');
INSERT INTO "islamhouse" VALUES(96,5,'96:5','به انسان آنچه را که نمی‌دانست [به تدریج] آموخت.');
INSERT INTO "islamhouse" VALUES(96,6,'96:6','چنین نیست [که شما می‌پندارید]؛ حقا که انسان طغیان [و سرکشی] می‌کند');
INSERT INTO "islamhouse" VALUES(96,7,'96:7','[بعد] از اینکه خود را بی‌نیاز [و توانگر] ببیند.');
INSERT INTO "islamhouse" VALUES(96,8,'96:8','یقیناً بازگشت [همه] به سوی پروردگار توست.');
INSERT INTO "islamhouse" VALUES(96,9,'96:9','[ای پیامبر،] آیا دیده‌ای آن کسی [= ابوجهل] را که [از عبادت الله] باز می‌دارد');
INSERT INTO "islamhouse" VALUES(96,10,'96:10','بنده‌ای [= محمد] را هنگامی که [کنار کعبه] نماز می‌خواند.');
INSERT INTO "islamhouse" VALUES(96,11,'96:11','به من خبر بده اگر این [بنده بازداشته‌شده] بر [راه] هدایت باشد');
INSERT INTO "islamhouse" VALUES(96,12,'96:12','یا [مردم را] به پرهیزگاری فرمان دهد، [سزای کسی‌ که چنین بنده‌ای را بیازارد چیست؟]');
INSERT INTO "islamhouse" VALUES(96,13,'96:13','به من خبر بده اگر این [شخص سرکش، حق را] تکذیب کند و رویگردان شود [آیا سزاوار عذاب نیست؟].');
INSERT INTO "islamhouse" VALUES(96,14,'96:14','آیا نمی‌داند که بی‌تردید، الله [همه اعمالش را] می‌بیند؟');
INSERT INTO "islamhouse" VALUES(96,15,'96:15','چنین نیست [که او گمان می‌کند]؛ اگر باز نیاید [و دست از شرارت برندارد،] قطعاً موی پیشانی [او] را به شدّت خواهیم گرفت [و به سوی دوزخ خواهیم کشاند]؛');
INSERT INTO "islamhouse" VALUES(96,16,'96:16','موی پیشانیِ [همان] دروغگوی خطاکار را.');
INSERT INTO "islamhouse" VALUES(96,17,'96:17','پس باید هم‌مجلسانش را [به کمک] بخواند.');
INSERT INTO "islamhouse" VALUES(96,18,'96:18','ما [نیز] آتشبانان [دوزخ] را فرا خواهیم ‌خواند.');
INSERT INTO "islamhouse" VALUES(96,19,'96:19','چنین نیست [که او می‌پندارد]؛ هرگز او را اطاعت مکن و سجده کن، و [به الله] تقرّب جوی.');
INSERT INTO "islamhouse" VALUES(97,1,'97:1','همانا ما آن [قرآن] را در شب قدر نازل کردیم.');
INSERT INTO "islamhouse" VALUES(97,2,'97:2','و تو چه دانی شب قدر چیست؟');
INSERT INTO "islamhouse" VALUES(97,3,'97:3','شب قدر، بهتر از هزار ماه است.');
INSERT INTO "islamhouse" VALUES(97,4,'97:4','فرشتگان و روح [= جبرئیل] در آن [شب،] به فرمان پروردگارشان برای [انجام] هر کاری نازل می‌شوند.');
INSERT INTO "islamhouse" VALUES(97,5,'97:5','[آن شب] تا طلوع فجر، سلامت [و رحمت] است.');
INSERT INTO "islamhouse" VALUES(98,1,'98:1','کسانی از اهل کتاب و مشرکان ‌که کافر شدند، [از آیین کفر] دست برنمی‌دارند تا اینکه دلیل روشنی برایشان بیاید.');
INSERT INTO "islamhouse" VALUES(98,2,'98:2','پیامبری از سوی الله [بیاید] که صحیفه‌های پاک را بخواند.');
INSERT INTO "islamhouse" VALUES(98,3,'98:3','و نوشته‌هایی استوار [و درست] در آن [صحیفه‌ها] است.');
INSERT INTO "islamhouse" VALUES(98,4,'98:4','و کسانی‌ که به آنان کتاب داده شد [از جمله یهود و نصاری] پراکنده نشدند [و اختلاف نکردند،] مگر بعد از آنکه دلیل روشن برایشان آمد.');
INSERT INTO "islamhouse" VALUES(98,5,'98:5','و آنان فرمان نیافتند جز اینکه الله را عبادت کنند در حالی ‌که دین [خود] را برای او خالص گردانند [و از شرک و عبادت معبودان باطل،] به توحید روی آورند؛ و نماز برپا دارند و زکات بپردازند؛ و این است آیین راستین و مستقیم.');
INSERT INTO "islamhouse" VALUES(98,6,'98:6','بی‌گمان، کسانی از اهل کتاب و مشرکان ‌که کافر شدند، جاودانه در آتش جهنم خواهند ماند. آنان بدترینِ آفریدگان هستند.');
INSERT INTO "islamhouse" VALUES(98,7,'98:7','همانا کسانی که ایمان آوردند و کارهای شایسته انجام دادند، آنان بهترینِ آفریدگان هستند.');
INSERT INTO "islamhouse" VALUES(98,8,'98:8','پاداش آنان نزد پروردگار‌شان، باغ‌های [بهشت] جاویدان است که جویبارها از زیر [درختان] آن جاری است؛ همیشه در آن خواهند ماند؛ الله از [اعمال] آنها راضی است و آنان [نیز] از [پاداشِ] او راضیند؛ و این [مقام و پاداش،] برای کسی است که از پروردگارش بترسد.');
INSERT INTO "islamhouse" VALUES(99,1,'99:1','هنگامی که زمین با شدید‌ترین تکان‌هایش به لرزه درآید.');
INSERT INTO "islamhouse" VALUES(99,2,'99:2','و زمین بار‌های سنگینش را بیرون ریزد.');
INSERT INTO "islamhouse" VALUES(99,3,'99:3','و انسان می‌گوید: «آن [= زمین] را چه شده است؟»');
INSERT INTO "islamhouse" VALUES(99,4,'99:4','در آن روز، [زمین] تمام خبرهایش را بازگو می‌کند؛');
INSERT INTO "islamhouse" VALUES(99,5,'99:5','زیرا که پروردگارت به او [چنین] وحی [و حکم] کرده است.');
INSERT INTO "islamhouse" VALUES(99,6,'99:6','در آن روز، مردم گروه‌گروه باز می‌گردند تا [نتیجۀ] اعمالشان به آنان نشان داده شود.');
INSERT INTO "islamhouse" VALUES(99,7,'99:7','آنگاه هر کس به‌ اندازۀ ذره‌ای کار نیک انجام داده باشد، [پاداشِ] آن را می‌بیند؛');
INSERT INTO "islamhouse" VALUES(99,8,'99:8','و هر کس به اندازه ذره‌ای کار بد کرده باشد، [کیفرِ] آن را می‌بیند.');
INSERT INTO "islamhouse" VALUES(100,1,'100:1','سوگند به اسب‌های دونده که نفس‌زنان [به سوی میدان جهاد] پیش می‌روند.');
INSERT INTO "islamhouse" VALUES(100,2,'100:2','و سوگند به اسب‌هایی که [با برخورد سُم‌هایشان به سنگ‌ها] جرقۀ [آتش] ایجاد می‌کنند.');
INSERT INTO "islamhouse" VALUES(100,3,'100:3','باز سوگند به اسب‌هایی که در صبحگاهان [بر دشمن] یورش می‌برند.');
INSERT INTO "islamhouse" VALUES(100,4,'100:4','و در آن هنگام گرد و غبار برمی‌انگیزند.');
INSERT INTO "islamhouse" VALUES(100,5,'100:5','آنگاه در دل [سپاه دشمن] می‌تازند.');
INSERT INTO "islamhouse" VALUES(100,6,'100:6','یقیناً انسان در برابر [نعمت‌های] پروردگارش بسیار ناسپاس است،');
INSERT INTO "islamhouse" VALUES(100,7,'100:7','و بی‌گمان، او [خود] بر این [ناسپاسی] گواه است،');
INSERT INTO "islamhouse" VALUES(100,8,'100:8','و همانا او علاقۀ فراوانی به مال [دنیا] دارد.');
INSERT INTO "islamhouse" VALUES(100,9,'100:9','آیا او نمی‌داند که در آن روز، آنچه در گور‌هاست، [همه زنده و] برانگیخته می‌شوند؟');
INSERT INTO "islamhouse" VALUES(100,10,'100:10','و آنچه در سینه‌هاست [همه] آشکار می‌گردد.');
INSERT INTO "islamhouse" VALUES(100,11,'100:11','یقیناً در آن روز، پروردگارشان کاملاً به [وضع و حال] آنان آگاه است.');
INSERT INTO "islamhouse" VALUES(101,1,'101:1','[آن] فروکوبنده.');
INSERT INTO "islamhouse" VALUES(101,2,'101:2','[آن] فروکوبنده چیست؟');
INSERT INTO "islamhouse" VALUES(101,3,'101:3','و تو چه ‌دانی که [آن] فروکوبنده چیست؟');
INSERT INTO "islamhouse" VALUES(101,4,'101:4','روزی‌ که مردم مانند پروانه‌های پراکنده [حیران و سرگردان] خواهند بود.');
INSERT INTO "islamhouse" VALUES(101,5,'101:5','و کوه‌ها مانند پشم رنگینِ حلاجی‌شده خواهند شد.');
INSERT INTO "islamhouse" VALUES(101,6,'101:6','و اما هر کس که [در آن روز،] کفّۀ میزانش سنگین باشد،');
INSERT INTO "islamhouse" VALUES(101,7,'101:7','[در بهشت،] در زندگی رضایت‌بخشی خواهد بود.');
INSERT INTO "islamhouse" VALUES(101,8,'101:8','و اما هر کس که کفّۀ میزانش سبک باشد،');
INSERT INTO "islamhouse" VALUES(101,9,'101:9','[مسکن و] پناهگاهش «هاویه» است.');
INSERT INTO "islamhouse" VALUES(101,10,'101:10','و تو چه دانی که آن [هاویه] چیست؟');
INSERT INTO "islamhouse" VALUES(101,11,'101:11','آتشی است سوزان [و شعله‌ور].');
INSERT INTO "islamhouse" VALUES(102,1,'102:1','زیاده‌خواهی، شما را به خود مشغول [و از یادِ پروردگار غافل] ساخت');
INSERT INTO "islamhouse" VALUES(102,2,'102:2','تا [پایانِ] كارتان به گورستان رسید.');
INSERT INTO "islamhouse" VALUES(102,3,'102:3','هرگز چنین نیست [که شما می‌پندارید]؛ به زودی خواهید دانست.');
INSERT INTO "islamhouse" VALUES(102,4,'102:4','باز [هم] چنین نیست؛ به زودی خواهید دانست.');
INSERT INTO "islamhouse" VALUES(102,5,'102:5','چنین نیست [که شما گمان می‌کنید]؛ اگر به دانشِ یقینی [و تردیدناپذیر، حقیقتِ قیامت را] می‌دانستید، [از زیاده‌خواهی و افتخار به اموال و فرزندانتان دست برمی‌داشتید].');
INSERT INTO "islamhouse" VALUES(102,6,'102:6','سوگند که دوزخ را خواهید دید.');
INSERT INTO "islamhouse" VALUES(102,7,'102:7','باز هم سوگند که آن را با چشم یقین خواهید دید؛');
INSERT INTO "islamhouse" VALUES(102,8,'102:8','آنگاه قطعاً در آن روز، از [تمام] نعمت‌ها بازخواست خواهید شد.');
INSERT INTO "islamhouse" VALUES(103,1,'103:1','سوگند به عصر');
INSERT INTO "islamhouse" VALUES(103,2,'103:2','که انسان در زیان است.');
INSERT INTO "islamhouse" VALUES(103,3,'103:3','مگر مؤمنان نیکوکاری که یکدیگر را به حق سفارش نموده‌اند و به شکیبایی توصیه کرده‌اند.');
INSERT INTO "islamhouse" VALUES(104,1,'104:1','وای بر هر غیبت‌کننده [و] عیبجوی‌!');
INSERT INTO "islamhouse" VALUES(104,2,'104:2','[همان] کسی که مال فراونی گرد می‌آورد و آن را بارها می‌شمارد.');
INSERT INTO "islamhouse" VALUES(104,3,'104:3','گمان می‌کند که مالَش او را جاودانه می‌سازد.');
INSERT INTO "islamhouse" VALUES(104,4,'104:4','هرگز چنین نیست [که او گمان می‌کند]؛ قطعاً در آتشِ درهم‌کوبنده انداخته خواهد شد.');
INSERT INTO "islamhouse" VALUES(104,5,'104:5','و [ای پیامبر،] تو چه می‌دانی که آتشِ درهم‌کوبنده چیست؟');
INSERT INTO "islamhouse" VALUES(104,6,'104:6','آتش برافروختۀ الهی است؛');
INSERT INTO "islamhouse" VALUES(104,7,'104:7','[آتشی] که بر دل‌ها چیره می‌گردد [و می‌سوزانَد].');
INSERT INTO "islamhouse" VALUES(104,8,'104:8','بی‌گمان، آن [آتش] بر آنان بسته شده [و از هر سو محاصره‌شان کرده] است.');
INSERT INTO "islamhouse" VALUES(104,9,'104:9','[افروخته] در ستون‌های بلند است.');
INSERT INTO "islamhouse" VALUES(105,1,'105:1','[ای پیامبر،] آیا ندیدی پروردگارت با اصحاب فیل چه کرد؟');
INSERT INTO "islamhouse" VALUES(105,2,'105:2','آیا [فریب و] نقشۀ آنان را در تباهی قرار نداد؟');
INSERT INTO "islamhouse" VALUES(105,3,'105:3','و پرندگانی گروه‌گروه بر [سر] آنان فرستاد');
INSERT INTO "islamhouse" VALUES(105,4,'105:4','[که] با سنگ‌گِل [که در منقار و چنگال داشتند] آنان را سنگباران می‌کردند؛');
INSERT INTO "islamhouse" VALUES(105,5,'105:5','و آنها را همچون کاهِ جویده‌شده ساخت.');
INSERT INTO "islamhouse" VALUES(106,1,'106:1','برای [سپاسگزاری از] همبستگی و اُنس قریش،');
INSERT INTO "islamhouse" VALUES(106,2,'106:2','[همان] همبستگی و اُنسِ آنان در سفر‌های زمستانی [به یَمَن] و تابستانی [به شام]؛');
INSERT INTO "islamhouse" VALUES(106,3,'106:3','باید پروردگارِ خانۀ کعبه را عبادت کنند؛');
INSERT INTO "islamhouse" VALUES(106,4,'106:4','همان [پروردگاری] که آنان را از گرسنگی [رهانید، به آنان] خوراک داد و از وحشت و ترس ایمن نمود.');
INSERT INTO "islamhouse" VALUES(107,1,'107:1','[ای پیامبر،] آیا کسی را که [روز] جزا را تکذیب می‌کنند، دیده‌ای؟');
INSERT INTO "islamhouse" VALUES(107,2,'107:2','او [همان] کسی است ‌که یتیم را [با خشونت از خود] می‌رانَد');
INSERT INTO "islamhouse" VALUES(107,3,'107:3','و [دیگران را] به اطعامِ مسکین تشویق نمی‌کند.');
INSERT INTO "islamhouse" VALUES(107,4,'107:4','پس وای بر نمازگزاران!');
INSERT INTO "islamhouse" VALUES(107,5,'107:5','همان کسانی ‌که از نمازشان غافلند');
INSERT INTO "islamhouse" VALUES(107,6,'107:6','کسانی ‌که خودنمایی می‌کنند');
INSERT INTO "islamhouse" VALUES(107,7,'107:7','و [از امانت دادنِ] وسایل ضروری زندگی دریغ می‌ورزند.');
INSERT INTO "islamhouse" VALUES(108,1,'108:1','[ای پیامبر،] به راستی که ما خیرِ بی‌نهایت [از جمله رود کوثر در بهشت را] به تو عطا کردیم؛');
INSERT INTO "islamhouse" VALUES(108,2,'108:2','پس برای پروردگارت نماز بخوان و قربانی کن.');
INSERT INTO "islamhouse" VALUES(108,3,'108:3','بی‌تردید، دشمنت [از همۀ خیرات و برکات و نام و نشان، بی‌بهره و] بُریده است.');
INSERT INTO "islamhouse" VALUES(109,1,'109:1','[ای پیامبر،] بگو: «ای کافران،');
INSERT INTO "islamhouse" VALUES(109,2,'109:2','آنچه را که شما عبادت می‌کنید، من عبادت نمی‌کنم،');
INSERT INTO "islamhouse" VALUES(109,3,'109:3','و [نیز] آنچه را که من عبادت می‌کنم شما عبادت نمی‌کنید.');
INSERT INTO "islamhouse" VALUES(109,4,'109:4','و نه من عبادت‌کننده آنچه شما عبادت کرده‌اید خواهم بود؛');
INSERT INTO "islamhouse" VALUES(109,5,'109:5','و نه شما عبادت‌کننده آنچه من عبادت می‌کنم خواهید بود.');
INSERT INTO "islamhouse" VALUES(109,6,'109:6','[بنابراین] آیین شما [که آن را برای خویش ساخته‌اید] برای خود‌تان باد و آیین من [که الله آن را بر من فرستاده است] برای خودم».');
INSERT INTO "islamhouse" VALUES(110,1,'110:1','[ای پیامبر،] هنگامی ‌که یاری الله و پیروزی فرا رسد [و فتح مکه رخ دهد]،');
INSERT INTO "islamhouse" VALUES(110,2,'110:2','و مردم را ببینی که گروه‌گروه در دین الله داخل می‌شوند.');
INSERT INTO "islamhouse" VALUES(110,3,'110:3','پس به ستایش پروردگارت تسبیح گوی و از او آمرزش بخواه؛ همانا او بسیار توبه‌پذیر است.');
INSERT INTO "islamhouse" VALUES(111,1,'111:1','بریده باد هر دو دست اَبولَهَب و هلاک باد خودش!');
INSERT INTO "islamhouse" VALUES(111,2,'111:2','مال و ثروتش و آنچه به دست آورده بود، او را بی‌نیاز نساخت [و برایش سودی نبخشید].');
INSERT INTO "islamhouse" VALUES(111,3,'111:3','به زودی به آتش شعله‌ور درخواهد آمد؛');
INSERT INTO "islamhouse" VALUES(111,4,'111:4','و همسرش [اُمّ‌جمیل، آن] هیزم‌کش [آتش‌افروز نیز وارد آتش دوزخ می‌شود].');
INSERT INTO "islamhouse" VALUES(111,5,'111:5','[در حالی که] طنابی از لیفِ خرما بر گردنش است.');
INSERT INTO "islamhouse" VALUES(112,1,'112:1','[ای پیامبر] بگو: «او الله یکتا و یگانه است.');
INSERT INTO "islamhouse" VALUES(112,2,'112:2','الله بی‌نیاز است [و همه نیازمند او هستند].');
INSERT INTO "islamhouse" VALUES(112,3,'112:3','نه [فرزندی] زاده و نه [از کسی] زاده شده است.');
INSERT INTO "islamhouse" VALUES(112,4,'112:4','و هیچ کس همانند و همتای او نبوده و نیست».');
INSERT INTO "islamhouse" VALUES(113,1,'113:1','[ای پیامبر،] بگو: «به پروردگار سپیده‌دم پناه می‌برم،');
INSERT INTO "islamhouse" VALUES(113,2,'113:2','از شرِ تمام آنچه آفریده است،');
INSERT INTO "islamhouse" VALUES(113,3,'113:3','و از شر تاریکی شب، آنگاه که همه جا را فراگیرد،');
INSERT INTO "islamhouse" VALUES(113,4,'113:4','و از شر [زنان جادوگر] که با افسون در گره‌ها می‌دمند.');
INSERT INTO "islamhouse" VALUES(113,5,'113:5','و از شر [هر] حسود، آنگاه که حسد ورزد».');
INSERT INTO "islamhouse" VALUES(114,1,'114:1','[ای پیامبر،] بگو: «به پروردگار مردم پناه می‌برم،');
INSERT INTO "islamhouse" VALUES(114,2,'114:2','فرمانروای مردم،');
INSERT INTO "islamhouse" VALUES(114,3,'114:3','معبود مردم،');
INSERT INTO "islamhouse" VALUES(114,4,'114:4','از شرِ [شیطانِ] وسوسه‌گر که [به هنگام ذکر الله،] پنهان می‌گردد؛');
INSERT INTO "islamhouse" VALUES(114,5,'114:5','همان [وسوسه‌گری] که در دل‌های مردم وسوسه می‌کند؛');
INSERT INTO "islamhouse" VALUES(114,6,'114:6','از جنیان [باشد] و [یا از] آدمیان».');
