INSERT INTO "jalandhari" VALUES(1,1,'1:1','شروع الله کا نام لے کر جو بڑا مہربان نہایت رحم والا ہے');
INSERT INTO "jalandhari" VALUES(1,2,'1:2','سب طرح کی تعریف خدا ہی کو (سزاوار) ہے جو تمام مخلوقات کا پروردگار ہے');
INSERT INTO "jalandhari" VALUES(1,3,'1:3','بڑا مہربان نہایت رحم والا');
INSERT INTO "jalandhari" VALUES(1,4,'1:4','انصاف کے دن کا حاکم');
INSERT INTO "jalandhari" VALUES(1,5,'1:5','(اے پروردگار) ہم تیری ہی عبادت کرتے ہیں اور تجھ ہی سے مدد مانگتے ہیں');
INSERT INTO "jalandhari" VALUES(1,6,'1:6','ہم کو سیدھے رستے چلا');
INSERT INTO "jalandhari" VALUES(1,7,'1:7','ان لوگوں کے رستے جن پر تو اپنا فضل وکرم کرتا رہا نہ ان کے جن پر غصے ہوتا رہا اور نہ گمراہوں کے');
INSERT INTO "jalandhari" VALUES(2,1,'2:1','الم');
INSERT INTO "jalandhari" VALUES(2,2,'2:2','یہ کتاب (قرآن مجید) اس میں کچھ شک نہیں (کہ کلامِ خدا ہے۔ خدا سے) ڈرنے والوں کی رہنما ہے');
INSERT INTO "jalandhari" VALUES(2,3,'2:3','جو غیب پر ایمان لاتے اور آداب کے ساتھ نماز پڑھتے اور جو کچھ ہم نے ان کو عطا فرمایا ہے اس میں سے خرچ کرتے ہیں');
INSERT INTO "jalandhari" VALUES(2,4,'2:4','اور جو کتاب (اے محمدصلى الله عليه وسلم) تم پر نازل ہوئی اور جو کتابیں تم سے پہلے (پیغمبروں پر) نازل ہوئیں سب پر ایمان لاتے اور آخرت کا یقین رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(2,5,'2:5','یہی لوگ اپنے پروردگار (کی طرف) سے ہدایت پر ہیں اور یہی نجات پانے والے ہیں');
INSERT INTO "jalandhari" VALUES(2,6,'2:6','جو لوگ کافر ہیں انہیں تم نصیحت کرو یا نہ کرو ان کے لیے برابر ہے۔ وہ ایمان نہیں لانے کے');
INSERT INTO "jalandhari" VALUES(2,7,'2:7','خدا نے ان کے دلوں اور کانوں پر مہر لگا رکھی ہے اور ان کی آنکھوں پر پردہ (پڑا ہوا) ہے اور ان کے لیے بڑا عذاب (تیار) ہے');
INSERT INTO "jalandhari" VALUES(2,8,'2:8','اور بعض لوگ ایسے ہیں جو کہتے ہیں کہ ہم خدا پر اور روزِ آخرت پر ایمان رکھتے ہیں حالانکہ وہ ایمان نہیں رکھتے');
INSERT INTO "jalandhari" VALUES(2,9,'2:9','یہ (اپنے پندار میں) خدا کو اور مومنوں کو چکما دیتے ہیں مگر (حقیقت میں) اپنے سوا کسی کو چکما نہیں دیتے اور اس سے بے خبر ہیں');
INSERT INTO "jalandhari" VALUES(2,10,'2:10','ان کے دلوں میں (کفر کا) مرض تھا۔ خدا نے ان کا مرض اور زیادہ کر دیا اور ان کے جھوٹ بولنے کے سبب ان کو دکھ دینے والا عذاب ہوگا');
INSERT INTO "jalandhari" VALUES(2,11,'2:11','اور جب ان سے کہا جاتا ہے کہ زمین میں فساد نہ ڈالو تو کہتے ہیں، ہم تو اصلاح کرنے والے ہیں');
INSERT INTO "jalandhari" VALUES(2,12,'2:12','دیکھو یہ بلاشبہ مفسد ہیں، لیکن خبر نہیں رکھتے');
INSERT INTO "jalandhari" VALUES(2,13,'2:13','اور جب ان سے کہا جاتا ہے کہ جس طرح اور لوگ ایمان لے آئے، تم بھی ایمان لے آؤ تو کہتے ہیں، بھلا جس طرح بےوقوف ایمان لے آئے ہیں اسی طرح ہم بھی ایمان لے آئیں؟ سن لو کہ یہی بےوقوف ہیں لیکن نہیں جانتے');
INSERT INTO "jalandhari" VALUES(2,14,'2:14','اور یہ لوگ جب مومنوں سے ملتے ہیں تو کہتے ہیں کہ ہم ایمان لے آئے ہیں، اور جب اپنے شیطانوں میں جاتے ہیں تو (ان سے) کہتے ہیں کہ ہم تمھارے ساتھ ہیں اور (پیروانِ محمدصلى الله عليه وسلم سے) تو ہم ہنسی کیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(2,15,'2:15','ان (منافقوں) سے خدا ہنسی کرتا ہے اور انہیں مہلت دیئے جاتا ہے کہ شرارت وسرکشی میں پڑے بہک رہے ہیں');
INSERT INTO "jalandhari" VALUES(2,16,'2:16','یہ وہ لوگ ہیں جنہوں نے ہدایت چھوڑ کر گمراہی خریدی، تو نہ تو ان کی تجارت ہی نے کچھ نفع دیا اور نہ وہ ہدایت یاب ہی ہوئے');
INSERT INTO "jalandhari" VALUES(2,17,'2:17','ان کی مثال اس شخص کی سی ہے کہ جس نے (شبِ تاریک میں) آگ جلائی۔ جب آگ نے اس کے اردگرد کی چیزیں روشن کیں تو خدا نے ان کی روشنی زائل کر دی اور ان کو اندھیروں میں چھوڑ دیا کہ کچھ نہیں دیکھتے');
INSERT INTO "jalandhari" VALUES(2,18,'2:18','(یہ) بہرے ہیں، گونگے ہیں، اندھے ہیں کہ (کسی طرح سیدھے رستے کی طرف) لوٹ ہی نہیں سکتے');
INSERT INTO "jalandhari" VALUES(2,19,'2:19','یا ان کی مثال مینہ کی سی ہے کہ آسمان سے (برس رہا ہو اور) اس میں اندھیرے پر اندھیرا (چھا رہا) ہو اور (بادل) گرج (رہا) ہو اور بجلی (کوند رہی) ہو تو یہ کڑک سے (ڈر کر) موت کے خوف سے کانوں میں انگلیاں دے لیں اور الله کافروں کو (ہر طرف سے) گھیرے ہوئے ہے');
INSERT INTO "jalandhari" VALUES(2,20,'2:20','قریب ہے کہ بجلی (کی چمک) ان کی آنکھوں (کی بصارت) کو اچک لے جائے۔ جب بجلی (چمکتی اور) ان پر روشنی ڈالی ہے تو اس میں چل پڑتے ہیں اور جب اندھیرا ہو جاتا ہے تو کھڑے کے کھڑے رہ جاتے ہیں اور اگر الله چاہتا تو ان کے کانوں (کی شنوائی) اور آنکھوں (کی بینائی دونوں) کو زائل کر دیتا ہے۔ بے شک الله ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(2,21,'2:21','لوگو! اپنے پروردگار کی عبات کرو جس نے تم کو اور تم سے پہلے لوگوں کو پیدا کیا تاکہ تم (اس کے عذاب سے) بچو');
INSERT INTO "jalandhari" VALUES(2,22,'2:22','جس نے تمھارے لیے زمین کو بچھونا اور آسمان کو چھت بنایا اور آسمان سے مینہ برسا کر تمہارے کھانے کے لیے انواع و اقسام کے میوے پیدا کئے۔ پس کسی کو خدا کا ہمسر نہ بناؤ۔ اور تم جانتے تو ہو');
INSERT INTO "jalandhari" VALUES(2,23,'2:23','اور اگر تم کو اس (کتاب) میں، جو ہم نے اپنے بندے (محمدصلى الله عليه وسلم عربی) پر نازل فرمائی ہے کچھ شک ہو تو اسی طرح کی ایک سورت تم بھی بنا لاؤ اور خدا کے سوا جو تمہارے مددگار ہوں ان کو بھی بلالو اگر تم سچے ہو');
INSERT INTO "jalandhari" VALUES(2,24,'2:24','لیکن اگر (ایسا) نہ کر سکو اور ہرگز نہیں کر سکو گے تو اس آگ سے ڈرو جس کا ایندھن آدمی اور پتھر ہوں گے (اور جو) کافروں کے لیے تیار کی گئی ہے');
INSERT INTO "jalandhari" VALUES(2,25,'2:25','اور جو لوگ ایمان لائے اور نیک عمل کرتے رہے، ان کو خوشخبری سنا دو کہ ان کے لیے (نعمت کے) باغ ہیں، جن کے نیچے نہریں بہہ رہی ہیں۔ جب انہیں ان میں سے کسی قسم کا میوہ کھانے کو دیا جائے گا تو کہیں گے، یہ تو وہی ہے جو ہم کو پہلے دیا گیا تھا۔ اور ان کو ایک دوسرے کے ہم شکل میوے دیئے جائیں گے اور وہاں ان کے لیے پاک بیویاں ہوں گی اور وہ بہشتوں میں ہمیشہ رہیں گے');
INSERT INTO "jalandhari" VALUES(2,26,'2:26','الله اس بات سے عار نہیں کرتا کہ مچھر یا اس سے بڑھ کر کسی چیز (مثلاً مکھی مکڑی وغیرہ) کی مثال بیان فرمائے۔ جو مومن ہیں، وہ یقین کرتے ہیں وہ ان کے پروردگار کی طرف سے سچ ہے اور جو کافر ہیں وہ کہتے ہیں کہ اس مثال سے خدا کی مراد ہی کیا ہے۔ اس سے (خدا) بہتوں کو گمراہ کرتا ہے اور بہتوں کو ہدایت بخشتا ہے اور گمراہ بھی کرتا تو نافرمانوں ہی کو');
INSERT INTO "jalandhari" VALUES(2,27,'2:27','جو خدا کے اقرار کو مضبوط کرنے کے بعد توڑ دیتے ہیں اور جس چیز (یعنی رشتہٴ قرابت) کے جوڑے رکھنے کا الله نے حکم دیا ہے اس کو قطع کئے ڈالتے ہیں اور زمین میں خرابی کرتے ہیں یہی لوگ نقصان اٹھانے والے ہیں');
INSERT INTO "jalandhari" VALUES(2,28,'2:28','(کافرو!) تم خدا سے کیوں کر منکر ہو سکتے ہو جس حال میں کہ تم بےجان تھے تو اس نے تم کو جان بخشی پھر وہی تم کو مارتا ہے پھر وہی تم کو زندہ کرے گا پھر تم اسی کی طرف لوٹ کر جاؤ گے');
INSERT INTO "jalandhari" VALUES(2,29,'2:29','وہی تو ہے جس نے سب چیزیں جو زمین میں ہیں تمہارے لیے پیدا کیں پھر آسمان کی طرف متوجہ ہوا تو ان کو ٹھیک سات آسمان بنا دیا اور وہ ہر چیز سے خبردار ہے');
INSERT INTO "jalandhari" VALUES(2,30,'2:30','اور (وہ وقت یاد کرنے کے قابل ہے) جب تمہارے پروردگار نے فرشتوں سے فرمایا کہ میں زمین میں (اپنا) نائب بنانے والا ہوں۔ انہوں نے کہا۔ کیا تُو اس میں ایسے شخص کو نائب بنانا چاہتا ہے جو خرابیاں کرے اور کشت وخون کرتا پھرے اور ہم تیری تعریف کے ساتھ تسبیح وتقدیس کرتے رہتے ہیں۔ (خدا نے) فرمایا میں وہ باتیں جانتا ہوں جو تم نہیں جانتے');
INSERT INTO "jalandhari" VALUES(2,31,'2:31','اور اس نے آدم کو سب (چیزوں کے) نام سکھائے پھر ان کو فرشتوں کے سامنے کیا اور فرمایا کہ اگر تم سچے ہو تو مجھے ان کے نام بتاؤ');
INSERT INTO "jalandhari" VALUES(2,32,'2:32','انہوں نے کہا، تو پاک ہے۔ جتنا علم تو نے ہمیں بخشا ہے، اس کے سوا ہمیں کچھ معلوم نہیں۔ بے شک تو دانا (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(2,33,'2:33','(تب) خدا نے (آدم کو) حکم دیا کہ آدم! تم ان کو ان (چیزوں) کے نام بتاؤ۔ جب انہوں نے ان کو ان کے نام بتائے تو (فرشتوں سے) فرمایا کیوں میں نے تم سے نہیں کہا تھا کہ میں آسمانوں اور زمین کی (سب) پوشیدہ باتیں جاتنا ہوں اور جو تم ظاہر کرتے ہو اور جو پوشیدہ کرتے ہو (سب) مجھ کو معلوم ہے');
INSERT INTO "jalandhari" VALUES(2,34,'2:34','اور جب ہم نے فرشتوں کو حکم دیا کہ آدم کے آگے سجدہ کرو تو وہ سجدے میں گر پڑے مگر شیطان نے انکار کیا اور غرور میں آکر کافر بن گیا');
INSERT INTO "jalandhari" VALUES(2,35,'2:35','اور ہم نے کہا کہ اے آدم تم اور تمہاری بیوی بہشت میں رہو اور جہاں سے چاہو بے روک ٹوک کھاؤ (پیو) لیکن اس درخت کے پاس نہ جانا نہیں تو ظالموں میں (داخل) ہو جاؤ گے');
INSERT INTO "jalandhari" VALUES(2,36,'2:36','پھر شیطان نے دونوں کو وہاں سے پھسلا دیا اور جس (عیش ونشاط) میں تھے، اس سے ان کو نکلوا دیا۔ تب ہم نے حکم دیا کہ (بہشت بریں سے) چلے جاؤ۔ تم ایک دوسرے کے دشمن ہو، اور تمہارے لیے زمین میں ایک وقت تک ٹھکانا اور معاش (مقرر کر دیا گیا) ہے');
INSERT INTO "jalandhari" VALUES(2,37,'2:37','پھر آدم نے اپنے پروردگار سے کچھ کلمات سیکھے (اور معافی مانگی) تو اس نے ان کا قصور معاف کر دیا بے شک وہ معاف کرنے والا (اور) صاحبِ رحم ہے');
INSERT INTO "jalandhari" VALUES(2,38,'2:38','ہم نے فرمایا کہ تم سب یہاں سے اتر جاؤ جب تمہارے پاس میری طرف سے ہدایت پہنچے تو (اس کی پیروی کرنا کہ) جنہوں نے میری ہدایت کی پیروی کی ان کو نہ کچھ خوف ہوگا اور نہ وہ غمناک ہوں گے');
INSERT INTO "jalandhari" VALUES(2,39,'2:39','اور جنہوں نے (اس کو) قبول نہ کیا اور ہماری آیتوں کو جھٹلایا، وہ دوزخ میں جانے والے ہیں (اور) وہ ہمیشہ اس میں رہیں گے');
INSERT INTO "jalandhari" VALUES(2,40,'2:40','اے یعقوب کی اولاد! میرے وہ احسان یاد کرو جو میں نے تم پر کئے تھے اور اس اقرار کو پورا کرو جو تم نے مجھ سے کیا تھا۔ میں اس اقرار کو پورا کروں گا جو میں نے تم سے کیا تھا اور مجھی سے ڈرتے رہو');
INSERT INTO "jalandhari" VALUES(2,41,'2:41','اور جو کتاب میں نے (اپنے رسول محمدصلى الله عليه وسلم پر) نازل کی ہے جو تمہاری کتاب تورات کو سچا کہتی ہے، اس پر ایمان لاؤ اور اس سے منکرِ اول نہ بنو، اور میری آیتوں میں (تحریف کر کے) ان کے بدلے تھوڑی سی قیمت (یعنی دنیاوی منعفت) نہ حاصل کرو، اور مجھی سے خوف رکھو');
INSERT INTO "jalandhari" VALUES(2,42,'2:42','اور حق کو باطل کے ساتھ نہ ملاؤ، اور سچی بات کو جان بوجھ کر نہ چھپاؤ');
INSERT INTO "jalandhari" VALUES(2,43,'2:43','اور نماز پڑھا کرو اور زکوٰة دیا کرو اور (خدا کے آگے) جھکنے والوں کے ساتھ جھکا کرو');
INSERT INTO "jalandhari" VALUES(2,44,'2:44','(یہ) کیا (عقل کی بات ہے کہ) تم لوگوں کو نیکی کرنے کو کہتے ہو اور اپنے تئیں فراموش کئے دیتے ہو، حالانکہ تم کتاب (خدا) بھی پڑھتے ہو۔ کیا تم سمجھتے نہیں؟');
INSERT INTO "jalandhari" VALUES(2,45,'2:45','اور (رنج وتکلیف میں) صبر اور نماز سے مدد لیا کرو اور بے شک نماز گراں ہے، مگر ان لوگوں پر (گراں نہیں) جو عجز کرنے والے ہیں');
INSERT INTO "jalandhari" VALUES(2,46,'2:46','اور جو یقین کئے ہوئے ہیں کہ وہ اپنے پروردگار سے ملنے والے ہیں اور اس کی طرف لوٹ کر جانے والے ہیں');
INSERT INTO "jalandhari" VALUES(2,47,'2:47','اے یعقوب کی اولاد! میرے وہ احسان یاد کرو، جو میں نے تم پر کئے تھے اور یہ کہ میں نے تم کو جہان کے لوگوں پر فضیلت بخشی تھی');
INSERT INTO "jalandhari" VALUES(2,48,'2:48','اور اس دن سے ڈرو جب کوئی کسی کے کچھ کام نہ آئے اور نہ کسی کی سفارش منظور کی جائے اور نہ کسی سے کسی طرح کا بدلہ قبول کیا جائے اور نہ لوگ (کسی اور طرح) مدد حاصل کر سکیں');
INSERT INTO "jalandhari" VALUES(2,49,'2:49','اور (ہمارے ان احسانات کو یاد کرو) جب ہم نے تم کو قومِ فرعون سے نجات بخشی وہ (لوگ) تم کو بڑا دکھ دیتے تھے تمہارے بیٹوں کو تو قتل کر ڈالتے تھے اور بیٹیوں کو زندہ رہنے دیتے تھے اور اس میں تمہارے پروردگار کی طرف سے بڑی (سخت) آزمائش تھی');
INSERT INTO "jalandhari" VALUES(2,50,'2:50','اور جب ہم نے تمہارے لیے دریا کو پھاڑ دیا تم کو نجات دی اور فرعون کی قوم کو غرق کر دیا اور تم دیکھ ہی تو رہے تھے');
INSERT INTO "jalandhari" VALUES(2,51,'2:51','اور جب ہم نے موسیٰ سے چالیس رات کا وعدہ کیا تو تم نے ان کے پیچھے بچھڑے کو (معبود) مقرر کر لیا اور تم ظلم کر رہے تھے');
INSERT INTO "jalandhari" VALUES(2,52,'2:52','پھر اس کے بعد ہم نے تم کو معاف کر دیا، تاکہ تم شکر کرو');
INSERT INTO "jalandhari" VALUES(2,53,'2:53','اور جب ہم نے موسیٰ کو کتاب اور معجزے عنایت کئے، تاکہ تم ہدایت حاصل کرو');
INSERT INTO "jalandhari" VALUES(2,54,'2:54','اور جب موسیٰ نے اپنی قوم کے لوگوں سے کہا کہ بھائیو، تم نے بچھڑے کو (معبود) ٹھہرانے میں (بڑا) ظلم کیا ہے، تو اپنے پیدا کرنے والے کے آگے توبہ کرو اور اپنے تئیں ہلاک کر ڈالو۔ تمہارے خالق کے نزدیک تمہارے حق میں یہی بہتر ہے۔ پھر اس نے تمہارا قصور معاف کر دیا۔ وہ بے شک معاف کرنے والا (اور) صاحبِ رحم ہے');
INSERT INTO "jalandhari" VALUES(2,55,'2:55','اور جب تم نے (موسیٰ) سے کہا کہ موسیٰ، جب تک ہم خدا کو سامنے نہ دیکھ لیں گے، تم پر ایمان نہیں لائیں گے، تو تم کو بجلی نے آ گھیرا اور تم دیکھ رہے تھے');
INSERT INTO "jalandhari" VALUES(2,56,'2:56','پھر موت آ جانے کے بعد ہم نے تم کو ازسرِ نو زندہ کر دیا، تاکہ احسان مانو');
INSERT INTO "jalandhari" VALUES(2,57,'2:57','اور بادل کا تم پر سایہ کئے رکھا اور (تمہارے لیے) من و سلویٰ اتارتے رہے کہ جو پاکیزہ چیزیں ہم نے تم کو عطا فرمائی ہیں، ان کو کھاؤ (پیو) مگر تمہارے بزرگوں نے ان نعمتوں کی کچھ قدر نہ جانی (اور) وہ ہمارا کچھ نہیں بگاڑتے تھے بلکہ اپنا ہی نقصان کرتے تھے');
INSERT INTO "jalandhari" VALUES(2,58,'2:58','اور جب ہم نے (ان سے) کہا کہ اس گاؤں میں داخل ہو جاؤ اور اس میں جہاں سے چاہو، خوب کھاؤ (پیو) اور (دیکھنا) دروازے میں داخل ہونا تو سجدہ کرنا اور حطة کہنا، ہم تمہارے گناہ معاف کر دیں گے اور نیکی کرنے والوں کو اور زیادہ دیں گے');
INSERT INTO "jalandhari" VALUES(2,59,'2:59','تو جو ظالم تھے، انہوں نے اس لفظ کو، جس کا ان کو حکم دیا تھا، بدل کر اس کی جگہ اور لفظ کہنا شروع کیا، پس ہم نے (ان) ظالموں پر آسمان سے عذاب نازل کیا، کیونکہ نافرمانیاں کئے جاتے تھے');
INSERT INTO "jalandhari" VALUES(2,60,'2:60','اور جب موسیٰ نے اپنی قوم کے لیے (خدا سے) پانی مانگا تو ہم نے کہا کہ اپنی لاٹھی پتھر پر مارو۔ (انہوں نے لاٹھی ماری) تو پھر اس میں سے بارہ چشمے پھوٹ نکلے، اور تمام لوگوں نے اپنا اپنا گھاٹ معلوم کر (کے پانی پی) لیا۔ (ہم نے حکم دیا کہ) خدا کی (عطا فرمائی ہوئی) روزی کھاؤ اور پیو، مگر زمین میں فساد نہ کرتے پھرنا');
INSERT INTO "jalandhari" VALUES(2,61,'2:61','اور جب تم نے کہا کہ موسیٰ! ہم سے ایک (ہی) کھانے پر صبر نہیں ہو سکتا تو اپنے پروردگار سے دعا کیجئے کہ ترکاری اور ککڑی اور گیہوں اور مسور اور پیاز (وغیرہ) جو نباتات زمین سے اُگتی ہیں، ہمارے لیے پیدا کر دے۔ انہوں نے کہا کہ بھلا عمدہ چیزیں چھوڑ کر ان کے عوض ناقص چیزیں کیوں چاہتے ہوں۔ (اگر یہی چیزیں مطلوب ہیں) تو کسی شہر میں جا اترو، وہاں جو مانگتے ہو، مل جائے گا۔ اور (آخرکار) ذلت (ورسوائی) اور محتاجی (وبے نوائی) ان سے چمٹا دی گئی اور وہ الله کے غضب میں گرفتار ہو گئے۔ یہ اس لیے کہ وہ الله کی آیتوں سے انکار کرتے تھے اور (اس کے) نبیوں کو ناحق قتل کر دیتے تھے۔ (یعنی) یہ اس لیے کہ نافرمانی کئے جاتے اور حد سے بڑھے جاتے تھے');
INSERT INTO "jalandhari" VALUES(2,62,'2:62','جو لوگ مسلمان ہیں یا یہودی یا عیسائی یا ستارہ پرست، (یعنی کوئی شخص کسی قوم و مذہب کا ہو) جو خدا اور روز قیامت پر ایمان لائے گا، اور نیک عمل کرے گا، تو ایسے لوگوں کو ان (کے اعمال) کا صلہ خدا کے ہاں ملے گا اور (قیامت کے دن) ان کو نہ کسی طرح کا خوف ہوگا اور نہ وہ غم ناک ہوں گے');
INSERT INTO "jalandhari" VALUES(2,63,'2:63','اور جب ہم نے تم سے عہد (کر) لیا اور کوہِ طُور کو تم پر اٹھا کھڑا کیا (اور حکم دیا) کہ جو کتاب ہم نے تم کو دی ہے، اس کو زور سے پکڑے رہو، اور جو اس میں (لکھا) ہے، اسے یاد رکھو، تاکہ (عذاب سے) محفوظ رہو');
INSERT INTO "jalandhari" VALUES(2,64,'2:64','تو تم اس کے بعد (عہد سے) پھر گئے اور اگر تم پر خدا کا فضل اور اس کی مہربانی نہ ہوتی تو تم خسارے میں پڑے گئے ہوتے');
INSERT INTO "jalandhari" VALUES(2,65,'2:65','اور تم ان لوگوں کو خوب جانتے ہوں، جو تم میں سے ہفتے کے دن (مچھلی کا شکار کرنے) میں حد سے تجاوز کر گئے تھے، تو ہم نے ان سے کہا کہ ذلیل وخوار بندر ہو جاؤ');
INSERT INTO "jalandhari" VALUES(2,66,'2:66','اور اس قصے کو اس وقت کے لوگوں کے لیے اور جو ان کے بعد آنے والے تھے عبرت اور پرہیز گاروں کے لیے نصیحت بنا دیا');
INSERT INTO "jalandhari" VALUES(2,67,'2:67','اور جب موسیٰ نے اپنی قوم کے لوگوں سے کہا کہ خدا تم کو حکم دیتا ہے کہ ایک بیل ذبح کرو۔ وہ بولے، کیا تم ہم سے ہنسی کرتے ہو۔ (موسیٰ نے) کہا کہ میں الله کی پناہ مانگتا ہوں کہ نادان بنوں');
INSERT INTO "jalandhari" VALUES(2,68,'2:68','انہوں نے کہا کہ اپنے پروردگار سے التجا کیجئے کہ وہ ہمیں یہ بتائے کہ وہ بیل کس طرح کا ہو۔ (موسیٰ نے) کہا کہ پروردگار فرماتا ہے کہ وہ بیل نہ تو بوڑھا ہو اور نہ بچھڑا، بلکہ ان کے درمیان (یعنی جوان) ہو۔ جیسا تم کو حکم دیا گیا ہے، ویسا کرو');
INSERT INTO "jalandhari" VALUES(2,69,'2:69','انہوں نے کہا کہ پروردگار سے درخواست کیجئے کہ ہم کو یہ بھی بتائے کہ اس کا رنگ کیسا ہو۔ موسیٰ نے کہا ، پروردگار فرماتا ہے کہ اس کا رنگ گہرا زرد ہو کہ دیکھنے والوں (کے دل) کو خوش کر دیتا ہو');
INSERT INTO "jalandhari" VALUES(2,70,'2:70','انہوں نے کہا کہ (اب کے) پروردگار سے پھر درخواست کیجئے کہ ہم کو بتا دے کہ وہ اور کس کس طرح کا ہو، کیونکہ بہت سے بیل ہمیں ایک دوسرے کے مشابہ معلوم ہوتے ہیں، (پھر) خدا نے چاہا تو ہمیں ٹھیک بات معلوم ہو جائے گی');
INSERT INTO "jalandhari" VALUES(2,71,'2:71','موسیٰ نے کہا کہ خدا فرماتا ہے کہ وہ بیل کام میں لگا ہوا نہ ہو، نہ تو زمین جوتتا ہو اور نہ کھیتی کو پانی دیتا ہو۔ اس میں کسی طرح کا داغ نہ ہو۔ کہنے لگے، اب تم نے سب باتیں درست بتا دیں۔ غرض (بڑی مشکل سے) انہوں نے اس بیل کو ذبح کیا، اور وہ ایسا کرنے والے تھے نہیں');
INSERT INTO "jalandhari" VALUES(2,72,'2:72','اور جب تم نے ایک شخص کو قتل کیا، تو اس میں باہم جھگڑنے لگے۔ لیکن جو بات تم چھپا رہے تھے، خدا اس کو ظاہر کرنے والا تھا');
INSERT INTO "jalandhari" VALUES(2,73,'2:73','تو ہم نے کہا کہ اس بیل کا کوئی سا ٹکڑا مقتول کو مارو۔ اس طرح خدا مردوں کو زندہ کرتا ہے اور تم کو اپنی (قدرت کی) نشانیاں دکھاتا ہے تاکہ تم سمجھو');
INSERT INTO "jalandhari" VALUES(2,74,'2:74','پھر اس کے بعد تمہارے دل سخت ہو گئے۔ گویا وہ پتھر ہیں یا ان سے بھی زیادہ سخت۔ اور پتھر تو بعضے ایسے ہوتے ہیں کہ ان میں سے چشمے پھوٹ نکلتے ہیں، اور بعضے ایسے ہوتے ہیں کہ پھٹ جاتے ہیں،اور ان میں سے پانی نکلنے لگتا ہے، اور بعضے ایسے ہوتے ہیں کہ خدا کے خوف سے گر پڑتے ہیں، اور خدا تمہارے عملوں سے بے خبر نہیں');
INSERT INTO "jalandhari" VALUES(2,75,'2:75','(مومنو) کیا تم امید رکھتے ہو کہ یہ لوگ تمہارے (دین کے) قائل ہو جائیں گے، (حالانکہ) ان میں سے کچھ لوگ کلامِ خدا (یعنی تورات) کو سنتے، پھر اس کے سمجھ لینے کے بعد اس کو جان بوجھ کر بدل دیتے رہے ہیں');
INSERT INTO "jalandhari" VALUES(2,76,'2:76','اور یہ لوگ جب مومنوں سے ملتے ہیں تو کہتے ہیں، ہم ایمان لے آئے ہیں۔ اور جب آپس میں ایک دوسرے سے ملتے ہیں تو کہتے ہیں، جو بات خدا نے تم پر ظاہر فرمائی ہے، وہ تم ان کو اس لیے بتائے دیتے ہو کہ (قیامت کے دن) اسی کے حوالے سے تمہارے پروردگار کے سامنے تم کو الزام دیں۔ کیا تم سمجھتے نہیں؟');
INSERT INTO "jalandhari" VALUES(2,77,'2:77','کیا یہ لوگ یہ نہیں جانتے کہ جو کچھ یہ چھپاتے اور جو کچھ ظاہر کرتے ہیں، خدا کو (سب) معلوم ہے');
INSERT INTO "jalandhari" VALUES(2,78,'2:78','اور بعض ان میں ان پڑھ ہیں کہ اپنے باطل خیالات کے سوا (خدا کی) کتاب سے واقف ہی نہیں اور وہ صرف ظن سے کام لیتے ہیں');
INSERT INTO "jalandhari" VALUES(2,79,'2:79','تو ان لوگوں پر افسوس ہے جو اپنے ہاتھ سے تو کتاب لکھتے ہیں اور کہتے یہ ہیں کہ یہ خدا کے پاس سے (آئی) ہے، تاکہ اس کے عوض تھوڑی سے قیمت (یعنی دنیوی منفعت) حاصل کریں۔ ان پر افسوس ہے، اس لیے کہ (بےاصل باتیں) اپنے ہاتھ سے لکھتے ہیں اور (پھر) ان پر افسوس ہے، اس لیے کہ ایسے کام کرتے ہیں');
INSERT INTO "jalandhari" VALUES(2,80,'2:80','اور کہتے ہیں کہ (دوزخ کی) آگ ہمیں چند روز کے سوا چھو ہی نہیں سکے گی۔ ان سے پوچھو، کیا تم نے خدا سے اقرار لے رکھا ہے کہ خدا اپنے اقرار کے خلاف نہیں کرے گا۔ (نہیں)، بلکہ تم خدا کے بارے میں ایسی باتیں کہتے ہو جن کا تمہیں مطلق علم نہیں');
INSERT INTO "jalandhari" VALUES(2,81,'2:81','ہاں جو برے کام کرے، اور اس کے گناہ (ہر طرف سے) گھیر لیں تو ایسے لوگ دوزخ (میں جانے) والے ہیں (اور) وہ ہمیشہ اس میں (جلتے) رہیں گے');
INSERT INTO "jalandhari" VALUES(2,82,'2:82','اور جو ایمان لائیں اور نیک کام کریں، وہ جنت کے مالک ہوں گے (اور) ہمیشہ اس میں (عیش کرتے) رہیں گے');
INSERT INTO "jalandhari" VALUES(2,83,'2:83','اور جب ہم نے بنی اسرائیل سے عہد لیا کہ خدا کے سوا کسی کی عبادت نہ کرنا اور ماں باپ اور رشتہ داروں اور یتیموں اور محتاجوں کے ساتھ بھلائی کرتے رہنا اور لوگوں سے اچھی باتیں کہنا، اور نماز پڑھتے اور زکوٰة دیتے رہنا، تو چند شخصوں کے سوا تم سب (اس عہد سے) منہ پھیر کر پھر بیٹھے');
INSERT INTO "jalandhari" VALUES(2,84,'2:84','اور جب ہم نے تم سے عہد لیا کہ آپس میں کشت وخون نہ کرنا اور اپنے کو ان کے وطن سے نہ نکالنا تو تم نے اقرار کر لیا، اور تم (اس بات کے) گواہ ہو');
INSERT INTO "jalandhari" VALUES(2,85,'2:85','پھر تم وہی ہو کہ اپنوں کو قتل بھی کر دیتے ہو اور اپنے میں سے بعض لوگوں پر گناہ اور ظلم سے چڑھائی کرکے انہیں وطن سے نکال بھی دیتے ہو، اور اگر وہ تمہارے پاس قید ہو کر آئیں تو بدلہ دے کر ان کو چھڑا بھی لیتے ہو، حالانکہ ان کا نکال دینا ہی تم کو حرام تھا۔ (یہ) کیا (بات ہے کہ) تم کتابِ (خدا) کے بعض احکام کو تو مانتے ہو اور بعض سے انکار کئے دیتے ہو، تو جو تم میں سے ایسی حرکت کریں، ان کی سزا اس کے سوا اور کیا ہو سکتی ہے کہ دنیا کی زندگی میں تو رسوائی ہو اور قیامت کے دن سخت سے سخت عذاب میں ڈال دیئے جائیں اور جو کام تم کرتے ہو، خدا ان سے غافل نہیں');
INSERT INTO "jalandhari" VALUES(2,86,'2:86','یہ وہ لوگ ہیں جنہوں نے آخرت کے بدلے دنیا کی زندگی خریدی۔ سو نہ تو ان سے عذاب ہی ہلکا کیا جائے گا اور نہ ان کو (اور طرح کی) مدد ملے گی');
INSERT INTO "jalandhari" VALUES(2,87,'2:87','اور ہم نے موسیٰ کو کتاب عنایت کی اور ان کے پیچھے یکے بعد دیگرے پیغمبر بھیجتے رہے اور عیسیٰ بن مریم کو کھلے نشانات بخشے اور روح القدس (یعنی جبرئیل) سے ان کو مدد دی۔تو جب کوئی پیغمبر تمہارے پاس ایسی باتیں لے کر آئے، جن کو تمہارا جی نہیں چاہتا تھا، تو تم سرکش ہو جاتے رہے، اور ایک گروہ (انبیاء) کو تو جھٹلاتے رہے اور ایک گروہ کو قتل کرتے رہے');
INSERT INTO "jalandhari" VALUES(2,88,'2:88','اور کہتے ہیں، ہمارے دل پردے میں ہیں۔ (نہیں) بلکہ الله نے ان کے کفر کے سبب ان پر لعنت کر رکھی ہے۔ پس یہ تھوڑے ہی پر ایمان لاتے ہیں');
INSERT INTO "jalandhari" VALUES(2,89,'2:89','اور جب الله کے ہاں سے ان کے پاس کتاب آئی جو ان کی (آسمانی) کتاب کی بھی تصدیق کرتی ہے، اور وہ پہلے (ہمیشہ) کافروں پر فتح مانگا کرتے تھے، تو جس چیز کو وہ خوب پہچانتے تھے، جب ان کے پاس آپہنچی تو اس سے کافر ہو گئے۔ پس کافروں پر الله کی لعنت');
INSERT INTO "jalandhari" VALUES(2,90,'2:90','جس چیز کے بدلے انہوں نے اپنے تئیں بیچ ڈالا، وہ بہت بری ہے، یعنی اس جلن سے کہ خدا اپنے بندوں میں جس پر چاہتا ہے، اپنی مہربانی سے نازل فرماتا ہے۔ خدا کی نازل کی ہوئی کتاب سے کفر کرنے لگے تو وہ (اس کے) غضب بالائے غضب میں مبتلا ہو گئے۔ اور کافروں کے لیے ذلیل کرنے والا عذاب ہے');
INSERT INTO "jalandhari" VALUES(2,91,'2:91','اور جب ان سے کہا جاتا ہے کہ جو (کتاب) خدا نے (اب) نازل فرمائی ہے، اس کو مانو۔ تو کہتے ہیں کہ جو کتاب ہم پر (پہلے) نازل ہو چکی ہے، ہم تو اسی کو مانتے ہیں۔ (یعنی) یہ اس کے سوا کسی اور (کتاب) کو نہیں مانتے، حالانکہ وہ (سراسر) سچی ہے اور جو ان کی (آسمانی) کتاب ہے، اس کی بھی تصدیق کرتی ہے۔ (ان سے) کہہ دو کہ اگر تم صاحبِ ایمان ہوتے تو الله کے پیغمبروں کو پہلے ہی کیوں قتل کیا کرتے');
INSERT INTO "jalandhari" VALUES(2,92,'2:92','اور موسیٰ تمہارے پاس کھلے ہوئے معجزات لے کر آئے تو تم ان کے (کوہِ طور جانے کے) بعد بچھڑے کو معبود بنا بیٹھے اور تم (اپنے ہی حق میں) ظلم کرتے تھے');
INSERT INTO "jalandhari" VALUES(2,93,'2:93','اور جب ہم نے تم (لوگوں) سے عہد واثق لیا اور کوہ طور کو تم پر اٹھا کھڑا کیا (اور حکم دیا کہ) جو (کتاب) ہم نے تم کو دی ہے، اس کو زور سے پکڑو اور جو تمہیں حکم ہوتا ہے (اس کو) سنو تو وہ (جو تمہارے بڑے تھے) کہنے لگے کہ ہم نے سن تو لیا لیکن مانتے نہیں۔ اور ان کے کفر کے سبب بچھڑا (گویا) ان کے دلوں میں رچ گیا تھا۔ (اے پیغمبر ان سے) کہہ دو کہ اگر تم مومن ہو تو تمہارا ایمان تم کو بری بات بتاتا ہے');
INSERT INTO "jalandhari" VALUES(2,94,'2:94','کہہ دو کہ اگر آخرت کا گھر اور لوگوں (یعنی مسلمانوں) کے لیے نہیں اور خدا کے نزدیک تمہارے ہی لیے مخصوص ہے تو اگر سچے ہو تو موت کی آرزو تو کرو');
INSERT INTO "jalandhari" VALUES(2,95,'2:95','لیکن ان اعمال کی وجہ سے، جو ان کے ہاتھ آگے بھیج چکے ہیں، یہ کبھی اس کی آرزو نہیں کریں گے، اور خدا ظالموں سے (خوب) واقف ہے');
INSERT INTO "jalandhari" VALUES(2,96,'2:96','بلکہ ان کو تم اور لوگوں سے زندگی کے کہیں حریص دیکھو گے، یہاں تک کہ مشرکوں سے بھی۔ ان میں سے ہر ایک یہی خواہش کرتا ہے کہ کاش وہ ہزار برس جیتا رہے، مگر اتنی لمبی عمر اس کو مل بھی جائے تو اسے عذاب سے تو نہیں چھڑا سکتی۔ اور جو کام یہ کرتے ہیں، خدا ان کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(2,97,'2:97','کہہ دو کہ جو شخص جبرئیل کا دشمن ہو (اس کو غصے میں مر جانا چاہیئے) اس نے تو (یہ کتاب) خدا کے حکم سے تمہارے دل پر نازل کی ہے جو پہلی کتابوں کی تصدیق کرتی ہے، اور ایمان والوں کے لیے ہدایت اور بشارت ہے');
INSERT INTO "jalandhari" VALUES(2,98,'2:98','جو شخص خدا کا اور اس کے فرشتوں کا اور اس کے پیغمبروں کا اور جبرئیل اور میکائیل کا دشمن ہو تو ایسے کافروں کا خدا دشمن ہے');
INSERT INTO "jalandhari" VALUES(2,99,'2:99','اور ہم نے تمہارے پاس سلجھی ہوئی آیتیں ارسال فرمائی ہیں، اور ان سے انکار وہی کرتے ہیں جو بدکار ہیں');
INSERT INTO "jalandhari" VALUES(2,100,'2:100','ان لوگوں نے جب (خدا سے) عہد واثق کیا تو ان میں سے ایک فریق نے اس کو (کسی چیز کی طرح) پھینک دیا۔ حقیقت یہ ہے کہ ان میں اکثر بے ایمان ہیں');
INSERT INTO "jalandhari" VALUES(2,101,'2:101','اور جب ان کے پاس الله کی طرف سے پیغمبر (آخرالزماں) آئے، اور وہ ان کی (آسمانی) کتاب کی بھی تصدیق کرتے ہیں تو جن لوگوں کو کتاب دی گئی تھی، ان میں سے ایک جماعت نے خدا کی کتاب کو پیٹھ پیچھے پھینک دیا، گویا وہ جانتے ہی نہیں');
INSERT INTO "jalandhari" VALUES(2,102,'2:102','اور ان (ہزلیات) کے پیچھے لگ گئے جو سلیمان کے عہدِ سلطنت میں شیاطین پڑھا کرتے تھے اور سلیمان نے مطلق کفر کی بات نہیں کی، بلکہ شیطان ہی کفر کرتے تھے کہ لوگوں کو جادو سکھاتے تھے۔ اور ان باتوں کے بھی (پیچھے لگ گئے) جو شہر بابل میں دو فرشتوں (یعنی) ہاروت اور ماروت پر اتری تھیں۔ اور وہ دونوں کسی کو کچھ نہیں سکھاتے تھے، جب تک یہ نہ کہہ دیتے کہ ہم تو (ذریعہٴ) آزمائش ہیں۔ تم کفر میں نہ پڑو۔ غرض لوگ ان سے (ایسا) جادو سیکھتے، جس سے میاں بیوی میں جدائی ڈال دیں۔ اور خدا کے حکم کے سوا وہ اس (جادو) سے کسی کا کچھ بھی نہیں بگاڑ سکتے تھے۔ اور کچھ ایسے (منتر) سیکھتے جو ان کو نقصان ہی پہنچاتے اور فائدہ کچھ نہ دیتے۔ اور وہ جانتے تھے کہ جو شخص ایسی چیزوں (یعنی سحر اور منتر وغیرہ) کا خریدار ہوگا، اس کا آخرت میں کچھ حصہ نہیں۔ اور جس چیز کے عوض انہوں نے اپنی جانوں کو بیچ ڈالا، وہ بری تھی۔ کاش وہ (اس بات کو) جانتے');
INSERT INTO "jalandhari" VALUES(2,103,'2:103','اور اگر وہ ایمان لاتے اور پرہیز گاری کرتے تو خدا کے ہاں سے بہت اچھا صلہ ملتا۔ اے کاش، وہ اس سے واقف ہوتے');
INSERT INTO "jalandhari" VALUES(2,104,'2:104','اے اہل ایمان! (گفتگو کے وقت پیغمبرِ خدا سے) راعنا نہ کہا کرو۔ انظرنا کہا کرو۔ اور خوب سن رکھو، اور کافروں کے لیے دکھ دینے والا عذاب ہے');
INSERT INTO "jalandhari" VALUES(2,105,'2:105','جو لوگ کافر ہیں، اہل کتاب یا مشرک وہ اس بات کو پسند نہیں کرتے کہ تم پر تمہارے پروردگار کی طرف سے خیر (وبرکت) نازل ہو۔ اور خدا تو جس کو چاہتا ہے، اپنی رحمت کے ساتھ خاص کر لیتا ہے اور خدا بڑے فضل کا مالک ہے');
INSERT INTO "jalandhari" VALUES(2,106,'2:106','ہم جس آیت کو منسوخ کر دیتے یا اسے فراموش کرا دیتے ہیں تو اس سے بہتر یا ویسی ہی اور آیت بھیج دیتے ہیں۔ کیا تم نہیں جانتے کہ خدا ہر بات پر قادر ہے');
INSERT INTO "jalandhari" VALUES(2,107,'2:107','تمہیں معلوم نہیں کہ آسمانوں اور زمین کی بادشاہت خدا ہی کی ہے، اور خدا کے سوا تمہارا کوئی دوست اور مدد گار نہیں');
INSERT INTO "jalandhari" VALUES(2,108,'2:108','کیا تم یہ چاہتے ہو کہ اپنے پیغمبر سے اسی طرح کے سوال کرو، جس طرح کے سوال پہلے موسیٰ سے کئے گئے تھے۔ اور جس شخص نے ایمان (چھوڑ کر اس) کے بدلے کفر لیا، وہ سیدھے رستے سے بھٹک گیا');
INSERT INTO "jalandhari" VALUES(2,109,'2:109','بہت سے اہل کتاب اپنے دل کی جلن سے یہ چاہتے ہیں کہ ایمان لا چکنے کے بعد تم کو پھر کافر بنا دیں۔ حالانکہ ان پر حق ظاہر ہو چکا ہے۔ تو تم معاف کردو اور درگزر کرو۔ یہاں تک کہ خدا اپنا (دوسرا) حکم بھیجے۔ بے شک خدا ہر بات پر قادر ہے');
INSERT INTO "jalandhari" VALUES(2,110,'2:110','اور نماز ادا کرتے رہو اور زکوٰة دیتے رہو۔ اور جو بھلائی اپنے لیے آگے بھیج رکھو گے، اس کو خدا کے ہاں پا لو گے۔ کچھ شک نہیں کہ خدا تمہارے سب کاموں کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(2,111,'2:111','اور (یہودی اور عیسائی) کہتے ہیں کہ یہودیوں اور عیسائیوں کے سوا کوئی بہشت میں نہیں جانے کا۔ یہ ان لوگوں کے خیالاتِ باطل ہیں۔ (اے پیغمبر ان سے) کہہ دو کہ اگر سچے ہو تو دلیل پیش کرو');
INSERT INTO "jalandhari" VALUES(2,112,'2:112','ہاں جو شخص خدا کے آگے گردن جھکا دے، (یعنی ایمان لے آئے) اور وہ نیکو کار بھی ہو تو اس کا صلہ اس کے پروردگار کے پاس ہے اور ایسے لوگوں کو (قیامت کے دن) نہ کسی طرح کا خوف ہوگا اور نہ وہ غمناک ہوں گے');
INSERT INTO "jalandhari" VALUES(2,113,'2:113','اور یہودی کہتے ہیں کہ عیسائی رستے پر نہیں اور عیسائی کہتے ہیں کہ یہودی رستے پر نہیں۔ حالانکہ وہ کتاب (الہٰی) پڑھتے ہیں۔ اسی طرح بالکل انہی کی سی بات وہ لوگ کہتے ہیں جو (کچھ) نہیں جانتے (یعنی مشرک) تو جس بات میں یہ لوگ اختلاف کر رہے خدا قیامت کے دن اس کا ان میں فیصلہ کر دے گا');
INSERT INTO "jalandhari" VALUES(2,114,'2:114','اور اس سے بڑھ کر ظالم کون، جو خدا کی مسجدوں میں خدا کے نام کا ذکر کئے جانے کو منع کرے اور ان کی ویرانی میں ساعی ہو۔ان لوگوں کو کچھ حق نہیں کہ ان میں داخل ہوں، مگر ڈرتے ہوئے۔ ان کے لیے دنیا میں رسوائی ہے اور آخرت میں بڑا عذاب');
INSERT INTO "jalandhari" VALUES(2,115,'2:115','اور مشرق اور مغرب سب خدا ہی کا ہے۔ تو جدھر تم رخ کرو۔ ادھر خدا کی ذات ہے۔ بے شک خدا صاحبِ وسعت اور باخبر ہے');
INSERT INTO "jalandhari" VALUES(2,116,'2:116','اور یہ لوگ اس بات کے قائل ہیں کہ خدا اولاد رکھتا ہے۔ (نہیں) وہ پاک ہے، بلکہ جو کچھ آسمانوں اور زمین میں ہے، سب اسی کا ہے اور سب اس کے فرماں بردار ہیں');
INSERT INTO "jalandhari" VALUES(2,117,'2:117','(وہی) آسمانوں اور زمین کا پیدا کرنے والاہے۔ جب کوئی کام کرنا چاہتا ہے تو اس کو ارشاد فرما دیتا ہے کہ ہوجا تو وہ ہو جاتا ہے .');
INSERT INTO "jalandhari" VALUES(2,118,'2:118','اور جو لوگ (کچھ) نہیں جانتے (یعنی مشرک) وہ کہتے ہیں کہ خدا ہم سے کلام کیوں نہیں کرتا۔ یا ہمارے پاس کوئی نشانی کیوں نہیں آتی۔ اسی طرح جو لوگ ان سے پہلے تھے، وہ بھی انہی کی سی باتیں کیا کرتے تھے۔ ان لوگوں کے دل آپس میں ملتے جلتے ہیں۔ جو لوگ صاحبِ یقین ہیں، ان کے (سمجھانے کے) لیے نشانیاں بیان کردی ہیں');
INSERT INTO "jalandhari" VALUES(2,119,'2:119','(اے محمدصلى الله عليه وسلم) ہم نے تم کو سچائی کے ساتھ خوشخبری سنانے والا اور ڈرانے والا بنا کر بھیجا ہے۔ اور اہل دوزخ کے بارے میں تم سے کچھ پرسش نہیں ہوگی');
INSERT INTO "jalandhari" VALUES(2,120,'2:120','اور تم سے نہ تو یہودی کبھی خوش ہوں گے اور نہ عیسائی، یہاں تک کہ تم ان کے مذہب کی پیروی اختیار کرلو۔ (ان سے) کہہ دو کہ خدا کی ہدایت (یعنی دین اسلام) ہی ہدایت ہے۔ اور (اے پیغمبر) اگر تم اپنے پاس علم (یعنی وحی خدا) کے آ جانے پر بھی ان کی خواہشوں پر چلو گے تو تم کو (عذاب) خدا سے (بچانے والا) نہ کوئی دوست ہوگا اور نہ کوئی مددگار');
INSERT INTO "jalandhari" VALUES(2,121,'2:121','جن لوگوں کو ہم نے کتاب عنایت کی ہے، وہ اس کو (ایسا) پڑھتے ہیں جیسا اس کے پڑھنے کا حق ہے۔ یہی لوگ اس پر ایمان رکھنے والے ہیں، اور جو اس کو نہیں مانتے، وہ خسارہ پانے والے ہیں');
INSERT INTO "jalandhari" VALUES(2,122,'2:122','اے بنی اسرائیل ! میرے وہ احسان یاد کرو، جو میں نے تم پر کئے اور یہ کہ میں نے تم کو اہلِ عالم پر فضیلت بخشی');
INSERT INTO "jalandhari" VALUES(2,123,'2:123','اور اس دن سے ڈرو جب کوئی شخص کسی شخص کے کچھ کام نہ آئے، اور نہ اس سے بدلہ قبول کیا جائے اور نہ اس کو کسی کی سفارش کچھ فائدہ دے اور نہ لوگوں کو (کسی اور طرح کی) مدد مل سکے');
INSERT INTO "jalandhari" VALUES(2,124,'2:124','اور جب پروردگار نے چند باتوں میں ابراہیم کی آزمائش کی تو ان میں پورے اترے۔ خدا نے کہا کہ میں تم کو لوگوں کا پیشوا بناؤں گا۔ انہوں نے کہا کہ (پروردگار) میری اولاد میں سے بھی (پیشوا بنائیو)۔ خدا نے فرمایا کہ ہمارا اقرار ظالموں کے لیے نہیں ہوا کرتا');
INSERT INTO "jalandhari" VALUES(2,125,'2:125','اور جب ہم نے خانہٴ کعبہ کو لوگوں کے لیے جمع ہونے اور امن پانے کی جگہ مقرر کیا اور (حکم دیا کہ) جس مقام پر ابراہیم کھڑے ہوئے تھے، اس کو نماز کی جگہ بنا لو۔ اور ابراہیم اور اسمٰعیل کو کہا کہ طواف کرنے والوں اور اعتکاف کرنے والوں اور رکوع کرنے والوں اور سجدہ کرنے والوں کے لیے میرے گھر کو پاک صاف رکھا کرو');
INSERT INTO "jalandhari" VALUES(2,126,'2:126','اور جب ابراہیم نے دعا کی کہ اے پروردگار، اس جگہ کو امن کا شہر بنا اور اس کے رہنے والوں میں سے جو خدا پر اور روزِ آخرت پر ایمان لائیں، ان کے کھانے کو میوے عطا کر، تو خدا نے فرمایا کہ جو کافر ہوگا، میں اس کو بھی کسی قدر متمتع کروں گا، (مگر) پھر اس کو (عذاب) دوزخ کے (بھگتنے کے) لیے ناچار کردوں گا، اور وہ بری جگہ ہے');
INSERT INTO "jalandhari" VALUES(2,127,'2:127','اور جب ابراہیم اور اسمٰعیل بیت الله کی بنیادیں اونچی کر رہے تھے (تو دعا کئے جاتے تھے کہ) اے پروردگار، ہم سے یہ خدمت قبول فرما۔ بےشک تو سننے والا (اور) جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(2,128,'2:128','اے پروردگار، ہم کو اپنا فرمانبردار بنائے رکھیو۔ اور ہماری اولاد میں سے بھی ایک گروہ کو اپنا مطیع بنائے رہیو، اور (پروردگار) ہمیں طریق عبادت بتا اور ہمارے حال پر (رحم کے ساتھ) توجہ فرما۔ بے شک تو توجہ فرمانے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(2,129,'2:129','اے پروردگار، ان (لوگوں) میں انہیں میں سے ایک پیغمبر مبعوث کیجیو جو ان کو تیری آیتیں پڑھ پڑھ کر سنایا کرے اور کتاب اور دانائی سکھایا کرے اور ان (کے دلوں) کو پاک صاف کیا کرے۔ بےشک تو غالب اور صاحبِ حکمت ہے');
INSERT INTO "jalandhari" VALUES(2,130,'2:130','اور ابراہیم کے دین سے کون رو گردانی کر سکتا ہے، بجز اس کے جو نہایت نادان ہو۔ ہم نے ان کو دنیا میں بھی منتخب کیا تھا اور آخرت میں بھی وہ (زمرہٴ) صلحا میں سے ہوں گے');
INSERT INTO "jalandhari" VALUES(2,131,'2:131','جب ان سے ان کے پروردگار نے فرمایا کہ اسلام لے آؤ تو انہوں نے عرض کی کہ میں رب العالمین کے آگے سر اطاعت خم کرتا ہوں');
INSERT INTO "jalandhari" VALUES(2,132,'2:132','اور ابرہیم نے اپنے بیٹوں کو اسی بات کی وصیت کی اور یعقوب نے بھی (اپنے فرزندوں سے یہی کہا) کہ بیٹا خدا نے تمہارے لیے یہی دین پسند فرمایا ہے تو مرنا ہے تو مسلمان ہی مرنا');
INSERT INTO "jalandhari" VALUES(2,133,'2:133','بھلا جس وقت یعقوب وفات پانے لگے تو تم اس وقت موجود تھے، جب انہوں نے اپنے بیٹوں سے پوچھا کہ میرے بعد تم کس کی عبادت کرو گے، تو انہوں نے کہا کہ آپ کے معبود اور آپ کے باپ دادا ابراہیم اور اسمٰعیل اور اسحاق کے معبود کی عبادت کریں گے جو معبود یکتا ہے اور ہم اُسی کے حکم بردار ہیں');
INSERT INTO "jalandhari" VALUES(2,134,'2:134','یہ جماعت گزرچکی۔ ان کو اُن کے اعمال (کا بدلہ ملے گا) اور تم کو تمھارے اعمال (کا) اور جو عمل وہ کرتے تھے ان کی پرسش تم سے نہیں ہوگی');
INSERT INTO "jalandhari" VALUES(2,135,'2:135','اور (یہودی اور عیسائی) کہتے ہیں کہ یہودی یا عیسائی ہو جاؤ تو سیدھے رستے پر لگ جاؤ۔ (اے پیغمبر ان سے) کہہ دو، (نہیں) بلکہ (ہم) دین ابراہیم (اختیار کئے ہوئے ہیں) جو ایک خدا کے ہو رہے تھے اور مشرکوں میں سے نہ تھے');
INSERT INTO "jalandhari" VALUES(2,136,'2:136','(مسلمانو) کہو کہ ہم خدا پر ایمان لائے اور جو (کتاب) ہم پر اتری، اس پر اور جو (صحیفے) ابراہیم اور اسمٰعیل اور اسحاق اور یعقوب اور ان کی اولاد پر نازل ہوئے ان پر اور جو (کتابیں) موسیٰ اور عیسی کو عطا ہوئیں، ان پر، اور جو اور پیغمبروں کو ان کے پروردگار کی طرف سے ملیں، ان پر (سب پر ایمان لائے) ہم ان پیغمروں میں سے کسی میں کچھ فرق نہیں کرتے اور ہم اسی (خدائے واحد) کے فرمانبردار ہیں');
INSERT INTO "jalandhari" VALUES(2,137,'2:137','تو اگر یہ لوگ بھی اسی طرح ایمان لے آئیں جس طرح تم ایمان لے آئے ہو تو ہدایت یاب ہو جائیں اور اگر منہ پھیر لیں (اور نہ مانیں) تو وہ (تمھارے) مخالف ہیں اور ان کے مقابلے میں تمھیں خدا کافی ہے۔ اور وہ سننے والا (اور) جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(2,138,'2:138','(کہہ دو کہ ہم نے) خدا کا رنگ (اختیار کر لیا ہے) اور خدا سے بہتر رنگ کس کا ہو سکتا ہے۔ اور ہم اسی کی عبادت کرنے والے ہیں');
INSERT INTO "jalandhari" VALUES(2,139,'2:139','(ان سے) کہو، کیا تم خدا کے بارے میں ہم سے جھگڑتے ہو، حالانکہ وہی ہمارا اور تمھارا پروردگار ہے اور ہم کو ہمارے اعمال (کا بدلہ ملے گا) اور تم کو تمھارے اعمال (کا) اور ہم خاص اسی کی عبادت کرنے والے ہیں');
INSERT INTO "jalandhari" VALUES(2,140,'2:140','(اے یہود ونصاریٰ) کیا تم اس بات کے قائل ہو کہ ابراہیم اور اسمٰعیل اور اسحاق اور یعقوب اور ان کی اولاد یہودی یا عیسائی تھے۔ (اے محمدصلى الله عليه وسلم ان سے) کہو کہ بھلا تم زیادہ جانتے ہو یا خدا؟ اور اس سے بڑھ کر ظالم کون، جو خدا کی شہادت کو، جو اس کے پاس (کتاب میں موجود) ہے چھپائے۔ اور جو کچھ تم کر رہے ہو، خدا اس سے غافل نہیں');
INSERT INTO "jalandhari" VALUES(2,141,'2:141','یہ جماعت گزر چکی۔ ان کو وہ (ملے گا) جو انہوں نے کیا، اور تم کو وہ جو تم نے کیا۔ اور جو عمل وہ کرتے تھے، اس کی پرسش تم سے نہیں ہوگی');
INSERT INTO "jalandhari" VALUES(2,142,'2:142','احمق لوگ کہیں گے کہ مسلمان جس قبلے پر (پہلے سے چلے آتے) تھے (اب) اس سے کیوں منہ پھیر بیٹھے۔ تم کہہ دو کہ مشرق اور مغرب سب خدا ہی کا ہے۔ وہ جس کو چاہتا ہے، سیدھے رستے پر چلاتا ہے');
INSERT INTO "jalandhari" VALUES(2,143,'2:143','اور اسی طرح ہم نے تم کو امتِ معتدل بنایا ہے، تاکہ تم لوگوں پر گواہ بنو اور پیغمبر (آخرالزماں) تم پر گواہ بنیں۔ اور جس قبلے پر تم (پہلے) تھے، اس کو ہم نے اس لیے مقرر کیا تھا کہ معلوم کریں، کون (ہمارے) پیغمبر کا تابع رہتا ہے، اور کون الٹے پاؤں پھر جاتا ہے۔ اور یہ بات (یعنی تحویل قبلہ لوگوں کو) گراں معلوم ہوئی، مگر جن کو خدا نے ہدایت بخشی (وہ اسے گراں نہیں سمجھتے) اور خدا ایسا نہیں کہ تمہارے ایمان کو یونہی کھو دے۔ خدا تو لوگوں پر بڑا مہربان (اور) صاحبِ رحمت ہے');
INSERT INTO "jalandhari" VALUES(2,144,'2:144','(اے محمدصلى الله عليه وسلم) ہم تمہارا آسمان کی طرف منہ پھیر پھیر کر دیکھنا دیکھ رہے ہیں۔ سو ہم تم کو اسی قبلے کی طرف جس کو تم پسند کرتے ہو، منہ کرنے کا حکم دیں گے تو اپنا منہ مسجد حرام (یعنی خانہٴ کعبہ) کی طرف پھیر لو۔ اور تم لوگ جہاں ہوا کرو، (نماز پڑھنے کے وقت) اسی مسجد کی طرف منہ کر لیا کرو۔ اور جن لوگوں کو کتاب دی گئی ہے، وہ خوب جانتے ہیں کہ (نیا قبلہ) ان کے پروردگار کی طرف سے حق ہے۔ اور جو کام یہ لوگ کرتے ہیں، خدا ان سے بے خبر نہیں');
INSERT INTO "jalandhari" VALUES(2,145,'2:145','اور اگر تم ان اہلِ کتاب کے پاس تمام نشانیاں بھی لے کر آؤ، تو بھی یہ تمہارے قبلے کی پیروی نہ کریں۔ اور تم بھی ان کے قبلے کی پیروی کرنے والے نہیں ہو۔ اور ان میں سے بھی بعض بعض کے قبلے کے پیرو نہیں۔ اور اگر تم باوجود اس کے کہ تمہارے پاس دانش (یعنی وحئ خدا) آ چکی ہے، ان کی خواہشوں کے پیچھے چلو گے تو ظالموں میں (داخل) ہو جاؤ گے');
INSERT INTO "jalandhari" VALUES(2,146,'2:146','جن لوگوں کو ہم نے کتاب دی ہے، وہ ان (پیغمبر آخرالزماں) کو اس طرح پہچانتے ہیں، جس طرح اپنے بیٹوں کو پہچانا کرتے ہیں، مگر ایک فریق ان میں سے سچی بات کو جان بوجھ کر چھپا رہا ہے');
INSERT INTO "jalandhari" VALUES(2,147,'2:147','(اے پیغمبر، یہ نیا قبلہ) تمہارے پروردگار کی طرف سے حق ہے تو تم ہرگز شک کرنے والوں میں نہ ہونا');
INSERT INTO "jalandhari" VALUES(2,148,'2:148','اور ہر ایک (فرقے) کے لیے ایک سمت (مقرر) ہے۔ جدھر وہ (عبادت کے وقت) منہ کیا کرتے ہیں۔ تو تم نیکیوں میں سبقت حاصل کرو۔ تم جہاں رہو گے خدا تم سب کو جمع کرلے گا۔ بے شک خدا ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(2,149,'2:149','اور تم جہاں سے نکلو، (نماز میں) اپنا منہ مسجد محترم کی طرف کر لیا کرو بےشک وہ تمہارے پروردگار کی طرف سے حق ہے۔ اور تم لوگ جو کچھ کرتے ہو۔ خدا اس سے بے خبر نہیں');
INSERT INTO "jalandhari" VALUES(2,150,'2:150','اور تم جہاں سے نکلو، مسجدِ محترم کی طرف منہ (کرکے نماز پڑھا) کرو۔ اور مسلمانو، تم جہاں ہوا کرو، اسی (مسجد) کی طرف رخ کیا کرو۔ (یہ تاکید) اس لیے (کی گئی ہے) کہ لوگ تم کو کسی طرح کا الزام نہ دے سکیں۔ مگر ان میں سے جو ظالم ہیں، (وہ الزام دیں تو دیں) سو ان سے مت ڈرنا اور مجھی سے ڈرتے رہنا۔ اور یہ بھی مقصود ہے کہ تم کو اپنی تمام نعمتیں بخشوں اور یہ بھی کہ تم راہِ راست پر چلو');
INSERT INTO "jalandhari" VALUES(2,151,'2:151','جس طرح (منجملہ اور نعمتوں کے) ہم نے تم میں تمھیں میں سے ایک رسول بھیجے ہیں جو تم کو ہماری آیتیں پڑھ پڑھ کر سناتے اور تمہیں پاک بناتے اور کتاب (یعنی قرآن) اور دانائی سکھاتے ہیں، اور ایسی باتیں بتاتے ہیں، جو تم پہلے نہیں جانتے تھے');
INSERT INTO "jalandhari" VALUES(2,152,'2:152','سو تم مجھے یاد کرو۔ میں تمہیں یاد کیا کروں گا۔ اور میرے احسان مانتے رہنا اور ناشکری نہ کرنا');
INSERT INTO "jalandhari" VALUES(2,153,'2:153','اے ایمان والو صبر اور نماز سے مدد لیا کرو بےشک خدا صبر کرنے والوں کے ساتھ ہے');
INSERT INTO "jalandhari" VALUES(2,154,'2:154','اور جو لوگ خدا کی راہ میں مارے جائیں ان کی نسبت یہ کہنا کہ وہ مرے ہوئے ہیں (وہ مردہ نہیں) بلکہ زندہ ہیں لیکن تم نہیں جانتے');
INSERT INTO "jalandhari" VALUES(2,155,'2:155','اور ہم کسی قدر خوف اور بھوک اور مال اور جانوں اور میوؤں کے نقصان سے تمہاری آزمائش کریں گے توصبر کرنے والوں کو (خدا کی خوشنودی کی) بشارت سنا دو');
INSERT INTO "jalandhari" VALUES(2,156,'2:156','ان لوگوں پر جب کوئی مصیبت واقع ہوتی ہے تو کہتے ہیں کہ ہم خدا ہی کا مال ہیں اور اسی کی طرف لوٹ کر جانے والے ہیں');
INSERT INTO "jalandhari" VALUES(2,157,'2:157','یہی لوگ ہیں جن پر ان کے پروردگار کی مہربانی اور رحمت ہے۔ اور یہی سیدھے رستے پر ہیں');
INSERT INTO "jalandhari" VALUES(2,158,'2:158','بےشک (کوہ) صفا اور مروہ خدا کی نشانیوں میں سے ہیں۔ تو جو شخص خانہٴ کعبہ کا حج یا عمرہ کرے اس پر کچھ گناہ نہیں کہ دونوں کا طواف کرے۔ (بلکہ طواف ایک قسم کا نیک کام ہے) اور جو کوئی نیک کام کرے تو خدا قدر شناس اور دانا ہے');
INSERT INTO "jalandhari" VALUES(2,159,'2:159','جو لوگ ہمارے حکموں اور ہدایتوں کو جو ہم نے نازل کی ہیں (کسی غرض فاسد سے) چھپاتے ہیں باوجود یہ کہ ہم نے ان لوگوں کے (سمجھانے کے) لئے اپنی کتاب میں کھول کھول کر بیان کردیا ہے۔ ایسوں پر خدا اور تمام لعنت کرنے والے لعنت کرتے ہیں');
INSERT INTO "jalandhari" VALUES(2,160,'2:160','ہاں جو توبہ کرتے ہیں اور اپنی حالت درست کرلیتے اور (احکام الہیٰ کو) صاف صاف بیان کردیتے ہیں تو میں ان کے قصور معاف کردیتا ہوں اور میں بڑا معاف کرنے والا (اور) رحم والا ہوں');
INSERT INTO "jalandhari" VALUES(2,161,'2:161','جو لوگ کافر ہوئے اور کافر ہی مرے ایسوں پر خدا کی اور فرشتوں اور لوگوں کی سب کی لعنت');
INSERT INTO "jalandhari" VALUES(2,162,'2:162','وہ ہمیشہ اسی (لعنت) میں (گرفتار) رہیں گے۔ ان سے نہ تو عذاب ہی ہلکا کیا جائے گا اور نہ انہیں (کچھ) مہلت ملے گی');
INSERT INTO "jalandhari" VALUES(2,163,'2:163','اور (لوگو) تمہارا معبود خدائے واحد ہے اس بڑے مہربان (اور) رحم کرنے کے سوا کوئی عبادت کے لائق نہیں');
INSERT INTO "jalandhari" VALUES(2,164,'2:164','بےشک آسمانوں اور زمین کے پیدا کرنے میں اور رات اور دن کے ایک دوسرے کے پیچھے آنے جانے میں اور کشتیوں اور جہازوں میں جو دریا میں لوگوں کے فائدے کی چیزیں لے کر رواں ہیں اور مینہ میں جس کو خدا آسمان سے برساتا اور اس سے زمین کو مرنے کے بعد زندہ (یعنی خشک ہوئے پیچھے سرسبز) کردیتا ہے اور زمین پر ہر قسم کے جانور پھیلانے میں اور ہواؤں کے چلانےمیں اور بادلوں میں جو آسمان اور زمین کے درمیان گھرے رہتے ہیں۔ عقلمندوں کے لئے (خدا کی قدرت کی) نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(2,165,'2:165','اور بعض لوگ ایسے ہیں جو غیر خدا کو شریک (خدا) بناتے اور ان سے خدا کی سی محبت کرتے ہیں۔ لیکن جو ایمان والے ہیں وہ تو خدا ہی کے سب سے زیادہ دوستدار ہیں۔ اور اے کاش ظالم لوگ جو بات عذاب کے وقت دیکھیں گے اب دیکھ لیتے کہ سب طرح کی طاقت خدا ہی کو ہے۔ اور یہ کہ خدا سخت عذاب کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(2,166,'2:166','اس دن (کفر کے) پیشوا اپنے پیرووں سے بیزاری ظاہر کریں گے اور (دونوں) عذاب (الہیٰ) دیکھ لیں گے اور ان کے آپس کے تعلقات منقطع ہوجائیں گے');
INSERT INTO "jalandhari" VALUES(2,167,'2:167','(یہ حال دیکھ کر) پیروی کرنے والے (حسرت سے) کہیں گے کہ اے کاش ہمیں پھر دنیا میں جانا نصیب ہو تاکہ جس طرح یہ ہم سے بیزار ہو رہے ہیں اسی طرح ہم بھی ان سے بیزار ہوں۔ اسی طرح خدا ان کے اعمال انہیں حسرت بنا کر دکھائے گااور وہ دوزخ سے نکل نہیں سکیں گے');
INSERT INTO "jalandhari" VALUES(2,168,'2:168','لوگو جو چیزیں زمین میں حلال طیب ہیں وہ کھاؤ۔ اور شیطان کے قدموں پر نہ چلو۔ وہ تمہارا کھلا دشمن ہے');
INSERT INTO "jalandhari" VALUES(2,169,'2:169','وہ تو تم کو برائی اور بےحیائی ہی کے کام کرنے کو کہتا ہے اور یہ بھی کہ خدا کی نسبت ایسی باتیں کہو جن کا تمہیں (کچھ بھی) علم نہیں');
INSERT INTO "jalandhari" VALUES(2,170,'2:170','اور جب ان لوگوں سے کہا جاتا ہے کہ جو (کتاب) خدا نے نازل فرمائی ہے اس کی پیروی کرو تو کہتے ہیں (نہیں) بلکہ ہم تو اسی چیز کی پیروی کریں گے جس پر ہم نے اپنے باپ دادا کو پایا۔ بھلا اگرچہ ان کے باپ دادا نہ کچھ سمجھتے ہوں اورنہ سیدھے رستے پر ہوں (تب بھی وہ انہیں کی تقلید کئے جائیں گے)');
INSERT INTO "jalandhari" VALUES(2,171,'2:171','جو لوگ کافر ہیں ان کی مثال اس شخص کی سی ہے جو کسی ایسی چیز کو آواز دے جو پکار اور آواز کے سوا کچھ سن نہ سکے۔ (یہ) بہرے ہیں گونگے ہیں اندھے ہیں کہ (کچھ) سمجھ ہی نہیں سکتے');
INSERT INTO "jalandhari" VALUES(2,172,'2:172','اے اہل ایمان جو پاکیزہ چیزیں ہم نے تم کو عطا فرمائیں ہیں ان کو کھاؤ اور اگر خدا ہی کے بندے ہو تو اس (کی نعمتوں) کا شکر بھی ادا کرو');
INSERT INTO "jalandhari" VALUES(2,173,'2:173','اس نے تم پر مرا ہوا جانور اور لہو اور سور کا گوشت اور جس چیز پر خدا کے سوا کسی اور کا نام پکارا جائے حرام کردیا ہے ہاں جو ناچار ہوجائے (بشرطیکہ ) خدا کی نافرمانی نہ کرے اور حد (ضرورت) سے باہر نہ نکل جائے اس پر کچھ گناہ نہیں۔ بےشک خدا بخشنے والا (اور) رحم کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(2,174,'2:174','جو لوگ (خدا) کی کتاب سے ان (آیتوں اور ہدایتوں) کو جو اس نے نازل فرمائی ہیں چھپاتے اور ان کے بدلے تھوڑی سی قیمت (یعنی دنیاوی منفعت)حاصل کرتے ہیں وہ اپنے پیٹوں میں محض آگ بھرتے ہیں۔ ایسے لوگوں سے خدا قیامت کے دن نہ کلام کرے گا اور نہ ان کو (گناہوں سے) پاک کرے گا۔اور ان کے لئے دکھ دینے والا عذاب ہے');
INSERT INTO "jalandhari" VALUES(2,175,'2:175','یہ وہ لوگ ہیں جنہوں نے ہدایت چھوڑ کر گمراہی اور بخشش چھوڑ کر عذاب خریدا۔ یہ (آتش) جہنم کی کیسی برداشت کرنے والے ہیں!');
INSERT INTO "jalandhari" VALUES(2,176,'2:176','یہ اس لئے کہ خدا نے کتاب سچائی کے ساتھ نازل فرمائی۔ اور جن لوگوں نے اس کتاب میں اختلاف کیا وہ ضد میں (آکر نیکی سے) دور (ہوگئے) ہیں');
INSERT INTO "jalandhari" VALUES(2,177,'2:177','نیکی یہی نہیں کہ تم مشرق یا مغرب کو (قبلہ سمجھ کر ان) کی طرف منہ کرلو بلکہ نیکی یہ ہے کہ لوگ خدا پر اور روز آخرت پر اور فرشتوں پر اور (خدا کی) کتاب پر اور پیغمبروں پر ایمان لائیں۔ اور مال باوجود عزیز رکھنے کے رشتہ داروں اور یتیموں اور محتاجوں اور مسافروں اور مانگنے والوں کو دیں اور گردنوں (کے چھڑانے) میں (خرچ کریں) اور نماز پڑھیں اور زکوٰة دیں۔ اور جب عہد کرلیں تو اس کو پورا کریں۔ اور سختی اور تکلیف میں اور (معرکہ) کارزار کے وقت ثابت قدم رہیں۔ یہی لوگ ہیں جو (ایمان میں) سچے ہیں اور یہی ہیں جو (خدا سے) ڈرنے والے ہیں');
INSERT INTO "jalandhari" VALUES(2,178,'2:178','مومنو! تم کو مقتولوں کے بارےمیں قصاص (یعنی خون کے بدلے خون) کا حکم دیا جاتا ہے (اس طرح پر کہ)آزاد کے بدلے آزاد (مارا جائے) اور غلام کے بدلے غلام اور عورت کے بدلے عورت اور قاتل کو اس کے (مقتول) بھائی (کے قصاص میں) سے کچھ معاف کردیا جائے تو (وارث مقتول) کو پسندیدہ طریق سے (قرار داد کی) پیروی (یعنی مطالبہٴ خون بہا) کرنا اور (قاتل کو) خوش خوئی کے ساتھ ادا کرنا چاہیئے یہ پروردگار کی طرف سے تمہارے لئے آسانی اور مہربانی ہے جو اس کے بعد زیادتی کرے اس کے لئے دکھ کا عذاب ہے');
INSERT INTO "jalandhari" VALUES(2,179,'2:179','اور اے اہل عقل (حکم) قصاص میں (تمہاری) زندگانی ہے کہ تم (قتل و خونریزی سے) بچو');
INSERT INTO "jalandhari" VALUES(2,180,'2:180','تم پر فرض کیا جاتا ہے کہ جب تم میں سے کسی کو موت کا وقت آجائے تو اگر وہ کچھ مال چھوڑ جانے والا ہو تو ماں با پ اور رشتہ داروں کے لئے دستور کے مطابق وصیت کرجائے (خدا سے) ڈر نے والوں پر یہ ایک حق ہے');
INSERT INTO "jalandhari" VALUES(2,181,'2:181','جو شخص وصیت کو سننے کے بعد بدل ڈالے تو اس (کے بدلنے) کا گناہ انہیں لوگوں پر ہے جو اس کو بدلیں۔ اور بےشک خدا سنتا جانتا ہے');
INSERT INTO "jalandhari" VALUES(2,182,'2:182','اگر کسی کو وصیت کرنے والے کی طرف سے (کسی وارث کی) طرفداری یا حق تلفی کا اندیشہ ہو تو اگر وہ (وصیت کو بدل کر) وارثوں میں صلح کرادے تو اس پر کچھ گناہ نہیں۔ بےشک خدا بخشنے والا (اور) رحم والا ہے');
INSERT INTO "jalandhari" VALUES(2,183,'2:183','مومنو! تم پر روزے فرض کئے گئے ہیں۔ جس طرح تم سے پہلے لوگوں پر فرض کئے گئے تھے تاکہ تم پرہیزگار بنو');
INSERT INTO "jalandhari" VALUES(2,184,'2:184','(روزوں کے دن) گنتی کے چند روز ہیں تو جو شخص تم میں سے بیمار ہو یا سفر میں ہو تو دوسرے دنوں میں روزوں کا شمار پورا کرلے اور جو لوگ روزہ رکھنے کی طاقت رکھیں (لیکن رکھیں نہیں) وہ روزے کے بدلے محتاج کو کھانا کھلا دیں اور جو کوئی شوق سے نیکی کرے تو اس کے حق میں زیادہ اچھا ہے۔ اور اگر سمجھو تو روزہ رکھنا ہی تمہارے حق میں بہتر ہے');
INSERT INTO "jalandhari" VALUES(2,185,'2:185','(روزوں کا مہینہ) رمضان کا مہینہ (ہے) جس میں قرآن (اول اول) نازل ہوا جو لوگوں کا رہنما ہے اور (جس میں) ہدایت کی کھلی نشانیاں ہیں اور (جو حق و باطل کو) الگ الگ کرنے والا ہے تو جو کوئی تم میں سے اس مہینے میں موجود ہو چاہیئے کہ پورے مہینے کے روزے رکھے اور جو بیمار ہو یا سفر میں ہو تو دوسرے دنوں میں (رکھ کر) ان کا شمار پورا کرلے۔ خدا تمہارے حق میں آسانی چاہتا ہے اور سختی نہیں چاہتا اور (یہ آسانی کا حکم) اس لئے (دیا گیا ہے) کہ تم روزوں کا شمار پورا کرلو اور اس احسان کے بدلے کہ خدا نے تم کو ہدایت بخشی ہے تم اس کو بزرگی سے یاد کر واور اس کا شکر کرو');
INSERT INTO "jalandhari" VALUES(2,186,'2:186','اور (اے پیغمبر) جب تم سے میرے بندے میرے بارے میں دریافت کریں تو (کہہ دو کہ) میں تو (تمہارے) پاس ہوں جب کوئی پکارنے والا مجھے پکارتا ہے تو میں اس کی دعا قبول کرتا ہوں تو ان کو چاہیئے کہ میرے حکموں کو مانیں اور مجھ پر ایمان لائیں تاکہ نیک رستہ پائیں');
INSERT INTO "jalandhari" VALUES(2,187,'2:187','روزوں کی راتوں میں تمہارے لئے اپنی عورتوں کے پاس جانا کردیا گیا ہے وہ تمہاری پوشاک ہیں اور تم ان کی پوشاک ہو خدا کو معلوم ہے کہ تم (ان کے پاس جانے سے) اپنے حق میں خیانت کرتے تھے سو اس نے تم پر مہربانی کی اور تمہاری حرکات سےدرگزرفرمائی۔اب (تم کو اختیار ہے کہ) ان سے مباشرت کرو۔ اور خدا نے جو چیز تمہارے لئے لکھ رکھی ہے (یعنی اولاد) اس کو (خدا سے) طلب کرو اور کھاؤ پیو یہاں تک کہ صبح کی سفید دھاری (رات کی) سیاہ دھاری سے الگ نظر آنے لگے۔ پھر روزہ (رکھ کر) رات تک پورا کرو اور جب تم مسجدوں میں اعتکاف بیٹھے ہو تو ان سے مباشرت نہ کرو۔ یہ خدا کی حدیں ہیں ان کے پاس نہ جانا۔ اسی طرح خدا اپنی آیتیں لوگوں کے (سمجھانے کے) لئے کھول کھول کر بیان فرماتا ہے تاکہ وہ پرہیزگار بنیں');
INSERT INTO "jalandhari" VALUES(2,188,'2:188','اور ایک دوسرے کا مال ناحق نہ کھاؤ اورنہ اس کو (رشوةً) حاکموں کے پاس پہنچاؤ تاکہ لوگوں کے مال کا کچھ حصہ ناجائز طور پر کھا جاؤ اور (اسے) تم جانتے بھی ہو');
INSERT INTO "jalandhari" VALUES(2,189,'2:189','(اے محمدصلى الله عليه وسلم) لوگ تم سے نئے چاند کے بارے میں دریافت کرتے ہیں (کہ گھٹتا بڑھتا کیوں ہے) کہہ دو کہ وہ لوگوں کے (کاموں کی میعادیں) اور حج کے وقت معلوم ہونے کا ذریعہ ہے اور نیکی اس بات میں نہیں کہ (احرام کی حالت میں) گھروں میں ان کے پچھواڑے کی طرف سے آؤ۔ بلکہ نیکوکار وہ ہے جو پرہیز گار ہو اور گھروں میں ان کے دروازوں سے آیا کرو اور خدا سے ڈرتے رہو تاکہ نجات پاؤ');
INSERT INTO "jalandhari" VALUES(2,190,'2:190','اور جو لوگ تم سے لڑتے ہیں تم بھی خدا کی راہ میں ان سے لڑو مگر زیادتی نہ کرنا کہ خدا زیادتی کرنے والوں کو دوست نہیں رکھتا');
INSERT INTO "jalandhari" VALUES(2,191,'2:191','اور ان کو جہاں پاؤ قتل کردو اور جہاں سے انہوں نے تم کو نکالا ہے (یعنی مکے سے) وہاں سے تم بھی ان کو نکال دو۔ اور (دین سے گمراہ کرنے کا) فساد قتل وخونریزی سے کہیں بڑھ کر ہے اور جب تک وہ تم سے مسجد محترم (یعنی خانہ کعبہ) کے پاس نہ لڑیں تم بھی وہاں ان سے نہ لڑنا۔ ہاں اگر وہ تم سے لڑیں تو تم ان کو قتل کرڈالو۔ کافروں کی یہی سزا ہے');
INSERT INTO "jalandhari" VALUES(2,192,'2:192','اور اگر وہ باز آجائیں تو خدا بخشنے والا (اور) رحم کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(2,193,'2:193','اور ان سے اس وقت تک لڑتے رہنا کہ فساد نابود ہوجائے اور (ملک میں) خدا ہی کا دین ہوجائے اور اگر وہ (فساد سے) باز آجائیں تو ظالموں کے سوا کسی پر زیادتی نہیں (کرنی چاہیئے)');
INSERT INTO "jalandhari" VALUES(2,194,'2:194','ادب کا مہینہ ادب کے مہینے کے مقابل ہے اور ادب کی چیزیں ایک دوسرے کا بدلہ ہیں۔ پس اگر کوئی تم پر زیادتی کرے تو جیسی زیادتی وہ تم پر کرے ویسی ہی تم اس پر کرو۔ اور خدا سے ڈرتے رہو اور جان رکھو کہ خدا ڈرنے والوں کے ساتھ ہے');
INSERT INTO "jalandhari" VALUES(2,195,'2:195','اور خدا کی راہ میں (مال) خرچ کرو اور اپنے آپ کو ہلاکت میں نہ ڈالو اور نیکی کرو بےشک خدا نیکی کرنے والوں کو دوست رکھتا ہے');
INSERT INTO "jalandhari" VALUES(2,196,'2:196','اور خدا (کی خوشنودی) کے لئے حج اور عمرے کو پورا کرو۔ اور اگر (راستےمیں) روک لئے جاؤ تو جیسی قربانی میسر ہو (کردو) اور جب تک قربانی اپنے مقام پر نہ پہنچ جائے سر نہ منڈاؤ۔ اور اگر کوئی تم میں بیمار ہو یا اس کے سر میں کسی طرح کی تکلیف ہو تو (اگر وہ سر منڈالے تو) اس کے بدلے روزے رکھے یا صدقہ دے یا قربانی کرے پھر جب (تکلیف دور ہو کر) تم مطمئن ہوجاؤ تو جو (تم میں) حج کے وقت تک عمرے سے فائدہ اٹھانا چاہے وہ جیسی قربانی میسر ہو کرے۔ اور جس کو (قربانی) نہ ملے وہ تین روزے ایام حج میں رکھے اور سات جب واپس ہو۔ یہ پورے دس ہوئے۔ یہ حکم اس شخص کے لئے ہے جس کے اہل وعیال مکے میں نہ رہتے ہوں اور خدا سے ڈرتے رہو اور جان رکھو کہ خدا سخت عذاب دینے والا ہے');
INSERT INTO "jalandhari" VALUES(2,197,'2:197','حج کے مہینے (معین ہیں جو) معلوم ہیں تو شخص ان مہینوں میں حج کی نیت کرلے تو حج (کے دنوں) میں نہ عورتوں سے اختلاط کرے نہ کوئی برا کام کرے نہ کسی سے جھگڑے۔ اور جو نیک کام تم کرو گے وہ خدا کو معلوم ہوجائے گا اور زاد راہ (یعنی رستے کا خرچ) ساتھ لے جاؤ کیونکہ بہتر (فائدہ) زاد راہ (کا) پرہیزگاری ہے اور اے اہل عقل مجھ سے ڈرتے رہو');
INSERT INTO "jalandhari" VALUES(2,198,'2:198','اس کا تمہیں کچھ گناہ نہیں کہ (حج کے دنوں میں بذریعہ تجارت) اپنے پروردگار سے روزی طلب کرو اور جب عرفات سے واپس ہونے لگو تو مشعر حرام (یعنی مزدلفے) میں خدا کا ذکر کرو اور اس طرح ذکر کرو جس طرح اس نے تم کو سکھایا۔ اور اس سے پیشتر تم لوگ (ان طریقوں سے) محض ناواقف تھے');
INSERT INTO "jalandhari" VALUES(2,199,'2:199','پھر جہاں سے اور لوگ واپس ہوں وہیں سے تم بھی واپس ہو اور خدا سے بخشش مانگو۔ بےشک خدا بخشنے والا اور رحمت کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(2,200,'2:200','پھر جب حج کے تمام ارکان پورے کرچکو تو (منیٰ میں) خدا کو یاد کرو۔ جس طرح اپنے باپ دادا کو یاد کیا کرتے تھے بلکہ اس سے بھی زیادہ اور بعض لوگ ایسے ہیں جو (خدا سے) التجا کرتے ہیں کہ اے پروردگار ہم کو (جو دنیا ہے) دنیا ہی میں عنایت کر ایسے لوگوں کا آخرت میں کچھ حصہ نہیں');
INSERT INTO "jalandhari" VALUES(2,201,'2:201','اور بعضے ایسے ہیں کہ دعا کرتے ہیں کہ پروردگار ہم کو دنیا میں بھی نعمت عطا فرما اور آخرت میں بھی نعمت بخشیو اور دوزخ کے عذاب سے محفوظ رکھیو');
INSERT INTO "jalandhari" VALUES(2,202,'2:202','یہی لوگ ہیں جن کے لئے ان کے کاموں کا حصہ (یعنی اجر نیک تیار) ہے اور خدا جلد حساب لینے والا (اور جلد اجر دینے والا) ہے');
INSERT INTO "jalandhari" VALUES(2,203,'2:203','اور (قیام منیٰ کے) دنوں میں (جو) گنتی کے (دن میں) خدا کو یاد کرو۔ اگر کوئی جلدی کرے (اور) دو ہی دن میں (چل دے) تو اس پر بھی کچھ گناہ نہیں۔ اور جو بعد تک ٹھہرا رہے اس پر بھی کچھ گناہ نہیں۔ یہ باتیں اس شخص کے لئے ہیں جو (خدا سے) ڈرے اور تم لوگ خدا سے ڈرتے رہو اور جان رکھو کہ تم سب اس کے پاس جمع کئے جاؤ گے۔');
INSERT INTO "jalandhari" VALUES(2,204,'2:204','اور کوئی شخص تو ایسا ہے جس کی گفتگو دنیا کی زندگی میں تم کو دلکش معلوم ہوتی ہے اور وہ اپنی مانی الضمیر پر خدا کو گواہ بناتا ہے حالانکہ وہ سخت جھگڑالو ہے');
INSERT INTO "jalandhari" VALUES(2,205,'2:205','اور جب پیٹھ پھیر کر چلا جاتا ہے تو زمین میں دوڑتا پھرتا ہے تاکہ اس میں فتنہ انگیزی کرے اور کھیتی کو (برباد) اور (انسانوں اور حیوانوں کی) نسل کو نابود کردے اور خدا فتنہ انگیزی کو پسند نہیں کرتا');
INSERT INTO "jalandhari" VALUES(2,206,'2:206','اور جب اس سے کہا جاتا ہے کہ خدا سے خوف کر تو غرور اس کو گناہ میں پھنسا دیتا ہے۔ سو ایسے کو جہنم سزاوار ہے۔ اور وہ بہت برا ٹھکانہ ہے');
INSERT INTO "jalandhari" VALUES(2,207,'2:207','اور کوئی شخص ایسا ہے کہ خدا کی خوشنودی حاصل کرنے کے لئے اپنی جان بیچ ڈالتا ہے اور خدا بندوں پر بہت مہربان ہے');
INSERT INTO "jalandhari" VALUES(2,208,'2:208','مومنو! اسلام میں پورے پورے داخل ہوجاؤ اور شیطان کے پیچھے نہ چلو وہ تو تمہارا صریح دشمن ہے');
INSERT INTO "jalandhari" VALUES(2,209,'2:209','پھر اگر تم احکام روشن پہنچ جانے کے بعد لڑکھڑاجاؤ تو جان جاؤ کہ خدا غالب (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(2,210,'2:210','کیا یہ لوگ اسی بات کے منتظر ہیں کہ ان پر خدا (کاعذاب) بادل کے سائبانوں میں آنازل ہو اور فرشتے بھی (اتر آئیں) اور کام تمام کردیا جائے اور سب کاموں کا رجوع خدا ہی کی طرف ہے');
INSERT INTO "jalandhari" VALUES(2,211,'2:211','(اے محمد) بنی اسرائیل سے پوچھو کہ ہم نے ان کو کتنی کھلی نشانیاں دیں۔ اور جو شخص خدا کی نعمت کو اپنے پاس آنے کے بعد بدل دے تو خدا سخت عذاب کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(2,212,'2:212','اور جو کافر ہیں ان کے لئے دنیا کی زندگی خوشنما کر دی گئی ہے اور وہ مومنوں سے تمسخر کرتے ہیں لیکن جو پرہیز گار ہیں وہ قیامت کے دن ان پر غالب ہوں گے اور خدا جس کو چاہتا ہے بےشمار رزق دیتا ہے');
INSERT INTO "jalandhari" VALUES(2,213,'2:213','(پہلے تو سب) لوگوں کا ایک ہی مذہب تھا (لیکن وہ آپس میں اختلاف کرنے لگے) تو خدا نے (ان کی طرف) بشارت دینے والے اور ڈر سنانے والے پیغمبر بھیجے اور ان پر سچائی کے ساتھ کتابیں نازل کیں تاکہ جن امور میں لوگ اختلاف کرتے تھے ان کا ان میں فیصلہ کردے۔ اور اس میں اختلاف بھی انہیں لوگوں نے کیا جن کو کتاب دی گئی تھی باوجود یہ کہ ان کے پاس کھلے ہوئے احکام آچکے تھے (اور یہ اختلاف انہوں نے صرف) آپس کی ضد سے (کیا) تو جس امر حق میں وہ اختلاف کرتے تھے خدا نے اپنی مہربانی سے مومنوں کو اس کی راہ دکھا دی۔ اور خدا جس کو چاہتا ہے سیدھا رستہ دکھا دیتا ہے');
INSERT INTO "jalandhari" VALUES(2,214,'2:214','کیا تم یہ خیال کرتے ہو کہ (یوں ہی) بہشت میں داخل ہوجاؤ گے اور ابھی تم کو پہلے لوگوں کی سی (مشکلیں) تو پیش آئی ہی نہیں۔ ان کو (بڑی بڑی) سختیاں اور تکلیفیں پہنچیں اور وہ (صعوبتوں میں) ہلا ہلا دیئے گئے۔ یہاں تک کہ پیغمبر اور مومن لوگ جو ان کے ساتھ تھے سب پکار اٹھے کہ کب خدا کی مدد آئے گی ۔ دیکھو خدا کی مدد (عن) قریب (آيا چاہتی) ہے');
INSERT INTO "jalandhari" VALUES(2,215,'2:215','(اے محمدصلى الله عليه وسلم) لوگ تم سے پوچھتے ہیں کہ (خدا کی راہ میں) کس طرح کا مال خرچ کریں۔ کہہ دو کہ (جو چاہو خرچ کرو لیکن) جو مال خرچ کرنا چاہو وہ (درجہ بدرجہ اہل استحقاق یعنی) ماں باپ اور قریب کے رشتے داروں کو اور یتیموں کو اور محتاجوں کو اور مسافروں کو (سب کو دو) اور جو بھلائی تم کرو گے خدا اس کو جانتا ہے');
INSERT INTO "jalandhari" VALUES(2,216,'2:216','(مسلمانو) تم پر (خدا کے رستے میں) لڑنا فرض کردیا گیا ہے وہ تمہیں ناگوار تو ہوگا مگر عجب نہیں کہ ایک چیز تم کو بری لگے اور وہ تمہارے حق میں بھلی ہو اور عجب نہیں کہ ایک چیز تم کو بھلی لگے اور وہ تمہارے لئے مضر ہو۔ اور ان باتوں کو) خدا ہی بہتر جانتا ہے اور تم نہیں جانتے');
INSERT INTO "jalandhari" VALUES(2,217,'2:217','(اے محمدصلى الله عليه وسلم) لوگ تم سے عزت والے مہینوں میں لڑائی کرنے کے بارے میں دریافت کرتے ہیں کہہ دو کہ ان میں لڑنا بڑا (گناہ) ہےاور خدا کی راہ سے روکنا اور اس سے کفر کرنا اور مسجد حرام (یعنی خانہ کعبہ میں جانے) سے (بند کرنا)۔ اور اہل مسجد کو اس میں سے نکال دینا (جو یہ کفار کرتے ہیں) خدا کے نزدیک اس سے بھی زیادہ (گناہ) ہے۔ اور فتنہ انگیزی خونریزی سے بھی بڑھ کر ہے۔ اور یہ لوگ ہمیشہ تم سے لڑتے رہیں گے یہاں تک کہ اگر مقدور رکھیں تو تم کو تمہارے دین سے پھیر دیں۔ اور جو کوئی تم میں سے اپنے دین سے پھر کر (کافر ہو) جائے گا اور کافر ہی مرے گا تو ایسے لوگوں کے اعمال دنیا اور آخرت دونوں میں برباد ہوجائیں گے اور یہی لوگ دوزخ (میں جانے) والے ہیں جس میں ہمیشہ رہیں گے');
INSERT INTO "jalandhari" VALUES(2,218,'2:218','جو لوگ ایمان لائے اور خدا کے لئے وطن چھوڑ گئے اور (کفار سے) جنگ کرتے رہے وہی خدا کی رحمت کے امیدوار ہیں۔ اور خدا بخشنے والا (اور) رحمت کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(2,219,'2:219','(اے پیغمبر) لوگ تم سے شراب اور جوئے کا حکم دریافت کرتے ہیں۔ کہہ دو کہ ان میں نقصان بڑے ہیں اور لوگوں کے لئے کچھ فائدے بھی ہیں مگر ان کے نقصان فائدوں سے کہیں زیادہ ہیں اور یہ بھی تم سے پوچھتے ہیں کہ (خدا کی راہ میں) کون سا مال خرچ کریں۔ کہہ دو کہ جو ضرورت سے زیادہ ہو۔ اس طرح خدا تمہارے لئے اپنے احکام کھول کھول کر بیان فرماتا ہے تاکہ تم سوچو');
INSERT INTO "jalandhari" VALUES(2,220,'2:220','(یعنی) دنیا اور آخرت (کی باتوں) میں (غور کرو)۔ اور تم سے یتیموں کے بارے میں دریافت کرتے ہیں کہہ دو کہ ان کی (حالت کی) اصلاح بہت اچھا کام ہے۔ اور اگر تم ان سے مل جل کر رہنا (یعنی خرچ اکھٹا رکھنا) چاہو تو وہ تمہارے بھائی ہیں اور خدا خوب جانتا ہے کہ خرابی کرنے والا کون ہے اور اصلاح کرنے والا کون۔ اور اگر خدا چاہتا تو تم کو تکلیف میں ڈال دیتا۔بےشک خدا غالب (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(2,221,'2:221','اور (مومنو) مشرک عورتوں سے جب تک کہ ایمان نہ لائیں نکاح نہ کرنا۔ کیونکہ مشرک عورت خواہ تم کو کیسی ہی بھلی لگے اس سے مومن لونڈی بہتر ہے۔ اور (اسی طرح) مشرک مرد جب تک ایمان نہ لائیں مومن عورتوں کو ان کو زوجیت میں نہ دینا کیونکہ مشرک (مرد) سے خواہ وہ تم کو کیسا ہی بھلا لگے مومن غلام بہتر ہے۔ یہ (مشرک لوگوں کو) دوزخ کی طرف بلاتے ہیں۔ اور خدا اپنی مہربانی سے بہشت اور بخشش کی طرف بلاتا ہے۔ اور اپنے حکم لوگوں سے کھول کھول کر بیان کرتا ہے تاکہ نصیحت حاصل کریں');
INSERT INTO "jalandhari" VALUES(2,222,'2:222','اور تم سے حیض کے بارے میں دریافت کرتے ہیں۔ کہہ دو کہ وہ تو نجاست ہے۔ سو ایام حیض میں عورتوں سے کنارہ کش رہو۔ اور جب تک پاک نہ ہوجائیں ان سے مقاربت نہ کرو۔ ہاں جب پاک ہوجائیں تو جس طریق سے خدا نے ارشاد فرمایا ہے ان کے پاس جاؤ۔ کچھ شک نہیں کہ خدا توبہ کرنے والوں اور پاک صاف رہنے والوں کو دوست رکھتا ہے');
INSERT INTO "jalandhari" VALUES(2,223,'2:223','تمہاری عورتیں تمہارای کھیتی ہیں تو اپنی کھیتی میں جس طرح چاہو جاؤ۔ اور اپنے لئے (نیک عمل) آگے بھیجو۔ اور خدا سے ڈرتے رہو اور جان رکھو کہ (ایک دن) تمہیں اس کے روبرو حاضر ہونا ہے اور (اے پیغمبر) ایمان والوں کو بشارت سنا دو');
INSERT INTO "jalandhari" VALUES(2,224,'2:224','اور خدا (کے نام کو) اس بات کا حیلہ نہ بنانا کہ (اس کی) قسمیں کھا کھا کر سلوک کرنے اورپرہیزگاری کرنے اور لوگوں میں صلح و سازگاری کرانے سے رک جاؤ۔ اور خدا سب کچھ سنتا اور جانتا ہے');
INSERT INTO "jalandhari" VALUES(2,225,'2:225','خدا تمہاری لغو قسموں پر تم سے مواخذہ نہ کرے گا۔ لیکن جو قسمیں تم قصد دلی سے کھاؤ گے ان پر مواخذہ کرے گا۔ اور خدا بخشنے والا بردبار ہے');
INSERT INTO "jalandhari" VALUES(2,226,'2:226','جو لوگ اپنی عورتوں کے پاس جانے سے قسم کھالیں ان کو چار مہینے تک انتظار کرنا چاہیئے۔ اگر (اس عرصے میں قسم سے) رجوع کرلیں تو خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(2,227,'2:227','اور اگر طلاق کا ارادہ کرلیں تو بھی خدا سنتا (اور) جانتا ہے');
INSERT INTO "jalandhari" VALUES(2,228,'2:228','اور طلاق والی عورتیں تین حیض تک اپنی تئیں روکے رہیں۔ اور اگر وہ خدا اور روز قیامت پر ایمان رکھتی ہیں تو ان کا جائز نہیں کہ خدا نے جو کچھ ان کے شکم میں پیدا کیا ہے اس کو چھپائیں۔ اور ان کے خاوند اگر پھر موافقت چاہیں تو اس (مدت) میں وہ ان کو اپنی زوجیت میں لے لینے کے زیادہ حقدار ہیں۔ اور عورتوں کا حق (مردوں پر) ویسا ہی ہے جیسے دستور کے مطابق (مردوں کا حق) عورتوں پر ہے۔ البتہ مردوں کو عورتوں پر فضیلت ہے۔ اور خدا غالب (اور) صاحب حکمت ہے');
INSERT INTO "jalandhari" VALUES(2,229,'2:229','طلاق (صرف) دوبار ہے (یعنی جب دو دفعہ طلاق دے دی جائے تو) پھر (عورتوں کو) یا تو بطریق شائستہ (نکاح میں) رہنے دینا یا بھلائی کے ساتھ چھوڑ دینا۔ اور یہ جائز نہیں کہ جو مہر تم ان کو دے چکے ہو اس میں سے کچھ واپس لے لو۔ ہاں اگر زن و شوہر کو خوف ہو کہ وہ خدا کی حدوں کو قائم نہیں رکھ سکیں گے تو اگر عورت (خاوند کے ہاتھ سے) رہائی پانے کے بدلے میں کچھ دے ڈالے تو دونوں پر کچھ گناہ نہیں۔ یہ خدا کی (مقرر کی ہوئی) حدیں ہیں ان سے باہر نہ نکلنا۔ اور جو لوگ خدا کی حدوں سے باہر نکل جائیں گے وہ گنہگار ہوں گے');
INSERT INTO "jalandhari" VALUES(2,230,'2:230','پھر اگر شوہر (دو طلاقوں کے بعد تیسری) طلاق عورت کو دے دے تو اس کے بعد جب تک عورت کسی دوسرے شخص سے نکاح نہ کرلے اس (پہلے شوہر) پر حلال نہ ہوگی۔ ہاں اگر دوسرا خاوند بھی طلاق دے دے اورعورت اور پہلا خاوند پھر ایک دوسرے کی طرف رجوع کرلیں تو ان پر کچھ گناہ نہیں بشرطیکہ دونوں یقین کریں کہ خدا کی حدوں کو قائم رکھ سکیں گے اور یہ خدا کی حدیں ہیں ان کو وہ ان لوگوں کے لئے بیان فرماتا ہے جو دانش رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(2,231,'2:231','اور جب تم عورتوں کو (دو دفعہ) طلاق دے چکو اور ان کی عدت پوری ہوجائے تو انہیں یا تو حسن سلوک سے نکاح میں رہنے دو یا بطریق شائستہ رخصت کردو اور اس نیت سے ان کو نکاح میں نہ رہنے دینا چاہئے کہ انہیں تکلیف دو اور ان پر زیادتی کرو۔ اور جو ایسا کرے گا وہ اپنا ہی نقصان کرے گا اور خدا کے احکام کو ہنسی (اور کھیل) نہ بناؤ اور خدا نے تم کو جو نعمتیں بخشی ہیں اور تم پر جو کتاب اور دانائی کی باتیں نازل کی ہیں جن سے وہ تمہیں نصیحت فرماتا ہے ان کو یاد کرو۔ اور خدا سے ڈرتے رہو اور جان رکھوکہ خدا ہر چیز سے واقف ہے');
INSERT INTO "jalandhari" VALUES(2,232,'2:232','اور جب تم عورتوں کو طلاق دے چکو اور ان کی عدت پوری ہوجائے تو ان کو دوسرے شوہروں کے ساتھ جب وہ آپس میں جائز طور پر راضی ہوجائیں نکاح کرنے سے مت روکو۔ اس (حکم) سے اس شخص کو نصیحت کی جاتی ہے جو تم میں خدا اور روز آخرت پر یقین رکھتا ہے۔ یہ تمہارے لئے نہایت خوب اور بہت پاکیزگی کی بات ہے اور خدا جانتا ہے اور تم نہیں جانتے');
INSERT INTO "jalandhari" VALUES(2,233,'2:233','اور مائیں اپنے بچوں کو پورے دو سال دودھ پلائیں یہ (حکم) اس شخص کے لئے ہے جو پوری مدت تک دودھ پلوانا چاہے۔ اور دودھ پلانے والی ماؤں کا کھانا اور کپڑا دستور کے مطابق باپ کے ذمے ہوگا۔ کسی شخص کو اس کی طاقت سے زیادہ تکلیف نہیں دی جاتی (تو یاد رکھو کہ) نہ تو ماں کو اس کے بچے کے سبب نقصان پہنچایا جائے اور نہ باپ کو اس کی اولاد کی وجہ سے نقصان پہنچایا جائے اور اسی طرح (نان نفقہ) بچے کے وارث کے ذمے ہے۔ اور اگر دونوں (یعنی ماں باپ) آپس کی رضامندی اور صلاح سے بچے کا دودھ چھڑانا چاہیں تو ان پر کچھ گناہ نہیں۔ اور اگر تم اپنی اولاد کو دودھ پلوانا چاہو تو تم پر کچھ گناہ نہیں بشرطیکہ تم دودھ پلانے والیوں کو دستور کے مطابق ان کا حق جو تم نے دینا کیا تھا دے دو اور خدا سے ڈرتے رہو اور جان رکھو کہ جو کچھ تم کرتے ہو خدا اس کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(2,234,'2:234','اور جو لوگ تم میں سے مرجائیں اور عورتیں چھوڑ جائیں تو عورتیں چار مہینے دس دن اپنے آپ کو روکے رہیں۔ اور جب (یہ) عدت پوری کرچکیں اور اپنے حق میں پسندیدہ کام (یعنی نکاح) کرلیں تو ان پر کچھ گناہ نہیں۔ اور خدا تمہارے سب کاموں سے واقف ہے');
INSERT INTO "jalandhari" VALUES(2,235,'2:235','اور اگر تم کنائے کی باتوں میں عورتوں کو نکاح کا پیغام بھیجو یا (نکاح کی خواہش کو) اپنے دلوں میں مخفی رکھو تو تو تم پر کچھ گناہ نہیں۔ خدا کو معلوم ہے کہ تم ان سے (نکاح کا) ذکر کرو گے۔ مگر (ایام عدت میں) اس کے سوا کہ دستور کے مطابق کوئی بات کہہ دو پوشیدہ طور پر ان سے قول واقرار نہ کرنا۔ اور جب تک عدت پوری نہ ہولے نکاح کا پختہ ارادہ نہ کرنا۔ اور جان رکھو کہ جو کچھ تمہارے دلوں میں ہے خدا کو سب معلوم ہے تو اس سے ڈرتے رہو اور جان رکھو کہ خدا بخشنے والا اور حلم والا ہے');
INSERT INTO "jalandhari" VALUES(2,236,'2:236','اور اگر تم عورتوں کو ان کے پاس جانے یا ان کا مہر مقرر کرنے سے پہلے طلاق دے دو تو تم پر کچھ گناہ نہیں۔ ہاں ان کو دستور کے مطابق کچھ خرچ ضرور دو (یعنی) مقدور والا اپنے مقدور کے مطابق دے اور تنگدست اپنی حیثیت کے مطابق۔ نیک لوگوں پر یہ ایک طرح کا حق ہے');
INSERT INTO "jalandhari" VALUES(2,237,'2:237','اور اگر تم عورتوں کو ان کے پاس جانے سے پہلے طلاق دے دو لیکن مہر مقرر کرچکے ہو تو آدھا مہر دینا ہوگا۔ ہاں اگر عورتیں مہر بخش دیں یا مرد جن کے ہاتھ میں عقد نکاح ہے (اپنا حق) چھوڑ دیں۔ (اور پورا مہر دے دیں تو ان کو اختیار ہے) اور اگر تم مرد لوگ ہ اپنا حق چھوڑ دو تو یہ پرہیزگاری کی بات ہے۔ اور آپس میں بھلائی کرنے کو فراموش نہ کرنا۔ کچھ شک نہیں کہ خدا تمہارے سب کاموں کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(2,238,'2:238','(مسلمانو) سب نمازیں خصوصاً بیچ کی نماز (یعنی نماز عصر) پورے التزام کے ساتھ ادا کرتے رہو۔ اور خدا کے آگے ادب سے کھڑے رہا کرو');
INSERT INTO "jalandhari" VALUES(2,239,'2:239','اگر تم خوف کی حالت میں ہو تو پیادے یا سوار (جس حال میں ہو نماز پڑھ لو) پھر جب امن (واطمینان) ہوجائے تو جس طریق سے خدا نے تم کو سکھایا ہے جو تم پہلے نہیں جانتے تھے خدا کو یاد کرو');
INSERT INTO "jalandhari" VALUES(2,240,'2:240','اور جو لوگ تم میں سے مرجائیں اور عورتیں چھوڑ جائیں وہ اپنی عورتوں کے حق میں وصیت کرجائیں کہ ان کو ایک سال تک خرچ دیا جائے اور گھر سے نہ نکالی جائیں۔ ہاں اگر وہ خود گھر سے نکل جائیں اور اپنے حق میں پسندیدہ کام (یعنی نکاح) کرلیں تو تم پر کچھ گناہ نہیں۔ اور خدا زبردست حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(2,241,'2:241','اور مطلقہ عورتوں کو بھی دستور کے مطابق نان و نفقہ دینا چاہیئے پرہیزگاروں پر (یہ بھی) حق ہے');
INSERT INTO "jalandhari" VALUES(2,242,'2:242','اسی طرح خدا اپنے احکام تمہارے لئے بیان فرماتا ہے تاکہ تم سمجھو');
INSERT INTO "jalandhari" VALUES(2,243,'2:243','بھلا تم نے ان لوگوں کو نہیں دیکھا جو (شمار میں) ہزاروں ہی تھے اور موت کے ڈر سے اپنے گھروں سے نکل بھاگے تھے۔ تو خدا نے ان کو حکم دیا کہ مرجاؤ۔ پھر ان کو زندہ بھی کردیا۔ کچھ شک نہیں کہ خدا لوگوں پر مہربانی رکھتا ہے۔ لیکن اکثر لوگ شکر نہیں کرتے');
INSERT INTO "jalandhari" VALUES(2,244,'2:244','اور (مسلمانو) خدا کی راہ میں جہاد کرو اور جان رکھو کہ خدا (سب کچھ) جانتا ہے');
INSERT INTO "jalandhari" VALUES(2,245,'2:245','کوئی ہے کہ خدا کو قرض حسنہ دے کہ وہ اس کے بدلے اس کو کئی حصے زیادہ دے گا۔ اور خدا ہی روزی کو تنگ کرتا اور (وہی اسے) کشادہ کرتا ہے۔ اور تم اسی کی طرف لوٹ کر جاؤ گے');
INSERT INTO "jalandhari" VALUES(2,246,'2:246','بھلا تم نے بنی اسرائیل کی ایک جماعت کو نہیں دیکھا جس نے موسیٰ کے بعد اپنے پیغمبر سے کہا کہ آپ ہمارے لئے ایک بادشاہ مقرر کردیں تاکہ ہم خدا کی راہ میں جہاد کریں۔ پیغمبر نے کہا کہ اگر تم کو جہاد کا حکم دیا جائے تو عجب نہیں کہ لڑنے سے پہلو تہی کرو۔ وہ کہنے لگے کہ ہم راہ خدا میں کیوں نہ لڑیں گے جب کہ ہم وطن سے (خارج) اور بال بچوں سے جدا کردیئے گئے۔ لیکن جب ان کو جہاد کا حکم دیا گیا تو چند اشخاص کے سوا سب پھر گئے۔ اور خدا ظالموں سے خوب واقف ہے');
INSERT INTO "jalandhari" VALUES(2,247,'2:247','اور پیغمبر نے ان سے (یہ بھی) کہا کہ خدا نے تم پر طالوت کو بادشاہ مقرر فرمایا ہے۔ وہ بولے کہ اسے ہم پر بادشاہی کا حق کیونکر ہوسکتا ہےبادشاہی کے مستحق تو ہم ہیں اور اس کے پاس تو بہت سی دولت بھی نہیں۔ پیغمبر نے کہا کہ خدا نےاس کو تم پر فضیلت دی ہے اور (بادشاہی کے لئے) منتخب فرمایا ہے اس نے اسے علم بھی بہت سا بخشا ہے اور تن و توش بھی (بڑا عطا کیا ہے) اور خدا (کو اختیار ہے) جسے چاہے بادشاہی بخشے۔ وہ بڑا کشائش والا اور دانا ہے');
INSERT INTO "jalandhari" VALUES(2,248,'2:248','اور پیغمبر نے ان سے کہا کہ ان کی بادشاہی کی نشانی یہ ہے کہ تمہارے پاس ایک صندوق آئے گا جس کو فرشتے اٹھائے ہوئے ہوں گے اس میں تمہارے پروردگار کی طرف سے تسلی (بخشنے والی چیز) ہوگی اور کچھ اور چیزیں بھی ہوں گی جو موسیٰ اور ہارون چھوڑ گئے تھے۔ اگر تم ایمان رکھتے ہو تو یہ تمہارے لئے ایک بڑی نشانی ہے');
INSERT INTO "jalandhari" VALUES(2,249,'2:249','غرض جب طالوت فوجیں لے کر روانہ ہوا تو اس نے (ان سے) کہا کہ خدا ایک نہر سے تمہاری آزمائش کرنے والا ہے۔ جو شخص اس میں سے پانی پی لے گا (اس کی نسبت تصور کیا جائے گا کہ) وہ میرا نہیں۔ اور جو نہ پئے گا وہ (سمجھا جائے گا کہ) میرا ہے۔ ہاں اگر کوئی ہاتھ سے چلو بھر پانی پی لے (تو خیر۔ جب وہ لوگ نہر پر پہنچے) تو چند شخصوں کے سوا سب نے پانی پی لیا۔ پھر جب طالوت اور مومن لوگ جو اس کے ساتھ تھے نہر کے پار ہوگئے۔ تو کہنے لگے کہ آج ہم میں جالوت اور اس کے لشکر سے مقابلہ کرنے کی طاقت نہیں۔ جو لوگ یقین رکھتے تھے کہ ان کو خدا کے روبرو حاضر ہونا ہے وہ کہنے لگے کہ بسااوقات تھوڑی سی جماعت نے خدا کے حکم سے بڑی جماعت پر فتح حاصل کی ہے اور خدا استقلال رکھنے والوں کے ساتھ ہے');
INSERT INTO "jalandhari" VALUES(2,250,'2:250','اور جب وہ لوگ جالوت اور اس کے لشکر کے مقابل آئے تو (خدا سے) دعا کی کہ اے پروردگار ہم پر صبر کے دہانے کھول دے اور ہمیں (لڑائی میں) ثابت قدم رکھ اور (لشکر) کفار پر فتحیاب کر');
INSERT INTO "jalandhari" VALUES(2,251,'2:251','تو طالوت کی فوج نے خدا کے حکم سے ان کو ہزیمت دی۔ اور داؤد نے جالوت کو قتل کر ڈالا۔ اور خدا نے اس کو بادشاہی اور دانائی بخشی اور جو کچھ چاہا سکھایا۔ اور خدا لوگوں کو ایک دوسرے (پر چڑھائی اور حملہ کرنے) سے ہٹاتا نہ رہتا تو ملک تباہ ہوجاتا لیکن خدا اہل عالم پر بڑا مہربان ہے');
INSERT INTO "jalandhari" VALUES(2,252,'2:252','یہ خدا کی آیتیں ہیں جو ہم تم کو سچائی کے ساتھ پڑھ کر سناتے ہیں (اور اے محمدصلى الله عليه وسلم) تم بلاشبہ پیغمبروں میں سے ہو');
INSERT INTO "jalandhari" VALUES(2,253,'2:253','یہ پیغمبر (جو ہم وقتاً فوقتاً بھیجتے رہیں ہیں) ان میں سے ہم نے بعض کو بعض پر فضیلت دی ہے۔ بعض ایسے ہیں جن سے خدا نے گفتگو فرمائی اور بعض کے (دوسرے امور میں) مرتبے بلند کئے۔ اور عیسیٰ بن مریم کو ہم نے کھلی ہوئی نشانیاں عطا کیں اور روح القدس سے ان کو مدد دی۔ اور اگر خداچاہتا تو ان سے پچھلے لوگ اپنے پاس کھلی نشانیاں آنے کے بعد آپس میں نہ لڑتے لیکن انہوں نے اختلاف کیا تو ان میں سے بعض تو ایمان لے آئے اور بعض کافر ہی رہے۔ اور اگر خدا چاہتا تو یہ لوگ باہم جنگ و قتال نہ کرتے۔ لیکن خدا جو چاہتا ہے کرتا ہے');
INSERT INTO "jalandhari" VALUES(2,254,'2:254','اے ایمان والو جو (مال) ہم نے تم کو دیا ہے اس میں سے اس دن کے آنے سے پہلے پہلے خرچ کرلو جس میں نہ (اعمال کا) سودا ہو اور نہ دوستی اور سفارش ہو سکے اور کفر کرنے والے لوگ ظالم ہیں');
INSERT INTO "jalandhari" VALUES(2,255,'2:255','خدا (وہ معبود برحق ہے کہ) اس کے سوا کوئی عبادت کے لائق نہیں زندہ ہمیشہ رہنے والا اسے نہ اونگھ آتی ہے نہ نیند جو کچھ آسمانوں میں اور جو کچھ زمین میں ہیں سب اسی کا ہے کون ہے جو اس کی اجازت کے بغیر اس سے (کسی کی) سفارش کر سکے جو کچھ لوگوں کے روبرو ہو رہا ہے اور جو کچھ ان کے پیچھے ہوچکا ہے اسے سب معلوم ہے اور وہ اس کی معلومات میں سے کسی چیز پر دسترس حاصل نہیں کر سکتے ہاں جس قدر وہ چاہتا ہے (اسی قدر معلوم کرا دیتا ہے) اس کی بادشاہی (اور علم) آسمان اور زمین سب پر حاوی ہے اور اسے ان کی حفاظت کچھ بھی دشوار نہیں وہ بڑا عالی رتبہ اور جلیل القدر ہے');
INSERT INTO "jalandhari" VALUES(2,256,'2:256','دین (اسلام) میں زبردستی نہیں ہے ہدایت (صاف طور پر ظاہر اور) گمراہی سے الگ ہو چکی ہے تو جو شخص بتوں سے اعتقاد نہ رکھے اور خدا پر ایمان لائے اس نے ایسی مضبوط رسی ہاتھ میں پکڑ لی ہے جو کبھی ٹوٹنے والی نہیں اور خدا (سب کچھ) سنتا اور (سب کچھ) جانتا ہے');
INSERT INTO "jalandhari" VALUES(2,257,'2:257','جو لوگ ایمان لائے ہیں ان کا دوست خدا ہے کہ اُن کو اندھیرے سے نکال کر روشنی میں لے جاتا ہے اور جو کافر ہیں ان کے دوست شیطان ہیں کہ ان کو روشنی سے نکال کر اندھیرے میں لے جاتے ہیں یہی لوگ اہل دوزخ ہیں کہ اس میں ہمیشہ رہیں گے');
INSERT INTO "jalandhari" VALUES(2,258,'2:258','بھلا تم نے اس شخص کو نہیں دیکھا جو اس (غرور کے) سبب سے کہ خدا نے اس کو سلطنت بخشی تھی ابراہیم سے پروردگار کے بارے میں جھگڑنے لگا۔ جب ابراہیم نے کہا میرا پروردگار تو وہ ہے جو جلاتا اور مارتا ہے۔ وہ بولا کہ جلا اور مار تو میں بھی سکتا ہوں۔ ابراہیم نے کہا کہ خدا تو سورج کو مشرق سے نکالتا ہے آپ اسے مغرب سے نکال دیجیئے (یہ سن کر) کافر حیران رہ گیا اور خدا بےانصافوں کو ہدایت نہیں دیا کرتا');
INSERT INTO "jalandhari" VALUES(2,259,'2:259','یا اسی طرح اس شخص کو (نہیں دیکھا) جسے ایک گاؤں میں جو اپنی چھتوں پر گرا پڑا تھا اتفاق گزر ہوا۔ تو اس نے کہا کہ خدا اس (کے باشندوں) کو مرنے کے بعد کیونکر زندہ کرے گا۔ تو خدا نے اس کی روح قبض کرلی (اور) سو برس تک (اس کو مردہ رکھا) پھر اس کو جلا اٹھایا اور پوچھا تم کتنا عرصہ (مرے)رہے ہو اس نے جواب دیا کہ ایک دن یا اس سے بھی کم۔ خدا نے فرمایا (نہیں) بلکہ سو برس (مرے) رہے ہو۔ اور اپنے کھانے پینے کی چیزوں کو دیکھو کہ (اتنی مدت میں مطلق) سڑی بسی نہیں اور اپنے گدھے کو بھی دیکھو (جو مرا پڑا ہے) غرض (ان باتوں سے) یہ ہے کہ ہم تم کو لوگوں کے لئے (اپنی قدرت کی) نشانی بنائیں اور (ہاں گدھے) کی ہڈیوں کو دیکھو کہ ہم ان کو کیونکر جوڑے دیتے اور ان پر (کس طرح) گوشت پوست چڑھا دیتے ہیں۔ جب یہ واقعات اس کے مشاہدے میں آئے تو بول اٹھا کہ میں یقین کرتا ہوں کہ خدا ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(2,260,'2:260','اور جب ابراہیم نے (خدا سے) کہا کہ اے پروردگار مجھے دکھا کہ تو مردوں کو کیونکر زندہ کرے گا۔ خدا نے فرمایا کیا تم نے (اس بات کو) باور نہیں کیا۔ انہوں نے کہا کیوں نہیں۔ لیکن (میں دیکھنا) اس لئے (چاہتا ہوں) کہ میرا دل اطمینان کامل حاصل کرلے۔ خدا نے فرمایا کہ چار جانور پکڑوا کر اپنے پاس منگا لو (اور ٹکڑے ٹکڑے کرادو) پھر ان کا ایک ٹکڑا ہر ایک پہاڑ پر رکھوا دو پھر ان کو بلاؤ تو وہ تمہارے پاس دوڑتے چلے آئیں گے۔ اور جان رکھو کہ خدا غالب اور صاحب حکمت ہے۔');
INSERT INTO "jalandhari" VALUES(2,261,'2:261','جو لوگ اپنا مال خدا کی راہ میں خرچ کرتے ہیں ان (کے مال) کی مثال اس دانے کی سی ہے جس سے سات بالیں اگیں اور ہر ایک بال میں سو سو دانے ہوں اور خدا جس (کے مال) کو چاہتا ہے زیادہ کرتا ہے۔ وہ بڑی کشائش والا اور سب کچھ جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(2,262,'2:262','جو لوگ اپنا مال خدا کے رستے میں صرف کرتے ہیں پھر اس کے بعد نہ اس خرچ کا (کسی پر) احسان رکھتے ہیں اور نہ (کسی کو) تکلیف دیتے ہیں۔ ان کا صلہ ان کے پروردگار کے پاس (تیار) ہے۔ اور (قیامت کے روز) نہ ان کو کچھ خوف ہوگا اور نہ وہ غمگین ہوں گے');
INSERT INTO "jalandhari" VALUES(2,263,'2:263','جس خیرات دینے کے بعد (لینے والے کو) ایذا دی جائے اس سے تو نرم بات کہہ دینی اور (اس کی بے ادبی سے) درگزر کرنا بہتر ہے اور خدا بےپروا اور بردبار ہے');
INSERT INTO "jalandhari" VALUES(2,264,'2:264','مومنو! اپنے صدقات (وخیرات)احسان رکھنے اور ایذا دینے سے اس شخص کی طرح برباد نہ کردینا۔ جو لوگوں کو دکھاوے کے لئے مال خرچ کرتا ہے اور خدا اور روز آخرت پر ایمان نہیں رکھتا۔ تو اس (کے مال) کی مثال اس چٹان کی سی ہے جس پر تھوڑی سی مٹی پڑی ہو اور اس پر زور کا مینہ برس کر اسے صاف کر ڈالے۔ (اسی طرح) یہ (ریاکار) لوگ اپنے اعمال کا کچھ بھی صلہ حاصل نہیں کرسکیں گے۔ اور خدا ایسے ناشکروں کو ہدایت نہیں دیا کرتا');
INSERT INTO "jalandhari" VALUES(2,265,'2:265','اور جو لوگ خدا کی خوشنودی حاصل کرنے کے لئے خلوص نیت سے اپنا مال خرچ کرتے ہیں ان کی مثال ایک باغ کی سی ہے جو اونچی جگہ پر واقع ہو(جب) اس پر مینہ پڑے تو دگنا پھل لائے۔ اور اگر مینہ نہ بھی پڑے تو خیر پھوار ہی سہی اور خدا تمہارے کاموں کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(2,266,'2:266','بھلا تم میں کوئی یہ چاہتا ہے کہ اس کا کھجوروں اور انگوروں کا باغ ہو جس میں نہریں بہہ رہی ہوں اور اس میں اس کے لئے ہر قسم کے میوے موجود ہوں اور اسے بڑھاپا آپکڑے اور اس کے ننھے ننھے بچے بھی ہوں۔ تو (ناگہاں) اس باغ پر آگ کا بھرا ہوا بگولا چلے اور وہ جل کر (راکھ کا ڈھیر ہو) جائے۔ اس طرح خدا تم سے اپنی آیتیں کھول کھول کر بیان فرماتا ہے تاکہ تم سوچو (اور سمجھو)');
INSERT INTO "jalandhari" VALUES(2,267,'2:267','مومنو! جو پاکیزہ اور عمدہ مال تم کماتے ہوں اور جو چیزیں ہم تمہارے لئے زمین سےنکالتے ہیں ان میں سے (راہ خدا میں) خرچ کرو۔ اور بری اور ناپاک چیزیں دینے کا قصد نہ کرنا کہ (اگر وہ چیزیں تمہیں دی جائیں تو) بجز اس کے کہ (لیتے وقت) آنکھیں بند کرلو ان کو کبھی نہ لو۔ اور جان رکھو کہ خدا بےپروا (اور) قابل ستائش ہے');
INSERT INTO "jalandhari" VALUES(2,268,'2:268','(اور دیکھنا) شیطان (کا کہنا نہ ماننا وہ) تمہیں تنگ دستی کا خوف دلاتا اور بےحیائی کے کام کر نے کو کہتا ہے۔ اور خدا تم سے اپنی بخشش اور رحمت کا وعدہ کرتا ہے۔ اور خدا بڑی کشائش والا (اور) سب کچھ جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(2,269,'2:269','وہ جس کو چاہتا ہے دانائی بخشتا ہے۔ اور جس کو دانائی ملی بےشک اس کو بڑی نعمت ملی۔ اور نصیحت تو وہی لوگ قبول کرتے ہیں جو عقلمند ہیں');
INSERT INTO "jalandhari" VALUES(2,270,'2:270','اور تم (خدا کی راہ میں) جس طرح کا خرچ کرو یا کوئی نذر مانو خدا اس کو جانتا ہے اور ظالموں کا کوئی مددگار نہیں');
INSERT INTO "jalandhari" VALUES(2,271,'2:271','اگر تم خیرات ظاہر دو تو وہ بھی خوب ہے اور اگر پوشیدہ دو اور دو بھی اہل حاجت کو تو وہ خوب تر ہے اور (اس طرح کا دینا) تمہارے گناہوں کو بھی دور کردے گا۔ اور خدا کو تمہارے سب کاموں کی خبر ہے');
INSERT INTO "jalandhari" VALUES(2,272,'2:272','(اے محمدصلى الله عليه وسلم) تم ان لوگوں کی ہدایت کے ذمہ دار نہیں ہو بلکہ خدا ہی جس کو چاہتا ہے ہدایت بخشتا ہے۔ اور (مومنو) تم جو مال خرچ کرو گے تو اس کا فائدہ تمہیں کو ہے اور تم جو خرچ کرو گے خدا کی خوشنودی کے لئے کرو گے۔ اور جو مال تم خرچ کرو گے وہ تمہیں پورا پورا دے دیا جائے گا اور تمہارا کچھ نقصان نہیں کیا جائے گا،');
INSERT INTO "jalandhari" VALUES(2,273,'2:273','(اور ہاں تم جو خرچ کرو گے تو) ان حاجتمندوں کے لئے جو خدا کی راہ میں رکے بیٹھے ہیں اور ملک میں کسی طرف جانے کی طاقت نہیں رکھتے (اور مانگنے سے عار رکھتے ہیں) یہاں تک کہ نہ مانگنے کی وجہ سے ناواقف شخص ان کو غنی خیال کرتا ہے اور تم قیافے سے ان کو صاف پہچان لو (کہ حاجتمند ہیں اور شرم کے سبب) لوگوں سے (منہ پھوڑ کر اور) لپٹ کر نہیں مانگ سکتے اور تم جو مال خرچ کرو گے کچھ شک نہیں کہ خدا اس کو جانتا ہے');
INSERT INTO "jalandhari" VALUES(2,274,'2:274','جو لوگ اپنا مال رات اور دن اور پوشیدہ اور ظاہر (راہ خدا میں) خرچ کرتے رہتے ہیں ان کا صلہ پروردگار کے پاس ہے اور ان کو (قیامت کے دن) نہ کسی طرح کا خوف ہوگا اور نہ غم');
INSERT INTO "jalandhari" VALUES(2,275,'2:275','جو لوگ سود کھاتے ہیں وہ (قبروں سے) اس طرح (حواس باختہ) اٹھیں گے جیسے کسی کو جن نے لپٹ کر دیوانہ بنا دیا ہو یہ اس لئے کہ وہ کہتے ہیں کہ سودا بیچنا بھی تو (نفع کے لحاظ سے) ویسا ہی ہے جیسے سود (لینا) حالانکہ سودے کو خدا نے حلال کیا ہے اور سود کو حرام۔ تو جس شخص کے پاس خدا کی نصیحت پہنچی اور وہ (سود لینے سے) باز آگیا تو جو پہلے ہوچکا وہ اس کا۔ اور (قیامت میں) اس کا معاملہ خدا کے سپرد اور جو پھر لینے لگا تو ایسے لوگ دوزخی ہیں کہ ہمیشہ دوزخ میں (جلتے) رہیں گے');
INSERT INTO "jalandhari" VALUES(2,276,'2:276','خدا سود کو نابود (یعنی بےبرکت) کرتا اور خیرات (کی برکت) کو بڑھاتا ہے اور خدا کسی ناشکرے گنہگار کو دوست نہیں رکھتا');
INSERT INTO "jalandhari" VALUES(2,277,'2:277','جو لوگ ایمان لائے اور نیک عمل کرتے اور نماز پڑھتے اور زکوٰة دیتے رہے ان کو ان کے کاموں کا صلہ خدا کے ہاں ملے گا اور (قیامت کے دن) ان کو نہ کچھ خوف ہوا اور نہ وہ غمناک ہوں گے');
INSERT INTO "jalandhari" VALUES(2,278,'2:278','مومنو! خدا سے ڈرو اور اگر ایمان رکھتے ہو تو جتنا سود باقی رہ گیا ہے اس کو چھوڑ دو');
INSERT INTO "jalandhari" VALUES(2,279,'2:279','اگر ایسا نہ کرو گے تو خبردار ہوجاؤ (کہ تم) خدا اور رسول سے جنگ کرنے کے لئے (تیار ہوتے ہو) اور اگر توبہ کرلو گے (اور سود چھوڑ دو گے) تو تم کو اپنی اصل رقم لینے کا حق ہے جس میں نہ اوروں کا نقصان اور تمہارا نقصان');
INSERT INTO "jalandhari" VALUES(2,280,'2:280','اور اگر قرض لینے والا تنگ دست ہو تو (اسے) کشائش (کے حاصل ہونے) تک مہلت (دو) اور اگر (زر قرض) بخش ہی دو توتمہارے لئے زیادہ اچھا ہے بشرطیکہ سمجھو');
INSERT INTO "jalandhari" VALUES(2,281,'2:281','اور اس دن سے ڈرو جب کہ تم خدا کے حضور میں لوٹ کر جاؤ گے اور ہر شخص اپنے اعمال کا پورا پورا بدلہ پائے گا۔ اور کسی کا کچھ نقصان نہ ہوگا');
INSERT INTO "jalandhari" VALUES(2,282,'2:282','مومنو! جب تم آپس میں کسی میعاد معین کے لئے قرض کا معاملہ کرنے لگو تو اس کو لکھ لیا کرو اور لکھنے والا تم میں (کسی کا نقصان نہ کرے بلکہ) انصاف سے لکھے نیز لکھنے والا جیسا اسے خدا نے سکھایا ہے لکھنے سے انکار بھی نہ کرے اور دستاویز لکھ دے۔ اور جو شخص قرض لے وہی (دستاویز کا) مضمون بول کر لکھوائے اور خدا سے کہ اس کا مالک ہے خوف کرے اور زر قرض میں سے کچھ کم نہ لکھوائے۔ اور اگر قرض لینے والا بےعقل یا ضعیف ہو یا مضمون لکھوانے کی قابلیت نہ رکھتا ہو تو جو اس کا ولی ہو وہ انصاف کے ساتھ مضمون لکھوائے۔ اور اپنے میں سے دو مردوں کو (ایسے معاملے کے) گواہ کرلیا کرو۔ اور اگر دو مرد نہ ہوں تو ایک مرد اور دو عورتیں جن کو تم گواہ پسند کرو (کافی ہیں) کہ اگر ان میں سے ایک بھول جائے گی تو دوسری اسے یاد دلادے گی۔ اور جب گواہ (گواہی کے لئے طلب کئے جائیں تو انکار نہ کریں۔ اور قرض تھوڑا ہو یا بہت اس (کی دستاویز) کے لکھنے میں کاہلی نہ کرنا۔ یہ بات خدا کے نزدیک نہایت قرین انصاف ہے اور شہادت کے لئے بھی یہ بہت درست طریقہ ہے۔ اس سے تمہیں کسی طرح کا شک وہ شبہ بھی نہیں پڑے گا۔ ہاں اگر سودا دست بدست ہو جو تم آپس میں لیتے دیتے ہو تو اگر (ایسے معاملے کی) دستاویز نہ لکھوتو تم پر کچھ گناہ نہیں۔ اور جب خرید وفروخت کیا کرو تو بھی گواہ کرلیا کرو۔ اور کاتب دستاویز اور گواہ (معاملہ کرنے والوں کا) کسی طرح نقصان نہ کریں۔ اگر تم (لوگ) ایسا کرو تو یہ تمہارے لئے گناہ کی بات ہے۔ اور خدا سے ڈرو اور (دیکھو کہ) وہ تم کو (کیسی مفید باتیں) سکھاتا ہے اور خدا ہر چیز سے واقف ہے');
INSERT INTO "jalandhari" VALUES(2,283,'2:283','اور اگر تم سفر پر ہواور (دستاویز) لکھنے والا مل نہ سکے تو (کوئی چیز) رہن یا قبضہ رکھ کر (قرض لے لو) اور اگر کوئی کسی کو امین سمجھے (یعنی رہن کے بغیر قرض دیدے) تو امانتدار کو چاہیئے کہ صاحب امانت کی امانت ادا کردے اور خدا سے جو اس کا پروردگار ہے ڈرے۔اور (دیکھنا) شہادت کو مت چھپانا۔ جو اس کو چھپائے گا وہ دل کا گنہگار ہوگا۔ اور خدا تمہارے سب کاموں سے واقف ہے');
INSERT INTO "jalandhari" VALUES(2,284,'2:284','جو کچھ آسمانوں میں اور جو کچھ زمین میں ہے سب خدا ہی کا ہے۔ تم اپنے دلوں کی بات کو ظاہر کرو گے تو یا چھپاؤ گے تو خدا تم سے اس کا حساب لے گا پھر وہ جسے چاہے مغفرت کرے اور جسے چاہے عذاب دے۔ اور خدا ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(2,285,'2:285','رسول (خدا) اس کتاب پر جو ان کے پروردگار کی طرف سے ان پر نازل ہوئی ایمان رکھتے ہیں اور مومن بھی۔ سب خدا پر اور اس کے فرشتوں پر اور اس کی کتابوں پر اور اس کے پیغمبروں پر ایمان رکھتے ہیں (اورکہتے ہیں کہ) ہم اس کے پیغمبروں سے کسی میں کچھ فرق نہیں کرتے اور وہ (خدا سے) عرض کرتے ہیں کہ ہم نے (تیرا حکم) سنا اور قبول کیا۔ اے پروردگار ہم تیری بخشش مانگتے ہیں اور تیری ہی طرف لوٹ کر جانا ہے');
INSERT INTO "jalandhari" VALUES(2,286,'2:286','خدا کسی شخص کو اس کی طاقت سے زیادہ تکلیف نہیں دیتا۔ اچھے کام کرے گا تو اس کو ان کا فائدہ ملے گا برے کرے گا تو اسے ان کا نقصان پہنچے گا۔ اے پروردگار اگر ہم سے بھول یا چوک ہوگئی ہو تو ہم سے مؤاخذہ نہ کیجیو۔ اے پروردگار ہم پر ایسا بوجھ نہ ڈالیو جیسا تو نے ہم سے پہلے لوگوں پر ڈالا تھا۔ اے پروردگار جتنا بوجھ اٹھانے کی ہم میں طاقت نہیں اتنا ہمارے سر پر نہ رکھیو۔ اور (اے پروردگار) ہمارے گناہوں سے درگزر کر اور ہمیں بخش دے۔ اور ہم پر رحم فرما۔ تو ہی ہمارا مالک ہے اور ہم کو کافروں پر غالب فرما');
INSERT INTO "jalandhari" VALUES(3,1,'3:1','الم');
INSERT INTO "jalandhari" VALUES(3,2,'3:2','خدا (جو معبود برحق ہے) اس کے سوا کوئی عبادت کے لائق نہیں ہمیشہ زندہ رہنے والا');
INSERT INTO "jalandhari" VALUES(3,3,'3:3','اس نے (اے محمدصلى الله عليه وسلم) تم پر سچی کتاب نازل کی جو پہلی (آسمانی) کتابوں کی تصدیق کرتی ہے اور اسی نے تورات اور انجیل نازل کی');
INSERT INTO "jalandhari" VALUES(3,4,'3:4','(یعنی) لوگوں کی ہدایت کے لیے پہلے (تورات اور انجیل اتاری) اور (پھر قرآن جو حق اور باطل کو) الگ الگ کر دینے والا (ہے) نازل کیا جو لوگ خدا کی آیتوں کا انکار کرتے ہیں ان کو سخت عذاب ہوگا اور خدا زبردست (اور) بدلہ لینے والا ہے');
INSERT INTO "jalandhari" VALUES(3,5,'3:5','خدا (ایسا خبیر وبصیر ہے کہ) کوئی چیز اس سے پوشیدہ نہیں نہ زمین میں اور نہ آسمان میں');
INSERT INTO "jalandhari" VALUES(3,6,'3:6','وہی تو ہے جو (ماں کے پیٹ میں) جیسی چاہتا ہے تمہاری صورتیں بناتا ہے اس غالب حکمت والے کے سوا کوئی عبادت کے لائق نہیں');
INSERT INTO "jalandhari" VALUES(3,7,'3:7','وہی تو ہے جس نے تم پر کتاب نازل کی جس کی بعض آیتیں محکم ہیں (اور) وہی اصل کتاب ہیں اور بعض متشابہ ہیں تو جن لوگوں کے دلوں میں کجی ہے وہ متشابہات کا اتباع کرتے ہیں تاکہ فتنہ برپا کریں اور مراد اصلی کا پتہ لگائیں حالانکہ مراد اصلی خدا کے سوا کوئی نہیں جانتا اور جو لوگ علم میں دست گاہ کامل رکھتے ہیں وہ یہ کہتے ہیں کہ ہم ان پر ایمان لائے یہ سب ہمارے پروردگار کی طرف سے ہیں اور نصیحت تو عقل مند ہی قبول کرتے ہیں');
INSERT INTO "jalandhari" VALUES(3,8,'3:8','اے پروردگار جب تو نے ہمیں ہدایت بخشی ہے تو اس کے بعد ہمارے دلوں میں کجی نہ پیدا کر دیجیو اور ہمیں اپنے ہاں سے نعمت عطا فرما تو تو بڑا عطا فرمانے والا ہے');
INSERT INTO "jalandhari" VALUES(3,9,'3:9','اے پروردگار! تو اس روز جس (کے آنے) میں کچھ بھی شک نہیں سب لوگوں کو (اپنے حضور میں) جمع کرلے گا بے شک خدا خلاف وعدہ نہیں کرتا');
INSERT INTO "jalandhari" VALUES(3,10,'3:10','جو لوگ کافر ہوئے (اس دن) نہ تو ان کا مال ہی خدا (کے عذاب) سے ان کو بچا سکے گا اور نہ ان کی اولاد ہی (کچھ کام آئے گی) اور یہ لوگ آتش (جہنم) کا ایندھن ہوں گے');
INSERT INTO "jalandhari" VALUES(3,11,'3:11','ان کا حال بھی فرعونیوں اور ان سے پہلے لوگوں کا سا ہوگا جنہوں نے ہماری آیتوں کی تکذیب کی تھی تو خدا نے ان کو ان کے گناہوں کے سبب (عذاب میں) پکڑ لیا تھا اور خدا سخت عذاب کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(3,12,'3:12','(اے پیغمبر) کافروں سے کہدو کہ تم (دنیا میں بھی) عنقریب مغلوب ہو جاؤ گے اور (آخرت میں) جہنم کی طرف ہانکے جاؤ گے اور وہ بری جگہ ہے');
INSERT INTO "jalandhari" VALUES(3,13,'3:13','تمہارے لیے دو گروہوں میں جو (جنگ بدر کے دن) آپس میں بھڑ گئے (قدرت خدا کی عظیم الشان) نشانی تھی ایک گروہ (مسلمانوں کا تھا وہ) خدا کی راہ میں لڑ رہا تھا اور دوسرا گروہ (کافروں کا تھا وہ) ان کو اپنی آنکھوں سے اپنے سے دگنا مشاہدہ کر رہا تھا اور خدا اپنی نصرت سے جس کو چاہتا ہے مدد دیتا ہے جو اہل بصارت ہیں ان کے لیے اس (واقعے) میں بڑی عبرت ہے');
INSERT INTO "jalandhari" VALUES(3,14,'3:14','لوگوں کو ان کی خواہشوں کی چیزیں یعنی عورتیں اور بیٹے اور سونے اور چاندی کے بڑے بڑے ڈھیر اور نشان لگے ہوئے گھوڑے اور مویشی اور کھیتی بڑی زینت دار معلوم ہوتی ہیں (مگر) یہ سب دنیا ہی کی زندگی کے سامان ہیں اور خدا کے پاس بہت اچھا ٹھکانا ہے');
INSERT INTO "jalandhari" VALUES(3,15,'3:15','(اے پیغمبر ان سے) کہو کہ بھلا میں تم کو ایسی چیز بتاؤں جو ان چیزوں سے کہیں اچھی ہو (سنو) جو لوگ پرہیزگار ہیں ان کے لیے خدا کے ہاں باغات (بہشت) ہیں جن کے نیچے نہریں بہہ رہی ہیں ان میں وہ ہمیشہ رہیں گے اور پاکیزہ عورتیں ہیں اور (سب سے بڑھ کر) خدا کی خوشنودی اور خدا (اپنے نیک) بندوں کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(3,16,'3:16','جو خدا سے التجا کرتے ہیں کہ اے پروردگار ہم ایمان لے آئے سو ہم کو ہمارے گناہ معاف فرما اور دوزخ کے عذاب سے محفوظ رکھ');
INSERT INTO "jalandhari" VALUES(3,17,'3:17','یہ وہ لوگ ہیں جو (مشکلات میں) صبر کرتے اور سچ بولتے اور عبادت میں لگے رہتے اور (راہ خدا میں) خرچ کرتے اور اوقات سحر میں گناہوں کی معافی مانگا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(3,18,'3:18','خدا تو اس بات کی گواہی دیتا ہے کہ اس کے سوا کوئی معبود نہیں اور فرشتے اور علم والے لوگ جو انصاف پر قائم ہیں وہ بھی (گواہی دیتے ہیں کہ) اس غالب حکمت والے کے سوا کوئی عبادت کے لائق نہیں');
INSERT INTO "jalandhari" VALUES(3,19,'3:19','دین تو خدا کے نزدیک اسلام ہے اور اہل کتاب نے جو (اس دین سے) اختلاف کیا تو علم ہونے کے بعد آپس کی ضد سے کیا اور جو شخص خدا کی آیتوں کو نہ مانے تو خدا جلد حساب لینے والا (اور سزا دینے والا) ہے');
INSERT INTO "jalandhari" VALUES(3,20,'3:20','اے پیغمبر اگر یہ لوگ تم سے جھگڑنے لگیں تو کہنا کہ میں اور میرے پیرو تو خدا کے فرمانبردار ہو چکے اور اہل کتاب اور ان پڑھ لوگوں سے کہو کہ کیا تم بھی (خدا کے فرمانبردار بنتے ہو) اور اسلام لاتے ہو؟ اگر یہ لوگ اسلام لے آئیں تو بے شک ہدایت پالیں اور اگر (تمہارا کہا) نہ مانیں تو تمہارا کام صرف خدا کا پیغام پہنچا دینا ہے اور خدا (اپنے) بندوں کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(3,21,'3:21','جو لوگ خدا کی آیتوں کو نہیں مانتے اور انبیاء کو ناحق قتل کرتے رہے ہیں اور جو انصاف (کرنے) کا حکم دیتے ہیں انہیں بھی مار ڈالتے ہیں ان کو دکھ دینے والے عذاب کی خوشخبری سنا دو');
INSERT INTO "jalandhari" VALUES(3,22,'3:22','یہ ایسے لوگ ہیں جن کے اعمال دنیا اور آخرت دونوں میں برباد ہیں اور ان کا کوئی مددگار نہیں (ہوگا)');
INSERT INTO "jalandhari" VALUES(3,23,'3:23','بھلا تم نے ان لوگوں کو نہیں دیکھا جن کو کتاب (خدا یعنی تورات سے) بہرہ دیا گیا اور وہ (اس) کتاب الله کی طرف بلائے جاتے ہیں تاکہ وہ (ان کے تنازعات کا) ان میں فیصلہ کر دے تو ایک فریق ان میں سے کج ادائی کے ساتھ منہ پھیر لیتا ہے');
INSERT INTO "jalandhari" VALUES(3,24,'3:24','یہ اس لیے کہ یہ اس بات کے قائل ہیں کہ (دوزخ کی) آگ ہمیں چند روز کے سوا چھو ہی نہیں سکے گی اور جو کچھ یہ دین کے بارے میں بہتان باندھتے رہے ہیں اس نے ان کو دھوکے میں ڈال رکھا ہے');
INSERT INTO "jalandhari" VALUES(3,25,'3:25','تو اس وقت کیا حال ہوگا جب ہم ان کو جمع کریں گے (یعنی) اس روز جس (کے آنے) میں کچھ بھی شک نہیں اور ہر نفس اپنے اعمال کا پورا پورا بدلہ پائے گا اور ان پر ظلم نہیں کیا جائے گا');
INSERT INTO "jalandhari" VALUES(3,26,'3:26','کہو کہ اے خدا (اے) بادشاہی کے مالک تو جس کو چاہے بادشاہی بخشے اور جس سے چاہے بادشاہی چھین لے اور جس کو چاہے عزت دے اور جسے چاہے ذلیل کرے ہر طرح کی بھلائی تیرے ہی ہاتھ ہے اور بے شک تو ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(3,27,'3:27','تو ہی رات کو دن میں داخل کرتا اور تو ہی دن کو رات میں داخل کرتا ہے تو ہی بے جان سے جاندار پیدا کرتا ہے اور تو ہی جاندار سے بے جان پیدا کرتا ہے اور توہی جس کو چاہتا ہے بے شمار رزق بخشتا ہے');
INSERT INTO "jalandhari" VALUES(3,28,'3:28','مؤمنوں کو چاہئے کہ مؤمنوں کے سوا کافروں کو دوست نہ بنائیں اور جو ایسا کرے گا اس سے خدا کا کچھ (عہد) نہیں ہاں اگر اس طریق سے تم ان (کے شر) سے بچاؤ کی صورت پیدا کرو (تو مضائقہ نہیں) اور خدا تم کو اپنے (غضب) سے ڈراتا ہے اور خدا ہی کی طرف (تم کو) لوٹ کر جانا ہے');
INSERT INTO "jalandhari" VALUES(3,29,'3:29','(اے پیغمبر لوگوں سے) کہہ دو کہ کوئی بات تم اپنے دلوں میں مخفی رکھو یا اسے ظاہر کرو خدا اس کو جانتا ہے اور جو کچھ آسمانوں میں اور جو کچھ زمین میں ہے اس کو سب کی خبر ہے اور وہ ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(3,30,'3:30','جس دن ہر شخص اپنے اعمال کی نیکی کو موجود پالے گا اور ان کی برائی کو بھی (دیکھ لے گا) تو آرزو کرے گا کہ اے کاش اس میں اور اس برائی میں دور کی مسافت ہو جاتی اور خدا تم کو اپنے (غضب) سے ڈراتا ہے اور خدا اپنے بندوں پر نہایت مہربان ہے');
INSERT INTO "jalandhari" VALUES(3,31,'3:31','(اے پیغمبر لوگوں سے) کہہ دو کہ اگر تم خدا کو دوست رکھتے ہو تو میری پیروی کرو خدا بھی تمہیں دوست رکھے گا اور تمہارے گناہ معاف کر دے گا اور خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(3,32,'3:32','کہہ دو کہ خدا اور اس کے رسول کا حکم مانو اگر نہ مانیں تو خدا بھی کافروں کو دوست نہیں رکھتا');
INSERT INTO "jalandhari" VALUES(3,33,'3:33','خدا نے آدم اور نوح اور خاندان ابراہیم اور خاندان عمران کو تمام جہان کے لوگوں میں منتخب فرمایا تھا');
INSERT INTO "jalandhari" VALUES(3,34,'3:34','ان میں سے بعض بعض کی اولاد تھے اور خدا سننے والا (اور) جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(3,35,'3:35','(وہ وقت یاد کرنے کے لائق ہے) جب عمران کی بیوی نے کہا کہ اے پروردگار جو (بچہ) میرے پیٹ میں ہے میں اس کو تیری نذر کرتی ہوں اسے دنیا کے کاموں سے آزاد رکھوں گی تو (اسے) میری طرف سے قبول فرما توتو سننے والا (اور) جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(3,36,'3:36','جب ان کے ہاں بچہ پیدا ہوا اور جو کچھ ان کے ہاں پیدا ہوا تھا خدا کو خوب معلوم تھا تو کہنے لگیں کہ پروردگار! میرے تو لڑکی ہوئی ہے اور (نذر کے لیے) لڑکا (موزوں تھا کہ وہ) لڑکی کی طرح (ناتواں) نہیں ہوتا اور میں نے اس کا نام مریم رکھا ہے اور میں اس کو اور اس کی اولاد کو شیطان مردود سے تیری پناہ میں دیتی ہوں');
INSERT INTO "jalandhari" VALUES(3,37,'3:37','تو پروردگار نے اس کو پسندیدگی کے ساتھ قبول فرمایا اور اسے اچھی طرح پرورش کیا اور زکریا کو اس کا متکفل بنایا زکریا جب کبھی عبادت گاہ میں اس کے پاس جاتے تو اس کے پاس کھانا پاتے (یہ کیفیت دیکھ کر ایک دن مریم سے) پوچھنے لگے کہ مریم یہ کھانا تمہارے پاس کہاں سے آتا ہے وہ بولیں خدا کے ہاں سے (آتا ہے) بیشک خدا جسے چاہتا ہے بے شمار رزق دیتا ہے');
INSERT INTO "jalandhari" VALUES(3,38,'3:38','اس وقت زکریا نے اپنے پروردگار سے دعا کی (اور) کہا کہ پروردگار مجھے اپنی جناب سے اولاد صالح عطا فرما تو بے شک دعا سننے (اور قبول کرنے) والا ہے');
INSERT INTO "jalandhari" VALUES(3,39,'3:39','وہ ابھی عبادت گاہ میں کھڑے نماز ہی پڑھ رہے تھے کہ فرشتوں نے آواز دی کہ (زکریا) خدا تمہیں یحییٰ کی بشارت دیتا ہے جو خدا کے فیض یعنی (عیسیٰ) کی تصدیق کریں گے اور سردار ہوں گے اور عورتوں سے رغبت نہ رکھنے والے اور (خدا کے) پیغمبر (یعنی) نیکو کاروں میں ہوں گے');
INSERT INTO "jalandhari" VALUES(3,40,'3:40','زکریا نے کہا اے پروردگار میرے ہاں لڑکا کیونکر پیدا ہوگا کہ میں تو بڈھا ہوگیا ہوں اور میری بیوی بانجھ ہے خدا نے فرمایا اسی طرح خدا جو چاہتا ہے کرتا ہے');
INSERT INTO "jalandhari" VALUES(3,41,'3:41','زکریا نے کہا کہ پروردگار (میرے لیے) کوئی نشانی مقرر فرما خدا نے فرمایا نشانی یہ ہے کہ تم لوگوں سے تین دن اشارے کے سوا بات نہ کر سکو گے تو (ان دنوں میں) اپنے پروردگار کی کثرت سے یاد اور صبح و شام اس کی تسبیح کرنا');
INSERT INTO "jalandhari" VALUES(3,42,'3:42','اور جب فرشتوں نے (مریم سے) کہا کہ مریم! خدا نے تم کو برگزیدہ کیا ہے اور پاک بنایا ہے اور جہان کی عورتوں میں منتخب کیا ہے');
INSERT INTO "jalandhari" VALUES(3,43,'3:43','مریم اپنے پروردگار کی فرمانبرداری کرنا اور سجدہ کرنا اور رکوع کرنے والوں کے ساتھ رکوع کرنا');
INSERT INTO "jalandhari" VALUES(3,44,'3:44','(اے محمدصلى الله عليه وسلم) یہ باتیں اخبار غیب میں سے ہیں جو ہم تمہارے پاس بھیجتے ہیں اور جب وہ لوگ اپنے قلم (بطور قرعہ) ڈال رہے تھے کہ مریم کا متکفل کون بنے تو تم ان کے پاس نہیں تھے اور نہ اس وقت ہی ان کے پاس تھے جب وہ آپس میں جھگڑ رہے تھے');
INSERT INTO "jalandhari" VALUES(3,45,'3:45','(وہ وقت بھی یاد کرنے کے لائق ہے) جب فرشتوں نے (مریم سے کہا) کہ مریم خدا تم کو اپنی طرف سے ایک فیض کی بشارت دیتا ہے جس کا نام مسیح (اور مشہور) عیسیٰ ابن مریم ہوگا (اور) جو دنیا اور آخرت میں باآبرو اور (خدا کے) خاصوں میں سے ہوگا');
INSERT INTO "jalandhari" VALUES(3,46,'3:46','اور ماں کی گود میں اور بڑی عمر کا ہو کر (دونوں حالتوں میں) لوگوں سے (یکساں) گفتگو کرے گا اور نیکو کاروں میں ہوگا');
INSERT INTO "jalandhari" VALUES(3,47,'3:47','مریم نے کہا پروردگار میرے ہاں بچہ کیونکر ہوگا کہ کسی انسان نے مجھے ہاتھ تک تو لگایا نہیں فرمایا کہ خدا اسی طرح جو چاہتا ہے پیدا کرتا ہے جب وہ کوئی کام کرنا چاہتا ہے تو ارشاد فرما دیتا ہے کہ ہوجا تو وہ ہو جاتا ہے');
INSERT INTO "jalandhari" VALUES(3,48,'3:48','اور وہ انہیں لکھنا (پڑھنا) اور دانائی اور تورات اور انجیل سکھائے گا');
INSERT INTO "jalandhari" VALUES(3,49,'3:49','اور (عیسیٰ) بنی اسرائیل کی طرف پیغمبر (ہو کر جائیں گے اور کہیں گے) کہ میں تمہارے پروردگار کی طرف سے نشانی لے کر آیا ہوں وہ یہ کہ تمہارے سامنے مٹی کی مورت بشکل پرند بناتا ہوں پھر اس میں پھونک مارتا ہوں تو وہ خدا کے حکم سے (سچ مچ) جانور ہو جاتا ہے اور اندھے اور ابرص کو تندرست کر دیتا ہوں اور خدا کے حکم سے مردے میں جان ڈال دیتا ہوں اور جو کچھ تم کھا کر آتے ہو اور جو اپنے گھروں میں جمع کر رکھتے ہو سب تم کو بتا دیتا ہوں اگر تم صاحب ایمان ہو تو ان باتوں میں تمہارے لیے (قدرت خدا کی) نشانی ہے');
INSERT INTO "jalandhari" VALUES(3,50,'3:50','اور مجھ سے پہلے جو تورات (نازل ہوئی) تھی اس کی تصدیق بھی کرتا ہوں اور (میں) اس لیے بھی (آیا ہوں) کہ بعض چیزیں جو تم پر حرام تھیں ان کو تمہارے لیے حلال کر دوں اور میں تو تمہارے پروردگار کی طرف سے نشانی لے کر آیا ہوں تو خدا سے ڈرو اور میرا کہا مانو');
INSERT INTO "jalandhari" VALUES(3,51,'3:51','کچھ شک نہیں کہ خدا ہی میرا اور تمہارا پروردگار ہے تو اسی کی عبادت کرو یہی سیدھا رستہ ہے');
INSERT INTO "jalandhari" VALUES(3,52,'3:52','جب عیسیٰؑ نے ان کی طرف سے نافرمانی اور (نیت قتل) دیکھی تو کہنے لگے کہ کوئی ہے جو خدا کا طرف دار اور میرا مددگار ہو حواری بولے کہ ہم خدا کے (طرفدار اور آپ کے) مددگار ہیں ہم خدا پر ایمان لائے اور آپ گواہ رہیں کہ ہم فرمانبردار ہیں');
INSERT INTO "jalandhari" VALUES(3,53,'3:53','اے پروردگار جو (کتاب) تو نے نازل فرمائی ہے ہم اس پر ایمان لے آئے اور (تیرے) پیغمبر کے متبع ہو چکے تو ہم کو ماننے والوں میں لکھ رکھ');
INSERT INTO "jalandhari" VALUES(3,54,'3:54','اور وہ (یعنی یہود قتل عیسیٰ کے بارے میں ایک) چال چلے اور خدا بھی (عیسیٰ کو بچانے کے لیے) چال چلا اور خدا خوب چال چلنے والا ہے');
INSERT INTO "jalandhari" VALUES(3,55,'3:55','اس وقت خدا نے فرمایا کہ عیسیٰ! میں تمہاری دنیا میں رہنے کی مدت پوری کرکے تم کو اپنی طرف اٹھا لوں گا اور تمہیں کافروں (کی صحبت) سے پاک کر دوں گا اور جو لوگ تمہاری پیروی کریں گے ان کو کافروں پر قیامت تک فائق (وغالب) رکھوں گا پھر تم سب میرے پاس لوٹ کر آؤ گے تو جن باتوں میں تم اختلاف کرتے تھے اس دن تم میں ان کا فیصلہ کردوں گا');
INSERT INTO "jalandhari" VALUES(3,56,'3:56','یعنی جو کافر ہوئے ان کو دنیا اور آخرت (دونوں) میں سخت عذاب دوں گا اور ان کا کوئی مددگار نہ ہوگا');
INSERT INTO "jalandhari" VALUES(3,57,'3:57','اور جو ایمان لائے اور نیک عمل کرتے رہے ان کو خدا پورا پورا صلہ دے گا اور خدا ظالموں کو دوست نہیں رکھتا');
INSERT INTO "jalandhari" VALUES(3,58,'3:58','(اے محمدصلى الله عليه وسلم) یہ ہم تم کو (خدا کی) آیتیں اور حکمت بھری نصیحتیں پڑھ پڑھ کر سناتے ہیں');
INSERT INTO "jalandhari" VALUES(3,59,'3:59','عیسیٰ کا حال خدا کے نزدیک آدم کا سا ہے کہ اس نے (پہلے) مٹی سے ان کا قالب بنایا پھر فرمایا کہ (انسان) ہو جا تو وہ (انسان) ہو گئے');
INSERT INTO "jalandhari" VALUES(3,60,'3:60','(یہ بات) تمہارے پروردگار کی طرف سے حق ہے تو تم ہرگز شک کرنے والوں میں نہ ہونا');
INSERT INTO "jalandhari" VALUES(3,61,'3:61','پھر اگر یہ لوگ عیسیٰ کے بارے میں تم سے جھگڑا کریں اور تم کو حقیقت الحال تو معلوم ہو ہی چلی ہے تو ان سے کہنا کہ آؤ ہم اپنے بیٹوں اور عورتوں کو بلائیں تم اپنے بیٹوں اور عورتوں کو بلاؤ اور ہم خود بھی آئیں اور تم خود بھی آؤ پھر دونوں فریق (خدا سے) دعا والتجا کریں اور جھوٹوں پر خدا کی لعنت بھیجیں');
INSERT INTO "jalandhari" VALUES(3,62,'3:62','یہ تمام بیانات صحیح ہیں اور خدا کے سوا کوئی معبود نہیں اور بیشک خدا غالب اور صاحبِ حکمت ہے');
INSERT INTO "jalandhari" VALUES(3,63,'3:63','تو اگر یہ لوگ پھر جائیں تو خدا مفسدوں کو خوب جانتا ہے');
INSERT INTO "jalandhari" VALUES(3,64,'3:64','کہہ دو کہ اے اہل کتاب جو بات ہمارے اور تمہارے دونوں کے درمیان یکساں (تسلیم کی گئی) ہے اس کی طرف آؤ وہ یہ کہ خدا کے سوا ہم کسی کی عبادت نہ کریں اور اس کے ساتھ کسی چیز کو شریک نہ بنائیں اور ہم میں سے کوئی کسی کو خدا کے سوا اپنا کار ساز نہ سمجھے اگر یہ لوگ (اس بات کو) نہ مانیں تو (ان سے) کہہ دو کہ تم گواہ رہو کہ ہم (خدا کے) فرماں بردار ہیں');
INSERT INTO "jalandhari" VALUES(3,65,'3:65','اے اہلِ کتاب تم ابراہیم کے بارے میں کیوں جھگڑتے ہو حالانکہ تورات اور انجیل ان کے بعد اتری ہیں (اور وہ پہلے ہو چکے ہیں) تو کیا تم عقل نہیں رکھتے');
INSERT INTO "jalandhari" VALUES(3,66,'3:66','دیکھو ایسی بات میں تو تم نے جھگڑا کیا ہی تھا جس کا تمہیں کچھ علم تھا بھی مگر ایسی بات میں کیوں جھگڑتے ہو جس کا تمہیں کچھ بھی علم نہیں اور خدا جانتا ہے اور تم نہیں جانتے');
INSERT INTO "jalandhari" VALUES(3,67,'3:67','ابراہیم نہ تو یہودی تھے اور نہ عیسائی بلکہ سب سے بے تعلق ہو کر ایک (خدا) کے ہو رہے تھے اور اسی کے فرماں بردار تھے اور مشرکوں میں نہ تھے');
INSERT INTO "jalandhari" VALUES(3,68,'3:68','ابراہیم سے قرب رکھنے والے تو وہ لوگ ہیں جو ان کی پیروی کرتے ہیں اور پیغمبر (آخرالزمان) اور وہ لوگ جو ایمان لائے ہیں اور خدا مومنوں کا کارساز ہے');
INSERT INTO "jalandhari" VALUES(3,69,'3:69','(اے اہل اسلام) بعضے اہلِ کتاب اس بات کی خواہش رکھتے ہیں کہ تم کو گمراہ کر دیں مگر یہ (تم کو کیا گمراہ کریں گے) اپنے آپ کو ہی گمراہ کر رہے ہیں اور نہیں جانتے');
INSERT INTO "jalandhari" VALUES(3,70,'3:70','اے اہلِ کتاب تم خدا کی آیتوں سے کیوں انکار کرتے ہو اور تم (تورات کو) مانتے تو ہو');
INSERT INTO "jalandhari" VALUES(3,71,'3:71','اے اہلِ کتاب تم سچ کو جھوٹ کے ساتھ خلط ملط کیوں کرتے ہو اور حق کو کیوں چھپاتے ہو اور تم جانتے بھی ہو');
INSERT INTO "jalandhari" VALUES(3,72,'3:72','اور اہلِ کتاب ایک دوسرے سے کہتے ہیں کہ جو (کتاب) مومنوں پر نازل ہوئی ہے اس پر دن کے شروع میں تو ایمان لے آیا کرو اور اس کے آخر میں انکار کر دیا کرو تاکہ وہ (اسلام سے) برگشتہ ہو جائیں');
INSERT INTO "jalandhari" VALUES(3,73,'3:73','اور اپنے دین کے پیرو کے سوا کسی اور کے قائل نہ ہونا (اے پیغمبر) کہہ دو کہ ہدایت تو خدا ہی کی ہدایت ہے (وہ یہ بھی کہتے ہیں) یہ بھی (نہ ماننا) کہ جو چیز تم کو ملی ہے ویسی کسی اور کو ملے گی یا وہ تمہیں خدا کے روبرو قائل معقول کر سکیں گے یہ بھی کہہ دو کہ بزرگی خدا ہی کے ہاتھ میں ہے وہ جسے چاہتا ہے دیتا ہے اور خدا کشائش والا (اور) علم والا ہے');
INSERT INTO "jalandhari" VALUES(3,74,'3:74','وہ اپنی رحمت سے جس کو چاہتا ہے خاص کر لیتا ہے اور خدا بڑے فضل کا مالک ہے');
INSERT INTO "jalandhari" VALUES(3,75,'3:75','اور اہلِ کتاب میں سے کوئی تو ایسا ہے کہ اگر تم اس کے پاس (روپوں کا) ڈھیر امانت رکھ دو تو تم کو (فوراً) واپس دے دے اور کوئی اس طرح کا ہے کہ اگر اس کے پاس ایک دینار بھی امانت رکھو تو جب تک اس کے سر پر ہر وقت کھڑے نہ رہو تمہیں دے ہی نہیں یہ اس لیے کہ وہ کہتے ہیں کہ امیوں کے بارے میں ہم سے مواخذہ نہیں ہوگا یہ خدا پر محض جھوٹ بولتے ہیں اور (اس بات کو) جانتے بھی ہیں');
INSERT INTO "jalandhari" VALUES(3,76,'3:76','ہاں جو شخص اپنے اقرار کو پورا کرے اور (خدا سے) ڈرے تو خدا ڈرنے والوں کو دوست رکھتا ہے');
INSERT INTO "jalandhari" VALUES(3,77,'3:77','جو لوگ خدا کے اقراروں اور اپنی قسموں (کو بیچ ڈالتے ہیں اور ان) کے عوض تھوڑی سی قیمت حاصل کرتے ہیں ان کا آخرت میں کچھ حصہ نہیں ان سے خدا نہ تو کلام کرے گا اور نہ قیامت کے روز ان کی طرف دیکھے گا اور نہ ان کو پاک کرے گا اور ان کو دکھ دینے والا عذاب ہوگا');
INSERT INTO "jalandhari" VALUES(3,78,'3:78','اور ان (اہلِ کتاب) میں بعضے ایسے ہیں کہ کتاب (تورات) کو زبان مروڑ مروڑ کر پڑھتے ہیں تاکہ تم سمجھو کہ جو کچھ وہ پڑھتے ہیں کتاب میں سے ہے حالانکہ وہ کتاب میں سے نہیں ہے اور کہتے ہیں کہ وہ خدا کی طرف سے (نازل ہوا) ہے حالانکہ وہ خدا کی طرف سے نہیں ہوتا اور خدا پر جھوٹ بولتے ہیں اور (یہ بات) جانتے بھی ہیں');
INSERT INTO "jalandhari" VALUES(3,79,'3:79','کسی آدمی کو شایاں نہیں کہ خدا تو اسے کتاب اور حکومت اور نبوت عطا فرمائے اور وہ لوگوں سے کہے کہ خدا کو چھوڑ کر میرے بندے ہو جاؤ بلکہ (اس کو یہ کہنا سزاوار ہے کہ اے اہلِ کتاب) تم (علمائے) ربانی ہو جاؤ کیونکہ تم کتابِ (خدا) پڑھتے پڑھاتے رہتے ہو');
INSERT INTO "jalandhari" VALUES(3,80,'3:80','اور اس کو یہ بھی نہیں کہنا چاہیے کہ تم فرشتوں اور پیغمبروں کو خدا بنالو بھلا جب تم مسلمان ہو چکے تو کیا اسے زیبا ہے کہ تمہیں کافر ہونے کو کہے');
INSERT INTO "jalandhari" VALUES(3,81,'3:81','اور جب خدا نے پیغمبروں سے عہد لیا کہ جب میں تم کو کتاب اور دانائی عطا کروں پھر تمہارے پاس کوئی پیغمبر آئے جو تمہاری کتاب کی تصدیق کرے تو تمھیں ضرور اس پر ایمان لانا ہوگا اور ضرور اس کی مدد کرنی ہوگی اور (عہد لینے کے بعد) پوچھا کہ بھلا تم نے اقرار کیا اور اس اقرار پر میرا ذمہ لیا (یعنی مجھے ضامن ٹہرایا) انہوں نے کہا (ہاں) ہم نے اقرار کیا (خدا نے) فرمایا کہ تم (اس عہد وپیمان کے) گواہ رہو اور میں بھی تمہارے ساتھ گواہ ہوں');
INSERT INTO "jalandhari" VALUES(3,82,'3:82','تو جو اس کے بعد پھر جائیں وہ بد کردار ہیں');
INSERT INTO "jalandhari" VALUES(3,83,'3:83','کیا یہ (کافر) خدا کے دین کے سوا کسی اور دین کے طالب ہیں حالانکہ سب اہلِ آسمان و زمین خوشی یا زبردستی سے خدا کے فرماں بردار ہیں اور اسی کی طرف لوٹ کر جانے والے ہیں');
INSERT INTO "jalandhari" VALUES(3,84,'3:84','کہو کہ ہم خدا پر ایمان لائے اور جو کتاب ہم پر نازل ہوئی اور جو صحیفے ابراہیم اور اسماعیل اور اسحٰق اور یعقوب اور ان کی اولاد پر اترے اور جو کتابیں موسیٰ اور عیسیٰ اور دوسرے انبیاء کو پروردگار کی طرف سے ملیں سب پر ایمان لائے ہم ان پیغمبروں میں سے کسی میں کچھ فرق نہیں کرتے اور ہم اسی (خدائے واحد) کے فرماں بردار ہیں');
INSERT INTO "jalandhari" VALUES(3,85,'3:85','اور جو شخص اسلام کے سوا کسی اور دین کا طالب ہوگا وہ اس سے ہرگز قبول نہیں کیا جائے گا اور ایسا شخص آخرت میں نقصان اٹھانے والوں میں ہوگا');
INSERT INTO "jalandhari" VALUES(3,86,'3:86','خدا ایسے لوگوں کو کیونکر ہدایت دے جو ایمان لانے کے بعد کافر ہوگئے اور (پہلے) اس بات کی گواہی دے چکے کہ یہ پیغمبر برحق ہے اور ان کے پاس دلائل بھی آگئے اور خدا بے انصافوں کو ہدایت نہیں دیتا');
INSERT INTO "jalandhari" VALUES(3,87,'3:87','ان لوگوں کی سزا یہ ہے کہ ان پر خدا کی اور فرشتوں کی اور انسانوں کی سب کی لعنت ہو');
INSERT INTO "jalandhari" VALUES(3,88,'3:88','ہمیشہ اس لعنت میں (گرفتار) رہیں گے ان سے نہ تو عذاب ہلکا کیا جائے گا اور نہ انہیں مہلت دے جائے گی');
INSERT INTO "jalandhari" VALUES(3,89,'3:89','ہاں جنہوں نے اس کے بعد توبہ کی اور اپنی حالت درست کر لی تو خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(3,90,'3:90','جو لوگ ایمان لانے کے بعد کافر ہو گئے پھر کفر میں بڑھتے گئے ایسوں کی توبہ ہرگز قبول نہ ہوگی اور یہ لوگ گمراہ ہیں');
INSERT INTO "jalandhari" VALUES(3,91,'3:91','جو لوگ کافر ہوئے اور کفر ہی کی حالت میں مر گئے وہ اگر (نجات حاصل کرنی چاہیں اور) بدلے میں زمین بھر کر سونا دیں تو ہرگز قبول نہ کیا جائے گا ان لوگوں کو دکھ دینے والا عذاب ہو گا اور ان کی کوئی مدد نہیں کرے گا');
INSERT INTO "jalandhari" VALUES(3,92,'3:92','(مومنو!) جب تک تم ان چیزوں میں سے جو تمھیں عزیز ہیں (راہِ خدا میں) صرف نہ کرو گے کبھی نیکی حاصل نہ کر سکو گے اور جو چیز تم صرف کرو گے خدا اس کو جانتا ہے');
INSERT INTO "jalandhari" VALUES(3,93,'3:93','بنی اسرائیل کے لیے (تورات کے نازل ہونے سے) پہلے کھانے کی تمام چیزیں حلال تھیں بجز ان کے جو یعقوب نے خود اپنے اوپر حرام کر لی تھیں کہہ دو کہ اگر سچے ہو تو تورات لاؤ اور اسے پڑھو (یعنی دلیل پیش کرو)');
INSERT INTO "jalandhari" VALUES(3,94,'3:94','جو اس کے بعد بھی خدا پر جھوٹے افترا کریں تو ایسے لوگ ہی بےانصاف ہیں');
INSERT INTO "jalandhari" VALUES(3,95,'3:95','کہہ دو کہ خدا نے سچ فرمایا دیا پس دین ابراہیم کی پیروی کرو جو سب سے بےتعلق ہو کر ایک (خدا) کے ہو رہے تھے اور مشرکوں سے نہ تھے');
INSERT INTO "jalandhari" VALUES(3,96,'3:96','پہلا گھر جو لوگوں (کے عبادت کرنے) کے لیے مقرر کیا گیا تھا وہی ہے جو مکے میں ہے بابرکت اور جہاں کے لیے موجبِ ہدایت');
INSERT INTO "jalandhari" VALUES(3,97,'3:97','اس میں کھلی ہوئی نشانیاں ہیں جن میں سے ایک ابراہیم کے کھڑے ہونے کی جگہ ہے جو شخص اس (مبارک) گھر میں داخل ہوا اس نے امن پا لیا اور لوگوں پر خدا کا حق (یعنی فرض) ہے کہ جو اس گھر تک جانے کا مقدور رکھے وہ اس کا حج کرے اور جو اس حکم کی تعمیل نہ کرے گا تو خدا بھی اہلِ عالم سے بے نیاز ہے');
INSERT INTO "jalandhari" VALUES(3,98,'3:98','کہو کہ اہلِ کتاب! تم خدا کی آیتوں سے کیوں کفر کرتے ہو اور خدا تمہارے سب اعمال سے باخبر ہے');
INSERT INTO "jalandhari" VALUES(3,99,'3:99','کہو کہ اہلِ کتاب تم مومنوں کو خدا کے رستے سے کیوں روکتے ہو اور باوجود یہ کہ تم اس سے واقف ہو اس میں کجی نکالتے ہو اور خدا تمھارے کاموں سے بےخبر نہیں');
INSERT INTO "jalandhari" VALUES(3,100,'3:100','مومنو! اگر تم اہلِ کتاب کے کسی فریق کا کہا مان لو گے تو وہ تمھیں ایمان لانے کے بعد کافر بنا دیں گے');
INSERT INTO "jalandhari" VALUES(3,101,'3:101','اور تم کیونکر کفر کرو گے جبکہ تم کو خدا کی آیتیں پڑھ پڑھ کر سنائی جاتی ہیں اور تم میں اس کے پیغمبر موجود ہیں اور جس نے خدا (کی ہدایت کی رسی) کو مضبوط پکڑ لیا وہ سیدھے رستے لگ گیا');
INSERT INTO "jalandhari" VALUES(3,102,'3:102','مومنو! خدا سے ڈرو جیسا کہ اس سے ڈرنے کا حق ہے اور مرنا تو مسلمان ہی مرنا');
INSERT INTO "jalandhari" VALUES(3,103,'3:103','اور سب مل کر خدا کی (ہدایت کی رسی) کو مضبوط پکڑے رہنا اور متفرق نہ ہونا اور خدا کی اس مہربانی کو یاد کرو جب تم ایک دوسرے کے دشمن تھے تو اس نے تمہارے دلوں میں الفت ڈال دی اور تم اس کی مہربانی سے بھائی بھائی ہوگئے اور تم آگ کے گڑھے کے کنارے تک پہنچ چکے تھے تو خدا نے تم کو اس سے بچا لیا اس طرح خدا تم کو اپنی آیتیں کھول کھول کر سناتا ہے تاکہ تم ہدایت پاؤ');
INSERT INTO "jalandhari" VALUES(3,104,'3:104','اور تم میں ایک جماعت ایسی ہونی چاہیئے جو لوگوں کو نیکی کی طرف بلائے اور اچھے کام کرنے کا حکم دے اور برے کاموں سے منع کرے یہی لوگ ہیں جو نجات پانے والے ہیں');
INSERT INTO "jalandhari" VALUES(3,105,'3:105','اور ان لوگوں کی طرح نہ ہونا جو متفرق ہو گئے اور احکام بین آنے کے بعد ایک دوسرےسے (خلاف و) اختلاف کرنے لگے یہ وہ لوگ ہیں جن کو قیامت کے دن بڑا عذاب ہوگا');
INSERT INTO "jalandhari" VALUES(3,106,'3:106','جس دن بہت سے منہ سفید ہوں گے اور بہت سے منہ سیاہ تو جن لوگوں کے منہ سیاہ ہوں گے (ان سے خدا فرمائے گا) کیا تم ایمان لا کر کافر ہوگئے تھے؟ سو (اب) اس کفر کے بدلے عذاب (کے مزے) چکھو');
INSERT INTO "jalandhari" VALUES(3,107,'3:107','اور جن لوگوں کے منہ سفید ہوں گے وہ خدا کی رحمت (کے باغوں) میں ہوں گے اور ان میں ہمیشہ رہیں گے');
INSERT INTO "jalandhari" VALUES(3,108,'3:108','یہ خدا کی آیتیں ہیں جو ہم تم کو صحت کے ساتھ پڑھ کر سناتے ہیں اور خدا اہلِ عالم پر ظلم نہیں کرنا چاہتا');
INSERT INTO "jalandhari" VALUES(3,109,'3:109','اور جو کچھ آسمانوں میں اور جو کچھ زمین میں ہے سب خدا ہی کا ہے اور سب کاموں کا رجوع (اور انجام) خدا ہی کی طرف ہے');
INSERT INTO "jalandhari" VALUES(3,110,'3:110','(مومنو) جتنی امتیں (یعنی قومیں) لوگوں میں پیدا ہوئیں تم ان سب سے بہتر ہو کہ نیک کام کرنے کو کہتے ہو اور برے کاموں سے منع کرتے ہو اور خدا پر ایمان رکھتے ہو اور اگر اہلِ کتاب بھی ایمان لے آتے تو ان کے لیے بہت اچھا ہوتا ان میں ایمان لانے والے بھی ہیں (لیکن تھوڑے) اور اکثر نافرمان ہیں');
INSERT INTO "jalandhari" VALUES(3,111,'3:111','اور یہ تمہیں خفیف سی تکلیف کے سوا کچھ نقصان نہیں پہنچا سکیں گے اور اگر تم سے لڑیں گے تو پیٹھ پھیر کر بھاگ جائیں گے پھر ان کو مدد بھی (کہیں سے) نہیں ملے گی');
INSERT INTO "jalandhari" VALUES(3,112,'3:112','یہ جہاں نظر آئیں گے ذلت (کو دیکھو گے کہ) ان سے چمٹ رہی ہے بجز اس کے کہ یہ خدا اور (مسلمان) لوگوں کی پناہ میں آ جائیں اور یہ لوگ خدا کے غضب میں گرفتار ہیں اور ناداری ان سے لپٹ رہی ہے یہ اس لیے کہ خدا کی آیتوں سے انکار کرتےتھے اور (اس کے) پیغمبروں کو ناحق قتل کر دیتے تھے یہ اس لیے کہ یہ نافرمانی کیے جاتے اور حد سے بڑھے جاتے تھے');
INSERT INTO "jalandhari" VALUES(3,113,'3:113','یہ بھی سب ایک جیسے نہیں ہیں ان اہلِ کتاب میں کچھ لوگ (حکمِ خدا پر) قائم بھی ہیں جو رات کے وقت خدا کی آیتیں پڑھتے اور (اس کے آگے) سجدہ کرتے ہیں');
INSERT INTO "jalandhari" VALUES(3,114,'3:114','(اور) خدا پر اور روز آخرت پر ایمان رکھتے اور اچھے کام کرنےکو کہتے اور بری باتوں سے منع کرتےاور نیکیوں پر لپکتے ہیں اور یہی لوگ نیکوکار ہیں');
INSERT INTO "jalandhari" VALUES(3,115,'3:115','اور یہ جس طرح کی نیکی کریں گے اس کی ناقدری نہیں کی جائے گی اور خدا پرہیزگاروں کو خوب جانتا ہے');
INSERT INTO "jalandhari" VALUES(3,116,'3:116','جو لوگ کافر ہیں ان کے مال اور اولاد خدا کے غضب کو ہرگز نہیں ٹال سکیں گے اور یہ لوگ اہلِ دوزخ ہیں کہ ہمیشہ اسی میں رہیں گے');
INSERT INTO "jalandhari" VALUES(3,117,'3:117','یہ جو مال دنیا کی زندگی میں خرچ کرتے ہیں اس کی مثال ہوا کی سی ہے جس میں سخت سردی ہو اور وہ ایسے لوگوں کی کھیتی پر جو اپنے آپ پر ظلم کرتے تھے چلے اور اسے تباہ کر دے اور خدا نے ان پر کچھ ظلم نہیں کیا بلکہ یہ خود اپنے اوپر ظلم کر رہے ہیں');
INSERT INTO "jalandhari" VALUES(3,118,'3:118','مومنو! کسی غیر (مذہب کے آدمی) کو اپنا رازداں نہ بنانا یہ لوگ تمہاری خرابی اور (فتنہ انگیزی کرنے) میں کسی طرح کی کوتاہی نہیں کرتے اور چاہتے ہیں کہ (جس طرح ہو) تمہیں تکلیف پہنچے ان کی زبانوں سے تو دشمنی ظاہر ہوہی چکی ہے اور جو (کینے) ان کے سینوں میں مخفی ہیں وہ کہیں زیادہ ہیں اگر تم عقل رکھتے ہو تو ہم نے تم کو اپنی آیتیں کھول کھول کر سنا دی ہیں');
INSERT INTO "jalandhari" VALUES(3,119,'3:119','دیکھو تم ایسے (صاف دل) لوگ ہو کہ ان لوگوں سے دوستی رکھتے ہو حالانکہ وہ تم سے دوستی نہیں رکھتے اور تم سب کتابوں پر ایمان رکھتے ہو (اور وہ تمہاری کتاب کو نہیں مانتے) اور جب تم سے ملتے ہیں تو کہتے ہیں ہم ایمان لے آئے اور جب الگ ہوتے ہیں تو تم پر غصے کے سبب انگلیاں کاٹ کاٹ کھاتے ہیں (ان سے) کہہ دو کہ (بدبختو) غصے میں مر جاؤ خدا تمہارے دلوں کی باتوں سے خوب واقف ہے');
INSERT INTO "jalandhari" VALUES(3,120,'3:120','اگر تمہیں آسودگی حاصل ہو تو ان کو بری لگتی ہے اور اگر رنج پہنچے تو خوش ہوتے ہیں اور اگر تم تکلیفوں کی برداشت اور (ان سے) کنارہ کشی کرتے رہو گے تو ان کا فریب تمھیں کچھ بھی نقصان نہ پہنچا سکے گا یہ جو کچھ کرتے ہیں خدا اس پر احاطہ کیے ہوئے ہے');
INSERT INTO "jalandhari" VALUES(3,121,'3:121','اور (اس وقت کو یاد کرو) جب تم صبح کو اپنے گھر روانہ ہو کر ایمان والوں کو لڑائی کے لیے مورچوں پر (موقع بہ موقع) متعین کرنے لگے اور خدا سب کچھ سنتا اور جانتا ہے');
INSERT INTO "jalandhari" VALUES(3,122,'3:122','اس وقت تم میں سے دو جماعتوں نے جی چھوڑ دینا چاہا مگر خدا ان کا مددگار تھا اور مومنوں کو خدا ہی پر بھروسہ رکھنا چاہیئے');
INSERT INTO "jalandhari" VALUES(3,123,'3:123','اور خدا نے جنگِ بدر میں بھی تمہاری مدد کی تھی اور اس وقت بھی تم بے سرو وسامان تھے پس خدا سے ڈرو (اور ان احسانوں کو یاد کرو) تاکہ شکر کرو');
INSERT INTO "jalandhari" VALUES(3,124,'3:124','جب تم مومنوں سے یہ کہہ (کر ان کے دل بڑھا) رہے تھے کہ کیا یہ کافی نہیں کہ پروردگار تین ہزار فرشتے نازل کر کے تمہیں مدد دے');
INSERT INTO "jalandhari" VALUES(3,125,'3:125','ہاں اگر تم دل کو مضبوط رکھو اور (خدا سے) ڈرتے رہو اور کافر تم پر جوش کے ساتھ دفعتہً حملہ کردیں تو پروردگار پانچ ہزار فرشتے جن پر نشان ہوں گے تمہاری مدد کو بھیجے گا');
INSERT INTO "jalandhari" VALUES(3,126,'3:126','اور اس مدد کو خدا نے تمھارے لیے (ذریعہٴ) بشارت بنایا یعنی اس لیے کہ تمہارے دلوں کو اس سے تسلی حاصل ہو ورنہ مدد تو خدا ہی کی ہے جو غالب (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(3,127,'3:127','(یہ خدا نے) اس لیے (کیا) کہ کافروں کی ایک جماعت کو ہلاک یا انہیں ذلیل ومغلوب کر دے کہ (جیسے آئے تھے ویسے ہی) ناکام واپس جائیں');
INSERT INTO "jalandhari" VALUES(3,128,'3:128','(اے پیغمبر) اس کام میں تمہارا کچھ اختیار نہیں (اب دو صورتیں ہیں) یا خدا انکے حال پر مہربانی کرے یا انہیں عذاب دے کہ یہ ظالم لوگ ہیں');
INSERT INTO "jalandhari" VALUES(3,129,'3:129','اور جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے سب خدا ہی کا ہے وہ جسے چاہے بخش دے اور جسے چاہے عذاب کرے اور خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(3,130,'3:130','اےایمان والو! دگنا چوگنا سود نہ کھاؤ اور خدا سے ڈرو تاکہ نجات حاصل کرو');
INSERT INTO "jalandhari" VALUES(3,131,'3:131','اور (دوزخ کی) آگ سے بچو جو کافروں کے لیے تیار کی گئی ہے');
INSERT INTO "jalandhari" VALUES(3,132,'3:132','اور خدا اور اس کے رسول کی اطاعت کرو تاکہ تم پر رحمت کی جائے');
INSERT INTO "jalandhari" VALUES(3,133,'3:133','اپنے پروردگار کی بخشش اور بہشت کی طرف لپکو جس کا عرض آسمان اور زمین کے برابر ہے اور جو (خدا سے) ڈرنے والوں کے لیے تیار کی گئی ہے');
INSERT INTO "jalandhari" VALUES(3,134,'3:134','جو آسودگی اور تنگی میں (اپنا مال خدا کی راہ میں) خرچ کرتےہیں اور غصے کو روکتے اور لوگوں کے قصور معاف کرتے ہیں اور خدا نیکو کاروں کو دوست رکھتا ہے');
INSERT INTO "jalandhari" VALUES(3,135,'3:135','اور وہ کہ جب کوئی کھلا گناہ یا اپنے حق میں کوئی اور برائی کر بیٹھتے ہیں تو خدا کو یاد کرتے اور اپنے گناہوں کی بخشش مانگتے ہیں اور خدا کے سوا گناہ بخش بھی کون سکتا ہے؟ اور جان بوجھ کر اپنے افعال پر اڑے نہیں رہتے');
INSERT INTO "jalandhari" VALUES(3,136,'3:136','ایسے ہی لوگوں کا صلہ پروردگار کی طرف سے بخشش اور باغ ہیں جن کے نیچے نہریں بہہ رہی ہیں (اور) وہ ان میں ہمیشہ بستے رہیں گے اور (اچھے) کام کرنے والوں کا بدلہ بہت اچھا ہے');
INSERT INTO "jalandhari" VALUES(3,137,'3:137','تم لوگوں سے پہلے بھی بہت سے واقعات گزر چکے ہیں تو تم زمین کی سیر کرکے دیکھ لو کہ جھٹلانے والوں کا کیسا انجام ہوا');
INSERT INTO "jalandhari" VALUES(3,138,'3:138','یہ (قرآن) لوگوں کے لیے بیان صریح اور اہلِ تقویٰ کے لیے ہدایت اور نصیحت ہے');
INSERT INTO "jalandhari" VALUES(3,139,'3:139','اور (دیکھو) بے دل نہ ہونا اور نہ کسی طرح کا غم کرنا اگر تم مومن (صادق) ہو تو تم ہی غالب رہو گے');
INSERT INTO "jalandhari" VALUES(3,140,'3:140','اگر تمہیں زخم (شکست) لگا ہے تو ان لوگوں کو بھی ایسا زخم لگ چکا ہے اور یہ دن ہیں کہ ہم ان کو لوگوں میں بدلتے رہتے ہیں اور اس سے یہ بھی مقصود تھا کہ خدا ایمان والوں کو متمیز کر دے اور تم میں سے گواہ بنائے اور خدا بےانصافوں کو پسند نہیں کرتا');
INSERT INTO "jalandhari" VALUES(3,141,'3:141','اور یہ بھی مقصود تھا کہ خدا ایمان والوں کو خالص (مومن) بنا دے اور کافروں کو نابود کر دے');
INSERT INTO "jalandhari" VALUES(3,142,'3:142','کیا تم یہ سمجھتے ہو کہ (بےآزمائش) بہشت میں جا داخل ہو گے حالانکہ ابھی خدا نے تم میں سے جہاد کرنے والوں کو تو اچھی طرح معلوم کیا ہی نہیں اور (یہ بھی مقصود ہے) کہ وہ ثابت قدم رہنے والوں کو معلوم کرے');
INSERT INTO "jalandhari" VALUES(3,143,'3:143','اور تم موت (شہادت) کے آنے سے پہلے اس کی تمنا کیا کرتے تھے سو تم نے اس کو آنکھوں سے دیکھ لیا');
INSERT INTO "jalandhari" VALUES(3,144,'3:144','اور محمد (صلی الله علیہ وسلم) تو صرف (خدا کے) پیغمبر ہیں ان سے پہلے بھی بہت سے پیغمبر ہو گزرے ہیں بھلا اگر یہ مر جائیں یا مارے جائیں تو تم الٹے پاؤں پھر جاؤ؟ (یعنی مرتد ہو جاؤ؟) اور جو الٹے پاؤں پھر جائے گا تو خدا کا کچھ نقصان نہ کر سکے گا اور خدا شکر گزاروں کو (بڑا) ثواب دے گا');
INSERT INTO "jalandhari" VALUES(3,145,'3:145','اور کسی شخص میں طاقت نہیں کہ خدا کے حکم کے بغیر مر جائے (اس نے موت کا) وقت مقرر کر کے لکھ رکھا ہے اور جو شخص دنیا میں (اپنے اعمال کا) بدلہ چاہے اس کو ہم یہیں بدلہ دے دیں گے اور جو آخرت میں طالبِ ثواب ہو اس کو وہاں اجر عطا کریں گے اور ہم شکر گزاروں کو عنقریب (بہت اچھا) صلہ دیں گے');
INSERT INTO "jalandhari" VALUES(3,146,'3:146','اور بہت سے نبی ہوئے ہیں جن کے ساتھ ہو کر اکثر اہل الله (خدا کے دشمنوں سے) لڑے ہیں تو جو مصبتیں ان پر راہِ خدا میں واقع ہوئیں ان کے سبب انہوں نے نہ تو ہمت ہاری اور نہ بزدلی کی نہ (کافروں سے) دبے اور خدا استقلال رکھنے والوں کو دوست رکھتا ہے');
INSERT INTO "jalandhari" VALUES(3,147,'3:147','اور (اس حالت میں) ان کے منہ سے کوئی بات نکلتی ہے تو یہی کہ اے پروردگار ہمارے گناہ اور زیادتیاں جو ہم اپنے کاموں میں کرتے رہے ہیں معاف فرما اور ہم کو ثابت قدم رکھ اور کافروں پر فتح عنایت فرما');
INSERT INTO "jalandhari" VALUES(3,148,'3:148','تو خدا نے ان کو دنیا میں بھی بدلہ دیا اور آخرت میں بھی بہت اچھا بدلہ (دے گا) اور خدا نیکو کاروں کو دوست رکھتا ہے');
INSERT INTO "jalandhari" VALUES(3,149,'3:149','مومنو! اگر تم کافروں کا کہا مان لو گے تو وہ تم کو الٹے پاؤں پھیر کر (مرتد کر) دیں گے پھر تم بڑے خسارے میں پڑ جاؤ گے');
INSERT INTO "jalandhari" VALUES(3,150,'3:150','(یہ تمہارے مددگار نہیں ہیں) بلکہ خدا تمہارا مددگار ہے اور وہ سب سے بہتر مددگار ہے');
INSERT INTO "jalandhari" VALUES(3,151,'3:151','ہم عنقریب کافروں کے دلوں میں تمہارا رعب بٹھا دیں گے کیونکہ یہ خدا کے ساتھ شرک کرتے ہیں جس کی اس نے کوئی بھی دلیل نازل نہیں کی اور ان کا ٹھکانہ دوزخ ہے وہ ظالموں کا بہت بُرا ٹھکانا ہے');
INSERT INTO "jalandhari" VALUES(3,152,'3:152','اور خدا نے اپنا وعدہ سچا کر دیا (یعنی) اس وقت جبکہ تم کافروں کو اس کے حکم سے قتل کر رہے تھے یہاں تک کہ جو تم چاہتے تھے خدا نے تم کو دکھا دیا اس کے بعد تم نے ہمت ہار دی اور حکم (پیغمبر) میں جھگڑا کرنے لگے اور اس کی نافرمانی کی بعض تو تم میں سے دنیا کے خواستگار تھے اور بعض آخرت کے طالب اس وقت خدا نے تم کو ان (کے مقابلے) سے پھیر (کر بھگا) دیا تاکہ تمہاری آزمائش کرے اور اس نے تمہارا قصور معاف کر دیا اور خدا مومنو پر بڑا فضل کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(3,153,'3:153','(وہ وقت بھی یاد کرنے کے لائق ہے) جب تم لوگ دور بھاگے جاتے تھے اور کسی کو پیچھے پھر کر نہیں دیکھتے تھے اور رسول الله تم کو تمہارے پیچھے کھڑے بلا رہے تھے تو خدا نے تم کو غم پر غم پہنچایا تاکہ جو چیز تمہارے ہاتھ سے جاتی رہی یا جو مصیبت تم پر واقع ہوئی ہے اس سے تم اندوہ ناک نہ ہو اور خدا تمہارے سب اعمال سے خبردار ہے');
INSERT INTO "jalandhari" VALUES(3,154,'3:154','پھر خدا نے غم ورنج کے بعد تم پر تسلی نازل فرمائی (یعنی) نیند کہ تم میں سے ایک جماعت پر طاری ہو گئی اور کچھ لوگ جن کو جان کے لالے پڑ رہے تھے خدا کے بارے میں ناحق (ایام) کفر کے سے گمان کرتے تھے اور کہتے تھے بھلا ہمارے اختیار کی کچھ بات ہے؟ تم کہہ دو کہ بےشک سب باتیں خدا ہی کے اختیار میں ہیں یہ لوگ (بہت سی باتیں) دلوں میں مخفی رکھتے ہیں جو تم پر ظاہر نہیں کرتے تھے کہتے تھے کہ ہمارے بس کی بات ہوتی تو ہم یہاں قتل ہی نہ کیے جاتے کہہ دو کہ اگر تم اپنے گھروں میں بھی ہوتے تو جن کی تقدیر میں مارا جانا لکھا تھا وہ اپنی اپنی قتل گاہوں کی طرف ضرور نکل آتے اس سے غرض یہ تھی کہ خدا تمہارے سینوں کی باتوں کو آزمائے اور جو کچھ تمہارے دلوں میں ہے اس کو خالص اور صاف کر دے اور خدا دلوں کی باتوں سے خوب واقف ہے');
INSERT INTO "jalandhari" VALUES(3,155,'3:155','جو لوگ تم میں سے (اُحد کے دن) جبکہ (مومنوں اور کافروں کی) دو جماعتیں ایک دوسرے سے گتھ گئیں (جنگ سے) بھاگ گئے تو ان کے بعض افعال کے سبب شیطان نے ان کو پھسلا دیا مگر خدا نے ان کا قصور معاف کر دیا بےشک خدا بخشنے والا اور بردبار ہے');
INSERT INTO "jalandhari" VALUES(3,156,'3:156','مومنو! ان لوگوں جیسے نہ ہونا جو کفر کرتے ہیں اور ان کے (مسلمان) بھائی جب (خدا کی راہ میں) سفر کریں (اور مر جائیں) یا جہاد کو نکلیں (اور مارے جائیں) تو ان کی نسبت کہتے ہیں کہ اگر وہ ہمارے پاس رہتے تو نہ مرتے اور نہ مارے جاتے۔ ان باتوں سے مقصود یہ ہے کہ خدا ان لوگوں کے دلوں میں افسوس پیدا کر دے اور زندگی اور موت تو خدا ہی دیتا ہے اور خدا تمہارے سب کاموں کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(3,157,'3:157','اور اگر تم خدا کے رستے میں مارے جاؤ یا مرجاؤ تو جو (مال و متاع) لوگ جمع کرتے ہیں اس سے خدا کی بخشش اور رحمت کہیں بہتر ہے');
INSERT INTO "jalandhari" VALUES(3,158,'3:158','اور اگر تم مرجاؤ یا مارے جاؤ خدا کے حضور میں ضرور اکھٹے کئے جاؤ گے');
INSERT INTO "jalandhari" VALUES(3,159,'3:159','(اے محمدصلى الله عليه وسلم) خدا کی مہربانی سے تمہاری افتاد مزاج ان لوگوں کے لئے نرم واقع ہوئی ہے۔ اور اگر تم بدخو اور سخت دل ہوتے تو یہ تمہارے پاس سے بھاگ کھڑے ہوتے۔ تو ان کو معاف کردو اور ان کے لئے (خدا سے) مغفرت مانگو۔ اور اپنے کاموں میں ان سے مشورت لیا کرو۔ اور جب (کسی کام کا) عزم مصمم کرلو تو خدا پر بھروسا رکھو۔ بےشک خدا بھروسا رکھنے والوں کو دوست رکھتا ہے');
INSERT INTO "jalandhari" VALUES(3,160,'3:160','اور خدا تمہارا مددگار ہے تو تم پر کوئی غالب نہیں آسکتا۔ اور اگر وہ تمہیں چھوڑ دے تو پھر کون ہے کہ تمہاری مدد کرے اور مومنوں کو چاہیئے کہ خدا ہی پر بھروسا رکھیں');
INSERT INTO "jalandhari" VALUES(3,161,'3:161','اور کبھی نہیں ہوسکتا کہ پیغمبر (خدا) خیانت کریں۔ اور خیانت کرنے والوں کو قیامت کے دن خیانت کی ہوئی چیز (خدا کے روبرو) لاحاضر کرنی ہوگی۔ پھر ہر شخص کو اس کے اعمال کا پورا پورا بدلا دیا جائے گا اور بےانصافی نہیں کی جائے گی');
INSERT INTO "jalandhari" VALUES(3,162,'3:162','بھلا جو شخص خدا کی خوشنودی کا تابع ہو وہ اس شخص کی طرح(مرتکب خیانت) ہوسکتا ہے جو خدا کی ناخوشی میں گرفتار ہو اور جس کا ٹھکانہ دوزخ ہے، اور وہ برا ٹھکانا ہے');
INSERT INTO "jalandhari" VALUES(3,163,'3:163','ان لوگوں کے خدا کے ہاں (مختلف اور متفاوت) درجے ہیں اور خدا ان کے سب اعمال کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(3,164,'3:164','خدا نے مومنوں پر بڑا احسان کیا ہے کہ ان میں انہیں میں سے ایک پیغمبر بھیجے۔ جو ان کو خدا کی آیتیں پڑھ پڑھ کر سناتے اور ان کو پاک کرتے اور (خدا کی) کتاب اور دانائی سکھاتے ہیں اور پہلے تو یہ لوگ صریح گمراہی میں تھے');
INSERT INTO "jalandhari" VALUES(3,165,'3:165','(بھلا یہ) کیا (بات ہے کہ) جب (اُحد کے دن کافر کے ہاتھ سے) تم پر مصیبت واقع ہوئی حالانکہ (جنگ بدر میں) اس سے دوچند مصیبت تمہارے ہاتھ سے ان پر پڑچکی ہے توتم چلا اٹھے کہ (ہائے) آفت (ہم پر) کہاں سے آپڑی کہہ دو کہ یہ تمہاری ہی شامت اعمال ہے (کہ تم نے پیغمبر کے حکم کے خلاف کیا) بےشک خدا ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(3,166,'3:166','اور جو مصیبت تم پر دونوں جماعتوں کے مقابلے کے دن واقع ہوئی سو خدا کے حکم سے (واقع ہوئی) اور (اس سے) یہ مقصود تھا کہ خدا مومنوں کو اچھی طرح معلوم');
INSERT INTO "jalandhari" VALUES(3,167,'3:167','اور منافقوں کو بھی معلوم کرلے اور (جب) ان سے کہا گیا کہ آؤ خدا کے رستے میں جنگ کرو یا (کافروں کے) حملوں کو روکو۔ تو کہنے لگے کہ اگر ہم کو لڑائی کی خبر ہوتی تو ہم ضرور تمہارے ساتھ رہتے یہ اس دن ایمان کی نسبت کفر سے زیادہ قریب تھے منہ سے وہ باتیں کہتے ہیں جو ان کے دل میں نہیں ہیں۔ اور جو کچھ یہ چھپاتے ہیں خدا ان سے خوب واقف ہے');
INSERT INTO "jalandhari" VALUES(3,168,'3:168','یہ خود تو (جنگ سے بچ کر) بیٹھ ہی رہے تھے مگر (جنہوں نے راہ خدا میں جانیں قربان کردیں) اپنے (ان) بھائیوں کے بارے میں بھی کہتے ہیں کہ اگر ہمارا کہا مانتے تو قتل نہ ہوتے۔ کہہ دو کہ اگر سچے ہو تو اپنے اوپر سے موت کو ٹال دینا');
INSERT INTO "jalandhari" VALUES(3,169,'3:169','جو لوگ خدا کی راہ میں مارے گئے ان کو مرے ہوئے نہ سمجھنا (وہ مرے ہوئے نہیں ہیں) بلکہ خدا کے نزدیک زندہ ہیں اور ان کو رزق مل رہا ہے');
INSERT INTO "jalandhari" VALUES(3,170,'3:170','جو کچھ خدا نے ان کو اپنے فضل سے بخش رکھا ہے اس میں خوش ہیں۔ اور جو لوگ ان کے پیچھے رہ گئے اور( شہید ہوکر) ان میں شامل نہیں ہوسکے ان کی نسبت خوشیاں منا رہے ہیں کہ (قیامت کے دن) ان کو بھی نہ کچھ خوف ہوگا اور نہ وہ غمناک ہوں گے');
INSERT INTO "jalandhari" VALUES(3,171,'3:171','اور خدا کے انعامات اور فضل سے خوش ہورہے ہیں۔ اور اس سے کہ خدا مومنوں کا اجر ضائع نہیں کرتا');
INSERT INTO "jalandhari" VALUES(3,172,'3:172','جنہوں نے باوجود زخم کھانے کے خدا اور رسول (کے حکم) کو قبول کیا جو لوگ ان میں نیکوکار اور پرہیزگار ہیں ان کے لئے بڑا ثواب ہے');
INSERT INTO "jalandhari" VALUES(3,173,'3:173','(جب) ان سے لوگوں نے آکر بیان کیا کہ کفار نے تمہارے (مقابلے کے) لئے لشکر کثیر) جمع کیا ہے تو ان سے ڈرو۔ تو ان کا ایمان اور زیادہ ہوگیا۔ اور کہنے لگے ہم کو خدا کافی ہے اور وہ بہت اچھا کارساز ہے');
INSERT INTO "jalandhari" VALUES(3,174,'3:174','پھر وہ خدا کی نعمتوں اور اس کے فضل کے ساتھ (خوش وخرم) واپس آئے ان کو کسی طرح کا ضرر نہ پہنچا۔ اور وہ خدا کی خوشنودی کے تابع رہے۔ اور خدا بڑے فضل کا مالک ہے');
INSERT INTO "jalandhari" VALUES(3,175,'3:175','یہ (خوف دلانے والا) تو شیطان ہے جو اپنے دوستوں سے ڈراتا ہے تو اگر تم مومن ہو تو ان سے مت ڈرنا اور مجھ ہی سے ڈرتے رہنا');
INSERT INTO "jalandhari" VALUES(3,176,'3:176','اور جو لوگ کفر میں جلدی کرتے ہیں ان (کی وجہ) سے غمگین نہ ہونا۔ یہ خدا کا کچھ نقصان نہیں کرسکتے خدا چاہتا ہے کہ آخرت میں ان کو کچھ حصہ نہ دے اور ان کے لئے بڑا عذاب تیار ہے');
INSERT INTO "jalandhari" VALUES(3,177,'3:177','جن لوگوں نے ایمان کے بدلے کفر خریدا وہ خدا کا کچھ نہیں بگاڑ سکتے اور ان کو دکھ دینے والا عذاب ہوگا');
INSERT INTO "jalandhari" VALUES(3,178,'3:178','اور کافر لوگ یہ نہ خیال کریں کہ ہم جو ان کو مہلت دیئے جاتے ہیں تو یہ ان کے حق میں اچھا ہے۔ (نہیں بلکہ) ہم ان کو اس لئے مہلت دیتے ہیں کہ اور گناہ کرلیں۔ آخرکار ان کو ذلیل کرنے والا عذاب ہوگا');
INSERT INTO "jalandhari" VALUES(3,179,'3:179','(لوگو) جب تک خدا ناپاک کو پاک سے الگ نہ کردے گا مومنوں کو اس حال میں جس میں تم ہو ہرگز نہیں رہنے دے گا۔ اور الله تم کوغیب کی باتوں سے بھی مطلع نہیں کرے گاالبتہ خدا اپنے پیغمبروں میں سے جسے چاہتا ہے انتخاب کرلیتا ہے۔ تو تم خدا پر اور اس کے رسولوں پر ایمان لاؤاور اگر ایمان لاؤ گے اور پرہیزگاری کرو گے تو تم کو اجر عظیم ملے گا');
INSERT INTO "jalandhari" VALUES(3,180,'3:180','جو لوگ مال میں جو خدا نے اپنے فضل سے ان کو عطا فرمایا ہے بخل کرتے ہیں وہ اس بخل کو اپنے حق میں اچھا نہ سمجھیں۔ (وہ اچھا نہیں) بلکہ ان کے لئے برا ہے وہ جس مال میں بخل کرتے ہیں قیامت کے دن اس کا طوق بنا کر ان کی گردنوں میں ڈالا جائے گا۔ اور آسمانوں اور زمین کا وارث خدا ہی ہے۔ اور جو عمل تم کرتے ہوخدا کو معلوم ہے');
INSERT INTO "jalandhari" VALUES(3,181,'3:181','خدا نے ان لوگوں کا قول سن لیا ہے جو کہتے ہیں کہ خدا فقیر ہے۔ اور ہم امیر ہیں۔ یہ جو کہتے ہیں ہم اس کو لکھ لیں گے۔ اور پیغمبروں کو جو یہ ناحق قتل کرتے رہے ہیں اس کو بھی (قلمبند کر رکھیں گے) اور (قیامت کے روز) کہیں گے کہ عذاب (آتش) سوزاں کے مزے چکھتے رہو');
INSERT INTO "jalandhari" VALUES(3,182,'3:182','یہ ان کاموں کی سزا ہے جو تمہارے ہاتھ آگے بھیجتے رہے ہیں اور خدا تو بندوں پر مطلق ظلم نہیں کرتا');
INSERT INTO "jalandhari" VALUES(3,183,'3:183','جو لوگ کہتے ہی کہ خدا نے ہمیں حکم بھیجا ہے کہ جب تک کوئی پیغمبر ہمارے پاس ایسی نیاز لے کر نہ آئے جس کو آگ آکر کھا جائے تب تک ہم اس پر ایمان نہ لائیں گے (اے پیغمبر ان سے) کہہ دو کہ مجھ سے پہلے کئی پیغمبر تمہارے پاس کھلی ہوئی نشانیاں لے کر آئے اور وہ (معجزہ) بھی لائے جو تم کہتے ہو تو اگر سچے ہو تو تم نے ان کو قتل کیوں کیا؟');
INSERT INTO "jalandhari" VALUES(3,184,'3:184','پھر اگر یہ لوگ تم کو سچا نہ سمجھیں تو تم سے پہلے بہت سے پیغمبر کھلی ہوئی نشانیاں اور صحیفے اور روشن کتابیں لے کر آچکے ہیں اور لوگوں نے ان کو بھی سچا نہیں سمجھا');
INSERT INTO "jalandhari" VALUES(3,185,'3:185','ہر متنفس کو موت کا مزا چکھنا ہے اور تم کو قیامت کے دن تمہارے اعمال کا پورا پورا بدلا دیا جائے گا۔ تو جو شخص آتش جہنم سے دور رکھا گیا اور بہشت میں داخل کیا گیا وہ مراد کو پہنچ گیا اور دنیا کی زندگی تو دھوکے کا سامان ہے');
INSERT INTO "jalandhari" VALUES(3,186,'3:186','(اے اہل ایمان) تمہارے مال و جان میں تمہاری آزمائش کی جائے گی۔ اور تم اہل کتاب سے اور ان لوگوں سے جو مشرک ہیں بہت سی ایذا کی باتیں سنو گے۔ اور تو اگر صبر اور پرہیزگاری کرتے رہو گے تو یہ بڑی ہمت کے کام ہیں');
INSERT INTO "jalandhari" VALUES(3,187,'3:187','اور جب خدا نے ان لوگوں سے جن کو کتاب عنایت کی گئی تھی اقرار کرلیا کہ (جو کچھ اس میں لکھا ہے) اسے صاف صاف بیان کرتے رہنا۔ اور اس (کی کسی بات) کو نہ چھپانا تو انہں نے اس کو پس پشت پھینک دیا اور اس کے بدلے تھوڑی سی قیمت حاصل کی۔ یہ جو کچھ حاصل کرتے ہیں برا ہے');
INSERT INTO "jalandhari" VALUES(3,188,'3:188','جو لوگ اپنے (ناپسند) کاموں سے خوش ہوتے ہیں اور پسندیدہ کام) جو کرتے نہیں ان کے لئے چاہتے ہیں کہ ان ک تعریف کی جائے ان کی نسبت خیال نہ کرنا کہ وہ عذاب سے رستگار ہوجائیں گے۔ اور انہیں درد دینے والا عذاب ہوگا');
INSERT INTO "jalandhari" VALUES(3,189,'3:189','اور آسمانوں اور زمین کی بادشاہی خدا ہی کو ہے اور خدا ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(3,190,'3:190','بے شک آسمانوں اور زمین کی پیدائش اور رات اور دن کے بدل بدل کے آنے جانے میں عقل والوں کے لیے نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(3,191,'3:191','جو کھڑے اور بیٹھے اور لیٹے (ہر حال میں) خدا کو یاد کرتے اور آسمان اور زمین کی پیدائش میں غور کرتے (اور کہتے ہیں) کہ اے پروردگار! تو نے اس (مخلوق) کو بے فائدہ نہیں پیدا کیا تو پاک ہے تو (قیامت کے دن) ہمیں دوزخ کے عذاب سے بچائیو');
INSERT INTO "jalandhari" VALUES(3,192,'3:192','اے پروردگار جس کو تو نے دوزخ میں ڈالا اسے رسوا کیا اور ظالموں کا کوئی مددگار نہیں');
INSERT INTO "jalandhari" VALUES(3,193,'3:193','اے پروردگارہم نے ایک ندا کرنے والے کو سنا کہ ایمان کے لیے پکار رہا تھا (یعنی) اپنے پروردگار پر ایمان لاؤ تو ہم ایمان لے آئے اے پروردگار ہمارے گناہ معاف فرما اور ہماری برائیوں کو ہم سے محو کر اور ہم کو دنیا سے نیک بندوں کے ساتھ اٹھا');
INSERT INTO "jalandhari" VALUES(3,194,'3:194','اے پروردگار تو نے جن جن چیزوں کے ہم سے اپنے پیغمبروں کے ذریعے سے وعدے کیے ہیں وہ ہمیں عطا فرما اور قیامت کے دن ہمیں رسوا نہ کیجو کچھ شک نہیں کہ تو خلاف وعدہ نہیں کرتا');
INSERT INTO "jalandhari" VALUES(3,195,'3:195','تو ان کے پرردگار نے ان کی دعا قبول کر لی (اور فرمایا) کہ میں کسی عمل کرنے والے کے عمل کو مرد ہو یا عورت ضائع نہیں کرتا تم ایک دوسرے کی جنس ہو تو جو لوگ میرے لیے وطن چھوڑ گئے اور اپنے گھروں سے نکالے گئے اور ستائے گئے اور لڑے اور قتل کیے گئے میں ان کے گناہ دور کردوں گا اور ان کو بہشتوں میں داخل کروں گا جن کے نیچے نہریں بہ رہی ہیں (یہ) خدا کے ہاں سے بدلہ ہے اور خدا کے ہاں اچھا بدلہ ہے');
INSERT INTO "jalandhari" VALUES(3,196,'3:196','(اے پیغمبر) کافروں کا شہروں میں چلنا پھرنا تمہیں دھوکا نہ دے');
INSERT INTO "jalandhari" VALUES(3,197,'3:197','(یہ دنیا کا) تھوڑا سا فائدہ ہے پھر (آخرت میں) تو ان کا ٹھکانا دوزخ ہے اور وہ بری جگہ ہے');
INSERT INTO "jalandhari" VALUES(3,198,'3:198','لیکن جو لوگ اپنے پروردگار سے ڈرتے رہے ان کے لیے باغ ہے جن کے نیچے نہریں بہہ رہی ہیں (اور) ان میں ہمیشہ رہیں گے (یہ) خدا کے ہاں سے (ان کی) مہمانی ہے اور جو کچھ خدا کے ہاں ہے وہ نیکو کاروں کے لیے بہت اچھا ہے');
INSERT INTO "jalandhari" VALUES(3,199,'3:199','اور بعض اہلِ کتاب ایسے بھی ہیں جو خدا پر اور اس (کتاب) پر جو تم پر نازل ہوئی اور اس پر جو ان پر نازل ہوئی ایمان رکھتے ہیں اور خدا کے آگے عاجزی کرتے ہیں اور خدا کی آیتوں کے بدلے تھوڑی سی قیمت نہیں لیتے یہی لوگ ہیں جن کا صلہ ان کے پروردگار کے ہاں تیار ہے اور خدا جلد حساب لینے والا ہے');
INSERT INTO "jalandhari" VALUES(3,200,'3:200','اے اہل ایمان (کفار کے مقابلے میں) ثابت قدم رہو اور استقامت رکھو اور مورچوں پر جمے رہو اور خدا سے ڈرو تاکہ مراد حاصل کرو');
INSERT INTO "jalandhari" VALUES(4,1,'4:1','لوگو اپنے پروردگار سے ڈرو جس نے تم کو ایک شخص سے پیدا کیا (یعنی اول) اس سے اس کا جوڑا بنایا۔ پھر ان دونوں سے کثرت سے مرد وعورت (پیدا کرکے روئے زمین پر) پھیلا دیئے۔ اور خدا سے جس کے نام کو تم اپنی حاجت بر آری کا ذریعہ بناتے ہو ڈرو اور (قطع مودت) ارحام سے (بچو) کچھ شک نہیں کہ خدا تمہیں دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(4,2,'4:2','اور یتیموں کا مال (جو تمہاری تحویل میں ہو) ان کے حوالے کردو اور ان کے پاکیزہ (اور عمدہ) مال کو (اپنے ناقص اور) برے مال سے نہ بدلو۔ اور نہ ان کا مال اپنے مال میں ملا کر کھاؤ۔ کہ یہ بڑا سخت گناہ ہے');
INSERT INTO "jalandhari" VALUES(4,3,'4:3','اور اگر تم کو اس بات کا خوف ہو کہ یتیم لڑکیوں کے بارےانصاف نہ کرسکوگے تو ان کے سوا جو عورتیں تم کو پسند ہوں دو دو یا تین تین یا چار چار ان سے نکاح کرلو۔ اور اگر اس بات کا اندیشہ ہو کہ (سب عورتوں سے) یکساں سلوک نہ کرسکو گے تو ایک عورت (کافی ہے) یا لونڈی جس کے تم مالک ہو۔ اس سے تم بےانصافی سے بچ جاؤ گے');
INSERT INTO "jalandhari" VALUES(4,4,'4:4','اور عورتوں کو ان کے مہر خوشی سے دے دیا کرو۔ ہاں اگر وہ اپنی خوشی سے اس میں سے کچھ تم کو چھوڑ دیں تو اسے ذوق شوق سے کھالو');
INSERT INTO "jalandhari" VALUES(4,5,'4:5','اور بےعقلوں کو ان کا مال جسے خدا نے تم لوگوں کے لئے سبب معیشت بنایا ہے مت دو (ہاں) اس میں سے ان کو کھلاتے اور پہناتے رہے اور ان سے معقول باتیں کہتے رہو');
INSERT INTO "jalandhari" VALUES(4,6,'4:6','اور یتمیوں کو بالغ ہونے تک کام کاج میں مصروف رکھو پھر (بالغ ہونے پر) اگر ان میں عقل کی پختگی دیکھو تو ان کا مال ان کے حوالے کردو اور اس خوف سے کہ وہ بڑے ہوجائیں گے (یعنی بڑے ہو کر تم سے اپنا مال واپس لے لیں گے) اس کو فضول خرچی اور جلدی میں نہ اڑا دینا۔ جو شخص آسودہ حال ہو اس کو (ایسے مال سے قطعی طور پر) پرہیز رکھنا چاہیئے اور جو بے مقدور ہو وہ مناسب طور پر (یعنی بقدر خدمت) کچھ لے لے اور جب ان کا مال ان کے حوالے کرنے لگو تو گواہ کرلیا کرو۔ اور حقیقت میں تو خدا ہی (گواہ اور) حساب لینے والا کافی ہے');
INSERT INTO "jalandhari" VALUES(4,7,'4:7','جو مال ماں باپ اور رشتہ دار چھوڑ مریں تھوڑا ہو یا بہت۔ اس میں مردوں کا بھی حصہ ہے اور عورتوں کا بھی یہ حصے (خدا کے) مقرر کئے ہوئے ہیں');
INSERT INTO "jalandhari" VALUES(4,8,'4:8','اور جب میراث کی تقسیم کے وقت (غیر وارث) رشتہ دار اور یتیم اور محتاج آجائیں تو ان کو بھی اس میں سے کچھ دے دیا کرو۔ اور شیریں کلامی سے پیش آیا کرو');
INSERT INTO "jalandhari" VALUES(4,9,'4:9','اور ایسے لوگوں کو ڈرنا چاہیئے جو (ایسی حالت میں ہوں کہ) اپنے بعد ننھے ننھے بچے چھوڑ جائیں اور ان کو ان کی نسبت خوف ہو (کہ ان کے مرنے کے بعد ان بیچاروں کا کیا حال ہوگا) پس چاہیئے کہ یہ لوگ خدا سے ڈریں اور معقول بات کہیں');
INSERT INTO "jalandhari" VALUES(4,10,'4:10','لوگ یتیموں کا مال ناجائز طور پر کھاتے ہیں وہ اپنے پیٹ میں آگ بھرتے ہیں۔ اور دوزخ میں ڈالے جائیں گے');
INSERT INTO "jalandhari" VALUES(4,11,'4:11','خدا تمہاری اولاد کے بارے میں تم کو ارشاد فرماتا ہے کہ ایک لڑکے کا حصہ دو لڑکیوں کے حصے کے برابر ہے۔ اور اگر اولاد میت صرف لڑکیاں ہی ہوں (یعنی دو یا) دو سے زیادہ تو کل ترکے میں ان کادو تہائی۔ اور اگر صرف ایک لڑکی ہو تو اس کا حصہ نصف۔ اور میت کے ماں باپ کا یعنی دونوں میں سے ہر ایک کا ترکے میں چھٹا حصہ بشرطیکہ میت کے اولاد ہو۔ اور اگر اولاد نہ ہو اور صرف ماں باپ ہی اس کے وارث ہوں تو ایک تہائی ماں کا حصہ۔ اور اگر میت کے بھائی بھی ہوں تو ماں کا چھٹا حصہ۔ (اور یہ تقسیم ترکہ میت کی) وصیت (کی تعمیل) کے بعد جو اس نے کی ہو یا قرض کے (ادا ہونے کے بعد جو اس کے ذمے ہو عمل میں آئے گی) تم کو معلوم نہیں کہ تمہارے باپ دادؤں اور بیٹوں پوتوں میں سے فائدے کے لحاظ سے کون تم سے زیادہ قریب ہے، یہ حصے خدا کے مقرر کئے ہوئے ہیں اور خدا سب کچھ جاننے والا اور حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(4,12,'4:12','اور جو مال تمہاری عورتیں چھوڑ مریں۔ اگر ان کے اولاد نہ ہو تو اس میں نصف حصہ تمہارا۔ اور اگر اولاد ہو تو ترکے میں تمہارا حصہ چوتھائی۔ (لیکن یہ تقسیم) وصیت (کی تعمیل) کے بعد جو انہوں نے کی ہو یا قرض کے (ادا ہونے کے بعد جو ان کے ذمے ہو، کی جائے گی) اور جو مال تم (مرد) چھوڑ مرو۔ اگر تمہارے اولاد نہ ہو تو تمہاری عورتوں کا اس میں چوتھا حصہ۔ اور اگر اولاد ہو تو ان کا آٹھواں حصہ (یہ حصے) تمہاری وصیت (کی تعمیل) کے بعد جو تم نے کی ہو اور (ادائے) قرض کے (بعد تقسیم کئے جائیں گے) اور اگر ایسے مرد یا عورت کی میراث ہو جس کے نہ باپ ہو نہ بیٹا مگر اس کے بھائی بہن ہو تو ان میں سے ہر ایک کا چھٹا حصہ اور اگر ایک سے زیادہ ہوں تو سب ایک تہائی میں شریک ہوں گے (یہ حصے بھی ادائے وصیت و قرض بشرطیکہ ان سے میت نے کسی کا نقصان نہ کیا ہو (تقسیم کئے جائیں گے) یہ خدا کا فرمان ہے۔ اور خدا نہایت علم والا (اور) نہایت حلم والا ہے');
INSERT INTO "jalandhari" VALUES(4,13,'4:13','(یہ تمام احکام) خدا کی حدیں ہیں۔ اور جو شخص خدا اور اس کے پیغمبر کی فرمانبرداری کرے گا خدا اس کو بہشتوں میں داخل کرے گا جن میں نہریں بہہ رہی ہیں وہ ان میں ہمیشہ رہیں گے۔اور یہ بڑی کامیابی ہے');
INSERT INTO "jalandhari" VALUES(4,14,'4:14','اور جو خدا اور اس کے رسول کی نافرمانی کرے گا اور اس کی حدوں سے نکل جائے گا اس کو خدا دوزخ میں ڈالے گا جہاں وہ ہمیشہ رہے گا۔ اور اس کو ذلت کا عذاب ہوگا');
INSERT INTO "jalandhari" VALUES(4,15,'4:15','مسلمانو تمہاری عورتوں میں جو بدکاری کا ارتکاب کر بیٹھیں ان پر اپنے لوگوں میں سے چار شخصوں کی شہادت لو۔ اگر وہ (ان کی بدکاری کی)گواہی دیں تو ان عورتوں کو گھروں میں بند رکھو یہاں تک کہ موت ان کا کام تمام کردے یا خدا ان کے لئے کوئی اور سبیل (پیدا) کرے');
INSERT INTO "jalandhari" VALUES(4,16,'4:16','اور جو دو مرد تم میں سے بدکاری کریں تو ان کو ایذا دو۔ پھر اگر وہ توبہ کرلیں اور نیکوکار ہوجائیں تو ان کا پیچھا چھوڑ دو۔ بےشک خدا توبہ قبول کرنے والا (اور) مہربان ہے');
INSERT INTO "jalandhari" VALUES(4,17,'4:17','خدا انہیں لوگوں کی توبہ قبول فرماتا ہے جو نادانی سے بری حرکت کر بیٹھے ہیں۔ پھر جلد توبہ قبول کرلیتے ہیں پس ایسے لوگوں پر خدا مہربانی کرتا ہے۔ اور وہ سب کچھ جانتا (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(4,18,'4:18','اور ایسے لوگوں کی توبہ قبول نہیں ہوتی جو (ساری عمر) برے کام کرتے ہیں۔ یہاں تک کہ جب ان میں سے کسی موت آموجود ہو تو اس وقت کہنے لگے کہ اب میں توبہ کرتا ہوں اور نہ ان کی (توبہ قبول ہوتی ہے) جو کفر کی حالت میں مریں۔ ایسے لوگوں کے لئے ہم نے عذاب الیم تیار کر رکھا ہے');
INSERT INTO "jalandhari" VALUES(4,19,'4:19','مومنو! تم کو جائز نہیں کہ زبردستی عورتوں کے وارث بن جاؤ۔ اور (دیکھنا) اس نیت سے کہ جو کچھ تم نے ان کو دیا ہے اس میں سے کچھ لے لو انہیں (گھروں میں) میں مت روک رکھنا ہاں اگر وہ کھلے طور پر بدکاری کی مرتکب ہوں (تو روکنا مناسب نہیں) اور ان کے ساتھ اچھی طرح رہو سہو اگر وہ تم کو ناپسند ہوں تو عجب نہیں کہ تم کسی چیز کو ناپسند کرو اور خدا اس میں بہت سی بھلائی پیدا کردے');
INSERT INTO "jalandhari" VALUES(4,20,'4:20','اور اگر تم ایک عورت کو چھوڑ کر دوسری عورت کرنی چاہو۔ اور پہلی عورت کو بہت سال مال دے چکے ہو تو اس میں سے کچھ مت لینا۔ بھلا تم ناجائز طور پر اور صریح ظلم سے اپنا مال اس سے واپس لے لوگے؟');
INSERT INTO "jalandhari" VALUES(4,21,'4:21','اور تم دیا ہوا مال کیونکر واپس لے سکتے ہو جب کہ تم ایک دوسرے کے ساتھ صحبت کرچکے ہو۔ اور وہ تم سے عہد واثق بھی لے چکی ہے');
INSERT INTO "jalandhari" VALUES(4,22,'4:22','اور جن عورتوں سے تمہارے باپ نے نکاح کیا ہو ان نکاح مت کرنا (مگر جاہلیت میں) جو ہوچکا (سوہوچکا) یہ نہایت بےحیائی اور (خدا کی) ناخوشی کی بات تھی۔ اور بہت برا دستور تھا');
INSERT INTO "jalandhari" VALUES(4,23,'4:23','تم پر تمہاری مائیں اور بیٹیاں اور بہنیں اور پھوپھیاں اور خالائیں اور بھتیجیاں اور بھانجیاں اور وہ مائیں جنہوں نے تم کو دودھ پلایا ہو اور رضاعی بہنیں اور ساسیں حرام کر دی گئی ہیں اور جن عورتوں سے تم مباشرت کر چکے ہو ان کی لڑکیاں جنہیں تم پرورش کرتے (ہو وہ بھی تم پر حرام ہیں) ہاں اگر ان کے ساتھ تم نے مباشرت نہ کی ہو تو (ان کی لڑکیوں کے ساتھ نکاح کر لینے میں) تم پر کچھ گناہ نہیں اور تمہارے صلبی بیٹوں کی عورتیں بھی اور دو بہنوں کا اکٹھا کرنا بھی (حرام ہے) مگر جو ہو چکا (سو ہو چکا) بے شک خدا بخشنے والا (اور) رحم کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(4,24,'4:24','اور شوہر والی عورتیں بھی (تم پر حرام ہیں) مگر وہ جو (اسیر ہو کر لونڈیوں کے طور پر) تمہارے قبضے میں آجائیں (یہ حکم) خدا نے تم کو لکھ دیا ہے اور ان (محرمات) کے سوا اور عورتیں تم کو حلال ہیں اس طرح سے کہ مال خرچ کر کے ان سے نکاح کرلو بشرطیکہ (نکاح سے) مقصود عفت قائم رکھنا ہو نہ شہوت رانی تو جن عورتوں سے تم فائدہ حاصل کرو ان کا مہر جو مقرر کیا ہو ادا کردو اور اگر مقرر کرنے کے بعد آپس کی رضامندی سے مہر میں کمی بیشی کرلو تو تم پر کچھ گناہ نہیں بےشک خدا سب کچھ جاننے والا (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(4,25,'4:25','اور جو شخص تم میں سے مومن آزاد عورتوں (یعنی بیبیوں) سے نکاح کرنے کا مقدور نہ رکھے تو مومن لونڈیوں میں ہی جو تمہارے قبضے میں آگئی ہوں (نکاح کرلے) اور خدا تمہارے ایمان کو اچھی طرح جانتا ہے تم آپس میں ایک دوسرے کے ہم جنس ہو تو ان لونڈیوں کے ساتھ ان کے مالکوں سے اجازت حاصل کرکے نکاح کر لو اور دستور کے مطابق ان کا مہر بھی ادا کردو بشرطیکہ عفیفہ ہوں نہ ایسی کہ کھلم کھلا بدکاری کریں اور نہ درپردہ دوستی کرنا چاہیں پھر اگر نکاح میں آکر بدکاری کا ارتکاب کر بیٹھیں تو جو سزا آزاد عورتوں (یعنی بیبیوں) کے لئے ہے اس کی آدھی ان کو (دی جائے) یہ (لونڈی کے ساتھ نکاح کرنے کی) اجازت اس شخص کو ہے جسے گناہ کر بیٹھنے کا اندیشہ ہو اور اگر صبر کرو تو یہ تمہارے لئے بہت اچھا ہے اور خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(4,26,'4:26','خدا چاہتا ہے کہ (اپنی آیتیں) تم سے کھول کھول کر بیان فرمائے اور تم کو اگلے لوگوں کے طریقے بتائے اور تم پر مہربانی کرے اور خدا جاننے والا (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(4,27,'4:27','اور خدا تو چاہتا ہے کہ تم پر مہربانی کرے اور جو لوگ اپنی خواہشوں کے پیچھے چلتے ہیں وہ چاہتے ہیں کہ تم سیدھے راستے سے بھٹک کر دور جا پڑو');
INSERT INTO "jalandhari" VALUES(4,28,'4:28','خدا چاہتا ہے کہ تم پر سے بوجھ ہلکا کرے اور انسان (طبعاً) کمزور پیدا ہوا ہے');
INSERT INTO "jalandhari" VALUES(4,29,'4:29','مومنو! ایک دوسرے کا مال ناحق نہ کھاؤ ہاں اگر آپس کی رضامندی سے تجارت کا لین دین ہو (اور اس سے مالی فائدہ حاصل ہو جائے تو وہ جائز ہے) اور اپنے آپ کو ہلاک نہ کرو کچھ شک نہیں کہ خدا تم پر مہربان ہے');
INSERT INTO "jalandhari" VALUES(4,30,'4:30','اور جو تعدی اور ظلم سے ایسا کرے گا ہم اس کو عنقریب جہنم میں داخل کریں گے اور یہ خدا کو آسان ہے');
INSERT INTO "jalandhari" VALUES(4,31,'4:31','اگر تم بڑے بڑے گناہوں سے جن سے تم کو منع کیا جاتا ہے اجتناب رکھو گے تو ہم تمہارے (چھوٹے چھوٹے) گناہ معاف کردیں گے اور تمہیں عزت کے مکانوں میں داخل کریں گے');
INSERT INTO "jalandhari" VALUES(4,32,'4:32','اور جس چیز میں خدا نے تم میں سے بعض کو بعض پر فضیلت دی ہے اس کی ہوس مت کرو مردوں کو ان کاموں کا ثواب ہے جو انہوں نے کئے اور عورتوں کو ان کاموں کا ثواب ہے جو انہوں نے کئے اور خدا سے اس کا فضل (وکرم) مانگتے رہو کچھ شک نہیں کہ خدا ہر چیز سے واقف ہے');
INSERT INTO "jalandhari" VALUES(4,33,'4:33','اور جو مال ماں باپ اور رشتہ دار چھوڑ مریں تو (حق داروں میں تقسیم کردو کہ) ہم نے ہر ایک کے حقدار مقرر کردیئے ہیں اور جن لوگوں سے تم عہد کرچکے ہو ان کو بھی ان کا حصہ دو بےشک خدا ہر چیز کے سامنے ہے');
INSERT INTO "jalandhari" VALUES(4,34,'4:34','مرد عورتوں پر مسلط وحاکم ہیں اس لئے کہ خدا نے بعض کو بعض سے افضل بنایا ہے اور اس لئے بھی کہ مرد اپنا مال خرچ کرتے ہیں تو جو نیک بیبیاں ہیں وہ مردوں کے حکم پر چلتی ہیں اور ان کے پیٹھ پیچھے خدا کی حفاظت میں (مال وآبرو کی) خبرداری کرتی ہیں اور جن عورتوں کی نسبت تمہیں معلوم ہو کہ سرکشی (اور بدخوئی) کرنے لگی ہیں تو (پہلے) ان کو (زبانی) سمجھاؤ (اگر نہ سمجھیں تو) پھر ان کے ساتھ سونا ترک کردو اگر اس پر بھی باز نہ آئیں تو زدوکوب کرو اور اگر فرمانبردار ہوجائیں تو پھر ان کو ایذا دینے کا کوئی بہانہ مت ڈھونڈو بےشک خدا سب سے اعلیٰ (اور) جلیل القدر ہے');
INSERT INTO "jalandhari" VALUES(4,35,'4:35','اور اگر تم کو معلوم ہو کہ میاں بیوی میں ان بن ہے تو ایک منصف مرد کے خاندان میں سے اور ایک منصف عورت کے خاندان میں سے مقرر کرو وہ اگر صلح کرا دینی چاہیں گے تو خدا ان میں موافقت پیدا کردے گا کچھ شک نہیں کہ خدا سب کچھ جانتا اور سب باتوں سے خبردار ہے');
INSERT INTO "jalandhari" VALUES(4,36,'4:36','اور خدا ہی کی عبادت کرو اور اس کے ساتھ کسی چیز کو شریک نہ بناؤ اور ماں باپ اور قرابت والوں اور یتیموں اور محتاجوں اور رشتہ دار ہمسائیوں اور اجنبی ہمسائیوں اور رفقائے پہلو (یعنی پاس بیٹھنے والوں) اور مسافروں اور جو لوگ تمہارے قبضے میں ہوں سب کے ساتھ احسان کرو کہ خدا (احسان کرنے والوں کو دوست رکھتا ہے اور) تکبر کرنے والے بڑائی مارنے والے کو دوست نہیں رکھتا');
INSERT INTO "jalandhari" VALUES(4,37,'4:37','جو خود بھی بخل کریں اور لوگوں کو بھی بخل سکھائیں اور جو (مال) خدا نے ان کو اپنے فضل سے عطا فرمایا ہے اسے چھپا چھپا کے رکھیں اور ہم نے ناشکروں کے لئے ذلت کا عذاب تیار کر رکھا ہے');
INSERT INTO "jalandhari" VALUES(4,38,'4:38','اور خرچ بھی کریں تو (خدا کے لئے نہیں بلکہ) لوگوں کے دکھانے کو اور ایمان نہ خدا پر لائیں اور نہ روز آخرت پر (ایسے لوگوں کو ساتھی شیطان ہے) اور جس کا ساتھی شیطان ہوا تو (کچھ شک نہیں کہ) وہ برا ساتھی ہے');
INSERT INTO "jalandhari" VALUES(4,39,'4:39','اور اگر یہ لوگ خدا پر اور روز قیامت پر ایمان لاتے اور جو کچھ خدا نے ان کو دیا تھا اس میں سے خرچ کرتے تو ان کا کیا نقصان ہوتا اور خدا ان کو خوب جانتا ہے');
INSERT INTO "jalandhari" VALUES(4,40,'4:40','خدا کسی کی ذرا بھی حق تلفی نہیں کرتا اور اگر نیکی (کی) ہوگی تو اس کو دوچند کردے گا اور اپنے ہاں سے اجرعظیم بخشے گا');
INSERT INTO "jalandhari" VALUES(4,41,'4:41','بھلا اس دن کا کیا حال ہوگا جب ہم ہر امت میں سے احوال بتائے والے کو بلائیں گے اور تم کو ان لوگوں کا حال (بتانے کو) گواہ طلب کریں گے');
INSERT INTO "jalandhari" VALUES(4,42,'4:42','اس روز کافر اور پیغمبر کے نافرمان آرزو کریں گے کہ کاش ان کو زمین میں مدفون کرکے مٹی برابر کردی جاتی اور خدا سے کوئی بات چھپا نہیں سکیں گے');
INSERT INTO "jalandhari" VALUES(4,43,'4:43','مومنو! جب تم نشے کی حالت میں ہو تو جب تک (ان الفاظ کو) جو منہ سے کہو سمجھنے (نہ) لگو نماز کے پاس نہ جاؤ اور جنابت کی حالت میں بھی (نماز کے پاس نہ جاؤ) جب تک کہ غسل (نہ) کرلو ہاں اگر بحالت سفر رستے چلے جارہے ہو اور پانی نہ ملنے کے سبب غسل نہ کرسکو تو تیمم کرکے نماز پڑھ لو) اور اگر تم بیمار ہو سفر میں ہو یا تم میں سے کوئی بیت الخلاء سے ہو کر آیا ہو یا تم عورتوں سے ہم بستر ہوئے ہو اور تمہیں پانی نہ ملے تو پاک مٹی لو اور منہ اور ہاتھوں پر مسح (کرکے تیمم) کرلو بےشک خدا معاف کرنے والا اور بخشنے والا ہے');
INSERT INTO "jalandhari" VALUES(4,44,'4:44','بھلا تم نے ان لوگوں کو نہیں دیکھا جن کو کتاب سے حصہ دیا گیا تھا کہ وہ گمراہی کو خریدتے ہیں اور چاہتے ہیں کہ تم بھی رستے سے بھٹک جاؤ');
INSERT INTO "jalandhari" VALUES(4,45,'4:45','اور خدا تمہارے دشمنوں سے خوب واقف ہے اور خدا ہی کافی کارساز ہے اور کافی مددگار ہے');
INSERT INTO "jalandhari" VALUES(4,46,'4:46','اور یہ جو یہودی ہیں ان میں سے کچھ لوگ ایسے بھی ہیں کہ کلمات کو ان کے مقامات سے بدل دیتے ہیں اور کہتے ہیں کہ ہم نے سن لیا اور نہیں مانا اور سنیئے نہ سنوائے جاؤ اور زبان کو مروڑ کر اور دین میں طعن کی راہ سے (تم سے گفتگو) کے وقت راعنا کہتے ہیں اور اگر (یوں) کہتے ہیں کہ ہم نے سن لیا اور مان لیا اور (صرف) اسمع اور (راعنا کی جگہ) انظرنا (کہتے) تو ان کے حق میں بہتر ہوتا اور بات بھی بہت درست ہوتی لیکن خدان نے ان کے کفر کے سبب ان پر لعنت کر رکھی ہے تو یہ کچھ تھوڑے ہی ایمان لاتے ہیں');
INSERT INTO "jalandhari" VALUES(4,47,'4:47','اے کتاب والو! قبل اس کے کہ ہم لوگوں کے مونہوں کو بگاڑ کر ان کی پیٹھ کی طرف پھیر دیں یا ان پر اس طرح لعنت کریں جس طرح ہفتے والوں پر کی تھی ہماری نازل کی ہوئی کتاب پر جو تمہاری کتاب کی بھی تصدیق کرتی ہے ایمان لے آؤ اور خدا نے جو حکم فرمایا سو (سمجھ لو کہ) ہوچکا');
INSERT INTO "jalandhari" VALUES(4,48,'4:48','خدا اس گناہ کو نہیں بخشے گا کہ کسی کو اس کا شریک بنایا جائے اور اس کے سوا اور گناہ جس کو چاہے معاف کردے اور جس نے خدا کا شریک مقرر کیا اس نے بڑا بہتان باندھا');
INSERT INTO "jalandhari" VALUES(4,49,'4:49','کیا تم نے ان لوگوں کو نہیں دیکھا جو اپنے تئیں پاکیزہ کہتے ہیں (نہیں) بلکہ خدا ہی جس کو چاہتا ہے پاکیزہ کرتا ہے اور ان پر دھاگے کے برابر بھی ظلم نہیں ہوگا');
INSERT INTO "jalandhari" VALUES(4,50,'4:50','دیکھو یہ خدا پر کیسا جھوٹ (طوفان) باندھتے ہیں اور یہی گناہ صریح کافی ہے');
INSERT INTO "jalandhari" VALUES(4,51,'4:51','بھلا تم نے ان لوگوں کو نہیں دیکھا جن کو کتاب سے حصہ دیا گیا ہے کہ بتوں اور شیطان کو مانتے ہیں اور کفار کے بارے میں کہتے ہیں کہ یہ لوگ مومنوں کی نسبت سیدھے رستے پر ہیں');
INSERT INTO "jalandhari" VALUES(4,52,'4:52','یہی لوگ ہیں جن پر خدا نے لعنت کی ہے اور جس پر خدا لعنت کرے تو تم اس کا کسی کو مددگار نہ پاؤ گے');
INSERT INTO "jalandhari" VALUES(4,53,'4:53','کیا ان کے پاس بادشاہی کا کچھ حصہ ہے تو لوگوں کو تل برابر بھی نہ دیں گے');
INSERT INTO "jalandhari" VALUES(4,54,'4:54','یا جو خدا نے لوگوں کو اپنے فضل سے دے رکھا ہے اس کا حسد کرتے ہیں تو ہم نے خاندان ابراہیم ؑ کو کتاب اور دانائی عطا فرمائی تھی اور سلطنت عظیم بھی بخشی تھی');
INSERT INTO "jalandhari" VALUES(4,55,'4:55','پھر لوگوں میں سے کسی نے تو اس کتاب کو مانا اور کوئی اس سے رکا (اور ہٹا) رہا تو نہ ماننے والوں (کے جلانے) کو دوزخ کی جلتی ہوئی آگ کافی ہے');
INSERT INTO "jalandhari" VALUES(4,56,'4:56','جن لوگوں نے ہماری آیتوں سے کفر کیا ان کو ہم عنقریب آگ میں داخل کریں گے جب ان کی کھالیں گل (اور جل) جائیں گی تو ہم اور کھالیں بدل دیں گے تاکہ (ہمیشہ) عذاب (کا مزہ چکھتے) رہیں بےشک خدا غالب حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(4,57,'4:57','اور جو ایمان لائے اور نیک عمل کرتے رہے ان کو ہم بہشتوں میں داخل کریں گے جن کے نیچے نہریں بہہ رہی ہیں وہ ان میں ہمیشہ ہمیشہ رہیں گے وہاں ان کے لئے پاک بیبیاں ہیں اور ان کو ہم گھنے سائے میں داخل کریں گے');
INSERT INTO "jalandhari" VALUES(4,58,'4:58','خدا تم کو حکم دیتا ہے کہ امانت والوں کی امانتیں ان کے حوالے کردیا کرو اور جب لوگوں میں فیصلہ کرنے لگو تو انصاف سے فیصلہ کیا کرو خدا تمہیں بہت خوب نصیحت کرتا ہے بےشک خدا سنتا اور دیکھتا ہے');
INSERT INTO "jalandhari" VALUES(4,59,'4:59','مومنو! خدا اور اس کے رسول کی فرمانبرداری کرو اور جو تم میں سے صاحب حکومت ہیں ان کی بھی اور اگر کسی بات میں تم میں اختلاف واقع ہو تو اگر خدا اور روز آخرت پر ایمان رکھتے ہو تو اس میں خدا اور اس کے رسول (کے حکم) کی طرف رجوع کرو یہ بہت اچھی بات ہے اور اس کا مآل بھی اچھا ہے');
INSERT INTO "jalandhari" VALUES(4,60,'4:60','کیا تم نے ان لوگوں کو نہیں دیکھا جو دعویٰ تو یہ کرتے ہیں کہ جو (کتاب) تم پر نازل ہوئی اور جو (کتابیں) تم سے پہلے نازل ہوئیں ان سب پر ایمان رکھتے ہیں اور چاہتے یہ ہیں کہ اپنا مقدمہ ایک سرکش کے پاس لے جا کر فیصلہ کرائیں حالانکہ ان کو حکم دیا گیا تھا کہ اس سے اعتقاد نہ رکھیں اور شیطان (تو یہ) چاہتا ہے کہ ان کو بہکا کر رستے سے دور ڈال دے');
INSERT INTO "jalandhari" VALUES(4,61,'4:61','اور جب ان سے کہا جاتا ہے کہ جو حکم خدا نے نازل فرمایا ہے اس کی طرف (رجوع کرو) اور پیغمبر کی طرف آؤ تو تم منافقوں کو دیکھتے ہو کہ تم سے اعراض کرتے اور رکے جاتے ہیں');
INSERT INTO "jalandhari" VALUES(4,62,'4:62','تو کیسی (ندامت کی) بات ہے کہ جب ان کے اعمال (کی شامت سے) ان پر کوئی مصیبت واقع ہوتی ہے تو تمہارے پاس بھاگے آتے ہیں اور قسمیں کھاتے ہیں کہ والله ہمارا مقصود تو بھلائی اور موافقت تھا');
INSERT INTO "jalandhari" VALUES(4,63,'4:63','ان لوگوں کے دلوں میں جو کچھ ہے خدا اس کو خوب جانتا ہے تم ان (کی باتوں) کو کچھ خیال نہ کرو اور انہیں نصیحت کرو اور ان سے ایسی باتیں کہو جو ان کے دلوں میں اثر کر جائیں');
INSERT INTO "jalandhari" VALUES(4,64,'4:64','اور ہم نے جو پیغمبر بھیجا ہے اس لئے بھیجا ہے کہ خدا کے فرمان کے مطابق اس کا حکم مانا جائے اور یہ لوگ جب اپنے حق میں ظلم کر بیٹھے تھے اگر تمہارے پاس آتے اور خدا سے بخشش مانگتے اور رسول (خدا) بھی ان کے لئے بخشش طلب کرتے تو خدا کو معاف کرنے والا (اور) مہربان پاتے');
INSERT INTO "jalandhari" VALUES(4,65,'4:65','تمہارے پروردگار کی قسم یہ لوگ جب تک اپنے تنازعات میں تمہیں منصف نہ بنائیں اور جو فیصلہ تم کردو اس سے اپنے دل میں تنگ نہ ہوں بلکہ اس کو خوشی سے مان لیں تب تک مومن نہیں ہوں گے');
INSERT INTO "jalandhari" VALUES(4,66,'4:66','اور اگر ہم انہیں حکم دیتے کہ اپنے آپ کو قتل کر ڈالو یا اپنے گھر چھوڑ کر نکل جاؤ تو ان میں سے تھوڑے ہی ایسا کرتے اور اگر یہ اس نصیحت پر کاربند ہوتے جو ان کو کی جاتی ہے تو ان کے حق میں بہتر اور (دین میں) زیادہ ثابت قدمی کا موجب ہوتا');
INSERT INTO "jalandhari" VALUES(4,67,'4:67','اور ہم ان کو اپنے ہاں سے اجر عظیم بھی عطا فرماتے');
INSERT INTO "jalandhari" VALUES(4,68,'4:68','اور سیدھا رستہ بھی دکھاتے');
INSERT INTO "jalandhari" VALUES(4,69,'4:69','اور جو لوگ خدا اور اس کے رسول کی اطاعت کرتے ہیں وہ (قیامت کے روز) ان لوگوں کے ساتھ ہوں گے جن پر خدا نے بڑا فضل کیا یعنی انبیاء اور صدیق اور شہید اور نیک لوگ اور ان لوگوں کی رفاقت بہت ہی خوب ہے');
INSERT INTO "jalandhari" VALUES(4,70,'4:70','یہ خدا کا فضل ہے اور خدا جاننے والا کافی ہے');
INSERT INTO "jalandhari" VALUES(4,71,'4:71','مومنو! (جہاد کے لئے) ہتھیار لے لیا کرو پھر یا تو جماعت جماعت ہو کر نکلا کرو یا سب اکھٹے کوچ کیا کرو');
INSERT INTO "jalandhari" VALUES(4,72,'4:72','اور تم میں کوئی ایسا بھی ہے کہ (عمداً) دیر لگاتا ہے۔ پھر اگر تم پر کوئی مصیبت پڑ جائے تو کہتا ہے کہ خدا نے مجھ پر بڑی مہربانی کی کہ میں ان میں موجود نہ تھا');
INSERT INTO "jalandhari" VALUES(4,73,'4:73','اور اگر خدا تم پر فضل کرے تو اس طرح سے کہ گویا تم میں اس میں دوستی تھی ہی نہیں (کہ افسوس کرتا اور) کہتا ہے کہ کاش میں بھی ان کے ساتھ ہوتا تو مقصد عظیم حاصل کرتا');
INSERT INTO "jalandhari" VALUES(4,74,'4:74','تو جو لوگ آخرت (کو خریدتے اور اس) کے بدلے دنیا کی زندگی کو بیچنا چاہتے ہیں اُن کو چاہیئے کہ خدا کی راہ میں جنگ کریں اور جو شخص خدا کی راہ میں جنگ کرے اور شہید ہوجائے یا غلبہ پائے ہم عنقریب اس کو بڑا ثواب دیں گے');
INSERT INTO "jalandhari" VALUES(4,75,'4:75','اور تم کو کیا ہوا ہے کہ خدا کی راہ میں اور اُن بےبس مردوں اور عورتوں اور بچوں کی خاطر نہیں لڑتے جو دعائیں کیا کرتے ہیں کہ اے پروردگار ہم کو اس شہر سے جس کے رہنے والے ظالم ہیں نکال کر کہیں اور لے جا۔ اور اپنی طرف سے کسی کو ہمارا حامی بنا۔ اور اپنی ہی طرف سے کسی کو ہمارا مددگار مقرر فرما');
INSERT INTO "jalandhari" VALUES(4,76,'4:76','جو مومن ہیں وہ تو خدا کے لئے لڑتے ہیں اور جو کافر ہیں وہ بتوں کے لئے لڑتے ہیں سو تم شیطان کے مددگاروں سے لڑو۔ (اور ڈرو مت) کیونکہ شیطان کا داؤ بودا ہوتا ہے');
INSERT INTO "jalandhari" VALUES(4,77,'4:77','بھلا تم نے ان لوگوں کو نہیں دیکھا جن کو (پہلے یہ) حکم دیا گیا تھا کہ اپنے ہاتھوں کو (جنگ سے) روکے رہو اور نماز پڑھتے رہو اور زکوٰة دیتے رہو پھر جب ان پر جہاد فرض کردیا گیا تو بعض لوگ ان میں سے لوگوں سے یوں ڈرنے لگے جیسے خدا سے ڈرا کرتے ہیں بلکہ اس سے بھی زیادہ اور بڑبڑانے لگے کہ اے خدا تو نے ہم پر جہاد (جلد) کیوں فرض کردیا تھوڑی مدت اور ہمیں کیوں مہلت نہ دی (اے پیغمبر ان س)ے کہہ دو کہ دنیا کا فائدہ بہت تھوڑا ہے اور بہت اچھی چیز تو پرہیزگار کے لئے (نجات) آخرت ہے اور تم پر دھاگے برابر بھی ظلم نہیں کیا جائے گا');
INSERT INTO "jalandhari" VALUES(4,78,'4:78','(اے جہاد سے ڈرنے والو) تم کہیں رہو موت تو تمہیں آ کر رہے گی خواہ بڑے بڑے محلوں میں رہو اور ان لوگوں کو اگر کوئی فائدہ پہنچتا ہے تو کہتے ہیں یہ خدا کی طرف سے ہے اور اگر کوئی گزند پہنچتا ہے تو (اے محمدصلى الله عليه وسلم تم سے) کہتے ہیں کہ یہ گزند آپ کی وجہ سے (ہمیں پہنچا) ہے کہہ دو کہ (رنج وراحت) سب الله ہی کی طرف سے ہے ان لوگوں کو کیا ہوگیا ہے کہ بات بھی نہیں سمجھ سکتے');
INSERT INTO "jalandhari" VALUES(4,79,'4:79','اے (آدم زاد) تجھ کو جو فائدہ پہنچے وہ خدا کی طرف سے ہے اور جو نقصان پہنچے وہ تیری ہی (شامت اعمال) کی وجہ سے ہے اور (اے محمدصلى الله عليه وسلم) ہم نے تم کو لوگوں (کی ہدایت) کے لئے پیغمبر بنا کر بھیجا ہے اور (اس بات کا) خدا ہی گواہ کافی ہے');
INSERT INTO "jalandhari" VALUES(4,80,'4:80','جو شخص رسول کی فرمانبرداری کرے گا تو بےشک اس نے خدا کی فرمانبرداری کی اور جو نافرمانی کرے گا تو اے پیغمبر تمہیں ہم نے ان کا نگہبان بنا کر نہیں بھیجا');
INSERT INTO "jalandhari" VALUES(4,81,'4:81','اور یہ لوگ منہ سے تو کہتے ہیں کہ (آپ کی) فرمانبرداری (دل سے منظور ہے) لیکن جب تمہارے پاس سے چلے جاتے ہیں تو ان میں سے بعض لوگ رات کو تمہاری باتوں کے خلاف مشورے کرتے ہیں اور جو مشورے یہ کرتے ہیں خدا ان کو لکھ لیتا ہے تو ان کا کچھ خیال نہ کرو اور خدا پر بھروسہ رکھو اور خدا ہی کافی کارساز ہے');
INSERT INTO "jalandhari" VALUES(4,82,'4:82','بھلا یہ قرآن میں غور کیوں نہیں کرتے؟ اگر یہ خدا کے سوا کسی اور کا (کلام) ہوتا تو اس میں (بہت سا) اختلاف پاتے');
INSERT INTO "jalandhari" VALUES(4,83,'4:83','اور جب ان کے پاس امن یا خوف کی کوئی خبر پہنچتی ہے تو اس کو مشہور کردیتے ہیں اور اگر اس کو پیغمبر اور اپنے سرداروں کے پاس پہنچاتے تو تحقیق کرنے والے اس کی تحقیق کر لیتے اور اگر تم پر خدا کا فضل اور اس کی مہربانی نہ ہوتی تو چند اشخاص کے سوا سب شیطان کے پیرو ہوجاتے');
INSERT INTO "jalandhari" VALUES(4,84,'4:84','تو (اے محمدصلى الله عليه وسلم) تم خدا کی راہ میں لڑو تم اپنے سوا کسی کے ذمہ دار نہیں اور مومنوں کو بھی ترغیب دو قریب ہے کہ خدا کافروں کی لڑائی کو بند کردے اور خدا لڑائی کے اعتبار سے بہت سخت ہے اور سزا کے لحاظ سے بھی بہت سخت ہے');
INSERT INTO "jalandhari" VALUES(4,85,'4:85','جو شخص نیک بات کی سفارش کرے تو اس کو اس (کے ثواب) میں سے حصہ ملے گا اور جو بری بات کی سفارش کرے اس کو اس (کے عذاب) میں سے حصہ ملے گا اور خدا ہر چیز پر قدرت رکھتا ہے');
INSERT INTO "jalandhari" VALUES(4,86,'4:86','اور جب تم کو کوئی دعا دے تو (جواب میں) تم اس سے بہتر (کلمے) سے (اسے) دعا دو یا انہیں لفظوں سے دعا دو بےشک خدا ہر چیز کا حساب لینے والا ہے');
INSERT INTO "jalandhari" VALUES(4,87,'4:87','خدا (وہ معبود برحق ہے کہ) اس کے سوا کوئی عبادت کے لائق نہیں وہ قیامت کے دن تم سب کو ضرور جمع کرے گا اور خدا سے بڑھ کر بات کا سچا کون ہے؟');
INSERT INTO "jalandhari" VALUES(4,88,'4:88','تو کیا سبب ہے کہ تم منافقوں کے بارے میں دو گروہ ہو رہے ہو حالانکہ خدا نے ان کو ان کے کرتوتوں کے سبب اوندھا کردیا ہے کیا تم چاہتے ہو کہ جس شخص کو خدا نے گمراہ کردیا ہے اس کو رستے پر لے آؤ اور جس شخص کو خدا گمراہ کردے تو اس کے لئے کبھی بھی رستہ نہیں پاؤ گے');
INSERT INTO "jalandhari" VALUES(4,89,'4:89','وہ تو یہی چاہتے ہیں کہ جس طرح وہ خود کافر ہیں (اسی طرح) تم بھی کافر ہو کر (سب) برابر ہوجاؤ تو جب تک وہ خدا کی راہ میں وطن نہ چھوڑ جائیں ان میں سے کسی کو دوست نہ بنانا اگر (ترک وطن کو) قبول نہ کریں تو ان کو پکڑ لو اور جہاں پاؤ قتل کردو اور ان میں سے کسی کو اپنا رفیق اور مددگار نہ بناؤ');
INSERT INTO "jalandhari" VALUES(4,90,'4:90','مگر جو لوگ ایسے لوگوں سے جا ملے ہوں جن میں اور تم میں (صلح کا) عہد ہو یا اس حال میں کہ ان کے دل تمہارے ساتھ یا اپنی قوم کے ساتھ لڑنے سے رک گئے ہوں تمہارے پاس آجائیں (تو احتراز ضروری نہیں) اور اگر خدا چاہتا تو ان کو تم پر غالب کردیتا تو وہ تم سے ضرور لڑتے پھر اگر وہ تم سے (جنگ کرنے سے) کنارہ کشی کریں اور لڑیں نہیں اور تمہاری طرف صلح (کا پیغام) بھیجیں تو خدا نے تمہارے لئے ان پر (زبردستی کرنے کی) کوئی سبیل مقرر نہیں کی');
INSERT INTO "jalandhari" VALUES(4,91,'4:91','تم کچھ اور لوگ ایسے بھی پاؤ گے جو یہ چاہتے ہیں کہ تم سے بھی امن میں رہیں اور اپنی قوم سے بھی امن میں رہیں لیکن فتنہ انگیزی کو بلائے جائیں تو اس میں اوندھے منہ گر پڑیں تو ایسے لوگ اگر تم سے (لڑنے سے) کنارہ کشی نہ کریں اور نہ تمہاری طرف (پیغام) صلح بھیجیں اور نہ اپنے ہاتھوں کو روکیں تو ان کو پکڑ لو اور جہاں پاؤ قتل کردو ان لوگوں کے مقابلے میں ہم نے تمہارے لئے سند صریح مقرر کردی ہے');
INSERT INTO "jalandhari" VALUES(4,92,'4:92','اور کسی مومن کو شایان نہیں کہ مومن کو مار ڈالے مگر بھول کر اور جو بھول کر بھی مومن کو مار ڈالے تو (ایک تو) ایک مسلمان غلام آزاد کردے اور (دوسرے) مقتول کے وارثوں کو خون بہا دے ہاں اگر وہ معاف کردیں (تو ان کو اختیار ہے) اگر مقتول تمہارے دشمنوں کی جماعت میں سے ہو اور وہ خود مومن ہو تو صرف ایک مسلمان غلام آزاد کرنا چاہیئے اور اگر مقتول ایسے لوگوں میں سے ہو جن میں اور تم میں صلح کا عہد ہو تو وارثان مقتول کو خون بہا دینا اور ایک مسلمان غلام آزاد کرنا چاہیئے اور جس کو یہ میسر نہ ہو وہ متواتر دو مہینے کے روزے رکھے یہ (کفارہ) خدا کی طرف سے (قبول) توبہ (کے لئے) ہے اور خدا (سب کچھ) جانتا اور بڑی حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(4,93,'4:93','اور جو شخص مسلمان کو قصداً مار ڈالے گا تو اس کی سزا دوزخ ہے جس میں وہ ہمیشہ (جلتا) رہے گا اور خدا اس پر غضبناک ہوگا اور اس پر لعنت کرے گا اور ایسے شخص کے لئے اس نے بڑا (سخت) عذاب تیار کر رکھا ہے');
INSERT INTO "jalandhari" VALUES(4,94,'4:94','مومنو! جب تم خدا کی راہ میں باہر نکلو کرو تو تحقیق سے کام لیا کرو اور جو شخص تم سے سلام علیک کرے اس سے یہ نہ کہو کہ تم مومن نہیں اور اس سے تمہاری غرض یہ ہو کہ دنیا کی زندگی کا فائدہ حاصل کرو سو خدا کے نزدیک بہت سے غنیمتیں ہیں تم بھی تو پہلے ایسے ہی تھے پھر خدا نے تم پر احسان کیا تو (آئندہ) تحقیق کرلیا کرو اور جو عمل تم کرتے ہو خدا کو سب کی خبر ہے');
INSERT INTO "jalandhari" VALUES(4,95,'4:95','جو مسلمان (گھروں میں) بیٹھ رہتے (اور لڑنے سے جی چراتے) ہیں اور کوئی عذر نہیں رکھتے وہ اور جو خدا کی راہ میں اپنے مال اور جان سے لڑتے ہیں وہ دونوں برابر نہیں ہو سکتے خدا نے مال اور جان سے جہاد کرنے والوں کو بیٹھ رہنے والوں پر درجے میں فضیلت بخشی ہے اور (گو) نیک وعدہ سب سے ہے لیکن اجر عظیم کے لحاظ سے خدا نے جہاد کرنے والوں کو بیٹھ رہنے والوں پر کہیں فضیلت بخشی ہے');
INSERT INTO "jalandhari" VALUES(4,96,'4:96','(یعنی) خدا کی طرف سے درجات میں اور بخشش میں اور رحمت میں اور خدا بڑا بخشنے والا (اور) مہربان ہے');
INSERT INTO "jalandhari" VALUES(4,97,'4:97','اور جو لوگ اپنی جانوں پر ظلم کرتے ہیں جب فرشتے ان کی جان قبض کرنے لگتے ہیں تو ان سے پوچھتے ہیں کہ تم کس حال میں تھے وہ کہتے ہیں کہ ہم ملک میں عاجز وناتواں تھے فرشتے کہتے ہیں کیا خدا کا ملک فراخ نہیں تھا کہ تم اس میں ہجرت کر جاتے ایسے لوگوں کا ٹھکانہ دوزخ ہے اور وہ بری جگہ ہے');
INSERT INTO "jalandhari" VALUES(4,98,'4:98','ہاں جو مرد اور عورتیں اور بچے بےبس ہیں کہ نہ تو کوئی چارہ کر سکتے ہیں اور نہ رستہ جانتے ہیں');
INSERT INTO "jalandhari" VALUES(4,99,'4:99','قریب ہے کہ خدا ایسوں کو معاف کردے اور خدا معاف کرنے والا (اور) بخشنے والا ہے');
INSERT INTO "jalandhari" VALUES(4,100,'4:100','اور جو شخص خدا کی راہ میں گھر بار چھوڑ جائے وہ زمین میں بہت سی جگہ اور کشائش پائے گا اور جو شخص خدا اور رسول کی طرف ہجرت کرکے گھر سے نکل جائے پھر اس کو موت آپکڑے تو اس کا ثواب خدا کے ذمے ہوچکا اور خدا بخشنے والا اور مہربان ہے');
INSERT INTO "jalandhari" VALUES(4,101,'4:101','اور جب تم سفر کو جاؤ تو تم پر کچھ گناہ نہیں کہ نماز کو کم کرکے پڑھو بشرطیکہ تم کو خوف ہو کہ کافر لوگ تم کو ایذا دیں گے بےشک کافر تمہارے کھلے دشمن ہیں');
INSERT INTO "jalandhari" VALUES(4,102,'4:102','اور (اے پیغمبر) جب تم ان (مجاہدین کے لشکر) میں ہو اور ان کو نماز پڑھانے لگو تو چاہیئے کہ ان کی ایک جماعت تمہارے ساتھ مسلح ہو کر کھڑی رہے جب وہ سجدہ کرچکیں تو پرے ہو جائیں پھر دوسری جماعت جس نے نماز نہیں پڑھی (ان کی جگہ) آئے اور ہوشیار اور مسلح ہو کر تمہارے ساتھ نماز ادا کرے کافر اس گھات میں ہیں کہ تم ذرا اپنے ہتھیاروں اور سامان سے غافل ہو جاؤ تو تم پر یکبارگی حملہ کردیں اگر تم بارش کے سبب تکلیف میں یا بیمار ہو تو تم پر کچھ گناہ نہیں کہ ہتھیار اتار رکھو مگر ہوشیار ضرور رہنا خدا نے کافروں کے لئے ذلت کا عذاب تیار کر رکھا ہے');
INSERT INTO "jalandhari" VALUES(4,103,'4:103','پھر جب تم نماز تمام کرچکو تو کھڑے اور بیٹھے اور لیٹے (ہر حالت میں) خدا کو یاد کرو پھر جب خوف جاتا رہے تو (اس طرح سے) نماز پڑھو (جس طرح امن کی حالت میں پڑھتے ہو) بےشک نماز کا مومنوں پر اوقات (مقررہ) میں ادا کرنا فرض ہے');
INSERT INTO "jalandhari" VALUES(4,104,'4:104','اور کفار کا پیچھا کرنے میں سستی نہ کرنا اگر تم بےآرام ہوتے ہو تو جس طرح تم بےآرام ہوتے ہو اسی طرح وہ بھی بےآرام ہوتے ہیں اور تم خدا سے ایسی ایسی امیدیں رکھتے ہو جو وہ نہیں رکھ سکتے اور خدا سب کچھ جانتا اور (بڑی) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(4,105,'4:105','(اے پیغمبر) ہم نے تم پر سچی کتاب نازل کی ہے تاکہ خدا کی ہدایت کے مطابق لوگوں کے مقدمات میں فیصلہ کرو اور (دیکھو) دغابازوں کی حمایت میں کبھی بحث نہ کرنا');
INSERT INTO "jalandhari" VALUES(4,106,'4:106','اور خدا سے بخشش مانگنا بےشک خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(4,107,'4:107','اور لوگ اپنے ہم جنسوں کی خیانت کرتے ہیں ان کی طرف سے بحث نہ کرنا کیونکہ خدا خائن اور مرتکب جرائم کو دوست نہیں رکھتا');
INSERT INTO "jalandhari" VALUES(4,108,'4:108','یہ لوگوں سے تو چھپتے ہیں اور خدا سے نہیں چھپتے حالانکہ جب وہ راتوں کو ایسی باتوں کے مشورے کیا کرتے ہیں جن کو وہ پسند نہیں کرتا ان کے ساتھ ہوا کرتا ہے اور خدا ان کے (تمام) کاموں پر احاطہ کئے ہوئے ہے');
INSERT INTO "jalandhari" VALUES(4,109,'4:109','بھلا تم لوگ دنیا کی زندگی میں تو ان کی طرف سے بحث کر لیتے ہو قیامت کو ان کی طرف سے خدا کے ساتھ کون جھگڑے گا اور کون ان کا وکیل بنے گا؟');
INSERT INTO "jalandhari" VALUES(4,110,'4:110','اور جو شخص کوئی برا کام کر بیٹھے یا اپنے حق میں ظلم کرلے پھر خدا سے بخشش مانگے تو خدا کو بخشنے والا اور مہربان پائے گا');
INSERT INTO "jalandhari" VALUES(4,111,'4:111','اور جو کوئی گناہ کرتا ہے تو اس کا وبال اسی پر ہے اور خدا جاننے والا (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(4,112,'4:112','اور جو شخص کوئی قصور یا گناہ تو خود کرے لیکن اس سے کسی بےگناہ کو مہتم کردے تو اس نے بہتان اور صریح گناہ کا بوجھ اپنے سر پر رکھا');
INSERT INTO "jalandhari" VALUES(4,113,'4:113','اور اگر تم پر خدا کا فضل اور مہربانی نہ ہوتی تو ان میں سے ایک جماعت تم کو بہکانے کا قصد کر ہی چکی تھی اور یہ اپنے سوا (کسی کو) بہکا نہیں سکتے اور نہ تمہارا کچھ بگاڑ سکتے ہیں اور خدا نے تم پر کتاب اور دانائی نازل فرمائی ہے اور تمہیں وہ باتیں سکھائی ہیں جو تم جانتے نہیں تھے اور تم پر خدا کا بڑا فضل ہے');
INSERT INTO "jalandhari" VALUES(4,114,'4:114','ان لوگوں کی بہت سی مشورتیں اچھی نہیں ہاں (اس شخص کی مشورت اچھی ہوسکتی ہے) جو خیرات یا نیک بات یا لوگوں میں صلح کرنے کو کہے اور جو ایسے کام خدا کی خوشنودی حاصل کرنے کے لئے کرے گا تو ہم اس کو بڑا ثواب دیں گے');
INSERT INTO "jalandhari" VALUES(4,115,'4:115','اور جو شخص سیدھا رستہ معلوم ہونے کے بعد پیغمبر کی مخالف کرے اور مومنوں کے رستے کے سوا اور رستے پر چلے تو جدھر وہ چلتا ہے ہم اسے ادھر ہی چلنے دیں گے اور (قیامت کے دن) جہنم میں داخل کریں گے اور وہ بری جگہ ہے');
INSERT INTO "jalandhari" VALUES(4,116,'4:116','خدا اس کے گناہ کو نہیں بخشے گا کہ کسی کو اس کا شریک بنایا جائے اور اس کے سوا (اور گناہ) جس کو چاہیے گا بخش دے گا۔ اور جس نے خدا کے ساتھ شریک بنایا وہ رستے سے دور جا پڑا');
INSERT INTO "jalandhari" VALUES(4,117,'4:117','یہ جو خدا کے سوا پرستش کرتے ہیں تو عورتوں کی اور پکارتے ہیں تو شیطان کی سرکش ہی کو');
INSERT INTO "jalandhari" VALUES(4,118,'4:118','جس پر خدا نے لعنت کی ہے (جو خدا سے) کہنے لگا میں تیرے بندوں سے (غیر خدا کی نذر دلوا کر مال کا) ایک مقرر حصہ لے لیا کروں گا۔');
INSERT INTO "jalandhari" VALUES(4,119,'4:119','اور ان کو گمراہ کرتا اور امیدیں دلاتا ہروں گا اور یہ سکھاتا رہوں گا کہ جانوروں کے کان چیرتے رہیں اور (یہ بھی) کہتا رہوں گا کہ وہ خدا کی بنائی ہوئی صورتوں کو بدلتے رہیں اور جس شخص نے خدا کو چھوڑ کر شیطان کو دوست بنایا اور وہ صریح نقصان میں پڑ گیا');
INSERT INTO "jalandhari" VALUES(4,120,'4:120','وہ ان کو وعدے دیتا رہا اور امیدیں دلاتا ہے اور جو کچھ شیطان انہیں وعدے دیتا ہے جو دھوکا ہی دھوکا ہے');
INSERT INTO "jalandhari" VALUES(4,121,'4:121','ایسے لوگوں کا ٹھکانا جہنم ہے۔ اور وہ وہاں سے مخلصی نہیں پاسکیں گے');
INSERT INTO "jalandhari" VALUES(4,122,'4:122','اور جو لوگ ایمان لائے اور نیک کام کرتے رہے ان کو ہم بہشتوں میں داخل کریں گے جن کے نیچے نہریں جاری ہیں۔ ابدالآباد ان میں رہیں گے۔ یہ خدا کا سچا وعدہ ہے۔ اور خدا سے زیادہ بات کا سچا کون ہوسکتا ہے');
INSERT INTO "jalandhari" VALUES(4,123,'4:123','(نجات) نہ تو تمہاری آرزوؤں پر ہے اور نہ اہل کتاب کی آرزوؤں پر۔ جو شخص برے عمل کرے گا اسے اسی (طرح) کا بدلا دیا جائے گا اور وہ خدا کے سوا نہ کسی کو حمایتی پائے گا اور نہ مددگار');
INSERT INTO "jalandhari" VALUES(4,124,'4:124','اور جو نیک کام کرے گا مرد ہو یا عورت اور وہ صاحب ایمان بھی ہوگا تو ایسے لوگ بہشت میں داخل ہوں گے اور ان کی تل برابر بھی حق تلفی نہ کی جائے گی');
INSERT INTO "jalandhari" VALUES(4,125,'4:125','اور اس شخص سے کس کا دین اچھا ہوسکتا ہے جس نے حکم خدا کو قبول کیا اور وہ نیکوکار بھی ہے۔ اور ابراہیم کے دین کا پیرو ہے جو یکسوں (مسلمان ) تھے اور خدا نے ابراہیم کو اپنا دوست بنایا تھا');
INSERT INTO "jalandhari" VALUES(4,126,'4:126','اور آسمان وزمین میں جو کچھ ہے سب خدا ہی کا ہے۔ اور خدا ہر چیز پر احاطے کئے ہوئے ہے');
INSERT INTO "jalandhari" VALUES(4,127,'4:127','(اے پیغمبر) لوگ تم سے (یتیم) عورتوں کے بارے میں فتویٰ طلب کرتے ہیں۔ کہہ دو کہ خدا تم کو ان کے (ساتھ نکاح کرنے کے) معاملے میں اجازت دیتا ہے اور جو حکم اس کتاب میں پہلے دیا گیا ہے وہ ان یتیم عورتوں کے بارے میں ہے جن کو تم ان کا حق تو دیتے نہیں اور خواہش رکھتے ہو کہ ان کے ساتھ نکاح کرلو اور (نیز) بیچارے بیکس بچوں کے بارے میں۔ اور یہ (بھی حکم دیتا ہے) کہ یتیموں کے بارے میں انصاف پر قائم رہو۔ اور جو بھلائی تم کرو گے خدا اس کو جانتا ہے');
INSERT INTO "jalandhari" VALUES(4,128,'4:128','اور اگر کسی عورت کو اپنے خاوند کی طرف سے زیادتی یا بےرغبتی کا اندیشہ ہو تم میاں بیوی پر کچھ گناہ نہیں کہ آپس میں کسی قرارداد پر صلح کرلیں۔ اور صلح خوب (چیز) ہے اور طبیعتیں تو بخل کی طرف مائل ہوتی ہیں اور اگر تم نیکوکاری اور پرہیزگاری کرو گے تو خدا تمہارے سب کاموں سے واقف ہے');
INSERT INTO "jalandhari" VALUES(4,129,'4:129','اور تم خوا کتنا ہی چاہو عورتوں میں ہرگز برابری نہیں کرسکو گے تو ایسا بھی نہ کرنا کہ ایک ہی کی طرف ڈھل جاؤ اور دوسری کو (ایسی حالت میں) چھوڑ دو کہ گویا ادھر ہوا میں لٹک رہی ہے اور اگر آپس میں موافقت کرلو اور پرہیزگاری کرو تو خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(4,130,'4:130','اور اگر میاں بیوی (میں موافقت نہ ہوسکے اور) ایک دوسرے سے جدا ہوجائیں تو خدا ہر ایک کو اپنی دولت سے غنی کردے گا اور خدا بڑی کشائش والا اور حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(4,131,'4:131','اور جو کچھ آسمانوں اور جو کچھ زمین میں ہے سب خدا ہی کا ہے۔ اور جن لوگوں کو تم سے پہلے کتاب دی گئی تھی ان کو بھی اور (اے محمدصلى الله عليه وسلم) تم کو بھی ہم نے حکم تاکیدی کیا ہے کہ خدا سے ڈرتے رہو اور اگر کفر کرو گے تو (سمجھ رکھو کہ) جو کچھ آسمانوں میں اور جو کچھ زمین میں ہے سب خدا ہی کا ہے۔ اور خدا بے پروا اور سزاوار حمدوثنا ہے');
INSERT INTO "jalandhari" VALUES(4,132,'4:132','اور (پھر سن رکھو کہ) جو کچھ آسمانوں میں اور جو کچھ زمین میں ہے سب خدا ہی کا ہے اور خدا کارساز کافی ہے');
INSERT INTO "jalandhari" VALUES(4,133,'4:133','لوگو! اگر وہ چاہے تو تم کو فنا کردے اور (تمہاری جگہ) اور لوگوں کو پیدا کردے۔اور خدا اس بات پر قادر ہے');
INSERT INTO "jalandhari" VALUES(4,134,'4:134','جو شخص دنیا (میں عملوں) کی جزا کا طالب ہو تو خدا کے پاس دنیا اور آخرت (دونوں) کے لئے اجر (موجود) ہیں۔ اور خدا سنتا دیکھتا ہے');
INSERT INTO "jalandhari" VALUES(4,135,'4:135','اے ایمان والو! انصاف پر قائم رہو اور خدا کے لئے سچی گواہی دو خواہ (اس میں) تمہارا یا تمہارےماں باپ اور رشتہ داروں کا نقصان ہی ہو۔ اگر کوئی امیر ہے یا فقیر تو خدا ان کا خیر خواہ ہے۔ تو تم خواہش نفس کے پیچھے چل کر عدل کو نہ چھوڑ دینا۔ اگر تم پیچیدا شہادت دو گے یا (شہادت سے) بچنا چاہو گے تو (جان رکھو) خدا تمہارے سب کاموں سے واقف ہے');
INSERT INTO "jalandhari" VALUES(4,136,'4:136','مومنو! خدا پر اور اس کے رسول پر اور جو کتاب اس نے اپنی پیغمبر (آخرالزماں) پر نازل کی ہے اور جو کتابیں اس سے پہلے نازل کی تھیں سب پر ایمان لاؤ۔ اور جو شخص خدا اور اس کے فرشتوں اور اس کی کتابوں اور اس کے پیغمبروں اور روزقیامت سے انکار کرے وہ رستے سے بھٹک کر دور جا پڑا');
INSERT INTO "jalandhari" VALUES(4,137,'4:137','جو لوگ ایمان لائے پھر کافر ہوگئے پھر ایمان لائے پھر کافر ہوگئے پھر کفر میں بڑھتے گئے ان کو خدا نہ تو بخشے گا اور نہ سیدھا رستہ دکھائے گا');
INSERT INTO "jalandhari" VALUES(4,138,'4:138','(اے پیغمبر) منافقوں (یعنی دو رخے لوگوں) کو بشارت سناد دو کہ ان کے لئے دکھ دینے والا عذاب (تیار) ہے');
INSERT INTO "jalandhari" VALUES(4,139,'4:139','جو مومنوں کو چھوڑ کر کافروں کو دوست بناتے ہیں۔ کیا یہ ان کے ہاں عزت حاصل کرنا چاہتے ہیں تو عزت تو سب خدا ہی کی ہے');
INSERT INTO "jalandhari" VALUES(4,140,'4:140','اور خدا نے تم (مومنوں) پر اپنی کتاب میں (یہ حکم) نازل فرمایا ہے کہ جب تم (کہیں) سنو کہ خدا کی آیتوں سے انکار ہورہا ہے اور ان کی ہنسی اڑائی جاتی ہے تو جب تک وہ لوگ اور باتیں (نہ) کرنے لگیں۔ ان کے پاس مت بیٹھو۔ ورنہ تم بھی انہیں جیسے ہوجاؤ گے۔ کچھ شک نہیں کہ خدا منافقوں اور کافروں سب کو دوزخ میں اکھٹا کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(4,141,'4:141','جو تم کو دیکھتے رہتے ہیں اگر خدا کی طرف سے تم کو فتح ملے تو کہتے ہیں کیا ہم تمہارے ساتھ نہ تھے۔ اور اگر کافروں کو (فتح) نصیب ہو تو (ان سے) کہتے ہیں کیا ہم تم پر غالب نہیں تھے اور تم کو مسلمانوں (کے ہاتھ) سے بچایا نہیں۔ تو خدا تم میں قیامت کے دن فیصلہ کردے گا۔ اور خدا کافروں کو مومنوں پر ہرگز غلبہ نہیں دے گا');
INSERT INTO "jalandhari" VALUES(4,142,'4:142','منافق (ان چالوں سے اپنے نزدیک) خدا کو دھوکا دیتے ہیں (یہ اس کو کیا دھوکا دیں گے) وہ انہیں کو دھوکے میں ڈالنے والا ہے اور جب یہ نماز کو کھڑے ہوتے ہیں تو سست اور کاہل ہو کر (صرف) لوگوں کے دکھانے کو اور خدا کی یاد ہی نہیں کرتے مگر بہت کم');
INSERT INTO "jalandhari" VALUES(4,143,'4:143','بیچ میں پڑے لٹک رہے ہیں نہ ان کی طرف (ہوتے ہیں) نہ ان کی طرف اور جس کو خدا بھٹکائے تو اس کے لئے کبھی بھی رستہ نہ پاؤ گے');
INSERT INTO "jalandhari" VALUES(4,144,'4:144','اے اہل ایمان! مومنوں کے سوا کافروں کو دوست نہ بناؤ کیا تم چاہتے ہو کہ اپنے اوپر خدا کا صریح الزام لو؟');
INSERT INTO "jalandhari" VALUES(4,145,'4:145','کچھ شک نہیں کہ منافق لوگ دوزخ کے سب سے نیچے کے درجے میں ہوں گے۔ اور تم ان کا کسی کو مددگار نہ پاؤ گے');
INSERT INTO "jalandhari" VALUES(4,146,'4:146','ہاں جنہوں نے توبہ کی اور اپنی حالت کو درست کیا اور خدا (کی رسی) کو مضبوط پکڑا اور خاص خدا کے فرمانبردار ہوگئے تو ایسے لوگ مومنوں کے زمرے میں ہوں گے اور خدا عنقریب مومنوں کو بڑا ثواب دے گا');
INSERT INTO "jalandhari" VALUES(4,147,'4:147','اگر تم (خدا کے شکرگزار رہو اور (اس پر) ایمان لے آؤ تو خدا تم کو عذاب دے کر کیا کرے گا۔ اور خدا تو قدرشناس اور دانا ہے');
INSERT INTO "jalandhari" VALUES(4,148,'4:148','خدا اس بات کو پسند نہیں کرتا کہ کوئی کسی کو علانیہ برا کہے مگر وہ جو مظلوم ہو۔ اور خدا (سب کچھ) سنتا (اور) جانتا ہے');
INSERT INTO "jalandhari" VALUES(4,149,'4:149','اگر تم لوگ بھلائی کھلم کھلا کرو گے یا چھپا کر یا برائی سے درگزر کرو گے تو خدا بھی معاف کرنے والا (اور) صاحب قدرت ہے');
INSERT INTO "jalandhari" VALUES(4,150,'4:150','جو لوگ خدا سے اور اس کے پیغمبروں سے کفر کرتے ہیں اور خدا اور اس کے پیغمبروں میں فرق کرنا چاہتے ہیں اور کہتے ہیں کہ ہم بعض کو مانتے ہیں اور بعض کو نہیں مانتے اور ایمان اور کفر کے بیچ میں ایک راہ نکالنی چاہتے ہیں');
INSERT INTO "jalandhari" VALUES(4,151,'4:151','وہ بلا اشتباہ کافر ہیں اور کافروں کے لئے ہم نے ذلت کا عذاب تیار کر رکھا ہے');
INSERT INTO "jalandhari" VALUES(4,152,'4:152','اور جو لوگ خدا اور اس کے پیغمبروں پر ایمان لائے اور ان میں سے کسی میں فرق نہ کیا (یعنی سب کو مانا) ایسے لوگوں کو وہ عنقریب ان (کی نیکیوں) کے صلے عطا فرمائے گا اور خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(4,153,'4:153','(اے محمدصلى الله عليه وسلم) اہل کتاب تم سے درخواست کرتے ہیں کہ تم ان پر ایک (لکھی ہوئی) کتاب آسمان سے اتار لاؤ تو یہ موسیٰ سے اس سے بھی بڑی بڑی درخواستیں کرچکے ہیں (ان سے) کہتے تھے ہمیں خدا ظاہر (یعنی آنکھوں سے) دکھا دو سو ان کے گناہ کی وجہ سے ان کو بجلی نے آپکڑا۔ پھر کھلی نشانیاں آئے پیچھے بچھڑے کو (معبود) بنا بیٹھے تو اس سے بھی ہم نے درگزر کی۔ اور موسیٰ کو صریح غلبہ دیا');
INSERT INTO "jalandhari" VALUES(4,154,'4:154','اور ان سے عہد لینے کو ہم نے ان پر کوہ طور اٹھا کھڑا کیا اور انہیں حکم دیا کہ (شہر کے) دروازے میں (داخل ہونا تو) سجدہ کرتے ہوئے داخل ہونا اور یہ بھی حکم دیا کہ ہفتے کے دن (مچھلیاں پکڑنے) میں تجاویز (یعنی حکم کے خلاف) نہ کرنا۔ غرض ہم نے ان سے مضبوط عہد لیا');
INSERT INTO "jalandhari" VALUES(4,155,'4:155','(لیکن انہوں نے عہد کو توڑ ڈالا) تو ان کے عہد توڑ دینے اور خدا کی آیتوں سے کفر کرنے اور انبیاء کو ناحق مار ڈالنے اور یہ کہنے کے سبب کہ ہمارے دلوں پر پردے (پڑے ہوئے) ہیں۔ (خدا نے ان کو مردود کردیا اور ان کے دلوں پر پردے نہیں ہیں) بلکہ ان کے کفر کے سبب خدا نے ان پر مہر کردی ہے تو یہ کم ہی ایمان لاتے ہیں');
INSERT INTO "jalandhari" VALUES(4,156,'4:156','اور ان کے کفر کے سبب اور مریم پر ایک بہتان عظیم باندھنے کے سبب');
INSERT INTO "jalandhari" VALUES(4,157,'4:157','اور یہ کہنے کے سبب کہ ہم نے مریم کے بیٹے عیسیٰ مسیح کو جو خدا کے پیغمبر (کہلاتے) تھے قتل کردیا ہے (خدا نے ان کو معلون کردیا) اور انہوں نے عیسیٰ کو قتل نہیں کیا اور نہ انہیں سولی پر چڑھایا بلکہ ان کو ان کی سی صورت معلوم ہوئی اور جو لوگ ان کے بارے میں اختلاف کرتے ہیں وہ ان کے حال سے شک میں پڑے ہوئے ہیں اور پیروئی ظن کے سوا ان کو اس کا مطلق علم نہیں۔ اور انہوں نے عیسیٰ کو یقیناً قتل نہیں کیا');
INSERT INTO "jalandhari" VALUES(4,158,'4:158','بلکہ خدا نے ان کو اپنی طرف اٹھا لیا۔ اور خدا غالب اور حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(4,159,'4:159','اور کوئی اہل کتاب نہیں ہوگا مگر ان کی موت سے پہلے ان پر ایمان لے آئے گا۔ اور وہ قیامت کے دن ان پر گواہ ہوں گے');
INSERT INTO "jalandhari" VALUES(4,160,'4:160','تو ہم نے یہودیوں کے ظلموں کے سبب (بہت سی) پاکیزہ چیزیں جو ان کو حلال تھیں ان پر حرام کردیں اور اس سبب سے بھی کہ وہ اکثر خدا کے رستے سے (لوگوں کو) روکتے تھے');
INSERT INTO "jalandhari" VALUES(4,161,'4:161','اور اس سبب سے بھی کہ باوجود منع کئے جانے کے سود لیتے تھے اور اس سبب سے بھی کہ لوگوں کا مال ناحق کھاتے تھے۔ اور ان میں سے جو کافر ہیں ان کے لئے ہم نے درد دینے والا عذاب تیار کر رکھا ہے');
INSERT INTO "jalandhari" VALUES(4,162,'4:162','مگر جو لوگ ان میں سے علم میں پکے ہیں اور جو مومن ہیں وہ اس (کتاب) پر جو تم پر نازل ہوئی اور جو (کتابیں) تم سے پہلے نازل ہوئیں (سب پر) ایمان رکھتے ہیں اور نماز پڑھتے ہیں اور زکوٰة دیتے ہیں اور خدا اور روز آخرت کو مانتے ہیں۔ ان کو ہم عنقریب اجر عظیم دیں گے');
INSERT INTO "jalandhari" VALUES(4,163,'4:163','(اے محمدصلى الله عليه وسلم) ہم نے تمہاری طرف اسی طرح وحی بھیجی ہے جس طرح نوح اور ان سے پچھلے پیغمبروں کی طرف بھیجی تھی۔ اور ابراہیم اور اسمعیل اور اسحاق اور یعقوب اور اولاد یعقوب اور عیسیٰ اور ایوب اور یونس اور ہارون اور سلیمان کی طرف بھی ہم نے وحی بھیجی تھی اور داؤد کو ہم نے زبور بھی عنایت کی تھی');
INSERT INTO "jalandhari" VALUES(4,164,'4:164','اور بہت سے پیغمبر ہیں جن کے حالات ہم تم سے پیشتر بیان کرچکے ہیں اور بہت سے پیغمبر ہیں جن کے حالات تم سے بیان نہیں کئے۔ اور موسیٰ سے تو خدا نے باتیں بھی کیں');
INSERT INTO "jalandhari" VALUES(4,165,'4:165','(سب) پیغمبروں کو (خدا نے) خوشخبری سنانے والے اور ڈرانے والے (بنا کر بھیجا تھا) تاکہ پیغمبروں کے آنے کے بعد لوگوں کو خدا پر الزام کا موقع نہ رہے اور خدا غالب حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(4,166,'4:166','لیکن خدا نے جو (کتاب) تم پر نازل کی ہے اس کی نسبت خدا گواہی دیتا ہے کہ اس نے اپنے علم سے نازل کی ہے اور فرشتے بھی گواہی دیتے ہیں۔ اور گواہ تو خدا ہی کافی ہے');
INSERT INTO "jalandhari" VALUES(4,167,'4:167','جن لوگوں نے کفر کیا اور (لوگوں کو) خدا کے رستے سے روکا وہ رستے سے بھٹک کر دور جا پڑے');
INSERT INTO "jalandhari" VALUES(4,168,'4:168','جو لوگ کافر ہوئے اور ظلم کرتے رہے خدا ان کو بخشنے والا نہیں اور نہ انہیں رستہ ہی دکھائے گا');
INSERT INTO "jalandhari" VALUES(4,169,'4:169','ہاں دوزخ کا رستہ جس میں وہ ہمیشہ (جلتے) رہیں گے۔ اور یہ (بات) خدا کو آسان ہے');
INSERT INTO "jalandhari" VALUES(4,170,'4:170','لوگو! خدا کے پیغمبر تمہارے پاس تمہارے پروردگار کی طرف سے حق بات لے کر آئے ہیں تو (ان پر) ایمان لاؤ (یہی) تمہارے حق میں بہتر ہے۔ اور اگر کفر کرو گے تو (جان رکھو کہ) جو کچھ آسمانوں اور زمین میں ہے سب خداہی کا ہے اور خدا سب کچھ جاننے والا (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(4,171,'4:171','اے اہل کتاب اپنے دین (کی بات) میں حد سے نہ بڑھو اور خدا کے بارے میں حق کے سوا کچھ نہ کہو۔ مسیح (یعنی) مریم کے بیٹے عیسیٰ (نہ خدا تھے نہ خدا کے بیٹے بلکہ) خدا کے رسول اور کا کلمہٴ (بشارت) تھے جو اس نے مریم کی طرف بھیجا تھا اور اس کی طرف سے ایک روح تھے تو خدا اوراس کے رسولوں پر ایمان لاؤ۔ اور (یہ) نہ کہو (کہ خدا) تین (ہیں۔ اس اعتقاد سے) باز آؤ کہ یہ تمہارے حق میں بہتر ہے۔ خدا ہی معبود واحد ہے اور اس سے پاک ہے کہ اس کے اولاد ہو۔ جو کچھ آسمانوں میں اور جو کچھ زمین میں ہے سب اسی کا ہے۔ اور خدا ہی کارساز کافی ہے');
INSERT INTO "jalandhari" VALUES(4,172,'4:172','مسیح اس بات سے عار نہیں رکھتے کہ خدا کے بندے ہوں اور نہ مقرب فرشتے (عار رکھتے ہیں) اور جو شخص خدا کا بندہ ہونے کو موجب عار سمجھے اور سرکشی کرے تو خدا سب کو اپنے پاس جمع کرلے گا');
INSERT INTO "jalandhari" VALUES(4,173,'4:173','تو جو لوگ ایمان لائے اور نیک کام کرتے رہے وہ ان کو ان کا پورا بدلا دے گا اور اپنے فضل سے کچھ زیادہ بھی عنایت کرے گا۔ اور جنہوں نے (بندوں ہونے سے) عاروانکار اور تکبر کیا ان کو تکلیف دینے والا عذاب دے گا۔ اور یہ لوگ خدا کے سوا اپنا حامی اور مددگار نہ پائیں گے');
INSERT INTO "jalandhari" VALUES(4,174,'4:174','لوگو تمہارے پروردگار کی طرف سے تمہارے پاس دلیل (روشن) آچکی ہے اور ہم نے (کفر اور ضلالت کا اندھیرا دور کرنے کو) تمہاری طرف چمکتا ہوا نور بھیج دیا ہے');
INSERT INTO "jalandhari" VALUES(4,175,'4:175','پس جو لوگ خدا پر ایمان لائے اور اس (کے دین کی رسی) کو مضبوط پکڑے رہے ان کو وہ اپنی رحمت اور فضل (کے بہشتوں) میں داخل کرے گا۔ اور اپنی طرف (پہچنے کا) سیدھا رستہ دکھائے گا');
INSERT INTO "jalandhari" VALUES(4,176,'4:176','(اے پیغمبر) لوگ تم سے (کلالہ کے بارے میں) حکم (خدا) دریافت کرتے ہیں کہہ دو کہ خدا کلالہ بارے میں یہ حکم دیتا ہے کہ اگر کوئی ایسا مرد مرجائے جس کے اولاد نہ ہو (اور نہ ماں باپ) اور اس کے بہن ہو تو اس کو بھائی کے ترکے میں سے آدھا حصہ ملے گا۔ اور اگر بہن مرجائے اور اس کے اولاد نہ ہو تو اس کے تمام مال کا وارث بھائی ہوگا اور اگر (مرنے والے بھائی کی) دو بہنیں ہوں تو دونوں کو بھائی کے ترکے میں سے دو تہائی۔ اور اگر بھائی اور بہن یعنی مرد اور عورتیں ملے جلے وارث ہوں تو مرد کا حصہ دو عورتوں کے برابر ہے۔ (یہ احکام) خدا تم سے اس لئے بیان فرماتا ہے کہ بھٹکتے نہ پھرو۔ اور خدا ہر چیز سے واقف ہے');
INSERT INTO "jalandhari" VALUES(5,1,'5:1','اے ایمان والو! اپنے اقراروں کو پورا کرو۔ تمہارے لیے چارپائے جانور (جو چرنے والے ہیں) حلال کر دیئے گئے ہیں۔ بجز ان کے جو تمہیں پڑھ کر سنائے جاتے ہیں مگر احرام (حج) میں شکار کو حلال نہ جاننا۔ خدا جیسا چاہتا ہے حکم دیتا ہے');
INSERT INTO "jalandhari" VALUES(5,2,'5:2','مومنو! خدا کے نام کی چیزوں کی بےحرمتی نہ کرنا اور نہ ادب کے مہینے کی اور نہ قربانی کے جانوروں کی اور نہ ان جانوروں کی (جو خدا کی نذر کر دیئے گئے ہوں اور) جن کے گلوں میں پٹے بندھے ہوں اور نہ ان لوگوں کی جو عزت کے گھر (یعنی بیت الله) کو جا رہے ہوں (اور) اپنے پروردگار کے فضل اور اس کی خوشنودی کے طلبگار ہوں اور جب احرام اتار دو تو (پھر اختیار ہے کہ) شکار کرو اور لوگوں کی دشمنی اس وجہ سے کہ انہوں نے تم کو عزت والی مسجد سے روکا تھا تمہیں اس بات پر آمادہ نہ کرے کہ تم ان پر زیادتی کرنے لگو اور (دیکھو) نیکی اور پرہیزگاری کے کاموں میں ایک دوسرے کی مدد کیا کرو اور گناہ اور ظلم کی باتوں میں مدد نہ کیا کرو اور خدا سے ڈرتے رہو۔ کچھ شک نہیں کہ خدا کا عذاب سخت ہے');
INSERT INTO "jalandhari" VALUES(5,3,'5:3','تم پر مرا ہوا جانور اور (بہتا) لہو اور سور کا گوشت اور جس چیز پر خدا کے سوا کسی اور کا نام پکارا جائے اور جو جانور گلا گھٹ کر مر جائے اور جو چوٹ لگ کر مر جائے اور جو گر کر مر جائے اور جو سینگ لگ کر مر جائے یہ سب حرام ہیں اور وہ جانور بھی جس کو درندے پھاڑ کھائیں۔ مگر جس کو تم (مرنے سے پہلے) ذبح کرلو اور وہ جانور بھی جو تھان پر ذبح کیا جائے اور یہ بھی کہ پاسوں سے قسمت معلوم کرو یہ سب گناہ (کے کام) ہیں آج کافر تمہارے دین سے ناامید ہو گئے ہیں تو ان سے مت ڈرو اور مجھی سے ڈرتے رہو (اور) آج ہم نے تمہارے لئے تمہارا دین کامل کر دیا اور اپنی نعمتیں تم پر پوری کر دیں اور تمہارے لئے اسلام کو دین پسند کیا ہاں جو شخص بھوک میں ناچار ہو جائے (بشرطیکہ) گناہ کی طرف مائل نہ ہو تو خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(5,4,'5:4','تم سے پوچھتے ہیں کہ کون کون سی چیزیں ان کے لیے حلال ہیں (ان سے) کہہ دو کہ سب پاکیزہ چیزیں تم کو حلال ہیں اور وہ (شکار) بھی حلال ہے جو تمہارے لیے ان شکاری جانوروں نے پکڑا ہو جن کو تم نے سدھا رکھا ہو اور جس (طریق) سے خدا نے تمہیں (شکار کرنا) سکھایا ہے (اس طریق سے) تم نے ان کو سکھایا ہو تو جو شکار وہ تمہارے لئے پکڑ رکھیں اس کو کھا لیا کرو اور (شکاری جانوروں کو چھوڑتے وقت) خدا کا نام لے لیا کرو اور خدا سے ڈرتے رہو۔ بےشک خدا جلد حساب لینے والا ہے');
INSERT INTO "jalandhari" VALUES(5,5,'5:5','آج تمہارے لیے سب پاکیزہ چیزیں حلال کر دی گئیں اور اہل کتاب کا کھانا بھی تم کو حلال ہے اور تمہارا کھانا ان کو حلال ہے اور پاک دامن مومن عورتیں اور پاک دامن اہل کتاب عورتیں بھی (حلال ہیں) جبکہ ان کا مہر دے دو۔ اور ان سے عفت قائم رکھنی مقصود ہو نہ کھلی بدکاری کرنی اور نہ چھپی دوستی کرنی اور جو شخص ایمان سے منکر ہوا اس کے عمل ضائع ہو گئے اور وہ آخرت میں نقصان پانے والوں میں ہوگا');
INSERT INTO "jalandhari" VALUES(5,6,'5:6','مومنو! جب تم نماز پڑھنے کا قصد کیا کرو تم منہ اور کہنیوں تک ہاتھ دھو لیا کرو اور سر کا مسح کر لیا کرو اور ٹخنوں تک پاؤں (دھو لیا کرو) اور اگر نہانے کی حاجت ہو تو (نہا کر) پاک ہو جایا کرو اور اگر بیمار ہو یا سفر میں ہو یا کوئی تم میں سے بیت الخلا سے ہو کر آیا ہو یا تم عورتوں سے ہم بستر ہوئے ہو اور تمہیں پانی نہ مل سکے تو پاک مٹی لو اور اس سے منہ اور ہاتھوں کا مسح (یعنی تیمم) کر لو۔ خدا تم پر کسی طرح کی تنگی نہیں کرنا چاہتا بلکہ یہ چاہتا ہے کہ تمہیں پاک کرے اور اپنی نعمتیں تم پر پوری کرے تاکہ تم شکر کرو');
INSERT INTO "jalandhari" VALUES(5,7,'5:7','اور خدا نے جو تم پر احسان کئے ہیں ان کو یاد کرو اور اس عہد کو بھی جس کا تم سے قول لیا تھا (یعنی) جب تم نے کہا تھا کہ ہم نے (خدا کا حکم) سن لیا اور قبول کیا۔ اور الله سے ڈرو۔ کچھ شک نہیں کہ خدا دلوں کی باتوں (تک) سے واقف ہے');
INSERT INTO "jalandhari" VALUES(5,8,'5:8','اے ایمان والوں! خدا کے لیے انصاف کی گواہی دینے کے لیے کھڑے ہو جایا کرو۔ اور لوگوں کی دشمنی تم کو اس بات پر آمادہ نہ کرے کہ انصاف چھوڑ دو۔ انصاف کیا کرو کہ یہی پرہیزگاری کی بات ہے اور خدا سے ڈرتے رہو۔ کچھ شک نہیں کہ خدا تمہارے سب اعمال سے خبردار ہے');
INSERT INTO "jalandhari" VALUES(5,9,'5:9','جو لوگ ایمان لائے اور نیک کام کرتے رہے ان سے خدا نے وعدہ فرمایا ہے کہ ان کے لیے بخشش اور اجر عظیم ہے');
INSERT INTO "jalandhari" VALUES(5,10,'5:10','اور جنہوں نے کفر کیا اور ہماری آیتوں کو جھٹلایا وہ جہنمی ہیں');
INSERT INTO "jalandhari" VALUES(5,11,'5:11','اے ایمان والو! خدا نے جو تم پر احسان کیا ہے اس کو یاد کرو۔ جب ایک جماعت نے ارادہ کیا کہ تم پر دست درازی کریں تو اس نے ان کے ہاتھ روک دیئے اور خدا سے ڈرتے رہوں اور مومنو کو خدا ہی پر بھروسہ رکھنا چاہیئے');
INSERT INTO "jalandhari" VALUES(5,12,'5:12','اور خدا نے بنی اسرائیل سے اقرار لیا اور ان میں ہم نے بارہ سردار مقرر کئے پھر خدا نے فرمایا کہ میں تمہارے ساتھ ہوں اگر تم نماز پڑھتے اور زکوٰة دیتے رہو گے اور میرے پیغمبروں پر ایمان لاؤ گے اور ان کی مدد کرو گے اور خدا کو قرض حسنہ دو گے تو میں تم سے تمہارے گناہ دور کر دوں گا اور تم کو بہشتوں میں داخل کروں گا جن کے نیچے نہریں بہہ رہی ہیں پھر جس نے اس کے بعد تم میں سے کفر کیا وہ سیدھے رستے سے بھٹک گیا');
INSERT INTO "jalandhari" VALUES(5,13,'5:13','تو ان لوگوں کے عہد توڑ دینے کے سبب ہم نے ان پر لعنت کی اور ان کے دلوں کو سخت کر دیا یہ لوگ کلمات (کتاب) کو اپنے مقامات سے بدل دیتے ہیں اور جن باتوں کی ان کو نصیحت کی گئی تھی ان کا بھی ایک حصہ فراموش کر بیٹھے اور تھوڑے آدمیوں کے سوا ہمیشہ تم ان کی (ایک نہ ایک) خیانت کی خبر پاتے رہتے ہو تو ان کی خطائیں معاف کردو اور (ان سے) درگزر کرو کہ خدا احسان کرنے والوں کو دوست رکھتا ہے');
INSERT INTO "jalandhari" VALUES(5,14,'5:14','اور جو لوگ (اپنے تئیں) کہتے ہیں کہ ہم نصاریٰ ہیں ہم نے ان سے بھی عہد لیا تھا مگر انہوں نے بھی اس نصیحت کا جو ان کو کی گئی تھی ایک حصہ فراموش کر دیا تو ہم نے ان کے باہم قیامت تک کے لیے دشمنی اور کینہ ڈال دیا اور جو کچھ وہ کرتے رہے خدا عنقریب ان کو اس سے آگاہ کرے گا');
INSERT INTO "jalandhari" VALUES(5,15,'5:15','اے اہل کتاب! تمہارے پاس ہمارے پیغمبر (آخرالزماں) آ گئے ہیں کہ جو کچھ تم کتاب (الہٰی) میں سے چھپاتے تھے وہ اس میں سے بہت کچھ تمہیں کھول کھول کر بتا دیتے ہیں اور تمہارے بہت سے قصور معاف کر دیتے ہیں بےشک تمہارے پاس خدا کی طرف سے نور اور روشن کتاب آ چکی ہے');
INSERT INTO "jalandhari" VALUES(5,16,'5:16','جس سے خدا اپنی رضا پر چلنے والوں کو نجات کے رستے دکھاتا ہے اور اپنے حکم سے اندھیرے میں سے نکال کر روشنی کی طرف لے جاتا اور ان کو سیدھے رستہ پر چلاتا ہے');
INSERT INTO "jalandhari" VALUES(5,17,'5:17','جو لوگ اس بات کے قائل ہیں کہ عیسیٰ بن مریم خدا ہیں وہ بےشک کافر ہیں (ان سے) کہہ دو کہ اگر خدا عیسیٰ بن مریم کو اور ان کی والدہ کو اور جتنے لوگ زمین میں ہیں سب کو ہلاک کرنا چاہے تو اس کے آگے کس کی پیش چل سکتی ہے؟ اور آسمان اور زمین اور جو کچھ ان دونوں میں ہے سب پر خدا ہی کی بادشاہی ہے وہ جو چاہتا ہے پیدا کرتا ہے اور خدا ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(5,18,'5:18','اور یہود اور نصاریٰ کہتے ہیں کہ ہم خدا کے بیٹے اور اس کے پیارے ہیں کہو کہ پھر وہ تمہاری بداعمالیوں کے سبب تمھیں عذاب کیوں دیتا ہے (نہیں) بلکہ تم اس کی مخلوقات میں (دوسروں کی طرح کے) انسان ہو وہ جسے چاہے بخشے اور جسے چاہے عذاب دے اور آسمان زمین اور جو کچھ ان دونوں میں ہے سب پر خدا ہی کی حکومت ہے اور (سب کو) اسی کی طرف لوٹ کر جانا ہے');
INSERT INTO "jalandhari" VALUES(5,19,'5:19','اے اہلِ کتاب پیغمبروں کے آنے کا سلسلہ جو (ایک عرصے تک) منقطع رہا تو (اب) تمہارے پاس ہمارے پیغمبر آ گئے ہیں جو تم سے (ہمارے احکام) بیان کرتے ہیں تاکہ تم یہ نہ کہو کہ ہمارے پاس کوئی خوشخبری یا ڈر سنانے والا نہیں آیا سو (اب) تمہارے پاس خوشخبری اور ڈر سنانے والے آ گئے ہیں اور خدا ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(5,20,'5:20','اور جب موسیٰ نے اپنی قوم سے کہا کہ بھائیو تم پر خدا نے جو احسان کئے ہیں ان کو یاد کرو کہ اس نے تم میں پیغمبر پیدا کیے اور تمہیں بادشاہ بنایا اور تم کو اتنا کچھ عنایت کیا کہ اہل عالم میں سے کسی کو نہیں دیا');
INSERT INTO "jalandhari" VALUES(5,21,'5:21','تو بھائیو! تم ارض مقدس (یعنی ملک شام) میں جسے خدا نے تمہارے لیے لکھ رکھا ہے چل داخل ہو اور (دیکھنا مقابلے کے وقت) پیٹھ نہ پھیر دینا ورنہ نقصان میں پڑ جاؤ گے');
INSERT INTO "jalandhari" VALUES(5,22,'5:22','وہ کہنے لگے کہ موسیٰ! وہاں تو بڑے زبردست لوگ (رہتے) ہیں اور جب تک وہ اس سرزمین سے نکل نہ جائیں ہم وہاں جا نہیں سکتے ہاں اگر وہ وہاں سے نکل جائیں تو ہم جا داخل ہوں گے');
INSERT INTO "jalandhari" VALUES(5,23,'5:23','جو لوگ (خدا سے) ڈرتے تھے ان میں سے دو شخص جن پر خدا کی عنایت تھی کہنے لگے کہ ان لوگوں پر دروازے کے رستے سے حملہ کردو جب تم دروازے میں داخل ہو گئے تو فتح تمہارے ہے اور خدا ہی پر بھروسہ رکھو بشرطیکہ صاحبِ ایمان ہو');
INSERT INTO "jalandhari" VALUES(5,24,'5:24','وہ بولے کہ موسیٰ! جب تک وہ لوگ وہاں ہیں ہم کبھی وہاں نہیں جا سکتے (اگر لڑنا ہی ضرور ہے) تو تم اور تمہارا خدا جاؤ اور لڑو ہم یہیں بیٹھے رہیں گے');
INSERT INTO "jalandhari" VALUES(5,25,'5:25','موسیٰ نے (خدا سے) التجا کی کہ پروردگار میں اپنے اور اپنے بھائی کے سوا اور کسی پر اختیار نہیں رکھتا تو ہم میں اور ان نافرمان لوگوں میں جدائی کردے');
INSERT INTO "jalandhari" VALUES(5,26,'5:26','خدا نے فرمایا کہ وہ ملک ان پر چالیس برس تک کے لیے حرام کر دیا گیا (کہ وہاں جانے نہ پائیں گے اور جنگل کی) زمین میں سرگرداں پھرتے رہیں گے تو ان نافرمان لوگوں کے حال پر افسوس نہ کرو');
INSERT INTO "jalandhari" VALUES(5,27,'5:27','اور (اے محمد) ان کو آدم کے دو بیٹوں (ہابیل اور قابیل) کے حالات (جو بالکل) سچے (ہیں) پڑھ کر سنا دو کہ جب ان دونوں نے خدا (کی جناب میں) کچھ نیازیں چڑھائیں تو ایک کی نیاز تو قبول ہو گئی اور دوسرے کی قبول نہ ہوئی (تب قابیل ہابیل سے) کہنے لگا کہ میں تجھے قتل کروں گا اس نے کہا کہ خدا پرہیزگاروں ہی کی (نیاز) قبول فرمایا کرتا ہے');
INSERT INTO "jalandhari" VALUES(5,28,'5:28','اور اگر تو مجھے قتل کرنے کے لیے مجھ پر ہاتھ چلائے گا تو میں تجھ کو قتل کرنے کے لئے تجھ پر ہاتھ نہیں چلاؤں گا مجھے تو خدائے رب العالمین سے ڈر لگتا ہے');
INSERT INTO "jalandhari" VALUES(5,29,'5:29','میں چاہتا ہوں کہ تو میرے گناہ میں بھی ماخوذ ہو اور اپنے گناہ میں بھی پھر (زمرہ) اہل دوزخ میں ہو اور ظالموں کی یہی سزا ہے');
INSERT INTO "jalandhari" VALUES(5,30,'5:30','مگر اس کے نفس نے اس کو بھائی کے قتل ہی کی ترغیب دی تو اس نے اسے قتل کر دیا اور خسارہ اٹھانے والوں میں ہو گیا');
INSERT INTO "jalandhari" VALUES(5,31,'5:31','اب خدا نے ایک کوّا بھیجا جو زمین کریدنے لگا تاکہ اسے دکھائے کہ اپنے بھائی کی لاش کو کیونکر چھپائے کہنے لگا اے ہے مجھ سے اتنا بھی نہ ہو سکا کہ اس کوے کے برابر ہوتا کہ اپنے بھائی کی لاش چھپا دیتا پھر وہ پشیمان ہوا');
INSERT INTO "jalandhari" VALUES(5,32,'5:32','اس قتل کی وجہ سے ہم نے بنی اسرائیل پر یہ حکم نازل کیا کہ جو شخص کسی کو (ناحق) قتل کرے گا (یعنی) بغیر اس کے کہ جان کا بدلہ لیا جائے یا ملک میں خرابی کرنے کی سزا دی جائے اُس نے گویا تمام لوگوں کو قتل کیا اور جو اس کی زندگانی کا موجب ہوا تو گویا تمام لوگوں کی زندگانی کا موجب ہوا اور ان لوگوں کے پاس ہمارے پیغمبر روشن دلیلیں لا چکے ہیں پھر اس کے بعد بھی ان سے بہت سے لوگ ملک میں حدِ اعتدال سے نکل جاتے ہیں');
INSERT INTO "jalandhari" VALUES(5,33,'5:33','جو لوگ خدا اور اس کے رسول سے لڑائی کریں اور ملک میں فساد کرنے کو دوڑتے پھریں ان کی یہی سزا ہے کہ قتل کر دیئے جائیں یا سولی چڑھا دیئے جائیں یا ان کے ایک ایک طرف کے ہاتھ اور ایک ایک طرف کے پاؤں کاٹ دیئے جائیں یا ملک سے نکال دیئے جائیں یہ تو دنیا میں ان کی رسوائی ہے اور آخرت میں ان کے لیے بڑا (بھاری) عذاب تیار ہے');
INSERT INTO "jalandhari" VALUES(5,34,'5:34','ہاں جن لوگوں نے اس سے پیشتر کہ تمہارے قابو میں آ جائیں توبہ کر لی تو جان رکھو کہ خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(5,35,'5:35','اے ایمان والو! خدا سے ڈرتے رہو اور اس کا قرب حاصل کرنے کا ذریعہ تلاش کرتے رہو اور اس کے رستے میں جہاد کرو تاکہ رستگاری پاؤ');
INSERT INTO "jalandhari" VALUES(5,36,'5:36','جو لوگ کافر ہیں اگر ان کے پاس روئے زمین (کے تمام خزانے اور اس) کا سب مال ومتاع ہو اور اس کے ساتھ اسی قدر اور بھی ہو تاکہ قیامت کے روز عذاب (سے رستگاری حاصل کرنے) کا بدلہ دیں تو ان سے قبول نہیں کیا جائے گا اور ان کو درد دینے والا عذاب ہوگا');
INSERT INTO "jalandhari" VALUES(5,37,'5:37','(ہر چند) چاہیں گے کہ آگ سے نکل جائیں مگر اس سے نہیں نکل سکیں گے اور ان کے لئے ہمیشہ کا عذاب ہے');
INSERT INTO "jalandhari" VALUES(5,38,'5:38','اور جو چوری کرے مرد ہو یا عورت ان کے ہاتھ کاٹ ڈالو یہ ان کے فعلوں کی سزا اور خدا کی طرف سے عبرت ہے اور خدا زبردست (اور) صاحب حکمت ہے');
INSERT INTO "jalandhari" VALUES(5,39,'5:39','اور جو شخص گناہ کے بعد توبہ کرے اور نیکوکار ہو جائے تو خدا اس کو معاف کر دے گا کچھ شک نہیں کہ خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(5,40,'5:40','کیا تم کو معلوم نہیں کہ آسمانوں اور زمین میں خدا ہی کی سلطنت ہے؟ جس کو چاہے عذاب کرے اور جسے چاہے بخش دے اور خدا ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(5,41,'5:41','اے پیغمبر! جو لوگ کفر میں جلدی کرتے ہیں (کچھ تو) ان میں سے (ہیں) جو منہ سے کہتے ہیں کہ ہم مومن ہیں لیکن ان کے دل مومن نہیں ہیں اور (کچھ) ان میں سے جو یہودی ہیں ان کی وجہ سے غمناک نہ ہونا یہ غلط باتیں بنانے کے لیے جاسوسی کرتے پھرتے ہیں اور ایسے لوگوں (کے بہکانے) کے لیے جاسوس بنے ہیں جو ابھی تمہارے پاس نہیں آئے (صحیح) باتوں کو ان کے مقامات (میں ثابت ہونے) کے بعد بدل دیتے ہیں (اور لوگوں سے) کہتے ہیں کہ اگر تم کو یہی (حکم) ملے تو اسے قبول کر لینا اور اگر یہ نہ ملے تو اس سے احتراز کرنا اور اگر کسی کو خدا گمراہ کرنا چاہے تو اس کے لیے تم کچھ بھی خدا سے (ہدایت کا) اختیار نہیں رکھتے یہ وہ لوگ ہیں جن کے دلوں کو خدا نے پاک کرنا نہیں چاہا ان کے لیے دنیا میں بھی ذلت ہے اور آخرت میں بھی بڑا عذاب ہے');
INSERT INTO "jalandhari" VALUES(5,42,'5:42','(یہ) جھوٹی باتیں بنانے کے جاسوسی کرنے والے اور (رشوت کا) حرام مال کھانے والے ہیں اگر یہ تمہارے پاس (کوئی مقدمہ فیصل کرانے کو) آئیں تو تم ان میں فیصلہ کر دینا یا اعراض کرنا اور اگر ان سے اعراض کرو گے تو وہ تمہارا کچھ بھی نہیں بگاڑ سکیں گے اور اگر فیصلہ کرنا چاہو تو انصاف کا فیصلہ کرنا کہ خدا انصاف کرنے والوں کو دوست رکھتا ہے');
INSERT INTO "jalandhari" VALUES(5,43,'5:43','اور یہ تم سے (اپنے مقدمات) کیونکر فیصل کرایں گے جبکہ خود ان کے پاس تورات (موجود) ہے جس میں خدا کا حکم (لکھا ہوا) ہے (یہ اسے جانتے ہیں) پھر اس کے بعد اس سے پھر جاتے ہیں اور یہ لوگ ایمان ہی نہیں رکھتے');
INSERT INTO "jalandhari" VALUES(5,44,'5:44','بیشک ہم نے توریت نازل فرمائی جس میں ہدایت اور روشنی ہے اسی کے مطابق انبیاء جو (خدا کے) فرمانبردار تھے یہودیوں کو حکم دیتے رہے ہیں اور مشائخ اور علماء بھی کیونکہ وہ کتاب خدا کے نگہبان مقرر کیے گئے تھے اور اس پر گواہ تھے (یعنی حکم الہٰی کا یقین رکھتے تھے) تو تم لوگوں سے مت ڈرنا اور مجھی سے ڈرتے رہنا اور میری آیتوں کے بدلے تھوڑی سی قیمت نہ لینا اور جو خدا کے نازل فرمائے ہوئے احکام کے مطابق حکم نہ دے تو ایسے ہی لوگ کافر ہیں');
INSERT INTO "jalandhari" VALUES(5,45,'5:45','اور ہم نے ان لوگوں کے لیے تورات میں یہ حکم لکھ دیا تھا کہ جان کے بدلے جان اور آنکھ کے بدلے آنکھ اور ناک کے بدلے ناک اور کان کے بدلے کان اور دانت کے بدلے دانت اور سب زخموں کا اسی طرح بدلہ ہے لیکن جو شخص بدلہ معاف کر دے وہ اس کے لیے کفارہ ہوگا اور جو خدا کے نازل فرمائے ہوئے احکام کے مطابق حکم نہ دے تو ایسے ہی لوگ بےانصاف ہیں');
INSERT INTO "jalandhari" VALUES(5,46,'5:46','اور ان پیغمبروں کے بعد انہی کے قدموں پر ہم نے عیسیٰ بن مریم کو بھیجا جو اپنے سے پہلے کی کتاب تورات کی تصدیق کرتے تھے اور ان کو انجیل عنایت کی جس میں ہدایت اور نور ہے اور تورات کی جو اس سے پہلی کتاب (ہے) تصدیق کرتی ہے اور پرہیزگاروں کو راہ بتاتی اور نصیحت کرتی ہے');
INSERT INTO "jalandhari" VALUES(5,47,'5:47','اور اہل انجیل کو چاہیئے کہ جو احکام خدا نے اس میں نازل فرمائے ہیں اس کے مطابق حکم دیا کریں اور جو خدا کے نازل کئے ہوئے احکام کے مطابق حکم نہ دے گا تو ایسے لوگ نافرماں ہیں');
INSERT INTO "jalandhari" VALUES(5,48,'5:48','اور (اے پیغمبر!) ہم نے تم پر سچی کتاب نازل کی ہے جو اپنے سے پہلی کتابوں کی تصدیق کرتی ہے اور ان (سب) پر شامل ہے تو جو حکم خدا نے نازل فرمایا ہے اس کے مطابق ان کا فیصلہ کرنا اور حق جو تمہارے پاس آچکا ہے اس کو چھوڑ کر ان کی خواہشوں کی پیروی نہ کرنا ہم نے تم میں سے ہر ایک (فرقے) کے لیے ایک دستور اور طریقہ مقرر کیا ہے اور اگر خدا چاہتا تو سب کو ایک ہی شریعت پر کر دیتا مگر جو حکم اس نے تم کو دیئے ہیں ان میں وہ تمہاری آزمائش کرنی چاہتا ہے سو نیک کاموں میں جلدی کرو تم سب کو خدا کی طرف لوٹ کر جانا ہے پھر جن باتوں میں تم کو اختلاف تھا وہ تم کو بتا دے گا');
INSERT INTO "jalandhari" VALUES(5,49,'5:49','اور (ہم پھر تاکید کرتے ہیں کہ) جو (حکم) خدا نے نازل فرمایا ہے اسی کے مطابق ان میں فیصلہ کرنا اور ان کی خواہشوں کی پیروی نہ کرنا اور ان سے بچتے رہنا کہ کسی حکم سے جو خدا نے تم پر نازل فرمایا ہے یہ کہیں تم کو بہکانہ دیں اگر یہ نہ مانیں تو جان لو کہ خدا چاہتا ہے کہ ان کے بعض گناہوں کے سبب ان پر مصیبت نازل کرے اور اکثر لوگ تو نافرمان ہیں');
INSERT INTO "jalandhari" VALUES(5,50,'5:50','کیا یہ زمانہٴ جاہلیت کے حکم کے خواہش مند ہیں؟ اور جو یقین رکھتے ہیں ان کے لیے خدا سے اچھا حکم کس کا ہے؟');
INSERT INTO "jalandhari" VALUES(5,51,'5:51','اے ایمان والو! یہود اور نصاریٰ کو دوست نہ بناؤ یہ ایک دوسرے کے دوست ہیں اور جو شخص تم میں سے ان کو دوست بنائے گا وہ بھی انہیں میں سے ہوگا بیشک خدا ظالم لوگوں کو ہدایت نہیں دیتا');
INSERT INTO "jalandhari" VALUES(5,52,'5:52','تو جن لوگوں کے دلوں میں (نفاق کا) مرض ہے تم ان کو دیکھو گے کہ ان میں دوڑ دوڑ کے ملے جاتے ہیں کہتے ہیں کہ ہمیں خوف ہے کہ کہیں ہم پر زمانے کی گردش نہ آجائے سو قریب ہے کہ خدا فتح بھیجے یا اپنے ہاں سے کوئی اور امر (نازل فرمائے) پھر یہ اپنے دل کی باتوں پر جو چھپایا کرتے تھے پشیمان ہو کر رہ جائیں گے');
INSERT INTO "jalandhari" VALUES(5,53,'5:53','اور اس (وقت) مسلمان (تعجب سے) کہیں گے کہ کیا یہ وہی ہیں جو خدا کی سخت سخت قسمیں کھایا کرتے تھے کہ ہم تمہارے ساتھ ہیں ان کےعمل اکارت گئے اور وہ خسارے میں پڑ گئے');
INSERT INTO "jalandhari" VALUES(5,54,'5:54','اے ایمان والو اگر کوئی تم میں سے اپنے دین سے پھر جائے گا تو خدا ایسے لوگ پیدا کر دے گا جن کو وہ دوست رکھے اور جسے وہ دوست رکھیں اور جو مومنوں کے حق میں نرمی کریں اور کافروں سے سختی سے پیش آئیں خدا کی راہ میں جہاد کریں اور کسی ملامت کرنے والی کی ملامت سے نہ ڈریں یہ خدا کا فضل ہے وہ جسے چاہتا ہے دیتا ہے اور الله بڑی کشائش والا اور جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(5,55,'5:55','تمہارے دوست تو خدا اور اس کے پیغمبر اور مومن لوگ ہی ہیں جو نماز پڑھتے اور زکوٰة دیتے اور (خدا کے آگے) جھکتے ہیں');
INSERT INTO "jalandhari" VALUES(5,56,'5:56','اور جو شخص خدا اور اس کے پیغمبر اور مومنوں سے دوستی کرے گا تو (وہ خدا کی جماعت میں داخل ہوگا اور) خدا کی جماعت ہی غلبہ پانے والی ہے');
INSERT INTO "jalandhari" VALUES(5,57,'5:57','اے ایمان والوں! جن لوگوں کو تم سے پہلے کتابیں دی گئی تھیں ان کو اور کافروں کو جنہوں نے تمہارے دین کو ہنسی اور کھیل بنا رکھا ہے دوست نہ بناؤ اور مومن ہو تو خدا سے ڈرتے رہو');
INSERT INTO "jalandhari" VALUES(5,58,'5:58','اور جب تم لوگ نماز کے لیے اذان دیتے ہو تو یہ اسے بھی ہنسی اور کھیل بناتے ہیں یہ اس لیے کہ سمجھ نہیں رکھتے');
INSERT INTO "jalandhari" VALUES(5,59,'5:59','کہو کہ اے اہل کتاب! تم ہم میں برائی ہی کیا دیکھتے ہو سوا اس کے کہ ہم خدا پر اور جو (کتاب) ہم پر نازل ہوئی اس پر اور جو (کتابیں) پہلے نازل ہوئیں ان پر ایمان لائے ہیں اور تم میں اکثر بدکردار ہیں');
INSERT INTO "jalandhari" VALUES(5,60,'5:60','کہو کہ میں تمہیں بتاؤں کہ خدا کے ہاں اس سے بھی بدتر جزا پانے والے کون ہیں؟ وہ لوگ ہیں جن پر خدا نے لعنت کی اور جن پر وہ غضبناک ہوا اور (جن کو) ان میں سے بندر اور سور بنا دیا اور جنہوں نے شیطان کی پرستش کی ایسے لوگوں کا برا ٹھکانہ ہے اور وہ سیدھے رستے سے بہت دور ہیں');
INSERT INTO "jalandhari" VALUES(5,61,'5:61','اور جب یہ لوگ تمہارے پاس آتے ہیں تو کہتے ہیں کہ ہم ایمان لے آئے حالانکہ کفر لے کر آتے ہیں اور اسی کو لیکر جاتے ہیں اور جن باتوں کو یہ مخفی رکھتے ہیں خدا ان کو خوب جانتا ہے');
INSERT INTO "jalandhari" VALUES(5,62,'5:62','اور تم دیکھو گے کہ ان میں اکثر گناہ اور زیادتی اور حرام کھانے میں جلدی کر رہے ہیں بےشک یہ جو کچھ کرتے ہیں برا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(5,63,'5:63','بھلا ان کے مشائخ اور علماء انہیں گناہ کی باتوں اور حرام کھانے سے منع کیوں نہیں کرتے؟ بلاشبہ وہ بھی برا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(5,64,'5:64','اور یہود کہتے ہیں کہ خدا کا ہاتھ (گردن سے) بندھا ہوا ہے (یعنی الله بخیل ہے) انہیں کے ہاتھ باندھے جائیں اور ایسا کہنے کے سبب ان پر لعنت ہو (اس کا ہاتھ بندھا ہوا نہیں) بلکہ اس کے دونوں ہاتھ کھلے ہیں وہ جس طرح (اور جتنا) چاہتا ہے خرچ کرتا ہے اور (اے محمد) یہ (کتاب) جو تمہارے پروردگار کی طرف سے تم پر نازل ہوئی اس سے ان میں سے اکثر کی شرارت اور انکار اور بڑھے گا اور ہم نے ان کے باہم عداوت اور بغض قیامت تک کے لیے ڈال دیا ہے یہ جب لڑائی کے لیے آگ جلاتے ہیں خدا اس کو بجھا دیتا ہے اور یہ ملک میں فساد کے لیے دوڑے پھرتے ہیں اور خدا فساد کرنے والوں کو دوست نہیں رکھتا');
INSERT INTO "jalandhari" VALUES(5,65,'5:65','اور اگر اہل کتاب ایمان لاتے اور پرہیز گاری کرتے تو ہم ان سے ان کے گناہ محو کر دیتے اور ان کو نعمت کے باغوں میں داخل کرتے');
INSERT INTO "jalandhari" VALUES(5,66,'5:66','اور اگر وہ تورات اور انجیل کو اور جو (اور کتابیں) ان کے پروردگار کی طرف سے ان پر نازل ہوئیں ان کو قائم رکھتے (تو ان پر رزق مینہ کی طرح برستا کہ) اپنے اوپر سے پاؤں کے نیچے سے کھاتے ان میں کچھ لوگ میانہ رو ہیں اور بہت سے ایسے ہیں جن کے اعمال برے ہیں');
INSERT INTO "jalandhari" VALUES(5,67,'5:67','اے پیغمبر جو ارشادات خدا کی طرف سے تم پر نازل ہوئے ہیں سب لوگوں کو پہنچا دو اور اگر ایسا نہ کیا تو تم خدا کے پیغام پہنچانے میں قاصر رہے (یعنی پیغمبری کا فرض ادا نہ کیا) اور خدا تم کو لوگوں سے بچائے رکھے گا بیشک خدا منکروں کو ہدایت نہیں دیتا');
INSERT INTO "jalandhari" VALUES(5,68,'5:68','کہو کہ اے اہل کتاب! جب تک تم تورات اور انجیل کو اور جو (اور کتابیں) تمہارے پروردگار کی طرف سے تم لوگوں پر نازل ہوئیں ان کو قائم نہ رکھو گے کچھ بھی راہ پر نہیں ہو سکتے اور یہ (قرآن) جو تمہارے پروردگار کی طرف سے تم پر نازل ہوا ہے ان میں سے اکثر کی سرکشی اور کفر اور بڑھے گا تو تم قوم کفار پر افسوس نہ کرو');
INSERT INTO "jalandhari" VALUES(5,69,'5:69','جو لوگ خدا پر اور روز آخرت پر ایمان لائیں گے اور عمل نیک کریں گے خواہ وہ مسلمان ہوں یا یہودی یا ستارہ پرست یا عیسائی ان کو (قیامت کے دن) نہ کچھ خوف ہو گا اور نہ غمناک ہوں گے');
INSERT INTO "jalandhari" VALUES(5,70,'5:70','ہم نے بنی اسرائیل سے عہد بھی لیا اور ان کی طرف پیغمبر بھی بھیجے (لیکن) جب کوئی پیغمبر ان کے پاس ایسی باتیں لےکر آتا جن کو ان کے دل نہیں چاہتے تھے تو وہ (انبیاء کی) ایک جماعت کو تو جھٹلا دیتے اور ایک جماعت کو قتل کر دیتے تھے');
INSERT INTO "jalandhari" VALUES(5,71,'5:71','اور خیال کرتے تھے کہ (اس سے ان پر) کوئی آفت نہیں آنے کی تو وہ اندھے اور بہرے ہو گئے پھر خدا نے ان پر مہربانی فرمائی (لیکن) پھر ان میں سے بہت سے اندھے اور بہرے ہو گئے اور خدا ان کے سب کاموں کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(5,72,'5:72','وہ لوگ بےشبہ کافر ہیں جو کہتے ہیں کہ مریم کے بیٹے (عیسیٰ) مسیح خدا ہیں حالانکہ مسیح یہود سے یہ کہا کرتے تھے کہ اے بنی اسرائیل خدا ہی کی عبادت کرو جو میرا بھی پروردگار ہے اور تمہارا بھی (اور جان رکھو کہ) جو شخص خدا کے ساتھ شرک کرے گا خدا اس پر بہشت حرام کر دے گا اور اس کا ٹھکانہ دوزخ ہے اور ظالموں کا کوئی مددگار نہیں');
INSERT INTO "jalandhari" VALUES(5,73,'5:73','وہ لوگ (بھی) کافر ہیں جو اس بات کے قائل ہیں کہ خدا تین میں کا تیسرا ہے حالانکہ اس معبود یکتا کے سوا کوئی عبادت کے لائق نہیں اگر یہ لوگ ایسے اقوال (وعقائد) سے باز نہیں آئیں گے تو ان میں جو کافر ہوئے ہیں وہ تکلیف دینے والا عذاب پائیں گے');
INSERT INTO "jalandhari" VALUES(5,74,'5:74','تو یہ کیوں خدا کے آگے توبہ نہیں کرتے اور اس سے گناہوں کی معافی نہیں مانگتے اور خدا تو بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(5,75,'5:75','مسیح ابن مریم تو صرف (خدا) کے پیغمبر تھے ان سے پہلے بھی بہت سے رسول گزر چکے تھے اور ان کی والدہ (مریم خدا کی) ولی اور سچی فرمانبردار تھیں دونوں (انسان تھے اور) کھانا کھاتے تھے دیکھو ہم ان لوگوں کے لیے اپنی آیتیں کس طرح کھول کھول کر بیان کرتے ہیں پھر (یہ) دیکھو کہ یہ کدھر الٹے جا رہے ہیں');
INSERT INTO "jalandhari" VALUES(5,76,'5:76','کہو کہ تم خدا کے سوا ایسی چیز کی کیوں پرستش کرتے ہو جس کو تمہارے نفع اور نقصان کا کچھ بھی اختیار نہیں؟ اور خدا ہی (سب کچھ) سنتا جانتا ہے');
INSERT INTO "jalandhari" VALUES(5,77,'5:77','کہو کہ اے اہل کتاب! اپنے دین (کی بات) میں ناحق مبالغہ نہ کرو اور ایسے لوگوں کی خواہشوں کے پیچھے نہ چلو جو (خود بھی) پہلے گمراہ ہوئے اور اَور بھی اکثروں کو گمراہ کر گئے اور سیدھے رستے سے بھٹک گئے');
INSERT INTO "jalandhari" VALUES(5,78,'5:78','جو لوگ بنی اسرائیل میں کافر ہوئے ان پر داؤد اور عیسیٰ بن مریم کی زبان سے لعنت کی گئی یہ اس لیے کہ نافرمانی کرتے تھے اور حد سے تجاوز کرتے تھے');
INSERT INTO "jalandhari" VALUES(5,79,'5:79','(اور) برے کاموں سے جو وہ کرتے تھے ایک دوسرے کو روکتے نہیں تھے بلاشبہ وہ برا کرتے تھے');
INSERT INTO "jalandhari" VALUES(5,80,'5:80','تم ان میں سے بہتوں کو دیکھو گے کہ کافروں سے دوستی رکھتے ہیں انہوں نے جو کچھ اپنے واسطے آگے بھیجا ہے برا ہے (وہ یہ) کہ خدا ان سے ناخوش ہوا اور وہ ہمیشہ عذاب میں (مبتلا) رہیں گے');
INSERT INTO "jalandhari" VALUES(5,81,'5:81','اور اگر وہ خدا پر اور پیغمبر پر اور جو کتاب ان پر نازل ہوئی تھی اس پر یقین رکھتے تو ان لوگوں کو دوست نہ بناتے لیکن ان میں اکثر بدکردار ہیں');
INSERT INTO "jalandhari" VALUES(5,82,'5:82','(اے پیغمبرصلى الله عليه وسلم!) تم دیکھو گے کہ مومنوں کے ساتھ سب سے زیادہ دشمنی کرنے والے یہودی اور مشرک ہیں اور دوستی کے لحاظ سے مومنوں سے قریب تر ان لوگوں کو پاؤ گے جو کہتے ہیں کہ ہم نصاریٰ ہیں یہ اس لیے کہ ان میں عالم بھی ہیں اور مشائخ بھی اور وہ تکبر نہیں کرتے');
INSERT INTO "jalandhari" VALUES(5,83,'5:83','اور جب اس (کتاب) کو سنتے ہیں جو (سب سے پہلے) پیغمبر (محمدصلى الله عليه وسلم) پر نازل ہوئی تو تم دیکھتے ہو کہ ان کی آنکھوں سے آنسو جاری ہو جاتے ہیں اس لیے کہ انہوں نے حق بات پہچان لی اور وہ (خدا کی جناب میں) عرض کرتے ہیں کہ اے پروردگار ہم ایمان لے آئے تو ہم کو ماننے والوں میں لکھ لے');
INSERT INTO "jalandhari" VALUES(5,84,'5:84','اور ہمیں کیا ہوا ہے کہ خدا پر اور حق بات پر جو ہمارے پاس آئی ہے ایمان نہ لائیں اور ہم امید رکھتے ہیں کہ پروردگار ہم کو نیک بندوں کے ساتھ (بہشت میں) داخل کرے گا');
INSERT INTO "jalandhari" VALUES(5,85,'5:85','تو خدا نے ان کو اس کہنے کے عوض (بہشت کے) باغ عطا فرمائے جن کے نیچے نہریں بہہ رہی ہیں وہ ہمیشہ ان میں رہیں گے اور نیکو کاروں کا یہی صلہ ہے');
INSERT INTO "jalandhari" VALUES(5,86,'5:86','اور جن لوگوں نے کفر کیا اور ہماری آیتوں کو جھٹلایا وہ جہنمی ہیں');
INSERT INTO "jalandhari" VALUES(5,87,'5:87','مومنو! جو پاکیزہ چیزیں خدا نے تمہارے لیے حلال کی ہیں ان کو حرام نہ کرو اور حد سے نہ بڑھو کہ خدا حد سے بڑھنے والوں کو دوست نہیں رکھتا');
INSERT INTO "jalandhari" VALUES(5,88,'5:88','اور جو حلال طیّب روزی خدا نے تم کو دی ہے اسے کھاؤ اور خدا سے جس پر ایمان رکھتے ہو ڈرتے رہو');
INSERT INTO "jalandhari" VALUES(5,89,'5:89','خدا تمہاری بےارادہ قسموں پر تم سے مواخذہ نہیں کرے گا لیکن پختہ قسموں پر (جن کے خلاف کرو گے) مواخذہ کرے گا تو اس کا کفارہ دس محتاجوں کو اوسط درجے کا کھانا کھلانا ہے جو تم اپنے اہل وعیال کو کھلاتے ہو یا ان کو کپڑے دینا یا ایک غلام آزاد کرنا اور جس کو میسر نہ ہو وہ تین روزے رکھے یہ تمہاری قسموں کا کفارہ ہے جب تم قسم کھا لو (اور اسے توڑ دو) اور (تم کو) چاہئے کہ اپنی قسموں کی حفاظت کرو اس طرح خدا تمہارے (سمجھانے کے) لیے اپنی آیتیں کھول کھول کر بیان فرماتا ہے تاکہ تم شکر کرو');
INSERT INTO "jalandhari" VALUES(5,90,'5:90','اے ایمان والو! شراب اور جوا اور بت اور پاسے (یہ سب) ناپاک کام اعمال شیطان سے ہیں سو ان سے بچتے رہنا تاکہ نجات پاؤ');
INSERT INTO "jalandhari" VALUES(5,91,'5:91','شیطان تو یہ چاہتا ہے کہ شراب اور جوئے کے سبب تمہارے آپس میں دشمنی اور رنجش ڈلوا دے اور تمہیں خدا کی یاد سے اور نماز سے روک دے تو تم کو (ان کاموں سے) باز رہنا چاہیئے');
INSERT INTO "jalandhari" VALUES(5,92,'5:92','اور خدا کی فرمانبرداری اور رسولِ (خدا) کی اطاعت کرتے رہو اور ڈرتے رہو اگر منہ پھیرو گے تو جان رکھو کہ ہمارے پیغمبر کے ذمے تو صرف پیغام کا کھول کر پہنچا دینا ہے');
INSERT INTO "jalandhari" VALUES(5,93,'5:93','جو لوگ ایمان لائے اور نیک کام کرتے رہے ان پر ان چیزوں کا کچھ گناہ نہیں جو وہ کھا چکے جب کہ انہوں نے پرہیز کیا اور ایمان لائے اور نیک کام کیے پھر پرہیز کیا اور ایمان لائے پھر پرہیز کیا اور نیکو کاری کی اور خدا نیکو کاروں کو دوست رکھتا ہے');
INSERT INTO "jalandhari" VALUES(5,94,'5:94','مومنو! کسی قدر شکار سے جن کو تم ہاتھوں اور نیزوں سے پکڑ سکو خدا تمہاری آزمائش کرے گا (یعنی حالت احرام میں شکار کی ممانعت سے) تا کہ معلوم کرے کہ اس سے غائبانہ کون ڈرتا ہے تو جو اس کے بعد زیادتی کرے اس کے لیے دکھ دینے والا عذاب (تیار) ہے');
INSERT INTO "jalandhari" VALUES(5,95,'5:95','مومنو! جب تم احرام کی حالت میں ہو تو شکار نہ مارنا اور جو تم میں سے جان بوجھ کر اسے مارے تو (یا تو اس کا) بدلہ (دے اور وہ یہ ہے کہ) اسی طرح کا چارپایہ جسے تم میں دو معتبر شخص مقرر کردیں قربانی (کرے اور یہ قربانی) کعبے پہنچائی جائے یا کفارہ (دے اور وہ) مسکینوں کو کھانا کھلانا (ہے) یا اس کے برابر روزے رکھے تاکہ اپنے کام کی سزا (کا مزہ) چکھے (اور) جو پہلے ہو چکا وہ خدا نے معاف کر دیا اور جو پھر (ایسا کام) کرے گا تو خدا اس سے انتقام لے گا اور خدا غالب اور انتقام لینے والا ہے');
INSERT INTO "jalandhari" VALUES(5,96,'5:96','تمہارے لیے دریا (کی چیزوں) کا شکار اور ان کا کھانا حلال کر دیا گیا ہے (یعنی) تمہارے اور مسافروں کے فائدے کے لیے اور جنگل (کی چیزوں) کا شکار جب تک تم احرام کی حالت میں رہو تم پر حرام ہے اور خدا سے جس کے پاس تم (سب) جمع کئے جاؤ گے ڈرتے رہو');
INSERT INTO "jalandhari" VALUES(5,97,'5:97','خدا نے عزت کے گھر (یعنی) کعبے کو لوگوں کے لیے موجب امن مقرر فرمایا ہے اور عزت کے مہینوں کو اور قربانی کو اور ان جانوروں کو جن کے گلے میں پٹے بندھے ہوں یہ اس لیے کہ تم جان لو کہ جو کچھ آسمانوں میں اور جو کچھ زمین میں ہے خدا سب کو جانتا ہے اور یہ کہ خدا کو ہر چیز کا علم ہے');
INSERT INTO "jalandhari" VALUES(5,98,'5:98','جان رکھو کہ خدا سخت غداب دینے والا ہے اور یہ کہ خدا بخشنے والا مہربان بھی ہے');
INSERT INTO "jalandhari" VALUES(5,99,'5:99','پیغمبر کے ذمے تو صرف پیغام خدا کا پہنچا دینا ہے اور جو کچھ تم ظاہر کرتے ہو اور جو کچھ مخفی کرتے ہو خدا کو سب معلوم ہے');
INSERT INTO "jalandhari" VALUES(5,100,'5:100','کہہ دو کہ ناپاک چیزیں اور پاک چیزیں برابر نہیں ہوتیں گو ناپاک چیزوں کی کثرت تمہیں خوش ہی لگے تو عقل والو خدا سے ڈرتے رہو تاکہ رستگاری حاصل کرو');
INSERT INTO "jalandhari" VALUES(5,101,'5:101','مومنو! ایسی چیزوں کے بارے میں مت سوال کرو کہ اگر (ان کی حقیقتیں) تم پر ظاہر کر دی جائیں تو تمہیں بری لگیں اور اگر قرآن کے نازل ہونے کے ایام میں ایسی باتیں پوچھو گے تو تم پر ظاہر بھی کر دی جائیں گی (اب تو) خدا نے ایسی باتوں (کے پوچھنے) سے درگزر فرمایا ہے اور خدا بخشنے والا بردبار ہے');
INSERT INTO "jalandhari" VALUES(5,102,'5:102','اس طرح کی باتیں تم سے پہلے لوگوں نے بھی پوچھی تھیں (مگر جب بتائی گئیں تو) پھر ان سے منکر ہو گئے');
INSERT INTO "jalandhari" VALUES(5,103,'5:103','خدا نے نہ تو بحیرہ کچھ چیز بنایا ہے اور نہ سائبہ اور نہ وصیلہ اور نہ حام بلکہ کافر خدا پر جھوٹ افترا کرتے ہیں اور یہ اکثر عقل نہیں رکھتے');
INSERT INTO "jalandhari" VALUES(5,104,'5:104','اور جب ان لوگوں سے کہا جاتا ہے کہ جو (کتاب) خدا نے نازل فرمائی ہے اس کی اور رسول الله کی طرف رجوع کرو تو کہتے ہیں کہ جس طریق پر ہم نے اپنے باپ دادا کو پایا ہے وہی ہمیں کافی ہے بھلا اگر ان کے باپ دادا نہ تو کچھ جانتے ہوں اور نہ سیدھے رستے پر ہوں (تب بھی؟)');
INSERT INTO "jalandhari" VALUES(5,105,'5:105','اے ایمان والو! اپنی جانوں کی حفاظت کرو جب تم ہدایت پر ہو تو کوئی گمراہ تمہارا کچھ بھی بگاڑ نہیں سکتا تم سب کو خدا کی طرف لوٹ کر جانا ہے اس وقت وہ تم کو تمہارے سب کاموں سے جو (دنیا میں) کئے تھے آگاہ کرے گا (اور ان کا بدلہ دے گا)');
INSERT INTO "jalandhari" VALUES(5,106,'5:106','مومنو! جب تم میں سے کسی کی موت آموجود ہو تو شہادت (کا نصاب) یہ ہے کہ وصیت کے وقت تم (مسلمانوں) میں سے دو عادل (یعنی صاحب اعتبار) گواہ ہوں یا اگر (مسلمان نہ ملیں اور) تم سفر کر رہے ہو اور (اس وقت) تم پر موت کی مصیبت واقع ہو تو کسی دوسرے مذہب کے دو (شخصوں کو) گواہ (کر لو) اگر تم کو ان گواہوں کی نسبت کچھ شک ہو تو ان کو (عصر کی) نماز کے بعد کھڑا کرو اور دونوں خدا کی قسمیں کھائیں کہ ہم شہادت کا کچھ عوض نہیں لیں گے گو ہمارا رشتہ دار ہی ہو اور نہ ہم الله کی شہادت کو چھپائیں گے اگر ایسا کریں گے تو گنہگار ہوں گے');
INSERT INTO "jalandhari" VALUES(5,107,'5:107','پھر اگر معلوم ہو جائے کہ ان دونوں نے (جھوٹ بول کر) گناہ حاصل کیا ہے تو جن لوگوں کا انہوں نے حق مارنا چاہا تھا ان میں سے ان کی جگہ اور دو گواہ کھڑے ہوں جو (میت سے) قرابت قریبہ رکھتے ہوں پھر وہ خدا کی قسمیں کھائیں کہ ہماری شہادت ان کی شہادت سے بہت اچھی ہے اور ہم نے کوئی زیادتی نہیں کی ایسا کیا ہو تو ہم بےانصاف ہیں');
INSERT INTO "jalandhari" VALUES(5,108,'5:108','اس طریق سے بہت قریب ہے کہ یہ لوگ صحیح صحیح شہادت دیں یا اس بات سے خوف کریں کہ (ہماری) قسمیں ان کی قسموں کے بعد رد کر دی جائیں گی اور خدا سے ڈرو اور اس کے حکموں کو (گوشِ ہوش سے) سنو اور خدا نافرمان لوگوں کو ہدایت نہیں دیتا');
INSERT INTO "jalandhari" VALUES(5,109,'5:109','(وہ دن یاد رکھنے کے لائق ہے) جس دن خدا پیغمبروں کو جمع کرے گا پھر ان سے پوچھے گا کہ تمہیں کیا جواب ملا تھا وہ عرض کریں گے کہ ہمیں کچھ معلوم نہیں توہی غیب کی باتوں سے واقف ہے');
INSERT INTO "jalandhari" VALUES(5,110,'5:110','جب خدا (عیسیٰ سے) فرمائے گا کہ اے عیسیٰ بن مریم! میرے ان احسانوں کو یاد کرو جو میں نے تم پر اور تمہاری والدہ پر کئے جب میں نے روح القدس (یعنی جبرئیل) سے تمہاری مدد کی تم جھولے میں اور جوان ہو کر (ایک ہی نسق پر) لوگوں سے گفتگو کرتے تھے اور جب میں نے تم کو کتاب اور دانائی اور تورات اور انجیل سکھائی اور جب تم میرے حکم سے مٹی کا جانور بنا کر اس میں پھونک مار دیتے تھے تو وہ میرے حکم سے اڑنے لگتا تھا اور مادر زاد اندھے اور سفید داغ والے کو میرے حکم سے چنگا کر دیتے تھے اور مردے کو میرے حکم سے (زندہ کرکے قبر سے) نکال کھڑا کرتے تھے اور جب میں نے بنی اسرائیل (کے ہاتھوں) کو تم سے روک دیا جب تم ان کے پاس کھلے نشان لے کر آئے تو جو ان میں سے کافر تھے کہنے لگے کہ یہ صریح جادو ہے');
INSERT INTO "jalandhari" VALUES(5,111,'5:111','اور جب میں نے حواریوں کی طرف حکم بھیجا کہ مجھ پر اور میرے پیغمبر پر ایمان لاؤ وہ کہنے لگے کہ (پروردگار) ہم ایمان لائے تو شاہد رہیو کہ ہم فرمانبردار ہیں');
INSERT INTO "jalandhari" VALUES(5,112,'5:112','(وہ قصہ بھی یاد کرو) جب حواریوں نے کہا کہ اے عیسیٰ بن مریم! کیا تمہارا پروردگار ایسا کر سکتا ہے کہ ہم پر آسمان سے (طعام کا) خوان نازل کرے؟ انہوں نے کہا کہ اگر ایمان رکھتے ہو تو خدا سے ڈرو');
INSERT INTO "jalandhari" VALUES(5,113,'5:113','وہ بولے کہ ہماری یہ خواہش ہے کہ ہم اس میں سے کھائیں اور ہمارے دل تسلی پائیں اور ہم جان لیں کہ تم نے ہم سے سچ کہا ہے اور ہم اس (خوان کے نزول) پر گواہ رہیں');
INSERT INTO "jalandhari" VALUES(5,114,'5:114','(تب) عیسیٰ بن مریم نے دعا کی کہ اے ہمارے پروردگار! ہم پر آسمان سے خوان نازل فرما کہ ہمارے لیے (وہ دن) عید قرار پائے یعنی ہمارے اگلوں اور پچھلوں (سب) کے لیے اور وہ تیری طرف سے نشانی ہو اور ہمیں رزق دے تو بہتر رزق دینے والا ہے');
INSERT INTO "jalandhari" VALUES(5,115,'5:115','خدا نے فرمایا میں تم پر ضرور خوان نازل فرماؤں گا لیکن جو اس کے بعد تم میں سے کفر کرے گا اسے ایسا عذاب دوں گا کہ اہل عالم میں کسی کو ایسا عذاب نہ دوں گا');
INSERT INTO "jalandhari" VALUES(5,116,'5:116','اور (اس وقت کو بھی یاد رکھو) جب خدا فرمائے گا کہ اے عیسیٰ بن مریم! کیا تم نے لوگوں سے کہا تھا کہ خدا کے سوا مجھے اور میری والدہ کو معبود مقرر کرو؟ وہ کہیں گے کہ تو پاک ہے مجھے کب شایاں تھا کہ میں ایسی بات کہتا جس کا مجھے کچھ حق نہیں اگر میں نے ایسا کہا ہوگا تو تجھ کو معلوم ہوگا (کیونکہ) جو بات میرے دل میں ہے تو اسے جانتا ہے اور جو تیرے ضمیر میں ہے اسے میں نہیں جانتا بےشک تو علاّم الغیوب ہے');
INSERT INTO "jalandhari" VALUES(5,117,'5:117','میں نے ان سے کچھ نہیں کہا بجز اس کے جس کا تو نے مجھے حکم دیا ہے وہ یہ کہ تم خدا کی عبادت کرو جو میرا اور تمہارا سب کا پروردگار ہے اور جب تک میں ان میں رہا ان (کے حالات) کی خبر رکھتا رہا جب تو نے مجھے دنیا سے اٹھا لیا تو تو ان کا نگران تھا اور تو ہر چیز سے خبردار ہے');
INSERT INTO "jalandhari" VALUES(5,118,'5:118','اگر تو ان کو عذاب دے تو یہ تیرے بندے ہیں اور اگر بخش دے تو (تیری مہربانی ہے) بےشک تو غالب اور حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(5,119,'5:119','خدا فرمائے گا کہ آج وہ دن ہے کہ راست بازوں کو ان کی سچائی ہی فائدہ دے گی ان کے لئے باغ ہیں جن کے نیچے نہریں بہہ رہی ہیں ابدالآباد ان میں بستے رہیں گے خدا ان سے خوش ہے اور وہ خدا سے خوش ہیں یہ بڑی کامیابی ہے');
INSERT INTO "jalandhari" VALUES(5,120,'5:120','آسمان اور زمین اور جو کچھ ان (دونوں) میں ہے سب پر خدا ہی کی بادشاہی ہے اور وہ ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(6,1,'6:1','ہر طرح کی تعریف خدا ہی کو سزاوار ہے جس نے آسمانوں اور زمین کو پیدا کیا اور اندھیرا اور روشنی بنائی پھر بھی کافر (اور چیزوں کو) خدا کے برابر ٹھیراتے ہیں');
INSERT INTO "jalandhari" VALUES(6,2,'6:2','وہی تو ہے جس نے تم کو مٹی سے پیدا کیا پھر (مرنے کا) ایک وقت مقرر کر دیا اور ایک مدت اس کے ہاں اور مقرر ہے پھر بھی تم (اے کافرو خدا کے بارے میں) شک کرتے ہو');
INSERT INTO "jalandhari" VALUES(6,3,'6:3','اور آسمانوں اور زمین میں وہی (ایک) خدا ہے تمہاری پوشیدہ اور ظاہر سب باتیں جانتا ہے اور تم جو عمل کرتے ہو سب سے واقف ہے');
INSERT INTO "jalandhari" VALUES(6,4,'6:4','اور خدا کی نشانیوں میں سے کوئی نشانی ان لوگوں کے پاس نہیں آتی مگر یہ اس سے منہ پھیر لیتے ہیں');
INSERT INTO "jalandhari" VALUES(6,5,'6:5','جب ان کے پاس حق آیا تو اس کو بھی جھٹلا دیا سو ان کو ان چیزوں کا جن سے یہ استہزا کرتے ہیں عنقریب انجام معلوم ہو جائے گا');
INSERT INTO "jalandhari" VALUES(6,6,'6:6','کیا انہوں نے نہیں دیکھا کہ ہم نے ان سے پہلے کتنی امتوں کو ہلاک کر دیا جن کے پاؤں ملک میں ایسے جما دیئے تھے کہ تمہارے پاؤں بھی ایسے نہیں جمائے اور ان پر آسمان سے لگاتار مینہ برسایا اور نہریں بنا دیں جو ان کے (مکانوں کے) نیچے بہہ رہی تھیں پھر ان کو ان کے گناہوں کے سبب ہلاک کر دیا اور ان کے بعد اور امتیں پیدا کر دیں');
INSERT INTO "jalandhari" VALUES(6,7,'6:7','اور اگر ہم تم پر کاغذوں پر لکھی ہوئی کتاب نازل کرتے اور یہ اسے اپنے ہاتھوں سے بھی ٹٹول لیتے تو جو کافر ہیں وہ یہی کہہ دیتے کہ یہ تو (صاف اور) صریح جادو ہے');
INSERT INTO "jalandhari" VALUES(6,8,'6:8','اور کہتے ہیں کہ ان (پیغمبر) پر فرشتہ کیوں نازل نہ ہوا (جو ان کی تصدیق کرتا) اگر ہم فرشتہ نازل کرتے تو کام ہی فیصل ہو جاتا پھر انھیں (مطلق) مہلت نہ دی جاتی');
INSERT INTO "jalandhari" VALUES(6,9,'6:9','نیز اگر ہم کسی فرشتہ کو بھیجتے تو اسے مرد کی صورت میں بھیجتے اور جو شبہ (اب) کرتے ہیں اسی شبے میں پھر انہیں ڈال دیتے');
INSERT INTO "jalandhari" VALUES(6,10,'6:10','اور تم سے پہلے بھی پیغمبروں کے ساتھ تمسخر ہوتے رہے ہیں سو جو لوگ ان میں سے تمسخر کیا کرتے تھے ان کو تمسخر کی سزا نے آگھیرا');
INSERT INTO "jalandhari" VALUES(6,11,'6:11','کہو کہ (اے منکرین رسالت) ملک میں چلو پھرو پھر دیکھو کہ جھٹلانے والوں کا کیا انجام ہوا');
INSERT INTO "jalandhari" VALUES(6,12,'6:12','(ان سے) پوچھو کہ آسمان اور زمین میں جو کچھ ہے کس کا ہے کہہ دو خدا کا اس نے اپنی ذات (پاک) پر رحمت کو لازم کر لیا ہے وہ تم سب کو قیامت کے دن جس میں کچھ بھی شک نہیں ضرور جمع کرے گا جن لوگوں نے اپنے تیئیں نقصان میں ڈال رکھا ہے وہ ایمان نہیں لاتے');
INSERT INTO "jalandhari" VALUES(6,13,'6:13','اور جو مخلوق رات اور دن میں بستی ہے سب اسی کی ہے اور وہ سنتا جانتا ہے');
INSERT INTO "jalandhari" VALUES(6,14,'6:14','کہو کیا میں خدا کو چھوڑ کر کسی اور کو مددگار بناؤں کہ (وہی تو) آسمانوں اور زمین کا پیدا کرنے والا ہے اور وہی (سب کو) کھانا دیتا ہے اور خود کسی سے کھانا نہیں لیتا (یہ بھی) کہہ دو کہ مجھے یہ حکم ہوا ہے کہ میں سب سے پہلے اسلام لانے والا ہوں اور یہ کہ تم (اے پیغمبر!) مشرکوں میں نہ ہونا');
INSERT INTO "jalandhari" VALUES(6,15,'6:15','(یہ بھی) کہہ دو کہ اگر میں اپنے پروردگار کی نافرمانی کروں تو مجھے بڑے دن کے عذاب کا خوف ہے');
INSERT INTO "jalandhari" VALUES(6,16,'6:16','جس شخص سے اس روز عذاب ٹال دیا گیا اس پر خدا نے (بڑی) مہربانی فرمائی اور یہ کھلی کامیابی ہے');
INSERT INTO "jalandhari" VALUES(6,17,'6:17','اور اگر خدا تم کو کوئی سختی پہنچائے تو اس کے سوا اس کو کوئی دور کرنے والا نہیں اور اگر نعمت (وراحت) عطا کرے تو (کوئی اس کو روکنے والا نہیں) وہ ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(6,18,'6:18','اور وہ اپنے بندوں پر غالب ہے اور وہ دانا اور خبردار ہے');
INSERT INTO "jalandhari" VALUES(6,19,'6:19','ان سے پوچھو کہ سب سے بڑھ کر (قرین انصاف) کس کی شہادت ہے کہہ دو کہ خدا ہی مجھ میں اور تم میں گواہ ہے اور یہ قرآن مجھ پر اس لیے اتارا گیا ہے کہ اس کے ذریعے سے تم کو اور جس شخص تک وہ پہنچ سکے آگاہ کردوں کیا تم لوگ اس بات کی شہادت دیتے ہو کہ خدا کے ساتھ اور بھی معبود ہیں (اے محمدصلى الله عليه وسلم!) کہہ دو کہ میں تو (ایسی) شہادت نہیں دیتا کہہ دو کہ صرف وہی ایک معبود ہے اور جن کو تم لوگ شریک بناتے ہو میں ان سے بیزار ہوں');
INSERT INTO "jalandhari" VALUES(6,20,'6:20','جن لوگوں کو ہم نے کتاب دی ہے وہ ان (ہمارے پیغمبرصلى الله عليه وسلم) کو اس طرح پہچانتے ہیں جس طرح اپنے بیٹوں کو پہچانا کرتے ہیں جنہوں نے اپنے تئیں نقصان میں ڈال رکھا ہے وہ ایمان نہیں لاتے');
INSERT INTO "jalandhari" VALUES(6,21,'6:21','اور اس شخص سے زیادہ کون ظالم ہے جس نے خدا پر جھوٹ افتراء کیا یا اس کی آیتوں کو جھٹلایا۔ کچھ شک نہیں کہ ظالم لوگ نجات نہیں پائیں گے');
INSERT INTO "jalandhari" VALUES(6,22,'6:22','اور جس دن ہم سب لوگوں کو جمع کریں گے پھر مشرکوں سے پوچھیں گے کہ (آج) وہ تمہارے شریک کہاں ہیں جن کو تمہیں دعویٰ تھا');
INSERT INTO "jalandhari" VALUES(6,23,'6:23','تو ان سے کچھ عذر نہ بن پڑے گا (اور) بجز اس کے (کچھ چارہ نہ ہوگا) کہ کہیں خدا کی قسم جو ہمارا پروردگار ہے ہم شریک نہیں بناتے تھے');
INSERT INTO "jalandhari" VALUES(6,24,'6:24','دیکھو انہوں نے اپنے اوپر کیسا جھوٹ بولا اور جو کچھ یہ افتراء کیا کرتے تھے سب ان سے جاتا رہا');
INSERT INTO "jalandhari" VALUES(6,25,'6:25','اور ان میں بعض ایسے ہیں کہ تمہاری (باتوں کی) طرف کان رکھتے ہیں۔ اور ہم نے ان کے دلوں پر تو پردے ڈال دیئے ہیں کہ ان کو سمجھ نہ سکیں اور کانوں میں ثقل پیدا کردیا ہے (کہ سن نہ سکیں) اور اگر یہ تمام نشانیاں بھی دیکھ لیں تب بھی ان پر ایمان نہ لائیں۔ یہاں تک کہ جب تمہارے پاس تم سے بحث کرنے کو آتے ہیں تو جو کافر ہیں کہتے ہیں یہ (قرآن) اور کچھ بھی نہیں صرف پہلے لوگوں کی کہانیاں ہیں');
INSERT INTO "jalandhari" VALUES(6,26,'6:26','وہ اس سے (اوروں کو بھی) روکتے ہیں اور خود بھی پرے رہتے ہیں مگر (ان باتوں سے) اپنے آپ ہی کو ہلاک کرتے ہیں اور (اس سے) بےخبر ہیں');
INSERT INTO "jalandhari" VALUES(6,27,'6:27','کاش تم (ان کو اس وقت) دیکھو جب یہ دوزخ کے کنارے کھڑے کئے جائیں گے اور کہیں گے کہ اے کاش ہم پھر (دنیا میں) لوٹا دیئے جائیں تاکہ اپنے پروردگار کی آیتوں کی تکذیب نہ کریں اور مومن ہوجائیں');
INSERT INTO "jalandhari" VALUES(6,28,'6:28','ہاں یہ جو کچھ پہلے چھپایا کرتے تھے (آج) ان پر ظاہر ہوگیا ہے اور اگر یہ (دنیا میں) لوٹائے بھی جائیں تو جن (کاموں) سے ان کو منع کیا گیا تھا وہی پھر کرنے لگیں۔کچھ شک نہیں کہ یہ جھوٹے ہیں');
INSERT INTO "jalandhari" VALUES(6,29,'6:29','اور کہتے ہیں کہ ہماری جو دنیا کی زندگی ہے بس یہی (زندگی) ہے اور ہم (مرنے کے بعد) پھر زندہ نہیں کئے جائیں گے');
INSERT INTO "jalandhari" VALUES(6,30,'6:30','اور کاش تم (ان کو اس وقت) دیکھو جب یہ اپنے پروردگار کےسامنے کھڑے کئے جائیں گے اور وہ فرمائےگا کیا یہ (دوبارہ زندہ ہونا) برحق نہیں تو کہیں گے کیوں نہیں پروردگار کی قسم (بالکل برحق ہے)خدا فرمائے گا اب کفر کے بدلے (جو دنیا میں کرتے تھے) عذاب (کے مزے) چکھو');
INSERT INTO "jalandhari" VALUES(6,31,'6:31','جن لوگوں نے خدا کے روبرو حاضر ہونے کو جھوٹ سمجھا وہ گھاٹے میں آگئے۔ یہاں تک کہ جب ان پر قیامت ناگہاں آموجود ہوگی تو بول اٹھیں گے کہ (ہائے) اس تقصیر پر افسوس ہے جو ہم نے قیامت کے بارے میں کی۔ اور وہ اپنے (اعمال کے) بوجھ اپنی پیٹھوں پر اٹھائے ہوئے ہوں گے۔ دیکھو جو بوجھ یہ اٹھا رہے ہیں بہت برا ہے');
INSERT INTO "jalandhari" VALUES(6,32,'6:32','اور دنیا کی زندگی تو ایک کھیل اور مشغولہ ہے۔ اور بہت اچھا گھر تو آخرت کا گھر ہے (یعنی) ان کے لئے جو (خدا سے) ڈرتے ہیں۔ کیا تم سمجھتے نہیں');
INSERT INTO "jalandhari" VALUES(6,33,'6:33','ہم کو معلوم ہے کہ ان (کافروں) کی باتیں تمہیں رنج پہنچاتی ہیں (مگر) یہ تمہاری تکذیب نہیں کرتے بلکہ ظالم خدا کی آیتوں سے انکار کرتے ہیں');
INSERT INTO "jalandhari" VALUES(6,34,'6:34','اور تم سے پہلے کبھی پیغمبر جھٹلائے جاتے رہے تو وہ تکذیب اور ایذا پر صبر کرتے رہے یہاں تک کہ ان کے پاس ہماری مدد پہنچتی رہی اور خدا کی باتوں کو کوئی بھی بدلنے والا نہیں۔ اور تم کو پیغمبروں (کے احوال) کی خبریں پہنچ چکی ہیں (تو تم بھی صبر سے کام لو)');
INSERT INTO "jalandhari" VALUES(6,35,'6:35','اور اگر ان کی روگردانی تم پر شاق گزرتی ہے تو اگر طاقت ہو تو زمین میں کوئی سرنگ ڈھونڈ نکالو یا آسمان میں سیڑھی (تلاش کرو) پھر ان کے پاس کوئی معجزہ لاؤ۔ اور اگر خدا چاہتا تو سب کو ہدایت پر جمع کردیتا پس تم ہرگز نادانوں میں نہ ہونا');
INSERT INTO "jalandhari" VALUES(6,36,'6:36','بات یہ ہے کہ (حق کو) قبول وہی کرتے ہیں جو سنتے بھی ہیں اور مردوں کو تو خدا (قیامت ہی کو) اٹھائے گا۔ پھر اسی کی طرف لوٹ کر جائیں گے');
INSERT INTO "jalandhari" VALUES(6,37,'6:37','اور کہتے ہیں کہ ان پر ان کے پروردگارکے پاس کوئی نشانی کیوں نازل نہیں ہوئی۔ کہہ دو کہ خدا نشانی اتارنے پر قادر ہے لیکن اکثر لوگ نہیں جانتے');
INSERT INTO "jalandhari" VALUES(6,38,'6:38','اور زمین میں جو چلنے پھرنے والا (حیوان) یا دو پروں سے اڑنے والا جانور ہے ان کی بھی تم لوگوں کی طرح جماعتیں ہیں۔ ہم نے کتاب (یعنی لوح محفوظ) میں کسی چیز (کے لکھنے) میں کوتاہی نہیں کی پھر سب اپنے پروردگار کی طرف جمع کئے جائیں گے');
INSERT INTO "jalandhari" VALUES(6,39,'6:39','اور جن لوگوں نے ہماری آیتوں کو جھٹلایا وہ بہرے اور گونگے ہیں (اس کے علاوہ) اندھیرے میں (پڑے ہوئے) جس کو خدا چاہے گمراہ کردے اور جسے چاہے سیدھے رستے پر چلا دے');
INSERT INTO "jalandhari" VALUES(6,40,'6:40','کہو (کافرو) بھلا دیکھو تو اگر تم پر خدا کا عذاب آجائےیا قیامت آموجود ہو تو کیا تم (ایسی حالت میں) خدا کے سوا کسی اور کو پکارو گے؟ اگر سچے ہو (تو بتاؤ)');
INSERT INTO "jalandhari" VALUES(6,41,'6:41','بلکہ (مصیبت کے وقت تم) اسی کو پکارتے ہو تو جس دکھ کے لئے اسے پکارتے ہو۔ وہ اگر چاہتا ہے تو اس کو دور کردیتا ہے اور جن کو تم شریک بناتے ہو (اس وقت) انہیں بھول جاتے ہو');
INSERT INTO "jalandhari" VALUES(6,42,'6:42','اور ہم نے تم سے پہلے بہت سی امتوں کی طرف پیغمبر بھیجے۔ پھر (ان کی نافرمانیوں کے سبب) ہم انہیں سختیوں اور تکلیفوں میں پکڑتے رہے تاکہ عاجزی کریں');
INSERT INTO "jalandhari" VALUES(6,43,'6:43','تو جب ان پر ہمارا عذاب آتا رہا کیوں نہیں عاجزی کرتے رہے۔ مگر ان کے تو دل ہی سخت ہوگئے تھے۔ اور جو وہ کام کرتے تھے شیطان ان کو (ان کی نظروں میں) آراستہ کر دکھاتا تھا');
INSERT INTO "jalandhari" VALUES(6,44,'6:44','پھر جب انہوں نے اس نصیحت کو جو ان کو گی گئی تھی فراموش کردیا تو ہم نے ان پر ہر چیز کے دروازے کھول دیئے۔ یہاں تک کہ جب ان چیزوں سے جو ان کو دی گئی تھیں خوب خوش ہوگئے تو ہم نے ان کو ناگہاں پکڑ لیا اور وہ اس وقت مایوس ہو کر رہ گئے');
INSERT INTO "jalandhari" VALUES(6,45,'6:45','غرض ظالم لوگوں کی جڑ کاٹ دی گئی۔ اور سب تعریف خدائے رب العالمین ہی کو (سزاوار ہے)');
INSERT INTO "jalandhari" VALUES(6,46,'6:46','(ان کافروں سے) کہو کہ بھلا دیکھو تو اگر خدا تمہارے کان اور آنکھیں چھین لے اور تمہارے دلوں پر مہر لگادے تو خداکے سوا کون سا معبود ہے جو تمہیں یہ نعمتیں پھر بخشے؟ دیکھو ہم کس کس طرح اپنی آیتیں بیان کرتے ہیں۔ پھر بھی یہ لوگ ردگردانی کرتے ہیں');
INSERT INTO "jalandhari" VALUES(6,47,'6:47','کہو کہ بھلا بتاؤ تو اگر تم پر خدا کا عذاب بےخبری میں یا خبر آنے کے بعد آئے تو کیا ظالم لوگوں کے سوا کوئی اور بھی ہلاک ہوگا؟');
INSERT INTO "jalandhari" VALUES(6,48,'6:48','اور ہم جو پیغمبروں کو بھیجتے رہے ہیں تو خوشخبری سنانے اور ڈرانے کو پھر جو شخص ایمان لائے اور نیکوکار ہوجائے تو ایسے لوگوں کو نہ کچھ خوف ہوگا اور نہ وہ اندوہناک ہوں گے');
INSERT INTO "jalandhari" VALUES(6,49,'6:49','اور جنہوں نے ہماری آیتوں کو جھٹلایا ان کی نافرمانیوں کے سبب انہیں عذاب ہوگا');
INSERT INTO "jalandhari" VALUES(6,50,'6:50','کہہ دو کہ میں تم سے یہ نہیں کہتا کہ میرے پاس الله تعالیٰ کے خزانے ہیں اور نہ (یہ کہ) میں غیب جانتا ہوں اور نہ تم سے یہ کہتا کہ میں فرشتہ ہوں۔ میں تو صرف اس حکم پر چلتا ہوں جو مجھے (خدا کی طرف سے) آتا ہے۔ کہہ دو کہ بھلا اندھا اور آنکھ والے برابر ہوتے ہیں؟ تو پھر تم غور کیوں نہیں کرتے');
INSERT INTO "jalandhari" VALUES(6,51,'6:51','اور جو لوگ جو خوف رکھتے ہیں کہ اپنے پروردگار کے روبرو حاضر کئے جائیں گے (اور جانتے ہیں کہ) اس کے سوا نہ تو ان کا کوئی دوست ہوگا اور نہ سفارش کرنے والا، ان کو اس (قرآن) کے ذریعے سے نصیحت کر دو تاکہ پرہیزگار بنیں');
INSERT INTO "jalandhari" VALUES(6,52,'6:52','اور جو لوگ صبح وشام اپنی پروردگار سے دعا کرتے ہیں (اور) اس کی ذات کے طالب ہیں ان کو (اپنے پاس سے) مت نکالو۔ ان کے حساب (اعمال) کی جوابدہی تم پر کچھ نہیں اور تمہارے حساب کی جوابدہی ان پر کچھ نہیں (پس ایسا نہ کرنا) اگر ان کو نکالوگے تو ظالموں میں ہوجاؤ گے');
INSERT INTO "jalandhari" VALUES(6,53,'6:53','اور اسی طرح ہم نے بعض لوگوں کی بعض سے آزمائش کی ہے کہ (جو دولتمند ہیں وہ غریبوں کی نسبت) کہتے ہیں کیا یہی لوگ ہیں جن پر خدا نے ہم میں سے فضل کیا ہے (خدا نے فرمایا) بھلا خدا شکر کرنے والوں سے واقف نہیں؟');
INSERT INTO "jalandhari" VALUES(6,54,'6:54','اور جب تمہارے پاس ایسے لوگ آیا کریں جو ہماری آیتوں پر ایمان لاتے ہیں تو (ان سے) سلام علیکم کہا کرو خدا نے اپنی ذات (پاک) پر رحمت کو لازم کرلیا ہے کہ جو کوئی تم میں نادانی سے کوئی بری حرکت کر بیٹھے پھر اس کے بعد توبہ کرلے اور نیکوکار ہوجائے تو وہ بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(6,55,'6:55','اور اس طرح ہم اپنی آیتیں کھول کھول کر بیان کرتے ہیں (تاکہ تم لوگ ان پر عمل کرو) اور اس لئے کہ گنہگاروں کا رستہ ظاہر ہوجائے');
INSERT INTO "jalandhari" VALUES(6,56,'6:56','(اے پیغمبر! کفار سے) کہہ دو کہ جن کو تم خدا کے سوا پکارتے ہو مجھے ان کی عبادت سے منع کیا گیا ہے۔ (یہ بھی) کہہ دو کہ میں تمہاری خواہشوں کی پیروی نہیں کروں گا ایسا کروں تو گمراہ ہوجاؤں اور ہدایت یافتہ لوگوں میں نہ رہوں');
INSERT INTO "jalandhari" VALUES(6,57,'6:57','کہہ دو کہ میں تو اپنے پروردگار کی دلیل روشن پر ہوں اور تم اس کی تکذیب کرتے ہو۔ جس چیز (یعنی عذاب) کے لئے تم جلدی کر رہے ہو وہ میرے پاس نہیں ہے (ایسا) حکم الله ہی کے اختیار میں ہے وہ سچی بات بیان فرماتا ہے اور وہ سب سے بہتر فیصلہ کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(6,58,'6:58','کہہ دو کہ جس چیز کے لئے تم جلدی کر رہے ہو اگر وہ میرے اختیار میں ہوتی تو مجھ میں اور تم میں فیصلہ ہوچکا ہوتا۔ اور خدا ظالموں سے خوب واقف ہے');
INSERT INTO "jalandhari" VALUES(6,59,'6:59','اور اسی کے پاس غیب کی کنجیاں ہیں جن کو اس کے سوا کوئی نہیں جانتا۔ اور اسے جنگلوں اور دریاؤں کی سب چیزوں کا علم ہے۔ اور کوئی پتہ نہیں جھڑتا مگر وہ اس کو جانتا ہے اور زمین کے اندھیروں میں کوئی دانہ اور کوئی ہری اور سوکھی چیز نہیں ہے مگر کتاب روشن میں (لکھی ہوئی) ہے');
INSERT INTO "jalandhari" VALUES(6,60,'6:60','اور وہی تو ہے جو رات کو (سونے کی حالت میں) تمہاری روح قبض کرلیتا ہے اور جو کچھ تم دن میں کرتے ہو اس سے خبر رکھتا ہے پھر تمہیں دن کو اٹھا دیتا ہے تاکہ (یہی سلسلہ جاری رکھ کر زندگی کی) معین مدت پوری کردی جائے پھر تم (سب) کو اسی کی طرف لوٹ کر جانا ہے (اس روز) وہ تم کو تمہارے عمل جو تم کرتے ہو (ایک ایک کرکے) بتائے گا');
INSERT INTO "jalandhari" VALUES(6,61,'6:61','اور وہ اپنے بندوں پر غالب ہے۔ اور تم پر نگہبان مقرر کئے رکھتا ہے۔ یہاں تک کہ جب تم میں سے کسی کی موت آتی ہے تو ہمارے فرشتے اس کی روح قبض کرلیتے ہیں اور وہ کسی طرح کی کوتاہی نہیں کرتے');
INSERT INTO "jalandhari" VALUES(6,62,'6:62','پھر (قیامت کے دن تمام) لوگ اپنے مالک برحق خدا تعالیٰ کے پاس واپس بلائے جائیں گے۔ سن لو کہ حکم اسی کا ہے اور وہ نہایت جلد حساب لینے والا ہے');
INSERT INTO "jalandhari" VALUES(6,63,'6:63','کہو بھلا تم کو جنگلوں اور دریاؤں کے اندھیروں سے کون مخلصی دیتا ہے (جب) کہ تم اسے عاجزی اور نیاز پنہانی سے پکارتے ہو (اور کہتے ہو) اگر خدا ہم کو اس (تنگی) سے نجات بخشے تو ہم اس کے بہت شکر گزار ہوں');
INSERT INTO "jalandhari" VALUES(6,64,'6:64','کہو کہ خدا ہی تم کو اس (تنگی) سے اور ہر سختی سے نجات بخشتا ہے۔ پھر (تم) اس کے ساتھ شرک کرتے ہو');
INSERT INTO "jalandhari" VALUES(6,65,'6:65','کہہ دو کہ وہ (اس پر بھی) قدرت رکھتا ہے کہ تم پر اوپر کی طرف سے یا تمہارے پاؤں کے نیچے سے عذاب بھیجے یا تمہیں فرقہ فرقہ کردے اور ایک کو دوسرے (سے لڑا کر آپس) کی لڑائی کا مزہ چکھادے۔ دیکھو ہم اپنی آیتوں کو کس کس طرح بیان کرتے ہیں تاکہ یہ لوگ سمجھیں');
INSERT INTO "jalandhari" VALUES(6,66,'6:66','اور اس (قرآن) کو تمہاری قوم نے جھٹلایا حالانکہ وہ سراسر حق ہے۔ کہہ دو کہ میں تمہارا داروغہ نہیں ہوں');
INSERT INTO "jalandhari" VALUES(6,67,'6:67','ہر خبر کے لئے ایک وقت مقرر ہے اور تم کو عنقریب معلوم ہوجائے گا');
INSERT INTO "jalandhari" VALUES(6,68,'6:68','اور جب تم ایسے لوگوں کو دیکھو جو ہماری آیتوں کے بارے میں بیہودہ بکواس کر رہے ہوں تو ان سے الگ ہوجاؤ یہاں تک کہ اور باتوں میں مصروف ہوجائیں۔ اور اگر (یہ بات) شیطان تمہیں بھلا دے تو یاد آنے پر ظالم لوگوں کے ساتھ نہ بیٹھو');
INSERT INTO "jalandhari" VALUES(6,69,'6:69','اور پرہیزگاروں پر ان لوگوں کے حساب کی کچھ بھی جواب دہی نہیں ہاں نصیحت تاکہ وہ بھی پرہیزگار ہوں');
INSERT INTO "jalandhari" VALUES(6,70,'6:70','اور جن لوگوں نے اپنےدین کو کھیل اور تماشا بنا رکھا ہے اور دنیا کی زندگی نے ان کو دھوکے میں ڈال رکھا ہے ان سے کچھ کام نہ رکھو ہاں اس (قرآن) کے ذریعے سے نصیحت کرتے رہو تاکہ (قیامت کے دن) کوئی اپنے اعمال کی سزا میں ہلاکت میں نہ ڈالا جائے (اس روز) خدا کےسوا نہ تو کوئی اس کا دوست ہوگا اور نہ سفارش کرنے والا۔ اور اگر وہ ہر چیز (جو روئے زمین پر ہے بطور) معاوضہ دینا چاہے تو وہ اس سے قبول نہ ہو یہی لوگ ہیں کہ اپنے اعمال کے وبال میں ہلاکت میں ڈالے گئے ان کے لئے پینے کو کھولتا ہوا پانی اور دکھ دینے والا عذاب ہے اس لئے کہ کفر کرتے تھے');
INSERT INTO "jalandhari" VALUES(6,71,'6:71','کہو۔ کیا ہم خدا کے سوا ایسی چیز کو پکاریں جو نہ ہمارا بھلا کرسکے نہ برا۔ اور جب ہم کو خدا نے سیدھا رستہ دکھا دیا تو (کیا) ہم الٹے پاؤں پھر جائیں؟ (پھر ہماری ایسی مثال ہو) جیسے کسی کو جنات نے جنگل میں بھلا دیا ہو (اور وہ) حیران (ہو رہا ہو) اور اس کے کچھ رفیق ہوں جو اس کو رستے کی طرف بلائیں کہ ہمارے پاس چلا آ۔ کہہ دو کہ رستہ تو وہی ہے جو خدا نے بتایا ہے۔ اور ہمیں تو یہ حکم ملا ہے کہ ہم خدائے رب العالمین کے فرمانبردار ہوں');
INSERT INTO "jalandhari" VALUES(6,72,'6:72','اور یہ (بھی) کہ نماز پڑھتے رہو اور اس سے ڈرتے رہو۔ اور وہی تو ہے جس کے پاس تم جمع کئے جاؤ گے');
INSERT INTO "jalandhari" VALUES(6,73,'6:73','اور وہی تو ہے جس نے آسمانوں اور زمین کو تدبیر سے پیدا کیا ہے۔ اور جس دن وہ فرمائے گا کہ ہو جا تو (حشر برپا) ہوجائے گا ۔ اس کا ارشاد برحق ہے۔ اور جس دن صور پھونکا جائے گا (اس دن) اسی کی بادشاہت ہوگی۔ وہی پوشیدہ اور ظاہر (سب) کا جاننے والا ہے اور وہی دانا اور خبردار ہے');
INSERT INTO "jalandhari" VALUES(6,74,'6:74','اور (وہ وقت بھی یاد کرنے کے لائق ہے) جب ابراہیم نے اپنے باپ آزر سے کہا کہ تم بتوں کو کیا معبود بناتے ہو۔ میں دیکھتا ہوں کہ تم اور تمہاری قوم صریح گمراہی میں ہو');
INSERT INTO "jalandhari" VALUES(6,75,'6:75','اور ہم اس طرح ابراہیم کو آسمانوں اور زمین کے عجائبات دکھانے لگے تاکہ وہ خوب یقین کرنے والوں میں ہوجائیں');
INSERT INTO "jalandhari" VALUES(6,76,'6:76','(یعنی) جب رات نے ان کو (پردہٴ تاریکی سے) ڈھانپ لیا (تو آسمان میں) ایک ستارا نظر پڑا۔ کہنے لگے یہ میرا پروردگار ہے۔ جب وہ غائب ہوگیا تو کہنے لگے کہ مجھے غائب ہوجانے والے پسند نہیں');
INSERT INTO "jalandhari" VALUES(6,77,'6:77','پھر جب چاند کو دیکھا کہ چمک رہا ہے تو کہنے لگے یہ میرا پروردگار ہے۔ لیکن جب وہ بھی چھپ گیا تو بول اٹھے کہ میرا پروردگار مجھے سیدھا رستہ نہیں دکھائے گا تو میں ان لوگوں میں ہوجاؤں گا جو بھٹک رہے ہیں');
INSERT INTO "jalandhari" VALUES(6,78,'6:78','پھر جب سورج کو دیکھا کہ جگمگا رہا ہے تو کہنے لگے میرا پروردگار یہ ہے یہ سب سے بڑا ہے۔ مگر جب وہ بھی غروب ہوگیا تو کہنے لگے لوگو! جن چیزوں کو تم (خدا کا) شریک بناتے ہو میں ان سے بیزار ہوں');
INSERT INTO "jalandhari" VALUES(6,79,'6:79','میں نے سب سے یکسو ہو کر اپنے تئیں اسی ذات کی طرف متوجہ کیا جس نے آسمانوں اور زمین کو پیدا کیا ہے اور میں مشرکوں میں سے نہیں ہوں');
INSERT INTO "jalandhari" VALUES(6,80,'6:80','اور ان کی قوم ان سے بحث کرنے لگی تو انہوں نے کہا کہ تم مجھ سے خدا کے بارےمیں (کیا) بحث کرتے ہو اس نے تو مجھے سیدھا رستہ دکھا دیا ہے۔ اور جن چیزوں کو تم اس کا شریک بناتے ہو میں ان سے نہیں ڈرتا۔ ہاں جو میرا پروردگار چاہے۔ میرا پروردگار اپنے علم سے ہر چیز پر احاطہ کئے ہوئے ہے۔ کیا تم خیال نہیں کرتے۔');
INSERT INTO "jalandhari" VALUES(6,81,'6:81','بھلا میں ان چیزوں سے جن کو تم (خدا کا) شریک بناتے ہو کیونکرڈروں جب کہ تم اس سے نہیں ڈرتے کہ خدا کے ساتھ شریک بناتے ہو جس کی اس نے کوئی سند نازل نہیں کی۔ اب دونوں فریق میں سے کون سا فریق امن (اور جمعیت خاطر) کا مستحق ہے۔ اگر سمجھ رکھتے ہو (تو بتاؤ)');
INSERT INTO "jalandhari" VALUES(6,82,'6:82','جو لوگ ایمان لائے اور اپنے ایمان کو (شرک کے) ظلم سے مخلوط نہیں کیا ان کے امن (اور جمعیت خاطر) ہے اور وہی ہدایت پانے والے ہیں');
INSERT INTO "jalandhari" VALUES(6,83,'6:83','اور یہ ہماری دلیل تھی جو ہم نے ابراہیم کو ان کی قوم کے مقابلے میں عطا کی تھی۔ ہم جس کے چاہتے ہیں درجے بلند کردیتے ہیں۔ بےشک تمہارا پروردگار دانا اور خبردار ہے');
INSERT INTO "jalandhari" VALUES(6,84,'6:84','اور ہم نے ان کو اسحاق اور یعقوب بخشے۔ (اور) سب کو ہدایت دی۔ اور پہلے نوح کو بھی ہدایت دی تھی اور ان کی اولاد میں سے داؤد اور سلیمان اور ایوب اور یوسف اور موسیٰ اور ہارون کو بھی۔ اور ہم نیک لوگوں کو ایسا ہی بدلا دیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(6,85,'6:85','اور زکریا اور یحییٰ اور عیسیٰ اور الیاس کو بھی۔ یہ سب نیکوکار تھے');
INSERT INTO "jalandhari" VALUES(6,86,'6:86','اور اسمٰعیل اور الیسع اور یونس اور لوط کو بھی۔ اور ان سب کو جہان کے لوگوں پر فضلیت بخشی تھی');
INSERT INTO "jalandhari" VALUES(6,87,'6:87','اور بعض بعض کو ان کے باپ دادا اور اولاد اور بھائیوں میں سے بھی۔ اور ان کو برگزیدہ بھی کیا تھا اور سیدھا رستہ بھی دکھایا تھا');
INSERT INTO "jalandhari" VALUES(6,88,'6:88','یہ خدا کی ہدایت ہے اس پر اپنے بندوں میں سے جسے چاہے چلائے۔ اور اگر وہ لوگ شرک کرتے تو جو عمل وہ کرتے تھے سب ضائع ہوجاتے');
INSERT INTO "jalandhari" VALUES(6,89,'6:89','یہ وہ لوگ تھے جن کو ہم نے کتاب اور حکم (شریعت) اور نبوت عطا فرمائی تھی۔ اگر یہ (کفار) ان باتوں سے انکار کریں تو ہم نے ان پر (ایمان لانے کے لئے) ایسے لوگ مقرر کردیئے ہیں کہ وہ ان سے کبھی انکار کرنے والے نہیں');
INSERT INTO "jalandhari" VALUES(6,90,'6:90','یہ وہ لوگ ہیں جن کو خدا نے ہدایت دی تھی تو تم انہیں کی ہدایت کی پیروی کرو۔ کہہ دو کہ میں تم سے اس (قرآن) کا صلہ نہیں مانگتا۔ یہ تو جہان کے لوگوں کے لئےمحض نصیحت ہے');
INSERT INTO "jalandhari" VALUES(6,91,'6:91','اور ان لوگوں نے خدا کی قدر جیسی جاننی چاہیئے تھی نہ جانی۔ جب انہوں نے کہا کہ خدا نے انسان پر (وحی اور کتاب وغیرہ) کچھ بھی نازل نہیں کیا۔ کہو جو کتاب موسیٰ لے کر آئے تھے اسے کس نے نازل کیا تھا جو لوگوں کے لئے نور اور ہدایت تھی اور جسے تم نے علیحدہ علیحدہ اوراق (پر نقل) کر رکھا ہے ان (کے کچھ حصے) کو تو ظاہر کرتے ہو اور اکثر کو چھپاتے ہو۔ اور تم کو وہ باتیں سکھائی گئیں جن کو نہ تم جانتے تھے اور نہ تمہارے باپ دادا۔ کہہ دو (اس کتاب کو) خدا ہی نے (نازل کیا تھا) پھر ان کو چھوڑ دیا کہ اپنی بیہودہ بکواس میں کھیلتے رہیں');
INSERT INTO "jalandhari" VALUES(6,92,'6:92','اور (ویسی ہی) یہ کتاب ہے جسے ہم نے نازل کیا ہے بابرکت جو اپنے سے پہلی (کتابوں) کی تصدیق کرتی ہے اور (جو) اس لئے (نازل کی گئی ہے) کہ تم مکے اور اس کے آس پاس کے لوگوں کو آگاہ کردو۔ اور جو لوگ آخرت پر ایمان رکھتے ہیں وہ اس کتاب پر بھی ایمان رکھتے ہیں اور وہ اپنی نمازوں کی پوری خبر رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(6,93,'6:93','اور اس سے بڑھ کر ظالم کون ہوگا جو خدا پر جھوٹ افتراء کرے۔ یا یہ کہے کہ مجھ پر وحی آئی ہے حالانکہ اس پر کچھ بھی وحی نہ آئی ہو اور جو یہ کہے کہ جس طرح کی کتاب خدا نے نازل کی ہے اس طرح کی میں بھی بنا لیتا ہوں۔ اور کاش تم ان ظالم (یعنی مشرک) لوگوں کو اس وقت دیکھو جب موت کی سختیوں میں (مبتلا) ہوں اور فرشتے (ان کی طرف عذاب کے لئے) ہاتھ بڑھا رہے ہوں کہ نکالو اپنی جانیں۔ آج تم کو ذلت کے عذاب کی سزا دی جائے گی اس لئے کہ تم خدا پر جھوٹ بولا کرتے تھے اور اس کی آیتوں سے سرکشی کرتے تھے');
INSERT INTO "jalandhari" VALUES(6,94,'6:94','اور جیسا ہم نے تم کو پہلی دفعہ پیدا کیا تھا ایسا ہی آج اکیلے اکیلے ہمارے پاس آئے اور جو (مال ومتاع) ہم نے تمہیں عطا فرمایا تھا وہ سب اپنی پیٹھ پیچھے چھوڑ آئے اور ہم تمہارے ساتھ تمہارے سفارشیوں کو بھی نہیں دیکھتے جن کی نسبت تم خیال کرتے تھے کہ وہ تمہارے (شفیع اور ہمارے) شریک ہیں۔ (آج) تمہارے آپس کے سب تعلقات منقطع ہوگئے اور جو دعوے تم کیا کرتے تھے سب جاتے رہے');
INSERT INTO "jalandhari" VALUES(6,95,'6:95','بے شک خدا ہی دانے اور گٹھلی کو پھاڑ کر (ان سے درخت وغیرہ) اگاتا ہے وہی جاندار کو بے جان سے نکالتا ہے اور وہی بےجان کا جاندار سے نکالنے والا ہے۔ یہی تو خدا ہے۔ پھر تم کہاں بہکے پھرتے ہو');
INSERT INTO "jalandhari" VALUES(6,96,'6:96','وہی (رات کے اندھیرے سے) صبح کی روشنی پھاڑ نکالتا ہے اور اسی نے رات کو (موجب) آرام (ٹھہرایا) اور سورج اور چاند کو (ذرائع) شمار بنایا ہے۔ یہ خدا کے (مقرر کئے ہوئے) اندازے ہیں جو غالب (اور) علم والا ہے');
INSERT INTO "jalandhari" VALUES(6,97,'6:97','اور وہی تو ہے جس نے تمہارے لئے ستارے بنائے تاکہ جنگلوں اور دریاؤں کے اندھیروں میں ان سے رستے معلوم کرو۔ عقل والوں کے لئے ہم نے اپنی آیتیں کھول کھول کر بیان کردی ہیں');
INSERT INTO "jalandhari" VALUES(6,98,'6:98','اور وہی تو ہے جس نے تم کو ایک شخص سے پیدا کیا۔ پھر (تمہارے لئے) ایک ٹھہرنے کی جگہ ہے اور ایک سپرد ہونے کی سمجھنے والوں کے لئے ہم نے (اپنی) آیتیں کھول کھول کر بیان کردی ہیں');
INSERT INTO "jalandhari" VALUES(6,99,'6:99','اور وہی تو ہے جو آسمان سے مینھ برساتا ہے۔ پھر ہم ہی (جو مینھ برساتے ہیں) اس سے ہر طرح کی روئیدگی اگاتے ہیں۔ پھر اس میں سے سبز سبز کونپلیں نکالتے ہیں۔ اور ان کونپلوں میں سے ایک دوسرے کے ساتھ جڑے ہوئے دانے نکالتے ہیں اور کھجور کے گابھے میں سے لٹکتے ہوئے گچھے اور انگوروں کے باغ اور زیتون اور انار جو ایک دوسرے سے ملتے جلتے بھی ہیں۔ اور نہیں بھی ملتے۔ یہ چیزیں جب پھلتی ہیں تو ان کے پھلوں پر اور (جب پکتی ہیں تو) ان کے پکنے پر نظر کرو۔ ان میں ان لوگوں کے لئے جو ایمان لاتے ہیں (قدرت خدا کی بہت سی) نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(6,100,'6:100','اور ان لوگوں نے جنوں کو خدا کا شریک ٹھہرایا۔ حالانکہ ان کو اسی نے پیدا کیا اور بےسمجھے (جھوٹ بہتان) اس کے لئے بیٹے اور بیٹیاں بنا کھڑی کیں وہ ان باتوں سے جو اس کی نسبت بیان کرتے ہیں پاک ہے اور (اس کی شان ان سے) بلند ہے');
INSERT INTO "jalandhari" VALUES(6,101,'6:101','(وہی) آسمانوں اور زمین کا پیدا کرنے والا (ہے)۔ اس کے اولاد کہاں سے ہو جب کہ اس کی بیوی ہی نہیں۔ اور اس نے ہر چیز کو پیدا کیا ہے۔ اور وہ ہر چیز سے باخبر ہے');
INSERT INTO "jalandhari" VALUES(6,102,'6:102','یہی (اوصاف رکھنے والا) خدا تمہارا پروردگار ہے۔ اس کے سوا کوئی معبود نہیں۔ (وہی) ہر چیز کا پیداکرنے والا (ہے) تو اسی کی عبادت کرو۔ اور وہ ہر چیز کا نگراں ہے');
INSERT INTO "jalandhari" VALUES(6,103,'6:103','(وہ ایسا ہے کہ) نگاہیں اس کا ادراک نہیں کرسکتیں اور وہ نگاہوں کا ادراک کرسکتا ہے اور وہ بھید جاننے والا خبردار ہے');
INSERT INTO "jalandhari" VALUES(6,104,'6:104','(اے محمدصلى الله عليه وسلم! ان سے کہہ دو کہ) تمہارے (پاس) پروردگار کی طرف سے (روشن) دلیلیں پہنچ چکی ہیں تو جس نے (ان کو آنکھ کھول کر) دیکھا اس نے اپنا بھلا کیا اور جو اندھا بنا رہا اس نے اپنے حق میں برا کیا۔ اور میں تمہارا نگہبان نہیں ہوں');
INSERT INTO "jalandhari" VALUES(6,105,'6:105','اور ہم اسی طرح اپنی آیتیں پھیر پھیر کر بیان کرتے ہیں تاکہ کافر یہ نہ کہیں کہ تم (یہ باتیں اہل کتاب سے) سیکھے ہوئے ہو اور تاکہ سمجھنے والے لوگوں کے لئے تشریح کردیں');
INSERT INTO "jalandhari" VALUES(6,106,'6:106','اور جو حکم تمہارے پروردگار کی طرف سے تمہارے پاس آتا ہے اسی کی پیروی کرو۔ اس (پروردگار) کے سوا کوئی معبود نہیں۔ اور مشرکوں سے کنارہ کرلو');
INSERT INTO "jalandhari" VALUES(6,107,'6:107','اور اگر خدا چاہتا تو یہ لوگ شرک نہ کرتے۔ اور (اے پیغمبر!) ہم نے تم کو ان پر نگہبان مقرر نہیں کیا۔ اور نہ تم ان کے داروغہ ہو');
INSERT INTO "jalandhari" VALUES(6,108,'6:108','اور جن لوگوں کو یہ مشرک خدا کے سوا پکارتے ہیں ان کو برا نہ کہنا کہ یہ بھی کہیں خدا کو بےادبی سے بے سمجھے برا (نہ) کہہ بیٹھیں۔ اس طرح ہم نے ہر ایک فرقے کے اعمال (ان کی نظروں میں) اچھے کر دکھائے ہیں۔ پھر ان کو اپنے پروردگار ک طرف لوٹ کر جانا ہے تب وہ ان کو بتائے گا کہ وہ کیا کیا کرتے تھے');
INSERT INTO "jalandhari" VALUES(6,109,'6:109','اور یہ لوگ خدا کی سخت سخت قسمیں کھاتے ہیں کہ اگر ان کے پاس کوئی نشانی آئے تو وہ اس پر ضروری ایمان لے آئیں۔ کہہ دو کہ نشانیاں تو سب خدا ہی کے پاس ہیں۔ اور (مومنو!) تمہیں کیا معلوم ہے (یہ تو ایسے بدبخت ہیں کہ ان کے پاس) نشانیاں آ بھی جائیں تب بھی ایمان نہ لائیں');
INSERT INTO "jalandhari" VALUES(6,110,'6:110','اور ہم ان کے دلوں اور آنکھوں کو الٹ دیں گے (تو) جیسے یہ اس (قرآن) پر پہلی دفعہ ایمان نہیں لائے (ویسے پھر نہ لائیں گے) اور ان کو چھوڑ دیں گے کہ اپنی سرکشی میں بہکتے رہیں');
INSERT INTO "jalandhari" VALUES(6,111,'6:111','اور اگر ہم ان پر فرشتے بھی اتار دیتے اور مردے بھی ان سے گفتگو کرنے لگتے اور ہم سب چیزوں کو ان کے سامنے لا موجود بھی کر دیتے تو بھی یہ ایمان لانے والے نہ تھے اِلّا ماشائالله بات یہ ہے کہ یہ اکثر نادان ہیں');
INSERT INTO "jalandhari" VALUES(6,112,'6:112','اور اسی طرح ہم نے شیطان (سیرت) انسانوں اور جنوں کو ہر پیغمبر کا دشمن بنا دیا تھا وہ دھوکا دینے کے لیے ایک دوسرے کے دل میں ملمع کی باتیں ڈالتے رہتے تھے اور اگر تمہارا پروردگار چاہتا تو وہ ایسا نہ کرتے تو ان کو اور جو کچھ یہ افتراء کرتے ہیں اسے چھوڑ دو');
INSERT INTO "jalandhari" VALUES(6,113,'6:113','اور (وہ ایسے کام) اس لیے بھی (کرتے تھے) کہ جو لوگ آخرت پر ایمان نہیں رکھتے ان کے دل ان کی باتوں پر مائل ہوں اور وہ انہیں پسند کریں اور جو کام وہ کرتے تھے وہ ہی کرنے لگیں');
INSERT INTO "jalandhari" VALUES(6,114,'6:114','(کہو) کیا میں خدا کے سوا اور منصف تلاش کروں حالانکہ اس نے تمہاری طرف واضع المطالب کتاب بھیجی ہے اور جن لوگوں کو ہم نے کتاب (تورات) دی ہے وہ جانتے ہیں کہ وہ تمہارے پروردگار کی طرف سے برحق نازل ہوئی ہے تو تم ہرگز شک کرنے والوں میں نہ ہونا');
INSERT INTO "jalandhari" VALUES(6,115,'6:115','اور تمہارے پروردگار کی باتیں سچائی اور انصاف میں پوری ہیں اس کی باتوں کو کوئی بدلنے والا نہیں اور وہ سنتا جانتا ہے');
INSERT INTO "jalandhari" VALUES(6,116,'6:116','اور اکثر لوگ جو زمین پر آباد ہیں (گمراہ ہیں) اگر تم ان کا کہا مان لو گے تو وہ تمہیں خدا کا رستہ بھلا دیں گے یہ محض خیال کے پیچھے چلتے اور نرے اٹکل کے تیر چلاتے ہیں');
INSERT INTO "jalandhari" VALUES(6,117,'6:117','تمہارا پروردگار ان لوگوں کو خوب جانتا ہے جو اس کے رستے سے بھٹکے ہوئے ہیں اور ان سے بھی خوب واقف ہے جو رستے پر چل رہے ہیں');
INSERT INTO "jalandhari" VALUES(6,118,'6:118','تو جس چیز پر (ذبح کے وقت) خدا کا نام لیا جائے اگر تم اس کی آیتوں پر ایمان رکھتے ہو تو اسے کھا لیا کرو');
INSERT INTO "jalandhari" VALUES(6,119,'6:119','اور سبب کیا ہے کہ جس چیز پر خدا کا نام لیا جائے تم اسے نہ کھاؤ حالانکہ جو چیزیں اس نے تمہارے لیے حرام ٹھیرا دی ہیں وہ ایک ایک کر کے بیان کر دی ہیں (بے شک ان کو نہیں کھانا چاہیے) مگر اس صورت میں کہ ان کے (کھانے کے) لیے ناچار ہو جاؤ اور بہت سے لوگ بےسمجھے بوجھے اپنے نفس کی خواہشوں سے لوگوں کو بہکا رہے ہیں کچھ شک نہیں کہ ایسے لوگوں کو جو (خدا کی مقرر کی ہوئی) حد سے باہر نکل جاتے ہیں تمہارا پروردگار خوب جانتا ہے');
INSERT INTO "jalandhari" VALUES(6,120,'6:120','اور ظاہری اور پوشیدہ (ہر طرح کا) گناہ ترک کر دو جو لوگ گناہ کرتے ہیں وہ عنقریب اپنے کئے کی سزا پائیں گے');
INSERT INTO "jalandhari" VALUES(6,121,'6:121','اور جس چیز پر خدا کا نام نہ لیا جائے اسے مت کھاؤ کہ اس کا کھانا گناہ ہے اور شیطان (لوگ) اپنے رفیقوں کے دلوں میں یہ بات ڈالتے ہیں کہ تم سے جھگڑا کریں اور اگر تم لوگ ان کے کہے پر چلے تو بےشک تم بھی مشرک ہوئے');
INSERT INTO "jalandhari" VALUES(6,122,'6:122','بھلا جو پہلے مردہ تھا پھر ہم نے اس کو زندہ کیا اور اس کے لیے روشنی کر دی جس کے ذریعے سے وہ لوگوں میں چلتا پھرتا ہے کہیں اس شخص جیسا ہو سکتا ہے جو اندھیرے میں پڑا ہوا ہو اور اس سے نکل ہی نہ سکے اسی طرح کافر جو عمل کر رہے ہیں وہ انہیں اچھے معلوم ہوتے ہیں');
INSERT INTO "jalandhari" VALUES(6,123,'6:123','اور اسی طرح ہم نے ہر بستی میں بڑے بڑے مجرم پیدا کئے کہ ان میں مکاریاں کرتے رہیں اور جو مکاریاں یہ کرتے ہیں ان کا نقصان انہیں کو ہے اور (اس سے) بےخبر ہیں');
INSERT INTO "jalandhari" VALUES(6,124,'6:124','اور جب ان کے پاس کوئی آیت آتی ہے تو کہتے ہیں کہ جس طرح کی رسالت خدا کے پیغمبروں کو ملی ہے جب تک اسی طرح کی رسالت ہم کو نہ ملے ہم ہرگز ایمان نہیں لائیں گے اس کو خدا ہی خوب جانتا ہے کہ (رسالت کا کون سا محل ہے اور) وہ اپنی پیغمبری کسے عنایت فرمائے جو لوگ جرم کرتے ہیں ان کو خدا کے ہاں ذلّت اور عذابِ شدید ہوگا اس لیے کہ مکّاریاں کرتے تھے');
INSERT INTO "jalandhari" VALUES(6,125,'6:125','تو جس شخص کو خدا چاہتا ہے کہ ہدایت بخشے اس کا سینہ اسلام کے لیے کھول دیتا ہے اور جسے چاہتا ہے کہ گمراہ کرے اس کا سینہ تنگ اور گھٹا ہوا کر دیتا ہے گویا وہ آسمان پر چڑھ رہا ہے اس طرح خدا ان لوگوں پر جو ایمان نہیں لاتے عذاب بھیجتا ہے');
INSERT INTO "jalandhari" VALUES(6,126,'6:126','اور یہی تمہارے پروردگار کا سیدھا رستہ ہے جو لوگ غور کرنے والے ہیں ان کے لیے ہم نے اپنی آیتیں کھول کھول کر بیان کر دی ہیں');
INSERT INTO "jalandhari" VALUES(6,127,'6:127','ان کے لیے ان کے اعمال کے صلے میں پروردگار کے ہاں سلامتی کا گھر ہے اور وہی ان کا دوستدار ہے');
INSERT INTO "jalandhari" VALUES(6,128,'6:128','اور جس دن وہ سب (جنّ وانس) کو جمع کرے گا (اور فرمائے گا کہ) اے گروہ جنّات تم نے انسانوں سے بہت (فائدے) حاصل کئے تو جو انسانوں میں ان کے دوستدار ہوں گے وہ کہیں گے کہ پروردگار ہم ایک دوسرے سے فائدہ اٹھاتے رہے اور (آخر) اس وقت کو پہنچ گئے جو تو نے ہمارے لیے مقرر کیا تھا خدا فرمائے گا (اب) تمہارا ٹھکانہ دوزخ ہے ہمیشہ اس میں (جلتے) رہو گے مگر جو خدا چاہے بےشک تمہارا پروردگار دانا اور خبردار ہے');
INSERT INTO "jalandhari" VALUES(6,129,'6:129','اور اسی طرح ہم ظالموں کو ان کے اعمال کے سبب جو وہ کرتے تھے ایک دوسرے پر مسلط کر دیتے ہیں');
INSERT INTO "jalandhari" VALUES(6,130,'6:130','اے جنّوں اور انسانوں کی جماعت کیا تمہارے پاس تم ہی میں سے پیغمبر نہیں آتے رہے جو میری آیتیں تم کو پڑھ پڑھ کر سناتے اور اس دن کے سامنے آموجود ہونے سے ڈراتے تھے وہ کہیں گے کہ (پروردگار) ہمیں اپنے گناہوں کا اقرار ہے ان لوگوں کو دنیاکی زندگی نے دھوکے میں ڈال رکھا تھا اور (اب) خود اپنے اوپر گواہی دی کہ کفر کرتے تھے');
INSERT INTO "jalandhari" VALUES(6,131,'6:131','(اے محمدصلى الله عليه وسلم!) یہ (جو پیغمبر آتے رہے اور کتابیں نازل ہوتی رہیں تو) اس لیے کہ تمہارا پروردگار ایسا نہیں کہ بستیوں کو ظلم سے ہلاک کر دے اور وہاں کے رہنے والوں کو (کچھ بھی) خبر نہ ہو');
INSERT INTO "jalandhari" VALUES(6,132,'6:132','اور سب لوگوں کے بلحاظ اعمال درجے (مقرر) ہیں اور جو کام یہ لوگ کرتے ہیں خدا ان سے بے خبر نہیں');
INSERT INTO "jalandhari" VALUES(6,133,'6:133','اور تمہارا پروردگار بےپروا (اور) صاحب رحمت ہے اگر چاہے (تو اے بندوں) تمہیں نابود کر دے اور تمہارے بعد جن لوگوں کو چاہے تمہارا جانشین بنا دے جیسا تم کو بھی دوسرے لوگوں کی نسل سے پیدا کیا ہے');
INSERT INTO "jalandhari" VALUES(6,134,'6:134','کچھ شک نہیں کہ جو وعدہ تم سے کیا جاتا ہے وہ (وقوع میں) آنے والا ہے اور تم (خدا کو) مغلوب نہیں کر سکتے');
INSERT INTO "jalandhari" VALUES(6,135,'6:135','کہہ دو کہ لوگو تم اپنی جگہ عمل کئے جاؤ میں (اپنی جگہ) عمل کئے جاتا ہوں عنقریب تم کو معلوم ہو جائے گا کہ آخرت میں (بہشت) کس کا گھر ہوگا کچھ شک نہیں کہ مشرک نجات نہیں پانے کے');
INSERT INTO "jalandhari" VALUES(6,136,'6:136','اور (یہ لوگ) خدا ہی کی پیدا کی ہوئی چیزوں یعنی کھیتی اور چوپایوں میں خدا کا بھی ایک حصہ مقرر کرتے ہیں اور اپنے خیال (باطل) سے کہتے ہیں کہ یہ (حصہ) تو خدا کا اور یہ ہمارے شریکوں (یعنی بتوں) کا تو جو حصہ ان کے شریکوں کا ہوتا ہے وہ تو خدا کی طرف نہیں جا سکتا اور جو حصہ خدا کا ہوتا ہے وہ ان کے شریکوں کی طرف جا سکتا ہے یہ کیسا برا انصاف ہے');
INSERT INTO "jalandhari" VALUES(6,137,'6:137','اسی طرح بہت سے مشرکوں کو ان کے شریکوں نے ان کے بچوں کو جان سے مار ڈالنا اچھا کر دکھایا ہے تاکہ انہیں ہلاکت میں ڈال دیں اور ان کے دین کو ان پر خلط ملط کر دیں اور اگر خدا چاہتا تو وہ ایسا نہ کرتے تو ان کو چھوڑ دو کہ وہ جانیں اور ان کا جھوٹ');
INSERT INTO "jalandhari" VALUES(6,138,'6:138','اور اپنے خیال سے یہ بھی کہتے ہیں کہ یہ چارپائے اور کھیتی منع ہے اسے اس شخص کے سوا جسے ہم چاہیں کوئی نہ کھائے اور (بعض) چارپائے ایسے ہیں کہ ان کی پیٹ پر چڑھنا منع کر دیا گیا ہے اور بعض مویشی ایسے ہیں جن پر (ذبح کرتے وقت) خدا کا نام نہیں لیتے سب خدا پر جھوٹ ہے وہ عنقریب ان کو ان کے جھوٹ کا بدلہ دے گا');
INSERT INTO "jalandhari" VALUES(6,139,'6:139','اور یہ بھی کہتے ہیں کہ جو بچہ ان چارپایوں کے پیٹ میں ہے وہ خاص ہمارے مردوں کے لئے ہے اور ہماری عورتوں کو (اس کا کھانا) حرام ہے اور اگر وہ بچہ مرا ہوا ہو تو سب اس میں شریک ہیں (یعنی اسے مرد اور عورتیں سب کھائیں) عنقریب خدا ان کو ان کے ڈھکوسلوں کی سزا دے گا بےشک وہ حکمت والا خبردار ہے');
INSERT INTO "jalandhari" VALUES(6,140,'6:140','جن لوگوں نے اپنی اولاد کو بیوقوفی سے بے سمجھی سے قتل کیا اور خدا پر افترا کر کے اس کی عطا فرمائی کی ہوئی روزی کو حرام ٹہرایا وہ گھاٹے میں پڑ گئے وہ بےشبہ گمراہ ہیں اور ہدایت یافتہ نہیں ہیں');
INSERT INTO "jalandhari" VALUES(6,141,'6:141','اور خدا ہی تو ہے جس نے باغ پیدا کئے چھتریوں پر چڑھائے ہوئے بھی اور جو چھتریوں پر نہیں چڑھائے ہوئے وہ بھی اور کھجور اور کھیتی جن کے طرح طرح کے پھل ہوتے ہیں اور زیتون اور انار جو (بعض باتوں میں) ایک دوسرے سے ملتے ہیں جب یہ چیزیں پھلیں تو ان کے پھل کھاؤ اور جس دن (پھل توڑو اور کھیتی) کاٹو تو خدا کا حق بھی اس میں سے ادا کرو اور بےجا نہ اڑاؤ کہ خدا بیجا اڑانے والوں کو دوست نہیں رکھتا');
INSERT INTO "jalandhari" VALUES(6,142,'6:142','اور چارپایوں میں بوجھ اٹھانے والے (یعنی بڑے بڑے) بھی پیدا کئے اور زمین سے لگے ہوئے (یعنی چھوٹے چھوٹے) بھی (پس) خدا کا دیا ہوا رزق کھاؤ اور شیطان کے قدموں پر نہ چلو وہ تمہارا صریح دشمن ہے');
INSERT INTO "jalandhari" VALUES(6,143,'6:143','(یہ بڑے چھوٹے چارپائے) آٹھ قسم کے (ہیں) دو (دو) بھیڑوں میں سے اور دو (دو) بکریوں میں سے (یعنی ایک ایک نر اور اور ایک ایک مادہ) (اے پیغمبر ان سے) پوچھو کہ (خدا نے) دونوں (کے) نروں کو حرام کیا ہے یا دونوں (کی) مادنیوں کو یا جو بچہ مادنیوں کے پیٹ میں لپٹ رہا ہو اسے اگر سچے ہو تو مجھے سند سے بتاؤ');
INSERT INTO "jalandhari" VALUES(6,144,'6:144','اور دو (دو) اونٹوں میں سے اور دو (دو) گایوں میں سے (ان کے بارے میں بھی ان سے) پوچھو کہ (خدا نے) دونوں (کے) نروں کو حرام کیا ہے یا دونوں (کی) مادنیوں کو یا جو بچہ مادنیوں کے پیٹ میں لپٹ رہا ہو اس کو بھلا جس وقت خدا نے تم کو اس کا حکم دیا تھا تم اس وقت موجود تھے؟ تو اس شخص سے زیادہ کون ظالم ہے جو خدا پر جھوٹ افتراء کرے تاکہ اِز راہ بے دانشی لوگوں کو گمراہ کرے کچھ شک نہیں کہ خدا ظالم لوگوں کو ہدایت نہیں دیتا');
INSERT INTO "jalandhari" VALUES(6,145,'6:145','کہو کہ جو احکام مجھ پر نازل ہوئے ہیں ان میں کوئی چیز جسے کھانے والا کھائے حرام نہیں پاتا بجز اس کے کہ وہ مرا ہوا جانور یا بہتا لہو یا سور کا گوشت کہ یہ سب ناپاک ہیں یا کوئی گناہ کی چیز ہو کہ اس پر خدا کے سوا کسی اور کا نام لیا گیا ہو اور اگر کوئی مجبور ہو جائے لیکن نہ تو نافرمانی کرے اور نہ حد سے باہر نکل جائے تو تمہارا پروردگار بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(6,146,'6:146','اور یہودیوں پر ہم نے سب ناخن والے جانور حرام کر دئیے تھے اور گایوں اور بکریوں سے ان کی چربی حرام کر دی تھی سوا اس کے جو ان کی پیٹھ پر لگی ہو یا اوجھڑی میں ہو یا ہڈی میں ملی ہو یہ سزا ہم نے ان کو ان کی شرارت کے سبب دی تھی اور ہم تو سچ کہنے والے ہیں');
INSERT INTO "jalandhari" VALUES(6,147,'6:147','اور اگر یوں لوگ تمہاری تکذیب کریں تو کہہ دو تمہارا پروردگار صاحب رحمت وسیع ہے مگر اس کا عذاب گنہ گاروں لوگوں سے نہیں ٹلے گا');
INSERT INTO "jalandhari" VALUES(6,148,'6:148','جو لوگ شرک کرتے ہیں وہ کہیں گے کہ اگر خدا چاہتا تو ہم شرک نہ کرتے اور نہ ہمارے باپ دادا (شرک کرتے) اور نہ ہم کسی چیز کو حرام ٹھہراتے اسی طرح ان لوگوں نے تکذیب کی تھی جو ان سے پہلے تھے یہاں تک کہ ہمارے عذاب کا مزہ چکھ کر رہے کہہ دو کیا تمہارے پاس کوئی سند ہے (اگر ہے) تو اسے ہمارے سامنے نکالو تم محض خیال کے پیچھے چلتے اور اٹکل کی تیر چلاتے ہو');
INSERT INTO "jalandhari" VALUES(6,149,'6:149','کہہ دو کہ خدا ہی کی حجت غالب ہے اگر وہ چاہتا تو تم سب کو ہدایت دے دیتا');
INSERT INTO "jalandhari" VALUES(6,150,'6:150','کہو کہ اپنے گواہوں کو لاؤ جو بتائیں کہ خدا نے یہ چیزیں حرام کی ہیں پھر اگر وہ (آ کر) گواہی دیں تو تم ان کے ساتھ گواہی نہ دینا اور نہ ان لوگوں کی خواہشوں کی پیروی کرنا جو ہماری آیتوں کو جھٹلاتے ہیں اور آخرت پر ایمان نہیں لاتے اور (بتوں کو) اپنے پروردگار کے برابر ٹھہراتے ہیں');
INSERT INTO "jalandhari" VALUES(6,151,'6:151','کہہ کہ (لوگو) آؤ میں تمہیں وہ چیزیں پڑھ کر سناؤں جو تمہارے پروردگار نے تم پر حرام کر دی ہیں (ان کی نسبت اس نے اس طرح ارشاد فرمایا ہے) کہ کسی چیز کو خدا کا شریک نہ بنانا اور ماں باپ (سے بدسلوکی نہ کرنا بلکہ) سلوک کرتے رہنا اور ناداری (کے اندیشے) سے اپنی اولاد کو قتل نہ کرنا کیونکہ تم کو اور ان کو ہم ہی رزق دیتے ہیں اور بےحیائی کے کام ظاہر ہوں یا پوشیدہ ان کے پاس نہ پھٹکنا اور کسی جان (والے) کو جس کے قتل کو خدا نے حرام کر دیا ہے قتل نہ کرنا مگر جائز طور پر (یعنی جس کا شریعت حکم دے) ان باتوں کا وہ تمہیں ارشاد فرماتا ہے تاکہ تم سمجھو');
INSERT INTO "jalandhari" VALUES(6,152,'6:152','اور یتیم کے مال کے پاس بھی نہ جانا مگر ایسے طریق سے کہ بہت ہی پسندیدہ ہو یہاں تک کہ وہ جوانی کو پہنچ جائے اور ناپ تول انصاف کے ساتھ پوری پوری کیا کرو ہم کسی کو تکلیف نہیں دیتے مگر اس کی طاقت کے مطابق اور جب (کسی کی نسبت) کوئی بات کہو تو انصاف سے کہو گو وہ (تمہارا) رشتہ دار ہی ہو اور خدا کے عہد کو پورا کرو ان باتوں کا خدا تمہیں حکم دیتا ہے تاکہ تم نصحیت کرو');
INSERT INTO "jalandhari" VALUES(6,153,'6:153','اور یہ کہ میرا سیدھا رستہ یہی ہے تو تم اسی پر چلنا اور اور رستوں پر نہ چلنا کہ (ان پر چل کر) خدا کے رستے سے الگ ہو جاؤ گے ان باتوں کا خدا تمہیں حکم دیتا ہے تاکہ تم پرہیزگار بنو');
INSERT INTO "jalandhari" VALUES(6,154,'6:154','(ہاں) پھر (سن لو کہ) ہم نے موسیؑ کو کتاب عنایت کی تھی تاکہ ان لوگوں پر جو نیکوکار ہیں نعمت پوری کر دیں اور (اس میں) ہر چیز کا بیان (ہے) اور ہدایت (ہے) اور رحمت ہے تاکہ (ان کی امت کے) لوگ اپنے پروردگار کے رُوبرو حاضر ہونے کا یقین کریں');
INSERT INTO "jalandhari" VALUES(6,155,'6:155','اور (اے کفر کرنے والوں) یہ کتاب بھی ہمیں نے اتاری ہے برکت والی تو اس کی پیروی کرو اور (خدا سے) ڈرو تاکہ تم پر مہربانی کی جائے');
INSERT INTO "jalandhari" VALUES(6,156,'6:156','(اور اس لیے اتاری ہے) کہ (تم یوں نہ) کہو کہ ہم سے پہلے دو ہی گروہوں پر کتابیں اتری تھیں اور ہم ان کے پڑھنے سے (معذور اور) بےخبر تھے');
INSERT INTO "jalandhari" VALUES(6,157,'6:157','یا (یہ نہ) کہو کہ اگر ہم پر بھی کتاب نازل ہوتی تو ہم ان لوگوں کی نسبت کہیں سیدھے رستے پر ہوتے سو تمہارے پاس تمہارے پروردگار کی طرف سے دلیل اور ہدایت اور رحمت آ گئی ہے تو اس سے بڑھ کر ظالم کون ہوگا جو خدا کی آیتوں کی تکذیب کرے اور ان سے (لوگوں کو) پھیرے جو لوگ ہماری آیتوں سے پھیرتے ہیں اس پھیرنے کے سبب ہم ان کو برے عذاب کی سزا دیں گے');
INSERT INTO "jalandhari" VALUES(6,158,'6:158','یہ اس کے سوا اور کس بات کے منتظر ہیں کہ ان کے پاس فرشتے آئیں یا خود تمہارا پروردگار آئے یا تمہارے پروردگار کی کچھ نشانیاں آئیں (مگر) جس روز تمہارے پروردگار کی کچھ نشانیاں آ جائیں گی تو جو شخص پہلے ایمان نہیں لایا ہوگا اس وقت اسے ایمان لانا کچھ فائدہ نہیں دے گا یا اپنے ایمان (کی حالت) میں نیک عمل نہیں کئے ہوں گے (تو گناہوں سے توبہ کرنا مفید نہ ہوگا اے پیغمبر ان سے) کہہ دو کہ تم بھی انتظار کرو ہم بھی انتظار کرتے ہیں');
INSERT INTO "jalandhari" VALUES(6,159,'6:159','جن لوگوں نے اپنے دین میں (بہت سے) رستے نکالے اور کئی کئی فرقے ہو گئے ان سے تم کو کچھ کام نہیں ان کا کام خدا کے حوالے پھر جو کچھ وہ کرتے رہے ہیں وہ ان کو (سب) بتائے گا');
INSERT INTO "jalandhari" VALUES(6,160,'6:160','اور جو کوئی (خدا کے حضور) نیکی لے کر آئے گا اس کو ویسی دس نیکیاں ملیں گی اور جو برائی لائے گا اسے سزا ویسے ہی ملے گی اور ان پر ظلم نہیں کیا جائے گا');
INSERT INTO "jalandhari" VALUES(6,161,'6:161','کہہ دو کہ مجھے میرے پروردگار نے سیدھا رستہ دکھا دیا ہے (یعنی دین صحیح) مذہب ابراہیم کا جو ایک (خدا) ہی کی طرف کے تھے اور مشرکوں میں سے نہ تھے');
INSERT INTO "jalandhari" VALUES(6,162,'6:162','(یہ بھی) کہہ دو کہ میری نماز اور میری عبادت اور میرا جینا اور میرا مرنا سب خدائے رب العالمین ہی کے لیے ہے');
INSERT INTO "jalandhari" VALUES(6,163,'6:163','جس کا کوئی شریک نہیں اور مجھ کو اسی بات کا حکم ملا ہے اور میں سب سے اول فرمانبردار ہوں');
INSERT INTO "jalandhari" VALUES(6,164,'6:164','کہو کیا میں خدا کے سوا اور پروردگار تلاش کروں اور وہی تو ہر چیز کا مالک ہے اور جو کوئی (برا) کام کرتا ہے تو اس کا ضرر اسی کو ہوتا ہے اور کوئی شخص کسی (کے گناہ) کا بوجھ نہیں اٹھائے گا پھر تم سب کو اپنے پروردگار کی طرف لوٹ کا جانا ہے تو جن جن باتوں میں تم اختلاف کیا کرتے تھے وہ تم کو بتائے گا');
INSERT INTO "jalandhari" VALUES(6,165,'6:165','اور وہی تو ہے جس نے زمین میں تم کو اپنا نائب بنایا اور ایک کے دوسرے پر درجے بلند کئے تاکہ جو کچھ اس نے تمہیں بخشا ہے اس میں تمہاری آزمائش ہے بےشک تمہارا پروردگار جلد عذاب دینے والا ہے اور بےشک وہ بخشنے والا مہربان بھی ہے');
INSERT INTO "jalandhari" VALUES(7,1,'7:1','المص');
INSERT INTO "jalandhari" VALUES(7,2,'7:2','(اے محمدصلى الله عليه وسلم) یہ کتاب (جو) تم پر نازل ہوئی ہے۔ اس سے تمہیں تنگ دل نہیں ہونا چاہیئے، (یہ نازل) اس لیے (ہوئی ہے) کہ تم اس کے ذریعے سے (لوگوں) کو ڈر سناؤ اور (یہ) ایمان والوں کے لیے نصیحت ہے');
INSERT INTO "jalandhari" VALUES(7,3,'7:3','(لوگو) جو (کتاب) تم پر تمہارے پروردگار کے ہاں نازل ہوئی ہے اس کی پیروی کرو اور اس کے سوا اور رفیقوں کی پیروی نہ کرو (اور) تم کم ہی نصیحت قبول کرتے ہو');
INSERT INTO "jalandhari" VALUES(7,4,'7:4','اور کتنی ہی بستیاں ہیں کہ ہم نے تباہ کر ڈالیں جن پر ہمارا عذاب (یا تو رات کو) آتا تھا جبکہ وہ سوتے تھے یا (دن کو) جب وہ قیلولہ (یعنی دوپہر کو آرام) کرتے تھے');
INSERT INTO "jalandhari" VALUES(7,5,'7:5','تو جس وقت ان پر عذاب آتا تھا ان کے منہ سے یہی نکلتا تھا کہ (ہائے) ہم (ہائے) ہم (اپنے اوپر) ظلم کرتے رہے');
INSERT INTO "jalandhari" VALUES(7,6,'7:6','تو جن لوگوں کی طرف پیغمبر بھیجے گئے ہم ان سے بھی پرسش کریں گے اور پیغمبروں سے بھی پوچھیں گے');
INSERT INTO "jalandhari" VALUES(7,7,'7:7','پھر اپنے علم سے ان کے حالات بیان کریں گے اور ہم کہیں غائب تو نہیں تھے');
INSERT INTO "jalandhari" VALUES(7,8,'7:8','اور اس روز (اعمال کا) تلنا برحق ہے تو جن لوگوں کے (عملوں کے) وزن بھاری ہوں گے وہ تو نجات پانے والے ہیں');
INSERT INTO "jalandhari" VALUES(7,9,'7:9','اور جن کے وزن ہلکے ہوں گے تو یہی لوگ ہیں جنہوں نے اپنے تئیں خسارے میں ڈالا اس لیے کہ ہماری آیتوں کے بارے میں بےانصافی کرتے تھے');
INSERT INTO "jalandhari" VALUES(7,10,'7:10','اور ہم ہی نے زمین میں تمہارا ٹھکانہ بنایا اور اس میں تمہارے لیے سامان معشیت پیدا کئے۔ (مگر) تم کم ہی شکر کرتے ہو');
INSERT INTO "jalandhari" VALUES(7,11,'7:11','اور ہم ہی نے تم کو (ابتدا میں مٹی سے) پیدا کیا پھر تمہاری صورت شکل بنائی پھر فرشتوں کو حکم دیا آدم کے آگے سجدہ کرو تو (سب نے) سجدہ کیا لیکن ابلیس کہ وہ سجدہ کرنے والوں میں (شامل) نہ ہوا');
INSERT INTO "jalandhari" VALUES(7,12,'7:12','(خدا نے) فرمایا جب میں نے تجھ کو حکم دیا تو کس چیز نے تجھے سجدہ کرنے سے باز رکھا۔ اس نے کہا کہ میں اس سے افضل ہوں۔ مجھے تو نے آگ سے پیدا کیا ہے اور اسے مٹی سے بنایا ہے');
INSERT INTO "jalandhari" VALUES(7,13,'7:13','فرمایا تو (بہشت سے) اتر جا تجھے شایاں نہیں کہ یہاں غرور کرے پس نکل جا۔ تو ذلیل ہے');
INSERT INTO "jalandhari" VALUES(7,14,'7:14','اس نے کہا کہ مجھے اس دن تک مہلت عطا فرما جس دن لوگ (قبروں سے) اٹھائے جائیں گے');
INSERT INTO "jalandhari" VALUES(7,15,'7:15','فرمایا (اچھا) تجھ کو مہلت دی جاتی ہے');
INSERT INTO "jalandhari" VALUES(7,16,'7:16','(پھر) شیطان نے کہا مجھے تو تُو نے ملعون کیا ہی ہے میں بھی تیرے سیدھے رستے پر ان (کو گمراہ کرنے) کے لیے بیٹھوں گا');
INSERT INTO "jalandhari" VALUES(7,17,'7:17','پھر ان کے آگے سے اور پیچھے سے دائیں سے اور بائیں سے (غرض ہر طرف سے) آؤں گا (اور ان کی راہ ماروں گا) اور تو ان میں اکثر کو شکر گزار نہیں پائے گا');
INSERT INTO "jalandhari" VALUES(7,18,'7:18','(خدا نے) فرمایا، نکل جا۔ یہاں سے پاجی۔ مردود جو لوگ ان میں سے تیری پیروی کریں گے میں (ان کو اور تجھ کو جہنم میں ڈال کر) تم سب سے جہنم کو بھر دوں گا');
INSERT INTO "jalandhari" VALUES(7,19,'7:19','اور ہم نے آدم (سے کہا کہ) تم اور تمہاری بیوی بہشت میں رہو سہو اور جہاں سے چاہو (اور جو چاہو) نوش جان کرو مگر اس درخت کے پاس نہ جاؤ ورنہ گنہگار ہو جاؤ گے');
INSERT INTO "jalandhari" VALUES(7,20,'7:20','تو شیطان دونوں کو بہکانے لگا تاکہ ان کی ستر کی چیزیں جو ان سے پوشیدہ تھیں کھول دے اور کہنے لگا کہ تم کو تمہارے پروردگار نے اس درخت سے صرف اس لیے منع کیا ہے کہ کہ تم فرشتے نہ بن جاؤ یا ہمیشہ جیتے نہ رہو');
INSERT INTO "jalandhari" VALUES(7,21,'7:21','اور ان سے قسم کھا کر کہا میں تو تمہارا خیر خواہ ہوں');
INSERT INTO "jalandhari" VALUES(7,22,'7:22','غرض (مردود نے) دھوکہ دے کر ان کو (معصیت کی طرف) کھینچ ہی لیا جب انہوں نے اس درخت (کے پھل) کو کھا لیا تو ان کی ستر کی چیزیں کھل گئیں اور وہ بہشت کے (درختوں کے) پتے توڑ توڑ کر اپنے اوپر چپکانے لگے اور (ستر چھپانے لگے) تب ان کے پروردگار نے ان کو پکارا کہ کیا میں نے تم کو اس درخت (کے پاس جانے) سے منع نہیں کیا تھا اور جتا نہیں دیا تھا کہ شیطان تمہارا کھلم کھلا دشمن ہے');
INSERT INTO "jalandhari" VALUES(7,23,'7:23','دونوں عرض کرنے لگے کہ پروردگار ہم نے اپنی جانوں پر ظلم کیا اور اگر تو ہمیں نہیں بخشے گا اور ہم پر رحم نہیں کرے گا تو ہم تباہ ہو جائیں گے');
INSERT INTO "jalandhari" VALUES(7,24,'7:24','(خدا نے) فرمایا (تم سب بہشت سے) اتر جاؤ (اب سے) تم ایک دوسرے کے دشمن ہو اور تمہارے لیے ایک وقت (خاص) تک زمین پر ٹھکانہ اور (زندگی کا) سامان (کر دیا گیا) ہے');
INSERT INTO "jalandhari" VALUES(7,25,'7:25','(یعنی) فرمایا کہ اسی میں تمہارا جینا ہوگا اور اسی میں مرنا اور اسی میں سے (قیامت کو زندہ کر کے) نکالے جاؤ گے');
INSERT INTO "jalandhari" VALUES(7,26,'7:26','اے نبی آدم ہم نے تم پر پوشاک اتاری کہ تمہارا ستر ڈھانکے اور (تمہارے بدن کو) زینت (دے) اور (جو) پرہیزگاری کا لباس (ہے) وہ سب سے اچھا ہے۔ یہ خدا کی نشانیاں ہیں تاکہ لوگ نصحیت پکڑ یں');
INSERT INTO "jalandhari" VALUES(7,27,'7:27','اے نبی آدم (دیکھنا کہیں) شیطان تمہیں بہکا نہ دے جس طرح تمہارے ماں باپ کو (بہکا کر) بہشت سے نکلوا دیا اور ان سے ان کے کپڑے اتروا دیئے تاکہ ان کے ستر ان کو کھول کر دکھا دے۔ وہ اور اس کے بھائی تم کو ایسی جگہ سے دیکھتے رہے ہیں جہاں سے تم ان کو نہیں دیکھ سکتے ہم نے شیطانوں کو انہیں لوگوں کا رفیق کار بنایا ہے جو ایمان نہیں رکھتے');
INSERT INTO "jalandhari" VALUES(7,28,'7:28','اور جب کوئی بے حیائی کا کام کرتے ہیں تو کہتے ہیں کہ ہم نے اپنے بزرگوں کو اسی طرح کرتے دیکھا ہے اور خدا نے بھی ہم کو یہی حکم دیا ہے۔ کہہ دو خدا بےحیائی کے کام کرنے کا ہرگز حکم نہیں دیتا۔ بھلا تم خدا کی نسبت ایسی بات کیوں کہتے ہو جس کا تمہیں علم نہیں');
INSERT INTO "jalandhari" VALUES(7,29,'7:29','کہہ دو کہ میرے پروردگار نے تو انصاف کرنے کا حکم دیا ہے۔ اور یہ کہ ہر نماز کے وقت سیدھا (قبلے کی طرف) رخ کیا کرو اور خاص اسی کی عبادت کرو اور اسی کو پکارو۔ اس نے جس طرح تم کو ابتداء میں پیدا کیا تھا اسی طرح تم پھر پیدا ہوگے');
INSERT INTO "jalandhari" VALUES(7,30,'7:30','ایک فریق کو تو اس نے ہدایت دی اور ایک فریق پر گمراہی ثابت ہوچکی۔ ان لوگوں نے خدا کو چھوڑ کر شیطانوں کو رفیق بنا لیا اور سمجھتے (یہ) ہیں کہ ہدایت یاب ہیں');
INSERT INTO "jalandhari" VALUES(7,31,'7:31','اے نبی آدم! ہر نماز کے وقت اپنے تئیں مزّین کیا کرو اور کھاؤ اور پیؤ اور بےجا نہ اڑاؤ کہ خدا بےجا اڑانے والوں کو دوست نہیں رکھتا');
INSERT INTO "jalandhari" VALUES(7,32,'7:32','پوچھو تو کہ جو زینت (وآرائش) اور کھانے (پینے) کی پاکیزہ چیزیں خدا نے اپنے بندوں کے لیے پیدا کی ہیں ان کو حرام کس نے کیا ہے؟ کہہ دو کہ یہ چیزیں دنیا کی زندگی میں ایمان والوں کے لیے ہیں اور قیامت کے دن خاص ان ہی کا حصہ ہوں گی۔ اسی طرح خدا اپنی آیتیں سمجھنے والوں کے لیے کھول کھول کر بیان فرماتا ہے');
INSERT INTO "jalandhari" VALUES(7,33,'7:33','کہہ دو کہ میرے پروردگار نے تو بےحیائی کی باتوں کو ظاہر ہوں یا پوشیدہ اور گناہ کو اور ناحق زیادتی کرنے کو حرام کیا ہے۔ اور اس کو بھی کہ تم کسی کو خدا کا شریک بناؤ جس کی اس نے کوئی سند نازل نہیں کی اور اس کو بھی کہ خدا کے بارے میں ایسی باتیں کہو جن کا تمہیں کچھ علم نہیں');
INSERT INTO "jalandhari" VALUES(7,34,'7:34','اور ہر ایک فرقے کے لیے (موت کا) ایک وقت مقرر ہے۔ جب وہ آ جاتا ہے تو نہ تو ایک گھڑی دیر کرسکتے ہیں نہ جلدی');
INSERT INTO "jalandhari" VALUES(7,35,'7:35','اے نبی آدم! (ہم تم کو یہ نصیحت ہمیشہ کرتے رہے ہیں کہ) جب ہمارے پیغمبر تمہارے پاس آیا کریں اور ہماری آیتیں تم کو سنایا کریں (تو ان پر ایمان لایا کرو) کہ جو شخص (ان پر ایمان لا کر خدا سے) ڈرتا رہے گا اور اپنی حالت درست رکھے گا تو ایسے لوگوں کو نہ کچھ خوف ہوگا اور نہ وہ غمناک ہوں گے');
INSERT INTO "jalandhari" VALUES(7,36,'7:36','اور جنہوں نے ہماری آیتوں کو جھٹلایا اور ان سے سرتابی کی وہی دوزخی ہیں کہ ہمیشہ اس میں (جلتے) رہیں گے');
INSERT INTO "jalandhari" VALUES(7,37,'7:37','تو اس سے زیادہ ظالم کون ہے جو خدا پر جھوٹ باندھے یا اس کی آیتوں کو جھٹلائے۔ ان کو ان کے نصیب کا لکھا ملتا ہی رہے گا یہاں تک کہ جب ان کے پاس ہمارے بھیجے ہوئے (فرشتے) جان نکالنے آئیں گے تو کہیں گے کہ جن کو تم خدا کے سوا پکارا کرتے تھے وہ (اب) کہاں ہیں؟ وہ کہیں گے (معلوم نہیں) کہ وہ ہم سے (کہاں) غائب ہوگئے اور اقرار کریں گے کہ بےشک وہ کافر تھے');
INSERT INTO "jalandhari" VALUES(7,38,'7:38','تو خدا فرمائے گا کہ جنّوں اور انسانوں کی جو جماعتیں تم سے پہلے ہو گزری ہیں ان کے ساتھ تم بھی داخل جہنم ہو جاؤ۔ جب ایک جماعت (وہاں) جا داخل ہو گئی تو اپنی (مذہبی) بہن (یعنی اپنے جیسی دوسری جماعت) پر لعنت کرے گی۔ یہاں تک کہ جب سب اس میں داخل ہو جائیں گے تو پچھلی جماعت پہلی کی نسبت کہے گی کہ اے پروردگار! ان ہی لوگوں نے ہم کو گمراہ کیا تھا تو ان کو آتش جہنم کا دگنا عذاب دے۔ خدا فرمائے گا کہ (تم) سب کو دگنا (عذاب دیا جائے گا) مگر تم نہیں جانتے');
INSERT INTO "jalandhari" VALUES(7,39,'7:39','اور پہلی جماعت پچھلی جماعت سے کہے گی کہ تم کو ہم پر کچھ بھی فضیلت نہ ہوئی تو جو (عمل) تم کیا کرتے تھے اس کے بدلے میں عذاب کے مزے چکھو');
INSERT INTO "jalandhari" VALUES(7,40,'7:40','جن لوگوں نے ہماری آیتوں کو جھٹلایا اور ان سے سرتابی کی۔ ان کے لیے نہ آسمان کے دروازے کھولے جائیں گے اور نہ وہ بہشت میں داخل ہوں گے۔ یہاں تک کہ اونٹ سوئی کے ناکے میں سے نہ نکل جائے اور گنہگاروں کو ہم ایسی ہی سزا دیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(7,41,'7:41','ایسے لوگوں کے لیے (نیچے) بچھونا بھی (آتش) جہنم کا ہوگا اور اوپر سے اوڑھنا بھی (اسی کا) اور ظالموں کو ہم ایسی ہی سزا دیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(7,42,'7:42','اور جو لوگ ایمان لائے اور عمل نیک کرتے رہے اور ہم (عملوں کے لیے) کسی شخص کو اس کی طاقت سے زیادہ تکلیف نہیں دیتے۔ ایسے ہی لوگ اہل بہشت ہیں (کہ) اس میں ہمیشہ رہیں گے');
INSERT INTO "jalandhari" VALUES(7,43,'7:43','اور جو کینے ان کے دلوں میں ہوں گے ہم سب نکال ڈالیں گے۔ ان کے محلوں کے نیچے نہریں بہہ رہی ہوں گی اور کہیں گے کہ خدا کا شکر ہے جس نے ہم کو یہاں کا راستہ دکھایا اور اگر خدا ہم کو رستہ نہ دکھاتا تو ہم رستہ نہ پا سکتے۔ بےشک ہمارا پروردگار کے رسول حق بات لے کر آئے تھے اور (اس روز) منادی کر دی جائے گی کہ تم ان اعمال کے صلے میں جو دنیا میں کرتے تھے اس بہشت کے وارث بنا دیئے گئے ہو');
INSERT INTO "jalandhari" VALUES(7,44,'7:44','اور اہل بہشت دوزخیوں سے پکار کر کہیں گے کہ جو وعدہ ہمارے پروردگار نے ہم سے کیا تھا ہم نے تو اسے سچا پالیا۔ بھلا جو وعدہ تمہارے پروردگار نے تم سے کیا تھا تم نے بھی اسے سچا پایا؟ وہ کہیں گے ہاں تو (اس وقت) ان میں ایک پکارنے والا پکارے گا کہ بےانصافوں پر خدا کی لعنت');
INSERT INTO "jalandhari" VALUES(7,45,'7:45','جو خدا کی راہ سے روکتے اور اس میں کجی ڈھونڈتے اور آخرت سے انکار کرتے تھے');
INSERT INTO "jalandhari" VALUES(7,46,'7:46','ان دونوں (یعنی بہشت اور دوزخ) کے درمیان (اعراف نام) ایک دیوار ہو گی اور اعراف پر کچھ آدمی ہوں گے جو سب کو ان کی صورتوں سے پہچان لیں گے۔ تو وہ اہل بہشت کو پکار کر کہیں گے کہ تم پر سلامتی ہو۔ یہ لوگ بھی بہشت میں داخل تو نہیں ہوں گے مگر امید رکھتے ہوں گے');
INSERT INTO "jalandhari" VALUES(7,47,'7:47','اور جب ان کی نگاہیں پلٹ کر اہل دوزخ کی طرف جائیں گی تو عرض کریں گے کہ اے ہمارے پروردگار ہم کو ظالم لوگوں کے ساتھ شامل نہ کیجیو');
INSERT INTO "jalandhari" VALUES(7,48,'7:48','اور اہل اعراف (کافر) لوگوں کو جنہیں ان کی صورتوں سے شناخت کرتے ہوں گے پکاریں گے اور کہیں گے (کہ آج) نہ تو تمہاری جماعت ہی تمہارے کچھ کام آئی اور نہ تمہارا تکبّر (ہی سودمند ہوا)');
INSERT INTO "jalandhari" VALUES(7,49,'7:49','(پھر مومنوں کی طرف اشارہ کر کے کہیں گے) کیا یہ وہی لوگ ہیں جن کے بارے میں تم قسمیں کھایا کرتے تھے کہ خدا اپنی رحمت سے ان کی دستگیری نہیں کرے گا (تو مومنو) تم بہشت میں داخل ہو جاؤ تمہیں کچھ خوف نہیں اور نہ تم کو کچھ رنج واندوہ ہوگا');
INSERT INTO "jalandhari" VALUES(7,50,'7:50','اور وہ دوزخی بہشتیوں سے (گڑگڑا کر) کہیں گے کہ کسی قدر ہم پر پانی بہاؤ یا جو رزق خدا نے تمہیں عنایت فرمایا ہے ان میں سے (کچھ ہمیں بھی دو) وہ جواب دیں گے کہ خدا نے بہشت کا پانی اور رزق کافروں پر حرام کر دیا ہے');
INSERT INTO "jalandhari" VALUES(7,51,'7:51','جنہوں نے اپنے دین کو تماشا اور کھیل بنا رکھا تھا اور دنیا کی زندگی نے ان کو دھوکے میں ڈال رکھا تھا۔ تو جس طرح یہ لوگ اس دن کے آنے کو بھولے ہوئے اور ہماری آیتوں سے منکر ہو رہے تھے۔ اسی طرح آج ہم بھی انہیں بھلا دیں گے');
INSERT INTO "jalandhari" VALUES(7,52,'7:52','اور ہم نے ان کے پاس کتاب پہنچا دی ہے جس کو علم ودانش کے ساتھ کھول کھول کر بیان کر دیا ہے (اور) وہ مومن لوگوں کے لیے ہدایت اور رحمت ہے');
INSERT INTO "jalandhari" VALUES(7,53,'7:53','کیا یہ لوگ اس کے وعدہٴ عذاب کے منتظر ہیں۔ جس دن وہ وعدہ آجائے گا تو جو لوگ اس کو پہلے سے بھولے ہوئے ہوں گے وہ بول اٹھیں گے کہ بےشک ہمارے پروردگار کے رسول حق لے کر آئے تھے۔ بھلا (آج) ہمارا کوئی سفارشی ہیں کہ ہماری سفارش کریں یا ہم (دنیا میں) پھر لوٹا دیئے جائیں کہ جو عمل (بد) ہم (پہلے) کرتے تھے (وہ نہ کریں بلکہ) ان کے سوا اور (نیک) عمل کریں۔ بےشک ان لوگوں نے اپنا نقصان کیا اور جو کچھ یہ افتراء کیا کرتے تھے ان سے سب جاتا رہا');
INSERT INTO "jalandhari" VALUES(7,54,'7:54','کچھ شک نہیں کہ تمہارا پروردگار خدا ہی ہے جس نے آسمانوں اور زمین کو چھ دن میں پیدا کیا پھر عرش پر جا ٹھہرا۔ وہی رات کو دن کا لباس پہناتا ہے کہ وہ اس کے پیچھے دوڑتا چلا آتا ہے۔ اور اسی نے سورج اور چاند ستاروں کو پیدا کیا سب اس کے حکم کے مطابق کام میں لگے ہوئے ہیں۔ دیکھو سب مخلوق بھی اسی کی ہے اور حکم بھی (اسی کا ہے)۔ یہ خدا رب العالمین بڑی برکت والا ہے');
INSERT INTO "jalandhari" VALUES(7,55,'7:55','(لوگو) اپنے پروردگار سے عاجزی سے اور چپکے چپکے دعائیں مانگا کرو۔ وہ حد سے بڑھنے والوں کو دوست نہیں رکھتا');
INSERT INTO "jalandhari" VALUES(7,56,'7:56','اور ملک میں اصلاح کے بعد خرابی نہ کرنا اور خدا سے خوف کرتے ہوئے اور امید رکھ کر دعائیں مانگتے رہنا۔ کچھ شک نہیں کہ خدا کی رحمت نیکی کرنے والوں سے قریب ہے');
INSERT INTO "jalandhari" VALUES(7,57,'7:57','اور وہی تو ہے جو اپنی رحمت (یعنی مینھ) سے پہلے ہواؤں کو خوشخبری (بنا کر) بھیجتا ہے۔ یہاں تک کہ جب وہ بھاری بھاری بادلوں کو اٹھا لاتی ہے تو ہم اس کو ایک مری ہوئی بستی کی طرف ہانک دیتے ہیں۔ پھر بادل سے مینھ برساتے ہیں۔ پھر مینھ سے ہر طرح کے پھل پیدا کرتے ہیں۔ اسی طرح ہم مردوں کو (زمین سے) زندہ کرکے باہر نکال لیں گے۔ (یہ آیات اس لیے بیان کی جاتی ہیں) تاکہ تم نصیحت پکڑو');
INSERT INTO "jalandhari" VALUES(7,58,'7:58','جو زمین پاکیزہ (ہے) اس میں سے سبزہ بھی پروردگار کے حکم سے (نفیس ہی) نکلتا ہے اور جو خراب ہے اس میں جو کچھ ہے ناقص ہوتا ہے۔ اسی طرح ہم آیتوں کو شکرگزار لوگوں کے لئے پھیر پھیر کر بیان کرتے ہیں');
INSERT INTO "jalandhari" VALUES(7,59,'7:59','ہم نے نوح کو ان کی قوم کی طرف بھیجا تو انہوں نے (ان سے کہا) اے میری برادری کے لوگو خدا کی عبادت کرو اس کے سوا تمہارا کوئی معبود نہیں۔ مجھے تمہارے بارے میں بڑے دن کے عذاب کا (بہت ہی) ڈر ہے');
INSERT INTO "jalandhari" VALUES(7,60,'7:60','تو جو ان کی قوم میں سردار تھے وہ کہنے لگے کہ ہم تمہیں صریح گمراہی میں (مبتلا) دیکھتے ہیں');
INSERT INTO "jalandhari" VALUES(7,61,'7:61','انہوں نے کہا اے قوم مجھ میں کسی طرح کی گمراہی نہیں ہے بلکہ میں پروردگار عالم کا پیغمبر ہوں');
INSERT INTO "jalandhari" VALUES(7,62,'7:62','تمہیں اپنے پروردگار کے پیغام پہنچاتا ہوں اور تمہاری خیرخواہی کرتا ہوں اور مجھ کو خدا کی طرف سے ایسی باتیں معلوم ہیں جن سے تم بےخبر ہو');
INSERT INTO "jalandhari" VALUES(7,63,'7:63','کیا تم کو اس بات سے تعجب ہوا ہے کہ تم میں سے ایک شخص کے ہاتھ تمہارے پروردگار کی طرف سے تمہارے پاس نصیحت آئی تاکہ وہ تم کو ڈرائے اور تاکہ تم پرہیزگار بنو اور تاکہ تم پر رحم کیا جائے');
INSERT INTO "jalandhari" VALUES(7,64,'7:64','مگر ان لوگوں نے ان کی تکذیب کی۔ تو ہم نے نوح کو اور جو ان کے ساتھ کشتی میں سوار تھے ان کو تو بچا لیا اور جن لوگوں نے ہماری آیتوں کو جھٹلایا تھا انہیں غرق کر دیا۔ کچھ شک نہیں کہ وہ اندھے لوگ تھے');
INSERT INTO "jalandhari" VALUES(7,65,'7:65','اور (اسی طرح) قوم عاد کی طرف ان کے بھائی ہود کو بھیجا۔ انہوں نے کہا کہ بھائیو خدا ہی کی عبادت کرو۔ اس کے سوا تمہارا کوئی معبود نہیں۔ کیا تم ڈرتے نہیں؟');
INSERT INTO "jalandhari" VALUES(7,66,'7:66','تو ان کی قوم کے سردار جو کافر تھے کہنے لگے کہ تم ہمیں احمق نظر آتے ہو اور ہم تمہیں جھوٹا خیال کرتے ہیں');
INSERT INTO "jalandhari" VALUES(7,67,'7:67','انہوں نے کہا کہ بھائیو مجھ میں حماقت کی کوئی بات نہیں ہے بلکہ میں رب العالمین کا پیغمبر ہوں');
INSERT INTO "jalandhari" VALUES(7,68,'7:68','میں تمہیں خدا کے پیغام پہنچاتا ہوں اور تمہارا امانت دار خیرخواہ ہوں');
INSERT INTO "jalandhari" VALUES(7,69,'7:69','کیا تم کو اس بات سے تعجب ہوا ہے کہ تم میں سے ایک شخص کے ہاتھ تمہارے پروردگار کی طرف سے تمہارے پاس نصیحت آئی تاکہ وہ تمہیں ڈرائے اور یاد کرو تو کرو جب اس نے تم کو قوم نوح کے بعد سردار بنایا۔ اور تم کو پھیلاؤ زیادہ دیا۔ پس خدا کی نعمتوں کو یاد کرو۔ تاکہ نجات حاصل کرو');
INSERT INTO "jalandhari" VALUES(7,70,'7:70','وہ کہنے لگے کہ تم ہمارے پاس اس لیے آئے ہو کہ ہم اکیلے خدا ہی کی عبادت کریں۔ اور جن کو ہمارے باپ دادا پوجتے چلے آئے ہیں ان کو چھوڑ دیں؟ تو اگر سچے ہو تو جس چیز سے ہمیں ڈراتے ہو اسے لے آؤ');
INSERT INTO "jalandhari" VALUES(7,71,'7:71','ہود نے کہا تمہارے پروردگار کی طرف سے تم پر عذاب اور غضب کا (نازل ہونا) مقرر ہو چکا ہے۔ کیا تم مجھ سے ایسے ناموں کے بارے میں جھگڑتے ہو جو تم نے اور تمہارے باپ دادا نے (اپنی طرف سے) رکھ لئے ہیں۔ جن کی خدا نے کوئی سند نازل نہیں کی۔ تو تم بھی انتظار کرو میں بھی تمہارے ساتھ انتظار کرتا ہوں');
INSERT INTO "jalandhari" VALUES(7,72,'7:72','پھر ہم نے ہود کو اور جو لوگ ان کے ساتھ تھے ان کو نجات بخشی اور جنہوں نے ہماری آیتوں کو جھٹلایا تھا ان کی جڑ کاٹ دی اور وہ ایمان لانے والے تھے ہی نہیں');
INSERT INTO "jalandhari" VALUES(7,73,'7:73','اور قوم ثمود کی طرف ان کے بھائی صالح کو بھیجا۔ (تو) صالح نے کہا کہ اے قوم! خدا ہی کی عبادت کرو اس کے سوا تمہارا کوئی معبود نہیں۔ تمہارے پاس تمہارے پروردگار کی طرف سے ایک معجزہ آ چکا ہے۔ (یعنی) یہی خدا کی اونٹنی تمہارے لیے معجزہ ہے۔ تو اسے (آزاد) چھوڑ دو کہ خدا کی زمین میں چرتی پھرے اور تم اسے بری نیت سے ہاتھ بھی نہ لگانا۔ ورنہ عذابِ الیم میں تمہیں پکڑ لے گا');
INSERT INTO "jalandhari" VALUES(7,74,'7:74','اور یاد تو کرو جب اس نے تم کو قوم عاد کے بعد سردار بنایا اور زمین پر آباد کیا کہ نرم زمین سے (مٹی لے لے کر) محل تعمیر کرتے ہو اور پہاڑوں کو تراش تراش کر گھر بناتے ہو۔ پس خدا کی نعمتوں کو یاد کرو اور زمین میں فساد نہ کرتے پھرو');
INSERT INTO "jalandhari" VALUES(7,75,'7:75','تو ان کی قوم میں سردار لوگ جو غرور رکھتے تھے غریب لوگوں سے جو ان میں سے ایمان لے آئے تھے کہنے لگے بھلا تم یقین کرتے ہو کہ صالح اپنے پروردگار کی طرف بھیجے گئے ہیں؟ انہوں نے کہا ہاں جو چیز دے کر وہ بھیجے گئے ہیں ہم اس پر بلاشبہ ایمان رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(7,76,'7:76','تو (سرداران) مغرور کہنے لگے کہ جس چیز پر تم ایمان لائے ہو ہم تو اس کو نہیں مانتے');
INSERT INTO "jalandhari" VALUES(7,77,'7:77','آخر انہوں نے اونٹی (کی کونچوں) کو کاٹ ڈالا اور اپنے پروردگار کے حکم سے سرکشی کی اور کہنے لگے کہ صالح! جس چیز سے تم ہمیں ڈراتے تھے اگر تم (خدا کے) پیغمبر ہو تو اسے ہم پر لے آؤ');
INSERT INTO "jalandhari" VALUES(7,78,'7:78','تو ان کو بھونچال نے آ پکڑا اور وہ اپنے گھروں میں اوندھے پڑے رہ گئے');
INSERT INTO "jalandhari" VALUES(7,79,'7:79','پھر صالح ان سے (ناامید ہو کر) پھرے اور کہا کہ میری قوم! میں نے تم کو خدا کا پیغام پہنچا دیا اور تمہاری خیر خواہی کی مگر تم (ایسے ہو کہ) خیر خواہوں کو دوست ہی نہیں رکھتے');
INSERT INTO "jalandhari" VALUES(7,80,'7:80','اور اسی طرح جب ہم نے لوط کو (پیغمبر بنا کر بھیجا تو) اس وقت انہوں نے اپنی قوم سے کہا کہ تم ایسی بےحیائی کا کام کیوں کرتے ہو کہ تم سے اہل عالم میں سے کسی نے اس طرح کا کام نہیں کیا');
INSERT INTO "jalandhari" VALUES(7,81,'7:81','یعنی خواہش نفسانی پورا کرنے کے لیے عورتوں کو چھوڑ کر لونڈوں پر گرتے ہو۔ حقیقت یہ ہے کہ تم لوگ حد سے نکل جانے والے ہو');
INSERT INTO "jalandhari" VALUES(7,82,'7:82','تو ان سے اس کا جواب کچھ نہ بن پڑا اور بولے تو یہ بولے کہ ان لوگوں (یعنی لوط اور اس کے گھر والوں) کو اپنے گاؤں سے نکال دو (کہ) یہ لوگ پاک بننا چاہتے ہیں');
INSERT INTO "jalandhari" VALUES(7,83,'7:83','تو ہم نے ان کو اور ان کے گھر والوں کو بچا لیا مگر ان کی بی بی (نہ بچی) کہ وہ پیچھے رہنے والوں میں تھی');
INSERT INTO "jalandhari" VALUES(7,84,'7:84','اور ہم نے ان پر (پتھروں کا) مینھ برسایا۔ سو دیکھ لو کہ گنہگاروں کا کیسا انجام ہوا');
INSERT INTO "jalandhari" VALUES(7,85,'7:85','اور مَدین کی طرف ان کے بھائی شعیب کو بھیجا۔ (تو) انہوں نے کہا کہ قوم! خدا ہی کی عبادت کرو اس کے سوا تمہارا کوئی معبود نہیں۔ تمہارے پاس تمہارے پروردگار کی طرف سے نشانی آچکی ہے تو تم ناپ تول پوری کیا کرو اور لوگوں کو چیزیں کم نہ دیا کرو۔ اور زمین میں اصلاح کے بعد خرابی نہ کرو۔ اگر تم صاحب ایمان ہو تو سمجھ لو کہ یہ بات تمہارے حق میں بہتر ہے');
INSERT INTO "jalandhari" VALUES(7,86,'7:86','اور ہر رستے پر مت بیٹھا کرو کہ جو شخص خدا پر ایمان نہیں لاتا ہے اسے تم ڈراتے اور راہ خدا سے روکتے اور اس میں کجی ڈھونڈتے ہو اور (اس وقت کو) یاد کرو جب تم تھوڑے سے تھے تو خدا نے تم کو جماعت کثیر کر دیا اور دیکھ لو کہ خرابی کرنے والوں کا انجام کیسا ہوا');
INSERT INTO "jalandhari" VALUES(7,87,'7:87','اور اگر تم میں سے ایک جماعت میری رسالت پر ایمان لے آئی ہے اور ایک جماعت ایمان نہیں لائی ہے۔ اور ایک جماعت ایمان نہیں لائی۔ تو صبر کیے رہو یہاں تک کہ خدا ہمارے تمہارے درمیان فیصلہ کر دے اور وہ سب سے بہتر فیصلہ کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(7,88,'7:88','(تو) ان کی قوم میں جو لوگ سردار اور بڑے آدمی تھے، وہ کہنے لگے کہ شعیب! (یا تو) ہم تم کو اور جو لوگ تمہارے ساتھ ایمان لائے ہیں، ان کو اپنے شہر سے نکال دیں گے۔ یا تم ہمارے مذہب میں آجاؤ۔ انہوں نے کہا خواہ ہم (تمہارے دین سے) بیزار ہی ہوں (تو بھی؟)');
INSERT INTO "jalandhari" VALUES(7,89,'7:89','اگر ہم اس کے بعد کہ خدا ہمیں اس سے نجات بخش چکا ہے تمہارے مذہب میں لوٹ جائیں تو بےشک ہم نے خدا پر جھوٹ افتراء باندھا۔ اور ہمیں شایاں نہیں کہ ہم اس میں لوٹ جائیں ہاں خدا جو ہمارا پروردگار ہے وہ چاہے تو (ہم مجبور ہیں)۔ ہمارے پروردگار کا علم ہر چیز پر احاطہ کیے ہوئے ہے۔ ہمارا خدا ہی پر بھروسہ ہے۔ اے پروردگار ہم میں اور ہماری قوم میں انصاف کے ساتھ فیصلہ کردے اور تو سب سے بہتر فیصلہ کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(7,90,'7:90','اور ان کی قوم میں سے سردار لوگ جو کافر تھے، کہنے لگے (بھائیو) اگر تم نے شعیب کی پیروی کی تو بےشک تم خسارے میں پڑگئے');
INSERT INTO "jalandhari" VALUES(7,91,'7:91','تو ان کو بھونچال نے آپکڑا اور وہ اپنے گھروں میں اوندھے پڑے رہ گئے');
INSERT INTO "jalandhari" VALUES(7,92,'7:92','(یہ لوگ) جنہوں نے شعیب کی تکذیب کی تھی ایسے برباد ہوئے تھے کہ گویا وہ ان میں کبھی آباد ہی نہیں ہوئے تھے (غرض) جنہوں نے شعیب کو جھٹلایا وہ خسارے میں پڑگئے');
INSERT INTO "jalandhari" VALUES(7,93,'7:93','تو شعیب ان میں سے نکل آئے اور کہا کہ بھائیو میں نے تم کو اپنے پروردگار کے پیغام پہنچا دیئے ہیں اور تمہاری خیرخواہی کی تھی۔ تو میں کافروں پر (عذاب نازل ہونے سے) رنج وغم کیوں کروں');
INSERT INTO "jalandhari" VALUES(7,94,'7:94','اور ہم نے کسی شہر میں کوئی پیغمبر نہیں بھیجا مگر وہاں کے رہنے والوں کو (جو ایمان نہ لائے) دکھوں اور مصیبتوں میں مبتلا کیا تاکہ وہ عاجزی اور زاری کریں');
INSERT INTO "jalandhari" VALUES(7,95,'7:95','پھر ہم نے تکلیف کو آسودگی سے بدل دیا یہاں تک کہ (مال واولاد میں) زیادہ ہوگئے تو کہنے لگے کہ اس طرح کا رنج وراحت ہمارے بڑوں کو بھی پہنچتا رہا ہے تو ہم نے ان کو ناگہاں پکڑلیا اور وہ (اپنے حال میں) بےخبر تھے');
INSERT INTO "jalandhari" VALUES(7,96,'7:96','اگر ان بستیوں کے لوگ ایمان لے آتے اور پرہیزگار ہوجاتے۔ تو ہم ان پر آسمان اور زمین کی برکات (کے دروازے) کھول دیتے مگر انہوں نے تو تکذیب کی۔ سو ان کے اعمال کی سزا میں ہم نے ان کو پکڑ لیا');
INSERT INTO "jalandhari" VALUES(7,97,'7:97','کیا بستیوں کے رہنے والے اس سے بےخوف ہیں کہ ان پر ہمارا عذاب رات کو واقع ہو اور وہ (بےخبر) سو رہے ہوں');
INSERT INTO "jalandhari" VALUES(7,98,'7:98','اور کیا اہلِ شہر اس سے نڈر ہیں کہ ان پر ہمارا عذاب دن چڑھے آ نازل ہو اور وہ کھیل رہے ہوں');
INSERT INTO "jalandhari" VALUES(7,99,'7:99','کیا یہ لوگ خدا کے داؤ کا ڈر نہیں رکھتے (سن لو کہ) خدا کے داؤ سے وہی لوگ نڈر ہوتے ہیں جو خسارہ پانے والے ہیں');
INSERT INTO "jalandhari" VALUES(7,100,'7:100','کیا ان لوگوں کو جو اہلِ زمین کے (مرجانے کے) بعد زمین کے مالک ہوتے ہیں، یہ امر موجب ہدایت نہیں ہوا کہ اگر ہم چاہیں تو ان کے گناہوں کے سبب ان پر مصیبت ڈال دیں۔ اور ان کے دلوں پر مہر لگادیں کہ کچھ سن ہی نہ سکیں');
INSERT INTO "jalandhari" VALUES(7,101,'7:101','یہ بستیاں ہیں جن کے کچھ حالات ہم تم کو سناتے ہیں۔ اور ان کے پاس ان کے پیغمبر نشانیاں لے کر آئے۔ مگر وہ ایسے نہیں تھے کہ جس چیز کو پہلے جھٹلا چکے ہوں اسے مان لیں اسی طرح خدا کافروں کے دلوں پر مہر لگا دیتا ہے');
INSERT INTO "jalandhari" VALUES(7,102,'7:102','اور ہم نے ان میں سے اکثروں میں (عہد کا نباہ) نہیں دیکھا۔ اور ان میں اکثروں کو (دیکھا تو) بدکار ہی دیکھا');
INSERT INTO "jalandhari" VALUES(7,103,'7:103','پھر ان (پیغمبروں) کے بعد ہم نے موسیٰ کو نشانیاں دے کر فرعون اور اس کے اعیانِ سلطنت کے پاس بھیجا تو انہوں نے ان کے ساتھ کفر کیا۔ سو دیکھ لو کہ خرابی کرنے والوں کا انجام کیا ہوا');
INSERT INTO "jalandhari" VALUES(7,104,'7:104','اور موسیٰ نے کہا کہ اے فرعون میں رب العالمین کا پیغمبر ہوں');
INSERT INTO "jalandhari" VALUES(7,105,'7:105','مجھ پر واجب ہے کہ خدا کی طرف سے جو کچھ کہوں سچ ہی کہوں۔ میں تمہارے پاس تمہارے پروردگار کی طرف سے نشانی لے کر آیا ہوں۔ سو بنی اسرائیل کو میرے ساتھ جانے کی رخصت دے دیجیے');
INSERT INTO "jalandhari" VALUES(7,106,'7:106','فرعون نے کہا اگر تم نشانی لے کر آئے ہو تو اگر سچے ہو تو لاؤ (دکھاؤ)');
INSERT INTO "jalandhari" VALUES(7,107,'7:107','موسیٰ نے اپنی لاٹھی (زمین پر) ڈال دی تو وہ اسی وقت صریح اژدھا (ہوگیا)');
INSERT INTO "jalandhari" VALUES(7,108,'7:108','اور اپنا ہاتھ باہر نکالا تو اسی دم دیکھنے والوں کی نگاہوں میں سفید براق (تھا)');
INSERT INTO "jalandhari" VALUES(7,109,'7:109','تو قوم فرعون میں جو سردار تھے وہ کہنے لگے کہ یہ بڑا علامہ جادوگر ہے');
INSERT INTO "jalandhari" VALUES(7,110,'7:110','اس کا ارادہ یہ ہے کہ تم کو تمہارے ملک سے نکال دے۔ بھلا تمہاری کیا صلاح ہے؟');
INSERT INTO "jalandhari" VALUES(7,111,'7:111','انہوں نے (فرعون سے) کہا کہ فی الحال موسیٰ اور اس کے بھائی کے معاملے کو معاف رکھیے اور شہروں میں نقیب روانہ کر دیجیے');
INSERT INTO "jalandhari" VALUES(7,112,'7:112','کہ تمام ماہر جادوگروں کو آپ کے پاس لے آئیں');
INSERT INTO "jalandhari" VALUES(7,113,'7:113','(چنانچہ ایسا ہی کیا گیا) اور جادوگر فرعون کے پاس آپہنچے اور کہنے لگے کہ اگر ہم جیت گئے تو ہمیں صلہ عطا کیا جائے');
INSERT INTO "jalandhari" VALUES(7,114,'7:114','(فرعون نے) کہا ہاں (ضرور) اور (اس کے علاوہ) تم مقربوں میں داخل کرلیے جاؤ گے');
INSERT INTO "jalandhari" VALUES(7,115,'7:115','(جب فریقین روزِ مقررہ پر جمع ہوئے تو) جادوگروں نے کہا کہ موسیٰ یا تو تم (جادو کی چیز) ڈالو یا ہم ڈالتے ہیں');
INSERT INTO "jalandhari" VALUES(7,116,'7:116','(موسیٰ نے) کہا تم ہی ڈالو۔ جب انہوں نے (جادو کی چیزیں) ڈالیں تو لوگوں کی آنکھوں پر جادو کردیا (یعنی نظربندی کردی) اور (لاٹھیوں اور رسیوں کے سانپ بنا بنا کر) انہیں ڈرا دیا اور بہت بڑا جادو دکھایا');
INSERT INTO "jalandhari" VALUES(7,117,'7:117','(اس وقت) ہم نے موسیٰ کی طرف وحی بھیجی کہ تم بھی اپنی لاٹھی ڈال دو۔ وہ فوراً (سانپ بن کر) جادوگروں کے بنائے ہوئے سانپوں کو (ایک ایک کرکے) نگل جائے گی');
INSERT INTO "jalandhari" VALUES(7,118,'7:118','(پھر) تو حق ثابت ہوگیا اور جو کچھ فرعونی کرتے تھے، باطل ہوگیا');
INSERT INTO "jalandhari" VALUES(7,119,'7:119','اور وہ مغلوب ہوگئے اور ذلیل ہوکر رہ گئے');
INSERT INTO "jalandhari" VALUES(7,120,'7:120','(یہ کیفیت دیکھ کر) جادوگر سجدے میں گر پڑے');
INSERT INTO "jalandhari" VALUES(7,121,'7:121','اور کہنے لگے کہ ہم جہان کے پروردگار پر ایمان لائے');
INSERT INTO "jalandhari" VALUES(7,122,'7:122','یعنی موسیٰ اور ہارون کے پروردگار پر');
INSERT INTO "jalandhari" VALUES(7,123,'7:123','فرعون نے کہا کہ پیشتر اس کے کہ میں تمہیں اجازت دوں تم اس پر ایمان لے آئے؟ بےشک یہ فریب ہے جو تم نے مل کر شہر میں کیا ہے تاکہ اہلِ شہر کو یہاں سے نکال دو۔ سو عنقریب (اس کا نتیجہ) معلوم کرلو گے');
INSERT INTO "jalandhari" VALUES(7,124,'7:124','میں (پہلے تو) تمہارے ایک طرف کے ہاتھ اور دوسری طرف کے پاؤں کٹوا دوں گا پھر تم سب کو سولی چڑھوا دوں گا');
INSERT INTO "jalandhari" VALUES(7,125,'7:125','وہ بولے کہ ہم تو اپنے پروردگار کی طرف لوٹ کر جانے والے ہیں');
INSERT INTO "jalandhari" VALUES(7,126,'7:126','اور اس کے سوا تجھ کو ہماری کون سی بات بری لگی ہے کہ جب ہمارے پروردگار کی نشانیاں ہمارے پاس آگئیں تو ہم ان پر ایمان لے آئے۔ اے پروردگار ہم پر صبرواستقامت کے دہانے کھول دے اور ہمیں (ماریو تو) مسلمان ہی ماریو');
INSERT INTO "jalandhari" VALUES(7,127,'7:127','اور قومِ فرعون میں جو سردار تھے کہنے لگے کہ کیا آپ موسیٰ اور اس کی قوم کو چھوڑ دیجیے گا کہ ملک میں خرابی کریں اور آپ سے اور آپ کے معبودوں سے دست کش ہوجائیں۔ وہ بولے کہ ہم ان کے لڑکوں کو قتل کرڈالیں گے اور لڑکیوں کو زندہ رہنے دیں گے اور بےشک ہم ان پر غالب ہیں');
INSERT INTO "jalandhari" VALUES(7,128,'7:128','موسیٰ نے اپنی قوم سے کہا کہ خدا سے مدد مانگو اور ثابت قدم رہو۔ زمین تو خدا کی ہے۔ وہ اپنے بندوں میں سے جسے چاہتا ہے اس کا مالک بناتا ہے۔ اور آخر بھلا تو ڈرنے والوں کا ہے');
INSERT INTO "jalandhari" VALUES(7,129,'7:129','وہ بولے کہ تمہارے آنے سے پہلے بھی ہم کو اذیتیں پہنچتی رہیں اور آنے کے بعد بھی۔ موسیٰ نے کہا کہ قریب ہے کہ تمہارا پروردگار تمہارے دشمن کو ہلاک کردے اور اس کی جگہ تمہیں زمین میں خلیفہ بنائے پھر دیکھے کہ تم کیسے عمل کرتے ہو');
INSERT INTO "jalandhari" VALUES(7,130,'7:130','اور ہم نے فرعونیوں کو قحطوں اور میووں کے نقصان میں پکڑا تاکہ نصیحت حاصل کریں');
INSERT INTO "jalandhari" VALUES(7,131,'7:131','تو جب ان کو آسائش حاصل ہوتی تو کہتے کہ ہم اس کے مستحق ہیں۔ اور اگر سختی پہنچتی تو موسیٰ اور ان کے رفیقوں کی بدشگونی بتاتے۔ دیکھو ان کی بدشگونی خدا کے ہاں مقرر ہے لیکن ان میں اکثر نہیں جانتے');
INSERT INTO "jalandhari" VALUES(7,132,'7:132','اور کہنے لگے کہ تم ہمارے پاس (خواہ) کوئی ہی نشانی لاؤ تاکہ اس سے ہم پر جادو کرو۔ مگر ہم تم پر ایمان لانے والے نہیں ہیں');
INSERT INTO "jalandhari" VALUES(7,133,'7:133','تو ہم نے ان پر طوفان اور ٹڈیاں اور جوئیں اور مینڈک اور خون کتنی کھلی ہوئی نشانیاں بھیجیں۔ مگر وہ تکبر ہی کرتے رہے اور وہ لوگ تھے ہی گنہگار');
INSERT INTO "jalandhari" VALUES(7,134,'7:134','اور جب ان پر عذاب واقع ہوتا تو کہتے کہ موسیٰ ہمارے لیے اپنے پروردگار سے دعا کرو۔ جیسا اس نے تم سے عہد کر رکھا ہے۔ اگر تم ہم سے عذاب کو ٹال دو گے تو ہم تم پر ایمان بھی لے آئیں گے اور بنی اسرائیل کو بھی تمہارے ساتھ جانے (کی اجازت) دیں گے');
INSERT INTO "jalandhari" VALUES(7,135,'7:135','پھر جب ہم ایک مدت کے لیے جس تک ان کو پہنچنا تھا ان سے عذاب دور کردیتے تو وہ عہد کو توڑ ڈالتے');
INSERT INTO "jalandhari" VALUES(7,136,'7:136','تو ہم نے ان سے بدلہ لے کر ہی چھوڑا کہ ان کو دریا میں ڈبو دیا اس لیے کہ وہ ہماری آیتوں کو جھٹلاتے اور ان سے بےپروائی کرتے تھے');
INSERT INTO "jalandhari" VALUES(7,137,'7:137','اور جو لوگ کمزور سمجھے جاتے تھے ان کو زمین (شام) کے مشرق ومغرب کا جس میں ہم نے برکت دی تھی وارث کردیا اور بنی اسرائیل کے بارے میں ان کے صبر کی وجہ سے تمہارے پروردگار کا وعدہٴ نیک پورا ہوا اور فرعون اور قوم فرعون جو (محل) بناتے اور (انگور کے باغ) جو چھتریوں پر چڑھاتے تھے سب کو ہم نے تباہ کردیا');
INSERT INTO "jalandhari" VALUES(7,138,'7:138','اور ہم نے بنی اسرائیل کو دریا کے پار اتارا تو وہ ایسے لوگوں کے پاس جا پہنچے جو اپنے بتوں (کی عبادت) کے لیے بیٹھے رہتے تھے۔ (بنی اسرائیل) کہنے لگے کہ موسیٰ جیسے ان لوگوں کے معبود ہیں۔ ہمارے لیے بھی ایک معبود بنا دو۔ موسیٰ نے کہا کہ تم بڑے ہی جاہل لوگ ہو');
INSERT INTO "jalandhari" VALUES(7,139,'7:139','یہ لوگ جس (شغل) میں (پھنسے ہوئے) ہیں وہ برباد ہونے والا ہے اور جو کام یہ کرتے ہیں سب بیہودہ ہیں');
INSERT INTO "jalandhari" VALUES(7,140,'7:140','(اور یہ بھی) کہا کہ بھلا میں خدا کے سوا تمہارے لیے کوئی اور معبود تلاش کروں حالانکہ اس نے تم کو تمام اہل عالم پر فضیلت بخشی ہے');
INSERT INTO "jalandhari" VALUES(7,141,'7:141','(اور ہمارے ان احسانوں کو یاد کرو) جب ہم نے تم کو فرعونیوں (کے ہاتھ) سے نجات بخشی وہ لوگ تم کو بڑا دکھ دیتے تھے۔ تمہارے بیٹوں کو قتل کر ڈالتے تھے اور بیٹیوں کو زندہ رہنے دیتے تھے۔ اور اس میں تمہارے پروردگار کی طرف سے سخت آزمائش تھی');
INSERT INTO "jalandhari" VALUES(7,142,'7:142','اور ہم نے موسیٰ سے تیس رات کی میعاد مقرر کی۔ اور اس دس (راتیں) اور ملا کر اسے پورا (چلّہ) کردیا تو اس کے پروردگار کی چالیس رات کی میعاد پوری ہوگئی۔ اور موسیٰ نے اپنے بھائی ہارون سے کہا کہ میرے (کوہِٰ طور پر جانے کے) بعد تم میری قوم میں میرے جانشین ہو (ان کی) اصلاح کرتے رہنا ٹھیک اور شریروں کے رستے نہ چلنا');
INSERT INTO "jalandhari" VALUES(7,143,'7:143','اور جب موسیٰ ہمارے مقرر کیے ہوئے وقت پر (کوہ طور) پر پہنچے اور ان کے پروردگار نے ان سے کلام کیا تو کہنے لگے کہ اے پروردگار مجھے (جلوہ) دکھا کہ میں تیرا دیدار (بھی) دیکھوں۔ پروردگار نے کہا کہ تم مجھے ہرگز نہ دیکھ سکو گے۔ ہاں پہاڑ کی طرف دیکھتے رہو اگر یہ اپنی جگہ قائم رہا تو تم مجھے دیکھ سکو گے۔ جب ان کا پروردگار پہاڑ پر نمودار ہوا تو (تجلی انوارِ ربانی) نے اس کو ریزہ ریزہ کردیا اور موسیٰ بےہوش ہو کر گر پڑے۔ جب ہوش میں آئے تو کہنے لگے کہ تیری ذات پاک ہے اور میں تیرے حضور توبہ کرتا ہوں اور جو ایمان لانے والے ہیں ان میں سب سے اول ہوں');
INSERT INTO "jalandhari" VALUES(7,144,'7:144','(خدا نے) فرمایا موسیٰ میں نے تم کو اپنے پیغام اور اپنے کلام سے لوگوں سے ممتاز کیا ہے۔ تو جو میں نے تم کو عطا کیا ہے اسے پکڑ رکھو اور (میرا) شکر بجالاؤ');
INSERT INTO "jalandhari" VALUES(7,145,'7:145','اور ہم نے (تورات) کی تختیوں میں ان کے لیے ہر قسم کی نصیحت اور ہر چیز کی تفصیل لکھ دی پھر (ارشاد فرمایا کہ) اسے زور سے پکڑے رہو اور اپنی قوم سے بھی کہہ دو کہ ان باتوں کو جو اس میں (مندرج ہیں اور) بہت بہتر ہیں پکڑے رہیں۔ میں عنقریب تم کو نافرمان لوگوں کا گھر دکھاؤں گا');
INSERT INTO "jalandhari" VALUES(7,146,'7:146','جو لوگ زمین میں ناحق غرور کرتے ہیں ان کو اپنی آیتوں سے پھیر دوں گا۔ اگر یہ سب نشانیاں بھی دیکھ لیں تب بھی ان پر ایمان نہ لائیں اور اگر راستی کا رستہ دیکھیں تو اسے (اپنا) رستہ نہ بنائیں۔ اور اگر گمراہی کی راہ دیکھیں تو اسے رستہ بنالیں۔ یہ اس لیے کہ انہوں نے ہماری آیات کو جھٹلایا اور ان سے غفلت کرتے رہے');
INSERT INTO "jalandhari" VALUES(7,147,'7:147','اور جن لوگوں نے ہماری آیتوں اور آخرت کے آنے کو جھٹلایا ان کے اعمال ضائع ہوجائیں گے۔ یہ جیسے عمل کرتے ہیں ویسا ہی ان کو بدلہ ملے گا');
INSERT INTO "jalandhari" VALUES(7,148,'7:148','اور قوم موسیٰ نے موسیٰ کے بعد اپنے زیور کا ایک بچھڑا بنا لیا (وہ) ایک جسم (تھا) جس میں سے بیل کی آواز نکلتی تھی۔ ان لوگوں نے یہ نہ دیکھا کہ وہ نہ ان سے بات کرسکتا ہے اور نہ ان کو راستہ دکھا سکتا ہے۔ اس کو انہوں نے (معبود) بنالیا اور (اپنے حق میں) ظلم کیا');
INSERT INTO "jalandhari" VALUES(7,149,'7:149','اور جب وہ نادم ہوئے اور دیکھا کہ گمراہ ہوگئے ہیں تو کہنے لگے کہ اگر ہمارا پروردگار ہم پر رحم نہیں کرے گا اور ہم کو معاف نہیں فرمائے گا تو ہم برباد ہوجائیں گے');
INSERT INTO "jalandhari" VALUES(7,150,'7:150','اور جب موسیٰ اپنی قوم میں نہایت غصے اور افسوس کی حالت میں واپس آئے۔ تو کہنے لگے کہ تم نے میرے بعد بہت ہی بداطواری کی۔ کیا تم نے اپنے پروردگار کا حکم (یعنی میرا اپنے پاس آنا) جلد چاہا (یہ کہا) اور (شدت غضب سے تورات کی) تختیاں ڈال دیں اور اپنے بھائی کے سر (کے بالوں) کو پکڑ کر اپنی طرف کھینچنے لگے۔ انہوں نے کہا کہ بھائی جان لوگ تو مجھے کمزور سمجھتے تھے اور قریب تھا کہ قتل کردیں۔ تو ایسا کام نہ کیجیے کہ دشمن مجھ پر ہنسیں اور مجھے ظالم لوگوں میں مت ملایئے');
INSERT INTO "jalandhari" VALUES(7,151,'7:151','تب انہوں نے دعا کی کہ اے میرے پروردگار مجھے اور میرے بھائی کو معاف فرما اور ہمیں اپنی رحمت میں داخل کر تو سب سے بڑھ کر رحم کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(7,152,'7:152','(خدا نے فرمایا کہ) جن لوگوں نے بچھڑے کو (معبود) بنا لیا تھا ان پر پروردگار کا غضب واقع ہوگا اور دنیا کی زندگی میں ذلت (نصیب ہوگی) اور ہم افتراء پردازوں کو ایسا ہی بدلہ دیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(7,153,'7:153','اور جنہوں نے برے کام کیے پھر اس کے بعد توبہ کرلی اور ایمان لے آئے تو کچھ شک نہیں کہ تمہارا پروردگار اس کے بعد (بخش دے گا کہ وہ) بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(7,154,'7:154','اور جب موسیٰ کا غصہ فرو ہوا تو (تورات) کی تختیاں اٹھالیں اور جو کچھ ان میں لکھا تھا وہ ان لوگوں کے لیے جو اپنے پروردگار سے ڈرتے ہیں۔ ہدایت اور رحمت تھی');
INSERT INTO "jalandhari" VALUES(7,155,'7:155','اور موسیٰ نے اس میعاد پر جو ہم نے مقرر کی تھی اپنی قوم کے ستر آدمی منتخب (کرکے کوہ طور پر حاضر) ٹل کیے۔ جب ان کو زلزلے نے پکڑا تو موسیٰ نے کہا کہ اے پروردگار تو چاہتا تو ان کو اور مجھ کو پہلے ہی سے ہلاک کر دیتا۔ کیا تو اس فعل کی سزا میں جو ہم میں سے بےعقل لوگوں نے کیا ہے ہمیں ہلاک کردے گا۔ یہ تو تیری آزمائش ہے۔ اس سے تو جس کو چاہے گمراہ کرے اور جس کو چاہے ہدایت بخشے۔ تو ہی ہمارا کارساز ہے تو ہمیں (ہمارے گناہ) بخش دے اور ہم پر رحم فرما اور تو سب سے بہتر بخشنے والا ہے');
INSERT INTO "jalandhari" VALUES(7,156,'7:156','اور ہمارے لیے اس دنیا میں بھی بھلائی لکھ دے اور آخرت میں بھی۔ ہم تیری طرف رجوع ہوچکے۔ فرمایا کہ جو میرا عذاب ہے اسے تو جس پر چاہتا ہوں نازل کرتا ہوں اور جو میری رحمت ہے وہ ہر چیز کو شامل ہے۔ میں اس کو ان لوگوں کے لیے لکھ دوں گا جو پرہیزگاری کرتے اور زکوٰة دیتے اور ہماری آیتوں پر ایمان رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(7,157,'7:157','وہ جو (محمدصلى الله عليه وسلم) رسول (الله) کی جو نبی اُمی ہیں پیروی کرتے ہیں جن (کے اوصاف) کو وہ اپنے ہاں تورات اور انجیل میں لکھا ہوا پاتے ہیں۔ وہ انہیں نیک کام کا حکم دیتے ہیں اور برے کام سے روکتے ہیں۔ اور پاک چیزوں کو ان کے لیے حلال کرتے ہیں اور ناپاک چیزوں کو ان پر حرام ٹہراتے ہیں اور ان پر سے بوجھ اور طوق جو ان (کے سر) پر (اور گلے میں) تھے اتارتے ہیں۔ تو جو لوگ ان پر ایمان لائے اور ان کی رفاقت کی اور انہیں مدد دی۔ اور جو نور ان کے ساتھ نازل ہوا ہے اس کی پیروی کی۔ وہی مراد پانے والے ہیں');
INSERT INTO "jalandhari" VALUES(7,158,'7:158','(اے محمدصلى الله عليه وسلم) کہہ دو کہ لوگو میں تم سب کی طرف خدا کا بھیجا ہوا (یعنی اس کا رسول) ہوں۔ (وہ) جو آسمانوں اور زمین کا بادشاہ ہے۔ اس کے سوا کوئی معبود نہیں وہی زندگانی بخشتا ہے اور وہی موت دیتا ہے۔ تو خدا پر اور اس کے رسول پیغمبر اُمی پر جو خدا پر اور اس کے تمام کلام پر ایمان رکھتے ہیں ایمان لاؤ اور ان کی پیروی کرو تاکہ ہدایت پاؤ');
INSERT INTO "jalandhari" VALUES(7,159,'7:159','اور قوم موسیٰ میں کچھ لوگ ایسے بھی ہیں جو حق کا راستہ بتاتے اور اسی کے ساتھ انصاف کرتے ہیں');
INSERT INTO "jalandhari" VALUES(7,160,'7:160','اور ہم نے ان کو (یعنی بنی اسرائیل کو) الگ الگ کرکے بارہ قبیلے (اور) بڑی بڑی جماعتیں بنا دیا۔ اور جب موسیٰ سے ان کی قوم نے پانی طلب کیا تو ہم نے ان کی طرف وحی بھیجی کہ اپنی لاٹھی پتھر پر مار دو۔ تو اس میں سے بارہ چشمے پھوٹ نکلے۔ اور سب لوگوں نے اپنا اپنا گھاٹ معلوم کرلیا۔ اور ہم نے ان (کے سروں) پر بادل کو سائبان بنائے رکھا اور ان پر من وسلویٰ اتارتے رہے۔ اور (ان سے کہا کہ) جو پاکیزہ چیزیں ہم تمہیں دیتے ہیں انہیں کھاؤ۔ اور ان لوگوں نے ہمارا کچھ نقصان نہیں کیا بلکہ (جو) نقصان کیا اپنا ہی کیا');
INSERT INTO "jalandhari" VALUES(7,161,'7:161','اور (یاد کرو) جب ان سے کہا گیا کہ اس شہر میں سکونت اختیار کرلو اور اس میں جہاں سے جی چاہے کھانا (پینا) اور (ہاں شہر میں جانا تو) حِطّتہٌ کہنا اور دروازے میں داخل ہونا تو سجدہ کرنا۔ ہم تمہارے گناہ معاف کردیں گے۔ اور نیکی کرنے والوں کو اور زیادہ دیں گے');
INSERT INTO "jalandhari" VALUES(7,162,'7:162','مگر جو ان میں ظالم تھے انہوں نے اس لفظ کو جس کا ان کو حکم دیا گیا تھا بدل کر اس کی جگہ اور لفظ کہنا شروع کیا تو ہم نے ان پر آسمان سے عذاب بھیجا اس لیے کہ ظلم کرتے تھے');
INSERT INTO "jalandhari" VALUES(7,163,'7:163','اور ان سے اس گاؤں کا حال تو پوچھو جب لب دریا واقع تھا۔ جب یہ لوگ ہفتے کے دن کے بارے میں حد سے تجاوز کرنے لگے (یعنی) اس وقت کہ ان کے ہفتے کے دن مچھلیاں ان کے سامنے پانی کے اوپر آتیں اور جب ہفتے کا دن نہ ہوتا تو نہ آتیں۔ اسی طرح ہم ان لوگوں کو ان کی نافرمانیوں کے سبب آزمائش میں ڈالنے لگے');
INSERT INTO "jalandhari" VALUES(7,164,'7:164','اور جب ان میں سے ایک جماعت نے کہا کہ تم ایسے لوگوں کو کیوں نصیحت کرتے ہو جن کو الله ہلاک کرنے والا یا سخت عذاب دینے والا ہے تو انہوں نے کہا اس لیے کہ تمہارے پروردگار کے سامنے معذرت کرسکیں اور عجب نہیں کہ وہ پرہیزگاری اختیار کریں');
INSERT INTO "jalandhari" VALUES(7,165,'7:165','جب انہوں نے ان باتوں کو فراموش کردیا جن کی ان کو نصیحت کی گئی تھی تو جو لوگ برائی سے منع کرتے تھے ان کو ہم نے نجات دی اور جو ظلم کرتے تھے ان کو برے عذاب میں پکڑ لیا کہ نافرمانی کئے جاتے تھے');
INSERT INTO "jalandhari" VALUES(7,166,'7:166','غرض جن اعمال (بد) سے ان کو منع کیا گیا تھا جب وہ ان (پراصرار اور ہمارے حکم سے) گردن کشی کرنے لگے تو ہم نے ان کو حکم دیا کہ ذلیل بندر ہوجاؤ');
INSERT INTO "jalandhari" VALUES(7,167,'7:167','(اور اس وقت کو یاد کرو) جب تمہارے پروردگار نے (یہود کو) آگاہ کردیا تھا کہ وہ ان پر قیامت تک ایسے شخص کو مسلط رکھے گا جو انہیں بری بری تکلیفیں دیتا رہے۔ بےشک تمہارا پروردگار جلد عذاب کرنے والا ہے اور وہ بخشنے والا مہربان بھی ہے');
INSERT INTO "jalandhari" VALUES(7,168,'7:168','اور ہم نے ان کو جماعت جماعت کرکے ملک میں منتشر کر دیا۔ بعض ان میں سے نیکوکار ہیں اور بعض اور طرح کے (یعنی بدکار) اور ہم آسائشوں، تکلیفوں (دونوں) سے ان کی آزمائش کرتے رہے تاکہ (ہماری طرف) رجوع کریں');
INSERT INTO "jalandhari" VALUES(7,169,'7:169','پھر ان کے بعد ناخلف ان کے قائم مقام ہوئے جو کتاب کے وارث بنے۔ یہ (بےتامل) اس دنیائے دنی کا مال ومتاع لے لیتے ہیں اور کہتے ہیں کہ ہم بخش دیئے جائیں گے۔ اور (لوگ ایسوں پر طعن کرتے ہیں) اگر ان کے سامنے بھی ویسا ہی مال آجاتا ہے تو وہ بھی اسے لے لیتے ہیں۔ کیا ان سے کتاب کی نسبت عہد نہیں لیا گیا کہ خدا پر سچ کے سوا اور کچھ نہیں کہیں گے۔ اور جو کچھ اس (کتاب) میں ہے اس کو انہوں نے پڑھ بھی لیا ہے۔ اور آخرت کا گھر پرہیزگاروں کے لیے بہتر ہے کیا تم سمجھتے نہیں');
INSERT INTO "jalandhari" VALUES(7,170,'7:170','اور جو لوگ کتاب کو مضبوط پکڑے ہوئے ہیں اور نماز کا التزام رکھتے ہیں (ان کو ہم اجر دیں گے کہ) ہم نیکوکاروں کا اجر ضائع نہیں کرتے');
INSERT INTO "jalandhari" VALUES(7,171,'7:171','اور جب ہم نے ان (کے سروں) پر پہاڑ اٹھا کھڑا کیا گویا وہ سائبان تھا اور انہوں نے خیال کیا کہ وہ ان پر گرتا ہے تو (ہم نے کہا کہ) جو ہم نے تمہیں دیا ہے اسے زور سے پکڑے رہو۔ اور جو اس میں لکھا ہے اس پر عمل کرو تاکہ بچ جاؤ');
INSERT INTO "jalandhari" VALUES(7,172,'7:172','اور جب تمہارے پروردگار نے بنی آدم سے یعنی ان کی پیٹھوں سے ان کی اولاد نکالی تو ان سے خود ان کے مقابلے میں اقرار کرا لیا (یعنی ان سے پوچھا کہ) کیا تمہارا پروردگار نہیں ہوں۔ وہ کہنے لگے کیوں نہیں ہم گواہ ہیں (کہ تو ہمارا پروردگار ہے)۔ یہ اقرار اس لیے کرایا تھا کہ قیامت کے دن (کہیں یوں نہ) کہنے لگو کہ ہم کو تو اس کی خبر ہی نہ تھی');
INSERT INTO "jalandhari" VALUES(7,173,'7:173','یا یہ (نہ) کہو کہ شرک تو پہلے ہمارے بڑوں نے کیا تھا۔ اور ہم تو ان کی اولاد تھے (جو) ان کے بعد (پیدا ہوئے)۔ تو کیا جو کام اہل باطل کرتے رہے اس کے بدلے تو ہمیں ہلاک کرتا ہے');
INSERT INTO "jalandhari" VALUES(7,174,'7:174','اور اسی طرح ہم (اپنی) آیتیں کھول کھول کر بیان کرتے ہیں تاکہ یہ رجوع کریں');
INSERT INTO "jalandhari" VALUES(7,175,'7:175','اور ان کو اس شخص کا حال پڑھ کر سنا دو جس کو ہم نے اپنی آیتیں عطا فرمائیں (اور ہفت پارچہٴ علم شرائع سے مزین کیا) تو اس نے ان کو اتار دیا پھر شیطان اس کے پیچھے لگا تو وہ گمراہوں میں ہوگیا');
INSERT INTO "jalandhari" VALUES(7,176,'7:176','اور اگر ہم چاہتے تو ان آیتوں سے اس (کے درجے) کو بلند کر دیتے مگر وہ تو پستی کی طرف مائل ہوگیا اور اپنی خواہش کے پیچھے چل پڑا۔ تو اس کی مثال کتے کی سی ہوگئی کہ اگر سختی کرو تو زبان نکالے رہے اور یونہی چھوڑ دو تو بھی زبان نکالے رہے۔ یہی مثال ان لوگوں کی ہے جنہوں نے ہماری آیتوں کو جھٹلایا تو ان سے یہ قصہ بیان کردو۔ تاکہ وہ فکر کریں');
INSERT INTO "jalandhari" VALUES(7,177,'7:177','جن لوگوں نے ہماری آیتوں کی تکذیب کی ان کی مثال بری ہے اور انہوں نے نقصان (کیا تو) اپنا ہی کیا');
INSERT INTO "jalandhari" VALUES(7,178,'7:178','جس کو خدا ہدایت دے وہی راہ یاب ہے اور جس کو گمراہ کرے تو ایسے ہی لوگ نقصان اٹھانے والے ہیں');
INSERT INTO "jalandhari" VALUES(7,179,'7:179','اور ہم نے بہت سے جن اور انسان دوزخ کے لیے پیدا کیے ہیں۔ ان کے دل ہیں لیکن ان سے سمجھتے نہیں اور ان کی آنکھیں ہیں مگر ان سے دیکھتے نہیں اور ان کے کان ہیں پر ان سے سنتے نہیں۔ یہ لوگ بالکل چارپایوں کی طرح ہیں بلکہ ان سے بھی بھٹکے ہوئے۔ یہی وہ ہیں جو غفلت میں پڑے ہوئے ہیں');
INSERT INTO "jalandhari" VALUES(7,180,'7:180','اور خدا کے سب نام اچھے ہی اچھے ہیں۔ تو اس کو اس کے ناموں سے پکارا کرو اور جو لوگ اس کے ناموں میں کجی اختیار کرتے ہیں ان کو چھوڑ دو۔ وہ جو کچھ کر رہے ہیں عنقریب اس کی سزا پائیں گے');
INSERT INTO "jalandhari" VALUES(7,181,'7:181','اور ہماری مخلوقات میں سے ایک وہ لوگ ہیں جو حق کا رستہ بتاتے ہیں اور اسی کے ساتھ انصاف کرتے ہیں');
INSERT INTO "jalandhari" VALUES(7,182,'7:182','اور جن لوگوں نے ہماری آیتوں کو جھٹلایا ان کو بتدریج اس طریق سے پکڑیں گے کہ ان کو معلوم ہی نہ ہوگا');
INSERT INTO "jalandhari" VALUES(7,183,'7:183','اور میں ان کو مہلت دیئے جاتا ہوں میری تدبیر (بڑی) مضبوط ہے');
INSERT INTO "jalandhari" VALUES(7,184,'7:184','کیا انہوں نے غور نہیں کیا کہ ان کے رفیق محمد (صلى الله عليه وسلم) کو (کسی طرح کا بھی) جنون نہیں ہے۔ وہ تو ظاہر ظہور ڈر سنانے والے ہیں');
INSERT INTO "jalandhari" VALUES(7,185,'7:185','کیا انہوں نے آسمان اور زمین کی بادشاہت میں جو چیزیں خدا نے پیدا کی ہیں ان پر نظر نہیں کی اور اس بات پر (خیال نہیں کیا) کہ عجب نہیں ان (کی موت) کا وقت نزدیک پہنچ گیا ہو۔ تو اس کے بعد وہ اور کس بات پر ایمان لائیں گے');
INSERT INTO "jalandhari" VALUES(7,186,'7:186','جس شخص کو خدا گمراہ کرے اس کو کوئی ہدایت دینے والا نہیں اور وہ ان (گمراہوں) کو چھوڑے رکھتا ہے کہ اپنی سرکشی میں پڑے بہکتے رہیں');
INSERT INTO "jalandhari" VALUES(7,187,'7:187','(یہ لوگ) تم سے قیامت کے بارے میں پوچھتے ہیں کہ اس کے واقع ہونے کا وقت کب ہے۔ کہہ دو کہ اس کا علم تو میرے پروردگار ہی کو ہے۔ وہی اسے اس کے وقت پر ظاہر کردےگا۔ وہ آسمان وزمین میں ایک بھاری بات ہوگی اور ناگہاں تم پر آجائے گی۔ یہ تم سے اس طرح دریافت کرتے ہیں کہ گویا تم اس سے بخوبی واقف ہو۔ کہو کہ اس کا علم تو خدا ہی کو ہے لیکن اکثر لوگ یہ نہیں جانتے');
INSERT INTO "jalandhari" VALUES(7,188,'7:188','کہہ دو کہ میں اپنے فائدے اور نقصان کا کچھ بھی اختیار نہیں رکھتا مگر جو الله چاہے اور اگر میں غیب کی باتیں جانتا ہوتا تو بہت سے فائدے جمع کرلیتا اور مجھ کو کوئی تکلیف نہ پہنچتی۔ میں تو مومنوں کو ڈر اور خوشخبری سنانے والا ہوں');
INSERT INTO "jalandhari" VALUES(7,189,'7:189','وہ خدا ہی تو ہے جس نے تم کو ایک شخص سے پیدا کیا اور اس سے اس کا جوڑا بنایا تاکہ اس سے راحت حاصل کرے۔ سو جب وہ اس کے پاس جاتا ہے تو اسے ہلکا سا حمل رہ جاتا ہے اور وہ اس کے ساتھ چلتی پھرتی ہے۔ پھر جب کچھ بوجھ معلوم کرتی یعنی بچہ پیٹ میں بڑا ہوتا ہے تو دونوں میاں بیوی اپنے پروردگار خدائے عزوجل سے التجا کرتے ہیں کہ اگر تو ہمیں صحیح وسالم (بچہ) دے گا تو ہم تیرے شکر گذار ہوں گے');
INSERT INTO "jalandhari" VALUES(7,190,'7:190','جب وہ ان کو صحیح و سالم (بچہ) دیتا ہے تو اس (بچے) میں جو وہ ان کو دیتا ہے اس کا شریک مقرر کرتے ہیں۔ جو وہ شرک کرتے ہیں (خدا کا رتبہ) اس سے بلند ہے');
INSERT INTO "jalandhari" VALUES(7,191,'7:191','کیا وہ ایسوں کو شریک بناتے ہیں جو کچھ بھی پیدا نہیں کرسکتے اور خود پیدا کیے جاتے ہیں');
INSERT INTO "jalandhari" VALUES(7,192,'7:192','اور نہ ان کی مدد کی طاقت رکھتے ہیں اور نہ اپنی ہی مدد کرسکتے ہیں');
INSERT INTO "jalandhari" VALUES(7,193,'7:193','اگر تم ان کو سیدھے رستے کی طرف بلاؤ تو تمہارا کہا نہ مانیں۔ تمہارے لیے برابر ہے کہ تم ان کو بلاؤ یا چپکے ہو رہو');
INSERT INTO "jalandhari" VALUES(7,194,'7:194','(مشرکو) جن کو تم خدا کے سوا پکارتے ہو وہ تمہاری طرح کے بندے ہی ہیں (اچھا) تم ان کو پکارو اگر سچے ہو تو چاہیئے کہ وہ تم کو جواب بھی دیں');
INSERT INTO "jalandhari" VALUES(7,195,'7:195','بھلا ان کے پاؤں ہیں جن سے چلیں یا ہاتھ ہیں جن سے پکڑیں یا آنکھیں ہیں جن سے دیکھیں یا کان ہیں جن سے سنیں؟ کہہ دو کہ اپنے شریکوں کو بلالو اور میرے بارے میں (جو) تدبیر (کرنی ہو) کرلو اور مجھے کچھ مہلت بھی نہ دو (پھر دیکھو کہ وہ میرا کیا کرسکتے ہیں)');
INSERT INTO "jalandhari" VALUES(7,196,'7:196','میرا مددگار تو خدا ہی ہے جس نے کتاب (برحق) نازل کی۔ اور نیک لوگوں کا وہی دوستدار ہے');
INSERT INTO "jalandhari" VALUES(7,197,'7:197','اور جن کو تم خدا کے سوا پکارتے ہو وہ نہ تمہاری ہی مدد کی طاقت رکھتے ہیں اور نہ خود ہی اپنی مدد کرسکتے ہیں');
INSERT INTO "jalandhari" VALUES(7,198,'7:198','اور اگر تم ان کو سیدھے رستے کی طرف بلاؤ تو سن نہ سکیں اور تم انہیں دیکھتے ہو کہ (بہ ظاہر) آنکھیں کھولے تمہاری طرف دیکھ رہے ہیں مگر (فی الواقع) کچھ نہیں دیکھتے');
INSERT INTO "jalandhari" VALUES(7,199,'7:199','(اے محمدصلى الله عليه وسلم) عفو اختیار کرو اور نیک کام کرنے کا حکم دو اور جاہلوں سے کنارہ کرلو');
INSERT INTO "jalandhari" VALUES(7,200,'7:200','اور اگر شیطان کی طرف سے تمہارے دل میں کسی طرح کا وسوسہ پیدا ہو تو خدا سے پناہ مانگو۔ بےشک وہ سننے والا (اور) سب کچھ جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(7,201,'7:201','جو لوگ پرہیزگار ہیں جب ان کو شیطان کی طرف سے کوئی وسوسہ پیدا ہوتا ہے تو چونک پڑتے ہیں اور (دل کی آنکھیں کھول کر) دیکھنے لگتے ہیں');
INSERT INTO "jalandhari" VALUES(7,202,'7:202','اور ان (کفار) کے بھائی انہیں گمراہی میں کھینچے جاتے ہیں پھر (اس میں کسی طرح کی) کوتاہی نہیں کرتے');
INSERT INTO "jalandhari" VALUES(7,203,'7:203','اور جب تم ان کے پاس (کچھ دنوں تک) کوئی آیت نہیں لاتے تو کہتے ہیں کہ تم نے (اپنی طرف سے) کیوں نہیں بنالی۔ کہہ دو کہ میں تو اسی کی پیروی کرتا ہوں جو میرے پروردگار کی طرف سے میرے پاس آتا ہے۔ یہ قرآن تمہارے پروردگار کی جانب سے دانش وبصیرت اور مومنوں کے لیے ہدایت اور رحمت ہے');
INSERT INTO "jalandhari" VALUES(7,204,'7:204','اور جب قرآن پڑھا جائے تو توجہ سے سنا کرو اور خاموش رہا کرو تاکہ تم پر رحم کیا جائے');
INSERT INTO "jalandhari" VALUES(7,205,'7:205','اور اپنے پروردگار کو دل ہی دل میں عاجزی اور خوف سے اور پست آواز سے صبح وشام یاد کرتے رہو اور (دیکھنا) غافل نہ ہونا');
INSERT INTO "jalandhari" VALUES(7,206,'7:206','جو لوگ تمہارے پروردگار کے پاس ہیں وہ اس کی عبادت سے گردن کشی نہیں کرتے اور اس پاک ذات کو یاد کرتے اور اس کے آگے سجدے کرتے رہتے ہیں');
INSERT INTO "jalandhari" VALUES(8,1,'8:1','(اے محمد! مجاہد لوگ) تم سے غنیمت کے مال کے بارے میں دریافت کرتے ہیں کہ (کیا حکم ہے) کہہ دو کہ غنیمت خدا اور اس کے رسول کا مال ہے۔ تو خدا سے ڈرو اور آپس میں صلح رکھو اور اگر ایمان رکھتے ہو تو خدا اور اس کے رسول کے حکم پر چلو');
INSERT INTO "jalandhari" VALUES(8,2,'8:2','مومن تو وہ ہیں کہ جب خدا کا ذکر کیا جاتا ہے کہ ان کے دل ڈر جاتے ہیں اور جب انہیں اس کی آیتیں پڑھ کر سنائی جاتی ہیں تو ان کا ایمان اور بڑھ جاتا ہے۔ اور وہ اپنے پروردگار پر بھروسہ رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(8,3,'8:3','(اور) وہ جو نماز پڑھتے ہیں اور جو مال ہم نے ان کو دیا ہے اس میں سے (نیک کاموں میں) خرچ کرتے ہیں');
INSERT INTO "jalandhari" VALUES(8,4,'8:4','یہی سچے مومن ہیں اور ان کے لیے پروردگار کے ہاں (بڑے بڑے درجے) اور بخشش اور عزت کی روزی ہے');
INSERT INTO "jalandhari" VALUES(8,5,'8:5','(ان لوگوں کو اپنے گھروں سے اسی طرح نکلنا چاہیئے تھا) جس طرح تمہارے پروردگار نے تم کو تدبیر کے ساتھ اپنے گھر سے نکالا اور (اس وقت) مومنوں ایک جماعت ناخوش تھی');
INSERT INTO "jalandhari" VALUES(8,6,'8:6','وہ لوگ حق بات میں اس کے ظاہر ہوئے پیچھے تم سے جھگڑنے لگے گویا موت کی طرف دھکیلے جاتے ہیں اور اسے دیکھ رہے ہیں');
INSERT INTO "jalandhari" VALUES(8,7,'8:7','اور (اس وقت کو یاد کرو) جب خدا تم سے وعدہ کرتا تھا کہ (ابوسفیان اور ابوجہل کے) دو گروہوں میں سے ایک گروہ تمہارا (مسخر) ہوجائے گا۔ اور تم چاہتے تھے کہ جو قافلہ بے (شان و) شوکت (یعنی بے ہتھیار ہے) وہ تمہارے ہاتھ آجائے اور خدا چاہتا تھا کہ اپنے فرمان سے حق کو قائم رکھے اور کافروں کی جڑ کاٹ کر (پھینک) دے');
INSERT INTO "jalandhari" VALUES(8,8,'8:8','تاکہ سچ کو سچ اور جھوٹ کو جھوٹ کردے۔ گو مشرک ناخوش ہی ہوں');
INSERT INTO "jalandhari" VALUES(8,9,'8:9','جب تم اپنے پروردگار سے فریاد کرتے تھے تو اس نے تمہاری دعا قبول کرلی (اور فرمایا) کہ (تسلی رکھو) ہم ہزار فرشتوں سے جو ایک دوسرے کے پیچھے آتے جائیں گے تمہاری مدد کریں گے');
INSERT INTO "jalandhari" VALUES(8,10,'8:10','اور اس مدد کو خدا نے محض بشارت بنایا تھا کہ تمہارے دل سے اطمینان حاصل کریں۔ اور مدد تو الله ہی کی طرف سے ہے۔ بےشک خدا غالب حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(8,11,'8:11','جب اس نے (تمہاری) تسکین کے لیے اپنی طرف سے تمہیں نیند (کی چادر) اُڑھا دی اور تم پر آسمان سے پانی برسادیا تاکہ تم کو اس سے (نہلا کر) پاک کر دے اور شیطانی نجاست کو تم سے دور کردے۔ اور اس لیے بھی کہ تمہارے دلوں کو مضبوط کردے اور اس سے تمہارے پاؤں جمائے رکھے');
INSERT INTO "jalandhari" VALUES(8,12,'8:12','جب تمہارا پروردگار فرشتوں کو ارشاد فرماتا تھا کہ میں تمہارے ساتھ ہوں تم مومنوں کو تسلی دو کہ ثابت قدم رہیں۔ میں ابھی ابھی کافروں کے دلوں میں رعب وہیبت ڈالے دیتا ہوں تو ان کے سر مار (کر) اڑا دو اور ان کا پور پور مار (کر توڑ) دو');
INSERT INTO "jalandhari" VALUES(8,13,'8:13','یہ (سزا) اس لیے دی گئی کہ انہوں نے خدا اور اس کے رسول کی مخالفت کی۔ اور جو شخص خدا اور اس کے رسول کی مخالفت کرتا ہے تو خدا بھی سخت عذاب دینے والا ہے');
INSERT INTO "jalandhari" VALUES(8,14,'8:14','یہ (مزہ تو یہاں) چکھو اور یہ (جانے رہو) کہ کافروں کے لیے (آخرت میں) دوزخ کا عذاب (بھی تیار) ہے');
INSERT INTO "jalandhari" VALUES(8,15,'8:15','اے اہل ایمان جب میدان جنگ میں کفار سے تمہار مقابلہ ہو تو ان سے پیٹھ نہ پھیرنا');
INSERT INTO "jalandhari" VALUES(8,16,'8:16','اور جو شخص جنگ کے روز اس صورت کے سوا کہ لڑائی کے لیے کنارے کنارے چلے (یعنی حکمت عملی سے دشمن کو مارے) یا اپنی فوج میں جا ملنا چاہے۔ ان سے پیٹھ پھیرے گا تو (سمجھو کہ) وہ خدا کے غضب میں گرفتار ہوگیا اور اس کا ٹھکانا دوزخ ہے۔ اور وہ بہت ہی بری جگہ ہے');
INSERT INTO "jalandhari" VALUES(8,17,'8:17','تم لوگوں نے ان (کفار) کو قتل نہیں کیا بلکہ خدا نے انہیں قتل کیا۔ اور (اے محمدصلى الله عليه وسلم) جس وقت تم نے کنکریاں پھینکی تھیں تو وہ تم نے نہیں پھینکی تھیں بلکہ الله نے پھینکی تھیں۔ اس سے یہ غرض تھی کہ مومنوں کو اپنے (احسانوں) سے اچھی طرح آزمالے۔ بےشک خدا سنتا جانتا ہے');
INSERT INTO "jalandhari" VALUES(8,18,'8:18','(بات) یہ (ہے) کچھ شک نہیں کہ خدا کافروں کی تدبیر کو کمزور کر دینے والا ہے');
INSERT INTO "jalandhari" VALUES(8,19,'8:19','(کافرو) اگر تم (محمد صلی الله علیہ وآلہ وسلم پر) فتح چاہتے ہو تو تمہارے پاس فتح آچکی۔ (دیکھو) اگر تم (اپنے افعال سے) باز آجاؤ تو تمہارے حق میں بہتر ہے۔ اور اگر پھر (نافرمانی) کرو گے تو ہم بھی پھر تمہیں عذاب کریں گے اور تمہاری جماعت خواہ کتنی ہی کثیر ہو تمہارے کچھ بھی کام نہ آئے گی۔ اور خدا تو مومنوں کے ساتھ ہے');
INSERT INTO "jalandhari" VALUES(8,20,'8:20','اے ایمان والو! خدا اور اس کے رسول کے حکم پر چلو اور اس سے روگردانی نہ کرو اور تم سنتے ہو');
INSERT INTO "jalandhari" VALUES(8,21,'8:21','اور ان لوگوں جیسے نہ ہونا جو کہتے ہیں کہ ہم نے حکم (خدا) سن لیا مگر (حقیقت میں) نہیں سنتے');
INSERT INTO "jalandhari" VALUES(8,22,'8:22','کچھ شک نہیں کہ خدا کے نزدیک تمام جانداروں سے بدتر بہرے گونگے ہیں جو کچھ نہیں سمجھتے');
INSERT INTO "jalandhari" VALUES(8,23,'8:23','اور اگر خدا ان میں نیکی (کا مادہ) دیکھتا تو ان کو سننے کی توفیق بخشتا۔ اور اگر (بغیر صلاحیت ہدایت کے) سماعت دیتا تو وہ منہ پھیر کر بھاگ جاتے');
INSERT INTO "jalandhari" VALUES(8,24,'8:24','مومنو! خدا اور اس کے رسول کا حکم قبول کرو جب کہ رسول خدا تمہیں ایسے کام کے لیے بلاتے ہیں جو تم کو زندگی (جاوداں) بخشتا ہے۔ اور جان رکھو کہ خدا آدمی اور اس کے دل کے درمیان حامل ہوجاتا ہے اور یہ بھی کہ تم سب اس کے روبرو جمع کیے جاؤ گے');
INSERT INTO "jalandhari" VALUES(8,25,'8:25','اور اس فتنے سے ڈرو جو خصوصیت کے ساتھ انہیں لوگوں پر واقع نہ ہوگا جو تم میں گنہگار ہیں۔ اور جان رکھو کہ خدا سخت عذاب دینے والا ہے');
INSERT INTO "jalandhari" VALUES(8,26,'8:26','اور اس وقت کو یاد کرو جب تم زمین (مکہ) میں قلیل اور ضعیف سمجھے جاتے تھے اور ڈرتے رہتے تھے کہ لوگ تمہیں اُڑا (نہ) لے جائیں (یعنی بےخان وماں نہ کردیں) تو اس نے تم کو جگہ دی اور اپنی مدد سے تم کو تقویت بخشی اور پاکیزہ چیزیں کھانے کو دیں تاکہ (اس کا) شکر کرو');
INSERT INTO "jalandhari" VALUES(8,27,'8:27','اے ایمان والو! نہ تو خدا اور رسول کی امانت میں خیانت کرو اور نہ اپنی امانتوں میں خیانت کرو اور تم (ان باتوں کو) جانتے ہو');
INSERT INTO "jalandhari" VALUES(8,28,'8:28','اور جان رکھو کہ تمہارا مال اور اولاد بڑی آزمائش ہے اور یہ کہ خدا کے پاس (نیکیوں کا) بڑا ثواب ہے');
INSERT INTO "jalandhari" VALUES(8,29,'8:29','مومنو! اگر تم خدا سے ڈرو گے تو وہ تمہارے لیے امر فارق پیدا کردے گا (یعنی تم کو ممتاز کردے گا) تو وہ تمہارے گناہ مٹادے گا اور تمہیں بخش دے گا۔ اور خدا بڑا فضل والا ہے');
INSERT INTO "jalandhari" VALUES(8,30,'8:30','اور (اے محمدصلى الله عليه وسلم اس وقت کو یاد کرو) جب کافر لوگ تمہارے بارے میں چال چل رہے تھے کہ تم کو قید کر دیں یا جان سے مار ڈالیں یا (وطن سے) نکال دیں تو (ادھر تو) وہ چال چل رہے تھے اور (اُدھر) خدا چال چل رہا تھا۔ اور خدا سب سے بہتر چال چلنے والا ہے');
INSERT INTO "jalandhari" VALUES(8,31,'8:31','اور جب ان کو ہماری آیتیں پڑھ کر سنائی جاتی ہیں تو کہتے ہیں (یہ کلام) ہم نے سن لیا ہے اگر ہم چاہیں تو اسی طرح کا (کلام) ہم بھی کہہ دیں اور یہ ہے ہی کیا صرف اگلے لوگوں کی حکایتیں ہیں');
INSERT INTO "jalandhari" VALUES(8,32,'8:32','اور جب انہوں نے کہا کہ اے خدا اگر یہ (قرآن) تیری طرف سے برحق ہے تو ہم پر آسمان سے پتھر برسا یا کوئی اور تکلیف دینے والا عذاب بھیج');
INSERT INTO "jalandhari" VALUES(8,33,'8:33','اور خدا ایسا نہ تھا کہ جب تک تم ان میں سے تھے انہیں عذاب دیتا۔ اور ایسا نہ تھا کہ وہ بخششیں مانگیں اور انہیں عذاب دے');
INSERT INTO "jalandhari" VALUES(8,34,'8:34','اور (اب) ان کے لیے کون سی وجہ ہے کہ وہ انہیں عذاب نہ دے جب کہ وہ مسجد محترم (میں نماز پڑھنے) سے روکتے ہیں اور وہ اس مسجد کے متولی بھی نہیں۔ اس کے متولی تو صرف پرہیزگار ہیں۔ لیکن ان میں اکثر نہیں جانتے');
INSERT INTO "jalandhari" VALUES(8,35,'8:35','اور ان لوگوں کی نماز خانہٴ کعبہ کے پاس سیٹیاں اور تالیاں بجانے کے سوا کچھ نہ تھی۔ تو تم جو کفر کرتے تھے اب اس کے بدلے عذاب (کا مزہ) چکھو');
INSERT INTO "jalandhari" VALUES(8,36,'8:36','جو لوگ کافر ہیں اپنا مال خرچ کرتے ہیں کہ (لوگوں کو) خدا کے رستے سے روکیں۔ سو ابھی اور خرچ کریں گے مگر آخر وہ (خرچ کرنا) ان کے لیے (موجب) افسوس ہوگا اور وہ مغلوب ہوجائیں گے۔ اور کافر لوگ دوزخ کی طرف ہانکے جائیں گے');
INSERT INTO "jalandhari" VALUES(8,37,'8:37','تاکہ خدا ناپاک کو پاک سے الگ کر دے اور ناپاک کو ایک دوسرے پر رکھ کر ایک ڈھیر بنا دے۔ پھر اس کو دوزخ میں ڈال دے۔ یہی لوگ خسارہ پانے والے ہیں');
INSERT INTO "jalandhari" VALUES(8,38,'8:38','(اے پیغمبر) کفار سے کہہ دو کہ اگر وہ اپنے افعال سے باز آجائیں تو جو ہوچکا وہ انہیں معاف کردیا جائے گا۔ اور اگر پھر (وہی حرکات) کرنے لگیں گے تو اگلے لوگوں کا (جو) طریق جاری ہوچکا ہے (وہی ان کے حق میں برتا جائے گا)');
INSERT INTO "jalandhari" VALUES(8,39,'8:39','اور ان لوگوں سے لڑتے رہو یہاں تک کہ فتنہ (یعنی کفر کا فساد) باقی نہ رہے اور دین سب خدا ہی کا ہوجائے اور اگر باز آجائیں تو خدا ان کے کاموں کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(8,40,'8:40','اور اگر روگردانی کریں تو جان رکھو کہ خدا تمہارا حمایتی ہے۔ (اور) وہ خوب حمایتی اور خوب مددگار ہے');
INSERT INTO "jalandhari" VALUES(8,41,'8:41','اور جان رکھو کہ جو چیز تم (کفار سے) لوٹ کر لاؤ اس میں سے پانچواں حصہ خدا کا اور اس کے رسول کا اور اہل قرابت کا اور یتیموں کا اور محتاجوں کا اور مسافروں کا ہے۔ اگر تم خدا پر اور اس (نصرت) پر ایمان رکھتے ہو جو (حق وباطل میں) فرق کرنے کے دن (یعنی جنگ بدر میں) جس دن دونوں فوجوں میں مڈھ بھیڑ ہوگئی۔ اپنے بندے (محمدصلى الله عليه وسلم) پر نازل فرمائی۔ اور خدا ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(8,42,'8:42','جس وقت تم (مدینے سے) قریب کے ناکے پر تھے اور کافر بعید کے ناکے پر اور قافلہ تم سے نیچے (اتر گیا) تھا۔ اور اگر تم (جنگ کے لیے) آپس میں قرارداد کرلیتے تو وقت معین (پر جمع ہونے) میں تقدیم وتاخیر ہو جاتی۔ لیکن خدا کو منظور تھا کہ جو کام ہو کر رہنے والا تھا اسے کر ہی ڈالے تاکہ جو مرے بصیرت پر (یعنی یقین جان کر) مرے اور جو جیتا رہے وہ بھی بصیرت پر (یعنی حق پہچان کر) جیتا رہے۔ اور کچھ شک نہیں کہ خدا سنتا جانتا ہے');
INSERT INTO "jalandhari" VALUES(8,43,'8:43','اس وقت خدا نے تمہیں خواب میں کافروں کو تھوڑی تعداد میں دکھایا۔ اور اگر بہت کر کے دکھاتا تو تم لوگ جی چھوڑ دیتے اور (جو) کام (درپیش تھا اس) میں جھگڑنے لگتے لیکن خدا نے (تمہیں اس سے) بچا لیا۔ بےشک وہ سینوں کی باتوں تک سے واقف ہے');
INSERT INTO "jalandhari" VALUES(8,44,'8:44','اور اس وقت جب تم ایک دوسرے کے مقابل ہوئے تو کافروں کو تمہاری نظروں میں تھوڑا کر کے دکھاتا تھا اور تم کو ان کی نگاہوں میں تھوڑا کر کے دکھاتا تھا تاکہ خدا کو جو کام منظور کرنا تھا اسے کر ڈالے۔ اور سب کاموں کا رجوع خدا کی طرف ہے');
INSERT INTO "jalandhari" VALUES(8,45,'8:45','مومنو! جب (کفار کی) کسی جماعت سے تمہارا مقابلہ ہو تو ثابت قدم رہو اور خدا کو بہت یاد کرو تاکہ مراد حاصل کرو');
INSERT INTO "jalandhari" VALUES(8,46,'8:46','اور خدا اور اس کے رسول کے حکم پر چلو اور آپس میں جھگڑا نہ کرنا کہ (ایسا کرو گے تو) تم بزدل ہو جاؤ گے اور تمہارا اقبال جاتا رہے گا اور صبر سے کام لو۔ کہ خدا صبر کرنے والوں کا مددگار ہے');
INSERT INTO "jalandhari" VALUES(8,47,'8:47','اور ان لوگوں جیسے نہ ہونا جو اِتراتے ہوئے (یعنی حق کا مقابلہ کرنے کے لیے) اور لوگوں کو دکھانے کے لیے گھروں سے نکل آئے اور لوگوں کو خدا کی راہ سے روکتے ہیں۔ اور جو اعمال یہ کرتے ہیں خدا ان پر احاطہ کئے ہوئے ہے');
INSERT INTO "jalandhari" VALUES(8,48,'8:48','اور جب شیطانوں نے ان کے اعمال ان کو آراستہ کر کے دکھائے اور کہا کہ آج کے دن لوگوں میں کوئی تم پر غالب نہ ہوگا اور میں تمہارا رفیق ہوں (لیکن) جب دونوں فوجیں ایک دوسرے کے مقابل صف آراء ہوئیں تو پسپا ہو کر چل دیا اور کہنے لگا کہ مجھے تم سے کوئی واسطہ نہیں۔ میں تو ایسی چیزیں دیکھ رہا ہوں جو تم نہیں دیکھ سکتے۔ مجھے تو خدا سے ڈر لگتا ہے۔ اور خدا سخت عذاب کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(8,49,'8:49','اس وقت منافق اور (کافر) جن کے دلوں میں مرض تھا کہتے تھے کہ ان لوگوں کو ان کے دین نے مغرور کر رکھا ہے اور جو شخص خدا پر بھروسہ رکھتا ہے تو خدا غالب حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(8,50,'8:50','اور کاش تم اس وقت (کی کیفیت) دیکھو۔ جب فرشتے کافروں کی جانیں نکالتے ہیں ان کے مونہوں اور پیٹھوں پر (کوڑے اور ہتھوڑے وغیرہ) مارتے (ہیں اور کہتے ہیں) کہ (اب) عذاب آتش (کا مزہ) چکھو');
INSERT INTO "jalandhari" VALUES(8,51,'8:51','یہ ان (اعمال) کی سزا ہے جو تمہارے ہاتھوں نے آگے بھیجے ہیں۔ اور یہ (جان رکھو) کہ خدا بندوں پر ظلم نہیں کرتا');
INSERT INTO "jalandhari" VALUES(8,52,'8:52','جیسا حال فرعوینوں اور ان سے پہلے لوگوں کا (ہوا تھا ویسا ہی ان کا ہوا کہ) انہوں نے خدا کی آیتوں سے کفر کیا تو خدا نےان کے گناہوں کی سزا میں ان کو پکڑ لیا۔ بےشک خدا زبردست اور سخت عذاب دینے والا ہے');
INSERT INTO "jalandhari" VALUES(8,53,'8:53','یہ اس لیے کہ جو نعمت خدا کسی قوم کو دیا کرتا ہے جب تک وہ خود اپنے دلوں کی حالت نہ بدل ڈالیں خدا اسے نہیں بدلا کرتا۔ اور اس لیے کہ خدا سنتا جانتا ہے');
INSERT INTO "jalandhari" VALUES(8,54,'8:54','جیسا حال فرعونیوں اور ان سے پہلے لوگوں کا (ہوا تھا ویسا ہی ان کا ہوا) انہوں نے اپنے پروردگار کی آیتوں کو جھٹلایا تو ہم نے ان کو ان کے گناہوں کے سبب ہلاک کر ڈالا اور فرعونیوں کو ڈبو دیا۔ اور وہ سب ظالم تھے');
INSERT INTO "jalandhari" VALUES(8,55,'8:55','جانداروں میں سب سے بدتر خدا کے نزدیک وہ لوگ ہیں جو کافر ہیں سو وہ ایمان نہیں لاتے');
INSERT INTO "jalandhari" VALUES(8,56,'8:56','جن لوگوں سے تم نے (صلح کا) عہد کیا ہے پھر وہ ہر بار اپنے عہد کو توڑ ڈالتے ہیں اور (خدا سے) نہیں ڈرتے');
INSERT INTO "jalandhari" VALUES(8,57,'8:57','اگر تم ان کو لڑائی میں پاؤ تو انہیں ایسی سزا دو کہ جو لوگ ان کے پس پشت ہیں وہ ان کو دیکھ کر بھاگ جائیں عجب نہیں کہ ان کو (اس سے) عبرت ہو');
INSERT INTO "jalandhari" VALUES(8,58,'8:58','اور اگر تم کو کسی قوم سے دغا بازی کا خوف ہو تو (ان کا عہد) انہیں کی طرف پھینک دو (اور) برابر (کا جواب دو) کچھ شک نہیں کہ خدا دغابازوں کو دوست نہیں رکھتا');
INSERT INTO "jalandhari" VALUES(8,59,'8:59','اور کافر یہ نہ خیال کریں کہ وہ بھاگ نکلے ہیں۔ وہ (اپنی چالوں سے ہم کو) ہرگز عاجز نہیں کرسکتے');
INSERT INTO "jalandhari" VALUES(8,60,'8:60','اور جہاں تک ہوسکے (فوج کی جمعیت کے) زور سے اور گھوڑوں کے تیار رکھنے سے ان کے (مقابلے کے) لیے مستعد رہو کہ اس سے خدا کے دشمنوں اور تمہارے دشمنوں اور ان کے سوا اور لوگوں پر جن کو تم نہیں جانتے اور خدا جانتا ہے ہیبت بیٹھی رہے گی۔ اور تم جو کچھ راہ خدا میں خرچ کرو گے اس کا ثواب تم کو پورا پورا دیا جائے گا اور تمہارا ذرا نقصان نہیں کیا جائے گا');
INSERT INTO "jalandhari" VALUES(8,61,'8:61','اور اگر یہ لوگ صلح کی طرف مائل ہوں تو تم بھی اس کی طرف مائل ہو جاؤ اور خدا پر بھروسہ رکھو۔ کچھ شک نہیں کہ وہ سب کچھ سنتا (اور) جانتا ہے');
INSERT INTO "jalandhari" VALUES(8,62,'8:62','اور اگر یہ چاہیں کہ تم کو فریب دیں تو خدا تمہیں کفایت کرے گا۔ وہی تو ہے جس نے تم کو اپنی مدد سے اور مسلمانوں (کی جمعیت) سے تقویت بخشی');
INSERT INTO "jalandhari" VALUES(8,63,'8:63','اور ان کے دلوں میں الفت پیدا کردی۔ اور اگر تم دنیا بھر کی دولت خرچ کرتے تب بھی ان کے دلوں میں الفت نہ پیدا کرسکتے۔ مگر خدا ہی نے ان میں الفت ڈال دی۔ بےشک وہ زبردست (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(8,64,'8:64','اے نبی! خدا تم کو اور مومنوں کو جو تمہارے پیرو ہیں کافی ہے');
INSERT INTO "jalandhari" VALUES(8,65,'8:65','اے نبی! مسلمانوں کو جہاد کی ترغیب دو۔ اور اگر تم بیس آدمی ثابت قدم رہنے والے ہوں گے تو دو سو کافروں پر غالب رہیں گے۔ اور اگر سو (ایسے) ہوں گے تو ہزار پر غالب رہیں گے۔ اس لیے کہ کافر ایسے لوگ ہیں کہ کچھ بھی سمجھ نہیں رکھتے');
INSERT INTO "jalandhari" VALUES(8,66,'8:66','اب خدا نے تم پر سے بوجھ ہلکا کر دیا اور معلوم کرلیا کہ (ابھی) تم میں کسی قدر کمزوری ہے۔ پس اگر تم میں ایک سو ثابت قدم رہنے والے ہوں گے تو دو سو پر غالب رہیں گے۔ اور اگر ایک ہزار ہوں گے تو خدا کے حکم سے دو ہزار پر غالب رہیں گے۔ اور خدا ثابت قدم رہنے والوں کا مدد گار ہے');
INSERT INTO "jalandhari" VALUES(8,67,'8:67','پیغمبر کو شایان نہیں کہ اس کے قبضے میں قیدی رہیں جب تک (کافروں کو قتل کر کے) زمین میں کثرت سے خون (نہ) بہا دے۔ تم لوگ دنیا کے مال کے طالب ہو۔ اور خدا آخرت (کی بھلائی) چاہتا ہے۔ اور خدا غالب حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(8,68,'8:68','اگر خدا کا حکم پہلے نہ ہوچکا ہوتا تو جو (فدیہ) تم نے لیا ہے اس کے بدلے تم پر بڑا عذاب نازل ہوتا');
INSERT INTO "jalandhari" VALUES(8,69,'8:69','تو جو مالِ غنیمت تمہیں ملا ہے اسے کھاؤ (کہ وہ تمہارے لیے) حلال طیب رہے اور خدا سے ڈرتے رہو۔ بےشک خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(8,70,'8:70','اے پیغمبر جو قیدی تمہارے ہاتھ میں (گرفتار) ہیں ان سے کہہ دو کہ اگر خدا تمہارے دلوں میں نیکی معلوم کرے گا تو جو (مال) تم سے چھن گیا ہے اس سے بہتر تمہیں عنایت فرمائے گا اور تمہارے گناہ بھی معاف کر دے گا اور خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(8,71,'8:71','اور اگر یہ لوگ تم سے دغا کرنا چاہیں گے تو یہ پہلے ہی خدا سے دغا کرچکے ہیں تو اس نے ان کو (تمہارے) قبضے میں کر دیا۔ اور خدا دانا حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(8,72,'8:72','جو لوگ ایمان لائے اور وطن سے ہجرت کر گئے اور خدا کی راہ میں اپنے مال اور جان سے لڑے وہ اور جنہوں نے (ہجرت کرنے والوں کو) جگہ دی اور ان کی مدد کی وہ آپس میں ایک دوسرے کے رفیق ہیں۔ اور جو لوگ ایمان تو لے آئے لیکن ہجرت نہیں کی تو جب تک وہ ہجرت نہ کریں تم کو ان کی رفاقت سے کچھ سروکار نہیں۔ اور اگر وہ تم سے دین (کے معاملات) میں مدد طلب کریں تو تم کو مدد کرنی لازم ہوگی۔ مگر ان لوگوں کے مقابلے میں کہ تم میں اور ان میں (صلح کا) عہد ہو (مدد نہیں کرنی چاہیئے) اور خدا تمہارے سب کاموں کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(8,73,'8:73','اور جو لوگ کافر ہیں (وہ بھی) ایک دوسرے کے رفیق ہیں۔ تو (مومنو) اگر تم یہ (کام) نہ کرو گے تو ملک میں فتنہ برپا ہو جائے گا اور بڑا فساد مچے گا');
INSERT INTO "jalandhari" VALUES(8,74,'8:74','اور جو لوگ ایمان لائے اور وطن سے ہجرت کر گئے اور خدا کی راہ میں لڑائیاں کرتے رہے اور جنہوں نے (ہجرت کرنے والوں کو) جگہ دی اور ان کی مدد کی۔ یہی لوگ سچے مسلمان ہیں۔ ان کے لیے (خدا کے ہاں) بخشش اور عزت کی روزی ہے');
INSERT INTO "jalandhari" VALUES(8,75,'8:75','اور جو لوگ بعد میں ایمان لائے اور وطن سے ہجرت کرگئے اور تمہارے ساتھ ہو کر جہاد کرتے رہے وہ بھی تم ہی میں سے ہیں۔ اور رشتہ دار خدا کے حکم کی رو سے ایک دوسرے کے زیادہ حقدار ہیں۔ کچھ شک نہیں کہ خدا ہر چیز سے واقف ہے');
INSERT INTO "jalandhari" VALUES(9,1,'9:1','(اے اہل اسلام اب) خدا اور اس کے رسول کی طرف سے مشرکوں سے جن سے تم نے عہد کر رکھا تھا بیزاری (اور جنگ کی تیاری) ہے');
INSERT INTO "jalandhari" VALUES(9,2,'9:2','تو (مشرکو تم) زمین میں چار مہینے چل پھر لو اور جان رکھو کہ تم خدا کو عاجز نہ کرسکو گے۔ اور یہ بھی کہ خدا کافروں کو رسوا کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(9,3,'9:3','اور حج اکبر کے دن خدا اور اس کے رسول کی طرف سے لوگوں کو آگاہ کیا جاتا ہے کہ خدا مشرکوں سے بیزار ہے اور اس کا رسول بھی (ان سے دستبردار ہے)۔ پس اگر تم توبہ کرلو تو تمھارے حق میں بہتر ہے۔ اور اگر نہ مانو (اور خدا سے مقابلہ کرو) تو جان رکھو کہ تم خدا کو ہرا نہیں سکو گے اور (اے پیغمبر) کافروں کو دکھ دینے والے عذاب کی خبر سنا دو');
INSERT INTO "jalandhari" VALUES(9,4,'9:4','البتہ جن مشرکوں کے ساتھ تم نے عہد کیا ہو اور انہوں نے تمہارا کسی طرح کا قصور نہ کیا ہو اور نہ تمہارے مقابلے میں کسی کی مدد کی ہو تو جس مدت تک ان کے ساتھ عہد کیا ہو اسے پورا کرو۔ (کہ) خدا پرہیزگاروں کو دوست رکھتا ہے');
INSERT INTO "jalandhari" VALUES(9,5,'9:5','جب عزت کے مہینے گزر جائیں تو مشرکوں کو جہاں پاؤ قتل کر دو اور پکڑلو اور گھیرلو اور ہر گھات کی جگہ ان کی تاک میں بیٹھے رہو۔ پھر اگر وہ توبہ کرلیں اور نماز پڑھنے اور زکوٰة دینے لگیں تو ان کی راہ چھوڑ دو۔ بےشک خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(9,6,'9:6','اور اگر کوئی مشرک تم سے پناہ کا خواستگار ہو تو اس کو پناہ دو یہاں تک کہ کلام خدا سننے لگے پھر اس کو امن کی جگہ واپس پہنچادو۔ اس لیے کہ یہ بےخبر لوگ ہیں');
INSERT INTO "jalandhari" VALUES(9,7,'9:7','بھلا مشرکوں کے لیے (جنہوں نے عہد توڑ ڈالا) خدا اور اس کے رسول کے نزدیک عہد کیونکر (قائم) رہ سکتا ہے ہاں جن لوگوں کے ساتھ تم نے مسجد محترم (یعنی خانہ کعبہ) کے نزدیک عہد کیا ہے اگر وہ (اپنے عہد پر) قائم رہیں تو تم بھی اپنے قول وقرار (پر) قائم رہو۔ بےشک خدا پرہیز گاروں کو دوست رکھتا ہے');
INSERT INTO "jalandhari" VALUES(9,8,'9:8','(بھلا ان سے عہد) کیونکر (پورا کیا جائے جب ان کا یہ حال ہے) کہ اگر تم پر غلبہ پالیں تو نہ قرابت کا لحاظ کریں نہ عہد کا۔ یہ منہ سے تو تمہیں خوش کر دیتے ہیں لیکن ان کے دل (ان باتوں کو) قبول نہیں کرتے۔ اور ان میں اکثر نافرمان ہیں');
INSERT INTO "jalandhari" VALUES(9,9,'9:9','یہ خدا کی آیتوں کے عوض تھوڑا سا فائدہ حاصل کرتے اور لوگوں کو خدا کے رستے سے روکتے ہیں۔ کچھ شک نہیں کہ جو کام یہ کرتے ہیں برے ہیں');
INSERT INTO "jalandhari" VALUES(9,10,'9:10','یہ لوگ کسی مومن کے حق میں نہ تو رشتہ داری کا پاس کرتے ہیں نہ عہد کا۔ اور یہ حد سے تجاوز کرنے والے ہیں');
INSERT INTO "jalandhari" VALUES(9,11,'9:11','اگر یہ توبہ کرلیں اور نماز پڑھنے اور زکوٰة دینے لگیں تو دین میں تمہارے بھائی ہیں۔ اور سمجھنے والے لوگوں کے لیے ہم اپنی آیتیں کھول کھول کر بیان کرتے ہیں');
INSERT INTO "jalandhari" VALUES(9,12,'9:12','اور اگر عہد کرنے کے بعد اپنی قسموں کو توڑ ڈالیں اور تمہارے دین میں طعنے کرنے لگیں تو ان کفر کے پیشواؤں سے جنگ کرو (یہ یہ بےایمان لوگ ہیں اور) ان کی قسموں کا کچھ اعتبار نہیں ہے۔ عجب نہیں کہ (اپنی حرکات سے) باز آجائیں');
INSERT INTO "jalandhari" VALUES(9,13,'9:13','بھلا تم ایسے لوگوں سے کیوں نہ لڑو جنہوں نے اپنی قسموں کو توڑ ڈالا اور پیغمبر (خدا) کے جلا وطن کرنے کا عزم مصمم کر لیا اور انہوں نے تم سے (عہد شکنی کی) ابتدا کی۔ کیا تم ایسے لوگوں سے ڈرتے ہو حالانکہ ڈرنے کے لائق خدا ہے بشرطیکہ ایمان رکھتے ہو');
INSERT INTO "jalandhari" VALUES(9,14,'9:14','ان سے (خوب) لڑو۔ خدا ان کو تمہارے ہاتھوں سے عذاب میں ڈالے گا اور رسوا کرے گا اور تم کو ان پر غلبہ دے گا اور مومن لوگوں کے سینوں کو شفا بخشے گا');
INSERT INTO "jalandhari" VALUES(9,15,'9:15','اور ان کے دلوں سے غصہ دور کرے گا اور جس پر چاہے گا رحمت کرے گا۔ اور خدا سب کچھ جانتا (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(9,16,'9:16','کیا تم لوگ یہ خیال کرتے ہو کہ (بےآزمائش) چھوڑ دیئے جاؤ گے اور ابھی خدا نے ایسے لوگوں کو متمیز کیا ہی نہیں جنہوں نے تم میں سے جہاد کئے اور خدا اور اس کے رسول اور مومنوں کے سوا کسی کو دلی دوست نہیں بنایا۔ اور خدا تمہارے سب کاموں سے واقف ہے');
INSERT INTO "jalandhari" VALUES(9,17,'9:17','مشرکوں کی زیبا نہیں کہ خدا کی مسجدوں کو آباد کریں جب کہ وہ اپنے آپ پر کفر کی گواہی دے رہے ہیں۔ ان لوگوں کے سب اعمال بےکار ہیں اور یہ ہمیشہ دوزخ میں رہیں گے');
INSERT INTO "jalandhari" VALUES(9,18,'9:18','خدا کی مسجدوں کو تو وہ لوگ آباد کرتے ہیں جو خدا پر اور روز قیامت پر ایمان لاتے ہیں اور نماز پڑھتے اور زکواة دیتے ہیں اور خدا کے سوا کسی سے نہیں ڈرتے۔ یہی لوگ امید ہے کہ ہدایت یافتہ لوگوں میں (داخل) ہوں');
INSERT INTO "jalandhari" VALUES(9,19,'9:19','کیا تم نے حاجیوں کو پانی پلانا اور مسجد محترم یعنی (خانہٴ کعبہ) کو آباد کرنا اس شخص کے اعمال جیسا خیال کیا ہے جو خدا اور روز آخرت پر ایمان رکھتا ہے اور خدا کی راہ میں جہاد کرتا ہے۔ یہ لوگ خدا کے نزدیک برابر نہیں ہیں۔ اور خدا ظالم لوگوں کو ہدایت نہیں دیا کرتا');
INSERT INTO "jalandhari" VALUES(9,20,'9:20','جو لوگ ایمان لائے اور وطن چھوڑ گئے اور خدا کی راہ میں مال اور جان سے جہاد کرتے رہے۔ خدا کے ہاں ان کے درجے بہت بڑے ہیں۔ اور وہی مراد کو پہنچنے والے ہیں');
INSERT INTO "jalandhari" VALUES(9,21,'9:21','ان کا پروردگار ان کو اپنی رحمت کی اور خوشنودی کی اور بہشتوں کی خوشخبری دیتا ہے جن میں ان کے لیے نعمت ہائے جاودانی ہے');
INSERT INTO "jalandhari" VALUES(9,22,'9:22','(اور وہ) ان میں ابدالاآباد رہیں گے۔ کچھ شک نہیں کہ خدا کے ہاں بڑا صلہ (تیار) ہے');
INSERT INTO "jalandhari" VALUES(9,23,'9:23','اے اہل ایمان! اگر تمہارے (ماں) باپ اور (بہن) بھائی ایمان کے مقابل کفر کو پسند کریں تو ان سے دوستی نہ رکھو۔ اور جو ان سے دوستی رکھیں گے وہ ظالم ہیں');
INSERT INTO "jalandhari" VALUES(9,24,'9:24','کہہ دو کہ اگر تمہارے باپ اور بیٹے اور بھائی اور عورتیں اور خاندان کے آدمی اور مال جو تم کماتے ہو اور تجارت جس کے بند ہونے سے ڈرتے ہو اور مکانات جن کو پسند کرتے ہو خدا اور اس کے رسول سے اور خدا کی راہ میں جہاد کرنے سے تمہیں زیادہ عزیز ہوں تو ٹھہرے رہو یہاں تک کہ خدا اپنا حکم (یعنی عذاب) بھیجے۔ اور خدا نافرمان لوگوں کو ہدایت نہیں دیا کرتا');
INSERT INTO "jalandhari" VALUES(9,25,'9:25','خدا نے بہت سے موقعوں پر تم کو مدد دی ہے اور (جنگ) حنین کے دن۔ جب تم کو اپنی (جماعت کی) کثرت پر غرّہ تھا تو وہ تمہارے کچھ بھی کام نہ آئی۔ اور زمین باوجود (اتنی بڑی) فراخی کے تم پر تنگ ہوگئی پھر تم پیٹھ پھیر کر پھر گئے');
INSERT INTO "jalandhari" VALUES(9,26,'9:26','پھر خدا نے اپنے پیغمبر پر اور مومنوں پر اپنی طرف سے تسکین نازل فرمائی (اور تمہاری مدد کو فرشتوں کے) لشکر جو تمہیں نظر نہیں آتے تھے (آسمان سے) اُتارے اور کافروں کو عذاب دیا۔ اور کفر کرنے والوں کی یہی سزا ہے');
INSERT INTO "jalandhari" VALUES(9,27,'9:27','پھر خدا اس کے بعد جس پر چاہے مہربانی سے توجہ فرمائے اور خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(9,28,'9:28','مومنو! مشرک تو پلید ہیں تو اس برس کے بعد وہ خانہٴ کعبہ کا پاس نہ جانے پائیں اور اگر تم کو مفلسی کا خوف ہو تو خدا چاہے گا تو تم کو اپنے فضل سے غنی کر دے گا۔ بےشک خدا سب کچھ جانتا (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(9,29,'9:29','جو اہل کتاب میں سے خدا پر ایمان نہیں لاتے اور نہ روز آخرت پر (یقین رکھتے ہیں) اور نہ ان چیزوں کو حرام سمجھتے ہیں جو خدا اور اس کے رسول نے حرام کی ہیں اور نہ دین حق کو قبول کرتے ہیں ان سے جنگ کرو یہاں تک کہ ذلیل ہوکر اپنے ہاتھ سے جزیہ دیں');
INSERT INTO "jalandhari" VALUES(9,30,'9:30','اور یہود کہتے ہیں کہ عُزیر خدا کے بیٹے ہیں اور عیسائی کہتے ہیں کہ مسیح خدا کے بیٹے ہیں۔ یہ ان کے منہ کی باتیں ہیں پہلے کافر بھی اسی طرح کی باتیں کہا کرتے تھے یہ بھی انہیں کی ریس کرنے میں لگے ہیں۔ خدا ان کو ہلاک کرے۔ یہ کہاں بہکے پھرتے ہیں');
INSERT INTO "jalandhari" VALUES(9,31,'9:31','انہوں نے اپنے علماء اور مشائخ اور مسیح ابن مریم کو الله کے سوا خدا بنا لیا حالانکہ اُن کو یہ حکم دیا گیا تھا کہ خدائے واحد کے سوا کسی کی عبادت نہ کریں۔ اس کے سوا کوئی معبود نہیں۔ اور وہ ان لوگوں کے شریک مقرر کرنے سے پاک ہے');
INSERT INTO "jalandhari" VALUES(9,32,'9:32','یہ چاہتے ہیں کہ خدا کے نور کو اپنے منہ سے (پھونک مار کر) بجھا دیں اور خدا اپنے نور کو پورا کئے بغیر رہنے کا نہیں۔ اگرچہ کافروں کو برا ہی لگے');
INSERT INTO "jalandhari" VALUES(9,33,'9:33','وہی تو ہے جس نے اپنے پیغمبر کو ہدایت اور دین حق دے کر بھیجا تاکہ اس (دین) کو (دنیا کے) تمام دینوں پر غالب کرے۔ اگرچہ کافر ناخوش ہی ہوں');
INSERT INTO "jalandhari" VALUES(9,34,'9:34','مومنو! (اہل کتاب کے) بہت سے عالم اور مشائخ لوگوں کا مال ناحق کھاتے اور (ان کو) راہ خدا سے روکتے ہیں۔ اور جو لوگ سونا اور چاندی جمع کرتے ہیں اور اس کو خدا کے رستے میں خرچ نہیں کرتے۔ ان کو اس دن عذاب الیم کی خبر سنادو');
INSERT INTO "jalandhari" VALUES(9,35,'9:35','جس دن وہ مال دوزخ کی آگ میں (خوب) گرم کیا جائے گا۔ پھر اس سے ان (بخیلوں) کی پیشانیاں اور پہلو اور پیٹھیں داغی جائیں گی (اور کہا جائے گا) کہ یہ وہی ہے جو تم نے اپنے لیے جمع کیا تھا سو جو تم جمع کرتے تھے (اب) اس کا مزہ چکھو');
INSERT INTO "jalandhari" VALUES(9,36,'9:36','خدا کے نزدیک مہینے گنتی میں (بارہ ہیں یعنی) اس روز (سے) کہ اس نے آسمانوں اور زمین کو پیدا کیا۔ کتاب خدا میں (برس کے) بارہ مہینے (لکھے ہوئے) ہیں۔ ان میں سے چار مہینے ادب کے ہیں۔ یہی دین (کا) سیدھا راستہ ہے۔ تو ان (مہینوں) میں (قتال ناحق سے) اپنے آپ پر ظلم نہ کرنا۔ اور تم سب کے سب مشرکوں سے لڑو جیسے وہ سب کے سب تم سے لڑتے ہیں۔ اور جان رکھو کہ خدا پرہیز گاروں کے ساتھ ہے');
INSERT INTO "jalandhari" VALUES(9,37,'9:37','امن کے کس مہینے کو ہٹا کر آگے پیچھے کر دینا کفر میں اضافہ کرتا ہے اس سے کافر گمراہی میں پڑے رہتے ہیں۔ ایک سال تو اس کو حلال سمجھ لیتے ہیں اور دوسرے سال حرام۔ تاکہ ادب کے مہینوں کو جو خدا نے مقرر کئے ہیں گنتی پوری کر لیں۔ اور جو خدا نے منع کیا ہے اس کو جائز کر لیں۔ ان کے برے اعمال ان کے بھلے دکھائی دیتے ہیں۔ اور خدا کافر لوگوں کو ہدایت نہیں دیا کرتا');
INSERT INTO "jalandhari" VALUES(9,38,'9:38','مومنو! تمہیں کیا ہوا ہے کہ جب تم سے کہا جاتا ہے کہ خدا کی راہ میں (جہاد کے لیے) نکلو تو تم (کاہلی کے سبب سے) زمین پر گرے جاتے ہو (یعنی گھروں سے نکلنا نہیں چاہتے) کیا تم آخرت (کی نعمتوں) کو چھوڑ کر دینا کی زندگی پر خوش ہو بیٹھے ہو۔ دنیا کی زندگی کے فائدے تو آخرت کے مقابل بہت ہی کم ہیں');
INSERT INTO "jalandhari" VALUES(9,39,'9:39','اور اگر تم نہ نکلو گے تو خدا تم کو بڑی تکلیف کا عذاب دے گا۔ اور تمہاری جگہ اور لوگ پیدا کر دے گا (جو خدا کے پورے فرمانبردار ہوں گے) اور تم اس کو کچھ نقصان نہ پہنچا سکو گے اور خدا ہر چیز پر قدرت رکھتا ہے');
INSERT INTO "jalandhari" VALUES(9,40,'9:40','اگر تم پیغمبر کی مدد نہ کرو گے تو خدا اُن کا مددگار ہے (وہ وقت تم کو یاد ہوگا) جب ان کو کافروں نے گھر سے نکال دیا۔ (اس وقت) دو (ہی ایسے شخص تھے جن) میں (ایک ابوبکرؓ تھے) اور دوسرے (خود رسول الله) جب وہ دونوں غار (ثور) میں تھے اس وقت پیغمبر اپنے رفیق کو تسلی دیتے تھے کہ غم نہ کرو خدا ہمارے ساتھ ہے۔ تو خدا نے ان پر تسکین نازل فرمائی اور ان کو ایسے لشکروں سے مدد دی جو تم کو نظر نہیں آتے تھے اور کافروں کی بات کو پست کر دیا۔ اور بات تو خدا ہی کی بلند ہے۔ اور خدا زبردست (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(9,41,'9:41','تم سبکبار ہو یا گراں بار (یعنی مال و اسباب تھوڑا رکھتے ہو یا بہت، گھروں سے) نکل آؤ۔ اور خدا کے رستے میں مال اور جان سے لڑو۔ یہی تمہارے حق میں اچھا ہے بشرطیکہ سمجھو');
INSERT INTO "jalandhari" VALUES(9,42,'9:42','اگر مالِ غنیمت سہل الحصول اور سفر بھی ہلکا سا ہوتا تو تمہارے ساتھ (شوق سے) چل دیتے۔ لیکن مسافت ان کو دور (دراز) نظر آئی (تو عذر کریں گے)۔ اور خدا کی قسمیں کھائیں گے کہ اگر ہم طاقت رکھتے تو آپ کے ساتھ ضرور نکل کھڑے ہوتے یہ (ایسے عذروں سے) اپنے تئیں ہلاک کر رہے ہیں۔ اور خدا جانتا ہے کہ جھوٹے ہیں');
INSERT INTO "jalandhari" VALUES(9,43,'9:43','خدا تمہیں معاف کرے۔ تم نے پیشتر اس کے کہ تم پر وہ لوگ بھی ظاہر ہو جاتے ہیں جو سچے ہیں اور وہ بھی تمہیں معلوم ہو جاتے جو جھوٹے ہیں اُن کو اجازت کیوں دی');
INSERT INTO "jalandhari" VALUES(9,44,'9:44','جو لوگ خدا پر اور روز آخرت پر ایمان رکھتے ہیں وہ تو تم سے اجازت نہیں مانگتے (کہ پیچھے رہ جائیں بلکہ چاہتے ہیں کہ) اپنے مال اور جان سے جہاد کریں۔ اور خدا ڈرنے والوں سے واقف ہے');
INSERT INTO "jalandhari" VALUES(9,45,'9:45','اجازت وہی لوگ مانگتے ہیں جو خدا پر اور پچھلے دن پر ایمان نہیں رکھتے اور ان کے دل شک میں پڑے ہوئے ہیں۔ سو وہ اپنے شک میں ڈانواں ڈول ہو رہے ہیں');
INSERT INTO "jalandhari" VALUES(9,46,'9:46','اور اگر وہ نکلنے کا ارادہ کرتے ہیں تو اس کے لیے سامان تیار کرتے لیکن خدا نے ان کا اُٹھنا (اور نکلنا) پسند نہ کیا تو ان کو ہلنے جلنے ہی نہ دیا اور (ان سے) کہہ دیا گیا کہ جہاں (معذور) بیٹھے ہیں تم بھی ان کے ساتھ بیٹھے رہو');
INSERT INTO "jalandhari" VALUES(9,47,'9:47','اگر وہ تم میں (شامل ہوکر) نکل بھی کھڑے ہوتے تو تمہارے حق میں شرارت کرتے اور تم میں فساد ڈلوانے کی غرض سے دوڑے دوڑے پھرتے اور تم میں ان کے جاسوس بھی ہیں اور خدا ظالموں کو خوب جانتا ہے');
INSERT INTO "jalandhari" VALUES(9,48,'9:48','یہ پہلے بھی طالب فساد رہے ہیں اور بہت سی باتوں میں تمہارے لیے الٹ پھیر کرتے رہے ہیں۔ یہاں تک کہ حق آپہنچا اور خدا کا حکم غالب ہوا اور وہ برا مانتے ہی رہ گئے');
INSERT INTO "jalandhari" VALUES(9,49,'9:49','اور ان میں کوئی ایسا بھی ہے جو کہتا ہے کہ مجھے تو اجازت ہی دیجئے اور آفت میں نہ ڈالئے۔ دیکھو یہ آفت میں پڑگئے ہیں اور دوزخ سب کافروں کو گھیرے ہوئے ہے');
INSERT INTO "jalandhari" VALUES(9,50,'9:50','(اے پیغمبر) اگر تم کو آسائش حاصل ہوتی ہے تو ان کو بری لگتی ہے۔ اور کوئی مشکل پڑتی ہے تو کہتے کہ ہم نے اپنا کام پہلے ہیں (درست) کر لیا تھا اور خوشیاں مناتے لوٹ جاتے ہیں');
INSERT INTO "jalandhari" VALUES(9,51,'9:51','کہہ دو کہ ہم کو کوئی مصیبت نہیں پہنچ سکتی بجز اس کے جو خدا نے ہمارے لیے لکھ دی ہو وہی ہمارا کارساز ہے۔ اور مومنوں کو خدا ہی کا بھروسہ رکھنا چاہیئے');
INSERT INTO "jalandhari" VALUES(9,52,'9:52','کہہ دو کہ تم ہمارے حق میں دو بھلائیوں میں سے ایک کے منتظر ہو اور ہم تمہارے حق میں اس بات کے منتظر ہیں کہ خدا (یا تو) اپنے پاس سے تم پر کوئی عذاب نازل کرے یا ہمارے ہاتھوں سے (عذاب دلوائے) تو تم بھی انتظار کرو ہم بھی تمہارے ساتھ انتظار کرتے ہیں');
INSERT INTO "jalandhari" VALUES(9,53,'9:53','کہہ دو کہ تم (مال) خوشی سے خرچ کرو یا ناخوشی سے تم سے ہرگز قبول نہیں کیا جائے گا تم نافرمان لوگ ہو');
INSERT INTO "jalandhari" VALUES(9,54,'9:54','اور ان کے خرچ (موال) کے قبول ہونے سے کوئی چیز مانع نہیں ہوئی سوا اس کے انہوں نے خدا سے اور اس کے رسول سے کفر کیا اور نماز کو آتے ہیں تو سست کاہل ہوکر اور خرچ کرتے ہیں تو ناخوشی سے');
INSERT INTO "jalandhari" VALUES(9,55,'9:55','تم ان کے مال اور اولاد سے تعجب نہ کرنا۔ خدا چاہتا ہے کہ ان چیزوں سے دنیا کی زندگی میں ان کو عذاب دے اور (جب) ان کی جان نکلے تو (اس وقت بھی) وہ کافر ہی ہوں');
INSERT INTO "jalandhari" VALUES(9,56,'9:56','اور خدا کی قسمیں کھاتے ہیں کہ وہ تم ہی میں سے ہیں حالانکہ ہو تم میں سے نہیں ہیں۔ اصل یہ ہے کہ یہ ڈرپوک لوگ ہیں');
INSERT INTO "jalandhari" VALUES(9,57,'9:57','اگر ان کی کوئی بچاؤ کی جگہ (جیسے قلعہ) یا غار ومغاک یا (زمین کے اندر) گھسنے کی جگہ مل جائے تو اسی طرف رسیاں تڑاتے ہوئے بھاگ جائیں');
INSERT INTO "jalandhari" VALUES(9,58,'9:58','اور ان میں سے بعض اسے بھی ہیں کہ (تقسیم) صدقات میں تم پر طعنہ زنی کرتے ہیں۔ اگر ان کو اس میں سے (خاطر خواہ) مل جائے تو خوش رہیں اور اگر (اس قدر) نہ ملے تو جھٹ خفا ہو جائیں');
INSERT INTO "jalandhari" VALUES(9,59,'9:59','اور اگر وہ اس پر خوش رہتے جو خدا اور اس کے رسول نے ان کو دیا تھا۔ اور کہتے کہ ہمیں خدا کافی ہے اور خدا اپنے فضل سے اور اس کے پیغمبر (اپنی مہربانی سے) ہمیں (پھر) دیں گے۔ اور ہمیں تو خدا ہی کی خواہش ہے (تو ان کے حق میں بہتر ہوتا)');
INSERT INTO "jalandhari" VALUES(9,60,'9:60','صدقات (یعنی زکوٰة وخیرات) تو مفلسوں اور محتاجوں اور کارکنان صدقات کا حق ہے اور ان لوگوں کا جن کی تالیف قلوب منظور ہے اور غلاموں کے آزاد کرانے میں اور قرضداروں (کے قرض ادا کرنے میں) اور خدا کی راہ میں اور مسافروں (کی مدد) میں (بھی یہ مال خرچ کرنا چاہیئے یہ حقوق) خدا کی طرف سے مقرر کر دیئے گئے ہیں اور خدا جاننے والا (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(9,61,'9:61','اور ان میں بعض ایسے ہیں جو پیغمبر کو ایذا دیتے ہیں اور کہتے ہیں کہ یہ شخص نرا کان ہے۔ (ان سے) کہہ دو کہ (وہ) کان (ہے تو) تمہاری بھلائی کے لیے۔ وہ خدا کا اور مومنوں (کی بات) کا یقین رکھتا ہے اور جو لوگ تم میں سے ایمان لائے ہیں ان کے لیے رحمت ہے۔ اور جو لوگ رسول خدا کو رنج پہنچاتے ہیں ان کے لیے عذاب الیم (تیار) ہے');
INSERT INTO "jalandhari" VALUES(9,62,'9:62','مومنو! یہ لوگ تمہارے سامنے خدا کی قسمیں کھاتے ہیں تاکہ تم کو خوش کر دیں۔ حالانکہ اگر یہ (دل سے) مومن ہوتے تو خدا اور اس کے پیغمبر خوش کرنے کے زیادہ مستحق ہیں');
INSERT INTO "jalandhari" VALUES(9,63,'9:63','کیا ان لوگوں کو معلوم نہیں کہ جو شخص خدا اور اس کے رسول سے مقابلہ کرتا ہے تو اس کے لیے جہنم کی آگ (تیار) ہے جس میں وہ ہمیشہ (جلتا) رہے گا۔ یہ بڑی رسوائی ہے');
INSERT INTO "jalandhari" VALUES(9,64,'9:64','منافق ڈرتے رہتے ہیں کہ ان (کے پیغمبر) پر کہیں کوئی ایسی سورت (نہ) اُتر آئے کہ ان کے دل کی باتوں کو ان (مسلمانوں) پر ظاہر کر دے۔ کہہ دو کہ ہنسی کئے جاؤ۔ جس بات سے تم ڈرتے ہو خدا اس کو ضرور ظاہر کردے گا');
INSERT INTO "jalandhari" VALUES(9,65,'9:65','اور اگر تم ان سے (اس بارے میں) دریافت کرو تو کہیں گے ہم تو یوں ہی بات چیت اور دل لگی کرتے تھے۔ کہو کیا تم خدا اور اس کی آیتوں اور اس کے رسول سے ہنسی کرتے تھے');
INSERT INTO "jalandhari" VALUES(9,66,'9:66','بہانے مت بناؤ تم ایمان لانے کے بعد کافر ہو چکے ہو۔ اگر ہم تم میں سے ایک جماعت کو معاف کردیں تو دوسری جماعت کو سزا بھی دیں گے کیونکہ وہ گناہ کرتے رہے ہیں');
INSERT INTO "jalandhari" VALUES(9,67,'9:67','منافق مرد اور منافق عورتیں ایک دوسرے کے ہم جنس (یعنی ایک طرح کے) ہیں کہ برے کام کرنے کو کہتے اور نیک کاموں سے منع کرتے اور (خرچ کرنے سے) ہاتھ بند کئے رہتے ہیں۔ انہوں نے خدا کو بھلا دیا تو خدا نے ان کو بھلا دیا۔ بےشک منافق نافرمان ہیں');
INSERT INTO "jalandhari" VALUES(9,68,'9:68','الله نے منافق مردوں اور منافق عورتوں اور کافروں سے آتش جہنم کا وعدہ کیا ہے جس میں ہمیشہ (جلتے) رہیں گے۔ وہی ان کے لائق ہے۔ اور خدا نے ان پر لعنت کر دی ہے۔ اور ان کے لیے ہمیشہ کا عذاب (تیار) ہے');
INSERT INTO "jalandhari" VALUES(9,69,'9:69','(تم منافق لوگ) ان لوگوں کی طرح ہو، جو تم سے پہلے ہوچکے ہیں۔ وہ تم سے بہت زیادہ طاقتور اور مال و اولاد میں کہیں زیادہ تھے تو وہ اپنے حصے سے بہرہ یاب ہوچکے۔ سو جس طرح تم سے پہلے لوگ اپنے حصے سے فائدہ اٹھا چکے ہیں۔ اسی طرح تم نے اپنے حصے سے فائدہ اٹھا لیا۔ اور جس طرح وہ باطل میں ڈوبے رہے اسی طرح تم باطل میں ڈوبے رہے یہ وہ لوگ ہیں جن کے اعمال دنیا اور آخرت میں ضائع ہوگئے۔ اور یہی نقصان اٹھانے والے ہیں');
INSERT INTO "jalandhari" VALUES(9,70,'9:70','کیا ان کو ان لوگوں (کے حالات) کی خبر نہیں پہنچی جو ان سے پہلے تھے (یعنی) نوح اور عاد اور ثمود کی قوم۔ اور ابراہیم کی قوم اور مدین والے اور الٹی ہوئی بستیوں والے۔ ان کے پاس پیغمبر نشانیاں لے لے کر آئے۔ اور خدا تو ایسا نہ تھا کہ ان پر ظلم کرتا لیکن وہی اپنے آپ پر ظلم کرتے تھے');
INSERT INTO "jalandhari" VALUES(9,71,'9:71','اور مومن مرد اور مومن عورتیں ایک دوسرے کے دوست ہیں کہ اچھے کام کرنے کو کہتے ہیں اور بری باتوں سے منع کرتے اور نماز پڑھتے اور زکوٰة دیتے اور خدا اور اس کے رسول کی اطاعت کرتے ہیں۔ یہی لوگ ہیں جن پر خدا رحم کرے گا۔ بےشک خدا غالب حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(9,72,'9:72','خدا نے مومن مردوں اور مومن عورتوں سے بہشتوں کا وعدہ کیا ہے جن کے نیچے نہریں بہہ رہی ہیں (وہ) ان میں ہمیشہ رہیں گے اور بہشت ہائے جاودانی میں نفیس مکانات کا (وعدہ کیا ہے) اور خدا کی رضا مندی تو سب سے بڑھ کر نعمت ہے یہی بڑی کامیابی ہے');
INSERT INTO "jalandhari" VALUES(9,73,'9:73','اے پیغمبر! کافروں اور منافقوں سے لڑو۔ اور ان پر سختی کرو۔ اور ان کا ٹھکانہ دورخ ہے اور وہ بری جگہ ہے');
INSERT INTO "jalandhari" VALUES(9,74,'9:74','یہ خدا کی قسمیں کھاتے ہیں کہ انہوں نے (تو کچھ) نہیں کہا حالانکہ انہوں نے کفر کا کلمہ کہا ہے اور یہ اسلام لانے کے بعد کافر ہوگئے ہیں اور ایسی بات کا قصد کرچکے ہیں جس پر قدرت نہیں پاسکے۔ اور انہوں نے (مسلمانوں میں) عیب ہی کون سا دیکھا ہے سوا اس کے کہ خدا نے اپنے فضل سے اور اس کے پیغمبر نے (اپنی مہربانی سے) ان کو دولت مند کر دیا ہے۔ تو اگر یہ لوگ توبہ کرلیں تو ان کے حق میں بہتر ہوگا۔ اور اگر منہ پھیر لیں تو ان کو دنیا اور آخرت میں دکھ دینے والا عذاب دے گا اور زمین میں ان کا کوئی دوست اور مددگار نہ ہوگا');
INSERT INTO "jalandhari" VALUES(9,75,'9:75','اور ان میں سے بعض ایسے ہیں جنہوں نے خدا سے عہد کیا تھا کہ اگر وہ ہم کو اپنی مہربانی سے (مال) عطا فرمائے گا تو ہم ضرور خیرات کیا کریں گے اور نیک کاروں میں ہو جائیں گے');
INSERT INTO "jalandhari" VALUES(9,76,'9:76','لیکن جب خدا نے ان کو اپنے فضل سے (مال) دیا تو اس میں بخل کرنے لگے اور (اپنے عہد سے) روگردانی کرکے پھر بیٹھے');
INSERT INTO "jalandhari" VALUES(9,77,'9:77','تو خدا نے اس کا انجام یہ کیا کہ اس روز تک کے لیے جس میں وہ خدا کے روبرو حاضر ہوں گے ان کے دلوں میں نفاق ڈال دیا اس لیے کہ انہوں نے خدا سے جو وعدہ کیا تھا اس کے خلاف کیا اور اس لیے کہ وہ جھوٹ بولتے تھے');
INSERT INTO "jalandhari" VALUES(9,78,'9:78','کیا ان کو معلوم نہیں کہ خدا ان کے بھیدوں اور مشوروں تک سے واقف ہے اور یہ کہ وہ غیب کی باتیں جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(9,79,'9:79','جو (ذی استطاعت) مسلمان دل کھول کر خیرات کرتے ہیں اور جو (بےچارے غریب صرف اتنا ہی کما سکتے ہیں جتنی مزدوری کرتے (اور تھوڑی سی کمائی میں سے خرچ بھی کرتے) ہیں ان پر جو (منافق) طعن کرتے ہیں اور ہنستے ہیں۔ خدا ان پر ہنستا ہے اور ان کے لیے تکلیف دینے والا عذاب (تیار) ہے');
INSERT INTO "jalandhari" VALUES(9,80,'9:80','تم ان کے لیے بخشش مانگو یا نہ مانگو۔ (بات ایک ہے)۔ اگر ان کے لیے ستّر دفعہ بھی بخشش مانگو گے تو بھی خدا ان کو نہیں بخشے گا۔ یہ اس لیے کہ انہوں نے خدا اور اس کے رسول سے کفر کیا۔ اور خدا نافرمان لوگوں کو ہدایت نہیں دیتا');
INSERT INTO "jalandhari" VALUES(9,81,'9:81','جو لوگ (غزوہٴ تبوک میں) پیچھے رہ گئے وہ پیغمبر خدا (کی مرضی) کے خلاف بیٹھے رہنے سے خوش ہوئے اور اس بات کو ناپسند کیا کہ خدا کی راہ میں اپنے مال اور جان سے جہاد کریں۔ اور (اوروں سے بھی) کہنے لگے کہ گرمی میں مت نکلنا۔ (ان سے) کہہ دو کہ دوزخ کی آگ اس سے کہیں زیادہ گرم ہے۔ کاش یہ (اس بات) کو سمجھتے');
INSERT INTO "jalandhari" VALUES(9,82,'9:82','یہ (دنیا میں) تھوڑا سا ہنس لیں اور (آخرت میں) ان کو ان اعمال کے بدلے جو کرتے رہے ہیں بہت سا رونا ہوگا');
INSERT INTO "jalandhari" VALUES(9,83,'9:83','پھر اگر خدا تم کو ان میں سے کسی گروہ کی طرف لے جائے اور وہ تم سے نکلنے کی اجازت طلب کریں تو کہہ دینا کہ تم میرے ساتھ ہرگز نہیں نکلو گے اور نہ میرے ساتھ (مددگار ہوکر) دشمن سے لڑائی کرو گے۔ تم پہلی دفعہ بیٹھ رہنے سے خوش ہوئے تو اب بھی پیچھے رہنے والوں کے ساتھ بیٹھے رہو');
INSERT INTO "jalandhari" VALUES(9,84,'9:84','اور (اے پیغمبر) ان میں سے کوئی مر جائے تو کبھی اس (کے جنازے) پر نماز نہ پڑھنا اور نہ اس کی قبر پر (جا کر) کھڑے ہونا۔ یہ خدا اور اس کے رسول کے ساتھ کفر کرتے رہے اور مرے بھی نافرمان (ہی مرے)');
INSERT INTO "jalandhari" VALUES(9,85,'9:85','ان کے اولاد اور مال سے تعجب نہ کرنا۔ ان چیزوں سے خدا یہ چاہتا ہے کہ ان کو دنیا میں عذاب کرے اور (جب) ان کی جان نکلے تو (اس وقت بھی) یہ کافر ہی ہوں');
INSERT INTO "jalandhari" VALUES(9,86,'9:86','اور جب کوئی سورت نازل ہوتی ہے کہ خدا پر ایمان لاؤ اور اس کے رسول کے ساتھ ہو کر لڑائی کرو تو جو ان میں دولت مند ہیں وہ تم سے اجازت طلب کرتے ہیں اور کہتے ہیں کہ ہمیں تو رہنے ہی دیجیئے کہ جو لوگ گھروں میں رہیں گے ہم بھی ان کے ساتھ رہیں');
INSERT INTO "jalandhari" VALUES(9,87,'9:87','یہ اس بات سے خوش ہیں کہ عورتوں کے ساتھ جو پیچھے رہ جاتی ہیں۔ (گھروں میں بیٹھ) رہیں ان کے دلوں پر مہر لگا دی گئی ہے تو یہ سمجھتے ہی نہیں');
INSERT INTO "jalandhari" VALUES(9,88,'9:88','لیکن پیغمبر اور جو لوگ ان کے ساتھ ایمان لائے سب اپنے مال اور جان سے لڑے۔ انہیں لوگوں کے لیے بھلائیاں ہیں۔ اور یہی مراد پانے والے ہیں');
INSERT INTO "jalandhari" VALUES(9,89,'9:89','خدا نے ان کے لیے باغات تیار کر رکھے ہیں جن کے نیچے نہریں بہہ رہی ہیں ہمیشہ ان میں رہی گے۔ یہ بڑی کامیابی ہے');
INSERT INTO "jalandhari" VALUES(9,90,'9:90','اور صحرا نشینوں میں سے بھی کچھ لوگ عذر کرتے ہوئے (تمہارے پاس) آئے کہ ان کو بھی اجازت دی جائے اور جنہوں نے خدا اور اس کے رسول سے جھوٹ بولا وہ (گھر میں) بیٹھ رہے سو جو لوگ ان میں سے کافر ہوئے ہیں ان کو دکھ دینے والا عذاب پہنچے گا');
INSERT INTO "jalandhari" VALUES(9,91,'9:91','نہ تو ضعیفوں پر کچھ گناہ ہے اور نہ بیماروں پر نہ ان پر جن کے پاس خرچ موجود نہیں (کہ شریک جہاد نہ ہوں یعنی) جب کہ خدا اور اس کے رسول کے خیراندیش (اور دل سے ان کے ساتھ) ہوں۔ نیکو کاروں پر کسی طرح کا الزام نہیں ہے۔ اور خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(9,92,'9:92','اور نہ ان (بےسروسامان) لوگوں پر (الزام) ہے کہ تمہارے پاس آئے کہ ان کو سواری دو اور تم نے کہا کہ میرے پاس کوئی ایسی چیز نہیں جس پر تم کو سوار کروں تو وہ لوٹ گئے اور اس غم سے کہ ان کے پاس خرچ موجود نہ تھا، ان کی آنکھوں سے آنسو بہہ رہے تھے');
INSERT INTO "jalandhari" VALUES(9,93,'9:93','الزام تو ان لوگوں پر ہے۔ جو دولت مند ہیں اور (پھر) تم سے اجازت طلب کرتے ہیں (یعنی) اس بات سے خوش ہیں کہ عورتوں کے ساتھ جو پیچھے رہ جاتی ہیں (گھروں میں بیٹھ) رہیں۔ خدا نے ان کے دلوں پر مہر کردی ہے پس وہ سمجھتے ہی نہیں');
INSERT INTO "jalandhari" VALUES(9,94,'9:94','جب تم ان کے پاس واپس جاؤ گے تو تم سے عذر کریں گے تم کہنا کہ مت عذر کرو ہم ہرگز تمہاری بات نہیں مانیں گے خدا نے ہم کو تمہارے سب حالات بتا دیئے ہیں۔ اور ابھی خدا اور اس کا رسول تمہارے عملوں کو (اور) دیکھیں گے پھر تم غائب وحاضر کے جاننے والے (خدائے واحد) کی طرف لوٹائے جاؤ گے اور جو عمل تم کرتے رہے ہو وہ سب تمہیں بتائے گا');
INSERT INTO "jalandhari" VALUES(9,95,'9:95','جب تم ان کے پاس لوٹ کر جاؤ گے تو تمہارے روبرو خدا کی قسمیں کھائیں گے تاکہ تم ان سے درگزر کرو سو اُن کی طرف التفات نہ کرنا۔ یہ ناپاک ہیں اور جو یہ کام کرتے رہے ہیں اس کے بدلہ ان کا ٹھکانہ دوزخ ہے');
INSERT INTO "jalandhari" VALUES(9,96,'9:96','یہ تمہارے آگے قسمیں کھائیں گے تاکہ تم ان سے خوش ہو جاؤ لیکن اگر تم اُن سے خوش ہو جاؤ گے تو خدا تو نافرمان لوگوں سے خوش نہیں ہوتا');
INSERT INTO "jalandhari" VALUES(9,97,'9:97','دیہاتی لوگ سخت کافر اور سخت منافق ہیں اور اس قابل ہیں کہ جو احکام (شریعت) خدا نے اپنے رسول پر نازل فرمائے ہیں ان سے واقف (ہی) نہ ہوں۔ اور خدا جاننے والا (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(9,98,'9:98','اور بعض دیہاتی ایسے ہیں کہ جو خرچ کرتے ہیں اسے تاوان سمجھتے ہیں اور تمہارے حق میں مصیبتوں کے منتظر ہیں۔ ان ہی پر بری مصیبت (واقع) ہو۔ اور خدا سننے والا (اور) جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(9,99,'9:99','اور بعض دیہاتی ایسے ہیں کہ خدا پر اور روز آخرت پر ایمان رکھتے ہیں اور جو کچھ خرچ کرتے ہیں اس کو خدا کی قُربت اور پیغمبر کی دعاؤں کا ذریعہ سمجھتے ہیں۔ دیکھو وہ بےشبہ ان کے لیے (موجب) قربت ہے خدا ان کو عنقریب اپنی رحمت میں داخل کرے گا۔ بےشک خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(9,100,'9:100','جن لوگوں نے سبقت کی (یعنی سب سے) پہلے (ایمان لائے) مہاجرین میں سے بھی اور انصار میں سے بھی۔ اور جنہوں نے نیکو کاری کے ساتھ ان کی پیروی کی خدا ان سے خوش ہے اور وہ خدا سے خوش ہیں اور اس نے ان کے لیے باغات تیار کئے ہیں جن کے نیچے نہریں بہہ رہی ہیں اور ہمیشہ ان میں رہیں گے۔ یہ بڑی کامیابی ہے');
INSERT INTO "jalandhari" VALUES(9,101,'9:101','اور تمہارے گرد و نواح کے بعض دیہاتی منافق ہیں اور بعض مدینے والے بھی نفاق پر اڑے ہوئے ہیں تم انہیں نہیں جانتے۔ ہم جانتے ہیں۔ ہم ان کو دوہرا عذاب دیں گے پھر وہ بڑے عذاب کی طرف لوٹائے جائیں گے');
INSERT INTO "jalandhari" VALUES(9,102,'9:102','اور کچھ اور لوگ ہیں کہ اپنے گناہوں کا (صاف) اقرار کرتے ہیں انہوں نے اچھے برے عملوں کو ملا جلا دیا تھا۔ قریب ہے کہ خدا ان پر مہربانی سے توجہ فرمائے۔ بےشک خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(9,103,'9:103','ان کے مال میں سے زکوٰة قبول کر لو کہ اس سے تم ان کو (ظاہر میں بھی) پاک اور (باطن میں بھی) پاکیزہ کرتے ہو اور ان کے حق میں دعائے خیر کرو کہ تمہاری دعا ان کے لیے موجب تسکین ہے اور خدا سننے والا اور جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(9,104,'9:104','کیا یہ لوگ نہیں جانتے کہ خدا ہی اپنے بندوں سے توبہ قبول فرماتا ہے اور صدقات (وخیرات) لیتا ہے اور بےشک خدا ہی توبہ قبول کرنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(9,105,'9:105','اور ان سے کہہ دو کہ عمل کئے جاؤ۔ خدا اور اس کا رسول اور مومن (سب) تمہارے عملوں کو دیکھ لیں گے۔ اور تم غائب وحاضر کے جاننے والے (خدائے واحد) کی طرف لوٹائے جاؤ گے پھر جو کچھ تم کرتے رہے ہو وہ سب تم کو بتا دے گا');
INSERT INTO "jalandhari" VALUES(9,106,'9:106','اور کچھ اور لوگ ہیں جن کا کام خدا کے حکم پر موقوف ہے۔ چاہے ان کو عذاب دے اور چاہے ان کو معاف کر دے۔ اور خدا جاننے والا اور حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(9,107,'9:107','اور (ان میں سے ایسے بھی ہیں) جنہوں نے اس غرض سے مسجد بنوائی کہ ضرر پہنچائیں اور کفر کریں اور مومنوں میں تفرقہ ڈالیں اور جو لوگ خدا اور اس کے رسول سے پہلے جنگ کرچکے ہیں ان کے لیے گھات کی جگہ بنائیں۔ اور قسمیں کھائیں گے کہ ہمارا مقصود تو صرف بھلائی تھی۔ مگر خدا گواہی دیتا ہے کہ یہ جھوٹے ہیں');
INSERT INTO "jalandhari" VALUES(9,108,'9:108','تم اس (مسجد) میں کبھی (جاکر) کھڑے بھی نہ ہونا۔ البتہ وہ مسجد جس کی بنیاد پہلے دن سے تقویٰ پر رکھی گئی ہے اس قابل ہے کہ اس میں جایا (اور نماز پڑھایا) کرو۔ اس میں ایسے لوگ ہیں جو کہ پاک رہنے کو پسند کرتے ہیں۔ اور خدا پاک رہنے والوں کو ہی پسند کرتا ہے');
INSERT INTO "jalandhari" VALUES(9,109,'9:109','بھلا جس شخص نے اپنی عمارت کی بنیاد خدا کے خوف اور اس کی رضامندی پر رکھی وہ اچھا ہے یا وہ جس نے اپنی عمارت کی بنیاد گر جانے والی کھائی کے کنارے پر رکھی کہ وہ اس کو دوزخ کی آگ میں لے گری اور خدا ظالموں کو ہدایت نہیں دیتا');
INSERT INTO "jalandhari" VALUES(9,110,'9:110','یہ عمارت جو انہوں نے بنائی ہے ہمیشہ ان کے دلوں میں (موجب) خلجان رہے گی (اور ان کو متردد رکھے گی) مگر یہ کہ ان کے دل پاش پاش ہو جائیں اور خدا جاننے والا اور حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(9,111,'9:111','خدا نے مومنوں سے ان کی جانیں اور ان کے مال خرید لیے ہیں (اور اس کے) عوض ان کے لیے بہشت (تیار کی) ہے۔ یہ لوگ خدا کی راہ میں لڑتے ہیں تو مارتے بھی ہیں اور مارے بھی جاتے ہیں بھی ہیں۔ یہ تورات اور انجیل اور قرآن میں سچا وعدہ ہے۔ جس کا پورا کرنا اسے ضرور ہے اور خدا سے زیادہ وعدہ پورا کرنے والا کون ہے تو جو سودا تم نے اس سے کیا ہے اس سے خوش رہو۔ اور یہی بڑی کامیابی ہے');
INSERT INTO "jalandhari" VALUES(9,112,'9:112','توبہ کرنے والے، عبادت کرنے والے، حمد کرنے والے، روزہ رکھنے والے، رکوع کرنے والے، سجدہ کرنے والے، نیک کاموں کا امر کرنے والے، بری باتوں سے منع کرنے والے، خدا کی حدوں کی حفاظت کرنے والے، (یہی مومن لوگ ہیں) اور اے پیغمبر مومنوں کو (بہشت کی) خوش خبری سنادو');
INSERT INTO "jalandhari" VALUES(9,113,'9:113','پیغمبر اور مسلمانوں کو شایاں نہیں کہ جب ان پر ظاہر ہوگیا کہ مشرک اہل دوزخ ہیں۔ تو ان کے لیے بخشش مانگیں گو وہ ان کے قرابت دار ہی ہوں');
INSERT INTO "jalandhari" VALUES(9,114,'9:114','اور ابراہیم کا اپنے باپ کے لیے بخشش مانگنا تو ایک وعدے کا سبب تھا جو وہ اس سے کر چکے تھے۔ لیکن جب ان کو معلوم ہوگیا کہ وہ خدا کا دشمن ہے تو اس سے بیزار ہوگئے۔ کچھ شک نہیں کہ ابراہیم بڑے نرم دل اور متحمل تھے');
INSERT INTO "jalandhari" VALUES(9,115,'9:115','اور خدا ایسا نہیں کہ کسی قوم کو ہدایت دینے کے بعد گمراہ کر دے جب تک کہ ان کو وہ چیز نہ بتا دے جس سے وہ پرہیز کریں۔ بےشک خدا ہر چیز سے واقف ہے');
INSERT INTO "jalandhari" VALUES(9,116,'9:116','خدا ہی ہے جس کے لیے آسمانوں اور زمینوں کی بادشاہت ہے۔ وہی زندگانی بخشتا ہے (وہی) موت دیتا ہے اور خدا کے سوا تمہارا کوئی دوست اور مددگار نہیں ہے');
INSERT INTO "jalandhari" VALUES(9,117,'9:117','بےشک خدا نے پیغمبر پر مہربانی کی اور مہاجرین اور انصار پر جو باوجود اس کے کہ ان میں سے بعضوں کے دل جلد پھر جانے کو تھے۔ مشکل کی گھڑی میں پیغمبر کے ساتھ رہے۔ پھر خدا نے ان پر مہربانی فرمائی۔ بےشک وہ ان پر نہایت شفقت کرنے والا (اور) مہربان ہے');
INSERT INTO "jalandhari" VALUES(9,118,'9:118','اور ان تینوں پر بھی جن کا معاملہ ملتوی کیا گیا تھا۔ یہاں تک کہ جب اُنہیں زمین باوجود فراخی کے ان پر تنگ ہوگئی اور ان کے جانیں بھی ان پر دوبھر ہوگئیں۔ اور انہوں نے جان لیا کہ خدا (کے ہاتھ) سے خود اس کے سوا کوئی پناہ نہیں۔ پھر خدا نے ان پر مہربانی کی تاکہ توبہ کریں۔ بےشک خدا توبہ قبول کرنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(9,119,'9:119','اے اہل ایمان! خدا سے ڈرتے رہو اور راست بازوں کے ساتھ رہو');
INSERT INTO "jalandhari" VALUES(9,120,'9:120','اہل مدینہ کو اور جو ان کے آس پاس دیہاتی رہتے ہیں ان کو شایاں نہ تھا کہ پیغمبر خدا سے پیچھے رہ جائیں اور نہ یہ کہ اپنی جانوں کو ان کی جان سے زیادہ عزیز رکھیں۔ یہ اس لیے کہ انہیں خدا کی راہ میں تکلیف پہنچتی ہے پیاس کی، محنت کی یا بھوک کی یا وہ ایسی جگہ چلتے ہیں کہ کافروں کو غصہ آئے یا دشمنوں سے کوئی چیز لیتے ہیں تو ہر بات پر ان کے لیے عمل نیک لکھا جاتا ہے۔ کچھ شک نہیں کہ خدا نیکو کاروں کا اجر ضائع نہیں کرتا');
INSERT INTO "jalandhari" VALUES(9,121,'9:121','اور (اسی طرح) جو وہ خرچ کرتے ہیں تھوڑا یا بہت یا کوئی میدان طے کرتے ہیں تو یہ سب کچھ ان کے لیے (اعمال صالحہ) میں لکھ لیا جاتا ہے تاکہ خدا ان کو ان کے اعمال کا بہت اچھا بدلہ دے');
INSERT INTO "jalandhari" VALUES(9,122,'9:122','اور یہ تو ہو نہیں سکتا کہ مومن سب کے سب نکل آئیں۔ تو یوں کیوں نہ کیا کہ ہر ایک جماعت میں سے چند اشخاص نکل جاتے تاکہ دین کا (علم سیکھتے اور اس) میں سمجھ پیدا کرتے اور جب اپنی قوم کی طرف واپس آتے تو ان کو ڈر سناتے تاکہ وہ حذر کرتے');
INSERT INTO "jalandhari" VALUES(9,123,'9:123','اے اہلِ ایمان! اپنے نزدیک کے (رہنے والے) کافروں سے جنگ کرو اور چاہیئے کہ وہ تم میں سختی (یعنی محنت وقوت جنگ) معلوم کریں۔ اور جان رکھو کہ خدا پرہیز گاروں کے ساتھ ہے');
INSERT INTO "jalandhari" VALUES(9,124,'9:124','اور جب کوئی سورت نازل ہوتی ہے تو بعض منافق (استہزاء کرتے اور) پوچھتے کہ اس سورت نے تم میں سے کس کا ایمان زیادہ کیا ہے۔ سو جو ایمان والے ہیں ان کا ایمان تو زیادہ کیا اور وہ خوش ہوتے ہیں');
INSERT INTO "jalandhari" VALUES(9,125,'9:125','اور جن کے دلوں میں مرض ہے، ان کے حق میں خبث پر خبث زیادہ کیا اور وہ مرے بھی تو کافر کے کافر');
INSERT INTO "jalandhari" VALUES(9,126,'9:126','کیا یہ دیکھتے نہیں کہ یہ ہر سال ایک یا دو بار بلا میں پھنسا دیئے جاتے ہیں پھر بھی توبہ نہیں کرتے اور نہ نصیحت پکڑتے ہیں');
INSERT INTO "jalandhari" VALUES(9,127,'9:127','اور جب کوئی سورت نازل ہوتی ہے ایک دوسرے کی جانب دیکھنے لگتے ہیں (اور پوچھتے ہیں کہ) بھلا تمہیں کوئی دیکھتا ہے پھر پھر جاتے ہیں۔ خدا نے ان کے دلوں کو پھیر رکھا ہے کیونکہ یہ ایسے لوگ ہیں کہ سمجھ سے کام نہیں لیتے');
INSERT INTO "jalandhari" VALUES(9,128,'9:128','(لوگو) تمہارے پاس تم ہی میں سے ایک پیغمبر آئے ہیں۔ تمہاری تکلیف ان کو گراں معلوم ہوتی ہے اور تمہاری بھلائی کے خواہش مند ہیں اور مومنوں پر نہایت شفقت کرنے والے (اور) مہربان ہیں');
INSERT INTO "jalandhari" VALUES(9,129,'9:129','پھر اگر یہ لوگ پھر جائیں (اور نہ مانیں) تو کہہ دو کہ خدا مجھے کفایت کرتا ہے اس کے سوا کوئی معبود نہیں اسی پر میرا بھروسہ ہے اور وہی عرش عظیم کا مالک ہے');
INSERT INTO "jalandhari" VALUES(10,1,'10:1','آلرا ۔ یہ بڑی دانائی کی کتاب کی آیتیں ہیں');
INSERT INTO "jalandhari" VALUES(10,2,'10:2','کیا لوگوں کو تعجب ہوا کہ ہم نے ان ہی میں سے ایک مرد کو حکم بھیجا کہ لوگوں کو ڈر سنا دو۔ اور ایمان لانے والوں کو خوشخبری دے دو کہ ان کے پروردگار کے ہاں ان کا سچا درجہ ہے۔ (ایسے شخص کی نسبت) کافر کہتے ہیں کہ یہ صریح جادوگر ہے');
INSERT INTO "jalandhari" VALUES(10,3,'10:3','تمہارا پروردگار تو خدا ہی ہے جس نے آسمان اور زمین چھ دن میں بنائے پھر عرش (تخت شاہی) پر قائم ہوا وہی ہر ایک کا انتظام کرتا ہے۔ کوئی (اس کے پاس) اس کا اذن حاصل کیے بغیر کسی کی سفارش نہیں کرسکتا، یہی خدا تمہارا پروردگار ہے تو اسی کی عبادت کرو۔ بھلا تم غور کیوں نہیں کرتے');
INSERT INTO "jalandhari" VALUES(10,4,'10:4','اسی کے پاس تم سب کو لوٹ کر جانا ہے۔ خدا کا وعدہ سچا ہے۔ وہی خلقت کو پہلی بار پیدا کرتا ہے۔ پھر وہی اس کو دوبارہ پیدا کرے گا تاکہ ایمان والوں اور نیک کام کرنے والوں کو انصاف کے ساتھ بدلہ دے۔ اور جو کافر ہیں ان کے لیے پینے کو نہایت گرم پانی اور درد دینے والا عذاب ہوگا کیوں کہ (خدا سے) انکار کرتے تھے');
INSERT INTO "jalandhari" VALUES(10,5,'10:5','وہی تو ہے جس نے سورج کو روشن اور چاند کو منور بنایا اور چاند کی منزلیں مقرر کیں تاکہ تم برسوں کا شمار اور (کاموں کا) حساب معلوم کرو۔ یہ (سب کچھ) خدا نے تدبیر سے پیدا کیا ہے۔ سمجھنے والوں کے لیے وہ اپنی آیاتیں کھول کھول کر بیان فرماتا ہے');
INSERT INTO "jalandhari" VALUES(10,6,'10:6','رات اور دن کے (ایک دوسرے کے پیچھے) آنے جانے میں اور جو چیزیں خدا نے آسمان اور زمین میں پیدا کی ہیں (سب میں) ڈرنے والوں کے لیے نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(10,7,'10:7','جن لوگوں کو ہم سے ملنے کی توقع نہیں اور دنیا کی زندگی سے خوش اور اسی پر مطئمن ہو بیٹھے اور ہماری نشانیوں سے غافل ہو رہے ہیں');
INSERT INTO "jalandhari" VALUES(10,8,'10:8','ان کا ٹھکانہ ان (اعمال) کے سبب جو وہ کرتے ہیں دوزخ ہے');
INSERT INTO "jalandhari" VALUES(10,9,'10:9','اور جو لوگ ایمان لائے اور نیک کام کرتے رہے ان کو پروردگار ان کے ایمان کی وجہ سے (ایسے محلوں کی) راہ دکھائے گا (کہ) ان کے نیچے نعمت کے باغوں میں نہریں بہہ رہی ہوں گی');
INSERT INTO "jalandhari" VALUES(10,10,'10:10','(جب وہ) ان میں (ان نعمتوں کو دیکھوں گے تو بےساختہ) کہیں گے سبحان الله۔ اور آپس میں ان کی دعا سلامٌ علیکم ہوگی اور ان کا آخری قول یہ (ہوگا) کہ خدائے رب العالمین کی حمد (اور اس کا شکر) ہے');
INSERT INTO "jalandhari" VALUES(10,11,'10:11','اور اگر خدا لوگوں کی برائی میں جلدی کرتا جس طرح وہ طلب خیر میں جلدی کرتے ہیں۔ تو ان کی (عمر کی) میعاد پوری ہوچکی ہوتی سو جن لوگوں کو ہم سے ملنے کی توقع نہیں انہیں ہم چھوڑے رکھتے ہیں کہ اپنی سرکشی میں بہکتے رہیں');
INSERT INTO "jalandhari" VALUES(10,12,'10:12','اور جب انسان کو تکلیف پہنچتی ہے تو لیٹا اور بیٹھا اور کھڑا (ہر حال میں) ہمیں پکارتا ہے۔ پھر جب ہم اس تکلیف کو اس سے دور کر دیتے ہیں تو (بےلحاظ ہو جاتا ہے اور) اس طرح گزر جاتا ہے گویا کسی تکلیف پہنچنے پر ہمیں کبھی پکارا ہی نہ تھا۔ اسی طرح حد سے نکل جانے والوں کو ان کے اعمال آراستہ کرکے دکھائے گئے ہیں');
INSERT INTO "jalandhari" VALUES(10,13,'10:13','اور تم سے پہلے ہم کئی امتوں کو جب انہوں نے ظلم کا راستہ اختیار کیا ہلاک کرچکے ہیں۔ اور ان کے پاس پیغمبر کھلی نشانیاں لے کر آئے مگر وہ ایسے نہ تھے کہ ایمان لاتے۔ ہم گنہگار لوگوں کو اسی طرح بدلہ دیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(10,14,'10:14','پھر ہم نے ان کے بعد تم لوگوں کو ملک میں خلیفہ بنایا تاکہ دیکھیں تم کیسے کام کرتے ہو');
INSERT INTO "jalandhari" VALUES(10,15,'10:15','اور جب ان کو ہماری آیتیں پڑھ کر سنائی جاتی ہیں تو جن لوگوں کو ہم سے ملنے کی امید نہیں وہ کہتے ہیں کہ (یا تو) اس کے سوا کوئی اور قرآن (بنا) لاؤ یا اس کو بدل دو۔ کہہ دو کہ مجھ کو اختیار نہیں ہے کہ اسے اپنی طرف سے بدل دو۔ میں تو اسی حکم کا تابع ہوں جو میری طرف آتا ہے۔ اگر میں اپنے پروردگار کی نافرمانی کروں تو مجھے بڑے (سخت) دن کے عذاب سے خوف آتا ہے');
INSERT INTO "jalandhari" VALUES(10,16,'10:16','(یہ بھی) کہہ دو کہ اگر خدا چاہتا تو (نہ تو) میں ہی یہ (کتاب) تم کو پڑھ کر سناتا اور نہ وہی تمہیں اس سے واقف کرتا۔ میں اس سے پہلے تم میں ایک عمر رہا ہوں (اور کبھی ایک کلمہ بھی اس طرح کا نہیں کہا) بھلا تم سمجھتے نہیں');
INSERT INTO "jalandhari" VALUES(10,17,'10:17','تو اس سے بڑھ کر ظالم کون جو خدا پر جھوٹ افترا کرے اور اس کی آیتوں کو جھٹلائے۔ بےشک گنہگار فلاح نہیں پائیں گے');
INSERT INTO "jalandhari" VALUES(10,18,'10:18','اور یہ (لوگ) خدا کے سوا ایسی چیزوں کی پرستش کرتے ہیں جو نہ ان کا کچھ بگاڑ ہی سکتی ہیں اور نہ کچھ بھلا ہی کر سکتی ہیں اور کہتے ہیں کہ یہ خدا کے پاس ہماری سفارش کرنے والے ہیں۔ کہہ دو کہ کیا تم خدا کو ایسی چیز بتاتے ہو جس کا وجود اسے نہ آسمانوں میں معلوم ہوتا ہے اور نہ زمین میں۔ وہ پاک ہے اور (اس کی شان) ان کے شرک کرنے سے بہت بلند ہے');
INSERT INTO "jalandhari" VALUES(10,19,'10:19','اور (سب) لوگ (پہلے) ایک ہی اُمت (یعنی ایک ہی ملت پر) تھے۔ پھر جدا جدا ہوگئے۔ اور اگر ایک بات جو تمہارے پروردگار کی طرف سے پہلے ہوچکی ہے نہ ہوتی تو جن باتوں میں وہ اختلاف کرتے ہیں ان میں فیصلہ کر دیا جاتا');
INSERT INTO "jalandhari" VALUES(10,20,'10:20','اور کہتے ہیں کہ اس پر اس کے پروردگار کی طرف سے کوئی نشانی کیوں نازل نہیں ہوئی۔ کہہ دو کہ غیب (کا علم) تو خدا کو ہے سو تم انتظار کرو۔ میں بھی تمہارے ساتھ انتظار کرتا ہوں');
INSERT INTO "jalandhari" VALUES(10,21,'10:21','اور جب ہم لوگوں کو تکلیف پہنچنے کے بعد (اپنی) رحمت (سے آسائش) کا مزہ چکھاتے ہیں تو وہ ہماری آیتوں میں حیلے کرنے لگتے ہیں۔ کہہ دو کہ خدا بہت جلد حیلہ کرنے والا ہے۔ اور جو حیلے تم کرتے ہو ہمارے فرشتے ان کو لکھتے جاتے ہیں');
INSERT INTO "jalandhari" VALUES(10,22,'10:22','وہی تو ہے جو تم کو جنگل اور دریا میں چلنے پھرنے اور سیر کرنے کی توفیق دیتا ہے۔ یہاں تک کہ جب تم کشتیوں میں (سوار) ہوتے اور کشتیاں پاکیزہ ہوا (کے نرم نرم جھونکوں) سے سواروں کو لے کر چلنے لگتی ہیں اور وہ ان سے خوش ہوتے ہیں تو ناگہاں زناٹے کی ہوا چل پڑتی ہے اور لہریں ہر طرف سے ان پر (جوش مارتی ہوئی) آنے لگتی ہیں اور وہ خیال کرتے ہیں کہ (اب تو) لہروں میں گھر گئے تو اس وقت خالص خدا ہی کی عبادت کرکے اس سے دعا مانگنے لگتے ہیں کہ (اے خدا) اگر تو ہم کو اس سے نجات بخشے تو ہم (تیرے) بہت ہی شکر گزار ہوں');
INSERT INTO "jalandhari" VALUES(10,23,'10:23','لیکن جب وہ ان کو نجات دے دیتا ہے تو ملک میں ناحق شرارت کرنے لگتے ہیں۔ لوگو! تمہاری شرارت کا وبال تمہاری ہی جانوں پر ہوگا تم دنیا کی زندگی کے فائدے اُٹھا لو۔ پھر تم کو ہمارے پاس لوٹ کر آنا ہے۔ اس وقت ہم تم کو بتائیں گے جو کچھ تم کیا کرتے تھے');
INSERT INTO "jalandhari" VALUES(10,24,'10:24','دنیا کی زندگی کی مثال مینھہ کی سی ہے کہ ہم نے اس کو آسمان سے برسایا۔ پھر اس کے ساتھ سبزہ جسے آدمی اور جانور کھاتے ہیں مل کر نکلا یہاں تک کہ زمین سبزے سے خوشنما اور آراستہ ہوگئی اور زمین والوں نے خیال کیا کہ وہ اس پر پوری دسترس رکھتے ہیں ناگہاں رات کو یا دن کو ہمارا حکم (عذاب) آپہنچا تو ہم نے اس کو کاٹ (کر ایسا کر) ڈالا کہ گویا کل وہاں کچھ تھا ہی نہیں۔ جو لوگ غور کرنے والے ہیں۔ ان کے لیے ہم (اپنی قدرت کی) نشانیاں اسی طرح کھول کھول کر بیان کرتے ہیں');
INSERT INTO "jalandhari" VALUES(10,25,'10:25','اور خدا سلامتی کے گھر کی طرف بلاتا ہے۔ اور جس کو چاہتا ہے سیدھا راستہ دکھاتا ہے');
INSERT INTO "jalandhari" VALUES(10,26,'10:26','جن لوگوں نے نیکو کاری کی ان کے لیے بھلائی ہے اور (مزید برآں) اور بھی اور ان کے مونہوں پر نہ تو سیاہی چھائے گی اور نہ رسوائی۔ یہی جنتی ہیں کہ اس میں ہمیشہ رہیں گے');
INSERT INTO "jalandhari" VALUES(10,27,'10:27','اور جنہوں نے برے کام کئے تو برائی کا بدلہ ویسا ہی ہوگا۔ اور ان کے مونہوں پر ذلت چھا جائے گی۔ اور کوئی ان کو خدا سے بچانے والا نہ ہوگا۔ ان کے مونہوں (کی سیاہی کا یہ عالم ہوگا کہ ان) پر گویا اندھیری رات کے ٹکڑے اُڑھا دیئے گئے ہیں۔ یہی دوزخی ہیں کہ ہمیشہ اس میں رہیں گے');
INSERT INTO "jalandhari" VALUES(10,28,'10:28','اور جس دن ہم ان سب کو جمع کریں گے پھر مشرکوں سے کہیں گے کہ تم اور تمہارے شریک اپنی اپنی جگہ ٹھہرے رہو۔ تو ہم ان میں تفرقہ ڈال دیں گے اور ان کے شریک (ان سے) کہیں گے کہ تم ہم کو نہیں پوجا کرتے تھے');
INSERT INTO "jalandhari" VALUES(10,29,'10:29','ہمارے اور تمہارے درمیان خدا ہی گواہ کافی ہے۔ ہم تمہاری پرستش سے بالکل بےخبر تھے');
INSERT INTO "jalandhari" VALUES(10,30,'10:30','وہاں ہر شخص (اپنے اعمال کی) جو اس نے آگے بھیجے ہوں گے آزمائش کرلے گا اور وہ اپنے سچے مالک کی طرف لوٹائے جائیں گے اور جو کچھ وہ بہتان باندھا کرتے تھے سب ان سے جاتا رہے گا');
INSERT INTO "jalandhari" VALUES(10,31,'10:31','(ان سے) پوچھو کہ تم کو آسمان اور زمین میں رزق کون دیتا ہے یا (تمہارے) کانوں اور آنکھوں کا مالک کون ہے اور بےجان سے جاندار کون پیدا کرتا ہے اور دنیا کے کاموں کا انتظام کون کرتا ہے۔ جھٹ کہہ دیں گے کہ خدا۔ تو کہو کہ پھر تم (خدا سے) ڈرتے کیوں نہیں؟');
INSERT INTO "jalandhari" VALUES(10,32,'10:32','یہی خدا تو تمہارا پروردگار برحق ہے۔ اور حق بات کے ظاہر ہونے کے بعد گمراہی کے سوا ہے ہی کیا؟ تو تم کہاں پھرے جاتے ہو');
INSERT INTO "jalandhari" VALUES(10,33,'10:33','اسی طرح خدا کا ارشاد ان نافرمانوں کے حق میں ثابت ہو کر رہا کہ یہ ایمان نہیں لائیں گے');
INSERT INTO "jalandhari" VALUES(10,34,'10:34','(ان سے) پوچھو کہ بھلا تمھارے شریکوں میں سے کوئی ایسا ہے کہ مخلوق کو ابتداً پیدا کرے (اور) پھر اس کو دوبارہ بنائے؟ کہہ دو کہ خدا ہی پہلی بار پیدا کرتا ہے پھر وہی اس کو دوبارہ پیدا کرے گا تو تم کہاں اُکسے جارہے ہو');
INSERT INTO "jalandhari" VALUES(10,35,'10:35','پوچھو کہ بھلا تمہارے شریکوں میں کون ایسا ہے کہ حق کا رستہ دکھائے۔ کہہ دو کہ خدا ہی حق کا رستہ دکھاتا ہے۔ بھلا جو حق کا رستہ دکھائے وہ اس قابل ہے کہ اُس کی پیروی کی جائے یا وہ کہ جب تک کوئی اسے رستہ نہ بتائے رستہ نہ پائے۔ تو تم کو کیا ہوا ہے کیسا انصاف کرتے ہو؟');
INSERT INTO "jalandhari" VALUES(10,36,'10:36','اور ان میں سے اکثر صرف ظن کی پیروی کرتے ہیں۔ اور کچھ شک نہیں کہ ظن حق کے مقابلے میں کچھ بھی کارآمد نہیں ہوسکتا۔ بےشک خدا تمہارے (سب) افعال سے واقف ہے');
INSERT INTO "jalandhari" VALUES(10,37,'10:37','اور یہ قرآن ایسا نہیں کہ خدا کے سوا کوئی اس کو اپنی طرف سے بنا لائے۔ ہاں (ہاں یہ خدا کا کلام ہے) جو (کتابیں) اس سے پہلے (کی) ہیں۔ ان کی تصدیق کرتا ہے اور ان ہی کتابوں کی (اس میں) تفصیل ہے اس میں کچھ شک نہیں (کہ) یہ رب العالمین کی طرف سے (نازل ہوا) ہے');
INSERT INTO "jalandhari" VALUES(10,38,'10:38','کیا یہ لوگ کہتے ہیں کہ پیغمبر نے اس کو اپنی طرف سے بنا لیا ہے کہہ دو کہ اگر سچے ہو تو تم بھی اس طرح کی ایک سورت بنا لاؤ اور خدا کے سوا جن کو تم بلا سکو بلا بھی لو');
INSERT INTO "jalandhari" VALUES(10,39,'10:39','حقیقت یہ ہے کہ جس چیز کے علم پر یہ قابو نہیں پاسکے اس کو (نادانی سے) جھٹلا دیا اور ابھی اس کی حقیقت ان پر کھلی ہی نہیں۔ اسی طرح جو لوگ ان سے پہلے تھے انہوں نے تکذیب کی تھی سو دیکھ لو ظالموں کا انجام کیسا ہوا');
INSERT INTO "jalandhari" VALUES(10,40,'10:40','اور ان میں سے کچھ تو ایسے ہیں کہ اس پر ایمان لے آتے ہیں اور کچھ ایسے ہیں کہ ایمان نہیں لاتے۔ اور تمھارا پروردگار شریروں سے خوب واقف ہے');
INSERT INTO "jalandhari" VALUES(10,41,'10:41','اور اگر یہ تمہاری تکذیب کریں تو کہہ دو کہ مجھ کو میرے اعمال (کا بدلہ ملے گا) اور تم کو تمہارے اعمال (کا) تم میرے عملوں کا جواب دہ نہیں ہو اور میں تمہارے عملوں کا جوابدہ نہیں ہوں');
INSERT INTO "jalandhari" VALUES(10,42,'10:42','اور ان میں سے بعض ایسے ہیں کہ تمہاری طرف کان لگاتے ہیں تو کیا تم بہروں کو سناؤ گے اگرچہ کچھ بھی (سنتے) سمجھتے نہ ہوں');
INSERT INTO "jalandhari" VALUES(10,43,'10:43','اور بعض ایسے ہیں کہ تمھاری طرف دیکھتے ہیں۔ تو کیا تم اندھوں کو راستہ دکھاؤ گے اگرچہ کچھ بھی دیکھتے (بھالتے) نہ ہوں');
INSERT INTO "jalandhari" VALUES(10,44,'10:44','خدا تو لوگوں پر کچھ ظلم نہیں کرتا لیکن لوگ ہی اپنے آپ پر ظلم کرتے ہیں');
INSERT INTO "jalandhari" VALUES(10,45,'10:45','اور جس دن خدا ان کو جمع کرے گا (تو وہ دنیا کی نسبت ایسا خیال کریں گے کہ) گویا (وہاں) گھڑی بھر دن سے زیادہ رہے ہی نہیں تھے (اور) آپس میں ایک دوسرے کو شناخت بھی کریں گے۔ جن لوگوں نے خدا کے روبرو حاضر ہونے کو جھٹلایا وہ خسارے میں پڑ گئے اور راہ یاب نہ ہوئے');
INSERT INTO "jalandhari" VALUES(10,46,'10:46','اور اگر ہم کوئی عذاب جس کا ان لوگوں سے وعدہ کرتے ہیں تمہاری آنکھوں کے سامنے (نازل) کریں یا (اس وقت جب) تمہاری مدت حیات پوری کردیں تو ان کو ہمارے ہی پاس لوٹ کر آنا ہے پھر جو کچھ یہ کر رہے ہیں خدا اس کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(10,47,'10:47','اور ہر ایک اُمت کی طرف سے پیغمبر بھیجا گیا۔ جب ان کا پیغمبر آتا ہے تو اُن میں انصاف کے ساتھ فیصلہ کر دیا جاتا ہے اور ان پر کچھ ظلم نہیں کیا جاتا');
INSERT INTO "jalandhari" VALUES(10,48,'10:48','اور یہ کہتے ہیں کہ اگر تم سچے ہو تو (جس عذاب کا) یہ وعدہ (ہے وہ آئے گا) کب؟');
INSERT INTO "jalandhari" VALUES(10,49,'10:49','کہہ دو کہ میں اپنے نقصان اور فائدے کا بھی کچھ اختیار نہیں رکھتا۔ مگر جو خدا چاہے۔ ہر ایک امت کے لیے (موت کا) ایک وقت مقرر ہے۔ جب وہ وقت آجاتا ہے تو ایک گھڑی بھی دیر نہیں کرسکتے اور نہ جلدی کرسکتے ہیں');
INSERT INTO "jalandhari" VALUES(10,50,'10:50','کہہ دو کہ بھلا دیکھو تو اگر اس کا عذاب تم پر (ناگہاں) آجائے رات کو یا دن کو تو پھر گنہگار کس بات کی جلدی کریں گے');
INSERT INTO "jalandhari" VALUES(10,51,'10:51','کیا جب وہ آ واقع ہوگا تب اس پر ایمان لاؤ گے (اس وقت کہا جائے گا کہ) اور اب (ایمان لائے؟) اس کے لیے تو تم جلدی مچایا کرتے تھے');
INSERT INTO "jalandhari" VALUES(10,52,'10:52','پھر ظالم لوگوں سے کہا جائے گا کہ عذاب دائمی کا مزہ چکھو۔ (اب) تم انہیں (اعمال) کا بدلہ پاؤ گے جو (دنیا میں) کرتے رہے');
INSERT INTO "jalandhari" VALUES(10,53,'10:53','اور تم سے دریافت کرتے ہیں کہ آیا یہ سچ ہے۔ کہہ دو ہاں خدا کی قسم سچ ہے اور تم (بھاگ کر خدا کو) عاجز نہیں کرسکو گے');
INSERT INTO "jalandhari" VALUES(10,54,'10:54','اور اگر ہر ایک نافرمان شخص کے پاس روئے زمین کی تمام چیزیں ہوں تو (عذاب سے بچنے کے) بدلے میں (سب) دے ڈالے اور جب وہ عذاب کو دیکھیں گے تو (پچھتائیں گے اور) ندامت کو چھپائیں گے۔ اور ان میں انصاف کے ساتھ فیصلہ کر دیا جائے گا اور (کسی طرح کا) ان پر ظلم نہیں ہوگا');
INSERT INTO "jalandhari" VALUES(10,55,'10:55','سن رکھو جو کچھ آسمانوں اور زمینوں میں ہے سب خدا ہی کا ہے۔ اور یہ بھی سن رکھو کہ خدا کا وعدہ سچا ہے لیکن اکثر لوگ نہیں جانتے');
INSERT INTO "jalandhari" VALUES(10,56,'10:56','وہی جان بخشتا اور (وہی) موت دیتا ہے اور تم لوگ اسی کی طرف لوٹ کر جاؤ گے');
INSERT INTO "jalandhari" VALUES(10,57,'10:57','لوگو تمہارے پروردگار کی طرف سے نصیحت اور دلوں کی بیماریوں کی شفا۔ اور مومنوں کے لیے ہدایت اور رحمت آپہنچی ہے');
INSERT INTO "jalandhari" VALUES(10,58,'10:58','کہہ دو کہ (یہ کتاب) خدا کے فضل اور اس کی مہربانی سے (نازل ہوئی ہے) تو چاہیئے کہ لوگ اس سے خوش ہوں۔ یہ اس سے کہیں بہتر ہے جو وہ جمع کرتے ہیں');
INSERT INTO "jalandhari" VALUES(10,59,'10:59','کہو کہ بھلا دیکھو تو خدا نے تمھارے لئے جو رزق نازل فرمایا تو تم نے اس میں سے (بعض کو) حرام ٹھہرایا اور (بعض کو) حلال (ان سے) پوچھو کیا خدا نے تم کو اس کا حکم دیا ہے یا تم خدا پر افتراء کرتے ہو');
INSERT INTO "jalandhari" VALUES(10,60,'10:60','اور جو لوگ خدا پر افتراء کرتے ہیں وہ قیامت کے دن کی نسبت کیا خیال رکھتے ہیں؟ بےشک خدا لوگوں پر مہربان ہے لیکن اکثر لوگ شکر نہیں کرتے');
INSERT INTO "jalandhari" VALUES(10,61,'10:61','اور تم جس حال میں ہوتے ہو یا قرآن میں کچھ پڑھتے ہو یا تم لوگ کوئی (اور) کام کرتے ہو جب اس میں مصروف ہوتے ہو ہم تمہارے سامنے ہوتے ہیں اور تمہارے پروردگار سے ذرہ برابر بھی کوئی چیز پوشیدہ نہیں ہے نہ زمین میں نہ آسمان میں اور نہ کوئی چیز اس سے چھوٹی ہے یا بڑی مگر کتاب روشن میں (لکھی ہوئی) ہے');
INSERT INTO "jalandhari" VALUES(10,62,'10:62','سن رکھو کہ جو خدا کے دوست ہیں ان کو نہ کچھ خوف ہوگا اور نہ وہ غمناک ہوں گے');
INSERT INTO "jalandhari" VALUES(10,63,'10:63','(یعنی) جو لوگ ایمان لائے اور پرہیزگار رہے');
INSERT INTO "jalandhari" VALUES(10,64,'10:64','ان کے لیے دنیا کی زندگی میں بھی بشارت ہے اور آخرت میں بھی۔ خدا کی باتیں بدلتی نہیں۔ یہی تو بڑی کامیابی ہے');
INSERT INTO "jalandhari" VALUES(10,65,'10:65','اور (اے پیغمبر) ان لوگوں کی باتوں سے آزردہ نہ ہونا (کیونکہ) عزت سب خدا ہی کی ہے وہ (سب کچھ) سنتا (اور) جانتا ہے');
INSERT INTO "jalandhari" VALUES(10,66,'10:66','سن رکھو کہ جو مخلوق آسمانوں میں ہے اور جو زمین میں ہے سب خدا کے (بندے اور اس کے مملوک) ہیں۔ اور یہ جو خدا کے سوا (اپنے بنائے ہوئے) شریکوں کو پکارتے ہیں۔ وہ (کسی اور چیز کے) پیچھے نہیں چلتے۔ صرف ظن کے پیچھے چلتے ہیں اور محض اٹکلیں دوڑا رہے ہیں');
INSERT INTO "jalandhari" VALUES(10,67,'10:67','وہی تو ہے جس نے تمہارے لیے رات بنائی تاکہ اس میں آرام کرو اور روز روشن بنایا ( تاکہ اس میں کام کرو) جو لوگ (مادہٴ) سماعت رکھتے ہیں ان کے لیے ان میں نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(10,68,'10:68','(بعض لوگ) کہتے ہیں کہ خدا نے بیٹا بنا لیا ہے۔ اس کی ذات (اولاد سے) پاک ہے (اور) وہ بےنیاز ہے۔ جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے وہ سب اسی کا ہے (اے افتراء پردازو) تمہارے پاس اس (قول باطل) کی کوئی دلیل نہیں ہے۔ تم خدا کی نسبت ایسی بات کیوں کہتے ہو جو جانتے نہیں');
INSERT INTO "jalandhari" VALUES(10,69,'10:69','کہہ دو جو لوگ خدا پر جھوٹ بہتان باندھتے ہیں فلاح نہیں پائیں گے');
INSERT INTO "jalandhari" VALUES(10,70,'10:70','(ان کے لیے جو) فائدے ہیں دنیا میں (ہیں) پھر ان کو ہماری ہی طرف لوٹ کر آنا ہے۔ اس وقت ہم ان کو شدید عذاب (کے مزے) چکھائیں گے کیونکہ کفر (کی باتیں) کیا کرتے تھے');
INSERT INTO "jalandhari" VALUES(10,71,'10:71','اور ان کو نوح کا قصہ پڑھ کر سنادو۔ جب انہوں نے اپنی قوم سے کہا کہ اے قوم! اگر تم کو میرا تم میں رہنا اور خدا کی آیتوں سے نصیحت کرنا ناگوار ہو تو میں خدا پر بھروسہ رکھتا ہوں۔ تم اپنے شریکوں کے ساتھ مل کر ایک کام (جو میرے بارے میں کرنا چاہو) مقرر کرلو اور وہ تمہاری تمام جماعت (کو معلوم ہوجائے اور کسی) سے پوشیدہ نہ رہے اور پھر وہ کام میرے حق میں کر گزرو اور مجھے مہلت نہ دو');
INSERT INTO "jalandhari" VALUES(10,72,'10:72','اور اگر تم نے منہ پھیر لیا تو (تم جانتے ہو کہ) میں نے تم سے کچھ معاوضہ نہیں مانگا۔ میرا معاوضہ تو خدا کے ذمے ہے۔ اور مجھے حکم ہوا ہے کہ میں فرمانبرداروں میں رہوں');
INSERT INTO "jalandhari" VALUES(10,73,'10:73','لیکن ان لوگوں نے ان کی تکذیب کی تو ہم نے ان کو اور جو لوگ ان کے ساتھ کشتی میں سوار تھے سب کو (طوفان سے) بچا لیا اور انہیں (زمین میں) خلیفہ بنادیا اور جن لوگوں نے ہماری آیتوں کو جھٹلایا ان کو غرق کر دیا تو دیکھ لو کہ جو لوگ ڈرائے گئے تھے ان کا کیا انجام ہوا');
INSERT INTO "jalandhari" VALUES(10,74,'10:74','پھر نوح کے بعد ہم نے اور پیغمبر اپنی اپنی قوم کی طرف بھیجے۔ تو وہ ان کے پاس کھلی نشانیاں لے کر آئے۔ مگر وہ لوگ ایسے نہ تھے کہ جس چیز کی پہلے تکذیب کرچکے تھے اس پر ایمان لے آتے۔ اسی طرح ہم زیادتی کرنے والوں کے دلوں پر مہر لگا دیتے ہیں');
INSERT INTO "jalandhari" VALUES(10,75,'10:75','پھر ان کے بعد ہم نے موسیٰ اور ہارون کو اپنی نشانیاں دے کر فرعون اور اس کے سرداروں کے پاس بھیجا تو انہوں نے تکبر کیا اور وہ گنہگار لوگ تھے');
INSERT INTO "jalandhari" VALUES(10,76,'10:76','تو جب ان کے پاس ہمارے ہاں سے حق آیا تو کہنے لگے کہ یہ تو صریح جادو ہے');
INSERT INTO "jalandhari" VALUES(10,77,'10:77','موسیٰ نے کہا کیا تم حق کے بارے میں جب وہ تمہارے پاس آیا یہ کہتے ہو کہ یہ جادو ہے۔ حالانکہ جادوگر فلاح نہیں پانے کے');
INSERT INTO "jalandhari" VALUES(10,78,'10:78','وہ بولے کیا تم ہمارے پاس اس لئے آئے ہو کہ جس (راہ) پر ہم اپنے باپ دادا کو پاتے رہے ہیں اس سے ہم کو پھیردو۔ اور (اس) ملک میں تم دونوں کی ہی سرداری ہوجائے اور ہم تم پر ایمان لانے والے نہیں ہیں');
INSERT INTO "jalandhari" VALUES(10,79,'10:79','اور فرعون نے حکم دیا کہ سب کامل فن جادوگروں کو ہمارے پاس لے آؤ');
INSERT INTO "jalandhari" VALUES(10,80,'10:80','جب جادوگر آئے تو موسیٰ نے ان سے کہا تم کو جو ڈالنا ہے ڈالو');
INSERT INTO "jalandhari" VALUES(10,81,'10:81','جب انہوں نے (اپنی رسیوں اور لاٹھیوں کو) ڈالا تو موسیٰ نے کہا کہ جو چیزیں تم (بنا کر) لائے ہو جادو ہے خدا اس کو بھی نیست ونابود کردے گا۔ خدا شریروں کے کام سنوارا نہیں کرتا');
INSERT INTO "jalandhari" VALUES(10,82,'10:82','اور خدا اپنے حکم سے سچ کو سچ ہی کردے گا اگرچہ گنہگار برا ہی مانیں');
INSERT INTO "jalandhari" VALUES(10,83,'10:83','تو موسیٰ پر کوئی ایمان نہ لایا۔ مگر اس کی قوم میں سے چند لڑکے (اور وہ بھی) فرعون اور اس کے اہل دربار سے ڈرتے ڈرتے کہ کہیں وہ ان کو آفت میں نہ پھنسا دے۔ اور فرعون ملک میں متکبر ومتغلب اور (کبر وکفر) میں حد سے بڑھا ہوا تھا');
INSERT INTO "jalandhari" VALUES(10,84,'10:84','اور موسیٰ نے کہا کہ بھائیو! اگر تم خدا پر ایمان لائے ہو تو اگر (دل سے) فرمانبردار ہو تو اسی پر بھروسہ رکھو');
INSERT INTO "jalandhari" VALUES(10,85,'10:85','تو وہ بولے کہ ہم خدا ہی پر بھروسہ رکھتے ہیں۔ اے ہمارے پروردگار ہم کو ظالم لوگوں کے ہاتھ سے آزمائش میں نہ ڈال');
INSERT INTO "jalandhari" VALUES(10,86,'10:86','اور اپنی رحمت سے قوم کفار سے نجات بخش');
INSERT INTO "jalandhari" VALUES(10,87,'10:87','اور ہم نے موسیٰ اور اس کے بھائی کی طرف وحی بھیجی کہ اپنے لوگوں کے لیے مصر میں گھر بناؤ اور اپنے گھروں کو قبلہ (یعنی مسجدیں) ٹھہراؤ اور نماز پڑھو۔ اور مومنوں کو خوشخبری سنادو');
INSERT INTO "jalandhari" VALUES(10,88,'10:88','اور موسیٰ نے کہا اے ہمارے پروردگار تو نے فرعون اور اس کے سرداروں کو دنیا کی زندگی میں (بہت سا) سازو برگ اور مال وزر دے رکھا ہے۔ اے پروردگار ان کا مال یہ ہے کہ تیرے رستے سے گمراہ کردیں۔ اے پروردگار ان کے مال کو برباد کردے اور ان کے دلوں کو سخت کردے کہ ایمان نہ لائیں جب تک عذاب الیم نہ دیکھ لیں');
INSERT INTO "jalandhari" VALUES(10,89,'10:89','خدا نے فرمایا کہ تمہاری دعا قبول کرلی گئی تو تم ثابت قدم رہنا اور بےعقلوں کے رستے نہ چلنا');
INSERT INTO "jalandhari" VALUES(10,90,'10:90','اور ہم نے بنی اسرائیل کو دریا سے پار کردیا تو فرعون اور اس کے لشکر نے سرکشی اور تعدی سے ان کا تعاقب کیا۔ یہاں تک کہ جب اس کو غرق (کے عذاب) نے آپکڑا تو کہنے لگا کہ میں ایمان لایا کہ جس (خدا) پر بنی اسرائیل ایمان لائے ہیں اس کے سوا کوئی معبود نہیں اور میں فرمانبرداروں میں ہوں');
INSERT INTO "jalandhari" VALUES(10,91,'10:91','(جواب ملا کہ) اب (ایمان لاتا ہے) حالانکہ تو پہلے نافرمانی کرتا رہا اور مفسد بنا رہا');
INSERT INTO "jalandhari" VALUES(10,92,'10:92','تو آج ہم تیرے بدن کو (دریا سے) نکال لیں گے تاکہ تو پچھلوں کے لئے عبرت ہو۔ اور بہت سے لوگ ہماری نشانیوں سے بےخبر ہیں');
INSERT INTO "jalandhari" VALUES(10,93,'10:93','اور ہم نے بنی اسرائیل کو رہنے کو عمدہ جگہ دی اور کھانے کو پاکیزہ چیزیں عطا کیں لیکن وہ باوجود علم ہونے کے اختلاف کرتے رہے۔ بےشک جن باتوں میں وہ اختلاف کرتے رہے ہیں تمہارا پروردگار قیامت کے دن ان میں ان باتوں کا فیصلہ کردے گا');
INSERT INTO "jalandhari" VALUES(10,94,'10:94','اگر تم کو اس (کتاب کے) بارے میں جو ہم نے تم پر نازل کی ہے کچھ شک ہو تو جو لوگ تم سے پہلے کی (اُتری ہوئی) کتابیں پڑھتے ہیں ان سے پوچھ لو۔ تمہارے پروردگار کی طرف سے تمہارے پاس حق آچکا ہے تو تم ہرگز شک کرنے والوں میں نہ ہونا');
INSERT INTO "jalandhari" VALUES(10,95,'10:95','اور نہ ان لوگوں میں ہونا جو خدا کی آیتوں کی تکذیب کرتے ہیں نہیں تو نقصان اٹھاؤ گے');
INSERT INTO "jalandhari" VALUES(10,96,'10:96','جن لوگوں کے بارے میں خدا کا حکم (عذاب) قرار پاچکا ہے وہ ایمان نہیں لانے کے');
INSERT INTO "jalandhari" VALUES(10,97,'10:97','جب تک کہ عذاب الیم نہ دیکھ لیں خواہ ان کے پاس ہر (طرح کی) نشانی آجائے');
INSERT INTO "jalandhari" VALUES(10,98,'10:98','تو کوئی بستی ایسی کیوں نہ ہوئی کہ ایمان لاتی تو اس کا ایمان اسے نفع دیتا ہاں یونس کی قوم۔ جب ایمان لائی تو ہم نے دنیا کی زندگی میں ان سے ذلت کا عذاب دور کردیا اور ایک مدت تک (فوائد دنیاوی سے) ان کو بہرہ مند رکھا');
INSERT INTO "jalandhari" VALUES(10,99,'10:99','اور اگر تمہارا پروردگار چاہتا تو جتنے لوگ زمین پر ہیں سب کے سب ایمان لے آتے۔ تو کیا تم لوگوں پر زبردستی کرنا چاہتے ہو کہ وہ مومن ہوجائیں');
INSERT INTO "jalandhari" VALUES(10,100,'10:100','حالانکہ کسی شخص کو قدرت نہیں ہے کہ خدا کے حکم کے بغیر ایمان لائے۔ اور جو لوگ بےعقل ہیں ان پر وہ (کفر وذلت کی) نجاست ڈالتا ہے');
INSERT INTO "jalandhari" VALUES(10,101,'10:101','(ان کفار سے) کہو دیکھو تو زمین اور آسمانوں میں کیا کچھ ہے۔ مگر جو لوگ ایمان نہیں رکھتے ان کی نشانیاں اور ڈرواے کچھ کام نہیں آتے');
INSERT INTO "jalandhari" VALUES(10,102,'10:102','سو جیسے (برے) دن ان سے پہلے لوگوں پر گزر چکے ہیں اسی طرح کے (دنوں کے) یہ منتظر ہیں۔ کہہ دو کہ تم بھی انتظار کرو۔ میں بھی تمہارے ساتھ انتظار کرتا ہوں');
INSERT INTO "jalandhari" VALUES(10,103,'10:103','اور ہم اپنے پیغمبروں کو اور مومنوں کو نجات دیتے رہے ہیں۔ اسی طرح ہمارا ذمہ ہے کہ مسلمانوں کو نجات دیں');
INSERT INTO "jalandhari" VALUES(10,104,'10:104','(اے پیغمبر) کہہ دو کہ لوگو اگر تم کو میرے دین میں کسی طرح کا شک ہو تو (سن رکھو کہ) جن لوگوں کی تم خدا کے سوا عبادت کرتے ہو میں ان کی عبادت نہیں کرتا۔ بلکہ میں خدا کی عبادت کرتا ہوں جو تمھاری روحیں قبض کرلیتا ہے اور مجھ کو یہی حکم ہوا ہے کہ ایمان لانے والوں میں ہوں');
INSERT INTO "jalandhari" VALUES(10,105,'10:105','اور یہ کہ (اے محمد سب سے) یکسو ہو کر دین (اسلام) کی پیروی کئے جاؤ۔ اور مشرکوں میں ہرگز نہ ہونا');
INSERT INTO "jalandhari" VALUES(10,106,'10:106','اور خدا کو چھوڑ کر ایسی چیز کو نہ پکارنا جو نہ تمہارا کچھ بھلا کرسکے اور نہ کچھ بگاڑ سکے۔ اگر ایسا کرو گے تو ظالموں میں ہوجاؤ گے');
INSERT INTO "jalandhari" VALUES(10,107,'10:107','اور اگر خدا تم کو کوئی تکلیف پہنچائے تو اس کے سوا اس کا کوئی دور کرنے والا نہیں اور اگر تم سے بھلائی کرنی چاہے تو اس کے فضل کو کوئی روکنے والا نہیں۔ وہ اپنے بندوں میں سے جسے چاہتا ہے فائدہ پہنچاتا ہے اور وہ بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(10,108,'10:108','کہہ دو کہ لوگو تمہارے پروردگار کے ہاں سے تمہارے پاس حق آچکا ہے تو جو کوئی ہدایت حاصل کرتا ہے تو ہدایت سے اپنے ہی حق میں بھلائی کرتا ہے۔ اور جو گمراہی اختیار کرتا ہے تو گمراہی سے اپنا ہی نقصان کرتا ہے۔ اور میں تمہارا وکیل نہیں ہوں');
INSERT INTO "jalandhari" VALUES(10,109,'10:109','اور (اے پیغمبر) تم کو جو حکم بھیجا جاتا ہے اس کی پیروی کئے جاؤ اور (تکلیفوں پر) صبر کرو یہاں تک کہ خدا فیصلہ کردے۔ اور وہ سب سے بہتر فیصلہ کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(11,1,'11:1','الٓرا۔ یہ وہ کتاب ہے جس کی آیتیں مستحکم ہیں اور خدائے حکیم وخبیر کی طرف سے بہ تفصیل بیان کردی گئی ہے');
INSERT INTO "jalandhari" VALUES(11,2,'11:2','(وہ یہ) کہ خدا کے سوا کسی کی عبادت نہ کرو اور میں اس کی طرف سے تم کو ڈر سنانے والا اور خوشخبری دینے والا ہوں');
INSERT INTO "jalandhari" VALUES(11,3,'11:3','اور یہ کہ اپنے پروردگار سے بخشش مانگو اور اس کے آگے توبہ کرو وہ تو تم کو ایک وقت مقررہ تک متاع نیک سے بہرہ مند کرے گا اور ہر صاحب بزرگ کو اس کی بزرگی (کی داد) دے گا۔ اور اگر روگردانی کرو گے تو مجھے تمہارے بارے میں (قیامت کے) بڑے دن کے عذاب کا ڈر ہے');
INSERT INTO "jalandhari" VALUES(11,4,'11:4','تم (سب) کو خدا کی طرف لوٹ کر جانا ہے اور وہ ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(11,5,'11:5','دیکھو یہ اپنے سینوں کو دوھرا کرتے ہیں تاکہ خدا سے پردہ کریں۔ سن رکھو جس وقت یہ کپڑوں میں لپٹ کر پڑتے ہیں (تب بھی) وہ ان کی چھپی اور کھلی باتوں کو جانتا ہے۔ وہ تو دلوں تک کی باتوں سے آگاہ ہے');
INSERT INTO "jalandhari" VALUES(11,6,'11:6','اور زمین پر کوئی چلنے پھرنے والا نہیں مگر اس کا رزق خدا کے ذمے ہے وہ جہاں رہتا ہے، اسے بھی جانتا ہے اور جہاں سونپا جاتا ہے اسے بھی۔ یہ سب کچھ کتاب روشن میں (لکھا ہوا) ہے');
INSERT INTO "jalandhari" VALUES(11,7,'11:7','اور وہی تو ہے جس نے آسمانوں اور زمین کو چھ دن میں بنایا اور (اس وقت) اس کا عرش پانی پر تھا۔ (تمہارے پیدا کرنے سے) مقصود یہ ہے کہ وہ تم کو آزمائے کہ تم میں عمل کے لحاظ سے کون بہتر ہے اور اگر تم کہو کہ تم لوگ مرنے کے بعد (زندہ کرکے) اٹھائے جاؤ گے تو کافر کہہ دیں گے کہ یہ تو کھلا جادو ہے');
INSERT INTO "jalandhari" VALUES(11,8,'11:8','اور اگر ایک مدت معین تک ہم ان سے عذاب روک دیں تو کہیں گے کہ کون سی چیز عذاب روکے ہوئے ہے۔ دیکھو جس روز وہ ان پر واقع ہوگا (پھر) ٹلنے کا نہیں اور جس چیز کے ساتھ یہ استہزاء کیا کرتے ہیں وہ ان کو گھیر لے گی');
INSERT INTO "jalandhari" VALUES(11,9,'11:9','اور اگر ہم انسان کو اپنے پاس سے نعمت بخشیں پھر اس سے اس کو چھین لیں تو ناامید (اور) ناشکرا (ہوجاتا) ہے');
INSERT INTO "jalandhari" VALUES(11,10,'11:10','اور اگر تکلیف پہنچنے کے بعد آسائش کا مزہ چکھائیں تو (خوش ہو کر) کہتا ہے کہ (آہا) سب سختیاں مجھ سے دور ہوگئیں۔ بےشک وہ خوشیاں منانے والا (اور) فخر کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(11,11,'11:11','ہاں جنہوں نے صبر کیا اور عمل نیک کئے۔ یہی ہیں جن کے لیے بخشش اور اجرعظیم ہے');
INSERT INTO "jalandhari" VALUES(11,12,'11:12','شاید تم کچھ چیز وحی میں سے جو تمہارے پاس آتی ہے چھوڑ دو اور اس (خیال) سے کہ تمہارا دل تنگ ہو کہ (کافر) یہ کہنے لگیں کہ اس پر کوئی خزانہ کیوں نہ نازل ہوا یا اس کے ساتھ کوئی فرشتہ کیوں نہیں آیا۔ اے محمدصلى الله عليه وسلم! تم تو صرف نصیحت کرنے والے ہو۔ اور خدا ہر چیز کا نگہبان ہے');
INSERT INTO "jalandhari" VALUES(11,13,'11:13','یہ کیا کہتے ہیں کہ اس نے قرآن ازخود بنا لیا ہے؟ کہہ دو کہ اگر سچے ہو تو تم بھی ایسی دس سورتیں بنا لاؤ اور خدا کے سوا جس جس کو بلاسکتے ہو، بلا بھی لو');
INSERT INTO "jalandhari" VALUES(11,14,'11:14','اگر وہ تمہاری بات قبول نہ کریں تو جان لو کہ وہ خدا کے علم سے اُترا ہے اور یہ کہ اس کے سوا کوئی معبود نہیں تو تمہیں بھی اسلام لے آنا چاہئیے');
INSERT INTO "jalandhari" VALUES(11,15,'11:15','جو لوگ دنیا کی زندگی اور اس کی زیب و زینت کے طالب ہوں ہم ان کے اعمال کا بدلہ انہیں دنیا میں ہی دے دیتے ہیں اور اس میں ان کی حق تلفی نہیں کی جاتی');
INSERT INTO "jalandhari" VALUES(11,16,'11:16','یہ وہ لوگ ہیں جن کے لیے آخرت میں آتش (جہنم) کے سوا کوئی چیز نہیں اور جو عمل انہوں نے دنیا میں کئے سب برباد اور جو کچھ وہ کرتے رہے، سب ضائع');
INSERT INTO "jalandhari" VALUES(11,17,'11:17','بھلا جو لوگ اپنے پروردگار کی طرف سے (روشن) دلیل رکھتے ہوں اور ان کے ساتھ ایک (آسمانی) گواہ بھی اس کی جانب سے ہو اور اس سے پہلے موسیٰ کی کتاب ہو جو پیشوا اور رحمت ہے (تو کیا وہ قرآن پر ایمان نہیں لائیں گے) یہی لوگ اس پر ایمان لاتے ہیں اور جو کوئی اور فرقوں میں سے اس سے منکر ہو تو اس کا ٹھکانہ آگ ہے۔ تو تم اس (قرآن) سے شک میں نہ ہونا۔ یہ تمہارے پروردگار کی طرف سے حق ہے لیکن اکثر لوگ ایمان نہیں لاتے');
INSERT INTO "jalandhari" VALUES(11,18,'11:18','اور اس سے بڑھ کر ظالم کون ہوگا جو خدا پر جھوٹ افتراء کرے ایسے لوگ خدا کے سامنے پیش کئے جائیں گے اور گواہ کہیں گے کہ یہی لوگ ہیں جنہوں نے اپنے پروردگار پر جھوٹ بولا تھا۔ سن رکھو کہ ظالموں پر الله کی لعنت ہے');
INSERT INTO "jalandhari" VALUES(11,19,'11:19','جو خدا کے رستے سے روکتے ہیں اور اس میں کجی چاہتے ہیں اور وہ آخرت سے بھی انکار کرتے ہیں');
INSERT INTO "jalandhari" VALUES(11,20,'11:20','یہ لوگ زمین میں (کہیں بھاگ کر خدا کو) نہیں ہرا سکتے اور نہ خدا کے سوا کوئی ان کا حمایتی ہے۔ (اے پیغمبر) ان کو دگنا عذاب دیا جائے گا کیونکہ یہ (شدت کفر سے تمہاری بات) نہیں سن سکتے تھے اور نہ (تم کو) دیکھ سکتے تھے');
INSERT INTO "jalandhari" VALUES(11,21,'11:21','یہی ہیں جنہوں نے اپنے تئیں خسارے میں ڈالا اور جو کچھ وہ افتراء کیا کرتے تھے ان سے جاتا رہا');
INSERT INTO "jalandhari" VALUES(11,22,'11:22','بلاشبہ یہ لوگ آخرت میں سب سے زیادہ نقصان پانے والے ہیں');
INSERT INTO "jalandhari" VALUES(11,23,'11:23','جو لوگ ایمان لائے اور عمل نیک کئے اور اپنے پروردگار کے آگے عاجزی کی۔ یہی صاحب جنت ہیں اور ہمیشہ اس میں رہیں گے');
INSERT INTO "jalandhari" VALUES(11,24,'11:24','دونوں فرقوں (یعنی کافرومومن) کی مثال ایسی ہے جیسے ایک اندھا بہرا ہو اور ایک دیکھتا سنتا۔ بھلا دونوں کا حال یکساں ہوسکتا ہے؟ پھر تم سوچتے کیوں نہیں؟');
INSERT INTO "jalandhari" VALUES(11,25,'11:25','اور ہم نے نوح کو ان کی قوم کی طرف بھیجا (تو انہوں نے ان سے کہا) کہ میں تم کو کھول کھول کر ڈر سنانے اور پیغام پہنچانے آیا ہوں');
INSERT INTO "jalandhari" VALUES(11,26,'11:26','کہ خدا کے سوا کسی کی عبادت نہ کرو۔ مجھے تمہاری نسبت عذاب الیم کا خوف ہے');
INSERT INTO "jalandhari" VALUES(11,27,'11:27','تو ان کی قوم کے سردار جو کافر تھے کہنے لگے کہ ہم تم کو اپنے ہی جیسا ایک آدمی دیکھتے ہیں اور یہ بھی دیکھتے ہیں کہ تمہارے پیرو وہی لوگ ہوئے ہیں جو ہم میں ادنیٰ درجے کے ہیں۔ اور وہ بھی رائے ظاہر سے (نہ غوروتعمق سے) اور ہم تم میں اپنے اوپر کسی طرح کی فضیلت نہیں دیکھتے بلکہ تمہیں جھوٹا خیال کرتے ہیں');
INSERT INTO "jalandhari" VALUES(11,28,'11:28','انہوں نے کہا کہ اے قوم! دیکھو تو اگر میں اپنے پروردگار کی طرف سے دلیل (روشن) رکھتا ہوں اور اس نے مجھے اپنے ہاں سے رحمت بخشی ہو جس کی حقیقت تم سے پوشیدہ رکھی گئی ہے۔ تو کیا ہم اس کے لیے تمہیں مجبور کرسکتے ہیں اور تم ہو کہ اس سے ناخوش ہو رہے ہو');
INSERT INTO "jalandhari" VALUES(11,29,'11:29','اور اے قوم! میں اس (نصیحت) کے بدلے تم سے مال وزر کا خواہاں نہیں ہوں، میرا صلہ تو خدا کے ذمے ہے اور جو لوگ ایمان لائے ہیں، میں ان کو نکالنے والا بھی نہیں ہوں۔ وہ تو اپنے پروردگار سے ملنے والے ہیں لیکن میں دیکھتا ہوں کہ تم لوگ نادانی کر رہے ہو');
INSERT INTO "jalandhari" VALUES(11,30,'11:30','اور برادران ملت! اگر میں ان کو نکال دوں تو (عذاب) خدا سے (بچانے کے لیے) کون میری مدد کرسکتا ہے۔ بھلا تم غور کیوں نہیں کرتے؟');
INSERT INTO "jalandhari" VALUES(11,31,'11:31','میں نہ تم سے یہ کہتا ہوں کہ میرے پاس خدا کے خزانے ہیں اور نہ یہ کہ میں غیب جانتا ہوں اور نہ یہ کہتا ہوں کہ میں فرشتہ ہوں اور نہ ان لوگوں کی نسبت جن کو تم حقارت کی نظر سے دیکھتے ہو یہ کہتا ہوں کہ خدا ان کو بھلائی (یعنی اعمال کی جزائے نیک) نہیں دے گا جو ان کے دلوں میں ہے اسے خدا خوب جانتا ہے۔ اگر میں ایسا کہوں تو بےانصافوں میں ہوں');
INSERT INTO "jalandhari" VALUES(11,32,'11:32','انہوں نے کہا کہ نوح تم نے ہم سے جھگڑا تو کیا اور جھگڑا بھی بہت کیا۔ لیکن اگر سچے ہو تو جس چیز سے ہمیں ڈراتے ہو وہ ہم پر لا نازل کرو');
INSERT INTO "jalandhari" VALUES(11,33,'11:33','نوح نے کہا کہ اس کو خدا ہی چاہے گا تو نازل کرے گا۔ اور تم (اُس کو کسی طرح) ہرا نہیں سکتے');
INSERT INTO "jalandhari" VALUES(11,34,'11:34','اور اگر میں یہ چاہوں کہ تمہاری خیرخواہی کروں اور خدا یہ چاہے وہ تمہیں گمراہ کرے تو میری خیرخواہی تم کو کچھ فائدہ نہیں دے سکتی۔ وہی تمہارا پروردگار ہے اور تمہیں اس کی طرف لوٹ کر جانا ہے');
INSERT INTO "jalandhari" VALUES(11,35,'11:35','کیا یہ کہتے ہیں کہ اس (پیغمبر) نے یہ قرآن اپنے دل سے بنا لیا ہے۔ کہہ دو کہ اگر میں نے دل سے بنالیا ہے تو میرے گناہ کا وبال مجھ پر اور جو گناہ تم کرتے ہو اس سے میں بری الذمہ ہوں');
INSERT INTO "jalandhari" VALUES(11,36,'11:36','اور نوح کی طرف وحی کی گئی کہ تمہاری قوم میں جو لوگ ایمان (لاچکے)، ان کے سوا کوئی اور ایمان نہیں لائے گا تو جو کام یہ کر رہے ہیں ان کی وجہ سے غم نہ کھاؤ');
INSERT INTO "jalandhari" VALUES(11,37,'11:37','اور ایک کشتی ہمارے حکم سے ہمارے روبرو بناؤ۔ اور جو لوگ ظالم ہیں ان کے بارے میں ہم سے کچھ نہ کہنا کیونکہ وہ ضرور غرق کردیئے جائیں گے');
INSERT INTO "jalandhari" VALUES(11,38,'11:38','تو نوح نے کشتی بنانی شروع کردی۔ اور جب ان کی قوم کے سردار ان کے پاس سے گزرتے تو ان سے تمسخر کرتے۔ وہ کہتے کہ اگر تم ہم سے تمسخر کرتے ہو تو جس طرح تم ہم سے تمسخر کرتے ہو اس طرح (ایک وقت) ہم بھی تم سے تمسخر کریں گے');
INSERT INTO "jalandhari" VALUES(11,39,'11:39','اور تم کو جلد معلوم ہوجائے گا کہ کس پر عذاب آتا ہے اور جو اسے رسوا کرے گا اور کس پر ہمیشہ کا عذاب نازل ہوتا ہے');
INSERT INTO "jalandhari" VALUES(11,40,'11:40','یہاں تک کہ جب ہمارا حکم آپہنچا اور تنور جوش مارنے لگا تو ہم نے نوح کو حکم دیا کہ ہر قسم (کے جانداروں) میں سے جوڑا جوڑا (یعنی) دو (دو جانور۔ ایک ایک نر اور ایک ایک مادہ) لے لو اور جس شخص کی نسبت حکم ہوچکا ہے (کہ ہلاک ہوجائے گا) اس کو چھوڑ کر اپنے گھر والوں کو جو ایمان لایا ہو اس کو کشتی میں سوار کر لو اور ان کے ساتھ ایمان بہت ہی کم لوگ لائے تھے');
INSERT INTO "jalandhari" VALUES(11,41,'11:41','(نوح نے) کہا کہ خدا کا نام لے کر (کہ اسی کے ہاتھ میں اس کا) چلنا اور ٹھہرنا (ہے) اس میں سوار ہوجاؤ۔ بےشک میرا پروردگار بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(11,42,'11:42','اور وہ ان کو لے کر (طوفان کی) لہروں میں چلنے لگی۔ (لہریں کیا تھیں) گویا پہاڑ (تھے) اس وقت نوح نے اپنے بیٹے کو کہ جو (کشتی سے) الگ تھا، پکارا کہ بیٹا ہمارے ساتھ سوار ہوجا اور کافروں میں شامل نہ ہو');
INSERT INTO "jalandhari" VALUES(11,43,'11:43','اس نے کہا کہ میں (ابھی) پہاڑ سے جا لگوں گا، وہ مجھے پانی سے بچالے گا۔ انہوں نے کہا کہ آج خدا کے عذاب سے کوئی بچانے والا نہیں (اور نہ کوئی بچ سکتا ہے) مگر جس پر خدا رحم کرے۔ اتنے میں دونوں کے درمیان لہر آحائل ہوئی اور وہ ڈوب کر رہ گیا');
INSERT INTO "jalandhari" VALUES(11,44,'11:44','اور حکم دیا گیا کہ اے زمین اپنا پانی نگل جا اور اے آسمان تھم جا۔ تو پانی خشک ہوگیا اور کام تمام کردیا گیا اور کشی کوہ جودی پر جا ٹھہری۔ اور کہہ دیا گیا کہ بےانصاف لوگوں پر لعنت');
INSERT INTO "jalandhari" VALUES(11,45,'11:45','اور نوح نے اپنے پروردگار کو پکارا اور کہا کہ پروردگار میرا بیٹا بھی میرے گھر والوں میں ہے (تو اس کو بھی نجات دے) تیرا وعدہ سچا ہے اور تو سب سے بہتر حاکم ہے');
INSERT INTO "jalandhari" VALUES(11,46,'11:46','خدا نے فرمایا کہ نوح وہ تیرے گھر والوں میں نہیں ہے وہ تو ناشائستہ افعال ہے تو جس چیز کی تم کو حقیقت معلوم نہیں ہے اس کے بارے میں مجھ سے سوال ہی نہ کرو۔ اور میں تم کو نصیحت کرتا ہوں کہ نادان نہ بنو');
INSERT INTO "jalandhari" VALUES(11,47,'11:47','نوح نے کہا پروردگار میں تجھ سے پناہ مانگتا ہوں کہ ایسی چیز کا تجھ سے سوال کروں جس کی حقیقت مجھے معلوم نہیں۔ اور اگر تو مجھے نہیں بخشے گا اور مجھ پر رحم نہیں کرے گا تو میں تباہ ہوجاؤں گا');
INSERT INTO "jalandhari" VALUES(11,48,'11:48','حکم ہوا کہ نوح ہماری طرف سے سلامتی اور برکتوں کے ساتھ (جو) تم پر اور تمہارے ساتھ کی جماعتوں پر (نازل کی گئی ہیں) اتر آؤ۔ اور کچھ اور جماعتیں ہوں گی جن کو ہم (دنیا کے فوائد سے) محظوظ کریں گے پھر ان کو ہماری طرف سے عذاب الیم پہنچے گا');
INSERT INTO "jalandhari" VALUES(11,49,'11:49','یہ (حالات) منجملہ غیب کی خبروں کے ہیں جو ہم تمہاری طرف بھیجتے ہیں۔ اور اس سے پہلے نہ تم ہی ان کو جانتے تھے اور نہ تمہاری قوم (ہی ان سے واقف تھی) تو صبر کرو کہ انجام پرہیزگاروں ہی کا (بھلا) ہے');
INSERT INTO "jalandhari" VALUES(11,50,'11:50','اور ہم نے عاد کی طرف ان کے بھائی ہود (کو بھیجا) انہوں نے کہا کہ میری قوم! خدا ہی کی عبادت کرو، اس کے سوا تمہارا کوئی معبود نہیں۔ تم (شرک کرکے خدا پر) محض بہتان باندھتے ہو');
INSERT INTO "jalandhari" VALUES(11,51,'11:51','میری قوم! میں اس (وعظ و نصیحت) کا تم سے کچھ صلہ نہیں مانگتا۔ میرا صلہ تو اس کے ذمّے ہے جس نے مجھے پیدا کیا۔ بھلا تم سمجھتے کیوں نہیں؟');
INSERT INTO "jalandhari" VALUES(11,52,'11:52','اور اے قوم! اپنے پروردگار سے بخشش مانگو پھر اس کے آگے توبہ کرو۔ وہ تم پر آسمان سے موسلادھار مینہ برسائے گا اور تمہاری طاقت پر طاقت بڑھائے گا اور (دیکھو) گنہگار بن کر روگردانی نہ کرو');
INSERT INTO "jalandhari" VALUES(11,53,'11:53','وہ بولے ہود تم ہمارے پاس کوئی دلیل ظاہر نہیں لائے اور ہم (صرف) تمہارے کہنے سے نہ اپنے معبودوں کو چھوڑنے والے ہیں اور نہ تم پر ایمان لانے والے ہیں');
INSERT INTO "jalandhari" VALUES(11,54,'11:54','ہم تو یہ سمجھتے ہیں کہ ہمارے کسی معبود نے تمہیں آسیب پہنچا کر (دیوانہ کر) دیا ہے۔ انہوں نے کہا کہ میں خدا کو گواہ کرتا ہوں اور تم بھی گواہ رہو کہ جن کو تم (خدا کا) شریک بناتے ہو میں اس سے بیزار ہوں');
INSERT INTO "jalandhari" VALUES(11,55,'11:55','(یعنی جن کی) خدا کے سوا (عبادت کرتے ہو تو) تم سب مل کر میرے بارے میں جو تدبیر (کرنی چاہو) کرلو اور مجھے مہلت نہ دو');
INSERT INTO "jalandhari" VALUES(11,56,'11:56','میں خدا پر جو میرا اور تمہارا (سب کا) پروردگار ہے، بھروسہ رکھتا ہوں (زمین پر) جو چلنے پھرنے والا ہے وہ اس کو چوٹی سے پکڑے ہوئے ہے۔ بےشک میرا پروردگار سیدھے رستے پر ہے');
INSERT INTO "jalandhari" VALUES(11,57,'11:57','اگر تم روگردانی کرو گے تو جو پیغام میرے ہاتھ تمہاری طرف بھیجا گیا ہے، وہ میں نے تمہیں پہنچا دیا ہے۔ اور میرا پروردگار تمہاری جگہ اور لوگوں کو لابسائے گا۔ اور تم خدا کا کچھ بھی نقصان نہیں کرسکتے۔ میرا پروردگار تو ہر چیز پر نگہبان ہے');
INSERT INTO "jalandhari" VALUES(11,58,'11:58','اور جب ہمارا حکم عذاب آپہنچا تو ہم نے ہود کو اور جو لوگ ان کے ساتھ ایمان لائے تھے ان کو اپنی مہربانی سے بچا لیا۔ اور ان کو عذاب شدید سے نجات دی');
INSERT INTO "jalandhari" VALUES(11,59,'11:59','یہ (وہی) عاد ہیں جنہوں نے خدا کی نشانیوں سے انکار کیا اور اس کے پیغمبروں کی نافرمانی کی اور ہر متکبر وسرکش کا کہا مانا');
INSERT INTO "jalandhari" VALUES(11,60,'11:60','تو اس دنیا میں بھی لعنت ان کے پیچھے لگی رہے گی اور قیامت کے دن بھی (لگی رہے گی) دیکھو عاد نے اپنے پروردگار سے کفر کیا۔ (اور) سن رکھو ہود کی قوم عاد پر پھٹکار ہے');
INSERT INTO "jalandhari" VALUES(11,61,'11:61','اور ثمود کی طرف ان کے بھائی صالح کو (بھیجا) تو انہوں نے کہا کہ قوم! خدا ہی کی عبادت کرو اس کے سوا تمہارا کوئی معبود نہیں۔ اسی نے تم کو زمین سے پیدا کیا اور اس میں آباد کیا تو اس سے مغفرت مانگو اور اس کے آگے توبہ کرو۔ بےشک میرا پروردگار نزدیک (بھی ہے اور دعا کا) قبول کرنے والا (بھی) ہے');
INSERT INTO "jalandhari" VALUES(11,62,'11:62','انہوں نے کہا کہ صالح اس سے پہلے ہم تم سے (کئی طرح کی) امیدیں رکھتے تھے (اب وہ منقطع ہوگئیں) کیا تم ہم کو ان چیزوں کے پوجنے سے منع کرتے ہو جن کو ہمارے بزرگ پوجتے آئے ہیں؟ اور جس بات کی طرف تم ہمیں بلاتے ہو، اس میں ہمیں قوی شبہ ہے');
INSERT INTO "jalandhari" VALUES(11,63,'11:63','صالح نے کہا اے قوم! بھلا دیکھو تو اگر میں اپنے پروردگار کی طرف سے کھلی دلیل پر ہوں اور اس نے مجھے اپنے ہاں سے (نبوت کی) نعمت بخشی ہو تو اگر میں خدا کی نافرمانی کروں تو اس کے سامنے میری کون مدد کرے گا؟ تم تو (کفر کی باتوں سے) میرا نقصان کرتے ہو');
INSERT INTO "jalandhari" VALUES(11,64,'11:64','اور یہ بھی کہا کہ اے قوم! یہ خدا کی اونٹنی تمہارے لیے ایک نشانی (یعنی معجزہ) ہے تو اس کو چھوڑ دو کہ خدا کی زمین میں (جہاں چاہے) چرے اور اس کو کسی طرح کی تکلیف نہ دینا ورنہ تمہیں جلد عذاب آپکڑے گا');
INSERT INTO "jalandhari" VALUES(11,65,'11:65','مگر انہوں نے اس کی کانچیں کاٹ ڈالیں۔ تو (صالح نے) کہا کہ اپنے گھروں میں تم تین دن (اور) فائدہ اٹھا لو۔ یہ وعدہ ہے کہ جھوٹا نہ ہوگا');
INSERT INTO "jalandhari" VALUES(11,66,'11:66','جب ہمارا حکم آگیا تو ہم نے صالح کو اور جو لوگ ان کے ساتھ ایمان لائے تھے ان کو اپنی مہربانی سے بچالیا۔ اور اس دن کی رسوائی سے (محفوظ رکھا)۔ بےشک تمہارا پروردگار طاقتور اور زبردست ہے');
INSERT INTO "jalandhari" VALUES(11,67,'11:67','اور جن لوگوں نے ظلم کیا تھا ان کو چنگھاڑ (کی صورت میں عذاب) نے آپکڑا تو وہ اپنے گھروں میں اوندھے پڑے رہ گئے');
INSERT INTO "jalandhari" VALUES(11,68,'11:68','گویا کبھی ان میں بسے ہی نہ تھے۔ سن رکھو کہ ثمود نے اپنے پروردگار سے کفر کیا۔ اور سن رکھو ثمود پر پھٹکار ہے');
INSERT INTO "jalandhari" VALUES(11,69,'11:69','اور ہمارے فرشتے ابراہیم کے پاس بشارت لے کر آئے تو سلام کہا۔ انہوں نے بھی (جواب میں) سلام کہا۔ ابھی کچھ وقفہ نہیں ہوا تھا کہ (ابراہیم) ایک بھنا ہوا بچھڑا لے آئے');
INSERT INTO "jalandhari" VALUES(11,70,'11:70','جب دیکھا کہ ان کے ہاتھ کھانے کی طرف نہیں جاتے (یعنی وہ کھانا نہیں کھاتے) تو ان کو اجنبی سمجھ کر دل میں خوف کیا۔ (فرشتوں نے) کہا کہ خوف نہ کیجیے، ہم قوم لوط کی طرف (ان کے ہلاک کرنے کو) بھیجے گئے ہیں');
INSERT INTO "jalandhari" VALUES(11,71,'11:71','اور ابراہیم کی بیوی (جو پاس) کھڑی تھی، ہنس پڑی تو ہم نے اس کو اسحاق کی اور اسحاق کے بعد یعقوب کی خوشخبری دی');
INSERT INTO "jalandhari" VALUES(11,72,'11:72','اس نے کہا اے ہے میرے بچہ ہوگا؟ میں تو بڑھیا ہوں اور میرے میاں بھی بوڑھے ہیں۔ یہ تو بڑی عجیب بات ہے');
INSERT INTO "jalandhari" VALUES(11,73,'11:73','انہوں نے کہا کیا تم خدا کی قدرت سے تعجب کرتی ہو؟ اے اہل بیت تم پر خدا کی رحمت اور اس کی برکتیں ہیں۔ وہ سزاوار تعریف اور بزرگوار ہے');
INSERT INTO "jalandhari" VALUES(11,74,'11:74','جب ابراہیم سے خوف جاتا رہا اور ان کو خوشخبری بھی مل گئی تو قوم لوط کے بارے میں لگے ہم سے بحث کرنے');
INSERT INTO "jalandhari" VALUES(11,75,'11:75','بےشک ابراہیم بڑے تحمل والے، نرم دل اور رجوع کرنے والے تھے');
INSERT INTO "jalandhari" VALUES(11,76,'11:76','اے ابراہیم اس بات کو جانے دو۔ تمہارے پروردگار کا حکم آپہنچا ہے۔ اور ان لوگوں پر عذاب آنے والا ہے جو کبھی نہیں ٹلنے کا');
INSERT INTO "jalandhari" VALUES(11,77,'11:77','اور جب ہمارے فرشتے لوط کے پاس آئے تو وہ ان (کے آنے) سے غمناک اور تنگ دل ہوئے اور کہنے لگے کہ آج کا دن بڑی مشکل کا دن ہے');
INSERT INTO "jalandhari" VALUES(11,78,'11:78','اور لوط کی قوم کے لوگ ان کے پاس بےتحاشا دوڑتے ہوئے آئے اور یہ لوگ پہلے ہی سے فعل شنیع کیا کرتے تھے۔ لوط نے کہا کہ اے قوم! یہ (جو) میری (قوم کی) لڑکیاں ہیں، یہ تمہارے لیے (جائز اور) پاک ہیں۔ تو خدا سے ڈرو اور میرے مہمانوں کے (بارے) میں میری آبرو نہ کھوؤ۔ کیا تم میں کوئی بھی شائستہ آدمی نہیں');
INSERT INTO "jalandhari" VALUES(11,79,'11:79','وہ بولے تم کو معلوم ہے کہ تمہاری (قوم کی) بیٹیوں کی ہمیں کچھ حاجت نہیں۔ اور جو ہماری غرض ہے اسے تم (خوب) جانتے ہو');
INSERT INTO "jalandhari" VALUES(11,80,'11:80','لوط نے کہا اے کاش مجھ میں تمہارے مقابلے کی طاقت ہوتی یا کسی مضبوط قلعے میں پناہ پکڑ سکتا');
INSERT INTO "jalandhari" VALUES(11,81,'11:81','فرشتوں نے کہا کہ لوط ہم تمہارے پروردگار کے فرشتے ہیں۔ یہ لوگ ہرگز تم تک نہیں پہنچ سکیں گے تو کچھ رات رہے سے اپنے گھر والوں کو لے کر چل دو اور تم میں سے کوئی شخص پیچھے پھر کر نہ دیکھے۔ مگر تمہاری بیوی کہ جو آفت ان پر پڑنے والی ہے وہی اس پر پڑے گی۔ ان کے (عذاب کے) وعدے کا وقت صبح ہے۔ اور کیا صبح کچھ دور ہے؟');
INSERT INTO "jalandhari" VALUES(11,82,'11:82','تو جب ہمارا حکم آیا ہم نے اس (بستی) کو (اُلٹ کر) نیچے اوپر کردیا اور ان پر پتھر کی تہہ بہ تہہ (یعنی پےدرپے) کنکریاں برسائیں');
INSERT INTO "jalandhari" VALUES(11,83,'11:83','جن پر تمہارے پروردگار کے ہاں سے نشان کئے ہوئے تھے اور وہ بستی ان ظالموں سے کچھ دور نہیں');
INSERT INTO "jalandhari" VALUES(11,84,'11:84','اور مدین کی طرف ان کے بھائی شعیب کو (بھیجا) تو اُنہوں نے کہا کہ اے قوم! خدا ہی کی عبادت کرو کہ اس کے سوا تمہارا کوئی معبود نہیں۔ اور ناپ تول میں کمی نہ کیا کرو۔ میں تو تم کو آسودہ حال دیکھتا ہوں اور (اگر تم ایمان نہ لاؤ گے تو) مجھے تمہارے بارے میں ایک ایسے دن کے عذاب کا خوف ہے جو تم کو گھیر کر رہے گا');
INSERT INTO "jalandhari" VALUES(11,85,'11:85','اور قوم! ماپ اور تول انصاف کے ساتھ پوری پوری کیا کرو اور لوگوں کو ان کی چیزیں کم نہ دیا کرو اور زمین میں خرابی کرتے نہ پھرو');
INSERT INTO "jalandhari" VALUES(11,86,'11:86','اگر تم کو (میرے کہنے کا) یقین ہو تو خدا کا دیا ہوا نفع ہی تمہارے لیے بہتر ہے اور میں تمہارا نگہبان نہیں ہوں');
INSERT INTO "jalandhari" VALUES(11,87,'11:87','انہوں نے کہا شعیب کیا تمہاری نماز تمہیں یہ سکھاتی ہے کہ جن کو ہمارے باپ دادا پوجتے آئے ہیں ہم ان کو ترک کر دیں یا اپنے مال میں تصرف کرنا چاہیں تو نہ کریں۔ تم تو بڑے نرم دل اور راست باز ہو');
INSERT INTO "jalandhari" VALUES(11,88,'11:88','انہوں نے کہا کہ اے قوم! دیکھو تو اگر میں اپنے پروردگار کی طرف سے دلیل روشن پر ہوں اور اس نے اپنے ہاں سے مجھے نیک روزی دی ہو (تو کیا میں ان کے خلاف کروں گا؟) اور میں نہیں چاہتا کہ جس امر سے میں تمہیں منع کروں خود اس کو کرنے لگوں۔ میں تو جہاں تک مجھ سے ہوسکے (تمہارے معاملات کی) اصلاح چاہتا ہوں اور (اس بارے میں) مجھے توفیق کا ملنا خدا ہی (کے فضل) سے ہے۔ میں اسی پر بھروسہ رکھتا ہوں اور اس کی طرف رجوع کرتا ہوں');
INSERT INTO "jalandhari" VALUES(11,89,'11:89','اور اے قوم! میری مخالفت تم سے کوئی ایسا کام نہ کرادے کہ جیسی مصیبت نوح کی قوم یا ہود کی قوم یا صالح کی قوم پر واقع ہوئی تھی ویسی ہی مصیبت تم پر واقع ہو۔ اور لوط کی قوم (کا زمانہ تو) تم سے کچھ دور نہیں');
INSERT INTO "jalandhari" VALUES(11,90,'11:90','اور اپنے پروردگار سے بخشش مانگو اور اس کے آگے توبہ کرو۔ بےشک میرا پروردگار رحم والا (اور) محبت والا ہے');
INSERT INTO "jalandhari" VALUES(11,91,'11:91','اُنہوں نے کہا کہ شعیب تمہاری بہت سی باتیں ہماری سمجھ میں نہیں آتیں اور ہم دیکھتے ہیں کہ تم ہم میں کمزور بھی ہو اور اگر تمہارے بھائی نہ ہوتے تو ہم تم کو سنگسار کر دیتے اور تم ہم پر (کسی طرح بھی) غالب نہیں ہو');
INSERT INTO "jalandhari" VALUES(11,92,'11:92','انہوں نے کہا کہ قوم! کیا میرے بھائی بندوں کا دباؤ تم پر خدا سے زیادہ ہے۔ اور اس کو تم نے پیٹھ پیچھے ڈال رکھا ہے۔ میرا پروردگار تو تمہارے سب اعمال پر احاطہ کیے ہوئے ہے');
INSERT INTO "jalandhari" VALUES(11,93,'11:93','اور برادران ملت! تم اپنی جگہ کام کیے جاؤ میں (اپنی جگہ) کام کیے جاتا ہوں۔ تم کو عنقریب معلوم ہوجائے گا کہ رسوا کرنے والا عذاب کس پر آتا ہے اور جھوٹا کون ہے اور تم بھی انتظار کرو، میں بھی تمہارے ساتھ انتظار کرتا ہوں');
INSERT INTO "jalandhari" VALUES(11,94,'11:94','اور جب ہمارا حکم آپہنچا تو ہم نے شعیب کو اور جو لوگ ان کے ساتھ ایمان لائے تھے ان کو تو اپنی رحمت سے بچا لیا۔ اور جو لوگ ظالم تھے، ان کو چنگھاڑ نے آدبوچا تو وہ اپنے گھروں میں اوندھے پڑے رہ گئے');
INSERT INTO "jalandhari" VALUES(11,95,'11:95','گویا ان میں کبھی بسے ہی نہ تھے۔ سن رکھو کہ مدین پر (ویسی ہی) پھٹکار ہے جیسی ثمود پر پھٹکار تھی');
INSERT INTO "jalandhari" VALUES(11,96,'11:96','اور ہم نے موسیٰ کو اپنی نشانیاں اور دلیل روشن دے کر بھیجا');
INSERT INTO "jalandhari" VALUES(11,97,'11:97','(یعنی) فرعون اور اس کے سرداروں کی طرف۔ تو وہ فرعون ہی کے حکم پر چلے۔ اور فرعون کا حکم درست نہیں تھا');
INSERT INTO "jalandhari" VALUES(11,98,'11:98','وہ قیامت کے دن اپنی قوم کے آگے آگے چلے گا اور ان کو دوزخ میں جا اُتارے گا اور جس مقام پر وہ اُتارے جائیں گے وہ برا ہے');
INSERT INTO "jalandhari" VALUES(11,99,'11:99','اور اس جہان میں بھی لعنت ان کے پیچھے لگا دی گئی اور قیامت کے دن بھی (پیچھے لگی رہے گی)۔ جو انعام ان کو ملا ہے برا ہے');
INSERT INTO "jalandhari" VALUES(11,100,'11:100','یہ (پرانی) بستیوں کے تھوڑے سے حالات ہیں جو ہم تم سے بیان کرتے ہیں۔ ان میں سے بعض تو باقی ہیں اور بعض کا تہس نہس ہوگیا');
INSERT INTO "jalandhari" VALUES(11,101,'11:101','اور ہم نے ان لوگوں پر ظلم نہیں کیا بلکہ انہوں نے خود اپنے اُوپر ظلم کیا۔ غرض جب تمہارے پروردگار کا حکم آپہنچا تو جن معبودوں کو وہ خدا کے سوا پکارا کرتے تھے وہ ان کے کچھ بھی کام نہ آئے۔ اور تباہ کرنے کے سوا ان کے حق میں اور کچھ نہ کرسکے');
INSERT INTO "jalandhari" VALUES(11,102,'11:102','اور تمہارا پروردگار جب نافرمان بستیوں کو پکڑا کرتا ہے تو اس کی پکڑ اسی طرح کی ہوتی ہے۔ بےشک اس کی پکڑ دکھ دینے والی اور سخت ہے');
INSERT INTO "jalandhari" VALUES(11,103,'11:103','ان (قصوں) میں اس شخص کے لیے جو عذاب آخرت سے ڈرے عبرت ہے۔ یہ وہ دن ہوگا جس میں سب لوگ اکٹھے کیے جائیں گے اور یہی وہ دن ہوگا جس میں سب (خدا کے روبرو) حاضر کیے جائیں گے');
INSERT INTO "jalandhari" VALUES(11,104,'11:104','اور ہم اس کے لانے میں ایک وقت معین تک تاخیر کر رہے ہیں');
INSERT INTO "jalandhari" VALUES(11,105,'11:105','جس روز وہ آجائے گا تو کوئی متنفس خدا کے حکم کے بغیر بول بھی نہیں سکے گا۔ پھر ان میں سے کچھ بدبخت ہوں گے اور کچھ نیک بخت');
INSERT INTO "jalandhari" VALUES(11,106,'11:106','تو جو بدبخت ہوں گے وہ دوزخ میں (ڈال دیئے جائیں گے) اس میں ان کا چلانا اور دھاڑنا ہوگا');
INSERT INTO "jalandhari" VALUES(11,107,'11:107','(اور) جب تک آسمان اور زمین ہیں، اسی میں رہیں گے مگر جتنا تمہارا پروردگار چاہے۔ بےشک تمہارا پروردگار جو چاہتا ہے کردیتا ہے');
INSERT INTO "jalandhari" VALUES(11,108,'11:108','اور جو نیک بخت ہوں گے، وہ بہشت میں داخل کیے جائیں گے اور جب تک آسمان اور زمین ہیں ہمیشہ اسی میں رہیں گے مگر جتنا تمہارا پروردگار چاہے۔ بےشک تمہارا پروردگار جو چاہتا ہے کردیتا ہے۔ اور جو نیک بخت ہوں گے وہ بہشت میں داخل کئے جائیں گے (اور) جب تک آسمان اور زمین ہیں ہمیشہ اسی میں رہیں گے۔ مگر جتنا تمہارا پروردگار چاہے۔ یہ (خدا کی) بخشش ہے جو کبھی منقطع نہیں ہوگی');
INSERT INTO "jalandhari" VALUES(11,109,'11:109','تو یہ لوگ جو (غیر خدا کی) پرستش کرتے ہیں۔ اس سے تم خلجان میں نہ پڑنا۔ یہ اسی طرح پرستش کرتے ہیں جس طرح پہلے سے ان کے باپ دادا پرستش کرتے آئے ہیں۔ اور ہم ان کو ان کا حصہ پورا پورا بلا کم وکاست دینے والے ہیں');
INSERT INTO "jalandhari" VALUES(11,110,'11:110','اور ہم نے موسیٰ کو کتاب دی تو اس میں اختلاف کیا گیا اور اگر تمہارے پروردگار کی طرف سے ایک بات پہلے نہ ہوچکی ہوتی تو ان میں فیصلہ کردیا جاتا۔ اور وہ تو اس سے قوی شبہے میں (پڑے ہوئے) ہیں');
INSERT INTO "jalandhari" VALUES(11,111,'11:111','اور تمہارا پروردگار ان سب کو (قیامت کے دن) ان کے اعمال کا پورا پورا بدلہ دے گا۔ بےشک جو عمل یہ کرتے ہیں وہ اس سے واقف ہے');
INSERT INTO "jalandhari" VALUES(11,112,'11:112','سو (اے پیغمبر) جیسا تم کو حکم ہوتا ہے (اس پر) تم اور جو لوگ تمہارے ساتھ تائب ہوئے ہیں قائم رہو۔ اور حد سے تجاوز نہ کرنا۔ وہ تمہارے سب اعمال کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(11,113,'11:113','اور جو لوگ ظالم ہیں، ان کی طرف مائل نہ ہونا، نہیں تو تمہیں (دوزخ کی) آگ آلپٹے گی اور خدا کے سوا تمہارے اور دوست نہیں ہیں۔ اگر تم ظالموں کی طرف مائل ہوگئے تو پھر تم کو (کہیں سے) مدد نہ مل سکے گی');
INSERT INTO "jalandhari" VALUES(11,114,'11:114','اور دن کے دونوں سروں (یعنی صبح اور شام کے اوقات میں) اور رات کی چند (پہلی) ساعات میں نماز پڑھا کرو۔ کچھ شک نہیں کہ نیکیاں گناہوں کو دور کر دیتی ہیں۔ یہ ان کے لیے نصیحت ہے جو نصیحت قبول کرنے والے ہیں');
INSERT INTO "jalandhari" VALUES(11,115,'11:115','اور صبر کیے رہو کہ خدا نیکوکاروں کا اجر ضائع نہیں کرتا');
INSERT INTO "jalandhari" VALUES(11,116,'11:116','تو جو اُمتیں تم سے پہلے گزر چکی ہیں، ان میں ایسے ہوش مند کیوں نہ ہوئے جو ملک میں خرابی کرنے سے روکتے ہاں (ایسے) تھوڑے سے (تھے) جن کو ہم نے ان میں سے مخلصی بخشی۔ اور جو ظالم تھے وہ ان ہی باتوں کے پیچھے لگے رہے جس میں عیش وآرام تھا اور وہ گناہوں میں ڈوبے ہوئے تھے');
INSERT INTO "jalandhari" VALUES(11,117,'11:117','اور تمہارا پروردگار ایسا نہیں ہے کہ بستیوں کو جب کہ وہاں کے باشندے نیکوکار ہوں ازراہِ ظلم تباہ کردے');
INSERT INTO "jalandhari" VALUES(11,118,'11:118','اور اگر تمہارا پروردگار چاہتا تو تمام لوگوں کو ایک ہی جماعت کردیتا لیکن وہ ہمیشہ اختلاف کرتے رہیں گے');
INSERT INTO "jalandhari" VALUES(11,119,'11:119','مگر جن پر تمہارا پروردگار رحم کرے۔ اور اسی لیے اس نے ان کو پیدا کیا ہے اور تمہارے پروردگار کا قول پورا ہوگیا کہ میں دوزخ کو جنوں اور انسانوں سب سے بھر دوں گا');
INSERT INTO "jalandhari" VALUES(11,120,'11:120','(اے محمدصلى الله عليه وسلم) اور پیغمبروں کے وہ سب حالات جو ہم تم سے بیان کرتے ہیں ان سے ہم تمہارے دل کو قائم رکھتے ہیں۔ اور ان (قصص) میں تمہارے پاس حق پہنچ گیا اور یہ مومنوں کے لیے نصیحت اور عبرت ہے');
INSERT INTO "jalandhari" VALUES(11,121,'11:121','اور جو لوگ ایمان نہیں لائے ان سے کہہ دو کہ تم اپنی جگہ عمل کیے جاؤ۔ ہم اپنی جگہ عمل کیے جاتے ہیں');
INSERT INTO "jalandhari" VALUES(11,122,'11:122','اور (نتیجہٴ اعمال کا) تم بھی انتظار کرو، ہم بھی انتظار کرتے ہیں');
INSERT INTO "jalandhari" VALUES(11,123,'11:123','اور آسمانوں اور زمین کی چھپی چیزوں کا علم خدا ہی کو ہے اور تمام امور کا رجوع اسی کی طرف ہے۔ تو اسی کی عبادت کرو اور اسی پر بھروسہ رکھو۔ اور جو کچھ تم کررہے ہو تمہارا پروردگار اس سے بےخبر نہیں');
INSERT INTO "jalandhari" VALUES(12,1,'12:1','الٓرا۔ یہ کتاب روشن کی آیتیں ہیں');
INSERT INTO "jalandhari" VALUES(12,2,'12:2','ہم نے اس قرآن کو عربی میں نازل کیا ہے تاکہ تم سمجھ سکو');
INSERT INTO "jalandhari" VALUES(12,3,'12:3','(اے پیغمبر) ہم اس قرآن کے ذریعے سے جو ہم نے تمہاری طرف بھیجا ہے تمہیں ایک نہایت اچھا قصہ سناتے ہیں اور تم اس سے پہلے بےخبر تھے');
INSERT INTO "jalandhari" VALUES(12,4,'12:4','جب یوسف نے اپنے والد سے کہا کہ ابا میں نے (خواب میں) گیارہ ستاروں اور سورج اور چاند کو دیکھا ہے۔ دیکھتا (کیا) ہوں کہ وہ مجھے سجدہ کر رہے ہیں');
INSERT INTO "jalandhari" VALUES(12,5,'12:5','انہوں نے کہا کہ بیٹا اپنے خواب کا ذکر اپنے بھائیوں سے نہ کرنا نہیں تو وہ تمہارے حق میں کوئی فریب کی چال چلیں گے۔ کچھ شک نہیں کہ شیطان انسان کا کھلا دشمن ہے');
INSERT INTO "jalandhari" VALUES(12,6,'12:6','اور اسی طرح خدا تمہیں برگزیدہ (وممتاز) کرے گا اور (خواب کی) باتوں کی تعبیر کا علم سکھائے گا۔ اور جس طرح اس نے اپنی نعمت پہلے تمہارے دادا، پردادا ابراہیم اور اسحاق پر پوری کی تھی اسی طرح تم پر اور اولاد یعقوب پر پوری کرے گا۔ بےشک تمہارا پروردگار (سب کچھ) جاننے والا (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(12,7,'12:7','ہاں یوسف اور ان کے بھائیوں (کے قصے) میں پوچھنے والوں کے لیے (بہت سی) نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(12,8,'12:8','جب انہوں نے (آپس میں) تذکرہ کیا کہ یوسف اور اس کا بھائی ابا کو ہم سے زیادہ پیارے ہیں حالانکہ ہم جماعت (کی جماعت) ہیں۔ کچھ شک نہیں کہ ابا صریح غلطی پر ہیں');
INSERT INTO "jalandhari" VALUES(12,9,'12:9','تو یوسف کو (یا تو جان سے) مار ڈالو یا کسی ملک میں پھینک آؤ۔ پھر ابا کی توجہ صرف تمہاری طرف ہوجائے گی۔ اور اس کے بعد تم اچھی حالت میں ہوجاؤ گے');
INSERT INTO "jalandhari" VALUES(12,10,'12:10','ان میں سے ایک کہنے والے نے کہا کہ یوسف کو جان سے نہ مارو کسی گہرے کنویں میں ڈال دو کہ کوئی راہگیر نکال (کر اور ملک میں) لے جائے گا۔ اگر تم کو کرنا ہے (تو یوں کرو)');
INSERT INTO "jalandhari" VALUES(12,11,'12:11','(یہ مشورہ کر کے وہ یعقوب سے) کہنے لگے کہ اباجان کیا سبب ہے کہ آپ یوسف کے بارے میں ہمارا اعتبار نہیں کرتے حالانکہ ہم اس کے خیرخواہ ہیں');
INSERT INTO "jalandhari" VALUES(12,12,'12:12','کل اسے ہمارے ساتھ بھیج دیجیئے کہ خوب میوے کھائے اور کھیلے کودے۔ ہم اس کے نگہبان ہیں');
INSERT INTO "jalandhari" VALUES(12,13,'12:13','انہوں نے کہا کہ یہ امر مجھے غمناک کئے دیتا ہے کہ تم اسے لے جاؤ (یعنی وہ مجھ سے جدا ہوجائے) اور مجھے یہ خوف بھی ہے کہ تم (کھیل میں) اس سے غافل ہوجاؤ اور اسے بھیڑیا کھا جائے');
INSERT INTO "jalandhari" VALUES(12,14,'12:14','وہ کہنے لگے کہ اگر ہماری موجودگی میں کہ ہم ایک طاقتور جماعت ہیں، اسے بھیڑیا کھا گیا تو ہم بڑے نقصان میں پڑگئے');
INSERT INTO "jalandhari" VALUES(12,15,'12:15','غرض جب وہ اس کو لے گئے اور اس بات پر اتفاق کرلیا کہ اس کو گہرے کنویں میں ڈال دیں۔ تو ہم نے یوسف کی طرف وحی بھیجی کہ (ایک وقت ایسا آئے گا کہ) تم ان کے اس سلوک سے آگاہ کرو گے اور ان کو (اس وحی کی) کچھ خبر نہ ہوگی');
INSERT INTO "jalandhari" VALUES(12,16,'12:16','(یہ حرکت کرکے) وہ رات کے وقت باپ کے پاس روتے ہوئے آئے');
INSERT INTO "jalandhari" VALUES(12,17,'12:17','(اور) کہنے لگے کہ اباجان ہم تو دوڑنے اور ایک دوسرے سے آگے نکلنے میں مصروف ہوگئے اور یوسف کو اپنے اسباب کے پاس چھوڑ گئے تو اسے بھیڑیا کھا گیا۔ اور آپ ہماری بات کو گو ہم سچ ہی کہتے ہوں باور نہیں کریں گے');
INSERT INTO "jalandhari" VALUES(12,18,'12:18','اور ان کے کرتے پر جھوٹ موٹ کا لہو بھی لگا لائے۔ یعقوب نے کہا (کہ حقیقت حال یوں نہیں ہے) بلکہ تم اپنے دل سے (یہ) بات بنا لائے ہو۔ اچھا صبر (کہ وہی) خوب (ہے) اور جو تم بیان کرتے ہو اس کے بارے میں خدا ہی سے مدد مطلوب ہے');
INSERT INTO "jalandhari" VALUES(12,19,'12:19','(اب خدا کی شان دیکھو کہ اس کنویں کے قریب) ایک قافلہ آوارد ہوا اور انہوں نے (پانی کے لیے) اپنا سقا بھیجا۔ اس نے کنویں میں ڈول لٹکایا (تو یوسف اس سے لٹک گئے) وہ بولا زہے قسمت یہ تو (نہایت حسین) لڑکا ہے۔ اور اس کو قیمتی سرمایہ سمجھ کر چھپا لیا اور جو کچھ وہ کرتے تھے خدا کو سب معلوم تھا');
INSERT INTO "jalandhari" VALUES(12,20,'12:20','اور اس کو تھوڑی سی قیمت (یعنی) معدودے چند درہموں پر بیچ ڈالا۔ اور انہیں ان (کے بارے) میں کچھ لالچ نہ تھا');
INSERT INTO "jalandhari" VALUES(12,21,'12:21','اور مصر میں جس شخص نے اس کو خریدا اس نے اپنی بیوی سے (جس کا نام زلیخا تھا) کہا کہ اس کو عزت واکرام سے رکھو عجب نہیں کہ یہ ہمیں فائدہ دے یا ہم اسے بیٹا بنالیں۔ اس طرح ہم نے یوسف کو سرزمین (مصر) میں جگہ دی اور غرض یہ تھی کہ ہم ان کو (خواب کی) باتوں کی تعبیر سکھائیں اور خدا اپنے کام پر غالب ہے لیکن اکثر لوگ نہیں جانتے');
INSERT INTO "jalandhari" VALUES(12,22,'12:22','اور جب وہ اپنی جوانی کو پہنچے تو ہم نے ان کو دانائی اور علم بخشا۔ اور نیکوکاروں کو ہم اسی طرح بدلہ دیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(12,23,'12:23','تو جس عورت کے گھر میں وہ رہتے تھے اس نے ان کو اپنی طرف مائل کرنا چاہا اور دروازے بند کرکے کہنے لگی (یوسف) جلدی آؤ۔ انہوں نے کہا کہ خدا پناہ میں رکھے (وہ یعنی تمہارے میاں) تو میرے آقا ہیں انہوں نے مجھے اچھی طرح سے رکھا ہے (میں ایسا ظلم نہیں کرسکتا) بےشک ظالم لوگ فلاح نہیں پائیں گے');
INSERT INTO "jalandhari" VALUES(12,24,'12:24','اور اس عورت نے ان کا قصد کیا اور انہوں نے اس کا قصد کیا۔ اگر وہ اپنے پروردگار کی نشانی نہ دیکھتے (تو جو ہوتا ہوتا) یوں اس لیے (کیا گیا) کہ ہم ان سے برائی اور بےحیائی کو روک دیں۔ بےشک وہ ہمارے خالص بندوں میں سے تھے');
INSERT INTO "jalandhari" VALUES(12,25,'12:25','اور دونوں دروازے کی طرف بھاگے (آگے یوسف اور پیچھے زلیخا) اور عورت نے ان کا کرتا پیچھے سے (پکڑ کر جو کھینچا تو) پھاڑ ڈالا اور دونوں کو دروازے کے پاس عورت کا خاوند مل گیا تو عورت بولی کہ جو شخص تمہاری بیوی کے ساتھ برا ارادہ کرے اس کی اس کے سوا کیا سزا ہے کہ یا تو قید کیا جائے یا دکھ کا عذاب دیا جائے');
INSERT INTO "jalandhari" VALUES(12,26,'12:26','یوسف نے کہا اسی نے مجھ کو اپنی طرف مائل کرنا چاہا تھا۔ اس کے قبیلے میں سے ایک فیصلہ کرنے والے نے فیصلہ کیا کہ اگر اس کا کرتا آگے سے پھٹا تو یہ سچی اور یوسف جھوٹا');
INSERT INTO "jalandhari" VALUES(12,27,'12:27','اور اگر کرتا پیچھے سے پھٹا ہو تو یہ جھوٹی اور وہ سچا ہے');
INSERT INTO "jalandhari" VALUES(12,28,'12:28','اور جب اس کا کرتا دیکھا (تو) پیچھے سے پھٹا تھا (تب اس نے زلیخا سے کہا) کہ یہ تمہارا ہی فریب ہے۔ اور کچھ شک نہیں کہ تم عورتوں کے فریب بڑے (بھاری) ہوتے ہیں');
INSERT INTO "jalandhari" VALUES(12,29,'12:29','یوسف اس بات کا خیال نہ کر۔ اور (زلیخا) تو اپنے گناہوں کی بخشش مانگ، بےشک خطا تیری ہے');
INSERT INTO "jalandhari" VALUES(12,30,'12:30','اور شہر میں عورتیں گفتگوئیں کرنے لگیں کہ عزیز کی بیوی اپنے غلام کو اپنی طرف مائل کرنا چاہتی ہے۔ اور اس کی محبت اس کے دل میں گھر کرگئی ہے۔ ہم دیکھتی ہیں کہ وہ صریح گمراہی میں ہے');
INSERT INTO "jalandhari" VALUES(12,31,'12:31','جب زلیخا نے ان عورتوں کی (گفتگو جو حقیقت میں دیدار یوسف کے لیے ایک) چال (تھی) سنی تو ان کے پاس (دعوت کا) پیغام بھیجا اور ان کے لیے ایک محفل مرتب کی۔ اور (پھل تراشنے کے لیے) ہر ایک کو ایک چھری دی اور (یوسف سے) کہا کہ ان کے سامنے باہر آؤ۔ جب عورتوں نے ان کو دیکھا تو ان کا رعب (حسن) ان پر (ایسا) چھا گیا کہ (پھل تراشتے تراشتے) اپنے ہاتھ کاٹ لیے اور بےساختہ بول اٹھیں کہ سبحان الله (یہ حسن) یہ آدمی نہیں کوئی بزرگ فرشتہ ہے');
INSERT INTO "jalandhari" VALUES(12,32,'12:32','تب زلیخا نے کہا یہ وہی ہے جس کے بارے میں تم مجھے طعنے دیتی تھیں۔ اور بےشک میں نے اس کو اپنی طرف مائل کرنا چاہا مگر یہ بچا رہا۔ اور اگر یہ وہ کام نہ کرے گا جو میں اسے کہتی ہوں تو قید کردیا جائے گا اور ذلیل ہوگا');
INSERT INTO "jalandhari" VALUES(12,33,'12:33','یوسف نے دعا کی کہ پروردگار جس کام کی طرف یہ مجھے بلاتی ہیں اس کی نسبت مجھے قید پسند ہے۔ اور اگر تو مجھ سے ان کے فریب کو نہ ہٹائے گا تو میں ان کی طرف مائل ہوجاؤں گا اور نادانوں میں داخل ہوجاؤں گا');
INSERT INTO "jalandhari" VALUES(12,34,'12:34','تو خدا نے ان کی دعا قبول کرلی اور ان سے عورتوں کا مکر دفع کر دیا۔ بےشک وہ سننے (اور) جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(12,35,'12:35','پھر باوجود اس کے کہ وہ لوگ نشان دیکھ چکے تھے ان کی رائے یہی ٹھہری کہ کچھ عرصہ کے لیے ان کو قید ہی کردیں');
INSERT INTO "jalandhari" VALUES(12,36,'12:36','اور ان کے ساتھ دو اور جوان بھی داخل زندان ہوئے۔ ایک نے ان میں سے کہا کہ (میں نے خواب دیکھا ہے) دیکھتا (کیا) ہوں کہ شراب (کے لیے انگور) نچوڑ رہا ہوں۔ دوسرے نے کہا کہ (میں نے بھی خواب دیکھا ہے) میں یہ دیکھتا ہوں کہ اپنے سر پر روٹیاں اٹھائے ہوئے ہوں اور جانور ان میں سے کھا رہے (ہیں تو) ہمیں ان کی تعبیر بتا دیجیئے کہ ہم تمہیں نیکوکار دیکھتے ہیں');
INSERT INTO "jalandhari" VALUES(12,37,'12:37','یوسف نے کہا کہ جو کھانا تم کو ملنے والا ہے وہ آنے نہیں پائے گا کہ میں اس سے پہلے تم کو اس کی تعبیر بتادوں گا۔ یہ ان (باتوں) میں سے ہے جو میرے پروردگار نے مجھے سکھائی ہیں جو لوگ خدا پر ایمان نہیں لاتے اور روز آخرت سے انکار کرتے ہیں میں ان کا مذہب چھوڑے ہوئے ہوں');
INSERT INTO "jalandhari" VALUES(12,38,'12:38','اور اپنے باپ دادا ابراہیم اور اسحاق اور یعقوب کے مذہب پر چلتا ہوں۔ ہمیں شایاں نہیں ہے کہ کسی چیز کو خدا کے ساتھ شریک بنائیں۔ یہ خدا کا فضل ہے ہم پر بھی اور لوگوں پر بھی ہے لیکن اکثر لوگ شکر نہیں کرتے');
INSERT INTO "jalandhari" VALUES(12,39,'12:39','میرے جیل خانے کے رفیقو! بھلا کئی جدا جدا آقا اچھے یا (ایک) خدائے یکتا وغالب؟');
INSERT INTO "jalandhari" VALUES(12,40,'12:40','جن چیزوں کی تم خدا کے سوا پرستش کرتے ہو وہ صرف نام ہی نام ہیں جو تم نے اور تمہارے باپ دادا نے رکھ لیے ہیں۔ خدا نے ان کی کوئی سند نازل نہیں کی۔ (سن رکھو کہ) خدا کے سوا کسی کی حکومت نہیں ہے۔ اس نے ارشاد فرمایا ہے کہ اس کے سوا کسی کی عبادت نہ کرو۔ یہی سیدھا دین ہے لیکن اکثر لوگ نہیں جانتے');
INSERT INTO "jalandhari" VALUES(12,41,'12:41','میرے جیل خانے کے رفیقو! تم میں سے ایک (جو پہلا خواب بیان کرنے والا ہے وہ) تو اپنے آقا کو شراب پلایا کرے گا اور جو دوسرا ہے وہ سولی دیا جائے گا اور جانور اس کا سر کھا جائیں گے۔ جو امر تم مجھ سے پوچھتے تھے وہ فیصلہ ہوچکا ہے');
INSERT INTO "jalandhari" VALUES(12,42,'12:42','اور دونوں شخصوں میں سے جس کی نسبت (یوسف نے) خیال کیا کہ وہ رہائی پا جائے گا اس سے کہا کہ اپنے آقا سے میرا ذکر بھی کرنا لیکن شیطان نے ان کا اپنے آقا سے ذکر کرنا بھلا دیا اور یوسف کئی برس جیل خانے میں رہے');
INSERT INTO "jalandhari" VALUES(12,43,'12:43','اور بادشاہ نے کہا کہ میں (نے خواب دیکھا ہے) دیکھتا (کیا) ہوں کہ سات موٹی گائیں ہیں جن کو سات دبلی گائیں کھا رہی ہیں اور سات خوشے سبز ہیں اور (سات) خشک۔ اے سردارو! اگر تم خوابوں کی تعبیر دے سکتے ہو تو مجھے میرے خواب کی تعبیر بتاؤ');
INSERT INTO "jalandhari" VALUES(12,44,'12:44','انہوں نے کہا یہ تو پریشان سے خواب ہیں۔ اور ہمیں ایسے خوابوں کی تعبیر نہیں آتی');
INSERT INTO "jalandhari" VALUES(12,45,'12:45','اب وہ شخص جو دونوں قیدیوں میں سے رہائی پا گیا تھا اور جسے مدت کے بعد وہ بات یاد آگئی بول اٹھا کہ میں آپ کو اس کی تعبیر (لا) بتاتا ہوں مجھے (جیل خانے) جانے کی اجازت دے دیجیئے');
INSERT INTO "jalandhari" VALUES(12,46,'12:46','(غرض وہ یوسف کے پاس آیا اور کہنے لگا) یوسف اے بڑے سچے (یوسف) ہمیں اس خواب کی تعبیر بتایئے کہ سات موٹی گائیوں کو سات دبلی گائیں کھا رہی ہیں۔ اور سات خوشے سبز ہیں اور سات سوکھے تاکہ میں لوگوں کے پاس واپس جا (کر تعبیر بتاؤں)۔ عجب نہیں کہ وہ (تمہاری قدر) جانیں');
INSERT INTO "jalandhari" VALUES(12,47,'12:47','انہوں نے کہا کہ تم لوگ سات سال متواتر کھیتی کرتے رہوگے تو جو (غلّہ) کاٹو تو تھوڑے سے غلّے کے سوا جو کھانے میں آئے اسے خوشوں میں ہی رہنے دینا');
INSERT INTO "jalandhari" VALUES(12,48,'12:48','پھر اس کے بعد (خشک سالی کے) سات سخت (سال) آئیں گے کہ جو (غلّہ) تم نے جمع کر رکھا ہوگا وہ اس سب کو کھا جائیں گے۔ صرف وہی تھوڑا سا رہ جائے گا جو تم احتیاط سے رکھ چھوڑو گے');
INSERT INTO "jalandhari" VALUES(12,49,'12:49','پھر اس کے بعد ایک سال آئے گا کہ خوب مینہ برسے گا اور لوگ اس میں رس نچوڑیں گے');
INSERT INTO "jalandhari" VALUES(12,50,'12:50','(یہ تعبیر سن کر) بادشاہ نے حکم دیا کہ یوسف کو میرے پاس لے آؤ۔ جب قاصد ان کے پاس گیا تو انہوں نے کہا کہ اپنے آقا کے پاس واپس جاؤ اور ان سے پوچھو کہ ان عورتوں کا کیا حال ہے جنہوں نے اپنے ہاتھ کاٹ لیے تھے۔ بےشک میرا پروردگار ان کے مکروں سے خوب واقف ہے');
INSERT INTO "jalandhari" VALUES(12,51,'12:51','بادشاہ نے عورتوں سے پوچھا کہ بھلا اس وقت کیا ہوا تھا جب تم نے یوسف کو اپنی طرف مائل کرنا چاہا۔ سب بول اٹھیں کہ حاش َللهِ ہم نے اس میں کوئی برائی معلوم نہیں کی۔ عزیز کی عورت نے کہا اب سچی بات تو ظاہر ہو ہی گئی ہے۔ (اصل یہ ہے کہ) میں نے اس کو اپنی طرف مائل کرنا چاہا تھا اور بےشک وہ سچا ہے');
INSERT INTO "jalandhari" VALUES(12,52,'12:52','(یوسف نے کہا کہ میں نے) یہ بات اس لیے (پوچھی ہے) کہ عزیز کو یقین ہوجائے کہ میں نے اس کی پیٹھ پیچھے اس کی (امانت میں خیانت نہیں کی) اور خدا خیانت کرنے والوں کے مکروں کو روبراہ نہیں کرتا');
INSERT INTO "jalandhari" VALUES(12,53,'12:53','اور میں اپنے تئیں پاک صاف نہیں کہتا کیونکہ نفس امارہ (انسان کو) برائی سکھاتا رہتا ہے۔ مگر یہ کہ میرا پروردگار رحم کرے گا۔ بےشک میرا پروردگار بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(12,54,'12:54','بادشاہ نے حکم دیا کہ اسے میرے پاس لاؤ میں اسے اپنا مصاحب خاص بناؤں گا۔ پھر جب ان سے گفتگو کی تو کہا کہ آج سے تم ہمارے ہاں صاحب منزلت اور صاحبِ اعتبار ہو');
INSERT INTO "jalandhari" VALUES(12,55,'12:55','(یوسف نے) کہا مجھے اس ملک کے خزانوں پر مقرر کر دیجیئے کیونکہ میں حفاظت بھی کرسکتا ہوں اور اس کام سے واقف ہوں');
INSERT INTO "jalandhari" VALUES(12,56,'12:56','اس طرح ہم نے یوسف کو ملک (مصر) میں جگہ دی اور وہ اس ملک میں جہاں چاہتے تھے رہتے تھے۔ ہم اپنی رحمت جس پر چاہتے ہیں کرتے ہیں اور نیکوکاروں کے اجر کو ضائع نہیں کرتے');
INSERT INTO "jalandhari" VALUES(12,57,'12:57','اور جو لوگ ایمان لائے اور ڈرتے رہے ان کے لیے آخرت کا اجر بہت بہتر ہے');
INSERT INTO "jalandhari" VALUES(12,58,'12:58','اور یوسف کے بھائی (کنعان سے مصر میں غلّہ خریدنے کے لیے) آئے تو یوسف کے پاس گئے تو یوسف نے ان کو پہچان لیا اور وہ ان کو نہ پہچان سکے');
INSERT INTO "jalandhari" VALUES(12,59,'12:59','جب یوسف نے ان کے لیے ان کا سامان تیار کر دیا تو کہا کہ (پھر آنا تو) جو باپ کی طرف سے تمہارا ایک اور بھائی ہے اسے بھی میرے پاس لیتے آنا۔ کیا تم نہیں دیکھتے کہ میں ناپ بھی پوری پوری دیتا ہوں اور مہمانداری بھی خوب کرتا ہوں');
INSERT INTO "jalandhari" VALUES(12,60,'12:60','اور اگر تم اسے میرے پاس نہ لاؤ گے تو نہ تمہیں میرے ہاں سے غلّہ ملے گا اور نہ تم میرے پاس ہی آسکو گے');
INSERT INTO "jalandhari" VALUES(12,61,'12:61','انہوں نے کہا کہ ہم اس کے بارے میں اس کے والد سے تذکرہ کریں گے اور ہم (یہ کام) کرکے رہیں گے');
INSERT INTO "jalandhari" VALUES(12,62,'12:62','(اور یوسف نے) اپنے خدام سے کہا کہ ان کا سرمایہ (یعنی غلّے کی قیمت) ان کے شلیتوں میں رکھ دو عجب نہیں کہ جب یہ اپنے اہل وعیال میں جائیں تو اسے پہچان لیں (اور) عجب نہیں کہ پھر یہاں آئیں');
INSERT INTO "jalandhari" VALUES(12,63,'12:63','جب وہ اپنے باپ کے پاس واپس گئے تو کہنے لگے کہ ابّا (جب تک ہم بنیامین کو ساتھ نہ لے جائیں) ہمارے لیے غلّے کی بندش کر دی گئی ہے تو ہمارے ساتھ ہمارے بھائی کو بھیج دے تاکہ ہم پھر غلّہ لائیں اور ہم اس کے نگہبان ہیں');
INSERT INTO "jalandhari" VALUES(12,64,'12:64','(یعقوب نے) کہا کہ میں اس کے بارے میں تمہارا اعتبار نہیں کرتا مگر ویسا ہی جیسا اس کے بھائی کے بارے میں کیا تھا۔ سو خدا ہی بہتر نگہبان ہے۔ اور وہ سب سے زیادہ رحم کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(12,65,'12:65','اور جب انہوں نے اپنا اسباب کھولا تو دیکھا کہ ان کا سرمایہ واپس کر دیا گیا ہے۔ کہنے لگے ابّا ہمیں (اور) کیا چاہیئے (دیکھیے) یہ ہماری پونجی بھی ہمیں واپس کر دی گئی ہے۔ اب ہم اپنے اہل وعیال کے لیے پھر غلّہ لائیں گے اور اپنے بھائی کی نگہبانی کریں گے اور ایک بار شتر زیادہ لائیں گے (کہ) یہ غلّہ جو ہم لائے ہیں تھوڑا ہے');
INSERT INTO "jalandhari" VALUES(12,66,'12:66','(یعقوب نے) کہا جب تک تم خدا کا عہد نہ دو کہ اس کو میرے پاس (صحیح سالم) لے آؤ گے میں اسے ہرگز تمہارے ساتھ نہیں بھیجنے کا۔ مگر یہ کہ تم گھیر لیے جاؤ (یعنی بےبس ہوجاؤ تو مجبوری ہے) جب انہوں نے ان سے عہد کرلیا تو (یعقوب نے) کہا کہ جو قول وقرار ہم کر رہے ہیں اس کا خدا ضامن ہے');
INSERT INTO "jalandhari" VALUES(12,67,'12:67','اور ہدایت کی کہ بیٹا ایک ہی دروازے سے داخل نہ ہونا بلکہ جدا جدا دروازوں سے داخل ہونا۔ اور میں خدا کی تقدیر کو تم سے نہیں روک سکتا۔ بےشک حکم اسی کا ہے میں اسی پر بھروسہ رکھتا ہوں۔ اور اہلِ توکل کو اسی پر بھروسہ رکھنا چاہیئے');
INSERT INTO "jalandhari" VALUES(12,68,'12:68','اور جب وہ ان ان مقامات سے داخل ہوئے جہاں جہاں سے (داخل ہونے کے لیے) باپ نے ان سے کہا تھا تو وہ تدبیر خدا کے حکم کو ذرا بھی نہیں ٹال سکتی تھی ہاں وہ یعقوب کے دل کی خواہش تھی جو انہوں نے پوری کی تھی۔ اور بےشک وہ صاحبِ علم تھے کیونکہ ہم نے ان کو علم سکھایا تھا لیکن اکثر لوگ نہیں جانتے');
INSERT INTO "jalandhari" VALUES(12,69,'12:69','اور جب وہ لوگ یوسف کے پاس پہنچے تو یوسف نے اپنے حقیقی بھائی کو اپنے پاس جگہ دی اور کہا کہ میں تمہارا بھائی ہوں تو جو سلوک یہ (ہمارے ساتھ) کرتے رہے ہیں اس پر افسوس نہ کرنا');
INSERT INTO "jalandhari" VALUES(12,70,'12:70','جب ان کا اسباب تیار کر دیا تو اپنے بھائی کے شلیتے میں گلاس رکھ دیا اور پھر (جب وہ آبادی سے باہر نکل گئے تو) ایک پکارنے والے نے آواز دی کہ قافلے والو تم تو چور ہو');
INSERT INTO "jalandhari" VALUES(12,71,'12:71','وہ ان کی طرف متوجہ ہو کر کہنے لگے تمہاری کیا چیز کھوئی گئی ہے');
INSERT INTO "jalandhari" VALUES(12,72,'12:72','وہ بولے کہ بادشاہ (کے پانی پینے) کا گلاس کھویا گیا ہے اور جو شخص اس کو لے آئے اس کے لیے ایک بار شتر (انعام) اور میں اس کا ضامن ہوں');
INSERT INTO "jalandhari" VALUES(12,73,'12:73','وہ کہنے لگے کہ خدا کی قسم تم کو معلوم ہے کہ ہم (اس) ملک میں اس لیے نہیں آئے کہ خرابی کریں اور نہ ہم چوری کیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(12,74,'12:74','بولے کہ اگر تم جھوٹے نکلے (یعنی چوری ثابت ہوئی) تو اس کی سزا کیا');
INSERT INTO "jalandhari" VALUES(12,75,'12:75','انہوں نے کہا کہ اس کی سزا یہ ہے کہ جس کے شلیتے میں وہ دستیاب ہو وہی اس کا بدل قرار دیا جائے ہم ظالموں کو یہی سزا دیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(12,76,'12:76','پھر یوسف نے اپنے بھائی کے شلیتے سے پہلے ان کے شلیتوں کو دیکھنا شروع کیا پھر اپنے بھائی کے شلیتے میں سے اس کو نکال لیا۔ اس طرح ہم نے یوسف کے لیے تدبیر کی (ورنہ) بادشاہ کے قانون کے مطابق وہ مشیتِ خدا کے سوا اپنے بھائی کو لے نہیں سکتے تھے۔ ہم جس کے لیے چاہتے ہیں درجے بلند کرتے ہیں۔ اور ہر علم والے سے دوسرا علم والا بڑھ کر ہے');
INSERT INTO "jalandhari" VALUES(12,77,'12:77','(برادران یوسف نے) کہا کہ اگر اس نے چوری کی ہو تو (کچھ عجب نہیں کہ) اس کے ایک بھائی نے بھی پہلے چوری کی تھی یوسف نے اس بات کو اپنے دل میں مخفی رکھا اور ان پر ظاہر نہ ہونے دیا (اور) کہا کہ تم بڑے بدقماش ہو۔ اور جو تم بیان کرتے ہو خدا اسے خوب جانتا ہے');
INSERT INTO "jalandhari" VALUES(12,78,'12:78','وہ کہنے لگے کہ اے عزیز اس کے والد بہت بوڑھے ہیں (اور اس سے بہت محبت رکھتے ہیں) تو (اس کو چھوڑ دیجیےاور) اس کی جگہ ہم میں سے کسی کو رکھ لیجیئے۔ ہم دیکھتے ہیں کہ آپ احسان کرنے والے ہیں');
INSERT INTO "jalandhari" VALUES(12,79,'12:79','(یوسف نے) کہا کہ خدا پناہ میں رکھے کہ جس شخص کے پاس ہم نے اپنی چیز پائی ہے اس کے سوا کسی اور کو پکڑ لیں ایسا کریں تو ہم (بڑے) بےانصاف ہیں');
INSERT INTO "jalandhari" VALUES(12,80,'12:80','جب وہ اس سے ناامید ہوگئے تو الگ ہو کر صلاح کرنے لگے۔ سب سے بڑے نے کہا کیا تم نہیں جانتے کہ تمہارے والد نے تم سے خدا کا عہد لیا ہے اور اس سے پہلے بھی تم یوسف کے بارے میں قصور کر چکے ہو تو جب تک والد صاحب مجھے حکم نہ دیں میں تو اس جگہ سے ہلنے کا نہیں یا خدا میرے لیے کوئی اور تدبیر کرے۔ اور وہ سب سے بہتر فیصلہ کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(12,81,'12:81','تم سب والد صاحب کے پاس واپس جاؤ اور کہو کہ ابا آپ کے صاحبزادے نے (وہاں جا کر) چوری کی۔ اور ہم نے اپنی دانست کے مطابق آپ سے (اس کے لے آنے کا) عہد کیا تھا مگر ہم غیب کی باتوں کو جاننے اور یاد رکھنے والے تو نہیں تھے');
INSERT INTO "jalandhari" VALUES(12,82,'12:82','اور جس بستی میں ہم (ٹھہرے) تھے وہاں سے (یعنی اہل مصر سے) اور جس قافلے میں آئے ہیں اس سے دریافت کر لیجیئے اور ہم اس بیان میں بالکل سچے ہیں');
INSERT INTO "jalandhari" VALUES(12,83,'12:83','(جب انہوں نے یہ بات یعقوب سے آ کر کہی تو) انہوں نے کہا کہ (حقیقت یوں نہیں ہے) بلکہ یہ بات تم نے اپنے دل سے بنالی ہے تو صبر ہی بہتر ہے۔ عجب نہیں کہ خدا ان سب کو میرے پاس لے آئے۔ بےشک وہ دانا (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(12,84,'12:84','پھر ان کے پاس سے چلے گئے اور کہنے لگے ہائے افسوس یوسف (ہائے افسوس) اور رنج والم میں (اس قدر روئے کہ) ان کی آنکھیں سفید ہوگئیں اور ان کا دل غم سے بھر رہا تھا');
INSERT INTO "jalandhari" VALUES(12,85,'12:85','بیٹے کہنے لگے کہ والله اگر آپ یوسف کو اسی طرح یاد ہی کرتے رہیں گے تو یا تو بیمار ہوجائیں گے یا جان ہی دے دیں گے');
INSERT INTO "jalandhari" VALUES(12,86,'12:86','انہوں نے کہا کہ میں اپنے غم واندوہ کا اظہار خدا سے کرتا ہوں۔ اور خدا کی طرف سے وہ باتیں جانتا ہوں جو تم نہیں جانتے');
INSERT INTO "jalandhari" VALUES(12,87,'12:87','بیٹا (یوں کرو کہ ایک دفعہ پھر) جاؤ اور یوسف اور اس کے بھائی کو تلاش کرو اور خدا کی رحمت سے ناامید نہ ہو۔ کہ خدا کی رحمت سے بےایمان لوگ ناامید ہوا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(12,88,'12:88','جب وہ یوسف کے پاس گئے تو کہنے لگے کہ عزیز ہمیں اور ہمارے اہل وعیال کو بڑی تکلیف ہو رہی ہے اور ہم تھوڑا سا سرمایہ لائے ہیں آپ ہمیں (اس کے عوض) پورا غلّہ دے دیجیئے اور خیرات کیجیئے۔ کہ خدا خیرات کرنے والوں کو ثواب دیتا ہے');
INSERT INTO "jalandhari" VALUES(12,89,'12:89','(یوسف نے) کہا تمہیں معلوم ہے جب تم نادانی میں پھنسے ہوئے تھے تو تم نے یوسف اور اس کے بھائی کے ساتھ کیا کیا تھا');
INSERT INTO "jalandhari" VALUES(12,90,'12:90','وہ بولے کیا تم ہی یوسف ہو؟ انہوں نے کہا ہاں میں ہی یوسف ہوں۔ اور (بنیامین کی طرف اشارہ کرکے کہنے لگے) یہ میرا بھائی ہے خدا نے ہم پر بڑا احسان کیا ہے۔ جو شخص خدا سے ڈرتا اور صبر کرتا ہے تو خدا نیکوکاروں کا اجر ضائع نہیں کرتا');
INSERT INTO "jalandhari" VALUES(12,91,'12:91','وہ بولے خدا کی قسم خدا نے تم کو ہم پر فضیلت بخشی ہے اور بےشک ہم خطاکار تھے');
INSERT INTO "jalandhari" VALUES(12,92,'12:92','(یوسف نے) کہا کہ آج کے دن سے تم پر کچھ عتاب (وملامت) نہیں ہے۔ خدا تم کو معاف کرے۔ اور وہ بہت رحم کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(12,93,'12:93','یہ میرا کرتہ لے جاؤ اور اسے والد صاحب کے منہ پر ڈال دو۔ وہ بینا ہو جائیں گے۔ اور اپنے تمام اہل وعیال کو میرے پاس لے آؤ');
INSERT INTO "jalandhari" VALUES(12,94,'12:94','اور جب قافلہ (مصر سے) روانہ ہوا تو ان کے والد کہنے لگے کہ اگر مجھ کو یہ نہ کہو کہ (بوڑھا) بہک گیا ہے تو مجھے تو یوسف کی بو آ رہی ہے');
INSERT INTO "jalandhari" VALUES(12,95,'12:95','وہ بولے کہ والله آپ اسی قدیم غلطی میں (مبتلا) ہیں');
INSERT INTO "jalandhari" VALUES(12,96,'12:96','جب خوشخبری دینے والا آ پہنچا تو کرتہ یعقوب کے منہ پر ڈال دیا اور وہ بینا ہو گئے (اور بیٹوں سے) کہنے لگے کیا میں نے تم سے نہیں کہا تھا کہ میں خدا کی طرف سے وہ باتیں جانتا ہوں جو تم نہیں جانتے');
INSERT INTO "jalandhari" VALUES(12,97,'12:97','بیٹوں نے کہا کہ ابا ہمارے لیے ہمارے گناہ کی مغفرت مانگیئے۔ بےشک ہم خطاکار تھے');
INSERT INTO "jalandhari" VALUES(12,98,'12:98','انہوں نے کہا کہ میں اپنے پروردگار سے تمہارے لیے بخشش مانگوں گا۔ بےشک وہ بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(12,99,'12:99','جب یہ (سب لوگ) یوسف کے پاس پہنچے تو یوسف نے اپنے والدین کو اپنے پاس بٹھایا اور کہا مصر میں داخل ہو جائیے خدا نے چاہا تو جمع خاطر سے رہیئے گا');
INSERT INTO "jalandhari" VALUES(12,100,'12:100','اور اپنے والدین کو تخت پر بٹھایا اور سب یوسفؑ کے آگے سجدہ میں گر پڑے اور (اس وقت) یوسف نے کہا ابا جان یہ میرے اس خواب کی تعبیر ہے جو میں نے پہلے (بچپن میں) دیکھا تھا۔ میرے پروردگار نے اسے سچ کر دکھایا اور اس نے مجھ پر (بہت سے) احسان کئے ہیں کہ مجھ کو جیل خانے سے نکالا۔ اور اس کے بعد کہ شیطان نے مجھ میں اور میرے بھائیوں میں فساد ڈال دیا تھا۔ آپ کو گاؤں سے یہاں لایا۔ بےشک میرا پروردگار جو چاہتا ہے تدبیر سے کرتا ہے۔ وہ دانا (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(12,101,'12:101','(جب یہ سب باتیں ہولیں تو یوسف نے خدا سے دعا کی کہ) اے میرے پروردگار تو نے مجھ کو حکومت سے بہرہ دیا اور خوابوں کی تعبیر کا علم بخشا۔ اے آسمانوں اور زمین کے پیدا کرنے والے تو ہی دنیا اور آخرت میں میرا کارساز ہے۔ تو مجھے (دنیا سے) اپنی اطاعت (کی حالت) میں اٹھائیو اور (آخرت میں) اپنے نیک بندوں میں داخل کیجیو');
INSERT INTO "jalandhari" VALUES(12,102,'12:102','(اے پیغمبر) یہ اخبار غیب میں سے ہیں جو ہم تمہاری طرف بھیجتے ہیں اور جب برادران یوسف نے اپنی بات پر اتفاق کیا تھا اور وہ فریب کر رہے تھے تو تم ان کے پاس تو نہ تھے');
INSERT INTO "jalandhari" VALUES(12,103,'12:103','اور بہت سے آدمی گو تم (کتنی ہی) خواہش کرو ایمان لانے والے نہیں ہیں');
INSERT INTO "jalandhari" VALUES(12,104,'12:104','اور تم ان سے اس (خیر خواہی) کا کچھ صلا بھی تو نہیں مانگتے۔ یہ قرآن اور کچھ نہیں تمام عالم کے لیے نصیحت ہے');
INSERT INTO "jalandhari" VALUES(12,105,'12:105','اور آسمان و زمین میں بہت سی نشانیاں ہیں جن پر یہ گزرتے ہیں اور ان سے اعراض کرتے ہیں');
INSERT INTO "jalandhari" VALUES(12,106,'12:106','اور یہ اکثر خدا پر ایمان نہیں رکھتے۔ مگر (اس کے ساتھ) شرک کرتے ہیں');
INSERT INTO "jalandhari" VALUES(12,107,'12:107','کیا یہ اس (بات) سے بےخوف ہیں کہ ان پر خدا کا عذاب نازل ہو کر ان کو ڈھانپ لے یا ان پر ناگہاں قیامت آجائے اور انہیں خبر بھی نہ ہو');
INSERT INTO "jalandhari" VALUES(12,108,'12:108','کہہ دو میرا رستہ تو یہ ہے میں خدا کی طرف بلاتا ہوں (از روئے یقین وبرہان) سمجھ بوجھ کر میں بھی (لوگوں کو خدا کی طرف بلاتا ہوں) اور میرے پیرو بھی۔ اور خدا پاک ہے۔ اور میں شرک کرنے والوں میں سے نہیں ہوں');
INSERT INTO "jalandhari" VALUES(12,109,'12:109','اور ہم نے تم سے پہلے بستیوں کے رہنے والوں میں سے مرد ہی بھیجے تھے جن کی طرف ہم وحی بھیجتے تھے۔ کیا ان لوگوں نے ملک میں سیر (وسیاحت) نہیں کی کہ دیکھ لیتے کہ جو لوگ ان سے پہلے تھے ان کا انجام کیا ہوا۔ اور متّقیوں کے لیے آخرت کا گھر بہت اچھا ہے۔ کیا تم سمجھتے نہیں؟');
INSERT INTO "jalandhari" VALUES(12,110,'12:110','یہاں تک کہ جب پیغمبر ناامید ہوگئے اور انہوں نے خیال کیا کہ اپنی نصرت کے بارے میں جو بات انہوں نے کہی تھی (اس میں) وہ سچے نہ نکلے تو ان کے پاس ہماری مدد آ پہنچی۔ پھر جسے ہم نے چاہا بچا دیا۔ اور ہمارا عذاب (اتر کر) گنہگار لوگوں سے پھرا نہیں کرتا');
INSERT INTO "jalandhari" VALUES(12,111,'12:111','ان کے قصے میں عقلمندوں کے لیے عبرت ہے۔ یہ (قرآن) ایسی بات نہیں ہے جو (اپنے دل سے) بنائی گئی ہو بلکہ جو (کتابیں) اس سے پہلے نازل ہوئی ہیں ان کی تصدیق (کرنے والا) ہے اور مومنوں کے لیے ہدایت اور رحمت ہے');
INSERT INTO "jalandhari" VALUES(13,1,'13:1','الٓمرا۔ (اے محمد) یہ کتاب (الہیٰ) کی آیتیں ہیں۔ اور جو تمہارے پروردگار کی طرف سے تم پر نازل ہوا ہے حق ہے لیکن اکثر لوگ ایمان نہیں لاتے');
INSERT INTO "jalandhari" VALUES(13,2,'13:2','خدا وہی تو ہے جس نے ستونوں کے بغیر آسمان جیسا کہ تم دیکھتے ہو (اتنے) اونچے بنائے۔ پھر عرش پر جا ٹھہرا اور سورج اور چاند کو کام میں لگا دیا۔ ہر ایک ایک میعاد معین تک گردش کر رہا ہے۔ وہی (دنیا کے) کاموں کا انتظام کرتا ہے (اس طرح) وہ اپنی آیتیں کھول کھول کر بیان کرتا ہے کہ تم اپنے پروردگار کے روبرو جانے کا یقین کرو');
INSERT INTO "jalandhari" VALUES(13,3,'13:3','اور وہ وہی ہے جس نے زمین کو پھیلایا اور اس میں پہاڑ اور دریا پیدا کئے اور ہر طرح کے میوؤں کی دو دو قسمیں بنائیں۔ وہی رات کو دن کا لباس پہناتا ہے۔ غور کرنے والوں کے لیے اس میں بہت سی نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(13,4,'13:4','اور زمین میں کئی طرح کے قطعات ہیں۔ ایک دوسرے سے ملے ہوئے اور انگور کے باغ اور کھیتی اور کھجور کے درخت۔ بعض کی بہت سی شاخیں ہوتی ہیں اور بعض کی اتنی نہیں ہوتیں (باوجود یہ کہ) پانی سب کو ایک ہی ملتا ہے۔ اور ہم بعض میوؤں کو بعض پر لذت میں فضیلت دیتے ہیں۔ اس میں سمجھنے والوں کے لیے بہت سی نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(13,5,'13:5','اگر تم عجیب بات سننی چاہو تو کافروں کا یہ کہنا عجیب ہے کہ جب ہم (مر کر) مٹی ہو جائیں گے تو کیا ازسرنو پیدا ہوں گے؟ یہی لوگ ہیں جو اپنے پروردگار سے منکر ہوئے ہیں۔ اور یہی ہیں جن کی گردنوں میں طوق ہوں گے اور یہی اہل دوزخ ہیں کہ ہمیشہ اس میں (جلتے) رہیں گے');
INSERT INTO "jalandhari" VALUES(13,6,'13:6','اور یہ لوگ بھلائی سے پہلے تم سے برائی کے جلد خواستگار یعنی (طالب عذاب) ہیں حالانکہ ان سے پہلے عذاب (واقع) ہوچکے ہیں اور تمہارا پروردگار لوگوں کو باوجود ان کی بےانصافیوں کے معاف کرنے والا ہے۔ اور بےشک تمہارا پروردگار سخت عذاب دینے والا ہے');
INSERT INTO "jalandhari" VALUES(13,7,'13:7','اور کافر لوگ کہتے ہیں کہ اس (پیغمبر) پر اس کے پروردگار کی طرف سے کوئی نشانی نازل نہیں ہوئی۔ سو (اے محمدصلى الله عليه وسلم) تم تو صرف ہدایت کرنے والے ہو اور ہر ایک قوم کے لیے رہنما ہوا کرتا ہے');
INSERT INTO "jalandhari" VALUES(13,8,'13:8','خدا ہی اس بچے سے واقف ہے جو عورت کے پیٹ میں ہوتا ہے اور پیٹ کے سکڑنے اور بڑھنے سے بھی (واقف ہے)۔ اور ہر چیز کا اس کے ہاں ایک اندازہ مقرر ہے');
INSERT INTO "jalandhari" VALUES(13,9,'13:9','وہ دانائے نہاں وآشکار ہے سب سے بزرگ (اور) عالی رتبہ ہے');
INSERT INTO "jalandhari" VALUES(13,10,'13:10','کوئی تم میں سے چپکے سے بات کہے یا پکار کر یا رات کو کہیں چھپ جائے یا دن کی روشنی میں کھلم کھلا چلے پھرے (اس کے نزدیک) برابر ہے');
INSERT INTO "jalandhari" VALUES(13,11,'13:11','اس کے آگے اور پیچھے خدا کے چوکیدار ہیں جو خدا کے حکم سے اس کی حفاظت کرتے ہیں۔ خدا اس (نعمت) کو جو کسی قوم کو (حاصل) ہے نہیں بدلتا جب تک کہ وہ اپنی حالت کو نہ بدلے۔ اور جب خدا کسی قوم کے ساتھ برائی کا ارادہ کرتا ہے تو پھر وہ پھر نہیں سکتی۔ اور خدا کے سوا ان کا کوئی مددگار نہیں ہوتا');
INSERT INTO "jalandhari" VALUES(13,12,'13:12','اور وہی تو ہے جو تم کو ڈرانے اور امید دلانے کے لیے بجلی دکھاتا اور بھاری بھاری بادل پیدا کرتا ہے');
INSERT INTO "jalandhari" VALUES(13,13,'13:13','اور رعد اور فرشتے سب اس کے خوف سے اس کی تسبیح و تحمید کرتے رہتے ہیں اور وہی بجلیاں بھیجتا ہے پھر جس پر چاہتا ہے گرا بھی دیتا ہے اور وہ خدا کے بارے میں جھگڑتے ہیں۔ اور وہ بڑی قوت والا ہے');
INSERT INTO "jalandhari" VALUES(13,14,'13:14','سودمند پکارنا تو اسی کا ہے اور جن کو یہ لوگ اس کے سوا پکارتے ہیں وہ ان کی پکار کو کسی طرح قبول نہیں کرتے مگر اس شخص کی طرح جو اپنے دونوں ہاتھ پانی کی طرف پھیلا دے تاکہ (دور ہی سے) اس کے منہ تک آ پہنچے حالانکہ وہ (اس تک کبھی بھی) نہیں آسکتا اور (اسی طرح) کافروں کی پکار بیکار ہے');
INSERT INTO "jalandhari" VALUES(13,15,'13:15','اور جتنی مخلوقات آسمانوں اور زمین میں ہے خوشی سے یا زبردستی سے خدا کے آگے سجدہ کرتی ہے اور ان کے سائے بھی صبح وشام (سجدے کرتے ہیں)');
INSERT INTO "jalandhari" VALUES(13,16,'13:16','ان سے پوچھو کہ آسمانوں اور زمین کا پروردگار کون ہے؟ (تم ہی ان کی طرف سے) کہہ دو کہ خدا۔ پھر (ان سے) کہو کہ تم نے خدا کو چھوڑ کر ایسے لوگوں کو کیوں کارساز بنایا ہے جو خود اپنے نفع ونقصان کا بھی اختیار نہیں رکھتے (یہ بھی) پوچھو کیا اندھا اور آنکھوں والا برابر ہیں؟ یا اندھیرا اور اُجالا برابر ہوسکتا ہے؟ بھلا ان لوگوں نے جن کو خدا کا شریک مقرر کیا ہے۔ کیا انہوں نے خدا کی سی مخلوقات پیدا کی ہے جس کے سبب ان کو مخلوقات مشتبہ ہوگئی ہے۔ کہہ دو کہ خدا ہی ہر چیز کا پیدا کرنے والا ہے اور وہ یکتا (اور) زبردست ہے');
INSERT INTO "jalandhari" VALUES(13,17,'13:17','اسی نے آسمان سے مینہ برسایا پھر اس سے اپنے اپنے اندازے کے مطابق نالے بہہ نکلے پھر نالے پر پھولا ہوا جھاگ آگیا۔ اور جس چیز کو زیور یا کوئی اور سامان بنانے کے لیے آگ میں تپاتے ہیں اس میں بھی ایسا ہی جھاگ ہوتا ہے۔ اس طرح خدا حق اور باطل کی مثال بیان فرماتا ہے۔ سو جھاگ تو سوکھ کر زائل ہو جاتا ہے۔ اور (پانی) جو لوگوں کو فائدہ پہنچاتا ہے وہ زمین میں ٹھہرا رہتا ہے۔ اس طرح خدا (صحیح اور غلط کی) مثالیں بیان فرماتا ہے (تاکہ تم سمجھو)');
INSERT INTO "jalandhari" VALUES(13,18,'13:18','جن لوگوں نے خدا کے حکم کو قبول کیا ان کی حالت بہت بہتر ہوگی۔ اور جنہوں نے اس کو قبول نہ کیا اگر روئے زمین کے سب خزانے ان کے اختیار میں ہوں تو وہ سب کے سب اور ان کے ساتھ اتنے ہی اور (نجات کے) بدلے میں صرف کرڈالیں (مگر نجات کہاں؟) ایسے لوگوں کا حساب بھی برا ہوگا۔ اور ان کا ٹھکانا بھی دوزخ ہے۔ اور وہ بری جگہ ہے');
INSERT INTO "jalandhari" VALUES(13,19,'13:19','بھلا جو شخص یہ جانتا ہے کہ جو کچھ تمہارے پروردگار کی طرف سے تم پر نازل ہوا ہے حق ہے وہ اس شخص کی طرح ہے جو اندھا ہے اور سمجھتے تو وہی ہیں جو عقلمند ہیں');
INSERT INTO "jalandhari" VALUES(13,20,'13:20','جو خدا کے عہد کو پورا کرتے ہیں اور اقرار کو نہیں توڑتے');
INSERT INTO "jalandhari" VALUES(13,21,'13:21','اور جن (رشتہ ہائے قرابت) کے جوڑے رکھنے کا خدا نے حکم دیا ہے ان کو جوڑے رکھتے اور اپنے پروردگار سے ڈرتے رہتے اور برے حساب سے خوف رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(13,22,'13:22','اور جو پروردگار کی خوشنودی حاصل کرنے کے لیے (مصائب پر) صبر کرتے ہیں اور نماز پڑھتے ہیں اور جو (مال) ہم نے ان کو دیا ہے اس میں سے پوشیدہ اور ظاہر خرچ کرتے ہیں اور نیکی سے برائی دور کرتے ہیں یہی لوگ ہیں جن کے لیے عاقبت کا گھر ہے');
INSERT INTO "jalandhari" VALUES(13,23,'13:23','(یعنی) ہمیشہ رہنے کے باغات جن میں وہ داخل ہوں گے اور ان کے باپ دادا اور بیبیوں اور اولاد میں سے جو نیکوکار ہوں گے وہ بھی (بہشت میں جائیں گے) اور فرشتے (بہشت کے) ہر ایک دروازے سے ان کے پاس آئیں گے');
INSERT INTO "jalandhari" VALUES(13,24,'13:24','(اور کہیں گے) تم پر رحمت ہو (یہ) تمہاری ثابت قدمی کا بدلہ ہے اور عاقبت کا گھر خوب (گھر) ہے');
INSERT INTO "jalandhari" VALUES(13,25,'13:25','اور جو لوگ خدا سے عہد واثق کر کے اس کو توڑ ڈالتے اور (رشتہ ہائے قرابت) کے جوڑے رکھنے کا خدا نے حکم دیا ہے ان کو قطع کر دیتے ہیں اور ملک میں فساد کرتے ہیں۔ ایسوں پر لعنت ہے اور ان کے لیے گھر بھی برا ہے');
INSERT INTO "jalandhari" VALUES(13,26,'13:26','خدا جس کا چاہتا ہے رزق فراخ کر دیتا ہے اور (جس کا چاہتا ہے) تنگ کر دیتا ہے۔ اور کافر لوگ دنیا کی زندگی پر خوش ہو رہے ہیں اور دنیا کی زندگی آخرت (کے مقابلے) میں (بہت) تھوڑا فائدہ ہے');
INSERT INTO "jalandhari" VALUES(13,27,'13:27','اور کافر کہتے ہیں کہ اس (پیغمبر) پر اس کے پروردگار کی طرف سے کوئی نشانی کیوں نازل نہیں ہوئی۔ کہہ دو کہ خدا جسے چاہتا ہے گمراہ کرتا ہے اور جو (اس کی طرف) رجوع ہوتا ہے اس کو اپنی طرف کا رستہ دکھاتا ہے');
INSERT INTO "jalandhari" VALUES(13,28,'13:28','(یعنی) جو لوگ ایمان لاتے اور جن کے دل یادِ خدا سے آرام پاتے ہیں (ان کو) اور سن رکھو کہ خدا کی یاد سے دل آرام پاتے ہیں');
INSERT INTO "jalandhari" VALUES(13,29,'13:29','جو لوگ ایمان لائے اور عمل نیک کئے ان کے لیے خوشحالی اور عمدہ ٹھکانہ ہے');
INSERT INTO "jalandhari" VALUES(13,30,'13:30','(جس طرح ہم اور پیغمبر بھیجتے رہے ہیں) اسی طرح (اے محمدصلى الله عليه وسلم) ہم نے تم کو اس امت میں جس سے پہلے بہت سی امتیں گزر چکی ہیں بھیجا ہے تاکہ تم ان کو وہ (کتاب) جو ہم نے تمہاری طرف بھیجی ہے پڑھ کر سنا دو اور یہ لوگ رحمٰن کو نہیں مانتے۔ کہہ دو وہی تو میرا پروردگار ہے اس کے سوا کوئی معبود نہیں۔ میں اسی پر بھروسہ رکھتا ہوں اور اسی کی طرف رجوع کرتا ہوں');
INSERT INTO "jalandhari" VALUES(13,31,'13:31','اور اگر کوئی قرآن ایسا ہوتا کہ اس (کی تاثیر) سے پہاڑ چل پڑتے یا زمین پھٹ جاتی یا مردوں سے کلام کرسکتے۔ (تو یہی قرآن ان اوصاف سے متصف ہوتا مگر) بات یہ ہے کہ سب باتیں خدا کے اختیار میں ہیں تو کیا مومنوں کو اس سے اطمینان نہیں ہوا کہ اگر خدا چاہتا تو سب لوگوں کو ہدایت کے رستے پر چلا دیتا۔ اور کافروں پر ہمیشہ ان کے اعمال کے بدلے بلا آتی رہے گی یا ان کے مکانات کے قریب نازل ہوتی رہے گی یہاں تک کہ خدا کا وعدہ آپہنچے۔ بےشک خدا وعدہ خلاف نہیں کرتا');
INSERT INTO "jalandhari" VALUES(13,32,'13:32','اور تم سے پہلے بھی رسولوں کے ساتھ تمسخر ہوتے رہے ہیں تو ہم نے کافروں کو مہلت دی پھر پکڑ لیا۔ سو (دیکھ لو کہ) ہمارا عذاب کیسا تھا');
INSERT INTO "jalandhari" VALUES(13,33,'13:33','تو کیا جو (خدا) ہر متنفس کے اعمال کا نگراں (ونگہباں) ہے (وہ بتوں کی طرح بےعلم وبےخبر ہوسکتا ہے) اور ان لوگوں نے خدا کے شریک مقرر کر رکھے ہیں۔ ان سے کہو کہ (ذرا) ان کے نام تو لو۔ کیا تم اسے ایسی چیزیں بتاتے ہو جس کو وہ زمین میں (کہیں بھی) معلوم نہیں کرتا یا (محض) ظاہری (باطل اور جھوٹی) بات کی (تقلید کرتے ہو) اصل یہ ہے کہ کافروں کو ان کے فریب خوبصورت معلوم ہوتے ہیں۔ اور وہ (ہدایت کے) رستے سے روک لیے گئے ہیں۔ اور جسے خدا گمراہ کرے اسے کوئی ہدایت کرنے والا نہیں');
INSERT INTO "jalandhari" VALUES(13,34,'13:34','ان کو دنیا کی زندگی میں بھی عذاب ہے اور آخرت کا عذاب تو بہت ہی سخت ہے۔ اور ان کو خدا (کے عذاب سے) کوئی بھی بچانے والا نہیں');
INSERT INTO "jalandhari" VALUES(13,35,'13:35','جس باغ کا متقیوں سے وعدہ کیا گیا ہے اس کے اوصاف یہ ہیں کہ اس کے نیچے نہریں بہہ رہی ہیں۔ اس کے پھل ہمیشہ (قائم رہنے والے) ہیں اور اس کے سائے بھی۔ یہ ان لوگوں کا انجام ہے جو متقی ہیں۔ اور کافروں کا انجام دوزخ ہے');
INSERT INTO "jalandhari" VALUES(13,36,'13:36','اور جن لوگوں کو ہم نے کتاب دی ہے وہ اس (کتاب) سے جو تم پر نازل ہوئی ہے خوش ہوتے ہیں اور بعض فرقے اس کی بعض باتیں نہیں بھی مانتے۔ کہہ دو کہ مجھ کو یہی حکم ہوا ہے کہ خدا ہی کی عبادت کروں اور اس کے ساتھ کسی کو شریک نہ بناؤں۔ میں اسی کی طرف بلاتا ہوں اور اسی کی طرف مجھے لوٹنا ہے');
INSERT INTO "jalandhari" VALUES(13,37,'13:37','اور اسی طرح ہم نے اس قرآن کو عربی زبان کا فرمان نازل کیا ہے۔ اور اگر تم علم (ودانش) آنے کے بعد ان لوگوں کی خواہشوں کے پیچھے چلو گے تو خدا کے سامنے کوئی نہ تمہارا مددگار ہوگا اور نہ کوئی بچانے والا');
INSERT INTO "jalandhari" VALUES(13,38,'13:38','اور (اے محمدصلى الله عليه وسلم) ہم نے تم سے پہلے بھی پیغمبر بھیجے تھے۔ اور ان کو بیبیاں اور اولاد بھی دی تھی ۔اور کسی پیغمبر کے اختیار کی بات نہ تھی کہ خدا کے حکم کے بغیر کوئی نشانی لائے۔ ہر (حکم) قضا (کتاب میں) مرقوم ہے');
INSERT INTO "jalandhari" VALUES(13,39,'13:39','خدا جس کو چاہتا ہے مٹا دیتا ہے اور (جس کو چاہتا ہے) قائم رکھتا ہے اور اسی کے پاس اصل کتاب ہے');
INSERT INTO "jalandhari" VALUES(13,40,'13:40','اور اگر ہم کوئی عذاب جس کا ان لوگوں سے وعدہ کرتے ہیں تمہیں دکھائیں (یعنی تمہارے روبرو ان پر نازل کریں) یا تمہاری مدت حیات پوری کر دیں (یعنی تمہارے انتقال کے بعد عذاب بھیجیں) تو تمہارا کام (ہمارے احکام کا) پہنچا دینا ہے اور ہمارا کام حساب لینا ہے');
INSERT INTO "jalandhari" VALUES(13,41,'13:41','کیا انہوں نے نہیں دیکھا کہ ہم زمین کو اس کے کناروں سے گھٹاتے چلے آتے ہیں۔ اور خدا (جیسا چاہتا ہے) حکم کرتا ہے کوئی اس کے حکم کا رد کرنے والا نہیں۔ اور وہ جلد حساب لینے والا ہے');
INSERT INTO "jalandhari" VALUES(13,42,'13:42','جو لوگ ان سے پہلے تھے وہ بھی (بہتری) چالیں چلتے رہے ہیں سو چال تو سب الله ہی کی ہے ہر متنفس جو کچھ کر رہا ہے وہ اسے جانتا ہے۔ اور کافر جلد معلوم کریں گے کہ عاقبت کا گھر (یعنی انجام محمود) کس کے لیے ہے');
INSERT INTO "jalandhari" VALUES(13,43,'13:43','اور کافر لوگ کہتے ہیں کہ تم (خدا کے) رسول نہیں ہو۔ کہہ دو کہ میرے اور تمہارے درمیان خدا اور وہ شخص جس کے پاس کتاب (آسمانی) کا علم ہے گواہ کافی ہیں');
INSERT INTO "jalandhari" VALUES(14,1,'14:1','الٓرٰ۔ (یہ) ایک (پُرنور) کتاب (ہے) اس کو ہم نے تم پر اس لیے نازل کیا ہے کہ لوگوں کو اندھیرے سے نکال کر روشنی کی طرف لے جاؤ (یعنی) ان کے پروردگار کے حکم سے غالب اور قابل تعریف (خدا) کے رستے کی طرف');
INSERT INTO "jalandhari" VALUES(14,2,'14:2','وہ خدا کہ جو کچھ آسمانوں اور زمین میں ہے سب اسی کا ہے۔ اور کافروں کے لیے عذاب سخت (کی وجہ) سے خرابی ہے');
INSERT INTO "jalandhari" VALUES(14,3,'14:3','جو آخرت کی نسبت دنیا کو پسند کرتے اور (لوگوں کو) خدا کے رستے سے روکتے اور اس میں کجی چاہتے ہیں۔ یہ لوگ پرلے سرے کی گمراہی میں ہیں');
INSERT INTO "jalandhari" VALUES(14,4,'14:4','اور ہم نے کوئی پیغمبر نہیں بھیجا مگر اپنی قوم کی زبان بولتا تھا تاکہ انہیں (احکام خدا) کھول کھول کر بتا دے۔ پھر خدا جسے چاہتا ہے گمراہ کرتا ہے اور جسے چاہتا ہے ہدایت دیتا ہے اور وہ غالب (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(14,5,'14:5','اور ہم نے موسیٰ کو اپنی نشانیاں دے کر بھیجا کہ اپنی قوم کو تاریکی سے نکال کر روشنی میں لے جاؤ۔ اور ان کو خدا کے دن یاد دلاؤ اس میں ان لوگوں کے لیے جو صابر وشاکر ہیں (قدرت خدا کی) نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(14,6,'14:6','اور جب موسیٰ نے اپنی قوم سے کہا کہ خدا نے جو تم پر مہربانیاں کی ہیں ان کو یاد کرو جب کہ تم کو فرعون کی قوم (کے ہاتھ) سے مخلصی دی وہ لوگ تمہیں بُرے عذاب دیتے تھے اور تمہارے بیٹوں کو مار ڈالتے تھے اور عورت ذات یعنی تمہاری لڑکیوں کو زندہ رہنے دیتے تھے اور اس میں تمہارے پروردگار کی طرف سے بڑی (سخت) آزمائش تھی');
INSERT INTO "jalandhari" VALUES(14,7,'14:7','اور جب تمہارے پروردگار نے (تم کو) آگاہ کیا کہ اگر شکر کرو گے تو میں تمہیں زیادہ دوں گا اور اگر ناشکری کرو گے تو (یاد رکھو کہ) میرا عذاب بھی سخت ہے');
INSERT INTO "jalandhari" VALUES(14,8,'14:8','اور موسیٰ نے (صاف صاف) کہہ دیا کہ اگر تم اور جتنے اور لوگ زمین میں ہیں سب کے سب ناشکری کرو تو خدا بھی بےنیاز (اور) قابل تعریف ہے');
INSERT INTO "jalandhari" VALUES(14,9,'14:9','بھلا تم کو ان لوگوں (کے حالات) کی خبر نہیں پہنچی جو تم سے پہلے تھے (یعنی) نوح اور عاد اور ثمود کی قوم۔ اور جو ان کے بعد تھے۔ جن کا علم خدا کے سوا کسی کو نہیں (جب) ان کے پاس پیغمبر نشانیاں لے کر آئے تو انہوں نے اپنے ہاتھ ان کے مونہوں پر رکھ دیئے (کہ خاموش رہو) اور کہنے لگے کہ ہم تو تمہاری رسالت کو تسلیم نہیں کرتے اور جس چیز کی طرف تم ہمیں بلاتے ہو ہم اس سے قوی شک میں ہیں');
INSERT INTO "jalandhari" VALUES(14,10,'14:10','ان کے پیغمبروں نے کہا کیا (تم کو) خدا (کے بارے) میں شک ہے جو آسمانوں اور زمین کا پیدا کرنے والا ہے۔ وہ تمہیں اس لیے بلاتا ہے کہ تمہارے گناہ بخشے اور (فائدہ پہنچانے کے لیے) ایک مدت مقرر تک تم کو مہلت دے۔ وہ بولے کہ تم تو ہمارے ہی جیسے آدمی ہو۔ تمہارا یہ منشاء ہے کہ جن چیزوں کو ہمارے بڑے پوجتے رہے ہیں ان (کے پوجنے) سے ہم کو بند کر دو تو (اچھا) کوئی کھلی دلیل لاؤ (یعنی معجزہ دکھاؤ)');
INSERT INTO "jalandhari" VALUES(14,11,'14:11','پیغمبروں نے ان سے کہا کہ ہاں ہم تمہارے ہی جیسے آدمی ہیں۔ لیکن خدا اپنے بندوں میں سے جس کو چاہتا ہے (نبوت کا) احسان کرتا ہے اور ہمارے اختیار کی بات نہیں کہ ہم خدا کے حکم کے بغیر تم کو (تمہاری فرمائش کے مطابق) معجزہ دکھائیں اور خدا ہی پر مومنوں کو بھروسہ رکھنا چاہیئے');
INSERT INTO "jalandhari" VALUES(14,12,'14:12','اور ہم کیونکر خدا پر بھروسہ نہ رکھیں حالانکہ اس نے ہم کو ہمارے (دین کے سیدھے) رستے بتائے ہیں۔ جو تکلیفیں تم ہم کو دیتے ہو اس پر صبر کریں گے۔ اور اہل توکل کو خدا ہی پر بھروسہ رکھنا چاہیئے');
INSERT INTO "jalandhari" VALUES(14,13,'14:13','اور جو کافر تھے انہوں نے اپنے پیغمبروں سے کہا کہ (یا تو) ہم تم کو اپنے ملک سے باہر نکال دیں گے یا ہمارے مذہب میں داخل ہو جاؤ۔ تو پروردگار نے ان کی طرف وحی بھیجی کہ ہم ظالموں کو ہلاک کر دیں گے');
INSERT INTO "jalandhari" VALUES(14,14,'14:14','اور ان کے بعد تم کو اس زمین میں آباد کریں گے۔ یہ اس شخص کے لیے ہے جو (قیامت کے روز) میرے سامنے کھڑے ہونے سے ڈرے اور میرے عذاب سے خوف کرے');
INSERT INTO "jalandhari" VALUES(14,15,'14:15','اور پیغمبروں نے (خدا سے اپنی) فتح چاہی تو ہر سرکش ضدی نامراد رہ گیا');
INSERT INTO "jalandhari" VALUES(14,16,'14:16','اس کے پیچھے دوزخ ہے اور اسے پیپ کا پانی پلایا جائے گا');
INSERT INTO "jalandhari" VALUES(14,17,'14:17','وہ اس کو گھونٹ گھونٹ پیئے گا اور گلے سے نہیں اتار سکے گا اور ہر طرف سے اسے موت آرہی ہوگی مگر وہ مرنے میں نہیں آئے گا۔ اور اس کے پیچھے سخت عذاب ہوگا');
INSERT INTO "jalandhari" VALUES(14,18,'14:18','جن لوگوں نے اپنے پروردگار سے کفر کیا ان کے اعمال کی مثال راکھ کی سی ہے کہ آندھی کے دن اس پر زور کی ہوا چلے (اور) اسے اڑا لے جائے (اس طرح) جو کام وہ کرتے رہے ان پر ان کو کچھ دسترس نہ ہوگی۔ یہی تو پرلے سرے کی گمراہی ہے');
INSERT INTO "jalandhari" VALUES(14,19,'14:19','کیا تم نے نہیں دیکھا کہ خدا نے آسمانوں اور زمین کو تدبیر سے پیدا کیا ہے۔ اگر وہ چاہے تو تم کو نابود کر دے اور (تمہاری جگہ) نئی مخلوق پیدا کر دے');
INSERT INTO "jalandhari" VALUES(14,20,'14:20','اور یہ خدا کو کچھ بھی مشکل نہیں');
INSERT INTO "jalandhari" VALUES(14,21,'14:21','اور (قیامت کے دن) سب لوگ خدا کے سامنے کھڑے ہوں گے تو ضعیف (العقل متبع اپنے رؤسائے) متکبرین سے کہیں گے کہ ہم تو تمہارے پیرو تھے۔ کیا تم خدا کا کچھ عذاب ہم پر سے دفع کرسکتے ہو۔ وہ کہیں گے کہ اگر خدا ہم کو ہدایت کرتا تو ہم تم کو ہدایت کرتے۔ اب ہم گھبرائیں یا ضد کریں ہمارے حق میں برابر ہے۔ کوئی جگہ (گریز اور) رہائی کی ہمارے لیے نہیں ہے');
INSERT INTO "jalandhari" VALUES(14,22,'14:22','جب (حساب کتاب کا) کام فیصلہ ہوچکے گا تو شیطان کہے گا (جو) وعدہ خدا نے تم سے کیا تھا (وہ تو) سچا (تھا) اور (جو) وعدہ میں نے تم سے کیا تھا وہ جھوٹا تھا۔ اور میرا تم پر کسی طرح کا زور نہیں تھا۔ ہاں میں نے تم کو (گمراہی اور باطل کی طرف) بلایا تو تم نے (جلدی سے اور بےدلیل) میرا کہا مان لیا۔ تو (آج) مجھے ملامت نہ کرو۔ اپنے آپ ہی کو ملامت کرو۔ نہ میں تمہاری فریاد رسی کرسکتا ہوں اور نہ تم میری فریاد رسی کرسکتے ہو۔ میں اس بات سے انکار کرتا ہوں کہ تم پہلے مجھے شریک بناتے تھے۔ بےشک جو ظالم ہیں ان کے لیے درد دینے والا عذاب ہے');
INSERT INTO "jalandhari" VALUES(14,23,'14:23','اور جو ایمان لائے اور عمل نیک کیے وہ بہشتوں میں داخل کیے جائیں گے جن کے نیچے نہریں بہہ رہی ہیں اپنے پروردگار کے حکم سے ہمیشہ ان میں رہیں گے۔ وہاں ان کی صاحب سلامت سلام ہوگا');
INSERT INTO "jalandhari" VALUES(14,24,'14:24','کیا تم نے نہیں دیکھا کہ خدا نے پاک بات کی کیسی مثال بیان فرمائی ہے (وہ ایسی ہے) جیسے پاکیزہ درخت جس کی جڑ مضبوط (یعنی زمین کو پکڑے ہوئے) ہو اور شاخیں آسمان میں');
INSERT INTO "jalandhari" VALUES(14,25,'14:25','اپنے پروردگار کے حکم سے ہر وقت پھل لاتا (اور میوے دیتا) ہو۔ اور خدا لوگوں کے لیے مثالیں بیان فرماتا ہے تاکہ وہ نصیحت پکڑیں');
INSERT INTO "jalandhari" VALUES(14,26,'14:26','اور ناپاک بات کی مثال ناپاک درخت کی سی ہے (نہ جڑ مستحکم نہ شاخیں بلند) زمین کے اوپر ہی سے اکھیڑ کر پھینک دیا جائے گا اس کو ذرا بھی قرار (وثبات) نہیں');
INSERT INTO "jalandhari" VALUES(14,27,'14:27','خدا مومنوں (کے دلوں) کو (صحیح اور) پکی بات سے دنیا کی زندگی میں بھی مضبوط رکھتا ہے اور آخرت میں بھی (رکھے گا) اور خدا بےانصافوں کو گمراہ کر دیتا ہے اور خدا جو چاہتا ہے کرتا ہے');
INSERT INTO "jalandhari" VALUES(14,28,'14:28','کیا تم نے ان لوگوں کو نہیں دیکھا جنہوں نے خدا کے احسان کو ناشکری سے بدل دیا۔ اور اپنی قوم کو تباہی کے گھر میں اتارا');
INSERT INTO "jalandhari" VALUES(14,29,'14:29','(وہ گھر) دوزخ ہے۔ (سب ناشکرے) اس میں داخل ہوں گے۔ اور وہ برا ٹھکانہ ہے');
INSERT INTO "jalandhari" VALUES(14,30,'14:30','اور ان لوگوں نے خدا کے شریک مقرر کئے کہ (لوگوں کو) اس کے رستے سے گمراہ کریں۔ کہہ دو کہ (چند روز) فائدے اٹھا لو آخرکار تم کو دوزخ کی طرف لوٹ کر جانا ہے');
INSERT INTO "jalandhari" VALUES(14,31,'14:31','(اے پیغمبر) میرے مومن بندوں سے کہہ دو کہ نماز پڑھا کریں اور اس دن کے آنے سے پیشتر جس میں نہ (اعمال کا) سودا ہوگا اور نہ دوستی (کام آئے گی) ہمارے دیئے ہوئے مال میں سے درپردہ اور ظاہر خرچ کرتے رہیں');
INSERT INTO "jalandhari" VALUES(14,32,'14:32','خدا ہی تو ہے جس نے آسمانوں اور زمین کو پیدا کیا اور آسمان سے مینہ برسایا پھر اس سے تمہارے کھانے کے لیے پھل پیدا کئے۔ اور کشتیوں (اور جہازوں) کو تمہارے زیر فرمان کیا تاکہ دریا (اور سمندر) میں اس کے حکم سے چلیں۔ اور نہروں کو بھی تمہارے زیر فرمان کیا');
INSERT INTO "jalandhari" VALUES(14,33,'14:33','اور سورج اور چاند کو تمہارے لیے کام میں لگا دیا کہ دونوں (دن رات) ایک دستور پر چل رہے ہیں۔ اور رات اور دن کو بھی تمہاری خاطر کام میں لگا دیا');
INSERT INTO "jalandhari" VALUES(14,34,'14:34','اور جو کچھ تم نے مانگا سب میں سے تم کو عنایت کیا۔ اور اگر خدا کے احسان گننے لگو تو شمار نہ کرسکو۔ (مگر لوگ نعمتوں کا شکر نہیں کرتے) کچھ شک نہیں کہ انسان بڑا بےانصاف اور ناشکرا ہے');
INSERT INTO "jalandhari" VALUES(14,35,'14:35','اور جب ابراہیم نے دعا کی کہ میرے پروردگار اس شہر کو (لوگوں کے لیے) امن کی جگہ بنا دے۔ اور مجھے اور میری اولاد کو اس بات سے کہ بتوں کی پرستش کرنے لگیں بچائے رکھ');
INSERT INTO "jalandhari" VALUES(14,36,'14:36','اے پروردگار انہوں نے بہت سے لوگوں کو گمراہ کیا ہے۔ سو جس شخص نے میرا کہا مانا وہ میرا ہے۔ اور جس نے میری نافرمانی کی تو تُو بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(14,37,'14:37','اے پروردگار میں نے اپنی اولاد کو میدان (مکہ) میں جہاں کھیتی نہیں تیرے عزت (وادب) والے گھر کے پاس لابسائی ہے۔ اے پروردگار تاکہ یہ نماز پڑھیں تو لوگوں کے دلوں کو ایسا کر دے کہ ان کی طرف جھکے رہیں اور ان کو میوؤں سے روزی دے تاکہ (تیرا) شکر کریں');
INSERT INTO "jalandhari" VALUES(14,38,'14:38','اے پروردگار جو بات ہم چھپاتے اور جو ظاہر کرتے ہیں تو سب جانتا ہے۔ اور خدا سے کوئی چیز مخفی نہیں (نہ) زمین میں نہ آسمان میں');
INSERT INTO "jalandhari" VALUES(14,39,'14:39','خدا کا شکر ہے جس نے مجھ کو بڑی عمر میں اسماعیل اور اسحاق بخشے۔ بےشک میرا پروردگار سننے والا ہے');
INSERT INTO "jalandhari" VALUES(14,40,'14:40','اے پروردگار مجھ کو (ایسی توفیق عنایت) کر کہ نماز پڑھتا رہوں اور میری اولاد کو بھی (یہ توفیق بخش) اے پروردگار میری دعا قبول فرما');
INSERT INTO "jalandhari" VALUES(14,41,'14:41','اے پروردگار حساب (کتاب) کے دن مجھ کو اور میرے ماں باپ کو اور مومنوں کو مغفرت کیجیو');
INSERT INTO "jalandhari" VALUES(14,42,'14:42','اور (مومنو) مت خیال کرنا کہ یہ ظالم جو عمل کر رہے ہیں خدا ان سے بےخبر ہے۔ وہ ان کو اس دن تک مہلت دے رہا ہے جب کہ (دہشت کے سبب) آنکھیں کھلی کی کھلی رہ جائیں گی');
INSERT INTO "jalandhari" VALUES(14,43,'14:43','(اور لوگ) سر اٹھائے ہوئے (میدان قیامت کی طرف) دوڑ رہے ہوں گے ان کی نگاہیں ان کی طرف لوٹ نہ سکیں گی اور ان کے دل (مارے خوف کے) ہوا ہو رہے ہوں گے');
INSERT INTO "jalandhari" VALUES(14,44,'14:44','اور لوگوں کو اس دن سے آگاہ کردو جب ان پر عذاب آجائے گا تب ظالم لوگ کہیں گے کہ اے ہمارے پروردگار ہمیں تھوڑی سی مدت مہلت عطا کر۔ تاکہ تیری دعوت (توحید) قبول کریں اور (تیرے) پیغمبروں کے پیچھے چلیں (تو جواب ملے گا) کیا تم پہلے قسمیں نہیں کھایا کرتے تھے کہ تم کو (اس حال سے جس میں تم ہو) زوال (اور قیامت کو حساب اعمال) نہیں ہوگا');
INSERT INTO "jalandhari" VALUES(14,45,'14:45','اور جو لوگ اپنے آپ پر ظلم کرتے تھے تم ان کے مکانوں میں رہتے تھے اور تم پر ظاہر ہوچکا تھا کہ ہم نے ان لوگوں کے ساتھ کس طرح (کا معاملہ) کیا تھا اور تمہارے (سمجھانے) کے لیے مثالیں بیان کر دی تھیں');
INSERT INTO "jalandhari" VALUES(14,46,'14:46','اور انہوں نے (بڑی بڑی) تدبیریں کیں اور ان کی (سب) تدبیریں خدا کے ہاں (لکھی ہوئی) ہیں گو وہ تدبیریں ایسی (غضب کی) تھیں کہ ان سے پہاڑ بھی ٹل جائیں');
INSERT INTO "jalandhari" VALUES(14,47,'14:47','تو ایسا خیال نہ کرنا کہ خدا نے جو اپنے پیغمبروں سے وعدہ کیا ہے اس کے خلاف کرے گا بےشک خدا زبردست (اور) بدلہ لینے والا ہے');
INSERT INTO "jalandhari" VALUES(14,48,'14:48','جس دن یہ زمین دوسری زمین سے بدل دی جائے گی اور آسمان بھی (بدل دیئے جائیں گے) اور سب لوگ خدائے یگانہ وزبردست کے سامنے نکل کھڑے ہوں گے');
INSERT INTO "jalandhari" VALUES(14,49,'14:49','اور اس دن تم گنہگاروں کو دیکھو گے کہ زنجیروں میں جکڑے ہوئے ہیں');
INSERT INTO "jalandhari" VALUES(14,50,'14:50','ان کے کرتے گندھک کے ہوں گے اور ان کے مونہوں کو آگ لپیٹ رہی ہوگی');
INSERT INTO "jalandhari" VALUES(14,51,'14:51','یہ اس لیے کہ خدا ہر شخص کو اس کے اعمال کا بدلہ دے۔ بےشک خدا جلد حساب لینے والا ہے');
INSERT INTO "jalandhari" VALUES(14,52,'14:52','یہ قرآن لوگوں کے نام (خدا کا پیغام) ہے تاکہ ان کو اس سے ڈرایا جائے اور تاکہ وہ جان لیں کہ وہی اکیلا معبود ہے اور تاکہ اہل عقل نصیحت پکڑیں');
INSERT INTO "jalandhari" VALUES(15,1,'15:1','آلرا۔ یہ خدا کی کتاب اور قرآن روشن کی آیتیں ہیں');
INSERT INTO "jalandhari" VALUES(15,2,'15:2','کسی وقت کافر لوگ آرزو کریں گے کہ اے کاش وہ مسلمان ہوتے');
INSERT INTO "jalandhari" VALUES(15,3,'15:3','(اے محمد) ان کو اُن کے حال پر رہنے دو کہ کھالیں اور فائدے اُٹھالیں اور (طول) امل ان کو دنیا میں مشغول کئے رہے عنقریب ان کو (اس کا انجام) معلوم ہو جائے گا');
INSERT INTO "jalandhari" VALUES(15,4,'15:4','اور ہم نے کوئی بستی ہلاک نہیں کی۔ مگر اس کا وقت مرقوم ومعین تھا');
INSERT INTO "jalandhari" VALUES(15,5,'15:5','کوئی جماعت اپنی مدت (وفات) سے نہ آگے نکل سکتی ہے نہ پیچھے رہ سکتی ہے');
INSERT INTO "jalandhari" VALUES(15,6,'15:6','اور کفار کہتے ہیں کہ اے شخص جس پر نصیحت (کی کتاب) نازل ہوئی ہے تُو تو دیوانہ ہے');
INSERT INTO "jalandhari" VALUES(15,7,'15:7','اگر تو سچا ہے تو ہمارے پاس فرشتوں کو کیوں نہیں لے آتا');
INSERT INTO "jalandhari" VALUES(15,8,'15:8','(کہہ دو) ہم فرشتوں کو نازل نہیں کیا کرتے مگر حق کے ساتھ اور اس وقت ان کو مہلت نہیں ملتی');
INSERT INTO "jalandhari" VALUES(15,9,'15:9','بےشک یہ (کتاب) نصیحت ہمیں نے اُتاری ہے اور ہم ہی اس کے نگہبان ہیں');
INSERT INTO "jalandhari" VALUES(15,10,'15:10','اور ہم نے تم سے پہلے لوگوں میں بھی پیغمبر بھیجے تھے');
INSERT INTO "jalandhari" VALUES(15,11,'15:11','اور اُن کے پاس کوئی پیغمبر نہیں آتا تھا مگر وہ اُس کے ساتھ استہزاء کرتے تھے');
INSERT INTO "jalandhari" VALUES(15,12,'15:12','اسی طرح ہم اس (تکذیب وضلال) کو گنہگاروں کے دلوں میں داخل کر دیتے ہیں');
INSERT INTO "jalandhari" VALUES(15,13,'15:13','سو وہ اس پر ایمان نہیں لاتے اور پہلوں کی روش بھی یہی رہی ہے');
INSERT INTO "jalandhari" VALUES(15,14,'15:14','اوراگر ہم آسمان کا کوئی دروازہ اُن پر کھول دیں اور وہ اس میں چڑھنے بھی لگیں');
INSERT INTO "jalandhari" VALUES(15,15,'15:15','تو بھی یہی کہیں کہ ہماری آنکھیں مخمور ہوگئی ہیں بلکہ ہم پر جادو کر دیا گیا ہے');
INSERT INTO "jalandhari" VALUES(15,16,'15:16','اور ہم ہی نے آسمان میں برج بنائے اور دیکھنے والوں کے لیے اُس کو سجا دیا');
INSERT INTO "jalandhari" VALUES(15,17,'15:17','اور ہر شیطان راندہٴ درگاہ سے اُسے محفوظ کر دیا');
INSERT INTO "jalandhari" VALUES(15,18,'15:18','ہاں اگر کوئی چوری سے سننا چاہے تو چمکتا ہوا انگارہ اس کے پیچھے لپکتا ہے');
INSERT INTO "jalandhari" VALUES(15,19,'15:19','اور زمین کو بھی ہم ہی نے پھیلایا اور اس پر پہاڑ (بنا کر) رکھ دیئے اور اس میں ہر ایک سنجیدہ چیز اُگائی');
INSERT INTO "jalandhari" VALUES(15,20,'15:20','اور ہم ہی نے تمہارے لیے اور ان لوگوں کے لیے جن کو تم روزی نہیں دیتے اس میں معاش کے سامان پیدا کئے');
INSERT INTO "jalandhari" VALUES(15,21,'15:21','اور ہمارے ہاں ہر چیز کے خزانے ہیں اور ہم ان کو بمقدار مناسب اُتارتے رہتے ہیں');
INSERT INTO "jalandhari" VALUES(15,22,'15:22','اور ہم ہی ہوائیں چلاتے ہیں (جو بادلوں کے پانی سے) بھری ہوئی ہوتی ہیں اور ہم ہی آسمان سے مینہ برساتے ہیں اور ہم ہی تم کو اس کا پانی پلاتے ہیں اور تم تو اس کا خرانہ نہیں رکھتے');
INSERT INTO "jalandhari" VALUES(15,23,'15:23','اور ہم ہی حیات بخشتے اور ہم ہی موت دیتے ہیں۔ اور ہم سب کے وارث (مالک) ہیں');
INSERT INTO "jalandhari" VALUES(15,24,'15:24','اور جو لوگ تم میں پہلے گزر چکے ہیں ہم کو معلوم ہیں اور جو پیچھے آنے والے ہیں وہ بھی ہم کو معلوم ہیں');
INSERT INTO "jalandhari" VALUES(15,25,'15:25','اور تمہارا پروردگار (قیامت کے دن) ان سب کو جمع کرے گا وہ بڑا دانا (اور) خبردار ہے');
INSERT INTO "jalandhari" VALUES(15,26,'15:26','اور ہم نے انسان کو کھنکھناتے سڑے ہوئے گارے سے پیدا کیا ہے');
INSERT INTO "jalandhari" VALUES(15,27,'15:27','اور جنوں کو اس سے بھی پہلے بےدھوئیں کی آگ سے پیدا کیا تھا');
INSERT INTO "jalandhari" VALUES(15,28,'15:28','اور جب تمہارے پروردگار نے فرشتوں سے فرمایا کہ میں کھنکھناتے سڑے ہوئے گارے سے ایک بشر بنانے والا ہوں');
INSERT INTO "jalandhari" VALUES(15,29,'15:29','جب اس کو (صورت انسانیہ میں) درست کر لوں اور اس میں اپنی (بےبہا چیز یعنی) روح پھونک دوں تو اس کے آگے سجدے میں گر پڑنا');
INSERT INTO "jalandhari" VALUES(15,30,'15:30','تو فرشتے تو سب کے سب سجدے میں گر پڑے');
INSERT INTO "jalandhari" VALUES(15,31,'15:31','مگر شیطان کہ اس نے سجدہ کرنے والوں کے ساتھ ہونے سے انکار کر دیا');
INSERT INTO "jalandhari" VALUES(15,32,'15:32','(خدا نے فرمایا) کہ ابلیس! تجھے کیا ہوا کہ تو سجدہ کرنے والوں میں شامل نہ ہوا');
INSERT INTO "jalandhari" VALUES(15,33,'15:33','(اس نے) کہا کہ میں ایسا نہیں ہوں کہ انسان کو جس کو تو نے کھنکھناتے سڑے ہوئے گارے سے بنایا ہے سجدہ کروں');
INSERT INTO "jalandhari" VALUES(15,34,'15:34','(خدا نے) فرمایا یہاں سے نکل جا۔ تو مردود ہے');
INSERT INTO "jalandhari" VALUES(15,35,'15:35','اور تجھ پر قیامت کے دن تک لعنت (برسے گی)');
INSERT INTO "jalandhari" VALUES(15,36,'15:36','(اس نے) کہا کہ پروردگار مجھے اس دن تک مہلت دے جب لوگ (مرنے کے بعد) زندہ کئے جائیں گے');
INSERT INTO "jalandhari" VALUES(15,37,'15:37','فرمایا کہ تجھے مہلت دی جاتی ہے');
INSERT INTO "jalandhari" VALUES(15,38,'15:38','وقت مقرر (یعنی قیامت) کے دن تک');
INSERT INTO "jalandhari" VALUES(15,39,'15:39','(اس نے) کہا کہ پروردگار جیسا تونے مجھے رستے سے الگ کیا ہے میں بھی زمین میں لوگوں کے لیے (گناہوں) کو آراستہ کر دکھاؤں گا اور سب کو بہکاؤں گا');
INSERT INTO "jalandhari" VALUES(15,40,'15:40','ہاں ان میں جو تیرے مخلص بندے ہیں (ان پر قابو چلنا مشکل ہے)');
INSERT INTO "jalandhari" VALUES(15,41,'15:41','(خدا نے) فرمایا کہ مجھ تک (پہنچنے کا) یہی سیدھا رستہ ہے');
INSERT INTO "jalandhari" VALUES(15,42,'15:42','جو میرے (مخلص) بندے ہیں ان پر تجھے کچھ قدرت نہیں (کہ ان کو گناہ میں ڈال سکے) ہاں بد راہوں میں سے جو تیرے پیچھے چل پڑے');
INSERT INTO "jalandhari" VALUES(15,43,'15:43','اور ان سب کے وعدے کی جگہ جہنم ہے');
INSERT INTO "jalandhari" VALUES(15,44,'15:44','اس کے سات دروازے ہیں۔ ہر ایک دروازے کے لیے ان میں سے جماعتیں تقسیم کردی گئی ہیں');
INSERT INTO "jalandhari" VALUES(15,45,'15:45','جو متقی ہیں وہ باغوں اور چشموں میں ہوں گے');
INSERT INTO "jalandhari" VALUES(15,46,'15:46','(ان سے کہا جائے گا کہ) ان میں سلامتی (اور خاطر جمع سے) داخل ہوجاؤ');
INSERT INTO "jalandhari" VALUES(15,47,'15:47','اور ان کے دلوں میں جو کدورت ہوگی ان کو ہم نکال کر (صاف کر) دیں گے (گویا) بھائی بھائی تختوں پر ایک دوسرے کے سامنے بیٹھے ہوئے ہیں');
INSERT INTO "jalandhari" VALUES(15,48,'15:48','نہ ان کو وہاں کوئی تکلیف پہنچے گی اور نہ وہاں سے نکالے جائیں گے');
INSERT INTO "jalandhari" VALUES(15,49,'15:49','(اے پیغمبر) میرے بندوں کو بتادو کہ میں بڑا بخشنے والا (اور) مہربان ہوں');
INSERT INTO "jalandhari" VALUES(15,50,'15:50','اور یہ کہ میرا عذاب بھی درد دینے والا عذاب ہے');
INSERT INTO "jalandhari" VALUES(15,51,'15:51','اور ان کو ابراہیم کے مہمانوں کا احوال سنادو');
INSERT INTO "jalandhari" VALUES(15,52,'15:52','جب وہ ابراہیم کے پاس آئے تو سلام کہا۔ (انہوں نے) کہا کہ ہمیں تو تم سے ڈر لگتا ہے');
INSERT INTO "jalandhari" VALUES(15,53,'15:53','(مہمانوں نے) کہا کہ ڈریئے نہیں ہم آپ کو ایک دانشمند لڑکے کی خوشخبری دیتے ہیں');
INSERT INTO "jalandhari" VALUES(15,54,'15:54','بولے کہ جب مجھے بڑھاپے نے آ پکڑا تو تم خوشخبری دینے لگے۔ اب کاہے کی خوشخبری دیتے ہو');
INSERT INTO "jalandhari" VALUES(15,55,'15:55','(انہوں نے) کہا ہم آپ کو سچی خوشخبری دیتے ہیں آپ مایوس نہ ہوئیے');
INSERT INTO "jalandhari" VALUES(15,56,'15:56','(ابراہیم نے) کہا کہ خدا کی رحمت سے (میں مایوس کیوں ہونے لگا اس سے) مایوس ہونا گمراہوں کا کام ہے');
INSERT INTO "jalandhari" VALUES(15,57,'15:57','پھر کہنے لگے کہ فرشتو! تمہیں (اور) کیا کام ہے');
INSERT INTO "jalandhari" VALUES(15,58,'15:58','(انہوں نے) کہا کہ ہم ایک گنہگار قوم کی طرف بھیجے گئے ہیں (کہ اس کو عذاب کریں)');
INSERT INTO "jalandhari" VALUES(15,59,'15:59','مگر لوط کے گھر والے کہ ان سب کو ہم بچالیں گے');
INSERT INTO "jalandhari" VALUES(15,60,'15:60','البتہ ان کی عورت (کہ) اس کے لیے ہم نے ٹھہرا دیا ہے کہ وہ پیچھے رہ جائے گی');
INSERT INTO "jalandhari" VALUES(15,61,'15:61','پھر جب فرشتے لوط کے گھر گئے');
INSERT INTO "jalandhari" VALUES(15,62,'15:62','تو لوط نے کہا تم تو ناآشنا سے لوگ ہو');
INSERT INTO "jalandhari" VALUES(15,63,'15:63','وہ بولے کہ نہیں بلکہ ہم آپ کے پاس وہ چیز لے کر آئے ہیں جس میں لوگ شک کرتے تھے');
INSERT INTO "jalandhari" VALUES(15,64,'15:64','اور ہم آپ کے پاس یقینی بات لے کر آئے ہیں اور ہم سچ کہتے ہیں');
INSERT INTO "jalandhari" VALUES(15,65,'15:65','تو آپ کچھ رات رہے سے اپنے گھر والوں کو لے نکلیں اور خود ان کے پیچھے چلیں اور اور آپ میں سے کوئی شخص مڑ کر نہ دیکھے۔ اور جہاں آپ کو حکم ہو وہاں چلے جایئے');
INSERT INTO "jalandhari" VALUES(15,66,'15:66','اور ہم نے لوط کی طرف وحی بھیجی کہ ان لوگوں کی جڑ صبح ہوتے ہوتے کاٹ دی جائے گی');
INSERT INTO "jalandhari" VALUES(15,67,'15:67','اور اہل شہر (لوط کے پاس) خوش خوش (دوڑے) آئے');
INSERT INTO "jalandhari" VALUES(15,68,'15:68','(لوط نے) کہا کہ یہ میرے مہمان ہیں (کہیں ان کے بارے میں) مجھے رسوا نہ کرنا');
INSERT INTO "jalandhari" VALUES(15,69,'15:69','اور خدا سے ڈرو۔ اور میری بےآبروئی نہ کیجو');
INSERT INTO "jalandhari" VALUES(15,70,'15:70','وہ بولے کیا ہم نے تم کو سارے جہان (کی حمایت وطرفداری) سے منع نہیں کیا');
INSERT INTO "jalandhari" VALUES(15,71,'15:71','(انہوں نے) کہا کہ اگر تمہیں کرنا ہی ہے تو یہ میری (قوم کی) لڑکیاں ہیں (ان سے شادی کرلو)');
INSERT INTO "jalandhari" VALUES(15,72,'15:72','(اے محمد) تمہاری جان کی قسم وہ اپنی مستی میں مدہوش (ہو رہے) تھے');
INSERT INTO "jalandhari" VALUES(15,73,'15:73','سو ان کو سورج نکلتے نکلتے چنگھاڑ نے آپکڑا');
INSERT INTO "jalandhari" VALUES(15,74,'15:74','اور ہم نے اس شہر کو (الٹ کر) نیچے اوپر کردیا۔ اور ان پر کھنگر کی پتھریاں برسائیں');
INSERT INTO "jalandhari" VALUES(15,75,'15:75','بےشک اس (قصے) میں اہل فراست کے لیے نشانی ہے');
INSERT INTO "jalandhari" VALUES(15,76,'15:76','اور وہ (شہر) اب تک سیدھے رستے پر (موجود) ہے');
INSERT INTO "jalandhari" VALUES(15,77,'15:77','بےشک اس میں ایمان لانے والوں کے لیے نشانی ہے');
INSERT INTO "jalandhari" VALUES(15,78,'15:78','اور بَن کے رہنے والے (یعنی قوم شعیب کے لوگ) بھی گنہگار تھے');
INSERT INTO "jalandhari" VALUES(15,79,'15:79','تو ہم نے ان سے بھی بدلہ لیا۔ اور یہ دونوں شہر کھلے رستے پر (موجود) ہیں');
INSERT INTO "jalandhari" VALUES(15,80,'15:80','اور (وادی) حجر کے رہنے والوں نے بھی پیغمبروں کی تکذیب کی');
INSERT INTO "jalandhari" VALUES(15,81,'15:81','ہم نے ان کو اپنی نشانیاں دیں اور وہ ان سے منہ پھرتے رہے');
INSERT INTO "jalandhari" VALUES(15,82,'15:82','اور وہ پہاڑوں کو تراش تراش کر گھر بناتے تھے (کہ) امن (واطمینان) سے رہیں گے');
INSERT INTO "jalandhari" VALUES(15,83,'15:83','تو چیخ نے ان کو صبح ہوتے ہوتے آپکڑا');
INSERT INTO "jalandhari" VALUES(15,84,'15:84','اور جو کام وہ کرتے تھے وہ ان کے کچھ بھی کام نہ آئے');
INSERT INTO "jalandhari" VALUES(15,85,'15:85','اور ہم نے آسمانوں اور زمین کو اور جو (مخلوقات) ان میں ہے اس کو تدبیر کے ساتھ پیدا کیا ہے۔ اور قیامت تو ضرور آکر رہے گی تو تم (ان لوگوں سے) اچھی طرح سے درگزر کرو');
INSERT INTO "jalandhari" VALUES(15,86,'15:86','کچھ شک نہیں کہ تمہارا پروردگار (سب کچھ) پیدا کرنے والا (اور) جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(15,87,'15:87','اور ہم نے تم کو سات (آیتیں) جو (نماز میں) دہرا کر پڑھی جاتی ہیں (یعنی سورہٴ الحمد) اور عظمت والا قرآن عطا فرمایا ہے');
INSERT INTO "jalandhari" VALUES(15,88,'15:88','اور ہم نے کفار کی کئی جماعتوں کو جو (فوائد دنیاوی سے) متمتع کیا ہے تم ان کی طرف (رغبت سے) آنکھ اٹھا کر نہ دیکھنا اور نہ ان کے حال پر تاسف کرنا اور مومنوں سے خاطر اور تواضع سے پیش آنا');
INSERT INTO "jalandhari" VALUES(15,89,'15:89','اور کہہ دو کہ میں تو علانیہ ڈر سنانے والا ہوں');
INSERT INTO "jalandhari" VALUES(15,90,'15:90','(اور ہم ان کفار پر اسی طرح عذاب نازل کریں گے) جس طرح ان لوگوں پر نازل کیا جنہوں نے تقسیم کردیا');
INSERT INTO "jalandhari" VALUES(15,91,'15:91','یعنی قرآن کو (کچھ ماننے اور کچھ نہ ماننے سے) ٹکڑے ٹکڑے کر ڈالا');
INSERT INTO "jalandhari" VALUES(15,92,'15:92','تمہارے پروردگار کی قسم ہم ان سے ضرور پرسش کریں گے');
INSERT INTO "jalandhari" VALUES(15,93,'15:93','ان کاموں کی جو وہ کرتے رہے');
INSERT INTO "jalandhari" VALUES(15,94,'15:94','پس جو حکم تم کو (خدا کی طرف سے) ملا ہے وہ (لوگوں کو) سنا دو اور مشرکوں کا (ذرا) خیال نہ کرو');
INSERT INTO "jalandhari" VALUES(15,95,'15:95','ہم تمہیں ان لوگوں (کے شر) سے بچانے کے لیے جو تم سے استہزاء کرتے ہیں کافی ہیں');
INSERT INTO "jalandhari" VALUES(15,96,'15:96','جو خدا کے ساتھ معبود قرار دیتے ہیں۔ سو عنقریب ان کو (ان باتوں کا انجام) معلوم ہوجائے گا');
INSERT INTO "jalandhari" VALUES(15,97,'15:97','اور ہم جانتے ہیں کہ ان باتوں سے تمہارا دل تنگ ہوتا ہے');
INSERT INTO "jalandhari" VALUES(15,98,'15:98','تو تم اپنے پروردگار کی تسبیح کہتے اور (اس کی) خوبیاں بیان کرتے رہو اور سجدہ کرنے والوں میں داخل رہو');
INSERT INTO "jalandhari" VALUES(15,99,'15:99','اور اپنے پروردگار کی عبادت کئے جاؤ یہاں تک کہ تمہاری موت (کا وقت) آجائے');
INSERT INTO "jalandhari" VALUES(16,1,'16:1','خدا کا حکم (یعنی عذاب گویا) آ ہی پہنچا تو (کافرو) اس کے لیے جلدی مت کرو۔ یہ لوگ جو (خدا کا) شریک بناتے ہیں وہ اس سے پاک اور بالاتر ہے');
INSERT INTO "jalandhari" VALUES(16,2,'16:2','وہی فرشتوں کو پیغام دے کر اپنے حکم سے اپنے بندوں میں سے جس کے پاس چاہتا ہے بھیجتا ہے کہ (لوگوں کو) بتادو کہ میرے سوا کوئی معبود نہیں تو مجھی سے ڈرو');
INSERT INTO "jalandhari" VALUES(16,3,'16:3','اسی نے آسمانوں اور زمین کو مبنی برحکمت پیدا کیا۔ اس کی ذات ان (کافروں) کے شرک سے اونچی ہے');
INSERT INTO "jalandhari" VALUES(16,4,'16:4','اسی نے انسان کو نطفے سے بنایا مگر وہ اس (خالق) کے بارے میں علانیہ جھگڑنے لگا');
INSERT INTO "jalandhari" VALUES(16,5,'16:5','اور چارپایوں کو بھی اسی نے پیدا کیا۔ ان میں تمہارے لیے جڑاول اور بہت سے فائدے ہیں اور ان میں سے بعض کو تم کھاتے بھی ہو');
INSERT INTO "jalandhari" VALUES(16,6,'16:6','اور جب شام کو انہیں (جنگل سے) لاتے ہو اور جب صبح کو (جنگل) چرانے لے جاتے ہو تو ان سے تمہاری عزت وشان ہے');
INSERT INTO "jalandhari" VALUES(16,7,'16:7','اور (دور دراز) شہروں میں جہاں تم زحمتِ شاقّہ کے بغیر پہنچ نہیں سکتے وہ تمہارے بوجھ اٹھا کر لے جاتے ہیں۔ کچھ شک نہیں کہ تمہارا پروردگار نہایت شفقت والا اور مہربان ہے');
INSERT INTO "jalandhari" VALUES(16,8,'16:8','اور اسی نے گھوڑے اور خچر اور گدھے پیدا کئے تاکہ تم ان پر سوار ہو اور (وہ تمہارے لیے) رونق وزینت (بھی ہیں) اور وہ (اور چیزیں بھی) پیدا کرتا ہے جن کی تم کو خبر نہیں');
INSERT INTO "jalandhari" VALUES(16,9,'16:9','اور سیدھا رستہ تو خدا تک جا پہنچتا ہے۔ اور بعض رستے ٹیڑھے ہیں (وہ اس تک نہیں پہنچتے) اور اگر وہ چاہتا تو تم سب کو سیدھے رستے پر چلا دیتا');
INSERT INTO "jalandhari" VALUES(16,10,'16:10','وہی تو ہے جس نے آسمان سے پانی برسایا جسے تم پیتے ہو اور اس سے درخت بھی (شاداب ہوتے ہیں) جن میں تم اپنے چارپایوں کو چراتے ہو');
INSERT INTO "jalandhari" VALUES(16,11,'16:11','اسی پانی سے وہ تمہارے لیے کھیتی اور زیتون اور کھجور اور انگور (اور بےشمار درخت) اُگاتا ہے۔ اور ہر طرح کے پھل (پیدا کرتا ہے) غور کرنے والوں کے لیے اس میں (قدرتِ خدا کی بڑی) نشانی ہے');
INSERT INTO "jalandhari" VALUES(16,12,'16:12','اور اسی نے تمہارے لیے رات اور دن اور سورج اور چاند کو کام میں لگایا۔ اور اسی کے حکم سے ستارے بھی کام میں لگے ہوئے ہیں۔ سمجھنے والوں کے لیے اس میں (قدرت خدا کی بہت سی) نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(16,13,'16:13','اور جو طرح طرح کے رنگوں کی چیزیں اس نے زمین میں پیدا کیں (سب تمہارے زیر فرمان کردیں) نصیحت پکڑنے والوں کے لیے اس میں نشانی ہے');
INSERT INTO "jalandhari" VALUES(16,14,'16:14','اور وہی تو ہے جس نے دریا کو تمہارے اختیار میں کیا تاکہ اس میں سے تازہ گوشت کھاؤ اور اس سے زیور (موتی وغیرہ) نکالو جسے تم پہنتے ہو۔ اور تم دیکھتے ہو کہ کشتیاں دریا میں پانی کو پھاڑتی چلی جاتی ہیں۔ اور اس لیے بھی (دریا کو تمہارے اختیار میں کیا) کہ تم خدا کے فضل سے (معاش) تلاش کرو تاکہ اس کا شکر کرو');
INSERT INTO "jalandhari" VALUES(16,15,'16:15','اور اسی نے زمین پر پہاڑ (بنا کر) رکھ دیئے کہ تم کو لے کر کہیں جھک نہ جائے اور نہریں اور رستے بنا دیئے تاکہ ایک مقام سے دوسرے مقام تک (آسانی سے) جاسکو');
INSERT INTO "jalandhari" VALUES(16,16,'16:16','اور (راستوں میں) نشانات بنا دیئے اور لوگ ستاروں سے بھی رستے معلوم کرتے ہیں');
INSERT INTO "jalandhari" VALUES(16,17,'16:17','تو جو (اتنی مخلوقات) پیدا کرے۔ کیا وہ ویسا ہے جو کچھ بھی پیدا نہ کرسکے تو پھر تم غور کیوں نہیں کرتے؟');
INSERT INTO "jalandhari" VALUES(16,18,'16:18','اور اگر تم خدا کی نعمتوں کو شمار کرنا چاہو تو گن نہ سکو۔ بےشک خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(16,19,'16:19','اور جو کچھ تم چھپاتے اور جو کچھ ظاہر کرتے ہو سب سے خدا واقف ہے');
INSERT INTO "jalandhari" VALUES(16,20,'16:20','اور جن لوگوں کو یہ خدا کے سوا پکارتے ہیں وہ کوئی چیز بھی تو نہیں بناسکتے بلکہ خود ان کو اور بناتے ہیں');
INSERT INTO "jalandhari" VALUES(16,21,'16:21','لاشیں ہیں بےجان۔ ان کو یہ بھی تو معلوم نہیں کہ اٹھائے کب جائیں گے');
INSERT INTO "jalandhari" VALUES(16,22,'16:22','تمہارا معبود تو اکیلا خدا ہے۔ تو جو آخرت پر ایمان نہیں رکھتے ان کے دل انکار کر رہے ہیں اور وہ سرکش ہو رہے ہیں');
INSERT INTO "jalandhari" VALUES(16,23,'16:23','یہ جو کچھ چھپاتے ہیں اور جو ظاہر کرتے ہیں خدا اس کو ضرور جانتا ہے۔ وہ سرکشوں کو ہرگز پسند نہیں کرتا');
INSERT INTO "jalandhari" VALUES(16,24,'16:24','اور جب ان (کافروں) سے کہا جاتا ہے کہ تمہارے پروردگار نے کیا اتارا ہے تو کہتے ہیں کہ (وہ تو) پہلے لوگوں کی حکایتیں ہیں');
INSERT INTO "jalandhari" VALUES(16,25,'16:25','(اے پیغمبر ان کو بکنے دو) یہ قیامت کے دن اپنے (اعمال کے) پورے بوجھ بھی اٹھائیں گے اور جن کو یہ بےتحقیق گمراہ کرتے ہیں ان کے بوجھ بھی اٹھائیں گے۔ سن رکھو کہ جو بوجھ اٹھا رہے ہیں برے ہیں');
INSERT INTO "jalandhari" VALUES(16,26,'16:26','ان سے پہلے لوگوں نے بھی (ایسی ہی) مکاریاں کی تھیں تو خدا (کا حکم) ان کی عمارت کے ستونوں پر آپہنچا اور چھت ان پر ان کے اوپر سے گر پڑی اور (ایسی طرف سے) ان پر عذاب آ واقع ہوا جہاں سے ان کو خیال بھی نہ تھا');
INSERT INTO "jalandhari" VALUES(16,27,'16:27','پھر وہ ان کو قیامت کے دن بھی ذلیل کرے گا اور کہے گا کہ میرے وہ شریک کہاں ہیں جن کے بارے میں تم جھگڑا کرتے تھے۔ جن لوگوں کو علم دیا گیا تھا وہ کہیں گے کہ آج کافروں کی رسوائی اور برائی ہے');
INSERT INTO "jalandhari" VALUES(16,28,'16:28','(ان کا حال یہ ہے کہ) جب فرشتے ان کی روحیں قبض کرنے لگتے ہیں (اور یہ) اپنے ہی حق میں ظلم کرنے والے (ہوتے ہیں) تو مطیع ومنقاد ہوجاتے ہیں (اور کہتے ہیں) کہ ہم کوئی برا کام نہیں کرتے تھے۔ ہاں جو کچھ تم کیا کرتے تھے خدا اسے خوب جانتا ہے');
INSERT INTO "jalandhari" VALUES(16,29,'16:29','سو دوزخ کے دروازوں میں داخل ہوجاؤ۔ ہمیشہ اس میں رہو گے۔ اب تکبر کرنے والوں کا برا ٹھکانا ہے');
INSERT INTO "jalandhari" VALUES(16,30,'16:30','اور (جب) پرہیزگاروں سے پوچھا جاتا ہے کہ تمہارے پروردگار نے کیا نازل کیا ہے۔ تو کہتے ہیں کہ بہترین (کلام)۔ جو لوگ نیکوکار ہیں ان کے لیے اس دنیا میں بھلائی ہے۔ اور آخرت کا گھر تو بہت ہی اچھا ہے۔ اور پرہیز گاروں کا گھر بہت خوب ہے');
INSERT INTO "jalandhari" VALUES(16,31,'16:31','(وہ) بہشت جاودانی (ہیں) جن میں وہ داخل ہوں گے ان کے نیچے نہریں بہہ رہی ہیں وہاں جو چاہیں گے ان کے لیے میسر ہوگا۔ خدا پرہیزگاروں کو ایسا ہی بدلہ دیتا ہے');
INSERT INTO "jalandhari" VALUES(16,32,'16:32','(ان کی کیفیت یہ ہے کہ) جب فرشتے ان کی جانیں نکالنے لگتے ہیں اور یہ (کفر وشرک سے) پاک ہوتے ہیں تو سلام علیکم کہتے ہیں (اور کہتے ہیں کہ) جو عمل تم کیا کرتے تھے ان کے بدلے میں بہشت میں داخل ہوجاؤ');
INSERT INTO "jalandhari" VALUES(16,33,'16:33','کیا یہ (کافر) اس بات کے منتظر ہیں کہ فرشتے ان کے پاس (جان نکالنے) آئیں یا تمہارے پروردگار کا حکم (عذاب) آپہنچے۔ اسی طرح اُن لوگوں نے کیا تھا جو اُن سے پہلے تھے اور خدا نے اُن پر ظلم نہیں کیا بلکہ وہ خود اپنے آپ پر ظلم کرتے تھے');
INSERT INTO "jalandhari" VALUES(16,34,'16:34','تو ان کو ان کے اعمال کے برے بدلے ملے اور جس چیز کے ساتھ وہ ٹھٹھے کیا کرتے تھے اس نے ان کو (ہر طرف سے) گھیر لیا');
INSERT INTO "jalandhari" VALUES(16,35,'16:35','اور مشرک کہتے ہیں کہ اگر خدا چاہتا تو نہ ہم ہی اس کے سوا کسی چیز کو پوجتے اور نہ ہمارے بڑے ہی (پوجتے) اور نہ اس کے (فرمان کے) بغیر ہم کسی چیز کو حرام ٹھہراتے۔ (اے پیغمبر) اسی طرح ان سے اگلے لوگوں نے کیا تھا۔ تو پیغمبروں کے ذمے (خدا کے احکام کو) کھول کر سنا دینے کے سوا اور کچھ نہیں');
INSERT INTO "jalandhari" VALUES(16,36,'16:36','اور ہم نے ہر جماعت میں پیغمبر بھیجا کہ خدا ہی کی عبادت کرو اور بتوں (کی پرستش) سے اجتناب کرو۔ تو ان میں بعض ایسے ہیں جن کو خدا نے ہدایت دی اور بعض ایسے ہیں جن پر گمراہی ثابت ہوئی۔ سو زمین پر چل پھر کر دیکھ لو کہ جھٹلانے والوں کا انجام کیسا ہوا');
INSERT INTO "jalandhari" VALUES(16,37,'16:37','اگر تم ان (کفار) کی ہدایت کے لیے للچاؤ تو جس کو خدا گمراہ کردیتا ہے اس کو وہ ہدایت نہیں دیا کرتا اور ایسے لوگوں کا کوئی مددگار بھی نہیں ہوتا');
INSERT INTO "jalandhari" VALUES(16,38,'16:38','اور یہ خدا کی سخت سخت قسمیں کھاتے ہیں کہ جو مرجاتا ہے خدا اسے (قیامت کے دن قبر سے) نہیں اٹھائے گا۔ ہرگز نہیں۔ یہ (خدا کا) وعدہ سچا ہے اور اس کا پورا کرنا اسے ضرور ہے لیکن اکثر لوگ نہیں جانتے');
INSERT INTO "jalandhari" VALUES(16,39,'16:39','تاکہ جن باتوں میں یہ اختلاف کرتے ہیں وہ ان پر ظاہر کردے اور اس لیے کہ کافر جان لیں کہ وہ جھوٹے تھے');
INSERT INTO "jalandhari" VALUES(16,40,'16:40','جب ہم کسی چیز کا ارادہ کرتے ہیں تو ہماری بات یہی ہے کہ اس کو کہہ دیتے ہیں کہ ہوجا تو وہ ہوجاتی ہے');
INSERT INTO "jalandhari" VALUES(16,41,'16:41','اور جن لوگوں نے ظلم سہنے کے بعد خدا کے لیے وطن چھوڑا ہم ان کو دنیا میں اچھا ٹھکانا دیں گے۔ اور آخرت کا اجر تو بہت بڑا ہے۔ کاش وہ (اسے) جانتے');
INSERT INTO "jalandhari" VALUES(16,42,'16:42','یعنی وہ لوگ جو صبر کرتے ہیں اور اپنے پروردگار پر بھروسا رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(16,43,'16:43','اور ہم نے تم سے پہلے مردوں ہی کو پیغمبر بنا کر بھیجا تھا جن کی طرف ہم وحی بھیجا کرتے تھے اگر تم لوگ نہیں جانتے تو اہل کتاب سے پوچھ لو');
INSERT INTO "jalandhari" VALUES(16,44,'16:44','(اور ان پیغمبروں کو) دلیلیں اور کتابیں دے کر (بھیجا تھا) اور ہم نے تم پر بھی یہ کتاب نازل کی ہے تاکہ جو (ارشادات) لوگوں پر نازل ہوئے ہیں وہ ان پر ظاہر کردو اور تاکہ وہ غور کریں');
INSERT INTO "jalandhari" VALUES(16,45,'16:45','کیا جو لوگ بری بری چالیں چلتے ہیں اس بات سے بےخوف ہیں کہ خدا ان کو زمین میں دھنسا دے یا (ایسی طرف سے) ان پر عذاب آجائے جہاں سے ان کو خبر ہی نہ ہو');
INSERT INTO "jalandhari" VALUES(16,46,'16:46','یا ان کو چلتے پھرتے پکڑ لے وہ (خدا کو) عاجز نہیں کرسکتے');
INSERT INTO "jalandhari" VALUES(16,47,'16:47','یا جب ان کو عذاب کا ڈر پیدا ہوگیا ہو تو ان کو پکڑلے۔ بےشک تمہارا پروردگار بہت شفقت کرنے والا اور مہربان ہے');
INSERT INTO "jalandhari" VALUES(16,48,'16:48','کیا ان لوگوں نے خدا کی مخلوقات میں سے ایسی چیزیں نہیں دیکھیں جن کے سائے دائیں سے (بائیں کو) اور بائیں سے (دائیں کو) لوٹتے رہتے ہیں (یعنی) خدا کے آگے عاجز ہو کر سجدے میں پڑے رہتے ہیں');
INSERT INTO "jalandhari" VALUES(16,49,'16:49','اور تمام جاندار جو آسمانوں میں ہیں اور جو زمین میں ہیں سب خدا کے آگے سجدہ کرتے ہیں اور فرشتے بھی اور وہ ذرا غرور نہیں کرتے');
INSERT INTO "jalandhari" VALUES(16,50,'16:50','اور اپنے پروردگار سے جو ان کے اوپر ہے ڈرتے ہیں اور جو ان کو ارشاد ہوتا ہے اس پر عمل کرتے ہیں');
INSERT INTO "jalandhari" VALUES(16,51,'16:51','اور خدا نے فرمایا ہے کہ دو دو معبود نہ بناؤ۔ معبود وہی ایک ہے۔ تو مجھ ہی سے ڈرتے رہو');
INSERT INTO "jalandhari" VALUES(16,52,'16:52','اور جو کچھ آسمانوں میں اور جو کچھ زمین میں ہے سب اسی کا ہے اور اسی کی عبادت لازم ہے۔ تو تم خدا کے سوا اوروں سے کیوں ڈرتے ہو');
INSERT INTO "jalandhari" VALUES(16,53,'16:53','اور جو نعمتیں تم کو میسر ہیں سب خدا کی طرف سے ہیں۔ پھر جب تم کو کوئی تکلیف پہنچتی ہے تو اسی کے آگے چلاتے ہو');
INSERT INTO "jalandhari" VALUES(16,54,'16:54','پھر جب وہ تم سے تکلیف کو دور کردیتا ہے تو کچھ لوگ تم میں سے خدا کے ساتھ شریک کرنے لگتے ہیں');
INSERT INTO "jalandhari" VALUES(16,55,'16:55','تاکہ جو (نعمتیں) ہم نے ان کو عطا فرمائی ہیں ان کی ناشکری کریں تو (مشرکو) دنیا میں فائدے اٹھالو۔ عنقریب تم کو (اس کا انجام) معلوم ہوجائے گا');
INSERT INTO "jalandhari" VALUES(16,56,'16:56','اور ہمارے دیئے ہوئے مال میں سے ایسی چیزوں کا حصہ مقرر کرتے ہیں جن کو جانتے ہی نہیں۔ (کافرو) خدا کی قسم کہ جو تم افتراء کرتے ہو اس کی تم سے ضرور پرسش ہوگی');
INSERT INTO "jalandhari" VALUES(16,57,'16:57','اور یہ لوگ خدا کے لیے تو بیٹیاں تجویز کرتے ہیں۔ (اور) وہ ان سے پاک ہے اور اپنے لیے (بیٹے) جو مرغوب ودلپسند ہیں');
INSERT INTO "jalandhari" VALUES(16,58,'16:58','حالانکہ جب ان میں سے کسی کو بیٹی (کے پیدا ہونے) کی خبر ملتی ہے تو اس کا منہ (غم کے سبب) کالا پڑ جاتا ہے اور (اس کے دل کو دیکھو تو) وہ اندوہناک ہوجاتا ہے');
INSERT INTO "jalandhari" VALUES(16,59,'16:59','اور اس خبر بد سے (جو وہ سنتا ہے) لوگوں سے چھپتا پھرتا ہے (اور سوچتا ہے) کہ آیا ذلت برداشت کرکے لڑکی کو زندہ رہنے دے یا زمین میں گاڑ دے۔ دیکھو یہ جو تجویز کرتے ہیں بہت بری ہے');
INSERT INTO "jalandhari" VALUES(16,60,'16:60','جو لوگ آخرت پر ایمان نہیں رکھتے ان ہی کے لیے بری باتیں (شایان) ہیں۔ اور خدا کو صفت اعلیٰ (زیب دیتی ہے) اور وہ غالب حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(16,61,'16:61','اور اگر خدا لوگوں کو ان کے ظلم کے سبب پکڑنے لگے تو ایک جاندار کو زمین پر نہ چھوڑے لیکن ان کو ایک وقت مقرر تک مہلت دیئے جاتا ہے۔ جب وہ وقت آجاتا ہے تو ایک گھڑی نہ پیچھے رہ سکتے ہیں نہ آگے بڑھ سکتے ہیں');
INSERT INTO "jalandhari" VALUES(16,62,'16:62','اور یہ خدا کے لیے ایسی چیزیں تجویز کرتے ہیں جن کو خود ناپسند کرتے ہیں اور زبان سے جھوٹ بکے جاتے ہیں کہ ان کو (قیامت کے دن) بھلائی (یعنی نجات) ہوگی۔ کچھ شک نہیں کہ ان کے لیے (دوزخ کی) آگ (تیار) ہے اور یہ (دوزخ میں) سب سے آگے بھیجے جائیں گے');
INSERT INTO "jalandhari" VALUES(16,63,'16:63','خدا کی قسم ہم نے تم سے پہلی امتوں کی طرف بھی پیغمبر بھیجے تو شیطان نے ان کے کردار (ناشائستہ) ان کو آراستہ کر دکھائے تو آج بھی وہی ان کا دوست ہے اور ان کے لیے عذاب الیم ہے');
INSERT INTO "jalandhari" VALUES(16,64,'16:64','اور ہم نے جو تم پر کتاب نازل کی ہے تو اس کے لیے جس امر میں ان لوگوں کو اختلاف ہے تم اس کا فیصلہ کردو۔ اور (یہ) مومنوں کے لیے ہدایت اور رحمت ہے');
INSERT INTO "jalandhari" VALUES(16,65,'16:65','اور خدا ہی نے آسمان سے پانی برسایا پھر اس سے زمین کو اس کے مرنے کے بعد زندہ کیا۔ بےشک اس میں سننے والوں کے لیے نشانی ہے');
INSERT INTO "jalandhari" VALUES(16,66,'16:66','اور تمہارے لیے چارپایوں میں بھی (مقام) عبرت (وغور) ہے کہ ان کے پیٹوں میں جو گوبر اور لہو ہے اس سے ہم تم کو خالص دودھ پلاتے ہیں جو پینے والوں کے لیے خوشگوار ہے');
INSERT INTO "jalandhari" VALUES(16,67,'16:67','اور کھجور اور انگور کے میووں سے بھی (تم پینے کی چیزیں تیار کرتے ہو کہ ان سے شراب بناتے ہو) اور عمدہ رزق (کھاتے ہو) جو لوگ سمجھ رکھتے ہیں ان کے لیے ان (چیزوں) میں (قدرت خدا کی) نشانی ہے');
INSERT INTO "jalandhari" VALUES(16,68,'16:68','اور تمہارے پروردگار نے شہد کی مکھیوں کو ارشاد فرمایا کہ پہاڑوں میں اور درختوں میں اور اونچی اونچی چھتریوں میں جو لوگ بناتے ہیں گھر بنا');
INSERT INTO "jalandhari" VALUES(16,69,'16:69','اور ہر قسم کے میوے کھا۔ اور اپنے پروردگار کے صاف رستوں پر چلی جا۔ اس کے پیٹ سے پینے کی چیز نکلتی ہے جس کے مختلف رنگ ہوتے ہیں اس میں لوگوں (کے کئی امراض) کی شفا ہے۔ بےشک سوچنے والوں کے لیے اس میں بھی نشانی ہے');
INSERT INTO "jalandhari" VALUES(16,70,'16:70','اور خدا ہی نے تم کو پیدا کیا۔ پھر وہی تم کو موت دیتا ہے اور تم میں بعض ایسے ہوتے ہیں کہ نہایت خراب عمر کو پہنچ جاتے ہیں اور (بہت کچھ) جاننے کے بعد ہر چیز سے بےعلم ہوجاتے ہیں۔ بےشک خدا (سب کچھ) جاننے والا (اور) قدرت والا ہے');
INSERT INTO "jalandhari" VALUES(16,71,'16:71','اور خدا نے رزق (ودولت) میں بعض کو بعض پر فضیلت دی ہے تو جن لوگوں کو فضیلت دی ہے وہ اپنا رزق اپنے مملوکوں کو تو دے ڈالنے والے ہیں نہیں کہ سب اس میں برابر ہوجائیں۔ تو کیا یہ لوگ نعمت الہیٰ کے منکر ہیں');
INSERT INTO "jalandhari" VALUES(16,72,'16:72','اور خدا ہی نے تم میں سے تمہارے لیے عورتیں پیدا کیں اور عورتوں سے تمہارے بیٹے اور پوتے پیدا کیے اور کھانے کو تمہیں پاکیزہ چیزیں دیں۔ تو کیا بےاصل چیزوں پر اعتقاد رکھتے اور خدا کی نعمتوں سے انکار کرتے ہیں');
INSERT INTO "jalandhari" VALUES(16,73,'16:73','اور خدا کے سوا ایسوں کو پوجتے ہیں جو ان کو آسمانوں اور زمین میں روزی دینے کا ذرا بھی اختیار نہیں رکھتے اور نہ کسی اور طرح کا مقدور رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(16,74,'16:74','تو (لوگو) خدا کے بارے میں (غلط) مثالیں نہ بناؤ۔ (صحیح مثالوں کا طریقہ) خدا ہی جانتا ہے اور تم نہیں جانتے');
INSERT INTO "jalandhari" VALUES(16,75,'16:75','خدا ایک اور مثال بیان فرماتا ہے کہ ایک غلام ہے جو (بالکل) دوسرے کے اختیار میں ہے اور کسی چیز پر قدرت نہیں رکھتا اور ایک ایسا شخص ہے جس کو ہم نے اپنے ہاں سے (بہت سا) مال طیب عطا فرمایا ہے اور وہ اس میں سے (رات دن) پوشیدہ اور ظاہر خرچ کرتا رہتا ہے تو کیا یہ دونوں شخص برابر ہیں؟ (ہرگز نہیں) الحمدلله لیکن ان میں سے اکثر لوگ نہیں سمجھ رکھتے');
INSERT INTO "jalandhari" VALUES(16,76,'16:76','اور خدا ایک اور مثال بیان فرماتا ہے کہ دو آدمی ہیں ایک اُن میں سے گونگا (اور دوسرے کی ملک) ہے (بےاختیار وناتوان) کہ کسی چیز پر قدرت نہیں رکھتا۔ اور اپنے مالک کو دوبھر ہو رہا ہے وہ جہاں اُسے بھیجتا ہے (خیر سے کبھی) بھلائی نہیں لاتا۔ کیا ایسا (گونگا بہرا) اور وہ شخص جو (سنتا بولتا اور) لوگوں کو انصاف کرنے کا حکم دیتا ہے اور خود سیدھے راستے پر چل رہا ہے دونوں برابر ہیں؟');
INSERT INTO "jalandhari" VALUES(16,77,'16:77','اور آسمانوں اور زمین کا علم خدا ہی کو ہے اور (خدا کے نزدیک) قیامت کا آنا یوں ہے جیسے آنکھ کا جھپکنا بلکہ اس سے بھی جلد تر۔ کچھ شک نہیں کہ خدا ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(16,78,'16:78','اور خدا ہی نے تم کو تمہاری ماؤں کے شکم سے پیدا کیا کہ تم کچھ نہیں جانتے تھے۔ اور اس نے تم کو کان اور آنکھیں اور دل (اور اُن کے علاوہ اور) اعضا بخشے تاکہ تم شکر کرو');
INSERT INTO "jalandhari" VALUES(16,79,'16:79','کیا ان لوگوں نے پرندوں کو نہیں دیکھا کہ آسمان کی ہوا میں گھرے ہوئے (اُڑتے رہتے) ہیں۔ ان کو خدا ہی تھامے رکھتا ہے۔ ایمان والوں کے لیے اس میں (بہت سی) نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(16,80,'16:80','اور خدا ہی نے تمہارے لیے گھروں کو رہنے کی جگہ بنایا اور اُسی نے چوپایوں کی کھالوں سے تمہارے لیے ڈیرے بنائے۔ جن کو تم سبک دیکھ کر سفر اور حضر میں کام میں لاتے ہو اور اُن کی اون، پشم اور بالوں سے تم اسباب اور برتنے کی چیزیں (بناتے ہو جو) مدت تک (کام دیتی ہیں)');
INSERT INTO "jalandhari" VALUES(16,81,'16:81','اور خدا ہی نے تمہارے (آرام کے) لیے اپنی پیدا کی ہوئی چیزوں کے سائے بنائے اور پہاڑوں میں غاریں بنائیں اور کُرتے بنائے جو تم کو گرمی سے بچائیں۔ اور (ایسے) کُرتے (بھی) جو تم کو اسلحہ جنگ (کے ضرر) سے محفوظ رکھیں۔ اسی طرح خدا اپنا احسان تم پر پورا کرتا ہے تاکہ تم فرمانبردار بنو');
INSERT INTO "jalandhari" VALUES(16,82,'16:82','اور اگر یہ لوگ اعتراض کریں تو (اے پیغمبر) تمہارا کام فقط کھول کر سنا دینا ہے');
INSERT INTO "jalandhari" VALUES(16,83,'16:83','یہ خدا کی نعمتوں سے واقف ہیں۔ مگر (واقف ہو کر) اُن سے انکار کرتے ہیں اور یہ اکثر ناشکرے ہیں');
INSERT INTO "jalandhari" VALUES(16,84,'16:84','اور جس دن ہم ہر اُمت میں سے گواہ (یعنی پیغمبر) کھڑا کریں گے تو نہ تو کفار کو (بولنے کی) اجازت ملے گی اور نہ اُن کے عذر قبول کئے جائیں گے');
INSERT INTO "jalandhari" VALUES(16,85,'16:85','اور جب ظالم لوگ عذاب دیکھ لیں گے تو پھر نہ تو اُن کے عذاب ہی میں تخفیف کی جائے گی اور نہ اُن کو مہلت ہی دی جائے گی');
INSERT INTO "jalandhari" VALUES(16,86,'16:86','اور جب مشرک (اپنے بنائے ہوئے) شریکوں کو دیکھیں گے تو کہیں گے کہ پروردگار یہ وہی ہمارے شریک ہیں جن کو ہم تیرے سوا پُکارا کرتے تھے۔ تو وہ (اُن کے کلام کو مسترد کردیں گے اور) اُن سے کہیں گے کہ تم تو جھوٹے ہو');
INSERT INTO "jalandhari" VALUES(16,87,'16:87','اور اس دن خدا کے سامنے سرنگوں ہو جائیں گے اور جو طوفان وہ باندھا کرتے تھے سب اُن سے جاتا رہے گا');
INSERT INTO "jalandhari" VALUES(16,88,'16:88','جن لوگوں نے کفر کیا اور (لوگوں کو) خدا کے رستے سے روکا ہم اُن کو عذاب پر عذاب دیں گے۔ اس لیے کہ شرارت کیا کرتے تھے');
INSERT INTO "jalandhari" VALUES(16,89,'16:89','اور (اس دن کو یاد کرو) جس دن ہم ہر اُمت میں سے خود اُن پر گواہ کھڑے کریں گے۔ اور (اے پیغمبر) تم کو ان لوگوں پر گواہ لائیں گے۔ اور ہم نے تم پر (ایسی) کتاب نازل کی ہے کہ (اس میں) ہر چیز کا بیان (مفصل) ہے اور مسلمانوں کے لیے ہدایت اور رحمت اور بشارت ہے');
INSERT INTO "jalandhari" VALUES(16,90,'16:90','خدا تم کو انصاف اور احسان کرنے اور رشتہ داروں کو (خرچ سے مدد) دینے کا حکم دیتا ہے۔ اور بےحیائی اور نامعقول کاموں سے اور سرکشی سے منع کرتا ہے (اور) تمہیں نصیحت کرتا ہے تاکہ تم یاد رکھو');
INSERT INTO "jalandhari" VALUES(16,91,'16:91','اور جب خدا سے عہد واثق کرو تو اس کو پورا کرو اور جب پکی قسمیں کھاؤ تو اُن کو مت توڑو کہ تم خدا کو اپنا ضامن مقرر کرچکے ہو۔ اور جو کچھ تم کرتے ہو خدا اس کو جانتا ہے');
INSERT INTO "jalandhari" VALUES(16,92,'16:92','اور اُس عورت کی طرح نہ ہونا جس نے محنت سے تو سوت کاتا۔ پھر اس کو توڑ کر ٹکڑے ٹکڑے کر ڈالا۔ کہ تم اپنی قسموں کو آپس میں اس بات کا ذریعہ بنانے لگو کہ ایک گروہ دوسرے گروہ سے زیادہ غالب رہے۔ بات یہ ہے کہ خدا تمہیں اس سے آزماتا ہے۔ اور جن باتوں میں تم اختلاف کرتے ہو قیامت کو اس کی حقیقت تم پر ظاہر کر دے گا');
INSERT INTO "jalandhari" VALUES(16,93,'16:93','اور اگر خدا چاہتا تو تم (سب) کو ایک ہی جماعت بنا دیتا۔ لیکن وہ جسے چاہتا ہے گمراہ کرتا ہے اور جسے چاہتا ہے ہدایت دیتا ہے۔ اور جو عمل تم کرتے ہو (اُس دن) اُن کے بارے میں تم سے ضرور پوچھا جائے گا');
INSERT INTO "jalandhari" VALUES(16,94,'16:94','اور اپنی قسموں کو آپس میں اس بات کا ذریعہ نہ بناؤ کہ (لوگوں کے) قدم جم چکنے کے بعد لڑ کھڑا جائیں اور اس وجہ سے کہ تم نے لوگوں کو خدا کے رستے سے روکا تم کو عقوبت کا مزہ چکھنا پڑے۔ اور بڑا سخت عذاب ملے');
INSERT INTO "jalandhari" VALUES(16,95,'16:95','اور خدا سے جو تم نے عہد کیا ہے (اس کو مت بیچو اور) اس کے بدلے تھوڑی سی قیمت نہ لو۔ (کیونکہ ایفائے عہد کا) جو صلہ خدا کے ہاں مقرر ہے وہ اگر سمجھو تو تمہارے لیے بہتر ہے');
INSERT INTO "jalandhari" VALUES(16,96,'16:96','جو کچھ تمہارے پاس ہے وہ ختم ہو جاتا ہے اور جو خدا کے پاس ہے وہ باقی ہے کہ (کبھی ختم نہیں ہوگا) اور جن لوگوں نے صبر کیا ہم اُن کو ان کے اعمال کا بہت اچھا بدلہ دیں گے');
INSERT INTO "jalandhari" VALUES(16,97,'16:97','جو شخص نیک اعمال کرے گا مرد ہو یا عورت وہ مومن بھی ہوگا تو ہم اس کو (دنیا میں) پاک (اور آرام کی) زندگی سے زندہ رکھیں گے اور (آخرت میں) اُن کے اعمال کا نہایت اچھا صلہ دیں گے');
INSERT INTO "jalandhari" VALUES(16,98,'16:98','اور جب تم قرآن پڑھنے لگو تو شیطان مردود سے پناہ مانگ لیا کرو');
INSERT INTO "jalandhari" VALUES(16,99,'16:99','کہ جو مومن ہیں اپنے پروردگار پر بھروسہ رکھتے ہیں اُن پر اس کا کچھ زور نہیں چلتا');
INSERT INTO "jalandhari" VALUES(16,100,'16:100','اس کا زور ان ہی لوگوں پر چلتا ہے جو اس کو رفیق بناتے ہیں اور اس کے (وسوسے کے) سبب (خدا کے ساتھ) شریک مقرر کرتے ہیں');
INSERT INTO "jalandhari" VALUES(16,101,'16:101','اور جب ہم کوئی آیت کسی آیت کی جگہ بدل دیتے ہیں۔ اور خدا جو کچھ نازل فرماتا ہے اسے خوب جانتا ہے تو (کافر) کہتے ہیں کہ تم یونہی اپنی طرف سے بنا لاتے ہو۔ حقیقت یہ ہے کہ ان میں اکثر نادان ہیں');
INSERT INTO "jalandhari" VALUES(16,102,'16:102','کہہ دو کہ اس کو روح القدس تمہارے پروردگار کی طرف سے سچائی کے ساتھ لے کر نازل ہوئے ہیں تاکہ یہ (قرآن) مومنوں کو ثابت قدم رکھے اور حکم ماننے والوں کے لئے تو (یہ) ہدایت اور بشارت ہے');
INSERT INTO "jalandhari" VALUES(16,103,'16:103','اور ہمیں معلوم ہے کہ یہ کہتے ہیں کہ اس (پیغمبر) کو ایک شخص سکھا جاتا ہے۔ مگر جس کی طرف (تعلیم کی) نسبت کرتے ہیں اس کی زبان تو عجمی ہے اور یہ صاف عربی زبان ہے');
INSERT INTO "jalandhari" VALUES(16,104,'16:104','یہ لوگ خدا کی آیتوں پر ایمان نہیں لاتے ان کو خدا ہدایت نہیں دیتا اور ان کے لئے عذاب الیم ہے');
INSERT INTO "jalandhari" VALUES(16,105,'16:105','جھوٹ افتراء تو وہی لوگ کیا کرتے ہیں جو خدا کی آیتوں پر ایمان نہیں لاتے۔ اور وہی جھوٹے ہیں');
INSERT INTO "jalandhari" VALUES(16,106,'16:106','جو شخص ایمان لانے کے بعد خدا کے ساتھ کفر کرے وہ نہیں جو (کفر پر زبردستی) مجبور کیا جائے اور اس کا دل ایمان کے ساتھ مطمئن ہو۔ بلکہ وہ جو (دل سے اور) دل کھول کر کفر کرے۔ تو ایسوں پر الله کا غضب ہے۔ اور ان کو بڑا سخت عذاب ہوگا');
INSERT INTO "jalandhari" VALUES(16,107,'16:107','یہ اس لئے کہ انہوں نے دنیا کی زندگی کو آخرت کے مقابلے میں عزیز رکھا۔ اور اس لئے خدا کافر لوگوں کو ہدایت نہیں دیتا');
INSERT INTO "jalandhari" VALUES(16,108,'16:108','یہی لوگ ہیں جن کے دلوں پر اور کانوں پر اور آنکھوں پر خدا نے مہر لگا رکھی ہے۔ اور یہی غفلت میں پڑے ہوئے ہیں');
INSERT INTO "jalandhari" VALUES(16,109,'16:109','کچھ شک نہیں کہ یہ آخرت میں خسارہ اٹھانے والے ہوں گے');
INSERT INTO "jalandhari" VALUES(16,110,'16:110','پھر جن لوگوں نے ایذائیں اٹھانے کے بعد ترک وطن کیا۔ پھر جہاد کئے اور ثابت قدم رہے تمہارا پروردگار ان کو بےشک ان (آزمائشوں) کے بعد بخشنے والا (اور ان پر) رحمت کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(16,111,'16:111','جس دن ہر متنفس اپنی طرف سے جھگڑا کرنے آئے گا۔ اور ہر شخص کو اس کے اعمال کا پورا پورا بدلہ دیا جائے گا۔ اور کسی کا نقصان نہیں کیا جائے گا');
INSERT INTO "jalandhari" VALUES(16,112,'16:112','اور خدا ایک بستی کی مثال بیان فرماتا ہے کہ (ہر طرح) امن چین سے بستی تھی ہر طرف سے رزق بافراغت چلا آتا تھا۔ مگر ان لوگوں نے خدا کی نعمتوں کی ناشکری کی تو خدا نے ان کے اعمال کے سبب ان کو بھوک اور خوف کا لباس پہنا کر (ناشکری کا) مزہ چکھا دیا');
INSERT INTO "jalandhari" VALUES(16,113,'16:113','اور ان کے پاس ان ہی میں سے ایک پیغمبر آیا تو انہوں نے اس کو جھٹلایا سو ان کو عذاب نے آپکڑا اور وہ ظالم تھے');
INSERT INTO "jalandhari" VALUES(16,114,'16:114','پس خدا نے جو تم کو حلال طیّب رزق دیا ہے اسے کھاؤ۔ اور الله کی نعمتوں کا شکر کرو۔ اگر اسی کی عبادت کرتے ہو');
INSERT INTO "jalandhari" VALUES(16,115,'16:115','اس نے تم پر مُردار اور لہو اور سور کا گوشت حرام کردیا ہے اور جس چیز پر خدا کے سوا کسی اور کا نام پکارا جائے (اس کو بھی) ہاں اگر کوئی ناچار ہوجائے تو بشرطیکہ گناہ کرنے والا نہ ہو اور نہ حد سے نکلنے والا تو خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(16,116,'16:116','اور یوں ہی جھوٹ جو تمہاری زبان پر آجائے مت کہہ دیا کرو کہ یہ حلال ہے اور یہ حرام ہے کہ خدا پر جھوٹ بہتان باندھنے لگو۔ جو لوگ خدا پر جھوٹ بہتان باندھتے ہیں ان کا بھلا نہیں ہوگا');
INSERT INTO "jalandhari" VALUES(16,117,'16:117','(جھوٹ کا) فائدہ تو تھوڑا سا ہے مگر (اس کے بدلے) ان کو عذاب الیم بہت ہوگا');
INSERT INTO "jalandhari" VALUES(16,118,'16:118','اور چیزیں ہم تم سے پہلے بیان کرچکے ہیں وہ ہم نے یہودیوں پر حرام کردی تھیں۔ اور ہم نے ان پر کچھ ظلم نہیں کیا بلکہ وہی اپنے آپ پر ظلم کرتے تھے');
INSERT INTO "jalandhari" VALUES(16,119,'16:119','پھر جن لوگوں نے نادانی سے برا کام کیا۔ پھر اس کے بعد توبہ کی اور نیکوکار ہوگئے تو تمہارا پروردگار (ان کو) توبہ کرنے اور نیکوکار ہوجانے کے بعد بخشنے والا اور ان پر رحمت کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(16,120,'16:120','بےشک ابراہیم (لوگوں کے) امام اور خدا کے فرمانبردار تھے۔ جو ایک طرف کے ہو رہے تھے اور مشرکوں میں سے نہ تھے');
INSERT INTO "jalandhari" VALUES(16,121,'16:121','اس کی نعمتوں کے شکرگزار تھے۔ خدا نے ان کو برگزیدہ کیا تھا اور (اپنی) سیدھی راہ پر چلایا تھا');
INSERT INTO "jalandhari" VALUES(16,122,'16:122','اور ہم نے ان کو دنیا میں بھی خوبی دی تھی۔ اور وہ آخرت میں بھی نیک لوگوں میں ہوں گے');
INSERT INTO "jalandhari" VALUES(16,123,'16:123','پھر ہم نے تمہاری طرف وحی بھیجی کہ دین ابراہیم کی پیروی اختیار کرو جو ایک طرف کے ہو رہے تھے اور مشرکوں میں سے نہ تھے');
INSERT INTO "jalandhari" VALUES(16,124,'16:124','ہفتے کا دن تو ان ہی لوگوں کے لئے مقرر کیا گیا تھا۔ جنہوں نے اس میں اختلاف کیا۔ اور تمہارا پروردگار قیامت کے دن ان میں ان باتوں کا فیصلہ کردے گا جن میں وہ اختلاف کرتے تھے');
INSERT INTO "jalandhari" VALUES(16,125,'16:125','(اے پیغمبر) لوگوں کو دانش اور نیک نصیحت سے اپنے پروردگار کے رستے کی طرف بلاؤ۔ اور بہت ہی اچھے طریق سے ان سے مناظرہ کرو۔ جو اس کے رستے سے بھٹک گیا تمہارا پروردگار اسے بھی خوب جانتا ہے اور جو رستے پر چلنے والے ہیں ان سے بھی خوب واقف ہے');
INSERT INTO "jalandhari" VALUES(16,126,'16:126','اور اگر تم ان کو تکلیف دینی چاہو تو اتنی ہی دو جتنی تکلیف تم کو ان سے پہنچی۔ اور اگر صبر کرو تو وہ صبر کرنے والوں کے لیے بہت اچھا ہے');
INSERT INTO "jalandhari" VALUES(16,127,'16:127','اور صبر ہی کرو اور تمہارا صبر بھی خدا ہی کی مدد سے ہے اور ان کے بارے میں غم نہ کرو اور جو یہ بداندیشی کرتے ہیں اس سے تنگدل نہ ہو');
INSERT INTO "jalandhari" VALUES(16,128,'16:128','کچھ شک نہیں کہ جو پرہیزگار ہیں اور جو نیکوکار ہیں خدا ان کا مددگار ہے');
INSERT INTO "jalandhari" VALUES(17,1,'17:1','وہ (ذات) پاک ہے جو ایک رات اپنے بندے کو مسجدالحرام یعنی (خانہٴ کعبہ) سے مسجد اقصیٰ (یعنی بیت المقدس) تک جس کے گردا گرد ہم نے برکتیں رکھی ہیں لے گیا تاکہ ہم اسے اپنی (قدرت کی) نشانیاں دکھائیں۔ بےشک وہ سننے والا (اور) دیکھنے والا ہے');
INSERT INTO "jalandhari" VALUES(17,2,'17:2','اور ہم نے موسیٰ کو کتاب عنایت کی تھی اور اس کو بنی اسرائیل کے لئے رہنما مقرر کیا تھا کہ میرے سوا کسی کو کارساز نہ ٹھہرانا');
INSERT INTO "jalandhari" VALUES(17,3,'17:3','اے اُن لوگوں کی اولاد جن کو ہم نے نوح کے ساتھ (کشتی میں) سوار کیا تھا۔ بےشک نوح (ہمارے) شکرگزار بندے تھے');
INSERT INTO "jalandhari" VALUES(17,4,'17:4','اور ہم نے کتاب میں بنی اسرائیل سے کہہ دیا تھا کہ زمین میں دو دفعہ فساد مچاؤ گے اور بڑی سرکشی کرو گے');
INSERT INTO "jalandhari" VALUES(17,5,'17:5','پس جب پہلے (وعدے) کا وقت آیا تو ہم نے سخت لڑائی لڑنے والے بندے تم پر مسلط کردیئے اور وہ شہروں کے اندر پھیل گئے۔ اور وہ وعدہ پورا ہو کر رہا');
INSERT INTO "jalandhari" VALUES(17,6,'17:6','پھر ہم نے دوسری بات تم کو اُن پر غلبہ دیا اور مال اور بیٹوں سے تمہاری مدد کی۔ اور تم کو جماعت کثیر بنا دیا');
INSERT INTO "jalandhari" VALUES(17,7,'17:7','اگر تم نیکوکاری کرو گے تو اپنی جانوں کے لئے کرو گے۔ اور اگر اعمال بد کرو گے تو (اُن کا) وبال بھی تمہاری ہی جانوں پر ہوگا پھر جب دوسرے (وعدے) کا وقت آیا (تو ہم نے پھر اپنے بندے بھیجے) تاکہ تمہارے چہروں کو بگاڑ دیں اور جس طرح پہلی دفعہ مسجد (بیت المقدس) میں داخل ہوگئے تھے اسی طرح پھر اس میں داخل ہوجائیں اور جس چیز پر غلبہ پائیں اُسے تباہ کردیں');
INSERT INTO "jalandhari" VALUES(17,8,'17:8','امید ہے کہ تمہارا پروردگار تم پر رحم کرے، اور اگر تم پھر وہی (حرکتیں) کرو گے تو ہم بھی (وہی پہلا سلوک) کریں گے اور ہم نے جہنم کو کافروں کے لئے قید خانہ بنا رکھا ہے');
INSERT INTO "jalandhari" VALUES(17,9,'17:9','یہ قرآن وہ رستہ دکھاتا ہے جو سب سے سیدھا ہے اور مومنوں کو جو نیک عمل کرتے ہیں بشارت دیتا ہے کہ اُن کے لئے اجر عظیم ہے');
INSERT INTO "jalandhari" VALUES(17,10,'17:10','اور یہ بھی (بتاتا ہے) کہ جو آخرت پر ایمان نہیں رکھتے اُن کے لئے ہم نے دکھ دینے والا عذاب تیار کر رکھا ہے');
INSERT INTO "jalandhari" VALUES(17,11,'17:11','اور انسان جس طرح (جلدی سے) بھلائی مانگتا ہے اسی طرح برائی مانگتا ہے۔ اور انسان جلد باز (پیدا ہوا) ہے');
INSERT INTO "jalandhari" VALUES(17,12,'17:12','اور ہم نے دن اور رات کو دو نشانیاں بنایا ہے رات کی نشانی کو تاریک بنایا اور دن کی نشانی کو روشن۔ تاکہ تم اپنے پروردگار کا فضل (یعنی) روزی تلاش کرو اور برسوں کا شمار اور حساب جانو۔ اور ہم نے ہر چیز کو (بخوبی) تفصیل کردی ہے');
INSERT INTO "jalandhari" VALUES(17,13,'17:13','اور ہم نے ہر انسان کے اعمال کو (بہ صورت کتاب) اس کے گلے میں لٹکا دیا ہے۔ اور قیامت کے روز (وہ) کتاب اسے نکال دکھائیں گے جسے وہ کھلا ہوا دیکھے گا');
INSERT INTO "jalandhari" VALUES(17,14,'17:14','(کہا جائے گا کہ) اپنی کتاب پڑھ لے۔ تو آج اپنا آپ ہی محاسب کافی ہے');
INSERT INTO "jalandhari" VALUES(17,15,'17:15','جو شخص ہدایت اختیار کرتا ہے تو اپنے لئے اختیار کرتا ہے۔ اور جو گمراہ ہوتا ہے گمراہی کا ضرر بھی اسی کو ہوگا۔ اور کوئی شخص کسی دوسرے کا بوجھ نہیں اٹھائے گا۔ اور جب تک ہم پیغمبر نہ بھیج لیں عذاب نہیں دیا کرتے');
INSERT INTO "jalandhari" VALUES(17,16,'17:16','اور جب ہمارا ارادہ کسی بستی کے ہلاک کرنے کا ہوا تو وہاں کے آسودہ لوگوں کو (فواحش پر) مامور کردیا تو وہ نافرمانیاں کرتے رہے۔ پھر اس پر (عذاب کا) حکم ثابت ہوگیا۔ اور ہم نے اسے ہلاک کر ڈالا');
INSERT INTO "jalandhari" VALUES(17,17,'17:17','اور ہم نے نوح کے بعد بہت سی اُمتوں کو ہلاک کر ڈالا۔ اور تمہارا پروردگار اپنے بندوں کے گناہوں کو جاننے اور دیکھنے والا کافی ہے');
INSERT INTO "jalandhari" VALUES(17,18,'17:18','جو شخص دنیا (کی آسودگی) کا خواہشمند ہو تو ہم اس میں سے جسے چاہتے ہیں اور جتنا چاہتے ہیں جلد دے دیتے ہیں۔ پھر اس کے لئے جہنم کو (ٹھکانا) مقرر کر رکھا ہے۔ جس میں وہ نفرین سن کر اور (درگاہ خدا سے) راندہ ہو کر داخل ہوگا');
INSERT INTO "jalandhari" VALUES(17,19,'17:19','اور جو شخص آخرت کا خواستگار ہوا اور اس میں اتنی کوشش کرے جتنی اسے لائق ہے اور وہ مومن بھی ہو تو ایسے ہی لوگوں کی کوشش ٹھکانے لگتی ہے');
INSERT INTO "jalandhari" VALUES(17,20,'17:20','ہم اُن کو اور ان کو سب کو تمہارے پروردگار کی بخشش سے مدد دیتے ہیں۔ اور تمہارے پروردگار کی بخشش (کسی سے) رکی ہوئی نہیں');
INSERT INTO "jalandhari" VALUES(17,21,'17:21','دیکھو ہم نے کس طرح بعض کو بعض پر فضیلت بخشی ہے۔ اور آخرت درجوں میں (دنیا سے) بہت برتر اور برتری میں کہیں بڑھ کر ہے');
INSERT INTO "jalandhari" VALUES(17,22,'17:22','اور خدا کے ساتھ کوئی اور معبود نہ بنانا کہ ملامتیں سن کر اور بےکس ہو کر بیٹھے رہ جاؤ گے');
INSERT INTO "jalandhari" VALUES(17,23,'17:23','اور تمہارے پروردگار نے ارشاد فرمایا ہے کہ اس کے سوا کسی کی عبادت نہ کرو اور ماں باپ کے ساتھ بھلائی کرتے رہو۔ اگر ان میں سے ایک یا دونوں تمہارے سامنے بڑھاپے کو پہنچ جائیں تو اُن کو اُف تک نہ کہنا اور نہ انہیں جھڑکنا اور اُن سے بات ادب کے ساتھ کرنا');
INSERT INTO "jalandhari" VALUES(17,24,'17:24','اور عجزو نیاز سے ان کے آگے جھکے رہو اور ان کے حق میں دعا کرو کہ اے پروردگار جیسا انہوں نے مجھے بچپن میں (شفقت سے) پرورش کیا ہے تو بھی اُن (کے حال) پر رحمت فرما');
INSERT INTO "jalandhari" VALUES(17,25,'17:25','جو کچھ تمہارے دلوں میں ہے تمہارا پروردگار اس سے بخوبی واقف ہے۔ اگر تم نیک ہوگے تو وہ رجوع لانے والوں کو بخش دینے والا ہے');
INSERT INTO "jalandhari" VALUES(17,26,'17:26','اور رشتہ داروں اور محتاجوں اور مسافروں کو ان کا حق ادا کرو۔ اور فضول خرچی سے مال نہ اُڑاؤ');
INSERT INTO "jalandhari" VALUES(17,27,'17:27','کہ فضول خرچی کرنے والے تو شیطان کے بھائی ہیں۔ اور شیطان اپنے پروردگار (کی نعمتوں) کا کفر ان کرنے والا (یعنی ناشکرا) ہے');
INSERT INTO "jalandhari" VALUES(17,28,'17:28','اور اگر تم نے اپنے پروردگار کی رحمت (یعنی فراخ دستی) کے انتظار میں جس کی تمہیں امید ہو ان (مستحقین) کی طرف توجہ نہ کرسکو اُن سے نرمی سے بات کہہ دیا کرو');
INSERT INTO "jalandhari" VALUES(17,29,'17:29','اور اپنے ہاتھ کو نہ تو گردن سے بندھا ہوا (یعنی بہت تنگ) کرلو (کہ کسی کچھ دو ہی نہیں) اور نہ بالکل کھول ہی دو (کہ سبھی دے ڈالو اور انجام یہ ہو) کہ ملامت زدہ اور درماندہ ہو کر بیٹھ جاؤ');
INSERT INTO "jalandhari" VALUES(17,30,'17:30','بےشک تمہارا پروردگار جس کی روزی چاہتا ہے فراخ کردیتا ہے اور (جس کی روزی چاہتا ہے) تنگ کردیتا ہے وہ اپنے بندوں سے خبردار ہے اور (ان کو) دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(17,31,'17:31','اور اپنی اولاد کو مفلسی کے خوف سے قتل نہ کرنا۔ (کیونکہ) ان کو اور تم کو ہم ہی رزق دیتے ہیں۔ کچھ شک نہیں کہ ان کا مار ڈالنا بڑا سخت گناہ ہے');
INSERT INTO "jalandhari" VALUES(17,32,'17:32','اور زنا کے بھی پاس نہ جانا کہ وہ بےحیائی اور بری راہ ہے');
INSERT INTO "jalandhari" VALUES(17,33,'17:33','اور جس کا جاندار کا مارنا خدا نے حرام کیا ہے اسے قتل نہ کرنا مگر جائز طور پر (یعنی بفتویٰ شریعت)۔ اور جو شخص ظلم سے قتل کیا جائے ہم نے اس کے وارث کو اختیار دیا ہے (کہ ظالم قاتل سے بدلہ لے) تو اس کو چاہیئے کہ قتل (کے قصاص) میں زیادتی نہ کرے کہ وہ منصورو فتحیاب ہے');
INSERT INTO "jalandhari" VALUES(17,34,'17:34','اور یتیم کے مال کے پاس بھی نہ پھٹکنا مگر ایسے طریق سے کہ بہت بہتر ہو یہاں تک کہ ہو جوانی کو پہنچ جائے۔ اور عہد کو پورا کرو کہ عہد کے بارے میں ضرور پرسش ہوگی');
INSERT INTO "jalandhari" VALUES(17,35,'17:35','اور جب (کوئی چیز) ناپ کر دینے لگو تو پیمانہ پورا بھرا کرو اور (جب تول کر دو تو) ترازو سیدھی رکھ کر تولا کرو۔ یہ بہت اچھی بات اور انجام کے لحاظ سے بھی بہت بہتر ہے');
INSERT INTO "jalandhari" VALUES(17,36,'17:36','اور (اے بندے) جس چیز کا تجھے علم نہیں اس کے پیچھے نہ پڑ۔ کہ کان اور آنکھ اور دل ان سب (جوارح) سے ضرور باز پرس ہوگی');
INSERT INTO "jalandhari" VALUES(17,37,'17:37','اور زمین پر اکڑ کر (اور تن کر) مت چل کہ تو زمین کو پھاڑ تو نہیں ڈالے گا اور نہ لمبا ہو کر پہاڑوں (کی چوٹی) تک پہنچ جائے گا');
INSERT INTO "jalandhari" VALUES(17,38,'17:38','ان سب (عادتوں) کی برائی تیرے پروردگار کے نزدیک بہت ناپسند ہے');
INSERT INTO "jalandhari" VALUES(17,39,'17:39','اے پیغمبر یہ ان (ہدایتوں) میں سے ہیں جو خدا نے دانائی کی باتیں تمہاری طرف وحی کی ہیں۔ اور خدا کے ساتھ کوئی معبود نہ بنانا کہ (ایسا کرنے سے) ملامت زدہ اور (درگاہ خدا سے) راندہ بنا کر جہنم میں ڈال دیئے جاؤ گے');
INSERT INTO "jalandhari" VALUES(17,40,'17:40','(مشرکو!) کیا تمہارے پروردگار نے تم کو لڑکے دیئے اور خود فرشتوں کو بیٹیاں بنایا۔ کچھ شک نہیں کہ (یہ) تم بڑی (نامعقول بات) کہتے ہو');
INSERT INTO "jalandhari" VALUES(17,41,'17:41','اور ہم نے اس قرآن میں طرح طرح کی باتیں بیان کی ہیں تاکہ لوگ نصیحت پکڑیں گے۔ مگر وہ اس سے اور بدک جاتے ہیں');
INSERT INTO "jalandhari" VALUES(17,42,'17:42','کہہ دو کہ اگر خدا کے ساتھ اور معبود ہوتے جیسا کہ یہ کہتے ہیں تو وہ ضرور (خدائے) مالک عرش کی طرف (لڑنے بھڑنے کے لئے) رستہ نکالتے');
INSERT INTO "jalandhari" VALUES(17,43,'17:43','وہ پاک ہے اور جو کچھ یہ بکواس کرتے ہیں اس سے (اس کا رتبہ) بہت عالی ہے');
INSERT INTO "jalandhari" VALUES(17,44,'17:44','ساتوں آسمان اور زمین اور جو لوگ ان میں ہیں سب اسی کی تسبیح کرتے ہیں۔ اور (مخلوقات میں سے) کوئی چیز نہیں مگر اس کی تعریف کے ساتھ تسبیح کرتی ہے۔ لیکن تم ان کی تسبیح کو نہیں سمجھتے۔ بےشک وہ بردبار (اور) غفار ہے');
INSERT INTO "jalandhari" VALUES(17,45,'17:45','اور جب قرآن پڑھا کرتے ہو تو ہم تم میں اور ان لوگوں میں جو آخرت پر ایمان نہیں رکھتے حجاب پر حجاب کر دیتے ہیں');
INSERT INTO "jalandhari" VALUES(17,46,'17:46','اور ان کے دلوں پر پردہ ڈال دیتے ہیں کہ اسے سمجھ نہ سکیں اور ان کے کانوں میں ثقل پیدا کر دیتے ہیں۔ اور جب تم قرآن میں اپنے پروردگار یکتا کا ذکر کرتے ہو تو وہ بدک جاتے اور پیٹھ پھیر کر چل دیتے ہیں');
INSERT INTO "jalandhari" VALUES(17,47,'17:47','یہ لوگ جب تمہاری طرف کان لگاتے ہیں تو جس نیت سے یہ سنتے ہیں ہم اسے خوب جانتے ہیں اور جب یہ سرگوشیاں کرتے ہیں (یعنی) جب ظالم کہتے ہیں کہ تم ایک ایسے شخص کی پیروی کرتے ہو جس پر جادو کیا گیا ہے');
INSERT INTO "jalandhari" VALUES(17,48,'17:48','دیکھو انہوں نے کس کس طرح کی تمہارے بارے میں باتیں بنائیں ہیں۔ سو یہ گمراہ ہو رہے ہیں اور رستہ نہیں پاسکتے');
INSERT INTO "jalandhari" VALUES(17,49,'17:49','اور کہتے ہیں کہ جب ہم (مر کر بوسیدہ) ہڈیوں اور چُور چُور ہوجائیں گے تو کیا ازسرنو پیدا ہو کر اُٹھیں گے');
INSERT INTO "jalandhari" VALUES(17,50,'17:50','کہہ دو کہ (خواہ تم) پتھر ہوجاؤ یا لوہا');
INSERT INTO "jalandhari" VALUES(17,51,'17:51','یا کوئی اور چیز جو تمہارے نزدیک (پتھر اور لوہے سے بھی) بڑی (سخت) ہو (جھٹ کہیں گے) کہ (بھلا) ہمیں دوبارہ کون جِلائے گا؟ کہہ دو کہ وہی جس نے تم کو پہلی بار پیدا کیا۔ تو (تعجب سے) تمہارے آگے سرہلائیں گے اور پوچھیں گے کہ ایسا کب ہوگا؟ کہہ دو کہ امید ہے جلد ہوگا');
INSERT INTO "jalandhari" VALUES(17,52,'17:52','جس دن وہ تمہیں پکارے گا تو تم اس کی تعریف کے ساتھ جواب دو گے اور خیال کرو گے کہ تم (دنیا میں) بہت کم (مدت) رہے');
INSERT INTO "jalandhari" VALUES(17,53,'17:53','اور میرے بندوں سے کہہ دو کہ (لوگوں سے) ایسی باتیں کہا کریں جو بہت پسندیدہ ہوں۔ کیونکہ شیطان (بری باتوں سے) ان میں فساد ڈلوا دیتا ہے۔ کچھ شک نہیں کہ شیطان انسان کا کھلا دشمن ہے');
INSERT INTO "jalandhari" VALUES(17,54,'17:54','تمہارا پروردگار تم سے خوب واقف ہے۔ اگر چاہے تو تم پر رحم کرے یا اگر چاہے تو تمہیں عذاب دے۔ اور ہم نے تم کو ان پر داروغہ (بنا کر) نہیں بھیجا');
INSERT INTO "jalandhari" VALUES(17,55,'17:55','اور جو لوگ آسمانوں اور زمین میں ہیں تمہارا پروردگار ان سے خوب واقف ہے۔ اور ہم نے بعض پیغمبروں کو بعض پر فضیلت بخشی اور داؤد کو زبور عنایت کی');
INSERT INTO "jalandhari" VALUES(17,56,'17:56','کہو کہ (مشرکو) جن لوگوں کی نسبت تمہیں (معبود ہونے کا) گمان ہے ان کو بلا کر دیکھو۔ وہ تم سے تکلیف کے دور کرنے یا اس کے بدل دینے کا کچھ بھی اختیار نہیں رکھتے');
INSERT INTO "jalandhari" VALUES(17,57,'17:57','یہ لوگ جن کو (خدا کے سوا) پکارتے ہیں وہ خود اپنے پروردگار کے ہاں ذریعہ (تقرب) تلاش کرتے رہتے ہیں کہ کون ان میں (خدا کا) زیادہ مقرب ہوتا ہے اور اس کی رحمت کے امیدوار رہتے ہیں اور اس کے عذاب سے خوف رکھتے ہیں۔ بےشک تمہارے پروردگار کا عذاب ڈرنے کی چیز ہے');
INSERT INTO "jalandhari" VALUES(17,58,'17:58','اور (کفر کرنے والوں کی) کوئی بستی نہیں مگر قیامت کے دن سے پہلے ہم اسے ہلاک کردیں گے یا سخت عذاب سے معذب کریں گے۔ یہ کتاب (یعنی تقدیر) میں لکھا جاچکا ہے');
INSERT INTO "jalandhari" VALUES(17,59,'17:59','اور ہم نے نشانیاں بھیجنی اس لئے موقوف کردیں کہ اگلے لوگوں نے اس کی تکذیب کی تھی۔ اور ہم نے ثمود کو اونٹنی (نبوت صالح کی کھلی) نشانی دی۔ تو انہوں نے اس پر ظلم کیا اور ہم جو نشانیاں بھیجا کرتے ہیں تو ڈرانے کو');
INSERT INTO "jalandhari" VALUES(17,60,'17:60','جب ہم نے تم سے کہا کہ تمہارا پروردگار لوگوں کو احاطہ کئے ہوئے ہے۔ اور جو نمائش ہم نے تمہیں دکھائی اس کو لوگوں کے لئے آرمائش کیا۔ اور اسی طرح (تھوہر کے) درخت کو جس پر قرآن میں لعنت کی گئی۔ اور ہم انہیں ڈراتے ہیں تو ان کو اس سے بڑی (سخت) سرکشی پیدا ہوتی ہے');
INSERT INTO "jalandhari" VALUES(17,61,'17:61','اور جب ہم نے فرشتوں سے کہا کہ آدم کو سجدہ کرو تو سب نے سجدہ کیا مگر ابلیس نے نہ کیا۔ بولا کہ بھلا میں ایسے شخص کو سجدہ کرو جس کو تو نے مٹی سے پیدا کیا ہے');
INSERT INTO "jalandhari" VALUES(17,62,'17:62','(اور از راہ طنز) کہنے لگا کہ دیکھ تو یہی وہ ہے جسے تو نے مجھ پر فضیلت دی ہے۔ اگر تو مجھ کو قیامت کے دن تک مہلت دے تو میں تھوڑے سے شخصوں کے سوا اس کی (تمام) اولاد کی جڑ کاٹتا رہوں گا');
INSERT INTO "jalandhari" VALUES(17,63,'17:63','خدا نے فرمایا (یہاں سے) چلا جا۔ جو شخص ان میں سے تیری پیروی کرے گا تو تم سب کی جزا جہنم ہے (اور وہ) پوری سزا (ہے)');
INSERT INTO "jalandhari" VALUES(17,64,'17:64','اور ان میں سے جس کو بہکا سکے اپنی آواز سے بہکاتا رہ۔ اور ان پر اپنے سواروں اور پیاروں کو چڑھا کر لاتا رہ اور ان کے مال اور اولاد میں شریک ہوتا رہ اور ان سے وعدے کرتا رہ۔ اور شیطان جو وعدے ان سے کرتا ہے سب دھوکا ہے');
INSERT INTO "jalandhari" VALUES(17,65,'17:65','جو میرے (مخلص) بندے ہیں ان پر تیرا کچھ زور نہیں۔ اور (اے پیغمبر) تمہارا پروردگار کارساز کافی ہے');
INSERT INTO "jalandhari" VALUES(17,66,'17:66','تمہارا پروردگار وہ ہے جو تمہارے لئے دریا میں کشتیاں چلاتا ہے تاکہ تم اس کے فضل سے (روزی) تلاش کرو۔ بےشک وہ تم پر مہربان ہے');
INSERT INTO "jalandhari" VALUES(17,67,'17:67','اور جب تم کو دریا میں تکلیف پہنچتی ہے (یعنی ڈوبنے کا خوف ہوتا ہے) تو جن کو تم پکارا کرتے ہو سب اس (پروردگار) کے سوا گم ہوجاتے ہیں۔ پھر جب وہ تم کو (ڈوبنے سے) بچا کر خشکی پر لے جاتا ہے تو تم منہ پھیر لیتے ہو اور انسان ہے ہی ناشکرا');
INSERT INTO "jalandhari" VALUES(17,68,'17:68','کیا تم (اس سے) بےخوف ہو کہ خدا تمہیں خشکی کی طرف (لے جا کر زمین میں) دھنسا دے یا تم پر سنگریزوں کی بھری ہوئی آندھی چلادے۔ پھر تم اپنا کوئی نگہبان نہ پاؤ');
INSERT INTO "jalandhari" VALUES(17,69,'17:69','یا (اس سے) بےخوف ہو کر تم دوسری دفعہ دریا میں لے جائے پھر تم پر تیز ہوا چلائے اور تمہارے کفر کے سبب تمہیں ڈبو دے۔ پھر تم اس غرق کے سبب اپنے لئے کوئی ہمارا پیچھا کرنے والا نہ پاؤ');
INSERT INTO "jalandhari" VALUES(17,70,'17:70','اور ہم نے بنی آدم کو عزت بخشی اور ان کو جنگل اور دریا میں سواری دی اور پاکیزہ روزی عطا کی اور اپنی بہت سی مخلوقات پر فضیلت دی');
INSERT INTO "jalandhari" VALUES(17,71,'17:71','جس دن ہم سب لوگوں کو ان کے پیشواؤں کے ساتھ بلائیں گے۔ تو جن (کے اعمال) کی کتاب ان کے داہنے ہاتھ میں دی جائے گی وہ اپنی کتاب کو (خوش ہو ہو کر) پڑھیں گے اور ان پر دھاگے برابر بھی ظلم نہ ہوگا');
INSERT INTO "jalandhari" VALUES(17,72,'17:72','اور جو شخص اس (دنیا) میں اندھا ہو وہ آخرت میں بھی اندھا ہوگا۔ اور (نجات کے) رستے سے بہت دور');
INSERT INTO "jalandhari" VALUES(17,73,'17:73','اور اے پیغمبر جو وحی ہم نے تمہاری طرف بھیجی ہے قریب تھا کہ یہ (کافر) لوگ تم کو اس سے بچلا دیں تاکہ تم اس کے سوا اور باتیں ہماری نسبت بنالو۔ اور اس وقت وہ تم کو دوست بنا لیتے');
INSERT INTO "jalandhari" VALUES(17,74,'17:74','اور اگر تم کو ثابت قدم نہ رہنے دیتے تو تم کسی قدر ان کی طرف مائل ہونے ہی لگے تھے');
INSERT INTO "jalandhari" VALUES(17,75,'17:75','اس وقت ہم تم کو زندگی میں (عذاب کا) دونا اور مرنے پر بھی دونا مزا چکھاتے پھر تم ہمارے مقابلے میں کسی کو اپنا مددگار نہ پاتے');
INSERT INTO "jalandhari" VALUES(17,76,'17:76','اور قریب تھا کہ یہ لوگ تمہیں زمین (مکہ) سے پھسلا دیں تاکہ تمہیں وہاں سے جلاوطن کر دیں۔ اور اس وقت تمہارے پیچھے یہ بھی نہ رہتے مگر کم');
INSERT INTO "jalandhari" VALUES(17,77,'17:77','جو پیغمبر ہم نے تم سے پہلے بھیجے تھے ان کا (اور ان کے بارے میں ہمارا یہی) طریق رہا ہے اور تم ہمارے طریق میں تغیروتبدل نہ پاؤ گے');
INSERT INTO "jalandhari" VALUES(17,78,'17:78','(اے محمدصلى الله عليه وسلم) سورج کے ڈھلنے سے رات کے اندھیرے تک (ظہر، عصر، مغرب، عشا کی) نمازیں اور صبح کو قرآن پڑھا کرو۔ کیوں صبح کے وقت قرآن کا پڑھنا موجب حضور (ملائکہ) ہے');
INSERT INTO "jalandhari" VALUES(17,79,'17:79','اور بعض حصہ شب میں بیدار ہوا کرو (اور تہجد کی نماز پڑھا کرو)۔ (یہ شب خیزی) تمہاری لئے (سبب) زیادت ہے (ثواب اور نماز تہجد تم کو نفل) ہے قریب ہے کہ خدا تم کو مقام محمود میں داخل کرے');
INSERT INTO "jalandhari" VALUES(17,80,'17:80','اور کہو کہ اے پروردگار مجھے (مدینے میں) اچھی طرح داخل کیجیو اور (مکے سے) اچھی طرح نکالیو۔ اور اپنے ہاں سے زور وقوت کو میرا مددگار بنائیو');
INSERT INTO "jalandhari" VALUES(17,81,'17:81','اور کہہ دو کہ حق آگیا اور باطل نابود ہوگیا۔ بےشک باطل نابود ہونے والا ہے');
INSERT INTO "jalandhari" VALUES(17,82,'17:82','اور ہم قرآن (کے ذریعے) سے وہ چیز نازل کرتے ہیں جو مومنوں کے لئے شفا اور رحمت ہے اور ظالموں کے حق میں تو اس سے نقصان ہی بڑھتا ہے');
INSERT INTO "jalandhari" VALUES(17,83,'17:83','اور جب ہم انسان کو نعمت بخشتے ہیں تو ردگرداں ہوجاتا اور پہلو پھیر لیتا ہے۔ اور جب اسے سختی پہنچتی ہے تو ناامید ہوجاتا ہے');
INSERT INTO "jalandhari" VALUES(17,84,'17:84','کہہ دو کہ ہر شخص اپنے طریق کے مطابق عمل کرتا ہے۔ سو تمہارا پروردگار اس شخص سے خوب واقف ہے جو سب سے زیادہ سیدھے رستے پر ہے');
INSERT INTO "jalandhari" VALUES(17,85,'17:85','اور تم سے روح کے بارے میں سوال کرتے ہیں۔ کہہ دو کہ وہ میرے پروردگار کی ایک شان ہے اور تم لوگوں کو (بہت ہی) کم علم دیا گیا ہے');
INSERT INTO "jalandhari" VALUES(17,86,'17:86','اور اگر ہم چاہیں تو جو (کتاب) ہم تمہاری طرف بھیجتے ہیں اسے (دلوں سے) محو کردیں۔ پھر تم اس کے لئے ہمارے مقابلے میں کسی کو مددگار نہ پاؤ');
INSERT INTO "jalandhari" VALUES(17,87,'17:87','مگر (اس کا قائم رہنا) تمہارے پروردگار کی رحمت ہے۔ کچھ شک نہیں کہ تم پر اس کا بڑا فضل ہے');
INSERT INTO "jalandhari" VALUES(17,88,'17:88','کہہ دو کہ اگر انسان اور جن اس بات پر مجتمع ہوں کہ اس قرآن جیسا بنا لائیں تو اس جیسا نہ لاسکیں گے اگرچہ وہ ایک دوسرے کو مددگار ہوں');
INSERT INTO "jalandhari" VALUES(17,89,'17:89','اور ہم نے قرآن میں سب باتیں طرح طرح سے بیان کردی ہیں۔ مگر اکثر لوگوں نے انکار کرنے کے سوا قبول نہ کیا');
INSERT INTO "jalandhari" VALUES(17,90,'17:90','اور کہنے لگے کہ ہم تم پر ایمان نہیں لائیں گے جب تک کہ (عجیب وغریب باتیں نہ دکھاؤ یعنی یا تو) ہمارے لئے زمین سے چشمہ جاری کردو');
INSERT INTO "jalandhari" VALUES(17,91,'17:91','یا تمہارا کھجوروں اور انگوروں کا کوئی باغ ہو اور اس کے بیچ میں نہریں بہا نکالو');
INSERT INTO "jalandhari" VALUES(17,92,'17:92','یا جیسا تم کہا کرتے ہو ہم پر آسمان کے ٹکڑے لا گراؤ یا خدا اور فرشتوں کو (ہمارے) سامنے لاؤ');
INSERT INTO "jalandhari" VALUES(17,93,'17:93','یا تو تمہارا سونے کا گھر ہو یا تم آسمان پر چڑھ جاؤ۔ اور ہم تمہارے چڑھنے کو بھی نہیں مانیں گے جب تک کہ کوئی کتاب نہ لاؤ جسے ہم پڑھ بھی لیں۔ کہہ دو کہ میرا پروردگار پاک ہے میں تو صرف ایک پیغام پہنچانے والا انسان ہوں');
INSERT INTO "jalandhari" VALUES(17,94,'17:94','اور جب لوگوں کے پاس ہدایت آگئی تو ان کو ایمان لانے سے اس کے سوا کوئی چیز مانع نہ ہوئی کہ کہنے لگے کہ کیا خدا نے آدمی کو پیغمبر کرکے بھیجا ہے');
INSERT INTO "jalandhari" VALUES(17,95,'17:95','کہہ دو کہ اگر زمین میں فرشتے ہوتے (کہ اس میں) چلتے پھرتے (اور) آرام کرتے (یعنی بستے) تو ہم اُن کے پاس فرشتے کو پیغمبر بنا کر بھیجتے');
INSERT INTO "jalandhari" VALUES(17,96,'17:96','کہہ دو کہ میرے اور تمہارے درمیان خدا ہی گواہ کافی ہے۔ وہی اپنے بندوں سے خبردار (اور ان کو) دیکھنے والا ہے');
INSERT INTO "jalandhari" VALUES(17,97,'17:97','اور جس شخص کو خدا ہدایت دے وہی ہدایت یاب ہے۔ اور جن کو گمراہ کرے تو تم خدا کے سوا اُن کے رفیق نہیں پاؤ گے۔ اور ہم اُن کو قیامت کے دن اوندھے منہ اندھے گونگے اور بہرے (بنا کر) اٹھائیں گے۔ اور ان کا ٹھکانہ دوزخ ہے۔ جب (اس کی آگ) بجھنے کو ہوگی تو ہم ان کو (عذاب دینے کے لئے) اور بھڑکا دیں گے');
INSERT INTO "jalandhari" VALUES(17,98,'17:98','یہ ان کی سزا ہے اس لئے کہ وہ ہماری آیتوں سے کفر کرتے تھے اور کہتے تھے کہ جب ہم (مر کر بوسیدہ) ہڈیاں اور ریزہ ریزہ ہوجائیں گے تو کیا ازسرنو پیدا کئے جائیں گے');
INSERT INTO "jalandhari" VALUES(17,99,'17:99','کیا انہوں نے نہیں دیکھا کہ خدا جس نے آسمانوں اور زمین کو پیدا کیا ہے اس بات پر قادر ہے کہ ان جیسے (لوگ) پیدا کردے۔ اور اس نے ان کے لئے ایک وقت مقرر کر دیا ہے جس میں کچھ بھی شک نہیں۔ تو ظالموں نے انکار کرنے کے سوا (اسے) قبول نہ کیا');
INSERT INTO "jalandhari" VALUES(17,100,'17:100','کہہ دو کہ اگر میرے پروردگار کی رحمت کے خزانے تمہارے ہاتھ میں ہوتے تو تم خرچ ہوجانے کے خوف سے (ان کو) بند رکھتے۔ اور انسان دل کا بہت تنگ ہے');
INSERT INTO "jalandhari" VALUES(17,101,'17:101','اور ہم نے موسیٰ کو نو کھلی نشانیاں دیں تو بنی اسرائیل سے دریافت کرلو کہ جب وہ ان کے پاس آئے تو فرعون نے ان سے کہا کہ موسیٰ میں خیال کرتا ہوں کہ تم پر جادو کیا گیا ہے');
INSERT INTO "jalandhari" VALUES(17,102,'17:102','انہوں نے کہا کہ تم یہ جانتے ہو کہ آسمانوں اور زمین کے پروردگار کے سوا ان کو کسی نے نازل نہیں کیا۔ (اور وہ بھی تم لوگوں کے) سمجھانے کو۔ اور اے فرعون میں خیال کرتا ہوں کہ تم ہلاک ہوجاؤ گے');
INSERT INTO "jalandhari" VALUES(17,103,'17:103','تو اس نے چاہا کہ ان کو سر زمین (مصر) سے نکال دے تو ہم نے اس کو اور جو اس کے ساتھ تھے سب کو ڈبو دیا');
INSERT INTO "jalandhari" VALUES(17,104,'17:104','اور اس کے بعد بنی اسرائیل سے کہا کہ تم اس ملک میں رہو سہو۔ پھر جب آخرت کا وعدہ آجائے گا تو ہم تم سب کو جمع کرکے لے آئیں گے');
INSERT INTO "jalandhari" VALUES(17,105,'17:105','اور ہم نے اس قرآن کو سچائی کے ساتھ نازل کیا ہے اور وہ سچائی کے ساتھ نازل ہوا اور (اے محمدصلى الله عليه وسلم) ہم نے تم کو صرف خوشخبری دینے والا اور ڈر سنانے والا بنا کر بھیجا ہے');
INSERT INTO "jalandhari" VALUES(17,106,'17:106','اور ہم نے قرآن کو جزو جزو کرکے نازل کیا ہے تاکہ تم لوگوں کو ٹھیر ٹھیر کر پڑھ کر سناؤ اور ہم نے اس کو آہستہ آہستہ اُتارا ہے');
INSERT INTO "jalandhari" VALUES(17,107,'17:107','کہہ دو کہ تم اس پر ایمان لاؤ یا نہ لاؤ (یہ فی نفسہ حق ہے) جن لوگوں کو اس سے پہلے علم (کتاب) دیا ہے۔ جب وہ ان کو پڑھ کر سنایا جاتا ہے تو وہ تھوڑیوں کے بل سجدے میں گر پڑتے ہیں');
INSERT INTO "jalandhari" VALUES(17,108,'17:108','اور کہتے ہیں کہ ہمارا پروردگار پاک ہے بےشک ہمارے پروردگار کا وعدہ پورا ہو کر رہا');
INSERT INTO "jalandhari" VALUES(17,109,'17:109','اور وہ تھوڑیوں کے بل گر پڑتے ہیں (اور) روتے جاتے ہیں اور اس سے ان کو اور زیادہ عاجزی پیدا ہوتی ہے');
INSERT INTO "jalandhari" VALUES(17,110,'17:110','کہہ دو کہ تم (خدا کو) الله (کے نام سے) پکارو یا رحمٰن (کے نام سے) جس نام سے پکارو اس کے سب اچھے نام ہیں۔ اور نماز نہ بلند آواز سے پڑھو اور نہ آہستہ بلکہ اس کے بیچ کا طریقہ اختیار کرو');
INSERT INTO "jalandhari" VALUES(17,111,'17:111','اور کہو کہ سب تعریف خدا ہی کو ہے جس نے نہ تو کسی کو بیٹا بنایا ہے اور نہ اس کی بادشاہی میں کوئی شریک ہے اور نہ اس وجہ سے کہ وہ عاجز وناتواں ہے کوئی اس کا مددگار ہے اور اس کو بڑا جان کر اس کی بڑائی کرتے رہو');
INSERT INTO "jalandhari" VALUES(18,1,'18:1','سب تعریف خدا ہی کو ہے جس نے اپنے بندے (محمدصلى الله عليه وسلم) پر (یہ) کتاب نازل کی اور اس میں کسی طرح کی کجی (اور پیچیدگی) نہ رکھی');
INSERT INTO "jalandhari" VALUES(18,2,'18:2','سیدھی (اور سلیس اتاری) تاکہ لوگوں کو عذاب سخت سے جو اس کی طرف سے (آنے والا) ہے ڈرائے اور مومنوں کو جو نیک عمل کرتے ہیں خوشخبری سنائے کہ اُن کے لئے (ان کے کاموں کا) نیک بدلہ (یعنی) بہشت ہے');
INSERT INTO "jalandhari" VALUES(18,3,'18:3','جس میں وہ ابدا لاآباد رہیں گے');
INSERT INTO "jalandhari" VALUES(18,4,'18:4','اور ان لوگوں کو بھی ڈرائے جو کہتے ہیں کہ خدا نے (کسی کو) بیٹا بنا لیا ہے');
INSERT INTO "jalandhari" VALUES(18,5,'18:5','ان کو اس بات کا کچھ بھی علم نہیں اور نہ ان کے باپ دادا ہی کو تھا۔ (یہ) بڑی سخت بات ہے جو ان کے منہ سے نکلتی ہے (اور کچھ شک نہیں) کہ یہ جو کہتے ہیں محض جھوٹ ہے');
INSERT INTO "jalandhari" VALUES(18,6,'18:6','(اے پیغمبر) اگر یہ اس کلام پر ایمان نہ لائیں تو شاید تم کے ان پیچھے رنج کر کر کے اپنے تئیں ہلاک کردو گے');
INSERT INTO "jalandhari" VALUES(18,7,'18:7','جو چیز زمین پر ہے ہم نے اس کو زمین کے لئے آرائش بنایا ہے تاکہ لوگوں کی آزمائش کریں کہ ان میں کون اچھے عمل کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(18,8,'18:8','اور جو چیز زمین پر ہے ہم اس کو (نابود کرکے) بنجر میدان کردیں گے');
INSERT INTO "jalandhari" VALUES(18,9,'18:9','کیا تم خیال کرتے ہو کہ غار اور لوح والے ہمارے نشانیوں میں سے عجیب تھے');
INSERT INTO "jalandhari" VALUES(18,10,'18:10','جب وہ جوان غار میں جا رہے تو کہنے لگے کہ اے ہمارے پروردگار ہم پر اپنے ہاں سے رحمت نازل فرما۔ اور ہمارے کام درستی (کے سامان) مہیا کر');
INSERT INTO "jalandhari" VALUES(18,11,'18:11','تو ہم نے غار میں کئی سال تک ان کے کانوں پر (نیند کا) پردہ ڈالے (یعنی ان کو سلائے) رکھا');
INSERT INTO "jalandhari" VALUES(18,12,'18:12','پھر ان کو جگا اُٹھایا تاکہ معلوم کریں کہ جتنی مدّت وہ (غار میں) رہے دونوں جماعتوں میں سے اس کی مقدار کس کو خوب یاد ہے');
INSERT INTO "jalandhari" VALUES(18,13,'18:13','ہم اُن کے حالات تم سے صحیح صحیح بیان کرتے ہیں۔ وہ کئی جوان تھے جو اپنے پروردگار پر ایمان لائے تھے اور ہم نے ان کو اور زیادہ ہدایت دی تھی');
INSERT INTO "jalandhari" VALUES(18,14,'18:14','اور ان کے دلوں کو مربوط (یعنی مضبوط) کردیا۔ جب وہ (اُٹھ) کھڑے ہوئے تو کہنے لگے کہ ہمارا پروردگار آسمانوں اور زمین کا مالک ہے۔ ہم اس کے سوا کسی کو معبود (سمجھ کر) نہ پکاریں گے (اگر ایسا کیا) تو اس وقت ہم نے بعید از عقل بات کہی');
INSERT INTO "jalandhari" VALUES(18,15,'18:15','ان ہماری قوم کے لوگوں نے اس کے سوا اور معبود بنا رکھے ہیں۔ بھلا یہ ان (کے خدا ہونے) پر کوئی کھلی دلیل کیوں نہیں لاتے۔ تو اس سے زیادہ کون ظالم ہے جو خدا پر جھوٹ افتراء کرے');
INSERT INTO "jalandhari" VALUES(18,16,'18:16','اور جب تم نے ان (مشرکوں) سے اور جن کی یہ خدا کے سوا عبادت کرتے ہیں ان سے کنارہ کرلیا ہے تو غار میں چل رہو تمہارا پروردگار تمہارے لئے اپنی رحمت وسیع کردے گا اور تمہارے کاموں میں آسانی (کے سامان) مہیا کرے گا');
INSERT INTO "jalandhari" VALUES(18,17,'18:17','اور جب سورج نکلے تو تم دیکھو کہ (دھوپ) ان کے غار سے داہنی طرف سمٹ جائے اور جب غروب ہو تو ان سے بائیں طرف کترا جائے اور وہ اس کے میدان میں تھے۔ یہ خدا کی نشانیوں میں سے ہیں۔ جس کو خدا ہدایت دے یا وہ ہدایت یاب ہے اور جس کو گمراہ کرے تو تم اس کے لئے کوئی دوست راہ بتانے والا نہ پاؤ گے');
INSERT INTO "jalandhari" VALUES(18,18,'18:18','اور تم ان کو خیال کرو کہ جاگ رہے ہیں حالانکہ وہ سوتے ہیں۔ اور ہم ان کو دائیں اور بائیں کروٹ بدلاتے تھے۔ اور ان کا کتا چوکھٹ پر دونوں ہاتھ پھیلائے ہوئے تھا۔ اگر تم ان کو جھانک کر دیکھتے تو پیٹھ پھیر کر بھاگ جاتے اور ان سے دہشت میں آجاتے');
INSERT INTO "jalandhari" VALUES(18,19,'18:19','اور اس طرح ہم نے ان کو اٹھایا تاکہ آپس میں ایک دوسرے سے دریافت کریں۔ ایک کہنے والے نے کہا کہ تم (یہاں) کتنی مدت رہے؟ انہوں نے کہا کہ ایک دن یا اس سے بھی کم۔ انہوں نے کہا کہ جتنی مدت تم رہے ہو تمہارا پروردگار ہی اس کو خوب جانتا ہے۔ تو اپنے میں سے کسی کو یہ روپیہ دے کر شہر کو بھیجو وہ دیکھے کہ نفیس کھانا کون سا ہے تو اس میں سے کھانا لے آئے اور آہستہ آہستہ آئے جائے اور تمہارا حال کسی کو نہ بتائے');
INSERT INTO "jalandhari" VALUES(18,20,'18:20','اگر وہ تم پر دسترس پالیں گے تو تمہیں سنگسار کردیں گے یا پھر اپنے مذہب میں داخل کرلیں گے اور اس وقت تم کبھی فلاح نہیں پاؤ گے');
INSERT INTO "jalandhari" VALUES(18,21,'18:21','اور اسی طرح ہم نے (لوگوں کو) ان (کے حال) سے خبردار کردیا تاکہ وہ جانیں کہ خدا کا وعدہ سچا ہے اور یہ کہ قیامت (جس کا وعدہ کیا جاتا ہے) اس میں کچھ شک نہیں۔ اس وقت لوگ ان کے بارے میں باہم جھگڑنے لگے اور کہنے لگے کہ ان (کے غار) پر عمارت بنا دو۔ ان کا پروردگار ان (کے حال) سے خوب واقف ہے۔ جو لوگ ان کے معاملے میں غلبہ رکھتے تھے وہ کہنے لگے کہ ہم ان (کے غار) پر مسجد بنائیں گے');
INSERT INTO "jalandhari" VALUES(18,22,'18:22','(بعض لوگ) اٹکل پچو کہیں گے کہ وہ تین تھے (اور) چوتھا ان کا کتّا تھا۔ اور (بعض) کہیں گے کہ وہ پانچ تھے اور چھٹا ان کا کتّا تھا۔ اور (بعض) کہیں گے کہ وہ سات تھے اور آٹھواں ان کا کتّا تھا۔ کہہ دو کہ میرا پروردگار ہی ان کے شمار سے خوب واقف ہے ان کو جانتے بھی ہیں تو تھوڑے ہی لوگ (جانتے ہیں) تو تم ان (کے معاملے) میں گفتگو نہ کرنا مگر سرسری سی گفتگو۔ اور نہ ان کے بارے میں ان میں کسی سے کچھ دریافت ہی کرنا');
INSERT INTO "jalandhari" VALUES(18,23,'18:23','اور کسی کام کی نسبت نہ کہنا کہ میں اسے کل کردوں گا');
INSERT INTO "jalandhari" VALUES(18,24,'18:24','مگر (انشاء الله کہہ کر یعنی اگر) خدا چاہے تو (کردوں گا) اور جب خدا کا نام لینا بھول جاؤ تو یاد آنے پر لے لو۔ اور کہہ دو کہ امید ہے کہ میرا پروردگار مجھے اس سے بھی زیادہ ہدایت کی باتیں بتائے');
INSERT INTO "jalandhari" VALUES(18,25,'18:25','اور اصحاب کہف اپنے غار میں نو اوپر تین سو سال رہے');
INSERT INTO "jalandhari" VALUES(18,26,'18:26','کہہ دو کہ جتنی مدّت وہ رہے اسے خدا ہی خوب جانتا ہے۔ اسی کو آسمانوں اور زمین کی پوشیدہ باتیں (معلوم) ہیں۔ وہ کیا خوب دیکھنے والا اور کیا خوب سننے والا ہے۔ اس کے سوا ان کا کوئی کارساز نہیں اور نہ وہ اپنے حکم میں کسی شریک کو کرتا ہے');
INSERT INTO "jalandhari" VALUES(18,27,'18:27','اور اپنے پروردگار کی کتاب جو تمہارے پاس بھیجی جاتی ہے پڑھتے رہا کرو۔ اس کی باتوں کو کوئی بدلنے والا نہیں۔ اور اس کے سوا تم کہیں پناہ کی جگہ بھی نہیں پاؤ گے');
INSERT INTO "jalandhari" VALUES(18,28,'18:28','اور جو لوگ صبح و شام اپنے پروردگار کو پکارتے اور اس کی خوشنودی کے طالب ہیں۔ ان کے ساتھ صبر کرتے رہو۔ اور تمہاری نگاہیں ان میں (گزر کر اور طرف) نہ دوڑیں کہ تم آرائشِ زندگانی دنیا کے خواستگار ہوجاؤ۔ اور جس شخص کے دل کو ہم نے اپنی یاد سے غافل کردیا ہے اور وہ اپنی خواہش کی پیروی کرتا ہے اور اس کا کام حد سے بڑھ گیا ہے اس کا کہا نہ ماننا');
INSERT INTO "jalandhari" VALUES(18,29,'18:29','اور کہہ دو کہ (لوگو) یہ قرآن تمہارے پروردگار کی طرف سے برحق ہے تو جو چاہے ایمان لائے اور جو چاہے کافر رہے۔ ہم نے ظالموں کے لئے دوزخ کی آگ تیار کر رکھی ہے جس کی قناتیں ان کو گھیر رہی ہوں گی۔ اور اگر فریاد کریں گے تو ایسے کھولتے ہوئے پانی سے ان کی دادرسی کی جائے گی (جو) پگھلے ہوئے تانبے کی طرح (گرم ہوگا اور جو) مونہوں کو بھون ڈالے گا (ان کے پینے کا) پانی بھی برا اور آرام گاہ بھی بری');
INSERT INTO "jalandhari" VALUES(18,30,'18:30','(اور) جو ایمان لائے اور کام بھی نیک کرتے رہے تو ہم نیک کام کرنے والوں کا اجر ضائع نہیں کرتے');
INSERT INTO "jalandhari" VALUES(18,31,'18:31','ایسے لوگوں کے لئے ہمیشہ رہنے کے باغ ہیں جن میں ان کے (محلوں کے) نیچے نہریں بہہ رہی ہیں ان کو وہاں سونے کے کنگن پہنائے جائیں گے اور وہ باریک دیبا اور اطلس کے سبز کپڑے پہنا کریں گے (اور) تختوں پر تکیئے لگا کر بیٹھا کریں گے۔ (کیا) خوب بدلہ اور (کیا) خوب آرام گاہ ہے');
INSERT INTO "jalandhari" VALUES(18,32,'18:32','اور ان سے دو شخصوں کا حال بیان کرو جن میں سے ایک ہم نے انگور کے دو باغ (عنایت) کئے تھے اور ان کے گردا گرد کھجوروں کے درخت لگا دیئے تھے اور ان کے درمیان کھیتی پیدا کردی تھی');
INSERT INTO "jalandhari" VALUES(18,33,'18:33','دونوں باغ (کثرت سے) پھل لاتے۔ اور اس (کی پیداوار) میں کسی طرح کی کمی نہ ہوتی اور دونوں میں ہم نے ایک نہر بھی جاری کر رکھی تھی');
INSERT INTO "jalandhari" VALUES(18,34,'18:34','اور (اس طرح) اس (شخص) کو (ان کی) پیداوار (ملتی رہتی) تھی تو (ایک دن) جب کہ وہ اپنے دوست سے باتیں کر رہا تھا کہنے لگا کہ میں تم سے مال ودولت میں بھی زیادہ ہوں اور جتھے (اور جماعت) کے لحاظ سے بھی زیادہ عزت والا ہوں');
INSERT INTO "jalandhari" VALUES(18,35,'18:35','اور (ایسی شیخیوں) سے اپنے حق میں ظلم کرتا ہوا اپنے باغ میں داخل ہوا۔ کہنے لگا کہ میں نہیں خیال کرتا کہ یہ باغ کبھی تباہ ہو');
INSERT INTO "jalandhari" VALUES(18,36,'18:36','اور نہ خیال کرتا ہوں کہ قیامت برپا ہو۔ اور اگر میں اپنے پروردگار کی طرف لوٹایا بھی جاؤں تو (وہاں) ضرور اس سے اچھی جگہ پاؤں گا');
INSERT INTO "jalandhari" VALUES(18,37,'18:37','تو اس کا دوست جو اس سے گفتگو کر رہا تھا کہنے لگا کہ کیا تم اس (خدا) سے کفر کرتے ہو جس نے تم کو مٹی سے پیدا کیا پھر نطفے سے پھر تمہیں پورا مرد بنایا');
INSERT INTO "jalandhari" VALUES(18,38,'18:38','مگر میں تو یہ کہتا ہوں کہ خدا ہی میرا پروردگار ہے اور میں اپنے پروردگار کے ساتھ کسی کو شریک نہیں کرتا');
INSERT INTO "jalandhari" VALUES(18,39,'18:39','اور (بھلا) جب تم اپنے باغ میں داخل ہوئے تو تم نے ماشاالله لاقوة الابالله کیوں نہ کہا۔ اگر تم مجھے مال واولاد میں اپنے سے کمتر دیکھتے ہو');
INSERT INTO "jalandhari" VALUES(18,40,'18:40','تو عجب نہیں کہ میرا پروردگار مجھے تمہارے باغ سے بہتر عطا فرمائے اور اس (تمہارے باغ) پر آسمان سے آفت بھیج دے تو وہ صاف میدان ہوجائے');
INSERT INTO "jalandhari" VALUES(18,41,'18:41','یا اس (کی نہر) کا پانی گہرا ہوجائے تو پھر تم اسے نہ لاسکو');
INSERT INTO "jalandhari" VALUES(18,42,'18:42','اور اس کے میووں کو عذاب نے آگھیرا اور وہ اپنی چھتریوں پر گر کر رہ گیا۔ تو جو مال اس نے اس پر خرچ کیا تھا اس پر (حسرت سے) ہاتھ ملنے لگا اور کہنے لگا کہ کاش میں اپنے پروردگار کے ساتھ کسی کو شریک نہ بناتا');
INSERT INTO "jalandhari" VALUES(18,43,'18:43','(اس وقت) خدا کے سوا کوئی جماعت اس کی مددگار نہ ہوئی اور نہ وہ بدلہ لے سکا');
INSERT INTO "jalandhari" VALUES(18,44,'18:44','یہاں (سے ثابت ہوا کہ) حکومت سب خدائے برحق ہی کی ہے۔ اسی کا صلہ بہتر اور (اسی کا) بدلہ اچھا ہے');
INSERT INTO "jalandhari" VALUES(18,45,'18:45','اور ان سے دنیا کی زندگی کی مثال بھی بیان کردو (وہ ایسی ہے) جیسے پانی جسے ہم نے آسمان سے برسایا۔ تو اس کے ساتھ زمین کی روئیدگی مل گئی۔ پھر وہ چورا چورا ہوگئی کہ ہوائیں اسے اڑاتی پھرتی ہیں۔ اور خدا تو ہر چیز پر قدرت رکھتا ہے');
INSERT INTO "jalandhari" VALUES(18,46,'18:46','مال اور بیٹے تو دنیا کی زندگی کی (رونق و) زینت ہیں۔ اور نیکیاں جو باقی رہنے والی ہیں وہ ثواب کے لحاظ سے تمہارے پروردگار کے ہاں بہت اچھی اور امید کے لحاظ سے بہت بہتر ہیں');
INSERT INTO "jalandhari" VALUES(18,47,'18:47','اور جس دن ہم پہاڑوں کو چلائیں گے اور تم زمین کو صاف میدان دیکھو گے اور ان (لوگوں کو) ہم جمع کرلیں گے تو ان میں سے کسی کو بھی نہیں چھوڑیں گے');
INSERT INTO "jalandhari" VALUES(18,48,'18:48','اور سب تمہارے پروردگار کے سامنے صف باندھ کر لائے جائیں گے (تو ہم ان سے کہیں گے کہ) جس طرح ہم نے تم کو پہلی بار پیدا کیا تھا (اسی طرح آج) تم ہمارے سامنے آئے لیکن تم نے تو یہ خیال کر رکھا تھا کہ ہم نے تمہارے لئے (قیامت کا) کوئی وقت مقرر ہی نہیں کیا');
INSERT INTO "jalandhari" VALUES(18,49,'18:49','اور (عملوں کی) کتاب (کھول کر) رکھی جائے گی تو تم گنہگاروں کو دیکھو گے کہ جو کچھ اس میں (لکھا) ہوگا اس سے ڈر رہے ہوں گے اور کہیں گے ہائے شامت یہ کیسی کتاب ہے کہ نہ چھوٹی بات کو چھوڑتی ہے نہ بڑی کو۔ (کوئی بات بھی نہیں) مگر اسے لکھ رکھا ہے۔ اور جو عمل کئے ہوں گے سب کو حاضر پائیں گے۔ اور تمہارا پروردگار کسی پر ظلم نہیں کرے گا');
INSERT INTO "jalandhari" VALUES(18,50,'18:50','اور جب ہم نے فرشتوں کو حکم دیا کہ آدم کو سجدہ کرو تو سب نے سجدہ کیا مگر ابلیس (نے نہ کیا) وہ جنات میں سے تھا تو اپنے پروردگار کے حکم سے باہر ہوگیا۔ کیا تم اس کو اور اس کی اولاد کو میرے سوا دوست بناتے ہو۔ حالانکہ وہ تمہارے دشمن ہیں (اور شیطان کی دوستی) ظالموں کے لئے (خدا کی دوستی کا) برا بدل ہے');
INSERT INTO "jalandhari" VALUES(18,51,'18:51','میں نے ان کو نہ تو آسمانوں اور زمین کے پیدا کرنے کے وقت بلایا تھا اور نہ خود ان کے پیدا کرنے کے وقت۔ اور میں ایسا نہ تھا کہ گمراہ کرنے والوں کو مددگار بناتا');
INSERT INTO "jalandhari" VALUES(18,52,'18:52','اور جس دن خدا فرمائے گا کہ (اب) میرے شریکوں کو جن کی نسبت تم گمان (الوہیت) رکھتے تھے بلاؤ تو وہ ان کے بلائیں گے مگر وہ ان کو کچھ جواب نہ دیں گے۔ اور ہم ان کے بیچ میں ایک ہلاکت کی جگہ بنادیں گے');
INSERT INTO "jalandhari" VALUES(18,53,'18:53','اور گنہگار لوگ دوزخ کو دیکھیں گے تو یقین کرلیں گے کہ وہ اس میں پڑنے والے ہیں۔ اور اس سے بچنے کا کوئی رستہ نہ پائیں گے');
INSERT INTO "jalandhari" VALUES(18,54,'18:54','اور ہم نے اس قرآن میں لوگوں (کے سمجھانے) کے لئے طرح طرح کی مثالیں بیان فرمائی ہیں۔ لیکن انسان سب چیزوں سے بڑھ کر جھگڑالو ہے');
INSERT INTO "jalandhari" VALUES(18,55,'18:55','اور لوگوں کے پاس جب ہدایت آگئی تو ان کو کس چیز نے منع کیا کہ ایمان لائیں۔ اور اپنے پروردگار سے بخشش مانگیں۔ بجز اس کے کہ (اس بات کے منتظر ہوں کہ) انہیں بھی پہلوں کا سا معاملہ پیش آئے یا ان پر عذاب سامنے آموجود ہو');
INSERT INTO "jalandhari" VALUES(18,56,'18:56','اور ہم جو پیغمبروں کو بھیجا کرتے ہیں تو صرف اس لئے کہ (لوگوں کو خدا کی نعمتوں کی) خوشخبریاں سنائیں اور (عذاب سے) ڈرائیں۔ اور جو کافر ہیں وہ باطل کی (سند) سے جھگڑا کرتے ہیں تاکہ اس سے حق کو پھسلا دیں اور انہوں نے ہماری آیتوں کو اور جس چیز سے ان کو ڈرایا جاتا ہے ہنسی بنا لیا');
INSERT INTO "jalandhari" VALUES(18,57,'18:57','اور اس سے ظالم کون جس کو اس کے پروردگار کے کلام سے سمجھایا گیا تو اُس نے اس سے منہ پھیر لیا۔ اور جو اعمال وہ آگے کرچکا اس کو بھول گیا۔ ہم نے ان کے دلوں پر پردے ڈال دیئے کہ اسے سمجھ نہ سکیں۔ اور کانوں میں ثقل (پیدا کردیا ہے کہ سن نہ سکیں) اور اگر تم ان کو رستے کی طرف بلاؤ تو کبھی رستے پر نہ آئیں گے');
INSERT INTO "jalandhari" VALUES(18,58,'18:58','اور تمہارا پروردگار بخشنے والا صاحب رحمت ہے۔ اگر وہ ان کے کرتوتوں پر ان کو پکڑنے لگے تو ان پر جھٹ عذاب بھیج دے۔ مگر ان کے لئے ایک وقت (مقرر کر رکھا) ہے کہ اس کے عذاب سے کوئی پناہ کی جگہ نہ پائیں گے');
INSERT INTO "jalandhari" VALUES(18,59,'18:59','اور یہ بستیاں (جو ویران پڑی ہیں) جب انہوں نے (کفر سے) ظلم کیا تو ہم نے ان کو تباہ کر دیا۔ اور ان کی تباہی کے لئے ایک وقت مقرر کردیا تھا');
INSERT INTO "jalandhari" VALUES(18,60,'18:60','اور جب موسیٰ نے اپنے شاگرد سے کہا کہ جب تک دو دریاؤں کے ملنے کی جگہ نہ پہنچ جاؤں ہٹنے کا نہیں خواہ برسوں چلتا رہوں');
INSERT INTO "jalandhari" VALUES(18,61,'18:61','جب ان کے ملنے کے مقام پر پہنچے تو اپنی مچھلی بھول گئے تو اس نے دریا میں سرنگ کی طرح اپنا رستہ بنالیا');
INSERT INTO "jalandhari" VALUES(18,62,'18:62','جب آگے چلے تو (موسیٰ نے) اپنے شاگرد سے کہا کہ ہمارے لئے کھانا لاؤ۔ اس سفر سے ہم کو بہت تکان ہوگئی ہے');
INSERT INTO "jalandhari" VALUES(18,63,'18:63','(اس نے) کہا کہ بھلا آپ نے دیکھا کہ جب ہم نے پتھر کے ساتھ آرام کیا تھا تو میں مچھلی (وہیں) بھول گیا۔ اور مجھے (آپ سے) اس کا ذکر کرنا شیطان نے بھلا دیا۔ اور اس نے عجب طرح سے دریا میں اپنا رستہ لیا');
INSERT INTO "jalandhari" VALUES(18,64,'18:64','(موسیٰ نے) کہا یہی تو (وہ مقام) ہے جسے ہم تلاش کرتے تھے تو وہ اپنے پاؤں کے نشان دیکھتے دیکھتے لوٹ گئے');
INSERT INTO "jalandhari" VALUES(18,65,'18:65','(وہاں) انہوں نے ہمارے بندوں میں سے ایک بندہ دیکھا جس کو ہم نے اپنے ہاں سے رحمت (یعنی نبوت یا نعمت ولایت) دی تھی اور اپنے پاس سے علم بخشا تھا');
INSERT INTO "jalandhari" VALUES(18,66,'18:66','موسیٰ نے ان سے (جن کا نام خضر تھا) کہا کہ جو علم (خدا کی طرف سے) آپ کو سکھایا گیا ہے اگر آپ اس میں سے مجھے کچھ بھلائی (کی باتیں) سکھائیں تو میں آپ کے ساتھ رہوں');
INSERT INTO "jalandhari" VALUES(18,67,'18:67','(خضر نے) کہا کہ تم میرے ساتھ رہ کر صبر نہیں کرسکو گے');
INSERT INTO "jalandhari" VALUES(18,68,'18:68','اور جس بات کی تمہیں خبر ہی نہیں اس پر صبر کر بھی کیوں کرسکتے ہو');
INSERT INTO "jalandhari" VALUES(18,69,'18:69','(موسیٰ نے) کہا خدا نے چاہا تو آپ مجھے صابر پایئے گا۔ اور میں آپ کے ارشاد کے خلاف نہیں کروں گا');
INSERT INTO "jalandhari" VALUES(18,70,'18:70','(خضر نے) کہا کہ اگر تم میرے ساتھ رہنا چاہو تو (شرط یہ ہے) مجھ سے کوئی بات نہ پوچھنا جب تک میں خود اس کا ذکر تم سے نہ کروں');
INSERT INTO "jalandhari" VALUES(18,71,'18:71','تو دونوں چل پڑے۔ یہاں تک کہ جب کشتی میں سوار ہوئے تو (خضر نے) کشتی کو پھاڑ ڈالا۔ (موسیٰ نے) کہا کیا آپ نے اس لئے پھاڑا ہے کہ سواروں کو غرق کردیں یہ تو آپ نے بڑی (عجیب) بات کی');
INSERT INTO "jalandhari" VALUES(18,72,'18:72','(خضر نے) کہا۔ کیا میں نے نہیں کہا تھا کہ تم میرے ساتھ صبر نہ کرسکو گے');
INSERT INTO "jalandhari" VALUES(18,73,'18:73','(موسیٰ نے) کہا کہ جو بھول مجھ سے ہوئی اس پر مواخذہ نہ کیجیئے اور میرے معاملے میں مجھ پر مشکل نہ ڈالئے');
INSERT INTO "jalandhari" VALUES(18,74,'18:74','پھر دونوں چلے۔ یہاں تک کہ (رستے میں) ایک لڑکا ملا تو (خضر نے) اُسے مار ڈالا۔ (موسیٰ نے) کہا کہ آپ نے ایک بےگناہ شخص کو ناحق بغیر قصاص کے مار ڈالا۔ (یہ تو) آپ نے بری بات کی');
INSERT INTO "jalandhari" VALUES(18,75,'18:75','(خضر نے) کہا کیا میں نے نہیں کہا تھا کہ تم سے میرے ساتھ صبر نہیں کرسکو گے');
INSERT INTO "jalandhari" VALUES(18,76,'18:76','انہوں نے کہا کہ اگر میں اس کے بعد (پھر) کوئی بات پوچھوں (یعنی اعتراض کروں) تو مجھے اپنے ساتھ نہ رکھیئے گا کہ آپ میری طرف سے عذر (کے قبول کرنے میں غایت) کو پہنچ گئے');
INSERT INTO "jalandhari" VALUES(18,77,'18:77','پھر دونوں چلے۔ یہاں تک کہ ایک گاؤں والوں کے پاس پہنچے اور ان سے کھانا طلب کیا۔ انہوں نے ان کی ضیافت کرنے سے انکار کر دیا۔ پھر انہوں نے وہاں ایک دیوار دیکھی جو (جھک کر) گرا چاہتی تھی۔ خضر نے اس کو سیدھا کر دیا۔ موسیٰ نے کہا اگر آپ چاہتے تو ان سے (اس کا) معاوضہ لیتے (تاکہ کھانے کا کام چلتا)');
INSERT INTO "jalandhari" VALUES(18,78,'18:78','خضر نے کہا اب مجھ میں اور تجھ میں علیحدگی۔ (مگر) جن باتوں پر تم صبر نہ کرسکے میں ان کا تمہیں بھید بتائے دیتا ہوں');
INSERT INTO "jalandhari" VALUES(18,79,'18:79','(کہ وہ جو) کشتی (تھی) غریب لوگوں کی تھی جو دریا میں محنت (کرکے یعنی کشتیاں چلا کر گذارہ) کرتے تھے۔ اور ان کے سامنے (کی طرف) ایک بادشاہ تھا جو ہر ایک کشتی کو زبردستی چھین لیتا تھا تو میں نے چاہا کہ اسے عیب دار کردوں (تاکہ وہ اسے غصب نہ کرسکے)');
INSERT INTO "jalandhari" VALUES(18,80,'18:80','اور وہ جو لڑکا تھا اس کے ماں باپ دنوں مومن تھے ہمیں اندیشہ ہوا کہ (وہ بڑا ہو کر بدکردار ہوتا کہیں) ان کو سرکشی اور کفر میں نہ پھنسا دے');
INSERT INTO "jalandhari" VALUES(18,81,'18:81','تو ہم نے چاہا کہ ان کا پروردگار اس کی جگہ ان کو اور (بچّہ) عطا فرمائے جو پاک طینتی میں اور محبت میں اس سے بہتر ہو');
INSERT INTO "jalandhari" VALUES(18,82,'18:82','اور وہ جو دیوار تھی سو وہ دو یتیم لڑکوں کی تھی (جو) شہر میں (رہتے تھے) اور اس کے نیچے ان کا خزانہ (مدفون) تھا اور ان کا باپ ایک نیک بخت آدمی تھا۔ تو تمہارے پروردگار نے چاہا کہ وہ اپنی جوانی کو پہنچ جائیں اور (پھر) اپنا خزانہ نکالیں۔ یہ تمہارے پروردگار کی مہربانی ہے۔ اور یہ کام میں نے اپنی طرف سے نہیں کئے۔ یہ ان باتوں کا راز ہے جن پر تم صبر نہ کرسکے');
INSERT INTO "jalandhari" VALUES(18,83,'18:83','اور تم سے ذوالقرنین کے بارے میں دریافت کرتے ہیں۔ کہہ دو کہ میں اس کا کسی قدر حال تمہیں پڑھ کر سناتا ہوں');
INSERT INTO "jalandhari" VALUES(18,84,'18:84','ہم نے اس کو زمین میں بڑی دسترس دی تھی اور ہر طرح کا سامان عطا کیا تھا');
INSERT INTO "jalandhari" VALUES(18,85,'18:85','تو اس نے (سفر کا) ایک سامان کیا');
INSERT INTO "jalandhari" VALUES(18,86,'18:86','یہاں تک کہ جب سورج کے غروب ہونے کی جگہ پہنچا تو اسے ایسا پایا کہ ایک کیچڑ کی ندی میں ڈوب رہا ہے اور اس (ندی) کے پاس ایک قوم دیکھی۔ ہم نے کہا ذوالقرنین! تم ان کو خواہ تکلیف دو خواہ ان (کے بارے) میں بھلائی اختیار کرو (دونوں باتوں میں تم کو قدرت ہے)');
INSERT INTO "jalandhari" VALUES(18,87,'18:87','ذوالقرنین نے کہا کہ جو (کفر وبدکرداری سے) ظلم کرے گا اسے ہم عذاب دیں گے۔ پھر (جب) وہ اپنے پروردگار کی طرف لوٹایا جائے گا تو وہ بھی اسے بُرا عذاب دے گا');
INSERT INTO "jalandhari" VALUES(18,88,'18:88','اور جو ایمان لائے گا اور عمل نیک کرے گا اس کے لئے بہت اچھا بدلہ ہے۔ اور ہم اپنے معاملے میں (اس پر کسی طرح کی سختی نہیں کریں گے بلکہ) اس سے نرم بات کہیں گے');
INSERT INTO "jalandhari" VALUES(18,89,'18:89','پھر اس نے ایک اور سامان (سفر کا) کیا');
INSERT INTO "jalandhari" VALUES(18,90,'18:90','یہاں تک کہ سورج کے طلوع ہونے کے مقام پر پہنچا تو دیکھا کہ وہ ایسے لوگوں پر طلوع کرتا ہے جن کے لئے ہم نے سورج کے اس طرف کوئی اوٹ نہیں بنائی تھی');
INSERT INTO "jalandhari" VALUES(18,91,'18:91','(حقیقت حال) یوں (تھی) اور جو کچھ اس کے پاس تھا ہم کو سب کی خبر تھی');
INSERT INTO "jalandhari" VALUES(18,92,'18:92','پھر اس نے ایک اور سامان کیا');
INSERT INTO "jalandhari" VALUES(18,93,'18:93','یہاں تک کہ دو دیواروں کے درمیان پہنچا تو دیکھا کہ ان کے اس طرف کچھ لوگ ہیں کہ بات کو سمجھ نہیں سکتے');
INSERT INTO "jalandhari" VALUES(18,94,'18:94','ان لوگوں نے کہا ذوالقرنین! یاجوج اور ماجوج زمین میں فساد کرتے رہتے ہیں بھلا ہم آپ کے لئے خرچ (کا انتظام) کردیں کہ آپ ہمارے اور ان کے درمیان ایک دیوار کھینچ دیں');
INSERT INTO "jalandhari" VALUES(18,95,'18:95','ذوالقرنین نے کہا کہ خرچ کا جو مقدور خدا نے مجھے بخشا ہے وہ بہت اچھا ہے۔ تم مجھے قوت (بازو) سے مدد دو۔ میں تمہارے اور ان کے درمیان ایک مضبوط اوٹ بنا دوں گا');
INSERT INTO "jalandhari" VALUES(18,96,'18:96','تو تم لوہے کے (بڑے بڑے) تختے لاؤ (چنانچہ کام جاری کردیا گیا) یہاں تک کہ جب اس نے دونوں پہاڑوں کے درمیان (کا حصہ) برابر کر دیا۔ اور کہا کہ (اب اسے) دھونکو۔ یہاں تک کہ جب اس کو (دھونک دھونک) کر آگ کر دیا تو کہا کہ (اب) میرے پاس تانبہ لاؤ اس پر پگھلا کر ڈال دوں');
INSERT INTO "jalandhari" VALUES(18,97,'18:97','پھر ان میں یہ قدرت نہ رہی کہ اس پر چڑھ سکیں اور نہ یہ طاقت رہی کہ اس میں نقب لگا سکیں');
INSERT INTO "jalandhari" VALUES(18,98,'18:98','بولا کہ یہ میرے پروردگار کی مہربانی ہے۔ جب میرے پروردگار کا وعدہ آپہنچے گا تو اس کو (ڈھا کر) ہموار کردے گا اور میرے پروردگار کا وعدہ سچا ہے');
INSERT INTO "jalandhari" VALUES(18,99,'18:99','(اس روز) ہم ان کو چھوڑ دیں گے کہ (روئے زمین پر پھیل کر) ایک دوسرے میں گھس جائیں گے اور صور پھونکا جائے گا تو ہم سب کو جمع کرلیں گے');
INSERT INTO "jalandhari" VALUES(18,100,'18:100','اور اُس روز جہنم کو کافروں کے سامنے لائیں گے');
INSERT INTO "jalandhari" VALUES(18,101,'18:101','جن کی آنکھیں میری یاد سے پردے میں تھیں اور وہ سننے کی طاقت نہیں رکھتے تھے');
INSERT INTO "jalandhari" VALUES(18,102,'18:102','کیا کافر یہ خیال کرتے ہیں کہ وہ ہمارے بندوں کو ہمارے سوا (اپنا) کارساز بنائیں گے (تو ہم خفا نہیں ہوں گے) ہم نے (ایسے) کافروں کے لئے جہنم کی (مہمانی) تیار کر رکھی ہے');
INSERT INTO "jalandhari" VALUES(18,103,'18:103','کہہ دو کہ ہم تمہیں بتائیں جو عملوں کے لحاظ سے بڑے نقصان میں ہیں');
INSERT INTO "jalandhari" VALUES(18,104,'18:104','وہ لوگ جن کی سعی دنیا کی زندگی میں برباد ہوگئی۔ اور وہ یہ سمجھے ہوئے ہیں کہ اچھے کام کر رہے ہیں');
INSERT INTO "jalandhari" VALUES(18,105,'18:105','یہ وہ لوگ ہیں جنہوں نے اپنے پروردگار کی آیتوں اور اس کے سامنے جانے سے انکار کیا تو ان کے اعمال ضائع ہوگئے اور ہم قیامت کے دن ان کے لئے کچھ بھی وزن قائم نہیں کریں گے');
INSERT INTO "jalandhari" VALUES(18,106,'18:106','یہ ان کی سزا ہے (یعنی) جہنم۔ اس لئے کہ انہوں نے کفر کیا اور ہماری آیتوں اور ہمارے پیغمبروں کی ہنسی اُڑائی');
INSERT INTO "jalandhari" VALUES(18,107,'18:107','جو لوگ ایمان لائے اور عمل نیک کئے ان کے لئے بہشت کے باغ مہمانی ہوں گے');
INSERT INTO "jalandhari" VALUES(18,108,'18:108','ہمیشہ ان میں رہیں گے اور وہاں سے مکان بدلنا نہ چاہیں گے');
INSERT INTO "jalandhari" VALUES(18,109,'18:109','کہہ دو کہ اگر سمندر میرے پروردگار کی باتوں کے (لکھنے کے) لئے سیاہی ہو تو قبل اس کے کہ میرے پروردگار کی باتیں تمام ہوں سمندر ختم ہوجائے اگرچہ ہم ویسا ہی اور (سمندر) اس کی مدد کو لائیں');
INSERT INTO "jalandhari" VALUES(18,110,'18:110','کہہ دو کہ میں تمہاری کا ایک بشر ہوں۔ (البتہ) میری طرف وحی آتی ہے کہ تمہارا معبود (وہی) ایک معبود ہے۔ تو جو شخص اپنے پروردگار سے ملنے کی امید رکھے چاہیئے کہ عمل نیک کرے اور اپنے پروردگار کی عبادت میں کسی کو شریک نہ بنائے');
INSERT INTO "jalandhari" VALUES(19,1,'19:1','کہیٰعص');
INSERT INTO "jalandhari" VALUES(19,2,'19:2','(یہ) تمہارے پروردگار کی مہربانی کا بیان (ہے جو اس نے) اپنے بندے زکریا پر (کی تھی)');
INSERT INTO "jalandhari" VALUES(19,3,'19:3','جب انہوں نے اپنے پروردگار کو دبی آواز سے پکارا');
INSERT INTO "jalandhari" VALUES(19,4,'19:4','(اور) کہا کہ اے میرے پروردگار میری ہڈیاں بڑھاپے کے سبب کمزور ہوگئی ہیں اور سر (ہے کہ) بڑھاپے (کی وجہ سے) شعلہ مارنے لگا ہے اور اے میرے پروردگار میں تجھ سے مانگ کر کبھی محروم نہیں رہا');
INSERT INTO "jalandhari" VALUES(19,5,'19:5','اور میں اپنے بعد اپنے بھائی بندوں سے ڈرتا ہوں اور میری بیوی بانجھ ہے تو مجھے اپنے پاس سے ایک وارث عطا فرما');
INSERT INTO "jalandhari" VALUES(19,6,'19:6','جو میری اور اولاد یعقوب کی میراث کا مالک ہو۔ اور (اے) میرے پروردگار اس کو خوش اطوار بنائیو');
INSERT INTO "jalandhari" VALUES(19,7,'19:7','اے زکریا ہم تم کو ایک لڑکے کی بشارت دیتے ہیں جس کا نام یحییٰ ہے۔ اس سے پہلے ہم نے اس نام کا کوئی شخص پیدا نہیں کیا');
INSERT INTO "jalandhari" VALUES(19,8,'19:8','انہوں نے کہا پروردگار میرے ہاں کس طرح لڑکا ہوگا۔ جس حال میں میری بیوی بانجھ ہے اور میں بڑھاپے کی انتہا کو پہنچ گیا ہوں');
INSERT INTO "jalandhari" VALUES(19,9,'19:9','حکم ہوا کہ اسی طرح (ہوگا) تمہارے پروردگار نے فرمایا ہے کہ مجھے یہ آسان ہے اور میں پہلے تم کو بھی تو پیدا کرچکا ہوں اور تم کچھ چیز نہ تھے');
INSERT INTO "jalandhari" VALUES(19,10,'19:10','کہا کہ پروردگار میرے لئے کوئی نشانی مقرر فرما۔ فرمایا نشانی یہ ہے کہ تم صحیح وسالم ہو کر تین (رات دن) لوگوں سے بات نہ کرسکو گے');
INSERT INTO "jalandhari" VALUES(19,11,'19:11','پھر وہ (عبادت کے) حجرے سے نکل کر اپنی قوم کے پاس آئے تو ان سے اشارے سے کہا کہ صبح وشام (خدا کو) یاد کرتے رہو');
INSERT INTO "jalandhari" VALUES(19,12,'19:12','اے یحییٰ (ہماری) کتاب کو زور سے پکڑے رہو۔ اور ہم نے ان کو لڑکپن میں دانائی عطا فرمائی تھی');
INSERT INTO "jalandhari" VALUES(19,13,'19:13','اور اپنے پاس شفقت اور پاکیزگی دی تھی۔ اور پرہیزگار تھے');
INSERT INTO "jalandhari" VALUES(19,14,'19:14','اور ماں باپ کے ساتھ نیکی کرنے والے تھے اور سرکش اور نافرمان نہیں تھے');
INSERT INTO "jalandhari" VALUES(19,15,'19:15','اور جس دن وہ پیدا ہوئے اور جس دن وفات پائیں گے اور جس دن زندہ کرکے اٹھائے جائیں گے۔ ان پر سلام اور رحمت (ہے)');
INSERT INTO "jalandhari" VALUES(19,16,'19:16','اور کتاب (قرآن) میں مریم کا بھی مذکور کرو، جب وہ اپنے لوگوں سے الگ ہو کر مشرق کی طرف چلی گئیں');
INSERT INTO "jalandhari" VALUES(19,17,'19:17','تو انہوں نے ان کی طرف سے پردہ کرلیا۔ (اس وقت) ہم نے ان کی طرف اپنا فرشتہ بھیجا۔ تو ان کے سامنے ٹھیک آدمی (کی شکل) بن گیا');
INSERT INTO "jalandhari" VALUES(19,18,'19:18','مریم بولیں کہ اگر تم پرہیزگار ہو تو میں تم سے خدا کی پناہ مانگتی ہوں');
INSERT INTO "jalandhari" VALUES(19,19,'19:19','انہوں نے کہا کہ میں تو تمہارے پروردگار کا بھیجا ہوا (یعنی فرشتہ) ہوں (اور اس لئے آیا ہوں) کہ تمہیں پاکیزہ لڑکا بخشوں');
INSERT INTO "jalandhari" VALUES(19,20,'19:20','مریم نے کہا کہ میرے ہاں لڑکا کیونکر ہوگا مجھے کسی بشر نے چھوا تک نہیں اور میں بدکار بھی نہیں ہوں');
INSERT INTO "jalandhari" VALUES(19,21,'19:21','(فرشتے نے) کہا کہ یونہی (ہوگا) تمہارے پروردگار نے فرمایا کہ یہ مجھے آسان ہے۔ اور (میں اسے اسی طریق پر پیدا کروں گا) تاکہ اس کو لوگوں کے لئے اپنی طرف سے نشانی اور (ذریعہٴ) رحمت اور (مہربانی) بناؤں اور یہ کام مقرر ہوچکا ہے');
INSERT INTO "jalandhari" VALUES(19,22,'19:22','تو وہ اس (بچّے) کے ساتھ حاملہ ہوگئیں اور اسے لے کر ایک دور جگہ چلی گئیں');
INSERT INTO "jalandhari" VALUES(19,23,'19:23','پھر درد زہ ان کو کھجور کے تنے کی طرف لے آیا۔ کہنے لگیں کہ کاش میں اس سے پہلے مرچکتی اور بھولی بسری ہوگئی ہوتی');
INSERT INTO "jalandhari" VALUES(19,24,'19:24','اس وقت ان کے نیچے کی جانب سے فرشتے نے ان کو آواز دی کہ غمناک نہ ہو تمہارے پروردگار نے تمہارے نیچے ایک چشمہ جاری کردیا ہے');
INSERT INTO "jalandhari" VALUES(19,25,'19:25','اور کھجور کے تنے کو پکڑ کر اپنی طرف ہلاؤ تم پر تازہ تازہ کھجوریں جھڑ پڑیں گی');
INSERT INTO "jalandhari" VALUES(19,26,'19:26','تو کھاؤ اور پیو اور آنکھیں ٹھنڈی کرو۔ اگر تم کسی آدمی کو دیکھو تو کہنا کہ میں نے خدا کے لئے روزے کی منت مانی تو آج میں کسی آدمی سے ہرگز کلام نہیں کروں گی');
INSERT INTO "jalandhari" VALUES(19,27,'19:27','پھر وہ اس (بچّے) کو اٹھا کر اپنی قوم کے لوگوں کے پاس لے آئیں۔ وہ کہنے لگے کہ مریم یہ تو تُونے برا کام کیا');
INSERT INTO "jalandhari" VALUES(19,28,'19:28','اے ہارون کی بہن نہ تو تیرا باپ ہی بداطوار آدمی تھا اور نہ تیری ماں ہی بدکار تھی');
INSERT INTO "jalandhari" VALUES(19,29,'19:29','تو مریم نے اس لڑکے کی طرف اشارہ کیا۔ وہ بولے کہ ہم اس سے کہ گود کا بچہ ہے کیونکر بات کریں');
INSERT INTO "jalandhari" VALUES(19,30,'19:30','بچے نے کہا کہ میں خدا کا بندہ ہوں اس نے مجھے کتاب دی ہے اور نبی بنایا ہے');
INSERT INTO "jalandhari" VALUES(19,31,'19:31','اور میں جہاں ہوں (اور جس حال میں ہوں) مجھے صاحب برکت کیا ہے اور جب تک زندہ ہوں مجھ کو نماز اور زکوٰة کا ارشاد فرمایا ہے');
INSERT INTO "jalandhari" VALUES(19,32,'19:32','اور (مجھے) اپنی ماں کے ساتھ نیک سلوک کرنے والا (بنایا ہے) اور سرکش وبدبخت نہیں بنایا');
INSERT INTO "jalandhari" VALUES(19,33,'19:33','اور جس دن میں پیدا ہوا اور جس دن مروں گا اور جس دن زندہ کرکے اٹھایا جاؤں گا مجھ پر سلام (ورحمت) ہے');
INSERT INTO "jalandhari" VALUES(19,34,'19:34','یہ مریم کے بیٹے عیسیٰ ہیں (اور یہ) سچی بات ہے جس میں لوگ شک کرتے ہیں');
INSERT INTO "jalandhari" VALUES(19,35,'19:35','خدا کو سزاوار نہیں کہ کسی کو بیٹا بنائے۔ وہ پاک ہے جب کسی چیز کا ارادہ کرتا ہے تو اس کو یہی کہتا ہے کہ ہوجا تو وہ ہوجاتی ہے');
INSERT INTO "jalandhari" VALUES(19,36,'19:36','اور بےشک خدا ہی میرا اور تمہارا پروردگار ہے تو اسی کی عبادت کرو۔ یہی سیدھا رستہ ہے');
INSERT INTO "jalandhari" VALUES(19,37,'19:37','پھر (اہل کتاب کے) فرقوں نے باہم اختلاف کیا۔ سو جو لوگ کافر ہوئے ہیں ان کو بڑے دن (یعنی قیامت کے روز) حاضر ہونے سے خرابی ہے');
INSERT INTO "jalandhari" VALUES(19,38,'19:38','وہ جس دن ہمارے سامنے آئیں گے۔ کیسے سننے والے اور کیسے دیکھنے والے ہوں گے مگر ظالم آج صریح گمراہی میں ہیں');
INSERT INTO "jalandhari" VALUES(19,39,'19:39','اور ان کو حسرت (وافسوس) کے دن سے ڈراؤ جب بات فیصل کردی جائے گی۔ اور (ہیہات) وہ غفلت میں (پڑے ہوئے ہیں) اور ایمان نہیں لاتے');
INSERT INTO "jalandhari" VALUES(19,40,'19:40','ہم ہی زمین کے اور جو لوگ اس پر (بستے) ہیں ان کے وارث ہیں۔ اور ہماری ہی طرف ان کو لوٹنا ہوگا');
INSERT INTO "jalandhari" VALUES(19,41,'19:41','اور کتاب میں ابراہیم کو یاد کرو۔ بےشک وہ نہایت سچے پیغمبر تھے');
INSERT INTO "jalandhari" VALUES(19,42,'19:42','جب انہوں نے اپنے باپ سے کہا کہ ابّا آپ ایسی چیزوں کو کیوں پوجتے ہیں جو نہ سنیں اور نہ دیکھیں اور نہ آپ کے کچھ کام آسکیں');
INSERT INTO "jalandhari" VALUES(19,43,'19:43','ابّا مجھے ایسا علم ملا ہے جو آپ کو نہیں ملا ہے تو میرے ساتھ ہوجیئے میں آپ کو سیدھی راہ پر چلا دوں گا');
INSERT INTO "jalandhari" VALUES(19,44,'19:44','ابّا شیطان کی پرستش نہ کیجیئے۔ بےشک شیطان خدا کا نافرمان ہے');
INSERT INTO "jalandhari" VALUES(19,45,'19:45','ابّا مجھے ڈر لگتا ہے کہ آپ کو خدا کا عذاب آپکڑے تو آپ شیطان کے ساتھی ہوجائیں');
INSERT INTO "jalandhari" VALUES(19,46,'19:46','اس نے کہا ابراہیم کیا تو میرے معبودوں سے برگشتہ ہے؟ اگر تو باز نہ آئے گا تو میں تجھے سنگسار کردوں گا اور تو ہمیشہ کے لئے مجھ سے دور ہوجا');
INSERT INTO "jalandhari" VALUES(19,47,'19:47','ابراہیم نے سلام علیک کہا (اور کہا کہ) میں آپ کے لئے اپنے پروردگار سے بخشش مانگوں گا۔ بےشک وہ مجھ پر نہایت مہربان ہے');
INSERT INTO "jalandhari" VALUES(19,48,'19:48','اور میں آپ لوگوں سے اور جن کو آپ خدا کے سوا پکارا کرتے ہیں ان سے کنارہ کرتا ہوں اور اپنے پروردگار ہی کو پکاروں گا۔ امید ہے کہ میں اپنے پروردگار کو پکار کر محروم نہیں رہوں گا');
INSERT INTO "jalandhari" VALUES(19,49,'19:49','اور جب ابراہیم ان لوگوں سے اور جن کی وہ خدا کے سوا پرستش کرتے تھے اُن سے الگ ہوگئے تو ہم نے ان کو اسحاق اور (اسحاق کو) یعقوب بخشے۔ اور سب کو پیغمبر بنایا');
INSERT INTO "jalandhari" VALUES(19,50,'19:50','اور ان کو اپنی رحمت سے (بہت سی چیزیں) عنایت کیں۔ اور ان کا ذکر جمیل بلند کیا');
INSERT INTO "jalandhari" VALUES(19,51,'19:51','اور کتاب میں موسیٰ کا بھی ذکر کرو۔ بےشک وہ (ہمارے) برگزیدہ اور پیغمبر مُرسل تھے');
INSERT INTO "jalandhari" VALUES(19,52,'19:52','اور ہم نے ان کو طور کی داہنی جانب پکارا اور باتیں کرنے کے لئے نزدیک بلایا');
INSERT INTO "jalandhari" VALUES(19,53,'19:53','اور اپنی مہربانی سے اُن کو اُن کا بھائی ہارون پیغمبر عطا کیا');
INSERT INTO "jalandhari" VALUES(19,54,'19:54','اور کتاب میں اسمٰعیل کا بھی ذکر کرو وہ وعدے کے سچے اور ہمارے بھیجے ہوئے نبی تھے');
INSERT INTO "jalandhari" VALUES(19,55,'19:55','اور اپنے گھر والوں کو نماز اور زکوٰة کا حکم کرتے تھے اور اپنے پروردگار کے ہاں پسندیدہ (وبرگزیدہ) تھے');
INSERT INTO "jalandhari" VALUES(19,56,'19:56','اور کتاب میں ادریس کا بھی ذکر کرو۔ وہ بھی نہایت سچے نبی تھے');
INSERT INTO "jalandhari" VALUES(19,57,'19:57','اور ہم نے ان کو اونچی جگہ اُٹھا لیا تھا');
INSERT INTO "jalandhari" VALUES(19,58,'19:58','یہ وہ لوگ ہیں جن پر خدا نے اپنے پیغمبروں میں سے فضل کیا۔ (یعنی) اولاد آدم میں سے اور ان لوگوں میں سے جن کو نوح کے ساتھ (کشتی میں) سوار کیا اور ابراہیم اور یعقوب کی اولاد میں سے اور ان لوگوں میں سے جن کو ہم نے ہدایت دی اور برگزیدہ کیا۔ جب ان کے سامنے ہماری آیتیں پڑھی جاتی تھیں تو سجدے میں گر پڑتے اور روتے رہتے تھے');
INSERT INTO "jalandhari" VALUES(19,59,'19:59','پھر ان کے بعد چند ناخلف ان کے جانشیں ہوئے جنہوں نے نماز کو (چھوڑ دیا گویا اسے) کھو دیا۔ اور خواہشات نفسانی کے پیچھے لگ گئے۔ سو عنقریب ان کو گمراہی (کی سزا) ملے گی');
INSERT INTO "jalandhari" VALUES(19,60,'19:60','ہاں جس نے توبہ کی اور ایمان لایا اور عمل نیک کئے تو اسے لوگ بہشت میں داخل ہوں گے اور ان کا ذرا نقصان نہ کیا جائے گا');
INSERT INTO "jalandhari" VALUES(19,61,'19:61','(یعنی) بہشت جاودانی (میں) جس کا خدا نے اپنے بندوں سے وعدہ کیا ہے (اور جو ان کی آنکھوں سے) پوشیدہ (ہے)۔ بےشک اس کا وعدہ (نیکوکاروں کے سامنے) آنے والا ہے');
INSERT INTO "jalandhari" VALUES(19,62,'19:62','وہ اس میں سلام کے سوا کوئی بیہودہ کلام نہ سنیں گے، اور ان کے لئے صبح وشام کا کھانا تیار ہوگا');
INSERT INTO "jalandhari" VALUES(19,63,'19:63','یہی وہ جنت ہے جس کا ہم اپنے بندوں میں سے ایسے شخص کو وارث بنائیں گے جو پرہیزگار ہوگا');
INSERT INTO "jalandhari" VALUES(19,64,'19:64','اور (فرشتوں نے پیغمبر کو جواب دیا کہ) ہم تمہارے پروردگار کے حکم سوا اُتر نہیں سکتے۔ جو کچھ ہمارے آگے ہے اور پیچھے ہے اور جو ان کے درمیان ہے سب اسی کا ہے اور تمہارا پروردگار بھولنے والا نہیں');
INSERT INTO "jalandhari" VALUES(19,65,'19:65','(یعنی) آسمان اور زمین کا اور جو ان دونوں کے درمیان ہے سب کا پروردگار ہے۔ تو اسی کی عبادت کرو اور اسی کی عبادت پر ثابت قدم رہو۔ بھلا تم کوئی اس کا ہم نام جانتے ہو');
INSERT INTO "jalandhari" VALUES(19,66,'19:66','اور (کافر) انسان کہتا ہے کہ جب میں مر جاؤ گا تو کیا زندہ کرکے نکالا جاؤں گا؟');
INSERT INTO "jalandhari" VALUES(19,67,'19:67','کیا (ایسا) انسان یاد نہیں کرتا کہ ہم نے اس کو پہلے بھی پیدا کیا تھا اور وہ کچھ بھی چیز نہ تھا');
INSERT INTO "jalandhari" VALUES(19,68,'19:68','تمہارے پروردگار کی قسم! ہم ان کو جمع کریں گے اور شیطانوں کو بھی۔ پھر ان سب کو جہنم کے گرد حاضر کریں گے (اور وہ) گھٹنوں پر گرے ہوئے (ہوں گے)');
INSERT INTO "jalandhari" VALUES(19,69,'19:69','پھر ہر جماعت میں سے ہم ایسے لوگوں کو کھینچ نکالیں گے جو خدا سے سخت سرکشی کرتے تھے');
INSERT INTO "jalandhari" VALUES(19,70,'19:70','اور ہم ان لوگوں سے خوب واقف ہیں جو ان میں داخل ہونے کے زیادہ لائق ہیں');
INSERT INTO "jalandhari" VALUES(19,71,'19:71','اور تم میں کوئی (شخص) نہیں مگر اسے اس پر گزرنا ہوگا۔ یہ تمہارے پروردگار پر لازم اور مقرر ہے');
INSERT INTO "jalandhari" VALUES(19,72,'19:72','پھر ہم پرہیزگاروں کو نجات دیں گے۔ اور ظالموں کو اس میں گھٹنوں کے بل پڑا ہوا چھوڑ دیں گے');
INSERT INTO "jalandhari" VALUES(19,73,'19:73','اور جب ان لوگوں کے سامنے ہماری آیتیں پڑھی جاتی ہیں تو جو کافر ہیں وہ مومنوں سے کہتے ہیں کہ دونوں فریق میں سے مکان کس کے اچھے اور مجلسیں کس کی بہتر ہیں');
INSERT INTO "jalandhari" VALUES(19,74,'19:74','اور ہم نے ان سے پہلے بہت سی اُمتیں ہلاک کردیں۔ وہ لوگ (ان سے) ٹھاٹھ اور نمود میں کہیں اچھے تھے');
INSERT INTO "jalandhari" VALUES(19,75,'19:75','کہہ دو کہ جو شخص گمراہی میں پڑا ہوا ہے خدا اس کو آہستہ آہستہ مہلت دیئے جاتا ہے۔ یہاں تک کہ جب اس چیز کو دیکھ لیں گے جس کا ان سے وعدہ کیا جاتا ہے خواہ عذاب اور خواہ قیامت۔ تو (اس وقت) جان لیں گے کہ مکان کس کا برا ہے اور لشکر کس کا کمزور ہے');
INSERT INTO "jalandhari" VALUES(19,76,'19:76','اور جو لوگ ہدایت یاب ہیں خدا ان کو زیادہ ہدایت دیتا ہے۔ اور نیکیاں جو باقی رہنے والی ہیں وہ تمہارے پروردگار کے صلے کے لحاظ سے خوب اور انجام کے اعتبار سے بہتر ہیں');
INSERT INTO "jalandhari" VALUES(19,77,'19:77','بھلا تم نے اس شخص کو دیکھا جس نے ہماری آیتوں سے کفر کیا اور کہنے لگا کہ (اگر میں ازسرنو زندہ ہوا بھی تو یہی) مال اور اولاد مجھے (وہاں) ملے گا');
INSERT INTO "jalandhari" VALUES(19,78,'19:78','کیا اس نے غیب کی خبر پالی ہے یا خدا کے یہاں (سے) عہد لے لیا ہے؟');
INSERT INTO "jalandhari" VALUES(19,79,'19:79','ہرگز نہیں۔ یہ جو کچھ کہتا ہے ہم اس کو لکھتے جاتے اور اس کے لئے آہستہ آہستہ عذاب بڑھاتے جاتے ہیں');
INSERT INTO "jalandhari" VALUES(19,80,'19:80','اور جو چیزیں یہ بتاتا ہے ان کے ہم وارث ہوں گے اور یہ اکیلا ہمارے سامنے آئے گا');
INSERT INTO "jalandhari" VALUES(19,81,'19:81','اور ان لوگوں نے خدا کے سوا اور معبود بنالئے ہیں تاکہ وہ ان کے لئے (موجب عزت و) مدد ہوں');
INSERT INTO "jalandhari" VALUES(19,82,'19:82','ہرگز نہیں وہ (معبودان باطل) ان کی پرستش سے انکار کریں گے اور ان کے دشمن (ومخالف) ہوں گے');
INSERT INTO "jalandhari" VALUES(19,83,'19:83','کیا تم نے نہیں دیکھا کہ ہم نے شیطانوں کو کافروں پر چھوڑ رکھا ہے کہ ان کو برانگیختہ کرتے رہتے ہیں');
INSERT INTO "jalandhari" VALUES(19,84,'19:84','تو تم ان پر (عذاب کے لئے) جلدی نہ کرو۔ اور ہم تو ان کے لئے (دن) شمار کر رہے ہیں');
INSERT INTO "jalandhari" VALUES(19,85,'19:85','جس روز ہم پرہیزگاروں کو خدا کے سامنے (بطور) مہمان جمع کریں گے');
INSERT INTO "jalandhari" VALUES(19,86,'19:86','اور گنہگاروں کو دوزخ کی طرف پیاسے ہانک لے جائیں گے');
INSERT INTO "jalandhari" VALUES(19,87,'19:87','(تو لوگ) کسی کی سفارش کا اختیار نہ رکھیں گے مگر جس نے خدا سے اقرار لیا ہو');
INSERT INTO "jalandhari" VALUES(19,88,'19:88','اور کہتے ہیں کہ خدا بیٹا رکھتا ہے');
INSERT INTO "jalandhari" VALUES(19,89,'19:89','(ایسا کہنے والو یہ تو) تم بری بات (زبان پر) لائے ہو');
INSERT INTO "jalandhari" VALUES(19,90,'19:90','قریب ہے کہ اس (افتراء) سے آسمان پھٹ پڑیں اور زمین شق ہوجائے اور پہاڑ پارہ پارہ ہو کر گر پڑیں');
INSERT INTO "jalandhari" VALUES(19,91,'19:91','کہ انہوں نے خدا کے لئے بیٹا تجویز کیا');
INSERT INTO "jalandhari" VALUES(19,92,'19:92','اور خدا کو شایاں نہیں کہ کسی کو بیٹا بنائے');
INSERT INTO "jalandhari" VALUES(19,93,'19:93','تمام شخص جو آسمانوں اور زمین میں ہیں سب خدا کے روبرو بندے ہو کر آئیں گے');
INSERT INTO "jalandhari" VALUES(19,94,'19:94','اُس نے ان (سب) کو (اپنے علم سے) گھیر رکھا اور (ایک ایک کو) شمار کر رکھا ہے');
INSERT INTO "jalandhari" VALUES(19,95,'19:95','اور سب قیامت کے دن اس کے سامنے اکیلے اکیلے حاضر ہوں گے');
INSERT INTO "jalandhari" VALUES(19,96,'19:96','اور جو لوگ ایمان لائے اور عمل نیک کئے خدا ان کی محبت (مخلوقات کے دل میں) پیدا کردے گا');
INSERT INTO "jalandhari" VALUES(19,97,'19:97','(اے پیغمبر) ہم نے یہ (قرآن) تمہاری زبان میں آسان (نازل) کیا ہے تاکہ تم اس سے پرہیزگاروں کو خوشخبری پہنچا دو اور جھگڑالوؤں کو ڈر سنا دو');
INSERT INTO "jalandhari" VALUES(19,98,'19:98','اور ہم نے اس سے پہلے بہت سے گروہوں کو ہلاک کردیا ہے۔ بھلا تم ان میں سے کسی کو دیکھتے ہو یا (کہیں) ان کی بھنک سنتے ہو');
INSERT INTO "jalandhari" VALUES(20,1,'20:1','طہٰ');
INSERT INTO "jalandhari" VALUES(20,2,'20:2','(اے محمدصلى الله عليه وسلم) ہم نے تم پر قرآن اس لئے نازل نہیں کیا کہ تم مشقت میں پڑ جاؤ');
INSERT INTO "jalandhari" VALUES(20,3,'20:3','بلکہ اس شخص کو نصیحت دینے کے لئے (نازل کیا ہے) جو خوف رکھتا ہے');
INSERT INTO "jalandhari" VALUES(20,4,'20:4','یہ اس (ذات برتر) کا اتارا ہوا ہے جس نے زمین اور اونچے اونچے آسمان بنائے');
INSERT INTO "jalandhari" VALUES(20,5,'20:5','(یعنی خدائے) رحمٰن جس نےعرش پر قرار پکڑا');
INSERT INTO "jalandhari" VALUES(20,6,'20:6','جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے اور جو کچھ ان دونوں کے بیچ میں ہے اور جو کچھ (زمین کی) مٹی کے نیچے ہے سب اسی کا ہے');
INSERT INTO "jalandhari" VALUES(20,7,'20:7','اور اگر تم پکار کر بات کہو تو وہ تو چھپے بھید اور نہایت پوشیدہ بات تک کو جانتا ہے');
INSERT INTO "jalandhari" VALUES(20,8,'20:8','(وہ معبود برحق ہے کہ) اس کے سوا کوئی معبود نہیں ہے۔ اس کے (سب) نام اچھے ہیں');
INSERT INTO "jalandhari" VALUES(20,9,'20:9','اور کیا تمہیں موسیٰ (کے حال) کی خبر ملی ہے');
INSERT INTO "jalandhari" VALUES(20,10,'20:10','جب انہوں نے آگ دیکھی تو اپنے گھر والوں سے کہا کہ تم (یہاں) ٹھہرو میں نے آگ دیکھی ہے (میں وہاں جاتا ہوں) شاید اس میں سے میں تمہارے پاس انگاری لاؤں یا آگ (کے مقام) کا رستہ معلوم کرسکوں');
INSERT INTO "jalandhari" VALUES(20,11,'20:11','جب وہاں پہنچے تو آواز آئی کہ موسیٰ');
INSERT INTO "jalandhari" VALUES(20,12,'20:12','میں تو تمہارا پروردگار ہوں تو اپنی جوتیاں اتار دو۔ تم (یہاں) پاک میدان (یعنی) طویٰ میں ہو');
INSERT INTO "jalandhari" VALUES(20,13,'20:13','اور میں نے تم کو انتخاب کرلیا ہے تو جو حکم دیا جائے اسے سنو');
INSERT INTO "jalandhari" VALUES(20,14,'20:14','بےشک میں ہی خدا ہوں۔ میرے سوا کوئی معبود نہیں تو میری عبادت کرو اور میری یاد کے لئے نماز پڑھا کرو');
INSERT INTO "jalandhari" VALUES(20,15,'20:15','قیامت یقیناً آنے والی ہے۔ میں چاہتا ہوں کہ اس (کے وقت) کو پوشیدہ رکھوں تاکہ ہر شخص جو کوشش کرے اس کا بدلا پائے');
INSERT INTO "jalandhari" VALUES(20,16,'20:16','تو جو شخص اس پر ایمان نہیں رکھتا اور اپنی خواہش کے پیچھے چلتا ہے (کہیں) تم کو اس (کے یقین) سے روک نہ دے تو (اس صورت میں) تم ہلاک ہوجاؤ');
INSERT INTO "jalandhari" VALUES(20,17,'20:17','اور موسی یہ تمہارے داہنے ہاتھ میں کیا ہے');
INSERT INTO "jalandhari" VALUES(20,18,'20:18','انہوں نے کہا یہ میری لاٹھی ہے۔ اس پر میں سہارا لگاتا ہوں اور اس سے اپنی بکریوں کے لئے پتے جھاڑتا ہوں اور اس میں میرے لئے اور بھی کئی فائدے ہیں');
INSERT INTO "jalandhari" VALUES(20,19,'20:19','فرمایا کہ موسیٰ اسے ڈال دو');
INSERT INTO "jalandhari" VALUES(20,20,'20:20','تو انہوں نے اس کو ڈال دیا اور وہ ناگہاں سانپ بن کر دوڑنے لگا');
INSERT INTO "jalandhari" VALUES(20,21,'20:21','خدا نے فرمایا کہ اسے پکڑ لو اور ڈرنا مت۔ ہم اس کو ابھی اس کی پہلی حالت پر لوٹا دیں گے');
INSERT INTO "jalandhari" VALUES(20,22,'20:22','اور اپنا ہاتھ اپنی بغل سے لگالو وہ کسی عیب (وبیماری)کے بغیر سفید (چمکتا دمکتا) نکلے گا۔ (یہ) دوسری نشانی (ہے)');
INSERT INTO "jalandhari" VALUES(20,23,'20:23','تاکہ ہم تمہیں اپنے نشانات عظیم دکھائیں');
INSERT INTO "jalandhari" VALUES(20,24,'20:24','تم فرعون کے پاس جاؤ (کہ) وہ سرکش ہو رہا ہے');
INSERT INTO "jalandhari" VALUES(20,25,'20:25','کہا میرے پروردگار (اس کام کے لئے) میرا سینہ کھول دے');
INSERT INTO "jalandhari" VALUES(20,26,'20:26','اور میرا کام آسان کردے');
INSERT INTO "jalandhari" VALUES(20,27,'20:27','اور میری زبان کی گرہ کھول دے');
INSERT INTO "jalandhari" VALUES(20,28,'20:28','تاکہ وہ بات سمجھ لیں');
INSERT INTO "jalandhari" VALUES(20,29,'20:29','اور میرے گھر والوں میں سے (ایک کو) میرا وزیر (یعنی مددگار) مقرر فرما');
INSERT INTO "jalandhari" VALUES(20,30,'20:30','(یعنی) میرے بھائی ہارون کو');
INSERT INTO "jalandhari" VALUES(20,31,'20:31','اس سے میری قوت کو مضبوط فرما');
INSERT INTO "jalandhari" VALUES(20,32,'20:32','اور اسے میرے کام میں شریک کر');
INSERT INTO "jalandhari" VALUES(20,33,'20:33','تاکہ ہم تیری بہت سی تسبیح کریں');
INSERT INTO "jalandhari" VALUES(20,34,'20:34','اور تجھے کثرت سے یاد کریں');
INSERT INTO "jalandhari" VALUES(20,35,'20:35','تو ہم کو (ہر حال میں) دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(20,36,'20:36','فرمایا موسیٰ تمہاری دعا قبول کی گئی');
INSERT INTO "jalandhari" VALUES(20,37,'20:37','اور ہم نے تم پر ایک بار اور بھی احسان کیا تھا');
INSERT INTO "jalandhari" VALUES(20,38,'20:38','جب ہم نے تمہاری والدہ کو الہام کیا تھا جو تمہیں بتایا جاتا ہے');
INSERT INTO "jalandhari" VALUES(20,39,'20:39','(وہ یہ تھا) کہ اسے (یعنی موسیٰ کو) صندوق میں رکھو پھر اس (صندوق) کو دریا میں ڈال دو تو دریا اسے کنارے پر ڈال دے گا (اور) میرا اور اس کا دشمن اسے اٹھا لے گا۔ اور (موسیٰ) میں نے تم پر اپنی طرف سے محبت ڈال دی ہے (اس لئے کہ تم پر مہربانی کی جائے) اور اس لئے کہ تم میرے سامنے پرورش پاؤ');
INSERT INTO "jalandhari" VALUES(20,40,'20:40','جب تمہاری بہن (فرعون کے ہاں) گئی اور کہنے لگی کہ میں تمہیں ایسا شخص بتاؤں جو اس کو پالے۔ تو (اس طریق سے) ہم نے تم کو تمہاری ماں کے پاس پہنچا دیا تاکہ ان کی آنکھیں ٹھنڈی ہوں اور وہ رنج نہ کریں۔ اور تم نے ایک شخص کو مار ڈالا تو ہم نے تم کو غم سے مخلصی دی اور ہم نے تمہاری (کئی بار) آزمائش کی۔ پھر تم کئی سال اہل مدین میں ٹھہرے رہے۔ پھر اے موسیٰ تم (قابلیت رسالت کے) اندازے پر آ پہنچے');
INSERT INTO "jalandhari" VALUES(20,41,'20:41','اور میں نے تم کو اپنے (کام کے) لئے بنایا ہے');
INSERT INTO "jalandhari" VALUES(20,42,'20:42','تو تم اور تمہارا بھائی دونوں ہماری نشانیاں لے کر جاؤ اور میری یاد میں سستی نہ کرنا');
INSERT INTO "jalandhari" VALUES(20,43,'20:43','دونوں فرعون کے پاس جاؤ وہ سرکش ہو رہا ہے');
INSERT INTO "jalandhari" VALUES(20,44,'20:44','اور اس سے نرمی سے بات کرنا شاید وہ غور کرے یا ڈر جائے');
INSERT INTO "jalandhari" VALUES(20,45,'20:45','دونوں کہنے لگے کہ ہمارے پروردگار ہمیں خوف ہے کہ ہم پر تعدی کرنے لگے یا زیادہ سرکش ہوجائے');
INSERT INTO "jalandhari" VALUES(20,46,'20:46','خدا نے فرمایا کہ ڈرو مت میں تمہارے ساتھ ہوں (اور) سنتا اور دیکھتا ہوں');
INSERT INTO "jalandhari" VALUES(20,47,'20:47','(اچھا) تو اس کے پاس جاؤ اور کہو کہ ہم آپ کے پروردگار کے بھیجے ہوئے ہیں تو بنی اسرائیل کو ہمارے ساتھ جانے کی اجازت دیجیئے۔ اور انہیں عذاب نہ کیجیئے۔ ہم آپ کے پاس آپ کے پروردگار کی طرف سے نشانی لے کر آئے ہیں۔ اور جو ہدایت کی بات مانے اس کو سلامتی ہو');
INSERT INTO "jalandhari" VALUES(20,48,'20:48','ہماری طرف یہ وحی آئی ہے کہ جو جھٹلائے اور منہ پھیرے اس کے لئے عذاب (تیار) ہے');
INSERT INTO "jalandhari" VALUES(20,49,'20:49','(غرض موسیٰ اور ہارون فرعون کے پاس گئے) اس نے کہا کہ موسیٰ تمہارا پروردگار کون ہے؟');
INSERT INTO "jalandhari" VALUES(20,50,'20:50','کہا کہ ہمارا پروردگار وہ ہے جس نے ہر چیز کو اس کی شکل وصورت بخشی پھر راہ دکھائی');
INSERT INTO "jalandhari" VALUES(20,51,'20:51','کہا تو پہلی جماعتوں کا کیا حال؟');
INSERT INTO "jalandhari" VALUES(20,52,'20:52','کہا کہ ان کا علم میرے پروردگار کو ہے (جو) کتاب میں (لکھا ہوا ہے)۔ میرا پروردگار نہ چوکتا ہے نہ بھولتا ہے');
INSERT INTO "jalandhari" VALUES(20,53,'20:53','وہ (وہی تو ہے) جس نے تم لوگوں کے لئے زمین کو فرش بنایا اور اس میں تمہارے لئے رستے جاری کئے اور آسمان سے پانی برسایا۔ پھر اس سے انواع واقسام کی مختلف روئیدگیاں پیدا کیں');
INSERT INTO "jalandhari" VALUES(20,54,'20:54','کہ (خود بھی) کھاؤ اور اپنے چارپایوں کو بھی چراؤ۔ بےشک ان (باتوں) میں عقل والوں کے لئے (بہت سی) نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(20,55,'20:55','اسی (زمین) سے ہم تم کو پیدا کیا اور اسی میں تمہیں لوٹائیں گے اور اسی سے دوسری دفعہ نکالیں گے');
INSERT INTO "jalandhari" VALUES(20,56,'20:56','اور ہم نے فرعون کو اپنی سب نشانیاں دکھائیں مگر وہ تکذیب وانکار ہی کرتا رہا');
INSERT INTO "jalandhari" VALUES(20,57,'20:57','کہنے لگا کہ موسیٰ تم ہمارے پاس اس لئے آئے ہو کہ اپنے جادو (کے زور) سے ہمیں ہمارے ملک سے نکال دو');
INSERT INTO "jalandhari" VALUES(20,58,'20:58','تو ہم بھی تمہارے مقابل ایسا ہی جادو لائیں گے تو ہمارے اور اپنے درمیان ایک وقت مقرر کر لو کہ نہ تو ہم اس کے خلاف کریں اور نہ تم (اور یہ مقابلہ) ایک ہموار میدان میں (ہوگا)');
INSERT INTO "jalandhari" VALUES(20,59,'20:59','موسیٰ نے کہا آپ کے لئے (مقابلے کا) دن نو روز (مقرر کیا جاتا ہے) اور یہ کہ لوگ اس دن چاشت کے وقت اکھٹے ہوجائیں');
INSERT INTO "jalandhari" VALUES(20,60,'20:60','تو فرعون لوٹ گیا اور اپنے سامان جمع کرکے پھر آیا');
INSERT INTO "jalandhari" VALUES(20,61,'20:61','موسیٰ نے ان (جادوگروں) سے کہا کہ ہائے تمہاری کمبختی۔ خدا پر جھوٹ افتراء نہ کرو کہ وہ تمہیں عذاب سے فنا کردے گا اور جس نے افتراء کیا وہ نامراد رہا');
INSERT INTO "jalandhari" VALUES(20,62,'20:62','تو وہ باہم اپنے معاملے میں جھگڑانے اور چپکے چپکے سرگوشی کرنے لگے');
INSERT INTO "jalandhari" VALUES(20,63,'20:63','کہنے لگے یہ دونوں جادوگر ہیں چاہتے ہیں کہ اپنے جادو (کے زور) سے تم کو تمہارے ملک سے نکل دیں اور تمہارے شائستہ مذہب کو نابود کردیں');
INSERT INTO "jalandhari" VALUES(20,64,'20:64','تو تم (جادو کا) سامان اکھٹا کرلو اور پھر قطار باندھ کر آؤ۔ آج جو غالب رہا وہی کامیاب ہوا');
INSERT INTO "jalandhari" VALUES(20,65,'20:65','بولے کہ موسیٰ یا تم (اپنی چیز) ڈالو یا ہم (اپنی چیزیں) پہلے ڈالتے ہیں');
INSERT INTO "jalandhari" VALUES(20,66,'20:66','موسیٰ نے کہا نہیں تم ہی ڈالو۔ (جب انہوں نے چیزیں ڈالیں) تو ناگہاں ان کی رسیاں اور لاٹھیاں موسی کے خیال میں ایسی آنے لگیں کہ وہ (میدان) میں ادھر اُدھر دوڑ رہی ہیں');
INSERT INTO "jalandhari" VALUES(20,67,'20:67','(اُس وقت) موسیٰ نے اپنے دل میں خوف معلوم کیا');
INSERT INTO "jalandhari" VALUES(20,68,'20:68','ہم نے کہا خوف نہ کرو بلاشبہ تم ہی غالب ہو');
INSERT INTO "jalandhari" VALUES(20,69,'20:69','اور جو چیز (یعنی لاٹھی) تمہارے داہنے ہاتھ میں ہے اسے ڈال دو کہ جو کچھ انہوں نے بنایا ہے اس کو نگل جائے گی۔ جو کچھ انہوں نے بنایا ہے (یہ تو) جادوگروں کے ہتھکنڈے ہیں اور جادوگر جہاں جائے فلاح نہیں پائے گا');
INSERT INTO "jalandhari" VALUES(20,70,'20:70','(القصہ یوں ہی ہوا) تو جادوگر سجدے میں گر پڑے (اور) کہنے لگے کہ ہم موسیٰ اور ہارون کے پروردگار پر ایمان لائے');
INSERT INTO "jalandhari" VALUES(20,71,'20:71','(فرعون) بولا کہ پیشتر اس کے میں تمہیں اجازت دوں تم اس پر ایمان لے آئے۔ بےشک وہ تمہارا بڑا (یعنی استاد) ہے جس نے تم کو جادو سکھایا ہے۔ سو میں تمہارے ہاتھ اور پاؤں (جانب) خلاف سے کٹوا دوں گا اور کھجور کے تنوں پر سولی چڑھوا دوں گا (اس وقت) تم کو معلوم ہوگا کہ ہم میں سے کس کا عذاب زیادہ سخت اور دیر تک رہنے والا ہے');
INSERT INTO "jalandhari" VALUES(20,72,'20:72','انہوں نے کہا جو دلائل ہمارے پاس آگئے ہیں ان پر اور جس نے ہم کو پیدا ہے اس پر ہم آپ کو ہرگز ترجیح نہیں دیں گے تو آپ کو جو حکم دینا ہو دے دیجیئے۔ اور آپ (جو) حکم دے سکتے ہیں وہ صرف اسی دنیا کی زندگی میں (دے سکتے ہیں)');
INSERT INTO "jalandhari" VALUES(20,73,'20:73','ہم اپنے پروردگار پر ایمان لے آئے تاکہ وہ ہمارے گناہوں کو معاف کرے اور (اسے بھی) جو آپ نے ہم سے زبردستی جادو کرایا۔ اور خدا بہتر اور باقی رہنے والا ہے');
INSERT INTO "jalandhari" VALUES(20,74,'20:74','جو شخص اپنے پروردگار کے پاس گنہگار ہو کر آئے گا تو اس کے لئے جہنم ہے۔ جس میں نہ مرے گا نہ جیئے گا');
INSERT INTO "jalandhari" VALUES(20,75,'20:75','اور جو اس کے روبرو ایماندار ہو کر آئے گا اور عمل بھی نیک کئے ہوں گے تو ایسے لوگوں کے لئے اونچے اونچے درجے ہیں');
INSERT INTO "jalandhari" VALUES(20,76,'20:76','(یعنی) ہمیشہ رہنے کے باغ جن کے نیچے نہریں بہہ رہی ہیں۔ ہمیشہ ان میں رہیں گے۔ اور یہ اس شخص کا بدلہ ہے جو پاک ہوا');
INSERT INTO "jalandhari" VALUES(20,77,'20:77','اور ہم نے موسیٰ کی طرف وحی بھیجی کہ ہمارے بندوں کو راتوں رات نکال لے جاؤ پھر ان کے لئے دریا میں (لاٹھی مار کر) خشک رستہ بنا دو پھر تم کو نہ تو (فرعون کے) آپکڑنے کا خوف ہوگا اور نہ (غرق ہونے کا) ڈر');
INSERT INTO "jalandhari" VALUES(20,78,'20:78','پھر فرعون نے اپنے لشکر کے ساتھ ان کا تعاقب کیا تو دریا (کی موجوں) نے ان پر چڑھ کر انہیں ڈھانک لیا (یعنی ڈبو دیا)');
INSERT INTO "jalandhari" VALUES(20,79,'20:79','اور فرعون نے اپنی قوم کو گمراہ کردیا اور سیدھے رستے پر نہ ڈالا');
INSERT INTO "jalandhari" VALUES(20,80,'20:80','اے آل یعقوب ہم نے تم کو تمہارے دشمن سے نجات دی اور تورات دینے کے لئے تم سے کوہ طور کی داہنی طرف مقرر کی اور تم پر من اور سلویٰ نازل کیا');
INSERT INTO "jalandhari" VALUES(20,81,'20:81','(اور حکم دیا کہ) جو پاکیزہ چیزیں ہم نے تم کو دی ہیں ان کو کھاؤ۔ اور اس میں حد سے نہ نکلنا۔ ورنہ تم پر میرا غضب نازل ہوگا۔ اور جس پر میرا غضب نازل ہوا وہ ہلاک ہوگیا');
INSERT INTO "jalandhari" VALUES(20,82,'20:82','اور جو توبہ کرے اور ایمان لائے اور عمل نیک کرے پھر سیدھے رستے چلے اس کو میں بخش دینے والا ہوں');
INSERT INTO "jalandhari" VALUES(20,83,'20:83','اور اے موسیٰ تم نے اپنی قوم سے (آگے چلے آنے میں) کیوں جلدی کی');
INSERT INTO "jalandhari" VALUES(20,84,'20:84','کہا وہ میرے پیچھے (آ رہے) ہیں اور اے پروردگار میں نے تیری طرف (آنے کی) جلدی اس لئے کی کہ تو خوش ہو');
INSERT INTO "jalandhari" VALUES(20,85,'20:85','فرمایا کہ ہم نے تمہاری قوم کو تمہارے بعد آزمائش میں ڈال دیا ہے اور سامری نے ان کو بہکا دیا ہے');
INSERT INTO "jalandhari" VALUES(20,86,'20:86','اور موسیٰ غصّے اور غم کی حالت میں اپنی قوم کے پاس واپس آئے (اور) کہنے لگے کہ اے قوم کیا تمہارے پروردگار نے تم سے ایک اچھا وعدہ نہیں کیا تھا؟ کیا (میری جدائی کی) مدت تمہیں دراز (معلوم) ہوئی یا تم نے چاہا کہ تم پر تمہارے پروردگار کی طرف سے غضب نازل ہو۔ اور (اس لئے) تم نے مجھ سے جو وعدہ کیا تھا (اس کے) خلاف کیا');
INSERT INTO "jalandhari" VALUES(20,87,'20:87','وہ کہنے لگے کہ ہم نے اپنے اختیار سے تم سے وعدہ خلاف نہیں کیا۔ بلکہ ہم لوگوں کے زیوروں کا بوجھ اٹھائے ہوئے تھے۔ پھر ہم نے اس کو (آگ میں) ڈال دیا اور اسی طرح سامری نے ڈال دیا');
INSERT INTO "jalandhari" VALUES(20,88,'20:88','تو اس نے ان کے لئے ایک بچھڑا بنا دیا (یعنی اس کا) قالب جس کی آواز گائے کی سی تھی۔ تو لوگ کہنے لگے کہ یہی تمہارا معبود ہے اور موسیٰ کا بھی معبود ہے۔ مگر وہ بھول گئے ہیں');
INSERT INTO "jalandhari" VALUES(20,89,'20:89','کیا یہ لوگ نہیں دیکھتے کہ وہ ان کی کسی بات کا جواب نہیں دیتا۔ اور نہ ان کے نقصان اور نفع کا کچھ اختیار رکھتا ہے');
INSERT INTO "jalandhari" VALUES(20,90,'20:90','اور ہارون نے ان سے پہلے ہی کہہ دیا تھا کہ لوگو اس سے صرف تمہاری آزمائش کی گئی ہے۔ اور تمہارا پروردگار تو خدا ہے تو میری پیروی کرو اور میرا کہا مانو');
INSERT INTO "jalandhari" VALUES(20,91,'20:91','وہ کہنے لگے کہ جب تک موسیٰ ہمارے پاس واپس نہ آئیں ہم تو اس کی پوجا پر قائم رہیں گے');
INSERT INTO "jalandhari" VALUES(20,92,'20:92','(پھر موسیٰ نے ہارون سے) کہا کہ ہارون جب تم نے ان کو دیکھا تھا کہ گمراہ ہو رہے ہیں تو تم کو کس چیز نے روکا');
INSERT INTO "jalandhari" VALUES(20,93,'20:93','(یعنی) اس بات سے کہ تم میرے پیچھے چلے آؤ۔ بھلا تم نے میرے حکم کے خلاف (کیوں) کیا؟');
INSERT INTO "jalandhari" VALUES(20,94,'20:94','کہنے لگے کہ بھائی میری ڈاڑھی اور سر (کے بالوں) کو نہ پکڑیئے۔ میں تو اس سے ڈرا کہ آپ یہ نہ کہیں کہ تم نے بنی اسرائیل میں تفرقہ ڈال دیا اور میری بات کو ملحوظ نہ رکھا');
INSERT INTO "jalandhari" VALUES(20,95,'20:95','پھر (سامری سے) کہنے لگے کہ سامری تیرا کیا حال ہے؟');
INSERT INTO "jalandhari" VALUES(20,96,'20:96','اس نے کہا کہ میں نے ایسی چیز دیکھی جو اوروں نے نہیں دیکھی تو میں نے فرشتے کے نقش پا سے (مٹی کی) ایک مٹھی بھر لی۔ پھر اس کو (بچھڑے کے قالب میں) ڈال دیا اور مجھے میرے جی نے (اس کام کو) اچھا بتایا');
INSERT INTO "jalandhari" VALUES(20,97,'20:97','(موسیٰ نے) کہا جا تجھ کو دنیا کی زندگی میں یہ (سزا) ہے کہ کہتا رہے کہ مجھ کو ہاتھ نہ لگانا اور تیرے لئے ایک اور وعدہ ہے (یعنی عذاب کا) جو تجھ سے ٹل نہ سکے گا اور جس معبود (کی پوجا) پر تو (قائم و) معتکف تھا اس کو دیکھ۔ ہم اسے جلادیں گے پھر اس (کی راکھ) کو اُڑا کر دریا میں بکھیر دیں گے');
INSERT INTO "jalandhari" VALUES(20,98,'20:98','تمہارا معبود خدا ہی ہے جس کے سوا کوئی معبود نہیں۔ اس کا علم ہر چیز پر محیط ہے');
INSERT INTO "jalandhari" VALUES(20,99,'20:99','اس طرح پر ہم تم سے وہ حالات بیان کرتے ہیں جو گذر چکے ہیں۔ اور ہم نے تمہیں اپنے پاس سے نصیحت (کی کتاب) عطا فرمائی ہے');
INSERT INTO "jalandhari" VALUES(20,100,'20:100','جو شخص اس سے منہ پھیرے گا وہ قیامت کے دن (گناہ کا) بوجھ اُٹھائے گا');
INSERT INTO "jalandhari" VALUES(20,101,'20:101','(ایسے لوگ) ہمیشہ اس (عذاب) میں (مبتلا) رہیں گے اور یہ بوجھ قیامت کے روز ان کے لئے برا ہے');
INSERT INTO "jalandhari" VALUES(20,102,'20:102','جس روز صور پھونکا جائے گا اور ہم گنہگاروں کو اکھٹا کریں گے اور ان کی آنکھیں نیلی نیلی ہوں گی');
INSERT INTO "jalandhari" VALUES(20,103,'20:103','(تو) وہ آپس میں آہستہ آہستہ کہیں گے کہ تم (دنیا میں) صرف دس ہی دن رہے ہو');
INSERT INTO "jalandhari" VALUES(20,104,'20:104','جو باتیں یہ کریں گے ہم خوب جانتے ہیں۔ اس وقت ان میں سب سے اچھی راہ والا (یعنی عاقل وہوشمند) کہے گا کہ (نہیں بلکہ) صرف ایک ہی روز ٹھہرے ہو');
INSERT INTO "jalandhari" VALUES(20,105,'20:105','اور تم سے پہاڑوں کے بارے میں دریافت کرتے ہیں۔ کہہ دو کہ خدا ان کو اُڑا کر بکھیر دے گا');
INSERT INTO "jalandhari" VALUES(20,106,'20:106','اور زمین کو ہموار میدان کر چھوڑے گا');
INSERT INTO "jalandhari" VALUES(20,107,'20:107','جس میں نہ تم کجی (اور پستی) دیکھو گے نہ ٹیلا (اور بلندی)');
INSERT INTO "jalandhari" VALUES(20,108,'20:108','اس روز لوگ ایک پکارنے والے کے پیچھے چلیں گے اور اس کی پیروی سے انحراف نہ کرسکیں گے اور خدا کے سامنے آوازیں پست ہوجائیں گی تو تم آواز خفی کے سوا کوئی آواز نہ سنو گے');
INSERT INTO "jalandhari" VALUES(20,109,'20:109','اس روز (کسی کی) سفارش کچھ فائدہ نہ دے گی مگر اس شخص کی جسے خدا اجازت دے اور اس کی بات کو پسند فرمائے');
INSERT INTO "jalandhari" VALUES(20,110,'20:110','جو کچھ ان کے آگے ہے اور کچھ ان کے پیچھے ہے وہ اس کو جانتا ہے اور وہ (اپنے) علم سے خدا (کے علم) پر احاطہ نہیں کرسکتے');
INSERT INTO "jalandhari" VALUES(20,111,'20:111','اور اس زندہ و قائم کے رو برو منہ نیچے ہوجائیں گے۔ اور جس نے ظلم کا بوجھ اٹھایا وہ نامراد رہا');
INSERT INTO "jalandhari" VALUES(20,112,'20:112','اور جو نیک کام کرے گا اور مومن بھی ہوگا تو اس کو نہ ظلم کا خوف ہوگا اور نہ نقصان کا');
INSERT INTO "jalandhari" VALUES(20,113,'20:113','اور ہم نے اس کو اسی طرح کا قرآن عربی نازل کیا ہے اور اس میں طرح طرح کے ڈراوے بیان کردیئے ہیں تاکہ لوگ پرہیزگار بنیں یا خدا ان کے لئے نصیحت پیدا کردے');
INSERT INTO "jalandhari" VALUES(20,114,'20:114','تو خدا جو سچا بادشاہ ہے عالی قدر ہے۔ اور قرآن کی وحی جو تمہاری طرف بھیجی جاتی ہے اس کے پورا ہونے سے پہلے قرآن کے (پڑھنے کے) لئے جلدی نہ کیا کرو اور دعا کرو کہ میرے پروردگار مجھے اور زیادہ علم دے');
INSERT INTO "jalandhari" VALUES(20,115,'20:115','اور ہم نے پہلے آدم سے عہد لیا تھا مگر وہ (اسے) بھول گئے اور ہم نے ان میں صبر وثبات نہ دیکھا');
INSERT INTO "jalandhari" VALUES(20,116,'20:116','اور جب ہم نے فرشتوں سے کہا کہ آدم کے آگے سجدہ کرو تو سب سجدے میں گر پڑے مگر ابلیس نے انکار کیا');
INSERT INTO "jalandhari" VALUES(20,117,'20:117','ہم نے فرمایا کہ آدم یہ تمہارا اور تمہاری بیوی کا دشمن ہے تو یہ کہیں تم دونوں کو بہشت سے نکلوا نہ دے۔ پھر تم تکلیف میں پڑجاؤ');
INSERT INTO "jalandhari" VALUES(20,118,'20:118','یہاں تم کو یہ (آسائش) ہوگی کہ نہ بھوکے رہو نہ ننگے');
INSERT INTO "jalandhari" VALUES(20,119,'20:119','اور یہ کہ نہ پیاسے رہو اور نہ دھوپ کھاؤ');
INSERT INTO "jalandhari" VALUES(20,120,'20:120','تو شیطان نے ان کے دل میں وسوسہ ڈالا۔ (اور) کہا کہ آدم بھلا میں تم کو (ایسا) درخت بتاؤں (جو) ہمیشہ کی زندگی کا (ثمرہ دے) اور (ایسی) بادشاہت کہ کبھی زائل نہ ہو');
INSERT INTO "jalandhari" VALUES(20,121,'20:121','تو دونوں نے اس درخت کا پھل کھا لیا تو ان پر ان کی شرمگاہیں ظاہر ہوگئیں اور وہ اپنے (بدنوں) پر بہشت کے پتّے چپکانے لگے۔ اور آدم نے اپنے پروردگار کے حکم خلاف کیا تو (وہ اپنے مطلوب سے) بےراہ ہو گئے');
INSERT INTO "jalandhari" VALUES(20,122,'20:122','پھر ان کے پروردگار نے ان کو نوازا تو ان پر مہربانی سے توجہ فرمائی اور سیدھی راہ بتائی');
INSERT INTO "jalandhari" VALUES(20,123,'20:123','فرمایا کہ تم دونوں یہاں سے نیچے اتر جاؤ۔ تم میں بعض بعض کے دشمن (ہوں گے) پھر اگر میری طرف سے تمہارے پاس ہدایت آئے تو جو شخص میری ہدایت کی پیروی کرے گا وہ نہ گمراہ ہوگا اور نہ تکلیف میں پڑے گا');
INSERT INTO "jalandhari" VALUES(20,124,'20:124','اور جو میری نصیحت سے منہ پھیرے گا اس کی زندگی تنگ ہوجائے گی اور قیامت کو ہم اسے اندھا کرکے اٹھائیں گے');
INSERT INTO "jalandhari" VALUES(20,125,'20:125','وہ کہے گا میرے پروردگار تو نے مجھے اندھا کرکے کیوں اٹھایا میں تو دیکھتا بھالتا تھا');
INSERT INTO "jalandhari" VALUES(20,126,'20:126','خدا فرمائے گا کہ ایسا ہی (چاہیئے تھا) تیرے پاس میری آیتیں آئیں تو تونے ان کو بھلا دیا۔ اسی طرح آج ہم تجھ کو بھلا دیں گے');
INSERT INTO "jalandhari" VALUES(20,127,'20:127','اور جو شخص حد سے نکل جائے اور اپنے پروردگار کی آیتوں پر ایمان نہ لائے ہم اس کو ایسا ہی بدلہ دیتے ہیں۔ اور آخرت کا عذاب بہت سخت اور بہت دیر رہنے والا ہے');
INSERT INTO "jalandhari" VALUES(20,128,'20:128','کیا یہ بات ان لوگوں کے لئے موجب ہدایت نہ ہوئی کہ ہم ان سے پہلے بہت سے لوگوں کو ہلاک کرچکے ہیں جن کے رہنے کے مقامات میں یہ چلتے پھرتے ہیں۔ عقل والوں کے لئے اس میں (بہت سی) نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(20,129,'20:129','اور اگر ایک بات تمہارے پروردگار کی طرف سے پہلے صادر اور (جزائے اعمال کے لئے) ایک میعاد مقرر نہ ہوچکی ہوتی تو (نزول) عذاب لازم ہوجاتا');
INSERT INTO "jalandhari" VALUES(20,130,'20:130','پس جو کچھ یہ بکواس کرتے ہیں اس پر صبر کرو۔ اور سورج کے نکلنے سے پہلے اور اس کے غروب ہونے سے پہلے اپنے پروردگار کی تسبیح وتحمید کیا کرو۔ اور رات کی ساعات (اولین) میں بھی اس کی تسبیح کیا کرو اور دن کی اطراف (یعنی دوپہر کے قریب ظہر کے وقت بھی) تاکہ تم خوش ہوجاؤ');
INSERT INTO "jalandhari" VALUES(20,131,'20:131','اور کئی طرح کے لوگوں کو جو ہم نے دنیا کی زندگی میں آرائش کی چیزوں سے بہرہ مند کیا ہے تاکہ ان کی آزمائش کریں ان پر نگاہ نہ کرنا۔ اور تمہاری پروردگار کی (عطا فرمائی ہوئی) روزی بہت بہتر اور باقی رہنے والی ہے');
INSERT INTO "jalandhari" VALUES(20,132,'20:132','اور اپنے گھر والوں کو نماز کا حکم کرو اور اس پر قائم رہو۔ ہم تم سے روزی کے خواستگار نہیں۔ بلکہ تمہیں ہم روزی دیتے ہیں اور (نیک) انجام (اہل) تقویٰ کا ہے');
INSERT INTO "jalandhari" VALUES(20,133,'20:133','اور کہتے ہیں کہ یہ (پیغمبر) اپنے پروردگار کی طرف سے ہمارے پاس کوئی نشانی کیوں نہیں لاتے۔ کیا ان کے پاس پہلی کتابوں کی نشانی نہیں آئی؟');
INSERT INTO "jalandhari" VALUES(20,134,'20:134','اور اگر ہم ان کو پیغمبر (کے بھیجنے) سے پیشتر کسی عذاب سے ہلاک کردیتے تو وہ کہتے کہ اے ہمارے پروردگار تو نے ہماری طرف کوئی پیغمبر کیوں نہ بھیجا کہ ہم ذلیل اور رسوا ہونے سے پہلے تیرے کلام (واحکام) کی پیروی کرتے');
INSERT INTO "jalandhari" VALUES(20,135,'20:135','کہہ دو کہ سب (نتائج اعمال) کے منتظر ہیں سو تم بھی منتظر رہو۔ عنقریب تم کو معلوم ہوجائے گا کہ (دین کے) سیدھے رستے پر چلنے والے کون ہیں اور (جنت کی طرف) راہ پانے والے کون ہیں (ہم یا تم)');
INSERT INTO "jalandhari" VALUES(21,1,'21:1','لوگوں کا حساب (اعمال کا وقت) نزدیک آپہنچا ہے اور وہ غفلت میں (پڑے اس سے) منہ پھیر رہے ہیں');
INSERT INTO "jalandhari" VALUES(21,2,'21:2','ان کے پاس کوئی نئی نصیحت ان کے پروردگار کی طرف سے نہیں آتی مگر وہ اسے کھیلتے ہوئے سنتے ہیں');
INSERT INTO "jalandhari" VALUES(21,3,'21:3','ان کے دل غفلت میں پڑے ہوئے ہیں اور ظالم لوگ (آپس میں) چپکے چپکے باتیں کرتے ہیں کہ یہ (شخص کچھ بھی) نہیں مگر تمہارے جیسا آدمی ہے۔ تو تم آنکھوں دیکھتے جادو (کی لپیٹ) میں کیوں آتے ہو');
INSERT INTO "jalandhari" VALUES(21,4,'21:4','(پیغمبر نے) کہا کہ جو بات آسمان اور زمین میں (کہی جاتی) ہے میرا پروردگار اسے جانتا ہے۔ اور وہ سننے والا (اور) جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(21,5,'21:5','بلکہ (ظالم) کہنے لگے کہ (یہ قرآن) پریشان (باتیں ہیں جو) خواب (میں دیکھ لی) ہیں۔ (نہیں) بلکہ اس نے اس کو اپنی طرف سے بنا لیا ہے (نہیں) بلکہ (یہ شعر ہے جو اس) شاعر (کا نتیجہٴ طبع) ہے۔ تو جیسے پہلے (پیغمبر نشانیاں دے کر) بھیجے گئے تھے (اسی طرح) یہ بھی ہمارے پاس کوئی نشانی لائے');
INSERT INTO "jalandhari" VALUES(21,6,'21:6','ان سے پہلے جن بستیوں کو ہم نے ہلاک کیا وہ ایمان نہیں لاتی تھیں۔ تو کیا یہ ایمان لے آئیں گے');
INSERT INTO "jalandhari" VALUES(21,7,'21:7','اور ہم نے تم سے پہلے مرد ہی (پیغمبر بنا کر) بھیجے جن کی طرف ہم وحی بھیجتے تھے۔ اگر تم نہیں جانتے تو جو یاد رکھتے ہیں ان سے پوچھ لو');
INSERT INTO "jalandhari" VALUES(21,8,'21:8','اور ہم نے ان کے لئے ایسے جسم نہیں بنائے تھے کہ کھانا نہ کھائیں اور نہ وہ ہمیشہ رہنے والے تھے');
INSERT INTO "jalandhari" VALUES(21,9,'21:9','پھر ہم نے ان کے بارے میں (اپنا) وعدہ سچا کردیا تو ان کو اور جس کو چاہا نجات دی اور حد سے نکل جانے والوں کو ہلاک کردیا');
INSERT INTO "jalandhari" VALUES(21,10,'21:10','ہم نے تمہاری طرف ایسی کتاب نازل کی ہے جس میں تمہارا تذکرہ ہے۔ کیا تم نہیں سمجھتے');
INSERT INTO "jalandhari" VALUES(21,11,'21:11','اور ہم نے بہت سی بستیوں کو جو ستمگار تھیں ہلاک کر مارا اور ان کے بعد اور لوگ پیدا کردیئے');
INSERT INTO "jalandhari" VALUES(21,12,'21:12','جب انہوں نے ہمارے (مقدمہ) عذاب کو دیکھا تو لگے اس سے بھاگنے');
INSERT INTO "jalandhari" VALUES(21,13,'21:13','مت بھاگو اور جن (نعمتوں) میں تم عیش وآسائش کرتے تھے ان کی اور اپنے گھروں کی طرف لوٹ جاؤ۔ شاید تم سے (اس بارے میں) دریافت کیا جائے');
INSERT INTO "jalandhari" VALUES(21,14,'21:14','کہنے لگے ہائے شامت بےشک ہم ظالم تھے');
INSERT INTO "jalandhari" VALUES(21,15,'21:15','تو وہ ہمیشہ اسی طرح پکارتے رہے یہاں تک کہ ہم نے ان کو (کھیتی کی طرح) کاٹ کر (اور آگ کی طرح) بجھا کر ڈھیر کردیا');
INSERT INTO "jalandhari" VALUES(21,16,'21:16','اور ہم نے آسمان اور زمین کو جو اور (مخلوقات) ان دونوں کے درمیان ہے اس کو لہوولعب کے لئے پیدا نہیں کیا');
INSERT INTO "jalandhari" VALUES(21,17,'21:17','اگر ہم چاہتے کہ کھیل (کی چیزیں یعنی زن وفرزند) بنائیں تو اگر ہم کو کرنا ہوتا تو ہم اپنے پاس سے بنالیتے');
INSERT INTO "jalandhari" VALUES(21,18,'21:18','بلکہ ہم سچ کو جھوٹ پر کھینچ مارتے ہیں تو وہ اس کا سر توڑ دیتا ہے اور جھوٹ اسی وقت نابود ہوجاتا ہے۔ اور جو باتیں تم بناتے ہو ان سے تمہاری ہی خرابی ہے');
INSERT INTO "jalandhari" VALUES(21,19,'21:19','اور جو لوگ آسمانوں میں اور جو زمین میں ہیں سب اسی کے (مملوک اور اُسی کا مال) ہیں۔ اور جو (فرشتے) اُس کے پاس ہیں وہ اس کی عبادت سے نہ کنیاتے ہیں اور نہ اکتاتے ہیں');
INSERT INTO "jalandhari" VALUES(21,20,'21:20','رات دن (اُس کی) تسبیح کرتے رہتے ہیں (نہ تھکتے ہیں) نہ اکتاتے ہیں');
INSERT INTO "jalandhari" VALUES(21,21,'21:21','بھلا لوگوں نے جو زمین کی چیزوں سے (بعض کو) معبود بنا لیا ہے (تو کیا) وہ ان کو (مرنے کے بعد) اُٹھا کھڑا کریں گے؟');
INSERT INTO "jalandhari" VALUES(21,22,'21:22','اگر آسمان اور زمین میں خدا کے سوا اور معبود ہوتے تو زمین وآسمان درہم برہم ہوجاتے۔ جو باتیں یہ لوگ بتاتے ہیں خدائے مالک عرش ان سے پاک ہے');
INSERT INTO "jalandhari" VALUES(21,23,'21:23','وہ جو کام کرتا ہے اس کی پرستش نہیں ہوگی اور (جو کام یہ لوگ کرتے ہیں اس کی) ان سے پرستش ہوگی');
INSERT INTO "jalandhari" VALUES(21,24,'21:24','کیا لوگوں نے خدا کو چھوڑ کر اور معبود بنالئے ہیں۔ کہہ دو کہ (اس بات پر) اپنی دلیل پیش کرو۔ یہ (میری اور) میرے ساتھ والوں کی کتاب بھی ہے اور جو مجھ سے پہلے (پیغمبر) ہوئے ہیں۔ ان کی کتابیں بھی ہیں۔ بلکہ (بات یہ ہے کہ) ان اکثر حق بات کو نہیں جانتے اور اس لئے اس سے منہ پھیر لیتے ہیں');
INSERT INTO "jalandhari" VALUES(21,25,'21:25','اور جو پیغمبر ہم نےتم سے پہلے بھیجے ان کی طرف یہی وحی بھیجی کہ میرے سوا کوئی معبود نہیں تو میری ہی عبادت کرو');
INSERT INTO "jalandhari" VALUES(21,26,'21:26','اور کہتے ہیں کہ خدا بیٹا رکھتا ہے۔ وہ پاک ہے (اس کے نہ بیٹا ہے نہ بیٹی) بلکہ (جن کو یہ لوگ اس کے بیٹے بیٹیاں سمجھتے ہیں) وہ اس کے عزت والے بندے ہیں');
INSERT INTO "jalandhari" VALUES(21,27,'21:27','اس کے آگے بڑھ کر بول نہیں سکتے۔ اور اس کے حکم پر عمل کرتے ہیں');
INSERT INTO "jalandhari" VALUES(21,28,'21:28','جو کچھ ان کے آگے ہوچکا ہے اور پیچھے ہوگا وہ سب سے واقف ہے اور وہ (اس کے پاس کسی کی) سفارش نہیں کرسکتے مگر اس شخص کی جس سے خدا خوش ہو اور وہ اس کی ہیبت سے ڈرتے رہتے ہیں');
INSERT INTO "jalandhari" VALUES(21,29,'21:29','اور جو شخص ان میں سے یہ کہے کہ خدا کے سوا میں معبود ہوں تو اسے ہم دوزخ کی سزا دیں گے اور ظالموں کو ہم ایسی ہی سزا دیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(21,30,'21:30','کیا کافروں نے نہیں دیکھا کہ آسمان اور زمین دونوں ملے ہوئے تھے تو ہم نے جدا جدا کردیا۔ اور تمام جاندار چیزیں ہم نے پانی سے بنائیں۔ پھر یہ لوگ ایمان کیوں نہیں لاتے؟');
INSERT INTO "jalandhari" VALUES(21,31,'21:31','اور ہم نے زمین میں پہاڑ بنائے تاکہ لوگوں (کے بوجھ) سے ہلنے (اور جھکنے) نہ لگے اور اس میں کشادہ راستے بنائے تاکہ لوگ ان پر چلیں');
INSERT INTO "jalandhari" VALUES(21,32,'21:32','اور آسمان کو محفوظ چھت بنایا۔ اس پر بھی وہ ہماری نشانیوں سے منہ پھیر رہے ہیں');
INSERT INTO "jalandhari" VALUES(21,33,'21:33','اور وہی تو ہے جس نے رات اور دن اور سورج اور چاند کو بنایا۔ (یہ) سب (یعنی سورج اور چاند اور ستارے) آسمان میں (اس طرح چلتے ہیں گویا) تیر رہے ہیں');
INSERT INTO "jalandhari" VALUES(21,34,'21:34','اور (اے پیغمبر) ہم نے تم سے پہلے کسی آدمی کو بقائے دوام نہیں بخشا۔ بھلا اگر تم مرجاؤ تو کیا یہ لوگ ہمیشہ رہیں گے');
INSERT INTO "jalandhari" VALUES(21,35,'21:35','ہر متنفس کو موت کا مزا چکھنا ہے۔ اور ہم تو لوگوں کو سختی اور آسودگی میں آزمائش کے طور پر مبتلا کرتے ہیں۔ اور تم ہماری طرف ہی لوٹ کر آؤ گے');
INSERT INTO "jalandhari" VALUES(21,36,'21:36','اور جب کافر تم کو دیکھتے ہیں تو تم سے استہزاء کرتے ہیں کہ کیا یہی شخص ہے جو تمہارے معبودوں کا ذکر (برائی سے) کیا کرتا ہے حالانکہ وہ خود رحمٰن کے نام سے منکر ہیں');
INSERT INTO "jalandhari" VALUES(21,37,'21:37','انسان (کچھ ایسا جلد باز ہے کہ گویا) جلد بازی ہی سے بنایا گیا ہے۔ میں تم لوگوں کو عنقریب اپنی نشانیاں دکھاؤں گا تو تم جلدی نہ کرو');
INSERT INTO "jalandhari" VALUES(21,38,'21:38','اور کہتے ہیں کہ اگر تم سچے ہو تو (جس عذاب کا) یہ وعید (ہے وہ) کب (آئے گا)؟');
INSERT INTO "jalandhari" VALUES(21,39,'21:39','اے کاش کافر اس وقت کو جانیں جب وہ اپنے مونہوں پر سے (دوزخ کی) آگ کو روک نہ سکیں گے اور نہ اپنی پیٹھوں پر سے اور نہ ان کا کوئی مددگار ہوگا');
INSERT INTO "jalandhari" VALUES(21,40,'21:40','بلکہ قیامت ان پر ناگہاں آ واقع ہوگی۔ اور ان کے ہوش کھو دے گی۔ پھر نہ تو وہ اس کو ہٹا سکیں گے اور نہ ان کو مہلت دی جائے گی');
INSERT INTO "jalandhari" VALUES(21,41,'21:41','اور تم سے پہلے بھی پیغمبروں کے ساتھ استہزاء ہوتا رہا ہے تو جو لوگ ان میں سے تمسخر کیا کرتے تھے ان کو اسی (عذاب) نے جس کی ہنسی اُڑاتے تھے آگھیرا');
INSERT INTO "jalandhari" VALUES(21,42,'21:42','کہو کہ رات اور دن میں خدا سے تمہاری کون حفاظت کرسکتا ہے؟ بات یہ ہے کہ اپنے پروردگار کی یاد سے منہ پھیرے ہوئے ہیں');
INSERT INTO "jalandhari" VALUES(21,43,'21:43','کیا ہمارے سوا ان کے اور معبود ہیں کہ ان کو (مصائب سے) بچاسکیں۔ وہ آپ اپنی مدد تو کر ہی نہیں سکتے اور نہ ہم سے پناہ ہی دیئے جائیں گے');
INSERT INTO "jalandhari" VALUES(21,44,'21:44','بلکہ ہم ان لوگوں کو اور ان کے باپ دادا کو متمتع کرتے رہے یہاں تک کہ (اسی حالت میں) ان کی عمریں بسر ہوگئیں۔ کیا یہ نہیں دیکھتے کہ ہم زمین کو اس کے کناروں سے گھٹاتے چلے آتے ہیں۔ تو کیا یہ لوگ غلبہ پانے والے ہیں؟');
INSERT INTO "jalandhari" VALUES(21,45,'21:45','کہہ دو کہ میں تم کو حکم خدا کے مطابق نصیحت کرتا ہوں۔ اور بہروں کوجب نصیحت کی جائے تو وہ پکار کر سنتے ہی نہیں');
INSERT INTO "jalandhari" VALUES(21,46,'21:46','اور اگر ان کو تمہارے پروردگار کا تھوڑا سا عذاب بھی پہنچے تو کہنے لگیں کہ ہائے کم بختی ہم بےشک ستمگار تھے');
INSERT INTO "jalandhari" VALUES(21,47,'21:47','اور ہم قیامت کے دن انصاف کی ترازو کھڑی کریں گے تو کسی شخص کی ذرا بھی حق تلفی نہ کی جائے گی۔ اور اگر رائی کے دانے کے برابر بھی (کسی کا عمل) ہوگا تو ہم اس کو لاحاضر کریں گے۔ اور ہم حساب کرنے کو کافی ہیں');
INSERT INTO "jalandhari" VALUES(21,48,'21:48','اور ہم نے موسیٰ اور ہارون کو (ہدایت اور گمراہی میں) فرق کر دینے والی اور (سرتاپا) روشنی اور نصیحت (کی کتاب) عطا کی (یعنی) پرہیز گاروں کے لئے');
INSERT INTO "jalandhari" VALUES(21,49,'21:49','جو بن دیکھے اپنے پروردگار سے ڈرتے ہیں اور قیامت کا بھی خوف رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(21,50,'21:50','یہ مبارک نصیحت ہے جسے ہم نے نازل فرمایا ہے تو کیا تم اس سے انکار کرتے ہو؟');
INSERT INTO "jalandhari" VALUES(21,51,'21:51','اور ہم نے ابراہیمؑ کو پہلے ہی سے ہدایت دی تھی اور ہم ان کے حال سے واقف تھے');
INSERT INTO "jalandhari" VALUES(21,52,'21:52','جب انہوں نے اپنے باپ اور اپنی قوم کے لوگوں سے کہا یہ کیا مورتیں ہیں جن (کی پرستش) پر تم معتکف (وقائم) ہو؟');
INSERT INTO "jalandhari" VALUES(21,53,'21:53','وہ کہنے لگے کہ ہم نے اپنے باپ دادا کو ان کی پرستش کرتے دیکھا ہے');
INSERT INTO "jalandhari" VALUES(21,54,'21:54','(ابراہیم نے) کہا کہ تم بھی (گمراہ ہو) اور تمہارے باپ دادا بھی صریح گمراہی میں پڑے رہے');
INSERT INTO "jalandhari" VALUES(21,55,'21:55','وہ بولے کیا تم ہمارے پاس (واقعی) حق لائے ہو یا (ہم سے) کھیل (کی باتیں) کرتے ہو؟');
INSERT INTO "jalandhari" VALUES(21,56,'21:56','(ابراہیم نے) کہا (نہیں) بلکہ تمہارا پروردگار آسمانوں اور زمین کا پروردگار ہے جس نے ان کو پیدا کیا ہے۔ اور میں اس (بات) کا گواہ (اور اسی کا قائل) ہوں');
INSERT INTO "jalandhari" VALUES(21,57,'21:57','اور خدا کی قسم جب تم پیٹھ پھیر کر چلے جاؤ گے تو میں تمہارے بتوں سے ایک چال چلوں گا');
INSERT INTO "jalandhari" VALUES(21,58,'21:58','پھر ان کو توڑ کر ریزہ ریزہ کردیا مگر ایک بڑے (بت) کو (نہ توڑا) تاکہ وہ اس کی طرف رجوع کریں');
INSERT INTO "jalandhari" VALUES(21,59,'21:59','کہنے لگے کہ ہمارے معبودوں کے ساتھ یہ معاملہ کس نے کیا؟ وہ تو کوئی ظالم ہے');
INSERT INTO "jalandhari" VALUES(21,60,'21:60','لوگوں نے کہا کہ ہم نے ایک جوان کو ان کا ذکر کرتے ہوئے سنا ہے اس کو ابراہیم کہتے ہیں');
INSERT INTO "jalandhari" VALUES(21,61,'21:61','وہ بولے کہ اسے لوگوں کے سامنے لاؤ تاکہ گواہ رہیں');
INSERT INTO "jalandhari" VALUES(21,62,'21:62','(جب ابراہیم آئے تو) بت پرستوں نے کہا کہ ابراہیم بھلا یہ کام ہمارے معبودوں کے ساتھ تم نے کیا ہے؟');
INSERT INTO "jalandhari" VALUES(21,63,'21:63','(ابراہیم نے) کہا (نہیں) بلکہ یہ ان کے اس بڑے (بت) نے کیا (ہوگا)۔ اگر یہ بولتے ہیں تو ان سے پوچھ لو');
INSERT INTO "jalandhari" VALUES(21,64,'21:64','انہوں نے اپنے دل غور کیا تو آپس میں کہنے لگے بےشک تم ہی بےانصاف ہو');
INSERT INTO "jalandhari" VALUES(21,65,'21:65','پھر (شرمندہ ہو کر) سر نیچا کرلیا (اس پر بھی ابراہیم سے کہنے لگے کہ) تم جانتے ہو یہ بولتے نہیں');
INSERT INTO "jalandhari" VALUES(21,66,'21:66','(ابراہیم نے) کہا پھر تم خدا کو چھوڑ کر کیوں ایسی چیزوں کو پوجتے ہو جو نہ تمہیں کچھ فائدہ دے سکیں اور نقصان پہنچا سکیں؟');
INSERT INTO "jalandhari" VALUES(21,67,'21:67','تف ہے تم پر اور جن کو تم خدا کے سوا پوجتے ہو ان پر بھی کیا تم عقل نہیں رکھتے؟');
INSERT INTO "jalandhari" VALUES(21,68,'21:68','(تب وہ) کہنے لگے کہ اگر تمہیں (اس سے اپنے معبود کا انتقام لینا اور) کچھ کرنا ہے تو اس کو جلا دو اور اپنے معبودوں کی مدد کرو');
INSERT INTO "jalandhari" VALUES(21,69,'21:69','ہم نے حکم دیا اے آگ سرد ہوجا اور ابراہیم پر (موجب) سلامتی (بن جا)');
INSERT INTO "jalandhari" VALUES(21,70,'21:70','اور ان لوگوں نے برا تو ان کا چاہا تھا مگر ہم نے ان ہی کو نقصان میں ڈال دیا');
INSERT INTO "jalandhari" VALUES(21,71,'21:71','اور ابراہیم اور لوط کو اس سرزمین کی طرف بچا نکالا جس میں ہم نے اہل عالم کے لئے برکت رکھی تھی');
INSERT INTO "jalandhari" VALUES(21,72,'21:72','اور ہم نے ابراہیم کو اسحق عطا کئے۔ اور مستزاد برآں یعقوب۔ اور سب کو نیک بخت کیا');
INSERT INTO "jalandhari" VALUES(21,73,'21:73','اور ان کو پیشوا بنایا کہ ہمارے حکم سے ہدایت کرتے تھے اور ان کو نیک کام کرنے اور نماز پڑھنے اور زکوٰة دینے کا حکم بھیجا۔ اور وہ ہماری عبادت کیا کرتے تھے');
INSERT INTO "jalandhari" VALUES(21,74,'21:74','اور لوط (کا قصہ یاد کرو) جب ان کو ہم نے حکم (یعنی حکمت ونبوت) اور علم بخشا اور اس بستی سے جہاں کے لوگ گندے کام کیا کرتے تھے۔ بچا نکالا۔ بےشک وہ برے اور بدکردار لوگ تھے');
INSERT INTO "jalandhari" VALUES(21,75,'21:75','اور انہیں اپنی رحمت کے (محل میں) داخل کیا۔ کچھ شک نہیں کہ وہ نیک بختوں میں تھے');
INSERT INTO "jalandhari" VALUES(21,76,'21:76','اور نوح (کا قصہ بھی یاد کرو) جب (اس سے) پیشتر انہوں نے ہم کو پکارا تو ہم نے ان کی دعا قبول فرمائی اور ان کو اور ان کے ساتھیوں کو بڑی گھبراہٹ سے نجات دی');
INSERT INTO "jalandhari" VALUES(21,77,'21:77','اور جو لوگ ہماری آیتوں کی تکذیب کرتے تھے ان پر نصرت بخشی۔ وہ بےشک برے لوگ تھے سو ہم نے ان سب کو غرق کردیا');
INSERT INTO "jalandhari" VALUES(21,78,'21:78','اور داؤد اور سلیمان (کا حال بھی سن لو کہ) جب وہ ایک کھیتی کا مقدمہ فیصلہ کرنے لگے جس میں کچھ لوگوں کی بکریاں رات کو چر گئی (اور اسے روند گئی) تھیں اور ہم ان کے فیصلے کے وقت موجود تھے');
INSERT INTO "jalandhari" VALUES(21,79,'21:79','تو ہم نے فیصلہ (کرنے کا طریق) سلیمان کو سمجھا دیا۔ اور ہم نے دونوں کو حکم (یعنی حکمت ونبوت) اور علم بخشا تھا۔ اور ہم نے پہاڑوں کو داؤد کا مسخر کردیا تھا کہ ان کے ساتھ تسبیح کرتے تھے اور جانوروں کو بھی (مسخر کردیا تھا اور ہم ہی ایسا) کرنے والے تھے');
INSERT INTO "jalandhari" VALUES(21,80,'21:80','اور ہم نے تمہارے لئے ان کو ایک (طرح) کا لباس بنانا بھی سکھا دیا تاکہ تم کو لڑائی (کے ضرر) سے بچائے۔ پس تم کو شکرگزار ہونا چاہیئے');
INSERT INTO "jalandhari" VALUES(21,81,'21:81','اور ہم نے تیز ہوا سلیمان کے تابع (فرمان) کردی تھی جو ان کے حکم سے اس ملک میں چلتی تھی جس میں ہم نے برکت دی تھی (یعنی شام) اور ہم ہر چیز سے خبردار ہیں');
INSERT INTO "jalandhari" VALUES(21,82,'21:82','اور دیوؤں (کی جماعت کو بھی ان کے تابع کردیا تھا کہ ان) میں سے بعض ان کے لئے غوطے مارتے تھے اور اس کے سوا اور کام بھی کرتے تھے اور ہم ان کے نگہبان تھے');
INSERT INTO "jalandhari" VALUES(21,83,'21:83','اور ایوب کو (یاد کرو) جب انہوں نے اپنے پروردگار سے دعا کی کہ مجھے ایذا ہو رہی ہے اور تو سب سے بڑھ کر رحم کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(21,84,'21:84','تو ہم نے ان کی دعا قبول کرلی اور جو ان کو تکلیف تھی وہ دور کردی اور ان کو بال بچے بھی عطا فرمائے اور اپنی مہربانی کے ساتھ اتنے ہی اور (بخشے) اور عبادت کرنے والوں کے لئے (یہ) نصیحت ہے');
INSERT INTO "jalandhari" VALUES(21,85,'21:85','اور اسمٰعیل اور ادریس اور ذوالکفل (کو بھی یاد کرو) یہ سب صبر کرنے والے تھے');
INSERT INTO "jalandhari" VALUES(21,86,'21:86','اور ہم نے ان کو اپنی رحمت میں داخل کیا۔ بلاشبہ وہ نیکوکار تھے');
INSERT INTO "jalandhari" VALUES(21,87,'21:87','اور ذوالنون (کو یاد کرو) جب وہ (اپنی قوم سے ناراض ہو کر) غصے کی حالت میں چل دیئے اور خیال کیا کہ ہم ان پر قابو نہیں پاسکیں گے۔ آخر اندھیرے میں (خدا کو) پکارنے لگے کہ تیرے سوا کوئی معبود نہیں۔ تو پاک ہے (اور) بےشک میں قصوروار ہوں');
INSERT INTO "jalandhari" VALUES(21,88,'21:88','تو ہم نے ان کی دعا قبول کرلی اور ان کو غم سے نجات بخشی۔ اور ایمان والوں کو ہم اسی طرح نجات دیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(21,89,'21:89','اور زکریا (کو یاد کرو) جب انہوں نے اپنے پروردگار کو پکارا کہ پروردگار مجھے اکیلا نہ چھوڑ اور تو سب سے بہتر وارث ہے');
INSERT INTO "jalandhari" VALUES(21,90,'21:90','تو ہم نے ان کی پکار سن لی۔ اور ان کو یحییٰ بخشے اور ان کی بیوی کو اُن کے (حسن معاشرت کے) قابل بنادیا۔ یہ لوگ لپک لپک کر نیکیاں کرتے اور ہمیں امید سے پکارتے اور ہمارے آگے عاجزی کیا کرتے تھے');
INSERT INTO "jalandhari" VALUES(21,91,'21:91','اور ان (مریم) کو (بھی یاد کرو) جنہوں نے اپنی عفّت کو محفوظ رکھا۔ تو ہم نے ان میں اپنی روح پھونک دی اور ان کے بیٹے کو اہل عالم کے لئے نشانی بنا دیا');
INSERT INTO "jalandhari" VALUES(21,92,'21:92','یہ تمہاری جماعت ایک ہی جماعت ہے اور میں تمہارا پروردگار ہوں تو میری ہی عبادت کیا کرو');
INSERT INTO "jalandhari" VALUES(21,93,'21:93','اور یہ لوگ اپنے معاملے میں باہم متفرق ہوگئے۔ (مگر) سب ہماری طرف رجوع کرنے والے ہیں');
INSERT INTO "jalandhari" VALUES(21,94,'21:94','جو نیک کام کرے گا اور مومن بھی ہوگا تو اس کی کوشش رائیگاں نہ جائے گی۔ اور ہم اس کے لئے (ثواب اعمال) لکھ رہے ہیں');
INSERT INTO "jalandhari" VALUES(21,95,'21:95','اور جس بستی (والوں) کو ہم نے ہلاک کردیا محال ہے کہ (وہ دنیا کی طرف رجوع کریں) وہ رجوع نہیں کریں گے');
INSERT INTO "jalandhari" VALUES(21,96,'21:96','یہاں تک کہ یاجوج ماجوج کھول دیئے جائیں اور وہ ہر بلندی سے دوڑ رہے ہوں');
INSERT INTO "jalandhari" VALUES(21,97,'21:97','اور (قیامت کا) سچا وعدہ قریب آجائے تو ناگاہ کافروں کی آنکھیں کھلی کی کھلی رہ جائیں (اور کہنے لگیں کہ) ہائے شامت ہم اس (حال) سے غفلت میں رہے بلکہ (اپنے حق میں) ظالم تھے');
INSERT INTO "jalandhari" VALUES(21,98,'21:98','(کافرو اس روز) تم اور جن کی تم خدا کے سوا عبادت کرتے ہو دوزخ کا ایندھن ہوں گے۔ اور تم سب اس میں داخل ہو کر رہو گے');
INSERT INTO "jalandhari" VALUES(21,99,'21:99','اگر یہ لوگ (درحقیقت) معبود ہوتے تو اس میں داخل نہ ہوتے۔ سب اس میں ہمیشہ (جلتے) رہیں گے');
INSERT INTO "jalandhari" VALUES(21,100,'21:100','وہاں ان کو چلاّنا ہوگا اور اس میں (کچھ) نہ سن سکیں گے');
INSERT INTO "jalandhari" VALUES(21,101,'21:101','جن لوگوں کے لئے ہماری طرف سے پہلے بھلائی مقرر ہوچکی ہے۔ وہ اس سے دور رکھے جائیں گے');
INSERT INTO "jalandhari" VALUES(21,102,'21:102','(یہاں تک کہ) اس کی آواز بھی تو نہیں سنیں گے۔ اور جو کچھ ان کا جی چاہے گا اس میں (یعنی) ہر طرح کے عیش اور لطف میں ہمیشہ رہیں گے');
INSERT INTO "jalandhari" VALUES(21,103,'21:103','ان کو (اس دن کا) بڑا بھاری خوف غمگین نہیں کرے گا۔ اور فرشتے ان کو لینے آئیں گے (اور کہیں گے کہ) یہی وہ دن ہے جس کا تم سے وعدہ کیا جاتا ہے');
INSERT INTO "jalandhari" VALUES(21,104,'21:104','جس دن ہم آسمان کو اس طرح لپیٹ لیں گے جیسے خطوں کا طومار لپیٹ لیتے ہیں۔ جس طرح ہم نے (کائنات کو) پہلے پیدا کیا اسی طرح دوبارہ پیدا کردیں گے۔ (یہ) وعدہ (جس کا پورا کرنا لازم) ہے۔ ہم (ایسا) ضرور کرنے والے ہیں');
INSERT INTO "jalandhari" VALUES(21,105,'21:105','اور ہم نے نصیحت (کی کتاب یعنی تورات) کے بعد زبور میں لکھ دیا تھا کہ میرے نیکوکار بندے ملک کے وارث ہوں گے');
INSERT INTO "jalandhari" VALUES(21,106,'21:106','عبادت کرنے والے لوگوں کے لئے اس میں (خدا کے حکموں کی) تبلیغ ہے');
INSERT INTO "jalandhari" VALUES(21,107,'21:107','اور (اے محمدصلى الله عليه وسلم) ہم نے تم کو تمام جہان کے لئے رحمت (بنا کر) بھیجا ہے');
INSERT INTO "jalandhari" VALUES(21,108,'21:108','کہہ دو کہ مجھ پر (خدا کی طرح سے) یہ وحی آتی ہے کہ تم سب کا معبود خدائے واحد ہے۔ تو تم کو چاہیئے کہ فرمانبردار بن جاؤ');
INSERT INTO "jalandhari" VALUES(21,109,'21:109','اگر یہ لوگ منہ پھیریں تو کہہ دو کہ میں نے تم کو سب کو یکساں (احکام الہیٰ سے) آگاہ کردیا ہے۔ اور مجھ کو معلوم نہیں کہ جس چیز کا تم سے وعدہ کیا جاتا ہے وہ (عن) قریب (آنے والی) ہے یا (اس کا وقت) دور ہے');
INSERT INTO "jalandhari" VALUES(21,110,'21:110','اور جو بات پکار کی جائے وہ اسے بھی جانتا ہے اور جو تم پوشیدہ کرتے ہو اس سے بھی واقف ہے');
INSERT INTO "jalandhari" VALUES(21,111,'21:111','اور میں نہیں جانتا شاید وہ تمہارے لئے آزمائش ہو اور ایک مدت تک (تم اس سے) فائدہ (اٹھاتے رہو)');
INSERT INTO "jalandhari" VALUES(21,112,'21:112','پیغمبر نے کہا کہ اے میرے پروردگار حق کے ساتھ فیصلہ کردے۔ اور ہمارا پروردگار بڑا مہربان ہے اسی سے ان باتوں میں جو تم بیان کرتے ہو مدد مانگی جاتی ہے');
INSERT INTO "jalandhari" VALUES(22,1,'22:1','لوگو! اپنے پروردگار سے ڈرو۔ کہ قیامت کا زلزلہ ایک حادثہٴ عظیم ہوگا');
INSERT INTO "jalandhari" VALUES(22,2,'22:2','(اے مخاطب) جس دن تو اس کو دیکھے گا (اُس دن یہ حال ہوگا کہ) تمام دودھ پلانے والی عورتیں اپنے بچوں کو بھول جائیں گی۔ اور تمام حمل والیوں کے حمل گر پڑیں گے۔ اور لوگ تجھ کو متوالے نظر آئیں گے مگر وہ متوالے نہیں ہوں گے بلکہ (عذاب دیکھ کر) مدہوش ہو رہے ہوں گے۔ بےشک خدا کا عذاب بڑا سخت ہے');
INSERT INTO "jalandhari" VALUES(22,3,'22:3','اور بعض لوگ ایسے ہیں جو خدا (کی شان) میں علم (ودانش) کے بغیر جھگڑتے اور ہر شیطان سرکش کی پیروی کرتے ہیں');
INSERT INTO "jalandhari" VALUES(22,4,'22:4','جس کے بارے میں لکھ دیا گیا ہے کہ جو اسے دوست رکھے گا تو اس کو گمراہ کردے گا اور دوزخ کے عذاب کا رستہ دکھائے گا');
INSERT INTO "jalandhari" VALUES(22,5,'22:5','لوگو اگر تم کو مرنے کے بعد جی اُٹھنے میں کچھ شک ہو تو ہم نے تم کو (پہلی بار بھی تو) پیدا کیا تھا (یعنی ابتدا میں) مٹی سے پھر اس سے نطفہ بنا کر۔ پھر اس سے خون کا لوتھڑا بنا کر۔ پھر اس سے بوٹی بنا کر جس کی بناوٹ کامل بھی ہوتی ہے اور ناقص بھی تاکہ تم پر (اپنی خالقیت) ظاہر کردیں۔ اور ہم جس کو چاہتے ہیں ایک میعاد مقرر تک پیٹ میں ٹھہرائے رکھتے ہیں پھر تم کو بچہ بنا کر نکالتے ہیں۔ پھر تم جوانی کو پہنچتے ہو۔ اور بعض (قبل از پیری مرجاتے ہیں اور بعض شیخ فالی ہوجاتے اور بڑھاپے کی) نہایت خراب عمر کی طرف لوٹائے جاتے ہیں کہ بہت کچھ جاننے کے بعد بالکل بےعلم ہوجاتے ہیں۔ اور (اے دیکھنے والے) تو دیکھتا ہے (کہ ایک وقت میں) زمین خشک (پڑی ہوتی ہے) پھر جب ہم اس پر مینہ برساتے ہیں تو شاداب ہوجاتی اور ابھرنے لگتی ہے اور طرح طرح کی بارونق چیزیں اُگاتی ہے');
INSERT INTO "jalandhari" VALUES(22,6,'22:6','ان قدرتوں سے ظاہر ہے کہ خدا ہی (قادر مطلق ہے جو) برحق ہے اور یہ کہ وہ مردوں کو زندہ کردیتا ہے۔ اور یہ کہ وہ ہر چیز پر قدرت رکھتا ہے');
INSERT INTO "jalandhari" VALUES(22,7,'22:7','اور یہ کہ قیامت آنے والی ہے۔ اس میں کچھ شک نہیں۔ اور یہ کہ خدا سب لوگوں کو جو قبروں میں ہیں جلا اٹھائے گا');
INSERT INTO "jalandhari" VALUES(22,8,'22:8','اور لوگوں میں کوئی ایسا بھی ہے جو خدا (کی شان) میں بغیر علم (ودانش) کے اور بغیر ہدایت کے اور بغیر کتاب روشن کے جھگڑتا ہے');
INSERT INTO "jalandhari" VALUES(22,9,'22:9','(اور تکبر سے) گردن موڑ لیتا (ہے) تاکہ (لوگوں کو) خدا کے رستے سے گمراہ کردے۔ اس کے لئے دنیا میں ذلت ہے۔ اور قیامت کے دن ہم اسے عذاب (آتش) سوزاں کا مزہ چکھائیں گے');
INSERT INTO "jalandhari" VALUES(22,10,'22:10','(اے سرکش) یہ اس (کفر) کی سزا ہے جو تیرے ہاتھوں نے آگے بھیجا ہے اور خدا اپنے بندوں پر ظلم کرنے والا نہیں');
INSERT INTO "jalandhari" VALUES(22,11,'22:11','اور لوگوں میں بعض ایسا بھی ہے جو کنارے پر (کھڑا ہو کر) خدا کی عبادت کرتا ہے۔ اگر اس کو کوئی (دنیاوی) فائدہ پہنچے تو اس کے سبب مطمئن ہوجائے اور اگر کوئی آفت پڑے تو منہ کے بل لوٹ جائے (یعنی پھر کافر ہوجائے) اس نے دنیا میں بھی نقصان اٹھایا اور آخرت میں بھی۔ یہی تو نقصان صریح ہے');
INSERT INTO "jalandhari" VALUES(22,12,'22:12','یہ خدا کے سوا ایسی چیز کو پکارتا ہے جو نہ اسے نقصان پہنچائے اور نہ فائدہ دے سکے۔ یہی تو پرلے درجے کی گمراہی ہے');
INSERT INTO "jalandhari" VALUES(22,13,'22:13','ایسے شخص کو پکارتا ہے جس کا نقصان فائدہ سے زیادہ قریب ہے۔ ایسا دوست برا بھی اور ایسا ہم صحبت بھی برا');
INSERT INTO "jalandhari" VALUES(22,14,'22:14','جو لوگ ایمان لائے اور عمل نیک کرتے رہے خدا ان کو بہشتوں میں داخل کرے گا جن کے نیچے نہریں چل رہیں ہیں۔ کچھ شک نہیں کہ خدا جو چاہتا ہے کرتا ہے');
INSERT INTO "jalandhari" VALUES(22,15,'22:15','جو شخص یہ گمان کرتا ہے کہ خدا اس کو دنیا اور آخرت میں مدد نہیں دے گا تو اس کو چاہیئے کہ اوپر کی طرف (یعنی اپنے گھر کی چھت میں) ایک رسی باندھے پھر (اس سے اپنا) گلا گھونٹ لے۔ پھر دیکھے کہ آیا یہ تدبیر اس کے غصے کو دور کردیتی ہے');
INSERT INTO "jalandhari" VALUES(22,16,'22:16','اور اسی طرح ہم نے اس قرآن کو اُتارا ہے (جس کی تمام) باتیں کھلی ہوئی (ہیں) اور یہ (یاد رکھو) کہ خدا جس کو چاہتا ہے ہدایات دیتا ہے');
INSERT INTO "jalandhari" VALUES(22,17,'22:17','جو لوگ مومن (یعنی مسلمان) ہیں اور جو یہودی ہیں اور ستارہ پرست اور عیسائی اور مجوسی اور مشرک۔ خدا ان (سب) میں قیامت کے دن فیصلہ کردے گا۔ بےشک خدا ہر چیز سے باخبر ہے');
INSERT INTO "jalandhari" VALUES(22,18,'22:18','کیا تم نے نہیں دیکھا کہ جو (مخلوق) آسمانوں میں ہے اور جو زمین میں ہے اور سورج اور چاند ستارے اور پہاڑ اور درخت اور چار پائے اور بہت سے انسان خدا کو سجدہ کرتے ہیں۔ اور بہت سے ایسے ہیں جن پر عذاب ثابت ہوچکا ہے۔ اور جس شخص کو خدا ذلیل کرے اس کو عزت دینے والا نہیں۔ بےشک خدا جو چاہتا ہے کرتا ہے');
INSERT INTO "jalandhari" VALUES(22,19,'22:19','یہ دو (فریق) ایک دوسرے کے دشمن اپنے پروردگار (کے بارے) میں جھگڑتے ہیں۔ تو کافر ہیں ان کے لئے آگ کے کپڑے قطع کئے جائیں گے (اور) ان کے سروں پر جلتا ہوا پانی ڈالا جائے گا');
INSERT INTO "jalandhari" VALUES(22,20,'22:20','اس سے ان کے پیٹ کے اندر کی چیزیں اور کھالیں گل جائیں گی');
INSERT INTO "jalandhari" VALUES(22,21,'22:21','اور ان (کے مارنے ٹھوکنے) کے لئے لوہے کے ہتھوڑے ہوں گے');
INSERT INTO "jalandhari" VALUES(22,22,'22:22','جب وہ چاہیں گے کہ اس رنج (وتکلیف) کی وجہ سے دوزخ سے نکل جائیں تو پھر اسی میں لوٹا دیئے جائیں گے۔ اور (کہا جائے گا کہ) جلنے کے عذاب کا مزہ چکھتے رہو');
INSERT INTO "jalandhari" VALUES(22,23,'22:23','جو لوگ ایمان لائے اور عمل نیک کرتے رہے خدا ان کو بہشتوں میں داخل کرے گا جن کے تلے نہریں بہہ رہیں ہیں۔ وہاں ان کو سونے کے کنگن پہنائے جائیں گے اور موتی۔ اور وہاں ان کا لباس ریشمی ہوگا');
INSERT INTO "jalandhari" VALUES(22,24,'22:24','اور ان کو پاکیزہ کلام کی ہدایت کی گئی اور (خدائے) حمید کی راہ بتائی گئی');
INSERT INTO "jalandhari" VALUES(22,25,'22:25','جو لوگ کافر ہیں اور (لوگوں کو) خدا کے رستے سے اور مسجد محترم سے جسے ہم نے لوگوں کے لئے یکساں (عبادت گاہ) بنایا ہے روکتے ہیں۔ خواہ وہاں کے رہنے والے ہوں یا باہر سے آنے والے۔ اور جو اس میں شرارت سے کج روی (وکفر) کرنا چاہے اس کو ہم درد دینے والے عذاب کا مزہ چکھائیں گے۔');
INSERT INTO "jalandhari" VALUES(22,26,'22:26','(اور ایک وقت تھا) جب ہم نے ابراہیم کے لئے خانہ کعبہ کو مقرر کیا (اور ارشاد فرمایا) کہ میرے ساتھ کسی چیز کو شریک نہ کیجیو اور طواف کرنے والوں اور قیام کرنے والوں اور رکوع کرنے والوں (اور) سجدہ کرنے والوں کے لئے میرے گھر کو صاف رکھا کرو');
INSERT INTO "jalandhari" VALUES(22,27,'22:27','اور لوگوں میں حج کے لئے ندا کر دو کہ تمہاری پیدل اور دبلے دبلے اونٹوں پر جو دور دراز رستوں سے چلے آتے ہو (سوار ہو کر) چلے آئیں');
INSERT INTO "jalandhari" VALUES(22,28,'22:28','تاکہ اپنے فائدے کے کاموں کے لئے حاضر ہوں۔ اور (قربانی کے) ایام معلوم میں چہار پایاں مویشی (کے ذبح کے وقت) جو خدا نے ان کو دیئے ہیں ان پر خدا کا نام لیں۔ اس میں سے تم خود بھی کھاؤ اور فقیر درماندہ کو بھی کھلاؤ');
INSERT INTO "jalandhari" VALUES(22,29,'22:29','پھر چاہیئے کہ لوگ اپنا میل کچیل دور کریں اور نذریں پوری کریں اور خانہٴ قدیم (یعنی بیت الله) کا طواف کریں');
INSERT INTO "jalandhari" VALUES(22,30,'22:30','یہ (ہمارا حکم ہے) جو شخص ادب کی چیزوں کی جو خدا نے مقرر کی ہیں عظمت رکھے تو یہ پروردگار کے نزدیک اس کے حق میں بہتر ہے۔ اور تمہارے لئے مویشی حلال کردیئے گئے ہیں۔ سوا ان کے جو تمہیں پڑھ کر سنائے جاتے ہیں تو بتوں کی پلیدی سے بچو اور جھوٹی بات سے اجتناب کرو');
INSERT INTO "jalandhari" VALUES(22,31,'22:31','صرف ایک خدا کے ہو کر اس کے ساتھ شریک نہ ٹھیرا کر۔ اور جو شخص (کسی کو) خدا کے ساتھ شریک مقرر کرے تو وہ گویا ایسا ہے جیسے آسمان سے گر پڑے پھر اس کو پرندے اُچک لے جائیں یا ہوا کسی دور جگہ اُڑا کر پھینک دے');
INSERT INTO "jalandhari" VALUES(22,32,'22:32','(یہ ہمارا حکم ہے) اور جو شخص ادب کی چیزوں کی جو خدا نے مقرر کی ہیں عظمت رکھے تو یہ (فعل) دلوں کی پرہیزگاری میں سے ہے');
INSERT INTO "jalandhari" VALUES(22,33,'22:33','ان میں ایک وقت مقرر تک تمہارے لئے فائدے ہیں پھر ان کو خانہٴ قدیم (یعنی بیت الله) تک پہنچانا (اور ذبح ہونا) ہے');
INSERT INTO "jalandhari" VALUES(22,34,'22:34','اور ہم نے ہر اُمت کے لئے قربانی کا طریق مقرر کردیا ہے تاکہ جو مویشی چارپائے خدا نے ان کو دیئے ہیں (ان کے ذبح کرنے کے وقت) ان پر خدا کا نام لیں۔ سو تمہارا معبود ایک ہی ہے تو اسی کے فرمانبردار ہوجاؤ۔ اور عاجزی کرنے والوں کو خوشخبری سنادو');
INSERT INTO "jalandhari" VALUES(22,35,'22:35','یہ وہ لوگ ہیں کہ جب خدا کا نام لیا جاتا ہے تو ان کے دل ڈر جاتے ہیں اور جب ان پر مصیبت پڑتی ہے تو صبر کرتے ہیں اور نماز آداب سے پڑھتے ہیں اور جو (مال) ہم نے ان کو عطا فرمایا ہے (اس میں سے) (نیک کاموں میں) خرچ کرتے ہیں');
INSERT INTO "jalandhari" VALUES(22,36,'22:36','اور قربانی کے اونٹوں کو بھی ہم نے تمہارے لئے شعائر خدا مقرر کیا ہے۔ ان میں تمہارے لئے فائدے ہیں۔ تو (قربانی کرنے کے وقت) قطار باندھ کر ان پر خدا کا نام لو۔ جب پہلو کے بل گر پڑیں تو ان میں سے کھاؤ اور قناعت سے بیٹھ رہنے والوں اور سوال کرنے والوں کو بھی کھلاؤ۔ اس طرح ہم نے ان کو تمہارے زیرفرمان کردیا ہے تاکہ تم شکر کرو');
INSERT INTO "jalandhari" VALUES(22,37,'22:37','خدا تک نہ اُن کا گوشت پہنچتا ہے اور نہ خون۔ بلکہ اس تک تمہاری پرہیزگاری پہنچتی ہے۔ اسی طرح خدا نے ان کو تمہارا مسخر کر دیا ہے تاکہ اس بات کے بدلے کہ اس نے تم کو ہدایت بخشی ہے اسے بزرگی سے یاد کرو۔ اور (اے پیغمبر) نیکوکاروں کو خوشخبری سنا دو');
INSERT INTO "jalandhari" VALUES(22,38,'22:38','خدا تو مومنوں سے ان کے دشمنوں کو ہٹاتا رہتا ہے۔ بےشک خدا کسی خیانت کرنے والے اور کفران نعمت کرنے والے کو دوست نہیں رکھتا۔');
INSERT INTO "jalandhari" VALUES(22,39,'22:39','جن مسلمانوں سے (خواہ مخواہ) لڑائی کی جاتی ہے ان کو اجازت ہے (کہ وہ بھی لڑیں) کیونکہ ان پر ظلم ہو رہا ہے۔ اور خدا (ان کی مدد کرے گا وہ) یقیناً ان کی مدد پر قادر ہے');
INSERT INTO "jalandhari" VALUES(22,40,'22:40','یہ وہ لوگ ہیں کہ اپنے گھروں سے ناحق نکال دیئے گئے (انہوں نے کچھ قصور نہیں کیا) ہاں یہ کہتے ہیں کہ ہمارا پروردگار خدا ہے۔ اور اگر خدا لوگوں کو ایک دوسرے سے نہ ہٹاتا رہتا تو (راہبوں کے) صومعے اور (عیسائیوں کے) گرجے اور (یہودیوں کے) عبادت خانے اور (مسلمانوں کی) مسجدیں جن میں خدا کا بہت سا ذکر کیا جاتا ہے ویران ہوچکی ہوتیں۔ اور جو شخص خدا کی مدد کرتا ہے خدا اس کی ضرور مدد کرتا ہے۔ بےشک خدا توانا اور غالب ہے');
INSERT INTO "jalandhari" VALUES(22,41,'22:41','یہ وہ لوگ ہیں کہ اگر ہم ان کو ملک میں دسترس دیں تو نماز پڑھیں اور زکوٰة ادا کریں اور نیک کام کرنے کا حکم دیں اور برے کاموں سے منع کریں اور سب کاموں کا انجام خدا ہی کے اختیار میں ہے');
INSERT INTO "jalandhari" VALUES(22,42,'22:42','اور اگر یہ لوگ تم کو جھٹلاتے ہیں ان سے پہلے نوح کی قوم اور عاد وثمود بھی (اپنے پیغمبروں کو) جھٹلا چکے ہیں');
INSERT INTO "jalandhari" VALUES(22,43,'22:43','اور قوم ابراہیم اور قوم لوط بھی');
INSERT INTO "jalandhari" VALUES(22,44,'22:44','اور مدین کے رہنے والے بھی۔ اور موسیٰ بھی تو جھٹلائے جاچکے ہیں لیکن میں کافروں کو مہلت دیتا رہا پھر ان کو پکڑ لیا۔ تو (دیکھ لو) کہ میرا عذاب کیسا (سخت) تھا');
INSERT INTO "jalandhari" VALUES(22,45,'22:45','اور بہت سی بستیاں ہیں کہ ہم نے ان کو تباہ کر ڈالا کہ وہ نافرمان تھیں۔ سو وہ اپنی چھتوں پر گری پڑی ہیں۔ اور (بہت سے) کنوئیں بےکار اور (بہت سے) محل ویران پڑے ہیں');
INSERT INTO "jalandhari" VALUES(22,46,'22:46','کیا ان لوگوں نے ملک میں سیر نہیں کی تاکہ ان کے دل (ایسے) ہوتے کہ ان سے سمجھ سکتے۔ اور کان (ایسے) ہوتے کہ ان سے سن سکتے۔ بات یہ ہے کہ آنکھیں اندھی نہیں ہوتیں بلکہ دل جو سینوں میں ہیں (وہ) اندھے ہوتے ہیں');
INSERT INTO "jalandhari" VALUES(22,47,'22:47','اور (یہ لوگ) تم سے عذاب کے لئے جلدی کر رہے ہیں اور خدا اپنا وعدہ ہرگز خلاف نہیں کرے گا۔ اور بےشک تمہارے پروردگار کے نزدیک ایک روز تمہارے حساب کے رو سے ہزار برس کے برابر ہے');
INSERT INTO "jalandhari" VALUES(22,48,'22:48','اور بہت سی بستیاں ہیں کہ میں ان کو مہلت دیتا رہا اور وہ نافرمان تھیں۔ پھر میں نے ان کو پکڑ لیا۔ اور میری طرف ہی لوٹ کر آنا ہے');
INSERT INTO "jalandhari" VALUES(22,49,'22:49','(اے پیغمبر) کہہ دو کہ لوگو! میں تم کو کھلم کھلا نصیحت کرنے والا ہوں');
INSERT INTO "jalandhari" VALUES(22,50,'22:50','تو جو لوگ ایمان لائے اور نیک کام کئے ان کے لئے بخشش اور آبرو کی روزی ہے');
INSERT INTO "jalandhari" VALUES(22,51,'22:51','اور جن لوگوں نے ہماری آیتوں میں (اپنے زعم باطل میں) ہمیں عاجز کرنے کے لئے سعی کی، وہ اہل دوزخ ہیں');
INSERT INTO "jalandhari" VALUES(22,52,'22:52','اور ہم نے تم سے پہلے کوئی رسول اور نبی نہیں بھیجا مگر (اس کا یہ حال تھا کہ) جب وہ کوئی آرزو کرتا تھا تو شیطان اس کی آرزو میں (وسوسہ) ڈال دیتا تھا۔ تو جو (وسوسہ) شیطان ڈالتا ہے خدا اس کو دور کردیتا ہے۔ پھر خدا اپنی آیتوں کو مضبوط کردیتا ہے۔ اور خدا علم والا اور حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(22,53,'22:53','غرض (اس سے) یہ ہے کہ جو (وسوسہ) شیطان ڈالتا ہے اس کو ان لوگوں کے لئے جن کے دلوں میں بیماری ہے اور جن کے دل سخت ہیں ذریعہ آزمائش ٹھہرائے۔ بےشک ظالم پرلے درجے کی مخالفت میں ہیں');
INSERT INTO "jalandhari" VALUES(22,54,'22:54','اور یہ بھی غرض ہے کہ جن لوگوں کو علم عطا ہوا ہے وہ جان لیں کہ وہ (یعنی وحی) تمہارے پروردگار کی طرف سے حق ہے تو وہ اس پر ایمان لائیں اور ان کے دل خدا کے آگے عاجزی کریں۔ اور جو لوگ ایمان لائے ہیں خدا ان کو سیدھے رستے کی طرف ہدایت کرتا ہے');
INSERT INTO "jalandhari" VALUES(22,55,'22:55','اور کافر لوگ ہمیشہ اس سے شک میں رہیں گے یہاں تک کہ قیامت ان پر ناگہاں آجائے یا ایک نامبارک دن کا عذاب ان پر واقع ہو');
INSERT INTO "jalandhari" VALUES(22,56,'22:56','اس روز بادشاہی خدا ہی کی ہوگی۔ اور ان میں فیصلہ کردے گا تو جو لوگ ایمان لائے اور عمل نیک کرتے رہے وہ نعمت کے باغوں میں ہوں گے');
INSERT INTO "jalandhari" VALUES(22,57,'22:57','اور جو کافر ہوئے اور ہماری آیتوں کو جھٹلاتے رہے ان کے لئے ذلیل کرنے والا عذاب ہوگا');
INSERT INTO "jalandhari" VALUES(22,58,'22:58','اور جن لوگوں نے خدا کی راہ میں ہجرت کی پھر مارے گئے یا مر گئے۔ ان کو خدا اچھی روزی دے گا۔ اور بےشک خدا سب سے بہتر رزق دینے والا ہے');
INSERT INTO "jalandhari" VALUES(22,59,'22:59','وہ ان کو ایسے مقام میں داخل کرے گا جسے وہ پسند کریں گے۔ اور خدا تو جاننے والا (اور) بردبار ہے');
INSERT INTO "jalandhari" VALUES(22,60,'22:60','یہ (بات خدا کے ہاں ٹھہر چکی ہے) اور جو شخص (کسی کو) اتنی ہی ایذا دے جتنی ایذا اس کو دی گئی پھر اس شخص پر زیادتی کی جائے تو خدا اس کی مدد کرے گا۔ بےشک خدا معاف کرنے والا اور بخشنے والا ہے');
INSERT INTO "jalandhari" VALUES(22,61,'22:61','یہ اس لئے کہ خدا رات کو دن میں داخل کردیتا ہے اور دن کو رات میں داخل کرتا ہے۔ اور خدا تو سننے والا دیکھنے والا ہے');
INSERT INTO "jalandhari" VALUES(22,62,'22:62','یہ اس لئے کہ خدا ہی برحق ہے اور جس چیز کو (کافر) خدا کے سوا پکارتے ہیں وہ باطل ہے اور اس لئے خدا رفیع الشان اور بڑا ہے');
INSERT INTO "jalandhari" VALUES(22,63,'22:63','کیا تم نہیں دیکھتے کہ خدا آسمان سے مینہ برساتا ہے تو زمین سرسبز ہوجاتی ہے۔ بےشک خدا باریک بین اور خبردار ہے');
INSERT INTO "jalandhari" VALUES(22,64,'22:64','جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے اسی کا ہے۔ اور بےشک خدا بےنیاز اور قابل ستائش ہے۔');
INSERT INTO "jalandhari" VALUES(22,65,'22:65','کیا تم نہیں دیکھتے کہ جتنی چیزیں زمین میں ہیں (سب) خدا نے تمہارے زیرفرمان کر رکھی ہیں اور کشتیاں (بھی) جو اسی کے حکم سے دریا میں چلتی ہیں۔ اور وہ آسمان کو تھامے رہتا ہے کہ زمین پر (نہ) گڑ پڑے مگر اس کے حکم سے۔ بےشک خدا لوگوں پر نہایت شفقت کرنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(22,66,'22:66','اور وہی تو ہے جس نے تم کو حیات بخشی۔ پھر تم کو مارتا ہے۔ پھر تمہیں زندہ بھی کرے گا۔ اور انسان تو بڑا ناشکر ہے');
INSERT INTO "jalandhari" VALUES(22,67,'22:67','ہم نے ہر ایک اُمت کے لئے ایک شریعت مقرر کردی ہے جس پر وہ چلتے ہیں تو یہ لوگ تم سے اس امر میں جھگڑا نہ کریں اور تم (لوگوں کو) اپنے پروردگار کی طرف بلاتے رہو۔ بےشک تم سیدھے رستے پر ہو');
INSERT INTO "jalandhari" VALUES(22,68,'22:68','اور اگر یہ تم سے جھگڑا کریں تو کہہ دو کہ جو عمل تم کرتے ہو خدا ان سے خوب واقف ہے');
INSERT INTO "jalandhari" VALUES(22,69,'22:69','جن باتوں میں تم اختلاف کرتے ہو خدا تم میں قیامت کے روز ان کا فیصلہ کردے گا');
INSERT INTO "jalandhari" VALUES(22,70,'22:70','کیا تم نہیں جانتے کہ جو کچھ آسمان اور زمین میں ہے خدا اس کو جانتا ہے۔ یہ (سب کچھ) کتاب میں (لکھا ہوا) ہے۔ بےشک یہ سب خدا کو آسان ہے');
INSERT INTO "jalandhari" VALUES(22,71,'22:71','اور (یہ لوگ) خدا کے سوا ایسی چیزوں کی عبادت کرتے ہیں جن کی اس نے کوئی سند نازل نہیں فرمائی اور نہ ان کے پاس اس کی کوئی دلیل ہے۔ اور ظالموں کا کوئی بھی مددگار نہیں ہوگا');
INSERT INTO "jalandhari" VALUES(22,72,'22:72','اور جب ان کو ہماری آیتیں پڑھ کر سنائی جاتی تو (ان کی شکل بگڑ جاتی ہے اور) تم ان کے چہروں میں صاف طور پر ناخوشی (کے آثار) دیکھتے ہو۔ قریب ہوتے ہیں کہ جو لوگ ان کو ہماری آیتیں پڑھ کر سناتے ہیں ان پر حملہ کردیں۔ کہہ دو کہ میں تم کو اس سے بھی بری چیز بتاؤں؟ وہ دوزخ کی آگ ہے۔ جس کا خدا نے کافروں سے وعدہ کیا ہے۔ اور وہ برا ٹھکانا ہے');
INSERT INTO "jalandhari" VALUES(22,73,'22:73','لوگو! ایک مثال بیان کی جاتی ہے اسے غور سے سنو۔ کہ جن لوگوں کو تم خدا کے سوا پکارتے ہو وہ ایک مکھی بھی نہیں بنا سکتے اگرچہ اس کے لئے سب مجتمع ہوجائیں۔ اور اگر ان سے مکھی کوئی چیز لے جائے تو اسے اس سے چھڑا نہیں سکتے۔ طالب اور مطلوب (یعنی عابد اور معبود دونوں) گئے گزرے ہیں');
INSERT INTO "jalandhari" VALUES(22,74,'22:74','ان لوگوں نے خدا کی قدر جیسی کرنی چاہیئے تھی نہیں کی۔ کچھ شک نہیں کہ خدا زبردست اور غالب ہے');
INSERT INTO "jalandhari" VALUES(22,75,'22:75','خدا فرشتوں میں سے پیغام پہنچانے والے منتخب کرلیتا ہے اور انسانوں میں سے بھی۔ بےشک خدا سننے والا (اور) دیکھنے والا ہے');
INSERT INTO "jalandhari" VALUES(22,76,'22:76','جو ان کے آگے ہے اور جن ان کے پیچھے ہے وہ اس سے واقف ہے۔ اور سب کاموں کا رجوع خدا ہی کی طرف ہے');
INSERT INTO "jalandhari" VALUES(22,77,'22:77','مومنو! رکوع کرتے اور سجدے کرتے اور اپنے پروردگار کی عبادت کرتے رہو اور نیک کام کرو تاکہ فلاح پاؤ');
INSERT INTO "jalandhari" VALUES(22,78,'22:78','اور خدا (کی راہ) میں جہاد کرو جیسا جہاد کرنے کا حق ہے۔ اس نے تم کو برگزیدہ کیا ہے اور تم پر دین کی (کسی بات) میں تنگی نہیں کی۔ (اور تمہارے لئے) تمہارے باپ ابراہیم کا دین (پسند کیا) اُسی نے پہلے (یعنی پہلی کتابوں میں) تمہارا نام مسلمان رکھا تھا اور اس کتاب میں بھی (وہی نام رکھا ہے تو جہاد کرو) تاکہ پیغمبر تمہارے بارے میں شاہد ہوں۔ اور تم لوگوں کے مقابلے میں شاہد اور نماز پڑھو اور زکوٰة دو اور خدا کے دین کی (رسی کو) پکڑے رہو۔ وہی تمہارا دوست ہے۔ اور خوب دوست اور خوب مددگار ہے');
INSERT INTO "jalandhari" VALUES(23,1,'23:1','بےشک ایمان والے رستگار ہوگئے');
INSERT INTO "jalandhari" VALUES(23,2,'23:2','جو نماز میں عجزو نیاز کرتے ہیں');
INSERT INTO "jalandhari" VALUES(23,3,'23:3','اور جو بیہودہ باتوں سے منہ موڑے رہتے ہیں');
INSERT INTO "jalandhari" VALUES(23,4,'23:4','اور جو زکوٰة ادا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(23,5,'23:5','اور جو اپنی شرمگاہوں کی حفاظت کرتے ہیں');
INSERT INTO "jalandhari" VALUES(23,6,'23:6','مگر اپنی بیویوں سے یا (کنیزوں سے) جو ان کی مِلک ہوتی ہیں کہ (ان سے) مباشرت کرنے سے انہیں ملامت نہیں');
INSERT INTO "jalandhari" VALUES(23,7,'23:7','اور جو ان کے سوا اوروں کے طالب ہوں وہ (خدا کی مقرر کی ہوئی حد سے) نکل جانے والے ہیں');
INSERT INTO "jalandhari" VALUES(23,8,'23:8','اور جو امانتوں اور اقراروں کو ملحوظ رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(23,9,'23:9','اور جو نمازوں کی پابندی کرتے ہیں');
INSERT INTO "jalandhari" VALUES(23,10,'23:10','یہ ہی لوگ میراث حاصل کرنے والے ہیں');
INSERT INTO "jalandhari" VALUES(23,11,'23:11','(یعنی) جو بہشت کی میراث حاصل کریں گے۔ اور اس میں ہمیشہ رہیں گے');
INSERT INTO "jalandhari" VALUES(23,12,'23:12','اور ہم نے انسان کو مٹی کے خلاصے سے پیدا کیا ہے');
INSERT INTO "jalandhari" VALUES(23,13,'23:13','پھر اس کو ایک مضبوط (اور محفوظ) جگہ میں نطفہ بنا کر رکھا');
INSERT INTO "jalandhari" VALUES(23,14,'23:14','پھر نطفے کا لوتھڑا بنایا۔ پھر لوتھڑے کی بوٹی بنائی پھر بوٹی کی ہڈیاں بنائیں پھر ہڈیوں پر گوشت (پوست) چڑھایا۔ پھر اس کو نئی صورت میں بنا دیا۔ تو خدا جو سب سے بہتر بنانے والا بڑا بابرکت ہے');
INSERT INTO "jalandhari" VALUES(23,15,'23:15','پھر اس کے بعد تم مرجاتے ہو');
INSERT INTO "jalandhari" VALUES(23,16,'23:16','پھر قیامت کے روز اُٹھا کھڑے کئے جاؤ گے');
INSERT INTO "jalandhari" VALUES(23,17,'23:17','اور ہم نے تمہارے اوپر (کی جانب) سات آسمان پیدا کئے۔ اور ہم خلقت سے غافل نہیں ہیں');
INSERT INTO "jalandhari" VALUES(23,18,'23:18','اور ہم ہی نے آسمان سے ایک اندازے کے ساتھ پانی نازل کیا۔ پھر اس کو زمین میں ٹھہرا دیا اور ہم اس کے نابود کردینے پر بھی قادر ہیں');
INSERT INTO "jalandhari" VALUES(23,19,'23:19','پھر ہم نے اس سے تمہارے لئے کھجوروں اور انگوروں کے باغ بنائے، ان میں تمہارے لئے بہت سے میوے پیدا ہوتے ہیں۔ اور ان میں سے تم کھاتے بھی ہو');
INSERT INTO "jalandhari" VALUES(23,20,'23:20','اور وہ درخت بھی (ہم ہی نے پیدا کیا) جو طور سینا میں پیدا ہوتا ہے (یعنی زیتون کا درخت کہ) کھانے کے لئے روغن اور سالن لئے ہوئے اُگتا ہے');
INSERT INTO "jalandhari" VALUES(23,21,'23:21','اور تمہارے لئے چارپایوں میں بھی عبرت (اور نشانی) ہے کہ ان کے پیٹوں میں ہے اس سے ہم تمہیں (دودھ) پلاتے ہیں اور تمہارے لئے ان میں اور بھی بہت سے فائدے ہیں اور بعض کو تم کھاتے بھی ہو');
INSERT INTO "jalandhari" VALUES(23,22,'23:22','اور ان پر اور کشتیوں پر تم سوار ہوتے ہو');
INSERT INTO "jalandhari" VALUES(23,23,'23:23','اور ہم نے نوح کو ان کی قوم کی طرف بھیجا تو انہوں نے ان سے کہا کہ اے قوم! خدا ہی کی عبادت کرو اس کے سوا تمہارا کوئی معبود نہیں، کیا تم ڈرتے نہیں');
INSERT INTO "jalandhari" VALUES(23,24,'23:24','تو ان کی قوم کے سردار جو کافر تھے کہنے لگے کہ یہ تو تم ہی جیسا آدمی ہے۔ تم پر بڑائی حاصل کرنی چاہتا ہے۔ اور اگر خدا چاہتا تو فرشتے اُتار دیتا۔ ہم نے اپنے اگلے باپ دادا میں تو یہ بات کبھی سنی نہیں تھی');
INSERT INTO "jalandhari" VALUES(23,25,'23:25','اس آدمی کو تو دیوانگی (کا عارضہ) ہے تو اس کے بارے میں کچھ مدت انتظار کرو');
INSERT INTO "jalandhari" VALUES(23,26,'23:26','نوح نے کہا کہ پروردگار انہوں نے مجھے جھٹلایا ہے تو میری مدد کر');
INSERT INTO "jalandhari" VALUES(23,27,'23:27','پس ہم نے ان کی طرف وحی بھیجی کہ ہمارے سامنے اور ہمارے حکم سے ایک کشتی بناؤ۔ پھر جب ہمارا حکم آ پہنچے اور تنور (پانی سے بھر کر) جوش مارنے لگے تو سب (قسم کے حیوانات) میں جوڑا جوڑا (یعنی نر اور مادہ) دو دو کشتی میں بٹھا دو اور اپنے گھر والوں کو بھی، سو ان کے جن کی نسبت ان میں سے (ہلاک ہونے کا) حکم پہلے صادر ہوچکا ہے۔ اور ظالموں کے بارے میں ہم سے کچھ نہ کہنا، وہ ضرور ڈبو دیئے جائیں گے');
INSERT INTO "jalandhari" VALUES(23,28,'23:28','اور جب تم اور تمہارے ساتھی کشتی میں بیٹھ جاؤ تو (خدا کا شکر کرنا اور) کہنا کہ سب تعریف خدا ہی کو (سزاوار) ہے۔ جس نے ہم کو نجات بخشی ظالم لوگوں سے');
INSERT INTO "jalandhari" VALUES(23,29,'23:29','اور (یہ بھی) دعا کرنا کہ اے پروردگار ہم کو مبارک جگہ اُتاریو اور تو سب سے بہتر اُتارنے والا ہے');
INSERT INTO "jalandhari" VALUES(23,30,'23:30','بےشک اس (قصے) میں نشانیاں ہیں اور ہمیں تو آزمائش کرنی تھی');
INSERT INTO "jalandhari" VALUES(23,31,'23:31','پھر ان کے بعد ہم نے ایک اور جماعت پیدا کی');
INSERT INTO "jalandhari" VALUES(23,32,'23:32','اور ان ہی میں سے ان میں ایک پیغمبر بھیجا (جس نے ان سے کہا) کہ خدا ہی کی عبادت کرو (کہ) اس کے سوا تمہارا کوئی معبود نہیں، تو کیا تم ڈرتے نہیں');
INSERT INTO "jalandhari" VALUES(23,33,'23:33','تو ان کی قوم کے سردار جو کافر تھے اور آخرت کے آنے کو جھوٹ سمجھتے تھے اور دنیا کی زندگی میں ہم نے ان کو آسودگی دے رکھی تھی۔ کہنے لگے کہ یہ تو تم ہی جیسا آدمی ہے، جس قسم کا کھانا تم کھاتے ہو، اسی طرح کا یہ بھی کھاتا ہے اور جو پانی تم پیتے ہو اسی قسم کا یہ بھی پیتا ہے');
INSERT INTO "jalandhari" VALUES(23,34,'23:34','اور اگر تم اپنے ہی جیسے آدمی کا کہا مان لیا تو گھاٹے میں پڑ گئے');
INSERT INTO "jalandhari" VALUES(23,35,'23:35','کیا یہ تم سے یہ کہتا ہے کہ جب تم مر جاؤ گے اور مٹی ہو جاؤ گے اور استخوان (کے سوا کچھ نہ رہے گا) تو تم (زمین سے) نکالے جاؤ گے');
INSERT INTO "jalandhari" VALUES(23,36,'23:36','جس بات کا تم سے وعدہ کیا جاتا ہے (بہت) بعید اور (بہت) بعید ہے');
INSERT INTO "jalandhari" VALUES(23,37,'23:37','زندگی تو یہی ہماری دنیا کی زندگی ہے کہ (اسی میں) ہم مرتے اور جیتے ہیں، اور ہم پھر نہیں اُٹھائے جائیں گے');
INSERT INTO "jalandhari" VALUES(23,38,'23:38','یہ تو ایک ایسا آدمی ہے جس نے خدا پر جھوٹ افتراء کیا ہے اور ہم اس کو ماننے والے نہیں');
INSERT INTO "jalandhari" VALUES(23,39,'23:39','پیغمبر نے کہا کہ اے پروردگار انہوں نے مجھے جھوٹا سمجھا ہے تو میری مدد کر');
INSERT INTO "jalandhari" VALUES(23,40,'23:40','فرمایا کہ یہ تھوڑے ہی عرصے میں پشیمان ہو کر رہ جائیں گے');
INSERT INTO "jalandhari" VALUES(23,41,'23:41','تو ان کو (وعدہٴ برحق کے مطابق) زور کی آواز نے آپکڑا، تو ہم نے ان کو کوڑا کرڈالا۔ پس ظالم لوگوں پر لعنت ہے');
INSERT INTO "jalandhari" VALUES(23,42,'23:42','پھر ان کے بعد ہم نے اور جماعتیں پیدا کیں');
INSERT INTO "jalandhari" VALUES(23,43,'23:43','کوئی جماعت اپنے وقت سے نہ آگے جاسکتی ہے نہ پیچھے رہ سکتی ہے');
INSERT INTO "jalandhari" VALUES(23,44,'23:44','پھر ہم نے پے درپے اپنے پیغمبر بھیجتے رہے۔ جب کسی اُمت کے پاس اس کا پیغمبر آتا تھا تو وہ اسے جھٹلاتے تھے تو ہم بھی بعض کو بعض کے پیچھے (ہلاک کرتے اور ان پر عذاب) لاتے رہے اور ان کے افسانے بناتے رہے۔ پس جو لوگ ایمان نہیں لاتے ان پر لعنت');
INSERT INTO "jalandhari" VALUES(23,45,'23:45','پھر ہم نے موسیٰ اور ان کے بھائی ہارون کو اپنی نشانیاں اور دلیل ظاہر دے کر بھیجا');
INSERT INTO "jalandhari" VALUES(23,46,'23:46','(یعنی) فرعون اور اس کی جماعت کی طرف، تو انہوں نے تکبر کیا اور وہ سرکش لوگ تھے');
INSERT INTO "jalandhari" VALUES(23,47,'23:47','کہنے لگے کہ کیا ہم ان اپنے جیسے دو آدمیوں پر ایمان لے آئیں اور اُن کو قوم کے لوگ ہمارے خدمت گار ہیں');
INSERT INTO "jalandhari" VALUES(23,48,'23:48','تو اُن لوگوں نے اُن کی تکذیب کی سو (آخر) ہلاک کر دیئے گئے');
INSERT INTO "jalandhari" VALUES(23,49,'23:49','اور ہم نے موسیٰ کو کتاب دی تھی تاکہ وہ لوگ ہدایت پائیں');
INSERT INTO "jalandhari" VALUES(23,50,'23:50','اور ہم نے مریم کے بیٹے (عیسیٰ) اور ان کی ماں کو (اپنی) نشانی بنایا تھا اور ان کو ایک اونچی جگہ پر جو رہنے کے لائق تھی اور جہاں (نتھرا ہوا) پانی جاری تھا، پناہ دی تھی');
INSERT INTO "jalandhari" VALUES(23,51,'23:51','اے پیغمبرو! پاکیزہ چیزیں کھاؤ اور عمل نیک کرو۔ جو عمل تم کرتے ہو میں ان سے واقف ہوں');
INSERT INTO "jalandhari" VALUES(23,52,'23:52','اور یہ تمہاری جماعت (حقیقت میں) ایک ہی جماعت ہے اور میں تمہارا پروردگار ہوں تو مجھ سے ڈرو');
INSERT INTO "jalandhari" VALUES(23,53,'23:53','تو پھر آپس میں اپنے کام کو متفرق کرکے جدا جدا کردیا۔ جو چیزیں جس فرقے کے پاس ہے وہ اس سے خوش ہو رہا ہے');
INSERT INTO "jalandhari" VALUES(23,54,'23:54','تو ان کو ایک مدت تک ان کی غفلت میں رہنے دو');
INSERT INTO "jalandhari" VALUES(23,55,'23:55','کیا یہ لوگ خیال کرتے ہیں کہ ہم جو دنیا میں ان کو مال اور بیٹوں سے مدد دیتے ہیں');
INSERT INTO "jalandhari" VALUES(23,56,'23:56','تو (اس سے) ان کی بھلائی میں جلدی کر رہے ہیں (نہیں) بلکہ یہ سمجھتے ہی نہیں');
INSERT INTO "jalandhari" VALUES(23,57,'23:57','جو اپنے پروردگار کے خوف سے ڈرتے ہیں');
INSERT INTO "jalandhari" VALUES(23,58,'23:58','اور جو اپنے پروردگار کی آیتوں پر ایمان رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(23,59,'23:59','اور جو اپنے پروردگار کے ساتھ شریک نہیں کرتے');
INSERT INTO "jalandhari" VALUES(23,60,'23:60','اور جو دے سکتے ہیں دیتے ہیں اور ان کے دل اس بات سے ڈرتے رہتے ہیں کہ ان کو اپنے پروردگار کی لوٹ کر جانا ہے');
INSERT INTO "jalandhari" VALUES(23,61,'23:61','یہی لوگ نیکیوں میں جلدی کرے اور یہی اُن کے لئے آگے نکل جاتے ہیں');
INSERT INTO "jalandhari" VALUES(23,62,'23:62','اور ہم کسی شخص کو اس کی طاقت سے زیادہ تکلیف نہیں دیتے اور ہمارے پاس کتاب ہے جو سچ سچ کہہ دیتی ہے اور ان لوگوں پر ظلم نہیں کیا جائے گا');
INSERT INTO "jalandhari" VALUES(23,63,'23:63','مگر ان کے دل ان (باتوں) کی طرف سے غفلت میں (پڑے ہوئے) ہیں، اور ان کے سوا اور اعمال بھی ہیں جو یہ کرتے رہتے ہیں');
INSERT INTO "jalandhari" VALUES(23,64,'23:64','یہاں تک کہ جب ہم نے ان میں سے آسودہ حال لوگوں کو پکڑ لیا تو وہ اس وقت چلاّئیں گے');
INSERT INTO "jalandhari" VALUES(23,65,'23:65','آج مت چلاّؤ! تم کو ہم سے کچھ مدد نہیں ملے گی');
INSERT INTO "jalandhari" VALUES(23,66,'23:66','میری آیتیں تم کو پڑھ پڑھ کر سنائی جاتی تھیں اور تم الٹے پاؤں پھر پھر جاتے تھے');
INSERT INTO "jalandhari" VALUES(23,67,'23:67','ان سے سرکشی کرتے، کہانیوں میں مشغول ہوتے اور بیہودہ بکواس کرتے تھے');
INSERT INTO "jalandhari" VALUES(23,68,'23:68','کیا انہوں نے اس کلام میں غور نہیں کیا یا ان کے پاس کوئی ایسی چیز آئی ہے جو ان کے اگلے باپ دادا کے پاس نہیں تھی');
INSERT INTO "jalandhari" VALUES(23,69,'23:69','یا یہ اپنے پیغمبر کو جانتے پہنچانتے نہیں، اس وجہ سے ان کو نہیں مانتے');
INSERT INTO "jalandhari" VALUES(23,70,'23:70','کیا یہ کہتے ہیں کہ اسے سودا ہے (نہیں) بلکہ وہ ان کے پاس حق کو لے کر آئے ہیں اور ان میں سے اکثر حق کو ناپسند کرتے ہیں');
INSERT INTO "jalandhari" VALUES(23,71,'23:71','اور خدائے (برحق) ان کی خواہشوں پر چلے تو آسمان اور زمین اور جو ان میں ہیں سب درہم برہم ہوجائیں۔ بلکہ ہم نے ان کے پاس ان کی نصیحت (کی کتاب) پہنچا دی ہے اور وہ اپنی (کتاب) نصیحت سے منہ پھیر رہے ہیں');
INSERT INTO "jalandhari" VALUES(23,72,'23:72','کیا تم ان سے (تبلیغ کے صلے میں) کچھ مال مانگتے ہو، تو تمہارا پروردگار کا مال بہت اچھا ہے اور وہ سب سے بہتر رزق دینے والا ہے');
INSERT INTO "jalandhari" VALUES(23,73,'23:73','اور تم تو ان کو سیدھے راستے کی طرف بلاتے ہو');
INSERT INTO "jalandhari" VALUES(23,74,'23:74','اور جو لوگ آخرت پر ایمان نہیں لاتے وہ رستے سے الگ ہو رہے ہیں');
INSERT INTO "jalandhari" VALUES(23,75,'23:75','اور اگر ہم ان پر رحم کریں اور جو تکلیفیں ان کو پہنچ رہی ہیں، وہ دور کردیں تو اپنی سرکشی پر اڑے رہیں (اور) بھٹکتے (پھریں)');
INSERT INTO "jalandhari" VALUES(23,76,'23:76','اور ہم نے ان کو عذاب میں پکڑا تو بھی انہوں نے خدا کے آگے عاجزی نہ کی اور وہ عاجزی کرتے ہی نہیں');
INSERT INTO "jalandhari" VALUES(23,77,'23:77','یہاں تک کہ جب ہم نے پر عذاب شدید کا دروازہ کھول دیا تو اس وقت وہاں ناامید ہوگئے');
INSERT INTO "jalandhari" VALUES(23,78,'23:78','اور وہی تو ہے جس نے تمہارے کان اور آنکھیں اور دل بنائے۔ (لیکن) تم کم شکرگزاری کرتے ہو');
INSERT INTO "jalandhari" VALUES(23,79,'23:79','اور وہی تو ہے جس نے تم کو زمین میں پیدا کیا اور اسی کی طرف تم جمع ہو کر جاؤ گے');
INSERT INTO "jalandhari" VALUES(23,80,'23:80','اور وہی ہے جو زندگی بخشتا ہے اور موت دیتا ہے اور رات اور دن کا بدلتے رہنا اسی کا تصرف ہے، کیا تم سمجھتے نہیں');
INSERT INTO "jalandhari" VALUES(23,81,'23:81','بات یہ ہے کہ جو بات اگلے (کافر) کہتے تھے اسی طرح کی (بات یہ) کہتے ہیں');
INSERT INTO "jalandhari" VALUES(23,82,'23:82','کہتے ہیں کہ جب ہم مر جائیں گے اور مٹی ہو جائیں گے اور استخوان (بوسیدہ کے سوا کچھ) نہ رہے گا تو کیا ہم پھر اٹھائے جائیں گے؟');
INSERT INTO "jalandhari" VALUES(23,83,'23:83','یہ وعدہ ہم سے اور ہم سے پہلے ہمارے باپ دادا سے بھی ہوتا چلا آیا ہے (اجی) یہ تو صرف اگلے لوگوں کی کہانیاں ہیں');
INSERT INTO "jalandhari" VALUES(23,84,'23:84','کہو کہ اگر تم جانتے ہو تو بتاؤ کہ زمین اور جو کچھ زمین میں ہے سب کس کا مال ہے؟');
INSERT INTO "jalandhari" VALUES(23,85,'23:85','جھٹ بول اٹھیں گے کہ خدا کا۔ کہو کہ پھر تم سوچتے کیوں نہیں؟');
INSERT INTO "jalandhari" VALUES(23,86,'23:86','(ان سے) پوچھو کہ سات آسمانوں کا کون مالک ہے اور عرش عظیم کا (کون) مالک (ہے؟)');
INSERT INTO "jalandhari" VALUES(23,87,'23:87','بےساختہ کہہ دیں گے کہ یہ (چیزیں) خدا ہی کی ہیں، کہو کہ پھر تم ڈرتے کیوں نہیں؟');
INSERT INTO "jalandhari" VALUES(23,88,'23:88','کہو کہ اگر تم جانتے ہو تو بتاؤ کہ وہ کون ہے جس کے ہاتھ میں ہر چیز کی بادشاہی ہے اور وہ پناہ دیتا ہے اور اس کے مقابل کوئی کسی کو پناہ نہیں دے سکتا');
INSERT INTO "jalandhari" VALUES(23,89,'23:89','فوراً کہہ دیں گے کہ (ایسی بادشاہی تو) خدا ہی کی ہے، تو کہو پھر تم پر جادو کہاں سے پڑ جاتا ہے؟');
INSERT INTO "jalandhari" VALUES(23,90,'23:90','بات یہ ہے کہ ہم نے ان کے پاس حق پہنچا دیا ہے اور جو (بت پرستی کئے جاتے ہیں) بےشک جھوٹے ہیں');
INSERT INTO "jalandhari" VALUES(23,91,'23:91','خدا نے نہ تو (اپنا) کسی کو بیٹا بنایا ہے اور نہ اس کے ساتھ کوئی معبود ہے، ایسا ہوتا تو ہر معبود اپنی اپنی مخلوقات کو لے کر چل دیتا اور ایک دوسرے پر غالب آجاتا۔ یہ لوگ جو کچھ خدا کے بارے میں بیان کرتے ہیں خدا اس سے پاک ہے');
INSERT INTO "jalandhari" VALUES(23,92,'23:92','وہ پوشیدہ اور ظاہر کو جانتا ہے اور (مشرک) جو اس کے ساتھ شریک کرتے ہیں اس کی شان اس سے اونچی ہے');
INSERT INTO "jalandhari" VALUES(23,93,'23:93','(اے محمدصلى الله عليه وسلم) کہو کہ اے پروردگار جس عذاب کا ان (کفار) سے وعدہ ہو رہا ہے، اگر تو میری زندگی میں ان پر نازل کرکے مجھے بھی دکھادے');
INSERT INTO "jalandhari" VALUES(23,94,'23:94','تو اے پروردگار مجھے (اس سے محفوظ رکھیئے اور) ان ظالموں میں شامل نہ کیجیئے');
INSERT INTO "jalandhari" VALUES(23,95,'23:95','اور جو وعدہ ہم ان سے کر رہے ہیں ہم تم کو دکھا کر ان پر نازل کرنے پر قادر ہیں');
INSERT INTO "jalandhari" VALUES(23,96,'23:96','اور بری بات کے جواب میں ایسی بات کہو جو نہایت اچھی ہو۔ اور یہ جو کچھ بیان کرتے ہیں ہمیں خوب معلوم ہے');
INSERT INTO "jalandhari" VALUES(23,97,'23:97','اور کہو کہ اے پروردگار! میں شیطانوں کے وسوسوں سے تیری پناہ مانگتا ہو');
INSERT INTO "jalandhari" VALUES(23,98,'23:98','اور اے پروردگار! اس سے بھی تیری پناہ مانگتا ہوں کہ وہ میرے پاس آموجود ہوں');
INSERT INTO "jalandhari" VALUES(23,99,'23:99','(یہ لوگ اسی طرح غفلت میں رہیں گے) یہاں تک کہ جب ان میں سے کسی کے پاس موت آجائے گی تو کہے گا کہ اے پروردگار! مجھے پھر (دنیا میں) واپس بھیج دے');
INSERT INTO "jalandhari" VALUES(23,100,'23:100','تاکہ میں اس میں جسے چھوڑ آیا ہوں نیک کام کیا کروں۔ ہرگز نہیں۔ یہ ایک ایسی بات ہے کہ وہ اسے زبان سے کہہ رہا ہوگا (اور اس کے ساتھ عمل نہیں ہوگا) اور اس کے پیچھے برزخ ہے (جہاں وہ) اس دن تک کہ (دوبارہ) اٹھائے جائیں گے، (رہیں گے)');
INSERT INTO "jalandhari" VALUES(23,101,'23:101','پھر جب صور پھونکا جائے گا تو نہ تو ان میں قرابتیں ہوں گی اور نہ ایک دوسرے کو پوچھیں گے');
INSERT INTO "jalandhari" VALUES(23,102,'23:102','تو جن کے (عملوں کے) بوجھ بھاری ہوں گے۔ وہ فلاح پانے والے ہیں');
INSERT INTO "jalandhari" VALUES(23,103,'23:103','اور جن کے بوجھ ہلکے ہوں گے وہ لوگ ہیں جنہوں نے اپنے تئیں خسارے میں ڈالا، ہمیشہ دوزخ میں رہیں گے');
INSERT INTO "jalandhari" VALUES(23,104,'23:104','آگ ان کے مونہوں کو جھلس دے گی اور وہ اس میں تیوری چڑھائے ہوں گے');
INSERT INTO "jalandhari" VALUES(23,105,'23:105','کیا تم کو میری آیتیں پڑھ کر نہیں سنائی جاتیں تھیں (نہیں) تم ان کو سنتے تھے (اور) جھٹلاتے تھے');
INSERT INTO "jalandhari" VALUES(23,106,'23:106','اے ہمارے پروردگار! ہم پر ہماری کم بختی غالب ہوگئی اور ہم رستے سے بھٹک گئے');
INSERT INTO "jalandhari" VALUES(23,107,'23:107','اے پروردگار! ہم کو اس میں سے نکال دے، اگر ہم پھر (ایسے کام) کریں تو ظالم ہوں گے');
INSERT INTO "jalandhari" VALUES(23,108,'23:108','(خدا) فرمائے گا کہ اسی میں ذلت کے ساتھ پڑے رہو اور مجھ سے بات نہ کرو');
INSERT INTO "jalandhari" VALUES(23,109,'23:109','میرے بندوں میں ایک گروہ تھا جو دعا کیا کرتا تھا کہ اے ہمارے پروردگار ہم ایمان لائے تو تُو ہم کو بخش دے اور ہم پر رحم کر اور تو سب سے بہتر رحم کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(23,110,'23:110','تو تم ان سے تمسخر کرتے رہے یہاں تک کہ ان کے پیچھے میری یاد بھی بھول گئے اور تم (ہمیشہ) ان سے ہنسی کیا کرتے تھے');
INSERT INTO "jalandhari" VALUES(23,111,'23:111','آج میں نے اُن کو اُن کے صبر کا بدلہ دیا، کہ وہ کامیاب ہوگئے');
INSERT INTO "jalandhari" VALUES(23,112,'23:112','(خدا) پوچھے گا کہ تم زمین میں کتنے برس رہے؟');
INSERT INTO "jalandhari" VALUES(23,113,'23:113','وہ کہیں گے کہ ہم ایک روز یا ایک روز سے بھی کم رہے تھے، شمار کرنے والوں سے پوچھ لیجیئے');
INSERT INTO "jalandhari" VALUES(23,114,'23:114','(خدا) فرمائے گا کہ (وہاں) تم (بہت ہی) کم رہے۔ کاش تم جانتے ہوتے');
INSERT INTO "jalandhari" VALUES(23,115,'23:115','کیا تم یہ خیال کرتے ہو کہ ہم نے تم کو بےفائدہ پیدا کیا ہے اور یہ تم ہماری طرف لوٹ کر نہیں آؤ گے؟');
INSERT INTO "jalandhari" VALUES(23,116,'23:116','تو خدا جو سچا بادشاہ ہے (اس کی شان) اس سے اونچی ہے، اس کے سوا کوئی معبود نہیں، وہی عرش بزرگ کا مالک ہے');
INSERT INTO "jalandhari" VALUES(23,117,'23:117','اور جو شخص خدا کے ساتھ اور معبود کو پکارتا ہے جس کی اس کے پاس کچھ بھی سند نہیں تو اس کا حساب خدا ہی کے ہاں ہوگا۔ کچھ شک نہیں کہ کافر رستگاری نہیں پائیں گے');
INSERT INTO "jalandhari" VALUES(23,118,'23:118','اور خدا سے دعا کرو کہ میرے پروردگار مجھے بخش دے اور (مجھ پر) رحم کر اور تو سب سے بہتر رحم کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(24,1,'24:1','یہ (ایک) سورت ہے جس کو ہم نے نازل کیا اور اس (کے احکام) کو فرض کر دیا، اور اس میں واضح المطالب آیتیں نازل کیں تاکہ تم یاد رکھو');
INSERT INTO "jalandhari" VALUES(24,2,'24:2','بدکاری کرنے والی عورت اور بدکاری کرنے والا مرد (جب ان کی بدکاری ثابت ہوجائے تو) دونوں میں سے ہر ایک کو سو درے مارو۔ اور اگر تم خدا اور روز آخرت پر ایمان رکھتے ہو تو شرع خدا (کے حکم) میں تمہیں ان پر ہرگز ترس نہ آئے۔ اور چاہیئے کہ ان کی سزا کے وقت مسلمانوں کی ایک جماعت بھی موجود ہو');
INSERT INTO "jalandhari" VALUES(24,3,'24:3','بدکار مرد تو بدکار یا مشرک عورت کے سوا نکاح نہیں کرتا اور بدکار عورت کو بھی بدکار یا مشرک مرد کے سوا اور کوئی نکاح میں نہیں لاتا اور یہ (یعنی بدکار عورت سے نکاح کرنا) مومنوں پر حرام ہے');
INSERT INTO "jalandhari" VALUES(24,4,'24:4','اور جو لوگ پرہیزگار عورتوں کو بدکاری کا عیب لگائیں اور اس پر چار گواہ نہ لائیں تو ان کو اسی درے مارو اور کبھی ان کی شہادت قبول نہ کرو۔ اور یہی بدکردار ہیں');
INSERT INTO "jalandhari" VALUES(24,5,'24:5','ہاں جو اس کے بعد توبہ کرلیں اور (اپنی حالت) سنوار لیں تو خدا (بھی) بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(24,6,'24:6','اور جو لوگ اپنی عورتوں پر بدکاری کی تہمت لگائیں اور خود ان کے سوا ان کے گواہ نہ ہوں تو ہر ایک کی شہادت یہ ہے کہ پہلے تو چار بار خدا کی قسم کھائے کہ بےشک وہ سچا ہے');
INSERT INTO "jalandhari" VALUES(24,7,'24:7','اور پانچویں بار یہ (کہے) کہ اگر وہ جھوٹا ہو تو اس پر خدا کی لعنت');
INSERT INTO "jalandhari" VALUES(24,8,'24:8','اور عورت سے سزا کو یہ بات ٹال سکتی ہے کہ وہ پہلے چار بار خدا کی قسم کھائے کہ بےشک یہ جھوٹا ہے');
INSERT INTO "jalandhari" VALUES(24,9,'24:9','اور پانچویں دفعہ یوں (کہے) کہ اگر یہ سچا ہو تو مجھ پر خدا کا غضب (نازل ہو)');
INSERT INTO "jalandhari" VALUES(24,10,'24:10','اور اگر تم پر خدا کا فضل اور اس کی مہربانی نہ ہوتی تو بہت سی خرابیاں پیدا ہوجاتیں۔ مگر وہ صاحب کرم ہے اور یہ کہ خدا توبہ قبول کرنے والا حکیم ہے');
INSERT INTO "jalandhari" VALUES(24,11,'24:11','جن لوگوں نے بہتان باندھا ہے تم ہی میں سے ایک جماعت ہے اس کو اپنے حق میں برا نہ سمجھنا۔ بلکہ وہ تمہارے لئے اچھا ہے۔ ان میں سے جس شخص نے گناہ کا جتنا حصہ لیا اس کے لئے اتنا ہی وبال ہے۔ اور جس نے ان میں سے اس بہتان کا بڑا بوجھ اٹھایا ہے اس کو بڑا عذاب ہوگا');
INSERT INTO "jalandhari" VALUES(24,12,'24:12','جب تم نے وہ بات سنی تھی تو مومن مردوں اور عورتوں نے کیوں اپنے دلوں میں نیک گمان نہ کیا۔ اور کیوں نہ کہا کہ یہ صریح طوفان ہے');
INSERT INTO "jalandhari" VALUES(24,13,'24:13','یہ (افتراء پرداز) اپنی بات (کی تصدیق) کے (لئے) چار گواہ کیوں نہ لائے۔ تو جب یہ گواہ نہیں لاسکے تو خدا کے نزدیک یہی جھوٹے ہیں');
INSERT INTO "jalandhari" VALUES(24,14,'24:14','اور اگر دنیا اور آخرت میں تم پر خدا کا فضل اور اس کی رحمت نہ ہوتی تو جس بات کا تم چرچا کرتے تھے اس کی وجہ سے تم پر بڑا (سخت) عذاب نازل ہوتا');
INSERT INTO "jalandhari" VALUES(24,15,'24:15','جب تم اپنی زبانوں سے اس کا ایک دوسرے سے ذکر کرتے تھے اور اپنے منہ سے ایسی بات کہتے تھے جس کا تم کو کچھ علم نہ تھا اور تم اسے ایک ہلکی بات سمجھتے تھے اور خدا کے نزدیک وہ بڑی بھاری بات تھی');
INSERT INTO "jalandhari" VALUES(24,16,'24:16','اور جب تم نے اسے سنا تھا تو کیوں نہ کہہ دیا کہ ہمیں شایاں نہیں کہ ایسی بات زبان پر نہ لائیں۔ (پروردگار) تو پاک ہے یہ تو (بہت) بڑا بہتان ہے');
INSERT INTO "jalandhari" VALUES(24,17,'24:17','خدا تمہیں نصیحت کرتا ہے کہ اگر مومن ہو تو پھر کبھی ایسا کام نہ کرنا');
INSERT INTO "jalandhari" VALUES(24,18,'24:18','اور خدا تمہارے (سمجھانے کے لئے) اپنی آیتیں کھول کھول کر بیان فرماتا ہے۔ اور خدا جاننے والا حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(24,19,'24:19','اور جو لوگ اس بات کو پسند کرتے ہیں کہ مومنوں میں بےحیائی یعنی (تہمت بدکاری کی خبر) پھیلے ان کو دنیا اور آخرت میں دکھ دینے والا عذاب ہوگا۔ اور خدا جانتا ہے اور تم نہیں جانتے');
INSERT INTO "jalandhari" VALUES(24,20,'24:20','اور اگر تم پر خدا کا فضل اور اس کی رحمت نہ ہوتی (تو کیا کچھ نہ ہوتا مگر وہ کریم ہے) اور یہ کہ خدا نہایت مہربان اور رحیم ہے');
INSERT INTO "jalandhari" VALUES(24,21,'24:21','اے مومنو! شیطان کے قدموں پر نہ چلنا۔ اور جو شخص شیطان کے قدموں پر چلے گا تو شیطان تو بےحیائی (کی باتیں) اور برے کام ہی بتائے گا۔ اور اگر تم پر خدا کا فضل اور اس کی مہربانی نہ ہوتی تو ایک شخص بھی تم میں پاک نہ ہوسکتا۔ مگر خدا جس کو چاہتا ہے پاک کردیتا ہے۔ اور خدا سننے والا (اور) جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(24,22,'24:22','اور جو لوگ تم میں صاحب فضل (اور صاحب) وسعت ہیں، وہ اس بات کی قسم نہ کھائیں کہ رشتہ داروں اور محتاجوں اور وطن چھوڑ جانے والوں کو کچھ خرچ پات نہیں دیں گے۔ ان کو چاہیئے کہ معاف کردیں اور درگزر کریں۔ کیا تم پسند نہیں کرتے کہ خدا تم کو بخش دے؟ اور خدا تو بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(24,23,'24:23','جو لوگ پرہیزگار اور برے کاموں سے بےخبر اور ایمان دار عورتوں پر بدکاری کی تہمت لگاتے ہیں ان پر دنیا وآخرت (دونوں) میں لعنت ہے۔ اور ان کو سخت عذاب ہوگا');
INSERT INTO "jalandhari" VALUES(24,24,'24:24','(یعنی قیامت کے روز) جس دن ان کی زبانیں ہاتھ اور پاؤں سب ان کے کاموں کی گواہی دیں گے');
INSERT INTO "jalandhari" VALUES(24,25,'24:25','اس دن خدا ان کو (ان کے اعمال کا) پورا پورا (اور) ٹھیک بدلہ دے گا اور ان کو معلوم ہوجائے گا کہ خدا برحق (اور حق کو) ظاہر کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(24,26,'24:26','ناپاک عورتیں ناپاک مردوں کے لئے اور ناپاک مرد ناپاک عورتوں کے لئے۔ اور پاک عورتیں پاک مردوں کے لئے۔ اور پاک مرد پاک عورتوں کے لئے۔ یہ (پاک لوگ) ان (بدگویوں) کی باتوں سے بری ہیں (اور) ان کے لئے بخشش اور نیک روزی ہے');
INSERT INTO "jalandhari" VALUES(24,27,'24:27','مومنو! اپنے گھروں کے سوا دوسرے (لوگوں کے) گھروں میں گھر والوں سے اجازت لئے اور ان کو سلام کئے بغیر داخل نہ ہوا کرو۔ یہ تمہارے حق میں بہتر ہے (اور ہم) یہ نصیحت اس لئے کرتے ہیں کہ شاید تم یاد رکھو');
INSERT INTO "jalandhari" VALUES(24,28,'24:28','اگر تم گھر میں کسی کو موجود نہ پاؤ تو جب تک تم کو اجازت نہ دی جائے اس میں مت داخل ہو۔ اور اگر یہ کہا جائے کہ (اس وقت) لوٹ جاؤ تو لوٹ جایا کرو۔ یہ تمہارے لئے بڑی پاکیزگی کی بات ہے۔ اور جو کام تم کرتے ہو خدا سب جانتا ہے');
INSERT INTO "jalandhari" VALUES(24,29,'24:29','ہاں اگر تم کسی ایسے مکان میں جاؤ جس میں کوئی نہ بستا ہو اور اس میں تمہارا اسباب (رکھا) ہو، تم پر کچھ گناہ نہیں، اور جو کچھ تم ظاہر کرتے ہو اور جو پوشیدہ کرتے ہو خدا کو سب معلوم ہے');
INSERT INTO "jalandhari" VALUES(24,30,'24:30','مومن مردوں سے کہہ دو کہ اپنی نظریں نیچی رکھا کریں اور اپنی شرم گاہوں کی حفاظت کیا کریں۔ یہ ان کے لئے بڑی پاکیزگی کی بات ہے اور جو کام یہ کرتے ہیں خدا ان سے خبردار ہے');
INSERT INTO "jalandhari" VALUES(24,31,'24:31','اور مومن عورتوں سے بھی کہہ دو کہ وہ بھی اپنی نگاہیں نیچی رکھا کریں اور اپنی شرم گاہوں کی حفاظت کیا کریں اور اپنی آرائش (یعنی زیور کے مقامات) کو ظاہر نہ ہونے دیا کریں مگر جو ان میں سے کھلا رہتا ہو۔ اور اپنے سینوں پر اوڑھنیاں اوڑھے رہا کریں اور اپنے خاوند اور باپ اور خسر اور بیٹیوں اور خاوند کے بیٹوں اور بھائیوں اور بھتیجیوں اور بھانجوں اور اپنی (ہی قسم کی) عورتوں اور لونڈی غلاموں کے سوا نیز ان خدام کے جو عورتوں کی خواہش نہ رکھیں یا ایسے لڑکوں کے جو عورتوں کے پردے کی چیزوں سے واقف نہ ہوں (غرض ان لوگوں کے سوا) کسی پر اپنی زینت (اور سنگار کے مقامات) کو ظاہر نہ ہونے دیں۔ اور اپنے پاؤں (ایسے طور سے زمین پر) نہ ماریں (کہ جھنکار کانوں میں پہنچے اور) ان کا پوشیدہ زیور معلوم ہوجائے۔ اور مومنو! سب خدا کے آگے توبہ کرو تاکہ فلاح پاؤ');
INSERT INTO "jalandhari" VALUES(24,32,'24:32','اور اپنی قوم کی بیوہ عورتوں کے نکاح کردیا کرو۔ اور اپنے غلاموں اور لونڈیوں کے بھی جو نیک ہوں (نکاح کردیا کرو) اگر وہ مفلس ہوں گے تو خدا ان کو اپنے فضل سے خوش حال کردے گا۔ اور خدا (بہت) وسعت والا اور (سب کچھ) جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(24,33,'24:33','اور جن کو بیاہ کا مقدور نہ ہو وہ پاک دامنی کو اختیار کئے رہیں یہاں تک کہ خدا ان کو اپنے فضل سے غنی کردے۔ اور جو غلام تم سے مکاتبت چاہیں اگر تم ان میں (صلاحیت اور) نیکی پاؤ تو ان سے مکاتبت کرلو۔ اور خدا نے جو مال تم کو بخشا ہے اس میں سے ان کو بھی دو۔ اور اپنی لونڈیوں کو اگر وہ پاک دامن رہنا چاہیں تو (بےشرمی سے) دنیاوی زندگی کے فوائد حاصل کرنے کے لئے بدکاری پر مجبور نہ کرنا۔ اور جو ان کو مجبور کرے گا تو ان (بیچاریوں) کے مجبور کئے جانے کے بعد خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(24,34,'24:34','اور ہم نے تمہاری طرف روشن آیتیں نازل کی ہیں اور جو لوگ تم سے پہلے گزر چکے ہیں ان کی خبریں اور پرہیزگاروں کے لئے نصیحت');
INSERT INTO "jalandhari" VALUES(24,35,'24:35','خدا آسمانوں اور زمین کا نور ہے۔ اس کے نور کی مثال ایسی ہے کہ گویا ایک طاق ہے جس میں چراغ ہے۔ اور چراغ ایک قندیل میں ہے۔ اور قندیل (ایسی صاف شفاف ہے کہ) گویا موتی کا سا چمکتا ہوا تارہ ہے اس میں ایک مبارک درخت کا تیل جلایا جاتا ہے (یعنی) زیتون کہ نہ مشرق کی طرف ہے نہ مغرب کی طرف۔ (ایسا معلوم ہوتا ہے کہ) اس کا تیل خواہ آگ اسے نہ بھی چھوئے جلنے کو تیار ہے (پڑی) روشنی پر روشنی (ہو رہی ہے) خدا اپنے نور سے جس کو چاہتا ہے سیدھی راہ دکھاتا ہے۔ اور خدا نے (جو مثالیں) بیان فرماتا ہے (تو) لوگوں کے (سمجھانے کے) لئے اور خدا ہر چیز سے واقف ہے');
INSERT INTO "jalandhari" VALUES(24,36,'24:36','(وہ قندیل) ان گھروں میں (ہے) جن کے بارے میں خدا نے ارشاد فرمایا ہے کہ بلند کئے جائیں اور وہاں خدا کے نام کا ذکر کیا جائے (اور) ان میں صبح وشام اس کی تسبیح کرتے رہیں');
INSERT INTO "jalandhari" VALUES(24,37,'24:37','(یعنی ایسے) لوگ جن کو خدا کے ذکر اور نماز پڑھنے اور زکوٰة دینے سے نہ سوداگری غافل کرتی ہے نہ خرید وفروخت۔ وہ اس دن سے جب دل (خوف اور گھبراہٹ کے سبب) الٹ جائیں گے اور آنکھیں (اوپر کو چڑھ جائیں گی) ڈرتے ہیں');
INSERT INTO "jalandhari" VALUES(24,38,'24:38','تاکہ خدا ان کو ان کے عملوں کا بہت اچھا بدلہ دے اور اپنے فضل سے زیادہ بھی عطا کرے۔ اور جس کو چاہتا ہے خدا بےشمار رزق دیتا ہے');
INSERT INTO "jalandhari" VALUES(24,39,'24:39','جن لوگوں نے کفر کیا ان کے اعمال کی مثال ایسی ہے جیسے میدان میں ریت کہ پیاسا اسے پانی سمجھے یہاں تک کہ جب اس کے پاس آئے تو اسے کچھ بھی نہ پائے اور خدا ہی کو اپنے پاس دیکھے تو وہ اسے اس کا حساب پورا پورا چکا دے۔ اور خدا جلد حساب کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(24,40,'24:40','یا (ان کے اعمال کی مثال ایسی ہے) جیسے دریائے عمیق میں اندھیرے جس پر لہر چڑھی چلی آتی ہو اور اس کے اوپر اور لہر (آرہی ہو) اور اس کے اوپر بادل ہو، غرض اندھیرے ہی اندھیرے ہوں، ایک پر ایک (چھایا ہوا) جب اپنا ہاتھ نکالے تو کچھ نہ دیکھ سکے۔ اور جس کو خدا روشنی نہ دے اس کو (کہیں بھی) روشنی نہیں (مل سکتی)');
INSERT INTO "jalandhari" VALUES(24,41,'24:41','کیا تم نے نہیں دیکھا کہ جو لوگ آسمانوں اور زمین میں ہیں خدا کی تسبیح کرتے ہیں اور پر پھیلائے ہوئے جانور بھی۔ اور سب اپنی نماز اور تسبیح کے طریقے سے واقف ہیں۔ اور جو کچھ وہ کرتے ہیں (سب) خدا کو معلوم ہے');
INSERT INTO "jalandhari" VALUES(24,42,'24:42','اور آسمان اور زمین کی بادشاہی خدا کے لئے ہے۔ اور خدا ہی کی طرف لوٹ کر جانا ہے');
INSERT INTO "jalandhari" VALUES(24,43,'24:43','کیا تم نے نہیں دیکھا کہ خدا ہی بادلوں کو چلاتا ہے، اور ان کو آپس میں ملا دیتا ہے، پھر ان کو تہ بہ تہ کردیتا ہے، پھر تم دیکھتے ہو کہ بادل میں سے مینہ نکل (کر برس) رہا ہے اور آسمان میں جو (اولوں کے) پہاڑ ہیں، ان سے اولے نازل کرتا ہے تو جس پر چاہتا ہے اس کو برسا دیتا ہے اور جس سے چاہتا ہے ہٹا دیتا ہے۔ اور بادل میں جو بجلی ہوتی ہے اس کی چمک آنکھوں کو خیرہ کرکے بینائی کو اُچکے لئے جاتی ہے');
INSERT INTO "jalandhari" VALUES(24,44,'24:44','اور خدا ہی رات اور دن کو بدلتا رہتا ہے۔ اہل بصارت کے لئے اس میں بڑی عبرت ہے');
INSERT INTO "jalandhari" VALUES(24,45,'24:45','اور خدا ہی نے ہر چلنے پھرنے والے جاندار کو پانی سے پیدا کیا۔ تو اس میں بعضے ایسے ہیں کہ پیٹ کے بل چلتے ہیں اور بعض ایسے ہیں جو دو پاؤں پر چلتے ہیں اور بعض ایسے ہیں جو چار پاؤں پر چلتے ہیں۔ خدا جو چاہتا ہے پیدا کرتا ہے، بےشک خدا ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(24,46,'24:46','ہم ہی نے روشن آیتیں نازل کیں ہیں اور خدا جس کو چاہتا ہے سیدھے رستے کی طرف ہدایات کرتا ہے');
INSERT INTO "jalandhari" VALUES(24,47,'24:47','اور بعض لوگ کہتے ہیں کہ ہم خدا پر اور رسول پر ایمان لائے اور (ان کا) حکم مان لیا پھر اس کے بعد ان میں سے ایک فرقہ پھر جاتا ہے اور یہ لوگ صاحب ایمان ہی نہیں ہیں');
INSERT INTO "jalandhari" VALUES(24,48,'24:48','اور جب ان کو خدا اور اس کے رسول کی طرف بلایا جاتا ہے تاکہ (رسول خدا) ان کا قضیہ چکا دیں تو ان میں سے ایک فرقہ منہ پھیر لیتا ہے');
INSERT INTO "jalandhari" VALUES(24,49,'24:49','اگر (معاملہ) حق (ہو اور) ان کو (پہنچتا) ہو تو ان کی طرف مطیع ہو کر چلے آتے ہیں');
INSERT INTO "jalandhari" VALUES(24,50,'24:50','کیا ان کے دلوں میں بیماری ہے یا (یہ) شک میں ہیں یا ان کو یہ خوف ہے کہ خدا اور اس کا رسول ان کے حق میں ظلم کریں گے (نہیں) بلکہ یہ خود ظالم ہیں');
INSERT INTO "jalandhari" VALUES(24,51,'24:51','مومنوں کی تو یہ بات ہے کہ جب خدا اور اس کے رسول کی طرف بلائے جائیں تاکہ وہ ان میں فیصلہ کریں تو کہیں کہ ہم نے (حکم) سن لیا اور مان لیا۔ اور یہی لوگ فلاح پانے والے ہیں');
INSERT INTO "jalandhari" VALUES(24,52,'24:52','اور جو شخص خدا اور اس کے رسول کی فرمانبرداری کرے گا اور اس سے ڈرے گا تو ایسے لوگ مراد کو پہنچنے والے ہیں');
INSERT INTO "jalandhari" VALUES(24,53,'24:53','اور (یہ) خدا کی سخت سخت قسمیں کھاتے ہیں کہ اگر تم ان کو حکم دو تو (سب گھروں سے) نکل کھڑے ہوں۔ کہہ دو کہ قسمیں مت کھاؤ، پسندیدہ فرمانبرداری (درکار ہے)۔ بےشک خدا تمہارے سب اعمال سے خبردار ہے');
INSERT INTO "jalandhari" VALUES(24,54,'24:54','کہہ دو کہ خدا کی فرمانبرداری کرو اور رسول خدا کے حکم پر چلو۔ اگر منہ موڑو گے تو رسول پر (اس چیز کا ادا کرنا) جو ان کے ذمے ہے اور تم پر (اس چیز کا ادا کرنا) ہے جو تمہارے ذمے ہے اور اگر تم ان کے فرمان پر چلو گے تو سیدھا رستہ پالو گے اور رسول کے ذمے تو صاف صاف (احکام خدا کا) پہنچا دینا ہے');
INSERT INTO "jalandhari" VALUES(24,55,'24:55','جو لوگ تم میں سے ایمان لائے اور نیک کام کرتے رہے ان سے خدا کا وعدہ ہے کہ ان کو ملک کا حاکم بنادے گا جیسا ان سے پہلے لوگوں کو حاکم بنایا تھا اور ان کے دین کو جسے اس نے ان کے لئے پسند کیا ہے مستحکم وپائیدار کرے گا اور خوف کے بعد ان کو امن بخشے گا۔ وہ میری عبادت کریں گے اور میرے ساتھ کسی چیز کو شریک نہ بنائیں گے۔ اور جو اس کے بعد کفر کرے تو ایسے لوگ بدکردار ہیں');
INSERT INTO "jalandhari" VALUES(24,56,'24:56','اور نماز پڑھتے رہو اور زکوٰة دیتے رہو اور پیغمبر خدا کے فرمان پر چلتے رہو تاکہ تم پر رحمت کی جائے');
INSERT INTO "jalandhari" VALUES(24,57,'24:57','اور ایسا خیال نہ کرنا کہ تم پر کافر لوگ غالب آجائیں گے (وہ جا ہی کہاں سکتے ہیں) ان کا ٹھکانا دوزخ ہے اور وہ بہت برا ٹھکانا ہے');
INSERT INTO "jalandhari" VALUES(24,58,'24:58','مومنو! تمہارے غلام لونڈیاں اور جو بچّے تم میں سے بلوغ کو نہیں پہنچے تین دفعہ یعنی (تین اوقات میں) تم سے اجازت لیا کریں۔ (ایک تو) نماز صبح سے پہلے اور (دوسرے گرمی کی دوپہر کو) جب تم کپڑے اتار دیتے ہو۔ اور تیسرے عشاء کی نماز کے بعد۔ (یہ) تین (وقت) تمہارے پردے (کے) ہیں ان کے (آگے) پیچھے (یعنی دوسرے وقتوں میں) نہ تم پر کچھ گناہ ہے اور نہ ان پر۔ کہ کام کاج کے لئے ایک دوسرے کے پاس آتے رہتے ہو۔ اس طرح خدا اپنی آیتیں تم سے کھول کھول کر بیان فرماتا ہے اور خدا بڑا علم والا اور بڑا حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(24,59,'24:59','اور جب تمہارے لڑکے بالغ ہوجائیں تو ان کو بھی اسی طرح اجازت لینی چاہیئے جس طرح ان سے اگلے (یعنی بڑے آدمی) اجازت حاصل کرتے رہے ہیں۔ اس طرح خدا تم سے اپنی آیتیں کھول کھول کر سناتا ہے۔ اور خدا جاننے والا اور حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(24,60,'24:60','اور بڑی عمر کی عورتیں جن کو نکاح کی توقع نہیں رہی، اور وہ کپڑے اتار کر سر ننگا کرلیا کریں تو ان پر کچھ گناہ نہیں بشرطیکہ اپنی زینت کی چیزیں نہ ظاہر کریں۔ اور اس سے بھی بچیں تو یہ ان کے حق میں بہتر ہے۔ اور خدا سنتا اور جانتا ہے');
INSERT INTO "jalandhari" VALUES(24,61,'24:61','نہ تو اندھے پر کچھ گناہ ہے اور نہ لنگڑے پر اور نہ بیمار پر اور نہ خود تم پر کہ اپنے گھروں سے کھانا کھاؤ یا اپنے باپوں کے گھروں سے یا اپنی ماؤں کے گھروں سے یا بھائیوں کے گھروں سے یا اپنی بہنوں کے گھروں سے یا اپنے چچاؤں کے گھروں سے یا اپنی پھوپھیوں کے گھروں سے یا اپنے ماموؤں کے گھروں سے یا اپنی خالاؤں کے گھروں سے یا اس گھر سے جس کی کنجیاں تمہارے ہاتھ میں ہوں یا اپنے دوستوں کے گھروں سے (اور اس کا بھی) تم پر کچھ گناہ نہیں کہ سب مل کر کھانا کھاؤ یا جدا جدا۔ اور جب گھروں میں جایا کرو تو اپنے (گھر والوں کو) سلام کیا کرو۔ (یہ) خدا کی طرف سے مبارک اور پاکیزہ تحفہ ہے۔ اس طرح خدا اپنی آیتیں کھول کھول کر بیان فرماتا ہے تاکہ تم سمجھو');
INSERT INTO "jalandhari" VALUES(24,62,'24:62','مومن تو وہ ہیں جو خدا پر اور اس کے رسول پر ایمان لائے اور جب کبھی ایسے کام کے لئے جو جمع ہو کر کرنے کا ہو پیغمبر خدا کے پاس جمع ہوں تو ان سے اجازت لئے بغیر چلے نہیں جاتے۔ اے پیغمبر جو لوگ تم سے اجازت حاصل کرتے ہیں وہی خدا پر اور اس کے رسول پر ایمان رکھتے ہیں۔ سو جب یہ لوگ تم سے کسی کام کے لئے اجازت مانگا کریں تو ان میں سے جسے چاہا کرو اجازت دے دیا کرو اور ان کے لئے خدا سے بخششیں مانگا کرو۔ کچھ شک نہیں کہ خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(24,63,'24:63','مومنو پیغمبر کے بلانے کو ایسا خیال نہ کرنا جیسا تم آپس میں ایک دوسرے کو بلاتے ہو۔ بےشک خدا کو یہ لوگ معلوم ہیں جو تم میں سے آنکھ بچا کر چل دیتے ہیں تو جو لوگ ان کے حکم کی مخالفت کرتے ہیں ان کو ڈرنا چاہیئے کہ (ایسا نہ ہو کہ) ان پر کوئی آفت پڑ جائے یا تکلیف دینے والا عذاب نازل ہو');
INSERT INTO "jalandhari" VALUES(24,64,'24:64','دیکھو جو کچھ آسمانوں اور زمین میں ہے سب خدا ہی کا ہے۔ جس (طریق) پر تم ہو وہ اسے جانتا ہے۔ اور جس روز لوگ اس کی طرف لوٹائے جائیں گے تو جو لوگ عمل کرتے رہے وہ ان کو بتا دے گا۔ اور خدا ہر چیز پر قادر ہے۔');
INSERT INTO "jalandhari" VALUES(25,1,'25:1','وہ (خدائے غزوجل) بہت ہی بابرکت ہے جس نے اپنے بندے پر قرآن نازل فرمایا تاکہ اہل حال کو ہدایت کرے');
INSERT INTO "jalandhari" VALUES(25,2,'25:2','وہی کہ آسمان اور زمین کی بادشاہی اسی کی ہے اور جس نے (کسی کو) بیٹا نہیں بنایا اور جس کا بادشاہی میں کوئی شریک نہیں اور جس نے ہر چیز کو پیدا کیا اور پھر اس کا ایک اندازہ ٹھہرایا');
INSERT INTO "jalandhari" VALUES(25,3,'25:3','اور (لوگوں نے) اس کے سوا اور معبود بنا لئے ہیں جو کوئی چیز بھی پیدا نہیں کرسکتے اور خود پیدا کئے گئے ہیں۔ اور نہ اپنے نقصان اور نفع کا کچھ اختیار رکھتے ہیں اور نہ مرنا ان کے اختیار میں ہے اور نہ جینا اور نہ مر کر اُٹھ کھڑے ہونا');
INSERT INTO "jalandhari" VALUES(25,4,'25:4','اور کافر کہتے ہیں کہ یہ (قرآن) من گھڑت باتیں ہی جو اس (مدعی رسالت) نے بنالی ہیں۔ اور لوگوں نے اس میں اس کی مدد کی ہے۔ یہ لوگ (ایسا کہنے سے) ظلم اور جھوٹ پر (اُتر) آئے ہیں');
INSERT INTO "jalandhari" VALUES(25,5,'25:5','اور کہتے ہیں کہ یہ پہلے لوگوں کی کہانیاں ہیں جس کو اس نے لکھ رکھا ہے اور وہ صبح وشام اس کو پڑھ پڑھ کر سنائی جاتی ہیں');
INSERT INTO "jalandhari" VALUES(25,6,'25:6','کہہ دو کہ اُس نے اُس کو اُتارا ہے جو آسمانوں اور زمین کی پوشیدہ باتوں کو جانتا ہے۔ بےشک وہ بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(25,7,'25:7','اور کہتے ہیں کہ یہ کیسا پیغمبر ہے کہ کھاتا ہے اور بازاروں میں چلتا پھرتا ہے۔ کیوں نازل نہیں کیا گیا اس کے پاس کوئی فرشتہ اس کے ساتھ ہدایت کرنے کو رہتا');
INSERT INTO "jalandhari" VALUES(25,8,'25:8','یا اس کی طرف (آسمان سے) خزانہ اتارا جاتا یا اس کا کوئی باغ ہوتا کہ اس میں کھایا کرتا۔ اور ظالم کہتے ہیں کہ تم تو ایک جادو زدہ شخص کی پیروی کرتے ہو');
INSERT INTO "jalandhari" VALUES(25,9,'25:9','(اے پیغمبر) دیکھو تو یہ تمہارے بارے میں کس کس طرح کی باتیں کرتے ہیں سو گمراہ ہوگئے اور رستہ نہیں پاسکتے');
INSERT INTO "jalandhari" VALUES(25,10,'25:10','وہ (خدا) بہت بابرکت ہے جو اگر چاہے تو تمہارے لئے اس سے بہتر (چیزیں) بنا دے (یعنی) باغات جن کے نیچے نہریں بہہ رہی ہوں۔ نیز تمہارے لئے محل بنادے');
INSERT INTO "jalandhari" VALUES(25,11,'25:11','بلکہ یہ تو قیامت ہی کو جھٹلاتے ہیں اور ہم نے قیامت کے جھٹلانے والوں کے لئے دوزخ تیار کر رکھی ہے');
INSERT INTO "jalandhari" VALUES(25,12,'25:12','جس وقت وہ ان کو دور سے دیکھے گی (تو غضبناک ہو رہی ہوگی اور یہ) اس کے جوش (غضب) اور چیخنے چلانے کو سنیں گے');
INSERT INTO "jalandhari" VALUES(25,13,'25:13','اور جب یہ دوزخ کی کسی تنگ جگہ میں (زنجیروں میں) جکڑ کر ڈالے جائیں گے تو وہاں موت کو پکاریں گے');
INSERT INTO "jalandhari" VALUES(25,14,'25:14','آج ایک ہی موت کو نہ پکارو بہت سی موتوں کو پکارو');
INSERT INTO "jalandhari" VALUES(25,15,'25:15','پوچھو کہ یہ بہتر ہے یا بہشت جاودانی جس کا پرہیزگاروں سے وعدہ ہے۔ یہ ان (کے عملوں) کا بدلہ اور رہنے کا ٹھکانہ ہوگا');
INSERT INTO "jalandhari" VALUES(25,16,'25:16','وہاں جو چاہیں گے ان کے لئے میسر ہوگا ہمیشہ اس میں رہیں گے۔ یہ وعدہ خدا کو (پورا کرنا) لازم ہے اور اس لائق ہے کہ مانگ لیا جائے');
INSERT INTO "jalandhari" VALUES(25,17,'25:17','اور جس دن (خدا) ان کو اور اُن کو جنہیں یہ خدا کے سوا پوجتے ہیں جمع کرے گا تو فرمائے گا کیا تم نے میرے ان بندوں کو گمراہ کیا تھا یا یہ خود گمراہ ہوگئے تھے');
INSERT INTO "jalandhari" VALUES(25,18,'25:18','وہ کہیں گے تو پاک ہے ہمیں یہ بات شایان نہ تھی کہ تیرے سوا اوروں کو دوست بناتے۔ لیکن تو نے ہی ان کو اور ان کے باپ دادا کو برتنے کو نعمتیں دیں یہاں تک کہ وہ تیری یاد کو بھول گئے۔ اور یہ ہلاک ہونے والے لوگ تھے');
INSERT INTO "jalandhari" VALUES(25,19,'25:19','تو (کافرو) انہوں نے تو تم کو تمہاری بات میں جھٹلا دیا۔ پس (اب) تم (عذاب کو) نہ پھیر سکتے ہو۔ نہ (کسی سے) مدد لے سکتے ہو۔ اور جو شخص تم میں سے ظلم کرے گا ہم اس کو بڑے عذاب کا مزا چکھائیں گے');
INSERT INTO "jalandhari" VALUES(25,20,'25:20','اور ہم نے تم سے پہلے جتنے پیغمبر بھیجے ہیں سب کھانا کھاتے تھے اور بازاروں میں چلتے پھرتے تھے۔ اور ہم نے تمہیں ایک دوسرے کے لئے آزمائش بنایا ہے۔ کیا تم صبر کرو گے۔ اور تمہارا پروردگار تو دیکھنے والا ہے');
INSERT INTO "jalandhari" VALUES(25,21,'25:21','اور جو لوگ ہم سے ملنے کی امید نہیں رکھتے۔ کہتے ہیں کہ ہم پر فرشتے کیوں نہ نازل کئے گئے۔ یا ہم اپنی آنکھ سے اپنے پروردگار کو دیکھ لیں۔ یہ اپنے خیال میں بڑائی رکھتے ہیں اور (اسی بنا پر) بڑے سرکش ہو رہے ہی');
INSERT INTO "jalandhari" VALUES(25,22,'25:22','جس دن یہ فرشتوں کو دیکھیں گے اس دن گنہگاروں کے لئے خوشی کی بات نہیں ہوگی اور کہیں گے (خدا کرے تم) روک لئے (اور بند کردیئے) جاؤ');
INSERT INTO "jalandhari" VALUES(25,23,'25:23','اور جو انہوں نے عمل کئے ہوں گے ہم ان کی طرف متوجہ ہوں گے تو ان کو اُڑتی خاک کردیں گے');
INSERT INTO "jalandhari" VALUES(25,24,'25:24','اس دن اہل جنت کا ٹھکانا بھی بہتر ہوگا اور مقام استراحت بھی ہوگا');
INSERT INTO "jalandhari" VALUES(25,25,'25:25','اور جس دن آسمان ابر کے ساتھ پھٹ جائے گا اور فرشتے نازل کئے جائیں گے');
INSERT INTO "jalandhari" VALUES(25,26,'25:26','اس دن سچی بادشاہی خدا ہی کی ہوگی۔ اور وہ دن کافروں پر (سخت) مشکل ہوگا');
INSERT INTO "jalandhari" VALUES(25,27,'25:27','اور جس دن (ناعاقبت اندیش) ظالم اپنے ہاتھ کاٹ کاٹ کر کھائے گا (اور کہے گا) کہ اے کاش میں نے پیغمبر کے ساتھ رشتہ اختیار کیا ہوتا');
INSERT INTO "jalandhari" VALUES(25,28,'25:28','ہائے شامت کاش میں نے فلاں شخص کو دوست نہ بنایا ہوتا');
INSERT INTO "jalandhari" VALUES(25,29,'25:29','اس نے مجھ کو (کتاب) نصیحت کے میرے پاس آنے کے بعد بہکا دیا۔ اور شیطان انسان کو وقت پر دغا دینے والا ہے');
INSERT INTO "jalandhari" VALUES(25,30,'25:30','اور پیغمبر کہیں گے کہ اے پروردگار میری قوم نے اس قرآن کو چھوڑ رکھا تھا');
INSERT INTO "jalandhari" VALUES(25,31,'25:31','اور اسی طرح ہم نے گنہگاروں میں سے ہر پیغمبر کا دشمن بنا دیا۔ اور تمہارا پروردگار ہدایت دینے اور مدد کرنے کو کافی ہے');
INSERT INTO "jalandhari" VALUES(25,32,'25:32','اور کافر کہتے ہیں کہ اس پر قرآن ایک ہی دفعہ کیوں نہیں اُتارا گیا۔ اس طرح (آہستہ آہستہ) اس لئے اُتارا گیا کہ اس سے تمہارے دل کو قائم رکھیں۔ اور اسی واسطے ہم اس کو ٹھہر ٹھہر کر پڑھتے رہے ہیں');
INSERT INTO "jalandhari" VALUES(25,33,'25:33','اور یہ لوگ تمہارے پاس جو (اعتراض کی) بات لاتے ہیں ہم تمہارے پاس اس کا معقول اور خوب مشرح جواب بھیج دیتے ہیں');
INSERT INTO "jalandhari" VALUES(25,34,'25:34','جو لوگ اپنے مونہوں کے بل دوزخ کی طرف جمع کئے جائیں گے ان کا ٹھکانا بھی برا ہے اور وہ رستے سے بھی بہکے ہوئے ہیں');
INSERT INTO "jalandhari" VALUES(25,35,'25:35','اور ہم نے موسیٰ کو کتاب دی اور ان کے بھائی ہارون کو مددگار بنا کر ان کے ساتھ کیا');
INSERT INTO "jalandhari" VALUES(25,36,'25:36','اور کہا کہ دونوں ان لوگوں کے پاس جاؤ جن لوگوں نے ہماری آیتوں کی تکذیب کی۔ (جب تکذیب پر اڑے رہے) تو ہم نے ان کو ہلاک کر ڈالا');
INSERT INTO "jalandhari" VALUES(25,37,'25:37','اور نوح کی قوم نے بھی جب پیغمبروں کو جھٹلایا تو ہم نے انہیں غرق کر ڈالا اور لوگوں کے لئے نشانی بنا دیا۔ اور ظالموں کے لئے ہم نے دکھ دینے والا عذاب تیار کر رکھا ہے');
INSERT INTO "jalandhari" VALUES(25,38,'25:38','اور عاد اور ثمود اور کنوئیں والوں اور ان کے درمیان اور بہت سی جماعتوں کو بھی (ہلاک کر ڈالا)');
INSERT INTO "jalandhari" VALUES(25,39,'25:39','اور سب کے (سمجھانے کے لئے) ہم نے مثالیں بیان کیں اور (نہ ماننے پر) سب کا تہس نہس کردیا');
INSERT INTO "jalandhari" VALUES(25,40,'25:40','اور یہ کافر اس بستی پر بھی گزر چکے ہیں جس پر بری طرح کا مینہ برسایا گیا تھا۔ کیا وہ اس کو دیکھتے نہ ہوں گے۔ بلکہ ان کو (مرنے کے بعد) جی اُٹھنے کی امید ہی نہیں تھی۔');
INSERT INTO "jalandhari" VALUES(25,41,'25:41','اور یہ لوگ جب تم کو دیکھتے ہیں تو تمہاری ہنسی اُڑاتے ہیں۔ کہ کیا یہی شخص ہے جس کو خدا نے پیغمبر بنا کر بھیجا ہے');
INSERT INTO "jalandhari" VALUES(25,42,'25:42','اگر ہم نے اپنے معبودوں کے بارے میں ثابت قدم نہ رہتے تو یہ ضرور ہم کو بہکا دیتا۔ (اور ان سے پھیر دیتا) اور یہ عنقریب معلوم کرلیں گے جب عذاب دیکھیں گے کہ سیدھے رستے سے کون بھٹکا ہوا ہے');
INSERT INTO "jalandhari" VALUES(25,43,'25:43','کیا تم نے اس شخص کو دیکھا جس نے خواہش نفس کو معبود بنا رکھا ہے تو کیا تم اس پر نگہبان ہوسکتے ہو');
INSERT INTO "jalandhari" VALUES(25,44,'25:44','یا تم یہ خیال کرتے ہو کہ ان میں اکثر سنتے یا سمجھتے ہیں (نہیں) یہ تو چوپایوں کی طرح ہیں بلکہ ان سے بھی زیادہ گمراہ ہیں');
INSERT INTO "jalandhari" VALUES(25,45,'25:45','بلکہ تم نے اپنے پروردگار (کی قدرت) کو نہیں دیکھا کہ وہ سائے کو کس طرح دراز کر (کے پھیلا) دیتا ہے۔ اور اگر وہ چاہتا تو اس کو (بےحرکت) ٹھیرا رکھتا پھر سورج کو اس کا رہنما بنا دیتا ہے');
INSERT INTO "jalandhari" VALUES(25,46,'25:46','پھر اس کو ہم آہستہ آہستہ اپنی طرف سمیٹ لیتے ہیں');
INSERT INTO "jalandhari" VALUES(25,47,'25:47','اور وہی تو ہے جس نے رات کو تمہارے لئے پردہ اور نیند کو آرام بنایا اور دن کو اُٹھ کھڑے ہونے کا وقت ٹھہرایا');
INSERT INTO "jalandhari" VALUES(25,48,'25:48','اور وہی تو ہے جو اپنی رحمت کے مینھہ کے آگے ہواؤں کو خوش خبری بنا کر بھیجتا ہے۔ اور ہم آسمان سے پاک (اور نتھرا ہوا) پانی برساتے ہیں');
INSERT INTO "jalandhari" VALUES(25,49,'25:49','تاکہ اس سے شہر مردہ (یعنی زمین افتادہ) کو زندہ کردیں اور پھر اسے بہت سے چوپایوں اور آدمیوں کو جو ہم نے پیدا کئے ہیں پلاتے ہیں');
INSERT INTO "jalandhari" VALUES(25,50,'25:50','اور ہم نے اس (قرآن کی آیتوں) کو طرح طرح سے لوگوں میں بیان کیا تاکہ نصیحت پکڑیں مگر بہت سے لوگوں نے انکار کے سوا قبول نہ کیا');
INSERT INTO "jalandhari" VALUES(25,51,'25:51','اور اگر ہم چاہتے تو ہر بستی میں ڈرانے والا بھیج دیتے');
INSERT INTO "jalandhari" VALUES(25,52,'25:52','تو تم کافروں کا کہا نہ مانو اور ان سے اس قرآن کے حکم کے مطابق بڑے شدومد سے لڑو');
INSERT INTO "jalandhari" VALUES(25,53,'25:53','اور وہی تو ہے جس نے دو دریاؤں کو ملا دیا ایک کا پانی شیریں ہے پیاس بجھانے والا اور دوسرے کا کھاری چھاتی جلانے والا۔ اور دونوں کے درمیان ایک آڑ اور مضبوط اوٹ بنادی');
INSERT INTO "jalandhari" VALUES(25,54,'25:54','اور وہی تو ہے جس نے پانی سے آدمی پیدا کیا۔ پھر اس کو صاحب نسب اور صاحب قرابت دامادی بنایا۔ اور تمہارا پروردگار (ہر طرح کی) قدرت رکھتا ہے');
INSERT INTO "jalandhari" VALUES(25,55,'25:55','اور یہ لوگ خدا کو چھوڑ کر ایسی چیز کی پرستش کر تے ہیں جو نہ ان کو فائدہ پہنچا سکے اور نہ ضرر۔ اور کافر اپنے پروردگار کی مخالفت میں بڑا زور مارتا ہے');
INSERT INTO "jalandhari" VALUES(25,56,'25:56','اور ہم نے (اے محمدصلى الله عليه وسلم) تم کو صرف خوشی اور عذاب کی خبر سنانے کو بھیجا ہے');
INSERT INTO "jalandhari" VALUES(25,57,'25:57','کہہ دو کہ میں تم سے اس (کام) کی اجرت نہیں مانگتا، ہاں جو شخص چاہے اپنے پروردگار کی طرف جانے کا رستہ اختیار کرے');
INSERT INTO "jalandhari" VALUES(25,58,'25:58','اور اس (خدائے) زندہ پر بھروسہ رکھو جو (کبھی) نہیں مرے گا اور اس کی تعریف کے ساتھ تسبیح کرتے رہو۔ اور وہ اپنے بندوں کے گناہوں سے خبر رکھنے کو کافی ہے');
INSERT INTO "jalandhari" VALUES(25,59,'25:59','جس نے آسمانوں اور زمین کو اور جو کچھ ان دونوں کے درمیان ہے چھ دن میں پیدا کیا پھر عرش پر جا ٹھہرا وہ (جس کا نام) رحمٰن (یعنی بڑا مہربان ہے) تو اس کا حال کسی باخبر سے دریافت کرلو');
INSERT INTO "jalandhari" VALUES(25,60,'25:60','اور جب ان (کفار) سے کہا جاتا ہے کہ رحمٰن کو سجدہ کرو تو کہتے ہیں رحمٰن کیا؟ کیا جس کے لئے تم ہم سے کہتے ہو ہم اس کے آگے سجدہ کریں اور اس سے بدکتے ہیں');
INSERT INTO "jalandhari" VALUES(25,61,'25:61','اور (خدا) بڑی برکت والا ہے جس نے آسمانوں میں برج بنائے اور ان میں (آفتاب کا نہایت روشن) چراغ اور چمکتا ہوا چاند بھی بنایا');
INSERT INTO "jalandhari" VALUES(25,62,'25:62','اور وہی تو ہے جس نے رات اور دن کو ایک دوسرے کے پیچھے آنے جانے والا بنایا۔ (یہ باتیں) اس شخص کے لئے جو غور کرنا چاہے یا شکرگزاری کا ارادہ کرے (سوچنے اور سمجھنے کی ہیں)');
INSERT INTO "jalandhari" VALUES(25,63,'25:63','اور خدا کے بندے تو وہ ہیں جو زمین پر آہستگی سے چلتے ہیں اور جب جاہل لوگ ان سے (جاہلانہ) گفتگو کرتے ہیں تو سلام کہتے ہیں');
INSERT INTO "jalandhari" VALUES(25,64,'25:64','اور جو وہ اپنے پروردگار کے آگے سجدے کرکے اور (عجز وادب سے) کھڑے رہ کر راتیں بسر کرتے ہیں');
INSERT INTO "jalandhari" VALUES(25,65,'25:65','اور جو دعا مانگتے رہتے ہیں کہ اے پروردگار دوزخ کے عذاب کو ہم سے دور رکھیو کہ اس کا عذاب بڑی تکلیف کی چیز ہے');
INSERT INTO "jalandhari" VALUES(25,66,'25:66','اور دوزخ ٹھیرنے اور رہنے کی بہت بری جگہ ہے');
INSERT INTO "jalandhari" VALUES(25,67,'25:67','اور وہ جب خرچ کرتے ہیں تو نہ بےجا اُڑاتے ہیں اور نہ تنگی کو کام میں لاتے ہیں بلکہ اعتدال کے ساتھ۔ نہ ضرورت سے زیادہ نہ کم');
INSERT INTO "jalandhari" VALUES(25,68,'25:68','اور وہ جو خدا کے ساتھ کسی اور معبود کو نہیں پکارتے اور جن جاندار کو مار ڈالنا خدا نے حرام کیا ہے اس کو قتل نہیں کرتے مگر جائز طریق پر (یعنی شریعت کے مطابق) اور بدکاری نہیں کرتے۔ اور جو یہ کام کرے گا سخت گناہ میں مبتلا ہوگا');
INSERT INTO "jalandhari" VALUES(25,69,'25:69','قیامت کے دن اس کو دونا عذاب ہوگا اور ذلت وخواری سے ہمیشہ اس میں رہے گا');
INSERT INTO "jalandhari" VALUES(25,70,'25:70','مگر جس نے توبہ کی اور ایمان لایا اور اچھے کام کئے تو ایسے لوگوں کے گناہوں کو خدا نیکیوں سے بدل دے گا۔ اور خدا تو بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(25,71,'25:71','اور جو توبہ کرتا اور عمل نیک کرتا ہے تو بےشک وہ خدا کی طرف رجوع کرتا ہے');
INSERT INTO "jalandhari" VALUES(25,72,'25:72','اور وہ جو جھوٹی گواہی نہیں دیتے اور جب ان کو بیہودہ چیزوں کے پاس سے گزرنے کا اتفاق ہو تو بزرگانہ انداز سے گزرتے ہیں');
INSERT INTO "jalandhari" VALUES(25,73,'25:73','اور وہ کہ جب ان کو پروردگار کی باتیں سمجھائی جاتی ہیں تو اُن پر اندھے اور بہرے ہو کر نہیں گرتے (بلکہ غور سے سنتے ہیں)');
INSERT INTO "jalandhari" VALUES(25,74,'25:74','اور وہ جو (خدا سے) دعا مانگتے ہیں کہ اے پروردگار ہم کو ہماری بیویوں کی طرف سے (دل کا چین) اور اولاد کی طرف سے آنکھ کی ٹھنڈک عطا فرما اور ہمیں پرہیزگاروں کا امام بنا');
INSERT INTO "jalandhari" VALUES(25,75,'25:75','ان (صفات کے) لوگوں کو ان کے صبر کے بدلے اونچے اونچے محل دیئے جائیں گے۔ اور وہاں فرشتے ان سے دعا وسلام کے ساتھ ملاقات کریں گے');
INSERT INTO "jalandhari" VALUES(25,76,'25:76','اس میں وہ ہمیشہ رہیں گے۔ اور وہ ٹھیرنے اور رہنے کی بہت ہی عمدہ جگہ ہے');
INSERT INTO "jalandhari" VALUES(25,77,'25:77','کہہ دو کہ اگر تم (خدا کو) نہیں پکارتے تو میرا پروردگار بھی تمہاری کچھ پروا نہیں کرتا۔ تم نے تکذیب کی ہے سو اس کی سزا (تمہارے لئے) لازم ہوگی');
INSERT INTO "jalandhari" VALUES(26,1,'26:1','طٰسٓمٓ');
INSERT INTO "jalandhari" VALUES(26,2,'26:2','یہ کتاب روشن کی آیتیں ہیں');
INSERT INTO "jalandhari" VALUES(26,3,'26:3','(اے پیغمبرصلى الله عليه وسلم) شاید تم اس (رنج) سے کہ یہ لوگ ایمان نہیں لاتے اپنے تئیں ہلاک کردو گے');
INSERT INTO "jalandhari" VALUES(26,4,'26:4','اگر ہم چاہیں تو ان پر آسمان سے نشانی اُتار دیں۔ پھر ان کی گردنیں اس کے آگے جھک جائیں');
INSERT INTO "jalandhari" VALUES(26,5,'26:5','اور ان کے پاس (خدائے) رحمٰن کی طرف سے کوئی نصیحت نہیں آتی مگر یہ اس سے منہ پھیر لیتے ہیں');
INSERT INTO "jalandhari" VALUES(26,6,'26:6','سو یہ تو جھٹلا چکے اب ان کو اس چیز کی حقیقت معلوم ہوگی جس کی ہنسی اُڑاتے تھے');
INSERT INTO "jalandhari" VALUES(26,7,'26:7','کیا انہوں نے زمین کی طرف نہیں دیکھا کہ ہم نے اس میں ہر قسم کی کتنی نفیس چیزیں اُگائی ہیں');
INSERT INTO "jalandhari" VALUES(26,8,'26:8','کچھ شک نہیں کہ اس میں (قدرت خدا کی) نشانی ہے مگر یہ اکثر ایمان لانے والے نہیں ہیں');
INSERT INTO "jalandhari" VALUES(26,9,'26:9','اور تمہارا پروردگار غالب (اور) مہربان ہے');
INSERT INTO "jalandhari" VALUES(26,10,'26:10','اور جب تمہارے پروردگار نے موسیٰ کو پکارا کہ ظالم لوگوں کے پاس جاؤ');
INSERT INTO "jalandhari" VALUES(26,11,'26:11','(یعنی) قوم فرعون کے پاس، کیا یہ ڈرتے نہیں');
INSERT INTO "jalandhari" VALUES(26,12,'26:12','انہوں نے کہا کہ میرے پروردگار میں ڈرتا ہوں کہ یہ مجھے جھوٹا سمجھیں');
INSERT INTO "jalandhari" VALUES(26,13,'26:13','اور میرا دل تنگ ہوتا ہے اور میری زبان رکتی ہے تو ہارون کو حکم بھیج کہ میرے ساتھ چلیں');
INSERT INTO "jalandhari" VALUES(26,14,'26:14','اور ان لوگوں کا مجھ پر ایک گناہ (یعنی قبطی کے خون کا دعویٰ) بھی ہے سو مجھے یہ بھی خوف ہے کہ مجھ کو مار ہی ڈالیں');
INSERT INTO "jalandhari" VALUES(26,15,'26:15','فرمایا ہرگز نہیں۔ تم دونوں ہماری نشانیاں لے کر جاؤ ہم تمہارے ساتھ سننے والے ہیں');
INSERT INTO "jalandhari" VALUES(26,16,'26:16','تو دونوں فرعون کے پاس جاؤ اور کہو کہ ہم تمام جہان کے مالک کے بھیجے ہوئے ہیں');
INSERT INTO "jalandhari" VALUES(26,17,'26:17','(اور اس لئے آئے ہیں) کہ آپ بنی اسرائیل کو ہمارے ساتھ جانے کی اجازت دیں');
INSERT INTO "jalandhari" VALUES(26,18,'26:18','(فرعون نے موسیٰ سے کہا) کیا ہم نے تم کو کہ ابھی بچّے تھے پرورش نہیں کیا اور تم نے برسوں ہمارے ہاں عمر بسر (نہیں) کی');
INSERT INTO "jalandhari" VALUES(26,19,'26:19','اور تم نے وہ کام کیا تھا جو کیا اور تم ناشکرے معلوم ہوتے ہو');
INSERT INTO "jalandhari" VALUES(26,20,'26:20','(موسیٰ نے) کہاں (ہاں) وہ حرکت مجھ سے ناگہاں سرزد ہوئی تھی اور میں خطا کاروں میں تھا');
INSERT INTO "jalandhari" VALUES(26,21,'26:21','تو جب مجھے تم سے ڈر لگا تو تم میں سے بھاگ گیا۔ پھر خدا نے مجھ کو نبوت وعلم بخشا اور مجھے پیغمبروں میں سے کیا');
INSERT INTO "jalandhari" VALUES(26,22,'26:22','اور (کیا) یہی احسان ہے جو آپ مجھ پر رکھتے ہیں کہ آپ نے بنی اسرائیل کو غلام بنا رکھا ہے');
INSERT INTO "jalandhari" VALUES(26,23,'26:23','فرعون نے کہا کہ تمام جہان مالک کیا');
INSERT INTO "jalandhari" VALUES(26,24,'26:24','کہا کہ آسمانوں اور زمین اور جو کچھ ان دونوں میں ہے سب کا مالک۔ بشرطیکہ تم لوگوں کو یقین ہو');
INSERT INTO "jalandhari" VALUES(26,25,'26:25','فرعون نے اپنے اہالی موالی سے کہا کہ کیا تم سنتے نہیں');
INSERT INTO "jalandhari" VALUES(26,26,'26:26','(موسیٰ نے) کہا کہ تمہارا اور تمہارے پہلے باپ دادا کا مالک');
INSERT INTO "jalandhari" VALUES(26,27,'26:27','(فرعون نے) کہا کہ (یہ) پیغمبر جو تمہاری طرف بھیجا گیا ہے باؤلا ہے');
INSERT INTO "jalandhari" VALUES(26,28,'26:28','موسیٰ نے کہا کہ مشرق اور مغرب اور جو کچھ ان دونوں میں ہے سب کا مالک، بشرطیکہ تم کو سمجھ ہو');
INSERT INTO "jalandhari" VALUES(26,29,'26:29','(فرعون نے) کہا کہ اگر تم نے میرے سوا کسی اور کو معبود بنایا تو میں تمہیں قید کردوں گا');
INSERT INTO "jalandhari" VALUES(26,30,'26:30','(موسیٰ نے) کہا خواہ میں آپ کے پاس روشن چیز لاؤں (یعنی معجزہ)');
INSERT INTO "jalandhari" VALUES(26,31,'26:31','فرعون نے کہا اگر سچے ہو تو اسے لاؤ (دکھاؤ)');
INSERT INTO "jalandhari" VALUES(26,32,'26:32','پس انہوں نے اپنی لاٹھی ڈالی تو وہ اسی وقت صریح اژدہا بن گئی');
INSERT INTO "jalandhari" VALUES(26,33,'26:33','اور اپنا ہاتھ نکالا تو اسی دم دیکھنے والوں کے لئے سفید (براق نظر آنے لگا)');
INSERT INTO "jalandhari" VALUES(26,34,'26:34','فرعون نے اپنے گرد کے سرداروں سے کہا کہ یہ تو کامل فن جادوگر ہے');
INSERT INTO "jalandhari" VALUES(26,35,'26:35','چاہتا ہے کہ تم کو اپنے جادو (کے زور) سے تمہارے ملک سے نکال دے تو تمہاری کیا رائے ہے؟');
INSERT INTO "jalandhari" VALUES(26,36,'26:36','انہوں نے کہا کہ اسے اور اس کے بھائی (کے بارے) میں کچھ توقف کیجیئے اور شہروں میں ہرکارے بھیج دیجیئے');
INSERT INTO "jalandhari" VALUES(26,37,'26:37','کہ سب ماہر جادوگروں کو (جمع کرکے) آپ کے پاس لے آئیں');
INSERT INTO "jalandhari" VALUES(26,38,'26:38','تو جادوگر ایک مقررہ دن کی میعاد پر جمع ہوگئے');
INSERT INTO "jalandhari" VALUES(26,39,'26:39','اور لوگوں سے کہہ دیا گیا کہ تم (سب) کو اکھٹے ہو کر جانا چاہیئے');
INSERT INTO "jalandhari" VALUES(26,40,'26:40','تاکہ اگر جادوگر غالب رہیں تو ہم ان کے پیرو ہوجائیں');
INSERT INTO "jalandhari" VALUES(26,41,'26:41','جب جادوگر آگئے تو فرعون سے کہنے لگے اگر ہم غالب رہے تو ہمیں صلہ بھی عطا ہوگا؟');
INSERT INTO "jalandhari" VALUES(26,42,'26:42','فرعون نے کہا ہاں اور تم مقربوں میں بھی داخل کرلئے جاؤ گے');
INSERT INTO "jalandhari" VALUES(26,43,'26:43','موسیٰ نے ان سے کہا کہ جو چیز ڈالنی چاہتے ہو، ڈالو');
INSERT INTO "jalandhari" VALUES(26,44,'26:44','تو انہوں نے اپنی رسیاں اور لاٹھیاں ڈالیں اور کہنے لگے کہ فرعون کے اقبال کی قسم ہم ضرور غالب رہیں گے');
INSERT INTO "jalandhari" VALUES(26,45,'26:45','پھر موسیٰ نے اپنی لاٹھی ڈالی تو وہ ان چیزوں کو جو جادوگروں نے بنائی تھیں یکایک نگلنے لگی');
INSERT INTO "jalandhari" VALUES(26,46,'26:46','تب جادوگر سجدے میں گر پڑے');
INSERT INTO "jalandhari" VALUES(26,47,'26:47','(اور) کہنے لگے کہ ہم تمام جہان کے مالک پر ایمان لے آئے');
INSERT INTO "jalandhari" VALUES(26,48,'26:48','جو موسیٰ اور ہارون کا مالک ہے');
INSERT INTO "jalandhari" VALUES(26,49,'26:49','فرعون نے کہا کیا اس سے پہلے کہ میں تم کو اجازت دوں تم اس پر ایمان لے آئے، بےشک یہ تمہارا بڑا ہے جس نے تم کو جادو سکھایا ہے۔ سو عنقریب تم (اس کا انجام) معلوم کرلو گے کہ میں تمہارے ہاتھ اور پاؤں اطراف مخالف سے کٹوا دوں گا اور تم سب کو سولی پر چڑھوا دوں گا');
INSERT INTO "jalandhari" VALUES(26,50,'26:50','انہوں نے کہا کہ کچھ نقصان (کی بات) نہیں ہم اپنے پروردگار کی طرف لوٹ جانے والے ہیں');
INSERT INTO "jalandhari" VALUES(26,51,'26:51','ہمیں امید ہے کہ ہمارا پروردگار ہمارے گناہ بخش دے گا۔ اس لئے کہ ہم اول ایمان لانے والوں میں ہیں');
INSERT INTO "jalandhari" VALUES(26,52,'26:52','اور ہم نے موسیٰ کی طرف وحی بھیجی کہ ہمارے بندوں کو رات کو لے نکلو کہ (فرعونیوں کی طرف سے) تمہارا تعاقب کیا جائے گا');
INSERT INTO "jalandhari" VALUES(26,53,'26:53','تو فرعون نے شہروں میں نقیب راونہ کئے');
INSERT INTO "jalandhari" VALUES(26,54,'26:54','(اور کہا) کہ یہ لوگ تھوڑی سی جماعت ہے');
INSERT INTO "jalandhari" VALUES(26,55,'26:55','اور یہ ہمیں غصہ دلا رہے ہیں');
INSERT INTO "jalandhari" VALUES(26,56,'26:56','اور ہم سب باسازو سامان ہیں');
INSERT INTO "jalandhari" VALUES(26,57,'26:57','تو ہم نے ان کو باغوں اور چشموں سے نکال دیا');
INSERT INTO "jalandhari" VALUES(26,58,'26:58','اور خزانوں اور نفیس مکانات سے');
INSERT INTO "jalandhari" VALUES(26,59,'26:59','(ان کے ساتھ ہم نے) اس طرح (کیا) اور ان چیزوں کا وارث بنی اسرائیل کو کر دیا');
INSERT INTO "jalandhari" VALUES(26,60,'26:60','تو انہوں نے سورج نکلتے (یعنی صبح کو) ان کا تعاقب کیا');
INSERT INTO "jalandhari" VALUES(26,61,'26:61','جب دونوں جماعتیں آمنے سامنے ہوئیں تو موسیٰ کے ساتھی کہنے لگے کہ ہم تو پکڑ لئے گئے');
INSERT INTO "jalandhari" VALUES(26,62,'26:62','موسیٰ نے کہا ہرگز نہیں میرا پروردگار میرے ساتھ ہے وہ مجھے رستہ بتائے گا');
INSERT INTO "jalandhari" VALUES(26,63,'26:63','اس وقت ہم نے موسیٰ کی طرف وحی بھیجی کہ اپنی لاٹھی دریا پر مارو۔ تو دریا پھٹ گیا۔ اور ہر ایک ٹکڑا (یوں) ہوگیا (کہ) گویا بڑا پہاڑ (ہے)');
INSERT INTO "jalandhari" VALUES(26,64,'26:64','اور دوسروں کو وہاں ہم نے قریب کردیا');
INSERT INTO "jalandhari" VALUES(26,65,'26:65','اور موسیٰ اور ان کے ساتھ والوں کو تو بچا لیا');
INSERT INTO "jalandhari" VALUES(26,66,'26:66','پھر دوسروں کو ڈبو دیا');
INSERT INTO "jalandhari" VALUES(26,67,'26:67','بےشک اس (قصے) میں نشانی ہے۔ لیکن یہ اکثر ایمان لانے والے نہیں');
INSERT INTO "jalandhari" VALUES(26,68,'26:68','اور تمہارا پروردگار تو غالب (اور) مہربان ہے');
INSERT INTO "jalandhari" VALUES(26,69,'26:69','اور ان کو ابراہیم کا حال پڑھ کر سنا دو');
INSERT INTO "jalandhari" VALUES(26,70,'26:70','جب انہوں نے اپنے باپ اور اپنی قوم کے لوگوں سے کہا کہ تم کس چیز کو پوجتے ہو');
INSERT INTO "jalandhari" VALUES(26,71,'26:71','وہ کہنے لگے کہ ہم بتوں کو پوجتے ہیں اور ان کی پوجا پر قائم ہیں');
INSERT INTO "jalandhari" VALUES(26,72,'26:72','ابراہیم نے کہا کہ جب تم ان کو پکارتے ہو تو کیا وہ تمہاری آواز کو سنتے ہیں؟');
INSERT INTO "jalandhari" VALUES(26,73,'26:73','یا تمہیں کچھ فائدے دے سکتے یا نقصان پہنچا سکتے ہیں؟');
INSERT INTO "jalandhari" VALUES(26,74,'26:74','انہوں نے کہا (نہیں) بلکہ ہم نے اپنے باپ دادا کو اسی طرح کرتے دیکھا ہے');
INSERT INTO "jalandhari" VALUES(26,75,'26:75','ابراہیم نے کہا کیا تم نے دیکھا کہ جن کو تم پوجتے رہے ہو');
INSERT INTO "jalandhari" VALUES(26,76,'26:76','تم بھی اور تمہارے اگلے باپ دادا بھی');
INSERT INTO "jalandhari" VALUES(26,77,'26:77','وہ میرے دشمن ہیں۔ مگر خدائے رب العالمین (میرا دوست ہے)');
INSERT INTO "jalandhari" VALUES(26,78,'26:78','جس نے مجھے پیدا کیا ہے اور وہی مجھے رستہ دکھاتا ہے');
INSERT INTO "jalandhari" VALUES(26,79,'26:79','اور وہ جو مجھے کھلاتا اور پلاتا ہے');
INSERT INTO "jalandhari" VALUES(26,80,'26:80','اور جب میں بیمار پڑتا ہوں تو مجھے شفا بخشتا ہے');
INSERT INTO "jalandhari" VALUES(26,81,'26:81','اور جو مجھے مارے گا اور پھر زندہ کرے گا');
INSERT INTO "jalandhari" VALUES(26,82,'26:82','اور وہ جس سے میں امید رکھتا ہوں کہ قیامت کے دن میرے گناہ بخشے گا');
INSERT INTO "jalandhari" VALUES(26,83,'26:83','اے پروردگار مجھے علم ودانش عطا فرما اور نیکوکاروں میں شامل کر');
INSERT INTO "jalandhari" VALUES(26,84,'26:84','اور پچھلے لوگوں میں میرا ذکر نیک (جاری) کر');
INSERT INTO "jalandhari" VALUES(26,85,'26:85','اور مجھے نعمت کی بہشت کے وارثوں میں کر');
INSERT INTO "jalandhari" VALUES(26,86,'26:86','اور میرے باپ کو بخش دے کہ وہ گمراہوں میں سے ہے');
INSERT INTO "jalandhari" VALUES(26,87,'26:87','اور جس دن لوگ اٹھا کھڑے کئے جائیں گے مجھے رسوا نہ کیجیو');
INSERT INTO "jalandhari" VALUES(26,88,'26:88','جس دن نہ مال ہی کچھ فائدہ دے سکا گا اور نہ بیٹے');
INSERT INTO "jalandhari" VALUES(26,89,'26:89','ہاں جو شخص خدا کے پاس پاک دل لے کر آیا (وہ بچ جائے گا)');
INSERT INTO "jalandhari" VALUES(26,90,'26:90','اور بہشت پرہیزگاروں کے قریب کردی جائے گی');
INSERT INTO "jalandhari" VALUES(26,91,'26:91','اور دوزخ گمراہوں کے سامنے لائی جائے گی');
INSERT INTO "jalandhari" VALUES(26,92,'26:92','اور ان سے کہا جائے گا کہ جن کو تم پوجتے تھے وہ کہاں ہیں؟');
INSERT INTO "jalandhari" VALUES(26,93,'26:93','یعنی جن کو خدا کے سوا (پوجتے تھے) کیا وہ تمہاری مدد کرسکتے ہیں یا خود بدلہ لے سکتے ہیں');
INSERT INTO "jalandhari" VALUES(26,94,'26:94','تو وہ اور گمراہ (یعنی بت اور بت پرست) اوندھے منہ دوزخ میں ڈال دیئے جائیں گے');
INSERT INTO "jalandhari" VALUES(26,95,'26:95','اور شیطان کے لشکر سب کے سب (داخل جہنم ہوں گے)');
INSERT INTO "jalandhari" VALUES(26,96,'26:96','وہ آپس میں جھگڑیں گے اور کہیں گے');
INSERT INTO "jalandhari" VALUES(26,97,'26:97','کہ خدا کی قسم ہم تو صریح گمراہی میں تھے');
INSERT INTO "jalandhari" VALUES(26,98,'26:98','جب کہ تمہیں (خدائے) رب العالمین کے برابر ٹھہراتے تھے');
INSERT INTO "jalandhari" VALUES(26,99,'26:99','اور ہم کو ان گنہگاروں ہی نے گمراہ کیا تھا');
INSERT INTO "jalandhari" VALUES(26,100,'26:100','تو (آج) نہ کوئی ہمارا سفارش کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(26,101,'26:101','اور نہ گرم جوش دوست');
INSERT INTO "jalandhari" VALUES(26,102,'26:102','کاش ہمیں (دنیا میں) پھر جانا ہو تم ہم مومنوں میں ہوجائیں');
INSERT INTO "jalandhari" VALUES(26,103,'26:103','بےشک اس میں نشانی ہے اور ان میں اکثر ایمان لانے والے نہیں');
INSERT INTO "jalandhari" VALUES(26,104,'26:104','اور تمہارا پروردگار تو غالب اور مہربان ہے');
INSERT INTO "jalandhari" VALUES(26,105,'26:105','قوم نوح نے بھی پیغمبروں کو جھٹلایا');
INSERT INTO "jalandhari" VALUES(26,106,'26:106','جب ان سے ان کے بھائی نوح نے کہا کہ تم ڈرتے کیوں نہیں');
INSERT INTO "jalandhari" VALUES(26,107,'26:107','میں تو تمہارا امانت دار ہوں');
INSERT INTO "jalandhari" VALUES(26,108,'26:108','تو خدا سے ڈرو اور میرا کہا مانو');
INSERT INTO "jalandhari" VALUES(26,109,'26:109','اور اس کام کا تم سے کچھ صلہ نہیں مانگتا۔ میرا صلہ تو خدائے رب العالمین ہی پر ہے');
INSERT INTO "jalandhari" VALUES(26,110,'26:110','تو خدا سے ڈرو اور میرے کہنے پر چلو');
INSERT INTO "jalandhari" VALUES(26,111,'26:111','وہ بولے کہ کیا ہم تم کو مان لیں اور تمہارے پیرو تو رذیل لوگ ہوتے ہیں');
INSERT INTO "jalandhari" VALUES(26,112,'26:112','نوح نے کہا کہ مجھے کیا معلوم کہ وہ کیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(26,113,'26:113','ان کا حساب (اعمال) میرے پروردگار کے ذمے ہے کاش تم سمجھو');
INSERT INTO "jalandhari" VALUES(26,114,'26:114','اور میں مومنوں کو نکال دینے والا نہیں ہوں');
INSERT INTO "jalandhari" VALUES(26,115,'26:115','میں تو صرف کھول کھول کر نصیحت کرنے والا ہوں');
INSERT INTO "jalandhari" VALUES(26,116,'26:116','انہوں نے کہا کہ نوح اگر تم باز نہ آؤ گے تو سنگسار کردیئے جاؤ گے');
INSERT INTO "jalandhari" VALUES(26,117,'26:117','نوح نے کہا کہ پروردگار میری قوم نے تو مجھے جھٹلا دیا');
INSERT INTO "jalandhari" VALUES(26,118,'26:118','سو تو میرے اور ان کے درمیان ایک کھلا فیصلہ کردے اور مجھے اور جو میرے ساتھ ہیں ان کو بچا لے');
INSERT INTO "jalandhari" VALUES(26,119,'26:119','پس ہم نے ان کو اور جو ان کے ساتھ کشتی میں سوار تھے، ان کو بچا لیا');
INSERT INTO "jalandhari" VALUES(26,120,'26:120','پھر اس کے بعد باقی لوگوں کو ڈبو دیا');
INSERT INTO "jalandhari" VALUES(26,121,'26:121','بےشک اس میں نشانی ہے اور ان میں اکثر ایمان لانے والے نہیں تھے');
INSERT INTO "jalandhari" VALUES(26,122,'26:122','اور تمہارا پروردگار تو غالب (اور) مہربان ہے');
INSERT INTO "jalandhari" VALUES(26,123,'26:123','عاد نے بھی پیغمبروں کو جھٹلایا');
INSERT INTO "jalandhari" VALUES(26,124,'26:124','جب ان سے ان کے بھائی ہود نے کہا کیا تم ڈرتے نہیں');
INSERT INTO "jalandhari" VALUES(26,125,'26:125','میں تو تمہارا امانت دار پیغمبر ہوں');
INSERT INTO "jalandhari" VALUES(26,126,'26:126','تو خدا سے ڈرو اور میرا کہا مانو');
INSERT INTO "jalandhari" VALUES(26,127,'26:127','اور میں اس کا تم سے کچھ بدلہ نہیں مانگتا۔ میرا بدلہ (خدائے) رب العالمین کے ذمے ہے');
INSERT INTO "jalandhari" VALUES(26,128,'26:128','بھلا تم ہر اونچی جگہ پر نشان تعمیر کرتے ہو');
INSERT INTO "jalandhari" VALUES(26,129,'26:129','اور محل بناتے ہو شاید تم ہمیشہ رہو گے');
INSERT INTO "jalandhari" VALUES(26,130,'26:130','اور جب (کسی کو) پکڑتے ہو تو ظالمانہ پکڑتے ہو');
INSERT INTO "jalandhari" VALUES(26,131,'26:131','تو خدا سے ڈرو اور میری اطاعت کرو');
INSERT INTO "jalandhari" VALUES(26,132,'26:132','اور اس سے جس نے تم کو ان چیزوں سے مدد دی جن کو تم جانتے ہو۔ ڈرو');
INSERT INTO "jalandhari" VALUES(26,133,'26:133','اس نے تمہیں چارپایوں اور بیٹوں سے مدد دی');
INSERT INTO "jalandhari" VALUES(26,134,'26:134','اور باغوں اور چشموں سے');
INSERT INTO "jalandhari" VALUES(26,135,'26:135','مجھ کو تمہارے بارے میں بڑے (سخت) دن کے عذاب کا خوف ہے');
INSERT INTO "jalandhari" VALUES(26,136,'26:136','وہ کہنے لگے کہ ہمیں خواہ نصیحت کرو یا نہ کرو ہمارے لئے یکساں ہے');
INSERT INTO "jalandhari" VALUES(26,137,'26:137','یہ تو اگلوں ہی کے طریق ہیں');
INSERT INTO "jalandhari" VALUES(26,138,'26:138','اور ہم پر کوئی عذاب نہیں آئے گا');
INSERT INTO "jalandhari" VALUES(26,139,'26:139','تو انہوں نے ہود کو جھٹلایا تو ہم نے ان کو ہلاک کر ڈالا۔ بےشک اس میں نشانی ہے۔ اور ان میں اکثر ایمان لانے والے نہیں تھے');
INSERT INTO "jalandhari" VALUES(26,140,'26:140','اور تمہارا پروردگار تو غالب اور مہربان ہے');
INSERT INTO "jalandhari" VALUES(26,141,'26:141','(اور) قوم ثمود نے بھی پیغمبروں کو جھٹلا دیا');
INSERT INTO "jalandhari" VALUES(26,142,'26:142','جب ان سے ان کے بھائی صالح نے کہا کہ تم ڈرتے کیوں نہیں؟');
INSERT INTO "jalandhari" VALUES(26,143,'26:143','میں تو تمہارا امانت دار ہوں');
INSERT INTO "jalandhari" VALUES(26,144,'26:144','تو خدا سے ڈرو اور میرا کہا مانو');
INSERT INTO "jalandhari" VALUES(26,145,'26:145','اور میں اس کا تم سے بدلہ نہیں مانگتا۔ میرا بدلہ (خدا) رب العالمین کے ذمے ہے');
INSERT INTO "jalandhari" VALUES(26,146,'26:146','کیا وہ چیزیں (تمہیں یہاں میسر) ہیں ان میں تم بےخوف چھوڑ دیئے جاؤ گے');
INSERT INTO "jalandhari" VALUES(26,147,'26:147','(یعنی) باغ اور چشمے');
INSERT INTO "jalandhari" VALUES(26,148,'26:148','اور کھیتیاں اور کھجوریں جن کے خوشے لطیف ونازک ہوتے ہیں');
INSERT INTO "jalandhari" VALUES(26,149,'26:149','اور تکلف سے پہاڑوں میں تراش خراش کر گھر بناتے ہو');
INSERT INTO "jalandhari" VALUES(26,150,'26:150','تو خدا سے ڈرو اور میرے کہنے پر چلو');
INSERT INTO "jalandhari" VALUES(26,151,'26:151','اور حد سے تجاوز کرنے والوں کی بات نہ مانو');
INSERT INTO "jalandhari" VALUES(26,152,'26:152','جو ملک میں فساد کرتے ہیں اور اصلاح نہیں کرتے');
INSERT INTO "jalandhari" VALUES(26,153,'26:153','وہ کہنے لگے کہ تم تو جادو زدہ ہو');
INSERT INTO "jalandhari" VALUES(26,154,'26:154','تم اور کچھ نہیں ہماری طرح آدمی ہو۔ اگر سچے ہو تو کوئی نشانی پیش کرو');
INSERT INTO "jalandhari" VALUES(26,155,'26:155','صالح نے کہا (دیکھو) یہ اونٹنی ہے (ایک دن) اس کی پانی پینے کی باری ہے اور ایک معین روز تمہاری باری');
INSERT INTO "jalandhari" VALUES(26,156,'26:156','اور اس کو کوئی تکلیف نہ دینا (نہیں تو) تم کو سخت عذاب آ پکڑے گا');
INSERT INTO "jalandhari" VALUES(26,157,'26:157','تو انہوں نے اس کی کونچیں کاٹ ڈالیں پھر نادم ہوئے');
INSERT INTO "jalandhari" VALUES(26,158,'26:158','سو ان کو عذاب نے آن پکڑا۔ بےشک اس میں نشانی ہے۔ اور ان میں اکثر ایمان لانے والے نہیں تھے');
INSERT INTO "jalandhari" VALUES(26,159,'26:159','اور تمہارا پروردگار تو غالب (اور) مہربان ہے');
INSERT INTO "jalandhari" VALUES(26,160,'26:160','(اور قوم) لوط نے بھی پیغمبروں کو جھٹلایا');
INSERT INTO "jalandhari" VALUES(26,161,'26:161','جب ان سے ان کے بھائی لوط نے کہا کہ تم کیوں نہیں ڈرتے؟');
INSERT INTO "jalandhari" VALUES(26,162,'26:162','میں تو تمہارا امانت دار پیغمبر ہوں');
INSERT INTO "jalandhari" VALUES(26,163,'26:163','تو خدا سے ڈرو اور میرا کہا مانو');
INSERT INTO "jalandhari" VALUES(26,164,'26:164','اور میں تم سے اس (کام) کا بدلہ نہیں مانگتا۔ میرا بدلہ (خدائے) رب العالمین کے ذمے ہے');
INSERT INTO "jalandhari" VALUES(26,165,'26:165','کیا تم اہل عالم میں سے لڑکوں پر مائل ہوتے ہو');
INSERT INTO "jalandhari" VALUES(26,166,'26:166','اور تمہارے پروردگار نے جو تمہارے لئے تمہاری بیویاں پیدا کی ہیں ان کو چھوڑ دیتے ہو۔ حقیقت یہ ہے کہ تم حد سے نکل جانے والے ہو');
INSERT INTO "jalandhari" VALUES(26,167,'26:167','وہ کہنے لگے کہ لوط اگر تم باز نہ آؤ گے تو شہر بدر کردیئے جاؤ گے');
INSERT INTO "jalandhari" VALUES(26,168,'26:168','لوط نے کہا کہ میں تمہارے کام کا سخت دشمن ہوں');
INSERT INTO "jalandhari" VALUES(26,169,'26:169','اے میرے پروردگار مجھ کو اور میرے گھر والوں کو ان کے کاموں (کے وبال) سے نجات دے');
INSERT INTO "jalandhari" VALUES(26,170,'26:170','سو ہم نے ان کو اور ان کے گھر والوں کو سب کو نجات دی');
INSERT INTO "jalandhari" VALUES(26,171,'26:171','مگر ایک بڑھیا کہ پیچھے رہ گئی');
INSERT INTO "jalandhari" VALUES(26,172,'26:172','پھر ہم نے اوروں کو ہلاک کردیا');
INSERT INTO "jalandhari" VALUES(26,173,'26:173','اور ان پر مینھہ برسایا۔ سو جو مینھہ ان (لوگوں) پر (برسا) جو ڈرائے گئے برا تھا');
INSERT INTO "jalandhari" VALUES(26,174,'26:174','بےشک اس میں نشانی ہے۔ اور ان میں اکثر ایمان لانے والے نہیں تھے');
INSERT INTO "jalandhari" VALUES(26,175,'26:175','اور تمہارا پروردگار تو غالب (اور) مہربان ہے۔');
INSERT INTO "jalandhari" VALUES(26,176,'26:176','اور بن کے رہنے والوں نے بھی پیغمبروں کو جھٹلایا');
INSERT INTO "jalandhari" VALUES(26,177,'26:177','جب ان سے شعیب نے کہا کہ تم ڈرتے کیوں نہیں؟');
INSERT INTO "jalandhari" VALUES(26,178,'26:178','میں تو تمہارا امانت دار پیغمبر ہوں');
INSERT INTO "jalandhari" VALUES(26,179,'26:179','تو خدا سے ڈرو اور میرا کہا مانو');
INSERT INTO "jalandhari" VALUES(26,180,'26:180','اور میں اس کام کا تم سے کچھ بدلہ نہیں مانگتا میرا بدلہ تو خدائے رب العالمین کے ذمے ہے');
INSERT INTO "jalandhari" VALUES(26,181,'26:181','(دیکھو) پیمانہ پورا بھرا کرو اور نقصان نہ کیا کرو');
INSERT INTO "jalandhari" VALUES(26,182,'26:182','اور ترازو سیدھی رکھ کر تولا کرو');
INSERT INTO "jalandhari" VALUES(26,183,'26:183','اور لوگوں کو ان کی چیزیں کم نہ دیا کرو اور ملک میں فساد نہ کرتے پھرو');
INSERT INTO "jalandhari" VALUES(26,184,'26:184','اور اس سے ڈرو جس نے تم کو اور پہلی خلقت کو پیدا کیا');
INSERT INTO "jalandhari" VALUES(26,185,'26:185','وہ کہنے لگے کہ تم جادو زدہ ہو');
INSERT INTO "jalandhari" VALUES(26,186,'26:186','اور تم اور کچھ نہیں ہم ہی جیسے آدمی ہو۔ اور ہمارا خیال ہے کہ تم جھوٹے ہو');
INSERT INTO "jalandhari" VALUES(26,187,'26:187','اور اگر سچے ہو تو ہم پر آسمان سے ایک ٹکڑا لا کر گراؤ');
INSERT INTO "jalandhari" VALUES(26,188,'26:188','شعیب نے کہا کہ جو کام تم کرتے ہو میرا پروردگار اس سے خوب واقف ہے');
INSERT INTO "jalandhari" VALUES(26,189,'26:189','تو ان لوگوں نے ان کو جھٹلایا، پس سائبان کے عذاب نے ان کو آ پکڑا۔ بےشک وہ بڑے (سخت) دن کا عذاب تھا');
INSERT INTO "jalandhari" VALUES(26,190,'26:190','اس میں یقیناً نشانی ہے۔ اور ان میں اکثر ایمان لانے والے نہیں تھے');
INSERT INTO "jalandhari" VALUES(26,191,'26:191','اور تمہارا پروردگار تو غالب (اور) مہربان ہے');
INSERT INTO "jalandhari" VALUES(26,192,'26:192','اور یہ قرآن (خدائے) پروردگار عالم کا اُتارا ہوا ہے');
INSERT INTO "jalandhari" VALUES(26,193,'26:193','اس کو امانت دار فرشتہ لے کر اُترا ہے');
INSERT INTO "jalandhari" VALUES(26,194,'26:194','(یعنی اس نے) تمہارے دل پر (القا) کیا ہے تاکہ (لوگوں کو) نصیحت کرتے رہو');
INSERT INTO "jalandhari" VALUES(26,195,'26:195','اور (القا بھی) فصیح عربی زبان میں (کیا ہے)');
INSERT INTO "jalandhari" VALUES(26,196,'26:196','اور اس کی خبر پہلے پیغمبروں کی کتابوں میں (لکھی ہوئی) ہے');
INSERT INTO "jalandhari" VALUES(26,197,'26:197','کیا ان کے لئے یہ سند نہیں ہے کہ علمائے بنی اسرائیل اس (بات) کو جانتے ہیں');
INSERT INTO "jalandhari" VALUES(26,198,'26:198','اور اگر ہم اس کو کسی غیر اہل زبان پر اُتارتے');
INSERT INTO "jalandhari" VALUES(26,199,'26:199','اور وہ اسے ان (لوگوں کو) پڑھ کر سناتا تو یہ اسے (کبھی) نہ مانتے');
INSERT INTO "jalandhari" VALUES(26,200,'26:200','اسی طرح ہم نے انکار کو گنہگاروں کے دلوں میں داخل کردیا');
INSERT INTO "jalandhari" VALUES(26,201,'26:201','وہ جب تک درد دینے والا عذاب نہ دیکھ لیں گے، اس کو نہیں مانیں گے');
INSERT INTO "jalandhari" VALUES(26,202,'26:202','وہ ان پر ناگہاں آ واقع ہوگا اور انہیں خبر بھی نہ ہوگی');
INSERT INTO "jalandhari" VALUES(26,203,'26:203','اس وقت کہیں گے کیا ہمیں ملہت ملے گی؟');
INSERT INTO "jalandhari" VALUES(26,204,'26:204','تو کیا یہ ہمارے عذاب کو جلدی طلب کر رہے ہیں');
INSERT INTO "jalandhari" VALUES(26,205,'26:205','بھلا دیکھو تو اگر ہم ان کو برسوں فائدے دیتے رہے');
INSERT INTO "jalandhari" VALUES(26,206,'26:206','پھر ان پر وہ (عذاب) آ واقع ہو جس کا تم سے وعدہ کیا جاتا ہے');
INSERT INTO "jalandhari" VALUES(26,207,'26:207','تو جو فائدے یہ اٹھاتے رہے ان کے کس کام آئیں گے');
INSERT INTO "jalandhari" VALUES(26,208,'26:208','اور ہم نے کوئی بستی ہلاک نہیں کی مگر اس کے لئے نصیحت کرنے والے (پہلے بھیج دیتے) تھے');
INSERT INTO "jalandhari" VALUES(26,209,'26:209','نصیحت کردیں اور ہم ظالم نہیں ہیں');
INSERT INTO "jalandhari" VALUES(26,210,'26:210','اور اس (قرآن) کو شیطان لے کر نازل نہیں ہوئے');
INSERT INTO "jalandhari" VALUES(26,211,'26:211','یہ کام نہ تو ان کو سزاوار ہے اور نہ وہ اس کی طاقت رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(26,212,'26:212','وہ (آسمانی باتوں) کے سننے (کے مقامات) سے الگ کر دیئے گئے ہیں');
INSERT INTO "jalandhari" VALUES(26,213,'26:213','تو خدا کے سوا کسی اور معبود کو مت پکارنا، ورنہ تم کو عذاب دیا جائے گا');
INSERT INTO "jalandhari" VALUES(26,214,'26:214','اور اپنے قریب کے رشتہ داروں کو ڈر سنا دو');
INSERT INTO "jalandhari" VALUES(26,215,'26:215','اور جو مومن تمہارے پیرو ہوگئے ہیں ان سے متواضع پیش آؤ');
INSERT INTO "jalandhari" VALUES(26,216,'26:216','پھر اگر لوگ تمہاری نافرمانی کریں تو کہہ دو کہ میں تمہارے اعمال سے بےتعلق ہوں');
INSERT INTO "jalandhari" VALUES(26,217,'26:217','اور (خدائے) غالب اور مہربان پر بھروسا رکھو');
INSERT INTO "jalandhari" VALUES(26,218,'26:218','جو تم کو جب تم (تہجد) کے وقت اُٹھتے ہو دیکھتا ہے');
INSERT INTO "jalandhari" VALUES(26,219,'26:219','اور نمازیوں میں تمہارے پھرنے کو بھی');
INSERT INTO "jalandhari" VALUES(26,220,'26:220','بےشک وہ سننے اور جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(26,221,'26:221','(اچھا) میں تمیں بتاؤں کہ شیطان کس پر اُترتے ہیں');
INSERT INTO "jalandhari" VALUES(26,222,'26:222','ہر جھوٹے گنہگار پر اُترتے ہیں');
INSERT INTO "jalandhari" VALUES(26,223,'26:223','جو سنی ہوئی بات (اس کے کام میں) لا ڈالتے ہیں اور وہ اکثر جھوٹے ہیں');
INSERT INTO "jalandhari" VALUES(26,224,'26:224','اور شاعروں کی پیروی گمراہ لوگ کیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(26,225,'26:225','کیا تم نے نہیں دیکھا کہ وہ ہر وادی میں سر مارتے پھرتے ہیں');
INSERT INTO "jalandhari" VALUES(26,226,'26:226','اور کہتے وہ ہیں جو کرتے نہیں');
INSERT INTO "jalandhari" VALUES(26,227,'26:227','مگر جو لوگ ایمان لائے اور نیک کام کئے اور خدا کو بہت یاد کرتے رہے اور اپنے اوپر ظلم ہونے کے بعد انتقام لیا اور ظالم عنقریب جان لیں گے کہ کون سی جگہ لوٹ کر جاتے ہیں');
INSERT INTO "jalandhari" VALUES(27,1,'27:1','ٰطٰسٓ۔ یہ قرآن اور روشن کتاب کی آیتیں ہیں');
INSERT INTO "jalandhari" VALUES(27,2,'27:2','مومنوں کے لئے ہدایت اور بشارت');
INSERT INTO "jalandhari" VALUES(27,3,'27:3','وہ جو نماز پڑھتے اور زکوٰة دیتے اور آخرت کا یقین رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(27,4,'27:4','جو لوگ آخرت پر ایمان نہیں رکھتے ہیں ہم نے ان کے اعمال ان کے لئے آرستہ کردیئے ہیں تو وہ سرگرداں ہو رہے ہیں');
INSERT INTO "jalandhari" VALUES(27,5,'27:5','یہی لوگ ہیں جن کے لئے بڑا عذاب ہے اور وہ آخرت میں بھی وہ بہت نقصان اٹھانے والے ہیں');
INSERT INTO "jalandhari" VALUES(27,6,'27:6','اور تم کو قرآن (خدائے) حکیم وعلیم کی طرف سے عطا کیا جاتا ہے');
INSERT INTO "jalandhari" VALUES(27,7,'27:7','جب موسیٰ نے اپنے گھر والوں سے کہا کہ میں نے آگ دیکھی ہے، میں وہاں سے (رستے) کا پتہ لاتا ہوں یا سلگتا ہوا انگارہ تمہارے پاس لاتا ہوں تاکہ تم تاپو');
INSERT INTO "jalandhari" VALUES(27,8,'27:8','جب موسیٰ اس کے پاس آئے تو ندا آئی کہ وہ جو آگ میں (تجلّی دکھاتا) ہے بابرکت ہے۔ اور جو آگ کے اردگرد ہیں اور خدا جو تمام عالم کا پروردگار ہے پاک ہے');
INSERT INTO "jalandhari" VALUES(27,9,'27:9','اے موسیٰ میں ہی خدائے غالب ودانا ہوں');
INSERT INTO "jalandhari" VALUES(27,10,'27:10','اور اپنی لاٹھی ڈال دو۔ جب اُسے دیکھا تو (اس طرح) ہل رہی تھی گویا سانپ ہے تو پیٹھ پھیر کر بھاگے اور پیچھے مڑ کر نہ دیکھا (حکم ہوا کہ) موسیٰ ڈرو مت۔ ہمارے پاس پیغمبر ڈرا نہیں کرتے');
INSERT INTO "jalandhari" VALUES(27,11,'27:11','ہاں جس نے ظلم کیا پھر برائی کے بعد اسے نیکی سے بدل دیا تو میں بخشنے والا مہربان ہوں');
INSERT INTO "jalandhari" VALUES(27,12,'27:12','اور اپنا ہاتھ اپنے گریبان میں ڈالو سفید نکلے گا۔ (ان دو معجزوں کے ساتھ جو) نو معجزوں میں (داخل ہیں) فرعون اور اس کی قوم کے پاس جاؤ کہ وہ بےحکم لوگ ہیں');
INSERT INTO "jalandhari" VALUES(27,13,'27:13','جب ان کے پاس ہماری روشن نشانیاں پہنچیں، کہنے لگے یہ صریح جادو ہے');
INSERT INTO "jalandhari" VALUES(27,14,'27:14','اور بےانصافی اور غرور سے ان سے انکار کیا لیکن ان کے دل ان کو مان چکے تھے۔ سو دیکھ لو فساد کرنے والوں کا انجام کیسا ہوا');
INSERT INTO "jalandhari" VALUES(27,15,'27:15','اور ہم نے داؤد اور سلیمان کو علم بخشا اور انہوں نے کہا کہ خدا کا شکر ہے جس نے ہمیں بہت سے مومن بندوں پر فضلیت دی');
INSERT INTO "jalandhari" VALUES(27,16,'27:16','اور سلیمان اور داؤد کے قائم مقام ہوئے۔ اور کہنے لگے کہ لوگو! ہمیں (خدا کی طرف سے) جانوروں کی بولی سکھائی گئی ہے اور ہر چیز عنایت فرمائی گئی ہے۔ بےشک یہ (اُس کا) صریح فضل ہے');
INSERT INTO "jalandhari" VALUES(27,17,'27:17','اور سلیمان کے لئے جنوں اور انسانوں اور پرندوں کے لشکر جمع کئے گئے اور قسم وار کئے جاتے تھے');
INSERT INTO "jalandhari" VALUES(27,18,'27:18','یہاں تک کہ جب چیونٹیوں کے میدان میں پہنچے تو ایک چیونٹی نے کہا کہ چیونٹیوں اپنے اپنے بلوں میں داخل ہو جاؤ ایسا نہ ہو کہ سلیمان اور اس کے لشکر تم کو کچل ڈالیں اور ان کو خبر بھی نہ ہو');
INSERT INTO "jalandhari" VALUES(27,19,'27:19','تو وہ اس کی بات سن کر ہنس پڑے اور کہنے لگے کہ اے پروردگار! مجھے توفیق عطا فرما کہ جو احسان تونے مجھ پر اور میرے ماں باپ پر کئے ہیں ان کا شکر کروں اور ایسے نیک کام کروں کہ تو ان سے خوش ہوجائے اور مجھے اپنی رحمت سے اپنے نیک بندوں میں داخل فرما');
INSERT INTO "jalandhari" VALUES(27,20,'27:20','انہوں نے جانوروں کا جائزہ لیا تو کہنے لگے کیا سبب ہے کہ ہُدہُد نظر نہیں آتا۔ کیا کہیں غائب ہوگیا ہے؟');
INSERT INTO "jalandhari" VALUES(27,21,'27:21','میں اسے سخت سزا دوں گا یا ذبح کر ڈالوں گا یا میرے سامنے (اپنی بےقصوری کی) دلیل صریح پیش کرے');
INSERT INTO "jalandhari" VALUES(27,22,'27:22','ابھی تھوڑی ہی دیر ہوئی تھی کہ ہُدہُد آ موجود ہوا اور کہنے لگا کہ مجھے ایک ایسی چیز معلوم ہوئی ہے جس کی آپ کو خبر نہیں اور میں آپ کے پاس (شہر) سبا سے ایک سچی خبر لے کر آیا ہوں');
INSERT INTO "jalandhari" VALUES(27,23,'27:23','میں نے ایک عورت دیکھی کہ ان لوگوں پر بادشاہت کرتی ہے اور ہر چیز اسے میسر ہے اور اس کا ایک بڑا تخت ہے');
INSERT INTO "jalandhari" VALUES(27,24,'27:24','میں نے دیکھا کہ وہ اور اس کی قوم خدا کو چھوڑ کر آفتاب کو سجدہ کرتے ہیں اور شیطان نے ان کے اعمال انہیں آراستہ کر دکھائے ہیں اور ان کو رستے سے روک رکھا ہے پس وہ رستے پر نہیں آئے');
INSERT INTO "jalandhari" VALUES(27,25,'27:25','(اور نہیں سمجھتے) کہ خدا کو آسمانوں اور زمین میں چھپی چیزوں کو ظاہر کردیتا اور تمہارے پوشیدہ اور ظاہر اعمال کو جانتا ہے کیوں سجدہ نہ کریں');
INSERT INTO "jalandhari" VALUES(27,26,'27:26','خدا کے سوا کوئی عبادت کے لائق نہیں وہی عرش عظیم کا مالک ہے');
INSERT INTO "jalandhari" VALUES(27,27,'27:27','سلیمان نے کہا (اچھا) ہم دیکھیں گے، تونے سچ کہا ہے یا تو جھوٹا ہے');
INSERT INTO "jalandhari" VALUES(27,28,'27:28','یہ میرا خط لے جا اور اسے ان کی طرف ڈال دے پھر ان کے پاس سے پھر آ اور دیکھ کہ وہ کیا جواب دیتے ہیں');
INSERT INTO "jalandhari" VALUES(27,29,'27:29','ملکہ نے کہا کہ دربار والو! میری طرف ایک نامہ گرامی ڈالا گیا ہے');
INSERT INTO "jalandhari" VALUES(27,30,'27:30','وہ سلیمان کی طرف سے ہے اور مضمون یہ ہے کہ شروع خدا کا نام لے کر جو بڑا مہربان نہایت رحم والا ہے');
INSERT INTO "jalandhari" VALUES(27,31,'27:31','(بعد اس کے یہ) کہ مجھے سرکشی نہ کرو اور مطیع ومنقاد ہو کر میرے پاس چلے آؤ');
INSERT INTO "jalandhari" VALUES(27,32,'27:32','(خط سنا کر) وہ کہنے لگی کہ اے اہل دربار میرے اس معاملے میں مجھے مشورہ دو، جب تک تم حاضر نہ ہو (اور صلاح نہ دو) میں کسی کام کو فیصل کرنے والی نہیں');
INSERT INTO "jalandhari" VALUES(27,33,'27:33','وہ بولے کہ ہم بڑے زورآور اور سخت جنگجو ہیں اور حکم آپ کے اختیار ہے تو جو حکم دیجیئے گا (اس کے مآل پر) نظر کرلیجیئے گا');
INSERT INTO "jalandhari" VALUES(27,34,'27:34','اس نے کہا کہ بادشاہ جب کسی شہر میں داخل ہوتے ہیں تو اس کو تباہ کر دیتے ہیں اور وہاں کے عزت والوں کو ذلیل کر دیا کرتے ہیں اور اسی طرح یہ بھی کریں گے');
INSERT INTO "jalandhari" VALUES(27,35,'27:35','اور میں ان کی طرف کچھ تحفہ بھیجتی ہوں اور دیکھتی ہوں کہ قاصد کیا جواب لاتے ہیں');
INSERT INTO "jalandhari" VALUES(27,36,'27:36','جب (قاصد) سلیمان کے پاس پہنچا تو سلیمان نے کہا کیا تم مجھے مال سے مدد دینا چاہتے ہو، جو کچھ خدا نے مجھے عطا فرمایا ہے وہ اس سے بہتر ہے جو تمہیں دیا ہے حقیقت یہ ہے کہ تم ہی اپنے تحفے سے خوش ہوتے ہوگے');
INSERT INTO "jalandhari" VALUES(27,37,'27:37','ان کے پاس واپس جاؤ ہم ان پر ایسے لشکر سے حملہ کریں گے جس کے مقابلے کی ان میں طاقت نہ ہوگی اور ان کو وہاں سے بےعزت کرکے نکال دیں گے اور وہ ذلیل ہوں گے');
INSERT INTO "jalandhari" VALUES(27,38,'27:38','سلیمان نے کہا کہ اے دربار والو! کوئی تم میں ایسا ہے کہ قبل اس کے کہ وہ لوگ فرمانبردار ہو کر ہمارے پاس آئیں ملکہ کا تخت میرے پاس لے آئے');
INSERT INTO "jalandhari" VALUES(27,39,'27:39','جنات میں سے ایک قوی ہیکل جن نے کہا کہ قبل اس کے کہ آپ اپنی جگہ سے اٹھیں میں اس کو آپ کے پاس لاحاضر کرتا ہوں اور میں اس (کے اٹھانے کی) طاقت رکھتا ہوں (اور) امانت دار ہوں');
INSERT INTO "jalandhari" VALUES(27,40,'27:40','ایک شخص جس کو کتاب الہیٰ کا علم تھا کہنے لگا کہ میں آپ کی آنکھ کے جھپکنے سے پہلے پہلے اسے آپ کے پاس حاضر کئے دیتا ہوں۔ جب سلیمان نے تخت کو اپنے پاس رکھا ہوا دیکھا تو کہا کہ یہ میرے پروردگار کا فضل ہے تاکہ مجھے آزمائے کہ میں شکر کرتا ہوں یا کفران نعمت کرتا ہوں اور جو شکر کرتا ہے تو اپنے ہی فائدے کے لئے شکر کرتا ہے اور جو ناشکری کرتا ہے تو میرا پروردگار بےپروا (اور) کرم کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(27,41,'27:41','سلیمان نے کہا کہ ملکہ کے (امتحان عقل کے) لئے اس کے تخت کی صورت بدل دو۔ دیکھیں کہ وہ سوجھ رکھتی ہے یا ان لوگوں میں ہے جو سوجھ نہیں رکھتے');
INSERT INTO "jalandhari" VALUES(27,42,'27:42','جب وہ آ پہنچی تو پوچھا گیا کہ کیا آپ کا تخت بھی اسی طرح کا ہے؟ اس نے کہا کہ یہ تو گویا ہو بہو وہی ہے اور ہم کو اس سے پہلے ہی (سلیمان کی عظمت شان) کا علم ہوگیا تھا اور ہم فرمانبردار ہیں');
INSERT INTO "jalandhari" VALUES(27,43,'27:43','اور وہ جو خدا کے سوا (اور کی) پرستش کرتی تھی، سلیمان نے اس کو اس سے منع کیا (اس سے پہلے تو) وہ کافروں میں سے تھی');
INSERT INTO "jalandhari" VALUES(27,44,'27:44','(پھر) اس سے کہا گیا کہ محل میں چلیے، جب اس نے اس (کے فرش) کو دیکھا تو اسے پانی کا حوض سمجھا اور (کپڑا اٹھا کر) اپنی پنڈلیاں کھول دیں۔ سلیمان نے کہا یہ ایسا محل ہے جس میں (نیچے بھی) شیشے جڑے ہوئے ہیں۔ وہ بول اٹھی کہ پروردگار میں اپنے آپ پر ظلم کرتی رہی تھی اور (اب) میں سلیمان کے ہاتھ پر خدائے رب العالمین پر ایمان لاتی ہوں');
INSERT INTO "jalandhari" VALUES(27,45,'27:45','اور ہم نے ثمود کی طرف اس کے بھائی صالح کو بھیجا کہ خدا کی عبادت کرو تو وہ دو فریق ہو کر آپس میں جھگڑنے لگے');
INSERT INTO "jalandhari" VALUES(27,46,'27:46','صالح نے کہا کہ بھائیو تم بھلائی سے پہلے برائی کے لئے کیوں جلدی کرتے ہو (اور) خدا سے بخشش کیوں نہیں مانگتے تاکہ تم پر رحم کیا جائے');
INSERT INTO "jalandhari" VALUES(27,47,'27:47','وہ کہنے لگے کہ تم اور تمہارے ساتھی ہمارے لئے شگون بد ہے۔ صالح نے کہا کہ تمہاری بدشگونی خدا کی طرف سے ہے بلکہ تم ایسے لوگ ہو جن کی آزمائش کی جاتی ہے');
INSERT INTO "jalandhari" VALUES(27,48,'27:48','اور شہر میں نو شخص تھے جو ملک میں فساد کیا کرتے تھے اور اصلاح سے کام نہیں لیتے تھے');
INSERT INTO "jalandhari" VALUES(27,49,'27:49','کہنے لگے کہ خدا کی قسم کھاؤ کہ ہم رات کو اس پر اور اس کے گھر والوں پر شب خون ماریں گے پھر اس کے وارث سے کہہ دیں گے کہ ہم تو صالح کے گھر والوں کے موقع ہلاکت پر گئے ہی نہیں اور ہم سچ کہتے ہیں');
INSERT INTO "jalandhari" VALUES(27,50,'27:50','اور وہ ایک چال چلے اور ان کو کچھ خبر نہ ہوئی');
INSERT INTO "jalandhari" VALUES(27,51,'27:51','تو دیکھ لو ان کی چال کا کیسا انجام ہوا۔ ہم نے ان کو اور ان کی قوم سب کو ہلاک کر ڈالا');
INSERT INTO "jalandhari" VALUES(27,52,'27:52','اب یہ ان کے گھر ان کے ظلم کے سبب خالی پڑے ہیں۔ جو لوگ دانش رکھتے ہیں، ان کے لئے اس میں نشانی ہے');
INSERT INTO "jalandhari" VALUES(27,53,'27:53','اور جو لوگ ایمان لائے اور ڈرتے تھے ان کو ہم نے نجات دی');
INSERT INTO "jalandhari" VALUES(27,54,'27:54','اور لوط کو (یاد کرو) جب انہوں نے اپنی قوم سے کہا کہ تم بےحیائی (کے کام) کیوں کرتے ہو اور تم دیکھتے ہو');
INSERT INTO "jalandhari" VALUES(27,55,'27:55','کیا تم عورتوں کو چھوڑ کر (لذت حاصل کرنے) کے لئے مردوں کی طرف مائل ہوتے ہو۔ حقیقت یہ ہے کہ تم احمق لوگ ہو');
INSERT INTO "jalandhari" VALUES(27,56,'27:56','تو ان کی قوم کے لوگ (بولے تو) یہ بولے اور اس کے سوا ان کا کچھ جواب نہ تھا کہ لوط کے گھر والوں کو اپنے شہر سے نکال دو۔ یہ لوگ پاک رہنا چاہتے ہیں');
INSERT INTO "jalandhari" VALUES(27,57,'27:57','تو ہم نے ان کو اور ان کے گھر والوں کو نجات دی۔ مگر ان کی بیوی کہ اس کی نسبت ہم نے مقرر کر رکھا ہے (کہ وہ پیچھے رہنے والوں میں ہوگی)');
INSERT INTO "jalandhari" VALUES(27,58,'27:58','اور ہم نے ان پر مینھہ برسایا سو (جو) مینھہ ان لوگوں پر برسا جن کو متنبہ کردیا گیا تھا، برا تھا');
INSERT INTO "jalandhari" VALUES(27,59,'27:59','کہہ دو کہ سب تعریف خدا ہی کو سزاوار ہے اور اس کے بندوں پر سلام ہے جن کو اس نے منتخب فرمایا۔ بھلا خدا بہتر ہے یا وہ جن کو یہ (اس کا شریک) ٹھہراتے ہیں');
INSERT INTO "jalandhari" VALUES(27,60,'27:60','بھلا کس نے آسمانوں اور زمین کو پیدا کیا اور (کس نے) تمہارے لئے آسمان سے پانی برسایا۔ (ہم نے) پھر ہم ہی نے اس سے سرسبز باغ اُگائے۔ تمہارا کام تو نہ تھا کہ تم اُن کے درختوں کو اگاتے۔ تو کیا خدا کے ساتھ کوئی اور بھی معبود ہے؟ (ہرگز نہیں) بلکہ یہ لوگ رستے سے الگ ہو رہے ہیں');
INSERT INTO "jalandhari" VALUES(27,61,'27:61','بھلا کس نے زمین کو قرار گاہ بنایا اور اس کے بیچ نہریں بنائیں اور اس کے لئے پہاڑ بنائے اور (کس نے) دو دریاؤں کے بیچ اوٹ بنائی (یہ سب کچھ خدا نے بنایا) تو کیا خدا کے ساتھ کوئی اور معبود بھی ہے؟ (ہرگز نہیں) بلکہ ان میں اکثر دانش نہیں رکھتے');
INSERT INTO "jalandhari" VALUES(27,62,'27:62','بھلا کون بیقرار کی التجا قبول کرتا ہے۔ جب وہ اس سے دعا کرتا ہے اور (کون اس کی) تکلیف کو دور کرتا ہے اور (کون) تم کو زمین میں (اگلوں کا) جانشین بناتا ہے (یہ سب کچھ خدا کرتا ہے) تو کیا خدا کے ساتھ کوئی اور معبود بھی ہے (ہرگز نہیں مگر) تم بہت کم غور کرتے ہو');
INSERT INTO "jalandhari" VALUES(27,63,'27:63','بھلا کون تم کو جنگل اور دریا کے اندھیروں میں رستہ بناتا ہے اور (کون) ہواؤں کو اپنی رحمت کے آگے خوشخبری بناکر بھیجتا ہے (یہ سب کچھ خدا کرتا ہے) تو کیا خدا کے ساتھ کوئی اور معبود بھی ہے؟ (ہرگز نہیں)۔ یہ لوگ جو شرک کرتے ہیں خدا (کی شان) اس سے بلند ہے');
INSERT INTO "jalandhari" VALUES(27,64,'27:64','بھلا کون خلقت کو پہلی بار پیدا کرتا۔ پھر اس کو بار بار پیدا کرتا رہتا ہے اور (کون) تم کو آسمان اور زمین سے رزق دیتا ہے (یہ سب کچھ خدا کرتا ہے) تو کیا خدا کے ساتھ کوئی اور معبود بھی ہے (ہرگز نہیں) کہہ دو کہ (مشرکو) اگر تم سچے ہو تو دلیل پیش کرو');
INSERT INTO "jalandhari" VALUES(27,65,'27:65','کہہ دو کہ جو لوگ آسمانوں اور زمین میں ہیں خدا کے سوا غیب کی باتیں نہیں جانتے۔ اور نہ یہ جانتے ہیں کہ کب (زندہ کرکے) اٹھائے جائیں گے');
INSERT INTO "jalandhari" VALUES(27,66,'27:66','بلکہ آخرت (کے بارے) میں ان کا علم منتہی ہوچکا ہے بلکہ وہ اس سے شک میں ہیں۔ بلکہ اس سے اندھے ہو رہے ہیں');
INSERT INTO "jalandhari" VALUES(27,67,'27:67','اور جو لوگ کافر ہیں کہتے ہیں جب ہم اور ہمارے باپ دادا مٹی ہو جائیں گے تو کیا ہم پھر (قبروں سے) نکالے جائیں گے');
INSERT INTO "jalandhari" VALUES(27,68,'27:68','یہ وعدہ ہم سے اور ہمارے باپ دادا سے پہلے سے ہوتا چلا آیا ہے (کہاں کا اُٹھنا اور کیسی قیامت) یہ تو صرف پہلے لوگوں کی کہانیاں ہیں');
INSERT INTO "jalandhari" VALUES(27,69,'27:69','کہہ دو کہ ملک میں چلو پھرو پھر دیکھو کہ گنہگاروں کا انجام کیا ہوا ہے');
INSERT INTO "jalandhari" VALUES(27,70,'27:70','اور ان (کے حال) پر غم نہ کرنا اور نہ اُن چالوں سے جو یہ کر رہے ہیں تنگ دل ہونا');
INSERT INTO "jalandhari" VALUES(27,71,'27:71','اور کہتے ہیں کہ اگر تم سچے ہو تو یہ وعدہ کب پورا ہوگا؟');
INSERT INTO "jalandhari" VALUES(27,72,'27:72','کہہ دو کہ جس (عذاب) کے لئے تم جلدی کر رہے ہو شاید اس میں سے کچھ تمہارے نزدیک آپہنچا ہو');
INSERT INTO "jalandhari" VALUES(27,73,'27:73','اور تمہارا پروردگار تو لوگوں پر فضل کرنے والا ہے لیکن ان میں سے اکثر شکر نہیں کرتے');
INSERT INTO "jalandhari" VALUES(27,74,'27:74','اور جو باتیں ان کے سینوں میں پوشیدہ ہوتی ہیں اور جو کام وہ ظاہر کرتے ہیں تمہارا پروردگار ان (سب) کو جانتا ہے');
INSERT INTO "jalandhari" VALUES(27,75,'27:75','اور آسمانوں اور زمین میں کوئی پوشیدہ چیز نہیں ہے مگر (وہ) کتاب روشن میں (لکھی ہوئی) ہے');
INSERT INTO "jalandhari" VALUES(27,76,'27:76','بےشک یہ قرآن بنی اسرائیل کے سامنے اکثر باتیں جن میں وہ اختلاف کرتے ہیں، بیان کر دیتا ہے');
INSERT INTO "jalandhari" VALUES(27,77,'27:77','اور بےشک یہ مومنوں کے لئے ہدایات اور رحمت ہے');
INSERT INTO "jalandhari" VALUES(27,78,'27:78','تمہارا پروردگار (قیامت کے روز) اُن میں اپنے حکم سے فیصلہ کر دے گا اور وہ غالب (اور) علم والا ہے');
INSERT INTO "jalandhari" VALUES(27,79,'27:79','تو خدا پر بھروسہ رکھو تم تو حق صریح پر ہو');
INSERT INTO "jalandhari" VALUES(27,80,'27:80','کچھ شک نہیں کہ تم مردوں کو (بات) نہیں سنا سکتے اور نہ بہروں کو جب کہ وہ پیٹھ پھیر کر پھر جائیں آواز سنا سکتے ہو');
INSERT INTO "jalandhari" VALUES(27,81,'27:81','اور نہ اندھوں کو گمراہی سے (نکال کر) رستہ دیکھا سکتے ہو۔ تم ان ہی کو سنا سکتے ہو جو ہماری آیتوں پر ایمان لاتے ہیں اور وہ فرمانبردار ہو جاتے ہیں');
INSERT INTO "jalandhari" VALUES(27,82,'27:82','اور جب اُن کے بارے میں (عذاب) کا وعدہ پورا ہوگا تو ہم اُن کے لئے زمین میں سے ایک جانور نکالیں گے جو ان سے بیان کر دے گا۔ اس لئے کہ لوگ ہماری آیتوں پر ایمان نہیں لاتے تھے');
INSERT INTO "jalandhari" VALUES(27,83,'27:83','اور جس روز ہم ہر اُمت میں سے اس گروہ کو جمع کریں گے جو ہماری آیتوں کی تکذیب کرتے تھے تو اُن کی جماعت بندی کی جائے گی');
INSERT INTO "jalandhari" VALUES(27,84,'27:84','یہاں تک کہ جب (سب) آجائیں گے تو (خدا) فرمائے گا کہ کیا تم نے میری آیتوں کو جھٹلا دیا تھا اور تم نے (اپنے) علم سے ان پر احاطہ تو کیا ہی نہ تھا۔ بھلا تم کیا کرتے تھے');
INSERT INTO "jalandhari" VALUES(27,85,'27:85','اور اُن کے ظلم کے سبب اُن کے حق میں وعدہ (عذاب) پورا ہوکر رہے گا تو وہ بول بھی نہ سکیں گے');
INSERT INTO "jalandhari" VALUES(27,86,'27:86','کیا اُنہوں نے نہیں دیکھا کہ ہم نے رات کو (اس لئے) بنایا ہے کہ اس میں آرام کریں اور دن کو روشن (بنایا ہے کہ اس میں کام کریں) بےشک اس میں مومن لوگوں کے لئے نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(27,87,'27:87','اور جس روز صور پھونکا جائے گا تو جو لوگ آسمانوں اور زمین میں ہیں سب گھبرا اُٹھیں گے مگر وہ جسے خدا چاہے اور سب اس کے پاس عاجز ہو کر چلے آئیں گے');
INSERT INTO "jalandhari" VALUES(27,88,'27:88','اور تم پہاڑوں کو دیکھتے ہو تو خیال کرتے ہو کہ (اپنی جگہ پر) کھڑے ہیں مگر وہ (اس روز) اس طرح اُڑے پھریں گے جیسے بادل۔ (یہ) خدا کی کاریگری ہے جس نے ہر چیز کو مضبوط بنایا۔ بےشک وہ تمہارے سب افعال سے باخبر ہے');
INSERT INTO "jalandhari" VALUES(27,89,'27:89','جو شخص نیکی لےکر آئے گا تو اس کے لئے اس سے بہتر (بدلہ تیار) ہے اور ایسے لوگ (اُس روز) گھبراہٹ سے بےخوف ہوں گے');
INSERT INTO "jalandhari" VALUES(27,90,'27:90','اور جو برائی لے کر آئے گا تو ایسے لوگ اوندھے منہ دوزخ میں ڈال دیئے جائیں گے۔ تم کو تو اُن ہی اعمال کا بدلہ ملے گا جو تم کرتے رہے ہو');
INSERT INTO "jalandhari" VALUES(27,91,'27:91','(کہہ دو) کہ مجھ کو یہی ارشاد ہوا ہے کہ اس شہر (مکہ) کے مالک کی عبادت کروں جس نے اس کو محترم (اور مقام ادب) بنایا ہے اور سب چیز اُسی کی ہے اور یہ بھی حکم ہوا ہے کہ اس کا حکم بردار رہوں');
INSERT INTO "jalandhari" VALUES(27,92,'27:92','اور یہ بھی کہ قرآن پڑھا کروں۔ تو جو شخص راہ راست اختیار کرتا ہے تو اپنے ہی فائدے کے لئے اختیار کرتا ہے۔ اور جو گمراہ رہتا ہے تو کہہ دو کہ میں تو صرف نصیحت کرنے والا ہوں');
INSERT INTO "jalandhari" VALUES(27,93,'27:93','اور کہو کہ خدا کا شکر ہے۔ وہ تم کو عنقریب اپنی نشانیاں دکھائے گا تو تم اُن کو پہچان لو گے۔ اور جو کام تم کرتے ہو تمہارا پروردگار اُن سے بےخبر نہیں ہے');
INSERT INTO "jalandhari" VALUES(28,1,'28:1','طٰسٓمٓ');
INSERT INTO "jalandhari" VALUES(28,2,'28:2','یہ کتاب روشن کی آیتیں ہیں');
INSERT INTO "jalandhari" VALUES(28,3,'28:3','(اے محمدصلى الله عليه وسلم) ہم تمہیں موسٰی اور فرعون کے کچھ حالات مومن لوگوں کو سنانے کے لئے صحیح صحیح سناتے ہیں');
INSERT INTO "jalandhari" VALUES(28,4,'28:4','کہ فرعون نے ملک میں سر اُٹھا رکھا تھا اور وہاں کے باشندوں کو گروہ گروہ بنا رکھا تھا اُن میں سے ایک گروہ کو (یہاں تک) کمزور کر دیا تھا کہ اُن کے بیٹوں کو ذبح کر ڈالتا اور اُن کی لڑکیوں کو زندہ رہنے دیتا۔ بیشک وہ مفسدوں میں تھا');
INSERT INTO "jalandhari" VALUES(28,5,'28:5','اور ہم چاہتے تھے کہ جو لوگ ملک میں کمزور کر دیئے گئے ہیں اُن پر احسان کریں اور اُن کو پیشوا بنائیں اور انہیں (ملک کا) وارث کریں');
INSERT INTO "jalandhari" VALUES(28,6,'28:6','اور ملک میں ان کو قدرت دیں اور فرعون اور ہامان اور اُن کے لشکر کو وہ چیزیں دکھا دیں جس سے وہ ڈرتے تھے');
INSERT INTO "jalandhari" VALUES(28,7,'28:7','اور ہم نے موسٰی کی ماں کی طرف وحی بھیجی کہ اس کو دودھ پلاؤ جب تم کو اس کے بارے میں کچھ خوف پیدا ہو تو اسے دریا میں ڈال دینا اور نہ تو خوف کرنا اور نہ رنج کرنا۔ ہم اس کو تمہارے پاس واپس پہنچا دیں گے اور (پھر) اُسے پیغمبر بنا دیں گے');
INSERT INTO "jalandhari" VALUES(28,8,'28:8','تو فرعون کے لوگوں نے اس کو اُٹھا لیا اس لئے کہ (نتیجہ یہ ہونا تھا کہ) وہ اُن کا دشمن اور (ان کے لئے موجب) غم ہو۔ بیشک فرعون اور ہامان اور اُن کے لشکر چوک گئے');
INSERT INTO "jalandhari" VALUES(28,9,'28:9','اور فرعون کی بیوی نے کہا کہ (یہ) میری اور تمہاری (دونوں کی) آنکھوں کی ٹھنڈک ہے اس کو قتل نہ کرنا۔ شاید یہ ہمیں فائدہ پہنچائے یا ہم اُسے بیٹا بنالیں اور وہ انجام سے بےخبر تھے');
INSERT INTO "jalandhari" VALUES(28,10,'28:10','اور موسٰی کی ماں کا دل بے صبر ہو گیا اگر ہم اُن کے دل مضبوط نہ کر دیتے تو قریب تھا کہ وہ اس (قصّے) کو ظاہر کر دیں۔ غرض یہ تھی کہ وہ مومنوں میں رہیں');
INSERT INTO "jalandhari" VALUES(28,11,'28:11','اور اس کی بہن سے کہا کہ اس کے پیچھے پیچھے چلی جا تو وہ اُسے دور سے دیکھتی رہی اور ان (لوگوں) کو کچھ خبر نہ تھی');
INSERT INTO "jalandhari" VALUES(28,12,'28:12','اور ہم نے پہلے ہی سے اس پر (دائیوں) کے دودھ حرام کر دیئے تھے۔ تو موسٰی کی بہن نے کہا کہ میں تمہیں ایسے گھر والے بتاؤں کہ تمہارے لئے اس (بچے) کو پالیں اور اس کی خیر خواہی (سے پرورش) کریں');
INSERT INTO "jalandhari" VALUES(28,13,'28:13','تو ہم نے (اس طریق سے) اُن کو ان کی ماں کے پاس واپس پہنچا دیا تاکہ اُن کی آنکھیں ٹھنڈی ہوں اور وہ غم نہ کھائیں اور معلوم کریں کہ خدا کا وعدہ سچا ہے لیکن یہ اکثر نہیں جانتے');
INSERT INTO "jalandhari" VALUES(28,14,'28:14','اور جب موسٰی جوانی کو پہنچے اور بھرپور (جوان) ہو گئے تو ہم نے اُن کو حکمت اور علم عنایت کیا۔ اور ہم نیکو کاروں کو ایسا ہی بدلہ دیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(28,15,'28:15','اور وہ ایسے وقت شہر میں داخل ہوئے کہ وہاں کے باشندے بےخبر ہو رہے تھے تو دیکھا کہ وہاں دو شخص لڑ رہے تھے ایک تو موسٰی کی قوم کا ہے اور دوسرا اُن کے دشمنوں میں سے تو جو شخص اُن کی قوم میں سے تھا اس نے دوسرے شخص کے مقابلے میں جو موسٰی کے دشمنوں میں سے تھا مدد طلب کی تو اُنہوں نے اس کو مکا مارا اور اس کا کام تمام کر دیا کہنے لگے کہ یہ کام تو (اغوائے) شیطان سے ہوا بیشک وہ (انسان کا) دشمن اور صریح بہکانے والا ہے');
INSERT INTO "jalandhari" VALUES(28,16,'28:16','بولے کہ اے پروردگار میں نے اپنے آپ پر ظلم کیا تو مجھے بخش دے تو خدا نے اُن کو بخش دیا۔ بیشک وہ بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(28,17,'28:17','کہنے لگے کہ اے پروردگار تو نے جو مجھ پر مہربانی فرمائی ہے میں (آئندہ) کبھی گنہگاروں کا مددگار نہ بنوں');
INSERT INTO "jalandhari" VALUES(28,18,'28:18','الغرض صبح کے وقت شہر میں ڈرتے ڈرتے داخل ہوئے کہ دیکھیں (کیا ہوتا ہے) تو ناگہاں وہی شخص جس نے کل اُن سے مدد مانگی تھی پھر اُن کو پکار رہا ہے۔ موسٰی نے اس سے کہا کہ تُو تو صریح گمراہ ہے');
INSERT INTO "jalandhari" VALUES(28,19,'28:19','جب موسٰی نے ارادہ کیا کہ اس شخص کو جو ان دونوں کا دشمن تھا پکڑ لیں تو وہ (یعنی موسٰی کی قوم کا آدمی) بول اُٹھا کہ جس طرح تم نے کل ایک شخص کو مار ڈالا تھا اسی طرح چاہتے ہو کہ مجھے بھی مار ڈالو۔ تم تو یہی چاہتے ہو کہ ملک میں ظلم وستم کرتے پھرو اور یہ نہیں چاہتے ہو کہ نیکو کاروں میں ہو');
INSERT INTO "jalandhari" VALUES(28,20,'28:20','اور ایک شخص شہر کی پرلی طرف سے دوڑتا ہوا آیا (اور) بولا کہ موسٰی (شہر کے) رئیس تمہارے بارے میں صلاحیں کرتے ہیں کہ تم کو مار ڈالیں سو تم یہاں سے نکل جاؤ۔ میں تمہارا خیر خواہ ہوں');
INSERT INTO "jalandhari" VALUES(28,21,'28:21','موسٰی وہاں سے ڈرتے ڈرتے نکل کھڑے ہوئے کہ دیکھیں (کیا ہوتا ہے) اور دعا کرنے لگے کہ اے پروردگار مجھے ظالم لوگوں سے نجات دے۔');
INSERT INTO "jalandhari" VALUES(28,22,'28:22','اور جب مدین کی طرف رخ کیا تو کہنے لگے اُمید ہے کہ میرا پروردگار مجھے سیدھا رستہ بتائے');
INSERT INTO "jalandhari" VALUES(28,23,'28:23','اور جب مدین کے پانی (کے مقام) پر پہنچے تو دیکھا کہ وہاں لوگ جمع ہو رہے (اور اپنے چارپایوں کو) پانی پلا رہے ہیں اور ان کے ایک طرف دو عورتیں (اپنی بکریوں کو) روکے کھڑی ہیں۔ موسٰی نے (اُن سے) کہا تمہارا کیا کام ہے۔ وہ بولیں کہ جب تک چرواہے (اپنے چارپایوں کو) لے نہ جائیں ہم پانی نہیں پلا سکتے اور ہمارے والد بڑی عمر کے بوڑھے ہیں');
INSERT INTO "jalandhari" VALUES(28,24,'28:24','تو موسٰی نے اُن کے لئے (بکریوں کو) پانی پلا دیا پھر سائے کی طرف چلے گئے۔ اور کہنے لگے کہ پروردگار میں اس کا محتاج ہوں کہ تو مجھ پر اپنی نعمت نازل فرمائے');
INSERT INTO "jalandhari" VALUES(28,25,'28:25','(تھوڑی دیر کے بعد) ان میں سے ایک عورت جو شرماتی اور لجاتی چلی آتی تھی۔ موسٰی کے پاس آئی اور کہنے لگی کہ تم کو میرے والد بلاتے ہیں کہ تم نے جو ہمارے لئے پانی پلایا تھا اس کی تم کو اُجرت دیں۔ جب وہ اُن کے پاس آئے اور اُن سے اپنا ماجرا بیان کیا تو اُنہوں نے کہا کہ کچھ خوف نہ کرو۔ تم ظالم لوگوں سے بچ آئے ہو');
INSERT INTO "jalandhari" VALUES(28,26,'28:26','ایک لڑکی بولی کہ ابّا ان کو نوکر رکھ لیجئے کیونکہ بہتر نوکر جو آپ رکھیں وہ ہے (جو) توانا اور امانت دار (ہو)');
INSERT INTO "jalandhari" VALUES(28,27,'28:27','اُنہوں نے( موسٰی سے) کہا کہ میں چاہتا ہوں اپنی دو بیٹیوں میں سے ایک کو تم سے بیاہ دوں اس عہد پر کہ تم آٹھ برس میری خدمت کرو اور اگر دس سال پورے کر دو تو تمہاری طرف سے (احسان) ہے اور میں تم پر تکلیف ڈالنی نہیں چاہتا۔ مجھے انشاء الله نیک لوگوں میں پاؤ گے');
INSERT INTO "jalandhari" VALUES(28,28,'28:28','موسٰی نے کہا کہ مجھ میں اور آپ میں یہ (عہد پختہ ہوا) میں جونسی مدت (چاہوں) پوری کردوں پھر مجھ پر کوئی زیادتی نہ ہو۔ اور ہم جو معاہدہ کرتے ہیں خدا اس کا گواہ ہے');
INSERT INTO "jalandhari" VALUES(28,29,'28:29','جب موسٰی نے مدت پوری کردی اور اپنے گھر کے لوگوں کو لے کر چلے تو طور کی طرف سے آگ دکھائی دی تو اپنے گھر والوں سے کہنے لگے کہ تم یہاں ٹھیرو۔ مجھے آگ نظر آئی ہے شاید میں وہاں سے (رستے کا) کچھ پتہ لاؤں یا آگ کا انگارہ لے آؤں تاکہ تم تاپو');
INSERT INTO "jalandhari" VALUES(28,30,'28:30','جب اس کے پاس پہنچے تو میدان کے دائیں کنارے سے ایک مبارک جگہ میں ایک درخت میں سے آواز آئی کہ موسٰی میں تو خدائے رب العالمین ہوں');
INSERT INTO "jalandhari" VALUES(28,31,'28:31','اور یہ کہ اپنی لاٹھی ڈالدو۔ جب دیکھا کہ وہ حرکت کر رہی ہے گویا سانپ ہے، تو پیٹھ پھیر کر چل دیئے اور پیچھے پھر کر بھی نہ دیکھا۔ (ہم نے کہا کہ) موسٰی آگے آؤ اور ڈرومت تم امن پانے والوں میں ہو');
INSERT INTO "jalandhari" VALUES(28,32,'28:32','اپنا ہاتھ گریبان میں ڈالو تو بغیر کسی عیب کے سفید نکل آئے گا اور خوف دور ہونے (کی وجہ) سے اپنے بازو کو اپنی طرف سیکڑلو۔ یہ دو دلیلیں تمہارے پروردگار کی طرف سے ہیں (ان کے ساتھ) فرعون اور اس کے درباریوں کے پاس جاؤ کہ وہ نافرمان لوگ ہیں');
INSERT INTO "jalandhari" VALUES(28,33,'28:33','موسٰی نے کہا اے پروردگار اُن میں کا ایک شخص میرے ہاتھ سے قتل ہوچکا ہے سو مجھے خوف ہے کہ وہ (کہیں) مجھ کو مار نہ ڈالیں');
INSERT INTO "jalandhari" VALUES(28,34,'28:34','اور ہارون (جو) میرا بھائی (ہے) اس کی زبان مجھ سے زیادہ فصیح ہے تو اس کو میرے ساتھ مددگار بناکر بھیج کہ میری تصدیق کرے مجھے خوف ہے کہ وہ میری لوگ تکذیب کریں گے');
INSERT INTO "jalandhari" VALUES(28,35,'28:35','(خدا نے) فرمایا ہم تمہارے بھائی سے تمہارے بازو مضبوط کریں گے اور تم دونوں کو غلبہ دیں گے تو ہماری نشانیوں کے سبب وہ تم تک پہنچ نہ سکیں گے (اور) تم اور جنہوں نے تمہاری پیروی کی غالب رہو گے');
INSERT INTO "jalandhari" VALUES(28,36,'28:36','اور جب موسٰی اُن کے پاس ہماری کھلی نشانیاں لےکر آئے تو وہ کہنے لگے کہ یہ جادو ہے جو اُس نے بنا کھڑا کیا ہے اور یہ باتیں ہم نے اپنے اگلے باپ دادا میں تو (کبھی) سنی نہیں');
INSERT INTO "jalandhari" VALUES(28,37,'28:37','اور موسٰی نے کہا کہ میرا پروردگار اس شخص کو خوب جانتا ہے جو اس کی طرف سے حق لےکر آیا ہے اور جس کے لئے عاقبت کا گھر (یعنی بہشت) ہے۔ بیشک ظالم نجات نہیں پائیں گے');
INSERT INTO "jalandhari" VALUES(28,38,'28:38','اور فرعون نے کہا کہ اے اہلِ دربار میں تمہارا اپنے سوا کسی کو خدا نہیں جانتا تو ہامان میرے لئے گارے کو آگ لگوا (کر اینٹیں پکوا) دو پھر ایک (اُونچا) محل بنادو تاکہ میں موسٰی کے خدا کی طرف چڑھ جاؤں اور میں تو اُسے جھوٹا سمجھتا ہوں');
INSERT INTO "jalandhari" VALUES(28,39,'28:39','اور وہ اور اس کے لشکر ملک میں ناحق مغرور ہورہے تھے اور خیال کرتے تھے کہ وہ ہماری طرف لوٹ کر نہیں آئیں گے');
INSERT INTO "jalandhari" VALUES(28,40,'28:40','تو ہم نے اُن کو اور اُن کے لشکروں کو پکڑلیا اور دریا میں ڈال دیا۔ سو دیکھ لو ظالموں کا کیسا انجام ہوا');
INSERT INTO "jalandhari" VALUES(28,41,'28:41','اور ہم نے ان کو پیشوا بنایا تھا وہ (لوگوں) کو دوزخ کی طرف بلاتے تھے اور قیامت کے دن اُن کی مدد نہیں کی جائے گی');
INSERT INTO "jalandhari" VALUES(28,42,'28:42','اور اس دنیا سے ہم نے اُن کے پیچھے لعنت لگادی اور وہ قیامت کے روز بھی بدحالوں میں ہوں گے');
INSERT INTO "jalandhari" VALUES(28,43,'28:43','اور ہم نے پہلی اُمتوں کے ہلاک کرنے کے بعد موسٰی کو کتاب دی جو لوگوں کے لئے بصیرت اور ہدایت اور رحمت ہے تاکہ وہ نصیحت پکڑیں');
INSERT INTO "jalandhari" VALUES(28,44,'28:44','اور جب ہم نے موسٰی کی طرف حکم بھیجا تو تم (طور کی) غرب کی طرف نہیں تھے اور نہ اس واقعے کے دیکھنے والوں میں تھے');
INSERT INTO "jalandhari" VALUES(28,45,'28:45','لیکن ہم نے (موسٰی کے بعد) کئی اُمتوں کو پیدا کیا پھر ان پر مدت طویل گذر گئی اور نہ تم مدین والوں میں رہنے والے تھے کہ ان کو ہماری آیتیں پڑھ پڑھ کر سناتے تھے۔ ہاں ہم ہی تو پیغمبر بھیجنے والے تھے');
INSERT INTO "jalandhari" VALUES(28,46,'28:46','اور نہ تم اس وقت جب کہ ہم نے (موسٰی کو) آواز دی طور کے کنارے تھے بلکہ (تمہارا بھیجا جانا) تمہارے پروردگار کی رحمت ہے تاکہ تم اُن لوگوں کو جن کے پاس تم سے پہلے کوئی ہدایت کرنے والا نہیں آیا ہدایت کرو تاکہ وہ نصیحت پکڑیں');
INSERT INTO "jalandhari" VALUES(28,47,'28:47','اور (اے پیغمبر ہم نے تو کو اس لئے بھیجا ہے کہ) ایسا نہ ہو کہ اگر ان (اعمال) کے سبب جو اُن کے ہاتھ آگے بھیج چکے ہیں ان پر کوئی مصیبت واقع ہو تو یہ کہنے لگیں کہ اے پروردگار تو نے ہماری طرف کوئی پیغمبر کیوں نہ بھیجا کہ ہم تیری آیتوں کی پیروی کرنے اور ایمان لانے والوں میں ہوتے');
INSERT INTO "jalandhari" VALUES(28,48,'28:48','پھر جب اُن کے پاس ہماری طرف سے حق آپہنچا تو کہنے لگے کہ جیسی (نشانیاں) موسٰی کو ملی تھیں ویسی اس کو کیوں نہیں ملیں۔ کیا جو (نشانیاں) پہلے موسٰی کو دی گئی تھیں اُنہوں نے اُن سے کفر نہیں کیا۔ کہنے لگے کہ دونوں جادوگر ہیں ایک دوسرے کے موافق۔ اور بولے کہ ہم سب سے منکر ہیں');
INSERT INTO "jalandhari" VALUES(28,49,'28:49','کہہ دو کہ اگر سچے ہو تو تم خدا کے پاس سے کوئی کتاب لے آؤ جو ان دونوں (کتابوں) سے بڑھ کر ہدایت کرنے والی ہو۔ تاکہ میں بھی اسی کی پیروی کروں');
INSERT INTO "jalandhari" VALUES(28,50,'28:50','پھر اگر یہ تمہاری بات قبول نہ کریں تو جان لو کہ یہ صرف اپنی خواہشوں کی پیروی کرتے ہیں۔ اور اس سے زیادہ کون گمراہ ہوگا جو خدا کی ہدایت کو چھوڑ کر اپنی خواہش کے پیچھے چلے۔ بیشک خدا ظالم لوگوں کو ہدایت نہیں دیتا');
INSERT INTO "jalandhari" VALUES(28,51,'28:51','اور ہم پے درپے اُن لوگوں کے پاس (ہدایت کی) باتیں بھیجتے رہے ہیں تاکہ نصیحت پکڑیں');
INSERT INTO "jalandhari" VALUES(28,52,'28:52','جن لوگوں کو ہم نے اس سے پہلے کتاب دی تھی وہ اس پر ایمان لے آتے ہیں');
INSERT INTO "jalandhari" VALUES(28,53,'28:53','اور جب (قرآن) اُن کو پڑھ کر سنایا جاتا ہے تو کہتے ہیں کہ ہم اس پر ایمان لے آئے بیشک وہ ہمارے پروردگار کی طرف سے برحق ہے اور ہم تو اس سے پہلے کے حکمبردار ہیں');
INSERT INTO "jalandhari" VALUES(28,54,'28:54','ان لوگوں کو دگنا بدلہ دیا جائے گا کیونکہ صبر کرتے رہے ہیں اور بھلائی کے ساتھ برائی کو دور کرتے ہیں اور جو (مال) ہم نے اُن کو دیا ہے اس میں سے خرچ کرتے ہیں');
INSERT INTO "jalandhari" VALUES(28,55,'28:55','اور جب بیہودہ بات سنتے ہیں تو اس سے منہ پھیر لیتے ہیں اور کہتے ہیں کہ ہم کو ہمارے اعمال اور تم کو تمہارے اعمال۔ تم کو سلام۔ ہم جاہلوں کے خواستگار نہیں ہیں');
INSERT INTO "jalandhari" VALUES(28,56,'28:56','(اے محمدصلى الله عليه وسلم) تم جس کو دوست رکھتے ہو اُسے ہدایت نہیں کر سکتے بلکہ خدا ہی جس کو چاہتا ہے ہدایت کرتا ہے اور وہ ہدایت پانیوالوں کو خوب جانتا ہے');
INSERT INTO "jalandhari" VALUES(28,57,'28:57','اور کہتے ہیں کہ اگر ہم تمہارے ساتھ ہدایت کی پیروی کریں تو اپنے ملک سے اُچک لئے جائیں۔ کیا ہم نے اُن کو حرم میں جو امن کا مقام ہے جگہ نہیں دی۔ جہاں ہر قسم کے میوے پہنچائے جاتے ہیں (اور یہ) رزق ہماری طرف سے ہے لیکن ان میں سے اکثر نہیں جانتے');
INSERT INTO "jalandhari" VALUES(28,58,'28:58','اور ہم نے بہت سی بستیوں کو ہلاک کر ڈالا جو اپنی (فراخی) معیشت میں اترا رہے تھے۔ سو یہ اُن کے مکانات ہیں جو اُن کے بعد آباد ہی نہیں ہوئے مگر بہت کم۔ اور اُن کے پیچھے ہم ہی اُن کے وارث ہوئے');
INSERT INTO "jalandhari" VALUES(28,59,'28:59','اور تمہارا پروردگار بستیوں کو ہلاک نہیں کیا کرتا۔ جب تک اُن کے بڑے شہر میں پیغمبر نہ بھیج لے جو اُن کو ہماری آیتیں پڑھ پڑھ کر سنائے اور ہم بستیوں کو ہلاک نہیں کیا کرتے مگر اس حالت میں کہ وہاں کے باشندے ظالم ہوں');
INSERT INTO "jalandhari" VALUES(28,60,'28:60','اور جو چیز تم کو دی گئی ہے وہ دنیا کی زندگی کا فائدہ اور اس کی زینت ہے۔ اور جو خدا کے پاس ہے وہ بہتر اور باقی رہنے والی ہے۔ کیا تم سمجھتے نہیں؟');
INSERT INTO "jalandhari" VALUES(28,61,'28:61','بھلا جس شخص سے ہم نے نیک وعدہ کیا اور اُس نے اُسے حاصل کرلیا تو کیا وہ اس شخص کا سا ہے جس کو ہم نے دنیا کی زندگی کے فائدے سے بہرہ مند کیا پھر وہ قیامت کے روز ان لوگوں میں ہو جو (ہمارے روبرو) حاضر کئے جائیں گے');
INSERT INTO "jalandhari" VALUES(28,62,'28:62','اور جس روز خدا اُن کو پکارے گا اور کہے گا کہ میرے وہ شریک کہاں ہیں جن کا تمہیں دعویٰ تھا');
INSERT INTO "jalandhari" VALUES(28,63,'28:63','(تو) جن لوگوں پر (عذاب کا) حکم ثابت ہوچکا ہوگا وہ کہیں گے کہ ہمارے پروردگار یہ وہ لوگ ہیں جن کو ہم نے گمراہ کیا تھا۔ اور جس طرح ہم خود گمراہ ہوئے تھے اسی طرح اُن کو گمراہ کیا تھا (اب) ہم تیری طرف (متوجہ ہوکر) اُن سے بیزار ہوتے ہیں یہ ہمیں نہیں پوجتے تھے');
INSERT INTO "jalandhari" VALUES(28,64,'28:64','اور کہا جائے گا کہ اپنے شریکوں کو بلاؤ۔ تو وہ اُن کو پکاریں گے اور وہ اُن کو جواب نہ دے سکیں گے اور (جب) عذاب کو دیکھ لیں گے (تو تمنا کریں گے کہ) کاش وہ ہدایت یاب ہوتے');
INSERT INTO "jalandhari" VALUES(28,65,'28:65','اور جس روز خدا اُن کو پکارے گا اور کہے گا کہ تم نے پیغمبروں کو کیا جواب دیا');
INSERT INTO "jalandhari" VALUES(28,66,'28:66','تو وہ اس روز خبروں سے اندھے ہو جائیں گے، اور آپس میں کچھ بھی پوچھ نہ سکیں گے');
INSERT INTO "jalandhari" VALUES(28,67,'28:67','لیکن جس نے توبہ کی اور ایمان لایا اور عمل نیک کئے تو اُمید ہے کہ وہ نجات پانے والوں میں ہو');
INSERT INTO "jalandhari" VALUES(28,68,'28:68','اور تمہارا پروردگار جو چاہتا ہے پیدا کرتا ہے اور (جسے چاہتا ہے) برگزیدہ کرلیتا ہے۔ ان کو اس کا اختیار نہیں ہے۔ یہ جو شرک کرتے ہیں خدا اس سے پاک وبالاتر ہے');
INSERT INTO "jalandhari" VALUES(28,69,'28:69','اور ان کے سینے جو کچھ مخفی کرتے اور جو یہ ظاہر کرتے ہیں تمہارا پروردگار اس کو جانتا ہے');
INSERT INTO "jalandhari" VALUES(28,70,'28:70','اور وہی خدا ہے اس کے سوا کوئی معبود نہیں دنیا اور آخرت میں اُسی کی تعریف ہے اور اُسی کا حکم اور اسی کی طرف تم لوٹائے جاؤ گے');
INSERT INTO "jalandhari" VALUES(28,71,'28:71','کہو بھلا دیکھو تو اگر خدا تم پر ہمیشہ قیامت کے دن تک رات (کی تاریکی) کئے رہے تو خدا کے سوا کون معبود ہے ہے جو تم کو روشنی لا دے تو کیا تم سنتے نہیں؟');
INSERT INTO "jalandhari" VALUES(28,72,'28:72','کہو تو بھلا دیکھو تو اگر خدا تم پر ہمیشہ قیامت تک دن کئے رہے تو خدا کے سوا کون معبود ہے کہ تم کو رات لا دے جس میں تم آرام کرو۔ تو کیا تم دیکھتے نہیں؟');
INSERT INTO "jalandhari" VALUES(28,73,'28:73','اور اس نے اپنی رحمت سے تمہارے لئے رات کو اور دن کو بنایا تاکہ تم اس میں آرام کرو اور اس میں اس کا فضل تلاش کرو اور تاکہ شکر کرو');
INSERT INTO "jalandhari" VALUES(28,74,'28:74','اور جس دن وہ اُن کو پکارے گا اور کہے گا کہ میرے وہ شریک جن کا تمہیں دعویٰ تھا کہاں گئے؟');
INSERT INTO "jalandhari" VALUES(28,75,'28:75','اور ہم ہر ایک اُمت میں سے گواہ نکال لیں گے پھر کہیں گے کہ اپنی دلیل پیش کرو تو وہ جان لیں گے کہ سچ بات خدا کی ہے اور جو کچھ وہ افتراء کیا کرتے تھے ان سے جاتا رہے گا');
INSERT INTO "jalandhari" VALUES(28,76,'28:76','قارون موسٰی کی قوم میں سے تھا اور ان پر تعدّی کرتا تھا۔ اور ہم نے اس کو اتنے خزانے دیئے تھے کہ اُن کی کنجیاں ایک طاقتور جماعت کو اُٹھانی مشکل ہوتیں جب اس سے اس کی قوم نے کہا کہ اترائیے مت۔ کہ خدا اترانے والوں کو پسند نہیں کرتا');
INSERT INTO "jalandhari" VALUES(28,77,'28:77','اور جو (مال) تم کو خدا نے عطا فرمایا ہے اس سے آخرت کی بھلائی طلب کیجئے اور دنیا سے اپنا حصہ نہ بھلائیے اور جیسی خدا نے تم سے بھلائی کی ہے (ویسی) تم بھی (لوگوں سے) بھلائی کرو۔ اور ملک میں طالب فساد نہ ہو۔ کیونکہ خدا فساد کرنے والوں کو دوست نہیں رکھتا');
INSERT INTO "jalandhari" VALUES(28,78,'28:78','بولا کہ یہ (مال) مجھے میری دانش (کے زور) سے ملا ہے کیا اس کو معلوم نہیں کہ خدا نے اس سے پہلے بہت سی اُمتیں جو اس سے قوت میں بڑھ کر اور جمعیت میں بیشتر تھیں ہلاک کر ڈالی ہیں۔ اور گنہگاروں سے اُن کے گناہوں کے بارے میں پوچھا نہیں جائے گا');
INSERT INTO "jalandhari" VALUES(28,79,'28:79','تو (ایک روز) قارون (بڑی) آرائش (اور ٹھاٹھ) سے اپنی قوم کے سامنے نکلا۔ جو لوگ دنیا کی زندگی کے طالب تھے کہنے لگے کہ جیسا (مال ومتاع) قارون کو ملا ہے کاش ایسا ہی ہمیں بھی ملے۔ وہ تو بڑا ہی صاحب نصیب ہے');
INSERT INTO "jalandhari" VALUES(28,80,'28:80','اور جن لوگوں کو علم دیا گیا تھا وہ کہنے لگے کہ تم پر افسوس۔ مومنوں اور نیکوکاروں کے لئے (جو) ثواب خدا (کے ہاں تیار ہے وہ) کہیں بہتر ہے اور وہ صرف صبر کرنے والوں ہی کو ملے گا');
INSERT INTO "jalandhari" VALUES(28,81,'28:81','پس ہم نے قارون کو اور اس کے گھر کو زمین میں دھنسا دیا تو خدا کے سوا کوئی جماعت اس کی مددگار نہ ہوسکی۔ اور نہ وہ بدلہ لے سکا');
INSERT INTO "jalandhari" VALUES(28,82,'28:82','اور وہ لوگ جو کل اُس کے رتبے کی تمنا کرتے تھے صبح کو کہنے لگے ہائے شامت! خدا ہی تو اپنے بندوں میں سے جس کے لئے چاہتا ہے رزق فراخ کر دیتا ہے اور (جس کے لئے چاہتا ہے) تنگ کر دیتا ہے۔ اگر خدا ہم پر احسان نہ کرتا تو ہمیں بھی دھنسا دیتا۔ ہائے خرابی! کافر نجات نہیں پا سکتے');
INSERT INTO "jalandhari" VALUES(28,83,'28:83','وہ (جو) آخرت کا گھر (ہے) ہم نے اُسے اُن لوگوں کے لئے (تیار) کر رکھا ہے جو ملک میں ظلم اور فساد کا ارادہ نہیں رکھتے اور انجام (نیک) تو پرہیزگاروں ہی کا ہے');
INSERT INTO "jalandhari" VALUES(28,84,'28:84','جو شخص نیکی لے کر آئے گا اس کے لئے اس سے بہتر (صلہ موجود) ہے اور جو برائی لائے گا تو جن لوگوں نے برے کام کئے ان کو بدلہ بھی اسی طرح کا ملے گا جس طرح کے وہ کام کرتے تھے');
INSERT INTO "jalandhari" VALUES(28,85,'28:85','(اے پیغمبر) جس (خدا) نے تم پر قرآن (کے احکام) کو فرض کیا ہے وہ تمہیں بازگشت کی جگہ لوٹا دے گا۔ کہہ دو کہ میرا پروردگار اس شخص کو بھی خوب جانتا ہے جو ہدایت لےکر آیا اور (اس کو بھی) جو صریح گمراہی میں ہے');
INSERT INTO "jalandhari" VALUES(28,86,'28:86','اور تمہیں اُمید نہ تھی کہ تم پر کتاب نازل کی جائے گی۔ مگر تمہارے پروردگار کی مہربانی سے (نازل ہوئی) تو تم ہرگز کافروں کے مددگار نہ ہونا');
INSERT INTO "jalandhari" VALUES(28,87,'28:87','اور وہ تمہیں خدا کی آیتوں کی تبلیغ سے بعد اس کے کہ وہ تم پر نازل ہوچکی ہیں روک نہ دیں اور اپنے پروردگار کو پکارتے رہو اور مشرکوں میں ہرگز نہ ہو جیو');
INSERT INTO "jalandhari" VALUES(28,88,'28:88','اور خدا کے ساتھ کسی اور کو معبود (سمجھ کر) نہ پکارنا اس کے سوا کوئی معبود نہیں۔ اس کی ذات (پاک) کے سوا ہر چیز فنا ہونے والی ہے۔ اسی کا حکم ہے اور اسی کی طرف تم لوٹ کر جاؤ گے');
INSERT INTO "jalandhari" VALUES(29,1,'29:1','الم');
INSERT INTO "jalandhari" VALUES(29,2,'29:2','کیا لوگ یہ خیال کئے ہوئے ہیں کہ صرف یہ کہنے سے کہ ہم ایمان لے آئے چھوڑ دیئے جائیں گے اور اُن کی آزمائش نہیں کی جائے گی');
INSERT INTO "jalandhari" VALUES(29,3,'29:3','اور جو لوگ اُن سے پہلے ہو چکے ہیں ہم نے اُن کو بھی آزمایا تھا (اور ان کو بھی آزمائیں گے) سو خدا اُن کو ضرور معلوم کریں گے جو (اپنے ایمان میں) سچے ہیں اور اُن کو بھی جو جھوٹے ہیں');
INSERT INTO "jalandhari" VALUES(29,4,'29:4','کیا وہ لوگ جو برے کام کرتے ہیں یہ سمجھے ہوئے ہیں کہ یہ ہمارے قابو سے نکل جائیں گے۔ جو خیال یہ کرتے ہیں برا ہے');
INSERT INTO "jalandhari" VALUES(29,5,'29:5','جو شخص خدا کی ملاقات کی اُمید رکھتا ہو خدا کا (مقرر کیا ہوا) وقت ضرور آنے والا ہے۔ اور وہ سننے والا اور جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(29,6,'29:6','اور جو شخص محنت کرتا ہے تو اپنے ہی فائدے کے لئے محنت کرتا ہے۔ اور خدا تو سارے جہان سے بےپروا ہے');
INSERT INTO "jalandhari" VALUES(29,7,'29:7','اور جو لوگ ایمان لائے اور نیک عمل کرتے رہے ہم ان کے گناہوں کو اُن سے دور کردیں گے اور ان کو ان کے اعمال کا بہت اچھا بدلہ دیں گے');
INSERT INTO "jalandhari" VALUES(29,8,'29:8','اور ہم نے انسان کو اپنے ماں باپ کے ساتھ نیک سلوک کرنے کا حکم دیا ہے۔ ( اے مخاطب) اگر تیرے ماں باپ تیرے درپے ہوں کہ تو میرے ساتھ کسی کو شریک بنائے جس کی حقیقت کی تجھے واقفیت نہیں۔ تو ان کا کہنا نہ مانیو۔ تم( سب) کو میری طرف لوٹ کر آنا ہے۔ پھر جو کچھ تم کرتے تھے میں تم کو جتا دوں گا');
INSERT INTO "jalandhari" VALUES(29,9,'29:9','اور جو لوگ ایمان لائے اور نیک عمل کرتے رہے ان کو ہم نیک لوگوں میں داخل کریں گے');
INSERT INTO "jalandhari" VALUES(29,10,'29:10','اور بعض لوگ ایسے ہیں جو کہتے ہیں کہ ہم خدا پر ایمان لائے جب اُن کو خدا (کے رستے) میں کوئی ایذا پہنچتی ہے تو لوگوں کی ایذا کو (یوں) سمجھتے ہیں جیسے خدا کا عذاب۔ اگر تمہارے پروردگار کی طرف سے مدد پہنچے تو کہتے ہیں کہ ہم تمہارے ساتھ تھے۔ کیا جو اہل عالم کے سینوں میں ہے خدا اس سے واقف نہیں؟');
INSERT INTO "jalandhari" VALUES(29,11,'29:11','اور خدا اُن کو ضرور معلوم کرے گا جو (سچے) مومن ہیں اور منافقوں کو بھی معلوم کرکے رہے گا');
INSERT INTO "jalandhari" VALUES(29,12,'29:12','اور جو کافر ہیں وہ مومنوں سے کہتے ہیں کہ ہمارے طریق کی پیروی کرو ہم تمہارے گناہ اُٹھالیں گے۔ حالانکہ وہ اُن کے گناہوں کا کچھ بھی بوجھ اُٹھانے والے نہیں۔ کچھ شک نہیں کہ یہ جھوٹے ہیں');
INSERT INTO "jalandhari" VALUES(29,13,'29:13','اور یہ اپنے بوجھ بھی اُٹھائیں گے اور اپنے بوجھوں کے ساتھ اور (لوگوں کے) بوجھ بھی۔ اور جو بہتان یہ باندھتے رہے قیامت کے دن اُن کی اُن سے ضرور پرسش ہوگی');
INSERT INTO "jalandhari" VALUES(29,14,'29:14','اور ہم نے نوحؑ کو اُن کی قوم کی طرف بھیجا تو وہ ان میں پچاس برس کم ہزار برس رہے پھر اُن کو طوفان (کے عذاب) نے آپکڑا۔ اور وہ ظالم تھے');
INSERT INTO "jalandhari" VALUES(29,15,'29:15','پھر ہم نے نوحؑ کو اور کشتی والوں کو نجات دی اور کشتی کو اہل عالم کے لئے نشانی بنا دیا');
INSERT INTO "jalandhari" VALUES(29,16,'29:16','اور ابراہیمؑ کو (یاد کرو) جب اُنہوں نے اپنی قوم سے کہا کہ خدا کی عبادت کرو اور اس سے ڈرو اگر تم سمجھ رکھتے ہو تو یہ تمہارے حق میں بہتر ہے');
INSERT INTO "jalandhari" VALUES(29,17,'29:17','تو تم خدا کو چھوڑ کر بتوں کو پوجتے اور طوفان باندھتے ہو تو جن لوگوں کو تم خدا کے سوا پوجتے ہو وہ تم کو رزق دینے کا اختیار نہیں رکھتے پس خدا ہی کے ہاں سے رزق طلب کرو اور اسی کی عبادت کرو اور اسی کا شکر کرو اسی کی طرف تم لوٹ کر جاؤ گے');
INSERT INTO "jalandhari" VALUES(29,18,'29:18','اور اگر تم (میری) تکذیب کرو تو تم سے پہلے بھی اُمتیں (اپنے پیغمبروں کی) تکذیب کرچکی ہیں۔ اور پیغمبر کے ذمے کھول کر سنا دینے کے سوا اور کچھ نہیں');
INSERT INTO "jalandhari" VALUES(29,19,'29:19','کیا اُنہوں نے نہیں دیکھا کہ خدا کس طرح خلقت کو پہلی بار پیدا کرتا پھر (کس طرح) اس کو بار بار پیدا کرتا رہتا ہے۔ یہ خدا کو آسان ہے');
INSERT INTO "jalandhari" VALUES(29,20,'29:20','کہہ دو کہ ملک میں چلو پھرو اور دیکھو کہ اس نے کس طرح خلقت کو پہلی دفعہ پیدا کیا ہے پھر خدا ہی پچھلی پیدائش پیدا کرے گا۔ بےشک خدا ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(29,21,'29:21','وہ جسے چاہے عذاب دے اور جس پر چاہے رحم کرے۔ اور اُسی کی طرف تم لوٹائے جاؤ گے');
INSERT INTO "jalandhari" VALUES(29,22,'29:22','اور تم (اُس کو) نہ زمین میں عاجز کرسکتے ہو نہ آسمان میں اور نہ خدا کے سوا تمہارا کوئی دوست ہے اور نہ مددگار');
INSERT INTO "jalandhari" VALUES(29,23,'29:23','اور جن لوگوں نے خدا کی آیتوں سے اور اس کے ملنے سے انکار کیا وہ میری رحمت سے نااُمید ہوگئے ہیں اور ان کو درد دینے والا عذاب ہوگا');
INSERT INTO "jalandhari" VALUES(29,24,'29:24','تو اُن کی قوم کے لوگ جواب میں بولے تو یہ بولے کہ اُسے مار ڈالو یا جلا دو۔ مگر خدا نے اُن کو آگ (کی سوزش) سے بچالیا۔ جو لوگ ایمان رکھتے ہیں اُن کے لئے اس میں نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(29,25,'29:25','اور ابراہیم نے کہا کہ تم جو خدا کو چھوڑ کر بتوں کو لے بیٹھے ہو تو دنیا کی زندگی میں باہم دوستی کے لئے (مگر) پھر قیامت کے دن تم ایک دوسرے (کی دوستی) سے انکار کر دو گے اور ایک دوسرے پر لعنت بھیجو گے اور تمہارا ٹھکانا دوزخ ہوگا اور کوئی تمہارا مددگار نہ ہوگا');
INSERT INTO "jalandhari" VALUES(29,26,'29:26','پس اُن پر (ایک) لوط ایمان لائے اور (ابراہیم) کہنے لگے کہ میں اپنے پروردگار کی طرف ہجرت کرنے والا ہوں۔ بیشک وہ غالب حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(29,27,'29:27','اور ہم نے اُن کو اسحٰق اور یعقوب بخشے اور اُن کی اولاد میں پیغمبری اور کتاب (مقرر) کر دی اور ان کو دنیا میں بھی اُن کا صلہ عنایت کیا اور وہ آخرت میں بھی نیک لوگوں میں ہوں گے');
INSERT INTO "jalandhari" VALUES(29,28,'29:28','اور لوط (کو یاد کرو) جب اُنہوں نے اپنی قوم سے کہا کہ تم (عجب) بےحیائی کے مرتکب ہوتے ہو۔ تم سے پہلے اہل عالم میں سے کسی نے ایسا کام نہیں کیا');
INSERT INTO "jalandhari" VALUES(29,29,'29:29','تم کیوں (لذت کے ارادے سے) لونڈوں کی طرف مائل ہوتے اور (مسافروں کی) رہزنی کرتے ہو اور اپنی مجلسوں میں ناپسندیدہ کام کرتے ہو۔ تو اُن کی قوم کے لوگ جواب میں بولے تو یہ بولے کہ اگر تم سچے ہو تو ہم پر عذاب لے آؤ');
INSERT INTO "jalandhari" VALUES(29,30,'29:30','لوط نے کہا کہ اے میرے پروردگار ان مفسد لوگوں کے مقابلے میں مجھے نصرت عنایت فرما');
INSERT INTO "jalandhari" VALUES(29,31,'29:31','اور جب ہمارے فرشتے ابراہیم کے پاس خوشی کی خبر لے کر آئے تو کہنے لگے کہ ہم اس بستی کے لوگوں کو ہلاک کر دینے والے ہیں کہ یہاں کے رہنے والے نافرمان ہیں');
INSERT INTO "jalandhari" VALUES(29,32,'29:32','ابراہیم نے کہا کہ اس میں تو لوط بھی ہیں۔ وہ کہنے لگے کہ جو لوگ یہاں (رہتے) ہیں ہمیں سب معلوم ہیں۔ ہم اُن کو اور اُن کے گھر والوں کو بچالیں گے بجز اُن کی بیوی کے وہ پیچھے رہنے والوں میں ہوگی');
INSERT INTO "jalandhari" VALUES(29,33,'29:33','اور جب ہمارے فرشتے لوط کے پاس آئے تو وہ اُن (کی وجہ) سے ناخوش اور تنگ دل ہوئے۔ فرشتوں نے کہا کچھ خوف نہ کیجئے۔ اور نہ رنج کیجئے ہم آپ کو اور آپ کے گھر والوں کو بچالیں گے مگر آپ کی بیوی کہ پیچھے رہنے والوں میں ہوگی');
INSERT INTO "jalandhari" VALUES(29,34,'29:34','ہم اس بستی کے رہنے والوں پر اس سبب سے کہ یہ بدکرداری کرتے رہے ہیں آسمان سے عذاب نازل کرنے والے ہیں');
INSERT INTO "jalandhari" VALUES(29,35,'29:35','اور ہم نے سمجھنے والے لوگوں کے لئے اس بستی سے ایک کھلی نشانی چھوڑ دی');
INSERT INTO "jalandhari" VALUES(29,36,'29:36','اور مدین کی طرف اُن کے بھائی شعیب کو (بھیجا) تو اُنہوں نے کہا (اے قوم) خدا کی عبادت کرو اور پچھلے دن کے آنے کی اُمید رکھو اور ملک میں فساد نہ مچاؤ');
INSERT INTO "jalandhari" VALUES(29,37,'29:37','مگر اُنہوں نے اُن کو جھوٹا سمجھا سو اُن کو زلزلے (کے عذاب) نے آپکڑا اور وہ اپنے گھروں میں اوندھے پڑے رہ گئے');
INSERT INTO "jalandhari" VALUES(29,38,'29:38','اور عاد اور ثمود کو بھی (ہم نے ہلاک کر دیا) چنانچہ اُن کے (ویران گھر) تمہاری آنکھوں کے سامنے ہیں اور شیطان نے اُن کے اعمال ان کو آراستہ کر دکھائے اور ان کو (سیدھے) رستے سے روک دیا۔ حالانکہ وہ دیکھنے والے (لوگ )تھے');
INSERT INTO "jalandhari" VALUES(29,39,'29:39','اور قارون اور فرعون اور ہامان کو بھی (ہلاک کر دیا) اور اُن کے پاس موسٰی کھلی نشانی لےکر آئے تو وہ ملک میں مغرور ہوگئے اور ہمارے قابو سے نکل جانے والے نہ تھے');
INSERT INTO "jalandhari" VALUES(29,40,'29:40','تو ہم نے سب کو اُن کے گناہوں کے سبب پکڑ لیا۔ سو ان میں کچھ تو ایسے تھے جن پر ہم نے پتھروں کا مینھہ برسایا۔ اور کچھ ایسے تھے جن کو چنگھاڑ نے آپکڑا اور کچھ ایسے تھے جن کو ہم نے زمین میں دھنسا دیا۔ اور کچھ ایسے تھے جن کو غرق کر دیا اور خدا ایسا نہ تھا کہ اُن پر ظلم کرتا لیکن وہی اپنے آپ پر ظلم کرتے تھے');
INSERT INTO "jalandhari" VALUES(29,41,'29:41','جن لوگوں نے خدا کے سوا (اوروں کو) کارساز بنا رکھا ہے اُن کی مثال مکڑی کی سی ہے کہ وہ بھی ایک (طرح کا) گھر بناتی ہے۔ اور کچھ شک نہیں کہ تمام گھروں سے کمزور مکڑی کا گھر ہے کاش یہ (اس بات کو) جانتے');
INSERT INTO "jalandhari" VALUES(29,42,'29:42','یہ جس چیز کو خدا کے سوا پکارتے ہیں (خواہ) وہ کچھ ہی ہو خدا اُسے جانتا ہے۔ اور وہ غالب اور حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(29,43,'29:43','اور یہ مثالیں ہم لوگوں کے (سمجھانے کے) لئے بیان کرتے ہیں اور اُسے تو اہلِ دانش ہی سمجھتے ہیں');
INSERT INTO "jalandhari" VALUES(29,44,'29:44','خدا نے آسمانوں اور زمین کو حکمت کے ساتھ پیدا کیا ہے۔ کچھ شک نہیں کہ ایمان والوں کے لئے اس میں نشانی ہے');
INSERT INTO "jalandhari" VALUES(29,45,'29:45','(اے محمدصلى الله عليه وسلم! یہ) کتاب جو تمہاری طرف وحی کی گئی ہے اس کو پڑھا کرو اور نماز کے پابند رہو۔ کچھ شک نہیں کہ نماز بےحیائی اور بری باتوں سے روکتی ہے۔ اور خدا کا ذکر بڑا (اچھا کام) ہے۔ اور جو کچھ تم کرتے ہو خدا اُسے جانتا ہے');
INSERT INTO "jalandhari" VALUES(29,46,'29:46','اور اہلِ کتاب سے جھگڑا نہ کرو مگر ایسے طریق سے کہ نہایت اچھا ہو۔ ہاں جو اُن میں سے بےانصافی کریں (اُن کے ساتھ اسی طرح مجادلہ کرو) اور کہہ دو کہ جو (کتاب) ہم پر اُتری اور جو (کتابیں) تم پر اُتریں ہم سب پر ایمان رکھتے ہیں اور ہمارا اور تمہارا معبود ایک ہی ہے اور ہم اُسی کے فرمانبردار ہیں');
INSERT INTO "jalandhari" VALUES(29,47,'29:47','اور اسی طرح ہم نے تمہاری طرف کتاب اُتاری ہے۔ تو جن لوگوں کو ہم نے کتابیں دی تھیں وہ اس پر ایمان لے آتے ہیں۔ اور بعض ان( مشرک) لوگوں میں سے بھی اس پر ایمان لے آتے ہیں۔ اور ہماری آیتوں سے وہی انکار کرتے ہیں جو کافر (ازلی) ہیں');
INSERT INTO "jalandhari" VALUES(29,48,'29:48','اور تم اس سے پہلے کوئی کتاب نہیں پڑھتے تھے اور نہ اُسے اپنے ہاتھ سے لکھ ہی سکتے تھے ایسا ہوتا تو اہلِ باطل ضرور شک کرتے');
INSERT INTO "jalandhari" VALUES(29,49,'29:49','بلکہ یہ روشن آیتیں ہیں۔ جن لوگوں کو علم دیا گیا ہے اُن کے سینوں میں (محفوظ) اور ہماری آیتوں سے وہی لوگ انکار کرتے ہیں جو بےانصاف ہیں');
INSERT INTO "jalandhari" VALUES(29,50,'29:50','اور (کافر) کہتے ہیں کہ اس پر اس کے پروردگار کی طرف سے نشانیاں کیوں نازل نہیں ہوئیں کہہ دو کہ نشانیاں تو خدا ہی کے پاس ہیں۔ اور میں تو کھلم کھلا ہدایت کرنے والا ہوں');
INSERT INTO "jalandhari" VALUES(29,51,'29:51','کیا اُن لوگوں کے لئے یہ کافی نہیں کہ ہم نے تم پر کتاب نازل کی جو اُن کو پڑھ کر سنائی جاتی ہے۔ کچھ شک نہیں کہ مومن لوگوں کے لیے اس میں رحمت اور نصیحت ہے');
INSERT INTO "jalandhari" VALUES(29,52,'29:52','کہہ دو کہ میرے اور تمہارے درمیان خدا ہی گواہ کافی ہے جو چیز آسمانوں میں اور زمین میں ہے وہ سب کو جانتا ہے۔ اور جن لوگوں نے باطل کو مانا اور خدا سے انکار کیا وہی نقصان اُٹھانے والے ہیں');
INSERT INTO "jalandhari" VALUES(29,53,'29:53','اور یہ لوگ تم سے عذاب کے لئے جلدی کر رہے ہیں۔ اگر ایک وقت مقرر نہ( ہو چکا) ہوتا تو اُن پر عذاب آبھی گیا ہوتا۔ اور وہ (کسی وقت میں) اُن پر ضرور ناگہاں آکر رہے گا اور اُن کو معلوم بھی نہ ہوگا');
INSERT INTO "jalandhari" VALUES(29,54,'29:54','یہ تم سے عذاب کے لئے جلدی کررہے ہیں۔ اور دوزخ تو کافروں کو گھیر لینے والی ہے');
INSERT INTO "jalandhari" VALUES(29,55,'29:55','جس دن عذاب اُن کو اُن کے اُوپر سے اور نیچے سے ڈھانک لے گا اور (خدا) فرمائے گا کہ جو کام تم کیا کرتے تھے (اب) اُن کا مزہ چکھو');
INSERT INTO "jalandhari" VALUES(29,56,'29:56','اے میرے بندو جو ایمان لائے ہو میری زمین فراخ ہے تو میری ہی عبادت کرو');
INSERT INTO "jalandhari" VALUES(29,57,'29:57','ہر متنفس موت کا مزہ چکھنے والا ہے۔ پھر تم ہماری ہی طرف لوٹ کر آؤ گے');
INSERT INTO "jalandhari" VALUES(29,58,'29:58','اور جو لوگ ایمان لائے اور نیک عمل کرتے رہے اُن کو ہم بہشت کے اُونچے اُونچے محلوں میں جگہ دیں گے۔ جن کے نیچے نہریں بہ رہی ہیں۔ ہمیشہ ان میں رہیں گے۔ (نیک )عمل کرنے والوں کا (یہ) خوب بدلہ ہے');
INSERT INTO "jalandhari" VALUES(29,59,'29:59','جو صبر کرتے اور اپنے پروردگار پر بھروسہ رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(29,60,'29:60','اور بہت سے جانور ہیں جو اپنا رزق اُٹھائے نہیں پھرتے خدا ہی ان کو رزق دیتا ہے اور تم کو بھی۔ اور وہ سننے والا اور جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(29,61,'29:61','اور اگر اُن سے پوچھو کہ آسمانوں اور زمین کو کس نے پیدا کیا۔ اور سورج اور چاند کو کس نے (تمہارے) زیر فرمان کیا تو کہہ دیں گے خدا نے۔ تو پھر یہ کہاں اُلٹے جا رہے ہیں');
INSERT INTO "jalandhari" VALUES(29,62,'29:62','خدا ہی اپنے بندوں میں سے جس کے لئے چاہتا ہے روزی فراخ کر دیتا ہے اور جس کے لئے چاہتا ہے تنگ کر دیتا ہے بیشک خدا ہر چیز سے واقف ہے');
INSERT INTO "jalandhari" VALUES(29,63,'29:63','اور اگر تم ان سے پوچھو کہ آسمان سے پانی کس نے نازل فرمایا پھر اس سے زمین کو اس کے مرنے کے بعد (کس نے) زندہ کیا تو کہہ دیں گے کہ خدا نے۔ کہہ دو کہ خدا کا شکر ہے۔ لیکن ان میں اکثر نہیں سمجھتے');
INSERT INTO "jalandhari" VALUES(29,64,'29:64','اور یہ دنیا کی زندگی تو صرف کھیل اور تماشہ ہے اور( ہمیشہ کی) زندگی (کا مقام) تو آخرت کا گھر ہے۔ کاش یہ (لوگ) سمجھتے');
INSERT INTO "jalandhari" VALUES(29,65,'29:65','پھر جب یہ کشتی میں سوار ہوتے ہیں تو خدا کو پکارتے (اور) خالص اُسی کی عبادت کرتے ہیں۔ لیکن جب وہ اُن کو نجات دے کر خشکی پر پہنچا دیتا ہے تو جھٹ شرک کرنے لگے جاتے ہیں');
INSERT INTO "jalandhari" VALUES(29,66,'29:66','تاکہ جو ہم نے اُن کو بخشا ہے اُس کی ناشکری کریں اور فائدہ اٹھائیں (سو خیر) عنقریب اُن کو معلوم ہوجائے گا');
INSERT INTO "jalandhari" VALUES(29,67,'29:67','کیا اُنہوں نے نہیں دیکھا کہ ہم نے حرم کو مقام امن بنایا ہے اور لوگ اس کے گرد ونواح سے اُچک لئے جاتے ہیں۔ کیا یہ لوگ باطل پر اعتقاد رکھتے ہیں اور خدا کی نعمتوں کی ناشکری کرتے ہیں');
INSERT INTO "jalandhari" VALUES(29,68,'29:68','اور اس سے ظالم کون جو خدا پر جھوٹ بہتان باندھے یا جب حق بات اُس کے پاس آئے تو اس کی تکذیب کرے۔ کیا کافروں کا ٹھکانا جہنم میں نہیں ہے؟');
INSERT INTO "jalandhari" VALUES(29,69,'29:69','اور جن لوگوں نے ہمارے لئے کوشش کی ہم اُن کو ضرور اپنے رستے دکھا دیں گے۔ اور خدا تو نیکو کاروں کے ساتھ ہے');
INSERT INTO "jalandhari" VALUES(30,1,'30:1','الٓمٓ');
INSERT INTO "jalandhari" VALUES(30,2,'30:2','(اہلِ ) روم مغلوب ہوگئے');
INSERT INTO "jalandhari" VALUES(30,3,'30:3','نزدیک کے ملک میں اور وہ مغلوب ہونے کے بعد عنقریب غالب آجائیں گے');
INSERT INTO "jalandhari" VALUES(30,4,'30:4','چند ہی سال میں پہلے بھی اور پیچھے بھی خدا ہی کا حکم ہے اور اُس روز مومن خوش ہوجائیں گے');
INSERT INTO "jalandhari" VALUES(30,5,'30:5','(یعنی) خدا کی مدد سے۔ وہ جسے چاہتا ہے مدد دیتا ہے اور وہ غالب (اور) مہربان ہے');
INSERT INTO "jalandhari" VALUES(30,6,'30:6','(یہ) خدا کا وعدہ (ہے) خدا اپنے وعدے کے خلاف نہیں کرتا لیکن اکثر لوگ نہیں جانتے');
INSERT INTO "jalandhari" VALUES(30,7,'30:7','یہ تو دنیا کی ظاہری زندگی کو جانتے ہیں۔ اور آخرت (کی طرف) سے غافل ہیں');
INSERT INTO "jalandhari" VALUES(30,8,'30:8','کیا اُنہوں نے اپنے دل میں غور نہیں کیا۔ کہ خدا نے آسمانوں اور زمین کو اور جو کچھ ان دونوں کے درمیان ہے اُن کو حکمت سے اور ایک وقت مقرر تک کے لئے پیدا کیا ہے۔ اور بہت سے لوگ اپنے پروردگار سے ملنے کے قائل ہی نہیں');
INSERT INTO "jalandhari" VALUES(30,9,'30:9','کیا اُن لوگوں نے ملک میں سیر نہیں کی (سیر کرتے )تو دیکھ لیتے کہ جو لوگ اُن سے پہلے تھے ان کا انجام کیسے ہوا۔ وہ اُن سے زورو قوت میں کہیں زیادہ تھے اور اُنہوں نے زمین کو جوتا اور اس کو اس سے زیادہ آباد کیا تھا جو اُنہوں نے آباد کیا۔ اور اُن کے پاس اُن کے پیغمبر نشانیاں لےکر آتے رہے تو خدا ایسا نہ تھا کہ اُن پر ظلم کرتا۔ بلکہ وہی اپنے آپ پر ظلم کرتے تھے');
INSERT INTO "jalandhari" VALUES(30,10,'30:10','پھر جن لوگوں نے برائی کی اُن کا انجام بھی برا ہوا اس لیے کہ خدا کی آیتوں کو جھٹلاتے اور اُن کی ہنسی اُڑاتے رہے تھے');
INSERT INTO "jalandhari" VALUES(30,11,'30:11','خدا ہی خلقت کو پہلی بار پیدا کرتا ہے وہی اس کو پھر پیدا کرے گا پھر تم اُسی کی طرف لوٹ جاؤ گے');
INSERT INTO "jalandhari" VALUES(30,12,'30:12','اور جس دن قیامت برپا ہوگی گنہگار نااُمید ہوجائیں گے');
INSERT INTO "jalandhari" VALUES(30,13,'30:13','اور ان کے (بنائے ہوئے) شریکوں میں سے کوئی ان کا سفارشی نہ ہوگا اور وہ اپنے شریکوں سے نامعتقد ہوجائیں گے');
INSERT INTO "jalandhari" VALUES(30,14,'30:14','اور جس دن قیامت برپا ہوگی اس روز وہ الگ الگ فرقے ہوجائیں گے');
INSERT INTO "jalandhari" VALUES(30,15,'30:15','تو جو لوگ ایمان لائے اور عمل نیک کرتے رہے وہ (بہشت کے) باغ میں خوش حال ہوں گے');
INSERT INTO "jalandhari" VALUES(30,16,'30:16','اور جنہوں نے کفر کیا اور ہماری آیتوں اور آخرت کے آنے کو جھٹلایا۔ وہ عذاب میں ڈالے جائیں گے');
INSERT INTO "jalandhari" VALUES(30,17,'30:17','تو جس وقت تم کو شام ہو اور جس وقت صبح ہو خدا کی تسبیح کرو (یعنی نماز پڑھو)');
INSERT INTO "jalandhari" VALUES(30,18,'30:18','اور آسمانوں اور زمین میں اُسی کی تعریف ہے۔ اور تیسرے پہر بھی اور جب دوپہر ہو (اُس وقت بھی نماز پڑھا کرو)');
INSERT INTO "jalandhari" VALUES(30,19,'30:19','وہی زندے کو مردے سے نکالتا اور (وہی) مردے کو زندے سے نکالتا ہے اور( وہی) زمین کو اس کے مرنے کے بعد زندہ کرتا ہے۔ اور اسی طرح تم (دوبارہ زمین میں سے) نکالے جاؤ گے');
INSERT INTO "jalandhari" VALUES(30,20,'30:20','اور اسی کے نشانات (اور تصرفات) میں سے ہے کہ اُس نے تمہیں مٹی سے پیدا کیا۔ پھر اب تم انسان ہوکر جا بجا پھیل رہے ہو');
INSERT INTO "jalandhari" VALUES(30,21,'30:21','اور اسی کے نشانات (اور تصرفات) میں سے ہے کہ اُس نے تمہارے لئے تمہاری ہی جنس کی عورتیں پیدا کیں تاکہ اُن کی طرف (مائل ہوکر) آرام حاصل کرو اور تم میں محبت اور مہربانی پیدا کر دی جو لوگ غور کرتے ہیں اُن کے لئے ان باتوں میں (بہت سی) نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(30,22,'30:22','اور اسی کے نشانات (اور تصرفات) میں سے ہے آسمانوں اور زمین کا پیدا کرنا اور تمہاری زبانوں اور رنگوں کا جدا جدا ہونا۔ اہلِ دانش کے لیے ان (باتوں) میں (بہت سی) نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(30,23,'30:23','اور اسی کے نشانات (اور تصرفات) میں سے ہے تمہارا رات اور دن میں سونا اور اُس کے فضل کا تلاش کرنا۔ جو لوگ سنتے ہیں اُن کے لیے ان باتوں میں (بہت سی) نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(30,24,'30:24','اور اسی کے نشانات (اور تصرفات) میں سے ہے کہ تم کو خوف اور اُمید دلانے کے لئے بجلی دکھاتا ہے اور آسمان سے مینھہ برساتا ہے۔ پھر زمین کو اس کے مر جانے کے بعد زندہ (و شاداب) کر دیتا ہے۔ عقل والوں کے لئے ان (باتوں) میں (بہت سی) نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(30,25,'30:25','اور اسی کے نشانات (اور تصرفات) میں سے ہے کہ آسمان اور زمین اس کے حکم سے قائم ہیں۔ پھر جب وہ تم کو زمین میں سے (نکلنے کے لئے) آواز دے گا تو تم جھٹ نکل پڑو گے');
INSERT INTO "jalandhari" VALUES(30,26,'30:26','اور آسمانوں اور زمین میں (جتنے فرشتے اور انسان وغیرہ ہیں) اسی کے( مملوک )ہیں (اور) تمام اس کے فرمانبردار ہیں');
INSERT INTO "jalandhari" VALUES(30,27,'30:27','اور وہی تو ہے جو خلقت کو پہلی دفعہ پیدا کرتا ہے پھر اُسے دوبارہ پیدا کرے گا۔ اور یہ اس کو بہت آسان ہے۔ اور آسمانوں اور زمین میں اس کی شان بہت بلند ہے۔ اور وہ غالب حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(30,28,'30:28','وہ تمہارے لئے تمہارے ہی حال کی ایک مثال بیان فرماتا ہے کہ بھلا جن (لونڈی غلاموں) کے تم مالک ہو وہ اس (مال) میں جو ہم نے تم کو عطا فرمایا ہے تمہارے شریک ہیں، اور (کیا )تم اس میں (اُن کو اپنے) برابر (مالک سمجھتے) ہو( اور کیا) تم اُن سے اس طرح ڈرتے ہو جس طرح اپنوں سے ڈرتے ہو، اسی طرح عقل والوں کے لئے اپنی آیتیں کھول کھول کر بیان کرتے ہیں');
INSERT INTO "jalandhari" VALUES(30,29,'30:29','مگر جو ظالم ہیں بےسمجھے اپنی خواہشوں کے پیچھے چلتے ہیں تو جس کو خدا گمراہ کرے اُسے کون ہدایت دے سکتا ہے؟ اور ان کا کوئی مددگار نہیں');
INSERT INTO "jalandhari" VALUES(30,30,'30:30','تو تم ایک طرف کے ہوکر دین (خدا کے رستے) پر سیدھا منہ کئے چلے جاؤ (اور) خدا کی فطرت کو جس پر اُس نے لوگوں کو پیدا کیا ہے (اختیار کئے رہو) خدا کی بنائی ہوئی (فطرت) میں تغیر وتبدل نہیں ہو سکتا۔ یہی سیدھا دین ہے لیکن اکثر لوگ نہیں جانتے');
INSERT INTO "jalandhari" VALUES(30,31,'30:31','(مومنو) اُسی (خدا )کی طرف رجوع کئے رہو اور اس سے ڈرتے رہو اور نماز پڑھتے رہو اور مشرکوں میں نہ ہونا');
INSERT INTO "jalandhari" VALUES(30,32,'30:32','(اور نہ) اُن لوگوں میں (ہونا) جنہوں نے اپنے دین کو ٹکڑے ٹکڑے کر دیا اور (خود) فرقے فرقے ہو گئے۔ سب فرقے اسی سے خوش ہیں جو اُن کے پاس ہے');
INSERT INTO "jalandhari" VALUES(30,33,'30:33','اور جب لوگوں کو تکلیف پہنچتی ہے تو اپنے پروردگار کو پکارتے اور اسی کی طرف رجوع ہوتے ہیں۔ پھر جب وہ ان کو اپنی رحمت کا مزا چکھاتا ہے تو ایک فرقہ اُن میں سے اپنے پروردگار سے شرک کرنے لگتا ہے');
INSERT INTO "jalandhari" VALUES(30,34,'30:34','تاکہ جو ہم نے ان کو بخشا ہے اُس کی ناشکری کریں سو (خیر) فائدے اُٹھالو عنقریب تم کو (اس کا انجام) معلوم ہو جائے گا');
INSERT INTO "jalandhari" VALUES(30,35,'30:35','کیا ہم نے ان پر کوئی ایسی دلیل نازل کی ہے کہ اُن کو خدا کے ساتھ شرک کرنا بتاتی ہے');
INSERT INTO "jalandhari" VALUES(30,36,'30:36','اور جب ہم لوگوں کو اپنی رحمت کا مزا چکھاتے ہیں تو اُس سے خوش ہو جاتے ہیں اور اگر اُن کے عملوں کے سبب جو اُن کے ہاتھوں نے آگے بھیجے ہیں کوئی گزند پہنچے تو نااُمید ہو کر رہ جاتے ہیں');
INSERT INTO "jalandhari" VALUES(30,37,'30:37','کیا اُنہوں نے نہیں دیکھا کہ خدا ہی جس کے لئے چاہتا ہے رزق فراخ کرتا ہے اور( جس کے لئے چاہتا ہے) تنگ کرتا ہے۔ بیشک اس میں ایمان لانے والوں کے لئے نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(30,38,'30:38','تو اہلِ قرابت اور محتاجوں اور مسافروں کو ان کا حق دیتے رہو۔ جو لوگ رضائے خدا کے طالب ہیں یہ اُن کے حق میں بہتر ہے۔ اور یہی لوگ نجات حاصل کرنے والے ہیں');
INSERT INTO "jalandhari" VALUES(30,39,'30:39','اور جو تم سود دیتے ہو کہ لوگوں کے مال میں افزائش ہو تو خدا کے نزدیک اس میں افزائش نہیں ہوتی اور جو تم زکوٰة دیتے ہو اور اُس سے خدا کی رضا مندی طلب کرتے ہو تو (وہ موجبِ برکت ہے اور) ایسے ہی لوگ (اپنے مال کو) دو چند سہ چند کرنے والے ہیں');
INSERT INTO "jalandhari" VALUES(30,40,'30:40','خدا ہی تو ہے جس نے تم کو پیدا کیا پھر تم کو رزق دیا پھر تمہیں مارے گا۔ پھر زندہ کرے گا۔ بھلا تمہارے (بنائے ہوئے) شریکوں میں بھی کوئی ایسا ہے جو ان کاموں میں سے کچھ کر سکے۔ وہ پاک ہے اور (اس کی شان) ان کے شریکوں سے بلند ہے');
INSERT INTO "jalandhari" VALUES(30,41,'30:41','خشکی اور تری میں لوگوں کے اعمال کے سبب فساد پھیل گیا ہے تاکہ خدا اُن کو اُن کے بعض اعمال کا مزہ چکھائے عجب نہیں کہ وہ باز آجائیں');
INSERT INTO "jalandhari" VALUES(30,42,'30:42','کہہ دو کہ ملک میں چلو پھرو اور دیکھو کہ جو لوگ( تم سے) پہلے ہوئے ہیں ان کا کیسا انجام ہوا ہے۔ ان میں زیادہ تر مشرک ہی تھے');
INSERT INTO "jalandhari" VALUES(30,43,'30:43','تو اس روز سے پہلے جو خدا کی طرف سے آکر رہے گا اور رک نہیں سکے گا دین (کے رستے) پر سیدھا منہ کئے چلے چلو اس روز (سب) لوگ منتشر ہوجائیں گے');
INSERT INTO "jalandhari" VALUES(30,44,'30:44','جس شخص نے کفر کیا تو اس کے کفر کا ضرر اُسی کو ہے اور جس نے نیک عمل کئے تو ایسے لوگ اپنے ہی لئے آرام گاہ درست کرتے ہیں');
INSERT INTO "jalandhari" VALUES(30,45,'30:45','جو لوگ ایمان لائے اور نیک عمل کرتے رہے اُن کو خدا اپنے فضل سے بدلہ دے گا۔ بیشک وہ کافروں کو دوست نہیں رکھتا');
INSERT INTO "jalandhari" VALUES(30,46,'30:46','اور اُسی کی نشانیوں میں سے ہے کہ ہواؤں کو بھیجتا ہے کہ خوشخبری دیتی ہیں تاکہ تم کو اپنی رحمت کے مزے چکھائے اور تاکہ اس کے حکم سے کشتیاں چلیں اور تاکہ اس کے فضل سے (روزی) طلب کرو عجب نہیں کہ تم شکر کرو');
INSERT INTO "jalandhari" VALUES(30,47,'30:47','اور ہم نے تم سے پہلے بھی پیغمبر ان کی قوم کی طرف بھیجے تو وہ اُن کے پاس نشانیاں لےکر آئے سو جو لوگ نافرمانی کرتے تھے ہم نے اُن سے بدلہ لےکر چھوڑا اور مومنوں کی مدد ہم پر لازم تھی');
INSERT INTO "jalandhari" VALUES(30,48,'30:48','خدا ہی تو ہے جو ہواؤں کو چلاتا ہے تو وہ بادل کو اُبھارتی ہیں۔ پھر خدا اس کو جس طرح چاہتا ہے آسمان میں پھیلا دیتا اور تہ بتہ کر دیتا ہے پھر تم دیکھتے ہو کہ اس کے بیچ میں سے مینھہ نکلنے لگتا ہے پھر جب وہ اپنے بندوں میں سے جن پر چاہتا ہے اُسے برسا دیتا ہے تو وہ خوش ہو جاتے ہیں');
INSERT INTO "jalandhari" VALUES(30,49,'30:49','اور بیشتر تو وہ مینھہ کے اُترنے سے پہلے نااُمید ہو رہے تھے');
INSERT INTO "jalandhari" VALUES(30,50,'30:50','تو (اے دیکھنے والے) خدا کی رحمت کی نشانیوں کی طرف دیکھ کہ وہ کس طرح زمین کو اس کے مرنے کے بعد زندہ کرتا ہے۔ بیشک وہ مردوں کو زندہ کرنے والا ہے۔ اور وہ ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(30,51,'30:51','اور اگر ہم ایسی ہوا بھیجیں کہ وہ (اس کے سبب) کھیتی کو دیکھیں (کہ) زرد (ہو گئی ہے) تو اس کے بعد وہ ناشکری کرنے لگ جائیں');
INSERT INTO "jalandhari" VALUES(30,52,'30:52','تو تم مردوں کی (بات) نہیں سنا سکتے اور نہ بہروں کو جب وہ پیٹھ پھیر کر پھر جائیں آواز سنا سکتے ہو');
INSERT INTO "jalandhari" VALUES(30,53,'30:53','اور نہ اندھوں کو اُن کی گمراہی سے (نکال کر) راہ راست پر لاسکتے ہو۔ تم تو انہی لوگوں کو سنا سکتے ہو جو ہماری آیتوں پر ایمان لاتے ہیں سو وہی فرمانبردار ہیں');
INSERT INTO "jalandhari" VALUES(30,54,'30:54','خدا ہی تو ہے جس نے تم کو (ابتدا میں) کمزور حالت میں پیدا کیا پھر کمزوری کے بعد طاقت عنایت کی پھر طاقت کے بعد کمزوری اور بڑھاپا دیا۔ وہ جو چاہتا ہے پیدا کرتا ہے اور وہ صاحب دانش اور صاحب قدرت ہے');
INSERT INTO "jalandhari" VALUES(30,55,'30:55','اور جس روز قیامت برپا ہوگی گنہگار قسمیں کھائیں گے کہ وہ (دنیا میں) ایک گھڑی سے زیادہ نہیں رہے تھے۔ اسی طرح وہ (رستے سے) اُلٹے جاتے تھے');
INSERT INTO "jalandhari" VALUES(30,56,'30:56','اور جن لوگوں کو علم اور ایمان دیا گیا تھا وہ کہیں گے کہ خدا کی کتاب کے مطابق تم قیامت تک رہے ہو۔ اور یہ قیامت ہی کا دن ہے لیکن تم کو اس کا یقین ہی نہیں تھا');
INSERT INTO "jalandhari" VALUES(30,57,'30:57','تو اس روز ظالم لوگوں کو ان کا عذر کچھ فائدہ نہ دے گا اور نہ اُن سے توبہ قبول کی جائے گی');
INSERT INTO "jalandhari" VALUES(30,58,'30:58','اور ہم نے لوگوں کے (سمجھانے کے) لئے اس قرآن میں ہر طرح کی مثال بیان کر دی ہے اور اگر تم اُن کے سامنے کوئی نشانی پیش کرو تو یہ کافر کہہ دیں گے کہ تم تو جھوٹے ہو');
INSERT INTO "jalandhari" VALUES(30,59,'30:59','اسی طرح خدا اُن لوگوں کے دلوں پر جو سمجھ نہیں رکھتے مہر لگا دیتا ہے');
INSERT INTO "jalandhari" VALUES(30,60,'30:60','پس تم صبر کرو بیشک خدا کا وعدہ سچا ہے اور( دیکھو) جو لوگ یقین نہیں رکھتے وہ تمہیں اوچھا نہ بنادیں');
INSERT INTO "jalandhari" VALUES(31,1,'31:1','الٓمٓ');
INSERT INTO "jalandhari" VALUES(31,2,'31:2','یہ حکمت کی (بھری ہوئی) کتاب کی آیتیں ہیں');
INSERT INTO "jalandhari" VALUES(31,3,'31:3','نیکوکاروں کے لئے ہدایت اور رحمت ہے');
INSERT INTO "jalandhari" VALUES(31,4,'31:4','جو نماز کی پابندی کرتے اور زکوٰة دیتے اور آخرت کا یقین رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(31,5,'31:5','یہی اپنے پروردگار (کی طرف) سے ہدایت پر ہیں اور یہی نجات پانے والے ہیں');
INSERT INTO "jalandhari" VALUES(31,6,'31:6','اور لوگوں میں بعض ایسا ہے جو بیہودہ حکایتیں خریدتا ہے تاکہ( لوگوں کو) بےسمجھے خدا کے رستے سے گمراہ کرے اور اس سے استہزاء کرے یہی لوگ ہیں جن کو ذلیل کرنے والا عذاب ہوگا');
INSERT INTO "jalandhari" VALUES(31,7,'31:7','اور جب اس کو ہماری آیتیں سنائی جاتی ہیں تو اکڑ کر منہ پھیر لیتا ہے گویا اُن کو سنا ہی نہیں جیسے اُن کے کانوں میں ثقل ہے تو اس کو درد دینے والے عذاب کی خوشخبری سنادو');
INSERT INTO "jalandhari" VALUES(31,8,'31:8','جو لوگ ایمان لائے اور نیک کام کرتے رہے اُن کے لئے نعمت کے باغ ہیں');
INSERT INTO "jalandhari" VALUES(31,9,'31:9','ہمیشہ اُن میں رہیں گے۔ خدا کا وعدہ سچا ہے اور وہ غالب حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(31,10,'31:10','اُسی نے آسمانوں کو ستونوں کے بغیر پیدا کیا جیسا کہ تم دیکھتے ہو اور زمین پر پہاڑ (بنا کر) رکھ دیئے تاکہ تم کو ہلا ہلا نہ دے اور اس میں ہر طرح کے جانور پھیلا دیئے۔ اور ہم ہی نے آسمانوں سے پانی نازل کیا پھر (اُس سے) اس میں ہر قسم کی نفیس چیزیں اُگائیں');
INSERT INTO "jalandhari" VALUES(31,11,'31:11','یہ تو خدا کی پیدائش ہے تو مجھے دکھاؤ کہ خدا کے سوا جو لوگ ہیں اُنہوں نے کیا پیدا کیا ہے؟ حقیقت یہ ہے کہ یہ ظالم صریح گمراہی میں ہیں');
INSERT INTO "jalandhari" VALUES(31,12,'31:12','اور ہم نے لقمان کو دانائی بخشی۔ کہ خدا کا شکر کرو۔ اور جو شخص شکر کرتا ہے تو اپنے ہی فائدے کے لئے شکر کرتا ہے۔ اور جو ناشکری کرتا ہے تو خدا بھی بےپروا اور سزاوار حمد (وثنا) ہے');
INSERT INTO "jalandhari" VALUES(31,13,'31:13','اور (اُس وقت کو یاد کرو) جب لقمان نے اپنے بیٹے کو نصیحت کرتے ہوئے کہا کہ بیٹا خدا کے ساتھ شرک نہ کرنا۔ شرک تو بڑا (بھاری) ظلم ہے');
INSERT INTO "jalandhari" VALUES(31,14,'31:14','اور ہم نے انسان کو جسے اُس کی ماں تکلیف پر تکلیف سہہ کر پیٹ میں اُٹھائے رکھتی ہے (پھر اس کو دودھ پلاتی ہے) اور( آخرکار) دو برس میں اس کا دودھ چھڑانا ہوتا ہے (اپنے نیز) اس کے ماں باپ کے بارے میں تاکید کی ہے کہ میرا بھی شکر کرتا رہ اور اپنے ماں باپ کا بھی (کہ تم کو) میری ہی طرف لوٹ کر آنا ہے');
INSERT INTO "jalandhari" VALUES(31,15,'31:15','اور اگر وہ تیرے درپے ہوں کہ تو میرے ساتھ کسی ایسی چیز کو شریک کرے جس کا تجھے کچھ بھی علم نہیں تو ان کا کہا نہ ماننا۔ ہاں دنیا (کے کاموں) میں ان کا اچھی طرح ساتھ دینا اور جو شخص میری طرف رجوع لائے اس کے رستے پر چلنا پھر تم کو میری طرف لوٹ کر آنا ہے۔ تو جو کام تم کرتے رہے میں سب سے تم کو آگاہ کروں گا');
INSERT INTO "jalandhari" VALUES(31,16,'31:16','( لقمان نے یہ بھی کہا کہ) بیٹا اگر کوئی عمل (بالفرض) رائی کے دانے کے برابر بھی (چھوٹا) ہو اور ہو بھی کسی پتھر کے اندر یا آسمانوں میں (مخفی ہو) یا زمین میں۔ خدا اُس کو قیامت کے دن لاموجود کرے گا۔ کچھ شک نہیں کہ خدا باریک بین (اور) خبردار ہے');
INSERT INTO "jalandhari" VALUES(31,17,'31:17','بیٹا نماز کی پابندی رکھنا اور (لوگوں کو) اچھے کاموں کے کرنے کا امر اور بری باتوں سے منع کرتے رہنا اور جو مصیبت تجھ پر واقع ہو اس پر صبر کرنا۔ بیشک یہ بڑی ہمت کے کام ہیں');
INSERT INTO "jalandhari" VALUES(31,18,'31:18','اور (ازراہ غرور) لوگوں سے گال نہ پھلانا اور زمین میں اکڑ کر نہ چلنا۔ کہ خدا کسی اترانے والے خود پسند کو پسند نہیں کرتا');
INSERT INTO "jalandhari" VALUES(31,19,'31:19','اور اپنی چال میں اعتدال کئے رہنا اور (بولتے وقت) آواز نیچی رکھنا کیونکہ (اُونچی آواز گدھوں کی ہے اور کچھ شک نہیں کہ) سب آوازوں سے بُری آواز گدھوں کی ہے');
INSERT INTO "jalandhari" VALUES(31,20,'31:20','کیا تم نے نہیں دیکھا کہ جو کچھ آسمانوں میں اور جو کچھ زمین میں ہے سب کو خدا نے تمہارے قابو میں کر دیا ہے اور تم پر اپنی ظاہری اور باطنی نعمتیں پوری کردی ہیں۔ اور بعض لوگ ایسے ہیں کہ خدا کے بارے میں جھگڑتے ہیں نہ علم رکھتے ہیں اور نہ ہدایت اور نہ کتاب روشن');
INSERT INTO "jalandhari" VALUES(31,21,'31:21','اور جب اُن سے کہا جاتا ہے کہ جو (کتاب) خدا نے نازل فرمائی ہے اُس کی پیروی کرو۔ تو کہتے ہیں کہ ہم تو اسی کی پیروی کریں گے جس پر اپنے باپ دادا کو پایا۔ بھلا اگرچہ شیطان ان کو دوزخ کے عذاب کی طرف بلاتا ہو (تب بھی؟)');
INSERT INTO "jalandhari" VALUES(31,22,'31:22','اور جو شخص اپنے تئیں خدا کا فرمانبردار کردے اور نیکوکار بھی ہو تو اُس نے مضبوط دستاویز ہاتھ میں لے لی۔ اور (سب)کاموں کا انجام خدا ہی کی طرف ہے');
INSERT INTO "jalandhari" VALUES(31,23,'31:23','اور جو کفر کرے تو اُس کا کفر تمہیں غمناک نہ کردے ان کو ہماری طرف لوٹ کر آنا ہے پھر جو کام وہ کیا کرتے تھے ہم اُن کو جتا ئیں گے۔ بیشک خدا دلوں کی باتوں سے واقف ہے');
INSERT INTO "jalandhari" VALUES(31,24,'31:24','ہم اُن کو تھوڑا سا فائدہ پہنچائیں گے پھر عذاب شدید کی طرف مجبور کر کے لیجائیں گے');
INSERT INTO "jalandhari" VALUES(31,25,'31:25','اور اگر تم اُن سے پوچھو کہ آسمانوں اور زمین کو کس نے پیدا کیا تو بول اُٹھیں گے کہ خدا نے۔ کہہ دو کہ خدا کا شکر ہے لیکن ان میں اکثر سمجھ نہیں رکھتے');
INSERT INTO "jalandhari" VALUES(31,26,'31:26','جو کچھ آسمانوں اور زمین میں ہے (سب) خدا ہی کا ہے۔ بیشک خدا بےپروا اور سزا وارِ حمد (وثنا) ہے');
INSERT INTO "jalandhari" VALUES(31,27,'31:27','اور اگر یوں ہو کہ زمین میں جتنے درخت ہیں (سب کے سب) قلم ہوں اور سمندر (کا تمام پانی) سیاہی ہو (اور) اس کے بعد سات سمندر اور (سیاہی ہو جائیں) تو خدا کی باتیں (یعنی اس کی صفتیں) ختم نہ ہوں۔ بیشک خدا غالب حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(31,28,'31:28','(خدا کو) تمہارا پیدا کرنا اور جِلا اُٹھانا ایک شخص (کے پیدا کرنے اور جلا اُٹھانے) کی طرح ہے۔ بیشک خدا سننے والا دیکھنے والا ہے');
INSERT INTO "jalandhari" VALUES(31,29,'31:29','کیا تم نے نہیں دیکھا کہ خدا ہی رات کو دن میں داخل کرتا ہے اور (وہی) دن کو رات میں داخل کرتا ہے اور اُسی نے سورج اور چاند کو (تمہارے) زیر فرمان کر رکھا ہے۔ ہر ایک ایک وقتِ مقرر تک چل رہا ہے اور یہ کہ خدا تمہارے سب اعمال سے خبردار ہے');
INSERT INTO "jalandhari" VALUES(31,30,'31:30','یہ اس لئے کہ خدا کی ذات برحق ہے اور جن کو یہ لوگ خدا کے سوا پکارتے ہیں وہ لغو ہیں اور یہ کہ خدا ہی عالی رتبہ اور گرامی قدر ہے');
INSERT INTO "jalandhari" VALUES(31,31,'31:31','کیا تم نے نہیں دیکھا کہ خدا ہی کی مہربانی سے کشتیاں دریا میں چلتی ہیں۔ تاکہ وہ تم کو اپنی کچھ نشانیاں دکھائے۔ بیشک اس میں ہر صبر کرنے والے (اور) شکر کرنے والے کے لئے نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(31,32,'31:32','اور جب اُن پر (دریا کی) لہریں سائبانوں کی طرح چھا جاتی ہیں تو خدا کو پکارنے (اور) خالص اس کی عبادت کرنے لگتے ہیں پھر جب وہ اُن کو نجات دے کر خشکی پر پہنچا دیتا ہے تو بعض ہی انصاف پر قائم رہتے ہیں۔ اور ہماری نشانیوں سے وہی انکار کرتے ہیں جو عہد شکن اور ناشکرے ہیں');
INSERT INTO "jalandhari" VALUES(31,33,'31:33','لوگو اپنے پروردگار سے ڈرو اور اُس دن کا خوف کرو کہ نہ تو باپ اپنے بیٹے کے کچھ کام آئے۔ اور نہ بیٹا باپ کے کچھ کام آسکے۔ بیشک خدا کا وعدہ سچا ہے پس دنیا کی زندگی تم کو دھوکے میں نہ ڈال دے۔ اور نہ فریب دینے والا (شیطان) تمہیں خدا کے بارے میں کسی طرح کا فریب دے');
INSERT INTO "jalandhari" VALUES(31,34,'31:34','خدا ہی کو قیامت کا علم ہے اور وہی مینھہ برساتا ہے۔ اور وہی (حاملہ کے) پیٹ کی چیزوں کو جانتا ہے (کہ نر ہے یا مادہ) اور کوئی شخص نہیں جانتا کہ وہ کل کیا کام کرے گا۔ اور کوئی متنفس نہیں جانتا کہ کس سرزمین میں اُسے موت آئے گی بیشک خدا ہی جاننے والا (اور) خبردار ہے');
INSERT INTO "jalandhari" VALUES(32,1,'32:1','الٓمٓ');
INSERT INTO "jalandhari" VALUES(32,2,'32:2','اس میں کچھ شک نہیں کہ اس کتاب کا نازل کیا جانا تمام جہان کے پروردگار کی طرف سے ہے');
INSERT INTO "jalandhari" VALUES(32,3,'32:3','کیا یہ لوگ یہ کہتے ہیں کہ پیغمبر نے اس کو از خود بنا لیا ہے (نہیں) بلکہ وہ تمہارے پروردگار کی طرف سے برحق ہے تاکہ تم ان لوگوں کو ہدایت کرو جن کے پاس تم سے پہلے کوئی ہدایت کرنے والا نہیں آیا تاکہ یہ رستے پر چلیں');
INSERT INTO "jalandhari" VALUES(32,4,'32:4','خدا ہی تو ہے جس نے آسمانوں اور زمین کو اور جو چیزیں ان دونوں میں ہیں سب کو چھ دن میں پیدا کیا پھر عرش پر جا ٹھہرا۔ اس کے سوا نہ تمہارا کوئی دوست ہے اور نہ سفارش کرنے والا۔ کیا تم نصیحت نہیں پکڑتے؟');
INSERT INTO "jalandhari" VALUES(32,5,'32:5','وہی آسمان سے زمین تک (کے) ہر کام کا انتظام کرتا ہے۔ پھر وہ ایک روز جس کی مقدار تمہارے شمار کے مطابق ہزار برس ہوگی۔ اس کی طرف صعود (اور رجوع) کرے گا');
INSERT INTO "jalandhari" VALUES(32,6,'32:6','یہی تو پوشیدہ اور ظاہر کا جاننے والا (اور) غالب اور رحم والا (خدا) ہے');
INSERT INTO "jalandhari" VALUES(32,7,'32:7','جس نے ہر چیز کو بہت اچھی طرح بنایا (یعنی) اس کو پیدا کیا۔ اور انسان کی پیدائش کو مٹی سے شروع کیا');
INSERT INTO "jalandhari" VALUES(32,8,'32:8','پھر اس کی نسل خلاصے سے (یعنی) حقیر پانی سے پیدا کی');
INSERT INTO "jalandhari" VALUES(32,9,'32:9','پھر اُس کو درست کیا پھر اس میں اپنی( طرف سے) روح پھونکی اور تمہارے کان اور آنکھیں اور دل بنائے مگر تم بہت کم شکر کرتے ہو');
INSERT INTO "jalandhari" VALUES(32,10,'32:10','اور کہنے لگے کہ جب ہم زمین میں ملیامیٹ ہوجائیں گے تو کیا ازسرنو پیدا ہوں گے۔ حقیقت یہ ہے کہ یہ لوگ اپنے پروردگار کے سامنے جانے ہی کے قائل نہیں');
INSERT INTO "jalandhari" VALUES(32,11,'32:11','کہہ دو کہ موت کا فرشتہ جو تم پر مقرر کیا گیا ہے تمہاری روحیں قبض کر لیتا ہے پھر تم اپنے پروردگار کی طرف لوٹائے جاؤ گے');
INSERT INTO "jalandhari" VALUES(32,12,'32:12','اور تم (تعجب کرو) جب دیکھو کہ گنہگار اپنے پروردگار کے سامنے سرجھکائے ہوں گے (اور کہیں گے کہ) اے ہمارے پروردگار ہم نے دیکھ لیا اور سن لیا تو ہم کو (دنیا میں) واپس بھیج دے کہ نیک عمل کریں بیشک ہم یقین کرنے والے ہیں');
INSERT INTO "jalandhari" VALUES(32,13,'32:13','اور اگر ہم چاہتے تو ہر شخص کو ہدایت دے دیتے۔ لیکن میری طرف سے یہ بات قرار پاچکی ہے کہ میں دوزخ کو جنوں اور انسانوں سب سے بھردوں گا');
INSERT INTO "jalandhari" VALUES(32,14,'32:14','سو (اب آگ کے) مزے چکھو اس لئے کہ تم نے اُس دن کے آنے کو بھلا رکھا تھا (آج) ہم بھی تمہیں بھلا دیں گے اور جو کام تم کرتے تھے اُن کی سزا میں ہمیشہ کے عذاب کے مزے چکھتے رہو');
INSERT INTO "jalandhari" VALUES(32,15,'32:15','ہماری آیتوں پر تو وہی لوگ ایمان لاتے ہیں کہ جب اُن کو اُن سے نصیحت کی جاتی ہے تو سجدے میں گرپڑتے اور اپنے پروردگار کی تعریف کے ساتھ تسبیح کرتے ہیں اور غرور نہیں کرتے');
INSERT INTO "jalandhari" VALUES(32,16,'32:16','اُن کے پہلو بچھونوں سے الگ رہتے ہیں (اور) وہ اپنے پروردگار کو خوف اور اُمید سے پکارتے اور جو (مال) ہم نے اُن کو دیا ہے اس میں سے خرچ کرتے ہیں');
INSERT INTO "jalandhari" VALUES(32,17,'32:17','کوئی متنفس نہیں جانتا کہ اُن کے لئے کیسی آنکھوں کی ٹھنڈک چھپا کر رکھی گئی ہے۔ یہ ان اعمال کا صلہ ہے جو وہ کرتے تھے');
INSERT INTO "jalandhari" VALUES(32,18,'32:18','بھلا جو مومن ہو وہ اس شخص کی طرح ہوسکتا ہے جو نافرمان ہو؟ دونوں برابر نہیں ہو سکتے');
INSERT INTO "jalandhari" VALUES(32,19,'32:19','جو لوگ ایمان لائے اور نیک عمل کرتے رہے اُن کے (رہنے کے) لئے باغ ہیں یہ مہمانی اُن کاموں کی جزا ہے جو وہ کرتے تھے');
INSERT INTO "jalandhari" VALUES(32,20,'32:20','اور جنہوں نے نافرمانی کی اُن کے رہنے کے لئے دوزخ ہے جب چاہیں گے کہ اس میں سے نکل جائیں تو اس میں لوٹا دیئے جائیں گے۔ اور اُن سے کہا جائے گا کہ جس دوزخ کے عذاب کو تم جھوٹ سمجھتے تھے اس کے مزے چکھو');
INSERT INTO "jalandhari" VALUES(32,21,'32:21','اور ہم اُن کو (قیامت کے) بڑے عذاب کے سوا عذاب دنیا کا بھی مزہ چکھائیں گے۔ شاید (ہماری طرف) لوٹ آئیں');
INSERT INTO "jalandhari" VALUES(32,22,'32:22','اور اس شخص سے بڑھ کر ظالم کون جس کو اس کے پروردگار کی آیتوں سے نصیحت کی جائے تو وہ اُن سے منہ پھیر لے۔ ہم گنہگاروں سے ضرور بدلہ لینے والے ہیں');
INSERT INTO "jalandhari" VALUES(32,23,'32:23','اور ہم نے موسٰی کو کتاب دی تو تم اُن کے ملنے سے شک میں نہ ہونا اور ہم نے اس (کتاب) کو (یا موسٰی کو) بنی اسرائیل کے لئے( ذریعہ) ہدایت بنایا');
INSERT INTO "jalandhari" VALUES(32,24,'32:24','اور ان میں سے ہم نے پیشوا بنائے تھے جو ہمارے حکم سے ہدایت کیا کرتے تھے۔ جب وہ صبر کرتے تھے اور وہ ہماری آیتوں پر یقین رکھتے تھے');
INSERT INTO "jalandhari" VALUES(32,25,'32:25','بلاشبہ تمہارا پروردگار ان میں جن باتوں میں وہ اختلاف کرتے تھے۔ قیامت کے روز فیصلہ کر دے گا');
INSERT INTO "jalandhari" VALUES(32,26,'32:26','کیا اُن کو اس (امر )سے ہدایت نہ ہوئی کہ ہم نے اُن سے پہلے بہت سی اُمتوں کو جن کے مقامات سکونت میں یہ چلتے پھرتے ہیں ہلاک کر دیا۔ بیشک اس میں نشانیاں ہیں۔ تو یہ سنتے کیوں نہیں');
INSERT INTO "jalandhari" VALUES(32,27,'32:27','کیا اُنہوں نے نہیں دیکھا کہ ہم بنجر زمین کی طرف پانی رواں کرتے ہیں پھر اس سے کھیتی پیدا کرتے ہیں جس میں سے ان کے چوپائے بھی کھاتے ہیں اور وہ خود بھی (کھاتے ہیں) تو یہ دیکھتے کیوں نہیں۔');
INSERT INTO "jalandhari" VALUES(32,28,'32:28','اور کہتے ہیں اگر تم سچے ہو تو یہ فیصلہ کب ہوگا؟');
INSERT INTO "jalandhari" VALUES(32,29,'32:29','کہہ دو کہ فیصلے کے دن کافروں کو ان کا ایمان لانا کچھ بھی فائدہ نہ دے گا اور نہ اُن کو مہلت دی جائے گی');
INSERT INTO "jalandhari" VALUES(32,30,'32:30','تو اُن سے منہ پھیر لو اور انتظار کرو یہ بھی انتظار کر رہے ہیں');
INSERT INTO "jalandhari" VALUES(33,1,'33:1','اے پیغمبر خدا سے ڈرتے رہنا اور کافروں اور منافقوں کا کہا نہ ماننا۔ بےشک خدا جاننے والا اور حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(33,2,'33:2','اور جو (کتاب) تم کو تمہارے پروردگار کی طرف سے وحی کی جاتی ہے اُسی کی پیروی کئے جانا۔ بےشک خدا تمہارے سب عملوں سے خبردار ہے');
INSERT INTO "jalandhari" VALUES(33,3,'33:3','اور خدا پر بھروسہ رکھنا۔ اور خدا ہی کارساز کافی ہے');
INSERT INTO "jalandhari" VALUES(33,4,'33:4','خدا نے کسی آدمی کے پہلو میں دو دل نہیں بنائے۔ اور نہ تمہاری عورتوں کو جن کو تم ماں کہہ بیٹھتے ہو تمہاری ماں بنایا اور نہ تمہارے لے پالکوں کو تمہارے بیٹے بنایا۔ یہ سب تمہارے منہ کی باتیں ہیں۔ اور خدا تو سچی بات فرماتا ہے اور وہی سیدھا رستہ دکھاتا ہے');
INSERT INTO "jalandhari" VALUES(33,5,'33:5','مومنو! لےپالکوں کو اُن کے (اصلی) باپوں کے نام سے پکارا کرو۔ کہ خدا کے نزدیک یہی بات درست ہے۔ اگر تم کو اُن کے باپوں کے نام معلوم نہ ہوں تو دین میں وہ تمہارے بھائی اور دوست ہیں اور جو بات تم سے غلطی سے ہوگئی ہو اس میں تم پر کچھ گناہ نہیں۔ لیکن جو قصد دلی سے کرو (اس پر مواخذہ ہے) اور خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(33,6,'33:6','پیغمبر مومنوں پر اُن کی جانوں سے بھی زیادہ حق رکھتے ہیں اور پیغمبر کی بیویاں اُن کی مائیں ہیں۔ اور رشتہ دار آپس میں کتاب الله کے رُو سے مسلمانوں اور مہاجروں سے ایک دوسرے (کے ترکے) کے زیادہ حقدار ہیں۔ مگر یہ کہ تم اپنے دوستوں سے احسان کرنا چاہو۔ (تو اور بات ہے)۔ یہ حکم کتاب یعنی (قرآن) میں لکھ دیا گیا ہے');
INSERT INTO "jalandhari" VALUES(33,7,'33:7','اور جب ہم نے پیغمبروں سے عہد لیا اور تم سے نوح سے اور ابراہیم سے اور موسیٰ سے اور مریم کے بیٹے عیسیٰ سے۔ اور عہد بھی اُن سے پکّا لیا');
INSERT INTO "jalandhari" VALUES(33,8,'33:8','تاکہ سچ کہنے والوں سے اُن کی سچائی کے بارے میں دریافت کرے اور اس نے کافروں کے لئے دکھ دینے والا عذاب تیار کر رکھا ہے');
INSERT INTO "jalandhari" VALUES(33,9,'33:9','مومنو خدا کی اُس مہربانی کو یاد کرو جو (اُس نے) تم پر (اُس وقت کی) جب فوجیں تم پر (حملہ کرنے کو) آئیں۔ تو ہم نے اُن پر ہوا بھیجی اور ایسے لشکر (نازل کئے) جن کو تم دیکھ نہیں سکتے تھے۔ اور جو کام تم کرتے ہو خدا اُن کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(33,10,'33:10','جب وہ تمہارے اُوپر اور نیچے کی طرف سے تم پر چڑھ آئے اور جب آنکھیں پھر گئیں اور دل (مارے دہشت کے) گلوں تک پہنچ گئے اور تم خدا کی نسبت طرح طرح کے گمان کرنے لگے');
INSERT INTO "jalandhari" VALUES(33,11,'33:11','وہاں مومن آزمائے گئے اور سخت طور پر ہلائے گئے');
INSERT INTO "jalandhari" VALUES(33,12,'33:12','اور جب منافق اور وہ لوگ جن کے دلوں میں بیماری ہے کہنے لگے کہ خدا اور اس کے رسول نے ہم سے محض دھوکے کا وعدہ کیا تھا');
INSERT INTO "jalandhari" VALUES(33,13,'33:13','اور جب اُن میں سے ایک جماعت کہتی تھی کہ اے اہل مدینہ (یہاں) تمہارے ٹھہرنے کا مقام نہیں تو لوٹ چلو۔ اور ایک گروہ ان میں سے پیغمبر سے اجازت مانگنے اور کہنے لگا کہ ہمارے گھر کھلے پڑے ہیں حالانکہ وہ کھلے نہیں تھے۔ وہ تو صرف بھاگنا چاہتے تھے');
INSERT INTO "jalandhari" VALUES(33,14,'33:14','اور اگر (فوجیں) اطراف مدینہ سے ان پر آ داخل ہوں پھر اُن سے خانہ جنگی کے لئے کہا جائے تو (فوراً) کرنے لگیں اور اس کے لئے بہت ہی کم توقف کریں');
INSERT INTO "jalandhari" VALUES(33,15,'33:15','حالانکہ پہلے خدا سے اقرار کر چکے تھے کہ پیٹھ نہیں پھریں گے۔ اور خدا سے (جو) اقرار (کیا جاتا ہے اُس کی) ضرور پرسش ہوگی');
INSERT INTO "jalandhari" VALUES(33,16,'33:16','کہہ دو کہ اگر تم مرنے یا مارے سے بھاگتے ہو تو بھاگنا تم کو فائدہ نہیں دے گا اور اس وقت تم بہت ہی کم فائدہ اٹھاؤ گے');
INSERT INTO "jalandhari" VALUES(33,17,'33:17','کہہ دو کہ اگر خدا تمہارے ساتھ برائی کا ارادہ کرے تو کون تم کو اس سے بچا سکتا ہے یا اگر تم پر مہربانی کرنی چاہے تو (کون اس کو ہٹا سکتا ہے) اور یہ لوگ خدا کے سوا کسی کو نہ اپنا دوست پائیں گے اور نہ مددگار');
INSERT INTO "jalandhari" VALUES(33,18,'33:18','خدا تم میں سے ان لوگوں کو بھی جانتا ہے جو (لوگوں کو) منع کرتے ہیں اور اپنے بھائیوں سے کہتے ہیں کہ ہمارے پاس چلے آؤ۔ اور لڑائی میں نہیں آتے مگر کم');
INSERT INTO "jalandhari" VALUES(33,19,'33:19','(یہ اس لئے کہ) تمہارے بارے میں بخل کرتے ہیں۔ پھر جب ڈر (کا وقت) آئے تو تم ان کو دیکھو کہ تمہاری طرف دیکھ رہے ہیں (اور) اُن کی آنکھیں (اسی طرح) پھر رہی ہیں جیسے کسی کو موت سے غشی آرہی ہو۔ پھر جب خوف جاتا رہے تو تیز زبانوں کے ساتھ تمہارے بارے میں زبان درازی کریں اور مال میں بخل کریں۔ یہ لوگ (حقیقت میں) ایمان لائے ہی نہ تھے تو خدا نے ان کے اعمال برباد کر دیئے۔ اور یہ خدا کو آسان تھا');
INSERT INTO "jalandhari" VALUES(33,20,'33:20','(خوف کے سبب) خیال کرتے ہیں کہ فوجیں نہیں گئیں۔ اور اگر لشکر آجائیں تو تمنا کریں کہ (کاش) گنواروں میں جا رہیں (اور) تمہاری خبر پوچھا کریں۔ اور اگر تمہارے درمیان ہوں تو لڑائی نہ کریں مگر کم');
INSERT INTO "jalandhari" VALUES(33,21,'33:21','تم کو پیغمبر خدا کی پیروی (کرنی) بہتر ہے (یعنی) اس شخص کو جسے خدا (سے ملنے) اور روز قیامت (کے آنے) کی اُمید ہو اور وہ خدا کا ذکر کثرت سے کرتا ہو');
INSERT INTO "jalandhari" VALUES(33,22,'33:22','اور جب مومنوں نے (کافروں کے) لشکر کو دیکھا تو کہنے لگے یہ وہی ہے جس کا خدا اور اس کے پیغمبر نے ہم سے وعدہ کیا تھا اور خدا اور اس کے پیغمبر نے سچ کہا تھا۔ اور اس سے ان کا ایمان اور اطاعت اور زیادہ ہوگئی');
INSERT INTO "jalandhari" VALUES(33,23,'33:23','مومنوں میں کتنے ہی ایسے شخص ہیں کہ جو اقرار اُنہوں نے خدا سے کیا تھا اس کو سچ کر دکھایا۔ تو ان میں بعض ایسے ہیں جو اپنی نذر سے فارغ ہوگئے اور بعض ایسے ہیں کہ انتظار کر رہے ہیں اور اُنہوں نے (اپنے قول کو) ذرا بھی نہیں بدلا');
INSERT INTO "jalandhari" VALUES(33,24,'33:24','تاکہ خدا سچّوں کو اُن کی سچائی کا بدلہ دے اور منافقوں کو چاہے تو عذاب دے اور (چاہے) تو اُن پر مہربانی کرے۔ بےشک خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(33,25,'33:25','اور جو کافر تھے اُن کو خدا نے پھیر دیا وہ اپنے غصے میں (بھرے ہوئے تھے) کچھ بھلائی حاصل نہ کر سکے۔ اور خدا مومنوں کو لڑائی کے بارے میں کافی ہوا۔ اور خدا طاقتور (اور) زبردست ہے');
INSERT INTO "jalandhari" VALUES(33,26,'33:26','اور اہل کتاب میں سے جنہوں نے اُن کی مدد کی تھی اُن کو اُن کے قلعوں سے اُتار دیا اور اُن کے دلوں میں دہشت ڈال دی۔ تو کتنوں کو تم قتل کر دیتے تھے اور کتنوں کو قید کرلیتے تھے');
INSERT INTO "jalandhari" VALUES(33,27,'33:27','اور اُن کی زمین اور ان کے گھروں اور ان کے مال کا اور اس زمین کا جس میں تم نے پاؤں بھی نہیں رکھا تھا تم کو وارث بنا دیا۔ اور خدا ہر چیز پر قدرت رکھتا ہے');
INSERT INTO "jalandhari" VALUES(33,28,'33:28','اے پیغمبر اپنی بیویوں سے کہہ دو کہ اگر تم دنیا کی زندگی اور اس کی زینت وآرائش کی خواستگار ہو تو آؤ میں تمہیں کچھ مال دوں اور اچھی طرح سے رخصت کردوں');
INSERT INTO "jalandhari" VALUES(33,29,'33:29','اور اگر تم خدا اور اس کے پیغمبر اور عاقبت کے گھر (یعنی بہشت) کی طلبگار ہو تو تم میں جو نیکوکاری کرنے والی ہیں اُن کے لئے خدا نے اجر عظیم تیار کر رکھا ہے');
INSERT INTO "jalandhari" VALUES(33,30,'33:30','اے پیغمبر کی بیویو تم میں سے جو کوئی صریح ناشائستہ (الفاظ کہہ کر رسول الله کو ایذا دینے کی) حرکت کرے گی۔ اس کو دونی سزا دی جائے گی۔ اور یہ (بات) خدا کو آسان ہے');
INSERT INTO "jalandhari" VALUES(33,31,'33:31','اور جو تم میں سے خدا اور اس کے رسول کی فرمانبردار رہے گی اور عمل نیک کرے گی۔ اس کو ہم دونا ثواب دیں گے اور اس کے لئے ہم نے عزت کی روزی تیار کر رکھی ہے');
INSERT INTO "jalandhari" VALUES(33,32,'33:32','اے پیغمبر کی بیویو تم اور عورتوں کی طرح نہیں ہو۔ اگر تم پرہیزگار رہنا چاہتی ہو تو کسی (اجنبی شخص سے) نرم نرم باتیں نہ کیا کرو تاکہ وہ شخص جس کے دل میں کسی طرح کا مرض ہے کوئی امید (نہ) پیدا کرے۔ اور ان دستور کے مطابق بات کیا کرو');
INSERT INTO "jalandhari" VALUES(33,33,'33:33','اور اپنے گھروں میں ٹھہری رہو اور جس طرح (پہلے) جاہلیت (کے دنوں) میں اظہار تجمل کرتی تھیں اس طرح زینت نہ دکھاؤ۔ اور نماز پڑھتی رہو اور زکوٰة دیتی رہو اور خدا اور اس کے رسول کی فرمانبرداری کرتی رہو۔ اے (پیغمبر کے) اہل بیت خدا چاہتا ہے کہ تم سے ناپاکی (کا میل کچیل) دور کردے اور تمہیں بالکل پاک صاف کردے');
INSERT INTO "jalandhari" VALUES(33,34,'33:34','اور تمہارے گھروں میں جو خدا کی آیتیں پڑھی جاتی ہیں اور حکمت (کی باتیں سنائی جاتی ہیں) ان کو یاد رکھو۔ بےشک خدا باریک بیں اور باخبر ہے');
INSERT INTO "jalandhari" VALUES(33,35,'33:35','(جو لوگ خدا کے آگے سر اطاعت خم کرنے والے ہیں یعنی) مسلمان مرد اور مسلمان عورتیں اور مومن مرد اور مومن عورتیں اور فرماں بردار مرد اور فرماں بردار عورتیں اور راست باز مرد اور راست باز عورتیں اور صبر کرنے والے مرد اور صبر کرنے والی عورتیں اور فروتنی کرنے والے مرد اور فروتنی کرنے والی عورتیں اور خیرات کرنے والے مرد اور اور خیرات کرنے والی عورتیں اور روزے رکھنے والے مرد اور روزے رکھنے والی عورتیں اور اپنی شرمگاہوں کی حفاظت کرنے والے مرد اور حفاظت کرنے والی عورتیں اور خدا کو کثرت سے یاد کرنے والے مرد اور کثرت سے یاد کرنے والی عورتیں۔ کچھ شک نہیں کہ ان کے لئے خدا نے بخشش اور اجر عظیم تیار کر رکھا ہے');
INSERT INTO "jalandhari" VALUES(33,36,'33:36','اور کسی مومن مرد اور مومن عورت کو حق نہیں ہے کہ جب خدا اور اس کا رسول کوئی امر مقرر کردیں تو وہ اس کام میں اپنا بھی کچھ اختیار سمجھیں۔ اور جو کوئی خدا اور اس کے رسول کی نافرمانی کرے وہ صریح گمراہ ہوگیا');
INSERT INTO "jalandhari" VALUES(33,37,'33:37','اور جب تم اس شخص سے جس پر خدا نے احسان کیا اور تم نے بھی احسان کیا (یہ) کہتے تھے کہ اپنی بیوی کو اپنے پاس رہنے دے اور خدا سے ڈر اور تم اپنے دل میں وہ بات پوشیدہ کرتے تھے جس کو خدا ظاہر کرنے والا تھا اور تم لوگوں سے ڈرتے تھے۔ حالانکہ خدا ہی اس کا زیادہ مستحق ہے کہ اس سے ڈرو۔ پھر جب زید نے اس سے (کوئی) حاجت (متعلق) نہ رکھی (یعنی اس کو طلاق دے دی) تو ہم نے تم سے اس کا نکاح کردیا تاکہ مومنوں کے لئے ان کے منہ بولے بیٹوں کی بیویوں (کے ساتھ نکاح کرنے کے بارے) میں جب وہ ان سے اپنی حاجت (متعلق) نہ رکھیں (یعنی طلاق دے دیں) کچھ تنگی نہ رہے۔ اور خدا کا حکم واقع ہو کر رہنے والا تھا');
INSERT INTO "jalandhari" VALUES(33,38,'33:38','پیغمبر پر اس کام میں کچھ تنگی نہیں جو خدا نے ان کے لئے مقرر کردیا۔ اور جو لوگ پہلے گزر چکے ہیں ان میں بھی خدا کا یہی دستور رہا ہے۔ اور خدا کا حکم ٹھیر چکا ہے');
INSERT INTO "jalandhari" VALUES(33,39,'33:39','اور جو خدا کے پیغام (جوں کے توں) پہنچاتے اور اس سے ڈرتے ہیں اور خدا کے سوا کسی سے نہیں ڈرتے تھے۔ اور خدا ہی حساب کرنے کو کافی ہے');
INSERT INTO "jalandhari" VALUES(33,40,'33:40','محمدصلى الله عليه وسلم تمہارے مردوں میں سے کسی کے والد نہیں ہیں بلکہ خدا کے پیغمبر اور نبیوں (کی نبوت) کی مہر (یعنی اس کو ختم کردینے والے) ہیں اور خدا ہر چیز سے واقف ہے');
INSERT INTO "jalandhari" VALUES(33,41,'33:41','اے اہل ایمان خدا کا بہت ذکر کیا کرو');
INSERT INTO "jalandhari" VALUES(33,42,'33:42','اور صبح اور شام اس کی پاکی بیان کرتے رہو');
INSERT INTO "jalandhari" VALUES(33,43,'33:43','وہی تو ہے جو تم پر رحمت بھیجتا ہے اور اس کے فرشتے بھی۔ تاکہ تم کو اندھیروں سے نکال کر روشنی کی طرف لے جائے۔ اور خدا مومنوں پر مہربان ہے');
INSERT INTO "jalandhari" VALUES(33,44,'33:44','جس روز وہ اس سے ملیں گے ان کا تحفہ (خدا کی طرف سے) سلام ہوگا اور اس نے ان کے لئے بڑا ثواب تیار کر رکھا ہے');
INSERT INTO "jalandhari" VALUES(33,45,'33:45','اے پیغمبر ہم نے تم کو گواہی دینے والا اور خوشخبری سنانے اور ڈرانے والا بنا کر بھیجا ہے');
INSERT INTO "jalandhari" VALUES(33,46,'33:46','اور خدا کی طرف بلانے والا اور چراغ روشن');
INSERT INTO "jalandhari" VALUES(33,47,'33:47','اور مومنوں کو خوشخبری سنا دو کہ ان کے لئے خدا کی طرف سے بڑا فضل ہوگا');
INSERT INTO "jalandhari" VALUES(33,48,'33:48','اور کافروں اور منافقوں کا کہا نہ ماننا اور نہ ان کے تکلیف دینے پر نظر کرنا اور خدا پر بھروسہ رکھنا۔ اور خدا ہی کارساز کافی ہے');
INSERT INTO "jalandhari" VALUES(33,49,'33:49','مومنو! جب تم مومن عورتوں سے نکاح کرکے ان کو ہاتھ لگانے (یعنی ان کے پاس جانے) سے پہلے طلاق دے دو تو تم کو کچھ اختیار نہیں کہ ان سے عدت پوری کراؤ۔ ان کو کچھ فائدہ (یعنی خرچ) دے کر اچھی طرح سے رخصت کردو');
INSERT INTO "jalandhari" VALUES(33,50,'33:50','اے پیغمبر ہم نے تمہارے لئے تمہاری بیویاں جن کو تم نے ان کے مہر دے دیئے ہیں حلال کردی ہیں اور تمہاری لونڈیاں جو خدا نے تم کو (کفار سے بطور مال غنیمت) دلوائی ہیں اور تمہارے چچا کی بیٹیاں اور تمہاری پھوپھیوں کی بیٹیاں اور تمہارے ماموؤں کی بیٹیاں اور تمہاری خالاؤں کی بیٹیاں جو تمہارے ساتھ وطن چھوڑ کر آئی ہیں (سب حلال ہیں) اور کوئی مومن عورت اگر اپنے تئیں پیغمبر کو بخش دے (یعنی مہر لینے کے بغیر نکاح میں آنا چاہے) بشرطیکہ پیغمبر بھی ان سے نکاح کرنا چاہیں (وہ بھی حلال ہے لیکن) یہ اجازت (اے محمدصلى الله عليه وسلم) خاص تم ہی کو ہے سب مسلمانوں کو نہیں۔ ہم نے ان کی بیویوں اور لونڈیوں کے بارے میں جو (مہر واجب الادا) مقرر کردیا ہے ہم کو معلوم ہے (یہ) اس لئے (کیا گیا ہے) کہ تم پر کسی طرح کی تنگی نہ رہے۔ اور خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(33,51,'33:51','(اور تم کو یہ بھی اختیار ہے کہ) جس بیوی کو چاہو علیحدہ رکھو اور جسے چاہو اپنے پاس رکھو۔ اور جس کو تم نے علیحدہ کردیا ہو اگر اس کو پھر اپنے پاس طلب کرلو تو تم پر کچھ گناہ نہیں۔ یہ (اجازت) اس لئے ہے کہ ان کی آنکھیں ٹھنڈی رہیں اور وہ غمناک نہ ہوں اور جو کچھ تم ان کو دو۔ اسے لے کر سب خوش رہیں۔ اور جو کچھ تمہارے دلوں میں ہے خدا اسے جانتا ہے۔ اور خدا جاننے والا اور بردبار ہے');
INSERT INTO "jalandhari" VALUES(33,52,'33:52','(اے پیغمبر) ان کے سوا اور عورتیں تم کو جائز نہیں اور نہ یہ کہ ان بیویوں کو چھوڑ کر اور بیویاں کرو خواہ ان کا حسن تم کو (کیسا ہی) اچھا لگے مگر وہ جو تمہارے ہاتھ کا مال ہے (یعنی لونڈیوں کے بارے میں تم کو اختیار ہے) اور خدا ہر چیز پر نگاہ رکھتا ہے');
INSERT INTO "jalandhari" VALUES(33,53,'33:53','مومنو پیغمبر کے گھروں میں نہ جایا کرو مگر اس صورت میں کہ تم کو کھانے کے لئے اجازت دی جائے اور اس کے پکنے کا انتظار بھی نہ کرنا پڑے۔ لیکن جب تمہاری دعوت کی جائے تو جاؤ اور جب کھانا کھاچکو تو چل دو اور باتوں میں جی لگا کر نہ بیٹھ رہو۔ یہ بات پیغمبر کو ایذا دیتی ہے۔ اور وہ تم سے شرم کرتے ہیں (اور کہتے نہیں ہیں) لیکن خدا سچی بات کے کہنے سے شرم نہیں کرتا۔ اور جب پیغمبروں کی بیویوں سے کوئی سامان مانگو تو پردے کے باہر مانگو۔ یہ تمہارے اور ان کے دونوں کے دلوں کے لئے بہت پاکیزگی کی بات ہے۔ اور تم کو یہ شایاں نہیں کہ پیغمبر خدا کو تکلیف دو اور نہ یہ کہ ان کی بیویوں سے کبھی ان کے بعد نکاح کرو۔ بےشک یہ خدا کے نزدیک بڑا (گناہ کا کام) ہے');
INSERT INTO "jalandhari" VALUES(33,54,'33:54','اگر تم کسی چیز کو ظاہر کرو یا اس کو مخفی رکھو تو (یاد رکھو کہ) خدا ہر چیز سے باخبر ہے');
INSERT INTO "jalandhari" VALUES(33,55,'33:55','عورتوں پر اپنے باپوں سے (پردہ نہ کرنے میں) کچھ گناہ نہیں اور نہ اپنے بیٹوں سے اور نہ اپنے بھائیوں سے اور نہ اپنے بھتیجوں سے اور نہ اپنے بھانجوں سے نہ اپنی (قسم کی) عورتوں سے اور نہ لونڈیوں سے۔ اور (اے عورتو) خدا سے ڈرتی رہو۔ بےشک خدا ہر چیز سے واقف ہے');
INSERT INTO "jalandhari" VALUES(33,56,'33:56','خدا اور اس کے فرشتے پیغمبر پر درود بھیجتے ہیں۔ مومنو تم بھی ان پر دُرود اور سلام بھیجا کرو');
INSERT INTO "jalandhari" VALUES(33,57,'33:57','جو لوگ خدا اور اس کے پیغمبر کو رنج پہنچاتے ہیں ان پر خدا دنیا اور آخرت میں لعنت کرتا ہے اور ان کے لئے اس نے ذلیل کرنے والا عذاب تیار کر رکھا ہے');
INSERT INTO "jalandhari" VALUES(33,58,'33:58','اور جو لوگ مومن مردوں اور مومن عورتوں کو ایسے کام (کی تہمت سے) جو انہوں نے نہ کیا ہو ایذا دیں تو انہوں نے بہتان اور صریح گناہ کا بوجھ اپنے سر پر رکھا');
INSERT INTO "jalandhari" VALUES(33,59,'33:59','اے پیغمبر اپنی بیویوں اور بیٹیوں اور مسلمانوں کی عورتوں سے کہہ دو کہ (باہر نکلا کریں تو) اپنے (مونہوں) پر چادر لٹکا (کر گھونگھٹ نکال) لیا کریں۔ یہ امر ان کے لئے موجب شناخت (وامتیاز) ہوگا تو کوئی ان کو ایذا نہ دے گا۔ اور خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(33,60,'33:60','اگر منافق اور وہ لوگ جن کے دلوں میں مرض ہے اور جو مدینے (کے شہر میں) بری بری خبریں اُڑایا کرتے ہیں (اپنے کردار) سے باز نہ آئیں گے تو ہم تم کو ان کے پیچھے لگا دیں گے پھر وہاں تمہارے پڑوس میں نہ رہ سکیں گے مگر تھوڑے دن');
INSERT INTO "jalandhari" VALUES(33,61,'33:61','(وہ بھی) پھٹکارے ہوئے۔ جہاں پائے گئے پکڑے گئے اور جان سے مار ڈالے گئے');
INSERT INTO "jalandhari" VALUES(33,62,'33:62','جو لوگ پہلے گزر چکے ہیں ان کے بارے میں بھی خدا کی یہی عادت رہی ہے۔ اور تم خدا کی عادت میں تغیر وتبدل نہ پاؤ گے');
INSERT INTO "jalandhari" VALUES(33,63,'33:63','لوگ تم سے قیامت کی نسبت دریافت کرتے ہیں (کہ کب آئے گی) کہہ دو کہ اس کا علم خدا ہی کو ہے۔ اور تمہیں کیا معلوم ہے شاید قیامت قریب ہی آگئی ہو');
INSERT INTO "jalandhari" VALUES(33,64,'33:64','بےشک خدا نے کافروں پر لعنت کی ہے اور ان کے لئے (جہنم کی) آگ تیار کر رکھی ہے');
INSERT INTO "jalandhari" VALUES(33,65,'33:65','اس میں ابدا لآباد رہیں گے۔ نہ کسی کو دوست پائیں گے اور نہ مددگار');
INSERT INTO "jalandhari" VALUES(33,66,'33:66','جس دن ان کے منہ آگ میں الٹائے جائیں گے کہیں اے کاش ہم خدا کی فرمانبرداری کرتے اور رسول (خدا) کا حکم مانتے');
INSERT INTO "jalandhari" VALUES(33,67,'33:67','اور کہیں گے کہ اے ہمارے پروردگار ہم نے اپنے سرداروں اور بڑے لوگوں کا کہا مانا تو انہوں نے ہم کو رستے سے گمراہ کردیا');
INSERT INTO "jalandhari" VALUES(33,68,'33:68','اے ہمارے پروردگار ان کو دگنا عذاب دے اور ان پر بڑی لعنت کر');
INSERT INTO "jalandhari" VALUES(33,69,'33:69','مومنو تم ان لوگوں جیسے نہ ہونا جنہوں نے موسیٰ (کو عیب لگا کر) رنج پہنچایا تو خدا نے ان کو بےعیب ثابت کیا۔ اور وہ خدا کے نزدیک آبرو والے تھے');
INSERT INTO "jalandhari" VALUES(33,70,'33:70','مومنو خدا سے ڈرا کرو اور بات سیدھی کہا کرو');
INSERT INTO "jalandhari" VALUES(33,71,'33:71','وہ تمہارے اعمال درست کردے گا اور تمہارے گناہ بخش دے گا۔ اور جو شخص خدا اور اس کے رسول کی فرمانبرداری کرے گا تو بےشک بڑی مراد پائے گا');
INSERT INTO "jalandhari" VALUES(33,72,'33:72','ہم نے (بار) امانت کو آسمانوں اور زمین پر پیش کیا تو انہوں نے اس کے اٹھانے سے انکار کیا اور اس سے ڈر گئے۔ اور انسان نے اس کو اٹھا لیا۔ بےشک وہ ظالم اور جاہل تھا');
INSERT INTO "jalandhari" VALUES(33,73,'33:73','تاکہ خدا منافق مردوں اور منافق عورتوں اور مشرک مردوں اور مشرک عورتوں کو عذاب دے اور خدا مومن مردوں اور مومن عورتوں پر مہربانی کرے۔ اور خدا تو بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(34,1,'34:1','سب تعریف خدا ہی کو (سزاوار) ہے (جو سب چیزوں کا مالک ہے یعنی) وہ کہ جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے سب اسی کا ہے اور آخرت میں بھی اسی کی تعریف ہے۔ اور وہ حکمت والا خبردار ہے');
INSERT INTO "jalandhari" VALUES(34,2,'34:2','جو کچھ زمین میں داخل ہوتا ہے اور جو اس میں سے نکلتا ہے اور جو آسمان سے اُترتا ہے اور جو اس پر چڑھتا ہے سب اس کو معلوم ہے۔ اور وہ مہربان (اور) بخشنے والا ہے');
INSERT INTO "jalandhari" VALUES(34,3,'34:3','اور کافر کہتے ہیں کہ (قیامت کی) گھڑی ہم پر نہیں آئے گی۔ کہہ دو کیوں نہیں (آئے گی) میرے پروردگار کی قسم وہ تم پر ضرور آکر رہے گی (وہ پروردگار) غیب کا جاننے والا (ہے) ذرہ بھر چیز بھی اس سے پوشیدہ نہیں (نہ) آسمانوں میں اور نہ زمین میں اور کوئی چیز ذرے سے چھوٹی یا بڑی ایسی نہیں مگر کتاب روشن میں (لکھی ہوئی) ہے');
INSERT INTO "jalandhari" VALUES(34,4,'34:4','اس لئے کہ جو لوگ ایمان لائے اور عمل نیک کرتے رہے ان کو بدلہ دے۔ یہی ہیں جن کے لئے بخشش اور عزت کی روزی ہے');
INSERT INTO "jalandhari" VALUES(34,5,'34:5','اور جنہوں نے ہماری آیتوں میں کوشش کی کہ ہمیں ہرا دیں گے۔ ان کے لئے سخت درد دینے والے عذاب کی سزا ہے');
INSERT INTO "jalandhari" VALUES(34,6,'34:6','اور جن لوگوں کو علم دیا گیا ہے وہ جانتے ہیں کہ جو (قرآن) تمہارے پروردگار کی طرف سے تم پر نازل ہوا ہے وہ حق ہے۔ اور (خدائے) غالب اور سزاوار تعریف کا رستہ بتاتا ہے');
INSERT INTO "jalandhari" VALUES(34,7,'34:7','اور کافر کہتے ہیں کہ بھلا ہم تمہیں ایسا آدمی بتائیں جو تمہیں خبر دیتا ہے کہ جب تم (مر کر) بالکل پارہ پارہ ہو جاؤ گے تو نئے سرے سے پیدا ہوگے');
INSERT INTO "jalandhari" VALUES(34,8,'34:8','یا تو اس نے خدا پر جھوٹ باندھ لیا ہے۔ یا اسے جنون ہے۔ بات یہ ہے کہ جو لوگ آخرت پر ایمان نہیں رکھتے وہ آفت اور پرلے درجے کی گمراہی میں (مبتلا) ہیں');
INSERT INTO "jalandhari" VALUES(34,9,'34:9','کیا انہوں نے اس کو نہیں دیکھا جو ان کے آگے اور پیچھے ہے یعنی آسمان اور زمین۔ اگر ہم چاہیں تو ان کو زمین میں دھنسا دیں یا ان پر آسمان کے ٹکڑے گرا دیں۔ اس میں ہر بندے کے لئے جو رجوع کرنے والا ہے ایک نشانی ہے');
INSERT INTO "jalandhari" VALUES(34,10,'34:10','اور ہم نے داؤد کو اپنی طرف سے برتری بخشی تھی۔ اے پہاڑو ان کے ساتھ تسبیح کرو اور پرندوں کو (ان کا مسخر کردیا) اور ان کے لئے ہم نے لوہے کو نرم کردیا');
INSERT INTO "jalandhari" VALUES(34,11,'34:11','کہ کشادہ زرہیں بناؤ اور کڑیوں کو اندازے سے جوڑو اور نیک عمل کرو۔ جو عمل تم کرتے ہو میں ان کو دیکھنے والا ہوں');
INSERT INTO "jalandhari" VALUES(34,12,'34:12','اور ہوا کو (ہم نے) سلیمان کا تابع کردیا تھا اس کی صبح کی منزل ایک مہینے کی راہ ہوتی اور شام کی منزل بھی مہینے بھر کی ہوتی۔ اور ان کے لئے ہم نے تانبے کا چشمہ بہا دیا تھا اور جِنّوں میں سے ایسے تھے جو ان کے پروردگار کے حکم سے ان کے آگے کام کرتے تھے۔ اور جو کوئی ان میں سے ہمارے حکم سے پھرے گا اس کو ہم (جہنم کی) آگ کا مزہ چکھائیں گے');
INSERT INTO "jalandhari" VALUES(34,13,'34:13','وہ جو چاہتے یہ ان کے لئے بناتے یعنی قلعے اور مجسمے اور (بڑے بڑے) لگن جیسے تالاب اور دیگیں جو ایک ہی جگہ رکھی رہیں۔ اے داؤد کی اولاد (میرا) شکر کرو اور میرے بندوں میں شکرگزار تھوڑے ہیں');
INSERT INTO "jalandhari" VALUES(34,14,'34:14','پھر جب ہم نے ان کے لئے موت کا حکم صادر کیا تو کسی چیز سے ان کا مرنا معلوم نہ ہوا مگر گھن کے کیڑے سے جو ان کے عصا کو کھاتا رہا۔ جب عصا گر پڑا تب جنوں کو معلوم ہوا (اور کہنے لگے) کہ اگر وہ غیب جانتے ہوتے تو ذلت کی تکلیف میں نہ رہتے');
INSERT INTO "jalandhari" VALUES(34,15,'34:15','(اہل) سبا کے لئے ان کے مقام بودوباش میں ایک نشانی تھی (یعنی) دو باغ (ایک) داہنی طرف اور (ایک) بائیں طرف۔ اپنے پروردگار کا رزق کھاؤ اور اس کا شکر کرو۔ (یہاں تمہارے رہنے کو یہ) پاکیزہ شہر ہے اور (وہاں بخشنے کو) خدائے غفار');
INSERT INTO "jalandhari" VALUES(34,16,'34:16','تو انہوں نے (شکرگزاری سے) منہ پھیر لیا پس ہم نے ان پر زور کا سیلاب چھوڑ دیا اور انہیں ان کے باغوں کے بدلے دو ایسے باغ دیئے جن کے میوے بدمزہ تھے اور جن میں کچھ تو جھاؤ تھا اور تھوڑی سی بیریاں');
INSERT INTO "jalandhari" VALUES(34,17,'34:17','یہ ہم نے ان کی ناشکری کی ان کو سزا دی۔ اور ہم سزا ناشکرے ہی کو دیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(34,18,'34:18','اور ہم نے ان کے اور (شام کی) ان بستیوں کے درمیان جن میں ہم نے برکت دی تھی (ایک دوسرے کے متصل) دیہات بنائے تھے جو سامنے نظر آتے تھے اور ان میں آمد ورفت کا اندازہ مقرر کردیا تھا کہ رات دن بےخوف وخطر چلتے رہو');
INSERT INTO "jalandhari" VALUES(34,19,'34:19','تو انہوں نے دعا کی کہ اے پروردگار ہماری مسافتوں میں بُعد (اور طول پیدا) کردے اور (اس سے) انہوں نے اپنے حق میں ظلم کیا تو ہم نے (انہیں نابود کرکے) ان کے افسانے بنادیئے اور انہیں بالکل منتشر کردیا۔ اس میں ہر صابر وشاکر کے لئے نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(34,20,'34:20','اور شیطان نے ان کے بارے میں اپنا خیال سچ کر دکھایا کہ مومنوں کی ایک جماعت کے سوا وہ اس کے پیچھے چل پڑے');
INSERT INTO "jalandhari" VALUES(34,21,'34:21','اور اس کا ان پر کچھ زور نہ تھا مگر (ہمارا) مقصود یہ تھا کہ جو لوگ آخرت میں شک رکھتے ہیں ان سے ان لوگوں کو جو اس پر ایمان رکھتے تھے متمیز کردیں۔ اور تمہارا پروردگار ہر چیز پر نگہبان ہے');
INSERT INTO "jalandhari" VALUES(34,22,'34:22','کہہ دو کہ جن کو تم خدا کے سوا (معبود) خیال کرتے ہو ان کو بلاؤ۔ وہ آسمانوں اور زمین میں ذرہ بھر چیز کے بھی مالک نہیں ہیں اور نہ ان میں ان کی شرکت ہے اور نہ ان میں سے کوئی خدا کا مددگار ہے');
INSERT INTO "jalandhari" VALUES(34,23,'34:23','اور خدا کے ہاں (کسی کے لئے) سفارش فائدہ نہ دے گی مگر اس کے لئے جس کے بارے میں وہ اجازت بخشے۔ یہاں تک کہ جب ان کے دلوں سے اضطراب دور کردیا جائے گا تو کہیں گے تمہارے پروردگار نے کیا فرمایا ہے۔ (فرشتے) کہیں گے کہ حق (فرمایا ہے) اور وہ عالی رتبہ اور گرامی قدر ہے');
INSERT INTO "jalandhari" VALUES(34,24,'34:24','پوچھو کہ تم کو آسمانوں اور زمین سے کون رزق دیتا ہے؟ کہو کہ خدا اور ہم یا تم (یا تو) سیدھے رستے پر ہیں یا صریح گمراہی میں');
INSERT INTO "jalandhari" VALUES(34,25,'34:25','کہہ دو کہ نہ ہمارے گناہوں کی تم سے پرسش ہوگی اور نہ تمہارے اعمال کی ہم سے پرسش ہوگی');
INSERT INTO "jalandhari" VALUES(34,26,'34:26','کہہ دو کہ ہمارا پروردگار ہم کو جمع کرے گا پھر ہمارے درمیان انصاف کے ساتھ فیصلہ کردے گا۔ اور وہ خوب فیصلہ کرنے والا اور صاحب علم ہے');
INSERT INTO "jalandhari" VALUES(34,27,'34:27','کہو کہ مجھے وہ لوگ تو دکھاؤ جن کو تم نے شریک (خدا) بنا کر اس کے ساتھ ملا رکھا ہے۔ کوئی نہیں بلکہ وہی (اکیلا) خدا غالب (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(34,28,'34:28','اور (اے محمدصلى الله عليه وسلم) ہم نے تم کو تمام لوگوں کے لئے خوشخبری سنانے والا اور ڈرانے والا بنا کر بھیجا ہے لیکن اکثر لوگ نہیں جانتے');
INSERT INTO "jalandhari" VALUES(34,29,'34:29','اور کہتے ہیں اگر تم سچ کہتے ہو تو یہ (قیامت کا) وعدہ کب وقوع میں آئے گا');
INSERT INTO "jalandhari" VALUES(34,30,'34:30','کہہ دو کہ تم سے ایک دن کا وعدہ ہے جس سے نہ ایک گھڑی پیچھے رہوگے اور نہ آگے بڑھو گے');
INSERT INTO "jalandhari" VALUES(34,31,'34:31','اور جو کافر ہیں وہ کہتے ہیں کہ ہم نہ تو اس قرآن کو مانیں گے اور نہ ان (کتابوں) کو جو ان سے پہلے کی ہیں اور کاش (ان) ظالموں کو تم اس وقت دیکھو جب یہ اپنے پروردگار کے سامنے کھڑے ہوں گے اور ایک دوسرے سے ردوکد کر رہے ہوں گے۔ جو لوگ کمزور سمجھے جاتے تھے وہ بڑے لوگوں سے کہیں گے کہ اگر تم نہ ہوتے تو ہم ضرور مومن ہوجاتے');
INSERT INTO "jalandhari" VALUES(34,32,'34:32','بڑے لوگ کمزوروں سے کہیں گے کہ بھلا ہم نے تم کو ہدایت سے جب وہ تمہارے پاس آچکی تھی روکا تھا؟ (نہیں) بلکہ تم ہی گنہگار تھے');
INSERT INTO "jalandhari" VALUES(34,33,'34:33','اور کمزور لوگ بڑے لوگوں سے کہیں گے (نہیں) بلکہ (تمہاری) رات دن کی چالوں نے (ہمیں روک رکھا تھا) جب تم ہم سے کہتے تھے کہ ہم خدا سے کفر کریں اور اس کا شریک بنائیں۔ اور جب وہ عذاب کو دیکھیں گے تو دل میں پشیمان ہوں گے۔ اور ہم کافروں کی گردنوں میں طوق ڈال دیں گے۔ بس جو عمل وہ کرتے تھے ان ہی کا ان کو بدلہ ملے گا');
INSERT INTO "jalandhari" VALUES(34,34,'34:34','اور ہم نے کسی بستی میں کوئی ڈرانے والا نہیں بھیجا مگر وہاں کے خوش حال لوگوں نے کہا کہ جو چیز تم دے کر بھیجے گئے ہو ہم اس کے قائل نہیں');
INSERT INTO "jalandhari" VALUES(34,35,'34:35','اور (یہ بھی) کہنے لگے کہ ہم بہت سا مال اور اولاد رکھتے ہیں اور ہم کو عذاب نہیں ہوگا');
INSERT INTO "jalandhari" VALUES(34,36,'34:36','کہہ دو کہ میرا رب جس کے لئے چاہتا ہے روزی فراخ کردیتا ہے (اور جس کے لئے چاہتا ہے) تنگ کردیتا ہے لیکن اکثر لوگ نہیں جانتے');
INSERT INTO "jalandhari" VALUES(34,37,'34:37','اور تمہارا مال اور اولاد ایسی چیز نہیں کہ تم کو ہمارا مقرب بنا دیں۔ ہاں (ہمارا مقرب وہ ہے) جو ایمان لایا اور عمل نیک کرتا رہا۔ ایسے ہی لوگوں کو ان کے اعمال کے سبب دگنا بدلہ ملے گا اور وہ خاطر جمع سے بالاخانوں میں بیٹھے ہوں گے');
INSERT INTO "jalandhari" VALUES(34,38,'34:38','جو لوگ ہماری آیتوں میں کوشش کرتے ہیں کہ ہمیں ہرا دیں وہ عذاب میں حاضر کئے جائیں گے');
INSERT INTO "jalandhari" VALUES(34,39,'34:39','کہہ دو کہ میرا پروردگار اپنے بندوں میں سے جس کے لئے چاہتا ہے روزی فراخ کردیتا ہے اور (جس کے لئے چاہتا ہے) تنگ کردیتا ہے اور تم جو چیز خرچ کرو گے وہ اس کا (تمہیں) عوض دے گا۔ اور وہ سب سے بہتر رزق دینے والا ہے');
INSERT INTO "jalandhari" VALUES(34,40,'34:40','اور جس دن وہ ان سب کو جمع کرے گا پھر فرشتوں سے فرمائے گا کیا یہ لوگ تم کو پوجا کرتے تھے');
INSERT INTO "jalandhari" VALUES(34,41,'34:41','وہ کہیں گے تو پاک ہے تو ہی ہمارا دوست ہے۔ نہ یہ۔ بلکہ یہ جِنّات کو پوجا کرتے تھے۔ اور اکثر انہی کو مانتے تھے');
INSERT INTO "jalandhari" VALUES(34,42,'34:42','تو آج تم میں سے کوئی کسی کو نفع اور نقصان پہنچانے کا اختیار نہیں رکھتا۔ اور ہم ظالموں سے کہیں گے کہ دوزخ کے عذاب کا جس کو تم جھوٹ سمجھتے تھے مزہ چکھو');
INSERT INTO "jalandhari" VALUES(34,43,'34:43','اور جب ان کو ہماری روشن آیتیں پڑھ کر سنائی جاتی ہیں تو کہتے ہیں یہ ایک (ایسا) شخص ہے جو چاہتا ہے کہ جن چیزوں کی تمہارے باپ دادا پرستش کیا کرتے تھے ان سے تم کو روک دے اور (یہ بھی) کہتے ہیں کہ یہ (قرآن) محض جھوٹ ہے (جو اپنی طرف سے) بنا لیا گیا ہے۔ اور کافروں کے پاس جب حق آیا تو اس کے بارے میں کہنے لگے کہ یہ تو صریح جادو ہے');
INSERT INTO "jalandhari" VALUES(34,44,'34:44','اور ہم نے نہ تو ان (مشرکوں) کو کتابیں دیں جن کو یہ پڑھتے ہیں اور نہ تم سے پہلے ان کی طرف کوئی ڈرانے والا بھیجا مگر انہوں نے تکذیب کی');
INSERT INTO "jalandhari" VALUES(34,45,'34:45','اور جو لوگ ان سے پہلے تھے انہوں نے تکذیب کی تھی اور جو کچھ ہم نے ان کو دیا تھا یہ اس کے دسویں حصے کو بھی نہیں پہنچے تو انہوں نے میرے پیغمبروں کو جھٹلایا۔ سو میرا عذاب کیسا ہوا');
INSERT INTO "jalandhari" VALUES(34,46,'34:46','کہہ دو کہ میں تمہیں صرف ایک بات کی نصیحت کرتا ہوں کہ تم خدا کے لئے دو دو اور اکیلے اکیلے کھڑے ہوجاؤ پھر غور کرو۔ تمہارے رفیق کو سودا نہیں وہ تم کو عذاب سخت (کے آنے) سے پہلے صرف ڈرانے والے ہیں');
INSERT INTO "jalandhari" VALUES(34,47,'34:47','کہہ دو کہ میں نے تم سے کچھ صلہ مانگا ہو تو وہ تم ہی کو (مبارک رہے)۔ میرا صلہ خدا ہی کے ذمے ہے۔ اور وہ ہر چیز سے خبردار ہے');
INSERT INTO "jalandhari" VALUES(34,48,'34:48','کہہ دو کہ میرا پروردگار اوپر سے حق اُتارتا ہے (اور وہ) غیب کی باتوں کا جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(34,49,'34:49','کہہ دو کہ حق آچکا اور (معبود) باطل نہ تو پہلی بار پیدا کرسکتا ہے اور نہ دوبارہ پیدا کرے گا');
INSERT INTO "jalandhari" VALUES(34,50,'34:50','کہہ دو کہ اگر میں گمراہ ہوں تو میری گمراہی کا ضرر مجھی کو ہے۔ اور اگر ہدایت پر ہوں تو یہ اس کا طفیل ہے جو میرا پروردگار میری طرف وحی بھیجتا ہے۔ بےشک وہ سننے والا (اور) نزدیک ہے');
INSERT INTO "jalandhari" VALUES(34,51,'34:51','اور کاش تم دیکھو جب یہ گھبرا جائیں گے تو (عذاب سے) بچ نہیں سکیں گے اور نزدیک ہی سے پکڑ لئے جائیں گے');
INSERT INTO "jalandhari" VALUES(34,52,'34:52','اور کہیں گے کہ ہم اس پر ایمان لے آئے اور (اب) اتنی دور سے ان کا ہاتھ ایمان کے لینے کو کیونکر پہنچ سکتا ہے');
INSERT INTO "jalandhari" VALUES(34,53,'34:53','اور پہلے تو اس سے انکار کرتے رہے اور بن دیکھے دور ہی سے (ظن کے) تیر چلاتے رہے');
INSERT INTO "jalandhari" VALUES(34,54,'34:54','اور ان میں اور ان کی خواہش کی چیزوں میں پردہ حائل کردیا گیا جیسا کہ پہلے ان کے ہم جنسوں سے کیا گیا وہ بھی الجھن میں ڈالنے والے شک میں پڑے ہوئے تھے');
INSERT INTO "jalandhari" VALUES(35,1,'35:1','سب تعریف خدا ہی کو (سزاوار ہے) جو آسمانوں اور زمین کا پیدا کرنے والا (اور) فرشتوں کو قاصد بنانے والا ہے جن کے دو دو اور تین تین اور چار چار پر ہیں۔ وہ (اپنی) مخلوقات میں جو چاہتا ہے بڑھاتا ہے۔ بےشک خدا ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(35,2,'35:2','خدا جو اپنی رحمت (کا دروازہ) کھول دے تو کوئی اس کو بند کرنے والا نہیں۔ اور جو بند کردے تو اس کے بعد کوئی اس کو کھولنے والا نہیں۔ اور وہ غالب حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(35,3,'35:3','لوگو خدا کے جو تم پر احسانات ہیں ان کو یاد کرو۔ کیا خدا کے سوا کوئی اور خالق (اور رازق ہے) جو تم کو آسمان اور زمین سے رزق دے۔ اس کے سوا کوئی معبود نہیں پس تم کہاں بہکے پھرتے ہو؟');
INSERT INTO "jalandhari" VALUES(35,4,'35:4','اور (اے پیغمبر) اگر یہ لوگ تم کو جھٹلائیں تو تم سے پہلے بھی پیغمبر جھٹلائے گئے ہیں۔ اور (سب) کام خدا ہی کی طرف لوٹائے جائیں گے');
INSERT INTO "jalandhari" VALUES(35,5,'35:5','لوگو خدا کا وعدہ سچا ہے۔ تو تم کو دنیا کی زندگی دھوکے میں نہ ڈال دے اور نہ (شیطان) فریب دینے والا تمہیں فریب دے');
INSERT INTO "jalandhari" VALUES(35,6,'35:6','شیطان تمہارا دشمن ہے تم بھی اسے دشمن ہی سمجھو۔ وہ اپنے (پیروؤں کے) گروہ کو بلاتا ہے تاکہ دوزخ والوں میں ہوں');
INSERT INTO "jalandhari" VALUES(35,7,'35:7','جہنوں نے کفر کیا ان کے لئے سخت عذاب ہے۔ اور جو ایمان لائے اور عمل نیک کرتے رہے ان کے لئے بخشش اور بڑا ثواب ہے');
INSERT INTO "jalandhari" VALUES(35,8,'35:8','بھلا جس شخص کو اس کے اعمال بد آراستہ کرکے دکھائے جائیں اور وہ ان کو عمدہ سمجھنے لگے تو (کیا وہ نیکوکار آدمی جیسا ہوسکتا ہے)۔ بےشک خدا جس کو چاہتا ہے گمراہ کرتا ہے اور جس کو چاہتا ہے ہدایت دیتا ہے۔ تو ان لوگوں پر افسوس کرکے تمہارا دم نہ نکل جائے۔ یہ جو کچھ کرتے ہیں خدا اس سے واقف ہے');
INSERT INTO "jalandhari" VALUES(35,9,'35:9','اور خدا ہی تو ہے جو ہوائیں چلاتا ہے اور وہ بادل کو اُبھارتی ہیں پھر ہم ان کو ایک بےجان شہر کی طرف چلاتے ہیں۔ پھر اس سے زمین کو اس کے مرنے کے بعد زندہ کردیتے ہیں۔ اسی طرح مردوں کو جی اُٹھنا ہوگا');
INSERT INTO "jalandhari" VALUES(35,10,'35:10','جو شخص عزت کا طلب گار ہے تو عزت تو سب خدا ہی کی ہے۔ اسی کی طرف پاکیزہ کلمات چڑھتے ہیں اور نیک عمل اس کو بلند کرتے ہیں۔ اور جو لوگ برے برے مکر کرتے ہیں ان کے لئے سخت عذاب ہے۔ اور ان کا مکر نابود ہوجائے گا');
INSERT INTO "jalandhari" VALUES(35,11,'35:11','اور خدا ہی نے تم کو مٹی سے پیدا کیا پھر نطفے سے پھر تم کو جوڑا جوڑا بنا دیا۔ اور کوئی عورت نہ حاملہ ہوتی ہے اور نہ جنتی ہے مگر اس کے علم سے۔ اور نہ کسی بڑی عمر والے کو عمر زیادہ دی جاتی ہے اور نہ اس کی عمر کم کی جاتی ہے مگر (سب کچھ) کتاب میں (لکھا ہوا) ہے۔ بےشک یہ خدا کو آسان ہے');
INSERT INTO "jalandhari" VALUES(35,12,'35:12','اور دونوں دریا (مل کر) یکساں نہیں ہوجاتے۔ یہ تو میٹھا ہے پیاس بجھانے والا۔ جس کا پانی خوشگوار ہے اور یہ کھاری ہے کڑوا۔ اور سب سے تم تازہ گوشت کھاتے ہو اور زیور نکالتے ہو جسے پہنتے ہو۔ اور تم دریا میں کشتیوں کو دیکھتے ہو کہ (پانی کو) پھاڑتی چلی آتی ہیں تاکہ تم اس کے فضل سے (معاش) تلاش کرو اور تاکہ شکر کرو');
INSERT INTO "jalandhari" VALUES(35,13,'35:13','وہی رات کو دن میں داخل کرتا اور (وہی) دن کو رات میں داخل کرتا ہے اور اسی نے سورج اور چاند کو کام میں لگا دیا ہے۔ ہر ایک ایک وقت مقرر تک چل رہا ہے۔ یہی خدا تمہارا پروردگار ہے اسی کی بادشاہی ہے۔ اور جن لوگوں کو تم اس کے سوا پکارتے ہو وہ کھجور کی گٹھلی کے چھلکے کے برابر بھی تو (کسی چیز کے) مالک نہیں');
INSERT INTO "jalandhari" VALUES(35,14,'35:14','اگر تم ان کو پکارو تو وہ تمہاری پکار نہ سنیں اور اگر سن بھی لیں تو تمہاری بات کو قبول نہ کرسکیں۔ اور قیامت کے دن تمہارے شرک سے انکار کردیں گے۔ اور (خدائے) باخبر کی طرح تم کو کوئی خبر نہیں دے گا');
INSERT INTO "jalandhari" VALUES(35,15,'35:15','لوگو تم (سب) خدا کے محتاج ہو اور خدا بےپروا سزاوار (حمد وثنا) ہے');
INSERT INTO "jalandhari" VALUES(35,16,'35:16','اگر چاہے تو تم کو نابود کردے اور نئی مخلوقات لا آباد کرے');
INSERT INTO "jalandhari" VALUES(35,17,'35:17','اور یہ خدا کو کچھ مشکل نہیں');
INSERT INTO "jalandhari" VALUES(35,18,'35:18','اور کوئی اٹھانے والا دوسرے کا بوجھ نہ اٹھائے گا۔ اور کوئی بوجھ میں دبا ہوا اپنا بوجھ بٹانے کو کسی کو بلائے تو کوئی اس میں سے کچھ نہ اٹھائے گا اگرچہ قرابت دار ہی ہو۔ (اے پیغمبر) تم انہی لوگوں کو نصیحت کرسکتے ہو جو بن دیکھے اپنے پروردگار سے ڈرتے اور نماز بالالتزام پڑھتے ہیں۔ اور جو شخص پاک ہوتا ہے اپنے ہی لئے پاک ہوتا ہے۔ اور (سب کو) خدا ہی کی طرف لوٹ کر جانا ہے');
INSERT INTO "jalandhari" VALUES(35,19,'35:19','اور اندھا اور آنکھ والا برابر نہیں');
INSERT INTO "jalandhari" VALUES(35,20,'35:20','اور نہ اندھیرا اور روشنی');
INSERT INTO "jalandhari" VALUES(35,21,'35:21','اور نہ سایہ اور دھوپ');
INSERT INTO "jalandhari" VALUES(35,22,'35:22','اور نہ زندے اور مردے برابر ہوسکتے ہیں۔ خدا جس کو چاہتا ہے سنا دیتا ہے۔ اور تم ان کو جو قبروں میں مدفون ہیں نہیں سنا سکتے');
INSERT INTO "jalandhari" VALUES(35,23,'35:23','تم تو صرف ڈرانے والے ہو');
INSERT INTO "jalandhari" VALUES(35,24,'35:24','ہم نے تم کو حق کے ساتھ خوشخبری سنانے والا اور ڈرانے والا بھیجا ہے۔ اور کوئی اُمت نہیں مگر اس میں ہدایت کرنے والا گزر چکا ہے');
INSERT INTO "jalandhari" VALUES(35,25,'35:25','اور اگر یہ تمہاری تکذیب کریں تو جو لوگ ان سے پہلے تھے وہ بھی تکذیب کرچکے ہیں ان کے پاس ان کے پیغمبر نشانیاں اور صحیفے اور روشن کتابیں لے لے کر آتے رہے');
INSERT INTO "jalandhari" VALUES(35,26,'35:26','پھر میں نے کافروں کو پکڑ لیا سو (دیکھ لو کہ) میرا عذاب کیسا ہوا');
INSERT INTO "jalandhari" VALUES(35,27,'35:27','کیا تم نے نہیں دیکھا کہ خدا نے آسمان سے مینہ برسایا۔ تو ہم نے اس سے طرح طرح کے رنگوں کے میوے پیدا کئے۔ اور پہاڑوں میں سفید اور سرخ رنگوں کے قطعات ہیں اور (بعض) کالے سیاہ ہیں');
INSERT INTO "jalandhari" VALUES(35,28,'35:28','انسانوں اور جانوروں اور چارپایوں کے بھی کئی طرح کے رنگ ہیں۔ خدا سے تو اس کے بندوں میں سے وہی ڈرتے ہیں جو صاحب علم ہیں۔ بےشک خدا غالب (اور) بخشنے والا ہے');
INSERT INTO "jalandhari" VALUES(35,29,'35:29','جو لوگ خدا کی کتاب پڑھتے اور نماز کی پابندی کرتے ہیں اور جو کچھ ہم نے ان کو دیا ہے اس میں سے پوشیدہ اور ظاہر خرچ کرتے ہیں وہ اس تجارت (کے فائدے) کے امیدوار ہیں جو کبھی تباہ نہیں ہوگی');
INSERT INTO "jalandhari" VALUES(35,30,'35:30','کیونکہ خدا ان کو پورا پورا بدلہ دے گا اور اپنے فضل سے کچھ زیادہ بھی دے گا۔ وہ تو بخشنے والا (اور) قدردان ہے');
INSERT INTO "jalandhari" VALUES(35,31,'35:31','اور یہ کتاب جو ہم نے تمہاری طرف بھیجی ہے برحق ہے۔ اور ان (کتابوں) کی تصدیق کرتی ہے جو اس سے پہلے کی ہیں۔ بےشک خدا اپنے بندوں سے خبردار (اور ان کو) دیکھنے والا ہے');
INSERT INTO "jalandhari" VALUES(35,32,'35:32','پھر ہم نے ان لوگوں کو کتاب کا وارث ٹھیرایا جن کو اپنے بندوں میں سے برگزیدہ کیا۔ تو کچھ تو ان میں سے اپنے آپ پر ظلم کرتے ہیں۔ اور کچھ میانہ رو ہیں۔ اور کچھ خدا کے حکم سے نیکیوں میں آگے نکل جانے والے ہیں۔ یہی بڑا فضل ہے');
INSERT INTO "jalandhari" VALUES(35,33,'35:33','(ان لوگوں کے لئے) بہشتِ جاودانی (ہیں) جن میں وہ داخل ہوں گے۔ وہاں ان کو سونے کے کنگن اور موتی پہنائے جائیں گے۔ اور ان کی پوشاک ریشمی ہوگی');
INSERT INTO "jalandhari" VALUES(35,34,'35:34','وہ کہیں گے کہ خدا کا شکر ہے جس نے ہم سے غم دور کیا۔ بےشک ہمارا پروردگار بخشنے والا (اور) قدردان ہے');
INSERT INTO "jalandhari" VALUES(35,35,'35:35','جس نے ہم کو اپنے فضل سے ہمیشہ کے رہنے کے گھر میں اُتارا۔ یہاں نہ تو ہم کو رنج پہنچے گا اور نہ ہمیں تکان ہی ہوگی');
INSERT INTO "jalandhari" VALUES(35,36,'35:36','اور جن لوگوں نے کفر کیا ان کے لئے دوزخ کی آگ ہے۔ نہ انہیں موت آئے گی کہ مرجائیں اور نہ ان کا عذاب ہی ان سے ہلکا کیا جائے گا۔ ہم ہر ایک ناشکرے کو ایسا ہی بدلہ دیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(35,37,'35:37','وہ اس میں چلائیں گے کہ اے پروردگار ہم کو نکال لے (اب) ہم نیک عمل کیا کریں گے۔ نہ وہ جو (پہلے) کرتے تھے۔ کیا ہم نے تم کو اتنی عمر نہیں دی تھی کہ اس میں جو سوچنا چاہتا سوچ لیتا اور تمہارے پاس ڈرانے والا بھی آیا۔ تو اب مزے چکھو۔ ظالموں کا کوئی مددگار نہیں');
INSERT INTO "jalandhari" VALUES(35,38,'35:38','بےشک خدا ہی آسمانوں اور زمین کی پوشیدہ باتوں کا جاننے والا ہے۔ وہ تو دل کے بھیدوں تک سے واقف ہے');
INSERT INTO "jalandhari" VALUES(35,39,'35:39','وہی تو ہے جس نے تم کو زمین میں (پہلوں کا) جانشین بنایا۔ تو جس نے کفر کیا اس کے کفر کا ضرر اسی کو ہے۔ اور کافروں کے حق میں ان کے کفر سے پروردگار کے ہاں ناخوشی ہی بڑھتی ہے اور کافروں کو ان کا کفر نقصان ہی زیادہ کرتا ہے');
INSERT INTO "jalandhari" VALUES(35,40,'35:40','بھلا تم نے اپنے شریکوں کو دیکھا جن کو تم خدا کے سوا پکارتے ہو۔ مجھے دکھاؤ کہ انہوں نے زمین سے کون سی چیز پیدا کی ہے یا (بتاؤ کہ) آسمانوں میں ان کی شرکت ہے۔ یا ہم نے ان کو کتاب دی ہے تو وہ اس کی سند رکھتے ہیں (ان میں سے کوئی بات بھی نہیں) بلکہ ظالم جو ایک دوسرے کو وعدہ دیتے ہیں محض فریب ہے');
INSERT INTO "jalandhari" VALUES(35,41,'35:41','خدا ہی آسمانوں اور زمین کو تھامے رکھتا ہے کہ ٹل نہ جائیں۔ اگر وہ ٹل جائیں تو خدا کے سوا کوئی ایسا نہیں جو ان کو تھام سکے۔ بےشک وہ بردبار (اور) بخشنے والا ہے');
INSERT INTO "jalandhari" VALUES(35,42,'35:42','اور یہ خدا کی سخت سخت قسمیں کھاتے ہیں کہ اگر ان کے پاس کوئی ہدایت کرنے والا آئے تو ہر ایک اُمت سے بڑھ کر ہدایت پر ہوں۔ مگر جب ان کے پاس ہدایت کرنے والا آیا تو اس سے ان کو نفرت ہی بڑھی');
INSERT INTO "jalandhari" VALUES(35,43,'35:43','یعنی (انہوں نے) ملک میں غرور کرنا اور بری چال چلنا (اختیار کیا) اور بری چال کا وبال اس کے چلنے والے ہی پر پڑتا ہے۔ یہ اگلے لوگوں کی روش کے سوا اور کسی چیز کے منتظر نہیں۔ سو تم خدا کی عادت میں ہرگز تبدل نہ پاؤ گے۔ اور خدا کے طریقے میں کبھی تغیر نہ دیکھو گے');
INSERT INTO "jalandhari" VALUES(35,44,'35:44','کیا انہوں نے زمین میں کبھی سیر نہیں کی تاکہ دیکھتے کہ جو لوگ ان سے پہلے تھے ان کا انجام کیا ہوا حالانکہ وہ ان سے قوت میں بہت زیادہ تھے۔ اور خدا ایسا نہیں کہ آسمانوں اور زمین میں کوئی چیز اس کو عاجز کرسکے۔ وہ علم والا (اور) قدرت والا ہے');
INSERT INTO "jalandhari" VALUES(35,45,'35:45','اور اگر خدا لوگوں کو ان کے اعمال کے سبب پکڑنے لگتا۔ تو روئے زمین پر ایک چلنے پھرنے والے کو نہ چھوڑتا۔ لیکن وہ ان کو ایک وقت مقرر تک مہلت دیئے جاتا ہے۔ سو جب ان کا وقت آجائے گا تو (ان کے اعمال کا بدلہ دے گا) خدا تو اپنے بندوں کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(36,1,'36:1','یٰسٓ');
INSERT INTO "jalandhari" VALUES(36,2,'36:2','قسم ہے قرآن کی جو حکمت سے بھرا ہوا ہے');
INSERT INTO "jalandhari" VALUES(36,3,'36:3','اے محمدصلى الله عليه وسلم) بےشک تم پیغمبروں میں سے ہو');
INSERT INTO "jalandhari" VALUES(36,4,'36:4','سیدھے رستے پر');
INSERT INTO "jalandhari" VALUES(36,5,'36:5','یہ خدائے) غالب (اور) مہربان نے نازل کیا ہے');
INSERT INTO "jalandhari" VALUES(36,6,'36:6','تاکہ تم ان لوگوں کو جن کے باپ دادا کو متنبہ نہیں کیا گیا تھا متنبہ کردو وہ غفلت میں پڑے ہوئے ہیں');
INSERT INTO "jalandhari" VALUES(36,7,'36:7','ان میں سے اکثر پر (خدا کی) بات پوری ہوچکی ہے سو وہ ایمان نہیں لائیں گے');
INSERT INTO "jalandhari" VALUES(36,8,'36:8','ہم نے ان کی گردنوں میں طوق ڈال رکھے ہیں اور وہ ٹھوڑیوں تک (پھنسے ہوئے ہیں) تو ان کے سر اُلل رہے ہیں');
INSERT INTO "jalandhari" VALUES(36,9,'36:9','اور ہم نے ان کے آگے بھی دیوار بنا دی اور ان کے پیچھے بھی۔ پھر ان پر پردہ ڈال دیا تو یہ دیکھ نہیں سکتے');
INSERT INTO "jalandhari" VALUES(36,10,'36:10','اور تم ان کو نصیحت کرو یا نہ کرو ان کے لئے برابر ہے وہ ایمان نہیں لانے کے');
INSERT INTO "jalandhari" VALUES(36,11,'36:11','تم تو صرف اس شخص کو نصیحت کرسکتے ہو جو نصیحت کی پیروی کرے اور خدا سے غائبانہ ڈرے سو اس کو مغفرت اور بڑے ثواب کی بشارت سنا دو');
INSERT INTO "jalandhari" VALUES(36,12,'36:12','بےشک ہم مردوں کو زندہ کریں گے اور جو کچھ وہ آگے بھیج چکے اور (جو) ان کے نشان پیچھے رہ گئے ہم ان کو قلمبند کرلیتے ہیں۔ اور ہر چیز کو ہم نے کتاب روشن (یعنی لوح محفوظ) میں لکھ رکھا ہے۔');
INSERT INTO "jalandhari" VALUES(36,13,'36:13','اور ان سے گاؤں والوں کا قصہ بیان کرو جب ان کے پاس پیغمبر آئے');
INSERT INTO "jalandhari" VALUES(36,14,'36:14','(یعنی) جب ہم نے ان کی طرف دو (پیغمبر) بھیجے تو انہوں نے ان کو جھٹلایا۔ پھر ہم نے تیسرے سے تقویت دی تو انہوں نے کہا کہ ہم تمہاری طرف پیغمبر ہو کر آئے ہیں');
INSERT INTO "jalandhari" VALUES(36,15,'36:15','وہ بولے کہ تم (اور کچھ) نہیں مگر ہماری طرح کے آدمی (ہو) اور خدا نے کوئی چیز نازل نہیں کی تم محض جھوٹ بولتے ہو');
INSERT INTO "jalandhari" VALUES(36,16,'36:16','انہوں نے کہا کہ ہمارا پروردگار جانتا ہے کہ ہم تمہاری طرف (پیغام دے کر) بھیجے گئے ہیں');
INSERT INTO "jalandhari" VALUES(36,17,'36:17','اور ہمارے ذمے تو صاف صاف پہنچا دینا ہے اور بس');
INSERT INTO "jalandhari" VALUES(36,18,'36:18','وہ بولے کہ ہم تم کو نامبارک سمجھتے ہیں۔ اگر تم باز نہ آؤ گے تو ہم تمہیں سنگسار کردیں گے اور تم کو ہم سے دکھ دینے والا عذاب پہنچے گا');
INSERT INTO "jalandhari" VALUES(36,19,'36:19','انہوں نے کہا کہ تمہاری نحوست تمہارے ساتھ ہے۔ کیا اس لئے کہ تم کو نصیحت کی گئی۔ بلکہ تم ایسے لوگ ہو جو حد سے تجاوز کر گئے ہو');
INSERT INTO "jalandhari" VALUES(36,20,'36:20','اور شہر کے پرلے کنارے سے ایک آدمی دوڑتا ہوا آیا کہنے لگا کہ اے میری قوم پیغمبروں کے پیچھے چلو');
INSERT INTO "jalandhari" VALUES(36,21,'36:21','ایسوں کے جو تم سے صلہ نہیں مانگتے اور وہ سیدھے رستے پر ہیں');
INSERT INTO "jalandhari" VALUES(36,22,'36:22','اور مجھے کیا ہے میں اس کی پرستش نہ کروں جس نے مجھے پیدا کیا اور اسی کی طرف تم کو لوٹ کر جانا ہے');
INSERT INTO "jalandhari" VALUES(36,23,'36:23','کیا میں ان کو چھوڑ کر اوروں کو معبود بناؤں؟ اگر خدا میرے حق میں نقصان کرنا چاہے تو ان کی سفارش مجھے کچھ بھی فائدہ نہ دے سکے اور نہ وہ مجھ کو چھڑا ہی سکیں');
INSERT INTO "jalandhari" VALUES(36,24,'36:24','تب تو میں صریح گمراہی میں مبتلا ہوگیا');
INSERT INTO "jalandhari" VALUES(36,25,'36:25','میں تمہارے پروردگار پر ایمان لایا ہوں سو میری بات سن رکھو');
INSERT INTO "jalandhari" VALUES(36,26,'36:26','حکم ہوا کہ بہشت میں داخل ہوجا۔ بولا کاش! میری قوم کو خبر ہو');
INSERT INTO "jalandhari" VALUES(36,27,'36:27','کہ خدا نے مجھے بخش دیا اور عزت والوں میں کیا');
INSERT INTO "jalandhari" VALUES(36,28,'36:28','اور ہم نے اس کے بعد اس کی قوم پر کوئی لشکر نہیں اُتارا اور نہ ہم اُتارنے والے تھے ہی');
INSERT INTO "jalandhari" VALUES(36,29,'36:29','وہ تو صرف ایک چنگھاڑ تھی (آتشین) سو وہ (اس سے) ناگہاں بجھ کر رہ گئے');
INSERT INTO "jalandhari" VALUES(36,30,'36:30','بندوں پر افسوس ہے کہ ان کے پاس کوئی پیغمبر نہیں آتا مگر اس سے تمسخر کرتے ہیں');
INSERT INTO "jalandhari" VALUES(36,31,'36:31','کیا انہوں نے نہیں دیکھا کہ ہم نے ان سے پہلے بہت سے لوگوں کو ہلاک کردیا تھا اب وہ ان کی طرف لوٹ کر نہیں آئیں گے');
INSERT INTO "jalandhari" VALUES(36,32,'36:32','اور سب کے سب ہمارے روبرو حاضر کيے جائیں گے');
INSERT INTO "jalandhari" VALUES(36,33,'36:33','اور ایک نشانی ان کے لئے زمین مردہ ہے کہ ہم نے اس کو زندہ کیا اور اس میں سے اناج اُگایا۔ پھر یہ اس میں سے کھاتے ہیں');
INSERT INTO "jalandhari" VALUES(36,34,'36:34','اور اس میں کھجوروں اور انگوروں کے باغ پیدا کیے اور اس میں چشمے جاری کردیئے');
INSERT INTO "jalandhari" VALUES(36,35,'36:35','تاکہ یہ ان کے پھل کھائیں اور ان کے ہاتھوں نے تو ان کو نہیں بنایا تو پھر یہ شکر کیوں نہیں کرتے؟');
INSERT INTO "jalandhari" VALUES(36,36,'36:36','وہ خدا پاک ہے جس نے زمین کی نباتات کے اور خود ان کے اور جن چیزوں کی ان کو خبر نہیں سب کے جوڑے بنائے');
INSERT INTO "jalandhari" VALUES(36,37,'36:37','اور ایک نشانی ان کے لئے رات ہے کہ اس میں سے ہم دن کو کھینچ لیتے ہیں تو اس وقت ان پر اندھیرا چھا جاتا ہے');
INSERT INTO "jalandhari" VALUES(36,38,'36:38','اور سورج اپنے مقرر رستے پر چلتا رہتا ہے۔ یہ (خدائے) غالب اور دانا کا (مقرر کیا ہوا) اندازہ ہے');
INSERT INTO "jalandhari" VALUES(36,39,'36:39','اور چاند کی بھی ہم نے منزلیں مقرر کردیں یہاں تک کہ (گھٹتے گھٹتے) کھجور کی پرانی شاخ کی طرح ہو جاتا ہے');
INSERT INTO "jalandhari" VALUES(36,40,'36:40','نہ تو سورج ہی سے ہوسکتا ہے کہ چاند کو جا پکڑے اور نہ رات ہی دن سے پہلے آسکتی ہے۔ اور سب اپنے اپنے دائرے میں تیر رہے ہیں');
INSERT INTO "jalandhari" VALUES(36,41,'36:41','اور ایک نشانی ان کے لئے یہ ہے کہ ہم نے ان کی اولاد کو بھری ہوئی کشتی میں سوار کیا');
INSERT INTO "jalandhari" VALUES(36,42,'36:42','اور ان کے لئے ویسی ہی اور چیزیں پیدا کیں جن پر وہ سوار ہوتے ہیں');
INSERT INTO "jalandhari" VALUES(36,43,'36:43','اور اگر ہم چاہیں تو ان کو غرق کردیں۔ پھر نہ تو ان کا کوئی فریاد رس ہوا اور نہ ان کو رہائی ملے');
INSERT INTO "jalandhari" VALUES(36,44,'36:44','مگر یہ ہماری رحمت اور ایک مدت تک کے فائدے ہیں');
INSERT INTO "jalandhari" VALUES(36,45,'36:45','اور جب ان سے کہا جاتا ہے کہ جو تمہارے آگے اور جو تمہارے پیچھے ہے اس سے ڈرو تاکہ تم پر رحم کیا جائے');
INSERT INTO "jalandhari" VALUES(36,46,'36:46','اور ان کے پاس ان کے پروردگار کی کوئی نشانی نہیں آتی مگر اس سے منہ پھیر لیتے ہیں');
INSERT INTO "jalandhari" VALUES(36,47,'36:47','اور جب ان سے کہا جاتا ہے کہ جو رزق خدا نے تم کو دیا ہے اس میں سے خرچ کرو۔ تو کافر مومنوں سے کہتے ہیں کہ بھلا ہم ان لوگوں کو کھانا کھلائیں جن کو اگر خدا چاہتا تو خود کھلا دیتا۔ تم تو صریح غلطی میں ہو');
INSERT INTO "jalandhari" VALUES(36,48,'36:48','اور کہتے ہیں اگر تم سچ کہتے ہو تو یہ وعدہ کب (پورا) ہوگا؟');
INSERT INTO "jalandhari" VALUES(36,49,'36:49','یہ تو ایک چنگھاڑ کے منتظر ہیں جو ان کو اس حال میں کہ باہم جھگڑ رہے ہوں گے آپکڑے گی');
INSERT INTO "jalandhari" VALUES(36,50,'36:50','پھر نہ وصیت کرسکیں گے اور نہ اپنے گھر والوں میں واپس جاسکیں گے');
INSERT INTO "jalandhari" VALUES(36,51,'36:51','اور (جس وقت) صور پھونکا جائے گا یہ قبروں سے (نکل کر) اپنے پروردگار کی طرف دوڑ پڑیں گے');
INSERT INTO "jalandhari" VALUES(36,52,'36:52','کہیں گے اے ہے ہمیں ہماری خوابگاہوں سے کس نے (جگا) اُٹھایا؟ یہ وہی تو ہے جس کا خدا نے وعدہ کیا تھا اور پیغمبروں نے سچ کہا تھا');
INSERT INTO "jalandhari" VALUES(36,53,'36:53','صرف ایک زور کی آواز کا ہونا ہوگا کہ سب کے سب ہمارے روبرو آحاضر ہوں گے');
INSERT INTO "jalandhari" VALUES(36,54,'36:54','اس روز کسی شخص پر کچھ ظلم نہیں کیا جائے گا اور تم کو بدلہ ویسا ہی ملے گا جیسے تم کام کرتے تھے');
INSERT INTO "jalandhari" VALUES(36,55,'36:55','اہل جنت اس روز عیش ونشاط کے مشغلے میں ہوں گے');
INSERT INTO "jalandhari" VALUES(36,56,'36:56','وہ بھی اور ان کی بیویاں بھی سایوں میں تختوں پر تکیے لگائے بیٹھے ہوں گے');
INSERT INTO "jalandhari" VALUES(36,57,'36:57','وہاں ان کے لئے میوے اور جو چاہیں گے (موجود ہوگا)');
INSERT INTO "jalandhari" VALUES(36,58,'36:58','پروردگار مہربان کی طرف سے سلام (کہا جائے گا)');
INSERT INTO "jalandhari" VALUES(36,59,'36:59','اور گنہگارو! آج الگ ہوجاؤ');
INSERT INTO "jalandhari" VALUES(36,60,'36:60','اے آدم کی اولاد ہم نے تم سے کہہ نہیں دیا تھا کہ شیطان کو نہ پوجنا وہ تمہارا کھلا دشمن ہے');
INSERT INTO "jalandhari" VALUES(36,61,'36:61','اور یہ کہ میری ہی عبادت کرنا۔ یہی سیدھا رستہ ہے');
INSERT INTO "jalandhari" VALUES(36,62,'36:62','اور اس نے تم میں سے بہت سی خلقت کو گمراہ کردیا تھا۔ تو کیا تم سمجھتے نہیں تھے؟');
INSERT INTO "jalandhari" VALUES(36,63,'36:63','یہی وہ جہنم ہے جس کی تمہیں خبر دی جاتی ہے');
INSERT INTO "jalandhari" VALUES(36,64,'36:64','(سو) جو تم کفر کرتے رہے ہو اس کے بدلے آج اس میں داخل ہوجاؤ');
INSERT INTO "jalandhari" VALUES(36,65,'36:65','آج ہم ان کے مونہوں پر مہر لگا دیں گے اور جو کچھ یہ کرتے رہے تھے ان کے ہاتھ ہم سے بیان کردیں گے اور ان کے پاؤں (اس کی) گواہی دیں گے');
INSERT INTO "jalandhari" VALUES(36,66,'36:66','اور اگر ہم چاہیں تو ان کی آنکھوں کو مٹا کر (اندھا کر) دیں۔ پھر یہ رستے کو دوڑیں تو کہاں دیکھ سکیں گے');
INSERT INTO "jalandhari" VALUES(36,67,'36:67','اور اگر ہم چاہیں تو ان کی جگہ پر ان کی صورتیں بدل دیں پھر وہاں سے نہ آگے جاسکیں اور نہ (پیچھے) لوٹ سکیں');
INSERT INTO "jalandhari" VALUES(36,68,'36:68','اور جس کو ہم بڑی عمر دیتے ہیں تو اسے خلقت میں اوندھا کردیتے ہیں تو کیا یہ سمجھتے نہیں؟');
INSERT INTO "jalandhari" VALUES(36,69,'36:69','اور ہم نے ان (پیغمبر) کو شعر گوئی نہیں سکھائی اور نہ وہ ان کو شایاں ہے۔ یہ تو محض نصیحت اور صاف صاف قرآن (پُرازحکمت) ہے');
INSERT INTO "jalandhari" VALUES(36,70,'36:70','تاکہ اس شخص کو جو زندہ ہو ہدایت کا رستہ دکھائے اور کافروں پر بات پوری ہوجائے');
INSERT INTO "jalandhari" VALUES(36,71,'36:71','کیا انہوں نے نہیں دیکھا کہ جو چیزیں ہم نے اپنے ہاتھوں سے بنائیں ان میں سے ہم نے ان کے لئے چارپائے پیدا کر دیئے اور یہ ان کے مالک ہیں');
INSERT INTO "jalandhari" VALUES(36,72,'36:72','اور ان کو ان کے قابو میں کردیا تو کوئی تو ان میں سے ان کی سواری ہے اور کسی کو یہ کھاتے ہیں');
INSERT INTO "jalandhari" VALUES(36,73,'36:73','اور ان میں ان کے لئے (اور) فائدے اور پینے کی چیزیں ہیں۔ تو یہ شکر کیوں نہیں کرتے؟');
INSERT INTO "jalandhari" VALUES(36,74,'36:74','اور انہوں نے خدا کے سوا (اور) معبود بنا لیے ہیں کہ شاید (ان سے) ان کو مدد پہنچے');
INSERT INTO "jalandhari" VALUES(36,75,'36:75','(مگر) وہ ان کی مدد کی (ہرگز) طاقت نہیں رکھتے۔ اور وہ ان کی فوج ہو کر حاضر کیے جائیں گے');
INSERT INTO "jalandhari" VALUES(36,76,'36:76','تو ان کی باتیں تمہیں غمناک نہ کردیں۔ یہ جو کچھ چھپاتے اور جو کچھ ظاہر کرتے ہیں ہمیں سب معلوم ہے');
INSERT INTO "jalandhari" VALUES(36,77,'36:77','کیا انسان نے نہیں دیکھا کہ ہم نے اس کو نطفے سے پیدا کیا۔ پھر وہ تڑاق پڑاق جھگڑنے لگا');
INSERT INTO "jalandhari" VALUES(36,78,'36:78','اور ہمارے بارے میں مثالیں بیان کرنے لگا اور اپنی پیدائش کو بھول گیا۔ کہنے لگا کہ (جب) ہڈیاں بوسیدہ ہوجائیں گی تو ان کو کون زندہ کرے گا؟');
INSERT INTO "jalandhari" VALUES(36,79,'36:79','کہہ دو کہ ان کو وہ زندہ کرے گا جس نے ان کو پہلی بار پیدا کیا تھا۔ اور وہ سب قسم کا پیدا کرنا جانتا ہے');
INSERT INTO "jalandhari" VALUES(36,80,'36:80','جس نے تمہارے لئے سبز درخت سے آگ پیدا کی پھر تم اس (کی ٹہنیوں کو رگڑ کر ان) سے آگ نکالتے ہو');
INSERT INTO "jalandhari" VALUES(36,81,'36:81','بھلا جس نے آسمانوں اور زمین کو پیدا کیا، کیا وہ اس بات پر قادر نہیں کہ (ان کو پھر) ویسے ہی پیدا کر دے۔ کیوں نہیں۔ اور وہ تو بڑا پیدا کرنے والا اور علم والا ہے');
INSERT INTO "jalandhari" VALUES(36,82,'36:82','اس کی شان یہ ہے کہ جب وہ کسی چیز کا ارادہ کرتا ہے تو اس سے فرما دیتا ہے کہ ہوجا تو وہ ہوجاتی ہے');
INSERT INTO "jalandhari" VALUES(36,83,'36:83','وہ (ذات) پاک ہے جس کے ہاتھ میں ہر چیز کی بادشاہت ہے اور اسی کی طرف تم کو لوٹ کر جانا ہے');
INSERT INTO "jalandhari" VALUES(37,1,'37:1','قسم ہے صف باندھنے والوں کی پرا جما کر');
INSERT INTO "jalandhari" VALUES(37,2,'37:2','پھر ڈانٹنے والوں کی جھڑک کر');
INSERT INTO "jalandhari" VALUES(37,3,'37:3','پھر ذکر (یعنی قرآن) پڑھنے والوں کی (غور کرکر)');
INSERT INTO "jalandhari" VALUES(37,4,'37:4','کہ تمہارا معبود ایک ہے');
INSERT INTO "jalandhari" VALUES(37,5,'37:5','جو آسمانوں اور زمین اور جو چیزیں ان میں ہیں سب کا مالک ہے اور سورج کے طلوع ہونے کے مقامات کا بھی مالک ہے');
INSERT INTO "jalandhari" VALUES(37,6,'37:6','بےشک ہم ہی نے آسمان دنیا کو ستاروں کی زینت سے مزین کیا');
INSERT INTO "jalandhari" VALUES(37,7,'37:7','اور ہر شیطان سرکش سے اس کی حفاظت کی');
INSERT INTO "jalandhari" VALUES(37,8,'37:8','کہ اوپر کی مجلس کی طرف کان نہ لگاسکیں اور ہر طرف سے (ان پر انگارے) پھینکے جاتے ہیں');
INSERT INTO "jalandhari" VALUES(37,9,'37:9','(یعنی وہاں سے) نکال دینے کو اور ان کے لئے دائمی عذاب ہے');
INSERT INTO "jalandhari" VALUES(37,10,'37:10','ہاں جو کوئی (فرشتوں کی کسی بات کو) چوری سے جھپٹ لینا چاہتا ہے تو جلتا ہوا انگارہ ان کے پیچھے لگتا ہے');
INSERT INTO "jalandhari" VALUES(37,11,'37:11','تو ان سے پوچھو کہ ان کا بنانا مشکل ہے یا جتنی خلقت ہم نے بنائی ہے؟ انہیں ہم نے چپکتے گارے سے بنایا ہے');
INSERT INTO "jalandhari" VALUES(37,12,'37:12','ہاں تم تو تعجب کرتے ہو اور یہ تمسخر کرتے ہیں');
INSERT INTO "jalandhari" VALUES(37,13,'37:13','اور جب ان کو نصیحت دی جاتی ہے تو نصیحت قبول نہیں کرتے');
INSERT INTO "jalandhari" VALUES(37,14,'37:14','اور جب کوئی نشانی دیکھتے ہیں تو ٹھٹھے کرتے ہیں');
INSERT INTO "jalandhari" VALUES(37,15,'37:15','اور کہتے ہیں کہ یہ تو صریح جادو ہے');
INSERT INTO "jalandhari" VALUES(37,16,'37:16','بھلا جب ہم مرگئے اور مٹی اور ہڈیاں ہوگئے تو کیا پھر اٹھائے جائیں گے؟');
INSERT INTO "jalandhari" VALUES(37,17,'37:17','اور کیا ہمارے باپ دادا بھی (جو) پہلے (ہو گزرے ہیں)');
INSERT INTO "jalandhari" VALUES(37,18,'37:18','کہہ دو کہ ہاں اور تم ذلیل ہوگے');
INSERT INTO "jalandhari" VALUES(37,19,'37:19','وہ تو ایک زور کی آواز ہوگی اور یہ اس وقت دیکھنے لگیں گے');
INSERT INTO "jalandhari" VALUES(37,20,'37:20','اور کہیں گے، ہائے شامت یہی جزا کا دن ہے');
INSERT INTO "jalandhari" VALUES(37,21,'37:21','(کہا جائے گا کہ ہاں) فیصلے کا دن جس کو تم جھوٹ سمجھتے تھے یہی ہے');
INSERT INTO "jalandhari" VALUES(37,22,'37:22','جو لوگ ظلم کرتے تھے ان کو اور ان کے ہم جنسوں کو اور جن کو وہ پوجا کرتے تھے (سب کو) جمع کرلو');
INSERT INTO "jalandhari" VALUES(37,23,'37:23','(یعنی جن کو) خدا کے سوا (پوجا کرتے تھے) پھر ان کو جہنم کے رستے پر چلا دو');
INSERT INTO "jalandhari" VALUES(37,24,'37:24','اور ان کو ٹھیرائے رکھو کہ ان سے (کچھ) پوچھنا ہے');
INSERT INTO "jalandhari" VALUES(37,25,'37:25','تم کو کیا ہوا کہ ایک دوسرے کی مدد نہیں کرتے؟');
INSERT INTO "jalandhari" VALUES(37,26,'37:26','بلکہ آج تو وہ فرمانبردار ہیں');
INSERT INTO "jalandhari" VALUES(37,27,'37:27','اور ایک دوسرے کی طرف رخ کرکے سوال (وجواب) کریں گے');
INSERT INTO "jalandhari" VALUES(37,28,'37:28','کہیں گے کیا تم ہی ہمارے پاس دائیں (اور بائیں) سے آتے تھے');
INSERT INTO "jalandhari" VALUES(37,29,'37:29','وہ کہیں گے بلکہ تم ہی ایمان لانے والے نہ تھے');
INSERT INTO "jalandhari" VALUES(37,30,'37:30','اور ہمارا تم پر کچھ زور نہ تھا۔ بلکہ تم سرکش لوگ تھے');
INSERT INTO "jalandhari" VALUES(37,31,'37:31','سو ہمارے بارے میں ہمارے پروردگار کی بات پوری ہوگئی اب ہم مزے چکھیں گے');
INSERT INTO "jalandhari" VALUES(37,32,'37:32','ہم نے تم کو بھی گمراہ کیا (اور) ہم خود بھی گمراہ تھے');
INSERT INTO "jalandhari" VALUES(37,33,'37:33','پس وہ اس روز عذاب میں ایک دوسرے کے شریک ہوں گے');
INSERT INTO "jalandhari" VALUES(37,34,'37:34','ہم گنہگاروں کے ساتھ ایسا ہی کیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(37,35,'37:35','ان کا یہ حال تھا کہ جب ان سے کہا جاتا تھا کہ خدا کے سوا کوئی معبود نہیں تو غرور کرتے تھے');
INSERT INTO "jalandhari" VALUES(37,36,'37:36','اور کہتے تھے کہ بھلا ہم ایک دیوانے شاعر کے کہنے سے کہیں اپنے معبودوں کو چھوڑ دینے والے ہیں');
INSERT INTO "jalandhari" VALUES(37,37,'37:37','بلکہ وہ حق لے کر آئے ہیں اور (پہلے) پیغمبروں کو سچا کہتے ہیں');
INSERT INTO "jalandhari" VALUES(37,38,'37:38','بےشک تم تکلیف دینے والے عذاب کا مزہ چکھنے والے ہو');
INSERT INTO "jalandhari" VALUES(37,39,'37:39','اور تم کو بدلہ ویسا ہی ملے گا جیسے تم کام کرتے تھے');
INSERT INTO "jalandhari" VALUES(37,40,'37:40','مگر جو خدا کے بندگان خاص ہیں');
INSERT INTO "jalandhari" VALUES(37,41,'37:41','یہی لوگ ہیں جن کے لئے روزی مقرر ہے');
INSERT INTO "jalandhari" VALUES(37,42,'37:42','(یعنی) میوے اور ان کا اعزاز کیا جائے گا');
INSERT INTO "jalandhari" VALUES(37,43,'37:43','نعمت کے باغوں میں');
INSERT INTO "jalandhari" VALUES(37,44,'37:44','ایک دوسرے کے سامنے تختوں پر (بیٹھے ہوں گے)');
INSERT INTO "jalandhari" VALUES(37,45,'37:45','شراب لطیف کے جام کا ان میں دور چل رہا ہوگا');
INSERT INTO "jalandhari" VALUES(37,46,'37:46','جو رنگ کی سفید اور پینے والوں کے لئے (سراسر) لذت ہوگی');
INSERT INTO "jalandhari" VALUES(37,47,'37:47','نہ اس سے دردِ سر ہو اور نہ وہ اس سے متوالے ہوں گے');
INSERT INTO "jalandhari" VALUES(37,48,'37:48','اور ان کے پاس عورتیں ہوں گی جو نگاہیں نیچی رکھتی ہوں گی اور آنکھیں بڑی بڑی');
INSERT INTO "jalandhari" VALUES(37,49,'37:49','گویا وہ محفوظ انڈے ہیں');
INSERT INTO "jalandhari" VALUES(37,50,'37:50','پھر وہ ایک دوسرے کی طرف رخ کرکے سوال (وجواب) کریں گے');
INSERT INTO "jalandhari" VALUES(37,51,'37:51','ایک کہنے والا ان میں سے کہے گا کہ میرا ایک ہم نشین تھا');
INSERT INTO "jalandhari" VALUES(37,52,'37:52','(جو) کہتا تھا کہ بھلا تم بھی ایسی باتوں کے باور کرنے والوں میں ہو');
INSERT INTO "jalandhari" VALUES(37,53,'37:53','بھلا جب ہم مر گئے اور مٹی اور ہڈیاں ہوگئے تو کیا ہم کو بدلہ ملے گا؟');
INSERT INTO "jalandhari" VALUES(37,54,'37:54','(پھر) کہے گا کہ بھلا تم (اسے) جھانک کر دیکھنا چاہتے ہو؟');
INSERT INTO "jalandhari" VALUES(37,55,'37:55','(اتنے میں) وہ (خود) جھانکے گا تو اس کو وسط دوزخ میں دیکھے گا');
INSERT INTO "jalandhari" VALUES(37,56,'37:56','کہے گا کہ خدا کی قسم تُو تو مجھے ہلاک ہی کرچکا تھا');
INSERT INTO "jalandhari" VALUES(37,57,'37:57','اور اگر میرے پروردگار کی مہربانی نہ ہوتی تو میں بھی ان میں ہوتا جو (عذاب میں) حاضر کئے گئے ہیں');
INSERT INTO "jalandhari" VALUES(37,58,'37:58','کیا (یہ نہیں کہ) ہم (آئندہ کبھی) مرنے کے نہیں');
INSERT INTO "jalandhari" VALUES(37,59,'37:59','ہاں (جو) پہلی بار مرنا (تھا سو مرچکے) اور ہمیں عذاب بھی نہیں ہونے کا');
INSERT INTO "jalandhari" VALUES(37,60,'37:60','بےشک یہ بڑی کامیابی ہے');
INSERT INTO "jalandhari" VALUES(37,61,'37:61','ایسی ہی (نعمتوں) کے لئے عمل کرنے والوں کو عمل کرنے چاہئیں');
INSERT INTO "jalandhari" VALUES(37,62,'37:62','بھلا یہ مہمانی اچھی ہے یا تھوہر کا درخت؟');
INSERT INTO "jalandhari" VALUES(37,63,'37:63','ہم نے اس کو ظالموں کے لئے عذاب بنا رکھا ہے');
INSERT INTO "jalandhari" VALUES(37,64,'37:64','وہ ایک درخت ہے کہ جہنم کے اسفل میں اُگے گا');
INSERT INTO "jalandhari" VALUES(37,65,'37:65','اُس کے خوشے ایسے ہوں گے جیسے شیطانوں کے سر');
INSERT INTO "jalandhari" VALUES(37,66,'37:66','سو وہ اسی میں سے کھائیں گے اور اسی سے پیٹ بھریں گے');
INSERT INTO "jalandhari" VALUES(37,67,'37:67','پھر اس (کھانے) کے ساتھ ان کو گرم پانی ملا کر دیا جائے گا');
INSERT INTO "jalandhari" VALUES(37,68,'37:68','پھر ان کو دوزخ کی طرف لوٹایا جائے گا');
INSERT INTO "jalandhari" VALUES(37,69,'37:69','انہوں نے اپنے باپ دادا کو گمراہ ہی پایا');
INSERT INTO "jalandhari" VALUES(37,70,'37:70','سو وہ ان ہی کے پیچھے دوڑے چلے جاتے ہیں');
INSERT INTO "jalandhari" VALUES(37,71,'37:71','اور ان سے پیشتر بہت سے لوگ بھی گمراہ ہوگئے تھے');
INSERT INTO "jalandhari" VALUES(37,72,'37:72','اور ہم نے ان میں متنبہ کرنے والے بھیجے');
INSERT INTO "jalandhari" VALUES(37,73,'37:73','سو دیکھ لو کہ جن کو متنبہ کیا گیا تھا ان کا انجام کیسا ہوا');
INSERT INTO "jalandhari" VALUES(37,74,'37:74','ہاں خدا کے بندگان خاص (کا انجام بہت اچھا ہوا)');
INSERT INTO "jalandhari" VALUES(37,75,'37:75','اور ہم کو نوح نے پکارا سو (دیکھ لو کہ) ہم (دعا کو کیسے) اچھے قبول کرنے والے ہیں');
INSERT INTO "jalandhari" VALUES(37,76,'37:76','اور ہم نے ان کو اور ان کے گھر والوں کو بڑی مصیبت سے نجات دی');
INSERT INTO "jalandhari" VALUES(37,77,'37:77','اور ان کی اولاد کو ایسا کیا کہ وہی باقی رہ گئے');
INSERT INTO "jalandhari" VALUES(37,78,'37:78','اور پیچھے آنے والوں میں ان کا ذکر (جمیل باقی) چھوڑ دیا');
INSERT INTO "jalandhari" VALUES(37,79,'37:79','یعنی) تمام جہان میں (کہ) نوح پر سلام');
INSERT INTO "jalandhari" VALUES(37,80,'37:80','نیکوکاروں کو ہم ایسا ہی بدلہ دیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(37,81,'37:81','بےشک وہ ہمارے مومن بندوں میں سے تھا');
INSERT INTO "jalandhari" VALUES(37,82,'37:82','پھر ہم نے دوسروں کو ڈبو دیا');
INSERT INTO "jalandhari" VALUES(37,83,'37:83','اور ان ہی کے پیرووں میں ابراہیم تھے');
INSERT INTO "jalandhari" VALUES(37,84,'37:84','جب وہ اپنے پروردگار کے پاس (عیب سے) پاک دل لے کر آئے');
INSERT INTO "jalandhari" VALUES(37,85,'37:85','جب انہوں نے اپنے باپ سے اور اپنی قوم سے کہا کہ تم کن چیزوں کو پوجتے ہو؟');
INSERT INTO "jalandhari" VALUES(37,86,'37:86','کیوں جھوٹ (بنا کر) خدا کے سوا اور معبودوں کے طالب ہو؟');
INSERT INTO "jalandhari" VALUES(37,87,'37:87','بھلا پروردگار عالم کے بارے میں تمہارا کیا خیال ہے؟');
INSERT INTO "jalandhari" VALUES(37,88,'37:88','تب انہوں نے ستاروں کی طرف ایک نظر کی');
INSERT INTO "jalandhari" VALUES(37,89,'37:89','اور کہا میں تو بیمار ہوں');
INSERT INTO "jalandhari" VALUES(37,90,'37:90','تب وہ ان سے پیٹھ پھیر کر لوٹ گئے');
INSERT INTO "jalandhari" VALUES(37,91,'37:91','پھر ابراہیم ان کے معبودوں کی طرف متوجہ ہوئے اور کہنے لگے کہ تم کھاتے کیوں نہیں؟');
INSERT INTO "jalandhari" VALUES(37,92,'37:92','تمہیں کیا ہوا ہے تم بولتے نہیں؟');
INSERT INTO "jalandhari" VALUES(37,93,'37:93','پھر ان کو داہنے ہاتھ سے مارنا (اور توڑنا) شروع کیا');
INSERT INTO "jalandhari" VALUES(37,94,'37:94','تو وہ لوگ ان کے پاس دوڑے ہوئے آئے');
INSERT INTO "jalandhari" VALUES(37,95,'37:95','انہوں نے کہا کہ تم ایسی چیزوں کو کیوں پوجتے ہو جن کو خود تراشتے ہو؟');
INSERT INTO "jalandhari" VALUES(37,96,'37:96','حالانکہ تم کو اور جو تم بناتے ہو اس کو خدا ہی نے پیدا کیا ہے');
INSERT INTO "jalandhari" VALUES(37,97,'37:97','وہ کہنے لگے کہ اس کے لئے ایک عمارت بناؤ پھر اس کو آگ کے ڈھیر میں ڈال دو');
INSERT INTO "jalandhari" VALUES(37,98,'37:98','غرض انہوں نے ان کے ساتھ ایک چال چلنی چاہی اور ہم نے ان ہی کو زیر کردیا');
INSERT INTO "jalandhari" VALUES(37,99,'37:99','اور ابراہیم بولے کہ میں اپنے پروردگار کی طرف جانے والا ہوں وہ مجھے رستہ دکھائے گا');
INSERT INTO "jalandhari" VALUES(37,100,'37:100','اے پروردگار مجھے (اولاد) عطا فرما (جو) سعادت مندوں میں سے (ہو)');
INSERT INTO "jalandhari" VALUES(37,101,'37:101','تو ہم نے ان کو ایک نرم دل لڑکے کی خوشخبری دی');
INSERT INTO "jalandhari" VALUES(37,102,'37:102','جب وہ ان کے ساتھ دوڑنے (کی عمر) کو پہنچا تو ابراہیم نے کہا کہ بیٹا میں خواب میں دیکھتا ہوں کہ (گویا) تم کو ذبح کر رہا ہوں تو تم سوچو کہ تمہارا کیا خیال ہے؟ انہوں نے کہا کہ ابا جو آپ کو حکم ہوا ہے وہی کیجیئے خدا نے چاہا تو آپ مجھے صابروں میں پایئے گا');
INSERT INTO "jalandhari" VALUES(37,103,'37:103','جب دونوں نے حکم مان لیا اور باپ نے بیٹے کو ماتھے کے بل لٹا دیا');
INSERT INTO "jalandhari" VALUES(37,104,'37:104','تو ہم نے ان کو پکارا کہ اے ابراہیم');
INSERT INTO "jalandhari" VALUES(37,105,'37:105','تم نے خواب کو سچا کر دکھایا۔ ہم نیکوکاروں کو ایسا ہی بدلہ دیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(37,106,'37:106','بلاشبہ یہ صریح آزمائش تھی');
INSERT INTO "jalandhari" VALUES(37,107,'37:107','اور ہم نے ایک بڑی قربانی کو ان کا فدیہ دیا');
INSERT INTO "jalandhari" VALUES(37,108,'37:108','اور پیچھے آنے والوں میں ابراہیم کا (ذکر خیر باقی) چھوڑ دیا');
INSERT INTO "jalandhari" VALUES(37,109,'37:109','کہ ابراہیم پر سلام ہو');
INSERT INTO "jalandhari" VALUES(37,110,'37:110','نیکوکاروں کو ہم ایسا ہی بدلہ دیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(37,111,'37:111','وہ ہمارے مومن بندوں میں سے تھے');
INSERT INTO "jalandhari" VALUES(37,112,'37:112','اور ہم نے ان کو اسحاق کی بشارت بھی دی (کہ وہ) نبی (اور) نیکوکاروں میں سے (ہوں گے)');
INSERT INTO "jalandhari" VALUES(37,113,'37:113','اور ہم نے ان پر اور اسحاق پر برکتیں نازل کی تھیں۔ اور ان دونوں اولاد کی میں سے نیکوکار بھی ہیں اور اپنے آپ پر صریح ظلم کرنے والے (یعنی گنہگار) بھی ہیں');
INSERT INTO "jalandhari" VALUES(37,114,'37:114','اور ہم نے موسیٰ اور ہارون پر بھی احسان کئے');
INSERT INTO "jalandhari" VALUES(37,115,'37:115','اور ان کو اور ان کی قوم کو مصیبت عظیمہ سے نجات بخشی');
INSERT INTO "jalandhari" VALUES(37,116,'37:116','اور ان کی مدد کی تو وہ غالب ہوگئے');
INSERT INTO "jalandhari" VALUES(37,117,'37:117','اور ان دونوں کو کتاب واضح (المطالب) عنایت کی');
INSERT INTO "jalandhari" VALUES(37,118,'37:118','اور ان کو سیدھا رستہ دکھایا');
INSERT INTO "jalandhari" VALUES(37,119,'37:119','اور پیچھے آنے والوں میں ان کا ذکر (خیر باقی) چھوڑ دیا');
INSERT INTO "jalandhari" VALUES(37,120,'37:120','کہ موسیٰ اور ہارون پر سلام');
INSERT INTO "jalandhari" VALUES(37,121,'37:121','بےشک ہم نیکوکاروں کو ایسا ہی بدلہ دیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(37,122,'37:122','وہ دونوں ہمارے مومن بندوں میں سے تھے');
INSERT INTO "jalandhari" VALUES(37,123,'37:123','اور الیاس بھی پیغمبروں میں سے تھے');
INSERT INTO "jalandhari" VALUES(37,124,'37:124','جب انہوں نے اپنی قوم سے کہا کہ تم ڈرتے کیوں نہیں؟');
INSERT INTO "jalandhari" VALUES(37,125,'37:125','کیا تم بعل کو پکارتے (اور اسے پوجتے) ہو اور سب سے بہتر پیدا کرنے والے کو چھوڑ دیتے ہو');
INSERT INTO "jalandhari" VALUES(37,126,'37:126','(یعنی) خدا کو جو تمہارا اور تمہارے اگلے باپ دادا کا پروردگار ہے');
INSERT INTO "jalandhari" VALUES(37,127,'37:127','تو ان لوگوں نے ان کو جھٹلا دیا۔ سو وہ (دوزخ میں) حاضر کئے جائیں گے');
INSERT INTO "jalandhari" VALUES(37,128,'37:128','ہاں خدا کے بندگان خاص (مبتلائے عذاب نہیں) ہوں گے');
INSERT INTO "jalandhari" VALUES(37,129,'37:129','اور ان کا ذکر (خیر) پچھلوں میں (باقی) چھوڑ دیا');
INSERT INTO "jalandhari" VALUES(37,130,'37:130','کہ اِل یاسین پر سلام');
INSERT INTO "jalandhari" VALUES(37,131,'37:131','ہم نیک لوگوں کو ایسا ہی بدلہ دیتے ہیں');
INSERT INTO "jalandhari" VALUES(37,132,'37:132','بےشک وہ ہمارے مومن بندوں میں سے تھے');
INSERT INTO "jalandhari" VALUES(37,133,'37:133','اور لوط بھی پیغمبروں میں سے تھے');
INSERT INTO "jalandhari" VALUES(37,134,'37:134','جب ہم نے ان کو اور ان کے گھر والوں کو سب کو (عذاب سے) نجات دی');
INSERT INTO "jalandhari" VALUES(37,135,'37:135','مگر ایک بڑھیا کہ پیچھے رہ جانے والوں میں تھی');
INSERT INTO "jalandhari" VALUES(37,136,'37:136','پھر ہم نے اوروں کو ہلاک کردیا');
INSERT INTO "jalandhari" VALUES(37,137,'37:137','اور تم دن کو بھی ان (کی بستیوں) کے پاس سے گزرتے رہتے ہو');
INSERT INTO "jalandhari" VALUES(37,138,'37:138','اور رات کو بھی۔ تو کیا تم عقل نہیں رکھتے');
INSERT INTO "jalandhari" VALUES(37,139,'37:139','اور یونس بھی پیغمبروں میں سے تھے');
INSERT INTO "jalandhari" VALUES(37,140,'37:140','جب بھاگ کر بھری ہوئی کشتی میں پہنچے');
INSERT INTO "jalandhari" VALUES(37,141,'37:141','اس وقت قرعہ ڈالا تو انہوں نے زک اُٹھائی');
INSERT INTO "jalandhari" VALUES(37,142,'37:142','پھر مچھلی نے ان کو نگل لیا اور وہ (قابل) ملامت (کام) کرنے والے تھے');
INSERT INTO "jalandhari" VALUES(37,143,'37:143','پھر اگر وہ (خدا کی) پاکی بیان نہ کرتے');
INSERT INTO "jalandhari" VALUES(37,144,'37:144','تو اس روز تک کہ لوگ دوبارہ زندہ کئے جائیں گے اسی کے پیٹ میں رہتے');
INSERT INTO "jalandhari" VALUES(37,145,'37:145','پھر ہم نے ان کو جب کہ وہ بیمار تھے فراخ میدان میں ڈال دیا');
INSERT INTO "jalandhari" VALUES(37,146,'37:146','اور ان پر کدو کا درخت اُگایا');
INSERT INTO "jalandhari" VALUES(37,147,'37:147','اور ان کو لاکھ یا اس سے زیادہ (لوگوں) کی طرف (پیغمبر بنا کر) بھیجا');
INSERT INTO "jalandhari" VALUES(37,148,'37:148','تو وہ ایمان لے آئے سو ہم نے بھی ان کو (دنیا میں) ایک وقت (مقرر) تک فائدے دیتے رہے');
INSERT INTO "jalandhari" VALUES(37,149,'37:149','ان سے پوچھو تو کہ بھلا تمہارے پروردگار کے لئے تو بیٹیاں اور ان کے لئے بیٹے');
INSERT INTO "jalandhari" VALUES(37,150,'37:150','یا ہم نے فرشتوں کو عورتیں بنایا اور وہ (اس وقت) موجود تھے');
INSERT INTO "jalandhari" VALUES(37,151,'37:151','دیکھو یہ اپنی جھوٹ بنائی ہوئی (بات) کہتے ہیں');
INSERT INTO "jalandhari" VALUES(37,152,'37:152','کہ خدا کے اولاد ہے کچھ شک نہیں کہ یہ جھوٹے ہیں');
INSERT INTO "jalandhari" VALUES(37,153,'37:153','کیا اس نے بیٹوں کی نسبت بیٹیوں کو پسند کیا ہے؟');
INSERT INTO "jalandhari" VALUES(37,154,'37:154','تم کیسے لوگ ہو، کس طرح کا فیصلہ کرتے ہو');
INSERT INTO "jalandhari" VALUES(37,155,'37:155','بھلا تم غور کیوں نہیں کرتے');
INSERT INTO "jalandhari" VALUES(37,156,'37:156','یا تمہارے پاس کوئی صریح دلیل ہے');
INSERT INTO "jalandhari" VALUES(37,157,'37:157','اگر تم سچے ہو تو اپنی کتاب پیش کرو');
INSERT INTO "jalandhari" VALUES(37,158,'37:158','اور انہوں نے خدا میں اور جنوں میں رشتہ مقرر کیا۔ حالانکہ جنات جانتے ہیں کہ وہ (خدا کے سامنے) حاضر کئے جائیں گے');
INSERT INTO "jalandhari" VALUES(37,159,'37:159','یہ جو کچھ بیان کرتے ہیں خدا اس سے پاک ہے');
INSERT INTO "jalandhari" VALUES(37,160,'37:160','مگر خدا کے بندگان خالص (مبتلائے عذاب نہیں ہوں گے)');
INSERT INTO "jalandhari" VALUES(37,161,'37:161','سو تم اور جن کو تم پوجتے ہو');
INSERT INTO "jalandhari" VALUES(37,162,'37:162','خدا کے خلاف بہکا نہیں سکتے');
INSERT INTO "jalandhari" VALUES(37,163,'37:163','مگر اس کو جو جہنم میں جانے والا ہے');
INSERT INTO "jalandhari" VALUES(37,164,'37:164','اور (فرشتے کہتے ہیں کہ) ہم میں سے ہر ایک کا ایک مقام مقرر ہے');
INSERT INTO "jalandhari" VALUES(37,165,'37:165','اور ہم صف باندھے رہتے ہیں');
INSERT INTO "jalandhari" VALUES(37,166,'37:166','اور (خدائے) پاک (ذات) کا ذکر کرتے رہتے ہیں');
INSERT INTO "jalandhari" VALUES(37,167,'37:167','اور یہ لوگ کہا کرتے تھے');
INSERT INTO "jalandhari" VALUES(37,168,'37:168','کہ اگر ہمارے پاس اگلوں کی کوئی نصیحت (کی کتاب) ہوتی');
INSERT INTO "jalandhari" VALUES(37,169,'37:169','تو ہم خدا کے خالص بندے ہوتے');
INSERT INTO "jalandhari" VALUES(37,170,'37:170','لیکن (اب) اس سے کفر کرتے ہیں سو عنقریب ان کو (اس کا نتیجہ) معلوم ہوجائے گا');
INSERT INTO "jalandhari" VALUES(37,171,'37:171','اور اپنے پیغام پہنچانے والے بندوں سے ہمارا وعدہ ہوچکا ہے');
INSERT INTO "jalandhari" VALUES(37,172,'37:172','کہ وہی (مظفرو) منصور ہیں');
INSERT INTO "jalandhari" VALUES(37,173,'37:173','اور ہمارا لشکر غالب رہے گا');
INSERT INTO "jalandhari" VALUES(37,174,'37:174','تو ایک وقت تک ان سے اعراض کئے رہو');
INSERT INTO "jalandhari" VALUES(37,175,'37:175','اور انہیں دیکھتے رہو۔ یہ بھی عنقریب (کفر کا انجام) دیکھ لیں گے');
INSERT INTO "jalandhari" VALUES(37,176,'37:176','کیا یہ ہمارے عذاب کے لئے جلدی کر رہے ہیں');
INSERT INTO "jalandhari" VALUES(37,177,'37:177','مگر جب وہ ان کے میدان میں آ اُترے گا تو جن کو ڈر سنا دیا گیا تھا ان کے لئے برا دن ہوگا');
INSERT INTO "jalandhari" VALUES(37,178,'37:178','اور ایک وقت تک ان سے منہ پھیرے رہو');
INSERT INTO "jalandhari" VALUES(37,179,'37:179','اور دیکھتے رہو یہ بھی عنقریب (نتیجہ) دیکھ لیں گے');
INSERT INTO "jalandhari" VALUES(37,180,'37:180','یہ جو کچھ بیان کرتے ہیں تمہارا پروردگار جو صاحب عزت ہے اس سے (پاک ہے)');
INSERT INTO "jalandhari" VALUES(37,181,'37:181','اور پیغمبروں پر سلام');
INSERT INTO "jalandhari" VALUES(37,182,'37:182','اور سب طرح کی تعریف خدائے رب العالمین کو (سزاوار) ہے');
INSERT INTO "jalandhari" VALUES(38,1,'38:1','ص۔ قسم ہے اس قرآن کی جو نصیحت دینے والا ہے (کہ تم حق پر ہو)');
INSERT INTO "jalandhari" VALUES(38,2,'38:2','مگر جو لوگ کافر ہیں وہ غرور اور مخالفت میں ہیں');
INSERT INTO "jalandhari" VALUES(38,3,'38:3','ہم نے ان سے پہلے بہت سی اُمتوں کو ہلاک کردیا تو وہ (عذاب کے وقت) لگے فریاد کرنے اور وہ رہائی کا وقت نہیں تھا');
INSERT INTO "jalandhari" VALUES(38,4,'38:4','اور انہوں نے تعجب کیا کہ ان کے پاس ان ہی میں سے ہدایت کرنے والا آیا اور کافر کہنے لگے کہ یہ تو جادوگر ہے جھوٹا');
INSERT INTO "jalandhari" VALUES(38,5,'38:5','کیا اس نے اتنے معبودوں کی جگہ ایک ہی معبود بنا دیا۔ یہ تو بڑی عجیب بات ہے');
INSERT INTO "jalandhari" VALUES(38,6,'38:6','تو ان میں جو معزز تھے وہ چل کھڑے ہوئے (اور بولے) کہ چلو اور اپنے معبودوں (کی پوجا) پر قائم رہو۔ بےشک یہ ایسی بات ہے جس سے (تم پر شرف وفضلیت) مقصود ہے');
INSERT INTO "jalandhari" VALUES(38,7,'38:7','یہ پچھلے مذہب میں ہم نے کبھی سنی ہی نہیں۔ یہ بالکل بنائی ہوئی بات ہے');
INSERT INTO "jalandhari" VALUES(38,8,'38:8','کیا ہم سب میں سے اسی پر نصیحت (کی کتاب) اُتری ہے؟ (نہیں) بلکہ یہ میری نصیحت کی کتاب سے شک میں ہیں۔ بلکہ انہوں نے ابھی میرے عذاب کا مزہ نہیں چکھا');
INSERT INTO "jalandhari" VALUES(38,9,'38:9','کیا ان کے پاس تمہارے پروردگار کی رحمت کے خزانے ہیں جو غالب اور بہت عطا کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(38,10,'38:10','یا آسمانوں اور زمین اور جو کچھ ان میں ہے ان (سب) پر ان ہی کی حکومت ہے۔ تو چاہیئے کہ رسیاں تان کر (آسمانوں) پر چڑھ جائیں');
INSERT INTO "jalandhari" VALUES(38,11,'38:11','یہاں شکست کھائے ہوئے گروہوں میں سے یہ بھی ایک لشکر ہے');
INSERT INTO "jalandhari" VALUES(38,12,'38:12','ان سے پہلے نوح کی قوم اور عاد اور میخوں والا فرعون (اور اس کی قوم کے لوگ) بھی جھٹلا چکے ہیں');
INSERT INTO "jalandhari" VALUES(38,13,'38:13','اور ثمود اور لوط کی قوم اور بن کے رہنے والے بھی۔ یہی وہ گروہ ہیں');
INSERT INTO "jalandhari" VALUES(38,14,'38:14','(ان) سب نے پیغمبروں کو جھٹلایا تو میرا عذاب (ان پر) آ واقع ہوا');
INSERT INTO "jalandhari" VALUES(38,15,'38:15','اور یہ لوگ تو صرف ایک زور کی آواز کا جس میں (شروع ہوئے پیچھے) کچھ وقفہ نہیں ہوگا، انتظار کرتے ہیں');
INSERT INTO "jalandhari" VALUES(38,16,'38:16','اور کہتے ہیں کہ اے ہمارے پروردگار ہم کو ہمارا حصہ حساب کے دن سے پہلے ہی دے دے');
INSERT INTO "jalandhari" VALUES(38,17,'38:17','(اے پیغمبر) یہ جو کچھ کہتے ہیں اس پر صبر کرو۔ اور ہمارے بندے داؤد کو یاد کرو جو صاحب قوت تھے (اور) بےشک وہ رجوع کرنے والے تھے');
INSERT INTO "jalandhari" VALUES(38,18,'38:18','ہم نے پہاڑوں کو ان کے زیر فرمان کردیا تھا کہ صبح وشام ان کے ساتھ (خدائے) پاک (کا) ذکر کرتے تھے');
INSERT INTO "jalandhari" VALUES(38,19,'38:19','اور پرندوں کو بھی کہ جمع رہتے تھے۔ سب ان کے فرمانبردار تھے');
INSERT INTO "jalandhari" VALUES(38,20,'38:20','اور ہم نے ان کی بادشاہی کو مستحکم کیا اور ان کو حکمت عطا کی اور (خصومت کی) بات کا فیصلہ (سکھایا)');
INSERT INTO "jalandhari" VALUES(38,21,'38:21','بھلا تمہارے پاس ان جھگڑنے والوں کی بھی خبر آئی ہے جب وہ دیوار پھاند کر عبادت خانے میں داخل ہوئے');
INSERT INTO "jalandhari" VALUES(38,22,'38:22','جس وقت وہ داؤد کے پاس آئے تو وہ ان سے گھبرا گئے انہوں نے کہا کہ خوف نہ کیجیئے۔ ہم دونوں کا ایک مقدمہ ہے کہ ہم میں سے ایک نے دوسرے پر زیادتی کی ہے تو آپ ہم میں انصاف کا فیصلہ کر دیجیئے اور بےانصافی نہ کیجیئے گا اور ہم کو سیدھا رستہ دکھا دیجیئے');
INSERT INTO "jalandhari" VALUES(38,23,'38:23','(کیفیت یہ ہے کہ) یہ میرا بھائی ہے اس کے (ہاں) ننانوے دنبیاں ہیں اور میرے (پاس) ایک دُنبی ہے۔ یہ کہتا ہے کہ یہ بھی میرے حوالے کردے اور گفتگو میں مجھ پر زبردستی کرتا ہے');
INSERT INTO "jalandhari" VALUES(38,24,'38:24','انہوں نے کہا کہ یہ جو تیری دنبی مانگتا ہے کہ اپنی دنبیوں میں ملالے بےشک تجھ پر ظلم کرتا ہے۔ اور اکثر شریک ایک دوسرے پر زیادتی ہی کیا کرتے ہیں۔ ہاں جو ایمان لائے اور عمل نیک کرتے رہے اور ایسے لوگ بہت کم ہیں۔ اور داؤد نے خیال کیا کہ (اس واقعے سے) ہم نے ان کو آزمایا ہے تو انہوں نے اپنے پروردگار سے مغفرت مانگی اور جھک کر گڑ پڑے اور (خدا کی طرف) رجوع کیا');
INSERT INTO "jalandhari" VALUES(38,25,'38:25','تو ہم نے ان کو بخش دیا۔ اور بےشک ان کے لئے ہمارے ہاں قرب اور عمدہ مقام ہے');
INSERT INTO "jalandhari" VALUES(38,26,'38:26','اے داؤد ہم نے تم کو زمین میں بادشاہ بنایا ہے تو لوگوں میں انصاف کے فیصلے کیا کرو اور خواہش کی پیروی نہ کرنا کہ وہ تمہیں خدا کے رستے سے بھٹکا دے گی۔ جو لوگ خدا کے رستے سے بھٹکتے ہیں ان کے لئے سخت عذاب (تیار) ہے کہ انہوں نے حساب کے دن کو بھلا دیا');
INSERT INTO "jalandhari" VALUES(38,27,'38:27','اور ہم نے آسمان اور زمین کو اور جو کائنات ان میں ہے اس کو خالی از مصلحت نہیں پیدا کیا۔ یہ ان کا گمان ہے جو کافر ہیں۔ سو کافروں کے لئے دوزخ کا عذاب ہے');
INSERT INTO "jalandhari" VALUES(38,28,'38:28','جو لوگ ایمان لائے اور عمل نیک کرتے رہے۔ کیا ان کو ہم ان کی طرح کر دیں گے جو ملک میں فساد کرتے ہیں۔ یا پرہیزگاروں کو بدکاروں کی طرح کر دیں گے');
INSERT INTO "jalandhari" VALUES(38,29,'38:29','(یہ) کتاب جو ہم نے تم پر نازل کی ہے بابرکت ہے تاکہ لوگ اس کی آیتوں میں غور کریں اور تاکہ اہل عقل نصیحت پکڑیں');
INSERT INTO "jalandhari" VALUES(38,30,'38:30','اور ہم نے داؤد کو سلیمان عطا کئے۔ بہت خوب بندے (تھے اور) وہ (خدا کی طرف) رجوع کرنے والے تھے');
INSERT INTO "jalandhari" VALUES(38,31,'38:31','جب ان کے سامنے شام کو خاصے کے گھوڑے پیش کئے گئے');
INSERT INTO "jalandhari" VALUES(38,32,'38:32','تو کہنے لگے کہ میں نے اپنے پروردگار کی یاد سے (غافل ہو کر) مال کی محبت اختیار کی۔ یہاں تک کہ (آفتاب) پردے میں چھپ گیا');
INSERT INTO "jalandhari" VALUES(38,33,'38:33','(بولے کہ) ان کو میرے پاس واپس لے آؤ۔ پھر ان کی ٹانگوں اور گردنوں پر ہاتھ پھیرنے لگے');
INSERT INTO "jalandhari" VALUES(38,34,'38:34','اور ہم نے سلیمان کی آزمائش کی اور ان کے تخت پر ایک دھڑ ڈال دیا پھر انہوں نے (خدا کی طرف) رجوع کیا');
INSERT INTO "jalandhari" VALUES(38,35,'38:35','(اور) دعا کی کہ اے پروردگار مجھے مغفرت کر اور مجھ کو ایسی بادشاہی عطا کر کہ میرے بعد کسی کو شایاں نہ ہو۔ بےشک تو بڑا عطا فرمانے والا ہے');
INSERT INTO "jalandhari" VALUES(38,36,'38:36','پھر ہم نے ہوا کو ان کے زیرفرمان کردیا کہ جہاں وہ پہنچنا چاہتے ان کے حکم سے نرم نرم چلنے لگتی');
INSERT INTO "jalandhari" VALUES(38,37,'38:37','اَور دیووں کو بھی (ان کے زیرفرمان کیا) وہ سب عمارتیں بنانے والے اور غوطہ مارنے والے تھے');
INSERT INTO "jalandhari" VALUES(38,38,'38:38','اور اَوروں کو بھی جو زنجیروں میں جکڑے ہوئے تھے');
INSERT INTO "jalandhari" VALUES(38,39,'38:39','(ہم نے کہا) یہ ہماری بخشش ہے (چاہو) تو احسان کرو یا (چاہو تو) رکھ چھوڑو (تم سے) کچھ حساب نہیں ہے');
INSERT INTO "jalandhari" VALUES(38,40,'38:40','اور بےشک ان کے لئے ہمارے ہاں قُرب اور عمدہ مقام ہے');
INSERT INTO "jalandhari" VALUES(38,41,'38:41','اور ہمارے بندے ایوب کو یاد کرو جب انہوں نے اپنے رب کو پکارا کہ (بار الہٰا) شیطان نے مجھ کو ایذا اور تکلیف دے رکھی ہے');
INSERT INTO "jalandhari" VALUES(38,42,'38:42','(ہم نے کہا کہ زمین پر) لات مارو (دیکھو) یہ (چشمہ نکل آیا) نہانے کو ٹھنڈا اور پینے کو (شیریں)');
INSERT INTO "jalandhari" VALUES(38,43,'38:43','اور ہم نے ان کو اہل و عیال اور ان کے ساتھ ان کے برابر اور بخشے۔ (یہ) ہماری طرف سے رحمت اور عقل والوں کے لئے نصیحت تھی');
INSERT INTO "jalandhari" VALUES(38,44,'38:44','اور اپنے ہاتھ میں جھاڑو لو اور اس سے مارو اور قسم نہ توڑو۔ بےشک ہم نے ان کو ثابت قدم پایا۔ بہت خوب بندے تھے بےشک وہ رجوع کرنے والے تھے');
INSERT INTO "jalandhari" VALUES(38,45,'38:45','اور ہمارے بندوں ابراہیم اور اسحاق اور یعقوب کو یاد کرو جو ہاتھوں والے اور آنکھوں والے تھے');
INSERT INTO "jalandhari" VALUES(38,46,'38:46','ہم نے ان کو ایک (صفت) خاص (آخرت کے) گھر کی یاد سے ممتاز کیا تھا');
INSERT INTO "jalandhari" VALUES(38,47,'38:47','اور ہمارے نزدیک منتخب اور نیک لوگوں میں سے تھے');
INSERT INTO "jalandhari" VALUES(38,48,'38:48','اور اسمٰعیل اور الیسع اور ذوالکفل کو یاد کرو۔ وہ سب نیک لوگوں میں سے تھے');
INSERT INTO "jalandhari" VALUES(38,49,'38:49','یہ نصیحت ہے اور پرہیزگاروں کے لئے تو عمدہ مقام ہے');
INSERT INTO "jalandhari" VALUES(38,50,'38:50','ہمیشہ رہنے کے باغ جن کے دروازے ان کے لئے کھلے ہوں گے');
INSERT INTO "jalandhari" VALUES(38,51,'38:51','ان میں تکیٴے لگائے بیٹھے ہوں گے اور (کھانے پینے کے لئے) بہت سے میوے اور شراب منگواتے رہیں گے');
INSERT INTO "jalandhari" VALUES(38,52,'38:52','اور ان کے پاس نیچی نگاہ رکھنے والی (اور) ہم عمر (عورتیں) ہوں گی');
INSERT INTO "jalandhari" VALUES(38,53,'38:53','یہ وہ چیزیں ہیں جن کا حساب کے دن کے لئے تم سے وعدہ کیا جاتا تھا');
INSERT INTO "jalandhari" VALUES(38,54,'38:54','یہ ہمارا رزق ہے جو کبھی ختم نہیں ہوگا');
INSERT INTO "jalandhari" VALUES(38,55,'38:55','یہ (نعمتیں تو فرمانبرداروں کے لئے ہیں) اور سرکشوں کے لئے برا ٹھکانا ہے');
INSERT INTO "jalandhari" VALUES(38,56,'38:56','(یعنی) دوزخ۔ جس میں وہ داخل ہوں گے اور وہ بری آرام گاہ ہے');
INSERT INTO "jalandhari" VALUES(38,57,'38:57','یہ کھولتا ہوا گرم پانی اور پیپ (ہے) اب اس کے مزے چکھیں');
INSERT INTO "jalandhari" VALUES(38,58,'38:58','اور اسی طرح کے اور بہت سے (عذاب ہوں گے)');
INSERT INTO "jalandhari" VALUES(38,59,'38:59','یہ ایک فوج ہے جو تمہارے ساتھ داخل ہوگی۔ ان کو خوشی نہ ہو یہ دوزخ میں جانے والے ہیں');
INSERT INTO "jalandhari" VALUES(38,60,'38:60','کہیں گے بلکہ تم ہی کو خوشی نہ ہو۔ تم ہی تو یہ (بلا) ہمارے سامنے لائے سو (یہ) برا ٹھکانا ہے');
INSERT INTO "jalandhari" VALUES(38,61,'38:61','وہ کہیں گے اے پروردگار جو اس کو ہمارے سامنے لایا ہے اس کو دوزخ میں دونا عذاب دے');
INSERT INTO "jalandhari" VALUES(38,62,'38:62','اور کہیں گے کیا سبب ہے کہ (یہاں) ہم ان شخصوں کو نہیں دیکھتے جن کو بروں میں شمار کرتے تھے');
INSERT INTO "jalandhari" VALUES(38,63,'38:63','کیا ہم نے ان سے ٹھٹھا کیا ہے یا (ہماری) آنکھیں ان (کی طرف) سے پھر گئی ہیں؟');
INSERT INTO "jalandhari" VALUES(38,64,'38:64','بےشک یہ اہل دوزخ کا جھگڑنا برحق ہے');
INSERT INTO "jalandhari" VALUES(38,65,'38:65','کہہ دو کہ میں تو صرف ہدایت کرنے والا ہوں۔ اور خدائے یکتا اور غالب کے سوا کوئی معبود نہیں');
INSERT INTO "jalandhari" VALUES(38,66,'38:66','جو آسمانوں اور زمین اور جو مخلوق ان میں ہے سب کا مالک ہے غالب (اور) بخشنے والا');
INSERT INTO "jalandhari" VALUES(38,67,'38:67','کہہ دو کہ یہ ایک بڑی (ہولناک چیز کی) خبر ہے');
INSERT INTO "jalandhari" VALUES(38,68,'38:68','جس کو تم دھیان میں نہیں لاتے');
INSERT INTO "jalandhari" VALUES(38,69,'38:69','مجھ کو اوپر کی مجلس (والوں) کا جب وہ جھگڑتے تھے کچھ بھی علم نہ تھا');
INSERT INTO "jalandhari" VALUES(38,70,'38:70','میری طرف تو یہی وحی کی جاتی ہے کہ میں کھلم کھلا ہدایت کرنے والا ہوں');
INSERT INTO "jalandhari" VALUES(38,71,'38:71','جب تمہارے پروردگار نے فرشتوں سے کہا کہ میں مٹی سے انسان بنانے والا ہوں');
INSERT INTO "jalandhari" VALUES(38,72,'38:72','جب اس کو درست کرلوں اور اس میں اپنی روح پھونک دوں تو اس کے آگے سجدے میں گر پڑنا');
INSERT INTO "jalandhari" VALUES(38,73,'38:73','تو تمام فرشتوں نے سجدہ کیا');
INSERT INTO "jalandhari" VALUES(38,74,'38:74','مگر شیطان اکڑ بیٹھا اور کافروں میں ہوگیا');
INSERT INTO "jalandhari" VALUES(38,75,'38:75','خدا نے) فرمایا کہ اے ابلیس جس شخص کو میں نے اپنے ہاتھوں سے بنایا اس کے آگے سجدہ کرنے سے تجھے کس چیز نے منع کیا۔ کیا تو غرور میں آگیا یا اونچے درجے والوں میں تھا؟');
INSERT INTO "jalandhari" VALUES(38,76,'38:76','بولا کہ میں اس سے بہتر ہوں (کہ )تو نے مجھ کو کو آگ سے پیدا کیا اور اِسے مٹی سے بنایا');
INSERT INTO "jalandhari" VALUES(38,77,'38:77','فرمایا یہاں سے نکل جا تو مردود ہے');
INSERT INTO "jalandhari" VALUES(38,78,'38:78','اور تجھ پر قیامت کے دن تک میری لعنت (پڑتی) رہے گی');
INSERT INTO "jalandhari" VALUES(38,79,'38:79','کہنے لگا کہ میرے پروردگار مجھے اس روز تک کہ لوگ اٹھائے جائیں مہلت دے');
INSERT INTO "jalandhari" VALUES(38,80,'38:80','فرمایا کہ تجھ کو مہلت دی جاتی ہے');
INSERT INTO "jalandhari" VALUES(38,81,'38:81','اس روز تک جس کا وقت مقرر ہے');
INSERT INTO "jalandhari" VALUES(38,82,'38:82','کہنے لگا کہ مجھے تیری عزت کی قسم میں ان سب کو بہکاتا رہوں گا');
INSERT INTO "jalandhari" VALUES(38,83,'38:83','سوا ان کے جو تیرے خالص بندے ہیں');
INSERT INTO "jalandhari" VALUES(38,84,'38:84','فرمایا سچ (ہے) اور میں بھی سچ کہتا ہوں');
INSERT INTO "jalandhari" VALUES(38,85,'38:85','کہ میں تجھ سے اور جو ان میں سے تیری پیروی کریں گے سب سے جہنم کو بھر دوں گا');
INSERT INTO "jalandhari" VALUES(38,86,'38:86','اے پیغمبر کہہ دو کہ میں تم سے اس کا صلہ نہیں مانگتا اور نہ میں بناوٹ کرنے والوں میں ہوں');
INSERT INTO "jalandhari" VALUES(38,87,'38:87','یہ قرآن تو اہل عالم کے لئے نصیحت ہے');
INSERT INTO "jalandhari" VALUES(38,88,'38:88','اور تم کو اس کا حال ایک وقت کے بعد معلوم ہوجائے گا');
INSERT INTO "jalandhari" VALUES(39,1,'39:1','اس کتاب کا اُتارا جانا خدائے غالب (اور) حکمت والے کی طرف سے ہے');
INSERT INTO "jalandhari" VALUES(39,2,'39:2','(اے پیغمبر) ہم نے یہ کتاب تمہاری طرف سچائی کے ساتھ نازل کی ہے تو خدا کی عبادت کرو (یعنی) اس کی عبادت کو (شرک سے) خالص کرکے');
INSERT INTO "jalandhari" VALUES(39,3,'39:3','دیکھو خالص عبادت خدا ہی کے لئے (زیبا ہے) اور جن لوگوں نے اس کے سوا اور دوست بنائے ہیں۔ (وہ کہتے ہیں کہ) ہم ان کو اس لئے پوجتے ہیں کہ ہم کو خدا کا مقرب بنادیں۔ تو جن باتوں میں یہ اختلاف کرتے ہیں خدا ان میں ان کا فیصلہ کردے گا۔ بےشک خدا اس شخص کو جو جھوٹا ناشکرا ہے ہدایت نہیں دیتا');
INSERT INTO "jalandhari" VALUES(39,4,'39:4','اگر خدا کسی کو اپنا بیٹا بنانا چاہتا تو اپنی مخلوق میں سے جس کو چاہتا انتخاب کرلیتا۔ وہ پاک ہے وہی تو خدا یکتا (اور) غالب ہے');
INSERT INTO "jalandhari" VALUES(39,5,'39:5','اسی نے آسمانوں اور زمین کو تدبیر کے ساتھ پیدا کیا ہے۔ (اور) وہی رات کو دن پر لپیٹتا ہے اور دن کو رات پر لپیٹتا ہے اور اسی نے سورج اور چاند کو بس میں کر رکھا ہے۔ سب ایک وقت مقرر تک چلتے رہیں گے۔ دیکھو وہی غالب (اور) بخشنے والا ہے');
INSERT INTO "jalandhari" VALUES(39,6,'39:6','اسی نے تم کو ایک شخص سے پیدا کیا پھر اس سے اس کا جوڑا بنایا اور اسی نے تمہارے لئے چار پایوں میں سے آٹھ جوڑے بنائے۔ وہی تم کو تمہاری ماؤں کے پیٹ میں (پہلے) ایک طرح پھر دوسری طرح تین اندھیروں میں بناتا ہے۔ یہی خدا تمہارا پروردگار ہے اسی کی بادشاہی ہے۔ اس کے سوا کوئی معبود نہیں پھر تم کہاں پھرے جاتے ہو؟');
INSERT INTO "jalandhari" VALUES(39,7,'39:7','اگر ناشکری کرو گے تو خدا تم سے بےپروا ہے۔ اور وہ اپنے بندوں کے لئے ناشکری پسند نہیں کرتا اور اگر شکر کرو گے تو وہ اس کو تمہارے لئے پسند کرے گا۔ اور کوئی اٹھانے والا دوسرے کا بوجھ نہیں اٹھائے گا۔ پھر تم اپنے پروردگار کی طرف لوٹنا ہے۔ پھر جو کچھ تم کرتے رہے وہ تم کو بتائے گا۔ وہ تو دلوں کی پوشیدہ باتوں تک سے آگاہ ہے');
INSERT INTO "jalandhari" VALUES(39,8,'39:8','اور جب انسان کو تکلیف پہنچتی ہے تو اپنے پروردگار کو پکارتا (اور) اس کی طرف دل سے رجوع کرتا ہے۔ پھر جب وہ اس کو اپنی طرف سے کوئی نعمت دیتا ہے تو جس کام کے لئے پہلے اس کو پکارتا ہے اسے بھول جاتا ہے اور خدا کا شریک بنانے لگتا ہے تاکہ (لوگوں کو) اس کے رستے سے گمراہ کرے۔ کہہ دو کہ (اے کافر نعمت) اپنی ناشکری سے تھوڑا سا فائدہ اٹھالے۔ پھر تُو تو دوزخیوں میں ہوگا');
INSERT INTO "jalandhari" VALUES(39,9,'39:9','(بھلا مشرک اچھا ہے) یا وہ جو رات کے وقتوں میں زمین پر پیشانی رکھ کر اور کھڑے ہو کر عبادت کرتا اور آخرت سے ڈرتا اور اپنے پروردگار کی رحمت کی امید رکھتا ہے۔ کہو بھلا جو لوگ علم رکھتے ہیں اور جو نہیں رکھتے دونوں برابر ہوسکتے ہیں؟ (اور) نصیحت تو وہی پکڑتے ہیں جو عقلمند ہیں');
INSERT INTO "jalandhari" VALUES(39,10,'39:10','کہہ دو کہ اے میرے بندو جو ایمان لائے ہو اپنے پروردگار سے ڈرو۔ جنہوں نے اس دنیا میں نیکی کی ان کے لئے بھلائی ہے۔ اور خدا کی زمین کشادہ ہے۔ جو صبر کرنے والے ہیں ان کو بےشمار ثواب ملے گا');
INSERT INTO "jalandhari" VALUES(39,11,'39:11','کہہ دو کہ مجھ سے ارشاد ہوا ہے کہ خدا کی عبادت کو خالص کرکے اس کی بندگی کروں');
INSERT INTO "jalandhari" VALUES(39,12,'39:12','اور یہ بھی ارشاد ہوا ہے کہ میں سب سے اول مسلمان بنوں');
INSERT INTO "jalandhari" VALUES(39,13,'39:13','کہہ دو کہ اگر میں اپنے پروردگار کا حکم نہ مانوں تو مجھے بڑے دن کے عذاب سے ڈر لگتا ہے');
INSERT INTO "jalandhari" VALUES(39,14,'39:14','کہہ دو کہ میں اپنے دین کو (شرک سے) خالص کرکے اس کی عبادت کرتا ہوں');
INSERT INTO "jalandhari" VALUES(39,15,'39:15','تو تم اس کے سوا جس کی چاہو پرستش کرو۔ کہہ دو کہ نقصان اٹھانے والے وہی لوگ ہیں جنہوں نے قیامت کے دن اپنے آپ کو اور اپنے گھر والوں کو نقصان میں ڈالا۔ دیکھو یہی صریح نقصان ہے');
INSERT INTO "jalandhari" VALUES(39,16,'39:16','ان کے اوپر تو آگ کے سائبان ہوں گے اور نیچے (اس کے) فرش ہوں گے۔ یہ وہ (عذاب) ہے جس سے خدا اپنے بندوں کو ڈراتا ہے۔ تو اے میرے بندو مجھ سے ڈرتے رہو');
INSERT INTO "jalandhari" VALUES(39,17,'39:17','اور جنہوں نے اس سے اجتناب کیا کہ بتوں کو پوجیں اور خدا کی طرف رجوع کیا ان کے لئے بشارت ہے۔ تو میرے بندوں کو بشارت سنا دو');
INSERT INTO "jalandhari" VALUES(39,18,'39:18','جو بات کو سنتے اور اچھی باتوں کی پیروی کرتے ہیں۔ یہی وہ لوگ ہیں جن کو خدا نے ہدایت دی اور یہی عقل والے ہیں');
INSERT INTO "jalandhari" VALUES(39,19,'39:19','بھلا جس شخص پر عذاب کا حکم صادر ہوچکا۔ تو کیا تم (ایسے) دوزخی کو مخلصی دے سکو گے؟');
INSERT INTO "jalandhari" VALUES(39,20,'39:20','لیکن جو لوگ اپنے پروردگار سے ڈرتے ہیں ان کے لئے اونچے اونچے محل ہیں جن کے اوپر بالا خانے بنے ہوئے ہیں۔ (اور) ان کے نیچے نہریں بہہ رہی ہیں۔ (یہ) خدا کا وعدہ ہے۔ خدا وعدے کے خلاف نہیں کرتا');
INSERT INTO "jalandhari" VALUES(39,21,'39:21','کیا تم نے نہیں دیکھا کہ خدا آسمان سے پانی نازل کرتا پھر اس کو زمین میں چشمے بنا کر جاری کرتا پھر اس سے کھیتی اُگاتا ہے جس کے طرح طرح کے رنگ ہوتے ہیں۔ پھر وہ خشک ہوجاتی ہے تو تم اس کو دیکھتے ہو (کہ) زرد (ہوگئی ہے) پھر اسے چورا چورا کر دیتا ہے۔ بےشک اس میں عقل والوں کے لئے نصیحت ہے');
INSERT INTO "jalandhari" VALUES(39,22,'39:22','بھلا جس شخص کا سینہ خدا نے اسلام کے لئے کھول دیا ہو اور وہ اپنے پروردگار کی طرف سے روشنی پر ہو (تو کیا وہ سخت دل کافر کی طرح ہوسکتا ہے) پس ان پر افسوس ہے جن کے دل خدا کی یاد سے سخت ہو رہے ہیں۔ اور یہی لوگ صریح گمراہی میں ہیں');
INSERT INTO "jalandhari" VALUES(39,23,'39:23','خدا نے نہایت اچھی باتیں نازل فرمائی ہیں (یعنی) کتاب (جس کی آیتیں باہم) ملتی جلتی (ہیں) اور دہرائی جاتی (ہیں) جو لوگ اپنے پروردگار سے ڈرتے ہیں ان کے بدن کے (اس سے) رونگٹے کھڑے ہوجاتے ہیں۔ پھر ان کے بدن اور دل نرم (ہو کر) خدا کی یاد کی طرف (متوجہ) ہوجاتے ہیں۔ یہی خدا کی ہدایت ہے وہ اس سے جس کو چاہتا ہے ہدایت دیتا ہے۔ اور جس کو خدا گمراہ کرے اس کو کوئی ہدایت دینے والا نہیں');
INSERT INTO "jalandhari" VALUES(39,24,'39:24','بھلا جو شخص قیامت کے دن اپنے منہ سے برے عذاب کو روکتا ہو (کیا وہ ویسا ہوسکتا ہے جو چین میں ہو) اور ظالموں سے کہا جائے گا کہ جو کچھ تم کرتے رہے تھے اس کے مزے چکھو');
INSERT INTO "jalandhari" VALUES(39,25,'39:25','جو لوگ ان سے پہلے تھے انہوں نے بھی تکذیب کی تھی تو ان پر عذاب ایسی جگہ سے آگیا کہ ان کو خبر ہی نہ تھی');
INSERT INTO "jalandhari" VALUES(39,26,'39:26','پھر ان کو خدا نے دنیا کی زندگی میں رسوائی کا مزہ چکھا دیا۔ اور آخرت کا عذاب تو بہت بڑا ہے۔ کاش یہ سمجھ رکھتے');
INSERT INTO "jalandhari" VALUES(39,27,'39:27','اور ہم نے لوگوں کے (سمجھانے کے) لئے اس قرآن میں ہر طرح کی مثالیں بیان کی ہیں تاکہ وہ نصیحت پکڑیں');
INSERT INTO "jalandhari" VALUES(39,28,'39:28','یہ) قرآن عربی (ہے) جس میں کوئی عیب (اور اختلاف) نہیں تاکہ وہ ڈر مانیں');
INSERT INTO "jalandhari" VALUES(39,29,'39:29','خدا ایک مثال بیان کرتا ہے کہ ایک شخص ہے جس میں کئی (آدمی) شریک ہیں۔ (مختلف المزاج اور) بدخو اور ایک آدمی خاص ایک شخص کا (غلام) ہے۔ بھلا دونوں کی حالت برابر ہے۔ (نہیں) الحمدلله بلکہ یہ اکثر لوگ نہیں جانتے');
INSERT INTO "jalandhari" VALUES(39,30,'39:30','(اے پیغمبر) تم بھی مر جاؤ گے اور یہ بھی مر جائیں گے');
INSERT INTO "jalandhari" VALUES(39,31,'39:31','پھر تم سب قیامت کے دن اپنے پروردگار کے سامنے جھگڑو گے (اور جھگڑا فیصل کردیا جائے گا)');
INSERT INTO "jalandhari" VALUES(39,32,'39:32','تو اس سے بڑھ کر ظالم کون جو خدا پر جھوٹ بولے اور سچی بات جب اس کے پاس پہنچ جائے تو اسے جھٹلائے۔ کیا جہنم میں کافروں کا ٹھکانا نہیں ہے؟');
INSERT INTO "jalandhari" VALUES(39,33,'39:33','اور جو شخص سچی بات لے کر آیا اور جس نے اس کی تصدیق کی وہی لوگ متقی ہیں');
INSERT INTO "jalandhari" VALUES(39,34,'39:34','وہ جو چاہیں گے ان کے لئے ان کے پروردگار کے پاس (موجود) ہے۔ نیکوکاروں کا یہی بدلہ ہے');
INSERT INTO "jalandhari" VALUES(39,35,'39:35','تاکہ خدا ان سے برائیوں کو جو انہوں نے کیں دور کردے اور نیک کاموں کا جو وہ کرتے رہے ان کو بدلہ دے');
INSERT INTO "jalandhari" VALUES(39,36,'39:36','کیا خدا اپنے بندوں کو کافی نہیں۔ اور یہ تم کو ان لوگوں سے جو اس کے سوا ہیں (یعنی غیر خدا سے) ڈراتے ہیں۔ اور جس کو خدا گمراہ کرے اسے کوئی ہدایت دینے والا نہیں');
INSERT INTO "jalandhari" VALUES(39,37,'39:37','اور جس کو خدا ہدایت دے اس کو کوئی گمراہ کرنے والا نہیں۔ کیا خدا غالب (اور) بدلہ لینے والا نہیں ہے؟');
INSERT INTO "jalandhari" VALUES(39,38,'39:38','اور اگر تم ان سے پوچھو کہ آسمانوں اور زمین کو کس نے پیدا کیا تو کہہ دیں کہ خدا نے۔ کہو کہ بھلا دیکھو تو جن کو تم خدا کے سوا پکارتے ہو۔ اگر خدا مجھ کو کوئی تکلیف پہنچانی چاہے تو کیا وہ اس تکلیف کو دور کرسکتے ہیں یا اگر مجھ پر مہربانی کرنا چاہے تو وہ اس کی مہربانی کو روک سکتے ہیں؟ کہہ دو کہ مجھے خدا ہی کافی ہے۔ بھروسہ رکھنے والے اسی پر بھروسہ رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(39,39,'39:39','کہہ دو کہ اے قوم تم اپنی جگہ عمل کئے جاؤ میں (اپنی جگہ) عمل کئے جاتا ہوں۔ عنقریب تم کو معلوم ہوجائے گا');
INSERT INTO "jalandhari" VALUES(39,40,'39:40','کہ کس پر عذاب آتا ہے جو اسے رسوا کرے گا۔ اور کس پر ہمیشہ کا عذاب نازل ہوتا ہے');
INSERT INTO "jalandhari" VALUES(39,41,'39:41','ہم نے تم پر کتاب لوگوں (کی ہدایت) کے لئے سچائی کے ساتھ نازل کی ہے۔ تو جو شخص ہدایت پاتا ہے تو اپنے (بھلے کے) لئے اور جو گمراہ ہوتا ہے گمراہی سے تو اپنا ہی نقصان کرتا ہے۔ اور (اے پیغمبر) تم ان کے ذمہ دار نہیں ہو');
INSERT INTO "jalandhari" VALUES(39,42,'39:42','خدا لوگوں کے مرنے کے وقت ان کی روحیں قبض کرلیتا ہے اور جو مرے نہیں (ان کی روحیں) سوتے میں (قبض کرلیتا ہے) پھر جن پر موت کا حکم کرچکتا ہے ان کو روک رکھتا ہے اور باقی روحوں کو ایک وقت مقرر تک کے لئے چھوڑ دیتا ہے۔ جو لوگ فکر کرتے ہیں ان کے لئے اس میں نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(39,43,'39:43','کیا انہوں نے خدا کے سوا اور سفارشی بنالئے ہیں۔ کہو کہ خواہ وہ کسی چیز کا بھی اختیار نہ رکھتے ہوں اور نہ (کچھ) سمجھتے ہی ہوں');
INSERT INTO "jalandhari" VALUES(39,44,'39:44','کہہ دو کہ سفارش تو سب خدا ہی کے اختیار میں ہے۔ اسی کے لئے آسمانوں اور زمین کی بادشاہت ہے۔ پھر تم اسی کی طرف لوٹ کر جاؤ گے');
INSERT INTO "jalandhari" VALUES(39,45,'39:45','اور جب تنہا خدا کا ذکر کیا جاتا ہے تو جو لوگ آخرت پر ایمان نہیں رکھتے ان کے دل منقبض ہوجاتے ہیں۔ اور جب اس کے سوا اوروں کا ذکر کیا جاتا ہے تو خوش ہوجاتے ہیں');
INSERT INTO "jalandhari" VALUES(39,46,'39:46','کہو کہ اے خدا (اے) آسمانوں اور زمین کے پیدا کرنے والے (اور) پوشیدہ اور ظاہر کے جاننے والے تو ہی اپنے بندوں میں ان باتوں کا جن میں وہ اختلاف کرتے رہے ہیں فیصلہ کرے گا');
INSERT INTO "jalandhari" VALUES(39,47,'39:47','اور اگر ظالموں کے پاس وہ سب (مال ومتاع) ہو جو زمین میں ہے اور اس کے ساتھ اسی قدر اور ہو تو قیامت کے روز برے عذاب (سے مخلصی پانے) کے بدلے میں دے دیں۔ اور ان پر خدا کی طرف سے وہ امر ظاہر ہوجائے گا جس کا ان کو خیال بھی نہ تھا');
INSERT INTO "jalandhari" VALUES(39,48,'39:48','اور ان کے اعمال کی برائیاں ان پر ظاہر ہوجائیں گی اور جس (عذاب) کی وہ ہنسی اُڑاتے تھے وہ ان کو آگھیرے گا');
INSERT INTO "jalandhari" VALUES(39,49,'39:49','جب انسان کو تکلیف پہنچتی ہے تو ہمیں پکارنے لگتا ہے۔ پھر جب ہم اس کو اپنی طرف سے نعمت بخشتے ہیں تو کہتا ہے کہ یہ تو مجھے (میرے) علم (ودانش) کے سبب ملی ہے۔ (نہیں) بلکہ وہ آزمائش ہے مگر ان میں سے اکثر نہیں جانتے');
INSERT INTO "jalandhari" VALUES(39,50,'39:50','جو لوگ ان سے پہلے تھے وہ بھی یہی کہا کرتے تھے تو جو کچھ وہ کیا کرتے تھے ان کے کچھ بھی کام نہ آیا');
INSERT INTO "jalandhari" VALUES(39,51,'39:51','ان پر ان کے اعمال کے وبال پڑ گئے۔ اور جو لوگ ان میں سے ظلم کرتے رہے ہیں ان پر ان کے عملوں کے وبال عنقریب پڑیں گے۔ اور وہ (خدا کو) عاجز نہیں کرسکتے');
INSERT INTO "jalandhari" VALUES(39,52,'39:52','کیا ان کو معلوم نہیں کہ خدا ہی جس کے لئے چاہتا ہے رزق کو فراخ کردیتا ہے اور (جس کے لئے چاہتا ہے) تنگ کر دیتا ہے۔ جو لوگ ایمان لاتے ہیں ان کے لئے اس میں (بہت سی) نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(39,53,'39:53','(اے پیغمبر میری طرف سے لوگوں کو) کہہ دو کہ اے میرے بندو جنہوں نے اپنی جانوں پر زیادتی کی ہے خدا کی رحمت سے ناامید نہ ہونا۔ خدا تو سب گناہوں کو بخش دیتا ہے۔ (اور) وہ تو بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(39,54,'39:54','اور اس سے پہلے کہ تم پر عذاب آ واقع ہو، اپنے پروردگار کی طرف رجوع کرو اور اس کے فرمانبردار ہوجاؤ پھر تم کو مدد نہیں ملے گی');
INSERT INTO "jalandhari" VALUES(39,55,'39:55','اور اس سے پہلے کہ تم پر ناگہاں عذاب آجائے اور تم کو خبر بھی نہ ہو اس نہایت اچھی (کتاب) کی جو تمہارے پروردگار کی طرف سے تم پر نازل ہوئی ہے پیروی کرو');
INSERT INTO "jalandhari" VALUES(39,56,'39:56','کہ (مبادا اس وقت) کوئی متنفس کہنے لگے کہ (ہائے ہائے) اس تقصیر پر افسوس ہے جو میں نے خدا کے حق میں کی اور میں تو ہنسی ہی کرتا رہا');
INSERT INTO "jalandhari" VALUES(39,57,'39:57','یا یہ کہنے لگے کہ اگر خدا مجھ کو ہدایت دیتا تو میں بھی پرہیزگاروں میں ہوتا');
INSERT INTO "jalandhari" VALUES(39,58,'39:58','یا جب عذاب دیکھ لے تو کہنے لگے کہ اگر مجھے پھر ایک دفعہ دنیا میں جانا ہو تو میں نیکوکاروں میں ہو جاؤں');
INSERT INTO "jalandhari" VALUES(39,59,'39:59','(خدا فرمائے گا) کیوں نہیں میری آیتیں تیرے پاس پہنچ گئی ہیں مگر تو نے ان کو جھٹلایا اور شیخی میں آگیا اور تو کافر بن گیا');
INSERT INTO "jalandhari" VALUES(39,60,'39:60','اور جن لوگوں نے خدا پر جھوٹ بولا تم قیامت کے دن دیکھو گے کہ ان کے منہ کالے ہو رہے ہوں گے۔ کیا غرور کرنے والوں کو ٹھکانا دوزخ میں نہیں ہے');
INSERT INTO "jalandhari" VALUES(39,61,'39:61','اور جو پرہیزگار ہیں ان کی (سعادت اور) کامیابی کے سبب خدا ان کو نجات دے گا نہ تو ان کو کوئی سختی پہنچے گی اور نہ غمناک ہوں گے');
INSERT INTO "jalandhari" VALUES(39,62,'39:62','خدا ہی ہر چیز کا پیدا کرنے والا ہے۔ اور وہی ہر چیز کا نگراں ہے');
INSERT INTO "jalandhari" VALUES(39,63,'39:63','اسی کے پاس آسمانوں اور زمین کی کنجیاں ہیں۔ اور جنہوں نے خدا کی آیتوں سے کفر کیا وہی نقصان اُٹھانے والے ہیں');
INSERT INTO "jalandhari" VALUES(39,64,'39:64','کہہ دو کہ اے نادانو! تم مجھ سے یہ کہتے ہو کہ میں غیر خدا کی پرستش کرنے لگوں');
INSERT INTO "jalandhari" VALUES(39,65,'39:65','اور (اے محمدصلى الله عليه وسلم) تمہاری طرف اور ان (پیغمبروں) کی طرف جو تم سے پہلے ہوچکے ہیں یہی وحی بھیجی گئی ہے۔ کہ اگر تم نے شرک کیا تو تمہارے عمل برباد ہوجائیں گے اور تم زیاں کاروں میں ہوجاؤ گے');
INSERT INTO "jalandhari" VALUES(39,66,'39:66','بلکہ خدا ہی کی عبادت کرو اور شکرگزاروں میں ہو');
INSERT INTO "jalandhari" VALUES(39,67,'39:67','اور انہوں نے خدا کی قدر شناسی جیسی کرنی چاہیئے تھی نہیں کی۔ اور قیامت کے دن تمام زمین اس کی مٹھی میں ہوگی اور آسمان اس کے داہنے ہاتھ میں لپٹے ہوں گے۔ (اور) وہ ان لوگوں کے شرک سے پاک اور عالی شان ہے');
INSERT INTO "jalandhari" VALUES(39,68,'39:68','اور جب صور پھونکا جائے گا تو جو لوگ آسمان میں ہیں اور جو زمین میں ہیں سب بےہوش ہو کر گر پڑیں گے مگر وہ جس کو خدا چاہے۔ پھر دوسری دفعہ پھونکا جائے گا تو فوراً سب کھڑے ہو کر دیکھنے لگیں گے');
INSERT INTO "jalandhari" VALUES(39,69,'39:69','اور زمین اپنے پروردگار کے نور سے جگمگا اُٹھے گی اور (اعمال کی) کتاب (کھول کر) رکھ دی جائے گی اور پیغمبر اور (اور) گواہ حاضر کئے جائیں گے اور ان میں انصاف کے ساتھ فیصلہ کیا جائے گا اور بےانصافی نہیں کی جائے گی');
INSERT INTO "jalandhari" VALUES(39,70,'39:70','اور جس شخص نے جو عمل کیا ہوگا اس کو اس کا پورا پورا بدلہ مل جائے گا اور جو کچھ یہ کرتے ہیں اس کو سب کی خبر ہے');
INSERT INTO "jalandhari" VALUES(39,71,'39:71','اور کافروں کو گروہ گروہ بنا کر جہنم کی طرف لے جائیں گے۔ یہاں تک کہ جب وہ اس کے پاس پہنچ جائیں گے تو اس کے دروازے کھول دیئے جائیں گے تو اس کے داروغہ ان سے کہیں گے کہ کیا تمہارے پاس تم ہی میں سے پیغمبر نہیں آئے تھے جو تم کو تمہارے پروردگار کی آیتیں پڑھ پڑھ کر سناتے اور اس دن کے پیش آنے سے ڈراتے تھے کہیں گے کیوں نہیں لیکن کافروں کے حق میں عذاب کا حکم متحقق ہوچکا تھا');
INSERT INTO "jalandhari" VALUES(39,72,'39:72','کہا جائے گا کہ دوزخ کے دروازوں میں داخل ہوجاؤ ہمیشہ اس میں رہو گے۔ تکبر کرنے والوں کا برا ٹھکانا ہے');
INSERT INTO "jalandhari" VALUES(39,73,'39:73','اور جو لوگ اپنے پروردگار سے ڈرتے ہیں ان کو گروہ گروہ بنا کر بہشت کی طرف لے جائیں گے یہاں تک کہ جب اس کے پاس پہنچ جائیں گے اور اس کے دروازے کھول دیئے جائیں گے تو اس کے داروغہ ان سے کہیں کہ تم پر سلام تم بہت اچھے رہے۔ اب اس میں ہمیشہ کے لئے داخل ہوجاؤ');
INSERT INTO "jalandhari" VALUES(39,74,'39:74','وہ کہیں گے کہ خدا کا شکر ہے جس نے اپنے وعدہ کو ہم سے سچا کردیا اور ہم کو اس زمین کا وارث بنا دیا ہم بہشت میں جس مکان میں چاہیں رہیں تو (اچھے) عمل کرنے والوں کا بدلہ بھی کیسا خوب ہے');
INSERT INTO "jalandhari" VALUES(39,75,'39:75','تم فرشتوں کو دیکھو گے کہ عرش کے گرد گھیرا باندھے ہوئے ہیں (اور) اپنے پروردگار کی تعریف کے ساتھ تسبیح کر رہے ہیں۔ اور ان میں انصاف کے ساتھ فیصلہ کیا جائے گا اور کہا جائے گا کہ ہر طرح کی تعریف خدا ہی کو سزاوار ہے جو سارے جہان کا مالک ہے');
INSERT INTO "jalandhari" VALUES(40,1,'40:1','حٰم');
INSERT INTO "jalandhari" VALUES(40,2,'40:2','اس کتاب کا اتارا جانا خدائے غالب ودانا کی طرف سے ہے');
INSERT INTO "jalandhari" VALUES(40,3,'40:3','جو گناہ بخشنے والا اور توبہ قبول کرنے والا ہے اور سخت عذاب دینے والا اور صاحب کرم ہے۔ اس کے سوا کوئی معبود نہیں۔ اسی کی طرف پھر کر جانا ہے');
INSERT INTO "jalandhari" VALUES(40,4,'40:4','خدا کی آیتوں میں وہی لوگ جھگڑتے ہیں جو کافر ہیں۔ تو ان لوگوں کا شہروں میں چلنا پھرنا تمہیں دھوکے میں نہ ڈال دے');
INSERT INTO "jalandhari" VALUES(40,5,'40:5','ان سے پہلے نوح کی قوم اور ان کے بعد اور اُمتوں نے بھی (پیغمبروں کی) تکذیب کی۔ اور ہر اُمت نے اپنے پیغمبر کے بارے میں یہی قصد کیا کہ اس کو پکڑ لیں اور بیہودہ (شہبات سے) جھگڑتے رہے کہ اس سے حق کو زائل کردیں تو میں نے ان کو پکڑ لیا (سو دیکھ لو) میرا عذاب کیسا ہوا');
INSERT INTO "jalandhari" VALUES(40,6,'40:6','اور اسی طرح کافروں کے بارے میں بھی تمہارے پروردگار کی بات پوری ہوچکی ہے کہ وہ اہل دوزخ ہیں');
INSERT INTO "jalandhari" VALUES(40,7,'40:7','جو لوگ عرش کو اٹھائے ہوئے اور جو اس کے گردا گرد (حلقہ باندھے ہوئے) ہیں (یعنی فرشتے) وہ اپنے پروردگار کی تعریف کے ساتھ تسبیح کرتے رہتے ہیں اور مومنوں کے لئے بخشش مانگتے رہتے ہیں۔ کہ اے ہمارے پروردگار تیری رحمت اور تیرا علم ہر چیز کو احاطہ کئے ہوئے ہے تو جن لوگوں نے توبہ کی اور تیرے رستے پر چلے ان کو بخش دے اور دوزخ کے عذاب سے بچالے');
INSERT INTO "jalandhari" VALUES(40,8,'40:8','اے ہمارے پروردگار ان کو ہمیشہ رہنے کے بہشتوں میں داخل کر جن کا تونے ان سے وعدہ کیا ہے اور جو ان کے باپ دادا اور ان کی بیویوں اور ان کی اولاد میں سے نیک ہوں ان کو بھی۔ بےشک تو غالب حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(40,9,'40:9','اور ان کو عذابوں سے بچائے رکھ۔ اور جس کو تو اس روز عذابوں سے بچا لے گا تو بےشک اس پر مہربانی فرمائی اور یہی بڑی کامیابی ہے');
INSERT INTO "jalandhari" VALUES(40,10,'40:10','جن لوگوں نے کفر کیا ان سے پکار کر کہہ دیا جائے گا کہ جب تم (دنیا میں) ایمان کی طرف بلائے جاتے تھے اور مانتے نہیں تھے تو خدا اس سے کہیں زیادہ بیزار ہوتا تھا جس قدر تم اپنے آپ سے بیزار ہو رہے ہو');
INSERT INTO "jalandhari" VALUES(40,11,'40:11','وہ کہیں گے کہ اے ہمارے پروردگار تو نے ہم کو دو دفعہ بےجان کیا اور دو دفعہ جان بخشی۔ ہم کو اپنے گناہوں کا اقرار ہے تو کیا نکلنے کی کوئی سبیل ہے؟');
INSERT INTO "jalandhari" VALUES(40,12,'40:12','یہ اس لئے کہ جب تنہا خدا کو پکارا جاتا تھا تو تم انکار کردیتے تھے۔ اور اگر اس کے ساتھ شریک مقرر کیا جاتا تھا تو تسلیم کرلیتے تھے تو حکم تو خدا ہی کا ہے جو (سب سے) اوپر اور (سب سے) بڑا ہے');
INSERT INTO "jalandhari" VALUES(40,13,'40:13','وہی تو ہے جو تم کو اپنی نشانیاں دکھاتا ہے اور تم پر آسمان سے رزق اُتارتا ہے۔ اور نصیحت تو وہی پکڑتا ہے جو (اس کی طرف) رجوع کرتا ہے');
INSERT INTO "jalandhari" VALUES(40,14,'40:14','تو خدا کی عبادت کو خالص کر کر اُسی کو پکارو اگرچہ کافر برا ہی مانیں');
INSERT INTO "jalandhari" VALUES(40,15,'40:15','مالک درجات عالی اور صاحب عرش ہے۔ اپنے بندوں میں سے جس پر چاہتا ہے اپنے حکم سے وحی بھیجتا ہے تاکہ ملاقات کے دن سے ڈراوے');
INSERT INTO "jalandhari" VALUES(40,16,'40:16','جس روز وہ نکل پڑیں گے ان کی کوئی چیز خدا سے مخفی نہ رہے گی۔ آج کس کی بادشاہت ہے؟ خدا کی جو اکیلا اور غالب ہے');
INSERT INTO "jalandhari" VALUES(40,17,'40:17','آج کے دن ہر شخص کو اس کے اعمال کا بدلہ دیا جائے گا۔ آج (کسی کے حق میں) بےانصافی نہیں ہوگی۔ بےشک خدا جلد حساب لینے والا ہے');
INSERT INTO "jalandhari" VALUES(40,18,'40:18','اور ان کو قریب آنے والے دن سے ڈراؤ جب کہ دل غم سے بھر کر گلوں تک آرہے ہوں گے۔ (اور) ظالموں کا کوئی دوست نہ ہوگا اور نہ کوئی سفارشی جس کی بات قبول کی جائے');
INSERT INTO "jalandhari" VALUES(40,19,'40:19','وہ آنکھوں کی خیانت کو جانتا ہے اور جو (باتیں) سینوں میں پوشیدہ ہیں (ان کو بھی)');
INSERT INTO "jalandhari" VALUES(40,20,'40:20','اور خدا سچائی کے ساتھ حکم فرماتا ہے اور جن کو یہ لوگ پکارتے ہیں وہ کچھ بھی حکم نہیں دے سکتے۔ بےشک خدا سننے والا (اور) دیکھنے والا ہے');
INSERT INTO "jalandhari" VALUES(40,21,'40:21','کیا انہوں نے زمین میں سیر نہیں کی تاکہ دیکھ لیتے کہ جو لوگ ان سے پہلے تھے ان کا انجام کیسا ہوا۔ وہ ان سے زور اور زمین میں نشانات (بنانے) کے لحاظ سے کہیں بڑھ کر تھے تو خدا نے ان کو ان کے گناہوں کے سبب پکڑ لیا۔ اور ان کو خدا (کے عذاب) سے کوئی بھی بچانے والا نہ تھا');
INSERT INTO "jalandhari" VALUES(40,22,'40:22','یہ اس لئے کہ ان کے پاس پیغمبر کھلی دلیلیں لاتے تھے تو یہ کفر کرتے تھے سو خدا نے ان کو پکڑ لیا۔ بےشک وہ صاحب قوت (اور) سخت عذاب دینے والا ہے');
INSERT INTO "jalandhari" VALUES(40,23,'40:23','اور ہم نے موسیٰ کو اپنی نشانیاں اور دلیل روشن دے کر بھیجا');
INSERT INTO "jalandhari" VALUES(40,24,'40:24','(یعنی) فرعون اور ہامان اور قارون کی طرف تو انہوں نے کہا کہ یہ تو جادوگر ہے جھوٹا');
INSERT INTO "jalandhari" VALUES(40,25,'40:25','غرض جب وہ ان کے پاس ہماری طرف سے حق لے کر پہنچے تو کہنے لگے کہ جو اس کے ساتھ (خدا پر) ایمان لائے ہیں ان کے بیٹوں کو قتل کردو اور بیٹیوں کو زندہ رہنے دو۔ اور کافروں کی تدبیریں بےٹھکانے ہوتی ہیں');
INSERT INTO "jalandhari" VALUES(40,26,'40:26','اور فرعون بولا کہ مجھے چھوڑو کہ موسیٰ کو قتل کردوں اور وہ اپنے پروردگار کو بلالے۔ مجھے ڈر ہے کہ وہ (کہیں) تمہارے دین کو نہ بدل دے یا ملک میں فساد (نہ) پیدا کردے');
INSERT INTO "jalandhari" VALUES(40,27,'40:27','موسیٰ نے کہا کہ میں ہر متکبر سے جو حساب کے دن (یعنی قیامت) پر ایمان نہیں لاتا۔ اپنے اور تمہارے پروردگار کی پناہ لے چکا ہوں');
INSERT INTO "jalandhari" VALUES(40,28,'40:28','اور فرعون کے لوگوں میں سے ایک مومن شخص جو اپنے ایمان کو پوشیدہ رکھتا تھا کہنے لگا کیا تم ایسے شخص کو قتل کرنا چاہتے ہو جو کہتا ہے کہ میرا پروردگار خدا ہے اور وہ تمہارے پروردگار (کی طرف) سے نشانیاں بھی لے کر آیا ہے۔ اور اگر وہ جھوٹا ہوگا تو اس کے جھوٹ کا ضرر اسی کو ہوگا۔ اور اگر سچا ہوگا تو کوئی سا عذاب جس کا وہ تم سے وعدہ کرتا ہے تم پر واقع ہو کر رہے گا۔ بےشک خدا اس شخص کو ہدایت نہیں دیتا جو بےلحاظ جھوٹا ہے');
INSERT INTO "jalandhari" VALUES(40,29,'40:29','اے قوم آج تمہاری ہی بادشاہت ہے اور تم ہی ملک میں غالب ہو۔ (لیکن) اگر ہم پر خدا کا عذاب آگیا تو (اس کے دور کرنے کے لئے) ہماری مدد کون کرے گا۔ فرعون نے کہا کہ میں تمہیں وہی بات سُجھاتا ہوں جو مجھے سوجھی ہے اور وہی راہ بتاتا ہوں جس میں بھلائی ہے');
INSERT INTO "jalandhari" VALUES(40,30,'40:30','تو جو مومن تھا وہ کہنے لگا کہ اے قوم مجھے تمہاری نسبت خوف ہے کہ (مبادا) تم پر اور اُمتوں کی طرح کے دن کا عذاب آجائے');
INSERT INTO "jalandhari" VALUES(40,31,'40:31','یعنی) نوح کی قوم اور عاد اور ثمود اور جو لوگ ان کے پیچھے ہوئے ہیں ان کے حال کی طرح (تمہارا حال نہ ہوجائے) اور خدا تو بندوں پر ظلم کرنا نہیں چاہتا');
INSERT INTO "jalandhari" VALUES(40,32,'40:32','اور اے قوم مجھے تمہاری نسبت پکار کے دن (یعنی قیامت) کا خوف ہے');
INSERT INTO "jalandhari" VALUES(40,33,'40:33','جس دن تم پیٹھ پھیر کر (قیامت کے دن سے) بھاگو گے (اس دن) تم کو کوئی (عذاب) خدا سے بچانے والا نہ ہوگا۔ اور جس شخص کو خدا گمراہ کرے اس کو کوئی ہدایت دینے والا نہیں');
INSERT INTO "jalandhari" VALUES(40,34,'40:34','اور پہلے یوسف بھی تمہارے پاس نشانیاں لے کر آئے تھے تو جو وہ لائے تھے اس سے تم ہمیشہ شک ہی میں رہے۔ یہاں تک کہ جب وہ فوت ہوگئے تو تم کہنے لگے کہ خدا اس کے بعد کبھی کوئی پیغمبر نہیں بھیجے گا۔ اسی طرح خدا اس شخص کو گمراہ کر دیتا ہے جو حد سے نکل جانے والا اور شک کرنے والا ہو');
INSERT INTO "jalandhari" VALUES(40,35,'40:35','جو لوگ بغیر اس کے کہ ان کے پاس کوئی دلیل آئی ہو خدا کی آیتوں میں جھگڑتے ہیں۔ خدا کے نزدیک اور مومنوں کے نزدیک یہ جھگڑا سخت ناپسند ہے۔ اسی طرح خدا ہر متکبر سرکش کے دل پر مہر لگا دیتا ہے');
INSERT INTO "jalandhari" VALUES(40,36,'40:36','اور فرعون نے کہا کہ ہامان میرے لئے ایک محل بناؤ تاکہ میں (اس پر چڑھ کر) رستوں پر پہنچ جاؤں');
INSERT INTO "jalandhari" VALUES(40,37,'40:37','(یعنی) آسمانوں کے رستوں پر، پھر موسیٰ کے خدا کو دیکھ لوں اور میں تو اسے جھوٹا سمجھتا ہوں۔ اور اسی طرح فرعون کو اس کے اعمال بد اچھے معلوم ہوتے تھے اور وہ رستے سے روک دیا گیا تھا۔ اور فرعون کی تدبیر تو بےکار تھی');
INSERT INTO "jalandhari" VALUES(40,38,'40:38','اور وہ شخص جو مومن تھا اس نے کہا کہ بھائیو میرے پیچھے چلو میں تمہیں بھلائی کا رستہ دکھاؤ ں');
INSERT INTO "jalandhari" VALUES(40,39,'40:39','بھائیو یہ دنیا کی زندگی (چند روزہ) فائدہ اٹھانے کی چیز ہے۔ اور جو آخرت ہے وہی ہمیشہ رہنے کا گھر ہے');
INSERT INTO "jalandhari" VALUES(40,40,'40:40','جو برے کام کرے گا اس کو بدلہ بھی ویسا ہی ملے گا۔ اور جو نیک کام کرے گا مرد ہو یا عورت اور وہ صاحب ایمان بھی ہوگا تو ایسے لوگ بہشت میں داخل ہوں گے وہاں ان کو بےشمار رزق ملے گا');
INSERT INTO "jalandhari" VALUES(40,41,'40:41','اور اے قوم میرا کیا (حال) ہے کہ میں تم کو نجات کی طرف بلاتا ہوں اور تم مجھے (دوزخ کی) آگ کی طرف بلاتے ہو');
INSERT INTO "jalandhari" VALUES(40,42,'40:42','تم مجھے اس لئے بلاتے ہو کہ خدا کے ساتھ کفر کروں اور اس چیز کو اس کا شریک مقرر کروں جس کا مجھے کچھ بھی علم نہیں۔ اور میں تم کو (خدائے) غالب (اور) بخشنے والے کی طرف بلاتا ہوں');
INSERT INTO "jalandhari" VALUES(40,43,'40:43','سچ تو یہ ہے کہ جس چیز کی طرف تم مجھے بلاتے ہو اس کو دنیا اور آخرت میں بلانے (یعنی دعا قبول کرنے) کا مقدور نہیں اور ہم کو خدا کی طرف لوٹنا ہے اور حد سے نکل جانے والے دوزخی ہیں');
INSERT INTO "jalandhari" VALUES(40,44,'40:44','جو بات میں تم سے کہتا ہوں تم اسے آگے چل کر یاد کرو گے۔ اور میں اپنا کام خدا کے سپرد کرتا ہوں۔ بےشک خدا بندوں کو دیکھنے والا ہے');
INSERT INTO "jalandhari" VALUES(40,45,'40:45','غرض خدا نے موسیٰ کو ان لوگوں کی تدبیروں کی برائیوں سے محفوظ رکھا اور فرعون والوں کو برے عذاب نے آگھیرا');
INSERT INTO "jalandhari" VALUES(40,46,'40:46','یعنی) آتش (جہنم) کہ صبح وشام اس کے سامنے پیش کئے جاتے ہیں۔ اور جس روز قیامت برپا ہوگی (حکم ہوگا کہ) فرعون والوں کو نہایت سخت عذاب میں داخل کرو');
INSERT INTO "jalandhari" VALUES(40,47,'40:47','اور جب وہ دوزخ میں جھگڑیں گے تو ادنیٰ درجے کے لوگ بڑے آدمیوں سے کہیں گے کہ ہم تو تمہارے تابع تھے تو کیا تم دوزخ (کے عذاب) کا کچھ حصہ ہم سے دور کرسکتے ہو؟');
INSERT INTO "jalandhari" VALUES(40,48,'40:48','بڑے آدمی کہیں گے کہ تم (بھی اور) ہم (بھی) سب دوزخ میں (رہیں گے) خدا بندوں میں فیصلہ کرچکا ہے');
INSERT INTO "jalandhari" VALUES(40,49,'40:49','اور جو لوگ آگ میں (جل رہے) ہوں گے وہ دوزخ کے داروغوں سے کہیں گے کہ اپنے پروردگار سے دعا کرو کہ ایک روز تو ہم سے عذاب ہلکا کردے');
INSERT INTO "jalandhari" VALUES(40,50,'40:50','وہ کہیں گے کہ کیا تمہارے پاس تمہارے پیغمبر نشانیاں لے کر نہیں آئے تھے۔ وہ کہیں گے کیوں نہیں تو وہ کہیں گے کہ تم ہی دعا کرو۔ اور کافروں کی دعا (اس روز) بےکار ہوگی');
INSERT INTO "jalandhari" VALUES(40,51,'40:51','ہم اپنے پیغمبروں کی اور جو لوگ ایمان لائے ہیں ان کی دنیا کی زندگی میں بھی مدد کرتے ہیں اور جس دن گواہ کھڑے ہوں گے (یعنی قیامت کو بھی)');
INSERT INTO "jalandhari" VALUES(40,52,'40:52','جس دن ظالموں کو ان کی معذرت کچھ فائدہ نہ دے گی اور ان کے لئے لعنت اور برا گھر ہے');
INSERT INTO "jalandhari" VALUES(40,53,'40:53','اور ہم نے موسیٰ کو ہدایت (کی کتاب) دی اور بنی اسرائیل کو اس کتاب کا وارث بنایا');
INSERT INTO "jalandhari" VALUES(40,54,'40:54','عقل والوں کے لئے ہدایت اور نصیحت ہے');
INSERT INTO "jalandhari" VALUES(40,55,'40:55','تو صبر کرو بےشک خدا کا وعدہ سچا ہے اور اپنے گناہوں کی معافی مانگو اور صبح وشام اپنے پروردگار کی تعریف کے ساتھ تسبیح کرتے رہو');
INSERT INTO "jalandhari" VALUES(40,56,'40:56','جو لوگ بغیر کسی دلیل کے جو ان کے پاس آئی ہو خدا کی آیتوں میں جھگڑتے ہیں ان کے دلوں میں اور کچھ نہیں (ارادہٴ) عظمت ہے اور وہ اس کو پہنچنے والے نہیں تو خدا کی پناہ مانگو۔ بےشک وہ سننے والا (اور) دیکھنے والا ہے');
INSERT INTO "jalandhari" VALUES(40,57,'40:57','آسمانوں اور زمین کا پیدا کرنا لوگوں کے پیدا کرنے کی نسبت بڑا (کام) ہے لیکن اکثر لوگ نہیں جانتے');
INSERT INTO "jalandhari" VALUES(40,58,'40:58','اور اندھا اور آنکھ والا برابر نہیں۔ اور نہ ایمان لانے والے نیکوکار اور نہ بدکار (برابر ہیں) (حقیقت یہ ہے کہ) تم بہت کم غور کرتے ہو');
INSERT INTO "jalandhari" VALUES(40,59,'40:59','قیامت آنے والی ہے اس میں کچھ شک نہیں۔ لیکن اکثر لوگ ایمان نہیں رکھتے');
INSERT INTO "jalandhari" VALUES(40,60,'40:60','اور تمہارے پروردگار نے کہا ہے کہ تم مجھ سے دعا کرو میں تمہاری (دعا) قبول کروں گا۔ جو لوگ میری عبادت سے ازراہ تکبر کنیاتے ہیں۔ عنقریب جہنم میں ذلیل ہو کر داخل ہوں گے');
INSERT INTO "jalandhari" VALUES(40,61,'40:61','خدا ہی تو ہے جس نے تمہارے لئے رات بنائی کہ اس میں آرام کرو اور دن کو روشن بنایا (کہ اس میں کام کرو) بےشک خدا لوگوں پر فضل کرنے والا ہے۔ لیکن اکثر لوگ شکر نہیں کرتے');
INSERT INTO "jalandhari" VALUES(40,62,'40:62','یہی خدا تمہارا پروردگار ہے جو ہر چیز کا پیدا کرنے والا ہے۔ اس کے سوا کوئی معبود نہیں پھر تم کہاں بھٹک رہے ہو؟');
INSERT INTO "jalandhari" VALUES(40,63,'40:63','اسی طرح وہ لوگ بھٹک رہے تھے جو خدا کی آیتوں سے انکار کرتے تھے');
INSERT INTO "jalandhari" VALUES(40,64,'40:64','خدا ہی تو ہے جس نے زمین کو تمہارے لئے ٹھیرنے کی جگہ اور آسمان کو چھت بنایا اور تمہاری صورتیں بنائیں اور صورتیں بھی خوب بنائیں اور تمہیں پاکیزہ چیزیں کھانے کو دیں۔ یہی خدا تمہارا پروردگار ہے۔ پس خدائے پروردگار عالم بہت ہی بابرکت ہے');
INSERT INTO "jalandhari" VALUES(40,65,'40:65','وہ زندہ ہے (جسے موت نہیں) اس کے سوا کوئی عبادت کے لائق نہیں تو اس کی عبادت کو خالص کر کر اسی کو پکارو۔ ہر طرح کی تعریف خدا ہی کو (سزاوار) ہے جو تمام جہان کا پروردگار ہے');
INSERT INTO "jalandhari" VALUES(40,66,'40:66','(اے محمدصلى الله عليه وسلم ان سے) کہہ دو کہ مجھے اس بات کی ممانعت کی گئی ہے کہ جن کو تم خدا کے سوا پکارتے ہو ان کی پرستش کروں (اور میں ان کی کیونکر پرستش کروں) جب کہ میرے پاس میرے پروردگار (کی طرف) سے کھلی دلیلیں آچکی ہیں اور مجھ کو یہ حکم ہوا ہے کہ پروردگار عالم ہی کا تابع فرمان ہوں');
INSERT INTO "jalandhari" VALUES(40,67,'40:67','وہی تو ہے جس نے تم کو (پہلے) مٹی سے پیدا کیا۔ ہھر نطفہ بنا کر پھر لوتھڑا بنا کر پھر تم کو نکالتا ہے (کہ تم) بچّے (ہوتے ہو) پھر تم اپنی جوانی کو پہنچتے ہو۔ پھر بوڑھے ہوجاتے ہو۔ اور کوئی تم میں سے پہلے ہی مرجاتا ہے اور تم (موت کے) وقت مقرر تک پہنچ جاتے ہو اور تاکہ تم سمجھو');
INSERT INTO "jalandhari" VALUES(40,68,'40:68','وہی تو ہے جو جلاتا ہے اور مارتا ہے۔ پھر جب وہ کوئی کام کرنا (اور کسی کو پیدا کرنا) چاہتا ہے تو اس سے کہہ دیتا ہے کہ ہوجا تو وہ جاتا ہے');
INSERT INTO "jalandhari" VALUES(40,69,'40:69','کیا تم نے ان لوگوں کو نہیں دیکھا جو خدا کی آیتوں میں جھگڑتے ہیں۔ یہ کہاں بھٹک رہے ہیں؟');
INSERT INTO "jalandhari" VALUES(40,70,'40:70','جن لوگوں نے کتاب (خدا) کو اور جو کچھ ہم نے پیغمبروں کو دے کر بھیجا اس کو جھٹلایا۔ وہ عنقریب معلوم کرلیں گے');
INSERT INTO "jalandhari" VALUES(40,71,'40:71','جب کہ ان کی گردنوں میں طوق اور زنجیریں ہوں گی (اور) گھسیٹے جائیں گے');
INSERT INTO "jalandhari" VALUES(40,72,'40:72','(یعنی) کھولتے ہوئے پانی میں۔ پھر آگ میں جھونک دیئے جائیں گے');
INSERT INTO "jalandhari" VALUES(40,73,'40:73','پھر ان سے کہا جائے گا کہ وہ کہاں ہیں جن کو تم (خدا کے) شریک بناتے تھے');
INSERT INTO "jalandhari" VALUES(40,74,'40:74','(یعنی غیر خدا) کہیں گے وہ تو ہم سے جاتے رہے بلکہ ہم تو پہلے کسی چیز کو پکارتے ہی نہیں تھے۔ اسی طرح خدا کافروں کو گمراہ کرتا ہے');
INSERT INTO "jalandhari" VALUES(40,75,'40:75','یہ اس کا بدلہ ہے کہ تم زمین میں حق کے بغیر (یعنی اس کے خلاف) خوش ہوا کرتے تھے اور اس کی (سزا ہے) کہ اترایا کرتے تھے');
INSERT INTO "jalandhari" VALUES(40,76,'40:76','(اب) جہنم کے دروازوں میں داخل ہوجاؤ۔ ہمیشہ اسی میں رہو گے۔ متکبروں کا کیا برا ٹھکانا ہے');
INSERT INTO "jalandhari" VALUES(40,77,'40:77','تو (اے پیغمبر) صبر کرو خدا کا وعدہ سچا ہے۔ اگر ہم تم کو کچھ اس میں سے دکھادیں جس کا ہم تم سے وعدہ کرتے ہیں۔ (یعنی کافروں پر عذاب نازل کریں) یا تمہاری مدت حیات پوری کردیں تو ان کو ہماری ہی طرف لوٹ کر آنا ہے');
INSERT INTO "jalandhari" VALUES(40,78,'40:78','اور ہم نے تم سے پہلے (بہت سے) پیغمبر بھیجے۔ ان میں کچھ تو ایسے ہیں جن کے حالات تم سے بیان کر دیئے ہیں اور کچھ ایسے ہیں جن کے حالات بیان نہیں کئے۔ اور کسی پیغمبر کا مقدور نہ تھا کہ خدا کے حکم کے سوا کوئی نشانی لائے۔ پھر جب خدا کا حکم آپہنچا تو انصاف کے ساتھ فیصلہ کردیا گیا اور اہل باطل نقصان میں پڑ گئے');
INSERT INTO "jalandhari" VALUES(40,79,'40:79','خدا ہی تو ہے جس نے تمہارے لئے چارپائے بنائے تاکہ ان میں سے بعض پر سوار ہو اور بعض کو تم کھاتے ہو');
INSERT INTO "jalandhari" VALUES(40,80,'40:80','اور تمہارے لئے ان میں (اور بھی) فائدے ہیں اور اس لئے بھی کہ (کہیں جانے کی) تمہارے دلوں میں جو حاجت ہو ان پر (چڑھ کر وہاں) پہنچ جاؤ۔ اور ان پر اور کشتیوں پر تم سوار ہوتے ہو');
INSERT INTO "jalandhari" VALUES(40,81,'40:81','اور وہ تمہیں اپنی نشانیاں دکھاتا ہے تو تم خدا کی کن کن نشانیوں کو نہ مانو گے');
INSERT INTO "jalandhari" VALUES(40,82,'40:82','کیا ان لوگوں نے زمین میں سیر نہیں کی تاکہ دیکھتے جو لوگ ان سے پہلے تھے ان کا انجام کیسا ہوا۔ (حالانکہ) وہ ان سے کہیں زیادہ طاقتور اور زمین میں نشانات (بنانے) کے اعتبار سے بہت بڑھ کر تھے۔ تو جو کچھ وہ کرتے تھے وہ ان کے کچھ کام نہ آیا');
INSERT INTO "jalandhari" VALUES(40,83,'40:83','اور جب ان کے پیغمبر ان کے پاس کھلی نشانیاں لے کر آئے تو جو علم (اپنے خیال میں) ان کے پاس تھا اس پر اترانے لگے اور جس چیز سے تمسخر کیا کرتے تھے اس نے ان کو آ گھیرا');
INSERT INTO "jalandhari" VALUES(40,84,'40:84','پھر جب انہوں نے ہمارا عذاب دیکھ لیا تو کہنے لگے کہ ہم خدائے واحد پر ایمان لائے اور جس چیز کو اس کے ساتھ شریک بناتے تھے اس سے نامعتقد ہوئے');
INSERT INTO "jalandhari" VALUES(40,85,'40:85','لیکن جب وہ ہمارا عذاب دیکھ چکے (اس وقت) ان کے ایمان نے ان کو کچھ بھی فائدہ نہ دیا۔ (یہ) خدا کی عادت (ہے) جو اس کے بندوں (کے بارے) میں چلی آتی ہے۔ اور وہاں کافر گھاٹے میں پڑ گئے');
INSERT INTO "jalandhari" VALUES(41,1,'41:1','حٰم');
INSERT INTO "jalandhari" VALUES(41,2,'41:2','(یہ کتاب خدائے) رحمٰن ورحیم (کی طرف) سے اُتری ہے');
INSERT INTO "jalandhari" VALUES(41,3,'41:3','کتاب جس کی آیتیں واضح (المعانی) ہیں (یعنی) قرآن عربی ان لوگوں کے لئے جو سمجھ رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(41,4,'41:4','جو بشارت بھی سناتا ہے اور خوف بھی دلاتا ہے لیکن ان میں سے اکثروں نے منہ پھیر لیا اور وہ سنتے ہی نہیں');
INSERT INTO "jalandhari" VALUES(41,5,'41:5','اور کہتے ہیں کہ جس چیز کی طرف تم ہمیں بلاتے ہو اس سے ہمارے دل پردوں میں ہیں اور ہمارے کانوں میں بوجھ (یعنی بہراپن) ہے اور ہمارے اور تمہارے درمیان پردہ ہے تو تم (اپنا) کام کرو ہم (اپنا) کام کرتے ہیں');
INSERT INTO "jalandhari" VALUES(41,6,'41:6','کہہ دو کہ میں بھی آدمی ہوں جیسے تم۔ (ہاں) مجھ پر یہ وحی آتی ہے کہ تمہارا معبود خدائے واحد ہے تو سیدھے اسی کی طرف (متوجہ) رہو اور اسی سے مغفرت مانگو اور مشرکوں پر افسوس ہے');
INSERT INTO "jalandhari" VALUES(41,7,'41:7','جو زکوٰة نہیں دیتے اور آخرت کے بھی قائل نہیں');
INSERT INTO "jalandhari" VALUES(41,8,'41:8','جو لوگ ایمان لائے اور عمل نیک کرتے رہے ان کے لئے (ایسا) ثواب ہے جو ختم ہی نہ ہو');
INSERT INTO "jalandhari" VALUES(41,9,'41:9','کہو کیا تم اس سے انکار کرتے ہو جس نے زمین کو دو دن میں پیدا کیا۔ اور (بتوں کو) اس کا مدمقابل بناتے ہو۔ وہی تو سارے جہان کا مالک ہے');
INSERT INTO "jalandhari" VALUES(41,10,'41:10','اور اسی نے زمین میں اس کے اوپر پہاڑ بنائے اور زمین میں برکت رکھی اور اس میں سب سامان معیشت مقرر کیا (سب) چار دن میں۔ (اور تمام) طلبگاروں کے لئے یکساں');
INSERT INTO "jalandhari" VALUES(41,11,'41:11','پھر آسمان کی طرف متوجہ ہوا اور وہ دھواں تھا تو اس نے اس سے اور زمین سے فرمایا کہ دونوں آؤ (خواہ) خوشی سے خواہ ناخوشی سے۔ انہوں نے کہا کہ ہم خوشی سے آتے ہیں');
INSERT INTO "jalandhari" VALUES(41,12,'41:12','پھر دو دن میں سات آسمان بنائے اور ہر آسمان میں اس (کے کام) کا حکم بھیجا اور ہم نے آسمان دنیا کو چراغوں (یعنی ستاروں) سے مزین کیا اور (شیطانوں سے) محفوظ رکھا۔ یہ زبردست (اور) خبردار کے (مقرر کئے ہوئے) اندازے ہیں');
INSERT INTO "jalandhari" VALUES(41,13,'41:13','پھر اگر یہ منہ پھیر لیں تو کہہ دو کہ میں تم کو ایسے چنگھاڑ (کے عذاب) سے آگاہ کرتا ہوں جیسے عاد اور ثمود پر چنگھاڑ (کا عذاب آیا تھا)');
INSERT INTO "jalandhari" VALUES(41,14,'41:14','جب ان کے پاس پیغمبر ان کے آگے اور پیچھے سے آئے کہ خدا کے سوا (کسی کی) عبادت نہ کرو۔ کہنے لگے کہ اگر ہمارا پروردگار چاہتا تو فرشتے اُتار دیتا سو جو تم دے کر بھیجے گئے ہو ہم اس کو نہیں مانتے');
INSERT INTO "jalandhari" VALUES(41,15,'41:15','جو عاد تھے وہ ناحق ملک میں غرور کرنے لگے اور کہنے لگے کہ ہم سے بڑھ کر قوت میں کون ہے؟ کیا انہوں نے نہیں دیکھا کہ خدا جس نے ان کو پیدا کیا وہ ان سے قوت میں بہت بڑھ کر ہے۔ اور وہ ہماری آیتوں سے انکار کرتے رہے');
INSERT INTO "jalandhari" VALUES(41,16,'41:16','تو ہم نے بھی ان پر نحوست کے دنوں میں زور کی ہوا چلائی تاکہ ان کو دنیا کی زندگی میں ذلت کے عذاب کا مزہ چکھا دیں۔ اور آخرت کا عذاب تو بہت ہی ذلیل کرنے والا ہے اور (اس روز) ان کو مدد بھی نہ ملے گی');
INSERT INTO "jalandhari" VALUES(41,17,'41:17','اور جو ثمود تھے ان کو ہم نے سیدھا رستہ دکھا دیا تھا مگر انہوں نے ہدایت کے مقابلے میں اندھا دھند رہنا پسند کیا تو ان کے اعمال کی سزا میں کڑک نے ان کو آپکڑا۔ اور وہ ذلت کا عذاب تھا');
INSERT INTO "jalandhari" VALUES(41,18,'41:18','اور جو ایمان لائے اور پرہیزگاری کرتے رہے ان کو ہم نے بچا لیا');
INSERT INTO "jalandhari" VALUES(41,19,'41:19','اور جس دن خدا کے دشمن دوزخ کی طرف چلائے جائیں گے تو ترتیب وار کرلیئے جائیں گے');
INSERT INTO "jalandhari" VALUES(41,20,'41:20','یہاں تک کہ جب اس کے پاس پہنچ جائیں گے تو ان کے کان اور آنکھیں اور چمڑے (یعنی دوسرے اعضا) ان کے خلاف ان کے اعمال کی شہادت دیں گے');
INSERT INTO "jalandhari" VALUES(41,21,'41:21','اور وہ اپنے چمڑوں (یعنی اعضا) سے کہیں گے کہ تم نے ہمارے خلاف کیوں شہادت دی؟ وہ کہیں گے کہ جس خدا نے سب چیزوں کو نطق بخشا اسی نے ہم کو بھی گویائی دی اور اسی نے تم کو پہلی بار پیدا کیا تھا اور اسی کی طرف تم کو لوٹ کر جانا ہے');
INSERT INTO "jalandhari" VALUES(41,22,'41:22','اور تم اس (بات کے خوف) سے تو پردہ نہیں کرتے تھے کہ تمہارے کان اور تمہاری آنکھیں اور چمڑے تمہارے خلاف شہادت دیں گے بلکہ تم یہ خیال کرتے تھے کہ خدا کو تمہارے بہت سے عملوں کی خبر ہی نہیں');
INSERT INTO "jalandhari" VALUES(41,23,'41:23','اور اسی خیال نے جو تم اپنے پروردگار کے بارے میں رکھتے تھے تم کو ہلاک کردیا اور تم خسارہ پانے والوں میں ہوگئے');
INSERT INTO "jalandhari" VALUES(41,24,'41:24','اب اگر یہ صبر کریں گے تو ان کا ٹھکانا دوزخ ہے۔ اور اگر توبہ کریں گے تو ان کی توبہ قبول نہیں کی جائے گی');
INSERT INTO "jalandhari" VALUES(41,25,'41:25','اور ہم نے (شیطانوں کو) ان کا ہم نشین مقرر کردیا تھا تو انہوں نے ان کے اگلے اور پچھلے اعمال ان کو عمدہ کر دکھائے تھے اور جنات اور انسانوں کی جماعتیں جو ان سے پہلے گذر چکیں ان پر بھی خدا (کے عذاب) کا وعدہ پورا ہوگیا۔ بےشک یہ نقصان اٹھانے والے ہیں');
INSERT INTO "jalandhari" VALUES(41,26,'41:26','اور کافر کہنے لگے کہ اس قرآن کو سنا ہی نہ کرو اور (جب پڑھنے لگیں تو) شور مچا دیا کرو تاکہ تم غالب رہو');
INSERT INTO "jalandhari" VALUES(41,27,'41:27','سو ہم بھی کافروں کو سخت عذاب کے مزے چکھائیں گے اور ان کے برے عملوں کی جو وہ کرتے تھے سزا دیں گے');
INSERT INTO "jalandhari" VALUES(41,28,'41:28','یہ خدا کے دشمنوں کا بدلہ ہے (یعنی) دوزخ۔ ان کے لئے اسی میں ہمیشہ کا گھر ہے۔ یہ اس کی سزا ہے کہ ہماری آیتوں سے انکار کرتے تھے');
INSERT INTO "jalandhari" VALUES(41,29,'41:29','اور کافر کہیں گے کہ اے ہمارے پروردگار جنوں اور انسانوں میں سے جن لوگوں نے ہم کو گمراہ کیا تھا ان کو ہمیں دکھا کہ ہم ان کو اپنے پاؤں کے تلے (روند) ڈالیں تاکہ وہ نہایت ذلیل ہوں');
INSERT INTO "jalandhari" VALUES(41,30,'41:30','جن لوگوں نے کہا کہ ہمارا پروردگار خدا ہے پھر وہ (اس پر) قائم رہے ان پر فرشتے اُتریں گے (اور کہیں گے) کہ نہ خوف کرو اور نہ غمناک ہو اور بہشت کی جس کا تم سے وعدہ کیا جاتا تھا خوشی مناؤ');
INSERT INTO "jalandhari" VALUES(41,31,'41:31','ہم دنیا کی زندگی میں بھی تمہارے دوست تھے اور آخرت میں بھی (تمہارے رفیق ہیں)۔ اور وہاں جس (نعمت) کو تمہارا جی چاہے گا تم کو (ملے گی) اور جو چیز طلب کرو گے تمہارے لئے (موجود ہوگی)');
INSERT INTO "jalandhari" VALUES(41,32,'41:32','(یہ) بخشنے والے مہربان کی طرف سے مہمانی ہے');
INSERT INTO "jalandhari" VALUES(41,33,'41:33','اور اس شخص سے بات کا اچھا کون ہوسکتا ہے جو خدا کی طرف بلائے اور عمل نیک کرے اور کہے کہ میں مسلمان ہوں');
INSERT INTO "jalandhari" VALUES(41,34,'41:34','اور بھلائی اور برائی برابر نہیں ہوسکتی۔ تو (سخت کلامی کا) ایسے طریق سے جواب دو جو بہت اچھا ہو (ایسا کرنے سے تم دیکھو گے) کہ جس میں اور تم میں دشمنی تھی گویا وہ تمہارا گرم جوش دوست ہے');
INSERT INTO "jalandhari" VALUES(41,35,'41:35','اور یہ بات ان ہی لوگوں کو حاصل ہوتی ہے جو برداشت کرنے والے ہیں۔ اور ان ہی کو نصیب ہوتی ہے جو بڑے صاحب نصیب ہیں');
INSERT INTO "jalandhari" VALUES(41,36,'41:36','اور اگر تمہیں شیطان کی جانب سے کوئی وسوسہ پیدا ہو تو خدا کی پناہ مانگ لیا کرو۔ بےشک وہ سنتا جانتا ہے');
INSERT INTO "jalandhari" VALUES(41,37,'41:37','اور رات اور دن اور سورج اور چاند اس کی نشانیوں میں سے ہیں۔ تم لوگ نہ تو سورج کو سجدہ کرو اور نہ چاند کو۔ بلکہ خدا ہی کو سجدہ کرو جس نے ان چیزوں کو پیدا کیا ہے اگر تم کو اس کی عبادت منظور ہے');
INSERT INTO "jalandhari" VALUES(41,38,'41:38','اگر یہ لوگ سرکشی کریں تو (خدا کو بھی ان کی پروا نہیں) جو (فرشتے) تمہارے پروردگار کے پاس ہیں وہ رات دن اس کی تسبیح کرتے رہتے ہیں اور (کبھی) تھکتے ہی نہیں');
INSERT INTO "jalandhari" VALUES(41,39,'41:39','اور (اے بندے یہ) اسی کی قدرت کے نمونے ہیں کہ تو زمین کو دبی ہوئی (یعنی خشک) دیکھتا ہے۔ جب ہم اس پر پانی برسا دیتے ہیں تو شاداب ہوجاتی اور پھولنے لگتی ہے تو جس نے زمین کو زندہ کیا وہی مردوں کو زندہ کرنے والا ہے۔ بےشک وہ ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(41,40,'41:40','جو لوگ ہماری آیتوں میں کج راہی کرتے ہیں وہ ہم سے پوشیدہ نہیں ہیں۔ بھلا جو شخص دوزخ میں ڈالا جائے وہ بہتر ہے یا وہ جو قیامت کے دن امن وامان سے آئے۔ (تو خیر) جو چاہو سو کرلو۔ جو کچھ تم کرتے ہو وہ اس کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(41,41,'41:41','جن لوگوں نے نصیحت کو نہ مانا جب وہ ان کے پاس آئی۔ اور یہ تو ایک عالی رتبہ کتاب ہے');
INSERT INTO "jalandhari" VALUES(41,42,'41:42','اس پر جھوٹ کا دخل نہ آگے سے ہوسکتا ہے نہ پیچھے سے۔ (اور) دانا (اور) خوبیوں والے (خدا) کی اُتاری ہوئی ہے');
INSERT INTO "jalandhari" VALUES(41,43,'41:43','تم سے وہی باتیں کہیں جاتی ہیں جو تم سے پہلے اور پیغمبروں سے کہی گئی تھیں۔ بےشک تمہارا پروردگار بخش دینے والا بھی اور عذاب الیم دینے والا بھی ہے');
INSERT INTO "jalandhari" VALUES(41,44,'41:44','اور اگر ہم اس قرآن کو غیر زبان عرب میں (نازل) کرتے تو یہ لوگ کہتے کہ اس کی آیتیں (ہماری زبان میں) کیوں کھول کر بیان نہیں کی گئیں۔ کیا (خوب کہ قرآن تو) عجمی اور (مخاطب) عربی۔ کہہ دو کہ جو ایمان لاتے ہیں ان کے لئے (یہ) ہدایت اور شفا ہے۔ اور جو ایمان نہیں لاتے ان کے کانوں میں گرانی (یعنی بہراپن) ہے اور یہ ان کے حق میں (موجب) نابینائی ہے۔ گرانی کے سبب ان کو (گویا) دور جگہ سے آواز دی جاتی ہے');
INSERT INTO "jalandhari" VALUES(41,45,'41:45','اور ہم نے موسیٰ کو کتاب دی تو اس میں اختلاف کیا گیا۔ اور اگر تمہارے پروردگار کی طرف سے ایک بات پہلے نہ ٹھہر چکی ہوتی تو ان میں فیصلہ کردیا جاتا۔ اور یہ اس (قرآن) سے شک میں الجھ رہے ہیں');
INSERT INTO "jalandhari" VALUES(41,46,'41:46','جو نیک کام کرے گا تو اپنے لئے۔ اور جو برے کام کرے گا تو ان کا ضرر اسی کو ہوگا۔ اور تمہارا پروردگار بندوں پر ظلم کرنے والا نہیں');
INSERT INTO "jalandhari" VALUES(41,47,'41:47','قیامت کے علم کا حوالہ اسی کی طرف دیا جاتا ہے (یعنی قیامت کا علم اسی کو ہے) اور نہ تو پھل گا بھوں سے نکلتے ہیں اور نہ کوئی مادہ حاملہ ہوتی اور نہ جنتی ہے مگر اس کے علم سے۔ اور جس دن وہ ان کو پکارے گا (اور کہے گا) کہ میرے شریک کہاں ہیں تو وہ کہیں گے کہ ہم تجھ سے عرض کرتے ہیں کہ ہم میں سے کسی کو (ان کی) خبر ہی نہیں');
INSERT INTO "jalandhari" VALUES(41,48,'41:48','اور جن کو پہلے وہ (خدا کے سوا) پکارا کرتے تھے (سب) ان سے غائب ہوجائیں گے اور وہ یقین کرلیں گے کہ ان کے لئے مخلصی نہیں');
INSERT INTO "jalandhari" VALUES(41,49,'41:49','انسان بھلائی کی دعائیں کرتا کرتا تو تھکتا نہیں اور اگر تکلیف پہنچ جاتی ہے تو ناامید ہوجاتا اور آس توڑ بیٹھتا ہے');
INSERT INTO "jalandhari" VALUES(41,50,'41:50','اور اگر تکلیف پہنچنے کے بعد ہم اس کو اپنی رحمت کا مزہ چکھاتے ہیں تو کہتا ہے کہ یہ تو میرا حق تھا اور میں نہیں خیال کرتا کہ قیامت برپا ہو۔ اور اگر (قیامت سچ مچ بھی ہو اور) میں اپنے پروردگار کی طرف لوٹایا بھی جاؤں تو میرے لئے اس کے ہاں بھی خوشحالی ہے۔ پس کافر جو عمل کیا کرتے وہ ہم ان کو ضرور جتائیں گے اور ان کو سخت عذاب کا مزہ چکھائیں گے');
INSERT INTO "jalandhari" VALUES(41,51,'41:51','اور جب ہم انسان پر کرم کرتے ہیں تو منہ موڑ لیتا ہے اور پہلو پھیر کر چل دیتا ہے۔ اور جب اس کو تکلیف پہنچتی ہے تو لمبی لمبی دعائیں کرنے لگتا ہے');
INSERT INTO "jalandhari" VALUES(41,52,'41:52','کہو کہ بھلا دیکھو اگر یہ (قرآن) خدا کی طرف سے ہو پھر تم اس سے انکار کرو تو اس سے بڑھ کر کون گمراہ ہے جو (حق کی) پرلے درجے کی مخالفت میں ہو');
INSERT INTO "jalandhari" VALUES(41,53,'41:53','ہم عنقریب ان کو اطراف (عالم) میں بھی اور خود ان کی ذات میں بھی اپنی نشانیاں دکھائیں گے یہاں تک کہ ان پر ظاہر ہوجائے گا کہ (قرآن) حق ہے۔ کیا تم کو یہ کافی نہیں کہ تمہارا پروردگار ہر چیز سے خبردار ہے');
INSERT INTO "jalandhari" VALUES(41,54,'41:54','دیکھو یہ اپنے پروردگار کے روبرو حاضر ہونے سے شک میں ہیں۔ سن رکھو کہ وہ ہر چیز پر احاطہ کئے ہوئے ہے');
INSERT INTO "jalandhari" VALUES(42,1,'42:1','حٰمٓ');
INSERT INTO "jalandhari" VALUES(42,2,'42:2','عٓسٓقٓ');
INSERT INTO "jalandhari" VALUES(42,3,'42:3','خدائے غالب و دانا اسی طرح تمہاری طرف مضامین اور (براہین) بھیجتا ہے جس طرح تم سے پہلے لوگوں کی طرف وحی بھیجتا رہا ہے');
INSERT INTO "jalandhari" VALUES(42,4,'42:4','جو کچھ آسمانوں میں اور جو کچھ زمین میں ہے سب اسی کا ہے۔ اور وہ عالی رتبہ اور گرامی قدر ہے');
INSERT INTO "jalandhari" VALUES(42,5,'42:5','قریب ہے کہ آسمان اوپر سے پھٹ پڑیں اور فرشتے اپنے پروردگار کی تعریف کے ساتھ اس کی تسبیج کرتے رہتے ہیں اور جو لوگ زمین میں ہیں ان کے لئے معافی مانگتے رہتے ہیں۔ سن رکھو کہ خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(42,6,'42:6','اور جن لوگوں نے اس کے سوا کارساز بنا رکھے ہیں وہ خدا کو یاد ہیں۔ اور تم ان پر داروغہ نہیں ہو');
INSERT INTO "jalandhari" VALUES(42,7,'42:7','اور اسی طرح تمہارے پاس قرآن عربی بھیجا ہے تاکہ تم بڑے گاؤں (یعنی مکّے) کے رہنے والوں کو اور جو لوگ اس کے اردگرد رہتے ہیں ان کو رستہ دکھاؤ اور انہیں قیامت کے دن کا بھی جس میں کچھ شک نہیں ہے خوف دلاؤ۔ اس روز ایک فریق بہشت میں ہوگا اور ایک فریق دوزخ میں');
INSERT INTO "jalandhari" VALUES(42,8,'42:8','اور اگر خدا چاہتا تو ان کو ایک ہی جماعت کردیتا لیکن وہ جس کو چاہتا ہے اپنی رحمت میں داخل کرلیتا ہے اور ظالموں کا نہ کوئی یار ہے اور نہ مددگار');
INSERT INTO "jalandhari" VALUES(42,9,'42:9','کیا انہوں نے اس کے سوا کارساز بنائے ہیں؟ کارساز تو خدا ہی ہے اور وہی مردوں کو زندہ کرے گا اور وہ ہر چیز پر قدرت رکھتا ہے');
INSERT INTO "jalandhari" VALUES(42,10,'42:10','اور تم جس بات میں اختلاف کرتے ہو اس کا فیصلہ خدا کی طرف (سے ہوگا) یہی خدا میرا پروردگار ہے میں اسی پر بھروسہ رکھتا ہوں۔ اور اسی کی طرف رجوع کرتا ہوں');
INSERT INTO "jalandhari" VALUES(42,11,'42:11','آسمانوں اور زمین کا پیدا کرنے والا (وہی ہے)۔ اسی نے تمہارے لئے تمہاری ہی جنس کے جوڑے بنائے اور چارپایوں کے بھی جوڑے (بنائے اور) اسی طریق پر تم کو پھیلاتا رہتا ہے۔ اس جیسی کوئی چیز نہیں۔ اور وہ دیکھتا سنتا ہے');
INSERT INTO "jalandhari" VALUES(42,12,'42:12','آسمانوں اور زمین کی کنجیاں اسی کے ہاتھ میں ہیں۔ وہ جس کے لئے چاہتا ہے رزق فراخ کردیتا ہے (اور جس کے لئے چاہتا ہے) تنگ کردیتا ہے۔ بےشک وہ ہر چیز سے واقف ہے');
INSERT INTO "jalandhari" VALUES(42,13,'42:13','اسی نے تمہارے لئے دین کا وہی رستہ مقرر کیا جس (کے اختیار کرنے کا) نوح کو حکم دیا تھا اور جس کی (اے محمدصلى الله عليه وسلم) ہم نے تمہاری طرف وحی بھیجی ہے اور جس کا ابراہیم اور موسیٰ اور عیسیٰ کو حکم دیا تھا (وہ یہ) کہ دین کو قائم رکھنا اور اس میں پھوٹ نہ ڈالنا۔ جس چیز کی طرف تم مشرکوں کو بلاتے ہو وہ ان کو دشوار گزرتی ہے۔ الله جس کو چاہتا ہے اپنی بارگاہ کا برگزیدہ کرلیتا ہے اور جو اس کی طرف رجوع کرے اسے اپنی طرف رستہ دکھا دیتا ہے');
INSERT INTO "jalandhari" VALUES(42,14,'42:14','اور یہ لوگ جو الگ الگ ہوئے ہیں تو علم (حق) آچکنے کے بعد آپس کی ضد سے (ہوئے ہیں)۔ اور اگر تمہارے پروردگار کی طرف سے ایک وقت مقرر تک کے لئے بات نہ ٹھہر چکی ہوتی تو ان میں فیصلہ کردیا جاتا۔ اور جو لوگ ان کے بعد (خدا کی) کتاب کے وارث ہوئے وہ اس (کی طرف) سے شبہے کی الجھن میں (پھنسے ہوئے) ہیں');
INSERT INTO "jalandhari" VALUES(42,15,'42:15','تو (اے محمدصلى الله عليه وسلم) اسی (دین کی) طرف (لوگوں کو) بلاتے رہنا اور جیسا تم کو حکم ہوا ہے (اسی پر) قائم رہنا۔ اور ان کی خواہشوں کی پیروی نہ کرنا۔ اور کہہ دو کہ جو کتاب خدا نے نازل فرمائی ہے میں اس پر ایمان رکھتا ہوں۔ اور مجھے حکم ہوا ہے کہ تم میں انصاف کروں۔ خدا ہی ہمارا اور تمہارا پروردگار ہے۔ ہم کو ہمارے اعمال (کا بدلہ ملے گا) اور تم کو تمہارے اعمال کا۔ ہم میں اور تم میں کچھ بحث وتکرار نہیں۔ خدا ہم (سب) کو اکھٹا کرے گا۔ اور اسی کی طرف لوٹ کر جانا ہے');
INSERT INTO "jalandhari" VALUES(42,16,'42:16','اور جو لوگ خدا (کے بارے) میں بعد اس کے کہ اسے (مومنوں نے) مان لیا ہو جھگڑتے ہیں ان کے پروردگار کے نزدیک ان کا جھگڑا لغو ہے۔ اور ان پر (خدا کا) غضب اور ان کے لئے سخت عذاب ہے');
INSERT INTO "jalandhari" VALUES(42,17,'42:17','خدا ہی تو ہے جس نے سچائی کے ساتھ کتاب نازل فرمائی اور (عدل وانصاف کی) ترازو۔ اور تم کو کیا معلوم شاید قیامت قریب ہی آ پہنچی ہو');
INSERT INTO "jalandhari" VALUES(42,18,'42:18','جو لوگ اس پر ایمان نہیں رکھتے وہ اس کے لئے جلدی کر رہے ہیں۔ اور جو مومن ہیں وہ اس سے ڈرتے ہیں۔ اور جانتے ہیں کہ وہ برحق ہے۔ دیکھو جو لوگ قیامت میں جھگڑتے ہیں وہ پرلے درجے کی گمراہی میں ہیں');
INSERT INTO "jalandhari" VALUES(42,19,'42:19','خدا اپنے بندوں پر مہربان ہے وہ جس کو چاہتا ہے رزق دیتا ہے۔ اور وہ زور والا (اور) زبردست ہے');
INSERT INTO "jalandhari" VALUES(42,20,'42:20','جو شخص آخرت کی کھیتی کا خواستگار ہو اس کو ہم اس میں سے دیں گے۔ اور جو دنیا کی کھیتی کا خواستگار ہو اس کو ہم اس میں سے دے دیں گے۔ اور اس کا آخرت میں کچھ حصہ نہ ہوگا');
INSERT INTO "jalandhari" VALUES(42,21,'42:21','کیا ان کے وہ شریک ہیں جنہوں نے ان کے لئے ایسا دین مقرر کیا ہے جس کا خدا نے حکم نہیں دیا۔ اور اگر فیصلے (کے دن) کا وعدہ نہ ہوتا تو ان میں فیصلہ کردیا جاتا اور جو ظالم ہیں ان کے لئے درد دینے والا عذاب ہے');
INSERT INTO "jalandhari" VALUES(42,22,'42:22','تم دیکھو گے کہ ظالم اپنے اعمال (کے وبال) سے ڈر رہے ہوں گے اور وہ ان پر پڑے گا۔ اور جو لوگ ایمان لائے اور نیک عمل کرتے رہے وہ بہشت کے باغوں میں ہوں گے۔ وہ جو کچھ چاہیں گے ان کے لیے ان کے پروردگار کے پاس (موجود) ہوگا۔ یہی بڑا فضل ہے');
INSERT INTO "jalandhari" VALUES(42,23,'42:23','یہی وہ (انعام ہے) جس کی خدا اپنے ان بندوں کو جو ایمان لاتے اور عمل نیک کرتے ہیں بشارت دیتا ہے۔ کہہ دو کہ میں اس کا تم سے صلہ نہیں مانگتا مگر (تم کو) قرابت کی محبت (تو چاہیئے) اور جو کوئی نیکی کرے گا ہم اس کے لئے اس میں ثواب بڑھائیں گے۔ بےشک خدا بخشنے والا قدردان ہے');
INSERT INTO "jalandhari" VALUES(42,24,'42:24','کیا یہ لوگ کہتے ہیں کہ پیغمبر نے خدا پر جھوٹ باندھ لیا ہے؟ اگر خدا چاہے تو (اے محمدصلى الله عليه وسلم) تمہارے دل پر مہر لگا دے۔ اور خدا جھوٹ کو نابود کرتا اور اپنی باتوں سے حق کو ثابت کرتا ہے۔ بےشک وہ سینے تک کی باتوں سے واقف ہے');
INSERT INTO "jalandhari" VALUES(42,25,'42:25','اور وہی تو ہے جو اپنے بندوں کی توبہ قبول کرتا اور (ان کے) قصور معاف فرماتا ہے اور جو تم کرتے ہو (سب) جانتا ہے');
INSERT INTO "jalandhari" VALUES(42,26,'42:26','اور جو ایمان لائے اور عمل نیک کئے ان کی (دعا) قبول فرماتا ہے اور ان کو اپنے فضل سے بڑھاتا ہے۔ اور جو کافر ہیں ان کے لئے سخت عذاب ہے');
INSERT INTO "jalandhari" VALUES(42,27,'42:27','اور اگر خدا اپنے بندوں کے لئے رزق میں فراخی کردیتا تو زمین میں فساد کرنے لگتے۔ لیکن وہ جو چیز چاہتا ہے اندازے کے ساتھ نازل کرتا ہے۔ بےشک وہ اپنے بندوں کو جانتا اور دیکھتا ہے');
INSERT INTO "jalandhari" VALUES(42,28,'42:28','اور وہی تو ہے جو لوگوں کے ناامید ہوجانے کے بعد مینہ برساتا اور اپنی رحمت (یعنی بارش) کی برکت کو پھیلا دیتا ہے۔ اور وہ کارساز اور سزاوار تعریف ہے');
INSERT INTO "jalandhari" VALUES(42,29,'42:29','اور اسی کی نشانیوں میں سے ہے آسمانوں اور زمین کا پیدا کرنا اور ان جانوروں کا جو اس نے ان میں پھیلا رکھے ہیں اور وہ جب چاہے ان کے جمع کرلینے پر قادر ہے');
INSERT INTO "jalandhari" VALUES(42,30,'42:30','اور جو مصیبت تم پر واقع ہوتی ہے سو تمہارے اپنے فعلوں سے اور وہ بہت سے گناہ تو معاف ہی کردیتا ہے');
INSERT INTO "jalandhari" VALUES(42,31,'42:31','اور تم زمین میں (خدا کو) عاجز نہیں کرسکتے۔ اور خدا کے سوا نہ تمہارا کوئی دوست ہے اور نہ مددگار');
INSERT INTO "jalandhari" VALUES(42,32,'42:32','اور اسی کی نشانیوں میں سے سمندر کے جہاز ہیں (جو) گویا پہاڑ (ہیں)');
INSERT INTO "jalandhari" VALUES(42,33,'42:33','اگر خدا چاہے تو ہوا کو ٹھیرا دے اور جہاز اس کی سطح پر کھڑے رہ جائیں۔ تمام صبر اور شکر کرنے والوں کے لئے ان (باتوں) میں قدرت خدا کے نمونے ہیں');
INSERT INTO "jalandhari" VALUES(42,34,'42:34','یا ان کے اعمال کے سبب ان کو تباہ کردے۔ اور بہت سے قصور معاف کردے');
INSERT INTO "jalandhari" VALUES(42,35,'42:35','اور (انتقام اس لئے لیا جائے کہ) جو لوگ ہماری آیتوں میں جھگڑتے ہیں۔ وہ جان لیں کہ ان کے لئے خلاصی نہیں');
INSERT INTO "jalandhari" VALUES(42,36,'42:36','(لوگو) جو (مال ومتاع) تم کو دیا گیا ہے وہ دنیا کی زندگی کا (ناپائدار) فائدہ ہے۔ اور جو کچھ خدا کے ہاں ہے وہ بہتر اور قائم رہنے والا ہے (یعنی) ان لوگوں کے لئے جو ایمان لائے اور اپنے پروردگار پر بھروسا رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(42,37,'42:37','اور جو بڑے بڑے گناہوں اور بےحیائی کی باتوں سے پرہیز کرتے ہیں۔ اور جب غصہ آتا ہے تو معاف کردیتے ہیں');
INSERT INTO "jalandhari" VALUES(42,38,'42:38','اور جو اپنے پروردگار کا فرمان قبول کرتے ہیں اور نماز پڑھتے ہیں۔ اور اپنے کام آپس کے مشورے سے کرتے ہیں۔ اور جو مال ہم نے ان کو عطا فرمایا ہے اس میں سے خرچ کرتے ہیں');
INSERT INTO "jalandhari" VALUES(42,39,'42:39','اور جو ایسے ہیں کہ جب ان پر ظلم وتعدی ہو تو (مناسب طریقے سے) بدلہ لیتے ہیں');
INSERT INTO "jalandhari" VALUES(42,40,'42:40','اور برائی کا بدلہ تو اسی طرح کی برائی ہے۔ مگر جو درگزر کرے اور (معاملے کو) درست کردے تو اس کا بدلہ خدا کے ذمے ہے۔ اس میں شک نہیں کہ وہ ظلم کرنے والوں کو پسند نہیں کرتا');
INSERT INTO "jalandhari" VALUES(42,41,'42:41','اور جس پر ظلم ہوا ہو اگر وہ اس کے بعد انتقام لے تو ایسے لوگوں پر کچھ الزام نہیں');
INSERT INTO "jalandhari" VALUES(42,42,'42:42','الزام تو ان لوگوں پر ہے جو لوگوں پر ظلم کرتے ہیں اور ملک میں ناحق فساد پھیلاتے ہیں۔ یہی لوگ ہیں جن کو تکلیف دینے والا عذاب ہوگا');
INSERT INTO "jalandhari" VALUES(42,43,'42:43','اور جو صبر کرے اور قصور معاف کردے تو یہ ہمت کے کام ہیں');
INSERT INTO "jalandhari" VALUES(42,44,'42:44','اور جس شخص کو خدا گمراہ کرے تو اس کے بعد اس کا کوئی دوست نہیں۔ اور تم ظالموں کو دیکھو گے کہ جب وہ (دوزخ کا) عذاب دیکھیں گے تو کہیں گے کیا (دنیا میں) واپس جانے کی بھی کوئی سبیل ہے؟');
INSERT INTO "jalandhari" VALUES(42,45,'42:45','اور تم ان کو دیکھو گے کہ دوزخ کے سامنے لائے جائیں گے ذلت سے عاجزی کرتے ہوئے چھپی (اور نیچی) نگاہ سے دیکھ رہے ہوں گے۔ اور مومن لوگ کہیں کے کہ خسارہ اٹھانے والے تو وہ ہیں جنہوں نے قیامت کے دن اپنے آپ کو اور اپنے گھر والوں کو خسارے میں ڈالا۔ دیکھو کہ بےانصاف لوگ ہمیشہ کے دکھ میں (پڑے) رہیں گے');
INSERT INTO "jalandhari" VALUES(42,46,'42:46','اور خدا کے سوا ان کے کوئی دوست نہ ہوں گے کہ خدا کے سوا ان کو مدد دے سکیں۔ اور جس کو خدا گمراہ کرے اس کے لئے (ہدایت کا) کوئی رستہ نہیں');
INSERT INTO "jalandhari" VALUES(42,47,'42:47','ان سے کہہ دو کہ) قبل اس کے کہ وہ دن جو ٹلے گا نہیں خدا کی طرف سے آ موجود ہو اپنے پروردگار کا حکم قبول کرو۔ اس دن تمہارے لئے نہ کوئی جائے پناہ ہوگی اور نہ تم سے گناہوں کا انکار ہی بن پڑے گا');
INSERT INTO "jalandhari" VALUES(42,48,'42:48','پھر اگر یہ منہ پھیر لیں تو ہم نے تم کو ان پر نگہبان بنا کر نہیں بھیجا۔ تمہارا کام تو صرف (احکام کا) پہنچا دینا ہے۔ اور جب ہم انسان کو اپنی رحمت کا مزہ چکھاتے ہیں تو اس سے خوش ہوجاتا ہے۔ اور اگر ان کو ان ہی کے اعمال کے سبب کوئی سختی پہنچتی ہے تو (سب احسانوں کو بھول جاتے ہیں) بےشک انسان بڑا ناشکرا ہے');
INSERT INTO "jalandhari" VALUES(42,49,'42:49','(تمام) بادشاہت خدا ہی کی ہے آسمانوں کی بھی اور زمین کی بھی۔ وہ جو چاہتا ہے پیدا کرتا ہے۔ جسے چاہتا ہے بیٹیاں عطا کرتا ہے اور جسے چاہتا ہے بیٹے بخشتا ہے');
INSERT INTO "jalandhari" VALUES(42,50,'42:50','یا ان کو بیٹے اور بیٹیاں دونوں عنایت فرماتا ہے۔ اور جس کو چاہتا ہے بےاولاد رکھتا ہے۔ وہ تو جاننے والا (اور) قدرت والا ہے');
INSERT INTO "jalandhari" VALUES(42,51,'42:51','اور کسی آدمی کے لئے ممکن نہیں کہ خدا اس سے بات کرے مگر الہام (کے ذریعے) سے یا پردے کے پیچھے سے یا کوئی فرشتہ بھیج دے تو وہ خدا کے حکم سے جو خدا چاہے القا کرے۔ بےشک وہ عالی رتبہ (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(42,52,'42:52','اور اسی طرح ہم نے اپنے حکم سے تمہاری طرف روح القدس کے ذریعے سے (قرآن) بھیجا ہے۔ تم نہ تو کتاب کو جانتے تھے اور نہ ایمان کو۔ لیکن ہم نے اس کو نور بنایا ہے کہ اس سے ہم اپنے بندوں میں سے جس کو چاہتے ہیں ہدایت کرتے ہیں۔ اور بےشک (اے محمدصلى الله عليه وسلم) تم سیدھا رستہ دکھاتے ہو');
INSERT INTO "jalandhari" VALUES(42,53,'42:53','(یعنی) خدا کا رستہ جو آسمانوں اور زمین کی سب چیزوں کا مالک ہے۔ دیکھو سب کام خدا کی طرف رجوع ہوں گے (اور وہی ان میں فیصلہ کرے گا)');
INSERT INTO "jalandhari" VALUES(43,1,'43:1','حٰم');
INSERT INTO "jalandhari" VALUES(43,2,'43:2','کتاب روشن کی قسم');
INSERT INTO "jalandhari" VALUES(43,3,'43:3','کہ ہم نے اس کو قرآن عربی بنایا ہے تاکہ تم سمجھو');
INSERT INTO "jalandhari" VALUES(43,4,'43:4','اور یہ بڑی کتاب (یعنی لوح محفوظ) میں ہمارے پاس (لکھی ہوئی اور) بڑی فضیلت اور حکمت والی ہے');
INSERT INTO "jalandhari" VALUES(43,5,'43:5','بھلا اس لئے کہ تم حد سے نکلے ہوئے لوگ ہو ہم تم کو نصیحت کرنے سے باز رہیں گے');
INSERT INTO "jalandhari" VALUES(43,6,'43:6','اور ہم نے پہلے لوگوں میں بھی بہت سے پیغمبر بھیجے تھے');
INSERT INTO "jalandhari" VALUES(43,7,'43:7','اور کوئی پیغمبر ان کے پاس نہیں آتا تھا مگر وہ اس سے تمسخر کرتے تھے');
INSERT INTO "jalandhari" VALUES(43,8,'43:8','تو جو ان میں سخت زور والے تھے ان کو ہم نے ہلاک کردیا اور اگلے لوگوں کی حالت گزر گئی');
INSERT INTO "jalandhari" VALUES(43,9,'43:9','اور اگر تم ان سے پوچھو کہ آسمانوں اور زمین کو کس نے پیدا کیا ہے تو کہہ دیں گے کہ ان کو غالب اور علم والے (خدا) نے پیدا کیا ہے');
INSERT INTO "jalandhari" VALUES(43,10,'43:10','جس نے تمہارے لئے زمین کو بچھونا بنایا۔ اور اس میں تمہارے لئے رستے بنائے تاکہ تم راہ معلوم کرو');
INSERT INTO "jalandhari" VALUES(43,11,'43:11','اور جس نے ایک اندازے کے ساتھ آسمان سے پانی نازل کیا۔ پھر ہم نے اس سے شہر مردہ کو زندہ کیا۔ اسی طرح تم زمین سے نکالے جاؤ گے');
INSERT INTO "jalandhari" VALUES(43,12,'43:12','اور جس نے تمام قسم کے حیوانات پیدا کئے اور تمہارے لئے کشتیاں اور چارپائے بنائے جن پر تم سوار ہوتے ہو');
INSERT INTO "jalandhari" VALUES(43,13,'43:13','تاکہ تم ان کی پیٹھ پر چڑھ بیٹھو اور جب اس پر بیٹھ جاؤ پھر اپنے پروردگار کے احسان کو یاد کرو اور کہو کہ وہ (ذات) پاک ہے جس نے اس کو ہمارے زیر فرمان کر دیا اور ہم میں طاقت نہ تھی کہ اس کو بس میں کرلیتے');
INSERT INTO "jalandhari" VALUES(43,14,'43:14','اور ہم اپنے پروردگار کی طرف لوٹ کر جانے والے ہیں');
INSERT INTO "jalandhari" VALUES(43,15,'43:15','اور انہوں نے اس کے بندوں میں سے اس کے لئے اولاد مقرر کی۔ بےشک انسان صریح ناشکرا ہے');
INSERT INTO "jalandhari" VALUES(43,16,'43:16','کیا اس نے اپنی مخلوقات میں سے خود تو بیٹیاں لیں اور تم کو چن کر بیٹے دیئے');
INSERT INTO "jalandhari" VALUES(43,17,'43:17','حالانکہ جب ان میں سے کسی کو اس چیز کی خوشخبری دی جاتی ہے جو انہوں نے خدا کے لئے بیان کی ہے تو اس کا منہ سیاہ ہوجاتا اور وہ غم سے بھر جاتا ہے');
INSERT INTO "jalandhari" VALUES(43,18,'43:18','کیا وہ جو زیور میں پرورش پائے اور جھگڑے کے وقت بات نہ کرسکے (خدا کی) بیٹی ہوسکتی ہے؟');
INSERT INTO "jalandhari" VALUES(43,19,'43:19','اور انہوں نے فرشتوں کو کہ وہ بھی خدا کے بندے ہیں (خدا کی) بیٹیاں مقرر کیا۔ کیا یہ ان کی پیدائش کے وقت حاضر تھے عنقریب ان کی شہادت لکھ لی جائے گی اور ان سے بازپرس کی جائے گی');
INSERT INTO "jalandhari" VALUES(43,20,'43:20','اور کہتے ہیں اگر خدا چاہتا تو ہم ان کو نہ پوجتے۔ ان کو اس کا کچھ علم نہیں۔ یہ تو صرف اٹکلیں دوڑا رہے ہیں');
INSERT INTO "jalandhari" VALUES(43,21,'43:21','یا ہم نے ان کو اس سے پہلے کوئی کتاب دی تھی تو یہ اس سے سند پکڑتے ہیں');
INSERT INTO "jalandhari" VALUES(43,22,'43:22','بلکہ کہنے لگے کہ ہم نے اپنے باپ دادا کو ایک رستے پر پایا ہے اور ہم انہی کے قدم بقدم چل رہے ہیں');
INSERT INTO "jalandhari" VALUES(43,23,'43:23','اور اسی طرح ہم نے تم سے پہلے کسی بستی میں کوئی ہدایت کرنے والا نہیں بھیجا مگر وہاں کے خوشحال لوگوں نے کہا کہ ہم نے اپنے باپ دادا کو ایک راہ پر پایا اور ہم قدم بقدم ان ہی کے پیچھے چلتے ہیں');
INSERT INTO "jalandhari" VALUES(43,24,'43:24','پیغمبر نے کہا اگرچہ میں تمہارے پاس ایسا (دین) لاؤں کہ جس رستے پر تم نے اپنے باپ دادا کو پایا وہ اس سے کہیں سیدھا رستہ دکھاتا ہے کہنے لگے کہ جو (دین) تم دے کر بھیجے گئے ہو ہم اس کو نہیں مانتے');
INSERT INTO "jalandhari" VALUES(43,25,'43:25','تو ہم نے ان سے انتقام لیا سو دیکھ لو کہ جھٹلانے والوں کا انجام کیسا ہوا');
INSERT INTO "jalandhari" VALUES(43,26,'43:26','اور جب ابراہیم نے اپنے باپ اور اپنی قوم کے لوگوں سے کہا کہ جن چیزوں کو تم پوجتے ہو میں ان سے بیزار ہوں');
INSERT INTO "jalandhari" VALUES(43,27,'43:27','ہاں جس نے مجھ کو پیدا کیا وہی مجھے سیدھا رستہ دکھائے گا');
INSERT INTO "jalandhari" VALUES(43,28,'43:28','اور یہی بات اپنی اولاد میں پیچھے چھوڑ گئے تاکہ وہ (خدا کی طرف) رجوع کریں');
INSERT INTO "jalandhari" VALUES(43,29,'43:29','بات یہ ہے کہ میں ان کفار کو اور ان کے باپ دادا کو متمتع کرتا رہا یہاں تک کہ ان کے پاس حق اور صاف صاف بیان کرنے والا پیغمبر آ پہنچا');
INSERT INTO "jalandhari" VALUES(43,30,'43:30','اور جب ان کے پاس حق (یعنی قرآن) آیا تو کہنے لگے کہ یہ تو جادو ہے اور ہم اس کو نہیں مانتے');
INSERT INTO "jalandhari" VALUES(43,31,'43:31','اور (یہ بھی) کہنے لگے کہ یہ قرآن ان دونوں بستیوں (یعنی مکّے اور طائف) میں سے کسی بڑے آدمی پر کیوں نازل نہ کیا گیا؟');
INSERT INTO "jalandhari" VALUES(43,32,'43:32','کیا یہ لوگ تمہارے پروردگار کی رحمت کو بانٹتے ہیں؟ ہم نے ان میں ان کی معیشت کو دنیا کی زندگی میں تقسیم کردیا اور ایک کے دوسرے پر درجے بلند کئے تاکہ ایک دوسرے سے خدمت لے اور جو کچھ یہ جمع کرتے ہیں تمہارے پروردگار کی رحمت اس سے کہیں بہتر ہے');
INSERT INTO "jalandhari" VALUES(43,33,'43:33','اور اگر یہ خیال نہ ہوتا کہ سب لوگ ایک ہی جماعت ہوجائیں گے تو جو لوگ خدا سے انکار کرتے ہیں ہم ان کے گھروں کی چھتیں چاندی کی بنا دیتے اور سیڑھیاں (بھی) جن پر وہ چڑھتے ہیں');
INSERT INTO "jalandhari" VALUES(43,34,'43:34','اور ان کے گھروں کے دروازے بھی اور تخت بھی جن پر تکیہ لگاتے ہیں');
INSERT INTO "jalandhari" VALUES(43,35,'43:35','اور (خوب) تجمل وآرائش (کردیتے) اور یہ سب دنیا کی زندگی کا تھوڑا سا سامان ہے۔ اور آخرت تمہارے پروردگار کے ہاں پرہیزگاروں کے لئے ہے');
INSERT INTO "jalandhari" VALUES(43,36,'43:36','اور جو کوئی خدا کی یاد سے آنکھیں بند کرکے (یعنی تغافل کرے) ہم اس پر ایک شیطان مقرر کردیتے ہیں تو وہ اس کا ساتھی ہوجاتا ہے');
INSERT INTO "jalandhari" VALUES(43,37,'43:37','اور یہ (شیطان) ان کو رستے سے روکتے رہتے ہیں اور وہ سمجھتے ہیں کہ سیدھے رستے پر ہیں');
INSERT INTO "jalandhari" VALUES(43,38,'43:38','یہاں تک کہ جب ہمارے پاس آئے گا تو کہے گا کہ اے کاش مجھ میں اور تجھ میں مشرق ومغرب کا فاصلہ ہوتا تو برا ساتھی ہے');
INSERT INTO "jalandhari" VALUES(43,39,'43:39','اور جب تم ظلم کرتے رہے تو آج تمہیں یہ بات فائدہ نہیں دے سکتی کہ تم (سب) عذاب میں شریک ہو');
INSERT INTO "jalandhari" VALUES(43,40,'43:40','کیا تم بہرے کو سنا سکتے ہو یا اندھے کو رستہ دکھا سکتے ہو اور جو صریح گمراہی میں ہو (اسے راہ پر لاسکتے ہو)');
INSERT INTO "jalandhari" VALUES(43,41,'43:41','اگر ہم تم کو (وفات دے کر) اٹھا لیں تو ان لوگوں سے تو ہم انتقام لے کر رہیں گے');
INSERT INTO "jalandhari" VALUES(43,42,'43:42','یا (تمہاری زندگی ہی میں) تمہیں وہ (عذاب) دکھا دیں گے جن کا ہم نے ان سے وعدہ کیا ہے ہم ان پر قابو رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(43,43,'43:43','پس تمہاری طرف جو وحی کی گئی ہے اس کو مضبوط پکڑے رہو۔ بےشک تم سیدھے رستے پر ہو');
INSERT INTO "jalandhari" VALUES(43,44,'43:44','اور یہ (قرآن) تمہارے لئے اور تمہاری قوم کے لئے نصیحت ہے اور (لوگو) تم سے عنقریب پرسش ہوگی');
INSERT INTO "jalandhari" VALUES(43,45,'43:45','اور (اے محمدصلى الله عليه وسلم) جو اپنے پیغمبر ہم نے تم سے پہلے بھیجے ہیں ان سے دریافت کرلو۔ کیا ہم نے (خدائے) رحمٰن کے سوا اور معبود بنائے تھے کہ ان کی عبادت کی جائے');
INSERT INTO "jalandhari" VALUES(43,46,'43:46','اور ہم نے موسیٰ کو اپنی نشانیاں دے کر فرعون اور اس کے درباریوں کی طرف بھیجا تو انہوں نے کہا کہ میں پروردگار عالم کا بھیجا ہوا ہوں');
INSERT INTO "jalandhari" VALUES(43,47,'43:47','جب وہ ان کے پاس ہماری نشانیاں لے کر آئے تو وہ نشانیوں سے ہنسی کرنے لگے');
INSERT INTO "jalandhari" VALUES(43,48,'43:48','اور جو نشانی ہم ان کو دکھاتے تھے وہ دوسری سے بڑی ہوتی تھی اور ہم نے ان کو عذاب میں پکڑ لیا تاکہ باز آئیں');
INSERT INTO "jalandhari" VALUES(43,49,'43:49','اور کہنے لگے کہ اے جادوگر اس عہد کے مطابق جو تیرے پروردگار نے تجھ سے کر رکھا ہے اس سے دعا کر بےشک ہم ہدایت یاب ہو جائیں گے');
INSERT INTO "jalandhari" VALUES(43,50,'43:50','سو جب ہم نے ان سے عذاب کو دور کردیا تو وہ عہد شکنی کرنے لگے');
INSERT INTO "jalandhari" VALUES(43,51,'43:51','اور فرعون نے اپنی قوم سے پکار کر کہا کہ اے قوم کیا مصر کی حکومت میرے ہاتھ میں نہیں ہے۔ اور یہ نہریں جو میرے (محلوں کے) نیچے بہہ رہی ہیں (میری نہیں ہیں) کیا تم دیکھتے نہیں');
INSERT INTO "jalandhari" VALUES(43,52,'43:52','بےشک میں اس شخص سے جو کچھ عزت نہیں رکھتا اور صاف گفتگو بھی نہیں کرسکتا کہیں بہتر ہوں');
INSERT INTO "jalandhari" VALUES(43,53,'43:53','تو اس پر سونے کے کنگن کیوں نہ اُتارے گئے یا (یہ ہوتا کہ) فرشتے جمع ہو کر اس کے ساتھ آتے');
INSERT INTO "jalandhari" VALUES(43,54,'43:54','غرض اس نے اپنی قوم کی عقل مار دی۔ اور انہوں نے اس کی بات مان لی۔ بےشک وہ نافرمان لوگ تھے');
INSERT INTO "jalandhari" VALUES(43,55,'43:55','جب انہوں نے ہم کو خفا کیا تو ہم نے ان سے انتقام لے کر اور ان سب کو ڈبو کر چھوڑا');
INSERT INTO "jalandhari" VALUES(43,56,'43:56','اور ان کو گئے گزرے کردیا اور پچھلوں کے لئے عبرت بنا دیا');
INSERT INTO "jalandhari" VALUES(43,57,'43:57','اور جب مریم کے بیٹے (عیسیٰ) کا حال بیان کیا گیا تو تمہاری قوم کے لوگ اس سے چِلا اُٹھے');
INSERT INTO "jalandhari" VALUES(43,58,'43:58','اور کہنے لگے کہ بھلا ہمارے معبود اچھے ہیں یا عیسیٰ؟ انہوں نے عیسیٰ کی جو مثال بیان کی ہے تو صرف جھگڑنے کو۔ حقیقت یہ ہے یہ لوگ ہیں ہی جھگڑالو');
INSERT INTO "jalandhari" VALUES(43,59,'43:59','وہ تو ہمارے ایسے بندے تھے جن پر ہم نے فضل کیا اور بنی اسرائیل کے لئے ان کو (اپنی قدرت کا) نمونہ بنا دیا');
INSERT INTO "jalandhari" VALUES(43,60,'43:60','اور اگر ہم چاہتے تو تم میں سے فرشتے بنا دیتے جو تمہاری جگہ زمین میں رہتے');
INSERT INTO "jalandhari" VALUES(43,61,'43:61','اور وہ قیامت کی نشانی ہیں۔ تو (کہہ دو کہ لوگو) اس میں شک نہ کرو اور میرے پیچھے چلو۔ یہی سیدھا رستہ ہے');
INSERT INTO "jalandhari" VALUES(43,62,'43:62','اور (کہیں) شیطان تم کو (اس سے) روک نہ دے۔ وہ تو تمہارا اعلاینہ دشمن ہے');
INSERT INTO "jalandhari" VALUES(43,63,'43:63','اور جب عیسیٰ نشانیاں لے کر آئے تو کہنے لگے کہ میں تمہارے پاس دانائی (کی کتاب) لے کر آیا ہوں۔ نیز اس لئے کہ بعض باتیں جن میں تم اختلاف کر رہے ہو تم کو سمجھا دوں۔ تو خدا سے ڈرو اور میرا کہا مانو');
INSERT INTO "jalandhari" VALUES(43,64,'43:64','کچھ شک نہیں کہ خدا ہی میرا اور تمہارا پروردگار ہے پس اسی کی عبادت کرو۔ یہی سیدھا رستہ ہے');
INSERT INTO "jalandhari" VALUES(43,65,'43:65','پھر کتنے فرقے ان میں سے پھٹ گئے۔ سو جو لوگ ظالم ہیں ان کی درد دینے والے دن کے عذاب سے خرابی ہے');
INSERT INTO "jalandhari" VALUES(43,66,'43:66','یہ صرف اس بات کے منتظر ہیں کہ قیامت ان پر ناگہاں آموجود ہو اور ان کو خبر تک نہ ہو');
INSERT INTO "jalandhari" VALUES(43,67,'43:67','(جو آپس میں) دوست (ہیں) اس روز ایک دوسرے کے دشمن ہوں گے۔ مگر پرہیزگار (کہ باہم دوست ہی رہیں گے)');
INSERT INTO "jalandhari" VALUES(43,68,'43:68','میرے بندو آج تمہیں نہ کچھ خوف ہے اور نہ تم غمناک ہوگے');
INSERT INTO "jalandhari" VALUES(43,69,'43:69','جو لوگ ہماری آیتوں پر ایمان لائے اور فرمانبردار ہوگئے');
INSERT INTO "jalandhari" VALUES(43,70,'43:70','(ان سے کہا جائے گا) کہ تم اور تمہاری بیویاں عزت (واحترام) کے ساتھ بہشت میں داخل ہوجاؤ');
INSERT INTO "jalandhari" VALUES(43,71,'43:71','ان پر سونے کی پرچوں اور پیالوں کا دور چلے گا۔ اور وہاں جو جی چاہے اور جو آنکھوں کو اچھا لگے (موجود ہوگا) اور (اے اہل جنت) تم اس میں ہمیشہ رہو گے');
INSERT INTO "jalandhari" VALUES(43,72,'43:72','اور یہ جنت جس کے تم مالک کر دیئے گئے ہو تمہارے اعمال کا صلہ ہے');
INSERT INTO "jalandhari" VALUES(43,73,'43:73','وہاں تمہارے لئے بہت سے میوے ہیں جن کو تم کھاؤ گے');
INSERT INTO "jalandhari" VALUES(43,74,'43:74','(اور کفار) گنہگار ہمیشہ دوزخ کے عذاب میں رہیں گے');
INSERT INTO "jalandhari" VALUES(43,75,'43:75','جو ان سے ہلکا نہ کیا جائے گا اور وہ اس میں نامید ہو کر پڑے رہیں گے');
INSERT INTO "jalandhari" VALUES(43,76,'43:76','اور ہم نے ان پر ظلم نہیں کیا۔ بلکہ وہی (اپنے آپ پر) ظلم کرتے تھے');
INSERT INTO "jalandhari" VALUES(43,77,'43:77','اور پکاریں گے کہ اے مالک تمہارا پروردگار ہمیں موت دے دے۔ وہ کہے گا کہ تم ہمیشہ (اسی حالت میں) رہو گے');
INSERT INTO "jalandhari" VALUES(43,78,'43:78','ہم تمہارے پاس حق لے کر آئے ہیں لیکن تم اکثر حق سے ناخوش ہوتے رہے');
INSERT INTO "jalandhari" VALUES(43,79,'43:79','کیا انہوں نے کوئی بات ٹھہرا رکھی ہے تو ہم بھی کچھ ٹھہرانے والے ہیں');
INSERT INTO "jalandhari" VALUES(43,80,'43:80','کیا یہ لوگ یہ خیال کرتے ہیں کہ ہم ان کی پوشیدہ باتوں اور سرگوشیوں کو سنتے نہیں؟ ہاں ہاں (سب سنتے ہیں) اور ہمارے فرشتے ان کے پاس (ان کی سب باتیں) لکھ لیتے ہیں');
INSERT INTO "jalandhari" VALUES(43,81,'43:81','کہہ دو کہ اگر خدا کے اولاد ہو تو میں (سب سے) پہلے (اس کی) عبادت کرنے والا ہوں');
INSERT INTO "jalandhari" VALUES(43,82,'43:82','یہ جو کچھ بیان کرتے ہیں آسمانوں اور زمین کا مالک (اور) عرش کا مالک اس سے پاک ہے');
INSERT INTO "jalandhari" VALUES(43,83,'43:83','تو ان کو بک بک کرنے اور کھیلنے دو۔ یہاں تک کہ جس دن کا ان سے وعدہ کیا جاتا ہے اس کو دیکھ لیں');
INSERT INTO "jalandhari" VALUES(43,84,'43:84','اور وہی (ایک) آسمانوں میں معبود ہے اور (وہی) زمین میں معبود ہے۔ اور وہ دانا (اور) علم والا ہے');
INSERT INTO "jalandhari" VALUES(43,85,'43:85','اور وہ بہت بابرکت ہے جس کے لئے آسمانوں اور زمین کی اور جو کچھ ان دونوں میں ہے سب کی بادشاہت ہے۔ اور اسی کو قیامت کا علم ہے اور اسی کی طرف تم لوٹ کر جاؤ گے');
INSERT INTO "jalandhari" VALUES(43,86,'43:86','اور جن کو یہ لوگ خدا کے سوا پکارتے ہیں وہ سفارش کا کچھ اختیار نہیں رکھتے۔ ہاں جو علم ویقین کے ساتھ حق کی گواہی دیں (وہ سفارش کرسکتے ہیں)');
INSERT INTO "jalandhari" VALUES(43,87,'43:87','اور اگر تم ان سے پوچھو کہ ان کو کس نے پیدا کیا ہے تو کہہ دیں گے کہ خدا نے۔ تو پھر یہ کہاں بہکے پھرتے ہیں؟');
INSERT INTO "jalandhari" VALUES(43,88,'43:88','اور (بسااوقات) پیغمبر کہا کرتے ہیں کہ اے پروردگار یہ ایسے لوگ ہیں کہ ایمان نہیں لاتے');
INSERT INTO "jalandhari" VALUES(43,89,'43:89','تو ان سے منہ پھیر لو اور سلام کہہ دو۔ ان کو عنقریب (انجام) معلوم ہوجائے گا');
INSERT INTO "jalandhari" VALUES(44,1,'44:1','حٰم');
INSERT INTO "jalandhari" VALUES(44,2,'44:2','اس کتاب روشن کی قسم');
INSERT INTO "jalandhari" VALUES(44,3,'44:3','کہ ہم نے اس کو مبارک رات میں نازل فرمایا ہم تو رستہ دکھانے والے ہیں');
INSERT INTO "jalandhari" VALUES(44,4,'44:4','اسی رات میں تمام حکمت کے کام فیصل کئے جاتے ہیں');
INSERT INTO "jalandhari" VALUES(44,5,'44:5','(یعنی) ہمارے ہاں سے حکم ہو کر۔ بےشک ہم ہی (پیغمبر کو) بھیجتے ہیں');
INSERT INTO "jalandhari" VALUES(44,6,'44:6','(یہ) تمہارے پروردگار کی رحمت ہے۔ وہ تو سننے والا جاننے والا ہے');
INSERT INTO "jalandhari" VALUES(44,7,'44:7','آسمانوں اور زمین کا اور جو کچھ ان دونوں میں ہے سب کا مالک۔ بشرطیکہ تم لوگ یقین کرنے والے ہو');
INSERT INTO "jalandhari" VALUES(44,8,'44:8','اس کے سوا کوئی معبود نہیں۔ (وہی) جِلاتا ہے اور (وہی) مارتا ہے۔ وہی تمہارا اور تمہارے باپ دادا کا پروردگار ہے');
INSERT INTO "jalandhari" VALUES(44,9,'44:9','لیکن یہ لوگ شک میں کھیل رہے ہیں');
INSERT INTO "jalandhari" VALUES(44,10,'44:10','تو اس دن کا انتظار کرو کہ آسمان سے صریح دھواں نکلے گا');
INSERT INTO "jalandhari" VALUES(44,11,'44:11','جو لوگوں پر چھا جائے گا۔ یہ درد دینے والا عذاب ہے');
INSERT INTO "jalandhari" VALUES(44,12,'44:12','اے پروردگار ہم سے اس عذاب کو دور کر ہم ایمان لاتے ہیں');
INSERT INTO "jalandhari" VALUES(44,13,'44:13','(اس وقت) ان کو نصیحت کہاں مفید ہوگی جب کہ ان کے پاس پیغمبر آچکے جو کھول کھول کر بیان کر دیتے ہیں');
INSERT INTO "jalandhari" VALUES(44,14,'44:14','پھر انہوں نے ان سے منہ پھیر لیا اور کہنے لگے (یہ تو) پڑھایا ہوا (اور) دیوانہ ہے');
INSERT INTO "jalandhari" VALUES(44,15,'44:15','ہم تو تھوڑے دنوں عذاب ٹال دیتے ہیں (مگر) تم پھر کفر کرنے لگتے ہو');
INSERT INTO "jalandhari" VALUES(44,16,'44:16','جس دن ہم بڑی سخت پکڑ پکڑیں گے تو بےشک انتقام لے کر چھوڑیں گے');
INSERT INTO "jalandhari" VALUES(44,17,'44:17','اور ان سے پہلے ہم نے قوم فرعون کی آزمائش کی اور ان کے پاس ایک عالی قدر پیغمبر آئے');
INSERT INTO "jalandhari" VALUES(44,18,'44:18','(جنہوں نے) یہ (کہا) کہ خدا کے بندوں (یعنی بنی اسرائیل) کو میرے حوالے کردو میں تمہارا امانت دار پیغمبر ہوں');
INSERT INTO "jalandhari" VALUES(44,19,'44:19','اور خدا کے سامنے سرکشی نہ کرو۔ میں تمہارے پاس کھلی دلیل لے کر آیا ہوں');
INSERT INTO "jalandhari" VALUES(44,20,'44:20','اور اس (بات) سے کہ تم مجھے سنگسار کرو اپنے اور تمہارے پروردگار کی پناہ مانگتا ہوں');
INSERT INTO "jalandhari" VALUES(44,21,'44:21','اور اگر تم مجھ پر ایمان نہیں لاتے تو مجھ سے الگ ہو جاؤ');
INSERT INTO "jalandhari" VALUES(44,22,'44:22','تب موسیٰ نے اپنے پروردگار سے دعا کی کہ یہ نافرمان لوگ ہیں');
INSERT INTO "jalandhari" VALUES(44,23,'44:23','(خدا نے فرمایا کہ) میرے بندوں کو راتوں رات لے کر چلے جاؤ اور (فرعونی) ضرور تمہارا تعاقب کریں گے');
INSERT INTO "jalandhari" VALUES(44,24,'44:24','اور دریا سے (کہ) خشک (ہو رہا ہوگا) پار ہو جاؤ (تمہارے بعد) ان کا تمام لشکر ڈبو دیا جائے گا');
INSERT INTO "jalandhari" VALUES(44,25,'44:25','وہ لوگ بہت سے باغ اور چشمے چھوڑ گئے');
INSERT INTO "jalandhari" VALUES(44,26,'44:26','اور کھیتیاں اور نفیس مکان');
INSERT INTO "jalandhari" VALUES(44,27,'44:27','اور آرام کی چیزیں جن میں عیش کیا کرتے تھے');
INSERT INTO "jalandhari" VALUES(44,28,'44:28','اسی طرح (ہوا) اور ہم نے دوسرے لوگوں کو ان چیزوں کا مالک بنا دیا');
INSERT INTO "jalandhari" VALUES(44,29,'44:29','پھر ان پر نہ تو آسمان کو اور زمین کو رونا آیا اور نہ ان کو مہلت ہی دی گئی');
INSERT INTO "jalandhari" VALUES(44,30,'44:30','اور ہم نے بنی اسرائیل کو ذلت کے عذاب سے نجات دی');
INSERT INTO "jalandhari" VALUES(44,31,'44:31','(یعنی) فرعون سے۔ بےشک وہ سرکش (اور) حد سے نکلا ہوا تھا');
INSERT INTO "jalandhari" VALUES(44,32,'44:32','اور ہم نے بنی اسرائیل کو اہل عالم سے دانستہ منتخب کیا تھا');
INSERT INTO "jalandhari" VALUES(44,33,'44:33','اور ان کو ایسی نشانیاں دی تھیں جن میں صریح آزمائش تھی');
INSERT INTO "jalandhari" VALUES(44,34,'44:34','یہ لوگ یہ کہتے ہیں');
INSERT INTO "jalandhari" VALUES(44,35,'44:35','کہ ہمیں صرف پہلی دفعہ (یعنی ایک بار) مرنا ہے اور (پھر) اُٹھنا نہیں');
INSERT INTO "jalandhari" VALUES(44,36,'44:36','پس اگر تم سچے ہو تو ہمارے باپ دادا کو (زندہ کر) لاؤ');
INSERT INTO "jalandhari" VALUES(44,37,'44:37','بھلا یہ اچھے ہیں یا تُبّع کی قوم اور وہ لوگ جو تم سے پہلے ہوچکے ہیں۔ ہم نے ان (سب) کو ہلاک کردیا۔ بےشک وہ گنہگار تھے');
INSERT INTO "jalandhari" VALUES(44,38,'44:38','اور ہم نے آسمانوں اور زمین کو اور جو کچھ ان میں ہے ان کو کھیلتے ہوئے نہیں بنایا');
INSERT INTO "jalandhari" VALUES(44,39,'44:39','ان کو ہم نے تدبیر سے پیدا کیا ہے لیکن اکثر لوگ نہیں جانتے');
INSERT INTO "jalandhari" VALUES(44,40,'44:40','کچھ شک نہیں کہ فیصلے کا دن ان سب (کے اُٹھنے) کا وقت ہے');
INSERT INTO "jalandhari" VALUES(44,41,'44:41','جس دن کوئی دوست کسی دوست کے کچھ کام نہ آئے گا اور نہ ان کو مدد ملے گی');
INSERT INTO "jalandhari" VALUES(44,42,'44:42','مگر جس پر خدا مہربانی کرے۔ وہ تو غالب اور مہربان ہے');
INSERT INTO "jalandhari" VALUES(44,43,'44:43','بلاشبہ تھوہر کا درخت');
INSERT INTO "jalandhari" VALUES(44,44,'44:44','گنہگار کا کھانا ہے');
INSERT INTO "jalandhari" VALUES(44,45,'44:45','جیسے پگھلا ہوا تانبا۔ پیٹوں میں (اس طرح) کھولے گا');
INSERT INTO "jalandhari" VALUES(44,46,'44:46','جس طرح گرم پانی کھولتا ہے');
INSERT INTO "jalandhari" VALUES(44,47,'44:47','(حکم دیا جائے گا کہ) اس کو پکڑ لو اور کھینچتے ہوئے دوزخ کے بیچوں بیچ لے جاؤ');
INSERT INTO "jalandhari" VALUES(44,48,'44:48','پھر اس کے سر پر کھولتا ہوا پانی انڈیل دو (کہ عذاب پر) عذاب (ہو)');
INSERT INTO "jalandhari" VALUES(44,49,'44:49','(اب) مزہ چکھ۔ تو بڑی عزت والا (اور) سردار ہے');
INSERT INTO "jalandhari" VALUES(44,50,'44:50','یہ وہی (دوزخ) ہے جس میں تم لوگ شک کیا کرتے تھے');
INSERT INTO "jalandhari" VALUES(44,51,'44:51','بےشک پرہیزگار لوگ امن کے مقام میں ہوں گے');
INSERT INTO "jalandhari" VALUES(44,52,'44:52','(یعنی) باغوں اور چشموں میں');
INSERT INTO "jalandhari" VALUES(44,53,'44:53','حریر کا باریک اور دبیز لباس پہن کر ایک دوسرے کے سامنے بیٹھے ہوں گے');
INSERT INTO "jalandhari" VALUES(44,54,'44:54','اس طرح (کا حال ہوگا) اور ہم بڑی بڑی آنکھوں والی سفید رنگ کی عورتوں سے ان کے جوڑے لگائیں گے');
INSERT INTO "jalandhari" VALUES(44,55,'44:55','وہاں خاطر جمع سے ہر قسم کے میوے منگوائیں گے (اور کھائیں گے)');
INSERT INTO "jalandhari" VALUES(44,56,'44:56','(اور) پہلی دفعہ کے مرنے کے سوا (کہ مرچکے تھے) موت کا مزہ نہیں چکھیں گے۔ اور خدا ان کو دوزخ کے عذاب سے بچا لے گا');
INSERT INTO "jalandhari" VALUES(44,57,'44:57','یہ تمہارے پروردگار کا فضل ہے۔ یہی تو بڑی کامیابی ہے');
INSERT INTO "jalandhari" VALUES(44,58,'44:58','ہم نے اس (قرآن) کو تمہاری زبان میں آسان کردیا ہے تاکہ یہ لوگ نصیحت پکڑیں');
INSERT INTO "jalandhari" VALUES(44,59,'44:59','پس تم بھی انتظار کرو یہ بھی انتظار کر رہے ہیں');
INSERT INTO "jalandhari" VALUES(45,1,'45:1','حٰم');
INSERT INTO "jalandhari" VALUES(45,2,'45:2','اس کتاب کا اُتارا جانا خدائے غالب (اور) دانا (کی طرف) سے ہے');
INSERT INTO "jalandhari" VALUES(45,3,'45:3','بےشک آسمانوں اور زمین میں ایمان والوں کے لئے (خدا کی قدرت کی) نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(45,4,'45:4','اور تمہاری پیدائش میں بھی۔ اور جانوروں میں بھی جن کو وہ پھیلاتا ہے یقین کرنے والوں کے لئے نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(45,5,'45:5','اور رات اور دن کے آگے پیچھے آنے جانے میں اور وہ جو خدا نے آسمان سے (ذریعہٴ) رزق نازل فرمایا پھر اس سے زمین کو اس کے مرجانے کے بعد زندہ کیا اس میں اور ہواؤں کے بدلنے میں عقل والوں کے لئے نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(45,6,'45:6','یہ خدا کی آیتیں ہیں جو ہم تم کو سچائی کے ساتھ پڑھ کر سناتے ہیں۔ تو یہ خدا اور اس کی آیتوں کے بعد کس بات پر ایمان لائیں گے؟');
INSERT INTO "jalandhari" VALUES(45,7,'45:7','ہر جھوٹے گنہگار پر افسوس ہے');
INSERT INTO "jalandhari" VALUES(45,8,'45:8','(کہ) خدا کی آیتیں اس کو پڑھ کر سنائی جاتی ہیں تو ان کو سن تو لیتا ہے (مگر) پھر غرور سے ضد کرتا ہے کہ گویا ان کو سنا ہی نہیں۔ سو ایسے شخص کو دکھ دینے والے عذاب کی خوشخبری سنا دو');
INSERT INTO "jalandhari" VALUES(45,9,'45:9','اور جب ہماری کچھ آیتیں اسے معلوم ہوتی ہیں تو ان کی ہنسی اُڑاتا ہے۔ ایسے لوگوں کے لئے ذلیل کرنے والا عذاب ہے');
INSERT INTO "jalandhari" VALUES(45,10,'45:10','ان کے سامنے دوزخ ہے۔ اور جو کام وہ کرتے رہے کچھ بھی ان کے کام نہ آئیں گے۔ اور نہ وہی (کام آئیں گے) جن کو انہوں نے خدا کے سوا معبود بنا رکھا تھا۔ اور ان کے لئے بڑا عذاب ہے');
INSERT INTO "jalandhari" VALUES(45,11,'45:11','یہ ہدایت (کی کتاب) ہے۔ اور جو لوگ اپنے پروردگار کی آیتوں سے انکار کرتے ہیں ان کو سخت قسم کا درد دینے والا عذاب ہوگا');
INSERT INTO "jalandhari" VALUES(45,12,'45:12','خدا ہی تو ہے جس نے دریا کو تمہارے قابو کردیا تاکہ اس کے حکم سے اس میں کشتیاں چلیں اور تاکہ تم اس کے فضل سے (معاش) تلاش کرو اور تاکہ شکر کرو');
INSERT INTO "jalandhari" VALUES(45,13,'45:13','اور جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے سب کو اپنے (حکم) سے تمہارے کام میں لگا دیا۔ جو لوگ غور کرتے ہیں ان کے لئے اس میں (قدرت خدا کی) نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(45,14,'45:14','مومنوں سے کہہ دو کہ جو لوگ خدا کے دنوں کی (جو اعمال کے بدلے کے لئے مقرر ہیں) توقع نہیں رکھتے ان سے درگزر کریں۔ تاکہ وہ ان لوگوں کو ان کے اعمال کا بدلے دے');
INSERT INTO "jalandhari" VALUES(45,15,'45:15','جو کوئی عمل نیک کرے گا تو اپنے لئے۔ اور جو برے کام کرے گا تو ان کا ضرر اسی کو ہوگا۔ پھر تم اپنے پروردگار کی طرف لوٹ کر جاؤ گے');
INSERT INTO "jalandhari" VALUES(45,16,'45:16','اور ہم نے بنی اسرائیل کو کتاب (ہدایت) اور حکومت اور نبوت بخشی اور پاکیزہ چیزیں عطا فرمائیں اور اہل عالم پر فضیلت دی');
INSERT INTO "jalandhari" VALUES(45,17,'45:17','اور ان کو دین کے بارے میں دلیلیں عطا کیں۔ تو انہوں نے جو اختلاف کیا تو علم آچکنے کے بعد آپس کی ضد سے کیا۔ بےشک تمہارا پروردگار قیامت کے دن ان میں ان باتوں کا جن میں وہ اختلاف کرتے تھے فیصلہ کرے گا');
INSERT INTO "jalandhari" VALUES(45,18,'45:18','پھر ہم نے تم کو دین کے کھلے رستے پر (قائم) کر دیا تو اسی (رستے) پر چلے چلو اور نادانوں کی خواہشوں کے پیچھے نہ چلنا');
INSERT INTO "jalandhari" VALUES(45,19,'45:19','یہ خدا کے سامنے تمہارے کسی کام نہیں آئیں گے۔ اور ظالم لوگ ایک دوسرے کے دوست ہوتے ہیں۔ اور خدا پرہیزگاروں کا دوست ہے');
INSERT INTO "jalandhari" VALUES(45,20,'45:20','یہ قرآن لوگوں کے لئے دانائی کی باتیں ہیں اور جو یقین رکھتے ہیں ان کے لئے ہدایت اور رحمت ہے');
INSERT INTO "jalandhari" VALUES(45,21,'45:21','جو لوگ برے کام کرتے ہیں کیا وہ یہ خیال کرتے ہیں کہ ہم ان کو ان لوگوں جیسا کردیں گے جو ایمان لائے اور عمل نیک کرتے رہے اور ان کی زندگی اور موت یکساں ہوگی۔ یہ جو دعوے کرتے ہیں برے ہیں');
INSERT INTO "jalandhari" VALUES(45,22,'45:22','اور خدا نے آسمانوں اور زمین کو حکمت سے پیدا کیا ہے اور تاکہ ہر شخص اپنے اعمال کا بدلہ پائے اور ان پر ظلم نہیں کیا جائے گا');
INSERT INTO "jalandhari" VALUES(45,23,'45:23','بھلا تم نے اس شخص کو دیکھا جس نے اپنی خواہش کو معبود بنا رکھا ہے اور باوجود جاننے بوجھنے کے (گمراہ ہو رہا ہے تو) خدا نے (بھی) اس کو گمراہ کردیا اور اس کے کانوں اور دل پر مہر لگا دی اور اس کی آنکھوں پر پردہ ڈال دیا۔ اب خدا کے سوا اس کو کون راہ پر لاسکتا ہے۔ بھلا تم کیوں نصیحت نہیں پکڑتے؟');
INSERT INTO "jalandhari" VALUES(45,24,'45:24','اور کہتے ہیں کہ ہماری زندگی تو صرف دنیا ہی کی ہے کہ (یہیں) مرتے اور جیتے ہیں اور ہمیں تو زمانہ مار دیتا ہے۔ اور ان کو اس کا کچھ علم نہیں۔ صرف ظن سے کام لیتے ہیں');
INSERT INTO "jalandhari" VALUES(45,25,'45:25','اور جب ان کے سامنے ہماری کھلی کھلی آیتیں پڑھی جاتی ہیں تو ان کی یہی حجت ہوتی ہے کہ اگر تم سچے ہو تو ہمارے باپ دادا کو (زندہ کر) لاؤ');
INSERT INTO "jalandhari" VALUES(45,26,'45:26','کہہ دو کہ خدا ہی تم کو جان بخشتا ہے پھر (وہی) تم کو موت دیتا ہے پھر تم کو قیامت کے روز جس (کے آنے) میں کچھ شک نہیں تم کو جمع کرے گا لیکن بہت سے لوگ نہیں جانتے');
INSERT INTO "jalandhari" VALUES(45,27,'45:27','اور آسمانوں اور زمین کی بادشاہت خدا ہی کی ہے۔ اور جس روز قیامت برپا ہوگی اس روز اہل باطل خسارے میں پڑ جائیں گے');
INSERT INTO "jalandhari" VALUES(45,28,'45:28','اور تم ہر ایک فرقے کو دیکھو گے کہ گھٹنوں کے بل بیٹھا ہوگا۔ اور ہر ایک جماعت اپنی کتاب (اعمال) کی طرف بلائی جائے گی۔ جو کچھ تم کرتے رہے ہو آج تم کو اس کا بدلہ دیا جائے گا');
INSERT INTO "jalandhari" VALUES(45,29,'45:29','یہ ہماری کتاب تمہارے بارے میں سچ سچ بیان کردے گی۔ جو کچھ تم کیا کرتے تھے ہم لکھواتے جاتے ہیں');
INSERT INTO "jalandhari" VALUES(45,30,'45:30','تو جو لوگ ایمان لائے اور نیک کام کرتے رہے ان کا پروردگار انہیں رحمت (کے باغ) میں داخل کرے گا۔ یہی صریح کامیابی ہے');
INSERT INTO "jalandhari" VALUES(45,31,'45:31','اور جنہوں نے کفر کیا۔ (ان سے کہا جائے گا کہ) بھلا ہماری آیتیں تم کو پڑھ کر سنائی نہیں جاتی تھیں؟ پھر تم نے تکبر کیا اور تم نافرمان لوگ تھے');
INSERT INTO "jalandhari" VALUES(45,32,'45:32','اور جب کہا جاتا تھا کہ خدا کا وعدہ سچا ہے اور قیامت میں کچھ شک نہیں تو تم کہتے تھے ہم نہیں جانتے کہ قیامت کیا ہے۔ ہم اس کو محض ظنی خیال کرتے ہیں اور ہمیں یقین نہیں آتا');
INSERT INTO "jalandhari" VALUES(45,33,'45:33','اور ان کے اعمال کی برائیاں ان پر ظاہر ہوجائیں گی اور جس (عذاب) کی وہ ہنسی اُڑاتے تھے وہ ان کو آگھیرے گا');
INSERT INTO "jalandhari" VALUES(45,34,'45:34','اور کہا جائے گا کہ جس طرح تم نے اس دن کے آنے کو بھلا رکھا تھا۔ اسی طرح آج ہم تمہیں بھلا دیں گے اور تمہارا ٹھکانا دوزخ ہے اور کوئی تمہارا مددگار نہیں');
INSERT INTO "jalandhari" VALUES(45,35,'45:35','یہ اس لئے کہ تم نے خدا کی آیتوں کو مخول بنا رکھا تھا اور دنیا کی زندگی نے تم کو دھوکے میں ڈال رکھا تھا۔ سو آج یہ لوگ نہ دوزخ سے نکالے جائیں گے اور نہ ان کی توبہ قبول کی جائے گی');
INSERT INTO "jalandhari" VALUES(45,36,'45:36','پس خدا ہی کو ہر طرح کی تعریف (سزاوار) ہے جو آسمانوں کا مالک اور زمین کا مالک اور تمام جہان کا پروردگار ہے');
INSERT INTO "jalandhari" VALUES(45,37,'45:37','اور آسمانوں اور زمین میں اُسی کے لئے بڑائی ہے۔ اور وہ غالب اور دانا ہے');
INSERT INTO "jalandhari" VALUES(46,1,'46:1','حٰم');
INSERT INTO "jalandhari" VALUES(46,2,'46:2','(یہ) کتاب خدائے غالب (اور) حکمت والے کی طرف سے نازل ہوئی ہے');
INSERT INTO "jalandhari" VALUES(46,3,'46:3','ہم نے آسمانوں اور زمین کو اور جو کچھ ان دونوں میں ہے مبنی برحکمت اور ایک وقت مقرر تک کے لئے پیدا کیا ہے۔ اور کافروں کو جس چیز کی نصیحت کی جاتی ہے اس سے منہ پھیر لیتے ہیں');
INSERT INTO "jalandhari" VALUES(46,4,'46:4','کہو کہ بھلا تم نے ان چیزوں کو دیکھا ہے جن کو تم خدا کے سوا پکارتے ہو (ذرا) مجھے بھی تو دکھاؤ کہ انہوں نے زمین میں کون سی چیز پیدا کی ہے۔ یا آسمانوں میں ان کی شرکت ہے۔ اگر سچے ہو تو اس سے پہلے کی کوئی کتاب میرے پاس لاؤ۔ یا علم (انبیاء میں) سے کچھ (منقول) چلا آتا ہو (تو اسے پیش کرو)');
INSERT INTO "jalandhari" VALUES(46,5,'46:5','اور اس شخص سے بڑھ کر کون گمراہ ہوسکتا ہے جو ایسے کو پکارے جو قیامت تک اسے جواب نہ دے سکے اور ان کو ان کے پکارنے ہی کی خبر نہ ہو');
INSERT INTO "jalandhari" VALUES(46,6,'46:6','اور جب لوگ جمع کئے جائیں گے تو وہ ان کے دشمن ہوں گے اور ان کی پرستش سے انکار کریں گے');
INSERT INTO "jalandhari" VALUES(46,7,'46:7','اور جب ان کے سامنے ہماری کھلی آیتیں پڑھی جاتی ہیں تو کافر حق کے بارے میں جب ان کے پاس آچکا کہتے ہیں کہ یہ تو صریح جادو ہے');
INSERT INTO "jalandhari" VALUES(46,8,'46:8','کیا یہ کہتے ہیں کہ اس نے اس کو از خود بنا لیا ہے۔ کہہ دو کہ اگر میں نے اس کو اپنی طرف سے بنایا ہو تو تم خدا کے سامنے میرے (بچاؤ کے) لئے کچھ اختیار نہیں رکھتے۔ وہ اس گفتگو کو خوب جانتا ہے جو تم اس کے بارے میں کرتے ہو۔ وہی میرے اور تمہارے درمیان گواہ کافی ہے۔ اور وہ بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(46,9,'46:9','کہہ دو کہ میں کوئی نیا پیغمبر نہیں آیا۔ اور میں نہیں جانتا کہ میرے ساتھ کیا سلوک کیا جائے گا اور تمہارے ساتھ کیا (کیا جائے گا) میں تو اسی کی پیروی کرتا ہوں جو مجھ پر وحی آتی ہے اور میرا کام تو علانیہ ہدایت کرنا ہے');
INSERT INTO "jalandhari" VALUES(46,10,'46:10','کہو کہ بھلا دیکھو تو اگر یہ (قرآن) خدا کی طرف سے ہو اور تم نے اس سے انکار کیا اور بنی اسرائیل میں سے ایک گواہ اسی طرح کی ایک (کتاب) کی گواہی دے چکا اور ایمان لے آیا اور تم نے سرکشی کی (تو تمہارے ظالم ہونے میں کیا شک ہے)۔ بےشک خدا ظالم لوگوں کو ہدایت نہیں دیتا');
INSERT INTO "jalandhari" VALUES(46,11,'46:11','اور کافر مومنوں سے کہتے ہیں کہ اگر یہ (دین) کچھ بہتر ہوتا تو یہ لوگ اس کی طرف ہم سے پہلے نہ دوڑ پڑتے اور جب وہ اس سے ہدایت یاب نہ ہوئے تو اب کہیں گے کہ یہ پرانا جھوٹ ہے');
INSERT INTO "jalandhari" VALUES(46,12,'46:12','اور اس سے پہلے موسیٰ کی کتاب تھی (لوگوں کے لئے) رہنما اور رحمت۔ اور یہ کتاب عربی زبان میں ہے اسی کی تصدیق کرنے والی تاکہ ظالموں کو ڈرائے۔ اور نیکوکاروں کو خوشخبری سنائے');
INSERT INTO "jalandhari" VALUES(46,13,'46:13','جن لوگوں نے کہا کہ ہمارا پروردگار خدا ہے پھر وہ (اس پر) قائم رہے تو ان کو نہ کچھ خوف ہوگا اور نہ وہ غمناک ہوں گے');
INSERT INTO "jalandhari" VALUES(46,14,'46:14','یہی اہل جنت ہیں کہ ہمیشہ اس میں رہیں گے۔ (یہ) اس کا بدلہ (ہے) جو وہ کیا کرتے تھے');
INSERT INTO "jalandhari" VALUES(46,15,'46:15','اور ہم نے انسان کو اپنے والدین کے ساتھ بھلائی کرنے کا حکم دیا۔ اس کی ماں نے اس کو تکلیف سے پیٹ میں رکھا اور تکلیف ہی سے جنا۔ اور اس کا پیٹ میں رہنا اور دودھ چھوڑنا ڈھائی برس میں ہوتا ہے۔ یہاں تک کہ جب خوب جوان ہوتا ہے اور چالیس برس کو پہنچ جاتا ہے تو کہتا ہے کہ اے میرے پروردگار مجھے توفیق دے کہ تو نے جو احسان مجھ پر اور میرے ماں باپ پر کئے ہیں ان کا شکر گزار ہوں اور یہ کہ نیک عمل کروں جن کو تو پسند کرے۔ اور میرے لئے میری اولاد میں صلاح (وتقویٰ) دے۔ میں تیری طرف رجوع کرتا ہوں اور میں فرمانبرداروں میں ہوں');
INSERT INTO "jalandhari" VALUES(46,16,'46:16','یہی لوگ ہیں جن کے اعمال نیک ہم قبول کریں گے اور ان کے گناہوں سے درگزر فرمائیں گے اور (یہی) اہل جنت میں (ہوں گے)۔ (یہ) سچا وعدہ (ہے) جو ان سے کیا جاتا ہے');
INSERT INTO "jalandhari" VALUES(46,17,'46:17','اور جس شخص نے اپنے ماں باپ سے کہا کہ اُف اُف! تم مجھے یہ بتاتے ہو کہ میں (زمین سے) نکالا جاؤں گا حالانکہ بہت سے لوگ مجھ سے پہلے گزر چکے ہیں۔ اور وہ دونوں خدا کی جناب میں فریاد کرتے (ہوئے کہتے) تھے کہ کم بخت ایمان لا۔ خدا کا وعدہ تو سچا ہے۔ تو کہنے لگا یہ تو پہلے لوگوں کی کہانیاں ہیں');
INSERT INTO "jalandhari" VALUES(46,18,'46:18','یہی وہ لوگ ہیں جن کے بارے میں جنوں اور انسانوں کی (دوسری) اُمتوں میں سے جو ان سے پہلے گزر چکیں عذاب کا وعدہ متحقق ہوگیا۔ بےشک وہ نقصان اٹھانے والے تھے');
INSERT INTO "jalandhari" VALUES(46,19,'46:19','اور لوگوں نے جیسے کام کئے ہوں گے ان کے مطابق سب کے درجے ہوں گے۔ غرض یہ ہے کہ ان کو ان کے اعمال کا پورا بدلہ دے اور ان کا نقصان نہ کیا جائے');
INSERT INTO "jalandhari" VALUES(46,20,'46:20','اور جس دن کافر دوزخ کے سامنے کئے جائیں گے (تو کہا جائے گا کہ) تم اپنی دنیا کی زندگی میں لذتیں حاصل کرچکے اور ان سے متمتع ہوچکے سو آج تم کو ذلت کا عذاب ہے، (یہ) اس کی سزا (ہے) کہ تم زمین میں ناحق غرور کیا کرتے تھے۔ اور اس کی بدکرداری کرتے تھے');
INSERT INTO "jalandhari" VALUES(46,21,'46:21','اور (قوم) عاد کے بھائی (ہود) کو یاد کرو کہ جب انہوں نے اپنی قوم کو سرزمین احقاف میں ہدایت کی اور ان سے پہلے اور پیچھے بھی ہدایت کرنے والے گزرچکے تھے کہ خدا کے سوا کسی کی عبادت نہ کرو۔ مجھے تمہارے بارے میں بڑے دن کے عذاب کا ڈر لگتا ہے');
INSERT INTO "jalandhari" VALUES(46,22,'46:22','کہنے لگے کیا تم ہمارے پاس اس لئے آئے ہو کہ ہم کو ہمارے معبودوں سے پھیر دو۔ اگر سچے ہو تو جس چیز سے ہمیں ڈراتے ہو اسے ہم پر لے آؤ');
INSERT INTO "jalandhari" VALUES(46,23,'46:23','(انہوں نے) کہا کہ (اس کا) علم تو خدا ہی کو ہے۔ اور میں تو جو (احکام)دے کر بھیجا گیا ہوں وہ تمہیں پہنچا رہا ہوں لیکن میں دیکھتا ہوں کہ تم لوگ نادانی میں پھنس رہے ہو');
INSERT INTO "jalandhari" VALUES(46,24,'46:24','پھر جب انہوں نے اس (عذاب کو) دیکھا کہ بادل (کی صورت میں) ان کے میدانوں کی طرف آرہا ہے تو کہنے لگے یہ تو بادل ہے جو ہم پر برس کر رہے گا۔ (نہیں) بلکہ (یہ) وہ چیز ہے جس کے لئے تم جلدی کرتے تھے یعنی آندھی جس میں درد دینے والا عذاب بھرا ہوا ہے');
INSERT INTO "jalandhari" VALUES(46,25,'46:25','ہر چیز کو اپنے پروردگار کے حکم سے تباہ کئے دیتی ہے تو وہ ایسے ہوگئے کہ ان کے گھروں کے سوا کچھ نظر ہی نہیں آتا تھا۔ گنہگار لوگوں کو ہم اسی طرح سزا دیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(46,26,'46:26','اور ہم نے ان کو ایسے مقدور دیئے تھے جو تم لوگوں کو نہیں دیئے اور انہیں کان اور آنکھیں اور دل دیئے تھے۔ تو جب کہ وہ خدا کی آیتوں سے انکار کرتے تھے تو نہ تو ان کے کان ہی ان کے کچھ کام آسکے اور نہ آنکھیں اور نہ دل۔ اور جس چیز سے استہزاء کیا کرتے تھے اس نے ان کو آ گھیرا');
INSERT INTO "jalandhari" VALUES(46,27,'46:27','اور تمہارے اردگرد کی بستیوں کو ہم نے ہلاک کر دیا۔ اور بار بار (اپنی) نشانیاں ظاہر کردیں تاکہ وہ رجوع کریں');
INSERT INTO "jalandhari" VALUES(46,28,'46:28','تو جن کو ان لوگوں نے تقرب (خدا) کے سوا معبود بنایا تھا انہوں نے ان کی کیوں مدد نہ کی۔ بلکہ وہ ان (کے سامنے) سے گم ہوگئے۔ اور یہ ان کا جھوٹ تھا اور یہی وہ افتراء کیا کرتے تھے');
INSERT INTO "jalandhari" VALUES(46,29,'46:29','اور جب ہم نے جنوں میں سے کئی شخص تمہاری طرف متوجہ کئے کہ قرآن سنیں۔ تو جب وہ اس کے پاس آئے تو (آپس میں) کہنے لگے کہ خاموش رہو۔ جب (پڑھنا) تمام ہوا تو اپنی برادری کے لوگوں میں واپس گئے کہ (ان کو) نصیحت کریں');
INSERT INTO "jalandhari" VALUES(46,30,'46:30','کہنے لگے کہ اے قوم! ہم نے ایک کتاب سنی ہے جو موسیٰ کے بعد نازل ہوئی ہے۔ جو (کتابیں) اس سے پہلے (نازل ہوئی) ہیں ان کی تصدیق کرتی ہے (اور) سچا (دین) اور سیدھا رستہ بتاتی ہے');
INSERT INTO "jalandhari" VALUES(46,31,'46:31','اے قوم! خدا کی طرف بلانے والے کی بات قبول کرو اور اس پر ایمان لاؤ۔ خدا تمہارے گناہ بخش دے گا اور تمہیں دکھ دینے والے عذاب سے پناہ میں رکھے گا');
INSERT INTO "jalandhari" VALUES(46,32,'46:32','اور جو شخص خدا کی طرف بلانے والے کی بات قبول نہ کرے گا تو وہ زمین میں (خدا کو) عاجز نہیں کرسکے گا اور نہ اس کے سوا اس کے حمایتی ہوں گے۔ یہ لوگ صریح گمراہی میں ہیں');
INSERT INTO "jalandhari" VALUES(46,33,'46:33','کیا انہوں نے نہیں سمجھا کہ جس خدا نے آسمانوں اور زمین کو پیدا کیا اور ان کے پیدا کرنے سے تھکا نہیں۔ وہ اس (بات) پر بھی قادر ہے کہ مردوں کو زندہ کر دے۔ ہاں ہاں وہ ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(46,34,'46:34','اور جس روز آگ کے سامنے کئے جائیں گے (اور کہا جائے گا) کیا یہ حق نہیں ہے؟ تو کہیں گے کیوں نہیں ہمارے پروردگار کی قسم (حق ہے) حکم ہوگا کہ تم جو (دنیا میں) انکار کیا کرتے تھے (اب) عذاب کے مزے چکھو');
INSERT INTO "jalandhari" VALUES(46,35,'46:35','پس (اے محمدصلى الله عليه وسلم) جس طرح اور عالی ہمت پیغمبر صبر کرتے رہے ہیں اسی طرح تم بھی صبر کرو اور ان کے لئے (عذاب) جلدی نہ مانگو۔ جس دن یہ اس چیز کو دیکھیں گے جس کا ان سے وعدہ کیا جاتا ہے تو (خیال کریں گے کہ) گویا (دنیا میں) رہے ہی نہ تھے مگر گھڑی بھر دن۔ (یہ قرآن) پیغام ہے۔ سو (اب) وہی ہلاک ہوں گے جو نافرمان تھے');
INSERT INTO "jalandhari" VALUES(47,1,'47:1','جن لوگوں نے کفر کیا اور (اَوروں کو) خدا کے رستے سے روکا۔ خدا نے ان کے اعمال برباد کر دیئے');
INSERT INTO "jalandhari" VALUES(47,2,'47:2','اور جو ایمان لائے اور نیک عمل کرتے رہے اور جو (کتاب) محمدصلى الله عليه وسلم پر نازل ہوئی اسے مانتے رہے اور وہ ان کے پروردگار کی طرف سے برحق ہے ان سے ان کے گناہ دور کردیئے اور ان کی حالت سنوار دی');
INSERT INTO "jalandhari" VALUES(47,3,'47:3','یہ (حبط اعمال اور اصلاح حال) اس لئے ہے کہ جن لوگوں نے کفر کیا انہوں نے جھوٹی بات کی پیروی کی اور جو ایمان لائے وہ اپنے پروردگار کی طرف سے (دین) حق کے پیچھے چلے۔ اسی طرح خدا لوگوں سے ان کے حالات بیان فرماتا ہے');
INSERT INTO "jalandhari" VALUES(47,4,'47:4','جب تم کافروں سے بھڑ جاؤ تو ان کی گردنیں اُڑا دو۔ یہاں تک کہ جب ان کو خوب قتل کرچکو تو (جو زندہ پکڑے جائیں ان کو) مضبوطی سے قید کرلو۔ پھر اس کے بعد یا تو احسان رکھ کر چھوڑ دینا چاہیئے یا کچھ مال لے کر یہاں تک کہ (فریق مقابل) لڑائی (کے) ہتھیار (ہاتھ سے) رکھ دے۔ (یہ حکم یاد رکھو) اور اگر خدا چاہتا تو (اور طرح) ان سے انتقام لے لیتا۔ لیکن اس نے چاہا کہ تمہاری آزمائش ایک (کو) دوسرے سے (لڑوا کر) کرے۔ اور جو لوگ خدا کی راہ میں مارے گئے ان کے عملوں کو ہرگز ضائع نہ کرے گا');
INSERT INTO "jalandhari" VALUES(47,5,'47:5','ان کو سیدھے رستے پر چلائے گا اور ان کی حالت درست کر دے گا');
INSERT INTO "jalandhari" VALUES(47,6,'47:6','اور ان کو بہشت میں جس سے انہیں شناسا کر رکھا ہے داخل کرے گا');
INSERT INTO "jalandhari" VALUES(47,7,'47:7','اے اہل ایمان! اگر تم خدا کی مدد کرو گے تو وہ بھی تمہاری مدد کرے گا اور تم کو ثابت قدم رکھے گا');
INSERT INTO "jalandhari" VALUES(47,8,'47:8','اور جو کافر ہیں ان کے لئے ہلاکت ہے۔ اور وہ ان کے اعمال کو برباد کر دے گا');
INSERT INTO "jalandhari" VALUES(47,9,'47:9','یہ اس لئے کہ خدا نے جو چیز نازل فرمائی انہوں نے اس کو ناپسند کیا تو خدا نے بھی ان کے اعمال اکارت کردیئے');
INSERT INTO "jalandhari" VALUES(47,10,'47:10','کیا انہوں نے ملک میں سیر نہیں کی تاکہ دیکھتے کہ جو لوگ ان سے پہلے تھے ان کا انجام کیسا ہوا؟ خدا نے ان پر تباہی ڈال دی۔ اور اسی طرح کا (عذاب) ان کافروں کو ہوگا');
INSERT INTO "jalandhari" VALUES(47,11,'47:11','یہ اس لئے کہ جو مومن ہیں ان کا خدا کارساز ہے اور کافروں کا کوئی کارساز نہیں');
INSERT INTO "jalandhari" VALUES(47,12,'47:12','جو لوگ ایمان لائے اور عمل نیک کرتے رہے ان کو خدا بہشتوں میں جن کے نیچے نہریں بہہ رہی ہیں داخل فرمائے گا۔ اور جو کافر ہیں وہ فائدے اٹھاتے ہیں اور (اس طرح) کھاتے ہیں جیسے حیوان کھاتے ہیں۔ اور ان کا ٹھکانہ دوزخ ہے');
INSERT INTO "jalandhari" VALUES(47,13,'47:13','اور بہت سی بستیاں تمہاری بستی سے جس (کے باشندوں نے تمہیں وہاں) سے نکال دیا زور وقوت میں کہیں بڑھ کر تھیں ہم نے ان کا ستیاناس کردیا اور ان کا کوئی مددگار نہ ہوا');
INSERT INTO "jalandhari" VALUES(47,14,'47:14','بھلا جو شخص اپنے پروردگار (کی مہربانی) سے کھلے رستے پر (چل رہا) ہو وہ ان کی طرح (ہوسکتا) ہے جن کے اعمال بد انہیں اچھے کرکے دکھائی جائیں اور جو اپنی خواہشوں کی پیروی کریں');
INSERT INTO "jalandhari" VALUES(47,15,'47:15','جنت جس کا پرہیزگاروں سے وعدہ کیا جاتا ہے۔ اس کی صفت یہ ہے کہ اس میں پانی کی نہریں ہیں جو بو نہیں کرے گا۔ اور دودھ کی نہریں ہیں جس کا مزہ نہیں بدلے گا۔ اور شراب کی نہریں ہیں جو پینے والوں کے لئے (سراسر) لذت ہے۔ اور شہد مصفا کی نہریں ہیں (جو حلاوت ہی حلاوت ہے) اور (وہاں) ان کے لئے ہر قسم کے میوے ہیں اور ان کے پروردگار کی طرف سے مغفرت ہے۔ (کیا یہ پرہیزگار) ان کی طرح (ہوسکتے) ہیں جو ہمیشہ دوزخ میں رہیں گے اور جن کو کھولتا ہوا پانی پلایا جائے گا تو ان کی انتڑیوں کو کاٹ ڈالے گا');
INSERT INTO "jalandhari" VALUES(47,16,'47:16','اور ان میں بعض ایسے بھی ہیں جو تمہاری طرف کان لگائے یہاں تک کہ (سب کچھ سنتے ہیں لیکن) جب تمہارے پاس سے نکل کر چلے جاتے ہیں تو جن لوگوں کو علم (دین) دیا گیا ہے ان سے کہتے ہیں کہ (بھلا) انہوں نے ابھی کیا کہا تھا؟ یہی لوگ ہیں جن کے دلوں پر خدا نے مہر لگا رکھی ہے اور وہ اپنی خواہشوں کے پیچھے چل رہے ہیں');
INSERT INTO "jalandhari" VALUES(47,17,'47:17','اور جو لوگ ہدایت یافتہ ہیں ان کو وہ ہدایت مزید بخشتا اور پرہیزگاری عنایت کرتا ہے');
INSERT INTO "jalandhari" VALUES(47,18,'47:18','اب تو یہ لوگ قیامت ہی کو دیکھ رہے ہیں کہ ناگہاں ان پر آ واقع ہو۔ سو اس کی نشانیاں (وقوع میں) آچکی ہیں۔ پھر جب وہ ان پر آ نازل ہوگی اس وقت انہیں نصیحت کہاں (مفید ہوسکے گی؟)');
INSERT INTO "jalandhari" VALUES(47,19,'47:19','پس جان رکھو کہ خدا کے سوا کوئی معبود نہیں اور اپنے گناہوں کی معافی مانگو اور (اور) مومن مردوں اور مومن عورتوں کے لئے بھی۔ اور خدا تم لوگوں کے چلنے پھرنے اور ٹھیرنے سے واقف ہے');
INSERT INTO "jalandhari" VALUES(47,20,'47:20','اور مومن لوگ کہتے ہیں کہ (جہاد کی) کوئی سورت کیوں نازل نہیں ہوتی؟ لیکن جب کوئی صاف معنوں کی سورت نازل ہو اور اس میں جہاد کا بیان ہو تو جن لوگوں کے دلوں میں (نفاق کا) مرض ہے تم ان کو دیکھو کہ تمہاری طرف اس طرح دیکھنے لگیں جس طرح کسی پر موت کی بےہوشی (طاری) ہو رہی ہو۔ سو ان کے لئے خرابی ہے');
INSERT INTO "jalandhari" VALUES(47,21,'47:21','(خوب کام تو) فرمانبرداری اور پسندیدہ بات کہنا (ہے) پھر جب (جہاد کی) بات پختہ ہوگئی تو اگر یہ لوگ خدا سے سچے رہنا چاہتے تو ان کے لئے بہت اچھا ہوتا');
INSERT INTO "jalandhari" VALUES(47,22,'47:22','(اے منافقو!) تم سے عجب نہیں کہ اگر تم حاکم ہو جاؤ تو ملک میں خرابی کرنے لگو اور اپنے رشتوں کو توڑ ڈالو');
INSERT INTO "jalandhari" VALUES(47,23,'47:23','یہی لوگ ہیں جن پر خدا نے لعنت کی ہے اور ان (کے کانوں) کو بہرا اور (ان کی) آنکھوں کو اندھا کردیا ہے');
INSERT INTO "jalandhari" VALUES(47,24,'47:24','بھلا یہ لوگ قرآن میں غور نہیں کرتے یا (ان کے) دلوں پر قفل لگ رہے ہیں');
INSERT INTO "jalandhari" VALUES(47,25,'47:25','جو لوگ راہ ہدایت ظاہر ہونے کے بعد پیٹھ دے کر پھر گئے۔ شیطان نے (یہ کام) ان کو مزین کر دکھایا اور انہیں طول (عمر کا وعدہ) دیا');
INSERT INTO "jalandhari" VALUES(47,26,'47:26','یہ اس لئے کہ جو لوگ خدا کی اُتاری ہوئی (کتاب) سے بیزار ہیں یہ ان سے کہتے ہیں کہ بعض کاموں میں ہم تمہاری بات بھی مانیں گے۔ اور خدا ان کے پوشیدہ مشوروں سے واقف ہے');
INSERT INTO "jalandhari" VALUES(47,27,'47:27','تو اُس وقت (ان کا) کیسا (حال) ہوگا جب فرشتے ان کی جان نکالیں گے اور ان کے مونہوں اور پیٹھوں پر مارتے جائیں گے');
INSERT INTO "jalandhari" VALUES(47,28,'47:28','یہ اس لئے کہ جس چیز سے خدا ناخوش ہے یہ اس کے پیچھے چلے اور اس کی خوشنودی کو اچھا نہ سمجھے تو اُس نے بھی ان کے عملوں کو برباد کر دیا');
INSERT INTO "jalandhari" VALUES(47,29,'47:29','کیا وہ لوگ جن کے دلوں میں بیماری ہے یہ خیال کئے ہوئے ہیں کہ خدا ان کے کینوں کو ظاہر نہیں کرے گا؟');
INSERT INTO "jalandhari" VALUES(47,30,'47:30','اور اگر ہم چاہتے تو وہ لوگ تم کو دکھا بھی دیتے اور تم ان کو ان کے چہروں ہی سے پہچان لیتے۔ اور تم انہیں (ان کے) انداز گفتگو ہی سے پہچان لو گے! اور خدا تمہارے اعمال سے واقف ہے');
INSERT INTO "jalandhari" VALUES(47,31,'47:31','اور ہم تو لوگوں کو آزمائیں گے تاکہ جو تم میں لڑائی کرنے والے اور ثابت قدم رہنے والے ہیں ان کو معلوم کریں۔ اور تمہارے حالات جانچ لیں');
INSERT INTO "jalandhari" VALUES(47,32,'47:32','جن لوگوں کو سیدھا رستہ معلوم ہوگیا (اور) پھر بھی انہوں نے کفر کیا اور (لوگوں کو) خدا کی راہ سے روکا اور پیغمبر کی مخالفت کی وہ خدا کا کچھ بھی بگاڑ نہیں سکیں گے۔ اور خدا ان کا سب کیا کرایا اکارت کردے گا');
INSERT INTO "jalandhari" VALUES(47,33,'47:33','مومنو! خدا کا ارشاد مانو اور پیغمبر کی فرمانبرداری کرو اور اپنے عملوں کو ضائع نہ ہونے دو');
INSERT INTO "jalandhari" VALUES(47,34,'47:34','جو لوگ کافر ہوئے اور خدا کے رستے سے روکتے رہے پھر کافر ہی مرگئے خدا ان کو ہرگز نہیں بخشے گا');
INSERT INTO "jalandhari" VALUES(47,35,'47:35','تو تم ہمت نہ ہارو اور (دشمنوں کو) صلح کی طرف نہ بلاؤ۔ اور تم تو غالب ہو۔ اور خدا تمہارے ساتھ ہے وہ ہرگز تمہارے اعمال کو کم (اور گم) نہیں کرے گا');
INSERT INTO "jalandhari" VALUES(47,36,'47:36','دنیا کی زندگی تو محض کھیل اور تماشا ہے۔ اور اگر تم ایمان لاؤ گے اور پرہیزگاری کرو گے تو وہ تم کو تمہارا اجر دے گا۔ اور تم سے تمہارا مال طلب نہیں کرے گا');
INSERT INTO "jalandhari" VALUES(47,37,'47:37','اگر وہ تم سے مال طلب کرے اور تمہیں تنگ کرے تو تم بخل کرنے لگو اور وہ (بخل) تمہاری بدنیتی ظاہر کرکے رہے');
INSERT INTO "jalandhari" VALUES(47,38,'47:38','دیکھو تم وہ لوگ ہو کہ خدا کی راہ میں خرچ کرنے کے لئے بلائے جاتے ہو۔ تو تم میں ایسے شخص بھی ہیں جو بخل کرنے لگتے ہیں۔ اور جو بخل کرتا ہے اپنے آپ سے بخل کرتا ہے۔ اور خدا بےنیاز ہے اور تم محتاج۔ اور اگر تم منہ پھیرو گے تو وہ تمہاری جگہ اور لوگوں کو لے آئے گا اور وہ تمہاری طرح کے نہیں ہوں گے');
INSERT INTO "jalandhari" VALUES(48,1,'48:1','(اے محمدصلى الله عليه وسلم) ہم نے تم کو فتح دی۔ فتح بھی صریح وصاف');
INSERT INTO "jalandhari" VALUES(48,2,'48:2','تاکہ خدا تمہارے اگلے اور پچھلے گناہ بخش دے اور تم پر اپنی نعمت پوری کردے اور تمہیں سیدھے رستے چلائے');
INSERT INTO "jalandhari" VALUES(48,3,'48:3','اور خدا تمہاری زبردست مدد کرے');
INSERT INTO "jalandhari" VALUES(48,4,'48:4','وہی تو ہے جس نے مومنوں کے دلوں پر تسلی نازل فرمائی تاکہ ان کے ایمان کے ساتھ اور ایمان بڑھے۔ اور آسمانوں اور زمین کے لشکر (سب) خدا ہی کے ہیں۔ اور خدا جاننے والا (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(48,5,'48:5','(یہ) اس لئے کہ وہ مومن مردوں اور مومن عورتوں کو بہشتوں میں جن کے نیچے نہریں بہہ رہی ہیں داخل کرے وہ اس میں ہمیشہ رہیں گے اور ان سے ان کے گناہوں کو دور کردے۔ اور یہ خدا کے نزدیک بڑی کامیابی ہے');
INSERT INTO "jalandhari" VALUES(48,6,'48:6','اور (اس لئے کہ) منافق مردوں اور منافق عورتوں اور مشرک مردوں اور مشرک عورتوں کو جو خدا کے حق میں برے برے خیال رکھتے ہیں عذاب دے۔ ان ہی پر برے حادثے واقع ہوں۔ اور خدا ان پر غصے ہوا اور ان پر لعنت کی اور ان کے لئے دوزخ تیار کی۔ اور وہ بری جگہ ہے');
INSERT INTO "jalandhari" VALUES(48,7,'48:7','اور آسمانوں اور زمین کے لشکر خدا ہی کے ہیں۔ اور خدا غالب (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(48,8,'48:8','اور ہم نے (اے محمدصلى الله عليه وسلم) تم کو حق ظاہر کرنے والا اور خوشخبری سنانے والا اور خوف دلانے والا (بنا کر) بھیجا ہے');
INSERT INTO "jalandhari" VALUES(48,9,'48:9','تاکہ (مسلمانو) تم لوگ خدا پر اور اس کے پیغمبر پر ایمان لاؤ اور اس کی مدد کرو اور اس کو بزرگ سمجھو۔ اور صبح وشام اس کی تسبیح کرتے رہو');
INSERT INTO "jalandhari" VALUES(48,10,'48:10','جو لوگ تم سے بیعت کرتے ہیں وہ خدا سے بیعت کرتے ہیں۔ خدا کا ہاتھ ان کے ہاتھوں پر ہے۔ پھر جو عہد کو توڑے تو عہد توڑنے کا نقصان اسی کو ہے۔ اور جو اس بات کو جس کا اس نے خدا سے عہد کیا ہے پورا کرے تو وہ اسے عنقریب اجر عظیم دے گا');
INSERT INTO "jalandhari" VALUES(48,11,'48:11','جو گنوار پیچھے رہ گئے وہ تم سے کہیں گے کہ ہم کو ہمارے مال اور اہل وعیال نے روک رکھا آپ ہمارے لئے (خدا سے) بخشش مانگیں۔ یہ لوگ اپنی زبان سے وہ بات کہتے ہیں جو ان کے دل میں نہیں ہے۔ کہہ دو کہ اگر خدا تم (لوگوں) کو نقصان پہنچانا چاہے یا تمہیں فائدہ پہنچانے کا ارادہ فرمائے تو کون ہے جو اس کے سامنے تمہارے لئے کسی بات کا کچھ اختیار رکھے (کوئی نہیں) بلکہ جو کچھ تم کرتے ہو خدا اس سے واقف ہے');
INSERT INTO "jalandhari" VALUES(48,12,'48:12','بات یہ ہے کہ تم لوگ یہ سمجھ بیٹھے تھے کہ پیغمبر اور مومن اپنے اہل وعیال میں کبھی لوٹ کر آنے ہی کے نہیں۔ اور یہی بات تمہارے دلوں کو اچھی معلوم ہوئی۔ اور (اسی وجہ سے) تم نے برے برے خیال کئے اور (آخرکار) تم ہلاکت میں پڑ گئے');
INSERT INTO "jalandhari" VALUES(48,13,'48:13','اور جو شخص خدا پر اور اس کے پیغمبر پر ایمان نہ لائے تو ہم نے (ایسے) کافروں کے لئے آگ تیار کر رکھی ہے');
INSERT INTO "jalandhari" VALUES(48,14,'48:14','اور آسمانوں اور زمین کی بادشاہی خدا ہی کی ہے۔ وہ جسے چاہے بخشے اور جسے چاہے سزا دے۔ اور خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(48,15,'48:15','جب تم لوگ غنیمتیں لینے چلو گے تو جو لوگ پیچھے رہ گئے تھے وہ کہیں گے ہمیں بھی اجازت دیجیئے کہ آپ کے ساتھ چلیں۔ یہ چاہتے ہیں کہ خدا کے قول کو بدل دیں۔ کہہ دو کہ تم ہرگز ہمارے ساتھ نہیں چل سکتے۔ اسی طرح خدا نے پہلے سے فرما دیا ہے۔ پھر کہیں گے (نہیں) تم تو ہم سے حسد کرتے ہو۔ بات یہ ہے کہ یہ لوگ سمجھتے ہی نہیں مگر بہت کم');
INSERT INTO "jalandhari" VALUES(48,16,'48:16','جو گنوار پیچھے رہ گئے تھے ان سے کہہ دو کہ تم ایک سخت جنگجو قوم کے (ساتھ لڑائی کے) لئے بلائے جاؤ گے ان سے تم (یا تو) جنگ کرتے رہو گے یا وہ اسلام لے آئیں گے۔ اگر تم حکم مانو گے تو خدا تم کو اچھا بدلہ دے گا۔ اور اگر منہ پھیر لو گے جیسے پہلی دفعہ پھیرا تھا تو وہ تم کو بری تکلیف کی سزا دے گا');
INSERT INTO "jalandhari" VALUES(48,17,'48:17','نہ تو اندھے پر گناہ ہے (کہ سفر جنگ سے پیچھے رہ جائے) اور نہ لنگڑے پر گناہ ہے اور نہ بیمار پر گناہ ہے۔ اور جو شخص خدا اور اس کے پیغمبر کے فرمان پر چلے گا خدا اس کو بہشتوں میں داخل کرے گا جن کے تلے نہریں بہہ رہی ہیں۔ اور جو روگردانی کرے گا اسے برے دکھ کی سزا دے گا');
INSERT INTO "jalandhari" VALUES(48,18,'48:18','(اے پیغمبر) جب مومن تم سے درخت کے نیچے بیعت کر رہے تھے تو خدا ان سے خوش ہوا۔ اور جو (صدق وخلوص) ان کے دلوں میں تھا وہ اس نے معلوم کرلیا۔ تو ان پر تسلی نازل فرمائی اور انہیں جلد فتح عنایت کی');
INSERT INTO "jalandhari" VALUES(48,19,'48:19','اور بہت سی غنیمتیں جو انہوں نے حاصل کیں۔ اور خدا غالب حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(48,20,'48:20','خدا نے تم سے بہت سی غنیمتوں کا وعدہ فرمایا کہ تم ان کو حاصل کرو گے سو اس نے غنیمت کی تمہارے لئے جلدی فرمائی اور لوگوں کے ہاتھ تم سے روک دیئے۔ غرض یہ تھی کہ یہ مومنوں کے لئے (خدا کی) قدرت کا نمونہ ہو اور وہ تم کو سیدھے رستے پر چلائے');
INSERT INTO "jalandhari" VALUES(48,21,'48:21','اور اَور (غنیمتیں دیں) جن پر تم قدرت نہیں رکھتے تھے (اور) وہ خدا ہی کی قدرت میں تھیں۔ اور خدا ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(48,22,'48:22','اور اگر تم سے کافر لڑتے تو پیٹھ پھیر کر بھاگ جاتے پھر کسی کو دوست نہ پاتے اور نہ مددگار');
INSERT INTO "jalandhari" VALUES(48,23,'48:23','(یہی) خدا کی عادت ہے جو پہلے سے چلی آتی ہے۔ اور تم خدا کی عادت کبھی بدلتی نہ دیکھو گے');
INSERT INTO "jalandhari" VALUES(48,24,'48:24','اور وہی تو ہے جس نے تم کو ان (کافروں) پر فتحیاب کرنے کے بعد سرحد مکہ میں ان کے ہاتھ تم سے اور تمہارے ہاتھ ان سے روک دیئے۔ اور جو کچھ تم کرتے ہو خدا اس کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(48,25,'48:25','یہ وہی لوگ ہیں جنہوں نے کفر کیا اور تم کو مسجد حرام سے روک دیا اور قربانیوں کو بھی کہ اپنی جگہ پہنچنے سے رکی رہیں۔ اور اگر ایسے مسلمان مرد اور مسلمان عورتیں نہ ہوتیں جن کو تم جانتے نہ تھے کہ اگر تم ان کو پامال کر دیتے تو تم کو ان کی طرف سے بےخبری میں نقصان پہنچ جاتا۔ (تو بھی تمہارے ہاتھ سے فتح ہوجاتی مگر تاخیر) اس لئے (ہوئی) کہ خدا اپنی رحمت میں جس کو چاہے داخل کرلے۔ اور اگر دونوں فریق الگ الگ ہوجاتے تو جو ان میں کافر تھے ان ہم دکھ دینے والا عذاب دیتے');
INSERT INTO "jalandhari" VALUES(48,26,'48:26','جب کافروں نے اپنے دلوں میں ضد کی اور ضد بھی جاہلیت کی۔ تو خدا نے اپنے پیغمبر اور مومنوں پر اپنی طرف سے تسکین نازل فرمائی اور ان کو پرہیزگاری کی بات پر قائم رکھا اور وہ اسی کے مستحق اور اہل تھے۔ اور خدا ہر چیز سے خبردار ہے');
INSERT INTO "jalandhari" VALUES(48,27,'48:27','بےشک خدا نے اپنے پیغمبر کو سچا (اور) صحیح خواب دکھایا۔ کہ تم خدا نے چاہا تو مسجد حرام میں اپنے سر منڈوا کر اور اپنے بال کتروا کر امن وامان سے داخل ہوگے۔ اور کسی طرح کا خوف نہ کرو گے۔ جو بات تم نہیں جانتے تھے اس کو معلوم تھی سو اس نے اس سے پہلے ہی جلد فتح کرادی');
INSERT INTO "jalandhari" VALUES(48,28,'48:28','وہی تو ہے جس نے اپنے پیغمبر کو ہدایت (کی کتاب) اور دین حق دے کر بھیجا تاکہ اس کو تمام دینوں پر غالب کرے۔ اور حق ظاہر کرنے کے لئے خدا ہی کافی ہے');
INSERT INTO "jalandhari" VALUES(48,29,'48:29','محمدصلى الله عليه وسلم خدا کے پیغمبر ہیں اور جو لوگ ان کے ساتھ ہیں وہ کافروں کے حق میں سخت ہیں اور آپس میں رحم دل، (اے دیکھنے والے) تو ان کو دیکھتا ہے کہ (خدا کے آگے) جھکے ہوئے سر بسجود ہیں اور خدا کا فضل اور اس کی خوشنودی طلب کر رہے ہیں۔ (کثرت) سجود کے اثر سے ان کی پیشانیوں پر نشان پڑے ہوئے ہیں۔ ان کے یہی اوصاف تورات میں (مرقوم) ہیں۔ اور یہی اوصاف انجیل میں ہیں۔ (وہ) گویا ایک کھیتی ہیں جس نے (پہلے زمین سے) اپنی سوئی نکالی پھر اس کو مضبوط کیا پھر موٹی ہوئی اور پھر اپنی نال پر سیدھی کھڑی ہوگئی اور لگی کھیتی والوں کو خوش کرنے تاکہ کافروں کا جی جلائے۔ جو لوگ ان میں سے ایمان لائے اور نیک عمل کرتے رہے ان سے خدا نے گناہوں کی بخشش اور اجر عظیم کا وعدہ کیا ہے');
INSERT INTO "jalandhari" VALUES(49,1,'49:1','مومنو! (کسی بات کے جواب میں) خدا اور اس کے رسول سے پہلے نہ بول اٹھا کرو اور خدا سے ڈرتے رہو۔ بےشک خدا سنتا جانتا ہے');
INSERT INTO "jalandhari" VALUES(49,2,'49:2','اے اہل ایمان! اپنی آوازیں پیغمبر کی آواز سے اونچی نہ کرو اور جس طرح آپس میں ایک دوسرے سے زور سے بولتے ہو (اس طرح) ان کے روبرو زور سے نہ بولا کرو (ایسا نہ ہو) کہ تمہارے اعمال ضائع ہوجائیں اور تم کو خبر بھی نہ ہو');
INSERT INTO "jalandhari" VALUES(49,3,'49:3','جو لوگ پیغمبر خدا کے سامنے دبی آواز سے بولتے ہیں خدا نے ان کے دل تقویٰ کے لئے آزما لئے ہیں۔ ان کے لئے بخشش اور اجر عظیم ہے');
INSERT INTO "jalandhari" VALUES(49,4,'49:4','جو لوگ تم کو حجروں کے باہر سے آواز دیتے ہیں ان میں اکثر بےعقل ہیں');
INSERT INTO "jalandhari" VALUES(49,5,'49:5','اور اگر وہ صبر کئے رہتے یہاں تک کہ تم خود نکل کر ان کے پاس آتے تو یہ ان کے لئے بہتر تھا۔ اور خدا تو بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(49,6,'49:6','مومنو! اگر کوئی بدکردار تمہارے پاس کوئی خبر لے کر آئے تو خوب تحقیق کرلیا کرو (مبادا) کہ کسی قوم کو نادانی سے نقصان پہنچا دو۔ پھر تم کو اپنے کئے پر نادم ہونا پڑے');
INSERT INTO "jalandhari" VALUES(49,7,'49:7','اور جان رکھو کہ تم میں خدا کے پیغمبرصلى الله عليه وسلم ہیں۔ اگر بہت سی باتوں میں وہ تمہارا کہا مان لیا کریں تو تم مشکل میں پڑ جاؤ لیکن خدا نے تم کو ایمان عزیز بنا دیا اور اس کو تمہارے دلوں میں سجا دیا اور کفر اور گناہ اور نافرمانی سے تم کو بیزار کردیا۔ یہی لوگ راہ ہدایت پر ہیں');
INSERT INTO "jalandhari" VALUES(49,8,'49:8','(یعنی) خدا کے فضل اور احسان سے۔ اور خدا جاننے والا (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(49,9,'49:9','اور اگر مومنوں میں سے کوئی دو فریق آپس میں لڑ پڑیں تو ان میں صلح کرا دو۔ اور اگر ایک فریق دوسرے پر زیادتی کرے تو زیادتی کرنے والے سے لڑو یہاں تک کہ وہ خدا کے حکم کی طرف رجوع لائے۔ پس جب وہ رجوع لائے تو وہ دونوں فریق میں مساوات کے ساتھ صلح کرا دو اور انصاف سے کام لو۔ کہ خدا انصاف کرنے والوں کو پسند کرتا ہے');
INSERT INTO "jalandhari" VALUES(49,10,'49:10','مومن تو آپس میں بھائی بھائی ہیں۔ تو اپنے دو بھائیوں میں صلح کرادیا کرو۔ اور خدا سے ڈرتے رہو تاکہ تم پر رحمت کی جائے');
INSERT INTO "jalandhari" VALUES(49,11,'49:11','مومنو! کوئی قوم کسی قوم سے تمسخر نہ کرے ممکن ہے کہ وہ لوگ ان سے بہتر ہوں اور نہ عورتیں عورتوں سے (تمسخر کریں) ممکن ہے کہ وہ ان سے اچھی ہوں۔ اور اپنے (مومن بھائی) کو عیب نہ لگاؤ اور نہ ایک دوسرے کا برا نام رکھو۔ ایمان لانے کے بعد برا نام (رکھنا) گناہ ہے۔ اور جو توبہ نہ کریں وہ ظالم ہیں');
INSERT INTO "jalandhari" VALUES(49,12,'49:12','اے اہل ایمان! بہت گمان کرنے سے احتراز کرو کہ بعض گمان گناہ ہیں۔ اور ایک دوسرے کے حال کا تجسس نہ کیا کرو اور نہ کوئی کسی کی غیبت کرے۔ کیا تم میں سے کوئی اس بات کو پسند کرے گا کہ اپنے مرے ہوئے بھائی کا گوشت کھائے؟ اس سے تو تم ضرور نفرت کرو گے۔ (تو غیبت نہ کرو) اور خدا کا ڈر رکھو بےشک خدا توبہ قبول کرنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(49,13,'49:13','لوگو! ہم نے تم کو ایک مرد اور ایک عورت سے پیدا کیا اور تمہاری قومیں اور قبیلے بنائے۔ تاکہ ایک دوسرے کو شناخت کرو۔ اور خدا کے نزدیک تم میں زیادہ عزت والا وہ ہے جو زیادہ پرہیزگار ہے۔ بےشک خدا سب کچھ جاننے والا (اور) سب سے خبردار ہے');
INSERT INTO "jalandhari" VALUES(49,14,'49:14','دیہاتی کہتے ہیں کہ ہم ایمان لے آئے۔ کہہ دو کہ تم ایمان نہیں لائے (بلکہ یوں) کہو کہ ہم اسلام لائے ہیں اور ایمان تو ہنوز تمہارے دلوں میں داخل ہی نہیں ہوا۔ اور تم خدا اور اس کے رسول کی فرمانبرداری کرو گے تو خدا تمہارے اعمال سے کچھ کم نہیں کرے گا۔ بےشک خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(49,15,'49:15','مومن تو وہ ہیں جو خدا اور اس کے رسول پر ایمان لائے پھر شک میں نہ پڑے اور خدا کی راہ میں مال اور جان سے لڑے۔ یہی لوگ (ایمان کے) سچے ہیں');
INSERT INTO "jalandhari" VALUES(49,16,'49:16','ان سے کہو کیا تم خدا کو اپنی دینداری جتلاتے ہو۔ اور خدا تو آسمانوں اور زمین کی سب چیزوں سے واقف ہے۔ اور خدا ہر شے کو جانتا ہے');
INSERT INTO "jalandhari" VALUES(49,17,'49:17','یہ لوگ تم پر احسان رکھتے ہیں کہ مسلمان ہوگئے ہیں۔ کہہ دو کہ اپنے مسلمان ہونے کا مجھ پر احسان نہ رکھو۔ بلکہ خدا تم پر احسان رکھتا ہے کہ اس نے تمہیں ایمان کا رستہ دکھایا بشرطیکہ تم سچے (مسلمان) ہو');
INSERT INTO "jalandhari" VALUES(49,18,'49:18','بےشک خدا آسمانوں اور زمین کی پوشیدہ باتوں کو جانتا ہے اور جو کچھ تم کرتے ہو اسے دیکھتا ہے');
INSERT INTO "jalandhari" VALUES(50,1,'50:1','قٓ۔ قرآن مجید کی قسم (کہ محمد پیغمبر خدا ہیں)');
INSERT INTO "jalandhari" VALUES(50,2,'50:2','لیکن ان لوگوں نے تعجب کیا کہ انہی میں سے ایک ہدایت کرنے والا ان کے پاس آیا تو کافر کہنے لگے کہ یہ بات تو (بڑی) عجیب ہے');
INSERT INTO "jalandhari" VALUES(50,3,'50:3','بھلا جب ہم مر گئے اور مٹی ہوگئے (تو پھر زندہ ہوں گے؟) یہ زندہ ہونا (عقل سے) بعید ہے');
INSERT INTO "jalandhari" VALUES(50,4,'50:4','ان کے جسموں کو زمین جتنا (کھا کھا کر) کم کرتی جاتی ہے ہمیں معلوم ہے۔ اور ہمارے پاس تحریری یادداشت بھی ہے');
INSERT INTO "jalandhari" VALUES(50,5,'50:5','بلکہ (عجیب بات یہ ہے کہ) جب ان کے پاس (دین) حق آ پہنچا تو انہوں نے اس کو جھوٹ سمجھا سو یہ ایک الجھی ہوئی بات میں (پڑ رہے) ہیں');
INSERT INTO "jalandhari" VALUES(50,6,'50:6','کیا انہوں نے اپنے اوپر آسمان کی طرف نگاہ نہیں کی کہ ہم نے اس کو کیونکر بنایا اور (کیونکر) سجایا اور اس میں کہیں شگاف تک نہیں');
INSERT INTO "jalandhari" VALUES(50,7,'50:7','اور زمین کو (دیکھو اسے) ہم نے پھیلایا اور اس میں پہاڑ رکھ دیئے اور اس میں ہر طرح کی خوشنما چیزیں اُگائیں');
INSERT INTO "jalandhari" VALUES(50,8,'50:8','تاکہ رجوع لانے والے بندے ہدایت اور نصیحت حاصل کریں');
INSERT INTO "jalandhari" VALUES(50,9,'50:9','اور آسمان سے برکت والا پانی اُتارا اور اس سے باغ وبستان اُگائے اور کھیتی کا اناج');
INSERT INTO "jalandhari" VALUES(50,10,'50:10','اور لمبی لمبی کھجوریں جن کا گابھا تہہ بہ تہہ ہوتا ہے');
INSERT INTO "jalandhari" VALUES(50,11,'50:11','(یہ سب کچھ) بندوں کو روزی دینے کے لئے (کیا ہے) اور اس (پانی) سے ہم نے شہر مردہ (یعنی زمین افتادہ) کو زندہ کیا۔ (بس) اسی طرح (قیامت کے روز) نکل پڑنا ہے');
INSERT INTO "jalandhari" VALUES(50,12,'50:12','ان سے پہلے نوح کی قوم اور کنوئیں والے اور ثمود جھٹلا چکے ہیں');
INSERT INTO "jalandhari" VALUES(50,13,'50:13','اور عاد اور فرعون اور لوط کے بھائی');
INSERT INTO "jalandhari" VALUES(50,14,'50:14','اور بن کے رہنے والے اور تُبّع کی قوم۔ (غرض) ان سب نے پیغمبروں کو جھٹلایا تو ہمارا وعید (عذاب) بھی پورا ہو کر رہا');
INSERT INTO "jalandhari" VALUES(50,15,'50:15','کیا ہم پہلی بار پیدا کرکے تھک گئے ہیں؟ (نہیں) بلکہ یہ ازسرنو پیدا کرنے میں شک میں (پڑے ہوئے) ہیں');
INSERT INTO "jalandhari" VALUES(50,16,'50:16','اور ہم ہی نے انسان کو پیدا کیا ہے اور جو خیالات اس کے دل میں گزرتے ہیں ہم ان کو جانتے ہیں۔ اور ہم اس کی رگ جان سے بھی اس سے زیادہ قریب ہیں');
INSERT INTO "jalandhari" VALUES(50,17,'50:17','جب (وہ کوئی کام کرتا ہے تو) دو لکھنے والے جو دائیں بائیں بیٹھے ہیں، لکھ لیتے ہیں');
INSERT INTO "jalandhari" VALUES(50,18,'50:18','کوئی بات اس کی زبان پر نہیں آتی مگر ایک نگہبان اس کے پاس تیار رہتا ہے');
INSERT INTO "jalandhari" VALUES(50,19,'50:19','اور موت کی بےہوشی حقیقت کھولنے کو طاری ہوگئی۔ (اے انسان) یہی (وہ حالت) ہے جس سے تو بھاگتا تھا');
INSERT INTO "jalandhari" VALUES(50,20,'50:20','اور صور پھونکا جائے گا۔ یہی (عذاب کے) وعید کا دن ہے');
INSERT INTO "jalandhari" VALUES(50,21,'50:21','اور ہر شخص (ہمارے سامنے) آئے گا۔ ایک (فرشتہ) اس کے ساتھ چلانے والا ہوگا اور ایک (اس کے عملوں کی) گواہی دینے والا');
INSERT INTO "jalandhari" VALUES(50,22,'50:22','(یہ وہ دن ہے کہ) اس سے تو غافل ہو رہا تھا۔ اب ہم نے تجھ پر سے پردہ اُٹھا دیا۔ تو آج تیری نگاہ تیز ہے');
INSERT INTO "jalandhari" VALUES(50,23,'50:23','اور اس کا ہم نشین (فرشتہ) کہے گا کہ یہ (اعمال نامہ) میرے پاس حاضر ہے');
INSERT INTO "jalandhari" VALUES(50,24,'50:24','(حکم ہوگا کہ) ہر سرکش ناشکرے کو دوزخ میں ڈال دو');
INSERT INTO "jalandhari" VALUES(50,25,'50:25','جو مال میں بخل کرنے والا حد سے بڑھنے والا شبہے نکالنے والا تھا');
INSERT INTO "jalandhari" VALUES(50,26,'50:26','جس نے خدا کے ساتھ اور معبود مقرر کر رکھے تھے۔ تو اس کو سخت عذاب میں ڈال دو');
INSERT INTO "jalandhari" VALUES(50,27,'50:27','اس کا ساتھی (شیطان) کہے گا کہ اے ہمارے پروردگار میں نے اس کو گمراہ نہیں کیا تھا بلکہ یہ آپ ہی رستے سے دور بھٹکا ہوا تھا');
INSERT INTO "jalandhari" VALUES(50,28,'50:28','(خدا) فرمائے گا کہ ہمارے حضور میں ردوکد نہ کرو۔ ہم تمہارے پاس پہلے ہی (عذاب کی) وعید بھیج چکے تھے');
INSERT INTO "jalandhari" VALUES(50,29,'50:29','ہمارے ہاں بات بدلا نہیں کرتی اور ہم بندوں پر ظلم نہیں کیا کرتے');
INSERT INTO "jalandhari" VALUES(50,30,'50:30','اس دن ہم دوزخ سے پوچھیں گے کہ کیا تو بھر گئی؟ وہ کہے گی کہ کچھ اور بھی ہے؟');
INSERT INTO "jalandhari" VALUES(50,31,'50:31','اور بہشت پرہیزگاروں کے قریب کردی جائے گی (کہ مطلق) دور نہ ہوگی');
INSERT INTO "jalandhari" VALUES(50,32,'50:32','یہی وہ چیز ہے جس کا تم سے وعدہ کیا جاتا تھا (یعنی) ہر رجوع لانے والے حفاظت کرنے والے سے');
INSERT INTO "jalandhari" VALUES(50,33,'50:33','جو خدا سے بن دیکھے ڈرتا ہے اور رجوع لانے والا دل لے کر آیا');
INSERT INTO "jalandhari" VALUES(50,34,'50:34','اس میں سلامتی کے ساتھ داخل ہوجاؤ۔ یہ ہمیشہ رہنے کا دن ہے');
INSERT INTO "jalandhari" VALUES(50,35,'50:35','وہاں وہ جو چاہیں گے ان کے لئے حاضر ہے اور ہمارے ہاں اور بھی (بہت کچھ) ہے');
INSERT INTO "jalandhari" VALUES(50,36,'50:36','اور ہم نے ان سے پہلے کئی اُمتیں ہلاک کر ڈالیں۔ وہ ان سے قوت میں کہیں بڑھ کر تھے وہ شہروں میں گشت کرنے لگے۔ کیا کہیں بھاگنے کی جگہ ہے؟');
INSERT INTO "jalandhari" VALUES(50,37,'50:37','جو شخص دل (آگاہ) رکھتا ہے یا دل سے متوجہ ہو کر سنتا ہے اس کے لئے اس میں نصیحت ہے');
INSERT INTO "jalandhari" VALUES(50,38,'50:38','اور ہم نے آسمانوں اور زمین کو اور جو (مخلوقات) ان میں ہے سب کو چھ دن میں بنا دیا۔ اور ہم کو ذرا تکان نہیں ہوئی');
INSERT INTO "jalandhari" VALUES(50,39,'50:39','تو جو کچھ یہ (کفار) بکتے ہیں اس پر صبر کرو اور آفتاب کے طلوع ہونے سے پہلے اور اس کے غروب ہونے سے پہلے اپنے پروردگار کی تعریف کے ساتھ تسبیح کرتے رہو');
INSERT INTO "jalandhari" VALUES(50,40,'50:40','اور رات کے بعض اوقات میں بھی اور نماز کے بعد بھی اس (کے نام) کی تنزیہ کیا کرو');
INSERT INTO "jalandhari" VALUES(50,41,'50:41','اور سنو جس دن پکارنے والا نزدیک کی جگہ سے پکارے گا');
INSERT INTO "jalandhari" VALUES(50,42,'50:42','جس دن لوگ چیخ یقیناً سن لیں گے۔ وہی نکل پڑنے کا دن ہے');
INSERT INTO "jalandhari" VALUES(50,43,'50:43','ہم ہی تو زندہ کرتے ہیں اور ہم ہی مارتے ہیں اور ہمارے ہی پاس لوٹ کر آنا ہے');
INSERT INTO "jalandhari" VALUES(50,44,'50:44','اس دن زمین ان پر سے پھٹ جائے گی اور وہ جھٹ پٹ نکل کھڑے ہوں گے۔ یہ جمع کرنا ہمیں آسان ہے');
INSERT INTO "jalandhari" VALUES(50,45,'50:45','یہ لوگ جو کچھ کہتے ہیں ہمیں خوب معلوم ہے اور تم ان پر زبردستی کرنے والے نہیں ہو۔ پس جو ہمارے (عذاب کی) وعید سے ڈرے اس کو قرآن سے نصیحت کرتے رہو');
INSERT INTO "jalandhari" VALUES(51,1,'51:1','بکھیرنے والیوں کی قسم جو اُڑا کر بکھیر دیتی ہیں');
INSERT INTO "jalandhari" VALUES(51,2,'51:2','پھر (پانی کا) بوجھ اٹھاتی ہیں');
INSERT INTO "jalandhari" VALUES(51,3,'51:3','پھر آہستہ آہستہ چلتی ہیں');
INSERT INTO "jalandhari" VALUES(51,4,'51:4','پھر چیزیں تقسیم کرتی ہیں');
INSERT INTO "jalandhari" VALUES(51,5,'51:5','کہ جس چیز کا تم سے وعدہ کیا جاتا ہے وہ سچا ہے');
INSERT INTO "jalandhari" VALUES(51,6,'51:6','اور انصاف (کا دن) ضرور واقع ہوگا');
INSERT INTO "jalandhari" VALUES(51,7,'51:7','اور آسمان کی قسم جس میں رسے ہیں');
INSERT INTO "jalandhari" VALUES(51,8,'51:8','کہ (اے اہل مکہ) تم ایک متناقض بات میں (پڑے ہوئے) ہو');
INSERT INTO "jalandhari" VALUES(51,9,'51:9','اس سے وہی پھرتا ہے جو (خدا کی طرف سے) پھیرا جائے');
INSERT INTO "jalandhari" VALUES(51,10,'51:10','اٹکل دوڑانے والے ہلاک ہوں');
INSERT INTO "jalandhari" VALUES(51,11,'51:11','جو بےخبری میں بھولے ہوئے ہیں');
INSERT INTO "jalandhari" VALUES(51,12,'51:12','پوچھتے ہیں کہ جزا کا دن کب ہوگا؟');
INSERT INTO "jalandhari" VALUES(51,13,'51:13','اُس دن (ہوگا) جب ان کو آگ میں عذاب دیا جائے گا');
INSERT INTO "jalandhari" VALUES(51,14,'51:14','اب اپنی شرارت کا مزہ چکھو۔ یہ وہی ہے جس کے لئے تم جلدی مچایا کرتے تھے');
INSERT INTO "jalandhari" VALUES(51,15,'51:15','بےشک پرہیزگار بہشتوں اور چشموں میں (عیش کر رہے) ہوں گے');
INSERT INTO "jalandhari" VALUES(51,16,'51:16','اور) جو جو (نعمتیں) ان کا پروردگار انہیں دیتا ہوگا ان کو لے رہے ہوں گے۔ بےشک وہ اس سے پہلے نیکیاں کرتے تھے');
INSERT INTO "jalandhari" VALUES(51,17,'51:17','رات کے تھوڑے سے حصے میں سوتے تھے');
INSERT INTO "jalandhari" VALUES(51,18,'51:18','اور اوقات سحر میں بخشش مانگا کرتے تھے');
INSERT INTO "jalandhari" VALUES(51,19,'51:19','اور ان کے مال میں مانگنے والے اور نہ مانگنے والے (دونوں) کا حق ہوتا تھا');
INSERT INTO "jalandhari" VALUES(51,20,'51:20','اور یقین کرنے والوں کے لئے زمین میں (بہت سی) نشانیاں ہیں');
INSERT INTO "jalandhari" VALUES(51,21,'51:21','اور خود تمہارے نفوس میں تو کیا تم دیکھتے نہیں؟');
INSERT INTO "jalandhari" VALUES(51,22,'51:22','اور تمہارا رزق اور جس چیز کا تم سے وعدہ کیا جاتا ہے آسمان میں ہے');
INSERT INTO "jalandhari" VALUES(51,23,'51:23','تو آسمانوں اور زمین کے مالک کی قسم! یہ (اسی طرح) قابل یقین ہے جس طرح تم بات کرتے ہو');
INSERT INTO "jalandhari" VALUES(51,24,'51:24','بھلا تمہارے پاس ابراہیمؑ کے معزز مہمانوں کی خبر پہنچی ہے؟');
INSERT INTO "jalandhari" VALUES(51,25,'51:25','جب وہ ان کے پاس آئے تو سلام کہا۔ انہوں نے بھی (جواب میں) سلام کہا (دیکھا تو) ایسے لوگ کہ نہ جان نہ پہچان');
INSERT INTO "jalandhari" VALUES(51,26,'51:26','تو اپنے گھر جا کر ایک (بھنا ہوا) موٹا بچھڑا لائے');
INSERT INTO "jalandhari" VALUES(51,27,'51:27','(اور کھانے کے لئے) ان کے آگے رکھ دیا۔ کہنے لگے کہ آپ تناول کیوں نہیں کرتے؟');
INSERT INTO "jalandhari" VALUES(51,28,'51:28','اور دل میں ان سے خوف معلوم کیا۔ (انہوں نے) کہا کہ خوف نہ کیجیئے۔ اور ان کو ایک دانشمند لڑکے کی بشارت بھی سنائی');
INSERT INTO "jalandhari" VALUES(51,29,'51:29','تو ابراہیمؑ کی بیوی چلاّتی آئی اور اپنا منہ پیٹ کر کہنے لگی کہ (اے ہے ایک تو) بڑھیا اور (دوسرے) بانجھ');
INSERT INTO "jalandhari" VALUES(51,30,'51:30','(انہوں نے) کہا (ہاں) تمہارے پروردگار نے یوں ہی فرمایا ہے۔ وہ بےشک صاحبِ حکمت (اور) خبردار ہے');
INSERT INTO "jalandhari" VALUES(51,31,'51:31','ابراہیمؑ نے کہا کہ فرشتو! تمہارا مدعا کیا ہے؟');
INSERT INTO "jalandhari" VALUES(51,32,'51:32','انہوں نے کہا کہ ہم گنہگار لوگوں کی طرف بھیجے گئے ہیں');
INSERT INTO "jalandhari" VALUES(51,33,'51:33','تاکہ ان پر کھنگر برسائیں');
INSERT INTO "jalandhari" VALUES(51,34,'51:34','جن پر حد سے بڑھ جانے والوں کے لئے تمہارے پروردگار کے ہاں سے نشان کردیئے گئے ہیں');
INSERT INTO "jalandhari" VALUES(51,35,'51:35','تو وہاں جتنے مومن تھے ان کو ہم نے نکال لیا');
INSERT INTO "jalandhari" VALUES(51,36,'51:36','اور اس میں ایک گھر کے سوا مسلمانوں کا کوئی گھر نہ پایا');
INSERT INTO "jalandhari" VALUES(51,37,'51:37','اور جو لوگ عذاب الیم سے ڈرتے ہیں ان کے لئے وہاں نشانی چھوڑ دی');
INSERT INTO "jalandhari" VALUES(51,38,'51:38','اور موسیٰ (کے حال) میں (بھی نشانی ہے) جب ہم نے ان کو فرعون کی طرف کھلا ہوا معجزہ دے کر بھیجا');
INSERT INTO "jalandhari" VALUES(51,39,'51:39','تو اس نے اپنی جماعت (کے گھمنڈ) پر منہ موڑ لیا اور کہنے لگا یہ تو جادوگر ہے یا دیوانہ');
INSERT INTO "jalandhari" VALUES(51,40,'51:40','تو ہم نے اس کو اور اس کے لشکروں کو پکڑ لیا اور ان کو دریا میں پھینک دیا اور وہ کام ہی قابل ملامت کرتا تھا');
INSERT INTO "jalandhari" VALUES(51,41,'51:41','اور عاد (کی قوم کے حال) میں بھی (نشانی ہے) جب ہم نے ان پر نامبارک ہوا چلائی');
INSERT INTO "jalandhari" VALUES(51,42,'51:42','وہ جس چیز پر چلتی اس کو ریزہ ریزہ کئے بغیر نہ چھوڑتی');
INSERT INTO "jalandhari" VALUES(51,43,'51:43','اور (قوم) ثمود (کے حال) میں (نشانی ہے) جب ان سے کہا گیا کہ ایک وقت تک فائدہ اٹھالو');
INSERT INTO "jalandhari" VALUES(51,44,'51:44','تو انہوں نے اپنے پروردگار کے حکم سے سرکشی کی۔ سو ان کو کڑک نے آ پکڑا اور وہ دیکھ رہے تھے');
INSERT INTO "jalandhari" VALUES(51,45,'51:45','پھر وہ نہ تو اُٹھنے کی طاقت رکھتے تھے اور نہ مقابلہ ہی کرسکتے تھے');
INSERT INTO "jalandhari" VALUES(51,46,'51:46','اور اس سے پہلے (ہم) نوح کی قوم کو (ہلاک کرچکے تھے) بےشک وہ نافرمان لوگ تھے');
INSERT INTO "jalandhari" VALUES(51,47,'51:47','اور آسمانوں کو ہم ہی نے ہاتھوں سے بنایا اور ہم کو سب مقدور ہے');
INSERT INTO "jalandhari" VALUES(51,48,'51:48','اور زمین کو ہم ہی نے بچھایا تو (دیکھو) ہم کیا خوب بچھانے والے ہیں');
INSERT INTO "jalandhari" VALUES(51,49,'51:49','اور ہر چیز کی ہم نے دو قسمیں بنائیں تاکہ تم نصیحت پکڑو');
INSERT INTO "jalandhari" VALUES(51,50,'51:50','تو تم لوگ خدا کی طرف بھاگ چلو میں اس کی طرف سے تم کو صریح رستہ بتانے والا ہوں');
INSERT INTO "jalandhari" VALUES(51,51,'51:51','اور خدا کے ساتھ کسی اَور کو معبود نہ بناؤ۔ میں اس کی طرف سے تم کو صریح رستہ بتانے والا ہوں');
INSERT INTO "jalandhari" VALUES(51,52,'51:52','اسی طرح ان سے پہلے لوگوں کے پاس جو پیغمبر آتا وہ اس کو جادوگر یا دیوانہ کہتے');
INSERT INTO "jalandhari" VALUES(51,53,'51:53','کیا یہ کہ ایک دوسرے کو اسی بات کی وصیت کرتے آئے ہیں بلکہ یہ شریر لوگ ہیں');
INSERT INTO "jalandhari" VALUES(51,54,'51:54','تو ان سے اعراض کرو۔ تم کو (ہماری) طرف سے ملامت نہ ہوگی');
INSERT INTO "jalandhari" VALUES(51,55,'51:55','اور نصیحت کرتے رہو کہ نصیحت مومنوں کو نفع دیتی ہے');
INSERT INTO "jalandhari" VALUES(51,56,'51:56','اور میں نے جنوں اور انسانوں کو اس لئے پیدا کیا ہے کہ میری عبادت کریں');
INSERT INTO "jalandhari" VALUES(51,57,'51:57','میں ان سے طالب رزق نہیں اور نہ یہ چاہتا ہوں کہ مجھے (کھانا) کھلائیں');
INSERT INTO "jalandhari" VALUES(51,58,'51:58','خدا ہی تو رزق دینے والا زور آور اور مضبوط ہے');
INSERT INTO "jalandhari" VALUES(51,59,'51:59','کچھ شک نہیں کہ ان ظالموں کے لئے بھی (عذاب کی) نوبت مقرر ہے جس طرح ان کے ساتھیوں کی نوبت تھی تو ان کو مجھ سے (عذاب) جلدی نہیں طلب کرنا چاہیئے');
INSERT INTO "jalandhari" VALUES(51,60,'51:60','جس دن کا ان کافروں سے وعدہ کیا جاتا ہے اس سے ان کے لئے خرابی ہے');
INSERT INTO "jalandhari" VALUES(52,1,'52:1','(کوہ) طور کی قسم');
INSERT INTO "jalandhari" VALUES(52,2,'52:2','اور کتاب کی جو لکھی ہوئی ہے');
INSERT INTO "jalandhari" VALUES(52,3,'52:3','کشادہ اوراق میں');
INSERT INTO "jalandhari" VALUES(52,4,'52:4','اور آباد گھر کی');
INSERT INTO "jalandhari" VALUES(52,5,'52:5','اور اونچی چھت کی');
INSERT INTO "jalandhari" VALUES(52,6,'52:6','اور ابلتے ہوئے دریا کی');
INSERT INTO "jalandhari" VALUES(52,7,'52:7','کہ تمہارے پروردگار کا عذاب واقع ہو کر رہے گا');
INSERT INTO "jalandhari" VALUES(52,8,'52:8','(اور) اس کو کوئی روک نہیں سکے گا');
INSERT INTO "jalandhari" VALUES(52,9,'52:9','جس دن آسمان لرزنے لگا کپکپا کر');
INSERT INTO "jalandhari" VALUES(52,10,'52:10','اور پہاڑ اُڑنے لگے اون ہو کر');
INSERT INTO "jalandhari" VALUES(52,11,'52:11','اس دن جھٹلانے والوں کے لئے خرابی ہے');
INSERT INTO "jalandhari" VALUES(52,12,'52:12','جو خوض (باطل) میں پڑے کھیل رہے ہیں');
INSERT INTO "jalandhari" VALUES(52,13,'52:13','جس دن ان کو آتش جہنم کی طرف دھکیل دھکیل کر لے جائیں گے');
INSERT INTO "jalandhari" VALUES(52,14,'52:14','یہی وہ جہنم ہے جس کو تم جھوٹ سمجھتے تھے');
INSERT INTO "jalandhari" VALUES(52,15,'52:15','تو کیا یہ جادو ہے یا تم کو نظر ہی نہیں آتا');
INSERT INTO "jalandhari" VALUES(52,16,'52:16','اس میں داخل ہوجاؤ اور صبر کرو یا نہ کرو تمہارے لئے یکساں ہے۔ جو کام تم کیا کرتے تھے (یہ) انہی کا تم کو بدلہ مل رہا ہے');
INSERT INTO "jalandhari" VALUES(52,17,'52:17','جو پرہیزگار ہیں وہ باغوں اور نعتموں میں ہوں گے');
INSERT INTO "jalandhari" VALUES(52,18,'52:18','جو کچھ ان کے پروردگار نے ان کو بخشا اس (کی وجہ) سے خوشحال۔ اور ان کے پروردگار نے ان کو دوزخ کے عذاب سے بچا لیا');
INSERT INTO "jalandhari" VALUES(52,19,'52:19','اپنے اعمال کے صلے میں مزے سے کھاؤ اور پیو');
INSERT INTO "jalandhari" VALUES(52,20,'52:20','تختوں پر جو برابر برابر بچھے ہوئے ہیں تکیہ لگائے ہوئے اور بڑی بڑی آنکھوں والی حوروں سے ہم ان کا عقد کر دیں گے');
INSERT INTO "jalandhari" VALUES(52,21,'52:21','اور جو لوگ ایمان لائے اور ان کی اولاد بھی (راہ) ایمان میں ان کے پیچھے چلی۔ ہم ان کی اولاد کو بھی ان (کے درجے) تک پہنچا دیں گے اور ان کے اعمال میں سے کچھ کم نہ کریں گے۔ ہر شخص اپنے اعمال میں پھنسا ہوا ہے');
INSERT INTO "jalandhari" VALUES(52,22,'52:22','اور جس طرح کے میوے اور گوشت کو ان کا جی چاہے گا ہم ان کو عطا کریں گے');
INSERT INTO "jalandhari" VALUES(52,23,'52:23','وہاں وہ ایک دوسرے سے جام شراب جھپٹ لیا کریں گے جس (کے پینے) سے نہ ہذیان سرائی ہوگی نہ کوئی گناہ کی بات');
INSERT INTO "jalandhari" VALUES(52,24,'52:24','اور نوجوان خدمت گار (جو ایسے ہوں گے) جیسے چھپائے ہوئے موتی ان کے آس پاس پھریں گے');
INSERT INTO "jalandhari" VALUES(52,25,'52:25','اور ایک دوسرے کی طرف رخ کرکے آپس میں گفتگو کریں گے');
INSERT INTO "jalandhari" VALUES(52,26,'52:26','کہیں گے کہ اس سے پہلے ہم اپنے گھر میں (خدا سے) ڈرتے رہتے تھے');
INSERT INTO "jalandhari" VALUES(52,27,'52:27','تو خدا نے ہم پر احسان فرمایا اور ہمیں لو کے عذاب سے بچا لیا');
INSERT INTO "jalandhari" VALUES(52,28,'52:28','اس سے پہلے ہم اس سے دعائیں کیا کرتے تھے۔ بےشک وہ احسان کرنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(52,29,'52:29','تو (اے پیغمبر) تم نصیحت کرتے رہو تم اپنے پروردگار کے فضل سے نہ تو کاہن ہو اور نہ دیوانے');
INSERT INTO "jalandhari" VALUES(52,30,'52:30','کیا کافر کہتے ہیں کہ یہ شاعر ہے (اور) ہم اس کے حق میں زمانے کے حوادث کا انتظار کر رہے ہیں');
INSERT INTO "jalandhari" VALUES(52,31,'52:31','کہہ دو کہ انتظار کئے جاؤ میں بھی تمہارے ساتھ انتظار کرتا ہوں');
INSERT INTO "jalandhari" VALUES(52,32,'52:32','کیا ان کی عقلیں ان کو یہی سکھاتی ہیں۔ بلکہ یہ لوگ ہیں ہی شریر');
INSERT INTO "jalandhari" VALUES(52,33,'52:33','کیا (کفار) کہتے ہیں کہ ان پیغمبر نے قرآن از خود بنا لیا ہے بات یہ ہے کہ یہ (خدا پر) ایمان نہیں رکھتے');
INSERT INTO "jalandhari" VALUES(52,34,'52:34','اگر یہ سچے ہیں تو ایسا کلام بنا تو لائیں');
INSERT INTO "jalandhari" VALUES(52,35,'52:35','کیا یہ کسی کے پیدا کئے بغیر ہی پیدا ہوگئے ہیں۔ یا یہ خود (اپنے تئیں) پیدا کرنے والے ہیں');
INSERT INTO "jalandhari" VALUES(52,36,'52:36','یا انہوں نے آسمانوں اور زمین کو پیدا کیا ہے؟ (نہیں) بلکہ یہ یقین ہی نہیں رکھتے');
INSERT INTO "jalandhari" VALUES(52,37,'52:37','کیا ان کے پاس تمہارے پروردگار کے خزانے ہیں۔ یا یہ (کہیں کے) داروغہ ہیں؟');
INSERT INTO "jalandhari" VALUES(52,38,'52:38','یا ان کے پاس کوئی سیڑھی ہے جس پر (چڑھ کر آسمان سے باتیں) سن آتے ہیں۔ تو جو سن آتا ہے وہ صریح سند دکھائے');
INSERT INTO "jalandhari" VALUES(52,39,'52:39','کیا خدا کی تو بیٹیاں اور تمہارے بیٹے');
INSERT INTO "jalandhari" VALUES(52,40,'52:40','(اے پیغمبر) کیا تم ان سے صلہ مانگتے ہو کہ ان پر تاوان کا بوجھ پڑ رہا ہے');
INSERT INTO "jalandhari" VALUES(52,41,'52:41','یا ان کے پاس غیب (کا علم) ہے کہ وہ اسے لکھ لیتے ہیں');
INSERT INTO "jalandhari" VALUES(52,42,'52:42','کیا یہ کوئی داؤں کرنا چاہتے ہیں تو کافر تو خود داؤں میں آنے والے ہیں');
INSERT INTO "jalandhari" VALUES(52,43,'52:43','کیا خدا کے سوا ان کا کوئی اور معبود ہے؟ خدا ان کے شریک بنانے سے پاک ہے');
INSERT INTO "jalandhari" VALUES(52,44,'52:44','اور اگر یہ آسمان سے (عذاب) کا کوئی ٹکڑا گرتا ہوا دیکھیں تو کہیں کہ یہ گاڑھا بادل ہے');
INSERT INTO "jalandhari" VALUES(52,45,'52:45','پس ان کو چھوڑ دو یہاں تک کہ وہ روز جس میں وہ بےہوش کردیئے جائیں گے، سامنے آجائے');
INSERT INTO "jalandhari" VALUES(52,46,'52:46','جس دن ان کا کوئی داؤں کچھ بھی کام نہ آئے اور نہ ان کو (کہیں سے) مدد ہی ملے');
INSERT INTO "jalandhari" VALUES(52,47,'52:47','اور ظالموں کے لئے اس کے سوا اور عذاب بھی ہے لیکن ان میں کے اکثر نہیں جانتے');
INSERT INTO "jalandhari" VALUES(52,48,'52:48','اور تم اپنے پروردگار کے حکم کے انتظار میں صبر کئے رہو۔ تم تو ہماری آنکھوں کے سامنے ہو اور جب اُٹھا کرو تو اپنے پروردگار کی تعریف کے ساتھ تسبیح کیا کرو');
INSERT INTO "jalandhari" VALUES(52,49,'52:49','اور رات کے بعض اوقات میں بھی اور ستاروں کے غروب ہونے کے بعد بھی اس کی تنزیہ کیا کرو');
INSERT INTO "jalandhari" VALUES(53,1,'53:1','تارے کی قسم جب غائب ہونے لگے');
INSERT INTO "jalandhari" VALUES(53,2,'53:2','کہ تمہارے رفیق (محمدصلى الله عليه وسلم) نہ رستہ بھولے ہیں نہ بھٹکے ہیں');
INSERT INTO "jalandhari" VALUES(53,3,'53:3','اور نہ خواہش نفس سے منہ سے بات نکالتے ہیں');
INSERT INTO "jalandhari" VALUES(53,4,'53:4','یہ (قرآن) تو حکم خدا ہے جو (ان کی طرف) بھیجا جاتا ہے');
INSERT INTO "jalandhari" VALUES(53,5,'53:5','ان کو نہایت قوت والے نے سکھایا');
INSERT INTO "jalandhari" VALUES(53,6,'53:6','(یعنی جبرائیل) طاقتور نے پھر وہ پورے نظر آئے');
INSERT INTO "jalandhari" VALUES(53,7,'53:7','اور وہ (آسمان کے) اونچے کنارے میں تھے');
INSERT INTO "jalandhari" VALUES(53,8,'53:8','پھر قریب ہوئے اوراَور آگے بڑھے');
INSERT INTO "jalandhari" VALUES(53,9,'53:9','تو دو کمان کے فاصلے پر یا اس سے بھی کم');
INSERT INTO "jalandhari" VALUES(53,10,'53:10','پھر خدا نے اپنے بندے کی طرف جو بھیجا سو بھیجا');
INSERT INTO "jalandhari" VALUES(53,11,'53:11','جو کچھ انہوں نے دیکھا ان کے دل نے اس کو جھوٹ نہ مانا');
INSERT INTO "jalandhari" VALUES(53,12,'53:12','کیا جو کچھ وہ دیکھتے ہیں تم اس میں ان سے جھگڑتے ہو؟');
INSERT INTO "jalandhari" VALUES(53,13,'53:13','اور انہوں نے اس کو ایک بار بھی دیکھا ہے');
INSERT INTO "jalandhari" VALUES(53,14,'53:14','پرلی حد کی بیری کے پاس');
INSERT INTO "jalandhari" VALUES(53,15,'53:15','اسی کے پاس رہنے کی جنت ہے');
INSERT INTO "jalandhari" VALUES(53,16,'53:16','جب کہ اس بیری پر چھا رہا تھا جو چھا رہا تھا');
INSERT INTO "jalandhari" VALUES(53,17,'53:17','ان کی آنکھ نہ تو اور طرف مائل ہوئی اور نہ (حد سے) آگے بڑھی');
INSERT INTO "jalandhari" VALUES(53,18,'53:18','انہوں نے اپنے پروردگار (کی قدرت) کی کتنی ہی بڑی بڑی نشانیاں دیکھیں');
INSERT INTO "jalandhari" VALUES(53,19,'53:19','بھلا تم لوگوں نے لات اور عزیٰ کو دیکھا');
INSERT INTO "jalandhari" VALUES(53,20,'53:20','اور تیسرے منات کو (کہ یہ بت کہیں خدا ہوسکتے ہیں)');
INSERT INTO "jalandhari" VALUES(53,21,'53:21','(مشرکو!) کیا تمہارے لئے تو بیٹے اور خدا کے لئے بیٹیاں');
INSERT INTO "jalandhari" VALUES(53,22,'53:22','یہ تقسیم تو بہت بےانصافی کی ہے');
INSERT INTO "jalandhari" VALUES(53,23,'53:23','وہ تو صرف نام ہی نام ہیں جو تم نے اور تمہارے باپ دادا نے گھڑ لئے ہیں۔ خدا نے تو ان کی کوئی سند نازل نہیں کی۔ یہ لوگ محض ظن (فاسد) اور خواہشات نفس کے پیچھے چل رہے ہیں۔ حالانکہ ان کے پروردگار کی طرف سے ان کے پاس ہدایت آچکی ہے');
INSERT INTO "jalandhari" VALUES(53,24,'53:24','کیا جس چیز کی انسان آرزو کرتا ہے وہ اسے ضرور ملتی ہے');
INSERT INTO "jalandhari" VALUES(53,25,'53:25','آخرت اور دنیا تو الله ہی کے ہاتھ میں ہے');
INSERT INTO "jalandhari" VALUES(53,26,'53:26','اور آسمانوں میں بہت سے فرشتے ہیں جن کی سفارش کچھ بھی فائدہ نہیں دیتی مگر اس وقت کہ خدا جس کے لئے چاہے اجازت بخشے اور (سفارش) پسند کرے');
INSERT INTO "jalandhari" VALUES(53,27,'53:27','جو لوگ آخرت پر ایمان نہیں لاتے وہ فرشتوں کو (خدا کی) لڑکیوں کے نام سے موسوم کرتے ہیں');
INSERT INTO "jalandhari" VALUES(53,28,'53:28','حالانکہ ان کو اس کی کچھ خبر نہیں۔ وہ صرف ظن پر چلتے ہیں۔ اور ظن یقین کے مقابلے میں کچھ کام نہیں آتا');
INSERT INTO "jalandhari" VALUES(53,29,'53:29','تو جو ہماری یاد سے روگردانی اور صرف دنیا ہی کی زندگی کا خواہاں ہو اس سے تم بھی منہ پھیر لو');
INSERT INTO "jalandhari" VALUES(53,30,'53:30','ان کے علم کی انتہا یہی ہے۔ تمہارا پروردگار اس کو بھی خوب جانتا ہے جو اس کے رستے سے بھٹک گیا اور اس سے بھی خوب واقف ہے جو رستے پر چلا');
INSERT INTO "jalandhari" VALUES(53,31,'53:31','اور جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے سب خدا ہی کا ہے (اور اس نے خلقت کو) اس لئے (پیدا کیا ہے) کہ جن لوگوں نے برے کام کئے ان کو ان کے اعمال کا (برا) بدلا دے اور جنہوں نے نیکیاں کیں ان کو نیک بدلہ دے');
INSERT INTO "jalandhari" VALUES(53,32,'53:32','جو صغیرہ گناہوں کے سوا بڑے بڑے گناہوں اور بےحیائی کی باتوں سے اجتناب کرتے ہیں۔ بےشک تمہارا پروردگار بڑی بخشش والا ہے۔ وہ تم کو خوب جانتا ہے۔ جب اس نے تم کو مٹی سے پیدا کیا اور جب تم اپنی ماؤں کے پیٹ میں بچّے تھے۔ تو اپنے آپ کو پاک صاف نہ جتاؤ۔ جو پرہیزگار ہے وہ اس سے خوب واقف ہے');
INSERT INTO "jalandhari" VALUES(53,33,'53:33','بھلا تم نے اس شخص کو دیکھا جس نے منہ پھیر لیا');
INSERT INTO "jalandhari" VALUES(53,34,'53:34','اور تھوڑا سا دیا (پھر) ہاتھ روک لیا');
INSERT INTO "jalandhari" VALUES(53,35,'53:35','کیا اس کے پاس غیب کا علم ہے کہ وہ اس کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(53,36,'53:36','کیا جو باتیں موسیٰ کے صحیفوں میں ہیں ان کی اس کو خبر نہیں پہنچی');
INSERT INTO "jalandhari" VALUES(53,37,'53:37','اور ابراہیمؑ کی جنہوں نے (حق طاعت ورسالت) پورا کیا');
INSERT INTO "jalandhari" VALUES(53,38,'53:38','یہ کہ کوئی شخص دوسرے (کے گناہ) کا بوجھ نہیں اٹھائے گا');
INSERT INTO "jalandhari" VALUES(53,39,'53:39','اور یہ کہ انسان کو وہی ملتا ہے جس کی وہ کوشش کرتا ہے');
INSERT INTO "jalandhari" VALUES(53,40,'53:40','اور یہ کہ اس کی کوشش دیکھی جائے گی');
INSERT INTO "jalandhari" VALUES(53,41,'53:41','پھر اس کو اس کا پورا پورا بدلا دیا جائے گا');
INSERT INTO "jalandhari" VALUES(53,42,'53:42','اور یہ کہ تمہارے پروردگار ہی کے پاس پہنچنا ہے');
INSERT INTO "jalandhari" VALUES(53,43,'53:43','اور یہ کہ وہ ہنساتا اور رلاتا ہے');
INSERT INTO "jalandhari" VALUES(53,44,'53:44','اور یہ کہ وہی مارتا اور جلاتا ہے');
INSERT INTO "jalandhari" VALUES(53,45,'53:45','اور یہ کہ وہی نر اور مادہ دو قسم (کے حیوان) پیدا کرتا ہے');
INSERT INTO "jalandhari" VALUES(53,46,'53:46','(یعنی) نطفے سے جو (رحم میں) ڈالا جاتا ہے');
INSERT INTO "jalandhari" VALUES(53,47,'53:47','اور یہ کہ (قیامت کو) اسی پر دوبارہ اٹھانا لازم ہے');
INSERT INTO "jalandhari" VALUES(53,48,'53:48','اور یہ کہ وہی دولت مند بناتا اور مفلس کرتا ہے');
INSERT INTO "jalandhari" VALUES(53,49,'53:49','اور یہ کہ وہی شعریٰ کا مالک ہے');
INSERT INTO "jalandhari" VALUES(53,50,'53:50','اور یہ کہ اسی نے عاد اول کو ہلاک کر ڈالا');
INSERT INTO "jalandhari" VALUES(53,51,'53:51','اور ثمود کو بھی۔ غرض کسی کو باقی نہ چھوڑا');
INSERT INTO "jalandhari" VALUES(53,52,'53:52','اور ان سے پہلے قوم نوحؑ کو بھی۔ کچھ شک نہیں کہ وہ لوگ بڑے ہی ظالم اور بڑے ہی سرکش تھے');
INSERT INTO "jalandhari" VALUES(53,53,'53:53','اور اسی نے الٹی ہوئی بستیوں کو دے پٹکا');
INSERT INTO "jalandhari" VALUES(53,54,'53:54','پھر ان پر چھایا جو چھایا');
INSERT INTO "jalandhari" VALUES(53,55,'53:55','تو (اے انسان) تو اپنے پروردگار کی کون سی نعمت پر جھگڑے گا');
INSERT INTO "jalandhari" VALUES(53,56,'53:56','یہ (محمدصلى الله عليه وسلم) بھی اگلے ڈر سنانے والوں میں سے ایک ڈر سنانے والے ہیں');
INSERT INTO "jalandhari" VALUES(53,57,'53:57','آنے والی (یعنی قیامت) قریب آ پہنچی');
INSERT INTO "jalandhari" VALUES(53,58,'53:58','اس (دن کی تکلیفوں) کو خدا کے سوا کوئی دور نہیں کرسکے گا');
INSERT INTO "jalandhari" VALUES(53,59,'53:59','اے منکرین خدا) کیا تم اس کلام سے تعجب کرتے ہو؟');
INSERT INTO "jalandhari" VALUES(53,60,'53:60','اور ہنستے ہو اور روتے نہیں؟');
INSERT INTO "jalandhari" VALUES(53,61,'53:61','اور تم غفلت میں پڑ رہے ہو');
INSERT INTO "jalandhari" VALUES(53,62,'53:62','تو خدا کے آگے سجدہ کرو اور (اسی کی) عبادت کرو');
INSERT INTO "jalandhari" VALUES(54,1,'54:1','قیامت قریب آ پہنچی اور چاند شق ہوگیا');
INSERT INTO "jalandhari" VALUES(54,2,'54:2','اور اگر (کافر) کوئی نشانی دیکھتے ہیں تو منہ پھیر لیتے ہیں اور کہتے ہیں کہ یہ ایک ہمیشہ کا جادو ہے');
INSERT INTO "jalandhari" VALUES(54,3,'54:3','اور انہوں نے جھٹلایا اور اپنی خواہشوں کی پیروی کی اور ہر کام کا وقت مقرر ہے');
INSERT INTO "jalandhari" VALUES(54,4,'54:4','اور ان کو ایسے حالات (سابقین) پہنچ چکے ہیں جن میں عبرت ہے');
INSERT INTO "jalandhari" VALUES(54,5,'54:5','اور کامل دانائی (کی کتاب بھی) لیکن ڈرانا ان کو کچھ فائدہ نہیں دیتا');
INSERT INTO "jalandhari" VALUES(54,6,'54:6','تو تم بھی ان کی کچھ پروا نہ کرو۔ جس دن بلانے والا ان کو ایک ناخوش چیز کی طرف بلائے گا');
INSERT INTO "jalandhari" VALUES(54,7,'54:7','تو آنکھیں نیچی کئے ہوئے قبروں سے نکل پڑیں گے گویا بکھری ہوئی ٹڈیاں');
INSERT INTO "jalandhari" VALUES(54,8,'54:8','اس بلانے والے کی طرف دوڑتے جاتے ہوں گے۔ کافر کہیں گے یہ دن بڑا سخت ہے');
INSERT INTO "jalandhari" VALUES(54,9,'54:9','ان سے پہلے نوحؑ کی قوم نے بھی تکذیب کی تھی تو انہوں نے ہمارے بندے کو جھٹلایا اور کہا کہ دیوانہ ہے اور انہیں ڈانٹا بھی');
INSERT INTO "jalandhari" VALUES(54,10,'54:10','تو انہوں نے اپنے پروردگار سے دعا کی کہ (بار الٓہا) میں (ان کے مقابلے میں) کمزور ہوں تو (ان سے) بدلہ لے');
INSERT INTO "jalandhari" VALUES(54,11,'54:11','پس ہم نے زور کے مینہ سے آسمان کے دہانے کھول دیئے');
INSERT INTO "jalandhari" VALUES(54,12,'54:12','اور زمین میں چشمے جاری کردیئے تو پانی ایک کام کے لئے جو مقدر ہوچکا تھا جمع ہوگیا');
INSERT INTO "jalandhari" VALUES(54,13,'54:13','اور ہم نے نوحؑ کو ایک کشتی پر جو تختوں اور میخوں سے تیار کی گئی تھی سوار کرلیا');
INSERT INTO "jalandhari" VALUES(54,14,'54:14','وہ ہماری آنکھوں کے سامنے چلتی تھی۔ (یہ سب کچھ) اس شخص کے انتقام کے لئے (کیا گیا) جس کو کافر مانتے نہ تھے');
INSERT INTO "jalandhari" VALUES(54,15,'54:15','اور ہم نے اس کو ایک عبرت بنا چھوڑا تو کوئی ہے کہ سوچے سمجھے؟');
INSERT INTO "jalandhari" VALUES(54,16,'54:16','سو (دیکھ لو کہ) میرا عذاب اور ڈرانا کیسا ہوا؟');
INSERT INTO "jalandhari" VALUES(54,17,'54:17','اور ہم نے قرآن کو سمجھنے کے لئے آسان کردیا ہے تو کوئی ہے کہ سوچے سمجھے؟');
INSERT INTO "jalandhari" VALUES(54,18,'54:18','عاد نے بھی تکذیب کی تھی سو (دیکھ لو کہ) میرا عذاب اور ڈرانا کیسا ہوا');
INSERT INTO "jalandhari" VALUES(54,19,'54:19','ہم نے ان پر سخت منحوس دن میں آندھی چلائی');
INSERT INTO "jalandhari" VALUES(54,20,'54:20','وہ لوگوں کو (اس طرح) اکھیڑے ڈالتی تھی گویا اکھڑی ہوئی کھجوروں کے تنے ہیں');
INSERT INTO "jalandhari" VALUES(54,21,'54:21','سو (دیکھ لو کہ) میرا عذاب اور ڈرانا کیسا ہوا');
INSERT INTO "jalandhari" VALUES(54,22,'54:22','اور ہم نے قرآن کو سمجھنے کے لئے آسان کردیا ہے تو کوئی ہے کہ سوچے سمجھے؟');
INSERT INTO "jalandhari" VALUES(54,23,'54:23','ثمود نے بھی ہدایت کرنے والوں کو جھٹلایا');
INSERT INTO "jalandhari" VALUES(54,24,'54:24','اور کہا کہ بھلا ایک آدمی جو ہم ہی میں سے ہے ہم اس کی پیروی کریں؟ یوں ہو تو ہم گمراہی اور دیوانگی میں پڑ گئے');
INSERT INTO "jalandhari" VALUES(54,25,'54:25','کیا ہم سب میں سے اسی پر وحی نازل ہوئی ہے؟ (نہیں) بلکہ یہ جھوٹا خود پسند ہے');
INSERT INTO "jalandhari" VALUES(54,26,'54:26','ان کو کل ہی معلوم ہوجائے گا کہ کون جھوٹا خود پسند ہے');
INSERT INTO "jalandhari" VALUES(54,27,'54:27','(اے صالح) ہم ان کی آزمائش کے لئے اونٹنی بھیجنے والے ہیں تو تم ان کو دیکھتے رہو اور صبر کرو');
INSERT INTO "jalandhari" VALUES(54,28,'54:28','اور ان کو آگاہ کردو کہ ان میں پانی کی باری مقرر کر دی گئی ہے۔ ہر (باری والے کو اپنی) باری پر آنا چاہیئے');
INSERT INTO "jalandhari" VALUES(54,29,'54:29','تو ان لوگوں نے اپنے رفیق کو بلایا اور اس نے (اونٹنی کو پکڑ کر اس کی) کونچیں کاٹ ڈالیں');
INSERT INTO "jalandhari" VALUES(54,30,'54:30','سو (دیکھ لو کہ) میرا عذاب اور ڈرانا کیسا ہوا');
INSERT INTO "jalandhari" VALUES(54,31,'54:31','ہم نے ان پر (عذاب کے لئے) ایک چیخ بھیجی تو وہ ایسے ہوگئے جیسے باڑ والے کی سوکھی اور ٹوٹی ہوئی باڑ');
INSERT INTO "jalandhari" VALUES(54,32,'54:32','اور ہم نے قرآن کو سمجھنے کے لئے آسان کردیا ہے تو کوئی ہے کہ سوچے سمجھے؟');
INSERT INTO "jalandhari" VALUES(54,33,'54:33','لوط کی قوم نے بھی ڈر سنانے والوں کو جھٹلایا تھا');
INSERT INTO "jalandhari" VALUES(54,34,'54:34','تو ہم نے ان پر کنکر بھری ہوا چلائی مگر لوط کے گھر والے کہ ہم نے ان کو پچھلی رات ہی سے بچا لیا');
INSERT INTO "jalandhari" VALUES(54,35,'54:35','اپنے فضل سے۔ شکر کرنے والوں کو ہم ایسا ہی بدلہ دیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(54,36,'54:36','اور لوطؑ نے ان کو ہماری پکڑ سے ڈرایا بھی تھا مگر انہوں نے ڈرانے میں شک کیا');
INSERT INTO "jalandhari" VALUES(54,37,'54:37','اور ان سے ان کے مہمانوں کو لے لینا چاہا تو ہم نے ان کی آنکھیں مٹا دیں سو (اب) میرے عذاب اور ڈرانے کے مزے چکھو');
INSERT INTO "jalandhari" VALUES(54,38,'54:38','اور ان پر صبح سویرے ہی اٹل عذاب آ نازل ہوا');
INSERT INTO "jalandhari" VALUES(54,39,'54:39','تو اب میرے عذاب اور ڈرانے کے مزے چکھو');
INSERT INTO "jalandhari" VALUES(54,40,'54:40','اور ہم نے قرآن کو سمجھنے کے لئے آسان کردیا ہے تو کوئی ہے کہ سوچے سمجھے؟');
INSERT INTO "jalandhari" VALUES(54,41,'54:41','اور قوم فرعون کے پاس بھی ڈر سنانے والے آئے');
INSERT INTO "jalandhari" VALUES(54,42,'54:42','انہوں نے ہماری تمام نشانیوں کو جھٹلایا تو ہم نے ان کو اس طرح پکڑ لیا جس طرح ایک قوی اور غالب شخص پکڑ لیتا ہے');
INSERT INTO "jalandhari" VALUES(54,43,'54:43','(اے اہل عرب) کیا تمہارے کافر ان لوگوں سے بہتر ہیں یا تمہارے لئے (پہلی) کتابوں میں کوئی فارغ خطی لکھ دی گئی ہے');
INSERT INTO "jalandhari" VALUES(54,44,'54:44','کیا یہ لوگ کہتے ہیں کہ ہماری جماعت بڑی مضبوط ہے');
INSERT INTO "jalandhari" VALUES(54,45,'54:45','عنقریب یہ جماعت شکست کھائے گی اور یہ لوگ پیٹھ پھیر کر بھاگ جائیں گے');
INSERT INTO "jalandhari" VALUES(54,46,'54:46','ان کے وعدے کا وقت تو قیامت ہے اور قیامت بڑی سخت اور بہت تلخ ہے');
INSERT INTO "jalandhari" VALUES(54,47,'54:47','بےشک گنہگار لوگ گمراہی اور دیوانگی میں (مبتلا) ہیں');
INSERT INTO "jalandhari" VALUES(54,48,'54:48','اس روز منہ کے بل دوزخ میں گھسیٹے جائیں گے اب آگ کا مزہ چکھو');
INSERT INTO "jalandhari" VALUES(54,49,'54:49','ہم نے ہر چیز اندازہٴ مقرر کے ساتھ پیدا کی ہے');
INSERT INTO "jalandhari" VALUES(54,50,'54:50','اور ہمارا حکم تو آنکھ کے جھپکنے کی طرح ایک بات ہوتی ہے');
INSERT INTO "jalandhari" VALUES(54,51,'54:51','اور ہم تمہارے ہم مذہبوں کو ہلاک کرچکے ہیں تو کوئی ہے کہ سوچے سمجھے؟');
INSERT INTO "jalandhari" VALUES(54,52,'54:52','اور جو کچھ انہوں نے کیا، (ان کے) اعمال ناموں میں (مندرج) ہے');
INSERT INTO "jalandhari" VALUES(54,53,'54:53','(یعنی) ہر چھوٹا اور بڑا کام لکھ دیا گیا ہے');
INSERT INTO "jalandhari" VALUES(54,54,'54:54','جو پرہیزگار ہیں وہ باغوں اور نہروں میں ہوں گے');
INSERT INTO "jalandhari" VALUES(54,55,'54:55','(یعنی) پاک مقام میں ہر طرح کی قدرت رکھنے والے بادشاہ کی بارگاہ میں');
INSERT INTO "jalandhari" VALUES(55,1,'55:1','(خدا جو) نہایت مہربان');
INSERT INTO "jalandhari" VALUES(55,2,'55:2','اسی نے قرآن کی تعلیم فرمائی');
INSERT INTO "jalandhari" VALUES(55,3,'55:3','اسی نے انسان کو پیدا کیا');
INSERT INTO "jalandhari" VALUES(55,4,'55:4','اسی نے اس کو بولنا سکھایا');
INSERT INTO "jalandhari" VALUES(55,5,'55:5','سورج اور چاند ایک حساب مقرر سے چل رہے ہیں');
INSERT INTO "jalandhari" VALUES(55,6,'55:6','اور بوٹیاں اور درخت سجدہ کر رہے ہیں');
INSERT INTO "jalandhari" VALUES(55,7,'55:7','اور اسی نے آسمان کو بلند کیا اور ترازو قائم کی');
INSERT INTO "jalandhari" VALUES(55,8,'55:8','کہ ترازو (سے تولنے) میں حد سے تجاوز نہ کرو');
INSERT INTO "jalandhari" VALUES(55,9,'55:9','اور انصاف کے ساتھ ٹھیک تولو۔ اور تول کم مت کرو');
INSERT INTO "jalandhari" VALUES(55,10,'55:10','اور اسی نے خلقت کے لئے زمین بچھائی');
INSERT INTO "jalandhari" VALUES(55,11,'55:11','اس میں میوے اور کھجور کے درخت ہیں جن کے خوشوں پر غلاف ہوتے ہیں');
INSERT INTO "jalandhari" VALUES(55,12,'55:12','اور اناج جس کے ساتھ بھس ہوتا ہے اور خوشبودار پھول');
INSERT INTO "jalandhari" VALUES(55,13,'55:13','تو (اے گروہ جن وانس) تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,14,'55:14','اسی نے انسان کو ٹھیکرے کی طرح کھنکھناتی مٹی سے بنایا');
INSERT INTO "jalandhari" VALUES(55,15,'55:15','اور جنات کو آگ کے شعلے سے پیدا کیا');
INSERT INTO "jalandhari" VALUES(55,16,'55:16','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,17,'55:17','وہی دونوں مشرقوں اور دونوں مغربوں کا مالک (ہے)');
INSERT INTO "jalandhari" VALUES(55,18,'55:18','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,19,'55:19','اسی نے دو دریا رواں کئے جو آپس میں ملتے ہیں');
INSERT INTO "jalandhari" VALUES(55,20,'55:20','دونوں میں ایک آڑ ہے کہ (اس سے) تجاوز نہیں کرسکتے');
INSERT INTO "jalandhari" VALUES(55,21,'55:21','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,22,'55:22','دونوں دریاؤں سے موتی اور مونگے نکلتے ہیں');
INSERT INTO "jalandhari" VALUES(55,23,'55:23','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,24,'55:24','اور جہاز بھی اسی کے ہیں جو دریا میں پہاڑوں کی طرح اونچے کھڑے ہوتے ہیں');
INSERT INTO "jalandhari" VALUES(55,25,'55:25','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,26,'55:26','جو (مخلوق) زمین پر ہے سب کو فنا ہونا ہے');
INSERT INTO "jalandhari" VALUES(55,27,'55:27','اور تمہارے پروردگار ہی کی ذات (بابرکات) جو صاحب جلال وعظمت ہے باقی رہے گی');
INSERT INTO "jalandhari" VALUES(55,28,'55:28','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,29,'55:29','آسمان اور زمین میں جتنے لوگ ہیں سب اسی سے مانگتے ہیں۔ وہ ہر روز کام میں مصروف رہتا ہے');
INSERT INTO "jalandhari" VALUES(55,30,'55:30','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,31,'55:31','اے دونوں جماعتو! ہم عنقریب تمہاری طرف متوجہ ہوتے ہیں');
INSERT INTO "jalandhari" VALUES(55,32,'55:32','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,33,'55:33','اے گروہِ جن وانس اگر تمہیں قدرت ہو کہ آسمان اور زمین کے کناروں سے نکل جاؤ تو نکل جاؤ۔ اور زور کے سوا تم نکل سکنے ہی کے نہیں');
INSERT INTO "jalandhari" VALUES(55,34,'55:34','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,35,'55:35','تم پر آگ کے شعلے اور دھواں چھوڑ دیا جائے گا تو پھر تم مقابلہ نہ کرسکو گے');
INSERT INTO "jalandhari" VALUES(55,36,'55:36','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,37,'55:37','پھر جب آسمان پھٹ کر تیل کی تلچھٹ کی طرح گلابی ہوجائے گا (تو) وہ کیسا ہولناک دن ہوگا');
INSERT INTO "jalandhari" VALUES(55,38,'55:38','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,39,'55:39','اس روز نہ تو کسی انسان سے اس کے گناہوں کے بارے میں پرسش کی جائے گی اور نہ کسی جن سے');
INSERT INTO "jalandhari" VALUES(55,40,'55:40','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,41,'55:41','گنہگار اپنے چہرے ہی سے پہچان لئے جائیں گے تو پیشانی کے بالوں اور پاؤں سے پکڑ لئے جائیں گے');
INSERT INTO "jalandhari" VALUES(55,42,'55:42','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,43,'55:43','یہی وہ جہنم ہے جسے گنہگار لوگ جھٹلاتے تھے');
INSERT INTO "jalandhari" VALUES(55,44,'55:44','وہ دوزخ اور کھولتے ہوئے گرم پانی کے درمیان گھومتے پھریں گے');
INSERT INTO "jalandhari" VALUES(55,45,'55:45','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,46,'55:46','اور جو شخص اپنے پروردگار کے سامنے کھڑے ہونے سے ڈرا اس کے لئے دو باغ ہیں');
INSERT INTO "jalandhari" VALUES(55,47,'55:47','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,48,'55:48','ان دونوں میں بہت سی شاخیں (یعنی قسم قسم کے میووں کے درخت ہیں)');
INSERT INTO "jalandhari" VALUES(55,49,'55:49','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,50,'55:50','ان میں دو چشمے بہہ رہے ہیں');
INSERT INTO "jalandhari" VALUES(55,51,'55:51','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,52,'55:52','ان میں سب میوے دو دو قسم کے ہیں');
INSERT INTO "jalandhari" VALUES(55,53,'55:53','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,54,'55:54','(اہل جنت) ایسے بچھونوں پر جن کے استرا طلس کے ہیں تکیہ لگائے ہوئے ہوں گے۔ اور دونوں باغوں کے میوے قریب (جھک رہے) ہیں');
INSERT INTO "jalandhari" VALUES(55,55,'55:55','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,56,'55:56','ان میں نیچی نگاہ والی عورتیں ہیں جن کو اہل جنت سے پہلے نہ کسی انسان نے ہاتھ لگایا اور نہ کسی جن نے');
INSERT INTO "jalandhari" VALUES(55,57,'55:57','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,58,'55:58','گویا وہ یاقوت اور مرجان ہیں');
INSERT INTO "jalandhari" VALUES(55,59,'55:59','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,60,'55:60','نیکی کا بدلہ نیکی کے سوا کچھ نہیں ہے');
INSERT INTO "jalandhari" VALUES(55,61,'55:61','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,62,'55:62','اور ان باغوں کے علاوہ دو باغ اور ہیں');
INSERT INTO "jalandhari" VALUES(55,63,'55:63','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,64,'55:64','دونوں خوب گہرے سبز');
INSERT INTO "jalandhari" VALUES(55,65,'55:65','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,66,'55:66','ان میں دو چشمے ابل رہے ہیں');
INSERT INTO "jalandhari" VALUES(55,67,'55:67','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,68,'55:68','ان میں میوے اور کھجوریں اور انار ہیں');
INSERT INTO "jalandhari" VALUES(55,69,'55:69','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,70,'55:70','ان میں نیک سیرت (اور) خوبصورت عورتیں ہیں');
INSERT INTO "jalandhari" VALUES(55,71,'55:71','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,72,'55:72','(وہ) حوریں (ہیں جو) خیموں میں مستور (ہیں)');
INSERT INTO "jalandhari" VALUES(55,73,'55:73','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,74,'55:74','ان کو اہل جنت سے پہلے نہ کسی انسان نے ہاتھ لگایا اور نہ کسی جن نے');
INSERT INTO "jalandhari" VALUES(55,75,'55:75','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,76,'55:76','سبز قالینوں اور نفیس مسندوں پر تکیہ لگائے بیٹھے ہوں گے');
INSERT INTO "jalandhari" VALUES(55,77,'55:77','تو تم اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "jalandhari" VALUES(55,78,'55:78','(اے محمدصلى الله عليه وسلم) تمہارا پروردگار جو صاحب جلال وعظمت ہے اس کا نام بڑا بابرکت ہے');
INSERT INTO "jalandhari" VALUES(56,1,'56:1','جب واقع ہونے والی واقع ہوجائے');
INSERT INTO "jalandhari" VALUES(56,2,'56:2','اس کے واقع ہونے میں کچھ جھوٹ نہیں');
INSERT INTO "jalandhari" VALUES(56,3,'56:3','کسی کو پست کرے کسی کو بلند');
INSERT INTO "jalandhari" VALUES(56,4,'56:4','جب زمین بھونچال سے لرزنے لگے');
INSERT INTO "jalandhari" VALUES(56,5,'56:5','اور پہاڑ ٹوٹ کر ریزہ ریزہ ہوجائیں');
INSERT INTO "jalandhari" VALUES(56,6,'56:6','پھر غبار ہو کر اُڑنے لگیں');
INSERT INTO "jalandhari" VALUES(56,7,'56:7','اور تم لوگ تین قسم ہوجاؤ');
INSERT INTO "jalandhari" VALUES(56,8,'56:8','تو داہنے ہاتھ والے (سبحان الله) داہنے ہاتھ والے کیا (ہی چین میں) ہیں');
INSERT INTO "jalandhari" VALUES(56,9,'56:9','اور بائیں ہاتھ والے (افسوس) بائیں ہاتھ والے کیا (گرفتار عذاب) ہیں');
INSERT INTO "jalandhari" VALUES(56,10,'56:10','اور جو آگے بڑھنے والے ہیں (ان کا کیا کہنا) وہ آگے ہی بڑھنے والے ہیں');
INSERT INTO "jalandhari" VALUES(56,11,'56:11','وہی (خدا کے) مقرب ہیں');
INSERT INTO "jalandhari" VALUES(56,12,'56:12','نعمت کے بہشتوں میں');
INSERT INTO "jalandhari" VALUES(56,13,'56:13','وہ بہت سے تو اگلے لوگوں میں سے ہوں گے');
INSERT INTO "jalandhari" VALUES(56,14,'56:14','اور تھوڑے سے پچھلوں میں سے');
INSERT INTO "jalandhari" VALUES(56,15,'56:15','(لعل و یاقوت وغیرہ سے) جڑے ہوئے تختوں پر');
INSERT INTO "jalandhari" VALUES(56,16,'56:16','آمنے سامنے تکیہ لگائے ہوئے');
INSERT INTO "jalandhari" VALUES(56,17,'56:17','نوجوان خدمت گزار جو ہمیشہ (ایک ہی حالت میں) رہیں گے ان کے آس پاس پھریں گے');
INSERT INTO "jalandhari" VALUES(56,18,'56:18','یعنی آبخورے اور آفتابے اور صاف شراب کے گلاس لے لے کر');
INSERT INTO "jalandhari" VALUES(56,19,'56:19','اس سے نہ تو سر میں درد ہوگا اور نہ ان کی عقلیں زائل ہوں گی');
INSERT INTO "jalandhari" VALUES(56,20,'56:20','اور میوے جس طرح کے ان کو پسند ہوں');
INSERT INTO "jalandhari" VALUES(56,21,'56:21','اور پرندوں کا گوشت جس قسم کا ان کا جی چاہے');
INSERT INTO "jalandhari" VALUES(56,22,'56:22','اور بڑی بڑی آنکھوں والی حوریں');
INSERT INTO "jalandhari" VALUES(56,23,'56:23','جیسے (حفاظت سے) تہہ کئے ہوئے (آب دار) موتی');
INSERT INTO "jalandhari" VALUES(56,24,'56:24','یہ ان اعمال کا بدلہ ہے جو وہ کرتے تھے');
INSERT INTO "jalandhari" VALUES(56,25,'56:25','وہاں نہ بیہودہ بات سنیں گے اور نہ گالی گلوچ');
INSERT INTO "jalandhari" VALUES(56,26,'56:26','ہاں ان کا کلام سلام سلام (ہوگا)');
INSERT INTO "jalandhari" VALUES(56,27,'56:27','اور داہنے ہاتھ والے (سبحان الله) داہنے ہاتھ والے کیا (ہی عیش میں) ہیں');
INSERT INTO "jalandhari" VALUES(56,28,'56:28','(یعنی) بےخار کی بیریوں');
INSERT INTO "jalandhari" VALUES(56,29,'56:29','اور تہہ بہ تہہ کیلوں');
INSERT INTO "jalandhari" VALUES(56,30,'56:30','اور لمبے لمبے سایوں');
INSERT INTO "jalandhari" VALUES(56,31,'56:31','اور پانی کے جھرنوں');
INSERT INTO "jalandhari" VALUES(56,32,'56:32','اور میوہ ہائے کثیرہ (کے باغوں) میں');
INSERT INTO "jalandhari" VALUES(56,33,'56:33','جو نہ کبھی ختم ہوں اور نہ ان سے کوئی روکے');
INSERT INTO "jalandhari" VALUES(56,34,'56:34','اور اونچے اونچے فرشوں میں');
INSERT INTO "jalandhari" VALUES(56,35,'56:35','ہم نے ان (حوروں) کو پیدا کیا');
INSERT INTO "jalandhari" VALUES(56,36,'56:36','تو ان کو کنواریاں بنایا');
INSERT INTO "jalandhari" VALUES(56,37,'56:37','(اور شوہروں کی) پیاریاں اور ہم عمر');
INSERT INTO "jalandhari" VALUES(56,38,'56:38','یعنی داہنے ہاتھ والوں کے لئے');
INSERT INTO "jalandhari" VALUES(56,39,'56:39','(یہ) بہت سے اگلے لوگوں میں سے ہیں');
INSERT INTO "jalandhari" VALUES(56,40,'56:40','اور بہت سے پچھلوں میں سے');
INSERT INTO "jalandhari" VALUES(56,41,'56:41','اور بائیں ہاتھ والے (افسوس) بائیں ہاتھ والے کیا (ہی عذاب میں) ہیں');
INSERT INTO "jalandhari" VALUES(56,42,'56:42','(یعنی دوزخ کی) لپٹ اور کھولتے ہوئے پانی میں');
INSERT INTO "jalandhari" VALUES(56,43,'56:43','اور سیاہ دھوئیں کے سائے میں');
INSERT INTO "jalandhari" VALUES(56,44,'56:44','(جو) نہ ٹھنڈا (ہے) نہ خوشنما');
INSERT INTO "jalandhari" VALUES(56,45,'56:45','یہ لوگ اس سے پہلے عیشِ نعیم میں پڑے ہوئے تھے');
INSERT INTO "jalandhari" VALUES(56,46,'56:46','اور گناہ عظیم پر اڑے ہوئے تھے');
INSERT INTO "jalandhari" VALUES(56,47,'56:47','اور کہا کرتے تھے کہ بھلا جب ہم مرگئے اور مٹی ہوگئے اور ہڈیاں (ہی ہڈیاں رہ گئے) تو کیا ہمیں پھر اُٹھنا ہوگا؟');
INSERT INTO "jalandhari" VALUES(56,48,'56:48','اور کیا ہمارے باپ دادا کو بھی؟');
INSERT INTO "jalandhari" VALUES(56,49,'56:49','کہہ دو کہ بےشک پہلے اور پچھلے');
INSERT INTO "jalandhari" VALUES(56,50,'56:50','(سب) ایک روز مقرر کے وقت پر جمع کئے جائیں گے');
INSERT INTO "jalandhari" VALUES(56,51,'56:51','پھر تم اے جھٹلانے والے گمرا ہو!');
INSERT INTO "jalandhari" VALUES(56,52,'56:52','تھوہر کے درخت کھاؤ گے');
INSERT INTO "jalandhari" VALUES(56,53,'56:53','اور اسی سے پیٹ بھرو گے');
INSERT INTO "jalandhari" VALUES(56,54,'56:54','اور اس پر کھولتا ہوا پانی پیو گے');
INSERT INTO "jalandhari" VALUES(56,55,'56:55','اور پیو گے بھی تو اس طرح جیسے پیاسے اونٹ پیتے ہیں');
INSERT INTO "jalandhari" VALUES(56,56,'56:56','جزا کے دن یہ ان کی ضیافت ہوگی');
INSERT INTO "jalandhari" VALUES(56,57,'56:57','ہم نے تم کو (پہلی بار بھی تو) پیدا کیا ہے تو تم (دوبارہ اُٹھنے کو) کیوں سچ نہیں سمجھتے؟');
INSERT INTO "jalandhari" VALUES(56,58,'56:58','دیکھو تو کہ جس (نطفے) کو تم (عورتوں کے رحم میں) ڈالتے ہو');
INSERT INTO "jalandhari" VALUES(56,59,'56:59','کیا تم اس (سے انسان) کو بناتے ہو یا ہم بناتے ہیں؟');
INSERT INTO "jalandhari" VALUES(56,60,'56:60','ہم نے تم میں مرنا ٹھہرا دیا ہے اور ہم اس (بات) سے عاجز نہیں');
INSERT INTO "jalandhari" VALUES(56,61,'56:61','کہ تمہاری طرح کے اور لوگ تمہاری جگہ لے آئیں اور تم کو ایسے جہان میں جس کو تم نہیں جانتے پیدا کر دیں');
INSERT INTO "jalandhari" VALUES(56,62,'56:62','اور تم نے پہلی پیدائش تو جان ہی لی ہے۔ پھر تم سوچتے کیوں نہیں؟');
INSERT INTO "jalandhari" VALUES(56,63,'56:63','بھلا دیکھو تو کہ جو کچھ تم بوتے ہو');
INSERT INTO "jalandhari" VALUES(56,64,'56:64','تو کیا تم اسے اُگاتے ہو یا ہم اُگاتے ہیں؟');
INSERT INTO "jalandhari" VALUES(56,65,'56:65','اگر ہم چاہیں تو اسے چورا چورا کردیں اور تم باتیں بناتے رہ جاؤ');
INSERT INTO "jalandhari" VALUES(56,66,'56:66','(کہ ہائے) ہم تو مفت تاوان میں پھنس گئے');
INSERT INTO "jalandhari" VALUES(56,67,'56:67','بلکہ ہم ہیں ہی بےنصیب');
INSERT INTO "jalandhari" VALUES(56,68,'56:68','بھلا دیکھو تو کہ جو پانی تم پیتے ہو');
INSERT INTO "jalandhari" VALUES(56,69,'56:69','کیا تم نے اس کو بادل سے نازل کیا ہے یا ہم نازل کرتے ہیں؟');
INSERT INTO "jalandhari" VALUES(56,70,'56:70','اگر ہم چاہیں تو ہم اسے کھاری کردیں پھر تم شکر کیوں نہیں کرتے؟');
INSERT INTO "jalandhari" VALUES(56,71,'56:71','بھلا دیکھو تو جو آگ تم درخت سے نکالتے ہو');
INSERT INTO "jalandhari" VALUES(56,72,'56:72','کیا تم نے اس کے درخت کو پیدا کیا ہے یا ہم پیدا کرتے ہیں؟');
INSERT INTO "jalandhari" VALUES(56,73,'56:73','ہم نے اسے یاد دلانے اور مسافروں کے برتنے کو بنایا ہے');
INSERT INTO "jalandhari" VALUES(56,74,'56:74','تو تم اپنے پروردگار بزرگ کے نام کی تسبیح کرو');
INSERT INTO "jalandhari" VALUES(56,75,'56:75','ہمیں تاروں کی منزلوں کی قسم');
INSERT INTO "jalandhari" VALUES(56,76,'56:76','اور اگر تم سمجھو تو یہ بڑی قسم ہے');
INSERT INTO "jalandhari" VALUES(56,77,'56:77','کہ یہ بڑے رتبے کا قرآن ہے');
INSERT INTO "jalandhari" VALUES(56,78,'56:78','(جو) کتاب محفوظ میں (لکھا ہوا ہے)');
INSERT INTO "jalandhari" VALUES(56,79,'56:79','اس کو وہی ہاتھ لگاتے ہیں جو پاک ہیں');
INSERT INTO "jalandhari" VALUES(56,80,'56:80','پروردگار عالم کی طرف سے اُتارا گیا ہے');
INSERT INTO "jalandhari" VALUES(56,81,'56:81','کیا تم اس کلام سے انکار کرتے ہو؟');
INSERT INTO "jalandhari" VALUES(56,82,'56:82','اور اپنا وظیفہ یہ بناتے ہو کہ (اسے) جھٹلاتے ہو');
INSERT INTO "jalandhari" VALUES(56,83,'56:83','بھلا جب روح گلے میں آ پہنچتی ہے');
INSERT INTO "jalandhari" VALUES(56,84,'56:84','اور تم اس وقت کی (حالت کو) دیکھا کرتے ہو');
INSERT INTO "jalandhari" VALUES(56,85,'56:85','اور ہم اس (مرنے والے) سے تم سے بھی زیادہ نزدیک ہوتے ہیں لیکن تم کو نظر نہیں آتے');
INSERT INTO "jalandhari" VALUES(56,86,'56:86','پس اگر تم کسی کے بس میں نہیں ہو');
INSERT INTO "jalandhari" VALUES(56,87,'56:87','تو اگر سچے ہو تو روح کو پھیر کیوں نہیں لیتے؟');
INSERT INTO "jalandhari" VALUES(56,88,'56:88','پھر اگر وہ (خدا کے) مقربوں میں سے ہے');
INSERT INTO "jalandhari" VALUES(56,89,'56:89','تو (اس کے لئے) آرام اور خوشبودار پھول اور نعمت کے باغ ہیں');
INSERT INTO "jalandhari" VALUES(56,90,'56:90','اور اگر وہ دائیں ہاتھ والوں میں سے ہے');
INSERT INTO "jalandhari" VALUES(56,91,'56:91','تو (کہا جائے گا کہ) تجھ پر داہنے ہاتھ والوں کی طرف سے سلام');
INSERT INTO "jalandhari" VALUES(56,92,'56:92','اور اگر وہ جھٹلانے والے گمراہوں میں سے ہے');
INSERT INTO "jalandhari" VALUES(56,93,'56:93','تو (اس کے لئے) کھولتے پانی کی ضیافت ہے');
INSERT INTO "jalandhari" VALUES(56,94,'56:94','اور جہنم میں داخل کیا جانا');
INSERT INTO "jalandhari" VALUES(56,95,'56:95','یہ (داخل کیا جانا یقیناً صحیح یعنی) حق الیقین ہے');
INSERT INTO "jalandhari" VALUES(56,96,'56:96','تو تم اپنے پروردگار بزرگ کے نام کی تسبیح کرتے رہو');
INSERT INTO "jalandhari" VALUES(57,1,'57:1','جو مخلوق آسمانوں اور زمین میں ہے خدا کی تسبیح کرتی ہے۔ اور وہ غالب (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(57,2,'57:2','آسمانوں اور زمین کی بادشاہی اسی کی ہے۔ (وہی) زندہ کرتا اور مارتا ہے۔ اور وہ ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(57,3,'57:3','وہ (سب سے) پہلا اور (سب سے) پچھلا اور (اپنی قدرتوں سے سب پر) ظاہر اور (اپنی ذات سے) پوشیدہ ہے اور وہ تمام چیزوں کو جانتا ہے');
INSERT INTO "jalandhari" VALUES(57,4,'57:4','وہی ہے جس نے آسمانوں اور زمین کو چھ دن میں پیدا کیا پھر عرش پر جا ٹھہرا۔ جو چیز زمین میں داخل ہوتی اور جو اس سے نکلتی ہے اور جو آسمان سے اُترتی اور جو اس کی طرف چڑھتی ہے سب اس کو معلوم ہے۔ اور تم جہاں کہیں ہو وہ تمہارے ساتھ ہے۔ اور جو کچھ تم کرتے ہو خدا اس کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(57,5,'57:5','آسمانوں اور زمین کی بادشاہی اسی کی ہے۔ اور سب امور اسی کی طرف رجوع ہوتے ہیں');
INSERT INTO "jalandhari" VALUES(57,6,'57:6','رات کو دن میں داخل کرتا اور دن کو رات میں داخل کرتا ہے۔ اور وہ دلوں کے بھیدوں تک سے واقف ہے');
INSERT INTO "jalandhari" VALUES(57,7,'57:7','تو) خدا پر اور اس کے رسول پر ایمان لاؤ اور جس (مال) میں اس نے تم کو (اپنا) نائب بنایا ہے اس میں سے خرچ کرو۔ جو لوگ تم میں سے ایمان لائے اور (مال) خرچ کرتے رہے ان کے لئے بڑا ثواب ہے');
INSERT INTO "jalandhari" VALUES(57,8,'57:8','اور تم کیسے لوگ ہو کہ خدا پر ایمان نہیں لاتے۔ حالانکہ (اس کے) پیغمبر تمہیں بلا رہے ہیں کہ اپنے پروردگار پر ایمان لاؤ اور اگر تم کو باور ہو تو وہ تم سے (اس کا) عہد بھی لے چکا ہے');
INSERT INTO "jalandhari" VALUES(57,9,'57:9','وہی تو ہے جو اپنے بندے پر واضح (المطالب) آیتیں نازل کرتا ہے تاکہ تم کو اندھیروں میں سے نکال کر روشنی میں لائے۔ بےشک خدا تم پر نہایت شفقت کرنے والا (اور) مہربان ہے');
INSERT INTO "jalandhari" VALUES(57,10,'57:10','اور تم کو کیا ہوا ہے کہ خدا کے رستے میں خرچ نہیں کرتے حالانکہ آسمانوں اور زمین کی وراثت خدا ہی کی ہے۔ جس شخص نے تم میں سے فتح (مکہ) سے پہلے خرچ کیا اور لڑائی کی وہ (اور جس نے یہ کام پیچھے کئے وہ) برابر نہیں۔ ان کا درجہ ان لوگوں سے کہیں بڑھ کر ہے جنہوں نے بعد میں خرچ (اموال) اور (کفار سے) جہاد وقتال کیا۔ اور خدا نے سب سے (ثواب) نیک (کا) وعدہ تو کیا ہے۔ اور جو کام تم کرتے ہو خدا ان سے واقف ہے');
INSERT INTO "jalandhari" VALUES(57,11,'57:11','کون ہے جو خدا کو (نیت) نیک (اور خلوص سے) قرض دے تو وہ اس کو اس سے دگنا کرے اور اس کے لئے عزت کا صلہ (یعنی جنت) ہے');
INSERT INTO "jalandhari" VALUES(57,12,'57:12','جس دن تم مومن مردوں اور مومن عورتوں کو دیکھو گے کہ ان (کے ایمان) کا نور ان کے آگے آگے اور داہنی طرف چل رہا ہے (تو ان سے کہا جائے گا کہ) تم کو بشارت ہو (کہ آج تمہارے لئے) باغ ہیں جن کے تلے نہریں بہہ رہی ہیں ان میں ہمیشہ رہو گے۔ یہی بڑی کامیابی ہے');
INSERT INTO "jalandhari" VALUES(57,13,'57:13','اُس دن منافق مرد اور منافق عورتیں مومنوں سے کہیں گے کہ ہماری طرف سے (شفقت) کیجیئے کہ ہم بھی تمہارے نور سے روشنی حاصل کریں۔ تو ان سے کہا جائے گا کہ پیچھے کو لوٹ جاؤ اور (وہاں) نور تلاش کرو۔ پھر ان کے بیچ میں ایک دیوار کھڑی کر دی جائے گی۔ جس میں ایک دروازہ ہوگا جو اس کی جانب اندرونی ہے اس میں تو رحمت ہے اور جو جانب بیرونی ہے اس طرف عذاب (واذیت)');
INSERT INTO "jalandhari" VALUES(57,14,'57:14','تو منافق لوگ مومنوں سے کہیں گے کہ کیا ہم (دنیا میں) تمہارے ساتھ نہ تھے وہ کہیں گے کیوں نہیں تھے۔ لیکن تم نے خود اپنے تئیں بلا میں ڈالا اور (ہمارے حق میں حوادث کے) منتظر رہے اور (اسلام میں) شک کیا اور (لاطائل) آرزوؤں نے تم کو دھوکہ دیا یہاں تک کہ خدا کا حکم آ پہنچا اور خدا کے بارے میں تم کو (شیطان) دغاباز دغا دیتا رہا');
INSERT INTO "jalandhari" VALUES(57,15,'57:15','تو آج تم سے معاوضہ نہیں لیا جائے گا اور نہ (وہ) کافروں ہی سے (قبول کیا جائے گا) تم سب کا ٹھکانا دوزخ ہے۔ (کہ) وہی تمہارے لائق ہے اور وہ بری جگہ ہے');
INSERT INTO "jalandhari" VALUES(57,16,'57:16','کیا ابھی تک مومنوں کے لئے اس کا وقت نہیں آیا کہ خدا کی یاد کرنے کے وقت اور (قرآن) جو (خدائے) برحق (کی طرف) سے نازل ہوا ہے اس کے سننے کے وقت ان کے دل نرم ہوجائیں اور وہ ان لوگوں کی طرف نہ ہوجائیں جن کو (ان سے) پہلے کتابیں دی گئی تھیں۔ پھر ان پر زمان طویل گزر گیا تو ان کے دل سخت ہوگئے۔ اور ان میں سے اکثر نافرمان ہیں');
INSERT INTO "jalandhari" VALUES(57,17,'57:17','جان رکھو کہ خدا ہی زمین کو اس کے مرنے کے بعد زندہ کرتا ہے۔ ہم نے اپنی نشانیاں تم سے کھول کھول کر بیان کردی ہیں تاکہ تم سمجھو');
INSERT INTO "jalandhari" VALUES(57,18,'57:18','جو لوگ خیرات کرنے والے ہیں مرد بھی اور عورتیں بھی۔ اور خدا کو (نیت) نیک (اور خلوص سے) قرض دیتے ہیں ان کو دوچند ادا کیا جائے گا اور ان کے لئے عزت کا صلہ ہے');
INSERT INTO "jalandhari" VALUES(57,19,'57:19','اور جو لوگ خدا اور اس کے پیغمبروں پر ایمان لائے یہی اپنے پروردگار کے نزدیک صدیق اور شہید ہیں۔ ان کے لئے ان (کے اعمال) کا صلہ ہوگا۔ اور ان (کے ایمان) کی روشنی۔ اور جن لوگوں نے کفر کیا اور ہماری آیتوں کو جھٹلایا وہی اہل دوزخ ہیں');
INSERT INTO "jalandhari" VALUES(57,20,'57:20','جان رکھو کہ دنیا کی زندگی محض کھیل اور تماشا اور زینت (وآرائش) اور تمہارے آپس میں فخر (وستائش) اور مال واولاد کی ایک دوسرے سے زیادہ طلب (وخواہش) ہے (اس کی مثال ایسی ہے) جیسے بارش کہ (اس سے کھیتی اُگتی اور) کسانوں کو کھیتی بھلی لگتی ہے پھر وہ خوب زور پر آتی ہے پھر (اے دیکھنے والے) تو اس کو دیکھتا ہے کہ (پک کر) زرد پڑ جاتی ہے پھر چورا چورا ہوجاتی ہے اور آخرت میں (کافروں کے لئے) عذاب شدید اور (مومنوں کے لئے) خدا کی طرف سے بخشش اور خوشنودی ہے۔ اور دنیا کی زندگی تو متاع فریب ہے');
INSERT INTO "jalandhari" VALUES(57,21,'57:21','(بندو) اپنے پروردگار کی بخشش کی طرف اور جنت کی( طرف) جس کا عرض آسمان اور زمین کے عرض کا سا ہے۔ اور جو ان لوگوں کے لئے تیار کی گئی ہے جو خدا پر اور اس کے پیغمبروں پر ایمان لائے ہیں لپکو۔ یہ خدا کا فضل ہے جسے چاہے عطا فرمائے۔ اور خدا بڑے فضل کا مالک ہے');
INSERT INTO "jalandhari" VALUES(57,22,'57:22','کوئی مصیبت ملک پر اور خود تم پر نہیں پڑتی مگر پیشتر اس کے کہ ہم اس کو پیدا کریں ایک کتاب میں (لکھی ہوئی) ہے۔ (اور) یہ (کام) خدا کو آسان ہے');
INSERT INTO "jalandhari" VALUES(57,23,'57:23','تاکہ جو (مطلب) تم سے فوت ہوگیا ہو اس کا غم نہ کھایا کرو اور جو تم کو اس نے دیا ہو اس پر اترایا نہ کرو۔ اور خدا کسی اترانے اور شیخی بگھارنے والے کو دوست نہیں رکھتا');
INSERT INTO "jalandhari" VALUES(57,24,'57:24','جو خود بھی بخل کریں اور لوگوں کو بھی بخل سکھائیں اور جو شخص روگردانی کرے تو خدا بھی بےپروا (اور) وہی سزاوار حمد (وثنا) ہے');
INSERT INTO "jalandhari" VALUES(57,25,'57:25','ہم نے اپنے پیغمبروں کو کھلی نشانیاں دے کر بھیجا۔ اور اُن پر کتابیں نازل کیں اور ترازو (یعنی قواعد عدل) تاکہ لوگ انصاف پر قائم رہیں۔ اور لوہا پیدا کیا اس میں (اسلحہٴ جنگ کے لحاظ سے) خطرہ بھی شدید ہے۔ اور لوگوں کے لئے فائدے بھی ہیں اور اس لئے کہ جو لوگ بن دیکھے خدا اور اس کے پیغمبروں کی مدد کرتے ہیں خدا ان کو معلوم کرے۔ بےشک خدا قوی (اور) غالب ہے');
INSERT INTO "jalandhari" VALUES(57,26,'57:26','اور ہم نے نوح اور ابراہیم کو (پیغمبر بنا کر) بھیجا اور ان کی اولاد میں پیغمبری اور کتاب (کے سلسلے) کو (وقتاً فوقتاً جاری) رکھا تو بعض تو ان میں سے ہدایت پر ہیں۔ اور اکثر ان میں سے خارج از اطاعت ہیں');
INSERT INTO "jalandhari" VALUES(57,27,'57:27','پھر ان کے پیچھے انہی کے قدموں پر (اور) پیغمبر بھیجے اور ان کے پیچھے مریمؑ کے بیٹے عیسیٰ کو بھیجا اور ان کو انجیل عنایت کی۔ اور جن لوگوں نے ان کی پیروی کی ان کے دلوں میں شفقت اور مہربانی ڈال دی۔ اور لذات سے کنارہ کشی کی تو انہوں نے خود ایک نئی بات نکال لی ہم نے ان کو اس کا حکم نہیں دیا تھا مگر (انہوں نے اپنے خیال میں) خدا کی خوشنودی حاصل کرنے کے لئے (آپ ہی ایسا کرلیا تھا) پھر جیسا اس کو نباہنا چاہیئے تھا نباہ بھی نہ سکے۔ پس جو لوگ ان میں سے ایمان لائے ان کو ہم نے ان کا اجر دیا اور ان میں بہت سے نافرمان ہیں');
INSERT INTO "jalandhari" VALUES(57,28,'57:28','مومنو! خدا سے ڈرو اور اس کے پیغمبر پر ایمان لاؤ وہ تمہیں اپنی رحمت سے دگنا اجر عطا فرمائے گا اور تمہارے لئے روشنی کردے گا جس میں چلو گے اور تم کو بخش دے گا۔ اور خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(57,29,'57:29','(یہ باتیں) اس لئے (بیان کی گئی ہیں) کہ اہل کتاب جان لیں کہ وہ خدا کے فضل پر کچھ بھی قدرت نہیں رکھتے۔ اور یہ کہ فضل خدا ہی کے ہاتھ ہے جس کو چاہتا ہے دیتا ہے اور خدا بڑے فضل کا مالک ہے');
INSERT INTO "jalandhari" VALUES(58,1,'58:1','(اے پیغمبر) جو عورت تم سے اپنے شوہر کے بارے میں بحث جدال کرتی اور خدا سے شکایت (رنج وملال) کرتی تھی۔ خدا نے اس کی التجا سن لی اور خدا تم دونوں کی گفتگو سن رہا تھا۔ کچھ شک نہیں کہ خدا سنتا دیکھتا ہے');
INSERT INTO "jalandhari" VALUES(58,2,'58:2','جو لوگ تم میں سے اپنی عورتوں کو ماں کہہ دیتے ہیں وہ ان کی مائیں نہیں (ہوجاتیں)۔ ان کی مائیں تو وہی ہیں جن کے بطن سے وہ پیدا ہوئے۔ بےشک وہ نامعقول اور جھوٹی بات کہتے ہیں اور خدا بڑا معاف کرنے والا (اور) بخشنے والا ہے');
INSERT INTO "jalandhari" VALUES(58,3,'58:3','اور جو لوگ اپنی بیویوں کو ماں کہہ بیٹھیں پھر اپنے قول سے رجوع کرلیں تو (ان کو) ہم بستر ہونے سے پہلے ایک غلام آزاد کرنا (ضروری) ہے۔ (مومنو) اس (حکم) سے تم کو نصیحت کی جاتی ہے۔ اور جو کچھ تم کرتے ہو خدا اس سے خبردار ہے');
INSERT INTO "jalandhari" VALUES(58,4,'58:4','جس کو غلام نہ ملے وہ مجامعت سے پہلے متواتر دو مہینے کے روزے (رکھے) جس کو اس کا بھی مقدور نہ ہوا (اسے) ساٹھ مسکینوں کو کھانا کھلانا (چاہیئے)۔ یہ (حکم) اس لئے (ہے) کہ تم خدا اور اسکے رسول کے فرمانبردار ہوجاؤ۔ اور یہ خدا کی حدیں ہیں۔ اور نہ ماننے والوں کے لئے درد دینے والا عذاب ہے');
INSERT INTO "jalandhari" VALUES(58,5,'58:5','جو لوگ خدا اور اس کے رسول کی مخالفت کرتے ہیں وہ (اسی طرح) ذلیل کئے جائیں گے جس طرح ان سے پہلے لوگ ذلیل کئے گئے تھے اور ہم نے صاف اور صریح آیتیں نازل کردی ہیں۔ جو نہیں مانتے ان کو ذلت کا عذاب ہوگا');
INSERT INTO "jalandhari" VALUES(58,6,'58:6','جس دن خدا ان سب کو جلا اٹھائے گا تو جو کام وہ کرتے رہے ان کو جتائے گا۔ خدا کو وہ سب (کام) یاد ہیں اور یہ ان کو بھول گئے ہیں اور خدا ہر چیز سے واقف ہے');
INSERT INTO "jalandhari" VALUES(58,7,'58:7','کیا تم کو معلوم نہیں کہ جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے خدا کو سب معلوم ہے۔ (کسی جگہ) تین (شخصوں) کا (مجمع اور) کانوں میں صلاح ومشورہ نہیں ہوتا مگر وہ ان میں چوتھا ہوتا ہے اور نہ کہیں پانچ کا مگر وہ ان میں چھٹا ہوتا ہے اور نہ اس سے کم یا زیادہ مگر وہ ان کے ساتھ ہوتا ہے خواہ وہ کہیں ہوں۔ پھر جو جو کام یہ کرتے رہے ہیں قیامت کے دن وہ (ایک ایک) ان کو بتائے گا۔ بےشک خدا ہر چیز سے واقف ہے');
INSERT INTO "jalandhari" VALUES(58,8,'58:8','کیا تم نے ان لوگوں کو نہیں دیکھا جن کو سرگوشیاں کرنے سے منع کیا گیا تھا۔ پھر جس (کام) سے منع کیا گیا تھا وہی پھر کرنے لگے اور یہ تو گناہ اور ظلم اور رسول (خدا) کی نافرمانی کی سرگوشیاں کرتے ہیں۔ اور جب تمہارے پاس آتے ہیں تو جس (کلمے) سے خدا نے تم کو دعا نہیں دی اس سے تمہیں دعا دیتے ہیں۔ اور اپنے دل میں کہتے ہیں کہ (اگر یہ واقعی پیغمبر ہیں تو) جو کچھ ہم کہتے ہیں خدا ہمیں اس کی سزا کیوں نہیں دیتا؟ (اے پیغمبر) ان کو دوزخ (ہی کی سزا) کافی ہے۔ یہ اسی میں داخل ہوں گے۔ اور وہ بری جگہ ہے');
INSERT INTO "jalandhari" VALUES(58,9,'58:9','مومنو! جب تم آپس میں سرگوشیاں کرنے لگو تو گناہ اور زیادتی اور پیغمبر کی نافرمانی کی باتیں نہ کرنا بلکہ نیکوکاری اور پرہیزگاری کی باتیں کرنا۔ اور خدا سے جس کے سامنے جمع کئے جاؤ گے ڈرتے رہنا');
INSERT INTO "jalandhari" VALUES(58,10,'58:10','کافروں کی) سرگوشیاں تو شیطان (کی حرکات) سے ہیں (جو) اس لئے (کی جاتی ہیں) کہ مومن (ان سے) غمناک ہوں مگر خدا کے حکم کے سوا ان سے انہیں کچھ نقصان نہیں پہنچ سکتا۔ تو مومنو کو چاہیئے کہ خدا ہی پر بھروسہ رکھیں');
INSERT INTO "jalandhari" VALUES(58,11,'58:11','مومنو! جب تم سے کہا جائے کہ مجلس میں کھل کر بیٹھو تو کھل بیٹھا کرو۔ خدا تم کو کشادگی بخشے گا۔ اور جب کہا جائے کہ اُٹھ کھڑے ہو تو اُٹھ کھڑے ہوا کرو۔ جو لوگ تم میں سے ایمان لائے ہیں اور جن کو علم عطا کیا گیا ہے خدا ان کے درجے بلند کرے گا۔ اور خدا تمہارے سب کاموں سے واقف ہے');
INSERT INTO "jalandhari" VALUES(58,12,'58:12','مومنو! جب تم پیغمبر کے کان میں کوئی بات کہو تو بات کہنے سے پہلے (مساکین کو) کچھ خیرات دے دیا کرو۔ یہ تمہارے لئے بہت بہتر اور پاکیزگی کی بات ہے۔ اور اگر خیرات تم کو میسر نہ آئے تو خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(58,13,'58:13','کیا تم اس سےکہ پیغمبر کے کان میں کوئی بات کہنے سے پہلے خیرات دیا کرو ڈر گئے؟ پھر جب تم نے (ایسا) نہ کیا اور خدا نے تمہیں معاف کردیا تو نماز پڑھتے اور زکوٰة دیتے رہو اور خدا اور اس کے رسول کی فرمانبرداری کرتے رہو۔ اور جو کچھ تم کرتے ہو خدا اس سے خبردار ہے');
INSERT INTO "jalandhari" VALUES(58,14,'58:14','بھلا تم نے ان لوگوں کو نہیں دیکھا جو ایسوں سے دوستی کرتے ہیں جن پر خدا کا غضب ہوا۔ وہ نہ تم میں ہیں نہ ان میں۔ اور جان بوجھ کر جھوٹی باتوں پر قسمیں کھاتے ہیں');
INSERT INTO "jalandhari" VALUES(58,15,'58:15','خدا نے ان کے لئے سخت عذاب تیار کر رکھا ہے۔ یہ جو کچھ کرتے ہیں یقیناً برا ہے');
INSERT INTO "jalandhari" VALUES(58,16,'58:16','انہوں نے اپنی قسموں کو ڈھال بنا لیا اور (لوگوں کو) خدا کے راستے سے روک دیا ہے سو ان کے لئے ذلت کا عذاب ہے');
INSERT INTO "jalandhari" VALUES(58,17,'58:17','خدا کے (عذاب کے) سامنے نہ تو ان کا مال ہی کچھ کام آئے گا اور نہ اولاد ہی (کچھ فائدہ دے گی)۔ یہ لوگ اہل دوزخ ہیں اس میں ہمیشہ (جلتے) رہیں گے');
INSERT INTO "jalandhari" VALUES(58,18,'58:18','جس دن خدا ان سب کو جلا اٹھائے گا تو جس طرح تمہارے سامنے قسمیں کھاتے (اسی طرح) خدا کے سامنے قسمیں کھائیں گے اور خیال کریں گے کہ (ایسا کرنے سے) کام لے نکلے ہیں۔ دیکھو یہ جھوٹے (اور برسر غلط) ہیں');
INSERT INTO "jalandhari" VALUES(58,19,'58:19','شیطان نے ان کو قابو میں کرلیا ہے۔ اور خدا کی یاد ان کو بھلا دی ہے۔ یہ (جماعت) شیطان کا لشکر ہے۔ اور سن رکھو کہ شیطان کا لشکر نقصان اٹھانے والا ہے');
INSERT INTO "jalandhari" VALUES(58,20,'58:20','جو لوگ خدا اور اس کے رسول کی مخالفت کرتے ہیں وہ نہایت ذلیل ہوں گے');
INSERT INTO "jalandhari" VALUES(58,21,'58:21','خدا کا حکم ناطق ہے کہ میں اور میرے پیغمبر ضرور غالب رہیں گے۔ بےشک خدا زورآور (اور) زبردست ہے');
INSERT INTO "jalandhari" VALUES(58,22,'58:22','جو لوگ خدا پر اور روز قیامت پر ایمان رکھتے ہیں تم ان کو خدا اور اس کے رسول کے دشمنوں سے دوستی کرتے ہوئے نہ دیکھو گے۔ خواہ وہ ان کے باپ یا بیٹے یا بھائی یا خاندان ہی کے لوگ ہوں۔ یہ وہ لوگ ہیں جن کے دلوں میں خدا نے ایمان (پتھر پر لکیر کی طرح) تحریر کردیا ہے اور فیض غیبی سے ان کی مدد کی ہے۔ اور وہ ان کو بہشتوں میں جن کے تلے نہریں بہہ رہی ہیں داخل کرے گا ہمیشہ ان میں رہیں گے۔ خدا ان سے خوش اور وہ خدا سے خوش۔ یہی گروہ خدا کا لشکر ہے۔ (اور) سن رکھو کہ خدا ہی کا لشکر مراد حاصل کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(59,1,'59:1','جو چیزیں آسمانوں میں ہیں اور جو چیزیں زمین میں ہیں (سب) خدا کی تسبیح کرتی ہیں۔ اور وہ غالب حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(59,2,'59:2','وہی تو ہے جس نے کفار اہل کتاب کو حشر اول کے وقت ان کے گھروں سے نکال دیا۔ تمہارے خیال میں بھی نہ تھا کہ وہ نکل جائیں گے اور وہ لوگ یہ سمجھے ہوئے تھے کہ ان کے قلعے ان کو خدا (کے عذاب) سے بچا لیں گے۔ مگر خدا نے ان کو وہاں سے آ لیا جہاں سے ان کو گمان بھی نہ تھا۔ اور ان کے دلوں میں دہشت ڈال دی کہ اپنے گھروں کو خود اپنے ہاتھوں اور مومنوں کے ہاتھوں سے اُجاڑنے لگے تو اے (بصیرت کی) آنکھیں رکھنے والو عبرت پکڑو');
INSERT INTO "jalandhari" VALUES(59,3,'59:3','اور اگر خدا نے ان کے بارے میں جلاوطن کرنا نہ لکھ رکھا ہوتا تو ان کو دنیا میں بھی عذاب دے دیتا۔ اور آخرت میں تو ان کے لئے آگ کا عذاب (تیار) ہے');
INSERT INTO "jalandhari" VALUES(59,4,'59:4','یہ اس لئے کہ انہوں نے خدا اور اس کے رسول کی مخالفت کی۔ اور جو شخص خدا کی مخالفت کرے تو خدا سخت عذاب دینے والا ہے');
INSERT INTO "jalandhari" VALUES(59,5,'59:5','(مومنو) کھجور کے جو درخت تم نے کاٹ ڈالے یا ان کو اپنی جڑوں پر کھڑا رہنے دیا سو خدا کے حکم سے تھا اور مقصود یہ تھا کہ وہ نافرمانوں کو رسوا کرے');
INSERT INTO "jalandhari" VALUES(59,6,'59:6','اور جو (مال) خدا نے اپنے پیغمبر کو ان لوگوں سے (بغیر لڑائی بھڑائی کے) دلوایا ہے اس میں تمہارا کچھ حق نہیں کیونکہ اس کے لئے نہ تم نے گھوڑے دوڑائے نہ اونٹ لیکن خدا اپنے پیغمبروں کو جن پر چاہتا ہے مسلط کردیتا ہے۔ اور خدا ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(59,7,'59:7','جو مال خدا نے اپنے پیغمبر کو دیہات والوں سے دلوایا ہے وہ خدا کے اور پیغمبر کے اور (پیغمبر کے) قرابت والوں کے اور یتیموں کے اور حاجتمندوں کے اور مسافروں کے لئے ہے۔ تاکہ جو لوگ تم میں دولت مند ہیں ان ہی کے ہاتھوں میں نہ پھرتا رہے۔ سو جو چیز تم کو پیغمبر دیں وہ لے لو۔ اور جس سے منع کریں (اس سے) باز رہو۔ اور خدا سے ڈرتے رہو۔ بےشک خدا سخت عذاب دینے والا ہے');
INSERT INTO "jalandhari" VALUES(59,8,'59:8','(اور) ان مفلسان تارک الوطن کے لئے بھی جو اپنے گھروں اور مالوں سے خارج (اور جدا) کر دیئے گئے ہیں (اور) خدا کے فضل اور اس کی خوشنودی کے طلبگار اور خدا اور اس کے پیغمبر کے مددگار ہیں۔ یہی لوگ سچے (ایماندار) ہیں');
INSERT INTO "jalandhari" VALUES(59,9,'59:9','اور (ان لوگوں کے لئے بھی) جو مہاجرین سے پہلے (ہجرت کے) گھر (یعنی مدینے) میں مقیم اور ایمان میں (مستقل) رہے (اور) جو لوگ ہجرت کرکے ان کے پاس آتے ہیں ان سے محبت کرتے ہیں اور جو کچھ ان کو ملا اس سے اپنے دل میں کچھ خواہش (اور خلش) نہیں پاتے اور ان کو اپنی جانوں سے مقدم رکھتے ہیں خواہ ان کو خود احتیاج ہی ہو۔ اور جو شخص حرص نفس سے بچا لیا گیا تو ایسے لوگ مراد پانے والے ہیں');
INSERT INTO "jalandhari" VALUES(59,10,'59:10','اور (ان کے لئے بھی) جو ان (مہاجرین) کے بعد آئے (اور) دعا کرتے ہیں کہ اے پروردگار ہمارے اور ہمارے بھائیوں کے جو ہم سے پہلے ایمان لائے ہیں گناہ معاف فرما اور مومنوں کی طرف سے ہمارے دل میں کینہ (وحسد) نہ پیدا ہونے دے۔ اے ہمارے پروردگار تو بڑا شفقت کرنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(59,11,'59:11','کیا تم نے ان منافقوں کو نہیں دیکھا جو اپنے کافر بھائیوں سے جو اہل کتاب ہیں کہا کرتے ہیں کہ اگر تم جلا وطن کئے گئے تو ہم بھی تمہارے ساتھ نکل چلیں گے اور تمہارے بارے میں کبھی کسی کا کہا نہ مانیں گے۔ اور اگر تم سے جنگ ہوئی تو تمہاری مدد کریں گے۔ مگر خدا ظاہر کئے دیتا ہے کہ یہ جھوٹے ہیں');
INSERT INTO "jalandhari" VALUES(59,12,'59:12','اگر وہ نکالے گئے تو یہ ان کے ساتھ نہیں نکلیں گے۔ اور اگر ان سے جنگ ہوئی تو ان کی مدد نہیں کریں گے۔ اگر مدد کریں گے تو پیٹھ پھیر کر بھاگ جائیں گے۔ پھر ان کو (کہیں سے بھی) مدد نہ ملے گی');
INSERT INTO "jalandhari" VALUES(59,13,'59:13','(مسلمانو!) تمہاری ہیبت ان لوگوں کے دلوں میں خدا سے بھی بڑھ کر ہے۔ یہ اس لئے کہ یہ سمجھ نہیں رکھتے');
INSERT INTO "jalandhari" VALUES(59,14,'59:14','یہ سب جمع ہو کر بھی تم سے (بالمواجہہ) نہیں لڑ سکیں گے مگر بستیوں کے قلعوں میں (پناہ لے کر) یا دیواروں کی اوٹ میں (مستور ہو کر) ان کا آپس میں بڑا رعب ہے۔ تم شاید خیال کرتے ہو کہ یہ اکھٹے (اور ایک جان) ہیں مگر ان کے دل پھٹے ہوئے ہیں یہ اس لئے کہ یہ بےعقل لوگ ہیں');
INSERT INTO "jalandhari" VALUES(59,15,'59:15','ان کا حال ان لوگوں کا سا ہے جو ان سے کچھ ہی پیشتر اپنے کاموں کی سزا کا مزہ چکھ چکے ہیں۔ اور (ابھی) ان کے لئے دکھ دینے والا عذاب (تیار) ہے');
INSERT INTO "jalandhari" VALUES(59,16,'59:16','منافقوں کی) مثال شیطان کی سی ہے کہ انسان سے کہتا رہا کہ کافر ہوجا۔ جب وہ کافر ہوگیا تو کہنے لگا کہ مجھے تجھ سے کچھ سروکار نہیں۔ مجھ کو خدائے رب العالمین سے ڈر لگتا ہے');
INSERT INTO "jalandhari" VALUES(59,17,'59:17','تو دونوں کا انجام یہ ہوا کہ دونوں دوزخ میں (داخل ہوئے) ہمیشہ اس میں رہیں گے۔ اور بےانصافوں کی یہی سزا ہے');
INSERT INTO "jalandhari" VALUES(59,18,'59:18','اے ایمان والوں! خدا سے ڈرتے رہو اور ہر شخص کو دیکھنا چاہیئے کہ اس نے کل (یعنی فردائے قیامت) کے لئے کیا (سامان) بھیجا ہے اور (ہم پھر کہتے ہیں کہ) خدا سے ڈرتے رہو بےشک خدا تمہارے سب اعمال سے خبردار ہے');
INSERT INTO "jalandhari" VALUES(59,19,'59:19','اور ان لوگوں جیسے نہ ہونا جنہوں نے خدا کو بھلا دیا تو خدا نے انہیں ایسا کردیا کہ خود اپنے تئیں بھول گئے۔ یہ بدکردار لوگ ہیں');
INSERT INTO "jalandhari" VALUES(59,20,'59:20','اہل دوزخ اور اہل بہشت برابر نہیں۔ اہل بہشت تو کامیابی حاصل کرنے والے ہیں');
INSERT INTO "jalandhari" VALUES(59,21,'59:21','اگر ہم یہ قرآن کسی پہاڑ پر نازل کرتے تو تم اس کو دیکھتے کہ خدا کے خوف سے دبا اور پھٹا جاتا ہے۔ اور یہ باتیں ہم لوگوں کے لئے بیان کرتے ہیں تاکہ وہ فکر کریں');
INSERT INTO "jalandhari" VALUES(59,22,'59:22','وہی خدا ہے جس کے سوا کوئی معبود نہیں۔ پوشیدہ اور ظاہر کا جاننے والا ہے وہ بڑا مہربان نہایت رحم والا ہے');
INSERT INTO "jalandhari" VALUES(59,23,'59:23','وہی خدا ہے جس کے سوا کوئی عبادت کے لائق نہیں۔ بادشاہ (حقیقی) پاک ذات (ہر عیب سے) سلامتی امن دینے والا نگہبان غالب زبردست بڑائی والا۔ خدا ان لوگوں کے شریک مقرر کرنے سے پاک ہے');
INSERT INTO "jalandhari" VALUES(59,24,'59:24','وہی خدا (تمام مخلوقات کا) خالق۔ ایجاد واختراع کرنے والا صورتیں بنانے والا اس کے سب اچھے سے اچھے نام ہیں۔ جتنی چیزیں آسمانوں اور زمین میں ہیں سب اس کی تسبیح کرتی ہیں اور وہ غالب حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(60,1,'60:1','مومنو! اگر تم میری راہ میں لڑنے اور میری خوشنودی طلب کرنے کے لئے (مکے سے) نکلے ہو تو میرے اور اپنے دشمنوں کو دوست نہ بناؤ۔ تم تو ان کو دوستی کے پیغام بھیجتے ہو اور وہ (دین) حق سے جو تمہارے پاس آیا ہے منکر ہیں۔ اور اس باعث سے کہ تم اپنے پروردگار خدا تعالیٰ پر ایمان لائے ہو پیغمبر کو اور تم کو جلاوطن کرتے ہیں۔ تم ان کی طرف پوشیدہ پوشیدہ دوستی کے پیغام بھیجتے ہو۔ اور جو کچھ تم مخفی طور پر اور جو علیٰ الاعلان کرتے ہو وہ مجھے معلوم ہے۔ اور جو کوئی تم میں سے ایسا کرے گا وہ سیدھے راستے سے بھٹک گیا');
INSERT INTO "jalandhari" VALUES(60,2,'60:2','اگر یہ کافر تم پر قدرت پالیں تو تمہارے دشمن ہوجائیں اور ایذا کے لئے تم پر ہاتھ (بھی) چلائیں اور زبانیں (بھی) اور چاہتے ہیں کہ تم کسی طرح کافر ہوجاؤ');
INSERT INTO "jalandhari" VALUES(60,3,'60:3','قیامت کے دن نہ تمہارے رشتے ناتے کام آئیں گے اور نہ اولاد۔ اس روز وہی تم میں فیصلہ کرے گا۔ اور جو کچھ تم کرتے ہو خدا اس کو دیکھتا ہے');
INSERT INTO "jalandhari" VALUES(60,4,'60:4','تمہیں ابراہیم اور ان کے رفقاء کی نیک چال چلنی (ضرور) ہے۔ جب انہوں نے اپنی قوم کے لوگوں سے کہا کہ ہم تم سے اور ان (بتوں) سے جن کو تم خدا کے سوا پوجتے ہو بےتعلق ہیں (اور) تمہارے (معبودوں کے کبھی) قائل نہیں (ہوسکتے) اور جب تک تم خدائے واحد اور ایمان نہ لاؤ ہم میں تم میں ہمیشہ کھلم کھلا عداوت اور دشمنی رہے گی۔ ہاں ابراہیمؑ نے اپنے باپ سے یہ (ضرور) کہا کہ میں آپ کے لئے مغفرت مانگوں گا اور خدا کے سامنے آپ کے بارے میں کسی چیز کا کچھ اختیار نہیں رکھتا۔ اے ہمارے پروردگار تجھ ہی پر ہمارا بھروسہ ہے اور تیری ہی طرف ہم رجوع کرتے ہیں اور تیرے ہی حضور میں (ہمیں) لوٹ کر آنا ہے');
INSERT INTO "jalandhari" VALUES(60,5,'60:5','اے ہمارے پروردگار ہم کو کافروں کے ہاتھ سے عذاب نہ دلانا اور اے پروردگار ہمارے ہمیں معاف فرما۔ بےشک تو غالب حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(60,6,'60:6','تم (مسلمانوں) کو یعنی جو کوئی خدا (کے سامنے جانے) اور روز آخرت (کے آنے) کی امید رکھتا ہو اسے ان لوگوں کی نیک چال چلنی (ضرور) ہے۔ اور روگردانی کرے تو خدا بھی بےپرواہ اور سزاوار حمد (وثنا) ہے');
INSERT INTO "jalandhari" VALUES(60,7,'60:7','عجب نہیں کہ خدا تم میں اور ان لوگوں میں جن سے تم دشمنی رکھتے ہو دوستی پیدا کردے۔ اور خدا قادر ہے اور خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(60,8,'60:8','جن لوگوں نے تم سے دین کے بارے میں جنگ نہیں کی اور نہ تم کو تمہارے گھروں سے نکالا ان کے ساتھ بھلائی اور انصاف کا سلوک کرنے سے خدا تم کو منع نہیں کرتا۔ خدا تو انصاف کرنے والوں کو دوست رکھتا ہے');
INSERT INTO "jalandhari" VALUES(60,9,'60:9','خدا ان ہی لوگوں کے ساتھ تم کو دوستی کرنے سے منع کرتا ہے جنہوں نے تم سے دین کے بارے میں لڑائی کی اور تم کو تمہارے گھروں سے نکالا اور تمہارے نکالنے میں اوروں کی مدد کی۔ تو جو لوگ ایسوں سے دوستی کریں گے وہی ظالم ہیں');
INSERT INTO "jalandhari" VALUES(60,10,'60:10','مومنو! جب تمہارے پاس مومن عورتیں وطن چھوڑ کر آئیں تو ان کی آزمائش کرلو۔ (اور) خدا تو ان کے ایمان کو خوب جانتا ہے۔ سو اگر تم کو معلوم ہو کہ مومن ہیں تو ان کو کفار کے پاس واپس نہ بھیجو۔ کہ نہ یہ ان کو حلال ہیں اور نہ وہ ان کو جائز۔ اور جو کچھ انہوں نے (ان پر) خرچ کیا ہو وہ ان کو دے دو۔ اور تم پر کچھ گناہ نہیں کہ ان عورتوں کو مہر دے کر ان سے نکاح کرلو اور کافر عورتوں کی ناموس کو قبضے میں نہ رکھو (یعنی کفار کو واپس دے دو) اور جو کچھ تم نے ان پر خرچ کیا ہو تم ان سے طلب کرلو اور جو کچھ انہوں نے (اپنی عورتوں پر) خرچ کیا ہو وہ تم سے طلب کرلیں۔ یہ خدا کا حکم ہے جو تم میں فیصلہ کئے دیتا ہے اور خدا جاننے والا حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(60,11,'60:11','اور اگر تمہاری عورتوں میں سے کوئی عورت تمہارے ہاتھ سے نکل کر کافروں کے پاس چلی جائے (اور اس کا مہر وصول نہ ہوا ہو) پھر تم ان سے جنگ کرو (اور ان سے تم کو غنیمت ہاتھ لگے) تو جن کی عورتیں چلی گئی ہیں ان کو (اس مال میں سے) اتنا دے دو جتنا انہوں نے خرچ کیا تھا اور خدا سے جس پر تم ایمان لائے ہو ڈرو');
INSERT INTO "jalandhari" VALUES(60,12,'60:12','اے پیغمبر! جب تمہارے پاس مومن عورتیں اس بات پر بیعت کرنے کو آئیں کہ خدا کے ساتھ نہ شرک کریں گی نہ چوری کریں گی نہ بدکاری کریں گی نہ اپنی اولاد کو قتل کریں گی نہ اپنے ہاتھ پاؤں میں کوئی بہتان باندھ لائیں گی اور نہ نیک کاموں میں تمہاری نافرمانی کریں گی تو ان سے بیعت لے لو اور ان کے لئے خدا سے بخشش مانگو۔ بےشک خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(60,13,'60:13','مومنو! ان لوگوں سے جن پر خدا غصے ہوا ہے دوستی نہ کرو (کیونکہ) جس طرح کافروں کو مردوں (کے جی اُٹھنے) کی امید نہیں اسی طرح ان لوگوں کو بھی آخرت (کے آنے) کی امید نہیں');
INSERT INTO "jalandhari" VALUES(61,1,'61:1','جو چیز آسمانوں میں ہے اور زمین میں ہے سب خدا کی تنزیہ کرتی ہے اور وہ غالب حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(61,2,'61:2','مومنو! تم ایسی باتیں کیوں کہا کرتے ہو جو کیا نہیں کرتے');
INSERT INTO "jalandhari" VALUES(61,3,'61:3','خدا اس بات سے سخت بیزار ہے کہ ایسی بات کہو جو کرو نہیں');
INSERT INTO "jalandhari" VALUES(61,4,'61:4','جو لوگ خدا کی راہ میں (ایسے طور پر) پرے جما کر لڑتے کہ گویا سیسہ پلائی دیوار ہیں وہ بےشک محبوب کردگار ہیں');
INSERT INTO "jalandhari" VALUES(61,5,'61:5','اور وہ وقت یاد کرنے کے لائق ہے جب موسیٰ نے اپنی قوم سے کہا کہ اے قوم! تم مجھے کیوں ایذا دیتے ہو حالانکہ تم جانتے ہو کہ میں تمہارے پاس خدا کا بھیجا ہوا آیا ہوں۔ تو جب ان لوگوں نے کج روی کی خدا نے بھی ان کے دل ٹیڑھے کردیئے۔ اور خدا نافرمانوں کو ہدایت نہیں دیتا');
INSERT INTO "jalandhari" VALUES(61,6,'61:6','اور (وہ وقت بھی یاد کرو) جب مریمؑ کے بیٹے عیسیٰ نے کہا کے اے بنی اسرائیل میں تمہارے پاس خدا کا بھیجا ہوا آیا ہوں (اور) جو (کتاب) مجھ سے پہلے آچکی ہے (یعنی) تورات اس کی تصدیق کرتا ہوں اور ایک پیغمبر جو میرے بعد آئیں گے جن کا نام احمدصلى الله عليه وسلم ہوگا ان کی بشارت سناتا ہوں۔ (پھر) جب وہ ان لوگوں کے پاس کھلی نشانیاں لے کر آئے تو کہنے لگے کہ یہ تو صریح جادو ہے');
INSERT INTO "jalandhari" VALUES(61,7,'61:7','اور اس سے ظالم کون کہ بلایا تو جائے اسلام کی طرف اور وہ خدا پر جھوٹ بہتان باندھے۔ اور خدا ظالم لوگوں کو ہدایت نہیں دیا کرتا');
INSERT INTO "jalandhari" VALUES(61,8,'61:8','یہ چاہتے ہیں کہ خدا کے (چراغ) کی روشنی کو منہ سے (پھونک مار کر) بجھا دیں۔ حالانکہ خدا اپنی روشنی کو پورا کرکے رہے گا خواہ کافر ناخوش ہی ہوں');
INSERT INTO "jalandhari" VALUES(61,9,'61:9','وہی تو ہے جس نے اپنے پیغمبر کو ہدایت اور دین حق دے کر بھیجا تاکہ اسے اور سب دینوں پر غالب کرے خواہ مشرکوں کو برا ہی لگے');
INSERT INTO "jalandhari" VALUES(61,10,'61:10','مومنو! میں تم کو ایسی تجارت بتاؤں جو تمہیں عذاب الیم سے مخلصی دے');
INSERT INTO "jalandhari" VALUES(61,11,'61:11','(وہ یہ کہ) خدا پر اور اس کے رسولصلى الله عليه وسلم پر ایمان لاؤ اور خدا کی راہ میں اپنے مال اور جان سے جہاد کرو۔ اگر سمجھو تو یہ تمہارے حق میں بہتر ہے');
INSERT INTO "jalandhari" VALUES(61,12,'61:12','وہ تمہارے گناہ بخش دے گا اور تم کو باغہائے جنت میں جن میں نہریں بہہ رہیں ہیں اور پاکیزہ مکانات میں جو بہشت ہائے جاودانی میں (تیار) ہیں داخل کرے گا۔ یہ بڑی کامیابی ہے');
INSERT INTO "jalandhari" VALUES(61,13,'61:13','اور ایک اور چیز جس کو تم بہت چاہتے ہو (یعنی تمہیں) خدا کی طرف سے مدد (نصیب ہوگی) اور فتح عنقریب ہوگی اور مومنوں کو (اس کی) خوشخبری سنا دو');
INSERT INTO "jalandhari" VALUES(61,14,'61:14','مومنو! خدا کے مددگار بن جاؤ جیسے عیسیٰ ابن مریم نے حواریوں سے کہا کہ بھلا کون ہیں جو خدا کی طرف (بلانے میں) میرے مددگار ہوں۔ حواریوں نے کہا کہ ہم خدا کے مددگار ہیں۔ تو بنی اسرائیل میں سے ایک گروہ تو ایمان لے آیا اور ایک گروہ کافر رہا۔ آخر الامر ہم نے ایمان لانے والوں کو ان کے دشمنوں کے مقابلے میں مدد دی اور وہ غالب ہوگئے');
INSERT INTO "jalandhari" VALUES(62,1,'62:1','جو چیز آسمانوں میں ہے اور جو چیز زمین میں ہے سب خدا کی تسبیح کرتی ہے جو بادشاہ حقیقی پاک ذات زبردست حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(62,2,'62:2','وہی تو ہے جس نے ان پڑھوں میں ان ہی میں سے (محمدصلى الله عليه وسلم) کو پیغمبر (بنا کر) بھیجا جو ان کے سامنے اس کی آیتیں پڑھتے اور ان کو پاک کرتے اور (خدا کی) کتاب اور دانائی سکھاتے ہیں۔ اور اس ے پہلے تو یہ لوگ صریح گمراہی میں تھے');
INSERT INTO "jalandhari" VALUES(62,3,'62:3','اور ان میں سے اور لوگوں کی طرف بھی (ان کو بھیجا ہے) جو ابھی ان (مسلمانوں سے) نہیں ملے۔ اور وہ غالب حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(62,4,'62:4','یہ خدا کا فضل ہے جسے چاہتا ہے عطا کرتا ہے۔ اور خدا بڑے فضل کا مالک ہے');
INSERT INTO "jalandhari" VALUES(62,5,'62:5','جن لوگوں (کے سر) پر تورات لدوائی گئی پھر انہوں نے اس (کے بار تعمیل) کو نہ اٹھایا ان کی مثال گدھے کی سی ہے جن پر بڑی بڑی کتابیں لدی ہوں۔ جو لوگ خدا کی آیتوں کی تکذیب کرتے ہیں ان کی مثال بری ہے۔ اور خدا ظالم لوگوں کو ہدایت نہیں دیتا');
INSERT INTO "jalandhari" VALUES(62,6,'62:6','کہہ دو کہ اے یہود اگر تم کو دعویٰ ہو کہ تم ہی خدا کے دوست ہو اور لوگ نہیں تو اگر تم سچے ہو تو (ذرا) موت کی آرزو تو کرو');
INSERT INTO "jalandhari" VALUES(62,7,'62:7','اور یہ ان (اعمال) کے سبب جو کرچکے ہیں ہرگز اس کی آرزو نہیں کریں گے۔ اور خدا ظالموں سے خوب واقف ہے');
INSERT INTO "jalandhari" VALUES(62,8,'62:8','کہہ دو کہ موت جس سے تم گریز کرتے ہو وہ تو تمہارے سامنے آ کر رہے گی۔ پھر تم پوشیدہ اور ظاہر کے جاننے والے (خدا) کی طرف لوٹائے جاؤ گے پھر جو کچھ تم کرتے رہے ہو وہ سب تمہیں بتائے گا');
INSERT INTO "jalandhari" VALUES(62,9,'62:9','مومنو! جب جمعے کے دن نماز کے لئے اذان دی جائے تو خدا کی یاد (یعنی نماز) کے لئے جلدی کرو اور (خریدو) فروخت ترک کردو۔ اگر سمجھو تو یہ تمہارے حق میں بہتر ہے');
INSERT INTO "jalandhari" VALUES(62,10,'62:10','پھر جب نماز ہوچکے تو اپنی اپنی راہ لو اور خدا کا فضل تلاش کرو اور خدا کو بہت بہت یاد کرتے رہو تاکہ نجات پاؤ');
INSERT INTO "jalandhari" VALUES(62,11,'62:11','اور جب یہ لوگ سودا بکتا یا تماشا ہوتا دیکھتے ہیں تو ادھر بھاگ جاتے ہیں اور تمہیں (کھڑے کا) کھڑا چھوڑ جاتے ہیں۔ کہہ دو کہ جو چیز خدا کے ہاں ہے وہ تماشے اور سودے سے کہیں بہتر ہے اور خدا سب سے بہتر رزق دینے والا ہے');
INSERT INTO "jalandhari" VALUES(63,1,'63:1','(اے محمدصلى الله عليه وسلم) جب منافق لوگ تمہارے پاس آتے ہیں تو (از راہ نفاق) کہتے ہیں کہ ہم اقرار کرتے ہیں کہ آپ بےشک خدا کے پیغمبر ہیں اور خدا جانتا ہے کہ درحقیقت تم اس کے پیغمبر ہو لیکن خدا ظاہر کئے دیتا ہے کہ منافق (دل سے اعتقاد نہ رکھنے کے لحاظ سے) جھوٹے ہیں');
INSERT INTO "jalandhari" VALUES(63,2,'63:2','انہوں نے اپنی قسموں کو ڈھال بنا رکھا ہے اور ان کے ذریعے سے (لوگوں کو) راہ خدا سے روک رہے ہیں۔ کچھ شک نہیں کہ جو کام یہ کرتے ہیں برے ہیں');
INSERT INTO "jalandhari" VALUES(63,3,'63:3','یہ اس لئے کہ یہ (پہلے تو) ایمان لائے پھر کافر ہوگئے تو ان کے دلوں پر مہر لگادی گئی۔ سو اب یہ سمجھتے ہی نہیں');
INSERT INTO "jalandhari" VALUES(63,4,'63:4','اور جب تم ان (کے تناسب اعضا) کو دیکھتے ہو تو ان کے جسم تمہیں (کیا ہی) اچھے معلوم ہوتے ہیں۔ اور جب وہ گفتگو کرتے ہیں تو تم ان کی تقریر کو توجہ سے سنتے ہو (مگر فہم وادراک سے خالی) گویا لکڑیاں ہیں جو دیواروں سے لگائی گئی ہیں۔ (بزدل ایسے کہ) ہر زور کی آواز کو سمجھیں (کہ) ان پر بلا آئی۔ یہ (تمہارے) دشمن ہیں ان سے بےخوف نہ رہنا۔ خدا ان کو ہلاک کرے۔ یہ کہاں بہکے پھرتے ہیں');
INSERT INTO "jalandhari" VALUES(63,5,'63:5','اور جب ان سے کہا جائے کہ آؤ رسول خدا تمہارے لئے مغفرت مانگیں تو سر ہلا دیتے ہیں اور تم ان کو دیکھو کہ تکبر کرتے ہوئے منہ پھیر لیتے ہیں');
INSERT INTO "jalandhari" VALUES(63,6,'63:6','تم ان کے لئے مغفرت مانگو یا نہ مانگو ان کے حق میں برابر ہے۔ خدا ان کو ہرگز نہ بخشے گا۔ بےشک خدا نافرمانوں کو ہدایت نہیں دیا کرتا');
INSERT INTO "jalandhari" VALUES(63,7,'63:7','یہی ہیں جو کہتے ہیں کہ جو لوگ رسول خدا کے پاس (رہتے) ہیں ان پر (کچھ) خرچ نہ کرو۔ یہاں تک کہ یہ (خود بخود) بھاگ جائیں۔ حالانکہ آسمانوں اور زمین کے خزانے خدا ہی کہ ہیں لیکن منافق نہیں سمجھتے');
INSERT INTO "jalandhari" VALUES(63,8,'63:8','کہتے ہیں کہ اگر ہم لوٹ کر مدینے پہنچے تو عزت والے ذلیل لوگوں کو وہاں سے نکال باہر کریں گے۔ حالانکہ عزت خدا کی ہے اور اس کے رسول کی اور مومنوں کی لیکن منافق نہیں جانتے');
INSERT INTO "jalandhari" VALUES(63,9,'63:9','مومنو! تمہارا مال اور اولاد تم کو خدا کی یاد سے غافل نہ کردے۔ اور جو ایسا کرے گا تو وہ لوگ خسارہ اٹھانے والے ہیں');
INSERT INTO "jalandhari" VALUES(63,10,'63:10','اور جو (مال) ہم نے تم کو دیا ہے اس میں سے اس (وقت) سے پیشتر خرچ کرلو کہ تم میں سے کسی کی موت آجائے تو (اس وقت) کہنے لگے کہ اے میرے پروردگار تو نے مجھے تھوڑی سی اور مہلت کیوں نہ دی تاکہ میں خیرات کرلیتا اور نیک لوگوں میں داخل ہوجاتا');
INSERT INTO "jalandhari" VALUES(63,11,'63:11','اور جب کسی کی موت آجاتی ہے تو خدا اس کو ہرگز مہلت نہیں دیتا اور جو کچھ تم کرتے ہو خدا اس سے خبردار ہے');
INSERT INTO "jalandhari" VALUES(64,1,'64:1','جو چیز آسمانوں میں ہے اور جو چیز زمین میں ہے (سب) خدا کی تسبیح کرتی ہے۔ اسی کی سچی بادشاہی ہے اور اسی کی تعریف (لامتناہی) ہے اور وہ ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(64,2,'64:2','وہی تو ہے جس نے تم کو پیدا کیا پھر کوئی تم میں کافر ہے اور کوئی مومن۔ اور جو کچھ تم کرتے ہو خدا اس کو دیکھتا ہے');
INSERT INTO "jalandhari" VALUES(64,3,'64:3','اسی نے آسمانوں اور زمین کو مبنی برحکمت پیدا کیا اور اسی نے تمہاری صورتیں بنائیں اور صورتیں بھی پاکیزہ بنائیں۔ اور اسی کی طرف (تمہیں) لوٹ کر جانا ہے');
INSERT INTO "jalandhari" VALUES(64,4,'64:4','جو کچھ آسمانوں اور زمین میں ہے وہ سب جانتا ہے اور جو کچھ تم چھپا کر کرتے ہو اور جو کھلم کھلا کرتے ہو اس سے بھی آگاہ ہے۔ اور خدا دل کے بھیدوں سے واقف ہے');
INSERT INTO "jalandhari" VALUES(64,5,'64:5','کیا تم کو ان لوگوں کے حال کی خبر نہیں پہنچی جو پہلے کافر ہوئے تھے تو انہوں نے اپنے کاموں کی سزا کا مزہ چکھ لیا اور (ابھی) دکھ دینے والا عذاب (اور) ہونا ہے');
INSERT INTO "jalandhari" VALUES(64,6,'64:6','یہ اس لئے کہ ان کے پاس پیغمبر کھلی نشانیاں لے کر آئے تو یہ کہتے کہ کیا آدمی ہمارے ہادی بنتے ہیں؟ تو انہوں نے (ان کو) نہ مانا اور منہ پھیر لیا اور خدا نے بھی بےپروائی کی۔ اور خدا بےپروا (اور) سزاوار حمد (وثنا) ہے');
INSERT INTO "jalandhari" VALUES(64,7,'64:7','جو لوگ کافر ہیں ان کا اعتقاد ہے کہ وہ (دوبارہ) ہرگز نہیں اٹھائے جائیں گے۔ کہہ دو کہ ہاں ہاں میرے پروردگار کی قسم تم ضرور اٹھائے جاؤ گے پھر جو کام تم کرتے رہے ہو وہ تمہیں بتائے جائیں گے اور یہ (بات) خدا کو آسان ہے');
INSERT INTO "jalandhari" VALUES(64,8,'64:8','تو خدا پر اور اس کے رسول پر اور نور (قرآن) پر جو ہم نے نازل فرمایا ہے ایمان لاؤ۔ اور خدا تمہارے سب اعمال سے خبردار ہے');
INSERT INTO "jalandhari" VALUES(64,9,'64:9','جس دن وہ تم کو اکھٹا ہونے (یعنی قیامت) کے دن اکھٹا کرے گا وہ نقصان اٹھانے کا دن ہے۔ اور جو شخص خدا پر ایمان لائے اور نیک عمل کرے وہ اس سے اس کی برائیاں دور کردے گا اور باغہائے بہشت میں جن کے نیچے نہریں بہہ رہی ہیں داخل کرے گا۔ ہمیشہ ان میں رہیں گے۔ یہ بڑی کامیابی ہے');
INSERT INTO "jalandhari" VALUES(64,10,'64:10','اور جنہوں نے کفر کیا اور ہماری آیتوں کو جھٹلایا وہی اہل ذوزخ ہیں۔ ہمیشہ اس میں رہیں گے۔ اور وہ بری جگہ ہے');
INSERT INTO "jalandhari" VALUES(64,11,'64:11','کوئی مصیبت نازل نہیں ہوتی مگر خدا کے حکم سے۔ اور جو شخص خدا پر ایمان لاتا ہے وہ اس کے دل کو ہدایت دیتا ہے۔ اور خدا ہر چیز سے باخبر ہے');
INSERT INTO "jalandhari" VALUES(64,12,'64:12','اور خدا کی اطاعت کرو اور اس کے رسول کی اطاعت کرو۔ اگر تم منہ پھیر لو گے تو ہمارے پیغمبر کے ذمے تو صرف پیغام کا کھول کھول کر پہنچا دینا ہے');
INSERT INTO "jalandhari" VALUES(64,13,'64:13','خدا (جو معبود برحق ہے اس) کے سوا کوئی عبادت کے لائق نہیں تو مومنوں کو چاہیئے کہ خدا ہی پر بھروسا رکھیں');
INSERT INTO "jalandhari" VALUES(64,14,'64:14','مومنو! تمہاری عورتیں اور اولاد میں سے بعض تمہارے دشمن (بھی) ہیں سو ان سے بچتے رہو۔ اور اگر معاف کردو اور درگزر کرو اور بخش دو تو خدا بھی بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(64,15,'64:15','تمہارا مال اور تمہاری اولاد تو آزمائش ہے۔ اور خدا کے ہاں بڑا اجر ہے');
INSERT INTO "jalandhari" VALUES(64,16,'64:16','سو جہاں تک ہوسکے خدا سے ڈرو اور (اس کے احکام کو) سنو اور (اس کے) فرمانبردار رہو اور (اس کی راہ میں) خرچ کرو (یہ) تمہارے حق میں بہتر ہے۔ اور جو شخص طبعیت کے بخل سے بچایا گیا تو ایسے ہی لوگ راہ پانے والے ہیں');
INSERT INTO "jalandhari" VALUES(64,17,'64:17','اگر تم خدا کو (اخلاص اور نیت) نیک (سے) قرض دو گے تو وہ تم کو اس کا دوچند دے گا اور تمہارے گناہ بھی معاف کردے گا۔ اور خدا قدر شناس اور بردبار ہے');
INSERT INTO "jalandhari" VALUES(64,18,'64:18','پوشیدہ اور ظاہر کا جاننے والا غالب اور حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(65,1,'65:1','اے پیغمبر (مسلمانوں سے کہہ دو کہ) جب تم عورتوں کو طلاق دینے لگو تو عدت کے شروع میں طلاق دو اور عدت کا شمار رکھو۔ اور خدا سے جو تمہارا پروردگار ہے ڈرو۔ (نہ تو تم ہی) ان کو (ایام عدت میں) ان کے گھروں سے نکالو اور نہ وہ (خود ہی) نکلیں۔ ہاں اگر وہ صریح بےحیائی کریں (تو نکال دینا چاہیئے) اور یہ خدا کی حدیں ہیں۔ جو خدا کی حدوں سے تجاوز کرے گا وہ اپنے آپ پر ظلم کرے گا۔ (اے طلاق دینے والے) تجھے کیا معلوم شاید خدا اس کے بعد کوئی (رجعت کی) سبیل پیدا کردے');
INSERT INTO "jalandhari" VALUES(65,2,'65:2','پھر جب وہ اپنی میعاد (یعنی انقضائے عدت) کے قریب پہنچ جائیں تو یا تو ان کو اچھی طرح (زوجیت میں) رہنے دو یا اچھی طرح سے علیحدہ کردو اور اپنے میں سے دو منصف مردوں کو گواہ کرلو اور (گواہ ہو!) خدا کے لئے درست گواہی دینا۔ ان باتوں سے اس شخص کو نصیحت کی جاتی ہے جو خدا پر اور روز آخرت پر ایمان رکھتا ہے۔ اور جو کوئی خدا سے ڈرے گا وہ اس کے لئے (رنج ومحن سے) مخلصی (کی صورت) پیدا کرے گا');
INSERT INTO "jalandhari" VALUES(65,3,'65:3','اور اس کو ایسی جگہ سے رزق دے گا جہاں سے (وہم و) گمان بھی نہ ہو۔ اور جو خدا پر بھروسہ رکھے گا تو وہ اس کو کفایت کرے گا۔ خدا اپنے کام کو (جو وہ کرنا چاہتا ہے) پورا کردیتا ہے۔ خدا نے ہر چیز کا اندازہ مقرر کر رکھا ہے');
INSERT INTO "jalandhari" VALUES(65,4,'65:4','اور تمہاری (مطلقہ) عورتیں جو حیض سے ناامید ہوچکی ہوں اگر تم کو (ان کی عدت کے بارے میں) شبہ ہو تو ان کی عدت تین مہینے ہے اور جن کو ابھی حیض نہیں آنے لگا (ان کی عدت بھی یہی ہے) اور حمل والی عورتوں کی عدت وضع حمل (یعنی بچّہ جننے) تک ہے۔ اور جو خدا سے ڈرے گا خدا اس کے کام میں سہولت پیدا کردے گا');
INSERT INTO "jalandhari" VALUES(65,5,'65:5','یہ خدا کے حکم ہیں جو خدا نے تم پر نازل کئے ہیں۔ اور جو خدا سے ڈرے گا وہ اس سے اس کے گناہ دور کر دے گا اور اسے اجر عظیم بخشے گا');
INSERT INTO "jalandhari" VALUES(65,6,'65:6','عورتوں کو (ایام عدت میں) اپنے مقدور کے مطابق وہیں رکھو جہاں خود رہتے ہو اور ان کو تنگ کرنے کے لئے تکلیف نہ دو اور اگر حمل سے ہوں تو بچّہ جننے تک ان کا خرچ دیتے رہو۔ پھر اگر وہ بچّے کو تمہارے کہنے سے دودھ پلائیں تو ان کو ان کی اجرت دو۔ اور (بچّے کے بارے میں) پسندیدہ طریق سے مواقفت رکھو۔ اور اگر باہم ضد (اور نااتفاقی) کرو گے تو (بچّے کو) اس کے (باپ کے) کہنے سے کوئی اور عورت دودھ پلائے گی');
INSERT INTO "jalandhari" VALUES(65,7,'65:7','صاحب وسعت کو اپنی وسعت کے مطابق خرچ کرنا چاہیئے۔ اور جس کے رزق میں تنگی ہو وہ جتنا خدا نے اس کو دیا ہے اس کے موافق خرچ کرے۔ خدا کسی کو تکلیف نہیں دیتا مگر اسی کے مطابق جو اس کو دیا ہے۔ اور خدا عنقریب تنگی کے بعد کشائش بخشے گا');
INSERT INTO "jalandhari" VALUES(65,8,'65:8','اور بہت سی بستیوں (کے رہنے والوں)نے اپنے پروردگار اور اس کے پیغمبروں کے احکام سے سرکشی کی تو ہم نے ان کو سخت حساب میں پکڑ لیا اور ان پر (ایسا) عذاب نازل کیا جو نہ دیکھا تھا نہ سنا');
INSERT INTO "jalandhari" VALUES(65,9,'65:9','سو انہوں نے اپنے کاموں کی سزا کا مزہ چکھ لیا اور ان کا انجام نقصان ہی تو تھا');
INSERT INTO "jalandhari" VALUES(65,10,'65:10','خدا نے ان کے لئے سخت عذاب تیار کر رکھا ہے۔ تو اے ارباب دانش جو ایمان لائے ہو خدا سے ڈرو۔ خدا نے تمہارے پاس نصیحت (کی کتاب) بھیجی ہے');
INSERT INTO "jalandhari" VALUES(65,11,'65:11','(اور اپنے) پیغمبر (بھی بھیجے ہیں) جو تمہارے سامنے خدا کی واضح المطالب آیتیں پڑھتے ہیں تاکہ جو لوگ ایمان لائے اور عمل نیک کرتے رہے ہیں ان کو اندھیرے سے نکال کر روشنی میں لے آئیں اور جو شخص ایمان لائے گا اور عمل نیک کرے گا ان کو باغہائے بہشت میں داخل کرے گا جن کے نیچے نہریں بہہ رہیں ہیں ابد الاآباد ان میں رہیں گے۔ خدا نے ان کو خوب رزق دیا ہے');
INSERT INTO "jalandhari" VALUES(65,12,'65:12','خدا ہی تو ہے جس نے سات آسمان پیدا کئے اور ایسی ہی زمینیں۔ ان میں (خدا کے) حکم اُترتے رہتے ہیں تاکہ تم لوگ جان لو کہ خدا چیز پر قادر ہے۔ اور یہ کہ خدا اپنے علم سے ہر چیز پر احاطہ کئے ہوئے ہے');
INSERT INTO "jalandhari" VALUES(66,1,'66:1','اے پیغمبر جو چیز خدا نے تمہارے لئے جائز کی ہے تم اس سے کنارہ کشی کیوں کرتے ہو؟ (کیا اس سے) اپنی بیویوں کی خوشنودی چاہتے ہو؟ اور خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(66,2,'66:2','خدا نے تم لوگوں کے لئے تمہاری قسموں کا کفارہ مقرر کردیا ہے۔ اور خدا ہی تمہارا کارساز ہے۔ اور وہ دانا (اور) حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(66,3,'66:3','اور (یاد کرو) جب پیغمبر نے اپنی ایک بی بی سے ایک بھید کی بات کہی تو (اس نے دوسری کو بتا دی)۔ جب اس نے اس کو افشاء کیا اور خدا نے اس (حال) سے پیغمبر کو آگاہ کردیا تو پیغمبر نے ان (بی بی کو وہ بات) کچھ تو بتائی اور کچھ نہ بتائی۔ تو جب وہ ان کو جتائی تو پوچھنے لگیں کہ آپ کو کس نے بتایا؟ انہوں نے کہا کہ مجھے اس نے بتایا ہے جو جاننے والا خبردار ہے');
INSERT INTO "jalandhari" VALUES(66,4,'66:4','اگر تم دونوں خدا کے آگے توبہ کرو (تو بہتر ہے کیونکہ) تمہارے دل کج ہوگئے ہیں۔ اور اگر پیغمبر (کی ایذا) پر باہم اعانت کرو گی تو خدا اور جبریل اور نیک کردار مسلمان ان کے حامی (اور دوستدار) ہیں۔ اور ان کے علاوہ (اور) فرشتے بھی مددگار ہیں');
INSERT INTO "jalandhari" VALUES(66,5,'66:5','اگر پیغمبر تم کو طلاق دے دیں تو عجب نہیں کہ ان کا پروردگار تمہارے بدلے ان کو تم سے بہتر بیبیاں دے دے۔ مسلمان، صاحب ایمان فرمانبردار توبہ کرنے والیاں عبادت گذار روزہ رکھنے والیاں بن شوہر اور کنواریاں');
INSERT INTO "jalandhari" VALUES(66,6,'66:6','مومنو! اپنے آپ کو اور اپنے اہل عیال کو آتش (جہنم) سے بچاؤ جس کا ایندھن آدمی اور پتھر ہیں اور جس پر تند خو اور سخت مزاج فرشتے (مقرر) ہیں جو ارشاد خدا ان کو فرماتا ہے اس کی نافرمانی نہیں کرتے اور جو حکم ان کو ملتا ہے اسے بجا لاتے ہیں');
INSERT INTO "jalandhari" VALUES(66,7,'66:7','کافرو! آج بہانے مت بناؤ۔ جو عمل تم کیا کرتے ہو ان ہی کا تم کو بدلہ دیا جائے گا');
INSERT INTO "jalandhari" VALUES(66,8,'66:8','مومنو! خدا کے آگے صاف دل سے توبہ کرو۔ امید ہے کہ وہ تمہارے گناہ تم سے دور کر دے گا اور تم کو باغہائے بہشت میں جن کے نیچے نہریں بہہ رہی ہیں داخل کرے گا۔ اس دن پیغمبر کو اور ان لوگوں کو جو ان کے ساتھ ایمان لائے ہیں رسوا نہیں کرے گا (بلکہ) ان کا نور ایمان ان کے آگے اور داہنی طرف (روشنی کرتا ہوا) چل رہا ہوگا۔ اور وہ خدا سے التجا کریں گے کہ اے پروردگار ہمارا نور ہمارے لئے پورا کر اور ہمیں معاف کرنا۔ بےشک خدا ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(66,9,'66:9','اے پیغمبر! کافروں اور منافقوں سے لڑو اور ان پر سختی کرو۔ ان کا ٹھکانا دوزخ ہے۔ اور وہ بہت بری جگہ ہے');
INSERT INTO "jalandhari" VALUES(66,10,'66:10','خدا نے کافروں کے لئے نوحؑ کی بیوی اور لوطؑ کی بیوی کی مثال بیان فرمائی ہے۔ دونوں ہمارے دو نیک بندوں کے گھر میں تھیں اور دونوں نے ان کی خیانت کی تو وہ خدا کے مقابلے میں اور ان عورتوں کے کچھ بھی کام نہ آئے اور ان کو حکم دیا گیا کہ اور داخل ہونے والوں کے ساتھ تم بھی دوزخ میں داخل ہو جاؤ');
INSERT INTO "jalandhari" VALUES(66,11,'66:11','اور مومنوں کے لئے (ایک) مثال (تو) فرعون کی بیوی کی بیان فرمائی کہ اس نے خدا سے التجا کی کہ اے میرے پروردگار میرے لئے بہشت میں اپنے پاس ایک گھر بنا اور مجھے فرعون اور اس کے اعمال (زشت مآل) سے نجات بخش اور ظالم لوگوں کے ہاتھ سے مجھ کو مخلصی عطا فرما');
INSERT INTO "jalandhari" VALUES(66,12,'66:12','اور (دوسری) عمران کی بیٹی مریمؑ کی جنہوں نے اپنی شرمگاہ کو محفوظ رکھا تو ہم نے اس میں اپنی روح پھونک دی اور اپنے پروردگار کے کلام اور اس کی کتابوں کو برحق سمجھتی تھیں اور فرمانبرداروں میں سے تھیں');
INSERT INTO "jalandhari" VALUES(67,1,'67:1','وہ (خدا) جس کے ہاتھ میں بادشاہی ہے بڑی برکت والا ہے۔ اور وہ ہر چیز پر قادر ہے');
INSERT INTO "jalandhari" VALUES(67,2,'67:2','اسی نے موت اور زندگی کو پیدا کیا تاکہ تمہاری آزمائش کرے کہ تم میں کون اچھے عمل کرتا ہے۔ اور وہ زبردست (اور) بخشنے والا ہے');
INSERT INTO "jalandhari" VALUES(67,3,'67:3','اس نے سات آسمان اوپر تلے بنائے۔ (اے دیکھنے والے) کیا تو (خدا) رحمٰن کی آفرنیش میں کچھ نقص دیکھتا ہے؟ ذرا آنکھ اٹھا کر دیکھ بھلا تجھ کو (آسمان میں) کوئی شکاف نظر آتا ہے؟');
INSERT INTO "jalandhari" VALUES(67,4,'67:4','پھر دو بارہ (سہ بارہ) نظر کر، تو نظر (ہر بار) تیرے پاس ناکام اور تھک کر لوٹ آئے گی');
INSERT INTO "jalandhari" VALUES(67,5,'67:5','اور ہم نے قریب کے آسمان کو (تاروں کے) چراغوں سے زینت دی۔ اور ان کو شیطان کے مارنے کا آلہ بنایا اور ان کے لئے دہکتی آگ کا عذاب تیار کر رکھا ہے');
INSERT INTO "jalandhari" VALUES(67,6,'67:6','اور جن لوگوں نے اپنے پروردگار سے انکار کیا ان کے لئے جہنم کا عذاب ہے۔ اور وہ برا ٹھکانہ ہے');
INSERT INTO "jalandhari" VALUES(67,7,'67:7','جب وہ اس میں ڈالے جائیں گے تو اس کا چیخنا چلانا سنیں گے اور وہ جوش مار رہی ہوگی');
INSERT INTO "jalandhari" VALUES(67,8,'67:8','گویا مارے جوش کے پھٹ پڑے گی۔ جب اس میں ان کی کوئی جماعت ڈالی جائے گی تو دوزخ کے داروغہ ان سے پوچھیں گے کہ تمہارے پاس کوئی ہدایت کرنے والا نہیں آیا تھا؟');
INSERT INTO "jalandhari" VALUES(67,9,'67:9','وہ کہیں گے کیوں نہیں ضرور ہدایت کرنے والا آیا تھا لیکن ہم نے اس کو جھٹلا دیا اور کہا کہ خدا نے تو کوئی چیز نازل ہی نہیں کی۔ تم تو بڑی غلطی میں (پڑے ہوئے) ہو');
INSERT INTO "jalandhari" VALUES(67,10,'67:10','اور کہیں گے اگر ہم سنتے یا سمجھتے ہوتے تو دوزخیوں میں نہ ہوتے');
INSERT INTO "jalandhari" VALUES(67,11,'67:11','پس وہ اپنے گناہ کا اقرار کرلیں گے۔ سو دوزخیوں کے لئے (رحمت خدا سے) دور ہی ہے');
INSERT INTO "jalandhari" VALUES(67,12,'67:12','(اور) جو لوگ بن دیکھے اپنے پروردگار سے ڈرتے ہیں ان کے لئے بخشش اور اجر عظیم ہے');
INSERT INTO "jalandhari" VALUES(67,13,'67:13','اور تم (لوگ) بات پوشیدہ کہو یا ظاہر۔ وہ دل کے بھیدوں تک سے واقف ہے');
INSERT INTO "jalandhari" VALUES(67,14,'67:14','بھلا جس نے پیدا کیا وہ بےخبر ہے؟ وہ تو پوشیدہ باتوں کا جاننے والا اور (ہر چیز سے) آگاہ ہے');
INSERT INTO "jalandhari" VALUES(67,15,'67:15','وہی تو ہے جس نے تمہارے لئے زمین کو نرم کیا تو اس کی راہوں میں چلو پھرو اور خدا کا (دیا ہو) رزق کھاؤ اور تم کو اسی کے پاس (قبروں سے) نکل کر جانا ہے');
INSERT INTO "jalandhari" VALUES(67,16,'67:16','کیا تم اس سے جو آسمان میں ہے بےخوف ہو کہ تم کو زمین میں دھنسا دے اور وہ اس وقت حرکت کرنے لگے');
INSERT INTO "jalandhari" VALUES(67,17,'67:17','کیا تم اس سے جو آسمان میں ہے نڈر ہو کہ تم پر کنکر بھری ہوا چھوڑ دے۔ سو تم عنقریب جان لو گے کہ میرا ڈرانا کیسا ہے');
INSERT INTO "jalandhari" VALUES(67,18,'67:18','اور جو لوگ ان سے پہلے تھے انہوں نے بھی جھٹلایا تھا سو (دیکھ لو کہ) میرا کیسا عذاب ہوا');
INSERT INTO "jalandhari" VALUES(67,19,'67:19','کیا انہوں نے اپنے سروں پر اڑتے ہوئے جانوروں کو نہیں دیکھا جو پروں کو پھیلائے رہتے ہیں اور ان کو سکیڑ بھی لیتے ہیں۔ خدا کے سوا انہیں کوئی تھام نہیں سکتا۔ بےشک وہ ہر چیز کو دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(67,20,'67:20','بھلا ایسا کون ہے جو تمہاری فوج ہو کر خدا کے سوا تمہاری مدد کرسکے۔ کافر تو دھوکے میں ہیں');
INSERT INTO "jalandhari" VALUES(67,21,'67:21','بھلا اگر وہ اپنا رزق بند کرلے تو کون ہے جو تم کو رزق دے؟ لیکن یہ سرکشی اور نفرت میں پھنسے ہوئے ہیں');
INSERT INTO "jalandhari" VALUES(67,22,'67:22','بھلا جو شخص چلتا ہوا منہ کے بل گر پڑتا ہے وہ سیدھے رستے پر ہے یا وہ جو سیدھے رستے پر برابر چل رہا ہو؟');
INSERT INTO "jalandhari" VALUES(67,23,'67:23','کہو وہ خدا ہی تو ہے جس نے تم کو پیدا کیا۔ اور تمہارے کان اور آنکھیں اور دل بنائے (مگر) تم کم احسان مانتے ہو');
INSERT INTO "jalandhari" VALUES(67,24,'67:24','کہہ دو کہ وہی ہے جس نے تم کو زمین میں پھیلایا اور اسی کے روبرو تم جمع کئے جاؤ گے');
INSERT INTO "jalandhari" VALUES(67,25,'67:25','اور کافر کہتے ہیں کہ اگر تم سچے ہو تو یہ وعید کب (پورا) ہوگا؟');
INSERT INTO "jalandhari" VALUES(67,26,'67:26','کہہ دو اس کا علم خدا ہی کو ہے۔ اور میں تو کھول کھول کر ڈر سنانے دینے والا ہوں');
INSERT INTO "jalandhari" VALUES(67,27,'67:27','سو جب وہ دیکھ لیں گے کہ وہ (وعدہ) قریب آگیا تو کافروں کے منہ برے ہوجائیں گے اور (ان سے) کہا جائے گا کہ یہ وہی ہے جس کے تم خواستگار تھے');
INSERT INTO "jalandhari" VALUES(67,28,'67:28','کہو کہ بھلا دیکھو تو اگر خدا مجھ کو اور میرے ساتھیوں کو ہلاک کردے یا ہم پر مہربانی کرے۔ تو کون ہے کافروں کو دکھ دینے والے عذاب سے پناہ دے؟');
INSERT INTO "jalandhari" VALUES(67,29,'67:29','کہہ دو کہ وہ جو (خدائے) رحمٰن (ہے) ہم اسی پر ایمان لائے اور اسی پر بھروسا رکھتے ہیں۔ تم کو جلد معلوم ہوجائے گا کہ صریح گمراہی میں کون پڑ رہا تھا');
INSERT INTO "jalandhari" VALUES(67,30,'67:30','کہو کہ بھلا دیکھو تو اگر تمہارا پانی (جو تم پیتے ہو اور برتے ہو) خشک ہوجائے تو (خدا کے) سوا کون ہے جو تمہارے لئے شیریں پانی کا چشمہ بہا لائے');
INSERT INTO "jalandhari" VALUES(68,1,'68:1','نٓ۔ قلم کی اور جو (اہل قلم) لکھتے ہیں اس کی قسم');
INSERT INTO "jalandhari" VALUES(68,2,'68:2','کہ (اے محمدصلى الله عليه وسلم) تم اپنے پروردگار کے فضل سے دیوانے نہیں ہو');
INSERT INTO "jalandhari" VALUES(68,3,'68:3','اور تمہارے لئے بے انتہا اجر ہے');
INSERT INTO "jalandhari" VALUES(68,4,'68:4','اور اخلاق تمہارے بہت (عالی) ہیں');
INSERT INTO "jalandhari" VALUES(68,5,'68:5','سو عنقریب تم بھی دیکھ لو گے اور یہ (کافر) بھی دیکھ لیں گے');
INSERT INTO "jalandhari" VALUES(68,6,'68:6','کہ تم میں سے کون دیوانہ ہے');
INSERT INTO "jalandhari" VALUES(68,7,'68:7','تمہارا پروردگار اس کو بھی خوب جانتا ہے جو اس کے رستے سے بھٹک گیا اور ان کو بھی خوب جانتا ہے جو سیدھے راستے پر چل رہے ہیں');
INSERT INTO "jalandhari" VALUES(68,8,'68:8','تو تم جھٹلانے والوں کا کہا نہ ماننا');
INSERT INTO "jalandhari" VALUES(68,9,'68:9','یہ لوگ چاہتے ہیں کہ تم نرمی اختیار کرو تو یہ بھی نرم ہوجائیں');
INSERT INTO "jalandhari" VALUES(68,10,'68:10','اور کسی ایسے شخص کے کہے میں نہ آجانا جو بہت قسمیں کھانے والا ذلیل اوقات ہے');
INSERT INTO "jalandhari" VALUES(68,11,'68:11','طعن آمیز اشارتیں کرنے والا چغلیاں لئے پھرنے والا');
INSERT INTO "jalandhari" VALUES(68,12,'68:12','مال میں بخل کرنے والا حد سے بڑھا ہوا بدکار');
INSERT INTO "jalandhari" VALUES(68,13,'68:13','سخت خو اور اس کے علاوہ بدذات ہے');
INSERT INTO "jalandhari" VALUES(68,14,'68:14','اس سبب سے کہ مال اور بیٹے رکھتا ہے');
INSERT INTO "jalandhari" VALUES(68,15,'68:15','جب اس کو ہماری آیتیں پڑھ کر سنائی جاتی ہیں تو کہتا ہے کہ یہ اگلے لوگوں کے افسانے ہیں');
INSERT INTO "jalandhari" VALUES(68,16,'68:16','ہم عنقریب اس کی ناک پر داغ لگائیں گے');
INSERT INTO "jalandhari" VALUES(68,17,'68:17','ہم نے ان لوگوں کی اسی طرح آزمائش کی ہے جس طرح باغ والوں کی آزمائش کی تھی۔ جب انہوں نے قسمیں کھا کھا کر کہا کہ صبح ہوتے ہوتے ہم اس کا میوہ توڑ لیں گے');
INSERT INTO "jalandhari" VALUES(68,18,'68:18','اور انشاء الله نہ کہا');
INSERT INTO "jalandhari" VALUES(68,19,'68:19','سو وہ ابھی سو ہی رہے تھے کہ تمہارے پروردگار کی طرف سے (راتوں رات) اس پر ایک آفت پھر گئی');
INSERT INTO "jalandhari" VALUES(68,20,'68:20','تو وہ ایسا ہوگیا جیسے کٹی ہوئی کھیتی');
INSERT INTO "jalandhari" VALUES(68,21,'68:21','جب صبح ہوئی تو وہ لوگ ایک دوسرے کو پکارنے لگے');
INSERT INTO "jalandhari" VALUES(68,22,'68:22','اگر تم کو کاٹنا ہے تو اپنی کھیتی پر سویرے ہی جا پہنچو');
INSERT INTO "jalandhari" VALUES(68,23,'68:23','تو وہ چل پڑے اور آپس میں چپکے چپکے کہتے جاتے تھے');
INSERT INTO "jalandhari" VALUES(68,24,'68:24','آج یہاں تمہارے پاس کوئی فقیر نہ آنے پائے');
INSERT INTO "jalandhari" VALUES(68,25,'68:25','اور کوشش کے ساتھ سویرے ہی جا پہنچے (گویا کھیتی پر) قادر ہیں');
INSERT INTO "jalandhari" VALUES(68,26,'68:26','جب باغ کو دیکھا تو (ویران) کہنے لگے کہ ہم رستہ بھول گئے ہیں');
INSERT INTO "jalandhari" VALUES(68,27,'68:27','نہیں بلکہ ہم (برگشتہ نصیب) بےنصیب ہیں');
INSERT INTO "jalandhari" VALUES(68,28,'68:28','ایک جو اُن میں فرزانہ تھا بولا کہ کیا میں نے تم سے نہیں کہا تھا کہ تم تسبیح کیوں نہیں کرتے؟');
INSERT INTO "jalandhari" VALUES(68,29,'68:29','(تب) وہ کہنے لگے کہ ہمارا پروردگار پاک ہے بےشک ہم ہی قصوروار تھے');
INSERT INTO "jalandhari" VALUES(68,30,'68:30','پھر لگے ایک دوسرے کو رو در رو ملامت کرنے');
INSERT INTO "jalandhari" VALUES(68,31,'68:31','کہنے لگے ہائے شامت ہم ہی حد سے بڑھ گئے تھے');
INSERT INTO "jalandhari" VALUES(68,32,'68:32','امید ہے کہ ہمارا پروردگار اس کے بدلے میں ہمیں اس سے بہتر باغ عنایت کرے ہم اپنے پروردگار کی طرف سے رجوع لاتے ہیں');
INSERT INTO "jalandhari" VALUES(68,33,'68:33','(دیکھو) عذاب یوں ہوتا ہے۔ اور آخرت کا عذاب اس سے کہیں بڑھ کر ہے۔ کاش! یہ لوگ جانتے ہوتے');
INSERT INTO "jalandhari" VALUES(68,34,'68:34','پرہیزگاروں کے لئے ان کے پروردگار کے ہاں نعمت کے باغ ہیں');
INSERT INTO "jalandhari" VALUES(68,35,'68:35','کیا ہم فرمانبرداروں کو نافرمانوں کی طرف (نعمتوں سے) محروم کردیں گے؟');
INSERT INTO "jalandhari" VALUES(68,36,'68:36','تمہیں کیا ہوگیا ہے کیسی تجویزیں کرتے ہو؟');
INSERT INTO "jalandhari" VALUES(68,37,'68:37','کیا تمہارے پاس کوئی کتاب ہے جس میں (یہ) پڑھتے ہو');
INSERT INTO "jalandhari" VALUES(68,38,'68:38','کہ جو چیز تم پسند کرو گے وہ تم کو ضرور ملے گی');
INSERT INTO "jalandhari" VALUES(68,39,'68:39','یا تم نے ہم سے قسمیں لے رکھی ہیں جو قیامت کے دن تک چلی جائیں گی کہ جس شے کا تم حکم کرو گے وہ تمہارے لئے حاضر ہوگی');
INSERT INTO "jalandhari" VALUES(68,40,'68:40','ان سے پوچھو کہ ان میں سے اس کا کون ذمہ لیتا ہے؟');
INSERT INTO "jalandhari" VALUES(68,41,'68:41','کیا (اس قول میں) ان کے اور بھی شریک ہیں؟ اگر یہ سچے ہیں تو اپنے شریکوں کو لا سامنے کریں');
INSERT INTO "jalandhari" VALUES(68,42,'68:42','جس دن پنڈلی سے کپڑا اٹھا دیا جائے گا اور کفار سجدے کے لئے بلائے جائیں گے تو سجدہ نہ کرسکیں گے');
INSERT INTO "jalandhari" VALUES(68,43,'68:43','ان کی آنکھیں جھکی ہوئی ہوں گی اور ان پر ذلت چھا رہی ہوگی حالانکہ پہلے (اُس وقت) سجدے کے لئے بلاتے جاتے تھے جب کہ صحیح وسالم تھے');
INSERT INTO "jalandhari" VALUES(68,44,'68:44','تو مجھ کو اس کلام کے جھٹلانے والوں سے سمجھ لینے دو۔ ہم ان کو آہستہ آہستہ ایسے طریق سے پکڑیں گے کہ ان کو خبر بھی نہ ہوگی');
INSERT INTO "jalandhari" VALUES(68,45,'68:45','اور میں ان کو مہلت دیئے جاتا ہوں میری تدبیر قوی ہے');
INSERT INTO "jalandhari" VALUES(68,46,'68:46','کیا تم ان سے کچھ اجر مانگتے ہو کہ ان پر تاوان کا بوجھ پڑ رہا ہے');
INSERT INTO "jalandhari" VALUES(68,47,'68:47','یا ان کے پاس غیب کی خبر ہے کہ (اسے) لکھتے جاتے ہیں');
INSERT INTO "jalandhari" VALUES(68,48,'68:48','تو اپنے پروردگار کے حکم کے انتظار میں صبر کئے رہو اور مچھلی (کا لقمہ ہونے) والے یونس کی طرح رہو نا کہ انہوں نے (خدا) کو پکارا اور وہ (غم و) غصے میں بھرے ہوئے تھے');
INSERT INTO "jalandhari" VALUES(68,49,'68:49','اگر تمہارے پروردگار کی مہربانی ان کی یاوری نہ کرتی تو وہ چٹیل میدان میں ڈال دیئے جاتے اور ان کا حال ابتر ہوجاتا');
INSERT INTO "jalandhari" VALUES(68,50,'68:50','پھر پروردگار نے ان کو برگزیدہ کرکے نیکوکاروں میں کرلیا');
INSERT INTO "jalandhari" VALUES(68,51,'68:51','اور کافر جب (یہ) نصیحت (کی کتاب) سنتے ہیں تو یوں لگتے ہیں کہ تم کو اپنی نگاہوں سے پھسلا دیں گے اور کہتے یہ تو دیوانہ ہے');
INSERT INTO "jalandhari" VALUES(68,52,'68:52','اور (لوگو) یہ (قرآن) اہل عالم کے لئے نصیحت ہے');
INSERT INTO "jalandhari" VALUES(69,1,'69:1','سچ مچ ہونے والی');
INSERT INTO "jalandhari" VALUES(69,2,'69:2','وہ سچ مچ ہونے والی کیا ہے؟');
INSERT INTO "jalandhari" VALUES(69,3,'69:3','اور تم کو کیا معلوم ہے کہ سچ مچ ہونے والی کیا ہے؟');
INSERT INTO "jalandhari" VALUES(69,4,'69:4','کھڑکھڑانے والی (جس) کو ثمود اور عاد (دونوں) نے جھٹلایا');
INSERT INTO "jalandhari" VALUES(69,5,'69:5','سو ثمود تو کڑک سے ہلاک کردیئے گئے');
INSERT INTO "jalandhari" VALUES(69,6,'69:6','رہے عاد تو ان کا نہایت تیز آندھی سے ستیاناس کردیا گیا');
INSERT INTO "jalandhari" VALUES(69,7,'69:7','خدا نے اس کو سات رات اور آٹھ دن لگاتار ان پر چلائے رکھا تو (اے مخاطب) تو لوگوں کو اس میں (اس طرح) ڈھئے (اور مرے) پڑے دیکھے جیسے کھجوروں کے کھوکھلے تنے');
INSERT INTO "jalandhari" VALUES(69,8,'69:8','بھلا تو ان میں سے کسی کو بھی باقی دیکھتا ہے؟');
INSERT INTO "jalandhari" VALUES(69,9,'69:9','اور فرعون اور جو لوگ اس سے پہلے تھے اور وہ جو الٹی بستیوں میں رہتے تھے سب گناہ کے کام کرتے تھے');
INSERT INTO "jalandhari" VALUES(69,10,'69:10','انہوں نے اپنے پروردگار کے پیغمبر کی نافرمانی کی تو خدا نے بھی ان کو بڑا سخت پکڑا');
INSERT INTO "jalandhari" VALUES(69,11,'69:11','جب پانی طغیانی پر آیا تو ہم نے تم (لوگوں )کو کشتی میں سوار کرلیا');
INSERT INTO "jalandhari" VALUES(69,12,'69:12','تاکہ اس کو تمہارے لئے یادگار بنائیں اور یاد رکھنے والے کان اسے یاد رکھیں');
INSERT INTO "jalandhari" VALUES(69,13,'69:13','تو جب صور میں ایک (بار) پھونک مار دی جائے گی');
INSERT INTO "jalandhari" VALUES(69,14,'69:14','اور زمین اور پہاڑ دونوں اٹھا لئے جائیں گے۔ پھر ایک بارگی توڑ پھوڑ کر برابر کردیئے جائیں گے');
INSERT INTO "jalandhari" VALUES(69,15,'69:15','تو اس روز ہو پڑنے والی (یعنی قیامت) ہو پڑے گی');
INSERT INTO "jalandhari" VALUES(69,16,'69:16','اور آسمان پھٹ جائے گا تو وہ اس دن کمزور ہوگا');
INSERT INTO "jalandhari" VALUES(69,17,'69:17','اور فرشتے اس کے کناروں پر (اُتر آئیں گے) اور تمہارے پروردگار کے عرش کو اس روز آٹھ فرشتے اپنے سروں پر اُٹھائے ہوں گے');
INSERT INTO "jalandhari" VALUES(69,18,'69:18','اس روز تم (سب لوگوں کے سامنے) پیش کئے جاؤ گے اور تمہاری کوئی پوشیدہ بات چھپی نہ رہے گی');
INSERT INTO "jalandhari" VALUES(69,19,'69:19','تو جس کا (اعمال) نامہ اس کے داہنے ہاتھ میں دیا جائے گا وہ (دوسروں سے) کہے گا کہ لیجیئے میرا نامہ (اعمال) پڑھیئے');
INSERT INTO "jalandhari" VALUES(69,20,'69:20','مجھے یقین تھا کہ مجھ کو میرا حساب (کتاب) ضرور ملے گا');
INSERT INTO "jalandhari" VALUES(69,21,'69:21','پس وہ (شخص) من مانے عیش میں ہوگا');
INSERT INTO "jalandhari" VALUES(69,22,'69:22','(یعنی) اونچے (اونچے محلوں) کے باغ میں');
INSERT INTO "jalandhari" VALUES(69,23,'69:23','جن کے میوے جھکے ہوئے ہوں گے');
INSERT INTO "jalandhari" VALUES(69,24,'69:24','جو (عمل) تم ایام گزشتہ میں آگے بھیج چکے ہو اس کے صلے میں مزے سے کھاؤ اور پیو');
INSERT INTO "jalandhari" VALUES(69,25,'69:25','اور جس کا نامہ (اعمال) اس کے بائیں ہاتھ میں یاد جائے گا وہ کہے گا اے کاش مجھ کو میرا (اعمال) نامہ نہ دیا جاتا');
INSERT INTO "jalandhari" VALUES(69,26,'69:26','اور مجھے معلوم نہ ہو کہ میرا حساب کیا ہے');
INSERT INTO "jalandhari" VALUES(69,27,'69:27','اے کاش موت (ابد الاآباد کے لئے میرا کام) تمام کرچکی ہوتی');
INSERT INTO "jalandhari" VALUES(69,28,'69:28','آج) میرا مال میرے کچھ بھی کام بھی نہ آیا');
INSERT INTO "jalandhari" VALUES(69,29,'69:29','(ہائے) میری سلطنت خاک میں مل گئی');
INSERT INTO "jalandhari" VALUES(69,30,'69:30','(حکم ہوگا کہ) اسے پکڑ لو اور طوق پہنا دو');
INSERT INTO "jalandhari" VALUES(69,31,'69:31','پھر دوزخ کی آگ میں جھونک دو');
INSERT INTO "jalandhari" VALUES(69,32,'69:32','پھر زنجیر سے جس کی ناپ ستر گز ہے جکڑ دو');
INSERT INTO "jalandhari" VALUES(69,33,'69:33','یہ نہ تو خدائے جل شانہ پر ایمان لاتا تھا');
INSERT INTO "jalandhari" VALUES(69,34,'69:34','اور نہ فقیر کے کھانا کھلانے پر آمادہ کرتا تھا');
INSERT INTO "jalandhari" VALUES(69,35,'69:35','سو آج اس کا بھی یہاں کوئی دوستدار نہیں');
INSERT INTO "jalandhari" VALUES(69,36,'69:36','اور نہ پیپ کے سوا (اس کے لئے) کھانا ہے');
INSERT INTO "jalandhari" VALUES(69,37,'69:37','جس کو گنہگاروں کے سوا کوئی نہیں کھائے گا');
INSERT INTO "jalandhari" VALUES(69,38,'69:38','تو ہم کو ان چیزوں کی قسم جو تم کو نظر آتی ہیں');
INSERT INTO "jalandhari" VALUES(69,39,'69:39','اور ان کی جو نظر میں نہیں آتیں');
INSERT INTO "jalandhari" VALUES(69,40,'69:40','کہ یہ (قرآن) فرشتہٴ عالی مقام کی زبان کا پیغام ہے');
INSERT INTO "jalandhari" VALUES(69,41,'69:41','اور یہ کسی شاعر کا کلام نہیں۔ مگر تم لوگ بہت ہی کم ایمان لاتے ہو');
INSERT INTO "jalandhari" VALUES(69,42,'69:42','اور نہ کسی کاہن کے مزخرفات ہیں۔ لیکن تم لوگ بہت ہی کم فکر کرتے ہو');
INSERT INTO "jalandhari" VALUES(69,43,'69:43','یہ تو) پروردگار عالم کا اُتارا (ہوا) ہے');
INSERT INTO "jalandhari" VALUES(69,44,'69:44','اگر یہ پیغمبر ہماری نسبت کوئی بات جھوٹ بنا لاتے');
INSERT INTO "jalandhari" VALUES(69,45,'69:45','تو ہم ان کا داہنا ہاتھ پکڑ لیتے');
INSERT INTO "jalandhari" VALUES(69,46,'69:46','پھر ان کی رگ گردن کاٹ ڈالتے');
INSERT INTO "jalandhari" VALUES(69,47,'69:47','پھر تم میں سے کوئی (ہمیں) اس سے روکنے والا نہ ہوتا');
INSERT INTO "jalandhari" VALUES(69,48,'69:48','اور یہ (کتاب) تو پرہیزگاروں کے لئے نصیحت ہے');
INSERT INTO "jalandhari" VALUES(69,49,'69:49','اور ہم جانتے ہیں کہ تم میں سے بعض اس کو جھٹلانے والے ہیں');
INSERT INTO "jalandhari" VALUES(69,50,'69:50','نیز یہ کافروں کے لئے (موجب) حسرت ہے');
INSERT INTO "jalandhari" VALUES(69,51,'69:51','اور کچھ شک نہیں کہ یہ برحق قابل یقین ہے');
INSERT INTO "jalandhari" VALUES(69,52,'69:52','سو تم اپنے پروردگار عزوجل کے نام کی تنزیہ کرتے رہو');
INSERT INTO "jalandhari" VALUES(70,1,'70:1','ایک طلب کرنے والے نے عذاب طلب کیا جو نازل ہو کر رہے گا');
INSERT INTO "jalandhari" VALUES(70,2,'70:2','(یعنی) کافروں پر (اور) کوئی اس کو ٹال نہ سکے گا');
INSERT INTO "jalandhari" VALUES(70,3,'70:3','(اور وہ) خدائے صاحب درجات کی طرف سے (نازل ہوگا)');
INSERT INTO "jalandhari" VALUES(70,4,'70:4','جس کی طرف روح (الامین) اور فرشتے پڑھتے ہیں (اور) اس روز (نازل ہوگا) جس کا اندازہ پچاس ہزار برس کا ہوگا');
INSERT INTO "jalandhari" VALUES(70,5,'70:5','(تو تم کافروں کی باتوں کو) قوت کے ساتھ برداشت کرتے رہو');
INSERT INTO "jalandhari" VALUES(70,6,'70:6','وہ ان لوگوں کی نگاہ میں دور ہے');
INSERT INTO "jalandhari" VALUES(70,7,'70:7','اور ہماری نظر میں نزدیک');
INSERT INTO "jalandhari" VALUES(70,8,'70:8','جس دن آسمان ایسا ہو جائے گا جیسے پگھلا ہوا تانبا');
INSERT INTO "jalandhari" VALUES(70,9,'70:9','اور پہاڑ (ایسے) جیسے (دھنکی ہوئی) رنگین اون');
INSERT INTO "jalandhari" VALUES(70,10,'70:10','اور کوئی دوست کسی دوست کا پرسان نہ ہوگا');
INSERT INTO "jalandhari" VALUES(70,11,'70:11','ایک دوسرے کو سامنے دیکھ رہے ہوں گے (اس روز) گنہگار خواہش کرے گا کہ کسی طرح اس دن کے عذاب کے بدلے میں (سب کچھ) دے دے یعنی اپنے بیٹے');
INSERT INTO "jalandhari" VALUES(70,12,'70:12','اور اپنی بیوی اور اپنے بھائی');
INSERT INTO "jalandhari" VALUES(70,13,'70:13','اور اپنا خاندان جس میں وہ رہتا تھا');
INSERT INTO "jalandhari" VALUES(70,14,'70:14','اور جتنے آدمی زمین میں ہیں (غرض) سب (کچھ دے دے) اور اپنے تئیں عذاب سے چھڑا لے');
INSERT INTO "jalandhari" VALUES(70,15,'70:15','(لیکن) ایسا ہرگز نہیں ہوگا وہ بھڑکتی ہوئی آگ ہے');
INSERT INTO "jalandhari" VALUES(70,16,'70:16','کھال ادھیڑ ڈالنے والی');
INSERT INTO "jalandhari" VALUES(70,17,'70:17','ان لوگوں کو اپنی طرف بلائے گی جنہوں نے (دین حق سے) اعراض کیا');
INSERT INTO "jalandhari" VALUES(70,18,'70:18','اور (مال )جمع کیا اور بند کر رکھا');
INSERT INTO "jalandhari" VALUES(70,19,'70:19','کچھ شک نہیں کہ انسان کم حوصلہ پیدا ہوا ہے');
INSERT INTO "jalandhari" VALUES(70,20,'70:20','جب اسے تکلیف پہنچتی ہے تو گھبرا اٹھتا ہے');
INSERT INTO "jalandhari" VALUES(70,21,'70:21','اور جب آسائش حاصل ہوتی ہے تو بخیل بن جاتا ہے');
INSERT INTO "jalandhari" VALUES(70,22,'70:22','مگر نماز گزار');
INSERT INTO "jalandhari" VALUES(70,23,'70:23','جو نماز کا التزام رکھتے (اور بلاناغہ پڑھتے) ہیں');
INSERT INTO "jalandhari" VALUES(70,24,'70:24','اور جن کے مال میں حصہ مقرر ہے');
INSERT INTO "jalandhari" VALUES(70,25,'70:25','(یعنی) مانگنے والے کا۔ اور نہ مانگے والے والا کا');
INSERT INTO "jalandhari" VALUES(70,26,'70:26','اور جو روز جزا کو سچ سمجھتے ہیں');
INSERT INTO "jalandhari" VALUES(70,27,'70:27','اور جو اپنے پروردگار کے عذاب سے خوف رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(70,28,'70:28','بےشک ان کے پروردگار کا عذاب ہے ہی ایسا کہ اس سے بےخوف نہ ہوا جائے');
INSERT INTO "jalandhari" VALUES(70,29,'70:29','اور جو اپنی شرمگاہوں کی حفاظت کرتے ہیں');
INSERT INTO "jalandhari" VALUES(70,30,'70:30','مگر اپنی بیویوں یا لونڈیوں سے کہ (ان کے پاس جانے پر) انہیں کچھ ملامت نہیں');
INSERT INTO "jalandhari" VALUES(70,31,'70:31','اور جو لوگ ان کے سوا اور کے خواستگار ہوں وہ حد سے نکل جانے والے ہیں');
INSERT INTO "jalandhari" VALUES(70,32,'70:32','اور جو اپنی امانتوں اور اقراروں کا پاس کرتے ہیں');
INSERT INTO "jalandhari" VALUES(70,33,'70:33','اور جو اپنی شہادتوں پر قائم رہتے ہیں');
INSERT INTO "jalandhari" VALUES(70,34,'70:34','اور جو اپنی نماز کی خبر رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(70,35,'70:35','یہی لوگ باغہائے بہشت میں عزت واکرام سے ہوں گے');
INSERT INTO "jalandhari" VALUES(70,36,'70:36','تو ان کافروں کو کیا ہوا ہے کہ تمہاری طرف دوڑے چلے آتے ہیں');
INSERT INTO "jalandhari" VALUES(70,37,'70:37','اور) دائیں بائیں سے گروہ گروہ ہو کر (جمع ہوتے جاتے ہیں)');
INSERT INTO "jalandhari" VALUES(70,38,'70:38','کیا ان میں سے ہر شخص یہ توقع رکھتا ہے کہ نعمت کے باغ میں داخل کیا جائے گا');
INSERT INTO "jalandhari" VALUES(70,39,'70:39','ہرگز نہیں۔ ہم نے ان کو اس چیز سے پیدا کیا ہے جسے وہ جانتے ہیں');
INSERT INTO "jalandhari" VALUES(70,40,'70:40','ہمیں مشرقوں اور مغربوں کے مالک کی قسم کہ ہم طاقت رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(70,41,'70:41','(یعنی) اس بات پر (قادر ہیں) کہ ان سے بہتر لوگ بدل لائیں اور ہم عاجز نہیں ہیں');
INSERT INTO "jalandhari" VALUES(70,42,'70:42','تو (اے پیغمبر) ان کو باطل میں پڑے رہنے اور کھیل لینے دو یہاں تک کہ جس دن کا ان سے وعدہ کیا جاتا ہے وہ ان کے سامنے آ موجود ہو');
INSERT INTO "jalandhari" VALUES(70,43,'70:43','اس دن یہ قبر سے نکل کر (اس طرح) دوڑیں گے جیسے (شکاری) شکار کے جال کی طرف دوڑتے ہیں');
INSERT INTO "jalandhari" VALUES(70,44,'70:44','ان کی آنکھیں جھک رہی ہوں گی اور ذلت ان پر چھا رہی ہوگی۔ یہی وہ دن ہے جس کا ان سے وعدہ کیا جاتا تھا');
INSERT INTO "jalandhari" VALUES(71,1,'71:1','ہم نے نوحؑ کو ان کی قوم کی طرف بھیجا کہ پیشتر اس کے کہ ان پر درد دینے والا عذاب واقع ہو اپنی قوم کو ہدایت کردو');
INSERT INTO "jalandhari" VALUES(71,2,'71:2','انہوں نے کہا کہ اے قوم! میں تم کو کھلے طور پر نصیحت کرتا ہوں');
INSERT INTO "jalandhari" VALUES(71,3,'71:3','کہ خدا کی عبات کرو اور اس سے ڈرو اور میرا کہا مانو');
INSERT INTO "jalandhari" VALUES(71,4,'71:4','وہ تمہارے گناہ بخش دے گا اور (موت کے) وقت مقررہ تک تم کو مہلت عطا کرے گا۔ جب خدا کا مقرر کیا ہوگا وقت آجاتا ہے تو تاخیر نہیں ہوتی۔ کاش تم جانتے ہوتے');
INSERT INTO "jalandhari" VALUES(71,5,'71:5','جب لوگوں نے نہ مانا تو (نوحؑ نے) خدا سے عرض کی کہ پروردگار میں اپنی قوم کو رات دن بلاتا رہا');
INSERT INTO "jalandhari" VALUES(71,6,'71:6','لیکن میرے بلانے سے وہ اور زیادہ گزیر کرتے رہے');
INSERT INTO "jalandhari" VALUES(71,7,'71:7','جب جب میں نے ان کو بلایا کہ (توبہ کریں اور) تو ان کو معاف فرمائے تو انہوں نے اپنے کانوں میں انگلیاں دے لیں اور کپڑے اوڑھ لئے اور اڑ گئے اور اکڑ بیٹھے');
INSERT INTO "jalandhari" VALUES(71,8,'71:8','پھر میں ان کو کھلے طور پر بھی بلاتا رہا');
INSERT INTO "jalandhari" VALUES(71,9,'71:9','اور ظاہر اور پوشیدہ ہر طرح سمجھاتا رہا');
INSERT INTO "jalandhari" VALUES(71,10,'71:10','اور کہا کہ اپنے پروردگار سے معافی مانگو کہ وہ بڑا معاف کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(71,11,'71:11','وہ تم پر آسمان سے لگاتار مینہ برسائے گا');
INSERT INTO "jalandhari" VALUES(71,12,'71:12','اور مال اور بیٹوں سے تمہاری مدد فرمائے گا اور تمہیں باغ عطا کرے گا اور ان میں تمہارے لئے نہریں بہا دے گا');
INSERT INTO "jalandhari" VALUES(71,13,'71:13','تم کو کیا ہوا ہے کہ تم خدا کی عظمت کا اعتقاد نہیں رکھتے');
INSERT INTO "jalandhari" VALUES(71,14,'71:14','حالانکہ اس نے تم کو طرح طرح (کی حالتوں) کا پیدا کیا ہے');
INSERT INTO "jalandhari" VALUES(71,15,'71:15','کیا تم نے نہیں دیکھا کہ خدا نے سات آسمان کیسے اوپر تلے بنائے ہیں');
INSERT INTO "jalandhari" VALUES(71,16,'71:16','اور چاند کو ان میں (زمین کا) نور بنایا ہے اور سورج کو چراغ ٹھہرایا ہے');
INSERT INTO "jalandhari" VALUES(71,17,'71:17','اور خدا ہی نے تم کو زمین سے پیدا کیا ہے');
INSERT INTO "jalandhari" VALUES(71,18,'71:18','پھر اسی میں تمہیں لوٹا دے گا اور (اسی سے) تم کو نکال کھڑا کرے گا');
INSERT INTO "jalandhari" VALUES(71,19,'71:19','اور خدا ہی نے زمین کو تمہارے لئے فرش بنایا');
INSERT INTO "jalandhari" VALUES(71,20,'71:20','تاکہ اس کے بڑے بڑے کشادہ رستوں میں چلو پھرو');
INSERT INTO "jalandhari" VALUES(71,21,'71:21','(اس کے بعد) نوح نے عرض کی کہ میرے پروردگار! یہ لوگ میرے کہنے پر نہیں چلے اور ایسوں کے تابع ہوئے جن کو ان کے مال اور اولاد نے نقصان کے سوا کچھ فائدہ نہیں دیا');
INSERT INTO "jalandhari" VALUES(71,22,'71:22','اور وہ بڑی بڑی چالیں چلے');
INSERT INTO "jalandhari" VALUES(71,23,'71:23','اور کہنے لگے کہ اپنے معبودوں کو ہرگز نہ چھوڑنا اور ود اور سواع اور یغوث اور یعقوب اور نسر کو کبھی ترک نہ کرنا');
INSERT INTO "jalandhari" VALUES(71,24,'71:24','(پروردگار) انہوں نے بہت لوگوں کو گمراہ کردیا ہے۔ تو تُو ان کو اور گمراہ کردے');
INSERT INTO "jalandhari" VALUES(71,25,'71:25','(آخر) وہ اپنے گناہوں کے سبب پہلے غرقاب کردیئے گئے پھر آگ میں ڈال دیئے گئے۔ تو انہوں نے خدا کے سوا کسی کو اپنا مددگار نہ پایا');
INSERT INTO "jalandhari" VALUES(71,26,'71:26','اور (پھر) نوحؑ نے (یہ) دعا کی کہ میرے پروردگار اگر کسی کافر کو روئے زمین پر بسا نہ رہنے دے');
INSERT INTO "jalandhari" VALUES(71,27,'71:27','اگر تم ان کو رہنے دے گا تو تیرے بندوں کو گمراہ کریں گے اور ان سے جو اولاد ہوگی وہ بھی بدکار اور ناشکر گزار ہوگی');
INSERT INTO "jalandhari" VALUES(71,28,'71:28','اے میرے پروردگار مجھ کو اور میرے ماں باپ کو اور جو ایمان لا کر میرے گھر میں آئے اس کو اور تمام ایمان والے مردوں اور ایمان والی عورتوں کو معاف فرما اور ظالم لوگوں کے لئے اور زیادہ تباہی بڑھا');
INSERT INTO "jalandhari" VALUES(72,1,'72:1','(اے پیغمبر لوگوں سے) کہہ دو کہ میرے پاس وحی آئی ہے کہ جنوں کی ایک جماعت نے (اس کتاب کو) سنا تو کہنے لگے کہ ہم نے ایک عجیب قرآن سنا');
INSERT INTO "jalandhari" VALUES(72,2,'72:2','جو بھلائی کا رستہ بتاتا ہے سو ہم اس پر ایمان لے آئے۔ اور ہم اپنے پروردگار کے ساتھ کسی کو شریک نہیں بنائیں گے');
INSERT INTO "jalandhari" VALUES(72,3,'72:3','اور یہ کہ ہمارے پروردگار کی عظمت (شان) بہت بڑی ہے اور وہ نہ بیوی رکھتا ہے نہ اولاد');
INSERT INTO "jalandhari" VALUES(72,4,'72:4','اور یہ کہ ہم میں سے بعض بےوقوف خدا کے بارے میں جھوٹ افتراء کرتا ہے');
INSERT INTO "jalandhari" VALUES(72,5,'72:5','اور ہمارا (یہ) خیال تھا کہ انسان اور جن خدا کی نسبت جھوٹ نہیں بولتے');
INSERT INTO "jalandhari" VALUES(72,6,'72:6','اور یہ کہ بعض بنی آدم بعض جنات کی پناہ پکڑا کرتے تھے (اس سے) ان کی سرکشی اور بڑھ گئی تھی');
INSERT INTO "jalandhari" VALUES(72,7,'72:7','اور یہ کہ ان کا بھی یہی اعتقاد تھا جس طرح تمہارا تھا کہ خدا کسی کو نہیں جلائے گا');
INSERT INTO "jalandhari" VALUES(72,8,'72:8','اور یہ کہ ہم نے آسمان کو ٹٹولا تو اس کو مضبوط چوکیداروں اور انگاروں سے سے بھرا پایا');
INSERT INTO "jalandhari" VALUES(72,9,'72:9','اور یہ کہ پہلے ہم وہاں بہت سے مقامات میں (خبریں) سننے کے لئے بیٹھا کرتے تھے۔ اب کوئی سننا چاہے تو اپنے لئے انگارا تیار پائے');
INSERT INTO "jalandhari" VALUES(72,10,'72:10','اور یہ کہ ہمیں معلوم نہیں کہ اس سے اہل زمین کے حق میں برائی مقصود ہے یا ان کے پروردگار نے ان کی بھلائی کا ارادہ فرمایا ہے');
INSERT INTO "jalandhari" VALUES(72,11,'72:11','اور یہ کہ ہم میں کوئی نیک ہیں اور کوئی اور طرح کے۔ ہمارے کئی طرح کے مذہب ہیں');
INSERT INTO "jalandhari" VALUES(72,12,'72:12','اور یہ کہ ہم نے یقین کرلیا ہے کہ ہم زمین میں (خواہ کہیں ہوں) خدا کو ہرا نہیں سکتے اور نہ بھاگ کر اس کو تھکا سکتے ہیں');
INSERT INTO "jalandhari" VALUES(72,13,'72:13','اور جب ہم نے ہدایت (کی کتاب) سنی اس پر ایمان لے آئے۔ تو جو شخص اپنے پروردگار پر ایمان لاتا ہے اس کو نہ نقصان کا خوف ہے نہ ظلم کا');
INSERT INTO "jalandhari" VALUES(72,14,'72:14','اور یہ کہ ہم میں بعض فرمانبردار ہیں اور بعض (نافرمان) گنہگار ہیں۔ تو جو فرمانبردار ہوئے وہ سیدھے رستے پر چلے');
INSERT INTO "jalandhari" VALUES(72,15,'72:15','اور جو گنہگار ہوئے وہ دوزخ کا ایندھن بنے');
INSERT INTO "jalandhari" VALUES(72,16,'72:16','اور (اے پیغمبر) یہ (بھی ان سے کہہ دو) کہ اگر یہ لوگ سیدھے رستے پر رہتے تو ہم ان کے پینے کو بہت سا پانی دیتے');
INSERT INTO "jalandhari" VALUES(72,17,'72:17','تاکہ اس سے ان کی آزمائش کریں۔ اور جو شخص اپنے پروردگار کی یاد سے منہ پھیرے گا وہ اس کو سخت عذاب میں داخل کرے گا');
INSERT INTO "jalandhari" VALUES(72,18,'72:18','اور یہ کہ مسجدیں (خاص) خدا کی ہیں تو خدا کے ساتھ کسی اور کی عبادت نہ کرو');
INSERT INTO "jalandhari" VALUES(72,19,'72:19','اور جب خدا کے بندے (محمدصلى الله عليه وسلم) اس کی عبادت کو کھڑے ہوئے تو کافر ان کے گرد ہجوم کرلینے کو تھے');
INSERT INTO "jalandhari" VALUES(72,20,'72:20','کہہ دو کہ میں تو اپنے پروردگار ہی کی عبادت کرتا ہوں اور کسی کو اس کا شریک نہیں بناتا');
INSERT INTO "jalandhari" VALUES(72,21,'72:21','(یہ بھی) کہہ دو کہ میں تمہارے حق میں نقصان اور نفع کا کچھ اختیار نہیں رکھتا');
INSERT INTO "jalandhari" VALUES(72,22,'72:22','(یہ بھی) کہہ دو کہ خدا (کے عذاب) سے مجھے کوئی پناہ نہیں دے سکتا۔ اور میں اس کے سوا کہیں جائے پناہ نہیں دیکھتا');
INSERT INTO "jalandhari" VALUES(72,23,'72:23','ہاں خدا کی طرف سے احکام کا اور اس کے پیغاموں کا پہنچا دینا (ہی) میرے ذمے ہے۔ اور جو شخص خدا اور اس کے پیغمبر کی نافرمانی کرے گا تو ایسوں کے لئے جہنم کی آگ ہے ہمیشہ ہمیشہ اس میں رہیں گے');
INSERT INTO "jalandhari" VALUES(72,24,'72:24','یہاں تک کہ جب یہ لوگ وہ (دن) دیکھ لیں گے جس کا ان سے وعدہ کیا جاتا ہے تب ان کو معلوم ہو جائے گا کہ مددگار کس کے کمزور اور شمار کن کا تھوڑا ہے');
INSERT INTO "jalandhari" VALUES(72,25,'72:25','کہہ دو کہ جس (دن) کا تم سے وعدہ کیا جاتا ہے میں نہیں جانتا کہ وہ (عن) قریب (آنے والا ہے) یا میرے پروردگار نے اس کی مدت دراز کر دی ہے');
INSERT INTO "jalandhari" VALUES(72,26,'72:26','غیب (کی بات) جاننے والا ہے اور کسی پر اپنے غیب کو ظاہر نہیں کرتا');
INSERT INTO "jalandhari" VALUES(72,27,'72:27','ہاں جس پیغمبر کو پسند فرمائے تو اس (کو غیب کی باتیں بتا دیتا اور اس) کے آگے اور پیچھے نگہبان مقرر کر دیتا ہے');
INSERT INTO "jalandhari" VALUES(72,28,'72:28','تاکہ معلوم فرمائے کہ انہوں نے اپنے پروردگار کے پیغام پہنچا دیئے ہیں اور (یوں تو) اس نے ان کی سب چیزوں کو ہر طرف سے قابو کر رکھا ہے اور ایک ایک چیز گن رکھی ہے');
INSERT INTO "jalandhari" VALUES(73,1,'73:1','اے (محمدصلى الله عليه وسلم) جو کپڑے میں لپٹ رہے ہو');
INSERT INTO "jalandhari" VALUES(73,2,'73:2','رات کو قیام کیا کرو مگر تھوڑی سی رات');
INSERT INTO "jalandhari" VALUES(73,3,'73:3','(قیام) آدھی رات (کیا کرو)');
INSERT INTO "jalandhari" VALUES(73,4,'73:4','یا اس سے کچھ کم یا کچھ زیادہ اور قرآن کو ٹھہر ٹھہر کر پڑھا کرو');
INSERT INTO "jalandhari" VALUES(73,5,'73:5','ہم عنقریب تم پر ایک بھاری فرمان نازل کریں گے');
INSERT INTO "jalandhari" VALUES(73,6,'73:6','کچھ شک نہیں کہ رات کا اٹھنا (نفس بہیمی) کو سخت پامال کرتا ہے اور اس وقت ذکر بھی خوب درست ہوتا ہے');
INSERT INTO "jalandhari" VALUES(73,7,'73:7','دن کے وقت تو تمہیں اور بہت سے شغل ہوتے ہیں');
INSERT INTO "jalandhari" VALUES(73,8,'73:8','تو اپنے پروردگار کے نام کا ذکر کرو اور ہر طرف سے بےتعلق ہو کر اسی کی طرف متوجہ ہوجاؤ');
INSERT INTO "jalandhari" VALUES(73,9,'73:9','مشرق اور مغرب کا مالک (ہے اور) اس کے سوا کوئی معبود نہیں تو اسی کو اپنا کارساز بناؤ');
INSERT INTO "jalandhari" VALUES(73,10,'73:10','اور جو جو (دل آزار) باتیں یہ لوگ کہتے ہیں ان کو سہتے رہو اور اچھے طریق سے ان سے کنارہ کش رہو');
INSERT INTO "jalandhari" VALUES(73,11,'73:11','اور مجھے ان جھٹلانے والوں سے جو دولتمند ہیں سمجھ لینے دو اور ان کو تھوڑی سی مہلت دے دو');
INSERT INTO "jalandhari" VALUES(73,12,'73:12','کچھ شک نہیں کہ ہمارے پاس بیڑیاں ہیں اور بھڑکتی ہوئی آگ ہے');
INSERT INTO "jalandhari" VALUES(73,13,'73:13','اور گلوگیر کھانا ہے اور درد دینے والا عذاب (بھی) ہے');
INSERT INTO "jalandhari" VALUES(73,14,'73:14','جس دن زمین اور پہاڑ کانپنے لگیں اور پہاڑ ایسے بھر بھرے (گویا) ریت کے ٹیلے ہوجائیں');
INSERT INTO "jalandhari" VALUES(73,15,'73:15','(اے اہل مکہ) جس طرح ہم نے فرعون کے پاس (موسیٰ کو) پیغمبر (بنا کر) بھیجا تھا (اسی طرح) تمہارے پاس بھی (محمدصلى الله عليه وسلم) رسول بھیجے ہیں جو تمہارے مقابلے میں گواہ ہوں گے');
INSERT INTO "jalandhari" VALUES(73,16,'73:16','سو فرعون نے (ہمارے) پیغمبر کا کہا نہ مانا تو ہم نے اس کو بڑے وبال میں پکڑ لیا');
INSERT INTO "jalandhari" VALUES(73,17,'73:17','اگر تم بھی (ان پیغمبروں کو) نہ مانو گے تو اس دن سے کیونکر بچو گے جو بچّوں کو بوڑھا کر دے گا');
INSERT INTO "jalandhari" VALUES(73,18,'73:18','(اور) جس سے آسمان پھٹ جائے گا۔ یہ اس کا وعدہ (پورا) ہو کر رہے گا');
INSERT INTO "jalandhari" VALUES(73,19,'73:19','یہ (قرآن) تو نصیحت ہے۔ سو جو چاہے اپنے پروردگار تک (پہنچنے کا) رستہ اختیار کرلے');
INSERT INTO "jalandhari" VALUES(73,20,'73:20','تمہارا پروردگار خوب جانتا ہے کہ تم اور تمہارے ساتھ کے لوگ (کبھی) دو تہائی رات کے قریب اور (کبھی) آدھی رات اور (کبھی) تہائی رات قیام کیا کرتے ہو۔ اور خدا تو رات اور دن کا اندازہ رکھتا ہے۔ اس نے معلوم کیا کہ تم اس کو نباہ نہ سکو گے تو اس نے تم پر مہربانی کی۔ پس جتنا آسانی سے ہوسکے (اتنا) قرآن پڑھ لیا کرو۔ اس نے جانا کہ تم میں بعض بیمار بھی ہوتے ہیں اور بعض خدا کے فضل (یعنی معاش) کی تلاش میں ملک میں سفر کرتے ہیں اور بعض خدا کی راہ میں لڑتے ہیں۔ تو جتنا آسانی سے ہوسکے اتنا پڑھ لیا کرو۔ اور نماز پڑھتے رہو اور زکوٰة ادا کرتے رہو اور خدا کو نیک (اور خلوص نیت سے) قرض دیتے رہو۔ اور جو عمل نیک تم اپنے لئے آگے بھیجو گے اس کو خدا کے ہاں بہتر اور صلے میں بزرگ تر پاؤ گے۔ اور خدا سے بخشش مانگتے رہو۔ بےشک خدا بخشنے والا مہربان ہے');
INSERT INTO "jalandhari" VALUES(74,1,'74:1','اے (محمدصلى الله عليه وسلم) جو کپڑا لپیٹے پڑے ہو');
INSERT INTO "jalandhari" VALUES(74,2,'74:2','اُٹھو اور ہدایت کرو');
INSERT INTO "jalandhari" VALUES(74,3,'74:3','اور اپنے پروردگار کی بڑائی کرو');
INSERT INTO "jalandhari" VALUES(74,4,'74:4','اور اپنے کپڑوں کو پاک رکھو');
INSERT INTO "jalandhari" VALUES(74,5,'74:5','اور ناپاکی سے دور رہو');
INSERT INTO "jalandhari" VALUES(74,6,'74:6','اور (اس نیت سے) احسان نہ کرو کہ اس سے زیادہ کے طالب ہو');
INSERT INTO "jalandhari" VALUES(74,7,'74:7','اور اپنے پروردگار کے لئے صبر کرو');
INSERT INTO "jalandhari" VALUES(74,8,'74:8','جب صور پھونکا جائے گا');
INSERT INTO "jalandhari" VALUES(74,9,'74:9','وہ دن کا مشکل دن ہوگا');
INSERT INTO "jalandhari" VALUES(74,10,'74:10','(یعنی) کافروں پر آسان نہ ہوگا');
INSERT INTO "jalandhari" VALUES(74,11,'74:11','ہمیں اس شخص سے سمجھ لینے دو جس کو ہم نے اکیلا پیدا کیا');
INSERT INTO "jalandhari" VALUES(74,12,'74:12','اور مال کثیر دیا');
INSERT INTO "jalandhari" VALUES(74,13,'74:13','اور (ہر وقت اس کے پاس) حاضر رہنے والے بیٹے دیئے');
INSERT INTO "jalandhari" VALUES(74,14,'74:14','اور ہر طرح کے سامان میں وسعت دی');
INSERT INTO "jalandhari" VALUES(74,15,'74:15','ابھی خواہش رکھتا ہے کہ اور زیادہ دیں');
INSERT INTO "jalandhari" VALUES(74,16,'74:16','ایسا ہرگز نہیں ہوگا۔ یہ ہماری آیتیں کا دشمن رہا ہے');
INSERT INTO "jalandhari" VALUES(74,17,'74:17','ہم اسے صعود پر چڑھائیں گے');
INSERT INTO "jalandhari" VALUES(74,18,'74:18','اس نے فکر کیا اور تجویز کی');
INSERT INTO "jalandhari" VALUES(74,19,'74:19','یہ مارا جائے اس نے کیسی تجویز کی');
INSERT INTO "jalandhari" VALUES(74,20,'74:20','پھر یہ مارا جائے اس نے کیسی تجویز کی');
INSERT INTO "jalandhari" VALUES(74,21,'74:21','پھر تامل کیا');
INSERT INTO "jalandhari" VALUES(74,22,'74:22','پھر تیوری چڑھائی اور منہ بگاڑ لیا');
INSERT INTO "jalandhari" VALUES(74,23,'74:23','پھر پشت پھیر کر چلا اور (قبول حق سے) غرور کیا');
INSERT INTO "jalandhari" VALUES(74,24,'74:24','پھر کہنے لگا کہ یہ تو جادو ہے جو (اگلوں سے) منتقل ہوتا آیا ہے');
INSERT INTO "jalandhari" VALUES(74,25,'74:25','(پھر بولا) یہ (خدا کا کلام نہیں بلکہ) بشر کا کلام ہے');
INSERT INTO "jalandhari" VALUES(74,26,'74:26','ہم عنقریب اس کو سقر میں داخل کریں گے');
INSERT INTO "jalandhari" VALUES(74,27,'74:27','اور تم کیا سمجھے کہ سقر کیا ہے؟');
INSERT INTO "jalandhari" VALUES(74,28,'74:28','(وہ آگ ہے کہ) نہ باقی رکھے گی اور نہ چھوڑے گی');
INSERT INTO "jalandhari" VALUES(74,29,'74:29','اور بدن جھلس کر سیاہ کردے گی');
INSERT INTO "jalandhari" VALUES(74,30,'74:30','اس پر اُنیس داروغہ ہیں');
INSERT INTO "jalandhari" VALUES(74,31,'74:31','اور ہم نے دوزخ کے داروغہ فرشتے بنائے ہیں۔ اور ان کا شمار کافروں کی آزمائش کے لئے مقرر کیا ہے (اور) اس لئے کہ اہل کتاب یقین کریں اور مومنوں کا ایمان اور زیادہ ہو اور اہل کتاب اور مومن شک نہ لائیں۔ اور اس لئے کہ جن لوگوں کے دلوں میں (نفاق کا) مرض ہے اور (جو) کافر (ہیں) کہیں کہ اس مثال (کے بیان کرنے) سے خدا کا مقصد کیا ہے؟ اسی طرح خدا جس کو چاہتا ہے گمراہ کرتا ہے اور جس کو چاہتا ہے ہدایت کرتا ہے اور تمہارے پروردگار کے لشکروں کو اس کے سوا کوئی نہیں جانتا۔ اور یہ تو بنی آدم کے لئے نصیحت ہے');
INSERT INTO "jalandhari" VALUES(74,32,'74:32','ہاں ہاں (ہمیں) چاند کی قسم');
INSERT INTO "jalandhari" VALUES(74,33,'74:33','اور رات کی جب پیٹھ پھیرنے لگے');
INSERT INTO "jalandhari" VALUES(74,34,'74:34','اور صبح کی جب روشن ہو');
INSERT INTO "jalandhari" VALUES(74,35,'74:35','کہ وہ (آگ) ایک بہت بڑی (آفت) ہے');
INSERT INTO "jalandhari" VALUES(74,36,'74:36','(اور) بنی آدم کے لئے مؤجب خوف');
INSERT INTO "jalandhari" VALUES(74,37,'74:37','جو تم میں سے آگے بڑھنا چاہے یا پیچھے رہنا چاہے');
INSERT INTO "jalandhari" VALUES(74,38,'74:38','ہر شخص اپنے اعمال کے بدلے گرو ہے');
INSERT INTO "jalandhari" VALUES(74,39,'74:39','مگر داہنی طرف والے (نیک لوگ)');
INSERT INTO "jalandhari" VALUES(74,40,'74:40','(کہ) وہ باغہائے بہشت میں (ہوں گے اور) پوچھتے ہوں گے');
INSERT INTO "jalandhari" VALUES(74,41,'74:41','(یعنی آگ میں جلنے والے) گنہگاروں سے');
INSERT INTO "jalandhari" VALUES(74,42,'74:42','کہ تم دوزخ میں کیوں پڑے؟');
INSERT INTO "jalandhari" VALUES(74,43,'74:43','وہ جواب دیں گے کہ ہم نماز نہیں پڑھتے تھے');
INSERT INTO "jalandhari" VALUES(74,44,'74:44','اور نہ فقیروں کو کھانا کھلاتے تھے');
INSERT INTO "jalandhari" VALUES(74,45,'74:45','اور اہل باطل کے ساتھ مل کر (حق سے) انکار کرتے تھے');
INSERT INTO "jalandhari" VALUES(74,46,'74:46','اور روز جزا کو جھٹلاتے تھے');
INSERT INTO "jalandhari" VALUES(74,47,'74:47','یہاں تک کہ ہمیں موت آگئی');
INSERT INTO "jalandhari" VALUES(74,48,'74:48','(تو اس حال میں) سفارش کرنے والوں کی سفارش ان کے حق میں کچھ فائدہ نہ دے گی');
INSERT INTO "jalandhari" VALUES(74,49,'74:49','ان کو کیا ہوا ہے کہ نصیحت سے روگرداں ہو رہے ہیں');
INSERT INTO "jalandhari" VALUES(74,50,'74:50','گویا گدھے ہیں کہ بدک جاتے ہیں');
INSERT INTO "jalandhari" VALUES(74,51,'74:51','(یعنی) شیر سے ڈر کر بھاگ جاتے ہیں');
INSERT INTO "jalandhari" VALUES(74,52,'74:52','اصل یہ ہے کہ ان میں سے ہر شخص یہ چاہتا ہے کہ اس کے پاس کھلی ہوئی کتاب آئے');
INSERT INTO "jalandhari" VALUES(74,53,'74:53','ایسا ہرگز نہیں ہوگا۔ حقیقت یہ ہے کہ ان کو آخرت کا خوف ہی نہیں');
INSERT INTO "jalandhari" VALUES(74,54,'74:54','کچھ شک نہیں کہ یہ نصیحت ہے');
INSERT INTO "jalandhari" VALUES(74,55,'74:55','تو جو چاہے اسے یاد رکھے');
INSERT INTO "jalandhari" VALUES(74,56,'74:56','اور یاد بھی تب ہی رکھیں گے جب خدا چاہے۔ وہی ڈرنے کے لائق اور بخشش کا مالک ہے');
INSERT INTO "jalandhari" VALUES(75,1,'75:1','ہم کو روز قیامت کی قسم');
INSERT INTO "jalandhari" VALUES(75,2,'75:2','اور نفس لوامہ کی (کہ سب لوگ اٹھا کر) کھڑے کئے جائیں گے');
INSERT INTO "jalandhari" VALUES(75,3,'75:3','کیا انسان یہ خیال کرتا ہے کہ ہم اس کی (بکھری ہوئی) ہڈیاں اکٹھی نہیں کریں گے؟');
INSERT INTO "jalandhari" VALUES(75,4,'75:4','ضرور کریں گے (اور) ہم اس بات پر قادر ہیں کہ اس کی پور پور درست کردیں');
INSERT INTO "jalandhari" VALUES(75,5,'75:5','مگر انسان چاہتا ہے کہ آگے کو خود سری کرتا جائے');
INSERT INTO "jalandhari" VALUES(75,6,'75:6','پوچھتا ہے کہ قیامت کا دن کب ہوگا؟');
INSERT INTO "jalandhari" VALUES(75,7,'75:7','جب آنکھیں چندھیا جائیں');
INSERT INTO "jalandhari" VALUES(75,8,'75:8','اور چاند گہنا جائے');
INSERT INTO "jalandhari" VALUES(75,9,'75:9','اور سورج اور چاند جمع کردیئے جائیں');
INSERT INTO "jalandhari" VALUES(75,10,'75:10','اس دن انسان کہے گا کہ (اب) کہاں بھاگ جاؤں؟');
INSERT INTO "jalandhari" VALUES(75,11,'75:11','بےشک کہیں پناہ نہیں');
INSERT INTO "jalandhari" VALUES(75,12,'75:12','اس روز پروردگار ہی کے پاس ٹھکانا ہے');
INSERT INTO "jalandhari" VALUES(75,13,'75:13','اس دن انسان کو جو (عمل) اس نے آگے بھیجے اور پیچھے چھوڑے ہوں گے سب بتا دیئے جائیں گے');
INSERT INTO "jalandhari" VALUES(75,14,'75:14','بلکہ انسان آپ اپنا گواہ ہے');
INSERT INTO "jalandhari" VALUES(75,15,'75:15','اگرچہ عذر ومعذرت کرتا رہے');
INSERT INTO "jalandhari" VALUES(75,16,'75:16','اور (اے محمدصلى الله عليه وسلم) وحی کے پڑھنے کے لئے اپنی زبان نہ چلایا کرو کہ اس کو جلد یاد کرلو');
INSERT INTO "jalandhari" VALUES(75,17,'75:17','اس کا جمع کرنا اور پڑھانا ہمارے ذمے ہے');
INSERT INTO "jalandhari" VALUES(75,18,'75:18','جب ہم وحی پڑھا کریں تو تم (اس کو سنا کرو اور) پھر اسی طرح پڑھا کرو');
INSERT INTO "jalandhari" VALUES(75,19,'75:19','پھر اس (کے معانی) کا بیان بھی ہمارے ذمے ہے');
INSERT INTO "jalandhari" VALUES(75,20,'75:20','مگر (لوگو) تم دنیا کو دوست رکھتے ہو');
INSERT INTO "jalandhari" VALUES(75,21,'75:21','اور آخرت کو ترک کئے دیتے ہو');
INSERT INTO "jalandhari" VALUES(75,22,'75:22','اس روز بہت سے منہ رونق دار ہوں گے');
INSERT INTO "jalandhari" VALUES(75,23,'75:23','اور) اپنے پروردگار کے محو دیدار ہوں گے');
INSERT INTO "jalandhari" VALUES(75,24,'75:24','اور بہت سے منہ اس دن اداس ہوں گے');
INSERT INTO "jalandhari" VALUES(75,25,'75:25','خیال کریں گے کہ ان پر مصیبت واقع ہونے کو ہے');
INSERT INTO "jalandhari" VALUES(75,26,'75:26','دیکھو جب جان گلے تک پہنچ جائے');
INSERT INTO "jalandhari" VALUES(75,27,'75:27','اور لوگ کہنے لگیں (اس وقت) کون جھاڑ پھونک کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(75,28,'75:28','اور اس (جان بلب) نے سمجھا کہ اب سب سے جدائی ہے');
INSERT INTO "jalandhari" VALUES(75,29,'75:29','اور پنڈلی سے پنڈلی لپٹ جائے');
INSERT INTO "jalandhari" VALUES(75,30,'75:30','اس دن تجھ کو اپنے پروردگار کی طرف چلنا ہے');
INSERT INTO "jalandhari" VALUES(75,31,'75:31','تو اس (ناعاقبت) اندیش نے نہ تو (کلام خدا) کی تصدیق کی نہ نماز پڑھی');
INSERT INTO "jalandhari" VALUES(75,32,'75:32','بلکہ جھٹلایا اور منہ پھیر لیا');
INSERT INTO "jalandhari" VALUES(75,33,'75:33','پھر اپنے گھر والوں کے پاس اکڑتا ہوا چل دیا');
INSERT INTO "jalandhari" VALUES(75,34,'75:34','افسوس ہے تجھ پر پھر افسوس ہے');
INSERT INTO "jalandhari" VALUES(75,35,'75:35','پھر افسوس ہے تجھ پر پھر افسوس ہے');
INSERT INTO "jalandhari" VALUES(75,36,'75:36','کیا انسان خیال کرتا ہے کہ یوں ہی چھوڑ دیا جائے گا؟');
INSERT INTO "jalandhari" VALUES(75,37,'75:37','کیا وہ منی کا جو رحم میں ڈالی جاتی ہے ایک قطرہ نہ تھا؟');
INSERT INTO "jalandhari" VALUES(75,38,'75:38','پھر لوتھڑا ہوا پھر (خدا نے) اس کو بنایا پھر (اس کے اعضا کو) درست کیا');
INSERT INTO "jalandhari" VALUES(75,39,'75:39','پھر اس کی دو قسمیں بنائیں (ایک) مرد اور (ایک) عورت');
INSERT INTO "jalandhari" VALUES(75,40,'75:40','کیا اس خالق کو اس بات پر قدرت نہیں کہ مردوں کو جلا اُٹھائے؟');
INSERT INTO "jalandhari" VALUES(76,1,'76:1','بےشک انسان پر زمانے میں ایک ایسا وقت بھی آچکا ہے کہ وہ کوئی چیز قابل ذکر نہ تھی');
INSERT INTO "jalandhari" VALUES(76,2,'76:2','ہم نے انسان کو نطفہٴ مخلوط سے پیدا کیا تاکہ اسے آزمائیں تو ہم نے اس کو سنتا دیکھتا بنایا');
INSERT INTO "jalandhari" VALUES(76,3,'76:3','(اور) اسے رستہ بھی دکھا دیا۔ (اب) وہ خواہ شکرگزار ہو خواہ ناشکرا');
INSERT INTO "jalandhari" VALUES(76,4,'76:4','ہم نے کافروں کے لئے زنجیر اور طوق اور دہکتی آگ تیار کر رکھی ہے');
INSERT INTO "jalandhari" VALUES(76,5,'76:5','جو نیکو کار ہیں اور وہ ایسی شراب نوش جان کریں گے جس میں کافور کی آمیزش ہوگی');
INSERT INTO "jalandhari" VALUES(76,6,'76:6','یہ ایک چشمہ ہے جس میں سے خدا کے بندے پئیں گے اور اس میں سے (چھوٹی چھوٹی) نہریں نکالیں گے');
INSERT INTO "jalandhari" VALUES(76,7,'76:7','یہ لوگ نذریں پوری کرتے ہیں اور اس دن سے جس کی سختی پھیل رہی ہوگی خوف رکھتے ہیں');
INSERT INTO "jalandhari" VALUES(76,8,'76:8','اور باوجود یہ کہ ان کو خود طعام کی خواہش (اور حاجت) ہے فقیروں اور یتیموں اور قیدیوں کو کھلاتے ہیں');
INSERT INTO "jalandhari" VALUES(76,9,'76:9','(اور کہتے ہیں کہ) ہم تم کو خالص خدا کے لئے کھلاتے ہیں۔ نہ تم سے عوض کے خواستگار ہیں نہ شکرگزاری کے (طلبگار)');
INSERT INTO "jalandhari" VALUES(76,10,'76:10','ہم کو اپنے پروردگار سے اس دن کا ڈر لگتا ہے (جو چہروں کو) کریہہ المنظر اور (دلوں کو) سخت (مضطر کر دینے والا) ہے');
INSERT INTO "jalandhari" VALUES(76,11,'76:11','تو خدا ان کو اس دن کی سختی سے بچالے گا اور تازگی اور خوش دلی عنایت فرمائے گا');
INSERT INTO "jalandhari" VALUES(76,12,'76:12','اور ان کے صبر کے بدلے ان کو بہشت (کے باغات) اور ریشم (کے ملبوسات) عطا کرے گا');
INSERT INTO "jalandhari" VALUES(76,13,'76:13','ان میں وہ تختوں پر تکیے لگائے بیٹھے ہوں گے۔ وہاں نہ دھوپ (کی حدت) دیکھیں گے نہ سردی کی شدت');
INSERT INTO "jalandhari" VALUES(76,14,'76:14','ان سے (ثمردار شاخیں اور) ان کے سائے قریب ہوں گے اور میوؤں کے گچھے جھکے ہوئے لٹک رہے ہوں گے');
INSERT INTO "jalandhari" VALUES(76,15,'76:15','خدام) چاندی کے باسن لئے ہوئے ان کے اردگرد پھریں گے اور شیشے کے (نہایت شفاف) گلاس');
INSERT INTO "jalandhari" VALUES(76,16,'76:16','اور شیشے بھی چاندی کے جو ٹھیک اندازے کے مطابق بنائے گئے ہیں');
INSERT INTO "jalandhari" VALUES(76,17,'76:17','اور وہاں ان کو ایسی شراب (بھی) پلائی جائے گی جس میں سونٹھ کی آمیزش ہوگی');
INSERT INTO "jalandhari" VALUES(76,18,'76:18','یہ بہشت میں ایک چشمہ ہے جس کا نام سلسبیل ہے');
INSERT INTO "jalandhari" VALUES(76,19,'76:19','اور ان کے پاس لڑکے آتے جاتے ہوں گے جو ہمیشہ (ایک ہی حالت پر) رہیں گے۔ جب تم ان پر نگاہ ڈالو تو خیال کرو کہ بکھرے ہوئے موتی ہیں');
INSERT INTO "jalandhari" VALUES(76,20,'76:20','اور بہشت میں (جہاں) آنکھ اٹھاؤ گے کثرت سے نعمت اور عظیم (الشان) سلطنت دیکھو گے');
INSERT INTO "jalandhari" VALUES(76,21,'76:21','ان (کے بدنوں) پر دیبا سبز اور اطلس کے کپڑے ہوں گے۔ اور انہیں چاندی کے کنگن پہنائے جائیں گے اور ان کا پروردگار ان کو نہایت پاکیزہ شراب پلائے گا');
INSERT INTO "jalandhari" VALUES(76,22,'76:22','یہ تمہارا صلہ اور تمہاری کوشش (خدا کے ہاں) مقبول ہوئی');
INSERT INTO "jalandhari" VALUES(76,23,'76:23','اے محمد(صلى الله عليه وسلم) ہم نے تم پر قرآن آہستہ آہستہ نازل کیا ہے');
INSERT INTO "jalandhari" VALUES(76,24,'76:24','تو اپنے پروردگار کے حکم کے مطابق صبر کئے رہو اور ان لوگوں میں سے کسی بد عمل اور ناشکرے کا کہا نہ مانو');
INSERT INTO "jalandhari" VALUES(76,25,'76:25','اور صبح وشام اپنے پروردگار کا نام لیتے رہو');
INSERT INTO "jalandhari" VALUES(76,26,'76:26','اور رات کو بڑی رات تک سجدے کرو اور اس کی پاکی بیان کرتے رہو');
INSERT INTO "jalandhari" VALUES(76,27,'76:27','یہ لوگ دنیا کو دوست رکھتے ہیں اور (قیامت کے) بھاری دن کو پس پشت چھوڑے دیتے ہیں');
INSERT INTO "jalandhari" VALUES(76,28,'76:28','ہم نے ان کو پیدا کیا اور ان کے مقابل کو مضبوط بنایا۔ اور اگر ہم چاہیں تو ان کے بدلے ان ہی کی طرح اور لوگ لے آئیں');
INSERT INTO "jalandhari" VALUES(76,29,'76:29','یہ تو نصیحت ہے۔ جو چاہے اپنے پروردگار کی طرف پہنچنے کا رستہ اختیار کرے');
INSERT INTO "jalandhari" VALUES(76,30,'76:30','اور تم کچھ بھی نہیں چاہ سکتے مگر جو خدا کو منظور ہو۔ بےشک خدا جاننے والا حکمت والا ہے');
INSERT INTO "jalandhari" VALUES(76,31,'76:31','جس کو چاہتا ہے اپنی رحمت میں داخل کرلیتا ہے اور ظالموں کے لئے اس نے دکھ دینے والا عذاب تیار کر رکھا ہے');
INSERT INTO "jalandhari" VALUES(77,1,'77:1','ہواؤں کی قسم جو نرم نرم چلتی ہیں');
INSERT INTO "jalandhari" VALUES(77,2,'77:2','پھر زور پکڑ کر جھکڑ ہو جاتی ہیں');
INSERT INTO "jalandhari" VALUES(77,3,'77:3','اور (بادلوں کو) پھاڑ کر پھیلا دیتی ہیں');
INSERT INTO "jalandhari" VALUES(77,4,'77:4','پھر ان کو پھاڑ کر جدا جدا کر دیتی ہیں');
INSERT INTO "jalandhari" VALUES(77,5,'77:5','پھر فرشتوں کی قسم جو وحی لاتے ہیں');
INSERT INTO "jalandhari" VALUES(77,6,'77:6','تاکہ عذر (رفع) کردیا جائے یا ڈر سنا دیا جائے');
INSERT INTO "jalandhari" VALUES(77,7,'77:7','کہ جس بات کا تم سے وعدہ کیا جاتا ہے وہ ہو کر رہے گی');
INSERT INTO "jalandhari" VALUES(77,8,'77:8','جب تاروں کی چمک جاتی رہے');
INSERT INTO "jalandhari" VALUES(77,9,'77:9','اور جب آسمان پھٹ جائے');
INSERT INTO "jalandhari" VALUES(77,10,'77:10','اور جب پہاڑ اُڑے اُڑے پھریں');
INSERT INTO "jalandhari" VALUES(77,11,'77:11','اور جب پیغمبر فراہم کئے جائیں');
INSERT INTO "jalandhari" VALUES(77,12,'77:12','بھلا (ان امور میں) تاخیر کس دن کے لئے کی گئی؟');
INSERT INTO "jalandhari" VALUES(77,13,'77:13','فیصلے کے دن کے لئے');
INSERT INTO "jalandhari" VALUES(77,14,'77:14','اور تمہیں کیا خبر کہ فیصلے کا دن کیا ہے؟');
INSERT INTO "jalandhari" VALUES(77,15,'77:15','اس دن جھٹلانے والوں کے لئے خرابی ہے');
INSERT INTO "jalandhari" VALUES(77,16,'77:16','کیا ہم نے پہلے لوگوں کو ہلاک نہیں کر ڈالا');
INSERT INTO "jalandhari" VALUES(77,17,'77:17','پھر ان پچھلوں کو بھی ان کے پیچھے بھیج دیتے ہیں');
INSERT INTO "jalandhari" VALUES(77,18,'77:18','ہم گنہگاروں کے ساتھ ایسا ہی کیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(77,19,'77:19','اس دن جھٹلانے والوں کی خرابی ہے');
INSERT INTO "jalandhari" VALUES(77,20,'77:20','کیا ہم نے تم کو حقیر پانی سے نہیں پیدا کیا؟');
INSERT INTO "jalandhari" VALUES(77,21,'77:21','اس کو ایک محفوظ جگہ میں رکھا');
INSERT INTO "jalandhari" VALUES(77,22,'77:22','ایک وقت معین تک');
INSERT INTO "jalandhari" VALUES(77,23,'77:23','پھر اندازہ مقرر کیا اور ہم کیا ہی خوب اندازہ مقرر کرنے والے ہیں');
INSERT INTO "jalandhari" VALUES(77,24,'77:24','اس دن جھٹلانے والوں کی خرابی ہے');
INSERT INTO "jalandhari" VALUES(77,25,'77:25','کیا ہم نے زمین کو سمیٹنے والی نہیں بنایا');
INSERT INTO "jalandhari" VALUES(77,26,'77:26','یعنی) زندوں اور مردوں کو');
INSERT INTO "jalandhari" VALUES(77,27,'77:27','(بنایا) اور اس پر اونچے اونچے پہاڑ رکھ دیئے اور تم لوگوں کو میٹھا پانی پلایا');
INSERT INTO "jalandhari" VALUES(77,28,'77:28','اس دن جھٹلانے والوں کی خرابی ہے');
INSERT INTO "jalandhari" VALUES(77,29,'77:29','جس چیز کو تم جھٹلایا کرتے تھے۔ (اب) اس کی طرف چلو');
INSERT INTO "jalandhari" VALUES(77,30,'77:30','(یعنی) اس سائے کی طرف چلو جس کی تین شاخیں ہیں');
INSERT INTO "jalandhari" VALUES(77,31,'77:31','نہ ٹھنڈی چھاؤں اور نہ لپٹ سے بچاؤ');
INSERT INTO "jalandhari" VALUES(77,32,'77:32','اس سے (آگ کی اتنی اتنی بڑی) چنگاریاں اُڑتی ہیں جیسے محل');
INSERT INTO "jalandhari" VALUES(77,33,'77:33','گویا زرد رنگ کے اونٹ ہیں');
INSERT INTO "jalandhari" VALUES(77,34,'77:34','اس دن جھٹلانے والوں کی خرابی ہے');
INSERT INTO "jalandhari" VALUES(77,35,'77:35','یہ وہ دن ہے کہ( لوگ) لب تک نہ ہلا سکیں گے');
INSERT INTO "jalandhari" VALUES(77,36,'77:36','اور نہ ان کو اجازت دی جائے گی کہ عذر کرسکیں');
INSERT INTO "jalandhari" VALUES(77,37,'77:37','اس دن جھٹلانے والوں کی خرابی ہے');
INSERT INTO "jalandhari" VALUES(77,38,'77:38','یہی فیصلے کا دن ہے (جس میں) ہم نے تم کو اور پہلے لوگوں کو جمع کیا ہے');
INSERT INTO "jalandhari" VALUES(77,39,'77:39','اگر تم کو کوئی داؤں آتا ہو تو مجھ سے کر لو');
INSERT INTO "jalandhari" VALUES(77,40,'77:40','اس دن جھٹلانے والوں کی خرابی ہے');
INSERT INTO "jalandhari" VALUES(77,41,'77:41','بےشک پرہیزگار سایوں اور چشموں میں ہوں گے');
INSERT INTO "jalandhari" VALUES(77,42,'77:42','اور میؤوں میں جو ان کو مرغوب ہوں');
INSERT INTO "jalandhari" VALUES(77,43,'77:43','اور جو عمل تم کرتے رہے تھے ان کے بدلے میں مزے سے کھاؤ اور پیو');
INSERT INTO "jalandhari" VALUES(77,44,'77:44','ہم نیکو کاروں کو ایسا ہی بدلہ دیا کرتے ہیں');
INSERT INTO "jalandhari" VALUES(77,45,'77:45','اس دن جھٹلانے والوں کی خرابی ہوگی');
INSERT INTO "jalandhari" VALUES(77,46,'77:46','(اے جھٹلانے والو!) تم کسی قدر کھا لو اور فائدے اُٹھا لو تم بےشک گنہگار ہو');
INSERT INTO "jalandhari" VALUES(77,47,'77:47','اس دن جھٹلانے والوں کی خرابی ہے');
INSERT INTO "jalandhari" VALUES(77,48,'77:48','اور جب ان سے کہا جاتا ہے کہ (خدا کے آگے) جھکو تو جھکتے نہیں');
INSERT INTO "jalandhari" VALUES(77,49,'77:49','اس دن جھٹلانے والوں کی خرابی ہے');
INSERT INTO "jalandhari" VALUES(77,50,'77:50','اب اس کے بعد یہ کون سی بات پر ایمان لائیں گے؟');
INSERT INTO "jalandhari" VALUES(78,1,'78:1','(یہ) لوگ کس چیز کی نسبت پوچھتے ہیں؟');
INSERT INTO "jalandhari" VALUES(78,2,'78:2','(کیا) بڑی خبر کی نسبت؟');
INSERT INTO "jalandhari" VALUES(78,3,'78:3','جس میں یہ اختلاف کر رہے ہیں');
INSERT INTO "jalandhari" VALUES(78,4,'78:4','دیکھو یہ عنقریب جان لیں گے');
INSERT INTO "jalandhari" VALUES(78,5,'78:5','پھر دیکھو یہ عنقریب جان لیں گے');
INSERT INTO "jalandhari" VALUES(78,6,'78:6','کیا ہم نے زمین کو بچھونا نہیں بنایا');
INSERT INTO "jalandhari" VALUES(78,7,'78:7','اور پہاڑوں کو (ا س کی) میخیں (نہیں ٹھہرایا؟)');
INSERT INTO "jalandhari" VALUES(78,8,'78:8','(بے شک بنایا) اور تم کو جوڑا جوڑابھی پیدا کیا');
INSERT INTO "jalandhari" VALUES(78,9,'78:9','اور نیند کو تمہارے لیے (موجب) آرام بنایا');
INSERT INTO "jalandhari" VALUES(78,10,'78:10','اور رات کو پردہ مقرر کیا');
INSERT INTO "jalandhari" VALUES(78,11,'78:11','اور دن کو معاش (کا وقت) قرار دیا');
INSERT INTO "jalandhari" VALUES(78,12,'78:12','اور تمہارے اوپر سات مضبوط (آسمان) بنائے');
INSERT INTO "jalandhari" VALUES(78,13,'78:13','اور (آفتاب کا) روشن چراغ بنایا');
INSERT INTO "jalandhari" VALUES(78,14,'78:14','اور نچڑتے بادلوں سے موسلا دھار مینہ برسایا');
INSERT INTO "jalandhari" VALUES(78,15,'78:15','تاکہ اس سے اناج اور سبزہ پیدا کریں');
INSERT INTO "jalandhari" VALUES(78,16,'78:16','اور گھنے گھنے باغ');
INSERT INTO "jalandhari" VALUES(78,17,'78:17','بےشک فیصلہ کا دن مقرر ہے');
INSERT INTO "jalandhari" VALUES(78,18,'78:18','جس دن صور پھونکا جائے گا تو تم لوگ غٹ کے غٹ آ موجود ہو گے');
INSERT INTO "jalandhari" VALUES(78,19,'78:19','اور آسمان کھولا جائے گا تو (اس میں) دروازے ہو جائیں گے');
INSERT INTO "jalandhari" VALUES(78,20,'78:20','اور پہاڑ چلائے جائیں گے تو وہ ریت ہو کر رہ جائیں گے');
INSERT INTO "jalandhari" VALUES(78,21,'78:21','بےشک دوزخ گھات میں ہے');
INSERT INTO "jalandhari" VALUES(78,22,'78:22','(یعنی) سرکشوں کا وہی ٹھکانہ ہے');
INSERT INTO "jalandhari" VALUES(78,23,'78:23','اس میں وہ مدتوں پڑے رہیں گے');
INSERT INTO "jalandhari" VALUES(78,24,'78:24','وہاں نہ ٹھنڈک کا مزہ چکھیں گے۔ نہ (کچھ) پینا (نصیب ہو گا)');
INSERT INTO "jalandhari" VALUES(78,25,'78:25','مگر گرم پانی اور بہتی پیپ');
INSERT INTO "jalandhari" VALUES(78,26,'78:26','(یہ) بدلہ ہے پورا پورا');
INSERT INTO "jalandhari" VALUES(78,27,'78:27','یہ لوگ حساب (آخرت) کی امید ہی نہیں رکھتے تھے');
INSERT INTO "jalandhari" VALUES(78,28,'78:28','اور ہماری آیتوں کو جھوٹ سمجھ کر جھٹلاتے رہتے تھے');
INSERT INTO "jalandhari" VALUES(78,29,'78:29','اور ہم نے ہر چیز کو لکھ کر ضبط کر رکھا ہے');
INSERT INTO "jalandhari" VALUES(78,30,'78:30','سو (اب) مزہ چکھو۔ ہم تم پر عذاب ہی بڑھاتے جائیں گے');
INSERT INTO "jalandhari" VALUES(78,31,'78:31','بے شک پرہیز گاروں کے لیے کامیابی ہے');
INSERT INTO "jalandhari" VALUES(78,32,'78:32','(یعنی) باغ اور انگور');
INSERT INTO "jalandhari" VALUES(78,33,'78:33','اور ہم عمر نوجوان عورتیں');
INSERT INTO "jalandhari" VALUES(78,34,'78:34','اور شراب کے چھلکتے ہوئے گلاس');
INSERT INTO "jalandhari" VALUES(78,35,'78:35','وہاں نہ بیہودہ بات سنیں گے نہ جھوٹ (خرافات)');
INSERT INTO "jalandhari" VALUES(78,36,'78:36','یہ تمہارے پروردگار کی طرف سے صلہ ہے انعام کثیر');
INSERT INTO "jalandhari" VALUES(78,37,'78:37','وہ جو آسمانوں اور زمین اور جو ان دونوں میں ہے سب کا مالک ہے بڑا مہربان کسی کو اس سے بات کرنے کا یارا نہیں ہوگا');
INSERT INTO "jalandhari" VALUES(78,38,'78:38','جس دن روح (الامین) اور فرشتے صف باندھ کر کھڑے ہوں گے تو کوئی بول نہ سکے گا مگر جس کو (خدائے رحمٰن) اجازت بخشے اور اس نے بات بھی درست کہی ہو');
INSERT INTO "jalandhari" VALUES(78,39,'78:39','یہ دن برحق ہے۔ پس جو شخص چاہے اپنے پروردگار کے پاس ٹھکانہ بنا ئے');
INSERT INTO "jalandhari" VALUES(78,40,'78:40','ہم نے تم کو عذاب سے جو عنقریب آنے والا ہے آگاہ کر دیا ہے جس دن ہر شخص ان (اعمال) کو جو اس نے آگے بھیجے ہوں گے دیکھ لے گا اور کافر کہے گا کہ اے کاش میں مٹی ہوتا');
INSERT INTO "jalandhari" VALUES(79,1,'79:1','ان (فرشتوں) کی قسم جو ڈوب کر کھینچ لیتے ہیں');
INSERT INTO "jalandhari" VALUES(79,2,'79:2','اور ان کی جو آسانی سے کھول دیتے ہیں');
INSERT INTO "jalandhari" VALUES(79,3,'79:3','اور ان کی جو تیرتے پھرتے ہیں');
INSERT INTO "jalandhari" VALUES(79,4,'79:4','پھر لپک کر آگے بڑھتے ہیں');
INSERT INTO "jalandhari" VALUES(79,5,'79:5','پھر (دنیا کے) کاموں کا انتظام کرتے ہیں');
INSERT INTO "jalandhari" VALUES(79,6,'79:6','(کہ وہ دن آ کر رہے گا) جس دن زمین کو بھونچال آئے گا');
INSERT INTO "jalandhari" VALUES(79,7,'79:7','پھر اس کے پیچھے اور (بھونچال) آئے گا');
INSERT INTO "jalandhari" VALUES(79,8,'79:8','اس دن (لوگوں) کے دل خائف ہو رہے ہوں گے');
INSERT INTO "jalandhari" VALUES(79,9,'79:9','اور آنکھیں جھکی ہوئی');
INSERT INTO "jalandhari" VALUES(79,10,'79:10','(کافر) کہتے ہیں کیا ہم الٹے پاؤں پھر لوٹ جائیں گے');
INSERT INTO "jalandhari" VALUES(79,11,'79:11','بھلا جب ہم کھوکھلی ہڈیاں ہو جائیں گے (تو پھر زندہ کئے جائیں گے)');
INSERT INTO "jalandhari" VALUES(79,12,'79:12','کہتے ہیں کہ یہ لوٹنا تو( موجب) زیاں ہے');
INSERT INTO "jalandhari" VALUES(79,13,'79:13','وہ تو صرف ایک ڈانٹ ہوگی');
INSERT INTO "jalandhari" VALUES(79,14,'79:14','اس وقت وہ (سب) میدان (حشر) میں آ جمع ہوں گے');
INSERT INTO "jalandhari" VALUES(79,15,'79:15','بھلا تم کو موسیٰ کی حکایت پہنچی ہے');
INSERT INTO "jalandhari" VALUES(79,16,'79:16','جب اُن کے پروردگار نے ان کو پاک میدان (یعنی) طویٰ میں پکارا');
INSERT INTO "jalandhari" VALUES(79,17,'79:17','(اور حکم دیا) کہ فرعون کے پاس جاؤ وہ سرکش ہو رہا ہے');
INSERT INTO "jalandhari" VALUES(79,18,'79:18','اور (اس سے) کہو کہ کیا تو چاہتا ہے کہ پاک ہو جائے؟');
INSERT INTO "jalandhari" VALUES(79,19,'79:19','اور میں تجھے تیرے پروردگار کا رستہ بتاؤں تاکہ تجھ کو خوف (پیدا) ہو');
INSERT INTO "jalandhari" VALUES(79,20,'79:20','غرض انہوں نے اس کو بڑی نشانی دکھائی');
INSERT INTO "jalandhari" VALUES(79,21,'79:21','مگر اس نے جھٹلایا اور نہ مانا');
INSERT INTO "jalandhari" VALUES(79,22,'79:22','پھر لوٹ گیا اور تدبیریں کرنے لگا');
INSERT INTO "jalandhari" VALUES(79,23,'79:23','اور (لوگوں کو) اکٹھا کیا اور پکارا');
INSERT INTO "jalandhari" VALUES(79,24,'79:24','کہنے لگا کہ تمہارا سب سے بڑا مالک میں ہوں');
INSERT INTO "jalandhari" VALUES(79,25,'79:25','تو خدا نے اس کو دنیا اور آخرت (دونوں) کے عذاب میں پکڑ لیا');
INSERT INTO "jalandhari" VALUES(79,26,'79:26','جو شخص (خدا سے) ڈر رکھتا ہے اس کے لیے اس (قصے) میں عبرت ہے');
INSERT INTO "jalandhari" VALUES(79,27,'79:27','بھلا تمہارا بنانا آسان ہے یا آسمان کا؟ اسی نے اس کو بنایا');
INSERT INTO "jalandhari" VALUES(79,28,'79:28','اس کی چھت کو اونچا کیا اور پھر اسے برابر کر دیا');
INSERT INTO "jalandhari" VALUES(79,29,'79:29','اور اسی نے رات کو تاریک بنایا اور (دن کو) دھوپ نکالی');
INSERT INTO "jalandhari" VALUES(79,30,'79:30','اور اس کے بعد زمین کو پھیلا دیا');
INSERT INTO "jalandhari" VALUES(79,31,'79:31','اسی نے اس میں سے اس کا پانی نکالا اور چارا اگایا');
INSERT INTO "jalandhari" VALUES(79,32,'79:32','اور اس پر پہاڑوں کابوجھ رکھ دیا');
INSERT INTO "jalandhari" VALUES(79,33,'79:33','یہ سب کچھ تمہارے اور تمہارے چارپایوں کے فائدے کے لیے (کیا )');
INSERT INTO "jalandhari" VALUES(79,34,'79:34','تو جب بڑی آفت آئے گی');
INSERT INTO "jalandhari" VALUES(79,35,'79:35','اس دن انسان اپنے کاموں کو یاد کرے گا');
INSERT INTO "jalandhari" VALUES(79,36,'79:36','اور دوزخ دیکھنے والے کے سامنے نکال کر رکھ دی جائے گی');
INSERT INTO "jalandhari" VALUES(79,37,'79:37','تو جس نے سرکشی کی');
INSERT INTO "jalandhari" VALUES(79,38,'79:38','اور دنیا کی زندگی کو مقدم سمجھا');
INSERT INTO "jalandhari" VALUES(79,39,'79:39','اس کا ٹھکانہ دوزخ ہے');
INSERT INTO "jalandhari" VALUES(79,40,'79:40','اور جو اپنے پروردگار کے سامنے کھڑے ہونے سے ڈرتا اور جی کو خواہشوں سے روکتا رہا');
INSERT INTO "jalandhari" VALUES(79,41,'79:41','اس کا ٹھکانہ بہشت ہے');
INSERT INTO "jalandhari" VALUES(79,42,'79:42','(اے پیغمبر، لوگ )تم سے قیامت کے بارے میں پوچھتے ہیں کہ اس کا وقوع کب ہو گا؟');
INSERT INTO "jalandhari" VALUES(79,43,'79:43','سو تم اس کے ذکر سے کس فکر میں ہو');
INSERT INTO "jalandhari" VALUES(79,44,'79:44','اس کا منتہا (یعنی واقع ہونے کا وقت) تمہارے پروردگار ہی کو (معلوم ہے)');
INSERT INTO "jalandhari" VALUES(79,45,'79:45','جو شخص اس سے ڈر رکھتا ہے تم تو اسی کو ڈر سنانے والے ہو');
INSERT INTO "jalandhari" VALUES(79,46,'79:46','جب وہ اس کو دیکھیں گے (تو ایسا خیال کریں گے) کہ گویا( دنیا میں صرف) ایک شام یا صبح رہے تھے');
INSERT INTO "jalandhari" VALUES(80,1,'80:1','(محمد مصطفٰےصلى الله عليه وسلم) ترش رُو ہوئے اور منہ پھیر بیٹھے');
INSERT INTO "jalandhari" VALUES(80,2,'80:2','کہ ان کے پاس ایک نابینا آیا');
INSERT INTO "jalandhari" VALUES(80,3,'80:3','اور تم کو کیا خبر شاید وہ پاکیزگی حاصل کرتا');
INSERT INTO "jalandhari" VALUES(80,4,'80:4','یا سوچتا تو سمجھانا اسے فائدہ دیتا');
INSERT INTO "jalandhari" VALUES(80,5,'80:5','جو پروا نہیں کرتا');
INSERT INTO "jalandhari" VALUES(80,6,'80:6','اس کی طرف تو تم توجہ کرتے ہو');
INSERT INTO "jalandhari" VALUES(80,7,'80:7','حالانکہ اگر وہ نہ سنورے تو تم پر کچھ (الزام) نہیں');
INSERT INTO "jalandhari" VALUES(80,8,'80:8','اور جو تمہارے پاس دوڑتا ہوا آیا');
INSERT INTO "jalandhari" VALUES(80,9,'80:9','اور (خدا سے) ڈرتا ہے');
INSERT INTO "jalandhari" VALUES(80,10,'80:10','اس سے تم بےرخی کرتے ہو');
INSERT INTO "jalandhari" VALUES(80,11,'80:11','دیکھو یہ (قرآن) نصیحت ہے');
INSERT INTO "jalandhari" VALUES(80,12,'80:12','پس جو چاہے اسے یاد رکھے');
INSERT INTO "jalandhari" VALUES(80,13,'80:13','قابل ادب ورقوں میں (لکھا ہوا)');
INSERT INTO "jalandhari" VALUES(80,14,'80:14','جو بلند مقام پر رکھے ہوئے (اور) پاک ہیں');
INSERT INTO "jalandhari" VALUES(80,15,'80:15','لکھنے والوں کے ہاتھوں میں');
INSERT INTO "jalandhari" VALUES(80,16,'80:16','جو سردار اور نیکو کار ہیں');
INSERT INTO "jalandhari" VALUES(80,17,'80:17','انسان ہلاک ہو جائے کیسا ناشکرا ہے');
INSERT INTO "jalandhari" VALUES(80,18,'80:18','اُسے (خدا نے) کس چیز سے بنایا؟');
INSERT INTO "jalandhari" VALUES(80,19,'80:19','نطفے سے بنایا پھر اس کا اندازہ مقرر کیا');
INSERT INTO "jalandhari" VALUES(80,20,'80:20','پھر اس کے لیے رستہ آسان کر دیا');
INSERT INTO "jalandhari" VALUES(80,21,'80:21','پھر اس کو موت دی پھر قبر میں دفن کرایا');
INSERT INTO "jalandhari" VALUES(80,22,'80:22','پھر جب چاہے گا اسے اٹھا کھڑا کرے گا');
INSERT INTO "jalandhari" VALUES(80,23,'80:23','کچھ شک نہیں کہ خدا نے اسے جو حکم دیا اس نے اس پر عمل نہ کیا');
INSERT INTO "jalandhari" VALUES(80,24,'80:24','تو انسان کو چاہیئے کہ اپنے کھانے کی طرف نظر کرے');
INSERT INTO "jalandhari" VALUES(80,25,'80:25','بے شک ہم ہی نے پانی برسایا');
INSERT INTO "jalandhari" VALUES(80,26,'80:26','پھر ہم ہی نے زمین کو چیرا پھاڑا');
INSERT INTO "jalandhari" VALUES(80,27,'80:27','پھر ہم ہی نے اس میں اناج اگایا');
INSERT INTO "jalandhari" VALUES(80,28,'80:28','اور انگور اور ترکاری');
INSERT INTO "jalandhari" VALUES(80,29,'80:29','اور زیتون اور کھجوریں');
INSERT INTO "jalandhari" VALUES(80,30,'80:30','اور گھنے گھنے باغ');
INSERT INTO "jalandhari" VALUES(80,31,'80:31','اور میوے اور چارا');
INSERT INTO "jalandhari" VALUES(80,32,'80:32','(یہ سب کچھ) تمہارے اور تمہارے چارپایوں کے لیے بنایا');
INSERT INTO "jalandhari" VALUES(80,33,'80:33','تو جب (قیامت کا) غل مچے گا');
INSERT INTO "jalandhari" VALUES(80,34,'80:34','اس دن آدمی اپنے بھائی سے دور بھاگے گا');
INSERT INTO "jalandhari" VALUES(80,35,'80:35','اور اپنی ماں اور اپنے باپ سے');
INSERT INTO "jalandhari" VALUES(80,36,'80:36','اور اپنی بیوی اور اپنے بیٹے سے');
INSERT INTO "jalandhari" VALUES(80,37,'80:37','ہر شخص اس روز ایک فکر میں ہو گا جو اسے( مصروفیت کے لیے) بس کرے گا');
INSERT INTO "jalandhari" VALUES(80,38,'80:38','اور کتنے منہ اس روز چمک رہے ہوں گے');
INSERT INTO "jalandhari" VALUES(80,39,'80:39','خنداں و شاداں (یہ مومنان نیکو کار ہیں)');
INSERT INTO "jalandhari" VALUES(80,40,'80:40','اور کتنے منہ ہوں گے جن پر گرد پڑ رہی ہو گی');
INSERT INTO "jalandhari" VALUES(80,41,'80:41','(اور) سیاہی چڑھ رہی ہو گی');
INSERT INTO "jalandhari" VALUES(80,42,'80:42','یہ کفار بدکردار ہیں');
INSERT INTO "jalandhari" VALUES(81,1,'81:1','جب سورج لپیٹ لیا جائے گا');
INSERT INTO "jalandhari" VALUES(81,2,'81:2','جب تارے بےنور ہو جائیں گے');
INSERT INTO "jalandhari" VALUES(81,3,'81:3','اور جب پہاڑ چلائے جائیں گے');
INSERT INTO "jalandhari" VALUES(81,4,'81:4','اور جب بیانے والی اونٹنیاں بےکار ہو جائیں گی');
INSERT INTO "jalandhari" VALUES(81,5,'81:5','اور جب وحشی جانور جمع اکٹھے ہو جائیں گے');
INSERT INTO "jalandhari" VALUES(81,6,'81:6','اور جب دریا آگ ہو جائیں گے');
INSERT INTO "jalandhari" VALUES(81,7,'81:7','اور جب روحیں (بدنوں سے) ملا دی جائیں گی');
INSERT INTO "jalandhari" VALUES(81,8,'81:8','اور جب لڑکی سے جو زندہ دفنا دی گئی ہو پوچھا جائے گا');
INSERT INTO "jalandhari" VALUES(81,9,'81:9','کہ وہ کس گناہ پرماری گئی');
INSERT INTO "jalandhari" VALUES(81,10,'81:10','اور جب (عملوں کے) دفتر کھولے جائیں گے');
INSERT INTO "jalandhari" VALUES(81,11,'81:11','اور جب آسمانوں کی کھال کھینچ لی جائے گی');
INSERT INTO "jalandhari" VALUES(81,12,'81:12','اور جب دوزخ (کی آگ) بھڑکائی جائے گی');
INSERT INTO "jalandhari" VALUES(81,13,'81:13','اور بہشت جب قریب لائی جائے گی');
INSERT INTO "jalandhari" VALUES(81,14,'81:14','تب ہر شخص معلوم کر لے گا کہ وہ کیا لے کر آیا ہے');
INSERT INTO "jalandhari" VALUES(81,15,'81:15','ہم کو ان ستاروں کی قسم جو پیچھے ہٹ جاتے ہیں');
INSERT INTO "jalandhari" VALUES(81,16,'81:16','(اور) جو سیر کرتے اور غائب ہو جاتے ہیں');
INSERT INTO "jalandhari" VALUES(81,17,'81:17','اور رات کی قسم جب ختم ہونے لگتی ہے');
INSERT INTO "jalandhari" VALUES(81,18,'81:18','اور صبح کی قسم جب نمودار ہوتی ہے');
INSERT INTO "jalandhari" VALUES(81,19,'81:19','کہ بےشک یہ (قرآن) فرشتہٴ عالی مقام کی زبان کا پیغام ہے');
INSERT INTO "jalandhari" VALUES(81,20,'81:20','جو صاحب قوت مالک عرش کے ہاں اونچے درجے والا ہے');
INSERT INTO "jalandhari" VALUES(81,21,'81:21','سردار (اور) امانت دار ہے');
INSERT INTO "jalandhari" VALUES(81,22,'81:22','اور (مکے والو) تمہارے رفیق (یعنی محمدصلى الله عليه وسلم) دیوانے نہیں ہیں');
INSERT INTO "jalandhari" VALUES(81,23,'81:23','بےشک انہوں نے اس (فرشتے) کو( آسمان کے کھلے یعنی) مشرقی کنارے پر دیکھا ہے');
INSERT INTO "jalandhari" VALUES(81,24,'81:24','اور وہ پوشیدہ باتوں (کے ظاہر کرنے) میں بخیل نہیں');
INSERT INTO "jalandhari" VALUES(81,25,'81:25','اور یہ شیطان مردود کا کلام نہیں');
INSERT INTO "jalandhari" VALUES(81,26,'81:26','پھر تم کدھر جا رہے ہو');
INSERT INTO "jalandhari" VALUES(81,27,'81:27','یہ تو جہان کے لوگوں کے لیے نصیحت ہے');
INSERT INTO "jalandhari" VALUES(81,28,'81:28','(یعنی) اس کے لیے جو تم میں سے سیدھی چال چلنا چاہے');
INSERT INTO "jalandhari" VALUES(81,29,'81:29','اور تم کچھ بھی نہیں چاہ سکتے مگر وہی جو خدائے رب العالمین چاہے');
INSERT INTO "jalandhari" VALUES(82,1,'82:1','جب آسمان پھٹ جائے گا');
INSERT INTO "jalandhari" VALUES(82,2,'82:2','اور جب تارے جھڑ پڑیں گے');
INSERT INTO "jalandhari" VALUES(82,3,'82:3','اور جب دریا بہہ (کر ایک دوسرے سے مل) جائیں گے');
INSERT INTO "jalandhari" VALUES(82,4,'82:4','اور جب قبریں اکھیڑ دی جائیں گی');
INSERT INTO "jalandhari" VALUES(82,5,'82:5','تب ہر شخص معلوم کرلے گا کہ اس نے آگے کیا بھیجا تھا اور پیچھے کیا چھوڑا تھا');
INSERT INTO "jalandhari" VALUES(82,6,'82:6','اے انسان تجھ کو اپنے پروردگار کرم گستر کے باب میں کس چیز نے دھوکا دیا');
INSERT INTO "jalandhari" VALUES(82,7,'82:7','(وہی تو ہے) جس نے تجھے بنایا اور (تیرے اعضا کو) ٹھیک کیا اور (تیرے قامت کو) معتدل رکھا');
INSERT INTO "jalandhari" VALUES(82,8,'82:8','اور جس صورت میں چاہا تجھے جوڑ دیا');
INSERT INTO "jalandhari" VALUES(82,9,'82:9','مگر ہیہات تم لوگ جزا کو جھٹلاتے ہو');
INSERT INTO "jalandhari" VALUES(82,10,'82:10','حالانکہ تم پر نگہبان مقرر ہیں');
INSERT INTO "jalandhari" VALUES(82,11,'82:11','عالی قدر( تمہاری باتوں کے) لکھنے والے');
INSERT INTO "jalandhari" VALUES(82,12,'82:12','جو تم کرتے ہو وہ اسے جانتے ہیں');
INSERT INTO "jalandhari" VALUES(82,13,'82:13','بے شک نیکوکار نعمتوں (کی بہشت) میں ہوں گے۔');
INSERT INTO "jalandhari" VALUES(82,14,'82:14','اور بدکردار دوزخ میں');
INSERT INTO "jalandhari" VALUES(82,15,'82:15','(یعنی) جزا کے دن اس میں داخل ہوں گے');
INSERT INTO "jalandhari" VALUES(82,16,'82:16','اور اس سے چھپ نہیں سکیں گے');
INSERT INTO "jalandhari" VALUES(82,17,'82:17','اور تمہیں کیا معلوم کہ جزا کا دن کیسا ہے؟');
INSERT INTO "jalandhari" VALUES(82,18,'82:18','پھر تمہیں کیا معلوم کہ جزا کا دن کیسا ہے؟');
INSERT INTO "jalandhari" VALUES(82,19,'82:19','جس روز کوئی کسی کا بھلا نہ کر سکے گا اور حکم اس روز خدا ہی کا ہو گا');
INSERT INTO "jalandhari" VALUES(83,1,'83:1','ناپ اور تول میں کمی کرنے والوں کے لیے خرابی ہے');
INSERT INTO "jalandhari" VALUES(83,2,'83:2','جو لوگوں سے ناپ کر لیں تو پورا لیں');
INSERT INTO "jalandhari" VALUES(83,3,'83:3','اور جب ان کو ناپ کر یا تول کر دیں تو کم کر دیں');
INSERT INTO "jalandhari" VALUES(83,4,'83:4','کیا یہ لوگ نہیں جانتے کہ اٹھائے بھی جائیں گے');
INSERT INTO "jalandhari" VALUES(83,5,'83:5','(یعنی) ایک بڑے (سخت) دن میں');
INSERT INTO "jalandhari" VALUES(83,6,'83:6','جس دن (تمام) لوگ رب العالمین کے سامنے کھڑے ہوں گے');
INSERT INTO "jalandhari" VALUES(83,7,'83:7','سن رکھو کہ بدکارروں کے اعمال سجّین میں ہیں');
INSERT INTO "jalandhari" VALUES(83,8,'83:8','اور تم کیا جانتے ہوں کہ سجّین کیا چیز ہے؟');
INSERT INTO "jalandhari" VALUES(83,9,'83:9','ایک دفتر ہے لکھا ہوا');
INSERT INTO "jalandhari" VALUES(83,10,'83:10','اس دن جھٹلانے والوں کی خرابی ہے');
INSERT INTO "jalandhari" VALUES(83,11,'83:11','(یعنی) جو انصاف کے دن کو جھٹلاتے ہیں');
INSERT INTO "jalandhari" VALUES(83,12,'83:12','اور اس کو جھٹلاتا وہی ہے جو حد سے نکل جانے والا گنہگار ہے');
INSERT INTO "jalandhari" VALUES(83,13,'83:13','جب اس کو ہماری آیتیں سنائی جاتی ہیں تو کہتا ہے کہ یہ تو اگلے لوگوں کے افسانے ہیں');
INSERT INTO "jalandhari" VALUES(83,14,'83:14','دیکھو یہ جو (اعمال بد) کرتے ہیں ان کا ان کے دلوں پر زنگ بیٹھ گیا ہے');
INSERT INTO "jalandhari" VALUES(83,15,'83:15','بےشک یہ لوگ اس روز اپنے پروردگار (کے دیدار) سے اوٹ میں ہوں گے');
INSERT INTO "jalandhari" VALUES(83,16,'83:16','پھر دوزخ میں جا داخل ہوں گے');
INSERT INTO "jalandhari" VALUES(83,17,'83:17','پھر ان سے کہا جائے گا کہ یہ وہی چیز ہے جس کو تم جھٹلاتے تھے');
INSERT INTO "jalandhari" VALUES(83,18,'83:18','(یہ بھی) سن رکھو کہ نیکوکاروں کے اعمال علیین میں ہیں');
INSERT INTO "jalandhari" VALUES(83,19,'83:19','اور تم کو کیا معلوم کہ علیین کیا چیز ہے؟');
INSERT INTO "jalandhari" VALUES(83,20,'83:20','ایک دفتر ہے لکھا ہوا');
INSERT INTO "jalandhari" VALUES(83,21,'83:21','جس کے پاس مقرب (فرشتے) حاضر رہتے ہیں');
INSERT INTO "jalandhari" VALUES(83,22,'83:22','بےشک نیک لوگ چین میں ہوں گے');
INSERT INTO "jalandhari" VALUES(83,23,'83:23','تختوں پر بیٹھے ہوئے نظارے کریں گے');
INSERT INTO "jalandhari" VALUES(83,24,'83:24','تم ان کے چہروں ہی سے راحت کی تازگی معلوم کر لو گے');
INSERT INTO "jalandhari" VALUES(83,25,'83:25','ان کو خالص شراب سربمہر پلائی جائے گی');
INSERT INTO "jalandhari" VALUES(83,26,'83:26','جس کی مہر مشک کی ہو گی تو (نعمتوں کے) شائقین کو چاہیے کہ اسی سے رغبت کریں');
INSERT INTO "jalandhari" VALUES(83,27,'83:27','اور اس میں تسنیم (کے پانی) کی آمیزش ہو گی');
INSERT INTO "jalandhari" VALUES(83,28,'83:28','وہ ایک چشمہ ہے جس میں سے (خدا کے) مقرب پیئیں گے');
INSERT INTO "jalandhari" VALUES(83,29,'83:29','جو گنہگار (یعنی کفار) ہیں وہ (دنیا میں) مومنوں سے ہنسی کیا کرتے تھے');
INSERT INTO "jalandhari" VALUES(83,30,'83:30','اور جب ان کے پاس سے گزرتے تو حقارت سے اشارے کرتے');
INSERT INTO "jalandhari" VALUES(83,31,'83:31','اور جب اپنے گھر کو لوٹتے تو اتراتے ہوئے لوٹتے');
INSERT INTO "jalandhari" VALUES(83,32,'83:32','اور جب ان( مومنوں) کو دیکھتے تو کہتے کہ یہ تو گمراہ ہیں');
INSERT INTO "jalandhari" VALUES(83,33,'83:33','حالانکہ وہ ان پر نگراں بنا کر نہیں بھیجے گئے تھے');
INSERT INTO "jalandhari" VALUES(83,34,'83:34','تو آج مومن کافروں سے ہنسی کریں گے');
INSERT INTO "jalandhari" VALUES(83,35,'83:35','(اور) تختوں پر (بیٹھے ہوئے ان کا حال) دیکھ رہے ہوں گے');
INSERT INTO "jalandhari" VALUES(83,36,'83:36','تو کافروں کو ان کے عملوں کا (پورا پورا )بدلہ مل گیا');
INSERT INTO "jalandhari" VALUES(84,1,'84:1','جب آسمان پھٹ جائے گا');
INSERT INTO "jalandhari" VALUES(84,2,'84:2','اور اپنے پروردگار کا فرمان بجا لائے گا اور اسے واجب بھی یہ ہی ہے');
INSERT INTO "jalandhari" VALUES(84,3,'84:3','اور جب زمین ہموار کر دی جائے گی');
INSERT INTO "jalandhari" VALUES(84,4,'84:4','جو کچھ اس میں ہے اسے نکال کر باہر ڈال دے گی اور (بالکل) خالی ہو جائے گی');
INSERT INTO "jalandhari" VALUES(84,5,'84:5','اور اپنے پروردگار کے ارشاد کی تعمیل کرے گی اور اس کو لازم بھی یہی ہے (تو قیامت قائم ہو جائے گی)');
INSERT INTO "jalandhari" VALUES(84,6,'84:6','اے انسان! تو اپنے پروردگار کی طرف (پہنچنے میں) خوب کوشِش کرتا ہے سو اس سے جا ملے گا');
INSERT INTO "jalandhari" VALUES(84,7,'84:7','تو جس کا نامہٴ (اعمال) اس کے داہنے ہاتھ میں دیا جائے گا');
INSERT INTO "jalandhari" VALUES(84,8,'84:8','اس سے حساب آسان لیا جائے گا');
INSERT INTO "jalandhari" VALUES(84,9,'84:9','اور وہ اپنے گھر والوں میں خوش خوش آئے گا');
INSERT INTO "jalandhari" VALUES(84,10,'84:10','اور جس کا نامہٴ (اعمال) اس کی پیٹھ کے پیچھے سے دیا جائے گا');
INSERT INTO "jalandhari" VALUES(84,11,'84:11','وہ موت کو پکارے گا');
INSERT INTO "jalandhari" VALUES(84,12,'84:12','اور وہ دوزخ میں داخل ہو گا');
INSERT INTO "jalandhari" VALUES(84,13,'84:13','یہ اپنے اہل (و عیال) میں مست رہتا تھا');
INSERT INTO "jalandhari" VALUES(84,14,'84:14','اور خیال کرتا تھا کہ (خدا کی طرف) پھر کر نہ جائے گا');
INSERT INTO "jalandhari" VALUES(84,15,'84:15','ہاں ہاں۔ اس کا پروردگار اس کو دیکھ رہا تھا');
INSERT INTO "jalandhari" VALUES(84,16,'84:16','ہمیں شام کی سرخی کی قسم');
INSERT INTO "jalandhari" VALUES(84,17,'84:17','اور رات کی اور جن چیزوں کو وہ اکٹھا کر لیتی ہے ان کی');
INSERT INTO "jalandhari" VALUES(84,18,'84:18','اور چاند کی جب کامل ہو جائے');
INSERT INTO "jalandhari" VALUES(84,19,'84:19','کہ تم درجہ بدرجہ (رتبہٴ اعلیٰ پر) چڑھو گے');
INSERT INTO "jalandhari" VALUES(84,20,'84:20','تو ان لوگوں کو کیا ہوا ہے کہ ایمان نہیں لاتے');
INSERT INTO "jalandhari" VALUES(84,21,'84:21','اور جب ان کے سامنے قرآن پڑھا جاتا ہے تو سجدہ نہیں کرتے');
INSERT INTO "jalandhari" VALUES(84,22,'84:22','بلکہ کافر جھٹلاتے ہیں');
INSERT INTO "jalandhari" VALUES(84,23,'84:23','اور خدا ان باتوں کو جو یہ اپنے دلوں میں چھپاتے ہیں خوب جانتا ہے');
INSERT INTO "jalandhari" VALUES(84,24,'84:24','تو ان کو دکھ دینے والے عذاب کی خبر سنا دو');
INSERT INTO "jalandhari" VALUES(84,25,'84:25','ہاں جو لوگ ایمان لائے اور نیک عمل کرتے رہے ان کے لیے بےانتہا اجر ہے');
INSERT INTO "jalandhari" VALUES(85,1,'85:1','آسمان کی قسم جس میں برج ہیں');
INSERT INTO "jalandhari" VALUES(85,2,'85:2','اور اس دن کی جس کا وعدہ ہے');
INSERT INTO "jalandhari" VALUES(85,3,'85:3','اور حاضر ہونے والے کی اور جو اس کے پاس حاضر کیا جائے اسکی');
INSERT INTO "jalandhari" VALUES(85,4,'85:4','کہ خندقوں (کے کھودنے) والے ہلاک کر دیئے گئے');
INSERT INTO "jalandhari" VALUES(85,5,'85:5','(یعنی) آگ (کی خندقیں) جس میں ایندھن (جھونک رکھا) تھا');
INSERT INTO "jalandhari" VALUES(85,6,'85:6','جب کہ وہ ان (کے کناروں) پر بیٹھے ہوئے تھے');
INSERT INTO "jalandhari" VALUES(85,7,'85:7','اور جو( سختیاں) اہل ایمان پر کر رہے تھے ان کو سامنے دیکھ رہے تھے');
INSERT INTO "jalandhari" VALUES(85,8,'85:8','ان کو مومنوں کی یہی بات بری لگتی تھی کہ وہ خدا پر ایمان لائے ہوئے تھے جو غالب (اور) قابل ستائش ہے');
INSERT INTO "jalandhari" VALUES(85,9,'85:9','وہی جس کی آسمانوں اور زمین میں بادشاہت ہے۔ اور خدا ہر چیز سے واقف ہے');
INSERT INTO "jalandhari" VALUES(85,10,'85:10','جن لوگوں نے مومن مردوں اور مومن عورتوں کو تکلیفیں دیں اور توبہ نہ کی ان کو دوزخ کا (اور) عذاب بھی ہوگا اور جلنے کا عذاب بھی ہوگا');
INSERT INTO "jalandhari" VALUES(85,11,'85:11','(اور) جو لوگ ایمان لائے اور نیک کام کرتے رہے ان کے لیے باغات ہیں جن کے نیچے نہریں بہہ رہی ہیں۔ یہ ہی بڑی کامیابی ہے');
INSERT INTO "jalandhari" VALUES(85,12,'85:12','بےشک تمہارے پروردگار کی پکڑ بڑی سخت ہے');
INSERT INTO "jalandhari" VALUES(85,13,'85:13','وہی پہلی دفعہ پیدا کرتا ہے اور وہی دوبارہ( زندہ) کرے گا');
INSERT INTO "jalandhari" VALUES(85,14,'85:14','اور وہ بخشنے والا اور محبت کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(85,15,'85:15','عرش کا مالک بڑی شان والا');
INSERT INTO "jalandhari" VALUES(85,16,'85:16','جو چاہتا ہے کر دیتا ہے');
INSERT INTO "jalandhari" VALUES(85,17,'85:17','بھلا تم کو لشکروں کا حال معلوم ہوا ہے');
INSERT INTO "jalandhari" VALUES(85,18,'85:18','(یعنی) فرعون اور ثمود کا');
INSERT INTO "jalandhari" VALUES(85,19,'85:19','لیکن کافر (جان بوجھ کر) تکذیب میں (گرفتار) ہیں');
INSERT INTO "jalandhari" VALUES(85,20,'85:20','اور خدا (بھی) ان کو گردا گرد سے گھیرے ہوئے ہے');
INSERT INTO "jalandhari" VALUES(85,21,'85:21','(یہ کتاب ہزل و بطلان نہیں) بلکہ یہ قرآن عظیم الشان ہے');
INSERT INTO "jalandhari" VALUES(85,22,'85:22','لوح محفوظ میں (لکھا ہوا)');
INSERT INTO "jalandhari" VALUES(86,1,'86:1','آسمان اور رات کے وقت آنے والے کی قسم');
INSERT INTO "jalandhari" VALUES(86,2,'86:2','اور تم کو کیا معلوم کہ رات کے وقت آنے والا کیا ہے');
INSERT INTO "jalandhari" VALUES(86,3,'86:3','وہ تارا ہے چمکنے والا');
INSERT INTO "jalandhari" VALUES(86,4,'86:4','کہ کوئی متنفس نہیں جس پر نگہبان مقرر نہیں');
INSERT INTO "jalandhari" VALUES(86,5,'86:5','تو انسان کو دیکھنا چاہئے کہ وہ کاہے سے پیدا ہوا ہے');
INSERT INTO "jalandhari" VALUES(86,6,'86:6','وہ اچھلتے ہوئے پانی سے پیدا ہوا ہے');
INSERT INTO "jalandhari" VALUES(86,7,'86:7','جو پیٹھ اور سینے کے بیچ میں سے نکلتا ہے');
INSERT INTO "jalandhari" VALUES(86,8,'86:8','بےشک خدا اس کے اعادے (یعنی پھر پیدا کرنے) پر قادر ہے');
INSERT INTO "jalandhari" VALUES(86,9,'86:9','جس دن دلوں کے بھید جانچے جائیں گے');
INSERT INTO "jalandhari" VALUES(86,10,'86:10','تو انسان کی کچھ پیش نہ چل سکے گی اور نہ کوئی اس کا مددگار ہو گا');
INSERT INTO "jalandhari" VALUES(86,11,'86:11','آسمان کی قسم جو مینہ برساتا ہے');
INSERT INTO "jalandhari" VALUES(86,12,'86:12','اور زمین کی قسم جو پھٹ جاتی ہے');
INSERT INTO "jalandhari" VALUES(86,13,'86:13','کہ یہ کلام (حق کو باطل سے) جدا کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(86,14,'86:14','اور بیہودہ بات نہیں ہے');
INSERT INTO "jalandhari" VALUES(86,15,'86:15','یہ لوگ تو اپنی تدبیروں میں لگ رہے ہیں');
INSERT INTO "jalandhari" VALUES(86,16,'86:16','اور ہم اپنی تدبیر کر رہے ہیں');
INSERT INTO "jalandhari" VALUES(86,17,'86:17','تو تم کافروں کو مہلت دو بس چند روز ہی مہلت دو');
INSERT INTO "jalandhari" VALUES(87,1,'87:1','(اے پیغمبر) اپنے پروردگار جلیل الشان کے نام کی تسبیح کرو');
INSERT INTO "jalandhari" VALUES(87,2,'87:2','جس نے (انسان کو) بنایا پھر( اس کے اعضاء کو) درست کیا');
INSERT INTO "jalandhari" VALUES(87,3,'87:3','اور جس نے( اس کا)اندازہ ٹہرایا (پھر اس کو) رستہ بتایا');
INSERT INTO "jalandhari" VALUES(87,4,'87:4','اور جس نے چارہ اگایا');
INSERT INTO "jalandhari" VALUES(87,5,'87:5','پھر اس کو سیاہ رنگ کا کوڑا کر دیا');
INSERT INTO "jalandhari" VALUES(87,6,'87:6','ہم تمہیں پڑھا دیں گے کہ تم فراموش نہ کرو گے');
INSERT INTO "jalandhari" VALUES(87,7,'87:7','مگر جو خدا چاہے۔ وہ کھلی بات کو بھی جانتا ہے اور چھپی کو بھی');
INSERT INTO "jalandhari" VALUES(87,8,'87:8','ہم تم کو آسان طریقے کی توفیق دیں گے');
INSERT INTO "jalandhari" VALUES(87,9,'87:9','سو جہاں تک نصیحت (کے) نافع( ہونے کی امید) ہو نصیحت کرتے رہو');
INSERT INTO "jalandhari" VALUES(87,10,'87:10','جو خوف رکھتا ہے وہ تو نصیحت پکڑے گا');
INSERT INTO "jalandhari" VALUES(87,11,'87:11','اور (بےخوف) بدبخت پہلو تہی کرے گا');
INSERT INTO "jalandhari" VALUES(87,12,'87:12','جو (قیامت کو) بڑی (تیز) آگ میں داخل ہو گا');
INSERT INTO "jalandhari" VALUES(87,13,'87:13','پھر وہاں نہ مرے گا اور نہ جئے گا');
INSERT INTO "jalandhari" VALUES(87,14,'87:14','بے شک وہ مراد کو پہنچ گیا جو پاک ہوا');
INSERT INTO "jalandhari" VALUES(87,15,'87:15','اور اپنے پروردگار کے نام کا ذکر کرتا رہا اور نماز پڑھتا رہا');
INSERT INTO "jalandhari" VALUES(87,16,'87:16','مگر تم لوگ تو دنیا کی زندگی کو اختیار کرتے ہو');
INSERT INTO "jalandhari" VALUES(87,17,'87:17','حالانکہ آخرت بہت بہتر اور پائندہ تر ہے');
INSERT INTO "jalandhari" VALUES(87,18,'87:18','یہ بات پہلے صحیفوں میں (مرقوم) ہے');
INSERT INTO "jalandhari" VALUES(87,19,'87:19','(یعنی) ابراہیم اور موسیٰ کے صحیفوں میں');
INSERT INTO "jalandhari" VALUES(88,1,'88:1','بھلا تم کو ڈھانپ لینے والی (یعنی قیامت کا) حال معلوم ہوا ہے');
INSERT INTO "jalandhari" VALUES(88,2,'88:2','اس روز بہت سے منہ (والے) ذلیل ہوں گے');
INSERT INTO "jalandhari" VALUES(88,3,'88:3','سخت محنت کرنے والے تھکے ماندے');
INSERT INTO "jalandhari" VALUES(88,4,'88:4','دہکتی آگ میں داخل ہوں گے');
INSERT INTO "jalandhari" VALUES(88,5,'88:5','ایک کھولتے ہوئے چشمے کا ان کو پانی پلایا جائے گا');
INSERT INTO "jalandhari" VALUES(88,6,'88:6','اور خار دار جھاڑ کے سوا ان کے لیے کوئی کھانا نہیں (ہو گا)');
INSERT INTO "jalandhari" VALUES(88,7,'88:7','جو نہ فربہی لائے اور نہ بھوک میں کچھ کام آئے');
INSERT INTO "jalandhari" VALUES(88,8,'88:8','اور بہت سے منہ (والے) اس روز شادماں ہوں گے');
INSERT INTO "jalandhari" VALUES(88,9,'88:9','اپنے اعمال (کی جزا )سے خوش دل');
INSERT INTO "jalandhari" VALUES(88,10,'88:10','بہشت بریں میں');
INSERT INTO "jalandhari" VALUES(88,11,'88:11','وہاں کسی طرح کی بکواس نہیں سنیں گے');
INSERT INTO "jalandhari" VALUES(88,12,'88:12','اس میں چشمے بہ رہے ہوں گے');
INSERT INTO "jalandhari" VALUES(88,13,'88:13','وہاں تخت ہوں گے اونچے بچھے ہوئے');
INSERT INTO "jalandhari" VALUES(88,14,'88:14','اور آبخورے (قرینے سے) رکھے ہوئے');
INSERT INTO "jalandhari" VALUES(88,15,'88:15','اور گاؤ تکیے قطار کی قطار لگے ہوئے');
INSERT INTO "jalandhari" VALUES(88,16,'88:16','اور نفیس مسندیں بچھی ہوئی');
INSERT INTO "jalandhari" VALUES(88,17,'88:17','یہ لوگ اونٹوں کی طرف نہیں دیکھتے کہ کیسے (عجیب )پیدا کیے گئے ہیں');
INSERT INTO "jalandhari" VALUES(88,18,'88:18','اور آسمان کی طرف کہ کیسا بلند کیا گیا ہے');
INSERT INTO "jalandhari" VALUES(88,19,'88:19','اور پہاڑوں کی طرف کہ کس طرح کھڑے کیے گئے ہیں');
INSERT INTO "jalandhari" VALUES(88,20,'88:20','اور زمین کی طرف کہ کس طرح بچھائی گئی');
INSERT INTO "jalandhari" VALUES(88,21,'88:21','تو تم نصیحت کرتے رہو کہ تم نصیحت کرنے والے ہی ہو');
INSERT INTO "jalandhari" VALUES(88,22,'88:22','تم ان پر داروغہ نہیں ہو');
INSERT INTO "jalandhari" VALUES(88,23,'88:23','ہاں جس نے منہ پھیرا اور نہ مانا');
INSERT INTO "jalandhari" VALUES(88,24,'88:24','تو خدا اس کو بڑا عذاب دے گا');
INSERT INTO "jalandhari" VALUES(88,25,'88:25','بےشک ان کو ہمارے پاس لوٹ کر آنا ہے');
INSERT INTO "jalandhari" VALUES(88,26,'88:26','پھر ہم ہی کو ان سے حساب لینا ہے');
INSERT INTO "jalandhari" VALUES(89,1,'89:1','فجر کی قسم');
INSERT INTO "jalandhari" VALUES(89,2,'89:2','اور دس راتوں کی');
INSERT INTO "jalandhari" VALUES(89,3,'89:3','اور جفت اور طاق کی');
INSERT INTO "jalandhari" VALUES(89,4,'89:4','اور رات کی جب جانے لگے');
INSERT INTO "jalandhari" VALUES(89,5,'89:5','اور بے شک یہ چیزیں عقلمندوں کے نزدیک قسم کھانے کے لائق ہیں کہ (کافروں کو ضرور عذاب ہو گا)');
INSERT INTO "jalandhari" VALUES(89,6,'89:6','کیا تم نے نہیں دیکھا کہ تمہارے پروردگار نے عاد کے ساتھ کیا کیا');
INSERT INTO "jalandhari" VALUES(89,7,'89:7','(جو) ارم (کہلاتے تھے اتنے) دراز قد');
INSERT INTO "jalandhari" VALUES(89,8,'89:8','کہ تمام ملک میں ایسے پیدا نہیں ہوئے تھے');
INSERT INTO "jalandhari" VALUES(89,9,'89:9','اور ثمود کے ساتھ (کیا کیا) جو وادئِ (قریٰ) میں پتھر تراشتے تھے (اور گھر بناتے) تھے');
INSERT INTO "jalandhari" VALUES(89,10,'89:10','اور فرعون کے ساتھ (کیا کیا) جو خیمے اور میخیں رکھتا تھا');
INSERT INTO "jalandhari" VALUES(89,11,'89:11','یہ لوگ ملکوں میں سرکش ہو رہے تھے');
INSERT INTO "jalandhari" VALUES(89,12,'89:12','اور ان میں بہت سی خرابیاں کرتے تھے');
INSERT INTO "jalandhari" VALUES(89,13,'89:13','تو تمہارے پروردگار نے ان پر عذاب کا کوڑا نازل کیا');
INSERT INTO "jalandhari" VALUES(89,14,'89:14','بے شک تمہارا پروردگار تاک میں ہے');
INSERT INTO "jalandhari" VALUES(89,15,'89:15','مگر انسان (عجیب مخلوق ہے کہ) جب اس کا پروردگار اس کو آزماتا ہے تو اسے عزت دیتا اور نعمت بخشتا ہے۔ تو کہتا ہے کہ (آہا) میرے پروردگار نے مجھے عزت بخشی');
INSERT INTO "jalandhari" VALUES(89,16,'89:16','اور جب (دوسری طرح) آزماتا ہے کہ اس پر روزی تنگ کر دیتا ہے تو کہتا ہے کہ (ہائے) میرے پروردگار نے مجھے ذلیل کیا');
INSERT INTO "jalandhari" VALUES(89,17,'89:17','نہیں بلکہ تم لوگ یتیم کی خاطر نہیں کرتے');
INSERT INTO "jalandhari" VALUES(89,18,'89:18','اور نہ مسکین کو کھانا کھلانے کی ترغیب دیتے ہو');
INSERT INTO "jalandhari" VALUES(89,19,'89:19','اور میراث کے مال سمیٹ کر کھا جاتے ہو');
INSERT INTO "jalandhari" VALUES(89,20,'89:20','اور مال کو بہت ہی عزیز رکھتے ہو');
INSERT INTO "jalandhari" VALUES(89,21,'89:21','تو جب زمین کی بلندی کوٹ کوٹ کو پست کر دی جائے گی');
INSERT INTO "jalandhari" VALUES(89,22,'89:22','اور تمہارا پروردگار (جلوہ فرما ہو گا) اور فرشتے قطار باندھ باندھ کر آ موجود ہوں گے');
INSERT INTO "jalandhari" VALUES(89,23,'89:23','اور دوزخ اس دن حاضر کی جائے گی تو انسان اس دن متنبہ ہو گا مگر تنبہ (سے) اسے (فائدہ) کہاں (مل سکے گا)');
INSERT INTO "jalandhari" VALUES(89,24,'89:24','کہے گا کاش میں نے اپنی زندگی (جاودانی کے لیے) کچھ آگے بھیجا ہوتا');
INSERT INTO "jalandhari" VALUES(89,25,'89:25','تو اس دن نہ کوئی خدا کے عذاب کی طرح کا (کسی کو) عذاب دے گا');
INSERT INTO "jalandhari" VALUES(89,26,'89:26','اور نہ کوئی ویسا جکڑنا جکڑے گا');
INSERT INTO "jalandhari" VALUES(89,27,'89:27','اے اطمینان پانے والی روح!');
INSERT INTO "jalandhari" VALUES(89,28,'89:28','اپنے پروردگار کی طرف لوٹ چل۔ تو اس سے راضی وہ تجھ سے راضی');
INSERT INTO "jalandhari" VALUES(89,29,'89:29','تو میرے (ممتاز) بندوں میں شامل ہو جا');
INSERT INTO "jalandhari" VALUES(89,30,'89:30','اور میری بہشت میں داخل ہو جا');
INSERT INTO "jalandhari" VALUES(90,1,'90:1','ہمیں اس شہر (مکہ) کی قسم');
INSERT INTO "jalandhari" VALUES(90,2,'90:2','اور تم اسی شہر میں تو رہتے ہو');
INSERT INTO "jalandhari" VALUES(90,3,'90:3','اور باپ (یعنی آدم) اور اس کی اولاد کی قسم');
INSERT INTO "jalandhari" VALUES(90,4,'90:4','کہ ہم نے انسان کو تکلیف (کی حالت) میں (رہنے والا) بنایا ہے');
INSERT INTO "jalandhari" VALUES(90,5,'90:5','کیا وہ خیال رکھتا ہے کہ اس پر کوئی قابو نہ پائے گا');
INSERT INTO "jalandhari" VALUES(90,6,'90:6','کہتا ہے کہ میں نے بہت سا مال برباد کیا');
INSERT INTO "jalandhari" VALUES(90,7,'90:7','کیا اسے یہ گمان ہے کہ اس کو کسی نے دیکھا نہیں');
INSERT INTO "jalandhari" VALUES(90,8,'90:8','بھلا ہم نےاس کو دو آنکھیں نہیں دیں؟');
INSERT INTO "jalandhari" VALUES(90,9,'90:9','اور زبان اور دو ہونٹ (نہیں دیئے)');
INSERT INTO "jalandhari" VALUES(90,10,'90:10','(یہ چیزیں بھی دیں) اور اس کو (خیر و شر کے) دونوں رستے بھی دکھا دیئے');
INSERT INTO "jalandhari" VALUES(90,11,'90:11','مگر وہ گھاٹی پر سے ہو کر نہ گزرا');
INSERT INTO "jalandhari" VALUES(90,12,'90:12','اور تم کیا سمجھے کہ گھاٹی کیا ہے؟');
INSERT INTO "jalandhari" VALUES(90,13,'90:13','کسی (کی) گردن کا چھڑانا');
INSERT INTO "jalandhari" VALUES(90,14,'90:14','یا بھوک کے دن کھانا کھلانا');
INSERT INTO "jalandhari" VALUES(90,15,'90:15','یتیم رشتہ دار کو');
INSERT INTO "jalandhari" VALUES(90,16,'90:16','یا فقیر خاکسار کو');
INSERT INTO "jalandhari" VALUES(90,17,'90:17','پھر ان لوگوں میں بھی (داخل) ہو جو ایمان لائے اور صبر کی نصیحت اور (لوگوں پر) شفقت کرنے کی وصیت کرتے رہے');
INSERT INTO "jalandhari" VALUES(90,18,'90:18','یہی لوگ صاحب سعادت ہیں');
INSERT INTO "jalandhari" VALUES(90,19,'90:19','اور جنہوں نے ہماری آیتوں کو نہ مانا وہ بدبخت ہیں');
INSERT INTO "jalandhari" VALUES(90,20,'90:20','یہ لوگ آگ میں بند کر دیئے جائیں گے');
INSERT INTO "jalandhari" VALUES(91,1,'91:1','سورج کی قسم اور اس کی روشنی کی');
INSERT INTO "jalandhari" VALUES(91,2,'91:2','اور چاند کی جب اس کے پیچھے نکلے');
INSERT INTO "jalandhari" VALUES(91,3,'91:3','اور دن کی جب اُسے چمکا دے');
INSERT INTO "jalandhari" VALUES(91,4,'91:4','اور رات کی جب اُسے چھپا لے');
INSERT INTO "jalandhari" VALUES(91,5,'91:5','اور آسمان کی اور اس ذات کی جس نے اسے بنایا');
INSERT INTO "jalandhari" VALUES(91,6,'91:6','اور زمین کی اور اس کی جس نے اسے پھیلایا');
INSERT INTO "jalandhari" VALUES(91,7,'91:7','اور انسان کی اور اس کی جس نے اس (کے اعضا) کو برابر کیا');
INSERT INTO "jalandhari" VALUES(91,8,'91:8','پھر اس کو بدکاری (سے بچنے) اور پرہیزگاری کرنے کی سمجھ دی');
INSERT INTO "jalandhari" VALUES(91,9,'91:9','کہ جس نے (اپنے) نفس (یعنی روح) کو پاک رکھا وہ مراد کو پہنچا');
INSERT INTO "jalandhari" VALUES(91,10,'91:10','اور جس نے اسے خاک میں ملایا وہ خسارے میں رہا');
INSERT INTO "jalandhari" VALUES(91,11,'91:11','(قوم) ثمود نے اپنی سرکشی کے سبب (پیغمبر کو) جھٹلایا');
INSERT INTO "jalandhari" VALUES(91,12,'91:12','جب ان میں سے ایک نہایت بدبخت اٹھا');
INSERT INTO "jalandhari" VALUES(91,13,'91:13','تو خدا کے پیغمبر (صالح) نے ان سے کہا کہ خدا کی اونٹنی اور اس کے پانی پینے کی باری سے عذر کرو');
INSERT INTO "jalandhari" VALUES(91,14,'91:14','مگر انہوں نے پیغمبر کو جھٹلایا اور اونٹنی کی کونچیں کاٹ دیں تو خدا نے ان کےگناہ کے سبب ان پر عذاب نازل کیا اور سب کو (ہلاک کر کے) برابر کر دیا');
INSERT INTO "jalandhari" VALUES(91,15,'91:15','اور اس کو ان کے بدلہ لینے کا کچھ بھی ڈر نہیں');
INSERT INTO "jalandhari" VALUES(92,1,'92:1','رات کی قسم جب (دن کو) چھپالے');
INSERT INTO "jalandhari" VALUES(92,2,'92:2','اور دن کی قسم جب چمک اٹھے');
INSERT INTO "jalandhari" VALUES(92,3,'92:3','اور اس (ذات) کی قسم جس نے نر اور مادہ پیدا کیے');
INSERT INTO "jalandhari" VALUES(92,4,'92:4','کہ تم لوگوں کی کوششں طرح طرح کی ہے');
INSERT INTO "jalandhari" VALUES(92,5,'92:5','تو جس نے (خدا کے رستے میں مال) دیا اور پرہیز گاری کی');
INSERT INTO "jalandhari" VALUES(92,6,'92:6','اور نیک بات کو سچ جانا');
INSERT INTO "jalandhari" VALUES(92,7,'92:7','اس کو ہم آسان طریقے کی توفیق دیں گے');
INSERT INTO "jalandhari" VALUES(92,8,'92:8','اور جس نے بخل کیا اور بےپروا بنا رہا');
INSERT INTO "jalandhari" VALUES(92,9,'92:9','اور نیک بات کو جھوٹ سمجھا');
INSERT INTO "jalandhari" VALUES(92,10,'92:10','اسے سختی میں پہنچائیں گے');
INSERT INTO "jalandhari" VALUES(92,11,'92:11','اور جب وہ (دوزخ کے گڑھے میں) گرے گا تو اس کا مال اس کے کچھ کام نہ آئے گا');
INSERT INTO "jalandhari" VALUES(92,12,'92:12','ہمیں تو راہ دکھا دینا ہے');
INSERT INTO "jalandhari" VALUES(92,13,'92:13','اور آخرت او ردنیا ہماری ہی چیزیں ہیں');
INSERT INTO "jalandhari" VALUES(92,14,'92:14','سو میں نے تم کو بھڑکتی آگ سے متنبہ کر دیا');
INSERT INTO "jalandhari" VALUES(92,15,'92:15','اس میں وہی داخل ہو گا جو بڑا بدبخت ہے');
INSERT INTO "jalandhari" VALUES(92,16,'92:16','جس نے جھٹلایا اور منہ پھیرا');
INSERT INTO "jalandhari" VALUES(92,17,'92:17','اور جو بڑا پرہیزگار ہے وہ (اس سے) بچا لیا جائے گا');
INSERT INTO "jalandhari" VALUES(92,18,'92:18','جو مال دیتا ہے تاکہ پاک ہو');
INSERT INTO "jalandhari" VALUES(92,19,'92:19','اور (اس لیے) نہیں( دیتا کہ) اس پر کسی کا احسان (ہے) جس کا وہ بدلہ اتارتا ہے');
INSERT INTO "jalandhari" VALUES(92,20,'92:20','بلکہ اپنے خداوند اعلیٰ کی رضامندی حاصل کرنے کے لیے دیتا ہے');
INSERT INTO "jalandhari" VALUES(92,21,'92:21','اور وہ عنقریب خوش ہو جائے گا');
INSERT INTO "jalandhari" VALUES(93,1,'93:1','آفتاب کی روشنی کی قسم');
INSERT INTO "jalandhari" VALUES(93,2,'93:2','اور رات (کی تاریکی) کی جب چھا جائے');
INSERT INTO "jalandhari" VALUES(93,3,'93:3','کہ (اے محمدصلى الله عليه وسلم) تمہارے پروردگار نے نہ تو تم کو چھوڑا اور نہ( تم سے )ناراض ہوا');
INSERT INTO "jalandhari" VALUES(93,4,'93:4','اور آخرت تمہارے لیے پہلی (حالت یعنی دنیا) سے کہیں بہتر ہے');
INSERT INTO "jalandhari" VALUES(93,5,'93:5','اور تمہیں پروردگار عنقریب وہ کچھ عطا فرمائے گا کہ تم خوش ہو جاؤ گے');
INSERT INTO "jalandhari" VALUES(93,6,'93:6','بھلا اس نے تمہیں یتیم پا کر جگہ نہیں دی؟ (بےشک دی)');
INSERT INTO "jalandhari" VALUES(93,7,'93:7','اور رستے سے ناواقف دیکھا تو رستہ دکھایا');
INSERT INTO "jalandhari" VALUES(93,8,'93:8','اور تنگ دست پایا تو غنی کر دیا');
INSERT INTO "jalandhari" VALUES(93,9,'93:9','تو تم بھی یتیم پر ستم نہ کرنا');
INSERT INTO "jalandhari" VALUES(93,10,'93:10','اور مانگنے والے کو جھڑکی نہ دینا');
INSERT INTO "jalandhari" VALUES(93,11,'93:11','اور اپنے پروردگار کی نعمتوں کا بیان کرتے رہنا');
INSERT INTO "jalandhari" VALUES(94,1,'94:1','(اے محمدصلى الله عليه وسلم) کیا ہم نے تمہارا سینہ کھول نہیں دیا؟ (بےشک کھول دیا)');
INSERT INTO "jalandhari" VALUES(94,2,'94:2','اور تم پر سے بوجھ بھی اتار دیا');
INSERT INTO "jalandhari" VALUES(94,3,'94:3','جس نے تمہاری پیٹھ توڑ رکھی تھی');
INSERT INTO "jalandhari" VALUES(94,4,'94:4','اور تمہارا ذکر بلند کیا');
INSERT INTO "jalandhari" VALUES(94,5,'94:5','ہاں ہاں مشکل کے ساتھ آسانی بھی ہے');
INSERT INTO "jalandhari" VALUES(94,6,'94:6','(اور) بے شک مشکل کے ساتھ آسانی ہے');
INSERT INTO "jalandhari" VALUES(94,7,'94:7','تو جب فارغ ہوا کرو تو (عبادت میں) محنت کیا کرو');
INSERT INTO "jalandhari" VALUES(94,8,'94:8','اور اپنے پروردگار کی طرف متوجہ ہو جایا کرو');
INSERT INTO "jalandhari" VALUES(95,1,'95:1','انجیر کی قسم اور زیتون کی');
INSERT INTO "jalandhari" VALUES(95,2,'95:2','اور طور سینین کی');
INSERT INTO "jalandhari" VALUES(95,3,'95:3','اور اس امن والے شہر کی');
INSERT INTO "jalandhari" VALUES(95,4,'95:4','کہ ہم نے انسان کو بہت اچھی صورت میں پیدا کیا ہے');
INSERT INTO "jalandhari" VALUES(95,5,'95:5','پھر (رفتہ رفتہ) اس (کی حالت) کو (بدل کر) پست سے پست کر دیا');
INSERT INTO "jalandhari" VALUES(95,6,'95:6','مگر جو لوگ ایمان لائے اور نیک عمل کرتے رہے انکے لیے بےانتہا اجر ہے');
INSERT INTO "jalandhari" VALUES(95,7,'95:7','تو (اے آدم زاد) پھر تو جزا کے دن کو کیوں جھٹلاتا ہے؟');
INSERT INTO "jalandhari" VALUES(95,8,'95:8','کیا خدا سب سے بڑا حاکم نہیں ہے؟');
INSERT INTO "jalandhari" VALUES(96,1,'96:1','(اے محمدصلى الله عليه وسلم) اپنے پروردگار کا نام لے کر پڑھو جس نے (عالم کو) پیدا کیا');
INSERT INTO "jalandhari" VALUES(96,2,'96:2','جس نے انسان کو خون کی پھٹکی سے بنایا');
INSERT INTO "jalandhari" VALUES(96,3,'96:3','پڑھو اور تمہارا پروردگار بڑا کریم ہے');
INSERT INTO "jalandhari" VALUES(96,4,'96:4','جس نے قلم کے ذریعے سے علم سکھایا');
INSERT INTO "jalandhari" VALUES(96,5,'96:5','اور انسان کو وہ باتیں سکھائیں جس کا اس کو علم نہ تھا');
INSERT INTO "jalandhari" VALUES(96,6,'96:6','مگر انسان سرکش ہو جاتا ہے');
INSERT INTO "jalandhari" VALUES(96,7,'96:7','جب کہ اپنے تیئں غنی دیکھتا ہے');
INSERT INTO "jalandhari" VALUES(96,8,'96:8','کچھ شک نہیں کہ (اس کو) تمہارے پروردگار ہی کی طرف لوٹ کر جانا ہے');
INSERT INTO "jalandhari" VALUES(96,9,'96:9','بھلا تم نے اس شخص کو دیکھا جو منع کرتا ہے');
INSERT INTO "jalandhari" VALUES(96,10,'96:10','(یعنی) ایک بندے کو جب وہ نماز پڑھنے لگتا ہے');
INSERT INTO "jalandhari" VALUES(96,11,'96:11','بھلا دیکھو تو اگر یہ راہِ راست پر ہو');
INSERT INTO "jalandhari" VALUES(96,12,'96:12','یا پرہیز گاری کا حکم کرے (تو منع کرنا کیسا)');
INSERT INTO "jalandhari" VALUES(96,13,'96:13','اور دیکھو تو اگر اس نے دین حق کو جھٹلایا اور اس سے منہ موڑا (تو کیا ہوا)');
INSERT INTO "jalandhari" VALUES(96,14,'96:14','کیااس کو معلوم نہیں کہ خدا دیکھ رہا ہے');
INSERT INTO "jalandhari" VALUES(96,15,'96:15','دیکھو اگر وہ باز نہ آئے گا تو ہم (اس کی) پیشانی کے بال پکڑ گھسیٹیں گے');
INSERT INTO "jalandhari" VALUES(96,16,'96:16','یعنی اس جھوٹے خطاکار کی پیشانی کے بال');
INSERT INTO "jalandhari" VALUES(96,17,'96:17','تو وہ اپنے یاروں کی مجلس کو بلالے');
INSERT INTO "jalandhari" VALUES(96,18,'96:18','ہم بھی اپنے موکلانِ دوزخ کو بلا لیں گے');
INSERT INTO "jalandhari" VALUES(96,19,'96:19','دیکھو اس کا کہا نہ ماننا اور قربِ (خدا) حاصل کرتے رہنا');
INSERT INTO "jalandhari" VALUES(97,1,'97:1','ہم نے اس( قرآن) کو شب قدر میں نازل (کرنا شروع) کیا');
INSERT INTO "jalandhari" VALUES(97,2,'97:2','اور تمہیں کیا معلوم کہ شب قدر کیا ہے؟');
INSERT INTO "jalandhari" VALUES(97,3,'97:3','شب قدر ہزار مہینے سے بہتر ہے');
INSERT INTO "jalandhari" VALUES(97,4,'97:4','اس میں روح (الامین) اور فرشتے ہر کام کے (انتظام کے) لیے اپنے پروردگار کے حکم سے اترتے ہیں');
INSERT INTO "jalandhari" VALUES(97,5,'97:5','یہ( رات) طلوع صبح تک (امان اور) سلامتی ہے');
INSERT INTO "jalandhari" VALUES(98,1,'98:1','جو لوگ کافر ہیں (یعنی) اہل کتاب اور مشرک وہ (کفر سے) باز رہنے والے نہ تھے جب تک ان کے پاس کھلی دلیل (نہ) آتی');
INSERT INTO "jalandhari" VALUES(98,2,'98:2','(یعنی) خدا کے پیغمبر جو پاک اوراق پڑھتے ہیں');
INSERT INTO "jalandhari" VALUES(98,3,'98:3','جن میں( مستحکم) آیتیں لکھی ہوئی ہیں');
INSERT INTO "jalandhari" VALUES(98,4,'98:4','اور اہل کتاب جو متفرق (و مختلف) ہوئے ہیں تو دلیل واضح آنے کے بعد( ہوئے ہیں)');
INSERT INTO "jalandhari" VALUES(98,5,'98:5','اور ان کو حکم تو یہی ہوا تھا کہ اخلاص عمل کے ساتھ خدا کی عبادت کریں (اور) یکسو ہو کراورنماز پڑھیں اور زکوٰة دیں اور یہی سچا دین ہے');
INSERT INTO "jalandhari" VALUES(98,6,'98:6','جو لوگ کافر ہیں (یعنی) اہل کتاب اور مشرک وہ دوزخ کی آگ میں پڑیں گے (اور) ہمیشہ اس میں رہیں گے۔ یہ لوگ سب مخلوق سے بدتر ہیں');
INSERT INTO "jalandhari" VALUES(98,7,'98:7','(اور) جو لوگ ایمان لائے اور نیک عمل کرتے رہے وہ تمام خلقت سے بہتر ہیں');
INSERT INTO "jalandhari" VALUES(98,8,'98:8','ان کا صلہ ان کے پروردگار کے ہاں ہمیشہ رہنے کے باغ ہیں جن کے نیچے نہریں بہہ رہی ہیں ابدالاباد ان میں رہیں گے۔ خدا ان سے خوش اور وہ اس سے خوش۔ یہ (صلہ) اس کے لیے ہے جو اپنے پروردگار سے ڈرتا رہا');
INSERT INTO "jalandhari" VALUES(99,1,'99:1','جب زمین بھونچال سے ہلا دی جائے گی');
INSERT INTO "jalandhari" VALUES(99,2,'99:2','اور زمین اپنے (اندر) کے بوجھ نکال ڈالے گی');
INSERT INTO "jalandhari" VALUES(99,3,'99:3','اور انسان کہے گا کہ اس کو کیا ہوا ہے؟');
INSERT INTO "jalandhari" VALUES(99,4,'99:4','اس روز وہ اپنے حالات بیان کردے گی');
INSERT INTO "jalandhari" VALUES(99,5,'99:5','کیونکہ تمہارے پروردگار نے اس کو حکم بھیجا (ہوگا )');
INSERT INTO "jalandhari" VALUES(99,6,'99:6','اس دن لوگ گروہ گروہ ہو کر آئیں گے تاکہ ان کو ان کے اعمال دکھا دیئے جائیں');
INSERT INTO "jalandhari" VALUES(99,7,'99:7','تو جس نے ذرہ بھر نیکی کی ہو گی وہ اس کو دیکھ لے گا');
INSERT INTO "jalandhari" VALUES(99,8,'99:8','اور جس نے ذرہ بھر برائی کی ہوگی وہ اسے دیکھ لے گا');
INSERT INTO "jalandhari" VALUES(100,1,'100:1','ان سرپٹ دوڑنے والے گھوڑوں کی قسم جو ہانپ اٹھتےہیں');
INSERT INTO "jalandhari" VALUES(100,2,'100:2','پھر (پتھروں پر نعل) مار کر آگ نکالتے ہیں');
INSERT INTO "jalandhari" VALUES(100,3,'100:3','پھر صبح کو چھاپہ مارتے ہیں');
INSERT INTO "jalandhari" VALUES(100,4,'100:4','پھر اس میں گرد اٹھاتے ہیں');
INSERT INTO "jalandhari" VALUES(100,5,'100:5','پھر اس وقت دشمن کی فوج میں جا گھستے ہیں');
INSERT INTO "jalandhari" VALUES(100,6,'100:6','کہ انسان اپنے پروردگار کا احسان ناشناس (اور ناشکرا) ہے');
INSERT INTO "jalandhari" VALUES(100,7,'100:7','اور وہ اس سے آگاہ بھی ہے');
INSERT INTO "jalandhari" VALUES(100,8,'100:8','وہ تو مال سے سخت محبت کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(100,9,'100:9','کیا وہ اس وقت کو نہیں جانتا کہ جو( مردے) قبروں میں ہیں وہ باہر نکال لیے جائیں گے');
INSERT INTO "jalandhari" VALUES(100,10,'100:10','اور جو (بھید) دلوں میں ہیں وہ ظاہر کر دیئے جائیں گے');
INSERT INTO "jalandhari" VALUES(100,11,'100:11','بےشک ان کا پروردگار اس روز ان سے خوب واقف ہوگا');
INSERT INTO "jalandhari" VALUES(101,1,'101:1','کھڑ کھڑانے والی');
INSERT INTO "jalandhari" VALUES(101,2,'101:2','کھڑ کھڑانے والی کیا ہے؟');
INSERT INTO "jalandhari" VALUES(101,3,'101:3','اور تم کیا جانوں کھڑ کھڑانے والی کیا ہے؟');
INSERT INTO "jalandhari" VALUES(101,4,'101:4','(وہ قیامت ہے) جس دن لوگ ایسے ہوں گے جیسے بکھرے ہوئے پتنگے');
INSERT INTO "jalandhari" VALUES(101,5,'101:5','اور پہاڑ ایسے ہو جائیں گے جیسے دھنکی ہوئی رنگ برنگ کی اون');
INSERT INTO "jalandhari" VALUES(101,6,'101:6','تو جس کے (اعمال کے) وزن بھاری نکلیں گے');
INSERT INTO "jalandhari" VALUES(101,7,'101:7','وہ دل پسند عیش میں ہو گا');
INSERT INTO "jalandhari" VALUES(101,8,'101:8','اور جس کے وزن ہلکے نکلیں گے');
INSERT INTO "jalandhari" VALUES(101,9,'101:9','اس کا مرجع ہاویہ ہے');
INSERT INTO "jalandhari" VALUES(101,10,'101:10','اور تم کیا سمجھے کہ ہاویہ کیا چیز ہے؟');
INSERT INTO "jalandhari" VALUES(101,11,'101:11','دہکتی ہوئی آگ ہے');
INSERT INTO "jalandhari" VALUES(102,1,'102:1','(لوگو) تم کو(مال کی) بہت سی طلب نے غافل کر دیا');
INSERT INTO "jalandhari" VALUES(102,2,'102:2','یہاں تک کہ تم نے قبریں جا دیکھیں');
INSERT INTO "jalandhari" VALUES(102,3,'102:3','دیکھو تمہیں عنقریب معلوم ہو جائے گا');
INSERT INTO "jalandhari" VALUES(102,4,'102:4','پھر دیکھو تمہیں عنقریب معلوم ہو جائے گا');
INSERT INTO "jalandhari" VALUES(102,5,'102:5','دیکھو اگر تم جانتے (یعنی) علم الیقین (رکھتے تو غفلت نہ کرتے)');
INSERT INTO "jalandhari" VALUES(102,6,'102:6','تم ضرور دوزخ کو دیکھو گے');
INSERT INTO "jalandhari" VALUES(102,7,'102:7','پھر اس کو (ایسا) دیکھو گے (کہ) عین الیقین (آ جائے گا )');
INSERT INTO "jalandhari" VALUES(102,8,'102:8','پھر اس روز تم سے (شکر) نعمت کے بارے میں پرسش ہو گی');
INSERT INTO "jalandhari" VALUES(103,1,'103:1','عصر کی قسم');
INSERT INTO "jalandhari" VALUES(103,2,'103:2','کہ انسان نقصان میں ہے');
INSERT INTO "jalandhari" VALUES(103,3,'103:3','مگر وہ لوگ جو ایمان لائے اور نیک عمل کرتے رہے اور آپس میں حق (بات) کی تلقین اور صبر کی تاکید کرتے رہے');
INSERT INTO "jalandhari" VALUES(104,1,'104:1','ہر طعن آمیز اشارتیں کرنے والے چغل خور کی خرابی ہے');
INSERT INTO "jalandhari" VALUES(104,2,'104:2','جو مال جمع کرتا اور اس کو گن گن کر رکھتا ہے');
INSERT INTO "jalandhari" VALUES(104,3,'104:3','(اور) خیال کرتا ہے کہ اس کا مال اس کی ہمیشہ کی زندگی کا موجب ہو گا');
INSERT INTO "jalandhari" VALUES(104,4,'104:4','ہر گز نہیں وہ ضرور حطمہ میں ڈالا جائے گا');
INSERT INTO "jalandhari" VALUES(104,5,'104:5','اور تم کیا سمجھے حطمہ کیا ہے؟');
INSERT INTO "jalandhari" VALUES(104,6,'104:6','وہ خدا کی بھڑکائی ہوئی آگ ہے');
INSERT INTO "jalandhari" VALUES(104,7,'104:7','جو دلوں پر جا لپٹے گی');
INSERT INTO "jalandhari" VALUES(104,8,'104:8','(اور) وہ اس میں بند کر دیئے جائیں گے');
INSERT INTO "jalandhari" VALUES(104,9,'104:9','(یعنی آگ کے) لمبے لمبے ستونوں میں');
INSERT INTO "jalandhari" VALUES(105,1,'105:1','کیا تم نے نہیں دیکھا کہ تمہارے پروردگار نے ہاتھی والوں کے ساتھ کیا کیا');
INSERT INTO "jalandhari" VALUES(105,2,'105:2','کیا ان کا داؤں غلط نہیں کیا؟ (گیا)');
INSERT INTO "jalandhari" VALUES(105,3,'105:3','اور ان پر جھلڑ کے جھلڑ جانور بھیجے');
INSERT INTO "jalandhari" VALUES(105,4,'105:4','جو ان پر کھنگر کی پتھریاں پھینکتے تھے');
INSERT INTO "jalandhari" VALUES(105,5,'105:5','تو ان کو ایسا کر دیا جیسے کھایا ہوا بھس');
INSERT INTO "jalandhari" VALUES(106,1,'106:1','قریش کے مانوس کرنے کے سبب');
INSERT INTO "jalandhari" VALUES(106,2,'106:2','(یعنی) ان کو جاڑے اور گرمی کے سفر سے مانوس کرنے کے سبب');
INSERT INTO "jalandhari" VALUES(106,3,'106:3','لوگوں کو چاہیئے کہ (اس نعمت کے شکر میں) اس گھر کے مالک کی عبادت کریں');
INSERT INTO "jalandhari" VALUES(106,4,'106:4','جس نے ان کو بھوک میں کھانا کھلایا اور خوف سے امن بخشا');
INSERT INTO "jalandhari" VALUES(107,1,'107:1','بھلا تم نے اس شخص کو دیکھا جو (روزِ) جزا کو جھٹلاتا ہے؟');
INSERT INTO "jalandhari" VALUES(107,2,'107:2','یہ وہی (بدبخت) ہے، جو یتیم کو دھکے دیتا ہے');
INSERT INTO "jalandhari" VALUES(107,3,'107:3','اور فقیر کو کھانا کھلانے کے لیے( لوگوں کو) ترغیب نہیں دیتا');
INSERT INTO "jalandhari" VALUES(107,4,'107:4','تو ایسے نمازیوں کی خرابی ہے');
INSERT INTO "jalandhari" VALUES(107,5,'107:5','جو نماز کی طرف سے غافل رہتے ہیں');
INSERT INTO "jalandhari" VALUES(107,6,'107:6','جو ریا کاری کرتے ہیں');
INSERT INTO "jalandhari" VALUES(107,7,'107:7','اور برتنے کی چیزیں عاریتہً نہیں دیتے');
INSERT INTO "jalandhari" VALUES(108,1,'108:1','(اے محمدصلى الله عليه وسلم) ہم نے تم کو کوثر عطا فرمائی ہے');
INSERT INTO "jalandhari" VALUES(108,2,'108:2','تو اپنے پروردگار کے لیے نماز پڑھا کرو اور قربانی دیا کرو');
INSERT INTO "jalandhari" VALUES(108,3,'108:3','کچھ شک نہیں کہ تمہارا دشمن ہی بےاولاد رہے گا');
INSERT INTO "jalandhari" VALUES(109,1,'109:1','(اے پیغمبر ان منکران اسلام سے) کہہ دو کہ اے کافرو!');
INSERT INTO "jalandhari" VALUES(109,2,'109:2','جن (بتوں) کو تم پوچتے ہو ان کو میں نہیں پوجتا');
INSERT INTO "jalandhari" VALUES(109,3,'109:3','اور جس (خدا) کی میں عبادت کرتا ہوں اس کی تم عبادت نہیں کرتے');
INSERT INTO "jalandhari" VALUES(109,4,'109:4','اور( میں پھر کہتا ہوں کہ) جن کی تم پرستش کرتے ہوں ان کی میں پرستش کرنے والا نہیں ہوں');
INSERT INTO "jalandhari" VALUES(109,5,'109:5','اور نہ تم اس کی بندگی کرنے والے (معلوم ہوتے) ہو جس کی میں بندگی کرتا ہوں');
INSERT INTO "jalandhari" VALUES(109,6,'109:6','تم اپنے دین پر میں اپنے دین پر');
INSERT INTO "jalandhari" VALUES(110,1,'110:1','جب خدا کی مدد آ پہنچی اور فتح (حاصل ہو گئی)');
INSERT INTO "jalandhari" VALUES(110,2,'110:2','اور تم نے دیکھ لیا کہ لوگ غول کے غول خدا کے دین میں داخل ہو رہے ہیں');
INSERT INTO "jalandhari" VALUES(110,3,'110:3','تو اپنے پروردگار کی تعریف کے ساتھ تسبیح کرو اور اس سے مغفرت مانگو، بے شک وہ معاف کرنے والا ہے');
INSERT INTO "jalandhari" VALUES(111,1,'111:1','ابولہب کے ہاتھ ٹوٹیں اور وہ ہلاک ہو');
INSERT INTO "jalandhari" VALUES(111,2,'111:2','نہ تو اس کا مال ہی اس کے کچھ کام آیا اور نہ وہ جو اس نے کمایا');
INSERT INTO "jalandhari" VALUES(111,3,'111:3','وہ جلد بھڑکتی ہوئی آگ میں داخل ہو گا');
INSERT INTO "jalandhari" VALUES(111,4,'111:4','اور اس کی جورو بھی جو ایندھن سر پر اٹھائے پھرتی ہے');
INSERT INTO "jalandhari" VALUES(111,5,'111:5','اس کے گلے میں مونج کی رسّی ہو گی');
INSERT INTO "jalandhari" VALUES(112,1,'112:1','کہو کہ وہ (ذات پاک جس کا نام) الله (ہے) ایک ہے');
INSERT INTO "jalandhari" VALUES(112,2,'112:2','معبود برحق جو بےنیاز ہے');
INSERT INTO "jalandhari" VALUES(112,3,'112:3','نہ کسی کا باپ ہے اور نہ کسی کا بیٹا');
INSERT INTO "jalandhari" VALUES(112,4,'112:4','اور کوئی اس کا ہمسر نہیں');
INSERT INTO "jalandhari" VALUES(113,1,'113:1','کہو کہ میں صبح کے پروردگار کی پناہ مانگتا ہوں');
INSERT INTO "jalandhari" VALUES(113,2,'113:2','ہر چیز کی بدی سے جو اس نے پیدا کی');
INSERT INTO "jalandhari" VALUES(113,3,'113:3','اور شب تاریکی کی برائی سے جب اس کااندھیرا چھا جائے');
INSERT INTO "jalandhari" VALUES(113,4,'113:4','اور گنڈوں پر (پڑھ پڑھ کر) پھونکنے والیوں کی برائی سے');
INSERT INTO "jalandhari" VALUES(113,5,'113:5','اور حسد کرنے والے کی برائی سے جب حسد کرنے لگے');
INSERT INTO "jalandhari" VALUES(114,1,'114:1','کہو کہ میں لوگوں کے پروردگار کی پناہ مانگتا ہوں');
INSERT INTO "jalandhari" VALUES(114,2,'114:2','(یعنی) لوگوں کے حقیقی بادشاہ کی');
INSERT INTO "jalandhari" VALUES(114,3,'114:3','لوگوں کے معبود برحق کی');
INSERT INTO "jalandhari" VALUES(114,4,'114:4','(شیطان) وسوسہ انداز کی برائی سے جو (خدا کا نام سن کر) پیچھے ہٹ جاتا ہے');
INSERT INTO "jalandhari" VALUES(114,5,'114:5','جو لوگوں کے دلوں میں وسوسے ڈالتا ہے');
INSERT INTO "jalandhari" VALUES(114,6,'114:6','وہ جنّات میں سے (ہو) یا انسانوں میں سے');
