INSERT INTO "junagarri" VALUES(1,1,'1:1','شروع کرتا ہوں اللہ تعالیٰ کے نام سے جو بڑا مہربان نہایت رحم والا ہے۔ ');
INSERT INTO "junagarri" VALUES(1,2,'1:2','سب تعریف اللہ تعالیٰ کے لئے ہے جو تمام جہانوں کا پالنے والا ہے۔ ');
INSERT INTO "junagarri" VALUES(1,3,'1:3','بڑا مہربان نہایت رحم کرنے والا۔ ');
INSERT INTO "junagarri" VALUES(1,4,'1:4','بدلے کے دن (یعنی قیامت) کا مالک ہے۔ ');
INSERT INTO "junagarri" VALUES(1,5,'1:5','ہم صرف تیری ہی عبادت کرتے ہیں اور صرف تجھ ہی سے مدد چاہتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(1,6,'1:6','ہمیں سیدھی (اور سچی) راه دکھا۔ ');
INSERT INTO "junagarri" VALUES(1,7,'1:7','ان لوگوں کی راه جن پر تو نے انعام کیا ان کی نہیں جن پر غضب کیا گیا (یعنی وه لوگ جنہوں نے حق کو پہچانا، مگر اس پر عمل پیرا نہیں ہوئے) اور نہ گمراہوں کی (یعنی وه لوگ جو جہالت کے سبب راه حق سے برگشتہ ہوگئے)۔');
INSERT INTO "junagarri" VALUES(2,1,'2:1','الم ');
INSERT INTO "junagarri" VALUES(2,2,'2:2','اس کتاب (کے اللہ کی کتاب ہونے) میں کوئی شک نہیں پرہیزگاروں کو راه دکھانے والی ہے۔ ');
INSERT INTO "junagarri" VALUES(2,3,'2:3','جو لوگ غیب پر ایمان لاتے ہیں اور نماز کو قائم رکھتے ہیں اور ہمارے دیئے ہوئے (مال) میں سے خرچ کرتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(2,4,'2:4','اور جو لوگ ایمان لاتے ہیں اس پر جو آپ کی طرف اتارا گیا اور جو آپ سے پہلے اتارا گیا ، اور وه آخرت پر بھی یقین رکھتے ہیں۔');
INSERT INTO "junagarri" VALUES(2,5,'2:5','یہی لوگ اپنے رب کی طرف سے ہدایت پر ہیں اور یہی لوگ فلاح اور نجات پانے والے ہیں۔ ');
INSERT INTO "junagarri" VALUES(2,6,'2:6','کافروں کو آپ کا ڈرانا، یا نہ ڈرانا برابر ہے، یہ لوگ ایمان نہ لائیں گے۔ ');
INSERT INTO "junagarri" VALUES(2,7,'2:7','اللہ تعالیٰ نے ان کے دلوں پر اور ان کے کانوں پر مہر کر دی ہے اور ان کی آنکھوں پر پرده ہے اور ان کے لئے بڑا عذاب ہے۔ ');
INSERT INTO "junagarri" VALUES(2,8,'2:8','بعض لوگ کہتے ہیں کہ ہم اللہ تعالیٰ پر اور قیامت کے دن پر ایمان رکھتے ہیں، لیکن درحقیقت وه ایمان والے نہیں ہیں۔ ');
INSERT INTO "junagarri" VALUES(2,9,'2:9','وه اللہ تعالیٰ کو اور ایمان والوں کو دھوکا دیتے ہیں، لیکن دراصل وه خود اپنے آپ کو دھوکا دے رہے ہیں، مگر سمجھتے نہیں۔');
INSERT INTO "junagarri" VALUES(2,10,'2:10','ان کے دلوں میں بیماری تھی اللہ تعالیٰ نے انہیں بیماری میں مزید بڑھا دیا اور ان کے جھوٹ کی وجہ سے ان کے لئے دردناک عذاب ہے۔');
INSERT INTO "junagarri" VALUES(2,11,'2:11','اور جب ان سے کہا جاتا ہے کہ زمین میں فساد نہ کرو تو جواب دیتے ہیں کہ ہم تو صرف اصلاح کرنے والے ہیں۔');
INSERT INTO "junagarri" VALUES(2,12,'2:12','خبردار ہو! یقیناً یہی لوگ فساد کرنے والے ہیں ، لیکن شعور (سمجھ) نہیں رکھتے۔');
INSERT INTO "junagarri" VALUES(2,13,'2:13','اور جب ان سے کہا جاتا ہے کہ اور لوگوں (یعنی صحابہ) کی طرح تم بھی ایمان لاؤ تو جواب دیتے ہیں کہ کیا ہم ایسا ایمان لائیں جیسا بیوقوف لائے ہیں ، خبردار ہوجاؤ! یقیناً یہی بیوقوف ہیں، لیکن جانتے نہیں۔ ');
INSERT INTO "junagarri" VALUES(2,14,'2:14','اور جب ایمان والوں سے ملتے ہیں تو کہتے ہیں کہ ہم بھی ایمان والے ہیں اور جب اپنے بڑوں کے پاس جاتے ہیں تو کہتے ہیں کہ ہم تو تمہارے ساتھ ہیں ہم تو ان سے صرف مذاق کرتے ہیں۔');
INSERT INTO "junagarri" VALUES(2,15,'2:15','اللہ تعالیٰ بھی ان سے مذاق کرتا ہے اور انہیں ان کی سرکشی اور بہکاوے میں اور بڑھا دیتا ہے۔');
INSERT INTO "junagarri" VALUES(2,16,'2:16','یہ وه لوگ ہیں جنہوں نے گمراہی کو ہدایت کے بدلے میں خرید لیا، پس نہ تو ان کی تجارت نے ان کو فائده پہنچایا اور نہ یہ ہدایت والے ہوئے۔');
INSERT INTO "junagarri" VALUES(2,17,'2:17','ان کی مثال اس شخص کی سی ہے جس نے آگ جلائی، پس آس پاس کی چیزیں روشنی میں آئی ہی تھیں کہ اللہ ان کے نور کو لے گیا اور انہیں اندھیروں میں چھوڑ دیا، جو نہیں دیکھتے۔ ');
INSERT INTO "junagarri" VALUES(2,18,'2:18','بہرے، گونگے، اندھے ہیں۔ پس وه نہیں لوٹتے۔');
INSERT INTO "junagarri" VALUES(2,19,'2:19','یا آسمانی برسات کی طرح جس میں اندھیریاں اور گرج اور بجلی ہو، موت سے ڈر کر کڑاکے کی وجہ سے اپنی انگلیاں اپنے کانوں میں ڈال لیتے ہیں۔ اور اللہ تعالیٰ کافروں کو گھیرنے والا ہے۔');
INSERT INTO "junagarri" VALUES(2,20,'2:20','قریب ہے کہ بجلی ان کی آنکھیں اُچک لے جائے، جب ان کے لئے روشنی کرتی ہے تو اس میں چلتے پھرتے ہیں اور جب ان پر اندھیرا کرتی ہے تو کھڑے ہوجاتے ہیں اور اگر اللہ تعالیٰ چاہے تو ان کے کانوں اور آنکھوں کو بیکار کردے ۔ یقیناً اللہ تعالیٰ ہر چیز پر قدرت رکھنے والا ہے');
INSERT INTO "junagarri" VALUES(2,21,'2:21','اے لوگو! اپنے اس رب کی عبادت کرو جس نے تمہیں اور تم سے پہلے کے لوگوں کو پیدا کیا، یہی تمہارا بچاؤ ہے۔');
INSERT INTO "junagarri" VALUES(2,22,'2:22','جس نے تمہارے لئے زمین کو فرش اور آسمان کو چھت بنایا اور آسمان سے پانی اتار کر اس سے پھل پیدا کرکے تمہیں روزی دی، خبردار باوجود جاننے کے اللہ کے شریک مقرر نہ کرو۔ ');
INSERT INTO "junagarri" VALUES(2,23,'2:23','ہم نے جو کچھ اپنے بندے پر اتارا ہے اس میں اگر تمہیں شک ہو اور تم سچے ہو تو اس جیسی ایک سورت تو بنا لاؤ، تمہیں اختیار ہے کہ اللہ تعالیٰ کے سوا اپنے مددگاروں کو بھی بلا لو۔ ');
INSERT INTO "junagarri" VALUES(2,24,'2:24','پس اگر تم نے نہ کیا اور تم ہرگز نہیں کرسکتے تو (اسے سچا مان کر) اس آگ سے بچو جس کا ایندھن انسان اور پتھر ہیں ، جو کافروں کے لئے تیار کی گئی ہے۔ ');
INSERT INTO "junagarri" VALUES(2,25,'2:25','اور ایمان والوں اور نیک عمل کرنے والوں کو ان جنتوں کی خوشخبریاں دو، جن کےنیچے نہریں بہہ رہی ہیں۔ جب کبھی وه پھلوں کا رزق دیئے جائیں گے اور ہم شکل لائے جائیں گے تو کہیں گے یہ وہی ہے جو ہم اس سے پہلے دیئے گئے تھے اور ان کے لئے بیویاں ہیں صاف ستھری اور وه ان جنتوں میں ہمیشہ رہنے والے ہیں۔ ');
INSERT INTO "junagarri" VALUES(2,26,'2:26','یقیناً اللہ تعالیٰ کسی مثال کے بیان کرنے سے نہیں شرماتا، خواه مچھر کی ہو، یا اس سے بھی ہلکی چیز کی ۔ ایمان والے تو اسے اپنے رب کی جانب سے صحیح سمجھتے ہیں اور کفار کہتے ہیں کہ اس مثال سے اللہ نے کیا مراد لی ہے؟ اس کے ذریعے بیشتر کو گمراه کرتا ہے اور اکثر لوگوں کو راه راست پر لاتا ہے اور گمراه تو صرف فاسقوں کو ہی کرتا ہے۔');
INSERT INTO "junagarri" VALUES(2,27,'2:27','جو لوگ اللہ تعالیٰ کے مضبوط عہد کو توڑ دیتے ہیں اور اللہ تعالیٰ نے جن چیزوں کے جوڑنے کا حکم دیا ہے، انہیں کاٹتے اور زمین میں فساد پھیلاتے ہیں، یہی لوگ نقصان اٹھانے والے ہیں۔ ');
INSERT INTO "junagarri" VALUES(2,28,'2:28','تم اللہ کے ساتھ کیسے کفر کرتے ہو؟ حالانکہ تم مرده تھے اس نے تمہیں زنده کیا، پھر تمہیں مار ڈالے گا، پھر زنده کرے گا ، پھر اسی کی طرف لوٹائے جاؤ گے۔');
INSERT INTO "junagarri" VALUES(2,29,'2:29','وه اللہ جس نے تمہارے لئے زمین کی تمام چیزوں کو پیدا کیا ، پھر آسمان کی طرف قصد کیا اور ان کو ٹھیک ٹھاک سات آسمان بنایا اور وه ہر چیز کو جانتا ہے۔');
INSERT INTO "junagarri" VALUES(2,30,'2:30','اور جب تیرے رب نے فرشتوں سے کہا کہ میں زمین میں خلیفہ بنانے والا ہوں، تو انہوں نے کہا ایسے شخص کو کیوں پیدا کرتا ہے جو زمین میں فساد کرے اور خون بہائے؟ اور ہم تیری تسبیح، حمد اور پاکیزگی بیان کرنے والے ہیں۔ اللہ تعالیٰ نے فرمایا، جو میں جانتا ہوں تم نہیں جانتے۔ ');
INSERT INTO "junagarri" VALUES(2,31,'2:31','اور اللہ تعالیٰ نے آدم کو تمام نام سکھا کر ان چیزوں کو فرشتوں کے سامنے پیش کیا اور فرمایا، اگر تم سچے ہو تو ان چیزوں کے نام بتاؤ۔');
INSERT INTO "junagarri" VALUES(2,32,'2:32','ان سب نے کہا اے اللہ! تیری ذات پاک ہے ہمیں تو صرف اتنا ہی علم ہے جتنا تونے ہمیں سکھا رکھا ہے، پورے علم وحکمت والا تو تو ہی ہے۔');
INSERT INTO "junagarri" VALUES(2,33,'2:33','اللہ تعالیٰ نے (حضرت) آدم ﴿علیہ السلام﴾ سے فرمایا تم ان کے نام بتا دو۔ جب انہوں نے بتا دیئے تو فرمایا کہ کیا میں نے تمہیں (پہلے ہی) نہ کہا تھا کہ زمین اور آسمانوں کا غیب میں ہی جانتا ہوں اور میرے علم میں ہے جو تم ظاہر کر رہے ہو اور جو تم چھپاتے تھے۔ ');
INSERT INTO "junagarri" VALUES(2,34,'2:34','اور جب ہم نے فرشتوں سے کہا کہ آدم کو سجده کرو تو ابلیس کے سوا سب نے سجده کیا۔ اس نے انکار کیا اور تکبر کیا اور وه کافروں میں ہوگیا۔ ');
INSERT INTO "junagarri" VALUES(2,35,'2:35','اور ہم نے کہہ دیا کہ اے آدم! تم اور تمہاری بیوی جنت میں رہو اور جہاں کہیں سے چاہو بافراغت کھاؤ پیو، لیکن اس درخت کے قریب بھی نہ جانا ورنہ ظالم ہوجاؤ گے');
INSERT INTO "junagarri" VALUES(2,36,'2:36','لیکن شیطان نے ان کو بہکا کر وہاں سے نکلوا ہی دیا اور ہم نے کہہ دیا کہ اتر جاؤ! تم ایک دوسرے کے دشمن ہو اور ایک وقت مقرر تک تمہارے لئے زمین میں ٹھہرنا اور فائده اٹھانا ہے');
INSERT INTO "junagarri" VALUES(2,37,'2:37','آدم (علیہ السلام) نے اپنے رب سے چند باتیں سیکھ لیں اور اللہ تعالیٰ نے ان کی توبہ قبول فرمائی، بےشک وہی توبہ قبول کرنے والا اور رحم کرنے والا ہے۔');
INSERT INTO "junagarri" VALUES(2,38,'2:38','ہم نے کہا تم سب یہاں سے چلے جاؤ، جب کبھی تمہارے پاس میری ہدایت پہنچے تو اس کی تابعداری کرنے والوں پر کوئی خوف وغم نہیں۔');
INSERT INTO "junagarri" VALUES(2,39,'2:39','اور جو انکار کرکے ہماری آیتوں کو جھٹلائیں، وه جہنمی ہیں اور ہمیشہ اسی میں رہیں گے۔ ');
INSERT INTO "junagarri" VALUES(2,40,'2:40','اے بنی اسرائیل! میری اس نعمت کو یاد کرو جو میں نے تم پر انعام کی اور میرے عہد کو پورا کرو میں تمہارے عہد کو پورا کروں گا اور مجھ ہی سے ڈرو۔');
INSERT INTO "junagarri" VALUES(2,41,'2:41','اور اس کتاب پر ایمان لاؤ جو میں نے تمہاری کتابوں کی تصدیق میں نازل فرمائی ہے اور اس کے ساتھ تم ہی پہلے کافر نہ بنو اور میری آیتوں کو تھوڑی تھوڑی قیمت پر نہ فروخت کرو اور صرف مجھ ہی سے ڈرو۔');
INSERT INTO "junagarri" VALUES(2,42,'2:42','اور حق کو باطل کے ساتھ خَلط مَلط نہ کرو اور نہ حق کو چھپاؤ، تمہیں تو خود اس کا علم ہے۔');
INSERT INTO "junagarri" VALUES(2,43,'2:43','اور نمازوں کو قائم کرو اور زکوٰة دو اور رکوع کرنے والوں کے ساتھ رکوع کرو۔');
INSERT INTO "junagarri" VALUES(2,44,'2:44','کیا لوگوں کو بھلائیوں کا حکم کرتے ہو؟ اور خود اپنے آپ کو بھول جاتے ہو باوجودیکہ تم کتاب پڑھتے ہو، کیا اتنی بھی تم میں سمجھ نہیں؟');
INSERT INTO "junagarri" VALUES(2,45,'2:45','اور صبر اور نماز کے ساتھ مدد طلب کرو یہ چیز شاق ہے، مگر ڈر رکھنے والوں پر(نہیں)۔ ');
INSERT INTO "junagarri" VALUES(2,46,'2:46','جو جانتے ہیں کہ بے شک وه اپنے رب سے ملاقات کرنے والے اور یقیناً وہ اسی کی طرف لوٹ کر جانے والے ہیں۔');
INSERT INTO "junagarri" VALUES(2,47,'2:47','اے اولاد یعقوب! میری اس نعمت کو یاد کرو جو میں نے تم پر انعام کی اور میں نے تمہیں تمام جہانوں پر فضیلت دی۔ ');
INSERT INTO "junagarri" VALUES(2,48,'2:48','اس دن سے ڈرتے رہو جب کوئی کسی کو نفع نہ دے سکے گا اور نہ ہی اس کی بابت کوئی سفارش قبول ہوگی اور نہ کوئی بدلہ اس کے عوض لیا جائے گا اور نہ وه مدد کئے جائیں گے۔');
INSERT INTO "junagarri" VALUES(2,49,'2:49','اور جب ہم نے تمہیں فرعونیوں سے نجات دی جو تمہیں بدترین عذاب دیتے تھے جو تمہارے لڑکوں کو مار ڈالتے تھے اور تمہاری لڑکیوں کو چھوڑ دیتے تھے، اس نجات دینے میں تمہارے رب کی بڑی مہربانی تھی۔');
INSERT INTO "junagarri" VALUES(2,50,'2:50','اور جب ہم نے تمہارے لئے دریا چیر (پھاڑ) دیا اور تمہیں اس سے پار کردیا اور فرعونیوں کو تمہاری نظروں کے سامنے اس میں ڈبو دیا۔');
INSERT INTO "junagarri" VALUES(2,51,'2:51','اور ہم نے (حضرت) موسیٰ ﴿علیہ السلام﴾ سے چالیس راتوں کا وعده کیا، پھر تم نے اس کے بعد بچھڑا پوجنا شروع کردیا اور ظالم بن گئے۔ ');
INSERT INTO "junagarri" VALUES(2,52,'2:52','لیکن ہم نے باوجود اس کے پھر بھی تمہیں معاف کردیا، تاکہ تم شکر کرو۔');
INSERT INTO "junagarri" VALUES(2,53,'2:53','اور ہم نے (حضرت) موسیٰ﴿علیہ السلام﴾ کو تمہاری ہدایت کے لئے کتاب اور معجزے عطا فرمائے۔ ');
INSERT INTO "junagarri" VALUES(2,54,'2:54','جب (حضرت موسیٰ) ﴿علیہ السلام﴾ نے اپنی قوم سے کہا کہ اے میری قوم! بچھڑے کو معبود بنا کر تم نے اپنی جانوں پر ظلم کیا ہے، اب تم اپنے پیدا کرنے والے کی طرف رجوع کرو، اپنے کو آپس میں قتل کرو، تمہاری بہتری اللہ تعالیٰ کے نزدیک اسی میں ہے، تو اس نے تمہاری توبہ قبول کی، وه توبہ قبول کرنے والا اور رحم وکرم کرنے والا ہے۔ ');
INSERT INTO "junagarri" VALUES(2,55,'2:55','اور (تم اسے بھی یاد کرو) تم نے (حضرت) موسیٰ ﴿علیہ السلام﴾ سے کہا تھا کہ جب تک ہم اپنے رب کو سامنے نہ دیکھ لیں ہرگز ایمان نہ لائیں گے (جس گستاخی کی سزا میں) تم پر تمہارے دیکھتے ہوئے بجلی گری۔');
INSERT INTO "junagarri" VALUES(2,56,'2:56','لیکن پھر اس لئے کہ تم شکرگزاری کرو، اس موت کے بعد بھی ہم نے تمہیں زنده کردیا۔');
INSERT INTO "junagarri" VALUES(2,57,'2:57','اور ہم نے تم پر بادل کا سایہ کیا اور تم پر من و سلویٰ اتارا (اور کہہ دیا) کہ ہماری دی ہوئی پاکیزه چیزیں کھاؤ، اور انہوں نے ہم پر ظلم نہیں کیا، البتہ وه خود اپنی جانوں پر ظلم کرتے تھے۔');
INSERT INTO "junagarri" VALUES(2,58,'2:58','اور ہم نے تم سے کہا کہ اس بستی میں جاؤ اور جو کچھ جہاں کہیں سے چاہو بافراغت کھاؤ پیو اور دروازے میں سجدے کرتے ہوئے گزرو اور زبان سے حِطّہ کہو ہم تمہاری خطائیں معاف فرمادیں گے اور نیکی کرنے والوں کو اور زیاده دیں گے۔');
INSERT INTO "junagarri" VALUES(2,59,'2:59','پھر ان ظالموں نے اس بات کو جو ان سے کہی گئی تھی بدل ڈالی، ہم نے بھی ان ظالموں پر ان کے فسق ونافرمانی کی وجہ سے آسمانی عذاب نازل کیا۔');
INSERT INTO "junagarri" VALUES(2,60,'2:60','اور جب موسیٰ (علیہ السلام) نے اپنی قوم کے لئے پانی مانگا تو ہم نے کہا کہ اپنی لاٹھی پتھر پر مارو، جس سے باره چشمے پھوٹ نکلے اور ہر گروه نے اپنا چشمہ پہچان لیا (اور ہم نے کہہ دیا کہ) اللہ تعالیٰ کا رزق کھاؤ پیو اورزمین میں فساد نہ کرتے پھرو۔');
INSERT INTO "junagarri" VALUES(2,61,'2:61','اور جب تم نے کہا اے موسیٰ! ہم سے ایک ہی قسم کے کھانے پر ہرگز صبر نہ ہوسکے گا، اس لئے اپنے رب سے دعا کیجیئے کہ وه ہمیں زمین کی پیداوار ساگ، ککڑی، گیہوں، مسور اور پیاز دے، آپ نے فرمایا، بہتر چیز کے بدلے ادنیٰ چیز کیوں طلب کرتے ہو! اچھا شہر میں جاؤ وہاں تمہاری چاہت کی یہ سب چیزیں ملیں گی ۔ ان پر ذلت اور مسکینی ڈال دی گئی اور اللہ تعالی کا غضب لے کر وه لوٹے یہ اس لئے کہ وه اللہ تعالیٰ کی آیتوں کے ساتھ کفر کرتے تھے اور نبیوں کو ناحق قتل کرتے تھے، یہ ان کی نافرمانیوں اور زیادتیوں کا نتیجہ ہے۔ ');
INSERT INTO "junagarri" VALUES(2,62,'2:62','مسلمان ہوں، یہودی ہوں، نصاریٰ ہوں یا صابی ہوں، جو کوئی بھی اللہ تعالیٰ پر اور قیامت کے دن پر ایمان لائے اور نیک عمل کرے ان کے اجر ان کے رب کے پاس ہیں اور ان پر نہ تو کوئی خوف ہے اور نہ اداسی ');
INSERT INTO "junagarri" VALUES(2,63,'2:63','اور جب ہم نے تم سے وعده لیا اور تم پر طور پہاڑ لاکھڑا کردیا (اور کہا) جو ہم نے تمہیں دیا ہے، اسے مضبوطی سے تھام لو اور جو کچھ اس میں ہے اسے یاد کرو تاکہ تم بچ سکو');
INSERT INTO "junagarri" VALUES(2,64,'2:64','لیکن تم اس کے بعد بھی پھر گئے، پھر اگر اللہ تعالیٰ کا فضل اور اس کی رحمت تم پر نہ ہوتی تو تم نقصان والے ہوجاتے۔');
INSERT INTO "junagarri" VALUES(2,65,'2:65','اور یقیناً تمہیں ان لوگوں کا علم بھی ہے جو تم میں سے ہفتہ کے بارے میں حد سے بڑھ گئے اور ہم نے بھی کہہ دیا کہ تم ذلیل بندر بن جاؤ۔');
INSERT INTO "junagarri" VALUES(2,66,'2:66','اسے ہم نے اگلوں پچھلوں کے لئے عبرت کا سبب بنا دیا اور پرہیزگاروں کے لئے وعظ ونصیحت کا۔');
INSERT INTO "junagarri" VALUES(2,67,'2:67','اور (حضرت) موسیٰ﴿علیہ السلام﴾ نے جب اپنی قوم سے کہا کہ اللہ تعالیٰ تمہیں ایک گائے ذبح کرنے کا حکم دیتا ہے تو انہوں نے کہا ہم سے مذاق کیوں کرتے ہیں؟ آپ نے جواب دیا کہ میں ایسا جاہل ہونے سے اللہ تعالیٰ کی پناه پکڑتا ہوں۔');
INSERT INTO "junagarri" VALUES(2,68,'2:68','انہوں نے کہا اے موسیٰ! دعا کیجیئے کہ اللہ تعالیٰ ہمارے لئے اس کی ماہیت بیان کردے، آپ نے فرمایا سنو! وه گائے نہ تو بالکل بڑھیا ہو، نہ بچہ، بلکہ درمیانی عمر کی نوجوان ہو، اب جو تمہیں حکم دیا گیا ہے بجا لاؤ۔');
INSERT INTO "junagarri" VALUES(2,69,'2:69','وه پھر کہنے لگے کہ دعا کیجیے کہ اللہ تعالیٰ بیان کرے کہ اس کا رنگ کیا ہے؟ فرمایا وه کہتا ہے کہ وه گائے زرد رنگ کی ہے، چمکیلا اور دیکھنے والوں کو بھلا لگنے والا اس کا رنگ ہے۔');
INSERT INTO "junagarri" VALUES(2,70,'2:70','وه کہنے لگے کہ اپنے رب سے اور دعا کیجیئے کہ ہمیں اس کی مزید ماہیت بتلائے، اس قسم کی گائے تو بہت ہیں پتہ نہیں چلتا، اگر اللہ نے چاہا تو ہم ہدایت والے ہوجائیں گے۔');
INSERT INTO "junagarri" VALUES(2,71,'2:71','آپ نے فرمایا کہ اللہ کا فرمان ہے کہ وه گائے کام کرنے والی زمین میں ہل جوتنے والی اور کھیتوں کو پانی پلانے والی نہیں، وه تندرست اور بےداغ ہے۔ انہوں نے کہا، اب آپ نے حق واضح کردیا گو وه حکم برداری کے قریب نہ تھے، لیکن اسے مانا اور وه گائے ذبح کردی۔ ');
INSERT INTO "junagarri" VALUES(2,72,'2:72','جب تم نے ایک شخص کو قتل کر ڈالا، پھر اس میں اختلاف کرنے لگے اور تمہاری پوشیدگی کو اللہ تعالیٰ ظاہر کرنے والا تھا۔ ');
INSERT INTO "junagarri" VALUES(2,73,'2:73','ہم نے کہا کہ اس گائے کا ایک ٹکڑا مقتول کے جسم پر لگا دو، (وه جی اٹھے گا) اسی طرح اللہ مردوں کو زنده کرکے تمہیں تمہاری عقل مندی کے لئے اپنی نشانیاں دکھاتا ہے۔ ');
INSERT INTO "junagarri" VALUES(2,74,'2:74','پھر اس کے بعد تمہارے دل پتھر جیسے بلکہ اس سے بھی زیاده سخت ہوگئے ، بعض پتھروں سے تو نہریں بہہ نکلتی ہیں، اور بعض پھٹ جاتے ہیں اور ان سے پانی نکل آتا ہے، اور بعض اللہ تعالیٰ کے ڈر سے گرگر پڑتے ہیں ، اور تم اللہ تعالیٰ کو اپنے اعمال سے غافل نہ جانو۔');
INSERT INTO "junagarri" VALUES(2,75,'2:75','(مسلمانو!) کیا تمہاری خواہش ہے کہ یہ لوگ ایماندار بن جائیں، حالانکہ ان میں ایسے لوگ بھی جو کلام اللہ کو سن کر، عقل وعلم والے ہوتے ہوئے، پھر بھی بدل ڈالا کرتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(2,76,'2:76','جب ایمان والوں سے ملتے ہیں تو اپنی ایمانداری ظاہر کرتے ہیں اور جب آپس میں ملتے ہیں تو کہتے ہیں کہ مسلمانوں کو کیوں وه باتیں پہنچاتے ہو جو اللہ تعالیٰ نے تمہیں سکھائی ہیں، کیا جانتے نہیں کہ یہ تو اللہ تعالیٰ کے پاس تم پر ان کی حجت ہوجائے گی');
INSERT INTO "junagarri" VALUES(2,77,'2:77','کیا یہ نہیں جانتے کہ اللہ تعالیٰ ان کی پوشیدگی اور ظاہرداری سب کو جانتا ہے؟ ');
INSERT INTO "junagarri" VALUES(2,78,'2:78','ان میں سے بعض ان پڑھ ایسے بھی ہیں کہ جو کتاب کے صرف ظاہری الفاظ کو ہی جانتے ہیں اور صرف گمان اور اٹکل ہی پر ہیں۔ ');
INSERT INTO "junagarri" VALUES(2,79,'2:79','ان لوگوں کے لئے “ویل” ہے جو اپنے ہاتھوں کی لکھی ہوئی کتاب کو اللہ تعالیٰ کی طرف کی کہتے ہیں اور اس طرح دنیا کماتے ہیں، ان کے ہاتھوں کی لکھائی کو اور ان کی کمائی کو ویل (ہلاکت) اور افسوس ہے۔ ');
INSERT INTO "junagarri" VALUES(2,80,'2:80','یہ لوگ کہتے ہیں کہ ہم تو صرف چند روز جہنم میں رہیں گے، ان سے کہو کہ کیا تمہارے پاس اللہ تعالیٰ کا کوئی پروانہ ہے؟ اگر ہے تو یقیناً اللہ تعالیٰ اپنے وعدے کا خلاف نہیں کرے گا، (ہرگز نہیں) بلکہ تم تو اللہ کے ذمے وه باتیں لگاتے ہو جنہیں تم نہیں جانتے۔');
INSERT INTO "junagarri" VALUES(2,81,'2:81','یقیناً جس نے بھی برے کام کئے اور اس کی نافرمانیوں نے اسے گھیر لیا، وه ہمیشہ کے لئے جہنمی ہے۔');
INSERT INTO "junagarri" VALUES(2,82,'2:82','اور جو لوگ ایمان لائیں اور نیک کام کریں وه جنتی ہیں جو جنت میں ہمیشہ رہیں گے۔ ');
INSERT INTO "junagarri" VALUES(2,83,'2:83','اور جب ہم نے بنی اسرائیل سے وعده لیا کہ تم اللہ تعالیٰ کے سوا دوسرے کی عبادت نہ کرنا اور ماں باپ کے ساتھ اچھا سلوک کرنا، اسی طرح قرابتداروں، یتیموں اور مسکینوں کے ساتھ اور لوگوں کو اچھی باتیں کہنا، نمازیں قائم رکھنا اور زکوٰة دیتے رہا کرنا، لیکن تھوڑے سے لوگوں کے علاوه تم سب پھر گئے اور منھ موڑ لیا۔');
INSERT INTO "junagarri" VALUES(2,84,'2:84','اورجب ہم نے تم سے وعده لیا کہ آپس میں خون نہ بہانا (قتل نہ کرنا) اور آپس والوں کو جلا وطن نہ کرنا، تم نے اقرار کیا اور تم اس کے شاہد بنے۔ ');
INSERT INTO "junagarri" VALUES(2,85,'2:85','لیکن پھر بھی تم نے آپس میں قتل کیا اورآپس کے ایک فرقے کو جلا وطن بھی کیا اور گناه اور زیادتی کے کاموں میں ان کے خلاف دوسرے کی طرفداری کی، ہاں جب وه قیدی ہو کر تمہارے پاس آئے تو تم نے ان کے فدیے دیئے، لیکن ان کا نکالنا جو تم پر حرام تھا (اس کا کچھ خیال نہ کیا) کیا بعض احکام پر ایمان رکھتے ہو اور بعض کے ساتھ کفر کرتے ہو؟ تم میں سے جو بھی ایسا کرے، اس کی سزا اس کے سوا کیا ہو کہ دنیا میں رسوائی اور قیامت کے دن سخت عذاب کی مار، اور اللہ تعالیٰ تمہارے اعمال سے بےخبر نہیں');
INSERT INTO "junagarri" VALUES(2,86,'2:86','یہ وه لوگ ہیں جنہوں نے دنیا کی زندگی کو آخرت کے بدلے خرید لیا ہے، ان کے نہ تو عذاب ہلکے ہوں گے اور نہ ان کی مدد کی جائے گی۔ ');
INSERT INTO "junagarri" VALUES(2,87,'2:87','ہم نے (حضرت) موسیٰ کو کتاب دی اور ان کے پیچھے اور رسول بھیجے اور ہم نے (حضرت) عیسیٰ ابن مریم کو روشن دلیلیں دیں اور روح القدس سے ان کی تائید کروائی ۔ لیکن جب کبھی تمہارے پاس رسول وه چیز لائے جو تمہاری طبیعتوں کے خلاف تھی، تم نے جھٹ سے تکبر کیا، پس بعض کو تو جھٹلادیا اور بعض کو قتل بھی کرڈالا۔ ');
INSERT INTO "junagarri" VALUES(2,88,'2:88','یہ کہتے ہیں کہ ہمارے دل غلاف والے ہیں نہیں نہیں بلکہ ان کے کفر کی وجہ سے انہیں اللہ تعالیٰ نے ملعون کردیا ہے، ان کا ایمان بہت ہی تھوڑا ہے۔ ');
INSERT INTO "junagarri" VALUES(2,89,'2:89','اور ان کے پاس جب اللہ تعالیٰ کی کتاب ان کی کتاب کو سچا کرنے والی آئی، حالانکہ پہلے یہ خود (اس کے ذریعہ) کافروں پر فتح چاہتے تھے تو باوجود آجانے اور باوجود پہچان لینے کے پھر کفر کرنے لگے، اللہ تعالیٰ کی لعنت ہو کافروں پر۔');
INSERT INTO "junagarri" VALUES(2,90,'2:90','بہت بری ہے وه چیز جس کے بدلے انہوں نے اپنے آپ کو بیچ ڈالا، وه ان کا کفر کرنا ہے۔ اللہ تعالیٰ کی طرف سے نازل شده چیز کے ساتھ محض اس بات سے جل کر کہ اللہ تعالیٰ نے اپنا فضل اپنے جس بنده پر چاہا نازل فرمایا، اس کے باعث یہ لوگ غضب پر غضب کے مستحق ہوگئے اور ان کافروں کے لئے رسوا کرنے والا عذاب ہے');
INSERT INTO "junagarri" VALUES(2,91,'2:91','اور جب ان سے کہا جاتا ہے کہ اللہ تعالیٰ کی اتاری ہوئی کتاب پر ایمان لاؤ تو کہہ دیتے ہیں کہ جو ہم پر اتاری گئی اس پر ہمارا ایمان ہے ۔ حالانکہ اس کے بعد والی کے ساتھ جو ان کی کتاب کی تصدیق کرنے والی ہے، کفر کرتے ہیں، اچھا ان سے یہ تو دریافت کریں کہ اگر تمہارا ایمان پہلی کتابوں پر ہے تو پھر تم نے اگلے انبیا کو کیوں قتل کیا؟ ');
INSERT INTO "junagarri" VALUES(2,92,'2:92','تمہارے پاس تو موسیٰ یہی دلیلیں لے کر آئے لیکن تم نے پھر بھی بچھڑا پوجا تم ہو ہی ظالم۔');
INSERT INTO "junagarri" VALUES(2,93,'2:93','جب ہم نے تم سے وعده لیا اور تم پر طور کو کھڑا کردیا (اور کہہ دیا) کہ ہماری دی ہوئی چیز کو مضبوط تھامو اور سنو! تو انہوں نے کہا، ہم نے سنا اور نافرمانی کی اور ان کے دلوں میں بچھڑے کی محبت (گویا) پلا دی گئی بسبب ان کے کفر کے ۔ ان سے کہہ دیجیئے کہ تمہارا ایمان تمہیں برا حکم دے رہا ہے، اگر تم مومن ہو');
INSERT INTO "junagarri" VALUES(2,94,'2:94','آپ کہہ دیجیئے کہ اگر آخرت کا گھر صرف تمہارے ہی لئے ہے، اللہ کے نزدیک اور کسی کے لئے نہیں، تو آو اپنی سچائی کے ثبوت میں موت طلب کرو۔');
INSERT INTO "junagarri" VALUES(2,95,'2:95','لیکن اپنی کرتوتوں کو دیکھتے ہوئے کبھی بھی موت نہیں مانگیں گے اللہ تعالیٰ ظالموں کو خوب جانتا ہے۔');
INSERT INTO "junagarri" VALUES(2,96,'2:96','بلکہ سب سے زیاده دنیا کی زندگی کا حریص اے نبی! آپ انہیں کو پائیں گے۔ یہ حرص زندگی میں مشرکوں سے بھی زیاده ہیں ان میں سے تو ہر شخص ایک ایک ہزار سال کی عمر چاہتا ہے، گویا یہ عمر دیا جانا بھی انہیں عذاب سے نہیں چھڑا سکتا، اللہ تعالیٰ ان کے کاموں کو بخوبی دیکھ رہا ہے');
INSERT INTO "junagarri" VALUES(2,97,'2:97','(اے نبی!) آپ کہہ دیجیئے : جو شخص جبریل کا دشمن ہے، تو بے شک اس نے اس (قرآن) کو آپ کے دل پر اللہ کے حکم سے اتارا ہے، جو اپنے سے پہلے کی کتاب کی تصدیق کرنے والا اور مومنوں کو ہدایت اور خوشخبری دینے والا ہے (تو اللہ بھی اس کا دشمن ہے)۔');
INSERT INTO "junagarri" VALUES(2,98,'2:98','جو شخص اللہ کا اور اس کے فرشتوں اور اس کے رسولوں اور جبرائیل اور میکائیل کا دشمن ہو، ایسے کافروں کا دشمن خود اللہ ہے۔ ');
INSERT INTO "junagarri" VALUES(2,99,'2:99','اور یقیناً ہم نے آپ کی طرف روشن دلیلیں بھیجی ہیں جن کا انکار سوائے بدکاروں کے کوئی نہیں کرتا۔');
INSERT INTO "junagarri" VALUES(2,100,'2:100','یہ لوگ جب کبھی کوئی عہد کرتے ہیں توان کی ایک نہ ایک جماعت اسے توڑ دیتی ہے، بلکہ ان میں سے اکثر ایمان سے خالی ہیں۔');
INSERT INTO "junagarri" VALUES(2,101,'2:101','جب کبھی ان کے پاس اللہ کا کوئی رسول ان کی کتاب کی تصدیق کرنے والا آیا، ان اہل کتاب کے ایک فرقہ نے اللہ کی کتاب کو اس طرح پیٹھ پیچھے ڈال دیا، گویا جانتے ہی نہ تھے۔ ');
INSERT INTO "junagarri" VALUES(2,102,'2:102','اور اس چیز کے پیچھے لگ گئے جسے شیاطین (حضرت) سلیمان کی حکومت میں پڑھتے تھے۔ سلیمان نے تو کفر نہ کیا تھا، بلکہ یہ کفر شیطانوں کا تھا، وه لوگوں کو جادو سکھایا کرتے تھے ، اور بابل میں ہاروت ماروت دو فرشتوں پرجو اتارا گیا تھا ، وه دونوں بھی کسی شخص کو اس وقت تک نہیں سکھاتے تھے جب تک یہ نہ کہہ دیں کہ ہم تو ایک آزمائش ہیں تو کفر نہ کر، پھر لوگ ان سے وه سیکھتے جس سے خاوند وبیوی میں جدائی ڈال دیں اور دراصل وه بغیر اللہ تعالیٰ کی مرضی کے کسی کو کوئی نقصان نہیں پہنچا سکتے ، یہ لوگ وه سیکھتے ہیں جو انہیں نقصان پہنچائے اور نفع نہ پہنچا سکے، اور وه بالیقین جانتے ہیں کہ اس کے لینے والے کا آخرت میں کوئی حصہ نہیں۔ اور وه بدترین چیز ہے جس کے بدلے وه اپنے آپ کو فروخت کر رہے ہیں، کاش کہ یہ جانتے ہوتے۔');
INSERT INTO "junagarri" VALUES(2,103,'2:103','اگر یہ لوگ صاحب ایمان متقی بن جاتے تو اللہ تعالیٰ کی طرف سے بہترین ثواب انہیں ملتا، اگر یہ جانتے ہوتے۔');
INSERT INTO "junagarri" VALUES(2,104,'2:104','اے ایمان والو! تم (نبی صلی اللہ علیہ وسلم کو) “راعنا” نہ کہا کرو، بلکہ “انظرنا” کہو یعنی ہماری طرف دیکھئے اور سنتے رہا کرو اور کافروں کے لئے دردناک عذاب ہے');
INSERT INTO "junagarri" VALUES(2,105,'2:105','نہ تو اہل کتاب کے کافر اور نہ مشرکین چاہتے ہیں کہ تم پر تمہارے رب کی کوئی بھلائی نازل ہو (ان کے اس حسد سے کیا ہوا) اللہ تعالیٰ جسے چاہے اپنی رحمت خصوصیت سے عطا فرمائے، اللہ تعالیٰ بڑے فضل والا ہے۔');
INSERT INTO "junagarri" VALUES(2,106,'2:106','جس آیت کو ہم منسوخ کردیں، یا بھلا دیں اس سے بہتر یا اس جیسی اور لاتے ہیں، کیا تو نہیں جانتا کہ اللہ تعالیٰ ہر چیز پر قادر ہے۔');
INSERT INTO "junagarri" VALUES(2,107,'2:107','کیا تجھے علم نہیں کہ زمین و آسمان کا ملک اللہ ہی کے لئے ہے اور اللہ کے سوا تمہارا کوئی ولی اور مددگار نہیں۔');
INSERT INTO "junagarri" VALUES(2,108,'2:108','کیا تم اپنے رسول سے یہی پوچھنا چاہتے ہو جو اس سے پہلے موسیٰ ﴿علیہ السلام﴾ سے پوچھا گیا تھا؟ (سنو) ایمان کو کفر سے بدلنے والا سیدھی راه سے بھٹک جاتا ہے۔');
INSERT INTO "junagarri" VALUES(2,109,'2:109','ان اہل کتاب کے اکثر لوگ باوجود حق واضح ہوجانے کے محض حسد وبغض کی بنا پر تمہیں بھی ایمان سے ہٹا دینا چاہتے ہیں، تم بھی معاف کرو اور چھوڑو یہاں تک کہ اللہ تعالیٰاپنا حکم لائے۔ یقیناً اللہ تعالے ہر چیز پر قدرت رکھتا ہے۔');
INSERT INTO "junagarri" VALUES(2,110,'2:110','تم نمازیں قائم رکھو اور زکوٰة دیتے رہا کرو اور جو کچھ بھلائی تم اپنے لئے آگے بھیجو گے، سب کچھ اللہ کے پاس پالو گے، بےشک اللہ تعالیٰ تمہارے اعمال کو خوب دیکھ رہا ہے۔ ');
INSERT INTO "junagarri" VALUES(2,111,'2:111','یہ کہتے ہیں کہ جنت میں یہود ونصاریٰ کے سوا اور کوئی نہ جائے گا، یہ صرف ان کی آرزوئیں ہیں، ان سے کہو کہ اگر تم سچے ہو تو کوئی دلیل تو پیش کرو۔ ');
INSERT INTO "junagarri" VALUES(2,112,'2:112','سنو! جو بھی اپنے آپ کو خلوص کے ساتھ اللہ کے سامنے جھکا دے ۔ بےشک اسے اس کا رب پورا بدلہ دے گا، اس پر نہ تو کوئی خوف ہوگا، نہ غم اور اداسی۔');
INSERT INTO "junagarri" VALUES(2,113,'2:113','یہود کہتے ہیں کہ نصرانی حق پر نہیں اور نصرانی کہتے ہیں کہ یہودی حق پر نہیں ، حالانکہ یہ سب لوگ تورات پڑھتے ہیں۔ اسی طرح ان ہی جیسی بات بےعلم بھی کہتے ہیں ۔ قیامت کے دن اللہ ان کے اس اختلاف کا فیصلہ ان کے درمیان کردے گا۔');
INSERT INTO "junagarri" VALUES(2,114,'2:114','اس شخص سے بڑھ کر ظالم کون ہے جو اللہ تعالیٰ کی مسجدوں میں اللہ تعالیٰ کے ذکر کئے جانے کو روکے اور ان کی بربادی کی کوشش کرے ، ایسے لوگوں کو خوف کھاتے ہوئے اس میں جانا چاہیئے ، ان کے لئے دنیا میں بھی رسوائی ہے اور آخرت میں بھی بڑا عذاب ہے۔');
INSERT INTO "junagarri" VALUES(2,115,'2:115','اور مشرق اور مغرب کا مالک اللہ ہی ہے۔ تم جدھر بھی منھ کرو ادھر ہی اللہ کا منھ ہے ، اللہ تعالیٰ کشادگی اور وسعت والا اور بڑے علم والا ہے۔');
INSERT INTO "junagarri" VALUES(2,116,'2:116','یہ کہتے ہیں کہ اللہ تعالیٰ کی اولاد ہے، (نہیں بلکہ) وه پاک ہے زمین و آسمان کی تمام مخلوق اس کی ملکیت میں ہے اور ہر ایک اس کافرمانبردار ہے۔');
INSERT INTO "junagarri" VALUES(2,117,'2:117','وه زمین اور آسمانوں کا ابتداءً پیدا کرنے والا ہے، وه جس کام کو کرنا چاہے کہہ دیتا ہے کہ ہوجا، بس وه وہیں ہوجاتا ہے۔ ');
INSERT INTO "junagarri" VALUES(2,118,'2:118','اسی طرح بے علم لوگوں نے بھی کہا کہ خود اللہ تعالیٰ ہم سے باتیں کیوں نہیں کرتا، یا ہمارے پاس کوئی نشانی کیوں نہیں آتی؟ اسی طرح ایسی ہی بات ان کے اگلوں نے بھی کہی تھی، ان کے اور ان کے دل یکساں ہوگئے ہم نے تو یقین والوں کے لئے نشانیاں بیان کردیں');
INSERT INTO "junagarri" VALUES(2,119,'2:119','ہم نے آپ کو حق کے ساتھ خوشخبری دینے والا اور ڈرانے والا بنا کر بھیجا ہے اور جہنمیوں کے بارے میں آپ سے پرسش نہیں ہوگی۔');
INSERT INTO "junagarri" VALUES(2,120,'2:120','آپ سے یہود ونصاریٰ ہرگز راضی نہیں ہوں گے جب تک کہ آپ ان کے مذہب کے تابع نہ بن جائیں ، آپ کہہ دیجیئے کہ اللہ کی ہدایت ہی ہدایت ہے اور اگر آپ نے باوجود اپنے پاس علم آجانے کے، پھر ان کی خواہشوں کی پیروی کی تو اللہ کے پاس آپ کا نہ تو کوئی ولی ہوگا اورنہ مددگار۔ ');
INSERT INTO "junagarri" VALUES(2,121,'2:121','جنہیں ہم نے کتاب دی ہے اور وه اسے پڑھنے کے حق کے ساتھ پڑھتے ہیں ، وه اس کتاب پر بھی ایمان رکھتے ہیں اور جو اس کے ساتھ کفر کرے وه نقصان والا ہے۔ ');
INSERT INTO "junagarri" VALUES(2,122,'2:122','اے اولاد یعقوب! میں نے جو نعمتیں تم پر انعام کی ہیں انہیں یاد کرو اور میں نے تو تمہیں تمام جہانوں پر فضیلت دے رکھی تھی۔');
INSERT INTO "junagarri" VALUES(2,123,'2:123','اس دن سے ڈرو جس دن کوئی نفس کسی نفس کو کچھ فائده نہ پہنچا سکے گا، نہ کسی شخص سے کوئی فدیہ قبول کیا جائے گا، نہ اسے کوئی شفاعت نفع دے گی، نہ ان کی مدد کی جائے گی۔');
INSERT INTO "junagarri" VALUES(2,124,'2:124','جب ابراہیم ﴿علیہ السلام﴾ کو ان کے رب نے کئی باتوں سے آزمایا اور انہوں نے سب کو پورا کردیا تو اللہ نے فرمایا کہ میں تمہیں لوگوں کا امام بنا دوں گا، عرض کرنے لگے: اور میری اولاد کو ، فرمایا میرا وعده ظالموں سے نہیں۔');
INSERT INTO "junagarri" VALUES(2,125,'2:125','ہم نے بیت اللہ کو لوگوں کے لئے ثواب اور امن وامان کی جگہ بنائی ، تم مقام ابراہیم کو جائے نماز مقرر کرلو ، ہم نے ابراہیم ﴿علیہ السلام﴾ اور اسماعیل ﴿علیہ السلام﴾ سے وعده لیا کہ تم میرے گھر کو طواف کرنے والوں اور اعتکاف کرنے والوں اور رکوع سجده کرنے والوں کے لئے پاک صاف رکھو۔');
INSERT INTO "junagarri" VALUES(2,126,'2:126','جب ابراہیم نے کہا، اے پروردگار! تو اس جگہ کو امن والا شہر بنا اور یہاں کے باشندوں کو جو اللہ تعالیٰ پر اور قیامت کے دن پر ایمان رکھنے والے ہوں، پھلوں کی روزیاں دے اللہ تعالیٰ نے فرمایا: میں کافروں کو بھی تھوڑا فائده دوں گا، پھر انہیں آگ کے عذاب کی طرف بےبس کردوں گا، یہ پہنچنے کی جگہ بری ہے۔');
INSERT INTO "junagarri" VALUES(2,127,'2:127','ابراہیم ﴿علیہ السلام﴾ اور اسماعیل ﴿علیہ السلام﴾ کعبہ کی بنیادیں اور دیواریں اٹھاتے جاتے تھے اور کہتے جارہے تھے کہ ہمارے پروردگار! تو ہم سے قبول فرما، تو ہی سننے والا اور جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(2,128,'2:128','اے ہمارے رب! ہمیں اپنا فرمانبردار بنا لے اور ہماری اولاد میں سے بھی ایک جماعت کو اپنی اطاعت گزار رکھ اور ہمیں اپنی عبادتیں سکھا اور ہماری توبہ قبول فرما، تو توبہ قبول فرمانے والا اور رحم وکرم کرنے والا ہے۔');
INSERT INTO "junagarri" VALUES(2,129,'2:129','اے ہمارے رب! ان میں انہی میں سے رسول بھیج جو ان کے پاس تیری آیتیں پڑھے، انہیں کتاب وحکمت سکھائے اور انہیں پاک کرے ، یقیناً تو غلبہ والا اور حکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(2,130,'2:130','دین ابراہیمی سے وہی بےرغبتی کرے گا جو محض بےوقوف ہو، ہم نے تو اسے دنیا میں بھی برگزیده کیا تھا اور آخرت میں بھی وه نیکوکاروں میں سے ہے۔ ');
INSERT INTO "junagarri" VALUES(2,131,'2:131','جب کبھی بھی انہیں ان کے رب نے کہا، فرمانبردار ہوجا، انہوں نے کہا، میں نے رب العالمین کی فرمانبرداری کی۔ ');
INSERT INTO "junagarri" VALUES(2,132,'2:132','اسی کی وصیت ابراہیم اور یعقوب نے اپنی اولادکو کی، کہ ہمارے بچو! اللہ تعالیٰ نے تمہارے لئے اس دین کو پسند فرمالیا ہے، خبردار! تم مسلمان ہی مرنا۔ ');
INSERT INTO "junagarri" VALUES(2,133,'2:133','کیا (حضرت) یعقوب کے انتقال کے وقت تم موجود تھے؟ جب انہوں نے اپنی اولاد کو کہا کہ میرے بعد تم کس کی عبادت کرو گے؟ تو سب نے جواب دیا کہ آپ کے معبود کی اور آپ کے آباواجداد ابرہیم ﴿علیہ السلام﴾ اور اسماعیل ﴿علیہ السلام﴾ اور اسحاق ﴿علیہ السلام﴾ کے معبود کی جو معبود ایک ہی ہے اور ہم اسی کے فرمانبردار رہیں گے۔');
INSERT INTO "junagarri" VALUES(2,134,'2:134','یہ جماعت تو گزر چکی، جو انہوں نے کیا وه ان کے لئے ہے اور جو تم کرو گے تمہارے لئے ہے۔ ان کے اعمال کے بارے میں تم نہیں پوچھے جاؤ گے۔ ');
INSERT INTO "junagarri" VALUES(2,135,'2:135','یہ کہتے ہیں کہ یہود و نصاریٰ بن جاؤ تو ہدایت پاؤ گے۔ تم کہو بلکہ صحیح راه پر ملت ابراہیمی والے ہیں، اور ابراہیم خالص اللہ کے پرستار تھے اور مشرک نہ تھے۔ ');
INSERT INTO "junagarri" VALUES(2,136,'2:136','اے مسلمانو! تم سب کہو کہ ہم اللہ پر ایمان لائے اور اس چیز پر بھی جو ہماری طرف اتاری گئی اور جو چیز ابراہیم اسماعیل اسحاق یعقوب (علیہم السلام) اور ان کی اولاد پر اتاری گئی اور جو کچھ اللہ کی جانب سے موسیٰ اور عیسیٰ (علیہما السلام) اور دوسرے انبیا (علیہم السلام) دیئے گئے۔ ہم ان میں سے کسی کے درمیان فرق نہیں کرتے، ہم اللہ کے فرمانبردار ہیں۔ ');
INSERT INTO "junagarri" VALUES(2,137,'2:137','اگر وه تم جیسا ایمان لائیں تو ہدایت پائیں، اور اگر منھ موڑیں تو وه صریح اختلاف میں ہیں، اللہ تعالی ان سے عنقریب آپ کی کفایت کرے گا اور وه خوب سننے اور جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(2,138,'2:138','اللہ کا رنگ اختیار کرو اور اللہ تعالیٰ سے اچھا رنگ کس کا ہوگا؟ ہم تو اسی کی عبادت کرنے والے ہیں۔');
INSERT INTO "junagarri" VALUES(2,139,'2:139','آپ کہہ دیجیئے کیا تم ہم سے اللہ کے بارے میں جھگڑتے ہو جو ہمارا اور تمہارا رب ہے، ہمارے لئے ہمارے اعمال ہیں اور تمہارے لئے تمہارے اعمال، ہم تو اسی کے لئے مخلص ہیں۔ ');
INSERT INTO "junagarri" VALUES(2,140,'2:140','کیا تم کہتے ہو کہ ابراہیم اور اسماعیل اور اسحاق اور یعقوب (علیہم السلام) اور ان کی اولاد یہودی یا نصرانی تھے؟ کہہ دو کہ کیا تم زیاده جانتے ہو، یا اللہ تعالیٰ؟ اللہ کے پاس شہادت چھپانے والے سے زیاده ظالم اور کون ہے؟ اور اللہ تمہارے کاموں سے غافل نہیں۔ ');
INSERT INTO "junagarri" VALUES(2,141,'2:141','یہ امت ہے جو گزرچکی، جو انہوں نے کیا ان کے لئے ہے اور جو تم نے کیا تمہارے لئے، تم ان کےاعمال کے بارے میں سوال نہ کئے جاؤ گے۔ ');
INSERT INTO "junagarri" VALUES(2,142,'2:142','عنقریب نادان لوگ کہیں گے کہ جس قبلہ پر یہ تھے اس سے انہیں کس چیز نے ہٹایا؟ آپ کہہ دیجیئے کہ مشرق ومغرب کا مالک اللہ تعالیٰ ہی ہے وه جسے چاہے سیدھی راه کی ہدایت کردے۔');
INSERT INTO "junagarri" VALUES(2,143,'2:143','ہم نے اسی طرح تمہیں عادل امت بنایا ہے تاکہ تم لوگوں پر گواه ہوجاؤ اور رسول (صلی اللہ علیہ وسلم) تم پر گواه ہوجائیں، جس قبلہ پر تم پہلے سے تھے اسے ہم نے صرف اس لئے مقرر کیا تھا کہ ہم جان لیں کہ رسول کا سچا تابعدار کون ہے اور کون ہے جو اپنی ایڑیوں کے بل پلٹ جاتا ہے گو یہ کام مشکل ہے، مگر جنہیں اللہ نے ہدایت دی ہے (ان پر کوئی مشکل نہیں) اللہ تعالیٰ تمہارے ایمان ضائع نہ کرے گا اللہ تعالیٰ لوگوں کے ساتھ شفقت اور مہربانی کرنے والا ہے');
INSERT INTO "junagarri" VALUES(2,144,'2:144','ہم آپ کے چہرے کو بار بار آسمان کی طرف اٹھتے ہوئے دیکھ رہے ہیں، اب ہم آپ کو اس قبلہ کی جانب متوجہ کریں گے جس سے آپ خوش ہوجائیں، آپ اپنا منھ مسجد حرام کی طرف پھیر لیں اور آپ جہاں کہیں ہوں اپنا منھ اسی طرف پھیرا کریں۔ اہل کتاب کو اس بات کے اللہ کی طرف سے برحق ہونے کا قطعی علم ہے اور اللہ تعالیٰ ان اعمال سے غافل نہیں جو یہ کرتے ہیں۔');
INSERT INTO "junagarri" VALUES(2,145,'2:145','اور آپ اگرچہ اہل کتاب کو تمام دلیلیں دے دیں لیکن وه آپ کے قبلے کی پیروی نہیں کریں گے اور نہ آپ ان کے قبلے کو ماننے والے ہیں اور نہ یہ آپس میں ایک دوسرے کے قبلے کو ماننے والے ہیں اور اگر آپ باوجودیکہ آپ کے پاس علم آچکا پھر بھی ان کی خواہشوں کے پیچھے لگ جائیں تو بالیقین آپ بھی ظالموں میں سے ہوجائیں گے۔ ');
INSERT INTO "junagarri" VALUES(2,146,'2:146','جنہیں ہم نے کتاب دی ہے وه تو اسے ایسا پہچانتے ہیں جیسے کوئی اپنے بچوں کو پہچانے، ان کی ایک جماعت حق کو پہچان کر پھر چھپاتی ہے۔ ');
INSERT INTO "junagarri" VALUES(2,147,'2:147','آپ کے رب کی طرف سے یہ سراسر حق ہے، خبردار آپ شک کرنے والوں میں سے نہ ہونا۔ ');
INSERT INTO "junagarri" VALUES(2,148,'2:148','ہر شخص ایک نہ ایک طرف متوجہ ہو رہا ہے تم نیکیوں کی طرف دوڑو۔ جہاں کہیں بھی تم ہوگے، اللہ تمہیں لے آئے گا۔ اللہ تعالیٰ ہر چیز پر قادر ہے۔');
INSERT INTO "junagarri" VALUES(2,149,'2:149','آپ جہاں سے نکلیں اپنا منھ (نماز کے لئے) مسجد حرام کی طرف کرلیا کریں، یہی حق ہے آپ کے رب کی طرف سے، جو کچھ تم کر رہے ہو اس سے اللہ تعالیٰ بےخبر نہیں۔');
INSERT INTO "junagarri" VALUES(2,150,'2:150','اور جس جگہ سے آپ نکلیں اپنا منھ مسجد حرام کی طرف پھیر لیں اور جہاں کہیں تم ہو اپنے چہرے اسی طرف کیا کرو تاکہ لوگوں کی کوئی حجت تم پر باقی نہ ره جائے سوائے ان لوگوں کے جنہوں نے ان میں سے ظلم کیا ہے تم ان سے نہ ڈرو مجھ ہی سے ڈرو اور تاکہ میں اپنی نعمت تم پر پوری کروں اور اس لئے بھی کہ تم راه راست پاؤ۔');
INSERT INTO "junagarri" VALUES(2,151,'2:151','جس طرح ہم نے تم میں تمہی میں سے رسول بھیجا جو ہماری آیتیں تمہارے سامنے تلاوت کرتا ہے اور تمہیں پاک کرتا ہے اور تمہیں کتاب وحکمت اور وه چیزیں سکھاتا ہے جن سے تم بےعلم تھے۔');
INSERT INTO "junagarri" VALUES(2,152,'2:152','اس لئے تم میرا ذکر کرو، میں بھی تمہیں یاد کروں گا، میری شکر گزاری کرو اور ناشکری سے بچو۔ ');
INSERT INTO "junagarri" VALUES(2,153,'2:153','اے ایمان والو! صبر اور نماز کے ذریعہ مدد چاہو، اللہ تعالی صبر والوں کا ساتھ دیتا ہے۔ ');
INSERT INTO "junagarri" VALUES(2,154,'2:154','اور اللہ تعالیٰ کی راه کے شہیدوں کو مرده مت کہو وه زنده ہیں، لیکن تم نہیں سمجھتے۔');
INSERT INTO "junagarri" VALUES(2,155,'2:155','اور ہم کسی نہ کسی طرح تمہاری آزمائش ضرور کریں گے، دشمن کے ڈر سے، بھوک پیاس سے، مال وجان اور پھلوں کی کمی سے اور ان صبر کرنے والوں کو خوشخبری دے دیجیئے۔');
INSERT INTO "junagarri" VALUES(2,156,'2:156','جنہیں، جب کبھی کوئی مصیبت آتی ہے تو کہہ دیا کرتے ہیں کہ ہم تو خود اللہ تعالیٰ کی ملکیت ہیں اور ہم اسی کی طرف لوٹنے والے ہیں۔');
INSERT INTO "junagarri" VALUES(2,157,'2:157','ان پر ان کے رب کی نوازشیں اور رحمتیں ہیں اور یہی لوگ ہدایت یافتہ ہیں۔ ');
INSERT INTO "junagarri" VALUES(2,158,'2:158','صفا اور مروه اللہ تعالیٰ کی نشانیوں میں سے ہیں ، اس لئے بیت اللہ کا حج وعمره کرنے والے پر ان کا طواف کرلینے میں بھی کوئی گناه نہیں اپنی خوشی سے بھلائی کرنے والوں کا اللہ قدردان ہے اور انہیں خوب جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(2,159,'2:159','جو لوگ ہماری اتاری ہوئی دلیلوں اور ہدایت کو چھپاتے ہیں باوجودیکہ ہم اسے اپنی کتاب میں لوگوں کے لئے بیان کرچکے ہیں، ان لوگوں پر اللہ کی اور تمام لعنت کرنے والوں کی لعنت ہے۔ ');
INSERT INTO "junagarri" VALUES(2,160,'2:160','مگر وه لوگ جو توبہ کرلیں اور اصلاح کرلیں اور بیان کردیں، تو میں ان کی توبہ قبول کرلیتا ہوں اور میں توبہ قبول کرنے والا اور رحم وکرم کرنے والا ہوں۔');
INSERT INTO "junagarri" VALUES(2,161,'2:161','یقیناً جو کفار اپنے کفر میں ہی مرجائیں، ان پر اللہ تعالیٰ کی، فرشتوں کی اور تمام لوگوں کی لعنت ہے۔ ');
INSERT INTO "junagarri" VALUES(2,162,'2:162','جس میں یہ ہمیشہ رہیں گے، نہ ان سے عذاب ہلکا کیا جائے گا اور نہ انہیں ڈھیل دی جائے گی۔');
INSERT INTO "junagarri" VALUES(2,163,'2:163','تم سب کا معبود ایک ہی معبود ہے، اس کے سوا کوئی معبود برحق نہیں وه بہت رحم کرنے والا اور بڑا مہربان ہے۔');
INSERT INTO "junagarri" VALUES(2,164,'2:164','آسمانوں اور زمین کی پیدائش، رات دن کا ہیر پھیر، کشتیوں کا لوگوں کو نفع دینے والی چیزوں کو لئے ہوئے سمندروں میں چلنا، آسمان سے پانی اتار کر، مرده زمین کو زنده کردینا ، اس میں ہر قسم کے جانوروں کو پھیلا دینا، ہواؤں کے رخ بدلنا، اور بادل، جو آسمان اور زمین کے درمیان مسخر ہیں، ان میں عقلمندوں کے لئے قدرت الٰہی کی نشانیاں ہیں۔');
INSERT INTO "junagarri" VALUES(2,165,'2:165','بعض لوگ ایسے بھی ہیں جو اللہ کے شریک اوروں کو ٹھہرا کر ان سے ایسی محبت رکھتے ہیں، جیسی محبت اللہ سے ہونی چاہیئے اور ایمان والے اللہ کی محبت میں بہت سخت ہوتے ہیں کاش کہ مشرک لوگ جانتے جب کہ اللہ کے عذاب کو دیکھ کر (جان لیں گے) کہ تمام طاقت اللہ ہی کو ہے اور اللہ تعالیٰ سخت عذاب دینے والا ہے (تو ہرگز شرک نہ کرتے)۔');
INSERT INTO "junagarri" VALUES(2,166,'2:166','جس وقت پیشوا لوگ اپنے تابعداروں سے بیزار ہوجائیں گے اور عذاب کو اپنی آنکھوں سے دیکھ لیں گے اور کل رشتے ناتے ٹوٹ جائیں گے۔');
INSERT INTO "junagarri" VALUES(2,167,'2:167','اور تابعدار لوگ کہنے لگیں گے، کاش ہم دنیا کی طرف دوباره جائیں تو ہم بھی ان سے ایسے ہی بیزار ہوجائیں جیسے یہ ہم سے ہیں، اسی طرح اللہ تعالیٰ انہیں ان کے اعمال دکھائے گا ان کو حسرت دلانے کو، یہ ہرگز جہنم سے نہ نکلیں گے۔ ');
INSERT INTO "junagarri" VALUES(2,168,'2:168','لوگو! زمین میں جتنی بھی حلال اور پاکیزه چیزیں ہیں انہیں کھاؤ پیو اور شیطانی راه پر نہ چلو ، وه تمہارا کھلا ہوا دشمن ہے۔');
INSERT INTO "junagarri" VALUES(2,169,'2:169','وه تمہیں صرف برائی اور بےحیائی کا اور اللہ تعالیٰ پر ان باتوں کے کہنے کا حکم دیتا ہے جن کا تمہیں علم نہیں۔');
INSERT INTO "junagarri" VALUES(2,170,'2:170','اور ان سے جب کبھی کہا جاتا ہے کہ اللہ تعالیٰ کی اتاری ہوئی کتاب کی تابعداری کرو تو جواب دیتے ہیں کہ ہم تو اس طریقے کی پیروی کریں گے جس پر ہم نے اپنے باپ دادوں کو پایا، گو ان کے باپ دادے بےعقل اور گم کرده راه ہوں۔ ');
INSERT INTO "junagarri" VALUES(2,171,'2:171','کفار کی مثال ان جانوروں کی طرح ہے جو اپنے چرواہے کی صرف پکار اور آواز ہی کو سنتے ہیں (سمجھتے نہیں) وه بہرے، گونگے اور ا ندھے ہیں، انہیں عقل نہیں۔ ');
INSERT INTO "junagarri" VALUES(2,172,'2:172','اے ایمان والو! جو پاکیزه چیزیں ہم نے تمہیں دے رکھی ہیں انہیں کھاؤ، پیو اور اللہ تعالیٰ کا شکر کرو، اگر تم خاص اسی کی عبادت کرتے ہو۔ ');
INSERT INTO "junagarri" VALUES(2,173,'2:173','تم پر مرده اور (بہا ہوا) خون اور سور کا گوشت اور ہر وه چیز جس پر اللہ کے سوا دوسروں کا نام پکارا گیا ہو حرام ہے پھر جو مجبور ہوجائے اور وه حد سے بڑھنے والا اور زیادتی کرنے والا نہ ہو، اس پر ان کے کھانے میں کوئی گناه نہیں، اللہ تعالیٰ بخشش کرنے والا مہربان ہے۔');
INSERT INTO "junagarri" VALUES(2,174,'2:174','بے شک جو لوگ اللہ تعالیٰ کی اتاری ہوئی کتاب چھپاتے ہیں اور اسے تھوڑی تھوڑی سی قیمت پر بیچتے ہیں، یقین مانو کہ یہ اپنے پیٹ میں آگ بھر رہے ہیں، قیامت کے دن اللہ تعالیٰ ان سے بات بھی نہ کرے گا، نہ انہیں پاک کرے گا، بلکہ ان کے لئے دردناک عذاب ہے۔');
INSERT INTO "junagarri" VALUES(2,175,'2:175','یہ وه لوگ ہیں جنہوں نے گمراہی کو ہدایت کے بدلے اور عذاب کو مغفرت کے بدلے خرید لیا ہے، یہ لوگ آگ کا عذاب کتنا برداشت کرنے والے ہیں۔');
INSERT INTO "junagarri" VALUES(2,176,'2:176','ان عذابوں کا باعث یہی ہے کہ اللہ تعالیٰ نے سچی کتاب اتاری اور اس کتاب میں اختلاف کرنے والے یقیناً دور کے خلاف میں ہیں۔');
INSERT INTO "junagarri" VALUES(2,177,'2:177','ساری اچھائی مشرق ومغرب کی طرف منھ کرنے میں ہی نہیں بلکہ حقیقتاً اچھا وه شخص ہے جو اللہ تعالی پر، قیامت کے دن پر، فرشتوں پر، کتاب اللہ پر اور نبیوں پر ایمان رکھنے والا ہو، جو مال سے محبت کرنے کے باوجود قرابت داروں، یتیموں، مسکینوں، مسافروں اور سوال کرنے والے کو دے، غلاموں کو آزاد کرے، نماز کی پابندی اور زکوٰة کی ادائیگی کرے، جب وعده کرے تب اسے پورا کرے، تنگدستی، دکھ درد اور لڑائی کے وقت صبر کرے، یہی سچے لوگ ہیں اور یہی پرہیزگار ہیں۔');
INSERT INTO "junagarri" VALUES(2,178,'2:178','اے ایمان والو! تم پر مقتولوں کا قصاص لینا فرض کیا گیا ہے، آزاد آزاد کے بدلے، غلام غلام کے بدلے، عورت عورت کے بدلے ۔ ہاں جس کسی کو اس کے بھائی کی طرف سے کچھ معافی دے دی جائے اسے بھلائی کی اتباع کرنی چاہئے اور آسانی کے ساتھ دیت ادا کرنی چاہئے ۔ تمہارے رب کی طرف سے یہ تخفیف اور رحمت ہے اس کے بعد بھی جو سرکشی کرے اسے درد ناک عذاب ہوگا۔ ');
INSERT INTO "junagarri" VALUES(2,179,'2:179','عقلمندو! قصاص میں تمہارے لئے زندگی ہے اس باعث تم (قتل ناحق سے) رکو گے۔ ');
INSERT INTO "junagarri" VALUES(2,180,'2:180','تم پر فرض کر دیا گیا ہے کہ جب تم میں سے کوئی مرنے لگے اور مال چھوڑ جاتا ہو تو اپنے ماں باپ اور قرابت داروں کے لئے اچھائی کے ساتھ وصیت کرجائے ، پرہیزگاروں پر یہ حق اور ثابت ہے۔');
INSERT INTO "junagarri" VALUES(2,181,'2:181','اب جو شخص اسے سننے کے بعد بدل دے اس کا گناه بدلنے والے پر ہی ہوگا، واقعی اللہ تعالیٰ سننے والا جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(2,182,'2:182','ہاں جو شخص وصیت کرنے والے کی جانب داری یا گناه کی وصیت کردینے سے ڈرے پس وه ان میں آپس میں اصلاح کرادے تو اس پر گناه نہیں، اللہ تعالیٰ بخشنے والا مہربان ہے۔');
INSERT INTO "junagarri" VALUES(2,183,'2:183','اے ایمان والو! تم پر روزے رکھنا فرض کیا گیا جس طرح تم سے پہلے لوگوں پر فرض کئے گئے تھے، تاکہ تم تقویٰ اختیار کرو۔ ');
INSERT INTO "junagarri" VALUES(2,184,'2:184','گنتی کے چند ہی دن ہیں لیکن تم میں سے جو شخص بیمار ہو یا سفر میں ہو تو وه اور دنوں میں گنتی کو پورا کر لے اور اس کی طاقت رکھنے والے فدیہ میں ایک مسکین کو کھانا دیں، پھر جو شخص نیکی میں سبقت کرے وه اسی کے لئے بہتر ہے لیکن تمہارے حق میں بہتر کام روزے رکھنا ہی ہے، اگر تم باعلم ہو۔');
INSERT INTO "junagarri" VALUES(2,185,'2:185','ماه رمضان وه ہے جس میں قرآن اتارا گیا جو لوگوں کو ہدایت کرنے والا ہے اور جس میں ہدایت کی اور حق وباطل کی تمیز کی نشانیاں ہیں، تم میں سے جو شخص اس مہینہ کو پائے اسے روزه رکھنا چاہئے، ہاں جو بیمار ہو یا مسافر ہو اسے دوسرے دنوں میں یہ گنتی پوری کرنی چاہئے، اللہ تعالیٰ کا اراده تمہارے ساتھ آسانی کا ہے، سختی کا نہیں، وه چاہتا ہے کہ تم گنتی پوری کرلو اور اللہ تعالیٰ کی دی ہوئی ہدایت پر اس کی بڑائیاں بیان کرو اور اس کا شکر کرو۔');
INSERT INTO "junagarri" VALUES(2,186,'2:186','جب میرے بندے میرے بارے میں آپ سے سوال کریں تو آپ کہہ دیں کہ میں بہت ہی قریب ہوں ہر پکارنے والے کی پکار کو جب کبھی وه مجھے پکارے، قبول کرتا ہوں اس لئے لوگوں کو بھی چاہئے کہ وه میری بات مان لیا کریں اور مجھ پر ایمان رکھیں، یہی ان کی بھلائی کا باعث ہے');
INSERT INTO "junagarri" VALUES(2,187,'2:187','روزے کی راتوں میں اپنی بیویوں سے ملنا تمہارے لئے حلال کیا گیا، وه تمہارا لباس ہیں اور تم ان کے لباس ہو، تمہاری پوشیده خیانتوں کا اللہ تعالیٰ کو علم ہے، اس نے تمہاری توبہ قبول فرما کر تم سے درگزر فرمالیا، اب تمہیں ان سے مباشرت کی اور اللہ تعالیٰ کی لکھی ہوئی چیز کو تلاش کرنے کی اجازت ہے، تم کھاتے پیتے رہو یہاں تک کہ صبح کا سفید دھاگہ سیاه دھاگے سے ظاہر ہوجائے ۔ پھر رات تک روزے کو پورا کرو اور عورتوں سے اس وقت مباشرت نہ کرو جب کہ تم مسجدوں میں اعتکاف میں ہو ۔ یہ اللہ تعالیٰ کی حدود ہیں، تم ان کے قریب بھی نہ جاؤ۔ اسی طرح اللہ تعالیٰ اپنی آیتیں لوگوں کے لئے بیان فرماتا ہے تاکہ وه بچیں');
INSERT INTO "junagarri" VALUES(2,188,'2:188','اور ایک دوسرے کا مال ناحق نہ کھایا کرو، نہ حاکموں کو رشوت پہنچا کر کسی کا کچھ مال ظلم وستم سے اپنا کر لیا کرو، حالانکہ تم جانتے ہو۔ ');
INSERT INTO "junagarri" VALUES(2,189,'2:189','لوگ آپ سے چاند کے بارے میں سوال کرتے ہیں آپ کہہ دیجیئے کہ یہ لوگوں (کی عبادت) کے وقتوں اور حج کے موسم کے لئے ہے (احرام کی حالت میں) اور گھروں کے پیچھے سے تمہارا آنا کچھ نیکی نہیں، بلکہ نیکی والا وه ہے جو متقی ہو۔ اور گھروں میں تو دروازوں میں سے آیا کرو اور اللہ سے ڈرتے رہو، تاکہ تم کامیاب ہوجاؤ۔');
INSERT INTO "junagarri" VALUES(2,190,'2:190','لڑو اللہ کی راه میں ان سے جو تم سے لڑتے ہیں اور زیادتی نہ کرو ، اللہ تعالیٰ زیادتی کرنے والوں کو پسند نہیں فرماتا۔');
INSERT INTO "junagarri" VALUES(2,191,'2:191','انہیں مارو جہاں بھی پاؤ اور انہیں نکالو جہاں سے انہوں نے تمہیں نکالا ہے اور (سنو) فتنہ قتل سے زیاده سخت ہے اور مسجد حرام کے پاس ان سے لڑائی نہ کرو جب تک کہ یہ خود تم سے نہ لڑیں، اگر یہ تم سے لڑیں تو تم بھی انہیں مارو کافروں کا بدلہ یہی ہے۔');
INSERT INTO "junagarri" VALUES(2,192,'2:192','اگر یہ باز آجائیں تو اللہ تعالیٰ بخشنے والا مہربان ہے۔');
INSERT INTO "junagarri" VALUES(2,193,'2:193','ان سے لڑو جب تک کہ فتنہ نہ مٹ جائے اور اللہ تعالیٰ کا دین غالب نہ آجائے، اگر یہ رک جائیں (تو تم بھی رک جاؤ) زیادتی تو صرف ظالموں پر ہی ہے۔');
INSERT INTO "junagarri" VALUES(2,194,'2:194','حرمت والے مہینے حرمت والے مہینوں کے بدلے ہیں اور حرمتیں ادلے بدلے کی ہیں جو تم پر زیادتی کرے تم بھی اس پر اسی کے مثل زیادتی کرو جو تم پر کی ہے اور اللہ تعالیٰ سے ڈرتے رہا کرو اور جان رکھو کہ اللہ تعالیٰ پرہیزگاروں کے ساتھ ہے۔');
INSERT INTO "junagarri" VALUES(2,195,'2:195','اللہ تعالیٰ کی راه میں خرچ کرو اور اپنے ہاتھوں ہلاکت میں نہ پڑو اور سلوک واحسان کرو، اللہ تعالیٰ احسان کرنے والوں کو دوست رکھتا ہے۔');
INSERT INTO "junagarri" VALUES(2,196,'2:196','حج اور عمرے کو اللہ تعالیٰ کے لئے پورا کرو ، ہاں اگر تم روک لئے جاؤ تو جو قربانی میسر ہو، اسے کرڈالو اور اپنے سر نہ منڈواؤ جب تک کہ قربانی قربان گاه تک نہ پہنچ جائے البتہ تم میں سے جو بیمار ہو، یا اس کے سر میں کوئی تکلیف ہو (جس کی وجہ سے سر منڈالے) تو اس پر فدیہ ہے، خواه روزے رکھ لے، خواه صدقہ دے دے، خواه قربانی کرے پس جب تم امن کی حالت میں ہوجاؤ تو جو شخص عمرے سے لے کر حج تک تمتع کرے، پس اسے جو قربانی میسر ہو اسے کر ڈالے، جسے طاقت ہی نہ ہو وه تین روزے تو حج کے دنوں میں رکھ لے اور سات واپسی میں ، یہ پورے دس ہوگئے۔ یہ حکم ان کے لئے ہے جو مسجد حرام کے رہنے والے نہ ہوں ، لوگو! اللہ سے ڈرتے رہو اور جان لو کہ اللہ تعالیٰ سخت عذاب والا ہے۔');
INSERT INTO "junagarri" VALUES(2,197,'2:197','حج کے مہینے مقرر ہیں اس لئے جو شخص ان میں حج لازم کرلے وه اپنی بیوی سے میل ملاپ کرنے، گناه کرنے اور لڑائی جھگڑے کرنے سے بچتا رہے ، تم جو نیکی کرو گے اس سے اللہ تعالیٰ باخبر ہے اور اپنے ساتھ سفر خرچ لے لیا کرو، سب سے بہتر توشہ اللہ تعالیٰ کا ڈر ہے اور اے عقلمندو! مجھ سے ڈرتے رہا کرو۔');
INSERT INTO "junagarri" VALUES(2,198,'2:198','تم پر اپنے رب کا فضل تلاش کرنے میں کوئی گناه نہیں جب تم عرفات سے لوٹو تو مشعر حرام کے پاس ذکر الٰہی کرو اور اس کا ذکر کرو جیسے کہ اس نے تمہیں ہدایت دی، حالانکہ تم اس سے پہلے راه بھولے ہوئے تھے۔ ');
INSERT INTO "junagarri" VALUES(2,199,'2:199','پھر تم اس جگہ سے لوٹو جس جگہ سے سب لوگ لوٹتے ہیں اور اللہ تعالیٰ سے طلب بخشش کرتے رہو یقیناً اللہ تعالیٰ بخشنے والا مہربان ہے۔');
INSERT INTO "junagarri" VALUES(2,200,'2:200','پھر جب تم ارکان حج ادا کر چکو تو اللہ تعالیٰ کا ذکر کرو جس طرح تم اپنے باپ دادوں کا ذکر کیا کرتے تھے، بلکہ اس سے بھی زیاده بعض لوگ وه بھی ہیں جو کہتے ہیں اے ہمارے رب! ہمیں دنیا میں دے۔ ایسے لوگوں کا آخرت میں کوئی حصہ نہیں۔');
INSERT INTO "junagarri" VALUES(2,201,'2:201','اور بعض لوگ وه بھی ہیں جو کہتے ہیں اے ہمارے رب! ہمیں دنیا میں نیکی دے اور آخرت میں بھی بھلائی عطا فرما اور ہمیں عذاب جہنم سے نجات دے۔');
INSERT INTO "junagarri" VALUES(2,202,'2:202','یہ وه لوگ ہیں جن کے لئے ان کے اعمال کا حصہ ہے اور اللہ تعالیٰ جلد حساب لینے والا ہے۔');
INSERT INTO "junagarri" VALUES(2,203,'2:203','اور اللہ تعالیٰ کی یاد ان گنتی کے چند دنوں (ایام تشریق) میں کرو ، دو دن کی جلدی کرنے والے پر بھی کوئی گناه نہیں، اور جو پیچھے ره جائے اس پر بھی کوئی گناه نہیں ، یہ پرہیزگارکے لئے ہے اور اللہ تعالیٰ سے ڈرتے رہو اور جان رکھو کہ تم سب اسی کی طرف جمع کئے جاؤ گے۔');
INSERT INTO "junagarri" VALUES(2,204,'2:204','بعض لوگوں کی دنیاوی غرض کی باتیں آپ کو خوش کر دیتی ہیں اور وه اپنے دل کی باتوں پر اللہ کو گواه کرتا ہے، حالانکہ دراصل وه زبردست جھگڑالو ہے۔ ');
INSERT INTO "junagarri" VALUES(2,205,'2:205','جب وه لوٹ کر جاتا ہے تو زمین میں فساد پھیلانے کی اور کھیتی اور نسل کی بربادی کی کوشش میں لگا رہتا ہے اور اللہ تعالیٰ فساد کو ناپسند کرتا ہے۔');
INSERT INTO "junagarri" VALUES(2,206,'2:206','اور جب اس سے کہا جائے کہ اللہ سے ڈر تو تکبر اور تعصب اسے گناه پر آماده کر دیتا ہے، ایسے کے لئے بس جہنم ہی ہے اور یقیناً وه بد ترین جگہ ہے۔');
INSERT INTO "junagarri" VALUES(2,207,'2:207','اور بعض لوگ وه بھی ہیں کہ اللہ تعالیٰ کی رضامندی کی طلب میں اپنی جان تک بیچ ڈالتے ہیں اور اللہ تعالیٰ اپنے بندوں پر بڑی مہربانی کرنے والا ہے۔');
INSERT INTO "junagarri" VALUES(2,208,'2:208','ایمان والو! اسلام میں پورے پورے داخل ہوجاؤ اور شیطان کے قدموں کی تابعداری نہ کرو وه تمہارا کھلا دشمن ہے۔');
INSERT INTO "junagarri" VALUES(2,209,'2:209','اگر تم باوجود تمہارے پاس دلیلیں آجانے کے بھی پھسل جاؤ تو جان لو کہ اللہ تعالیٰ غلبہ والا اور حکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(2,210,'2:210','کیا لوگوں کو اس بات کا انتظار ہے کہ ان کے پاس خود اللہ تعالیٰ ابر کے سائبانوں میں آجائے اور فرشتے بھی اور کام انتہا تک پہنچا دیا جائے، اللہ ہی کی طرف تمام کام لوٹائے جاتے ہیں۔');
INSERT INTO "junagarri" VALUES(2,211,'2:211','بنی اسرائیل سے پوچھو تو کہ ہم نے انہیں کس قدر روشن نشانیاں عطا فرمائیں اور جو شخص اللہ تعالیٰ کی نعمتوں کو اپنے پاس پہنچ جانے کے بعد بدل ڈالے (وه جان لے) کہ اللہ تعالیٰ بھی سخت عذابوں والا ہے');
INSERT INTO "junagarri" VALUES(2,212,'2:212','کافروں کے لئے دنیا کی زندگی خوب زینت دار کی گئی ہے، وه ایمان والوں سے ہنسی مذاق کرتے ہیں ، حالانکہ پرہیزگار لوگ قیامت کے دن ان سے اعلیٰ ہوں گے، اللہ تعالیٰ جسے چاہتا ہے بےحساب روزی دیتا ہے۔ ');
INSERT INTO "junagarri" VALUES(2,213,'2:213','دراصل لوگ ایک ہی گروه تھے اللہ تعالیٰ نے نبیوں کو خوشخبریاں دینے اور ڈرانے والا بنا کر بھیجا اور ان کے ساتھ سچی کتابیں نازل فرمائیں، تاکہ لوگوں کے ہر اختلافی امر کا فیصلہ ہوجائے۔ اور صرف ان ہی لوگوں نے جنھیں کتاب دی گئی تھی، اپنے پاس دلائل آچکنے کے بعد آپس کے بغض وعناد کی وجہ سے اس میں اختلاف کیا اس لئے اللہ پاک نے ایمان والوں کی اس اختلاف میں بھی حق کی طرف اپنی مشیئت سے رہبری کی اور اللہ جس کو چاہے سیدھی راه کی طرف رہبری کرتا ہے');
INSERT INTO "junagarri" VALUES(2,214,'2:214','کیا تم یہ گمان کئے بیٹھے ہو کہ جنت میں چلے جاؤ گے، حالانکہ اب تک تم پر وه حالات نہیں آئے جو تم سے اگلے لوگوں پر آئے تھے ۔ انہیں بیماریاں اور مصیبتیں پہنچیں اور وہ یہاں تک جھنجھوڑے گئے کہ رسول اور اس کے ساتھ کے ایمان والے کہنے لگے کہ اللہ کی مدد کب آئے گی؟ سن رکھو کہ اللہ کی مدد قریب ہی ہے۔ ');
INSERT INTO "junagarri" VALUES(2,215,'2:215','آپ سے پوچھتے ہیں کہ وه کیا خرچ کریں؟ آپ کہہ دیجیئے جو مال تم خرچ کرو وه ماں باپ کے لئے ہے اور رشتہداروں اور یتیموں اور مسکینوں اور مسافروں کے لئے ہے اور تم جو کچھ بھلائی کرو گے اللہ تعالیٰ کو اس کا علم ہے۔');
INSERT INTO "junagarri" VALUES(2,216,'2:216','تم پر جہاد فرض کیا گیا گو وه تمہیں دشوار معلوم ہو، ممکن ہے کہ تم کسی چیز کو بری جانو اور دراصل وہی تمہارے لئے بھلی ہو اور یہ بھی ممکن ہے کہ تم کسی چیز کو اچھی سمجھو، حالانکہ وه تمہارے لئے بری ہو، حقیقی علم اللہ ہی کو ہے، تم محض بےخبر ہو۔ ');
INSERT INTO "junagarri" VALUES(2,217,'2:217','لوگ آپ سے حرمت والے مہینوں میں لڑائی کی بابت سوال کرتے ہیں، آپ کہہ دیجیئے کہ ان میں لڑائی کرنا بڑا گناه ہے، لیکن اللہ کی راه سے روکنا، اس کے ساتھ کفر کرنا اور مسجد حرام سے روکنا اور وہاں کے رہنے والوں کو وہاں سے نکالنا، اللہ کے نزدیک اس سے بھی بڑا گناه ہے یہ فتنہ قتل سے بھی بڑا گناه ہے ، یہ لوگ تم سے لڑائی بھڑائی کرتے ہی رہیں گے یہاں تک کہ اگر ان سے ہوسکے تو تمہیں تمہارے دین سے مرتد کردیں اور تم میں سے جو لوگ اپنے دین سے پلٹ جائیں اور اسی کفر کی حالت میں مریں، ان کے اعمال دنیوی اور اخروی سب غارت ہوجائیں گے۔ یہ لوگ جہنمی ہوں گے اور ہمیشہ ہمیشہ جہنم میں ہی رہیں گے۔ ');
INSERT INTO "junagarri" VALUES(2,218,'2:218','البتہ ایمان لانے والے، ہجرت کرنے والے، اللہ کی راه میں جہاد کرنے والے ہی رحمت الٰہی کے امیدوار ہیں، اللہ تعالیٰ بہت بخشنے والا اور بہت مہربانی کرنے والا ہے۔');
INSERT INTO "junagarri" VALUES(2,219,'2:219','لوگ آپ سے شراب اور جوئے کا مسئلہ پوچھتے ہیں، آپ کہہ دیجیئے ان دونوں میں بہت بڑا گناه ہے اور لوگوں کو اس سے دنیاوی فائده بھی ہوتا ہے، لیکن ان کا گناه ان کے نفع سے بہت زیاده ہے ۔ آپ سے یہ بھی دریافت کرتے ہیں کہ کیا کچھ خرچ کریں؟ تو آپ کہہ دیجیئے حاجت سے زائد چیز ، اللہ تعالیٰ اسی طرح اپنے احکام صاف صاف تمہارے لئے بیان فرما رہا ہے، تاکہ تم سوچ سمجھ سکو۔');
INSERT INTO "junagarri" VALUES(2,220,'2:220','دنیا اور آخرت کے امور کو۔ اور تجھ سے یتیموں کے بارے میں بھی سوال کرتے ہیں آپ کہہ دیجیئے کہ ان کی خیرخواہی بہتر ہے، تم اگر ان کا مال اپنے مال میں ملا بھی لو تو وه تمہارے بھائی ہیں، بدنیت اور نیک نیت ہر ایک کو اللہ خوب جانتا ہے اور اگر اللہ چاہتا تو تمہیں مشقت میں ڈال دیتا ، یقیناً اللہ تعالیٰ غلبہ والا اور حکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(2,221,'2:221','اور شرک کرنے والی عورتوں سے تاوقتیکہ وه ایمان نہ لائیں تم نکاح نہ کرو ، ایمان والی لونڈی بھی شرک کرنے والی آزاد عورت سے بہت بہتر ہے، گو تمہیں مشرکہ ہی اچھی لگتی ہو اور نہ شرک کرنے والے مردوں کے نکاح میں اپنی عورتوں کو دو جب تک کہ وه ایمان نہ لائیں، ایمان والا غلام آزاد مشرک سے بہتر ہے، گو مشرک تمہیں اچھا لگے۔ یہ لوگ جہنم کی طرف بلاتے ہیں اور اللہ جنت کی طرف اور اپنی بخشش کی طرف اپنے حکم سے بلاتا ہے، وه اپنی آیتیں لوگوں کے لئے بیان فرما رہا ہے، تاکہ وه نصیحت حاصل کریں۔');
INSERT INTO "junagarri" VALUES(2,222,'2:222','آپ سے حیض کے بارے میں سوال کرتے ہیں، کہہ دیجیئے کہ وه گندگی ہے، حالت حیض میں عورتوں سے الگ رہو اور جب تک وه پاک نہ ہوجائیں ان کے قریب نہ جاؤ، ہاں جب وه پاک ہوجائیں تو ان کے پاس جاؤ جہاں سے اللہ نے تمہیں اجازت دی ہے، اللہ توبہ کرنے والوں کو اور پاک رہنے والوں کو پسند فرماتا ہے۔');
INSERT INTO "junagarri" VALUES(2,223,'2:223','تمہاری بیویاں تمہاری کھیتیاں ہیں، اپنی کھیتیوں میں جس طرح چاہو آؤ اور اپنے لئے (نیک اعمال) آگے بھیجو اور اللہ تعالیٰ سے ڈرتے رہا کرو اور جان رکھو کہ تم اس سے ملنے والے ہو اور ایمان والوں کو خوش خبری سنا دیجیئے۔');
INSERT INTO "junagarri" VALUES(2,224,'2:224','اور اللہ تعالیٰ کو اپنی قسموں کا (اس طرح) نشانہ نہ بناؤ کہ بھلائی اور پرہیزگاری اور لوگوں کے درمیان کی اصلاح کو چھوڑ بیٹھو اور اللہ تعالیٰ سننے والا جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(2,225,'2:225','اللہ تعالیٰ تمہیں تمہاری ان قسموں پر نہ پکڑے گا جو پختہ نہ ہوں ہاں اس کی پکڑ اس چیز پر ہے جو تمہارے دلوں کا فعل ہو، اللہ تعالیٰ بخشنے والا اور بردبار ہے۔');
INSERT INTO "junagarri" VALUES(2,226,'2:226','جو لوگ اپنی بیویوں سے (تعلق نہ رکھنے کی) قسمیں کھائیں، ان کے لئے چار مہینے کی مدت ہے، پھر اگر وه لوٹ آئیں تو اللہ تعالیٰ بھی بخشنے والا مہربان ہے۔');
INSERT INTO "junagarri" VALUES(2,227,'2:227','اور اگر طلاق کا ہی قصد کرلیں تو اللہ تعالیٰ سننے والا، جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(2,228,'2:228','طلاق والی عورتیں اپنے آپ کو تین حیض تک روکے رکھیں ، انہیں حلال نہیں کہ اللہ نے ان کے رحم میں جو پیدا کیا ہو اسے چھپائیں ، اگر انہیں اللہ تعالیٰ پر اور قیامت کے دن پر ایمان ہو، ان کے خاوند اس مدت میں انہیں لوٹا لینے کے پورے حقدار ہیں اگر ان کا اراده اصلاح کا ہو ۔ اور عورتوں کے بھی ویسے ہی حق ہیں جیسے ان پر مردوں کے ہیں اچھائی کے ساتھ۔ ہاں مردوں کو عورتوں پر فضلیت ہے اور اللہ تعالیٰ غالب ہے حکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(2,229,'2:229','یہ طلاقیں دو مرتبہ ہیں ، پھر یا تو اچھائی سے روکنا یا عمدگی کے ساتھ چھوڑ دینا ہے اور تمہیں حلال نہیں کہ تم نے انہیں جو دے دیا ہے اس میں سے کچھ بھی لو، ہاں یہ اور بات ہے کہ دونوں کو اللہ کی حدیں قائم نہ رکھ سکنے کا خوف ہو، اس لئے اگر تمہیں ڈر ہو کہ یہ دونوں اللہ کی حدیں قائم نہ رکھ سکیں گے تو عورت رہائی پانے کے لئے کچھ دے ڈالے، اس میں دونوں پر گناه نہیں یہ اللہ کی حدود ہیں خبردار ان سے آگے نہ بڑھنا اور جو لوگ اللہ کی حدوں سے تجاوز کرجائیں وه ظالم ہیں۔');
INSERT INTO "junagarri" VALUES(2,230,'2:230','پھر اگر اس کو (تیسری بار) طلاق دے دے تو اب اس کے لئے حلال نہیں جب تک وه عورت اس کے سوا دوسرے سے نکاح نہ کرے، پھر اگر وه بھی طلاق دے دے تو ان دونوں کو میل جول کرلینے میں کوئی گناه نہیں بشرطیکہ یہ جان لیں کہ اللہ کی حدوں کو قائم رکھ سکیں گے، یہ اللہ تعالیٰ کی حدود ہیں جنہیں وه جاننے والوں کے لئے بیان فرما رہا ہے۔');
INSERT INTO "junagarri" VALUES(2,231,'2:231','جب تم عورتوں کو طلاق دو اور وه اپنی عدت ختم کرنے پر آئیں تو اب انہیں اچھی طرح بساؤ، یا بھلائی کے ساتھ الگ کردو اور انہیں تکلیف پہنچانے کی غرض سے ظلم وزیادتی کے لئے نہ روکو، جو شخص ایسا کرے اس نے اپنی جان پر ظلم کیا۔ تم اللہ کے احکام کو ہنسی کھیل نہ بناؤ اور اللہ کا احسان جو تم پر ہے یاد کرو اور جو کچھ کتاب وحکمت اس نے نازل فرمائی ہے جس سے تمہیں نصیحت کر رہا ہے، اسے بھی۔ اور اللہ تعالیٰ سے ڈرتے رہا کرو اور جان رکھو کہ اللہ تعالیٰ ہر چیز کو جانتا ہے');
INSERT INTO "junagarri" VALUES(2,232,'2:232','اور جب تم اپنی عورتوں کو طلاق دو اور وه اپنی عدت پوری کرلیں تو انہیں ان کے خاوندوں سے نکاح کرنے سے نہ روکو جب کہ وه آپس میں دستور کے مطابق رضامند ہوں ۔ یہ نصیحت انہیں کی جاتی ہے جنہیں تم میں سے اللہ تعالیٰ پر اور قیامت کے دن پر یقین وایمان ہو، اس میں تمہاری بہترین صفائی اور پاکیزگی ہے۔ اللہ تعالیٰ جانتا ہے اور تم نہیں جانتے۔');
INSERT INTO "junagarri" VALUES(2,233,'2:233','مائیں اپنی اولاد کو دو سال کامل دودھ پلائیں جن کا اراده دودھ پلانے کی مدت بالکل پوری کرنے کا ہو اور جن کے بچے ہیں ان کے ذمہ ان کا روٹی کپڑا ہے جو مطابق دستور کے ہو ۔ ہر شخص اتنی ہی تکلیف دیا جاتا ہے جتنی اس کی طاقت ہو۔ ماں کو اس کے بچہ کی وجہ سے یا باپ کو اس کی اولادکی وجہ سے کوئی ضرر نہ پہنچایا جائے ۔ وارث پر بھی اسی جیسی ذمہ داری ہے، پھر اگر دونوں (یعنی ماں باپ) اپنی رضامندی اور باہمی مشورے سے دودھ چھڑانا چاہیں تو دونوں پر کچھ گناه نہیں اور اگر تمہارا اراده اپنی اولاد کو دودھ پلوانے کا ہو تو بھی تم پر کوئی گناه نہیں جب کہ تم ان کو مطابق دستور کے جو دینا ہو وه ان کے حوالے کردو ، اللہ تعالیٰ سے ڈرتے رہو اور جانتے رہو کہ اللہ تعالیٰ تمہارے اعمال کی دیکھ بھال کر رہا ہے۔');
INSERT INTO "junagarri" VALUES(2,234,'2:234','تم میں سے جو لوگ فوت ہوجائیں اور بیویاں چھوڑ جائیں، وه عورتیں اپنے آپ کو چار مہینے اور دس (دن) عدت میں رکھیں ، پھر جب مدت ختم کرلیں تو جو اچھائی کے ساتھ وه اپنے لئے کریں اس میں تم پر کوئی گناه نہیں اور اللہ تعالیٰ تمہارے ہر عمل سے خبردار ہے۔');
INSERT INTO "junagarri" VALUES(2,235,'2:235','تم پر اس میں کوئی گناه نہیں کہ تم اشارةً کنایتہً ان عورتوں سے نکاح کی بابت کہو، یا اپنے دل میں پوشیده اراده کرو، اللہ تعالیٰ کو علم ہے کہ تم ضرور ان کو یاد کرو گے، لیکن تم ان سے پوشیده وعدے نہ کرلو ہاں یہ اور بات ہے کہ تم بھلی بات بولا کرو اور عقد نکاح جب تک کہ عدت ختم نہ ہوجائے پختہ نہ کرو، جان رکھو کہ اللہ تعالیٰ کو تمہارے دلوں کی باتوں کا بھی علم ہے، تم اس سے خوف کھاتے رہا کرو اور یہ بھی جان رکھو کہ اللہ تعالیٰ بخشش اور علم والا ہے۔');
INSERT INTO "junagarri" VALUES(2,236,'2:236','اگر تم عورتوں کو بغیر ہاتھ لگائے اور بغیر مہر مقرر کئے طلاق دے دو تو بھی تم پر کوئی گناه نہیں، ہاں انہیں کچھ نہ کچھ فائده دو۔ خوشحال اپنے انداز سے اور تنگدست اپنی طاقت کے مطابق دستور کے مطابق اچھا فائده دے۔ بھلائی کرنے والوں پر یہ لازم ہے۔ ');
INSERT INTO "junagarri" VALUES(2,237,'2:237','اور اگر تم عورتوں کو اس سے پہلے طلاق دے دو کہ تم نے انہیں ہاتھ لگایا ہو اور تم نے ان کا مہر بھی مقرر کردیا ہو تو مقرره مہر کا آدھا مہر دے دو، یہ اور بات ہے کہ وه خود معاف کردیں یا وه شخص معاف کردے جس کے ہاتھ میں نکاح کی گره ہے تمہارا معاف کردینا تقویٰ سے بہت نزدیک ہے اور آپس کی فضیلت اور بزرگی کو فراموش نہ کرو، یقیناً اللہ تعالیٰ تمہارے اعمال کو دیکھ رہا ہے۔');
INSERT INTO "junagarri" VALUES(2,238,'2:238','نمازوں کی حفاظت کرو، بالخصوص درمیان والی نماز کی اور اللہ تعالیٰ کے لئے باادب کھڑے رہا کرو۔');
INSERT INTO "junagarri" VALUES(2,239,'2:239','اگر تمہیں خوف ہو تو پیدل ہی سہی یا سوار ہی سہی، ہاں جب امن ہوجائے تو اللہ کا ذکر کرو جس طرح کہ اس نے تمہیں اس بات کی تعلیم دی جسے تم نہیں جانتے تھے۔ ');
INSERT INTO "junagarri" VALUES(2,240,'2:240','جو لوگ تم میں سے فوت ہوجائیں اور بیویاں چھوڑ جائیں وه وصیت کر جائیں کہ ان کی بیویاں سال بھر تک فائده اٹھائیں انہیں کوئی نہ نکالے، ہاں اگر وه خود نکل جائیں تو تم پر اس میں کوئی گناه نہیں جو وه اپنے لئے اچھائی سے کریں، اللہ تعالیٰ غالب اور حکیم ہے۔');
INSERT INTO "junagarri" VALUES(2,241,'2:241','طلاق والیوں کو اچھی طرح فائده دینا پرہیزگاروں پر لازم ہے۔ ');
INSERT INTO "junagarri" VALUES(2,242,'2:242','اللہ تعالیٰ اسی طرح اپنی آیتیں تم پر ظاہر فرما رہا ہے تاکہ تم سمجھو۔');
INSERT INTO "junagarri" VALUES(2,243,'2:243','کیا تم نے انہیں نہیں دیکھا جو ہزاروں کی تعداد میں تھے اور موت کے ڈر کے مارے اپنے گھروں سے نکل کھڑے ہوئے تھے۔ اللہ تعالیٰ نے انہیں فرمایا مرجاؤ، پھر انہیں زنده کردیا بے شک اللہ تعالیٰ لوگوں پر بڑا فضل والا ہے، لیکن اکثر لوگ ناشکرے ہیں۔');
INSERT INTO "junagarri" VALUES(2,244,'2:244','اللہ تعالیٰ کی راه میں جہاد کرو اور جان لو کہ اللہ تعالیٰ سنتا، جانتا ہے۔');
INSERT INTO "junagarri" VALUES(2,245,'2:245','ایسا بھی کوئی ہے جو اللہ تعالیٰ کو اچھا قرض دے پس اللہ تعالیٰ اسے بہت بڑھا چڑھا کر عطا فرمائے، اللہ ہی تنگی اور کشادگی کرتا ہے اور تم سب اسی کی طرف لوٹائے جاؤ گے۔');
INSERT INTO "junagarri" VALUES(2,246,'2:246','کیا آپ نے (حضرت) موسیٰ کے بعد والی بنی اسرائیل کی جماعت کو نہیں دیکھا جب کہ انہوں نے اپنے پیغمبر سے کہا کہ کسی کو ہمارا بادشاه بنا دیجیئے تاکہ ہم اللہ کی راه میں جہاد کریں۔ پیغمبر نے کہا کہ ممکن ہے جہاد فرض ہوجانے کے بعد تم جہاد نہ کرو، انہوں نے کہا بھلا ہم اللہ کی راه میں جہاد کیوں نہ کریں گے؟ ہم تو اپنے گھروں سے اجاڑے گئے ہیں اور بچوں سے دور کردیئے گئے ہیں۔ پھر جب ان پر جہاد فرض ہوا تو سوائے تھوڑے سے لوگوں کے سب پھر گئے اور اللہ تعالیٰ ظالموں کو خوب جانتا ہے۔');
INSERT INTO "junagarri" VALUES(2,247,'2:247','اور انہیں ان کے نبی نے فرمایا کہ اللہ تعالیٰ نے طالوت کو تمہارا بادشاه بنا دیا ہے تو کہنے لگے بھلا اس کی ہم پر حکومت کیسے ہوسکتی ہے؟ اس سے تو بہت زیاده حقدار بادشاہت کے ہم ہیں، اس کو تو مالی کشادگی بھی نہیں دی گئی۔ نبی نے فرمایا سنو، اللہ تعالیٰ نے اسی کو تم پر برگزیده کیا ہے اور اسے علمی اور جسمانی برتری بھی عطا فرمائی ہے بات یہ ہے کہ اللہ جسے چاہے اپنا ملک دے، اللہ تعالیٰ کشادگی والا اور علم والا ہے۔');
INSERT INTO "junagarri" VALUES(2,248,'2:248','ان کے نبی نے انہیں پھر کہا کہ اس کی بادشاہت کی ظاہری نشانی یہ ہے کہ تمہارے پاس وه صندوق آ جائے گا جس میں تمہارے رب کی طرف سے دلجمعی ہے اور آل موسیٰ اور آل ہارون کا بقیہ ترکہ ہے، فرشتے اسے اٹھا کر لائیں گے۔ یقیناً یہ تو تمہارے لئے کھلی دلیل ہے اگر تم ایمان والے ہو۔');
INSERT INTO "junagarri" VALUES(2,249,'2:249','جب (حضرت) طالوت لشکروں کو لے کر نکلے تو کہا سنو اللہ تعالیٰ تمہیں ایک نہر سے آزمانے والاہے، جس نے اس میں سے پانی پی لیا وه میرا نہیں اور جو اسے نہ چکھے وه میرا ہے، ہاں یہ اور بات ہے کہ اپنے ہاتھ سے ایک چلو بھرلے۔ لیکن سوائے چند کے باقی سب نے وه پانی پی لیا (حضرت) طالوت مومنین سمیت جب نہر سے گزر گئے تو وه لوگ کہنے لگے آج تو ہم میں طاقت نہیں کہ جالوت اور اس کے لشکروں سے لڑیں ۔ لیکن اللہ تعالیٰ کی ملاقات پر یقین رکھنے والوں نے کہا، بسا اوقات چھوٹی اور تھوڑی سی جماعتیں بڑی اور بہت سی جماعتوں پر اللہ کے حکم سے غلبہ پالیتی ہیں، اللہ تعالیٰ صبر والوں کے ساتھ ہے۔');
INSERT INTO "junagarri" VALUES(2,250,'2:250','جب ان کا جالوت اور اس کے لشکر سے مقابلہ ہوا تو انہوں نے دعا مانگی کہ اے پروردگار ہمیں صبر دے، ثابت قدمی دے اور قوم کفار پر ہماری مدد فرما۔ ');
INSERT INTO "junagarri" VALUES(2,251,'2:251','چنانچہ اللہ تعالیٰ کے حکم سے انہوں نے جالوتیوں کو شکست دے دی اور (حضرت) داؤد (علیہ السلام) کے ہاتھوں جالوت قتل ہوا اور اللہ تعالیٰ نے داؤد (علیہ السلام) کو مملکت وحکمت اور جتنا کچھ چاہا علم بھی عطا فرمایا۔ اگر اللہ تعالیٰ بعض لوگوں کو بعض سے دفع نہ کرتا تو زمین میں فساد پھیل جاتا، لیکن اللہ تعالیٰ دنیا والوں پر بڑا فضل وکرم کرنے والا ہے۔ ');
INSERT INTO "junagarri" VALUES(2,252,'2:252','یہ اللہ تعالیٰ کی آیتیں ہیں جنہیں ہم حقانیت کے ساتھ آپ پر پڑھتے ہیں، بالیقین آپ رسولوں میں سے ہیں۔ ');
INSERT INTO "junagarri" VALUES(2,253,'2:253','یہ رسول ہیں جن میں سے ہم نے بعض کو بعض پر فضیلت دی ہے ، ان میں سے بعض وه ہیں جن سے اللہ تعالیٰ نے بات چیت کی ہے اور بعض کے درجے بلند کئے ہیں، اور ہم نے عیسیٰ بن مریم کو معجزات عطا فرمائے اور روح القدس سے ان کی تائید کی ۔ اگر اللہ تعالیٰ چاہتا تو ان کے بعد والے اپنے پاس دلیلیں آجانے کے بعد ہرگز آپس میں لڑائی بھڑائی نہ کرتے، لیکن ان لوگوں نے اختلاف کیا، ان میں سے بعض تو مومن ہوئے اور بعض کافر، اور اگر اللہ تعالیٰ چاہتا تو یہ آپس میں نہ لڑتے ، لیکن اللہ تعالیٰ جو چاہتا ہے کرتا ہے۔');
INSERT INTO "junagarri" VALUES(2,254,'2:254','اے ایمان والو! جو ہم نے تمہیں دے رکھا ہے اس میں سے خرچ کرتے رہو اس سے پہلے کہ وه دن آئے جس میں نہ تجارت ہے نہ دوستی اور شفاعت اور کافر ہی ظالم ہیں۔');
INSERT INTO "junagarri" VALUES(2,255,'2:255','اللہ تعالیٰ ہی معبود برحق ہے جس کے سوا کوئی معبود نہیں جو زنده اور سب کا تھامنے والا ہے، جسے نہ اونگھ آئے نہ نیند، اس کی ملکیت میں زمین اور آسمانوں کی تمام چیزیں ہیں۔ کون ہے جو اس کی اجازت کے بغیر اس کے سامنے شفاعت کرسکے، وه جانتا ہے جو ان کے سامنے ہے اور جو ان کے پیچھے ہے اور وه اس کے علم میں سے کسی چیز کا احاطہ نہیں کرسکتے مگر جتنا وه چاہے ، اس کی کرسی کی وسعت نے زمین و آسمان کو گھیر رکھا ہے اور اللہ تعالیٰ ان کی حفاظت سے نہ تھکتا اور نہ اکتاتا ہے، وه تو بہت بلند اور بہت بڑا ہے۔');
INSERT INTO "junagarri" VALUES(2,256,'2:256','دین کے بارے میں کوئی زبردستی نہیں، ہدایت ضلالت سے روشن ہوچکی ہے ، اس لئے جو شخص اللہ تعالیٰ کے سوا دوسرے معبودوں کا انکار کرکے اللہ تعالیٰ پر ایمان لائے اس نے مضبوط کڑے کو تھام لیا، جو کبھی نہ ٹوٹے گا اور اللہ تعالیٰ سننے والا، جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(2,257,'2:257','ایمان لانے والوں کا کارساز اللہ تعالیٰ خود ہے، وه انہیں اندھیروں سے روشنی کی طرف نکال لے جاتا ہے اور کافروں کے اولیاء شیاطین ہیں۔ وه انہیں روشنی سے نکال کر اندھیروں کی طرف لے جاتے ہیں، یہ لوگ جہنمی ہیں جو ہمیشہ اسی میں پڑے رہیں گے۔');
INSERT INTO "junagarri" VALUES(2,258,'2:258','کیا تونے اسے نہیں دیکھا جو سلطنت پا کر ابراہیم (علیہ السلام) سے اس کے رب کے بارے میں جھگڑ رہا تھا، جب ابراہیم (علیہ السلام) نے کہا کہ میرا رب تو وه ہے جو جلاتا ہے اور مارتا ہے، وه کہنے لگا میں بھی جلاتا اور مارتا ہوں، ابراہیم (علیہ السلام) نے کہا اللہ تعالیٰ سورج کو مشرق کی طرف سے لے آتا ہے تو اسے مغرب کی جانب سے لے آ۔ اب تو وه کافر بھونچکا ره گیا، اور اللہ تعالیٰ ظالموں کو ہدایت نہیں دیتا۔');
INSERT INTO "junagarri" VALUES(2,259,'2:259','یا اس شخص کے مانند کہ جس کا گزر اس بستی پر ہوا جو چھت کے بل اوندھی پڑی ہوئی تھی، وه کہنے لگا اس کی موت کے بعد اللہ تعالیٰ اسے کس طرح زنده کرے گا؟ تو اللہ تعالی نے اسے مار دیا سو سال کے لئے، پھر اسے اٹھایا، پوچھا کتنی مدت تجھ پر گزری؟ کہنے لگا ایک دن یا دن کا کچھ حصہ ، فرمایا بلکہ تو سو سال تک رہا، پھر اب تو اپنے کھانے پینے کو دیکھ کہ بالکل خراب نہیں ہوا اور اپنے گدھے کو بھی دیکھ، ہم تجھے لوگوں کے لئے ایک نشانی بناتے ہیں تو دیکھ کہ ہم ہڈیوں کو کس طرح اٹھاتے ہیں، پھر ان پر گوشت چڑھاتے ہیں، جب یہ سب ظاہر ہو چکا تو کہنے لگا میں جانتا ہوں کہ اللہ تعالیٰ ہر چیز پر قادر ہے۔ ');
INSERT INTO "junagarri" VALUES(2,260,'2:260','اور جب ابراہیم (علیہ السلام) نے کہا کہ اے میرے پروردگار! مجھے دکھا تو مُردوں کو کس طرح زنده کرے گا؟ (جناب باری تعالیٰ نے) فرمایا، کیا تمہیں ایمان نہیں؟ جواب دیا ایمان تو ہے لیکن میرے دل کی تسکین ہو جائے گی، فرمایا چار پرند لو، ان کے ٹکڑے کر ڈالو، پھر ہر پہاڑ پر ان کا ایک ایک ٹکڑا رکھ دو پھر انہیں پکارو، تمہارے پاس دوڑتے ہوئے آجائیں گے اور جان رکھو کہ اللہ تعالیٰ غالب ہے حکمتوں والا ہے۔');
INSERT INTO "junagarri" VALUES(2,261,'2:261','جو لوگ اپنا مال اللہ تعالیٰ کی راه میں خرچ کرتے ہیں اس کی مثال اس دانے جیسی ہے جس میں سے سات بالیاں نکلیں اور ہر بالی میں سودانے ہوں، اور اللہ تعالیٰ جسے چاہے بڑھا چڑھا کر دے اور اللہ تعالیٰ کشادگی والا اور علم والا ہے۔');
INSERT INTO "junagarri" VALUES(2,262,'2:262','جو لوگ اپنا مال اللہ تعالیٰ کی راه میں خرچ کرتے ہیں پھر اس کے بعد نہ تو احسان جتاتے ہیں نہ ایذا دیتے ہیں ، ان کا اجر ان کے رب کے پاس ہے ان پر نہ تو کچھ خوف ہے نہ وه اداس ہوں گے۔');
INSERT INTO "junagarri" VALUES(2,263,'2:263','نرم بات کہنا اور معاف کردینا اس صدقہ سے بہتر ہے جس کے بعد ایذا رسانی ہو اور اللہ تعالیٰ بےنیاز اور بردبار ہے۔');
INSERT INTO "junagarri" VALUES(2,264,'2:264','اے ایمان والو! اپنی خیرات کو احسان جتا کر اور ایذا پہنچا کر برباد نہ کرو! جس طرح وه شخص جو اپنا مال لوگوں کے دکھاوے کے لئے خرچ کرے اور نہ اللہ تعالیٰ پر ایمان رکھے نہ قیامت پر، اس کی مثال اس صاف پتھر کی طرح ہے جس پر تھوڑی سی مٹی ہو پھر اس پر زوردار مینہ برسے اور وه اسے بالکل صاف اور سخت چھوڑ دے ، ان ریاکاروں کو اپنی کمائی میں سے کوئی چیز ہاتھ نہیں لگتی اور اللہ تعالیٰ کافروں کی قوم کو (سیدھی) راه نہیں دکھاتا۔');
INSERT INTO "junagarri" VALUES(2,265,'2:265','ان لوگوں کی مثال جو اپنا مال اللہ تعالیٰ کی رضامندی کی طلب میں دل کی خوشی اور یقین کے ساتھ خرچ کرتے ہیں اس باغ جیسی ہے جو اونچی زمین پر ہو اور زوردار بارش اس پر برسے اور وه اپنا پھل دگنا لاوے اور اگر اس پر بارش نہ بھی برسے تو پھوار ہی کافی ہے اور اللہ تمہارے کام دیکھ رہا ہے۔');
INSERT INTO "junagarri" VALUES(2,266,'2:266','کیا تم میں سے کوئی بھی یہ چاہتا ہے کہ اس کا کھجوروں اور انگوروں کا باغ ہو، جس میں نہریں بہہ رہی ہوں اور ہر قسم کے پھل موجود ہوں، اس شخص کا بڑھاپا آگیا ہو، اس کے ننھے ننھے سے بچے بھی ہوں اور اچانک باغ کو بگولا لگ جائے جس میں آگ بھی ہو، پس وه باغ جل جائے ، اسی طرح اللہ تعالیٰ تمہارے لئے آیتیں بیان کرتا ہے تاکہ تم غوروفکر کرو۔');
INSERT INTO "junagarri" VALUES(2,267,'2:267','اے ایمان والو! اپنی پاکیزه کمائی میں سے اور زمین میں سے تمہارے لئے ہماری نکالی ہوئی چیزوں میں سے خرچ کرو ، ان میں سے بری چیزوں کے خرچ کرنے کا قصد نہ کرنا، جسے تم خود لینے والے نہیں ہو، ہاں اگر آنکھیں بند کر لو تو ، اور جان لو کہ اللہ تعالیٰ بے پرواه اور خوبیوں والا ہے');
INSERT INTO "junagarri" VALUES(2,268,'2:268','شیطان تمہیں فقیری سے دھمکاتا ہے اور بےحیائی کا حکم دیتا ہے ، اور اللہ تعالیٰ تم سے اپنی بخشش اور فضل کا وعده کرتا ہے، اللہ تعالیٰ وسعت والا اور علم والا ہے۔');
INSERT INTO "junagarri" VALUES(2,269,'2:269','وه جسے چاہے حکمت اور دانائی دیتا ہے اور جو شخص حکمت اور سمجھ دیا جائے وه بہت ساری بھلائی دیا گیا اور نصیحت صرف عقلمند ہی حاصل کرتے ہیں۔');
INSERT INTO "junagarri" VALUES(2,270,'2:270','تم جتنا کچھ خرچ کرو یعنی خیرات اور جو کچھ نذر مانو اسے اللہ تعالیٰ بخوبی جانتا ہے، اور ظالموں کا کوئی مددگار نہیں۔');
INSERT INTO "junagarri" VALUES(2,271,'2:271','اگر تم صدقے خیرات کو ظاہر کرو تو وه بھی اچھا ہے اور اگر تم اسے پوشیده پوشیده مسکینوں کو دے دو تو یہ تمہارے حق میں بہتر ہے ، اللہ تعالیٰ تمہارے گناہوں کو مٹا دے گا اور اللہ تعالیٰ تمہارے تمام اعمال کی خبر رکھنے والا ہے،');
INSERT INTO "junagarri" VALUES(2,272,'2:272','انہیں ہدایت پر لا کھڑا کرنا تیرے ذمہ نہیں بلکہ ہدایت اللہ تعالیٰ دیتا ہے جسے چاہتا ہے اور تم جو بھلی چیز اللہ کی راه میں دو گے اس کا فائده خود پاؤ گے۔ تمہیں صرف اللہ تعالیٰ کی رضامندی کی طلب کے لئے ہی خرچ کرنا چاہئے تم جو کچھ مال خرچ کرو گے اس کا پورا پورا بدلہ تمہیں دیا جائے گا ، اور تمہارا حق نہ مارا جائے گا۔');
INSERT INTO "junagarri" VALUES(2,273,'2:273','صدقات کے مستحق صرف وه غربا ہیں جو اللہ کی راه میں روک دیئے گئے، جو ملک میں چل پھر نہیں سکتے نادان لوگ ان کی بے سوالی کی وجہ سے انہیں مالدار خیال کرتے ہیں، آپ ان کے چہرے دیکھ کر قیافہ سے انہیں پہچان لیں گے وه لوگوں سے چمٹ کر سوال نہیں کرتے ، تم جو کچھ مال خرچ کرو تو اللہ تعالیٰ اس کا جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(2,274,'2:274','جو لوگ اپنے مالوں کو رات دن چھپے کھلے خرچ کرتے ہیں ان کے لئے ان کے رب تعالیٰ کے پاس اجر ہے اور نہ انہیں خوف ہے اور نہ غمگینی۔');
INSERT INTO "junagarri" VALUES(2,275,'2:275','سود خور لوگ نہ کھڑے ہوں گے مگر اسی طرح جس طرح وہ کھڑا ہوتا ہے جسے شیطان چھو کر خبطی بنا دے یہ اس لئے کہ یہ کہا کرتے تھے کہ تجارت بھی تو سود ہی کی طرح ہے ، حالانکہ اللہ تعالیٰ نے تجارت کو حلال کیا اور سود کو حرام، جو شخص اپنے پاس آئی ہوئی اللہ تعالیٰ کی نصیحت سن کر رک گیا اس کے لئے وہ ہے جو گزرا اور اس کا معاملہ اللہ تعالیٰ کی طرف ہے ، اور جو پھر دوبارہ ﴿حرام کی طرف﴾ لوٹا، وہ جہنمی ہے، ایسے لوگ ہمیشہ ہی اس میں رہیں گے۔');
INSERT INTO "junagarri" VALUES(2,276,'2:276','اللہ تعالیٰ سود کو مٹاتا ہے اور صدقہ کو بڑھاتا ہے اور اللہ تعالیٰ کسی ناشکرے اور گنہگار سے محبت نہیں کرتا۔');
INSERT INTO "junagarri" VALUES(2,277,'2:277','بے شک جو لوگ ایمان کے ساتھ ﴿سنت کے مطابق﴾ نیک کام کرتے ہیں، نمازوں کو قائم کرتے ہیں اور زکوٰة ادا کرتے ہیں، ان کا اجر ان کے رب تعالیٰ کے پاس ہے، ان پر نہ تو کوئی خوف ہے، نہ اداسی اور غم۔');
INSERT INTO "junagarri" VALUES(2,278,'2:278','اے ایمان والو! اللہ تعالیٰ سے ڈرو اور جو سود باقی رہ گیا ہے وہ چھوڑ دو، اگر تم سچ مچ ایمان والے ہو۔');
INSERT INTO "junagarri" VALUES(2,279,'2:279','اور اگر ایسا نہیں کرتے تو اللہ تعالیٰ سے اور اس کے رسول سے لڑنے کے لئے تیار ہو جاؤ ، ہاں اگر توبہ کرلو تو تمہارا اصل مال تمہارا ہی ہے، نہ تم ظلم کرو نہ تم پر ظلم کیا جائے گا ۔');
INSERT INTO "junagarri" VALUES(2,280,'2:280','اور اگر کوئی تنگی والا ہو تو اسے آسانی تک مہلت دینی چاہئے اور صدقہ کرو تو تمہارے لئے بہت ہی بہتر ہے ، اگر تم میں علم ہو۔');
INSERT INTO "junagarri" VALUES(2,281,'2:281','اور اس دن سے ڈرو جس میں تم سب اللہ تعالیٰ کی طرف لوٹائے جاؤگے اور ہر شخص کو اس کے اعمال کا پورا پورا بدلہ دیا جائے گا اور ان پر ظلم نہیں کیا جائے گا۔ ');
INSERT INTO "junagarri" VALUES(2,282,'2:282','اے ایمان والو! جب تم آپس میں ایک دوسرے سے میعاد مقرر پر قرض کا معاملہ کرو تو اسے لکھ لیا کرو ، اور لکھنے والے کو چاہئے کہ تمہارا آپس کا معاملہ عدل سے لکھے، کاتب کو چاہئے کہ لکھنے سے انکار نہ کرے جیسے اللہ تعالیٰ نے اسے سکھایا ہے، پس اسے بھی لکھ دینا چاہئے اور جس کے ذمہ حق ہو وہ لکھوائے اور اپنے اللہ تعالیٰ سے ڈرے جو اس کا رب ہے اور حق میں سے کچھ گھٹائے نہیں، ہاں جس شخص کے ذمہ حق ہے وہ اگر نادان ہو یا کمزور ہو یا لکھوانے کی طاقت نہ رکھتا ہو تو اس کا ولی عدل کے ساتھ لکھوا دے اور اپنے میں سے دو مرد گواہ رکھ لو، اگر دو مرد نہ ہوں تو ایک مرد اور دو عورتیں جنہیں تم گوہوں میں سے پسند کر لو تاکہ ایک کی بھول چوک کو دوسری یاد دلا دے اور گواہوں کو چاہئے کہ وہ جب بلائے جائیں تو انکار نہ کریں اور قرض کو جس کی مدت مقرر ہے خواہ چھوٹا ہو یا بڑا ہو لکھنے میں کاہلی نہ کرو، اللہ تعالیٰ کے نزدیک یہ بات بہت انصاف والی ہے اور گواہی کو بھی درست رکھنے والی اور شق و شبہ سے بھی زیادہ بچانے والی ہے ، ہاں یہ اور بات ہے کہ وہ معاملہ نقد تجارت کی شکل میں ہو جو آپس میں تم لین دین کر رہے ہو تو تم پر اس کے نہ لکھنے میں کوئی گناہ نہیں۔ خرید و فروخت کے وقت بھی گواہ مقرر کر لیا کرو اور ﴿یاد رکھو کہ﴾ نہ تو لکھنے والے کو نقصان پہنچایا جائے نہ گواہ کو اور اگر تم یہ کرو تو یہ تمہاری کھلی نافرمانی ہے، اللہ تعالیٰ سے ڈرو ، اللہ تمہیں تعلیم دے رہا ہے اور اللہ تعالیٰ ہر چیز کو خوب جاننے والا ہے');
INSERT INTO "junagarri" VALUES(2,283,'2:283','اور اگر تم سفر میں ہو اور لکھنے والا نہ پاؤ تو رہن قبضہ میں رکھ لیا کرو ، ہاں اگر آپس میں ایک دوسرے سے مطمئن ہو تو جسے امانت دی گئی ہے وه اسے ادا کردے اور اللہ تعالیٰ سے ڈرتا رہے جو اس کا رب ہے ۔ اور گواہی کو نہ چھپاؤ اور جو اسے چھپالے وه گنہگار دل والا ہے اور جو کچھ تم کرتے ہو اسے اللہ تعالیٰ خوب جانتا ہے۔');
INSERT INTO "junagarri" VALUES(2,284,'2:284','آسمانوں اور زمین کی ہر چیز اللہ تعالیٰ ہی کی ملکیت ہے۔ تمہارے دلوں میں جو کچھ ہے اسے تم ظاہر کرو یا چھپاؤ، اللہ تعالیٰ اس کا حساب تم سے لے گا ۔ پھر جسے چاہے بخشے اور جسے چاہے سزا دے اور اللہ تعالیٰ ہر چیز پر قادر ہے۔');
INSERT INTO "junagarri" VALUES(2,285,'2:285','رسول ایمان لایا اس چیز پر جو اس کی طرف اللہ تعالیٰ کی جانب سے اتری اور مومن بھی ایمان لائے، یہ سب اللہ تعالیٰ اور اس کے فرشتوں پر اور اس کی کتابوں پر اور اس کے رسولوں پر ایمان لائے، اس کے رسولوں میں سے کسی میں ہم تفریق نہیں کرتے، انہوں نے کہہ دیا کہ ہم نے سنا اور اطاعت کی، ہم تیری بخشش طلب کرتے ہیں اے ہمارے رب! اور ہمیں تیری ہی طرف لوٹنا ہے، ');
INSERT INTO "junagarri" VALUES(2,286,'2:286','اللہ تعالیٰ کسی جان کو اس کی طاقت سے زیاده تکلیف نہیں دیتا، جو نیکی وه کرے وه اس کے لئے اور جو برائی وه کرے وه اس پر ہے، اے ہمارے رب! اگر ہم بھول گئے ہوں یا خطا کی ہو تو ہمیں نہ پکڑنا، اے ہمارے رب! ہم پر وه بوجھ نہ ڈال جو ہم سے پہلے لوگوں پر ڈالا تھا، اے ہمارے رب! ہم پر وه بوجھ نہ ڈال جس کی ہمیں طاقت نہ ہو اور ہم سے درگزر فرما! اور ہمیں بخش دے اور ہم پر رحم کر! تو ہی ہمارا مالک ہے، ہمیں کافروں کی قوم پر غلبہ عطا فرما۔');
INSERT INTO "junagarri" VALUES(3,1,'3:1','الم');
INSERT INTO "junagarri" VALUES(3,2,'3:2','اللہ تعالیٰ وه ہے جس کے سوا کوئی معبود نہیں، جو زنده اور سب کا نگہبان ہے۔ ');
INSERT INTO "junagarri" VALUES(3,3,'3:3','جس نے آپ پر حق کے ساتھ اس کتاب کو نازل فرمایا ہے ، جو اپنے سے پہلے کی تصدیق کرنے والی ہے، اسی نے اس سے پہلے تورات اور انجیل کو اتارا تھا۔');
INSERT INTO "junagarri" VALUES(3,4,'3:4','اس سے پہلے، لوگوں کو ہدایت کرنے والی بنا کر ، اور قرآن بھی اسی نے اتارا ، جو لوگ اللہ تعالیٰ کی آیتوں سے کفر کرتے ہیں ان کے لئے سخت عذاب ہے اور اللہ تعالیٰ غالب ہے، بدلہ لینے والا ہے۔');
INSERT INTO "junagarri" VALUES(3,5,'3:5','یقیناً اللہ تعالیٰ پر زمین وآسمان کی کوئی چیز پوشیده نہیں۔');
INSERT INTO "junagarri" VALUES(3,6,'3:6','وه ماں کے پیٹ میں تمہاری صورتیں جس طرح چاہتا ہے بناتا ہے ۔ اس کے سواکوئی معبود برحق نہیں وه غالب ہے، حکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(3,7,'3:7','وہی اللہ تعالیٰ ہے جس نے تجھ پر کتاب اتاری جس میں واضح مضبوط آیتیں ہیں جو اصل کتاب ہیں اوربعض متشابہ آیتیں ہیں ۔ پس جن کے دلوں میں کجی ہے وه تواس کی متشابہ آیتوں کے پیچھے لگ جاتے ہیں، فتنے کی طلب اور ان کی مراد کی جستجو کے لئے، حالانکہ ان کے حقیقی مراد کو سوائے اللہ تعالیٰ کے کوئی نہیں جانتا اور پختہ ومضبوط علم والے یہی کہتے ہیں کہ ہم تو ان پر ایمان لاچکے، یہ ہمارے رب کی طرف سے ہیں اور نصیحت تو صرف عقلمند حاصل کرتے ہیں۔');
INSERT INTO "junagarri" VALUES(3,8,'3:8','اے ہمارے رب! ہمیں ہدایت دینے کے بعد ہمارے دل ٹیڑھے نہ کردے اور ہمیں اپنے پاس سے رحمت عطا فرما، یقیناً تو ہی بہت بڑی عطا دینے والا ہے۔');
INSERT INTO "junagarri" VALUES(3,9,'3:9','اے ہمارے رب! تو یقیناً لوگوں کوایک دن جمع کرنے والا ہے جس کے آنے میں کوئی شک نہیں، یقیناً اللہ تعالیٰ وعده خلافی نہیں کرتا۔');
INSERT INTO "junagarri" VALUES(3,10,'3:10','کافروں کوان کے مال اور ان کی اولاد اللہ تعالیٰ (کے عذاب) سے چھڑانے میں کچھ کام نہ آئیں گی، یہ تو جہنم کا ایندھن ہی ہیں۔');
INSERT INTO "junagarri" VALUES(3,11,'3:11','جیسا آل فرعون کاحال ہوا، اور ان کا جو ان سے پہلے تھے، انہوں نے ہماری آیتوں کوجھٹلایا، پھر اللہ تعالیٰ نے بھی انہیں ان کے گناہوں پر پکڑ لیا، اور اللہ تعالیٰ سخت عذاب والا ہے۔');
INSERT INTO "junagarri" VALUES(3,12,'3:12','کافروں سے کہہ دیجئے! کہ تم عنقریب مغلوب کئے جاؤ گے اور جہنم کی طرف جمع کئے جاؤ گے اور وه برا ٹھکانا ہے۔');
INSERT INTO "junagarri" VALUES(3,13,'3:13','یقیناً تمہارے لئے عبرت کی نشانی تھی ان دو جماعتوں میں جو گتھ گئی تھیں، ایک جماعت تو اللہ تعالیٰ کی راه میں لڑ رہی تھی اور دوسرا گروه کافروں کا تھا وه انہیں اپنی آنکھوں سے اپنے سے دگنا دیکھتے تھے اور اللہ تعالیٰ جسے چاہے اپنی مدد سے قوی کرتا ہے۔ یقیناً اس میں آنکھوں والوں کے لئے بڑی عبرت ہے۔');
INSERT INTO "junagarri" VALUES(3,14,'3:14','مرغوب چیزوں کی محبت لوگوں کے لئے مزین کر دی گئی ہے، جیسے عورتیں اور بیٹے اور سونے اور چاندی کے جمع کئے ہوئے خزانے اور نشاندار گھوڑے اور چوپائے اور کھیتی ، یہ دنیا کی زندگی کا سامان ہے اور لوٹنے کا اچھا ٹھکانا تو اللہ تعالیٰ ہی کے پاس ہے۔');
INSERT INTO "junagarri" VALUES(3,15,'3:15','آپ کہہ دیجئے! کیا میں تمہیں اس سے بہت ہی بہتر چیز بتاؤں؟ تقویٰ والوں کے لئے ان کے رب تعالیٰ کے پاس جنتیں ہیں جن کے نیچے نہریں بہہ رہی ہیں جن میں وه ہمیشہ رہیں گے اور پاکیزه بیویاں اور اللہ تعالیٰ کی رضامندی ہے، سب بندے اللہ تعالیٰ کی نگاه میں ہیں۔');
INSERT INTO "junagarri" VALUES(3,16,'3:16','جو کہتے ہیں کہ اے ہمارے رب! ہم ایمان لا چکے اس لئے ہمارے گناه معاف فرما اور ہمیں آگ کے عذاب سے بچا۔');
INSERT INTO "junagarri" VALUES(3,17,'3:17','جو صبر کرنے والے اور سچ بولنے والے اور فرمانبرداری کرنے والے اور اللہ کی راه میں خرچ کرنے والے اور پچھلی رات کو بخشش مانگنے والے ہیں۔');
INSERT INTO "junagarri" VALUES(3,18,'3:18','اللہ تعالیٰ، فرشتے اور اہل علم اس بات کی گواہی دیتے ہیں کہ اللہ کے سوا کوئی معبود نہیں اور وه عدل کو قائم رکھنے والا ہے، اس غالب اور حکمت والے کے سوا کوئی عبادت کے لائق نہیں۔');
INSERT INTO "junagarri" VALUES(3,19,'3:19','بے شک اللہ تعالیٰ کے نزدیک دین اسلام ہی ہے ، اور اہل کتاب نے اپنے پاس علم آجانے کے بعد آپس کی سرکشی اور حسد کی بنا پر ہی اختلاف کیا ہے اور اللہ تعالیٰ کی آیتوں کے ساتھ جو بھی کفر کرے اللہ تعالیٰ اس کا جلد حساب لینے والا ہے۔');
INSERT INTO "junagarri" VALUES(3,20,'3:20','پھر بھی اگر یہ آپ سے جھگڑیں توآپ کہہ دیں کہ میں اور میرے تابعداروں نے اللہ تعالیٰ کے سامنے اپنا سر تسلیم خم کر دیا ہے اور اہل کتاب سے اور انپڑھ لوگوں سے کہہ دیجیئے! کہ کیا تم بھی اطاعت کرتے ہو؟ پس اگر یہ بھی تابعدار بن جائیں تو یقیناً ہداہت والے ہیں اور اگر یہ روگردانی کریں، تو آپ پر صرف پہنچا دینا ہے اور اللہ بندوں کو خوب دیکھ بھال رہا ہے۔');
INSERT INTO "junagarri" VALUES(3,21,'3:21','جو لوگ اللہ تعالیٰ کی آیتوں سے کفر کرتے ہیں اورناحق نبیوں کو قتل کر ڈالتے ہیں اور جو لوگ عدل وانصاف کی بات کہیں انہیں بھی قتل کر ڈالتے ہیں ، تو اے نبی! انہیں دردناک عذاب کی خبردے دیجئے۔');
INSERT INTO "junagarri" VALUES(3,22,'3:22','ان کے اعمال دنیا وآخرت میں غارت ہیں اور ان کا کوئی مددگار نہیں۔');
INSERT INTO "junagarri" VALUES(3,23,'3:23','کیا آپ نے انہیں نہیں دیکھا جنہیں ایک حصہ کتاب کا دیا گیا ہے وه اپنے آپس کے فیصلوں کے لئے اللہ تعالیٰ کی کتاب کی طرف بلائے جاتے ہیں، پھر بھی ایک جماعت ان کی منھ پھیر کر لوٹ جاتی ہے۔ ');
INSERT INTO "junagarri" VALUES(3,24,'3:24','اس کی وجہ ان کا یہ کہنا کہ ہمیں تو گنے چنے چند دن ہی آگ جلائے گی، ان کی گڑھی گڑھائی باتوں نے انہیں ان کے دین کے بارے میں دھوکے میں ڈال رکھا ہے۔ ');
INSERT INTO "junagarri" VALUES(3,25,'3:25','پس کیا حال ہوگا جبکہ ہم انہیں اس دن جمع کرینگے؟ جس کے آنے میں کوئی شک نہیں اور ہر شخص اپنا اپنا کیا پورا پورا دیاجائے گا اور ان پر ظلم نہ کیا جائے گا۔ ');
INSERT INTO "junagarri" VALUES(3,26,'3:26','آپ کہہ دیجئے اے اللہ! اے تمام جہان کے مالک! تو جسے چاہے بادشاہی دے اور جس سے چاہے سلطنت چھین لے اور تو جسے چاہے عزت دے اورجسے چاہے ذلت دے، تیرے ہی ہاتھ میں سب بھلائیاں ہیں ، بے شک تو ہر چیز پر قادر ہے۔');
INSERT INTO "junagarri" VALUES(3,27,'3:27','تو ہی رات کو دن میں داخل کرتا ہے اور دن کو رات میں لے جاتا ہے ، تو ہی بے جان سے جاندار پیدا کرتا ہے اور تو ہی جاندار سے بے جان پیدا کرتا ہے ، تو ہی ہے کہ جسے چاہتا ہے بے شمار روزی دیتا ہے۔');
INSERT INTO "junagarri" VALUES(3,28,'3:28','مومنوں کو چاہئے کہ ایمان والوں کو چھوڑ کر کافروں کو اپنا دوست نہ بنائیں اور جوایسا کرے گا وه اللہ تعالیٰ کی کسی حمایت میں نہیں مگر یہ کہ ان کے شر سے کسی طرح بچاؤ مقصود ہو ، اور اللہ تعالیٰ خود تمہیں اپنی ذات سے ڈرا رہا ہے اور اللہ تعالیٰ ہی کی طرف لوٹ جانا ہے۔');
INSERT INTO "junagarri" VALUES(3,29,'3:29','کہہ دیجئے! کہ خواه تم اپنے سینوں کی باتیں چھپاؤ خواه ظاہر کرو اللہ تعالیٰ (بہرحال) جانتا ہے، آسمانوں اور زمین میں جو کچھ ہے سب اسے معلوم ہے اور اللہ تعالیٰ ہر چیز پر قادر ہے۔');
INSERT INTO "junagarri" VALUES(3,30,'3:30','جس دن ہر نفس (شخص) اپنی کی ہوئی نیکیوں کو اور اپنی کی ہوئی برائیوں کو موجود پالے گا، آرزو کرے گا کہ کاش! اس کے اور برائیوں کے درمیان بہت ہی دوری ہوتی۔ اللہ تعالیٰ تمہیں اپنی ذات سے ڈرا رہا ہے اور اللہ تعالیٰ اپنے بندوں پر بڑا ہی مہربان ہے۔');
INSERT INTO "junagarri" VALUES(3,31,'3:31','کہہ دیجئے! اگر تم اللہ تعالیٰ سے محبت رکھتے ہو تو میری تابعداری کرو ، خود اللہ تعالیٰ تم سے محبت کرے گا اور تمہارے گناه معاف فرما دے گا اور اللہ تعالیٰ بڑا بخشنے والا مہربان ہے۔');
INSERT INTO "junagarri" VALUES(3,32,'3:32','کہہ دیجئے! کہ اللہ تعالیٰ اور رسول کی اطاعت کرو، اگر یہ منھ پھیر لیں تو بے شک اللہ تعالیٰ کافروں سے محبت نہیں کرتا۔ ');
INSERT INTO "junagarri" VALUES(3,33,'3:33','بے شک اللہ تعالیٰ نے تمام جہان کے لوگوں میں سے آدم (علیہ السلام) کو اور نوح (علیہ السلام) کو، ابراہیم (علیہ السلام) کے خاندان اور عمران کے خاندان کو منتخب فرما لیا۔ ');
INSERT INTO "junagarri" VALUES(3,34,'3:34','کہ یہ سب آپس میں ایک دوسرے کی نسل سے ہیں اور اللہ تعالیٰ سنتا جانتا ہے۔');
INSERT INTO "junagarri" VALUES(3,35,'3:35','جب عمران کی بیوی نے کہا کہ اے میرے رب! میرے پیٹ میں جو کچھ ہے، اسے میں نے تیرے نام آزاد کرنے کی نذر مانی، تو میری طرف سے قبول فرما! یقیناً تو خوب سننے والا اور پوری طرح جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(3,36,'3:36','جب بچی کو جنا تو کہنے لگیں کہ پروردگار! مجھے تو لڑکی ہوئی، اللہ تعالیٰ کو خوب معلوم ہے کہ کیا اولاد ہوئی ہے اور لڑکا لڑکی جیسا نہیں میں نے اس کا نام مریم رکھا ، میں اسے اور اس کی اولاد کو شیطان مردود سے تیری پناه میں دیتی ہوں۔ ');
INSERT INTO "junagarri" VALUES(3,37,'3:37','پس اسے اس کے پروردگار نے اچھی طرح قبول فرمایا اور اسے بہترین پرورش دی۔ اس کی خیر خبر لینے والا زکریا (علیہ السلام) کو بنایا ، جب کبھی زکریا (علیہ السلام) ان کے حجرے میں جاتے ان کے پاس روزی رکھی ہوئی پاتے ، وه پوچھتے اے مریم! یہ روزی تمہارے پاس کہاں سے آئی؟ وه جواب دیتیں کہ یہ اللہ تعالیٰ کے پاس سے ہے، بے شک اللہ تعالیٰ جسے چاہے بے شمار روزی دے۔');
INSERT INTO "junagarri" VALUES(3,38,'3:38','اسی جگہ زکریا (علیہ السلام) نے اپنے رب سے دعا کی، کہا کہ اے میرے پروردگار! مجھے اپنے پاس سے پاکیزه اولاد عطا فرما، بے شک تو دعا کا سننے والا ہے۔');
INSERT INTO "junagarri" VALUES(3,39,'3:39','پس فرشتوں نے انہیں آواز دی، جب کہ وه حجرے میں کھڑے نماز پڑھ رہے تھے، کہ اللہ تعالیٰ تجھے یحيٰ کی یقینی خوشخبری دیتا ہے جو اللہ تعالیٰ کے کلمہ کی تصدیق کرنے والا ، سردار، ضابط نفس اور نبی ہے نیک لوگوں میں سے۔');
INSERT INTO "junagarri" VALUES(3,40,'3:40','کہنے لگے اے میرے رب! میرے ہاں بچہ کیسے ہوگا؟ میں بالکل بوڑھا ہو گیا ہوں اور میری بیوی بانجھ ہے، فرمایا، اسی طرح اللہ تعالیٰ جو چاہے کرتا ہے۔');
INSERT INTO "junagarri" VALUES(3,41,'3:41','کہنے لگے پروردگار! میرے لئے اس کی کوئی نشانی مقرر کر دے، فرمایا، نشانی یہ ہے کہ تین دن تک تو لوگوں سے بات نہ کر سکے گا، صرف اشارے سے سمجھائے گا، تو اپنے رب کا ذکر کثرت سے کر اور صبح وشام اسی کی تسبیح بیان کرتا ره!');
INSERT INTO "junagarri" VALUES(3,42,'3:42','اور جب فرشتوں نے کہا، اے مریم! اللہ تعالیٰ نے تجھے برگزیده کر لیا اور تجھے پاک کر دیا اور سارے جہان کی عورتوں میں سے تیرا انتخاب کر لیا۔ ');
INSERT INTO "junagarri" VALUES(3,43,'3:43','اے مریم! تو اپنے رب کی اطاعت کر اور سجده کر اور رکوع کرنے والوں کے ساتھ رکوع کر۔');
INSERT INTO "junagarri" VALUES(3,44,'3:44','یہ غیب کی خبروں میں سے ہے جسے ہم تیری طرف وحی سے پہنچاتے ہیں، تو ان کے پاس نہ تھا جب کہ وه اپنے قلم ڈال رہے تھے کہ مریم کو ان میں سے کون پالے گا؟ اور نہ تو ان کے جھگڑنے کے وقت ان کے پاس تھا۔ ');
INSERT INTO "junagarri" VALUES(3,45,'3:45','جب فرشتوں نے کہا اے مریم اللہ تعالیٰ تجھے اپنے ایک کلمے کی خوشخبری دیتا ہے جس کا نام مسیح عیسیٰ بن مریم ہے جو دنیا اور آخرت میں ذیعزت ہے اور وه میرے مقربین میں سے ہے۔');
INSERT INTO "junagarri" VALUES(3,46,'3:46','وه لوگوں سے اپنے گہوارے میں باتیں کرے گا اور ادھیڑ عمر میں بھی اور وه نیک لوگوں میں سے ہوگا۔');
INSERT INTO "junagarri" VALUES(3,47,'3:47','کہنے لگیں الٰہی مجھے لڑکا کیسے ہوگا؟ حالانکہ مجھے تو کسی انسان نے ہاتھ بھی نہیں لگایا، فرشتے نے کہا، اسی طرح اللہ تعالیٰ جو چاہے پیدا کرتا ہے، جب کبھی وه کسی کام کو کرنا جاہتا ہے تو صرف یہ کہہ دیتا ہے کہ ہو جا! تو وه ہو جاتا ہے۔ ');
INSERT INTO "junagarri" VALUES(3,48,'3:48','اللہ تعالیٰ اسے لکھنا اور حکمت اور توراة اورانجیل سکھائے گا۔');
INSERT INTO "junagarri" VALUES(3,49,'3:49','اور وه بنی اسرائیل کی طرف رسول ہوگا، کہ میں تمہارے پاس تمہارے رب کی نشانی لایا ہوں، میں تمہارے لئے پرندے کی شکل کی طرح مٹی کا پرنده بناتا ہوں ، پھر اس میں پھونک مارتا ہوں تو وه اللہ تعالیٰ کے حکم سے پرنده بن جاتاہے اور اللہ تعالیٰ کے حکم سے میں مادرزاد اندھے کو اور کوڑھی کو اچھا کر دیتا ہوں اور مرددوں کو زندہ کرتا ہوں اور جو کچھ تم کھاؤ اور جو اپنے گھروں میں ذخیره کرو میں تمہیں بتا دیتا ہوں، اس میں تمہارے لئے بڑی نشانی ہے، اگر تم ایمان لانے والے ہو۔');
INSERT INTO "junagarri" VALUES(3,50,'3:50','اور میں توراة کی تصدیق کرنے والا ہوں جو میرے سامنے ہے اور میں اس لئے آیا ہوں کہ تم پر بعض وه چیزیں حلال کروں جو تم پر حرام کر دی گئی ہیں اور میں تمہارے پاس تمہارے رب کی نشانی لایا ہوں، اس لئے تم اللہ تعالیٰ سے ڈرو اور میری فرمانبرداری کرو!');
INSERT INTO "junagarri" VALUES(3,51,'3:51','یقین مانو! میرا اور تمہارا رب اللہ ہی ہے، تم سب اسی کی عبادت کرو، یہی سیدھی راه ہے۔ ');
INSERT INTO "junagarri" VALUES(3,52,'3:52','مگر جب حضرت عیسیٰ علیہ السلام نے ان کا کفر محسوس کر لیا تو کہنے لگے اللہ تعالیٰ کی راه میں میری مدد کرنے والا کون کون ہے؟ حواریوں نے جواب دیا کہ ہم اللہ تعالیٰ کی راه کے مددگار ہیں، ہم اللہ تعالیٰ پر ایمان لائے اور آپ گواه رہئے کہ ہم تابعدار ہیں۔');
INSERT INTO "junagarri" VALUES(3,53,'3:53','اے ہمارے پالنے والے معبود! ہم تیری اتاری ہوئی وحی پر ایمان لائے اور ہم نے تیرے رسول کی اتباع کی، پس تو ہمیں گواہوں میں لکھ لے۔');
INSERT INTO "junagarri" VALUES(3,54,'3:54','اور کافروں نے مکر کیا اور اللہ تعالیٰ نے بھی (مکر) خفیہ تدبیر کی اور اللہ تعالیٰ سب خفیہ تدبیر کرنے والوں سے بہتر ہے۔ ');
INSERT INTO "junagarri" VALUES(3,55,'3:55','جب اللہ تعالیٰ نے فرمایا کہ اے عیسیٰ! میں تجھے پورا لینے والا ہوں اور تجھے اپنی جانب اٹھانے والا ہوں اور تجھے کافروں سے پاک کرنے والا ہوں اور تیرے تابعداروں کو کافروں کے اوپر غالب کرنے والا ہوں قیامت کے دن تک ، پھر تم سب کا لوٹنا میری ہی طرف ہے میں ہی تمہارے آپس کے تمام تر اختلافات کا فیصلہ کروں گا');
INSERT INTO "junagarri" VALUES(3,56,'3:56','پھر کافروں کو تو میں دنیا اور آخرت میں سخت تر عذاب دوں گا اور ان کا کوئی مددگار نہ ہوگا۔');
INSERT INTO "junagarri" VALUES(3,57,'3:57','لیکن ایمان والوں اور نیک اعمال والوں کو اللہ تعالیٰ ان کا ثواب پورا پورا دے گا اور اللہ تعالیٰ ظالموں سے محبت نہیں کرتا۔');
INSERT INTO "junagarri" VALUES(3,58,'3:58','یہ جسے ہم تیرے سامنے پڑھ رہے ہیں آیتیں ہیں اور حکمت والی نصیحت ہیں۔');
INSERT INTO "junagarri" VALUES(3,59,'3:59','اللہ تعالیٰ کے نزدیک عیسیٰ (علیہ السلام) کی مثال ہو بہو آدم (علیہ السلام) کی مثال ہے جسے مٹی سے بنا کر کے کہہ دیا کہ ہو جا! پس وه ہو گیا!');
INSERT INTO "junagarri" VALUES(3,60,'3:60','تیرے رب کی طرف سے حق یہی ہے خبردار شک کرنے والوں میں نہ ہونا۔');
INSERT INTO "junagarri" VALUES(3,61,'3:61','اس لئے جو شخص آپ کے پاس اس علم کے آجانے کے بعد بھی آپ سے اس میں جھگڑے تو آپ کہہ دیں کہ آو ہم تم اپنے اپنے فرزندوں کو اور ہم تم اپنی اپنی عورتوں کواور ہم تم خاص اپنی اپنی جانوں کو بلالیں، پھر ہم عاجزی کے ساتھ التجا کریں اور جھوٹوں پر اللہ کی لعنت کریں۔ ');
INSERT INTO "junagarri" VALUES(3,62,'3:62','یقیناً صرف یہی سچا بیان ہے اور کوئی معبود برحق نہیں بجز اللہ تعالیٰ کے اور بے شک غالب اور حکمت والا اللہ تعالیٰ ہی ہے۔');
INSERT INTO "junagarri" VALUES(3,63,'3:63','پھر بھی اگر قبول نہ کریں تو اللہ تعالیٰ بھی صحیح طور پر فسادیوں کو جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(3,64,'3:64','آپ کہہ دیجئے کہ اے اہل کتاب! ایسی انصاف والی بات کی طرف آو جو ہم میں تم میں برابر ہے کہ ہم اللہ تعالیٰ کے سوا کسی کی عبادت نہ کریں نہ اس کے ساتھ کسی کو شریک بنائیں ، نہ اللہ تعالیٰ کو چھوڑ کر آپس میں ایک دوسرے کو ہی رب بنائیں ۔ پس اگر وه منھ پھیر لیں تو تم کہہ دو کہ گواه رہو ہم تو مسلمان ہیں۔ ');
INSERT INTO "junagarri" VALUES(3,65,'3:65','اے اہل کتاب! تم ابراہیم کی بابت کیوں جھگڑتے ہو حالانکہ تورات وانجیل تو ان کے بعد نازل کی گئیں، کیا تم پھر بھی نہیں سمجھتے؟ ');
INSERT INTO "junagarri" VALUES(3,66,'3:66','سنو! تم لوگ اس میں جھگڑ چکے جس کا تمہیں علم تھا پھر اب اس بات میں کیوں جھگڑتے ہو جس کا تمہیں علم ہی نہیں؟ اور اللہ تعالیٰ جانتا ہے اور تم نہیں جانتے،');
INSERT INTO "junagarri" VALUES(3,67,'3:67','ابراہیم تو نہ یہودی تھے نہ نصرانی تھے بلکہ وه تو یک طرفہ (خالص) مسلمان تھے ، وه مشرک بھی نہیں تھے،');
INSERT INTO "junagarri" VALUES(3,68,'3:68','سب لوگوں سے زیاده ابراہیم سے نزدیک تر وه لوگ ہیں جنہوں نے ان کا کہا مانا اور یہ نبی اور جو لوگ ایمان لائے ، مومنوں کا ولی اور سہارا اللہ ہی ہے،۔');
INSERT INTO "junagarri" VALUES(3,69,'3:69','اہل کتاب کی ایک جماعت چاہتی ہے کہ تمہیں گمراه کر دیں۔ دراصل وه خود اپنے آپ کو گمراه کر رہے ہیں اور سمجھتے نہیں۔ ');
INSERT INTO "junagarri" VALUES(3,70,'3:70','اے اہل کتاب تم (باوجود قائل ہونے کے پھر بھی) دانستہ اللہ کی آیات کا کیوں کفر کر رہے ہو؟ ');
INSERT INTO "junagarri" VALUES(3,71,'3:71','اے اہل کتاب! باوجود جاننے کے حق وباطل کو کیوں خلط ملط کر رہے ہو اور کیوں حق کو چھپا رہے ہو؟ ');
INSERT INTO "junagarri" VALUES(3,72,'3:72','اور اہل کتاب کی ایک جماعت نے کہا کہ جو کچھ ایمان والوں پر اتارا گیا ہے اس پر دن چڑھے تو ایمان لاؤ اور شام کے وقت کافر بن جاؤ، تاکہ یہ لوگ بھی پلٹ جائیں۔ ');
INSERT INTO "junagarri" VALUES(3,73,'3:73','اور سوائے تمہارے دین پر چلنے والوں کے اور کسی کا یقین نہ کرو ۔ آپ کہہ دیجئے کہ بے شک ہدایت تو اللہ ہی کی ہدایت ہے (اور یہ بھی کہتے ہیں کہ اس بات کا بھی یقین نہ کرو) کہ کوئی اس جیسا دیا جائے جیسا تم دیئے گئے ہو ، یا یہ کہ یہ تم سے تمہارے رب کے پاس جھگڑا کریں گے، آپ کہہ دیجئے کہ فضل تو اللہ تعالیٰ ہی کے ہاتھ میں ہے، وه جسے چاہے اسے دے، اللہ تعالیٰ وسعت والا اورجاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(3,74,'3:74','وه اپنی رحمت کے ساتھ جسے چاہے مخصوص کر لے اور اللہ تعالیٰ بڑے فضل والا ہے۔ ');
INSERT INTO "junagarri" VALUES(3,75,'3:75','بعض اہل کتاب تو ایسے ہیں کہ اگر انہیں تو خزانے کا امین بنا دے تو بھی وه تجھے واپس کر دیں اور ان میں سے بعض ایسے بھی ہیں کہ اگر تو انہیں ایک دینار بھی امانت دے تو تجھے ادا نہ کریں۔ ہاں یہ اوربات ہے کہ تو اس کے سر پر ہی کھڑا رہے، یہ اس لئے کہ انہوں نے کہہ رکھا ہے کہ ہم پر ان جاہلوں (غیر یہودی) کے حق کاکوئی گناه نہیں، یہ لوگ باوجود جاننے کے اللہ تعالیٰ پر جھوٹ کہتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(3,76,'3:76','کیوں نہیں (مواخذہ ہوگا) البتہ جو شخص اپنا قرار پورا کرے اور پرہیزگاری کرے، تو اللہ تعالیٰ بھی ایسے پرہیز گاروں سے محبت کرتاہے۔ ');
INSERT INTO "junagarri" VALUES(3,77,'3:77','بے شک جو لوگ اللہ تعالیٰ کے عہد اور اپنی قسموں کو تھوڑی قیمت پر بیچ ڈالتے ہیں، ان کے لئے آخرت میں کوئی حصہ نہیں، اللہ تعالیٰ نہ تو ان سے بات چیت کرے گا نہ ان کی طرف قیامت کے دن دیکھے گا، نہ انہیں پاک کرے گا اور ان کے لئے دردناک عذاب ہے۔ ');
INSERT INTO "junagarri" VALUES(3,78,'3:78','یقیناً ان میں ایسا گروه بھی ہے جو کتاب پڑھتے ہوئے اپنی زبان مروڑتا ہے تاکہ تم اسے کتاب ہی کی عبارت خیال کرو حالانکہ دراصل وه کتاب میں سے نہیں، اور یہ کہتے بھی ہیں کہ وه اللہ تعالیٰ کی طرف سے ہے حالانکہ در اصل وه اللہ تعالی کی طرف سے نہیں، وه تو دانستہ اللہ تعالیٰ پر جھوٹ بولتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(3,79,'3:79','کسی ایسے انسان کو جسے اللہ تعالیٰ کتاب وحکمت اور نبوت دے، یہ لائق نہیں کہ پھر بھی وه لوگوں سے کہے کہ تم اللہ تعالیٰ کو چھوڑ کر میرے بندے بن جاؤ ، بلکہ وه تو کہے گا کہ تم سب رب کے ہو جاؤ، تمہارے کتاب سکھانے کے باعث اور تمہارے کتاب پڑھنے کے سبب۔ ');
INSERT INTO "junagarri" VALUES(3,80,'3:80','اور یہ نہیں (ہو سکتا) کہ وه تمہیں فرشتوں اورنبیوں کو رب بنا لینے کا حکم کرے، کیا وه تمہارے مسلمان ہونے کے بعد بھی تمہیں کفر کا حکم دے گا۔ ');
INSERT INTO "junagarri" VALUES(3,81,'3:81','جب اللہ تعالیٰ نے نبیوں سے عہد لیا کہ جو کچھ میں تمہیں کتاب وحکمت دوں پھر تمہارے پاس وه رسول آئے جو تمہارے پاس کی چیز کو سچ بتائے تو تمہارے لئے اس پر ایمان لانا اور اس کی مدد کرنا ضروری ہے ۔ فرمایا کہ تم اس کے اقراری ہو اور اس پر میرا ذمہ لے رہے ہو؟ سب نے کہا کہ ہمیں اقرار ہے، فرمایا تو اب گواه رہو اور خود میں بھی تمہارے ساتھ گواہوں میں ہوں۔');
INSERT INTO "junagarri" VALUES(3,82,'3:82','پس اس کے بعد بھی جو پلٹ جائیں وه یقیناً پورے نافرمان ہیں۔ ');
INSERT INTO "junagarri" VALUES(3,83,'3:83','کیا وه اللہ تعالیٰ کے دین کے سوا اور دین کی تلاش میں ہیں؟ حالانکہ تمام آسمانوں والے اور سب زمین والے اللہ تعالیٰ ہی کے فرمانبردار ہیں خوشی سے ہوں یا ناخوشی سے ، سب اسی کی طرف لوٹائے جائیں گے۔');
INSERT INTO "junagarri" VALUES(3,84,'3:84','آپ کہہ دیجئے کہ ہم اللہ تعالیٰ پر اور جو کچھ ہم پر اتارا گیا ہے اور جو کچھ ابراہیم (علیہ السلام) اور اسماعیل (علیہ السلام) اور یعقوب (علیہ السلام) اوران کی اولاد پر اتارا گیا اور جو کچھ موسیٰ وعیسیٰ (علیہما السلام) اور دوسرے انبیاء (علیہم السلام) اللہ تعالیٰ کی طرف سے دیئے گئے، ان سب پر ایمان لائے ، ہم ان میں سے کسی کے درمیان فرق نہیں کرتے اور ہم اللہ تعالیٰ کے فرمانبردار ہیں۔');
INSERT INTO "junagarri" VALUES(3,85,'3:85','جو شخص اسلام کے سوا اور دین تلاش کرے، اس کا دین قبول نہ کیا جائے گا اور وه آخرت میں نقصان پانے والوں میں ہوگا۔');
INSERT INTO "junagarri" VALUES(3,86,'3:86','اللہ تعالیٰ ان لوگوں کو کیسے ہدایت دے گا جو اپنے ایمان لانے اور رسول کی حقانیت کی گواہی دینے اور اپنے پاس روشن دلیلیں آجانے کے بعد کافر ہو جائیں، اللہ تعالیٰ ایسے بے انصاف لوگوں کو راه راست پرنہیں لاتا۔');
INSERT INTO "junagarri" VALUES(3,87,'3:87','ان کی تو یہی سزا ہے کہ ان پر اللہ تعالیٰ کی اور فرشتوں کی اور تمام لوگوں کی لعنت ہو۔');
INSERT INTO "junagarri" VALUES(3,88,'3:88','جس میں یہ ہمیشہ پڑے رہیں گے، نہ تو ان سے عذاب ہلکا کیا جائے گا نہ انہیں مہلت دی جائے گی۔');
INSERT INTO "junagarri" VALUES(3,89,'3:89','مگرجو لوگ اس کے بعد توبہ اور اصلاح کر لیں تو بے شک اللہ تعالیٰ بخشنے والا مہربان ہے۔ ');
INSERT INTO "junagarri" VALUES(3,90,'3:90','بے شک جو لوگ اپنے ایمان لانے کے بعد کفر کریں پھر کفر میں بڑھ جائیں، ان کی توبہ ہرگز ہرگز قبول نہ کی جائے گی ، یہی گمراه لوگ ہیں.');
INSERT INTO "junagarri" VALUES(3,91,'3:91','ہاں جو لوگ کفر کریں اور مرتے دم تک کافر رہیں ان میں سے کوئی اگر زمین بھر سونا دے، گو فدیئے میں ہی ہو تو بھی ہرگز قبول نہ کیا جائے گا۔ یہی لوگ ہیں جن کے لئے تکلیف دینے والا عذاب ہے اور جن کا کوئی مددگار نہیں. ');
INSERT INTO "junagarri" VALUES(3,92,'3:92','جب تک تم اپنی پسندیده چیز سے اللہ تعالیٰ کی راه میں خرچ نہ کروگے ہرگز بھلائی نہ پاؤ گے ، اور تم جو خرچ کرو اسے اللہ تعالیٰ بخوبی جانتا ہے. ');
INSERT INTO "junagarri" VALUES(3,93,'3:93','توراة کے نزول سے پہلے (حضرت) یعقوب (علیہ السلام) نے جس چیز کو اپنے اوپر حرام کر لیا تھا اس کے سوا تمام کھانے بنی اسرائیل پر حلال تھے، آپ کہہ دیجئے کہ اگر تم سچے ہو تو توراة لے آو اور پڑھ کر سناؤ. ');
INSERT INTO "junagarri" VALUES(3,94,'3:94','اس کے بعد بھی جو لوگ اللہ تعالیٰ پر جھوٹ بہتان باندھیں وہی ظالم ہیں.');
INSERT INTO "junagarri" VALUES(3,95,'3:95','کہہ دیجئے کہ اللہ تعالیٰ سچا ہے تم سب ابراہیم حنیف کے ملت کی پیروی کرو، جو مشرک نہ تھے.');
INSERT INTO "junagarri" VALUES(3,96,'3:96','اللہ تعالیٰ کا پہلا گھر جو لوگوں کے لئے مقرر کیا گیا وہی ہے جو مکہ (شریف) میں ہے جو تمام دنیا کے لئے برکت وہدایت والا ہے.');
INSERT INTO "junagarri" VALUES(3,97,'3:97','جس میں کھلی کھلی نشانیاں ہیں، مقام ابراہیم ہے، اس میں جو آ جائے امن والا ہو جاتا ہے اللہ تعالیٰ نے ان لوگوں پر جو اس کی طرف راه پا سکتے ہوں اس گھر کا حج فرض کر دیا ہے ۔ اور جو کوئی کفر کرے تو اللہ تعالیٰ (اس سے بلکہ) تمام دنیا سے بے پرواه ہے. ');
INSERT INTO "junagarri" VALUES(3,98,'3:98','آپ کہہ دیجئے کہ اے اہل کتاب تم اللہ تعالیٰ کی آیتوں کے ساتھ کفر کیوں کرتے ہو؟ جو کچھ تم کرتے ہو، اللہ تعالیٰ اس پر گواه ہے.');
INSERT INTO "junagarri" VALUES(3,99,'3:99','ان اہل کتاب سے کہو کہ تم اللہ تعالیٰ کی راه سے لوگوں کو کیوں روکتے ہو؟ اور اس میں عیب ٹٹولتے ہو، حالانکہ تم خود شاہد ہو ، اللہ تعالیٰ تمہارے اعمال سے بے خبر نہیں.');
INSERT INTO "junagarri" VALUES(3,100,'3:100','اے ایمان والو! اگر تم اہل کتاب کی کسی جماعت کی باتیں مانو گے تو وه تمہیں تمہارے ایمان لانے کے بعد مرتد کافر بنا دیں گے.');
INSERT INTO "junagarri" VALUES(3,101,'3:101','﴿گو یہ ظاہر ہے کہ﴾ تم کیسے کفر کر سکتے ہو؟ باوجود یہ کہ تم پر اللہ تعالیٰ کی آیتیں پڑھی جاتی ہیں اور تم میں رسول اللہ ﴿صلی اللہ علیہ وسلم﴾ موجود ہیں۔ جو شخص اللہ تعالیٰ (کے دین) کو مضبوط تھام لے تو بلاشبہ اسے راه راست دکھادی گئی.');
INSERT INTO "junagarri" VALUES(3,102,'3:102','اے ایمان والو! اللہ تعالیٰ سے اتنا ڈرو جتنا اس سے ڈرنا چاہئیے اور دیکھو مرتے دم تک مسلمان ہی رہنا.');
INSERT INTO "junagarri" VALUES(3,103,'3:103','اللہ تعالیٰ کی رسی کو سب مل کر مضبوط تھام لو اور پھوٹ نہ ڈالو ، اور اللہ تعالیٰ کی اس وقت کی نعمت کو یاد کرو جب تم ایک دوسرے کے دشمن تھے، تو اس نے تمہارے دلوں میں الفت ڈال دی، پس تم اس کی مہربانی سے بھائی بھائی ہوگئے، اور تم آگ کے گڑھے کے کنارے پہنچ چکے تھے تو اس نے تمہیں بچا لیا۔ اللہ تعالیٰ اسی طرح تمہارے لئے اپنی نشانیاں بیان کرتا ہے تاکہ تم ہدایت پاؤ.');
INSERT INTO "junagarri" VALUES(3,104,'3:104','تم میں سے ایک جماعت ایسی ہونی چاہئے جو بھلائی کی طرف بلائے اور نیک کاموں کا حکم کرے اور برے کاموں سے روکے، اور یہی لوگ فلاح ونجات پانے والے ہیں.');
INSERT INTO "junagarri" VALUES(3,105,'3:105','تم ان لوگوں کی طرح نہ ہو جانا جنہوں نے اپنے پاس روشن دلیلیں آ جانے کے بعد بھی تفرقہ ڈالا ، اور اختلاف کیا، انہیں لوگوں کے لئے بڑا عذاب ہے.');
INSERT INTO "junagarri" VALUES(3,106,'3:106','جس دن بعض چہرے سفید ہوں گے اور بعض سیاه ، سیاه چہرے والوں (سے کہا جائے گا) کہ کیا تم نے ایمان لانے کے بعد کفر کیا؟ اب اپنے کفر کا عذاب چکھو.');
INSERT INTO "junagarri" VALUES(3,107,'3:107','اور سفید چہرے والے اللہ تعالیٰ کی رحمت میں داخل ہوں گے اور اس میں ہمیشہ رہیں گے.');
INSERT INTO "junagarri" VALUES(3,108,'3:108','اے نبی! ہم ان حقانی آیتوں کی تلاوت آپ پر کر رہے ہیں اور اللہ تعالیٰ کا اراده لوگوں پر ظلم کرنے کا نہیں.');
INSERT INTO "junagarri" VALUES(3,109,'3:109','اللہ تعالیٰ ہی کے لئے ہے جو کچھ آسمانوں اور زمین میں ہے اور اللہ تعالیٰ ہی کی طرف تمام کام لوٹائے جاتے ہیں.');
INSERT INTO "junagarri" VALUES(3,110,'3:110','تم بہترین امت ہو جو لوگوں کے لئے پیدا کی گئی ہے کہ تم نیک باتوں کا حکم کرتے ہو اور بری باتوں سے روکتے ہو، اور اللہ تعالیٰ پر ایمان رکھتے ہو ، اگر اہل کتاب بھی ایمان لاتے تو ان کے لئے بہتر تھا، ان میں ایمان والے بھی ہیں لیکن اکثر تو فاسق ہیں.');
INSERT INTO "junagarri" VALUES(3,111,'3:111','یہ تمہیں ستانے کے سوا اور زیاده کچھ ضرر نہیں پہنچا سکتے، اگر لڑائی کا موقعہ آجائے تو پیٹھ موڑ لیں گے، پھر مدد نہ کیے جائیں گے. ');
INSERT INTO "junagarri" VALUES(3,112,'3:112','ان پر ہر جگہ ذلت کی مار پڑی، الاّ یہ کہ اللہ تعالیٰ کی یا لوگوں کی پناه میں ہوں ، یہ غضب الٰہی کے مستحق ہو گئے اور ان پر فقیری ڈال دی گئی، یہ اس لئے کہ یہ لوگ اللہ تعالیٰ کی آیتوں سے کفر کرتے تھے اور بے وجہ انبیا کو قتل کرتے تھے، یہ بدلہ ہے ان کی نافرمانیوں اور زیادتیوں کا. ');
INSERT INTO "junagarri" VALUES(3,113,'3:113','یہ سارے کے سارے یکساں نہیں بلکہ ان اہل کتاب میں ایک جماعت (حق پر) قائم رہنے والی بھی ہے جو راتوں کے وقت بھی کلام اللہ کی تلاوت کرتے ہیں اور سجدے بھی کرتے ہیں.');
INSERT INTO "junagarri" VALUES(3,114,'3:114','یہ اللہ تعالیٰ پر اورقیامت کے دن پر ایمان بھی رکھتے ہیں، بھلائیوں کا حکم کرتے ہیں اور برائیوں سے روکتے ہیں اور بھلائی کے کاموں میں جلدی کرتے ہیں۔ یہ نیک بخت لوگوں میں سے ہیں.');
INSERT INTO "junagarri" VALUES(3,115,'3:115','یہ جو کچھ بھی بھلائیاں کریں ان کی ناقدری نہ کی جائے گی اور اللہ تعالیٰ پرہیزگاروں کو خوب جانتا ہے. ');
INSERT INTO "junagarri" VALUES(3,116,'3:116','کافروں کو ان کے مال اور ان کی اولاد اللہ کے ہاں کچھ کام نہ آئیں گی، یہ تو جہنمی ہیں جو ہمیشہ اسی میں پڑے رہیں گے.');
INSERT INTO "junagarri" VALUES(3,117,'3:117','یہ کفار جو خرچ اخراجات کریں اس کی مثال یہ ہے کہ ایک تند ہوا چلی جس سے پالا تھا جو ظالموں کی کھیتی پر پڑا اور اسے تہس نہس کر دیا ۔ اللہ تعالیٰ نے ان پر ظلم نہیں کیا بلکہ وه خود اپنی جانوں پر ظلم کرتے تھے.');
INSERT INTO "junagarri" VALUES(3,118,'3:118','اے ایمان والو! تم اپنا دلی دوست ایمان والوں کے سوا اور کسی کو نہ بناؤ ۔ (تم تو) نہیں دیکھتے دوسرے لوگ تمہاری تباہی میں کوئی کسر اٹھا نہیں رکھتے، وه تو چاہتے ہیں کہ تم دکھ میں پڑو ان کی عداوت تو خود ان کی زبان سے بھی ظاہر ہو چکی ہے اور جو ان کے سینوں میں پوشیده ہے وه بہت زیاده ہے، ہم نے تمہارے لئے آیتیں بیان کر دیں اگر عقلمند ہو (تو غور کرو).');
INSERT INTO "junagarri" VALUES(3,119,'3:119','ہاں تم تو انہیں چاہتے ہو اور وه تم سے محبت نہیں رکھتے، تم پوری کتاب کو مانتے ہو، (وه نہیں مانتے پھر محبت کیسی؟) یہ تمہارے سامنے تو اپنے ایمان کا اقرار کرتے ہیں لیکن تنہائی میں مارے غصہ کے انگلیاں چباتے ہیں کہہ دو کہ اپنے غصہ ہی میں مر جاؤ، اللہ تعالیٰ دلوں کے راز کو بخوبی جانتا ہے.');
INSERT INTO "junagarri" VALUES(3,120,'3:120','تمہیں اگر بھلائی ملے تو یہ ناخوش ہوتے ہیں ہاں! اگر برائی پہنچے تو خوش ہوتے ہیں ، تم اگر صبر کرو اور پرہیزگاری کرو تو ان کا مکر تمہیں کچھ نقصان نہ دے گا ۔ اللہ تعالیٰ نے انکے اعمال کا احاطہ کر رکھا ہے.');
INSERT INTO "junagarri" VALUES(3,121,'3:121','اے نبی! اس وقت کو بھی یاد کرو جب صبح ہی صبح آپ اپنے گھر سے نکل کر مسلمانوں کو میدان جنگ میں لڑائی کے مورچوں پر باقاعده بٹھا رہے تھے اللہ تعالیٰ سننے جاننے والا ہے.');
INSERT INTO "junagarri" VALUES(3,122,'3:122','جب تمہاری دو جماعتیں پست ہمتی کا اراده کر چکی تھیں ، اللہ تعالیٰ انکا ولی اور مددگار ہے ۔ اور اسی کی پاک ذات پر مومنوں کو بھروسہ رکھنا چاہئے.');
INSERT INTO "junagarri" VALUES(3,123,'3:123','جنگ بدر میں اللہ تعالیٰ نے عین اس وقت تمہاری مدد فرمائی تھی جب کہ تم نہایت گری ہوئی حالت میں تھے ، اس لئے اللہ ہی سے ڈرو! (نہ کسی اور سے) تاکہ تمہیں شکر گزاری کی توفیق ہو.');
INSERT INTO "junagarri" VALUES(3,124,'3:124','(اور یہ شکر گزاری باعث نصرت وامداد ہو) جب آپ مومنوں کو تسلی دے رہے تھے، کیا آسمان سے تین ہزار فرشتے اتار کر اللہ تعالیٰ کا تمہاری مدد کرنا تمہیں کافی نہ ہوگا.');
INSERT INTO "junagarri" VALUES(3,125,'3:125','کیوں نہیں، بلکہ اگر تم صبر و پرہیزگاری کرو اور یہ لوگ اسی دم تمہارے پاس آجائیں تو تمہارا رب تمہاری امداد پانچ ہزار فرشتوں سے کرے گا جو نشاندار ہوں گے. ');
INSERT INTO "junagarri" VALUES(3,126,'3:126','اور یہ تو محض تمہارے دل کی خوشی اور اطمینان قلب کے لئے ہے، ورنہ مدد تو اللہ ہی کی طرف سے ہے جو غالب اور حکمتوں والا ہے.');
INSERT INTO "junagarri" VALUES(3,127,'3:127','(اس امداد الٰہی کا مقصد یہ تھا کہ اللہ) کافروں کی ایک جماعت کو کاٹ دے یا انہیں ذلیل کر ڈالے اور (سارے کے سارے) نامراد ہو کر واپس چلے جائیں. ');
INSERT INTO "junagarri" VALUES(3,128,'3:128','اے پیغمبر! آپ کے اختیار میں کچھ نہیں ، اللہ تعالیٰ چاہے تو ان کی توبہ قبول کرے یا عذاب دے، کیونکہ وه ظالم ہیں.');
INSERT INTO "junagarri" VALUES(3,129,'3:129','آسمانوں اور زمین میں جو کچھ ہے سب اللہ ہی کا ہے، وه جسے چاہے بخشے جسے چاہے عذاب کرے، اللہ تعالیٰ بخشش کرنے والا مہربان ہے.');
INSERT INTO "junagarri" VALUES(3,130,'3:130','اے ایمان والو! بڑھا چڑھا کر سود نہ کھاؤ ، اور اللہ تعالیٰ سے ڈرو تاکہ تمہیں نجات ملے.');
INSERT INTO "junagarri" VALUES(3,131,'3:131','اور اس آگ سے ڈرو جو کافروں کے لئے تیار کی گئی ہے.');
INSERT INTO "junagarri" VALUES(3,132,'3:132','اور اللہ اور اس کے رسول کی فرمانبرداری کرو تاکہ تم پر رحم کیا جائے.');
INSERT INTO "junagarri" VALUES(3,133,'3:133','اور اپنے رب کی بخشش کی طرف اور اس جنت کی طرف دوڑو جس کا عرض آسمانوں اور زمین کے برابر ہے، جو پرہیزگاروں کے لئے تیار کی گئی ہے.');
INSERT INTO "junagarri" VALUES(3,134,'3:134','جو لوگ آسانی میں سختی کے موقع پر بھی اللہ کے راستے میں خرچ کرتے ہیں ، غصہ پینے والے اور لوگوں سے درگزر کرنے والے ہیں ، اللہ تعالیٰ ان نیک کاروں سے محبت کرتا ہے.');
INSERT INTO "junagarri" VALUES(3,135,'3:135','جب ان سے کوئی ناشائستہ کام ہو جائے یا کوئی گناه کر بیٹھیں تو فوراً اللہ کا ذکر اور اپنے گناہوں کے لئے استغفار کرتے ہیں ، فیالواقع اللہ تعالیٰ کے سوا اور کون گناہوں کو بخش سکتا ہے؟ اور وه لوگ باوجود علم کے کسی برے کام پر اڑ نہیں جاتے.');
INSERT INTO "junagarri" VALUES(3,136,'3:136','انہیں کا بدلہ ان کے رب کی طرف سے مغفرت ہے اور جنتیں ہیں جن کے نیچے نہریں بہتی ہیں، جن میں وه ہمیشہ رہیں گے، ان نیک کاموں کے کرنے والوں کا ثواب کیا ہی اچھا ہے.');
INSERT INTO "junagarri" VALUES(3,137,'3:137','تم سے پہلے بھی ایسے واقعات گزر چکے ہیں، سو زمین میں چل پھر کر دیکھ لو کہ (آسمانی تعلیم کے) جھٹلانے والوں کا کیا انجام ہوا؟ ');
INSERT INTO "junagarri" VALUES(3,138,'3:138','عام لوگوں کے لئے تو یہ (قرآن) بیان ہے اور پرہیزگاروں کے لئے ہدایت ونصیحت ہے.');
INSERT INTO "junagarri" VALUES(3,139,'3:139','تم نہ سستی کرو اور غمگین ہو، تم ہی غالب رہو گے، اگر تم ایماندار ہو. ');
INSERT INTO "junagarri" VALUES(3,140,'3:140','اگر تم زخمی ہوئے ہو تو تمہارے مخالف لوگ بھی تو ایسے ہی زخمی ہو چکے ہیں، ہم ان دنوں کو لوگوں کے درمیان ادلتے بدلتے رہتے ہیں ۔ (شکست احد) اس لئے تھی کہ اللہ تعالیٰ ایمان والوں کو ظاہر کردے اور تم میں سے بعض کو شہادت کا درجہ عطا فرمائے، اللہ تعالیٰ ظالموں سے محبت نہیں کرتا.');
INSERT INTO "junagarri" VALUES(3,141,'3:141','(یہ وجہ بھی تھی) کہ اللہ تعالیٰ ایمان والوں کو بالکل الگ کر دے اور کافروں کو مٹا دے. ');
INSERT INTO "junagarri" VALUES(3,142,'3:142','کیا تم یہ سمجھ بیٹھے ہو کہ تم جنت میں چلے جاؤ گے ، حالانکہ اب تک اللہ تعالیٰ نے یہ ظاہر نہیں کیا کہ تم سے جہاد کرنے والے کون ہیں اور صبر کرنے والے کون ہیں؟ ');
INSERT INTO "junagarri" VALUES(3,143,'3:143','جنگ سے پہلے تو تم شہادت کی آرزو میں تھے اب اسے اپنی آنکھوں سے اپنے سامنے دیکھ لیا. ');
INSERT INTO "junagarri" VALUES(3,144,'3:144','(حضرت) محمد ﴿صلی اللہ علیہ وسلم﴾ صرف رسول ہی ہیں ، ان سے پہلے بہت سے رسول ہو چکے ہیں، کیا اگر ان کا انتقال ہو جائے یا یہ شہید ہو جائیں، تو تم اسلام سے اپنی ایڑیوں کے بل پھر جاؤ گے؟ اور جو کوئی پھر جائے اپنی ایڑیوں پر تو ہرگز اللہ تعالیٰ کا کچھ نہ بگاڑے گا ، عنقریب اللہ تعالیٰ شکر گزاروں کو نیک بدلہ دے گا. ');
INSERT INTO "junagarri" VALUES(3,145,'3:145','بغیر اللہ تعالیٰ کے حکم کے کوئی جاندار نہیں مرسکتا، مقرر شده وقت لکھا ہوا ہے، دنیا کی چاہت والوں کو ہم کچھ دنیا دے دیتے ہیں اور آخرت کا ثواب چاہنے والوں کو ہم وه بھی دیں گے ۔ اوراحسان ماننے والوں کو ہم بہت جلد نیک بدلہ دیں گے.');
INSERT INTO "junagarri" VALUES(3,146,'3:146','بہت سے نبیوں کے ہم رکاب ہو کر، بہت سے اللہ والے جہاد کر چکے ہیں، انہیں بھی اللہ کی راه میں تکلیفیں پہنچیں لیکن نہ تو انہوں نے ہمت ہاری نہ سست رہے اور نہ دبے، اور اللہ صبر کرنے والوں کو (ہی) چاہتا ہے. ');
INSERT INTO "junagarri" VALUES(3,147,'3:147','وه یہی کہتے رہے کہ اے پروردگار! ہمارے گناہوں کو بخش دے اور ہم سے ہمارے کاموں میں جو بے جا زیادتی ہوئی ہے اسے بھی معاف فرما اور ہمیں ثابت قدمی عطا فرما اور ہمیں کافروں کی قوم پر مدد دے.');
INSERT INTO "junagarri" VALUES(3,148,'3:148','اللہ تعالیٰ نے انہیں دنیا کا ثواب بھی دیا اور آخرت کے ثواب کی خوبی بھی عطا فرمائی اور اللہ تعالیٰ نیک لوگوں سے محبت کرتا ہے.');
INSERT INTO "junagarri" VALUES(3,149,'3:149','اے ایمان والو! اگر تم کافروں کی باتیں مانو گے تو وه تمہیں تمہاری ایڑیوں کے بل پلٹا دیں گے، (یعنی تمہیں مرتد بنا دیں گے) پھر تم نامراد ہو جاؤ گے.');
INSERT INTO "junagarri" VALUES(3,150,'3:150','بلکہ اللہ ہی تمہارا مولا ہے اور وہی بہترین مددگار ہے. ');
INSERT INTO "junagarri" VALUES(3,151,'3:151','ہم عنقریب کافروں کے دلوں میں رعب ڈال دیں گے، اس وجہ سے کہ یہ اللہ کے ساتھ ان چیزوں کو شریک کرتے ہیں جس کی کوئی دلیل اللہ نے نہیں اتاری ، ان کا ٹھکانہ جہنم ہے، اور ان ظالموں کی بری جگہ ہے.');
INSERT INTO "junagarri" VALUES(3,152,'3:152','اللہ تعالیٰ نے تم سے اپنا وعده سچا کر دکھایا جبکہ تم اس کے حکم سے انہیں کاٹ رہے تھے ۔ یہاں تک کہ جب تم نے پست ہمتی اختیار کی اور کام میں جھگڑنے لگے اور نافرمانی کی ، اس کے بعد کہ اس نے تمہاری چاہت کی چیز تمہیں دکھا دی ، تم میں سے بعض دنیا چاہتے تھے اور بعض کا اراده آخرت کا تھا تو پھر اس نے تمہیں ان سے پھیر دیا تاکہ تم کو آزمائے اور یقیناً اس نے تمہاری لغزش سے درگزر فرما دیا اور ایمان والوں پر اللہ تعالیٰ بڑے فضل والا ہے. ');
INSERT INTO "junagarri" VALUES(3,153,'3:153','جب کہ تم چڑھے چلے جا رہے تھے اور کسی طرف توجہ تک نہیں کرتے تھے اور اللہ کے رسول تمہیں تمہارے پیچھے سے آوازیں دے رہے تھے ، بس تمہیں غم پر غم پہنچا تاکہ تم فوت شده چیز پر غمگین نہ ہو اور نہ پہنچنے والی (تکلیف) پر اداس ہو ، اللہ تعالیٰ تمہارے تمام اعمال سے خبردار ہے.');
INSERT INTO "junagarri" VALUES(3,154,'3:154','پھر اس نے اس غم کے بعد تم پر امن نازل فرمایا اور تم میں سے ایک جماعت کو امن کی نیند آنے لگی ۔ ہاں کچھ وه لوگ بھی تھے کہ انہیں اپنی جانوں کی پڑی ہوئی تھی ، وه اللہ تعالیٰ کے ساتھ ناحق جہالت بھری بدگمانیاں کر رہے تھے اور کہتے تھے کیا ہمیں بھی کسی چیز کا اختیار ہے ؟ آپ کہہ دیجئے کہ کام کل کا کل اللہ کے اختیار میں ہے ، یہ لوگ اپنے دلوں کے بھید آپ کو نہیں بتاتے ، کہتے ہیں کہ اگر ہمیں کچھ بھی اختیار ہوتا تو یہاں قتل نہ کئے جاتے ۔ آپ کہہ دیجئے کہ گو تم اپنے گھروں میں ہوتے پھر بھی جن کی قسمت میں قتل ہونا تھا وه تو مقتل کی طرف چل کھڑے ہوتے ، اللہ تعالیٰ کو تمہارے سینوں کے اندر کی چیز کا آزمانا اور جو کچھ تمہارے دلوں میں ہے، اس کو پاک کرنا تھا ، اور اللہ تعالی سینوں کے بھید سے آگاه ہے. ');
INSERT INTO "junagarri" VALUES(3,155,'3:155','تم میں سے جن لوگوں نے اس دن پیٹھ دکھائی جس دن دونوں جماعتوں کی مدبھیڑ ہوئی تھی یہ لوگ اپنے بعض کرتوتوں کے باعث شیطان کے پھسلانے میں آگئے لیکن یقین جانو کہ اللہ تعالیٰ نے انہیں معاف کر دیا اللہ تعالیٰ ہے بخشنے والا اور تحمل والا.');
INSERT INTO "junagarri" VALUES(3,156,'3:156','اے ایمان والو! تم ان لوگوں کی طرح نہ ہو جانا جنہوں نے کفر کیا اور اپنے بھائیوں کے حق میں جب کہ وه سفر میں ہوں یا جہاد میں ہوں، کہا کہ اگر یہ ہمارے پاس ہوتے تو نہ مرتے اور نہ مارے جاتے ، اس کی وجہ یہ تھی کہ اس خیال کو اللہ تعالیٰ ان کی دلی حسرت کا سبب بنا دے ، اللہ تعالیٰ جلاتا ہے اور مارتا ہے اور اللہ تمہارے عمل کو دیکھ رہا ہے.');
INSERT INTO "junagarri" VALUES(3,157,'3:157','قسم ہے اگر اللہ تعالیٰ کی راه میں شہید کئے جاؤ یا اپنی موت مرو تو بے شک اللہ تعالیٰ کی بخشش ورحمت اس سے بہتر ہے جسے یہ جمع کر رہے ہیں. ');
INSERT INTO "junagarri" VALUES(3,158,'3:158','بالیقین خواہ تم مر جاؤ یا مار ڈالے جاؤ جمع تو اللہ تعالیٰ کی طرف ہی کئے جاؤ گے.');
INSERT INTO "junagarri" VALUES(3,159,'3:159','اللہ تعالیٰ کی رحمت کے باعث آپ ان پر نرم دل ہیں اور اگر آپ بد زبان اور سخت دل ہوتے تو یہ سب آپ کے پاس سے چھٹ جاتے، سو آپ ان سے درگزر کریں اور ان کے لئے استغفار کریں اور کام کا مشوره ان سے کیا کریں ، پھر جب آپ کا پختہ اراده ہو جائے تو اللہ تعالیٰ پر بھروسہ کریں ، بے شک اللہ تعالیٰ توکل کرنے والوں سے محبت کرتا ہے.');
INSERT INTO "junagarri" VALUES(3,160,'3:160','اگر اللہ تعالیٰ تمہاری مدد کرے تو تم پر کوئی غالب نہیں آسکتا اور اگر وہ تمہیں چھوڑ دے تو اس کے بعد کون ہے جو تمہاری مدد کرے؟ ایمان والوں کو اللہ تعالیٰ ہی پر بھروسہ رکھنا چاہئے.');
INSERT INTO "junagarri" VALUES(3,161,'3:161','ناممکن ہے کہ نبی سے خیانت ہو جائے ہر خیانت کرنے والا خیانت کو لئے ہوئے قیامت کے دن حاضر ہوگا، پھر ہر شخص کو اپنے اعمال کا پورا پورا بدلہ دیا جائے گا، اور وه ظلم نہ کئے جائیں گے.');
INSERT INTO "junagarri" VALUES(3,162,'3:162','کیا پس وه شخص جو اللہ تعالیٰ کی خوشنودی کے درپے ہے، اس شخص جیسا ہے جو اللہ تعالیٰ کی ناراضگی لے کر لوٹتا ہے؟ اور جس کی جگہ جہنم ہے جو بدترین جگہ ہے.');
INSERT INTO "junagarri" VALUES(3,163,'3:163','اللہ تعالیٰ کے پاس ان کے الگ الگ درجے ہیں اور ان کے تمام اعمال کو اللہ بخوبی دیکھ رہا ہے.');
INSERT INTO "junagarri" VALUES(3,164,'3:164','بے شک مسلمانوں پر اللہ تعالیٰ کا بڑا احسان ہے کہ ان ہی میں سے ایک رسول ان میں بھیجا ، جو انہیں اس کی آیتیں پڑھ کر سناتا ہے اور انہیں پاک کرتا ہے اور انہیں کتاب اور حکمت سکھاتا ہے ، یقیناً یہ سب اس سے پہلے کھلی گمراہی میں تھے.');
INSERT INTO "junagarri" VALUES(3,165,'3:165','(کیا بات ہے) کہ جب تمہیں ایک ایسی تکلیف پہنچی کہ تم اس جیسی دو چند پہنچا چکے ، تو یہ کہنے لگے کہ یہ کہاں سے آگئی؟ آپ کہہ دیجئے کہ یہ خود تمہاری طرف سے ہے ، بے شک اللہ تعالیٰ ہر چیز پر قادر ہے.');
INSERT INTO "junagarri" VALUES(3,166,'3:166','اور تمہیں جو کچھ اس دن پہنچا جس دن دو جماعتوں میں مڈ بھیڑ ہوئی تھی، وه سب اللہ کے حکم سے تھا اور اس لئے کہ اللہ تعالیٰ ایمان والوں کو ظاہری طور پر جان لے.');
INSERT INTO "junagarri" VALUES(3,167,'3:167','اور منافقوں کو بھی معلوم کر لے جن سے کہا گیا کہ آؤ اللہ کی راه میں جہاد کرو، یا کافروں کو ہٹاو، ٴ تو وه کہنے لگے کہ اگر ہم لڑائی جانتے ہوتے تو ضرور ساتھ دیتے ، وه اس دن بہ نسبت ایمان کے کفر سے بہت قریب تھے ، اپنے منھ سے وه باتیں بناتے ہیں جو ان کے دلوں میں نہیں ، اور اللہ تعالیٰ خوب جانتا ہے جسے وه چھپاتے ہیں.');
INSERT INTO "junagarri" VALUES(3,168,'3:168','یہ وه لوگ ہیں جو خود بھی بیٹھے رہے اور اپنے بھائیوں کی بابت کہا کہ اگر وه بھی ہماری بات مان لیتے تو قتل نہ کئے جاتے۔ کہہ دیجئے! کہ اگر تم سچے ہو تو اپنی جانوں سے موت کو ہٹا دو. ');
INSERT INTO "junagarri" VALUES(3,169,'3:169','جو لوگ اللہ کی راه میں شہید کئے گئے ہیں ان کو ہرگز مرده نہ سمجھیں، بلکہ وه زنده ہیں اپنے رب کے پاس روزیاں دیئے جاتے ہیں. ');
INSERT INTO "junagarri" VALUES(3,170,'3:170','اللہ تعالیٰ نے اپنا فضل جو انہیں دے رکھا ہے اس سے بہت خوش ہیں اور خوشیاں منا رہے ہیں ان لوگوں کی بابت جو اب تک ان سے نہیں ملے ان کے پیچھے ہیں ، اس پر کہ انہیں نہ کوئی خوف ہے اور نہ وه غمگین ہوں گے.');
INSERT INTO "junagarri" VALUES(3,171,'3:171','وه خوش ہوتے ہیں اللہ کی نعمت اور فضل سے اور اس سے بھی کہ اللہ تعالیٰ ایمان والوں کے اجر کو برباد نہیں کرتا. ');
INSERT INTO "junagarri" VALUES(3,172,'3:172','جن لوگوں نے اللہ اور رسول کے حکم کو قبول کیا اس کے بعد کہ انہیں پورے زخم لگ چکے تھے، ان میں سے جنہوں نے نیکی کی اور پرہیزگاری برتی ان کے لئے بہت زیاده اجر ہے. ');
INSERT INTO "junagarri" VALUES(3,173,'3:173','وه لوگ کہ جب ان سے لوگوں نے کہا کہ کافروں نے تمہارے مقابلے پر لشکر جمع کر لئے ہیں، تم ان سے خوف کھاؤ تو اس بات نے انہیں ایمان میں اور بڑھا دیا اور کہنے لگے ہمیں اللہ کافی ہے اور وه بہت اچھا کارساز ہے. ');
INSERT INTO "junagarri" VALUES(3,174,'3:174','(نتیجہ یہ ہوا کہ) اللہ کی نعمت وفضل کے ساتھ یہ لوٹے ، انہیں کوئی برائی نہ پہنچی، انہوں نے اللہ تعالیٰ کی رضامندی کی پیروی کی، اللہ بہت بڑے فضل والا ہے.');
INSERT INTO "junagarri" VALUES(3,175,'3:175','یہ خبر دینے والا صرف شیطان ہی ہے جو اپنے دوستوں سے ڈراتا ہے تم ان کافروں سے نہ ڈرو اور میرا خوف رکھو، اگر تم مومن ہو. ');
INSERT INTO "junagarri" VALUES(3,176,'3:176','کفر میں آگے بڑھنے والے لوگ تجھے غمناک نہ کریں، یقین مانو کہ یہ اللہ تعالیٰ کا کچھ نہ بگاڑ سکیں گے، اللہ تعالیٰ کا اراده ہے کہ ان کے لئے آخرت کا کوئی حصہ عطا نہ کرے ، اور ان کے لئے بڑا عذاب ہے.');
INSERT INTO "junagarri" VALUES(3,177,'3:177','کفر کو ایمان کے بدلے خریدنے والے ہرگز ہرگز اللہ تعالیٰ کو کوئی نقصان نہیں پہنچا سکتے اور ان ہی کے لئے المناک عذاب ہے.');
INSERT INTO "junagarri" VALUES(3,178,'3:178','کافر لوگ ہماری دی ہوئی مہلت کو اپنے حق میں بہتر نہ سمجھیں، یہ مہلت تو اس لئے ہے کہ وه گناہوں میں اور بڑھ جائیں ، ان ہی کے لئے ذلیل کرنے والا عذاب ہے.');
INSERT INTO "junagarri" VALUES(3,179,'3:179','جس حال میں تم ہو اسی پر اللہ ایمان والوں کو نہ چھوڑ دے گا جب تک کہ پاک اور ناپاک کو الگ الگ نہ کر دے ، اور نہ اللہ تعالیٰ ایسا ہے کہ تمہیں غیب سے آگاه کردے ، بلکہ اللہ تعالیٰ اپنے رسولوں میں سے جس کا چاہے انتخاب کر لیتا ہے ، اس لئے تم اللہ تعالیٰ پر اور اس کے رسولوں پر ایمان رکھو، اگر تم ایمان لاؤ اور تقویٰ کرو تو تمہارے لئے بڑا بھاری اجر ہے.');
INSERT INTO "junagarri" VALUES(3,180,'3:180','جنہیں اللہ تعالیٰ نے اپنے فضل سے کچھ دے رکھا ہے وه اس میں اپنی کنجوسی کو اپنے لئے بہتر خیال نہ کریں بلکہ وه ان کے لئے نہایت بدتر ہے، عنقریب قیامت والے دن یہ اپنی کنجوسی کی چیز کے طوق ڈالے جائیں گے ، آسمانوں اور زمین کی میراث اللہ تعالیٰ ہی کے لئے اور جو کچھ تم کر رہے ہو، اس سے اللہ تعالیٰ آگاه ہے.');
INSERT INTO "junagarri" VALUES(3,181,'3:181','یقیناً اللہ تعالیٰ نے ان لوگوں کا قول بھی سنا جنہوں نے کہا کہ اللہ تعالیٰ فقیر ہے اور ہم تونگر ہیں ان کے اس قول کو ہم لکھ لیں گے۔ اور ان کا انبیا کو بلا وجہ قتل کرنا بھی ، اور ہم ان سے کہیں گے کہ جلنے والا عذاب چکھو!');
INSERT INTO "junagarri" VALUES(3,182,'3:182','یہ تمہارے پیش کرده اعمال کا بدلہ ہے اور اللہ تعالیٰ اپنے بندوں پر ظلم کرنے والا نہیں.');
INSERT INTO "junagarri" VALUES(3,183,'3:183','یہ وه لوگ ہیں جنہوں نے کہا کہ اللہ تعالیٰ نے ہمیں حکم دیا ہے کہ کسی رسول کو نہ مانیں جب تک وه ہمارے پاس ایسی قربانی نہ لائے جسے آگ کھا جائے۔ آپ کہہ دیجئے کہ اگر تم سچے ہو تو مجھ سے پہلے تمہارے پاس جو رسول دیگر معجزوں کے ساتھ یہ بھی لائے جسے تم کہہ رہے ہو تو پھر تم نے انہیں کیوں مار ڈالا؟ ');
INSERT INTO "junagarri" VALUES(3,184,'3:184','پھر بھی اگر یہ لوگ آپ کو جھٹلائیں تو آپ سے پہلے بھی بہت سے رسول جھٹلائے گئے ہیں جو روشن دلیلیں صحیفے اور منور کتاب لے کر آئے. ');
INSERT INTO "junagarri" VALUES(3,185,'3:185','ہر جان موت کا مزه چکھنے والی ہے اور قیامت کے دن تم اپنے بدلے پورے پورے دیئے جاؤ گے، پس جو شخص آگ سے ہٹا دیا جائے اور جنت میں داخل کر دیا جائے بے شک وه کامیاب ہوگیا، اور دنیا کی زندگی تو صرف دھوکے کی جنس ہے.');
INSERT INTO "junagarri" VALUES(3,186,'3:186','یقیناً تمہارے مالوں اور جانوں سے تمہاری آزمائش کی جائے گی اور یہ بھی یقین ہے کہ تمہیں ان لوگوں کی جو تم سے پہلے کتاب دیئے گئے اور مشرکوں کی بہت سی دکھ دینے والی باتیں بھی سننی پڑیں گی اور اگر تم صبر کر لو اور پرہیزگاری اختیار کرو تو یقیناً یہ بہت بڑی ہمت کا کام ہے. ');
INSERT INTO "junagarri" VALUES(3,187,'3:187','اور اللہ تعالیٰ نے جب اہل کتاب سے عہد لیا کہ تم اسے سب لوگوں سے ضرور بیان کرو گے اور اسے چھپاؤ گے نہیں، تو پھر بھی ان لوگوں نے اس عہد کو اپنی پیٹھ پیچھے ڈال دیا اور اسے بہت کم قیمت پر بیچ ڈالا۔ ان کا یہ بیوپار بہت برا ہے. ');
INSERT INTO "junagarri" VALUES(3,188,'3:188','وه لوگ جو اپنے کرتوتوں پر خوش ہیں اور چاہتے ہیں کہ جو انہوں نے نہیں کیا اس پر بھی ان کی تعریفیں کی جائیں آپ انہیں عذاب سے چھٹکارا میں نہ سمجھئے ان کے لئے تو دردناک عذاب ہے. ');
INSERT INTO "junagarri" VALUES(3,189,'3:189','آسمانوں اور زمین کی بادشاہی اللہ ہی کے لئے ہے اور اللہ تعالیٰ ہر چیز پر قادر ہے.');
INSERT INTO "junagarri" VALUES(3,190,'3:190','آسمانوں اور زمین کی پیدائش میں اور رات دن کے ہیر پھیر میں یقیناً عقلمندوں کے لئے نشانیاں ہیں. ');
INSERT INTO "junagarri" VALUES(3,191,'3:191','جو اللہ تعالیٰ کا ذکر کھڑے اور بیٹھے اور اپنی کروٹوں پر لیٹے ہوئے کرتے ہیں اور آسمانوں وزمین کی پیدائش میں غوروفکر کرتے ہیں اور کہتے ہیں اے ہمارے پروردگار! تو نے یہ بے فائده نہیں بنایا، تو پاک ہے پس ہمیں آگ کے عذاب سے بچا لے. ');
INSERT INTO "junagarri" VALUES(3,192,'3:192','اے ہمارے پالنے والے! تو جسے جہنم میں ڈالے یقیناً تو نے اسے رسوا کیا، اور ظالموں کا مددگار کوئی نہیں.');
INSERT INTO "junagarri" VALUES(3,193,'3:193','اے ہمارے رب! ہم نے سنا کہ منادی کرنے والا بآواز بلند ایمان کی طرف بلا رہا ہے کہ لوگو! اپنے رب پر ایمان لاؤ، پس ہم ایمان لائے۔ یا الٰہی اب تو ہمارے گناه معاف فرما اور ہماری برائیاں ہم سے دور کر دے اور ہماری موت نیکوں کے ساتھ کر.');
INSERT INTO "junagarri" VALUES(3,194,'3:194','اے ہمارے پالنے والے معبود! ہمیں وه دے جس کا وعده تو نے ہم سے اپنے رسولوں کی زبانی کیا ہے اور ہمیں قیامت کے دن رسوا نہ کر، یقیناً تو وعده خلافی نہیں کرتا.');
INSERT INTO "junagarri" VALUES(3,195,'3:195','پس ان کے رب نے ان کی دعا قبول فرمالی کہ تم میں سے کسی کام کرنے والے کے کام کو خواه وه مرد ہو یا عورت میں ہرگز ضائع نہیں کرتا ، تم آپس میں ایک دوسرے کے ہم جنس ہو ، اس لئے وه لوگ جنہوں نے ہجرت کی اور اپنے گھروں سے نکال دیئے گئے اور جنہیں میری راه میں ایذا دی گئی اور جنہوں نے جہاد کیا اور شہید کئے گئے، میں ضرور ضرور ان کی برائیاں ان سے دور کر دوں گا اور بالیقین انہیں ان جنتوں میں لے جاؤں گا جن کے نیچے نہریں بہہ رہی ہیں، یہ ہے ثواب اللہ تعالیٰ کی طرف سے اور اللہ تعالیٰ ہی کے پاس بہترین ثواب ہے.');
INSERT INTO "junagarri" VALUES(3,196,'3:196','تجھے کافروں کا شہروں میں چلنا پھرنا فریب میں نہ ڈال دے. ');
INSERT INTO "junagarri" VALUES(3,197,'3:197','یہ تو بہت ہی تھوڑا فائده ہے ، اس کے بعد ان کا ٹھکانہ تو جہنم ہے اور وه بری جگہ ہے.');
INSERT INTO "junagarri" VALUES(3,198,'3:198','لیکن جو لوگ اپنے رب سے ڈرتے رہے ان کے لئے جنتیں ہیں جن کے نیچے نہریں جاری ہیں، ان میں وه ہمیشہ رہیں گے یہ مہمانی ہے اللہ کی طرف سے اور نیک کاروں کے لئے جو کچھ اللہ تعالیٰ کے پاس ہے وه بہت ہی بہتر ہے. ');
INSERT INTO "junagarri" VALUES(3,199,'3:199','یقیناً اہل کتاب میں سے بعض ایسے بھی ہیں جو اللہ تعالیٰ پر ایمان لاتے ہیں اور تمہاری طرف جو اتارا گیا ہے اور ان کی جانب جو نازل ہوا اس پر بھی، اللہ تعالیٰ سے ڈرتے ہیں اور اللہ تعالیٰ کی آیتوں کو تھوڑی تھوڑی قیمت پر بیچتے بھی نہیں ، ان کا بدلہ ان کے رب کے پاس ہے، یقیناً اللہ تعالیٰ جلد حساب لینے والا ہے.');
INSERT INTO "junagarri" VALUES(3,200,'3:200','اے ایمان والو! تم ثابت قدم رہو اور ایک دوسرے کو تھامے رکھو اور جہاد کے لئے تیار رہو اور اللہ تعالیٰ سے ڈرتے رہو تاکہ تم مراد کو پہنچو۔');
INSERT INTO "junagarri" VALUES(4,1,'4:1','اے لوگو! اپنے پروردگار سے ڈرو، جس نے تمہیں ایک جان سے پیدا کیا اور اسی سے اس کی بیوی کو پیدا کرکے ان دونوں سے بہت سے مرد اور عورتیں پھیلا دیں، اس اللہ سے ڈرو جس کے نام پر ایک دوسرے سے مانگتے ہو اور رشتے ناطے توڑنے سے بھی بچو بے شک اللہ تعالیٰ تم پر نگہبان ہے۔');
INSERT INTO "junagarri" VALUES(4,2,'4:2','اور یتیموں کو ان کے مال دے دو اور پاک اور حلال چیز کے بدلے ناپاک اور حرام چیز نہ لو، اور اپنے مالوں کے ساتھ ان کے مال ملا کر کھا نہ جاؤ، بے شک یہ بہت بڑا گناه ہے۔ ');
INSERT INTO "junagarri" VALUES(4,3,'4:3','اگر تمہیں ڈر ہو کہ یتیم لڑکیوں سے نکاح کرکے تم انصاف نہ رکھ سکو گے تو اور عورتوں میں سے جو بھی تمہیں اچھی لگیں تم ان سے نکاح کر لو، دو دو، تین تین، چار چار سے، لیکن اگر تمہیں برابری نہ کر سکنے کا خوف ہو تو ایک ہی کافی ہے یا تمہاری ملکیت کی لونڈی یہ زیاده قریب ہے، کہ (ایسا کرنے سے ناانصافی اور) ایک طرف جھک پڑنے سے بچ جاؤ۔ ');
INSERT INTO "junagarri" VALUES(4,4,'4:4','اور عورتوں کو ان کے مہر راضی خوشی دے دو، ہاں اگر وه خود اپنی خوشی سے کچھ مہر چھوڑ دیں تو اسے شوق سے خوش ہو کر کھا لو۔');
INSERT INTO "junagarri" VALUES(4,5,'4:5','بے عقل لوگوں کو اپنا مال نہ دے دو جس مال کو اللہ تعالیٰ نے تمہاری گزران کے قائم رکھنے کا ذریعہ بنایا ہے، ہاں انہیں اس مال سے کھلاؤ پلاؤ، پہناؤ، اوڑھاؤ اور انہیں معقولیت سے نرم بات کہو۔');
INSERT INTO "junagarri" VALUES(4,6,'4:6','اور یتیموں کو ان کے بالغ ہو جانے تک سدھارتے اور آزماتے رہو پھر اگر ان میں تم ہوشیاری اور حسن تدبیر پاؤ تو انہیں ان کے مال سونپ دو اور ان کے بڑے ہو جانے کے ڈر سے ان کے مالوں کو جلدی جلدی فضول خرچیوں میں تباه نہ کر دو، مال داروں کو چاہئے کہ (ان کے مال سے) بچتے رہیں، ہاں مسکین محتاج ہو تو دستور کے مطابق واجبی طور سے کھالے، پھر جب انہیں ان کے مال سونپو تو گواه بنا لو، دراصل حساب لینے والا اللہ تعالیٰ ہی کافی ہے۔ ');
INSERT INTO "junagarri" VALUES(4,7,'4:7','ماں باپ اور خویش واقارب کے ترکہ میں مردوں کا حصہ بھی ہے اور عورتوں کا بھی۔ (جو مال ماں باپ اور خویش واقارب چھوڑ مریں) خواه وه مال کم ہو یا زیاده (اس میں) حصہ مقرر کیا ہوا ہے۔ ');
INSERT INTO "junagarri" VALUES(4,8,'4:8','اور جب تقسیم کے وقت قرابت دار اور یتیم اور مسکین آجائیں تو تم اس میں سے تھوڑا بہت انہیں بھی دے دو اور ان سے نرمی سے بولو۔ ');
INSERT INTO "junagarri" VALUES(4,9,'4:9','اور چاہئے کہ وه اس بات سے ڈریں کہ اگر وه خود اپنے پیچھے (ننھے ننھے) ناتواں بچے چھوڑ جاتے جن کے ضائع ہو جانے کا اندیشہ رہتا ہے، (تو ان کی چاہت کیا ہوتی) پس اللہ تعالیٰ سے ڈر کر جچی تلی بات کہا کریں۔ ');
INSERT INTO "junagarri" VALUES(4,10,'4:10','جو لوگ ناحق ظلم سے یتیموں کا مال کھا جاتے ہیں، وه اپنے پیٹ میں آگ ہی بھر رہے ہیں اور عنقریب وه دوزخ میں جائیں گے۔');
INSERT INTO "junagarri" VALUES(4,11,'4:11','اللہ تعالیٰ تمہیں تمہاری اولاد کے بارے میں حکم کرتا ہے کہ ایک لڑکے کا حصہ دو لڑکیوں کے برابر ہے اور اگر صرف لڑکیاں ہی ہوں اور دو سے زیاده ہوں تو انہیں مال متروکہ کا دو تہائی ملے گا ۔ اور اگر ایک ہی لڑکی ہو تو اس کے لئے آدھا ہے اور میت کے ماں باپ میں سے ہر ایک کے لئے اس کے چھوڑے ہوئے مال کا چھٹا حصہ ہے، اگر اس (میت) کی اولاد ہو ، اور اگر اولاد نہ ہو اور ماں باپ وارث ہوتے ہوں تو اس کی ماں کے لئے تیسرا حصہ ہے ، ہاں اگر میت کے کئی بھائی ہوں تو پھر اس کی ماں کا چھٹا حصہ ہے ۔ یہ حصے اس وصیت (کی تکمیل) کے بعد ہیں جو مرنے والا کر گیا ہو یا ادائے قرض کے بعد، تمہارے باپ ہوں یا تمہارے بیٹے تمہیں نہیں معلوم کہ ان میں سے کون تمہیں نفع پہچانے میں زیاده قریب ہے ، یہ حصے اللہ تعالیٰ کی طرف سے مقرر کرده ہیں بے شک اللہ تعالیٰ پورے علم اور کامل حکمتوں والا ہے۔');
INSERT INTO "junagarri" VALUES(4,12,'4:12','تمہاری بیویاں جو کچھ چھوڑ مریں اور ان کی اولاد نہ ہو تو آدھوں آدھ تمہارا ہے اور اگر ان کی اولاد ہو تو ان کے چھوڑے ہوئے مال میں سے تمہارے لئے چوتھائی حصہ ہے ۔ اس وصیت کی ادائیگی کے بعد جو وه کر گئی ہوں یا قرض کے بعد۔ اور جو (ترکہ) تم چھوڑ جاؤ اس میں ان کے لئے چوتھائی ہے، اگر تمہاری اولاد نہ ہو اور اگر تمہاری اولاد ہو تو پھر انہیں تمہارے ترکہ کا آٹھواں حصہ ملے گا ، اس وصیت کے بعد جو تم کر گئے ہو اور قرض کی ادائیگی کے بعد۔ اور جن کی میراث لی جاتی ہے وه مرد یا عورت کلالہ ہو یعنی اس کا باپ بیٹا نہ ہو ۔ اور اس کا ایک بھائی یا ایک بہن ہو تو ان دونوں میں سے ہر ایک کا چھٹا حصہ ہے اور اس سے زیاده ہوں تو ایک تہائی میں سب شریک ہیں ، اس وصیت کے بعد جو کی جائے اور قرض کے بعد جب کہ اوروں کا نقصان نہ کیا گیا ہو یہ مقرر کیا ہوا اللہ تعالیٰ کی طرف سے ہے اور اللہ تعالیٰ دانا ہے بردبار۔');
INSERT INTO "junagarri" VALUES(4,13,'4:13','یہ حدیں اللہ تعالیٰ کی مقرر کی ہوئی ہیں اور جو اللہ تعالیٰ کی اور اس کے رسول اللہ ﴿صلی اللہ علیہ وسلم﴾ کی فرمانبرداری کرے گا اسے اللہ تعالیٰ جنتوں میں لے جائے گا جن کے نیچے نہریں بہہ رہی ہیں جن میں وه ہمیشہ رہیں گے اور یہ بہت بڑی کامیابی ہے۔');
INSERT INTO "junagarri" VALUES(4,14,'4:14','اور جو شخص اللہ تعالیٰ کی اور اس کے رسول اللہ ﴿صلی اللہ علیہ وسلم﴾ کی نافرمانی کرے اور اس کی مقرره حدوں سے آگے نکلے اسے وه جہنم میں ڈال دے گا جس میں وه ہمیشہ رہے گا، ایسوں ہی کے لئے رسوا کن عذاب ہے۔');
INSERT INTO "junagarri" VALUES(4,15,'4:15','تمہاری عورتوں میں سے جو بے حیائی کا کام کریں ان پر اپنے میں سے چار گواه طلب کرو، اگر وه گواہی دیں تو ان عورتوں کو گھروں میں قید رکھو، یہاں تک کہ موت ان کی عمریں پوری کردے ، یا اللہ تعالیٰ ان کے لئے کوئی اور راستہ نکالے۔ ');
INSERT INTO "junagarri" VALUES(4,16,'4:16','تم میں سے جو دو افراد ایسا کام کر لیں انہیں ایذا دو اگر وه توبہ اور اصلاح کر لیں تو ان سے منھ پھیر لو، بے شک اللہ تعالیٰ توبہ قبول کرنے والا اور رحم کرنے والا ہے۔');
INSERT INTO "junagarri" VALUES(4,17,'4:17','اللہ تعالیٰ صرف انہی لوگوں کی توبہ قبول فرماتا ہے جو بوجہ نادانی کوئی برائی کر گزریں پھر جلد اس سے باز آ جائیں اور توبہ کریں تو اللہ تعالیٰ بھی ان کی توبہ قبول کرتا ہے، اللہ تعالیٰ بڑے علم والا حکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(4,18,'4:18','ان کی توبہ نہیں جو برائیاں کرتے چلے جائیں یہاں تک کہ جب ان میں سے کسی کے پاس موت آجائے تو کہہ دے کہ میں نے اب توبہ کی ، اور ان کی توبہ بھی قبول نہیں جو کفر پر ہی مر جائیں، یہی لوگ ہیں جن کے لئے ہم نے المناک عذاب تیار کر رکھا ہے۔');
INSERT INTO "junagarri" VALUES(4,19,'4:19','ایمان والو! تمہیں حلال نہیں کہ زبردستی عورتوں کو ورثے میں لے بیٹھو انہیں اس لئے روک نہ رکھو کہ جو تم نے انہیں دے رکھا ہے، اس میں سے کچھ لے لو ہاں یہ اور بات ہے کہ وه کوئی کھلی برائی اور بے حیائی کریں ان کے ساتھ اچھے طریقے سے بودوباش رکھو، گو تم انہیں ناپسند کرو لیکن بہت ممکن ہے کہ تم کسی چیز کو برا جانو، اور اللہ تعالیٰ اس میں بہت ہی بھلائی کر دے۔ ');
INSERT INTO "junagarri" VALUES(4,20,'4:20','اور اگر تم ایک بیوی کی جگہ دوسری بیوی کرنا ہی چاہو اور ان میں سے کسی کو تم نے خزانہ کا خزانہ دے رکھا ہو، تو بھی اس میں سے کچھ نہ لو کیا تم اسے ناحق اور کھلا گناه ہوتے ہوئے بھی لے لو گے ۔');
INSERT INTO "junagarri" VALUES(4,21,'4:21','تم اسے کیسے لے لو گے حالانکہ تم ایک دوسرے سے مل چکے ہو اور ان عورتوں نے تم سے مضبوط عہد وپیمان لے رکھا ہے۔ ');
INSERT INTO "junagarri" VALUES(4,22,'4:22','اور ان عورتوں سے نکاح نہ کرو جن سے تمہارے باپوں نے نکاح کیا ہے مگر جو گزر چکا ہے، یہ بے حیائی کا کام اور بغض کا سبب ہے اور بڑی بری راه ہے۔');
INSERT INTO "junagarri" VALUES(4,23,'4:23','حرام کی گئیں تم پر تمہاری مائیں اور تمہاری لڑکیاں اور تمہاری بہنیں، تمہاری پھوپھیاں اور تمہاری خالائیں اور بھائی کی لڑکیاں اور بہن کی لڑکیاں اور تمہاری وه مائیں جنہوں نے تمہیں دودھ پلایا ہو اور تمہاری دودھ شریک بہنیں اور تمہاری ساس اور تمہاری وه پرورش کرده لڑکیاں جو تمہاری گود میں ہیں، تمہاری ان عورتوں سے جن سے تم دخول کر چکے ہو، ہاں اگر تم نے ان سے جماع نہ کیا ہو تو تم پر کوئی گناه نہیں اور تمہارے صلبی سگے بیٹوں کی بیویاں اور تمہارا دو بہنوں کا جمع کرنا ہاں جو گزر چکا سو گزر چکا، یقیناً اللہ تعالیٰ بخشنے والا مہربان ہے۔');
INSERT INTO "junagarri" VALUES(4,24,'4:24','اور (حرام کی گئیں) شوہر والی عورتیں مگر وه جو تمہاری ملکیت میں آجائیں ، اللہ تعالیٰ نے یہ احکام تم پر فرض کر دیئے ہیں، اور ان عورتوں کے سوا اور عورتیں تمہارے لئے حلال کی گئیں کہ اپنے مال کے مہر سے تم ان سے نکاح کرنا چاہو برے کام سے بچنے کے لئے نہ کہ شہوت رانی کرنے کے لئے ، اس لئے جن سے تم فائده اٹھاؤ انہیں ان کا مقرر کیا ہوا مہر دے دو ، اور مہر مقرر ہو جانے کے بعد تم آپس کی رضامندی سے جو طے کرلو اس میں تم پر کوئی گناه نہیں ، بے شک اللہ تعالیٰ علم والا حکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(4,25,'4:25','اور تم میں سے جس کسی کو آزاد مسلمان عورتوں سے نکاح کرنے کی پوری وسعت وطاقت نہ ہو تو وه مسلمان لونڈیوں سے جن کے تم مالک ہو (اپنا نکاح کر لے) اللہ تمہارے اعمال کو بخوبی جاننے والا ہے، تم سب آپس میں ایک ہی تو ہو، اس لئے ان کے مالکوں کی اجازت سے ان سے نکاح کر لو ، اور قاعده کے مطالق ان کے مہر ان کو دو، وه پاک دامن ہوں نہ کہ علانیہ بدکاری کرنے والیاں، نہ خفیہ آشنائی کرنے والیاں، پس جب یہ لونڈیاں نکاح میں آجائیں پھر اگر وه بے حیائی کا کام کریں تو انہیں آدھی سزا ہے اس سزا سے جو آزاد عورتوں کی ہے ۔ کنیزوں سے نکاح کا یہ حکم تم میں سے ان لوگوں کے لئے ہے جنہیں گناه اور تکلیف کا اندیشہ ہو اور تمہارا ضبط کرنا بہت بہتر ہے اور اللہ تعالیٰ بڑا بخشنے والا اور بڑی رحمت والا ہے۔ ');
INSERT INTO "junagarri" VALUES(4,26,'4:26','اللہ تعالیٰ چاہتا ہے کہ تمہارے واسطے خوب کھول کر بیان کرے اور تمہیں تم سے پہلے کے (نیک) لوگوں کی راه پر چلائے اور تمہاری توبہ قبول کرے، اور اللہ تعالیٰ جاننے والا حکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(4,27,'4:27','اور اللہ چاہتا ہے کہ تمہاری توبہ قبول کرے اور جو لوگ خواہشات کے پیرو ہیں وه چاہتے ہیں کہ تم اس سے بہت دور ہٹ جاؤ۔ ');
INSERT INTO "junagarri" VALUES(4,28,'4:28','اللہ چاہتا ہے کہ تم سے تخفیف کر دے کیونکہ انسان کمزور پیدا کیا گیا ہے۔ ');
INSERT INTO "junagarri" VALUES(4,29,'4:29','اے ایمان والو! اپنے آپس کے مال ناجائز طریقہ سے مت کھاؤ ، مگر یہ کہ تمہاری آپس کی رضا مندی سے ہو خرید وفروخت ، اور اپنے آپ کو قتل نہ کرو یقیناً اللہ تعالیٰ تم پر نہایت مہربان ہے۔');
INSERT INTO "junagarri" VALUES(4,30,'4:30','اور جو شخص یہ (نافرمانیاں) سرکشی اور ظلم سے کرے گا تو عنقریب ہم اس کو آگ میں داخل کریں گے اور یہ اللہ پرآسان ہے۔');
INSERT INTO "junagarri" VALUES(4,31,'4:31','اگر تم ان بڑے گناہوں سے بچتے رہوگے جن سے تم کو منع کیا جاتا ہے تو ہم تمہارے چھوٹے گناه دور کر دیں گے اور عزت وبزرگی کی جگہ داخل کریں گے۔');
INSERT INTO "junagarri" VALUES(4,32,'4:32','اوراس چیز کی آرزو نہ کرو جس کے باعث اللہ تعالیٰ نے تم میں سے بعض کو بعض پر بزرگی دی ہے، مردوں کا اس میں سے حصہ ہے جو انہوں نے کمایا اور عورتوں کے لئے ان میں سے حصہ ہے جو انہوں نے کمایا، اور اللہ تعالیٰ سے اس کا فضل مانگو ، یقیناً اللہ ہر چیز کا جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(4,33,'4:33','ماں باپ یا قرابت دار جو چھوڑ مریں اس کے وارث ہم نے ہر شخص کے مقرر کر دیئے ہیں اور جن سے تم نے اپنے ہاتھوں معاہده کیا ہے انہیں ان کا حصہ دو حقیقتاً اللہ تعالیٰ ہر چیز پر حاضر ہے۔');
INSERT INTO "junagarri" VALUES(4,34,'4:34','مرد عورتوں پر حاکم ہیں اس وجہ سے کہ اللہ تعالیٰ نے ایک کو دوسرے پر فضیلت دی ہے اور اس وجہ سے کہ مردوں نے اپنے مال خرچ کئے ہیں ، پس نیک فرمانبردار عورتین خاوند کی عدم موجودگی میں بہ حفاظت الٰہی نگہداشت رکھنے والیاں ہیں اور جن عورتوں کی نافرمانی اور بددماغی کا تمہیں خوف ہو انہیں نصیحت کرو اور انہیں الگ بستروں پر چھوڑ دو اور انہیں مار کی سزا دو پھر اگر وه تابعداری کریں تو ان پر کوئی راستہ تلاش نہ کرو ، بے شک اللہ تعالیٰ بڑی بلندی اور بڑائی والا ہے۔');
INSERT INTO "junagarri" VALUES(4,35,'4:35','اگر تمہیں میاں بیوی کے درمیان آپس کی ان بن کا خوف ہو تو ایک منصف مرد والوں میں سے اور ایک عورت کے گھر والوں میں سے مقرر کرو ، اگر یہ دونوں صلح کرانا چاہیں گے تو اللہ دونوں میں ملاپ کرا دے گا، یقیناً اللہ تعالیٰ پورے علم والا پوری خبر والا ہے۔');
INSERT INTO "junagarri" VALUES(4,36,'4:36','اور اللہ تعالیٰ کی عبادت کرو اور اس کے ساتھ کسی کو شریک نہ کرو اور ماں باپ کے ساتھ سلوک واحسان کرو اور رشتہ داروں سے اور یتیموں سے اور مسکینوں سے اور قرابت دار ہمسایہ سے اور اجنبی ہمسایہ سے اور پہلو کے ساتھی سے اور راه کے مسافر سے اور ان سے جن کے مالک تمہارے ہاتھ ہیں، (غلام کنیز) یقیناً اللہ تعالیٰ تکبر کرنے والوں اور شیخی خوروں کو پسند نہیں فرماتا۔ ');
INSERT INTO "junagarri" VALUES(4,37,'4:37','جو لوگ خود بخیلی کرتے ہیں اور دوسروں کو بھی بخیلی کرنے کو کہتے ہیں اور اللہ تعالیٰ نے جو اپنا فضل انہیں دے رکھا ہے اسے چھپا لیتے ہیں ہم نے ان کافروں کے لئے ذلت کی مار تیار کر رکھی ہے۔');
INSERT INTO "junagarri" VALUES(4,38,'4:38','اور جو لوگ اپنا مال لوگوں کے دکھاوے کے لئے خرچ کرتے ہیں اور اللہ تعالیٰ پر اور قیامت کے دن پر ایمان نہیں رکھتے اور جس کا ہم نشین اور ساتھی شیطان ہو ، وه بدترین ساتھی ہے۔');
INSERT INTO "junagarri" VALUES(4,39,'4:39','بھلا ان کا کیا نقصان تھا اگر یہ اللہ تعالیٰ پر اور قیامت کے دن پر ایمان لاتے اور اللہ تعالیٰ نے جو انہیں دے رکھا ہے اس میں خرچ کرتے، اللہ تعالیٰ انہیں خوب جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(4,40,'4:40','بے شک اللہ تعالیٰ ایک ذره برابر ظلم نہیں کرتا اور اگر نیکی ہو تو اسے دوگنی کر دیتا ہے اور خاص اپنے پاس سے بہت بڑا ثواب دیتا ہے۔');
INSERT INTO "junagarri" VALUES(4,41,'4:41','پس کیا حال ہوگا جس وقت کہ ہر امت میں سے ایک گواه ہم لائیں گے اور آپ کو ان لوگوں پر گواه بناکر لائیں گے۔ ');
INSERT INTO "junagarri" VALUES(4,42,'4:42','جس روز کافر اور رسول کے نافرمان آرزو کریں گے کہ کاش! انہیں زمین کے ساتھ ہموار کر دیا جاتا اور اللہ تعالیٰ سے کوئی بات نہ چھپا سکیں گے۔');
INSERT INTO "junagarri" VALUES(4,43,'4:43','اے ایمان والو! جب تم نشے میں مست ہو نماز کے قریب بھی نہ جاؤ ، جب تک کہ اپنی بات کو سمجھنے نہ لگو اور جنابت کی حالت میں جب تک کہ غسل نہ کر لو ، ہاں اگر راه چلتے گزر جانے والے ہو تو اور بات ہے اور اگر تم بیمار ہو یا سفر میں ہو یا تم میں سے کوئی قضائے حاجت سے آیا ہو یا تم نے عورتوں سے مباشرت کی ہو اور تمہیں پانی نہ ملے تو پاک مٹی کا قصد کرو اور اپنے منھ اور اپنے ہاتھ مل لو ۔ بے شک اللہ تعالیٰ معاف کرنے والا، بخشنے والا ہے۔');
INSERT INTO "junagarri" VALUES(4,44,'4:44','کیا تم نے انہیں نہیں دیکھا؟ جنہیں کتاب کا کچھ حصہ دیا گیا ہے، وه گمراہی خریدتے ہیں اور چاہتے ہیں کہ تم بھی راه سے بھٹک جاؤ۔');
INSERT INTO "junagarri" VALUES(4,45,'4:45','اللہ تعالیٰ تمہارے دشمنوں کو خوب جاننے والا ہے اور اللہ تعالیٰ کا دوست ہونا کافی ہے اور اللہ تعالیٰ کا مددگار ہونا بس ہے۔');
INSERT INTO "junagarri" VALUES(4,46,'4:46','بعض یہود کلمات کو ان کی ٹھیک جگہ سے ہیر پھیر کر دیتے ہیں اور کہتے ہیں کہ ہم نے سنا اور نافرمانی کی اور سن اس کے بغیر کہ تو سنا جائے اور ہماری رعایت کر! (لیکن اس کے کہنے میں) اپنی زبان کو پیچ دیتے ہیں اور دین میں طعنہ دیتے ہیں اور اگر یہ لوگ کہتے کہ ہم نے سنا اور ہم نے فرمانبرداری کی اور آپ سنئےاور ہمیں دیکھیئے تو یہ ان کے لئے بہت بہتر اور نہایت ہی مناسب تھا، لیکن اللہ تعالیٰ نے ان کے کفر کی وجہ سے انہیں لعنت کی ہے۔ پس یہ بہت ہی کم ایمان لاتے ہیں، ');
INSERT INTO "junagarri" VALUES(4,47,'4:47','اے اہل کتاب! جو کچھ ہم نے نازل فرمایا ہے جو اس کی بھی تصدیق کرنے والا ہے جو تمہارے پاس ہے، اس پر ایمان لاؤ اس سے پہلے کہ ہم چہرے بگاڑ دیں اور انہیں لوٹا کر پیٹھ کی طرف کر دیں ، یا ان پر لعنت بھیجیں جیسے ہم نے ہفتے کے دن والوں پر لعنت کی ہے ، اور اللہ تعالیٰ کا کام کیا گیا۔ ');
INSERT INTO "junagarri" VALUES(4,48,'4:48','یقیناً اللہ تعالیٰ اپنے ساتھ شریک کئے جانے کو نہیں بخشتا اور اس کے سوا جسے چاہے بخش دیتا ہے اور جو اللہ تعالیٰ کے ساتھ شریک مقرر کرے اس نے بہت بڑا گناه اور بہتان باندھا۔ ');
INSERT INTO "junagarri" VALUES(4,49,'4:49','کیا آپ نے انہیں نہیں دیکھا جو اپنی پاکیزگی اور ستائش خود کرتے ہیں؟ بلکہ اللہ تعالیٰ جسے چاہے پاکیزه کرتا ہے، کسی پر ایک دھاگے کے برابر ظلم نہ کیا جائے گا۔ ');
INSERT INTO "junagarri" VALUES(4,50,'4:50','دیکھو یہ لوگ اللہ تعالیٰ پر کس طرح جھوٹ باندھتے ہیں اور یہ (حرکت) صریح گناه ہونے کے لئے کافی ہے۔ ');
INSERT INTO "junagarri" VALUES(4,51,'4:51','کیا آپ نے انہیں نہیں دیکھا جنہیں کتاب کا کچھ حصہ ملا؟ جو بت کا اور باطل معبود کا اعتقاد رکھتے ہیں اور کافروں کے حق میں کہتے ہیں کہ یہ لوگ ایمان والوں سے زیاده راه راست پر ہیں۔ ');
INSERT INTO "junagarri" VALUES(4,52,'4:52','یہی وه لوگ ہیں جنہیں اللہ تعالیٰ نے لعنت کی ہے اور جسے اللہ تعالیٰ لعنت کر دے، تو اس کا کوئی مددگار نہ پائے گا۔');
INSERT INTO "junagarri" VALUES(4,53,'4:53','کیا ان کا کوئی حصہ سلطنت میں ہے؟ اگر ایسا ہو تو پھر یہ کسی کو ایک کھجور کی گٹھلی کے شگاف کے برابر بھی کچھ نہ دیں گے۔ ');
INSERT INTO "junagarri" VALUES(4,54,'4:54','یا یہ لوگوں سے حسد کرتے ہیں اس پر جو اللہ تعالیٰ نے اپنے فضل سے انہیں دیا ہے ، پس ہم نے تو آل ابراہیم کو کتاب اور حکمت بھی دی ہے اور بڑی سلطنت بھی عطا فرمائی ہے۔');
INSERT INTO "junagarri" VALUES(4,55,'4:55','پھر ان میں سے بعض نے تو اس کتاب کو مانا اور بعض اس سے رک گئے ، اور جہنم کا جلانا کافی ہے۔');
INSERT INTO "junagarri" VALUES(4,56,'4:56','جن لوگوں نے ہماری آیتوں سے کفر کیا، انہیں ہم یقیناً آگ میں ڈال دیں گے جب ان کی کھالیں پک جائیں گی ہم ان کے سوا اور کھالیں بدل دیں گے تاکہ وه عذاب چکھتے رہیں ، یقیناً اللہ تعالیٰ غالب حکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(4,57,'4:57','اور جو لوگ ایمان لائے اور شائستہ اعمال کئے ہم عنقریب انہیں ان جنتوں میں لے جائیں گے جن کے نیچے نہریں بہہ رہی ہیں، جن میں وه ہمیشہ ہمیشہ رہیں گے، ان کے لئے وہاں صاف ستھری بیویاں ہوں گی اور ہم انہیں گھنی چھاؤں (اور پوری راحت) میں لے جائیں گے۔ ');
INSERT INTO "junagarri" VALUES(4,58,'4:58','اللہ تعالیٰ تمہیں تاکیدی حکم دیتا ہے کہ امانت والوں کی امانتیں انہیں پہنچاؤ! اور جب لوگوں کا فیصلہ کرو تو عدل وانصاف سے فیصلہ کرو! یقیناً وه بہتر چیز ہے جس کی نصیحت تمہیں اللہ تعالیٰ کر رہا ہے ۔ بے شک اللہ تعالیٰ سنتا ہے، دیکھتا ہے۔');
INSERT INTO "junagarri" VALUES(4,59,'4:59','اے ایمان والو! فرمانبرداری کرو اللہ تعالیٰ کی اور فرمانبرداری کرو رسول (صلی اللہ علیہ وسلم) کی اور تم میں سے اختیار والوں کی ۔ پھر اگر کسی چیز میں اختلاف کرو تو اسے لوٹاؤ، اللہ تعالیٰ کی طرف اور رسول کی طرف، اگر تمہیں اللہ تعالیٰ پر اور قیامت کے دن پر ایمان ہے۔ یہ بہت بہتر ہے اور باعتبار انجام کے بہت اچھا ہے ۔');
INSERT INTO "junagarri" VALUES(4,60,'4:60','کیا آپ نے انہیں نہیں دیکھا؟ جن کا دعویٰ تو یہ ہے کہ جو کچھ آپ پر اور جو کچھ آپ سے پہلے اتارا گیا ہے اس پر ان کا ایمان ہے، لیکن وه اپنے فیصلے غیر اللہ کی طرف لے جانا چاہتے ہیں حالانکہ انہین حکم دیا گیا ہے کہ شیطان کا انکار کریں، شیطان تو یہ چاہتا ہے کہ انہیں بہکا کر دور ڈال دے۔');
INSERT INTO "junagarri" VALUES(4,61,'4:61','ان سے جب کبھی کہا جائے کہ اللہ تعالیٰ کے نازل کرده کلام کی اور رسول (صلی اللہ علیہ وسلم) کی طرف آؤ تو آپ دیکھ لیں گے کہ یہ منافق آپ سے منھ پھیر کر رکے جاتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(4,62,'4:62','پھر کیا بات ہے کہ جب ان پر ان کے کرتوت کے باعث کوئی مصیبت آپڑتی ہے تو پھر یہ آپ کے پاس آکر اللہ تعالیٰ کی قسمیں کھاتے ہیں کہ ہمارا اراده تو صرف بھلائی اور میل ملاپ ہی کا تھا۔ ');
INSERT INTO "junagarri" VALUES(4,63,'4:63','یہ وه لوگ ہیں کہ ان کے دلوں کا بھید اللہ تعالیٰ پر بخوبی روشن ہے، آپ ان سے چشم پوشی کیجئے، انہیں نصیحت کرتے رہیئے اور انہیں وه بات کہئے! جو ان کے دلوں میں گھر کرنے والی ہو۔ ');
INSERT INTO "junagarri" VALUES(4,64,'4:64','ہم نے ہر ہر رسول کو صرف اسی لئے بھیجا کہ اللہ تعالیٰ کے حکم سے اس کی فرمانبرداری کی جائے اور اگر یہ لوگ جب انہوں نے اپنی جانوں پر ظلم کیا تھا، تیرے پاس آ جاتے اور اللہ تعالیٰ سے استغفار کرتے اور رسول بھی ان کے لئے استغفار کرتے ، تو یقیناً یہ لوگ اللہ تعالیٰ کو معاف کرنے والا مہربان پاتے۔');
INSERT INTO "junagarri" VALUES(4,65,'4:65','سو قسم ہے تیرے پروردگار کی! یہ مومن نہیں ہو سکتے، جب تک کہ تمام آپس کے اختلاف میں آپ کو حاکم نہ مان لیں، پھر جو فیصلے آپ ان میں کر دیں ان سے اپنے دل میں اور کسی طرح کی تنگی اور ناخوشی نہ پائیں اور فرمانبرداری کے ساتھ قبول کر لیں۔ ');
INSERT INTO "junagarri" VALUES(4,66,'4:66','اور اگر ہم ان پر یہ فرض کر دیتے کہ اپنی جانوں کو قتل کر ڈالو! یا اپنے گھروں سے نکل جاؤ! تو اسے ان میں سے بہت ہی کم لوگ بجا لاتے اور اگر یہ وہی کریں جس کی انہیں نصیحت کی جاتی ہے تو یقیناً یہی ان کے لئے بہتر اور بہت زیاده مضبوطی والا ہے۔ ');
INSERT INTO "junagarri" VALUES(4,67,'4:67','اور تب تو انہیں ہم اپنے پاس سے بڑا ثواب دیں۔');
INSERT INTO "junagarri" VALUES(4,68,'4:68','اور یقیناً انہیں راه راست دکھا دیں۔');
INSERT INTO "junagarri" VALUES(4,69,'4:69','اور جو بھی اللہ تعالیٰ کی اور رسول (صلی اللہ علیہ وسلم) کی فرمانبرداری کرے، وه ان لوگوں کے ساتھ ہوگا جن پر اللہ تعالیٰ نے انعام کیا ہے، جیسے نبی اور صدیق اور شہید اور نیک لوگ، یہ بہترین رفیق ہیں۔ ');
INSERT INTO "junagarri" VALUES(4,70,'4:70','یہ فضل اللہ تعالیٰ کی طرف سے ہے اور کافی ہے اللہ تعالیٰ جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(4,71,'4:71','اے مسلمانو! اپنے بچاؤ کا سامان لے لو پھر گروه گروه بن کر کوچ کرو یا سب کے سب اکٹھے ہو کر نکل کھڑے ہو!');
INSERT INTO "junagarri" VALUES(4,72,'4:72','اور یقیناً تم میں بعض وه بھی ہیں جو پس وپیش کرتے ہیں ، پھر اگر تمہیں کوئی نقصان ہوتا ہے تو وه کہتے ہیں کہ اللہ تعالیٰ نے مجھ پر بڑا فضل کیا کہ میں ان کے ساتھ موجود نہ تھا۔');
INSERT INTO "junagarri" VALUES(4,73,'4:73','اور اگر تمہیں اللہ تعالیٰ کا کوئی فضل مل جائے تو اس طرح کہ گویا تم میں ان میں دوستی تھی ہی نہیں ، کہتے ہیں کاش! میں بھی ان کے ہمراه ہوتا تو بڑی کامیابی کو پہنچتا۔ ');
INSERT INTO "junagarri" VALUES(4,74,'4:74','پس جو لوگ دنیا کی زندگی کو آخرت کے بدلے بیچ چکے ہیں ، انہیں اللہ تعالیٰ کی راه میں جہاد کرنا چاہئے اور جو شخص اللہ تعالیٰ کی راه میں جہاد کرتے ہوئے شہادت پا لے یا غالب آجائے، یقیناً ہم اسے بہت بڑا ثواب عنایت فرمائیں گے۔');
INSERT INTO "junagarri" VALUES(4,75,'4:75','بھلا کیا وجہ ہے کہ تم اللہ کی راه میں اور ان ناتواں مردوں، عورتوں اور ننھے ننھے بچوں کے چھٹکارے کے لئے جہاد نہ کرو؟ جو یوں دعائیں مانگ رہے ہیں کہ اے ہمارے پروردگار! ان ظالموں کی بستی سے ہمیں نجات دے اور ہمارے لئے خود اپنے پاس سے حمایتی مقرر کر دے اور ہمارے لئے خاص اپنے پاس سے مددگار بنا۔ ');
INSERT INTO "junagarri" VALUES(4,76,'4:76','جو لوگ ایمان لائے ہیں وه تو اللہ تعالیٰ کی راه میں جہاد کرتے ہیں اور جن لوگوں نے کفر کیا ہے، وه اللہ تعالیٰ کے سوا اوروں کی راه میں لڑتے ہیں پس تم شیطان کے دوستوں سے جنگ کرو! یقین مانو کہ شیطانی حیلہ (بالکل بودا اور) سخت کمزور ہے۔');
INSERT INTO "junagarri" VALUES(4,77,'4:77','کیا تم نے انہیں نہیں دیکھا جنہیں حکم کیا گیا تھا کہ اپنے ہاتھوں کو روکے رکھو اور نمازیں پڑھتے رہو اور زکوٰة ادا کرتے رہو۔ پھر جب انہیں جہاد کا حکم دیا گیا تو اسی وقت ان کی ایک جماعت لوگوں سے اس قدر ڈرنے لگی جیسے اللہ تعالیٰ کا ڈر ہو، بلکہ اس سے بھی زیاده، اور کہنے لگے اے ہمارے رب! تو نے ہم پر جہاد کیوں فرض کر دیا؟ کیوں ہمیں تھوڑی سی زندگی اور نہ جینے دی؟ آپ کہہ دیجئے کہ دنیا کی سودمندی تو بہت ہی کم ہے اور پرہیزگاروں کے لئے تو آخرت ہی بہتر ہے اور تم پر ایک دھاگے کے برابر بھی ستم روا نہ رکھا جائے گا۔');
INSERT INTO "junagarri" VALUES(4,78,'4:78','تم جہاں کہیں بھی ہو موت تمہیں آپکڑے گی، گو تم مضبوط قلعوں میں ہو ، اور اگر انہیں کوئی بھلائی ملتی ہے تو کہتے ہیں کہ یہ اللہ تعالیٰ کی طرف سے ہے اور اگر کوئی برائی پہنچتی ہے تو کہہ اٹھتے ہیں کہ یہ تیری طرف سے ہے ۔ انہیں کہہ دو کہ یہ سب کچھ اللہ تعالیٰ کی طرف سے ہے۔ انہیں کیا ہوگیا ہے کہ کوئی بات سمجھنے کے بھی قریب نہیں۔ ');
INSERT INTO "junagarri" VALUES(4,79,'4:79','تجھے جو بھلائی ملتی ہے وه اللہ تعالیٰ کی طرف سے ہے اور جو برائی پہنچتی ہے وه تیرے اپنے نفس کی طرف سے ہے ، ہم نے تجھے تمام لوگوں کو پیغام پہنچانے والا بنا کر بھیجا ہے اور اللہ تعالیٰ گواه کافی ہے۔');
INSERT INTO "junagarri" VALUES(4,80,'4:80','اس رسول (صلی اللہ علیہ وسلم) کی جو اطاعت کرے اسی نے اللہ تعالیٰ کی فرمانبرداری کی اور جو منھ پھیر لے تو ہم نے آپ کو کچھ ان پر نگہبان بنا کر نہیں بھیجا۔');
INSERT INTO "junagarri" VALUES(4,81,'4:81','یہ کہتے تو ہیں کہ اطاعت ہے، پھر جب آپ کے پاس سے اٹھ کر باہر نکلتے ہیں تو ان میں کی ایک جماعت، جو بات آپ نے یا اس نے کہی ہے اس کے خلاف راتوں کو مشورے کرتی ہے ، ان کی راتوں کی بات چیت اللہ لکھ رہا ہے، تو آپ ان سے منھ پھیر لیں اور اللہ پر بھروسہ رکھیں، اللہ تعالیٰ کافی کارساز ہے۔');
INSERT INTO "junagarri" VALUES(4,82,'4:82','کیا یہ لوگ قرآن میں غور نہیں کرتے؟ اگر یہ اللہ تعالیٰ کے سوا کسی اور کی طرف سے ہوتا تو یقیناً اس میں بہت کچھ اختلاف پاتے۔ ');
INSERT INTO "junagarri" VALUES(4,83,'4:83','جہاں انہیں کوئی خبر امن کی یا خوف کی ملی انہوں نے اسے مشہور کرنا شروع کر دیا، حالانکہ اگر یہ لوگ اسے رسول (صلی اللہ علیہ وسلم) کے اور اپنے میں سے ایسی باتوں کی تہہ تک پہنچنے والوں کے حوالے کر دیتے، تو اس کی حقیقت وه لوگ معلوم کر لیتے جو نتیجہ اخذ کرتے ہیں اور اگر اللہ تعالیٰ کا فضل اور اس کی رحمت تم پر نہ ہوتی تو معدودے چند کے علاوه تم سب شیطان کے پیروکار بن جاتے۔');
INSERT INTO "junagarri" VALUES(4,84,'4:84','تو اللہ تعالیٰ کی راه میں جہاد کرتا ره، تجھے صرف تیری ذات کی نسبت حکم دیا جاتا ہے، ہاں ایمان والوں کو رغبت دلاتا ره، بہت ممکن ہے کہ اللہ تعالیٰ کافروں کی جنگ کو روک دے اور اللہ تعالیٰ سخت قوت والا ہے اور سزادینے میں بھی سخت ہے۔');
INSERT INTO "junagarri" VALUES(4,85,'4:85','جو شخص کسی نیکی یا بھلے کام کی سفارش کرے، اسے بھی اس کا کچھ حصہ ملے گا اور جو برائی اور بدی کی سفارش کرے اس کے لئے بھی اس میں سے ایک حصہ ہے، اور اللہ تعالیٰ ہر چیز پر قدرت رکھنے والا ہے۔');
INSERT INTO "junagarri" VALUES(4,86,'4:86','اور جب تمہیں سلام کیا جائے تو تم اس سے اچھا جواب دو یا انہی الفاظ کو لوٹا دو ، بے شبہ اللہ تعالیٰ ہر چیز کا حساب لینے والا ہے۔');
INSERT INTO "junagarri" VALUES(4,87,'4:87','اللہ وه ہے جس کے سوا کوئی معبود (برحق) نہیں وه تم سب کو یقیناً قیامت کے دن جمع کرے گا، جس کے (آنے) میں کوئی شک نہیں، اللہ تعالیٰ سے زیاده سچی بات والا اور کون ہوگا۔');
INSERT INTO "junagarri" VALUES(4,88,'4:88','تمہیں کیا ہوگیا ہے؟ کہ منافقوں کے بارے میں دو گروه ہو رہے ہو؟ انہیں تو ان کے اعمال کی وجہ سے اللہ تعالیٰ نے اوندھا کر دیا ہے ۔ اب کیا تم یہ منصوبے باندھ رہے ہو کہ اللہ تعالیٰ کے گمراه کئے ہوؤں کو تم راه راست پر لاکھڑا کرو، جسے اللہ تعالیٰ راه بھلا دے تو ہرگز اس کے لئے کوئی راه نہ پائے گا۔ ');
INSERT INTO "junagarri" VALUES(4,89,'4:89','ان کی تو چاہت ہے کہ جس طرح کے کافر وه ہیں تو بھی ان کی طرح کفر کرنے لگو اور پھر سب یکساں ہو جاؤ، پس جب تک یہ اسلام کی خاطر وطن نہ چھوڑیں ان میں سے کسی کو حقیقی دوست نہ بناؤ ، پھر اگر یہ منھ پھیر لیں تو انہیں پکڑو اور قتل کرو جہاں بھی یہ ہاتھ لگ جائیں ، خبردار! ان میں سے کسی کو اپنا رفیق اور مددگار نہ سمجھ بیٹھنا۔');
INSERT INTO "junagarri" VALUES(4,90,'4:90','سوائے ان کے جو اس قوم سے تعلق رکھتے ہوں جن سے تمہارا معاہده ہو چکا ہے یا جو تمہارے پاس اس حالت میں آئیں کہ تم سے جنگ کرنے سے بھی تنگ دل ہیں اور اپنی قوم سے بھی جنگ کرنے سے تنگ دل ہیں اور اگر اللہ تعالیٰ چاہتا تو انہیں تم پر مسلط کر دیتا اور وه تم سے یقیناً جنگ کرتے ، پس اگر یہ لوگ تم سے کناره کشی اختیار کر لیں اور تم سے لڑائی نہ کریں اور تمہاری جانب صلح کا پیغام ڈالیں ، تو اللہ تعالیٰ نے تمہارے لئے ان پر کوئی راه لڑائی کی نہیں کی۔');
INSERT INTO "junagarri" VALUES(4,91,'4:91','تم کچھ اور لوگوں کو ایسا بھی پاؤ گے جن کی (بظاہر) چاہت ہے کہ تم سے بھی امن میں رہیں۔ اور اپنی قوم سے بھی امن میں رہیں (لیکن) جب کبھی فتنہ انگیزی کی طرف لوٹائے جاتے ہیں تو اوندھے منھ اس میں ڈال دیئے جاتے ہیں، پس اگر یہ لوگ تم سے کناره کشی نہ کریں اور تم سے صلح کا سلسلہ جنبانی نہ کریں اور اپنے ہاتھ نہ روک لیں ، تو انہیں پکڑو اور مار ڈالو جہاں کہیں بھی پالو! یہی وه ہیں جن پر ہم نے تمہیں ظاہر حجت عنایت فرمائی ہے۔ ');
INSERT INTO "junagarri" VALUES(4,92,'4:92','کسی مومن کو دوسرے مومن کا قتل کر دینا زیبا نہیں مگر غلطی سے ہو جائے (تو اور بات ہے)، جو شخص کسی مسلمان کو بلا قصد مار ڈالے، اس پر ایک مسلمان غلام کی گردن آزاد کرنا اور مقتول کے عزیزوں کو خون بہا پہنچانا ہے ۔ ہاں یہ اور بات ہے کہ وه لوگ بطور صدقہ معاف کر دیں اور اگر مقتول تمہاری دشمن قوم کا ہو اور ہو وه مسلمان، تو صرف ایک مومن غلام کی گردن آزاد کرنی لازمی ہے ۔ اور اگر مقتول اس قوم سے ہو کہ تم میں اور ان میں عہد و پیماں ہے تو خون بہا لازم ہے، جو اس کے کنبے والوں کو پہنچایا جائے اور ایک مسلمان غلام کا آزاد کرنا بھی (ضروری ہے) ، پس جو نہ پائے اس کے ذمے دو مہینے کے لگاتار روزے ہیں ، اللہ تعالیٰ سے بخشوانے کے لئےاور اللہ تعالیٰ بخوبی جاننے والا اور حکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(4,93,'4:93','اور جو کوئی کسی مومن کو قصداً قتل کر ڈالے، اس کی سزا دوزخ ہے جس میں وه ہمیشہ رہے گا، اس پر اللہ تعالیٰ کا غضب ہے ، اسے اللہ تعالیٰ نے لعنت کی ہے اور اس کے لئے بڑا عذاب تیار رکھا ہے۔ ');
INSERT INTO "junagarri" VALUES(4,94,'4:94','اے ایمان والو! جب تم اللہ کی راه میں جا رہے ہو تو تحقیق کر لیا کرو اور جو تم سے سلام علیک کرے تو اسے یہ نہ کہہ دو کہ تو ایمان والا نہیں ۔ تم دنیاوی زندگی کے اسباب کی تلاش میں ہو تو اللہ تعالیٰ کے پاس بہت سی غنیمتیں ہیں ۔ پہلے تم بھی ایسے ہی تھے، پھر اللہ تعالیٰ نے تم پر احسان کیا لہٰذا تم ضرور تحقیق وتفتیش کر لیا کرو، بے شک اللہ تعالیٰ تمہارے اعمال سے باخبر ہے۔');
INSERT INTO "junagarri" VALUES(4,95,'4:95','اپنی جانوں اور مالوں سے اللہ کی راه میں جہاد کرنے والے مومن اور بغیر عذر کے بیٹھ رہنے والے مومن برابر نہیں ، اپنے مالوں اور اپنی جانوں سے جہاد کرنے والوں کو بیٹھ رہنے والوں پر اللہ تعالیٰ نے درجوں میں بہت فضیلت دے رکھی ہے اور یوں تو اللہ تعالیٰ نے ہر ایک کو خوبی اور اچھائی کا وعده دیا ہے لیکن مجاہدین کو بیٹھ رہنے والوں پر بہت بڑے اجر کی فضیلت دے رکھی ہے۔');
INSERT INTO "junagarri" VALUES(4,96,'4:96','اپنی طرف سے مرتبے کی بھی اور بخشش کی بھی اور رحمت کی بھی اور اللہ تعالیٰ بخشش کرنے والا اور رحم کرنے والا ہے۔');
INSERT INTO "junagarri" VALUES(4,97,'4:97','جو لوگ اپنی جانوں پر ظلم کرنے والے ہیں جب فرشتے ان کی روح قبض کرتے ہیں تو پوچھتے ہیں، تم کس حال میں تھے؟ یہ جواب دیتے ہیں کہ ہم اپنی جگہ کمزور اور مغلوب تھے ۔ فرشتے کہتے ہیں کیا اللہ تعالیٰ کی زمین کشاده نہ تھی کہ تم ہجرت کر جاتے؟ یہی لوگ ہیں جن کا ٹھکانا دوزخ ہے اور وه پہنچنے کی بری جگہ ہے۔');
INSERT INTO "junagarri" VALUES(4,98,'4:98','مگر جو مرد عورتیں اور بچے بے بس ہیں جنہیں نہ تو کسی چارہٴ کار کی طاقت اور نہ کسی راستے کا علم ہے۔ ');
INSERT INTO "junagarri" VALUES(4,99,'4:99','بہت ممکن ہے کہ اللہ تعالیٰ ان سے درگزر کرے، اللہ تعالیٰ درگزر کرنے والا اور معاف فرمانے والا ہے۔');
INSERT INTO "junagarri" VALUES(4,100,'4:100','جو کوئی اللہ کی راه میں وطن کو چھوڑے گا، وه زمین میں بہت سی قیام کی جگہیں بھی پائے گا اور کشادگی بھی ، اور جو کوئی اپنے گھر سے اللہ تعالیٰ اور اس کے رسول (صلی اللہ علیہ وسلم) کی طرف نکل کھڑا ہوا، پھر اسے موت نے آ پکڑا تو بھی یقیناً اس کا اجر اللہ تعالیٰ کے ذمہ ثابت ہو گیا ، اور اللہ تعالیٰ بڑا بخشنے والا مہربان ہے۔');
INSERT INTO "junagarri" VALUES(4,101,'4:101','جب تم سفر میں جا رہے ہو تو تم پر نمازوں کے قصر کرنے میں کوئی گناه نہیں، اگر تمہیں ڈر ہو کہ کافر تمہیں ستائیں گے ، یقیناً کافر تمہارے کھلے دشمن ہیں۔');
INSERT INTO "junagarri" VALUES(4,102,'4:102','جب تم ان میں ہو اور ان کے لئے نماز کھڑی کرو تو چاہئے کہ ان کی ایک جماعت تمہارے ساتھ اپنے ہتھیار لئے کھڑی ہو، پھر جب یہ سجده کر چکیں تو یہ ہٹ کر تمہارے پیچھے آجائیں اور وه دوسری جماعت جس نے نماز نہیں پڑھی وه آجائے اور تیرے ساتھ نماز ادا کرے اور اپنا بچاؤ اور اپنے ہتھیار لئے رہے، کافر چاہتے ہیں کہ کسی طرح تم اپنے ہتھیاروں اور اپنے سامان سے بے خبر ہو جاؤ تو وہ تم پر اچانک دھاوا بول دیں ، ہاں اپنے ہتھیار اتار رکھنے میں اس وقت تم پر کوئی گناه نہیں جب کہ تمہیں تکلیف ہو یا بوجہ بارش کے یا بسبب بیمار ہو جانے کے اور اپنے بچاؤ کی چیزیں ساتھ لئے رہو، یقیناً اللہ تعالیٰ نے منکروں کے لئے ذلت کی مار تیار کر رکھی ہے۔');
INSERT INTO "junagarri" VALUES(4,103,'4:103','پھر جب تم نماز ادا کر چکو تو اٹھتے بیٹھتے اور لیٹے اللہ تعالیٰ کا ذکر کرتے رہو اور جب اطمینان پاؤ تو نماز قائم کرو! یقیناً نماز مومنوں پر مقرره وقتوں پر فرض ہے۔ ');
INSERT INTO "junagarri" VALUES(4,104,'4:104','ان لوگوں کا پیچھا کرنے سے ہارے دل ہو کر بیٹھ نہ رہو! اگر تمہیں بے آرامی ہوتی ہے تو انہیں بھی تمہاری طرح بے آرامی ہوتی ہے اور تم اللہ تعالیٰ سے وه امیدیں رکھتے ہو، جو امیدیں انہیں نہیں ، اور اللہ تعالیٰ دانا اور حکیم ہے۔');
INSERT INTO "junagarri" VALUES(4,105,'4:105','یقیناً ہم نے تمہاری طرف حق کے ساتھ اپنی کتاب نازل فرمائی ہے تاکہ تم لوگوں میں اس چیز کے مطابق فیصلہ کرو جس سے اللہ نے تم کو شناسا کیا ہے اور خیانت کرنے والوں کے حمایتی نہ بنو۔');
INSERT INTO "junagarri" VALUES(4,106,'4:106','اور اللہ تعالیٰ سے بخشش مانگو! بے شک اللہ تعالیٰ بخشش کرنے والا، مہربانی کرنے والا ہے>');
INSERT INTO "junagarri" VALUES(4,107,'4:107','اور ان کی طرف سے جھگڑا نہ کرو جو خود اپنی ہی خیانت کرتے ہیں، یقیناً دغا باز گنہگار اللہ تعالیٰ کو اچھا نہیں لگتا۔');
INSERT INTO "junagarri" VALUES(4,108,'4:108','وه لوگوں سے تو چھپ جاتے ہیں، (لیکن) اللہ تعالیٰ سے نہیں چھپ سکتے، وه راتوں کے وقت جب کہ اللہ کی ناپسندیده باتوں کے خفیہ مشورے کرتے ہیں اس وقت بھی اللہ ان کے پاس ہوتا ہے، ان کے تمام اعمال کو وه گھیرے ہوئے ہے۔');
INSERT INTO "junagarri" VALUES(4,109,'4:109','ہاں تو یہ ہو تم لوگ کہ دنیا میں تم نے ان کی حمایت کی لیکن اللہ تعالیٰ کے سامنے قیامت کے دن ان کی حمایت کون کرے گا؟ اور وه کون ہے جو ان کا وکیل بن کر کھڑا ہو سکے گا؟ ');
INSERT INTO "junagarri" VALUES(4,110,'4:110','جو شخص کوئی برائی کرے یا اپنی جان پر ظلم کرے پھر اللہ سے استغفار کرے تو وه اللہ کو بخشنے والا، مہربانی کرنے والا پائے گا۔');
INSERT INTO "junagarri" VALUES(4,111,'4:111','اور جو گناه کرتا ہے اس کا بوجھ اسی پر ہے اور اللہ بخوبی جاننے والا اور پوری حکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(4,112,'4:112','اور جو شخص کوئی گناه یا خطا کر کے کسی بے گناه کے ذمے تھوپ دے، اس نے بہت بڑا بہتان اٹھایا اور کھلا گناه کیا۔ ');
INSERT INTO "junagarri" VALUES(4,113,'4:113','اگر اللہ تعالیٰ کا فضل ورحم تجھ پر نہ ہوتا تو ان کی ایک جماعت نے تو تجھے بہکانے کا قصد کر ہی لیا تھا ، مگر دراصل یہ اپنے آپ کو ہی گمراه کرتے ہیں، یہ تیرا کچھ نہیں بگاڑ سکتے، اللہ تعالیٰ نے تجھ پر کتاب وحکمت اتاری ہے اور تجھے وه سکھایا ہے جسے تو نہیں جانتا تھا اور اللہ تعالیٰ کا تجھ پر بڑا بھاری فضل ہے۔');
INSERT INTO "junagarri" VALUES(4,114,'4:114','ان کے اکثر خفیہ مشوروں میں کوئی خیر نہیں ، ہاں! بھلائی اس کے مشورے میں ہے جو خیرات کا یا نیک بات کا یا لوگوں میں صلح کرانے کا حکم کرے اور جو شخص صرف اللہ تعالیٰ کی رضامندی حاصل کرنے کے ارادے سے یہ کام کرے اسے ہم یقیناً بہت بڑا ثواب دیں گے۔');
INSERT INTO "junagarri" VALUES(4,115,'4:115','جو شخص باوجود راه ہدایت کے واضح ہو جانے کے بھی رسول (صلی اللہ علیہ وسلم) کا خلاف کرے اور تمام مومنوں کی راه چھوڑ کر چلے، ہم اسے ادھر ہی متوجہ کردیں گے جدھر وه خود متوجہ ہو اور دوزخ میں ڈال دیں گے ، وه پہنچنے کی بہت ہی بری جگہ ہے۔');
INSERT INTO "junagarri" VALUES(4,116,'4:116','اسے اللہ تعالیٰ قطعاً نہ بخشے گا کہ اس کے ساتھ شرک مقرر کیا جائے، ہاں شرک کے علاوه گناه جس کے چاہے معاف فرما دیتا ہے اور اللہ کے ساتھ شریک کرنے والا بہت دور کی گمراہی میں جا پڑا۔');
INSERT INTO "junagarri" VALUES(4,117,'4:117','یہ تو اللہ تعالیٰ کو چھوڑ کر صرف عورتوں کو پکارتے ہیں اور دراصل یہ صرف سرکش شیطان کو پوجتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(4,118,'4:118','جسے اللہ نے لعنت کی ہے اور اس نے بیڑا اٹھایا ہے کہ تیرے بندوں میں سے میں مقرر شده حصہ لے کر رہوں گا۔ ');
INSERT INTO "junagarri" VALUES(4,119,'4:119','اور انہیں راه سے بہکاتا رہوں گا اور باطل امیدیں دلاتا رہوں گا اور انہیں سکھاؤں گا کہ جانوروں کی کان چیر دیں ، اور ان سے کہوں گا کہ اللہ تعالیٰ کی بنائی ہوئی صورت کو بگاڑ دیں ، سنو! جو شخص اللہ کو چھوڑ کرشیطان کو اپنا رفیق بنائے گا وه صریح نقصان میں ڈوبے گا۔');
INSERT INTO "junagarri" VALUES(4,120,'4:120','وه ان سے زبانی وعدے کرتا رہے گا، اور سبز باغ دکھاتا رہے گا، (مگر یاد رکھو!) شیطان کے جو وعدے ان سے ہیں وه سراسر فریب کاریاں ہیں۔');
INSERT INTO "junagarri" VALUES(4,121,'4:121','یہ وه لوگ ہیں جن کی جگہ جہنم ہے، جہاں سے انہیں چھٹکارا نہ ملے گا۔');
INSERT INTO "junagarri" VALUES(4,122,'4:122','اور جو ایمان لائیں اور بھلے کام کریں ہم انہیں ان جنتوں میں لے جائیں گے جن کے نیچے چشمے جاری ہیں، جہاں یہ ابدالاباد رہیں گے، یہ ہے اللہ کا وعده جو سراسر سچا ہے اور کون ہے جو اپنی بات میں اللہ سے زیاده سچا ہو؟ ');
INSERT INTO "junagarri" VALUES(4,123,'4:123','حقیقت حال نہ تو تمہاری آرزو کے مطابق ہے اور نہ اہل کتاب کی امیدوں پر موقوف ہے، جو برا کرے گا اس کی سزا پائے گا اور کسی کو نہ پائے گا جو اس کی حمایت ومدد، اللہ کے پاس کر سکے۔');
INSERT INTO "junagarri" VALUES(4,124,'4:124','جو ایمان والا ہو مرد ہو یا عورت اور وه نیک اعمال کرے، یقیناً ایسے لوگ جنت میں جائیں گے اور کھجور کی گٹھلی کے شگاف برابر بھی ان کا حق نہ مارا جائے گا۔ ');
INSERT INTO "junagarri" VALUES(4,125,'4:125','باعتبار دین کے اس سے اچھا کون ہے؟ جو اپنے کو اللہ کے تابع کر دے اور ہو بھی نیکو کار، ساتھ ہی یکسوئی والے ابراہیم کے دین کی پیروی کر رہا ہو اور ابراہیم (علیہ السلام) کو اللہ تعالیٰ نے اپنا دوست بنا لیا ہے۔ ');
INSERT INTO "junagarri" VALUES(4,126,'4:126','آسمانوں اور زمین میں جو کچھ ہے سب اللہ ہی کا ہے اور اللہ تعالیٰ ہر چیز کو گھیرنے والا ہے۔');
INSERT INTO "junagarri" VALUES(4,127,'4:127','آپ سے عورتوں کے بارے میں حکم دریافت کرتے ہیں ، آپ کہہ دیجئے! کہ خود اللہ ان کے بارے میں حکم دے رہا ہے اور قرآن کی وه آیتیں جو تم پر ان یتیم لڑکیوں کے بارے میں پڑھی جاتی ہیں جنہیں ان کا مقرر حق تم نہیں دیتے اور انہیں اپنے نکاح میں لانے کی رغبت رکھتے ہو اور کمزور بچوں کے بارے میں اور اس بارے میں کہ یتیموں کی کارگزاری انصاف کے ساتھ کرو ۔ تم جو نیک کام کرو، بے شبہ اللہ اسے پوری طرح جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(4,128,'4:128','اگر کسی عورت کو اپنے شوہر کی بد دماغی اور بے پرواہی کا خوف ہو تو دونوں آپس میں جو صلح کر لیں اس میں کسی پر کوئی گناه نہیں ۔ صلح بہت بہتر چیز ہے، طمع ہر ہر نفس میں شامل کر دی گئی ہے ۔ اگر تم اچھا سلوک کرو اور پرہیزگاری کرو تو تم جو کر رہے ہو اس پر اللہ تعالیٰ پوری طرح خبردار ہے۔');
INSERT INTO "junagarri" VALUES(4,129,'4:129','تم سے یہ تو کبھی نہ ہو سکے گا کہ اپنی تمام بیویوں میں ہر طرح عدل کرو، گو تم اس کی کتنی ہی خواہش وکوشش کر لو، اس لئے بالکل ہی ایک کی طرف مائل ہو کر دوسری کو ادھڑ لٹکتی ہوئی نہ چھوڑو اور اگر تم اصلاح کرو اور تقویٰ اختیار کرو تو، بے شک اللہ تعالیٰ بڑی مغفرت اور رحمت والا ہے۔');
INSERT INTO "junagarri" VALUES(4,130,'4:130','اور اگر میاں بیوی جدا ہو جائیں تو اللہ تعالیٰ اپنی وسعت سے ہر ایک کو بے نیاز کر دے گا ، اللہ تعالیٰ وسعت والا حکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(4,131,'4:131','زمین اور آسمانوں کی ہر ہر چیز اللہ تعالیٰ ہی کی ملکیت میں ہے اور واقعی ہم نے ان لوگوں کو جو تم سے پہلے کتاب دیئے گئے تھے اور تم کو بھی یہی حکم کیا ہے کہ اللہ سے ڈرتے رہو اور اگر تم کفر کرو تو یاد رکھو کہ اللہ کے لئے ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے اور اللہ بہت بے نیاز اور تعریف کیا گیا ہے۔');
INSERT INTO "junagarri" VALUES(4,132,'4:132','اللہ کے اختیار میں ہیں آسمانوں کی سب چیزیں اور زمین کی بھی اور اللہ کار ساز کافی ہے۔');
INSERT INTO "junagarri" VALUES(4,133,'4:133','اگر اسے منظور ہو تو اے لوگو! وه تم سب کو لے جائے اور دوسروں کو لے آئے، اللہ تعالیٰ اس پرپوری قدرت رکھنے والا ہے۔ ');
INSERT INTO "junagarri" VALUES(4,134,'4:134','جو شخص دنیا کا ثواب چاہتا ہو تو (یاد رکھو کہ) اللہ تعالیٰ کے پاس تو دنیا اور آخرت (دونوں) کا ثواب موجود ہے اور اللہ تعالیٰ بہت سننے والا اور خوب دیکھنے والا ہے۔');
INSERT INTO "junagarri" VALUES(4,135,'4:135','اے ایمان والو! عدل وانصاف پر مضبوطی سے جم جانے والے اور خوشنودی مولا کے لئے سچی گواہی دینے والے بن جاؤ، گو وه خود تمہارے اپنے خلاف ہو یا اپنے ماں باپ کے یا رشتہ دار عزیزوں کے ، وه شخص اگر امیر ہو تو اور فقیر ہو تو دونوں کے ساتھ اللہ کو زیاده تعلق ہے، اس لئے تم خواہش نفس کے پیچھے پڑ کر انصاف نہ چھوڑ دینا اور اگر تم نے کج بیانی یا پہلو تہی کی تو جان لو کہ جو کچھ تم کرو گے اللہ تعالیٰ اس سے پوری طرح باخبر ہے۔');
INSERT INTO "junagarri" VALUES(4,136,'4:136','اے ایمان والو! اللہ تعالیٰ پر، اس کے رسول (صلی اللہ علیہ وسلم) پر اور اس کتاب پر جو اس نے اپنے رسول (صلی اللہ علیہ وسلم) پر اتاری ہے اور ان کتابوں پر جو اس سے پہلے اس نے نازل فرمائی ہیں، ایمان لاؤ! جو شخص اللہ تعالیٰ سے اور اس کے فرشتوں سے اور اس کی کتابوں سے اور اس کے رسولوں سے اور قیامت کے دن سے کفر کرے وه تو بہت بڑی دور کی گمراہی میں جا پڑا۔');
INSERT INTO "junagarri" VALUES(4,137,'4:137','جن لوگوں نے ایمان قبول کر کے پھر کفر کیا، پھر ایمان لاکر پھر کفر کیا، پھر اپنے کفر میں بڑھ گئے، اللہ تعالیٰ یقیناً انہیں نہ بخشے گا اور نہ انہیں راه ہدایت سمجھائے گا۔ ');
INSERT INTO "junagarri" VALUES(4,138,'4:138','منافقوں کو اس امر کی خبر پہنچا دو کہ ان کے لئے دردناک عذاب یقینی ہے۔');
INSERT INTO "junagarri" VALUES(4,139,'4:139','جن کی یہ حالت ہے کہ مسلمانوں کو چھوڑ کر کافروں کو دوست بناتے پھرتے ہیں ، کیا ان کے پاس عزت کی تلاش میں جاتے ہیں؟ (تو یاد رکھیں کہ) عزت تو ساری کی ساری اللہ تعالیٰ کے قبضہ میں ہے۔ ');
INSERT INTO "junagarri" VALUES(4,140,'4:140','اور اللہ تعالیٰ تمہارے پاس اپنی کتاب میں یہ حکم اتار چکا ہے کہ تم جب کسی مجلس والوں کو اللہ تعالیٰ کی آیتوں کے ساتھ کفر کرتے اور مذاق اڑاتے ہوئے سنو تو اس مجمع میں ان کے ساتھ نہ بیٹھو! جب تک کہ وه اس کے علاوه اور باتیں نہ کرنے لگیں، (ورنہ) تم بھی اس وقت انہی جیسے ہو ، یقیناً اللہ تعالیٰ تمام کافروں اور سب منافقوں کو جہنم میں جمع کرنے والا ہے۔');
INSERT INTO "junagarri" VALUES(4,141,'4:141','یہ لوگ تمہارے انجام کار کا انتظار کرتے رہتے ہیں پھر اگر تمہیں اللہ فتح دے تو یہ کہتے ہیں کہ کیا ہم تمہارے ساتھی نہیں اور اگر کافروں کو تھوڑا سا غلبہ مل جائے تو (ان سے) کہتے ہیں کہ ہم تم پر غالب نہ آنے لگے تھے اور کیا ہم نے تمہیں مسلمانوں کے ہاتھوں سے نہ بچایا تھا؟ پس قیامت میں خود اللہ تعالیٰ تمہارے درمیان فیصلہ کرے گا اور اللہ تعالیٰ کافروں کو ایمان والوں پر ہرگز راه نہ دے گا۔ ');
INSERT INTO "junagarri" VALUES(4,142,'4:142','بے شک منافق اللہ سے چالبازیاں کر رہے ہیں اور وه انہیں اس چالبازی کا بدلہ دینے والا ہے اور جب نماز کو کھڑے ہوتے ہیں تو بڑی کاہلی کی حالت میں کھڑے ہوتے ہیں صرف لوگوں کو دکھاتے ہیں ، اور یاد الٰہی تو یوں ہی سی برائے نام کرتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(4,143,'4:143','وه درمیان میں ہی معلق ڈگمگا رہے ہیں، نہ پورے ان کی طرف نہ صحیح طور پر ان کی طرف اور جسے اللہ تعالیٰ گمراہی میں ڈال دے تو تو اس کے لئے کوئی راه نہ پائے گا۔');
INSERT INTO "junagarri" VALUES(4,144,'4:144','اے ایمان والو! مومنوں کو چھوڑ کر کافروں کو دوست نہ بناؤ، کیا تم یہ چاہتے ہو کہ اپنے اوپر اللہ تعالیٰ کی صاف حجت قائم کر لو۔ ');
INSERT INTO "junagarri" VALUES(4,145,'4:145','منافق تو یقیناً جہنم کے سب سے نیچے کے طبقہ میں جائیں گے ، ناممکن ہے کہ تو ان کا کوئی مددگار پالے۔');
INSERT INTO "junagarri" VALUES(4,146,'4:146','ہاں جو توبہ کر لیں اور اصلاح کر لیں اور اللہ تعالیٰ پر کامل یقین رکھیں اور خالص اللہ ہی کے لئے دینداری کریں تو یہ لوگ مومنوں کے ساتھ ہیں ، اللہ تعالیٰ مومنوں کو بہت بڑا اجر دے گا۔');
INSERT INTO "junagarri" VALUES(4,147,'4:147','اللہ تعالیٰ تمہیں سزا دے کر کیا کرے گا؟ اگر تم شکر گزاری کرتے رہو اور باایمان رہو ، اللہ تعالیٰ بہت قدر کرنے والا اور پورا علم رکھنے والا ہے۔ ');
INSERT INTO "junagarri" VALUES(4,148,'4:148','برائی کے ساتھ آواز بلند کرنے کو اللہ تعالیٰ پسند نہیں فرماتا مگر مظلوم کی اجازت ہے اور اللہ تعالیٰ خوب سنتا جانتا ہے۔');
INSERT INTO "junagarri" VALUES(4,149,'4:149','اگر تم کسی نیکی کو علانیہ کرو یا پوشیده، یا کسی برائی سے درگزر کرو ، پس یقیناً اللہ تعالیٰ پوری معافی کرنے والا اور پوری قدرت والا ہے۔');
INSERT INTO "junagarri" VALUES(4,150,'4:150','جو لوگ اللہ کے ساتھ اور اس کے پیغمبروں کے ساتھ کفر کرتے ہیں اور جو لوگ یہ چاہتے ہیں کہ اللہ اور اس کے رسولوں کے درمیان فرق رکھیں اور جو لوگ کہتے ہیں کہ بعض نبیوں پر تو ہمارا ایمان ہے اور بعض پر نہیں اور چاہتے ہیں کہ اس کے اور اس کے بین بین کوئی راه نکالیں۔');
INSERT INTO "junagarri" VALUES(4,151,'4:151','یقین مانو کہ یہ سب لوگ اصلی کافر ہیں ، اور کافروں کے لئے ہم نے اہانت آمیز سزا تیار کر رکھی ہے۔');
INSERT INTO "junagarri" VALUES(4,152,'4:152','اور جو لوگ اللہ پر اور اس کے تمام پیغمبروں پر ایمان لاتے ہیں اور ان میں سے کسی میں فرق نہیں کرتے، یہ ہیں جنہیں اللہ ان کو پورا ثواب دے گا اور اللہ بڑی مغفرت والا بڑی رحمت والا ہے۔');
INSERT INTO "junagarri" VALUES(4,153,'4:153','آپ سے یہ اہل کتاب درخواست کرتے ہیں کہ آپ ان کے پاس کوئی آسمانی کتاب لائیں ، حضرت موسیٰ (علیہ السلام) سے تو انہوں نے اس سے بہت بڑی درخواست کی تھی کہ ہمیں کھلم کھلا اللہ تعالیٰ کو دکھا دے، پس ان کے اس ظلم کے باعث ان پر کڑاکے کی بجلی آ پڑی پھر باوجودیکہ ان کے پاس بہت دلیلیں پہنچ چکی تھیں انہوں نے بچھڑے کو اپنا معبود بنا لیا، لیکن ہم نے یہ بھی معاف فرما دیا اور ہم نے موسیٰ کو کھلا غلبہ (اور صریح دلیل) عنایت فرمائی۔');
INSERT INTO "junagarri" VALUES(4,154,'4:154','اور ان کا قول لینے کے لئے ہم نے ان کے سروں پر طور پہاڑ لا کھڑا کردیا اور انہیں حکم دیا کہ سجده کرتے ہوئے دروازے میں جاؤ اور یہ بھی فرمایا کہ ہفتہ کے دن میں تجاوز نہ کرنا اور ہم نے ان سے سخت سے سخت قول وقرار لئے۔');
INSERT INTO "junagarri" VALUES(4,155,'4:155','(یہ سزا تھی) بہ سبب ان کی عہد شکنی کے اور احکام الٰہی کے ساتھ کفر کرنے کے اور اللہ کے نبیوں کو ناحق قتل کر ڈالنے کے ، اور اس سبب سے کہ یوں کہتے ہیں کہ ہمارے دلوں پر غلاف ہے۔ حالانکہ دراصل ان کے کفر کی وجہ سے ان کے دلوں پر اللہ تعالیٰ نے مہر لگا دی ہے، اس لئے یہ قدر قلیل ہی ایمان لاتے ہیں۔');
INSERT INTO "junagarri" VALUES(4,156,'4:156','اور ان کے کفر کے باعث اور مریم پر بہت بڑا بہتان باندھنے کے باعث۔ ');
INSERT INTO "junagarri" VALUES(4,157,'4:157','اور یوں کہنے کے باعث کہ ہم نے اللہ کے رسول مسیح عیسیٰ بن مریم کو قتل کر دیا حالانکہ نہ تو انہوں نے اسے قتل کیا نہ سولی پر چڑھایا بلکہ ان کے لئے ان (عیسیٰ) کا شبیہ بنا دیا گیا تھا ۔ یقین جانو کہ حضرت عیسیٰ (علیہ السلام) کے بارے میں اختلاف کرنے والے ان کے بارے میں شک میں ہیں، انہیں اس کا کوئی یقین نہیں بجز تخمینی باتوں پر عمل کرنے کے اتنا یقینی ہے کہ انہوں نے انہیں قتل نہیں کیا۔');
INSERT INTO "junagarri" VALUES(4,158,'4:158','بلکہ اللہ تعالیٰ نے انہیں اپنی طرف اٹھا لیا اور اللہ بڑا زبردست اور پوری حکمتوں والا ہے۔ ');
INSERT INTO "junagarri" VALUES(4,159,'4:159','اہل کتاب میں ایک بھی ایسا نہ بچے گا جو حضرت عیسیٰ (علیہ السلام) کی موت سے پہلے ان پر ایمان نہ لاچکے اور قیامت کے دن آپ ان پر گواه ہوں گے۔ ');
INSERT INTO "junagarri" VALUES(4,160,'4:160','جو نفیس چیزیں ان کے لئے حلال کی گئی تھیں وه ہم نے ان پر حرام کردیں ان کے ظلم کے باعث اور اللہ تعالیٰ کی راه سے اکثر لوگوں کو روکنے کے باعث۔ ');
INSERT INTO "junagarri" VALUES(4,161,'4:161','اور سود جس سے منع کئے گئے تھے اسے لینے کے باعث اور لوگوں کا مال ناحق مار کھانے کے باعث اور ان میں جو کفار ہیں ہم نے ان کے لئے المناک عذاب مہیا کر رکھا ہے۔');
INSERT INTO "junagarri" VALUES(4,162,'4:162','لیکن ان میں سے جو کامل اور مضبوط علم والے ہیں اور ایمان والے ہیں جو اس پر ایمان لاتے ہیں جو آپ کی طرف اتارا گیا اور جو آپ سے پہلے اتارا گیا اور نمازوں کو قائم رکھنے والے ہیں اور زکوٰة کے ادا کرنے والے ہیں اور اللہ پر اور قیامت کے دن پر ایمان رکھنے والے ہیں یہ ہیں جنہیں ہم بہت بڑے اجر عطا فرمائیں گے۔');
INSERT INTO "junagarri" VALUES(4,163,'4:163','یقیناً ہم نے آپ کی طرف اسی طرح وحی کی ہے جیسے کہ نوح (علیہ السلام) اور ان کے بعد والے نبیوں کی طرف کی، اور ہم نے وحی کی ابراہیم اور اسماعیل اور اسحاق اور یعقوب اور ان کی اولاد پر اور عیسیٰ اور ایوب اور یونس اور ہارون اور سلیمان کی طرف ۔ اور ہم نے داؤد (علیہ السلام) کو زبور عطا فرمائی۔');
INSERT INTO "junagarri" VALUES(4,164,'4:164','اور آپ سے پہلے کے بہت سے رسولوں کے واقعات ہم نے آپ سے بیان کئے ہیں اور بہت سے رسولوں کے نہیں بھی کئے اور موسیٰ (علیہ السلام) سے اللہ تعالیٰ نے صاف طور پر کلام کیا۔');
INSERT INTO "junagarri" VALUES(4,165,'4:165','ہم نے انہیں رسول بنایا ہے، خوشخبریاں سنانے والے اور آگاه کرنے والے تاکہ لوگوں کی کوئی حجت اور الزام رسولوں کے بھیجنے کے بعد اللہ تعالیٰ پر ره نہ جائے ۔ اللہ تعالی بڑا غالب اور بڑا باحکمت ہے۔');
INSERT INTO "junagarri" VALUES(4,166,'4:166','جو کچھ آپ کی طرف اتارا ہے اس کی بابت خود اللہ تعالیٰ گواہی دیتا ہے کہ اسے اپنے علم سے اتارا ہے اور فرشتے بھی گواہی دیتے ہیں اور اللہ تعالیٰ بطور گواه کافی ہے۔');
INSERT INTO "junagarri" VALUES(4,167,'4:167','جن لوگوں نے کفر کیا اور اللہ تعالیٰ کی راه سے اوروں کو روکا وه یقیناً گمراہی میں دور نکل گئے۔');
INSERT INTO "junagarri" VALUES(4,168,'4:168','جن لوگوں نے کفر کیا اور ظلم کیا، انہیں اللہ تعالیٰ ہرگز ہرگز نہ بخشے گا اور نہ انہیں کوئی راه دکھائے گا۔ ');
INSERT INTO "junagarri" VALUES(4,169,'4:169','بجز جہنم کی راه کے جس میں وه ہمیشہ ہمیشہ پڑے رہیں گے، اور یہ اللہ تعالیٰ پر بالکل آسان ہے۔');
INSERT INTO "junagarri" VALUES(4,170,'4:170','اے لوگو! تمہارے پاس تمہارے رب کی طرف حق لے کر رسول آگیا ہے، پس تم ایمان لاؤ تاکہ تمہارے لئے بہتری ہو اور اگر تم کافر ہوگئے تو اللہ ہی کی ہے ہر وه چیز جو آسمانوں اور زمین میں ہے ، اور اللہ دانا ہے حکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(4,171,'4:171','اے اہل کتاب! اپنے دین کے بارے میں حد سے نہ گزر جاؤ اور اللہ پر بجز حق کے اور کچھ نہ کہو، مسیح عیسیٰ بن مریم (علیہ السلام) تو صرف اللہ تعالیٰ کے رسول اور اس کے کلمہ (کن سے پیدا شده) ہیں، جسے مریم (علیہا السلام) کی طرف ڈال دیا تھا اور اس کے پاس کی روح ہیں اس لئے تم اللہ کو اور اس کے سب رسولوں کو مانو اور نہ کہو کہ اللہ تین ہیں ، اس سے باز آجاؤ کہ تمہارے لئے بہتری ہے، اللہ عبادت کے لائق تو صرف ایک ہی ہے اور وه اس سے پاک ہے کہ اس کی اولاد ہو، اسی کے لئے ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے۔ اور اللہ کافی ہے کام بنانے والا۔');
INSERT INTO "junagarri" VALUES(4,172,'4:172','مسیح (علیہ السلام) کو اللہ کا بنده ہونے میں کوئی ننگ و عار یا تکبر وانکار ہرگز ہو ہی نہیں سکتا اور نہ مقرب فرشتوں کو ، اس کی بندگی سے جو بھی دل چرائے اور تکبر و انکار کرے، اللہ تعالیٰ ان سب کو اکٹھا اپنی طرف جمع کرے گا۔');
INSERT INTO "junagarri" VALUES(4,173,'4:173','پس جو لوگ ایمان لائے ہیں اور شائستہ اعمال کئے ہیں ان کو ان کا پورا پورا ثواب عنایت فرمائے گا اور اپنے فضل سے انہیں اور زیاده دے گا اور جن لوگوں نے ننگ و عار اور سرکشی اور انکار کیا ، انہیں المناک عذاب دے گا اور وه اپنے لئے سوائے اللہ کے کوئی حمایتی، اور امداد کرنے والا نہ پائیں گے۔');
INSERT INTO "junagarri" VALUES(4,174,'4:174','اے لوگو! تمہارے پاس تمہارے رب کی طرف سے سند اور دلیل آپہنچی اور ہم نے تمہاری جانب واضح اور صاف نور اتار دیا ہے۔ ');
INSERT INTO "junagarri" VALUES(4,175,'4:175','پس جو لوگ اللہ تعالیٰ پر ایمان لائے اور اسے مضبوط پکڑ لیا، انہیں تو وه عنقریب اپنی رحمت اور فضل میں لے لے گا اور انہیں اپنی طرف کی راه راست دکھا دے گا۔');
INSERT INTO "junagarri" VALUES(4,176,'4:176','آپ سے فتویٰ پوچھتے ہیں، آپ کہہ دیجیئے کہ اللہ تعالیٰ (خود) تمہیں کلالہ کے بارے میں فتویٰ دیتا ہے۔ اگر کوئی شخص مر جائے جس کی اولاد نہ ہو اور ایک بہن ہو تو اس کے لئے چھوڑے ہوئے مال کا آدھا حصہ ہے اور وه بھائی اس بہن کا وارث ہوگا اگر اس کے اولاد نہ ہو ۔ پس اگر بہنیں دو ہوں تو انہیں کل چھوڑے ہوئے کا دو تہائی ملے گا ۔ اور اگر کئی شخص اس ناطے کے ہیں مرد بھی اور عورتیں بھی تو مرد کے لئے حصہ ہے مثل دو عورتوں کے ، اللہ تعالیٰ تمہارے لئے بیان فرما رہا ہے کہ ایسا نہ ہو کہ تم بہک جاؤ اور اللہ تعالیٰ ہر چیز سے واقف ہے۔');
INSERT INTO "junagarri" VALUES(5,1,'5:1','اے ایمان والو! عہد و پیماں پورے کرو ، تمہارے لئے مویشی چوپائے حلال کئے گئے ہیں بجز ان کے جن کے نام پڑھ کر سنا دیئے جائیں گے مگر حالت احرام میں شکار کو حلال جاننے والے نہ بننا، یقیناً اللہ جو چاہے حکم کرتا ہے۔');
INSERT INTO "junagarri" VALUES(5,2,'5:2','اے ایمان والو! اللہ تعالیٰ کے شعائر کی بے حرمتی نہ کرو نہ ادب والے مہینوں کی نہ حرم میں قربان ہونے والے اور پٹے پہنائے گئے جانوروں کی جو کعبہ کو جا رہے ہوں اور نہ ان لوگوں کی جو بیت اللہ کے قصد سے اپنے رب تعالیٰ کے فضل اور اس کی رضاجوئی کی نیت سے جا رہے ہوں ، ہاں جب تم احرام اتار ڈالو تو شکار کھیل سکتے ہو ، جن لوگوں نے تمہیں مسجد حرام سے روکا تھا ان کی دشمنی تمہیں اس بات پر آماده نہ کرے کہ تم حد سے گزر جاؤ ، نیکی اور پرہیزگاری میں ایک دوسرے کی امداد کرتے رہو اور گناه اور ظلم و زیادتی میں مدد نہ کرو ، اور اللہ تعالیٰ سے ڈرتے رہو، بےشک اللہ تعالیٰ سخت سزا دینے والا ہے۔');
INSERT INTO "junagarri" VALUES(5,3,'5:3','تم پر حرام کیا گیا مردار اور خون اور خنزیر کا گوشت اور جس پر اللہ کے سوا دوسرے کا نام پکارا گیا ہو اور جو گلا گھٹنے سے مرا ہو اور جو کسی ضرب سے مر گیا ہو اور جو اونچی جگہ سے گر کر مرا ہو اور جو کسی کے سینگ مارنے سے مرا ہو اور جسے درندوں نے پھاڑ کھایا ہو لیکن اسے تم ذبح کر ڈالو تو حرام نہیں اور جو آستانوں پر ذبح کیا گیا ہو اور یہ بھی کہ قرعہ کے تیروں کے ذریعے فال گیری کرو یہ سب بدترین گناه ہیں، آج کفار تمہارے دین سے ناامید ہوگئے، خبردار! تم ان سے نہ ڈرنا اور مجھ سے ڈرتے رہنا، آج میں نے تمہارے لئے دین کو کامل کردیا اور تم پر اپنا انعام بھرپور کردیا اور تمہارے لئے اسلام کے دین ہونے پر رضامند ہوگیا۔ پس جو شخص شدت کی بھوک میں بے قرار ہو جائے بشرطیکہ کسی گناه کی طرف اس کا میلان نہ ہو تو یقیناً اللہ تعالیٰ معاف کرنے والا اور بہت بڑا مہربان ہے۔ ');
INSERT INTO "junagarri" VALUES(5,4,'5:4','آپ سے دریافت کرتے ہیں کہ ان کے لئے کیا کچھ حلال ہے؟ آپ کہہ دیجئے کہ تمام پاک چیزیں تمہارے لئے حلال کی گئی ہیں ، اور جن شکار کھیلنے والے جانوروں کو تم نے سدھا رکھا ہے یعنی جنہیں تم تھوڑا بہت وه سکھاتے ہو جس کی تعلیم اللہ تعالیٰ نے تمہیں دے رکھی ہے پس جس شکار کو وه تمہارے لئے پکڑ کر روک رکھیں تو تم اس سے کھا لو اور اس پر اللہ تعالیٰ کے نام کا ذکر کر لیا کرو ۔ اور اللہ تعالیٰ سے ڈرتے رہو، یقیناً اللہ تعالیٰ جلد حساب لینے والا ہے۔');
INSERT INTO "junagarri" VALUES(5,5,'5:5','کل پاکیزه چیزیں آج تمہارے لئے حلال کی گئیں اور اہل کتاب کا ذبیحہ تمہارے لئے حلال ہے اور تمہارا ذبیحہ ان کے لئے حلال ہے، اور پاک دامن مسلمان عورتیں اور جو لوگ تم سے پہلے کتاب دیئے گئے ہیں ان کی پاک دامن عورتیں بھی حلال ہیں جب کہ تم ان کے مہر ادا کرو، اس طرح کہ تم ان سے باقاعده نکاح کرو یہ نہیں کہ علانیہ زنا کرو یا پوشیده بدکاری کرو، منکرین ایمان کے اعمال ضائع اور اکارت ہیں اور آخرت میں وه ہارنے والوں میں سے ہیں۔');
INSERT INTO "junagarri" VALUES(5,6,'5:6','اے ایمان والو! جب تم نماز کے لئے اٹھو تو اپنے منھ کو، اور اپنے ہاتھوں کو کہنیوں سمیت دھو لو اپنے سروں کا مسح کرو اور اپنے پاؤں کو ٹخنوں سمیت دھو لو ، اور اگر تم جنابت کی حالت میں ہو تو غسل کرلو ، ہاں اگر تم بیمار ہو یا سفر کی حالت میں ہو یا تم میں سے کوئی حاجت ضروری سے فارغ ہو کر آیا ہو، یا تم عورتوں سے ملے ہو اور تمہیں پانی نہ ملے تو تم پاک مٹی سے تیمم کرلو، اسے اپنے چہروں پر اور ہاتھوں پر مل لو اللہ تعالیٰ تم پر کسی قسم کی تنگی ڈالنا نہیں چاہتا بلکہ اس کا اراده تمہیں پاک کرنے کا اور تمہیں اپنی بھرپور نعمت دینے کا ہے ، تاکہ تم شکر ادا کرتے رہو۔');
INSERT INTO "junagarri" VALUES(5,7,'5:7','تم پر اللہ تعالیٰ کی جو نعمتیں نازل ہوئی ہیں انہیں یاد رکھو اور اس کے اس عہد کو بھی جس کا تم سے معاہده ہوا ہے جبکہ تم نے کہا ہم نے سنا اور مانا اور اللہ تعالیٰ سے ڈرتے رہو، یقیناً اللہ تعالیٰ دلوں کی باتوں کا جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(5,8,'5:8','اے ایمان والو! تم اللہ کی خاطر حق پر قائم ہو جاؤ، راستی اور انصاف کے ساتھ گواہی دینے والے بن جاؤ ، کسی قوم کی عداوت تمہیں خلاف عدل پر آماده نہ کردے ، عدل کیا کرو جو پرہیز گاری کے زیاده قریب ہے، اور اللہ تعالیٰ سے ڈرتے رہو، یقین مانو کہ اللہ تعالیٰ تمہارے اعمال سے باخبر ہے۔');
INSERT INTO "junagarri" VALUES(5,9,'5:9','اللہ تعالیٰ کا وعده ہے کہ جو ایمان لائیں اور نیک کام کریں ان کے لئے وسیع مغفرت اور بہت بڑا اجر و ثواب ہے۔');
INSERT INTO "junagarri" VALUES(5,10,'5:10','اور جن لوگوں نے کفر کیا اور ہمارے احکام کو جھٹلایا وه دوزخی ہیں۔');
INSERT INTO "junagarri" VALUES(5,11,'5:11','اے ایمان والو! اللہ تعالیٰ نے جو احسان تم پر کیا ہے اسے یاد کرو جب کہ ایک قوم نے تم پر دست درازی کرنی چاہی تو اللہ تعالیٰ نے ان کے ہاتھوں کو تم تک پہنچنے سے روک دیا اور اللہ تعالیٰ سے ڈرتے رہو اور مومنوں کو اللہ تعالیٰ ہی پر بھروسہ کرنا چاہئے۔');
INSERT INTO "junagarri" VALUES(5,12,'5:12','اور اللہ تعالیٰ نے بنی اسرائیل سے عہد و پیمان لیا اور انہی میں سے باره سردار ہم نے مقرر فرمائے اور اللہ تعالیٰ نے فرما دیا کہ یقیناً میں تمہارے ساتھ ہوں، اگر تم نماز قائم رکھو گے اور زکوٰة دیتے رہو گے اور میرے رسولوں کو مانتے رہو گے اور ان کی مدد کرتے رہو گے اور اللہ تعالیٰ کو بہتر قرض دیتے رہو گے تو یقیناً میں تمہاری برائیاں تم سے دور رکھوں گا اور تمہیں ان جنتوں میں لے جاؤں گا جن کے نیچے چشمے بہہ رہے ہیں، اب اس عہد وپیمان کے بعد بھی تم میں سے جو انکاری ہو جائے وه یقیناً راه راست سے بھٹک گیا۔');
INSERT INTO "junagarri" VALUES(5,13,'5:13','پھر ان کی عہد شکنی کی وجہ سے ہم نے ان پر اپنی لعنت نازل فرمادی اور ان کے دل سخت کردیئے کہ وه کلام کو اس کی جگہ سے بدل ڈالتے ہیں اور جو کچھ نصیحت انہیں کی گئی تھی اس کا بہت بڑا حصہ بھلا بیٹھے ، ان کی ایک نہ ایک خیانت پر تجھے اطلاع ملتی ہی رہے گی ہاں تھوڑے سے ایسے نہیں بھی ہیں پس توانہیں معاف کرتا جا اور درگزر کرتا ره ، بےشک اللہ تعالیٰ احسان کرنے والوں سے محبت کرتا ہے۔');
INSERT INTO "junagarri" VALUES(5,14,'5:14','اور جو اپنے آپ کو نصرانی کہتے ہیں ہم نے ان سے بھی عہد وپیمان لیا، انہوں نے بھی اس کا بڑا حصہ فراموش کر دیا جو انہیں نصیحت کی گئی تھی، تو ہم نے بھی ان کے آپس میں بغض وعداوت ڈال دی جو تاقیامت رہے گی اور جو کچھ یہ کرتے تھے عنقریب اللہ تعالیٰ انہیں سب بتا دے گا۔');
INSERT INTO "junagarri" VALUES(5,15,'5:15','اے اہل کتاب! یقیناً تمہارے پاس ہمارا رسول (صلی اللہ علیہ وسلم) آچکا جو تمہارے سامنے کتاب اللہ کی بکثرت ایسی باتیں ظاہر کر رہا ہے جنہیں تم چھپا رہے تھے اور بہت سی باتوں سے درگزر کرتا ہے، تمہارے پاس اللہ تعالیٰ کی طرف سے نور اور واضح کتاب آچکی ہے۔ ');
INSERT INTO "junagarri" VALUES(5,16,'5:16','جس کے ذریعہ سے اللہ تعالیٰ انہیں جو رضائے رب کے درپے ہوں سلامتی کی راہیں بتلاتا ہے اور اپنی توفیق سے اندھیروں سے نکال کر نور کی طرف لاتا ہے اور راه راست کی طرف ان کی رہبری کرتا ہے۔');
INSERT INTO "junagarri" VALUES(5,17,'5:17','یقیناً وه لوگ کافر ہوگئے جنہوں نے کہا کہ اللہ ہی مسیح ابن مریم ہے، آپ ان سے کہہ دیجیئے کہ اگر اللہ تعالیٰ مسیح ابن مریم اور اس کی والده اور روئے زمین کے سب لوگوں کو ہلاک کر دینا چاہے تو کون ہے جو اللہ تعالیٰ پر کچھ بھی اختیار رکھتا ہو؟ آسمانوں و زمین اور دونوں کے درمیان کا کل ملک اللہ تعالیٰ ہی کا ہے، وه جو چاہتا ہے پیدا کرتا ہے، اور اللہ تعالیٰ ہر چیز پر قادر ہے۔ ');
INSERT INTO "junagarri" VALUES(5,18,'5:18','یہود و نصاریٰ کہتے ہیں کہ ہم اللہ کے بیٹے اور اس کے دوست ہیں ، آپ کہہ دیجیئے کہ پھر تمہیں تمہارے گناہوں کے باعث اللہ کیوں سزا دیتا ہے؟ نہیں بلکہ تم بھی اس کی مخلوق میں سے ایک انسان ہو وه جسے چاہتا ہے بخش دیتا ہے، اور جسے چاہتا ہے عذاب کرتا ہے ، زمین و آسمان اور ان کے درمیان کی ہر چیز اللہ تعالیٰ کی ملکیت ہے اور اسی کی طرف لوٹنا ہے۔');
INSERT INTO "junagarri" VALUES(5,19,'5:19','اے اہل کتاب! بالیقین ہمارا رسول تمہارے پاس رسولوں کی آمد کے ایک وقفے کے بعد آ پہنچا ہے۔ جو تمہارے لئے صاف صاف بیان کر رہا ہے تاکہ تمہاری یہ بات نہ ره جائے کہ ہمارے پاس تو کوئی بھلائی، برائی سنانے والا آیا ہی نہیں، پس اب تو یقیناً خوشخبری سنانے والا اور آگاه کرنے والا آ پہنچا اور اللہ تعالیٰ ہر چیز پر قادر ہے۔');
INSERT INTO "junagarri" VALUES(5,20,'5:20','اور یاد کرو موسیٰ (علیہ السلام) نے اپنی قوم سے کہا، اے میری قوم کے لوگو! اللہ تعالیٰ کے اس احسان کا ذکر کرو کہ اس نے تم میں سے پیغمبر بنائے اور تمہیں بادشاه بنا دیا اور تمہیں وه دیا جو تمام عالم میں کسی کو نہیں دیا۔ ');
INSERT INTO "junagarri" VALUES(5,21,'5:21','اے میری قوم والو! اس مقدس زمین میں داخل ہو جاؤ جو اللہ تعالیٰ نے تمہارے نام لکھ دی ہے اور اپنی پشت کے بل روگردانی نہ کرو کہ پھر نقصان میں جا پڑو۔');
INSERT INTO "junagarri" VALUES(5,22,'5:22','انہوں نے جواب دیا کہ اے موسیٰ وہاں تو زور آور سرکش لوگ ہیں اور جب تک وه وہاں سے نکل نہ جائیں ہم تو ہرگز وہاں نہ جائیں گے ہاں اگر وه وہاں سے نکل جائیں پھر تو ہم (بخوشی) چلے جائیں گے۔ ');
INSERT INTO "junagarri" VALUES(5,23,'5:23','دو شخصوں نے جو خدا ترس لوگوں میں سے تھے، جن پر اللہ تعالیٰ کا فضل تھا کہا کہ تم ان کے پاس دروازے میں تو پہنچ جاؤ، دروازے میں قدم رکھتے ہی یقیناً تم غالب آجاؤ گے، اور تم اگر مومن ہو تو تمہیں اللہ تعالیٰ ہی پر بھروسہ رکھنا چاہیئے۔ ');
INSERT INTO "junagarri" VALUES(5,24,'5:24','قوم نے جواب دیا کہ اے موسیٰ! جب تک وه وہاں ہیں تب تک ہم ہرگز وہاں نہ جائیں گے، اس لئے تم اور تمہارا پروردگار جا کر دونوں ہی لڑ بھڑ لو، ہم یہیں بیٹھے ہوئے ہیں۔۔ ');
INSERT INTO "junagarri" VALUES(5,25,'5:25','موسیٰ (علیہ السلام) کہنے لگے الٰہی! مجھے تو بجز اپنے اور میرے بھائی کے کسی اور پر کوئی اختیار نہیں، پس تو ہم میں اور ان نافرمانوں میں جدائی کردے۔ ');
INSERT INTO "junagarri" VALUES(5,26,'5:26','ارشاد ہوا کہ اب زمین ان پر چالیس سال تک حرام کر دی گئی ہے، یہ خانہ بدوش ادھر ادھر سرگرداں پھرتے رہیں گے اس لئے تم ان فاسقوں کے بارے میں غمگین نہ ہونا۔ ');
INSERT INTO "junagarri" VALUES(5,27,'5:27','آدم (علیہ السلام) کے دونوں بیٹوں کا کھرا کھرا حال بھی انہیں سنا دو ، ان دونوں نے ایک نذرانہ پیش کیا، ان میں سے ایک کی نذر تو قبول ہوگئی اور دوسرے کی مقبول نہ ہوئی تو وه کہنے لگا کہ میں تجھے مار ہی ڈالوں گا، اس نے کہا اللہ تعالیٰ تقویٰ والوں کا ہی عمل قبول کرتا ہے۔');
INSERT INTO "junagarri" VALUES(5,28,'5:28','گو تو میرے قتل کے لئے دست درازی کرے لیکن میں تیرے قتل کی طرف ہرگز اپنے ہاتھ نہ بڑھاؤں گا، میں تو اللہ تعالیٰ پروردگار عالم سے خوف کھاتا ہوں۔');
INSERT INTO "junagarri" VALUES(5,29,'5:29','میں تو چاہتا ہوں کہ تو میرا گناه اور اپنے گناه اپنے سر پر رکھ لے اور دوزخیوں میں شامل ہوجائے، ظالموں کا یہی بدلہ ہے۔');
INSERT INTO "junagarri" VALUES(5,30,'5:30','پس اسے اس کے نفس نے اپنے بھائی کے قتل پر آماده کر دیا اور اس نے اسے قتل کر ڈالا، جس سے نقصان پانے والوں میں سے ہوگیا۔ ');
INSERT INTO "junagarri" VALUES(5,31,'5:31','پھر اللہ تعالیٰ نے ایک کوے کو بھیجا جو زمین کھود رہا تھا تاکہ اسے دکھائے کہ وه کس طرح اپنے بھائی کی نعش کو چھپا دے، وه کہنے لگا، ہائے افسوس! کیا میں ایسا کرنے سے بھی گیا گزرا ہوگیا کہ اس کوے کی طرح اپنے بھائی کی لاش کو دفنا دیتا؟ پھر تو (بڑا ہی) پشیمان اور شرمنده ہوگیا۔');
INSERT INTO "junagarri" VALUES(5,32,'5:32','اسی وجہ سے ہم نے بنی اسرائیل پر یہ لکھ دیا کہ جو شخص کسی کو بغیر اس کے کہ وه کسی کا قاتل ہو یا زمین میں فساد مچانے والا ہو، قتل کر ڈالے تو گویا اس نے تمام لوگوں کو قتل کردیا، اور جو شخص کسی ایک کی جان بچا لے، اس نے گویا تمام لوگوں کو زنده کردیا اور ان کے پاس ہمارے بہت سے رسول ظاہر دلیلیں لے کر آئے لیکن پھر اس کے بعد بھی ان میں کے اکثر لوگ زمین میں ظلم و زیادتی اور زبردستی کرنے والے ہی رہے۔ ');
INSERT INTO "junagarri" VALUES(5,33,'5:33','جو اللہ تعالیٰ سے اور اس کے رسول سے لڑیں اور زمین میں فساد کرتے پھریں ان کی سزا یہی ہے کہ وه قتل کر دیئے جائیں یا سولی چڑھا دیئے جائیں یا مخالف جانب سے ان کے ہاتھ پاوں کاٹ دیئے جائیں، یا انہیں جلاوطن کر دیا جائے ، یہ تو ہوئی ان کی دنیوی ذلت اور خواری، اور آخرت میں ان کے لئے بڑا بھاری عذاب ہے۔');
INSERT INTO "junagarri" VALUES(5,34,'5:34','ہاں جو لوگ اس سے پہلے توبہ کر لیں کہ تم ان پر قابو پالو تو یقین مانو کہ اللہ تعالیٰ بہت بڑی بخشش اور رحم و کرم والا ہے۔');
INSERT INTO "junagarri" VALUES(5,35,'5:35','مسلمانو! اللہ تعالیٰ سے ڈرتے رہو اور اس کا قرب تلاش کرو اور اس کی راه میں جہاد کرو تاکہ تمہارا بھلا ہو۔');
INSERT INTO "junagarri" VALUES(5,36,'5:36','یقین مانو کہ کافروں کے لئے اگر وه سب کچھ ہو جو ساری زمین میں ہے بلکہ اسی کے مثل اور بھی ہو اور وه اس سب کو قیامت کے دن کے عذاب کے بدلے فدیے میں دینا چاہیں تو بھی ناممکن ہے کہ ان کا فدیہ قبول کرلیا جائے، ان کے لئے تو درد ناک عذاب ہی ہے۔ ');
INSERT INTO "junagarri" VALUES(5,37,'5:37','یہ چاہیں گے کہ دوزخ میں سے نکل جائیں لیکن یہ ہرگز اس میں سے نہ نکل سکیں گے، ان کے لئے تو دوامی عذاب ہیں۔ ');
INSERT INTO "junagarri" VALUES(5,38,'5:38','چوری کرنے والے مرد اور عورت کے ہاتھ کاٹ دیا کرو ۔ یہ بدلہ ہے اس کا جو انہوں نے کیا۔ عذاب، اللہ کی طرف سے اور اللہ تعالیٰ قوت وحکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(5,39,'5:39','جو شخص اپنے گناه کے بعد توبہ کرلے اور اصلاح کرلے تو اللہ تعالیٰ رحمت کے ساتھ اس کی طرف لوٹتا ہے ۔ یقیناً اللہ تعالیٰ معاف فرمانے والا مہربانی کرنے والا ہے۔');
INSERT INTO "junagarri" VALUES(5,40,'5:40','کیا تجھے معلوم نہیں کہ اللہ تعالیٰ ہی کے لئے زمین و آسمان کی بادشاہت ہے؟ جسے چاہے سزا دے اور جسے چاہے معاف کردے، اللہ تعالیٰ ہر چیز پر قادر ہے۔');
INSERT INTO "junagarri" VALUES(5,41,'5:41','اے رسول! آپ ان لوگوں کے پیچھے نہ کُڑھیے جو کفر میں سبقت کر رہے ہیں خواه وه ان (منافقوں) میں سے ہوں جو زبانی تو ایمان کا دعویٰ کرتے ہیں لیکن حقیقتاً ان کے دل باایمان نہیں اور یہودیوں میں سے کچھ لوگ ایسے ہیں جو غلط باتیں سننے کے عادی ہیں اور ان لوگوں کے جاسوس ہیں جو اب تک آپ کے پاس نہیں آئے، وه کلمات کے اصلی موقعہ کو چھوڑ کر انہیں متغیر کردیا کرتے ہیں، کہتے ہیں کہ اگر تم یہی حکم دیئے جاؤ تو قبول کرلینا اور اگر یہ حکم نہ دیئے جاؤ تو الگ تھلگ رہنا اور جس کا خراب کرنا اللہ کو منظور ہو تو آپ اس کے لئے خدائی ہدایت میں سے کسی چیز کے مختار نہیں۔ اللہ تعالیٰ کا اراده ان کے دلوں کو پاک کرنے کا نہیں، ان کے لئے دنیا میں بھی بڑی ذلت اور رسوائی ہے اور آخرت میں بھی ان کے لئے بڑی سخت سزا ہے۔');
INSERT INTO "junagarri" VALUES(5,42,'5:42','یہ کان لگا لگا کر جھوٹ کے سننے والے اور جی بھر بھر کر حرام کے کھانے والے ہیں، اگر یہ تمہارے پاس آئیں تو تمہیں اختیار ہے خواه ان کے آپس کا فیصلہ کرو خواه ان کو ٹال دو، اگر تم ان سے منھ بھی پھیرو گے تو بھی یہ تم کو ہرگز کوئی ضرر نہیں پہنچا سکتے، اور اگر تم فیصلہ کرو تو ان میں عدل وانصاف کے ساتھ فیصلہ کرو، یقیناً عدل والوں کے ساتھ اللہ محبت رکھتا ہے۔');
INSERT INTO "junagarri" VALUES(5,43,'5:43','(تعجب کی بات ہے کہ) وه کیسے اپنے پاس تورات ہوتے ہوئے جس میں احکام الٰہی ہیں تم کو منصف بناتے ہیں پھر اس کے بعد بھی پھر جاتے ہیں، دراصل یہ ایمان ویقین والے ہیں ہی نہیں۔');
INSERT INTO "junagarri" VALUES(5,44,'5:44','ہم نے تورات نازل فرمائی ہے جس میں ہدایت ونور ہے، یہودیوں میں اسی تورات کے ساتھ اللہ تعالیٰ کے ماننے والے انبیاء (علیہم السلام) اور اہل اللہ اور علما فیصلے کرتے تھے کیونکہ انہیں اللہ کی اس کتاب کی حفاظت کا حکم دیا گیا تھا ۔ اور وه اس پر اقراری گواه تھے اب تمہیں چاہیئے کہ لوگوں سے نہ ڈرو اور صرف میرا ڈر رکھو، میری آیتوں کو تھوڑے تھوڑے مول پر نہ بیچو ، جو لوگ اللہ کی اتاری ہوئی وحی کے ساتھ فیصلے نہ کریں وه (پورے اور پختہ) کافر ہیں۔ ');
INSERT INTO "junagarri" VALUES(5,45,'5:45','اور ہم نے یہودیوں کے ذمہ تورات میں یہ بات مقرر کر دی تھی کہ جان کے بدلے جان اور آنکھ کے بدلے آنکھ اور ناک کے بدلے ناک اور کان کے بدلے کان اور دانت کے بدلے دانت اور خاص زخموں کا بھی بدلہ ہے ، پھر جو شخص اس کو معاف کردے تو وه اس کے لئے کفاره ہے، اور جو لوگ اللہ کے نازل کئے ہوئے کے مطابق حکم نہ کریں، وہی لوگ ظالم ہیں۔ ');
INSERT INTO "junagarri" VALUES(5,46,'5:46','اور ہم نے ان کے پیچھے عیسیٰ بن مریم کو بھیجا جو اپنے سے پہلے کی کتاب یعنی تورات کی تصدیق کرنے والے تھے اور ہم نے انہیں انجیل عطا فرمائی جس میں نور اور ہدایت تھی اور وه اپنے سے پہلے کی کتاب تورات کی تصدیق کرتی تھی اور وه سراسر ہدایت ونصیحت تھی پارسا لوگوں کے لئے۔ ');
INSERT INTO "junagarri" VALUES(5,47,'5:47','اور انجیل والوں کو بھی چاہئے کہ اللہ تعالیٰ نے جو کچھ انجیل میں نازل فرمایا ہے اسی کے مطابق حکم کریں اور جو اللہ تعالیٰ کے نازل کرده سے ہی حکم نہ کریں وه (بدکار) فاسق ہیں۔');
INSERT INTO "junagarri" VALUES(5,48,'5:48','اور ہم نے آپ کی طرف حق کے ساتھ یہ کتاب نازل فرمائی ہے جو اپنے سے اگلی کتابوں کی تصدیق کرنے والی ہے اور ان کی محافظ ہے ۔ اس لئے آپ ان کے آپس کے معاملات میں اسی اللہ کی اتاری ہوئی کتاب کے ساتھ حکم کیجیئے ، اس حق سے ہٹ کر ان کی خواہشوں کے پیچھے نہ جائیے تم میں سے ہر ایک کے لئے ہم نے ایک دستور اور راه مقرر کردی ہے ۔ اگر منظور مولیٰ ہوتا تو تم سب کو ایک ہی امت بنا دیتا، لیکن اس کی چاہت ہے کہ جو تمہیں دیا ہے اس میں تمہیں آزمائے ، تم نیکیوں کی طرف جلدی کرو، تم سب کا رجوع اللہ ہی کی طرف ہے، پھر وه تمہیں ہر وه چیز بتا دے گا جس میں تم اختلاف کرتے رہتے ہو۔');
INSERT INTO "junagarri" VALUES(5,49,'5:49','آپ ان کے معاملات میں خدا کی نازل کرده وحی کے مطابق ہی حکم کیا کیجیئے، ان کی خواہشوں کی تابعداری نہ کیجیئے اور ان سے ہوشیار رہیئے کہ کہیں یہ آپ کو اللہ کے اتارے ہوئے کسی حکم سے ادھر ادھر نہ کریں، اگر یہ لوگ منھ پھیر لیں تو یقین کریں کہ اللہ کا اراده یہی ہے کہ انہیں ان کے بعض گناہوں کی سزا دے ہی ڈالے اور اکثر لوگ نا فرمان ہی ہوتے ہیں۔');
INSERT INTO "junagarri" VALUES(5,50,'5:50','کیا یہ لوگ پھر سے جاہلیت کا فیصلہ چاہتے ہیں یقین رکھنے والے لوگوں کے لئے اللہ تعالیٰ سے بہتر فیصلے اور حکم کرنے والا کون ہوسکتا ہے؟ ');
INSERT INTO "junagarri" VALUES(5,51,'5:51','اے ایمان والو! تم یہود ونصاریٰ کو دوست نہ بناؤ یہ تو آپس میں ہی ایک دوسرے کے دوست ہیں ۔ تم میں سے جو بھی ان میں کسی سے دوستی کرے وه بےشک انہی میں سے ہے، ظالموں کو اللہ تعالیٰ ہرگز راه راست نہیں دکھاتا۔ ');
INSERT INTO "junagarri" VALUES(5,52,'5:52','آپ دیکھیں گے کہ جن کے دلوں میں بیماری ہے وه دوڑ دوڑ کر ان میں گھس رہے ہیں اور کہتے ہیں کہ ہمیں خطره ہے، ایسا نہ ہو کہ کوئی حادثہ ہم پر پڑ جائے بہت ممکن ہے کہ اللہ تعالیٰ فتح دے دے ۔ یا اپنے پاس سے کوئی اور چیز لائے پھر تو یہ اپنے دلوں میں چھپائی ہوئی باتوں پر (بے طرح) نادم ہونے لگیں گے۔');
INSERT INTO "junagarri" VALUES(5,53,'5:53','اور ایمان والے کہیں گے، کیا یہی وه لوگ ہیں جو بڑے مبالغہ سے اللہ کی قسمیں کھا کھا کر کہتے ہیں کہ ہم تمہارے ساتھ ہیں۔ ان کے اعمال غارت ہوئے اور یہ ناکام ہوگئے۔');
INSERT INTO "junagarri" VALUES(5,54,'5:54','اے ایمان والو! تم میں سے جو شخص اپنے دین سے پھر جائے تو اللہ تعالیٰ بہت جلد ایسی قوم کو لائے گا جو اللہ کی محبوب ہوگی اور وه بھی اللہ سے محبت رکھتی ہوگی وه نرم دل ہوں گے مسلمانوں پر اور سخت اور تیز ہوں گے کفار پر، اللہ کی راه میں جہاد کریں گے اور کسی ملامت کرنے والے کی ملامت کی پرواه بھی نہ کریں گے ، یہ ہے اللہ تعالیٰ کا فضل جسے چاہے دے، اللہ تعالیٰ بڑی وسعت والا اور زبردست علم والا ہے۔');
INSERT INTO "junagarri" VALUES(5,55,'5:55','(مسلمانو)! تمہارا دوست خود اللہ ہے اور اس کا رسول ہے اور ایمان والے ہیں جو نمازوں کی پابندی کرتے ہیں اور زکوٰة ادا کرتے ہیں اور وه رکوع (خشوع وخضوع) کرنے والے ہیں۔');
INSERT INTO "junagarri" VALUES(5,56,'5:56','اور جو شخص اللہ تعالیٰ سے اور اس کے رسول سے اور مسلمانوں سے دوستی کرے، وه یقین مانے کہ اللہ تعالیٰ کی جماعت ہی غالب رہے گی۔ ');
INSERT INTO "junagarri" VALUES(5,57,'5:57','مسلمانو! ان لوگوں کو دوست نہ بناؤ جو تمہارے دین کو ہنسی کھیل بنائے ہوئے ہیں (خواه) وه ان میں سے ہوں جو تم سے پہلے کتاب دیئے گئے یا کفار ہوں اگر تم مومن ہو تو اللہ تعالیٰ سے ڈرتے رہو۔');
INSERT INTO "junagarri" VALUES(5,58,'5:58','اور جب تم نماز کے لئے پکارتے ہو تو وہ اسے ہنسی کھیل ٹھرا لیتے ہیں ۔ یہ اس واسطے کہ بےعقل ہیں۔');
INSERT INTO "junagarri" VALUES(5,59,'5:59','آپ کہہ دیجیئے اے یہودیوں اور نصرانیو! تم ہم سے صرف اس وجہ سے دشمنیاں کر رہے ہو کہ ہم اللہ تعالیٰ پر اور جو کچھ ہماری جانب نازل کیا گیا ہے اور جو کچھ اس سے پہلے اتارا گیا ہے اس پر ایمان لائے ہیں اور اس لئے بھی کہ تم میں اکثر فاسق ہیں۔');
INSERT INTO "junagarri" VALUES(5,60,'5:60','کہہ دیجیئے کہ کیا میں تمہیں بتاؤں؟ کہ اس سے بھی زیاده برے اجر پانے والا اللہ تعالیٰ کے نزدیک کون ہے؟ وه جس پر اللہ تعالیٰ نے لعنت کی اور اس پر وه غصہ ہوا اور ان میں سے بعض کو بندر اور سور بنا دیا اور جنہوں نے معبودان باطل کی پرستش کی، یہی لوگ بدتر درجے والے ہیں اور یہی راه راست سے بہت زیاده بھٹکنے والے ہیں۔ ');
INSERT INTO "junagarri" VALUES(5,61,'5:61','اور جب تمہارے پاس آتے ہیں تو کہتے ہیں کہ ہم ایمان لائے حالانکہ وه کفر لئے ہوئے ہی آئے تھے اور اسی کفر کے ساتھ ہی گئے بھی اور یہ جو کچھ چھپا رہے ہیں اسے اللہ تعالیٰ خوب جانتا ہے۔ ');
INSERT INTO "junagarri" VALUES(5,62,'5:62','آپ دیکھیں گے کہ ان میں سے اکثر گناه کے کاموں کی طرف اور ظلم وزیادتی کی طرف اور مال حرام کھانے کی طرف لپک رہے ہیں، جو کچھ یہ کر رہے ہیں وه نہایت برے کام ہیں۔');
INSERT INTO "junagarri" VALUES(5,63,'5:63','انہیں ان کے عابد وعالم جھوٹ باتوں کے کہنے اور حرام چیزوں کے کھانے سے کیوں نہیں روکتے، بےشک برا کام ہے جو یہ کر رہے ہیں۔ ');
INSERT INTO "junagarri" VALUES(5,64,'5:64','اور یہودیوں نے کہا کہ اللہ تعالیٰ کے ہاتھ بندھے ہوئے ہیں ۔ انہی کے ہاتھ بندھے ہوئے ہیں اور ان کے اس قول کی وجہ سے ان پر لعنت کی گئی، بلکہ اللہ تعالیٰ کے دونوں ہاتھ کھلے ہوئے ہیں۔ جس طرح چاہتا ہے خرچ کرتا ہے اور جو کچھ تیری طرف تیرے رب کی جانب سے اتارا جاتا ہے وه ان میں سے اکثر کو تو سرکشی اور کفر میں اور بڑھا دیتا ہے اور ہم نے ان میں آپس میں ہی قیامت تک کے لئے عداوت اور بغض ڈال دیا ہے، وه جب کبھی لڑائی کی آگ کو بھڑکانا چاہتے ہیں تو اللہ تعالیٰ اسے بجھا دیتا ہے ، یہ ملک بھر میں شر وفساد مچاتے پھرتے ہیں اور اللہ تعالیٰ فسادیوں سے محبت نہیں کرتا۔');
INSERT INTO "junagarri" VALUES(5,65,'5:65','اور اگر یہ اہل کتاب ایمان لاتے اور تقویٰ اختیار کرتے تو ہم ان کی تمام برائیاں معاف فرما دیتے اور ضرور انہیں راحت و آرام کی جنتوں میں لے جاتے۔');
INSERT INTO "junagarri" VALUES(5,66,'5:66','اور اگر یہ لوگ تورات وانجیل اور ان کی جانب جو کچھ اللہ تعالیٰ کی طرف سے نازل فرمایا گیا ہے، ان کے پورے پابند رہتے تو یہ لوگ اپنے اوپر سے اور نیچے سے روزیاں پاتے اور کھاتے ، ایک جماعت تو ان میں سے درمیانہ روش کی ہے، باقی ان میں سے بہت سے لوگوں کے برے اعمال ہیں۔ ');
INSERT INTO "junagarri" VALUES(5,67,'5:67','اے رسول جو کچھ بھی آپ کی طرف آپ کے رب کی جانب سے نازل کیا گیا ہے پہنچا دیجیئے۔ اگر آپ نے ایسا نہ کیا تو آپ نے اللہ کی رسالت ادا نہیں کی ، اور آپ کو اللہ تعالیٰ لوگوں سے بچا لے گ ا بےشک اللہ تعالیٰ کافر لوگوں کو ہدایت نہیں دیتا۔');
INSERT INTO "junagarri" VALUES(5,68,'5:68','آپ کہہ دیجیئے کہ اے اہل کتاب! تم دراصل کسی چیز پر نہیں جب تک کہ تورات وانجیل کو اور جو کچھ تمہاری طرف تمہارے رب کی سے طرف اتارا گیا ہے قائم نہ کرو، جو کچھ آپ کی جانب آپ کے رب کی طرف سے اترا ہے وه ان میں سے بہتوں کو شرارت اور انکار میں اور بھی بڑھائے گا ہی ، تو آپ ان کافروں پر غمگین نہ ہوں۔');
INSERT INTO "junagarri" VALUES(5,69,'5:69','مسلمان، یہودی، ستاره پرست اور نصرانی کوئی ہو، جو بھی اللہ تعالیٰ پر اور قیامت کے دن پر ایمان لائے اور نیک عمل کرے وه محض بےخوف رہے گا اور بالکل بےغم ہوجائے گا۔ ');
INSERT INTO "junagarri" VALUES(5,70,'5:70','ہم نے بالیقین بنیاسرائیل سے عہد وپیمان لیا اور ان کی طرف رسولوں کو بھیجا، جب کبھی رسول ان کے پاس وه احکام لے کر آئے جو ان کی اپنی منشا کے خلاف تھے تو انہوں نے ان کی ایک جماعت کی تکذیب کی اور ایک جماعت کو قتل کردیا۔');
INSERT INTO "junagarri" VALUES(5,71,'5:71','اور سمجھ بیٹھے کہ کوئی پکڑ نہ ہوگی، پس اندھے بہرے بن بیٹھے، پھر اللہ تعالیٰ نے ان کی توبہ قبول کی، اس کے بعد بھی ان میں سے اکثر اندھے بہرے ہوگئے ۔ اللہ تعالیٰ ان کے اعمال کو بخوبی دیکھنے والا ہے۔');
INSERT INTO "junagarri" VALUES(5,72,'5:72','بے شک وه لوگ کافر ہوگئے جن کا قول ہے کہ مسیح ابن مریم ہی اللہ ہے حالانکہ خود مسیح نے ان سے کہا تھا کہ اے بنی اسرائیل! اللہ ہی کی عبادت کرو جو میرا اور تمہارا سب کا رب ہے ، یقین مانو کہ جو شخص اللہ کے ساتھ شریک کرتا ہے اللہ تعالیٰ نے اس پر جنت حرام کر دی ہے، اس کا ٹھکانہ جہنم ہی ہے اور گنہگاروں کی مدد کرنے والا کوئی نہیں ہوگا۔ ');
INSERT INTO "junagarri" VALUES(5,73,'5:73','وه لوگ بھی قطعاً کافر ہوگئے جنہوں نے کہا، اللہ تین میں کا تیسرا ہے ، دراصل سوا اللہ تعالیٰ کے کوئی معبود نہیں۔ اگر یہ لوگ اپنے اس قول سے باز نہ رہے تو ان میں سے جو کفر پر رہیں گے، انہیں المناک عذاب ضرور پہنچے گا۔');
INSERT INTO "junagarri" VALUES(5,74,'5:74','یہ لوگ کیوں اللہ تعالیٰ کی طرف نہیں جھکتے اور کیوں استغفار نہیں کرتے؟ اللہ تعالیٰ تو بہت ہی بخشنے والا اور بڑا ہی مہربان ہے۔');
INSERT INTO "junagarri" VALUES(5,75,'5:75','مسیح ابن مریم سوا پیغمبر ہونے کے اور کچھ بھی نہیں، اس سے پہلے بھی بہت سے پیغمبر ہوچکے ہیں ان کی والده ایک راست باز عورت تھیں دونوں ماں بیٹے کھانا کھایا کرتے تھے ، آپ دیکھیے کہ کس طرح ہم ان کے سامنے دلیلیں رکھتے ہیں پھر غور کیجیئے کہ کس طرح وه پھرے جاتے ہیں۔');
INSERT INTO "junagarri" VALUES(5,76,'5:76','آپ کہہ دیجیئے کہ کیا تم اللہ کے سوا ان کی عبادت کرتے ہو جو نہ تمہارے کسی نقصان کے مالک ہیں نہ کسی نفع کے، اللہ ہی خوب سننے اور پوری طرح جاننے والا ہے۔ ');
INSERT INTO "junagarri" VALUES(5,77,'5:77','کہہ دیجیئے اے اہل کتاب! اپنے دین میں ناحق غلو اور زیادتی نہ کرو اور ان لوگوں کی نفسانی خواہشوں کی پیروی نہ کرو جو پہلے سے بہک چکے ہیں اور بہتوں کو بہکا بھی چکے ہیں اور سیدھی راه سے ہٹ گئے ہیں۔');
INSERT INTO "junagarri" VALUES(5,78,'5:78','بنی اسرائیل کے کافروں پر (حضرت) داؤد (علیہ السلام) اور (حضرت) عیسیٰ بن مریم (علیہ السلام) کی زبانی لعنت کی گئی اس وجہ سے کہ وه نافرمانیاں کرتے تھے اور حد سے آگے بڑھ جاتے تھے۔ ');
INSERT INTO "junagarri" VALUES(5,79,'5:79','آپس میں ایک دوسرے کو برے کاموں سے جو وه کرتے تھے روکتے نہ تھے جو کچھ بھی یہ کرتے تھے یقیناً وه بہت برا تھا۔');
INSERT INTO "junagarri" VALUES(5,80,'5:80','ان میں سے بہت سے لوگوں کو آپ دیکھیں گے کہ وه کافروں سے دوستیاں کرتے ہیں، جو کچھ انہوں نے اپنے لئے آگے بھیج رکھا ہے وه بہت برا ہے کہ اللہ تعالیٰ ان سے ناراض ہوا اور وه ہمیشہ عذاب میں رہیں گے۔ ');
INSERT INTO "junagarri" VALUES(5,81,'5:81','اگر انہیں اللہ تعالیٰ پر اور نبی پر اور جو نازل کیا گیا ہے اس پر ایمان ہوتا تو یہ کفار سے دوستیاں نہ کرتے، لیکن ان میں کے اکثر لوگ فاسق ہیں۔ ');
INSERT INTO "junagarri" VALUES(5,82,'5:82','یقیناً آپ ایمان والوں کا سب سے زیاده دشمن یہودیوں اور مشرکوں کو پائیں گے اور ایمان والوں سے سب سے زیاده دوستی کے قریب آپ یقیناً انہیں پائیں گے جو اپنے آپ کو نصاریٰ کہتے ہیں، یہ اس لئے کہ ان میں علما اور عبادت کے لئے گوشہ نشین افراد پائے جاتے ہیں اور اس وجہ سے کہ وه تکبر نہیں کرتے۔ ');
INSERT INTO "junagarri" VALUES(5,83,'5:83','اور جب وه رسول کی طرف نازل کرده (کلام) کو سنتے ہیں تو آپ ان کی آنکھیں آنسو سے بہتی ہوئی دیکھتے ہیں اس سبب سے کہ انہوں نے حق کو پہچان لیا، وه کہتے ہیں کہ اے ہمارے رب! ہم ایمان لے آئے پس تو ہم کو بھی ان لوگوں کے ساتھ لکھ لے جو تصدیق کرتے ہیں۔');
INSERT INTO "junagarri" VALUES(5,84,'5:84','اور ہمارے پاس کون سا عذر ہے کہ ہم اللہ تعالیٰ پر اور جو حق ہم کو پہنچا ہے اس پر ایمان نہ لائیں اور ہم اس بات کی امید رکھتے ہیں کہ ہمارا رب ہم کو نیک لوگوں کی رفاقت میں داخل کردے گا۔ ');
INSERT INTO "junagarri" VALUES(5,85,'5:85','اس لئے ان کو اللہ تعالیٰ ان کے اس قول کی وجہ سے ایسے باغ دے گا جن کے نیچے نہریں جاری ہوں گی، یہ ان میں ہمیشہ ہمیشہ رہیں گے اور نیک لوگوں کا یہی بدلہ ہے۔');
INSERT INTO "junagarri" VALUES(5,86,'5:86','اور جن لوگوں نے کفر کیا اور ہماری آیات کو جھٹلاتے رہے وه لوگ دوزخ والے ہیں۔');
INSERT INTO "junagarri" VALUES(5,87,'5:87','اے ایمان والو! اللہ تعالیٰ نے جو پاکیزه چیزیں تمہارے واسطے حلال کی ہیں ان کو حرام مت کرو اور حد سے آگے مت نکلو، بےشک اللہ تعالیٰ حد سے نکلنے والوں کو پسند نہیں کرتا۔');
INSERT INTO "junagarri" VALUES(5,88,'5:88','اور اللہ تعالیٰ نے جو چیزیں تم کو دی ہیں ان میں سے حلال مرغوب چیزیں کھاؤ اور اللہ تعالیٰ سے ڈرو جس پر تم ایمان رکھتے ہو۔');
INSERT INTO "junagarri" VALUES(5,89,'5:89','اللہ تعالیٰ تمہاری قسموں میں لغو قسم پر تم سے مؤاخذہ نہیں فرماتا لیکن مؤاخذہ اس پر فرماتا ہے کہ تم جن قسموں کو مضبوط کردو ۔ اس کا کفاره دس محتاجوں کو کھانا دینا ہے اوسط درجے کا جو اپنے گھر والوں کو کھلاتے ہو یا ان کو کپڑا دینا یا ایک غلام یا لونڈی آزاد کرنا ہے اور جس کو مقدور نہ ہو تو تین دن کے روزے ہیں یہ تمہاری قسموں کا کفاره ہے جب کہ تم قسم کھا لو اور اپنی قسموں کا خیال رکھو! اسی طرح اللہ تعالیٰ تمہارے واسطے اپنے احکام بیان فرماتا ہے تاکہ تم شکر کرو۔');
INSERT INTO "junagarri" VALUES(5,90,'5:90','اے ایمان والو! بات یہی ہے کہ شراب اور جوا اور تھان اور فال نکالنے کے پانسے کے تیر، یہ سب گندی باتیں، شیطانی کام ہیں ان سے بالکل الگ رہو تاکہ تم فلاح یاب ہو۔ ');
INSERT INTO "junagarri" VALUES(5,91,'5:91','شیطان تو یوں چاہتا ہے کہ شراب اور جوئے کے ذریعے سے تمہارے آپس میں عداوت اور بغض واقع کرا دے اور اللہ تعالیٰ کی یاد سے اور نماز سے تم کو باز رکھے سو اب بھی باز آجاؤ۔');
INSERT INTO "junagarri" VALUES(5,92,'5:92','اور تم اللہ تعالیٰ کی اطاعت کرتے رہو اور رسول کی اطاعت کرتے رہو اور احتیاط رکھو۔ اگر اعراض کرو گے تو یہ جان رکھو کہ ہمارے رسول کے ذمہ صرف صاف صاف پہنچا دینا ہے۔');
INSERT INTO "junagarri" VALUES(5,93,'5:93','ایسے لوگوں پر جو کہ ایمان رکھتے ہوں اور نیک کام کرتے ہوں اس چیز میں کوئی گناه نہیں جس کو وه کھاتے پیتے ہوں جبکہ وه لوگ تقویٰ رکھتے ہوں اور ایمان رکھتے ہوں اور نیک کام کرتے ہوں پھر پرہیزگاری کرتے ہوں اور ایمان رکھتے ہوں پھر پرہیزگاری کرتے ہوں اور خوب نیک عمل کرتے ہوں، اللہ ایسے نیکوکاروں سے محبت رکھتا ہے۔ ');
INSERT INTO "junagarri" VALUES(5,94,'5:94','اے ایمان والو! اللہ تعالیٰ قدرے شکار سے تمہارا امتحان کرے گا جن تک تمہارے ہاتھ اور تمہارے نیزے پہنچ سکیں گے تاکہ اللہ تعالیٰ معلوم کرلے کہ کون شخص اس سے بن دیکھے ڈرتا ہے سو جو شخص اس کے بعد حد سے نکلے گا اس کے واسطے دردناک سزا ہے۔');
INSERT INTO "junagarri" VALUES(5,95,'5:95','اے ایمان والو! (وحشی) شکار کو قتل مت کرو جب کہ تم حالت احرام میں ہو ۔ اور جو شخص تم میں سے اس کو جان بوجھ کر قتل کرے گا تو اس پر فدیہ واجب ہوگا جو کہ مساوی ہوگا اس جانور کے جس کو اس نے قتل کیا ہے جس کا فیصلہ تم میں سے دو معتبر شخص کردیں خواه وه فدیہ خاص چوپایوں میں سے ہو جو نیاز کے طور پر کعبہ تک پہنچایا جائے اور خواه کفاره مساکین کو دے دیا جائے اور خواه اس کے برابر روزے رکھ لئے جائیں تاکہ اپنے کئے شامت کا مزه چکھے، اللہ تعالیٰ نے گذشتہ کو معاف کردیا اور جو شخص پھر ایسی ہی حرکت کرے گا تو اللہ انتقام لے گا اور اللہ زبردست ہے انتقام لینے والا۔');
INSERT INTO "junagarri" VALUES(5,96,'5:96','تمہارے لئے دریا کا شکار پکڑنا اور اس کا کھانا حلال کیا گیا ہے ۔ تمہارے فائده کے واسطے اور مسافروں کے واسطے اور خشکی کا شکار پکڑنا تمہارے لئے حرام کیا گیا ہے جب تک تم حالت احرام میں رہو اور اللہ تعالیٰ سے ڈرو جس کے پاس جمع کئے جاؤ گے۔');
INSERT INTO "junagarri" VALUES(5,97,'5:97','اللہ نے کعبہ کو جو ادب کا مکان ہے لوگوں کے قائم رہنے کا سبب قرار دے دیا اور عزت والے مہینہ کو بھی اور حرم میں قربانی ہونے والے جانور کو بھی اور ان جانوروں کو بھی جن کے گلے میں پٹے ہوں یہ اس لئے تاکہ تم اس بات کا یقین کر لو کہ بےشک اللہ تمام آسمانوں اور زمین کے اندر کی چیزوں کا علم رکھتا ہے اور بےشک اللہ سب چیزوں کو خوب جانتا ہے۔');
INSERT INTO "junagarri" VALUES(5,98,'5:98','تم یقین جانو کہ اللہ تعالیٰ سزا بھی سخت دینے والا ہے اور اللہ تعالیٰ بڑی مغفرت اور بڑی رحمت والا بھی ہے۔');
INSERT INTO "junagarri" VALUES(5,99,'5:99','رسول کے ذمہ تو صرف پہنچانا ہے۔ اور اللہ تعالیٰ سب جانتا ہے جو کچھ تم ظاہر کرتے ہو اور جو کچھ پوشیده رکھتے ہو۔');
INSERT INTO "junagarri" VALUES(5,100,'5:100','آپ فرما دیجیئے کہ ناپاک اور پاک برابر نہیں گو آپ کو ناپاک کی کثرت بھلی لگتی ہو اللہ تعالیٰ سے ڈرتے رہو اے عقل مندو! تاکہ تم کامیاب ہو۔');
INSERT INTO "junagarri" VALUES(5,101,'5:101','اے ایمان والو! ایسی باتیں مت پوچھو کہ اگر تم پر ظاہر کر دی جائیں تو تمہیں ناگوار ہوں اور اگر تم زمانہٴ نزول قرآن میں ان باتوں کو پوچھو گے تو تم پر ظاہر کردی جائیں گی سوالات گزشتہ اللہ نے معاف کردیئے اور اللہ بڑی مغفرت والا بڑے حلم والا ہے۔');
INSERT INTO "junagarri" VALUES(5,102,'5:102','ایسی باتیں تم سے پہلے اور لوگوں نے بھی پوچھی تھیں، پھر ان باتوں کے منکر ہوگئے۔ ');
INSERT INTO "junagarri" VALUES(5,103,'5:103','اللہ تعالیٰ نے نہ بحیره کو مشروع کیا ہے اور نہ سائبہ کو اور نہ وصیلہ کو اور نہ حام کو لیکن جو لوگ کافر ہیں وه اللہ تعالیٰ پر جھوٹ لگاتے ہیں اور اکثر کافر عقل نہیں رکھتے۔');
INSERT INTO "junagarri" VALUES(5,104,'5:104','اور جب ان سے کہا جاتا ہے کہ اللہ تعالیٰ نے جو احکام نازل فرمائے ہیں ان کی طرف اور رسول کی طرف رجوع کرو تو کہتے ہیں کہ ہم کو وہی کافی ہے جس پر ہم نے اپنے بڑوں کو پایا، کیا اگر چہ ان کے بڑے نہ کچھ سمجھ رکھتے ہوں اور نہ ہدایت رکھتے ہوں۔');
INSERT INTO "junagarri" VALUES(5,105,'5:105','اے ایمان والوں! اپنی فکر کرو، جب تم راه راست پر چل رہے ہو تو جو شخص گمراه رہے اس سے تمہارا کوئی نقصان نہیں ۔ اللہ ہی کے پاس تم سب کو جانا ہے پھر وه تم سب کو بتلا دے گا جو کچھ تم سب کرتے تھے۔');
INSERT INTO "junagarri" VALUES(5,106,'5:106','اے ایمان والو! تمہارے آپس میں دو شخص کا گواه ہونا مناسب ہے جبکہ تم میں سے کسی کو موت آنے لگے اور وصیت کرنے کا وقت ہو وه دو شخص ایسے ہوں کہ دیندار ہوں خواه تم میں سے ہوں یا غیر لوگوں میں سے دو شخص ہوں اگر تم کہیں سفر میں گئے ہو اور تمہیں موت آ جائے اگر تم کو شبہ ہو تو ان دونوں کو بعد نماز روک لو پھر دونوں اللہ کی قسم کھائیں کہ ہم اس قسم کے عوض کوئی نفع نہیں لینا چاہتے اگر چہ کوئی قرابت دار بھی ہو اور اللہ تعالیٰ کی بات کو ہم پوشیده نہ کریں گے، ہم اس حالت میں سخت گنہگار ہوں گے۔');
INSERT INTO "junagarri" VALUES(5,107,'5:107','پھر اگر اس کی اطلاع ہو کہ وه دونوں گواه کسی گناه کے مرتکب ہوئے ہیں تو ان لوگوں میں سے جن کے مقابلہ میں گناه کا ارتکاب ہوا تھا اور دو شخص جو سب میں قریب تر ہیں جہاں وه دونوں کھڑے ہوئے تھے یہ دونوں کھڑے ہوں پھر دونوں اللہ کی قسم کھائیں کہ بالیقین ہماری یہ قسم ان دونوں کی اس قسم سے زیاده راست ہے اور ہم نے ذرا تجاوز نہیں کیا، ہم اس حالت میں سخت ظالم ہوں گے۔');
INSERT INTO "junagarri" VALUES(5,108,'5:108','یہ قریب ذریعہ ہے اس امر کا کہ وه لوگ واقعہ کو ٹھیک طور پر ظاہر کریں یا اس بات سے ڈر جائیں کہ ان سے قسمیں لینے کے بعد قسمیں الٹی پڑ جائیں گی اور اللہ تعالیٰ سے ڈرو اور سنو! اور اللہ تعالیٰ فاسق لوگوں کو ہدایت نہیں کرتا۔');
INSERT INTO "junagarri" VALUES(5,109,'5:109','جس روز اللہ تعالیٰ تمام پیغمبروں کو جمع کرے گا، پھر ارشاد فرمائے گا کہ تم کو کیا جواب ملا تھا، وه عرض کریں گے کہ ہم کو کچھ خبر نہیں تو ہی بےشک پوشیده باتوں کو پورا جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(5,110,'5:110','جب کہ اللہ تعالیٰ ارشاد فرمائے گا کہ اے عیسیٰ بن مریم! میرا انعام یاد کرو جو تم پر اور تمہاری والده پر ہوا ہے، جب میں نے تم کو روح القدس سے تائید دی۔ تم لوگوں سے کلام کرتے تھے گود میں بھی اور بڑی عمر میں بھی اور جب کہ میں نے تم کو کتاب اور حکمت کی باتیں اور تورات اور انجیل کی تعلیم دی اور جب کہ تم میرے حکم سے گارے سے ایک شکل بناتے تھے جیسے پرنده کی شکل ہوتی ہے پھر تم اس کے اندر پھونک مار دیتے تھے جس سے وه پرند بن جاتا تھا میرے حکم سے اور تم اچھا کر دیتے تھے مادرزاد اندھے کو اور کوڑھی کو میرے حکم سے اور جب کہ تم مردوں کو نکال کر کھڑا کرلیتے تھے میرے حکم سے اور جب کہ میں نے بنی اسرائیل کو تم سے باز رکھا جب تم ان کے پاس دلیلیں لے کر آئے تھے پھر ان میں جو کافر تھے انہوں نے کہا تھا کہ بجز کھلے جادو کے یہ اور کچھ بھی نہیں۔ ');
INSERT INTO "junagarri" VALUES(5,111,'5:111','اور جب کہ میں نے حواریین کو حکم دیا کہ تم مجھ پر اور میرے رسول پر ایمان لاؤ، انہوں نے کہا کہ ہم ایمان لائے اور آپ شاہد رہیئے کہ ہم پورے فرماں بردار ہیں۔');
INSERT INTO "junagarri" VALUES(5,112,'5:112','وه وقت یاد کے قابل ہے جب کہ حواریوں نے عرض کیا کہ اے عیسیٰ بن مریم! کیا آپ کا رب ایسا کرسکتا ہے کہ ہم پر آسمان سے ایک خوان ﴿دسترخوان﴾ نازل فرما دے؟ آپ نے فرمایا کہ اللہ سے ڈرو اگر تم ایمان والے ہو۔ ');
INSERT INTO "junagarri" VALUES(5,113,'5:113','وه بولے کہ ہم یہ چاہتے ہیں کہ اس میں سے کھائیں اور ہمارے دلوں کو پورا اطمینان ہوجائے اور ہمارا یہ یقین اور بڑھ جائے کہ آپ نے ہم سے سچ بولا ہے اور ہم گواہی دینے والوں میں سے ہوجائیں۔');
INSERT INTO "junagarri" VALUES(5,114,'5:114','عیسیٰ ابن مریم نے دعا کی کہ اے اللہ اے ہمارے پروردگار! ہم پر آسمان سے کھانا نازل فرما! کہ وه ہمارے لئے یعنی ہم میں جو اول ہیں اور جو بعد کے ہیں سب کے لئے ایک خوشی کی بات ہوجائے اور تیری طرف سے ایک نشانی ہوجائے اور تو ہم کو رزق عطا فرمادے اور تو سب عطا کرنے والوں سے اچھا ہے۔');
INSERT INTO "junagarri" VALUES(5,115,'5:115','حق تعالیٰ نے ارشاد فرمایا کہ میں وه کھانا تم لوگوں پر نازل کرنے والا ہوں، پھر جو شخص تم میں سے اس کے بعد ناحق شناسی کرے گا تو میں اس کو ایسی سزا دوں گا کہ وه سزا دنیا جہان والوں میں سے کسی کو نہ دوں گا۔ ');
INSERT INTO "junagarri" VALUES(5,116,'5:116','اور وه وقت بھی قابل ذکر ہے جب کہ اللہ تعالیٰ فرمائے گا کہ اے عیسیٰ ابن مریم! کیا تم نے ان لوگوں سے کہہ دیا تھا کہ مجھ کو اور میری ماں کو بھی علاوه اللہ کے معبود قرار دے لو! عیسیٰ عرض کریں گے کہ میں تو تجھ کو منزہ سمجھتا ہوں، مجھ کو کسی طرح زیبا نہ تھا کہ میں ایسی بات کہتا جس کے کہنے کا مجھ کو کوئی حق نہیں، اگر میں نے کہا ہوگا تو تجھ کو اس کا علم ہوگا۔ تو، تو میرے دل کے اندر کی بات بھی جانتا ہے اور میں تیرے نفس میں جو کچھ ہے اس کو نہیں جانتا ۔ تمام غیبوں کا جاننے والا تو ہی ہے۔');
INSERT INTO "junagarri" VALUES(5,117,'5:117','میں نے تو ان سے اور کچھ نہیں کہا مگر صرف وہی جو تو نے مجھ سے کہنے کو فرمایا تھا کہ تم اللہ کی بندگی اختیار کرو جو میرا بھی رب ہے اور تمہارا بھی رب ہے ۔ میں ان پر گواه رہا جب تک ان میں رہا۔ پھر جب تو نے مجھ کو اٹھا لیا تو، تو ہی ان پر مطلع رہا ۔ اور تو ہر چیز کی پوری خبر رکھتا ہے۔');
INSERT INTO "junagarri" VALUES(5,118,'5:118','اگر تو ان کو سزا دے تو یہ تیرے بندے ہیں اور اگر تو ان کو معاف فرما دے تو، تو زبردست ہے حکمت والا ہے۔ ');
INSERT INTO "junagarri" VALUES(5,119,'5:119','اللہ ارشاد فرمائے گا کہ یہ وه دن ہے کہ جو لوگ سچے تھے ان کا سچا ہونا ان کے کام آئے گا ان کو باغ ملیں گے جن کے نیچے نہریں جاری ہوں گی جن میں وه ہمیشہ ہمیشہ کو رہیں گے۔ اللہ تعالیٰ ان سے راضی اور خوش اور یہ اللہ سے راضی اور خوش ہیں، یہ بڑی (بھاری) کامیابی ہے۔');
INSERT INTO "junagarri" VALUES(5,120,'5:120','اللہ ہی کی ہے سلطنت آسمانوں کی اور زمین کی اور ان چیزوں کی جو ان میں موجود ہیں اور وه ہر شے پر پوری قدرت رکھتا ہے۔');
INSERT INTO "junagarri" VALUES(6,1,'6:1','تمام تعریفیں اللہ ہی کے لائق ہیں جس نے آسمانوں کو اور زمین کو پیدا کیا اور تاریکیوں اور نور کو بنایا پھر بھی کافر لوگ (غیر اللہ کو) اپنے رب کے برابر قرار دیتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(6,2,'6:2','وه ایسا ہے جس نے تم کو مٹی سے بنایا پھر ایک وقت معین کیا اور (دوسرا) معین وقت خاص اللہ ہی کے نزدیک ہے پھر بھی تم شک رکھتے ہو۔ ');
INSERT INTO "junagarri" VALUES(6,3,'6:3','اور وہی ہے معبود برحق آسمانوں میں بھی اور زمین میں بھی، وه تمہارے پوشیده احوال کو بھی اور تمہارے ظاہر احوال کو بھی جانتا ہے اور تم جو کچھ عمل کرتے ہو اس کو بھی جانتا ہے۔ ');
INSERT INTO "junagarri" VALUES(6,4,'6:4','اور ان کے پاس کوئی نشانی بھی ان کے رب کی نشانیوں میں نہیں آتی مگر وه اس سے اعراض ہی کرتے ہیں۔');
INSERT INTO "junagarri" VALUES(6,5,'6:5','انہوں نے اس سچی کتاب کو بھی جھٹلایا جب کہ وه ان کے پاس پہنچی، سو جلدی ہی ان کو خبر مل جائے گی اس چیز کی جس کے ساتھ یہ لوگ استہزا کیا کرتے تھے۔ ');
INSERT INTO "junagarri" VALUES(6,6,'6:6','کیا انہوں نے دیکھا نہیں کہ ہم ان سے پہلے کتنی جماعتوں کو ہلاک کرچکے ہیں جن کو ہم نے دنیا میں ایسی قوت دی تھی کہ تم کو وه قوت نہیں دی اور ہم نے ان پر خوب بارشیں برسائیں اور ہم نے ان کے نیچے سے نہریں جاری کیں۔ پھر ہم نے ان کو ان کے گناہوں کے سبب ہلاک کر ڈالا اور ان کے بعد دوسری جماعتوں کو پیدا کر دیا۔ ');
INSERT INTO "junagarri" VALUES(6,7,'6:7','اور اگر ہم کاغذ پر لکھا ہوا کوئی نوشتہ آپ پر نازل فرماتے پھر اس کو یہ لوگ اپنے ہاتھوں سے چھو بھی لیتے تب بھی یہ کافر لوگ یہی کہتے کہ یہ کچھ بھی نہیں مگر صریح جادو ہے۔ ');
INSERT INTO "junagarri" VALUES(6,8,'6:8','اور یہ لوگ یوں کہتے ہیں کہ ان کے پاس کوئی فرشتہ کیوں نہیں اتارا گیا اور اگر ہم کوئی فرشتہ بھیج دیتے تو سارا قصہ ہی ختم ہوجاتا۔ پھر ان کو ذرا مہلت نہ دی جاتی۔ ');
INSERT INTO "junagarri" VALUES(6,9,'6:9','اور اگر ہم اس کو فرشتہ تجویز کرتے تو ہم اس کو آدمی ہی بناتے اور ہمارے اس فعل سے پھر ان پر وہی اشکال ہوتا جو اب اشکال کر رہے ہیں۔ ');
INSERT INTO "junagarri" VALUES(6,10,'6:10','اور واقعی آپ سے پہلے جو پیغمبر ہوئے ہیں ان کے ساتھ بھی استہزا کیا گیا ہے۔ پھر جن لوگوں نے ان سے مذاق کیا تھا ان کو اس عذاب نے آ گھیرا جس کا تمسخر اڑاتے تھے۔');
INSERT INTO "junagarri" VALUES(6,11,'6:11','آپ فرما دیجئے کہ ذرا زمین میں چلو پھرو پھر دیکھ لو کہ تکذیب کرنے والوں کا کیا انجام ہوا۔');
INSERT INTO "junagarri" VALUES(6,12,'6:12','آپ کہئے کہ جو کچھ آسمانوں اور زمین میں موجود ہے یہ سب کس کی ملکیت ہے، آپ کہہ دیجئے کہ سب اللہ ہی کی ملکیت ہے، اللہ نے مہربانی فرمانا اپنے اوپر لازم فرما لیا ہے تم کو اللہ قیامت کے روز جمع کرے گا، اس میں کوئی شک نہیں، جن لوگوں نے اپنے آپ کو گھاٹے میں ڈالا ہے سو وه ایمان نہیں لائیں گے۔');
INSERT INTO "junagarri" VALUES(6,13,'6:13','اور اللہ ہی کی ملک ہیں وه سب کچھ جو رات میں اور دن میں رہتی ہیں اور وہی بڑا سننے والا بڑا جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(6,14,'6:14','آپ کہیے کہ کیا اللہ کے سوا، جو کہ آسمانوں اور زمین کا پیدا کرنے والا ہے اور جو کہ کھانے کو دیتا ہے اور اس کو کوئی کھانے کو نہیں دیتا، اور کسی کو معبود قرار دوں ، آپ فرما دیجئے کہ مجھ کو یہ حکم ہوا ہے کہ سب سے پہلے میں اسلام قبول کروں اور تو مشرکین میں ہرگز نہ ہونا۔');
INSERT INTO "junagarri" VALUES(6,15,'6:15','آپ کہہ دیجئے کہ میں اگر اپنے رب کا کہنا نہ مانوں تو میں ایک بڑے دن کے عذاب سے ڈرتا ہوں۔ ');
INSERT INTO "junagarri" VALUES(6,16,'6:16','جس شخص سے اس روز وه عذاب ہٹا دیا جائے تو اس پر اللہ نے بڑا رحم کیا اور یہ صریح کامیابی ہے۔ ');
INSERT INTO "junagarri" VALUES(6,17,'6:17','اور اگر تجھ کو اللہ تعالیٰ کوئی تکلیف پہنچائے تو اس کا دور کرنے والا سوائے اللہ تعالیٰ کے اور کوئی نہیں۔ اور اگر تجھ کو اللہ تعالیٰ کوئی نفع پہنچائے تو وه ہر چیز پر پوری قدرت رکھنے والا ہے۔ ');
INSERT INTO "junagarri" VALUES(6,18,'6:18','اور وہی اللہ اپنے بندوں کے اوپر غالب ہے برتر ہے اور وہی بڑی حکمت والا اور پوری خبر رکھنے والا ہے۔');
INSERT INTO "junagarri" VALUES(6,19,'6:19','آپ کہئے کہ سب سے بڑی چیز گواہی دینے کے لئے کون ہے، آپ کہئے کہ میرے اور تمہارے درمیان اللہ گواه ہے اور میرے پاس یہ قرآن بطور وحی کے بھیجا گیا ہے تاکہ میں اس قرآن کے ذریعے سے تم کو اور جس جس کو یہ قرآن پہنچے ان سب کو ڈراؤں کیا تم سچ مچ یہی گواہی دو گے کہ اللہ تعالیٰ کے ساتھ کچھ اور معبود بھی ہیں، آپ کہہ دیجئے کہ میں تو گواہی نہیں دیتا۔ آپ فرما دیجئے کہ بس وه تو ایک ہی معبود ہے اور بےشک میں تمہارے شرک سے بیزار ہوں۔');
INSERT INTO "junagarri" VALUES(6,20,'6:20','جن لوگوں کو ہم نے کتاب دی ہے وه لوگ رسول کو پہچانتے ہیں جس طرح اپنے بیٹوں کو پہنچانتے ہیں۔ جن لوگوں نے اپنے آپ کو گھاٹے میں ڈالا ہے سو وه ایمان نہیں لائیں گے۔ ');
INSERT INTO "junagarri" VALUES(6,21,'6:21','اور اس سے زیاده بےانصاف کون ہوگا جو اللہ تعالیٰ پر جھوٹ بہتان باندھے یا اللہ کی آیات کو جھوٹا بتلائے ایسے بےانصافوں کو کامیابی نہ ہوگی۔ ');
INSERT INTO "junagarri" VALUES(6,22,'6:22','اور وه وقت بھی یاد کرنے کے قابل ہے جس روز ہم ان تمام خلائق کو جمع کریں گے، پھر ہم مشرکین سے کہیں گے کہ تمہارے وه شرکا، جن کے معبود ہونے کا تم دعویٰ کرتے تھے، کہاں گئے؟');
INSERT INTO "junagarri" VALUES(6,23,'6:23','پھر ان کے شرک کا انجام اس کے سوا اور کچھ بھی نہ ہوگا کہ وه یوں کہیں گے کہ قسم اللہ کی اپنے پروردگار کی ہم مشرک نہ تھے۔ ');
INSERT INTO "junagarri" VALUES(6,24,'6:24','ذرا دیکھو تو انہوں نے کس طرح جھوٹ بولا اپنی جانوں پر اور جن چیزوں کو وه جھوٹ موٹ تراشا کرتے تھے وه سب غائب ہوگئے۔ ');
INSERT INTO "junagarri" VALUES(6,25,'6:25','اور ان میں بعض ایسے ہیں کہ آپ کی طرف کان لگاتے ہیں اور ہم نے ان کے دلوں پر پرده ڈال رکھا ہے اس سے کہ وه اس کو سمجھیں اور ان کے کانوں میں ڈاٹ دے رکھی ہے اور اگر وه لوگ تمام دلائل کو دیکھ لیں تو بھی ان پر کبھی ایمان نہ لائیں، یہاں تک کہ جب یہ لوگ آپ کے پاس آتے ہیں تو آپ سے خواه مخواه جھگڑتے ہیں، یہ لوگ جو کافر ہیں یوں کہتے ہیں کہ یہ تو کچھ بھی نہیں صرف بے سند باتیں ہیں جو پہلوں سے چلی آ رہی ہیں۔ ');
INSERT INTO "junagarri" VALUES(6,26,'6:26','اور یہ لوگ اس سے دوسروں کو بھی روکتے ہیں اور خود بھی اس سے دور دور رہتے ہیں اور یہ لوگ اپنے ہی کو تباه کر رہے ہیں اور کچھ خبر نہیں رکھتے۔ ');
INSERT INTO "junagarri" VALUES(6,27,'6:27','اور اگر آپ اس وقت دیکھیں جب کہ یہ دوزخ کے پاس کھڑے کئے جائیں تو کہیں گے ہائے کیا اچھی بات ہو کہ ہم پھر واپس بھیج دیئے جائیں اور اگر ایسا ہو جائے تو ہم اپنے رب کی آیات کو جھوٹا نہ بتلائیں اور ہم ایمان والوں میں سے ہو جائیں۔ ');
INSERT INTO "junagarri" VALUES(6,28,'6:28','بلکہ جس چیز کو اس کے قبل چھپایا کرتے تھے وه ان کے سامنے آگئی ہے1 اور اگر یہ لوگ پھر واپس بھیج دیئے جائیں تب بھی یہ وہی کام کریں گے جس سے ان کو منع کیا گیا تھا اور یقیناً یہ بالکل جھوٹے ہیں۔ ');
INSERT INTO "junagarri" VALUES(6,29,'6:29','اور یہ کہتے ہیں کہ صرف یہی دنیاوی زندگی ہماری زندگی ہے اور ہم زنده نہ کئے جائیں گے۔ ');
INSERT INTO "junagarri" VALUES(6,30,'6:30','اور اگر آپ اس وقت دیکھیں جب یہ اپنے رب کے سامنے کھڑے کئے جائیں گے۔ اللہ فرمائے گا کہ کیا یہ امر واقعی نہیں ہے؟ وه کہیں گے بےشک قسم اپنے رب کی۔ اللہ تعالیٰ فرمائے گا تو اب اپنے کفر کے عوض عذاب چکھو۔ ');
INSERT INTO "junagarri" VALUES(6,31,'6:31','بےشک خساره میں پڑے وه لوگ جنہوں نے اللہ سے ملنے کی تکذیب کی، یہاں تک کہ جب وه معین وقت ان پر دفعتاً آ پہنچے گا، کہیں گے کہ ہائے افسوس ہماری کوتاہی پر جو اس کے بارے میں ہوئی، اور حالت ان کی یہ ہوگی کہ وه اپنے بار اپنی پیٹھوں پر لادے ہوں گے، خوب سن لو کہ بری ہوگی وه چیز جس کو وه لادیں گے۔ ');
INSERT INTO "junagarri" VALUES(6,32,'6:32','اور دنیاوی زندگانی تو کچھ بھی نہیں بجز لہو ولعب کے۔ اور دار آخرت متقیوں کے لئے بہتر ہے۔ کیا تم سوچتے سمجھتے نہیں ہو۔');
INSERT INTO "junagarri" VALUES(6,33,'6:33','ہم خوب جانتے ہیں کہ آپ کو ان کے اقوال مغموم کرتے ہیں، سو یہ لوگ آپ کو جھوٹا نہیں کہتے لیکن یہ ظالم تو اللہ کی آیتوں کا انکار کرتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(6,34,'6:34','اور بہت سے پیغمبر جو آپ سے پہلے ہوئے ہیں ان کی بھی تکذیب کی جا چکی ہے سو انہوں نے اس پر صبر ہی کیا، ان کی تکذیب کی گئی اور ان کو ایذائیں پہنچائی گئیں یہاں تک کہ ہماری امداد ان کو پہنچی اور اللہ کی باتوں کا کوئی بدلنے والا نہیں اور آپ کے پاس بعض پیغمبروں کی بعض خبریں پہنچ چکی ہیں۔ ');
INSERT INTO "junagarri" VALUES(6,35,'6:35','اور اگر آپ کو ان کا اعراض گراں گزرتا ہے تو اگر آپ کو یہ قدرت ہے کہ زمین میں کوئی سرنگ یا آسمان میں کوئی سیڑھی ڈھونڈ لو پھر کوئی معجزه لے آؤ تو کرو اور اگر اللہ کو منظور ہوتا تو ان سب کو راه راست پر جمع کر دیتا سو آپ نادانوں میں سے نہ ہوجائیے۔ ');
INSERT INTO "junagarri" VALUES(6,36,'6:36','وہی لوگ قبول کرتے ہیں جو سنتے ہیں ۔ اور مُردوں کو اللہ زنده کر کے اٹھائے گا پھر سب اللہ ہی کی طرف لائے جائیں گے۔');
INSERT INTO "junagarri" VALUES(6,37,'6:37','اور یہ لوگ کہتے ہیں کہ ان پر کوئی معجزه کیوں نہیں نازل کیا گیا ان کے رب کی طرف سے آپ فرما دیجئے کہ اللہ تعالیٰ کو بےشک پوری قدرت ہے اس پر کہ وه معجزه نازل فرمادے ۔ لیکن ان میں اکثر بےخبر ہیں۔ ');
INSERT INTO "junagarri" VALUES(6,38,'6:38','اور جتنے قسم کے جاندار زمین پر چلنے والے ہیں اور جتنے قسم کے پرند جانور ہیں کہ اپنے دونوں بازوؤں سے اڑتے ہیں ان میں کوئی قسم ایسی نہیں جو کہ تمہاری طرح کے گروه نہ ہوں ، ہم نے دفتر میں کوئی چیز نہیں چھوڑی پھر سب اپنے پروردگار کے پاس جمع کئے جائیں گے۔ ');
INSERT INTO "junagarri" VALUES(6,39,'6:39','اور جو لوگ ہماری آیتوں کی تکذیب کرتے ہیں وه تو طرح طرح کی ظلمتوں میں بہرے گونگے ہو رہے ہیں، اللہ جس کو چاہے بے راه کردے اور وه جس کو چاہے سیدھی راه پر لگا دے۔ ');
INSERT INTO "junagarri" VALUES(6,40,'6:40','آپ کہیے کہ اپنا حال تو بتلاؤ کہ اگر تم پر اللہ کا کوئی عذاب آ پڑے یا تم پر قیامت ہی آ پہنچے تو کیا اللہ کے سوا کسی اور کو پکارو گے۔ اگر تم سچے ہو۔');
INSERT INTO "junagarri" VALUES(6,41,'6:41','بلکہ خاص اسی کو پکارو گے، پھر جس کے لئے تم پکارو گے اگر وه چاہے تو اس کو ہٹا بھی دے اور جن کو تم شریک ٹھہراتے ہو ان سب کو بھول بھال جاؤ گے۔ ');
INSERT INTO "junagarri" VALUES(6,42,'6:42','اور ہم نے اور امتوں کی طرف بھی جو کہ آپ سے پہلے گزر چکی ہیں پیغمبر بھیجے تھے، سو ہم نے ان کو تنگدستی اور بیماری سے پکڑا تاکہ وه اظہار عجز کرسکیں۔');
INSERT INTO "junagarri" VALUES(6,43,'6:43','سو جب ان کو ہماری سزا پہنچی تھی تو انہوں نے عاجزی کیوں نہیں اختیار کی؟ لیکن ان کے قلوب سخت ہوگئے اور شیطان نے ان کے اعمال کو ان کے خیال میں آراستہ کردیا۔ ');
INSERT INTO "junagarri" VALUES(6,44,'6:44','پھر جب وه لوگ ان چیزوں کو بھولے رہے جن کی ان کو نصیحت کی جاتی تھی تو ہم نے ان پر ہر چیز کے دروازے کشاده کردئے یہاں تک کہ جب ان چیزوں پر جو کہ ان کو ملی تھیں وه خوب اترا گئے ہم نے ان کو دفعتاً پکڑ لیا، پھر تو وه بالکل مایوس ہوگئے۔');
INSERT INTO "junagarri" VALUES(6,45,'6:45','پھر ظالم لوگوں کی جڑ کٹ گئی اور اللہ تعالیٰ کا شکر ہے جو تمام عالم کا پروردگار ہے۔ ');
INSERT INTO "junagarri" VALUES(6,46,'6:46','آپ کہئے کہ یہ بتلاؤ اگر اللہ تعالیٰ تمہاری سماعت اور بصارت بالکل لے لے اور تمہارے دلوں پر مہر کردے تو اللہ تعالیٰ کے سوا اور کوئی معبود ہے کہ یہ تم کو پھر دے دے۔ آپ دیکھئے تو ہم کس طرح دلائل کو مختلف پہلوؤں سے پیش کر رہے ہیں پھر بھی یہ اعراض کرتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(6,47,'6:47','آپ کہئے کہ یہ بتلاؤ اگر تم پر اللہ تعالیٰ کا عذاب آ پڑے خواه اچانک یا اعلانیہ تو کیا بجز ظالم لوگوں کے اور بھی کوئی ہلاک کیا جائے گا۔ ');
INSERT INTO "junagarri" VALUES(6,48,'6:48','اور ہم پیغمبروں کو صرف اس واسطے بھیجا کرتے ہیں کہ وه بشارت دیں اور ڈرائیں پھر جو ایمان لے آئے اور درستی کرلے سو ان لوگوں پر کوئی اندیشہ نہیں اور نہ وه مغموم ہوں گے۔ ');
INSERT INTO "junagarri" VALUES(6,49,'6:49','اور جو لوگ ہماری آیتوں کو جھوٹا بتلائیں ان کو عذاب پہنچے گا بوجہ اس کے کہ وه نافرمانی کرتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(6,50,'6:50','آپ کہہ دیجئے کہ نہ تو میں تم سے یہ کہتا ہوں کہ میرے پاس اللہ کے خزانے ہیں اور نہ میں غیب جانتا ہوں اور نہ میں تم سے یہ کہتا ہوں کہ میں فرشتہ ہوں۔ میں تو صرف جو کچھ میرے پاس وحی آتی ہے اس کی اتباع کرتا ہو ں آپ کہئے کہ اندھا اور بینا کہیں برابر ہوسکتا ہے ۔ سو کیا تم غور نہیں کرتے؟');
INSERT INTO "junagarri" VALUES(6,51,'6:51','اور ایسے لوگوں کو ڈرائیے جو اس بات سے اندیشہ رکھتے ہیں کہ اپنے رب کے پاس ایسی حالت میں جمع کئے جائیں گے کہ جتنے غیر اللہ ہیں نہ کوئی ان کا مددگار ہوگا اور نہ کوئی شفیع، اس امید پر کہ وه ڈر جائیں۔ ');
INSERT INTO "junagarri" VALUES(6,52,'6:52','اور ان لوگوں کو نہ نکالیے جو صبح وشام اپنے پروردگار کی عبادت کرتے ہیں، خاص اسی کی رضامندی کا قصد رکھتے ہیں۔ ان کا حساب ذرا بھی آپ کے متعلق نہیں اور آپ کا حساب ذرا بھی ان کے متعلق نہیں کہ آپ ان کو نکال دیں۔ ورنہ آپ ظلم کرنے والوں میں سے ہو جائیں گے۔ ');
INSERT INTO "junagarri" VALUES(6,53,'6:53','اور اسی طرح ہم نے بعض کو بعض کے ذریعہ سے آزمائش میں ڈال رکھا ہے تاکہ یہ لوگ کہا کریں، کیا یہ لوگ ہیں کہ ہم سب میں سے ان پر اللہ تعالیٰ نے فضل کیا ہے ۔ کیا یہ بات نہیں ہے کہ اللہ تعالیٰ شکر گزاروں کو خوب جانتا ہے۔ ');
INSERT INTO "junagarri" VALUES(6,54,'6:54','اور یہ لوگ جب آپ کے پاس آئیں جو ہماری آیتوں پر ایمان رکھتے ہیں تو (یوں) کہہ دیجیئے کہ تم پر سلامتی ہے تمہارے رب نے مہربانی فرمانا اپنے ذمہ مقرر کرلیا ہے کہ جو شخص تم میں سے برا کام کر بیٹھے جہالت سے پھر وه اس کے بعد توبہ کر لے اور اصلاح رکھے تو اللہ (کی یہ شان ہے کہ وه) بڑی مغفرت کرنے والا ہے بڑی رحمت والا ہے۔ ');
INSERT INTO "junagarri" VALUES(6,55,'6:55','اسی طرح ہم آیات کی تفصیل کرتے رہتے ہیں اور تاکہ مجرمین کا طریقہ ظاہر ہوجائے۔');
INSERT INTO "junagarri" VALUES(6,56,'6:56','آپ کہہ دیجئے کہ مجھ کو اس سے ممانعت کی گئی ہے کہ ان کی عبادت کروں جن کو تم لوگ اللہ تعالیٰ کو چھوڑ کر پکارتے ہو۔ آپ کہہ دیجئے کہ میں تمہاری خواہشات کی اتباع نہ کروں گا کیوں کہ اس حالت میں تو میں بے راه ہوجاؤں گا اور راه راست پر چلنے والوں میں نہ رہوں گا۔ ');
INSERT INTO "junagarri" VALUES(6,57,'6:57','آپ کہہ دیجئے کہ میرے پاس تو ایک دلیل ہے میرے رب کی طرف سے اور تم اس کی تکذیب کرتے ہو، جس چیز کی تم جلدبازی کر رہے ہو وه میرے پاس نہیں۔ حکم کسی کا نہیں بجز اللہ تعالیٰ کے اللہ تعالیٰ واقعی بات کو بتلا دیتا ہے اور سب سے اچھا فیصلہ کرنے والا وہی ہے۔');
INSERT INTO "junagarri" VALUES(6,58,'6:58','آپ کہہ دیجئے کہ اگر میرے پاس وه چیز ہوتی جس کا تم تقاضا کر رہے ہو تو میرا اور تمہارا باہمی قصہٴ فیصل ہوچکا ہوتا اور ظالموں کو اللہ تعالیٰ خوب جانتا ہے۔');
INSERT INTO "junagarri" VALUES(6,59,'6:59','اور اللہ تعالیٰ ہی کے پاس ہیں غیب کی کنجیاں، (خزانے) ان کو کوئی نہیں جانتا بجز اللہ کے۔ اور وه تمام چیزوں کو جانتا ہے جو کچھ خشکی میں ہیں اور جو کچھ دریاؤں میں ہیں اور کوئی پتا نہیں گرتا مگر وه اس کو بھی جانتا ہے اور کوئی دانہ زمین کے تاریک حصوں میں نہیں پڑتا اور نہ کوئی تر اور نہ کوئی خشک چیز گرتی ہے مگر یہ سب کتاب مبین میں ہیں۔ ');
INSERT INTO "junagarri" VALUES(6,60,'6:60','اور وه ایسا ہے کہ رات میں تمہاری روح کو (ایک گونہ) قبض کردیتا ہے اور جو کچھ تم دن میں کرتے ہو اس کو جانتا ہے پھر تم کو جگا اٹھاتا ہے تاکہ میعاد معین تمام کر دی جائے پھر اسی کی طرف تم کو جانا ہے پھر تم کو بتلائے گا جو کچھ تم کیا کرتے تھے۔');
INSERT INTO "junagarri" VALUES(6,61,'6:61','اور وہی اپنے بندوں کے اوپر غالب ہے برتر ہے اور تم پر نگہداشت رکھنے والے بھیجتا ہے یہاں تک کہ جب تم میں سے کسی کو موت آ پہنچتی ہے، اس کی روح ہمارے بھیجے ہوئے فرشتے قبض کرلیتے ہیں اور وه ذرا کوتاہی نہیں کرتے۔ ');
INSERT INTO "junagarri" VALUES(6,62,'6:62','پھر سب اپنے مالک حقیقی کے پاس لائے جائیں گے ۔ خوب سن لو فیصلہ اللہ ہی کا ہوگا اور وه بہت جلد حساب لے گا۔');
INSERT INTO "junagarri" VALUES(6,63,'6:63','آپ کہئے کہ وه کون ہے جو تم کو خشکی اور دریا کی ظلمات سے نجات دیتا ہے۔ تم اس کو پکارتے ہو گڑگڑا کر چپکے چپکے، کہ اگر تو ہم کو ان سے نجات دے دے تو ہم ضرور شکر کرنے والوں میں سے ہوجائیں گے۔');
INSERT INTO "junagarri" VALUES(6,64,'6:64','آپ کہہ دیجئے کہ اللہ ہی تم کو ان سے نجات دیتا ہے اور ہر غم سے، تم پھر بھی شرک کرنے لگتے ہو۔');
INSERT INTO "junagarri" VALUES(6,65,'6:65','آپ کہئے کہ اس پر بھی وہی قادر ہے کہ تم پر کوئی عذاب تمہارے اوپر سے بھیج دے یا تمہارے پاؤں تلے سے یا کہ تم کو گروه گروه کرکے سب کو بھڑا دے اور تمہارے ایک کو دوسرے کی لڑائی چکھا دے ۔ آپ دیکھیے تو سہی ہم کس طرح دلائل مختلف پہلوؤں سے بیان کرتے ہیں شاید وه سمجھ جائیں۔');
INSERT INTO "junagarri" VALUES(6,66,'6:66','اور آپ کی قوم اس کی تکذیب کرتی ہے حالانکہ وه یقینی ہے۔ آپ کہہ دیجئے کہ میں تم پر تعینات نہیں کیا گیا ہوں۔ ');
INSERT INTO "junagarri" VALUES(6,67,'6:67','ہر خبر (کے وقوع) کا ایک وقت ہے اور جلد ہی تم کو معلوم ہوجائے گا۔');
INSERT INTO "junagarri" VALUES(6,68,'6:68','اور جب آپ ان کو لوگوں کو دیکھیں جو ہماری آیات میں عیب جوئی کر رہے ہیں تو ان لوگوں سے کناره کش ہوجائیں یہاں تک کہ وه کسی اور بات میں لگ جائیں اور اگر آپ کو شیطان بھلا دے تو یاد آنے کے بعد پھر ایسے ظالم لوگوں کے ساتھ مت بیٹھیں۔ ');
INSERT INTO "junagarri" VALUES(6,69,'6:69','اور جو لوگ پرہیزگار ہیں ان پر ان کی باز پرس کا کوئی اثر نہ پہنچے گا اور لیکن ان کے ذمہ نصیحت کردینا ہے شاید وه تقویٰ اختیار کریں۔ ');
INSERT INTO "junagarri" VALUES(6,70,'6:70','اور ایسے لوگوں سے بالکل کناره کش رہیں جنہوں نے اپنے دین کو کھیل تماشا بنا رکھا ہے اور دنیوی زندگی نے انہیں دھوکہ میں ڈال رکھا ہے اور اس قرآن کے ذریعہ سے نصیحت بھی کرتے رہیں تاکہ کوئی شخص اپنے کردار کے سبب (اس طرح) نہ پھنس جائے کہ کوئی غیر اللہ اس کا نہ مددگار ہو اور نہ سفارشی اور یہ کیفیت ہو کہ اگر دنیا بھر کا معاوضہ بھی دے ڈالے تب بھی اس سے نہ لیا جائے ۔ ایسے ہی ہیں کہ اپنے کردار کے سبب پھنس گئے، ان کے لئے نہایت تیز گرم پانی پینے کے لئے ہوگا اور دردناک سزا ہوگی اپنے کفر کے سبب۔');
INSERT INTO "junagarri" VALUES(6,71,'6:71','آپ کہہ دیجئے کہ کیا ہم اللہ تعالیٰ کے سوا ایسی چیز کو پکاریں کہ نہ وه ہم کو نفع پہنچائے اور نہ ہم کو نقصان پہنچائے اور کیا ہم الٹے پھر جائیں اس کے بعد کہ ہم کو اللہ تعالیٰ نے ہدایت کردی ہے، جیسے کوئی شخص ہو کہ اس کو شیطانوں نے کہیں جنگل میں بےراه کردیا ہو اور وه بھٹکتا پھرتا ہو، اس کے کچھ ساتھی بھی ہوں کہ وه اس کو ٹھیک راستہ کی طرف بلا رہے ہوں کہ ہمارے پاس آ ۔ آپ کہہ دیجئے کہ یقینی بات ہے کہ راه راست وه خاص اللہ ہی کی راه ہے اور ہم کو یہ حکم ہوا ہے کہ ہم پروردگار عالم کے پورے مطیع ہوجائیں۔');
INSERT INTO "junagarri" VALUES(6,72,'6:72','اور یہ کہ نماز کی پابندی کرو اور اس سے ڈرو اور وہی ہے جس کے پاس تم سب جمع کئے جاؤ گے۔');
INSERT INTO "junagarri" VALUES(6,73,'6:73','اور وہی ہے جس نے آسمانوں اور زمین کو برحق پیدا کیا اور جس وقت اللہ تعالیٰ اتنا کہہ دے گا تو ہوجا بس وه ہو پڑے گا۔ اس کا کہنا حق اور بااثر ہے۔ اور ساری حکومت خاص اسی کی ہوگی جب کہ صُور میں پھونک ماری جائے گی وه جاننے والا ہے پوشیده چیزوں کا اور ظاہر چیزوں کا اور وہی ہے بڑی حکمت والا پوری خبر رکھنے والا۔');
INSERT INTO "junagarri" VALUES(6,74,'6:74','اور وه وقت بھی یاد کرنے کے قابل ہے جب ابراہیم (علیہ السلام) نے اپنے باپ آزر سے فرمایا کہ کیا تو بتوں کو معبود قرار دیتا ہے؟ بےشک میں تجھ کو اور تیری ساری قوم کو صریح گمراہی میں دیکھتا ہوں۔');
INSERT INTO "junagarri" VALUES(6,75,'6:75','اور ہم نے ایسے ہی طور پر ابراہیم (علیہ السلام) کو آسمانوں اور زمین کی مخلوقات دکھلائیں اور تاکہ کامل یقین کرنے والوں میں سے ہوجائیں۔ ');
INSERT INTO "junagarri" VALUES(6,76,'6:76','پھر جب رات کی تاریکی ان پر چھا گئی تو انہوں نے ایک ستاره دیکھا آپ نے فرمایا کہ یہ میرا رب ہے مگر جب وه غروب ہوگیا تو آپ نے فرمایا کہ میں غروب ہوجانے والوں سے محبت نہیں رکھتا۔ ');
INSERT INTO "junagarri" VALUES(6,77,'6:77','پھر جب چاند کو دیکھا چمکتا ہوا تو فرمایا کہ یہ میرا رب ہے لیکن جب وه غروب ہوگیا تو آپ نے فرمایا کہ اگر مجھ کو میرے رب نے ہدایت نہ کی تو میں گمراه لوگوں میں شامل ہوجاؤں گا۔');
INSERT INTO "junagarri" VALUES(6,78,'6:78','پھر جب آفتاب کو دیکھا چمکتا ہوا تو فرمایا کہ یہ میرا رب ہے یہ تو سب سے بڑا ہے پھر جب وه بھی غروب ہوگیا تو آپ نے فرمایا بےشک میں تمہارے شرک سے بیزار ہوں۔ ');
INSERT INTO "junagarri" VALUES(6,79,'6:79','میں اپنا رخ اس کی طرف کرتا ہوں جس نے آسمانوں اور زمین کو پیدا کیا یکسو ہو کر، اور میں شرک کرنے والوں میں سے نہیں ہوں۔');
INSERT INTO "junagarri" VALUES(6,80,'6:80','اور ان سے ان کی قوم نے حجت کرنا شروع کیا ، آپ نے فرمایا کیا تم اللہ کے معاملہ میں مجھ سے حجت کرتے ہو حالانکہ اس نے مجھ کو طریقہ بتلا دیا ہے اور میں ان چیزوں سے جن کو تم اللہ کے ساتھ شریک بناتے ہو نہیں ڈرتا ہاں اگر میرا پروردگار ہی کوئی امر چاہے میرا پروردگار ہر چیز کو اپنے علم میں گھیرے ہوئے ہے، کیا تم پھر بھی خیال نہیں کرتے۔');
INSERT INTO "junagarri" VALUES(6,81,'6:81','اور میں ان چیزوں سے کیسے ڈروں جن کو تم نے شریک بنایا ہے حالانکہ تم اس بات سے نہیں ڈرتے کہ تم نے اللہ کے ساتھ ایسی چیزوں کو شریک ٹھہرایا ہے جن پر اللہ تعالیٰ نے کوئی دلیل نازل نہیں فرمائی، سو ان دو جماعتوں میں سے امن کا زیاده مستحق کون ہے اگر تم خبر رکھتے ہو۔');
INSERT INTO "junagarri" VALUES(6,82,'6:82','جو لوگ ایمان رکھتے ہیں اور اپنے ایمان کو شرک کے ساتھ مخلوط نہیں کرتے، ایسوں ہی کے لئے امن ہے اور وہی راه راست پر چل رہے ہیں۔ ');
INSERT INTO "junagarri" VALUES(6,83,'6:83','اور یہ ہماری حجت تھی وه ہم نے ابراہیم (علیہ السلام) کو ان کی قوم کے مقابلے میں دی تھی ہم جس کو چاہتے ہیں مرتبوں میں بڑھا دیتے ہیں۔ بےشک آپ کا رب بڑا حکمت والا بڑا علم والا ہے۔');
INSERT INTO "junagarri" VALUES(6,84,'6:84','اور ہم نے ان کو اسحاق دیا اور یعقوب ہر ایک کو ہم نے ہدایت کی اور پہلے زمانہ میں ہم نے نوح کو ہدایت کی اور ان کی اولاد میں سے داؤد کو اور سلیمان کو اور ایوب کو اور یوسف کو اور موسیٰ کو اور ہارون کو اور اسی طرح ہم نیک کام کرنے والوں کو جزا دیا کرتے ہیں۔');
INSERT INTO "junagarri" VALUES(6,85,'6:85','اور (نیز) زکریا کو اور یحيٰ کو اور عیسیٰ کو اور الیاس کو، سب نیک لوگوں میں سے تھے۔');
INSERT INTO "junagarri" VALUES(6,86,'6:86','اور نیز اسماعیل کو اور یسع کو اور یونس کو اور لوط کو اور ہر ایک کو تمام جہان والوں پر ہم نے فضیلت دی۔');
INSERT INTO "junagarri" VALUES(6,87,'6:87','اور نیز ان کے کچھ باپ دادوں کو اور کچھ اولاد کو اور کچھ بھائیوں کو ، اور ہم نے ان کو مقبول بنایا اور ہم نے ان کو راه راست کی ہدایت کی۔');
INSERT INTO "junagarri" VALUES(6,88,'6:88','اللہ کی ہدایت ہی ہے جس کے ذریعے اپنے بندوں میں سے جس کو چاہے اس کی ہدایت کرتا ہے اور اگر فرضاً یہ حضرات بھی شرک کرتے تو جو کچھ یہ اعمال کرتے تھے وه سب اکارت ہوجاتے۔ ');
INSERT INTO "junagarri" VALUES(6,89,'6:89','یہ لوگ ایسے تھے کہ ہم نے ان کو کتاب اور حکمت اور نبوت عطا کی تھی سو اگر یہ لوگ نبوت کا انکار کریں تو ہم نے اس کے لئے ایسے بہت سے لوگ مقرر کردیئے ہیں جو اس کے منکر نہیں ہیں۔ ');
INSERT INTO "junagarri" VALUES(6,90,'6:90','یہی لوگ ایسے تھے جن کو اللہ تعالیٰ نے ہدایت کی تھی، سو آپ بھی ان ہی کے طریق پر چلیئے آپ کہہ دیجیئے کہ میں تم سے اس پر کوئی معاوضہ نہیں چاہتا یہ تو صرف تمام جہان والوں کے واسطے ایک نصیحت ہے۔ ');
INSERT INTO "junagarri" VALUES(6,91,'6:91','اور ان لوگوں نے، اللہ کی جیسی قدر کرنا واجب تھی ویسی قدر نہ کی جب کہ یوں کہہ دیا کہ اللہ نے کسی بشر پر کوئی چیز نازل نہیں کی آپ یہ کہیئے کہ وه کتاب کس نے نازل کی ہے جس کو موسیٰ لائے تھے جس کی کیفیت یہ ہے کہ وه نور ہے اور لوگوں کے لئے وه ہدایت ہے جس کو تم نے ان متفرق اوراق میں رکھ چھوڑا ہے جن کو ظاہر کرتے ہو اور بہت سی باتوں کو چھپاتے ہو اور تم کو بہت سی ایسی باتیں بتائی گئی ہیں جن کو تم نہ جانتے تھے اور نہ تمہارے بڑے ۔ آپ کہہ دیجیئے کہ اللہ نے نازل فرمایا ہے پھر ان کو ان کے خرافات میں کھیلتے رہنے دیجیئے۔');
INSERT INTO "junagarri" VALUES(6,92,'6:92','اور یہ بھی ایسی ہی کتاب ہے جس کو ہم نے نازل کیا ہے جو بڑی برکت والی ہے، اپنے سے پہلی کتابوں کی تصدیق کرنے والی ہے اور تاکہ آپ مکہ والوں کو اور آس پاس والوں کو ڈرائیں۔ اور جو لوگ آخرت کا یقین رکھتے ہیں ایسے لوگ اس پر ایمان لے آتے ہیں اور وه اپنی نماز پر مداومت رکھتے ہیں۔');
INSERT INTO "junagarri" VALUES(6,93,'6:93','اور اس شخص سے زیاده کون ظالم ہوگا جو اللہ تعالیٰ پر جھوٹ تہمت لگائے یا یوں کہے کہ مجھ پر وحی آتی ہے حالانکہ اس کے پاس کسی بات کی بھی وحی نہیں آئی اور جو شخص یوں کہے کہ جیسا کلام اللہ نے نازل کیا ہے اسی طرح کا میں بھی لاتا ہوں اور اگر آپ اس وقت دیکھیں جب کہ یہ ظالم لوگ موت کی سختیوں میں ہوں گے اور فرشتے اپنے ہاتھ بڑھا رہے ہوں گے کہ ہاں اپنی جانیں نکالو۔ آج تم کو ذلت کی سزا دی جائے گی اس سبب سے کہ تم اللہ تعالیٰ کے ذمہ جھوٹی باتیں لگاتے تھے، اور تم اللہ تعالیٰ کی آیات سے تکبر کرتے تھے۔ ');
INSERT INTO "junagarri" VALUES(6,94,'6:94','اور تم ہمارے پاس تنہا تنہا آگئے جس طرح ہم نے اول بار تم کو پیدا کیا تھا اور جو کچھ ہم نے تم کو دیا تھا اس کو اپنے پیچھے ہی چھوڑ آئے اور ہم تو تمہارے ہمراه تمہارے ان شفاعت کرنے والوں کو نہیں دیکھتے جن کی نسبت تم دعویٰ رکھتے تھے کہ وه تمہارے معاملے میں شریک ہیں۔ واقعی تمہارے آپس میں تو قطع تعلق ہوگیا اور وه تمہارا دعویٰ سب تم سے گیا گزرا ہوا۔');
INSERT INTO "junagarri" VALUES(6,95,'6:95','بےشک اللہ تعالیٰ دانہ کو اور گٹھلیوں کو پھاڑنے والا ہے ، وه جاندار کو بے جان سے نکال لاتا ہے اور وه بے جان کو جاندار سے نکالنے والا ہے اللہ تعالیٰ یہ ہے، سو تم کہاں الٹے چلے جا رہے ہو۔');
INSERT INTO "junagarri" VALUES(6,96,'6:96','وه صبح کا نکالنے والا ہے اور اس نے رات کو راحت کی چیز بنا یا ہے اور سورج اور چاند کو حساب سے رکھا ہے ۔ یہ ٹھہرائی بات ہے ایسی ذات کی جو کہ قادر ہے بڑے علم والا ہے۔');
INSERT INTO "junagarri" VALUES(6,97,'6:97','اور وه ایسا ہے جس نے تمہارے لئے ستاروں کو پیدا کیا، تاکہ تم ان کے ذریعہ سے اندھیروں میں، خشکی میں اور دریا میں بھی راستہ معلوم کرسکو ۔ بےشک ہم نے دلائل خوب کھول کھول کر بیان کردیئے ہیں ان لوگوں کے لئے جو خبر رکھتے ہیں۔');
INSERT INTO "junagarri" VALUES(6,98,'6:98','اور وه ایسا ہے جس نے تم کو ایک شخص سے پیدا کیا پھر ایک جگہ زیاده رہنے کی ہے اور ایک جگہ چندے، رہنے کی بےشک ہم نےدلائل خوب کھول کھول کر بیان کردیئے ان لوگوں کے لئے جو سمجھ بوجھ رکھتے ہیں۔');
INSERT INTO "junagarri" VALUES(6,99,'6:99','اور وه ایسا ہے جس نے آسمان سے پانی برسایا پھر ہم نے اس کے ذریعہ سے ہر قسم کے نبات کو نکالا پھر ہم نے اس سے سبز شاخ نکالی کہ اس سے ہم اوپر تلے دانے چڑھے ہوئے نکالتے ہیں اور کھجور کے درختوں سے یعنی ان کے گپھے میں سے، خوشے ہیں جو نیچے کو لٹکے جاتے ہیں اور انگوروں کے باغ اور زیتون اور انار کہ بعض ایک دوسرے سے ملتے جلتے ہوتے ہیں اور کچھ ایک دوسرے سے ملتے جلتے نہیں ہوتے ۔ ہر ایک کے پھل کو دیکھو جب وه پھلتا ہے اور اس کے پکنے کو دیکھو ان میں دلائل ہیں ان لوگوں کے لئے جو ایمان رکھتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(6,100,'6:100','اور لوگوں نے شیاطین کو اللہ تعالیٰ کا شریک قرار دے رکھا ہے حالانکہ ان لوگوں کو اللہ ہی نے پیدا کیا ہے اور ان لوگوں نے اللہ کے حق میں بیٹے اور بیٹیاں بلا سند تراش رکھی ہیں اور وه پاک اور برتر ہے ان باتوں سے جو یہ کرتے ہیں۔');
INSERT INTO "junagarri" VALUES(6,101,'6:101','وه آسمانوں اور زمین کا موجد ہے، اللہ تعالیٰ کے اولاد کہاں ہوسکتی ہے حالانکہ اس کے کوئی بیوی تو ہے نہیں اور اللہ تعالیٰ نے ہر چیز کو پیدا کیا اور وه ہر چیز کو خوب جانتا ہے۔');
INSERT INTO "junagarri" VALUES(6,102,'6:102','یہ ہے اللہ تعالیٰ تمہارا رب! اس کے سوا کوئی عبادت کے لائق نہیں، ہر چیز کا پیدا کرنے والا ہے، تو تم اس کی عبادت کرو اور وه ہر چیز کا کارساز ہے۔');
INSERT INTO "junagarri" VALUES(6,103,'6:103','اس کو تو کسی کی نگاه محیط نہیں ہوسکتی اور وه سب نگاہوں کو محیط ہو جاتا ہے اور وہی بڑا باریک بین باخبر ہے۔');
INSERT INTO "junagarri" VALUES(6,104,'6:104','اب بلاشبہ تمہارے پاس تمہارے رب کی جانب سے حق بینی کے ذرائع پہنچ چکے ہیں سو جو شخص دیکھ لے گا وه اپنا فائده کرے گا اور جو شخص اندھا رہے گا وه اپنا نقصان کرے گا ، اور میں تمہارا نگران نہیں ہوں۔ ');
INSERT INTO "junagarri" VALUES(6,105,'6:105','اور ہم اس طور پر دلائل کو مختلف پہلوؤں سے بیان کرتے ہیں تاکہ یہ یوں کہیں کہ آپ نے کسی سے پڑھ لیا ہے اور تاکہ ہم اس کو دانشمندوں کے لئے خوب ظاہر کردیں۔');
INSERT INTO "junagarri" VALUES(6,106,'6:106','آپ خود اس طریق پر چلتے رہئے جس کی وحی آپ کے رب تعالیٰ کی طرف سے آپ کے پاس آئی ہے، اللہ تعالیٰ کے سوا کوئی لائق عبادت نہیں اور مشرکین کی طرف خیال نہ کیجئے۔');
INSERT INTO "junagarri" VALUES(6,107,'6:107','اور اگر اللہ تعالیٰ کو منظور ہوتا تو یہ شرک نہ کرتے اور ہم نے آپ کو ان کا نگران نہیں بنایا۔ اور نہ آپ ان پر مختار ہیں! ');
INSERT INTO "junagarri" VALUES(6,108,'6:108','اور گالی مت دو ان کو جن کی یہ لوگ اللہ تعالیٰ کو چھوڑ کر عبادت کرتے ہیں کیونکہ پھر وه براه جہل حد سے گزر کر اللہ تعالیٰ کی شان میں گستاخی کریں گے ہم نے اسی طرح ہر طریقہ والوں کو ان کا عمل مرغوب بنا رکھا ہے۔ پھر اپنے رب ہی کے پاس ان کو جانا ہے سو وه ان کو بتلا دے گا جو کچھ بھی وه کیا کرتے تھے۔');
INSERT INTO "junagarri" VALUES(6,109,'6:109','اور ان لوگوں نے قسموں میں بڑا زور لگا کر اللہ تعالیٰ کی قسم کھائی کہ اگر ان کے پاس کوئی نشانی آ جائے تو وه ضرور ہی اس پر ایمان لے آئیں گے، آپ کہہ دیجئے کہ نشانیاں سب اللہ کے قبضے میں ہیں اور تم کو اس کی کیا خبر کہ وه نشانیاں جس وقت آجائیں گی یہ لوگ تب بھی ایمان نہ لائیں گے۔');
INSERT INTO "junagarri" VALUES(6,110,'6:110','اور ہم بھی ان کے دلوں کو اور ان کی نگاہوں کو پھیر دیں گے جیسا کہ یہ لوگ اس پر پہلی دفعہ ایمان نہیں لائے اور ہم ان کو ان کی سرکشی میں حیران رہنے دیں گے۔');
INSERT INTO "junagarri" VALUES(6,111,'6:111','اور اگر ہم ان کے پاس فرشتوں کو بھیج دیتے اور ان سے مردے باتیں کرنے لگتے اور ہم تمام موجودات کو ان کے پاس ان کی آنکھوں کے روبرو لا کر جمع کر دیتے ہیں تب بھی یہ لوگ ہرگز ایمان نہ لاتے ہاں اگر اللہ ہی چاہے تو اور بات ہے لیکن ان میں زیاده لوگ جہالت کی باتیں کرتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(6,112,'6:112','اور اسی طرح ہم نے ہر نبی کے دشمن بہت سے شیطان پیدا کئے تھے کچھ آدمی اور کچھ جن ، جن میں سے بعض بعضوں کو چکنی چپڑی باتوں کا وسوسہ ڈالتے رہتے تھے تاکہ ان کو دھوکہ میں ڈال دیں اور اگر اللہ تعالیٰ چاہتا تو یہ ایسے کام نہ کرسکتے سو ان لوگوں کو اور جو کچھ یہ افترا پردازی کر رہے ہیں اس کو آپ رہنے دیجئے۔');
INSERT INTO "junagarri" VALUES(6,113,'6:113','اور تاکہ اس کی طرف ان لوگوں کے قلوب مائل ہوجائیں جو آخرت پر یقین نہیں رکھتے اور تاکہ اس کو پسند کرلیں اور تاکہ مرتکب ہوجائیں ان امور کے جن کے وه مرتکب ہوتے تھے۔ ');
INSERT INTO "junagarri" VALUES(6,114,'6:114','تو کیا اللہ کے سوا کسی اور فیصلہ کرنے والے کو تلاش کروں حالانکہ وه ایسا ہے کہ اس نے ایک کتاب کامل تمہارے پاس بھیج دی ہے، اس کے مضامین خوب صاف صاف بیان کئے گئے ہیں اور جن لوگوں کو ہم نے کتاب دی ہے وه اس بات کو یقین کے ساتھ جانتے ہیں کہ یہ آپ کے رب کی طرف سے حق کے ساتھ بھیجی گئی ہے، سو آپ شبہ کرنے والوں میں سے نہ ہوں۔ ');
INSERT INTO "junagarri" VALUES(6,115,'6:115','آپ کے رب کا کلام سچائی اور انصاف کے اعتبار سے کامل ہے ، اس کے کلام کا کوئی بدلنے والا نہیں اور وه خوب سننے والا خوب جاننے والا ہے۔ ');
INSERT INTO "junagarri" VALUES(6,116,'6:116','اور دنیا میں زیاده لوگ ایسے ہیں کہ اگر آپ ان کا کہنا ماننے لگیں تو وه آپ کو اللہ کی راه سے بے راه کردیں وه محض بے اصل خیالات پر چلتے ہیں اور بالکل قیاسی باتیں کرتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(6,117,'6:117','بالیقین آپ کا رب ان کو خوب جانتا ہے جو اس کی راه سے بے راه ہوجاتا ہے۔ اور وه ان کو بھی خوب جانتا ہے جو اس کی راه پر چلتے ہیں۔');
INSERT INTO "junagarri" VALUES(6,118,'6:118','سو جس جانور پر اللہ تعالیٰ کا نام لیا جائے اس میں سے کھاؤ! اگر تم اس کے احکام پر ایمان رکھتے ہو۔ ');
INSERT INTO "junagarri" VALUES(6,119,'6:119','اور آخر کیا وجہ ہے کہ تم ایسے جانور میں سے نہ کھاؤ جس پر اللہ کا نام لیا گیا ہو حالانکہ اللہ تعالیٰ نے ان سب جانوروں کی تفصیل بتادی ہے جن کو تم پر حرام کیا ہے ، مگر وه بھی جب تم کو سخت ضرورت پڑجائے تو حلال ہے اور یہ یقینی بات ہے کہ بہت سے آدمی اپنے خیالات پر بلا کسی سند کے گمراه کرتے ہیں۔ اس میں کوئی شبہ نہیں کہ اللہ تعالیٰ حد سے نکل جانے والوں کو خوب جانتا ہے۔');
INSERT INTO "junagarri" VALUES(6,120,'6:120','اور تم ظاہری گناه کو بھی چھوڑ دو اور باطنی گناه کو بھی چھوڑ دو۔ بلاشبہ جو لوگ گناه کررہے ہیں ان کو ان کے کئے کی عنقریب سزا ملے گی۔');
INSERT INTO "junagarri" VALUES(6,121,'6:121','اور ایسے جانوروں میں سے مت کھاؤ جن پر اللہ کا نام نہ لیا گیا ہو اور یہ کام نافرمانی کا ہے اور یقیناً شیاطین اپنے دوستوں کے دل میں ڈالتے ہیں تاکہ یہ تم سے جدال کریں اور اگر تم ان لوگوں کی اطاعت کرنے لگو تو یقیناً تم مشرک ہوجاؤ گے۔');
INSERT INTO "junagarri" VALUES(6,122,'6:122','ایسا شخص جو پہلے مرده تھا پھر ہم نے اس کو زنده کردیا اور ہم نے اس کو ایک ایسا نور دے دیا کہ وه اس کو لئے ہوئے آدمیوں میں چلتا پھرتا ہے۔ کیا ایسا شخص اس شخص کی طرح ہوسکتا ہے؟ جو تاریکیوں سے نکل ہی نہیں پاتا ۔ اسی طرح کافروں کو ان کے اعمال خوش نما معلوم ہوا کرتے ہیں۔');
INSERT INTO "junagarri" VALUES(6,123,'6:123','اور اسی طرح ہم نے ہر بستی میں وہاں کے رئیسوں ہی کو جرائم کا مرتکب بنایا تاکہ وه لوگ وہاں فریب کریں ۔ اور وه لوگ اپنے ہی ساتھ فریب کررہے ہیں اور ان کو ذرا خبر نہیں۔ ');
INSERT INTO "junagarri" VALUES(6,124,'6:124','اور جب ان کو کوئی آیت پہنچتی ہے تو یوں کہتے ہیں کہ ہم ہرگز ایمان نہ لائیں گے جب تک کہ ہم کو بھی ایسی ہی چیز نہ دی جائے جو اللہ کے رسولوں کو دی جاتی ہے ، اس موقع کو تو اللہ ہی خوب جانتا ہے کہ کہاں وه اپنی پیغمبری رکھے؟ عنقریب ان لوگوں کو جنہوں نے جرم کیا ہے اللہ کے پاس پہنچ کر ذلت پہنچے گی اور ان کی شرارتوں کے مقابلے میں سخت سزائیں۔');
INSERT INTO "junagarri" VALUES(6,125,'6:125','سو جس شخص کو اللہ تعالیٰ راستہ پر ڈالنا چاہے اس کے سینہ کو اسلام کے لیے کشاده کر دیتا ہے اور جس کو بے راه رکھنا چاہے اس کے سینہ کو بہت تنگ کردیتا ہے جیسے کوئی آسمان میں چڑھتا ہے ، اسی طرح اللہ تعالیٰ ایمان نہ لانے والوں پر ناپاکی مسلط کردیتا ہے۔ ');
INSERT INTO "junagarri" VALUES(6,126,'6:126','اور یہی تیرے رب کا سیدھا راستہ ہے۔ ہم نے نصیحت حاصل کرنے والوں کے واسطے ان آیتوں کو صاف صاف بیان کردیا۔');
INSERT INTO "junagarri" VALUES(6,127,'6:127','ان لوگوں کے واسطے ان کے رب کے پاس سلامتی کا گھر ہے اور اللہ تعالیٰ ان سے محبت رکھتا ہے ان کے اعمال کی وجہ سے۔ ');
INSERT INTO "junagarri" VALUES(6,128,'6:128','اور جس روز اللہ تعالیٰ تمام خلائق کو جمع کرے گا، (کہے گا) اے جماعت جنات کی! تم نے انسانوں میں سے بہت سے اپنا لیے جو انسان ان کے ساتھ تعلق رکھنے والے تھے وه کہیں گے کہ اے ہمارے پروردگار! ہم میں ایک نے دوسرے سے فائده حاصل کیا تھا اور ہم اپنی اس معین میعاد تک آپہنچے جو تونے ہمارے لئے معین فرمائی ، اللہ فرمائے گا کہ تم سب کا ٹھکانہ دوزخ ہے جس میں ہمیشہ رہو گے، ہاں اگر اللہ ہی کو منظور ہو تو دوسری بات ہے ۔ بے شک آپ کا رب بڑی حکمت والا بڑا علم والا ہے۔');
INSERT INTO "junagarri" VALUES(6,129,'6:129','اور اسی طرح ہم بعض کفار کو بعض کے قریب رکھیں گے ان کے اعمال کے سبب۔ ');
INSERT INTO "junagarri" VALUES(6,130,'6:130','اے جنات اور انسانوں کی جماعت! کیا تمہارے پاس تم میں سے ہی پیغمبر نہیں آئے تھے ، جو تم سے میرے احکام بیان کرتے اور تم کو اس آج کے دن کی خبر دیتے؟ وه سب عرض کریں گے کہ ہم اپنے اوپر اقرار کرتے ہیں اور ان کو دنیاوی زندگی نے بھول میں ڈالے رکھا اور یہ لوگ اقرار کرنے والے ہوں گے کہ وه کافر تھے۔ ');
INSERT INTO "junagarri" VALUES(6,131,'6:131','یہ اس وجہ سے ہے کہ آپ کا رب کسی بستی والوں کو کفر کے سبب ایسی حالت میں ہلاک نہیں کرتا کہ اس بستی کے رہنے والے بے خبر ہوں۔');
INSERT INTO "junagarri" VALUES(6,132,'6:132','اور ہر ایک کے لئے ان کے اعمال کے سبب درجے ملیں گے اور آپ کا رب ان کے اعمال سے بے خبر نہیں ہے۔');
INSERT INTO "junagarri" VALUES(6,133,'6:133','اور آپ کا رب بالکل غنی ہے رحمت والا ہے ۔ اگر وه چاہے تو تم سب کو اٹھا لے اور تمہارے بعد جس کو چاہے تمہاری جگہ آباد کردے جیسا کہ تم کو ایک دوسری قوم کی نسل سے پیدا کیا ہے۔ ');
INSERT INTO "junagarri" VALUES(6,134,'6:134','جس چیز کا تم سے وعده کیا جاتا ہے وه بے شک آنے والی چیز ہے اور تم عاجز نہیں کرسکتے۔ ');
INSERT INTO "junagarri" VALUES(6,135,'6:135','آپ یہ فرما دیجئے کہ اے میری قوم! تم اپنی حالت پر عمل کرتے رہو میں بھی عمل کررہا ہوں ، سو اب جلد ہی تم کو معلوم ہوا جاتا ہے کہ اس عالم کا انجام کار کس کے لیے نافع ہوگا۔ یہ یقینی بات ہے کہ حق تلفی کرنے والوں کو کبھی فلاح نہ ہوگی۔ ');
INSERT INTO "junagarri" VALUES(6,136,'6:136','اور اللہ تعالیٰ نے جو کھیتی اور مواشی پیدا کیے ہیں ان لوگوں نے ان میں سے کچھ حصہ اللہ کا مقرر کیا اور بزعم خود کہتے ہیں کہ یہ تو اللہ کا ہے اور یہ ہمارے معبودوں کا ہے ، پھر جو چیز ان کے معبودوں کی ہوتی ہے وه تو اللہ کی طرف نہیں پہنچتی اور جو چیز اللہ کی ہوتی ہے وه ان کے معبودوں کی طرف پہنچ جاتی ہے کیا برا فیصلہ وه کرتے ہیں۔');
INSERT INTO "junagarri" VALUES(6,137,'6:137','اور اسی طرح بہت سے مشرکین کے خیال میں ان کے معبودوں نے ان کی اولاد کے قتل کرنے کو مستحسن بنا رکھا ہے تاکہ وه ان کو برباد کریں اور تاکہ ان کے دین کو ان پر مشتبہ کردیں اور اگر اللہ کو منظور ہوتا تو یہ ایسا کام نہ کرتے تو آپ ان کو اور جو کچھ یہ غلط باتیں بنا رہے ہیں یونہی رہنے دیجئے۔');
INSERT INTO "junagarri" VALUES(6,138,'6:138','اور وه اپنے خیال پر یہ بھی کہتے ہیں کہ یہ کچھ مواشی ہیں اور کھیت ہیں جن کا استعمال ہر شخص کو جائز نہیں ان کو کوئی نہیں کھا سکتا سوائے ان کے جن کو ہم چاہیں اور مواشی ہیں جن پر سواری یا باربرداری حرام کردی گئی اور کچھ مواشی ہیں جن پر یہ لوگ اللہ تعالیٰ کا نام نہیں لیتے محض اللہ پر افترا باندھنے کے طور پر ۔عنقریب اللہ تعالیٰ انہیں ان کے افترا کی سزا دے گا۔');
INSERT INTO "junagarri" VALUES(6,139,'6:139','اور وه کہتے ہیں کہ جو چیز ان مواشی کے پیٹ میں ہے وه خالص ہمارے مردوں کے لئے ہے اور ہماری عورتوں پر حرام ہے۔ اور اگر وه مرده ہے تو اس میں سب برابر ہیں ۔ ابھی اللہ ان کو ان کی غلط بیانی کی سزا دیئے دیتا ہے بلاشبہ وه حکمت والا ہے اور وه بڑا علم والا ہے۔');
INSERT INTO "junagarri" VALUES(6,140,'6:140','واقعی خرابی میں پڑ گئے وه لوگ جنہوں نے اپنی اولاد کو محض براه حماقت بلا کسی سند کے قتل کر ڈالا اور جو چیزیں ان کو اللہ نے کھانے پینے کو دی تھیں ان کو حرام کرلیا محض اللہ پر افترا باندھنے کے طور پر۔ بے شک یہ لوگ گمراہی میں پڑگئے اور کبھی راه راست پر چلنے والے نہیں ہوئے۔');
INSERT INTO "junagarri" VALUES(6,141,'6:141','اور وہی ہے جس نے باغات پیدا کئے وه بھی جو ٹٹیوں پر چڑھائے جاتے ہیں اور وه بھی جو ٹٹیوں پر نہیں چڑھائے جاتے اور کھجور کے درخت اور کھیتی جن میں کھانے کی چیزیں مختلف طور کی ہوتی ہیں اور زیتون اور انار جو باہم ایک دوسرے کے مشابہ بھی ہوتے ہیں اور ایک دوسرے کے مشابہ نہیں بھی ہوتے ، ان سب کے پھلوں میں سے کھاؤ جب وه نکل آئے اور اس میں جو حق واجب ہے وه اس کے کاٹنے کے دن دیا کرو اور حد سے مت گزرو یقیناً وه حد سے گزرنے والوں کو ناپسند کرتا ہے۔ ');
INSERT INTO "junagarri" VALUES(6,142,'6:142','اور مواشی میں اونچے قد کے اور چھوٹے قد کے (پیدا کیے)، جو کچھ اللہ نے تم کو دیا ہے کھاؤ اور شیطان کے قدم بقدم مت چلو ، بلاشک وه تمہارا صریح دشمن ہے۔');
INSERT INTO "junagarri" VALUES(6,143,'6:143','(پیدا کیے) آٹھ نر و ماده یعنی بھیڑ میں دو قسم اور بکری میں دو قسم آپ کہیے کہ کیا اللہ نے ان دونوں نروں کو حرام کیا ہے یا دونوں ماده کو؟ یا اس کو جس کو دونوں ماده پیٹ میں لئے ہوئے ہوں؟ تم مجھ کو کسی دلیل سے تو بتاؤ اگر سچے ہو۔ ');
INSERT INTO "junagarri" VALUES(6,144,'6:144','اور اونٹ میں دو قسم اور گائے میں دو قسم آپ کہیے کہ کیا اللہ تعالیٰ نے ان دونوں نروں کو حرام کیا ہے یا دونوں ماده کو؟ یا اس کو جس کو دونوں ماده پیٹ میں لئے ہوئے ہوں؟ کیا تم حاضر تھے جس وقت اللہ تعالیٰ نے تم کو اس کا حکم دیا؟ تو اس سے زیاده کون ظالم ہوگا جو اللہ تعالیٰ پر بلا دلیل جھوٹی تہمت لگائے ، تاکہ لوگوں کو گمراه کرے یقیناً اللہ تعالیٰ ظالم لوگوں کو راستہ نہیں دکھلاتا۔');
INSERT INTO "junagarri" VALUES(6,145,'6:145','آپ کہہ دیجئے کہ جو کچھ احکام بذریعہ وحی میرے پاس آئے ان میں تو میں کوئی حرام نہیں پاتا کسی کھانے والے کے لئے جو اس کو کھائے، مگر یہ کہ وه مردار ہو یا کہ بہتا ہوا خون ہو یا خنزیر کا گوشت ہو، کیونکہ وه بالکل ناپاک ہے یا جو شرک کا ذریعہ ہو کہ غیراللہ کے لئے نامزد کردیا گیا ہو ۔ پھر جو شخص مجبور ہوجائے بشرطیکہ نہ تو طالب لذت ہو اور نہ تجاوز کرنے والا ہو تو واقعی آپ کا رب غفور و رحیم ہے۔');
INSERT INTO "junagarri" VALUES(6,146,'6:146','اور یہود پر ہم نے تمام ناخن والے جانور حرام کرد یئے تھے اور گائے اور بکری میں سے ان دونوں کی چربیاں ان پر ہم نے حرام کردی تھیں مگر وه جو ان کی پشت پر یا انتڑیوں میں لگی ہو یا جو ہڈی سے ملی ہو ۔ ان کی شرارت کے سبب ہم نے ان کو یہ سزا دی اور ہم یقیناً سچے ہیں۔ ');
INSERT INTO "junagarri" VALUES(6,147,'6:147','پھر اگر یہ آپ کو کاذب کہیں تو آپ فرما دیجئے کہ تمہارا رب بڑی وسیع رحمت والا ہے اور اس کا عذاب مجرم لوگوں سے نہ ٹلے گا۔ ');
INSERT INTO "junagarri" VALUES(6,148,'6:148','یہ مشرکین (یوں) کہیں گے کہ اگر اللہ تعالیٰ کو منظور ہوتا تو نہ ہم شرک کرتے اور نہ ہمارے باپ دادا اور نہ ہم کسی چیز کو حرام کہہ سکتے ۔ اسی طرح جو لوگ ان سے پہلے ہوچکے ہیں انہوں نے بھی تکذیب کی تھی یہاں تک کہ انہوں نے ہمارے عذاب کامزه چکھا ۔ آپ کہیے کہ کیا تمہارے پاس کوئی دلیل ہے تو اس کو ہمارے روبرو ظاہر کرو ۔ تم لوگ محض خیالی باتوں پر چلتے ہو اور تم بالکل اٹکل سے باتیں بناتے ہو۔');
INSERT INTO "junagarri" VALUES(6,149,'6:149','آپ کہیے کہ بس پوری حجت اللہ ہی کی رہی۔ پھر اگر وه چاہتا تو تم سب کو راه راست پر لے آتا۔');
INSERT INTO "junagarri" VALUES(6,150,'6:150','آپ کہیے کہ اپنے گواہوں کو لاؤ جو اس بات پر شہادت دیں کہ اللہ نے ان چیزوں کو حرام کردیا ہے ، پھر اگر وه گواہی دے دیں تو آپ اس کی شہادت نہ دیجئے اور ایسے لوگوں کے باطل خیالات کا اتباع مت کیجئے! جو ہماری آیتوں کی تکذیب کرتے ہیں اور وه جو آخرت پر ایمان نہیں رکھتے اور وه اپنے رب کے برابر دوسروں کو ٹھہراتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(6,151,'6:151','آپ کہیے کہ آؤ میں تم کو وه چیزیں پڑھ کر سناؤں جن (یعنی جن کی مخالفت) کو تمہارے رب نے تم پر حرام فرما دیا ہے ، وه یہ کہ اللہ کے ساتھ کسی چیز کو شریک مت ٹھہراؤ اور ماں باپ کے ساتھ احسان کرو اور اپنی اولاد کو افلاس کے سبب قتل مت کرو۔ ہم تم کو اور ان کو رزق دیتے ہیں اور بے حیائی کے جتنے طریقے ہیں ان کے پاس بھی مت جاؤ خواه علانیہ ہوں خواه پوشیده، اور جس کا خون کرنا اللہ تعالیٰ نے حرام کردیا ہے اس کو قتل مت کرو، ہاں مگر حق کے ساتھ ان کا تم کو تاکیدی حکم دیا ہے تاکہ تم سمجھو۔');
INSERT INTO "junagarri" VALUES(6,152,'6:152','اور یتیم کے مال کے پاس نہ جاؤ مگر ایسے طریقے سے جو کہ مستحسن ہے یہاں تک کہ وه اپنے سن رشد کو پہنچ جائے اور ناپ تول پوری پوری کرو، انصاف کے ساتھ ، ہم کسی شخص کو اس کی طاقت سے زیاده تکلیف نہیں دیتے ۔ اور جب تم بات کرو تو انصاف کرو، گو وه شخص قرابت دار ہی ہو اور اللہ تعالیٰ سے جو عہد کیا اس کو پورا کرو، ان کا اللہ تعالیٰ نے تم کو تاکیدی حکم دیا ہے تاکہ تم یاد رکھو۔');
INSERT INTO "junagarri" VALUES(6,153,'6:153','اور یہ کہ یہ دین میرا راستہ ہے جو مستقیم ہے سو اس راه پر چلو اور دوسری راہوں پر مت چلو کہ وه راہیں تم کو اللہ کی راه سے جدا کردیں گی۔ اس کا تم کو اللہ تعالیٰ نے تاکیدی حکم دیا ہے تاکہ تم پرہیزگاری اختیار کرو۔');
INSERT INTO "junagarri" VALUES(6,154,'6:154','پھر ہم نے موسیٰ (علیہ السلام) کو کتاب دی تھی جس سے اچھی طرح عمل کرنے والوں پر نعمت پوری ہو اور سب احکام کی تفصیل ہوجائے اور رہنمائی ہو اور رحمت ہو تاکہ وه لوگ اپنے رب کے ملنے پر یقین لائیں۔');
INSERT INTO "junagarri" VALUES(6,155,'6:155','اور یہ ایک کتاب ہے جس کو ہم نے بھیجا بڑی خیر وبرکت والی ، سو اس کا اتباع کرو اور ڈرو تاکہ تم پر رحمت ہو۔');
INSERT INTO "junagarri" VALUES(6,156,'6:156','کہیں تم لوگ یوں نہ کہو کہ کتاب تو صرف ہم سے پہلے جو دو فرقے تھے ان پر نازل ہوئی تھی، اور ہم ان کے پڑھنے پڑھانے سے محض بے خبر تھے۔ ');
INSERT INTO "junagarri" VALUES(6,157,'6:157','یا یوں نہ کہو کہ اگر ہم پر کوئی کتاب نازل ہوتی تو ہم ان سے بھی زیاده راه راست پر ہوتے۔ سو اب تمہارے پاس تمہارے رب کے پاس سے ایک کتاب واضح اور رہنمائی کا ذریعہ اور رحمت آچکی ہے ۔ اب اس شخص سے زیاده ظالم کون ہوگا جو ہماری ان آیتوں کو جھوٹا بتائے اور اس سے روکے ۔ ہم جلد ہی ان لوگوں کو جو کہ ہماری آیتوں سے روکتے ہیں ان کے اس روکنے کے سبب سخت سزا دیں گے۔');
INSERT INTO "junagarri" VALUES(6,158,'6:158','کیا یہ لوگ صرف اس امر کے منتظر ہیں کہ ان کے پاس فرشتے آئیں یا ان کے پاس آپ کا رب آئے یا آپ کے رب کی کوئی (بڑی) نشانی آئے؟ جس روز آپ کے رب کی کوئی بڑی نشانی آپہنچے گی، کسی ایسے شخص کا ایمان اس کے کام نہ آئے گا جو پہلے سے ایمان نہیں رکھتا ۔ یا اس نے اپنے ایمان میں کوئی نیک عمل نہ کیا ہو ۔ آپ فرما دیجئے کہ تم منتظر رہو، ہم بھی منتظر ہیں۔ ');
INSERT INTO "junagarri" VALUES(6,159,'6:159','بےشک جن لوگوں نے اپنے دین کو جدا جدا کردیا اور گروه گروه بن گئے ، آپ کا ان سے کوئی تعلق نہیں بس ان کا معاملہ اللہ تعالیٰ کے حوالے ہے۔ پھر ان کو ان کا کیا ہوا جتلادیں گے۔');
INSERT INTO "junagarri" VALUES(6,160,'6:160','جو شخص نیک کام کرے گا اس کو اس کے دس گنا ملیں گے اور جو شخص برا کام کرے گا اس کو اس کے برابر ہی سزا ملے گی اور ان لوگوں پر ظلم نہ ہوگا۔');
INSERT INTO "junagarri" VALUES(6,161,'6:161','آپ کہہ دیجئے کہ مجھ کو میرے رب نے ایک سیدھا راستہ بتادیا ہے کہ وه ایک دین مستحکم ہے جو طریقہ ہے ابراہیم (علیہ السلام) کا جو اللہ کی طرف یکسو تھے۔ اور وه شرک کرنے والوں میں سے نہ تھے۔');
INSERT INTO "junagarri" VALUES(6,162,'6:162','آپ فرما دیجئے کہ بالیقین میری نماز اور میری ساری عبادت اور میرا جینا اور میرا مرنا یہ سب خالص اللہ ہی کا ہے جو سارے جہان کا مالک ہے۔');
INSERT INTO "junagarri" VALUES(6,163,'6:163','اس کا کوئی شریک نہیں اور مجھ کو اسی کا حکم ہوا ہے اور میں سب ماننے والوں میں سے پہلا ہوں۔ ');
INSERT INTO "junagarri" VALUES(6,164,'6:164','آپ فرما دیجئے کہ کیا میں اللہ کے سوا کسی اور کو رب بنانے کے لئے تلاش کروں حالانکہ وه مالک ہے ہر چیز کا اور جو شخص بھی کوئی عمل کرتا ہے وه اسی پر رہتا ہے اور کوئی کسی دوسرے کا بوجھ نہ اٹھائے گا ۔ پھر تم سب کو اپنے رب کے پاس جانا ہوگا۔ پھر وه تم کو جتلائے گا جس جس چیز میں تم اختلاف کرتے تھے۔ ');
INSERT INTO "junagarri" VALUES(6,165,'6:165','اور وه ایسا ہے جس نے تم کو زمین میں خلیفہ بنایا اور ایک کا دوسرے پر رتبہ بڑھایا تاکہ تم کو آزمائے ان چیزوں میں جو تم کو دی ہیں ۔ بالیقین آپ کا رب جلد سزا دینے والا ہے اور بالیقین وه واقعی بڑی مغفرت کرنے والا مہربانی کرنے والا ہے۔');
INSERT INTO "junagarri" VALUES(7,1,'7:1','المص');
INSERT INTO "junagarri" VALUES(7,2,'7:2','یہ ایک کتاب ہے جو آپ کے پاس اس لئے بھیجی گئی ہے کہ آپ اس کے ذریعہ سے ڈرائیں، سو آپ کے دل میں اس سے بالکل تنگی نہ ہو اور نصیحت ہے ایمان والوں کے لئے۔');
INSERT INTO "junagarri" VALUES(7,3,'7:3','تم لوگ اس کا اتباع کرو جو تمہارے رب کی طرف سے آئی ہے اور اللہ تعالیٰ کو چھوڑ کر من گھڑت سرپرستوں کی اتباع مت کرو تم لوگ بہت ہی کم نصیحت پکڑتے ہو۔');
INSERT INTO "junagarri" VALUES(7,4,'7:4','اور بہت بستیوں کو ہم نے تباه کردیا اور ان پر ہمارا عذاب رات کے وقت پہنچا یا ایسی حالت میں کہ وه دوپہر کے وقت آرام میں تھے۔ ');
INSERT INTO "junagarri" VALUES(7,5,'7:5','سو جس وقت ان پر ہمارا عذاب آیا اس وقت ان کے منھ سے بجز اس کے اور کوئی بات نہ نکلی کہ واقعی ہم ظالم تھے۔ ');
INSERT INTO "junagarri" VALUES(7,6,'7:6','پھر ہم ان لوگوں سے ضرور پوچھیں گے جن کے پاس پیغمبر بھیجے گئے تھے اور ہم پیغمبروں سے ضرور پوچھیں گے۔ ');
INSERT INTO "junagarri" VALUES(7,7,'7:7','پھر ہم چونکہ پوری خبر رکھتے ہیں ان کے روبرو بیان کردیں گے ۔ اور ہم کچھ بے خبر نہ تھے۔');
INSERT INTO "junagarri" VALUES(7,8,'7:8','اور اس روز وزن بھی برحق ہے پھر جس شخص کا پلا بھاری ہوگا سو ایسے لوگ کامیاب ہوں گے۔');
INSERT INTO "junagarri" VALUES(7,9,'7:9','اور جس شخص کا پلا ہلکا ہوگا سو یہ وه لوگ ہوں گے جنہوں نے اپنا نقصان کرلیا بسبب اس کے کہ ہماری آیتوں کے ساتھ ظلم کرتے تھے۔ ');
INSERT INTO "junagarri" VALUES(7,10,'7:10','اور بے شک ہم نے تم کو زمین پر رہنے کی جگہ دی اور ہم نے تمہارے لئے اس میں سامان رزق پیدا کیا، تم لوگ بہت ہی کم شکر کرتے ہو۔');
INSERT INTO "junagarri" VALUES(7,11,'7:11','اور ہم نے تم کو پیدا کیا ، پھر ہم ہی نے تمہاری صورت بنائی پھر ہم نے فرشتوں سے کہا کہ آدم کو سجده کرو سو سب نے سجده کیا بجز ابلیس کے، وه سجده کرنے والوں میں شامل نہ ہوا۔');
INSERT INTO "junagarri" VALUES(7,12,'7:12','حق تعالیٰ نے فرمایا تو جو سجده نہیں کرتا تو تجھ کو اس سے کون امر مانع ہے ، جبکہ میں تجھ کو حکم دے چکا، کہنے لگا میں اس سے بہتر ہوں، آپ نے مجھ کو آگ سے پیدا کیا ہے اور اس کو آپ نے خاک سے پیدا کیا ہے۔ ');
INSERT INTO "junagarri" VALUES(7,13,'7:13','حق تعالیٰ نے فرمایا آسمان سے اتر تجھ کو کوئی حق حاصل نہیں کہ تو آسمان میں ره کر تکبر کرے سو نکل بے شک تو ذلیلوں میں سے ہے۔ ');
INSERT INTO "junagarri" VALUES(7,14,'7:14','اس نے کہا کہ مجھ کو مہلت دیجئے قیامت کے دن تک۔');
INSERT INTO "junagarri" VALUES(7,15,'7:15','اللہ تعالیٰ نے فرمایا تجھ کو مہلت دی گئی۔ ');
INSERT INTO "junagarri" VALUES(7,16,'7:16','اس نے کہا بسبب اس کے کہ آپ نے مجھ کو گمراه کیا ہے میں قسم کھاتا ہوں کہ میں ان کے لئے آپ کی سیدھی راه پر بیٹھوں گا۔');
INSERT INTO "junagarri" VALUES(7,17,'7:17','پھر ان پر حملہ کروں گا ان کے آگے سے بھی اور ان کے پیچھے سے بھی اور ان کی داہنی جانب سے بھی اور ان کی بائیں جانب سے بھی اور آپ ان میں سے اکثر کو شکر گزار نہ پائیے گا۔ ');
INSERT INTO "junagarri" VALUES(7,18,'7:18','اللہ تعالیٰ نے فرمایا کہ یہاں سے ذلیل وخوار ہوکر نکل جا جو شخص ان میں سے تیرا کہنا مانے گا میں ضرور تم سب سے جہنم کو بھردوں گا۔');
INSERT INTO "junagarri" VALUES(7,19,'7:19','اور ہم نے حکم دیا کہ اے آدم! تم اور تمہاری بیوی جنت میں رہو۔ پھر جس جگہ سے چاہو دونوں کھاؤ، اور اس درخت کے پاس مت جاؤ ورنہ تم دونوں ظالموں میں سے ہوجاؤ گے۔');
INSERT INTO "junagarri" VALUES(7,20,'7:20','پھر شیطان نے ان دونوں کے دلوں میں وسوسہ ڈالا تاکہ ان کی شرمگاہیں جو ایک دوسرے سے پوشیده تھیں دونوں کے روبرو بے پرده کردے اور کہنے لگا کہ تمہارے رب نے تم دونوں کو اس درخت سے اور کسی سبب سے منع نہیں فرمایا، مگر محض اس وجہ سے کہ تم دونوں کہیں فرشتے ہوجاؤ یا کہیں ہمیشہ زنده رہنے والوں میں سے ہوجاؤ۔');
INSERT INTO "junagarri" VALUES(7,21,'7:21','اور ان دونوں کے روبرو قسم کھالی کہ یقین جانیئے میں تم دونوں کا خیر خواه ہوں> ');
INSERT INTO "junagarri" VALUES(7,22,'7:22','سو ان دونوں کو فریب سے نیچے لے آیا پس ان دونوں نے جب درخت کو چکھا دونوں کی شرمگاہیں ایک دوسرے کے روبرو بے پرده ہوگئیں اور دونوں اپنے اوپر جنت کے پتے جوڑ جوڑ کر رکھنے لگے اور ان کے رب نے ان کو پکارا کیا میں تم دونوں کو اس درخت سے منع نہ کرچکا تھا اور یہ نہ کہہ چکا کہ شیطان تمہارا صریح دشمن ہے؟ ');
INSERT INTO "junagarri" VALUES(7,23,'7:23','دونوں نے کہا اے ہمارے رب! ہم نے اپنا بڑا نقصان کیا اور اگر تو ہماری مغفرت نہ کرے گا اور ہم پر رحم نہ کرے گا تو واقعی ہم نقصان پانے والوں میں سے ہوجائیں گے۔ ');
INSERT INTO "junagarri" VALUES(7,24,'7:24','حق تعالیٰ نے فرمایا کہ نیچے ایسی حالت میں جاؤ کہ تم باہم ایک دوسرے کے دشمن ہوگے اور تمہارے واسطے زمین میں رہنے کی جگہ ہے اور نفع حاصل کرنا ہے ایک وقت تک۔');
INSERT INTO "junagarri" VALUES(7,25,'7:25','فرمایا تم کو وہاں ہی زندگی بسر کرنا ہے اور وہاں ہی مرنا ہے اور اسی میں سے پھر نکالے جاؤ گے۔');
INSERT INTO "junagarri" VALUES(7,26,'7:26','اے آدم (علیہ السلام) کی اولاد ہم نے تمہارے لئے لباس پیدا کیا جو تمہاری شرم گاہوں کو بھی چھپاتا ہے اور موجب زینت بھی ہے اور تقوے کا لباس ، یہ اس سے بڑھ کر ہے ۔ یہ اللہ تعالیٰ کی نشانیوں میں سے ہے تاکہ یہ لوگ یاد رکھیں۔');
INSERT INTO "junagarri" VALUES(7,27,'7:27','اے اولاد آدم! شیطان تم کو کسی خرابی میں نہ ڈال دے جیسا اس نے تمہارے ماں باپ کو جنت سے باہر کرا دیا ایسی حالت میں ان کا لباس بھی اتروا دیا تاکہ وه ان کو ان کی شرم گاہیں دکھائے۔ وه اور اس کا لشکر تم کو ایسے طور پر دیکھتا ہے کہ تم ان کو نہیں دیکھتے ہو ۔ ہم نے شیطانوں کو ان ہی لوگوں کا دوست بنایا ہے جو ایمان نہیں لاتے۔ ');
INSERT INTO "junagarri" VALUES(7,28,'7:28','اور وه لوگ جب کوئی فحش کام کرتے ہیں تو کہتے ہیں کہ ہم نے اپنے باپ دادا کو اسی طریق پر پایا ہے اور اللہ نے بھی ہم کو یہی بتایا ہے۔ آپ کہہ دیجیئے کہ اللہ تعالیٰ فحش بات کی تعلیم نہیں دیتا، کیا اللہ کے ذمہ ایسی بات لگاتے ہو جس کی تم سند نہیں رکھتے؟ ');
INSERT INTO "junagarri" VALUES(7,29,'7:29','آپ کہہ دیجیئے کہ میرے رب نے حکم دیا ہے انصاف کا اور یہ کہ تم ہر سجده کے وقت اپنا رخ سیدھا رکھا کرو اور اللہ تعالیٰ کی عبادت اس طور پر کرو کہ اس عبادت کو خالص اللہ ہی کے واسطے رکھو۔ تم کو اللہ نے جس طرح شروع میں پیدا کیا تھا اسی طرح تم دوباره پیدا ہوگے۔');
INSERT INTO "junagarri" VALUES(7,30,'7:30','بعض لوگوں کو اللہ نے ہدایت دی ہے اور بعض پر گمراہی ثابت ہوگئی ہے۔ ان لوگوں نے اللہ تعالیٰ کو چھوڑ کر شیطانوں کو دوست بنالیا ہے اور خیال رکھتے ہیں کہ وه راست پر ہیں۔');
INSERT INTO "junagarri" VALUES(7,31,'7:31','اے اولاد آدم! تم مسجد کی ہر حاضری کے وقت اپنا لباس پہن لیا کرو ۔ اور خوب کھاؤ اور پیو اور حد سے مت نکلو۔ بےشک اللہ حد سے نکل جانے والوں کو پسند نہیں کرتا۔ ');
INSERT INTO "junagarri" VALUES(7,32,'7:32','آپ فرمائیے کہ اللہ تعالیٰ کے پیدا کئے ہوئے اسباب زینت کو، جن کو اس نے اپنے بندوں کے واسطے بنایا ہے اور کھانے پینے کی حلال چیزوں کو کس شخص نے حرام کیا ہے؟ آپ کہہ دیجئے کہ یہ اشیا اس طور پر کہ قیامت کے روز خالص ہوں گی اہل ایمان کے لئے، دنیوی زندگی میں مومنوں کے لئے بھی ہیں ۔ ہم اسی طرح تمام آیات کو سمجھ داروں کے واسطے صاف صاف بیان کرتے ہیں۔');
INSERT INTO "junagarri" VALUES(7,33,'7:33','آپ فرمائیے کہ البتہ میرے رب نے صرف حرام کیا ہے ان تمام فحش باتوں کو جو علانیہ ہیں اور جو پوشیده ہیں اور ہر گناه کی بات کو اور ناحق کسی پر ظلم کرنے کو اور اس بات کو کہ تم اللہ کے ساتھ کسی ایسی چیز کو شریک ٹھہراؤ جس کی اللہ نے کوئی سند نازل نہیں کی اور اس بات کو کہ تم لوگ اللہ کے ذمے ایسی بات لگادو جس کو تم جانتے نہیں۔');
INSERT INTO "junagarri" VALUES(7,34,'7:34','اور ہر گروه کے لئے ایک میعاد معین ہے سو جس وقت ان کی میعاد معین آجائے گی اس وقت ایک ساعت نہ پیچھے ہٹ سکیں گے اور نہ آگے بڑھ سکیں گے۔');
INSERT INTO "junagarri" VALUES(7,35,'7:35','اے اولاد آدم! اگر تمہارے پاس پیغمبر آئیں جو تم ہی میں سے ہوں جو میرے احکام تم سے بیان کریں تو جو شخص تقویٰ اختیار کرے اور درستی کرے سو ان لوگوں پر نہ کچھ اندیشہ ہے اور نہ وه غمگین ہوں گے۔ ');
INSERT INTO "junagarri" VALUES(7,36,'7:36','اور جو لوگ ہمارے ان احکام کو جھٹلائیں اور ان سے تکبر کریں وه لوگ دوزخ والے ہوں گے وه اس میں ہمیشہ ہمیشہ رہیں گے۔ ');
INSERT INTO "junagarri" VALUES(7,37,'7:37','سو اس شخص سے زیاده ظالم کون ہوگا جو اللہ تعالیٰ پر جھوٹ باندھے یا اس کی آیتوں کو جھوٹا بتائے، ان لوگوں کے نصیب کا جو کچھ کتاب سے ہے وه ان کو مل جائے گا ، یہاں تک کہ جب ان کے پاس ہمارے بھیجے ہوئے فرشتے ان کی جان قبض کرنے آئیں گے تو کہیں گے کہ وه کہاں گئے جن کی تم اللہ کو چھوڑ کر عبادت کرتے تھے، وه کہیں گے کہ ہم سے سب غائب ہوگئے اور اپنے کافر ہونے کا اقرار کریں گے۔');
INSERT INTO "junagarri" VALUES(7,38,'7:38','اللہ تعالیٰ فرمائے گا کہ جو فرقے تم سے پہلے گزر چکے ہیں جنات میں سے بھی اور آدمیوں میں سے بھی، ان کے ساتھ تم بھی دوزخ میں جاؤ۔ جس وقت بھی کوئی جماعت داخل ہوگی اپنی دوسری جماعت کو لعنت کرے گی یہاں تک کہ جب اس میں سب جمع ہوجائیں گے تو پچھلے لوگ پہلے لوگوں کی نسبت کہیں گے کہ ہمارے پروردگار ہم کو ان لوگوں نے گمراه کیا تھا سو ان کو دوزخ کا عذاب دو گنا دے ۔ اللہ تعالیٰ فرمائے گا کہ سب ہی کا دوگنا ہے ، لیکن تم کو خبر نہیں۔');
INSERT INTO "junagarri" VALUES(7,39,'7:39','اور پہلے لوگ پچھلے لوگوں سے کہیں گے کہ پھر تم کو ہم پر کوئی فوقیت نہیں سو تم بھی اپنی کمائی کے بدلے میں عذاب کامزه چکھو۔');
INSERT INTO "junagarri" VALUES(7,40,'7:40','جن لوگوں نے ہماری آیتوں کو جھٹلایا اور ان سے تکبر کیا ان کے لئے آسمان کے دروازے نہ کھولے جائیں گے اور وه لوگ کبھی جنت میں نہ جائیں گے جب تک کہ اونٹ سوئی کے ناکہ کے اندر سے نہ چلا جائے اور ہم مجرم لوگوں کو ایسی ہی سزا دیتے ہیں۔');
INSERT INTO "junagarri" VALUES(7,41,'7:41','ان کے لئے آتش دوزخ کا بچھونا ہوگا اور ان کے اوپر (اسی کا) اوڑھنا ہوگا اور ہم ایسے ظالموں کو ایسی ہی سزا دیتے ہیں۔');
INSERT INTO "junagarri" VALUES(7,42,'7:42','اور جو لوگ ایمان لائے اور انہوں نے نیک کام کئے ہم کسی شخص کو اس کی قدرت سے زیاده کسی کا مکلف نہیں بناتے وہی لوگ جنت والے ہیں اور وه اس میں ہمیشہ ہمیشہ رہیں گے۔');
INSERT INTO "junagarri" VALUES(7,43,'7:43','اور جو کچھ ان کے دلوں میں (کینہ) تھا ہم اس کو دور کردیں گے ۔ ان کے نیچے نہریں جاری ہوں گی۔ اور وه لوگ کہیں گے کہ اللہ کا (لاکھ لاکھ) شکر ہے جس نے ہم کو اس مقام تک پہنچایا اور ہماری کبھی رسائی نہ ہوتی اگر اللہ تعالیٰ ہم کو نہ پہنچاتا ۔ واقعی ہمارے رب کے پیغمبر سچی باتیں لے کر آئے تھے۔ اور ان سے پکار کر کہا جائے گا کہ اس جنت کے تم وارث بنائے گئے ہو اپنے اعمال کے بدلے۔ ');
INSERT INTO "junagarri" VALUES(7,44,'7:44','اور اہل جنت اہل دوزخ کو پکاریں گے کہ ہم سے جو ہمارے رب نے وعده فرمایا تھا ہم نے تو اس کو واقعہ کے مطابق پایا، سو تم سے جو تمہارے رب نے وعده کیا تھا تم نے بھی اس کو واقعہ کے مطابق پایا وه کہیں گے ہاں، پھر ایک پکارنے والا دونوں کے درمیان میں پکارے گا کہ اللہ کی مار ہو ان ظالموں پر۔');
INSERT INTO "junagarri" VALUES(7,45,'7:45','جو اللہ کی راه سے اعراض کرتے تھے اور اس میں کجی تلاش کرتے تھے اور وه لوگ آخرت کے بھی منکر تھے۔');
INSERT INTO "junagarri" VALUES(7,46,'7:46','اور ان دونوں کے درمیان ایک آڑ ہوگی اور اعراف کے اوپر بہت سے آدمی ہوں گے وه لوگ ، ہر ایک کو ان کے قیافہ سے پہچانیں گے اور اہل جنت کو پکار کر کہیں گے، السلام علیکم! ابھی یہ اہل اعراف جنت میں داخل نہیں ہوئے ہوں گے اور اس کے امیدوار ہوں گے۔ ');
INSERT INTO "junagarri" VALUES(7,47,'7:47','اور جب ان کی نگاہیں اہل دوزخ کی طرف پھریں گی تو کہیں گے اے ہمارے رب! ہم کو ان ظالم لوگوں کے ساتھ شامل نہ کر۔');
INSERT INTO "junagarri" VALUES(7,48,'7:48','اور اہل اعراف بہت سے آدمیوں کو جن کو کہ ان کے قیافہ سے پہچانیں گے پکاریں گے کہیں گے کہ تمہاری جماعت اور تمہارا اپنے کو بڑا سمجھنا تمہارے کچھ کام نہ آیا۔ ');
INSERT INTO "junagarri" VALUES(7,49,'7:49','کیا یہ وہی ہیں جن کی نسبت تم قسمیں کھا کھا کر کہا کرتے تھے کہ اللہ تعالیٰ ان پر رحمت نہ کرے گا، ان کو یوں حکم ہوگا کہ جاؤ جنت میں تم پر نہ کچھ خوف ہے اور نہ تم مغموم ہوگے۔');
INSERT INTO "junagarri" VALUES(7,50,'7:50','اور دوزخ والے جنت والوں کو پکاریں گے، کہ ہمارےاوپر تھوڑا پانی ہی ڈال دو یا اور ہی کچھ دے دو، جو اللہ نے تم کو دے رکھا ہے۔ جنت والے کہیں گے کہ اللہ تعالیٰ نے دونوں چیزوں کی کافروں کے لئے بندش کردی ہے۔ ');
INSERT INTO "junagarri" VALUES(7,51,'7:51','جنہوں نے دنیا میں اپنے دین کو لہو ولعب بنا رکھا تھا اور جن کو دنیاوی زندگی نے دھوکہ میں ڈال رکھا تھا۔ سو ہم (بھی) آج کے روز ان کا نام بھول جائیں گے جیسا کہ وه اس دن کو بھول گئے اور جیسا یہ ہماری آیتوں کا انکار کرتے تھے۔');
INSERT INTO "junagarri" VALUES(7,52,'7:52','اور ہم نے ان لوگوں کے پاس ایک ایسی کتاب پہنچادی ہے جس کو ہم نے اپنے علم کامل سے بہت واضح کر کے بیان کردیا ہے ، وه ذریعہ ہدایت اور رحمت ان لوگوں کے لئے ہے جو ایمان لائے ہیں۔');
INSERT INTO "junagarri" VALUES(7,53,'7:53','ان لوگوں کو اور کسی بات کا انتظار نہیں صرف اس کے اخیر نتیجہ کا انتظار ہے ، جس روز اس کا اخیر نتیجہ پیش آئے گا اور اس روز جو لوگ اس کو پہلے سے بھولے ہوئے تھے یوں کہیں گے کہ واقعی ہمارے رب کے پیغمبر سچی سچی باتیں لائے تھے، سو اب کیا کوئی ہمارا سفارشی ہے کہ وه ہماری سفارش کردے یا کیا ہم پھر واپس بھیجے جاسکتے ہیں تاکہ ہم لوگ ان اعمال کے، جن کو ہم کیا کرتے تھے برخلاف دوسرے اعمال کریں۔ بےشک ان لوگوں نے اپنے آپ کو خساره میں ڈال دیا اور یہ جو جو باتیں تراشتے تھے سب گم ہوگئیں۔ ');
INSERT INTO "junagarri" VALUES(7,54,'7:54','بے شک تمہارا رب اللہ ہی ہے جس نے سب آسمانوں اور زمین کو چھ روز میں پیدا کیا ہے ، پھر عرش پر قائم ہوا ۔ وه شب سے دن کو ایسے طور پر چھپا دیتا ہے کہ وه شب اس دن کو جلدی سے آ لیتی ہے اور سورج اور چاند اور دوسرے ستاروں کو پیدا کیا ایسے طور پر کہ سب اس کے حکم کے تابع ہیں۔ یاد رکھو اللہ ہی کے لئے خاص ہے خالق ہونا اور حاکم ہونا، بڑی خوبیوں سے بھرا ہوا ہے اللہ جو تمام عالم کا پروردگار ہے۔');
INSERT INTO "junagarri" VALUES(7,55,'7:55','تم لوگ اپنے پروردگار سے دعا کیا کرو گڑگڑا کر کے بھی اور چپکے چپکے بھی۔ واقعی اللہ تعالیٰ ان لوگوں کو ناپسند کرتا ہے جو حد سے نکل جائیں۔');
INSERT INTO "junagarri" VALUES(7,56,'7:56','اور دنیا میں اس کے بعد کہ اس کی درستی کردی گئی ہے، فساد مت پھیلاؤ اور تم اللہ کی عبادت کرو اس سے ڈرتے ہوئے اور امیدوار رہتے ہوئے۔ بے شک اللہ تعالیٰ کی رحمت نیک کام کرنے والوں کے نزدیک ہے۔ ');
INSERT INTO "junagarri" VALUES(7,57,'7:57','اور وه ایسا ہے کہ اپنی باران رحمت سے پہلے ہواؤں کو بھیجتا ہے کہ وه خوش کر دیتی ہیں ، یہاں تک کہ جب وه ہوائیں بھاری بادلوں کو اٹھا لیتی ہیں ، تو ہم اس بادل کو کسی خشک سرزمین کی طرف ہانک لے جاتے ہیں، پھر اس بادل سے پانی برساتے ہیں پھر اس پانی سے ہر قسم کے پھل نکالتے ہیں ۔ یوں ہی ہم مردوں کو نکال کھڑا کریں گے تاکہ تم سمجھو۔ ');
INSERT INTO "junagarri" VALUES(7,58,'7:58','اور جو ستھری سرزمین ہوتی ہے اس کی پیداوار تو اللہ کے حکم سے خوب نکلتی ہے اور جو خراب ہے اس کی پیداوار بہت کم نکلتی ہے ، اسی طرح ہم دلائل کو طرح طرح سے بیان کرتے ہیں، ان لوگوں کے لئے جو شکر کرتے ہیں۔');
INSERT INTO "junagarri" VALUES(7,59,'7:59','ہم نے نوح (علیہ السلام) کو ان کی قوم کی طرف بھیجا توانہوں نے فرمایا اے میری قوم! تم اللہ کی عبادت کرو اس کے سوا کوئی تمہارا معبود ہونے کے قابل نہیں، مجھ کو تمہارے لئے ایک بڑے دن کے عذاب کا اندیشہ ہے۔');
INSERT INTO "junagarri" VALUES(7,60,'7:60','ان کی قوم کے بڑے لوگوں نے کہا کہ ہم تم کو صریح غلطی میں دیکھتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(7,61,'7:61','انہوں نے فرمایا کہ اے میری قوم! مجھ میں تو ذرا بھی گمراہی نہیں لیکن میں پروردگار عالم کا رسول ہوں۔');
INSERT INTO "junagarri" VALUES(7,62,'7:62','تم کو اپنے پروردگار کے پیغام پہنچاتا ہوں اور تمہاری خیر خواہی کرتا ہوں اور میں اللہ کی طرف سے ان امور کی خبر رکھتا ہوں جن کی تم کو خبر نہیں۔');
INSERT INTO "junagarri" VALUES(7,63,'7:63','اور کیا تم اس بات سے تعجب کرتے ہو کہ تمہارے پروردگار کی طرف سے تمہارے پاس ایک ایسے شخص کی معرفت، جو تمہاری ہی جنس کا ہے، کوئی نصیحت کی بات آگئی تاکہ وه شخص تم کو ڈرائے اور تاکہ تم ڈرجاؤ اور تاکہ تم پر رحم کیا جائے۔');
INSERT INTO "junagarri" VALUES(7,64,'7:64','سو وه لوگ ان کی تکذیب ہی کرتے رہے تو ہم نے نوح (علیہ السلام) کو اور ان کو جو ان کےساتھ کشتی میں تھے، بچالیا اور جن لوگوں نے ہماری آیتوں کو جھٹلایا تھا ان کو ہم نے غرق کردیا۔ بے شک وه لوگ اندھے ہورہے تھے۔ ');
INSERT INTO "junagarri" VALUES(7,65,'7:65','اور ہم نے قوم عاد کی طرف ان کے بھائی ہود (علیہ السلام) کو بھیجا ۔ انہوں نے فرمایا اے میری قوم! تم اللہ کی عبادت کرو، اس کے سوا کوئی تمہارا معبود نہیں، سو کیا تم نہیں ڈرتے');
INSERT INTO "junagarri" VALUES(7,66,'7:66','ان کی قوم میں جو بڑے لوگ کافر تھے انہوں نے کہا ہم تم کو کم عقلی میں دیکھتے ہیں ۔ اور ہم بے شک تم کو جھوٹے لوگوں میں سمجھتے ہیں۔');
INSERT INTO "junagarri" VALUES(7,67,'7:67','انہوں نے فرمایا کہ اے میری قوم! مجھ میں ذرا بھی کم عقلی نہیں لیکن میں پروردگار عالم کا بھیجا ہوا پیغمبر ہوں۔');
INSERT INTO "junagarri" VALUES(7,68,'7:68','تم کو اپنے پروردگار کے پیغام پہنچاتا ہوں اور میں تمہارا امانتدار خیرخواه ہوں۔');
INSERT INTO "junagarri" VALUES(7,69,'7:69','اور کیا تم اس بات سے تعجب کرتے ہو کہ تمہارے پروردگار کی طرف سے تمہارے پاس ایک ایسے شخص کی معرفت، جو تمہاری ہی جنس کاہے کوئی نصیحت کی بات آگئی تاکہ وه شخص تم کو ڈرائے اور تم یہ حالت یاد کرو کہ اللہ نے تم کو قوم نوح کے بعد جانشین بنایا اور ڈیل ڈول میں تم کو پھیلاؤ زیاده دیا ، سو اللہ کی نعمتوں کو یاد کرو تاکہ تم کو فلاح ہو۔');
INSERT INTO "junagarri" VALUES(7,70,'7:70','انہوں نے کہا کہ کیا آپ ہمارے پاس اس واسطے آئے ہیں کہ ہم صرف اللہ ہی کی عبادت کریں اور جن کو ہمارے باپ دادا پوجتے تھے ان کو چھوڑ دیں ، پس ہم کو جس عذاب کی دھمکی دیتے ہو اس کو ہمارے پاس منگوا دو اگر تم سچے ہو۔ ');
INSERT INTO "junagarri" VALUES(7,71,'7:71','انہوں نے فرمایا کہ بس اب تم پر اللہ کی طرف سے عذاب اور غضب آیا ہی چاہتا ہے کیا تم مجھ سے ایسے ناموں کے باب میں جھگڑتے ہو جن کو تم نے اور تمہارے باپ دادوں نے ٹھہرالیا ہے؟ ان کے معبود ہونے کی اللہ نے کوئی دلیل نہیں بھیجی۔ سو تم منتظر رہو میں بھی تمہارے ساتھ انتظار کر رہا ہوں۔');
INSERT INTO "junagarri" VALUES(7,72,'7:72','غرض ہم نے ان کو اور ان کے ساتھیوں کو اپنی رحمت سے بچالیا اور ان لوگوں کی جڑ کاٹ دی، جنہوں نے ہماری آیتوں کو جھٹلایا تھا اور وه ایمان لانے والے نہ تھے۔ ');
INSERT INTO "junagarri" VALUES(7,73,'7:73','اور ہم نے ثمود کی طرف ان کے بھائی صالح (علیہ السلام) کو بھیجا ۔ انہوں نے فرمایا اے میری قوم! تم اللہ کی عبادت کرو اس کے سوا کوئی تمہارا معبود نہیں۔ تمہارے پاس تمہارے پروردگار کی طرف سے ایک واضح دلیل آچکی ہے۔ یہ اونٹنی ہے اللہ کی جو تمہارے لئے دلیل ہے سو اس کو چھوڑ دو کہ اللہ تعالیٰ کی زمین میں کھاتی پھرے اور اس کو برائی کے ساتھ ہاتھ بھی مت لگانا کہ کہیں تم کو دردناک عذاب آپکڑے۔');
INSERT INTO "junagarri" VALUES(7,74,'7:74','اور تم یہ حالت یاد کرو کہ اللہ تعالیٰ نے تم کو عاد کے بعد جانشین بنایا اور تم کو زمین پر رہنے کا ٹھکانا دیا کہ نرم زمین پر محل بناتے ہو اور پہاڑوں کو تراش تراش کر ان میں گھر بناتے ہو ، سو اللہ تعالیٰ کی نعمتوں کو یاد کرو اور زمین میں فساد مت پھیلاؤ۔ ');
INSERT INTO "junagarri" VALUES(7,75,'7:75','ان کی قوم میں جو متکبر سردار تھے انہوں نے غریب لوگوں سے جو کہ ان میں سے ایمان لے آئے تھے پوچھا، کیا تم کواس بات کا یقین ہے کہ صالح (علیہ السلام) اپنے رب کی طرف سے بھیجے ہوئے ہیں؟ انہوں نے کہا کہ بےشک ہم تو اس پر پورا یقین رکھتے ہیں جو ان کو دے کر بھیجا گیا ہے۔ ');
INSERT INTO "junagarri" VALUES(7,76,'7:76','وه متکبر لوگ کہنے لگے کہ تم جس بات پر یقین لائے ہوئے ہو، ہم تو اس کے منکر ہیں۔ ');
INSERT INTO "junagarri" VALUES(7,77,'7:77','پس انہوں نے اس اونٹنی کو مار ڈالا اور اپنے پروردگار کے حکم سے سرکشی کی اور کہنے لگے کہ اے صالح! جس کی آپ ہم کو دھمکی دیتے تھے اس کو منگوائیے اگر آپ پیغمبر ہیں۔');
INSERT INTO "junagarri" VALUES(7,78,'7:78','پس ان کو زلزلہ نے آپکڑا اور وه اپنے گھروں میں اوندھے کے اوندھے پڑے ره گئے۔');
INSERT INTO "junagarri" VALUES(7,79,'7:79','اس وقت (صالح علیہ السلام) ان سے منھ موڑ کر چلے، اور فرمانے لگے کہ اے میری قوم! میں نے تو تم کو اپنے پروردگار کا حکم پہنچادیا تھا اور میں نے تمہاری خیرخواہی کی لیکن تم لوگ خیرخواہوں کو پسند نہیں کرتے۔');
INSERT INTO "junagarri" VALUES(7,80,'7:80','اور ہم نے لوط (علیہ السلام) کو بھیجا جبکہ انہوں نے اپنی قوم سے فرمایا کہ تم ایسا فحش کام کرتے ہو جس کو تم سے پہلے کسی نے دنیا جہان والوں میں سے نہیں کیا۔');
INSERT INTO "junagarri" VALUES(7,81,'7:81','تم مردوں کے ساتھ شہوت رانی کرتے ہو عورتوں کو چھوڑ کر ، بلکہ تم تو حد ہی سے گزر گئے ہو۔ ');
INSERT INTO "junagarri" VALUES(7,82,'7:82','اور ان کی قوم سے کوئی جواب نہ بن پڑا، بجز اس کے کہ آپس میں کہنے لگے کہ ان لوگوں کو اپنی بستی سے نکال دو۔ یہ لوگ بڑے پاک صاف بنتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(7,83,'7:83','سو ہم نے لوط (علیہ السلام) کو اور ان کے گھر والوں کو بچا لیا بجز ان کی بیوی کے کہ وه ان ہی لوگوں میں رہی جو عذاب میں ره گئے تھے۔ ');
INSERT INTO "junagarri" VALUES(7,84,'7:84','اور ہم نے ان پر خاص طرح کا مینہ برسایا پس دیکھو تو سہی ان مجرموں کا انجام کیسا ہوا؟ ');
INSERT INTO "junagarri" VALUES(7,85,'7:85','اور ہم نے مدین کی طرف ان کے بھائی شعیب (علیہ السلام) کو بھیجا ۔ انہوں نے فرمایا اے میری قوم! تم اللہ کی عبادت کرو اس کے سوا کوئی تمہارا معبود نہیں، تمہارے پاس تمہارے پروردگار کی طرف سے واضح دلیل آچکی ہے۔ پس تم ناپ اور تول پورا پورا کیا کرو اور لوگوں کو ان کی چیزیں کم کرکے مت دو اور روئے زمین میں، اس کے بعد کہ اس کی درستی کردی گئی، فساد مت پھیلاؤ، یہ تمہارے لئے نافع ہے اگر تم تصدیق کرو۔');
INSERT INTO "junagarri" VALUES(7,86,'7:86','اور تم سڑکوں پر اس غرض سے مت بیٹھا کرو کہ اللہ پر ایمان لانے والے کو دھمکیاں دو اور اللہ کی راه سے روکو اور اس میں کجی کی تلاش میں لگے رہو ۔ اور اس حالت کو یاد کرو جب کہ تم کم تھے پھر اللہ نے تم کو زیاده کردیا اور دیکھو کہ کیسا انجام ہوا فساد کرنے والوں کا۔');
INSERT INTO "junagarri" VALUES(7,87,'7:87','اور اگر تم میں سے کچھ لوگ اس حکم پر، جس کو دے کر مجھ کو بھیجا گیا، ایمان لے آئے ہیں اور کچھ ایمان نہیں لائے ہیں تو ذرا ٹھہر جاؤ! یہاں تک کہ ہمارے درمیان اللہ فیصلہ کئے دیتا ہے اور وه سب فیصلہ کرنے والوں سے بہتر ہے۔ ');
INSERT INTO "junagarri" VALUES(7,88,'7:88','ان کی قوم کے متکبر سرداروں نے کہا کہ اے شعیب! ہم آپ کو اور جو آپ کے ہمراه ایمان والے ہیں ان کو اپنی بستی سے نکال دیں گے الّا یہ کہ تم ہمارے مذہب میں پھر آجاؤ ۔ شعیب (علیہ السلام) نے جواب دیا کہ کیا ہم تمہارے مذہب میں آجائیں گو ہم اس کو مکروه ہی سمجھتے ہوں۔ ');
INSERT INTO "junagarri" VALUES(7,89,'7:89','ہم تو اللہ تعالیٰ پر بڑی جھوٹی تہمت لگانے والے ہو جائیں گے اگر ہم تمہارے دین میں آجائیں اس کے بعد کہ اللہ تعالیٰ نے ہم کو اس سے نجات دی اور ہم سے ممکن نہیں کہ تمہارے مذہب میں پھر آجائیں، لیکن ہاں یہ کہ اللہ ہی نے جو ہمارا مالک ہے مقدر کیا ہو ۔ ہمارے رب کا علم ہر چیز کو محیط ہے، ہم اللہ ہی پر بھروسہ رکھتے ہیں ۔ اے ہمارے پروردگار! ہمارے اور ہماری قوم کے درمیان حق کے موافق فیصلہ کر دے اور تو سب سے اچھا فیصلہ کرنے والا ہے۔ ');
INSERT INTO "junagarri" VALUES(7,90,'7:90','اور ان کی قوم کے کافر سرداروں نے کہا کہ اگر تم شعیب (علیہ السلام) کی راه پر چلو گے تو بےشک بڑا نقصان اٹھاؤ گے۔ ');
INSERT INTO "junagarri" VALUES(7,91,'7:91','پس ان کو زلزلے نے آپکڑا سو وه اپنے گھروں میں اوندھے کے اوندھے پڑے ره گئے۔ ');
INSERT INTO "junagarri" VALUES(7,92,'7:92','جنہوں نے شعیب (علیہ السلام) کی تکذیب کی تھی ان کی یہ حالت ہوگئی جیسے ان گھروں میں کبھی بسے ہی نہ تھے ۔ جنہوں نے شعیب (علیہ السلام) کی تکذیب کی تھی وہی خسارے میں پڑ گئے۔ ');
INSERT INTO "junagarri" VALUES(7,93,'7:93','اس وقت شعیب (علیہ السلام) ان سے منھ موڑ کر چلے اور فرمانے لگے کہ اے میری قوم! میں نے تم کو اپنے پروردگار کے احکام پہنچا دیئے تھے اور میں نے تمہاری خیر خواہی کی۔ پھر میں ان کافر لوگوں پر کیوں رنج کروں۔ ');
INSERT INTO "junagarri" VALUES(7,94,'7:94','اور ہم نے کسی بستی میں کوئی نبی نہیں بھیجا کہ وہاں کے رہنے والوں کو ہم نے سختی اور تکلیف میں نہ پکڑا ہو، تاکہ وه گڑ گڑائیں۔ ');
INSERT INTO "junagarri" VALUES(7,95,'7:95','پھر ہم نے اس بدحالی کی جگہ خوش حالی بدل دی، یہاں تک کہ ان کو خوب ترقی ہوئی اور کہنے لگے کہ ہمارے آباواجداد کو بھی تنگی اور راحت پیش آئی تھی تو ہم نے ان کو دفعتاً پکڑ لیا اور ان کو خبر بھی نہ تھی۔');
INSERT INTO "junagarri" VALUES(7,96,'7:96','اور اگر ان بستیوں کے رہنے والے ایمان لے آتے اور پرہیزگاری اختیار کرتے تو ہم ان پر آسمان اور زمین کی برکتیں کھول دیتے لیکن انہوں نے تکذیب کی تو ہم نے ان کے اعمال کی وجہ سے ان کو پکڑ لیا۔');
INSERT INTO "junagarri" VALUES(7,97,'7:97','کیا پھر بھی ان بستیوں کے رہنے والے اس بات سے بے فکر ہوگئے ہیں کہ ان پر ہمارا عذاب شب کے وقت آپڑے جس وقت وه سوتے ہوں۔');
INSERT INTO "junagarri" VALUES(7,98,'7:98','اور کیا ان بستیوں کے رہنے والے اس بات سے بےفکر ہوگئے ہیں کہ ان پر ہمارا عذاب دن چڑھے آپڑے جس وقت کہ وه اپنے کھیلوں میں مشغول ہوں۔');
INSERT INTO "junagarri" VALUES(7,99,'7:99','کیا پس وه اللہ کی اس پکڑ سے بےفکر ہوگئے۔ سو اللہ کی پکڑ سے بجز ان کے جن کی شامت ہی آگئی ہو اور کوئی بےفکر نہیں ہوتا۔ ');
INSERT INTO "junagarri" VALUES(7,100,'7:100','اور کیا ان لوگوں کو جو زمین کے وارث ہوئے وہاں کے لوگوں کی ہلاکت کے بعد (ان واقعات مذکوره نے) یہ بات نہیں بتلائی کہ اگر ہم چاہیں تو ان کے جرائم کے سبب ان کو ہلاک کر ڈالیں اور ہم ان کے دلوں پر بند لگا دیں، پس وه نہ سن سکیں۔ ');
INSERT INTO "junagarri" VALUES(7,101,'7:101','ان بستیوں کے کچھ کچھ قصے ہم آپ سے بیان کر رہے ہیں اور ان سب کے پاس ان کے پیغمبر معجزات لے کر آئے ، پھر جس چیز کو انہوں نے ابتدا میں جھوٹا کہہ دیا یہ بات نہ ہوئی کہ پھر اس کو مان لیتے ، اللہ تعالیٰ اسی طرح کافروں کے دلوں پر بند لگا دیتا ہے۔');
INSERT INTO "junagarri" VALUES(7,102,'7:102','اور اکثر لوگوں میں ہم نے وفائے عہد نہ دیکھا اور ہم نے اکثر لوگوں کو بےحکم ہی پایا۔');
INSERT INTO "junagarri" VALUES(7,103,'7:103','پھر ان کے بعد ہم نے موسیٰ (علیہ السلام) کو اپنے دلائل دے کر فرعون اور اس کے امرا کے پاس بھیجا ، مگر ان لوگوں نے ان کا بالکل حق ادا نہ کیا۔ سو دیکھئے ان مفسدوں کا کیا انجام ہوا؟ ');
INSERT INTO "junagarri" VALUES(7,104,'7:104','اور موسیٰ (علیہ السلام) نے فرمایا کہ اے فرعون! میں رب العالمین کی طرف سے پیغمبر ہوں۔');
INSERT INTO "junagarri" VALUES(7,105,'7:105','میرے لیے یہی شایان ہے کہ بجز سچ کے اللہ کی طرف کوئی بات منسوب نہ کروں، میں تمہارے پاس تمہارے رب کی طرف سے ایک بڑی دلیل بھی لایا ہوں ، سو تو بنی اسرائیل کو میرے ساتھ بھیج دے۔ ');
INSERT INTO "junagarri" VALUES(7,106,'7:106','فرعون نے کہا، اگر آپ کوئی معجزه لے کر آئے ہیں تو اس کو اب پیش کیجئے! اگر آپ سچے ہیں۔');
INSERT INTO "junagarri" VALUES(7,107,'7:107','پس آپ نے اپنا عصا ڈال دیا، سو دفعتاً وه صاف ایک اژدھا بن گیا۔');
INSERT INTO "junagarri" VALUES(7,108,'7:108','اور اپنا ہاتھ باہر نکالا سو وه یکایک سب دیکھنے والوں کے روبرو بہت ہی چمکتا ہوا ہو گیا۔ ');
INSERT INTO "junagarri" VALUES(7,109,'7:109','قوم فرعون میں جو سردار لوگ تھے انہوں نے کہا کہ واقعی یہ شخص بڑا ماہر جادوگر ہے۔ ');
INSERT INTO "junagarri" VALUES(7,110,'7:110','یہ چاہتا ہے کہ تم کو تمہاری سرزمین سے باہر کر دے سو تم لوگ کیا مشوره دیتے ہو۔');
INSERT INTO "junagarri" VALUES(7,111,'7:111','انہوں نے کہا کہ آپ ان کو اور ان کے بھائی کو مہلت دیجئے اور شہروں میں ہرکاروں کو بھیج دیجئے۔');
INSERT INTO "junagarri" VALUES(7,112,'7:112','کہ وه سب ماہر جادو گروں کو آپ کے پاس لا کر حاضر کر دیں۔ ');
INSERT INTO "junagarri" VALUES(7,113,'7:113','اور وه جادوگر فرعون کے پاس حاضر ہوئے، کہنے لگے کہ اگر ہم غالب آئے تو ہم کو کوئی بڑا صلہ ملے گا؟۔');
INSERT INTO "junagarri" VALUES(7,114,'7:114','فرعون نے کہا کہ ہاں اور تم مقرب لوگوں میں داخل ہو جاؤ گے۔ ');
INSERT INTO "junagarri" VALUES(7,115,'7:115','ان ساحروں نے عرض کیا کہ اے موسیٰ! خواه آپ ڈالئے اور یا ہم ہی ڈالیں؟ ');
INSERT INTO "junagarri" VALUES(7,116,'7:116','(موسیٰ علیہ السلام) نے فرمایا کہ تم ہی ڈالو ، پس جب انہوں نے ڈالا تو لوگوں کی نظر بندی کر دی اور ان پر ہیبت غالب کر دی اور ایک طرح کا بڑا جادو دکھلایا۔ ');
INSERT INTO "junagarri" VALUES(7,117,'7:117','اور ہم نے موسیٰ (علیہ السلام) کو حکم دیا کہ اپنا عصا ڈال دیجئے! سو عصا کا ڈالنا تھا کہ اس نے ان کے سارے بنے بنائے کھیل کو نگلنا شروع کیا۔ ');
INSERT INTO "junagarri" VALUES(7,118,'7:118','پس حق ظاہر ہوگیا اور انہوں نے جو کچھ بنایا تھا سب جاتا رہا۔');
INSERT INTO "junagarri" VALUES(7,119,'7:119','پس وه لوگ اس موقع پر ہار گئے اور خوب ذلیل ہوکر پھرے۔');
INSERT INTO "junagarri" VALUES(7,120,'7:120','اور وه جو ساحر تھے سجده میں گر گئے۔');
INSERT INTO "junagarri" VALUES(7,121,'7:121','کہنے لگے کہ ہم ایمان لائے رب العالمین پر۔ ');
INSERT INTO "junagarri" VALUES(7,122,'7:122','جو موسیٰ اور ہارون کا بھی رب ہے۔ ');
INSERT INTO "junagarri" VALUES(7,123,'7:123','فرعون کہنے لگا کہ تم موسیٰ پر ایمان لائے ہو بغیر اس کے کہ میں تم کو اجازت دوں؟ بےشک یہ سازش تھی جس پر تمہارا عمل درآمد ہوا ہے اس شہر میں تاکہ تم سب اس شہر سے یہاں کے رہنے والوں کو باہر نکال دو۔ سو اب تم کو حقیقت معلوم ہوئی جاتی ہے۔ ');
INSERT INTO "junagarri" VALUES(7,124,'7:124','میں تمہارے ایک طرف کے ہاتھ اور دوسری طرف کے پاؤں کاٹوں گا۔ پھر تم سب کو سولی پر لٹکا دوں گا۔ ');
INSERT INTO "junagarri" VALUES(7,125,'7:125','انہوں نے جواب دیا کہ ہم (مر کر) اپنے مالک ہی کے پاس جائیں گے۔ ');
INSERT INTO "junagarri" VALUES(7,126,'7:126','اور تو نے ہم میں کونسا عیب دیکھا ہے بجز اس کے کہ ہم اپنے رب کے احکام پر ایمان لے آئے ، جب وه ہمارے پاس آئے۔ اے ہمارے رب! ہمارے اوپر صبر کا فیضان فرما اور ہماری جان حالت اسلام پر نکال۔ ');
INSERT INTO "junagarri" VALUES(7,127,'7:127','اور قوم فرعون کے سرداروں نے کہا کہ کیا آپ موسیٰ (علیہ السلام) اور ان کی قوم کو یوں ہی رہنے دیں گے کہ وه ملک میں فساد کرتے پھریں ، اور وه آپ کو اور آپ کے معبودوں کو ترک کئے رہیں ۔ فرعون نے کہا کہ ہم ابھی ان لوگوں کے بیٹوں کو قتل کرنا شروع کر دیں گے اور عورتوں کو زنده رہنے دیں گے اور ہم کو ان پر ہر طرح کا زور ہے۔ ');
INSERT INTO "junagarri" VALUES(7,128,'7:128','موسیٰ (علیہ السلام) نے اپنی قوم سے فرمایا اللہ تعالیٰ کا سہارا حاصل کرو اور صبر کرو، یہ زمین اللہ تعالیٰ کی ہے، اپنے بندوں میں سے جس کو چاہے وه مالک بنا دے اور اخیر کامیابی ان ہی کی ہوتی ہے جو اللہ سے ڈرتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(7,129,'7:129','قوم کے لوگ کہنے لگے کہ ہم تو ہمیشہ مصیبت ہی میں رہے، آپ کی تشریف آوری سے قبل بھی اور آپ کی تشریف آوری کے بعد بھی ۔ موسیٰ (علیہ السلام) نے فرمایا کہ بہت جلد اللہ تمہارے دشمن کو ہلاک کر دے گا اور بجائے ان کے تم کو اس سرزمین کا خلیفہ بنا دے گا پھر تمہارا طرز عمل دیکھے گا۔ ');
INSERT INTO "junagarri" VALUES(7,130,'7:130','اور ہم نے فرعون والوں کو مبتلا کیا قحط سالی میں اور پھلوں کی کم پیداواری میں، تاکہ وه نصیحت قبول کریں۔ ');
INSERT INTO "junagarri" VALUES(7,131,'7:131','سو جب ان پر خوشحالی آجاتی تو کہتے کہ یہ تو ہمارے لیے ہونا ہی چاہئے اور اگر ان کو کوئی بدحالی پیش آتی تو موسیٰ (علیہ السلام) اور ان کے ساتھیوں کی نحوست بتلاتے ۔ یاد رکھو کہ ان کی نحوست اللہ تعالیٰ کے پاس ہے ، لیکن ان کے اکثر لوگ نہیں جانتے۔');
INSERT INTO "junagarri" VALUES(7,132,'7:132','اور یوں کہتے کیسی ہی بات ہمارے سامنے لاؤ کہ ان کے ذریعہ سے ہم پر جادو چلاؤ جب بھی ہم تمہاری بات ہر گز نہ مانیں گے۔ ');
INSERT INTO "junagarri" VALUES(7,133,'7:133','پھر ہم نے ان پر طوفان بھیجا اور ٹڈیاں اور گھن کا کیڑا اور مینڈک اور خون، کہ یہ سب کھلے کھلے معجزے تھے ۔ سو وه تکبر کرتے رہے اور وه لوگ کچھ تھے ہی جرائم پیشہ۔');
INSERT INTO "junagarri" VALUES(7,134,'7:134','اور جب ان پر کوئی عذاب واقع ہوتا تو یوں کہتے کہ اے موسیٰ! ہمارے لیے اپنے رب سے اس بات کی دعا کر دیجئے! جس کا اس نے آپ سے عہد کر رکھا ہے، اگر آپ اس عذاب کو ہم سے ہٹا دیں تو ہم ضرور ضرور آپ کے کہنے سے ایمان لے آئیں گے اور ہم بنی اسرائیل کو بھی (رہا کر کے) آپ کے ہمراه کر دیں گے۔');
INSERT INTO "junagarri" VALUES(7,135,'7:135','پھر جب ان سے اس عذاب کو ایک خاص وقت تک کہ اس تک ان کو پہنچنا تھا ہٹا دیتے، تو وه فوراً ہی عہد شکنی کرنے لگتے۔ ');
INSERT INTO "junagarri" VALUES(7,136,'7:136','پھر ہم نے ان سے بدلہ لیا یعنی ان کو دریا میں غرق کر دیا اس سبب سے کہ وه ہماری آیتوں کو جھٹلاتے تھے اور ان سے بالکل ہی غفلت کرتے تھے۔ ');
INSERT INTO "junagarri" VALUES(7,137,'7:137','اور ہم نے ان لوگوں کو جو کہ بالکل کمزور شمار کئے جاتے تھے ۔ اس سرزمین کے پورب پچھم کا مالک بنا دیا، جس میں ہم نے برکت رکھی ہے اور آپ کے رب کا نیک وعده، بنی اسرائیل کے حق میں ان کے صبر کی وجہ سے پورا ہوگیا اور ہم نے فرعون کے اور اس کی قوم کے ساختہ پرداختہ کارخانوں کو اور جو کچھ وه اونچی اونچی عمارتیں بنواتے تھے، سب کو درہم برہم کر دیا۔ ');
INSERT INTO "junagarri" VALUES(7,138,'7:138','اور ہم نے بنی اسرائیل کو دریا سے پار اتار دیا۔ پس ان لوگوں کا ایک قوم پر گزر ہوا جو اپنے چند بتوں سے لگے بیٹھے تھے، کہنے لگے اے موسیٰ! ہمارے لیے بھی ایک معبود ایسا ہی مقرر کر دیجئے! جیسے ان کے یہ معبود ہیں۔ آپ نے فرمایا کہ واقعی تم لوگوں میں بڑی جہالت ہے۔ ');
INSERT INTO "junagarri" VALUES(7,139,'7:139','یہ لوگ جس کام میں لگے ہیں یہ تباه کیا جائے گا اور ان کا یہ کام محض بےبنیاد ہے۔ ');
INSERT INTO "junagarri" VALUES(7,140,'7:140','فرمایا کیا اللہ تعالیٰ کے سوا اور کسی کو تمہارا معبود تجویز کردوں؟ حالانکہ اس نے تم کو تمام جہان والوں پر فوقیت دی ہے۔ ');
INSERT INTO "junagarri" VALUES(7,141,'7:141','اور وه وقت یاد کرو جب ہم نے تم کو فرعون والوں سے بچا لیا جو تم کو بڑی سخت تکلیفیں پہنچاتے تھے۔ تمہارے بیٹوں کو قتل کر ڈالتے تھے اور تمہاری عورتوں کو زنده چھوڑ دیتے تھے اور اس میں تمہارے پروردگار کی طرف سے بڑی بھاری آزمائش تھی۔ ');
INSERT INTO "junagarri" VALUES(7,142,'7:142','اور ہم نے موسیٰ (علیہ السلام) سے تیس راتوں کا وعده کیا اور دس رات مزید سے ان تیس راتوں کو پورا کیا۔ سو ان کے پروردگار کا وقت پورے چالیس رات کا ہوگیا ۔ اور موسیٰ (علیہ السلام) نے اپنے بھائی ہارون (علیہ السلام) سے کہا کہ میرے بعد ان کا انتظام رکھنا اور اصلاح کرتے رہنا اور بدنظم لوگوں کی رائے پر عمل مت کرنا۔ ');
INSERT INTO "junagarri" VALUES(7,143,'7:143','اور جب موسیٰ (علیہ السلام) ہمارے وقت پر آئے اور ان کے رب نے ان سے باتیں کیں تو عرض کیا کہ اے میرے پروردگار! اپنا دیدار مجھ کو کرا دیجئے کہ میں آپ کو ایک نظر دیکھ لوں ارشاد ہوا کہ تم مجھ کو ہرگز نہیں دیکھ سکتے لیکن تم اس پہاڑ کی طرف دیکھتے رہو وه اگر اپنی جگہ پر برقرار رہا تو تم بھی مجھے دیکھ سکو گے۔ پس جب ان کے رب نے پہاڑ پر تجلی فرمائی تو تجلی نے اس کے پرخچے اڑا دیئے اور موسیٰ (علیہ السلام) بےہوش ہوکر گر پڑے ۔ پھر جب ہوش میں آئے تو عرض کیا، بےشک آپ کی ذات منزه ہے میں آپ کی جناب میں توبہ کرتا ہوں اور میں سب سے پہلے آپ پر ایمان لانے والا ہوں۔ ');
INSERT INTO "junagarri" VALUES(7,144,'7:144','ارشاد ہوا کہ اے موسیٰ! میں نے پیغمبری اور اپنی ہمکلامی سے اور لوگوں پر تم کو امتیاز دیا ہے تو جو کچھ تم کو میں نے عطا کیا ہے اس کو لو اور شکر کرو۔ ');
INSERT INTO "junagarri" VALUES(7,145,'7:145','اور ہم نے چند تختیوں پر ہر قسم کی نصیحت اور ہر چیز کی تفصیل ان کو لکھ کر دی ، تم ان کو پوری طاقت سے پکڑ لو اور اپنی قوم کو حکم کرو کہ ان کے اچھے اچھے احکام پر عمل کریں ، اب بہت جلد تم لوگوں کو ان بے حکموں کا مقام دکھلاتا ہوں۔ ');
INSERT INTO "junagarri" VALUES(7,146,'7:146','میں ایسے لوگوں کو اپنے احکام سے برگشتہ ہی رکھوں گا جو دنیا میں تکبر کرتے ہیں، جس کا ان کو کوئی حق حاصل نہیں اور اگر تمام نشانیاں دیکھ لیں تب بھی وه ان پر ایمان نہ لائیں، اور اگر ہدایت کا راستہ دیکھیں تو اس کو اپنا طریقہ نہ بنائیں اور اگر گمراہی کا راستہ دیکھ لیں تو اس کو اپنا طریقہ بنالیں ۔ یہ اس سبب سے ہے کہ انہوں نے ہماری آیتوں کو جھٹلایا اور ان سے غافل رہے۔ ');
INSERT INTO "junagarri" VALUES(7,147,'7:147','اور یہ لوگ جنہوں نے ہماری آیتوں کو اور قیامت کے پیش آنے کو جھٹلایا ان کے سب کام غارت گئے۔ ان کو وہی سزا دی جائے گی جو کچھ یہ کرتے تھے۔ ');
INSERT INTO "junagarri" VALUES(7,148,'7:148','اور موسیٰ (علیہ السلام) کی قوم نے ان کے بعد اپنے زیوروں کا ایک بچھڑا معبود ٹھہرا لیا جو کہ ایک قالب تھا جس میں ایک آواز تھی۔ کیا انہوں نے یہ نہ دیکھا کہ وه ان سے بات نہیں کرتا تھا اور نہ ان کو کوئی راه بتلاتا تھا اس کو انہوں نے معبود قرار دیا اور بڑی بے انصافی کا کام کیا۔ ');
INSERT INTO "junagarri" VALUES(7,149,'7:149','اور جب نادم ہوئے اور معلوم ہوا کہ واقعی وه لوگ گمراہی میں پڑ گئے تو کہنے لگے کہ اگر ہمارا رب ہم پر رحم نہ کرے اور ہمارا گناه معاف نہ کرے تو ہم بالکل گئے گزرے ہو جائیں گے۔');
INSERT INTO "junagarri" VALUES(7,150,'7:150','اور جب موسیٰ (علیہ السلام) اپنی قوم کی طرف واپس آئے غصہ اور رنج میں بھرے ہوئے تو فرمایا کہ تم نے میرے بعد یہ بڑی بری جانشینی کی؟ کیا اپنے رب کے حکم سے پہلے ہی تم نے جلد بازی کرلی، اور جلدی سے تختیاں ایک طرف رکھیں اور اپنے بھائی کا سر پکڑ کر ان کو اپنی طرف گھسیٹنے لگے۔ ہارون (علیہ السلام) نے کہا کہ اے میرے ماں جائے! ان لوگوں نے مجھ کو بےحقیقت سمجھا اور قریب تھا کہ مجھ کو قتل کر ڈالیں تو تم مجھ پر دشمنوں کو مت ہنساؤ اور مجھ کو ان ظالموں کے ذیل میں مت شمار کرو۔ ');
INSERT INTO "junagarri" VALUES(7,151,'7:151','موسیٰ (علیہ السلام) نے کہا کہ اے میرے رب! میری خطا معاف فرما اور میرے بھائی کی بھی اور ہم دونوں کو اپنی رحمت میں داخل فرما اور تو سب رحم کرنے والوں سے زیاده رحم کرنے والا ہے۔');
INSERT INTO "junagarri" VALUES(7,152,'7:152','بےشک جن لوگوں نے گوسالہ پرستی کی ہے ان پر بہت جلد ان کے رب کی طرف سے غضب اور ذلت اس دنیوی زندگی ہی میں پڑے گی اور ہم افترا پردازوں کو ایسی ہی سزا دیا کرتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(7,153,'7:153','اور جن لوگوں نے گناه کے کام کئے پھر وه ان کے بعد توبہ کر لیں اور ایمان لے آئیں تو تمہارا رب اس توبہ کے بعد گناه معاف کر دینے والا، رحمت کرنے والا ہے۔ ');
INSERT INTO "junagarri" VALUES(7,154,'7:154','اور جب موسیٰ (علیہ السلام) کا غصہ فرد ہوا تو ان تختیوں کو اٹھا لیا اور ان کے مضامین میں ان لوگوں کے لیے جو اپنے رب سے ڈرتے تھے ہدایت اور رحمت تھی۔ ');
INSERT INTO "junagarri" VALUES(7,155,'7:155','اور موسیٰ (علیہ السلام) نے ستر آدمی اپنی قوم میں سے ہمارے وقت معین کے لیے منتخب کئے، سو جب ان کو زلزلہ نے آپکڑا تو موسیٰ (علیہ السلام) عرض کرنے لگے کہ اے میرے پروردگار! اگر تجھ کو یہ منظور ہوتا تو اس سے قبل ہی ان کو اور مجھ کو ہلاک کر دیتا۔ کیا تو ہم میں سے چند بے وقوفوں کی حرکت پر سب کو ہلاک کردے گا؟ یہ واقعہ محض تیری طرف سے ایک امتحان ہے، ایسے امتحانات سے جس کو تو چاہے گمراہی میں ڈال دے اور جس کو چاہے ہدایت پر قائم رکھے۔ تو ہی تو ہمارا کارساز ہے پس ہم پر مغفرت اور رحمت فرما اور تو سب معافی دینے والوں سے زیاده اچھا ہے۔ ');
INSERT INTO "junagarri" VALUES(7,156,'7:156','اور ہم لوگوں کے نام دنیا میں بھی نیک حالی لکھ دے اور آخرت میں بھی، ہم تیری طرف رجوع کرتے ہیں ۔ اللہ تعالیٰ نے فرمایا کہ میں اپنا عذاب اسی پر واقع کرتا ہوں جس پر چاہتا ہوں اور میری رحمت تمام اشیا پر محیط ہے ۔ تو وه رحمت ان لوگوں کے نام ضرور لکھوں گا جو اللہ سے ڈرتے ہیں اور زکوٰة دیتے ہیں اور جو ہماری آیتوں پر ایمان لاتے ہیں۔');
INSERT INTO "junagarri" VALUES(7,157,'7:157','جو لوگ ایسے رسول نبی امی کا اتباع کرتے ہیں جن کو وه لوگ اپنے پاس تورات وانجیل میں لکھا ہوا پاتے ہیں ۔ وه ان کو نیک باتوں کا حکم فرماتے ہیں اور بری باتوں سے منع کرتے ہیں اور پاکیزه چیزوں کو حلال بتاتے ہیں اور گندی چیزوں کو ان پر حرام فرماتے ہیں اور ان لوگوں پر جو بوجھ اور طوق تھے ان کو دور کرتے ہیں۔ سو جو لوگ اس نبی پر ایمان لاتے ہیں اور ان کی حمایت کرتے ہیں اور ان کی مدد کرتے ہیں اور اس نور کا اتباع کرتے ہیں جو ان کے ساتھ بھیجا گیا ہے، ایسے لوگ پوری فلاح پانے والے ہیں۔ ');
INSERT INTO "junagarri" VALUES(7,158,'7:158','آپ کہہ دیجئے کہ اے لوگو! میں تم سب کی طرف اس اللہ تعالیٰ کا بھیجا ہوا ہوں، جس کی بادشاہی تمام آسمانوں اور زمین میں ہے اس کے سوا کوئی عبادت کے لائق نہیں وہی زندگی دیتا ہے اور وہی موت دیتا ہے سو اللہ تعالیٰ پر ایمان لاؤ اور اس کے نبی امی پر جو کہ اللہ تعالیٰ پر اور اس کے احکام پر ایمان رکھتے ہیں اور ان کا اتباع کرو تاکہ تم راه پر آجاؤ۔ ');
INSERT INTO "junagarri" VALUES(7,159,'7:159','اور قوم موسیٰ میں ایک جماعت ایسی بھی ہے جو حق کے مطابق ہدایت کرتی ہے اور اسی کے مطابق انصاف بھی کرتی ہے۔ ');
INSERT INTO "junagarri" VALUES(7,160,'7:160','اور ہم نے ان کو باره خاندانوں میں تقسیم کرکے سب کی الگ الگ جماعت مقرر کر دی اور ہم نے موسیٰ (علیہ السلام) کو حکم دیا جب کہ ان کی قوم نے ان سے پانی مانگا کہ اپنے عصا کو فلاں پتھر پر مارو پس فوراً اس سے باره چشمے پھوٹ نکلے۔ ہر ہر شخص نے اپنے پانی پینے کا موقع معلوم کر لیا۔ اور ہم نے ان پر ابر کو سایہ فگن کیا اور ان کو من وسلوی (ترنجبین اور بٹیریں) پہنچائیں، کھاؤ نفیس چیزوں سے جو کہ ہم نے تم کو دی ہیں اور انہوں نے ہمارا کوئی نقصان نہیں کیا لیکن اپنا ہی نقصان کرتے تھے۔');
INSERT INTO "junagarri" VALUES(7,161,'7:161','اور جب ان کو حکم دیا گیا کہ تم لوگ اس آبادی میں جاکر رہو اور کھاؤ اس سے جس جگہ تم رغبت کرو اور زبان سے یہ کہتے جانا کہ توبہ ہے اور جھکے جھکے دروازه میں داخل ہونا ہم تمہاری خطائیں معاف کردیں گے۔ جو لوگ نیک کام کریں گے ان کو مزید برآں اور دیں گے۔');
INSERT INTO "junagarri" VALUES(7,162,'7:162','سو بدل ڈالا ان ظالموں نے ایک اور کلمہ جو خلاف تھا اس کلمہ کے جس کی ان سے فرمائش کی گئی تھی، اس پر ہم نے ان پر ایک آفت سماوی بھیجی اس وجہ سے کہ وه حکم کو ضائع کرتے تھے۔ ');
INSERT INTO "junagarri" VALUES(7,163,'7:163','اور آپ ان لوگوں سے ، اس بستی والوں کا جو کہ دریائے (شور) کے قریب آباد تھے اس وقت کا حال پوچھئے! جب کہ وه ہفتہ کے بارے میں حد سے نکل رہے تھے جب کہ ان کے ہفتہ کے روز تو ان کی مچھلیاں ظاہر ہو ہو کر ان کے سامنے آتی تھیں، اور جب ہفتہ کا دن نہ ہوتا تو ان کے سامنے نہ آتی تھیں، ہم ان کی اس طرح پر آزمائش کرتے تھے اس سبب سے کہ وه بےحکمی کیا کرتے تھے۔ ');
INSERT INTO "junagarri" VALUES(7,164,'7:164','اور جب کہ ان میں سے ایک جماعت نے یوں کہا کہ تم ایسے لوگوں کو کیوں نصیحت کرتے ہو جن کو اللہ بالکل ہلاک کرنے والا ہے یا ان کو سخت سزا دینے والا ہے؟ انہو ں نے جواب دیا کہ تمہارے رب کے روبرو عذر کرنے کے لیے اور اس لیے کہ شاید یہ ڈر جائیں۔');
INSERT INTO "junagarri" VALUES(7,165,'7:165','سو جب وه اس کو بھول گئے جو ان کو سمجھایا جاتا تھا تو ہم نے ان لوگوں کو تو بچا لیا جو اس بری عادت سے منع کیا کرتے تھے اور ان لوگوں کو جو کہ زیادتی کرتے تھے ایک سخت عذاب میں پکڑ لیا اس وجہ سے کہ وه بےحکمی کیا کرتے تھے۔ ');
INSERT INTO "junagarri" VALUES(7,166,'7:166','یعنی جب وه، جس کام سے ان کو منع کیا گیا تھا اس میں حد سے نکل گئے تو ہم نے ان کو کہہ دیا تم ذلیل بندر بن جاؤ۔ ');
INSERT INTO "junagarri" VALUES(7,167,'7:167','اور وه وقت یاد کرنا چاہیئے کہ آپ کے رب نے یہ بات بتلا دی کہ وه ان یہود پر قیامت تک ایسے شخص کو ضرور مسلط کرتا رہے گا جو ان کو سزائے شدید کی تکلیف پہنچاتا رہے گا، بلاشبہ آپ کا رب جلدی ہی سزا دے دیتا ہے اور بلاشبہ وه واقعی بڑی مغفرت اور بڑی رحمت والا ہے۔ ');
INSERT INTO "junagarri" VALUES(7,168,'7:168','اور ہم نے دنیا میں ان کی مختلف جماعتیں کر دیں۔ بعض ان میں نیک تھے اور بعض ان میں اور طرح تھے اور ہم ان کو خوش حالیوں اور بدحالیوں سے آزماتے رہے کہ شاید باز آجائیں۔ ');
INSERT INTO "junagarri" VALUES(7,169,'7:169','پھر ان کے بعد ایسے لوگ ان کے جانشین ہوئے کہ کتاب کو ان سے حاصل کیا وه اس دنیائے فانی کا مال متاع لے لیتے ہیں اور کہتے ہیں کہ ہماری ضرور مغفرت ہو جائے گی حالانکہ اگر ان کے پاس ویسا ہی مال متاع آنے لگے تو اس کو بھی لے لیں گے۔ کیا ان سے اس کتاب کے اس مضمون کا عہد نہیں لیا گیا کہ اللہ کی طرف بجز حق بات کے اور کسی بات کی نسبت نہ کریں ، اور انہوں نے اس کتاب میں جو کچھ تھا اس کو پڑھ لیا اور آخرت والا گھر ان لوگوں کے لیے بہتر ہے جو تقویٰ رکھتے ہیں، پھر کیا تم نہیں سمجھتے۔');
INSERT INTO "junagarri" VALUES(7,170,'7:170','اور جو لوگ کتاب کے پابند ہیں اور نماز کی پابندی کرتے ہیں، ہم ایسے لوگوں کا جو اپنی اصلاح کریں ثواب ضائع نہ کریں گے۔ ');
INSERT INTO "junagarri" VALUES(7,171,'7:171','اور وه وقت بھی قابل ذکر ہے جب ہم نے پہاڑ کو اٹھا کر سائبان کی طرح ان کے اوپر معلق کر دیا اور ان کو یقین ہوگیا کہ اب ان پر گرا اور کہا کہ جو کتاب ہم نے تم کو دی ہے اسے مضبوطی کے ساتھ قبول کرو اور یاد رکھو جو احکام اس میں ہیں اس سے توقع ہے کہ تم متقی بن جاؤ۔ ');
INSERT INTO "junagarri" VALUES(7,172,'7:172','اور جب آپ کے رب نے اولاد آدم کی پشت سے ان کی اولاد کو نکالا اور ان سے ان ہی کے متعلق اقرار لیا کہ کیا میں تمہارا رب نہیں ہوں؟ سب نے جواب دیا کیوں نہیں! ہم سب گواه بنتے ہیں ۔ تاکہ تم لوگ قیامت کے روز یوں نہ کہو کہ ہم تو اس سے محض بے خبر تھے۔');
INSERT INTO "junagarri" VALUES(7,173,'7:173','یا یوں کہو کہ پہلے پہلے شرک تو ہمارے بڑوں نے کیا اور ہم ان کے بعد ان کی نسل میں ہوئے، سو کیا ان غلط راه والوں کے فعل پر تو ہم کو ہلاکت میں ڈال دے گا؟ ');
INSERT INTO "junagarri" VALUES(7,174,'7:174','ہم اسی طرح آیات کو صاف صاف بیان کرتے ہیں اور تاکہ وه باز آجائیں۔');
INSERT INTO "junagarri" VALUES(7,175,'7:175','اور ان لوگوں کو اس شخص کا حال پڑھ کر سنائیے کہ جس کو ہم نے اپنی آیتیں دیں پھر وه ان سے بالکل ہی نکل گیا، پھر شیطان اس کے پیچھے لگ گیا سو وه گمراه لوگوں میں شامل ہوگیا۔ ');
INSERT INTO "junagarri" VALUES(7,176,'7:176','اور اگر ہم چاہتے تو اس کو ان آیتوں کی بدولت بلند مرتبہ کر دیتے لیکن وه تو دنیا کی طرف مائل ہوگیا اور اپنی نفسانی خواہش کی پیروی کرنے لگا سو اس کی حالت کتے کی سی ہوگئی کہ اگر تو اس پر حملہ کرے تب بھی ہانپے یا اس کو چھوڑ دے تب بھی ہانپے ، یہی حالت ان لوگوں کی ہے جنہوں نے ہماری آیتوں کو جھٹلایا۔ سو آپ اس حال کو بیان کر دیجئے شاید وه لوگ کچھ سوچیں۔ ');
INSERT INTO "junagarri" VALUES(7,177,'7:177','ان لوگوں کی حالت بھی بری حالت ہے جو ہماری آیات کو جھٹلاتے ہیں اور وه اپنا نقصان کرتے ہیں۔');
INSERT INTO "junagarri" VALUES(7,178,'7:178','جس کو اللہ ہدایت کرتا ہے سو ہدایت پانے والا وہی ہوتا ہے اور جس کو وه گمراه کر دے سو ایسے ہی لوگ خسارے میں پڑنے والے ہیں۔ ');
INSERT INTO "junagarri" VALUES(7,179,'7:179','اور ہم نے ایسے بہت سے جن اور انسان دوزخ کے لیے پیدا کئے ہیں ، جن کے دل ایسے ہیں جن سے نہیں سمجھتے اور جن کی آنکھیں ایسی ہیں جن سے نہیں دیکھتے اور جن کے کان ایسے ہیں جن سے نہیں سنتے۔ یہ لوگ چوپایوں کی طرح ہیں بلکہ یہ ان سے بھی زیاده گمراه ہیں ۔ یہی لوگ غافل ہیں۔');
INSERT INTO "junagarri" VALUES(7,180,'7:180','اور اچھے اچھے نام اللہ ہی کے لیے ہیں سو ان ناموں سے اللہ ہی کو موسوم کیا کرو اور ایسے لوگوں سے تعلق بھی نہ رکھو جو اس کے ناموں میں کج روی کرتے ہیں ، ان لوگوں کو ان کے کئے کی ضرور سزا ملے گی۔');
INSERT INTO "junagarri" VALUES(7,181,'7:181','اور ہماری مخلوق میں ایک جماعت ایسی بھی ہے جو حق کے موافق ہدایت کرتی ہے اور اس کے موافق انصاف بھی کرتی ہے۔');
INSERT INTO "junagarri" VALUES(7,182,'7:182','اور جو لوگ ہماری آیات کو جھٹلاتے ہیں ہم ان کو بتدریج (گرفت میں) لئے جارہے ہیں اس طور پر کہ ان کو خبر بھی نہیں۔');
INSERT INTO "junagarri" VALUES(7,183,'7:183','اور ان کو مہلت دیتا ہوں بےشک میری تدبیر بڑی مضبوط ہے۔ ');
INSERT INTO "junagarri" VALUES(7,184,'7:184','کیا ان لوگوں نے اس بات پر غور نہ کیا کہ ان کے ساتھی کو ذرا بھی جنون نہیں وه تو صرف ایک صاف صاف ڈرانے والے ہیں۔ ');
INSERT INTO "junagarri" VALUES(7,185,'7:185','اور کیا ان لوگوں نے غور نہیں کیا آسمانوں اور زمین کے عالم میں اور دوسری چیزوں میں جو اللہ نے پیدا کی ہیں اور اس بات میں کہ ممکن ہے کہ ان کی اجل قریب ہی آ پہنچی ہو ۔ پھر قرآن کے بعد کون سی بات پر یہ لوگ ایمان لائیں گے؟ ');
INSERT INTO "junagarri" VALUES(7,186,'7:186','جس کو اللہ تعالیٰ گمراه کر دے اس کو کوئی راه پر نہیں لا سکتا۔ اور اللہ تعالیٰ ان کو ان کی گمراہی میں بھٹکتے ہوئے چھوڑ دیتا ہے۔');
INSERT INTO "junagarri" VALUES(7,187,'7:187','یہ لوگ آپ سے قیامت کے متعلق سوال کرتے ہیں کہ اس کا وقوع کب ہوگا؟ آپ فرما دیجئے کہ اس کا علم صرف میرے رب ہی کے پاس ہے ، اس کے وقت پر اس کو سوا اللہ کے کوئی اور ظاہر نہ کرے گا۔ وه آسمانوں اور زمین میں بڑا بھاری (حادثہ) ہوگا وه تم پر محض اچانک آپڑے گی۔ وه آپ سے اس طرح پوچھتے ہیں جیسے گویا آپ اس کی تحقیقات کرچکے ہیں ۔ آپ فرما دیجئے کہ اس کا علم خاص اللہ ہی کے پاس ہے لیکن اکثر لوگ نہیں جانتے۔');
INSERT INTO "junagarri" VALUES(7,188,'7:188','آپ فرما دیجئے کہ میں خود اپنی ذات خاص کے لیے کسی نفع کا اختیار نہیں رکھتا اور نہ کسی ضرر کا، مگر اتنا ہی کہ جتنا اللہ نے چاہا ہو اور اگر میں غیب کی باتیں جانتا ہوتا تو میں بہت سے منافع حاصل کر لیتا اور کوئی نقصان مجھ کو نہ پہنچتا میں تو محض ڈرانے والا اور بشارت دینے والا ہوں ان لوگوں کو جو ایمان رکھتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(7,189,'7:189','وه اللہ تعالیٰ ایسا ہے جس نے تم کو ایک تن واحد سے پیدا کیا اور اسی سے اس کا جوڑا بنایا تاکہ وه اس اپنے جوڑے سے انس حاصل کرے پھر جب میاں نے بیوی سے قربت کی تو اس کو حمل ره گیا ہلکا سا۔ سو وه اس کو لئے ہوئے چلتی پھرتی رہی ، پھر جب وه بوجھل ہوگئی تو دونوں میاں بیوی اللہ سے جو ان کا مالک ہے دعا کرنے لگے کہ اگر تو نے ہم کو صحیح سالم اولاد دے دی تو ہم خوب شکر گزاری کریں گے۔ ');
INSERT INTO "junagarri" VALUES(7,190,'7:190','سو جب اللہ نے دونوں کو صحیح سالم اولاد دے دی تو اللہ کی دی ہوئی چیز میں وه دونوں اللہ کے شریک قرار دینے لگے ، سو اللہ پاک ہے ان کے شرک سے۔');
INSERT INTO "junagarri" VALUES(7,191,'7:191','کیا ایسوں کو شریک ٹھہراتے ہیں جو کسی چیز کو پیدا نہ کر سکیں اور وه خود ہی پیدا کئے گئے ہوں۔');
INSERT INTO "junagarri" VALUES(7,192,'7:192','اور وه ان کو کسی قسم کی مدد نہیں دے سکتے اور وه خود بھی مدد نہیں کرسکتے۔');
INSERT INTO "junagarri" VALUES(7,193,'7:193','اور اگر تم ان کو کوئی بات بتلانے کو پکارو تو تمہارے کہنے پر نہ چلیں تمہارے اعتبار سے دونوں امر برابر ہیں خواه تم ان کو پکارو یا تم خاموش رہو۔');
INSERT INTO "junagarri" VALUES(7,194,'7:194','واقعی تم اللہ کو چھوڑ کر جن کی عبادت کرتے ہو وه بھی تم ہی جیسے بندے ہیں سو تم ان کو پکارو پھر ان کو چاہئے کہ تمہارا کہنا کر دیں اگر تم سچے ہو۔');
INSERT INTO "junagarri" VALUES(7,195,'7:195','کیا ان کے پاؤں ہیں جن سے وه چلتے ہوں یا ان کے ہاتھ ہیں جن سے وه کسی چیز کو تھام سکیں، یا ان کی آنکھیں ہیں جن سے وه دیکھتے ہوں، یا ان کے کان ہیں جن سے وه سنتے ہیں آپ کہہ دیجئے! تم اپنے سب شرکا کو بلا لو، پھر میری ضرر رسانی کی تدبیر کرو پھر مجھ کو ذرا مہلت مت دو۔ ');
INSERT INTO "junagarri" VALUES(7,196,'7:196','یقیناً میرا مددگار اللہ تعالیٰ ہے جس نے یہ کتاب نازل فرمائی اور وه نیک بندوں کی مدد کرتا ہے۔');
INSERT INTO "junagarri" VALUES(7,197,'7:197','اور تم جن لوگوں کی اللہ کو چھوڑ کر عبادت کرتے ہو وه تمہاری کچھ مدد نہیں کرسکتے اور نہ وه اپنی مدد کرسکتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(7,198,'7:198','اور ان کو اگر کوئی بات بتلانے کو پکارو تو اس کو نہ سنیں اور ان کو آپ دیکھتے ہیں کہ گویا وه آپ کو دیکھ رہے ہیں اور وه کچھ بھی نہیں دیکھتے۔');
INSERT INTO "junagarri" VALUES(7,199,'7:199','آپ درگزر کو اختیار کریں نیک کام کی تعلیم دیں اور جاہلوں سے ایک کناره ہو جائیں۔ ');
INSERT INTO "junagarri" VALUES(7,200,'7:200','اور اگر آپ کو کوئی وسوسہ شیطان کی طرف سے آنے لگے تو اللہ کی پناه مانگ لیا کیجئے بلاشبہ وه خوب سننے والا خوب جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(7,201,'7:201','یقیناً جو لوگ خدا ترس ہیں جب ان کو کوئی خطره شیطان کی طرف سے آجاتا ہے تو وه یاد میں لگ جاتے ہیں، سو یکایک ان کی آنکھیں کھل جاتی ہیں۔ ');
INSERT INTO "junagarri" VALUES(7,202,'7:202','اور جو شیاطین کے تابع ہیں وه ان کو گمراہی میں کھینچے لے جاتے ہیں پس وه باز نہیں آتے۔ ');
INSERT INTO "junagarri" VALUES(7,203,'7:203','اور جب آپ کوئی معجزه ان کے سامنے ظاہر نہیں کرتے تو وه لوگ کہتے ہیں کہ آپ یہ معجزه کیوں نہ لائے؟ آپ فرما دیجئے! کہ میں اس کا اتباع کرتا ہوں جو مجھ پر میرے رب کی طرف سے حکم بھیجا گیا ہے یہ گویا بہت سی دلیلیں ہیں تمہارے رب کی طرف سے اور ہدایت اور رحمت ہے ان لوگوں کے لئے جو ایمان رکھتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(7,204,'7:204','اور جب قرآن پڑھا جایا کرے تو اس کی طرف کان لگا دیا کرو اور خاموش رہا کرو امید ہے کہ تم پر رحمت ہو۔ ');
INSERT INTO "junagarri" VALUES(7,205,'7:205','اور(اے پیغمبر ) اپنے رب کي یاد کیا کراپنے دل میں عاجزی کے ساتھ اور خوف کے ساتھ اور زور کی آواز کی نسبت کم آواز کے ساتھ صبح اور شام اور اہل غفلت میں سے مت ہونا۔');
INSERT INTO "junagarri" VALUES(7,206,'7:206','یقیناً جو تیرے رب کے نزدیک ہیں وه اس کی عبادت سے تکبر نہیں کرتے اور اس کی پاکی بیان کرتے ہیں اور اس کو سجده کرتے ہیں۔');
INSERT INTO "junagarri" VALUES(8,1,'8:1','یہ لوگ آپ سے غنیمتوں کا حکم دریافت کرتے ہیں ، آپ فرما دیجئے! کہ یہ غنیمتیں اللہ کی ہیں اور رسول کی ہیں ، سو تم اللہ سے ڈرو اور اپنے باہمی تعلقات کی اصلاح کرو اور اللہ تعالیٰ اور اس کے رسول کی اطاعت کرو اگر تم ایمان والے ہو۔ ');
INSERT INTO "junagarri" VALUES(8,2,'8:2','بس ایمان والے تو ایسے ہوتے ہیں کہ جب اللہ تعالیٰ کا ذکر آتا ہے تو ان کے قلوب ڈر جاتے ہیں اور جب اللہ کی آیتیں ان کو پڑھ کر سنائی جاتیں ہیں تو وه آیتیں ان کے ایمان کو اور زیاده کردیتی ہیں اور وه لوگ اپنے رب پر توکل کرتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(8,3,'8:3','جو کہ نماز کی پابندی کرتے ہیں اور ہم نے ان کو جو کچھ دیا ہے وه اس میں سے خرچ کرتے ہیں۔');
INSERT INTO "junagarri" VALUES(8,4,'8:4','سچے ایمان والے یہ لوگ ہیں ان کے لیے بڑے درجے ہیں ان کے رب کے پاس اور مغفرت اور عزت کی روزی ہے۔');
INSERT INTO "junagarri" VALUES(8,5,'8:5','جیسا کہ آپ کے رب نے آپ کے گھر سے حق کے ساتھ آپ کو روانہ کیا اور مسلمانوں کی ایک جماعت اس کو گراں سمجھتی تھی۔ ');
INSERT INTO "junagarri" VALUES(8,6,'8:6','وه اس حق کے بارے میں، اس کے بعد کہ اس کا ظہور ہوگیا تھا آپ سے اس طرح جھگڑ رہے تھے کہ گویا کوئی ان کو موت کی طرف ہانکے لیے جاتا ہے اور وه دیکھ رہے ہیں۔ ');
INSERT INTO "junagarri" VALUES(8,7,'8:7','اور تم لوگ اس وقت کو یاد کرو! جب کہ اللہ تم سے ان دو جماعتوں میں سے ایک کا وعده کرتا تھا کہ وه تمہارے ہاتھ آجائے گی اور تم اس تمنا میں تھے کہ غیر مسلح جماعت تمہارے ہاتھ آجائے اور اللہ تعالیٰ کو یہ منظور تھا کہ اپنے احکام سے حق کا حق ہونا ثابت کردے اور ان کافروں کی جڑ کاٹ دے۔');
INSERT INTO "junagarri" VALUES(8,8,'8:8','تاکہ حق کا حق ہونا اور باطل کا باطل ہونا ثابت کردے گو یہ مجرم لوگ ناپسند ہی کریں۔ ');
INSERT INTO "junagarri" VALUES(8,9,'8:9','اس وقت کو یاد کرو جب کہ تم اپنے رب سے فریاد کررہے تھے، پھر اللہ تعالیٰ نے تمہاری سن لی کہ میں تم کو ایک ہزار فرشتوں سے مدد دوں گا جو لگاتار چلے آئیں گے۔ ');
INSERT INTO "junagarri" VALUES(8,10,'8:10','اور اللہ تعالیٰ نے یہ امداد محض اس لیے کی کہ بشارت ہو اور تاکہ تمہارے دلوں کو قرار ہو جائے اور مدد صرف اللہ ہی کی طرف سے ہے جو کہ زبردست حکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(8,11,'8:11','اس وقت کو یاد کرو جب کہ اللہ تم پر اونگھ طاری کر رہا تھا اپنی طرف سے چین دینے کے لیے اور تم پر آسمان سے پانی برسا رہا تھا کہ اس پانی کے ذریعہ سے تم کو پاک کر دے اور تم سے شیطانی وسوسہ کو دفع کر دے اور تمہارے دلوں کو مضبوط کر دے اور تمہارے پاؤں جما دے۔ ');
INSERT INTO "junagarri" VALUES(8,12,'8:12','اس وقت کو یاد کرو جب کہ آپ کا رب فرشتوں کو حکم دیتا تھا کہ میں تمہارا ساتھی ہوں سو تم ایمان والوں کی ہمت بڑھاؤ میں ابھی کفار کے قلوب میں رعب ڈالے دیتا ہوں ، سو تم گردنوں پر مارو اور ان کے پور پور کو مارو۔ ');
INSERT INTO "junagarri" VALUES(8,13,'8:13','یہ اس بات کی سزا ہے کہ انہوں نے اللہ کی اور اس کے رسول کی مخالفت کی۔ اور جو اللہ کی اور اس کے رسول کی مخالفت کرتا ہے سو بےشک اللہ تعالیٰ سخت سزا دینے والا ہے۔');
INSERT INTO "junagarri" VALUES(8,14,'8:14','سو یہ سزا چکھو اور جان رکھو کہ کافروں کے لیے جہنم کا عذاب مقرر ہی ہے۔');
INSERT INTO "junagarri" VALUES(8,15,'8:15','اے ایمان والو! جب تم کافروں سے دو بدو مقابل ہو جاؤ تو ان سے پشت مت پھیرنا۔ ');
INSERT INTO "junagarri" VALUES(8,16,'8:16','اور جو شخص ان سے اس موقع پر پشت پھیرے گا مگر ہاں جو لڑائی کے لیے پینترا بدلتا ہو یا جو (اپنی) جماعت کی طرف پناه لینے آتا ہو وه مستثنیٰ ہے ۔ باقی اور جو ایسا کرے گا وه اللہ کے غضب میں آجائے گا اور اس کا ٹھکانہ دوزخ ہوگا وه بہت ہی بری جگہ ہے۔ ');
INSERT INTO "junagarri" VALUES(8,17,'8:17','سو تم نے انہیں قتل نہیں کیا لیکن اللہ تعالیٰ نے ان کو قتل کیا ۔ اور آپ نے خاک کی مٹھی نہیں پھینکی بلکہ اللہ تعالیٰ نے وه پھینکی اور تاکہ مسلمانوں کو اپنی طرف سے ان کی محنت کا خوب عوض دے بلاشبہ اللہ تعالیٰ خوب سننے والا خوب جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(8,18,'8:18','(ایک بات تو) یہ ہوئی اور (دوسری بات یہ ہے) اللہ تعالیٰ کو کافروں کی تدبیر کو کمزور کرنا تھا۔ ');
INSERT INTO "junagarri" VALUES(8,19,'8:19','اگر تم لوگ فیصلہ چاہتے ہو تو وه فیصلہ تمہارے سامنے آموجود ہوا اور اگر باز آجاؤ تو یہ تمہارے لیے نہایت خوب ہے اور اگر تم پھر وہی کام کرو گے تو ہم بھی پھر وہی کام کریں گے اور تمہاری جمعیت تمہارے ذرا بھی کام نہ آئے گی گو کتنی زیاده ہو اور واقعی بات یہ ہے کہ اللہ تعالیٰ ایمان والوں کے ساتھ ہے۔');
INSERT INTO "junagarri" VALUES(8,20,'8:20','اے ایمان والو! اللہ کا اور اس کے رسول کا کہنا مانو اور اس (کا کہنا ماننے) سے روگردانی مت کرو سنتے جانتے ہوئے۔');
INSERT INTO "junagarri" VALUES(8,21,'8:21','اور تم ان لوگوں کی طرح مت ہونا جو دعویٰ تو کرتے ہیں کہ ہم نے سن لیا حالانکہ وه سنتے (سناتے کچھ) نہیں۔ ');
INSERT INTO "junagarri" VALUES(8,22,'8:22','بےشک بدترین خلائق اللہ تعالیٰ کے نزدیک وه لوگ ہیں جو بہرے ہیں گونگے ہیں جو کہ (ذرا) نہیں سمجھتے۔ ');
INSERT INTO "junagarri" VALUES(8,23,'8:23','اور اگر اللہ تعالیٰ ان میں کوئی خوبی دیکھتا تو ان کو سننے کی توفیق دے دیتا اور اگر ان کو اب سنا دے تو ضرور روگردانی کریں گے بے رخی کرتے ہوئے۔ ');
INSERT INTO "junagarri" VALUES(8,24,'8:24','اے ایمان والو! تم اللہ اور رسول کے کہنے کو بجا لاؤ، جب کہ رسول تم کو تمہاری زندگی بخش چیز کی طرف بلاتے ہوں اور جان رکھو کہ اللہ تعالیٰ آدمی کے اور اس کے قلب کے درمیان آڑ بن جایا کرتا ہے اور بلاشبہ تم سب کو اللہ ہی کے پاس جمع ہونا ہے۔');
INSERT INTO "junagarri" VALUES(8,25,'8:25','اور تم ایسے وبال سے بچو! کہ جو خاص کر صرف ان ہی لوگوں پر واقع نہ ہو گا جو تم میں سے ان گناہوں کے مرتکب ہوئے ہیں اور یہ جان رکھو کہ اللہ سخت سزا دینے والا ہے۔');
INSERT INTO "junagarri" VALUES(8,26,'8:26','اور اس حالت کو یاد کرو! جب کہ تم زمین میں قلیل تھے، کمزور شمار کئے جاتے تھے۔ اس اندیشہ میں رہتے تھے کہ تم کو لوگ نوچ کھسوٹ نہ لیں، سو اللہ نے تم کو رہنے کی جگہ دی اور تم کو اپنی نصرت سے قوت دی اور تم کو نفیس نفیس چیزیں عطا فرمائیں تاکہ تم شکر کرو۔ ');
INSERT INTO "junagarri" VALUES(8,27,'8:27','اے ایمان والو! تم اللہ اور رسول (کے حقوق) میں جانتے ہوئے خیانت مت کرو اور اپنی قابل حفاظت چیزوں میں خیانت مت کرو۔ ');
INSERT INTO "junagarri" VALUES(8,28,'8:28','اور تم اس بات کو جان رکھو کہ تمہارے اموال اور تمہاری اولاد ایک امتحان کی چیز ہے ۔ اور اس بات کو بھی جان رکھو کہ اللہ تعالیٰ کے پاس بڑا بھاری اجر ہے۔');
INSERT INTO "junagarri" VALUES(8,29,'8:29','اے ایمان والو! اگر تم اللہ سے ڈرتے رہو گے تو اللہ تعالیٰ تم کو ایک فیصلہ کی چیز دے گا اور تم سے تمہارے گناه دور کر دے گا اور تم کو بخش دے گا اور اللہ تعالیٰ بڑے فضل والا ہے۔ ');
INSERT INTO "junagarri" VALUES(8,30,'8:30','اور اس واقعہ کا بھی ذکر کیجئے! جب کہ کافر لوگ آپ کی نسبت تدبیر سوچ رہے تھے کہ آپ کو قید کر لیں، یا آپ کو قتل کر ڈالیں یا آپ کو خارج وطن کر دیں اور وه تو اپنی تدبیریں کر رہے تھے اور اللہ اپنی تدبیر کر رہا تھا اور سب سے زیاده مستحکم تدبیر والا اللہ ہے ۔');
INSERT INTO "junagarri" VALUES(8,31,'8:31','اور جب ان کے سامنے ہماری آیتیں پڑھی جاتی ہیں تو کہتے ہیں کہ ہم نے سن لیا، اگر ہم چاہیں تو اس کے برابر ہم بھی کہہ دیں، یہ تو کچھ بھی نہیں صرف بے سند باتیں ہیں جو پہلوں سے منقول چلی آرہی ہیں۔');
INSERT INTO "junagarri" VALUES(8,32,'8:32','اور جب کہ ان لوگوں نے کہا کہ اے اللہ! اگر یہ قرآن آپ کی طرف سے واقعی ہے تو ہم پر آسمان سے پتھر برسا یا ہم پر کوئی دردناک عذاب واقع کر دے۔');
INSERT INTO "junagarri" VALUES(8,33,'8:33','اور اللہ تعالیٰ ایسا نہ کرے گا کہ ان میں آپ کے ہوتے ہوئے ان کو عذاب دے اور اللہ ان کو عذاب نہ دے گا اس حالت میں کہ وه استغفار بھی کرتے ہوں۔ ');
INSERT INTO "junagarri" VALUES(8,34,'8:34','اور ان میں کیا بات ہے کہ ان کو اللہ تعالیٰ سزا نہ دے حالانکہ وه لوگ مسجد حرام سے روکتے ہیں، جب کہ وه لوگ اس مسجد کے متولی نہیں۔ اس کے متولی تو سوا متقیوں کے اور اشخاص نہیں، لیکن ان میں اکثر لوگ علم نہیں رکھتے۔ ');
INSERT INTO "junagarri" VALUES(8,35,'8:35','اور ان کی نماز کعبہ کے پاس صرف یہ تھی سیٹیاں بجانا اور تالیاں بجانا ۔ سو اپنے کفر کے سبب اس عذاب کا مزه چکھو۔');
INSERT INTO "junagarri" VALUES(8,36,'8:36','بلاشک یہ کافر لوگ اپنے مالوں کو اس لئے خرچ کر رہے ہیں کہ اللہ کی راه سے روکیں سو یہ لوگ تو اپنے مالوں کو خرچ کرتے ہی رہیں گے، پھر وه مال ان کے حق میں باعث حسرت ہو جائیں گے۔ پھر مغلوب ہو جائیں گے اور کافر لوگوں کو دوزخ کی طرف جمع کیا جائے گا۔ ');
INSERT INTO "junagarri" VALUES(8,37,'8:37','تاکہ اللہ تعالیٰ ناپاک کو پاک سے الگ کر دے اور ناپاکوں کو ایک دوسرے سے ملا دے، پس ان سب کو اکھٹا ڈھیر کر دے پھر ان سب کو جہنم میں ڈال دے۔ ایسے لوگ پورے خسارے میں ہیں۔');
INSERT INTO "junagarri" VALUES(8,38,'8:38','آپ ان کافروں سے کہہ دیجئے! کہ اگر یہ لوگ باز آجائیں تو ان کے سارے گناہ جو پہلے ہو چکے ہیں سب معاف کر دیئے جائیں گے اور اگر اپنی وہی عادت رکھیں گے تو (کفار) سابقین کے حق میں قانون نافذ ہو چکا ہے۔ ');
INSERT INTO "junagarri" VALUES(8,39,'8:39','اور تم ان سے اس حد تک لڑو کہ ان میں فساد عقیده نہ رہے ۔ اور دین اللہ ہی کا ہو جائے پھر اگر یہ باز آجائیں تو اللہ تعالیٰ ان اعمال کو خوب دیکھتا ہے۔ ');
INSERT INTO "junagarri" VALUES(8,40,'8:40','اور اگر روگردانی کریں تو یقین رکھیں کہ اللہ تعالیٰ تمہارا کار ساز ہے وه بہت اچھا کار ساز ہے اور بہت اچھا مدد گار ہے۔ ');
INSERT INTO "junagarri" VALUES(8,41,'8:41','جان لو کہ تم جس قسم کی جو کچھ غنیمت حاصل کرو اس میں سے پانچواں حصہ تو اللہ کا ہے اور رسول کا اور قرابت داروں کا اور یتیموں اور مسکینوں کا اور مسافروں کا ، اگر تم اللہ پر ایمان لائے ہو اور اس چیز پر جو ہم نے اپنے بندے پر اس دن اتارا ہے ، جو دن حق وباطل کی جدائی کا تھا جس دن دو فوجیں بھڑ گئی تھیں ۔ اللہ ہر چیز پر قادر ہے۔');
INSERT INTO "junagarri" VALUES(8,42,'8:42','جب کہ تم پاس والے کنارے پر تھے اور وه دور والے کنارے پر تھے اور قافلہ تم سے نیچے تھا ۔ اگر تم آپس میں وعدے کرتے تو یقیناً تم وقت معین پر پہنچنے میں مختلف ہو جاتے ۔ لیکن اللہ کو تو ایک کام کر ہی ڈالنا تھا جو مقرر ہو چکا تھا تاکہ جو ہلاک ہو، دلیل پر (یعنی یقین جان کر) ہلاک ہو اور جو زنده رہے، وه بھی دلیل پر (حق پہچان کر) زنده رہے ۔ بیشک اللہ بہت سننے والا خوب جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(8,43,'8:43','جب کہ اللہ تعالیٰ نے تجھے تیرے خواب میں ان کی تعداد کم دکھائی، اگر ان کی زیادتی دکھاتا تو تم بزدل ہو جاتے اور اس کام کے بارے میں آپس میں اختلاف کرتے لیکن اللہ تعالیٰ نے بچا لیا، وه دلوں کے بھیدوں سے خوب آگاه ہے۔ ');
INSERT INTO "junagarri" VALUES(8,44,'8:44','جبکہ اس نے بوقت ملاقات انہیں تمہاری نگاہوں میں بہت کم دکھائے اور تمہیں ان کی نگاہوں میں کم دکھائے تاکہ اللہ تعالیٰ اس کام کو انجام تک پہنچا دے جو کرنا ہی تھا اور سب کام اللہ ہی کی طرف پھیرے جاتے ہیں۔');
INSERT INTO "junagarri" VALUES(8,45,'8:45','اے ایمان والو! جب تم کسی مخالف فوج سے بھڑ جاؤ تو ثابت قدم رہو اور بکثرت اللہ کو یاد کرو تاکہ تمہیں کامیابی حاصل ہو۔ ');
INSERT INTO "junagarri" VALUES(8,46,'8:46','اور اللہ کی اور اس کے رسول کی فرماں برداری کرتے رہو، آپس میں اختلاف نہ کرو ورنہ بزدل ہو جاؤ گے اور تمہاری ہوا اکھڑ جائے گی اور صبر و سہارا رکھو، یقیناً اللہ تعالیٰ صبر کرنے والوں کے ساتھ ہے۔ ');
INSERT INTO "junagarri" VALUES(8,47,'8:47','ان لوگوں جیسے نہ بنو جو اتراتے ہوئے اور لوگوں میں خود نمائی کرتے ہوئے اپنے گھروں سے چلے اور اللہ کی راه سے روکتے تھے ، جو کچھ وه کر رہے ہیں اللہ اسے گھیر لینے والا ہے۔');
INSERT INTO "junagarri" VALUES(8,48,'8:48','جبکہ ان کے اعمال کو شیطان انہیں زینت دار دکھا رہا تھا اور کہہ رہا تھا کہ لوگوں میں سے کوئی بھی آج تم پر غالب نہیں آسکتا، میں خود بھی تمہارا حمایتی ہوں لیکن جب دونوں جماعتیں نمودار ہوئیں تو اپنی ایڑیوں کے بل پیچھے ہٹ گیا اور کہنے لگا میں تم سے بری ہوں۔ میں وه دیکھ رہا ہوں جو تم نہیں دیکھ رہے ۔ میں اللہ سے ڈرتا ہوں ، اور اللہ تعالیٰ سخت عذاب والا ہے۔ ');
INSERT INTO "junagarri" VALUES(8,49,'8:49','جبکہ منافق کہہ رہے تھے اور وه بھی جن کے دلوں میں روگ تھا کہ انہیں تو ان کے دین نے دھوکے میں ڈال دیا ہے جو بھی اللہ پر بھروسہ کرے اللہ تعالیٰ بلا شک وشبہ غلبے والا اور حکمت والا ہے۔ ');
INSERT INTO "junagarri" VALUES(8,50,'8:50','کاش کہ تو دیکھتا جب کہ فرشتے کافروں کی روح قبض کرتے ہیں ان کے منھ پر اور سرینوں پر مار مارتے ہیں (اور کہتے ہیں) تم جلنے کا عذاب چکھو۔ ');
INSERT INTO "junagarri" VALUES(8,51,'8:51','یہ بسبب ان کاموں کے جو تمہارے ہاتھوں نے پہلے ہی بھیج رکھا ہے بیشک اللہ اپنے بندوں پر ظلم کرنے والا نہیں۔ ');
INSERT INTO "junagarri" VALUES(8,52,'8:52','مثل فرعونیوں کے حال کے اور ان سے اگلوں کے ، کہ انہوں نے اللہ کی آیتوں سے کفر کیا پس اللہ نے ان کے گناہوں کے باعث انہیں پکڑ لیا۔ اللہ تعالیٰ یقیناً قوت والا اور سخت عذاب والا ہے۔');
INSERT INTO "junagarri" VALUES(8,53,'8:53','یہ اس لئے کہ اللہ تعالیٰ ایسا نہیں کہ کسی قوم پر کوئی نعمت انعام فرما کر پھر بدل دے جب تک کہ وه خود اپنی اس حالت کو نہ بدل دیں جو کہ ان کی اپنی تھی اور یہ کہ اللہ سننے والا جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(8,54,'8:54','مثل حالت فرعونیوں کے اور ان سے پہلے کے لوگوں کے کہ انہوں نے اپنے رب کی باتیں جھٹلائیں۔ پس ان کے گناہوں کے باعث ہم نے انہیں برباد کیا اور فرعونیوں کو ڈبو دیا۔ یہ سارے ظالم تھے۔ ');
INSERT INTO "junagarri" VALUES(8,55,'8:55','تمام جانداروں سے بدتر، اللہ کے نزدیک وه ہیں جو کفر کریں، پھر وه ایمان نہ لائیں۔ ');
INSERT INTO "junagarri" VALUES(8,56,'8:56','جن سے آپ نے عہد وپیمان کر لیا پھر بھی وه اپنے عہد وپیمان کو ہر مرتبہ توڑ دیتے ہیں اور بالکل پرہیز نہیں کرتے۔ ');
INSERT INTO "junagarri" VALUES(8,57,'8:57','پس جب کبھی تو لڑائی میں ان پر غالب آجائے انہیں ایسی مار مار کہ ان کے پچھلے بھی بھاگ کھڑے ہوں ، ہو سکتا ہے کہ وه عبرت حاصل کریں۔');
INSERT INTO "junagarri" VALUES(8,58,'8:58','اور اگر تجھے کسی قوم کی خیانت کا ڈر ہو تو برابری کی حالت میں ان کا عہدنامہ توڑ دے ، اللہ تعالیٰ خیانت کرنے والوں کو پسند نہیں فرماتا۔ ');
INSERT INTO "junagarri" VALUES(8,59,'8:59','کافر یہ خیال نہ کریں کہ وه بھاگ نکلے۔ یقیناً وه عاجز نہیں کر سکتے۔');
INSERT INTO "junagarri" VALUES(8,60,'8:60','تم ان کے مقابلے کے لئے اپنی طاقت بھر قوت کی تیاری کرو اور گھوڑوں کے تیار رکھنے کی کہ اس سے تم اللہ کے دشمنوں کو خوف زده رکھ سکو اور ان کے سوا اوروں کو بھی، جنہیں تم نہیں جانتے، اللہ انہیں خوب جان رہا ہے جو کچھ بھی اللہ کی راه میں صرف کرو گے وه تمہیں پورا پورا دیا جائے گا اور تمہارا حق نہ مارا جائے گا۔');
INSERT INTO "junagarri" VALUES(8,61,'8:61','اگر وه صلح کی طرف جھکیں تو تو بھی صلح کی طرف جھک جا اور اللہ پر بھروسہ رکھ ، یقیناً وه بہت سننے جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(8,62,'8:62','اگر وه تجھ سے دغا بازی کرنا چاہیں گے تو اللہ تجھے کافی ہے، اسی نے اپنی مدد سے اور مومنوں سے تیری تائید کی ہے۔');
INSERT INTO "junagarri" VALUES(8,63,'8:63','ان کے دلوں میں باہمی الفت بھی اسی نے ڈالی ہے۔ زمین میں جو کچھ ہے تو اگر سارا کا سارا بھی خرچ کر ڈالتا ہے تو بھی ان کے دل آپس میں نہ ملا سکتا۔ یہ تو اللہ ہی نے ان میں الفت ڈال دی ہے وه غالب حکمتوں والا ہے۔');
INSERT INTO "junagarri" VALUES(8,64,'8:64','اے نبی! تجھے اللہ کافی ہے اور ان مومنوں کو جو تیری پیروی کر رہے ہیں۔');
INSERT INTO "junagarri" VALUES(8,65,'8:65','اے نبی! ایمان والوں کو جہاد کا شوق دلاؤ اگر تم میں بیس بھی صبر کرنے والے ہوں گے، تو دو سو پر غالب رہیں گے۔ اور اگر تم میں ایک سو ہوں گے تو ایک ہزار کافروں پر غالب رہیں گے اس واسطے کہ وه بے سمجھ لوگ ہیں۔');
INSERT INTO "junagarri" VALUES(8,66,'8:66','اچھا اب اللہ تمہارا بوجھ ہلکا کرتاہے، وه خوب جانتا ہے کہ تم میں ناتوانی ہے، پس اگر تم میں سے ایک سو صبر کرنے والے ہوں گے تو وه دو سو پر غالب رہیں گے اور اگر تم میں سے ایک ہزار ہوں گے تو وه اللہ کے حکم سے دو ہزار پرغالب رہیں گے ، اللہ صبر کرنے والوں کے ساتھ ہے۔ ');
INSERT INTO "junagarri" VALUES(8,67,'8:67','نبی کے ہاتھ میں قیدی نہیں چاہییں جب تک کہ ملک میں اچھی خونریزی کی جنگ نہ ہو جائے۔ تم تو دنیا کے مال چاہتے ہو اور اللہ کا اراده آخرت کا ہے اور اللہ زور آور باحکمت ہے۔');
INSERT INTO "junagarri" VALUES(8,68,'8:68','اگر پہلے ہی سے اللہ کی طرف سے بات لکھی ہوئی نہ ہوتی تو جو کچھ تم نے لیا ہے اس کے بارے میں تمہیں کوئی بڑی سزا ہوتی۔');
INSERT INTO "junagarri" VALUES(8,69,'8:69','پس جو کچھ حلال اور پاکیزه غنیمت تم نے حاصل کی ہے، خوب کھاؤ پیو اور اللہ سے ڈرتے رہو، یقیناً اللہ غفور ورحیم ہے۔');
INSERT INTO "junagarri" VALUES(8,70,'8:70','اے نبی! اپنے ہاتھ تلے کے قیدیوں سے کہہ دو کہ اگر اللہ تعالیٰ تمہارے دلوں میں نیک نیتی دیکھے گا تو جو کچھ تم سے لیا گیا ہے اس سے بہتر تمہیں دے گا اور پھر گناه بھی معاف فرمائے گا اور اللہ بخشنے والا مہربان ہے ہی۔');
INSERT INTO "junagarri" VALUES(8,71,'8:71','اور اگر وه تجھ سے خیانت کا خیال کریں گے تو یہ تو اس سے پہلے خود اللہ کی خیانت کر چکے ہیں آخر اس نے انہیں گرفتار کرا دیا ، اور اللہ علم وحکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(8,72,'8:72','جو لوگ ایمان لائے اور ہجرت کی اور اپنے مالوں اور جانوں سے اللہ کی راه میں جہاد کیا اور جن لوگوں نے ان کو پناه دی اور مدد کی ، یہ سب آپس میں ایک دوسرے کے رفیق ہیں ، اور جو ایمان تو لائے ہیں لیکن ہجرت نہیں کی تمہارے لئے ان کی کچھ بھی رفاقت نہیں جب تک کہ وه ہجرت نہ کریں ۔ ہاں اگر وه تم سے دین کے بارے میں مدد طلب کریں تو تم پر مدد کرنا ضروری ہے ، سوائے ان لوگوں کے کہ تم میں اور ان میں عہد وپیمان ہے ، تم جو کچھ کر رہے ہو اللہ خوب دیکھتا ہے۔');
INSERT INTO "junagarri" VALUES(8,73,'8:73','کافر آپس میں ایک دوسرے کے رفیق ہیں، اگر تم نے ایسا نہ کیا تو ملک میں فتنہ ہوگا اور زبردست فساد ہو جائے گا۔ ');
INSERT INTO "junagarri" VALUES(8,74,'8:74','جو لوگ ایمان لائے اور ہجرت کی اور اللہ کی راه میں جہاد کیا اور جنہوں نے پناه دی اور مدد پہنچائی، یہی لوگ سچے مومن ہیں، ان کے لئے بخشش ہے اور عزت کی روزی۔ ');
INSERT INTO "junagarri" VALUES(8,75,'8:75','اور جو لوگ اس کے بعد ایمان لائے اور ہجرت کی اور تمہارے ساتھ ہو کر جہاد کیا۔ پس یہ لوگ بھی تم میں سے ہی ہیں اور رشتے ناتے والے ان میں سے بعض بعض سے زیاده نزدیک ہیں اللہ کے حکم میں ، بیشک اللہ تعالیٰ ہر چیز کا جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(9,1,'9:1','اللہ اوراس کے رسول کی جانب سے بیزاری کا اعلان ہے ۔ ان مشرکوں کے بارے میں جن سے تم نے عہد وپیمان کیا تھا۔');
INSERT INTO "junagarri" VALUES(9,2,'9:2','پس (اے مشرکو!) تم ملک میں چار مہینے تک تو چل پھر لو ، جان لو کہ تم اللہ کو عاجز کرنے والے نہیں ہو، اور یہ (بھی یاد رہے) کہ اللہ کافروں کو رسوا کرنے والا ہے۔ ');
INSERT INTO "junagarri" VALUES(9,3,'9:3','اللہ اور اس کے رسول کی طرف سے لوگوں کو بڑے حج کے دن صاف اطلاع ہے کہ اللہ مشرکوں سے بیزار ہے، اور اس کا رسول بھی، اگر اب بھی تم توبہ کر لو تو تمہارے حق میں بہتر ہے، اور اگر تم روگردانی کرو تو جان لو کہ تم اللہ کو ہرا نہیں سکتے۔ اور کافروں کو دکھ اور مار کی خبر پہنچا دیجئے۔');
INSERT INTO "junagarri" VALUES(9,4,'9:4','بجز ان مشرکوں کے جن سے تمہارا معاہده ہو چکا ہے اور انہوں نے تمہیں ذرا سا بھی نقصان نہیں پہنچایا نہ کسی کی تمہارے خلاف مدد کی ہے تو تم بھی ان کے معاہدے کی مدت ان کے ساتھ پوری کرو ، اللہ تعالیٰ پرہیزگاروں کو دوست رکھتا ہے۔');
INSERT INTO "junagarri" VALUES(9,5,'9:5','پھر حرمت والے مہینوں کے گزرتے ہی مشرکوں کو جہاں پاؤ قتل کرو انہیں گرفتار کرو ، ان کا محاصره کرلو اور ان کی تاک میں ہر گھاٹی میں جا بیٹھو ، ہاں اگر وه توبہ کرلیں اور نماز کے پابند ہو جائیں اور زکوٰة ادا کرنے لگیں توتم ان کی راہیں چھوڑ دو ۔ یقیناً اللہ تعالیٰ بخشنے والا مہربان ہے۔');
INSERT INTO "junagarri" VALUES(9,6,'9:6','اگر مشرکوں میں سے کوئی تجھ سے پناه طلب کرے تو تو اسے پناه دے دے یہاں تک کہ وه کلام اللہ سن لے پھر اسے اپنی جائے امن تک پہنچا دے ۔ یہ اس لئے کہ یہ لوگ بے علم ہیں۔ ');
INSERT INTO "junagarri" VALUES(9,7,'9:7','مشرکوں کے لئے عہد اللہ اور اس کے رسول کے نزدیک کیسے ره سکتا ہے سوائے ان کے جن سے تم نے عہد وپیمان مسجد حرام کے پاس کیا ہے ، جب تک وه لوگ تم سے معاہده نبھائیں تم بھی ان سے وفاداری کرو، اللہ تعالیٰ متقیوں سے محبت رکھتا ہے۔ ');
INSERT INTO "junagarri" VALUES(9,8,'9:8','ان کے وعدوں کا کیا اعتبار ان کا اگر تم پر غلبہ ہو جائے تو نہ یہ قرابت داری کا خیال کریں نہ عہدوپیمان کا ، اپنی زبانوں سے تو تمہیں پرچا رہے ہیں لیکن ان کے دل نہیں مانتے ان میں سے اکثر تو فاسق ہیں۔');
INSERT INTO "junagarri" VALUES(9,9,'9:9','انہوں نے اللہ کی آیتوں کو بہت کم قیمت پر بیچ دیا اور اس کی راه سے روکا۔ بہت برا ہے جو یہ کر رہے ہیں۔');
INSERT INTO "junagarri" VALUES(9,10,'9:10','یہ تو کسی مسلمان کے حق میں کسی رشتہداری کا یا عہد کا مطلق لحاظ نہیں کرتے، یہ ہیں ہی حد سے گزرنے والے۔ ');
INSERT INTO "junagarri" VALUES(9,11,'9:11','اب بھی اگر یہ توبہ کر لیں اور نماز کے پابند ہو جائیں اور زکوٰة دیتے رہیں تو تمہارے دینی بھائی ہیں ۔ ہم تو جاننے والوں کے لئے اپنی آیتیں کھول کھول کر بیان کر رہے ہیں۔');
INSERT INTO "junagarri" VALUES(9,12,'9:12','اگر یہ لوگ عہدوپیمان کے بعد بھی اپنی قسموں کو توڑ دیں اور تمہارے دین میں طعنہ زنی کریں تو تم بھی ان سرداران کفر سے بھڑ جاؤ۔ ان کی قسمیں کوئی چیز نہیں، ممکن ہے کہ اس طرح وه بھی باز آجائیں۔');
INSERT INTO "junagarri" VALUES(9,13,'9:13','تم ان لوگوں کی سرکوبی کے لئے کیوں تیار نہیں ہوتے جنہوں نے اپنی قسموں کو توڑ دیا اور پیغمبر کو جلا وطن کرنے کی فکر میں ہیں اور خود ہی اول بار انہوں نے تم سے چھیڑ کی ہے ، کیا تم ان سے ڈرتے ہو؟ اللہ ہی زیاده مستحق ہے کہ تم اس کا ڈر رکھو بشرطیکہ تم ایمان والے ہو۔');
INSERT INTO "junagarri" VALUES(9,14,'9:14','ان سے تم جنگ کرو اللہ تعالیٰ انہیں تمہارے ہاتھوں عذاب دے گا، انہیں ذلیل ورسوا کرے گا، تمہیں ان پر مدد دے گا اور مسلمانوں کے کلیجے ٹھنڈے کرے گا۔');
INSERT INTO "junagarri" VALUES(9,15,'9:15','اور ان کے دل کا غم وغصہ دور کرے گا ، اور وه جس کی طرف چاہتا ہے رحمت سے توجہ فرماتا ہے۔ اللہ جانتا بوجھتا حکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(9,16,'9:16','کیا تم یہ سمجھے بیٹھے ہو کہ تم چھوڑ دیئے جاؤ گے حالانکہ اب تک اللہ نے تم میں سے انہیں ممتاز نہیں کیا جو مجاہد ہیں اور جنہوں نے اللہ کے اور اس کے رسول کے اور مومنوں کے سوا کسی کو دلی دوست نہیں بنایا ۔ اللہ خوب خبردار ہے جو تم کر رہے ہو۔');
INSERT INTO "junagarri" VALUES(9,17,'9:17','لائق نہیں کہ مشرک اللہ تعالیٰ کی مسجدوں کو آباد کریں۔ درآں حالیکہ وه خود اپنے کفر کے آپ ہی گواه ہیں ، ان کے اعمال غارت واکارت ہیں، اور وه دائمی طور پر جہنمی ہیں۔ ');
INSERT INTO "junagarri" VALUES(9,18,'9:18','اللہ کی مسجدوں کی رونق وآبادی تو ان کے حصے میں ہے جو اللہ پر اور قیامت کے دن پر ایمان رکھتے ہوں، نمازوں کے پابند ہوں، زکوٰة دیتے ہوں، اللہ کے سوا کسی سے نہ ڈرتے ہوں، توقع ہے کہ یہی لوگ یقیناً ہدایت یافتہ ہیں۔ ');
INSERT INTO "junagarri" VALUES(9,19,'9:19','کیا تم نے حاجیوں کو پانی پلا دینا اور مسجد حرام کی خدمت کرنا اس کے برابر کر دیا ہے جو اللہ پر اور آخرت کے دن پر ایمان لائے اور اللہ کی راه میں جہاد کیا، یہ اللہ کے نزدیک برابر کے نہیں اور اللہ تعالیٰ ظالموں کو ہدایت نہیں دیتا۔ ');
INSERT INTO "junagarri" VALUES(9,20,'9:20','جو لوگ ایمان لائے، ہجرت کی، اللہ کی راه میں اپنے مال اور اپنی جان سے جہاد کیا وه اللہ کے ہاں بہت بڑے مرتبہ والے ہیں، اور یہی لوگ مراد پانے والے ہیں۔');
INSERT INTO "junagarri" VALUES(9,21,'9:21','انہیں ان کا رب خوشخبری دیتا ہے اپنی رحمت کی اور رضامندی کی اور جنتوں کی، ان کے لئے وہاں دوامی نعمت ہے۔');
INSERT INTO "junagarri" VALUES(9,22,'9:22','وہاں یہ ہمیشہ رہنے والے ہیں اللہ کے پاس یقیناً بہت بڑے ثواب ہیں۔ ');
INSERT INTO "junagarri" VALUES(9,23,'9:23','اے ایمان والو! اپنے باپوں کو اور اپنے بھائیوں کو دوست نہ بناؤ اگر وه کفر کو ایمان سے زیاده عزیز رکھیں۔ تم میں سے جو بھی ان سے محبت رکھے گا وه پورا گنہگار ظالم ہے۔ ');
INSERT INTO "junagarri" VALUES(9,24,'9:24','آپ کہہ دیجئے کہ اگر تمہارے باپ اور تمہارے لڑکےاور تمہارے بھائی اور تمہاری بیویاں اور تمہارے کنبے قبیلے اور تمہارے کمائے ہوئے مال اور وه تجارت جس کی کمی سے تم ڈرتے ہو اور وه حویلیاں جنہیں تم پسند کرتے ہو اگر یہ تمہیں اللہ سے اور اس کے رسول سے اور اس کی راه میں جہاد سے بھی زیاده عزیز ہیں، تو تم انتظار کرو کہ اللہ تعالیٰ اپنا عذاب لے آئے۔ اللہ تعالیٰ فاسقوں کو ہدایت نہیں دیتا۔ ');
INSERT INTO "junagarri" VALUES(9,25,'9:25','یقیناً اللہ تعالیٰ نے بہت سے میدانوں میں تمہیں فتح دی ہے اور حنین کی لڑائی والے دن بھی جب کہ تمہیں اپنی کثرت پر ناز ہوگیا تھا، لیکن اس نے تمہیں کوئی فائده نہ دیا بلکہ زمین باوجود اپنی کشادگی کے تم پر تنگ ہوگئی پھر تم پیٹھ پھیر کر مڑ گئے۔');
INSERT INTO "junagarri" VALUES(9,26,'9:26','پھر اللہ نے اپنی طرف کی تسکین اپنے نبی پر اور مومنوں پر اتاری اور اپنے وه لشکر بھیجے جنہیں تم دیکھ نہیں رہے تھے اور کافروں کو پوری سزا دی۔ ان کفار کا یہی بدلہ تھا۔');
INSERT INTO "junagarri" VALUES(9,27,'9:27','پھر اس کے بعد بھی جس پر چاہے اللہ تعالیٰ اپنی رحمت کی توجہ فرمائے گا اللہ ہی بخشش ومہربانی کرنے والا ہے۔');
INSERT INTO "junagarri" VALUES(9,28,'9:28','اے ایمان والو! بے شک مشرک بالکل ہی ناپاک ہیں وه اس سال کے بعد مسجد حرام کے پاس بھی نہ پھٹکنے پائیں اگر تمہیں مفلسی کا خوف ہے تو اللہ تمہیں دولت مند کر دے گا اپنے فضل سے اگر چاہے اللہ علم وحکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(9,29,'9:29','ان لوگوں سے لڑو، جو اللہ پر اور قیامت کے دن پر ایمان نہیں لاتے جو اللہ اور اس کے رسول کی حرام کرده شے کو حرام نہیں جانتے، نہ دین حق کو قبول کرتے ہیں ان لوگوں میں جنہیں کتاب دی گئی ہے، یہاں تک کہ وه ذلیل وخوار ہو کر اپنے ہاتھ سے جزیہ ادا کریں۔ ');
INSERT INTO "junagarri" VALUES(9,30,'9:30','یہود کہتے ہیں عزیر اللہ کا بیٹا ہے اور نصرانی کہتے ہیں مسیح اللہ کا بیٹا ہے یہ قول صرف ان کے منھ کی بات ہے۔ اگلے منکروں کی بات کی یہ بھی نقل کرنے لگے اللہ انہیں غارت کرے وه کیسے پلٹائے جاتے ہیں۔');
INSERT INTO "junagarri" VALUES(9,31,'9:31','ان لوگوں نے اللہ کو چھوڑ کر اپنے عالموں اور درویشوں کو رب بنایا ہے اور مریم کے بیٹے مسیح کو حالانکہ انہیں صرف ایک اکیلے اللہ ہی کی عبادت کا حکم دیا گیا تھا جس کے سوا کوئی معبود نہیں وه پاک ہے ان کے شریک مقرر کرنے سے۔');
INSERT INTO "junagarri" VALUES(9,32,'9:32','وه چاہتے ہیں کہ اللہ کے نور کو اپنے منھ سے بجھا دیں اور اللہ تعالیٰ انکاری ہے مگر اسی بات کا کہ اپنا نور پورا کرے گو کافر نا خوش رہیں۔ ');
INSERT INTO "junagarri" VALUES(9,33,'9:33','اسی نے اپنے رسول کو ہدایت اور سچے دین کے ساتھ بھیجا ہے کہ اسے اور تمام مذہبوں پر غالب کر دے اگر چہ مشرک برا مانیں');
INSERT INTO "junagarri" VALUES(9,34,'9:34','اے ایمان والو! اکثر علما اور عابد، لوگوں کا مال ناحق کھا جاتے ہیں اور اللہ کی راه سے روک دیتے ہیں اور جو لوگ سونے چاندی کا خزانہ رکھتے ہیں اور اللہ کی راه میں خرچ نہیں کرتے، انہیں دردناک عذاب کی خبر پہنچا دیجئے۔ ');
INSERT INTO "junagarri" VALUES(9,35,'9:35','جس دن اس خزانے کو آتش دوزخ میں تپایا جائے گا پھر اس سے ان کی پیشانیاں اور پہلو اور پیٹھیں داغی جائیں گی (ان سے کہا جائے گا) یہ ہے جسے تم نے اپنے لئے خزانہ بنا کر رکھا تھا۔ پس اپنے خزانوں کا مزه چکھو۔');
INSERT INTO "junagarri" VALUES(9,36,'9:36','مہینوں کی گنتی اللہ کے نزدیک کتاب اللہ میں باره کی ہے، اسی دن سے جب سے آسمان وزمین کو اس نے پیدا کیا ہے اس میں سے چار حرمت وادب کے ہیں ۔ یہی درست دین ہے ، تم ان مہینوں میں اپنی جانوں پر ظلم نہ کرو اور تم تمام مشرکوں سے جہاد کرو جیسے کہ وه تم سب سے لڑتے ہیں اور جان رکھو کہ اللہ تعالی متقیوں کے ساتھ ہے۔');
INSERT INTO "junagarri" VALUES(9,37,'9:37','مہینوں کا آگے پیچھے کر دینا کفر کی زیادتی ہے اس سے وه لوگ گمراہی میں ڈالے جاتے ہیں جو کافر ہیں۔ ایک سال تو اسے حلال کر لیتے ہیں اور ایک سال اسی کو حرمت والا کر لیتے ہیں، کہ اللہ نے جو حرمت رکھی ہے اس کے شمار میں تو موافقت کر لیں پھر اسے حلال بنا لیں جسے اللہ نے حرام کیا ہے انہیں ان کے برے کام بھلے دکھادیئے گئے ہیں اور قوم کفار کی اللہ رہنمائی نہیں فرماتا۔');
INSERT INTO "junagarri" VALUES(9,38,'9:38','اے ایمان والو! تمہیں کیا ہو گیا ہے کہ جب تم سے کہا جاتا ہے کہ چلو اللہ کے راستے میں کوچ کرو تو تم زمین سے لگے جاتے ہو۔ کیا تم آخرت کے عوض دنیا کی زندگانی پر ہی ریجھ گئے ہو۔ سنو! دنیا کی زندگی تو آخرت کے مقابلے میں کچھ یونہی سی ہے۔');
INSERT INTO "junagarri" VALUES(9,39,'9:39','اگر تم نے کوچ نہ کیا تو تمہیں اللہ تعالیٰ دردناک سزا دے گا اور تمہارے سوا اور لوگوں کو بدل لائے گا، تم اللہ تعالی کو کوئی نقصان نہیں پہنچا سکتے اور اللہ ہر چیز پر قادر ہے۔');
INSERT INTO "junagarri" VALUES(9,40,'9:40','اگر تم ان (نبی صلی اللہ علیہ وسلم) کی مدد نہ کرو تو اللہ ہی نے ان کی مدد کی اس وقت جبکہ انہیں کافروں نے (دیس سے) نکال دیا تھا، دو میں سے دوسرا جبکہ وه دونوں غار میں تھے جب یہ اپنے ساتھی سے کہہ رہے تھے کہ غم نہ کر اللہ ہمارے ساتھ ہے ، پس جناب باری نے اپنی طرف سے تسکین اس پر نازل فرما کر ان لشکروں سے اس کی مدد کی جنہیں تم نے دیکھا ہی نہیں ، اس نے کافروں کی بات پست کر دی اور بلند وعزیز تو اللہ کا کلمہ ہی ہے، اللہ غالب ہے حکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(9,41,'9:41','نکل کھڑے ہو جاؤ ہلکے پھلکے ہو تو بھی اور بھاری بھر کم ہو تو بھی، اور راه رب میں اپنی مال وجان سے جہاد کرو، یہی تمہارے لئے بہتر ہے اگر تم میں علم ہو۔');
INSERT INTO "junagarri" VALUES(9,42,'9:42','اگر جلد وصول ہونے والا مال واسباب ہوتا اور ہلکا سفر ہوتا تو یہ ضرور آپ کے پیچھے ہو لیتے لیکن ان پر تو دوری اور دراز کی مشکل پڑ گئی۔ اب تو یہ اللہ کی قسمیں کھائیں گے کہ اگر ہم میں قوت وطاقت ہوتی تو ہم یقیناً آپ کے ساتھ نکلتے، یہ اپنی جانوں کو خود ہی ہلاکت میں ڈال رہے ہیں ان کے جھوٹا ہونے کا سچا علم اللہ کو ہے۔');
INSERT INTO "junagarri" VALUES(9,43,'9:43','اللہ تجھے معاف فرما دے، تو نے انہیں کیوں اجازت دے دی؟ بغیر اس کے کہ تیرے سامنے سچے لوگ کھل جائیں اور تو جھوٹے لوگوں کو بھی جان لے۔ ');
INSERT INTO "junagarri" VALUES(9,44,'9:44','اللہ پر اور قیامت کے دن پر ایمان ویقین رکھنے والے تو مالی اور جانی جہاد سے رک رہنے کی کبھی بھی تجھ سے اجازت طلب نہیں کریں گے ، اور اللہ تعالیٰ پرہیزگاروں کو خوب جانتا ہے۔');
INSERT INTO "junagarri" VALUES(9,45,'9:45','یہ اجازت تو تجھ سے وہی طلب کرتے ہیں جنہیں نہ اللہ پر ایمان ہے نہ آخرت کے دن کا یقین ہے جن کے دل شک میں پڑے ہوئے ہیں اور وه اپنے شک میں ہی سرگرداں ہیں۔ ');
INSERT INTO "junagarri" VALUES(9,46,'9:46','اگر ان کا اراده جہاد کے لئے نکلنے کا ہوتا تو وه اس سفر کے لئے سامان کی تیاری کر رکھتے لیکن اللہ کو ان کا اٹھنا پسند ہی نہ تھا اس لئے انہیں حرکت سے ہی روک دیا اور کہہ دیا گیا کہ تم بیٹھنے والوں کے ساتھ بیٹھے ہی رہو۔ ');
INSERT INTO "junagarri" VALUES(9,47,'9:47','اگر یہ تم میں مل کر نکلتے بھی تو تمہارے لئے سوائے فساد کے اور کوئی چیز نہ بڑھاتے بلکہ تمہارے درمیان خوب گھوڑے دوڑا دیتے اور تم میں فتنے ڈالنے کی تلاش میں رہتے ان کے ماننے والے خود تم میں موجود ہیں ، اور اللہ ان ظالموں کو خوب جانتا ہے۔');
INSERT INTO "junagarri" VALUES(9,48,'9:48','یہ تو اس سے پہلے بھی فتنے کی تلاش کرتے رہے ہیں اور تیرے لئے کاموں کو الٹ پلٹ کرتے رہے ہیں، یہاں تک کہ حق آپہنچا اور اللہ کا حکم غالب آگیا باوجودیکہ وه ناخوشی میں ہی رہے۔ ');
INSERT INTO "junagarri" VALUES(9,49,'9:49','ان میں سے کوئی تو کہتا ہے مجھے اجازت دیجئے مجھے فتنے میں نہ ڈالیئے، آگاه رہو وه تو فتنے میں پڑ چکے ہیں اور یقیناً دوزخ کافروں کو گھیر لینے والی ہے۔ ');
INSERT INTO "junagarri" VALUES(9,50,'9:50','آپ کو اگر کوئی بھلائی مل جائے تو انہیں برا لگتا ہے اور کوئی برائی پہنچ جائے تو یہ کہتے ہیں ہم نے تو اپنا معاملہ پہلے سے ہی درست کر لیا تھا، پھر تو بڑے ہی اتراتے ہوئے لوٹتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(9,51,'9:51','آپ کہہ دیجئے کہ ہمیں سوائے اللہ کے ہمارے حق میں لکھے ہوئے کہ کوئی چیز پہنچ ہی نہیں سکتی وه ہمارا کار ساز اور مولیٰ ہے۔ مومنوں کو تو اللہ کی ذات پاک پر ہی بھروسہ کرنا چاہئے۔ ');
INSERT INTO "junagarri" VALUES(9,52,'9:52','کہہ دیجئے کہ تم ہمارے بارے میں جس چیز کا انتظار کر رہے ہو وه دو بھلائیوں میں سے ایک ہے اور ہم تمہارے حق میں اس کا انتظار کرتے ہیں کہ یا تو اللہ تعالیٰ اپنے پاس سے کوئی سزا تمہیں دے یا ہمارے ہاتھوں سے ، پس ایک طرف تم منتظر رہو دوسری جانب تمہارے ساتھ ہم بھی منتظر ہیں۔');
INSERT INTO "junagarri" VALUES(9,53,'9:53','کہہ دیجئے کہ تم خوشی یاناخوشی کسی طرح بھی خرچ کرو قبول تو ہرگز نہ کیا جائے گا ، یقیناً تم فاسق لوگ ہو۔');
INSERT INTO "junagarri" VALUES(9,54,'9:54','کوئی سبب ان کے خرچ کی قبولیت کے نہ ہونے کا اس کے سوا نہیں کہ یہ اللہ اور اس کے رسول کے منکر ہیں اور بڑی کاہلی سے ہی نماز کو آتے ہیں اور برے دل سے ہی خرچ کرتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(9,55,'9:55','پس آپ کو ان کے مال واولاد تعجب میں نہ ڈال دیں ۔ اللہ کی چاہت یہی ہے کہ اس سے انہیں دنیا کی زندگی میں ہی سزا دے اور ان کے کفر ہی کی حالت میں ان کی جانیں نکل جائیں۔ ');
INSERT INTO "junagarri" VALUES(9,56,'9:56','یہ اللہ کی قسم کھا کھا کر کہتے ہیں کہ یہ تمہاری جماعت کے لوگ ہیں، حالانکہ وه دراصل تمہارے نہیں بات صرف اتنی ہے کہ یہ ڈرپوک لوگ ہیں۔ ');
INSERT INTO "junagarri" VALUES(9,57,'9:57','اگر یہ کوئی بچاؤ کی جگہ یا کوئی غار یا کوئی بھی سر گھسانے کی جگہ پالیں تو ابھی اس طرح لگام توڑ کر الٹے بھاگ چھوٹیں۔ ');
INSERT INTO "junagarri" VALUES(9,58,'9:58','ان میں وه بھی ہیں جو خیراتی مال کی تقسیم کے بارے میں آپ پر عیب رکھتے ہیں ، اگر انہیں اس میں سے مل جائے تو خوش ہیں اور اگر اس میں سے نہ ملا تو فوراً بگڑ کھڑے ہوئے۔ ');
INSERT INTO "junagarri" VALUES(9,59,'9:59','اگر یہ لوگ اللہ اور رسول کے دیئے ہوئے پر خوش رہتے اور کہہ دیتے کہ اللہ ہمیں کافی ہے اللہ ہمیں اپنے فضل سے دے گا اور اس کا رسول بھی، ہم تو اللہ کی ذات سے ہی توقع رکھنے والے ہیں۔');
INSERT INTO "junagarri" VALUES(9,60,'9:60','صدقے صرف فقیروں کے لئے ہیں اور مسکینوں کے لئے اور ان کے وصول کرنے والوں کے لئے اور ان کے لئے جن کے دل پرچائے جاتے ہوں اور گردن چھڑانے میں اور قرض داروں کے لئے اور اللہ کی راه میں اور راہرو مسافروں کے لئے ، فرض ہے اللہ کی طرف سے، اور اللہ علم وحکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(9,61,'9:61','ان میں سے وه بھی ہیں جو پیغمبر کو ایذا دیتے ہیں اور کہتے ہیں کان کا کچا ہے، آپ کہہ دیجئے کہ وه کان تمہارے بھلے کے لئے ہے وه اللہ پر ایمان رکھتا ہے اور مسلمانوں کی بات کا یقین کرتا ہے اور تم میں سے جو اہل ایمان ہیں یہ ان کے لئے رحمت ہے، رسول اللہ ﴿صلی اللہ علیہ وسلم﴾ کو جو لوگ ایذا دیتے ہیں ان کے لئے دکھ کی مار ہے۔');
INSERT INTO "junagarri" VALUES(9,62,'9:62','محض تمہیں خوش کرنے کے لئے تمہارے سامنے اللہ کی قسمیں کھا جاتے ہیں حالانکہ اگر یہ ایمان دار ہوتے تو اللہ اور اس کا رسول رضامند کرنے کے زیاده مستحق تھے۔');
INSERT INTO "junagarri" VALUES(9,63,'9:63','کیا یہ نہیں جانتے کہ جو بھی اللہ کی اور اس کے رسول کی مخالفت کرے گااس کے لئے یقیناً دوزخ کی آگ ہے جس میں وه ہمیشہ رہنے والا ہے، یہ زبردست رسوائی ہے۔');
INSERT INTO "junagarri" VALUES(9,64,'9:64','منافقوں کو ہر وقت اس بات کا کھٹکا لگارہتا ہے کہ کہیں مسلمانوں پر کوئی سورت نہ اترے جو ان کے دلوں کی باتیں انہیں بتلا دے۔ کہہ دیجئے کہ تم مذاق اڑاتے رہو۔ یقیناً اللہ تعالیٰ اسے ظاہر کرنے والا ہے جس سے تم ڈر دبک رہے ہو۔');
INSERT INTO "junagarri" VALUES(9,65,'9:65','اگر آپ ان سے پوچھیں تو صاف کہہ دیں گے کہ ہم تو یونہی آپس میں ہنس بول رہے تھے۔ کہہ دیجئے کہ اللہ، اس کی آیتیں اور اس کا رسول ہی تمہارے ہنسی مذاق کے لئے ره گئے ہیں؟ ');
INSERT INTO "junagarri" VALUES(9,66,'9:66','تم بہانے نہ بناؤ یقیناً تم اپنے ایمان کے بعد بے ایمان ہوگئے ، اگر ہم تم میں سے کچھ لوگوں سے درگزر بھی کر لیں تو کچھ لوگوں کو ان کے جرم کی سنگین سزا بھی دیں گے۔ ');
INSERT INTO "junagarri" VALUES(9,67,'9:67','تمام منافق مرد وعورت آپس میں ایک ہی ہیں ، یہ بری باتوں کا حکم دیتے ہیں اور بھلی باتوں سے روکتے ہیں اور اپنی مٹھی بند رکھتے ہیں ، یہ اللہ کو بھول گئے اللہ نے انہیں بھلا دیا ۔ بیشک منافق ہی فاسق وبدکردار ہیں۔');
INSERT INTO "junagarri" VALUES(9,68,'9:68','اللہ تعالیٰ ان منافق مردوں، عورتوں اورکافروں سے جہنم کی آگ کا وعده کر چکا ہے جہاں یہ ہمیشہ رہنے والے ہیں، وہی انہیں کافی ہے ان پر اللہ کی پھٹکار ہے، اور ان ہی کے لئے دائمی عذاب ہے۔');
INSERT INTO "junagarri" VALUES(9,69,'9:69','مثل ان لوگوں کے جو تم سے پہلے تھے ، تم میں سے وه زیاده قوت والے تھے اور زیاده مال واولاد والے تھے پس وه اپنا دینی حصہ برت گئے پھر تم نے بھی اپنا حصہ برت لیا جیسے تم سے پہلے کے لوگ اپنے حصے سے فائده مند ہوئے تھے اور تم نے بھی اسی طرح مذاقانہ بحث کی جیسے کہ انہوں نے کی تھی ۔ ان کے اعمال دنیا اور آخرت میں غارت ہوگئے۔ یہی لوگ نقصان پانے والے ہیں۔ ');
INSERT INTO "junagarri" VALUES(9,70,'9:70','کیا انہیں اپنے سے پہلے لوگوں کی خبریں نہیں پہنچیں، قوم نوح اور عاد اور ثمود اور قوم ابراہیم اور اہل مدین اور اہل مؤتفکات (الٹی ہوئی بستیوں کے رہنے والے) کی ، ان کے پاس ان کے پیغمبر دلیلیں لے کر پہنچے ، اللہ ایسا نہ تھا کہ ان پر ظلم کرے بلکہ انہوں نے خود ہی اپنے اوپر ظلم کیا۔ ');
INSERT INTO "junagarri" VALUES(9,71,'9:71','مومن مرد وعورت آپس میں ایک دوسرے کے (مددگار ومعاون اور) دوست ہیں ، وه بھلائیوں کا حکم دیتے ہیں اور برائیوں سے روکتے ہیں ، نمازوں کو پابندی سے بجا لاتے ہیں زکوٰة ادا کرتے ہیں، اللہ کی اور اس کے رسول کی بات مانتے ہیں ، یہی لوگ ہیں جن پر اللہ تعالیٰ بہت جلد رحم فرمائے گا بیشک اللہ غلبے والا حکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(9,72,'9:72','ان ایمان دار مردوں اور عورتوں سے اللہ نے ان جنتوں کا وعده فرمایا ہے جن کے نیچے نہریں لہریں لے رہی ہیں جہاں وه ہمیشہ ہمیشہ رہنے والے ہیں اور ان صاف ستھرے پاکیزه محلات کا جو ان ہمیشگی والی جنتوں میں ہیں، اور اللہ کی رضامندی سب سے بڑی چیز ہے ، یہی زبردست کامیابی ہے۔');
INSERT INTO "junagarri" VALUES(9,73,'9:73','اے نبی! کافروں اور منافقوں سے جہاد جاری رکھو ، اور ان پر سخت ہو جاؤ ان کی اصلی جگہ دوزخ ہے، جو نہایت بدترین جگہ ہے۔ ');
INSERT INTO "junagarri" VALUES(9,74,'9:74','یہ اللہ کی قسمیں کھا کر کہتے ہیں کہ انہوں نے نہیں کہا، حالانکہ یقیناً کفر کا کلمہ ان کی زبان سے نکل چکا ہے اور یہ اپنے اسلام کے بعد کافر ہوگئے ہیں اور انہوں نے اس کام کا قصد بھی کیا جو پورا نہ کر سکے ۔ یہ صرف اسی بات کا انتقام لے رہے ہیں کہ انہیں اللہ نے اپنے فضل سے اور اس کے رسول ﴿صلی اللہ علیہ وسلم﴾ نے دولت مند کردیا ، اگر یہ اب بھی توبہ کر لیں تو یہ ان کے حق میں بہتر ہے، اور اگر منھ موڑے رہیں تو اللہ تعالیٰ انہیں دنیا وآخرت میں دردناک عذاب دے گا اور زمین بھر میں ان کا کوئی حمایتی اور مددگار نہ کھڑا ہوگا۔');
INSERT INTO "junagarri" VALUES(9,75,'9:75','ان میں وه بھی ہیں جنہوں نے اللہ سے عہد کیا تھا کہ اگر وه ہمیں اپنے فضل سے مال دے گا تو ہم ضرور صدقہ وخیرات کریں گے اور پکی طرح نیکو کاروں میں ہو جائیں گے۔');
INSERT INTO "junagarri" VALUES(9,76,'9:76','لیکن جب اللہ نے اپنے فضل سے انہیں دیا تو یہ اس میں بخیلی کرنے لگے اور ٹال مٹول کرکے منھ موڑ لیا۔ ');
INSERT INTO "junagarri" VALUES(9,77,'9:77','پس اس کی سزا میں اللہ نے ان کے دلوں میں نفاق ڈال دیا اللہ سے ملنے کے دنوں تک، کیونکہ انہوں نے اللہ سے کئے ہوئے وعدے کا خلاف کیا اور کیوں کہ جھوٹ بولتے رہے۔');
INSERT INTO "junagarri" VALUES(9,78,'9:78','کیا وه نہیں جانتے کہ اللہ تعالیٰ کو ان کے دل کا بھید اور ان کی سرگوشی سب معلوم ہے اور اللہ تعالیٰ غیب کی تمام باتوں سے خبردار ہے۔ ');
INSERT INTO "junagarri" VALUES(9,79,'9:79','جولوگ ان مسلمانوں پر طعنہ زنی کرتے ہیں جو دل کھول کر خیرات کرتے ہیں اور ان لوگوں پر جنہیں سوائے اپنی محنت مزدوری کے اور کچھ میسر ہی نہیں، پس یہ ان کا مذاق اڑاتے ہیں ، اللہ بھی ان سے تمسخر کرتا ہے انہی کے لئے دردناک عذاب ہے۔');
INSERT INTO "junagarri" VALUES(9,80,'9:80','ان کے لئے تو استغفار کر یا نہ کر۔ اگر تو ستر مرتبہ بھی ان کے لئے استغفار کرے تو بھی اللہ انہیں ہرگز نہ بخشے گا یہ اس لئے کہ انہوں نے اللہ سے اور اس کے رسول سے کفر کیا ایسے فاسق لوگوں کو رب کریم ہدایت نہیں دیتا۔ ');
INSERT INTO "junagarri" VALUES(9,81,'9:81','پیچھے ره جانے والے لوگ رسول اللہ ﴿صلی اللہ علیہ وسلم﴾ کے جانے کے بعد اپنے بیٹھے رہنے پر خوش ہیں انہوں نے اللہ کی راه میں اپنے مال اور اپنی جانوں سے جہاد کرنا ناپسند رکھا اور انہوں نے کہہ دیا کہ اس گرمی میں مت نکلو۔ کہہ دیجئے کہ دوزخ کی آگ بہت ہی سخت گرم ہے، کاش کہ وه سمجھتے ہوتے۔ ');
INSERT INTO "junagarri" VALUES(9,82,'9:82','پس انہیں چاہئے کہ بہت کم ہنسیں اور بہت زیاده روئیں بدلے میں اس کے جو یہ کرتے تھے۔');
INSERT INTO "junagarri" VALUES(9,83,'9:83','پس اگر اللہ تعالیٰ آپ کو ان کی کسی جماعت کی طرف لوٹا کر واپس لے آئے پھر یہ آپ سے میدان جنگ میں نکلنے کی اجازت طلب کریں تو آپ کہہ دیجئے کہ تم میرے ساتھ ہرگز چل نہیں سکتے اور نہ میرے ساتھ تم دشمنوں سے لڑائی کر سکتے ہو۔ تم نے پہلی مرتبہ ہی بیٹھ رہنے کو پسند کیا تھا پس تم پیچھے ره جانے والوں میں ہی بیٹھے رہو۔ ');
INSERT INTO "junagarri" VALUES(9,84,'9:84','ان میں سے کوئی مر جائے تو آپ اس کے جنازے کی ہرگز نماز نہ پڑھیں اور نہ اس کی قبر پر کھڑے ہوں ۔ یہ اللہ اور اس کے رسول کے منکر ہیں اور مرتے دم تک بدکار بے اطاعت رہے ہیں۔ ');
INSERT INTO "junagarri" VALUES(9,85,'9:85','آپ کو ان کے مال واولاد کچھ بھی بھلے نہ لگیں! اللہ کی چاہت یہی ہے کہ انہیں ان چیزوں سے دنیوی سزا دے اور یہ اپنی جانیں نکلنے تک کافر ہی رہیں۔');
INSERT INTO "junagarri" VALUES(9,86,'9:86','جب کوئی سورت اتاری جاتی ہے تو اللہ پر ایمان لاؤ اور اس کے رسول کے ساتھ مل کر جہاد کرو تو ان میں سے دولت مندوں کا ایک طبقہ آپ کے پاس آکر یہ کہہ کر رخصت لے لیتاہے کہ ہمیں تو بیٹھے رہنے والوں میں ہی چھوڑ دیجئے۔ ');
INSERT INTO "junagarri" VALUES(9,87,'9:87','یہ تو خانہ نشین عورتوں کاساتھ دینے پر ریجھ گئے اور ان کے دلوں پر مہر لگا دی گئی اب وه کچھ سمجھ عقل نہیں رکھتے۔ ');
INSERT INTO "junagarri" VALUES(9,88,'9:88','لیکن خود رسول اللہ اور اس کے ساتھ کے ایمان والے اپنے مالوں اور جانوں سے جہاد کرتے ہیں، یہی لوگ بھلائیوں والے ہیں اور یہی لوگ کامیابی حاصل کرنے والے ہیں۔');
INSERT INTO "junagarri" VALUES(9,89,'9:89','ان کے لئے اللہ نے وه جنتیں تیار کی ہیں جن کے نیچے نہریں جاری ہیں جن میں یہ ہمیشہ رہنے والے ہیں۔ یہ بہت بڑی کامیابی ہے۔ ');
INSERT INTO "junagarri" VALUES(9,90,'9:90','بادیہ نشینوں میں سے عذر والے لوگ حاضر ہوئے کہ انہیں رخصت دے دی جائے اور وه بیٹھ رہے جنہوں نے اللہ سے اور اس کے رسول سے جھوٹی باتیں بنائی تھیں۔ اب تو ان میں جتنے کفار ہیں انہیں دکھ دینے والی مار پہنچ کر رہے گی۔ ');
INSERT INTO "junagarri" VALUES(9,91,'9:91','ضعیفوں پر اور بیماروں پر اور ان پر جن کے پاس خرچ کرنے کو کچھ بھی نہیں کوئی حرج نہیں بشرطیکہ وه اللہ اور اس کے رسول کی خیر خواہی کرتے رہیں۔ ایسے نیک کاروں پر الزام کی کوئی راه نہیں، اللہ تعالیٰ بڑی مغفرت ورحمت والا ہے۔ ');
INSERT INTO "junagarri" VALUES(9,92,'9:92','ہاں ان پر بھی کوئی حرج نہیں جو آپ کے پاس آتے ہیں کہ آپ انہیں سواری مہیا کر دیں تو آپ جواب دیتے ہیں کہ میں تو تمہاری سواری کے لئے کچھ بھی نہیں پاتا، تو وه رنج وغم سے اپنی آنکھوں سے آنسو بہاتے ہوئے لوٹ جاتے ہیں کہ انہیں خرچ کرنے کے لئے کچھ بھی میسر نہیں۔ ');
INSERT INTO "junagarri" VALUES(9,93,'9:93','بیشک انہیں لوگوں پر راه الزام ہے جو باوجود دولتمند ہونے کے آپ سے اجازت طلب کرتے ہیں۔ یہ خانہ نشین عورتوں کا ساتھ دینے پر خوش ہیں اور ان کے دلوں پر مہر خداوندی لگ چکی ہے جس سے وه محض بے علم ہو گئے ہیں۔ ');
INSERT INTO "junagarri" VALUES(9,94,'9:94','یہ لوگ تمہارے سامنے عذر پیش کریں گے جب تم ان کے پاس واپس جاؤ گے۔ آپ کہہ دیجئے کہ یہ عذر پیش مت کرو ہم کبھی تم کو سچا نہ سمجھیں گے، اللہ تعالیٰ ہم کو تمہاری خبر دے چکا ہے اور آئنده بھی اللہ اور اس کا رسول تمہاری کارگزاری دیکھ لیں گے پھر ایسے کے پاس لوٹائے جاؤ گے جو پوشیده اور ظاہر سب کا جاننے والا ہے پھر وه تم کو بتادے گا جو کچھ تم کرتے تھے۔');
INSERT INTO "junagarri" VALUES(9,95,'9:95','ہاں وه اب تمہارے سامنے اللہ کی قسمیں کھا جائیں گے جب تم ان کے پاس واپس جاؤ گے تاکہ تم ان کو ان کی حالت پر چھوڑ دو۔ سو تم ان کو ان کی حالت پر چھوڑ دو۔ وه لوگ بالکل گندے ہیں اور ان کا ٹھکانہ دوزخ ہے ان کاموں کے بدلے جنہیں وه کیا کرتے تھے۔');
INSERT INTO "junagarri" VALUES(9,96,'9:96','یہ اس لیے قسمیں کھائیں گے کہ تم ان سے راضی ہوجاؤ۔ سو اگر تم ان سے راضی بھی ہوجاؤ تو اللہ تعالیٰ تو ایسے فاسق لوگوں سے راضی نہیں ہوتا۔ ');
INSERT INTO "junagarri" VALUES(9,97,'9:97','دیہاتی لوگ کفر اور نفاق میں بہت ہی سخت ہیں اور ان کو ایسا ہونا ہی چاہئے کہ ان کو ان احکام کا علم نہ ہو جو اللہ تعالیٰ نے اپنے رسول پر نازل فرمائے ہیں اور اللہ بڑا علم والا بڑی حکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(9,98,'9:98','اور ان دیہاتیوں میں سے بعض ایسے ہیں کہ جو کچھ خرچ کرتے ہیں اس کو جرمانہ سمجھتے ہیں اور تم مسلمانوں کے واسطے برے وقت کے منتظر رہتے ہیں ، برا وقت ان ہی پر پڑنے والا ہے اور اللہ سننے والا جاننے والا ہے۔');
INSERT INTO "junagarri" VALUES(9,99,'9:99','اور بعض اہل دیہات میں ایسے بھی ہیں جو اللہ تعالیٰ پر اور قیامت کے دن پر ایمان رکھتے ہیں اور جو کچھ خرچ کرتے ہیں اس کو عنداللہ قرب حاصل ہونے کا ذریعہ اور رسول کی دعا کا ذریعہ بناتے ہیں ، یاد رکھو کہ ان کا یہ خرچ کرنا بیشک ان کے لیے موجب قربت ہے، ان کو اللہ تعالیٰ ضرور اپنی رحمت میں داخل کرے گا ۔ اللہ تعالیٰ بڑی مغفرت والا بڑی رحمت والا ہے۔');
INSERT INTO "junagarri" VALUES(9,100,'9:100','اور جو مہاجرین اور انصار سابق اور مقدم ہیں اور جتنے لوگ اخلاص کے ساتھ ان کے پیرو ہیں اللہ ان سب سے راضی ہوا اور وه سب اس سے راضی ہوئے اور اللہ نے ان کے لیے ایسے باغ مہیا کر رکھے ہیں جن کے نیچے نہریں جاری ہوں گی جن میں ہمیشہ رہیں گے یہ بڑی کامیابی ہے۔');
INSERT INTO "junagarri" VALUES(9,101,'9:101','اور کچھ تمہارے گردوپیش والوں میں اور کچھ مدینے والوں میں ایسے منافق ہیں کہ نفاق پر اڑے ہوئے ہیں، آپ ان کو نہیں جانتے ، ان کو ہم جانتے ہیں ہم ان کو دہری سزا دیں گے ، پھر وه بڑے بھاری عذاب کی طرف بھیجے جائیں گے۔');
INSERT INTO "junagarri" VALUES(9,102,'9:102','اور کچھ اور لوگ ہیں جو اپنی خطا کے اقراری ہیں جنہوں نے ملے جلے عمل کیے تھے، کچھ بھلے اور کچھ برے ۔ اللہ سے امید ہے کہ ان کی توبہ قبول فرمائے ۔ بلاشبہ اللہ تعالیٰ بڑی مغفرت والا بڑی رحمت والا ہے۔');
INSERT INTO "junagarri" VALUES(9,103,'9:103','آپ ان کے مالوں میں سے صدقہ لے لیجئے، جس کے ذریعہ سے آپ ان کو پاک صاف کردیں اور ان کے لیے دعا کیجئے ، بلاشبہ آپ کی دعا ان کے لیے موجب اطمینان ہے اور اللہ تعالیٰ خوب سنتا ہے خوب جانتا ہے۔');
INSERT INTO "junagarri" VALUES(9,104,'9:104','کیا ان کو یہ خبر نہیں کہ اللہ ہی اپنے بندوں کی توبہ قبول کرتا ہے اور وہی صدقات کو قبول فرماتا ہے اور یہ کہ اللہ ہی توبہ قبول کرنے میں اور رحمت کرنے میں کامل ہے۔');
INSERT INTO "junagarri" VALUES(9,105,'9:105','کہہ دیجئے کہ تم عمل کیے جاؤ تمہارے عمل اللہ خود دیکھ لے گا اور اس کا رسول اور ایمان والے (بھی دیکھ لیں گے) اور ضرور تم کو ایسے کے پاس جانا ہے جو تمام چھپی اور کھلی چیزوں کا جاننے والا ہے۔ سو وه تم کو تمہارا سب کیا ہوا بتلا دے گا۔ ');
INSERT INTO "junagarri" VALUES(9,106,'9:106','اور کچھ اور لوگ ہیں جن کا معاملہ اللہ کے حکم آنے تک ملتوی ہے ان کو سزا دے گا یا ان کی توبہ قبول کرلے گا ، اور اللہ خوب جاننے والا ہے بڑا حکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(9,107,'9:107','اور بعض ایسے ہیں جنہوں نے ان اغراض کے لیے مسجد بنائی ہے کہ ضرر پہنچائیں اور کفر کی باتیں کریں اور ایمانداروں میں تفریق ڈالیں اور اس شخص کے قیام کا سامان کریں جو اس سے پہلے سے اللہ اور رسول کا مخالف ہے ، اور قسمیں کھا جائیں گے کہ بجز بھلائی کے اور ہماری کچھ نیت نہیں، اور اللہ گواه ہے کہ وه بالکل جھوٹے ہیں۔ ');
INSERT INTO "junagarri" VALUES(9,108,'9:108','آپ اس میں کبھی کھڑے نہ ہوں ۔ البتہ جس مسجد کی بنیاد اول دن سے تقویٰ پر رکھی گئی ہے وه اس لائق ہے کہ آپ اس میں کھڑے ہوں ، اس میں ایسے آدمی ہیں کہ وه خوب پاک ہونے کو پسند کرتے ہیں ، اور اللہ تعالیٰ خوب پاک ہونے والوں کو پسند کرتا ہے۔');
INSERT INTO "junagarri" VALUES(9,109,'9:109','پھر آیا ایسا شخص بہتر ہے جس نے اپنی عمارت کی بنیاد اللہ سے ڈرنے پر اور اللہ کی خوشنودی پر رکھی ہو، یا وه شخص، کہ جس نے اپنی عمارت کی بنیاد کسی گھاٹی کے کنارے پر جو کہ گرنے ہی کو ہو، رکھی ہو، پھر وه اس کو لے کر آتش دوزخ میں گر پڑے ، اور اللہ تعالیٰ ایسے ظالموں کو سمجھ ہی نہیں دیتا۔');
INSERT INTO "junagarri" VALUES(9,110,'9:110','ان کی یہ عمارت جو انہوں نے بنائی ہے ہمیشہ ان کے دلوں میں شک کی بنیاد پر (کانٹا بن کر) کھٹکتی رہے گی، ہاں مگر ان کے دل ہی اگر پاش پاش ہوجائیں تو خیر، اور اللہ تعالیٰ بڑا علم والا بڑی حکمت والا ہے۔');
INSERT INTO "junagarri" VALUES(9,111,'9:111','بلاشبہ اللہ تعالیٰ نے مسلمانوں سے ان کی جانوں کو اور ان کے مالوں کو اس بات کے عوض میں خرید لیا ہے کہ ان کو جنت ملے گی ۔ وه لوگ اللہ کی راه میں لڑتے ہیں جس میں قتل کرتے ہیں اور قتل کیے جاتے ہیں، اس پر سچا وعده کیا گیا ہے تورات میں اور انجیل میں اور قرآن میں اور اللہ سے زیاده اپنے عہد کو کون پورا کرنے والا ہے ، تو تم لوگ اپنی اس بیع پر جس کا تم نے معاملہ ٹھہرایا ہے خوشی مناؤ ،اور یہی بہت بڑی کامیابی ہے۔');
INSERT INTO "junagarri" VALUES(9,112,'9:112','وه ایسے ہیں جو توبہ کرنے والے، عبادت کرنے والے، حمد کرنے والے، روزه رکھنے والے، (یا راه حق میں سفر کرنے والے) رکوع اور سجده کرنے والے، نیک باتوں کی تعلیم کرنے والے اور بری باتوں سے باز رکھنے والے اور اللہ کی حدوں کا خیال رکھنے والے ہیں اور ایسے مومنین کو آپ خوشخبری سنا دیجئے؟ ');
INSERT INTO "junagarri" VALUES(9,113,'9:113','پیغمبر کو اور دوسرے مسلمانوں کو جائز نہیں کہ مشرکین کے لیے مغفرت کی دعا مانگیں اگرچہ وه رشتہدار ہی ہوں اس امر کے ظاہر ہوجانے کے بعد کہ یہ لوگ دوزخی ہیں۔ ');
INSERT INTO "junagarri" VALUES(9,114,'9:114','اور ابراہیم (علیہ السلام) کا اپنے باپ کے لیے دعائے مغفرت مانگنا وه صرف وعده کے سبب سے تھا جو انہوں نے اس سے وعده کرلیا تھا۔ پھر جب ان پر یہ بات ظاہر ہوگئی کہ وه اللہ کا دشمن ہے تو وه اس سے محض بے تعلق ہوگئے ، واقعی ابراہیم (علیہ السلام) بڑے نرم دل اور برد بار تھے۔ ');
INSERT INTO "junagarri" VALUES(9,115,'9:115','اور اللہ ایسا نہیں کرتا کہ کسی قوم کو ہدایت کر کے بعد میں گمراه کر دے جب تک کہ ان چیزوں کو صاف صاف نہ بتلادے جن سے وه بچیں بیشک اللہ تعالیٰ ہر چیز کو خوب جانتا ہے۔');
INSERT INTO "junagarri" VALUES(9,116,'9:116','بلاشبہ اللہ ہی کی سلطنت ہے آسمانوں اور زمین میں۔ وہی جلاتا اور مارتا ہے، اور تمہارا اللہ کے سوا نہ کوئی یار ہے اور نہ کوئی مدد گار ہے۔');
INSERT INTO "junagarri" VALUES(9,117,'9:117','اللہ تعالیٰ نے پیغمبر کے حال پر توجہ فرمائی اور مہاجرین اور انصار کے حال پر بھی جنہوں نے ایسی تنگی کے وقت پیغمبر کا ساتھ دیا ، اس کے بعد کہ ان میں سے ایک گروه کے دلوں میں کچھ تزلزل ہو چلا تھا ۔ پھر اللہ نے ان کے حال پر توجہ فرمائی۔ بلاشبہ اللہ تعالیٰ ان سب پر بہت ہی شفیق مہربان ہے۔');
INSERT INTO "junagarri" VALUES(9,118,'9:118','اور تین شخصوں کے حال پر بھی جن کا معاملہ ملتوی چھوڑ دیا گیا تھا ۔ یہاں تک کہ جب زمین باوجود اپنی فراخی کے ان پر تنگ ہونے لگی اور وه خود اپنی جان سے تنگ آگئے اور انہوں نے سمجھ لیا کہ اللہ سے کہیں پناه نہیں مل سکتی بجز اس کے کہ اسی کی طرف رجوع کیا جائے پھر ان کے حال پر توجہ فرمائی تاکہ وه آئنده بھی توبہ کرسکیں ۔ بیشک اللہ تعالیٰ بہت توبہ قبول کرنے والا بڑا رحم والا ہے۔');
INSERT INTO "junagarri" VALUES(9,119,'9:119','اے ایمان والو! اللہ تعالیٰ سے ڈرو اور سچوں کے ساتھ رہو۔ ');
INSERT INTO "junagarri" VALUES(9,120,'9:120','مدینہ کے رہنے والوں کو اور جو دیہاتی ان کے گردوپیش ہیں ان کو یہ زیبا نہ تھا کہ رسول اللہ کو چھوڑ کر پیچھے ره جائیں اور نہ یہ کہ اپنی جان کو ان کی جان سے عزیز سمجھیں ، یہ اس سبب سے کہ ان کو اللہ کی راه میں جو پیاس لگی اور جو تکان پہنچی اور جو بھوک لگی اور جو کسی ایسی جگہ چلے جو کفار کے لیے موجب غیظ ہوا ہو اور دشمنوں کی جو کچھ خبر لی، ان سب پر ان کے نام (ایک ایک) نیک کام لکھا گیا۔ یقیناً اللہ تعالیٰ مخلصین کا اجر ضائع نہیں کرتا۔');
INSERT INTO "junagarri" VALUES(9,121,'9:121','اور جو کچھ چھوٹا بڑا انہوں نے خرچ کیا اور جتنے میدان ان کو طے کرنے پڑے ، یہ سب بھی ان کے نام لکھا گیا تاکہ اللہ تعالیٰ ان کے کاموں کا اچھے سے اچھا بدلہ دے۔');
INSERT INTO "junagarri" VALUES(9,122,'9:122','اور مسلمانوں کو یہ نہ چاہئے کہ سب کے سب نکل کھڑے ہوں سو ایسا کیوں نہ کیا جائے کہ ان کی ہر بڑی جماعت میں سے ایک چھوٹی جماعت جایا کرے تاکہ وه دین کی سمجھ بوجھ حاصل کریں اور تاکہ یہ لوگ اپنی قوم کو جب کہ وه ان کے پاس آئیں، ڈرائیں تاکہ وه ڈر جائیں۔ ');
INSERT INTO "junagarri" VALUES(9,123,'9:123','اے ایمان والو! ان کفار سے لڑو جو تمہارے آس پاس ہیں اور ان کو تمہارے اندر سختی پانا چاہئے ۔ اور یقین رکھو کہ اللہ تعالیٰ متقی لوگوں کے ساتھ ہے۔');
INSERT INTO "junagarri" VALUES(9,124,'9:124','اور جب کوئی سورت نازل کی جاتی ہے تو بعض منافقین کہتے ہیں کہ اس سورت نے تم میں سے کس کے ایمان کو زیاده کیا ہے ، سو جو لوگ ایمان والے ہیں اس سورت نے ان کے ایمان کو زیاده کیا ہے اور وه خوش ہورہے ہیں۔ ');
INSERT INTO "junagarri" VALUES(9,125,'9:125','اور جن کے دلوں میں روگ ہے اس سورت نے ان میں ان کی گندگی کے ساتھ اور گندگی بڑھا دی اور وه حالت کفر ہی میں مر گئے۔ ');
INSERT INTO "junagarri" VALUES(9,126,'9:126','اور کیا ان کو نہیں دکھلائی دیتا کہ یہ لوگ ہر سال ایک بار یا دو بار کسی نہ کسی آفت میں پھنستے رہتے ہیں پھر بھی نہ توبہ کرتے اور نہ نصیحت قبول کرتے ہیں۔');
INSERT INTO "junagarri" VALUES(9,127,'9:127','اور جب کوئی سورت نازل کی جاتی ہے تو ایک دوسرے کو دیکھنے لگتے ہیں کہ تم کو کوئی دیکھتا تو نہیں پھر چل دیتے ہیں اللہ تعالیٰ نے ان کا دل پھیر دیا ہے اس وجہ سے کہ وه بے سمجھ لوگ ہیں۔ ');
INSERT INTO "junagarri" VALUES(9,128,'9:128','تمہارے پاس ایک ایسے پیغمبر تشریف لائے ہیں جو تمہاری جنس سے ہیں جن کو تمہاری مضرت کی بات نہایت گراں گزرتی ہے جو تمہاری منفعت کے بڑے خواہش مند رہتے ہیں ایمان والوں کے ساتھ بڑے ہی شفیق اور مہربان ہیں۔ ');
INSERT INTO "junagarri" VALUES(9,129,'9:129','پھر اگر رو گردانی کریں تو آپ کہہ دیجیئے کہ میرے لیے اللہ کافی ہے ، اس کے سوا کوئی معبود نہیں۔ میں نے اسی پر بھروسہ کیا اور وه بڑے عرش کا مالک ہے۔ ');
INSERT INTO "junagarri" VALUES(10,1,'10:1','الرٰ۔ یہ پرحکمت کتاب کی آیتیں ہیں۔ ');
INSERT INTO "junagarri" VALUES(10,2,'10:2','کیا ان لوگوں کو اس بات سے تعجب ہوا کہ ہم نے ان میں سے ایک شخص کے پاس وحی بھیج دی کہ سب آدمیوں کو ڈرائیے اور جو ایمان لے آئے ان کو یہ خوشخبری سنائیے کہ ان کے رب کے پاس ان کو پورا اجر ومرتبہ ملے گا۔ کافروں نے کہا کہ یہ شخص تو بلاشبہ صریح جادوگر ہے۔ ');
INSERT INTO "junagarri" VALUES(10,3,'10:3','بلاشبہ تمہارا رب اللہ ہی ہے جس نے آسمانوں اور زمین کو چھ روز میں پیدا کردیا پھر عرش پر قائم ہوا وه ہر کام کی تدبیر کرتا ہے ۔ اس کی اجازت کے بغیر کوئی اس کے پاس سفارش کرنے والا نہیں ایسا اللہ تمہارا رب ہے سو تم اس کی عبادت کرو ، کیا تم پھر بھی نصیحت نہیں پکڑتے۔');
INSERT INTO "junagarri" VALUES(10,4,'10:4','تم سب کو اللہ ہی کے پاس جانا ہے، اللہ نے سچا وعده کر رکھا ہے۔ بیشک وہی پہلی بار بھی پیدا کرتا ہے پھر وہی دوباره بھی پیدا کرے گا تاکہ ایسے لوگوں کو جو کہ ایمان لائے اور انہوں نے نیک کام کیے انصاف کے ساتھ جزا دے اور جن لوگوں نے کفر کیا ان کے واسطے کھولتا ہوا پانی پینے کو ملے گا اور دردناک عذاب ہوگا ان کے کفر کی وجہ سے۔ ');
INSERT INTO "junagarri" VALUES(10,5,'10:5','وه اللہ تعالیٰ ایسا ہے جس نے آفتاب کو چمکتا ہوا بنایا اور چاند کو نورانی بنایا اور اس کے لیے منزلیں مقرر کیں تاکہ تم برسوں کی گنتی اور حساب معلوم کرلیا کرو ۔ اللہ تعالیٰ نے یہ چیزیں بے فائده نہیں پیدا کیں۔ وه یہ دلائل ان کو صاف صاف بتلا رہا ہے جو دانش رکھتے ہیں۔');
INSERT INTO "junagarri" VALUES(10,6,'10:6','بلاشبہ رات اور دن کے یکے بعد دیگرے آنے میں اور اللہ تعالیٰ نے جو کچھ آسمانوں اور زمین میں پیدا کیا ہے ان سب میں ان لوگوں کے واسطے دلائل ہیں جو اللہ کا ڈر رکھتے ہیں۔');
INSERT INTO "junagarri" VALUES(10,7,'10:7','جن لوگوں کو ہمارے پاس آنے کا یقین نہیں ہے اور وه دنیوی زندگی پر راضی ہوگئے ہیں اور اس میں جی لگا بیٹھے ہیں اور جو لوگ ہماری آیتوں سے غافل ہیں۔');
INSERT INTO "junagarri" VALUES(10,8,'10:8','ایسے لوگوں کا ٹھکانا ان کے اعمال کی وجہ سے دوزخ ہے۔');
INSERT INTO "junagarri" VALUES(10,9,'10:9','یقیناً جو لوگ ایمان لائے اور انہوں نے نیک کام کیے ان کا رب ان کو ان کے ایمان کے سبب ان کے مقصد تک پہنچا دے گا نعمت کے باغوں میں جن کے نیچے نہریں جاری ہوں گی۔');
INSERT INTO "junagarri" VALUES(10,10,'10:10','ان کے منھ سے یہ بات نکلے گی سبحان اللہ اور ان کا باہمی سلام یہ ہوگا السلام علیکم اور ان کی اخیر بات یہ ہوگی تمام تعریفیں اللہ کے لیے ہیں جو سارے جہان کا رب ہے۔');
INSERT INTO "junagarri" VALUES(10,11,'10:11','اور اگر اللہ لوگوں پر جلدی سے نقصان واقع کردیا کرتا جس طرح وه فائده کے لیے جلدی مچاتے ہیں تو ان کا وعده کبھی کا پورا ہوچکا ہوتا ۔ سو ہم ان لوگوں کو جن کو ہمارے پاس آنے کا یقین نہیں ہے ان کے حال پر چھوڑے رکھتے ہیں کہ اپنی سرکشی میں بھٹکتے رہیں۔');
INSERT INTO "junagarri" VALUES(10,12,'10:12','اور جب انسان کو کوئی تکلیف پہنچتی ہے تو ہم کو پکارتا ہے لیٹے بھی، بیٹھے بھی، کھڑے بھی۔ پھر جب ہم اس کی تکلیف اس سے ہٹا دیتے ہیں تو وه ایسا ہوجاتا ہے کہ گویا اس نے اپنی تکلیف کے لیے جو اسے پہنچی تھی کبھی ہمیں پکارا ہی نہ تھا ، ان حد سے گزرنے والوں کے اعمال کو ان کے لیے اسی طرح خوشنما بنادیا گیا ہے۔ ');
INSERT INTO "junagarri" VALUES(10,13,'10:13','اور ہم نے تم سے پہلے بہت سے گروہوں کو ہلاک کردیا جب کہ انہوں نے ظلم کیا حالانکہ ان کے پاس ان کے پیغمبر بھی دلائل لے کر آئے، اور وه ایسے کب تھے کہ ایمان لے آتے؟ ہم مجرم لوگوں کو ایسی ہی سزا دیا کرتے ہیں۔ ');
INSERT INTO "junagarri" VALUES(10,14,'10:14','پھر ان کے بعد ہم نے دنیا میں بجائے ان کے تم کو جانشین کیا تاکہ ہم دیکھ لیں کہ تم کس طرح کام کرتے ہو۔');
INSERT INTO "junagarri" VALUES(10,15,'10:15','اور جب ان کے سامنے ہماری آیتیں پڑھی جاتی ہیں ۔ جو بالکل صاف صاف ہیں تو یہ لوگ جن کو ہمارے پاس آنے کی امید نہیں ہے یوں کہتے ہیں کہ اس کے سوا کوئی دوسرا قرآن لائیے یا اس میں کچھ ترمیم کردیجئے۔ آپ (صلی اللہ علیہ وسلم) یوں کہہ دیجئے کہ مجھے یہ حق نہیں کہ میں اپنی طرف سے اس میں ترمیم کردوں بس میں تو اسی کا اتباع کروں گا جو میرے پاس وحی کے ذریعہ سے پہنچا ہے، اگر میں اپنے رب کی نافرمانی کروں تو میں ایک بڑے دن کے عذاب کا اندیشہ رکھتا ہوں۔ ');
INSERT INTO "junagarri" VALUES(10,16,'10:16','آپ یوں کہہ دیجئے کہ اگر اللہ کو منظور ہوتا تو نہ تو میں تم کو وه پڑھ کر سناتا اور نہ اللہ تعالیٰ تم کو اس کی اطلاع دیتا کیونکہ میں اس سے پہلے تو ایک بڑے حصہ عمر تک تم میں ره چکا ہوں۔ پھر کیا تم عقل نہیں رکھتے. ');
INSERT INTO "junagarri" VALUES(10,17,'10:17','سو اس شخص سے زیاده کون ظالم ہوگا جو اللہ پرجھوٹ باندھے یا اس کی آیتوں کو جھوٹا بتلائے، یقیناً ایسے مجرموں کو اصلاً فلاح نہ ہوگی');
INSERT INTO "junagarri" VALUES(10,18,'10:18','اور یہ لوگ اللہ کے سوا ایسی چیزوں کی عبادت کرتے ہیں جو نہ ان کو ضرر پہنچا سکیں اور نہ ان کو نفع پہنچا سکیں اور کہتے ہیں کہ یہ اللہ کے پاس ہمارے سفارشی ہیں ۔ آپ کہہ دیجئے کہ کیا تم اللہ کو ایسی چیز کی خبر دیتے ہو جو اللہ تعالیٰ کو معلوم نہیں، نہ آسمانوں میں اور نہ زمین میں ، وه پاک اور برتر ہے ان لوگوں کے شرک سے . ');
INSERT INTO "junagarri" VALUES(10,19,'10:19','اور تمام لوگ ایک ہی امت کے تھے پھر انہوں نے اختلاف پیداکرلیا اور اگر ایک بات نہ ہوتی جو آپ کے رب کی طرف سے پہلے ٹھہر چکی ہے تو جس چیز میں یہ لوگ اختلاف کررہے ہیں ان کا قطعی فیصلہ ہوچکا ہوتا . ');
INSERT INTO "junagarri" VALUES(10,20,'10:20','اور یہ لوگ یوں کہتے ہیں کہ ان پر ان کے رب کی جانب سے کوئی نشانی کیوں نہیں نازل ہوتی ؟ سو آپ فرما دیجئے کہ غیب کی خبر صرف اللہ کو ہے سو تم بھی منتظر رہو میں بھی تمہارے ساتھ منتظر ہوں .');
INSERT INTO "junagarri" VALUES(10,21,'10:21','اور جب ہم لوگوں کو اس امر کے بعد کہ ان پر کوئی مصیبت پڑ چکی ہو کسی نعمت کامزه چکھا دیتے ہیں تو وه فوراً ہی ہماری آیتوں کے بارے میں چالیں چلنے لگتے ہیں ، آپ کہہ دیجئے کہ اللہ چال چلنے میں تم سے زیاده تیز ہے ، بالیقین ہمارے فرشتے تمہاری سب چالوں کو لکھ رہے ہیں.');
INSERT INTO "junagarri" VALUES(10,22,'10:22','وه اللہ ایسا ہے کہ تم کو خشکی اور دریا میں چلاتا ہے ، یہاں تک کہ جب تم کشتی میں ہوتے ہو اور وه کشتیاں لوگوں کو موافق ہوا کے ذریعہ سے لے کر چلتی ہیں اور وه لوگ ان سے خوش ہوتے ہیں ان پر ایک جھونکا سخت ہوا کا آتا ہے اور ہر طرف سے ان پر موجیں اٹھتی چلی آتی ہیں اور وه سمجھتے ہیں کہ (برے) آ گھرے ، (اس وقت) سب خالص اعتقاد کرکے اللہ ہی کو پکارتے ہیں کہ اگر تو ہم کو اس سے بچالے تو ہم ضرور شکر گزار بن جائیں گے.');
INSERT INTO "junagarri" VALUES(10,23,'10:23','پھر جب اللہ تعالیٰ ان کو بچالیتا ہے تو فوراً ہی وه زمین میں ناحق سرکشی کرنے لگتے ہیں اے لوگو! یہ تمہاری سرکشی تمہارے لیے وبال ہونے والی ہے دنیاوی زندگی کے (چند) فائدے ہیں، پھر ہمارے پاس تم کو آنا ہے پھر ہم سب تمہارا کیا ہوا تم کو بتلادیں گے.');
INSERT INTO "junagarri" VALUES(10,24,'10:24','پس دنیاوی زندگی کی حالت تو ایسی ہے جیسے ہم نے آسمان سے پانی برسایا پھر اس سے زمین کی نباتات، جن کو آدمی اور چوپائے کھاتے ہیں، خوب گنجان ہوکر نکلی۔ یہاں تک کہ جب وه زمین اپنی رونق کا پورا حصہ لے چکی اور اس کی خوب زیبائش ہوگئی اور اس کے مالکوں نے سمجھ لیا کہ اب ہم اس پر بالکل قابض ہوچکے تو دن میں یا رات میں اس پر ہماری طرف سے کوئی حکم (عذاب) آپڑا سو ہم نے اس کو ایسا صاف کردیا کہ گویا کل وه موجود ہی نہ تھی۔ ہم اسی طرح آیات کو صاف صاف بیان کرتے ہیں ایسے لوگوں کے لیے جو سوچتے ہیں.');
INSERT INTO "junagarri" VALUES(10,25,'10:25','اور اللہ تعالیٰ سلامتی کے گھر کی طرف تم کو بلاتا ہے اور جس کو چاہتا ہے راه راست پر چلنے کی توفیق دیتا ہے.');
INSERT INTO "junagarri" VALUES(10,26,'10:26','جن لوگوں نے نیکی کی ہے ان کے واسطے خوبی ہے اور مزید برآں بھی اور ان کے چہروں پر نہ سیاہی چھائے گی اور نہ ذلت، یہ لوگ جنت میں رہنے والے ہیں وه اس میں ہمیشہ رہیں گے.');
INSERT INTO "junagarri" VALUES(10,27,'10:27','اور جن لوگوں نے بدکام کیے ان کی بدی کی سزا اس کے برابر ملے گی اور ان کو ذلت چھائے گی، ان کو اللہ تعالیٰ سے کوئی نہ بچا سکے گا ۔ گویا ان کے چہروں پر اندھیری رات کے پرت کے پرت لپیٹ دیے گئے ہیں ۔ یہ لوگ دوزخ میں رہنے والے ہیں، وه اس میں ہمیشہ رہیں گے.');
INSERT INTO "junagarri" VALUES(10,28,'10:28','اور وه دن بھی قابل ذکر ہے جس روز ہم ان سب کو جمع کریں گے پھر مشرکین سے کہیں گے کہ تم اور تمہارے شریک اپنی جگہ ٹھہرو پھر ہم ان کے آپس میں پھوٹ ڈال دیں گے اور ان کے وه شرکا کہیں گے کہ تم ہماری عبادت نہیں کرتے تھے.');
INSERT INTO "junagarri" VALUES(10,29,'10:29','سو ہمارے تمہارے درمیان اللہ کافی ہے گواه کے طور پر، کہ ہم کو تمہاری عبادت کی خبر بھی نہ تھی. ');
INSERT INTO "junagarri" VALUES(10,30,'10:30','اس مقام پر ہر شخص اپنے اگلے کیے ہوئے کاموں کی جانچ کرلے گا اور یہ لوگ اللہ کی طرف جو ان کا مالک حقیقی ہے لوٹائے جائیں گے اور جو کچھ جھوٹ باندھا کرتے تھے سب ان سے غائب ہوجائیں گے. ');
INSERT INTO "junagarri" VALUES(10,31,'10:31','آپ کہیے کہ وه کون ہے جو تم کو آسمان اور زمین سے رزق پہنچاتا ہے یا وه کون ہے جو کانوں اور آنکھوں پر پورا اختیار رکھتا ہے اور وه کون ہے جو زنده کو مرده سے نکالتا ہے اور مرده کو زنده سے نکالتا ہے اور وه کون ہے جو تمام کاموں کی تدبیر کرتا ہے؟ ضرور وه یہی کہیں گے کہ اللہ تو ان سے کہیے کہ پھر کیوں نہیں ڈرتے.');
INSERT INTO "junagarri" VALUES(10,32,'10:32','سو یہ ہے اللہ تعالیٰ جو تمہارا رب حقیقی ہے۔ پھر حق کے بعد اور کیا ره گیا بجز گمراہی کے، پھر کہاں پھرے جاتے ہو؟ ');
INSERT INTO "junagarri" VALUES(10,33,'10:33','اسی طرح آپ کے رب کی یہ بات کہ یہ ایمان نہ لائیں گے، تمام فاسق لوگوں کے حق میں ثابت ہوچکی ہے. ');
INSERT INTO "junagarri" VALUES(10,34,'10:34','آپ یوں کہیے کہ کیا تمہارے شرکا میں کوئی ایسا ہے جو پہلی بار بھی پیدا کرے، پھر دوباره بھی پیدا کرے؟ آپ کہہ دیجئے کہ اللہ ہی پہلی بار پیدا کرتا ہے پھر وہی دوباره بھی پیدا کرے گا۔ پھر تم کہاں پھرے جاتے ہو؟ ');
INSERT INTO "junagarri" VALUES(10,35,'10:35','آپ کہیے کہ تمہارے شرکا میں کوئی ایسا ہے کہ حق کا راستہ بتاتا ہو؟ آپ کہہ دیجئے کہ اللہ ہی حق کا راستہ بتاتا ہے ۔ تو پھر آیا جو شخص حق کا راستہ بتاتا ہو وه زیاده اتباع کے لائق ہے یا وه شخص جس کو بغیر بتائے خود ہی راستہ نہ سوجھے ؟ پس تم کو کیا ہوگیا ہے تم کیسے فیصلے کرتے ہو. ');
INSERT INTO "junagarri" VALUES(10,36,'10:36','اور ان میں سے اکثر لوگ صرف گمان پر چل رہے ہیں۔ یقیناً گمان، حق (کی معرفت) میں کچھ بھی کام نہیں دے سکتا یہ جو کچھ کررہے ہیں یقیناً اللہ کو سب خبر ہے. ');
INSERT INTO "junagarri" VALUES(10,37,'10:37','اور یہ قرآن ایسا نہیں ہے کہ اللہ (کی وحی) کے بغیر (اپنے ہی سے) گھڑ لیا گیا ہو۔ بلکہ یہ تو (ان کتابوں کی) تصدیق کرنے والا ہے جو اس کے قبل (نازل) ہوچکی ہیں اور کتاب (احکام ضروریہ) کی تفصیل بیان کرنے والا ہے اس میں کوئی بات شک کی نہیں کہ رب العالمین کی طرف سے ہے. ');
INSERT INTO "junagarri" VALUES(10,38,'10:38','کیا یہ لوگ یوں کہتے ہیں کہ آپ نے اس کو گھڑ لیا ہے؟ آپ کہہ دیجئے کہ تو پھر تم اس کے مثل ایک ہی سورت لاؤ اور جن جن غیراللہ کو بلا سکو، بلالو اگر تم سچے ہو. ');
INSERT INTO "junagarri" VALUES(10,39,'10:39','بلکہ ایسی چیز کی تکذیب کرنے لگے جس کو اپنے احاطہٴ علمی میں نہیں لائے اور ہنوز ان کو اس کا اخیر نتیجہ نہیں ملا ۔ جو لوگ ان سے پہلے ہوئے ہیں اسی طرح انہوں نے بھی جھٹلایا تھا، سو دیکھ لیجئے ان ظالموں کا انجام کیسا ہوا؟ ');
INSERT INTO "junagarri" VALUES(10,40,'10:40','اور ان میں سے بعض ایسے ہیں جو اس پر ایمان لے آئیں گے اور بعض ایسے ہیں کہ اس پر ایمان نہ لائیں گے۔ اور آپ کا رب مفسدوں کو خوب جانتا ہے. ');
INSERT INTO "junagarri" VALUES(10,41,'10:41','اور اگر آپ کو جھٹلاتے رہیں تو یہ کہہ دیجئے کہ میرے لیے میرا عمل اور تمہارے لیے تمہارا عمل، تم میرے عمل سے بری ہو اور میں تمہارے عمل سے بری ہوں. ');
INSERT INTO "junagarri" VALUES(10,42,'10:42','اور ان میں بعض ایسے ہیں جو آپ کی طرف کان لگائے بیٹھے ہیں۔ کیا آپ بہروں کو سناتے ہیں گو ان کو سمجھ بھی نہ ہو؟ ');
INSERT INTO "junagarri" VALUES(10,43,'10:43','اور ان میں بعض ایسے ہیں کہ آپ کو تک رہے ہیں۔ پھر کیا آپ اندھوں کو راستہ دکھلانا چاہتے ہیں گو ان کو بصیرت بھی نہ ہو؟ ');
INSERT INTO "junagarri" VALUES(10,44,'10:44','یہ یقینی بات ہے کہ اللہ لوگوں پر کچھ ظلم نہیں کرتا لیکن لوگ خود ہی اپنی جانوں پر ظلم کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(10,45,'10:45','اور ان کو وه دن یاد دلائیے جس میں اللہ ان کو (اپنے حضور) جمع کرے گا (تو ان کو ایسا محسوس ہوگا) کہ گویا وه (دنیا میں) سارے دن کی ایک آدھ گھڑی رہے ہوں گے اور آپس میں ایک دوسرے کو پہچاننے کو ٹھہرے ہوں گے ۔ واقعی خسارے میں پڑے وه لوگ جنہوں نے اللہ کے پاس جانے کو جھٹلایا اور وه ہدایت پانے والے نہ تھے.');
INSERT INTO "junagarri" VALUES(10,46,'10:46','اور جس کا ان سے ہم وعده کر رہے ہیں اس میں سے کچھ تھوڑا سا اگر ہم آپ کو دکھلا دیں یا (ان کے ظہور سے پہلے) ہم آپ کو وفات دے دیں، سو ہمارے پاس تو ان کو آنا ہی ہے۔ پھر اللہ ان کے سب افعال پر گواه ہے. ');
INSERT INTO "junagarri" VALUES(10,47,'10:47','اور ہر امت کے لیے ایک رسول ہے، سو جب ان کا وه رسول آچکتا ہے ان کا فیصلہ انصاف کے ساتھ کیا جاتا ہے ، اور ان پر ظلم نہیں کیا جاتا.');
INSERT INTO "junagarri" VALUES(10,48,'10:48','اور یہ لوگ کہتے ہیں کہ یہ وعده کب ہوگا؟ اگر تم سچے ہو.');
INSERT INTO "junagarri" VALUES(10,49,'10:49','آپ فرما دیجئے کہ میں اپنی ذات کے لیے تو کسی نفع کا اور کسی ضرر کا اختیار رکھتا ہی نہیں مگر جتنا اللہ کو منظور ہو۔ ہر امت کے لیے ایک معین وقت ہے جب ان کا وه معین وقت آپہنچتا ہے تو ایک گھڑی نہ پیچھے ہٹ سکتے ہیں اور نہ آگے سرک سکتے ہیں. ');
INSERT INTO "junagarri" VALUES(10,50,'10:50','آپ فرما دیجئے کہ یہ تو بتلاؤ کہ اگر تم پر اللہ کا عذاب رات کو آپڑے یا دن کو تو عذاب میں کون سی چیز ایسی ہے کہ مجرم لوگ اس کو جلدی مانگ رہے ہیں. ');
INSERT INTO "junagarri" VALUES(10,51,'10:51','کیا پھر جب وه آہی پڑے گا اس پر ایمان لاؤ گے۔ ہاں اب مانا ! حالانکہ تم اس کی جلدی مچایا کرتے تھے.');
INSERT INTO "junagarri" VALUES(10,52,'10:52','پھر ظالموں سے کہا جائے گا کہ ہمیشہ کا عذاب چکھو۔ تم کو تو تمہارے کیے کا ہی بدلہ ملا ہے.');
INSERT INTO "junagarri" VALUES(10,53,'10:53','اور وه آپ سے دریافت کرتے ہیں کہ کیا عذاب واقعی سچ ہے ؟ آپ فرما دیجئے کہ ہاں قسم ہے میرے رب کی وه واقعی سچ ہے اور تم کسی طرح اللہ کو عاجز نہیں کرسکتے.');
INSERT INTO "junagarri" VALUES(10,54,'10:54','اور اگر ہر جان، جس نے ظلم (شرک) کیا ہے، کے پاس اتنا ہو کہ ساری زمین بھر جائے تب بھی اس کو دے کر اپنی جان بچانے لگے اور جب عذاب کو دیکھیں گے تو پشیمانی کو پوشیده رکھیں گے۔ اور ان کا فیصلہ انصاف کے ساتھ ہوگا۔ اور ان پر ظلم نہ ہوگا.');
INSERT INTO "junagarri" VALUES(10,55,'10:55','یاد رکھو کہ جتنی چیزیں آسمانوں میں اور زمین میں ہیں سب اللہ ہی کی ملک ہیں۔ یاد رکھو کہ اللہ تعالیٰ کا وعده سچا ہے لیکن بہت سے آدمی علم ہی نہیں رکھتے.');
INSERT INTO "junagarri" VALUES(10,56,'10:56','وہی جان ڈالتا ہے وہی جان نکالتا ہے اور تم سب اسی کے پاس لائے جاؤ گے. ');
INSERT INTO "junagarri" VALUES(10,57,'10:57','اے لوگو! تمہارے پاس تمہارے رب کی طرف سے ایک ایسی چیز آئی ہے جو نصیحت ہے اور دلوں میں جو روگ ہیں ان کے لیے شفا ہے اور رہنمائی کرنے والی ہے اور رحمت ہے ایمان والوں کے لیے. ');
INSERT INTO "junagarri" VALUES(10,58,'10:58','آپ کہہ دیجئے کہ بس لوگوں کو اللہ کے اس انعام اور رحمت پر خوش ہونا چاہئے ۔ وه اس سے بدرجہا بہتر ہے جس کو وه جمع کر رہے ہیں.');
INSERT INTO "junagarri" VALUES(10,59,'10:59','آپ کہیے کہ یہ تو بتاؤ کہ اللہ نے تمہارے لیے جو کچھ رزق بھیجا تھا پھر تم نے اس کا کچھ حصہ حرام اور کچھ حلال قرار دے لیا ۔ آپ پوچھیے کہ کیا تم کو اللہ نے حکم دیا تھا یا اللہ پر افترا ہی کرتے ہو؟');
INSERT INTO "junagarri" VALUES(10,60,'10:60','اور جو لوگ اللہ پر جھوٹ افترا باندھتے ہیں ان کا قیامت کی نسبت کیا گمان ہے ؟ واقعی لوگوں پر اللہ تعالیٰ کا بڑا ہی فضل ہے لیکن اکثر آدمی شکر نہیں کرتے. ');
INSERT INTO "junagarri" VALUES(10,61,'10:61','اور آپ کسی حال میں ہوں اور منجملہ ان احوال کے آپ کہیں سے قرآن پڑھتے ہوں اور جو کام بھی کرتے ہوں ہم کو سب کی خبر رہتی ہے جب تم اس کام میں مشغول ہوتے ہو۔ اور آپ کے رب سے کوئی چیز ذره برابر بھی غائب نہیں نہ زمین میں اور نہ آسمان میں اورنہ کوئی چیز اس سے چھوٹی اور نہ کوئی چیز بڑی مگر یہ سب کتاب مبین میں ہے. ');
INSERT INTO "junagarri" VALUES(10,62,'10:62','یاد رکھو اللہ کے دوستوں پر نہ کوئی اندیشہ ہے اور نہ وه غمگین ہوتے ہیں. ');
INSERT INTO "junagarri" VALUES(10,63,'10:63','یہ وه لوگ ہیں جو ایمان لائے اور (برائیوں سے) پرہیز رکھتے ہیں.');
INSERT INTO "junagarri" VALUES(10,64,'10:64','ان کے لیے دنیاوی زندگی میں بھی اور آخرت میں بھی خوش خبری ہے اللہ تعالیٰ کی باتوں میں کچھ فرق ہوا نہیں کرتا۔ یہ بڑی کامیابی ہے.');
INSERT INTO "junagarri" VALUES(10,65,'10:65','اور آپ کو ان کی باتیں غم میں نہ ڈالیں۔ تمام تر غلبہ اللہ ہی کے لیے ہے وه سنتا جانتا ہے.');
INSERT INTO "junagarri" VALUES(10,66,'10:66','یاد رکھو کہ جتنے کچھ آسمانوں میں ہیں اور جتنے زمین میں ہیں یہ سب اللہ ہی کے ہیں اور جو لوگ اللہ کو چھوڑ کر دوسرے شرکا کی عبادت کررہے ہیں کس چیز کی اتباع کر رہے ہیں۔ محض بے سند خیال کی اتباع کر رہے ہیں اور محض اٹکلیں لگا رہے ہیں. ');
INSERT INTO "junagarri" VALUES(10,67,'10:67','وه ایسا ہے جس نے تمہارے لیے رات بنائی تاکہ تم اس میں آرام کرو اور دن بھی اس طور پر بنایا کہ دیکھنے بھالنے کا ذریعہ ہے، تحقیق اس میں دلائل ہیں ان لوگوں کے لیے جو سنتے ہیں.');
INSERT INTO "junagarri" VALUES(10,68,'10:68','وه کہتے ہیں کہ اللہ اولاد رکھتا ہے۔ سبحان اللہ! وه تو کسی کا محتاج نہیں اسی کی ملکیت ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے ۔ تمہارے پاس اس پر کوئی دلیل نہیں۔ کیا اللہ کے ذمہ ایسی بات لگاتے ہو جس کا تم علم نہیں رکھتے.');
INSERT INTO "junagarri" VALUES(10,69,'10:69','آپ کہہ دیجئے کہ جو لوگ اللہ پر جھوٹ افترا کرتے ہیں ، وه کامیاب نہ ہوں گے. ');
INSERT INTO "junagarri" VALUES(10,70,'10:70','یہ دنیا میں تھوڑا سا عیش ہے پھر ہمارے پاس ان کو آنا ہے پھر ہم ان کو ان کے کفر کے بدلے سخت عذاب چکھائیں گے.');
INSERT INTO "junagarri" VALUES(10,71,'10:71','اور آپ ان کو نوح﴿علیہ السلام﴾ کا قصہ پڑھ کر سنائیے جب کہ انہوں نے اپنی قوم سے فرمایا کہ اے میری قوم! اگر تم کو میرا رہنا اور احکام الٰہی کی نصیحت کرنا بھاری معلوم ہوتا ہے تو میرا تو اللہ ہی پر بھروسہ ہے۔ تم اپنی تدبیر مع اپنے شرکا کے پختہ کرلو پھر تمہاری تدبیر تمہاری گھٹن کا باعث نہ ہونی چاہئے ۔ پھر میرے ساتھ کر گزرو اور مجھ کو مہلت نہ دو.');
INSERT INTO "junagarri" VALUES(10,72,'10:72','پھر بھی اگر تم اعراض ہی کیے جاؤ تو میں نے تم سے کوئی معاوضہ تو نہیں مانگا ، میرا معاوضہ تو صرف اللہ ہی کے ذمہ ہے اور مجھ کو حکم کیا گیا ہے کہ میں مسلمانوں میں سے رہوں. ');
INSERT INTO "junagarri" VALUES(10,73,'10:73','سو وه لوگ ان کو جھٹلاتے رہے پس ہم نے ان کو اور جو ان کے ساتھ کشتی میں تھے ان کو نجات دی اور ان کو جانشین بنایا اور جنہوں نے ہماری آیتوں کو جھٹلایا تھا ان کو غرق کردیا۔ سو دیکھنا چاہئے کیسا انجام ہوا ان لوگوں کا جو ڈرائے جاچکے تھے.');
INSERT INTO "junagarri" VALUES(10,74,'10:74','پھر نوح (علیہ السلام) کے بعد ہم نے اور رسولوں کو ان کی قوموں کی طرف بھیجا سو وه ان کے پاس روشن دلیلیں لے کر آئے پس جس چیز کو انہوں نے اول میں جھوٹا کہہ دیا یہ نہ ہوا کہ پھر اس کو مان لیتے ۔ اللہ تعالیٰ اسی طرح حد سے بڑھنے والوں کے دلوں پر بند لگا دیتا ہے. ');
INSERT INTO "junagarri" VALUES(10,75,'10:75','پھر ان پیغمبروں کے بعد ہم نے موسیٰ اور ہارون (علیہما السلام) کو ، فرعون اور اس کے سرداروں کے پاس اپنی نشانیاں دے کر بھیجا ۔ سو انہوں نے تکبر کیا اور وه لوگ مجرم قوم تھے. ');
INSERT INTO "junagarri" VALUES(10,76,'10:76','پھر جب ان کو ہمارے پاس سے صحیح دلیل پہنچی تو وه لوگ کہنے لگے کہ یقیناً یہ صریح جادو ہے. ');
INSERT INTO "junagarri" VALUES(10,77,'10:77','موسیٰ (علیہ السلام) نے فرمایا کہ کیا تم اس صحیح دلیل کی نسبت جب کہ وه تمہارے پاس پہنچی ایسی بات کہتے ہو کیا یہ جادو ہے، حالانکہ جادوگر کامیاب نہیں ہوا کرتے. ');
INSERT INTO "junagarri" VALUES(10,78,'10:78','وه لوگ کہنے لگے کیا تم ہمارے پاس اس لیے آئے ہو کہ ہم کو اس طریقہ سے ہٹادو جس پر ہم نے اپنے باپ دادوں کو پایا ہے اور تم دونوں کو دنیا میں بڑائی مل جائے اور ہم تم دونوں کو کبھی نہ مانیں گے.');
INSERT INTO "junagarri" VALUES(10,79,'10:79','اور فرعون نے کہا کہ میرے پاس تمام ماہر جادوگروں کو حاضر کرو.');
INSERT INTO "junagarri" VALUES(10,80,'10:80','پھر جب جادوگر آئے تو موسیٰ (علیہ السلام) نے ان سے فرمایا کہ ڈالو جو کچھ تم ڈالنے والے ہو.');
INSERT INTO "junagarri" VALUES(10,81,'10:81','سو جب انہوں نے ڈالا تو موسی﴿علیہ السلام﴾ نے فرمایا کہ یہ جو کچھ تم لائے ہو جادو ہے۔ یقینی بات ہے کہ اللہ اس کو ابھی درہم برہم کیے دیتا ہے ، اللہ ایسے فسادیوں کا کام بننے نہیں دیتا. ');
INSERT INTO "junagarri" VALUES(10,82,'10:82','اور اللہ تعالیٰ حق کو اپنے فرمان سے ثابت کردیتا ہے گو مجرم کیسا ہی ناگوار سمجھیں.');
INSERT INTO "junagarri" VALUES(10,83,'10:83','پس موسیٰ (علیہ السلام) پر ان کی قوم میں سے صرف قدرے قلیل آدمی ایمان لائے وه بھی فرعون سے اور اپنے حکام سے ڈرتے ڈرتے کہ کہیں ان کو تکلیف پہنچائے اور واقع میں فرعون اس ملک میں زور رکھتا تھا، اور یہ بھی بات تھی کہ وه حد سے باہر ہو جاتا تھا. ');
INSERT INTO "junagarri" VALUES(10,84,'10:84','اور موسیٰ (علیہ السلام) نے فرمایا کہ اے میری قوم! اگر تم اللہ پر ایمان رکھتے ہو تو اسی پر توکل کرو اگر تم مسلمان ہو. ');
INSERT INTO "junagarri" VALUES(10,85,'10:85','انہوں نے عرض کیا کہ ہم نے اللہ ہی پر توکل کیا۔ اے ہمارے پروردگار! ہم کو ان ظالموں کے لئے فتنہ نہ بنا.');
INSERT INTO "junagarri" VALUES(10,86,'10:86','اور ہم کو اپنی رحمت سے ان کافر لوگوں سے نجات دے. ');
INSERT INTO "junagarri" VALUES(10,87,'10:87','اور ہم نے موسیٰ (علیہ السلام) اور ان کے بھائی کے پاس وحی بھیجی کہ تم دونوں اپنے ان لوگوں کے لیے مصر میں گھر برقرار رکھو اور تم سب اپنے انہی گھروں کو نماز پڑھنے کی جگہ قرار دے لو اور نماز کے پابند رہو اور آپ مسلمانوں کو بشارت دے دیں.');
INSERT INTO "junagarri" VALUES(10,88,'10:88','اور موسیٰ (علیہ السلام) نے عرض کیا اے ہمارے رب! تو نے فرعون کو اور اس کے سرداروں کو سامان زینت اور طرح طرح کے مال دنیاوی زندگی میں دیئے۔ اے ہمارے رب! (اسی واسطے دیئے ہیں کہ) وه تیری راه سے گمراه کریں۔ اے ہمارے رب! ان کے مالوں کو نیست ونابود کر دے اور ان کے دلوں کو سخت کردے سو یہ ایمان نہ لانے پائیں یہاں تک کہ دردناک عذاب کو دیکھ لیں. ');
INSERT INTO "junagarri" VALUES(10,89,'10:89','حق تعالیٰ نے فرمایا کہ تم دونوں کی دعا قبول کرلی گئی، سو تم ثابت قدم رہو اور ان لوگوں کی راه نہ چلنا جن کو علم نہیں. ');
INSERT INTO "junagarri" VALUES(10,90,'10:90','اور ہم نے بنی اسرائیل کو دریا سے پار کردیا پھر ان کے پیچھے پیچھے فرعون اپنے لشکر کے ساتھ ظلم اور زیادتی کے اراده سے چلا یہاں تک کہ جب ڈوبنے لگا تو کہنے لگا کہ میں ایمان لاتا ہوں کہ جس پر بنی اسرائیل ایمان لائے ہیں، اس کے سوا کوئی معبود نہیں اور میں مسلمانوں میں سے ہوں.');
INSERT INTO "junagarri" VALUES(10,91,'10:91','(جواب دیا گیا کہ) اب ایمان لاتا ہے؟ اور پہلے سرکشی کرتا رہا اور مفسدوں میں داخل رہا. ');
INSERT INTO "junagarri" VALUES(10,92,'10:92','سو آج ہم صرف تیری لاش کو نجات دیں گے تاکہ تو ان کے لیے نشان عبرت ہو جو تیرے بعد ہیں اور حقیقت یہ ہے کہ بہت سے آدمی ہماری نشانیوں سے غافل ہیں.');
INSERT INTO "junagarri" VALUES(10,93,'10:93','اور ہم نے بنی اسرائیل کو بہت اچھا ٹھکانا رہنے کو دیا اور ہم نے انہیں پاکیزه چیزیں کھانے کو دیں۔ سو انہوں نے اختلاف نہیں کیا یہاں تک کہ ان کے پاس علم پہنچ گیا ۔ یقینی بات ہے کہ آپ کا رب ان کے درمیان قیامت کے دن ان امور میں فیصلہ کرے گا جن میں وه اختلاف کرتے تھے.');
INSERT INTO "junagarri" VALUES(10,94,'10:94','پھر اگر آپ اس کی طرف سے شک میں ہوں جس کو ہم نے آپ کے پاس بھیجا ہے تو آپ ان لوگوں سے پوچھ دیکھیے جو آپ سے پہلی کتابوں کو پڑھتے ہیں۔ بیشک آپ کے پاس آپ کے رب کی طرف سے سچی کتاب آئی ہے۔ آپ ہرگز شک کرنے والوں میں سے نہ ہوں. ');
INSERT INTO "junagarri" VALUES(10,95,'10:95','اور نہ ان لوگوں میں سے ہوں جنہوں نے اللہ تعالیٰ کی آیتوں کو جھٹلایا، کہیں آپ خساره پانے والوں میں سے نہ ہوجائیں. ');
INSERT INTO "junagarri" VALUES(10,96,'10:96','یقیناً جن لوگوں کے حق میں آپ کے رب کی بات ثابت ہوچکی ہے وه ایمان نہ لائیں گے.');
INSERT INTO "junagarri" VALUES(10,97,'10:97','گو ان کے پاس تمام نشانیاں پہنچ جائیں جب تک کہ وه دردناک عذاب کو نہ دیکھ لیں. ');
INSERT INTO "junagarri" VALUES(10,98,'10:98','چنانچہ کوئی بستی ایمان نہ لائی کہ ایمان لانا اس کو نافع ہوتا سوائے یونس ﴿علیہ السلام﴾ کی قوم کے ۔ جب وه ایمان لے آئے تو ہم نے رسوائی کے عذاب کو دنیوی زندگی میں ان پر سے ٹال دیا اور ان کو ایک وقت (خاص) تک کے لیے زندگی سے فائده اٹھانے (کا موقع) دیا. ');
INSERT INTO "junagarri" VALUES(10,99,'10:99','اور اگر آپ کا رب چاہتا تو تمام روئے زمین کے لوگ سب کے سب ایمان لے آتے ، تو کیا آپ لوگوں پر زبردستی کرسکتے ہیں یہاں تک کہ وه مومن ہی ہوجائیں.');
INSERT INTO "junagarri" VALUES(10,100,'10:100','حالانکہ کسی شخص کا ایمان لانا اللہ کے حکم کے بغیر ممکن نہیں۔ اور اللہ تعالیٰ بے عقل لوگوں پر گندگی ڈال دیتا ہے. ');
INSERT INTO "junagarri" VALUES(10,101,'10:101','آپ کہہ دیجئے کہ تم غور کرو کہ کیا کیا چیزیں آسمانوں میں اور زمین میں ہیں اور جو لوگ ایمان نہیں لاتے ان کو نشانیاں اور دھمکیاں کچھ فائده نہیں پہنچاتیں.');
INSERT INTO "junagarri" VALUES(10,102,'10:102','سو وه لوگ صرف ان لوگوں کے سے واقعات کا انتظار کر رہے ہیں جو ان سے پہلے گزر چکے ہیں۔ آپ فرما دیجئے کہ اچھا تو تم انتظار میں رہو میں بھی تمہارے ساتھ انتظار کرنے والوں میں ہوں. ');
INSERT INTO "junagarri" VALUES(10,103,'10:103','پھر ہم اپنے پیغمبروں کو اور ایمان والوں کو بچالیتے تھے، اسی طرح ہمارے ذمہ ہے کہ ہم ایمان والوں کو نجات دیا کرتے ہیں.');
INSERT INTO "junagarri" VALUES(10,104,'10:104','آپ کہہ دیجئے کہ اے لوگو! اگر تم مرے دین کی طرف سے شک میں ہو تو میں ان معبودوں کی عبادت نہیں کرتا جن کی تم اللہ کو چھوڑ کر عبادت کرتے ہو ، لیکن ہاں اس اللہ کی عبادت کرتا ہوں جو تمہاری جان قبض کرتا ہے ۔ اور مجھ کو یہ حکم ہوا ہے کہ میں ایمان لانے والوں میں سے ہوں.');
INSERT INTO "junagarri" VALUES(10,105,'10:105','اور یہ کہ اپنا رخ یکسو ہو کر (اس) دین کی طرف کرلینا ، اور کبھی مشرکوں میں سے نہ ہونا.');
INSERT INTO "junagarri" VALUES(10,106,'10:106','اور اللہ کو چھوڑ کر ایسی چیز کی عبادت مت کرنا جو تجھ کو نہ کوئی نفع پہنچا سکے اور نہ کوئی ضرر پہنچا سکے۔ پھر اگر ایسا کیا تو تم اس حالت میں ظالموں میں سے ہو جاؤ گے. ');
INSERT INTO "junagarri" VALUES(10,107,'10:107','اور اگر تم کو اللہ کوئی تکلیف پہنچائے تو بجز اس کے اور کوئی اس کو دور کرنے والا نہیں ہے اور اگر وه تم کو کوئی خیر پہنچانا چاہے تو اس کے فضل کا کوئی ہٹانے والا نہیں ، وه اپنا فضل اپنے بندوں میں سے جس پر چاہے نچھاور کردے اور وه بڑی مغفرت بڑی رحمت والا ہے.');
INSERT INTO "junagarri" VALUES(10,108,'10:108','آپ کہہ دیجئے کہ اے لوگو! تمہارے پاس حق تمہارے رب کی طرف سے پہنچ چکا ہے ، اس لیے جو شخص راه راست پر آجائے سو وه اپنے واسطے راه راست پر آئے گا اور جو شخص بے راه رہے گا تو اس کا بے راه ہونا اسی پر پڑے گا اور میں تم پر مسلط نہیں کیا گیا. ');
INSERT INTO "junagarri" VALUES(10,109,'10:109','اور آپ اس کی اتباع کرتے رہیے جو کچھ آپ کے پاس وحی بھیجی جاتی ہے اور صبر کیجئے یہاں تک کہ اللہ فیصلہ کردے اور وه سب فیصلہ کرنے والوں میں اچھا ہے. ');
INSERT INTO "junagarri" VALUES(11,1,'11:1','الرٰ، یہ ایک ایسی کتاب ہے کہ اس کی آیتیں محکم کی گئی ہیں ، پھر صاف صاف بیان کی گئی ہیں ایک حکیم باخبر کی طرف سے. ');
INSERT INTO "junagarri" VALUES(11,2,'11:2','یہ کہ اللہ کے سوا کسی کی عبادت مت کرو میں تم کو اللہ کی طرف سے ڈرانے والا اور بشارت دینے والا ہوں.');
INSERT INTO "junagarri" VALUES(11,3,'11:3','اور یہ کہ تم لوگ اپنے گناه اپنے رب سے معاف کراؤ پھر اسی کی طرف متوجہ رہو، وه تم کو وقت مقرر تک اچھا سامان (زندگی) دے گا اور ہر زیاده عمل کرنے والے کو زیاده ثواب دے گا۔ اور اگر تم لوگ اعراض کرتے رہے تو مجھ کو تمہارے لیے ایک بڑے دن کے عذاب کا اندیشہ ہے.');
INSERT INTO "junagarri" VALUES(11,4,'11:4','تم کو اللہ ہی کے پاس جانا ہے اور وه ہر شے پر پوری قدرت رکھتا ہے.');
INSERT INTO "junagarri" VALUES(11,5,'11:5','یاد رکھو وه لوگ اپنے سینوں کو دہرا کیے دیتے ہیں تاکہ اپنی باتیں (اللہ) سے چھپا سکیں ۔ یاد رکھو کہ وه لوگ جس وقت اپنے کپڑے لپیٹتے ہیں وه اس وقت بھی سب جانتا ہے جو کچھ چھپاتے ہیں اور جو کچھ وه ظاہر کرتے ہیں۔ بالیقین وه دلوں کے اندر کی باتیں جانتا ہے.');
INSERT INTO "junagarri" VALUES(11,6,'11:6','زمین پر چلنے پھرنے والے جتنے جاندار ہیں سب کی روزیاں اللہ تعالیٰ پر ہیں وہی ان کے رہنے سہنے کی جگہ کو جانتا ہےاور ان کے سونپے جانے کی جگہ کو بھی، سب کچھ واضح کتاب میں موجود ہے.');
INSERT INTO "junagarri" VALUES(11,7,'11:7','اللہ ہی وه ہے جس نے چھ دن میں آسمان وزمین کو پیدا کیا اور اس کا عرش پانی پر تھا تاکہ وه تمہیں آزمائے کہ تم میں سے اچھے عمل والا کون ہے ، اگر آپ ان سے کہیں کہ تم لوگ مرنے کے بعد اٹھا کھڑے کئے جاؤ گے تو کافر لوگ پلٹ کر جواب دیں گے کہ یہ تو نرا صاف صاف جادو ہی ہے.');
INSERT INTO "junagarri" VALUES(11,8,'11:8','اور اگر ہم ان سے عذاب کو گنی چنی مدت تک کے لئے پیچھے ڈال دیں تو یہ ضرور پکار اٹھیں گے کہ عذاب کو کون سی چیز روکے ہوئے ہے، سنو! جس دن وه ان کے پاس آئے گا پھر ان سے ٹلنے والا نہیں پھر تو جس چیز کی ہنسی اڑا رہے تھے وه انہیں گھیر لے گی. ');
INSERT INTO "junagarri" VALUES(11,9,'11:9','اور اگر ہم انسان کو اپنی کسی نعمت کا ذائقہ چکھا کر پھر اسے اس سے لے لیں تو وه بہت ہی ناامید اور بڑا ہی ناشکرا بن جاتا ہے. ');
INSERT INTO "junagarri" VALUES(11,10,'11:10','اور اگر ہم اسے کوئی نعمت چکھائیں اس سختی کے بعد جو اسے پہنچ چکی تھی تو وه کہنے لگتا ہے کہ بس برائیاں مجھ سے جاتی رہیں ، یقیناً وه بڑا ہی اترانے والا شیخی خور ہے. ');
INSERT INTO "junagarri" VALUES(11,11,'11:11','سوائے ان کے جو صبر کرتے ہیں اور نیک کاموں میں لگے رہتے ہیں۔ انہی لوگوں کے لئے بخشش بھی ہے اور بہت بڑا نیک بدلہ بھی.');
INSERT INTO "junagarri" VALUES(11,12,'11:12','پس شاید کہ آپ اس وحی کے کسی حصے کو چھوڑ دینے والے ہیں جو آپ کی طرف نازل کی جاتی ہے اور اس سے آپ کا دل تنگ ہے، صرف ان کی اس بات پر کہ اس پر کوئی خزانہ کیوں نہیں اترا؟ یا اس کے ساتھ کوئی فرشتہ ہی آتا، سن لیجئے! آپ تو صرف ڈرانے والے ہی ہیں اور ہر چیز کا ذمہ دار اللہ تعالیٰ ہے.');
INSERT INTO "junagarri" VALUES(11,13,'11:13','کیا یہ کہتے ہیں کہ اس قرآن کو اسی نے گھڑا ہے۔ جواب دیجئے کہ پھر تم بھی اسی کی مثل دس سورتیں گھڑی ہوئی لے آؤ اور اللہ کے سوا جسے چاہو اپنے ساتھ بلا بھی لو اگر تم سچے ہو. ');
INSERT INTO "junagarri" VALUES(11,14,'11:14','پھر اگر وه تمہاری اس بات کو قبول نہ کریں تو تم یقین سے جان لو کہ یہ قرآن اللہ کے علم کے ساتھ اتارا گیا ہے اور یہ کہ اللہ کے سوا کوئی معبود نہیں، پس کیا تم مسلمان ہوتے ہو؟ ');
INSERT INTO "junagarri" VALUES(11,15,'11:15','جو شخص دنیا کی زندگی اور اس کی زینت پر فریفتہ ہوا چاہتا ہو ہم ایسوں کو ان کے کل اعمال (کا بدلہ) یہی بھرپور پہنچا دیتے ہیں اور یہاں انہیں کوئی کمی نہیں کی جاتی.');
INSERT INTO "junagarri" VALUES(11,16,'11:16','ہاں یہی وه لوگ ہیں جن کے لئے آخرت میں سوائے آگ کے اور کچھ نہیں اور جو کچھ انہوں نے یہاں کیا ہوگا وہاں سب اکارت ہے اور جو کچھ ان کےاعمال تھے سب برباد ہونے والے ہیں. ');
INSERT INTO "junagarri" VALUES(11,17,'11:17','کیا وه شخص جو اپنے رب کے پاس کی دلیل پر ہو اور اس کے ساتھ اللہ کی طرف کا گواه ہو اور اس سے پہلے موسیٰ کی کتاب (گواه ہو) جو پیشوا اور رحمت ہے (اوروں کے برابر ہو سکتا ہے؟) یہی لوگ ہیں جو اس پر ایمان رکھتے ہیں ، اور تمام فرقوں میں سے جو بھی اس کا منکر ہو اس کے آخری وعدے کی جگہ جہنم ہے ، پس تو اس میں کسی قسم کے شبہ میں نہ ره، یقیناً یہ تیرے رب کی جانب سے سراسر برحق ہے، لیکن اکثر لوگ ایمان لانے والے نہیں ہوتے. ');
INSERT INTO "junagarri" VALUES(11,18,'11:18','اس سے بڑھ کر ظالم کون ہوگا جو اللہ پرجھوٹ باندھے یہ لوگ اپنے پروردگار کے سامنے پیش کئے جائیں گے اور سارے گواه کہیں گے کہ یہ وه لوگ ہیں جنہوں نے اپنے پروردگار پر جھوٹ باندھا، خبردار ہو کہ اللہ کی لعنت ہے ظالموں پر. ');
INSERT INTO "junagarri" VALUES(11,19,'11:19','جو اللہ کی راه سے روکتے ہیں اور اس میں کجی تلاش کر لیتے ہیں ۔ یہی آخرت کے منکر ہیں.');
INSERT INTO "junagarri" VALUES(11,20,'11:20','نہ یہ لوگ دنیا میں اللہ کو ہرا سکے اور نہ ان کا کوئی حمایتی اللہ کے سوا ہوا، ان کے لئے عذاب دگنا کیا جائے گا نہ یہ سننے کی طاقت رکھتے تھے اور نہ یہ دیکھتے ہی تھے. ');
INSERT INTO "junagarri" VALUES(11,21,'11:21','یہی ہیں جنہوں نے اپنا نقصان آپ کر لیا اور وه سب کچھ ان سے کھو گیا، جو انہوں نے گھڑ رکھا تھا.');
INSERT INTO "junagarri" VALUES(11,22,'11:22','بیشک یہی لوگ آخرت میں زیاں کار ہوں گے.');
INSERT INTO "junagarri" VALUES(11,23,'11:23','یقیناً جو لوگ ایمان لائے اور انہوں نے کام بھی نیک کئے اور اپنے پالنے والے کی طرف جھکتے رہے، وہی جنت میں جانے والے ہیں، جہاں وه ہمیشہ ہی رہنے والے ہیں.');
INSERT INTO "junagarri" VALUES(11,24,'11:24','ان دونوں فرقوں کی مثال اندھے، بہرے اور دیکھنے، سننے والے جیسی ہے۔ کیا یہ دونوں مثال میں برابر ہیں؟ کیا پھر بھی تم نصیحت حاصل نہیں کرتے؟ ');
INSERT INTO "junagarri" VALUES(11,25,'11:25','یقیناً ہم نے نوح (علیہ السلام) کو اس کی قوم کی طرف رسول بنا کر بھیجا کہ میں تمہیں صاف صاف ہوشیار کر دینے والا ہوں۔');
INSERT INTO "junagarri" VALUES(11,26,'11:26','کہ تم صرف اللہ ہی کی عبادت کرو ، مجھے تو تم پر دردناک دن کے عذاب کا خوف ہے ۔');
INSERT INTO "junagarri" VALUES(11,27,'11:27','اس کی قوم کے کافروں کے سرداروں نے جواب دیا کہ ہم تو تجھے اپنے جیسا انسان ہی دیکھتے ہیں اور تیرے تابعداروں کو بھی ہم دیکھتے ہیں کہ یہ لوگ واضح طور پر سوائے نیچ لوگوں کے اور کوئی نہیں جو بے سوچے سمجھے (تمہاری پیروی کر رہے ہیں)، ہم تو تمہاری کسی قسم کی برتری اپنے اوپر نہیں دیکھ رہے، بلکہ ہم تو تمہیں جھوٹا سمجھ رہے ہیں۔');
INSERT INTO "junagarri" VALUES(11,28,'11:28','نوح نے کہا، میری قوم والو! مجھے بتاؤ تو اگر میں اپنے رب کی طرف سے کسی دلیل پر ہوا اور مجھے اس نے اپنے پاس کی کوئی رحمت عطا کی ہو ، پھر وه تمہاری نگاہوں میں نہ آئی تو کیا زبردستی میں اسے تمہارے گلے منڈھ دوں، حالانکہ تم اس سے بیزار ہو۔ ');
INSERT INTO "junagarri" VALUES(11,29,'11:29','میری قوم والو! میں تم سے اس پر کوئی مال نہیں مانگتا ۔ میرا ثواب تو صرف اللہ تعالیٰ کے ہاں ہے نہ میں ایمان والوں کو اپنے پاس سے نکال سکتا ہوں ، انہیں اپنے رب سے ملنا ہے لیکن میں دیکھتا ہوں کہ تم لوگ جہالت کر رہے ہو۔ ');
INSERT INTO "junagarri" VALUES(11,30,'11:30','میری قوم کے لوگو! اگر میں ان مومنوں کو اپنے پاس سے نکال دوں تو اللہ کے مقابلہ میں میری مدد کون کر سکتا ہے ؟ کیا تم کچھ بھی نصیحت نہیں پکڑتے۔');
INSERT INTO "junagarri" VALUES(11,31,'11:31','میں تم سے نہیں کہتا کہ میرے پاس اللہ کے خزانے ہیں، (سنو!) میں غیب کا علم بھی نہیں رکھتا، نہ میں یہ کہتا ہوں کہ میں کوئی فرشتہ ہوں، نہ میرا یہ قول ہے کہ جن پر تمہاری نگاہیں ذلت سے پڑ رہی ہیں انہیں اللہ تعالیٰ کوئی نعمت دے گا ہی نہیں ، ان کے دل میں جو ہے اسے اللہ ہی خوب جانتا ہے، اگر میں ایسی بات کہوں تو یقیناً میرا شمار ظالموں میں ہو جائے گا۔ ');
INSERT INTO "junagarri" VALUES(11,32,'11:32','(قوم کے لوگوں نے) کہا اے نوح! تو نے ہم سے بحث کر لی اور خوب بحث کر لی۔ اب تو جس چیز سے ہمیں دھمکا رہا ہے وہی ہمارے پاس لے آ، اگر تو سچوں میں ہے۔ ');
INSERT INTO "junagarri" VALUES(11,33,'11:33','جواب دیا کہ اسے بھی اللہ تعالیٰ ہی لائے گا اگر وه چاہے اور ہاں تم اسے ہرانے والے نہیں ہو۔ ');
INSERT INTO "junagarri" VALUES(11,34,'11:34','تمہیں میری خیر خواہی کچھ بھی نفع نہیں دے سکتی، گو میں کتنی ہی تمہاری خیر خواہی کیوں نہ چاہوں، بشرطیکہ اللہ کا اراده تمہیں گمراه کرنے کا ہو ، وہی تم سب کا پروردگار ہے اور اسی کی طرف لوٹائے جاؤ گے۔');
INSERT INTO "junagarri" VALUES(11,35,'11:35','کیا یہ کہتے ہیں کہ اسے خود اسی نے گھڑ لیا ہے؟ تو جواب دے کہ اگر میں نے اسے گھڑ لیا ہو تو میرا گناه مجھ پر ہے اور میں ان گناہوں سے بری ہوں جو تم کر رہے ہو۔ ');
INSERT INTO "junagarri" VALUES(11,36,'11:36','نوح کی طرف وحی بھیجی گئی کہ تیری قوم میں سے جو ایمان لا چکے ان کے سوا اور کوئی اب ایمان لائے گا ہی نہیں، پس تو ان کے کاموں پر غمگین نہ ہو۔ ');
INSERT INTO "junagarri" VALUES(11,37,'11:37','اورایک کشتی ہماری آنکھوں کے سامنے اور ہماری وحی سے تیار کر اور ظالموں کے بارے میں ہم سے کوئی بات چیت نہ کر وه پانی میں ڈبو دیئے جانے والے ہیں۔ ');
INSERT INTO "junagarri" VALUES(11,38,'11:38','وه (نوح) کشتی بنانے لگے ان کی قوم کے جو سردار ان کے پاس سے گزرتے وه ان کا مذاق اڑاتے ، وه کہتے اگر تم ہمارا مذاق اڑاتے ہو تو ہم بھی تم پر ایک دن ہنسیں گے جیسے تم ہم پر ہنستے ہو۔');
INSERT INTO "junagarri" VALUES(11,39,'11:39','تمیں بہت جلد معلوم ہو جائے گا کہ کس پر عذاب آتا ہے جو اسے رسوا کرے اور اس پر ہمیشگی کی سزا اتر آئے۔');
INSERT INTO "junagarri" VALUES(11,40,'11:40','یہاں تک کہ جب ہمارا حکم آپہنچا اور تنور ابلنے لگا ہم نے کہا کہ اس کشتی میں ہر قسم کے (جانداروں میں سے) جوڑے (یعنی) دو (جانور، ایک نر اور ایک ماده) سوار کرا لے اور اپنے گھر کے لوگوں کو بھی، سوائے ان کے جن پر پہلے سے بات پڑ چکی ہے اور سب ایمان والوں کو بھی ، اس کے ساتھ ایمان لانے والے بہت ہی کم تھے۔ ');
INSERT INTO "junagarri" VALUES(11,41,'11:41','نوح ﴿علیہ السلام﴾ نے کہا، اس کشتی میں بیٹھ جاؤ اللہ ہی کے نام سے اس کا چلنا اور ٹھہرنا ہے ، یقیناً میرا رب بڑی بخشش اور بڑے رحم والا ہے۔');
INSERT INTO "junagarri" VALUES(11,42,'11:42','وه کشتی انہیں پہاڑوں جیسی موجوں میں لے کر جا رہی تھی اور نوح (علیہ السلام) نے اپنے لڑکے کو جو ایک کنارے پر تھا، پکار کر کہا کہ اے میرے پیارے بچے ہمارے ساتھ سوار ہو جا اور کافروں میں شامل نہ ره۔ ');
INSERT INTO "junagarri" VALUES(11,43,'11:43','اس نے جواب دیا کہ میں تو کسی بڑے پہاڑ کی طرف پناه میں آجاؤں گا جو مجھے پانی سے بچا لے گا ، نوح ﴿علیہ السلام﴾ نے کہا آج اللہ کے امر سے بچانے والا کوئی نہیں، صرف وہی بچیں گے جن پر اللہ کا رحم ہوا۔ اسی وقت ان دونوں کے درمیان موج حائل ہوگئی اور وه ڈوبنے والوں میں سے ہوگیا۔ ');
INSERT INTO "junagarri" VALUES(11,44,'11:44','فرما دیا گیا کہ اے زمین اپنے پانی کو نگل جا اور اے آسمان بس کر تھم جا، اسی وقت پانی سکھا دیا گیا اور کام پورا کر دیا گیا اور کشتی جودی نامی پہاڑ پر جا لگی اور فرما دیا گیا کہ ظالم لوگوں پر لعنت نازل ہو۔ ');
INSERT INTO "junagarri" VALUES(11,45,'11:45','نوح ﴿علیہ السلام﴾ نے اپنے پروردگار کو پکارا اور کہا کہ میرے رب میرا بیٹا تو میرے گھر والوں میں سے ہے، یقیناً تیرا وعده بالکل سچا ہے اور تو تمام حاکموں سے بہتر حاکم ہے۔ ');
INSERT INTO "junagarri" VALUES(11,46,'11:46','اللہ تعالیٰ نے فرمایا اے نوح یقیناً وه تیرے گھرانے سے نہیں ہے ، اس کے کام بالکل ہی ناشائستہ ہیں تجھے ہرگز وه چیز نہ مانگنی چاہئے جس کا تجھے مطلقاً علم نہ ہو میں تجھے نصیحت کرتا ہوں کہ تو جاہلوں میں سے اپنا شمار کرانے سے باز رہے۔ ');
INSERT INTO "junagarri" VALUES(11,47,'11:47','نوح نے کہا میرے پالنہار میں تیری ہی پناه چاہتا ہوں اس بات سے کہ تجھ سے وه مانگوں جس کا مجھے علم ہی نہ ہو اگر تو مجھے نہ بخشے گا اور تو مجھ پر رحم نہ فرمائے گا، تو میں خساره پانے والوں میں ہو جاؤں گا۔ ');
INSERT INTO "junagarri" VALUES(11,48,'11:48','فرما دیا گیا کہ اے نوح! ہماری جانب سے سلامتی اور ان برکتوں کے ساتھ اتر ، جو تجھ پر ہیں اور تیرے ساتھ کی بہت سی جماعتوں پر اور بہت سی وه امتیں ہوں گی جنہیں ہم فائده تو ضرور پہنچائیں گے لیکن پھر انہیں ہماری طرف سے دردناک عذاب پہنچے گا۔ ');
INSERT INTO "junagarri" VALUES(11,49,'11:49','یہ خبریں غیب کی خبروں میں سے ہیں جن کی وحی ہم آپ کی طرف کرتے ہیں انہیں اس سے پہلے آپ جانتے تھے اور نہ آپ کی قوم ، اس لئے آپ صبر کرتے رہیئے (یقین مانیئے) کہ انجام کار پرہیزگاروں کے لئے ہی ہے۔ ');
INSERT INTO "junagarri" VALUES(11,50,'11:50','اور قوم عاد کی طرف ان کے بھائی ہود کو ہم نے بھیجا، اس نے کہا میری قوم والو! اللہ ہی کی عبادت کرو، اس کے سوا تمہارا کوئی معبود نہیں، تم تو صرف بہتان باندھ رہے ہو۔ ');
INSERT INTO "junagarri" VALUES(11,51,'11:51','اے میری قوم! میں تم سے اس کی کوئی اجرت نہیں مانگتا، میرا اجر اس کے ذمے ہے جس نے مجھے پیدا کیا ہے تو کیا پھر بھی تم عقل سے کام نہیں لیتے۔ ');
INSERT INTO "junagarri" VALUES(11,52,'11:52','اے میری قوم کے لوگو! تم اپنے پالنے والے سے اپنی تقصیروں کی معافی طلب کرو اور اس کی جناب میں توبہ کرو، تاکہ وه برسنے والے بادل تم پر بھیج دے اور تمہاری طاقت پر اور طاقت قوت بڑھا دے اور تم جرم کرتے ہوئے روگردانی نہ کرو۔ ');
INSERT INTO "junagarri" VALUES(11,53,'11:53','انہوں نے کہا اے ہود! تو ہمارے پاس کوئی دلیل تو لایا نہیں اور ہم صرف تیرے کہنے سے اپنے معبودوں کو چھوڑنے والے نہیں اور نہ ہم تجھ پر ایمان لانے والے ہیں۔ ');
INSERT INTO "junagarri" VALUES(11,54,'11:54','بلکہ ہم تو یہی کہتے ہیں کہ تو ہمارے کسی معبود کے برے جھپٹے میں آگیا ہے ۔ اس نے جواب دیا کہ میں اللہ کو گواه کرتا ہوں اور تم بھی گواه رہو کہ میں تو اللہ کے سوا ان سب سے بیزار ہوں، جنہیں تم شریک بنا رہے ہو۔ ');
INSERT INTO "junagarri" VALUES(11,55,'11:55','اچھا تم سب مل کر میرے خلاف چالیں چل لو اور مجھے بالکل مہلت بھی نہ دو۔ ');
INSERT INTO "junagarri" VALUES(11,56,'11:56','میرا بھروسہ صرف اللہ تعالیٰ پر ہی ہے، جو میرا اور تم سب کا پروردگار ہے جتنے بھی پاؤں دھرنے والے ہیں سب کی پیشانی وہی تھامے ہوئے ہے ۔ یقیناً میرا رب بالکل صحیح راه پر ہے۔ ');
INSERT INTO "junagarri" VALUES(11,57,'11:57','پس اگر تم روگردانی کرو تو کرو میں تو تمہیں وه پیغام پہنچا چکا جو دے کر مجھے تمہاری طرف بھیجا گیا تھا۔ میرا رب تمہارے قائم مقام اور لوگوں کو کر دے گا اور تم اس کا کچھ بھی بگاڑ نہ سکو گے ، یقیناً میرا پروردگار ہر چیز پر نگہبان ہے. ');
INSERT INTO "junagarri" VALUES(11,58,'11:58','اور جب ہمارا حکم آپہنچا تو ہم نے ہود کو اور اس کے مسلمان ساتھیوں کو اپنی خاص رحمت سے نجات عطا فرمائی اور ہم نے ان سب کو سخت عذاب سے بچا لیا. ');
INSERT INTO "junagarri" VALUES(11,59,'11:59','یہ تھی قوم عاد، جنہوں نے اپنے رب کی آیتوں کا انکار کیا اور اس کے رسولوں کی نافرمانی کی اور ہر ایک سرکش نافرمان کے حکم کی تابعداری کی. ');
INSERT INTO "junagarri" VALUES(11,60,'11:60','دنیا میں بھی ان کے پیچھے لعنت لگا دی گئی اور قیامت کے دن بھی ، دیکھ لو قوم عاد نے اپنے رب سے کفر کیا، ہود کی قوم عاد پر دوری ہو. ');
INSERT INTO "junagarri" VALUES(11,61,'11:61','اور قوم ثمود کی طرف ان کے بھائی صالح کو بھیجا ، اس نے کہا کہ اے میری قوم تم اللہ کی عبادت کرو اس کے سوا تمہارا کوئی معبود نہیں ، اسی نے تمہیں زمین سے پیدا کیا ہے اور اسی نے اس زمین میں تمہیں بسایا ہے ، پس تم اس سے معافی طلب کرو اور اس کی طرف رجوع کرو۔ بیشک میرا رب قریب اور دعاؤں کا قبول کرنے والا ہے.');
INSERT INTO "junagarri" VALUES(11,62,'11:62','انہوں نے کہا اے صالح! اس سے پہلے تو ہم تجھ سے بہت کچھ امیدیں لگائے ہوئے تھے، کیا تو ہمیں ان کی عبادتوں سے روک رہا ہے جن کی عبادت ہمارے باپ دادا کرتے چلے آئے، ہمیں تو اس دین میں حیران کن شک ہے جس کی طرف تو ہمیں بلا رہا ہے. ');
INSERT INTO "junagarri" VALUES(11,63,'11:63','اس نے جواب دیا کہ اے میری قوم کے لوگو! ذرا بتاؤ تو اگر میں اپنے رب کی طرف سے کسی مضبوط دلیل پر ہوا اور اس نے مجھے اپنے پاس کی رحمت عطا کی ہو ، پھر اگر میں نے اس کی نافرمانی کر لی تو کون ہے جو اس کے مقابلے میں میری مدد کرے؟ تم تو میرا نقصان ہی بڑھا رہے ہو. ');
INSERT INTO "junagarri" VALUES(11,64,'11:64','اور اے میری قوم والو! یہ اللہ کی بھیجی ہوئی اونٹنی ہے جو تمہارے لئے ایک معجزه ہے اب تم اسے اللہ کی زمین میں کھاتی ہوئی چھوڑ دو اور اسے کسی طرح کی ایذا نہ پہنچاؤ ورنہ ايک قریب عذاب تمہیں پکڑ لے گا . ');
INSERT INTO "junagarri" VALUES(11,65,'11:65','پھر بھی ان لوگوں نے اس اونٹنی کے پاؤں کاٹ ڈالے، اس پر صالح نے کہا کہ اچھا تم اپنے گھروں میں تین تین دن تک تو ره سہ لو، یہ وعده جھوٹا نہیں ہے. ');
INSERT INTO "junagarri" VALUES(11,66,'11:66','پھر جب ہمارا فرمان آپہنچا ، ہم نے صالح کو اور ان پر ایمان لانے والوں کو اپنی رحمت سے اس سے بھی بچا لیا اور اس دن کی رسوائی سے بھی۔ یقیناً تیرا رب نہایت توانا اور غالب ہے.');
INSERT INTO "junagarri" VALUES(11,67,'11:67','اور ظالموں کو بڑے زور کی چنگھاڑ نے آدبوچا ، پھر تو وه اپنے گھروں میں اوندھے پڑے ہوئے ره گئے. ');
INSERT INTO "junagarri" VALUES(11,68,'11:68','ایسے کہ گویا وه وہاں کبھی آباد ہی نہ تھے ، آگاه رہو کہ قوم ثمود نے اپنے رب سے کفر کیا۔ سن لو! ان ثمودیوں پر پھٹکار ہے.');
INSERT INTO "junagarri" VALUES(11,69,'11:69','اور ہمارے بھیجے ہوئے پیغامبر ابراہیم کے پاس خوشخبری لے کر پہنچے اور سلام کہا ، انہوں نے بھی جواب سلام دیا اور بغیر کسی تاخیر کے گائے کا بھنا ہوا بچھڑا لے آئے. ');
INSERT INTO "junagarri" VALUES(11,70,'11:70','اب جو دیکھا کہ ان کے تو ہاتھ بھی اس کی طرف نہیں پہنچ رہے تو ان سے اجنبیت محسوس کرکے دل ہی دل میں ان سے خوف کرنے لگے ، انہوں نے کہا ڈرو نہیں ہم تو قوم لوط کی طرف بھیجے ہوئے آئے ہیں. ');
INSERT INTO "junagarri" VALUES(11,71,'11:71','اس کی بیوی جو کھڑی ہوئی تھی وه ہنس پڑی ، تو ہم نے اسے اسحاق کی اور اسحاق کے پیچھے یعقوب کی خوشخبری دی.');
INSERT INTO "junagarri" VALUES(11,72,'11:72','وه کہنے لگی ہائے میری کم بختی! میرے ہاں اولاد کیسے ہو سکتی ہے میں خود بڑھیا اور یہ میرے خاوند بھی بہت بڑی عمر کے ہیں یہ تو یقیناً بڑی عجیب بات ہے! ');
INSERT INTO "junagarri" VALUES(11,73,'11:73','فرشتوں نے کہا کیا تو اللہ کی قدرت سے تعجب کر رہی ہے ؟ تم پر اے اس گھر کے لوگو اللہ کی رحمت اور اس کی برکتیں نازل ہوں ، بیشک اللہ حمد وثنا کا سزاوار اور بڑی شان والا ہے.');
INSERT INTO "junagarri" VALUES(11,74,'11:74','جب ابراہیم کا ڈر خوف جاتا رہا اور اسے بشارت بھی پہنچ چکی تو ہم سے قوم لوط کے بارے میں کہنے سننے لگے. ');
INSERT INTO "junagarri" VALUES(11,75,'11:75','یقیناً ابراہیم بہت تحمل والے نرم دل اور اللہ کی جانب جھکنے والے تھے.');
INSERT INTO "junagarri" VALUES(11,76,'11:76','اے ابراہیم! اس خیال کو چھوڑ دیجئے، آپ کے رب کا حکم آپہنچا ہے، اور ان پر نہ ٹالے جانے والا عذاب ضرور آنے والا ہے. ');
INSERT INTO "junagarri" VALUES(11,77,'11:77','جب ہمارے بھیجے ہوئے فرشتے لوط کے پاس پہنچے تو وه ان کی وجہ سے بہت غمگین ہوگئے اور دل ہی دل میں کڑھنے لگے اور کہنے لگے کہ آج کا دن بڑی مصیبت کا دن ہے.');
INSERT INTO "junagarri" VALUES(11,78,'11:78','اور اس کی قوم دوڑتی ہوئی اس کے پاس آپہنچی، وه تو پہلے ہی سے بدکاریوں میں مبتلا تھی ، لوط ﴿علیہ السلام﴾ نے کہا اے قوم کے لوگو! یہ ہیں میری بیٹیاں جو تمہارے لئے بہت ہی پاکیزه ہیں ، اللہ سے ڈرو اور مجھے میرے مہمانوں کے بارے میں رسوا نہ کرو، کیا تم میں ایک بھی بھلا آدمی نہیں. ');
INSERT INTO "junagarri" VALUES(11,79,'11:79','انہوں نے جواب دیا کہ تو بخوبی جانتا ہے کہ ہمیں تو تیری بیٹیوں پر کوئی حق نہیں ہے اور تو ہماری اصلی چاہت سے بخوبی واقف ہے. ');
INSERT INTO "junagarri" VALUES(11,80,'11:80','لوط ﴿علیہ السلام﴾ نے کہا کاش کہ مجھ میں تم سے مقابلہ کرنے کی قوت ہوتی یا میں کسی زبردست کا آسرا پکڑ پاتا. ');
INSERT INTO "junagarri" VALUES(11,81,'11:81','اب فرشتوں نے کہا اے لوط! ہم تیرے پروردگار کے بھیجے ہوئے ہیں ناممکن ہے کہ یہ تجھ تک پہنچ جائیں پس تو اپنے گھر والوں کو لے کر کچھ رات رہے نکل کھڑا ہو۔ تم میں سے کسی کو مڑ کر بھی نہ دیکھنا چاہئے، بجز تیری بیوی کے، اس لئے کہ اسے بھی وہی پہنچنے والا ہے جو ان سب کو پہنچے گا، یقیناً ان کے وعدے کا وقت صبح کا ہے، کیا صبح بالکل قریب نہیں. ');
INSERT INTO "junagarri" VALUES(11,82,'11:82','پھر جب ہمارا حکم آپہنچا، ہم نے اس بستی کو زیر وزبر کر دیا اوپر کا حصہ نیچے کر دیا اور ان پر کنکریلے پتھر برسائے جو تہ بہ تہ تھے.');
INSERT INTO "junagarri" VALUES(11,83,'11:83','تیرے رب کی طرف سے نشان دار تھے اور وه ان ظالموں سے کچھ بھی دور نہ تھے. ');
INSERT INTO "junagarri" VALUES(11,84,'11:84','اور ہم نے مدین والوں کی طرف ان کے بھائی شعیب کو بھیجا، اس نے کہا اے میری قوم! اللہ کی عبادت کرو اس کے سوا تمہارا کوئی معبود نہیں اور تم ناپ تول میں بھی کمی نہ کرو میں تو تمہیں آسوده حال دیکھ رہا ہوں اور مجھے تم پر گھیرنے والے دن کے عذاب کا خوف (بھی) ہے. ');
INSERT INTO "junagarri" VALUES(11,85,'11:85','اے میری قوم! ناپ تول انصاف کے ساتھ پوری پوری کرو لوگوں کو ان کی چیزیں کم نہ دو اور زمین میں فساد اور خرابی نہ مچاؤ. ');
INSERT INTO "junagarri" VALUES(11,86,'11:86','اللہ تعالی کا حلال کیا ہوا جو بچ رہے تمہارے لئے بہت ہی بہتر ہے اگر تم ایمان والے ہو ، میں تم پر کچھ نگہبان (اور داروغہ) نہیں ہوں. ');
INSERT INTO "junagarri" VALUES(11,87,'11:87','انہوں نے جواب دیا کہ اے شعیب! کیا تیری صلاة تجھے یہی حکم دیتی ہے کہ ہم اپنے باپ دادوں کے معبودوں کو چھوڑ دیں اور ہم اپنے مالوں میں جو کچھ چاہیں اس کا کرنا بھی چھوڑ دیں تو تو بڑا ہی باوقار اور نیک چلن آدمی ہے. ');
INSERT INTO "junagarri" VALUES(11,88,'11:88','کہا اے میری قوم! دیکھو تو اگر میں اپنے رب کی طرف سے روشن دلیل لئے ہوئے ہوں اور اس نے مجھے اپنے پاس سے بہترین روزی دے رکھی ہے ، میرا یہ اراده بالکل نہیں کہ تمہارا خلاف کر کے خود اس چیز کی طرف جھک جاؤں جس سے تمہیں روک رہا ہوں ، میرا اراده تو اپنی طاقت بھر اصلاح کرنے کا ہی ہے۔ میری توفیق اللہ ہی کی مدد سے ہے ، اسی پر میرا بھروسہ ہے اور اسی کی طرف میں رجوع کرتا ہوں.');
INSERT INTO "junagarri" VALUES(11,89,'11:89','اور اے میری قوم (کے لوگو!) کہیں ایسا نہ ہو کہ تم کو میری مخالفت ان عذابوں کا مستحق بنا دے جو قوم نوح اور قوم ہود اور قوم صالح کو پہنچے ہیں۔ اور قوم لوط تو تم سے کچھ دور نہیں. ');
INSERT INTO "junagarri" VALUES(11,90,'11:90','تم اپنے رب سے استغفار کرو اور اس کی طرف توبہ کرو، یقین مانو کہ میرا رب بڑی مہربانی والا اور بہت محبت کرنے والا ہے.');
INSERT INTO "junagarri" VALUES(11,91,'11:91','انہوں نے کہا اے شعیب! تیری اکثر باتیں تو ہماری سمجھ میں ہی نہیں آتیں اور ہم تو تجھے اپنے اندر بہت کمزور پاتے ہیں ، اگر تیرے قبیلے کا خیال نہ ہوتا تو ہم تو تجھے سنگسار کر دیتے ، اور ہم تجھے کوئی حیثیت والی ہستی نہیں گنتے. ');
INSERT INTO "junagarri" VALUES(11,92,'11:92','انہوں نے جواب دیا کہ اے میری قوم کے لوگو! کیا تمہارے نزدیک میرے قبیلے کے لوگ اللہ سے بھی زیاده ذی عزت ہیں کہ تم نے اسے پس پشت ڈال دیا ہے یقیناً میرا رب جو کچھ تم کر رہے ہو سب کو گھیرے ہوئے ہے.');
INSERT INTO "junagarri" VALUES(11,93,'11:93','اے میری قوم کے لوگو! اب تم اپنی جگہ عمل کئے جاؤ میں بھی عمل کر رہا ہوں، تمہیں عنقریب معلوم ہو جائے گا کہ کس کے پاس وه عذاب آتا ہے جو اسے رسوا کر دے اور کون ہے جو جھوٹا ہے۔ تم انتظار کرو میں بھی تمہارے ساتھ منتظر ہوں. ');
INSERT INTO "junagarri" VALUES(11,94,'11:94','جب ہمارا حکم (عذاب) آپہنچا ہم نے شعیب کو اور ان کے ساتھ (تمام) مومنوں کو اپنی خاص رحمت سے نجات بخشی اور ظالموں کو سخت چنگھاڑ کے عذاب نے دھر دبوچا جس سے وه اپنے گھروں میں اوندھے پڑے ہوئے ہوگئے.');
INSERT INTO "junagarri" VALUES(11,95,'11:95','گویا کہ وه ان گھروں میں کبھی بسے ہی نہ تھے، آگاه رہو مدین کے لئے بھی ویسی ہی دوری ہو جیسی دوری ثمود کو ہوئی.');
INSERT INTO "junagarri" VALUES(11,96,'11:96','اور یقیناً ہم نے ہی موسیٰ کو اپنی آیات اور روشن دلیلوں کے ساتھ بھیجا تھا. ');
INSERT INTO "junagarri" VALUES(11,97,'11:97','فرعون اور اس کے سرداروں کی طرف، پھر بھی ان لوگوں نے فرعون کے احکام کی پیروی کی اور فرعون کا کوئی حکم درست تھا ہی نہیں. ');
INSERT INTO "junagarri" VALUES(11,98,'11:98','وه تو قیامت کے دن اپنی قوم کا پیش رو ہو کر ان سب کو دوزخ میں جا کھڑا کرے گا ، وه بہت ہی برا گھاٹ ہے جس پر لا کھڑے کئے جائیں گے.');
INSERT INTO "junagarri" VALUES(11,99,'11:99','ان پر تو اس دنیا میں بھی لعنت چپکا دی گئی اور قیامت کے دن بھی برا انعام ہے جو دیا گیا. ');
INSERT INTO "junagarri" VALUES(11,100,'11:100','بستیوں کی یہ بعض خبریں جنہیں ہم تیرے سامنے بیان فرما رہے ہیں ان میں سے بعض تو موجود ہیں اور بعض (کی فصلیں) کٹ گئی ہیں. ');
INSERT INTO "junagarri" VALUES(11,101,'11:101','ہم نے ان پر کوئی ظلم نہیں کیا ، بلکہ خود انہوں نے ہی اپنے اوپر ظلم کیا ، اور انہیں ان کے معبودوں نے کوئی فائده نہ پہنچایا جنہیں وه اللہ کے سوا پکارا کرتے تھے، جب کہ تیرے پروردگار کا حکم آپہنچا، بلکہ اور ان کا نقصان ہی انہوں نے بڑھا دیا. ');
INSERT INTO "junagarri" VALUES(11,102,'11:102','تیرے پروردگار کی پکڑ کا یہی طریقہ ہے جب کہ وه بستیوں کے رہنے والے ظالموں کو پکڑتا ہے بیشک اس کی پکڑ دکھ دینے والی اور نہایت سخت ہے.');
INSERT INTO "junagarri" VALUES(11,103,'11:103','یقیناً اس میں ان لوگوں کے لئے نشان عبرت ہے جو قیامت کے عذاب سے ڈرتے ہیں۔ وه دن جس میں سب لوگ جمع کئے جائیں گے اور وه، وه دن ہے جس میں سب حاضر کئے جائیں گے. ');
INSERT INTO "junagarri" VALUES(11,104,'11:104','اسے ہم جو ملتوی کرتے ہیں وه صرف ایک مدت معین تک ہے. ');
INSERT INTO "junagarri" VALUES(11,105,'11:105','جس دن وه آجائے گی مجال نہ ہوگی کہ اللہ کی اجازت کے بغیر کوئی بات بھی کر لے، سو ان میں کوئی بدبخت ہوگا اور کوئی نیک بخت.');
INSERT INTO "junagarri" VALUES(11,106,'11:106','لیکن جو بدبخت ہوئے وه دوزخ میں ہوں گے وہاں چیخیں گے چلائیں گے.');
INSERT INTO "junagarri" VALUES(11,107,'11:107','وه وہیں ہمیشہ رہنے والے ہیں جب تک آسمان وزمین برقرار رہیں ۔ سوائے اس وقت کے جو تمہارا رب چاہے ۔ یقیناً تیرا رب جو کچھ چاہے کر گزرتا ہے.');
INSERT INTO "junagarri" VALUES(11,108,'11:108','لیکن جو نیک بخت کئے گئے وه جنت میں ہوں گے جہاں ہمیشہ رہیں گے جب تک آسمان وزمین باقی رہے مگر جو تیرا پروردگار چاہے ۔ یہ بے انتہا بخشش ہے. ');
INSERT INTO "junagarri" VALUES(11,109,'11:109','اس لئے آپ ان چیزوں سے شک وشبہ میں نہ رہیں جنہیں یہ لوگ پوج رہے ہیں، ان کی پوجا تو اس طرح ہے جس طرح ان کے باپ دادوں کی اس سے پہلے تھی۔ ہم ان سب کو ان کا پورا پورا حصہ بغیر کسی کمی کے دینے والے ہی ہیں. ');
INSERT INTO "junagarri" VALUES(11,110,'11:110','یقیناً ہم نے موسیٰ (علیہ السلام) کو کتاب دی۔ پھر اس میں اختلاف کیا گیا، اگر پہلے ہی آپ کے رب کی بات صادر نہ ہوگئی ہوتی تو یقیناً ان کا فیصلہ کر دیا جاتا ، انہیں تو اس میں سخت شبہ ہے.');
INSERT INTO "junagarri" VALUES(11,111,'11:111','یقیناًان میں سے ہر ایک جب ان کے روبرو جائے گا تو آپ کا رب اسے اس کے اعمال کا پورا پورا بدلہ دے گا۔ بیشک وه جو کر رہے ہیں ان سے وه باخبر ہے.');
INSERT INTO "junagarri" VALUES(11,112,'11:112','پس آپ جمے رہیئے جیسا کہ آپ کو حکم دیا گیا ہے اور وه لوگ بھی جو آپ کے ساتھ توبہ کر چکے ہیں، خبردار تم حد سے نہ بڑھنا ، اللہ تمہارے تمام اعمال کا دیکھنے والا ہے.');
INSERT INTO "junagarri" VALUES(11,113,'11:113','دیکھو ظالموں کی طرف ہرگز نہ جھکنا ورنہ تمہیں بھی (دوزخ کی) آگ لگ جائے گی اور اللہ کے سوا اور تمہارا مددگار نہ کھڑا ہو سکے گا اور نہ تم مدد دیئے جاؤ گے.');
INSERT INTO "junagarri" VALUES(11,114,'11:114','دن کے دونوں سروں میں نماز برپا رکھ اور رات کی کئی ساعتوں میں بھی ، یقیناً نیکیاں برائیوں کو دور کر دیتی ہیں ۔ یہ نصیحت ہے نصیحت پکڑنے والوں کے لئے.');
INSERT INTO "junagarri" VALUES(11,115,'11:115','آپ صبر کرتے رہئے یقیناً اللہ تعالیٰ نیکی کرنے والوں کا اجر ضائع نہیں کرتا.');
INSERT INTO "junagarri" VALUES(11,116,'11:116','پس کیوں نہ تم سے پہلے زمانے کے لوگوں میں سے ایسے اہل خیر لوگ ہوئے جو زمین میں فساد پھیلانے سے روکتے، سوائے ان چند کے جنہیں ہم نے ان میں سے نجات دی تھی ، ظالم لوگ تو اس چیز کے پیچھے پڑ گئے جس میں انہیں آسودگی دی گئی تھی اور وه گنہگار تھے. ');
INSERT INTO "junagarri" VALUES(11,117,'11:117','آپ کا رب ایسا نہیں کہ کسی بستی کو ظلم سے ہلاک کر دے اور وہاں کے لوگ نیکو کار ہوں.');
INSERT INTO "junagarri" VALUES(11,118,'11:118','اگر آپ کا پروردگار چاہتا تو سب لوگوں کو ایک ہی راه پر ایک گروه کر دیتا۔ وه تو برابر اختلاف کرنے والے ہی رہیں گے.');
INSERT INTO "junagarri" VALUES(11,119,'11:119','بجز ان کے جن پر آپ کا رب رحم فرمائے، انہیں تو اسی لئے پیدا کیا ہے ، اور آپ کے رب کی یہ بات پوری ہے کہ میں جہنم کو جنوں اور انسانوں سب سے پر کروں گا. ');
INSERT INTO "junagarri" VALUES(11,120,'11:120','رسولوں کے سب احوال ہم آپ کے سامنے آپ کے دل کی تسکین کے لئے بیان فرما رہے ہیں۔ آپ کے پاس اس سورت میں بھی حق پہنچ چکا جو نصیحت ووعظ ہے مومنوں کے لئے.');
INSERT INTO "junagarri" VALUES(11,121,'11:121','ایمان نہ لانے والوں سے کہہ دیجئے کہ تم اپنے طور پر عمل کئے جاؤ ہم بھی عمل میں مشغول ہیں.');
INSERT INTO "junagarri" VALUES(11,122,'11:122','اور تم بھی انتظار کرو ہم بھی منتظر ہیں. ');
INSERT INTO "junagarri" VALUES(11,123,'11:123','زمینوں اور آسمانوں کا علم غیب اللہ تعالیٰ ہی کو ہے، تمام معاملات کا رجوع بھی اسی کی جانب ہے، پس تجھے اسی کی عبادت کرنی چاہئے اور اسی پر بھروسہ رکھنا چاہئے اور تم جو کچھ کرتے ہو اس سے اللہ تعالیٰ بے خبر نہیں.');
INSERT INTO "junagarri" VALUES(12,1,'12:1','الرٰ، یہ روشن کتاب کی آیتیں ہیں۔.*');
INSERT INTO "junagarri" VALUES(12,2,'12:2','یقیناً ہم نے اس کو قرآن عربی نازل فرمایا ہے کہ تم سمجھ سکو. ');
INSERT INTO "junagarri" VALUES(12,3,'12:3','ہم آپ کے سامنے بہترین بیان پیش کرتے ہیں اس وجہ سے کہ ہم نے آپ کی جانب یہ قرآن وحی کے ذریعے نازل کیا اور یقیناً آپ اس سے پہلے بے خبروں میں سے تھے. ');
INSERT INTO "junagarri" VALUES(12,4,'12:4','جب کہ یوسف نے اپنے باپ سے ذکر کیا کہ ابا جان میں نے گیاره ستاروں کو اور سورج چاند کو دیکھا کہ وه سب مجھے سجده کر رہے ہیں.');
INSERT INTO "junagarri" VALUES(12,5,'12:5','یعقوب علیہ السلام نے کہا پیارے بچے! اپنے اس خواب کا ذکر اپنے بھائیوں سے نہ کرنا۔ ایسا نہ ہو کہ وه تیرے ساتھ کوئی فریب کاری کریں ، شیطان تو انسان کا کھلا دشمن ہے. ');
INSERT INTO "junagarri" VALUES(12,6,'12:6','اور اسی طرح تجھے تیرا پروردگار برگزیده کرے گا اور تجھے معاملہ فہمی (یا خوابوں کی تعبیر) بھی سکھائے گا اور اپنی نعمت تجھے بھرپور عطا فرمائے گا اور یعقوب کے گھر والوں کو بھی ، جیسے کہ اس نے اس سے پہلے تیرے دادا اور پردادا یعنی ابراہیم واسحاق کو بھی بھرپور اپنی نعمت دی، یقیناً تیرا رب بہت بڑے علم والا اور زبردست حکمت والا ہے.');
INSERT INTO "junagarri" VALUES(12,7,'12:7','یقیناً یوسف اور اس کے بھائیوں میں دریافت کرنے والوں کے لئے (بڑی) نشانیاں ہیں.');
INSERT INTO "junagarri" VALUES(12,8,'12:8','جب کہ انہوں نے کہا کہ یوسف اور اس کا بھائی بہ نسبت ہمارے، باپ کو بہت زیاده پیارے ہیں حالانکہ ہم (طاقتور) جماعت ہیں کوئی شک نہیں کہ ہمارے ابا صریح غلطی میں ہیں. ');
INSERT INTO "junagarri" VALUES(12,9,'12:9','یوسف کو تو مار ہی ڈالو یا اسے کسی (نامعلوم) جگہ پھینک دو کہ تمہارے والد کا رخ صرف تمہاری طرف ہی ہو جائے۔ اس کے بعد تم نیک ہو جانا. ');
INSERT INTO "junagarri" VALUES(12,10,'12:10','ان میں سے ایک نے کہا یوسف کو قتل تو نہ کرو بلکہ اسے کسی اندھے کنوئیں (کی تہ) میں ڈال آؤ کہ اسے کوئی (آتا جاتا) قافلہ اٹھا لے جائے اگر تمہیں کرنا ہی ہے تو یوں کرو. ');
INSERT INTO "junagarri" VALUES(12,11,'12:11','انہوں نے کہا ابا! آخر آپ یوسف (علیہ السلام) کے بارے میں ہم پر اعتبار کیوں نہیں کرتے ہم تو اس کے خیر خواه ہیں. ');
INSERT INTO "junagarri" VALUES(12,12,'12:12','کل آپ اسے ضرور ہمارے ساتھ بھیج دیجئے کہ خوب کھائے پئے اور کھیلے ، اس کی حفاظت کے ہم ذمہ دار ہیں.');
INSERT INTO "junagarri" VALUES(12,13,'12:13','(یعقوب علیہ السلام نے) کہا اسے تمہارا لے جانا مجھے تو سخت صدمہ دے گا اور مجھے یہ بھی کھٹکا لگا رہے گا کہ تمہاری غفلت میں اسے بھیڑیا کھا جائے.');
INSERT INTO "junagarri" VALUES(12,14,'12:14','انہوں نے جواب دیا کہ ہم جیسی (زور آور) جماعت کی موجودگی میں بھی اگر اسے بھیڑیا کھا جائے تو ہم بالکل نکمے ہی ہوئے.');
INSERT INTO "junagarri" VALUES(12,15,'12:15','پھر جب اسے لے چلے اور سب نے مل کر ٹھان لیا کہ اسے غیر آباد گہرے کنوئیں کی تہ میں پھینک دیں، ہم نے یوسف (علیہ السلام) کی طرف وحی کی کہ یقیناً (وقت آرہا ہے کہ) تو انہیں اس ماجرا کی خبر اس حال میں دے گا کہ وه جانتے ہی نہ ہوں. ');
INSERT INTO "junagarri" VALUES(12,16,'12:16','اور عشا کے وقت (وه سب) اپنے باپ کے پاس روتے ہوئے پہنچے.');
INSERT INTO "junagarri" VALUES(12,17,'12:17','اور کہنے لگے کہ ابا جان ہم تو آپس میں دوڑ میں لگ گئے اور یوسف (علیہ السلام) کو ہم نے اسباب کے پاس چھوڑا پس اسے بھیڑیا کھا گیا، آپ تو ہماری بات نہیں مانیں گے، گو ہم بالکل سچے ہی ہوں. ');
INSERT INTO "junagarri" VALUES(12,18,'12:18','اور یوسف کے کرتے کو جھوٹ موٹ کے خون سے خون آلود بھی کر لائے تھے، باپ نے کہا یوں نہیں، بلکہ تم نے اپنے دل ہی سے ایک بات بنا لی ہے۔ پس صبر ہی بہتر ہے ، اور تمہاری بنائی ہوئی باتوں پر اللہ ہی سے مدد کی طلب ہے. ');
INSERT INTO "junagarri" VALUES(12,19,'12:19','اور ایک قافلہ آیا اور انہوں نے اپنے پانی لانے والے کو بھیجا اس نے اپنا ڈول لٹکا دیا، کہنے لگا واه واه خوشی کی بات ہے یہ تو ایک لڑکا ہے ، انہوں نے اسے مال تجارت قرار دے کر چھپا دیا اور اللہ تعالیٰ اس سے باخبر تھا جو وه کر رہے تھے.');
INSERT INTO "junagarri" VALUES(12,20,'12:20','اور انہوں نے اسے بہت ہی ہلکی قیمت پر گنتی کے چند درہموں پر ہی بیچ ڈالا، وه تو یوسف کے بارے میں بہت ہی بے رغبت تھے. ');
INSERT INTO "junagarri" VALUES(12,21,'12:21','مصر والوں میں سے جس نے اسے خریدا تھا اس نے اپنی بیوی سے کہا کہ اسے بہت عزت واحترام کے ساتھ رکھو، بہت ممکن ہے کہ یہ ہمیں فائده پہنچائے یا اسے ہم اپنا بیٹا ہی بنا لیں، یوں ہم نے مصر کی سرزمین میں یوسف کا قدم جما دیا کہ ہم اسے خواب کی تعبیر کا کچھ علم سکھا دیں۔ اللہ اپنے ارادے پر غالب ہے لیکن اکثر لوگ بے علم ہوتے ہیں.');
INSERT INTO "junagarri" VALUES(12,22,'12:22','اور جب (یوسف) پختگی کی عمر کو پہنچ گئے ہم نے اسے قوت فیصلہ اور علم دیا ، ہم نیک کاروں کو اسی طرح بدلہ دیتے ہیں.');
INSERT INTO "junagarri" VALUES(12,23,'12:23','اس عورت نے جس کے گھر میں یوسف تھے، یوسف کو بہلانا پھسلانا شروع کیا کہ وه اپنے نفس کی نگرانی چھوڑ دے اور دروازے بند کرکے کہنے لگی لو آجاؤ یوسف نے کہا اللہ کی پناه! وه میرا رب ہے، مجھے اس نے بہت اچھی طرح رکھا ہے۔ بے انصافی کرنے والوں کا بھلا نہیں ہوتا. ');
INSERT INTO "junagarri" VALUES(12,24,'12:24','اس عورت نے یوسف کی طرف قصد کیا اور یوسف اس کا قصد کرتے اگر وه اپنے پروردگار کی دلیل نہ دیکھتے ، یونہی ہوا اس واسطے کہ ہم اس سے برائی اور بے حیائی دور کر دیں ۔ بے شک وه ہمارے چنے ہوئے بندوں میں سے تھا.');
INSERT INTO "junagarri" VALUES(12,25,'12:25','دونوں دروازے کی طرف دوڑے اور اس عورت نے یوسف کا کرتا پیچھے کی طرف سے کھینچ کر پھاڑ ڈالا اور دروازے کے پاس ہی عورت کا شوہر دونوں کو مل گیا، تو کہنے لگی جو شخص تیری بیوی کے ساتھ برا اراده کرے بس اس کی سزا یہی ہے کہ اسے قید کر دیا جائے یا اور کوئی دردناک سزا دی جائے. ');
INSERT INTO "junagarri" VALUES(12,26,'12:26','یوسف نے کہا یہ عورت ہی مجھے پھسلا رہی تھی ، اور عورت کے قبیلے ہی کے ایک شخص نے گواہی دی کہ اگر اس کا کرتا آگے سے پھٹا ہوا ہو تو عورت سچی ہے اور یوسف جھوٹ بولنے والوں میں سے ہے.');
INSERT INTO "junagarri" VALUES(12,27,'12:27','اور اگر اس کا کرتا پیچھے کی جانب سے پھاڑا گیاہے تو عورت جھوٹی ہے اور یوسف سچوں میں سے ہے.');
INSERT INTO "junagarri" VALUES(12,28,'12:28','خاوند نے جو دیکھا کہ یوسف کا کرتا پیٹھ کی جانب سے پھاڑا گیا ہے تو صاف کہہ دیا کہ یہ تو تم عورتوں کی چال بازی ہے، بیشک تمہاری چال بازی بہت بڑی ہے. ');
INSERT INTO "junagarri" VALUES(12,29,'12:29','یوسف اب اس بات کو آتی جاتی کرو اور (اے عورت) تو اپنے گناه سے توبہ کر، بیشک تو گنہگاروں میں سے ہے. ');
INSERT INTO "junagarri" VALUES(12,30,'12:30','اور شہر کی عورتوں میں چرچا ہونے لگا کہ عزیز کی بیوی اپنے (جوان) غلام کو اپنا مطلب نکالنے کے لئے بہلانے پھسلانے میں لگی رہتی ہے، ان کے دل میں یوسف کی محبت بیٹھ گئی ہے، ہمارے خیال میں تو وه صریح گمراہی میں ہے. ');
INSERT INTO "junagarri" VALUES(12,31,'12:31','اس نے جب ان کی اس پر فریب غیبت کا حال سنا تو انہیں بلوا بھیجا اور ان کے لئے ایک مجلس مرتب کی اور ان میں سے ہر ایک کو چھری دی۔ اور کہا اے یوسف! ان کے سامنے چلے آؤ ، ان عورتوں نے جب اسے دیکھا تو بہت بڑا جانا اور اپنے ہاتھ کاٹ لئے ، اور زبان سے نکل گیا کہ حاشاللہ! یہ انسان تو ہرگز نہیں، یہ تو یقیناً کوئی بہت ہی بزرگ فرشتہ ہے. ');
INSERT INTO "junagarri" VALUES(12,32,'12:32','اس وقت عزیز مصر کی بیوی نے کہا، یہی ہے جن کے بارے میں تم مجھے طعنے دے رہی تھیں ، میں نے ہر چند اس سے اپنا مطلب حاصل کرنا چاہا لیکن یہ بال بال بچا رہا، اور جو کچھ میں اس سے کہہ رہی ہوں اگر یہ نہ کرے گا تو یقیناً یہ قید کر دیا جائے گا اور بیشک یہ بہت ہی بے عزت ہوگا. ');
INSERT INTO "junagarri" VALUES(12,33,'12:33','یوسف نے دعا کی کہ اے میرے پروردگار! جس بات کی طرف یہ عورتیں مجھے بلا رہی ہیں اس سے تو مجھے جیل خانہ بہت پسند ہے، اگر تو نے ان کا فن فریب مجھ سے دور نہ کیا تو میں تو ان کی طرف مائل ہو جاؤں گا اور بالکل نادانوں میں جا ملوں گا. ');
INSERT INTO "junagarri" VALUES(12,34,'12:34','اس کے رب نے اس کی دعا قبول کر لی اور ان عورتوں کے داؤ پیچ اس سے پھیر دیے، یقیناً وه سننے والا جاننے والا ہے.');
INSERT INTO "junagarri" VALUES(12,35,'12:35','پھر ان تمام نشانیوں کے دیکھ لینے کے بعد بھی انہیں یہی مصلحت معلوم ہوئی کہ یوسف کو کچھ مدت کے لئے قید خانہ میں رکھیں. ');
INSERT INTO "junagarri" VALUES(12,36,'12:36','اس کے ساتھ ہی دو اور جوان بھی جیل خانے میں داخل ہوئے، ان میں سے ایک نے کہا کہ میں نے خواب میں اپنے آپ کو شراب نچوڑتے دیکھا ہے، اور دوسرے نے کہا میں نے اپنے آپ کو دیکھا ہے کہ میں اپنے سر پر روٹی اٹھائے ہوئے ہوں جسے پرندے کھا رہے ہیں، ہمیں آپ اس کی تعبیر بتایئے، ہمیں تو آپ خوبیوں والے شخص دکھائی دیتے ہیں. ');
INSERT INTO "junagarri" VALUES(12,37,'12:37','یوسف ﴿علیہ السلام﴾ نے کہا تمہیں جو کھانا دیا جاتا ہے اس کے تمہارے پاس پہنچنے سے پہلے ہی میں تمہیں اس کی تعبیر بتلا دوں گا۔ یہ سب اس علم کی بدولت ہے جو مجھے میرے رب نے سکھایا ہے ، میں نے ان لوگوں کا مذہب چھوڑ دیا ہے جو اللہ پر ایمان نہیں رکھتے اور آخرت کے بھی منکر ہیں. ');
INSERT INTO "junagarri" VALUES(12,38,'12:38','میں اپنے باپ دادوں کے دین کا پابند ہوں، یعنی ابراہیم واسحاق اور یعقوب کے دین کا ، ہمیں ہرگز یہ سزاوار نہیں کہ ہم اللہ تعالیٰ کے ساتھ کسی کو بھی شریک کریں ، ہم پر اور تمام اور لوگوں پر اللہ تعالیٰ کا یہ خاص فضل ہے، لیکن اکثر لوگ ناشکری کرتے ہیں.');
INSERT INTO "junagarri" VALUES(12,39,'12:39','اے میرے قید خانے کے ساتھیو ! کیا متفرق کئی ایک پروردگار بہتر ہیں ؟ یا ایک اللہ زبردست طاقتور؟');
INSERT INTO "junagarri" VALUES(12,40,'12:40','اس کے سوا تم جن کی پوجا پاٹ کر رہے ہو وه سب نام ہی نام ہیں جو تم نے اور تمہارے باپ دادوں نے خود ہی گھڑ لئے ہیں۔ اللہ تعالیٰ نے ان کی کوئی دلیل نازل نہیں فرمائی ، فرمانروائی صرف اللہ تعالیٰ ہی کی ہے، اس کا فرمان ہے کہ تم سب سوائے اس کے کسی اور کی عبادت نہ کرو، یہی دین درست ہے لیکن اکثر لوگ نہیں جانتے. ');
INSERT INTO "junagarri" VALUES(12,41,'12:41','اے میرے قیدخانے کے رفیقو ! تم دونوں میں سے ایک تو اپنے بادشاه کو شراب پلانے پر مقرر ہو جائے گا ، لیکن دوسرا سولی پر چڑھایا جائے گا اور پرندے اس کا سرنوچ نوچ کھائیں گے ، تم دونوں جس کے بارے میں تحقیق کر رہے تھے اس کام کا فیصلہ کردیا گیا. ');
INSERT INTO "junagarri" VALUES(12,42,'12:42','اور جس کی نسبت یوسف کا گمان تھا کہ ان دونوں میں سے یہ چھوٹ جائے گا اس سے کہا کہ اپنے بادشاه سے میرا ذکر بھی کر دینا، پھر اسے شیطان نے اپنے بادشاه سے ذکر کرنا بھلا دیا اور یوسف نے کئی سال قیدخانے میں ہی کاٹے. ');
INSERT INTO "junagarri" VALUES(12,43,'12:43','بادشاه نے کہا، میں نے خواب میں دیکھا ہے کہ سات موٹی تازی فربہ گائیں ہیں جن کو سات لاغر دبلی پتلی گائیں کھا رہی ہیں اور سات بالیاں ہیں ہری ہری اور دوسری سات بالکل خشک۔ اے درباریو! میرے اس خواب کی تعبیر بتلاؤ اگر تم خواب کی تعبیر دے سکتے ہو.');
INSERT INTO "junagarri" VALUES(12,44,'12:44','انہوں نے جواب دیا کہ یہ تو اڑتے اڑاتے پریشان خواب ہیں اور ایسے شوریده پریشان خوابوں کی تعبیر جاننے والے ہم نہیں. ');
INSERT INTO "junagarri" VALUES(12,45,'12:45','ان دو قیدیوں میں سے جو رہا ہوا تھا اسے مدت کے بعد یاد آگیا اور کہنے لگامیں تمہیں اس کی تعبیر بتلا دوں گا مجھے جانے کی اجازت دیجئے. ');
INSERT INTO "junagarri" VALUES(12,46,'12:46','اے یوسف! اے بہت بڑے سچے یوسف! آپ ہمیں اس خواب کی تعبیر بتلایئے کہ سات موٹی تازی گائیں ہیں جنہیں سات دبلی پتلی گائیں کھا رہی ہیں اور سات بالکل سبز خوشے ہیں اور سات ہی دوسرے بالکل خشک ہیں، تاکہ میں واپس جاکر ان لوگوں سے کہوں کہ وه سب جان لیں.');
INSERT INTO "junagarri" VALUES(12,47,'12:47','یوسف نے جواب دیا کہ تم سات سال تک پے درپے لگاتار حسب عادت غلہ بویا کرنا، اور فصل کاٹ کر اسے بالیوں سمیت ہی رہنے دینا سوائے اپنے کھانے کی تھوڑی سی مقدار کے.');
INSERT INTO "junagarri" VALUES(12,48,'12:48','اس کے بعد سات سال نہایت سخت قحط آئیں گے وه اس غلے کو کھا جائیں گے، جو تم نے ان کے لئے ذخیره رکھ چھوڑا تھا ، سوائے اس تھوڑے سے کے جو تم روک رکھتے ہو. ');
INSERT INTO "junagarri" VALUES(12,49,'12:49','اس کے بعد جو سال آئے گا اس میں لوگوں پر خوب بارش برسائی جائے گی اور اس میں (شیرہٴ انگور بھی) خوب نچوڑیں گے. ');
INSERT INTO "junagarri" VALUES(12,50,'12:50','اور بادشاه نے کہا یوسف کو میرے پاس لاؤ ، جب قاصد یوسف کے پاس پہنچا تو انہوں نے کہا، اپنے بادشاه کے پاس واپس جا اور اس سے پوچھ کہ ان عورتوں کا حقیقی واقعہ کیا ہے جنہوں نے اپنے ہاتھ کاٹ لئے ، تھے؟ ان کے حیلے کو (صحیح طور پر) جاننے والا میرا پروردگار ہی ہے.');
INSERT INTO "junagarri" VALUES(12,51,'12:51','بادشاه نے پوچھا اے عورتو! اس وقت کا صحیح واقعہ کیا ہے جب تم داؤ فریب کر کے یوسف کو اس کی دلی منشا سے بہکانا چاہتی تھیں، انہوں نے صاف جواب دیا کہ معاذ اللہ ہم نے یوسف میں کوئی برائی نہیں پائی، پھر تو عزیز کی بیوی بھی بول اٹھی کہ اب تو سچی بات نتھر آئی۔ میں نے ہی اسے ورغلایا تھا، اس کے جی سے، اور یقیناً وه سچوں میں سے ہے. ');
INSERT INTO "junagarri" VALUES(12,52,'12:52','(یوسف علیہ السلام نے کہا) یہ اس واسطے کہ (عزیز) جان لے کہ میں نے اس کی پیٹھ پیچھے اس کی خیانت نہیں کی اور یہ بھی کہ اللہ دغابازوں کے ہتھکنڈے چلنے نہیں دیتا. ');
INSERT INTO "junagarri" VALUES(12,53,'12:53','میں اپنے نفس کی پاکیزگی بیان نہیں کرتا ۔ بیشک نفس تو برائی پر ابھارنے والا ہی ہے ، مگر یہ کہ میرا پروردگار ہی اپنا رحم کرے ، یقیناً میرا پالنے والا بڑی بخشش کرنے والا اور بہت مہربانی فرمانے والا ہے.');
INSERT INTO "junagarri" VALUES(12,54,'12:54','بادشاه نے کہا اسے میرے پاس لاؤ کہ میں اسے اپنے خاص کاموں کے لئے مقرر کر لوں .، پھر جب اس سے بات چیت کی تو کہنے لگا کہ آپ ہمارے ہاں آج سے ذی عزت اور امانت دار ہیں. ');
INSERT INTO "junagarri" VALUES(12,55,'12:55','(یوسف نے) کہا آپ مجھے ملک کے خزانوں پر مقرر کر دیجئے ، میں حفاظت کرنے والا اور باخبر ہوں. ');
INSERT INTO "junagarri" VALUES(12,56,'12:56','اسی طرح ہم نے یوسف (علیہ السلام) کو ملک کا قبضہ دے دیا۔ کہ وه جہاں کہیں چاہے رہے سہے ، ہم جسے چاہیں اپنی رحمت پہنچا دیتے ہیں۔ ہم نیکو کاروں کا ثواب ضائع نہیں کرتے. ');
INSERT INTO "junagarri" VALUES(12,57,'12:57','یقیناً ایمان داروں اور پرہیز گاروں کا اخروی اجر بہت ہی بہتر ہے.');
INSERT INTO "junagarri" VALUES(12,58,'12:58','یوسف کے بھائی آئے اور یوسف کے پاس گئے تو اس نے انہیں پہچان لیا اور انہوں نے اسے نہ پہچانا. ');
INSERT INTO "junagarri" VALUES(12,59,'12:59','جب انہیں ان کا اسباب مہیا کر دیا تو کہا کہ تم میرے پاس اپنے اس بھائی کو بھی لانا جو تمہارے باپ سے ہے، کیا تم نے نہیں دیکھا کہ میں پورا ناپ کر دیتا ہوں اور میں ہوں بھی بہترین میزبانی کرنے والوں میں. ');
INSERT INTO "junagarri" VALUES(12,60,'12:60','پس اگر تم اسے لے کر پاس نہ آئے تو میری طرف سے تمہیں کوئی ناپ بھی نہ ملے گا بلکہ تم میرے قریب بھی نہ پھٹکنا. ');
INSERT INTO "junagarri" VALUES(12,61,'12:61','انہوں نے کہا اچھا ہم اس کے باپ کو اس کی بابت پھسلائیں گے اور پوری کوشش کریں گے. ');
INSERT INTO "junagarri" VALUES(12,62,'12:62','اپنے خدمت گاروں سے کہا کہ ان کی پونجی انہی کی بوریوں میں رکھ دو کہ جب لوٹ کر اپنے اہل وعیال میں جائیں اور پونجیوں کو پہچان لیں تو بہت ممکن ہے کہ یہ پھر لوٹ کر آئیں.');
INSERT INTO "junagarri" VALUES(12,63,'12:63','جب یہ لوگ لوٹ کر اپنے والد کے پاس گئے تو کہنے لگے کہ ہم سے تو غلہ کا ناپ روک لیا گیا ۔ اب آپ ہمارے ساتھ ہمارے بھائی کو بھیجئے کہ ہم پیمانہ بھر کر لائیں ہم اس کی نگہبانی کے ذمہ دار ہیں.');
INSERT INTO "junagarri" VALUES(12,64,'12:64','(یعقوب علیہ السلام نے) کہا کہ مجھے تو اس کی بابت تمہارا بس ویسا ہی اعتبار ہے جیسا اس سے پہلے اس کے بھائی کے بارے میں تھا ، بس اللہ ہی بہترین حافظ ہے اور وه سب مہربانوں سے بڑا مہربان ہے. ');
INSERT INTO "junagarri" VALUES(12,65,'12:65','جب انہوں نے اپنا اسباب کھولا تو اپنا سرمایہ موجود پایا جو ان کی جانب لوٹا دیا گیا تھا۔ کہنے لگے اے ہمارے باپ ہمیں اور کیا چاہیئے ۔ دیکھئے تو یہ ہمارا سرمایہ بھی ہمیں واپس لوٹا دیا گیا ہے۔ ہم اپنے خاندان کو رسد لادیں گے اور اپنے بھائی کی نگرانی رکھیں گے اور ایک اونٹ کے بوجھ کا غلہ زیاده لائیں گے ۔ یہ ناپ تو بہت آسان ہے. ');
INSERT INTO "junagarri" VALUES(12,66,'12:66','یعقوب (علیہ السلام) نے کہا! میں تو اسے ہرگز ہرگز تمہارے ساتھ نہ بھیجوں گا جب تک کہ تم اللہ کو بیچ میں رکھ کر مجھے قول وقرار نہ دو کہ تم اسے میرے پاس پہنچا دو گے، سوائے اس ایک صورت کے کہ تم سب گرفتار کر لئے جاؤ ۔ جب انہوں نے پکا قول قرار دے دیا تو انہوں نے کہا کہ ہم جو کچھ کہتے ہیں اللہ اس پر نگہبان ہے.');
INSERT INTO "junagarri" VALUES(12,67,'12:67','اور (یعقوب علیہ السلام) نے کہا اے میرے بچو! تم سب ایک دروازے سے نہ جانا بلکہ کئی جدا جدا دروازوں میں سے داخل ہونا ۔ میں اللہ کی طرف سے آنے والی کسی چیز کو تم سے ٹال نہیں سکتا۔ حکم صرف اللہ ہی کا چلتا ہے ۔ میرا کامل بھروسہ اسی پر ہے اور ہر ایک بھروسہ کرنے والے کو اسی پر بھروسہ کرنا چاہئے.');
INSERT INTO "junagarri" VALUES(12,68,'12:68','جب وه انہی راستوں سے جن کا حکم ان کے والد نے انہیں دیا تھا، گئے۔ کچھ نہ تھا کہ اللہ نے جو بات مقرر کر دی ہے وه اس سے انہیں ذرا بھی بچا لے۔ مگر یعقوب (علیہ السلام) کے دل میں ایک خیال (پیدا ہوا) جسے اس نے پورا کر لیا ، بلاشبہ وه ہمارے سکھلائے ہوئے علم کا عالم تھا لیکن اکثر لوگ نہیں جانتے. ');
INSERT INTO "junagarri" VALUES(12,69,'12:69','یہ سب جب یوسف کے پاس پہنچ گئے تو اس نے اپنے بھائی کو اپنے پاس بٹھا لیا اور کہا میں تیرا بھائی (یوسف) ہوں، پس یہ جو کچھ کرتے رہے اس کا کچھ رنج نہ کر. ');
INSERT INTO "junagarri" VALUES(12,70,'12:70','پھر جب انہیں ان کا سامان اسباب ٹھیک ٹھاک کرکے دیا تو اپنے بھائی کے اسباب میں پانی پینے کا پیالہ رکھ دیا۔ پھر ایک آواز دینے والے نے پکار کر کہا کہ اے قافلے والو! تم لوگ تو چور ہو. ');
INSERT INTO "junagarri" VALUES(12,71,'12:71','انہوں نے ان کی طرف منھ پھیر کر کہا کہ تمہاری کیا چیز کھوئی گئی ہے؟');
INSERT INTO "junagarri" VALUES(12,72,'12:72','جواب دیا کہ شاہی پیمانہ گم ہے جو اسے لے آئےاسے ایک اونٹ کے بوجھ کا غلہ ملے گا۔ اس وعدے کا میں ضامن ہوں. ');
INSERT INTO "junagarri" VALUES(12,73,'12:73','انہوں نے کہا اللہ کی قسم! تم کو خوب علم ہے کہ ہم ملک میں فساد پھیلانے کے لئے نہیں آئے اور نہ ہم چور ہیں. ');
INSERT INTO "junagarri" VALUES(12,74,'12:74','انہوں نے کہا اچھا چور کی کیا سزا ہے اگر تم جھوٹے ہو؟ ');
INSERT INTO "junagarri" VALUES(12,75,'12:75','جواب دیا کہ اس کی سزا یہی ہے کہ جس کے اسباب میں سے پایا جائے وہی اس کا بدلہ ہے ۔ ہم تو ایسے ظالموں کو یہی سزا دیا کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(12,76,'12:76','پس یوسف نے ان کے سامان کی تلاشی شروع کی، اپنے بھائی کے سامان کی تلاشی سے پہلے، پھر اس پیمانہ کو اپنے بھائی کے سامان (زنبیل) سے نکالا ۔ ہم نے یوسف کے لئے اسی طرح یہ تدبیر کی ۔ اس بادشاه کے قانون کی رو سے یہ اپنے بھائی کو نہ لے سکتا تھا مگر یہ کہ اللہ کو منظور ہو۔ ہم جس کے چاہیں درجے بلند کر دیں ، ہر ذی علم پر فوقیت رکھنے والا دوسرا ذی علم موجود ہے. ');
INSERT INTO "junagarri" VALUES(12,77,'12:77','انہوں نے کہا کہ اگر اس نے چوری کی (تو کوئی تعجب کی بات نہیں) اس کابھائی بھی پہلے چوری کر چکا ہے ۔ یوسف (علیہ السلام) نے اس بات کو اپنے دل میں رکھ لیا اور ان کے سامنے بالکل ظاہر نہ کیا۔ کہا کہ تم بدتر جگہ میں ہو ، اور جو تم بیان کرتے ہو اسے اللہ ہی خوب جانتا ہے.');
INSERT INTO "junagarri" VALUES(12,78,'12:78','انہوں نے کہا کہ اے عزیز مصر ! اس کے والد بہت بڑی عمر کے بالکل بوڑھے شخص ہیں۔ آپ اس کے بدلے ہم میں سے کسی کو لے لیجئے۔ ہم دیکھتے ہیں کہ آپ بڑے نیک نفس ہیں. ');
INSERT INTO "junagarri" VALUES(12,79,'12:79','یوسف (علیہ السلام) نے کہا ہم نے جس کے پاس اپنی چیز پائی ہے اس کے سوا دوسرے کی گرفتاری کرنے سے اللہ کی پناه چاہتے ہیں، ایسا کرنے سے تو ہم یقیناً ناانصافی کرنے والے ہو جائیں گے. ');
INSERT INTO "junagarri" VALUES(12,80,'12:80','جب یہ اس سے مایوس ہو گئے تو تنہائی میں بیٹھ کر مشوره کرنے لگے ۔ ان میں جو سب سے بڑا تھا اس نے کہا تمہیں معلوم نہیں کہ تمہارے والد نے تم سے اللہ کی قسم لے کر پختہ قول قرار لیا ہے اور اس سے پہلے یوسف کے بارے میں تم کوتاہی کر چکے ہو۔ پس میں تو اس سرزمین سے نہ ٹلوں گا جب تک کہ والد صاحب خود مجھے اجازت نہ دیں یا اللہ تعالیٰ میرے معاملے کا فیصلہ کر دے، وہی بہترین فیصلہ کرنے والا ہے.');
INSERT INTO "junagarri" VALUES(12,81,'12:81','تم سب والد صاحب کی خدمت میں واپس جاؤ اور کہو کہ اباجی! آپ کے صاحبزادے نے چوری کی اور ہم نے وہی گواہی دی تھی جو ہم جانتے تھے ۔ ہم کچھ غیب کی حفاظت کرنے والے نہ تھے. ');
INSERT INTO "junagarri" VALUES(12,82,'12:82','آپ اس شہر کے لوگوں سے دریافت فرما لیں جہاں ہم تھے اور اس قافلہ سے بھی پوچھ لیں جس کے ساتھ ہم آئے ہیں، اور یقیناً ہم بالکل سچے ہیں. ');
INSERT INTO "junagarri" VALUES(12,83,'12:83','(یعقوب علیہ السلام نے) کہا یہ تو نہیں، بلکہ تم نے اپنی طرف سے بات بنالی ، پس اب صبر ہی بہتر ہے۔ قریب ہے کہ اللہ تعالیٰ ان سب کو میرے پاس ہی پہنچا دے ۔ وه ہی علم وحکمت والا ہے.');
INSERT INTO "junagarri" VALUES(12,84,'12:84','پھر ان سے منھ پھیر لیا اور کہا ہائے یوسف ! ان کی آنکھیں بوجہ رنج وغم کے سفید ہو چکی تھیں اور وه غم کو دبائے ہوئے تھے.');
INSERT INTO "junagarri" VALUES(12,85,'12:85','بیٹوں نے کہا واللہ! آپ ہمیشہ یوسف کی یاد ہی میں لگے رہیں گے یہاں تک کہ گھل جائیں یا ختم ہی ہو جائیں. ');
INSERT INTO "junagarri" VALUES(12,86,'12:86','انہوں نے کہا کہ میں تو اپنی پریشانیوں اور رنج کی فریاد اللہ ہی سے کر رہا ہوں، مجھے اللہ کی طرف سے وه باتیں معلوم ہیں جو تم نہیں جانتے. ');
INSERT INTO "junagarri" VALUES(12,87,'12:87','میرے پیارے بچو! تم جاؤ اور یوسف (علیہ السلام) کی اور اس کے بھائی کی پوری طرح تلاش کرو اور اللہ کی رحمت سے ناامید نہ ہو۔ یقیناً رب کی رحمت سے ناامید وہی ہوتے ہیں جو کافر ہوتے ہیں. ');
INSERT INTO "junagarri" VALUES(12,88,'12:88','پھر جب یہ لوگ یوسف (علیہ السلام) کے پاس پہنچے تو کہنے لگے کہ اے عزیز! ہم کو اور ہمارے خانداں کو دکھ پہنچا ہے ۔ ہم حقیر پونجی لائے ہیں پس آپ ہمیں پورے غلہ کا ناپ دیجئے اور ہم پر خیرات کیجئے ، اللہ تعالیٰ خیرات کرنے والوں کو بدلہ دیتا ہے.');
INSERT INTO "junagarri" VALUES(12,89,'12:89','یوسف نے کہا جانتے بھی ہو کہ تم نے یوسف اوراس کے بھائی کے ساتھ اپنی نادانی کی حالت میں کیا کیا؟ ');
INSERT INTO "junagarri" VALUES(12,90,'12:90','انہوں نے کہا کیا (واقعی) تو ہی یوسف (علیہ السلام) ہے ۔ جواب دیا کہ ہاں میں یوسف (علیہ السلام) ہوں اور یہ میرا بھائی ہے۔ اللہ نے ہم پر فضل وکرم کیا، بات یہ ہے کہ جو بھی پرہیز گاری اور صبر کرے تو اللہ تعالیٰ کسی نیکوکار کا اجر ضائع نہیں کرتا. ');
INSERT INTO "junagarri" VALUES(12,91,'12:91','انہوں نے کہا اللہ کی قسم! اللہ تعالیٰ نے تجھے ہم پر برتری دی ہے اور یہ بھی بالکل سچ ہے کہ ہم خطا کار تھے. ');
INSERT INTO "junagarri" VALUES(12,92,'12:92','جواب دیا آج تم پر کوئی ملامت نہیں ہے ۔ اللہ تمہیں بخشے، وه سب مہربانوں سے بڑا مہربان ہے.');
INSERT INTO "junagarri" VALUES(12,93,'12:93','میرا یہ کرتا تم لے جاؤ اور اسے میرے والد کے منھ پر ڈال دو کہ وه دیکھنے لگیں ، اور آجائیں اور اپنے تمام خاندان کو میرے پاس لےآؤ. ');
INSERT INTO "junagarri" VALUES(12,94,'12:94','جب یہ قافلہ جدا ہوا تو ان کے والد نے کہا کہ مجھے تو یوسف کی خوشبو آرہی ہے اگر تم مجھے سٹھیایا ہوا قرار نہ دو. ');
INSERT INTO "junagarri" VALUES(12,95,'12:95','وه کہنے لگے کہ واللہ آپ اپنے اسی پرانے خبط میں مبتلا ہیں.');
INSERT INTO "junagarri" VALUES(12,96,'12:96','جب خوشخبری دینے والے نے پہنچ کر ان کے منھ پر وه کرتا ڈالا اسی وقت وه پھر سے بینا ہوگئے ۔ کہا! کیا میں تم سے نہ کہا کرتا تھا کہ میں اللہ کی طرف سے وه باتیں جانتا ہوں جو تم نہیں جانتے. ');
INSERT INTO "junagarri" VALUES(12,97,'12:97','انہوں نے کہا ابا جی! آپ ہمارے لئے گناہوں کی بخشش طلب کیجئے بیشک ہم قصور وار ہیں.');
INSERT INTO "junagarri" VALUES(12,98,'12:98','کہا اچھا میں جلد ہی تمہارے لئے اپنے پروردگار سے بخشش مانگوں گا ، وه بہت بڑا بخشنے والا اور نہایت مہربانی کرنے ولا ہے.');
INSERT INTO "junagarri" VALUES(12,99,'12:99','جب یہ سارا گھرانہ یوسف کے پاس پہنچ گیا تو یوسف نے اپنے ماں باپ کو اپنے پاس جگہ دی اور کہا کہ اللہ کو منظور ہے تو آپ سب امن وامان کے ساتھ مصر میں آؤ.');
INSERT INTO "junagarri" VALUES(12,100,'12:100','اور اپنے تخت پر اپنے ماں باپ کو اونچا بٹھایا اور سب اس کے سامنے سجدے میں گر گئے ۔ تب کہا کہ اباجی! یہ میرے پہلے کے خواب کی تعبیر ہے میرے رب نے اسے سچا کر دکھایا، اس نے میرے ساتھ بڑا احسان کیا جب کہ مجھے جیل خانے سے نکالا اور آپ لوگوں کو صحرا سے لے آیا اس اختلاف کے بعد جو شیطان نے مجھ میں اور میرے بھائیوں میں ڈال دیا تھا۔ میرا رب جو چاہے اس کے لئے بہترین تدبیر کرنے والا ہے۔ اور وه بہت علم وحکمت والا ہے.');
INSERT INTO "junagarri" VALUES(12,101,'12:101','اے میرے پروردگار! تو نے مجھے ملک عطا فرمایا اور تو نے مجھے خواب کی تعبیر سکھلائی ۔ اے آسمان وزمین کے پیدا کرنے والے! تو ہی دنیا وآخرت میں میرا ولی (دوست) اور کارساز ہے، تو مجھے اسلام کی حالت میں فوت کر اور نیکوں میں ملا دے. ');
INSERT INTO "junagarri" VALUES(12,102,'12:102','یہ غیب کی خبروں میں سے ہے جس کی ہم آپ کی طرف وحی کر رہے ہیں۔ آپ ان کے پاس نہ تھے جب کہ انہوں نے اپنی بات ٹھان لی تھی اور وه فریب کرنے لگے تھے. ');
INSERT INTO "junagarri" VALUES(12,103,'12:103','گو آپ لاکھ چاہیں لیکن اکثر لوگ ایمان دار نہ ہوں گے. ');
INSERT INTO "junagarri" VALUES(12,104,'12:104','آپ ان سے اس پر کوئی اجرت طلب نہیں کر رہے ہیں ۔ یہ تو تمام دنیا کے لئے نری نصیحت ہی نصیحت ہے. ');
INSERT INTO "junagarri" VALUES(12,105,'12:105','آسمانوں اور زمین میں بہت سی نشانیاں ہیں۔ جن سے یہ منھ موڑے گزر جاتے ہیں. ');
INSERT INTO "junagarri" VALUES(12,106,'12:106','ان میں سے اکثر لوگ باوجود اللہ پر ایمان رکھنے کے بھی مشرک ہی ہیں. ');
INSERT INTO "junagarri" VALUES(12,107,'12:107','کیا وه اس بات سے بے خوف ہوگئے ہیں کہ ان کے پاس اللہ کے عذابوں میں سے کوئی عام عذاب آجائے یا ان پر اچانک قیامت ٹوٹ پڑے اور وه بے خبر ہی ہوں.');
INSERT INTO "junagarri" VALUES(12,108,'12:108','آپ کہہ دیجئے میری راه یہی ہے۔ میں اور میرے متبعین اللہ کی طرف بلا رہے ہیں، پورے یقین اور اعتماد کے ساتھ۔ اور اللہ پاک ہے اور میں مشرکوں میں نہیں.');
INSERT INTO "junagarri" VALUES(12,109,'12:109','آپ سے پہلے ہم نے بستی والوں میں جتنے رسول بھیجے ہیں سب مرد ہی تھے جن کی طرف ہم وحی نازل فرماتے گئے ۔ کیا زمین میں چل پھر کر انہوں نے دیکھا نہیں کہ ان سے پہلے کے لوگوں کا کیسا کچھ انجام ہوا؟ یقیناً آخرت کا گھر پرہیزگاروں کے لئے بہت ہی بہتر ہے، کیا پھر بھی تم نہیں سمجھتے.');
INSERT INTO "junagarri" VALUES(12,110,'12:110','یہاں تک کہ جب رسول ناامید ہونے لگے اور وه (قوم کے لوگ) خیال کرنے لگے کہ انہیں جھوٹ کہا گیا ۔ فوراً ہی ہماری مدد ان کے پاس آپہنچی جسے ہم نے چاہا اسے نجات دی گئی ۔ بات یہ ہے کہ ہمارا عذاب گناهگاروں سے واپس نہیں کیا جاتا.');
INSERT INTO "junagarri" VALUES(12,111,'12:111','ان کے بیان میں عقل والوں کے لئے یقیناً نصیحت اور عبرت ہے، یہ قرآن جھوٹ بنائی ہوئی بات نہیں بلکہ یہ تصدیق ہے ان کتابوں کی جو اس سے پہلے کی ہیں، کھول کھول کر بیان کرنے والا ہے ہر چیز کو اور ہدایت اور رحمت ہے ایمان دار لوگوں کے لئے. ');
INSERT INTO "junagarri" VALUES(13,1,'13:1','ا ل م رٰ۔ یہ قرآن کی آیتیں ہیں، اور جو کچھ آپ کی طرف آپ کے رب کی جانب سے اتارا جاتا ہے، سب حق ہے لیکن اکثر لوگ ایمان نہیں لاتے.');
INSERT INTO "junagarri" VALUES(13,2,'13:2','اللہ وه ہے جس نے آسمانوں کو بغیر ستونوں کے بلند کر رکھا ہے کہ تم اسے دیکھ رہے ہو۔ پھر وه عرش پر قرار پکڑے ہوئے ہے ، اسی نے سورج اور چاند کو ماتحتی میں لگا رکھا ہے۔ ہر ایک میعاد معین پر گشت کر رہا ہے ، وہی کام کی تدبیر کرتا ہے وه اپنے نشانات کھول کھول کر بیان کر رہا ہے کہ تم اپنے رب کی ملاقات کا یقین کر لو.');
INSERT INTO "junagarri" VALUES(13,3,'13:3','اسی نے زمین پھیلا کر بچھا دی ہے اور اس میں پہاڑ اور نہریں پیدا کردی ہیں ۔ اور اس میں ہر قسم کے پھلوں کے جوڑے دوہرے دوہرے پیدا کردیئے ہیں ، وه رات کو دن سے چھپا دیتا ہے۔ یقیناً غور وفکر کرنے والوں کے لئے اس میں بہت سی نشانیاں ہیں.');
INSERT INTO "junagarri" VALUES(13,4,'13:4','اور زمین میں مختلف ٹکڑے ایک دوسرے سے لگتے لگاتے ہیں اور انگوروں کے باغات ہیں اور کھیت ہیں اور کھجوروں کے درخت ہیں، شاخ دار اور بعض ایسے ہیں جو بے شاخ ہیں سب ایک ہی پانی پلائے جاتے ہیں۔ پھر بھی ہم ایک کو ایک پر پھلوں میں برتری دیتے ہیں اس میں عقل مندوں کے لئے بہت سی نشانیاں ہیں.');
INSERT INTO "junagarri" VALUES(13,5,'13:5','اگر تجھے تعجب ہو تو واقعی ان کا یہ کہنا عجیب ہے کہ کیا جب ہم مٹی ہو جائیں گے۔ تو کیا ہم نئی پیدائش میں ہوں گے ؟ یہی وه لوگ ہیں جنہوں نے اپنے پروردگار سے کفر کیا۔ یہی ہیں جن کی گردنوں میں طوق ہوں گے اور یہی ہیں جو جہنم کے رہنے والے ہیں جو اس میں ہمیشہ ہمیشہ رہیں گے.');
INSERT INTO "junagarri" VALUES(13,6,'13:6','اور جو تجھ سے (سزا کی طلبی میں) جلد کر رہے ہیں راحت سے پہلے ہی، یقیناً ان سے پہلے سزائیں (بطور مثال) گزر چکی ہیں ، اور بیشک تیرا رب البتہ بخشنے والا ہے لوگوں کے بے جا ظلم پر بھی ۔ اور یہ بھی یقینی بات ہے کہ تیرا رب بڑی سخت سزا دینے والا بھی ہے. ');
INSERT INTO "junagarri" VALUES(13,7,'13:7','اور کافر کہتے ہیں کہ اس پر اس کے رب کی طرف سے کوئی نشانی (معجزه) کیوں نہیں اتاری گئی۔ بات یہ ہے کہ آپ تو صرف آگاه کرنے والے ہیں. ۔ اور ہر قوم کے لئے ہادی ہے. ');
INSERT INTO "junagarri" VALUES(13,8,'13:8','ماده اپنے شکم میں جو کچھ رکھتی ہے اسے اللہ بخوبی جانتا ہے اور پیٹ کا گھٹنا بڑھنا بھی ، ہر چیز اس کے پاس اندازے سے ہے. ');
INSERT INTO "junagarri" VALUES(13,9,'13:9','ظاہر وپوشیده کا وه عالم ہے (سب سے) بڑا اور (سب سے) بلند وبالا.');
INSERT INTO "junagarri" VALUES(13,10,'13:10','تم میں سے کسی کا اپنی بات کو چھپا کر کہنا اور بآواز بلند اسے کہنا اور جو رات کو چھپا ہوا ہو اور جو دن میں چل رہا ہو، سب اللہ پر برابر ویکساں ہیں.');
INSERT INTO "junagarri" VALUES(13,11,'13:11','اس کے پہرے دار انسان کے آگے پیچھے مقرر ہیں، جو اللہ کے حکم سے اس کی نگہبانی کرتے ہیں۔ کسی قوم کی حالت اللہ تعالیٰ نہیں بدلتا جب تک کہ وه خود اسے نہ بدلیں جو ان کے دلوں میں ہے ۔ اللہ تعالیٰ جب کسی قوم کی سزا کا اراده کر لیتا ہے تو وه بدلا نہیں کرتا اور سوائے اس کے کوئی بھی ان کا کارساز نہیں.');
INSERT INTO "junagarri" VALUES(13,12,'13:12','وه اللہ ہی ہے جو تمہیں بجلی کی چمک ڈرانے اور امید دلانے کے لئے دکھاتا ہے اور بھاری بادلوں کو پیدا کرتا ہے. ');
INSERT INTO "junagarri" VALUES(13,13,'13:13','گرج اس کی تسبیح وتعریف کرتی ہے اور فرشتے بھی، اس کے خوف سے ۔ وہی آسمان سے بجلیاں گراتا ہے اور جس پر چاہتا ہے اس پر ڈالتا ہے کفار اللہ کی بابت لڑ جھگڑ رہے ہیں اور اللہ سخت قوت والا ہے. ');
INSERT INTO "junagarri" VALUES(13,14,'13:14','اسی کو پکارنا حق ہے ۔ جو لوگ اوروں کو اس کے سوا پکارتے ہیں وه ان (کی پکار) کا کچھ بھی جواب نہیں دیتے مگر جیسے کوئی شخص اپنے دونوں ہاتھ پانی کی طرف پھیلائے ہوئے ہو کہ اس کے منھ میں پڑ جائے حالانکہ وه پانی اس کے منھ میں پہنچنے والا نہیں ، ان منکروں کی جتنی پکار ہے سب گمراہی میں ہے. ');
INSERT INTO "junagarri" VALUES(13,15,'13:15','اللہ ہی کے لئے زمین اور آسمانوں کی سب مخلوق خوشی اور ناخوشی سے سجده کرتی ہے اور ان کے سائے بھی صبح وشام. ');
INSERT INTO "junagarri" VALUES(13,16,'13:16','آپ پوچھئے کہ آسمانوں اور زمین کا پروردگار کون ہے؟ کہہ دیجئے! اللہ ۔ کہہ دیجئے! کیا تم پھر بھی اس کے سوا اوروں کو حمایتی بنا رہے ہو جو خود اپنی جان کے بھی بھلے برے کا اختیار نہیں رکھتے ۔ کہہ دیجئے کہ کیا اندھا اور بینا برابر ہو سکتا ہے؟ یا کیا اندھیریاں اور روشنی برابر ہو سکتی ہے ۔ کیا جنہیں یہ اللہ کے شریک ٹھہرا رہے ہیں انہوں نے بھی اللہ کی طرح مخلوق پیدا کی ہے کہ ان کی نظر میں پیدائش مشتبہ ہوگئی ہو، کہہ دیجئے کہ صرف اللہ ہی تمام چیزوں کا خالق ہے وه اکیلا ہے اور زبردست غالب ہے.');
INSERT INTO "junagarri" VALUES(13,17,'13:17','اسی نے آسمان سے پانی برسایا پھر اپنی اپنی وسعت کے مطابق نالے بہہ نکلے ۔ پھر پانی کے ریلے نے اوپر چڑھے جھاگ کو اٹھا لیا ، اور اس چیز میں بھی جس کو آگ میں ڈال کر تپاتے ہیں زیور یا ساز وسامان کے لئے اسی طرح کے جھاگ ہیں ، اسی طرح اللہ تعالیٰ حق وباطل کی مثال بیان فرماتا ہے ، اب جھاگ تو ناکاره ہو کر چلا جاتا ہے لیکن جو لوگوں کو نفع دینے والی چیز ہے وه زمین میں ٹھہری رہتی ہے ، اللہ تعالیٰ اسی طرح مثالیں بیان فرماتا ہے. ');
INSERT INTO "junagarri" VALUES(13,18,'13:18','جن لوگوں نے اپنے رب کے حکم کی بجا آوری کی ان کے لئے بھلائی ہے اور جن لوگوں نے اس کی حکم برداری نہ کی اگر ان کے لئے زمین میں جو کچھ ہے سب کچھ ہو اور اسی کے ساتھ ویسا ہی اور بھی ہو تو وه سب کچھ اپنے بدلے میں دے دیں ۔ یہی ہیں جن کے لئے برا حساب ہے اور جن کا ٹھکانہ جہنم ہے جو بہت بری جگہ ہے.');
INSERT INTO "junagarri" VALUES(13,19,'13:19','کیا وه ایک شخص جو یہ علم رکھتا ہو کہ آپ کی طرف آپ کے رب کی جانب سے جو اتارا گیا ہے وه حق ہے، اس شخص جیسا ہو سکتا ہے جو اندھا ہو نصیحت تو وہی قبول کرتے ہیں جو عقلمند ہوں. ');
INSERT INTO "junagarri" VALUES(13,20,'13:20','جو اللہ کے عہد (وپیمان) کو پورا کرتے ہیں اور قول وقرار کو توڑتے نہیں. ');
INSERT INTO "junagarri" VALUES(13,21,'13:21','اور اللہ نے جن چیزوں کے جوڑنے کا حکم دیا ہے وه اسے جوڑتے ہیں اور وه اپنے پروردگار سے ڈرتے ہیں اور حساب کی سختی کا اندیشہ رکھتے ہیں.');
INSERT INTO "junagarri" VALUES(13,22,'13:22','اور وه اپنے رب کی رضا مندی کی طلب کے لئے صبر کرتے ہیں ، اور نمازوں کو برابر قائم رکھتے ہیں اور جو کچھ ہم نے انہیں دے رکھا ہے اسے چھپے کھلے خرچ کرتے ہیں اور برائی کو بھی بھلائی سے ٹالتے ہیں، ان ہی کے لئے عاقبت کا گھر ہے. ');
INSERT INTO "junagarri" VALUES(13,23,'13:23','ہمیشہ رہنے کے باغات جہاں یہ خود جائیں گے اور ان کے باپ دادوں اور بیویوں اور اولادوں میں سے بھی جو نیکو کار ہوں گے ، ان کے پاس فرشتے ہر ہر دروازے سے آئیں گے.');
INSERT INTO "junagarri" VALUES(13,24,'13:24','کہیں گے کہ تم پر سلامتی ہو، صبر کے بدلے، کیا ہی اچھا (بدلہ) ہے اس دار آخرت کا.');
INSERT INTO "junagarri" VALUES(13,25,'13:25','اور جو اللہ کے عہد کو اس کی مضبوطی کے بعد توڑ دیتے ہیں اور جن چیزوں کےجوڑنے کا اللہ نے حکم دیا ہے انہیں توڑتے ہیں اور زمین میں فساد پھیلاتے ہیں، ان کے لئے لعنتیں ہیں اور ان کے لئے برا گھر ہے. ');
INSERT INTO "junagarri" VALUES(13,26,'13:26','اللہ تعالیٰ جس کی روزی چاہتا ہے بڑھاتا ہے اور گھٹاتا ہے یہ تو دنیا کی زندگی میں مست ہو گئے ۔ حالانکہ دنیا آخرت کے مقابلے میں نہایت (حقیر) پونجی ہے. ');
INSERT INTO "junagarri" VALUES(13,27,'13:27','کافر کہتے ہیں کہ اس پر کوئی نشانی (معجزه) کیوں نازل نہیں کیا گیا؟ جواب دے دیجئے کہ جسے اللہ گمراه کرنا چاہے کر دیتا ہے اور جو اس کی طرف جھکے اسے راستہ دکھا دیتا ہے.');
INSERT INTO "junagarri" VALUES(13,28,'13:28','جو لوگ ایمان لائے ان کے دل اللہ کے ذکر سے اطمینان حاصل کرتے ہیں۔ یاد رکھو اللہ کے ذکر سے ہی دلوں کو تسلی حاصل ہوتی ہے. ');
INSERT INTO "junagarri" VALUES(13,29,'13:29','جو لوگ ایمان لائے اور جنہوں نے نیک کام بھی کئے ان کے لئے خوشحالی ہے اور بہترین ٹھکانا.');
INSERT INTO "junagarri" VALUES(13,30,'13:30','اسی طرح ہم نے آپ کو اس امت میں بھیجا ہے جس سے پہلے بہت سی امتیں گزر چکی ہیں کہ آپ انہیں ہماری طرف سے جو وحی آپ پر اتری ہے پڑھ کر سنایئے یہ اللہ رحمٰن کے منکر ہیں ، آپ کہہ دیجئے کہ میرا پالنے والا تو وہی ہے اس کے سوا درحقیقت کوئی بھی لائق عبادت نہیں ، اسی کے اوپر میرا بھروسہ ہے اور اسی کی جانب میرا رجوع ہے.');
INSERT INTO "junagarri" VALUES(13,31,'13:31','اگر (بالفرض) کسی قرآن (آسمانی کتاب) کے ذریعے پہاڑ چلا دیئے جاتے یا زمین ٹکڑے ٹکڑے کر دی جاتی یا مردوں سے باتیں کرا دی جاتیں (پھر بھی وه ایمان نہ لاتے)، بات یہ ہے کہ سب کام اللہ کے ہاتھ میں ہے ، تو کیا ایمان والوں کو اس بات پر دل جمعی نہیں کہ اگر اللہ تعالیٰ چاہے تو تمام لوگوں کو ہدایت دے دے۔ کفار کو تو ان کے کفر کے بدلے ہمیشہ ہی کوئی نہ کوئی سخت سزا پہنچتی رہے گی یا ان کے مکانوں کے قریب نازل ہوتی رہے گی تاوقتیکہ وعدہٴ الٰہی آپہنچے ۔ یقیناً اللہ تعالیٰ وعده خلافی نہیں کرتا.');
INSERT INTO "junagarri" VALUES(13,32,'13:32','یقیناً آپ سے پہلے کے پیغمبروں کا مذاق اڑایا گیا تھا اور میں نے بھی کافروں کو ڈھیل دی تھی پھر انہیں پکڑ لیا تھا، پس میرا عذاب کیسا رہا؟ ');
INSERT INTO "junagarri" VALUES(13,33,'13:33','آیا وه اللہ جو نگہبانی کرنے والا ہے ہر شخص کی، اس کے کئے ہوئے اعمال پر ، ان لوگوں نے اللہ کے شریک ٹھہرائے ہیں کہہ دیجئے ذرا ان کے نام تو لو ، کیا تم اللہ کو وه باتیں بتاتے ہو جو وه زمین میں جانتا ہی نہیں، یا صرف اوپری اوپری باتیں بتا رہے ہو، بات اصل یہ ہے کہ کفر کرنے والوں کے لئے ان کے مکر سجا دیئے گئے ہیں ، اور وه صحیح راه سے روک دیئے گئے ہیں، اور جس کو اللہ گمراه کر دے اس کو راه دکھانے والا کوئی نہیں. ');
INSERT INTO "junagarri" VALUES(13,34,'13:34','ان کے لئے دنیا کی زندگی میں بھی عذاب ہے ، اور آخرت کا عذاب تو بہت ہی زیاده سخت ہے ، انہیں اللہ کے غضب سے بچانے والا کوئی بھی نہیں.');
INSERT INTO "junagarri" VALUES(13,35,'13:35','اس جنت کی صفت، جس کا وعده پرہیزگاروں کو دیا گیا ہے یہ ہے کہ اس کے نیچے سے نہریں بہہ رہی ہیں۔ اس کا میوه ہمیشگی والا ہے اور اس کا سایہ بھی۔ یہ ہے انجام پرہیزگاروں کا ، اور کافروں کا انجام کار دوزخ ہے.');
INSERT INTO "junagarri" VALUES(13,36,'13:36','جنہیں ہم نے کتاب دی ہے وه تو جو کچھ آپ پر اتارا جاتا ہے اس سے خوش ہوتے ہیں اور دوسرے فرقے اس کی بعض باتوں کے منکر ہیں ۔ آپ اعلان کر دیجئے کہ مجھے تو صرف یہی حکم دیا گیا ہے کہ میں اللہ کی عبادت کروں اور اس کے ساتھ شریک نہ کروں، میں اسی کی طرف بلا رہا ہوں اور اسی کی جانب میرا لوٹنا ہے.');
INSERT INTO "junagarri" VALUES(13,37,'13:37','اسی طرح ہم نے اس قرآن کو عربی زبان کا فرمان اتارا ہے ۔ اگر آپ نے ان کی خواہشوں کی پیروی کر لی اس کے بعد کہ آپ کے پاس علم آچکا ہے تو اللہ (کے عذابوں) سے آپ کو کوئی حمایتی ملے گا اور نہ بچانے والا. ');
INSERT INTO "junagarri" VALUES(13,38,'13:38','ہم آپ سے پہلے بھی بہت سے رسول بھیج چکے ہیں اور ہم نے ان سب کو بیوی بچوں والا بنایا تھا ، کسی رسول سے نہیں ہو سکتا کہ کوئی نشانی بغیر اللہ کی اجازت کے لے آئے ۔ ہر مقرره وعدے کی ایک لکھت ہے. ');
INSERT INTO "junagarri" VALUES(13,39,'13:39','اللہ جو چاہے مٹا دے اور جو چاہے ثابت رکھے، لوح محفوظ اسی کے پاس ہے. ');
INSERT INTO "junagarri" VALUES(13,40,'13:40','ان سے کیے ہوئے وعدوں میں سے کوئی اگر ہم آپ کو دکھا دیں یا آپ کو ہم فوت کر لیں تو آپ پر تو صرف پہنچا دینا ہی ہے۔ حساب تو ہمارے ذمہ ہی ہے.');
INSERT INTO "junagarri" VALUES(13,41,'13:41','کیا وه نہیں دیکھتے؟ کہ ہم زمین کو اس کے کناروں سے گھٹاتے چلے آرہے ہیں ، اللہ حکم کرتا ہے کوئی اس کے احکام پیچھے ڈالنے والا نہیں ، وه جلد حساب لینے والا ہے.');
INSERT INTO "junagarri" VALUES(13,42,'13:42','ان سے پہلے لوگوں نے بھی اپنی مکاری میں کمی نہ کی تھی ، لیکن تمام تدبیریں اللہ ہی کی ہیں، جو شخص جو کچھ کر رہا ہے اللہ کے علم میں ہے ۔عنقريب کافروں کو معلوم ہو جائے گا کہ (اس) جہان کی جزا کس کے لئے ہے؟');
INSERT INTO "junagarri" VALUES(13,43,'13:43','یہ کافر کہتے ہیں کہ آپ اللہ کے رسول نہیں۔ آپ جواب دیجئے کہ مجھ میں اور تم میں اللہ گواہی دینے والا کافی ہے اور وه جس کے پاس کتاب کا علم ہے. ');
INSERT INTO "junagarri" VALUES(14,1,'14:1','الرٰ! یہ عالی شان کتاب ہم نے آپ کی طرف اتاری ہے کہ آپ لوگوں کو اندھیروں سے اجالے کی طرف لائیں ، ان کے پروردگار کے حکم سے، زبردست اور تعریفوں والے اللہ کی طرف.');
INSERT INTO "junagarri" VALUES(14,2,'14:2','جس اللہ کا ہے جو کچھ آسمانوں اور زمین میں ہے۔ اور کافروں کے لئے تو سخت عذاب کی خرابی ہے.');
INSERT INTO "junagarri" VALUES(14,3,'14:3','جو آخرت کے مقابلے میں دنیوی زندگی کو پسند رکھتے ہیں اور اللہ کی راه سے روکتے ہیں اور اس میں ٹیڑھ پن پیدا کرنا چاہتے ہیں ۔ یہی لوگ پرلے درجے کی گمراہی میں ہیں. ');
INSERT INTO "junagarri" VALUES(14,4,'14:4','ہم نے ہر ہر نبی کو اس کی قومی زبان میں ہی بھیجا ہے تاکہ ان کے سامنے وضاحت سے بیان کر دے ۔ اب اللہ جسے چاہے گمراه کر دے، اور جسے چاہے راه دکھا دے، وه غلبہ اور حکمت والا ہے. ');
INSERT INTO "junagarri" VALUES(14,5,'14:5','(یاد رکھو جب کہ) ہم نے موسیٰ کو اپنی نشانیاں دے کر بھیجا کہ تو اپنی قوم کو اندھیروں سے روشنی میں نکال اور انہیں اللہ کے احسانات یاد دلا ۔ اس میں نشانیاں ہیں ہر ایک صبر شکر کرنے والے کے لئے. ');
INSERT INTO "junagarri" VALUES(14,6,'14:6','جس وقت موسیٰ نے اپنی قوم سے کہا کہ اللہ کے وه احسانات یاد کرو جو اس نے تم پر کیے ہیں، جبکہ اس نے تمہیں فرعونیوں سے نجات دی جو تمہیں بڑے دکھ پہنچاتے تھے۔ تمہارے لڑکوں کو قتل کرتے تھے اور تمہاری لڑکیوں کو زنده چھوڑتے تھے، اس میں تمہارے رب کی طرف سے تم پر بہت بڑی آزمائش تھی. ');
INSERT INTO "junagarri" VALUES(14,7,'14:7','اور جب تمہارے پروردگار نے تمہیں آگاه کر دیا کہ اگر تم شکر گزاری کرو گے تو بیشک میں تمہیں زیاده دوں گا اور اگر تم ناشکری کرو گے تو یقیناً میرا عذاب بہت سخت ہے. ');
INSERT INTO "junagarri" VALUES(14,8,'14:8','موسیٰ (علیہ السلام) نے کہا کہ اگر تم سب اور روئے زمین کے تمام انسان اللہ کی ناشکری کریں تو بھی اللہ بے نیاز اور تعریفوں والا ہے.');
INSERT INTO "junagarri" VALUES(14,9,'14:9','کیا تمہارے پاس تم سے پہلے کے لوگوں کی خبریں نہیں آئیں؟ یعنی قوم نوح کی اور عاد وثمود کی اور ان کے بعد والوں کی جنہیں سوائے اللہ تعالیٰ کے اور کوئی نہیں جانتا، ان کے پاس ان کے رسول معجزے لائے، لیکن انہوں نے اپنے ہاتھ اپنے منھ میں دبالیے اور صاف کہہ دیا کہ جو کچھ تمہیں دے کر بھیجا گیا ہے ہم اس کے منکر ہیں اور جس چیز کی طرف تم ہمیں بلا رہے ہو ہمیں تو اس میں بڑا بھاری شبہ ہے.');
INSERT INTO "junagarri" VALUES(14,10,'14:10','ان کے رسولوں نے انہیں کہا کہ کیا حق تعالیٰ کے بارے میں تمہیں شک ہے جو آسمانوں اور زمین کا بنانے والا ہے وه تو تمہیں اس لئے بلا رہا ہے کہ تمہارے گناه معاف فرما دے ، اور ایک مقرر وقت تک تمہیں مہلت عطا فرمائے، انہوں نے کہا کہ تم تو ہم جیسے ہی انسان ہو تم چاہتے ہو کہ ہمیں ان خداؤں کی عبادت سے روک دو جن کی عبادت ہمارے باپ دادا کرتے رہے ۔ اچھا تو ہمارے سامنے کوئی کھلی دلیل پیش کرو. ');
INSERT INTO "junagarri" VALUES(14,11,'14:11','ان کے پیغمبروں نے ان سے کہا کہ یہ تو سچ ہے کہ ہم تم جیسے ہی انسان ہیں لیکن اللہ تعالیٰ اپنے بندوں میں سے جس پر چاہتا ہے اپنا فضل کرتا ہے ۔ اللہ کے حکم کے بغیر ہماری مجال نہیں کہ ہم کوئی معجزه تمہیں لا دکھائیں اور ایمان والوں کو صرف اللہ تعالیٰ ہی پر بھروسہ رکھنا چاہئے. ');
INSERT INTO "junagarri" VALUES(14,12,'14:12','آخر کیا وجہ ہے کہ ہم اللہ تعالیٰ پر بھروسہ نہ رکھیں جبکہ اسی نے ہمیں ہماری راہیں سمجھائی ہیں۔ واللہ جو ایذائیں تم ہمیں دو گے ہم ان پر صبر ہی کریں گے۔ توکل کرنے والوں کو یہی لائق ہے کہ اللہ ہی پر توکل کریں. ');
INSERT INTO "junagarri" VALUES(14,13,'14:13','کافروں نے اپنے رسولوں سے کہا کہ ہم تمہیں ملک بدر کر دیں گے یا تم پھر سے ہمارے مذہب میں لوٹ آؤ۔ تو ان کے پروردگار نے ان کی طرف وحی بھیجی کہ ہم ان ظالموں کو ہی غارت کر دیں گے. ');
INSERT INTO "junagarri" VALUES(14,14,'14:14','اور ان کے بعد ہم خود تمہیں اس زمین میں بسائیں گے ۔ یہ ہے ان کے لئے جو میرے سامنے کھڑے ہونے کا ڈر رکھیں اور میری وعید سے خوفزده رہیں. ');
INSERT INTO "junagarri" VALUES(14,15,'14:15','اور انہوں نے فیصلہ طلب کیا اور تمام سرکش ضدی لوگ نامراد ہوگئے.');
INSERT INTO "junagarri" VALUES(14,16,'14:16','اس کے سامنے دوزخ ہے جہاں وه پیپ کا پانی پلایا جائے گا. ');
INSERT INTO "junagarri" VALUES(14,17,'14:17','جسے بمشکل گھونٹ گھونٹ پئے گا۔ پھر بھی اسے گلے سے اتار نہ سکے گا اور اسے ہر جگہ سے موت آتی دکھائی دے گی لیکن وه مرنے والا نہیں ۔ پھر اس کے پیچھے بھی سخت عذاب ہے.');
INSERT INTO "junagarri" VALUES(14,18,'14:18','ان لوگوں کی مثال جنہوں نے اپنے پالنے والے سے کفر کیا، ان کے اعمال مثل اس راکھ کے ہیں جس پر تیز ہوا آندھی والے دن چلے ۔ جو بھی انہوں نے کیا اس میں سے کسی چیز پر قادر نہ ہوں گے، یہی دور کی گمراہی ہے.');
INSERT INTO "junagarri" VALUES(14,19,'14:19','کیا تو نے نہیں دیکھا کہ اللہ تعالیٰ نے آسمانوں کو اور زمین کو بہترین تدبیر کے ساتھ پیدا کیا ہے۔ اگر وه چاہے تو تم سب کو فنا کردے اور نئی مخلوق لائے.');
INSERT INTO "junagarri" VALUES(14,20,'14:20','اللہ پر یہ کام کچھ بھی مشکل نہیں. ');
INSERT INTO "junagarri" VALUES(14,21,'14:21','سب کے سب اللہ کے سامنے روبرو کھڑے ہوں گے ۔ اس وقت کمزور لوگ بڑائی والوں سے کہیں گے کہ ہم تو تمہارے تابعدار تھے، تو کیا تم اللہ کے عذابوں میں سے کچھ عذاب ہم سے دور کرسکنے والے ہو؟ وه جواب دیں گے کہ اگر اللہ ہمیں ہدایت دیتا تو ہم بھی ضرور تمہاری رہنمائی کرتے، اب تو ہم پر بے قراری کرنا اور صبر کرنا دونوں ہی برابر ہے ہمارے لیے کوئی چھٹکارا نہیں. ');
INSERT INTO "junagarri" VALUES(14,22,'14:22','جب اور کام کا فیصلہ کردیا جائے گا تو شیطان کہے گا کہ اللہ نے تو تمہیں سچا وعده دیا تھا اور میں نے تم سے جو وعدے کیے تھے ان کے خلاف کیا ، میرا تم پر کوئی دباؤ تو تھا ہی نہیں ، ہاں میں نے تمہیں پکارا اور تم نے میری مان لی ، پس تم مجھے الزام نہ لگاؤ بلکہ خود اپنے آپ کو ملامت کرو ، نہ میں تمہارا فریاد رس اور نہ تم میری فریاد کو پہنچنے والے ، میں تو سرے سے مانتا ہی نہیں کہ تم مجھے اس سے پہلے اللہ کا شریک مانتے رہے ، یقیناً ظالموں کے لیے دردناک عذاب ہے. ');
INSERT INTO "junagarri" VALUES(14,23,'14:23','جو لوگ ایمان لائے اور نیک عمل کیے وه ان جنتوں میں داخل کیے جائیں گے جن کے نیچے چشمے جاری ہیں جہاں انہیں ہمیشگی ہوگی اپنے رب کے حکم سے ۔ جہاں ان کا خیر مقدم سلام سے ہوگا. ');
INSERT INTO "junagarri" VALUES(14,24,'14:24','کیا آپ نے نہیں دیکھا کہ اللہ تعالیٰ نے پاکیزه بات کی مثال کس طرح بیان فرمائی، مثل ایک پاکیزه درخت کے جس کی جڑ مضبوط ہے اور جس کی ٹہنیاں آسمان میں ہیں.');
INSERT INTO "junagarri" VALUES(14,25,'14:25','جو اپنے پروردگار کے حکم سے ہر وقت اپنے پھل لاتا ہے ، اور اللہ تعالیٰ لوگوں کے سامنے مثالیں بیان فرماتا ہے تاکہ وه نصیحت حاصل کریں.');
INSERT INTO "junagarri" VALUES(14,26,'14:26','اور ناپاک بات کی مثال گندے درخت جیسی ہے جو زمین کے کچھ ہی اوپر سے اکھاڑ لیا گیا۔ اسے کچھ ثبات تو ہے نہیں. ');
INSERT INTO "junagarri" VALUES(14,27,'14:27','ایمان والوں کو اللہ تعالیٰ پکی بات کے ساتھ مضبوط رکھتا ہے، دنیا کی زندگی میں بھی اور آخرت میں بھی ، ہاں ناانصاف لوگوں کو اللہ بہکا دیتا ہے اور اللہ جو چاہے کر گزرے.');
INSERT INTO "junagarri" VALUES(14,28,'14:28','کیا آپ نے ان کی طرف نظر نہیں ڈالی جنہوں نے اللہ کی نعمت کے بدلے ناشکری کی اور اپنی قوم کو ہلاکت کے گھر میں لا اتارا. ');
INSERT INTO "junagarri" VALUES(14,29,'14:29','یعنی دوزخ میں جس میں یہ سب جائیں گے، جو بدترین ٹھکانا ہے.');
INSERT INTO "junagarri" VALUES(14,30,'14:30','انہوں نے اللہ کے ہمسر بنالیے کہ لوگوں کو اللہ کی راه سے بہکائیں۔ آپ کہہ دیجئے کہ خیر مزے کرلو تمہاری بازگشت تو آخر جہنم ہی ہے. ');
INSERT INTO "junagarri" VALUES(14,31,'14:31','میرے ایمان والے بندوں سے کہہ دیجئے کہ نمازوں کو قائم رکھیں اور جو کچھ ہم نے انہیں دے رکھا ہے اس میں سے کچھ نہ کچھ پوشیده اور ظاہر خرچ کرتے رہیں اس سے پہلے کہ وه دن آجائے جس میں نہ خرید وفروخت ہوگی نہ دوستی اور محبت. ');
INSERT INTO "junagarri" VALUES(14,32,'14:32','اللہ وه ہے جس نے آسمانوں اور زمین کو پیدا کیا ہے اور آسمانوں سے بارش برسا کر اس کے ذریعے سے تمہاری روزی کے لیے پھل نکالے ہیں اور کشتیوں کو تمہارے بس میں کردیا ہے کہ دریاؤں میں اس کے حکم سے چلیں پھریں۔ اسی نے ندیاں اور نہریں تمہارے اختیار میں کردی ہیں. ');
INSERT INTO "junagarri" VALUES(14,33,'14:33','اسی نے تمہارے لیے سورج چاند کو مسخر کردیا ہے کہ برابر ہی چل رہے ہیں اور رات دن کو بھی تمہارے کام میں لگا رکھا ہے. ');
INSERT INTO "junagarri" VALUES(14,34,'14:34','اسی نے تمہیں تمہاری منہ مانگی کل چیزوں میں سے دے رکھا ہے ۔ اگر تم اللہ کے احسان گننا چاہو تو انہیں پورے گن بھی نہیں سکتے ۔ یقیناً انسان بڑا ہی بے انصاف اور ناشکرا ہے. ');
INSERT INTO "junagarri" VALUES(14,35,'14:35','(ابراہیم کی یہ دعا بھی یاد کرو) جب انہوں نے کہا کہ اے میرے پروردگار! اس شہر کو امن والا بنادے ، اور مجھے اور میری اولاد کو بت پرستی سے پناه دے.');
INSERT INTO "junagarri" VALUES(14,36,'14:36','اے میرے پالنے والے معبود! انہوں نے بہت سے لوگوں کو راه سے بھٹکا دیا ہے ۔ پس میری تابعداری کرنے والا میرا ہے اور جو میری نافرمانی کرے تو تو بہت ہی معاف اور کرم کرنے والا ہے.');
INSERT INTO "junagarri" VALUES(14,37,'14:37','اے ہمارے پروردگار! میں نے اپنی کچھ اولاد اس بے کھیتی کی وادی میں تیرے حرمت والے گھر کے پاس بسائی ہے۔ اے ہمارے پروردگار! یہ اس لیے کہ وه نماز قائم رکھیں ، پس تو کچھ لوگوں کے دلوں کو ان کی طرف مائل کردے۔ اور انہیں پھلوں کی روزیاں عنایت فرما تاکہ یہ شکر گزاری کریں.');
INSERT INTO "junagarri" VALUES(14,38,'14:38','اے ہمارے پروردگار! تو خوب جانتا ہے جو ہم چھپائیں اور جو ظاہر کریں۔ زمین و آسمان کی کوئی چیز اللہ پر پوشیده نہیں. ');
INSERT INTO "junagarri" VALUES(14,39,'14:39','اللہ کا شکر ہے جس نے مجھے اس بڑھاپے میں اسماعیل واسحاق (علیہما السلام) عطا فرمائے۔ کچھ شک نہیں کہ میرا پالنہار اللہ دعاؤں کا سننے والا ہے.');
INSERT INTO "junagarri" VALUES(14,40,'14:40','اے میرے پالنے والے! مجھے نماز کا پابند رکھ اور میری اولاد سے بھی ، اے ہمارے رب میری دعا قبول فرما.');
INSERT INTO "junagarri" VALUES(14,41,'14:41','اے ہمارے پروردگار! مجھے بخش دے اور میرے ماں باپ کو بھی بخش اور دیگر مومنوں کو بھی بخش جس دن حساب ہونے لگے.');
INSERT INTO "junagarri" VALUES(14,42,'14:42','ناانصافوں کے اعمال سے اللہ کو غافل نہ سمجھ وه تو انہیں اس دن تک مہلت دیے ہوئے ہے جس دن آنکھیں پھٹی کی پھٹی ره جائیں گی. ');
INSERT INTO "junagarri" VALUES(14,43,'14:43','وه اپنے سر اوپر اٹھائے دوڑ بھاگ کر رہے ہوں گے ، خود اپنی طرف بھی ان کی نگاہیں نہ لوٹیں گی اور ان کے دل خالی اور اڑے ہوئے ہوں گے. ');
INSERT INTO "junagarri" VALUES(14,44,'14:44','لوگوں کو اس دن سے ہوشیار کردے جب کہ ان کے پاس عذاب آجائے گا، اور ظالم کہیں گے کہ اے ہمارے رب ہمیں بہت تھوڑے قریب کے وقت تک کی ہی مہلت دے کہ ہم تیری تبلیغ مان لیں اور تیرے پیغمبروں کی تابعداری میں لگ جائیں۔ کیا تم اس سے پہلے بھی قسمیں نہیں کھا رہے تھے؟ کہ تمہارے لیے دنیا سے ٹلنا ہی نہیں. ');
INSERT INTO "junagarri" VALUES(14,45,'14:45','اور کیا تم ان لوگوں کے گھروں میں رہتے سہتے نہ تھے جنہوں نے اپنی جانوں پر ظلم کیا اور کیا تم پر وه معاملہ کھلا نہیں کہ ہم نے ان کے ساتھ کیسا کچھ کیا۔ ہم نے تو (تمہارے سمجھانے کو) بہت سی مثالیں بیان کردی تھیں. ');
INSERT INTO "junagarri" VALUES(14,46,'14:46','یہ اپنی اپنی چالیں چل رہے ہیں اور اللہ کو ان کی تمام چالوں کا علم ہے اور ان کی چالیں ایسی نہ تھیں کہ ان سے پہاڑ اپنی جگہ سے ٹل جائیں. ');
INSERT INTO "junagarri" VALUES(14,47,'14:47','آپ ہرگز یہ خیال نہ کریں کہ اللہ اپنے نبیوں سے وعده خلافی کرے گا ، اللہ بڑا ہی غالب اور بدلہ لینے والا ہے. ');
INSERT INTO "junagarri" VALUES(14,48,'14:48','جس دن زمین اس زمین کے سوا اور ہی بدل دی جائے گی اور آسمان بھی ، اور سب کے سب اللہ واحد غلبے والے کے روبرو ہوں گے.');
INSERT INTO "junagarri" VALUES(14,49,'14:49','آپ اس دن گناه گاروں کو دیکھیں گے کہ زنجیروں میں ملے جلے ایک جگہ جکڑے ہوئے ہوں گے.');
INSERT INTO "junagarri" VALUES(14,50,'14:50','ان کے لباس گندھک کے ہوں گے اور آگ ان کے چہروں پر بھی چڑھی ہوئی ہوگی.');
INSERT INTO "junagarri" VALUES(14,51,'14:51','یہ اس لیے کہ اللہ تعالیٰ ہر شخص کو اس کے کیے ہوئے اعمال کا بدلہ دے، بیشک اللہ تعالیٰ کو حساب لیتے کچھ دیر نہیں لگنے کی.');
INSERT INTO "junagarri" VALUES(14,52,'14:52','یہ قرآن تمام لوگوں کے لیے اطلاع نامہ ہے کہ اس کے ذریعہ سے وه ہوشیار کر دیے جائیں اور بخوبی معلوم کرلیں کہ اللہ ایک ہی معبود ہے اور تاکہ عقلمند لوگ سوچ سمجھ لیں.');
INSERT INTO "junagarri" VALUES(15,1,'15:1','الرٰ، یہ کتاب الٰہی کی آیتیں ہیں اور کھلے اور روشن قرآن کی. ');
INSERT INTO "junagarri" VALUES(15,2,'15:2','وه بھی وقت ہوگا کہ کافر اپنے مسلمان ہونے کی آرزو کریں گے. ');
INSERT INTO "junagarri" VALUES(15,3,'15:3','آپ انہیں کھاتا، نفع اٹھاتا اور (جھوٹی) امیدوں میں مشغول ہوتا چھوڑ دیجئے عنقریب یہ خود جان لیں گے. ');
INSERT INTO "junagarri" VALUES(15,4,'15:4','کسی بستی کو ہم نے ہلاک نہیں کیا مگر یہ کہ اس کے لیے مقرره نوشتہ تھا.');
INSERT INTO "junagarri" VALUES(15,5,'15:5','کوئی گروه اپنی موت سے نہ آگے بڑھتا ہے نہ پیچھے رہتا ہے. ');
INSERT INTO "junagarri" VALUES(15,6,'15:6','انہوں نے کہا کہ اے وه شخص جس پر قرآن اتارا گیا ہے یقیناً تو تو کوئی دیوانہ ہے.');
INSERT INTO "junagarri" VALUES(15,7,'15:7','اگر تو سچا ہی ہے تو ہمارے پاس فرشتوں کو کیوں نہیں لاتا. ');
INSERT INTO "junagarri" VALUES(15,8,'15:8','ہم فرشتوں کو حق کے ساتھ ہی اتارتے ہیں اور اس وقت وه مہلت دیئے گئے نہیں ہوتے. ');
INSERT INTO "junagarri" VALUES(15,9,'15:9','ہم نے ہی اس قرآن کو نازل فرمایا ہے اور ہم ہی اس کے محافظ ہیں. ');
INSERT INTO "junagarri" VALUES(15,10,'15:10','ہم نے آپ سے پہلے اگلی امتوں میں بھی اپنے رسول (برابر) بھیجے.');
INSERT INTO "junagarri" VALUES(15,11,'15:11','اور (لیکن) جو بھی رسول آتا وه اس کا مذاق اڑاتے. ');
INSERT INTO "junagarri" VALUES(15,12,'15:12','گناه گاروں کے دلوں میں ہم اسی طرح یہی رچا دیا کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(15,13,'15:13','وه اس پر ایمان نہیں لاتے اور یقیناً اگلوں کا طریقہ گزرا ہوا ہے. ');
INSERT INTO "junagarri" VALUES(15,14,'15:14','اور اگر ہم ان پر آسمان کا دروازه کھول بھی دیں اور یہ وہاں چڑھنے بھی لگ جائیں.');
INSERT INTO "junagarri" VALUES(15,15,'15:15','تب بھی یہی کہیں گے کہ ہماری نظر بندی کر دی گئی ہے بلکہ ہم لوگوں پر جادو کر دیا گیا ہے. ');
INSERT INTO "junagarri" VALUES(15,16,'15:16','یقیناً ہم نے آسمان میں برج بنائے ہیں اور دیکھنے والوں کے لیے اسے سجا دیا ہے.');
INSERT INTO "junagarri" VALUES(15,17,'15:17','اور اسے ہر مردود شیطان سے محفوظ رکھا ہے. ');
INSERT INTO "junagarri" VALUES(15,18,'15:18','ہاں مگر جو چوری چھپے سننے کی کوشش کرے اس کے پیچھے دہکتا ہوا (کھلا شعلہ) لگتا ہے. ');
INSERT INTO "junagarri" VALUES(15,19,'15:19','اور زمین کو ہم نے پھیلا دیا ہے اور اس پر (اٹل) پہاڑ ڈال دیئے ہیں، اور اس میں ہم نے ہر چیز ایک معین مقدار سے اگا دی ہے. ');
INSERT INTO "junagarri" VALUES(15,20,'15:20','اور اسی میں ہم نے تمہاری روزیاں بنا دی ہیں اور جنہیں تم روزی دینے والے نہیں ہو. ');
INSERT INTO "junagarri" VALUES(15,21,'15:21','اور جتنی بھی چیزیں ہیں ان سب کے خزانے ہمارے پاس ہیں ، اور ہم ہر چیز کو اس کے مقرره انداز سے اتارتے ہیں.');
INSERT INTO "junagarri" VALUES(15,22,'15:22','اور ہم بھیجتے ہیں بوجھل ہوائیں ، پھر آسمان سے پانی برسا کر وه تمہیں پلاتے ہیں اور تم اس کا ذخیره کرنے والے نہیں ہو. ');
INSERT INTO "junagarri" VALUES(15,23,'15:23','ہم ہی جلاتے اور مارتے ہیں اور ہم ہی (بالآخر) وارث ہیں.');
INSERT INTO "junagarri" VALUES(15,24,'15:24','اور تم میں سے آگے بڑھنے والے اور پیچھے ہٹنے والے بھی ہمارے علم میں ہیں.');
INSERT INTO "junagarri" VALUES(15,25,'15:25','آپ کا رب سب لوگوں کو جمع کرے گا یقیناً وه بڑی حکمتوں والا بڑے علم والا ہے.');
INSERT INTO "junagarri" VALUES(15,26,'15:26','یقیناً ہم نے انسان کو کالی اور سڑی ہوئی کھنکھناتی مٹی سے، پیدا فرمایا ہے. ');
INSERT INTO "junagarri" VALUES(15,27,'15:27','اور اس سے پہلے جنات کو ہم نے لو والی آگ سے پیدا کیا.');
INSERT INTO "junagarri" VALUES(15,28,'15:28','اور جب تیرے پروردگار نے فرشتوں سے فرمایا کہ میں ایک انسان کو کالی اور سڑی ہوئی کھنکھناتی مٹی سے پیدا کرنے والا ہوں.');
INSERT INTO "junagarri" VALUES(15,29,'15:29','تو جب میں اسے پورا بنا چکوں اور اس میں اپنی روح پھونک دوں تو تم سب اس کے لیے سجدے میں گر پڑنا. ');
INSERT INTO "junagarri" VALUES(15,30,'15:30','چنانچہ تمام فرشتوں نے سب کے سب نے سجده کر لیا.');
INSERT INTO "junagarri" VALUES(15,31,'15:31','مگر ابلیس کے۔ کہ اس نے سجده کرنے والوں میں شمولیت کرنے سے (صاف) انکار کر دیا.');
INSERT INTO "junagarri" VALUES(15,32,'15:32','(اللہ تعالیٰ نے) فرمایا اے ابلیس تجھے کیا ہوا کہ تو سجده کرنے والوں میں شامل نہ ہوا؟');
INSERT INTO "junagarri" VALUES(15,33,'15:33','وه بولا کہ میں ایسا نہیں کہ اس انسان کو سجده کروں جسے تو نے کالی اور سڑی ہوئی کھنکھناتی مٹی سے پیدا کیا ہے. ');
INSERT INTO "junagarri" VALUES(15,34,'15:34','فرمایا اب تو بہشت سے نکل جا کیوں کہ تو رانده درگاه ہے.');
INSERT INTO "junagarri" VALUES(15,35,'15:35','اور تجھ پر میری پھٹکار ہے قیامت کے دن تک.');
INSERT INTO "junagarri" VALUES(15,36,'15:36','کہنے لگا کہ اے میرے رب! مجھے اس دن تک کی ڈھیل دے کہ لوگ دوباره اٹھا کھڑے کیے جائیں.');
INSERT INTO "junagarri" VALUES(15,37,'15:37','فرمایا کہ اچھا تو ان میں سے ہے جنہیں مہلت ملی ہے.');
INSERT INTO "junagarri" VALUES(15,38,'15:38','روز مقرر کے وقت تک کی.');
INSERT INTO "junagarri" VALUES(15,39,'15:39','(شیطان نے) کہا کہ اے میرے رب! چونکہ تو نے مجھے گمراه کیا ہے مجھے بھی قسم ہے کہ میں بھی زمین میں ان کے لئے معاصی کو مزین کروں گا اور ان سب کو بہکاؤں گا بھی.');
INSERT INTO "junagarri" VALUES(15,40,'15:40','سوائے تیرے ان بندوں کے جو منتخب کر لیے گئے ہیں.');
INSERT INTO "junagarri" VALUES(15,41,'15:41','ارشاد ہوا کہ ہاں یہی مجھ تک پہنچنے کی سیدھی راه ہے. ');
INSERT INTO "junagarri" VALUES(15,42,'15:42','میرے بندوں پر تجھے کوئی غلبہ نہیں ، لیکن ہاں جو گمراه لوگ تیری پیروی کریں.');
INSERT INTO "junagarri" VALUES(15,43,'15:43','یقیناً ان سب کے وعدے کی جگہ جہنم ہے. ');
INSERT INTO "junagarri" VALUES(15,44,'15:44','جس کے سات دروازے ہیں۔ ہر دروازے کے لیے ان کا ایک حصہ بٹا ہوا ہے. ');
INSERT INTO "junagarri" VALUES(15,45,'15:45','پرہیزگار جنتی لوگ باغوں اور چشموں میں ہوں گے. ');
INSERT INTO "junagarri" VALUES(15,46,'15:46','(ان سے کہا جائے گا) سلامتی اور امن کے ساتھ اس میں داخل ہو جاؤ. ');
INSERT INTO "junagarri" VALUES(15,47,'15:47','ان کے دلوں میں جو کچھ رنجش وکینہ تھا، ہم سب کچھ نکال دیں گے ، وه بھائی بھائی بنے ہوئے ایک دوسرے کے آمنے سامنے تختوں پر بیٹھے ہوں گے.');
INSERT INTO "junagarri" VALUES(15,48,'15:48','نہ تو وہاں انہیں کوئی تکلیف چھو سکتی ہے اور نہ وه وہاں سے کبھی نکالے جائیں گے.');
INSERT INTO "junagarri" VALUES(15,49,'15:49','میرے بندوں کو خبر دے دو کہ میں بہت ہی بخشنے والا اور بڑا ہی مہربان ہوں.');
INSERT INTO "junagarri" VALUES(15,50,'15:50','اور ساتھ ہی میرے عذاب بھی نہایت دردناک ہیں.');
INSERT INTO "junagarri" VALUES(15,51,'15:51','انہیں ابراہیم کے مہمانوں کا (بھی) حال سنا دو.');
INSERT INTO "junagarri" VALUES(15,52,'15:52','کہ جب انہوں نے ان کے پاس آکر سلام کہا تو انہوں نے کہا کہ ہم کو تو تم سے ڈر لگتا ہے. ');
INSERT INTO "junagarri" VALUES(15,53,'15:53','انہوں نے کہا ڈرو نہیں، ہم تجھے ایک صاحب علم فرزند کی بشارت دیتے ہیں.');
INSERT INTO "junagarri" VALUES(15,54,'15:54','کہا، کیا اس بڑھاپے کے آجانے کے بعد تم مجھے خوشخبری دیتے ہو! یہ خوشخبری تم کیسے دے رہے ہو؟');
INSERT INTO "junagarri" VALUES(15,55,'15:55','انہوں نے کہا ہم آپ کو بالکل سچی خوشخبری سناتے ہیں آپ مایوس لوگوں میں شامل نہ ہوں. ');
INSERT INTO "junagarri" VALUES(15,56,'15:56','کہا اپنے رب تعالیٰ کی رحمت سے ناامید تو صرف گمراه اور بہکے ہوئے لوگ ہی ہوتے ہیں. ');
INSERT INTO "junagarri" VALUES(15,57,'15:57','پوچھا کہ اللہ کے بھیجے ہوئے (فرشتو!) تمہارا ایسا کیا اہم کام ہے؟ ');
INSERT INTO "junagarri" VALUES(15,58,'15:58','انہوں نے جواب دیا کہ ہم مجرم قوم کی طرف بھیجے گئے ہیں.');
INSERT INTO "junagarri" VALUES(15,59,'15:59','مگر خاندان لوط کہ ہم ان سب کو تو ضرور بچا لیں گے.');
INSERT INTO "junagarri" VALUES(15,60,'15:60','سوائے اس (لوط) کی بیوی کے کہ ہم نے اسے رکنے اور باقی ره جانے والوں میں مقرر کر دیا ہے.');
INSERT INTO "junagarri" VALUES(15,61,'15:61','جب بھیجے ہوئے فرشتے آل لوط کے پاس پہنچے.');
INSERT INTO "junagarri" VALUES(15,62,'15:62','تو انہوں (لوط علیہ السلام) نے کہا تم لوگ تو کچھ انجان سے معلوم ہو رہے ہو. ');
INSERT INTO "junagarri" VALUES(15,63,'15:63','انہوں نے کہا نہیں بلکہ ہم تیرے پاس وه چیز لائے ہیں جس میں یہ لوگ شک شبہ کر رہے تھے. ');
INSERT INTO "junagarri" VALUES(15,64,'15:64','ہم تو تیرے پاس (صریح) حق لائے ہیں اور ہیں بھی بالکل سچے. ');
INSERT INTO "junagarri" VALUES(15,65,'15:65','اب تو اپنے خاندان سمیت اس رات کے کسی حصہ میں چل دے اور آپ ان کے پیچھے رہنا ، اور (خبردار) تم میں سے کوئی (پیچھے) مڑکر بھی نہ دیکھے اور جہاں کا تمہیں حکم کیا جارہا ہے وہاں چلے جان.');
INSERT INTO "junagarri" VALUES(15,66,'15:66','اور ہم نے اس کی طرف اس بات کا فیصلہ کر دیا کہ صبح ہوتے ہوتے ان لوگوں کی جڑیں کاٹ دی جائیں گی. ');
INSERT INTO "junagarri" VALUES(15,67,'15:67','اور شہر والے خوشیاں مناتے ہوئے آئے. ');
INSERT INTO "junagarri" VALUES(15,68,'15:68','(لوط علیہ السلام نے) کہا یہ لوگ میرے مہمان ہیں تم مجھے رسوا نہ کرو. ');
INSERT INTO "junagarri" VALUES(15,69,'15:69','اللہ تعالیٰ سے ڈرو اور مجھے رسوا نہ کرو.');
INSERT INTO "junagarri" VALUES(15,70,'15:70','وه بولے کیا ہم نے تجھے دنیا بھر (کی ٹھیکیداری) سے منع نہیں کر رکھا؟ ');
INSERT INTO "junagarri" VALUES(15,71,'15:71','(لوط علیہ السلام نے) کہا اگر تمہیں کرنا ہی ہے تو یہ میری بچیاں موجود ہیں. ');
INSERT INTO "junagarri" VALUES(15,72,'15:72','تیری عمر کی قسم! وه تو اپنی بدمستی میں سرگرداں تھے. ');
INSERT INTO "junagarri" VALUES(15,73,'15:73','پس سورج نکلتے نکلتے انہیں ایک بڑے زور کی آواز نے پکڑ لیا. ');
INSERT INTO "junagarri" VALUES(15,74,'15:74','بالآخر ہم نے اس شہر کو اوپر تلے کر دیا اور ان لوگوں پر کنکر والے پتھر برسائے.');
INSERT INTO "junagarri" VALUES(15,75,'15:75','بلاشبہ بصیرت والوں کے لیے اس میں بہت سی نشانیاں ہیں.');
INSERT INTO "junagarri" VALUES(15,76,'15:76','یہ بستی ایسی راه پر ہے جو برابر چلتی رہتی (عام گذرگاه) ہے. ');
INSERT INTO "junagarri" VALUES(15,77,'15:77','اور اس میں ایمان والوں کے لیے بڑی نشانی ہے.');
INSERT INTO "junagarri" VALUES(15,78,'15:78','اور بے شک اَیکَہ بستی کے رہنے والے يقيناً ظالم تھے. ');
INSERT INTO "junagarri" VALUES(15,79,'15:79','جن سے (آخر) ہم نے انتقام لے ہی لیا۔ یہ دونوں شہر کھلے (عام) راستے پر ہیں. ');
INSERT INTO "junagarri" VALUES(15,80,'15:80','اور حِجر والوں نے بھی رسولوں کو جھٹلایا. ');
INSERT INTO "junagarri" VALUES(15,81,'15:81','اور ہم نے ان کو اپنی نشانیاں بھی عطا فرمائیں (لیکن) تاہم وه ان سے روگردانی ہی کرتے رہے. ');
INSERT INTO "junagarri" VALUES(15,82,'15:82','یہ لوگ پہاڑوں کو تراش تراش کر گھر بناتے تھے، بے خوف ہوکر. ');
INSERT INTO "junagarri" VALUES(15,83,'15:83','آخر انہیں بھی صبح ہوتے ہوتے چنگھاڑنے آدبوچا. ');
INSERT INTO "junagarri" VALUES(15,84,'15:84','پس ان کی کسی تدبیروعمل نے انہیں کوئی فائده نہ دیا.');
INSERT INTO "junagarri" VALUES(15,85,'15:85','ہم نے آسمانوں اور زمین کو اور ان کے درمیان کی سب چیزوں کو حق کے ساتھ ہی پیدا فرمایا ہے ، اور قیامت ضرور ضرور آنے والی ہے۔ پس تو حسن وخوبی (اور اچھائی) سے درگزر کر لے.');
INSERT INTO "junagarri" VALUES(15,86,'15:86','یقیناً تیرا پروردگار ہی پیدا کرنے والا اور جاننے والا ہے.');
INSERT INTO "junagarri" VALUES(15,87,'15:87','یقیناً ہم نے آپ کو سات آیتیں دے رکھی ہیں. کہ دہرائی جاتی ہیں اور عظیم قرآن بھی دے رکھا ہے.');
INSERT INTO "junagarri" VALUES(15,88,'15:88','آپ ہر گز اپنی نظریں اس چیز کی طرف نہ دوڑائیں، جس سے ہم نے ان میں سے کئی قسم کے لوگوں کو بہره مند کر رکھا ہے، نہ ان پر آپ افسوس کریں اور مومنوں کے لیے اپنے بازو جھکائے رہیں. ');
INSERT INTO "junagarri" VALUES(15,89,'15:89','اور کہہ دیجئے کہ میں تو کھلم کھلا ڈرانے والا ہوں.');
INSERT INTO "junagarri" VALUES(15,90,'15:90','جیسے کہ ہم نے ان تقسیم کرنے والوں پر اتارا. ');
INSERT INTO "junagarri" VALUES(15,91,'15:91','جنہوں نے اس کتاب الٰہی کے ٹکڑے ٹکڑے کر دیئے.');
INSERT INTO "junagarri" VALUES(15,92,'15:92','قسم ہے تیرے پالنے والے کی! ہم ان سب سے ضرور باز پرس کریں گے.');
INSERT INTO "junagarri" VALUES(15,93,'15:93','ہر اس چیز کی جو وه کرتے تھے.');
INSERT INTO "junagarri" VALUES(15,94,'15:94','پس آپ اس حکم کو جو آپ کو کیا جارہا ہے کھول کر سنا دیجئے! اور مشرکوں سے منھ پھیر لیجئے.');
INSERT INTO "junagarri" VALUES(15,95,'15:95','آپ سے جو لوگ مسخراپن کرتے ہیں ان کی سزا کے لیے ہم کافی ہیں.');
INSERT INTO "junagarri" VALUES(15,96,'15:96','جو اللہ کے ساتھ دوسرے معبود مقرر کرتے ہیں انہیں عنقریب معلوم ہو جائے گا.');
INSERT INTO "junagarri" VALUES(15,97,'15:97','ہمیں خوب علم ہے کہ ان کی باتوں سے آپ کا دل تنگ ہوتا ہے.');
INSERT INTO "junagarri" VALUES(15,98,'15:98','آپ اپنے پروردگار کی تسبیح اور حمد بیان کرتے رہیں اور سجده کرنے والوں میں شامل ہو جائیں.');
INSERT INTO "junagarri" VALUES(15,99,'15:99','اور اپنے رب کی عبادت کرتے رہیں یہاں تک کہ آپ کو موت آجائے. ');
INSERT INTO "junagarri" VALUES(16,1,'16:1','اللہ تعالیٰ کا حکم آپہنچا، اب اس کی جلدی نہ مچاؤ ۔ تمام پاکی اس کے لیے ہے وه برتر ہے ان سب سے جنہیں یہ اللہ کے نزدیک شریک بتلاتے ہیں.');
INSERT INTO "junagarri" VALUES(16,2,'16:2','وہی فرشتوں کو اپنی وحی دے کر اپنے حکم سے اپنے بندوں میں سے جس پر چاہتا ہے اتارتا ہے کہ تم لوگوں کو آگاه کر دو کہ میرے سوا اور کوئی معبود نہیں، پس تم مجھ سے ڈرو.');
INSERT INTO "junagarri" VALUES(16,3,'16:3','اسی نے آسمانوں اور زمین کو حق کے ساتھ پیدا کیا وه اس سے بری ہے جو مشرک کرتے ہیں.');
INSERT INTO "junagarri" VALUES(16,4,'16:4','اس نے انسان کو نطفے سے پیدا کیا پھر وه صریح جھگڑالو بن بیٹھا. ');
INSERT INTO "junagarri" VALUES(16,5,'16:5','اسی نے چوپائے پیدا کیے جن میں تمہارے لیے گرمی کے لباس ہیں اور بھی بہت سے نفع ہیں اور بعض تمہارے کھانے کے کام آتے ہیں.');
INSERT INTO "junagarri" VALUES(16,6,'16:6','اور ان میں تمہاری رونق بھی ہے جب چرا کر لاؤ تب بھی اور جب چرانے لے جاؤ تب بھی. ');
INSERT INTO "junagarri" VALUES(16,7,'16:7','اور وه تمہارے بوجھ ان شہروں تک اٹھالے جاتے ہیں جہاں تم بغیر آدھی جان کیے پہنچ ہی نہیں سکتے تھے۔ یقیناً تمہارا رب بڑا ہی شفیق اور نہایت مہربان ہے.');
INSERT INTO "junagarri" VALUES(16,8,'16:8','گھوڑوں کو، خچروں کو، گدھوں کو اس نے پیدا کیا کہ تم ان کی سواری لو اور وه باعث زینت بھی ہیں ۔ اور بھی وه ایسی بہت چیزیں پیدا کرتا ہے جن کا تمہیں علم بھی نہیں. ');
INSERT INTO "junagarri" VALUES(16,9,'16:9','اور اللہ پر سیدھی راه کا بتا دینا ہے اور بعض ٹیڑھی راہیں ہیں، اور اگر وه چاہتا تو تم سب کو راه راست پر لگا دیتا. ');
INSERT INTO "junagarri" VALUES(16,10,'16:10','وہی تمہارے فائدے کے لیے آسمان سے پانی برساتا ہے جسے تم پیتے بھی ہو اور اسی سے اگے ہوئے درختوں کو تم اپنے جانوروں کو چراتے ہو.');
INSERT INTO "junagarri" VALUES(16,11,'16:11','اسی سے وه تمہارے لیے کھیتی اور زیتون اور کھجور اور انگور اور ہر قسم کے پھل اگاتا ہے بےشک ان لوگوں کے لیے تو اس میں بڑی نشانی ہے جو غوروفکر کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(16,12,'16:12','اسی نے رات دن اور سورج چاند کو تمہارے لیے تابع کر دیا ہے اور ستارے بھی اسی کے حکم کے ماتحت ہیں۔ یقیناًاس میں عقلمند لوگوں کے لیے کئی ایک نشانیاں موجود ہیں. ');
INSERT INTO "junagarri" VALUES(16,13,'16:13','اور بھی بہت سی چیزیں طرح طرح کے رنگ روپ کی اس نے تمہارے لیے زمین پر پھیلا رکھی ہیں۔ بیشک نصیحت قبول کرنے والوں کے لیے اس میں بڑی بھاری نشانی ہے. ');
INSERT INTO "junagarri" VALUES(16,14,'16:14','اور دریا بھی اسی نے تمہارے بس میں کر دیے ہیں کہ تم اس میں سے (نکلا ہوا) تازه گوشت کھاؤ اور اس میں سے اپنے پہننے کے زیورات نکال سکو اور تم دیکھتے ہو کہ کشتیاں اس میں پانی چیرتی ہوئی (چلتی) ہیں اور اس لیے بھی کہ تم اس کا فضل تلاش کرو اور ہوسکتا ہے کہ تم شکر گزاری بھی کرو. ');
INSERT INTO "junagarri" VALUES(16,15,'16:15','اور اس نے زمین میں پہاڑ گاڑ دیے ہیں تاکہ تمہیں لے کر ہلے نہ ، اور نہریں اور راہیں بنا دیں تاکہ تم منزل مقصود کو پہنچو. ');
INSERT INTO "junagarri" VALUES(16,16,'16:16','اور بھی بہت سی نشانیاں مقرر فرمائیں۔ اور ستاروں سے بھی لوگ راه حاصل کرتے ہیں.');
INSERT INTO "junagarri" VALUES(16,17,'16:17','تو کیا وه جو پیدا کرتا ہے اس جیسا ہے جو پیدا نہیں کرسکتا؟ کیا تم بالکل نہیں سوچتے؟ ');
INSERT INTO "junagarri" VALUES(16,18,'16:18','اور اگر تم اللہ کی نعمتوں کا شمار کرنا چاہو تو تم اسے نہیں کر سکتے۔ بیشک اللہ بڑا بخشنے والا مہربان ہے.');
INSERT INTO "junagarri" VALUES(16,19,'16:19','اور جو کچھ تم چھپاؤ اور ظاہر کرو اللہ تعالیٰ سب کچھ جانتا ہے. ');
INSERT INTO "junagarri" VALUES(16,20,'16:20','اور جن جن کو یہ لوگ اللہ تعالیٰ کے سوا پکارتے ہیں وه کسی چیز کو پیدا نہیں کرسکتے، بلکہ وه خود پیدا کیے ہوئے ہیں. ');
INSERT INTO "junagarri" VALUES(16,21,'16:21','مردے ہیں زنده نہیں ، انہیں تو یہ بھی شعور نہیں کہ کب اٹھائے جائیں گے. ');
INSERT INTO "junagarri" VALUES(16,22,'16:22','تم سب کا معبود صرف اللہ تعالیٰ اکیلا ہے اور آخرت پر ایمان نہ رکھنے والوں کے دل منکر ہیں اور وه خود تکبر سے بھرے ہوئے ہیں. ');
INSERT INTO "junagarri" VALUES(16,23,'16:23','بےشک وشبہ اللہ تعالیٰ ہر اس چیز کو، جسے وه لوگ چھپاتے ہیں اور جسے ظاہر کرتے ہیں، بخوبی جانتا ہے۔ وه غرور کرنے والوں کو پسند نہیں فرماتا. ');
INSERT INTO "junagarri" VALUES(16,24,'16:24','ان سے جب دریافت کیا جاتا ہے کہ تمہارے پروردگار نے کیا نازل فرمایا ہے؟ تو جواب دیتے ہیں کہ اگلوں کی کہانیاں ہیں. ');
INSERT INTO "junagarri" VALUES(16,25,'16:25','اسی کا نتیجہ ہوگا کہ قیامت کے دن یہ لوگ اپنے پورے بوجھ کے ساتھ ہی ان کے بوجھ کے بھی حصے دار ہوں گے جنہیں بے علمی سے گمراه کرتے رہے۔ دیکھو تو کیسا برا بوجھ اٹھا رہے ہیں. ');
INSERT INTO "junagarri" VALUES(16,26,'16:26','ان سے پہلے کے لوگوں نے بھی مکر کیا تھا، (آخر) اللہ نے (ان کے منصوبوں) کی عمارتوں کو جڑوں سے اکھیڑ دیا اور ان (کے سروں) پر (ان کی) چھتیں اوپر سے گر پڑیں ، اور ان کے پاس عذاب وہاں سے آگیا جہاں کا انہیں وہم وگمان بھی نہ تھا. ');
INSERT INTO "junagarri" VALUES(16,27,'16:27','پھر قیامت والے دن بھی اللہ تعالیٰ انہیں رسوا کرے گا اور فرمائے گا کہ میرے وه شریک کہاں ہیں جن کے بارے میں تم لڑتے جھگڑتے تھے ، جنہیں علم دیا گیا تھا وه پکار اٹھیں گے کہ آج تو کافروں کو رسوائی اور برائی چمٹ گئی.');
INSERT INTO "junagarri" VALUES(16,28,'16:28','وه جو اپنی جانوں پر ظلم کرتے ہیں، فرشتے جب ان کی جان قبض کرنے لگتے ہیں اس وقت وه جھک جاتے ہیں کہ ہم برائی نہیں کرتے تھے ۔ کیوں نہیں؟ اللہ تعالیٰ خوب جاننے والا ہے جو کچھ تم کرتے تھے. ');
INSERT INTO "junagarri" VALUES(16,29,'16:29','پس اب تو ہمیشگی کے طور پر تم جہنم کے دروازوں میں داخل ہو جاؤ ، پس کیا ہی برا ٹھکانا ہے غرور کرنے والوں کا.');
INSERT INTO "junagarri" VALUES(16,30,'16:30','اور پرہیز گاروں سے پوچھا جاتا ہے کہ تمہارے پروردگار نے کیا نازل فرمایا ہے؟ تو وه جواب دیتے ہیں کہ اچھے سے اچھا۔ جن لوگوں نے بھلائی کی ان کے لیے اس دنیا میں بھلائی ہے، اور یقیناً آخرت کا گھر تو بہت ہی بہتر ہے، اور کیا ہی خوب پرہیز گاروں کا گھر ہے.');
INSERT INTO "junagarri" VALUES(16,31,'16:31','ہمیشگی والے باغات جہاں وه جائیں گے جن کے نیچے نہریں بہہ رہی ہیں، جو کچھ یہ طلب کریں گے وہاں ان کے لیے موجود ہوگا۔ پرہیز گاروں کو اللہ تعالیٰ اسی طرح بدلے عطا فرماتا ہے.');
INSERT INTO "junagarri" VALUES(16,32,'16:32','وه جن کی جانیں فرشتے اس حال میں قبض کرتے ہیں کہ وه پاک صاف ہوں کہتے ہیں کہ تمہارے لیے سلامتی ہی سلامتی ہے ، جاؤ جنت میں اپنے ان اعمال کے بدلے جو تم کرتے تھے. ');
INSERT INTO "junagarri" VALUES(16,33,'16:33','کیا یہ اسی بات کا انتظار کر رہے ہیں کہ ان کے پاس فرشتے آجائیں یا تیرے رب کا حکم آجائے ؟ ایسا ہی ان لوگوں نے بھی کیا تھا جو ان سے پہلے تھے ۔ ان پر اللہ تعالیٰ نے کوئی ظلم نہیں کیا بلکہ وه خود اپنی جانوں پر ظلم کرتے رہے. ');
INSERT INTO "junagarri" VALUES(16,34,'16:34','پس ان کے برے اعمال کے نتیجے انہیں مل گئے اور جس کی ہنسی اڑاتے تھے اس نے ان کو گھیر لیا. ');
INSERT INTO "junagarri" VALUES(16,35,'16:35','مشرک لوگوں نے کہا کہ اگر اللہ تعالیٰ چاہتا تو ہم اور ہمارے باپ دادے اس کے سوا کسی اور کی عبادت ہی نہ کرتے، نہ اس کے فرمان کے بغیر کسی چیز کو حرام کرتے۔ یہی فعل ان سے پہلے کے لوگوں کا رہا۔ تو رسولوں پر تو صرف کھلم کھلا پیغام کا پہنچا دینا ہے. ');
INSERT INTO "junagarri" VALUES(16,36,'16:36','ہم نے ہر امت میں رسول بھیجا کہ (لوگو!) صرف اللہ کی عبادت کرو اور اس کے سوا تمام معبودوں سے بچو۔ پس بعض لوگوں کو تو اللہ تعالیٰ نے ہدایت دی اور بعض پر گمراہی ثابت ہوگئی ، پس تم خود زمین میں چل پھر کر دیکھ لو کہ جھٹلانے والوں کا انجام کیسا کچھ ہوا؟');
INSERT INTO "junagarri" VALUES(16,37,'16:37','گو آپ ان کی ہدایت کے خواہش مند رہے ہیں لیکن اللہ تعالیٰ اسے ہدایت نہیں دیتا جسے گمراه کر دے اور نہ ان کا کوئی مددگار ہوتا ہے. ');
INSERT INTO "junagarri" VALUES(16,38,'16:38','وه لوگ بڑی سخت سخت قسمیں کھا کھا کر کہتے ہیں کہ مردوں کو اللہ تعالیٰ زنده نہیں کرے گا ۔ کیوں نہیں ضرور زنده کرے گا یہ تو اس کا برحق لازمی وعده ہے، لیکن اکثر لوگ جانتے نہیں. ');
INSERT INTO "junagarri" VALUES(16,39,'16:39','اس لیے بھی کہ یہ لوگ جس چیز میں اختلاف کرتے تھے اسے اللہ تعالیٰ صاف بیان کر دے اور اس لیے بھی کہ خود کافر اپنا جھوٹا ہونا جان لیں. ');
INSERT INTO "junagarri" VALUES(16,40,'16:40','ہم جب کسی چیز کا اراده کرتے ہیں تو صرف ہمارا یہ کہہ دینا ہوتا ہے کہ ہوجا، پس وه ہوجاتی ہے. ');
INSERT INTO "junagarri" VALUES(16,41,'16:41','جن لوگوں نے ظلم برداشت کرنے کے بعد اللہ کی راه میں ترک وطن کیا ہے ہم انہیں بہتر سے بہتر ٹھکانا دنیا میں عطا فرمائیں گے اور آخرت کا ثواب تو بہت ہی بڑا ہے ، کاش کہ لوگ اس سے واقف ہوتے.');
INSERT INTO "junagarri" VALUES(16,42,'16:42','وه جنہوں نے دامن صبر نہ چھوڑا اور اپنے پالنے والے ہی پر بھروسہ کرتے رہے.');
INSERT INTO "junagarri" VALUES(16,43,'16:43','آپ سے پہلے بھی ہم مَردوں کو ہی بھیجتے رہے، جن کی جانب وحی اتارا کرتے تھے پس اگر تم نہیں جانتے تو اہل علم سے دریافت کر لو. ');
INSERT INTO "junagarri" VALUES(16,44,'16:44','دلیلوں اور کتابوں کے ساتھ، یہ ذکر (کتاب) ہم نے آپ کی طرف اتارا ہے کہ لوگوں کی جانب جو نازل فرمایا گیا ہے آپ اسے کھول کھول کر بیان کر دیں، شاید کہ وه غور وفکر کریں.');
INSERT INTO "junagarri" VALUES(16,45,'16:45','بدترین داؤ پیچ کرنے والے کیا اس بات سے بے خوف ہوگئے ہیں کہ اللہ تعالیٰ انہیں زمین میں دھنسا دے یا ان کے پاس ایسی جگہ سے عذاب آجائے جہاں کا انہیں وہم وگمان بھی نہ ہو.');
INSERT INTO "junagarri" VALUES(16,46,'16:46','یا انہیں چلتے پھرتے پکڑ لے یہ کسی صورت میں اللہ تعالیٰ کو عاجز نہیں کر سکتے.');
INSERT INTO "junagarri" VALUES(16,47,'16:47','یا انہیں ڈرا دھمکا کر پکڑ لے ، پس یقیناً تمہارا پروردگار اعلیٰ شفقت اور انتہائی رحم والا ہے. ');
INSERT INTO "junagarri" VALUES(16,48,'16:48','کیا انہوں نے اللہ کی مخلوق میں سے کسی کو بھی نہیں دیکھا؟ کہ اس کے سائے دائیں بائیں جھک جھک کر اللہ تعالیٰ کے سامنے سر بسجود ہوتے اور عاجزی کا اظہار کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(16,49,'16:49','یقیناً آسمان وزمین کے کل جاندار اور تمام فرشتے اللہ تعالیٰ کے سامنے سجدے کرتے ہیں اور ذرا بھی تکبر نہیں کرتے.*');
INSERT INTO "junagarri" VALUES(16,50,'16:50','اور اپنے رب سے جو ان کے اوپر ہے، کپکپاتے رہتے ہیں اور جو حکم مل جائے اس کی تعمیل کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(16,51,'16:51','اللہ تعالیٰ ارشاد فرما چکا ہے کہ دو معبود نہ بناؤ۔ معبود تو صرف وہی اکیلا ہے ، پس تم سب صرف میرا ہی ڈر خوف رکھو.');
INSERT INTO "junagarri" VALUES(16,52,'16:52','آسمانوں میں اور زمین میں جو کچھ ہے سب اسی کا ہے اور اسی کی عبادت لازم ہے ، کیا پھر تم اس کے سوا اوروں سے ڈرتے ہو؟');
INSERT INTO "junagarri" VALUES(16,53,'16:53','تمہارے پاس جتنی بھی نعمتیں ہیں سب اسی کی دی ہوئی ہیں ، اب بھی جب تمہیں کوئی مصیبت پیش آجائے تو اسی کی طرف نالہ وفریاد کرتے ہو. ');
INSERT INTO "junagarri" VALUES(16,54,'16:54','اور جہاں اس نے وه مصیبت تم سے دفع کر دی تم میں سے کچھ لوگ اپنے رب کے ساتھ شرک کرنے لگ جاتے ہیں.');
INSERT INTO "junagarri" VALUES(16,55,'16:55','کہ ہماری دی ہوئی نعمتوں کی ناشکری کریں ۔ اچھا کچھ فائده اٹھالو آخر کار تمہیں معلوم ہو ہی جائے گ. ');
INSERT INTO "junagarri" VALUES(16,56,'16:56','اور جسے جانتے بوجھتے بھی نہیں اس کا حصہ ہماری دی ہوئی روزی میں سے مقرر کرتے ہیں ، واللہ تمہارے اس بہتان کا سوال تم سے ضرور ہی کیا جائے گا. ');
INSERT INTO "junagarri" VALUES(16,57,'16:57','اور وه اللہ سبحانہ وتعالیٰ کے لیے لڑکیاں مقرر کرتے ہیں اور اپنے لیے وه جو اپنی خواہش کے مطابق ہو. ');
INSERT INTO "junagarri" VALUES(16,58,'16:58','ان میں سے جب کسی کو لڑکی ہونے کی خبر دی جائے تو اس کا چہره سیاه ہو جاتا ہے اور دل ہی دل میں گھٹنے لگتا ہے.');
INSERT INTO "junagarri" VALUES(16,59,'16:59','اس بری خبر کی وجہ سے لوگوں سے چھپا چھپا پھرتا ہے۔ سوچتا ہے کہ کیا اس کو ذلت کے ساتھ لئے ہوئے ہی رہے یا اسے مٹی میں دبا دے، آه! کیا ہی برے فیصلے کرتے ہیں؟ ');
INSERT INTO "junagarri" VALUES(16,60,'16:60','آخرت پر ایمان نہ رکھنے والوں کی ہی بری مثال ہے ، اللہ کے لیے تو بہت ہی بلند صفت ہے، وه بڑا ہی غالب اور باحکمت ہے. ');
INSERT INTO "junagarri" VALUES(16,61,'16:61','اگر لوگوں کے گناه پر اللہ تعالیٰ ان کی گرفت کرتا تو روئے زمین پر ایک بھی جاندار باقی نہ رہتا ، لیکن وه تو انہیں ایک وقت مقرر تک ڈھیل دیتا ہے ، جب ان کا وه وقت آجاتا ہے تو وه ایک ساعت نہ پیچھے ره سکتے ہیں اور نہ آگے بڑھ سکتے ہیں.');
INSERT INTO "junagarri" VALUES(16,62,'16:62','اور وه اپنے لیے جو ناپسند رکھتے ہیں اللہ کے لیے ثابت کرتے ہیں اور ان کی زبانیں جھوٹی باتیں بیان کرتی ہیں کہ ان کے لیے خوبی ہے ۔ نہیں نہیں، دراصل ان کے لیے آگ ہے اور یہ دوزخیوں کے پیش رو ہیں. ');
INSERT INTO "junagarri" VALUES(16,63,'16:63','واللہ! ہم نے تجھ سے پہلے کی امتوں کی طرف بھی اپنے رسول بھیجے لیکن شیطان نے ان کے اعمال بد ان کی نگاہوں میں آراستہ کر دیئے ، وه شیطان آج بھی ان کا رفیق بنا ہوا ہے اور ان کے لیے درد ناک عذاب ہے.');
INSERT INTO "junagarri" VALUES(16,64,'16:64','اس کتاب کو ہم نے آپ پر اس لیے اتارا ہے کہ آپ ان کے لیے ہر اس چیز کو واضح کر دیں جس میں وه اختلاف کر رہے ہیں اور یہ ایمان والوں کے لیے رہنمائی اور رحمت ہے.');
INSERT INTO "junagarri" VALUES(16,65,'16:65','اور اللہ آسمان سے پانی برسا کر اس سے زمین کو اس کی موت کے بعد زنده کر دیتا ہے۔ یقیناً اس میں ان لوگوں کے لیے نشانی ہے جو سنیں.');
INSERT INTO "junagarri" VALUES(16,66,'16:66','تمہارے لیے تو چوپایوں میں بھی بڑی عبرت ہے کہ ہم تمہیں اس کے پیٹ میں جو کچھ ہے اسی میں سے گوبر اور لہو کے درمیان سے خالص دودھ پلاتے ہیں جو پینے والوں کے لیے سہتا پچتا ہے. ');
INSERT INTO "junagarri" VALUES(16,67,'16:67','اور کھجور اور انگور کے درختوں کے پھلوں سے تم شراب بنا لیتے ہو اور عمده روزی بھی۔ جو لوگ عقل رکھتے ہیں ان کے لیے تو اس میں بہت بڑی نشانی ہے.');
INSERT INTO "junagarri" VALUES(16,68,'16:68','آپ کے رب نے شہد کی مکھی کے دل میں یہ بات ڈال دی کہ پہاڑوں میں درختوں اور لوگوں کی بنائی ہوئی اونچی اونچی ٹٹیوں میں اپنے گھر (چھتے) بنا.');
INSERT INTO "junagarri" VALUES(16,69,'16:69','اور ہر طرح کے میوے کھا اور اپنے رب کی آسان راہوں میں چلتی پھرتی ره، ان کے پیٹ سے رنگ برنگ کا مشروب نکلتا ہے ، جس کے رنگ مختلف ہیں اور جس میں لوگوں کے لیے شفا ہے غوروفکر کرنے والوں کے لیے اس میں بھی بہت بڑی نشانی ہے.');
INSERT INTO "junagarri" VALUES(16,70,'16:70','اللہ تعالیٰ نے ہی تم سب کو پیدا کیا ہے وہی پھر تمہیں فوت کرے گا، تم میں ایسے بھی ہیں جو بدترین عمر کی طرف لوٹائے جاتے ہیں کہ بہت کچھ جاننے بوجھنے کے بعد بھی نہ جانیں۔ بیشک اللہ دانا اور توانا ہے ');
INSERT INTO "junagarri" VALUES(16,71,'16:71','اللہ تعالیٰ ہی نے تم میں سے ایک کو دوسرے پر روزی میں زیادتی دے رکھی ہے، پس جنہیں زیادتی دی گئی ہے وه اپنی روزی اپنے ماتحت غلاموں کو نہیں دیتے کہ وه اور یہ اس میں برابر ہو جائیں ، تو کیا یہ لوگ اللہ کی نعمتوں کے منکر ہو رہے ہیں؟ ');
INSERT INTO "junagarri" VALUES(16,72,'16:72','اللہ تعالیٰ نے تمہارے لیے تم میں سے ہی تمہاری بیویاں پیدا کیں اور تمہاری بیویوں سے تمہارے لیے تمہارے بیٹے اور پوتے پیدا کیے اور تمہیں اچھی اچھی چیزیں کھانے کو دیں۔ کیا پھر بھی لوگ باطل پر ایمان لائیں گے ؟ اور اللہ تعالیٰ کی نعمتوں کی ناشکری کریں گے؟');
INSERT INTO "junagarri" VALUES(16,73,'16:73','اور وه اللہ تعالیٰ کے سوا ان کی عبادت کرتے ہیں جو آسمانوں اور زمین سے انہیں کچھ بھی تو روزی نہیں دے سکتے اور نہ کچھ قدرت رکھتے ہیں.*');
INSERT INTO "junagarri" VALUES(16,74,'16:74','پس اللہ تعالیٰ کے لیے مثالیں مت بناؤ اللہ تعالیٰ خوب جانتا ہے اور تم نہیں جانتے.');
INSERT INTO "junagarri" VALUES(16,75,'16:75','اللہ تعالیٰ ایک مثال بیان فرماتا ہے کہ ایک غلام ہے دوسرے کی ملکیت کا، جو کسی بات کا اختیار نہیں رکھتا اور ایک اور شخص ہے جسے ہم نے اپنے پاس سے معقول روزی دے رکھی ہے، جس میں سے وه چھپے کھلے خرچ کرتا ہے۔ کیا یہ سب برابر ہوسکتے ہیں ؟ اللہ تعالیٰ ہی کے لیے سب تعریف ہے، بلکہ ان میں سے اکثر نہیں جانتے.');
INSERT INTO "junagarri" VALUES(16,76,'16:76','اللہ تعالیٰ ایک اور مثال بیان فرماتا ہے ، دو شخصوں کی، جن میں سے ایک تو گونگا ہے اور کسی چیز پر اختیار نہیں رکھتا بلکہ وه اپنے مالک پر بوجھ ہے کہیں بھی اسے بھیجے وه کوئی بھلائی نہیں لاتا، کیا یہ اور وه جو عدل کا حکم دیتا ہے اور ہے بھی سیدھی راه پر، برابر ہوسکتے ہیں؟');
INSERT INTO "junagarri" VALUES(16,77,'16:77','آسمانوں اور زمین کا غیب صرف اللہ تعالیٰ ہی کو معلوم ہے ۔ اور قیامت کا امر تو ایسا ہی ہے جیسے آنکھ کا جھپکنا، بلکہ اس سے بھی زیاده قریب۔ بیشک اللہ تعالیٰ ہر چیز پر قادر ہے. ');
INSERT INTO "junagarri" VALUES(16,78,'16:78','اللہ تعالیٰ نے تمہیں تمہاری ماؤں کے پیٹوں سے نکالا ہے کہ اس وقت تم کچھ بھی نہیں جانتے تھے ، اسی نے تمہارے کان اور آنکھیں اور دل بنائے کہ تم شکر گزاری کرو. ');
INSERT INTO "junagarri" VALUES(16,79,'16:79','کیا ان لوگوں نے پرندوں کو نہیں دیکھا جو تابع فرمان ہو کر فضا میں ہیں، جنہیں بجز اللہ تعالیٰ کے کوئی اور تھامے ہوئے نہیں ، بیشک اس میں ایمان لانے والے لوگوں کے لیے بڑی نشانیاں ہیں.');
INSERT INTO "junagarri" VALUES(16,80,'16:80','اور اللہ تعالیٰ نے تمہارے لیے تمہارے گھروں میں سکونت کی جگہ بنا دی ہے اور اسی نے تمہارے لیے چوپایوں کی کھالوں کے گھر بنا دیے ہیں، جنہیں تم ہلکا پھلکا پاتے ہو اپنے کوچ کے دن اور اپنے ٹھہرنے کے دن بھی ، اور ان کی اون اور روؤں اور بالوں سے بھی اس نے بہت سے سامان اور ایک وقت مقرره تک کے لیے فائده کی چیزیں بنائیں. ');
INSERT INTO "junagarri" VALUES(16,81,'16:81','اللہ ہی نے تمہارے لیے اپنی پیدا کرده چیزوں میں سے سائے بنائے ہیں اور اسی نے تمہارے لیے پہاڑوں میں غار بنائے ہیں اور اسی نے تمہارے لیے کرتے بنائے ہیں جو تمہیں گرمی سے بچائیں اور ایسے کرتے بھی جو تمہیں لڑائی کے وقت کام آئیں ۔ وه اسی طرح اپنی پوری پوری نعمتیں دے رہا ہے کہ تم حکم بردار بن جاؤ.');
INSERT INTO "junagarri" VALUES(16,82,'16:82','پھر بھی اگر یہ منھ موڑے رہیں تو آپ پر صرف کھول کر تبلیغ کر دینا ہی ہے.');
INSERT INTO "junagarri" VALUES(16,83,'16:83','یہ اللہ کی نعمتیں جانتے پہچانتے ہوئے بھی ان کے منکر ہو رہے ہیں، بلکہ ان میں سے اکثر ناشکرے ہیں. ');
INSERT INTO "junagarri" VALUES(16,84,'16:84','اور جس دن ہم ہر امت میں سے گواه کھڑا کریں گے پھر کافروں کو نہ اجازت دی جائے گی اور نہ ان سے توبہ کرنے کو کہا جائے گا.');
INSERT INTO "junagarri" VALUES(16,85,'16:85','اور جب یہ ظالم عذاب دیکھ لیں گے پھر نہ تو ان سے ہلکا کیا جائے گا اور نہ وه ڈھیل دیے جائیں گے. ');
INSERT INTO "junagarri" VALUES(16,86,'16:86','اور جب مشرکین اپنے شریکوں کو دیکھ لیں گے تو کہیں گے اے ہمارے پروردگار! یہی ہمارے وه شریک ہیں جنہیں ہم تجھے چھوڑ کر پکارا کرتے تھے، پس وه انہیں جواب دیں گے کہ تم بالکل ہی جھوٹے ہو. ');
INSERT INTO "junagarri" VALUES(16,87,'16:87','اس دن وه سب (عاجز ہوکر) اللہ کے سامنے اطاعت کا اقرار پیش کریں گے اور جو بہتان بازی کیا کرتے تھے وه سب ان سے گم ہو جائے گی.');
INSERT INTO "junagarri" VALUES(16,88,'16:88','جنہوں نے کفر کیا اور اللہ کی راه سے روکا ہم انہیں عذاب پر عذاب بڑھاتے جائیں گے ، یہ بدلہ ہوگا ان کی فتنہ پردازیوں کا.');
INSERT INTO "junagarri" VALUES(16,89,'16:89','اور جس دن ہم ہر امت میں انہی میں سے ان کے مقابلے پر گواه کھڑا کریں گے اور تجھے ان سب پر گواه بنا کر لائیں گے اور ہم نے تجھ پر یہ کتاب نازل فرمائی ہے جس میں ہر چیز کا شافی بیان ہے ، اور ہدایت اور رحمت اور خوشخبری ہے مسلمانوں کے لیے.');
INSERT INTO "junagarri" VALUES(16,90,'16:90','اللہ تعالیٰ عدل کا، بھلائی کا اور قرابت داروں کے ساتھ سلوک کرنے کا حکم دیتا ہے اور بےحیائی کے کاموں، ناشائستہ حرکتوں اور ظلم وزیادتی سے روکتا ہے ، وه خود تمہیں نصیحتیں کر رہا ہے کہ تم نصیحت حاصل کرو.');
INSERT INTO "junagarri" VALUES(16,91,'16:91','اور اللہ کے عہد کو پورا کرو جب کہ تم آپس میں قول وقرار کرو اور قسموں کو ان کی پختگی کے بعد مت توڑو، حالانکہ تم اللہ تعالیٰ کو اپنا ضامن ٹھہرا چکے ہو ، تم جو کچھ کرتے ہو اللہ اس کو بخوبی جان رہا ہے.');
INSERT INTO "junagarri" VALUES(16,92,'16:92','اور اس عورت کی طرح نہ ہو جاؤ جس نے اپنا سوت مضبوط کاتنے کے بعد ٹکڑے ٹکڑے کرکے توڑ ڈالا ، کہ تم اپنی قسموں کو آپس کے مکر کا باعث ٹھراؤ ، اس لیے کہ ایک گروه دوسرے گروه سے بڑھا چڑھا ہوجائے ۔ بات صرف یہی ہے کہ اس عہد سے اللہ تمہیں آزما رہا ہے۔ یقیناً اللہ تعالیٰ تمہارے لیے قیامت کے دن ہر اس چیز کو کھول کر بیان کر دے گا جس میں تم اختلاف کر رہے تھے.');
INSERT INTO "junagarri" VALUES(16,93,'16:93','اگر اللہ چاہتا تم سب کو ایک ہی گروه بنا دیتا لیکن وه جسے چاہے گمراه کرتا ہے اور جسے چاہے ہدایت دیتا ہے، یقیناً تم جو کچھ کر رہے ہو اس کے بارے میں باز پرس کی جانے والی ہے.');
INSERT INTO "junagarri" VALUES(16,94,'16:94','اور تم اپنی قسموں کو آپس کی دغابازی کا بہانہ نہ بناؤ۔ پھر تو تمہارے قدم اپنی مضبوطی کے بعد ڈگمگا جائیں گے اور تمہیں سخت سزا برداشت کرنا پڑے گی کیونکہ تم نے اللہ کی راه سے روک دیا اور تمہیں بڑا سخت عذاب ہوگا. ');
INSERT INTO "junagarri" VALUES(16,95,'16:95','تم اللہ کے عہد کو تھوڑے مول کے بدلے نہ بیچ دیا کرو۔ یاد رکھو اللہ کے پاس کی چیز ہی تمہارے لیے بہتر ہے بشرطیکہ تم میں علم ہو.');
INSERT INTO "junagarri" VALUES(16,96,'16:96','تمہارے پاس جو کچھ ہے سب فانی ہے اور اللہ تعالیٰ کے پاس جو کچھ ہے باقی ہے۔ اور صبر کرنے والوں کو ہم بھلے اعمال کا بہترین بدلہ ضرور عطا فرمائیں گے.');
INSERT INTO "junagarri" VALUES(16,97,'16:97','جو شخص نیک عمل کرے مرد ہو یا عورت، لیکن باایمان ہو تو ہم اسے یقیناً نہایت بہتر زندگی عطا فرمائیں گے ۔ اور ان کے نیک اعمال کا بہتر بدلہ بھی انہیں ضرور ضرور دیں گے.');
INSERT INTO "junagarri" VALUES(16,98,'16:98','قرآن پڑھنے کے وقت راندے ہوئے شیطان سے اللہ کی پناه طلب کرو. ');
INSERT INTO "junagarri" VALUES(16,99,'16:99','ایمان والوں اور اپنے پروردگار پر بھروسہ رکھنے والوں پر اس کا زور مطلقاً نہیں چلتا.');
INSERT INTO "junagarri" VALUES(16,100,'16:100','ہاں اس کا غلبہ ان پر تو یقیناً ہے جو اسی سے رفاقت کریں اور اسے اللہ کا شریک ٹھہرائیں.');
INSERT INTO "junagarri" VALUES(16,101,'16:101','اور جب ہم کسی آیت کی جگہ دوسری آیت بدل دیتے ہیں اور جو کچھ اللہ تعالیٰ نازل فرماتا ہے اسے وه خوب جانتا ہے تو یہ کہتے ہیں کہ تو تو بہتان باز ہے۔ بات یہ ہے کہ ان میں سے اکثر جانتے ہی نہیں. ');
INSERT INTO "junagarri" VALUES(16,102,'16:102','کہہ دیجئے کہ اسے آپ کے رب کی طرف سے جبرائیل حق کے ساتھ لے کر آئے ہیں تاکہ ایمان والوں کو اللہ تعالیٰ استقامت عطا فرمائے اور مسلمانوں کی رہنمائی اور بشارت ہو جائے. ');
INSERT INTO "junagarri" VALUES(16,103,'16:103','ہمیں بخوبی علم ہے کہ یہ کافر کہتے ہیں کہ اسے تو ایک آدمی سکھاتا ہے اس کی زبان جس کی طرف یہ نسبت کر رہے ہیں عجمی ہے اور یہ قرآن تو صاف عربی زبان میں ہے. ');
INSERT INTO "junagarri" VALUES(16,104,'16:104','جو لوگ اللہ تعالیٰ کی آیتوں پر ایمان نہیں رکھتے انہیں اللہ کی طرف سے بھی رہنمائی نہیں ہوتی اور ان کے لیے المناک عذاب ہیں.');
INSERT INTO "junagarri" VALUES(16,105,'16:105','جھوٹ افترا تو وہی باندھتے ہیں جنہیں اللہ تعالیٰ کی آیتوں پر ایمان نہیں ہوتا۔ یہی لوگ جھوٹے ہیں. ');
INSERT INTO "junagarri" VALUES(16,106,'16:106','جو شخص اپنے ایمان کے بعد اللہ سے کفر کرے بجز اس کے جس پر جبر کیا جائے اور اس کا دل ایمان پر برقرار ہو ، مگر جو لوگ کھلے دل سے کفر کریں تو ان پر اللہ کا غضب ہے اور انہی کے لیے بہت بڑا عذاب ہے. ');
INSERT INTO "junagarri" VALUES(16,107,'16:107','یہ اس لیے کہ انہوں نے دنیا کی زندگی کو آخرت سے زیاده محبوب رکھا۔ یقیناً اللہ تعالیٰ کافر لوگوں کو راه راست نہیں دکھاتا. ');
INSERT INTO "junagarri" VALUES(16,108,'16:108','یہ وه لوگ ہیں جن کے دلوں پر اور جن کے کانوں پر اور جن کی آنکھوں پر اللہ نے مہر لگا دی ہے اور یہی لوگ غافل ہیں. ');
INSERT INTO "junagarri" VALUES(16,109,'16:109','کچھ شک نہیں کہ یہی لوگ آخرت میں سخت نقصان اٹھانے والے ہیں.');
INSERT INTO "junagarri" VALUES(16,110,'16:110','جن لوگوں نے فتنوں میں ڈالے جانے کے بعد ہجرت کی پھر جہاد کیا اور صبر کا ثبوت دیا بیشک تیرا پروردگار ان باتوں کے بعد انہیں بخشنے والا اور مہربانیاں کرنے والا ہے. ');
INSERT INTO "junagarri" VALUES(16,111,'16:111','جس دن ہر شخص اپنی ذات کے لیے لڑتا جھگڑتا آئے اور ہر شخص کو اس کے کیے ہوئے اعمال کا پورا بدلہ دیا جائے گا اور لوگوں پر (مطلقاً) ظلم نہ کیا جائے گا. ');
INSERT INTO "junagarri" VALUES(16,112,'16:112','اللہ تعالیٰ اس بستی کی مثال بیان فرماتا ہے جو پورے امن واطمینان سے تھی اس کی روزی اس کے پاس بافراغت ہر جگہ سے چلی آرہی تھی۔ پھر اس نے اللہ تعالیٰ کی نعمتوں کا کفر کیا تو اللہ تعالیٰ نے اسے بھوک اور ڈر کا مزه چکھایا جو بدلہ تھا ان کے کرتوتوں کا. ');
INSERT INTO "junagarri" VALUES(16,113,'16:113','ان کے پاس انہی میں سے رسول پہنچا پھر بھی انہوں نے اسے جھٹلایا پس انہیں عذاب نے آدبوچا اور وه تھے ہی ظالم.');
INSERT INTO "junagarri" VALUES(16,114,'16:114','جو کچھ حلال اور پاکیزه روزی اللہ نے تمہیں دے رکھی ہے اسے کھاؤ اور اللہ کی نعمت کا شکر کرو اگر تم اسی کی عبادت کرتے ہو.*');
INSERT INTO "junagarri" VALUES(16,115,'16:115','تم پر صرف مردار اور خون اور سور کا گوشت اور جس چیز پر اللہ کے سوا دوسرے کا نام پکارا جائے حرام ہیں ، پھر اگر کوئی شخص بے بس کر دیا جائے نہ وه خواہشمند ہو اور نہ حد سے گزرنے والا ہو تو یقیناً اللہ بخشنے والا رحم کرنے والا ہے.');
INSERT INTO "junagarri" VALUES(16,116,'16:116','کسی چیز کو اپنی زبان سے جھوٹ موٹ نہ کہہ دیا کرو کہ یہ حلال ہے اور یہ حرام ہے کہ اللہ پر جھوٹ بہتان باندھ لو ، سمجھ لو کہ اللہ تعالیٰ پر بہتان بازی کرنے والے کامیابی سے محروم ہی رہتے ہیں.');
INSERT INTO "junagarri" VALUES(16,117,'16:117','انہیں بہت معمولی فائده ملتا ہے اور ان کے لئے ہی دردناک عذاب ہے.');
INSERT INTO "junagarri" VALUES(16,118,'16:118','اور یہودیوں پر جو کچھ ہم نے حرام کیا تھا اسے ہم پہلے ہی سے آپ کو سنا چکے ہیں ، ہم نے ان پر ظلم نہیں کیا بلکہ وه خود اپنی جانوں پر ظلم کرتے رہے.');
INSERT INTO "junagarri" VALUES(16,119,'16:119','جو کوئی جہالت سے برے عمل کرلے پھر توبہ کرلے اور اصلاح بھی کرلے تو پھر آپ کا رب بلا شک وشبہ بڑی بخشش کرنے والا اور نہایت ہی مہربان ہے.');
INSERT INTO "junagarri" VALUES(16,120,'16:120','بےشک ابراہیم پیشوا اور اللہ تعالیٰ کے فرمانبردار اور یک طرفہ مخلص تھے۔ وه مشرکوں میں سے نہ تھے.');
INSERT INTO "junagarri" VALUES(16,121,'16:121','اللہ تعالیٰ کی نعمتوں کے شکر گزار تھے، اللہ نے انہیں اپنا برگزیده کر لیا تھا اور انہیں راه راست سجھا دی تھی.');
INSERT INTO "junagarri" VALUES(16,122,'16:122','ہم نے اسے دنیا میں بھی بہتری دی تھی اور بےشک وه آخرت میں بھی نیکوکاروں میں ہیں.');
INSERT INTO "junagarri" VALUES(16,123,'16:123','پھر ہم نے آپ کی جانب وحی بھیجی کہ آپ ملت ابراہیم حنیف کی پیروی کریں ، جو مشرکوں میں سے نہ تھے.');
INSERT INTO "junagarri" VALUES(16,124,'16:124','ہفتے کے دن کی عظمت تو صرف ان لوگوں کے ذمے ہی ضروری کی گئی تھی جنہوں نے اس میں اختلاف کیا تھا ، بات یہ ہے کہ آپ کا پروردگار خود ہی ان میں ان کے اختلاف کا فیصلہ قیامت کے دن کرے گا.');
INSERT INTO "junagarri" VALUES(16,125,'16:125','اپنے رب کی راه کی طرف لوگوں کو حکمت اور بہترین نصیحت کے ساتھ بلایئے اور ان سے بہترین طریقے سے گفتگو کیجئے ، یقیناً آپ کا رب اپنی راه سے بہکنے والوں کو بھی بخوبی جانتا ہے اور وه راه یافتہ لوگوں سے بھی پورا واقف ہے. ');
INSERT INTO "junagarri" VALUES(16,126,'16:126','اور اگر بدلہ لو بھی تو بالکل اتنا ہی جتنا صدمہ تمہیں پہنچایا گیا ہو اور اگر صبر کرلو تو بےشک صابروں کے لئے یہی بہتر ہے. ');
INSERT INTO "junagarri" VALUES(16,127,'16:127','آپ صبر کریں بغیر توفیق الٰہی کے آپ صبر کر ہی نہیں سکتے اور ان کے حال پر رنجیده نہ ہوں اور جو مکر وفریب یہ کرتے رہتے ہیں ان سے تنگ دل نہ ہوں. ');
INSERT INTO "junagarri" VALUES(16,128,'16:128','یقین مانو کہ اللہ تعالیٰ پرہیزگاروں اور نیک کاروں کے ساتھ ہے.');
INSERT INTO "junagarri" VALUES(17,1,'17:1','پاک ہے وه اللہ تعالیٰ جو اپنے بندے کو رات ہی رات میں مسجد حرام سے مسجد اقصیٰ تک لے گیا جس کے آس پاس ہم نے برکت دے رکھی ہے، اس لئے کہ ہم اسے اپنی قدرت کے بعض نمونے دکھائیں ، یقیناً اللہ تعالیٰ ہی خوب سننے دیکھنے والا ہے.');
INSERT INTO "junagarri" VALUES(17,2,'17:2','ہم نے موسیٰ کو کتاب دی اور اسے بنی اسرائیل کے لئے ہدایت بنا دیا کہ تم میرے سوا کسی کو اپنا کارساز نہ بنانا.');
INSERT INTO "junagarri" VALUES(17,3,'17:3','اے ان لوگوں کی اولاد! جنہیں ہم نے نوح کے ساتھ سوار کردیا تھا، وه ہمارا بڑا ہی شکرگزار بنده تھا. ');
INSERT INTO "junagarri" VALUES(17,4,'17:4','ہم نے بنی اسرائیل کے لئے ان کی کتاب میں صاف فیصلہ کردیا تھا کہ تم زمین میں دوبار فساد برپا کرو گے اور تم بڑی زبردست زیادتیاں کرو گے.');
INSERT INTO "junagarri" VALUES(17,5,'17:5','ان دونوں وعدوں میں سے پہلے کے آتے ہی ہم نے تمہارے مقابلہ پر اپنے بندے بھیج دیئے جو بڑے ہی لڑاکے تھے۔ پس وه تمہارے گھروں کے اندر تک پھیل گئے اور اللہ کا یہ وعده پورا ہونا ہی تھا. ');
INSERT INTO "junagarri" VALUES(17,6,'17:6','پھر ہم نے ان پر تمہارا غلبہ دے کر تمہارے دن پھیرے اور مال اور اولاد سے تمہاری مدد کی اور تمہیں بڑے جتھے والا بنا دیا. ');
INSERT INTO "junagarri" VALUES(17,7,'17:7','اگر تم نے اچھے کام کئے تو خود اپنے ہی فائده کے لئے، اور اگر تم نے برائیاں کیں تو بھی اپنے ہی لئے، پھر جب دوسرے وعدے کا وقت آیا (تو ہم نے دوسرے بندوں کو بھیج دیا تاکہ) وه تمہارے چہرے بگاڑ دیں اور پہلی دفعہ کی طرح پھر اسی مسجد میں گھس جائیں۔ اور جس جس چیز پر قابو پائیں توڑ پھوڑ کر جڑ سے اکھاڑ دیں. ');
INSERT INTO "junagarri" VALUES(17,8,'17:8','امید ہے کہ تمہارا رب تم پر رحم کرے۔ ہاں اگر تم پھر بھی وہی کرنے لگے تو ہم بھی دوباره ایسا ہی کریں گے اور ہم نے منکروں کا قید خانہ جہنم کو بنا رکھا ہے. ');
INSERT INTO "junagarri" VALUES(17,9,'17:9','یقیناً یہ قرآن وه راستہ دکھاتا ہے جو بہت ہی سیدھا ہے اور ایمان والوں کو جو نیک اعمال کرتے ہیں اس بات کی خوشخبری دیتا ہے کہ ان کے لئے بہت بڑا اجر ہے.');
INSERT INTO "junagarri" VALUES(17,10,'17:10','اور یہ کہ جو لوگ آخرت پر یقین نہیں رکھتے ان کے لئے ہم نے دردناک عذاب تیار کر رکھا ہے.');
INSERT INTO "junagarri" VALUES(17,11,'17:11','اور انسان برائی کی دعائیں مانگنے لگتا ہے بالکل اس کی اپنی بھلائی کی دعا کی طرح، انسان ہے ہی بڑا جلد باز. ');
INSERT INTO "junagarri" VALUES(17,12,'17:12','ہم نے رات اور دن کو اپنی قدرت کی نشانیاں بنائی ہیں، رات کی نشانی کو تو ہم نے بےنور کردیا ہے اور دن کی نشانی کو روشن بنایا ہے تاکہ تم اپنے رب کا فضل تلاش کر سکو اور اس لئے بھی کہ برسوں کا شمار اور حساب معلوم کرسکو اور ہر ہر چیز کو ہم نے خوب تفصیل سے بیان فرما دیا ہے. ');
INSERT INTO "junagarri" VALUES(17,13,'17:13','ہم نے ہر انسان کی برائی بھلائی کو اس کے گلے لگا دیا ہے اور بروز قیامت ہم اس کے سامنے اس کا نامہٴ اعمال نکالیں گے جسے وه اپنے اوپر کھلا ہوا پالے گا.');
INSERT INTO "junagarri" VALUES(17,14,'17:14','لے! خود ہی اپنی کتاب آپ پڑھ لے۔ آج تو تو آپ ہی اپنا خود حساب لینے کو کافی ہے.');
INSERT INTO "junagarri" VALUES(17,15,'17:15','جو راه راست حاصل کرلے وه خود اپنے ہی بھلے کے لئے راه یافتہ ہوتا ہے اور جو بھٹک جائے اس کا بوجھ اسی کے اوپر ہے، کوئی بوجھ والا کسی اور کا بوجھ اپنے اوپر نہ لادے گا اور ہماری سنت نہیں کہ رسول بھیجنے سے پہلے ہی عذاب کرنے لگیں. ');
INSERT INTO "junagarri" VALUES(17,16,'17:16','اور جب ہم کسی بستی کی ہلاکت کا اراده کرلیتے ہیں تو وہاں کے خوشحال لوگوں کو (کچھ) حکم دیتے ہیں اور وه اس بستی میں کھلی نافرمانی کرنے لگتے ہیں تو ان پر (عذاب کی) بات ثابت ہوجاتی ہے پھر ہم اسے تباه وبرباد کردیتے ہیں. ');
INSERT INTO "junagarri" VALUES(17,17,'17:17','ہم نے نوح کے بعد بھی بہت سی قومیں ہلاک کیں اور تیرا رب اپنے بندوں کے گناہوں سے کافی خبردار اور خوب دیکھنے بھالنے والا ہے.');
INSERT INTO "junagarri" VALUES(17,18,'17:18','جس کا اراده صرف اس جلدی والی دنیا (فوری فائده) کا ہی ہو اسے ہم یہاں جس قدر جس کے لئے چاہیں سردست دیتے ہیں بالآخر اس کے لئے ہم جہنم مقرر کردیتے ہیں جہاں وه برے حالوں دھتکارا ہوا داخل ہوگا. ');
INSERT INTO "junagarri" VALUES(17,19,'17:19','اور جس کا اراده آخرت کا ہو اور جیسی کوشش اس کے لئے ہونی چاہیئے، وه کرتا بھی ہو اور وه باایمان بھی ہو، پس یہی لوگ ہیں جن کی کوشش کی اللہ کے ہاں پوری قدردانی کی جائے گی. ');
INSERT INTO "junagarri" VALUES(17,20,'17:20','ہر ایک کو ہم بہم پہنچائے جاتے ہیں انہیں بھی اور ا نہیں بھی تیرے پروردگار کے انعامات میں سے۔ تیرے پروردگار کی بخشش رکی ہوئی نہیں ہے. ');
INSERT INTO "junagarri" VALUES(17,21,'17:21','دیکھ لے کہ ان میں ایک کو ایک پر ہم نے کس طرح فضیلت دے رکھی ہے اور آخرت تو درجوں میں اور بھی بڑھ کر ہے اور فضیلت کے اعتبار سے بھی بہت بڑی ہے. ');
INSERT INTO "junagarri" VALUES(17,22,'17:22','اللہ کے ساتھ کسی اور کو معبود نہ ٹھہرا کہ آخرش تو برے حالوں بےکس ہو کر بیٹھ رہے گا.');
INSERT INTO "junagarri" VALUES(17,23,'17:23','اور تیرا پروردگار صاف صاف حکم دے چکا ہے کہ تم اس کے سوا کسی اور کی عبادت نہ کرنا اور ماں باپ کے ساتھ احسان کرنا۔ اگر تیری موجودگی میں ان میں سے ایک یا یہ دونوں بڑھاپے کو پہنچ جائیں تو ان کے آگے اف تک نہ کہنا، نہ انہیں ڈانٹ ڈپٹ کرنا بلکہ ان کے ساتھ ادب و احترام سے بات چیت کرنا. s');
INSERT INTO "junagarri" VALUES(17,24,'17:24','اور عاجزی اور محبت کے ساتھ ان کے سامنے تواضع کا بازو پست رکھے رکھنا اور دعا کرتے رہنا کہ اے میرے پروردگار! ان پر ویسا ہی رحم کر جیسا انہوں نے میرے بچپن میں میری پرورش کی ہے.');
INSERT INTO "junagarri" VALUES(17,25,'17:25','جو کچھ تمہارے دلوں میں ہے اسے تمہارا رب بخوبی جانتا ہے اگر تم نیک ہو تو وه تو رجوع کرنے والوں کو بخشنے والا ہے.');
INSERT INTO "junagarri" VALUES(17,26,'17:26','اور رشتے داروں کا اور مسکینوں اور مسافروں کا حق ادا کرتے رہو اور اسراف اور بیجا خرچ سے بچو.');
INSERT INTO "junagarri" VALUES(17,27,'17:27','بیجا خرچ کرنے والے شیطانوں کے بھائی ہیں۔ اور شیطان اپنے پروردگار کا بڑا ہی ناشکرا ہے. ');
INSERT INTO "junagarri" VALUES(17,28,'17:28','اور اگر تجھے ان سے منھ پھیر لینا پڑے اپنے رب کی اس رحمت کی جستجو میں، جس کی تو امید رکھتا ہے تو بھی تجھے چاہیئے کہ عمدگی اور نرمی سے انہیں سمجھا دے. ');
INSERT INTO "junagarri" VALUES(17,29,'17:29','اپنا ہاتھ اپنی گردن سے بندھا ہوا نہ رکھ اور نہ اسے بالکل ہی کھول دے کہ پھر ملامت کیا ہوا درمانده بیٹھ جائے. ');
INSERT INTO "junagarri" VALUES(17,30,'17:30','یقیناً تیرا رب جس کے لئے چاہے روزی کشاده کردیتا ہے اور جس کے لئے چاہے تنگ ۔ یقیناً وه اپنے بندوں سے باخبر اور خوب دیکھنے والا ہے.');
INSERT INTO "junagarri" VALUES(17,31,'17:31','اور مفلسی کے خوف سے اپنی اولاد کو نہ مار ڈالو، ان کو اور تم کو ہم ہی روزی دیتے ہیں۔ یقیناً ان کا قتل کرنا کبیره گناه ہے. ');
INSERT INTO "junagarri" VALUES(17,32,'17:32','خبردار زنا کے قریب بھی نہ پھٹکنا کیوں کہ وه بڑی بےحیائی ہے اور بہت ہی بری راه ہے. ');
INSERT INTO "junagarri" VALUES(17,33,'17:33','اور کسی جان کو جس کا مارنا اللہ نے حرام کردیا ہے ہرگز ناحق قتل نہ کرنا اور جو شخص مظلوم ہونے کی صورت میں مار ڈالا جائے ہم نے اس کے وارث کو طاقت دے رکھی ہے پس اسے چاہیئے کہ مار ڈالنے میں زیادتی نہ کرے بےشک وه مدد کیا گیا ہے. ');
INSERT INTO "junagarri" VALUES(17,34,'17:34','اور یتیم کے مال کے قریب بھی نہ جاؤ بجز اس طریقہ کے جو بہت ہی بہتر ہو، یہاں تک کہ وه اپنی بلوغت کو پہنچ جائے اور وعدے پورے کرو کیونکہ قول وقرار کی باز پرس ہونے والی ہے. ');
INSERT INTO "junagarri" VALUES(17,35,'17:35','اور جب ناپنے لگو تو بھر پور پیمانے سے ناپو اور سیدھی ترازو سے تولا کرو۔ یہی بہتر ہے اور انجام کے لحاظ سے بھی بہت اچھا ہے.');
INSERT INTO "junagarri" VALUES(17,36,'17:36','جس بات کی تجھے خبر ہی نہ ہو اس کے پیچھے مت پڑ ۔ کیونکہ کان اور آنکھ اور دل ان میں سے ہر ایک سے پوچھ گچھ کی جانے والی ہے. ');
INSERT INTO "junagarri" VALUES(17,37,'17:37','اور زمین میں اکڑ کر نہ چل کہ نہ تو زمین کو پھاڑ سکتا ہے اور نہ لمبائی میں پہاڑوں کو پہنچ سکتا ہے. ');
INSERT INTO "junagarri" VALUES(17,38,'17:38','ان سب کاموں کی برائی تیرے رب کے نزدیک (سخت) ناپسند ہے. ');
INSERT INTO "junagarri" VALUES(17,39,'17:39','یہ بھی منجملہ اس وحی کے ہے جو تیری جانب تیرے رب نے حکمت سے اتاری ہے تو اللہ کے ساتھ کسی اور کو معبود نہ بنانا کہ ملامت خورده اور راندہٴ درگاه ہو کر دوزخ میں ڈال دیا جائے.');
INSERT INTO "junagarri" VALUES(17,40,'17:40','کیا بیٹوں کے لئے تو اللہ نے تمہیں چھانٹ لیا اور خود اپنے لئے فرشتوں کو لڑکیاں بنالیں؟ بےشک تم بہت بڑا بول بول رہے ہو.');
INSERT INTO "junagarri" VALUES(17,41,'17:41','ہم نے تو اس قرآن میں ہر ہر طرح بیان فرما دیا کہ لوگ سمجھ جائیں لیکن اس سے انہیں تو نفرت ہی بڑھتی ہے.');
INSERT INTO "junagarri" VALUES(17,42,'17:42','کہہ دیجیئے! کہ اگر اللہ کے ساتھ اور معبود بھی ہوتے جیسے کہ یہ لوگ کہتے ہیں تو ضرور وه اب تک مالک عرش کی جانب راه ڈھونڈ نکالتے. ');
INSERT INTO "junagarri" VALUES(17,43,'17:43','جو کچھ یہ کہتے ہیں اس سے وه پاک اور بالاتر، بہت دور اور بہت بلند ہے. ');
INSERT INTO "junagarri" VALUES(17,44,'17:44','ساتوں آسمان اور زمین اور جو بھی ان میں ہے اسی کی تسبیح کر رہے ہیں۔ ایسی کوئی چیز نہیں جو اسے پاکیزگی اور تعریف کے ساتھ یاد نہ کرتی ہو۔ ہاں یہ صحیح ہے کہ تم اس کی تسبیح سمجھ نہیں سکتے ۔ وه بڑا بردبار اور بخشنے والا ہے.');
INSERT INTO "junagarri" VALUES(17,45,'17:45','تو جب قرآن پڑھتا ہے ہم تیرے اور ان لوگوں کے درمیان جو آخرت پر یقین نہیں رکھتے ایک پوشیده حجاب ڈال دیتے ہیں. ');
INSERT INTO "junagarri" VALUES(17,46,'17:46','اور ان کے دلوں پر ہم نے پردے ڈال دیئے ہیں کہ وه اسے سمجھیں اور ان کے کانوں میں بوجھ اور جب تو صرف اللہ ہی کا ذکر اس کی توحید کے ساتھ، اس قرآن میں کرتا ہے تو وه روگردانی کرتے پیٹھ پھیر کر بھاگ کھڑے ہوتے ہیں. ');
INSERT INTO "junagarri" VALUES(17,47,'17:47','جس غرض سے وه لوگ اسے سنتے ہیں ان (کی نیتوں) سے ہم خوب آگاه ہیں، جب یہ آپ کی طرف کان لگائے ہوئے ہوتے ہیں تب بھی اور جب یہ مشوره کرتے ہیں تب بھی جب کہ یہ ظالم کہتے ہیں کہ تم اس کی تابعداری میں لگے ہوئے ہو جن پر جادو کردیا گیا ہے.');
INSERT INTO "junagarri" VALUES(17,48,'17:48','دیکھیں تو سہی، آپ کے لئے کیا کیا مثالیں بیان کرتے ہیں، پس وه بہک رہے ہیں۔ اب تو راه پانا ان کے بس میں نہیں رہا. ');
INSERT INTO "junagarri" VALUES(17,49,'17:49','انہوں نے کہا کیا جب ہم ہڈیاں اور (مٹی ہو کر) ریزه ریزه ہوجائیں گے تو کیا ہم ازسرنو پیدا کر کے پھر دوباره اٹھا کر کھڑے کر دیئے جائیں گے.');
INSERT INTO "junagarri" VALUES(17,50,'17:50','جواب دیجیئے کہ تم پتھر بن جاؤ یا لوہا. ');
INSERT INTO "junagarri" VALUES(17,51,'17:51','یا کوئی اور ایسی خلقت جو تمہارے دلوں میں بہت ہی سخت معلوم ہو ، پھر وه پوچھیں کہ کون ہے جو دوباره ہماری زندگی لوٹائے؟ آپ جواب دے دیں کہ وہی اللہ جس نے تمہیں اول بار پیدا کیا، اس پر وه اپنے سر ہلا ہلا کر آپ سے دریافت کریں گے کہ اچھا یہ ہے کب؟ تو آپ جواب دے دیں کہ کیا عجب کہ وه (ساعت) قریب ہی آن لگی ہو. ');
INSERT INTO "junagarri" VALUES(17,52,'17:52','جس دن وه تمہیں بلائے گا تم اس کی تعریف کرتے ہوئے تعمیل ارشاد کرو گے اور گمان کرو گے کہ تمہارا رہنا بہت ہی تھوڑا ہے. ');
INSERT INTO "junagarri" VALUES(17,53,'17:53','اور میرے بندوں سے کہہ دیجیئے کہ وه بہت ہی اچھی بات منھ سے نکالا کریں کیونکہ شیطان آپس میں فساد ڈلواتا ہے ۔ بےشک شیطان انسان کا کھلا دشمن ہے.');
INSERT INTO "junagarri" VALUES(17,54,'17:54','تمہارا رب تم سے بہ نسبت تمہارے بہت زیاده جاننے والا ہے، وه اگر چاہے تو تم پر رحم کردے یا اگر وه چاہے تمہیں عذاب دے ۔ ہم نے آپ کو ان کا ذمہ دار ٹھہرا کر نہیں بھیجا. ');
INSERT INTO "junagarri" VALUES(17,55,'17:55','آسمانوں وزمین میں جو بھی ہے آپ کا رب سب کو بخوبی جانتا ہے۔ ہم نے بعض پیغمبروں کو بعض پر بہتری اور برتری دی ہے اور داؤد کو زبور ہم نے عطا فرمائی ہے.');
INSERT INTO "junagarri" VALUES(17,56,'17:56','کہہ دیجیئے کہ اللہ کے سوا جنہیں تم معبود سمجھ رہے ہو انہیں پکارو لیکن نہ تو وه تم سے کسی تکلیف کو دور کرسکتے ہیں اور نہ بدل سکتے ہیں.');
INSERT INTO "junagarri" VALUES(17,57,'17:57','جنہیں یہ لوگ پکارتے ہیں خود وه اپنے رب کے تقرب کی جستجو میں رہتے ہیں کہ ان میں سے کون زیاده نزدیک ہوجائے وه خود اس کی رحمت کی امید رکھتے اور اس کے عذاب سے خوفزده رہتے ہیں ، (بات بھی یہی ہے) کہ تیرے رب کا عذاب ڈرنے کی چیز ہی ہے.');
INSERT INTO "junagarri" VALUES(17,58,'17:58','جتنی بھی بستیاں ہیں ہم قیامت کے دن سے پہلے پہلے یا تو انہیں ہلاک کردینے والے ہیں یا سخت تر سزا دینے والے ہیں۔ یہ تو کتاب میں لکھا جاچکا ہے. ');
INSERT INTO "junagarri" VALUES(17,59,'17:59','ہمیں نشانات (معجزات) کے نازل کرنے سے روک صرف اسی کی ہے کہ اگلے لوگ انہیں جھٹلا چکے ہیں ۔ ہم نے ثمودیوں کو بطور بصیرت کے اونٹنی دی لیکن انہوں نے اس پر ظلم کیا ہم تو لوگوں کو دھمکانے کے لئے ہی نشانیاں بھیجتے ہیں.');
INSERT INTO "junagarri" VALUES(17,60,'17:60','اور یاد کرو جب کہ ہم نے آپ سے فرما دیا کہ آپ کے رب نے لوگوں کو گھیر لیا ہے۔ جو رویا (عینی رؤیت) ہم نے آپ کو دکھائی تھی وه لوگوں کے لئے صاف آزمائش ہی تھی اور اسی طرح وه درخت بھی جس سے قرآن میں اظہار نفرت کیا گیا ہے ۔ ہم انہیں ڈرا رہے ہیں لیکن یہ انہیں اور بڑی سرکشی میں بڑھا رہا ہے. ');
INSERT INTO "junagarri" VALUES(17,61,'17:61','جب ہم نے فرشتوں کو حکم دیا کہ آدم کو سجده کرو تو ابلیس کے سوا سب نے کیا، اس نے کہا کہ کیا میں اسے سجده کروں جسے تو نے مٹی سے پیدا کیا ہے.');
INSERT INTO "junagarri" VALUES(17,62,'17:62','اچھا دیکھ لے اسے تو نے مجھ پر بزرگی تو دی ہے، لیکن اگر مجھے بھی قیامت تک تو نے ڈھیل دی تو میں اس کی اولاد کو بجز بہت تھوڑے لوگوں کے، اپنے بس میں کرلوں گا.');
INSERT INTO "junagarri" VALUES(17,63,'17:63','ارشاد ہوا کہ جا ان میں سے جو بھی تیرا تابعدار ہوجائے گا تو تم سب کی سزا جہنم ہے جو پورا پورا بدلہ ہے.');
INSERT INTO "junagarri" VALUES(17,64,'17:64','ان میں سے تو جسے بھی اپنی آواز سے بہکا سکے بہکا لے اور ان پر اپنے سوار اور پیادے چڑھا لا اور ان کے مال اور اولاد میں سے اپنا بھی ساجھا لگا اور انہیں (جھوٹے) وعدے دے لے ۔ ان سے جتنے بھی وعدے شیطان کے ہوتے ہیں سب کے سب سراسر فریب ہیں. ');
INSERT INTO "junagarri" VALUES(17,65,'17:65','میرے سچے بندوں پر تیرا کوئی قابو اور بس نہیں ۔ تیرا رب کارسازی کرنے والا کافی ہے. ');
INSERT INTO "junagarri" VALUES(17,66,'17:66','تمہارا پروردگار وه ہے جو تمہارے لئے دریا میں کشتیاں چلاتا ہے تاکہ تم اس کا فضل تلاش کرو۔ وه تمہارے اوپر بہت ہی مہربان ہے. ');
INSERT INTO "junagarri" VALUES(17,67,'17:67','اور سمندروں میں مصیبت پہنچتے ہی جنہیں تم پکارتے تھے سب گم ہوجاتے ہیں صرف وہی اللہ باقی ره جاتا ہے۔ پھر جب وه تمہیں خشکی کی طرف بچا لاتا ہے تو تم منھ پھیر لیتے ہو اور انسان بڑا ہی ناشکرا ہے. ');
INSERT INTO "junagarri" VALUES(17,68,'17:68','تو کیا تم اس سے بےخوف ہوگئے ہو کہ تمہیں خشکی کی طرف (لے جاکر زمین) میں دھنسا دے یا تم پر پتھروں کی آندھی بھیج دے ۔ پھر تم اپنے لئے کسی نگہبان کو نہ پاسکو.');
INSERT INTO "junagarri" VALUES(17,69,'17:69','کیا تم اس بات سے بےخوف ہوگئے ہو کہ اللہ تعالیٰ پھر تمہیں دوباره دریا کے سفر میں لے آئے اور تم پر تیز وتند ہواؤں کے جھونکے بھیج دے اور تمہارے کفر کے باعث تمہیں ڈبو دے۔ پھر تم اپنے لئے ہم پر اس کا دعویٰ (پیچھا) کرنے والا کسی کو نہ پاؤ گے. ');
INSERT INTO "junagarri" VALUES(17,70,'17:70','یقیناً ہم نے اولاد آدم کو بڑی عزت دی اور انہیں خشکی اور تری کی سواریاں دیں اور انہیں پاکیزه چیزوں کی روزیاں دیں اور اپنی بہت سی مخلوق پر انہیں فضیلت عطا فرمائی. ');
INSERT INTO "junagarri" VALUES(17,71,'17:71','جس دن ہم ہر جماعت کو اس کے پیشوا سمیت بلائیں گے۔ پھر جن کا بھی اعمال نامہ دائیں ہاتھ میں دے دیا گیا وه تو شوق سے اپنا نامہٴ اعمال پڑھنے لگیں گے اور دھاگے کے برابر (ذره برابر) بھی ظلم نہ کئے جائیں گے. ');
INSERT INTO "junagarri" VALUES(17,72,'17:72','اور جو کوئی اس جہان میں اندھا رہا، وه آخرت میں بھی اندھا اور راستے سے بہت ہی بھٹکا ہوا رہے گا. ');
INSERT INTO "junagarri" VALUES(17,73,'17:73','یہ لوگ آپ کو اس وحی سے جو ہم نے آپ پر اتاری ہے بہکانا چاہتے کہ آپ اس کے سوا کچھ اور ہی ہمارے نام سے گھڑ گھڑالیں، تب تو آپ کو یہ لوگ اپنا ولی دوست بنا لیتے.');
INSERT INTO "junagarri" VALUES(17,74,'17:74','اگر ہم آپ کو ثابت قدم نہ رکھتے تو بہت ممکن تھا کہ ان کی طرف قدرے قلیل مائل ہو ہی جاتے. ');
INSERT INTO "junagarri" VALUES(17,75,'17:75','پھر تو ہم بھی آپ کو دوہرا عذاب دنیا کا کرتے اور دوہرا ہی موت کا ، پھر آپ تو اپنے لئے ہمارے مقابلے میں کسی کو مددگار بھی نہ پاتے.');
INSERT INTO "junagarri" VALUES(17,76,'17:76','یہ تو آپ کے قدم اس سرزمین سے اکھاڑنے ہی لگے تھے کہ آپ کو اس سے نکال دیں ۔ پھر یہ بھی آپ کے بعد بہت ہی کم ٹھہر پاتے. ');
INSERT INTO "junagarri" VALUES(17,77,'17:77','ایسا ہی دستور ان کا تھا جو آپ سے پہلے رسول ہم نے بھیجے اور آپ ہمارے دستور میں کبھی ردوبدل نہ پائیں گے. ');
INSERT INTO "junagarri" VALUES(17,78,'17:78','نماز کو قائم کریں آفتاب کے ڈھلنے سے لے کر رات کی تاریکی تک اور فجر کا قرآن پڑھنا بھی یقیناً فجر کے وقت کا قرآن پڑھنا حاضر کیا گیا ہے. ');
INSERT INTO "junagarri" VALUES(17,79,'17:79','رات کے کچھ حصے میں تہجد کی نماز میں قرآن کی تلاوت کریں یہ زیادتی آپ کے لئے ہے عنقریب آپ کا رب آپ کو مقام محمود میں کھڑا کرے گا. ');
INSERT INTO "junagarri" VALUES(17,80,'17:80','اور دعا کیا کریں کہ اے میرے پروردگار مجھے جہاں لے جا اچھی طرح لے جا اور جہاں سے نکال اچھی طرح نکال اور میرے لئے اپنے پاس سے غلبہ اور امداد مقرر فرما دے. ');
INSERT INTO "junagarri" VALUES(17,81,'17:81','اور اعلان کردے کہ حق آچکا اور ناحق نابود ہوگیا۔ یقیناً باطل تھا بھی نابود ہونے والا. ');
INSERT INTO "junagarri" VALUES(17,82,'17:82','یہ قرآن جو ہم نازل کر رہے ہیں مومنوں کے لئے تو سراسر شفا اور رحمت ہے۔ ہاں ظالموں کو بجز نقصان کے اور کوئی زیادتی نہیں ہوتی. ');
INSERT INTO "junagarri" VALUES(17,83,'17:83','اور انسان پر جب ہم اپنا انعام کرتے ہیں تو وه منھ موڑ لیتا ہے اور کروٹ بدل لیتا ہے اور جب اسے کوئی تکلیف پہنچتی ہے تو وه مایوس ہوجاتا ہے. ');
INSERT INTO "junagarri" VALUES(17,84,'17:84','کہہ دیجئیے! کہ ہر شخص اپنے طریقہ پر عامل ہے جو پوری ہدایت کے راستے پر ہیں انہیں تمہارا رب ہی بخوبی جاننے والا ہے.');
INSERT INTO "junagarri" VALUES(17,85,'17:85','اور یہ لوگ آپ سے روح کی بابت سوال کرتے ہیں، آپ جواب دے دیجئیے کہ روح میرے رب کے حکم سے ہے اور تمہیں بہت ہی کم علم دیا گیا ہے. ');
INSERT INTO "junagarri" VALUES(17,86,'17:86','اور اگر ہم چاہیں تو جو وحی آپ کی طرف ہم نے اتاری ہے سب سلب کرلیں ، پھر آپ کو اس کے لئے ہمارے مقابلے میں کوئی حمایتی میسر نہ آسکے. ');
INSERT INTO "junagarri" VALUES(17,87,'17:87','سوائے آپ کے رب کی رحمت کے ، یقیناً آپ پر اس کا بڑا ہی فضل ہے.');
INSERT INTO "junagarri" VALUES(17,88,'17:88','کہہ دیجیئے کہ اگر تمام انسان اور کل جنات مل کر اس قرآن کے مثل لانا چاہیں تو ان سب سے اس کے مثل لانا ناممکن ہے گو وه (آپس میں) ایک دوسرے کے مددگار بھی بن جائیں. ');
INSERT INTO "junagarri" VALUES(17,89,'17:89','ہم نے تو اس قرآن میں لوگوں کے سمجھنے کے لئے ہر طرح سے تمام مثالیں بیان کردی ہیں، مگر اکثر لوگ انکار سے باز نہیں ');
INSERT INTO "junagarri" VALUES(17,90,'17:90','انہوں نے کہا کہ ہم آپ پر ہرگز ایمان لانے کے نہیں تاوقتیکہ آپ ہمارے لئے زمین سے کوئی چشمہ جاری نہ کردیں.');
INSERT INTO "junagarri" VALUES(17,91,'17:91','یا خود آپ کے لئے ہی کوئی باغ ہو کھجوروں اور انگوروں کا اور اس کے درمیان آپ بہت سی نہریں جاری کر دکھائیں.');
INSERT INTO "junagarri" VALUES(17,92,'17:92','یا آپ آسمان کو ہم پر ٹکڑے ٹکڑے کرکے گرا دیں جیسا کہ آپ کا گمان ہے یا آپ خود اللہ تعالیٰ کو اور فرشتوں کو ہمارے سامنے لاکھڑا کریں. ');
INSERT INTO "junagarri" VALUES(17,93,'17:93','یا آپ کے اپنے لئے کوئی سونے کا گھر ہوجائے یا آپ آسمان پر چڑھ جائیں اور ہم تو آپ کے چڑھ جانے کا بھی اس وقت تک ہرگز یقین نہیں کریں گے جب تک کہ آپ ہم پر کوئی کتاب نہ اتار لائیں جسے ہم خود پڑھ لیں ، آپ جواب دے دیں کہ میرا پروردگار پاک ہے میں تو صرف ایک انسان ہی ہوں جو رسول بنایا گیا ہوں. ');
INSERT INTO "junagarri" VALUES(17,94,'17:94','لوگوں کے پاس ہدایت پہنچ چکنے کے بعد ایمان سے روکنے والی صرف یہی چیز رہی کہ انہوں نے کہا کیا اللہ نے ایک انسان کو ہی رسول بنا کر بھیجا؟ ');
INSERT INTO "junagarri" VALUES(17,95,'17:95','آپ کہہ دیں کہ اگر زمین میں فرشتے چلتے پھرتے اور رہتے بستے ہوتے تو ہم بھی ان کے پاس کسی آسمانی فرشتے ہی کو رسول بنا کر بھیجتے. ');
INSERT INTO "junagarri" VALUES(17,96,'17:96','کہہ دیجئیے کہ میرے اور تمہارے درمیان اللہ تعالیٰ کا گواه ہونا کافی ہے ۔ وه اپنے بندوں سے خوب آگاه اور بخوبی دیکھنے والا ہے.');
INSERT INTO "junagarri" VALUES(17,97,'17:97','اللہ جس کی رہنمائی کرے تو وه ہدایت یافتہ ہے اور جسے وه راه سے بھٹکا دے ناممکن ہے کہ تو اس کا مددگار اس کے سوا کسی اور کو پائے ، ایسے لوگوں کا ہم بروز قیامت اوندھے منھ حشر کریں گے ، دراں حالیکہ وه اندھے، گونگے اور بہرے ہوں گے ، ان کا ٹھکانہ جہنم ہوگا۔ جب کبھی وه بجھنے لگے گی ہم ان پر اسے اور بھڑکا دیں گے.');
INSERT INTO "junagarri" VALUES(17,98,'17:98','یہ سب ہماری آیتوں سے کفر کرنے اور اس کہنے کا بدلہ ہے کہ کیا جب ہم ہڈیاں اور ریزے ریزے ہوجائیں گے پھر ہم نئی پیدائش میں اٹھا کھڑے کئے جائیں گے؟');
INSERT INTO "junagarri" VALUES(17,99,'17:99','کیا انہوں نے اس بات پر نظر نہیں کی کہ جس اللہ نے آسمان وزمین کو پیدا کیا ہے وه ان جیسوں کی پیدائش پر پورا قادر ہے ، اسی نے ان کے لئے ایک ایسا وقت مقرر کر رکھا ہے جو شک شبہ سے یکسر خالی ہے ، لیکن ظالم لوگ انکار کئے بغیر رہتے ہی نہیں.');
INSERT INTO "junagarri" VALUES(17,100,'17:100','کہہ دیجیئے کہ اگر بالفرض تم میرے رب کی رحمتوں کے خزانوں کے مالک بن جاتے تو تم اس وقت بھی اس کے خرچ ہوجانے کے خوف سے اس کو روکے رکھتے اور انسان ہے ہی تنگ دل.');
INSERT INTO "junagarri" VALUES(17,101,'17:101','ہم نے موسیٰ کو نو معجزے بالکل صاف صاف عطا فرمائے، تو خود ہی بنی اسرائیل سے پوچھ لے کہ جب وه ان کے پاس پہنچے تو فرعون بولا کہ اے موسیٰ میرے خیال میں تو تجھ پر جادو کردیا گیا ہے.');
INSERT INTO "junagarri" VALUES(17,102,'17:102','موسیٰ نے جواب دیا کہ یہ تو تجھے علم ہوچکا ہے کہ آسمان وزمین کے پروردگار ہی نے یہ معجزے دکھانے، سمجھانے کو نازل فرمائے ہیں، اے فرعون! میں تو سمجھ رہا ہوں کہ تو یقیناً برباد وہلاک کیا گیا ہے.');
INSERT INTO "junagarri" VALUES(17,103,'17:103','آخر فرعون نے پختہ اراده کر لیا کہ انہیں زمین سے ہی اکھیڑ دے تو ہم نے خود اسے اور اس کے تمام ساتھیوں کو غرق کر دیا.');
INSERT INTO "junagarri" VALUES(17,104,'17:104','اس کے بعد ہم نے بنی اسرائیل سے فرما دیا کہ اس سرزمین پر تم رہو سہو۔ ہاں جب آخرت کا وعده آئے گا ہم تم سب کو سمیٹ اور لپیٹ کر لے آئیں گے.');
INSERT INTO "junagarri" VALUES(17,105,'17:105','اور ہم نے اس قرآن کو حق کے ساتھ اتارا اور یہ بھی حق کے ساتھ اترا ۔ ہم نے آپ کو صرف خوشخبری سنانے والا اور ڈرانے والا بنا کر بھیجا ہے.');
INSERT INTO "junagarri" VALUES(17,106,'17:106','قرآن کو ہم نے تھوڑا تھوڑا کرکے اس لئے اتارا ہے کہ آپ اسے بہ مہلت لوگوں کو سنائیں اور ہم نے خود بھی اسے بتدریج نازل فرمایا.');
INSERT INTO "junagarri" VALUES(17,107,'17:107','کہہ دیجئیے! تم اس پر ایمان لاؤ یا نہ لاؤ جنہیں اس سے پہلے علم دیا گیا ہے ان کے پاس تو جب بھی اس کی تلاوت کی جاتی ہے تو وه ٹھوڑیوں کے بل سجده میں گر پڑتے ہیں. ');
INSERT INTO "junagarri" VALUES(17,108,'17:108','اور کہتے ہیں کہ ہمارا رب پاک ہے، ہمارے رب کا وعده بلاشک وشبہ پورا ہو کر رہنے والا ہی ہے.');
INSERT INTO "junagarri" VALUES(17,109,'17:109','وه اپنی ٹھوڑیوں کے بل روتے ہوئے سجده میں گر پڑ تے ہیں اور یہ قرآن ان کی عاجزی اور خشوع اور خضوع بڑھا دیتا ہے. ');
INSERT INTO "junagarri" VALUES(17,110,'17:110','کہہ دیجئیے کہ اللہ کو اللہ کہہ کر پکارو یا رحمٰن کہہ کر، جس نام سے بھی پکارو تمام اچھے نام اسی کے ہیں ۔ نہ تو تو اپنی نماز بہت بلند آواز سے پڑھ اور نہ بالکل پوشیده بلکہ اس کے درمیان کا راستہ تلاش کرلے. ');
INSERT INTO "junagarri" VALUES(17,111,'17:111','اور یہ کہہ دیجیئے کہ تمام تعریفیں اللہ ہی کے لئے ہیں جو نہ اولاد رکھتا ہے نہ اپنی بادشاہت میں کسی کو شریک وساجھی رکھتا ہے اور نہ وه کمزور ہے کہ اسے کسی حمایتی کی ضرورت ہو اور تو اس کی پوری پوری بڑائی بیان کرتا ره.');
INSERT INTO "junagarri" VALUES(18,1,'18:1','تمام تعریفیں اسی اللہ کے لئے سزاوار ہیں جس نے اپنے بندے پر یہ قرآن اتارا اور اس میں کوئی کسر باقی نہ چھوڑی. ');
INSERT INTO "junagarri" VALUES(18,2,'18:2','بلکہ ہر طرح سے ٹھیک ٹھاک رکھا تاکہ اپنے پاس کی سخت سزا سے ہوشیار کردے اور ایمان لانے اور نیک عمل کرنے والوں کو خوشخبریاں سنا دے کہ ان کے لئے بہترین بدلہ ہے.');
INSERT INTO "junagarri" VALUES(18,3,'18:3','جس میں وه ہمیشہ ہمیشہ رہیں گے.');
INSERT INTO "junagarri" VALUES(18,4,'18:4','اور ان لوگوں کو بھی ڈرا دے جو کہتے ہیں کہ اللہ تعالیٰ اولاد رکھتا ہے. ');
INSERT INTO "junagarri" VALUES(18,5,'18:5','در حقیقت نہ تو خود انہیں اس کا علم ہے نہ ان کے باپ دادوں کو۔ یہ تہمت بڑی بری ہے جو ان کے منھ سے نکل رہی ہے وه نرا جھوٹ بک رہے ہیں.');
INSERT INTO "junagarri" VALUES(18,6,'18:6','پس اگر یہ لوگ اس بات پر ایمان نہ لائیں تو کیا آپ ان کے پیچھے اسی رنج میں اپنی جان ہلاک کر ڈالیں گے؟');
INSERT INTO "junagarri" VALUES(18,7,'18:7','روئے زمین پر جو کچھ ہے ہم نے اسے زمین کی رونق کا باعث بنایا ہے کہ ہم انہیں آزمالیں کہ ان میں سے کون نیک اعمال والا ہے.');
INSERT INTO "junagarri" VALUES(18,8,'18:8','اس پر جو کچھ ہے ہم اسے ایک ہموار صاف میدان کر ڈالنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(18,9,'18:9','کیا تو اپنے خیال میں غار اور کتبے والوں کو ہماری نشانیوں میں سے کوئی بہت عجیب نشانی سمجھ رہا ہے؟ ');
INSERT INTO "junagarri" VALUES(18,10,'18:10','ان چند نوجوانوں نے جب غار میں پناه لی تو دعا کی کہ اے ہمارے پروردگار! ہمیں اپنے پاس سے رحمت عطا فرما اور ہمارے کام میں ہمارے لئے راه یابی کو آسان کردے. ');
INSERT INTO "junagarri" VALUES(18,11,'18:11','پس ہم نے ان کے کانوں پر گنتی کے کئی سال تک اسی غار میں پردے ڈال دیئے. ');
INSERT INTO "junagarri" VALUES(18,12,'18:12','پھر ہم نے انہیں اٹھا کھڑا کیا کہ ہم یہ معلوم کرلیں کہ دونوں گروه میں سے اس انتہائی مدت کو جو انہوں نے گزاری کس نے زیاده یاد رکھی ہے.');
INSERT INTO "junagarri" VALUES(18,13,'18:13','ہم ان کا صحیح واقعہ تیرے سامنے بیان فرما رہے ہیں۔ یہ چند نوجوان اپنے رب پر ایمان لائے تھے اور ہم نے ان کی ہدایت میں ترقی دی تھی.');
INSERT INTO "junagarri" VALUES(18,14,'18:14','ہم نے ان کے دل مضبوط کردیئے تھے جب کہ یہ اٹھ کھڑے ہوئے اور کہنے لگے کہ ہمارا پروردگار تو وہی ہے جو آسمان وزمین کا پروردگار ہے، ناممکن ہے کہ ہم اس کے سوا کسی اور معبود کو پکاریں اگر ایسا کیا تو ہم نے نہایت ہی غلط بات کہی. ');
INSERT INTO "junagarri" VALUES(18,15,'18:15','یہ ہے ہماری قوم جس نے اس کے سوا اور معبود بنا رکھے ہیں۔ ان کے الہ ہونے کی یہ کوئی صاف دلیل کیوں پیش نہیں کرتے اللہ پر جھوٹ افترا باندھنے والے سے زیاده ظالم کون ہے؟');
INSERT INTO "junagarri" VALUES(18,16,'18:16','جب کہ تم ان سے اور اللہ کے سوا ان کے اور معبودوں سے کناره کش ہوگئے تو اب تم کسی غار میں جا بیٹھو، تمہارا رب تم پر اپنی رحمت پھیلا دے گا اور تمہارے لئے تمہارے کام میں سہولت مہیا کردے گ.ا');
INSERT INTO "junagarri" VALUES(18,17,'18:17','آپ دیکھیں گے کہ آفتاب بوقت طلوع ان کے غار سے دائیں جانب کو جھک جاتا ہے اور بوقت غروب ان کے بائیں جانب کترا جاتا ہے اور وه اس غار کی کشاده جگہ میں ہیں ۔ یہ اللہ کی نشانیوں میں سے ہے ۔ اللہ تعالیٰ جس کی رہبری فرمائے وه راه راست پر ہے اور جسے وه گمراه کردے ناممکن ہے کہ آپ اس کا کوئی کارساز اور رہنما پاسکیں. ');
INSERT INTO "junagarri" VALUES(18,18,'18:18','آپ خیال کرتے کہ وه بیدار ہیں، حالانکہ وه سوئے ہوئے تھے ، خود ہم ہی انہیں دائیں بائیں کروٹیں دلایا کرتے تھے ، ان کا کتا بھی چوکھٹ پر اپنے ہاتھ پھیلائے ہوئے تھا۔ اگر آپ جھانک کر انہیں دیکھنا چاہتے تو ضرور الٹے پاؤں بھاگ کھڑے ہوتے اور ان کے رعب سے آپ پر دہشت چھا جاتی. ');
INSERT INTO "junagarri" VALUES(18,19,'18:19','اسی طرح ہم نے انہیں جگا کر اٹھا دیا کہ آپس میں پوچھ گچھ کرلیں۔ ایک کہنے والے نے کہا کہ کیوں بھئی تم کتنی دیر ٹھہرے رہے؟ انہوں نے جواب دیا کہ ایک دن یا ایک دن سے بھی کم ۔ کہنے لگے کہ تمہارے ٹھہرے رہنے کا بخوبی علم اللہ تعالیٰ ہی کو ہے ۔ اب تو تم اپنے میں سے کسی کو اپنی یہ چاندی دے کر شہر بھیجو وه خوب دیکھ بھال لے کہ شہر کا کون سا کھانا پاکیزه تر ہے ، پھر اسی میں سے تمہارے کھانے کے لئے لے آئے، اور وه بہت احتیاط اور نرمی برتے اور کسی کو تمہاری خبر نہ ہونے دے. ');
INSERT INTO "junagarri" VALUES(18,20,'18:20','اگر یہ کافر تم پر غلبہ پالیں تو تمہیں سنگسار کر دیں گے یا تمہیں پھر اپنے دین میں لوٹا لیں گے اور پھر تم کبھی بھی کامیاب نہ ہو سکو گے. ');
INSERT INTO "junagarri" VALUES(18,21,'18:21','ہم نے اس طرح لوگوں کو ان کے حال سے آگاه کر دیا کہ وه جان لیں کہ اللہ کا وعده بالکل سچا ہے اور قیامت میں کوئی شک وشبہ نہیں ۔ جب کہ وه اپنے امر میں آپس میں اختلاف کر رہے تھے کہنے لگے کہ ان کے غار پر ایک عمارت بنا لو۔ ان کا رب ہی ان کے حال کا زیاده عالم ہے۔ جن لوگوں نے ان کے بارے میں غلبہ پایا وه کہنے لگے کہ ہم تو ان کے آس پاس مسجد بنالیں گے. ');
INSERT INTO "junagarri" VALUES(18,22,'18:22','کچھ لوگ تو کہیں گے کہ اصحاب کہف تین تھے اور چوتھا ان کا کتا تھا ۔ کچھ کہیں گے کہ پانچ تھے اور چھٹا ان کا کتا تھا، غیب کی باتوں میں اٹکل (کے تیر تکے) چلاتے ہیں ، کچھ کہیں گے کہ وه سات ہیں اور آٹھواں ان کا کتا ہے۔ آپ کہہ دیجیئے کہ میرا پروردگار ان کی تعداد کو بخوبی جاننے والا ہے، انہیں بہت ہی کم لوگ جانتے ہیں ۔ پس آپ ان کے مقدمے میں صرف سرسری گفتگو کریں اور ان میں سے کسی سے ان کے بارے میں پوچھ گچھ بھی نہ کریں. ');
INSERT INTO "junagarri" VALUES(18,23,'18:23','اور ہرگز ہرگز کسی کام پر یوں نہ کہنا کہ میں اسے کل کروں گا.');
INSERT INTO "junagarri" VALUES(18,24,'18:24','مگر ساتھ ہی انشاءاللہ کہہ لینا ۔ اور جب بھی بھولے، اپنے پروردگار کی یاد کر لیا کرنا اور کہتے رہنا کہ مجھے پوری امید ہے کہ میرا رب مجھے اس سے بھی زیاده ہدایت کے قریب کی بات کی رہبری کرے. ');
INSERT INTO "junagarri" VALUES(18,25,'18:25','وه لوگ اپنے غار میں تین سو سال تک رہے اور نو سال اور زیاده گزارے. ');
INSERT INTO "junagarri" VALUES(18,26,'18:26','آپ کہہ دیں اللہ ہی کو ان کے ٹھہرے رہنے کی مدت کا بخوبی علم ہے، آسمانوں اور زمینوں کا غیب صرف اسی کو حاصل ہے وه کیا ہی اچھا دیکھنے سننے والا ہے ۔ سوائے اللہ کے ان کا کوئی مددگار نہیں، اللہ تعالیٰ اپنے حکم میں کسی کو شریک نہیں کرتا.');
INSERT INTO "junagarri" VALUES(18,27,'18:27','تیری جانب جو تیرے رب کی کتاب وحی کی گئی ہے اسے پڑھتا ره ، اس کی باتوں کو کوئی بدلنے والا نہیں تو اس کے سوا ہرگزکوئی پناه کی جگہ نہ پائے گا. ');
INSERT INTO "junagarri" VALUES(18,28,'18:28','اور اپنے آپ کو انہیں کے ساتھ رکھا کر جو اپنے پروردگار کو صبح شام پکارتے ہیں اور اسی کے چہرے کے ارادے رکھتے ہیں (رضامندی چاہتے ہیں)، خبردار! تیری نگاہیں ان سے نہ ہٹنے پائیں کہ دنیوی زندگی کے ٹھاٹھ کے ارادے میں لگ جا۔ دیکھ اس کا کہنا نہ ماننا جس کے دل کو ہم نے اپنے ذکر سے غافل کردیا ہے اور جو اپنی خواہش کے پیچھے پڑا ہوا ہے اور جس کا کام حد سے گزر چکا ہے. ');
INSERT INTO "junagarri" VALUES(18,29,'18:29','اور اعلان کردے کہ یہ سراسر برحق قرآن تمہارے رب کی طرف سے ہے۔ اب جو چاہے ایمان لائے اور جو چاہے کفر کرے۔ ظالموں کے لئے ہم نے وه آگ تیار کر رکھی ہے جس کی قناتیں انہیں گھیر لیں گی۔ اگر وه فریاد رسی چاہیں گے تو ان کی فریاد رسی اس پانی سے کی جائے گی جو تیل کی تلچھٹ جیسا ہوگا جو چہرے بھون دے گا، بڑا ہی برا پانی ہے اور بڑی بری آرام گاه (دوزخ) ہے.');
INSERT INTO "junagarri" VALUES(18,30,'18:30','یقیناً جو لوگ ایمان لائیں اور نیک اعمال کریں تو ہم کسی نیک عمل کرنے والے کا ثواب ضائع نہیں کرتے. ');
INSERT INTO "junagarri" VALUES(18,31,'18:31','ان کے لئے ہمیشگی والی جنتیں ہیں، ان کے نیچے نہریں جاری ہوں گی، وہاں یہ سونے کے کنگن پہنائے جائیں گے اور سبز رنگ کے نرم وباریک اور موٹے ریشم کے لباس پہنیں گے ، وہاں تختوں کے اوپر تکیے لگائے ہوئے ہوں گے۔ کیا خوب بدلہ ہے، اور کس قدر عمده آرام گاه ہے.');
INSERT INTO "junagarri" VALUES(18,32,'18:32','اور انہیں ان دو شخصوں کی مثال بھی سنا دے جن میں سے ایک کو ہم نے دو باغ انگوروں کے دے رکھے تھے اور جنہیں کھجوروں کے درختوں سے ہم نے گھیر رکھا تھا اور دونوں کے درمیان کھیتی لگا رکھی تھی. ');
INSERT INTO "junagarri" VALUES(18,33,'18:33','دونوں باغ اپنا پھل خوب لائے اور اس میں کسی طرح کی کمی نہ کی اور ہم نے ان باغوں کے درمیان نہر جاری کر رکھی تھی. ');
INSERT INTO "junagarri" VALUES(18,34,'18:34','الغرض اس کے پاس میوے تھے، ایک دن اس نے باتوں ہی باتوں میں اپنے ساتھی سے کہا کہ میں تجھ سے زیاده مالدار اور جتھے کے اعتبار سے بھی زیاده مضبوط ہوں.');
INSERT INTO "junagarri" VALUES(18,35,'18:35','اور یہ اپنے باغ میں گیا اور تھا اپنی جان پر ظلم کرنے والا۔ کہنے لگا کہ میں خیال نہیں کرسکتا کہ کسی وقت بھی یہ برباد ہوجائے.');
INSERT INTO "junagarri" VALUES(18,36,'18:36','اور نہ میں قیامت کو قائم ہونے والی خیال کرتا ہوں اور اگر (بالفرض) میں اپنے رب کی طرف لوٹایا بھی گیا تو یقیناً میں (اس لوٹنے کی جگہ) اس سے بھی زیاده بہتر پاؤں گا.');
INSERT INTO "junagarri" VALUES(18,37,'18:37','اس کے ساتھی نے اس سے باتیں کرتے ہوئے کہا کہ کیا تو اس (معبود) سے کفر کرتا ہے جس نے تجھے مٹی سے پیدا کیا۔ پھر نطفے سے پھر تجھے پورا آدمی بنا دیا. ');
INSERT INTO "junagarri" VALUES(18,38,'18:38','لیکن میں تو عقیده رکھتا ہوں کہ وہی اللہ میرا پروردگار ہے میں اپنے رب کے ساتھ کسی کو شریک نہ کروں گا. ');
INSERT INTO "junagarri" VALUES(18,39,'18:39','تو نے اپنے باغ میں جاتے وقت کیوں نہ کہا کہ اللہ کا چاہا ہونے والا ہے، کوئی طاقت نہیں مگر اللہ کی مدد سے، اگر تو مجھے مال واولاد میں اپنے سے کم دیکھ رہا ہے.');
INSERT INTO "junagarri" VALUES(18,40,'18:40','بہت ممکن ہے کہ میرا رب مجھے تیرے اس باغ س ے بھی بہتر دے اور اس پر آسمانی عذاب بھیج دے تو یہ چٹیل اور چکنا میدان بن جائے. ');
INSERT INTO "junagarri" VALUES(18,41,'18:41','یا اس کا پانی نیچے اتر جائے اور تیرے بس میں نہ رہے کہ تو اسے ڈھونڈ لائے. ');
INSERT INTO "junagarri" VALUES(18,42,'18:42','اور اس کے (سارے) پھل گھیر لئے گئے ، پس وه اپنے اس خرچ پر جو اس نے اس میں کیا تھا اپنے ہاتھ ملنے لگا اور وه باغ تو اوندھا الٹا پڑا تھا اور (وه شخص) یہ کہہ رہا تھا کہ کاش! میں اپنے رب کے ساتھ کسی کو بھی شریک نہ کرتا. ');
INSERT INTO "junagarri" VALUES(18,43,'18:43','اس کی حمایت میں کوئی جماعت نہ اٹھی کہ اللہ سے اس کا کوئی بچاؤ کرتی اور نہ وه خود ہی بدلہ لینے والا بن سکا.');
INSERT INTO "junagarri" VALUES(18,44,'18:44','یہیں سے (ثابت ہے) کہ اختیارات اللہ برحق کے لئے ہیں وه ثواب دینے اور انجام کے اعتبار سے بہت ہی بہتر ہے.');
INSERT INTO "junagarri" VALUES(18,45,'18:45','ان کے سامنے دنیا کی زندگی کی مثال (بھی) بیان کرو جیسے پانی جسے ہم آسمان سے اتارتے ہیں اس سے زمین کا سبزه ملا جلا (نکلا) ہے، پھر آخر کار وه چورا چورا ہوجاتا ہے جسے ہوائیں اڑائے لئے پھرتی ہیں۔ اللہ تعالیٰ ہر چیز پر قادر ہے. ');
INSERT INTO "junagarri" VALUES(18,46,'18:46','مال واولاد تو دنیا کی ہی زینت ہے ، اور (ہاں) البتہ باقی رہنے والی نیکیاں تیرے رب کے نزدیک از روئے ثواب اور (آئنده کی) اچھی توقع کے بہت بہتر ہیں.');
INSERT INTO "junagarri" VALUES(18,47,'18:47','اور جس دن ہم پہاڑوں کو چلائیں گے اور زمین کو تو صاف کھلی ہوئی دیکھے گا اور تمام لوگوں کو ہم اکٹھا کریں گے ان میں سے ایک کو بھی باقی نہ چھوڑیں گے. ');
INSERT INTO "junagarri" VALUES(18,48,'18:48','اور سب کے سب تیرے رب کے سامنے صف بستہ حاضر کئے جائیں گے۔ یقیناً تم ہمارے پاس اسی طرح آئے جس طرح ہم نے تمہیں پہلی مرتبہ پیدا کیا تھا لیکن تم تو اسی خیال میں رہے کہ ہم ہرگز تمہارے لئے کوئی وعدے کا وقت مقرر کریں گے بھی نہیں.');
INSERT INTO "junagarri" VALUES(18,49,'18:49','اور نامہٴ اعمال سامنے رکھ دیئے جائیں گے۔ پس تو دیکھے گا کہ گنہگار اس کی تحریر سے خوفزده ہو رہے ہوں گے اور کہہ رہے ہوں گے ہائے ہماری خرابی یہ کیسی کتاب ہے جس نے کوئی چھوٹا بڑا بغیر گھیرے کے باقی ہی نہیں چھوڑا، اور جو کچھ انہوں نے کیا تھا سب موجود پائیں گے اور تیرا رب کسی پر ظلم وستم نہ کرے گا');
INSERT INTO "junagarri" VALUES(18,50,'18:50','اور جب ہم نے فرشتوں کو حکم دیا کہ تم آدم کو سجده کرو تو ابلیس کے سوا سب نے سجده کیا، یہ جنوں میں سے تھا ، اس نے اپنے پروردگار کی نافرمانی کی ، کیا پھر بھی تم اسے اور اس کی اولاد کو مجھے چھوڑ کر اپنا دوست بنا رہے ہو؟ حالانکہ وه تم سب کا دشمن ہے ۔ ایسے ظالموں کا کیا ہی برا بدل ہے. ');
INSERT INTO "junagarri" VALUES(18,51,'18:51','میں نے انہیں آسمانوں وزمین کی پیدائش کے وقت موجود نہیں رکھا تھا اور نہ خود ان کی اپنی پیدائش میں ، اور میں گمراه کرنے والوں کو اپنا مددگار بنانے والا بھی نہیں. ');
INSERT INTO "junagarri" VALUES(18,52,'18:52','اور جس دن وه فرمائے گا کہ تمہارے خیال میں جو میرے شریک تھے انہیں پکارو! یہ پکاریں گے لیکن ان میں سے کوئی بھی جواب نہ دے گا ہم ان کے درمیان ہلاکت کا سامان کردیں گے. ');
INSERT INTO "junagarri" VALUES(18,53,'18:53','اور گنہگار جہنم کو دیکھ کرسمجھ لیں گے کہ وه اسی میں جھونکے جانے والے ہیں لیکن اس سے بچنے کی جگہ نہ پائیں گے. ');
INSERT INTO "junagarri" VALUES(18,54,'18:54','ہم نے اس قرآن میں ہر ہر طریقے سے تمام کی تمام مثالیں لوگوں کے لئے بیان کردی ہیں لیکن انسان سب سے زیاده جھگڑالو ہے. ');
INSERT INTO "junagarri" VALUES(18,55,'18:55','لوگوں کے پاس ہدایت آچکنے کے بعد انہیں ایمان لانے اور اپنے رب سے استغفار کرنے سے صرف اسی چیز نے روکا کہ اگلے لوگوں کا سا معاملہ انہیں بھی پیش آئے یا ان کے سامنے کھلم کھلا عذاب آموجود ہوجائے. ');
INSERT INTO "junagarri" VALUES(18,56,'18:56','ہم تو اپنے رسولوں کو صرف اس لئے بھیجتے ہیں کہ وه خوشخبریاں سنا دیں اور ڈرا دیں۔ کافر لوگ باطل کے سہارے جھگڑتے ہیں اور (چاہتے ہیں کہ) اس سے حق کو لڑکھڑادیں، انہوں نے میری آیتوں کو اور جس چیز سے ڈرایا جائے اسے مذاق بنا ڈالا ہے. ');
INSERT INTO "junagarri" VALUES(18,57,'18:57','اس سے بڑھ کر ظالم کون ہے؟ جسے اس کے رب کی آیتوں سے نصیحت کی جائے وه پھر منھ موڑے رہے اور جو کچھ اس کے ہاتھوں نے آگے بھیج رکھا ہے اسے بھول جائے، بےشک ہم نے ان کے دلوں پر پردے ڈال دیئے ہیں کہ وه اسے (نہ) سمجھیں اور ان کے کانوں میں گرانی ہے، گو تو انہیں ہدایت کی طرف بلاتا رہے، لیکن یہ کبھی بھی ہدایت نہیں پانے کے.');
INSERT INTO "junagarri" VALUES(18,58,'18:58','تیرا پروردگار بہت ہی بخشش والا اور مہربانی والا ہے وه اگر ان کے اعمال کی سزا میں پکڑے تو بےشک انہیں جلد ہی عذاب کردے، بلکہ ان کے لئے ایک وعده کی گھڑی مقرر ہے جس سے وه سرکنے کی ہرگز جگہ نہیں پائیں گے. ');
INSERT INTO "junagarri" VALUES(18,59,'18:59','یہ ہیں وه بستیاں جنہیں ہم نے ان کے مظالم کی بنا پر غارت کردیا اور ان کی تباہی کی بھی ہم نے ایک میعاد مقرر کر رکھی تھی. ');
INSERT INTO "junagarri" VALUES(18,60,'18:60','جب کہ موسیٰ نے اپنے نوجوان سے کہا کہ میں تو چلتا ہی رہوں گا یہاں تک کہ دو دریاؤں کے سنگم پر پہنچوں، خواه مجھے سالہا سال چلنا پڑے. ');
INSERT INTO "junagarri" VALUES(18,61,'18:61','جب وه دونوں دریا کے سنگم پر پہنچے، وہاں اپنی مچھلی بھول گئے جس نے دریا میں سرنگ جیسا اپنا راستہ بنالیا.');
INSERT INTO "junagarri" VALUES(18,62,'18:62','جب یہ دونوں وہاں آگے بڑھے تو موسیٰ نے اپنے نوجوان سے کہا کہ لا ہمارا کھانا دے ہمیں تو اپنے اس سفر سے سخت تکلیف اٹھانی پڑی.');
INSERT INTO "junagarri" VALUES(18,63,'18:63','اس نے جواب دیا کہ کیا آپ نے دیکھا بھی؟ جب کہ ہم پتھر سے ٹیک لگا کر آرام کر رہے تھے وہیں میں مچھلی بھول گیا تھا، دراصل شیطان نے ہی مجھے بھلا دیا کہ میں آپ سے اس کا ذکر کروں۔ اس مچھلی نے ایک انوکھے طور پر دریا میں اپنا راستہ بنالیا.');
INSERT INTO "junagarri" VALUES(18,64,'18:64','موسیٰ نے کہا یہی تھا جس کی تلاش میں ہم تھے چنانچہ وہیں سے اپنے قدموں کے نشان ڈھونڈتے ہوئے واپس لوٹے.');
INSERT INTO "junagarri" VALUES(18,65,'18:65','پس ہمارے بندوں میں سے ایک بندے کو پایا، جسے ہم نے اپنے پاس کی خاص رحمت عطا فرما رکھی تھی اور اسے اپنے پاس سے خاص علم سکھا رکھا تھا.');
INSERT INTO "junagarri" VALUES(18,66,'18:66','اس سے موسیٰ نے کہا کہ میں آپ کی تابعداری کروں؟ کہ آپ مجھے اس نیک علم کو سکھا دیں جو آپ کو سکھایا گیا ہے.');
INSERT INTO "junagarri" VALUES(18,67,'18:67','اس نے کہا آپ میرے ساتھ ہرگز صبر نہیں کرسکتے.');
INSERT INTO "junagarri" VALUES(18,68,'18:68','اور جس چیز کو آپ نے اپنے علم میں نہ لیا ہو اس پر صبر بھی کیسے کر سکتے ہیں؟');
INSERT INTO "junagarri" VALUES(18,69,'18:69','موسیٰ نے جواب دیا کہ انشاءاللہ آپ مجھے صبر کرنے والا پائیں گے اور کسی بات میں، میں آپ کی نافرمانی نہ کروں گا.');
INSERT INTO "junagarri" VALUES(18,70,'18:70','اس نے کہا اچھا اگر آپ میرے ساتھ ہی چلنے پر اصرار کرتے ہیں تو یاد رہے کسی چیز کی نسبت مجھ سے کچھ نہ پوچھنا جب تک کہ میں خود اس کی نسبت کوئی تذکره نہ کروں.');
INSERT INTO "junagarri" VALUES(18,71,'18:71','پھر وه دونوں چلے، یہاں تک کہ ایک کشتی میں سوار ہوئے، تو اس نے کشتی کے تختے توڑ دیئے، موسیٰ نے کہا کیا آپ اسے توڑ رہے ہیں تاکہ کشتی والوں کو ڈبو دیں، یہ تو آپ نے بڑی (خطرناک) بات کردی. ');
INSERT INTO "junagarri" VALUES(18,72,'18:72','اس نے جواب دیا کہ میں نے تو پہلے ہی تجھ سے کہہ دیا تھا کہ تو میرے ساتھ ہرگز صبر نہ کرسکے گا.');
INSERT INTO "junagarri" VALUES(18,73,'18:73','موسیٰ نے جواب دیا کہ میری بھول پر مجھے نہ پکڑیئے اور مجھے اپنے کام میں تنگی میں نہ ڈالیے. ');
INSERT INTO "junagarri" VALUES(18,74,'18:74','پھر دونوں چلے، یہاں تک کہ ایک لڑکے کو پایا، اس نے اسے مار ڈالا، موسیٰ نے کہا کہ کیا آپ نے ایک پاک جان کو بغیر کسی جان کے عوض مار ڈالا؟ بےشک آپ نے تو بڑی ناپسندیده حرکت کی. ');
INSERT INTO "junagarri" VALUES(18,75,'18:75','وه کہنے لگے کہ میں نے تم سے نہیں کہا تھا کہ تم میرے ہمراه ره کر ہرگز صبر نہیں کر سکتے.');
INSERT INTO "junagarri" VALUES(18,76,'18:76','موسیٰ (علیہ السلام) نے جواب دیا اگر اب اس کے بعد میں آپ سے کسی چیز کے بارے میں سوال کروں تو بیشک آپ مجھے اپنے ساتھ نہ رکھنا، یقیناً آپ میری طرف سے (حد) عذر کو پہنچ چکے.');
INSERT INTO "junagarri" VALUES(18,77,'18:77','پھر دونوں چلے ایک گاؤں والوں کے پاس آکر ان سے کھانا طلب کیا تو انہوں نے ان کی مہمانداری سے صاف انکار کر دیا ، دونوں نے وہاں ایک دیوار پائی جو گراہی چاہتی تھی، اس نے اسے ٹھیک اور درست کر دیا، موسیٰ (علیہ السلام) کہنے لگے اگر آپ چاہتے تو اس پر اجرت لے لیتے. ');
INSERT INTO "junagarri" VALUES(18,78,'18:78','اس نے کہا بس یہ جدائی ہے میرے اور تیرے درمیان ، اب میں تجھے ان باتوں کی اصلیت بھی بتا دوں گا جس پر تجھ سے صبر نہ ہو سکا. ');
INSERT INTO "junagarri" VALUES(18,79,'18:79','کشتی تو چند مسکینوں کی تھی جو دریا میں کام کاج کرتے تھے۔ میں نے اس میں کچھ توڑ پھوڑ کرنے کا اراده کر لیا کیونکہ ان کے آگے ایک بادشاه تھا جو ہر ایک (صحیح سالم) کشتی کو جبراً ضبط کر لیتا تھا.');
INSERT INTO "junagarri" VALUES(18,80,'18:80','اور اس لڑکے کے ماں باپ ایمان والے تھے۔ ہمیں خوف ہوا کہ کہیں یہ انہیں اپنی سرکشی اور کفر سے عاجز وپریشان نہ کر دے.');
INSERT INTO "junagarri" VALUES(18,81,'18:81','اس لئے ہم نے چاہا کہ انہیں ان کا پروردگار اس کے بدلے اس سے بہتر پاکیزگی والا اور اس سے زیاده محبت اور پیار والا بچہ عنایت فرمائے.');
INSERT INTO "junagarri" VALUES(18,82,'18:82','دیوار کا قصہ یہ ہے کہ اس شہر میں دو یتیم بچے ہیں جن کا خزانہ ان کی اس دیوار کے نیچے دفن ہے، ان کا باپ بڑا نیک شخص تھا تو تیرے رب کی چاہت تھی کہ یہ دونوں یتیم اپنی جوانی کی عمر میں آکر اپنا یہ خزانہ تیرے رب کی مہربانی اور رحمت سے نکال لیں، میں نے اپنی رائے سے کوئی کام نہیں کیا ، یہ تھی اصل حقیقت ان واقعات کی جن پر آپ سے صبر نہ ہو سکا.');
INSERT INTO "junagarri" VALUES(18,83,'18:83','آپ سے ذوالقرنین کا واقعہ یہ لوگ دریافت کر رہے ہیں ، آپ کہہ دیجئے کہ میں ان کا تھوڑا سا حال تمہیں پڑھ کر سناتا ہوں.');
INSERT INTO "junagarri" VALUES(18,84,'18:84','ہم نے اسے زمین میں قوت عطا فرمائی تھی اور اسے ہر چیز کے سامان بھی عنایت کردیے تھے.');
INSERT INTO "junagarri" VALUES(18,85,'18:85','وه ایک راه کے پیچھے لگا. ');
INSERT INTO "junagarri" VALUES(18,86,'18:86','یہاں تک کہ سورج ڈوبنے کی جگہ پہنچ گیا اور اسے ایک دلدل کےچشمے میں غروب ہوتا ہوا پایا اور اس چشمے کے پاس ایک قوم کو بھی پایا، ہم نے فرما دیا کہ اے ذوالقرنین! یا تو تو انہیں تکلیف پہنچائے یا ان کے بارے میں تو کوئی بہترین روش اختیار کرے. ');
INSERT INTO "junagarri" VALUES(18,87,'18:87','اس نے کہا کہ جو ظلم کرے گا اسے تو ہم بھی اب سزا دیں گے ، پھر وه اپنے پروردگار کی طرف لوٹایا جائے گا اور وه اسے سخت تر عذاب دے گا.');
INSERT INTO "junagarri" VALUES(18,88,'18:88','ہاں جو ایمان لائے اور نیک اعمال کرے اس کے لئے تو بدلے میں بھلائی ہے اور ہم اسے اپنے کام میں بھی آسانی ہی کا حکم دیں گے.');
INSERT INTO "junagarri" VALUES(18,89,'18:89','پھر وه اور راه کے پیچھے لگا. ');
INSERT INTO "junagarri" VALUES(18,90,'18:90','یہاں تک کہ جب سورج نکلنے کی جگہ تک پہنچا تو اسے ایک ایسی قوم پر نکلتا پایا کہ ان کے لئے ہم نے اس سے اور کوئی اوٹ نہیں بنائی. ');
INSERT INTO "junagarri" VALUES(18,91,'18:91','واقعہ ایسا ہی ہے اور ہم نے اس کے پاس کی کل خبروں کا احاطہ کر رکھا ہے.');
INSERT INTO "junagarri" VALUES(18,92,'18:92','وه پھر ایک سفر کے سامان میں لگا. ');
INSERT INTO "junagarri" VALUES(18,93,'18:93','یہاں تک کہ جب دو دیواروں کے درمیان پہنچا ان دونوں کے پرے اس نے ایک ایسی قوم پائی جو بات سمجھنے کے قریب بھی نہ تھی. ');
INSERT INTO "junagarri" VALUES(18,94,'18:94','انہوں نے کہا اے ذوالقرنین ! یاجوج ماجوج اس ملک میں (بڑے بھاری) فسادی ہیں ، تو کیا ہم آپ کے لئے کچھ خرچ کا انتظام کر دیں؟ (اس شرط پر کہ) آپ ہمارے اور ان کے درمیان ایک دیوار بنادیں.');
INSERT INTO "junagarri" VALUES(18,95,'18:95','اس نے جواب دیا کہ میرے اختیار میں میرے پروردگار نے جو دے رکھا ہے وہی بہتر ہے، تم صرف قوت طاقت سے میری مدد کرو. میں تم میں اور ان میں مضبوط حجاب بنا دیتا ہوں');
INSERT INTO "junagarri" VALUES(18,96,'18:96','مجھے لوہے کی چادریں لا دو۔ یہاں تک کہ جب ان دونوں پہاڑوں کے درمیان دیوار برابر کر دی تو حکم دیا کہ آگ تیز جلاؤ تاوقتیکہ لوہے کی ان چادروں کو بالکل آگ کر دیا، تو فرمایا میرے پاس لاؤ اس پر پگھلا ہوا تانبا ڈال دوں. ');
INSERT INTO "junagarri" VALUES(18,97,'18:97','پس تو ان میں اس دیوار کے اوپر چڑھنے کی طاقت تھی اور نہ ان میں کوئی سوراخ کر سکتے تھے.');
INSERT INTO "junagarri" VALUES(18,98,'18:98','کہا یہ صرف میرے رب کی مہربانی ہے ہاں جب میرے رب کا وعده آئے گا تو اسے زمین بوس کر دے گا ، بیشک میرے رب کا وعده سچا اور حق ہے.');
INSERT INTO "junagarri" VALUES(18,99,'18:99','اس دن ہم انہیں آپس میں ایک دوسرے میں گڈ مڈ ہوتے ہوئے چھوڑ دیں گے اور صور پھونک دیا جائے گا پس سب کو اکٹھا کرکے ہم جمع کر لیں گے.');
INSERT INTO "junagarri" VALUES(18,100,'18:100','اس دن ہم جہنم کو (بھی) کافروں کے سامنے لاکھڑا کر دیں گے.');
INSERT INTO "junagarri" VALUES(18,101,'18:101','جن کی آنکھیں میری یاد سے پردے میں تھی اور (امر حق) سن بھی نہیں سکتے تھے.');
INSERT INTO "junagarri" VALUES(18,102,'18:102','کیا کافر یہ خیال کیے بیٹھے ہیں؟ کہ میرے سوا وه میرے بندوں کو اپنا حمایتی بنا لیں گے؟ (سنو) ہم نے تو ان کفار کی مہمانی کے لئے جہنم کو تیار کر رکھا ہے. ');
INSERT INTO "junagarri" VALUES(18,103,'18:103','کہہ دیجئے کہ اگر (تم کہو تو) میں تمہیں بتا دوں کہ باعتبار اعمال سب سے زیاده خسارے میں کون ہیں؟');
INSERT INTO "junagarri" VALUES(18,104,'18:104','وه ہیں کہ جن کی دنیوی زندگی کی تمام تر کوششیں بیکار ہوگئیں اور وه اسی گمان میں رہے کہ وه بہت اچھے کام کر رہے ہیں. ');
INSERT INTO "junagarri" VALUES(18,105,'18:105','یہی وه لوگ ہیں جنہوں نے اپنے پروردگار کی آیتوں اور اس کی ملاقات سے کفر کیا ، اس لئے ان کے اعمال غارت ہوگئے پس قیامت کے دن ہم ان کا کوئی وزن قائم نہ کریں گے. ');
INSERT INTO "junagarri" VALUES(18,106,'18:106','حال یہ ہے کہ ان کا بدلہ جہنم ہے کیونکہ انہوں نے کفر کیا اور میری آیتوں اور میرے رسولوں کو مذاق میں اڑایا.');
INSERT INTO "junagarri" VALUES(18,107,'18:107','جو لوگ ایمان لائے اور انہوں نے کام بھی اچھے کیے یقیناً ان کے لئے الفردوس کے باغات کی مہمانی ہے.');
INSERT INTO "junagarri" VALUES(18,108,'18:108','جہاں وه ہمیشہ رہا کریں گے جس جگہ کو بدلنے کا کبھی بھی ان کا اراده ہی نہ ہوگا. ');
INSERT INTO "junagarri" VALUES(18,109,'18:109','کہہ دیجئے کہ اگر میرے پروردگار کی باتوں کے لکھنے کے لئے سمندر سیاہی بن جائے تو وه بھی میرے رب کی باتوں کے ختم ہونے سے پہلے ہی ختم ہو جائے گا، گو ہم اسی جیسا اور بھی اس کی مدد میں لے آئیں.');
INSERT INTO "junagarri" VALUES(18,110,'18:110','آپ کہہ دیجئے کہ میں تو تم جیسا ہی ایک انسان ہوں - (ہاں) میری جانب وحی کی جاتی ہے کہ سب کا معبود صرف ایک ہی معبود ہے ، تو جسے بھی اپنے پروردگار سے ملنے کی آرزو ہو اسے چاہئے کہ نیک اعمال کرے اور اپنے پروردگار کی عبادت میں کسی کو بھی شریک نہ کرے.');
INSERT INTO "junagarri" VALUES(19,1,'19:1','کہیعص.*');
INSERT INTO "junagarri" VALUES(19,2,'19:2','یہ ہے تیرے پروردگار کی اس مہربانی کا ذکر جو اس نے اپنے بندے زکریا پر کی تھی.');
INSERT INTO "junagarri" VALUES(19,3,'19:3','جبکہ اس نے اپنے رب سے چپکے چپکے دعا کی تھی. ');
INSERT INTO "junagarri" VALUES(19,4,'19:4','کہ اے میرے پروردگار! میری ہڈیاں کمزور ہوگئی ہیں اور سر بڑھاپے کی وجہ سے بھڑک اٹھا ہے ، لیکن میں کبھی بھی تجھ سے دعا کر کے محروم نہیں رہا. ');
INSERT INTO "junagarri" VALUES(19,5,'19:5','مجھے اپنے مرنے کے بعد اپنے قرابت والوں کا ڈر ہے ، میری بیوی بھی بانجھ ہے پس تو مجھے اپنے پاس سے وارث عطا فرم.');
INSERT INTO "junagarri" VALUES(19,6,'19:6','جو میرا بھی وارث ہو اور یعقوب (علیہ السلام) کے خاندان کا بھی جانشین اور میرے رب! تو اسے مقبول بنده بنا لے.');
INSERT INTO "junagarri" VALUES(19,7,'19:7','اے زکریا! ہم تجھے ایک بچے کی خوشخبری دیتے ہیں جس کا نام یحيٰ ہے، ہم نے اس سے پہلے اس کا ہم نام بھی کسی کو نہیں کیا. ');
INSERT INTO "junagarri" VALUES(19,8,'19:8','زکریا (علیہ السلام) کہنے لگے میرے رب! میرے ہاں لڑکا کیسے ہوگا، جبکہ میری بیوی بانجھ اور میں خود بڑھاپے کے انتہائی ضعف کو پہنچ چکا ہوں. ');
INSERT INTO "junagarri" VALUES(19,9,'19:9','ارشاد ہوا کہ وعده اسی طرح ہو چکا، تیرے رب نے فرما دیا ہے کہ مجھ پر تو یہ بالکل آسان ہے اور تو خود جبکہ کچھ نہ تھا میں تجھے پیدا کر چکا ہوں. ');
INSERT INTO "junagarri" VALUES(19,10,'19:10','کہنے لگے میرے پروردگار میرے لئے کوئی علامت مقرر فرما دے، ارشاد ہوا کہ تیرے لئے علامت یہ ہے کہ باوجود بھلا چنگا ہونے کے تو تین راتوں تک کسی شخص سے بول نہ سکے گا. ');
INSERT INTO "junagarri" VALUES(19,11,'19:11','اب زکریا (علیہ السلام) اپنے حجرے سے نکل کر اپنی قوم کے پاس آکر انہیں اشاره کرتے ہیں کہ تم صبح وشام اللہ تعالیٰ کی تسبیح بیان کرو. ');
INSERT INTO "junagarri" VALUES(19,12,'19:12','اے یحيٰ! میری کتاب کو مضبوطی سے تھام لے اور ہم نے اسے لڑکپن ہی سے دانائی عطا فرما دی. ');
INSERT INTO "junagarri" VALUES(19,13,'19:13','اور اپنے پاس سے شفقت اور پاکیزگی بھی ، وه پرہیزگار شخص تھا.');
INSERT INTO "junagarri" VALUES(19,14,'19:14','اور اپنے ماں باپ سے نیک سلوک کرنے والا تھا وه سرکش اور گناه گار نہ تھا. ');
INSERT INTO "junagarri" VALUES(19,15,'19:15','اس پر سلام ہے جس دن وه پیدا ہوا اور جس دن وه مرے اور جس دن وه زنده کرکے اٹھایا جائے. ');
INSERT INTO "junagarri" VALUES(19,16,'19:16','اس کتاب میں مریم کا بھی واقعہ بیان کر۔ جبکہ وه اپنے گھر کے لوگوں سے علیحده ہو کر مشرقی جانب آئیں.');
INSERT INTO "junagarri" VALUES(19,17,'19:17','اور ان لوگوں کی طرف سے پرده کر لیا ، پھر ہم نے اس کے پاس اپنی روح (جبرائیل علیہ السلام) کو بھیجا پس وه اس کے سامنے پورا آدمی بن کر ظاہر ہوا. ');
INSERT INTO "junagarri" VALUES(19,18,'19:18','یہ کہنے لگیں میں تجھ سے رحمٰن کی پناه مانگتی ہوں اگر تو کچھ بھی اللہ سے ڈرنے والا ہے.');
INSERT INTO "junagarri" VALUES(19,19,'19:19','اس نے جواب دیا کہ میں تو اللہ کا بھیجا ہوا قاصد ہوں، تجھے ایک پاکیزه لڑکا دینے آیا ہوں.');
INSERT INTO "junagarri" VALUES(19,20,'19:20','کہنے لگیں بھلا میرے ہاں بچہ کیسے ہو سکتا ہے؟ مجھے تو کسی انسان کا ہاتھ تک نہیں لگا اور نہ میں بدکار ہوں.');
INSERT INTO "junagarri" VALUES(19,21,'19:21','اس نے کہا بات تو یہی ہے ۔ لیکن تیرے پروردگار کا ارشاد ہے کہ وه مجھ پر بہت ہی آسان ہے ہم تو اسے لوگوں کے لئے ایک نشانی بنا دیں گے اور اپنی خاص رحمت، یہ تو ایک طے شده بات ہے. ');
INSERT INTO "junagarri" VALUES(19,22,'19:22','پس وه حمل سے ہو گئیں اوراسی وجہ سے وه یکسو ہو کر ایک دور کی جگہ چلی گئیں.');
INSERT INTO "junagarri" VALUES(19,23,'19:23','پھر درِد زه ایک کھجور کے تنے کے نیچے لے آیا، بولی کاش! میں اس سے پہلے ہی مر گئی ہوتی اور لوگوں کی یاد سے بھی بھولی بسری ہو جاتی. ');
INSERT INTO "junagarri" VALUES(19,24,'19:24','اتنے میں اسے نیچے سے ہی آواز دی کہ آزرده خاطر نہ ہو، تیرے رب نے تیرے پاؤں تلے ایک چشمہ جاری کر دیا ہے.');
INSERT INTO "junagarri" VALUES(19,25,'19:25','اور اس کھجور کے تنے کو اپنی طرف ہلا، یہ تیرے سامنے تروتازه پکی کھجوریں گرا دے گا. ');
INSERT INTO "junagarri" VALUES(19,26,'19:26','اب چین سے کھا پی اور آنکھیں ٹھنڈی رکھ ، اگر تجھے کوئی انسان نظر پڑ جائے تو کہہ دینا کہ میں نے اللہ رحمنٰ کے نام کا روزه مان رکھا ہے۔ میں آج کسی شخص سے بات نہ کروں گی.');
INSERT INTO "junagarri" VALUES(19,27,'19:27','اب حضرت عیسیٰ (علیہ السلام) کو لئے ہوئے وه اپنی قوم کے پاس آئیں۔ سب کہنے لگے مریم تو نے بڑی بری حرکت کی.');
INSERT INTO "junagarri" VALUES(19,28,'19:28','اے ہارون کی بہن ! نہ تو تیرا باپ برا آدمی تھا اور نہ تیری ماں بدکار تھی.');
INSERT INTO "junagarri" VALUES(19,29,'19:29','مریم نے اپنے بچے کی طرف اشاره کیا۔ سب کہنے لگے کہ لو بھلا ہم گود کے بچے سے باتیں کیسے کریں؟');
INSERT INTO "junagarri" VALUES(19,30,'19:30','بچہ بول اٹھا کہ میں اللہ تعالیٰ کا بنده ہوں۔ اس نے مجھے کتاب عطا فرمائی اور مجھے اپنا پیغمبر بنایا ہے.');
INSERT INTO "junagarri" VALUES(19,31,'19:31','اور اس نے مجھے بابرکت کیا ہے جہاں بھی میں ہوں، اور اس نے مجھے نماز اور زکوٰة کا حکم دیا ہے جب تک بھی میں زنده رہوں.');
INSERT INTO "junagarri" VALUES(19,32,'19:32','اور اس نے مجھے اپنی والده کا خدمت گزار بنایا ہے اور مجھے سرکش اور بدبخت نہیں کیا. ');
INSERT INTO "junagarri" VALUES(19,33,'19:33','اور مجھ پر میری پیدائش کے دن اور میری موت کے دن اور جس دن کہ میں دوباره زنده کھڑا کیا جاؤں گا، سلام ہی سلام ہے.');
INSERT INTO "junagarri" VALUES(19,34,'19:34','یہ ہے صحیح واقعہ عیسیٰ بن مریم (علیہ السلام) کا، یہی ہے وه حق بات جس میں لوگ شک وشبہ میں مبتلا ہیں. ');
INSERT INTO "junagarri" VALUES(19,35,'19:35','اللہ تعالیٰ کے لئے اولاد کا ہونا لائق نہیں، وه تو بالکل پاک ذات ہے، وه تو جب کسی کام کے سر انجام دینے کا اراده کرتا ہے تو اسے کہہ دیتا ہے کہ ہو جا، وه اسی وقت ہو جاتا ہے. ');
INSERT INTO "junagarri" VALUES(19,36,'19:36','میرا اور تم سب کا پروردگار صرف اللہ تعالیٰ ہی ہے۔ تم سب اسی کی عبادت کرو، یہی سیدھی راه ہے.');
INSERT INTO "junagarri" VALUES(19,37,'19:37','پھر یہ فرقے آپس میں اختلاف کرنے لگے ، پس کافروں کے لئے ویل ہے ایک بڑے (سخت) دن کی حاضری سے. ');
INSERT INTO "junagarri" VALUES(19,38,'19:38','کیا خوب دیکھنے سننے والے ہوں گے اس دن جبکہ ہمارے سامنے حاضر ہوں گے ، لیکن آج تو یہ ظالم لوگ صریح گمراہی میں پڑے ہوئے ہیں.');
INSERT INTO "junagarri" VALUES(19,39,'19:39','تو انہیں اس رنج وافسوس کے دن کا ڈر سنا دے جبکہ کام انجام کو پہنچا دیا جائے گا ، اور یہ لوگ غفلت اور بے ایمانی میں ہی ره جائیں گے.');
INSERT INTO "junagarri" VALUES(19,40,'19:40','خود زمین کے اور تمام زمین والوں کے وارث ہم ہی ہوں گے اور سب لوگ ہماری ہی طرف لوٹا کر لائے جائیں گے.');
INSERT INTO "junagarri" VALUES(19,41,'19:41','اس کتاب میں ابراہیم (علیہ السلام) کا قصہ بیان کر، بیشک وه بڑی سچائی والے پیغمبر تھے. ');
INSERT INTO "junagarri" VALUES(19,42,'19:42','جبکہ انہوں نے اپنے باپ سے کہا کہ ابّا جان! آپ ان کی پوجا پاٹ کیوں کر رہے ہیں جو نہ سنیں نہ دیکھیں؟ نہ آپ کو کچھ بھی فائده پہنچا سکیں.');
INSERT INTO "junagarri" VALUES(19,43,'19:43','میرے مہربان باپ! آپ دیکھیئے میرے پاس وه علم آیا ہے جو آپ کے پاس آیا ہی نہیں ، تو آپ میری ہی مانیں میں بالکل سیدھی راه کی طرف آپ کی رہبری کروں گا.');
INSERT INTO "junagarri" VALUES(19,44,'19:44','میرے ابّا جان آپ شیطان کی پرستش سے باز آجائیں شیطان تو رحم وکرم والے اللہ تعالیٰ کا بڑا ہی نافرمان ہے. ');
INSERT INTO "junagarri" VALUES(19,45,'19:45','ابّا جان! مجھے خوف لگا ہوا کہ کہیں آپ پر کوئی عذاب الٰہی نہ آپڑے کہ آپ شیطان کے ساتھی بن جائیں. ');
INSERT INTO "junagarri" VALUES(19,46,'19:46','اس نے جواب دیا کہ اے ابراہیم! کیا تو ہمارے معبودوں سے روگردانی کر رہا ہے۔ سن اگر تو باز نہ آیا تو میں تجھے پتھروں سے مار ڈالوں گا، جا ایک مدت دراز تک مجھ سے الگ ره. ');
INSERT INTO "junagarri" VALUES(19,47,'19:47','کہا اچھا تم پر سلام ہو ، میں تو اپنے پروردگار سے تمہاری بخشش کی دعا کرتا رہوں گا ، وه مجھ پر حد درجہ مہربان ہے.');
INSERT INTO "junagarri" VALUES(19,48,'19:48','میں تو تمہیں بھی اور جن جن کو تم اللہ تعالیٰ کے سوا پکارتے ہو انہیں بھی سب کو چھوڑ رہا ہوں۔ صرف اپنے پروردگار کو پکارتا رہوں گا، مجھے یقین ہے کہ میں اپنے پروردگار سے دعا مانگ کر محروم نہ رہوں گا.');
INSERT INTO "junagarri" VALUES(19,49,'19:49','جب ابراہیم (علیہ السلام) ان سب کو اور اللہ کے سوا ان کے سب معبودوں کو چھوڑ چکے تو ہم نے انہیں اسحاق ویعقوب (علیہما السلام) عطا فرمائے ، اور دونوں کو نبی بنا دیا.');
INSERT INTO "junagarri" VALUES(19,50,'19:50','اور ان سب کو ہم نے اپنی بہت سی رحمتیں عطا فرمائیں اور ہم نے ان کے ذکر جمیل کو بلند درجے کا کر دیا. ');
INSERT INTO "junagarri" VALUES(19,51,'19:51','اس قرآن میں موسیٰ (علیہ السلام) کا ذکر بھی کر، جو چنا ہوا اور رسول اور نبی تھا.');
INSERT INTO "junagarri" VALUES(19,52,'19:52','ہم نے اسے طور کی دائیں جانب سے ندا کی اور راز گوئی کرتے ہوئے اسے قریب کر لیا.');
INSERT INTO "junagarri" VALUES(19,53,'19:53','اور اپنی خاص مہربانی سے اس کے بھائی کو نبی بنا کر عطا فرمایا.');
INSERT INTO "junagarri" VALUES(19,54,'19:54','اس کتاب میں اسماعیل (علیہ السلام) کا واقعہ بھی بیان کر، وه بڑا ہی وعدے کا سچا تھا اور تھا بھی رسول اور نبی.');
INSERT INTO "junagarri" VALUES(19,55,'19:55','وه اپنے گھر والوں کو برابر نماز اور زکوٰة کاحکم دیتا تھا، اور تھا بھی اپنے پروردگار کی بارگاه میں پسندیده اور مقبول.');
INSERT INTO "junagarri" VALUES(19,56,'19:56','اور اس کتاب میں ادریس (علیہ السلام) کا بھی ذکر کر، وه بھی نیک کردار پیغمبر تھا.');
INSERT INTO "junagarri" VALUES(19,57,'19:57','ہم نےاسے بلند مقام پر اٹھا لیا. ');
INSERT INTO "junagarri" VALUES(19,58,'19:58','یہی وه انبیا ہیں جن پر اللہ تعالیٰ نے فضل وکرم کیا جو اولاد آدم میں سے ہیں اور ان لوگوں کی نسل سے ہیں جنہیں ہم نے نوح (علیہ السلام) کے ساتھ کشتی میں چڑھا لیا تھا، اور اولاد ابراہیم ویعقوب سے اور ہماری طرف سے راه یافتہ اور ہمارے پسندیده لوگوں میں سے۔ ان کے سامنے جب اللہ رحمان کی آیتوں کی تلاوت کی جاتی تھی یہ سجده کرتے اور روتے گڑگڑاتے گر پڑتے تھے. ');
INSERT INTO "junagarri" VALUES(19,59,'19:59','پھر ان کے بعد ایسے ناخلف پیدا ہوئے کہ انہوں نے نماز ضائع کردی اور نفسانی خواہشوں کے پیچھے پڑ گئے، سو ان کا نقصان ان کے آگے آئے گا. ');
INSERT INTO "junagarri" VALUES(19,60,'19:60','بجز ان کے جو توبہ کر لیں اور ایمان لائیں اور نیک عمل کریں۔ ایسے لوگ جنت میں جائیں گے اور ان کی ذرا سی بھی حق تلفی نہ کی جائے گی. ');
INSERT INTO "junagarri" VALUES(19,61,'19:61','ہمیشگی والی جنتوں میں جن کا غائبانہ وعده اللہ مہربان نے اپنے بندوں سے کیا ہے۔ بیشک اس کا وعده پورا ہونے والا ہی ہے.');
INSERT INTO "junagarri" VALUES(19,62,'19:62','وه لوگ وہاں کوئی لغو بات نہ سنیں گے صرف سلام ہی سلام سنیں گے، ان کے لئے وہاں صبح شام ان کا رزق ہوگا. ');
INSERT INTO "junagarri" VALUES(19,63,'19:63','یہ ہے وه جنت جس کا وارث ہم اپنے بندوں میں سے انہیں بناتے ہیں جو متقی ہوں.');
INSERT INTO "junagarri" VALUES(19,64,'19:64','ہم بغیر تیرے رب کے حکم کے اتر نہیں سکتے ، ہمارے آگے پیچھے اور ان کے درمیان کی کل چیزیں اسی کی ملکیت میں ہیں، تیرا پروردگار بھولنے والا نہیں.');
INSERT INTO "junagarri" VALUES(19,65,'19:65','آسمانوں کا، زمین کا اور جو کچھ ان کے درمیان ہے سب کا رب وہی ہے تو اسی کی بندگی کر اور اس کی عبادت پر جم جا۔ کیا تیرے علم میں اس کا ہمنام ہم پلہ کوئی اور بھی ہے؟ ');
INSERT INTO "junagarri" VALUES(19,66,'19:66','انسان کہتا ہے کہ جب میں مرجاؤں گا تو کیا پھر زنده کر کے نکالا جاؤں گا؟ ');
INSERT INTO "junagarri" VALUES(19,67,'19:67','کیا یہ انسان اتنا بھی یاد نہیں رکھتا کہ ہم نے اسے اس سے پہلے پیدا کیا حالانکہ وه کچھ بھی نہ تھا. ');
INSERT INTO "junagarri" VALUES(19,68,'19:68','تیرے پروردگار کی قسم! ہم انہیں اور شیطانوں کو جمع کر کے ضرور ضرور جہنم کے اردگرد گھٹنوں کے بل گرے ہوئے حاضر کر دیں گے. ');
INSERT INTO "junagarri" VALUES(19,69,'19:69','ہم پھر ہر ہر گروه سے انہیں الگ نکال کھڑا کریں گے جو اللہ رحمنٰ سے بہت اکڑے اکڑے پھرتے تھے. ');
INSERT INTO "junagarri" VALUES(19,70,'19:70','پھر ہم انہیں بھی خوب جانتے ہیں جو جہنم کے داخلے کے زیاده سزاوار ہیں. ');
INSERT INTO "junagarri" VALUES(19,71,'19:71','تم میں سے ہر ایک وہاں ضرور وارد ہونے والا ہے، یہ تیرے پروردگار کے ذمے قطعی، فیصل شده امر ہے.');
INSERT INTO "junagarri" VALUES(19,72,'19:72','پھر ہم پرہیزگاروں کو تو بچالیں گے اور نافرمانوں کو اسی میں گھٹنوں کے بل گرا ہوا چھوڑ دیں گے. ');
INSERT INTO "junagarri" VALUES(19,73,'19:73','جب ان کے سامنے ہماری روشن آیتیں تلاوت کی جاتی ہیں تو کافر مسلمانوں سے کہتے ہیں بتاؤ ہم تم دونوں جماعتوں میں سے کس کا مرتبہ زیاده ہے؟ اور کس کی مجلس شاندار ہے؟ ');
INSERT INTO "junagarri" VALUES(19,74,'19:74','ہم تو ان سے پہلے بہت سی جماعتوں کو غارت کر چکے ہیں جو ساز وسامان اور نام ونمود میں ان سے بڑھ چڑھ کر تھیں.');
INSERT INTO "junagarri" VALUES(19,75,'19:75','کہہ دیجئے! جو گمراہی میں ہوتا ہے رحمنٰ اس کو خوب لمبی مہلت دیتا ہے، یہاں تک کہ وه ان چیزوں کو دیکھ لیں جن کا وعده کیے جاتے ہیں یعنی عذاب یا قیامت کو، اس وقت ان کو صحیح طور پر معلوم ہو جائے گا کہ کون برے مرتبے والا اور کس کا جتھا کمزور ہے. ');
INSERT INTO "junagarri" VALUES(19,76,'19:76','اور ہدایت یافتہ لوگوں کو اللہ تعالیٰ ہدایت میں بڑھاتا ہے ، اور باقی رہنے والی نیکیاں تیرے رب کے نزدیک ثواب کے لحاظ سے اور انجام کے لحاظ سے بہت ہی بہتر ہیں. ');
INSERT INTO "junagarri" VALUES(19,77,'19:77','کیا تو نے اسے بھی دیکھا جس نے ہماری آیتوں سے کفر کیا اور کہا کہ مجھے تو مال واولاد ضرور ہی دی جائے گی.');
INSERT INTO "junagarri" VALUES(19,78,'19:78','کیا وه غیب پر مطلع ہے یا اللہ کا کوئی وعده لے چکا ہے؟');
INSERT INTO "junagarri" VALUES(19,79,'19:79','ہرگز نہیں، یہ جو بھی کہہ رہا ہے ہم اسے ضرور لکھ لیں گے، اور اس کے لئے عذاب بڑھائے چلے جائیں گے.');
INSERT INTO "junagarri" VALUES(19,80,'19:80','یہ جن چیزوں کو کہہ رہا ہے اسے ہم اس کے بعد لے لیں گے۔ اور یہ تو بالکل اکیلا ہی ہمارے سامنے حاضر ہوگا. ');
INSERT INTO "junagarri" VALUES(19,81,'19:81','انہوں نے اللہ کے سوا دوسرے معبود بنا رکھے ہیں کہ وه ان کے لئے باعث عزت ہوں.');
INSERT INTO "junagarri" VALUES(19,82,'19:82','لیکن ایسا ہرگز ہونا نہیں۔ وه تو ان کی پوجا سے منکر ہو جائیں گے، اور الٹے ان کے دشمن بن جائیں گے.');
INSERT INTO "junagarri" VALUES(19,83,'19:83','کیا تو نے نہیں دیکھا کہ ہم کافروں کے پاس شیطانوں کو بھیجتے ہیں جو انہیں خوب اکساتے ہیں. ');
INSERT INTO "junagarri" VALUES(19,84,'19:84','تو ان کے بارے میں جلدی نہ کر، ہم تو خود ہی ان کے لئے مدت شماری کر رہے ہیں. ');
INSERT INTO "junagarri" VALUES(19,85,'19:85','جس دن ہم پرہیزگاروں کو اللہ رحمان کی طرف بطور مہمان جمع کریں گے.');
INSERT INTO "junagarri" VALUES(19,86,'19:86','اور گناه گاروں کوسخت پیاس کی حالت میں جہنم کی طرف ہانک لے جائیں گے. ');
INSERT INTO "junagarri" VALUES(19,87,'19:87','کسی کو شفاعت کا اختیار نہ ہوگا سوائے ان کے جنہوں نے اللہ تعالیٰ کی طرف سے کوئی قول قرار لے لیا ہے. ');
INSERT INTO "junagarri" VALUES(19,88,'19:88','ان کا قول تو یہ ہے کہ اللہ رحمٰن نے بھی اولاد اختیار کی ہے.');
INSERT INTO "junagarri" VALUES(19,89,'19:89','یقیناً تم بہت بری اور بھاری چیز لائے ہو.');
INSERT INTO "junagarri" VALUES(19,90,'19:90','قریب ہے کہ اس قول کی وجہ سے آسمان پھٹ جائیں اور زمین شق ہوجائے اور پہاڑ ریزے ریزے ہو جائیں.');
INSERT INTO "junagarri" VALUES(19,91,'19:91','کہ وه رحمان کی اولاد ثابت کرنے بیٹھے. ');
INSERT INTO "junagarri" VALUES(19,92,'19:92','شان رحمٰن کے لائق نہیں کہ وه اولاد رکھے.');
INSERT INTO "junagarri" VALUES(19,93,'19:93','آسمان وزمین میں جو بھی ہیں سب کے سب اللہ کے غلام بن کر ہی آنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(19,94,'19:94','ان سب کو اس نے گھیر رکھا ہے اور سب کو پوری طرح گن بھی رکھا ہے. ');
INSERT INTO "junagarri" VALUES(19,95,'19:95','یہ سارے کے سارے قیامت کے دن اکیلے اس کے پاس حاضر ہونے والے ہیں. ');
INSERT INTO "junagarri" VALUES(19,96,'19:96','بیشک جو ایمان لائے ہیں اور جنہوں نے شائستہ اعمال کیے ہیں ان کے لیے اللہ رحمٰن محبت پیدا کردے گا. ');
INSERT INTO "junagarri" VALUES(19,97,'19:97','ہم نے اس قرآن کو تیری زبان میں بہت ہی آسان کر دیا ہے کہ تو اس کے ذریعہ سے پرہیزگاروں کو خوشخبری دے اور جھگڑالو لوگوں کو ڈرا دے.');
INSERT INTO "junagarri" VALUES(19,98,'19:98','ہم نے ان سے پہلے بہت سی جماعتیں تباہ کر دی ہیں، کیا ان میں سے ایک کی بھی آہٹ تو پاتا ہے یا ان کی آواز کی بھنک بھی تیرے کان میں پڑتی ہے؟ ');
INSERT INTO "junagarri" VALUES(20,1,'20:1','طٰہٰ.');
INSERT INTO "junagarri" VALUES(20,2,'20:2','ہم نے یہ قرآن تجھ پر اس لئے نہیں اتارا کہ تو مشقت میں پڑ جائے. ');
INSERT INTO "junagarri" VALUES(20,3,'20:3','بلکہ اس کی نصیحت کے لئے جو اللہ سے ڈرتا ہے.');
INSERT INTO "junagarri" VALUES(20,4,'20:4','اس کا اتارنا اس کی طرف سے ہے جس نے زمین کو اور بلند آسمانوں کو پیدا کیا ہے.');
INSERT INTO "junagarri" VALUES(20,5,'20:5','جو رحمٰن ہے، عرش پر قائم ہے. ');
INSERT INTO "junagarri" VALUES(20,6,'20:6','جس کی ملکیت آسمانوں اور زمین اور ان دونوں کے درمیان اور (کرہٴ خاک) کے نیچے کی ہر ایک چیز پر ہے. ');
INSERT INTO "junagarri" VALUES(20,7,'20:7','اگر تو اونچی بات کہے تو وه تو ہر ایک پوشیده، بلکہ پوشیده سے پوشیده تر چیز کو بھی بخوبی جانتا ہے. ');
INSERT INTO "junagarri" VALUES(20,8,'20:8','وہی اللہ ہے جس کے سوا کوئی معبود نہیں، بہترین نام اسی کے ہیں. ');
INSERT INTO "junagarri" VALUES(20,9,'20:9','تجھے موسیٰ (علیہ السلام) کا قصہ بھی معلوم ہے؟');
INSERT INTO "junagarri" VALUES(20,10,'20:10','جبکہ اس نے آگ دیکھ کر اپنے گھر والوں سے کہا کہ تم ذرا سی دیر ٹھہر جاؤ مجھے آگ دکھائی دی ہے۔ بہت ممکن ہے کہ میں اس کا کوئی انگارا تمہارے پاس لاؤں یا آگ کے پاس سے راستے کی اطلاع پاؤں. ');
INSERT INTO "junagarri" VALUES(20,11,'20:11','جب وه وہاں پہنچے تو آواز دی گئی اے موسیٰ!');
INSERT INTO "junagarri" VALUES(20,12,'20:12','یقیناً میں ہی تیرا پروردگار ہوں تو اپنی جوتیاں اتار دے ، کیونکہ تو پاک میدان طویٰ میں ہے. ');
INSERT INTO "junagarri" VALUES(20,13,'20:13','اور میں نے تجھے منتخب کر لیا اب جو وحی کی جائے اسے کان لگا کر سن.');
INSERT INTO "junagarri" VALUES(20,14,'20:14','بیشک میں ہی اللہ ہوں، میرے سوا عبادت کے لائق اور کوئی نہیں پس تو میری ہی عبادت کر ، اور میری یاد کے لئے نماز قائم رکھ. ');
INSERT INTO "junagarri" VALUES(20,15,'20:15','قیامت یقیناً آنے والی ہے جسے میں پوشیده رکھنا چاہتا ہوں تاکہ ہر شخص کو وه بدلہ دیا جائے جو اس نے کوشش کی ہو.');
INSERT INTO "junagarri" VALUES(20,16,'20:16','پس اب اس کے یقین سے تجھے کوئی ایسا شخص روک نہ دے جو اس پر ایمان نہ رکھتا ہو اور اپنی خواہش کے پیچھے پڑا ہو، ورنہ تو ہلاک ہوجائے گا. ');
INSERT INTO "junagarri" VALUES(20,17,'20:17','اے موسیٰ! تیرے اس دائیں ہاتھ میں کیا ہے؟');
INSERT INTO "junagarri" VALUES(20,18,'20:18','جواب دیا کہ یہ میری لاٹھی ہے، جس پر میں ٹیک لگاتا ہوں اور جس سے میں اپنی بکریوں کے لئے پتے جھاڑ لیا کرتا ہوں اور بھی اس میں مجھے بہت سے فائدے ہیں.');
INSERT INTO "junagarri" VALUES(20,19,'20:19','فرمایا اے موسیٰ! اسے ہاتھ سے نیچے ڈال دے.');
INSERT INTO "junagarri" VALUES(20,20,'20:20','ڈالتے ہی وه سانپ بن کر دوڑنے لگی');
INSERT INTO "junagarri" VALUES(20,21,'20:21','فرمایا بے خوف ہو کر اسے پکڑ لے، ہم اسے اسی پہلی سی صورت میں دوباره لا دیں گے. ');
INSERT INTO "junagarri" VALUES(20,22,'20:22','اور اپنا ہاتھ اپنی بغل میں ڈال لے تو وه سفید چمکتا ہوا ہو کر نکلے گا، لیکن بغیر کسی عیب (اور روگ) کے یہ دوسرا معجزه ہے.');
INSERT INTO "junagarri" VALUES(20,23,'20:23','یہ اس لئے کہ ہم تجھے اپنی بڑی بڑی نشانیاں دکھانا چاہتے ہیں.');
INSERT INTO "junagarri" VALUES(20,24,'20:24','اب تو فرعون کی طرف جا اس نے بڑی سرکشی مچا رکھی ہے. ');
INSERT INTO "junagarri" VALUES(20,25,'20:25','موسیٰ (علیہ السلام) نے کہا اے میرے پروردگار! میرا سینہ میرے لئے کھول دے.');
INSERT INTO "junagarri" VALUES(20,26,'20:26','اور میرے کام کو مجھ پر آسان کر دے.');
INSERT INTO "junagarri" VALUES(20,27,'20:27','اور میری زبان کی گره بھی کھول دے.');
INSERT INTO "junagarri" VALUES(20,28,'20:28','تاکہ لوگ میری بات اچھی طرح سمجھ سکیں.');
INSERT INTO "junagarri" VALUES(20,29,'20:29','اور میرا وزیر میرے کنبے میں سے کر دے.');
INSERT INTO "junagarri" VALUES(20,30,'20:30','یعنی میرے بھائی ہارون (علیہ السلام) کو.');
INSERT INTO "junagarri" VALUES(20,31,'20:31','تو اس سے میری کمر کس دے.');
INSERT INTO "junagarri" VALUES(20,32,'20:32','اور اسے میرا شریک کار کر دے. ');
INSERT INTO "junagarri" VALUES(20,33,'20:33','تاکہ ہم دونوں بکثرت تیری تسبیح بیان کریں.');
INSERT INTO "junagarri" VALUES(20,34,'20:34','اور بکثرت تیری یاد کریں. ');
INSERT INTO "junagarri" VALUES(20,35,'20:35','بیشک تو ہمیں خوب دیکھنے بھالنے والا ہے. ');
INSERT INTO "junagarri" VALUES(20,36,'20:36','جناب باری تعالیٰ نے فرمایا موسیٰ تیرے تمام سوالات پورے کر دیئے گئے. ');
INSERT INTO "junagarri" VALUES(20,37,'20:37','ہم نے تو تجھ پرایک بار اور بھی بڑا احسان کیا ہے. ');
INSERT INTO "junagarri" VALUES(20,38,'20:38','جبکہ ہم نے تیری ماں کو وه الہام کیا جس کا ذکر اب کیا جا رہا ہے.');
INSERT INTO "junagarri" VALUES(20,39,'20:39','کہ تو اسے صندوق میں بند کرکے دریا میں چھوڑ دے، پس دریا اسے کنارے لا ڈالے گا اور میرا اور خود اس کا دشمن اسے لے لے گا ، اور میں نے اپنی طرف کی خاص محبت ومقبولیت تجھ پر ڈال دی ۔ تاکہ تیری پرورش میری آنکھوں کے سامنے کی جائے.');
INSERT INTO "junagarri" VALUES(20,40,'20:40','(یاد کر) جبکہ تیری بہن چل رہی تھی اور کہہ رہی تھی کہ اگر تم کہو تو میں اسے بتا دوں جو اس کی نگہبانی کرے ، اس تدبیر سے ہم نے تجھے پھر تیری ماں کے پاس پہنچایا کہ اس کی آنکھیں ٹھنڈی رہیں اور وه غمگین نہ ہو۔ اور تو نے ایک شخص کو مار ڈالا تھا اس پر بھی ہم نے تجھے غم سے بچا لیا، غرض ہم نے تجھے اچھی طرح آزما لیا ۔ پھر تو کئی سال تک مدین کے لوگوں میں ٹھہرا رہا ، پھر تقدیر الٰہی کے مطابق اے موسیٰ! تو آیا.');
INSERT INTO "junagarri" VALUES(20,41,'20:41','اور میں نے تجھے خاص اپنی ذات کے لئے پسند فرما لیا.');
INSERT INTO "junagarri" VALUES(20,42,'20:42','اب تو اپنے بھائی سمیت میری نشانیاں ہمراه لئے ہوئے جا، اور خبردار میرے ذکر میں سستی نہ کرنا. ');
INSERT INTO "junagarri" VALUES(20,43,'20:43','تم دونوں فرعون کے پاس جاؤ اس نے بڑی سرکشی کی ہے.');
INSERT INTO "junagarri" VALUES(20,44,'20:44','اسے نرمی سے سمجھاؤ کہ شاید وه سمجھ لے یا ڈر جائے.');
INSERT INTO "junagarri" VALUES(20,45,'20:45','دونوں نے کہا اے ہمارے رب! ہمیں خوف ہے کہ کہیں فرعون ہم پر کوئی زیادتی نہ کرے یا اپنی سرکشی میں بڑھ نہ جائے.');
INSERT INTO "junagarri" VALUES(20,46,'20:46','جواب ملا کہ تم مطلقاً خوف نہ کرو میں تمہارے ساتھ ہوں اور سنتا دیکھتا رہوں گا. ');
INSERT INTO "junagarri" VALUES(20,47,'20:47','تم اس کے پاس جا کر کہو کہ ہم تیرے پروردگار کے پیغمبر ہیں تو ہمارے ساتھ بنی اسرائیل کو بھیج دے، ان کی سزائیں موقوف کر۔ ہم تو تیرے پاس تیرے رب کی طرف سے نشانی لے کر آئے ہیں اور سلامتی اسی کے لئے ہے جو ہدایت کا پابند ہو جائے.');
INSERT INTO "junagarri" VALUES(20,48,'20:48','ہماری طرف وحی کی گئی ہے کہ جو جھٹلائے اور روگردانی کرے اس کے لئے عذاب ہے.');
INSERT INTO "junagarri" VALUES(20,49,'20:49','فرعون نے پوچھا کہ اے موسیٰ تم دونوں کا رب کون ہے؟');
INSERT INTO "junagarri" VALUES(20,50,'20:50','جواب دیا کہ ہمارا رب وه ہے جس نے ہر ایک کو اس کی خاص صورت، شکل عنایت فرمائی پھر راه سجھا دی. ');
INSERT INTO "junagarri" VALUES(20,51,'20:51','اس نے کہا اچھا یہ تو بتاؤ اگلے زمانے والوں کا حال کیا ہونا ہے. ');
INSERT INTO "junagarri" VALUES(20,52,'20:52','جواب دیا کہ ان کا علم میرے رب کے ہاں کتاب میں موجود ہے، نہ تو میرا رب غلطی کرتا ہے نہ بھولتا ہے. ');
INSERT INTO "junagarri" VALUES(20,53,'20:53','اسی نے تمہارے لئے زمین کو فرش بنایا ہے اور اس میں تمہارے چلنے کے لئے راستے بنائے ہیں اور آسمان سے پانی بھی وہی برساتا ہے، پھر اس برسات کی وجہ سے مختلف قسم کی پیداوار بھی ہم ہی پیدا کرتے ہیں.');
INSERT INTO "junagarri" VALUES(20,54,'20:54','تم خود کھاؤ اور اپنے چوپایوں کو بھی چراؤ ۔ کچھ شک نہیں کہ اس میں عقلمندوں کے لئے بہت سی نشانیاں ہیں.');
INSERT INTO "junagarri" VALUES(20,55,'20:55','اسی زمین میں سے ہم نے تمہیں پیدا کیا اور اسی میں پھر واپس لوٹائیں گے اور اسی سے پھر دوباره تم سب کو نکال کھڑا کریں گے.');
INSERT INTO "junagarri" VALUES(20,56,'20:56','ہم نے اسےاپنی سب نشانیاں دکھا دیں لیکن پھر بھی اس نے جھٹلایا اور انکار کر دیا.');
INSERT INTO "junagarri" VALUES(20,57,'20:57','کہنے لگا اے موسیٰ! کیا تو اسی لئے آیا ہے کہ ہمیں اپنے جادو کے زور سے ہمارے ملک سے باہر نکال دے. ');
INSERT INTO "junagarri" VALUES(20,58,'20:58','اچھا ہم بھی تیرے مقابلے میں اسی جیسا جادو ضرور لائیں گے پس تو ہمارے اور اپنے درمیان ایک وعدے کا وقت مقرر کر لے ، کہ نہ ہم اس کا خلاف کریں اور نہ تو، صاف میدان میں مقابلہ ہو. ');
INSERT INTO "junagarri" VALUES(20,59,'20:59','موسیٰ (علیہ السلام) نے جواب دیا کہ زینت اور جشن کے دن کا وعده ہے اور یہ کہ لوگ دن چڑھے ہی جمع ہو جائیں.');
INSERT INTO "junagarri" VALUES(20,60,'20:60','پس فرعون لوٹ گیا اور اس نے اپنے ہتھکنڈے جمع کیے پھر آگیا. ');
INSERT INTO "junagarri" VALUES(20,61,'20:61','موسی (علیہ السلام) نے ان سے کہا تمہاری شامت آچکی، اللہ تعالیٰ پر جھوٹ اور افترا نہ باندھو کہ وه تمہیں عذابوں سے ملیا میٹ کر دے، یاد رکھو وه کبھی کامیاب نہ ہوگا جس نے جھوٹی بات گھڑی. ');
INSERT INTO "junagarri" VALUES(20,62,'20:62','پس یہ لوگ آپس کے مشوروں میں مختلف رائے ہوگئے اور چھپ کر چپکے چپکے مشوره کرنے لگے. ');
INSERT INTO "junagarri" VALUES(20,63,'20:63','کہنے لگے یہ دونوں محض جادوگر ہیں اور ان کا پختہ اراده ہے کہ اپنے جادو کے زور سے تمہیں تمہارے ملک سے نکال باہر کر دیں اور تمہارے بہترین مذہب کو برباد کر دیں. ');
INSERT INTO "junagarri" VALUES(20,64,'20:64','تو تم بھی اپنا کوئی داؤ اٹھا نہ رکھو، پھر صف بندی کرکے آؤ۔ جو غالب آگیا وہی بازی لے گیا.');
INSERT INTO "junagarri" VALUES(20,65,'20:65','کہنے لگے کہ اے موسیٰ! یا تو تو پہلے ڈال یا ہم پہلے ڈالنے والے بن جائیں.');
INSERT INTO "junagarri" VALUES(20,66,'20:66','جواب دیا کہ نہیں تم ہی پہلے ڈالو ۔ اب تو موسیٰ (علیہ السلام) کو یہ خیال گزرنے لگا کہ ان کی رسیاں اور لکڑیاں ان کے جادو کے زور سے دوڑ بھاگ رہی ہیں. ');
INSERT INTO "junagarri" VALUES(20,67,'20:67','پس موسیٰ (علیہ السلام) نے اپنے دل ہی دل میں ڈر محسوس کیا.');
INSERT INTO "junagarri" VALUES(20,68,'20:68','ہم نے فرمایا کچھ خوف نہ کر یقیناً تو ہی غالب اور برتر رہے گا. ');
INSERT INTO "junagarri" VALUES(20,69,'20:69','اور تیرے دائیں ہاتھ میں جو ہے اسے ڈالدے کہ ان کی تمام کاریگری کووه نگل جائے، انہوں نے جو کچھ بنایا ہے یہ صرف جادوگروں کے کرتب ہیں اور جادوگر کہیں سے بھی آئے کامیاب نہیں ہوتا.');
INSERT INTO "junagarri" VALUES(20,70,'20:70','اب تو تمام جادوگر سجدے میں گر پڑے اور پکار اٹھے کہ ہم تو ہارون اور موسیٰ (علیہما السلام) کے رب پر ایمان لائے.');
INSERT INTO "junagarri" VALUES(20,71,'20:71','فرعون کہنے لگا کہ کیا میری اجازت سے پہلے ہی تم اس پرایمان لے آئے؟ یقیناً یہی تمہارا وه بڑا بزرگ ہے جس نے تم سب کو جادو سکھایا ہے، (سن لو) میں تمہارے پاؤں الٹے سیدھے کٹوا کر تم سب کو کھجور کے تنوں میں سولی پر لٹکوا دوں گا، اور تمہیں پوری طرح معلوم ہوجائے گا کہ ہم میں سے کس کی مار زیاده سخت اور دیرپا ہے.');
INSERT INTO "junagarri" VALUES(20,72,'20:72','انہوں نے جواب دیا کہ ناممکن ہے کہ ہم تجھے ترجیح دیں ان دلیلوں پر جو ہمارے سامنے آچکیں اور اس اللہ پر جس نے ہمیں پیدا کیا ہے اب تو تو جو کچھ کرنے ولا ہے کر گزر، تو جو کچھ بھی حکم چلا سکتا ہے وه اسی دنیوی زندگی میں ہی ہے.');
INSERT INTO "junagarri" VALUES(20,73,'20:73','ہم (اس امید سے) اپنے پروردگار پرایمان لائے کہ وه ہماری خطائیں معاف فرما دے اور (خاص کر) جادوگری (کا گناه،) جس پر تم نے ہمیں مجبور کیا ہے ، اللہ ہی بہتر اور ہمیشہ باقی رہنے والا ہے. ');
INSERT INTO "junagarri" VALUES(20,74,'20:74','بات یہی ہے کہ جو بھی گنہگار بن کر اللہ تعالیٰ کے ہاں حاضر ہوگا اس کے لئے دوزخ ہے، جہاں نہ موت ہوگی اور نہ زندگی. ');
INSERT INTO "junagarri" VALUES(20,75,'20:75','اور جو بھی اس کے پاس ایمان کی حالت میں حاضر ہوگا اور اس نے اعمال بھی نیک کیے ہوں گے اس کے لئے بلند وبالا درجے ہیں.');
INSERT INTO "junagarri" VALUES(20,76,'20:76','ہمیشگی والی جنتیں جن کے نیچے نہریں لہریں لے رہی ہیں جہاں وه ہمیشہ (ہمیشہ) رہیں گے۔ یہی انعام ہے ہر اس شخص کا جو پاک ہوا. ');
INSERT INTO "junagarri" VALUES(20,77,'20:77','ہم نے موسیٰ (علیہ السلام) کی طرف وحی نازل فرمائی کہ تو راتوں رات میرے بندوں کو لے چل ، اور ان کے لئے دریا میں خشک راستہ بنا لے ، پھر نہ تجھے کسی کے آپکڑنے کا خطره ہوگا نہ ڈر. ');
INSERT INTO "junagarri" VALUES(20,78,'20:78','فرعون نے اپنے لشکروں سمیت ان کا تعاقب کیا پھر تو دریا ان سب پر چھا گیا جیسا کچھ چھا جانے والا تھا. ');
INSERT INTO "junagarri" VALUES(20,79,'20:79','فرعون نے اپنی قوم کو گمراہی میں ڈال دیا اور سیدھا راستہ نہ دکھایا. ');
INSERT INTO "junagarri" VALUES(20,80,'20:80','اے بنی اسرائیل! دیکھو ہم نے تمہیں تمہارے دشمن سے نجات دی اور تم سے کوه طور کی دائیں طرف کا وعده کیا اور تم پر من وسلویٰ اتارا. ');
INSERT INTO "junagarri" VALUES(20,81,'20:81','تم ہماری دی ہوئی پاکیزه روزی کھاؤ، اور اس میں حد سے آگے نہ بڑھو ، ورنہ تم پر میرا غضب نازل ہوگا، اور جس پر میرا غضب نازل ہو جائے وه یقیناً تباه ہوا. ');
INSERT INTO "junagarri" VALUES(20,82,'20:82','ہاں بیشک میں انہیں بخش دینے والا ہوں جو توبہ کریں ایمان لائیں نیک عمل کریں اور راه راست پر بھی رہیں. ');
INSERT INTO "junagarri" VALUES(20,83,'20:83','اے موسیٰ! تجھے اپنی قوم سے (غافل کرکے) کون سی چیز جلدی لے آئی؟');
INSERT INTO "junagarri" VALUES(20,84,'20:84','کہا کہ وه لوگ بھی میرے پیچھے ہی پیچھے ہیں، اور میں نے اے رب! تیری طرف جلدی اس لئے کی کہ تو خوش ہو جائے. ');
INSERT INTO "junagarri" VALUES(20,85,'20:85','فرمایا! ہم نے تیری قوم کو تیرے پیچھے آزمائش میں ڈال دیا اور انہیں سامری نے بہکا دیا ہے. ');
INSERT INTO "junagarri" VALUES(20,86,'20:86','پس موسیٰ (علیہ السلام) سخت غضبناک ہو کر رنج کے ساتھ واپس لوٹے، اور کہنے لگے کہ اے میری قوم والو! کیا تم سے تمہارے پروردگار نے نیک وعده نہیں کیا تھا؟ کیا اس کی مدت تمہیں لمبی معلوم ہوئی ؟ بلکہ تمہارا اراده ہی یہ ہے کہ تم پر تمہارے پروردگار کا غضب نازل ہو؟ کہ تم نے میرے وعدے کا خلاف کیا. ');
INSERT INTO "junagarri" VALUES(20,87,'20:87','انہوں نے جواب دیا کہ ہم نے اپنےاختیار سے آپ کے ساتھ وعدے کا خلاف نہیں کیا ۔ بلکہ ہم پر زیورات قوم کے جو بوجھ لاد دیے گئے تھے انہیں ہم نے ڈال دیا، اور اسی طرح سامری نے بھی ڈال دیئے.');
INSERT INTO "junagarri" VALUES(20,88,'20:88','پھر اس نے لوگوں کے لئے ایک بچھڑا نکال کھڑا کیا یعنی بچھڑے کا بت، جس کی گائے کی سی آواز بھی تھی پھر کہنے لگے کہ یہی تمہارا معبود ہے اور موسیٰ کا بھی، لیکن موسیٰ بھول گیا ہے.');
INSERT INTO "junagarri" VALUES(20,89,'20:89','کیا یہ گمراه لوگ یہ بھی نہیں دیکھتے کہ وه تو ان کی بات کا جواب بھی نہیں دے سکتا اور نہ ان کے کسی برے بھلے کا اختیار رکھتا ہے. ');
INSERT INTO "junagarri" VALUES(20,90,'20:90','اور ہارون (علیہ السلام) نےاس سے پہلے ہی ان سے کہہ دیا تھا اے میری قوم والو! اس بچھڑے سے تو صرف تمہاری آزمائش کی گئی ہے، تمہارا حقیقی پروردگار تو اللہ رحمنٰ ہی ہے، پس تم سب میری تابعداری کرو۔ اور میری بات مانتے چلے جاؤ.*');
INSERT INTO "junagarri" VALUES(20,91,'20:91','انہوں نے جواب دیا کہ موسیٰ (علیہ السلام) کی واپسی تک تو ہم اسی کے مجاور بنے بیٹھے رہیں گے. ');
INSERT INTO "junagarri" VALUES(20,92,'20:92','موسیٰ (علیہ السلام) کہنے لگے اے ہارون! انہیں گمراه ہوتا ہوا دیکھتے ہوئے تجھے کس چیز نے روکا تھا.');
INSERT INTO "junagarri" VALUES(20,93,'20:93','کہ تو میرے پیچھے نہ آیا۔ کیا تو بھی میرے فرمان کا نافرمان بن بیٹھا. ');
INSERT INTO "junagarri" VALUES(20,94,'20:94','ہارون (علیہ السلام) نے کہا اے میرے ماں جائے بھائی! میری داڑھی نہ پکڑ اور سر کے بال نہ کھینچ، مجھے تو صرف یہ خیال دامن گیر ہوا کہ کہیں آپ یہ (نہ) فرمائیں کہ تو نے بنی اسرائیل میں تفرقہ ڈال دیا اور میری بات کا انتظار نہ کیا. ');
INSERT INTO "junagarri" VALUES(20,95,'20:95','موسیٰ (علیہ السلام) نے پوچھا سامری تیرا کیا معاملہ ہے.');
INSERT INTO "junagarri" VALUES(20,96,'20:96','اس نے جواب دیا کہ مجھے وه چیز دکھائی دی جو انہیں دکھائی نہیں دی، تو میں نے فرستادہٴ الٰہی کے نقش قدم سے ایک مٹھی بھر لی اسے اس میں ڈال دیا اسی طرح میرے دل نے یہ بات میرے لئے بھلی بنا دی.');
INSERT INTO "junagarri" VALUES(20,97,'20:97','کہا اچھا جا دنیا کی زندگی میں تیری سزا یہی ہے کہ تو کہتا رہے کہ مجھے نہ چھونا ، اور ایک اور بھی وعده تیرے ساتھ ہے جو تجھ سے ہر گز نہ ٹلے گا ، اور اب تو اپنے اس معبود کو بھی دیکھ لینا جس کا اعتکاف کیے ہوئے تھا کہ ہم اسے جلا کر دریا میں ریزه ریزه اڑا دیں گے. ');
INSERT INTO "junagarri" VALUES(20,98,'20:98','اصل بات یہی ہے کہ تم سب کا معبود برحق صرف اللہ ہی ہے اس کے سوا کوئی پرستش کے قابل نہیں۔ اس کا علم تمام چیزوں پر حاوی ہے.');
INSERT INTO "junagarri" VALUES(20,99,'20:99','اسی طرح ہم تیرے سامنے پہلے کی گزری ہوئی وارداتیں بیان فرما رہے ہیں اور یقیناً ہم تجھے اپنے پاس سے نصیحت عطا فرما چکے ہیں. ');
INSERT INTO "junagarri" VALUES(20,100,'20:100','اس سے جو منھ پھیر لے گا وه یقیناً قیامت کے دن اپنا بھاری بوجھ لادے ہوئے ہوگا. ');
INSERT INTO "junagarri" VALUES(20,101,'20:101','جس میں ہمیشہ ہی رہے گا ، اور ان کے لئے قیامت کے دن (بڑا) برا بوجھ ہے.');
INSERT INTO "junagarri" VALUES(20,102,'20:102','جس دن صور پھونکا جائے گا اور گناه گاروں کو ہم اس دن (دہشت کی وجہ سے) نیلی پیلی آنکھوں کے ساتھ گھیر لائیں گے.');
INSERT INTO "junagarri" VALUES(20,103,'20:103','وه آپس میں چپکے چپکے کہہ رہے ہوں گے کہ ہم تو (دنیا میں) صرف دس دن ہی رہے.');
INSERT INTO "junagarri" VALUES(20,104,'20:104','جوکچھ وه کہہ رہے ہیں اس کی حقیقت سے ہم باخبر ہیں ان میں سب سے زیاده اچھی راه والا کہہ رہا ہو گا کہ تم تو صرف ایک ہی دن رہے.');
INSERT INTO "junagarri" VALUES(20,105,'20:105','وه آپ سے پہاڑوں کی نسبت سوال کرتے ہیں، تو آپ کہہ دیں کہ انہیں میرا رب ریزه ریزه کر کے اڑا دے گا.');
INSERT INTO "junagarri" VALUES(20,106,'20:106','اور زمین کو بالکل ہموار صاف میدان کر کے چھوڑے گا.');
INSERT INTO "junagarri" VALUES(20,107,'20:107','جس میں تو نہ کہیں موڑ دیکھے گا نہ اونچ نیچ.');
INSERT INTO "junagarri" VALUES(20,108,'20:108','جس دن لوگ پکارنے والے کے پیچھے چلیں گے جس میں کوئی کجی نہ ہوگی اور اللہ رحمنٰ کے سامنے تمام آوازیں پست ہو جائیں گی سوائے کھسر پھسر کے تجھے کچھ بھی سنائی نہ دے گا. ');
INSERT INTO "junagarri" VALUES(20,109,'20:109','اس دن سفارش کچھ کام نہیں آئے گی مگر جسے رحمنٰ حکم دے اور اس کی بات کو پسند فرمائے. ');
INSERT INTO "junagarri" VALUES(20,110,'20:110','جو کچھ ان کے آگے پیچھے ہے اسے اللہ ہی جانتا ہے مخلوق کا علم اس پر حاوی نہیں ہو سکتا. ');
INSERT INTO "junagarri" VALUES(20,111,'20:111','تمام چہرے اس زنده اور قائم دائم مدبر، اللہ کے سامنے کمال عاجزی سے جھکے ہوئے ہونگے، یقیناً وه برباد ہوا جس نے ظلم لاد لیا. ');
INSERT INTO "junagarri" VALUES(20,112,'20:112','اور جو نیک اعمال کرے اور ایمان والا بھی ہو تو نہ اسے بے انصافی کا کھٹکا ہوگا نہ حق تلفی کا. ');
INSERT INTO "junagarri" VALUES(20,113,'20:113','اسی طرح ہم نے تجھ پر عربی قرآن نازل فرمایا ہے اور طرح طرح سے اس میں ڈر کا بیان سنایا ہے تاکہ لوگ پرہیز گار بن جائیں یا ان کے دل میں سوچ سمجھ تو پیدا کرے. ');
INSERT INTO "junagarri" VALUES(20,114,'20:114','پس اللہ عالی شان والا سچا اور حقیقی بادشاه ہے۔ تو قرآن پڑھنے میں جلدی نہ کر اس سے پہلے کہ تیری طرف جو وحی کی جاتی ہے وه پوری کی جائے ، ہاں یہ دعا کر کہ پروردگار! میرا علم بڑھا. ');
INSERT INTO "junagarri" VALUES(20,115,'20:115','ہم نے آدم کو پہلے ہی تاکیدی حکم دیا تھا لیکن وه بھول گیا اور ہم نے اس میں کوئی عزم نہیں پایا. ');
INSERT INTO "junagarri" VALUES(20,116,'20:116','اور جب ہم نے فرشتوں سے کہا کہ آدم (علیہ السلام) کو سجده کرو تو ابلیس کے سوا سب نے کیا، اس نے صاف انکار کر دیا.');
INSERT INTO "junagarri" VALUES(20,117,'20:117','تو ہم نے کہا اے آدم! یہ تیرا اور تیری بیوی کا دشمن ہے (خیال رکھنا) ایسا نہ ہو کہ وه تم دونوں کو جنت سے نکلوا دے کہ تو مصیبت میں پڑ جائے. ');
INSERT INTO "junagarri" VALUES(20,118,'20:118','یہاں تو تجھے یہ آرام ہے کہ نہ تو بھوکا ہوتا ہے نہ ننگا.');
INSERT INTO "junagarri" VALUES(20,119,'20:119','اور نہ تو یہاں پیاسا ہوتا ہے نہ دھوپ سے تکلیف اٹھاتا ہے.');
INSERT INTO "junagarri" VALUES(20,120,'20:120','لیکن شیطان نے اسے وسوسہ ڈالا، کہنے لگا کہ میں تجھے دائمی زندگی کا درخت اور بادشاہت بتلاؤں کہ جو کبھی پرانی نہ ہو.');
INSERT INTO "junagarri" VALUES(20,121,'20:121','چنانچہ ان دونوں نے اس درخت سے کچھ کھا لیا پس ان کے ستر کھل گئے اور بہشت کے پتے اپنے اوپر ٹانکنے لگے۔ آدم (علیہ السلام) نے اپنے رب کی نافرمانی کی، پس بہک گیا. ');
INSERT INTO "junagarri" VALUES(20,122,'20:122','پھر اس کے رب نے نوازا، اس کی توبہ قبول کی اور اس کی رہنمائی کی. ');
INSERT INTO "junagarri" VALUES(20,123,'20:123','فرمایا، تم دونوں یہاں سےاتر جاؤ تم آپس میں ایک دوسرے کے دشمن ہو، اب تمہارے پاس کبھی میری طرف سے ہدایت پہنچے تو جو میری ہدایت کی پیروی کرے نہ تو وه بہکے گا نہ تکلیف میں پڑے گا.');
INSERT INTO "junagarri" VALUES(20,124,'20:124','اور (ہاں) جو میری یاد سے روگردانی کرے گا اس کی زندگی تنگی میں رہے گی ، اور ہم اسے بروز قیامت اندھا کر کے اٹھائیں گے. ');
INSERT INTO "junagarri" VALUES(20,125,'20:125','وه کہے گا کہ الٰہی! مجھے تو نے اندھا بنا کر کیوں اٹھایا؟ حالانکہ میں تو دیکھتا بھالتا تھا.');
INSERT INTO "junagarri" VALUES(20,126,'20:126','(جواب ملے گا کہ) اسی طرح ہونا چاہئے تھا تو میری آئی ہوئی آیتوں کو بھول گیا تو آج تو بھی بھلا دیا جاتا ہے.');
INSERT INTO "junagarri" VALUES(20,127,'20:127','ہم ایسا ہی بدلہ ہر اس شخص کو دیا کرتے ہیں جو حد سے گزر جائے اور اپنے رب کی آیتوں پر ایمان نہ لائے، اور بیشک آخرت کا عذاب نہایت ہی سخت اور باقی رہنے والا ہے.');
INSERT INTO "junagarri" VALUES(20,128,'20:128','کیا ان کی رہبری اس بات نے بھی نہیں کی کہ ہم نے ان سے پہلے بہت سی بستیاں ہلاک کر دی ہیں جن کے رہنے سہنے کی جگہ یہ چل پھر رہے ہیں۔ یقیناً اس میں عقلمندوں کے لئے بہت سی نشانیاں ہیں.');
INSERT INTO "junagarri" VALUES(20,129,'20:129','اگر تیرے رب کی بات پہلے ہی سے مقرر شده اور وقت معین کرده نہ ہوتا تو اسی وقت عذاب آچمٹتا. ');
INSERT INTO "junagarri" VALUES(20,130,'20:130','پس ان کی باتوں پر صبر کر اور اپنے پروردگار کی تسبیح اور تعریف بیان کرتا ره، سورج نکلنے سے پہلے اور اس کے ڈوبنے سے پہلے، رات کے مختلف وقتوں میں بھی اور دن کے حصوں میں بھی تسبیح کرتا ره ، بہت ممکن ہے کہ تو راضی ہو جائے. ');
INSERT INTO "junagarri" VALUES(20,131,'20:131','اور اپنی نگاہیں ہرگز ان چیزوں کی طرف نہ دوڑانا جو ہم نے ان میں سے مختلف لوگوں کو آرائش دنیا کی دے رکھی ہیں تاکہ انہیں اس میں آزما لیں تیرے رب کا دیا ہوا ہی (بہت) بہتر اور باقی رہنے والا ہے. ');
INSERT INTO "junagarri" VALUES(20,132,'20:132','اپنے گھرانے کے لوگوں پرنماز کی تاکید رکھ اور خود بھی اس پر جما ره ، ہم تجھ سے روزی نہیں مانگتے، بلکہ ہم خود تجھے روزی دیتے ہیں، آخر میں بول بالا پرہیزگاری ہی کا ہے.');
INSERT INTO "junagarri" VALUES(20,133,'20:133','انہوں نے کہا کہ یہ نبی ہمارے پاس اپنے پروردگار کی طرف سے کوئی نشانی کیوں نہیں لایا ؟ کیا ان کے پاس اگلی کتابوں کی واضح دلیل نہیں پہنچی؟ ');
INSERT INTO "junagarri" VALUES(20,134,'20:134','اور اگر ہم اس سے پہلے ہی انہیں عذاب سے ہلاک کر دیتے تو یقیناً یہ کہہ اٹھتے کہ اے ہمارے پروردگار تو نے ہمارے پاس اپنا رسول کیوں نہ بھیجا؟ کہ ہم تیری آیتوں کی تابعداری کرتے اس سے پہلے کہ ہم ذلیل ورسوا ہوتے.');
INSERT INTO "junagarri" VALUES(20,135,'20:135','کہہ دیجئے! ہر ایک انجام کا منتظر ہے پس تم بھی انتظار میں رہو۔ عنقريب قطعاً جان لو گے کہ راه راست والے کون ہیں اور کون راه یافتہ ہیں. ');
INSERT INTO "junagarri" VALUES(21,1,'21:1','لوگوں کے حساب کا وقت قریب آگیا پھر بھی وه بے خبری میں منھ پھیرے ہوئے ہیں. ');
INSERT INTO "junagarri" VALUES(21,2,'21:2','ان کے پاس ان کے رب کی طرف سے جو بھی نئی نئی نصیحت آتی ہے اسے وه کھیل کود میں ہی سنتے ہیں. ');
INSERT INTO "junagarri" VALUES(21,3,'21:3','ان کے دل بالکل غافل ہیں اور ان ظالموں نے چپکے چپکے سرگوشیاں کیں کہ وه تم ہی جیسا انسان ہے، پھر کیا وجہ ہے جو تم آنکھوں دیکھتے جادو میں آجاتے ہو. ');
INSERT INTO "junagarri" VALUES(21,4,'21:4','پیغمبر نے کہا میرا پروردگار ہر اس بات کو جو زمین وآسمان میں ہے بخوبی جانتا ہے، وه بہت ہی سننے والا اور جاننے والا ہے. ');
INSERT INTO "junagarri" VALUES(21,5,'21:5','اتنا ہی نہیں بلکہ یہ تو کہتے ہیں کہ یہ قرآن پراگنده خوابوں کا مجموعہ ہے بلکہ اس نے از خود اسے گھڑ لیا ہے بلکہ یہ شاعر ہے، ورنہ ہمارے سامنے یہ کوئی ایسی نشانی لاتے جیسے کہ اگلے پیغمبر بھیجے گئے تھے.');
INSERT INTO "junagarri" VALUES(21,6,'21:6','ان سے پہلے جتنی بستیاں ہم نے اجاڑیں سب ایمان سے خالی تھیں۔ تو کیا اب یہ ایمان لائیں گے. ');
INSERT INTO "junagarri" VALUES(21,7,'21:7','تجھ سے پہلے جتنے پیغمبر ہم نے بھیجے سبھی مرد تھے جن کی طرف ہم وحی اتارتے تھے پس تم اہل کتاب سے پوچھ لو اگر خود تمہیں علم نہ ہو. ');
INSERT INTO "junagarri" VALUES(21,8,'21:8','ہم نے ان کے ایسے جسم نہیں بنائے تھے کہ وه کھانا نہ کھائیں اور نہ وه ہمیشہ رہنے والے تھے. ');
INSERT INTO "junagarri" VALUES(21,9,'21:9','پھر ہم نے ان سے کیے ہوئے سب وعدے سچے کیے انہیں اور جن جن کو ہم نے چاہا نجات عطا فرمائی اور حد سے نکل جانے والوں کو غارت کر دیا. ');
INSERT INTO "junagarri" VALUES(21,10,'21:10','یقیناً ہم نے تمہاری جانب کتاب نازل فرمائی ہے جس میں تمہارے لئے ذکر ہے، کیا پھر بھی تم عقل نہیں رکھتے؟');
INSERT INTO "junagarri" VALUES(21,11,'21:11','اور بہت سی بستیاں ہم نے تباه کر دیں جو ظالم تھیں اور ان کے بعد ہم نے دوسری قوم کو پیدا کر دیا.');
INSERT INTO "junagarri" VALUES(21,12,'21:12','جب انہوں نے ہمارے عذاب کا احساس کر لیا، تو لگے اس سے بھاگنے. ');
INSERT INTO "junagarri" VALUES(21,13,'21:13','بھاگ دوڑ نہ کرو اور جہاں تمہیں آسودگی دی گئی تھی وہیں واپس لوٹو اور اپنے مکانات کی طرف جاؤ تاکہ تم سے سوال تو کر لیا جائے. ');
INSERT INTO "junagarri" VALUES(21,14,'21:14','کہنے لگے ہائے ہماری خرابی! بیشک ہم ظالم تھے.');
INSERT INTO "junagarri" VALUES(21,15,'21:15','پھر تو ان کا یہی قول تھا یہاں تک کہ ہم نے انہیں جڑ سے کٹی ہوئی کھیتی اور بجھی پڑی آگ (کی طرح) کر دیا. ');
INSERT INTO "junagarri" VALUES(21,16,'21:16','ہم نے آسمان وزمین اور ان کے درمیان کی چیزوں کو کھیلتے ہوئے نہیں بنایا. ');
INSERT INTO "junagarri" VALUES(21,17,'21:17','اگر ہم یوں ہی کھیل تماشے کا اراده کرتے تو اسے اپنے پاس سے ہی بنا لیتے، اگر ہم کرنے والے ہی ہوتے. ');
INSERT INTO "junagarri" VALUES(21,18,'21:18','بلکہ ہم سچ کو جھوٹ پر پھینک مارتے ہیں پس سچ جھوٹ کا سر توڑ دیتا ہے اور وه اسی وقت نابود ہو جاتا ہے، تم جو باتیں بناتے ہو وه تمہاری لئے باعث خرابی ہیں. ');
INSERT INTO "junagarri" VALUES(21,19,'21:19','آسمانوں اور زمین میں جو ہے اسی اللہ کا ہے اور جو اس کے پاس ہیں وه اس کی عبادت سے نہ سرکشی کرتے ہیں اور نہ تھکتے ہیں.');
INSERT INTO "junagarri" VALUES(21,20,'21:20','وه دن رات تسبیح بیان کرتے ہیں اور ذرا سی بھی سستی نہیں کرتے.');
INSERT INTO "junagarri" VALUES(21,21,'21:21','کیا ان لوگوں نے زمین (کی مخلوقات میں) سے جنہیں معبود بنا رکھا ہے وه زنده کردیتے ہیں. ');
INSERT INTO "junagarri" VALUES(21,22,'21:22','اگر آسمان وزمین میں سوائے اللہ تعالیٰ کے اور بھی معبود ہوتے تو یہ دونوں درہم برہم ہوجاتے پس اللہ تعالیٰ عرش کا رب ہر اس وصف سے پاک ہے جو یہ مشرک بیان کرتے ہیں.');
INSERT INTO "junagarri" VALUES(21,23,'21:23','وه اپنے کاموں کے لئے (کسی کےآگے) جواب ده نہیں اور سب (اس کےآگے) جواب ده ہیں.');
INSERT INTO "junagarri" VALUES(21,24,'21:24','کیا ان لوگوں نے اللہ کے سوا اور معبود بنا رکھے ہیں، ان سے کہہ دو لاؤ اپنی دلیل پیش کرو۔ یہ ہے میرے ساتھ والوں کی کتاب اور مجھ سے اگلوں کی دلیل ۔ بات یہ ہے کہ ان میں کے اکثر لوگ حق کو نہیں جانتے اسی وجہ سے منھ موڑے ہوئے ہیں.');
INSERT INTO "junagarri" VALUES(21,25,'21:25','تجھ سے پہلے بھی جو رسول ہم نے بھیجا اس کی طرف یہی وحی نازل فرمائی کہ میرے سوا کوئی معبود برحق نہیں پس تم سب میری ہی عبادت کرو.*');
INSERT INTO "junagarri" VALUES(21,26,'21:26','(مشرک لوگ) کہتے ہیں کہ رحمٰن اولاد والا ہے (غلط ہے) اس کی ذات پاک ہے، بلکہ وه سب اس کے باعزت بندے ہیں. ');
INSERT INTO "junagarri" VALUES(21,27,'21:27','کسی بات میں اللہ پر پیش دستی نہیں کرتے بلکہ اس کے فرمان پر کاربند ہیں. ');
INSERT INTO "junagarri" VALUES(21,28,'21:28','وه ان کے آگے پیچھے تمام امور سے واقف ہے وه کسی کی بھی سفارش نہیں کرتے بجز ان کے جن سے اللہ خوش ہو وه تو خود ہیبت الٰہی سے لرزاں وترساں ہیں.');
INSERT INTO "junagarri" VALUES(21,29,'21:29','ان میں سے اگر کوئی بھی کہہ دے کہ اللہ کے سوا میں لائق عبادت ہوں تو ہم اسے دوزخ کی سزا دیں ہم ظالموں کو اسی طرح سزا دیتے ہیں.');
INSERT INTO "junagarri" VALUES(21,30,'21:30','کیا کافر لوگوں نے یہ نہیں دیکھا کہ آسمان وزمین باہم ملے جلے تھے پھر ہم نے انہیں جدا کیا اور ہر زنده چیز کو ہم نے پانی سے پیدا کیا ، کیا یہ لوگ پھر بھی ایمان نہیں لاتے.');
INSERT INTO "junagarri" VALUES(21,31,'21:31','اور ہم نے زمین میں پہاڑ بنا دیئے تاکہ وه مخلوق کو ہلا نہ سکے ، اور ہم نے اس میں کشاده راہیں بنا دیں تاکہ وه راستہ حاصل کریں.');
INSERT INTO "junagarri" VALUES(21,32,'21:32','آسمان کو محفوظ چھت بھی ہم نے ہی بنایا ہے۔ لیکن لوگ اس کی قدرت کے نمونوں پر دھیان نہیں دھرتے.');
INSERT INTO "junagarri" VALUES(21,33,'21:33','وہی اللہ ہے جس نے رات اور دن اور سورج اور چاند کو پیدا کیا ہے ۔ ان میں سے ہر ایک اپنے اپنے مدار میں تیرتے پھرتے ہیں. ');
INSERT INTO "junagarri" VALUES(21,34,'21:34','آپ سے پہلے کسی انسان کو بھی ہم نے ہمیشگی نہیں دی، کیا اگر آپ مرگئے تو وه ہمیشہ کے لئے ره جائیں گے. ');
INSERT INTO "junagarri" VALUES(21,35,'21:35','ہر جان دار موت کا مزه چکھنے والا ہے۔ ہم بطریق امتحان تم میں سے ہر ایک کو برائی بھلائی میں مبتلا کرتے ہیں اور تم سب ہماری ہی طرف لوٹائے جاؤ گے. ');
INSERT INTO "junagarri" VALUES(21,36,'21:36','یہ منکرین تجھے جب بھی دیکھتے ہیں تو تمہارا مذاق ہی اڑاتے ہیں کہ کیا یہی وه ہے جو تمہارے معبودوں کا ذکر برائی سے کرتا ہے، اور وه خود ہی رحمٰن کی یاد کے بالکل ہی منکر ہیں. ');
INSERT INTO "junagarri" VALUES(21,37,'21:37','انسان جلد باز مخلوق ہے۔ میں تمہیں اپنی نشانیاں ابھی ابھی دکھاؤں گا تم مجھ سے جلد بازی نہ کرو. ');
INSERT INTO "junagarri" VALUES(21,38,'21:38','کہتے ہیں کہ اگر سچے ہو تو بتا دو کہ یہ وعده کب ہے.');
INSERT INTO "junagarri" VALUES(21,39,'21:39','کاش! یہ کافر جانتے کہ اس وقت نہ تو یہ کافر آگ کو اپنے چہروں سے ہٹا سکیں گے اور نہ اپنی پیٹھوں سے اور نہ ان کی مدد کی جائے گی. ');
INSERT INTO "junagarri" VALUES(21,40,'21:40','(ہاں ہاں!) وعدے کی گھڑی ان کے پاس اچانک آجائے گی اور انہیں ہکا بکا کر دے گی ، پھر نہ تو یہ لوگ اسے ٹال سکیں گے اور نہ ذرا سی بھی مہلت دیئے جائیں گے.');
INSERT INTO "junagarri" VALUES(21,41,'21:41','اور تجھ سے پہلے رسولوں کے ساتھ بھی ہنسی مذاق کیا گیا پس ہنسی کرنے والوں کو ہی اس چیز نے گھیر لیا جس کی وه ہنسی اڑاتے تھے. ');
INSERT INTO "junagarri" VALUES(21,42,'21:42','ان سے پوچھئے کہ رحمٰن سے، دن اور رات تمہاری حفاظت کون کر سکتا ہے ؟ بات یہ ہے کہ یہ لوگ اپنے رب کے ذکر سے پھرے ہوئے ہیں.');
INSERT INTO "junagarri" VALUES(21,43,'21:43','کیا ہمارے سوا ان کے اور معبود ہیں جو انہیں مصیبتوں سے بچا لیں۔ کوئی بھی خود اپنی مدد کی طاقت نہیں رکھتا اور نہ کوئی ہماری طرف سے رفاقت دیا جاتا ہے. ');
INSERT INTO "junagarri" VALUES(21,44,'21:44','بلکہ ہم نے انہیں اور ان کے باپ دادوں کو زندگی کے سروسامان دیے یہاں تک کہ ان کی مدت عمر گزر گئی۔ کیا وه نہیں دیکھتے کہ ہم زمین کو اس کے کناروں سے گھٹاتے چلے آرہے ہیں، اب کیا وہی غالب ہیں؟ ');
INSERT INTO "junagarri" VALUES(21,45,'21:45','کہہ دیجئے! میں تو تمہیں اللہ کی وحی کے ذریعہ آگاه کر رہا ہوں مگر بہرے لوگ بات نہیں سنتے جبکہ انہیں آگاه کیا جائے. ');
INSERT INTO "junagarri" VALUES(21,46,'21:46','اگر انہیں تیرے رب کے کسی عذاب کا جھونکا بھی لگ جائے تو پکار اٹھیں کہ ہائے ہماری بدبختی! یقیناً ہم گنہگار تھے. ');
INSERT INTO "junagarri" VALUES(21,47,'21:47','قیامت کے دن ہم درمیان میں لا رکھیں گے ٹھیک ٹھیک تولنے والی ترازو کو۔ پھر کسی پر کچھ بھی ظلم نہ کیا جائے گا۔ اور اگر ایک رائی کے دانے کے برابر بھی عمل ہوگا ہم اسے لا حاضر کریں گے، اور ہم کافی ہیں حساب کرنے والے. ');
INSERT INTO "junagarri" VALUES(21,48,'21:48','یہ بالکل سچ ہے کہ ہم نے موسیٰ وہارون کو فیصلے کرنے والی نورانی اور پرہیزگاروں کے لئے وعظ ونصیحت والی کتاب عطا فرمائی. ');
INSERT INTO "junagarri" VALUES(21,49,'21:49','وه لوگ جو اپنے رب سے بن دیکھے خوف کھاتے ہیں اور قیامت (کے تصور) سے کانپتے رہتے ہیں. ');
INSERT INTO "junagarri" VALUES(21,50,'21:50','اور یہ نصیحت وبرکت والا قرآن بھی ہم ہی نے نازل فرمایا ہے کیا پھر بھی تم اس کے منکر ہو. ');
INSERT INTO "junagarri" VALUES(21,51,'21:51','یقیناً ہم نے اس سے پہلے ابراہیم کو اس کی سمجھ بوجھ بخشی تھی اور ہم اس کے احوال سے بخوبی واقف تھے.');
INSERT INTO "junagarri" VALUES(21,52,'21:52','جبکہ اس نے اپنے باپ سے اور اپنی قوم سے کہا کہ یہ مورتیاں جن کے تم مجاور بنے بیٹھے ہو کیا ہیں؟ ');
INSERT INTO "junagarri" VALUES(21,53,'21:53','سب نے جواب دیا کہ ہم نے اپنے باپ دادا کو انہی کی عبادت کرتے ہوئے پایا. ');
INSERT INTO "junagarri" VALUES(21,54,'21:54','آپ نے فرمایا! پھر تو تم اور تمہارے باپ دادا سبھی یقیناً کھلی گمراہی میں مبتلا رہے.');
INSERT INTO "junagarri" VALUES(21,55,'21:55','کہنے لگے کیا آپ ہمارے پاس سچ مچ حق لائے ہیں یا یوں ہی مذاق کر رہے ہیں. ');
INSERT INTO "junagarri" VALUES(21,56,'21:56','آپ نے فرمایا نہیں درحقیقت تم سب کا پروردگار تو وه ہے جو آسمانوں اور زمین کا مالک ہے جس نے انہیں پیدا کیا ہے، میں تو اسی بات کا گواه اور قائل ہوں. ');
INSERT INTO "junagarri" VALUES(21,57,'21:57','اور اللہ کی قسم! میں تمہارے ان معبودوں کے ساتھ جب تم علیحده پیٹھ پھیر کر چل دو گے ایک چال چلوں گا. ');
INSERT INTO "junagarri" VALUES(21,58,'21:58','پس اس نے ان سب کے ٹکڑے ٹکڑے کر دیئے ہاں صرف بڑے بت کو چھوڑ دیا یہ بھی اس لئے کہ وه سب اس کی طرف ہی لوٹیں. ');
INSERT INTO "junagarri" VALUES(21,59,'21:59','کہنے لگے کہ ہمارے خداؤں کے ساتھ یہ کس نے کیا؟ ایسا شخص تو یقیناً ظالموں میں سے ہے. ');
INSERT INTO "junagarri" VALUES(21,60,'21:60','بولے ہم نے ایک نوجوان کو ان کا تذکره کرتے ہوئے سنا تھا جسے ابراہیم (علیہ السلام) کہا جاتا ہے. ');
INSERT INTO "junagarri" VALUES(21,61,'21:61','سب نے کہا اچھا اسے مجمع میں لوگوں کی نگاہوں کے سامنے لاؤ تاکہ سب دیکھیں. ');
INSERT INTO "junagarri" VALUES(21,62,'21:62','کہنے لگے! اے ابراہیم (علیہ السلام) کیا تو نے ہی ہمارے خداؤں کے ساتھ یہ حرکت کی ہے.');
INSERT INTO "junagarri" VALUES(21,63,'21:63','آپ نے جواب دیا بلکہ اس کام کو ان کے بڑے نے کیا ہے تم اپنے خداؤں سے ہی پوچھ لو اگر یہ بولتے چالتے ہوں. ');
INSERT INTO "junagarri" VALUES(21,64,'21:64','پس یہ لوگ اپنے دلوں میں قائل ہوگئے اور کہنے لگے واقعی ظالم تو تم ہی ہو. ');
INSERT INTO "junagarri" VALUES(21,65,'21:65','پھر اپنے سروں کے بل اوندھے ہوگئے (اور کہنے لگے کہ) یہ تو تجھے بھی معلوم ہے کہ یہ بولنے چالنے والے نہیں. ');
INSERT INTO "junagarri" VALUES(21,66,'21:66','اللہ کے خلیل نے اسی وقت فرمایا افسوس! کیا تم اللہ کے علاوه ان کی عبادت کرتے ہو جو نہ تمہیں کچھ بھی نفع پہنچا سکیں نہ نقصان.');
INSERT INTO "junagarri" VALUES(21,67,'21:67','تف ہے تم پر اور ان پر جن کی تم اللہ کے سوا عبادت کرتے ہو۔ کیا تمہیں اتنی سی عقل بھی نہیں؟ ');
INSERT INTO "junagarri" VALUES(21,68,'21:68','کہنے لگے کہ اسے جلا دو اور اپنے خداؤں کی مدد کرو اگر تمہیں کچھ کرنا ہی ہے. ');
INSERT INTO "junagarri" VALUES(21,69,'21:69','ہم نے فرما دیا اے آگ! تو ٹھنڈی پڑ جا اور ابراہیم (علیہ السلام) کے لئے سلامتی (اور آرام کی چیز) بن جا!');
INSERT INTO "junagarri" VALUES(21,70,'21:70','گو انہوں نے ابراہیم (علیہ السلام) کا برا چاہا، لیکن ہم نے انہیں ناکام بنا دیا.');
INSERT INTO "junagarri" VALUES(21,71,'21:71','اور ہم ابراہیم اور لوط کو بچاکر اس زمین کی طرف لے چلے جس میں ہم نے تمام جہان والوں کے لئے برکت رکھی تھی. ');
INSERT INTO "junagarri" VALUES(21,72,'21:72','اور ہم نے اسےاسحاق عطا فرمایااور یعقوب اس پر مزید ۔ اور ہرایک کو ہم نے صالح بنایا.');
INSERT INTO "junagarri" VALUES(21,73,'21:73','اور ہم نے انہیں پیشوا بنا دیا کہ ہمارے حکم سے لوگوں کی رہبری کریں اور ہم نے ان کی طرف نیک کاموں کے کرنے اور نمازوں کے قائم رکھنے اور زکوٰة دینے کی وحی (تلقین) کی، اور وه سب کے سب ہمارے عبادت گزار بندے تھے.');
INSERT INTO "junagarri" VALUES(21,74,'21:74','ہم نے لوط (علیہ السلام) کو بھی حکم اور علم دیا اور اسے اسی بستی سے نجات دی جہاں کے لوگ گندے کاموں میں مبتلا تھے۔ اور تھے بھی بدترین گنہگار.');
INSERT INTO "junagarri" VALUES(21,75,'21:75','اور ہم نے لوط (علیہ السلام) کو اپنی رحمت میں داخل کر لیا بے شک وه نیکو کار لوگوں میں سے تھا. ');
INSERT INTO "junagarri" VALUES(21,76,'21:76','نوح کے اس وقت کو یاد کیجئے جبکہ اس نے اس سے پہلے دعا کی ہم نے اس کی دعا قبول فرمائی اور اسے اور اس کے گھر والوں کو بڑے کرب سے نجات دی.');
INSERT INTO "junagarri" VALUES(21,77,'21:77','اور جو لوگ ہماری آیتوں کو جھٹلا رہے تھے ان کے مقابلے میں ہم نے اس کی مدد کی، یقیناً وه برے لوگ تھے پس ہم نےان سب کو ڈبو دیا.');
INSERT INTO "junagarri" VALUES(21,78,'21:78','اور داؤد اور سلیمان (علیہما السلام) کو یاد کیجئے جبکہ وه کھیت کے معاملہ میں فیصلہ کر رہے تھے کہ کچھ لوگوں کی بکریاں رات کو اس میں چر چگ گئی تھیں، اور ان کے فیصلے میں ہم موجود تھے.');
INSERT INTO "junagarri" VALUES(21,79,'21:79','ہم نے اس کا صحیح فیصلہ سلیمان کو سمجھا دیا ۔ ہاں ہر ایک کو ہم نے حکم وعلم دے رکھا تھا اور داؤد کے تابع ہم نے پہاڑ کر دیئے تھے جو تسبیح کرتے تھے اور پرند بھی۔ ہم کرنے والے ہی تھے. ');
INSERT INTO "junagarri" VALUES(21,80,'21:80','اور ہم نے اسے تمہارے لئے لباس بنانے کی کاریگری سکھائی تاکہ لڑائی کے ضرر سے تمہارا بچاؤ ہو ۔ کیا تم شکر گزار بنو گے؟');
INSERT INTO "junagarri" VALUES(21,81,'21:81','ہم نے تند وتیز ہوا کو سلیمان (علیہ السلام) کے تابع کر دیا جو اس کے فرمان کے مطابق اس زمین کی طرف چلتی تھی جہاں ہم نے برکت دے رکھی تھی، اور ہم ہر چیز سے باخبر اور دانا ہیں.');
INSERT INTO "junagarri" VALUES(21,82,'21:82','اسی طرح سے بہت سے شیاطین بھی ہم نے اس کے تابع کیے تھے جو اس کے فرمان سے غوطے لگاتے تھے اور اس کے سوا بھی بہت سے کام کرتے تھے ، ان کے نگہبان ہم ہی تھے. ');
INSERT INTO "junagarri" VALUES(21,83,'21:83','ایوب (علیہ السلام) کی اس حالت کو یاد کرو جبکہ اس نے اپنے پروردگار کو پکارا کہ مجھے یہ بیماری لگ گئی ہے اور تو رحم کرنے والوں سے زیاده رحم کرنے والا ہے.');
INSERT INTO "junagarri" VALUES(21,84,'21:84','تو ہم نے اس کی سن لی اور جو دکھ انہیں تھا اسے دور کر دیا اور اس کو اہل وعیال عطا فرمائے بلکہ ان کے ساتھ ویسے ہی اور اپنی خاص مہربانی سے تاکہ سچے بندوں کے لئے سبب نصیحت ہو.');
INSERT INTO "junagarri" VALUES(21,85,'21:85','اور اسماعیل اور ادریس اور ذوالکفل ، (علیہم السلام) یہ سب صابر لوگ تھے.');
INSERT INTO "junagarri" VALUES(21,86,'21:86','ہم نے انہیں اپنی رحمت میں داخل کر لیا۔ یہ سب لوگ نیک تھے.');
INSERT INTO "junagarri" VALUES(21,87,'21:87','مچھلی والے (حضرت یونس علیہ السلام) کو یاد کرو! جبکہ وه غصہ سے چل دیا اور خیال کیا کہ ہم اسے نہ پکڑ سکیں گے۔ بالآخر وه اندھیروں کے اندر سے پکار اٹھا کہ الٰہی تیرے سوا کوئی معبود نہیں تو پاک ہے، بیشک میں ظالموں میں ہو گیا.');
INSERT INTO "junagarri" VALUES(21,88,'21:88','تو ہم نے اس کی پکار سن لی اور اسے غم سے نجات دے دی اور ہم ایمان والوں کو اسی طرح بچا لیا کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(21,89,'21:89','اور زکریا (علیہ السلام) کو یاد کرو جب اس نے اپنے رب سے دعا کی کہ اے میرے پروردگار! مجھے تنہا نہ چھوڑ، تو سب سے بہتر وارث ہے.');
INSERT INTO "junagarri" VALUES(21,90,'21:90','ہم نے اس کی دعا کو قبول فرما کر اسے یحيٰ (علیہ السلام) عطا فرمایا اور ان کی بیوی کو ان کے لئے درست کر دیا ۔ یہ بزرگ لوگ نیک کاموں کی طرف جلدی کرتے تھے اور ہمیں لالچ طمع اور ڈر خوف سے پکارتے تھے۔ اور ہمارے سامنے عاجزی کرنے والے تھے. ');
INSERT INTO "junagarri" VALUES(21,91,'21:91','اور وه پاک دامن بی بی جس نے اپنی عصمت کی حفاظت کی ہم نے اس کے اندر روح سے پھونک دی اور خود انہیں اور ان کے لڑکے کو تمام جہان کے لئے نشانی بنا دیا. ');
INSERT INTO "junagarri" VALUES(21,92,'21:92','یہ تمہاری امت ہے جو حقیقت میں ایک ہی امت ہے ، اور میں تم سب کا پروردگار ہوں پس تم میری ہی عبادت کرو.');
INSERT INTO "junagarri" VALUES(21,93,'21:93','مگر لوگوں نےآپس میں اپنے دین میں فرقہ بندیاں کر لیں، سب کے سب ہماری ہی طرف لوٹنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(21,94,'21:94','پھر جو بھی نیک عمل کرے اور وه مومن (بھی) ہو تو اس کی کوشش کی بے قدری نہیں کی جائے گی۔ ہم تو اس کے لکھنے والے ہیں.');
INSERT INTO "junagarri" VALUES(21,95,'21:95','اور جس بستی کو ہم نے ہلاک کر دیا اس پر لازم ہے کہ وہاں کے لوگ پلٹ کر نہیں آئیں گے. ');
INSERT INTO "junagarri" VALUES(21,96,'21:96','یہاں تک کہ یاجوج اور ماجوج کھول دیئے جائیں گے اور وه ہر بلندی سے دوڑتے ہوئے آئیں گے. ');
INSERT INTO "junagarri" VALUES(21,97,'21:97','اور سچا وعده قریب آلگے گا اس وقت کافروں کی نگاہیں پھٹی کی پھٹی ره جائیں گی ، کہ ہائے افسوس! ہم اس حال سے غافل تھے بلکہ فی الواقع ہم قصور وار تھے.');
INSERT INTO "junagarri" VALUES(21,98,'21:98','تم اور اللہ کے سوا جن جن کی تم عبادت کرتے ہو، سب دوزخ کا ایندھن بنو گے، تم سب دوزخ میں جانے والے ہو. ');
INSERT INTO "junagarri" VALUES(21,99,'21:99','اگر یہ (سچے) معبود ہوتے تو جہنم میں داخل نہ ہوتے، اور سب کے سب اسی میں ہمیشہ رہنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(21,100,'21:100','وه وہاں چلا رہے ہوں گے اور وہاں کچھ بھی نہ سن سکیں گے. ');
INSERT INTO "junagarri" VALUES(21,101,'21:101','البتہ بے شک جن کے لئے ہماری طرف سے نیکی پہلے ہی ٹھہر چکی ہے۔ وه سب جہنم سے دور ہی رکھے جائیں گے. ');
INSERT INTO "junagarri" VALUES(21,102,'21:102','وه تو دوزخ کی آہٹ تک نہ سنیں گے اور اپنی من بھاتی چیزوں میں ہمیشہ رہنے والے ہوں گے.');
INSERT INTO "junagarri" VALUES(21,103,'21:103','وه بڑی گھبراہٹ (بھی) انہیں غمگین نہ کر سکے گی اور فرشتے انہیں ہاتھوں ہاتھ لیں گے، کہ یہی تمہارا وه دن ہے جس کا تم وعده دیئے جاتے رہے.');
INSERT INTO "junagarri" VALUES(21,104,'21:104','جس دن ہم آسمان کو یوں لپیٹ لیں گے جیسے طومار میں اوراق لپیٹ دیئے جاتے ہیں ، جیسے کہ ہم نے اول دفعہ پیدائش کی تھی اسی طرح دوباره کریں گے۔ یہ ہمارے ذمے وعده ہے اور ہم اسے ضرور کر کے (ہی) رہیں گے.');
INSERT INTO "junagarri" VALUES(21,105,'21:105','ہم زبور میں پند ونصیحت کے بعد یہ لکھ چکے ہیں کہ زمین کے وارث میرے نیک بندے (ہی) ہوں گے.');
INSERT INTO "junagarri" VALUES(21,106,'21:106','عبادت گزار بندوں کے لئے تو اس میں ایک بڑا پیغام ہے. ');
INSERT INTO "junagarri" VALUES(21,107,'21:107','اور ہم نے آپ کو تمام جہان والوں کے لئے رحمت بنا کر ہی بھیجا ہے. ');
INSERT INTO "junagarri" VALUES(21,108,'21:108','کہہ دیجئے! میرے پاس تو پس وحی کی جاتی ہے کہ تم سب کا معبود ایک ہی ہے، تو کیا تم بھی اس کی فرمانبرداری کرنے والے ہو؟ ');
INSERT INTO "junagarri" VALUES(21,109,'21:109','پھر اگر یہ منھ موڑ لیں تو کہہ دیجئے کہ میں نے تمہیں یکساں طور پر خبردار کر دیا ہے ۔ مجھے علم نہیں کہ جس کا وعده تم سے کیا جا رہا ہے وه قریب ہے یا دور. ');
INSERT INTO "junagarri" VALUES(21,110,'21:110','البتہ اللہ تعالیٰ تو کھلی اور ظاہر بات کو بھی جانتا ہے اور جو تم چھپاتے ہو اسے بھی جانتا ہے.');
INSERT INTO "junagarri" VALUES(21,111,'21:111','مجھے اس کا بھی علم نہیں، ممکن ہے یہ تمہاری آزمائش ہو اور ایک مقرره وقت تک کا فائده (پہنچانا) ہے.');
INSERT INTO "junagarri" VALUES(21,112,'21:112','خود نبی نے کہا اے رب! انصاف کے ساتھ فیصلہ فرما اور ہمارا رب بڑا مہربان ہے جس سے مدد طلب کی جاتی ہے ان باتوں پر جو تم بیان کرتے ہو. ');
INSERT INTO "junagarri" VALUES(22,1,'22:1','لوگو! اپنے پروردگار سے ڈرو! بلاشبہ قیامت کا زلزلہ بہت ہی بڑی چیز ہے.');
INSERT INTO "junagarri" VALUES(22,2,'22:2','جس دن تم اسے دیکھ لو گے ہر دودھ پلانے والی اپنے دودھ پیتے بچے کو بھول جائے گی اور تمام حمل والیوں کے حمل گر جائیں گے اور تو دیکھے گا کہ لوگ مدہوش دکھائی دیں گے، حالانکہ درحقیقت وه متوالے نہ ہوں گے لیکن اللہ کا عذاب بڑا ہی سخت ہے. ');
INSERT INTO "junagarri" VALUES(22,3,'22:3','بعض لوگ اللہ کے بارے میں باتیں بناتے ہیں اور وه بھی بے علمی کے ساتھ اور ہر سرکش شیطان کی پیروی کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(22,4,'22:4','جس پر (قضائے الٰہی) لکھ دی گئی ہے کہ جو کوئی اس کی رفاقت کرے گا وه اسے گمراه کر دے گا اور اسے آگ کے عذاب کی طرف لے جائے گا.');
INSERT INTO "junagarri" VALUES(22,5,'22:5','لوگو! اگر تمہیں مرنے کے بعد جی اٹھنے میں شک ہے تو سوچو ہم نے تمہیں مٹی سے پیدا کیا پھر نطفہ سے پھر خون بستہ سے پھر گوشت کے لوتھڑے سے جو صورت دیا گیا تھا اور بے نقشہ تھا ۔ یہ ہم تم پر ظاہر کر دیتے ہیں ، اور ہم جسے چاہیں ایک ٹھہرائے ہوئے وقت تک رحم مادر میں رکھتے ہیں پھر تمہیں بچپن کی حالت میں دنیا میں لاتے ہیں پھر تاکہ تم اپنی پوری جوانی کو پہنچو، تم میں سے بعض تو وه ہیں جو فوت کر لئے جاتے ہیں اور بعض بے غرض عمر کی طرف پھر سے لوٹا دیئے جاتے ہیں کہ وه ایک چیز سے باخبر ہونے کے بعد پھر بے خبر ہو جائے ۔ تو دیکھتا ہے کہ زمین (بنجر اور) خشک ہے پھر ہم اس پر بارشیں برساتے ہیں تو وه ابھرتی ہے اور پھولتی ہے اور ہر قسم کی رونق دار نباتات اگاتی ہے. ');
INSERT INTO "junagarri" VALUES(22,6,'22:6','یہ اس لئے کہ اللہ ہی حق ہے اور وہی مردوں کو جِلاتا ہے اور وه ہر چیز پر قدرت رکھنے والا ہے.');
INSERT INTO "junagarri" VALUES(22,7,'22:7','اور یہ کہ قیامت قطعاً آنے والی ہے جس میں کوئی شک وشبہ نہیں اور یقیناً اللہ تعالیٰ قبروں والوں کو دوباره زنده فرمائے گا.');
INSERT INTO "junagarri" VALUES(22,8,'22:8','بعض لوگ اللہ کے بارے میں بغیر علم کے اور بغیر ہدایت کے اور بغیر روشن کتاب کے جھگڑتے ہیں.');
INSERT INTO "junagarri" VALUES(22,9,'22:9','جو اپنی پہلو موڑنے والا بن کر اس لئے کہ اللہ کی راه سے بہکادے، اسے دنیا میں بھی رسوائی ہوگی اور قیامت کے دن بھی ہم اسے جہنم میں جلنے کا عذاب چکھائیں گے.');
INSERT INTO "junagarri" VALUES(22,10,'22:10','یہ ان اعمال کی وجہ سے جو تیرے ہاتھوں نے آگے بھیج رکھے تھے۔ یقین مانو کہ اللہ تعالیٰ اپنے بندوں پر ظلم کرنے والا نہیں.');
INSERT INTO "junagarri" VALUES(22,11,'22:11','بعض لوگ ایسے بھی ہیں کہ ایک کنارے پر (کھڑے) ہو کر اللہ کی عبادت کرتے ہیں۔ اگر کوئی نفع مل گیا تو دلچسپی لینے لگتے ہیں اور اگر کوئی آفت آگئی تو اسی وقت منھ پھیر لیتے ہیں ، انہوں نے دونوں جہان کانقصان اٹھا لیا۔ واقعی یہ کھلا نقصان ہے.');
INSERT INTO "junagarri" VALUES(22,12,'22:12','اللہ کے سوا انہیں پکارتے ہیں جو نہ انہیں نقصان پہنچا سکیں نہ نفع۔ یہی تو دور دراز کی گمراہی ہے.');
INSERT INTO "junagarri" VALUES(22,13,'22:13','اسے پکارتے ہیں جس کا نقصان اس کے نفع سے زیاده قریب ہے، یقیناً برے والی ہیں اور برے ساتھی. ');
INSERT INTO "junagarri" VALUES(22,14,'22:14','ایمان اور نیک اعمال والوں کو اللہ تعالیٰ بہتی ہوئی نہروں والی جنتوں میں داخل کرے گا۔ اللہ جو اراده کرے اسے کر کے رہتا ہے.');
INSERT INTO "junagarri" VALUES(22,15,'22:15','جس کا یہ خیال ہو کہ اللہ تعالیٰ اپنے رسول کی مدد دونوں جہان میں نہ کرے گا وه اونچائی پر ایک رسہ باندھ کر (اپنے حلق میں پھنده ڈال کر اپنا گلا گھونٹ لے) پھر دیکھ لے کہ اس کی چالاکیوں سے وه بات ہٹ جاتی ہے جو اسے تڑپا رہی ہے؟');
INSERT INTO "junagarri" VALUES(22,16,'22:16','ہم نے اسی طرح اس قرآن کو واضح آیتوں میں اتارا ہے جسے اللہ چاہے ہدایت نصیب فرماتا ہے.');
INSERT INTO "junagarri" VALUES(22,17,'22:17','بیشک اہل ایمان اور یہودی اور صابی اور نصرانی اور مجوسی اور مشرکین ان سب کے درمیان قیامت کے دن خود اللہ تعالیٰ فیصلے کر دے گا ، اللہ تعالیٰ ہر چیز پر گواه ہے. ');
INSERT INTO "junagarri" VALUES(22,18,'22:18','کیا تو نہیں دیکھ رہا کہ اللہ کے سامنے سجده میں ہیں سب آسمانوں والے اور سب زمینوں والے اور سورج اور چاند اور ستارے اور پہاڑ اور درخت اور جانور اور بہت سے انسان بھی ۔ ہاں بہت سے وه بھی ہیں جن پر عذاب کا مقولہ ثابت ہو چکا ہے ، جسے رب ذلیل کردے اسے کوئی عزت دینے والا نہیں ، اللہ جو چاہتا ہے کرتا ہے.');
INSERT INTO "junagarri" VALUES(22,19,'22:19','یہ دونوں اپنے رب کے بارے میں اختلاف کرنے والے ہیں، پس کافروں کے لئے تو آگ کے کپڑے بیونت کر کاٹے جائیں گے، اور ان کے سروں کے اوپر سے سخت کھولتا ہوا پانی بہایا جائے گا.');
INSERT INTO "junagarri" VALUES(22,20,'22:20','جس سے ان کے پیٹ کی سب چیزیں اور کھالیں گلا دی جائیں گی.');
INSERT INTO "junagarri" VALUES(22,21,'22:21','اور ان کی سزا کے لئے لوہے کے ہتھوڑے ہیں.');
INSERT INTO "junagarri" VALUES(22,22,'22:22','یہ جب بھی وہاں کے غم سے نکل بھاگنے کا اراده کریں گے وہیں لوٹا دیئے جائیں گے اور (کہا جائے گا) جلنے کا عذاب چکھو! ');
INSERT INTO "junagarri" VALUES(22,23,'22:23','ایمان والوں اور نیک کام والوں کو اللہ تعالیٰ ان جنتوں میں لے جائے گا جن کے درختوں تلے سے نہریں لہریں لے رہی ہیں، جہاں وه سونے کے کنگن پہنائے جائیں گے اور سچے موتی بھی۔ وہاں ان کا لباس خالص ریشم ہوگا. ');
INSERT INTO "junagarri" VALUES(22,24,'22:24','ان کو پاکیزه بات کی رہنمائی کر دی گئی اور قابل صد تعریف راه کی ہدایت کر دی گئی. ');
INSERT INTO "junagarri" VALUES(22,25,'22:25','جن لوگوں نے کفر کیا اور اللہ کی راه سے روکنے لگے اور اس حرمت والی مسجد سے بھی جسے ہم نے تمام لوگوں کے لئے مساوی کر دیا ہے وہیں کے رہنے والے ہوں یا باہر کے ہوں ، جو بھی ظلم کے ساتھ وہاں الحاد کا اراده کرے ہم اسے درد ناک عذاب چکھائیں گے. ');
INSERT INTO "junagarri" VALUES(22,26,'22:26','اور جبکہ ہم نے ابراہیم (علیہ السلام) کو کعبہ کے مکان کی جگہ مقرر کر دی اس شرط پر کہ میرے ساتھ کسی کو شریک نہ کرنا اور میرے گھر کو طواف قیام رکوع سجده کرنے والوں کے لئے پاک صاف رکھنا. ');
INSERT INTO "junagarri" VALUES(22,27,'22:27','اور لوگوں میں حج کی منادی کر دے لوگ تیرے پاس پا پیاده بھی آئیں گے اور دبلے پتلے اونٹوں پر بھی اور دور دراز کی تمام راہوں سے آئیں گے.');
INSERT INTO "junagarri" VALUES(22,28,'22:28','اپنے فائدے حاصل کرنے کو آجائیں اور ان مقرره دنوں میں اللہ کا نام یاد کریں اور چوپایوں پر جو پالتو ہیں ۔ پس تم آپ بھی کھاؤ اور بھوکے فقیروں کو بھی کھلاؤ.');
INSERT INTO "junagarri" VALUES(22,29,'22:29','پھر وه اپنا میل کچیل دور کریں اور اپنی نذریں پوری کریں اور اللہ کے قدیم گھر کا طواف کریں. ');
INSERT INTO "junagarri" VALUES(22,30,'22:30','یہ ہے اور جو کوئی اللہ کی حرمتوں کی تعظیم کرے اس کے اپنے لئے اس کے رب کے پاس بہتری ہے۔ اور تمہارے لئے چوپائے جانور حلال کر دیئے گئے بجز ان کے جو تمہارے سامنے بیان کیے گئے ہیں پس تمہیں بتوں کی گندگی سے بچتے رہنا چاہئے اور جھوٹی بات سے بھی پرہیز کرنا چاہئے. ');
INSERT INTO "junagarri" VALUES(22,31,'22:31','اللہ کی توحید کو مانتے ہوئے اس کے ساتھ کسی کو شریک نہ کرتے ہوئے۔ سنو! اللہ کے ساتھ شریک کرنے والا گویا آسمان سے گر پڑا، اب یا تو اسے پرندے اچک لے جائیں گے یا ہوا کسی دور دراز کی جگہ پھینک دے گی. ');
INSERT INTO "junagarri" VALUES(22,32,'22:32','یہ سن لیا اب اور سنو! اللہ کی نشانیوں کی جو عزت وحرمت کرے اس کے دل کی پرہیز گاری کی وجہ سے یہ ہے. ');
INSERT INTO "junagarri" VALUES(22,33,'22:33','ان میں تمہارے لئے ایک مقرر وقت تک فائده ہے پھر ان کے حلال ہونے کی جگہ خانہ کعبہ ہے. ');
INSERT INTO "junagarri" VALUES(22,34,'22:34','اور ہر امت کے لئے ہم نے قربانی کے طریقے مقرر فرمائے ہیں تاکہ وه ان چوپائے جانوروں پر اللہ کا نام لیں جو اللہ نے انہیں دے رکھے ہیں ۔ سمجھ لو کہ تم سب کا معبود برحق صرف ایک ہی ہے تم اسی کے تابع فرمان ہو جاؤ عاجزی کرنے والوں کو خوشخبری سنا دیجئے!');
INSERT INTO "junagarri" VALUES(22,35,'22:35','انہیں کہ جب اللہ کا ذکر کیا جائے ان کے دل تھرا جاتے ہیں۔ انہیں جو برائی پہنچے اس پر صبر کرتے ہیں، نماز قائم کرنے والے ہیں اور جو کچھ ہم نے انہیں دے رکھا ہے وه اس میں سے بھی دیتے رہتے ہیں.');
INSERT INTO "junagarri" VALUES(22,36,'22:36','قربانی کے اونٹ ہم نے تمہارے لئے اللہ تعالیٰ کی نشانیاں مقرر کر دی ہیں ان میں تمہیں نفع ہے۔ پس انہیں کھڑا کر کے ان پر اللہ کا نام لو ، پھر جب ان کے پہلو زمین سے لگ جائیں اسے (خود بھی) کھاؤ اور مسکین سوال سے رکنے والوں اور سوال کرنے والوں کو بھی کھلاؤ ، اسی طرح ہم نے چوپایوں کو تمہارے ماتحت کردیا ہے کہ تم شکر گزاری کرو.');
INSERT INTO "junagarri" VALUES(22,37,'22:37','اللہ تعالیٰ کو قربانیوں کے گوشت نہیں پہنچتے نہ اس کے خون بلکہ اسے تو تمہارے دل کی پرہیزگاری پہنچتی ہے۔ اسی طرح اللہ نے ان جانوروں کو تمہارے مطیع کر دیا ہے کہ تم اس کی رہنمائی کے شکریئے میں اس کی بڑائیاں بیان کرو، اور نیک لوگوں کو خوشخبری سنا دیجئے!');
INSERT INTO "junagarri" VALUES(22,38,'22:38','سن رکھو! یقیناً سچے مومنوں کے دشمنوں کو خود اللہ تعالیٰ ہٹا دیتا ہے ۔ کوئی خیانت کرنے والا ناشکرا اللہ تعالیٰ کو ہرگز پسند نہیں.');
INSERT INTO "junagarri" VALUES(22,39,'22:39','جن (مسلمانوں) سے (کافر) جنگ کر رہے ہیں انہیں بھی مقابلے کی اجازت دی جاتی ہے کیونکہ وه مظلوم ہیں ۔ بیشک ان کی مدد پر اللہ قادر ہے.');
INSERT INTO "junagarri" VALUES(22,40,'22:40','یہ وه ہیں جنہیں ناحق اپنے گھروں سے نکالا گیا، صرف ان کےاس قول پر کہ ہمارا پروردگار فقط اللہ ہے۔ اگر اللہ تعالیٰ لوگوں کو آپس میں ایک دوسرے سے نہ ہٹاتا رہتا تو عبادت خانے اور گرجے اور مسجدیں اور یہودیوں کے معبد اور وه مسجدیں بھی ڈھا دی جاتیں جہاں اللہ کا نام بہ کثرت لیا جاتا ہے۔ جو اللہ کی مدد کرے گا اللہ بھی ضرور اس کی مدد کرے گا۔ بیشک اللہ تعالیٰ بڑی قوتوں والا بڑے غلبے والا ہے.');
INSERT INTO "junagarri" VALUES(22,41,'22:41','یہ وه لوگ ہیں کہ اگر ہم زمین میں ان کے پاؤں جما دیں تو یہ پوری پابندی سے نمازیں قائم کریں اور زکوٰتیں دیں اور اچھے کاموں کا حکم کریں اور برے کاموں سے منع کریں ۔ تمام کاموں کا انجام اللہ کے اختیار میں ہے. ');
INSERT INTO "junagarri" VALUES(22,42,'22:42','اگر یہ لوگ آپ کو جھٹلائیں (تو کوئی تعجب کی بات نہیں) تو ان سے پہلے نوح کی قوم اور عاد اور ثمود.');
INSERT INTO "junagarri" VALUES(22,43,'22:43','اور قوم ابراہیم اور قوم لوط.');
INSERT INTO "junagarri" VALUES(22,44,'22:44','اور مدین والے بھی اپنے اپنے نبیوں کو جھٹلا چکے ہیں۔ موسیٰ (علیہ السلام) بھی جھٹلائے جا چکے ہیں پس میں نے کافروں کو یوں ہی سی مہلت دی پھر دھر دبایا ، پھر میرا عذاب کیسا ہوا؟ ');
INSERT INTO "junagarri" VALUES(22,45,'22:45','بہت سی بستیاں ہیں جنہیں ہم نے تہ وبالا کر دیا اس لئے کہ وه ظالم تھے پس وه اپنی چھتوں کے بل اوندھی ہوئی پڑی ہیں اور بہت سے آباد کنوئیں بیکار پڑے ہیں اور بہت سے پکے اور بلند محل ویران پڑے ہیں.');
INSERT INTO "junagarri" VALUES(22,46,'22:46','کیا انہوں نے زمین میں سیر وسیاحت نہیں کی جو ان کے دل ان باتوں کے سمجھنے والے ہوتے یا کانوں سے ہی ان (واقعات) کو سن لیتے، بات یہ ہے کہ صرف آنکھیں ہی اندھی نہیں ہوتیں بلکہ وه دل اندھے ہو جاتے ہیں جو سینوں میں ہیں. ');
INSERT INTO "junagarri" VALUES(22,47,'22:47','اللہ ہرگز اپنا وعده نہیں ٹالے گا۔ ہاں البتہ آپ کے رب کے نزدیک ایک دن تمہاری گنتی کے اعتبار سے ایک ہزار سال کا ہے. ');
INSERT INTO "junagarri" VALUES(22,48,'22:48','بہت سی ظلم کرنے والی بستیوں کو میں نے ڈھیل دی پھر آخر انہیں پکڑ لیا، اور میری ہی طرف لوٹ کر آنا ہے. ');
INSERT INTO "junagarri" VALUES(22,49,'22:49','اعلان کر دو کہ لوگو! میں تمہیں کھلم کھلا چوکنا کرنے والا ہی ہوں. ');
INSERT INTO "junagarri" VALUES(22,50,'22:50','پس جو ایمان لائے ہیں اور جنہوں نے نیک عمل کیے ہیں ان ہی کے لئے بخشش ہے اور عزت والی روزی.');
INSERT INTO "junagarri" VALUES(22,51,'22:51','اور جو لوگ ہماری نشانیوں کو پست کرنے کے درپے رہتے ہیں وہی دوزخی ہیں.');
INSERT INTO "junagarri" VALUES(22,52,'22:52','ہم نے آپ سے پہلے جس رسول اور نبی کو بھیجا اس کے ساتھ یہ ہوا کہ جب وه اپنے دل میں کوئی آرزو کرنے لگا شیطان نے اس کی آرزو میں کچھ ملا دیا، پس شیطان کی ملاوٹ کو اللہ تعالیٰ دور کر دیتا ہے پھر اپنی باتیں پکی کر دیتا ہے ۔ اللہ تعالیٰ دانا اور باحکمت ہے.');
INSERT INTO "junagarri" VALUES(22,53,'22:53','یہ اس لئے کہ شیطانی ملاوٹ کو اللہ تعالیٰ ان لوگوں کی آزمائش کا ذریعہ بنا دے جن کے دلوں میں بیماری ہے اور جن کے دل سخت ہیں ۔ بیشک ظالم لوگ گہری مخالفت میں ہیں.');
INSERT INTO "junagarri" VALUES(22,54,'22:54','اور اس لئے بھی کہ جنہیں علم عطا فرمایا گیا ہے وه یقین کر لیں کہ یہ آپ کے رب ہی کی طرف سے سراسر حق ہی ہے پھر وه اس پر ایمان لائیں اور ان کے دل اس کی طرف جھک جائیں ۔ یقیناً اللہ تعالیٰ ایمان داروں کو راه راست کی طرف رہبری کرنے والا ہی ہے.');
INSERT INTO "junagarri" VALUES(22,55,'22:55','کافر اس وحی الٰہی میں ہمیشہ شک شبہ ہی کرتے رہیں گے حتیٰ کہ اچانک ان کے سروں پر قیامت آجائے یا ان کے پاس اس دن کا عذاب آجائے جو منحوس ہے. ');
INSERT INTO "junagarri" VALUES(22,56,'22:56','اس دن صرف اللہ ہی کی بادشاہت ہوگی وہی ان میں فیصلے فرمائے گا۔ ایمان اور نیک عمل والے تو نعمتوں سے بھری جنتوں میں ہوں گے.');
INSERT INTO "junagarri" VALUES(22,57,'22:57','اور جن لوگوں نے کفر کیا اور ہماری آیتوں کو جھٹلایا ان کے لئے ذلیل کرنے والے عذاب ہیں.');
INSERT INTO "junagarri" VALUES(22,58,'22:58','اور جن لوگوں نے راه خدا میں ترک وطن کیا پھر وه شہید کر دیئے گئے یا اپنی موت مر گئے اللہ تعالیٰ انہیں بہترین رزق عطا فرمائے گا ۔ اور بیشک اللہ تعالیٰ روزی دینے والوں میں سب سے بہتر ہے. ');
INSERT INTO "junagarri" VALUES(22,59,'22:59','انہیں اللہ تعالیٰ ایسی جگہ پہنچائے گا کہ وه اس سے راضی ہو جائیں گے ، بیشک اللہ تعالیٰ علم اور بردباری والا ہے.');
INSERT INTO "junagarri" VALUES(22,60,'22:60','بات یہی ہے ، اور جس نے بدلہ لیا اسی کے برابر جو اس کے ساتھ کیا گیا تھا پھر اگر اس سے زیادتی کی جائے تو یقیناً اللہ تعالیٰ خود اس کی مدد فرمائے گا ۔ بیشک اللہ درگزر کرنے والا بخشنے والا ہے. ');
INSERT INTO "junagarri" VALUES(22,61,'22:61','یہ اس لئے کہ اللہ رات کو دن میں داخل کرتا ہے اور دن کو رات میں داخل کرتا ہے ، اور بیشک اللہ سننے والا دیکھنے والا ہے.');
INSERT INTO "junagarri" VALUES(22,62,'22:62','یہ سب اس لئے کہ اللہ ہی حق ہے اور اس کے سوا جسے بھی یہ پکارتے ہیں وه باطل ہے اور بیشک اللہ ہی بلندی والا کبریائی والا ہے.');
INSERT INTO "junagarri" VALUES(22,63,'22:63','کیا آپ نے نہیں دیکھا کہ اللہ تعالیٰ آسمان سے پانی برساتا ہے، پس زمین سرسبز ہو جاتی ہے۔ بے شک اللہ تعالیٰ مہربان اور باخبر ہے. ');
INSERT INTO "junagarri" VALUES(22,64,'22:64','آسمانوں اور زمین میں جو کچھ ہے اسی کا ہے اور یقیناً اللہ وہی ہے بے نیاز تعریفوں والا.');
INSERT INTO "junagarri" VALUES(22,65,'22:65','کیا آپ نے نہیں دیکھا کہ اللہ ہی نے زمین کی تمام چیزیں تمہارے لئے مسخر کر دی ہیں اور اس کے فرمان سے پانی میں چلتی ہوئی کشتیاں بھی۔ وہی آسمان کو تھامے ہوئے ہے کہ زمین پر اس کی اجازت کے بغیر نہ گر پڑے ، بیشک اللہ تعالیٰ لوگوں پر شفقت ونرمی کرنے والا اور مہربان ہے. ');
INSERT INTO "junagarri" VALUES(22,66,'22:66','اسی نے تمہیں زندگی بخشی، پھر وہی تمہیں مار ڈالے گا پھر وہی تمہیں زنده کرے گا، بے شک انسان البتہ ناشکرا ہے. ');
INSERT INTO "junagarri" VALUES(22,67,'22:67','ہر امت کے لئے ہم نے عبادت کا ایک طریقہ مقرر کر دیا ہے، جسے وه بجا لانے والے ہیں پس انہیں اس امر میں آپ سے جھگڑا نہ کرنا چاہئے آپ اپنے پروردگار کی طرف لوگوں کو بلائیے۔ یقیناً آپ ٹھیک ہدایت پر ہی ہیں. ');
INSERT INTO "junagarri" VALUES(22,68,'22:68','پھر بھی اگر یہ لوگ آپ سے الجھنے لگیں تو آپ کہہ دیں کہ تمہارے اعمال سے اللہ بخوبی واقف ہے.');
INSERT INTO "junagarri" VALUES(22,69,'22:69','بیشک تمہارے سب کے اختلاف کا فیصلہ قیامت والے دن اللہ تعالیٰ آپ کرے گا. ');
INSERT INTO "junagarri" VALUES(22,70,'22:70','کیا آپ نے نہیں جانا کہ آسمان وزمین کی ہر چیز اللہ کے علم میں ہے۔ یہ سب لکھی ہوئی کتاب میں محفوظ ہے۔ اللہ تعالیٰ پر تو یہ امر بالکل آسان ہے. ');
INSERT INTO "junagarri" VALUES(22,71,'22:71','اور یہ اللہ کے سوا ان کی عبادت کر رہے ہیں جس کی کوئی الہی دلیل نازل نہیں ہوئی نہ وه خود ہی اس کا کوئی علم رکھتے ہیں ۔ ظالموں کا کوئی مددگار نہیں.');
INSERT INTO "junagarri" VALUES(22,72,'22:72','جب ان کے سامنے ہمارے کلام کی کھلی ہوئی آیتوں کی تلاوت کی جاتی ہے تو آپ کافروں کے چہروں پر ناخوشی کے صاف آثار پہچان لیتے ہیں۔ وه تو قریب ہوتے ہیں کہ ہماری آیتیں سنانے والوں پر حملہ کر بیٹھیں ، کہہ دیجئے کہ کیا میں تمہیں اس سے بھی زیاده بدتر خبر دوں۔ وه آگ ہے، جس کا وعده اللہ نے کافروں سے کر رکھا ہے ، اور وه بہت ہی بری جگہ ہے.');
INSERT INTO "junagarri" VALUES(22,73,'22:73','لوگو! ایک مثال بیان کی جا رہی ہے، ذرا کان لگا کر سن لو! اللہ کے سوا جن جن کو تم پکارتے رہے ہو وه ایک مکھی بھی تو پیدا نہیں کر سکتے، گو سارے کے سارے ہی جمع ہو جائیں ، بلکہ اگر مکھی ان سے کوئی چیز لے بھاگے تو یہ تو اسے بھی اس سے چھین نہیں سکتے، بڑا بودا ہے طلب کرنے والا اور بڑا بودا ہے وه جس سے طلب کیا جا رہا ہے.');
INSERT INTO "junagarri" VALUES(22,74,'22:74','انہوں نے اللہ کے مرتبہ کے مطابق اس کی قدر جانی ہی نہیں ، اللہ تعالیٰ بڑا ہی زور وقوت والا اور غالب وزبردست ہے.');
INSERT INTO "junagarri" VALUES(22,75,'22:75','فرشتوں میں سے اور انسانوں میں سے پیغام پہونچانے والوں کو اللہ ہی چھانٹ لیتا ہے، بیشک اللہ تعالیٰ سننے والا دیکھنے والا ہے. ');
INSERT INTO "junagarri" VALUES(22,76,'22:76','وه بخوبی جانتا ہے جو کچھ ان کے آگے ہے اور جو کچھ ان کے پیچھے ہے، اور اللہ ہی کی طرف سب کام لوٹائے جاتے ہیں. ');
INSERT INTO "junagarri" VALUES(22,77,'22:77','اے ایمان والو! رکوع سجده کرتے رہو اور اپنے پروردگار کی عبادت میں لگے رہو اور نیک کام کرتے رہو تاکہ تم کامیاب ہو جاؤ. ');
INSERT INTO "junagarri" VALUES(22,78,'22:78','اور اللہ کی راه میں ویسا ہی جہاد کرو جیسا جہاد کا حق ہے ۔ اسی نے تمہیں برگزیده بنایا ہے اور تم پر دین کے بارے میں کوئی تنگی نہیں ڈالی ، دین اپنے باپ ابراہیم (علیہ السلام) کا قائم رکھو، اسی اللہ نے تمہارا نام مسلمان رکھا ہے۔ اس قرآن سے پہلے اور اس میں بھی تاکہ پیغمبر تم پر گواه ہو جائے اور تم تمام لوگوں کے گواه بن جاؤ ۔ پس تمہیں چاہئے کہ نمازیں قائم رکھو اور زکوٰة ادا کرتے رہو اور اللہ کو مضبوط تھام لو، وہی تمہارا ولی اور مالک ہے۔ پس کیا ہی اچھا مالک ہے اور کتنا ہی بہتر مددگار ہے.');
INSERT INTO "junagarri" VALUES(23,1,'23:1','یقیناً ایمان والوں نے فلاح حاصل کرلی. ');
INSERT INTO "junagarri" VALUES(23,2,'23:2','جو اپنی نماز میں خشوع کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(23,3,'23:3','جو لغویات سے منھ موڑ لیتے ہیں. ');
INSERT INTO "junagarri" VALUES(23,4,'23:4','جو زکوٰة ادا کرنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(23,5,'23:5','جو اپنی شرمگاہوں کی حفاظت کرنے والے ہیں.');
INSERT INTO "junagarri" VALUES(23,6,'23:6','بجز اپنی بیویوں اور ملکیت کی لونڈیوں کے یقیناً یہ ملامتیوں میں سے نہیں ہیں.');
INSERT INTO "junagarri" VALUES(23,7,'23:7','جو اس کے سوا کچھ اور چاہیں وہی حد سے تجاوز کرجانے والے ہیں. ');
INSERT INTO "junagarri" VALUES(23,8,'23:8','جو اپنی امانتوں اور وعدے کی حفاظت کرنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(23,9,'23:9','جو اپنی نمازوں کی نگہبانی کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(23,10,'23:10','یہی وارث ہیں.');
INSERT INTO "junagarri" VALUES(23,11,'23:11','جو فردوس کے وارث ہوں گے جہاں وه ہمیشہ رہیں گے. ');
INSERT INTO "junagarri" VALUES(23,12,'23:12','یقیناً ہم نے انسان کو مٹی کے جوہر سے پیدا کیا. ');
INSERT INTO "junagarri" VALUES(23,13,'23:13','پھر اسے نطفہ بنا کر محفوظ جگہ میں قرار دے دیا. ');
INSERT INTO "junagarri" VALUES(23,14,'23:14','پھر نطفہ کو ہم نے جما ہوا خون بنا دیا، پھر اس خون کے لوتھڑے کو گوشت کا ٹکڑا کردیا۔ پھر گوشت کے ٹکڑے کو ہڈیاں بنا دیں، پھر ہڈیوں کو ہم نے گوشت پہنا دیا ، پھر دوسری بناوٹ میں اس کو پیدا کردیا ۔ برکتوں والا ہے وه اللہ جو سب سے بہترین پیدا کرنے والا ہے. ');
INSERT INTO "junagarri" VALUES(23,15,'23:15','اس کے بعد پھر تم سب یقیناً مر جانے والے ہو.');
INSERT INTO "junagarri" VALUES(23,16,'23:16','پھر قیامت کے دن بلا شبہ تم سب اٹھائے جاؤ گے.');
INSERT INTO "junagarri" VALUES(23,17,'23:17','ہم نے تمہارے اوپر سات آسمان بنائے ہیں اور ہم مخلوقات سے غافل نہیں ہیں. ');
INSERT INTO "junagarri" VALUES(23,18,'23:18','ہم ایک صحیح انداز سے آسمان سے پانی برساتے ہیں ، پھر اسے زمین میں ٹھہرا دیتے ہیں ، اور ہم اس کے لے جانے پر یقیناً قادر ہیں. ');
INSERT INTO "junagarri" VALUES(23,19,'23:19','اسی پانی کے ذریعہ سے ہم تمہارے لئے کھجوروں اور انگوروں کے باغات پیدا کردیتے ہیں، کہ تمہارے لئے ان میں بہت سے میوے ہوتے ہیں ان ہی میں سے تم کھاتے بھی ہو. ');
INSERT INTO "junagarri" VALUES(23,20,'23:20','اور وه درخت جو طور سینا پہاڑ سے نکلتا ہے جو تیل نکالتا ہے اور کھانے والے کے لئے سالن ہے. ');
INSERT INTO "junagarri" VALUES(23,21,'23:21','تمہارے لئے چوپایوں میں بھی بڑی بھاری عبرت ہے۔ ان کے پیٹوں میں سے ہم تمہیں دودھ پلاتے ہیں اور بھی بہت سے نفع تمہارے لئے ان میں ہیں ان میں سے بعض بعض کو تم کھاتے بھی ہو.');
INSERT INTO "junagarri" VALUES(23,22,'23:22','اور ان پر اور کشتیوں پر تم سوار کرائے جاتے ہو. ');
INSERT INTO "junagarri" VALUES(23,23,'23:23','یقیناً ہم نے نوح (علیہ السلام) کو اس کی قوم کی طرف رسول بنا کر بھیجا، اس نے کہا کہ اے میری قوم کے لوگو! اللہ کی عبادت کرو اور اس کے سوا تمہارا کوئی معبود نہیں، کیا تم (اس سے) نہیں ڈرتے.');
INSERT INTO "junagarri" VALUES(23,24,'23:24','اس کی قوم کے کافر سرداروں نے صاف کہہ دیا کہ یہ تو تم جیسا ہی انسان ہے، یہ تم پر فضیلت اور بڑائی حاصل کرنا چاہتا ہے ۔ اگر اللہ ہی کو منظور ہوتا تو کسی فرشتے کو اتارتا ، ہم نے تو اسے اپنے اگلے باپ دادوں کے زمانے میں سنا ہی نہیں. ');
INSERT INTO "junagarri" VALUES(23,25,'23:25','یقیناً اس شخص کو جنون ہے، پس تم اسے ایک وقت مقرر تک ڈھیل دو. ');
INSERT INTO "junagarri" VALUES(23,26,'23:26','نوح (علیہ السلام) نے دعا کی اے میرے رب! ان کے جھٹلانے پر تو میری مدد کر. ');
INSERT INTO "junagarri" VALUES(23,27,'23:27','تو ہم نے ان کی طرف وحی بھیجی کہ تو ہماری آنکھوں کے سامنے ہماری وحی کے مطابق ایک کشتی بنا۔ جب ہمارا حکم آجائے اور تنور ابل پڑے تو، تو ہر قسم کا ایک ایک جوڑا اس میں رکھ لے اور اپنے اہل کو بھی، مگر ان میں سے جن کی بابت ہماری بات پہلے گزر چکی ہے ۔ خبردار جن لوگوں نے ظلم کیا ہے ان کے بارے میں مجھ سے کچھ کلام نہ کرنا وه تو سب ڈبوئے جائیں گے. ');
INSERT INTO "junagarri" VALUES(23,28,'23:28','جب تو اور تیرے ساتھی کشتی پر باطمینان بیٹھ جاؤ تو کہنا کہ سب تعریف اللہ کے لئے ہی ہے جس نے ہمیں ظالم لوگوں سے نجات عطا فرمائی.');
INSERT INTO "junagarri" VALUES(23,29,'23:29','اور کہنا کہ اے میرے رب ! مجھے بابرکت اتارنا اتار اور تو ہی بہتر ہے اتارنے والوں میں .');
INSERT INTO "junagarri" VALUES(23,30,'23:30','یقیناً اس میں بڑی بڑی نشانیاں ہیں اور ہم بےشک آزمائش کرنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(23,31,'23:31','ان کے بعد ہم نے اور بھی امت پیدا کی. ');
INSERT INTO "junagarri" VALUES(23,32,'23:32','پھر ان میں خود ان میں سے (ہی) رسول بھی بھیجا کہ تم سب اللہ کی عبادت کرو اس کے سوا تمہارا کوئی معبود نہیں ، تم کیوں نہیں ڈرتے؟');
INSERT INTO "junagarri" VALUES(23,33,'23:33','اور سرداران قوم نے جواب دیا، جو کفر کرتے تھے اور آخرت کی ملاقات کو جھٹلاتے تھے اور ہم نے انہیں دنیوی زندگی میں خوشحال کر رکھا تھا ، کہ یہ تو تم جیسا ہی انسان ہے، تمہاری ہی خوراک یہ بھی کھاتا ہے اور تمہارے پینے کا پانی ہی یہ پیتا ہے. ');
INSERT INTO "junagarri" VALUES(23,34,'23:34','اگر تم نے اپنے جیسے ہی انسان کی تابعداری کر لی ہے تو بےشک تم سخت خسارے والے ہو. ');
INSERT INTO "junagarri" VALUES(23,35,'23:35','کیا یہ تمہیں اس بات کا وعده کرتا ہے کہ جب تم مرکر صرف خاک اور ہڈی ره جاؤ گے تو تم پھر زنده کئے جاؤ گے.');
INSERT INTO "junagarri" VALUES(23,36,'23:36','نہیں نہیں دور اور بہت دور ہے وه جس کا تم وعده دیئے جاتے ہو. ');
INSERT INTO "junagarri" VALUES(23,37,'23:37','(زندگی) تو صرف دنیا کی زندگی ہے ہم مرتے جیتے رہتے ہیں اور یہ نہیں کہ ہم پھر اٹھائے جائیں گے.');
INSERT INTO "junagarri" VALUES(23,38,'23:38','یہ تو بس ایسا شخص ہے جس نے اللہ پر جھوٹ (بہتان) باندھ لیا ہے ، ہم تو اس پر ایمان لانے والے نہیں ہیں.');
INSERT INTO "junagarri" VALUES(23,39,'23:39','نبی نے دعا کی کہ پروردگار! ان کے جھٹلانے پر تو میری مدد کر. ');
INSERT INTO "junagarri" VALUES(23,40,'23:40','جواب ملا کہ یہ تو بہت ہی جلد اپنے کئے پر پچھتانے لگیں گے. ');
INSERT INTO "junagarri" VALUES(23,41,'23:41','بالآخر عدل کے تقاضے کے مطابق چیخ نے پکڑ لیا اور ہم نے انہیں کوڑا کرکٹ کر ڈالا ، پس ظالموں کے لئے دوری ہو.');
INSERT INTO "junagarri" VALUES(23,42,'23:42','ان کے بعد ہم نے اور بھی بہت سی امتیں پیدا کیں. ');
INSERT INTO "junagarri" VALUES(23,43,'23:43','نہ تو کوئی امت اپنے وقت مقرره سے آگے بڑھی اور نہ پیچھے رہی. ');
INSERT INTO "junagarri" VALUES(23,44,'23:44','پھر ہم نے لگاتار رسول بھیجے، جب جب اس امت کے پاس اس کا رسول آیا اس نے جھٹلایا، پس ہم نے ایک دوسرے کے پیچھے لگا دیا اور انہیں افسانہ بنا دیا۔ ان لوگوں کو دوری ہے جو ایمان قبول نہیں کرتے.');
INSERT INTO "junagarri" VALUES(23,45,'23:45','پھر ہم نے موسیٰ (علیہ السلام) کو اور ان کے بھائی ہارون (علیہ السلام) کو اپنی آیتوں اور کھلی دلیل کے ساتھ بھیجا.');
INSERT INTO "junagarri" VALUES(23,46,'23:46','فرعون اور اس کے لشکروں کی طرف، پس انہوں نے تکبر کیا اور تھے ہی وه سرکش لوگ. ');
INSERT INTO "junagarri" VALUES(23,47,'23:47','کہنے لگے کہ کیا ہم اپنے جیسے دو شخصوں پر ایمان لائیں؟ حالانکہ خود ان کی قوم (بھی) ہمارے ماتحت ہے.');
INSERT INTO "junagarri" VALUES(23,48,'23:48','پس انہوں نے ان دونوں کو جھٹلایا آخر وه بھی ہلاک شده لوگوں میں مل گئے.');
INSERT INTO "junagarri" VALUES(23,49,'23:49','ہم نے تو موسیٰ (علیہ السلام) کو کتاب (بھی) دی کہ لوگ راه راست پر آجائیں. ');
INSERT INTO "junagarri" VALUES(23,50,'23:50','ہم نے ابن مریم اور اس کی والده کو ایک نشانی بنایا اور ان دونوں کو بلند صاف قرار والی اور جاری پانی والی جگہ میں پناه دی.');
INSERT INTO "junagarri" VALUES(23,51,'23:51','اے پیغمبرو! حلال چیزیں کھاؤ اور نیک عمل کرو تم جو کچھ کر رہے ہو اس سے میں بخوبی واقف ہوں.');
INSERT INTO "junagarri" VALUES(23,52,'23:52','یقیناً تمہارا یہ دین ایک ہی دین ہے اور میں ہی تم سب کا رب ہوں، پس تم مجھ سے ڈرتے رہو.');
INSERT INTO "junagarri" VALUES(23,53,'23:53','پھر انہوں نے خود (ہی) اپنے امر (دین) کے آپس میں ٹکڑے ٹکڑے کر لیئے، ہر گروه جو کچھ اس کے پاس ہے اسی پر اترا رہا ہے.');
INSERT INTO "junagarri" VALUES(23,54,'23:54','پس آپ (بھی) انہیں ان کی غفلت میں ہی کچھ مدت پڑا رہنے دیں. ');
INSERT INTO "junagarri" VALUES(23,55,'23:55','کیا یہ (یوں) سمجھ بیٹھے ہیں؟ کہ ہم جو بھی ان کے مال و اولاد بڑھا رہے ہیں.');
INSERT INTO "junagarri" VALUES(23,56,'23:56','وه ان کے لئے بھلائیوں میں جلدی کر رہے ہیں (نہیں نہیں) بلکہ یہ سمجھتے ہی نہیں.');
INSERT INTO "junagarri" VALUES(23,57,'23:57','یقیناً جو لوگ اپنے رب کی ہیبت سے ڈرتے ہیں.');
INSERT INTO "junagarri" VALUES(23,58,'23:58','اور جو اپنے رب کی آیتوں پر ایمان رکھتے ہیں.');
INSERT INTO "junagarri" VALUES(23,59,'23:59','اور جو اپنے رب کے ساتھ کسی کو شریک نہیں کرتے.');
INSERT INTO "junagarri" VALUES(23,60,'23:60','اور جو لوگ دیتے ہیں جو کچھ دیتے ہیں اور ان کے دل کپکپاتے ہیں کہ وه اپنے رب کی طرف لوٹنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(23,61,'23:61','یہی ہیں جو جلدی جلدی بھلائیاں حاصل کر رہے ہیں اور یہی ہیں جو ان کی طرف دوڑ جانے والے ہیں.');
INSERT INTO "junagarri" VALUES(23,62,'23:62','ہم کسی نفس کو اس کی طاقت سے زیاده تکلیف نہیں دیتے ، اور ہمارے پاس ایسی کتاب ہے جو حق کے ساتھ بولتی ہے، ان کے اوپر کچھ ظلم نہ کیا جائے گا.');
INSERT INTO "junagarri" VALUES(23,63,'23:63','بلکہ ان کے دل اس طرف سے غفلت میں ہیں اور ان کے لئے اس کے سوا بھی بہت سے اعمال ہیں جنہیں وه کرنے والے. ہیں');
INSERT INTO "junagarri" VALUES(23,64,'23:64','یہاں تک کہ جب ہم نے ان کے آسوده حال لوگوں کو عذاب میں پکڑ لیا تو وه بلبلانے لگے.');
INSERT INTO "junagarri" VALUES(23,65,'23:65','آج مت بلبلاؤ یقیناً تم ہمارے مقابلہ پر مدد نہ کئے جاؤ گے. ');
INSERT INTO "junagarri" VALUES(23,66,'23:66','میری آیتیں تو تمہارے سامنے پڑھی جاتی تھیں پھر بھی تم اپنی ایڑیوں کے بل الٹے بھاگتے تھے. ');
INSERT INTO "junagarri" VALUES(23,67,'23:67','اکڑتے اینٹھتے افسانہ گوئی کرتے اسے چھوڑ دیتے تھے. ');
INSERT INTO "junagarri" VALUES(23,68,'23:68','کیا انہوں نے اس بات میں غور وفکر ہی نہیں کیا ؟ بلکہ ان کے پاس وه آیا جو ان کے اگلے باپ دادوں کے پاس نہیں آیا تھا؟ ');
INSERT INTO "junagarri" VALUES(23,69,'23:69','یا انہوں نے اپنے پیغمبر کو پہچانا نہیں کہ اس کے منکر ہو رہے ہیں؟ ');
INSERT INTO "junagarri" VALUES(23,70,'23:70','یا یہ کہتے ہیں کہ اسے جنون ہے ؟ بلکہ وه تو ان کے پاس حق لایا ہے۔ ہاں ان میں اکثر حق سے چڑنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(23,71,'23:71','اگر حق ہی ان کی خواہشوں کا پیرو ہوجائے تو زمین وآسمان اور ان کے درمیان کی ہر چیز درہم برہم ہو جائے ۔ حق تو یہ ہے کہ ہم نے انہیں ان کی نصیحت پہنچا دی ہے لیکن وه اپنی نصیحت سے منھ موڑنے والے ہیں.');
INSERT INTO "junagarri" VALUES(23,72,'23:72','کیا آپ ان سے کوئی اجرت چاہتے ہیں؟ یاد رکھیئے کہ آپ کے رب کی اجرت بہت ہی بہتر ہے اور وه سب سے بہتر روزی رساں ہے.');
INSERT INTO "junagarri" VALUES(23,73,'23:73','یقیناً آپ تو انہیں راه راست کی طرف بلا رہے ہیں.');
INSERT INTO "junagarri" VALUES(23,74,'23:74','بےشک جو لوگ آخرت پر یقین نہیں رکھتے وه سیدھے راستے سے مڑ جانے والے ہیں. ');
INSERT INTO "junagarri" VALUES(23,75,'23:75','اور اگر ہم ان پر رحم فرمائیں اور ان کی تکلیفیں دور کردیں تو یہ تو اپنی اپنی سرکشی میں جم کر اور بہکنے لگیں. ');
INSERT INTO "junagarri" VALUES(23,76,'23:76','اور ہم نے انہیں عذاب میں بھی پکڑا تاہم یہ لوگ نہ تو اپنے پروردگار کے سامنے جھکے اور نہ ہی عاجزی اختیار کی. ');
INSERT INTO "junagarri" VALUES(23,77,'23:77','یہاں تک کہ جب ہم نے ان پر سخت عذاب کا دروازه کھول دیا تو اسی وقت فوراً مایوس ہوگئے. ');
INSERT INTO "junagarri" VALUES(23,78,'23:78','وه اللہ ہے جس نے تمہارے لئے کان اور آنکھیں اور دل پیدا کئے، مگر تم بہت (ہی) کم شکر کرتے ہو. ');
INSERT INTO "junagarri" VALUES(23,79,'23:79','اور وہی ہے جس نے تمہیں پیدا کرکے زمین میں پھیلا دیا اور اسی کی طرف تم جمع کئے جاؤ گے. ');
INSERT INTO "junagarri" VALUES(23,80,'23:80','اور یہ وہی ہے جو جلاتا اور مارتا ہے اور رات دن کے ردوبدل کا مختار بھی وہی ہے۔ کیا تم کو سمجھ بوجھ نہیں؟ ');
INSERT INTO "junagarri" VALUES(23,81,'23:81','بلکہ ان لوگوں نے بھی ویسی ہی بات کہی جو اگلے کہتے چلے آئے.');
INSERT INTO "junagarri" VALUES(23,82,'23:82','کہ کیا جب ہم مر کر مٹی اور ہڈی ہوجائیں گے کیا پھر بھی ہم ضرور اٹھائے جائیں گے؟');
INSERT INTO "junagarri" VALUES(23,83,'23:83','ہم سے اور ہمارے باپ دادوں سے پہلے ہی یہ وعده ہوتا چلا آیا ہے کچھ نہیں یہ تو صرف اگلے لوگوں کے افسانے ہیں. ');
INSERT INTO "junagarri" VALUES(23,84,'23:84','پوچھیئے تو سہی کہ زمین اور اس کی کل چیزیں کس کی ہیں؟ بتلاؤ اگر جانتے ہو؟');
INSERT INTO "junagarri" VALUES(23,85,'23:85','فوراً جواب دیں گے کہ اللہ کی، کہہ دیجیئے کہ پھر تم نصیحت کیوں نہیں حاصل کرتے.');
INSERT INTO "junagarri" VALUES(23,86,'23:86','دریافت کیجیئے کہ ساتوں آسمان کا اور بہت باعظمت عرش کا رب کون ہے؟');
INSERT INTO "junagarri" VALUES(23,87,'23:87','وه لوگ جواب دیں گے کہ اللہ ہی ہے۔ کہہ دیجیئے کہ پھر تم کیوں نہیں ڈرتے؟ ');
INSERT INTO "junagarri" VALUES(23,88,'23:88','پوچھیئے کہ تمام چیزوں کا اختیار کس کے ہاتھ میں ہے؟ جو پناه دیتا ہے اور جس کے مقابلے میں کوئی پناه نہیں دیا جاتا ، اگر تم جانتے ہو تو بتلا دو؟');
INSERT INTO "junagarri" VALUES(23,89,'23:89','یہی جواب دیں گے کہ اللہ ہی ہے، کہہ دیجیئے پھر تم کدھر سے جادو کر دیئے جاتے ہو؟ ');
INSERT INTO "junagarri" VALUES(23,90,'23:90','حق یہ ہے کہ ہم نے انہیں حق پہنچا دیا ہے اور یہ بےشک جھوٹے ہیں.');
INSERT INTO "junagarri" VALUES(23,91,'23:91','نہ تو اللہ نے کسی کو بیٹا بنایا اور نہ اس کے ساتھ اور کوئی معبود ہے، ورنہ ہر معبود اپنی مخلوق کو لئے لئے پھرتا اور ہر ایک دوسرے پر چڑھ دوڑتا۔ جو اوصاف یہ بتلاتے ہیں ان سے اللہ پاک (اور بےنیاز) ہے.');
INSERT INTO "junagarri" VALUES(23,92,'23:92','وه غائب حاضر کا جاننے والا ہے اور جو شرک یہ کرتے ہیں اس سے بالاتر ہے.');
INSERT INTO "junagarri" VALUES(23,93,'23:93','آپ دعا کریں کہ اے میرے پروردگار! اگر تو مجھے وه دکھائے جس کا وعده انہیں دیا جا رہا ہے.');
INSERT INTO "junagarri" VALUES(23,94,'23:94','تو اے رب! تو مجھے ان ظالموں کے گروه میں نہ کرنا. ');
INSERT INTO "junagarri" VALUES(23,95,'23:95','ہم جو کچھ وعدے انہیں دے رہے ہیں سب آپ کو دکھا دینے پر یقیناً قادر ہیں.');
INSERT INTO "junagarri" VALUES(23,96,'23:96','برائی کو اس طریقے سے دور کریں جو سراسر بھلائی والا ہو ، جو کچھ یہ بیان کرتے ہیں ہم بخوبی واقف ہیں.');
INSERT INTO "junagarri" VALUES(23,97,'23:97','اور دعا کریں کہ اے میرے پروردگار! میں شیطانوں کے وسوسوں سے تیری پناه چاہتا ہوں. ');
INSERT INTO "junagarri" VALUES(23,98,'23:98','اور اے رب! میں تیری پناه چاہتا ہوں کہ وه میرے پاس آجائیں. ');
INSERT INTO "junagarri" VALUES(23,99,'23:99','یہاں تک کہ جب ان میں کسی کو موت آنے لگتی ہے تو کہتا ہے اے میرے پروردگار! مجھے واپس لوٹا دے');
INSERT INTO "junagarri" VALUES(23,100,'23:100','کہ اپنی چھوڑی ہوئی دنیا میں جا کر نیک اعمال کر لوں ، ہرگز ایسا نہیں ہوگا ، یہ تو صرف ایک قول ہے جس کا یہ قائل ہے، ان کے پس پشت تو ایک حجاب ہے، ان کے دوباره جی اٹھنے کے دن تک. ');
INSERT INTO "junagarri" VALUES(23,101,'23:101','پس جب کہ صور پھونک دیا جائے گا اس دن نہ تو آپس کے رشتے دار ہی رہیں گے، نہ آپس کی پوچھ گچھ. ');
INSERT INTO "junagarri" VALUES(23,102,'23:102','جن کی ترازو کا پلہ بھاری ہوگیا وه تو نجات والے ہوگئے.');
INSERT INTO "junagarri" VALUES(23,103,'23:103','اور جن کے ترازو کا پلہ ہلکا ہوگیا یہ ہیں وه جنہوں نے اپنا نقصان آپ کر لیا جو ہمیشہ کے لئے جہنم واصل ہوئے.');
INSERT INTO "junagarri" VALUES(23,104,'23:104','ان کے چہروں کو آگ جھلستی رہے گی اور وه وہاں بدشکل بنے ہوئے ہوں گے. ');
INSERT INTO "junagarri" VALUES(23,105,'23:105','کیا میری آیتیں تمہارے سامنے تلاوت نہیں کی جاتی تھیں؟ پھر بھی تم انہیں جھٹلاتے تھے.');
INSERT INTO "junagarri" VALUES(23,106,'23:106','کہیں گے کہ اے پروردگار! ہماری بدبختی ہم پر غالب آگئی (واقعی) ہم تھے ہی گمراه.');
INSERT INTO "junagarri" VALUES(23,107,'23:107','اے ہمارے پروردگار! ہمیں یہاں سے نجات دے اگر اب بھی ہم ایسا ہی کریں تو بےشک ہم ظالم ہیں.');
INSERT INTO "junagarri" VALUES(23,108,'23:108','اللہ تعالیٰ فرمائے گا پھٹکارے ہوئے یہیں پڑے رہو اور مجھ سے کلام نہ کرو.');
INSERT INTO "junagarri" VALUES(23,109,'23:109','میرے بندوں کی ایک جماعت تھی جو برابر یہی کہتی رہی کہ اے ہمارے پروردگار! ہم ایمان لاچکے ہیں تو ہمیں بخش اور ہم پر رحم فرما تو سب مہربانوں سے زیاده مہربان ہے.');
INSERT INTO "junagarri" VALUES(23,110,'23:110','(لیکن) تم انہیں مذاق میں ہی اڑاتے رہے یہاں تک کہ (اس مشغلے نے) تم کو میری یاد (بھی) بھلا دی اور تم ان سے مذاق ہی کرتے رہے.');
INSERT INTO "junagarri" VALUES(23,111,'23:111','میں نے آج انہیں ان کے اس صبر کا بدلہ دے دیا ہے کہ وه خاطر خواه اپنی مراد کو پہنچ چکے ہیں. ');
INSERT INTO "junagarri" VALUES(23,112,'23:112','اللہ تعالیٰ دریافت فرمائے گا کہ تم زمین میں باعتبار برسوں کی گنتی کے کس قدر رہے؟');
INSERT INTO "junagarri" VALUES(23,113,'23:113','وه کہیں گے ایک دن یا ایک دن سے بھی کم، گنتی گننے والوں سے بھی پوچھ لیجیئے. ');
INSERT INTO "junagarri" VALUES(23,114,'23:114','اللہ تعالیٰ فرمائے گا فی الواقع تم وہاں بہت ہی کم رہے ہو اے کاش! تم اسے پہلے ہی سے جان لیتے؟ ');
INSERT INTO "junagarri" VALUES(23,115,'23:115','کیا تم یہ گمان کئے ہوئے ہو کہ ہم نے تمہیں یوں ہی بیکار پیدا کیا ہے اور یہ کہ تم ہماری طرف لوٹائے ہی نہ جاؤ گے.');
INSERT INTO "junagarri" VALUES(23,116,'23:116','اللہ تعالیٰ سچا بادشاه ہے وه بڑی بلندی والا ہے ۔ اس کے سوا کوئی معبود نہیں، وہی بزرگ عرش کا مالک ہے. ');
INSERT INTO "junagarri" VALUES(23,117,'23:117','جو شخص اللہ کے ساتھ کسی دوسرے معبود کو پکارے جس کی کوئی دلیل اس کے پاس نہیں، پس اس کا حساب تو اس کے رب کے اوپر ہی ہے۔ بےشک کافر لوگ نجات سے محروم ہیں. ');
INSERT INTO "junagarri" VALUES(23,118,'23:118','اور کہو کہ اے میرے رب! تو بخش اور رحم کر اور تو سب مہربانوں سے بہتر مہربانی کرنے والا ہے.');
INSERT INTO "junagarri" VALUES(24,1,'24:1','یہ ہے وه سورت جو ہم نے نازل فرمائی ہے اور مقرر کردی ہے اور جس میں ہم نے کھلی آیتیں (احکام) اتارے ہیں تاکہ تم یاد رکھو.');
INSERT INTO "junagarri" VALUES(24,2,'24:2','زناکار عورت و مرد میں سے ہر ایک کو سو کوڑے لگاؤ ۔ ان پر اللہ کی شریعت کی حد جاری کرتے ہوئے تمہیں ہرگز ترس نہ کھانا چاہیئے، اگر تمہیں اللہ پر اور قیامت کے دن پر ایمان ہو ۔ ان کی سزا کے وقت مسلمانوں کی ایک جماعت موجود ہونی چاہیئے. ');
INSERT INTO "junagarri" VALUES(24,3,'24:3','زانی مرد بجز زانیہ یا مشرکہ عورت کے اور سے نکاح نہیں کرتا اور زناکار عورت بھی بجز زانی یا مشرک مرد کے اور سے نکاح نہیں کرتی اور ایمان والوں پر یہ حرام کردیا گیا. ');
INSERT INTO "junagarri" VALUES(24,4,'24:4','جو لوگ پاک دامن عورتوں پر زنا کی تہمت لگائیں پھر چار گواه نہ پیش کرسکیں تو انہیں اسی کوڑے لگاؤ اور کبھی بھی ان کی گواہی قبول نہ کرو۔ یہ فاسق لوگ ہیں. ');
INSERT INTO "junagarri" VALUES(24,5,'24:5','ہاں جو لوگ اس کے بعد توبہ اور اصلاح کرلیں تو اللہ تعالیٰ بخشنے والا اور مہربانی کرنے والا ہے.');
INSERT INTO "junagarri" VALUES(24,6,'24:6','جو لوگ اپنی بیویوں پر بدکاری کی تہمت لائیں اور ان کا کوئی گواه بجز خود ان کی ذات کے نہ ہو تو ایسے لوگوں میں سے ہر ایک کا ثبوت یہ ہے کہ چار مرتبہ اللہ کی قسم کھا کر کہیں کہ وه سچوں میں سے ہیں.');
INSERT INTO "junagarri" VALUES(24,7,'24:7','اور پانچویں مرتبہ کہے کہ اس پر اللہ تعالیٰ کی لعنت ہو اگر وه جھوٹوں میں سے ہو. ');
INSERT INTO "junagarri" VALUES(24,8,'24:8','اور اس عورت سے سزا اس طرح دور ہوسکتی ہے کہ وه چار مرتبہ اللہ کی قسم کھا کر کہے کہ یقیناً اس کا مرد جھوٹ بولنے والوں میں سے ہے.');
INSERT INTO "junagarri" VALUES(24,9,'24:9','اور پانچویں دفعہ کہے کہ اس پر اللہ تعالیٰ کا غضب ہو اگر اس کا خاوند سچوں میں سے ہو. ');
INSERT INTO "junagarri" VALUES(24,10,'24:10','اگر اللہ تعالیٰ کا فضل وکرم تم پر نہ ہوتا (تو تم پر مشقت اترتی) اور اللہ تعالیٰ توبہ قبول کرنے والا باحکمت ہے.');
INSERT INTO "junagarri" VALUES(24,11,'24:11','جو لوگ یہ بہت بڑا بہتان باندھ لائے ہیں یہ بھی تم میں سے ہی ایک گروه ہے۔ تم اسے اپنے لئے برا نہ سمجھو، بلکہ یہ تو تمہارے حق میں بہتر ہے ۔ ہاں ان میں سے ہر ایک شخص پر اتنا گناه ہے جتنا اس نے کمایا ہے اور ان میں سے جس نے اس کے بہت بڑے حصے کو سرانجام دیا ہے اس کے لئے عذاب بھی بہت ہی بڑا ہے. ');
INSERT INTO "junagarri" VALUES(24,12,'24:12','اسے سنتے ہی مومن مردوں عورتوں نے اپنے حق میں نیک گمانی کیوں نہ کی اور کیوں نہ کہہ دیا کہ یہ تو کھلم کھلا صریح بہتان ہے. ');
INSERT INTO "junagarri" VALUES(24,13,'24:13','وه اس پر چار گواه کیوں نہ لائے؟ اور جب گواه نہیں لائے تو یہ بہتان باز لوگ یقیناً اللہ کے نزدیک محض جھوٹے ہیں.');
INSERT INTO "junagarri" VALUES(24,14,'24:14','اگر اللہ تعالیٰ کا فضل وکرم تم پر دنیا اور آخرت میں نہ ہوتا تو یقیناً تم نے جس بات کے چرچے شروع کر رکھے تھے اس بارے میں تمہیں بہت بڑا عذاب پہنچتا.');
INSERT INTO "junagarri" VALUES(24,15,'24:15','جب کہ تم اسے اپنی زبانوں سے نقل در نقل کرنے لگے اور اپنے منھ سے وه بات نکالنے لگے جس کی تمہیں مطلق خبر نہ تھی، گو تم اسے ہلکی بات سمجھتے رہے لیکن اللہ تعالیٰ کے نزدیک وه بہت بڑی بات تھی.');
INSERT INTO "junagarri" VALUES(24,16,'24:16','تم نے ایسی بات کو سنتے ہی کیوں نہ کہہ دیا کہ ہمیں ایسی بات منھ سے نکالنی بھی لائق نہیں۔ یا اللہ! تو پاک ہے، یہ تو بہت بڑا بہتان ہے اور تہمت ہے. ');
INSERT INTO "junagarri" VALUES(24,17,'24:17','اللہ تعالیٰ تمہیں نصیحت کرتا ہے کہ پھر کبھی بھی ایسا کام نہ کرنا اگر تم سچے مومن ہو.');
INSERT INTO "junagarri" VALUES(24,18,'24:18','اللہ تعالیٰ تمہارے سامنے اپنی آیتیں بیان فرما رہا ہے، اور اللہ تعالیٰ علم و حکمت والا ہے.');
INSERT INTO "junagarri" VALUES(24,19,'24:19','جو لوگ مسلمانوں میں بےحیائی پھیلانے کے آرزومند رہتے ہیں ان کے لئے دنیا اور آخرت میں دردناک عذاب ہیں ، اللہ سب کچھ جانتا ہے اور تم کچھ بھی نہیں جانتے.');
INSERT INTO "junagarri" VALUES(24,20,'24:20','اگر تم پر اللہ کا فضل اور اس کی رحمت نہ ہوتی اور یہ بھی کہ اللہ تعالیٰ بڑی شفقت رکھنے والا مہربان ہے ۔ (تو تم پر عذاب اتر جاتا).');
INSERT INTO "junagarri" VALUES(24,21,'24:21','ایمان والو! شیطان کے قدم بقدم نہ چلو۔ جو شخص شیطانی قدموں کی پیروی کرے تو وه تو بےحیائی اور برے کاموں کا ہی حکم کرے گا۔ اور اگر اللہ تعالیٰ کا فضل وکرم تم پر نہ ہوتا تو تم میں سے کوئی کبھی بھی پاک صاف نہ ہوتا۔ لیکن اللہ تعالی جسے پاک کرنا چاہے، کر دیتا ہے ۔ اور اللہ سب سننے والا سب جاننے والا ہے.');
INSERT INTO "junagarri" VALUES(24,22,'24:22','تم میں سے جو بزرگی اور کشادگی والے ہیں انہیں اپنے قرابت داروں اور مسکینوں اور مہاجروں کو فی سبیل اللہ دینے سے قسم نہ کھا لینی چاہیئے، بلکہ معاف کردینا اور درگزر کرلینا چاہیئے۔ کیا تم نہیں چاہتے کہ اللہ تعالیٰ تمہارے قصور معاف فرما دے ؟ اللہ قصوروں کو معاف فرمانے والا مہربان ہے.');
INSERT INTO "junagarri" VALUES(24,23,'24:23','جو لوگ پاک دامن بھولی بھالی باایمان عورتوں پر تہمت لگاتے ہیں وه دنیا وآخرت میں ملعون ہیں اور ان کے لئے بڑا بھاری عذاب ہے. ');
INSERT INTO "junagarri" VALUES(24,24,'24:24','جب کہ ان کے مقابلے میں ان کی زبانیں اور ان کے ہاتھ پاؤں ان کے اعمال کی گواہی دیں گے. ');
INSERT INTO "junagarri" VALUES(24,25,'24:25','اس دن اللہ تعالیٰ انہیں پورا پورا بدلہ حق و انصاف کے ساتھ دے گا اور وه جان لیں گے کہ اللہ تعالیٰ ہی حق ہے (اور وہی) ظاہر کرنے والا ہے.');
INSERT INTO "junagarri" VALUES(24,26,'24:26','خبیث عورتیں خبیث مردوں کے لائق ہیں اور خبیث مرد خبیث عورتوں کے لائق ہیں اور پاک عورتیں پاک مردوں کے لائق ہیں اور پاک مرد پاک عورتوں کے لائق ہیں ۔ ایسے پاک لوگوں کے متعلق جو کچھ بکواس (بہتان باز) کر رہے ہیں وه ان سے بالکل بری ہیں، ان کے لئے بخشش ہے اور عزت والی روزی. ');
INSERT INTO "junagarri" VALUES(24,27,'24:27','اے ایمان والو! اپنے گھروں کے سوا اور گھروں میں نہ جاؤ جب تک کہ اجازت نہ لے لو اور وہاں کے رہنے والوں کو سلام نہ کرلو ، یہی تمہارے لئے سراسر بہتر ہے تاکہ تم نصیحت حاصل کرو. ');
INSERT INTO "junagarri" VALUES(24,28,'24:28','اگر وہاں تمہیں کوئی بھی نہ مل سکے تو پھر اجازت ملے بغیر اندر نہ جاؤ۔ اگر تم سے لوٹ جانے کو کہا جائے تو تم لوٹ ہی جاؤ، یہی بات تمہارے لئے پاکیزه ہے، جو کچھ تم کر رہے ہو اللہ تعالیٰ خوب جانتا ہے.');
INSERT INTO "junagarri" VALUES(24,29,'24:29','ہاں غیر آباد گھروں میں جہاں تمہارا کوئی فائده یا اسباب ہو، جانے میں تم پر کوئی گناه نہیں ۔ تم جو کچھ بھی ظاہر کرتے ہو اور جو چھپاتے ہو اللہ تعالیٰ سب کچھ جانتا ہے. ');
INSERT INTO "junagarri" VALUES(24,30,'24:30','مسلمان مردوں سے کہو کہ اپنی نگاہیں نیچی رکھیں ، اور اپنی شرمگاہوں کی حفاظت رکھیں ۔ یہی ان کے لئے پاکیزگی ہے، لوگ جو کچھ کریں اللہ تعالیٰ سب سے خبردار ہے.');
INSERT INTO "junagarri" VALUES(24,31,'24:31','مسلمان عورتوں سے کہو کہ وه بھی اپنی نگاہیں نیچی رکھیں اور اپنی عصمت میں فرق نہ آنے دیں اور اپنی زینت کو ظاہر نہ کریں ، سوائے اس کے جو ظاہر ہے اور اپنے گریبانوں پر اپنی اوڑھنیاں ڈالے رہیں ، اور اپنی آرائش کو کسی کے سامنے ظاہر نہ کریں ، سوائے اپنے خاوندوں کے یا اپنے والد کے یا اپنے خسر کے یا اپنے لڑکوں کے یا اپنے خاوند کے لڑکوں کے یا اپنے بھائیوں کے یا اپنے بھتیجوں کے یا اپنے بھانجوں کے یا اپنے میل جول کی عورتوں کے یا غلاموں کے یا ایسے نوکر چاکر مردوں کے جو شہوت والے نہ ہوں یا ایسے بچوں کے جو عورتوں کے پردے کی باتوں سے مطلع نہیں ۔ اور اس طرح زور زور سے پاؤں مار کر نہ چلیں کہ ان کی پوشیده زینت معلوم ہوجائے ، اے مسلمانو! تم سب کے سب اللہ کی جناب میں توبہ کرو تاکہ تم نجات پاؤ. ');
INSERT INTO "junagarri" VALUES(24,32,'24:32','تم میں سے جو مرد عورت بےنکاح کے ہوں ان کا نکاح کر دو اور اپنے نیک بخت غلام اور لونڈیوں کا بھی ۔ اگر وه مفلس بھی ہوں گے تو اللہ تعالیٰ انہیں اپنے فضل سے غنی بنا دے گا ۔ اللہ تعالیٰ کشادگی والا اور علم والا ہے.');
INSERT INTO "junagarri" VALUES(24,33,'24:33','اور ان لوگوں کو پاک دامن رہنا چاہیئے جو اپنا نکاح کرنے کا مقدور نہیں رکھتے یہاں تک کہ اللہ تعالیٰ انہیں اپنے فضل سے مالدار بنا دے، تمہارے غلاموں میں جو کوئی کچھ تمہیں دے کر آزادی کی تحریر کرانی چاہے تو تم ایسی تحریر انہیں کردیا کرو اگر تم کو ان میں کوئی بھلائی نظر آتی ہو ۔ اور اللہ نے جو مال تمہیں دے رکھا ہے اس میں سے انہیں بھی دو، تمہاری جو لونڈیاں پاک دامن رہنا چاہتی ہیں انہیں دنیا کی زندگی کے فائدے کی غرض سے بدکاری پر مجبور نہ کرو اور جو انہیں مجبور کردے تو اللہ تعالیٰ ان پر جبر کے بعد بخشش دینے والا اور مہربانی کرنے والا ہے. ');
INSERT INTO "junagarri" VALUES(24,34,'24:34','ہم نے تمہاری طرف کھلی اور روشن آیتیں اتار دی ہیں اور ان لوگوں کی کہاوتیں جو تم سے پہلے گزرچکے ہیں اور پرہیزگاروں کے لئے نصیحت.');
INSERT INTO "junagarri" VALUES(24,35,'24:35','اللہ نور ہے آسمانوں اور زمین کا ، اس کے نور کی مثال مثل ایک طاق کے ہے جس میں چراغ ہو اور چراغ شیشہ کی قندیل میں ہو اور شیشہ مثل چمکتے ہوئے روشن ستارے کے ہو وه چراغ ایک بابرکت درخت زیتون کے تیل سے جلایا جاتا ہو جو درخت نہ مشرقی ہے نہ مغربی خود وه تیل قریب ہے کہ آپ ہی روشنی دینے لگے اگرچہ اسے آگ نہ بھی چھوئے، نور پر نور ہے ، اللہ تعالیٰ اپنے نور کی طرف رہنمائی کرتا ہے جسے چاہے ، لوگوں (کے سمجھانے) کو یہ مثالیں اللہ تعالیٰ بیان فرما رہا ہے ، اور اللہ تعالیٰ ہر چیز کے حال سے بخوبی واقف ہے.');
INSERT INTO "junagarri" VALUES(24,36,'24:36','ان گھروں میں جن کے بلند کرنے، اور جن میں اپنے نام کی یاد کا اللہ تعالیٰ نے حکم دیا ہے وہاں صبح وشام اللہ تعالیٰ کی تسبیح بیان کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(24,37,'24:37','ایسے لوگ جنہیں تجارت اور خرید وفروخت اللہ کے ذکر سے اور نماز کے قائم کرنے اور زکوٰة ادا کرنے سے غافل نہیں کرتی اس دن سے ڈرتے ہیں جس دن بہت سے دل اور بہت سی آنکھیں الٹ پلٹ ہوجائیں گی. ');
INSERT INTO "junagarri" VALUES(24,38,'24:38','اس ارادے سے کہ اللہ انہیں ان کے اعمال کا بہترین بدلے دے بلکہ اپنے فضل سے اور کچھ زیادتی عطا فرمائے۔ اللہ تعالیٰ جسے چاہے بےشمار روزیاں دیتا ہے. ');
INSERT INTO "junagarri" VALUES(24,39,'24:39','اور کافروں کے اعمال مثل اس چمکتی ہوئی ریت کے ہیں جو چٹیل میدان میں ہو جسے پیاسا شخص دور سے پانی سمجھتا ہے لیکن جب اس کے پاس پہنچتا ہے تو اسے کچھ بھی نہیں پاتا، ہاں اللہ کو اپنے پاس پاتا ہے جو اس کا حساب پورا پورا چکا دیتا ہے ۔ اللہ بہت جلد حساب کردینے والا ہے.');
INSERT INTO "junagarri" VALUES(24,40,'24:40','یا مثل ان اندھیروں کے ہے جو نہایت گہرے سمندر کی تہہ میں ہوں جسے اوپر تلے کی موجوں نے ڈھانپ رکھا ہو، پھر اوپر سے بادل چھائے ہوئے ہوں۔ الغرض اندھیریاں ہیں جو اوپر تلے پےدرپے ہیں۔ جب اپنا ہاتھ نکالے تو اسے بھی قریب ہے کہ نہ دیکھ سکے ، اور (بات یہ ہے کہ) جسے اللہ تعالیٰ ہی نور نہ دے اس کے پاس کوئی روشنی نہیں ہوتی. ');
INSERT INTO "junagarri" VALUES(24,41,'24:41','کیا آپ نے نہیں دیکھا کہ آسمانوں اور زمین کی کل مخلوق اور پر پھیلائے اڑنے والے کل پرند اللہ کی تسبیح میں مشغول ہیں۔ ہر ایک کی نماز اور تسبیح اسے معلوم ہے ، لوگ جو کچھ کریں اس سے اللہ بخوبی واقف ہے. ');
INSERT INTO "junagarri" VALUES(24,42,'24:42','زمین و آسمان کی بادشاہت اللہ ہی کی ہے اور اللہ تعالیٰ ہی کی طرف لوٹنا ہے. ');
INSERT INTO "junagarri" VALUES(24,43,'24:43','کیا آپ نے نہیں دیکھا کہ اللہ تعالیٰ بادلوں کو چلاتا ہے، پھر انہیں ملاتا ہے پھر انہیں تہہ بہ تہہ کر دیتا ہے، پھر آپ دیکھتے ہیں کہ ان کے درمیان میں سے مینہ برستا ہے۔ وہی آسمان کی جانب سے اولوں کے پہاڑ میں سے اولے برساتا ہے ، پھر جنہیں چاہے ان کے پاس انہیں برسائے اور جن سے چاہے ان سے انہیں ہٹا دے ۔ بادل ہی سے نکلنے والی بجلی کی چمک ایسی ہوتی ہے کہ گویا اب آنکھوں کی روشنی لے چلی. ');
INSERT INTO "junagarri" VALUES(24,44,'24:44','اللہ تعالیٰ ہی دن اور رات کو ردوبدل کرتا رہتا ہے آنکھوں والوں کے لیے تو اس میں یقیناً بڑی بڑی عبرتیں ہیں.');
INSERT INTO "junagarri" VALUES(24,45,'24:45','تمام کے تمام چلنے پھرنے والے جانداروں کو اللہ تعالیٰ ہی نے پانی سے پیدا کیا ہےان میں سے بعض تو اپنے پیٹ کے بل چلتے ہیں ، بعض دو پاؤں پر چلتے ہیں ۔ بعض چار پاؤں پر چلتے ہیں ، اللہ تعالیٰ جو چاہتا ہے پیدا کرتا ہے ۔ بےشک اللہ تعالیٰ ہر چیز پر قادر ہے.');
INSERT INTO "junagarri" VALUES(24,46,'24:46','بلاشک و شبہ ہم نے روشن اور واضح آیتیں اتار دی ہیں اللہ تعالیٰ جسے چاہے سیدھی راه دکھا دیتا ہے. ');
INSERT INTO "junagarri" VALUES(24,47,'24:47','اور کہتے ہیں کہ ہم اللہ تعالیٰ اور رسول پر ایمان لائے اور فرماں بردار ہوئے، پھر ان میں سے ایک فرقہ اس کے بعد بھی پھر جاتا ہے۔ یہ ایمان والے ہیں (ہی) نہیں. ');
INSERT INTO "junagarri" VALUES(24,48,'24:48','جب یہ اس بات کی طرف بلائے جاتے ہیں کہ اللہ اور اس کا رسول ان کے جھگڑے چکا دے تو بھی ان کی ایک جماعت منھ موڑنے والی بن جاتی ہے.');
INSERT INTO "junagarri" VALUES(24,49,'24:49','ہاں اگر ان ہی کو حق پہنچتا ہو تو مطیع وفرماں بردار ہو کر اس کی طرف چلے آتے ہیں. ');
INSERT INTO "junagarri" VALUES(24,50,'24:50','کیا ان کے دلوں میں بیماری ہے؟ یا یہ شک وشبہ میں پڑے ہوئے ہیں؟ یا انہیں اس بات کا ڈر ہے کہ اللہ تعالیٰ اور اس کا رسول ان کی حق تلفی نہ کریں؟ بات یہ ہے کہ یہ لوگ خود ہی بڑے ظالم ہیں. ');
INSERT INTO "junagarri" VALUES(24,51,'24:51','ایمان والوں کا قول تو یہ ہے کہ جب انہیں اس لئے بلایا جاتا ہے کہ اللہ اور اس کا رسول ان میں فیصلہ کردے تو وه کہتے ہیں کہ ہم نے سنا اور مان لیا ۔ یہی لوگ کامیاب ہونے والے ہیں.');
INSERT INTO "junagarri" VALUES(24,52,'24:52','جو بھی اللہ تعالیٰ کی، اس کے رسول کی فرماں برداری کریں، خوف الٰہی رکھیں اور اس کے عذابوں سے ڈرتے رہیں، وہی نجات پانے والے ہیں. ');
INSERT INTO "junagarri" VALUES(24,53,'24:53','بڑی پختگی کے ساتھ اللہ تعالیٰ کی قسمیں کھا کھا کر کہتے ہیں کہ آپ کا حکم ہوتے ہی نکل کھڑے ہوں گے۔ کہہ دیجیئے کہ بس قسمیں نہ کھاؤ (تمہاری) اطاعت (کی حقیقت) معلوم ہے ۔ جو کچھ تم کر رہے ہو اللہ تعالیٰ اس سے باخبر ہے. ');
INSERT INTO "junagarri" VALUES(24,54,'24:54','کہہ دیجیئے کہ اللہ تعالیٰ کا حکم مانو، رسول اللہ کی اطاعت کرو، پھر بھی اگر تم نے روگردانی کی تو رسول کے ذمے تو صرف وہی ہے جو اس پر لازم کردیا گیا ہے اور تم پر اس کی جوابدہی ہے جو تم پر رکھا گیا ہے ہدایت تو تمہیں اسی وقت ملے گی جب رسول کی ماتحتی کرو ۔ سنو رسول کے ذمے تو صرف صاف طور پر پہنچا دینا ہے. ');
INSERT INTO "junagarri" VALUES(24,55,'24:55','تم میں سے ان لوگوں سے جو ایمان لائے ہیں اور نیک اعمال کئے ہیں اللہ تعالیٰ وعده فرما چکا ہے کہ انہیں ضرور زمین میں خلیفہ بنائے گا جیسے کہ ان لوگوں کو خلیفہ بنایا تھا جو ان سے پہلے تھے اور یقیناً ان کے لئے ان کے اس دین کو مضبوطی کے ساتھ محکم کرکے جما دے گا جسے ان کے لئے وه پسند فرما چکا ہے اور ان کے اس خوف وخطر کو وه امن وامان سے بدل دے گا ، وه میری عبادت کریں گے میرے ساتھ کسی کو بھی شریک نہ ٹھہرائیں گے ۔ اس کے بعد بھی جو لوگ ناشکری اور کفر کریں وه یقیناً فاسق ہیں. ');
INSERT INTO "junagarri" VALUES(24,56,'24:56','نماز کی پابندی کرو، زکوٰة ادا کرو اور اللہ تعالیٰ کے رسول کی فرمانبرداری میں لگے رہو تاکہ تم پر رحم کیا جائے. ');
INSERT INTO "junagarri" VALUES(24,57,'24:57','یہ خیال آپ کبھی بھی نہ کرنا کہ منکر لوگ زمین میں (ادھر ادھر بھاگ کر) ہمیں ہرا دینے والے ہیں ، ان کا اصلی ٹھکانا تو جہنم ہے جو یقیناً بہت ہی برا ٹھکانا ہے.');
INSERT INTO "junagarri" VALUES(24,58,'24:58','ایمان والو! تم سے تمہاری ملکیت کے غلاموں کو اور انہیں بھی جو تم میں سے بلوغت کو نہ پہنچے ہوں (اپنے آنے کی) تین وقتوں میں اجازت حاصل کرنی ضروری ہے۔ نماز فجر سے پہلے اور ظہر کے وقت جب کہ تم اپنے کپڑے اتار رکھتے ہو اور عشا کی نماز کے بعد ، یہ تینوں وقت تمہاری (خلوت) اور پرده کے ہیں ۔ ان وقتوں کے ماسوا نہ تو تم پر کوئی گناه ہے نہ ان پر ۔ تم سب آپس میں ایک دوسرے کے پاس بکثرت آنے جانے والے ہو (ہی)، اللہ اسی طرح کھول کھول کر اپنے احکام تم سے بیان فرما رہا ہے۔ اللہ تعالیٰ پورے علم اور کامل حکمت والا ہے.');
INSERT INTO "junagarri" VALUES(24,59,'24:59','اور تمہارے بچے (بھی) جب بلوغت کو پہنچ جائیں تو جس طرح ان کے اگلے لوگ اجازت مانگتے ہیں انہیں بھی اجازت مانگ کر آنا چاہیئے ، اللہ تعالیٰ تم سے اسی طرح اپنی آیتیں بیان فرماتا ہے۔ اللہ تعالیٰ ہی علم وحکمت والا ہے.');
INSERT INTO "junagarri" VALUES(24,60,'24:60','بڑی بوڑھی عورتیں جنہیں نکاح کی امید (اور خواہش ہی) نہ رہی ہو وه اگر اپنے کپڑے اتار رکھیں تو ان پر کوئی گناه نہیں بشرطیکہ وه اپنا بناؤ سنگھار ظاہر کرنے والیاں نہ ہوں ، تاہم اگر ان سے بھی احتیاط رکھیں تو ان کے لئے بہت افضل ہے ، اور اللہ تعالیٰ سنتا جانتا ہے.');
INSERT INTO "junagarri" VALUES(24,61,'24:61','اندھے پر، لنگڑے پر، بیمار پر اور خود تم پر (مطلقاً) کوئی حرج نہیں کہ تم اپنے گھروں سے کھالو یا اپنے باپوں کے گھروں سے یا اپنی ماؤں کے گھروں سے یا اپنے بھائیوں کے گھروں سے یا اپنی بہنوں کے گھروں سے یا اپنے چچاؤں کے گھروں سے یا اپنی پھوپھیوں کے گھروں سے یا اپنے ماموؤں کے گھروں سے یا اپنی خالاؤں کے گھروں سے یا ان گھروں سے جن کی کنجیوں کے تم مالک ہو یا اپنے دوستوں کے گھروں سے۔ تم پر اس میں بھی کوئی گناه نہیں کہ تم سب ساتھ بیٹھ کر کھانا کھاؤ یا الگ الگ پس جب تم گھروں میں جانے لگو تو اپنے گھر والوں کو سلام کرلیا کرو دعائے خیر ہے جو بابرکت اور پاکیزه ہے اللہ تعالیٰ کی طرف سے نازل شده، یوں ہی اللہ تعالیٰ کھول کھول کر تم سے اپنے احکام بیان فرما رہا ہے تاکہ تم سمجھ لو.');
INSERT INTO "junagarri" VALUES(24,62,'24:62','باایمان لوگ تو وہی ہیں جو اللہ تعالیٰ پر اور اس کے رسول پر یقین رکھتے ہیں اور جب ایسے معاملہ میں جس میں لوگوں کے جمع ہونے کی ضرورت ہوتی ہے نبی کے ساتھ ہوتے ہیں تو جب تک آپ سے اجازت نہ لیں کہیں نہیں جاتے۔ جو لوگ ایسے موقع پر آپ سے اجازت لے لیتے ہیں حقیقت میں یہی ہیں وه جو اللہ تعالیٰ پر اور اس کے رسول پر ایمان لاچکے ہیں ۔ پس جب ایسے لوگ آپ سے اپنے کسی کام کے لئے اجازت طلب کریں تو آپ ان میں سے جسے چاہیں اجازت دے دیں اور ان کے لئے اللہ تعالیٰ سے بخشش کی دعا مانگیں، بےشک اللہ بخشنے والا مہربان ہے.');
INSERT INTO "junagarri" VALUES(24,63,'24:63','تم اللہ تعالیٰ کے نبی کے بلانے کو ایسا بلاوا نہ کرلو جیسا کہ آپس میں ایک دوسرے کو ہوتا ہے۔ تم میں سے انہیں اللہ خوب جانتا ہے جو نظر بچا کر چپکے سے سرک جاتے ہیں ۔ سنو جو لوگ حکم رسول کی مخالفت کرتے ہیں انہیں ڈرتے رہنا چاہیئے کہ کہیں ان پر کوئی زبردست آفت نہ آ پڑے یا انہیں درد ناک عذاب نہ پہنچے.');
INSERT INTO "junagarri" VALUES(24,64,'24:64','آگاه ہو جاؤ کہ آسمان وزمین میں جو کچھ ہے سب اللہ تعالیٰ ہی کا ہے ۔ جس روش پر تم ہو وه اسے بخوبی جانتا ہے ، اور جس دن یہ سب اس کی طرف لوٹائے جائیں گے اس دن ان کو ان کے کئے سے وه خبردار کر دے گا۔ اللہ تعالیٰ سب کچھ جاننے والا ہے.');
INSERT INTO "junagarri" VALUES(25,1,'25:1','بہت بابرکت ہے وه اللہ تعالیٰ جس نے اپنے بندے پر فرقان اتارا تاکہ وه تمام لوگوں کے لئے آگاه کرنے والا بن جائے.');
INSERT INTO "junagarri" VALUES(25,2,'25:2','اسی اللہ کی سلطنت ہے آسمانوں اور زمین کی اور وه کوئی اولاد نہیں رکھتا ، نہ اس کی سلطنت میں کوئی اس کا ساجھی ہے اور ہر چیز کو اس نے پیدا کرکے ایک مناسب اندازه ٹھہرا دیا ہے. ');
INSERT INTO "junagarri" VALUES(25,3,'25:3','ان لوگوں نے اللہ کے سوا جنہیں اپنے معبود ٹھہرا رکھے ہیں وه کسی چیز کو پیدا نہیں کرسکتے بلکہ وه خود پیدا کئے جاتے ہیں، یہ تو اپنی جان کے نقصان نفع کا بھی اختیارنہیں رکھتے اور نہ موت وحیات کے اور نہ دوباره جی اٹھنے کے وه مالک ہیں. ');
INSERT INTO "junagarri" VALUES(25,4,'25:4','اور کافروں نے کہا یہ تو بس خود اسی کا گھڑا گھڑایا جھوٹ ہے جس پر اور لوگوں نے بھی اس کی مدد کی ہے، دراصل یہ کافر بڑے ہی ظلم اور سرتاسر جھوٹ کے مرتکب ہوئے ہیں.');
INSERT INTO "junagarri" VALUES(25,5,'25:5','اور یہ بھی کہا کہ یہ تو اگلوں کے افسانے ہیں جو اس نے لکھا رکھے ہیں بس وہی صبح وشام اس کے سامنے پڑھے جاتے ہیں.');
INSERT INTO "junagarri" VALUES(25,6,'25:6','کہہ دیجیئے کہ اسے تو اس اللہ نے اتارا ہے جو آسمان وزمین کی تمام پوشیده باتوں کو جانتا ہے ۔ بےشک وه بڑا ہی بخشنے والا مہربان ہے.');
INSERT INTO "junagarri" VALUES(25,7,'25:7','اور انہوں نے کہا کہ یہ کیسا رسول ہے؟ کہ کھانا کھاتا ہے اور بازاروں میں چلتا پھرتا ہے ، اس کے پاس کوئی فرشتہ کیوں نہیں بھیجا جاتا؟ کہ وه بھی اس کے ساتھ ہو کر ڈرانے والا بن جاتا. ');
INSERT INTO "junagarri" VALUES(25,8,'25:8','یا اس کے پاس کوئی خزانہ ہی ڈال دیا جاتا یا اس کا کوئی باغ ہی ہوتا جس میں سے یہ کھاتا ۔ اور ان ظالموں نے کہا کہ تم ایسے آدمی کے پیچھے ہو لئے ہو جس پر جادو کر دیا گیا ہے. ');
INSERT INTO "junagarri" VALUES(25,9,'25:9','خیال تو کیجیئے! کہ یہ لوگ آپ کی نسبت کیسی کیسی باتیں بناتے ہیں۔ پس جس سے وه خود ہی بہک رہے ہیں اور کسی طرح راه پر نہیں آسکتے. ');
INSERT INTO "junagarri" VALUES(25,10,'25:10','اللہ تعالیٰ تو ایسا بابرکت ہے کہ اگر چاہے تو آپ کو بہت سے ایسے باغات عنایت فرما دے جو ان کے کہے ہوئے باغ سے بہت ہی بہتر ہوں جن کے نیچے نہریں لہریں لے رہی ہوں اور آپ کو بہت سے (پختہ) محل بھی دے دے. ');
INSERT INTO "junagarri" VALUES(25,11,'25:11','بات یہ ہے کہ یہ لوگ قیامت کو جھوٹ سمجھتے ہیں اور قیامت کے جھٹلانے والوں کے لئے ہم نے بھڑکتی ہوئی آگ تیار کر رکھی ہے.');
INSERT INTO "junagarri" VALUES(25,12,'25:12','جب وه انہیں دور سے دیکھے گی تو یہ اس کا غصے سے بپھرنا اور دھاڑنا سنیں گے. ');
INSERT INTO "junagarri" VALUES(25,13,'25:13','اور جب یہ جہنم کی کسی تنگ جگہ میں مشکیں کس کر پھینک دیئے جائیں گے تو وہاں اپنے لئے موت ہی موت پکاریں گے.');
INSERT INTO "junagarri" VALUES(25,14,'25:14','(ان سے کہا جائے گا) آج ایک ہی موت کو نہ پکارو بلکہ بہت سی اموات کو پکارو. ');
INSERT INTO "junagarri" VALUES(25,15,'25:15','آپ کہہ دیجیئے کہ کیا یہ بہتر ہے یا وه ہمیشگی والی جنت جس کا وعده پرہیزگاروں سے کیا گیا ہے، جو ان کا بدلہ ہے اور ان کے لوٹنے کی اصلی جگہ ہے.');
INSERT INTO "junagarri" VALUES(25,16,'25:16','وه جو چاہیں گے ان کے لئے وہاں موجود ہوگا، ہمیشہ رہنے والے۔ یہ تو آپ کے رب کے ذمے وعده ہے جو قابل طلب ہے. ');
INSERT INTO "junagarri" VALUES(25,17,'25:17','اور جس دن اللہ تعالیٰ انہیں اور سوائے اللہ کے جنہیں یہ پوجتے رہے، انہیں جمع کرکے پوچھے گا کہ کیا میرے ان بندوں کو تم نے گمراه کیا یا یہ خود ہی راه سے گم ہوگئے. ');
INSERT INTO "junagarri" VALUES(25,18,'25:18','وه جواب دیں گے کہ تو پاک ذات ہے خود ہمیں ہی یہ زیبا نہ تھا کہ تیرے سوا اوروں کو اپنا کارساز بناتے بات یہ ہے کہ تو نے انہیں اور ان کے باپ دادوں کو آسودگیاں عطا فرمائیں یہاں تک کہ وه نصیحت بھلا بیٹھے، یہ لوگ تھے ہی ہلاک ہونے والے.');
INSERT INTO "junagarri" VALUES(25,19,'25:19','تو انہوں نے تو تمہیں تمہاری تمام باتوں میں جھٹلایا، اب نہ تو تم میں عذابوں کے پھیرنے کی طاقت ہے، نہ مدد کرنے کی ، تم میں سے جس جس نے ظلم کیا ہے ہم اسے بڑا عذاب چکھائیں گے.');
INSERT INTO "junagarri" VALUES(25,20,'25:20','ہم نے آپ سے پہلے جتنے رسول بھیجے سب کے سب کھانا بھی کھاتے تھے اور بازاروں میں بھی چلتے پھرتے تھے اور ہم نے تم میں سے ہر ایک کو دوسرے کی آزمائش کا ذریعہ بنا دیا ۔ کیا تم صبر کرو گے؟ تیرا رب سب کچھ دیکھنے والا ہے. ');
INSERT INTO "junagarri" VALUES(25,21,'25:21','اور جنہیں ہماری ملاقات کی توقع نہیں انہوں نے کہا کہ ہم پر فرشتے کیوں نہیں اتارے جاتے ؟ یا ہم اپنی آنکھوں سے اپنے رب کو دیکھ لیتے ؟ ان لوگوں نے اپنے آپ کو ہی بہت بڑا سمجھ رکھا ہے اور سخت سرکشی کرلی ہے. ');
INSERT INTO "junagarri" VALUES(25,22,'25:22','جس دن یہ فرشتوں کو دیکھ لیں گے اس دن ان گناه گاروں کو کوئی خوشی نہ ہوگی اور کہیں گے یہ محروم ہی محروم کیے گئے. ');
INSERT INTO "junagarri" VALUES(25,23,'25:23','اور انہوں نے جو جو اعمال کیے تھے ہم نےان کی طرف بڑھ کر انہیں پراگنده ذروں کی طرح کردیا. ');
INSERT INTO "junagarri" VALUES(25,24,'25:24','البتہ اس دن جنتیوں کا ٹھکانا بہتر ہوگا اور خواب گاه بھی عمده ہوگی. ');
INSERT INTO "junagarri" VALUES(25,25,'25:25','اور جس دن آسمان بادل سمیت پھٹ جائے گا اور فرشتے لگاتار اتارے جائیں گے.');
INSERT INTO "junagarri" VALUES(25,26,'25:26','اس دن صحیح طور پر ملک صرف رحمٰن کا ہی ہوگا اور یہ دن کافروں پر بڑا بھاری ہوگا.');
INSERT INTO "junagarri" VALUES(25,27,'25:27','اور اس دن ظالم شخص اپنے ہاتھوں کو چبا چبا کر کہے گا ہائے کاش کہ میں نے رسول (صلی اللہ علیہ وسلم) کی راه اختیار کی ہوتی.');
INSERT INTO "junagarri" VALUES(25,28,'25:28','ہائے افسوس کاش کہ میں نے فلاں کو دوست نہ بنایا ہوتا. ');
INSERT INTO "junagarri" VALUES(25,29,'25:29','اس نے تو مجھے اس کے بعد گمراه کردیا کہ نصیحت میرے پاس آپہنچی تھی اور شیطان تو انسان کو (وقت پر) دغا دینے والا ہے.');
INSERT INTO "junagarri" VALUES(25,30,'25:30','اور رسول کہے گا کہ اے میرے پروردگار! بےشک میری امت نے اس قرآن کو چھوڑ رکھا تھا. ');
INSERT INTO "junagarri" VALUES(25,31,'25:31','اور اسی طرح ہم نے ہر نبی کے دشمن بعض گناه گاروں کو بنادیا ہے اور تیرا رب ہی ہدایت کرنے والا اور مدد کرنے والا کافی ہے. ');
INSERT INTO "junagarri" VALUES(25,32,'25:32','اور کافروں نے کہا کہ اس پر قرآن سارا کا سارا ایک ساتھ ہی کیوں نہ اتارا گیا اسی طرح ہم نے (تھوڑا تھوڑا کرکے) اتارا تاکہ اس سے ہم آپ کا دل قوی رکھیں، ہم نے اسے ٹھہر ٹھہر کر ہی پڑھ سنایا ہے. ');
INSERT INTO "junagarri" VALUES(25,33,'25:33','یہ آپ کے پاس جو کوئی مثال لائیں گے ہم اس کا سچا جواب اور عمده توجیہ آپ کو بتادیں گے. ');
INSERT INTO "junagarri" VALUES(25,34,'25:34','جو لوگ اپنے منھ کے بل جہنم کی طرف جمع کیے جائیں گے۔ وہی بدتر مکان والے اور گمراه تر راستے والے ہیں.');
INSERT INTO "junagarri" VALUES(25,35,'25:35','اور بلاشبہ ہم نے موسیٰ کو کتاب دی اور ان کے ہمراه ان کے بھائی ہارون کو ان کا وزیر بنادیا.');
INSERT INTO "junagarri" VALUES(25,36,'25:36','اور کہہ دیا کہ تم دونوں ان لوگوں کی طرف جاؤ جو ہماری آیتوں کو جھٹلا رہے ہیں۔ پھر ہم نے انہیں بالکل ہی پامال کردیا.');
INSERT INTO "junagarri" VALUES(25,37,'25:37','اور قوم نوح نے بھی جب رسولوں کو جھوٹا کہا تو ہم نے انہیں غرق کردیا اور لوگوں کے لیے انہیں نشان عبرت بنا دیا۔ اور ہم نے ظالموں کے لیے دردناک عذاب مہیا کر رکھا ہے.');
INSERT INTO "junagarri" VALUES(25,38,'25:38','اور عادیوں اور ثمودیوں اور کنوئیں والوں کو اور ان کے درمیان کی بہت سی امتوں کو (ہلاک کردیا).');
INSERT INTO "junagarri" VALUES(25,39,'25:39','اور ہم نے ان کے سامنے مثالیں بیان کیں پھر ہر ایک کو بالکل ہی تباه و برباد کردیا. ');
INSERT INTO "junagarri" VALUES(25,40,'25:40','یہ لوگ اس بستی کے پاس سے بھی آتے جاتے ہیں جن پر بری طرح کی بارش برسائی گئی ۔ کیا یہ پھر بھی اسے دیکھتے نہیں؟ حقیقت یہ ہے کہ انہیں مرکر جی اٹھنے کی امید ہی نہیں. ');
INSERT INTO "junagarri" VALUES(25,41,'25:41','اور تمہیں جب کبھی دیکھتے ہیں تو تم سے مسخرا پن کرنے لگتے ہیں۔ کہ کیا یہی وه شخص ہیں جنہیں اللہ تعالیٰ نے رسول بنا کر بھیجا ہے. ');
INSERT INTO "junagarri" VALUES(25,42,'25:42','(وه تو کہیئے) کہ ہم اس پر جمے رہے ورنہ انہوں نے تو ہمیں ہمارے معبودوں سے بہکادینے میں کوئی کسر نہیں چھوڑی تھی ۔ اور یہ جب عذابوں کو دیکھیں گے تو انہیں صاف معلوم ہوجائے گا کہ پوری طرح راه سے بھٹکا ہوا کون تھا؟ ');
INSERT INTO "junagarri" VALUES(25,43,'25:43','کیا آپ نے اسے بھی دیکھا جو اپنی خواہش نفس کو اپنا معبود بنائے ہوئے ہے کیا آپ اس کے ذمہدار ہوسکتے ہیں؟ ');
INSERT INTO "junagarri" VALUES(25,44,'25:44','کیا آپ اسی خیال میں ہیں کہ ان میں سے اکثر سنتے یا سمجھتے ہیں۔ وه تو نرے چوپایوں جیسے ہیں بلکہ ان سے بھی زیاده بھٹکے ہوئے. ');
INSERT INTO "junagarri" VALUES(25,45,'25:45','کیا آپ نے نہیں دیکھا کہ آپ کے رب نے سائے کو کس طرح پھیلا دیا ہے ؟ اگر چاہتا تو اسے ٹھہرا ہوا ہی کر دیتا ۔ پھر ہم نے آفتاب کو اس پر دلیل بنایا. ');
INSERT INTO "junagarri" VALUES(25,46,'25:46','پھر ہم نے اسے آہستہ آہستہ اپنی طرف کھینچ لیا. ');
INSERT INTO "junagarri" VALUES(25,47,'25:47','اور وہی ہے جس نے رات کو تمہارے لیے پرده بنایا ۔ اور نیند کو راحت بنائی ۔ اور دن کو اٹھ کھڑے ہونے کا وقت. ');
INSERT INTO "junagarri" VALUES(25,48,'25:48','اور وہی ہے جو باران رحمت سے پہلے خوش خبری دینے والی ہواؤں کو بھیجتا ہے اور ہم آسمان سے پاک پانی برساتے ہیں. ');
INSERT INTO "junagarri" VALUES(25,49,'25:49','تاکہ اس کے ذریعے سے مرده شہر کو زنده کردیں اوراسے ہم اپنی مخلوقات میں سے بہت سے چوپایوں اور انسانوں کو پلاتے ہیں.');
INSERT INTO "junagarri" VALUES(25,50,'25:50','اور بے شک ہم نے اسےان کے درمیان طرح طرح سے بیان کیا تاکہ وه نصیحت حاصل کریں، مگر پھر بھی اکثر لوگوں نے سوائے ناشکری کے مانا نہیں. ');
INSERT INTO "junagarri" VALUES(25,51,'25:51','اگر ہم چاہتے تو ہر بستی میں ایک ڈرانے والا بھیج دیتے.');
INSERT INTO "junagarri" VALUES(25,52,'25:52','پس آپ کافروں کا کہنا نہ مانیں اور قرآن کے ذریعے ان سے پوری طاقت سے بڑا جہاد کریں. ');
INSERT INTO "junagarri" VALUES(25,53,'25:53','اور وہی ہے جس نے دو سمندر آپس میں ملا رکھے ہیں، یہ ہے میٹھا اور مزیدار اور یہ ہے کھاری کڑوا ، اور ان دونوں کے درمیان ایک حجاب اور مضبوط اوٹ کردی. ');
INSERT INTO "junagarri" VALUES(25,54,'25:54','وه ہے جس نے پانی سے انسان کو پیدا کیا، پھر اسے نسب والا اور سسرالی رشتوں والا کردیا ۔ بلاشبہ آپ کا پروردگار (ہر چیز پر) قادر ہے.');
INSERT INTO "junagarri" VALUES(25,55,'25:55','یہ اللہ کو چھوڑ کر ان کی عبادت کرتے ہیں جو نہ تو انہیں کوئی نفع دے سکیں نہ کوئی نقصان پہنچاسکیں، اور کافر تو ہے ہی اپنے رب کے خلاف (شیطان کی) مدد کرنے والا.');
INSERT INTO "junagarri" VALUES(25,56,'25:56','ہم نے تو آپ کو خوشخبری اور ڈر سنانے والا (نبی) بنا کر بھیجا ہے.');
INSERT INTO "junagarri" VALUES(25,57,'25:57','کہہ دیجئے کہ میں قرآن کے پہنچانے پر تم سے کوئی بدلہ نہیں چاہتا مگر جو شخص اپنے رب کی طرف راه پکڑنا چاہے. ');
INSERT INTO "junagarri" VALUES(25,58,'25:58','اس ہمیشہ زنده رہنے والے اللہ تعالیٰ پر توکل کریں جسے کبھی موت نہیں اور اس کی تعریف کے ساتھ پاکیزگی بیان کرتے رہیں، وه اپنے بندوں کے گناہوں سے کافی خبردار ہے.');
INSERT INTO "junagarri" VALUES(25,59,'25:59','وہی ہے جس نے آسمانوں اور زمین اوران کے درمیان کی سب چیزوں کو چھ دن میں پیدا کردیا ہے، پھر عرش پر مستوی ہوا، وه رحمٰن ہے، آپ اس کے بارے میں کسی خبردار سے پوچھ لیں.');
INSERT INTO "junagarri" VALUES(25,60,'25:60','ان سے جب بھی کہا جاتا ہے کہ رحمٰن کو سجده کرو تو جواب دیتے ہیں رحمٰن ہے کیا؟ کیا ہم اسے سجده کریں جس کا تو ہمیں حکم دے رہا ہے اور اس (تبلیغ) نےان کی نفرت میں مزید اضافہ کردیا. ');
INSERT INTO "junagarri" VALUES(25,61,'25:61','بابرکت ہے وه جس نے آسمان میں برج بنائے اور اس میں آفتاب بنایا اور منور مہتاب بھی.');
INSERT INTO "junagarri" VALUES(25,62,'25:62','اور اسی نے رات اور دن کو ایک دوسرے کے پیچھے آنے جانے والا بنایا ۔ اس شخص کی نصیحت کے لیے جو نصیحت حاصل کرنے یا شکر گزاری کرنے کا اراده رکھتا ہو.');
INSERT INTO "junagarri" VALUES(25,63,'25:63','رحمٰن کے (سچے) بندے وه ہیں جو زمین پر فروتنی کے ساتھ چلتے ہیں اور جب بے علم لوگ ان سے باتیں کرنے لگتے ہیں تو وه کہہ دیتے ہیں کہ سلام ہے. ');
INSERT INTO "junagarri" VALUES(25,64,'25:64','اور جو اپنے رب کے سامنے سجدے اور قیام کرتے ہوئے راتیں گزار دیتے ہیں.');
INSERT INTO "junagarri" VALUES(25,65,'25:65','اور جو یہ دعا کرتے ہیں کہ اے ہمارے پروردگار! ہم سے دوزخ کا عذاب پرے ہی پرے رکھ، کیونکہ اس کا عذاب چمٹ جانے والا ہے. ');
INSERT INTO "junagarri" VALUES(25,66,'25:66','بے شک وه ٹھہرنے اور رہنے کے لحاظ سے بدترین جگہ ہے.');
INSERT INTO "junagarri" VALUES(25,67,'25:67','اور جو خرچ کرتے وقت بھی نہ تو اسراف کرتے ہیں نہ بخیلی، بلکہ ان دونوں کے درمیان معتدل طریقے پر خرچ کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(25,68,'25:68','اور اللہ کے ساتھ کسی دوسرے معبود کو نہیں پکارتے اور کسی ایسے شخص کو جسے قتل کرنا اللہ تعالیٰ نے منع کردیا ہو وه بجز حق کے قتل نہیں کرتے ، نہ وه زنا کے مرتکب ہوتے ہیں اور جو کوئی یہ کام کرے وه اپنے اوپر سخت وبال لائے گا.');
INSERT INTO "junagarri" VALUES(25,69,'25:69','اسے قیامت کے دن دوہرا عذاب کیا جائے گا اور وه ذلت و خواری کے ساتھ ہمیشہ اسی میں رہے گا.');
INSERT INTO "junagarri" VALUES(25,70,'25:70','سوائے ان لوگوں کے جو توبہ کریں اور ایمان لائیں اور نیک کام کریں ، ایسے لوگوں کے گناہوں کو اللہ تعالیٰ نیکیوں سے بدل دیتا ہے ، اللہ بخشنے والا مہربانی کرنے والا ہے.');
INSERT INTO "junagarri" VALUES(25,71,'25:71','اور جو شخص توبہ کرے اور نیک عمل کرے وه تو (حقیقتاً) اللہ تعالیٰ کی طرف سچا رجوع کرتا ہے. ');
INSERT INTO "junagarri" VALUES(25,72,'25:72','اور جو لوگ جھوٹی گواہی نہیں دیتے اور جب کسی لغو چیز پر ان کا گزر ہوتا ہے تو شرافت سے گزر جاتے ہیں. ');
INSERT INTO "junagarri" VALUES(25,73,'25:73','اور جب انہیں ان کے رب کے کلام کی آیتیں سنائی جاتی ہیں تو وه اندھے بہرے ہو کر ان پر نہیں گرتے۔. ');
INSERT INTO "junagarri" VALUES(25,74,'25:74','اور یہ دعا کرتے ہیں کہ اے ہمارے پروردگار! تو ہمیں ہماری بیویوں اور اولاد سے آنکھوں کی ٹھنڈک عطا فرما اور ہمیں پرہیزگاروں کا پیشوا بنا. ');
INSERT INTO "junagarri" VALUES(25,75,'25:75','یہی وه لوگ ہیں جنہیں ان کے صبر کے بدلے جنت کے بلند و بالاخانے دیئے جائیں گے جہاں انہیں دعا سلام پہنچایا جائے گا.');
INSERT INTO "junagarri" VALUES(25,76,'25:76','اس میں یہ ہمیشہ رہیں گے، وه بہت ہی اچھی جگہ اور عمده مقام ہے.');
INSERT INTO "junagarri" VALUES(25,77,'25:77','کہہ دیجئے! اگر تمہاری دعا التجا (پکارنا) نہ ہوتی تو میرا رب تمہاری مطلق پروا نہ کرتا ، تم تو جھٹلا چکے اب عنقریب اس کی سزا تمہیں چمٹ جانے والی ہوگی. ');
INSERT INTO "junagarri" VALUES(26,1,'26:1','طٰسم.');
INSERT INTO "junagarri" VALUES(26,2,'26:2','یہ آیتیں روشن کتاب کی ہیں.*');
INSERT INTO "junagarri" VALUES(26,3,'26:3','ان کے ایمان نہ لانے پر شاید آپ تو اپنی جان کھودیں گے. ');
INSERT INTO "junagarri" VALUES(26,4,'26:4','اگر ہم چاہتے تو ان پر آسمان سے کوئی ایسی نشانی اتارتے کہ جس کے سامنے ان کی گردنیں خم ہو جاتیں. ');
INSERT INTO "junagarri" VALUES(26,5,'26:5','اور ان کے پاس رحمٰن کی طرف سے جو بھی نئی نصیحت آئی یہ اس سے روگردانی کرنے والے بن گئے.');
INSERT INTO "junagarri" VALUES(26,6,'26:6','ان لوگوں نے جھٹلایا ہے اب ان کے پاس جلدی سے اس کی خبریں آجائیں گی جس کے ساتھ وه مسخرا پن کر رہے ہیں. ');
INSERT INTO "junagarri" VALUES(26,7,'26:7','کیا انہوں نے زمین پر نظریں نہیں ڈالیں؟ کہ ہم نے اس میں ہر طرح کے نفیس جوڑے کس قدر اگائے ہیں؟ ');
INSERT INTO "junagarri" VALUES(26,8,'26:8','بے شک اس میں یقیناً نشانی ہے اور ان میں کے اکثر لوگ مومن نہیں ہیں. ');
INSERT INTO "junagarri" VALUES(26,9,'26:9','اور تیرا رب یقیناً وہی غالب اور مہربان ہے. ');
INSERT INTO "junagarri" VALUES(26,10,'26:10','اور جب آپ کے رب نے موسیٰ (علیہ السلام) کو آواز دی کہ تو ظالم قوم کے پاس جا. ');
INSERT INTO "junagarri" VALUES(26,11,'26:11','قوم فرعون کے پاس، کیا وه پرہیزگاری نہ کریں گے.');
INSERT INTO "junagarri" VALUES(26,12,'26:12','موسیٰ (علیہ السلام) نے کہا میرے پروردگار! مجھے تو خوف ہے کہ کہیں وه مجھے جھٹلا (نہ) دیں.');
INSERT INTO "junagarri" VALUES(26,13,'26:13','اور میرا سینہ تنگ ہو رہا ہے میری زبان چل نہیں رہی پس تو ہارون کی طرف بھی وحی بھیج. ');
INSERT INTO "junagarri" VALUES(26,14,'26:14','اور ان کا مجھ پر میرے ایک قصور کا (دعویٰ) بھی ہے مجھے ڈر ہے کہ کہیں وه مجھے مار نہ ڈالیں. ');
INSERT INTO "junagarri" VALUES(26,15,'26:15','جناب باری نے فرمایا! ہرگز ایسا نہ ہوگا، تم دونوں ہماری نشانیاں لے کر جاؤ ہم خود سننے والے تمہارے ساتھ ہیں. ');
INSERT INTO "junagarri" VALUES(26,16,'26:16','تم دونوں فرعون کے پاس جاکر کہو کہ بلاشبہ ہم رب العالمین کے بھیجے ہوئے ہیں.');
INSERT INTO "junagarri" VALUES(26,17,'26:17','کہ تو ہمارے ساتھ بنی اسرائیل کو روانہ کردے. ');
INSERT INTO "junagarri" VALUES(26,18,'26:18','فرعون نے کہا کہ کیا ہم نے تجھے تیرے بچپن کے زمانہ میں اپنے ہاں نہیں پالا تھا؟ اور تو نے اپنی عمر کے بہت سے سال ہم میں نہیں گزارے؟ ');
INSERT INTO "junagarri" VALUES(26,19,'26:19','پھر تو اپنا وه کام کر گیا جو کر گیا اور تو ناشکروں میں ہے. ');
INSERT INTO "junagarri" VALUES(26,20,'26:20','(حضرت) موسیٰ (علیہ السلام) نے جواب دیا کہ میں نے اس کام کو اس وقت کیا تھا جبکہ میں راه بھولے ہوئے لوگوں میں سے تھا. ');
INSERT INTO "junagarri" VALUES(26,21,'26:21','پھر تم سے خوف کھا کر میں تم میں سے بھاگ گیا، پھر مجھے میرے رب نے حکم و علم عطا فرمایا اور مجھے اپنے پیغمبروں میں سے کر دیا. ');
INSERT INTO "junagarri" VALUES(26,22,'26:22','مجھ پر تیرا کیا یہی وه احسان ہے؟ جسے تو جتا رہا ہے جبکہ تو نے بنی اسرائیل کو غلام بنا رکھا ہے. ');
INSERT INTO "junagarri" VALUES(26,23,'26:23','فرعون نے کہا رب العالمین کیا (چیز) ہے؟ ');
INSERT INTO "junagarri" VALUES(26,24,'26:24','(حضرت) موسیٰ (علیہ السلام) نے فرمایا وه آسمانوں اور زمین اور ان کے درمیان کی تمام چیزوں کا رب ہے، اگر تم یقین رکھنے والے ہو.');
INSERT INTO "junagarri" VALUES(26,25,'26:25','فرعون نے اپنے اردگرد والوں سے کہا کہ کیا تم سن نہیں رہے؟ ');
INSERT INTO "junagarri" VALUES(26,26,'26:26','(حضرت) موسیٰ (علیہ السلام) نے فرمایا وه تمہارا اور تمہارے اگلے باپ دادوں کا پروردگار ہے.');
INSERT INTO "junagarri" VALUES(26,27,'26:27','فرعون نے کہا (لوگو!) تمہارا یہ رسول جو تمہاری طرف بھیجا گیا ہے یہ تو یقیناً دیوانہ ہے.');
INSERT INTO "junagarri" VALUES(26,28,'26:28','حضرت موسیٰ علیہ السلام نے فرمایا! وہی مشرق ومغرب کا اور ان کے درمیان کی تمام چیزوں کا رب ہے، اگر تم عقل رکھتے ہو.');
INSERT INTO "junagarri" VALUES(26,29,'26:29','فرعون کہنے لگا سن لے! اگر تو نے میرے سوا کسی اور کو معبود بنایا تو میں تجھے قیدیوں میں ڈال دوں گا. ');
INSERT INTO "junagarri" VALUES(26,30,'26:30','موسیٰ (علیہ السلام) نے کہا اگرچہ میں تیرے پاس کوئی کھلی چیز لے آؤں؟ ');
INSERT INTO "junagarri" VALUES(26,31,'26:31','فرعون نے کہا اگر تو سچوں میں سے ہے تو اسے پیش کر.');
INSERT INTO "junagarri" VALUES(26,32,'26:32','آپ نے (اسی وقت) اپنی لاٹھی ڈال دی جو اچانک کھلم کھلا (زبردست) اژدہا بن گئی. ');
INSERT INTO "junagarri" VALUES(26,33,'26:33','اور اپنا ہاتھ کھینچ نکالا تو وه بھی اسی وقت ہر دیکھنے والے کو سفید چمکیلا نظر آنے لگا. ');
INSERT INTO "junagarri" VALUES(26,34,'26:34','فرعون اپنے آس پاس کے سرداروں سے کہنے لگا بھئی یہ تو کوئی بڑا دانا جادوگر ہے. ');
INSERT INTO "junagarri" VALUES(26,35,'26:35','یہ تو چاہتا ہے کہ اپنے جادو کے زور سے تمہیں تمہاری سر زمین سے ہی نکال دے، بتاؤ اب تم کیا حکم دیتے ہو. ');
INSERT INTO "junagarri" VALUES(26,36,'26:36','ان سب نے کہا آپ اسے اور اس کے بھائی کو مہلت دیجئے اور تمام شہروں میں ہرکارے بھیج دیجئے.');
INSERT INTO "junagarri" VALUES(26,37,'26:37','جو آپ کے پاس ذی علم جادو گروں کو لے آئیں. ');
INSERT INTO "junagarri" VALUES(26,38,'26:38','پھر ایک مقرر دن کے وعدے پر تمام جادوگر جمع کیے گئے. ');
INSERT INTO "junagarri" VALUES(26,39,'26:39','اور عام لوگوں سے بھی کہہ دیا گیا کہ تم بھی مجمع میں حاضر ہوجاؤ گے؟ ');
INSERT INTO "junagarri" VALUES(26,40,'26:40','تاکہ اگر جادوگر غالب آجائیں تو ہم ان ہی کی پیروی کریں.');
INSERT INTO "junagarri" VALUES(26,41,'26:41','جادوگر آکر فرعون سے کہنے لگے کہ اگر ہم جیت گئے تو ہمیں کچھ انعام بھی ملے گا؟');
INSERT INTO "junagarri" VALUES(26,42,'26:42','فرعون نے کہا ہاں! (بڑی خوشی سے) بلکہ ایسی صورت میں تم میرے خاص درباری بن جاؤ گے.');
INSERT INTO "junagarri" VALUES(26,43,'26:43','(حضرت) موسیٰ (علیہ السلام) نے جادوگروں سے فرمایا جو کچھ تمہیں ڈالنا ہے ڈال دو. ');
INSERT INTO "junagarri" VALUES(26,44,'26:44','انہوں نے اپنی رسیاں اور لاٹھیاں ڈال دیں اور کہنے لگے عزت فرعون کی قسم! ہم یقیناً غالب ہی رہیں گے. ');
INSERT INTO "junagarri" VALUES(26,45,'26:45','اب (حضرت) موسیٰ (علیہ السلام) نے بھی اپنی لاٹھی میدان میں ڈال دی جس نے اسی وقت ان کے جھوٹ موٹ کے کرتب کو نگلنا شروع کردیا.');
INSERT INTO "junagarri" VALUES(26,46,'26:46','یہ دیکھتے ہی دیکھتے جادوگر بے اختیار سجدے میں گر گئے.');
INSERT INTO "junagarri" VALUES(26,47,'26:47','اورانہوں نے صاف کہہ دیا کہ ہم تو اللہ رب العالمین پر ایمان لائے.');
INSERT INTO "junagarri" VALUES(26,48,'26:48','یعنی موسیٰ (علیہ السلام) اور ہارون کے رب پر.');
INSERT INTO "junagarri" VALUES(26,49,'26:49','فرعون نے کہا کہ میری اجازت سے پہلے تم اس پر ایمان لے آئے؟ یقیناً یہی تمہارا وه بڑا (سردار) ہے جس نے تم سب کو جادو سکھایا ہے ، سو تمہیں ابھی ابھی معلوم ہوجائے گا، قسم ہے میں ابھی تمہارے ہاتھ پاؤں الٹے طور پر کاٹ دوں گا اور تم سب کو سولی پر لٹکا دوں گا. ');
INSERT INTO "junagarri" VALUES(26,50,'26:50','انہوں نے کہا کوئی حرج نہیں ، ہم تو اپنے رب کی طرف لوٹنے والے ہیں ہی.');
INSERT INTO "junagarri" VALUES(26,51,'26:51','اس بنا پر کہ ہم سب سے پہلےایمان والے بنے ہیں ہمیں امید پڑتی ہے کہ ہمارا رب ہماری سب خطائیں معاف فرما دے گا.');
INSERT INTO "junagarri" VALUES(26,52,'26:52','اور ہم نے موسیٰ کو وحی کی کہ راتوں رات میرے بندوں کو نکال لے چل تم سب پیچھا کیے جاؤ گے. ');
INSERT INTO "junagarri" VALUES(26,53,'26:53','فرعون نے شہروں میں ہرکاروں کو بھیج دیا.');
INSERT INTO "junagarri" VALUES(26,54,'26:54','کہ یقیناً یہ گروه بہت ہی کم تعداد میں ہے. ');
INSERT INTO "junagarri" VALUES(26,55,'26:55','اور اس پر یہ ہمیں سخت غضب ناک کر رہے ہیں. ');
INSERT INTO "junagarri" VALUES(26,56,'26:56','اور یقیناً ہم بڑی جماعت ہیں ان سے چوکنا رہنے والے. ');
INSERT INTO "junagarri" VALUES(26,57,'26:57','بالآخر ہم نےانہیں باغات سے اور چشموں سے.');
INSERT INTO "junagarri" VALUES(26,58,'26:58','اور خزانوں سے۔ اور اچھے اچھے مقامات سے نکال باہر کیا. ');
INSERT INTO "junagarri" VALUES(26,59,'26:59','اسی طرح ہوا اور ہم نےان (تمام) چیزوں کا وارث بنی اسرائیل کو بنا دیا. ');
INSERT INTO "junagarri" VALUES(26,60,'26:60','پس فرعونی سورج نکلتے ہی ان کے تعاقب میں نکلے. ');
INSERT INTO "junagarri" VALUES(26,61,'26:61','پس جب دونوں نے ایک دوسرے کو دیکھ لیا، تو موسیٰ کے ساتھیوں نے کہا، ہم تو یقیناً پکڑ لیے گئے. ');
INSERT INTO "junagarri" VALUES(26,62,'26:62','موسیٰ نے کہا، ہرگز نہیں۔ یقین مانو، میرا رب میرے ساتھ ہے جو ضرور مجھے راه دکھائے گا. ');
INSERT INTO "junagarri" VALUES(26,63,'26:63','ہم نے موسیٰ کی طرف وحی بھیجی کہ دریا پر اپنی لاٹھی مار ، پس اسی وقت دریا پھٹ گیا اور ہر ایک حصہ پانی کا مثل بڑے پہاڑ کے ہوگیا. ');
INSERT INTO "junagarri" VALUES(26,64,'26:64','اور ہم نے اسی جگہ دوسروں کو نزدیک لا کھڑا کر دیا. ');
INSERT INTO "junagarri" VALUES(26,65,'26:65','اور موسیٰ (علیہ السلام) کو اور اس کے تمام ساتھیوں کو نجات دے دی.');
INSERT INTO "junagarri" VALUES(26,66,'26:66','پھر اور سب دوسروں کو ڈبو دیا. ');
INSERT INTO "junagarri" VALUES(26,67,'26:67','یقیناً اس میں بڑی عبرت ہے اور ان میں کےاکثر لوگ ایمان والے نہیں. ');
INSERT INTO "junagarri" VALUES(26,68,'26:68','اور بیشک آپ کا رب بڑا ہی غالب اور مہربان ہے؟');
INSERT INTO "junagarri" VALUES(26,69,'26:69','انہیں ابراہیم (علیہ السلام) کا واقعہ بھی سنادو.');
INSERT INTO "junagarri" VALUES(26,70,'26:70','جبکہ انہوں نے اپنے باپ اور اپنی قوم سے فرمایا کہ تم کس کی عبادت کرتے ہو؟');
INSERT INTO "junagarri" VALUES(26,71,'26:71','انہوں نے جواب دیا کہ عبادت کرتے ہیں بتوں کی، ہم تو برابر ان کے مجاور بنے بیٹھے ہیں. ');
INSERT INTO "junagarri" VALUES(26,72,'26:72','آپ نے فرمایا کہ جب تم انہیں پکارتے ہو تو کیا وه سنتے بھی ہیں؟');
INSERT INTO "junagarri" VALUES(26,73,'26:73','یا تمہیں نفع نقصان بھی پہنچا سکتے ہیں. ');
INSERT INTO "junagarri" VALUES(26,74,'26:74','انہوں نے کہا یہ (ہم کچھ نہیں جانتے) ہم تو اپنے باپ دادوں کو اسی طرح کرتے پایا. ');
INSERT INTO "junagarri" VALUES(26,75,'26:75','آپ نے فرمایا کچھ خبر بھی ہے جنہیں تم پوج رہے ہو.');
INSERT INTO "junagarri" VALUES(26,76,'26:76','تم اور تمہارے اگلے باپ دادا، وه سب میرے دشمن ہیں. ');
INSERT INTO "junagarri" VALUES(26,77,'26:77','بجز سچے اللہ تعالیٰ کے جو تمام جہان کا پالنہار ہے. ');
INSERT INTO "junagarri" VALUES(26,78,'26:78','جس نے مجھے پیدا کیا ہے اور وہی میری رہبری فرماتا ہے. ');
INSERT INTO "junagarri" VALUES(26,79,'26:79','وہی ہے جو مجھے کھلاتا پلاتا ہے. ');
INSERT INTO "junagarri" VALUES(26,80,'26:80','اور جب میں بیمار پڑ جاؤں تو مجھے شفا عطا فرماتا ہے. ');
INSERT INTO "junagarri" VALUES(26,81,'26:81','اور وہی مجھے مار ڈالے گا پھر زنده کردے گا ');
INSERT INTO "junagarri" VALUES(26,82,'26:82','اور جس سے امید بندھی ہوئی ہے کہ وه روز جزا میں میرے گناہوں کو بخش دے گا. ');
INSERT INTO "junagarri" VALUES(26,83,'26:83','اے میرے رب! مجھے قوت فیصلہ عطا فرما اور مجھے نیک لوگوں میں ملا دے.');
INSERT INTO "junagarri" VALUES(26,84,'26:84','اور میرا ذکر خیر پچھلے لوگوں میں بھی باقی رکھ. ');
INSERT INTO "junagarri" VALUES(26,85,'26:85','مجھے نعمتوں والی جنت کے وارثوں میں سے بنادے.');
INSERT INTO "junagarri" VALUES(26,86,'26:86','اور میرے باپ کو بخش دے یقیناً وه گمراہوں میں سے تھا. ');
INSERT INTO "junagarri" VALUES(26,87,'26:87','اور جس دن کے لوگ دوباره جلائے جائیں مجھے رسوا نہ کر. ');
INSERT INTO "junagarri" VALUES(26,88,'26:88','جس دن کہ مال اور اولاد کچھ کام نہ آئے گی.');
INSERT INTO "junagarri" VALUES(26,89,'26:89','لیکن فائده والا وہی ہوگا جو اللہ تعالیٰ کے سامنے بے عیب دل لے کر جائے. ');
INSERT INTO "junagarri" VALUES(26,90,'26:90','اور پرہیزگاروں کے لیے جنت بالکل نزدیک لادی جائے گی.');
INSERT INTO "junagarri" VALUES(26,91,'26:91','اور گمراه لوگوں کے لیے جہنم ظاہر کردی جائے گی. ');
INSERT INTO "junagarri" VALUES(26,92,'26:92','اور ان سے پوچھا جائے گا کہ جن کی تم پوجا کرتے رہے وه کہاں ہیں؟');
INSERT INTO "junagarri" VALUES(26,93,'26:93','جو اللہ تعالیٰ کے سوا تھے ، کیاوه تمہاری مدد کرتے ہیں؟ یا کوئی بدلہ لے سکتے ہیں. ');
INSERT INTO "junagarri" VALUES(26,94,'26:94','پس وه سب اور کل گمراه لوگ جہنم میں اوندھے منھ ڈال دیے جائیں گے. ');
INSERT INTO "junagarri" VALUES(26,95,'26:95','اور ابلیس کے تمام کے تمام لشکر بھی، وہاں.');
INSERT INTO "junagarri" VALUES(26,96,'26:96','آپس میں لڑتے جھگڑتے ہوئے کہیں گے.');
INSERT INTO "junagarri" VALUES(26,97,'26:97','کہ قسم اللہ کی! یقیناً ہم تو کھلی غلطی پر تھے.');
INSERT INTO "junagarri" VALUES(26,98,'26:98','جبکہ تمہیں رب العالمین کے برابر سمجھ بیٹھے تھے. ');
INSERT INTO "junagarri" VALUES(26,99,'26:99','اور ہمیں تو سوا ان بدکاروں کے کسی اور نے گمراه نہیں کیا تھا. ');
INSERT INTO "junagarri" VALUES(26,100,'26:100','اب تو ہمارا کوئی سفارشی بھی نہیں.');
INSERT INTO "junagarri" VALUES(26,101,'26:101','اور نہ کوئی (سچا) غم خوار دوست. ');
INSERT INTO "junagarri" VALUES(26,102,'26:102','اگر کاش کہ ہمیں ایک مرتبہ پھر جانا ملتا تو ہم پکے سچے مومن بن جاتے. ');
INSERT INTO "junagarri" VALUES(26,103,'26:103','یہ ماجرا یقیناً ایک زبردست نشانی ہے ان میں سے اکثر لوگ ایمان لانے والے نہیں. ');
INSERT INTO "junagarri" VALUES(26,104,'26:104','یقیناً آپ کا پروردگار ہی غالب مہربان ہے.');
INSERT INTO "junagarri" VALUES(26,105,'26:105','قوم نوح نے بھی نبیوں کو جھٹلایا. ');
INSERT INTO "junagarri" VALUES(26,106,'26:106','جبکہ ان کے بھائی نوح (علیہ السلام) نے کہا کہ کیا تمہیں اللہ کا خوف نہیں!');
INSERT INTO "junagarri" VALUES(26,107,'26:107','سنو! میں تمہاری طرف اللہ کا امانتدار رسول ہوں. ');
INSERT INTO "junagarri" VALUES(26,108,'26:108','پس تمہیں اللہ سے ڈرنا چاہئے اور میری بات ماننی چاہئے. ');
INSERT INTO "junagarri" VALUES(26,109,'26:109','میں تم سے اس پر کوئی اجر نہیں چاہتا، میرا بدلہ تو صرف رب العالمین کے ہاں ہے. ');
INSERT INTO "junagarri" VALUES(26,110,'26:110','پس تم اللہ کا خوف رکھو اور میری فرمانبرداری کرو. ');
INSERT INTO "junagarri" VALUES(26,111,'26:111','قوم نے جواب دیا کہ ہم تجھ پر ایمان لائیں! تیری تابعداری تو رذیل لوگوں نے کی ہے. ');
INSERT INTO "junagarri" VALUES(26,112,'26:112','آپ نے فرمایا! مجھے کیا خبر کہ وه پہلے کیا کرتے رہے؟ ');
INSERT INTO "junagarri" VALUES(26,113,'26:113','ان کا حساب تو میرے رب کے ذمہ ہے اگر تمہیں شعور ہو تو.');
INSERT INTO "junagarri" VALUES(26,114,'26:114','میں ایمان والوں کو دھکے دینے والا نہیں. ');
INSERT INTO "junagarri" VALUES(26,115,'26:115','میں تو صاف طور پر ڈرا دینے والا ہوں. ');
INSERT INTO "junagarri" VALUES(26,116,'26:116','انہوں نے کہاکہ اے نوح! اگر تو باز نہ آیا تو یقیناً تجھے سنگسار کردیا جائے گا.');
INSERT INTO "junagarri" VALUES(26,117,'26:117','آپ نے کہا اے میرے پروردگار! میری قوم نے مجھے جھٹلا دیا.');
INSERT INTO "junagarri" VALUES(26,118,'26:118','پس تو مجھ میں اور ان میں کوئی قطعی فیصلہ کردے اور مجھے اور میرے با ایمان ساتھیوں کو نجات دے.');
INSERT INTO "junagarri" VALUES(26,119,'26:119','چنانچہ ہم نے اسے اور اس کے ساتھیوں کو بھری ہوئی کشتی میں (سوار کراکر) نجات دے دی.');
INSERT INTO "junagarri" VALUES(26,120,'26:120','بعد ازاں باقی کے تمام لوگوں کو ہم نے ڈبو دیا. ');
INSERT INTO "junagarri" VALUES(26,121,'26:121','یقیناً اس میں بہت بڑی عبرت ہے۔ ان میں سے اکثر لوگ ایمان لانے والے تھے بھی نہیں.');
INSERT INTO "junagarri" VALUES(26,122,'26:122','اور بیشک آپ کا پروردگار البتہ وہی ہے زبردست رحم کرنے والا.');
INSERT INTO "junagarri" VALUES(26,123,'26:123','عادیوں نے بھی رسولوں کو جھٹلایا. ');
INSERT INTO "junagarri" VALUES(26,124,'26:124','جبکہ ان سے ان کے بھائی ہود نے کہا کہ کیا تم ڈرتے نہیں؟');
INSERT INTO "junagarri" VALUES(26,125,'26:125','میں تمہارا امانتدار پیغمبر ہوں.');
INSERT INTO "junagarri" VALUES(26,126,'26:126','پس اللہ سے ڈرو اور میرا کہا مانو!');
INSERT INTO "junagarri" VALUES(26,127,'26:127','میں اس پر تم سے کوئی اجرت طلب نہیں کرتا، میرا ثواب تو تمام جہان کے پروردگار کے پاس ہی ہے.');
INSERT INTO "junagarri" VALUES(26,128,'26:128','کیا تم ایک ایک ٹیلے پر بطور کھیل تماشا یادگار (عمارت) بنا رہے ہو. ');
INSERT INTO "junagarri" VALUES(26,129,'26:129','اور بڑی صنعت والے (مضبوط محل تعمیر) کر رہے ہو، گویا کہ تم ہمیشہ یہیں رہو گے. ');
INSERT INTO "junagarri" VALUES(26,130,'26:130','اور جب کسی پر ہاتھ، ڈالتے ہو تو سختی اور ظلم سے پکڑتے ہو. ');
INSERT INTO "junagarri" VALUES(26,131,'26:131','اللہ سے ڈرو اور میری پیروی کرو. ');
INSERT INTO "junagarri" VALUES(26,132,'26:132','اس سے ڈرو جس نے ان چیزوں سے تمہاری امداد کی جنہیں تم جانتے ہو.');
INSERT INTO "junagarri" VALUES(26,133,'26:133','اس نے تمہاری مدد کی مال سے اور اولاد سے.');
INSERT INTO "junagarri" VALUES(26,134,'26:134','باغات سے اور چشموں سے.');
INSERT INTO "junagarri" VALUES(26,135,'26:135','مجھے تو تمہاری نسبت بڑے دن کےعذاب کا اندیشہ ہے. ');
INSERT INTO "junagarri" VALUES(26,136,'26:136','انہوں نے کہا کہ آپ وعظ کہیں یا وعظ کہنے والوں میں نہ ہوں ہم پر یکساں ہے.');
INSERT INTO "junagarri" VALUES(26,137,'26:137','یہ تو بس پرانے لوگوں کی عادت ہے. ');
INSERT INTO "junagarri" VALUES(26,138,'26:138','اور ہم ہرگز عذاب نہیں دیے جائیں گے. ');
INSERT INTO "junagarri" VALUES(26,139,'26:139','چونکہ عادیوں نے حضرت ہود کو جھٹلایا، اس لیے ہم نے انہیں تباه کردیا یقیناً اس میں نشانی ہے اور ان میں سے اکثر بے ایمان تھے.');
INSERT INTO "junagarri" VALUES(26,140,'26:140','بیشک آپ کا رب وہی غالب مہربان.');
INSERT INTO "junagarri" VALUES(26,141,'26:141','ثمودیوں نے بھی پیغمبروں کو جھٹلایا.');
INSERT INTO "junagarri" VALUES(26,142,'26:142','ان کے بھائی صالح نے ان سے فرمایا کہ کیا تم اللہ سے نہیں ڈرتے؟.');
INSERT INTO "junagarri" VALUES(26,143,'26:143','میں تمہاری طرف اللہ کا امانت دار پیغمبر ہوں.');
INSERT INTO "junagarri" VALUES(26,144,'26:144','تو تم اللہ سے ڈرو اور میرا کہا کرو.');
INSERT INTO "junagarri" VALUES(26,145,'26:145','میں اس پر تم سے کوئی اجرت نہیں مانگتا، میری اجرت تو بس پرودگار عالم پر ہی ہے.');
INSERT INTO "junagarri" VALUES(26,146,'26:146','کیا ان چیزوں میں جو یہاں ہیں تم امن کے ساتھ چھوڑ دیے جاؤ گے. ');
INSERT INTO "junagarri" VALUES(26,147,'26:147','یعنی ان باغوں اور ان چشموں.');
INSERT INTO "junagarri" VALUES(26,148,'26:148','اور ان کھیتوں اور ان کھجوروں کے باغوں میں جن کے شگوفے نرم و نازک ہیں. ');
INSERT INTO "junagarri" VALUES(26,149,'26:149','اور تم پہاڑوں کو تراش تراش کر پر تکلف مکانات بنا رہے ہو. ');
INSERT INTO "junagarri" VALUES(26,150,'26:150','پس اللہ سے ڈرو اور میری اطاعت کرو.');
INSERT INTO "junagarri" VALUES(26,151,'26:151','بے باک حد سے گزر جانے والوں کی اطاعت سے باز آجاؤ.');
INSERT INTO "junagarri" VALUES(26,152,'26:152','جو ملک میں فساد پھیلا رہے ہیں اور اصلاح نہیں کرتے.');
INSERT INTO "junagarri" VALUES(26,153,'26:153','وه بولے کہ بس تو ان میں سے ہے جن پر جادو کردیا گیا ہے.');
INSERT INTO "junagarri" VALUES(26,154,'26:154','تو تو ہم جیسا ہی انسان ہے۔ اگر تو سچوں سے ہے تو کوئی معجزه لے آ.');
INSERT INTO "junagarri" VALUES(26,155,'26:155','آپ نے فرمایا یہ ہے اونٹنی، پانی پینے کی ایک باری اس کی اور ایک مقرره دن کی باری پانی پینے کی تمہاری. ');
INSERT INTO "junagarri" VALUES(26,156,'26:156','(خبردار!) اسے برائی سے ہاتھ نہ لگانا ورنہ ایک بڑے بھاری دن کا عذاب تمہاری گرفت کر لے گا. ');
INSERT INTO "junagarri" VALUES(26,157,'26:157','پھر بھی انہوں نے اس کی کوچیں کاٹ ڈالیں ، بس وه پشیمان ہوگئے. ');
INSERT INTO "junagarri" VALUES(26,158,'26:158','اور عذاب نے انہیں آ دبوچا ۔ بیشک اس میں عبرت ہے۔ اور ان میں سے اکثر لوگ مومن نہ تھے.');
INSERT INTO "junagarri" VALUES(26,159,'26:159','اور بیشک آپ کا رب بڑا زبردست اور مہربان ہے.');
INSERT INTO "junagarri" VALUES(26,160,'26:160','قوم لوط نے بھی نبیوں کو جھُٹلایا.');
INSERT INTO "junagarri" VALUES(26,161,'26:161','ان سے ان کے بھائی لوط (علیہ السلام) نے کہا کیا تم اللہ کا خوف نہیں رکھتے؟');
INSERT INTO "junagarri" VALUES(26,162,'26:162','میں تمہاری طرف امانت دار رسول ہوں.');
INSERT INTO "junagarri" VALUES(26,163,'26:163','پس تم اللہ تعالیٰ سے ڈرو اور میری اطاعت کرو.');
INSERT INTO "junagarri" VALUES(26,164,'26:164','میں تم سے اس پر کوئی بدلہ نہیں مانگتا میرا اجر تو صرف اللہ تعالیٰ پر ہے جو تمام جہان کا رب ہے.');
INSERT INTO "junagarri" VALUES(26,165,'26:165','کیا تم جہان والوں میں سے مردوں کے ساتھ شہوت رانی کرتے ہو.');
INSERT INTO "junagarri" VALUES(26,166,'26:166','اور تمہاری جن عورتوں کو اللہ تعالیٰ نے تمہارا جوڑا بنایا ہے ان کو چھوڑ دیتے ہو ، بلکہ تم ہو ہی حد سے گزر جانے والے. ');
INSERT INTO "junagarri" VALUES(26,167,'26:167','انہوں نے جواب دیاکہ اے لوط! اگر تو باز نہ آیا تو یقیناً نکال دیا جائے گا. ');
INSERT INTO "junagarri" VALUES(26,168,'26:168','آپ نے فرمایا، میں تمہارے کام سے سخت ناخوش ہوں. ');
INSERT INTO "junagarri" VALUES(26,169,'26:169','میرے پروردگار! مجھے اور میرے گھرانے کو اس (وبال) سے بچالے جو یہ کرتے ہیں.');
INSERT INTO "junagarri" VALUES(26,170,'26:170','پس ہم نے اسے اور اس کے متعلقین کو سب کو بچالیا.');
INSERT INTO "junagarri" VALUES(26,171,'26:171','بجز ایک بڑھیا کے کہ وه پیچھے ره جانے والوں میں ہوگئی. ');
INSERT INTO "junagarri" VALUES(26,172,'26:172','پھر ہم نے باقی اور سب کو ہلاک کر دیا.');
INSERT INTO "junagarri" VALUES(26,173,'26:173','اور ہم نے ان پر ایک خاص قسم کا مینہ برسایا، پس بہت ہی برا مینہ تھا جو ڈرائے گئے ہوئے لوگوں پر برسا. ');
INSERT INTO "junagarri" VALUES(26,174,'26:174','یہ ماجرا بھی سراسر عبرت ہے۔ ان میں سے بھی اکثر مسلمان نہ تھے.');
INSERT INTO "junagarri" VALUES(26,175,'26:175','بیشک تیرا پروردگار وہی ہے غلبے والا مہربانی والا.');
INSERT INTO "junagarri" VALUES(26,176,'26:176','اَیکہ والوں نے بھی رسولوں کو جھٹلایا ');
INSERT INTO "junagarri" VALUES(26,177,'26:177','جبکہ ان سے شعیب (علیہ السلام) نے کہاکہ کیا تمہیں ڈر خوف نہیں؟');
INSERT INTO "junagarri" VALUES(26,178,'26:178','میں تمہاری طرف امانت دار رسول ہوں.');
INSERT INTO "junagarri" VALUES(26,179,'26:179','اللہ کا خوف کھاؤ اور میری فرمانبرداری کرو.');
INSERT INTO "junagarri" VALUES(26,180,'26:180','میں اس پر تم سے کوئی اجرت نہیں چاہتا، میرا اجر تمام جہانوں کے پالنے والے کے پاس ہے.');
INSERT INTO "junagarri" VALUES(26,181,'26:181','ناپ پورا بھرا کرو کم دینے والوں میں شمولیت نہ کرو. ');
INSERT INTO "junagarri" VALUES(26,182,'26:182','اور سیدھی صحیح ترازو سے تولا کرو. ');
INSERT INTO "junagarri" VALUES(26,183,'26:183','لوگوں کو ان کی چیزیں کمی سے نہ دو بے باکی کے ساتھ زمین میں فساد مچاتے نہ پھرو. ');
INSERT INTO "junagarri" VALUES(26,184,'26:184','اس اللہ کا خوف رکھو جس نے خود تمہیں اور اگلی مخلوق کو پیدا کیا ہے. ');
INSERT INTO "junagarri" VALUES(26,185,'26:185','انہوں نے کہا تو تو ان میں سے ہے جن پر جادو کردیا جاتا ہے.');
INSERT INTO "junagarri" VALUES(26,186,'26:186','اور تو تو ہم ہی جیسا ایک انسان ہے اور ہم تو تجھے جھوٹ بولنے والوں میں سے ہی سمجھتے ہیں. ');
INSERT INTO "junagarri" VALUES(26,187,'26:187','اگر تو سچے لوگوں میں سے ہے تو ہم پر آسمان کے ٹکڑے گرادے. ');
INSERT INTO "junagarri" VALUES(26,188,'26:188','کہاکہ میرا رب خوب جاننے والا ہے جو کچھ تم کر رہے ہو. ');
INSERT INTO "junagarri" VALUES(26,189,'26:189','چونکہ انہوں نے اسے جھٹلایا تو انہیں سائبان والے دن کے عذاب نے پکڑ لیا ۔ وه بڑے بھاری دن کا عذاب تھا.');
INSERT INTO "junagarri" VALUES(26,190,'26:190','یقیناً اس میں بڑی نشانی ہے اور ان میں کےاکثر مسلمان نہ تھے.');
INSERT INTO "junagarri" VALUES(26,191,'26:191','اور یقیناً تیرا پروردگار البتہ وہی ہے غلبے والا مہربانی والا.');
INSERT INTO "junagarri" VALUES(26,192,'26:192','اور بیشک و شبہ یہ (قرآن) رب العالمین کا نازل فرمایا ہوا ہے.');
INSERT INTO "junagarri" VALUES(26,193,'26:193','اسے امانت دار فرشتہ لے کر آیا ہے. ');
INSERT INTO "junagarri" VALUES(26,194,'26:194','آپ کے دل پر اترا ہے کہ آپ آگاه کر دینے والوں میں سے ہو جائیں. ');
INSERT INTO "junagarri" VALUES(26,195,'26:195','صاف عربی زبان میں ہے.');
INSERT INTO "junagarri" VALUES(26,196,'26:196','اگلے نبیوں کی کتابوں میں بھی اس قرآن کا تذکره ہے. ');
INSERT INTO "junagarri" VALUES(26,197,'26:197','کیا انہیں یہ نشانی کافی نہیں کہ حقانیت قرآن کو تو بنی اسرائیل کے علماء بھی جانتے ہیں. ');
INSERT INTO "junagarri" VALUES(26,198,'26:198','اور اگر ہم اسے کسی عجمی شخص پر نازل فرماتے.');
INSERT INTO "junagarri" VALUES(26,199,'26:199','پس وه ان کے سامنے اس کی تلاوت کرتا تو یہ اسے باور کرنے والے نہ ہوتے. ');
INSERT INTO "junagarri" VALUES(26,200,'26:200','اسی طرح ہم نے گناہگاروں کے دلوں میں اس انکار کو داخل کر دیا ہے. ');
INSERT INTO "junagarri" VALUES(26,201,'26:201','وه جب تک دردناک عذابوں کو ملاحظہ نہ کرلیں ایمان نہ لائیں گے.');
INSERT INTO "junagarri" VALUES(26,202,'26:202','پس وه عذاب ان کو ناگہاں آجائے گا انہیں اس کا شعور بھی نہ ہو گا.');
INSERT INTO "junagarri" VALUES(26,203,'26:203','اس وقت کہیں گے کہ کیا ہمیں کچھ مہلت دی جائے گی؟ ');
INSERT INTO "junagarri" VALUES(26,204,'26:204','پس کیا یہ ہمارے عذاب کی جلدی مچا رہے ہیں؟ ');
INSERT INTO "junagarri" VALUES(26,205,'26:205','اچھا یہ بھی بتاؤ کہ اگر ہم نے انہیں کئی سال بھی فائده اٹھانے دیا.');
INSERT INTO "junagarri" VALUES(26,206,'26:206','پھر انہیں وه عذاب آ لگا جن سے یہ دھمکائے جاتے تھے.');
INSERT INTO "junagarri" VALUES(26,207,'26:207','تو جو کچھ بھی یہ برتتے رہے اس میں سے کچھ بھی فائده نہ پہنچا سکے گا. ');
INSERT INTO "junagarri" VALUES(26,208,'26:208','ہم نے کسی بستی کو ہلاک نہیں کیا ہے مگر اسی حال میں کہ اس کے لیے ڈرانے والے تھے.');
INSERT INTO "junagarri" VALUES(26,209,'26:209','نصیحت کے طور پر اور ہم ظلم کرنے والے نہیں ہیں. ');
INSERT INTO "junagarri" VALUES(26,210,'26:210','اس قرآن کو شیطان نہیں لائے.');
INSERT INTO "junagarri" VALUES(26,211,'26:211','نہ وه اس کے قابل ہیں، نہ انہیں اس کی طاقت ہے.');
INSERT INTO "junagarri" VALUES(26,212,'26:212','بلکہ وه تو سننے سے بھی محروم کردیے گئے ہیں. ');
INSERT INTO "junagarri" VALUES(26,213,'26:213','پس تو اللہ کے ساتھ کسی اور معبود کو نہ پکار کہ تو بھی سزا پانے والوں میں سے ہوجائے.');
INSERT INTO "junagarri" VALUES(26,214,'26:214','اپنے قریبی رشتہ والوں کو ڈرا دے. ');
INSERT INTO "junagarri" VALUES(26,215,'26:215','اس کے ساتھ فروتنی سے پیش آ، جو بھی ایمان لانے والا ہو کر تیری تابعداری کرے.');
INSERT INTO "junagarri" VALUES(26,216,'26:216','اگر یہ لوگ تیری نافرمانی کریں تو تو اعلان کردے کہ میں ان کاموں سے بیزار ہوں جو تم کر رہے ہو.');
INSERT INTO "junagarri" VALUES(26,217,'26:217','اپنا پورا بھروسہ غالب مہربان اللہ پر رکھ.');
INSERT INTO "junagarri" VALUES(26,218,'26:218','جو تجھے دیکھتا رہتا ہے جبکہ تو کھڑا ہوتا ہے.');
INSERT INTO "junagarri" VALUES(26,219,'26:219','اور سجده کرنے والوں کے درمیان تیرا گھومنا پھرنا بھی. ');
INSERT INTO "junagarri" VALUES(26,220,'26:220','وه بڑا ہی سننے والا اور خوب ہی جاننے والا ہے.');
INSERT INTO "junagarri" VALUES(26,221,'26:221','کیا میں تمہیں بتاؤں کہ شیطان کس پر اترتے ہیں.');
INSERT INTO "junagarri" VALUES(26,222,'26:222','وه ہر ایک جھوٹے گنہگار پر اترتے ہیں. ');
INSERT INTO "junagarri" VALUES(26,223,'26:223','(اچٹتی) ہوئی سنی سنائی پہنچا دیتے ہیں اور ان میں سے اکثر جھوٹے ہیں. ');
INSERT INTO "junagarri" VALUES(26,224,'26:224','شاعروں کی پیروی وه کرتے ہیں جو بہکے ہوئے ہوں.');
INSERT INTO "junagarri" VALUES(26,225,'26:225','کیا آپ نے نہیں دیکھا کہ شاعر ایک ایک بیابان میں سر ٹکراتے پھرتے ہیں.');
INSERT INTO "junagarri" VALUES(26,226,'26:226','اور وه کہتے ہیں جو کرتے نہیں. ');
INSERT INTO "junagarri" VALUES(26,227,'26:227','سوائے ان کے جو ایمان لائے اور نیک عمل کیے اور بکثرت اللہ تعالیٰ کا ذکر کیا اور اپنی مظلومی کے بعد انتقام لیا ، جنہوں نے ظلم کیا ہے وه بھی عنقریب جان لیں گے کہ کس کروٹ الٹتے ہیں. ');
INSERT INTO "junagarri" VALUES(27,1,'27:1','طٰس، یہ آیتیں ہیں قرآن کی (یعنی واضح) اور روشن کتاب کی.');
INSERT INTO "junagarri" VALUES(27,2,'27:2','ہدایت اور خوشخبری ایمان والوں کے لیے.');
INSERT INTO "junagarri" VALUES(27,3,'27:3','جو نماز قائم کرتے ہیں اور زکوٰة ادا کرتے ہیں اور آخرت پر یقین رکھتے ہیں. ');
INSERT INTO "junagarri" VALUES(27,4,'27:4','جو لوگ قیامت پر ایمان نہیں لاتے ہم نے انہیں ان کے کرتوت زینت دار کر دکھائے ہیں، پس وه بھٹکتے پھرتے ہیں. ');
INSERT INTO "junagarri" VALUES(27,5,'27:5','یہی لوگ ہیں جن کے لیے برا عذاب ہے اور آخرت میں بھی وه سخت نقصان یافتہ ہیں.');
INSERT INTO "junagarri" VALUES(27,6,'27:6','بیشک آپ کو اللہ حکیم وعلیم کی طرف سے قرآن سکھایا جارہا ہے.');
INSERT INTO "junagarri" VALUES(27,7,'27:7','(یاد ہوگا) جبکہ موسیٰ (علیہ السلام) نے اپنے گھر والوں سے کہا کہ میں نے آگ دیکھی ہے، میں وہاں سے یا تو کوئی خبر لے کر یا آگ کا کوئی سلگتا ہوا انگارا لے کر ابھی تمہارے پاس آ جاؤں گا تاکہ تم سینک تاپ کر لو. ');
INSERT INTO "junagarri" VALUES(27,8,'27:8','جب وہاں پہنچے تو آواز دی گئی کہ بابرکت ہے وه جو اس آگ میں ہے اور برکت دیاگیا ہے وه جس کے آس پاس ہے اور پاک ہے اللہ جو تمام جہانوں کا پالنے والا ہے. ');
INSERT INTO "junagarri" VALUES(27,9,'27:9','موسیٰ! سن بات یہ ہے کہ میں ہی اللہ ہوں غالب با حکمت.');
INSERT INTO "junagarri" VALUES(27,10,'27:10','تو اپنی لاٹھی ڈال دے، موسیٰ نے جب اسے ہلتا جلتا دیکھا اس طرح کہ گویا وه ایک سانپ ہے تو منھ موڑے ہوئے پیٹھ پھیر کر بھاگے اور پلٹ کر بھی نہ دیکھا، اے موسیٰ! خوف نہ کھا ، میرے حضور میں پیغمبر ڈرا نہیں کرتے.');
INSERT INTO "junagarri" VALUES(27,11,'27:11','لیکن جو لوگ ظلم کریں پھر اس کے عوض نیکی کریں اس برائی کے پیچھے تو میں بھی بخشنے والا مہربان ہوں. ');
INSERT INTO "junagarri" VALUES(27,12,'27:12','اور اپنا ہاتھ اپنے گریبان میں ڈال، وه سفید چمکیلا ہو کر نکلے گا بغیر کسی عیب کے ، تو نو نشانیاں لے کر فرعون اور اس کی قوم کی طرف جا ، یقیناً وه بدکاروں کا گروه ہے.');
INSERT INTO "junagarri" VALUES(27,13,'27:13','پس جب ان کے پاس آنکھیں کھول دینے والے ہمارے معجزے پہنچے تو وه کہنے لگے یہ تو صریح جادو ہے.');
INSERT INTO "junagarri" VALUES(27,14,'27:14','انہوں نے انکار کردیا حالانکہ ان کے دل یقین کر چکے تھے صرف ظلم اور تکبر کی بنا پر ۔ پس دیکھ لیجئے کہ ان فتنہ پرداز لوگوں کا انجام کیسا کچھ ہوا.');
INSERT INTO "junagarri" VALUES(27,15,'27:15','اور ہم نے یقیناً داؤد اور سلیمان کو علم دے رکھا تھا ۔ اور دونوں نے کہا، تعریف اس اللہ کے لیے ہے جس نے ہمیں اپنے بہت سے ایماندار بندوں پر فضیلت عطا فرمائی ہے.');
INSERT INTO "junagarri" VALUES(27,16,'27:16','اور داؤد کے وارث سلیمان ہوئے اور کہنے لگے لوگو! ہمیں پرندوں کی بولی سکھائی گئی ہے اور ہم سب کچھ میں سے دیئے گئے ہیں۔ بیشک یہ بالکل کھلا ہوا فضل الٰہی ہے.');
INSERT INTO "junagarri" VALUES(27,17,'27:17','سلیمان کے سامنے ان کے تمام لشکر جنات اور انسان اور پرند میں سے جمع کیے گئے (ہر ہر قسم کی) الگ الگ درجہ بندی کردی گئی. ');
INSERT INTO "junagarri" VALUES(27,18,'27:18','جب وه چینوٹیوں کے میدان میں پہنچے تو ایک چیونٹی نے کہا اے چیونٹیو! اپنے اپنے گھروں میں گھس جاؤ، ایسا نہ ہو کہ بیخبری میں سلیمان اور اس کا لشکر تمہیں روند ڈالے. ');
INSERT INTO "junagarri" VALUES(27,19,'27:19','اس کی اس بات سے حضرت سلیمان مسکرا کر ہنس دیئے اور دعا کرنے لگے کہ اے پروردگار! تو مجھے توفیق دے کہ میں تیری ان نعمتوں کا شکر بجا لاؤں جو تو نے مجھ پر انعام کی ہیں اور میرے ماں باپ پر اور میں ایسے نیک اعمال کرتا رہوں جن سے تو خوش رہے مجھے اپنی رحمت سے نیک بندوں میں شامل کر لے. ');
INSERT INTO "junagarri" VALUES(27,20,'27:20','آپ نے پرندوں کی دیکھ بھال کی اور فرمانے لگے یہ کیا بات ہے کہ میں ہدہد کو نہیں دیکھتا؟ کیا واقعی وه غیر حاضر ہے؟ ');
INSERT INTO "junagarri" VALUES(27,21,'27:21','یقیناً میں اسے سخت سزا دوں گا، یا اسے ذبح کر ڈالوں گا، یا میرے سامنے کوئی صریح دلیل بیان کرے.');
INSERT INTO "junagarri" VALUES(27,22,'27:22','کچھ زیاده دیر نہ گزری تھی کہ آ کر اس نے کہا میں ایک ایسی چیز کی خبر لایا ہوں کہ تجھے اس کی خبر ہی نہیں ، میں سبا کی ایک سچی خبر تیرے پاس لایا. ہوں.');
INSERT INTO "junagarri" VALUES(27,23,'27:23','میں نے دیکھا کہ ان کی بادشاہت ایک عورت کر رہی ہے جسے ہر قسم کی چیز سے کچھ نہ کچھ دیا گیا ہے اور اس کا تخت بھی بڑی عظمت والا ہے. ');
INSERT INTO "junagarri" VALUES(27,24,'27:24','میں نے اسے اور اس کی قوم کو، اللہ تعالیٰ کو چھوڑ کر سورج کو سجده کرتے ہوئے پایا، شیطان نے ان کے کام انہیں بھلے کرکے دکھلا کر صحیح راه سے روک دیا ہے ۔ پس وه ہدایت پر نہیں آتے.');
INSERT INTO "junagarri" VALUES(27,25,'27:25','کہ اسی اللہ کے لیے سجدے کریں جو آسمانوں اور زمینوں کے پوشیده چیزوں کو باہر نکالتا ہے ، اور جو کچھ تم چھپاتے ہو اور ظاہر کرتے ہو وه سب کچھ جانتا ہے.');
INSERT INTO "junagarri" VALUES(27,26,'27:26','اس کے سوا کوئی معبود برحق نہیں وہی عظمت والے عرش کا مالک ہے.');
INSERT INTO "junagarri" VALUES(27,27,'27:27','سلیمان نے کہا، اب ہم دیکھیں گے کہ تو نے سچ کہا ہے یا تو جھوٹا ہے.');
INSERT INTO "junagarri" VALUES(27,28,'27:28','میرے اس خط کو لے جاکر انہیں دے دے پھر ان کے پاس سے ہٹ آ اور دیکھ کہ وه کیا جواب دیتے ہیں. ');
INSERT INTO "junagarri" VALUES(27,29,'27:29','وه کہنے لگی اے سردارو! میری طرف ایک باوقعت خط ڈالا گیا ہے.');
INSERT INTO "junagarri" VALUES(27,30,'27:30','جو سلیمان کی طرف سے ہے اور جو بخشش کرنے والے مہربان اللہ کے نام سے شروع ہے.');
INSERT INTO "junagarri" VALUES(27,31,'27:31','یہ کہ تم میرے سامنے سرکشی نہ کرو اور مسلمان بن کر میرے پاس آجاؤ. ');
INSERT INTO "junagarri" VALUES(27,32,'27:32','اس نے کہا اے میرے سردارو! تم میرے اس معاملہ میں مجھے مشوره دو۔ میں کسی امر کا قطعی فیصلہ جب تک تمہاری موجودگی اور رائے نہ ہو نہیں کیا کرتی.');
INSERT INTO "junagarri" VALUES(27,33,'27:33','ان سب نے جواب دیا کہ ہم طاقت اور قوت والے سخت لڑنے بھڑنے والے ہیں ۔ آگے آپ کو اختیار ہے آپ خود ہی سوچ لیجئے کہ ہمیں آپ کیا کچھ حکم فرماتی ہیں. ');
INSERT INTO "junagarri" VALUES(27,34,'27:34','اس نے کہا کہ بادشاه جب کسی بستی میں گھستے ہیں تو اسے اجاڑ دیتے ہیں اور وہاں کے باعزت لوگوں کو ذلیل کر دیتے ہیں اور یہ لوگ بھی ایسا ہی کریں گے. ');
INSERT INTO "junagarri" VALUES(27,35,'27:35','میں انہیں ایک ہدیہ بھیجنے والی ہوں، پھر دیکھ لوں گی کہ قاصد کیا جواب لے کر لوٹتے ہیں. ');
INSERT INTO "junagarri" VALUES(27,36,'27:36','پس جب قاصد حضرت سلیمان کے پاس پہنچا تو آپ نے فرمایا کیا تم مال سے مجھے مدد دینا چاہتے ہو ؟ مجھے تو میرے رب نے اس سے بہت بہتر دے رکھا ہے جو اس نے تمہیں دیا ہے پس تم ہی اپنے تحفے سے خوش رہو. ');
INSERT INTO "junagarri" VALUES(27,37,'27:37','جا ان کی طرف واپس لوٹ جا ، ہم ان (کے مقابلہ) پر وه لشکر لائیں گے جن کے سامنے پڑنے کی ان میں طاقت نہیں اور ہم انہیں ذلیل و پست کر کے وہاں سے نکال باہر کریں گے. ');
INSERT INTO "junagarri" VALUES(27,38,'27:38','آپ نے فرمایا اے سردارو! تم میں سے کوئی ہے جو ان کے مسلمان ہو کر پہنچنے سے پہلے ہی اس کا تخت مجھے لادے. ');
INSERT INTO "junagarri" VALUES(27,39,'27:39','ایک قوی ہیکل جن کہنے لگا آپ اپنی اس مجلس سے اٹھیں اس سے پہلے ہی پہلے میں اسے آپ کے پاس لادیتا ہوں، یقین مانیے کہ میں اس پر قادر ہوں اور ہوں بھی امانت دار. ');
INSERT INTO "junagarri" VALUES(27,40,'27:40','جس کے پاس کتاب کا علم تھا وه بول اٹھا کہ آپ پلک جھپکائیں اس سے بھی پہلے میں اسے آپ کے پاس پہنچا سکتا ہوں ۔ جب آپ نے اسے اپنے پاس موجود پایا تو فرمانے لگے یہی میرے رب کا فضل ہے، تاکہ وه مجھے آزمائے کہ میں شکر گزاری کرتا ہوں یا ناشکری، شکر گزار اپنے ہی نفع کے لیے شکر گزاری کرتا ہے اور جو ناشکری کرے تو میرا پروردگار (بے پروا اور بزرگ) غنی اور کریم ہے.');
INSERT INTO "junagarri" VALUES(27,41,'27:41','حکم دیا کہ اس کے تخت میں کچھ پھیر بدل کر دو تاکہ معلوم ہو جائے کہ یہ راه پالیتی ہے یا ان میں سے ہوتی ہے جو راه نہیں پاتے. ');
INSERT INTO "junagarri" VALUES(27,42,'27:42','پھر جب وه آگئی تو اس سے کہا (دریافت کیا) گیا کہ ایسا ہی تیرا (بھی) تخت ہے؟ اس نے جواب دیا کہ یہ گویا وہی ہے ، ہمیں اس سے پہلے ہی علم دیا گیا تھا اور ہم مسلمان تھے. ');
INSERT INTO "junagarri" VALUES(27,43,'27:43','اسے انہوں نے روک رکھا تھا جن کی وه اللہ کے سوا پرستش کرتی رہی تھی، یقیناً وه کافر لوگوں میں سے تھی. ');
INSERT INTO "junagarri" VALUES(27,44,'27:44','اس سے کہا گیا کہ محل میں چلی چلو، جسے دیکھ کر یہ سمجھ کر کہ یہ حوض ہے اس نے اپنی پنڈلیاں کھول دیں ، فرمایا یہ توشیشے سے منڈھی ہوئی عمارت ہے، کہنے لگی میرے پروردگار! میں نے اپنے آپ پر ظلم کیا۔ اب میں سلیمان کے ساتھ اللہ رب العالمین کی مطیع اور فرمانبردار بنتی ہوں. ');
INSERT INTO "junagarri" VALUES(27,45,'27:45','یقیناً ہم نے ثمود کی طرف ان کے بھائی صالح کو بھیجا کہ تم سب اللہ کی عبادت کرو پھر بھی وه دو فریق بن کر آپس میں لڑنے جھگڑنے لگے. ');
INSERT INTO "junagarri" VALUES(27,46,'27:46','آپ نے فرمایا اے میری قوم کے لوگو! تم نیکی سے پہلے برائی کی جلدی کیوں مچا رہے ہو؟ تم اللہ تعالیٰ سےاستغفار کیوں نہیں کرتے تاکہ تم پر رحم کیا جائے.');
INSERT INTO "junagarri" VALUES(27,47,'27:47','وه کہنے لگے ہم تو تیری اور تیرے ساتھیوں کی بدشگونی لے رہے ہیں؟ آپ نے فرمایا تمہاری بدشگونی اللہ کے ہاں ہے، بلکہ تم فتنے میں پڑے ہوئے لوگ ہو. ');
INSERT INTO "junagarri" VALUES(27,48,'27:48','اس شہر میں نو سردار تھے جو زمین میں فساد پھیلاتے رہتے تھے اور اصلاح نہیں کرتے تھے.');
INSERT INTO "junagarri" VALUES(27,49,'27:49','انہوں نے آپس میں بڑی قسمیں کھا کھا کر عہد کیا کہ رات ہی کو صالح اور اس کے گھر والوں پر ہم چھاپہ ماریں گے ، اور اس کے وارثوں سے صاف کہہ دیں گے کہ ہم اس کے اہل کی ہلاکت کے وقت موجود نہ تھے اور ہم بالکل سچے ہیں. ');
INSERT INTO "junagarri" VALUES(27,50,'27:50','انہوں نے مکر (خفیہ تدبیر) کیا اور ہم نے بھی اور وه اسے سمجھتے ہی نہ تھے. ');
INSERT INTO "junagarri" VALUES(27,51,'27:51','(اب) دیکھ لے ان کے مکر کا انجام کیسا کچھ ہوا؟ کہ ہم نے ان کو اور ان کی قوم کو سب کو غارت کردیا. ');
INSERT INTO "junagarri" VALUES(27,52,'27:52','یہ ہیں ان کے مکانات جو ان کے ظلم کی وجہ سے اجڑے پڑے ہیں، جو لوگ علم رکھتے ہیں ان کے لیےاس میں بڑی نشانی ہے.');
INSERT INTO "junagarri" VALUES(27,53,'27:53','ہم نے ان کو جو ایمان لائے تھے اور پرہیزگار تھے بال بال بچالیا.');
INSERT INTO "junagarri" VALUES(27,54,'27:54','اور لوط کا (ذکر کر) جبکہ اس نے اپنی قوم سے کہاکہ کیا باوجود دیکھنے بھالنے کے پھر بھی تم بدکاری کر رہے ہو؟ ');
INSERT INTO "junagarri" VALUES(27,55,'27:55','یہ کیا بات ہے کہ تم عورتوں کو چھوڑ کر مردوں کے پاس شہوت سے آتے ہو حق یہ ہے کہ تم بڑی ہی نادانی کر رہے ہو. ');
INSERT INTO "junagarri" VALUES(27,56,'27:56','قوم کا جواب بجز اس کہنے کے اور کچھ نہ تھا کہ آل لوط کو اپنے شہر سے شہر بدر کر دو، یہ تو بڑے پاک باز بن رہے ہیں. ');
INSERT INTO "junagarri" VALUES(27,57,'27:57','پس ہم نے اسے اور اس کے اہل کو بجز اس کی بیوی کے سب کو بچالیا، اس کا اندازه تو باقی ره جانے والوں میں ہم لگا ہی چکے تھے. ');
INSERT INTO "junagarri" VALUES(27,58,'27:58','اور ان پر ایک (خاص قسم) کی بارش برسادی ، پس ان دھمکائے ہوئے لوگوں پر بری بارش ہوئی. ');
INSERT INTO "junagarri" VALUES(27,59,'27:59','تو کہہ دے کہ تمام تعریف اللہ ہی کے لیے ہے اور اس کے برگزیده بندوں پر سلام ہے ۔ کیا اللہ تعالیٰ بہتر ہے یا وه جنہیں یہ لوگ شریک ٹھہرا رہے ہیں. ');
INSERT INTO "junagarri" VALUES(27,60,'27:60','بھلا بتاؤ تو؟ کہ آسمانوں کو اور زمین کو کس نے پیدا کیا؟ کس نے آسمان سے بارش برسائی؟ پھر اس سے ہرے بھرے بارونق باغات اگا دیئے؟ ان باغوں کے درختوں کو تم ہر گز نہ اگا سکتے ، کیا اللہ کے ساتھ اور کوئی معبود بھی ہے ؟ بلکہ یہ لوگ ہٹ جاتے ہیں (سیدھی راه سے).');
INSERT INTO "junagarri" VALUES(27,61,'27:61','کیا وه جس نے زمین کو قرار گاه بنایا اور اس کے درمیان نہریں جاری کر دیں اور اس کے لیے پہاڑ بنائے اور دو سمندروں کے درمیان روک بنا دی کیا اللہ کے ساتھ اور کوئی معبود بھی ہے؟ بلکہ ان میں سے اکثر کچھ جانتے ہی نہیں.');
INSERT INTO "junagarri" VALUES(27,62,'27:62','بے کس کی پکار کو جب کہ وه پکارے، کون قبول کرکے سختی کو دور کر دیتا ہے ؟ اور تمہیں زمین کا خلیفہ بناتا ہے ، کیا اللہ تعالیٰ کے ساتھ اور معبود ہے؟ تم بہت کم نصیحت وعبرت حاصل کرتے ہو.');
INSERT INTO "junagarri" VALUES(27,63,'27:63','کیا وه جو تمہیں خشکی اور تری کی تاریکیوں میں راه دکھاتا ہے اور جو اپنی رحمت سے پہلے ہی خوشخبریاں دینے والی ہوائیں چلاتا ہے ، کیا اللہ کے ساتھ کوئی اور معبود بھی ہے جنہیں یہ شریک کرتے ہیں ان سب سے اللہ بلند وبالاتر ہے.');
INSERT INTO "junagarri" VALUES(27,64,'27:64','کیا وه جو مخلوق کی اول دفعہ پیدائش کرتا ہے پھر اسے لوٹائے گا اور جو تمہیں آسمان اور زمین سے روزیاں دے رہا ہے ، کیا اللہ کے ساتھ کوئی اور معبود ہے کہہ دیجئے کہ اگر سچے ہو تو اپنی دلیل لاؤ.');
INSERT INTO "junagarri" VALUES(27,65,'27:65','کہہ دیجئے کہ آسمانوں والوں میں سے زمین والوں میں سے سوائے اللہ کے کوئی غیب نہیں جانتا ، انہیں تو یہ بھی نہیں معلوم کہ کب اٹھا کھڑے کیے جائیں گے؟');
INSERT INTO "junagarri" VALUES(27,66,'27:66','بلکہ آخرت کے بارے میں ان کا علم ختم ہوچکا ہے ، بلکہ یہ اس کی طرف سے شک میں ہیں۔ بلکہ یہ اس سے اندھے ہیں. ');
INSERT INTO "junagarri" VALUES(27,67,'27:67','کافروں نے کہا کہ کیا جب ہم مٹی ہو جائیں گے اور ہمارے باپ دادا بھی کیا ہم پھر نکالے جائیں گے.');
INSERT INTO "junagarri" VALUES(27,68,'27:68','ہم اور ہمارے باپ دادوں کو بہت پہلے سے یہ وعدے دیے جاتے رہے۔ کچھ نہیں یہ تو صرف اگلوں کے افسانے ہیں. ');
INSERT INTO "junagarri" VALUES(27,69,'27:69','کہہ دیجئے کہ زمین میں چل پھر کر ذرا دیکھو تو سہی کہ گنہگاروں کا کیسا انجام ہوا؟ ');
INSERT INTO "junagarri" VALUES(27,70,'27:70','آپ ان کے بارے میں غم نہ کریں اور ان کے داؤں گھات سے تنگ دل نہ ہوں.');
INSERT INTO "junagarri" VALUES(27,71,'27:71','کہتے ہیں کہ یہ وعده کب ہے اگر سچے ہو تو بتلا دو.');
INSERT INTO "junagarri" VALUES(27,72,'27:72','جواب دیجئے! کہ شاید بعض وه چیزیں جن کی تم جلدی مچا رہے ہو تم سے بہت ہی قریب ہوگئی ہوں. ');
INSERT INTO "junagarri" VALUES(27,73,'27:73','یقیناً آپ کا پروردگار تمام لوگوں پر بڑے ہی فضل والا ہے لیکن اکثر لوگ ناشکری کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(27,74,'27:74','بیشک آپ کا رب ان چیزوں کو بھی جانتا ہے جنہیں ان کے سینے چھپا رہے ہیں اور جنہیں ظاہر کر رہے ہیں.');
INSERT INTO "junagarri" VALUES(27,75,'27:75','آسمان وزمین کی کوئی پوشیده چیز بھی ایسی نہیں جو روشن اور کھلی کتاب میں نہ ہو. ');
INSERT INTO "junagarri" VALUES(27,76,'27:76','یقیناً یہ قرآن بنی اسرائیل کے سامنے ان اکثر چیزوں کا بیان کر رہا ہے جن میں یہ اختلاف کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(27,77,'27:77','اور یہ قرآن ایمان والوں کے لیے یقیناً ہدایت اور رحمت ہے. ');
INSERT INTO "junagarri" VALUES(27,78,'27:78','آپ کا رب ان کے درمیان اپنے حکم سے سب فیصلے کر دے گا ، وه بڑا ہی غالب اور دانا ہے.');
INSERT INTO "junagarri" VALUES(27,79,'27:79','پس آپ یقیناً اللہ ہی پر بھروسہ رکھیے، یقیناً آپ سچے اور کھلے دین پر ہیں. ');
INSERT INTO "junagarri" VALUES(27,80,'27:80','بیشک آپ نہ مُردوں کو سنا سکتے ہیں اور نہ بہروں کو اپنی پکار سنا سکتے ہیں ، جبکہ وه پیٹھ پھیرے روگرداں جا رہے ہوں. ');
INSERT INTO "junagarri" VALUES(27,81,'27:81','اور نہ آپ اندھوں کو ان کی گمراہی سے ہٹا کر رہنمائی کر سکتے ہیں آپ تو صرف انہیں سنا سکتے ہیں جو ہماری آیتوں پر ایمان لائے ہیں پھر وه فرمانبردار ہوجاتے ہیں.');
INSERT INTO "junagarri" VALUES(27,82,'27:82','جب ان کے اوپر عذاب کا وعده ثابت ہو جائے گا ، ہم زمین سے ان کے لیے ایک جانور نکالیں گے جو ان سے باتیں کرتا ہوگا کہ لوگ ہماری آیتوں پر یقین نہیں کرتے تھے. ');
INSERT INTO "junagarri" VALUES(27,83,'27:83','اور جس دن ہم ہر امت میں سے ان لوگوں کے گروه کو جو ہماری آیتوں کو جھٹلاتے تھے گھیر گھار کر لائیں گے پھر وه سب کے سب الگ کر دیئے جائیں گے. ');
INSERT INTO "junagarri" VALUES(27,84,'27:84','جب سب کے سب آپہنچیں گے تو اللہ تعالیٰ فرمائے گا کہ تم نے میری آیتوں کو باوجودیکہ تمہیں ان کا پورا علم نہ تھا کیوں جھٹلایا ؟ اور یہ بھی بتلاؤ کہ تم کیا کچھ کرتے رہے؟ ');
INSERT INTO "junagarri" VALUES(27,85,'27:85','بسبب اس کے کہ انہوں نے ظلم کیا تھا ان پر بات جم جائے گی اور وه کچھ بول نہ سکیں گے. ');
INSERT INTO "junagarri" VALUES(27,86,'27:86','کیا وه دیکھ نہیں رہے ہیں کہ ہم نے رات کو اس لیے بنایا ہے کہ وه اس میں آرام حاصل کرلیں اور دن کو ہم نے دکھلانے والا بنایا ہے، یقیناً اس میں ان لوگوں کے لیے نشانیاں ہیں جو ایمان ویقین رکھتے ہیں.');
INSERT INTO "junagarri" VALUES(27,87,'27:87','جس دن صور پھونکا جائے گا تو سب کے سب آسمانوں والے اور زمین والے گھبرا اٹھیں گے مگر جسے اللہ تعالیٰ چاہے ، اور سارے کے سارے عاجز وپست ہو کر اس کے سامنے حاضر ہوں گے.');
INSERT INTO "junagarri" VALUES(27,88,'27:88','اور آپ پہاڑوں کو دیکھ کر اپنی جگہ جمے ہوئے خیال کرتے ہیں لیکن وه بھی بادل کی طرح اڑتے پھریں گے ، یہ ہے صنعت اللہ کی جس نے ہر چیز کو مضبوط بنایا ہے ، جو کچھ تم کرتے ہو اس سے وه باخبر ہے.');
INSERT INTO "junagarri" VALUES(27,89,'27:89','جو لوگ نیک عمل لائیں گے انھیں اس سے بہتر بدلہ ملے گا اور وه اس دن کی گھبراہٹ سے بےخوف ہوں گے. ');
INSERT INTO "junagarri" VALUES(27,90,'27:90','اور جو برائی لے کر آئیں گے وه اوندھے منھ آگ میں جھونک دیئے جائیں گے۔ صرف وہی بدلہ دیئے جاؤ گے جو تم کرتے رہے.');
INSERT INTO "junagarri" VALUES(27,91,'27:91','مجھے تو بس یہی حکم دیا گیا ہے کہ میں اس شہر کے پروردگار کی عبادت کرتا رہوں جس نے اسے حرمت والا بنایا ہے ، جس کی ملکیت ہر چیز ہے اور مجھے یہ بھی فرمایا گیا ہے کہ میں فرماں برداروں میں ہو جاؤں.');
INSERT INTO "junagarri" VALUES(27,92,'27:92','اور میں قرآن کی تلاوت کرتا رہوں، جو راه راست پر آجائے وه اپنے نفع کے لیے راه راست پر آئے گا۔ اور جو بہک جائے تو کہہ دیجئے! کہ میں تو صرف ہوشیار کرنے والوں میں سے ہوں. ');
INSERT INTO "junagarri" VALUES(27,93,'27:93','کہہ دیجئے، کہ تمام تعریفیں اللہ ہی کو سزاوار ہیں وه عنقریب اپنی نشانیاں دکھائے گا جنہیں تم (خود) پہچان لو گے ۔ اور جو کچھ تم کرتے ہو اس سے آپ کا رب غافل نہیں. ');
INSERT INTO "junagarri" VALUES(28,1,'28:1','طٰسم.');
INSERT INTO "junagarri" VALUES(28,2,'28:2','یہ آیتیں ہیں روشن کتاب کی.*');
INSERT INTO "junagarri" VALUES(28,3,'28:3','ہم آپ کے سامنے موسیٰ اور فرعون کا صحیح واقعہ بیان کرتے ہیں ان لوگوں کے لیے جو ایمان رکھتے ہیں. ');
INSERT INTO "junagarri" VALUES(28,4,'28:4','یقیناً فرعون نے زمین میں سرکشی کر رکھی تھی اور وہاں کے لوگوں کو گروه گروه بنا رکھا تھا اور ان میں سے ایک فرقہ کو کمزور کر رکھا تھا اور ان کے لڑکوں کو تو ذبح کر ڈالتا تھا اور ان کی لڑکیوں کو زنده چھوڑ دیتا تھا۔ بیشک وشبہ وه تھا ہی مفسدوں میں سے.');
INSERT INTO "junagarri" VALUES(28,5,'28:5','پھر ہماری چاہت ہوئی کہ ہم ان پر کرم فرمائیں جنہیں زمین میں بےحد کمزور کر دیا گیا تھا، اور ہم انہیں کو پیشوا اور (زمین) کا وارث بنائیں. ');
INSERT INTO "junagarri" VALUES(28,6,'28:6','اور یہ بھی کہ ہم انہیں زمین میں قدرت واختیار دیں اور فرعون اور ہامان اور ان کے لشکروں کو وه دکھائیں جس سے وه ڈر رہے ہیں. ');
INSERT INTO "junagarri" VALUES(28,7,'28:7','ہم نے موسیٰ (علیہ السلام) کی ماں کو وحی کی کہ اسے دودھ پلاتی ره اور جب تجھے اس کی نسبت کوئی خوف معلوم ہو تو اسے دریا میں بہا دینا اور کوئی ڈر خوف یا رنج غم نہ کرنا ، ہم یقیناً اسے تیری طرف لوٹانے والے ہیں اور اسے اپنے پیغمبروں میں بنانے والے ہیں.');
INSERT INTO "junagarri" VALUES(28,8,'28:8','آخر فرعون کے لوگوں نے اس بچے کو اٹھا لیا کہ آخرکار یہی بچہ ان کا دشمن ہوا اور ان کے رنج کا باعث بنا ، کچھ شک نہیں کہ فرعون اور ہامان اور ان کے لشکر تھے ہی خطاکار. ');
INSERT INTO "junagarri" VALUES(28,9,'28:9','اور فرعون کی بیوی نے کہا یہ تو میری اور تیری آنکھوں کی ٹھنڈک ہے، اسے قتل نہ کرو ، بہت ممکن ہے کہ یہ ہمیں کوئی فائده پہنچائے یا ہم اسے اپنا ہی بیٹا بنا لیں اور یہ لوگ شعور ہی نہ رکھتے تھے. ');
INSERT INTO "junagarri" VALUES(28,10,'28:10','موسیٰ (علیہ السلام) کی والده کا دل بے قرار ہوگیا ، قریب تھیں کہ اس واقعہ کو بالکل ظاہر کر دیتیں اگر ہم ان کے دل کو ڈھارس نہ دے دیتے یہ اس لیے کہ وه یقین کرنے والوں میں رہے. ');
INSERT INTO "junagarri" VALUES(28,11,'28:11','موسیٰ (علیہ السلام) کی والده نے اس کی بہن سے کہا کہ تو اس کے پیچھے پیچھے جا، تو وه اسے دور ہی دور سے دیکھتی رہی اور فرعونیوں کو اس کا علم بھی نہ ہوا.');
INSERT INTO "junagarri" VALUES(28,12,'28:12','ان کے پہنچنے سے پہلے ہم نے موسیٰ (علیہ السلام) پر دائیوں کا دودھ حرام کر دیا تھا ۔ یہ کہنے لگی کہ کیا میں تمہیں ایسا گھرانا بتاؤں جو اس بچہ کی تمہارے لیے پرورش کرے اور ہوں بھی وه اس بچے کے خیر خواه.');
INSERT INTO "junagarri" VALUES(28,13,'28:13','پس ہم نے اسے اس کی ماں کی طرف واپس پہنچایا ، تاکہ اس کی آنکھیں ٹھنڈی رہیں اور آزرده خاطر نہ ہو اور جان لے کہ اللہ تعالی کا وعده سچا ہے لیکن اکثر لوگ نہیں جانتے. ');
INSERT INTO "junagarri" VALUES(28,14,'28:14','اور جب موسیٰ (علیہ السلام) اپنی جوانی کو پہنچ گئے اور پورے توانا ہوگئے ہم نے انہیں حکمت وعلم عطا فرمایا ، نیکی کرنے والوں کو ہم اسی طرح بدلہ دیا کرتے ہیں.');
INSERT INTO "junagarri" VALUES(28,15,'28:15','اور موسیٰ (علیہ السلام) ایک ایسے وقت شہر میں آئے جبکہ شہر کے لوگ غفلت میں تھے ۔ یہاں دو شخصوں کو لڑتے ہوئے پایا، یہ ایک تو اس کے رفیقوں میں سے تھا اور یہ دوسرا اس کے دشمنوں میں سے ، اس کی قوم والے نے اس کے خلاف جو اس کے دشمنوں میں سے تھا اس سے فریاد کی، جس پر موسیٰ (علیہ السلام) نے اس کو مکا مارا جس سے وه مر گیا موسیٰ (علیہ السلام) کہنے لگے یہ تو شیطانی کام ہے ، یقیناً شیطان دشمن اور کھلے طور پر بہکانے والا ہے. ');
INSERT INTO "junagarri" VALUES(28,16,'28:16','پھر دعا کرنے لگے کہ اے پروردگار! میں نے خود اپنے اوپر ظلم کیا، تو مجھے معاف فرما دے ، اللہ تعالیٰ نے اسے بخش دیا، وه بخشش اور بہت مہربانی کرنے والا ہے.');
INSERT INTO "junagarri" VALUES(28,17,'28:17','کہنے لگے اے میرے رب! جیسے تو نے مجھ پر یہ کرم فرمایا میں بھی اب ہرگز کسی گنہگار کا مددگار نہ بنوں گا. ');
INSERT INTO "junagarri" VALUES(28,18,'28:18','صبح ہی صبح ڈرتے اندیشہ کی حالت میں خبریں لینے کو شہر میں گئے، کہ اچانک وہی شخص جس نے کل ان سے مدد طلب کی تھی ان سے فریاد کر رہا ہے۔ موسیٰ (علیہ السلام) نے اس سے کہا کہ اس میں شک نہیں تو تو صریح بے راه ہے. ');
INSERT INTO "junagarri" VALUES(28,19,'28:19','پھر جب اپنے اور اس کے دشمن کو پکڑنا چاہا وه فریادی کہنے لگا کہ موسیٰ (علیہ السلام) کیا جس طرح تو نے کل ایک شخص کو قتل کیا ہے مجھے بھی مار ڈالنا چاہتا ہے، تو تو ملک میں ظالم وسرکش ہونا ہی چاہتا ہے اور تیرا یہ اراده ہی نہیں کہ ملاپ کرنے والوں میں سے ہو.');
INSERT INTO "junagarri" VALUES(28,20,'28:20','شہر کے پرلے کنارے سے ایک شخص دوڑتا ہوا آیا اور کہنے لگا اے موسیٰ! یہاں کے سردار تیرے قتل کا مشوره کر رہے ہیں، پس تو بہت جلد چلا جا مجھے اپنا خیر خواه مان.');
INSERT INTO "junagarri" VALUES(28,21,'28:21','پس موسیٰ (علیہ السلام) وہاں سے خوفزده ہوکر دیکھتے بھالتے نکل کھڑے ہوئے ، کہنے لگے اے پروردگار! مجھے ظالموں کے گروه سے بچا لے. ');
INSERT INTO "junagarri" VALUES(28,22,'28:22','اور جب مدین کی طرف متوجہ ہوئے تو کہنے لگے مجھے امید ہے کہ میرا رب مجھے سیدھی راه لے چلے گا. ');
INSERT INTO "junagarri" VALUES(28,23,'28:23','مدین کے پانی پر جب آپ پہنچے تو دیکھا کہ لوگوں کی ایک جماعت وہاں پانی پلا رہی ہے اور دو عورتیں الگ کھڑی اپنے (جانوروں کو) روکتی ہوئی دکھائی دیں، پوچھا کہ تمہارا کیا حال ہے ، وه بولیں کہ جب تک یہ چرواہے واپس نہ لوٹ جائیں ہم پانی نہیں پلاتیں اور ہمارے والد بہت بڑی عمر کے بوڑھے ہیں. ');
INSERT INTO "junagarri" VALUES(28,24,'28:24','پس آپ نے خود ان جانوروں کو پانی پلا دیا پھر سائے کی طرف ہٹ آئے اور کہنے لگے اے پروردگار! تو جو کچھ بھلائی میری طرف اتارے میں اس کا محتاج ہوں. ');
INSERT INTO "junagarri" VALUES(28,25,'28:25','اتنے میں ان دونوں عورتوں میں سے ایک ان کی طرف شرم وحیا سے چلتی ہوئی آئی ، کہنے لگی کہ میرے باپ آپ کو بلا رہے ہیں تاکہ آپ نے ہمارے (جانوروں) کو جو پانی پلایا ہے اس کی اجرت دیں ، جب حضرت موسیٰ (علیہ السلام) ان کے پاس پہنچے اور ان سے اپنا سارا حال بیان کیا تو وه کہنے لگے اب نہ ڈر تو نے ظالم قوم سے نجات پائی. ');
INSERT INTO "junagarri" VALUES(28,26,'28:26','ان دونوں میں سے ایک نے کہا کہ ابا جی! آپ انہیں مزدوری پر رکھ لیجئے، کیونکہ جنہیں آپ اجرت پر رکھیں ان میں سے سب سے بہتر وه ہے جو مضبوط اور امانت دار ہو. ');
INSERT INTO "junagarri" VALUES(28,27,'28:27','اس بزرگ نے کہا میں اپنی ان دونوں لڑکیوں میں سے ایک کو آپ کے نکاح میں دینا چاہتا ہوں اس (مہر پر) کہ آپ آٹھ سال تک میرا کام کاج کریں ۔ ہاں اگر آپ دس سال پورے کریں تو یہ آپ کی طرف سے بطور احسان کے ہے میں یہ ہرگز نہیں چاہتا کہ آپ کو کسی مشقت میں ڈالوں ، اللہ کو منظور ہے تو آگے چل کر آپ مجھے بھلا آدمی پائیں گے. ');
INSERT INTO "junagarri" VALUES(28,28,'28:28','موسیٰ (علیہ السلام) نے کہا، خیر تو یہ بات میرے اور آپ کے درمیان پختہ ہوگئی، میں ان دونوں مدتوں میں سے جسے پورا کروں مجھ پر کوئی زیادتی نہ ہو ، ہم یہ جو کچھ کہہ رہے ہیں اس پر اللہ (گواه اور) کارساز ہے. ');
INSERT INTO "junagarri" VALUES(28,29,'28:29','جب حضرت موسیٰ علیہ السلام نے مدت پوری کرلی اور اپنے گھر والوں کو لے کر چلے تو کوه طور کی طرف آگ دیکھی۔ اپنی بیوی سے کہنے لگے ٹھہرو! میں نے آگ دیکھی ہے بہت ممکن ہے کہ میں وہاں سے کوئی خبر لاؤں یا آگ کا کوئی انگاره لاؤں تاکہ تم سینک لو.');
INSERT INTO "junagarri" VALUES(28,30,'28:30','پس جب وہاں پہنچے تو اس بابرکت زمین کے میدان کے دائیں کنارے کے درخت میں سے آواز دیئے گئے کہ اے موسیٰ! یقیناً میں ہی اللہ ہوں سارے جہانوں کا پروردگار. ');
INSERT INTO "junagarri" VALUES(28,31,'28:31','اور یہ (بھی آواز آئی) کہ اپنی لاٹھی ڈال دے۔ پھر جب اسے دیکھا کہ وه سانپ کی طرح پھن پھنا رہی ہے تو پیٹھ پھیر کر واپس ہوگئے اور مڑ کر رخ بھی نہ کیا، ہم نے کہا اے موسیٰ! آگے آ ڈر مت، یقیناً تو ہر طرح امن والا ہے. ');
INSERT INTO "junagarri" VALUES(28,32,'28:32','اپنے ہاتھ کو اپنے گریبان میں ڈال وه بغیر کسی قسم کے روگ کے چمکتا ہوا نکلے گا بالکل سفید اور خوف سے (بچنے کے لیے) اپنے بازو اپنی طرف ملا لے ، پس یہ دونوں معجزے تیرے لیے تیرے رب کی طرف سے ہیں فرعون اور اس کی جماعت کی طرف، یقیناً وه سب کے سب بےحکم اور نافرمان لوگ ہیں. ');
INSERT INTO "junagarri" VALUES(28,33,'28:33','موسیٰ (علیہ السلام) نے کہا پروردگار! میں نے ان کا ایک آدمی قتل کر دیا تھا۔ اب مجھے اندیشہ ہے کہ وه مجھے بھی قتل کر ڈالیں. ');
INSERT INTO "junagarri" VALUES(28,34,'28:34','اور میرا بھائی ہارون (علیہ السلام) مجھ سے بہت زیاده فصیح زبان والا ہے تو اسے بھی میرا مددگار بنا کر میرے ساتھ بھیج کہ وه مجھے سچا مانے، مجھے تو خوف ہے کہ وه سب مجھے جھٹلا دیں گے.');
INSERT INTO "junagarri" VALUES(28,35,'28:35','اللہ تعالیٰ نے فرمایا کہ ہم تیرے بھائی کے ساتھ تیرا بازو مضبوط کردیں گے اور تم دونوں کو غلبہ دیں گے فرعونی تم تک پہنچ ہی نہ سکیں گے ، بسبب ہماری نشانیوں کے، تم دونوں اور تمہاری تابعداری کرنے والے ہی غالب رہیں گے. ');
INSERT INTO "junagarri" VALUES(28,36,'28:36','پس جب ان کے پاس موسیٰ (علیہ السلام) ہمارے دیے ہوئے کھلے معجزے لے کر پہنچے تو وه کہنے لگے یہ تو صرف گھڑا گھڑایا جادو ہے ہم نے اپنے اگلے باپ دادوں کے زمانہ میں کبھی یہ نہیں سنا. ');
INSERT INTO "junagarri" VALUES(28,37,'28:37','حضرت موسیٰ (علیہ السلام) کہنے لگے میرا رب تعالیٰ اسے خوب جانتا ہے جو اس کے پاس کی ہدایت لے کر آتا ہے اور جس کے لیے آخرت کا (اچھا) انجام ہوتا ہے ۔ یقیناً بے انصافوں کا بھلا نہ ہوگا. ');
INSERT INTO "junagarri" VALUES(28,38,'28:38','فرعون کہنے لگا اے درباریو! میں تو اپنے سوا کسی کو تمہارا معبود نہیں جانتا۔ سن اے ہامان! تو میرے لیے مٹی کو آگ سے پکوا پھر میرے لیے ایک محل تعمیر کر تو میں موسیٰ کے معبود کو جھانک لوں اسے میں تو جھوٹوں میں سے ہی گمان کر رہا ہوں. ');
INSERT INTO "junagarri" VALUES(28,39,'28:39','اس نے اور اس کے لشکروں نے ناحق طریقے پر ملک میں تکبر کیا اور سمجھ لیا کہ وه ہماری جانب لوٹائے ہی نہ جائیں گے.');
INSERT INTO "junagarri" VALUES(28,40,'28:40','بالآخر ہم نے اسے اور اس کے لشکروں کو پکڑ لیا اور دریا برد کر دیا ، اب دیکھ لے کہ ان گنہگاروں کا انجام کیسا کچھ ہوا؟');
INSERT INTO "junagarri" VALUES(28,41,'28:41','اور ہم نے انہیں ایسے امام بنا دیئے کہ لوگوں کو جہنم کی طرف بلائیں اور روز قیامت مطلق مدد نہ کیے جائیں.');
INSERT INTO "junagarri" VALUES(28,42,'28:42','اور ہم نے اس دنیا میں بھی ان کے پیچھے اپنی لعنت لگا دی اور قیامت کے دن بھی وه بدحال لوگوں میں سے ہوں گے. ');
INSERT INTO "junagarri" VALUES(28,43,'28:43','اور ان اگلے زمانہ والوں کو ہلاک کرنے کے بعد ہم نے موسیٰ (علیہ السلام) کو ایسی کتاب عنایت فرمائی جو لوگوں کے لیے دلیل اور ہدایت ورحمت ہوکر آئی تھی تاکہ وه نصیحت حاصل کرلیں. ');
INSERT INTO "junagarri" VALUES(28,44,'28:44','اور طور کے مغربی جانب جب کہ ہم نے موسیٰ (علیہ السلام) کو حکم احکام کی وحی پہنچائی تھی، نہ تو تو موجود تھا اور نہ تو دیکھنے والوں میں سے تھا. ');
INSERT INTO "junagarri" VALUES(28,45,'28:45','لیکن ہم نے بہت سی نسلیں پیدا کیں جن پر لمبی مدتیں گزر گئیں ، اور نہ تو مدین کے رہنے والوں میں سے تھا کہ ان کے سامنے ہماری آیتوں کی تلاوت کرتا بلکہ ہم ہی رسولوں کے بھیجنے والے رہے. ');
INSERT INTO "junagarri" VALUES(28,46,'28:46','اور نہ تو طور کی طرف تھا جب کہ ہم نے آواز دی بلکہ یہ تیرے پروردگار کی طرف سے ایک رحمت ہے ، اس لیے کہ تو ان لوگوں کو ہوشیار کر دے جن کے پاس تجھ سے پہلے کوئی ڈرانے والا نہیں پہنچا ، کیا عجب کہ وه نصیحت حاصل کرلیں.');
INSERT INTO "junagarri" VALUES(28,47,'28:47','اگر یہ بات نہ ہوتی کہ انہیں ان کے اپنے ہاتھوں آگے بھیجے ہوئے اعمال کی وجہ سے کوئی مصیبت پہنچتی تو یہ کہہ اٹھتے کہ اے ہمارے رب! تو نے ہماری طرف کوئی رسول کیوں نہ بھیجا؟ کہ ہم تیری آیتوں کی تابعداری کرتے اور ایمان والوں میں سے ہو جاتے. ');
INSERT INTO "junagarri" VALUES(28,48,'28:48','پھر جب ان کے پاس ہماری طرف سے حق آپہنچا تو کہتے ہیں کہ یہ وه کیوں نہیں دیا گیا جیسے دیئے گئے تھے موسیٰ (علیہ السلام) اچھا تو کیا موسیٰ (علیہ السلام) کو جو کچھ دیا گیا تھا اس کے ساتھ لوگوں نے کفر نہیں کیا تھا ، صاف کہا تھا کہ یہ دونوں جادوگر ہیں جو ایک دوسرے کے مددگار ہیں اور ہم تو ان سب کے منکر ہیں. ');
INSERT INTO "junagarri" VALUES(28,49,'28:49','کہہ دے کہ اگر سچے ہو تو تم بھی اللہ کے پاس سے کوئی ایسی کتاب لے آؤ جو ان دونوں سے زیاده ہدایت والی ہو میں اسی کی پیروی کروں گا. ');
INSERT INTO "junagarri" VALUES(28,50,'28:50','پھر اگر یہ تیری نہ مانیں تو تو یقین کرلے کہ یہ صرف اپنی خواہش کی پیروی کر رہے ہیں۔ اور اس سے بڑھ کر بہکا ہوا کون ہے؟ جو اپنی خواہش کے پیچھے پڑا ہوا ہو بغیر اللہ کی رہنمائی کے، بیشک اللہ تعالیٰ ظالم لوگوں کو ہدایت نہیں دیتا. ');
INSERT INTO "junagarri" VALUES(28,51,'28:51','اور ہم برابر پے درپے لوگوں کے لیے اپنا کلام بھیجتے رہے تاکہ وه نصیحت حاصل کرلیں. ');
INSERT INTO "junagarri" VALUES(28,52,'28:52','جس کو ہم نے اس سے پہلے کتاب عنایت فرمائی وه تو اس پر بھی ایمان رکھتے ہیں. ');
INSERT INTO "junagarri" VALUES(28,53,'28:53','اور جب اس کی آیتیں ان کے پاس پڑھی جاتی ہیں تو وه کہہ دیتے ہیں کہ اس کے ہمارے رب کی طرف سے حق ہونے پر ہمارا ایمان ہے ہم تو اس سے پہلے ہی مسلمان ہیں. ');
INSERT INTO "junagarri" VALUES(28,54,'28:54','یہ اپنے کیے ہوئے صبر کے بدلے دوہرا دوہرا اجر دیئے جائیں گے ۔ یہ نیکی سے بدی کو ٹال دیتے ہیں اور ہم نے جو انہیں دے رکھا ہے اس میں سے دیتے رہتے ہیں.');
INSERT INTO "junagarri" VALUES(28,55,'28:55','اور جب بیہوده بات کان میں پڑتی ہے تو اس سے کناره کر لیتے ہیں اور کہہ دیتے ہیں کہ ہمارے عمل ہمارے لیے اور تمہارے اعمال تمہارے لیے، تم پر سلام ہو ، ہم جاہلوں سے (الجھنا) نہیں چاہتے.');
INSERT INTO "junagarri" VALUES(28,56,'28:56','آپ جسے چاہیں ہدایت نہیں کر سکتے بلکہ اللہ تعالیٰ ہی جسے چاہے ہدایت کرتا ہے۔ ہدایت والوں سے وہی خوب آگاه ہے. ');
INSERT INTO "junagarri" VALUES(28,57,'28:57','کہنے لگے اگر ہم آپ کے ساتھ ہوکر ہدایت کے تابع دار بن جائیں تو ہم تو اپنے ملک سے اچک لیے جائیں ، کیا ہم نے انہیں امن وامان اور حرمت والے حرم میں جگہ نہیں دی ؟ جہاں تمام چیزوں کے پھل کِھچے چلے آتے ہیں جو ہمارے پاس بطور رزق کے ہیں ، لیکن ان میں سے اکثر کچھ نہیں جانتے.');
INSERT INTO "junagarri" VALUES(28,58,'28:58','اور ہم نے بہت سی وه بستیاں تباه کر دیں جو اپنی عیش و عشرت میں اترانے لگی تھیں، یہ ہیں ان کی رہائش کی جگہیں جو ان کے بعد بہت ہی کم آباد کی گئیں اور ہم ہی ہیں آخر سب کچھ کے وارث. ');
INSERT INTO "junagarri" VALUES(28,59,'28:59','تیرا رب کسی ایک بستی کو بھی اس وقت تک ہلاک نہیں کرتا جب تک کہ ان کی کسی بڑی بستی میں اپنا کوئی پیغمبر نہ بھیج دے جو انہیں ہماری آیتیں پڑھ کر سنا دے اور ہم بستیوں کو اسی وقت ہلاک کرتے ہیں جب کہ وہاں والے ظلم وستم پر کمر کس لیں. ');
INSERT INTO "junagarri" VALUES(28,60,'28:60','اور تمہیں جو کچھ دیا گیا ہے وه صرف زندگی دنیا کا سامان اور اسی کی رونق ہے، ہاں اللہ کے پاس جو ہے وه بہت ہی بہتر اور دیرپا ہے۔ کیا تم نہیں سمجھتے. ');
INSERT INTO "junagarri" VALUES(28,61,'28:61','کیا وه شخص جس سے ہم نے نیک وعده کیا ہے جسے وه قطعاً پانے والا ہے مثل اس شخص کے ہوسکتا ہے؟ جسے ہم نے زندگانیٴ دنیا کی کچھ یونہی سی منفعت دے دی پھر بالآخر وه قیامت کے روز پکڑا باندھا حاضر کیا جائے گا. ');
INSERT INTO "junagarri" VALUES(28,62,'28:62','اور جس دن اللہ تعالیٰ انہیں پکار کر فرمائے گا کہ تم جنہیں اپنے گمان میں میرا شریک ٹھہرا رہے تھے کہاں ہیں. ');
INSERT INTO "junagarri" VALUES(28,63,'28:63','جن پر بات آچکی وه جواب دیں گے کہ اے ہمارے پروردگار! یہی وه ہیں جنہیں ہم نے بہکا رکھا تھا ، ہم نے انہیں اسی طرح بہکایا جس طرح ہم بہکے تھے ، ہم تیری سرکار میں اپنی دست برداری کرتے ہیں ، یہ ہماری عبادت نہیں کرتے تھے. ');
INSERT INTO "junagarri" VALUES(28,64,'28:64','کہا جائے گا کہ اپنے شریکوں کو بلاؤ ، وه بلائیں گے لیکن انہیں وه جواب تک نہ دیں گے اور سب عذاب دیکھ لیں گے ، کاش یہ لوگ ہدایت پا لیتے. ');
INSERT INTO "junagarri" VALUES(28,65,'28:65','اس دن انہیں بلا کر پوچھے گا کہ تم نے نبیوں کو کیا جواب دیا؟ ');
INSERT INTO "junagarri" VALUES(28,66,'28:66','پھر تو اس دن ان کی تمام دلیلیں گم ہو جائیں گی اور ایک دوسرے سے سوال تک نہ کریں گے. ');
INSERT INTO "junagarri" VALUES(28,67,'28:67','ہاں جو شخص توبہ کرلے ایمان لے آئے اور نیک کام کرے یقین ہے کہ وه نجات پانے والوں میں سے ہو جائے گا.');
INSERT INTO "junagarri" VALUES(28,68,'28:68','اور آپ کا رب جو چاہتا ہے پیدا کرتا ہے اور جسے چاہتا ہے چن لیتا ہے، ان میں سے کسی کو کوئی اختیار نہیں ، اللہ ہی کے لیے پاکی ہے وه بلند تر ہے ہر اس چیز سے کہ لوگ شریک کرتے ہیں.');
INSERT INTO "junagarri" VALUES(28,69,'28:69','ان کے سینے جو کچھ چھپاتے اور جو کچھ ظاہر کرتے ہیں آپ کا رب سب کچھ جانتا ہے.');
INSERT INTO "junagarri" VALUES(28,70,'28:70','وہی اللہ ہے اس کے سوا کوئی لائق عبادت نہیں، دنیا اور آخرت میں اسی کی تعریف ہے۔ اسی کے لیے فرمانروائی ہے اور اسی کی طرف تم سب پھیرے جاؤ گے.');
INSERT INTO "junagarri" VALUES(28,71,'28:71','کہہ دیجئے! کہ دیکھو تو سہی اگر اللہ تعالیٰ تم پر رات ہی رات قیامت تک برابر کر دے تو سوائے اللہ کے کون معبود ہے جو تمہارے پاس دن کی روشنی لائے؟ کیا تم سنتے نہیں ہو؟');
INSERT INTO "junagarri" VALUES(28,72,'28:72','پوچھئے! کہ یہ بھی بتا دو کہ اگر اللہ تعالیٰ تم پر ہمیشہ قیامت تک دن ہی دن رکھے تو بھی سوائے اللہ تعالیٰ کے کوئی معبود ہے جو تمہارے پاس رات لے آئے؟ جس میں تم آرام حاصل کرو، کیا تم دیکھ نہیں رہے ہو؟');
INSERT INTO "junagarri" VALUES(28,73,'28:73','اسی نے تو تمہارے لیے اپنے فضل وکرم سے دن رات مقرر کر دیے ہیں کہ تم رات میں آرام کرو اور دن میں اس کی بھیجی ہوئی روزی تلاش کرو ، یہ اس لیے کہ تم شکر ادا کرو. ');
INSERT INTO "junagarri" VALUES(28,74,'28:74','اور جس دن انہیں پکار کر اللہ تعالیٰ فرمائے گا کہ جنہیں تم میرے شریک خیال کرتے تھے وه کہاں ہیں؟');
INSERT INTO "junagarri" VALUES(28,75,'28:75','اور ہم ہر امت میں سے ایک گواه الگ کرلیں گے کہ اپنی دلیلیں پیش کرو پس اس وقت جان لیں گے کہ حق اللہ تعالیٰ کی طرف ہے ، اور جو کچھ افترا وه جوڑتے تھے سب ان کے پاس سے کھو جائے گا. ');
INSERT INTO "junagarri" VALUES(28,76,'28:76','قارون تھا تو قوم موسیٰ سے، لیکن ان پر ظلم کرنے لگا تھا ہم نے اسے (اس قدر) خزانے دے رکھے تھے کہ کئی کئی طاقت ور لوگ بہ مشکل اس کی کنجیاں اٹھا سکتے تھے ، ایک بار اس کی قوم نے اس سے کہا کہ اترا مت ! اللہ تعالیٰ اترانے والوں سے محبت نہیں رکھتا. ');
INSERT INTO "junagarri" VALUES(28,77,'28:77','اور جو کچھ اللہ تعالی نے تجھے دے رکھا ہے اس میں سے آخرت کے گھر کی تلاش بھی رکھ اور اپنے دنیوی حصے کو بھی نہ بھول اور جیسے کہ اللہ نے تیرے ساتھ احسان کیا ہے تو بھی اچھا سلوک کر اور ملک میں فساد کا خواہاں نہ ہو ، یقین مان کہ اللہ مفسدوں کو ناپسند رکھتا ہے.');
INSERT INTO "junagarri" VALUES(28,78,'28:78','قارون نے کہا یہ سب کچھ مجھے میری اپنی سمجھ کی بنا پر ہی دیا گیا ہے ، کیا اسے اب تک یہ نہیں معلوم کہ اللہ تعالیٰ نے اس سے پہلے بہت سے بستی والوں کو غارت کر دیا جو اس سے بہت زیاده قوت والے اور بہت بڑی جمع پونجی والے تھے ۔ اور گنہگاروں سے ان کے گناہوں کی باز پرس ایسے وقت نہیں کی جاتی. ');
INSERT INTO "junagarri" VALUES(28,79,'28:79','پس قارون پوری آرائش کے ساتھ اپنی قوم کے مجمع میں نکلا ، تو دنیاوی زندگی کے متوالے کہنے لگے کاش کہ ہمیں بھی کسی طرح وه مل جاتا جو قارون کو دیا گیا ہے۔ یہ تو بڑا ہی قسمت کا دھنی ہے.');
INSERT INTO "junagarri" VALUES(28,80,'28:80','ذی علم لوگ انہیں سمجھانے لگے کہ افسوس! بہتر چیز تو وه ہے جو بطور ثواب انہیں ملے گی جو اللہ پر ایمان لائیں اور نیک عمل کریں یہ بات انہی کے دل میں ڈالی جاتی ہے جو صبر وسہارا والے ہوں.');
INSERT INTO "junagarri" VALUES(28,81,'28:81','(آخرکار) ہم نے اسے اس کے محل سمیت زمین میں دھنسا دیا اور اللہ کے سوا کوئی جماعت اس کی مدد کے لیے تیار نہ ہوئی نہ وه خود اپنے بچانے والوں میں سے ہو سکا.');
INSERT INTO "junagarri" VALUES(28,82,'28:82','اور جو لوگ کل اس کے مرتبہ پر پہنچنے کی آرزو مندیاں کر رہے تھے وه آج کہنے لگے کہ کیا تم نہیں دیکھتے کہ اللہ تعالیٰ ہی اپنے بندوں میں سے جس کے لیے چاہے روزی کشاده کر دیتا ہے اور تنگ بھی؟ اگر اللہ تعالیٰ ہم پر فضل نہ کرتا تو ہمیں بھی دھنسا دیتا ، کیا دیکھتے نہیں ہو کہ ناشکروں کو کبھی کامیابی نہیں ہوتی؟ ');
INSERT INTO "junagarri" VALUES(28,83,'28:83','آخرت کا یہ بھلا گھر ہم ان ہی کے لیے مقرر کر دیتے ہیں جو زمین میں اونچائی بڑائی اور فخر نہیں کرتے نہ فساد کی چاہت رکھتے ہیں۔ پرہیزگاروں کے لیے نہایت ہی عمده انجام ہے. ');
INSERT INTO "junagarri" VALUES(28,84,'28:84','جو شخص نیکی لائے گا اسے اس سے بہتر ملے گا اور جو برائی لے کر آئے گا تو ایسے بداعمالی کرنے والوں کو ان کے انہی اعمال کا بدلہ دیا جائے گا جو وه کرتے تھے. ');
INSERT INTO "junagarri" VALUES(28,85,'28:85','جس اللہ نے آپ پر قرآن نازل فرمایا ہے وه آپ کو دوباره پہلی جگہ لانے والا ہے ، کہہ دیجئے! کہ میرا رب اسے بھی بخوبی جانتا ہے جو ہدایت لایا ہے اور اسے بھی جو کھلی گمراہی میں ہے. ');
INSERT INTO "junagarri" VALUES(28,86,'28:86','آپ کو تو کبھی اس کا خیال بھی نہ گزرا تھا کہ آپ کی طرف کتاب نازل فرمائی جائے گی لیکن یہ آپ کے رب کی مہربانی سے اترا ۔ اب آپ کو ہرگز کافروں کا مددگار نہ ہونا چاہیئے. ');
INSERT INTO "junagarri" VALUES(28,87,'28:87','خیال رکھیئے کہ یہ کفار آپ کو اللہ تعالیٰ کی آیتوں کی تبلیغ سے روک نہ دیں اس کے بعد کہ یہ آپ کی جانب اتاری گئیں، تو اپنے رب کی طرف بلاتے رہیں اور شرک کرنے والوں میں سے نہ ہوں.');
INSERT INTO "junagarri" VALUES(28,88,'28:88','اللہ تعالیٰ کے ساتھ کسی اور معبود کو نہ پکارنا بجز اللہ تعالیٰ کے کوئی اور معبود نہیں، ہر چیز فنا ہونے والی ہے مگر اسی کا منھ (اور ذات) ۔ اسی کے لیے فرمانروائی ہے اور تم اسی کی طرف لوٹائے جاؤ گے. ');
INSERT INTO "junagarri" VALUES(29,1,'29:1','الم.');
INSERT INTO "junagarri" VALUES(29,2,'29:2','کیا لوگوں نے یہ گمان کر رکھا ہے کہ ان کے صرف اس دعوے پر کہ ہم ایمان لائے ہیں ہم انہیں بغیر آزمائے ہوئے ہی چھوڑ دیں گے؟. ');
INSERT INTO "junagarri" VALUES(29,3,'29:3','ان سے اگلوں کو بھی ہم نے خوب جانچا ۔ یقیناً اللہ تعالیٰ انہیں بھی جان لے گا جو سچ کہتے ہیں اور انہیں بھی معلوم کرلے گا جو جھوٹے ہیں.');
INSERT INTO "junagarri" VALUES(29,4,'29:4','کیا جو لوگ برائیاں کر رہے ہیں انہوں نے یہ سمجھ رکھا ہے کہ وه ہمارے قابو سے باہر ہو جائیں گے ، یہ لوگ کیسی بری تجویزیں کر رہے ہیں. ');
INSERT INTO "junagarri" VALUES(29,5,'29:5','جسے اللہ کی ملاقات کی امید ہو پس اللہ کا ٹھہرایا ہوا وقت یقیناً آنے والا ہے ، وه سب کچھ سننے والا، سب کچھ جاننے والا ہے. ');
INSERT INTO "junagarri" VALUES(29,6,'29:6','اور ہر ایک کوشش کرنے والا اپنے ہی بھلے کی کوشش کرتا ہے۔ ویسے تو اللہ تعالیٰ تمام جہان والوں سے بےنیاز ہے. ');
INSERT INTO "junagarri" VALUES(29,7,'29:7','اور جن لوگوں نے یقین کیا اور مطابق سنت کام کیے ہم ان کے تمام گناہوں کو ان سے دور کر دیں گے اور انہیں ان کے نیک اعمال کے بہترین بدلے دیں گے. ');
INSERT INTO "junagarri" VALUES(29,8,'29:8','ہم نے ہر انسان کو اپنے ماں باپ کے ساتھ اچھا سلوک کرنے کی نصیحت کی ہے ہاں اگر وه یہ کوشش کریں کہ آپ میرے ساتھ اسے شریک کرلیں جس کا آپ کو علم نہیں تو ان کا کہنا نہ مانیئے ، تم سب کا لوٹنا میری ہی طرف ہے پھر میں ہر اس چیز سے جو تم کرتے تھے تمہیں خبر دوں گا.');
INSERT INTO "junagarri" VALUES(29,9,'29:9','اور جن لوگوں نے ایمان قبول کیا اور نیک کام کیے انہیں میں اپنے نیک بندوں میں شمار کر لوں گا. ');
INSERT INTO "junagarri" VALUES(29,10,'29:10','اور بعض لوگ ایسے بھی ہیں جو زبانی کہتے ہیں کہ ہم ایمان لائے ہیں لیکن جب اللہ کی راه میں کوئی مشکل آن پڑتی ہے تو لوگوں کی ایذا دہی کو اللہ تعالیٰ کے عذاب کی طرح بنا لیتے ہیں ، ہاں اگر اللہ کی مدد آجائے تو پکار اٹھتے ہیں کہ ہم تو تمہارے ساتھی ہی ہیں کیا دنیا جہان کے سینوں میں جو کچھ ہے اس سے اللہ تعالیٰ دانا نہیں ہے؟ ');
INSERT INTO "junagarri" VALUES(29,11,'29:11','جو لوگ ایمان لائے اللہ انہیں بھی ظاہر کرکے رہے گا اور منافقوں کو بھی ظاہر کرکے رہے گا. ');
INSERT INTO "junagarri" VALUES(29,12,'29:12','کافروں نے ایماں والوں سے کہا کہ تم ہماری راه کی تابعداری کرو تمہارے گناه ہم اٹھا لیں گے ، حالانکہ وه ان کے گناہوں میں سے کچھ بھی نہیں اٹھانے والے، یہ تو محض جھوٹے ہیں. ');
INSERT INTO "junagarri" VALUES(29,13,'29:13','البتہ یہ اپنے بوجھ ڈھولیں گے اور اپنے بوجھوں کے ساتھ ہی اور بوجھ بھی ۔ اور جو کچھ افترا پردازیاں کر رہے ہیں ان سب کی بابت ان سے باز پرس کی جائے گی.');
INSERT INTO "junagarri" VALUES(29,14,'29:14','اور ہم نے نوح (علیہ السلام) کو ان کی قوم کی طرف بھیجا وه ان میں ساڑھے نو سو سال تک رہے ، پھر تو انہیں طوفان نے دھر پکڑا اور وه تھے بھی ظالم.');
INSERT INTO "junagarri" VALUES(29,15,'29:15','پھر ہم نے انہیں اور کشتی والوں کو نجات دی اور اس واقعہ کو ہم نے تمام جہان کے لیے عبرت کا نشان بنا دیا.');
INSERT INTO "junagarri" VALUES(29,16,'29:16','اور ابراہیم (علیہ السلام) نے بھی اپنی قوم سے فرمایا کہ اللہ تعالیٰ کی عبادت کرو اوراس سے ڈرتے رہو، اگر تم میں دانائی ہے تو یہی تمہارے لیے بہتر ہے.');
INSERT INTO "junagarri" VALUES(29,17,'29:17','تم تو اللہ تعالیٰ کے سوا بتوں کی پوجا پاٹ کر رہے ہو اور جھوٹی باتیں دل سے گھڑ لیتے ہو ۔ سنو! جن جنکی تم اللہ تعالیٰ کے سوا پوجا پاٹ کر رہے ہو وه تو تمہاری روزی کے مالک نہیں پس تمہیں چاہئے کہ تم اللہ تعالیٰ ہی سے روزیاں طلب کرو اور اسی کی عبادت کرو اور اسی کی شکر گزاری کرو اور اسی کی طرف تم لوٹائے جاؤ گے. ');
INSERT INTO "junagarri" VALUES(29,18,'29:18','اور اگر تم جھٹلاؤ تو تم سے پہلے کی امتوں نے بھی جھٹلایا ہے ، رسول کے ذمہ تو صرف صاف طور پر پہنچا دینا ہی ہے. ');
INSERT INTO "junagarri" VALUES(29,19,'29:19','کیا انہوں نے نہیں دیکھا کہ مخلوق کی ابتدا کس طرح اللہ نے کی پھر اللہ اس کا اعاده کرے گا ، یہ تو اللہ تعالیٰ پر بہت ہی آسان ہے. ');
INSERT INTO "junagarri" VALUES(29,20,'29:20','کہہ دیجئے! کہ زمین میں چل پھر کر دیکھو تو سہی کہ کس طرح اللہ تعالیٰ نے ابتداءً پیدائش کی۔ پھر اللہ تعالیٰ ہی دوسری نئی پیدائش کرے گا، اللہ تعالیٰ ہر چیز پر قادر ہے.');
INSERT INTO "junagarri" VALUES(29,21,'29:21','جسے چاہے عذاب کرے جس پر چاہے رحم کرے، سب اسی کی طرف لوٹائے جاؤ گے. ');
INSERT INTO "junagarri" VALUES(29,22,'29:22','تم نہ تو زمین میں اللہ تعالیٰ کو عاجز کرسکتے ہو نہ آسمان میں، اللہ تعالیٰ کے سوا تمہارا کوئی والی ہے نہ مددگار.');
INSERT INTO "junagarri" VALUES(29,23,'29:23','جو لوگ اللہ تعالیٰ کی آیتوں اور اس کی ملاقات کو بھلاتے ہیں وه میری رحمت سے ناامید ہو جائیں اور ان کے لیے دردناک عذاب ہے.');
INSERT INTO "junagarri" VALUES(29,24,'29:24','ان کی قوم کا جواب بجز اس کے کچھ نہ تھا کہ کہنے لگے کہ اسے مار ڈالو یا اسے جلا دو ۔ آخر اللہ نے انہیں آگ سے بچا لیا ، اس میں ایمان والے لوگوں کے لیے تو بہت سی نشانیاں ہیں.');
INSERT INTO "junagarri" VALUES(29,25,'29:25','(حضرت ابراہیم علیہ السلام نے) کہا کہ تم نے جن بتوں کی پرستش اللہ کے سوا کی ہے انہیں تم نے اپنی آپس کی دنیوی دوستی کی بنا ٹھہرا لی ہے ، تم سب قیامت کے دن ایک دوسرے سے کفر کرنے لگو گے اور ایک دوسرے پر لعنت کرنے لگو گے۔ اور تمہارا سب کا ٹھکانہ دوزخ ہوگا اور تمہارا کوئی مددگار نہ ہوگا.');
INSERT INTO "junagarri" VALUES(29,26,'29:26','پس حضرت ابراہیم (علیہ السلام) پر حضرت لوط (علیہ السلام) ایمان لائے اور کہنے لگے کہ میں اپنے رب کی طرف ہجرت کرنے والا ہوں ۔ وه بڑا ہی غالب اور حکیم ہے.');
INSERT INTO "junagarri" VALUES(29,27,'29:27','اور ہم نے انھیں (ابراہیم کو) اسحاق ویعقوب (علیہما السلام) عطا کیے اور ہم نے نبوت اور کتاب ان کی اولاد میں ہی کر دی اور ہم نے دنیا میں بھی اسے ثواب دیا اور آخرت میں تو وه صالح لوگوں میں سے ہیں. ');
INSERT INTO "junagarri" VALUES(29,28,'29:28','اور حضرت لوط (علیہ السلام) کا بھی ذکر کرو جب کہ انہوں نے اپنی قوم سے فرمایا کہ تم تو اس بدکاری پر اتر آئے ہو جسے تم سے پہلے دنیا بھر میں سے کسی نے نہیں کیا.');
INSERT INTO "junagarri" VALUES(29,29,'29:29','کیا تم مردوں کے پاس بدفعلی کے لیے آتے ہو اور راستے بند کرتے ہو اور اپنی عام مجلسوں میں بے حیائیوں کا کام کرتے ہو ؟ اس کے جواب میں اس کی قوم نے بجز اس کے اور کچھ نہیں کہا کہ بس جا اگر سچا ہے تو ہمارے پاس اللہ تعالیٰ کا عذاب لے آ.');
INSERT INTO "junagarri" VALUES(29,30,'29:30','حضرت لوط (علیہ السلام) نے دعا کی کہ پروردگار! اس مفسد قوم پر میری مدد فرما.');
INSERT INTO "junagarri" VALUES(29,31,'29:31','اور جب ہمارے بھیجے ہوئے فرشتے ابراہیم (علیہ السلام) کے پاس بشارت لے کر پہنچے کہنے لگے کہ اس بستی والوں کو ہم ہلاک کرنے والے ہیں ، یقیناً یہاں کے رہنے والے گنہگار ہیں.');
INSERT INTO "junagarri" VALUES(29,32,'29:32','(حضرت ابراہیم علیہ السلام نے) کہا اس میں تو لوط (علیہ السلام) ہیں، فرشتوں نے کہا یہاں جو ہیں ہم انہیں بخوبی جانتے ہیں ۔ لوط (علیہ السلام) کو اور اس کے خاندان کو سوائے اس کی بیوی کے ہم بچالیں گے، البتہ وه عورت پیچھے ره جانے والوں میں سے ہے. ');
INSERT INTO "junagarri" VALUES(29,33,'29:33','پھر جب ہمارے قاصد لوط (علیہ السلام) کے پاس پہنچے تو وه ان کی وجہ سے غمگین ہوئے اور دل ہی دل میں رنج کرنے لگے ۔ قاصدوں نے کہا آپ نہ خوف کھایئے نہ آزرده ہوں، ہم آپ کو مع آپ کے متعلقین کے بچالیں گے مگر آپ کی بیوی کہ وه عذاب کے لیے باقی ره جانے والوں میں سے ہوگی.');
INSERT INTO "junagarri" VALUES(29,34,'29:34','ہم اس بستی والوں پر آسمانی عذاب نازل کرنے والے ہیں اس وجہ سے کہ یہ بےحکم ہو رہے ہیں.');
INSERT INTO "junagarri" VALUES(29,35,'29:35','البتہ ہم نے اس بستی کو صریح عبرت کی نشانی بنا دیا ان لوگوں کے لیے جو عقل رکھتے ہیں. ');
INSERT INTO "junagarri" VALUES(29,36,'29:36','اور مدین کی طرف ہم نے ان کے بھائی شعیب (علیہ السلام) کو بھیجا انہوں نے کہا اے میری قوم کے لوگو! اللہ کی عبادت کرو قیامت کے دن کی توقع رکھو اور زمین میں فساد نہ کرتے پھرو. ');
INSERT INTO "junagarri" VALUES(29,37,'29:37','پھر بھی انہوں نے انہیں جھٹلایا آخر انہیں زلزلے نے پکڑ لیا اور وه اپنے گھروں میں بیٹھے کے بیٹھے مرده ہو کر ره گئے. ');
INSERT INTO "junagarri" VALUES(29,38,'29:38','اور ہم نے عادیوں اور ثمودیوں کو بھی غارت کیا جن کے بعض مکانات تمہارے سامنے ظاہر ہیں اور شیطان نے انہیں ان کی بداعمالیاں آراستہ کر دکھائی تھیں اور انہیں راه سے روک دیا تھا باوجودیکہ یہ آنکھوں والے اور ہوشیار تھے. ');
INSERT INTO "junagarri" VALUES(29,39,'29:39','اور قارون اور فرعون اور ہامان کو بھی، ان کے پاس حضرت موسیٰ (علیہ السلام) کھلے کھلے معجزے لے کر آئے تھے پھر بھی انہوں نے زمین میں تکبر کیا لیکن ہم سے آگے بڑھنے والے نہ ہو سکے. ');
INSERT INTO "junagarri" VALUES(29,40,'29:40','پھر تو ہر ایک کو ہم نے اس کے گناه کے وبال میں گرفتار کر لیا ، ان میں سے بعض پر ہم نے پتھروں کا مینہ برسایا اور ان میں سے بعض کو زور دار سخت آواز نے دبوچ لیا اور ان میں سے بعض کو ہم نے زمین میں دھنسا دیا اور ان میں سے بعض کو ہم نے ڈبو دیا ، اللہ تعالیٰ ایسا نہیں کہ ان پر ظلم کرے بلکہ یہی لوگ اپنی جانوں پر ظلم کرتے تھے. ');
INSERT INTO "junagarri" VALUES(29,41,'29:41','جن لوگوں نے اللہ تعالیٰ کے سوا اور کارساز مقرر کر رکھے ہیں ان کی مثال مکڑی کی سی ہے کہ وه بھی ایک گھر بنا لیتی ہے، حالانکہ تمام گھروں سے زیاده بودا گھر مکڑی کا گھر ہی ہے ، کاش! وه جان لیتے.');
INSERT INTO "junagarri" VALUES(29,42,'29:42','اللہ تعالیٰ ان تمام چیزوں کو جانتا ہے جنہیں وه اس کے سوا پکار رہے ہیں، وه زبردست اور ذی حکمت ہے');
INSERT INTO "junagarri" VALUES(29,43,'29:43','ہم ان مثالوں کو لوگوں کے لیے بیان فرما رہے ہیں انہیں صرف علم والے ہی سمجھتے ہیں. ');
INSERT INTO "junagarri" VALUES(29,44,'29:44','اللہ تعالیٰ نے آسمانوں اور زمین کو مصلحت اور حق کے ساتھ پیدا کیا ہے ، ایمان والوں کے لیے تو اس میں بڑی بھاری دلیل ہے. ');
INSERT INTO "junagarri" VALUES(29,45,'29:45','جو کتاب آپ کی طرف وحی کی گئی ہے اسے پڑھئے اور نماز قائم کریں ، یقیناً نماز بے حیائی اور برائی سے روکتی ہے ، بیشک اللہ کا ذکر بہت بڑی چیز ہے ، تم جو کچھ کر رہے ہو اس سے اللہ خبردار ہے.');
INSERT INTO "junagarri" VALUES(29,46,'29:46','اور اہل کتاب کے ساتھ بحث ومباحثہ نہ کرو مگر اس طریقہ پر جو عمده ہو مگر ان کے ساتھ جو ان میں ظالم ہیں اور صاف اعلان کر دو کہ ہمارا تو اس کتاب پر بھی ایمان ہے اور جو ہم پر اتاری گئی ہے اور اس پر بھی جو تم پر اتاری گئی ، ہمارا تمہارا معبود ایک ہی ہے۔ ہم سب اسی کے حکم برادر ہیں.');
INSERT INTO "junagarri" VALUES(29,47,'29:47','اور ہم نے اسی طرح آپ کی طرف اپنی کتاب نازل فرمائی ہے، پس جنہیں ہم نے کتاب دی ہے وه اس پر ایمان لاتے ہیں اور ان (مشرکین) میں سے بعض اس پر ایمان رکھتے ہیں اور ہماری آیتوں کا انکار صرف کافر ہی کرتے ہیں.');
INSERT INTO "junagarri" VALUES(29,48,'29:48','اس سے پہلے تو آپ کوئی کتاب پڑھتے نہ تھے اور نہ کسی کتاب کو اپنے ہاتھ سے لکھتے تھے کہ یہ باطل پرست لوگ شک وشبہ میں پڑتے. ');
INSERT INTO "junagarri" VALUES(29,49,'29:49','بلکہ یہ (قرآن) تو روشن آیتیں ہیں جو اہل علم کے سینوں میں محفوظ ہیں ، ہماری آیتوں کا منکر بجز ظالموں کے اور کوئی نہیں.');
INSERT INTO "junagarri" VALUES(29,50,'29:50','انہوں نے کہا کہ اس پر کچھ نشانیاں (معجزات) اس کے رب کی طرف سے کیوں نہیں اتارے گئے۔ آپ کہہ دیجئے کہ نشانیاں تو سب اللہ تعالیٰ کے پاس ہیں میں تو صرف کھلم کھلا آگاه کر دینے والا ہوں.');
INSERT INTO "junagarri" VALUES(29,51,'29:51','کیا انہیں یہ کافی نہیں؟ کہ ہم نے آپ پر کتاب نازل فرما دی جو ان پر پڑھی جا رہی ہے ، اس میں رحمت (بھی) ہے اور نصیحت (بھی) ہے ان لوگوں کے لئے جو ایمان والے ہیں. ');
INSERT INTO "junagarri" VALUES(29,52,'29:52','کہہ دیجئے کہ مجھ میں اور تم میں اللہ تعالیٰ گواه ہونا کافی ہے وه آسمان وزمین کی ہر چیز کا عالم ہے، جو لوگ باطل کے ماننے والے اور اللہ تعالیٰ سے کفر کرنے والے ہیں وه زبردست نقصان اور گھاٹے میں ہیں. ');
INSERT INTO "junagarri" VALUES(29,53,'29:53','یہ لوگ آپ سے عذاب کی جلدی کر رہے ہیں ۔ اگر میری طرف سے مقرر کیا ہوا وقت نہ ہوتا تو ابھی تک ان کے پاس عذاب آچکا ہوتا ، یہ یقینی بات ہے کہ اچانک ان کی بے خبری میں ان کے پاس عذاب آپہنچے گا. ');
INSERT INTO "junagarri" VALUES(29,54,'29:54','یہ عذاب کی جلدی مچا رہے ہیں اور (تسلی رکھیں) جہنم کافروں کو گھیر لینے والی ہے. ');
INSERT INTO "junagarri" VALUES(29,55,'29:55','اس دن ان کے اوپر تلے سے عذاب ڈھانپ رہا ہوگا اور اللہ تعالیٰ فرمائے گا کہ اب اپنے (بد) اعمال کا مزه چکھو.');
INSERT INTO "junagarri" VALUES(29,56,'29:56','اے میرے ایمان والے بندو! میری زمین بہت کشاده ہے سو تم میری ہی عبادت کرو. ');
INSERT INTO "junagarri" VALUES(29,57,'29:57','ہر جاندار موت کا مزه چکھنے والا ہےاور تم سب ہماری ہی طرف لوٹائے جاؤ گے. ');
INSERT INTO "junagarri" VALUES(29,58,'29:58','اور جولوگ ایمان لائے اور نیک کام کیے انہیں ہم یقیناً جنت کے ان بالا خانوں میں جگہ دینگے جن کے نیچے چشمے بہہ رہے ہیں جہاں وه ہمیشہ رہیں گے ، کام کرنے والوں کا کیا ہی اچھا اجر ہے.');
INSERT INTO "junagarri" VALUES(29,59,'29:59','وه جنہوں نے صبر کیا ، اور اپنے رب تعالیٰ پر بھروسہ رکھتے ہیں. ');
INSERT INTO "junagarri" VALUES(29,60,'29:60','اور بہت سے جانور ہیں جو اپنی روزی اٹھائے نہیں پھرتے ، ان سب کو اور تمہیں بھی اللہ تعالیٰ ہی روزی دیتا ہے ، وه بڑا ہی سننے جاننے والا ہے. ');
INSERT INTO "junagarri" VALUES(29,61,'29:61','اور اگر آپ ان سے دریافت کریں کہ زمین وآسمان کا خالق اور سورج چاند کو کام میں لگانے والا کون ہے؟ تو ان کا جواب یہی ہوگا کہ اللہ تعالیٰ ، پھر کدھر الٹے جا رہے ہیں. ');
INSERT INTO "junagarri" VALUES(29,62,'29:62','اللہ تعالیٰ اپنے بندوں میں سے جسے چاہے فراخ روزی دیتا ہے اور جسے چاہے تنگ ۔ یقیناً اللہ تعالیٰ ہر چیز کا جاننے والا ہے. ');
INSERT INTO "junagarri" VALUES(29,63,'29:63','اور اگر آپ ان سے سوال کریں کہ آسمان سے پانی اتار کر زمین کو اس کی موت کے بعد زنده کس نے کیا؟ تو یقیناً ان کا جواب یہی ہوگا اللہ تعالیٰ نے۔ آپ کہہ دیں کہ ہر تعریف اللہ ہی کے لئے سزاوار ہے، بلکہ ان میں سے اکثر بے عقل ہیں. ');
INSERT INTO "junagarri" VALUES(29,64,'29:64','اور دنیا کی یہ زندگانی تو محض کھیل تماشا ہے البتہ آخرت کے گھر کی زندگی ہی حقیقی زندگی ہے ، کاش! یہ جانتے ہوتے. ');
INSERT INTO "junagarri" VALUES(29,65,'29:65','پس یہ لوگ جب کشتیوں میں سوار ہوتے ہیں تو اللہ تعالیٰ ہی کو پکارتے ہیں اس کے لئے عبادت کو خالص کر کے پھر جب وه انہیں خشکی کی طرف بچا لاتا ہے تو اسی وقت شرک کرنے لگتے ہیں. ');
INSERT INTO "junagarri" VALUES(29,66,'29:66','تاکہ ہماری دی ہوئی نعمتوں سے مکرتے رہیں اور برتتے رہیں ۔ ابھی ابھی پتہ چل جائے گا.');
INSERT INTO "junagarri" VALUES(29,67,'29:67','کیا یہ نہیں دیکھتے کہ ہم نے حرم کو با امن بنا دیا ہے حالانکہ ان کے اردگرد سے لوگ اچک لیے جاتے ہیں ، کیا یہ باطل پر تو یقین رکھتے ہیں اور اللہ تعالیٰ کی نعمتوں پر ناشکری کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(29,68,'29:68','اور اس سے بڑا ظالم کون ہوگا؟ جو اللہ تعالیٰ پر جھوٹ باندھے یا جب حق اس کے پاس آجائے وه اسے جھٹلائے، کیا ایسے کافروں کا ٹھکانا جہنم میں نہ ہوگا؟');
INSERT INTO "junagarri" VALUES(29,69,'29:69','اور جو لوگ ہماری راه میں مشقتیں برداشت کرتے ہیں ہم انہیں اپنی راہیں ضرور دکھا دیں گے ۔ یقیناً اللہ تعالیٰ نیکو کاروں کا ساتھی ہے. ');
INSERT INTO "junagarri" VALUES(30,1,'30:1','الم.');
INSERT INTO "junagarri" VALUES(30,2,'30:2','رومی مغلوب ہوگئے ہیں.');
INSERT INTO "junagarri" VALUES(30,3,'30:3','نزدیک کی زمین پر اور وه مغلوب ہونے کے بعد عنقریب غالب آجائیں گے.');
INSERT INTO "junagarri" VALUES(30,4,'30:4','چند سال میں ہی۔ اس سے پہلے اور اس کے بعد بھی اختیار اللہ تعالیٰ ہی کا ہے۔ اس روز مسلمان شادمان ہوں گے.');
INSERT INTO "junagarri" VALUES(30,5,'30:5','اللہ کی مدد سے ، وه جس کی چاہتا ہے مدد کرتا ہے۔ اصل غالب اور مہربان وہی ہے.');
INSERT INTO "junagarri" VALUES(30,6,'30:6','اللہ کا وعده ہے ، اللہ تعالیٰ اپنے وعدے کا خلاف نہیں کرتا لیکن اکثر لوگ نہیں جانتے.');
INSERT INTO "junagarri" VALUES(30,7,'30:7','وه تو (صرف) دنیوی زندگی کے ظاہر کو (ہی) جانتے ہیں اور آخرت سے تو بالکل ہی بےخبر ہیں. ');
INSERT INTO "junagarri" VALUES(30,8,'30:8','کیا ان لوگوں نے اپنے دل میں یہ غور نہیں کیا؟ کہ اللہ تعالیٰ نے آسمانوں کو اور زمین اور ان کے درمیان جو کچھ ہے سب کو بہترین قرینے سے مقرر وقت تک کے لئے (ہی) پیدا کیا ہے، ہاں اکثر لوگ یقیناً اپنے رب کی ملاقات کے منکر ہیں. ');
INSERT INTO "junagarri" VALUES(30,9,'30:9','کیا انہوں نے زمین پر چل پھر کر یہ نہیں دیکھا کہ ان سے پہلے لوگوں کا انجام کیسا (برا) ہوا ؟ وه ان سے بہت زیاده توانا (اور طاقتور) تھے اور انہوں نے (بھی) زمین بوئی جوتی تھی اور ان سے زیاده آباد کی تھی اور ان کے پاس ان کے رسول روشن دلائل لے کر آئے تھے ۔ یہ تو ناممکن تھا کہ اللہ تعالیٰ ان پر ظلم کرتا لیکن (دراصل) وه خود اپنی جانوں پر ظلم کرتے تھے. ');
INSERT INTO "junagarri" VALUES(30,10,'30:10','پھر آخر برا کرنے والوں کا بہت ہی برا انجام ہوا ، اس لئے کہ وه اللہ تعالیٰ کی آیتوں کو جھٹلاتے اور ان کی ہنسی اڑاتے تھے.');
INSERT INTO "junagarri" VALUES(30,11,'30:11','اللہ تعالیٰ ہی مخلوق کی ابتدا کرتا ہے پھر وہی اسے دوباره پیدا کرے گا پھر تم سب اسی کی طرف لوٹائے جاؤ گے. ');
INSERT INTO "junagarri" VALUES(30,12,'30:12','اور جس دن قیامت قائم ہوگی تو گنہگار حیرت زده ره جائیں گے. ');
INSERT INTO "junagarri" VALUES(30,13,'30:13','اور ان کے تمام تر شریکوں میں سے ایک بھی ان کا سفارشی نہ ہوگا اور (خود یہ بھی) اپنے شریکوں کے منکر ہو جائیں گے. ');
INSERT INTO "junagarri" VALUES(30,14,'30:14','اور جس دن قیامت قائم ہوگی اس دن (جماعتیں) الگ الگ ہو جائیں گی. ');
INSERT INTO "junagarri" VALUES(30,15,'30:15','جو ایمان لاکر نیک اعمال کرتے رہے وه تو جنت میں خوش وخرم کر دیئے جائیں گے. ');
INSERT INTO "junagarri" VALUES(30,16,'30:16','اور جنہوں نے کفر کیا تھا اور ہماری آیتوں کو اور آخرت کی ملاقات کو جھوٹا ٹھہرایا تھا وه سب عذاب میں پکڑ کر حاضر رکھے جائیں گے. ');
INSERT INTO "junagarri" VALUES(30,17,'30:17','پس اللہ تعالیٰ کی تسبیح پڑھا کرو جب کہ تم شام کرو اور جب صبح کرو.');
INSERT INTO "junagarri" VALUES(30,18,'30:18','تمام تعریفوں کے لائق آسمان وزمین میں صرف وہی ہے تیسرے پہر کو اور ظہر کے وقت بھی (اس کی پاکیزگی بیان کرو). ');
INSERT INTO "junagarri" VALUES(30,19,'30:19','(وہی) زنده کو مرده سے اور مرده کو زنده سے نکالتا ہے ۔ اور وہی زمین کو اس کی موت کے بعد زنده کرتا ہے اسی طرح تم (بھی) نکالے جاؤ گے. ');
INSERT INTO "junagarri" VALUES(30,20,'30:20','اللہ کی نشانیوں میں سے ہے کہ اس نے تم کو مٹی سے پیدا کیا پھر اب انسان بن کر (چلتے پھرتے) پھیل رہے ہو. ');
INSERT INTO "junagarri" VALUES(30,21,'30:21','اور اس کی نشانیوں میں سے ہے کہ تمہاری ہی جنس سے بیویاں پیدا کیں تاکہ تم ان سے آرام پاؤ اس نے تمہارے درمیان محبت اور ہمدردی قائم کر دی ، یقیناً غور وفکر کرنے والوں کے لئے اس میں بہت سی نشانیاں ہیں.');
INSERT INTO "junagarri" VALUES(30,22,'30:22','اس (کی قدرت) کی نشانیوں میں سے آسمانوں اور زمین کی پیدائش اور تمہاری زبانوں اور رنگتوں کا اختلاف (بھی) ہے ، دانش مندوں کے لئے اس میں یقیناً بڑی نشانیاں ہیں.');
INSERT INTO "junagarri" VALUES(30,23,'30:23','اور (بھی) اس کی (قدرت کی) نشانی تمہاری راتوں اور دن کی نیند میں ہے اور اس کے فضل (یعنی روزی) کو تمہارا تلاش کرنا بھی ہے ۔ جو لوگ (کان لگا کر) سننے کے عادی ہیں ان کے لئے اس میں بہت سی نشانیاں ہیں.');
INSERT INTO "junagarri" VALUES(30,24,'30:24','اور اس کی نشانیوں میں سے ایک یہ (بھی) ہے کہ وه تمہیں ڈرانے اور امیدوار بنانے کے لئے بجلیاں دکھاتا ہے اور آسمان سے بارش برساتا ہے اور اس مرده زمین کو زنده کر دیتا ہے، اس میں (بھی) عقلمندوں کے لئے بہت سی نشانیاں ہیں.');
INSERT INTO "junagarri" VALUES(30,25,'30:25','اس کی ایک نشانی یہ بھی ہے کہ آسمان وزمین اسی کے حکم سے قائم ہیں، پھر جب وه تمہیں آواز دے گا صرف ایک بار کی آواز کے ساتھ ہی تم سب زمین سے نکل آؤ گے. ');
INSERT INTO "junagarri" VALUES(30,26,'30:26','اور زمین وآسمان کی ہر چیز اس کی ملکیت ہےاور ہر ایک اس کے فرمان کے ماتحت ہے. ');
INSERT INTO "junagarri" VALUES(30,27,'30:27','وہی ہے جو اول بار مخلوق کو پیدا کرتا ہے پھر سے دوباره پیدا کرے گا اور یہ تو اس پر بہت ہی آسان ہے۔ اسی کی بہترین اور اعلیٰ صفت ہے ، آسمانوں میں اور زمین میں بھی اور وہی غلبے والا حکمت والا ہے.');
INSERT INTO "junagarri" VALUES(30,28,'30:28','اللہ تعالیٰ نے تمہارے لئے ایک مثال خود تمہاری ہی بیان فرمائی، جو کچھ ہم نے تمہیں دے رکھا ہے کیا اس میں تمہارے غلاموں میں سے بھی کوئی تمہارا شریک ہے؟ کہ تم اور وه اس میں برابر درجے کے ہو ؟ اور تم ان کا ایسا خطره رکھتے ہو جیسا خود اپنوں کا ، ہم عقل رکھنے والوں کے لئے اسی طرح کھول کھول کر آیتیں بیان کر دیتے ہیں. ');
INSERT INTO "junagarri" VALUES(30,29,'30:29','بلکہ بات یہ ہے کہ یہ ظالم تو بغیر علم کے خواہش پرستی کر رہے ہیں، اسے کون راه دکھائے جسے اللہ تعالیٰ راه سے ہٹا دے ، ان کا ایک بھی مددگار نہیں. ');
INSERT INTO "junagarri" VALUES(30,30,'30:30','پس آپ یک سو ہو کر اپنا منھ دین کی طرف متوجہ کر دیں ۔ اللہ تعالیٰ کی وه فطرت جس پر اس نے لوگوں کو پیدا کیا ہے ، اللہ تعالیٰ کے بنائے کو بدلنا نہیں، یہی سیدھا دین ہے لیکن اکثر لوگ نہیں سمجھتے. ');
INSERT INTO "junagarri" VALUES(30,31,'30:31','(لوگو!) اللہ تعالیٰ کی طرف رجوع ہو کر اس سے ڈرتے رہو اور نماز کو قائم رکھو اور مشرکین میں سے نہ ہو جاؤ. ');
INSERT INTO "junagarri" VALUES(30,32,'30:32','ان لوگوں میں سے جنہوں نے اپنے دین کو ٹکڑے ٹکڑے کر دیا اور خود بھی گروه گروه ہوگئے ہر گروه اس چیز پر جو اس کے پاس ہے مگن ہے. ');
INSERT INTO "junagarri" VALUES(30,33,'30:33','لوگوں کو جب کبھی کوئی مصیبت پہنچتی ہے تو اپنے رب کی طرف (پوری طرح) رجوع ہو کر دعائیں کرتے ہیں، پھر جب وه اپنی طرف سے رحمت کاذائقہ چکھاتا ہے تو ان میں سے ایک جماعت اپنے رب کے ساتھ شرک کرنے لگتی ہے.');
INSERT INTO "junagarri" VALUES(30,34,'30:34','تاکہ وه اس چیز کی ناشکری کریں جو ہم نے انہیں دی ہے اچھا تم فائده اٹھا لو! ابھی ابھی تمہیں معلوم ہو جائے گا.');
INSERT INTO "junagarri" VALUES(30,35,'30:35','کیا ہم نے ان پر کوئی دلیل نازل کی ہے جو اسے بیان کرتی ہے جسے یہ اللہ کے ساتھ شریک کر رہے ہیں. ');
INSERT INTO "junagarri" VALUES(30,36,'30:36','اور جب ہم لوگوں کو رحمت کا مزه چکھاتے ہیں تو وه خوب خوش ہو جاتے ہیں اور اگر انہیں ان کے ہاتھوں کے کرتوت کی وجہ سے کوئی برائی پہنچے تو ایک دم وه محض ناامید ہو جاتے ہیں. ');
INSERT INTO "junagarri" VALUES(30,37,'30:37','کیا انہوں نے نہیں دیکھا کہ اللہ تعالیٰ جسے چاہے کشاده روزی دیتا ہے اور جسے چاہے تنگ ، اس میں بھی ان لوگوں کے لئے جو ایمان لاتے ہیں نشانیاں ہیں.');
INSERT INTO "junagarri" VALUES(30,38,'30:38','پس قرابت دار کو مسکین کو مسافر کو ہر ایک کو اس کا حق دیجئے ، یہ ان کے لئے بہتر ہے جو اللہ تعالیٰ کا منھ دیکھنا چاہتے ہوں ، ایسے ہی لوگ نجات پانے والے ہیں.');
INSERT INTO "junagarri" VALUES(30,39,'30:39','تم جو سود پر دیتے ہو کہ لوگوں کے مال میں بڑھتا رہے وه اللہ تعالیٰ کے ہاں نہیں بڑھتا ۔ اور جو کچھ صدقہ زکوٰة تم اللہ تعالیٰ کا منھ دیکھنے (اورخوشنودی کے لئے) دو تو ایسے لوگ ہی اپنا دو چند کرنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(30,40,'30:40','اللہ تعالیٰ وه ہے جس نے تمہیں پیدا کیا پھر روزی دی پھر مار ڈالے گا پھر زنده کر دے گا بتاؤ تمہارے شریکوں میں سے کوئی بھی ایسا ہے جو ان میں سے کچھ بھی کر سکتا ہو۔ اللہ تعالیٰ کے لئے پاکی اور برتری ہے ہر اس شریک سے جو یہ لوگ مقرر کرتے ہیں.');
INSERT INTO "junagarri" VALUES(30,41,'30:41','خشکی اور تری میں لوگوں کی بداعمالیوں کے باعث فساد پھیل گیا۔ اس لئے کہ انہیں ان کے بعض کرتوتوں کا پھل اللہ تعالیٰ چکھا دے (بہت) ممکن ہے کہ وه باز آجائیں. ');
INSERT INTO "junagarri" VALUES(30,42,'30:42','زمین میں چل پھر کر دیکھو تو سہی کہ اگلوں کا انجام کیا ہوا۔ جن میں اکثر لوگ مشرک تھے. ');
INSERT INTO "junagarri" VALUES(30,43,'30:43','پس آپ اپنا رخ اس سچے اور سیدھے دین کی طرف ہی رکھیں قبل اس کے کہ وه دن آجائے جس کا ٹل جانا اللہ تعالیٰ کی طرف سے ہے ہی نہیں ، اس دن سب متفرق ہو جائیں گے.');
INSERT INTO "junagarri" VALUES(30,44,'30:44','کفر کرنے والوں پر ان کے کفر کا وبال ہوگا اور نیک کام کرنے والے اپنی ہی آرام گاه سنوار رہے ہیں. ');
INSERT INTO "junagarri" VALUES(30,45,'30:45','تاکہ اللہ تعالیٰ انہیں اپنے فضل سے جزا دے جو ایمان لائے اور نیک اعمال کیے وه کافروں کو دوست نہیں رکھتا ہے.');
INSERT INTO "junagarri" VALUES(30,46,'30:46','اس کی نشانیوں میں سے خوشخبریاں دینے والی ہواؤں کو چلانا بھی ہے اس لئے کہ تمہیں اپنی رحمت سے لطف اندوز کرے ، اور اس لئے کہ اس کے حکم سے کشتیاں چلیں ، اور اس لئے کہ اس کے فضل کو تم ڈھونڈو ، اور اس لئے کہ تم شکر گزاری کرو. ');
INSERT INTO "junagarri" VALUES(30,47,'30:47','اور ہم نے آپ سے پہلے بھی اپنے رسولوں کو ان کی قوم کی طرف بھیجا وه ان کے پاس دلیلیں لائے۔ پھر ہم نے گناه گاروں سے انتقام لیا۔ ہم پر مومنوں کی مدد کرنا لازم ہے. ');
INSERT INTO "junagarri" VALUES(30,48,'30:48','اللہ تعالیٰ ہوائیں چلاتا ہے وه ابر کو اٹھاتی ہیں پھر اللہ تعالیٰ اپنی منشا کے مطابق اسے آسمان میں پھیلا دیتا ہے اور اس کے ٹکڑے ٹکڑے کر دیتا ہے پھر آپ دیکھتے ہیں کہ اس کےاندر سے قطرے نکلتے ہیں، اور جنہیں اللہ چاہتا ہے ان بندوں پر پانی برساتا ہے تو وه خوش خوش ہو جاتے ہیں ');
INSERT INTO "junagarri" VALUES(30,49,'30:49','یقین ماننا کہ بارش ان پر برسنے سے پہلے پہلے تو وه ناامید ہو رہے تھے.');
INSERT INTO "junagarri" VALUES(30,50,'30:50','پس آپ رحمت الٰہی کے آثار دیکھیں کہ زمین کی موت کے بعد کس طرح اللہ تعالیٰ اسے زنده کر دیتا ہے؟ کچھ شک نہیں کہ وہی مردوں کو زنده کرنے والا ہے ، اور وه ہر ہر چیز پر قادر ہے.');
INSERT INTO "junagarri" VALUES(30,51,'30:51','اور اگر ہم باد تند چلا دیں اور یہ لوگ انہی کھیتوں کو (مرجھائی ہوئی) زرد پڑی ہوئی دیکھ لیں تو پھر اس کے بعد ناشکری کرنے لگیں. ');
INSERT INTO "junagarri" VALUES(30,52,'30:52','بیشک آپ مردوں کو نہیں سنا سکتے اور نہ بہروں کو (اپنی) آواز سنا سکتے ہیں جب کہ وه پیٹھ پھیر کر مڑ گئے ہوں. ');
INSERT INTO "junagarri" VALUES(30,53,'30:53','اور نہ آپ اندھوں کو ان کی گمراہی سے ہدایت کرنے والے ہیں آپ تو صرف ان ہی لوگوں کو سناتے ہیں جو ہماری آیتوں پر ایمان رکھتے ہیں پس وہی اطاعت کرنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(30,54,'30:54','اللہ تعالیٰ وه ہے جس نے تمہیں کمزوری کی حالت میں پیدا کیا پھر اس کمزوری کے بعد توانائی دی، پھر اس توانائی کے بعد کمزوری اور بڑھاپا دیا جو چاہتا ہے پیدا کرتا ہے ، وه سب سے پورا واقف اور سب پر پورا قادر ہے.');
INSERT INTO "junagarri" VALUES(30,55,'30:55','اور جس دن قیامت برپا ہو جائے گی گناه گار لوگ قسمیں کھائیں گے کہ (دنیا میں) ایک گھڑی کے سوا نہیں ٹھہرے، اسی طرح یہ بہکے ہوئے ہی رہے. ');
INSERT INTO "junagarri" VALUES(30,56,'30:56','اور جن لوگوں کو علم اور ایمان دیا گیا وه جواب دیں گے کہ تم تو جیسا کہ کتاب اللہ میں ہے یوم قیامت تک ٹھہرے رہے ۔ آج کا یہ دن قیامت ہی کا دن ہے لیکن تم تو یقین ہی نہیں مانتے تھے. ');
INSERT INTO "junagarri" VALUES(30,57,'30:57','پس اس دن ظالموں کو ان کا عذر بہانہ کچھ کام نہ آئے گا اور نہ ان سے توبہ اور عمل طلب کیا جائے گا. ');
INSERT INTO "junagarri" VALUES(30,58,'30:58','بیشک ہم نےاس قرآن میں لوگوں کے سامنے کل مثالیں بیان کر دی ہیں ۔ آپ ان کے پاس کوئی بھی نشانی لائیں ، یہ کافر تو یہی کہیں گے کہ تم (بے ہوده گو) بالکل جھوٹے ہو. ');
INSERT INTO "junagarri" VALUES(30,59,'30:59','اللہ تعالیٰ ان لوگوں کے دلوں پر جو سمجھ نہیں رکھتے یوں ہی مہر کر دیتا ہے.');
INSERT INTO "junagarri" VALUES(30,60,'30:60','پس آپ صبر کریں یقیناً اللہ کا وعده سچا ہے۔ آپ کو وه لوگ ہلکا (بے صبرا) نہ کریں جویقین نہیں رکھتے.');
INSERT INTO "junagarri" VALUES(31,1,'31:1','الم. ');
INSERT INTO "junagarri" VALUES(31,2,'31:2','یہ حکمت والی کتاب کی آیتیں ہیں.');
INSERT INTO "junagarri" VALUES(31,3,'31:3','جو نیکو کاروں کے لئے رہبر اور (سراسر) رحمت ہے.');
INSERT INTO "junagarri" VALUES(31,4,'31:4','جو لوگ نماز قائم کرتے ہیں اور زکوٰة ادا کرتے ہیں اور آخرت پر (کامل) یقین رکھتے ہیں. ');
INSERT INTO "junagarri" VALUES(31,5,'31:5','یہی لوگ ہیں جو اپنے رب کی طرف سے ہدایت پر ہیں اور یہی لوگ نجات پانے والے ہیں. ');
INSERT INTO "junagarri" VALUES(31,6,'31:6','اور بعض لوگ ایسے بھی ہیں جو لغو باتوں کو مول لیتے ہیں کہ بےعلمی کے ساتھ لوگوں کو اللہ کی راه سے بہکائیں اور اسے ہنسی بنائیں ، یہی وه لوگ ہیں جن کے لئے رسوا کرنے والا عذاب ہے. ');
INSERT INTO "junagarri" VALUES(31,7,'31:7','جب اس کے سامنے ہماری آیتیں تلاوت کی جاتی ہیں تو تکبر کرتا ہوا اس طرح منھ پھیر لیتا ہے گویا اس نے سنا ہی نہیں گویا کہ اس کے دونوں کانوں میں ڈاٹ لگے ہوئے ہیں ، آپ اسے درد ناک عذاب کی خبر سنا دیجئے.');
INSERT INTO "junagarri" VALUES(31,8,'31:8','بیشک جن لوگوں نےایمان قبول کیا اور کام بھی نیک (مطابق سنت) کیے ان کے لئے نعمتوں والی جنتیں ہیں.');
INSERT INTO "junagarri" VALUES(31,9,'31:9','جہاں وه ہمیشہ رہیں گے۔ اللہ کا سچا وعده ہے، وه بہت بڑی عزت وغلبہ والا اور کامل حکمت والا ہے.');
INSERT INTO "junagarri" VALUES(31,10,'31:10','اسی نے آسمانوں کو بغیر ستون کے پیدا کیا ہے تم انہیں دیکھ رہے ہو اور اس نے زمین میں پہاڑوں کو ڈال دیا تاکہ وه تمہیں جنبش نہ دے سکے اور ہر طرح کے جاندار زمین میں پھیلا دیئے ۔ اور ہم نے آسمان سے پانی برسا کر زمین میں ہر قسم کے نفیس جوڑے اگا دیئے. ');
INSERT INTO "junagarri" VALUES(31,11,'31:11','یہ ہے اللہ کی مخلوق اب تم مجھے اس کے سوا دوسرے کسی کی کوئی مخلوق تو دکھاؤ (کچھ نہیں)، بلکہ یہ ظالم کھلی گمراہی میں ہیں.');
INSERT INTO "junagarri" VALUES(31,12,'31:12','اور ہم نے یقیناً لقمان کو حکمت دی تھی کہ تو اللہ تعالیٰ کا شکر کر ہر شکر کرنے والا اپنے ہی نفع کے لئے شکر کرتا ہے جو بھی ناشکری کرے وه جان لے کہ اللہ تعالیٰ بے نیاز اور تعریفوں والا ہے.');
INSERT INTO "junagarri" VALUES(31,13,'31:13','اور جب کہ لقمان نے وعظ کہتے ہوئے اپنے لڑکے سے فرمایا کہ میرے پیارے بچے! اللہ کے ساتھ شریک نہ کرنا بیشک شرک بڑا بھاری ظلم ہے. ');
INSERT INTO "junagarri" VALUES(31,14,'31:14','ہم نے انسان کو اس کے ماں باپ کے متعلق نصیحت کی ہے، اس کی ماں نے دکھ پر دکھ اٹھا کر اسے حمل میں رکھا اور اس کی دودھ چھڑائی دو برس میں ہے کہ تو میری اور اپنے ماں باپ کی شکر گزاری کر، (تم سب کو) میری ہی طرف لوٹ کر آنا ہے.');
INSERT INTO "junagarri" VALUES(31,15,'31:15','اور اگر وه دونوں تجھ پر اس بات کا دباؤ ڈالیں کہ تو میرے ساتھ شریک کرے جس کا تجھے علم نہ ہو تو تو ان کا کہنا نہ ماننا، ہاں دنیا میں ان کے ساتھ اچھی طرح بسر کرنا اور اس کی راه چلنا جو میری طرف جھکا ہوا ہو تمہارا سب کا لوٹنا میری ہی طرف ہے تم جو کچھ کرتے ہو اس سے پھر میں تمہیں خبردار کروں گا. ');
INSERT INTO "junagarri" VALUES(31,16,'31:16','پیارے بیٹے! اگر کوئی چیز رائی کے دانے کے برابر ہو پھر وه (بھی) خواه کسی چٹان میں ہو یا آسمانوں میں ہو یا زمین میں ہو اسے اللہ تعالیٰ ضرور لائے گا اللہ تعالیٰ بڑا باریک بین اور خبردار ہے.');
INSERT INTO "junagarri" VALUES(31,17,'31:17','اے میرے پیارے بیٹے! تو نماز قائم رکھنا، اچھے کاموں کی نصیحت کرتے رہنا، برے کاموں سے منع کیا کرنا اور جو مصیبت تم پر آجائے صبر کرنا (یقین مانو) کہ یہ بڑے تاکیدی کاموں میں سے ہے.');
INSERT INTO "junagarri" VALUES(31,18,'31:18','لوگوں کے سامنے اپنے گال نہ پھلا اور زمین پر اترا کر نہ چل کسی تکبر کرنے والے شیخی خورے کو اللہ تعالیٰ پسند نہیں فرماتا.');
INSERT INTO "junagarri" VALUES(31,19,'31:19','اپنی رفتار میں میانہ روی اختیار کر ، اور اپنی آواز پست کر یقیناً آوازوں میں سب سے بدتر آواز گدھوں کی آواز ہے.');
INSERT INTO "junagarri" VALUES(31,20,'31:20','کیا تم نہیں دیکھتے کہ اللہ تعالیٰ نے زمین وآسمان کی ہر چیز کو ہمارے کام میں لگا رکھا ہے اور تمہیں اپنی ظاہری وباطنی نعمتیں بھرپور دے رکھی ہیں ، بعض لوگ اللہ کے بارے میں بغیر علم کے اور بغیر ہدایت کے اور بغیر روشن کتاب کے جھگڑا کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(31,21,'31:21','اور جب ان سے کہا جاتا ہے کہ اللہ تعالیٰ کی اتاری ہوئی وحی کی تابعداری کرو تو کہتے ہیں کہ ہم نے تو جس طریق پر اپنے باپ دادوں کو پایا ہے اسی کی تابعداری کریں گے، اگرچہ شیطان ان کے بڑوں کو دوزخ کےعذاب کی طرف بلاتا ہو.');
INSERT INTO "junagarri" VALUES(31,22,'31:22','اور جو (شخص) اپنے آپ کو اللہ کے تابع کردے اور ہو بھی وه نیکو کار یقیناً اس نے مضبوط کڑا تھام لیا ، تمام کاموں کا انجام اللہ کی طرف ہے.');
INSERT INTO "junagarri" VALUES(31,23,'31:23','کافروں کے کفر سے آپ رنجیده نہ ہوں ، آخر ان سب کو لوٹنا تو ہماری جانب ہی ہے پھر ہم ان کو بتائیں گے جو انہوں نے کیا ہے، بے شک اللہ سینوں کے بھیدوں تک سے واقف ہے.');
INSERT INTO "junagarri" VALUES(31,24,'31:24','ہم انہیں گو کچھ یونہی سا فائده دے دیں لیکن (بالآخر) ہم انہیں نہایت بیچارگی کی حالت میں سخت عذاب کی طرف ہنکا لے جائیں گے. ');
INSERT INTO "junagarri" VALUES(31,25,'31:25','اگر آپ ان سے دریافت کریں کہ آسمان وزمین کا خالق کون ہے؟ تو یہ ضرور جواب دیں گے کہ اللہ ، تو کہہ دیجئے کہ سب تعریفوں کے لائق اللہ ہی ہے ، لیکن ان میں کے اکثر بے علم ہیں.');
INSERT INTO "junagarri" VALUES(31,26,'31:26','آسمانوں میں اور زمین میں جو کچھ ہے وه سب اللہ ہی کا ہے یقیناً اللہ تعالیٰ بہت بڑا بے نیاز اور سزاوار حمد وثنا ہے. ');
INSERT INTO "junagarri" VALUES(31,27,'31:27','روئے زمین کے (تمام) درختوں کے اگر قلمیں ہو جائیں اور تمام سمندروں کی سیاہی ہو اور ان کے بعد سات سمندر اور ہوں تاہم اللہ کے کلمات ختم نہیں ہو سکتے ، بیشک اللہ تعالیٰ غالب اور باحکمت ہے.');
INSERT INTO "junagarri" VALUES(31,28,'31:28','تم سب کی پیدائش اور مرنے کے بعد جلانا ایسا ہی ہے جیسے ایک جی کا ، بیشک اللہ تعالیٰ سننے والا دیکھنے والا ہے.');
INSERT INTO "junagarri" VALUES(31,29,'31:29','کیا آپ نہیں دیکھتے کہ اللہ تعالیٰ رات کو دن میں اور دن کو رات میں کھپا دیتا ہے ، سورج چاند کو اسی نے فرماں بردار کر رکھا ہے ہر ایک مقرره وقت تک چلتا رہے ، اللہ تعالیٰ ہر اس چیز سے جو تم کرتے ہو خبردار ہے.');
INSERT INTO "junagarri" VALUES(31,30,'31:30','یہ سب (انتظامات) اس وجہ سے ہیں کہ اللہ تعالیٰ حق ہے اور اس کے سوا جن جن کو لوگ پکارتے ہیں سب باطل ہیں اور یقیناً اللہ تعالیٰ بہت بلندیوں والا اور بڑی شان والا ہے. ');
INSERT INTO "junagarri" VALUES(31,31,'31:31','کیا تم اس پر غور نہیں کرتے کہ دریا میں کشتیاں اللہ کے فضل سے چل رہی ہیں اس لئے کہ وه تمہیں اپنی نشانیاں دکھاوے ، یقیناً اس میں ہر ایک صبر وشکر کرنے والے کے لئے بہت سی نشانیاں ہیں.');
INSERT INTO "junagarri" VALUES(31,32,'31:32','اور جب ان پر موجیں سائبانوں کی طرح چھا جاتی ہیں تو وه (نہایت) خلوص کے ساتھ اعتقاد کر کے اللہ تعالیٰ ہی کو پکارتے ہیں ۔ پھر جب وه (باری تعالیٰ) انہیں نجات دے کر خشکی کی طرف پہنچاتا ہے تو کچھ ان میں سے اعتدال پر رہتے ہیں ، اور ہماری آیتوں کا انکار صرف وہی کرتے ہیں جو بدعہد اور ناشکرے ہوں. ');
INSERT INTO "junagarri" VALUES(31,33,'31:33','لوگو! اپنے رب سے ڈرو اور اس دن کا خوف کرو جس دن باپ اپنے بیٹے کو کوئی نفع نہ پہنچا سکے گا اور نہ بیٹا اپنے باپ کا ذرا سا بھی نفع کرنے والا ہوگا (یاد رکھو) اللہ کا وعده سچا ہے (دیکھو) تمہیں دنیا کی زندگی دھوکے میں نہ ڈالے اور نہ دھوکے باز (شیطان) تمہیں دھوکے میں ڈال دے.');
INSERT INTO "junagarri" VALUES(31,34,'31:34','بے شک اللہ تعالیٰ ہی کے پاس قیامت کا علم ہے وہی بارش نازل فرماتا ہے اور ماں کے پیٹ میں جو ہے اسے جانتا ہے۔ کوئی (بھی) نہیں جانتا کہ کل کیا (کچھ) کرے گا؟ نہ کسی کو یہ معلوم ہے کہ کس زمین میں مرے گا ۔ (یاد رکھو) اللہ تعالیٰ ہی پورے علم والا اور صحیح خبروں والا ہے.');
INSERT INTO "junagarri" VALUES(32,1,'32:1','الم.');
INSERT INTO "junagarri" VALUES(32,2,'32:2','بلاشبہ اس کتاب کا اتارنا تمام جہانوں کے پروردگار کی طرف سے ہے. ');
INSERT INTO "junagarri" VALUES(32,3,'32:3','کیا یہ کہتے ہیں کہ اس نے اسے گھڑ لیا ہے ۔ (نہیں نہیں) بلکہ یہ تیرے رب تعالیٰ کی طرف سے حق ہے تاکہ آپ انہیں ڈرائیں جن کے پاس آپ سے پہلے کوئی ڈرانے والا نہیں آیا ۔ تاکہ وه راه راست پر آجائیں. ');
INSERT INTO "junagarri" VALUES(32,4,'32:4','اللہ تعالیٰ وه ہے جس نے آسمان وزمین کو اور جو کچھ ان کے درمیان ہے سب کو چھ دن میں پیدا کر دیا پھر عرش پر قائم ہوا ، تمہارے لئے اس کے سوا کوئی مددگار اور سفارشی نہیں ۔ کیا پھر بھی تم نصیحت حاصل نہیں کرتے. ');
INSERT INTO "junagarri" VALUES(32,5,'32:5','وه آسمان سے لے کر زمین تک (ہر) کام کی تدبیر کرتا ہے ۔ پھر (وه کام) ایک ایسے دن میں اس کی طرف چڑھ جاتا ہے جس کا اندازه تمہاری گنتی کے ایک ہزار سال کے برابر ہے. ');
INSERT INTO "junagarri" VALUES(32,6,'32:6','یہی ہے چھپے کھلے کا جاننے والا، زبردست غالب بہت ہی مہربان.');
INSERT INTO "junagarri" VALUES(32,7,'32:7','جس نے نہایت خوب بنائی جو چیز بھی بنائی اور انسان کی بناوٹ مٹی سے شروع کی. ');
INSERT INTO "junagarri" VALUES(32,8,'32:8','پھر اس کی نسل ایک بے وقعت پانی کے نچوڑ سے چلائی. ');
INSERT INTO "junagarri" VALUES(32,9,'32:9','جسے ٹھیک ٹھاک کر کے اس میں اپنی روح پھونکی ، اسی نے تمہارے کان آنکھیں اور دل بنائے (اس پر بھی) تم بہت ہی تھوڑا احسان مانتے ہو. ');
INSERT INTO "junagarri" VALUES(32,10,'32:10','انہوں نے کہا کیا جب ہم زمین میں مل جائیں گے کیا پھر نئی پیدائش میں آجائیں گے؟ بلکہ (بات یہ ہے) کہ وه لوگ اپنے پروردگار کی ملاقات کے منکر ہیں.');
INSERT INTO "junagarri" VALUES(32,11,'32:11','کہہ دیجئے! کہ تمہیں موت کا فرشتہ فوت کرے گا جو تم پر مقرر کیا گیا ہے . پھر تم سب اپنے پروردگار کی طرف لوٹائے جاؤ گے.');
INSERT INTO "junagarri" VALUES(32,12,'32:12','کاش کہ آپ دیکھتے جب کہ گناه گار لوگ اپنے رب تعالیٰ کے سامنے سر جھکائے ہوئے ہوں گے، کہیں گے اے ہمارے رب! ہم نے دیکھ لیا اور سن لیا اب تو ہمیں واپس لوٹا دے ہم نیک اعمال کریں گے ہم یقین کرنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(32,13,'32:13','اگر ہم چاہتے تو ہر شخص کو ہدایت نصیب فرما دیتے، لیکن میری یہ بات بالکل حق ہو چکی ہے کہ میں ضرور ضرور جہنم کو انسانوں اور جنوں سے پر کردوں گا. ');
INSERT INTO "junagarri" VALUES(32,14,'32:14','اب تم اپنے اس دن کی ملاقات کے فراموش کر دینے کا مزه چکھو، ہم نے بھی تمہیں بھلا دیا اور اپنے کیے ہوئے اعمال (کی شامت) سے ابدی عذاب کا مزه چکھو.');
INSERT INTO "junagarri" VALUES(32,15,'32:15','ہماری آیتوں پر وہی ایمان لاتے ہیں جنہیں جب کبھی ان سے نصیحت کی جاتی ہے تو وه سجدے میں گر پڑتے ہیں اور اپنے رب کی حمد کے ساتھ اس کی تسبیح پڑھتے ہیں اور تکبر نہیں کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(32,16,'32:16','ان کی کروٹیں اپنے بستروں سے الگ رہتی ہیں اپنے رب کو خوف اور امید کے ساتھ پکارتے ہیں اور جو کچھ ہم نے انہیں دے رکھا ہے وه خرچ کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(32,17,'32:17','کوئی نفس نہیں جانتا جو کچھ ہم نے ان کی آنکھوں کی ٹھنڈک ان کے لئے پوشیده کر رکھی ہے ، جو کچھ کرتے تھے یہ اس کا بدلہ ہے. ');
INSERT INTO "junagarri" VALUES(32,18,'32:18','کیا وه جو مومن ہو مثل اس کے ہے جو فاسق ہو ؟ یہ برابر نہیں ہو سکتے.');
INSERT INTO "junagarri" VALUES(32,19,'32:19','جن لوگوں نے ایمان قبول کیا اور نیک اعمال بھی کیے ان کے لئے ہمیشگی والی جنتیں ہیں، مہمانداری ہے ان کے اعمال کے بدلے جو وه کرتے تھے.');
INSERT INTO "junagarri" VALUES(32,20,'32:20','لیکن جن لوگوں نے حکم عدولی کی ان کا ٹھکانا دوزخ ہے۔ جب کبھی اس سے باہر نکلنا چاہیں گے اسی میں لوٹا دیئے جائیں گے ۔ اور کہہ دیا جائے گا کہ اپنے جھٹلانے کے بدلے آگ کا عذاب چکھو.');
INSERT INTO "junagarri" VALUES(32,21,'32:21','بالیقین ہم انہیں قریب کے چھوٹے سے بعض عذاب اس بڑے عذاب کے سوا چکھائیں گے تاکہ وه لوٹ آئیں. ');
INSERT INTO "junagarri" VALUES(32,22,'32:22','اس سے بڑھ کر ظالم کون ہے جسے اللہ تعالیٰ کی آیتوں سے وعظ کیا گیا پھر بھی اس نے ان سے منھ پھیر لیا، (یقین مانو) کہ ہم بھی گناه گاروں سے انتقام لینے والے ہیں.');
INSERT INTO "junagarri" VALUES(32,23,'32:23','بیشک ہم نے موسیٰ کو کتاب دی، پس آپ کو ہرگز اس کی ملاقات میں شک نہ کرنا چاہئے اور ہم نے اسے بنی اسرائیل کی ہدایت کا ذریعہ بنایا.');
INSERT INTO "junagarri" VALUES(32,24,'32:24','اور جب ان لوگوں نے صبر کیا تو ہم نے ان میں سے ایسے پیشوا بنائے جو ہمارے حکم سے لوگوں کو ہدایت کرتے تھے، اور وه ہماری آیتوں پر یقین رکھتے تھے. ');
INSERT INTO "junagarri" VALUES(32,25,'32:25','آپ کا رب ان (سب) کے درمیان ان (تمام) باتوں کا فیصلہ قیامت کے دن کرے گا جن میں وه اختلاف کر رہے ہیں. ');
INSERT INTO "junagarri" VALUES(32,26,'32:26','کیا اس بات نے بھی انہیں ہدایت نہیں دی کہ ہم نے ان سے پہلے بہت سی امتوں کو ہلاک کر دیا جن کے مکانوں میں یہ چل پھر رہے ہیں ۔ اس میں تو (بڑی) بڑی نشانیاں ہیں۔ کیا پھر بھی یہ نہیں سنتے؟');
INSERT INTO "junagarri" VALUES(32,27,'32:27','کیا یہ نہیں دیکھتے کہ ہم پانی کو بنجر (غیر آباد) زمین کی طرف بہا کر لے جاتے ہیں پھر اس سے ہم کھیتیاں نکالتے ہیں جسے ان کے چوپائے اور یہ خود کھاتے ہیں ، کیا پھر بھی یہ نہیں دیکھتے؟');
INSERT INTO "junagarri" VALUES(32,28,'32:28','اور کہتے ہیں کہ یہ فیصلہ کب ہوگا؟ اگر تم سچے ہو (تو بتلاؤ). ');
INSERT INTO "junagarri" VALUES(32,29,'32:29','جواب دے دو کہ فیصلے والے دن ایمان لانا بے ایمانوں کو کچھ کام نہ آئے گا اور نہ انہیں ڈھیل دی جائے گی. ');
INSERT INTO "junagarri" VALUES(32,30,'32:30','اب آپ ان کا خیال چھوڑ دیں اور منتظر رہیں ۔ یہ بھی منتظر ہیں. ');
INSERT INTO "junagarri" VALUES(33,1,'33:1','اے نبی! اللہ تعالیٰ سے ڈرتے رہنا اور کافروں اور منافقوں کی باتوں میں نہ آجانا، اللہ تعالیٰ بڑے علم والا اور بڑی حکمت والا ہے. ');
INSERT INTO "junagarri" VALUES(33,2,'33:2','جو کچھ آپ کی جانب آپ کے رب کی طرف سے وحی کی جاتی ہے اس کی تابعداری کریں (یقین مانو) کہ اللہ تمہارے ہر ایک عمل سے باخبر ہے. ');
INSERT INTO "junagarri" VALUES(33,3,'33:3','آپ اللہ ہی پر توکل رکھیں ، وه کار سازی کے لئے کافی ہے. ');
INSERT INTO "junagarri" VALUES(33,4,'33:4','کسی آدمی کے سینے میں اللہ تعالیٰ نے دو دل نہیں رکھے ، اور اپنی جن بیویوں کو تم ماں کہہ بیٹھتے ہو انہیں اللہ نے تمہاری (سچ مچ کی) مائیں نہیں بنایا، اور نہ تمہارے لے پالک لڑکوں کو (واقعی) تمہارے بیٹے بنایا ہے ، یہ تو تمہارے اپنے منھ کی باتیں ہیں ، اللہ تعالیٰ حق بات فرماتا ہے اور وه (سیدھی) راه سجھاتا ہے.');
INSERT INTO "junagarri" VALUES(33,5,'33:5','لے پالکوں کو ان کے (حقیقی) باپوں کی طرف نسبت کر کے بلاؤ اللہ کے نزدیک پورا انصاف یہی ہے۔ پھر اگر تمہیں ان کے (حقیقی) باپوں کا علم ہی نہ ہو تو وه تمہارے دینی بھائی اور دوست ہیں ، تم سے بھول چوک میں جو کچھ ہو جائے اس میں تم پر کوئی گناه نہیں ، البتہ گناه وه ہے جس کا تم اراده دل سے کرو ۔ اللہ تعالیٰ بڑا ہی بخشنے والا مہربان ہے.');
INSERT INTO "junagarri" VALUES(33,6,'33:6','پیغمبر مومنوں پر خود ان سے بھی زیاده حق رکھنے والے ہیں اور پیغمبر کی بیویاں مومنوں کی مائیں ہیں ، اور رشتے دار کتاب اللہ کی رو سے بہ نسبت دوسرے مومنوں اور مہاجروں کے آپس میں زیاده حق دار ہیں (ہاں) مگر یہ کہ تم اپنے دوستوں کے ساتھ حسن سلوک کرنا چاہو ۔ یہ حکم کتاب (الٰہی) میں لکھا ہوا ہے. ');
INSERT INTO "junagarri" VALUES(33,7,'33:7','جب کہ ہم نے تمام نبیوں سے عہد لیا اور (بالخصوص) آپ سے اور نوح سے اور ابراہیم سے اور موسیٰ سے اور مریم کے بیٹے عیسیٰ سے، اور ہم نے ان سے (پکا اور) پختہ عہد لیا. ');
INSERT INTO "junagarri" VALUES(33,8,'33:8','تاکہ اللہ تعالیٰ سچوں سے ان کی سچائی کے بارے میں دریافت فرمائے ، اور کافروں کے لئے ہم نے المناک عذاب تیار کر رکھے ہیں.');
INSERT INTO "junagarri" VALUES(33,9,'33:9','اے ایمان والو! اللہ تعالیٰ نے جو احسان تم پر کیا اسے یاد کرو جبکہ تمہارے مقابلے کو فوجوں پر فوجیں آئیں پھر ہم نے ان پر تیز وتند آندھی اور ایسے لشکر بھیجے جنہیں تم نے دیکھا ہی نہیں ، اور جو کچھ تم کرتے ہو اللہ تعالیٰ سب کچھ دیکھتا ہے.');
INSERT INTO "junagarri" VALUES(33,10,'33:10','جب کہ (دشمن) تمہارے پاس اوپر سے اور نیچے سے چڑھ آئے اور جب کہ آنکھیں پتھرا گئیں اور کلیجے منھ کو آگئے اور تم اللہ تعالیٰ کی نسبت طرح طرح کے گمان کرنے لگے. ');
INSERT INTO "junagarri" VALUES(33,11,'33:11','یہیں مومن آزمائے گئے اور پوری طرح وه جھنجھوڑ دیئے گئے. ');
INSERT INTO "junagarri" VALUES(33,12,'33:12','اور اس وقت منافق اور وه لوگ جن کے دلوں میں (شک کا) روگ تھا کہنے لگے اللہ تعالیٰ اور اس کے رسول نے ہم سے محض دھوکا فریب کا ہی وعده کیا تھا. ');
INSERT INTO "junagarri" VALUES(33,13,'33:13','ان ہی کی ایک جماعت نے ہانک لگائی کہ اے مدینہ والو ! تمہارے لئے ٹھکانہ نہیں چلو لوٹ چلو ، اور ان کی ایک اور جماعت یہ کہہ کر نبی ﴿صلی اللہ علیہ وسلم﴾ سے اجازت مانگنے لگی کہ ہمارے گھر غیر محفوظ ہیں ، حالانکہ وه (کھلے ہوئے اور) غیر محفوظ نہ تھے (لیکن) ان کا پختہ اراده بھاگ کھڑے ہونے کا تھا. ');
INSERT INTO "junagarri" VALUES(33,14,'33:14','اور اگر مدینے کے اطراف سے ان پر (لشکر) داخل کیے جاتے پھر ان سے فتنہ طلب کیا جاتا تو یہ ضرور اسے برپا کر دیتے اور نہ لڑتے مگر تھوڑی مدت. ');
INSERT INTO "junagarri" VALUES(33,15,'33:15','اس سے پہلے تو انہوں نے اللہ سے عہد کیا تھا کہ پیٹھ نہ پھیریں گے ، اور اللہ تعالیٰ سے کیے ہوئے وعده کی باز پرس ضرور ہوگی.');
INSERT INTO "junagarri" VALUES(33,16,'33:16','کہہ دیجئے کہ گو تم موت سے یا خوف قتل سے بھاگو تو یہ بھاگنا تمہیں کچھ بھی کام نہ آئے گا اور اس وقت تم بہت ہی کم فائده اٹھاؤ گے. ');
INSERT INTO "junagarri" VALUES(33,17,'33:17','پوچھیئے! تو کہ اگر اللہ تعالیٰ تمہیں کوئی برائی پہنچانا چاہے یا تم پر کوئی فضل کرنا چاہے تو کون ہے جو تمہیں بچا سکے (یا تم سے روک سکے؟) ، اپنے لیے بجز اللہ تعالیٰ کے نہ کوئی حمایتی پائیں گے نہ مددگار.');
INSERT INTO "junagarri" VALUES(33,18,'33:18','اللہ تعالیٰ تم میں سے انہیں (بخوبی) جانتا ہے جو دوسروں کو روکتے ہیں اور اپنے بھائی بندوں سے کہتے ہیں کہ ہمارے پاس چلے آؤ۔ اور کبھی کبھی ہی لڑائی میں آجاتے ہیں. ');
INSERT INTO "junagarri" VALUES(33,19,'33:19','تمہاری مدد میں (پورے) بخیل ہیں ، پھر جب خوف ودہشت کا موقعہ آجائے تو آپ انہیں دیکھیں گے کہ آپ کی طرف نظریں جما دیتے ہیں اور ان کی انکھیں اس طرح گھومتی ہیں جیسے اس شخص کی جس پر موت کی غشی طاری ہو ۔ پھر جب خوف جاتا رہتا ہے تو تم پر اپنی تیز زبانوں سے بڑی باتیں بناتے ہیں مال کے بڑے ہی حریص ہیں ، یہ ایمان لائے ہی نہیں ہیں اللہ تعالیٰ نے ان کے تمام اعمال نابود کر دیئے ہیں ، اور اللہ تعالیٰ پر یہ بہت ہی آسان ہے. ');
INSERT INTO "junagarri" VALUES(33,20,'33:20','سمجھتے ہیں کہ اب تک لشکر چلے نہیں گئے ، اور اگر فوجیں آجائیں تو تمنائیں کرتے ہیں کہ کاش! وه صحرا میں بادیہ نشینوں کے ساتھ ہوتے کہ تمہاری خبریں دریافت کیا کرتے ، اگر وه تم میں موجود ہوتے (تو بھی کیا؟) نہ لڑتے مگر برائے نام. ');
INSERT INTO "junagarri" VALUES(33,21,'33:21','یقیناً تمہارے لئے رسول اللہ میں عمده نمونہ (موجود) ہے ، ہر اس شخص کے لئے جو اللہ تعالیٰ کی اور قیامت کے دن کی توقع رکھتا ہے اور بکثرت اللہ تعالیٰ کی یاد کرتا ہے. ');
INSERT INTO "junagarri" VALUES(33,22,'33:22','اور ایمان والوں نے جب (کفار کے) لشکروں کو دیکھا (بے ساختہ) کہہ اٹھے! کہ انہیں کا وعده ہمیں اللہ تعالیٰ نے اور اس کے رسول نے دیا تھا اور اللہ تعالیٰ اور اس کے رسول نے سچ فرمایا ، اور اس (چیز) نے ان کے ایمان میں اور شیوہٴ فرماں برداری میں اور اضافہ کر دیا. ');
INSERT INTO "junagarri" VALUES(33,23,'33:23','مومنوں میں (ایسے) لوگ بھی ہیں جنہوں نے جو عہد اللہ تعالیٰ سے کیا تھا انہیں سچا کر دکھایا ، بعض نے تو اپنا عہد پورا کر دیا اور بعض (موقعہ کے) منتظر ہیں اور انہوں نے کوئی تبدیلی نہیں کی. ');
INSERT INTO "junagarri" VALUES(33,24,'33:24','تاکہ اللہ تعالیٰ سچوں کو ان کی سچائی کا بدلہ دے اور اگر چاہے تو منافقوں کو سزا دے یا ان کی توبہ قبول فرمائے ، اللہ تعالیٰ بڑا ہی بخشنے والا بہت ہی مہربان ہے.');
INSERT INTO "junagarri" VALUES(33,25,'33:25','اور اللہ تعالیٰ نےکافروں کو غصے بھرے ہوئے ہی (نامراد) لوٹا دیا انہوں نے کوئی فائده نہیں پایا ، اور اس جنگ میں اللہ تعالیٰ خود ہی مومنوں کو کافی ہوگیا اللہ تعالیٰ بڑی قوتوں والا اور غالب ہے.');
INSERT INTO "junagarri" VALUES(33,26,'33:26','اور جن اہل کتاب نے ان سے سازباز کر لی تھی انہیں (بھی) اللہ تعالیٰ نے ان کے قلعوں سے نکال دیا اور ان کے دلوں میں (بھی) رعب بھر دیا کہ تم ان کے ایک گروه کو قتل کر رہے ہو اور ایک گروه کو قیدی بنا رہے ہو.');
INSERT INTO "junagarri" VALUES(33,27,'33:27','اور اس نے تمہیں ان کی زمینوں کا اور ان کے گھر بار کا اور ان کے مال کا وارث کر دیا اور اس زمین کا بھی جس کو تمہارے قدموں نے روندا نہیں ، اللہ تعالیٰ ہر چیز پر قادر ہے.');
INSERT INTO "junagarri" VALUES(33,28,'33:28','اے نبی! اپنی بیویوں سے کہہ دو کہ اگر تم زندگانی دنیا اور زینت دنیا چاہتی ہو تو آؤ میں تمہیں کچھ دے دلا دوں اور تمہیں اچھائی کے ساتھ رخصت کر دوں.');
INSERT INTO "junagarri" VALUES(33,29,'33:29','اور اگر تمہاری مراد اللہ اور اس کا رسول اور آخرت کا گھر ہے تو (یقین مانو کہ) تم میں سے نیک کام کرنے والیوں کے لئے اللہ تعالیٰ نے بہت زبردست اجر رکھ چھوڑے ہیں. ');
INSERT INTO "junagarri" VALUES(33,30,'33:30','اے نبی کی بیویو! تم میں سے جو بھی کھلی بے حیائی (کا ارتکاب) کرے گی اسے دوہرا دوہرا عذاب دیا جائے گا ، اور اللہ تعالیٰ کے نزدیک یہ بہت ہی سہل (سی بات) ہے.');
INSERT INTO "junagarri" VALUES(33,31,'33:31','اور تم میں سے جو کوئی اللہ کی اور اس کے رسول کی فرمانبرداری کرے گی اور نیک کام کرے گی ہم اسے اجر (بھی) دوہرا دیں گے اور اس کے لئے ہم نے بہترین روزی تیار کر رکھی ہے.');
INSERT INTO "junagarri" VALUES(33,32,'33:32','اے نبی کی بیویو! تم عام عورتوں کی طرح نہیں ہو ، اگر تم پرہیزگاری اختیار کرو تو نرم لہجے سے بات نہ کرو کہ جس کے دل میں روگ ہو وه کوئی برا خیال کرے اور ہاں قاعدے کے مطابق کلام کرو. ');
INSERT INTO "junagarri" VALUES(33,33,'33:33','اور اپنے گھروں میں قرار سے رہو اور قدیم جاہلیت کے زمانے کی طرح اپنے بناؤ کا اظہار نہ کرو اور نماز ادا کرتی رہو اور زکوٰة دیتی رہو اور اللہ اور اس کے رسول کی اطاعت گزاری کرو ۔ اللہ تعالیٰ یہی چاہتا ہے کہ اے نبی کی گھر والیو ! تم سے وه (ہر قسم کی) گندگی کو دور کردے اور تمہیں خوب پاک کردے.');
INSERT INTO "junagarri" VALUES(33,34,'33:34','اور تمہارے گھروں میں اللہ کی جو آیتیں اور رسول کی احادیث پڑھی جاتی ہیں ان کا ذکر کرتی رہو ، یقیناً اللہ تعالیٰ لطف کرنے والا خبردار ہے.');
INSERT INTO "junagarri" VALUES(33,35,'33:35','بےشک مسلمان مرد اور مسلمان عورتیں مومن مرد اور مومن عورتیں فرماں برداری کرنے والے مرد اور فرمانبردار عورتیں راست باز مرد اور راست باز عورتیں صبر کرنے والے مرد اور صبر کرنے والی عورتیں، عاجزی کرنے والے مرد اور عاجزی کرنے والی عورتیں، خیرات کرنے والے مرد اور خیرات کرنے والی عورتیں، روزے رکھنے والے مرد اور روزے رکھنے والی عورتیں اپنی شرمگاه کی حفاظت کرنے والے مرد اور حفاظت کرنے والیاں بکثرت اللہ کا ذکر کرنے والے اور ذکر کرنے والیاں ان (سب کے) لئے اللہ تعالیٰ نے (وسیع) مغفرت اور بڑا ثواب تیار کر رکھا ہے.');
INSERT INTO "junagarri" VALUES(33,36,'33:36','اور (دیکھو) کسی مومن مرد و عورت کو اللہ اور اس کے رسول کا فیصلہ کے بعد اپنے کسی امر کا کوئی اختیار باقی نہیں رہتا ، (یاد رکھو) اللہ تعالیٰ اور اس کے رسول کی جو بھی نافرمانی کرے گا وه صریح گمراہی میں پڑے گا.');
INSERT INTO "junagarri" VALUES(33,37,'33:37','(یاد کرو) جب کہ تو اس شخص سے کہہ رہا تھا جس پر اللہ نے بھی انعام کیا اور تو نے بھی کہ تو اپنی بیوی کو اپنے پاس رکھ اور اللہ سے ڈر اور تو اپنے دل میں وه بات چھپائے ہوئے تھا جسے اللہ ظاہر کرنے والا تھا اور تو لوگوں سے خوف کھاتا تھا، حالانکہ اللہ تعالیٰ اس کا زیاده حق دار تھا کہ تو اس سے ڈرے ، پس جب کہ زید نے اس عورت سے اپنی غرض پوری کرلی ہم نے اسے تیرے نکاح میں دے دیا تاکہ مسلمانوں پر اپنے لے پالکوں کی بیویوں کے بارے میں کسی طرح کی تنگی نہ رہے جب کہ وه اپنی غرض ان سے پوری کرلیں ، اللہ کا (یہ) حکم تو ہو کر ہی رہنے والا تھا. ');
INSERT INTO "junagarri" VALUES(33,38,'33:38','جو چیزیں اللہ تعالیٰ نے اپنے نبی کے لئے مقرر کی ہیں ان میں نبی پر کوئی حرج نہیں ، (یہی) اللہ کا دستور ان میں بھی رہا جو پہلے ہوئے اور اللہ تعالیٰ کے کام اندازے پر مقرر کئے ہوئے ہیں. ');
INSERT INTO "junagarri" VALUES(33,39,'33:39','یہ سب ایسے تھے کہ اللہ تعالیٰ کے احکام پہنچایا کرتے تھے اور اللہ ہی سے ڈرتے تھے اور اللہ کے سوا کسی سے نہیں ڈرتے تھے ، اور اللہ تعالیٰ حساب لینے کے لئے کافی ہے. ');
INSERT INTO "junagarri" VALUES(33,40,'33:40','(لوگو) تمہارے مردوں میں سے کسی کے باپ محمد ﴿صلی اللہ علیہ وسلم﴾ نہیں لیکن آپ اللہ تعالیٰ کے رسول ہیں اور تمام نبیوں کے ختم کرنے والے ، اور اللہ تعالی ہر چیز کا (بخوبی) جاننے والا ہے.');
INSERT INTO "junagarri" VALUES(33,41,'33:41','مسلمانو! اللہ تعالیٰ کا ذکر بہت زیاده کرو.');
INSERT INTO "junagarri" VALUES(33,42,'33:42','اور صبح وشام اس کی پاکیزگی بیان کرو.');
INSERT INTO "junagarri" VALUES(33,43,'33:43','وہی ہے جو تم پر اپنی رحمتیں بھیجتا ہے اور اس کے فرشتے (تمہارے لئے دعائے رحمت کرتے ہیں) تاکہ وه تمہیں اندھیروں سے اجالے کی طرف لے جائے اور اللہ تعالیٰ مومنوں پر بہت ہی مہربان ہے.');
INSERT INTO "junagarri" VALUES(33,44,'33:44','جس دن یہ (اللہ سے) ملاقات کریں گے ان کا تحفہ سلام ہوگا ، ان کے لئے اللہ تعالیٰ نے باعزت اجر تیار کر رکھا ہے.');
INSERT INTO "junagarri" VALUES(33,45,'33:45','اے نبی! یقیناً ہم نے ہی آپ کو (رسول بنا کر) گواہیاں دینے والا ، خوشخبریاں سنانے والا، آگاه کرنے والا بھیجا ہے.');
INSERT INTO "junagarri" VALUES(33,46,'33:46','اور اللہ کے حکم سے اس کی طرف بلانے والا اور روشن چراغ. ');
INSERT INTO "junagarri" VALUES(33,47,'33:47','آپ مومنوں کو خوشخبری سنا دیجئے! کہ ان کے لئے اللہ کی طرف سے بہت بڑا فضل ہے.');
INSERT INTO "junagarri" VALUES(33,48,'33:48','اور کافروں اور منافقوں کا کہنا نہ مانیئے! اور جو ایذا (ان کی طرف سے پہنچے) اس کا خیال بھی نہ کیجیئے اللہ پر بھروسہ کئے رہیں، اور کافی ہے اللہ تعالیٰ کام بنانے والا.');
INSERT INTO "junagarri" VALUES(33,49,'33:49','اے مومنو! جب تم مومن عورتوں سے نکاح کرو پھر ہاتھ لگانے سے پہلے (ہی) طلاق دے دو تو ان پر تمہارا کوئی حق عدت کا نہیں جسے تم شمار کرو ، پس تم کچھ نہ کچھ انہیں دے دو اور بھلے طریق پر انہیں رخصت کر دو. ');
INSERT INTO "junagarri" VALUES(33,50,'33:50','اے نبی! ہم نے تیرے لئے تیری وه بیویاں حلال کر دی ہیں جنہوں تو ان کا مہر دے چکا ہے اور وه لونڈیاں بھی جو اللہ تعالیٰ نے غنیمت میں تجھے دی ہیں اور تیرے چچا کی لڑکیاں اور پھوپھیوں کی بیٹیاں اور تیرے ماموں کی بیٹیاں اور تیری خالاؤں کی بیٹیاں بھی جنہوں نے تیرے ساتھ ہجرت کی ہے ، اور وه باایمان عورت جو اپنا نفس نبی کو ہبہ کردے یہ اس صورت میں کہ خود نبی بھی اس سے نکاح کرنا چاہے ، یہ خاص طور پر صرف تیرے لئے ہی ہے اور مومنوں کے لئے نہیں ، ہم اسے بخوبی جانتے ہیں جو ہم نے ان پر ان کی بیویوں اور لونڈیوں کے بارے میں (احکام) مقرر کر رکھے ہیں ، یہ اس لئے کہ تجھ پر حرج واقع نہ ہو ، اللہ تعالیٰ بہت بخشنے اور بڑے رحم والا ہے.');
INSERT INTO "junagarri" VALUES(33,51,'33:51','ان میں سے جسے تو چاہے دور رکھ دے اور جسے چاہے اپنے پاس رکھ لے ، اور اگر تو نے ان میں سے بھی کسی کو اپنے پاس بلالے جنہیں تو نے الگ کر رکھا تھا تو تجھ پر کوئی گناه نہیں ، اس میں اس بات کی زیاده توقع ہے کہ ان عورتوں کی آنکھیں ٹھنڈی رہیں اور وه رنجیده نہ ہوں اور جو کچھ بھی تو انہیں دے دے اس پر سب کی سب راضی رہیں ۔ تمہارے دلوں میں جو کچھ ہے اسے اللہ (خوب) جانتا ہے ۔ اللہ تعالیٰ بڑا ہی علم اور حلم والا ہے.');
INSERT INTO "junagarri" VALUES(33,52,'33:52','اس کے بعد اور عورتیں آپ کے لئے حلال نہیں اور نہ یہ (درست ہے) کہ ان کے بدلے اور عورتوں سے (نکاح کرے) اگر چہ ان کی صورت اچھی بھی لگتی ہو مگر جو تیری مملوکہ ہوں ۔ اور اللہ تعالیٰ ہر چیز کا (پورا) نگہبان ہے.');
INSERT INTO "junagarri" VALUES(33,53,'33:53','اے ایمان والو! جب تک تمہیں اجازت نہ دی جائے تم نبی کے گھروں میں نہ جایا کرو کھانے کے لئے ایسے وقت میں کہ اس کے پکنے کا انتظار کرتے رہو بلکہ جب بلایا جائے جاؤ اور جب کھا چکو نکل کھڑے ہو، وہیں باتوں میں مشغول نہ ہوجایا کرو۔ نبی کو تمہاری اس بات سے تکلیف ہوتی ہے۔ تو وه لحاظ کر جاتے ہیں اور اللہ تعالیٰ (بیان) حق میں کسی کا لحاظ نہیں کرتا ، جب تم نبی کی بیویوں سے کوئی چیز طلب کرو تو پردے کے پیچھے سے طلب کرو ۔ تمہارے اور ان کے دلوں کے لئے کامل پاکیزگی یہی ہے ، نہ تمہیں یہ جائز ہے کہ تم رسول اللہ کو تکلیف دو اور نہ تمہیں یہ حلال ہے کہ آپ کے بعد کسی وقت بھی آپ کی بیویوں سے نکاح کرو۔ (یاد رکھو) اللہ کے نزدیک یہ بہت بڑا (گناه) ہے. ');
INSERT INTO "junagarri" VALUES(33,54,'33:54','تم کسی چیز کو ظاہر کرو یا مخفی رکھو اللہ تو ہر چیز کا بخوبی علم رکھنے والا ہے.');
INSERT INTO "junagarri" VALUES(33,55,'33:55','ان عورتوں پر کوئی گناه نہیں کہ وه اپنے باپوں اور اپنے بیٹوں اور بھائیوں اور بھتیجوں اور بھانجوں اور اپنی (میل جول کی) عورتوں اور ملکیت کے ماتحتوں (لونڈی، غلام) کے سامنے ہوں ۔ (عورتو!) اللہ سے ڈرتی رہو۔ اللہ تعالیٰ یقیناً ہر چیز پر شاہد ہے. ');
INSERT INTO "junagarri" VALUES(33,56,'33:56','اللہ تعالیٰ اور اس کے فرشتے اس نبی پر رحمت بھیجتے ہیں۔ اے ایمان والو! تم (بھی) ان پر درود بھیجو اور خوب سلام (بھی) بھیجتے رہا کرو. ');
INSERT INTO "junagarri" VALUES(33,57,'33:57','جو لوگ اللہ اور اس کے رسول کو ایذا دیتے ہیں ان پر دنیا اور آخرت میں اللہ کی پھٹکار ہے اور ان کے لئے نہایت رسوا کن عذاب ہے. ');
INSERT INTO "junagarri" VALUES(33,58,'33:58','اور جو لوگ مومن مردوں اور مومن عورتوں کو ایذا دیں بغیر کسی جرم کے جو ان سے سرزد ہوا ہو، وه (بڑے ہی) بہتان اور صریح گناه کا بوجھ اٹھاتے ہیں. ');
INSERT INTO "junagarri" VALUES(33,59,'33:59','اے نبی! اپنی بیویوں سے اور اپنی صاحبزادیوں سے اور مسلمانوں کی عورتیں سے کہہ دو کہ وه اپنے اوپر اپنی چادریں لٹکا لیا کریں ، اس سے بہت جلد ان کی شناخت ہو جایا کرے گی پھر نہ ستائی جائیں گی ، اور اللہ تعالیٰ بخشنے والا مہربان ہے.');
INSERT INTO "junagarri" VALUES(33,60,'33:60','اگر (اب بھی) یہ منافق اور وه جن کے دلوں میں بیماری ہے اور وه لوگ جو مدینہ میں غلط افواہیں اڑانے والے ہیں باز نہ آئے تو ہم آپ کو ان (کی تباہی) پر مسلط کردیں گے پھر تو وه چند دن ہی آپ کے ساتھ اس (شہر) میں ره سکیں گے.');
INSERT INTO "junagarri" VALUES(33,61,'33:61','ان پر پھٹکار برسائی گئی، جہاں بھی مل جائیں پکڑے جائیں اور خوب ٹکڑے ٹکڑے کردیئے جائیں. ');
INSERT INTO "junagarri" VALUES(33,62,'33:62','ان سے اگلوں میں بھی اللہ کا یہی دستور جاری رہا۔ اور تو اللہ کے دستور میں ہرگز ردوبدل نہ پائے گا.');
INSERT INTO "junagarri" VALUES(33,63,'33:63','لوگ آپ سے قیامت کے بارے میں سوال کرتے ہیں۔ آپ کہہ دیجیئے! کہ اس کا علم تو اللہ ہی کو ہے، آپ کو کیا خبر بہت ممکن ہے قیامت بالکل ہی قریب ہو.');
INSERT INTO "junagarri" VALUES(33,64,'33:64','اللہ تعالیٰ نے کافروں پر لعنت کی ہے اور ان کے لئے بھڑکتی ہوئی آگ تیار کر رکھی ہے.');
INSERT INTO "junagarri" VALUES(33,65,'33:65','جس میں وه ہمیشہ ہمیشہ رہیں گے۔ وه کوئی حامی ومددگار نہ پائیں گے.');
INSERT INTO "junagarri" VALUES(33,66,'33:66','اس دن ان کے چہرے آگ میں الٹ پلٹ کئے جائیں گے۔ (حسرت وافسوس سے) کہیں گے کہ کاش ہم اللہ تعالیٰ اور رسول کی اطاعت کرتے.');
INSERT INTO "junagarri" VALUES(33,67,'33:67','اور کہیں گے اے ہمارے رب! ہم نے اپنے سرداروں اور اپنے بڑوں کی مانی جنہوں نے ہمیں راه راست سے بھٹکا دیا. ');
INSERT INTO "junagarri" VALUES(33,68,'33:68','پروردگار تو انہیں دگنا عذاب دے اور ان پر بہت بڑی لعنت نازل فرما.');
INSERT INTO "junagarri" VALUES(33,69,'33:69','اے ایمان والو! ان لوگوں جیسے نہ بن جاؤ جنہوں نے موسیٰ کو تکلیف دی پس جو بات انہوں نے کہی تھی اللہ نے انہیں اس سے بری فرما دیا ، اور وه اللہ کے نزدیک باعزت تھے.');
INSERT INTO "junagarri" VALUES(33,70,'33:70','اے ایمان والو! اللہ تعالیٰ سے ڈرو اور سیدھی سیدھی (سچی) باتیں کیا کرو. ');
INSERT INTO "junagarri" VALUES(33,71,'33:71','تاکہ اللہ تعالیٰ تمہارے کام سنوار دے اور تمہارے گناه معاف فرما دے ، اور جو بھی اللہ اور اس کے رسول کی تابعداری کرے گا اس نے بڑی مراد پالی.');
INSERT INTO "junagarri" VALUES(33,72,'33:72','ہم نے اپنی امانت کو آسمانوں پر زمین پر اور پہاڑوں پر پیش کیا لیکن سب نے اس کے اٹھانے سے انکار کردیا اور اس سے ڈر گئے (مگر) انسان نے اسے اٹھا لیا ، وه بڑا ہی ظالم جاہل ہے. ');
INSERT INTO "junagarri" VALUES(33,73,'33:73','(یہ اس لئے) کہ اللہ تعالیٰ منافق مردوں عورتوں اور مشرک مردوں عورتوں کو سزا دے اور مومن مردوں عورتوں کی توبہ قبول فرمائے ، اور اللہ تعالیٰ بڑا ہی بخشنے والا اور مہربان ہے.');
INSERT INTO "junagarri" VALUES(34,1,'34:1','تمام تعریفیں اس اللہ کے لئے سزاوار ہیں جس کی ملکیت میں وه سب کچھ ہے جو آسمانوں اور زمین میں ہے آخرت میں بھی تعریف اسی کے لئے ہے ، وه (بڑی) حکمتوں والا اور (پورا) خبردار ہے.');
INSERT INTO "junagarri" VALUES(34,2,'34:2','جو زمین میں جائے اور جو اس سے نکلے جو آسمان سے اترے اور جو چڑھ کر اس میں جائے وه سب سے باخبر ہے۔ اور وه مہربان نہایت بخشش والا ہے.');
INSERT INTO "junagarri" VALUES(34,3,'34:3','کفار کہتے ہیں کہ ہم پر قیامت نہیں آئے گی۔ آپ کہہ دیجیئے! کہ مجھے میرے رب کی قسم! جو عالم الغیب ہے کہ وه یقیناً تم پر آئے گی اللہ تعالیٰ سے ایک ذرے کے برابر کی چیز بھی پوشیده نہیں نہ آسمانوں میں اور نہ زمین میں بلکہ اس سے بھی چھوٹی اور بڑی چیز کھلی کتاب میں موجود ہے. ');
INSERT INTO "junagarri" VALUES(34,4,'34:4','تاکہ وه ایمان والوں اور نیکوکاروں کو بھلائی عطا فرمائے ، یہی لوگ ہیں جن کے لئے مغفرت اور عزت کی روزی ہے.');
INSERT INTO "junagarri" VALUES(34,5,'34:5','اور ہماری آیتوں کو نیچا دکھانے کی جنہوں نے کوشش کی ہے یہ وه لوگ ہیں جن کے لئے بدترین قسم کا دردناک عذاب ہے.');
INSERT INTO "junagarri" VALUES(34,6,'34:6','اور جنہیں علم ہے وه دیکھ لیں گے کہ جو کچھ آپ کی جانب آپ کے رب کی طرف سے نازل ہوا ہے وه (سراسر) حق ہے اور اللہ غالب خوبیوں والے کی راه کی رہبری کرتا ہے. ');
INSERT INTO "junagarri" VALUES(34,7,'34:7','اور کافروں نے کہا (آؤ) ہم تمہیں ایک ایسا شخص بتلائیں جو تمہیں یہ خبر پہنچا رہا ہے کہ جب تم بالکل ہی ریزه ریزه ہوجاؤ گے تو تم پھر سے ایک نئی پیدائش میں آؤ گے. ');
INSERT INTO "junagarri" VALUES(34,8,'34:8','(ہم نہیں کہہ سکتے) کہ خود اس نے (ہی) اللہ پر جھوٹ باندھ لیا ہے یا اسے دیوانگی ہے بلکہ (حقیقت یہ ہے) کہ آخرت پر یقین نہ رکھنے والے ہی عذاب میں اور دور کی گمراہی میں ہیں. ');
INSERT INTO "junagarri" VALUES(34,9,'34:9','کیا پس وه اپنے آگے پیچھے آسمان وزمین کو دیکھ نہیں رہے ہیں ؟ اگر ہم چاہیں تو انہیں زمین میں دھنسا دیں یا ان پر آسمان کے ٹکڑے گرادیں ، یقیناً اس میں پوری دلیل ہے ہر اس بندے کے لئے جو (دل سے) متوجہ ہو.');
INSERT INTO "junagarri" VALUES(34,10,'34:10','اور ہم نے داؤد پر اپنا فضل کیا ، اے پہاڑو! اس کے ساتھ رغبت سے تسبیح پڑھا کرو اور پرندوں کو بھی (یہی حکم ہے) اور ہم نے اس کے لئے لوہا نرم کردیا. ');
INSERT INTO "junagarri" VALUES(34,11,'34:11','کہ تو پوری پوری زرہیں بنا اور جوڑوں میں اندازه رکھ تم سب نیک کام کیا کرو ۔ (یقین مانو) کہ میں تمہارے اعمال دیکھ رہا ہوں.');
INSERT INTO "junagarri" VALUES(34,12,'34:12','اور ہم نے سلیمان کے لئے ہوا کو مسخر کردیا کہ صبح کی منزل اس کی مہینہ بھر کی ہوتی تھی اور شام کی منزل بھی اور ہم نے ان کے لئے تانبے کا چشمہ بہا دیا ۔ اور اس کے رب کے حکم سے بعض جنات اس کی ماتحتی میں اس کے سامنے کام کرتے تھے اور ان میں سے جو بھی ہمارے حکم سے سرتابی کرے ہم اسے بھڑکتی ہوئی آگ کے عذاب کا مزه چکھائیں گے. ');
INSERT INTO "junagarri" VALUES(34,13,'34:13','جو کچھ سلیمان چاہتے وه جنات تیار کردیتے مثلا قلعے اور مجسمے اور حوضوں کے برابر لگن اور چولہوں پر جمی ہوئی مضبوط دیگیں ، اے آل داؤد اس کے شکریہ میں نیک عمل کرو، میرے بندوں میں سے شکرگزار بندے کم ہی ہوتے ہیں.');
INSERT INTO "junagarri" VALUES(34,14,'34:14','پھر جب ہم نے ان پر موت کا حکم بھیج دیا تو ان کی خبر جنات کو کسی نے نہ دی سوائے گھن کے کیڑے کے جو ان کی عصا کو کھا رہا تھا۔ پس جب (سلیمان) گر پڑے اس وقت جنوں نے جان لیا کہ اگر وه غیب دان ہوتے تو اس کے ذلت کے عذاب میں مبتلا نہ رہتے. ');
INSERT INTO "junagarri" VALUES(34,15,'34:15','قوم سبا کے لئے اپنی بستیوں میں (قدرت الٰہی کی) نشانی تھی ان کے دائیں بائیں دو باغ تھے (ہم نے ان کو حکم دیا تھا کہ) اپنے رب کی دی ہوئی روزی کھاؤ اور اس کا شکر ادا کرو ، یہ عمده شہر اور وه بخشنے والا رب ہے. ');
INSERT INTO "junagarri" VALUES(34,16,'34:16','لیکن انہوں نے روگردانی کی تو ہم نے ان پر زور کے سیلاب (کا پانی) بھیج دیا اور ہم نے ان کے (ہرے بھرے) باغوں کے بدلے دو (ایسے) باغ دیئے جو بدمزه میوؤں والے اور (بکثرت) جھاؤ اور کچھ بیری کے درختوں والے تھے. ');
INSERT INTO "junagarri" VALUES(34,17,'34:17','ہم نے ان کی ناشکری کا یہ بدلہ انہیں دیا۔ ہم (ایسی) سخت سزا بڑے بڑے ناشکروں ہی کو دیتے ہیں.');
INSERT INTO "junagarri" VALUES(34,18,'34:18','اور ہم نے ان کے اور ان بستیوں کے درمیان جن میں ہم نے برکت دے رکھی تھی چند بستیاں اور (آباد) رکھی تھیں جو برسرراه ظاہر تھیں، اور ان میں چلنے کی منزلیں مقرر کردی تھیں ان میں راتوں اور دنوں کو بہ امن وامان چلتے پھرتے رہو. ');
INSERT INTO "junagarri" VALUES(34,19,'34:19','لیکن انہوں نے پھر کہا کہ اے ہمارے پروردگار! ہمارے سفر دور دراز کردے چونکہ خود انہوں نے اپنے ہاتھوں اپنا برا کیا اس لئے ہم نے انہیں (گزشتہ) فسانوں کی صورت میں کردیا اور ان کے ٹکڑے ٹکڑے اڑا دیئے ۔ بلاشبہ ہر ایک صبروشکر کرنے والے کے لئے اس (ماجرے) میں بہت سی عبرتیں ہیں.');
INSERT INTO "junagarri" VALUES(34,20,'34:20','اور شیطان نے ان کے بارے میں اپنا گمان سچا کر دکھایا یہ لوگ سب کے سب اس کے تابعدار بن گئے سوائے مومنوں کی ایک جماعت کے.');
INSERT INTO "junagarri" VALUES(34,21,'34:21','شیطان کا ان پر کوئی زور (اور دباؤ) نہ تھا مگر اس لئے کہ ہم ان لوگوں کو جو آخرت پر ایمان رکھتے ہیں ظاہر کردیں ان لوگوں میں سے جو اس سے شک میں ہیں۔ اور آپ کا رب (ہر) ہر چیز پر نگہبان ہے.');
INSERT INTO "junagarri" VALUES(34,22,'34:22','کہہ دیجیئے! کہ اللہ کے سوا جن جن کا تمہیں گمان ہے (سب) کو پکار لو ، نہ ان میں سے کسی کو آسمانوں اور زمینوں میں سے ایک ذره کا اختیار ہے نہ ان کا ان میں کوئی حصہ ہے نہ ان میں سے کوئی اللہ کا مددگار ہے. ');
INSERT INTO "junagarri" VALUES(34,23,'34:23','شفاعت (سفارش) بھی اس کے پاس کچھ نفع نہیں دیتی بجز ان کے جن کے لئے اجازت ہوجائے ۔ یہاں تک کہ جب ان کے دلوں سے گھبراہٹ دور کردی جاتی ہے تو پوچھتے ہیں تمہارے پرودگار نے کیا فرمایا؟ جواب دیتے ہیں کہ حق فرمایا اور وه بلند وبالا اور بہت بڑا ہے.');
INSERT INTO "junagarri" VALUES(34,24,'34:24','پوچھیئے کہ تمہیں آسمانوں اور زمین سے روزی کون پہنچاتا ہے؟ (خود) جواب دیجیئے! کہ اللہ تعالیٰ۔ (سنو) ہم یا تم۔ یا تو یقیناً ہدایت پر ہیں یا کھلی گمراہی میں ہیں؟ ');
INSERT INTO "junagarri" VALUES(34,25,'34:25','کہہ دیجیئے! کہ ہمارے کئے ہوئے گناہوں کی بابت تم سے کوئی سوال نہ کیا جائے گا نہ تمہارے اعمال کی بازپرس ہم سے کی جائے گی.');
INSERT INTO "junagarri" VALUES(34,26,'34:26','انہیں خبر دے دیجیئے کہ ہم سب کو ہمارا رب جمع کرکے پھر ہم میں سچے فیصلے کردے گا ۔ وه فیصلے چکانے والا ہے اور دانا.');
INSERT INTO "junagarri" VALUES(34,27,'34:27','کہہ دیجیئے! کہ اچھا مجھے بھی تو انہیں دکھا دو جنہیں تم اللہ کا شریک ٹھہرا کر اس کے ساتھ ملا رہے ہو، ایسا ہرگز نہیں ، بلکہ وہی اللہ ہے غالب باحکمت.');
INSERT INTO "junagarri" VALUES(34,28,'34:28','ہم نے آپ کو تمام لوگوں کے لئے خوشخبریاں سنانے والا اور ڈرانے والا بنا کر بھیجا ہے ہاں مگر (یہ صحیح ہے) کہ لوگوں کی اکثریت بےعلم ہے. ');
INSERT INTO "junagarri" VALUES(34,29,'34:29','پوچھتے ہیں کہ وه وعده ہے کب؟ سچے ہو تو بتا دو. ');
INSERT INTO "junagarri" VALUES(34,30,'34:30','جواب دیجیئے کہ وعدے کا دن ٹھیک معین ہے جس سے ایک ساعت نہ تم پیچھے ہٹ سکتے ہو نہ آگے بڑھ سکتے ہو. ');
INSERT INTO "junagarri" VALUES(34,31,'34:31','اور کافروں نے کہا کہ ہم ہرگز نہ تو اس قرآن کو مانیں نہ اس سے پہلے کی کتابوں کو ! اے دیکھنے والے کاش کہ تو ان ظالموں کو اس وقت دیکھتا جب کہ یہ اپنے رب کے سامنے کھڑے ہوئے ایک دوسرے کو الزام دے رہے ہوں گے کمزور لوگ بڑے لوگوں سے کہیں گے اگر تم نہ ہوتے تو ہم تو مومن ہوتے. ');
INSERT INTO "junagarri" VALUES(34,32,'34:32','یہ بڑے لوگ ان کمزوروں کو جواب دیں گے کہ کیا تمہارے پاس ہدایت آچکنے کے بعد ہم نے تمہیں اس سے روکا تھا؟ (نہیں) بلکہ تم (خود) ہی مجرم تھے. ');
INSERT INTO "junagarri" VALUES(34,33,'34:33','(اس کے جواب میں) یہ کمزور لوگ ان متکبروں سے کہیں گے، (نہیں نہیں) بلکہ دن رات مکروفریب سے ہمیں اللہ کے ساتھ کفر کرنے اور اس کے شریک مقرر کرنے کا تمہارا حکم دینا ہماری بےایمانی کا باعث ہوا ، اور عذاب کو دیکھتے ہی سب کے سب دل میں پشیمان ہو رہے ہوں گے ، اور کافروں کی گردنوں میں ہم طوق ڈال دیں گے انہیں صرف ان کے کئے کرائے اعمال کا بدلہ دیا جائے گا. ');
INSERT INTO "junagarri" VALUES(34,34,'34:34','اور ہم تو جس بستی میں جو بھی آگاه کرنے والا بھیجا وہاں کے خوشحال لوگوں نے یہی کہا کہ جس چیز کے ساتھ تم بھیجے گئے ہو ہم اس کے ساتھ کفر کرنے والے ہیں.');
INSERT INTO "junagarri" VALUES(34,35,'34:35','اور کہا ہم مال واولاد میں بہت بڑھے ہوئے ہیں یہ نہیں ہوسکتا کہ ہم عذاب دیئے جائیں. ');
INSERT INTO "junagarri" VALUES(34,36,'34:36','کہہ دیجیئے! کہ میرا رب جس کے لئے چاہے روزی کشاده کردیتا ہے اور تنگ بھی کردیتا ہے ، لیکن اکثر لوگ نہیں جانتے.');
INSERT INTO "junagarri" VALUES(34,37,'34:37','اور تمہارے مال اور اولاد ایسے نہیں کہ تمہیں ہمارے پاس (مرتبوں سے) قریب کردیں ہاں جو ایمان لائیں اور نیک عمل کریں ان کے لئے ان کے اعمال کا دوہرا اجر ہے اور وه نڈر و بےخوف ہو کر بالا خانوں میں رہیں گے.');
INSERT INTO "junagarri" VALUES(34,38,'34:38','اور جو لوگ ہماری آیتوں کے مقابلے کی تگ ودو میں لگے رہتے ہیں یہی ہیں جو عذاب میں پکڑ کر حاضر رکھے جائیں گے.');
INSERT INTO "junagarri" VALUES(34,39,'34:39','کہہ دیجیئے! کہ میرا رب اپنے بندوں میں جس کے لئے چاہے روزی کشاده کرتا ہے اور جس کے لئے چاہے تنگ کردیتا ہے ، تم جو کچھ بھی اللہ کی راه میں خرچ کرو گے اللہ اس کا (پورا پورا) بدلہ دے گا اور وه سب سے بہتر روزی دینے والا ہے. ');
INSERT INTO "junagarri" VALUES(34,40,'34:40','اور ان سب کو اللہ اس دن جمع کرکے فرشتوں سے دریافت فرمائے گا کہ کیا یہ لوگ تمہاری عبادت کرتے تھے. ');
INSERT INTO "junagarri" VALUES(34,41,'34:41','وه کہیں گے تیری ذات پاک ہے اور ہمارا ولی تو تو ہے نہ کہ یہ بلکہ یہ لوگ جنوں کی عبادت کرتے تھے ، ان میں کے اکثر کا ان ہی پر ایمان تھا.');
INSERT INTO "junagarri" VALUES(34,42,'34:42','پس آج تم میں سے کوئی (بھی) کسی کے لئے (بھی کسی قسم کے) نفع نقصان کا مالک نہ ہوگا ۔ اور ہم ظالموں سے کہہ دیں گے کہ اس آگ کا عذاب چکھو جسے تم جھٹلاتے رہے.');
INSERT INTO "junagarri" VALUES(34,43,'34:43','اور جب ان کے سامنے ہماری صاف صاف آیتیں پڑھی جاتی ہیں تو کہتے ہیں کہ یہ ایسا شخص ہے جو تمہیں تمہارے باپ دادا کے معبودوں سے روک دینا چاہتا ہے۔ (اس کے سوا کوئی بات نہیں)، اور کہتے ہیں کہ یہ تو گھڑا ہوا جھوٹ ہے اور حق ان کے پاس آچکا پھر بھی کافر یہی کہتے رہے کہ یہ تو کھلا ہوا جادو ہے. ');
INSERT INTO "junagarri" VALUES(34,44,'34:44','اور ان (مکہ والوں) کو نہ تو ہم نے کتابیں دے رکھی ہیں جنہیں یہ پڑھتے ہوں نہ ان کے پاس آپ سے پہلے کوئی آگاه کرنے والا آیا. ');
INSERT INTO "junagarri" VALUES(34,45,'34:45','اور ان سے پہلے کے لوگوں نے بھی ہماری باتوں کو جھٹلایا تھا اور انہیں ہم نے جو دے رکھا تھا یہ تو اس کے دسویں حصے کو بھی نہیں پہنچے، پس انہوں نے میرے رسولوں کو جھٹلایا، (پھر دیکھ کہ) میرا عذاب کیسا (سخت) تھا. ');
INSERT INTO "junagarri" VALUES(34,46,'34:46','کہہ دیجیئے! کہ میں تمہیں صرف ایک ہی بات کی نصیحت کرتا ہوں کہ تم اللہ کے واسطے (ضد چھوڑ کر) دو دو مل کر یا تنہا تنہا کھڑے ہو کر سوچو تو سہی، تمہارے اس رفیق کو کوئی جنون نہیں ، وه تو تمہیں ایک بڑے (سخت) عذاب کے آنے سے پہلے ڈرانے والا ہے. ');
INSERT INTO "junagarri" VALUES(34,47,'34:47','کہہ دیجیئے! کہ جو بدلہ میں تم سے مانگوں وه تمہارے لئے ہے میرا بدلہ تو اللہ تعالیٰ ہی کے ذمے ہے ۔ وه ہر چیز سے باخبر (اور مطلع) ہے.');
INSERT INTO "junagarri" VALUES(34,48,'34:48','کہہ دیجیئے! کہ میرا رب حق (سچی وحی) نازل فرماتا ہے وه ہر غیب کا جاننے والا ہے.');
INSERT INTO "junagarri" VALUES(34,49,'34:49','کہہ دیجیئے کہ حق آچکا باطل نہ تو پہلے کچھ کرسکا ہے اور نہ کرسکے گا. ');
INSERT INTO "junagarri" VALUES(34,50,'34:50','کہہ دیجیئے کہ اگر میں بہک جاؤں تو میرے بہکنے (کا وبال) مجھ پر ہی ہے اور اگر میں راه ہدایت پر ہوں تو بہ سبب اس وحی کے جو میرا پروردگار مجھے کرتا ہے وه بڑا ہی سننے والا اور بہت ہی قریب ہے. ');
INSERT INTO "junagarri" VALUES(34,51,'34:51','اور اگر آپ (وه وقت) ملاحظہ کریں جب کہ یہ کفار گھبرائے پھریں گے پھر نکل بھاگنے کی کوئی صورت نہ ہوگی اور قریب کی جگہ سے گرفتار کر لئے جائیں گے.');
INSERT INTO "junagarri" VALUES(34,52,'34:52','اس وقت کہیں گے کہ ہم اس قرآن پر ایمان لائے لیکن اس قدر دور جگہ سے (مطلوب چیز) کیسے ہاتھ آسکتی ہے.');
INSERT INTO "junagarri" VALUES(34,53,'34:53','اس سے پہلے تو انہوں نے اس سے کفر کیا تھا، اور دور دراز سے بن دیکھے ہی پھینکتے رہے. ');
INSERT INTO "junagarri" VALUES(34,54,'34:54','ان کی چاہتوں اور ان کے درمیان پرده حائل کردیا گیا جیسے کہ اس سے پہلے بھی ان جیسوں کے ساتھ کیا گیا ، وه بھی (ان ہی کی طرح) شک وتردد میں (پڑے ہوئے) تھے. ');
INSERT INTO "junagarri" VALUES(35,1,'35:1','اس اللہ کے لئے تمام تعریفیں سزاوار ہیں جو (ابتداءً) آسمانوں اور زمین کا پیدا کرنے والا اور دو دو تین تین چار چار پروں والے فرشتوں کو اپنا پیغمبر (قاصد) بنانے والا ہے ، مخلوق میں جو چاہے زیادتی کرتا ہے اللہ تعالیٰ یقیناً ہر چیز پر قادر ہے.');
INSERT INTO "junagarri" VALUES(35,2,'35:2','اللہ تعالیٰ جو رحمت لوگوں کے لئے کھول دے سو اس کا کوئی بند کرنے والا نہیں اور جس کو بند کردے سو اس کے بعد اس کا کوئی جاری کرنے والا نہیں اور وہی غالب حکمت والا ہے.');
INSERT INTO "junagarri" VALUES(35,3,'35:3','لوگو! تم پر جو انعام اللہ تعالیٰ نے کئے ہیں انہیں یاد کرو۔ کیا اللہ کے سوا اور کوئی بھی خالق ہے جو تمہیں آسمان وزمین سے روزی پہنچائے؟ اس کے سوا کوئی معبود نہیں۔ پس تم کہاں الٹے جاتے ہو. ');
INSERT INTO "junagarri" VALUES(35,4,'35:4','اور اگر یہ آپ کو جھٹلائیں تو آپ سے پہلے کے تمام رسول بھی جھٹلائے جاچکے ہیں۔ تمام کام اللہ ہی کی طرف لوٹائے جاتے ہیں. ');
INSERT INTO "junagarri" VALUES(35,5,'35:5','لوگو! اللہ تعالیٰ کا وعده سچا ہے تمہیں زندگانیٴ دنیا دھوکے میں نہ ڈالے ، اور نہ دھوکے باز شیطان تمہیں غفلت میں ڈالے. ');
INSERT INTO "junagarri" VALUES(35,6,'35:6','یاد رکھو! شیطان تمہارا دشمن ہے، تم اسے دشمن جانو وه تو اپنے گروه کو صرف اس لئے ہی بلاتا ہے کہ وه سب جہنم واصل ہو جائیں.');
INSERT INTO "junagarri" VALUES(35,7,'35:7','جو لوگ کافر ہوئے ان کے لئے سخت سزا ہے اور جو لوگ ایمان لائے اور نیک اعمال کئے ان کے لئے بخشش ہے اور (بہت) بڑا اجر ہے. ');
INSERT INTO "junagarri" VALUES(35,8,'35:8','کیا پس وه شخص جس کے لئے اس کے برے اعمال مزین کردیئے گئے ہیں پس وه انہیں اچھاسمجھتا ہے (کیا وه ہدایت یافتہ شخص جیسا ہے)، (یقین مانو) کہ اللہ جسے چاہے گمراه کرتا ہے اور جسے چاہے راه راست دکھاتا ہے ۔ پس آپ کو ان پر غم کھا کھا کر اپنی جان ہلاکت میں نہ ڈالنی چاہیئے ، یہ جو کچھ کر رہے ہیں اس سے یقیناً اللہ تعالیٰ بخوبی واقف ہے. ');
INSERT INTO "junagarri" VALUES(35,9,'35:9','اور اللہ ہی ہوائیں چلاتا ہے جو بادلوں کو اٹھاتی ہیں پھر ہم بادلوں کو خشک زمین کی طرف لے جاتے ہیں اور اس سے اس زمین کو اس کی موت کے بعد زنده کردیتے ہیں۔ اسی طرح دوباره جی اٹھنا (بھی) ہے. ');
INSERT INTO "junagarri" VALUES(35,10,'35:10','جو شخص عزت حاصل کرنا چاہتا ہو تو اللہ تعالیٰ ہی کی ساری عزت ہے ، تمام تر ستھرے کلمات اسی کی طرف چڑھتے ہیں اور نیک عمل ان کو بلند کرتا ہے ، جو لوگ برائیوں کے داؤں گھات میں لگے رہتے ہیں ان کے لئے سخت تر عذاب ہے، اور ان کا یہ مکر برباد ہوجائے گا. ');
INSERT INTO "junagarri" VALUES(35,11,'35:11','لوگو! اللہ تعالیٰ نے تمہیں مٹی سے پھر نطفہ سے پیدا کیا ہے ، پھر تمہیں جوڑے جوڑے (مرد وعورت) بنا دیا ہے، عورتوں کو حاملہ ہونا اور بچوں کا تولد ہونا سب اس کے علم سے ہی ہے ، اور جو بڑی عمر والا عمر دیا جائے اور جس کی عمر گھٹے وه سب کتاب میں لکھا ہوا ہے ۔ اللہ تعالیٰ پر یہ بات بالکل آسان ہے.');
INSERT INTO "junagarri" VALUES(35,12,'35:12','اور برابر نہیں دو دریا یہ میٹھا ہے پیاس بجھاتا پینے میں خوشگوار اور یہ دوسرا کھاری ہے کڑوا، تم ان دونوں میں سے تازه گوشت کھاتے ہو اور وه زیوارت نکالتے ہو جنہیں تم پہنتے ہو۔ اور آپ دیکھتے ہیں کہ بڑی بڑی کشتیاں پانی کو چیرنے پھاڑنے والی ان دریاؤں میں ہیں تاکہ تم اس کا فضل ڈھونڈو اور تاکہ تم اس کا شکر کرو.');
INSERT INTO "junagarri" VALUES(35,13,'35:13','وه رات کو دن اور دن کو رات میں داخل کرتا ہے اور آفتاب وماہتاب کو اسی نے کام میں لگا دیا ہے۔ ہر ایک میعاد معین پر چل رہا ہے۔ یہی ہے اللہ تم سب کا پالنے والا اسی کی سلطنت ہے۔ جنہیں تم اس کے سوا پکار رہے ہو وه تو کھجور کی گٹھلی کے چھلکے کے بھی مالک نہیں. ');
INSERT INTO "junagarri" VALUES(35,14,'35:14','اگر تم انہیں پکارو تو وه تمہاری پکار سنتے ہی نہیں اور اگر (بالفرض) سن بھی لیں تو فریاد رسی نہیں کریں گے ، بلکہ قیامت کے دن تمہارے اس شرک کا صاف انکار کرجائیں گے ۔ آپ کو کوئی حق تعالیٰ جیسا خبردار خبریں نہ دے گا. ');
INSERT INTO "junagarri" VALUES(35,15,'35:15','اے لوگو! تم اللہ کے محتاج ہو اور اللہ بےنیاز خوبیوں والا ہے. ');
INSERT INTO "junagarri" VALUES(35,16,'35:16','اگر وه چاہے تو تم کو فنا کردے اور ایک نئی مخلوق پیدا کردے. ');
INSERT INTO "junagarri" VALUES(35,17,'35:17','اور یہ بات اللہ کو کچھ مشکل نہیں.');
INSERT INTO "junagarri" VALUES(35,18,'35:18','کوئی بھی بوجھ اٹھانے والا دوسرے کا بوجھ نہیں اٹھائے گا ، اگر کوئی گراں بار دوسرے کو اپنا بوجھ اٹھانے کے لئے بلائے گا تو وه اس میں سے کچھ بھی نہ اٹھائے گا گو قرابت دار ہی ہو ۔ تو صرف ان ہی کو آگاه کرسکتا ہے جو غائبانہ طور پر اپنے رب سے ڈرتے ہیں اور نمازوں کی پابندی کرتے ہیں اور جو بھی پاک ہوجائے وه اپنے ہی نفع کے لئے پاک ہوگا ۔ لوٹنا اللہ ہی کی طرف ہے.');
INSERT INTO "junagarri" VALUES(35,19,'35:19','اور اندھا اور آنکھوں والا برابر نہیں.');
INSERT INTO "junagarri" VALUES(35,20,'35:20','اور نہ تاریکی اور روشنی. ');
INSERT INTO "junagarri" VALUES(35,21,'35:21','اور نہ چھاؤں اور نہ دھوپ. ');
INSERT INTO "junagarri" VALUES(35,22,'35:22','اور زنده اور مردے برابر نہیں ہوسکتے ، اللہ تعالیٰ جس کو چاہتا ہے سنا دیتا ہے ، اور آپ ان لوگوں کو نہیں سنا سکتے جو قبروں میں ہیں. ');
INSERT INTO "junagarri" VALUES(35,23,'35:23','آپ تو صرف ڈرانے والے ہیں. ');
INSERT INTO "junagarri" VALUES(35,24,'35:24','ہم نے ہی آپ کو حق دے کر خوشخبری سنانے والا اور ڈر سنانے والا بنا کر بھیجا ہے اور کوئی امت ایسی نہیں ہوئی جس میں کوئی ڈر سنانے والا نہ گزرا ہو.');
INSERT INTO "junagarri" VALUES(35,25,'35:25','اور اگر یہ لوگ آپ کو جھٹلا دیں تو جو لوگ ان سے پہلے ہو گزرے ہیں انہوں نے بھی جھٹلایا تھا ان کے پاس بھی ان کے پیغمبر معجزے اور صحیفے اور روشن کتابیں لے کر آئے تھے. ');
INSERT INTO "junagarri" VALUES(35,26,'35:26','پھر میں نےان کافروں کو پکڑ لیا سو میرا عذاب کیسا ہوا. ');
INSERT INTO "junagarri" VALUES(35,27,'35:27','کیا آپ نے اس بات پر نظر نہیں کی کہ اللہ تعالیٰ نے آسمان سے پانی اتارا پھر ہم نے اس کے ذریعہ سے مختلف رنگتوں کے پھل نکالے اور پہاڑوں کے مختلف حصے ہیں سفید اور سرخ کہ ان کی بھی رنگتیں مختلف ہیں اور بہت گہرے سیاه. ');
INSERT INTO "junagarri" VALUES(35,28,'35:28','اور اسی طرح آدمیوں اور جانوروں اور چوپایوں میں بھی بعض ایسے ہیں کہ ان کی رنگتیں مختلف ہیں ، اللہ سے اس کے وہی بندے ڈرتے ہیں جو علم رکھتے ہیں واقعی اللہ تعالیٰ زبردست بڑا بخشنے والا ہے. ');
INSERT INTO "junagarri" VALUES(35,29,'35:29','جو لوگ کتاب اللہ کی تلاوت کرتے ہیں اور نماز کی پابندی رکھتے ہیں اور جو کچھ ہم نے ان کو عطا فرمایا ہے اس میں سے پوشیده اور علانیہ خرچ کرتے ہیں وه ایسی تجارت کے امیدوار ہیں جو کبھی خساره میں نہ ہوگی. ');
INSERT INTO "junagarri" VALUES(35,30,'35:30','تاکہ ان کو ان کی اجرتیں پوری دے اور ان کو اپنے فضل سے اور زیاده دے بےشک وه بڑا بخشنے والا قدردان ہے. ');
INSERT INTO "junagarri" VALUES(35,31,'35:31','اور یہ کتاب جو ہم نے آپ کے پاس وحی کے طور پر بھیجی ہے یہ بالکل ٹھیک ہے جو کہ اپنے سے پہلی کتابوں کی بھی تصدیق کرتی ہے ۔ اللہ تعالیٰ اپنے بندوں کی پوری خبر رکھنے والا خوب دیکھنے والا ہے. ');
INSERT INTO "junagarri" VALUES(35,32,'35:32','پھر ہم نے ان لوگوں کو (اس) کتاب کا وارث بنایا جن کو ہم نے اپنے بندوں میں سے پسند فرمایا۔ پھر بعضے تو ان میں اپنی جانوں پر ظلم کرنے والے ہیں اور بعضے ان میں متوسط درجے کے ہیں اور بعضے ان میں اللہ کی توفیق سے نیکیوں میں ترقی کئے چلے جاتے ہیں ۔ یہ بڑا فضل ہے. ');
INSERT INTO "junagarri" VALUES(35,33,'35:33','وه باغات میں ہمیشہ رہنے کے جن میں یہ لوگ داخل ہوں گے سونے کے کنگن اور موتی پہنائے جاویں گے۔ اور پوشاک ان کی وہاں ریشم کی ہوگی. ');
INSERT INTO "junagarri" VALUES(35,34,'35:34','اور کہیں گے کہ اللہ کا لاکھ لاکھ شکر ہے جس نے ہم سے غم دور کیا۔ بےشک ہمارا پروردگار بڑا بخشنے والا بڑا قدردان ہے.');
INSERT INTO "junagarri" VALUES(35,35,'35:35','جس نے ہم کو اپنے فضل سے ہمیشہ رہنے کے مقام میں لا اتارا جہاں نہ ہم کو کوئی تکلیف پہنچے گی اور نہ ہم کو کوئی خستگی پہنچے گی.');
INSERT INTO "junagarri" VALUES(35,36,'35:36','اور جو لوگ کافر ہیں ان کے لئے دوزخ کی آگ ہے نہ تو ان کی قضا ہی آئے گی کہ مر ہی جائیں اور نہ دوزخ کا عذاب ہی ان سے ہلکا کیا جائے گا۔ ہم ہر کافر کو ایسی ہی سزا دیتے ہیں.');
INSERT INTO "junagarri" VALUES(35,37,'35:37','اور وه لوگ اس میں چلائیں گے کہ اے ہمارے پروردگار! ہم کو نکال لے ہم اچھے کام کریں گے برخلاف ان کاموں کے جو کیا کرتے تھے ، (اللہ کہے گا) کیا ہم نے تم کو اتنی عمر نہ دی تھی کہ جس کو سمجھنا ہوتا وه سمجھ سکتا اور تمہارے پاس ڈرانے والا بھی پہنچا تھا ، سو مزه چکھو کہ (ایسے) ظالموں کا کوئی مددگار نہیں.');
INSERT INTO "junagarri" VALUES(35,38,'35:38','بےشک اللہ تعالیٰ جاننے والا ہے آسمانوں اور زمین کی پوشیده چیزوں کا ، بےشک وہی جاننے والا ہے سینوں کی باتوں کا. ');
INSERT INTO "junagarri" VALUES(35,39,'35:39','وہی ایسا ہے جس نے تم کو زمین میں آباد کیا، سو جو شخص کفر کرے گا اس کے کفر کا وبال اسی پر پڑے گا۔ اور کافروں کے لئے ان کا کفر ان کے پروردگار کے نزدیک ناراضی ہی بڑھنے کا باعث ہوتا ہے، اور کافروں کے لئے ان کا کفر خساره ہی بڑھنے کا باعث ہوتا ہے. ');
INSERT INTO "junagarri" VALUES(35,40,'35:40','آپ کہیئے! کہ تم اپنے قرارداد شریکوں کا حال تو بتلاؤ جن کو تم اللہ کے سوا پوجا کرتے ہو۔ یعنی مجھ کو یہ بتلاؤ کہ انہوں نے زمین میں سے کون سا (جزو) بنایا ہے یا ان کا آسمانوں میں کچھ ساجھا ہے یا ہم نے ان کو کوئی کتاب دی ہے کہ یہ اس کی دلیل پر قائم ہوں ، بلکہ یہ ظالم ایک دوسرے سے نرے دھوکے کی باتوں کا وعده کرتے آتے ہیں. ');
INSERT INTO "junagarri" VALUES(35,41,'35:41','یقینی بات ہے کہ اللہ تعالیٰ آسمانوں اور زمین کو تھامے ہوئے ہے کہ وه ٹل نہ جائیں اور اگر وه ٹل جائیں تو پھر اللہ کے سوا اور کوئی ان کو تھام بھی نہیں سکتا ۔ وه حلیم غفور ہے. ');
INSERT INTO "junagarri" VALUES(35,42,'35:42','اور ان کفار نے بڑی زوردار قسم کھائی تھی کہ اگر ان کے پاس کوئی ڈرانے والا آئے تو وه ہر ایک امت سے زیاده ہدایت قبول کرنے والے ہوں ۔ پھر جب ان کے پاس ایک پیغمبر آ پہنچے تو بس ان کی نفرت ہی میں اضافہ ہوا.');
INSERT INTO "junagarri" VALUES(35,43,'35:43','دنیا میں اپنے کو بڑا سمجھنے کی وجہ سے ، اور ان کی بری تدبیروں کی وجہ سے اور بری تدبیروں کا وبال ان تدبیر والوں ہی پر پڑتا ہے ، سو کیا یہ اسی دستور کے منتظر ہیں جو اگلے لوگوں کے ساتھ ہوتا رہا ہے ۔ سو آپ اللہ کے دستور کو کبھی بدلتا ہوا نہ پائیں گے ، اور آپ اللہ کے دستور کو کبھی منتقل ہوتا ہوا نہ پائیں گے. ');
INSERT INTO "junagarri" VALUES(35,44,'35:44','اور کیا یہ لوگ زمین میں چلے پھرے نہیں جس میں دیکھتے بھالتے کہ جو لوگ ان سے پہلے ہو گزرے ہیں ان کا انجام کیا ہوا؟ حالانکہ وه قوت میں ان سے بڑھے ہوئے تھے، اور اللہ ایسا نہیں ہے کہ کوئی چیز اس کو ہرا دے نہ آسمانوں میں اور نہ زمین میں۔ وه بڑے علم والا، بڑی قدرت والا ہے.');
INSERT INTO "junagarri" VALUES(35,45,'35:45','اور اگر اللہ تعالیٰ لوگوں پر ان کے اعمال کے سبب داروگیر فرمانے لگتا تو روئے زمین پر ایک جاندار کو نہ چھوڑتا ، لیکن اللہ تعالیٰ ان کو ایک میعاد معین تک مہلت دے رہا ہے ، سو جب ان کی وه میعاد آپہنچے گی اللہ تعالیٰ اپنے بندوں کو آپ دیکھ لے گا. ');
INSERT INTO "junagarri" VALUES(36,1,'36:1','یٰس. ');
INSERT INTO "junagarri" VALUES(36,2,'36:2','قسم ہے قرآن باحکمت کی. ');
INSERT INTO "junagarri" VALUES(36,3,'36:3','کہ بے شک آپ پیغمبروں میں سے ہیں. ');
INSERT INTO "junagarri" VALUES(36,4,'36:4','سیدھے راستے پر ہیں. ');
INSERT INTO "junagarri" VALUES(36,5,'36:5','یہ قرآن اللہ زبردست مہربان کی طرف سے نازل کیا گیا ہے. ');
INSERT INTO "junagarri" VALUES(36,6,'36:6','تاکہ آپ ایسے لوگوں کو ڈرائیں جن کے باپ دادے نہیں ڈرائے گئے تھے، سو (اسی وجہ سے) یہ غافل ہیں. ');
INSERT INTO "junagarri" VALUES(36,7,'36:7','ان میں سے اکثر لوگوں پر بات ثابت ہوچکی ہے سو یہ لوگ ایمان نہ لائیں گے. ');
INSERT INTO "junagarri" VALUES(36,8,'36:8','ہم نے ان کی گردنوں میں طوق ڈال دیئے ہیں پھر وه ٹھوڑیوں تک ہیں، جس سے ان کے سر اوپر کو الٹ گئے ہیں. ');
INSERT INTO "junagarri" VALUES(36,9,'36:9','اور ہم نے ایک آڑ ان کے سامنے کردی اورایک آڑ ان کے پیچھے کردی ، جس سے ہم نے ان کو ڈھانک دیا سو وه نہیں دیکھ سکتے.');
INSERT INTO "junagarri" VALUES(36,10,'36:10','اور آپ ان کو ڈرائیں یا نہ ڈرائیں دونوں برابر ہیں، یہ ایمان نہیں لائیں گے. ');
INSERT INTO "junagarri" VALUES(36,11,'36:11','بس آپ تو صرف ایسے شخص کو ڈرا سکتے ہیں جو نصیحت پر چلے اور رحمٰن سے بےدیکھے ڈرے، سو آپ اس کو مغفرت اور باوقار اجر کی خوش خبریاں سنا دیجیئے.');
INSERT INTO "junagarri" VALUES(36,12,'36:12','بےشک ہم مُردوں کو زنده کریں گے ، اور ہم لکھتے جاتے ہیں وه اعمال بھی جن کو لوگ آگے بھیجتے ہیں اور ان کے وه اعمال بھی جن کو پیچھے چھوڑ جاتے ہیں، اور ہم نے ہر چیز کو ایک واضح کتاب میں ضبط کر رکھا ہے. ');
INSERT INTO "junagarri" VALUES(36,13,'36:13','اور آپ ان کے سامنے ایک مثال (یعنی ایک) بستی والوں کی مثال (اس وقت کا) بیان کیجیئے جب کہ اس بستی میں (کئی) رسول آئے. ');
INSERT INTO "junagarri" VALUES(36,14,'36:14','جب ہم نے ان کے پاس دو کو بھیجا سو ان لوگوں نے (اول) دونوں کو جھٹلایا پھر ہم نے تیسرے سے تائید کی سو ان تینوں نے کہا کہ ہم تمہارے پاس بھیجے گئے ہیں. ');
INSERT INTO "junagarri" VALUES(36,15,'36:15','ان لوگوں نے کہا کہ تم تو ہماری طرح معمولی آدمی ہو اور رحمٰن نے کوئی چیز نازل نہیں کی۔ تم نرا جھوٹ بولتے ہو.');
INSERT INTO "junagarri" VALUES(36,16,'36:16','ان (رسولوں) نے کہا ہمارا پروردگار جانتا ہے کہ بےشک ہم تمہارے پاس بھیجے گئے ہیں.');
INSERT INTO "junagarri" VALUES(36,17,'36:17','اور ہمارے ذمہ تو صرف واضح طور پر پہنچا دینا ہے.');
INSERT INTO "junagarri" VALUES(36,18,'36:18','انہوں نے کہا کہ ہم تو تم کو منحوس سمجھتے ہیں ۔ اگر تم باز نہ آئے تو ہم پتھروں سے تمہارا کام تمام کردیں گے اور تم کو ہماری طرف سے سخت تکلیف پہنچے گی.');
INSERT INTO "junagarri" VALUES(36,19,'36:19','ان رسولوں نے کہا کہ تمہاری نحوست تمہارے ساتھ ہی لگی ہوئی ہے، کیا اس کو نحوست سمجھتے ہو کہ تم کو نصیحت کی جائے بلکہ تم حد سے نکل جانے والے لوگ ہو.');
INSERT INTO "junagarri" VALUES(36,20,'36:20','اور ایک شخص (اس) شہر کے آخری حصے سے دوڑتا ہوا آیا کہنے لگا کہ اے میری قوم! ان رسولوں کی راه پر چلو. ');
INSERT INTO "junagarri" VALUES(36,21,'36:21','ایسے لوگوں کی راه پر چلو جو تم سے کوئی معاوضہ نہیں مانگتے اور وه راه راست پر ہیں.');
INSERT INTO "junagarri" VALUES(36,22,'36:22','اور مجھے کیا ہو گیا ہے کہ میں اس کی عبادت نہ کروں جس نے مجھے پیدا کیا اور تم سب اسی کی طرف لوٹائے جاؤگے. ');
INSERT INTO "junagarri" VALUES(36,23,'36:23','کیا میں اسے چھوڑ کر ایسوں کو معبود بناؤں کہ اگر (اللہ) رحمٰن مجھے کوئی نقصان پہنچانا چاہے تو ان کی سفارش مجھے کچھ بھی نفع نہ پہنچا سکے اور نہ وه مجھے بچا سکیں. ');
INSERT INTO "junagarri" VALUES(36,24,'36:24','پھر تو میں یقیناً کھلی گمراہی میں ہوں. ');
INSERT INTO "junagarri" VALUES(36,25,'36:25','میری سنو! میں تو (سچے دل سے) تم سب کے رب پر ایمان لا چکا. ');
INSERT INTO "junagarri" VALUES(36,26,'36:26','(اس سے) کہا گیا کہ جنت میں چلا جا، کہنے لگا کاش! میری قوم کو بھی علم ہو جاتا.');
INSERT INTO "junagarri" VALUES(36,27,'36:27','کہ مجھے میرے رب نے بخش دیا اور مجھے باعزت لوگوں میں سے کر دیا. ');
INSERT INTO "junagarri" VALUES(36,28,'36:28','اس کے بعد ہم نے اس کی قوم پر آسمان سے کوئی لشکر نہ اتارا ، اور نہ اس طرح ہم اتارا کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(36,29,'36:29','وه تو صرف ایک زور کی چیخ تھی کہ یکایک وه سب کے سب بجھ بجھا گئے. ');
INSERT INTO "junagarri" VALUES(36,30,'36:30','(ایسے) بندوں پر افسوس ! کبھی بھی کوئی رسول ان کے پاس نہیں آیا جس کی ہنسی انہوں نے نہ اڑائی ہو.');
INSERT INTO "junagarri" VALUES(36,31,'36:31','کیا انہوں نے نہیں دیکھا کہ ان کے پہلے بہت سی قوموں کو ہم نے غارت کر دیا کہ وه ان کی طرف لوٹ کر نہیں آئیں گے.');
INSERT INTO "junagarri" VALUES(36,32,'36:32','اور نہیں ہے کوئی جماعت مگر یہ کہ وه جمع ہو کر ہمارے سامنے حاضر کی جائے گی. ');
INSERT INTO "junagarri" VALUES(36,33,'36:33','اور ان کے لئے ایک نشانی (خشک) زمین ہے جس کو ہم نے زنده کر دیا اور اس سے غلہ نکالا جس میں سے وه کھاتے ہیں.');
INSERT INTO "junagarri" VALUES(36,34,'36:34','اور ہم نے اس میں کھجوروں کے اور انگور کے باغات پیدا کر دیئے ، اور جن میں ہم نے چشمے بھی جاری کر دیئے ہیں.');
INSERT INTO "junagarri" VALUES(36,35,'36:35','تاکہ (لوگ) اس کے پھل کھائیں ، اور اس کو ان کے ہاتھوں نے نہیں بنایا ۔ پھر کیوں شکر گزاری نہیں کرتے.');
INSERT INTO "junagarri" VALUES(36,36,'36:36','وه پاک ذات ہے جس نے ہر چیز کے جوڑے پیدا کیے خواه وه زمین کی اگائی ہوئی چیزیں ہوں، خواه خود ان کے نفوس ہوں خواه وه (چیزیں) ہوں جنہیں یہ جانتے بھی نہیں. ');
INSERT INTO "junagarri" VALUES(36,37,'36:37','اور ان کے لئے ایک نشانی رات ہے جس سے ہم دن کو کھینچ دیتے ہیں تو وه یکایک اندھیرے میں ره جاتے ہیں. ');
INSERT INTO "junagarri" VALUES(36,38,'36:38','اور سورج کے لئے جو مقرره راه ہے وه اسی پر چلتا رہتا ہے ۔ یہ ہے مقرر کرده غالب، باعلم اللہ تعالیٰ کا.');
INSERT INTO "junagarri" VALUES(36,39,'36:39','اور چاند کی ہم نے منزلیں مقررکر رکھی ہیں ، یہاں تک کہ وه لوٹ کر پرانی ٹہنی کی طرح ہو جاتا ہے. ');
INSERT INTO "junagarri" VALUES(36,40,'36:40','نہ آفتاب کی یہ مجال ہے کہ چاند کو پکڑے اور نہ رات دن پرآگے بڑھ جانے والی ہے ، اور سب کے سب آسمان میں تیرتے پھرتے ہیں. ');
INSERT INTO "junagarri" VALUES(36,41,'36:41','اور ان کے لئے ایک نشانی (یہ بھی) ہے کہ ہم نے ان کی نسل کو بھری ہوئی کشتی میں سوار کیا. ');
INSERT INTO "junagarri" VALUES(36,42,'36:42','اور ان کے لئے اسی جیسی اور چیزیں پیدا کیں جن پر یہ سوار ہوتے ہیں. ');
INSERT INTO "junagarri" VALUES(36,43,'36:43','اور اگر ہم چاہتے تو انہیں ڈبو دیتے۔ پھر نہ تو کوئی ان کا فریاد رس ہوتا نہ وه بچائے جائیں.');
INSERT INTO "junagarri" VALUES(36,44,'36:44','لیکن ہم اپنی طرف سے رحمت کرتے ہیں اور ایک مدت تک کے لئے انہیں فائدے دے رہے ہیں.');
INSERT INTO "junagarri" VALUES(36,45,'36:45','اور ان سے جب (کبھی) کہا جاتا ہے کہ اگلے پچھلے (گناہوں) سے بچو تاکہ تم پر رحم کیا جائے.');
INSERT INTO "junagarri" VALUES(36,46,'36:46','اور ان کے پاس تو ان کے رب کی نشانیوں میں سے کوئی نشانی ایسی نہیں آئی جس سے یہ بے رخی نہ برتتے ہوں. ');
INSERT INTO "junagarri" VALUES(36,47,'36:47','اور ان سے جب کہا جاتا ہے کہ اللہ تعالیٰ کے دیئے ہوئے میں سے کچھ خرچ کرو ، تو یہ کفار ایمان والوں کو جواب دیتے ہیں کہ ہم انہیں کیوں کھلائیں؟ جنہیں اگر اللہ تعالیٰ چاہتا تو خود کھلا پلا دیتا ، تم تو ہو ہی کھلی گمراہی میں. ');
INSERT INTO "junagarri" VALUES(36,48,'36:48','وه کہتے ہیں کہ یہ وعده کب ہوگا، سچے ہو تو بتلاؤ.');
INSERT INTO "junagarri" VALUES(36,49,'36:49','انہیں صرف ایک سخت چیخ کاانتظار ہے جو انہیں آپکڑے گی اور یہ باہم لڑائی جھگڑے میں ہی ہوں گے. ');
INSERT INTO "junagarri" VALUES(36,50,'36:50','اس وقت نہ تو یہ وصیت کر سکیں گے اور نہ اپنے اہل کی طرف لوٹ سکیں گے.');
INSERT INTO "junagarri" VALUES(36,51,'36:51','تو صور کے پھونکے جاتے ہی سب کے سب اپنی قبروں سے اپنے پروردگار کی طرف (تیز تیز) چلنے لگیں گے.');
INSERT INTO "junagarri" VALUES(36,52,'36:52','کہیں گے ہائے ہائے! ہمیں ہماری خواب گاہوں سے کس نے اٹھا دیا ۔ یہی ہے جس کا وعده رحمٰن نے دیا تھا اور رسولوں نے سچ سچ کہہ دیا تھا.');
INSERT INTO "junagarri" VALUES(36,53,'36:53','یہ نہیں ہے مگر ایک چیخ کہ یکایک سارے کے سارے ہمارے سامنے حاضر کر دیئے جائیں گے.');
INSERT INTO "junagarri" VALUES(36,54,'36:54','پس آج کسی شخص پر کچھ بھی ظلم نہ کیا جائے گا اور تمہیں نہیں بدلہ دیا جائے گا، مگر صرف ان ہی کاموں کا جو تم کیا کرتے تھے.');
INSERT INTO "junagarri" VALUES(36,55,'36:55','جنتی لوگ آج کے دن اپنے (دلچسپ) مشغلوں میں ہشاش بشاش ہیں. ');
INSERT INTO "junagarri" VALUES(36,56,'36:56','وه اور ان کی بیویاں سایوں میں مسہریوں پر تکیہ لگائے بیٹھے ہوں گے.');
INSERT INTO "junagarri" VALUES(36,57,'36:57','ان کے لئے جنت میں ہر قسم کے میوے ہوں گے اور بھی جو کچھ وه طلب کریں.');
INSERT INTO "junagarri" VALUES(36,58,'36:58','مہربان پروردگار کی طرف سے انہیں سلام کہا جائے گا. ');
INSERT INTO "junagarri" VALUES(36,59,'36:59','اے گناهگارو! آج تم الگ ہو جاؤ. ');
INSERT INTO "junagarri" VALUES(36,60,'36:60','اے اولاد آدم! کیا میں نے تم سے قول قرار نہیں لیا تھا کہ تم شیطان کی عبادت نہ کرنا ، وه تو تمہارا کھلا دشمن ہے. ');
INSERT INTO "junagarri" VALUES(36,61,'36:61','اور میری ہی عبادت کرنا ۔ سیدھی راه یہی ہے. ');
INSERT INTO "junagarri" VALUES(36,62,'36:62','شیطان نے تو تم میں سے بہت ساری مخلوق کو بہکا دیا۔ کیا تم عقل نہیں رکھتے. ');
INSERT INTO "junagarri" VALUES(36,63,'36:63','یہی وه دوزخ ہے جس کا تمہیں وعده دیا جاتا تھا.');
INSERT INTO "junagarri" VALUES(36,64,'36:64','اپنے کفر کا بدلہ پانے کے لئے آج اس میں داخل ہوجاؤ. ');
INSERT INTO "junagarri" VALUES(36,65,'36:65','ہم آج کے دن ان کے منھ پر مہریں لگا دیں گے اور ان کے ہاتھ ہم سے باتیں کریں گے اور ان کے پاؤں گواہیاں دیں گے، ان کاموں کی جو وه کرتے تھے.');
INSERT INTO "junagarri" VALUES(36,66,'36:66','اگر ہم چاہتے تو ان کی آنکھیں بے نور کر دیتے پھر یہ رستے کی طرف دوڑتے پھرتے لیکن انہیں کیسے دکھائی دیتا؟ ');
INSERT INTO "junagarri" VALUES(36,67,'36:67','اور اگر ہم چاہتے تو ان کی جگہ ہی پر ان کی صورتیں مسخ کر دیتے پھر نہ وه چل پھر سکتے اور نہ لوٹ سکتے. ');
INSERT INTO "junagarri" VALUES(36,68,'36:68','اور جسے ہم بوڑھا کرتے ہیں اسے پیدائشی حالت کی طرف پھر الٹ دیتے ہیں کیا پھر بھی وه نہیں سمجھتے. ');
INSERT INTO "junagarri" VALUES(36,69,'36:69','نہ تو ہم نے اس پیغمبر کو شعر سکھائے اور نہ یہ اس کے لائق ہے۔ وه تو صرف نصیحت اور واضح قرآن ہے. ');
INSERT INTO "junagarri" VALUES(36,70,'36:70','تاکہ وه ہر اس شخص کو آگاه کر دے جو زنده ہے، اور کافروں پر حجت ثابت ہو جائے. ');
INSERT INTO "junagarri" VALUES(36,71,'36:71','کیا وه نہیں دیکھتے کہ ہم نے اپنے ہاتھوں سے بنائی ہوئی چیزوں میں سے ان کے لئے چوپائے (بھی) پیدا کر دیئے، جن کے یہ مالک ہوگئے ہیں. ');
INSERT INTO "junagarri" VALUES(36,72,'36:72','اور ان مویشیوں کو ہم نے ان کا تابع فرمان بنا دیا ہے جن میں سے بعض تو ان کی سواریاں ہیں اور بعض کا گوشت کھاتے ہیں.');
INSERT INTO "junagarri" VALUES(36,73,'36:73','انہیں ان سے اور بھی بہت سے فائدے ہیں ، اور پینے کی چیزیں۔ کیا پھر (بھی) یہ شکر ادا نہیں کریں گے؟');
INSERT INTO "junagarri" VALUES(36,74,'36:74','اور وه اللہ کے سوا دوسروں کو معبود بناتے ہیں تاکہ وه مدد کئے جائیں. ');
INSERT INTO "junagarri" VALUES(36,75,'36:75','(حالانکہ) ان میں ان کی مدد کی طاقت ہی نہیں، (لیکن) پھر بھی (مشرکین) ان کے لئے حاضر باش لشکری ہیں. ');
INSERT INTO "junagarri" VALUES(36,76,'36:76','پس آپ کو ان کی بات غمناک نہ کرے، ہم ان کی پوشیده اور علانیہ سب باتوں کو (بخوبی) جانتے ہیں.');
INSERT INTO "junagarri" VALUES(36,77,'36:77','کیا انسان کو اتنا بھی معلوم نہیں کہ ہم نے اسے نطفے سے پیدا کیا ہے؟ پھر یکایک وہ صریح جھگڑالو بن بیٹھا.');
INSERT INTO "junagarri" VALUES(36,78,'36:78','اور اس نے ہمارے لئے مثال بیان کی اور اپنی (اصل) پیدائش کو بھول گیا، کہنے لگا ان گلی سڑی ہڈیوں کو کون زنده کر سکتا ہے؟');
INSERT INTO "junagarri" VALUES(36,79,'36:79','آپ جواب دیجئے! کہ انہیں وه زنده کرے گا جس نے انہیں اول مرتبہ پیدا کیا ہے ، جو سب طرح کی پیدائش کا بخوبی جاننے والا ہے.');
INSERT INTO "junagarri" VALUES(36,80,'36:80','وہی جس نے تمہارے لئے سبز درخت سے آگ پیدا کر دی جس سے تم یکایک آگ سلگاتے ہو. ');
INSERT INTO "junagarri" VALUES(36,81,'36:81','جس نے آسمانوں اور زمین کو پیدا کیا ہے کیا وه ان جیسوں کے پیدا کرنے پرقادر نہیں، بےشک قادر ہے۔ اور وہی تو پیدا کرنے والا دانا (بینا) ہے.');
INSERT INTO "junagarri" VALUES(36,82,'36:82','وه جب کبھی کسی چیز کا اراده کرتا ہے اسے اتنا فرما دینا (کافی ہے) کہ ہو جا، وه اسی وقت ہو جاتی ہے. ');
INSERT INTO "junagarri" VALUES(36,83,'36:83','پس پاک ہے وه اللہ جس کے ہاتھ میں ہر چیز کی بادشاہت ہے اور جس کی طرف تم سب لوٹائے جاؤ گے. ');
INSERT INTO "junagarri" VALUES(37,1,'37:1','قسم ہے صف باندھنے والے (فرشتوں) کی.');
INSERT INTO "junagarri" VALUES(37,2,'37:2','پھر پوری طرح ڈانٹنے والوں کی.');
INSERT INTO "junagarri" VALUES(37,3,'37:3','پھر ذکر اللہ کی تلاوت کرنے والوں کی.');
INSERT INTO "junagarri" VALUES(37,4,'37:4','یقیناً تم سب کا معبود ایک ہی ہے. ');
INSERT INTO "junagarri" VALUES(37,5,'37:5','آسمانوں اور زمین اور ان کے درمیان کی تمام چیزوں اور مَشرقوں کا رب وہی ہے. ');
INSERT INTO "junagarri" VALUES(37,6,'37:6','ہم نے آسمان دنیا کو ستاروں کی زینت سے آراستہ کیا.');
INSERT INTO "junagarri" VALUES(37,7,'37:7','اور حفاظت کی سرکش شیطان سے. ');
INSERT INTO "junagarri" VALUES(37,8,'37:8','عالم بالا کے فرشتوں (کی باتوں) کو سننے کے لئے وه کان بھی نہیں لگا سکتے، بلکہ ہر طرف سے وه مارے جاتے ہیں.');
INSERT INTO "junagarri" VALUES(37,9,'37:9','بھگانے کے لئے اور ان کے لئے دائمی عذاب ہے.');
INSERT INTO "junagarri" VALUES(37,10,'37:10','مگر جو کوئی ایک آدھ بات اچک لے بھاگے تو (فوراً ہی) اس کے پیچھے دہکتا ہوا شعلہ لگ جاتا ہے.');
INSERT INTO "junagarri" VALUES(37,11,'37:11','ان کافروں سے پوچھو تو کہ آیا ان کا پیدا کرنا زیاده دشوار ہے یا (ان کا) جنہیں ہم نے (ان کے علاوه) پیدا کیا ؟ ہم نے (انسانوں) کو لیس دار مٹی سے پیدا کیا ہے. ');
INSERT INTO "junagarri" VALUES(37,12,'37:12','بلکہ تو تعجب کر رہا ہے اور یہ مسخرا پن کر رہے ہیں. ');
INSERT INTO "junagarri" VALUES(37,13,'37:13','اور جب انہیں نصیحت کی جاتی ہے یہ نہیں مانتے.');
INSERT INTO "junagarri" VALUES(37,14,'37:14','اور جب کسی معجزے کو دیکھتے ہیں تو مذاق اڑاتے ہیں.');
INSERT INTO "junagarri" VALUES(37,15,'37:15','اور کہتے ہیں کہ یہ تو بالکل کھلم کھلا جادو ہی ہے. ');
INSERT INTO "junagarri" VALUES(37,16,'37:16','کیا جب ہم مر جائیں گے اور خاک اور ہڈی ہو جائیں گے پھر کیا (سچ مچ) ہم اٹھائے جائیں گے؟');
INSERT INTO "junagarri" VALUES(37,17,'37:17','کیا ہم سے پہلے کے ہمارے باپ دادا بھی؟');
INSERT INTO "junagarri" VALUES(37,18,'37:18','آپ جواب دیجئے! کہ ہاں ہاں اور تم ذلیل (بھی) ہوں گے. ');
INSERT INTO "junagarri" VALUES(37,19,'37:19','وه تو صرف ایک زور کی جھڑکی ہے کہ یکایک یہ دیکھنے لگیں گے. ');
INSERT INTO "junagarri" VALUES(37,20,'37:20','اور کہیں گے کہ ہائے ہماری خرابی یہی جزا (سزا) کا دن ہے.');
INSERT INTO "junagarri" VALUES(37,21,'37:21','یہی فیصلہ کا دن ہے جسے تم جھٹلاتے رہے. ');
INSERT INTO "junagarri" VALUES(37,22,'37:22','ظالموں کو اور ان کے ہمراہیوں کو اور (جن) جن کی وه اللہ کے علاوه پرستش کرتے تھے. ');
INSERT INTO "junagarri" VALUES(37,23,'37:23','(ان سب کو) جمع کرکے انہیں دوزخ کی راه دکھا دو.');
INSERT INTO "junagarri" VALUES(37,24,'37:24','اور انہیں ٹھہرا لو ، (اس لئے) کہ ان سے (ضروری) سوال کیے جانے والے ہیں.');
INSERT INTO "junagarri" VALUES(37,25,'37:25','تمہیں کیا ہو گیا ہے کہ (اس وقت) تم ایک دوسرے کی مدد نہیں کرتے.');
INSERT INTO "junagarri" VALUES(37,26,'37:26','بلکہ وه (سب کے سب) آج فرمانبردار بن گئے.');
INSERT INTO "junagarri" VALUES(37,27,'37:27','وه ایک دوسرے کی طرف متوجہ ہو کر سوال وجواب کرنے لگیں گے.');
INSERT INTO "junagarri" VALUES(37,28,'37:28','کہیں گے کہ تم تو ہمارے پاس ہماری دائیں طرف سے آتے تھے. ');
INSERT INTO "junagarri" VALUES(37,29,'37:29','وه جواب دیں گے کہ نہیں بلکہ تم ہی ایمان والے نہ تھے. ');
INSERT INTO "junagarri" VALUES(37,30,'37:30','اور کچھ ہمارا زور تو تم پر تھا (ہی) نہیں۔ بلکہ تم (خود) سرکش لوگ تھے. ');
INSERT INTO "junagarri" VALUES(37,31,'37:31','اب تو ہم (سب) پر ہمارے رب کی یہ بات ثابت ہو چکی کہ ہم (عذاب) چکھنے والے ہیں.');
INSERT INTO "junagarri" VALUES(37,32,'37:32','پس ہم نے تمہیں گمراه کیا ہم تو خود بھی گمراه ہی تھے. ');
INSERT INTO "junagarri" VALUES(37,33,'37:33','سو اب آج کے دن تو (سب کے سب) عذاب میں شریک ہیں. ');
INSERT INTO "junagarri" VALUES(37,34,'37:34','ہم گناه گاروں کے ساتھ اسی طرح کیا کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(37,35,'37:35','یہ وه (لوگ) ہیں کہ جب ان سے کہا جاتا ہے کہ اللہ کے سوا کوئی معبود نہیں تو یہ سرکشی کرتے تھے. ');
INSERT INTO "junagarri" VALUES(37,36,'37:36','اور کہتے تھے کہ کیا ہم اپنے معبودوں کو ایک دیوانے شاعر کی بات پر چھوڑ دیں؟ ');
INSERT INTO "junagarri" VALUES(37,37,'37:37','(نہیں نہیں) بلکہ (نبی) تو حق (سچا دین) لائے ہیں اور سب رسولوں کو سچا جانتے ہیں. ');
INSERT INTO "junagarri" VALUES(37,38,'37:38','یقیناً تم دردناک عذاب (کا مزه) چکھنے والے ہو.');
INSERT INTO "junagarri" VALUES(37,39,'37:39','تمہیں اسی کا بدلہ دیا جائے گا جو تم کرتے تھے. ');
INSERT INTO "junagarri" VALUES(37,40,'37:40','مگر اللہ تعالیٰ کے خالص برگزیده بندے. ');
INSERT INTO "junagarri" VALUES(37,41,'37:41','انہیں کے لئے مقرره روزی ہے.');
INSERT INTO "junagarri" VALUES(37,42,'37:42','(ہر طرح کے) میوے، اور وه باعزت واکرام ہوں گے.');
INSERT INTO "junagarri" VALUES(37,43,'37:43','نعمتوں والی جنتوں میں.');
INSERT INTO "junagarri" VALUES(37,44,'37:44','تختوں پر ایک دوسرے کے سامنے (بیٹھے) ہوں گے.');
INSERT INTO "junagarri" VALUES(37,45,'37:45','جاری شراب کے جام کا ان پر دور چل رہا ہوگا. ');
INSERT INTO "junagarri" VALUES(37,46,'37:46','جو صاف شفاف اور پینے میں لذیذ ہوگی. ');
INSERT INTO "junagarri" VALUES(37,47,'37:47','نہ اس سے درد سر ہو اور نہ اس کے پینے سے بہکیں. ');
INSERT INTO "junagarri" VALUES(37,48,'37:48','اور ان کے پاس نیچی نظروں، بڑی بڑی آنکھوں والی (حوریں) ہوں گی. ');
INSERT INTO "junagarri" VALUES(37,49,'37:49','ایسی جیسے چھپائے ہوئے انڈے. ');
INSERT INTO "junagarri" VALUES(37,50,'37:50','(جنتی) ایک دوسرے کی طرف رخ کرکے پوچھیں گے. ');
INSERT INTO "junagarri" VALUES(37,51,'37:51','ان میں سے ایک کہنے والا کہے گا کہ میرا ایک ساتھی تھا.');
INSERT INTO "junagarri" VALUES(37,52,'37:52','جو (مجھ سے) کہا کرتا تھا کہ کیا تو (قیامت کے آنے کا) یقین کرنے والوں میں سے ہے؟ ');
INSERT INTO "junagarri" VALUES(37,53,'37:53','کیا جب کہ ہم مر کر مٹی اور ہڈی ہو جائیں گے کیا اس وقت ہم جزا دیئے جانے والے ہیں؟ ');
INSERT INTO "junagarri" VALUES(37,54,'37:54','کہے گا تم چاہتے ہو کہ جھانک کر دیکھ لو؟ ');
INSERT INTO "junagarri" VALUES(37,55,'37:55','جھانکتے ہی اسے بیچوں بیچ جہنم میں (جلتا ہوا) دیکھے گا.');
INSERT INTO "junagarri" VALUES(37,56,'37:56','کہے گا واللہ! قریب تھا کہ تو مجھے (بھی) برباد کر دے.');
INSERT INTO "junagarri" VALUES(37,57,'37:57','اگر میرے رب کا احسان نہ ہوتا تو میں بھی دوزخ میں حاضر کئے جانے والوں میں ہوتا. ');
INSERT INTO "junagarri" VALUES(37,58,'37:58','کیا (یہ صحیح ہے) کہ ہم مرنے والے ہی نہیں؟ ');
INSERT INTO "junagarri" VALUES(37,59,'37:59','بجز پہلی ایک موت کے، اور نہ ہم عذاب کیے جانے والے ہیں.');
INSERT INTO "junagarri" VALUES(37,60,'37:60','پھر تو (ظاہر بات ہے کہ) یہ بڑی کامیابی ہے. ');
INSERT INTO "junagarri" VALUES(37,61,'37:61','ایسی (کامیابی) کے لئے عمل کرنے والوں کو عمل کرنا چاہئے. ');
INSERT INTO "junagarri" VALUES(37,62,'37:62','کیا یہ مہمانی اچھی ہے یا سینڈھ (زقوم) کا درخت؟ ');
INSERT INTO "junagarri" VALUES(37,63,'37:63','جسے ہم نے ظالموں کے لئے سخت آزمائش بنا رکھا ہے. ');
INSERT INTO "junagarri" VALUES(37,64,'37:64','بے شک وه درخت جہنم کی جڑ میں سے نکلتا ہے. ');
INSERT INTO "junagarri" VALUES(37,65,'37:65','جس کے خوشے شیطانوں کے سروں جیسے ہوتے ہیں. ');
INSERT INTO "junagarri" VALUES(37,66,'37:66','(جہنمی) اسی درخت سے کھائیں گے اور اسی سے پیٹ بھریں گے. ');
INSERT INTO "junagarri" VALUES(37,67,'37:67','پھر اس پر گرم جلتے جلتے پانی کی ملونی ہوگی. ');
INSERT INTO "junagarri" VALUES(37,68,'37:68','پھر ان سب کا لوٹنا جہنم کی (آگ کے ڈھیرکی) طرف ہوگا. ');
INSERT INTO "junagarri" VALUES(37,69,'37:69','یقین مانو! کہ انہوں نے اپنے باپ دادا کو بہکا ہوا پایا.');
INSERT INTO "junagarri" VALUES(37,70,'37:70','اور یہ انہی کے نشان قدم پر دوڑتے رہے. ');
INSERT INTO "junagarri" VALUES(37,71,'37:71','ان سے پہلے بھی بہت سے اگلے بہک چکے ہیں. ');
INSERT INTO "junagarri" VALUES(37,72,'37:72','جن میں ہم نے ڈرانے والے (رسول) بھیجے تھے. ');
INSERT INTO "junagarri" VALUES(37,73,'37:73','اب تو دیکھ لے کہ جنہیں دھمکایا گیا تھا ان کا انجام کیسا کچھ ہوا.');
INSERT INTO "junagarri" VALUES(37,74,'37:74','سوائے اللہ کے برگزیده بندوں کے. ');
INSERT INTO "junagarri" VALUES(37,75,'37:75','اور ہمیں نوح (علیہ السلام) نے پکارا تو (دیکھ لو) ہم کیسے اچھے دعا قبول کرنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(37,76,'37:76','ہم نے اسے اور اس کے گھر والوں کو اس زبردست مصیبت سے بچا لیا.');
INSERT INTO "junagarri" VALUES(37,77,'37:77','اور اس کی اولاد کو ہم نے باقی رہنے والی بنا دی. ');
INSERT INTO "junagarri" VALUES(37,78,'37:78','اور ہم نے اس کا (ذکر خیر) پچھلوں میں باقی رکھا. ');
INSERT INTO "junagarri" VALUES(37,79,'37:79','نوح (علیہ السلام) پر تمام جہانوں میں سلام ہو');
INSERT INTO "junagarri" VALUES(37,80,'37:80','ہم نیکی کرنے والوں کو اسی طرح بدلہ دیتے ہیں. ');
INSERT INTO "junagarri" VALUES(37,81,'37:81','وه ہمارے ایمان والے بندوں میں سے تھا.');
INSERT INTO "junagarri" VALUES(37,82,'37:82','پھر ہم نے دوسروں کو ڈبو دیا.');
INSERT INTO "junagarri" VALUES(37,83,'37:83','اور اس (نوح علیہ السلام کی) تابعداری کرنے والوں میں سے (ہی) ابراہیم (علیہ السلام بھی) تھے. ');
INSERT INTO "junagarri" VALUES(37,84,'37:84','جبکہ اپنے رب کے پاس بے عیب دل لائے.');
INSERT INTO "junagarri" VALUES(37,85,'37:85','انہوں نے اپنے باپ اور اپنی قوم سے کہا کہ تم کیا پوج رہے ہو؟');
INSERT INTO "junagarri" VALUES(37,86,'37:86','کیا تم اللہ کے سوا گھڑے ہوئے معبود چاہتے ہو؟ ');
INSERT INTO "junagarri" VALUES(37,87,'37:87','تو یہ (بتلاؤ کہ) تم نے رب العالمین کو کیا سمجھ رکھا ہے؟ ');
INSERT INTO "junagarri" VALUES(37,88,'37:88','اب ابراہیم (علیہ السلام) نے ایک نگاه ستاروں کی طرف اٹھائی.');
INSERT INTO "junagarri" VALUES(37,89,'37:89','اور کہا میں تو بیمار ہوں. ');
INSERT INTO "junagarri" VALUES(37,90,'37:90','اس پر وه سب اس سے منھ موڑے ہوئے واپس چلے گئے.');
INSERT INTO "junagarri" VALUES(37,91,'37:91','آپ (چپ چپاتے) ان کے معبودوں کے پاس گئے اور فرمانے لگے تم کھاتے کیوں نہیں؟ ');
INSERT INTO "junagarri" VALUES(37,92,'37:92','تمہیں کیا ہو گیا کہ بات تک نہیں کرتے ہو.');
INSERT INTO "junagarri" VALUES(37,93,'37:93','پھر تو (پوری قوت کے ساتھ) دائیں ہاتھ سے انہیں مارنے پر پل پڑے. ');
INSERT INTO "junagarri" VALUES(37,94,'37:94','وه (بت پرست) دوڑے بھاگے آپ کی طرف متوجہ ہوئے.');
INSERT INTO "junagarri" VALUES(37,95,'37:95','تو آپ نے فرمایا تم انہیں پوجتے ہو جنہیں (خود) تم تراشتے ہو.');
INSERT INTO "junagarri" VALUES(37,96,'37:96','حالانکہ تمہیں اور تمہاری بنائی ہوئی چیزوں کو اللہ ہی نے پیدا کیا ہے. ');
INSERT INTO "junagarri" VALUES(37,97,'37:97','وه کہنے لگے اس کے لئے ایک مکان بناؤ اور اس (دہکتی ہوئی) آگ میں اسے ڈال دو.');
INSERT INTO "junagarri" VALUES(37,98,'37:98','انہوں نے تو اس (ابراہیم علیہ السلام) کے ساتھ مکر کرنا چاہا لیکن ہم نے انہی کو نیچا کر دیا. ');
INSERT INTO "junagarri" VALUES(37,99,'37:99','اور اس (ابراہیم علیہ السلام) نے کہا میں تو ہجرت کر کے اپنے پروردگار کی طرف جانے والا ہوں ۔ وه ضرور میری رہنمائی کرے گا.');
INSERT INTO "junagarri" VALUES(37,100,'37:100','اے میرے رب! مجھے نیک بخت اولاد عطا فرما.');
INSERT INTO "junagarri" VALUES(37,101,'37:101','تو ہم نے اسے ایک بردبار بچے کی بشارت دی. ');
INSERT INTO "junagarri" VALUES(37,102,'37:102','پھر جب وه (بچہ) اتنی عمر کو پہنچا کہ اس کے ساتھ چلے پھرے ، تو اس (ابراہیم علیہ السلام) نے کہا میرے پیارے بچے! میں خواب میں اپنے آپ کو تجھے ذبح کرتے ہوئے دیکھ رہا ہوں۔ اب تو بتا کہ تیری کیا رائے ہے ؟ بیٹے نے جواب دیا کہ ابا! جو حکم ہوا ہے اسے بجا لائیے انشاءاللہ آپ مجھے صبر کرنے والوں میں سے پائیں گے.');
INSERT INTO "junagarri" VALUES(37,103,'37:103','غرض جب دونوں مطیع ہوگئے اور اس نے (باپ نے) اس کو (بیٹے کو) پیشانی کے بل گرا دیا.');
INSERT INTO "junagarri" VALUES(37,104,'37:104','تو ہم نے آواز دی کہ اے ابراہیم!');
INSERT INTO "junagarri" VALUES(37,105,'37:105','یقیناً تو نے اپنے خواب کو سچا کر دکھایا ، بیشک ہم نیکی کرنے والوں کو اسی طرح جزا دیتے ہیں.');
INSERT INTO "junagarri" VALUES(37,106,'37:106','درحقیقت یہ کھلا امتحان تھا. ');
INSERT INTO "junagarri" VALUES(37,107,'37:107','اور ہم نے ایک بڑا ذبیحہ اس کے فدیہ میں دے دیا. ');
INSERT INTO "junagarri" VALUES(37,108,'37:108','اور ہم نے ان کا ذکر خیر پچھلوں میں باقی رکھا.');
INSERT INTO "junagarri" VALUES(37,109,'37:109','ابراہیم (علیہ السلام) پر سلام ہو.');
INSERT INTO "junagarri" VALUES(37,110,'37:110','ہم نیکو کاروں کو اسی طرح بدلہ دیتے ہیں.');
INSERT INTO "junagarri" VALUES(37,111,'37:111','بیشک وه ہمارے ایمان دار بندوں میں سے تھا.');
INSERT INTO "junagarri" VALUES(37,112,'37:112','اور ہم نے اس کو اسحاق (علیہ السلام) نبی کی بشارت دی جو صالح لوگوں میں سے ہوگا. ');
INSERT INTO "junagarri" VALUES(37,113,'37:113','اور ہم نے ابراہیم واسحاق (علیہ السلام) پر برکتیں نازل فرمائیں ، اور ان دونوں کی اولاد میں بعضے تو نیک بخت ہیں اور بعض اپنے نفس پر صریح ظلم کرنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(37,114,'37:114','یقیناً ہم نے موسیٰ اور ہارون (علیہ السلام) پر بڑا احسان کیا. ');
INSERT INTO "junagarri" VALUES(37,115,'37:115','اور انہیں اور ان کی قوم کو بہت بڑے دکھ درد سے نجات دے دی. ');
INSERT INTO "junagarri" VALUES(37,116,'37:116','اور ان کی مدد کی تو وہی غالب رہے.');
INSERT INTO "junagarri" VALUES(37,117,'37:117','اور ہم نے انہیں (واضح اور) روشن کتاب دی.');
INSERT INTO "junagarri" VALUES(37,118,'37:118','اور انہیں سیدھے راستہ پرقائم رکھا.');
INSERT INTO "junagarri" VALUES(37,119,'37:119','اور ہم نے ان دونوں کے لئے پیچھے آنے والوں میں یہ بات باقی رکھی.');
INSERT INTO "junagarri" VALUES(37,120,'37:120','کہ موسیٰ اور ہارون (علیہما السلام) پر سلام ہو.');
INSERT INTO "junagarri" VALUES(37,121,'37:121','بے شک ہم نیک لوگوں کو اسی طرح بدلے دیا کرتے ہیں.');
INSERT INTO "junagarri" VALUES(37,122,'37:122','یقیناً یہ دونوں ہمارے مومن بندوں میں سے تھے.');
INSERT INTO "junagarri" VALUES(37,123,'37:123','بے شک الیاس (علیہ السلام) بھی پیغمبروں میں سے تھے ');
INSERT INTO "junagarri" VALUES(37,124,'37:124','جب کہ انہوں نے اپنی قوم سے فرمایا کہ تم اللہ سے ڈرتے نہیں ہو؟ ');
INSERT INTO "junagarri" VALUES(37,125,'37:125','کیا تم بعل (نامی بت) کو پکارتے ہو؟ اور سب سے بہتر خالق کو چھوڑ دیتے ہو؟');
INSERT INTO "junagarri" VALUES(37,126,'37:126','اللہ جو تمہارا اور تمہارے اگلے تمام باپ دادوں کا رب ہے. ');
INSERT INTO "junagarri" VALUES(37,127,'37:127','لیکن قوم نے انہیں جھٹلایا، پس وه ضرور (عذاب میں) حاضر رکھے جائیں گے.');
INSERT INTO "junagarri" VALUES(37,128,'37:128','سوائے اللہ تعالی کے مخلص بندوں کے.');
INSERT INTO "junagarri" VALUES(37,129,'37:129','ہم نے (الیاس علیہ السلام) کا ذکر خیر پچھلوں میں بھی باقی رکھا.');
INSERT INTO "junagarri" VALUES(37,130,'37:130','کہ الیاس پر سلام ہو. ');
INSERT INTO "junagarri" VALUES(37,131,'37:131','ہم نیکی کرنے والوں کو اسی طرح بدلہ دیتے ہیں.*');
INSERT INTO "junagarri" VALUES(37,132,'37:132','بیشک وه ہمارے ایمان دار بندوں میں سے تھے. ');
INSERT INTO "junagarri" VALUES(37,133,'37:133','بیشک لوط (علیہ السلام بھی) پیغمبروں میں سے تھے.');
INSERT INTO "junagarri" VALUES(37,134,'37:134','ہم نے انہیں اور ان کے گھر والوں کو سب کو نجات دی.');
INSERT INTO "junagarri" VALUES(37,135,'37:135','بجز اس بڑھیا کے جو پیچھے ره جانے والوں میں سے ره گئی. ');
INSERT INTO "junagarri" VALUES(37,136,'37:136','پھر ہم نے اوروں کو ہلاک کر دیا.');
INSERT INTO "junagarri" VALUES(37,137,'37:137','اور تم تو صبح ہونے پر ان کی بستیوں کے پاس سے گزرتے ہو.');
INSERT INTO "junagarri" VALUES(37,138,'37:138','اور رات کو بھی، کیا پھر بھی نہیں سمجھتے؟ ');
INSERT INTO "junagarri" VALUES(37,139,'37:139','اور بلاشبہ یونس (علیہ السلام) نبیوں میں سے تھے.');
INSERT INTO "junagarri" VALUES(37,140,'37:140','جب بھاگ کر پہنچے بھری کشتی پر.');
INSERT INTO "junagarri" VALUES(37,141,'37:141','پھر قرعہ اندازی ہوئی تو یہ مغلوب ہوگئے.');
INSERT INTO "junagarri" VALUES(37,142,'37:142','تو پھر انہیں مچھلی نے نگل لیا اور وه خود اپنے آپ کو ملامت کرنے لگ گئے.');
INSERT INTO "junagarri" VALUES(37,143,'37:143','پس اگر یہ پاکی بیان کرنے والوں میں سے نہ ہوتے.');
INSERT INTO "junagarri" VALUES(37,144,'37:144','تو لوگوں کے اٹھائے جانے کے دن تک اس کے پیٹ میں ہی رہتے. ');
INSERT INTO "junagarri" VALUES(37,145,'37:145','پس انہیں ہم نے چٹیل میدان میں ڈال دیا اور وه اس وقت بیمار تھے. ');
INSERT INTO "junagarri" VALUES(37,146,'37:146','اور ان پر سایہ کرنے والا ایک بیل دار درخت ہم نے اگا دیا.');
INSERT INTO "junagarri" VALUES(37,147,'37:147','اور ہم نے انہیں ایک لاکھ بلکہ اور زیاده آدمیوں کی طرف بھیجا.');
INSERT INTO "junagarri" VALUES(37,148,'37:148','پس وه ایمان لائے ، اور ہم نے انہیں ایک زمانہ تک عیش وعشرت دی.');
INSERT INTO "junagarri" VALUES(37,149,'37:149','ان سے دریافت کیجئے! کہ کیا آپ کے رب کی تو بیٹیاں ہیں اور ان کے بیٹے ہیں؟');
INSERT INTO "junagarri" VALUES(37,150,'37:150','یا یہ اس وقت موجود تھے جبکہ ہم نے فرشتوں کو مؤنث پیدا کیا. ');
INSERT INTO "junagarri" VALUES(37,151,'37:151','آگاه رہو! کہ یہ لوگ صرف اپنی افترا پردازی سے کہہ رہے ہیں.');
INSERT INTO "junagarri" VALUES(37,152,'37:152','کہ اللہ تعالی کی اولاد ہے۔ یقیناً یہ محض جھوٹے ہیں.');
INSERT INTO "junagarri" VALUES(37,153,'37:153','کیا اللہ تعالی نے اپنے لیے بیٹیوں کو بیٹوں پر ترجیح دی. ');
INSERT INTO "junagarri" VALUES(37,154,'37:154','تمہیں کیا ہو گیا ہے کیسے حکم لگاتے پھرتے ہو؟');
INSERT INTO "junagarri" VALUES(37,155,'37:155','کیا تم اس قدر بھی نہیں سمجھتے؟ ');
INSERT INTO "junagarri" VALUES(37,156,'37:156','یا تمہارے پاس اس کی کوئی صاف دلیل ہے.');
INSERT INTO "junagarri" VALUES(37,157,'37:157','تو جاؤ اگر سچے ہو تو اپنی ہی کتاب لے آؤ. ');
INSERT INTO "junagarri" VALUES(37,158,'37:158','اور ان لوگوں نے تو اللہ کے اور جنات کے درمیان بھی قرابت داری ٹھہرائی ہے، اور حالانکہ خود جنات کو معلوم ہے کہ وه (اس عقیده کے لوگ عذاب کے سامنے) پیش کیے جائیں گے. ');
INSERT INTO "junagarri" VALUES(37,159,'37:159','جو کچھ یہ (اللہ کے بارے میں) بیان کر رہے ہیں اس سے اللہ تعالیٰ بالکل پاک ہے.');
INSERT INTO "junagarri" VALUES(37,160,'37:160','سوائے! اللہ کے مخلص بندوں کے. ');
INSERT INTO "junagarri" VALUES(37,161,'37:161','یقین مانو کہ تم سب اور تمہارے معبودان (باطل).');
INSERT INTO "junagarri" VALUES(37,162,'37:162','کسی ایک کو بھی بہکا نہیں سکتے.');
INSERT INTO "junagarri" VALUES(37,163,'37:163','بجز اس کے جو جہنمی ہی ہے. ');
INSERT INTO "junagarri" VALUES(37,164,'37:164','(فرشتوں کا قول ہے کہ) ہم میں سے تو ہر ایک کی جگہ مقرر ہے. ');
INSERT INTO "junagarri" VALUES(37,165,'37:165','اور ہم تو (بندگیٴ الٰہی میں) صف بستہ کھڑے ہیں.');
INSERT INTO "junagarri" VALUES(37,166,'37:166','اور اس کی تسبیح بیان کر رہے ہیں. ');
INSERT INTO "junagarri" VALUES(37,167,'37:167','کفار تو کہا کرتے تھے.');
INSERT INTO "junagarri" VALUES(37,168,'37:168','کہ اگر ہمارے سامنے اگلے لوگوں کا ذکر ہوتا.');
INSERT INTO "junagarri" VALUES(37,169,'37:169','تو ہم بھی اللہ کے چیده بندے بن جاتے. ');
INSERT INTO "junagarri" VALUES(37,170,'37:170','لیکن پھر اس قرآن کے ساتھ کفر کر گئے ، پس اب عنقریب جان لیں گے. ');
INSERT INTO "junagarri" VALUES(37,171,'37:171','اور البتہ ہمارا وعده پہلے ہی اپنے رسولوں کے لئے صادر ہو چکا ہے.');
INSERT INTO "junagarri" VALUES(37,172,'37:172','کہ یقیناً وه ہی مدد کیے جائیں گے.');
INSERT INTO "junagarri" VALUES(37,173,'37:173','اور ہمارا ہی لشکر غالب (اور برتر) رہے گا. ');
INSERT INTO "junagarri" VALUES(37,174,'37:174','اب آپ کچھ دنوں تک ان سے منھ پھیر لیجئے. ');
INSERT INTO "junagarri" VALUES(37,175,'37:175','اور انہیں دیکھتے رہیئے ، اور یہ بھی آگے چل کر دیکھ لیں گے.');
INSERT INTO "junagarri" VALUES(37,176,'37:176','کیا یہ ہمارے عذاب کی جلدی مچا رہے ہیں؟');
INSERT INTO "junagarri" VALUES(37,177,'37:177','سنو! جب ہمارا عذاب ان کے میدان میں اتر آئے گا اس وقت ان کی جن کو متنبہ کر دیا گیا تھا بڑی بری صبح ہوگی.');
INSERT INTO "junagarri" VALUES(37,178,'37:178','آپ کچھ وقت تک ان کا خیال چھوڑ دیجئے.');
INSERT INTO "junagarri" VALUES(37,179,'37:179','اور دیکھتے رہئیے یہ بھی ابھی ابھی دیکھ لیں گے. ');
INSERT INTO "junagarri" VALUES(37,180,'37:180','پاک ہے آپ کا رب جو بہت بڑی عزت والا ہے ہر اس چیز سے (جو مشرک) بیان کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(37,181,'37:181','پیغمبروں پر سلام ہے. ');
INSERT INTO "junagarri" VALUES(37,182,'37:182','اور سب طرح کی تعریف اللہ کے لئے ہے جو سارے جہان کا رب ہے. ');
INSERT INTO "junagarri" VALUES(38,1,'38:1','ص! اس نصیحت والے قرآن کی قسم. ');
INSERT INTO "junagarri" VALUES(38,2,'38:2','بلکہ کفار غرور ومخالفت میں پڑے ہوئے ہیں. ');
INSERT INTO "junagarri" VALUES(38,3,'38:3','ہم نے ان سے پہلے بھی بہت سی امتوں کو تباه کر ڈالا انہوں نے ہر چند چیخ وپکار کی لیکن وه وقت چھٹکارے کا نہ تھا. ');
INSERT INTO "junagarri" VALUES(38,4,'38:4','اور کافروں کو اس بات پر تعجب ہوا کہ ان ہی میں سے ایک انہیں ڈرانے والا آگیا اور کہنے لگے کہ یہ تو جادوگر اور جھوٹا ہے. ');
INSERT INTO "junagarri" VALUES(38,5,'38:5','کیا اس نےاتنے سارے معبودوں کا ایک ہی معبود کر دیا واقعی یہ بہت ہی عجیب بات ہے. ');
INSERT INTO "junagarri" VALUES(38,6,'38:6','ان کے سردار یہ کہتے ہوئے چلے کہ چلو جی اور اپنے معبودوں پر جمے رہو ، یقیناً اس بات میں تو کوئی غرض ہے. ');
INSERT INTO "junagarri" VALUES(38,7,'38:7','ہم نے تو یہ بات پچھلے دین میں بھی نہیں سنی ، کچھ نہیں یہ تو صرف گھڑنت ہے. ');
INSERT INTO "junagarri" VALUES(38,8,'38:8','کیا ہم سب میں سے اسی پر کلام الٰہی نازل کیا گیا ہے ؟ دراصل یہ لوگ میری وحی کی طرف سے شک میں ہیں ، بلکہ (صحیح یہ ہے کہ) انہوں نے اب تک میرا عذاب چکھا ہی نہیں. ');
INSERT INTO "junagarri" VALUES(38,9,'38:9','یا کیا ان کے پاس تیرے زبردست فیاض رب کی رحمت کے خزانے ہیں. ');
INSERT INTO "junagarri" VALUES(38,10,'38:10','یا کیا آسمان وزمین اور ان کے درمیان کی ہر چیز کی بادشاہت ان ہی کی ہے، تو پھر یہ رسیاں تان کر چڑھ جائیں. ');
INSERT INTO "junagarri" VALUES(38,11,'38:11','یہ بھی (بڑے بڑے) لشکروں میں سے شکست پایا ہوا (چھوٹا سا) لشکر ہے. ');
INSERT INTO "junagarri" VALUES(38,12,'38:12','ان سے پہلے بھی قوم نوح اور عاد اور میخوں والے فرعون نے جھٹلایا تھا.');
INSERT INTO "junagarri" VALUES(38,13,'38:13','اور ثمود نے اور قوم لوط نے اور ایکہ کے رہنے والوں نے بھی، یہی (بڑے) لشکر تھے.');
INSERT INTO "junagarri" VALUES(38,14,'38:14','ان میں سے ایک بھی ایسا نہ تھا جس نے رسولوں کی تکذیب نہ کی ہو پس میری سزا ان پر ثابت ہوگئی.');
INSERT INTO "junagarri" VALUES(38,15,'38:15','انہیں صرف ایک چیخ کا انتظار ہے جس میں کوئی توقف (اور ڈھیل) نہیں ہے. ');
INSERT INTO "junagarri" VALUES(38,16,'38:16','اور انہوں نے کہا کہ اے ہمارے رب! ہماری سرنوشت تو ہمیں روز حساب سے پہلے ہی دے دے. ');
INSERT INTO "junagarri" VALUES(38,17,'38:17','آپ ان کی باتوں پر صبر کریں اور ہمارے بندے داؤد (علیہ السلام) کو یاد کریں جو بڑی قوت والا تھا ، یقیناً وه بہت رجوع کرنے والا تھا.');
INSERT INTO "junagarri" VALUES(38,18,'38:18','ہم نے پہاڑوں کو اس کے تابع کر رکھا تھا کہ اس کے ساتھ شام کو اور صبح کو تسبیح خوانی کریں.');
INSERT INTO "junagarri" VALUES(38,19,'38:19','اور پرندوں کو بھی جمع ہو کر سب کے سب اس کے زیر فرمان رہتے. ');
INSERT INTO "junagarri" VALUES(38,20,'38:20','اور ہم نے اس کی سلطنت کو مضبوط کر دیا تھا اور اسے حکمت دی تھی اور بات کا فیصلہ کرنا. ');
INSERT INTO "junagarri" VALUES(38,21,'38:21','اور کیا تجھے جھگڑا کرنے والوں کی (بھی) خبر ملی؟ جبکہ وه دیوار پھاند کر محراب میں آگئے. ');
INSERT INTO "junagarri" VALUES(38,22,'38:22','جب یہ (حضرت) داؤد (علیہ السلام) کے پاس پہنچے، پس یہ ان سے ڈر گئے ، انہوں نے کہا خوف نہ کیجئے! ہم دو فریق مقدمہ ہیں، ہم میں سے ایک نے دوسرے پر زیادتی کی ہے، پس آپ ہمارے درمیان حق کے ساتھ فیصلہ کر دیجئے اور ناانصافی نہ کیجئے اور ہمیں سیدھی راه بتا دیجئے. ');
INSERT INTO "junagarri" VALUES(38,23,'38:23','(سنیے) یہ میرا بھائی ہے اس کے پاس نناوے دنبیاں ہیں اور میرے پاس ایک ہی دنبی ہے لیکن یہ مجھ سے کہہ رہا ہے کہ اپنی یہ ایک بھی مجھ ہی کو دے دے اور مجھ پر بات میں بڑی سختی برتتا ہے. ');
INSERT INTO "junagarri" VALUES(38,24,'38:24','آپ نے فرمایا! اس کا اپنی دنبیوں کے ساتھ تیری ایک دنبی ملا لینے کا سوال بیشک تیرے اوپر ایک ظلم ہے اور اکثر حصہ دار اور شریک (ایسے ہی ہوتے ہیں کہ) ایک دوسرے پر ظلم کرتے ہیں، سوائے ان کے جو ایمان لائے اور جنہوں نے نیک عمل کیے اور ایسے لوگ بہت ہی کم ہیں اور (حضرت) داؤد (علیہ السلام) سمجھ گئے کہ ہم نے انہیں آزمایا ہے، پھر تو اپنے رب سے استغفار کرنے لگے اور عاجزی کرتے ہوئے گر پڑے اور (پوری طرح) رجوع کیا.');
INSERT INTO "junagarri" VALUES(38,25,'38:25','پس ہم نے بھی ان کا وه (قصور) معاف کر دیا ، یقیناً وه ہمارے نزدیک بڑے مرتبہ والے اور بہت اچھے ٹھکانے والے ہیں.');
INSERT INTO "junagarri" VALUES(38,26,'38:26','اے داؤد! ہم نے تمہیں زمین میں خلیفہ بنا دیا تم لوگوں کے درمیان حق کے ساتھ فیصلے کرو اور اپنی نفسانی خواہش کی پیروی نہ کرو ورنہ وه تمہیں اللہ کی راه سے بھٹکا دے گی، یقیناً جو لوگ اللہ کی راه سے بھٹک جاتے ہیں ان کے لئے سخت عذاب ہے اس لئے کہ انہوں نے حساب کے دن کو بھلا دیا ہے.');
INSERT INTO "junagarri" VALUES(38,27,'38:27','اور ہم نے آسمان وزمین اور ان کے درمیان کی چیزوں کو ناحق پیدا نہیں کیا ، یہ گمان تو کافروں کا ہے سو کافروں کے لئے خرابی ہے آگ کی.');
INSERT INTO "junagarri" VALUES(38,28,'38:28','کیا ہم ان لوگوں کو جو ایمان لائے اور نیک عمل کیے ان کے برابر کر دیں گے جو (ہمیشہ) زمین میں فساد مچاتے رہے، یا پرہیزگاروں کو بدکاروں جیسا کر دینگے؟');
INSERT INTO "junagarri" VALUES(38,29,'38:29','یہ بابرکت کتاب ہے جسے ہم نے آپ کی طرف اس لئے نازل فرمایا ہے کہ لوگ اس کی آیتوں پر غور وفکر کریں اور عقلمند اس سے نصیحت حاصل کریں.');
INSERT INTO "junagarri" VALUES(38,30,'38:30','اور ہم نے داؤد کو سلیمان (نامی فرزند) عطا فرمایا، جو بڑا اچھا بنده تھا اور بے حد رجوع کرنے والا تھا.');
INSERT INTO "junagarri" VALUES(38,31,'38:31','جب ان کے سامنے شام کے وقت تیز رو خاصے گھوڑے پیش کیے گئے. ');
INSERT INTO "junagarri" VALUES(38,32,'38:32','تو کہنے لگے میں نے اپنے پروردگار کی یاد پر ان گھوڑوں کی محبت کو ترجیح دی، یہاں تک کہ (آفتاب) چھپ گیا.');
INSERT INTO "junagarri" VALUES(38,33,'38:33','ان (گھوڑوں) کو دوباره میرے سامنے لاؤ! پھر تو پنڈلیوں اور گردنوں پر ہاتھ پھیرنا شروع کر دیا. ');
INSERT INTO "junagarri" VALUES(38,34,'38:34','اور ہم نے سلیمان (علیہ السلام) کی آزمائش کی اور ان کے تخت پر ایک جسم ڈال دیا پھر اس نے رجوع کیا.');
INSERT INTO "junagarri" VALUES(38,35,'38:35','کہا کہ اے میرے رب! مجھے بخش دے اور مجھے ایسا ملک عطا فرما جو میرے سوا کسی (شخص) کے لائق نہ ہو ، تو بڑا ہی دینے والا ہے.');
INSERT INTO "junagarri" VALUES(38,36,'38:36','پس ہم نے ہوا کو ان کے ماتحت کر دیا اور آپ کے حکم سے جہاں آپ چاہتے نرمی سے پہنچا دیا کرتی تھی. ');
INSERT INTO "junagarri" VALUES(38,37,'38:37','اور (طاقت ور) جنات کو بھی (ان کا ماتحت کر دیا) ہر عمارت بنانے والے کو اور غوطہ خور کو.');
INSERT INTO "junagarri" VALUES(38,38,'38:38','اور دوسرے جنات کو بھی جو زنجیروں میں جکڑے رہتے. ');
INSERT INTO "junagarri" VALUES(38,39,'38:39','یہ ہے ہمارا عطیہ اب تو احسان کر یا روک رکھ، کچھ حساب نہیں. ');
INSERT INTO "junagarri" VALUES(38,40,'38:40','ان کے لئے ہمارے پاس بڑا تقرب ہے اور بہت اچھا ٹھکانا ہے. ');
INSERT INTO "junagarri" VALUES(38,41,'38:41','اور ہمارے بندے ایوب (علیہ السلام) کا (بھی) ذکر کر، جبکہ اس نے اپنے رب کو پکارا کہ مجھے شیطان نے رنج اور دکھ پہنچایا ہے. ');
INSERT INTO "junagarri" VALUES(38,42,'38:42','اپنا پاؤں مارو، یہ نہانے کا ٹھنڈا اور پینے کا پانی ہے. ');
INSERT INTO "junagarri" VALUES(38,43,'38:43','اور ہم نے اسے اس کا پورا کنبہ عطا فرمایا بلکہ اتنا ہی اور بھی اسی کے ساتھ اپنی (خاص) رحمت سے ، اور عقلمندوں کی نصیحت کے لئے. ');
INSERT INTO "junagarri" VALUES(38,44,'38:44','اور اپنے ہاتھوں میں تنکوں کا ایک مٹھا (جھاڑو) لے کر مار دے اور قسم کے خلاف نہ کر ، سچ تو یہ ہے کہ ہم نے اسے بڑا صابر بنده پایا، وه بڑا نیک بنده تھا اور بڑی ہی رغبت رکھنے والا.');
INSERT INTO "junagarri" VALUES(38,45,'38:45','ہمارے بندوں ابراہیم، اسحاق اور یعقوب (علیہم السلام) کا بھی لوگوں سے ذکر کرو جو ہاتھوں اور آنکھوں والے تھے.');
INSERT INTO "junagarri" VALUES(38,46,'38:46','ہم نے انہیں ایک خاص بات یعنی آخرت کی یاد کے ساتھ مخصوص کر دیا تھا. ');
INSERT INTO "junagarri" VALUES(38,47,'38:47','یہ سب ہمارے نزدیک برگزیده اور بہترین لوگ تھے.');
INSERT INTO "junagarri" VALUES(38,48,'38:48','اسماعیل، یسع اور ذوالکفل (علیہم السلام) کا بھی ذکر کر دیجئے۔ یہ سب بہترین لوگ تھے.');
INSERT INTO "junagarri" VALUES(38,49,'38:49','یہ نصیحت ہے اور یقین مانو کہ پرہیزگاروں کی بڑی اچھی جگہ ہے.');
INSERT INTO "junagarri" VALUES(38,50,'38:50','(یعنی ہمیشگی والی) جنتیں جن کے دروازے ان کے لئے کھلے ہوئے ہیں.');
INSERT INTO "junagarri" VALUES(38,51,'38:51','جن میں بافراغت تکیے لگائے بیٹھے ہوئے طرح طرح کے میوے اور قسم قسم کی شرابوں کی فرمائشیں کر رہے ہیں.');
INSERT INTO "junagarri" VALUES(38,52,'38:52','اور ان کے پاس نیچی نظروں والی ہم عمر حوریں ہوں گی. ');
INSERT INTO "junagarri" VALUES(38,53,'38:53','یہ ہے جس کا وعده تم سے حساب کے دن کے لئے کیا جاتا تھا.');
INSERT INTO "junagarri" VALUES(38,54,'38:54','بیشک روزیاں (خاص) ہمارا عطیہ ہیں جن کا کبھی خاتمہ ہی نہیں. ');
INSERT INTO "junagarri" VALUES(38,55,'38:55','یہ تو ہوئی جزا ، (یاد رکھو کہ) سرکشوں کے لئے بڑی بری جگہ ہے. ');
INSERT INTO "junagarri" VALUES(38,56,'38:56','دوزخ ہے جس میں وه جائیں گے (آه) کیا ہی برا بچھونا ہے.');
INSERT INTO "junagarri" VALUES(38,57,'38:57','یہ ہے، پس اسے چکھیں، گرم پانی اور پیپ. ');
INSERT INTO "junagarri" VALUES(38,58,'38:58','اس کے علاوه اور طرح طرح کے عذاب. ');
INSERT INTO "junagarri" VALUES(38,59,'38:59','یہ ایک قوم ہے جو تمہارے ساتھ (آگ میں) جانے والی ہے ، کوئی خوش آمدید ان کے لئے نہیں ہے یہی تو جہنم میں جانے والے ہیں. ');
INSERT INTO "junagarri" VALUES(38,60,'38:60','وه کہیں گے بلکہ تم ہی ہو جن کے لئے کوئی خوش آمدید نہیں ہے تم ہی نے تو اسے پہلے ہی سے ہمارے سامنے لا رکھا تھا ، پس رہنے کی بڑی بری جگہ ہے.');
INSERT INTO "junagarri" VALUES(38,61,'38:61','وه کہیں گے اے ہمارے رب! جس نے (کفر کی رسم) ہمارے لئے پہلے سے نکالی ہو اس کے حق میں جہنم کی دگنی سزا کر دے. ');
INSERT INTO "junagarri" VALUES(38,62,'38:62','اور جہنمی کہیں گے کیا بات ہے کہ وه لوگ ہمیں دکھائی نہیں دیتے جنہیں ہم برے لوگوں میں شمار کرتے تھے. ');
INSERT INTO "junagarri" VALUES(38,63,'38:63','کیا ہم نے ہی ان کا مذاق بنا رکھا تھا یا ہماری نگاہیں ان سے ہٹ گئی ہیں. ');
INSERT INTO "junagarri" VALUES(38,64,'38:64','یقین جانو کہ دوزخیوں کا یہ جھگڑا ضرور ہی ہوگا. ');
INSERT INTO "junagarri" VALUES(38,65,'38:65','کہہ دیجئے! کہ میں تو صرف خبردار کرنے والا ہوں اور بجز اللہ واحد غالب کے اور کوئی لائق عبادت نہیں.');
INSERT INTO "junagarri" VALUES(38,66,'38:66','جو پروردگار ہے آسمانوں کا اور زمین کا اور جو کچھ ان کے درمیان ہے، وه زبردست اور بڑا بخشنے والا ہے.');
INSERT INTO "junagarri" VALUES(38,67,'38:67','آپ کہہ دیجئے کہ یہ بہت بڑی خبر ہے. ');
INSERT INTO "junagarri" VALUES(38,68,'38:68','جس سے تم بے پرواه ہو رہے ہو.');
INSERT INTO "junagarri" VALUES(38,69,'38:69','مجھے ان بلند قدر فرشتوں کی (بات چیت کا) کوئی علم ہی نہیں جبکہ وه تکرار کر رہے تھے. ');
INSERT INTO "junagarri" VALUES(38,70,'38:70','میری طرف فقط یہی وحی کی جاتی ہے کہ میں تو صاف صاف آگاه کر دینے والا ہوں. ');
INSERT INTO "junagarri" VALUES(38,71,'38:71','جبکہ آپ کے رب نے فرشتوں سے ارشاد فرمایا کہ میں مٹی سے انسان کو پیدا کرنے والا ہوں.');
INSERT INTO "junagarri" VALUES(38,72,'38:72','سو جب میں اسے ٹھیک ٹھاک کر لوں اور اس میں اپنی روح پھونک دوں ، تو تم سب اس کے سامنے سجدے میں گر پڑنا. ');
INSERT INTO "junagarri" VALUES(38,73,'38:73','چنانچہ تمام فرشتوں نے سجده کیا. ');
INSERT INTO "junagarri" VALUES(38,74,'38:74','مگر ابلیس نے (نہ کیا)، اس نے تکبر کیا اور وه تھا کافروں میں سے. ');
INSERT INTO "junagarri" VALUES(38,75,'38:75','(اللہ تعالیٰ نے) فرمایا اے ابلیس! تجھے اسے سجده کرنے سے کس چیز نے روکا جسے میں نے اپنے ہاتھوں سے پیدا کیا۔ کیا تو کچھ گھمنڈ میں آگیا ہے؟ یا تو بڑے درجے والوں میں سے ہے.');
INSERT INTO "junagarri" VALUES(38,76,'38:76','اس نے جواب دیا کہ میں اس سے بہتر ہوں، تو نے مجھے آگ سے بنایا، اور اسے مٹی سے بنایا ہے. ');
INSERT INTO "junagarri" VALUES(38,77,'38:77','ارشاد ہوا کہ تو یہاں سے نکل جا تو مردود ہوا.');
INSERT INTO "junagarri" VALUES(38,78,'38:78','اور تجھ پر قیامت کے دن تک میری لعنت وپھٹکار ہے.');
INSERT INTO "junagarri" VALUES(38,79,'38:79','کہنے لگا میرے رب مجھے لوگوں کے اٹھ کھڑے ہونے کے دن تک مہلت دے.');
INSERT INTO "junagarri" VALUES(38,80,'38:80','(اللہ تعالیٰ نے) فرمایا تو مہلت والوں میں سے ہے.');
INSERT INTO "junagarri" VALUES(38,81,'38:81','متعین وقت کے دن تک.');
INSERT INTO "junagarri" VALUES(38,82,'38:82','کہنے لگا پھر تو تیری عزت کی قسم! میں ان سب کو یقیناً بہکا دوں گا.');
INSERT INTO "junagarri" VALUES(38,83,'38:83','بجز تیرے ان بندوں کے جو چیده اور پسندیده ہوں.');
INSERT INTO "junagarri" VALUES(38,84,'38:84','فرمایا سچ تو یہ ہے، اور میں سچ ہی کہا کرتا ہوں.');
INSERT INTO "junagarri" VALUES(38,85,'38:85','کہ تجھ سے اور تیرے تمام ماننے والوں سے میں (بھی) جہنم کو بھر دوں گا.');
INSERT INTO "junagarri" VALUES(38,86,'38:86','کہہ دیجئے کہ میں تم سے اس پر کوئی بدلہ طلب نہیں کرتا اور نہ میں تکلف کرنے والوں میں سے ہوں. ');
INSERT INTO "junagarri" VALUES(38,87,'38:87','یہ تو تمام جہان والوں کے لئے سراسر نصیحت (وعبرت) ہے. ');
INSERT INTO "junagarri" VALUES(38,88,'38:88','یقیناً تم اس کی حقیقت کو کچھ ہی وقت کے بعد (صحیح طور پر) جان لو گے. ');
INSERT INTO "junagarri" VALUES(39,1,'39:1','اس کتاب کا اتارنا اللہ تعالیٰ غالب باحکمت کی طرف سے ہے.');
INSERT INTO "junagarri" VALUES(39,2,'39:2','یقیناً ہم نے اس کتاب کو آپ کی طرف حق کے ساتھ نازل فرمایا ہے پس آپ اللہ ہی کی عبادت کریں، اسی کے لئے دین کو خالص کرتے ہوئے. ');
INSERT INTO "junagarri" VALUES(39,3,'39:3','خبردار! اللہ تعالیٰ ہی کے لئے خالص عبادت کرنا ہے اور جن لوگوں نے اس کے سوا اولیا بنا رکھے ہیں (اور کہتے ہیں) کہ ہم ان کی عبادت صرف اس لئے کرتے ہیں کہ یہ (بزرگ) اللہ کی نزدیکی کے مرتبہ تک ہماری رسائی کرا دیں ، یہ لوگ جس بارے میں اختلاف کر رہے ہیں اس کا (سچا) فیصلہ اللہ (خود) کرے گا ۔ جھوٹے اور ناشکرے (لوگوں) کو اللہ تعالیٰ راه نہیں دکھاتا. ');
INSERT INTO "junagarri" VALUES(39,4,'39:4','اگر اللہ تعالیٰ کااراده اولاد ہی کا ہوتا تو اپنی مخلوق میں سے جسے چاہتا چن لیتا۔ (لیکن) وه تو پاک ہے، وه وہی اللہ تعالیٰ ہے یگانہ اور قوت والا.');
INSERT INTO "junagarri" VALUES(39,5,'39:5','نہایت اچھی تدبیر سے اس نے آسمانوں اور زمین کو بنایا وه رات کو دن اور دن کو رات پر لپیٹ دیتا ہے اور اس نے سورج اور چاند کو کام پر لگا رکھا ہے۔ ہر ایک مقرره مدت تک چل رہا ہے یقین مانو کہ وہی زبردست اور گناہوں کا بخشنے والا ہے.');
INSERT INTO "junagarri" VALUES(39,6,'39:6','اس نے تم سب کو ایک ہی جان سے پیدا کیا ہے ، پھر اسی سے اس کا جوڑا پیدا کیا اور تمہارے لئے چوپایوں میں سے (آٹھ نر وماده) اتارے وه تمہیں تمہاری ماؤں کے پیٹوں میں ایک بناوٹ کے بعد دوسری بناوٹ پر بناتا ہے تین تین اندھیروں میں، یہی اللہ تعالیٰ تمہارا رب ہے اسی کے لئے بادشاہت ہے، اس کے سوا کوئی معبود نہیں، پھر تم کہاں بہک رہے ہو. ');
INSERT INTO "junagarri" VALUES(39,7,'39:7','اگر تم ناشکری کرو تو (یاد رکھو کہ) اللہ تعالیٰ تم (سب سے) بے نیاز ہے ، اور وه اپنے بندوں کی ناشکری سے خوش نہیں اور اگر تم شکر کرو تو وه اسے تمہارے لئے پسند کرے گا ۔ اور کوئی کسی کا بوجھ نہیں اٹھاتا پھر تم سب کا لوٹنا تمہارے رب ہی کی طرف ہے۔ تمہیں وه بتلا دے گا جو تم کرتے تھے۔ یقیناً وه دلوں تک کی باتوں کا واقف ہے.');
INSERT INTO "junagarri" VALUES(39,8,'39:8','اور انسان کو جب کبھی کوئی تکلیف پہنچتی ہے تو وه خوب رجوع ہو کر اپنے رب کو پکارتا ہے، پھر جب اللہ تعالیٰ اسے اپنے پاس سے نعمت عطا فرمادیتا ہے تو وه اس سے پہلے جو دعا کرتا تھا اسے (بالکل) بھول جاتا ہے اور اللہ تعالیٰ کے شریک مقرر کرنے لگتا ہے جس سے (اوروں کو بھی) اس کی راه سے بہکائے، آپ کہہ دیجئے! کہ اپنے کفر کا فائده کچھ دن اور اٹھالو، (آخر) تو دوزخیوں میں ہونے والا ہے.');
INSERT INTO "junagarri" VALUES(39,9,'39:9','بھلا جو شخص راتوں کے اوقات سجدے اور قیام کی حالت میں (عبادت میں) گزارتا ہو، آخرت سے ڈرتا ہو اور اپنے رب کی رحمت کی امید رکھتا ہو ، (اور جو اس کے برعکس ہو برابر ہو سکتے ہیں) بتاؤ تو علم والے اور بے علم کیا برابر کے ہیں ؟ یقیناً نصیحت وہی حاصل کرتے ہیں جو عقلمند ہوں۔ (اپنے رب کی طرف سے). ');
INSERT INTO "junagarri" VALUES(39,10,'39:10','کہہ دو کہ اے میرے ایمان والے بندو! اپنے رب سے ڈرتے رہو ، جو اس دنیا میں نیکی کرتے ہیں ان کے لئے نیک بدلہ ہے اور اللہ تعالیٰ کی زمین بہت کشاده ہے صبر کرنے والوں ہی کو ان کا پورا پورا بےشمار اجر دیا جاتا ہے. ');
INSERT INTO "junagarri" VALUES(39,11,'39:11','آپ کہہ دیجئے! کہ مجھے حکم دیا گیا ہے کہ اللہ تعالیٰ کی اس طرح عبادت کروں کہ اسی کے لئے عبادت کو خالص کر لوں.');
INSERT INTO "junagarri" VALUES(39,12,'39:12','اور مجھے حکم دیا گیا ہے کہ میں سب سے پہلا فرماں بردار بن جاؤں. ');
INSERT INTO "junagarri" VALUES(39,13,'39:13','کہہ دیجئے! کہ مجھے تو اپنے رب کی نافرمانی کرتے ہوئے بڑے دن کے عذاب کا خوف لگتا ہے.');
INSERT INTO "junagarri" VALUES(39,14,'39:14','کہہ دیجئے! کہ میں تو خالص کر کے صرف اپنے رب ہی کی عبادت کرتا ہوں.');
INSERT INTO "junagarri" VALUES(39,15,'39:15','تم اس کے سوا جس کی چاہو عبادت کرتے رہو کہہ دیجئے! کہ حقیقی زیاں کار وه ہیں جو اپنے آپ کو اور اپنے اہل کو قیامت کے دن نقصان میں ڈال دیں گے، یاد رکھو کہ کھلم کھلا نقصان یہی ہے.');
INSERT INTO "junagarri" VALUES(39,16,'39:16','انہیں نیچے اوپر سے آگ کے (شعلے مثل) سائبان (کے) ڈھانک رہے ہوں گے ۔ یہی (عذاب) ہے جن سے اللہ تعالیٰ اپنے بندوں کو ڈرا رہا ہے ، اے میرے بندو! پس مجھ سے ڈرتے رہو.');
INSERT INTO "junagarri" VALUES(39,17,'39:17','اور جن لوگوں نے طاغوت کی عبادت سے پرہیز کیا اور (ہمہ تن) اللہ تعالیٰ کی طرف متوجہ رہے وه خوش خبری کے مستحق ہیں، میرے بندوں کو خوشخبری سنا دیجئے.');
INSERT INTO "junagarri" VALUES(39,18,'39:18','جو بات کو کان لگا کر سنتے ہیں۔ پھر جو بہترین بات ہو اس کی اتباع کرتے ہیں۔ یہی ہیں جنہیں اللہ تعالیٰ نے ہدایت کی ہے اور یہی عقلمند بھی ہیں. ');
INSERT INTO "junagarri" VALUES(39,19,'39:19','بھلا جس شخص پر عذاب کی بات ثابت ہو چکی ہے ، تو کیا آپ اسے جو دوزخ میں ہے چھڑا سکتے ہیں؟ ');
INSERT INTO "junagarri" VALUES(39,20,'39:20','ہاں وه لوگ جو اپنے رب سے ڈرتے رہے ان کے لئے بالا خانے ہیں جن کے اوپر بھی بنے بنائے بالا خانے ہیں (اور) ان کے نیچے نہریں بہہ رہی ہیں۔ رب کا وعده ہے اور وه وعده خلافی نہیں کرتا.');
INSERT INTO "junagarri" VALUES(39,21,'39:21','کیا آپ نے نہیں دیکھا کہ اللہ تعالیٰ آسمان سے پانی اتارتا ہے اور اسے زمین کی سوتوں میں پہنچاتا ہے، پھر اسی کے ذریعہ سے مختلف قسم کی کھیتیاں اگاتا ہے پھر وه خشک ہو جاتی ہیں اور آپ انہیں زرد رنگ دیکھتے ہیں پھر انہیں ریزه ریزه کر دیتا ہے ، اس میں عقل مندوں کے لئے بہت زیاده نصیحت ہے. ');
INSERT INTO "junagarri" VALUES(39,22,'39:22','کیا وه شخص جس کا سینہ اللہ تعالیٰ نےاسلام کے لئے کھول دیا ہے پس وه اپنے پروردگار کی طرف سے ایک نور پر ہے اور ہلاکی ہے ان پر جن کے دل یاد الٰہی سے (اثر نہیں لیتے بلکہ) سخت ہو گئے ہیں۔ یہ لوگ صریح گمراہی میں (مبتلا) ہیں.');
INSERT INTO "junagarri" VALUES(39,23,'39:23','اللہ تعالیٰ نے بہترین کلام نازل فرمایا ہے جو ایسی کتاب ہے کہ آپس میں ملتی جلتی اور بار بار دہرائی ہوئی آیتوں کی ہے ، جس سے ان لوگوں کے رونگٹے کھڑے ہو جاتے ہیں جو اپنے رب کا خوف رکھتے ہیں آخر میں ان کے جسم اور دل اللہ تعالیٰ کے ذکر کی طرف نرم ہو جاتے ہیں ، یہ ہے اللہ تعالیٰ کی ہدایت جس کے ذریعہ جسے چاہے راه راست پر لگا دیتا ہے۔ اور جسے اللہ تعالیٰ ہی راه بھلا دے اس کا ہادی کوئی نہیں.');
INSERT INTO "junagarri" VALUES(39,24,'39:24','بھلا جو شخص قیامت کے دن کے بدترین عذاب کی سپر (ڈھال) اپنے منھ کو بنائے گا۔ (ایسے) ظالموں سے کہا جائے گا کہ اپنے کیے کا (وبال) چکھو. ');
INSERT INTO "junagarri" VALUES(39,25,'39:25','ان سے پہلے والوں نے بھی جھٹلایا، پھر ان پر وہاں سے عذاب آپڑا جہاں سے ان کو خیال بھی نہ تھا. ');
INSERT INTO "junagarri" VALUES(39,26,'39:26','اور اللہ تعالیٰ نے انہیں زندگانی دنیا میں رسوائی کا مزه چکھایا اور ابھی آخرت کا تو بڑا بھاری عذاب ہے کاش کہ یہ لوگ سمجھ لیں.');
INSERT INTO "junagarri" VALUES(39,27,'39:27','اور یقیناً ہم نے اس قرآن میں لوگوں کے لئے ہر قسم کی مثالیں بیان کر دیں ہیں کیا عجب کہ وه نصیحت حاصل کر لیں. ');
INSERT INTO "junagarri" VALUES(39,28,'39:28','قرآن ہے عربی میں جس میں کوئی کجی نہیں، ہو سکتا ہے کہ وه پرہیزگاری اختیار کر لیں. ');
INSERT INTO "junagarri" VALUES(39,29,'39:29','اللہ تعالیٰ مثال بیان فرما رہا ہے ایک وه شخص جس میں بہت سے باہم ضد رکھنے والے ساجھی ہیں، اور دوسرا وه شخص جو صرف ایک ہی کا (غلام) ہے، کیا یہ دونوں صفت میں یکساں ہیں ، اللہ تعالیٰ ہی کے لئے سب تعریف ہے ۔ بات یہ ہے کہ ان میں کے اکثر لوگ سمجھتے نہیں. ');
INSERT INTO "junagarri" VALUES(39,30,'39:30','یقیناً خود آپ کو بھی موت آئے گی اور یہ سب بھی مرنے والے ہیں.');
INSERT INTO "junagarri" VALUES(39,31,'39:31','پھر تم سب کے سب قیامت والے دن اپنے رب کے سامنے جھگڑو گے. ');
INSERT INTO "junagarri" VALUES(39,32,'39:32','اس سے بڑھ کر ظالم کون ہے جو اللہ تعالیٰ پر جھوٹ بولے ؟ اور سچا دین جب اس کے پاس آئے تو اسے جھوٹا بتائے ؟ کیا ایسے کفار کے لیے جہنم ٹھکانا نہیں ہے؟');
INSERT INTO "junagarri" VALUES(39,33,'39:33','اور جو سچے دین کو لائے اور جس نے اس کی تصدیق کی یہی لوگ پارسا ہیں.');
INSERT INTO "junagarri" VALUES(39,34,'39:34','ان کے لیے ان کے رب کے پاس (ہر) وه چیز ہے جو یہ چاہیں ، نیک لوگوں کا یہی بدلہ ہے. ');
INSERT INTO "junagarri" VALUES(39,35,'39:35','تاکہ اللہ تعالیٰ ان سے ان کے برے عملوں کو دور کردے اور جو نیک کام انہوں نے کیے ہیں ان کا اچھا بدلہ عطا فرمائے.');
INSERT INTO "junagarri" VALUES(39,36,'39:36','کیا اللہ تعالیٰ اپنے بندے کے لیے کافی نہیں ؟ یہ لوگ آپ کو اللہ کے سوا اوروں سے ڈرا رہے ہیں اور جسے اللہ گمراه کر دےاس کی رہنمائی کرنے والا کوئی نہیں. ');
INSERT INTO "junagarri" VALUES(39,37,'39:37','اور جسے وه ہدایت دے اسے کوئی گمراه کرنے والا نہیں کیا اللہ تعالیٰ غالب اور بدلہ لینے والا نہیں ہے؟ ');
INSERT INTO "junagarri" VALUES(39,38,'39:38','اگر آپ ان سے پوچھیں کہ آسمان و زمین کو کس نے پیدا کیا ہے؟ تو یقیناً وه یہی جواب دیں گے کہ اللہ نے۔ آپ ان سے کہئیے کہ اچھا یہ تو بتاؤ جنہیں تم اللہ کے سوا پکارتے ہو اگر اللہ تعالی مجھے نقصان پہنچانا چاہے توکیا یہ اس کے نقصان کو ہٹا سکتے ہیں؟ یا اللہ تعالیٰ مجھ پر مہربانی کا اراده کرے تو کیا یہ اس کی مہربانی کو روک سکتے ہیں؟ آپ کہہ دیں کہ اللہ مجھے کافی ہے ، توکل کرنے والے اسی پر توکل کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(39,39,'39:39','کہہ دیجیئے کہ اے میری قوم! تم اپنی جگہ پر عمل کیے جاؤ میں بھی عمل کر رہا ہوں ، ابھی ابھی تم جان لوگے.');
INSERT INTO "junagarri" VALUES(39,40,'39:40','کہ کس پر رسوا کرنے والا عذاب آتا ہے اور کس پر دائمی مار اور ہمیشگی کی سزا ہوتی ہے. ');
INSERT INTO "junagarri" VALUES(39,41,'39:41','آپ پر ہم نے حق کے ساتھ یہ کتاب لوگوں کے لیے نازل فرمائی ہے، پس جو شخص راه راست پر آجائے اس کے اپنے لیے نفع ہے اور جو گمراه ہوجائے اس کی گمراہی کا (وبال) اسی پر ہے، آپ ان کے ذمہ دار نہیں. ');
INSERT INTO "junagarri" VALUES(39,42,'39:42','اللہ ہی روحوں کو ان کی موت کے وقت اور جن کی موت نہیں آئی انہیں ان کی نیند کے وقت قبض کر لیتا ہے ، پھر جن پر موت کا حکم لگ چکا ہے انہیں تو روک لیتا ہے اور دوسری (روحوں) کو ایک مقرر وقت تک کے لیے چھوڑ دیتا ہے ۔ غور کرنے والوں کے لیے اس میں یقیناً بہت سی نشانیاں ہیں. ');
INSERT INTO "junagarri" VALUES(39,43,'39:43','کیا ان لوگوں نے اللہ تعالی کے سوا (اوروں) کو سفارشی مقرر کر رکھا ہے؟ آپ کہہ دیجیئے! کہ گو وه کچھ بھی اختیار نہ رکھتے ہوں اور نہ عقل رکھتے ہوں. ');
INSERT INTO "junagarri" VALUES(39,44,'39:44','کہہ دیجئے! کہ تمام سفارش کا مختار اللہ ہی ہے ۔ تمام آسمانوں اور زمین کا راج اسی کے لیے ہے تم سب اسی کی طرف پھیرے جاؤ گے.');
INSERT INTO "junagarri" VALUES(39,45,'39:45','جب اللہ اکیلے کا ذکر کیا جائے تو ان لوگوں کے دل نفرت کرنے لگتے ہیں جو آخرت کایقین نہیں رکھتے اور جب اس کے سوا (اور کا) ذکر کیا جائے تو ان کے دل کھل کر خوش ہو جاتے ہیں. ');
INSERT INTO "junagarri" VALUES(39,46,'39:46','آپ کہہ دیجئے! کہ اے اللہ! آسمانوں اور زمین کے پیدا کرنے والے، چھپے کھلے کے جاننے والے تو ہی اپنے بندوں میں ان امور کا فیصلہ فرمائے گا جن میں وه الجھ رہے تھے. ');
INSERT INTO "junagarri" VALUES(39,47,'39:47','اگر ظلم کرنے والوں کے پاس وه سب کچھ ہو جو روئے زمین پر ہے اور اس کے ساتھ اتنا ہی اور ہو، تو بھی بدترین سزا کے بدلے میں قیامت کے دن یہ سب کچھ دے دیں ، اور ان کے سامنے اللہ کی طرف سے وه ظاہر ہوگا جس کا گمان بھی انہیں نہ تھا. ');
INSERT INTO "junagarri" VALUES(39,48,'39:48','جو کچھ انہوں نے کیا تھا اس کی برائیاں ان پر کھل پڑیں گی اور جس کا وه مذاق کرتے تھے اور وه انہیں آ گھیرے گا. ');
INSERT INTO "junagarri" VALUES(39,49,'39:49','انسان کو جب کوئی تکلیف پہنچتی ہے تو ہمیں پکارنے لگتا ہے ، پھر جب ہم اسے اپنی طرف سے کوئی نعمت عطا فرمادیں تو کہنے لگتا ہے کہ اسے تو میں محض اپنے علم کی وجہ سے دیا گیا ہوں ، بلکہ یہ آزمائش ہے لیکن ان میں سے اکثر لوگ بے علم ہیں. ');
INSERT INTO "junagarri" VALUES(39,50,'39:50','ان سے اگلے بھی یہی بات کہہ چکے ہیں پس ان کی کارروائی ان کے کچھ کام نہ آئی. ');
INSERT INTO "junagarri" VALUES(39,51,'39:51','پھر ان کی تمام برائیاں ان پر آ پڑیں، اوران میں سے بھی جو گناه گار ہیں ان کی کی ہوئی برائیاں بھی اب ان پر آ پڑیں گی، یہ (ہمیں) ہرا دینے والے نہیں. ');
INSERT INTO "junagarri" VALUES(39,52,'39:52','کیا انہیں یہ معلوم نہیں کہ اللہ تعالیٰ جس کے لیے چاہے روزی کشاده کر دیتا ہے اور تنگ (بھی)، ایمان لانے والوں کے لیے اس میں (بڑی بڑی) نشانیاں ہیں. ');
INSERT INTO "junagarri" VALUES(39,53,'39:53','(میری جانب سے) کہہ دو کہ اے میرے بندو! جنہوں نے اپنی جانوں پر زیادتی کی ہے تم اللہ کی رحمت سے ناامید نہ ہو جاؤ، بالیقین اللہ تعالیٰ سارے گناہوں کو بخش دیتا ہے، واقعی وه بڑی بخشش بڑی رحمت والا ہے. ');
INSERT INTO "junagarri" VALUES(39,54,'39:54','تم (سب) اپنے پروردگار کی طرف جھک پڑو اور اس کی حکم برداری کیے جاؤ اس سے قبل کہ تمہارے پاس عذاب آ جائے اور پھر تمہاری مدد نہ کی جائے.');
INSERT INTO "junagarri" VALUES(39,55,'39:55','اور پیروری کرو اس بہترین چیزکی جو تمہاری طرف تمہارے پروردگار کی طرف سے نازل کی گئی ہے، اس سے پہلے کہ تم پر اچانک عذاب آجائے اور تمہیں اطلاع بھی نہ ہو. ');
INSERT INTO "junagarri" VALUES(39,56,'39:56','(ایسا نہ ہو کہ) کوئی شخص کہے ہائے افسوس! اس بات پر کہ میں نے اللہ تعالیٰ کے حق میں کوتاہی کی بلکہ میں تو مذاق اڑانے والوں میں ہی رہا.');
INSERT INTO "junagarri" VALUES(39,57,'39:57','یا کہے کہ اگر اللہ مجھے ہدایت کرتا تو میں بھی پارسا لوگوں میں ہوتا. ');
INSERT INTO "junagarri" VALUES(39,58,'39:58','یا عذاب کو دیکھ کر کہے کاش! کہ کسی طرح میرا لوٹ جانا ہوجاتا تو میں بھی نیکو کاروں میں ہوجاتا.');
INSERT INTO "junagarri" VALUES(39,59,'39:59','ہاں (ہاں) بیشک تیرے پاس میری آیتیں پہنچ چکی تھیں جنہیں تونے جھٹلایا اور غرور وتکبر کیا اور تو تھا ہی کافروں میں. ');
INSERT INTO "junagarri" VALUES(39,60,'39:60','اور جن لوگوں نے اللہ پر جھوٹ باندھا ہے تو آپ دیکھیں گے کہ قیامت کے دن ان کے چہرے سیاه ہوگئے ہوں گے کیا تکبر کرنے والوں کاٹھکانا جہنم میں نہیں؟ ');
INSERT INTO "junagarri" VALUES(39,61,'39:61','اور جن لوگوں نے پرہیزگاری کی انہیں اللہ تعالیٰ ان کی کامیابی کے ساتھ بچا لے گا، انہیں کوئی دکھ چھو بھی نہ سکے گا اور نہ وه کسی طرح غمگین ہوں گے. ');
INSERT INTO "junagarri" VALUES(39,62,'39:62','اللہ ہر چیز کا پیدا کرنے والا ہے اور وہی ہر چیز پر نگہبان ہے. ');
INSERT INTO "junagarri" VALUES(39,63,'39:63','آسمانوں اور زمین کی کنجیوں کامالک وہی ہے جن جن لوگوں نے اللہ کی آیتوں کا انکار کیا وہی خساره پانے والے ہیں. ');
INSERT INTO "junagarri" VALUES(39,64,'39:64','آپ کہہ دیجئے اے جاہلو! کیا تم مجھ سے اللہ کے سوا اوروں کی عبادت کو کہتے ہو. ');
INSERT INTO "junagarri" VALUES(39,65,'39:65','یقیناً تیری طرف بھی اور تجھ سے پہلے (کے تمام نبیوں) کی طرف بھی وحی کی گئی ہے کہ اگر تو نے شرک کیا تو بلاشبہ تیرا عمل ضائع ہو جائے گا اور بالیقین تو زیاںکاروں میں سے ہوجائے گا. ');
INSERT INTO "junagarri" VALUES(39,66,'39:66','بلکہ تو اللہ ہی کی عبادت کر اور شکر کرنے والوں میں سے ہو جا.');
INSERT INTO "junagarri" VALUES(39,67,'39:67','اور ان لوگوں نے جیسی قدر اللہ تعالیٰ کی کرنی چاہئے تھی نہیں کی ، ساری زمین قیامت کے دن اس کی مٹھی میں ہوگی اور تمام آسمان اس کے داہنے ہاتھ میں لپیٹے ہوئے ہوں گے ، وه پاک اور برتر ہے ہر اس چیز سے جسے لوگ اس کا شریک بنائیں.');
INSERT INTO "junagarri" VALUES(39,68,'39:68','اور صور پھونک دیا جائے گا پس آسمانوں اور زمین والے سب بے ہوش ہوکر گر پڑیں گے مگر جسے اللہ چاہے ، پھر دوباره صور پھونکا جائے گا پس وه ایک دم کھڑے ہو کر دیکھنے لگ جائیں گے. ');
INSERT INTO "junagarri" VALUES(39,69,'39:69','اور زمین اپنے پروردگار کے نور سے جگمگا اٹھے گی ، نامہٴ اعمال حاضر کیے جائیں گے نبیوں اور گواہوں کو لایا جائے گا اور لوگوں کے درمیان حق حق فیصلے کر دیے جائیں گے، اور وه ظلم نہ کیے جائیں گے. ');
INSERT INTO "junagarri" VALUES(39,70,'39:70','اور جس شخص نے جو کچھ کیا ہے بھرپور دے دیا جائے گا، جو کچھ لوگ کر رہے ہیں وه بخوبی جاننے والا ہے. ');
INSERT INTO "junagarri" VALUES(39,71,'39:71','کافروں کے غول کے غول جہنم کی طرف ہنکائے جائیں گے ، جب وه اس کے پاس پہنچ جائیں گے اس کے دروازے ان کے لیے کھول دیے جائیں گے ، اور وہاں کے نگہبان ان سے سوال کریں گے کہ کیا تمہارے پاس تم میں سے رسول نہیں آئے تھے؟ جو تم پر تمہارے رب کی آیتیں پڑھتے تھے اور تمہیں اس دن کی ملاقات سے ڈراتے تھے؟ یہ جواب دیں گے کہ ہاں درست ہے لیکن عذاب کا حکم کافروں پر ثابت ہو گیا. ');
INSERT INTO "junagarri" VALUES(39,72,'39:72','کہا جائے گا کہ اب جہنم کے دروازوں میں داخل ہو جاؤ جہاں ہمیشہ رہیں گے، پس سرکشوں کا ٹھکانا بہت ہی برا ہے.');
INSERT INTO "junagarri" VALUES(39,73,'39:73','اور جو لوگ اپنے رب سے ڈرتے تھے ان کے گروه کے گروه جنت کی طرف روانہ کیے جائیں گے یہاں تک کہ جب اس کے پاس آ جائیں گے اور دروازے کھول دیے جائیں گے اور وہاں کے نگہبان ان سے کہیں گے تم پر سلام ہو، تم خوش حال رہو تم اس میں ہمیشہ کے لیے چلے جاؤ.');
INSERT INTO "junagarri" VALUES(39,74,'39:74','یہ کہیں گے کہ اللہ کاشکر ہے کہ جس نے ہم سے اپنا وعده پورا کیا اور ہمیں اس زمین کا وارث بنا دیا کہ جنت میں جہاں چاہیں مقام کریں پس عمل کرنے والوں کا کیا ہی اچھا بدلہ ہے.');
INSERT INTO "junagarri" VALUES(39,75,'39:75','اور تو فرشتوں کو اللہ کے عرش کے اردگرد حلقہ باندھے ہوئے اپنے رب کی حمد و تسبیح کرتے ہوئے دیکھے گا اور ان میں انصاف کا فیصلہ کیا جائے گا اور کہہ دیا جائے گا کہ ساری خوبی اللہ ہی کے لیے ہے جو تمام جہانوں کا پالنہار ہے. ');
INSERT INTO "junagarri" VALUES(40,1,'40:1','حٰم.');
INSERT INTO "junagarri" VALUES(40,2,'40:2','اس کتاب کا نازل فرمانا اس اللہ کی طرف سے ہے جو غالب اور دانا ہے. ');
INSERT INTO "junagarri" VALUES(40,3,'40:3','گناه کا بخشنے والا اور توبہ کا قبول فرمانے والا سخت عذاب والا انعام و قدرت والا ، جس کے سوا کوئی معبود نہیں۔ اسی کی طرف واپس لوٹنا ہے.');
INSERT INTO "junagarri" VALUES(40,4,'40:4','اللہ تعالیٰ کی آیتوں میں وہی لوگ جھگڑتے ہیں جو کافر ہیں پس ان لوگوں کا شہروں میں چلنا پھرنا آپ کو دھوکے میں نہ ڈالے. ');
INSERT INTO "junagarri" VALUES(40,5,'40:5','قوم نوح نے اور ان کے بعد کے گروہوں نے بھی جھٹلایا تھا۔ اور ہر امت نے اپنے رسول کو گرفتار کر لینے کا اراده کیا اور باطل کے ذریعہ کج بحثیاں کیں، تاکہ ان سے حق کو بگاڑ دیں پس میں نے ان کو پکڑ لیا، سو میری طرف سے کیسی سزا ہوئی. ');
INSERT INTO "junagarri" VALUES(40,6,'40:6','اور اسی طرح آپ کے رب کا حکم کافروں پر ثابت ہو گیا کہ وه دوزخی ہیں. ');
INSERT INTO "junagarri" VALUES(40,7,'40:7','عرش کے اٹھانے والے اور اسکے آس پاس کے (فرشتے) اپنے رب کی تسبیح حمد کے ساتھ ساتھ کرتے ہیں اور اس پر ایمان رکھتے ہیں اور ایمان والوں کے لیے استفغار کرتے ہیں، کہتے ہیں کہ اے ہمارے پروردگار! تو نے ہر چیز کو اپنی بخشش اور علم سے گھیر رکھا ہے، پس تو انہیں بخش دے جو توبہ کریں اور تیری راه کی پیروی کریں اور تو انہیں دوزخ کے عذاب سے بھی بچا لے. ');
INSERT INTO "junagarri" VALUES(40,8,'40:8','اے ہمارے رب! تو انہیں ہمیشگی والی جنتوں میں لے جا جن کا تو نے ان سے وعده کیا ہے اور ان کے باپ دادوں اور بیویوں اور اولاد میں سے (بھی) ان (سب) کو جو نیک عمل ہیں ۔ یقیناً تو تو غالب و باحکمت ہے.');
INSERT INTO "junagarri" VALUES(40,9,'40:9','انہیں برائیوں سے بھی محفوظ رکھ ، حق تو یہ ہے کہ اس دن تونے جسے برائیوں سے بچا لیا اس پر تو نے رحمت کر دی اور بہت بڑی کامیابی تو یہی ہے. ');
INSERT INTO "junagarri" VALUES(40,10,'40:10','بےشک جن لوگوں نے کفر کیا انہیں یہ آواز دی جائے گی کہ یقیناً اللہ کا تم پر غصہ ہونااس سے بہت زیاده ہے جو تم غصہ ہوتے تھے اپنے جی سے، جب تم ایمان کی طرف بلائے جاتے تھے پھر کفر کرنے لگتے تھے. ');
INSERT INTO "junagarri" VALUES(40,11,'40:11','وه کہیں گے اے ہمارے پروردگار! تو نے ہمیں دوبار مارا اور دو بار ہی جلایا ، اب ہم اپنے گناہوں کے اقراری ہیں ، تو کیا اب کوئی راه نکلنے کی بھی ہے؟ ');
INSERT INTO "junagarri" VALUES(40,12,'40:12','یہ (عذاب) تمہیں اس لیے ہے کہ جب صرف اکیلے اللہ کا ذکر کیا جاتا تو تم انکار کر جاتے تھے اور اگر اس کے ساتھ کسی کو شریک کیا جاتا تھا تو تم مان لیتے تھے پس اب فیصلہ اللہ بلند و بزرگ ہی کا ہے. ');
INSERT INTO "junagarri" VALUES(40,13,'40:13','وہی ہے جو تمہیں اپنی نشانیاں دکھلاتا ہے اور تمہارے لیےآسمان سے روزی اتارتا ہے ، نصیحت تو صرف وہی حاصل کرتے ہیں جو (اللہ کی طرف) رجوع کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(40,14,'40:14','تم اللہ کو پکارتے رہو اس کے لیے دین کو خالص کر کے گو کافر برا مانیں. ');
INSERT INTO "junagarri" VALUES(40,15,'40:15','بلند درجوں والا عرش کامالک وه اپنے بندوں میں سے جس پر چاہتا ہے وحی نازل فرماتا ہے ، تاکہ وه ملاقات کے دن سے ڈرائے.');
INSERT INTO "junagarri" VALUES(40,16,'40:16','جس دن سب لوگ ظاہر ہو جائیں گے ، ان کی کوئی چیز اللہ سے پوشیده نہ رہے گی۔ آج کس کی بادشاہی ہے ؟ فقط اللہ واحد و قہار کی. ');
INSERT INTO "junagarri" VALUES(40,17,'40:17','آج ہر نفس کو اس کی کمائی کا بدلہ دیا جائے گا۔ آج (کسی قسم کا) ظلم نہیں، یقیناً اللہ تعالیٰ بہت جلد حساب کرنے والا ہے. ');
INSERT INTO "junagarri" VALUES(40,18,'40:18','اور انہیں بہت ہی قریب آنے والی (قیامت سے) آگاه کر دیجئے، جب کہ دل حلق تک پہنچ جائیں گے اور سب خاموش ہوں گے ، ظالموں کا نہ کوئی دلی دوست ہوگا نہ سفارشی، کہ جس کی بات مانی جائے گی.');
INSERT INTO "junagarri" VALUES(40,19,'40:19','وه آنکھوں کی خیانت کو اور سینوں کی پوشیده باتوں کو (خوب) جانتا ہے. ');
INSERT INTO "junagarri" VALUES(40,20,'40:20','اور اللہ تعالیٰ ٹھیک ٹھیک فیصلہ کر دے گا اس کے سوا جنہیں یہ لوگ پکارتے ہیں وه کسی چیز کا بھی فیصلہ نہیں کرسکتے ، بیشک اللہ تعالیٰ خوب سنتا خوب دیکھتا ہے.');
INSERT INTO "junagarri" VALUES(40,21,'40:21','کیا یہ لوگ زمین میں چلے پھرے نہیں کہ دیکھتے کہ جو لوگ ان سے پہلے تھے ان کا نتیجہ کیسا کچھ ہوا؟ وه باعتبار قوت و طاقت کے اور باعتبار زمین میں اپنی یادگاروں کے ان سے بہت زیاده تھے، پس اللہ نے انہیں ان کے گناہوں پر پکڑ لیا اور کوئی نہ ہوا جو انہیں اللہ کے عذاب سے بچا لیتا. ');
INSERT INTO "junagarri" VALUES(40,22,'40:22','یہ اس وجہ سے کہ ان کے پاس ان کے پیغمبر معجزے لے لے کرآتے تھے تو وه انکار کر دیتے تھے ، پس اللہ انہیں پکڑ لیتا تھا۔ یقیناً وه طاقتور اور سخت عذاب والا ہے.');
INSERT INTO "junagarri" VALUES(40,23,'40:23','اور ہم نے موسیٰ (علیہ السلام) کو اپنی آیتوں اور کھلی دلیلوں کے ساتھ بھیجا. ');
INSERT INTO "junagarri" VALUES(40,24,'40:24','فرعون ہامان اور قارون کی طرف تو انہوں نے کہا (یہ تو) جادوگر اور جھوٹا ہے. ');
INSERT INTO "junagarri" VALUES(40,25,'40:25','پس جب ان کے پاس (موسیٰ علیہ السلام) ہماری طرف سے (دین) حق کولے کر آئے توانہوں نے کہا کہ اس کے ساتھ جوایمان والے ہیں ان کے لڑکوں کو تو مار ڈالو اور ان کی لڑکیوں کو زنده رکھو اور کافروں کی جو حیلہ سازی ہے وه غلطی میں ہی ہے. ');
INSERT INTO "junagarri" VALUES(40,26,'40:26','اور فرعون نے کہا مجھے چھوڑ دو کہ میں موسیٰ (علیہ السلام) کو مار ڈالوں اور اسے چاہئے کہ اپنے رب کو پکارے ، مجھے تو ڈر ہے کہ یہ کہیں تمہارا دین نہ بدل ڈالے یا ملک میں کوئی (بہت بڑا) فساد برپا نہ کردے. ');
INSERT INTO "junagarri" VALUES(40,27,'40:27','موسیٰ (علیہ السلام) نے کہا میں اپنے اور تمہارے رب کی پناه میں آتا ہوں ہر اس تکبر کرنے والے شخص (کی برائی) سے جو روز حساب پر ایمان نہیں رکھتا. ');
INSERT INTO "junagarri" VALUES(40,28,'40:28','اور ایک مومن شخص نے، جو فرعون کے خاندان میں سے تھا اور اپنا ایمان چھپائے ہوئے تھا، کہا کہ کیا تم ایک شحض کو محض اس بات پر قتل کرتے ہو کہ وه کہتا ہے میرا رب اللہ ہے اور تمہارے رب کی طرف سے دلیلیں لے کر آیا ہے ، اگر وه جھوٹا ہو تو اس کا جھوٹ اسی پر ہے اور اگر وه سچا ہو، تو جس (عذاب) کا وه تم سے وعده کر رہا ہے اس میں سے کچھ نہ کچھ تو تم پر آ پڑے گا ، اللہ تعالیٰ اس کی رہبری نہیں کرتا جو حد سے گزر جانے والے اور جھوٹے ہیں. ');
INSERT INTO "junagarri" VALUES(40,29,'40:29','اے میری قوم کے لوگو! آج تو بادشاہت تمہاری ہے کہ اس زمین پر تم غالب ہو لیکن اگر اللہ کا عذاب ہم پر آ گیا تو کون ہماری مدد کرے گا ؟ فرعون بولا، میں تو تمہیں وہی رائے دے رہا ہوں جو خود دیکھ رہا ہوں اور میں تو تمہیں بھلائی کی راه ہی بتلا رہا ہوں. ');
INSERT INTO "junagarri" VALUES(40,30,'40:30','اس مومن نے کہا اے میری قوم! (کے لوگو) مجھے تو اندیشہ ہے کہ تم پر بھی ویسا ہی روز (بد عذاب) نہ آئے جو اور امتوں پر آیا.');
INSERT INTO "junagarri" VALUES(40,31,'40:31','جیسے امت نو ح اور عاد وثمود اور ان کے بعد والوں کا (حال ہوا) ، اللہ اپنے بندوں پر کسی طرح کا ظلم کرنا نہیں چاہتا. ');
INSERT INTO "junagarri" VALUES(40,32,'40:32','اور مجھے تم پر ہانک پکار کے دن کابھی ڈر ہے. ');
INSERT INTO "junagarri" VALUES(40,33,'40:33','جس دن تم پیٹھ پھیر کر لوٹو گے ، تمہیں اللہ سے بچانے والا کوئی نہ ہوگا اور جسے اللہ گمراه کر دے اس کا ہادی کوئی نہیں. ');
INSERT INTO "junagarri" VALUES(40,34,'40:34','اور اس سے پہلے تمہارے پاس (حضرت) یوسف دلیلیں لے کر آئے ، پھر بھی تم ان کی لائی ہوئی (دلیل) میں شک و شبہ ہی کرتے رہے یہاں تک کہ جب ان کی وفات ہو گئی تو کہنے لگے ان کے بعد تو اللہ کسی رسول کو بھیجے گا ہی نہیں اسی طرح اللہ گمراه کرتا ہے ہر اس شخص کو جو حد سے بڑھ جانے والا شک و شبہ کرنے والا ہو. ');
INSERT INTO "junagarri" VALUES(40,35,'40:35','جو بغیر کسی سند کے جو ان کے پاس آئی ہو اللہ کی آیتوں میں جھگڑتے ہیں ، اللہ کے نزدیک اور مومنوں کے نزدیک یہ تو بہت بڑی ناراضگی کی چیز ہے ، اللہ تعالیٰ اسی طرح ہر ایک مغرور سرکش کے دل پر مہر کردیتا ہے. ');
INSERT INTO "junagarri" VALUES(40,36,'40:36','فرعون نے کہا اے ہامان! میرے لیے ایک بالاخانہ بنا شاید کہ میں آسمان کے جو دروازے ہیں.');
INSERT INTO "junagarri" VALUES(40,37,'40:37','(ان) دروازوں تک پہنچ جاؤں اور موسیٰ کے معبود کو جھانک لوں اور بیشک میں سمجھتا ہوں وه جھوٹا ہے اور اسی طرح فرعون کی بدکرداریاں اسے بھلی دکھائی گئیں اور راه سے روک دیا گیا اور فرعون کی (ہر) حیلہ سازی تباہی میں ہی رہی. ');
INSERT INTO "junagarri" VALUES(40,38,'40:38','اور اس مومن شخص نے کہا کہ اے میری قوم! (کے لوگو) تم (سب) میری پیروی کرو میں نیک راه کی طرف تمہاری رہبری کروں گا. ');
INSERT INTO "junagarri" VALUES(40,39,'40:39','اے میری قوم! یہ حیات دنیا متاع فانی ہے ، (یقین مانو کہ قرار) اور ہمیشگی کا گھر تو آخرت ہی ہے. ');
INSERT INTO "junagarri" VALUES(40,40,'40:40','جس نے گناه کیا ہے اسے تو برابر برابر کا بدلہ ہی ہے اور جس نے نیکی کی ہے خواه وه مرد ہو یا عورت اور وه ایمان والا ہو تو یہ لوگ جنت میں جائیں گے اور وہاں بےشمار روزی پائیں گے. ');
INSERT INTO "junagarri" VALUES(40,41,'40:41','اے میری قوم! یہ کیا بات ہے کہ میں تمہیں نجات کی طرف بلا رہا ہوں اور تم مجھے دوزخ کی طرف بلا رہے ہو. ');
INSERT INTO "junagarri" VALUES(40,42,'40:42','تم مجھے یہ دعوت دے رہے ہوکہ میں اللہ کے ساتھ کفر کروں اور اس کے ساتھ شرک کروں جس کا کوئی علم مجھے نہیں اور میں تمہیں غالب بخشنے والے (معبود) کی طرف دعوت دے رہا ہوں. ');
INSERT INTO "junagarri" VALUES(40,43,'40:43','یہ یقینی امر ہے کہ تم مجھے جس کی طرف بلا رہے ہو وه تو نہ دنیا میں پکارے جانے کے قابل ہے نہ آخرت میں ، اور یہ (بھی یقینی بات ہے) کہ ہم سب کا لوٹنا اللہ کی طرف ہے اور حد سے گزر جانے والے ہی (یقیناً) اہل دوزخ ہیں. ');
INSERT INTO "junagarri" VALUES(40,44,'40:44','پس آگے چل کر تم میری باتوں کو یاد کرو گے میں اپنا معاملہ اللہ کے سپرد کرتا ہوں ، یقیناً اللہ تعالیٰ بندوں کا نگراں ہے. ');
INSERT INTO "junagarri" VALUES(40,45,'40:45','پس اسے اللہ تعالیٰ نے تمام بدیوں سے محفوظ رکھ لیا جو انہوں نے سوچ رکھی تھیں اور فرعون والوں پر بری طرح کا عذاب الٹ پڑا. ');
INSERT INTO "junagarri" VALUES(40,46,'40:46','آگ ہے جس کے سامنے یہ ہر صبح شام لائے جاتے ہیں اور جس دن قیامت قائم ہوگی (فرمان ہوگا کہ) فرعونیوں کو سخت ترین عذاب میں ڈالو. ');
INSERT INTO "junagarri" VALUES(40,47,'40:47','اور جب کہ دوزخ میں ایک دوسرے سے جھگڑیں گے تو کمزور لوگ تکبر والوں سے (جن کے یہ تابع تھے) کہیں گے کہ ہم تو تمہارے پیرو تھے تو کیا اب تم ہم سے اس آگ کا کوئی حصہ ہٹا سکتے ہو؟');
INSERT INTO "junagarri" VALUES(40,48,'40:48','وه بڑے لوگ جواب دیں گے ہم تو سبھی اس آگ میں ہیں، اللہ تعالیٰ اپنے بندوں کے درمیان فیصلے کر چکا ہے.');
INSERT INTO "junagarri" VALUES(40,49,'40:49','اور (تمام) جہنمی مل کر جہنم کے داروغوں سے کہیں گے کہ تم ہی اپنے پروردگار سے دعا کرو کہ وه کسی دن تو ہمارے عذاب میں کمی کردے.');
INSERT INTO "junagarri" VALUES(40,50,'40:50','وه جواب دیں گے کہ کیا تمہارے پاس تمہارے رسول معجزے لے کر نہیں آئے تھے؟ وه کہیں گے کیوں نہیں، وه کہیں گے کہ پھر تم ہی دعا کرو اور کافروں کی دعا محض بے اثر اور بےراه ہے. ');
INSERT INTO "junagarri" VALUES(40,51,'40:51','یقیناً ہم اپنے رسولوں کی اور ایمان والوں کی مدد زندگانیٴ دنیا میں بھی کریں گے اور اس دن بھی جب گواہی دینے والے کھڑے ہوں گے.');
INSERT INTO "junagarri" VALUES(40,52,'40:52','جس دن ظالموں کو ان کی (عذر) معذرت کچھ نفع نہ دے گی ان کے لیے لعنت ہی ہوگی اور ان کے لیے برا گھر ہو گا. ');
INSERT INTO "junagarri" VALUES(40,53,'40:53','ہم نے موسیٰ (علیہ السلام) کو ہدایت نامہ عطا فرمایا اور بنو اسرائیل کو اس کتاب کا وارث بنایا. ');
INSERT INTO "junagarri" VALUES(40,54,'40:54','کہ وه ہدایت و نصیحت تھی عقل مندوں کے لیے. ');
INSERT INTO "junagarri" VALUES(40,55,'40:55','پس اے نبی! تو صبر کر اللہ کا وعده بلاشک (وشبہ) سچا ہی ہے تو اپنے گناه کی معافی مانگتا ره اور صبح شام اپنے پروردگار کی تسبیح اور حمد بیان کرتا ره.');
INSERT INTO "junagarri" VALUES(40,56,'40:56','جو لوگ باوجود اپنے پاس کسی سند کے نہ ہونے کے آیات الٰہی میں جھگڑا کرتے ہیں ان کے دلوں میں بجز نری بڑائی کے اور کچھ نہیں وه اس تک پہنچنے والے ہی نہیں ، سو تو اللہ کی پناه مانگتا ره بیشک وه پورا سننے والا اور سب سے زیاده دیکھنے والا ہے.');
INSERT INTO "junagarri" VALUES(40,57,'40:57','آسمان و زمین کی پیدائش یقیناً انسان کی پیدائش سے بہت بڑا کام ہے، لیکن (یہ اور بات ہے کہ) اکثر لوگ بےعلم ہیں. ');
INSERT INTO "junagarri" VALUES(40,58,'40:58','اندھا اور بینا برابر نہیں نہ وه لوگ جو ایمان لائے اور بھلے کام کیے بدکاروں کے (برابر ہیں) ، تم (بہت) کم نصیحت حاصل کر رہے ہو.');
INSERT INTO "junagarri" VALUES(40,59,'40:59','قیامت بالیقین اور بےشبہ آنے والی ہے، لیکن (یہ اور بات ہے کہ) بہت سے لوگ ایمان نہیں لاتے.');
INSERT INTO "junagarri" VALUES(40,60,'40:60','اور تمہارے رب کا فرمان (سرزد ہوچکا ہے) کہ مجھ سے دعا کرو میں تمہاری دعاؤں کو قبول کروں گا یقین مانو کہ جو لوگ میری عبادت سے خودسری کرتے ہیں وه عنقريب ذلیل ہوکر جہنم میں پہنچ جائیں گے. ');
INSERT INTO "junagarri" VALUES(40,61,'40:61','اللہ تعالیٰ نے تمھارے لیے رات بنادی کہ تم اس میں آرام حاصل کرو اور دن کو دیکھنے والا بنا دیا ، بیشک اللہ تعالیٰ لوگوں پر فضل وکرم والا ہے لیکن اکثر لوگ شکر گزاری نہیں کرتے. ');
INSERT INTO "junagarri" VALUES(40,62,'40:62','یہی اللہ ہے تم سب کا رب ہر چیز کا خالق اس کے سوا کوئی معبود نہیں پھر کہاں تم پھرے جاتے ہو. ');
INSERT INTO "junagarri" VALUES(40,63,'40:63','اسی طرح وه لوگ بھی پھیرے جاتے رہے جو اللہ کی آیتوں کا انکار کرتے تھے.');
INSERT INTO "junagarri" VALUES(40,64,'40:64','اللہ ہی ہے جس نے تمہارے لیے زمین کو ٹھہرنے کی جگہ اور آسمان کو چھت بنا دیا اور تمہاری صورتیں بنائیں اور بہت اچھی بنائیں اور تمہیں عمده عمده چیزیں کھانے کو عطا فرمائیں ، یہی اللہ تمہارا پروردگار ہے، پس بہت ہی برکتوں والا اللہ ہے سارے جہان کا پرورش کرنے والا.');
INSERT INTO "junagarri" VALUES(40,65,'40:65','وه زنده ہے جس کے سوا کوئی معبود نہیں پس تم خالص اس کی عبادت کرتے ہوئے اسے پکارو ، تمام خوبیاں اللہ ہی کے لیے ہیں جو تمام جہانوں کا رب ہے.');
INSERT INTO "junagarri" VALUES(40,66,'40:66','آپ کہہ دیجئے! کہ مجھے ان کی عبادت سے روک دیا گیا ہے جنہیں تم اللہ کے سوا پکار رہے ہو اس بنا پر کہ میرے پاس میرے رب کی دلیلیں پہنچ چکی ہیں، مجھے یہ حکم دیا گیا ہے کہ میں تمام جہانوں کے رب کا تابع فرمان ہو جاؤں. ');
INSERT INTO "junagarri" VALUES(40,67,'40:67','وه وہی ہے جس نے تمہیں مٹی سے پھر نطفے سے پھر خون کے لوتھڑے سے پیدا کیا پھر تمہیں بچہ کی صورت میں نکالتا ہے، پھر (تمہیں بڑھاتا ہے کہ) تم اپنی پوری قوت کو پہنچ جاؤ پھر بوڑھے ہو جاؤ ، تم میں سے بعض اس سے پہلے ہی فوت ہو جاتے ہیں ، (وه تمہیں چھوڑ دیتا ہے) تاکہ تم مدت معین تک پہنچ جاؤ اور تاکہ تم سوچ سمجھ لو. ');
INSERT INTO "junagarri" VALUES(40,68,'40:68','وہی ہے جو جلاتا ہے اور مار ڈالتا ہے ، پھر جب وه کسی کام کا کرنا مقرر کرتا ہے تو اسے صرف یہ کہتا ہے کہ ہو جا پس وه ہو جاتا ہے. ');
INSERT INTO "junagarri" VALUES(40,69,'40:69','کیا تو نے انہیں دیکھا جو اللہ کی آیتوں میں جھگڑتے ہیں ، وه کہاں پھیر دیے جاتے ہیں. ');
INSERT INTO "junagarri" VALUES(40,70,'40:70','جن لوگوں نے کتاب کو جھٹلایا اور اسے بھی جو ہم نے اپنے رسولوں کے ساتھ بھیجا انہیں ابھی ابھی حقیقت حال معلوم ہو جائے گی.');
INSERT INTO "junagarri" VALUES(40,71,'40:71','جب کہ ان کی گردنوں میں طوق ہوں گے اور زنجیریں ہوں گی گھسیٹے جائیں گے. ');
INSERT INTO "junagarri" VALUES(40,72,'40:72','کھولتے ہوئے پانی میں اور پھر جہنم کی آگ میں جلائے جائیں گے. ');
INSERT INTO "junagarri" VALUES(40,73,'40:73','پھر ان سے پوچھا جائے گا کہ جنہیں تم شریک کرتے تھے وه کہاں ہیں.');
INSERT INTO "junagarri" VALUES(40,74,'40:74','جو اللہ کے سوا تھے وه کہیں گے کہ وه تو ہم سے بہک گئے بلکہ ہم تو اس سے پہلے کسی کو بھی پکارتے ہی نہ تھے ۔ اللہ تعالیٰ کافروں کو اسی طرح گمراه کرتا ہے. ');
INSERT INTO "junagarri" VALUES(40,75,'40:75','یہ بدلہ ہے اس چیز کا جو تم زمین میں ناحق پھولے نہ سماتے تھے۔ اور (بےجا) اتراتے پھرتے تھے. ');
INSERT INTO "junagarri" VALUES(40,76,'40:76','(اب آؤ) جہنم میں ہمیشہ رہنے کے لیے (اس کے) دروازوں میں داخل ہو جاؤ، کیا ہی بری جگہ ہے تکبر کرنے والے کی. ');
INSERT INTO "junagarri" VALUES(40,77,'40:77','پس آپ صبر کریں اللہ کا وعده قطعاً سچا ہے ، انہیں ہم نے جو وعدے دے رکھے ہیں ان میں سے کچھ ہم آپ کو دکھائیں یا (اس سے پہلے) ہم آپ کو وفات دے دیں، ان کا لوٹایا جانا تو ہماری ہی طرف ہے. ');
INSERT INTO "junagarri" VALUES(40,78,'40:78','یقیناً ہم آپ سے پہلے بھی بہت سے رسول بھیج چکے ہیں جن میں سے بعض کے (واقعات) ہم آپ کو بیان کر چکے ہیں اور ان میں سے بعض کے (قصے) تو ہم نے آپ کو بیان ہی نہیں کیے اور کسی رسول کا یہ (مقدور) نہ تھا کہ کوئی معجزه اللہ کی اجازت کے بغیر لا سکے پھر جس وقت اللہ کا حکم آئےگا حق کے ساتھ فیصلہ کر دیا جائے گا اور اس جگہ اہل باطل خسارے میں ره جائیں گے.');
INSERT INTO "junagarri" VALUES(40,79,'40:79','اللہ وه ہے جس نے تمہارے لیے چوپائے پیدا کیے جن میں سے بعض پر تم سوار ہوتے ہو اور بعض کو تم کھاتے ہو. ');
INSERT INTO "junagarri" VALUES(40,80,'40:80','اور بھی تمہارے لیے ان میں بہت سے نفع ہیں اور تاکہ اپنے سینوں میں چھپی ہوئی حاجتوں کو انہی پر سواری کر کے تم حاصل کر لو اور ان چوپایوں پر اور کشتیوں پر سوار کیے جاتے ہو. ');
INSERT INTO "junagarri" VALUES(40,81,'40:81','اللہ تمہیں اپنی نشانیاں دکھاتا جارہا ہے ، پس تم اللہ کی کن کن نشانیوں کے منکر بنتے رہو گے. ');
INSERT INTO "junagarri" VALUES(40,82,'40:82','کیا انہوں نے زمین میں چل پھر کر اپنے سے پہلوں کا انجام نہیں دیکھا ؟ جو ان سے تعداد میں زیاده تھے قوت میں سخت اور زمین میں بہت ساری یادگاریں چھوڑی تھیں ، ان کے کیے کاموں نے انہیں کچھ بھی فائده نہیں پہنچایا. ');
INSERT INTO "junagarri" VALUES(40,83,'40:83','پس جب بھی ان کے پاس ان کے رسول کھلی نشانیاں لے کر آئے تو یہ اپنے پاس کے علم پر اترانے لگے ، بالآخر جس چیز کو مذاق میں اڑا رہے تھے وه ان پر الٹ پڑی.');
INSERT INTO "junagarri" VALUES(40,84,'40:84','ہمارا عذاب دیکھتے ہی کہنے لگے کہ اللہ واحد پر ہم ایمان لائے اور جن جن کو ہم اس کا شریک بنا رہے تھے ہم نے ان سب سے انکار کیا.');
INSERT INTO "junagarri" VALUES(40,85,'40:85','لیکن ہمارے عذاب کو دیکھ لینے کے بعد ان کے ایمان نے انہیں نفع نہ دیا۔ اللہ نے اپنا معمول یہی مقرر کر رکھا ہے جو اس کے بندوں میں برابر چلا آ رہا ہے ۔ اور اس جگہ کافر خراب وخستہ ہوئے. ');
INSERT INTO "junagarri" VALUES(41,1,'41:1','حٰم.');
INSERT INTO "junagarri" VALUES(41,2,'41:2','اتاری ہوئی ہے بڑے مہربان بہت رحم والے کی طرف سے.*');
INSERT INTO "junagarri" VALUES(41,3,'41:3','(ایسی) کتاب ہے جس کی آیتوں کی واضح تفصیل کی گئی ہے ، (اس حال میں کہ) قرآن عربی زبان میں ہے اس قوم کے لیے جو جانتی ہے. ');
INSERT INTO "junagarri" VALUES(41,4,'41:4','خوش خبری سنانے والا اور ڈرانے والا ہے، پھر بھی ان کی اکثریت نے منھ پھیر لیا اور وه سنتے ہی نہیں. ');
INSERT INTO "junagarri" VALUES(41,5,'41:5','اور انہوں نے کہا کہ تو جس کی طرف ہمیں بلا رہا ہے ہمارے دل تو اس سے پردے میں ہیں اور ہمارے کانوں میں گرانی ہے اور ہم میں اور تجھ میں ایک حجاب ہے، اچھا تو اب اپنا کام کیے جا ہم بھی یقیناً کام کرنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(41,6,'41:6','آپ کہہ دیجیئے! کہ میں تو تم ہی جیسا انسان ہوں مجھ پر وحی نازل کی جاتی ہے کہ تم سب کا معبود ایک اللہ ہی ہے سو تم اس کی طرف متوجہ ہو جاؤ اور اس سے گناہوں کی معافی چاہو، اور ان مشرکوں کے لیے (بڑی ہی) خرابی ہے.');
INSERT INTO "junagarri" VALUES(41,7,'41:7','جو زکوٰة نہیں دیتے اورآخرت کے بھی منکر ہی رہتے ہیں.');
INSERT INTO "junagarri" VALUES(41,8,'41:8','بیشک جو لوگ ایمان لائیں اور بھلے کام کریں ان کے لیے نہ ختم ہونے والا اجر ہے. ');
INSERT INTO "junagarri" VALUES(41,9,'41:9','آپ کہہ دیجئے! کہ کیا تم اس (اللہ) کا انکار کرتے ہو اور تم اس کے شریک مقرر کرتے ہو جس نے دو دن میں زمین پیدا کردی ، سارے جہانوں کا پروردگار وہی ہے.');
INSERT INTO "junagarri" VALUES(41,10,'41:10','اور اس نے زمین میں اس کے اوپر سے پہاڑ گاڑ دیے اوراس میں برکت رکھ دی اوراس میں (رہنے والوں کی) غذاؤں کی تجویز بھی اسی میں کر دی (صرف) چار دن میں ، ضرورت مندوں کے لیے یکساں طور پر. ');
INSERT INTO "junagarri" VALUES(41,11,'41:11','پھر آسمان کی طرف متوجہ ہوا اور وه دھواں (سا) تھا پس اس سے اور زمین سے فرمایا کہ تم دونوں خوشی سے آؤ یا ناخوشی سے دونوں نے عرض کیا ہم بخوشی حاضر ہیں.');
INSERT INTO "junagarri" VALUES(41,12,'41:12','پس دو دن میں سات آسمان بنا دیئے اور ہر آسمان میں اس کے مناسب احکام کی وحی بھیج دی اور ہم نے آسمان دنیا کو چراغوں سے زینت دی اور نگہبانی کی ، یہ تدبیر اللہ غالب و دانا کی ہے.');
INSERT INTO "junagarri" VALUES(41,13,'41:13','اب بھی یہ روگرداں ہوں تو کہہ دیجئے! کہ میں تمہیں اس کڑک (عذاب آسمانی) سے ڈراتا ہوں جو مثل عادیوں اور ثمودیوں کی کڑک ہوگی.');
INSERT INTO "junagarri" VALUES(41,14,'41:14','ان کے پاس جب ان کے آگے پیچھے سے پیغمبر آئے کہ تم اللہ کے سوا کسی کی عبادت نہ کرو تو انہوں نے جواب دیا کہ اگر ہمارا پروردگار چاہتا تو فرشتوں کو بھیجتا۔ ہم تو تمہاری رسالت کے بالکل منکر ہیں. ');
INSERT INTO "junagarri" VALUES(41,15,'41:15','اب عاد نے تو بے وجہ زمین میں سرکشی شروع کر دی اور کہنے لگے کہ ہم سے زور آور کون ہے ؟ کیا انہیں یہ نظر نہ آیا کہ جس نے انہیں پیدا کیا ہے وه ان سے (بہت ہی) زیاده زور آور ہے ، وه (آخر تک) ہماری آیتوں کا انکار ہی کرتے رہے.');
INSERT INTO "junagarri" VALUES(41,16,'41:16','بالآخر ہم نے ان پر ایک تیز و تند آندھی منحوس دنوں میں بھیج دی کہ انہیں دنیاوی زندگی میں ذلت کے عذاب کامزه چکھا دیں، اور (یقین مانو) کہ آخرت کا عذاب اس سے بہت زیاده رسوائی والا ہے اور وه مدد نہیں کیے جائیں گے.');
INSERT INTO "junagarri" VALUES(41,17,'41:17','رہے ثمود، سو ہم نے ان کی بھی رہبری کی پھر بھی انہوں نے ہدایت پر اندھے پن کو ترجیح دی جس بنا پر انہیں (سراپا) ذلت کے عذاب، کی کڑک نے ان کے کرتوتوں کے باعث پکڑ لیا. ');
INSERT INTO "junagarri" VALUES(41,18,'41:18','اور (ہاں) ایماندار اور پارساؤں کو ہم نے (بال بال) بچا لیا.');
INSERT INTO "junagarri" VALUES(41,19,'41:19','اور جس دن اللہ کے دشمن دوزخ کی طرف لائے جائیں گے اور ان (سب) کو جمع کر دیا جائے گا. ');
INSERT INTO "junagarri" VALUES(41,20,'41:20','یہاں تک کہ جب بالکل جہنم کےپاس آجائیں گے اور ان پر ان کے کان اور ان کی آنکھیں اور ان کی کھالیں ان کے اعمال کی گواہی دیں گی. ');
INSERT INTO "junagarri" VALUES(41,21,'41:21','یہ اپنی کھالوں سے کہیں گے کہ تم نے ہمارے خلاف شہادت کیوں دی ، وه جواب دیں گی کہ ہمیں اس اللہ نے قوت گویائی عطا فرمائی جس نے ہر چیز کو بولنے کی طاقت بخشی ہے، اسی نے تمہیں اول مرتبہ پیدا کیا اور اسی کی طرف تم سب لوٹائے جاؤ گے. ');
INSERT INTO "junagarri" VALUES(41,22,'41:22','اور تم (اپنی بداعمالیاں) اس وجہ سے پوشیده رکھتے ہی نہ تھے کہ تم پر تمہارے کان اور تمہاری آنکھیں اور تمہاری کھالیں گواہی دیں گی ، ہاں تم یہ سمجھتے رہے کہ تم جو کچھ بھی کر رہے ہو اس میں سے بہت سےاعمال سے اللہ بےخبر ہے. ');
INSERT INTO "junagarri" VALUES(41,23,'41:23','تمہاری اسی بدگمانی نےجو تم نے اپنے رب سے کر رکھی تھی تمہیں ہلاک کر دیا اور بالآخر تم زیاں کاروں میں ہو گئے.');
INSERT INTO "junagarri" VALUES(41,24,'41:24','اب اگر یہ صبر کریں تو بھی ان کا ٹھکانا جہنم ہی ہے۔ اور اگر یہ (عذر اور) معافی کےخواستگار ہوں تو بھی (معذور اور) معاف نہیں رکھے جائیں گے. ');
INSERT INTO "junagarri" VALUES(41,25,'41:25','اور ہم نے ان کے کچھ ہم نشیں مقرر کر رکھے تھے، جنہوں نے ان کے اگلے پچھلے اعمال ان کی نگاہوں میں خوبصورت بنا رکھے تھے اور ان کے حق میں بھی اللہ کا قول ان امتوں کے ساتھ پورا ہوا جو ان سے پہلے جنوں انسانوں کی گزر چکی ہیں۔ یقیناً وه زیاں کار ثابت ہوئے.');
INSERT INTO "junagarri" VALUES(41,26,'41:26','اور کافروں نے کہا اس قرآن کو سنو ہی مت (اس کے پڑھے جانے کے وقت) اور بیہوده گوئی کرو کیا عجب کہ تم غالب آجاؤ. ');
INSERT INTO "junagarri" VALUES(41,27,'41:27','پس یقیناً ہم ان کافروں کو سخت عذاب کا مزه چکھائیں گے۔ اور انہیں ان کے بدترین اعمال کا بدلہ (ضرور) ضرور دیں گے. ');
INSERT INTO "junagarri" VALUES(41,28,'41:28','اللہ کے دشمنوں کی سزا یہی دوزح کی آگ ہے جس میں ان کا ہمیشگی کا گھر ہے (یہ) بدلہ ہے ہماری آیتوں سے انکار کرنے کا. ');
INSERT INTO "junagarri" VALUES(41,29,'41:29','اور کافر لوگ کہیں گے اے ہمارے رب! ہمیں جنوں انسانوں (کے وه دونوں فریق) دکھا جنہوں نے ہمیں گمراه کیا (تاکہ) ہم انہیں اپنے قدموں تلے ڈال دیں تاکہ وه جہنم میں سب سے نیچے (سخت عذاب میں) ہو جائیں. ');
INSERT INTO "junagarri" VALUES(41,30,'41:30','(واقعی) جن لوگوں نے کہا کہ ہمارا پروردگار اللہ ہے پھر اسی پر قائم رہے ان کے پاس فرشتے (یہ کہتے ہوئے آتے ہیں کہ تم کچھ بھی اندیشہ اور غم نہ کرو (بلکہ) اس جنت کی بشارت سن لو جس کا تم وعده دیئے گئے ہو. ');
INSERT INTO "junagarri" VALUES(41,31,'41:31','تمہاری دنیوی زندگی میں بھی ہم تمہارے رفیق تھے اور آخرت میں بھی رہیں گے ، جس چیز کو تمہارا جی چاہے اور جو کچھ تم مانگو سب تمہارے لیے (جنت میں موجود﴾ ہے.');
INSERT INTO "junagarri" VALUES(41,32,'41:32','غفور و رحیم (معبود) کی طرف سے یہ سب کچھ بطور مہمانی کے ہے.');
INSERT INTO "junagarri" VALUES(41,33,'41:33','اور اس سے زیاده اچھی بات والا کون ہے جو اللہ کی طرف بلائے اور نیک کام کرے اور کہے کہ میں یقیناً مسلمانوں میں سے ہوں. ');
INSERT INTO "junagarri" VALUES(41,34,'41:34','نیکی اور بدی برابر نہیں ہوتی ۔ برائی کو بھلائی سے دفع کرو پھر وہی جس کے اور تمہارے درمیان دشمنی ہے ایسا ہو جائے گا جیسے دلی دوست. ');
INSERT INTO "junagarri" VALUES(41,35,'41:35','اور یہ بات انہیں کو نصیب ہوتی ہے جو صبر کریں اور اسے سوائے بڑے نصیبے والوں کے کوئی نہیں پا سکتا. ');
INSERT INTO "junagarri" VALUES(41,36,'41:36','اور اگر شیطان کی طرف سے کوئی وسوسہ آئے تو اللہ کی پناه طلب کرو یقیناً وه بہت ہی سننے والا اور جاننے والا ہے. ');
INSERT INTO "junagarri" VALUES(41,37,'41:37','اور دن رات اور سورج چاند بھی (اسی کی) نشانیوں میں سے ہیں ، تم سورج کو سجده نہ کرو نہ چاند کو بلکہ سجده اس اللہ کے لیے کرو جس نے ان سب کو پیدا کیا ہے ، اگر تمہیں اس کی عبادت کرنی ہے تو.');
INSERT INTO "junagarri" VALUES(41,38,'41:38','پھر بھی اگر یہ کبر و غرور کریں تو وه (فرشتے) جو آپ کے رب کے نزدیک ہیں وه تو رات دن اس کی تسبیح بیان کر رہے ہیں اور (کسی وقت بھی) نہیں اکتاتے.');
INSERT INTO "junagarri" VALUES(41,39,'41:39','اس اللہ کی نشانیوں میں سے (یہ بھی) ہے کہ تو زمین کو دبی دبائی دیکھتا ہے پھر جب ہم اس پر مینہ برساتے ہیں تو وه تر وتازه ہوکر ابھرنے لگتی ہے جس نے اسے زنده کیا وہی یقینی طور پر مُردوں کو بھی زنده کرنے والا ہے ، بیشک وه ہر (ہر) چیز پر قادر ہے.');
INSERT INTO "junagarri" VALUES(41,40,'41:40','بیشک جو لوگ ہماری آیتوں میں کج روی کرتے ہیں وه (کچھ) ہم سے مخفی نہیں ، (بتلاؤ تو) جو آگ میں ڈالا جائے وه اچھا ہے یا وه جو امن و امان کے ساتھ قیامت کے دن آئے ؟ تم جو چاہو کرتے چلے جاؤ وه تمہارا سب کیا کرایا دیکھ رہا ہے.');
INSERT INTO "junagarri" VALUES(41,41,'41:41','جن لوگوں نے اپنے پاس قرآن پہنچ جانے کے باوجود اس سے کفر کیا، (وه بھی ہم سے پوشیده نہیں) یہ بڑی باوقعت کتاب ہے. ');
INSERT INTO "junagarri" VALUES(41,42,'41:42','جس کے پاس باطل پھٹک بھی نہیں سکتا نہ اس کے آگے سے نہ اس کے پیچھے سے، یہ ہے نازل کرده حکمتوں والے خوبیوں والے (اللہ) کی طرف سے. ');
INSERT INTO "junagarri" VALUES(41,43,'41:43','آپ سے وہی کہا جاتا ہے جو آپ سے پہلے کے رسولوں سے بھی کہا گیا ہے ، یقیناً آپ کا رب معافی والا اور دردناک عذاب والا ہے. ');
INSERT INTO "junagarri" VALUES(41,44,'41:44','اور اگر ہم اسے عجمی زبان کا قرآن بناتے تو کہتے کہ اس کی آیتیں صاف صاف بیان کیوں نہیں کی گئیں ؟ یہ کیا کہ عجمی کتاب اور آپ عربی رسول ؟ آپ کہہ دیجئے! کہ یہ تو ایمان والوں کے لیے ہدایت و شفا ہے اور جو ایمان نہیں لاتے ان کے کانوں میں تو (بہراپن اور) بوجھ ہے اور یہ ان پر اندھاپن ہے، یہ وه لوگ ہیں جو کسی بہت دور دراز جگہ سے پکارے جا رہے ہیں. ');
INSERT INTO "junagarri" VALUES(41,45,'41:45','یقیناً ہم نے موسیٰ (علیہ السلام) کو کتاب دی تھی، سو اس میں بھی اختلاف کیا گیا اور اگر (وه) بات نہ ہوتی (جو) آپ کے رب کی طرف سے پہلے ہی مقرر ہو چکی ہے ۔ توان کے درمیان (کبھی کا) فیصلہ ہو چکا ہوتا ، یہ لوگ تو اس کے بارے میں سخت بےچین کرنے والے شک میں ہیں. ');
INSERT INTO "junagarri" VALUES(41,46,'41:46','جو شخص نیک کام کرے گا وه اپنے نفع کے لیے اور جو برا کام کرے گا اس کا وبال اسی پر ہے۔ اور آپ کا رب بندوں پر ظلم کرنے والا نہیں. ');
INSERT INTO "junagarri" VALUES(41,47,'41:47','قیامت کا علم اللہ ہی کی طرف لوٹایا جاتا ہے اور جو جو پھل اپنے شگوفوں میں سے نکلتے ہیں اور جو ماده حمل سے ہوتی ہے اور جو بچے وه جنتی ہے سب کا علم اسے ہے اور جس دن اللہ تعالیٰ ان (مشرکوں) کو بلا کر دریافت فرمائے گا میرے شریک کہاں ہیں، وه جواب دیں گے کہ ہم نے تو تجھے کہہ سنایا کہ ہم میں سے تو کوئی اس کا گواه نہیں. ');
INSERT INTO "junagarri" VALUES(41,48,'41:48','اور یہ جن (جن) کی پرستش اس سے پہلے کرتے تھے وه ان کی نگاه سے گم ہوگئے اور انہوں نے سمجھ لیا کہ اب ان کے لیے کوئی بچاؤ نہیں. ');
INSERT INTO "junagarri" VALUES(41,49,'41:49','بھلائی کے مانگنے سے انسان تھکتا نہیں اور اگر اسے کوئی تکلیف پہنچ جائے تو مایوس اور ناامید ہو جاتا ہے. ');
INSERT INTO "junagarri" VALUES(41,50,'41:50','اور جو مصیبت اسے پہنچ چکی ہے اس کے بعد اگر ہم اسے کسی رحمت کا مزه چکھائیں تو وه کہہ اٹھتا ہے کہ اس کا تو میں حقدار ہی تھا اور میں تو خیال نہیں کرسکتا کہ قیامت قائم ہوگی اور اگر میں اپنے رب کے پاس واپس کیا گیا تو بھی یقیناً میرے لیے اس کے پاس بھی بہتری ہے، یقیناً ہم ان کفار کو ان کے اعمال سے خبردار کریں گے اور انہیں سخت عذاب کا مزه چکھائیں گے.');
INSERT INTO "junagarri" VALUES(41,51,'41:51','اور جب ہم انسان پر اپنا انعام کرتے ہیں تو وه منھ پھیر لیتا ہے اور کناره کش ہوجاتا ہے اور جب اسے مصیبت پڑتی ہے تو بڑی لمبی چوڑی دعائیں کرنے والا بن جاتا ہے. ');
INSERT INTO "junagarri" VALUES(41,52,'41:52','آپ کہہ دیجئے! کہ بھلا یہ تو بتاؤ کہ اگر یہ قرآن اللہ کی طرف سے آیا ہوا ہو پھر تم نے اسے نہ مانا بس اس سے بڑھ کر بہکا ہوا کون ہوگا جو مخالفت میں (حق سے) دور چلا جائے. ');
INSERT INTO "junagarri" VALUES(41,53,'41:53','عنقریب ہم انہیں اپنی نشانیاں آفاق عالم میں بھی دکھائیں گے اور خود ان کی اپنی ذات میں بھی یہاں تک کہ ان پر کھل جائے کہ حق یہی ہے ، کیا آپ کے رب کا ہر چیز سے واقف و آگاه ہونا کافی نہیں. ');
INSERT INTO "junagarri" VALUES(41,54,'41:54','یقین جانو! کہ یہ لوگ اپنے رب کے روبرو جانے سے شک میں ہیں ، یاد رکھو کہ اللہ تعالیٰ ہر چیز کا احاطہ کیے ہوئے ہے. ');
INSERT INTO "junagarri" VALUES(42,1,'42:1','حٰم.');
INSERT INTO "junagarri" VALUES(42,2,'42:2','عسق.');
INSERT INTO "junagarri" VALUES(42,3,'42:3','اللہ تعالیٰ جو زبردست ہے اور حکمت والا ہے اسی طرح تیری طرف اور تجھ سے اگلوں کی طرف وحی بھیجتا رہا. ');
INSERT INTO "junagarri" VALUES(42,4,'42:4','آسمانوں کی (تمام) چیزیں اور جو کچھ زمین میں ہے سب اسی کا ہے وه برتر اور عظیم الشان ہے.');
INSERT INTO "junagarri" VALUES(42,5,'42:5','قریب ہے آسمان اوپر سے پھٹ پڑیں اور تمام فرشتے اپنے رب کی پاکی تعریف کے ساتھ بیان کر رہے ہیں اور زمین والوں کے لیے استغفار کر رہے ہیں ۔ خوب سمجھ رکھو کہ اللہ تعالیٰ ہی معاف فرمانے والا رحمت والا ہے. ');
INSERT INTO "junagarri" VALUES(42,6,'42:6','اور جن لوگوں نے اس کے سوا دوسروں کو کارساز بنالیا ہے اللہ تعالیٰ ان پر نگران ہے اور آپ ان کے ذمہ دار نہیں ہیں. ');
INSERT INTO "junagarri" VALUES(42,7,'42:7','اسی طرح ہم نے آپ کی طرف عربی قرآن کی وحی کی ہے تاکہ آپ مکہ والوں کو اور اس کے آس پاس کے لوگوں کو خبردار کردیں اور جمع ہونے کے دن سے جس کے آنے میں کوئی شک نہیں ڈرا دیں۔ ایک گروه جنت میں ہوگا اور ایک گروه جہنم میں ہوگا. ');
INSERT INTO "junagarri" VALUES(42,8,'42:8','اگر اللہ تعالیٰ چاہتا تو ان سب کو ایک ہی امت کا بنا دیتا لیکن وه جسے چاہتا ہے اپنی رحمت میں داخل کرلیتا ہے اور ظالموں کا حامی اور مددگار کوئی نہیں.');
INSERT INTO "junagarri" VALUES(42,9,'42:9','کیا ان لوگوں نے اللہ تعالیٰ کے سوا اور کارساز بنالیے ہیں، (حقیقتاً تو) اللہ تعالیٰ ہی کارساز ہے وہی مُردوں کو زنده کرے گا اور وہی ہر چیز پر قادر ہے. ');
INSERT INTO "junagarri" VALUES(42,10,'42:10','اور جس جس چیز میں تمہارا اختلاف ہو اس کا فیصلہ اللہ تعالیٰ ہی کی طرف ہے ، یہی اللہ میرا رب ہے جس پر میں نے بھروسہ کر رکھا ہے اور جس کی طرف میں جھکتا ہوں.');
INSERT INTO "junagarri" VALUES(42,11,'42:11','وه آسمانوں اور زمین کا پیدا کرنے والا ہے اس نے تمہارے لیے تمہاری جنس کے جوڑے بنادیئے ہیں اور چوپایوں کے جوڑے بنائے ہیں تمہیں وه اس میں پھیلا رہا ہے اس جیسی کوئی چیز نہیں وه سننے اور دیکھنے والا ہے.');
INSERT INTO "junagarri" VALUES(42,12,'42:12','آسمانوں اور زمین کی کنجیاں اسی کی ہیں ، جس کی چاہے روزی کشاده کردے اور تنگ کردے، یقیناً وه ہر چیز کو جاننے والا ہے.');
INSERT INTO "junagarri" VALUES(42,13,'42:13','اللہ تعالیٰ نے تمہارے لیے وہی دین مقرر کردیا ہے جس کے قائم کرنے کا اس نے نوح (علیہ السلام) کو حکم دیا تھا اور جو (بذریعہ وحی) ہم نے تیری طرف بھیج دی ہے، اور جس کا تاکیدی حکم ہم نے ابراہیم اور موسیٰ اور عیسیٰ (علیہم السلام) کو دیا تھا ، کہ اس دین کو قائم رکھنا اور اس میں پھوٹ نہ ڈالنا جس چیز کی طرف آپ انہیں بلا رہے ہیں وه تو (ان) مشرکین پر گراں گزرتی ہے ، اللہ تعالیٰ جسے چاہتا ہے اپنا برگزیده بناتا ہے اور جو بھی اس کی طرف رجوع کرے وه اس کی صحیح ره نمائی کرتا ہے. ');
INSERT INTO "junagarri" VALUES(42,14,'42:14','ان لوگوں نے اپنے پاس علم آجانے کے بعد ہی اختلاف کیا (اور وه بھی) باہمی ضد بحث سے اور اگر آپ کے رب کی بات ایک وقت مقرر تک کے لیے پہلے ہی سے قرار پا گئی ہوئی نہ ہوتی تو یقیناً ان کا فیصلہ ہوچکا ہوتا اور جن لوگوں کو ان کے بعد کتاب دی گئی ہے وه بھی اس کی طرف سے الجھن والے شک میں پڑے ہوئے ہیں. ');
INSERT INTO "junagarri" VALUES(42,15,'42:15','پس آپ لوگوں کو اسی طرف بلاتے رہیں اور جو کچھ آپ سے کہا گیا ہے اس پر مضبوطی سے جم جائیں اور ان کی خواہشوں پر نہ چلیں اور کہہ دیں کہ اللہ تعالیٰ نے جتنی کتابیں نازل فرمائی ہیں میرا ان پر ایمان ہے اور مجھے حکم دیا گیا ہے کہ تم میں انصاف کرتا رہوں ۔ ہمارا اور تم سب کا پروردگار اللہ ہی ہے ہمارے اعمال ہمارے لیے ہیں اور تمہارے اعمال تمہارے لیے ہیں، ہم تم میں کوئی کٹ حجتی نہیں اللہ تعالیٰ ہم (سب) کو جمع کرے گا اور اسی کی طرف لوٹنا ہے.');
INSERT INTO "junagarri" VALUES(42,16,'42:16','اور جو لوگ اللہ تعالیٰ کی باتوں میں جھگڑا ڈالتے ہیں اس کے بعد کہ (مخلوق) اسے مان چکی ان کی کٹ حجتی اللہ کے نزدیک باطل ہے ، اور ان پر غضب ہے اور ان کے لیے سخت عذاب ہے.');
INSERT INTO "junagarri" VALUES(42,17,'42:17','اللہ تعالیٰ نے حق کے ساتھ کتاب نازل فرمائی ہے اور ترازو بھی (اتاری ہے) اور آپ کو کیا خبر شاید قیامت قریب ہی ہو.');
INSERT INTO "junagarri" VALUES(42,18,'42:18','اس کی جلدی انہیں پڑی ہے جو اسے نہیں مانتے اور جو اس پر یقین رکھتے ہیں وه تو اس سے ڈر رہے ہیں انہیں اس کے حق ہونے کا پورا علم ہے۔ یاد رکھو جو لوگ قیامت کے معاملہ میں لڑ جھگڑ رہے ہیں ، وه دور کی گمراہی میں پڑے ہوئے ہیں. ');
INSERT INTO "junagarri" VALUES(42,19,'42:19','اللہ تعالیٰ اپنے بندوں پر بڑا ہی لطف کرنے والا ہے، جسے چاہتا ہے کشاده روزی دیتا ہے اور وه بڑی طاقت، بڑے غلبہ والا ہے.');
INSERT INTO "junagarri" VALUES(42,20,'42:20','جس کا اراده آخرت کی کھیتی کا ہو ہم اسے اس کی کھیتی میں ترقی دیں گے اور جو دنیا کی کھیتی کی طلب رکھتا ہو ہم اسے اس میں سے ہی کچھ دے دیں گے ، ایسے شخص کا آخرت میں کوئی حصہ نہیں. ');
INSERT INTO "junagarri" VALUES(42,21,'42:21','کیا ان لوگوں نے ایسے (اللہ کے) شریک (مقرر کر رکھے) ہیں جنہوں نے ایسے احکام دین مقرر کر دیئے ہیں جو اللہ کے فرمائے ہوئے نہیں ہیں ۔ اگر فیصلے کے دن کا وعده نہ ہوتا تو (ابھی ہی) ان میں فیصلہ کردیا جاتا۔ یقیناً (ان) ظالموں کے لیے ہی دردناک عذاب ہے.');
INSERT INTO "junagarri" VALUES(42,22,'42:22','آپ دیکھیں گے کہ یہ ظالم اپنے اعمال سے ڈر رہے ہوں گے جن کے وبال ان پر واقع ہونے والے ہیں ، اور جو لوگ ایمان لائے اور انہوں نے نیک اعمال کیے وه بہشتوں کے باغات میں ہوں گے وه جو خواہش کریں اپنے رب کے پاس موجود پائیں گے یہی ہے بڑا فضل.');
INSERT INTO "junagarri" VALUES(42,23,'42:23','یہی وه ہے جس کی بشارت اللہ تعالیٰ اپنے ان بندوں کو دے رہا ہے جو ایمان لائے اور (سنت کے مطابق) نیک عمل کیے تو کہہ دیجئے! کہ میں اس پر تم سے کوئی بدلہ نہیں چاہتا مگر محبت رشتہ داری کی ، جو شخص کوئی نیکی کرے ہم اس کے لیے اس کی نیکی میں اور نیکی بڑھا دیں گے ۔ بیشک اللہ تعالیٰ بخشنے والا (اور) بہت قدر دان ہے. ');
INSERT INTO "junagarri" VALUES(42,24,'42:24','کیا یہ کہتے ہیں کہ (پیغمبر نے) اللہ پر جھوٹ باندھا ہے، اگر اللہ تعالیٰ چاہے تو آپ کے دل پر مہر لگادے اور اللہ تعالیٰ اپنی باتوں سے جھوٹ کو مٹا دیتا ہے اور سچ کو ثابت رکھتا ہے۔ وه سینے کی باتوں کو جاننے والا ہے.');
INSERT INTO "junagarri" VALUES(42,25,'42:25','وہی ہے جو اپنے بندوں کی توبہ قبول فرماتا ہے اور گناہوں سے درگزر فرماتا ہے اور جو کچھ تم کررہے ہو (سب) جانتا ہے.');
INSERT INTO "junagarri" VALUES(42,26,'42:26','ایمان والوں اور نیکوکار لوگوں کی سنتا ہے اور انہیں اپنے فضل سے اور بڑھا کر دیتا ہے اور کفار کے لیے سخت عذاب ہے.');
INSERT INTO "junagarri" VALUES(42,27,'42:27','اگر اللہ تعالیٰ اپنے (سب) بندوں کی روزی فراخ کر دیتا تو وه زمین میں فساد برپا کردیتے لیکن وه اندازے کے ساتھ جو کچھ چاہتا ہے نازل فرماتا ہے۔ وه اپنے بندوں سے پورا خبردار ہے اور خوب دیکھنے والا ہے.');
INSERT INTO "junagarri" VALUES(42,28,'42:28','اور وہی ہے جو لوگوں کے ناامید ہوجانے کے بعد بارش برساتا اور اپنی رحمت پھیلا دیتا ہے وہی ہے کارساز اور قابل حمد و ثنا. ');
INSERT INTO "junagarri" VALUES(42,29,'42:29','اور اس کی نشانیوں میں سے آسمانوں اور زمین کی پیدائش ہے اور ان میں جانداروں کا پھیلانا ہے۔ وه اس پر بھی قادر ہے کہ جب چاہے انہیں جمع کردے. ');
INSERT INTO "junagarri" VALUES(42,30,'42:30','تمہیں جو کچھ مصیبتیں پہنچتی ہیں وه تمہارے اپنے ہاتھوں کے کرتوت کابدلہ ہے، اور وه تو بہت سی باتوں سے درگزر فرما دیتا ہے. ');
INSERT INTO "junagarri" VALUES(42,31,'42:31','اور تم ہمیں زمین میں عاجز کرنے والے نہیں ہو ، تمہارے لیے سوائے اللہ تعالیٰ کے نہ کوئی کارساز ہے نہ مددگار.');
INSERT INTO "junagarri" VALUES(42,32,'42:32','اور دریا میں چلنے والی پہاڑوں جیسی کشتیاں اس کی نشانیوں میں سے ہیں. ');
INSERT INTO "junagarri" VALUES(42,33,'42:33','اگر وه چاہے تو ہوا بند کردے اور یہ کشتیاں سمندروں پر رکی ره جائیں۔ یقیناً اس میں ہر صبر کرنے والے شکرگزار کے لیے نشانیاں ہیں.');
INSERT INTO "junagarri" VALUES(42,34,'42:34','یا انہیں ان کے کرتوتوں کے باعث تباه کردے ، وه تو بہت سے خطاؤں سے درگزر فرمایا کرتا ہے. ');
INSERT INTO "junagarri" VALUES(42,35,'42:35','اور تاکہ جو لوگ ہماری نشانیوں میں جھگڑتے ہیں وه معلوم کرلیں کہ ان کے لیے کوئی چھٹکارا نہیں. ');
INSERT INTO "junagarri" VALUES(42,36,'42:36','تو تمہیں جو کچھ دیا گیا ہے وه زندگانی دنیا کا کچھ یونہی سا اسباب ہے ، اور اللہ کے پاس جو ہے وه اس سے بدرجہ بہتر اور پائیدار ہے، وه ان کے لیے ہے جو ایمان لائے اور صرف اپنے رب ہی پر بھروسہ رکھتے ہیں.');
INSERT INTO "junagarri" VALUES(42,37,'42:37','اور کبیره گناہوں سے اور بے حیائیوں سے بچتے ہیں اور غصے کے وقت (بھی) معاف کردیتے ہیں. ');
INSERT INTO "junagarri" VALUES(42,38,'42:38','اور اپنے رب کے فرمان کو قبول کرتے ہیں اور نماز کی پابندی کرتے ہیں اور ان کا (ہر) کام آپس کے مشورے سے ہوتا ہے ، اور جو ہم نے انہیں دے رکھا ہے اس میں سے (ہمارے نام پر) دیتے ہیں.');
INSERT INTO "junagarri" VALUES(42,39,'42:39','اور جب ان پر ظلم (و زیادتی) ہو تو وه صرف بدلہ لے لیتے ہیں. ');
INSERT INTO "junagarri" VALUES(42,40,'42:40','اور برائی کا بدلہ اسی جیسی برائی ہے ، اور جو معاف کر دے اور اصلاح کرلے اس کا اجر اللہ کے ذمے ہے، (فی الواقع) اللہ تعالیٰ ظالموں سے محبت نہیں کرتا.');
INSERT INTO "junagarri" VALUES(42,41,'42:41','اور جو شخص اپنے مظلوم ہونے کے بعد (برابر کا) بدلہ لے لے تو ایسے لوگوں پر (الزام کا) کوئی راستہ نہیں.');
INSERT INTO "junagarri" VALUES(42,42,'42:42','یہ راستہ صرف ان لوگوں پر ہے جو خود دوسروں پر ظلم کریں اور زمین میں ناحق فساد کرتے پھریں، یہی لوگ ہیں جن کے لیے دردناک عذاب ہے.');
INSERT INTO "junagarri" VALUES(42,43,'42:43','اور جو شخص صبر کر لے اور معاف کردے یقیناً یہ بڑی ہمت کے کاموں میں سے (ایک کام) ہے.');
INSERT INTO "junagarri" VALUES(42,44,'42:44','اور جسے اللہ تعالیٰ بہکا دے اس کا اس کے بعد کوئی چاره ساز نہیں، اور تو دیکھے گا کہ ظالم لوگ عذاب کو دیکھ کر کہہ رہے ہوں گے کہ کیا واپس جانے کی کوئی راه ہے.');
INSERT INTO "junagarri" VALUES(42,45,'42:45','اور تو انہیں دیکھے گا کہ وه (جہنم کے) سامنے لاکھڑے کیے جائیں گے مارے ذلت کے جھکے جارہے ہوں گے اور کنکھیوں سے دیکھ رہے ہوں گے، ایمان والے صاف کہیں گے کہ حقیقی زیاںکار وه ہیں جنہوں نے آج قیامت کے دن اپنے آپ کو اور اپنے گھر والوں کو نقصان میں ڈال دیا۔ یاد رکھو کہ یقیناً ظالم لوگ دائمی عذاب میں ہیں. ');
INSERT INTO "junagarri" VALUES(42,46,'42:46','ان کے کوئی مددگار نہیں جو اللہ تعالیٰ سے الگ ان کی امداد کرسکیں اور جسے اللہ گمراه کردے اس کے لیے کوئی راستہ ہی نہیں.');
INSERT INTO "junagarri" VALUES(42,47,'42:47','اپنے رب کا حکم مان لو اس سے پہلے کہ اللہ کی جانب سے وه دن آجائے جس کا ہٹ جانا ناممکن ہے، تمہیں اس روز نہ تو کوئی پناه کی جگہ ملے گی نہ چھپ کر انجان بن جانے کی. ');
INSERT INTO "junagarri" VALUES(42,48,'42:48','اگر یہ منھ پھیرلیں تو ہم نے آپ کو ان پر نگہبان بنا کر نہیں بھیجا، آپ کے ذمہ تو صرف پیغام پہنچا دینا ہے ، ہم جب کبھی انسان کو اپنی مہربانی کا مزه چکھاتے ہیں تو وه اس پر اترا جاتا ہے اور اگر انہیں ان کے اعمال کی وجہ سے کوئی مصیبت پہنچتی ہے تو بےشک انسان بڑا ہی ناشکرا ہے. ');
INSERT INTO "junagarri" VALUES(42,49,'42:49','آسمانوں کی اور زمین کی سلطنت اللہ تعالیٰ ہی کے لیے ہے، وه جو چاہتا ہے پیدا کرتا ہے جس کو چاہتا ہے بیٹیاں دیتا ہے اور جسے چاہتا ہے بیٹے دیتا ہے.');
INSERT INTO "junagarri" VALUES(42,50,'42:50','یا انہیں جمع کردیتا ہے بیٹے بھی اور بیٹیاں بھی اور جسے چاہے بانجھ کر دیتا ہے، وه بڑے علم والا اور کامل قدرت والا ہے.');
INSERT INTO "junagarri" VALUES(42,51,'42:51','ناممکن ہے کہ کسی بنده سے اللہ تعالیٰ کلام کرے مگر وحی کے ذریعہ یا پردے کے پیچھے سے یا کسی فرشتہ کو بھیجے اور وه اللہ کے حکم سے جو وه چاہے وحی کرے، بیشک وه برتر ہے حکمت والا ہے.');
INSERT INTO "junagarri" VALUES(42,52,'42:52','اور اسی طرح ہم نے آپ کی طرف اپنے حکم سے روح کو اتارا ہے ، آپ اس سے پہلے یہ بھی نہیں جانتے تھے کہ کتاب اور ایمان کیا چیز ہے ؟ لیکن ہم نے اسے نور بنایا، اس کے ذریعہ سے اپنے بندوں میں سے جسے چاہتے ہیں، ہدایت دیتے ہیں ، بیشک آپ راه راست کی رہنمائی کر رہے ہیں.');
INSERT INTO "junagarri" VALUES(42,53,'42:53','اس اللہ کی راه کی جس کی ملکیت میں آسمانوں اور زمین کی ہر چیز ہے۔ آگاه رہو سب کام اللہ تعالیٰ ہی کی طرف لوٹتے ہیں. ');
INSERT INTO "junagarri" VALUES(43,1,'43:1','حٰم.');
INSERT INTO "junagarri" VALUES(43,2,'43:2','قسم ہے اس واضح کتاب کی.');
INSERT INTO "junagarri" VALUES(43,3,'43:3','ہم نے اس کو عربی زبان کا قرآن بنایا ہے کہ تم سمجھ لو.');
INSERT INTO "junagarri" VALUES(43,4,'43:4','یقیناً یہ لوح محفوظ میں ہے اور ہمارے نزدیک بلند مرتبہ حکمت والی ہے.');
INSERT INTO "junagarri" VALUES(43,5,'43:5','کیا ہم اس نصیحت کو تم سے اس بنا پر ہٹالیں کہ تم حد سے گزر جانے والے لوگ ہو. ');
INSERT INTO "junagarri" VALUES(43,6,'43:6','اور ہم نے اگلے لوگوں میں بھی کتنے ہی نبی بھیجے.');
INSERT INTO "junagarri" VALUES(43,7,'43:7','جو نبی ان کے پاس آیا انہوں نے اس کا مذاق اڑایا.');
INSERT INTO "junagarri" VALUES(43,8,'43:8','پس ہم نے ان سے زیاده زور آوروں کو تباه کر ڈالا اور اگلوں کی مثال گزر چکی ہے. ');
INSERT INTO "junagarri" VALUES(43,9,'43:9','اگر آپ ان سے دریافت کریں کہ آسمانوں اور زمین کو کس نے پیدا کیا تو یقیناً ان کا جواب یہی ہوگا کہ انہیں غالب و دانا (اللہ) نے ہی پیدا کیا ہے.');
INSERT INTO "junagarri" VALUES(43,10,'43:10','وہی ہے جس نے تمہارے لیے زمین کو فرش (بچھونا) بنایا اور اس میں تمہارے لیے راستے کردیے تاکہ تم راه پالیا کرو. ');
INSERT INTO "junagarri" VALUES(43,11,'43:11','اسی نے آسمان سے ایک اندازے کے مطابق پانی نازل فرمایا، پس ہم نے اس سے مرده شہر کو زنده کردیا۔ اسی طرح تم نکالے جاؤ گے. ');
INSERT INTO "junagarri" VALUES(43,12,'43:12','جس نے تمام چیزوں کے جوڑے بنائے اور تمہارے لیے کشتیاں بنائیں اور چوپائے جانور (پیدا کیے) جن پر تم سوار ہوتے ہو.');
INSERT INTO "junagarri" VALUES(43,13,'43:13','تاکہ تم ان کی پیٹھ پر جم کر سوار ہوا کرو پھر اپنے رب کی نعمت کو یاد کرو جب اس پر ٹھیک ٹھاک بیٹھ جاؤ، اور کہو پاک ذات ہے اس کی جس نے اسے ہمارے بس میں کردیا حالانکہ ہمیں اسے قابو کرنے کی طاقت نہ تھی.');
INSERT INTO "junagarri" VALUES(43,14,'43:14','اور بالیقین ہم اپنے رب کی طرف لوٹ کر جانے والے ہیں. ');
INSERT INTO "junagarri" VALUES(43,15,'43:15','اور انہوں نے اللہ کے بعض بندوں کو اس کا جز ٹھہرا دیا یقیناً انسان کھلم کھلا ناشکرا ہے.');
INSERT INTO "junagarri" VALUES(43,16,'43:16','کیا اللہ تعالیٰ نے اپنی مخلوق میں سے بیٹیاں تو خود رکھ لیں اور تمہیں بیٹوں سے نوازا.*');
INSERT INTO "junagarri" VALUES(43,17,'43:17','(حالانکہ) ان میں سے کسی کو جب اس چیز کی خبر دی جائے جس کی مثال اس نے (اللہ) رحمٰن کے لیے بیان کی ہے تو اس کا چہره سیاه پڑ جاتا ہے اور وه غمگین ہو جاتا ہے. ');
INSERT INTO "junagarri" VALUES(43,18,'43:18','کیا (اللہ کی اولاد لڑکیاں ہیں) جو زیورات میں پلیں اور جھگڑے میں (اپنی بات) واضح نہ کرسکیں؟ ');
INSERT INTO "junagarri" VALUES(43,19,'43:19','اور انہوں نے فرشتوں کو جو رحمٰن کے عبادت گزار ہیں عورتیں قرار دے لیا۔ کیا ان کی پیدائش کے موقع پر یہ موجود تھے؟ ان کی یہ گواہی لکھ لی جائے گی اور ان سے (اس چیز کی) باز پرس کی جائے گی. ');
INSERT INTO "junagarri" VALUES(43,20,'43:20','اور کہتے ہیں اگر اللہ چاہتا تو ہم ان کی عبادت نہ کرتے۔ انہیں اس کی کچھ خبر نہیں ، یہ تو صرف اٹکل پچو (جھوٹ باتیں) کہتے ہیں۔.');
INSERT INTO "junagarri" VALUES(43,21,'43:21','کیا ہم نے انہیں اس سے پہلے کوئی (اور) کتاب دی ہے جسے یہ مضبوط تھامے ہوئے ہیں. ');
INSERT INTO "junagarri" VALUES(43,22,'43:22','(نہیں نہیں) بلکہ یہ تو کہتے ہیں کہ ہم نے اپنے باپ دادا کو ایک مذہب پر پایا اور ہم انہی کے نقش قدم پر چل کر راه یافتہ ہیں.');
INSERT INTO "junagarri" VALUES(43,23,'43:23','اسی طرح آپ سے پہلے بھی ہم نے جس بستی میں کوئی ڈرانے والا بھیجا وہاں کے آسوده حال لوگوں نے یہی جواب دیا کہ ہم نے اپنے باپ دادا کو (ایک راه پر اور) ایک دین پر پایا اور ہم تو انہی کے نقش پا کی پیروی کرنے والے ہیں.');
INSERT INTO "junagarri" VALUES(43,24,'43:24','(نبی نے) کہا بھی کہ اگرچہ میں تمہارے پاس اس سے بہت بہتر (مقصود تک پہنچانے والا) طریقہ لے آیا ہوں جس پر تم نے اپنے باپ دادوں کو پایا، تو انہوں نے جواب دیا کہ ہم اس کے منکر ہیں جسے دے کر تمہیں بھیجا گیا ہے. ');
INSERT INTO "junagarri" VALUES(43,25,'43:25','پس ہم نے ان سے انتقام لیا اور دیکھ لے جھٹلانے والوں کا کیسا انجام ہوا؟');
INSERT INTO "junagarri" VALUES(43,26,'43:26','اور جبکہ ابراہیم (علیہ السلام) نے اپنے والد سے اور اپنی قوم سے فرمایا کہ میں ان چیزوں سے بیزار ہوں جن کی تم عبادت کرتے ہو.');
INSERT INTO "junagarri" VALUES(43,27,'43:27','بجز اس ذات کے جس نے مجھے پیدا کیا ہے اور وہی مجھے ہدایت بھی کرے گا. ');
INSERT INTO "junagarri" VALUES(43,28,'43:28','اور (ابراہیم علیہ السلام) اسی کو اپنی اولاد میں بھی باقی رہنے والی بات قائم کر گئے تاکہ لوگ (شرک سے) باز آتے رہیں. ');
INSERT INTO "junagarri" VALUES(43,29,'43:29','بلکہ میں نے ان لوگوں کو اور ان کے باپ دادوں کو سامان (اور اسباب) دیا، یہاں تک کہ ان کے پاس حق اور صاف صاف سنانے والا رسول آگیا. ');
INSERT INTO "junagarri" VALUES(43,30,'43:30','اور حق کے پہنچتے ہی یہ بول پڑے کہ یہ تو جادو ہے اور ہم اس کے منکر ہیں. ');
INSERT INTO "junagarri" VALUES(43,31,'43:31','اور کہنے لگے، یہ قرآن ان دونوں بستیوں میں سے کسی بڑے آدمی پر کیوں نہ نازل کیا گیا. ');
INSERT INTO "junagarri" VALUES(43,32,'43:32','کیا آپ کے رب کی رحمت کو یہ تقسیم کرتے ہیں ؟ ہم نے ہی ان کی زندگانیٴ دنیا کی روزی ان میں تقسیم کی ہے اور ایک کو دوسرے سے بلند کیا ہے تاکہ ایک دوسرے کو ماتحت کر لے جسے یہ لوگ سمیٹتے پھرتے ہیں اس سے آپ کے رب کی رحمت بہت ہی بہتر ہے. ');
INSERT INTO "junagarri" VALUES(43,33,'43:33','اور اگر یہ بات نہ ہوتی کہ تمام لوگ ایک ہی طریقہ پر ہو جائیں گے تو رحمٰن کے ساتھ کفر کرنے والوں کے گھروں کی چھتوں کو ہم چاندی کی بنادیتے۔ اور زینوں کو (بھی) جن پر چڑھا کرتے.');
INSERT INTO "junagarri" VALUES(43,34,'43:34','اور ان کے گھروں کے دروازے اور تخت بھی جن پر وه تکیہ لگا لگا کر بیٹھتے.');
INSERT INTO "junagarri" VALUES(43,35,'43:35','اور سونے کے بھی ، اور یہ سب کچھ یونہی سا دنیا کی زندگی کا فائده ہے اور آخرت تو آپ کے رب کے نزدیک (صرف) پرہیزگاروں کے لیے (ہی) ہے. ');
INSERT INTO "junagarri" VALUES(43,36,'43:36','اور جو شخص رحمٰن کی یاد سے غفلت کرے ہم اس پر ایک شیطان مقرر کردیتے ہیں وہی اس کا ساتھی رہتا ہے. ');
INSERT INTO "junagarri" VALUES(43,37,'43:37','اور وه انہیں راه سے روکتے ہیں اور یہ اسی خیال میں رہتے ہیں کہ یہ ہدایت یافتہ ہیں. ');
INSERT INTO "junagarri" VALUES(43,38,'43:38','یہاں تک کہ جب وه ہمارے پاس آئے گا کہے گا کاش! میرے اور تیرے درمیان مشرق اور مغرب کی دوری ہوتی (تو) بڑا برا ساتھی ہے. ');
INSERT INTO "junagarri" VALUES(43,39,'43:39','اور جب کہ تم ظالم ٹھہر چکے تو تمہیں آج ہرگز تم سب کا عذاب میں شریک ہونا کوئی نفع نہ دے گا.');
INSERT INTO "junagarri" VALUES(43,40,'43:40','کیا پس تو بہرے کو سنا سکتا ہے یا اندھے کو راه دکھا سکتا ہے اور اسے جو کھلی گمراہی میں ہو. ');
INSERT INTO "junagarri" VALUES(43,41,'43:41','پس اگر ہم تجھے یہاں سے لے بھی جائیں تو بھی ہم ان سے بدلہ لینے والے ہیں. ');
INSERT INTO "junagarri" VALUES(43,42,'43:42','یا جو کچھ ان سے وعده کیا ہے وه تجھے دکھا دیں ہم ان پر بھی قدرت رکھتے ہیں. ');
INSERT INTO "junagarri" VALUES(43,43,'43:43','پس جو وحی آپ کی طرف کی گئی ہے اسے مضبوط تھامے رہیں بیشک آپ راه راست پر ہیں. ');
INSERT INTO "junagarri" VALUES(43,44,'43:44','اور یقیناً یہ (خود) آپ کے لیے اور آپ کی قوم کے لیے نصیحت ہے اور عنقریب تم لوگ پوچھے جاؤ گے.');
INSERT INTO "junagarri" VALUES(43,45,'43:45','اور ہمارے ان نبیوں سے پوچھو! جنہیں ہم نے آپ سے پہلے بھیجا تھا کہ کیا ہم نے سوائے رحمٰن کے اور معبود مقرر کیے تھے جن کی عبادت کی جائے؟ ');
INSERT INTO "junagarri" VALUES(43,46,'43:46','اور ہم نے موسیٰ (علیہ السلام) کو اپنی نشانیاں دے کر فرعون اور اس کے امراء کے پاس بھیجا تو (موسیٰ علیہ السلام نے جاکر) کہا کہ میں تمام جہانوں کے رب کا رسول ہوں. ');
INSERT INTO "junagarri" VALUES(43,47,'43:47','پس جب وه ہماری نشانیاں لے کر ان کے پاس آئے تو وه بےساختہ ان پر ہنسنے لگے. ');
INSERT INTO "junagarri" VALUES(43,48,'43:48','اور ہم انہیں جو نشانی دکھاتے تھے وه دوسری سے بڑھی چڑھی ہوتی تھی اور ہم نے انہیں عذاب میں پکڑا تاکہ وه باز آجائیں. ');
INSERT INTO "junagarri" VALUES(43,49,'43:49','اور انہوں نے کہا اے جادوگر ! ہمارے لیے اپنے رب سے اس کی دعا کر جس کا اس نے تجھ سے وعده کر رکھا ہے ، یقین مان کہ ہم راه پر لگ جائیں گے. ');
INSERT INTO "junagarri" VALUES(43,50,'43:50','پھر جب ہم نے وه عذاب ان سے ہٹالیا انہوں نے اسی وقت اپنا قول وقرار توڑ دیا.');
INSERT INTO "junagarri" VALUES(43,51,'43:51','اور فرعون نے اپنی قوم میں منادی کرائی اور کہا اے میری قوم! کیا مصر کا ملک میرا نہیں؟ اور میرے (محلوں کے) نیچے یہ نہریں بہہ رہی ہیں ، کیا تم دیکھتے نہیں؟');
INSERT INTO "junagarri" VALUES(43,52,'43:52','بلکہ میں بہتر ہوں بہ نسبت اس کے جو بےتوقیر ہے اور صاف بول بھی نہیں سکتا. ');
INSERT INTO "junagarri" VALUES(43,53,'43:53','اچھا اس پر سونے کے کنگن کیوں نہیں آپڑے یا اس کے ساتھ پر باندھ کر فرشتے ہی آجاتے. ');
INSERT INTO "junagarri" VALUES(43,54,'43:54','اس نے اپنی قوم کو بہلایا پھسلایا اور انہوں نے اسی کی مان لی ، یقیناً یہ سارے ہی نافرمان لوگ تھے.');
INSERT INTO "junagarri" VALUES(43,55,'43:55','پھر جب انہوں نے ہمیں غصہ دلایا تو ہم نے ان سے انتقام لیا اور سب کو ڈبو دیا.');
INSERT INTO "junagarri" VALUES(43,56,'43:56','پس ہم نے انہیں گیا گزرا کردیا اور پچھلوں کے لیے مثال بنادی. ');
INSERT INTO "junagarri" VALUES(43,57,'43:57','اور جب ابن مریم کی مثال بیان کی گئی تو اس سے تیری قوم (خوشی سے) چیخنے لگی ہے.');
INSERT INTO "junagarri" VALUES(43,58,'43:58','اور انہوں نے کہا کہ ہمارے معبود اچھے ہیں یا وه؟ تجھ سے ان کا یہ کہنا محض جھگڑے کی غرض سے ہے، بلکہ یہ لوگ ہیں ہی جھگڑالو. ');
INSERT INTO "junagarri" VALUES(43,59,'43:59','عیسیٰ (علیہ السلام) بھی صرف بنده ہی ہے جس پر ہم نے احسان کیا اور اسے بنی اسرائیل کے لیے نشان قدرت بنایا. ');
INSERT INTO "junagarri" VALUES(43,60,'43:60','اگر ہم چاہتے تو تمہارے عوض فرشتے کردیتے جو زمین میں جانشینی کرتے. ');
INSERT INTO "junagarri" VALUES(43,61,'43:61','اور یقیناً عیسیٰ (علیہ السلام) قیامت کی علامت ہے پس تم (قیامت) کے بارے میں شک نہ کرو اور میری تابعداری کرو، یہی سیدھی راه ہے.');
INSERT INTO "junagarri" VALUES(43,62,'43:62','اور شیطان تمہیں روک نہ دے، یقیناً وه تمہارا صریح دشمن ہے.');
INSERT INTO "junagarri" VALUES(43,63,'43:63','اور جب عیسیٰ (علیہ السلام) معجزے کو لائے تو کہا۔ کہ میں تمہارے پاس حکمت لایا ہوں اور اس لیے آیا ہوں کہ جن بعض چیزوں میں تم مختلف ہو، انہیں واضح کردوں ، پس تم اللہ تعالیٰ سے ڈرو اور میرا کہا مانو.');
INSERT INTO "junagarri" VALUES(43,64,'43:64','میرا اور تمہارا رب فقط اللہ تعالیٰ ہی ہے۔ پس تم سب اس کی عبادت کرو۔ راه راست (یہی) ہے.');
INSERT INTO "junagarri" VALUES(43,65,'43:65','پھر (بنی اسرائیل کی) جماعتوں نے آپس میں اختلاف کیا ، پس ظالموں کے لیے خرابی ہے دکھ والے دن کی آفت سے.');
INSERT INTO "junagarri" VALUES(43,66,'43:66','یہ لوگ صرف قیامت کے منتظر ہیں کہ وه اچانک ان پر آپڑے اور انہیں خبر بھی نہ ہو.');
INSERT INTO "junagarri" VALUES(43,67,'43:67','اس دن (گہرے) دوست بھی ایک دوسرے کے دشمن بن جائیں گے سوائے پرہیزگاروں کے. ');
INSERT INTO "junagarri" VALUES(43,68,'43:68','میرے بندو! آج تو تم پر کوئی خوف (و ہراس) ہے اور نہ تم (بد دل اور) غمزده ہوگے. ');
INSERT INTO "junagarri" VALUES(43,69,'43:69','جو ہماری آیتوں پر ایمان لائے اور تھے بھی وه (فرماں بردار) مسلمان.');
INSERT INTO "junagarri" VALUES(43,70,'43:70','تم اور تمہاری بیویاں ہشاش بشاش (راضی خوشی) جنت میں چلے جاؤ. ');
INSERT INTO "junagarri" VALUES(43,71,'43:71','ان کے چاروں طرف سے سونے کی رکابیاں اور سونے کے گلاسوں کا دور چلایا جائے گا ، ان کے جی جس چیز کی خواہش کریں اور جس سے ان کی آنکھیں لذت پائیں، سب وہاں ہوگا اور تم اس میں ہمیشہ رہو گے. ');
INSERT INTO "junagarri" VALUES(43,72,'43:72','یہی وه بہشت ہے کہ تم اپنے اعمال کے بدلے اس کے وارث بنائے گئے ہو.');
INSERT INTO "junagarri" VALUES(43,73,'43:73','یہاں تمہارے لیے بکثرت میوے ہیں جنہیں تم کھاتے رہو گے.');
INSERT INTO "junagarri" VALUES(43,74,'43:74','بیشک گنہگار لوگ عذاب دوزخ میں ہمیشہ رہیں گے.');
INSERT INTO "junagarri" VALUES(43,75,'43:75','یہ عذاب کبھی بھی ان سے ہلکا نہ کیا جائے گا اور وه اسی میں مایوس پڑے رہیں گے. ');
INSERT INTO "junagarri" VALUES(43,76,'43:76','اور ہم نے ان پر ظلم نہیں کیا بلکہ یہ خود ہی ظالم تھے.');
INSERT INTO "junagarri" VALUES(43,77,'43:77','اور پکار پکار کر کہیں گے کہ اے مالک ! تیرا رب ہمارا کام ہی تمام کردے ، وه کہے گا کہ تمہیں تو (ہمیشہ) رہنا ہے. ');
INSERT INTO "junagarri" VALUES(43,78,'43:78','ہم تو تمہارے پاس حق لے آئے لیکن تم میں سے اکثر لوگ حق سے نفرت رکھنے والے تھے؟');
INSERT INTO "junagarri" VALUES(43,79,'43:79','کیا انہوں نے کسی کام کا پختہ اراده کرلیا ہے تو یقین مانو کہ ہم بھی پختہ کام کرنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(43,80,'43:80','کیا ان کا یہ خیال ہے کہ ہم ان کی پوشیده باتوں کو اور ان کی سرگوشیوں کو نہیں سنتے، (یقیناً ہم برابر سن رہے ہیں ) بلکہ ہمارے بھیجے ہوئے ان کے پاس ہی لکھ رہے ہیں. ');
INSERT INTO "junagarri" VALUES(43,81,'43:81','آپ کہہ دیجئے! کہ اگر بالفرض رحمٰن کی اولاد ہو تو میں سب سے پہلے عبادت کرنے والا ہوتا. ');
INSERT INTO "junagarri" VALUES(43,82,'43:82','آسمانوں اور زمین اور عرش کا رب جو کچھ یہ بیان کرتے ہیں اس سے (بہت) پاک ہے. ');
INSERT INTO "junagarri" VALUES(43,83,'43:83','اب آپ انہیں اسی بحث مباحثہ اور کھیل کود میں چھوڑ دیجئے ، یہاں تک کہ انہیں اس دن سے سابقہ پڑجائے جن کا یہ وعده دیے جاتے ہیں. ');
INSERT INTO "junagarri" VALUES(43,84,'43:84','وہی آسمانوں میں معبود ہے اور زمین میں بھی وہی قابل عبادت ہے اور وه بڑی حکمت والا اور پورے علم والا ہے.');
INSERT INTO "junagarri" VALUES(43,85,'43:85','اور وه بہت برکتوں والا ہے جس کے پاس آسمان وزمین اور ان کے درمیان کی بادشاہت ہے ، اور قیامت کا علم بھی اسی کے پاس ہے اور اسی کی جانب تم سب لوٹائے جاؤ گے. ');
INSERT INTO "junagarri" VALUES(43,86,'43:86','جنہیں یہ لوگ اللہ کے سوا پکارتے ہیں وه شفاعت کرنے کا اختیار نہیں رکھتے ، ہاں (مستحق شفاعت وه ہیں) جو حق بات کا اقرار کریں اور انہیں علم بھی ہو. ');
INSERT INTO "junagarri" VALUES(43,87,'43:87','اگر آپ ان سے دریافت کریں کہ انہیں کس نے پیدا کیا ہے؟ تو یقیناً یہ جواب دیں گے کہ اللہ نے، پھر یہ کہاں الٹے جاتے ہیں؟');
INSERT INTO "junagarri" VALUES(43,88,'43:88','اور ان کا (پیغمبر کا اکثر) یہ کہنا کہ اے میرے رب! یقیناً یہ وه لوگ ہیں جو ایمان نہیں لاتے.');
INSERT INTO "junagarri" VALUES(43,89,'43:89','پس آپ ان سے منھ پھیر لیں اور کہہ دیں۔ (اچھا بھائی) سلام ! انہیں عنقریب (خود ہی) معلوم ہوجائے گا.');
INSERT INTO "junagarri" VALUES(44,1,'44:1','حٰم.');
INSERT INTO "junagarri" VALUES(44,2,'44:2','قسم ہے اس وضاحت والی کتاب کی.');
INSERT INTO "junagarri" VALUES(44,3,'44:3','یقیناً ہم نے اسے بابرکت رات میں اتارا ہے بیشک ہم ڈرانے والے ہیں. ');
INSERT INTO "junagarri" VALUES(44,4,'44:4','اسی رات میں ہر ایک مضبوط کام کا فیصلہ کیا جاتا ہے. ');
INSERT INTO "junagarri" VALUES(44,5,'44:5','ہمارے پاس سے حکم ہوکر ، ہم ہی ہیں رسول بناکر بھیجنے والے. ');
INSERT INTO "junagarri" VALUES(44,6,'44:6','آپ کے رب کی مہربانی سے ۔ وه ہی ہے سننے والا جاننے والا.');
INSERT INTO "junagarri" VALUES(44,7,'44:7','جو رب ہے آسمانوں کا اور زمین کا اور جو کچھ ان کے درمیان ہے۔ اگر تم یقین کرنے والے ہو.');
INSERT INTO "junagarri" VALUES(44,8,'44:8','کوئی معبود نہیں اس کے سوا وہی جلاتا ہے اور مارتا ہے، وہی تمہارا رب ہے اور تمہارے اگلے باپ دادوں کا. ');
INSERT INTO "junagarri" VALUES(44,9,'44:9','بلکہ وه شک میں پڑے کھیل رہے ہیں. ');
INSERT INTO "junagarri" VALUES(44,10,'44:10','آپ اس دن کے منتظر رہیں جب کہ آسمان ظاہر دھواں لائے گا. ');
INSERT INTO "junagarri" VALUES(44,11,'44:11','جو لوگوں کو گھیر لے گا، یہ دردناک عذاب ہے.');
INSERT INTO "junagarri" VALUES(44,12,'44:12','کہیں گے کہ اے ہمارے رب! یہ آفت ہم سے دور کر ہم ایمان قبول کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(44,13,'44:13','ان کے لیے نصیحت کہاں ہے؟ کھول کھول کر بیان کرنے والے پیغمبران کے پاس آچکے.');
INSERT INTO "junagarri" VALUES(44,14,'44:14','پھر بھی انہوں نے ان سے منھ پھیرا اور کہہ دیا کہ سکھایا پڑھایا ہوا باؤلا ہے.');
INSERT INTO "junagarri" VALUES(44,15,'44:15','ہم عذاب کو تھوڑا دور کردیں گے تو تم پھر اپنی اسی حالت پر آجاؤ گے.');
INSERT INTO "junagarri" VALUES(44,16,'44:16','جس دن ہم بڑی سخت پکڑ پکڑیں گے ، بالیقین ہم بدلہ لینے والے ہیں.');
INSERT INTO "junagarri" VALUES(44,17,'44:17','یقیناً ان سے پہلے ہم قوم فرعون کو (بھی) آزما چکے ہیں جن کے پاس (اللہ) کا باعزت رسول آیا.');
INSERT INTO "junagarri" VALUES(44,18,'44:18','کہ اللہ تعالیٰ کے بندوں کو میرے حوالے کر دو، یقین مانو کہ میں تمہارے لیے امانت دار رسول ہوں. ');
INSERT INTO "junagarri" VALUES(44,19,'44:19','اور تم اللہ تعالیٰ کے سامنے سرکشی نہ کرو ، میں تمہارے پاس کھلی دلیل لانے والا ہوں. ');
INSERT INTO "junagarri" VALUES(44,20,'44:20','اور میں اپنے اور تمہارے رب کی پناه میں آتا ہوں اس سے کہ تم مجھے سنگسار کردو. ');
INSERT INTO "junagarri" VALUES(44,21,'44:21','اور اگر تم مجھ پر ایمان نہیں لاتے تو مجھ سے الگ ہی رہو. ');
INSERT INTO "junagarri" VALUES(44,22,'44:22','پھر انہوں نے اپنے رب سے دعا کی کہ یہ سب گنہگار لوگ ہیں. ');
INSERT INTO "junagarri" VALUES(44,23,'44:23','(ہم نے کہہ دیا) کہ راتوں رات تو میرے بندوں کو لے کر نکل، یقیناً تمہارا پیچھا کیا جائے گا.');
INSERT INTO "junagarri" VALUES(44,24,'44:24','تو دریا کو ساکن چھوڑ کر چلا جا ، بلاشبہ یہ لشکر غرق کردیا جائے گا.');
INSERT INTO "junagarri" VALUES(44,25,'44:25','وه بہت سے باغات اور چشمے چھوڑ گئے.');
INSERT INTO "junagarri" VALUES(44,26,'44:26','اور کھیتیاں اور راحت بخش ٹھکانے.');
INSERT INTO "junagarri" VALUES(44,27,'44:27','اور وه آرام کی چیزیں جن میں عیش کررہے تھے.');
INSERT INTO "junagarri" VALUES(44,28,'44:28','اسی طرح ہوگیا اور ہم نے ان سب کا وارث دوسری قوم کو بنادیا. ');
INSERT INTO "junagarri" VALUES(44,29,'44:29','سو ان پر نہ تو آسمان وزمین روئے اور نہ انہیں مہلت ملی.');
INSERT INTO "junagarri" VALUES(44,30,'44:30','اور بےشک ہم نے (ہی) بنی اسرائیل کو (سخت) رسوا کن سزا سے نجات دی.');
INSERT INTO "junagarri" VALUES(44,31,'44:31','(جو) فرعون کی طرف سے (ہو رہی) تھی۔ فیالواقع وه سرکش اور حد سے گزر جانے والوں میں سے تھا.');
INSERT INTO "junagarri" VALUES(44,32,'44:32','اور ہم نے دانستہ طور پر بنی اسرائیل کو دنیا جہان والوں پر فوقیت دی. ');
INSERT INTO "junagarri" VALUES(44,33,'44:33','اور ہم نے انہیں ایسی نشانیاں دیں جن میں صریح آزمائش تھی. ');
INSERT INTO "junagarri" VALUES(44,34,'44:34','یہ لوگ تو یہی کہتے ہیں. ');
INSERT INTO "junagarri" VALUES(44,35,'44:35','کہ (آخری چیز) یہی ہماری پہلی بار (دنیا سے) مرجانا ہے اور ہم دوباره اٹھائے نہیں جائیں گے.');
INSERT INTO "junagarri" VALUES(44,36,'44:36','اگر تم سچے ہو تو ہمارے باپ دادوں کو لے آؤ. ');
INSERT INTO "junagarri" VALUES(44,37,'44:37','کیا یہ لوگ بہتر ہیں یا تبع کی قوم کے لوگ اور جو ان سے بھی پہلے تھے۔ ہم نے ان سب کو ہلاک کردیا یقیناً وه گنہ گار تھے. ');
INSERT INTO "junagarri" VALUES(44,38,'44:38','ہم نے زمین اور آسمانوں اور ان کے درمیان کی چیزوں کو کھیل کے طور پر پیدا نہیں کیا. ');
INSERT INTO "junagarri" VALUES(44,39,'44:39','بلکہ ہم نے انہیں درست تدبیر کے ساتھ ہی پیدا کیا ہے، لیکن ان میں سے اکثر لوگ نہیں جانتے. ');
INSERT INTO "junagarri" VALUES(44,40,'44:40','یقیناً فیصلے کا دن ان سب کا طے شده وقت ہے. ');
INSERT INTO "junagarri" VALUES(44,41,'44:41','اس دن کوئی دوست کسی دوست کے کچھ بھی کام نہ آئے گا اور نہ ان کی امداد کی جائے گی. ');
INSERT INTO "junagarri" VALUES(44,42,'44:42','مگر جس پر اللہ کی مہربانی ہوجائے وه زبردست اور رحم کرنے والا ہے.');
INSERT INTO "junagarri" VALUES(44,43,'44:43','بیشک زقوم (تھوہر) کا درخت.');
INSERT INTO "junagarri" VALUES(44,44,'44:44','گناه گار کا کھانا ہے.');
INSERT INTO "junagarri" VALUES(44,45,'44:45','جو مثل تلچھٹ کے ہے اور پیٹ میں کھولتا رہتا ہے.');
INSERT INTO "junagarri" VALUES(44,46,'44:46','مثل تیز گرم پانی کے. ');
INSERT INTO "junagarri" VALUES(44,47,'44:47','اسے پکڑ لو پھر گھسیٹتے ہوئے بیچ جہنم تک پہنچاؤ. ');
INSERT INTO "junagarri" VALUES(44,48,'44:48','پھر اس کے سر پر سخت گرم پانی کا عذاب بہاؤ.');
INSERT INTO "junagarri" VALUES(44,49,'44:49','(اس سے کہا جائے گا) چکھتا جا تو تو بڑا ذی عزت اور بڑے اکرام والا تھا. ');
INSERT INTO "junagarri" VALUES(44,50,'44:50','یہی وه چیز ہے جس میں تم شک کیا کرتے تھے.');
INSERT INTO "junagarri" VALUES(44,51,'44:51','بیشک (اللہ سے) ڈرنے والے امن چین کی جگہ میں ہوں گے.');
INSERT INTO "junagarri" VALUES(44,52,'44:52','باغوں اور چشموں میں.');
INSERT INTO "junagarri" VALUES(44,53,'44:53','باریک اور دبیز ریشم کے لباس پہنے ہوئے آمنے سامنے بیٹھے ہوں گے. ');
INSERT INTO "junagarri" VALUES(44,54,'44:54','یہ اسی طرح ہے اور ہم بڑی بڑی آنکھوں والی حوروں سے ان کا نکاح کردیں گے. ');
INSERT INTO "junagarri" VALUES(44,55,'44:55','دل جمعی کے ساتھ وہاں ہر طرح کے میوؤں کی فرمائشیں کرتے ہوں گے. ');
INSERT INTO "junagarri" VALUES(44,56,'44:56','وہاں وه موت چکھنے کے نہیں ہاں پہلی موت (جو وه مر چکے)، انہیں اللہ تعالیٰ نے دوزخ کی سزا سے بچا دیا.');
INSERT INTO "junagarri" VALUES(44,57,'44:57','یہ صرف تیرے رب کا فضل ہے ، یہی ہے بڑی کامیابی.');
INSERT INTO "junagarri" VALUES(44,58,'44:58','ہم نے اس (قرآن) کو تیری زبان میں آسان کردیا تاکہ وه نصیحت حاصل کریں.');
INSERT INTO "junagarri" VALUES(44,59,'44:59','اب تو منتظر ره یہ بھی منتظر ہیں. ');
INSERT INTO "junagarri" VALUES(45,1,'45:1','حٰم');
INSERT INTO "junagarri" VALUES(45,2,'45:2','یہ کتاب اللہ غالب حکمت والے کی طرف سے نازل کی ہوئی ہے.');
INSERT INTO "junagarri" VALUES(45,3,'45:3','آسمانوں اور زمین میں ایمان داروں کے لیے یقیناً بہت سی نشانیاں ہیں.');
INSERT INTO "junagarri" VALUES(45,4,'45:4','اور خود تمہاری پیدائش میں اور ان جانوروں کی پیدائش میں جنہیں وه پھیلاتا ہے یقین رکھنے والی قوم کے لیے بہت سی نشانیاں ہیں.');
INSERT INTO "junagarri" VALUES(45,5,'45:5','اور رات دن کے بدلنے میں اور جو کچھ روزی اللہ تعالیٰ آسمان سے نازل فرما کر زمین کو اس کی موت کے بعد زنده کر دیتا ہے ، (اس میں) اور ہواؤں کے بدلنے میں بھی ان لوگوں کے لیے جو عقل رکھتے ہیں نشانیاں ہیں. ');
INSERT INTO "junagarri" VALUES(45,6,'45:6','یہ ہیں اللہ کی آیتیں جنہیں ہم آپ کو راستی سے سنا رہے ہیں، پس اللہ تعالیٰ اور اس کی آیتوں کے بعد یہ کس بات پر ایمان لائیں گے. ');
INSERT INTO "junagarri" VALUES(45,7,'45:7','ویل اور افسوس ہے ہر ایک جھوٹے گنہگار پر. ');
INSERT INTO "junagarri" VALUES(45,8,'45:8','جو آیتیں اللہ کی اپنے سامنے پڑھی جاتی ہوئی سنے پھر بھی غرور کرتا ہوا اس طرح اڑا رہے کہ گویا سنی ہی نہیں ، تو ایسے لوگوں کو دردناک عذاب کی خبر (پہنچا) دیجئے.');
INSERT INTO "junagarri" VALUES(45,9,'45:9','وه جب ہماری آیتوں میں سے کسی آیت کی خبر پالیتا ہے تو اس کی ہنسی اڑاتا ہے ، یہی لوگ ہیں جن کے لیے رسوائی کی مار ہے.');
INSERT INTO "junagarri" VALUES(45,10,'45:10','ان کے پیچھے دوزخ ہے ، جو کچھ انہوں نے حاصل کیا تھا وه انہیں کچھ بھی نفع نہ دے گا اور نہ وه (کچھ کام آئیں گے) جن کو انہوں نے اللہ کے سوا کارساز بنا رکھا تھا، ان کے لیے تو بہت بڑا عذاب ہے.');
INSERT INTO "junagarri" VALUES(45,11,'45:11','یہ (سر تاپا) ہدایت ہے اور جن لوگوں نے اپنے رب کی آیتوں کو نہ مانا ان کے لیے بہت سخت دردناک عذاب ہے. ');
INSERT INTO "junagarri" VALUES(45,12,'45:12','اللہ ہی ہے جس نے تمہارے لیے دریا کو تابع بنادیا تاکہ اس کے حکم سے اس میں کشتیاں چلیں اور تم اس کا فضل تلاش کرو اور تاکہ تم شکر بجالاؤ. ');
INSERT INTO "junagarri" VALUES(45,13,'45:13','اور آسمان وزمین کی ہر ہر چیز کو بھی اس نے اپنی طرف سے تمہارے لیے تابع کر دیا ہے ۔ جو غور کریں یقیناً وه اس میں بہت سی نشانیاں پالیں گے.');
INSERT INTO "junagarri" VALUES(45,14,'45:14','آپ ایمان والوں سے کہہ دیں کہ وه ان لوگوں سے درگزر کریں جو اللہ کے دنوں کی توقع نہیں رکھتے، تاکہ اللہ تعالیٰ ایک قوم کو ان کے کرتوتوں کا بدلہ دے. ');
INSERT INTO "junagarri" VALUES(45,15,'45:15','جو نیکی کرے گا وه اپنے ذاتی بھلے کے لیے اور جو برائی کرے گا اس کا وبال اسی پر ہے ، پھر تم سب اپنے پروردگار کی طرف لوٹائے جاؤ گے. ');
INSERT INTO "junagarri" VALUES(45,16,'45:16','یقیناً ہم نے بنی اسرائیل کو کتاب، حکومت اور نبوت دی تھی، اور ہم نے انہیں پاکیزه (اور نفیس) روزیاں دی تھیں اور انہیں دنیا والوں پر فضیلت دی تھی. ');
INSERT INTO "junagarri" VALUES(45,17,'45:17','اور ہم نے انہیں دین کی صاف صاف دلیلیں دیں ، پھر انہوں نے اپنے پاس علم کے پہنچ جانے کے بعد آپس کی ضد بحث سے ہی اختلاف برپا کر ڈالا ، یہ جن جن چیزوں میں اختلاف کر رہے ہیں ان کا فیصلہ قیامت والے دن ان کے درمیان (خود) تیرا رب کرے گا. ');
INSERT INTO "junagarri" VALUES(45,18,'45:18','پھر ہم نے آپ کو دین کی (ظاہر) راه پر قائم کردیا ، سو آپ اسی پر لگے رہیں اور نادانوں کی خواہشوں کی پیروی میں نہ پڑیں. ');
INSERT INTO "junagarri" VALUES(45,19,'45:19','(یاد رکھیں) کہ یہ لوگ ہرگز اللہ کے سامنے آپ کے کچھ کام نہیں آسکتے۔ (سمجھ لیں کہ) ظالم لوگ آپس میں ایک دوسرے کے رفیق ہوتے ہیں اور پرہیزگاروں کا کارساز اللہ تعالیٰ ہے.');
INSERT INTO "junagarri" VALUES(45,20,'45:20','یہ (قرآن) لوگوں کے لیے بصیرت کی باتیں اور ہدایت ورحمت ہے اس قوم کے لیے جو یقین رکھتی ہے.');
INSERT INTO "junagarri" VALUES(45,21,'45:21','کیا ان لوگوں کا جو برے کام کرتے ہیں یہ گمان ہے کہ ہم انہیں ان لوگوں جیسا کردیں گے جو ایمان لائے اور نیک کام کیے کہ ان کا مرنا جینا یکساں ہوجائے ، برا ہے وه فیصلہ جو وه کررہے ہیں.');
INSERT INTO "junagarri" VALUES(45,22,'45:22','اور آسمانوں اور زمین کو اللہ نے بہت ہی عدل کے ساتھ پیدا کیا ہے اور تاکہ ہر شخص کو اس کے کیے ہوئے کام کا پورا بدلہ دیا جائے اور ان پر ظلم نہ کیا جائے گا. ');
INSERT INTO "junagarri" VALUES(45,23,'45:23','کیا آپ نے اسے بھی دیکھا؟ جس نے اپنی خواہش نفس کو اپنا معبود بنا رکھا ہے اور باوجود سمجھ بوجھ کے اللہ نے اسے گمراه کردیا ہے اور اس کے کان اور دل پر مہر لگادی ہے اور اس کی آنکھ پر بھی پرده ڈال دیا ہے، اب ایسے شخص کو اللہ کے بعد کون ہدایت دے سکتا ہے. ');
INSERT INTO "junagarri" VALUES(45,24,'45:24','کیا اب بھی تم نصیحت نہیں پکڑتے ۔ انہوں نے کہا کہ ہماری زندگی تو صرف دنیا کی زندگی ہی ہے۔ ہم مرتے ہیں اور جیتے ہیں اور ہمیں صرف زمانہ ہی مار ڈالتا ہے ، (دراصل) انہیں اس کا کچھ علم ہی نہیں۔ یہ تو صرف (قیاس اور) اٹکل سے ہی کام لے رہے ہیں.');
INSERT INTO "junagarri" VALUES(45,25,'45:25','اور جب ان کے سامنے ہماری واضح اور روشن آیتوں کی تلاوت کی جاتی ہے، تو ان کے پاس اس قول کے سوا کوئی دلیل نہیں ہوتی کہ اگر تم سچے ہو تو ہمارے باپ دادوں کو لاؤ. ');
INSERT INTO "junagarri" VALUES(45,26,'45:26','آپ کہہ دیجئے! اللہ ہی تمہیں زنده کرتا ہے پھر تمہیں مار ڈالتا ہے پھر تمہیں قیامت کے دن جمع کرے گا جس میں کوئی شک نہیں لیکن اکثر لوگ نہیں سمجھتے.');
INSERT INTO "junagarri" VALUES(45,27,'45:27','اور آسمانوں اور زمین کی بادشاہی اللہ ہی کی ہے اور جس دن قیامت قائم ہوگی اس دن اہل باطل بڑے نقصان میں پڑیں گے.');
INSERT INTO "junagarri" VALUES(45,28,'45:28','اور آپ دیکھیں گے کہ ہر امت گھٹنوں کے بل گری ہوئی ہوگی ۔ ہر گروه اپنے نامہٴ اعمال کی طرف بلایا جائے گا، آج تمہیں اپنے کیے کا بدلہ دیا جائے گا.');
INSERT INTO "junagarri" VALUES(45,29,'45:29','یہ ہے ہماری کتاب جو تمہارے بارے میں سچ سچ بول رہی ہے ، ہم تمہارے اعمال لکھواتے جاتے تھے. ');
INSERT INTO "junagarri" VALUES(45,30,'45:30','پس لیکن جو لوگ ایمان لائے اور انہوں نے نیک کام کیے تو ان کو ان کا رب اپنی رحمت تلے لے لے گا ، یہی صریح کامیابی ہے.');
INSERT INTO "junagarri" VALUES(45,31,'45:31','لیکن جن لوگوں نے کفر کیا تو (میں ان سے کہوں گا) کیا میری آیتیں تمہیں سنائی نہیں جاتی تھیں ؟ پھر بھی تم تکبر کرتے رہے اور تم تھے ہی گنہ گار لوگ. ');
INSERT INTO "junagarri" VALUES(45,32,'45:32','اور جب کبھی کہا جاتا کہ اللہ کا وعده یقیناً سچا ہے اور قیامت کے آنے میں کوئی شک نہیں تو تم جواب دیتے تھے کہ ہم نہیں جانتے قیامت کیا چیز ہے؟ ہمیں کچھ یوں ہی سا خیال ہوجاتا ہے لیکن ہمیں یقین نہیں. ');
INSERT INTO "junagarri" VALUES(45,33,'45:33','اور ان پر اپنے اعمال کی برائیاں کھل گئیں اور جس کا وه مذاق اڑا رہے تھے اس نے انہیں گھیر لیا. ');
INSERT INTO "junagarri" VALUES(45,34,'45:34','اور کہہ دیا گیا کہ آج ہم تمہیں بھلا دیں گے جیسے کہ تم نے اپنے اس دن سے ملنے کو بھلا دیا تھا تمہارا ٹھکانا جہنم ہے اور تمہارا مددگار کوئی نہیں.');
INSERT INTO "junagarri" VALUES(45,35,'45:35','یہ اس لیے ہے کہ تم نے اللہ تعالیٰ کی آیتوں کی ہنسی اڑائی تھی اور دنیا کی زندگی نے تمہیں دھوکے میں ڈال رکھا تھا، پس آج کے دن نہ تو یہ (دوزخ) سے نکالے جائیں گے اور نہ ان سے عذر و معذرت قبول کیا جائے گا. ');
INSERT INTO "junagarri" VALUES(45,36,'45:36','پس اللہ کی تعریف ہے جو آسمانوں اور زمین اور تمام جہان کا پالنہار ہے.');
INSERT INTO "junagarri" VALUES(45,37,'45:37','تمام (بزرگی اور) بڑائی آسمانوں اور زمین میں اسی کی ہے اور وہی غالب اور حکمت والا ہے.');
INSERT INTO "junagarri" VALUES(46,1,'46:1','حٰم. ');
INSERT INTO "junagarri" VALUES(46,2,'46:2','اس کتاب کا اتارنا اللہ تعالیٰ غالب حکمت والے کی طرف سے ہے.');
INSERT INTO "junagarri" VALUES(46,3,'46:3','ہم نے آسمانوں اور زمین اور ان دونوں کے درمیان کی تمام چیزوں کو بہترین تدبیر کے ساتھ ہی ایک مدت معین کے لئے پیدا کیا ہے ، اور کافر لوگ جس چیز سے ڈرائے جاتے ہیں منھ موڑ لیتے ہیں. ');
INSERT INTO "junagarri" VALUES(46,4,'46:4','آپ کہہ دیجئے! بھلا دیکھو تو جنہیں تم اللہ کے سوا پکارتے ہو مجھے بھی تو دکھاؤ کہ انہوں نے زمین کا کون سا ٹکڑا بنایا ہے یا آسمانوں میں ان کا کون سا حصہ ہے ؟ اگر تم سچے ہو تو اس سے پہلے ہی کی کوئی کتاب یا کوئی علم ہی جو نقل کیا جاتا ہو، میرے پاس لاؤ. ');
INSERT INTO "junagarri" VALUES(46,5,'46:5','اور اس سے بڑھ کر گمراه اور کون ہوگا؟ جو اللہ کے سوا ایسوں کو پکارتا ہے جو قیامت تک اس کی دعا قبول نہ کر سکیں بلکہ ان کے پکارنے سے محض بےخبر ہوں. ');
INSERT INTO "junagarri" VALUES(46,6,'46:6','اور جب لوگوں کو جمع کیا جائے گا تو یہ ان کے دشمن ہو جائیں گے اور ان کی پرستش سے صاف انکار کر جائیں گے. ');
INSERT INTO "junagarri" VALUES(46,7,'46:7','اور انہیں جب ہماری واضح آیتیں پڑھ کر سنائی جاتی ہیں تو منکر لوگ سچی بات کو جب کہ ان کے پاس آچکی، کہہ دیتے ہیں کہ یہ تو صریح جادو ہے.');
INSERT INTO "junagarri" VALUES(46,8,'46:8','کیا وه کہتے ہیں کہ اسے تو اس نے خود گھڑ لیا ہے آپ کہہ دیجئے! کہ اگر میں ہی اسے بنا لایا ہوں تو تم میرے لئے اللہ کی طرف سے کسی چیز کا اختیار نہیں رکھتے ، تم اس (قرآن) کے بارے میں جو کچھ کہہ سن رہے ہو اسے اللہ خوب جانتا ہے ، میرے اور تمہارے درمیان گواہی کے لئے وہی کافی ہے ، اور وه بخشنے والا مہربان ہے. ');
INSERT INTO "junagarri" VALUES(46,9,'46:9','آپ کہہ دیجئے! کہ میں کوئی بالکل انوکھا پیغمبر تو نہیں نہ مجھے یہ معلوم ہے کہ میرے ساتھ اور تمہارے ساتھ کیا کیا جائے گا ۔ میں تو صرف اسی کی پیروی کرتا ہوں جو میری طرف وحی بھیجی جاتی ہے اور میں تو صرف علیالاعلان آگاه کر دینے والا ہوں.');
INSERT INTO "junagarri" VALUES(46,10,'46:10','آپ کہہ دیجئے! اگر یہ (قرآن) اللہ ہی کی طرف سے ہو اور تم نے اسے نہ مانا ہو اور بنی اسرائیل کا ایک گواه اس جیسی کی گواہی بھی دے چکا ہو اور وه ایمان بھی لاچکا ہو اور تم نے سرکشی کی ہو ، تو بیشک اللہ تعالیٰ ظالموں کو راه نہیں دکھاتا.');
INSERT INTO "junagarri" VALUES(46,11,'46:11','اور کافروں نے ایمان داروں کی نسبت کہا کہ اگر یہ (دین) بہتر ہوتا تو یہ لوگ اس کی طرف ہم سے سبقت کرنے نہ پاتے، اور چونکہ انہوں نے قرآن سے ہدایت نہیں پائی پس یہ کہہ دیں گے کہ قدیمی جھوٹ ہے. ');
INSERT INTO "junagarri" VALUES(46,12,'46:12','اور اس سے پہلے موسیٰ کی کتاب پیشوا اور رحمت تھی۔ اور یہ کتاب ہے تصدیق کرنے والی عربی زبان میں تاکہ ظالموں کو ڈرائے اور نیک کاروں کو بشارت ہو.');
INSERT INTO "junagarri" VALUES(46,13,'46:13','بیشک جن لوگوں نے کہا کہ ہمارا رب اللہ ہے پھر اس پر جمے رہے تو ان پر نہ تو کوئی خوف ہوگا اور نہ غمگین ہوں گے.');
INSERT INTO "junagarri" VALUES(46,14,'46:14','یہ تو اہل جنت ہیں جو سدا اسی میں رہیں گے، ان اعمال کے بدلے جو وه کیا کرتے تھے.');
INSERT INTO "junagarri" VALUES(46,15,'46:15','اور ہم نے انسان کو اپنے ماں باپ کے ساتھ حسن سلوک کرنے کا حکم دیا ہے، اس کی ماں نے اسے تکلیف جھیل کر پیٹ میں رکھا اور تکلیف برداشت کرکے اسے جنا ۔ اس کے حمل کا اور اس کے دودھ چھڑانے کا زمانہ تیس مہینے کا ہے ۔ یہاں تک کہ جب وه پختگی اور چالیس سال کی عمر کو پہنچا تو کہنے لگا اے میرے پروردگار! مجھے توفیق دے کہ میں تیری اس نعمت کا شکر بجا لاؤں جو تو نے مجھ پر اور میرے ماں باپ پر انعام کی ہے اور یہ کہ میں ایسے نیک عمل کروں جن سے تو خوش ہو جائے اور تو میری اولاد بھی صالح بنا، میں تیری طرف رجوع کرتا ہوں اور میں مسلمانوں میں سے ہوں.');
INSERT INTO "junagarri" VALUES(46,16,'46:16','یہی وه لوگ ہیں جن کے نیک اعمال تو ہم قبول فرما لیتے ہیں اور جن کے بداعمال سے درگزر کر لیتے ہیں، (یہ) جنتی لوگوں میں ہیں۔ اس سچے وعدے کے مطابق جو ان سے کیا جاتا تھا.');
INSERT INTO "junagarri" VALUES(46,17,'46:17','اور جس نے اپنے ماں باپ سے کہا کہ تم سے میں تنگ آگیا ، تم مجھ سے یہی کہتے رہو گے کہ میں مرنے کے بعد پھر زنده کیا جاؤں گا مجھ سے پہلے بھی امتیں گزر چکی ہیں ، وه دونوں جناب باری میں فریادیں کرتے ہیں اور کہتے ہیں تجھے خرابی ہو تو ایمان لے آ، بیشک اللہ کا وعده حق ہے، وه جواب دیتا ہے کہ یہ تو صرف اگلوں کے افسانے ہیں. ');
INSERT INTO "junagarri" VALUES(46,18,'46:18','وه لوگ ہیں جن پر (اللہ کے عذاب کا) وعده صادق آگیا ، ان جنات اور انسانوں کے گروہوں کے ساتھ جو ان سے پہلے گزر چکے ہیں ، یقیناً یہ نقصان پانے والے .تھے.');
INSERT INTO "junagarri" VALUES(46,19,'46:19','اور ہر ایک کو اپنے اپنے اعمال کے مطابق درجے ملیں گے تاکہ انہیں ان کے اعمال کے پورے بدلے دے اور ان پر ظلم نہ کیا جائے گا. ');
INSERT INTO "junagarri" VALUES(46,20,'46:20','اور جس دن کافر جہنم کے سرے پر لائے جائیں گے (کہا جائے گا) تم نے اپنی نیکیاں دنیا کی زندگی میں ہی برباد کر دیں اور ان سے فائدے اٹھا چکے، پس آج تمہیں ذلت کے عذاب کی سزا دی جائے گی ، اس باعث کہ تم زمین میں ناحق تکبر کیا کرتے تھے اور اس باعث بھی کہ تم حکم عدولی کیا کرتے تھے. ');
INSERT INTO "junagarri" VALUES(46,21,'46:21','اور عاد کے بھائی کو یاد کرو، جبکہ اس نے اپنی قوم کو احقاف میں ڈرایا اور یقیناً اس سے پہلے بھی ڈرانے والے گزر چکے ہیں اور اس کے بعد بھی یہ کہ تم سوائے اللہ تعالیٰ کے اور کسی کی عبادت نہ کرو، بیشک میں تم پر بڑے دن کے عذاب سے خوف کھاتا ہوں. ');
INSERT INTO "junagarri" VALUES(46,22,'46:22','قوم نے جواب دیا، کیا آپ ہمارے پاس اس لئے آئے ہیں کہ ہمیں اپنے معبودوں (کی پرستش) سے باز رکھیں ؟ پس اگر آپ سچے ہیں تو جس عذاب کا آپ وعده کرتے ہیں اسے ہم پر لا ڈالیں.');
INSERT INTO "junagarri" VALUES(46,23,'46:23','(حضرت ہود نے) کہا (اس کا) علم تو اللہ ہی کے پاس ہے، میں تو جو پیغام دے کر بھیجا گیا تھا وه تمہیں پہنچا رہا ہوں لیکن میں دیکھتا ہوں کہ تم لوگ نادانی کر رہے ہو. ');
INSERT INTO "junagarri" VALUES(46,24,'46:24','پھر جب انہوں نے عذاب کو بصورت بادل دیکھا اپنی وادیوں کی طرف آتے ہوئے تو کہنے لگے، یہ ابر ہم پر برسنے والا ہے ، (نہیں) بلکہ دراصل یہ ابر وه (عذاب) ہے جس کی تم جلدی کر رہے تھے ، ہوا ہے جس میں دردناک عذاب ہے. ');
INSERT INTO "junagarri" VALUES(46,25,'46:25','جو اپنے رب کے حکم سے ہر چیز کو ہلاک کر دے گی، پس وه ایسے ہوگئے کہ بجر ان کے مکانات کے اور کچھ دکھائی نہ دیتا تھا۔ گنہگاروں کے گروه کو ہم یوں ہی سزا دیتے ہیں.');
INSERT INTO "junagarri" VALUES(46,26,'46:26','اور بالیقین ہم نے (قوم عاد) کو وه مقدور دیئے تھے جو تمہیں تو دیئے بھی نہیں اور ہم نے انہیں کان آنکھیں اور دل بھی دے رکھے تھے۔ لیکن ان کے کانوں اور آنکھوں اور دلوں نے انہیں کچھ بھی نفع نہ پہنچایا جبکہ وه اللہ تعالیٰ کی آیتوں کا انکار کرنے لگے اور جس چیز کا وه مذاق اڑایا کرتے تھے وہی ان پر الٹ پڑی. ');
INSERT INTO "junagarri" VALUES(46,27,'46:27','اور یقیناً ہم نے تمہارے آس پاس کی بستیاں تباه کر دیں اور طرح طرح کی ہم نے اپنی نشانیاں بیان کر دیں تاکہ وه رجوع کر لیں. ');
INSERT INTO "junagarri" VALUES(46,28,'46:28','پس قرب الٰہی حاصل کرنے کے لئے انہوں نے اللہ کے سوا جن جن کو اپنا معبود بنا رکھا تھا انہوں نے ان کی مدد کیوں نہ کی؟ بلکہ وه تو ان سے کھو گئے، (بلکہ دراصل) یہ ان کا محض جھوٹ اور (بالکل) بہتان تھا. ');
INSERT INTO "junagarri" VALUES(46,29,'46:29','اور یاد کرو! جبکہ ہم نے جنوں کی ایک جماعت کو تیری طرف متوجہ کیا کہ وه قرآن سنیں، پس جب (نبی کے) پاس پہنچ گئے تو (ایک دوسرے سے) کہنے لگے خاموش ہو جاؤ ، پھر جب پڑھ کر ختم ہوگیا تو اپنی قوم کو خبردار کرنے کے لئے واپس لوٹ گئے.');
INSERT INTO "junagarri" VALUES(46,30,'46:30','کہنے لگے اے ہماری قوم! ہم نے یقیناً وه کتاب سنی ہے جو موسیٰ (علیہ السلام) کے بعد نازل کی گئی ہے جو اپنے سے پہلی کتابوں کی تصدیق کرنے والی ہے جو سچے دین کی اور راه راست کی طرف رہنمائی کرتی ہے.');
INSERT INTO "junagarri" VALUES(46,31,'46:31','اے ہماری قوم! اللہ کے بلانے والے کا کہا مانو، اس پر ایمان لاؤ ، تو اللہ تمہارے گناه بخش دے گا اور تمہیں المناک عذاب سے پناه دےگا. ');
INSERT INTO "junagarri" VALUES(46,32,'46:32','اور جو شخص اللہ کے بلانے والے کا کہا نہ مانے گا پس وه زمین میں کہیں (بھاگ کر اللہ کو) عاجز نہیں کر سکتا ، نہ اللہ کے سوا اور کوئی اس کے مدد گار ہوں گے ، یہ لوگ کھلی گمراہی میں ہیں.');
INSERT INTO "junagarri" VALUES(46,33,'46:33','کیا وه نہیں دیکھتے کہ جس اللہ نے آسمانوں اور زمین کو پیدا کیا اور ان کے پیدا کرنے سے وه نہ تھکا، وه یقیناً مُردوں کو زنده کرنے پر قادر ہے؟ کیوں نہ ہو؟ وه یقیناً ہر چیز پر قادر ہے. ');
INSERT INTO "junagarri" VALUES(46,34,'46:34','وه لوگ جنہوں نے کفر کیا جس دن جہنم کے سامنے لائے جائیں گے (اور ان سے کہا جائے گا کہ) کیا یہ حق نہیں ہے؟ تو جواب دیں گے کہ ہاں قسم ہے ہمارے رب کی (حق ہے) (اللہ) فرمائے گا، اب اپنے کفر کے بدلے عذاب کا مزه چکھو. ');
INSERT INTO "junagarri" VALUES(46,35,'46:35','پس (اے پیغمبر!) تم ایسا صبر کرو جیسا صبر عالی ہمت رسولوں نے کیا اور ان کے لئے (عذاب طلب کرنے میں) جلدی نہ کرو ، یہ جس دن اس عذاب کو دیکھ لیں گے جس کا وعده دیئے جاتے ہیں تو (یہ معلوم ہونے لگے گا کہ) دن کی ایک گھڑی ہی (دنیا میں) ٹھہرے تھے، یہ ہے پیغام پہنچا دینا، پس بدکاروں کے سوا کوئی ہلاک نہ کیا جائے گا. ');
INSERT INTO "junagarri" VALUES(47,1,'47:1','جن لوگوں نے کفر کیا اور اللہ کی راه سے روکا اللہ نے ان کے اعمال برباد کر دیئے. ');
INSERT INTO "junagarri" VALUES(47,2,'47:2','اور جو لوگ ایمان لائے اور اچھے کام کیے اور اس پر بھی ایمان لائے جو محمد ﴿صلی اللہ علیہ وسلم﴾ پر اتاری گئی ہے اور دراصل ان کے رب کی طرف سے سچا (دین) بھی وہی ہے، اللہ نے ان کے گناه دور کر دیئے اور ان کے حال کی اصلاح کر دی. ');
INSERT INTO "junagarri" VALUES(47,3,'47:3','یہ اس لئے کہ کافروں نے باطل کی پیروی کی اور مومنوں نے اس دین حق کی اتباع کی جو ان کے اللہ کی طرف سے ہے، اللہ تعالیٰ لوگوں کو ان کے احوال اسی طرح بتاتا ہے. ');
INSERT INTO "junagarri" VALUES(47,4,'47:4','تو جب کافروں سے تمہاری مڈبھیڑ ہوتو گردنوں پر وار مارو ۔ جب ان کو اچھی طرح کچل ڈالو تو اب خوب مضبوط قید وبند سے گرفتار کرو ، (پھر اختیار ہے) کہ خواه احسان رکھ کر چھوڑ دو یا فدیہ لے کر تاوقتیکہ لڑائی اپنے ہتھیار رکھ دے ۔ یہی حکم ہے اور اللہ اگر چاہتا تو (خود) ہی ان سے بدلہ لے لیتا ، لیکن (اس کا منشا یہ ہے) کہ تم میں سے ایک کا امتحان دوسرے کے ذریعہ سے لے لے ، جو لوگ اللہ کی راه میں شہید کر دیے جاتے ہیں اللہ ان کے اعمال ہرگز ضائع نہ کرے گا. ');
INSERT INTO "junagarri" VALUES(47,5,'47:5','انہیں راه دکھائے گا اور ان کے حالات کی اصلاح کر دے گا. ');
INSERT INTO "junagarri" VALUES(47,6,'47:6','اور انہیں اس جنت میں لے جائے گا جس سے انہیں شناسا کر دیا ہے. ');
INSERT INTO "junagarri" VALUES(47,7,'47:7','اے ایمان والو! اگر تم اللہ (کے دین) کی مدد کرو گے تو وه تمہاری مدد کرے گا اور تمہیں ثابت قدم رکھے گا. ');
INSERT INTO "junagarri" VALUES(47,8,'47:8','اور جو لوگ کافر ہوئے انہیں ہلاکی ہو اللہ ان کے اعمال غارت کردے گا.');
INSERT INTO "junagarri" VALUES(47,9,'47:9','یہ اس لئے کہ وه اللہ کی نازل کرده چیز سے ناخوش ہوئے، پس اللہ تعالیٰ نے (بھی) ان کے اعمال ضائع کر دیئے. ');
INSERT INTO "junagarri" VALUES(47,10,'47:10','کیا ان لوگوں نے زمین میں چل پھر کر اس کا معائنہ نہیں کیا کہ ان سے پہلے کے لوگوں کا نتیجہ کیا ہوا ؟ اللہ نے انہیں ہلاک کر دیا اور کافروں کے لئے اسی طرح کی سزائیں ہیں. ');
INSERT INTO "junagarri" VALUES(47,11,'47:11','وه اس لئے کہ ایمان والوں کا کارساز خود اللہ تعالیٰ ہے اور اس لئے کہ کافروں کا کوئی کارساز نہیں. ');
INSERT INTO "junagarri" VALUES(47,12,'47:12','جو لوگ ایمان لائے اور انہوں نے نیک اعمال کیے انہیں اللہ تعالیٰ ایسے باغوں میں داخل کرے گا جن کے نیچے نہریں جاری ہیں اور جو لوگ کافر ہوئے وه (دنیا ہی کا) فائده اٹھا رہے ہیں اور مثل چوپایوں کے کھا رہے ہیں ، ان کا (اصل) ٹھکانہ جہنم ہے.');
INSERT INTO "junagarri" VALUES(47,13,'47:13','ہم نے کتنی بستیوں کو جو طاقت میں تیری اس بستی سے زیاده تھیں جس سے تجھے نکالا ہم نے انہیں ہلاک کر دیا ہے، جن کا مددگار کوئی نہ اٹھا.');
INSERT INTO "junagarri" VALUES(47,14,'47:14','کیا پس وه شخص جو اپنے پروردگار کی طرف سے دلیل پر ہو اس شخص جیسا ہو سکتا ہے؟ جس کے لئے اس کا برا کام مزین کر دیا گیا ہو اور وه اپنی نفسانی خواہشوں کا پیرو ہو؟ ');
INSERT INTO "junagarri" VALUES(47,15,'47:15','اس جنت کی صفت جس کا پرہیزگاروں سے وعده کیا گیا ہے، یہ ہے کہ اس میں پانی کی نہریں ہیں جو بدبو کرنے والا نہیں ، اور دودھ کی نہریں ہیں جن کا مزه نہیں بدلا اور شراب کی نہریں ہیں جن میں پینے والوں کے لئے بڑی لذت ہے اور نہریں ہیں شہد کی جو بہت صاف ہیں اور ان کے لئے وہاں ہر قسم کے میوے ہیں اور ان کے رب کی طرف سے مغفرت ہے، کیا یہ مثل اس کے ہیں جو ہمیشہ آگ میں رہنے والا ہے؟ اور جنہیں گرم کھولتا ہوا پانی پلایا جائے گا جو ان کی آنتوں کو ٹکڑے ٹکڑے کر دے گا. ');
INSERT INTO "junagarri" VALUES(47,16,'47:16','اور ان میں بعض (ایسے بھی ہیں کہ) تیری طرف کان لگاتے ہیں۔ یہاں تک کہ جب تیرے پاس سے جاتے ہیں تو اہل علم سے (بوجہ کند ذہنی ولاپرواہی کے) پوچھتے ہیں کہ اس نے ابھی کیا کہا تھا ؟ یہی لوگ ہیں جن کے دلوں پر اللہ نے مہر کر دی ہے اور وه اپنی خواہشوں کی پیروی کرتے ہیں.');
INSERT INTO "junagarri" VALUES(47,17,'47:17','اور جو لوگ ہدایت یافتہ ہیں اللہ نے انہیں ہدایت میں اور بڑھا دیا ہے اور انہیں ان کی پرہیزگاری عطا فرمائی ہے. ');
INSERT INTO "junagarri" VALUES(47,18,'47:18','تو کیا یہ قیامت کا انتظار کر رہے ہیں کہ وه ان کے پاس اچانک آجائے یقیناً اس کی علامتیں تو آچکی ہیں ، پھر جبکہ ان کے پاس قیامت آجائے انہیں نصیحت کرنا کہاں ہوگا؟ ');
INSERT INTO "junagarri" VALUES(47,19,'47:19','سو (اے نبی!) آپ یقین کر لیں کہ اللہ کے سوا کوئی معبود نہیں اور اپنے گناہوں کی بخشش مانگا کریں اور مومن مردوں اور مومن عورتوں کے حق میں بھی ، اللہ تم لوگوں کی آمد ورفت کی اور رہنے سہنے کی جگہ کو خوب جانتا ہے. ');
INSERT INTO "junagarri" VALUES(47,20,'47:20','اور جو لوگ ایمان لائے وه کہتے ہیں کوئی سورت کیوں نازل نہیں کی گئی ؟ پھر جب کوئی صاف مطلب والی سورت نازل کی جاتی ہے اور اس میں قتال کا ذکر کیا جاتا ہے تو آپ دیکھتے ہیں کہ جن لوگوں کے دلوں میں بیماری ہے وه آپ کی طرف اس طرح دیکھتے ہیں جیسے اس شخص کی نظر ہوتی ہے جس پر موت کی بیہوشی طاری ہو ، پس بہت بہتر تھا ان کے لئے.');
INSERT INTO "junagarri" VALUES(47,21,'47:21','فرمان کا بجا لانا اور اچھی بات کا کہنا ۔ پھر جب کام مقرر ہو جائے ، تو اللہ کے ساتھ سچے رہیں تو ان کے لئے بہتری ہے. ');
INSERT INTO "junagarri" VALUES(47,22,'47:22','اور تم سے یہ بھی بعید نہیں کہ اگر تم کو حکومت مل جائے تو تم زمین میں فساد برپا کر دو اور رشتے ناتے توڑ ڈالو.');
INSERT INTO "junagarri" VALUES(47,23,'47:23','یہ وہی لوگ ہیں جن پر اللہ کی پھٹکار ہے اور جن کی سماعت اور آنکھوں کی روشنی چھین لی ہے. ');
INSERT INTO "junagarri" VALUES(47,24,'47:24','کیا یہ قران میں غور وفکر نہیں کرتے؟ یا ان کے دلوں پر تالے لگ گئے ہیں. ');
INSERT INTO "junagarri" VALUES(47,25,'47:25','جو لوگ اپنی پیٹھ کے بل الٹے پھر گئے اس کے بعد ان کے لئے ہدایت واضح ہو چکی یقیناً شیطان نے ان کے لئے (ان کے فعل کو) مزین کر دیا ہے اور انہیں ڈھیل دے رکھی ہے. ');
INSERT INTO "junagarri" VALUES(47,26,'47:26','یہ اس لئے کہ انہوں نے ان لوگوں سے جنہوں نے اللہ کی نازل کرده وحی کو برا سمجھا یہ کہا کہ ہم بھی عنقریب بعض کاموں میں تمہارا کہا مانیں گے، اور اللہ ان کی پوشیده باتیں خوب جانتا ہے. ');
INSERT INTO "junagarri" VALUES(47,27,'47:27','پس ان کی کیسی (درگت) ہوگی جبکہ فرشتے ان کی روح قبض کرتے ہوئے ان کے چہروں اور ان کی سرینوں پر ماریں گے. ');
INSERT INTO "junagarri" VALUES(47,28,'47:28','یہ اس بنا پر کہ یہ وه راه چلے جس سے انہوں نے اللہ کو ناراض کر دیا اور انہوں نے اس کی رضا مندی کو برا جانا، تو اللہ نے ان کے اعمال اکارت کر دیئے.');
INSERT INTO "junagarri" VALUES(47,29,'47:29','کیا ان لوگوں نے جن کے دلوں میں بیماری ہے یہ سمجھ رکھا ہے کہ اللہ ان کے کینوں کو ظاہر نہ کرے گا. ');
INSERT INTO "junagarri" VALUES(47,30,'47:30','اور اگر ہم چاہتے تو ان سب کو تجھے دکھا دیتے پس تو انہیں ان کے چہرے سے ہی پہچان لیتا ، اور یقیناً تو انہیں ان کی بات کے ڈھب سے پہچان لے گا ، تمہارے سب کام اللہ کو معلوم ہیں.');
INSERT INTO "junagarri" VALUES(47,31,'47:31','یقیناً ہم تمہارا امتحان کریں گے تاکہ تم میں سے جہاد کرنے والوں اور صبر کرنے والوں کو ظاہر کر دیں اور ہم تمہاری حالتوں کی بھی جانچ کر لیں. ');
INSERT INTO "junagarri" VALUES(47,32,'47:32','یقیناً جن لوگوں نے کفر کیا اور اللہ کی راه سے لوگوں کو روکا اور رسول کی مخالفت کی اس کے بعد کہ ان کے لئے ہدایت ظاہر ہو چکی یہ ہرگز ہرگز اللہ کا کچھ نقصان نہ کر سکیں گے ۔ عنقریب ان کے اعمال وه غارت کردے گا. ');
INSERT INTO "junagarri" VALUES(47,33,'47:33','اے ایمان والو! اللہ کی اطاعت کرو اور رسول کا کہا مانو اور اپنے اعمال کو غارت نہ کرو. ');
INSERT INTO "junagarri" VALUES(47,34,'47:34','جن لوگوں نے کفر کیا اور اللہ کی راه سے اوروں کو روکا پھر کفر کی حالت میں ہی مرگئے (یقین کر لو) کہ اللہ انہیں ہرگز نہ بخشے گا.');
INSERT INTO "junagarri" VALUES(47,35,'47:35','پس تم بودے بن کر صلح کی درخواست پر نہ اتر آؤ جبکہ تم ہی بلند وغالب رہو گے اور اللہ تمہارے ساتھ ہے ، ناممکن ہے کہ وه تمہارے اعمال ضائع کر دے. ');
INSERT INTO "junagarri" VALUES(47,36,'47:36','واقعی زندگانیٴ دنیا تو صرف کھیل کود ہے اور اگر تم ایمان لے آؤ گے اور تقویٰ اختیار کرو گے تو اللہ تمہیں تمہارے اجر دے گا اور وه تم سے تمہارے مال نہیں مانگتا. ');
INSERT INTO "junagarri" VALUES(47,37,'47:37','اگر وه تم سے تمہارا مال مانگے اور زور دے کر مانگے تو تم اس سے بخیلی کرنے لگو گے اور وه تمہارے کینے ظاہر کر دے گا. ');
INSERT INTO "junagarri" VALUES(47,38,'47:38','خبردار! تم وه لوگ ہو کہ اللہ کی راه میں خرچ کرنے کے لئے بلائے جاتے ہو ، تو تم میں سے بعض بخیلی کرنے لگتے ہیں اور جو بخل کرتا ہے وه تو دراصل اپنی جان سے بخیلی کرتا ہے ۔ اللہ تعالیٰ غنی ہے اور تم فقیر (اور محتاج) ہو اور اگر تم روگردان ہو جاؤ تو وه تمہارے بدلے تمہارے سوا اور لوگوں کو لائے گا جو پھر تم جیسے نہ ہوں گے. ');
INSERT INTO "junagarri" VALUES(48,1,'48:1','بیشک (اے نبی) ہم نے آپ کو ایک کھلم کھلا فتح دی ہے.*');
INSERT INTO "junagarri" VALUES(48,2,'48:2','تاکہ جو کچھ تیرے گناه آگے ہوئے اور جو پیچھے سب کو اللہ تعالیٰ معاف فرمائے ، اور تجھ پر اپنا احسان پورا کر دے اور تجھے سیدھی راه چلائے. ');
INSERT INTO "junagarri" VALUES(48,3,'48:3','اور آپ کو ایک زبردست مدد دے.');
INSERT INTO "junagarri" VALUES(48,4,'48:4','وہی ہے جس نے مسلمانوں کے دلوں میں سکون (اور اطیمنان) ڈال دیا تاکہ اپنے ایمان کے ساتھ ہی ساتھ اور بھی ایمان میں بڑھ جائیں ، اور آسمانوں اور زمین کے ﴿کل﴾ لشکر اللہ ہی کے ہیں ۔ اور اللہ تعالیٰ دانا باحکمت ہے.');
INSERT INTO "junagarri" VALUES(48,5,'48:5','تاکہ مومن مردوں اور عورتوں کو ان جنتوں میں لے جائے جن کے نیچے نہریں بہ رہی ہیں جہاں وه ہمیشہ رہیں گے اور ان سے ان کے گناه دور کر دے، اور اللہ کے نزدیک یہ بہت بڑی کامیابی ہے.');
INSERT INTO "junagarri" VALUES(48,6,'48:6','اور تاکہ ان منافق مردوں اور منافق عورتوں اور مشرک مردوں اور مشرکہ عورتوں کو عذاب دے جو اللہ تعالیٰ کے بارے میں بدگمانیاں رکھنے والے ہیں ، (دراصل) انہیں پر برائی کا پھیرا ہے ، اللہ ان پر ناراض ہوا اور انہیں لعنت کی اور ان کے لئے دوزخ تیار کی اور وه (بہت) بری لوٹنے کی جگہ ہے.');
INSERT INTO "junagarri" VALUES(48,7,'48:7','اور اللہ ہی کے لئے آسمانوں اور زمین کے لشکر ہیں اور اللہ غالب اور حکمت والا ہے. ');
INSERT INTO "junagarri" VALUES(48,8,'48:8','یقیناً ہم نے تجھے گواہی دینے والا اور خوشخبری سنانے والا اور ڈرانے والا بنا کر بھیجا ہے.');
INSERT INTO "junagarri" VALUES(48,9,'48:9','تاکہ (اے مسلمانو)، تم اللہ اور اس کے رسول پر ایمان لاؤ اور اس کی مدد کرو اور اس کا ادب کرو اور اللہ کی پاکی بیان کرو صبح وشام.');
INSERT INTO "junagarri" VALUES(48,10,'48:10','جو لوگ تجھ سے بیعت کرتے ہیں وه یقیناً اللہ سے بیعت کرتے ہیں ، ان کے ہاتھوں پر اللہ کا ہاتھ ہے ، تو جو شخص عہد شکنی کرے وه اپنے نفس پر ہی عہد شکنی کرتا ہے اور جو شخص اس اقرار کو پورا کرے جو اس نے اللہ کے ساتھ کیا ہے تو اسے عنقریب اللہ بہت بڑا اجر دے گا.');
INSERT INTO "junagarri" VALUES(48,11,'48:11','دیہاتیوں میں سے جو لوگ پیچھے چھوڑ دیئے گئے تھے وه اب تجھ سے کہیں گے کہ ہم اپنے مال اور بال بچوں میں لگے ره گئے پس آپ ہمارے لئے مغفرت طلب کیجئے ۔ یہ لوگ اپنی زبانوں سے وه کہتے ہیں جو ان کے دلوں میں نہیں ہے ۔ آپ جواب دے دیجئے کہ تمہارے لئے اللہ کی طرف سے کسی چیز کا بھی اختیار کون رکھتا ہے اگر وه تمہیں نقصان پہنچانا چاہے تو یا تمہیں کوئی نفع دینا چاہے تو ، بلکہ تم جو کچھ کر رہے ہو اس سے اللہ خوب باخبر ہے. ');
INSERT INTO "junagarri" VALUES(48,12,'48:12','(نہیں) بلکہ تم نے تو یہ گمان کر رکھا تھا کہ پیغمبر اور مسلمانوں کا اپنے گھروں کی طرف لوٹ آنا قطعاً ناممکن ہے اور یہی خیال تمہارے دلوں میں رچ بس گیا تھا اور تم نے برا گمان کر رکھا تھا ۔ دراصل تم لوگ ہو بھی ہلاک ہونے والے. ');
INSERT INTO "junagarri" VALUES(48,13,'48:13','اور جو شخص اللہ پر اور اس کے رسول پر ایمان نہ لائے تو ہم نے بھی ایسے کافروں کے لئے دہکتی آگ تیار کر رکھی ہے.');
INSERT INTO "junagarri" VALUES(48,14,'48:14','اور زمین اور آسمانوں کی بادشاہت اللہ ہی کے لئے ہے جسے چاہے بخشے اور جسے چاہے عذاب کرے۔ اور اللہ بڑا بخشنے والا مہربان ہے. ');
INSERT INTO "junagarri" VALUES(48,15,'48:15','جب تم غنیمتیں لینے جانے لگو گے تو جھٹ سے یہ پیچھے چھوڑے ہوئے لوگ کہنے لگیں گے کہ ہمیں بھی اپنے ساتھ چلنے کی اجازت دیجئے ، وه چاہتے ہیں کہ اللہ تعالیٰ کے کلام کو بدل دیں آپ کہہ دیجئے! کہ اللہ تعالیٰ پہلے ہی فرما چکا ہے کہ تم ہرگز ہمارے ساتھ نہیں چلو گے ، وه اس کا جواب دیں گے (نہیں نہیں) بلکہ تم ہم سے حسد کرتے ہو ، (اصل بات یہ ہے) کہ وه لوگ بہت ہی کم سمجھتے ہیں. ');
INSERT INTO "junagarri" VALUES(48,16,'48:16','آپ پیچھے چھوڑے ہوئے بدویوں سے کہہ دو کہ عنقریب تم ایک سخت جنگجو قوم کی طرف بلائے جاؤ گے کہ تم ان سے لڑوگے یا وه مسلمان ہوجائیں گے پس اگر تم اطاعت کرو گے تو اللہ تمہیں بہت بہتر بدلہ دے گا اور اگر تم نے منھ پھیر لیا جیسا کہ اس سے پہلے تم منھ پھیر چکے ہو تو وه تمہیں دردناک عذاب دے گا. ');
INSERT INTO "junagarri" VALUES(48,17,'48:17','اندھے پر کوئی حرج نہیں ہے اور نہ لنگڑے پر کوئی حرج ہے اور نہ بیمار پر کوئی حرج ہے، جو کوئی اللہ اور اس کے رسول کی فرمانبرداری کرے اسے اللہ ایسی جنتوں میں داخل کرے گا جس کے (درختوں) تلے نہریں جاری ہیں اور جو منھ پھیر لے اسے دردناک عذاب (کی سزا) دے گا.');
INSERT INTO "junagarri" VALUES(48,18,'48:18','یقیناً اللہ تعالیٰ مومنوں سے خوش ہوگیا جبکہ وه درخت تلے تجھ سے بیعت کر رہے تھے ۔ ان کے دلوں میں جو تھا اسے اس نے معلوم کر لیا اور ان پر اطمینان نازل فرمایا اور انہیں قریب کی فتح عنایت فرمائی. ');
INSERT INTO "junagarri" VALUES(48,19,'48:19','اور بہت سی غنیمتیں جنہیں وه حاصل کریں گے اور اللہ غالب حکمت والا ہے.');
INSERT INTO "junagarri" VALUES(48,20,'48:20','اللہ تعالیٰ نے تم سے بہت ساری غنیمتوں کا وعده کیا ہے جنہیں تم حاصل کرو گے پس یہ تو تمہیں جلدی ہی عطا فرما دی اور لوگوں کے ہاتھ تم سے روک دیئے ، تاکہ مومنوں کے لئے یہ ایک نشانی ہو جائے اور (تاکہ) وه تمہیں سیدھی راه چلائے. ');
INSERT INTO "junagarri" VALUES(48,21,'48:21','اور تمہیں اور (غنیمتیں) بھی دے جن پر اب تک تم نے قابو نہیں پایا۔ اللہ تعالیٰ نے انہیں اپنے قابو میں رکھا ہے اور اللہ تعالیٰ ہر چیز پر قادر ہے.');
INSERT INTO "junagarri" VALUES(48,22,'48:22','اور اگر تم سے کافر جنگ کرتے تو یقیناً پیٹھ دکھا کر بھاگتے پھر نہ تو کوئی کار ساز پاتے نہ مددگار. ');
INSERT INTO "junagarri" VALUES(48,23,'48:23','اللہ کے اس قاعدے کے مطابق جو پہلے سے چلا آیا ہے ، تو کبھی بھی اللہ کے قاعدے کو بدلتا ہوا نہ پائے گا.');
INSERT INTO "junagarri" VALUES(48,24,'48:24','وہی ہے جس نے خاص مکہ میں کافروں کے ہاتھوں کو تم سے اور تمہارے ہاتھوں کو ان سے روک لیا اس کے بعد کہ اس نے تمہیں ان پر غلبہ دے دیا تھا ، اور تم جو کچھ کر رہے ہو اللہ تعالیٰ اسے دیکھ رہا ہے.');
INSERT INTO "junagarri" VALUES(48,25,'48:25','یہی وه لوگ ہیں جنہوں نے کفر کیا اور تم کو مسجد حرام سے روکا اور قربانی کے لئے موقوف جانور کو اس کی قربان گاه میں پہنچنے سے (روکا) ، اور اگر ایسے (بہت سے) مسلمان مرد اور (بہت سی) مسلمان عورتیں نہ ہوتیں جن کی تم کو خبر نہ تھ یعنی ان کے پس جانے کا احتمال نہ ہوتا جس پر ان کی وجہ سے تم کو بھی بے خبری میں ضرر پہنچتا ، (تو تمہیں لڑنے کی اجازت دے دی جاتی لیکن ایسا نہیں کیا گیا ) تاکہ اللہ تعالیٰ اپنی رحمت میں جس کو چاہے داخل کرے اور اگر یہ الگ الگ ہوتے تو ان میں جو کافر تھے ہم ان کو درد ناک سزا دیتے. ');
INSERT INTO "junagarri" VALUES(48,26,'48:26','جب کہ ان کافروں نے اپنے دلوں میں حمیت کو جگہ دی اور حمیت بھی جاہلیت کی، سو اللہ تعالیٰ نے اپنے رسول پر اور مومنین پر اپنی طرف سے تسکین نازل فرمائی اور اللہ تعالیٰ نے مسلمانوں کو تقوے کی بات پر جمائے رکھا اور وه اس کے اہل اور زیاده مستحق تھے اور اللہ تعالیٰ ہر چیز کو خوب جانتا ہے.');
INSERT INTO "junagarri" VALUES(48,27,'48:27','یقیناً اللہ تعالیٰ نے اپنے رسول کو خواب سچا دکھایا کہ انشاءاللہ تم یقیناً پورے امن وامان کے ساتھ مسجد حرام میں داخل ہوگے سرمنڈواتے ہوئے اور سر کے بال کترواتے ہوئے (چین کے ساتھ) نڈر ہو کر ، وه ان امور کو جانتا ہے جنہیں تم نہیں جانتے ، پس اس نے اس سے پہلے ایک نزدیک کی فتح تمہیں میسر کی. ');
INSERT INTO "junagarri" VALUES(48,28,'48:28','وہی ہے جس نےاپنے رسول کو ہدایت اور دین حق کے ساتھ بھیجا تاکہ اسے ہر دین پر غالب کرے ، اور اللہ تعالیٰ کافی ہے گواہی دینے والا.');
INSERT INTO "junagarri" VALUES(48,29,'48:29','محمد (صلی اللہ علیہ وسلم) اللہ کے رسول ہیں اور جو لوگ ان کے ساتھ کافروں پر سخت ہیں آپس میں رحمدل ہیں، تو انہیں دیکھے گا کہ رکوع اور سجدے کر رہے ہیں اللہ تعالیٰ کے فضل اور رضامندی کی جستجو میں ہیں، ان کا نشان ان کے چہروں پر سجدوں کے اثر سے ہے، ان کی یہی مثال تورات میں ہے اور ان کی مثال انجیل میں ہے ، مثل اسی کھیتی کے جس نے اپنا انکھوا نکالا پھر اسے مضبوط کیا اور وه موٹا ہوگیا پھر اپنے تنے پر سیدھا کھڑا ہوگیا اور کسانوں کو خوش کرنے لگا تاکہ ان کی وجہ سے کافروں کو چڑائے ، ان ایمان والوں اور نیک اعمال والوں سے اللہ نے بخشش کا اور بہت بڑے ثواب کا وعده کیا ہے. ');
INSERT INTO "junagarri" VALUES(49,1,'49:1','اے ایمان والے لوگو! اللہ اور اس کے رسول سے آگے نہ بڑھو اور اللہ سے ڈرتے رہا کرو۔ یقیناً اللہ تعالیٰ سننے والا، جاننے والا ہے.');
INSERT INTO "junagarri" VALUES(49,2,'49:2','اے ایمان والو! اپنی آوازیں نبی کی آواز سے اوپر نہ کرو اور نہ ان سے اونچی آواز سے بات کرو جیسے آپس میں ایک دوسرے سے کرتے ہو، کہیں (ایسا نہ ہو کہ) تمہارے اعمال اکارت جائیں اور تمہیں خبر بھی نہ ہو. ');
INSERT INTO "junagarri" VALUES(49,3,'49:3','بیشک جو لوگ رسول اللہ (صلی اللہ علیہ وسلم) کے حضور میں اپنی آوازیں پست رکھتے ہیں، یہی وه لوگ ہیں جن کے دلوں کو اللہ نے پرہیزگاری کے لئے جانچ لیا ہے۔ ان کے لئے مغفرت ہے اور بڑا ثواب ہے. ');
INSERT INTO "junagarri" VALUES(49,4,'49:4','جو لوگ آپ کو حجروں کے پیچھے سے پکارتے ہیں ان میں سے اکثر (بالکل) بے عقل ہیں. ');
INSERT INTO "junagarri" VALUES(49,5,'49:5','اگر یہ لوگ یہاں تک صبر کرتے کہ آپ خود سے نکل کر ان کے پاس آجاتے تو یہی ان کے لئے بہتر ہوتا ، اور اللہ غفور ورحیم ہے. ');
INSERT INTO "junagarri" VALUES(49,6,'49:6','اے مسلمانو! اگر تمہیں کوئی فاسق خبر دے تو تم اس کی اچھی طرح تحقیق کر لیا کرو ایسا نہ ہو کہ نادانی میں کسی قوم کو ایذا پہنچا دو پھر اپنے کیے پر پشیمانی اٹھاؤ.');
INSERT INTO "junagarri" VALUES(49,7,'49:7','اور جان رکھو کہ تم میں اللہ کے رسول موجود ہیں ، اگر وه تمہارا کہا کرتے رہے بہت امور میں، تو تم مشکل میں پڑجاؤ لیکن اللہ تعالیٰ نے ایمان کو تمہارے لئے محبوب بنا دیا ہے اور اسے تمہارے دلوں میں زینت دے رکھی ہے اور کفر کو اور گناه کو اور نافرمانی کو تمہارے نگاہوں میں ناپسندیده بنا دیا ہے، یہی لوگ راه یافتہ ہیں.');
INSERT INTO "junagarri" VALUES(49,8,'49:8','اللہ کے احسان وانعام سے اور اللہ دانا اور باحکمت ہے.');
INSERT INTO "junagarri" VALUES(49,9,'49:9','اور اگر مسلمانوں کی دو جماعتیں آپس میں لڑ پڑیں تو ان میں میل ملاپ کرا دیا کرو ۔ پھر اگر ان دونوں میں سے ایک جماعت دوسری جماعت پر زیادتی کرے تو تم (سب) اس گروه سے جو زیادتی کرتا ہے لڑو۔ یہاں تک کہ وه اللہ کے حکم کی طرف لوٹ آئے ، اگر لوٹ آئے تو پھر انصاف کے ساتھ صلح کرا دو اور عدل کرو بیشک اللہ تعالیٰ انصاف کرنے والوں سے محبت کرتا ہے. ');
INSERT INTO "junagarri" VALUES(49,10,'49:10','(یاد رکھو) سارے مسلمان بھائی بھائی ہیں پس اپنے دو بھائیوں میں ملاپ کرا دیا کرو ، اور اللہ سے ڈرتے رہو تاکہ تم پر رحم کیا جائے. ');
INSERT INTO "junagarri" VALUES(49,11,'49:11','اے ایمان والو! مرد دوسرے مردوں کا مذاق نہ اڑائیں ممکن ہے کہ یہ ان سے بہتر ہو اور نہ عورتیں عورتوں کا مذاق اڑائیں ممکن ہے یہ ان سے بہتر ہوں ، اور آپس میں ایک دوسرے کو عیب نہ لگاؤ اور نہ کسی کو برے لقب دو ۔ ایمان کے بعد فسق برا نام ہے ، اور جو توبہ نہ کریں وہی ظالم لوگ ہیں.');
INSERT INTO "junagarri" VALUES(49,12,'49:12','اے ایمان والو! بہت بدگمانیوں سے بچو یقین مانو کہ بعض بدگمانیاں گناه ہیں ۔ اور بھید نہ ٹٹولا کرو اور نہ تم میں سے کوئی کسی کی غیبت کرے ۔ کیا تم میں سے کوئی بھی اپنے مرده بھائی کا گوشت کھانا پسند کرتا ہے؟ تم کو اس سے گھن آئے گی ، اور اللہ سے ڈرتے رہو، بیشک اللہ توبہ قبول کرنے والا مہربان ہے.');
INSERT INTO "junagarri" VALUES(49,13,'49:13','اے لوگو! ہم نے تم سب کو ایک (ہی) مرد وعورت سے پیدا کیا ہے اور اس لئے کہ تم آپس میں ایک دوسرے کو پہچانو کنبے اور قبیلے بنا دیئے ہیں، اللہ کے نزدیک تم سب میں باعزت وه ہے جو سب سے زیاده ڈرنے والا ہے ۔ یقین مانو کہ اللہ دانا اور باخبر ہے.');
INSERT INTO "junagarri" VALUES(49,14,'49:14','دیہاتی لوگ کہتے ہیں کہ ہم ایمان لائے۔ آپ کہہ دیجئے کہ درحقیقت تم ایمان نہیں لائے لیکن تم یوں کہو کہ ہم اسلام لائے (مخالفت چھوڑ کر مطیع ہوگئے) حالانکہ ابھی تک تمہارے دلوں میں ایمان داخل ہی نہیں ہوا ۔ تم اگر اللہ کی اور اس کے رسول کی فرمانبرداری کرنے لگو گے تو اللہ تمہارے اعمال میں سے کچھ بھی کم نہ کرے گا۔ بیشک اللہ بخشنے والا مہربان ہے.');
INSERT INTO "junagarri" VALUES(49,15,'49:15','مومن تو وه ہیں جو اللہ اور اس کے رسول پر (پکا) ایمان لائیں پھر شک وشبہ نہ کریں اور اپنے مالوں سے اور اپنی جانوں سے اللہ کی راه میں جہاد کرتے رہیں، (اپنے دعوائے ایمان میں) یہی سچے اور راست گو ہیں. ');
INSERT INTO "junagarri" VALUES(49,16,'49:16','کہہ دیجئے! کہ کیا تم اللہ تعالیٰ کو اپنی دینداری سے آگاه کر رہے ہو ، اللہ ہر اس چیز سے جو آسمانوں میں اور زمین میں ہے بخوبی آگاه ہے۔ اور اللہ ہر چیز کا جاننے والا ہے. ');
INSERT INTO "junagarri" VALUES(49,17,'49:17','اپنے مسلمان ہونے کا آپ پر احسان جتاتے ہیں۔ آپ کہہ دیجئے کہ اپنے مسلمان ہونے کا احسان مجھ پر نہ رکھو، بلکہ دراصل اللہ کا تم پر احسان ہے کہ اس نے تمہیں ایمان کی ہدایت کی اگر تم راست گو ہو. ');
INSERT INTO "junagarri" VALUES(49,18,'49:18','یقین مانو کہ آسمانوں اور زمین کی پوشیده باتیں اللہ خوب جانتا ہے۔ اور جو کچھ تم کر رہے ہو اسے اللہ خوب دیکھ رہا ہے.');
INSERT INTO "junagarri" VALUES(50,1,'50:1','ق! بہت بڑی شان والے اس قرآن کی قسم ہے. ');
INSERT INTO "junagarri" VALUES(50,2,'50:2','بلکہ انہیں تعجب معلوم ہوا کہ ان کے پاس انہی میں سے ایک آگاه کرنے والا آیا تو کافروں نے کہا کہ یہ ایک عجیب چیز ہے. ');
INSERT INTO "junagarri" VALUES(50,3,'50:3','کیا جب ہم مر کر مٹی ہو جائیں گے۔ پھر یہ واپسی دور (از عقل) ہے. ');
INSERT INTO "junagarri" VALUES(50,4,'50:4','زمین جو کچھ ان سے گھٹاتی ہے وه ہمیں معلوم ہے اور ہمارے پاس سب یاد رکھنے والی کتاب ہے. ');
INSERT INTO "junagarri" VALUES(50,5,'50:5','بلکہ انہوں نے سچی بات کو جھوٹ کہا ہے جبکہ وه ان کے پاس پہنچ چکی پس وه ایک الجھاؤ میں پڑ گئے ہیں. ');
INSERT INTO "junagarri" VALUES(50,6,'50:6','کیا انہوں نے آسمان کو اپنے اوپر نہیں دیکھا؟ کہ ہم نے اسے کس طرح بنایا ہے اور زینت دی ہے اس میں کوئی شگاف نہیں. ');
INSERT INTO "junagarri" VALUES(50,7,'50:7','اور زمین کو ہم نے بچھا دیا اور اس میں ہم نے پہاڑ ڈال دیئے ہیں اور اس میں ہم نے قسم قسم کی خوشنما چیزیں اگا دی ہیں. ');
INSERT INTO "junagarri" VALUES(50,8,'50:8','تاکہ ہر رجوع کرنے والے بندے کے لئے بینائی اور دانائی کا ذریعہ ہو. ');
INSERT INTO "junagarri" VALUES(50,9,'50:9','اور ہم نے آسمان سے بابرکت پانی برسایا اور اس سے باغات اور کٹنے والے کھیت کے غلے پیدا کیے. ');
INSERT INTO "junagarri" VALUES(50,10,'50:10','اور کھجوروں کے بلند وبالا درخت جن کے خوشے تہ بہ تہ ہیں. ');
INSERT INTO "junagarri" VALUES(50,11,'50:11','بندوں کی روزی کے لئے اور ہم نے پانی سے مرده شہر کو زنده کر دیا۔ اسی طرح (قبروں سے) نکلنا ہے. ');
INSERT INTO "junagarri" VALUES(50,12,'50:12','ان سے پہلے نوح کی قوم نے اور رس والوں نے اور ثمود نے.');
INSERT INTO "junagarri" VALUES(50,13,'50:13','اور عاد نے فرعون نے اور برادران لوط نے.');
INSERT INTO "junagarri" VALUES(50,14,'50:14','اور ایکہ والوں نے تبع کی قوم نے بھی تکذیب کی تھی۔ سب نے پیغمبروں کو جھٹلایا پس میرا وعدہٴ عذاب ان پر صادق آگیا.');
INSERT INTO "junagarri" VALUES(50,15,'50:15','کیا ہم پہلی بار کے پیدا کرنے سے تھک گئے ؟ بلکہ یہ لوگ نئی پیدائش کی طرف سے شک میں ہیں. ');
INSERT INTO "junagarri" VALUES(50,16,'50:16','ہم نے انسان کو پیدا کیا ہے اور اس کے دل میں جو خیالات اٹھتے ہیں ان سے ہم واقف ہیں اور ہم اس کی رگ جان سے بھی زیاده اس سے قریب ہیں. ');
INSERT INTO "junagarri" VALUES(50,17,'50:17','جس وقت دو لینے والے جا لیتے ہیں ایک دائیں طرف اور ایک بائیں طرف بیٹھا ہوا.');
INSERT INTO "junagarri" VALUES(50,18,'50:18','(انسان) منھ سے کوئی لفظ نکال نہیں پاتا مگر کہ اس کے پاس نگہبان تیار ہے. ');
INSERT INTO "junagarri" VALUES(50,19,'50:19','اور موت کی بے ہوشی حق لے کر آ پہنچی، یہی ہے جس سے تو بدکتا پھرتا تھا. ');
INSERT INTO "junagarri" VALUES(50,20,'50:20','اور صور پھونک دیا جائے گا۔ وعدہٴ عذاب کا دن یہی ہے.');
INSERT INTO "junagarri" VALUES(50,21,'50:21','اور ہر شخص اس طرح آئے گا کہ اس کے ساتھ ایک لانے والا ہوگا اور ایک گواہی دینے والا. ');
INSERT INTO "junagarri" VALUES(50,22,'50:22','یقیناً تو اس سے غفلت میں تھا لیکن ہم نے تیرے سامنے سے پرده ہٹا دیا پس آج تیری نگاه بہت تیز ہے.');
INSERT INTO "junagarri" VALUES(50,23,'50:23','اس کا ہم نشین (فرشتہ) کہے گا یہ حاضر ہے جو کہ میرے پاس تھا. ');
INSERT INTO "junagarri" VALUES(50,24,'50:24','ڈال دو جہنم میں ہر کافر سرکش کو.');
INSERT INTO "junagarri" VALUES(50,25,'50:25','جو نیک کام سے روکنے والا حد سے گزر جانے والا اور شک کرنے والا تھا.');
INSERT INTO "junagarri" VALUES(50,26,'50:26','جس نے اللہ کے ساتھ دوسرا معبود بنا لیا تھا پس اسے سخت عذاب میں ڈال دو. ');
INSERT INTO "junagarri" VALUES(50,27,'50:27','اس کا ہمنشین (شیطان) کہے گا اے ہمارے رب! میں نے اسے گمراه نہیں کیا تھا بلکہ یہ خود ہی دور دراز کی گمراہی میں تھا. ');
INSERT INTO "junagarri" VALUES(50,28,'50:28','حق تعالیٰ فرمائے گا بس میرے سامنے جھگڑنے کی بات مت کرو میں تو پہلے ہی تمہاری طرف وعید (وعدہٴ عذاب) بھیج چکا تھا. ');
INSERT INTO "junagarri" VALUES(50,29,'50:29','میرے ہاں بات بدلتی نہیں اور نہ میں اپنے بندوں پر ذرا بھی ظلم کرنے والا ہوں. ');
INSERT INTO "junagarri" VALUES(50,30,'50:30','جس دن ہم دوزخ سے پوچھیں گے کیا تو بھر چکی؟ وه جواب دے گی کیا کچھ اور زیاده بھی ہے؟ ');
INSERT INTO "junagarri" VALUES(50,31,'50:31','اور جنت پرہیزگاروں کے لئے بالکل قریب کر دی جائے گی ذرا بھی دور نہ ہوگی. ');
INSERT INTO "junagarri" VALUES(50,32,'50:32','یہ ہے جس کا تم سے وعده کیا جاتا تھا ہر اس شخص کے لئے جو رجوع کرنے والا اور پابندی کرنے والا ہو. ');
INSERT INTO "junagarri" VALUES(50,33,'50:33','جو رحمٰن کا غائبانہ خوف رکھتا ہو اور توجہ والا دل لایا ہو. ');
INSERT INTO "junagarri" VALUES(50,34,'50:34','تم اس جنت میں سلامتی کے ساتھ داخل ہو جاؤ۔ یہ ہمیشہ رہنے کا دن ہے');
INSERT INTO "junagarri" VALUES(50,35,'50:35','یہ وہاں جو چاہیں انھیں ملے گا (بلکہ) ہمارے پاس اور بھی زیاده ہے. ');
INSERT INTO "junagarri" VALUES(50,36,'50:36','اور ان سے پہلے بھی ہم بہت سی امتوں کو ہلاک کر چکے ہیں جو ان سے طاقت میں بہت زیاده تھیں وه شہروں میں ڈھونڈھتے ہی ره گئے، کہ کوئی بھاگنے کا ٹھکانہ ہے؟');
INSERT INTO "junagarri" VALUES(50,37,'50:37','اس میں ہر صاحب دل کے لئے عبرت ہے اور اس کے لئے جو دل سے متوجہ ہو کر کان لگائے اور وه حاضر ہو. ');
INSERT INTO "junagarri" VALUES(50,38,'50:38','یقیناً ہم نے آسمانوں اور زمین اور جو کچھ اس کے درمیان ہے سب کو (صرف) چھ دن میں پیدا کر دیا اور ہمیں تکان نے چھوا تک نہیں.');
INSERT INTO "junagarri" VALUES(50,39,'50:39','پس یہ جو کچھ کہتے ہیں آپ اس پر صبر کریں اور اپنے رب کی تسبیح تعریف کے ساتھ بیان کریں سورج نکلنے سے پہلے بھی اور سورج غروب ہونے سے پہلے بھی. ');
INSERT INTO "junagarri" VALUES(50,40,'50:40','اور رات کے وقت بھی تسبیح کریں اور نماز کے بعد بھی. ');
INSERT INTO "junagarri" VALUES(50,41,'50:41','اور سن رکھیں کہ جس دن ایک پکارنے والا قریب ہی کی جگہ سے پکارے گا. ');
INSERT INTO "junagarri" VALUES(50,42,'50:42','جس روز اس تند وتیز چیخ کو یقین کے ساتھ سن لیں گے، یہ دن ہوگا نکلنے کا. ');
INSERT INTO "junagarri" VALUES(50,43,'50:43','ہم ہی جلاتے ہیں اور ہم ہی مارتے ہیں اور ہماری ہی طرف لوٹ پھر کر آنا ہے. ');
INSERT INTO "junagarri" VALUES(50,44,'50:44','جس دن زمین پھٹ جائے گی اور یہ دوڑتے ہوئے (نکل پڑیں گے) یہ جمع کر لینا ہم پر بہت ہی آسان ہے.');
INSERT INTO "junagarri" VALUES(50,45,'50:45','یہ جو کچھ کہہ رہے ہیں ہم بخوبی جانتے ہیں اور آپ ان پر جبر کرنے والے نہیں ، تو آپ قرآن کے ذریعے انہیں سمجھاتے رہیں جو میرے وعید (ڈرانے کے وعدوں) سے ڈرتے ہیں. ');
INSERT INTO "junagarri" VALUES(51,1,'51:1','قسم ہے بکھیرنے والیوں کی اڑا کر. ');
INSERT INTO "junagarri" VALUES(51,2,'51:2','پھر اٹھانے والیاں بوجھ کو. ');
INSERT INTO "junagarri" VALUES(51,3,'51:3','پھر چلنے والیاں نرمی سے. ');
INSERT INTO "junagarri" VALUES(51,4,'51:4','پھر کام کو تقسیم کرنے والیاں. ');
INSERT INTO "junagarri" VALUES(51,5,'51:5','یقین مانو کہ تم سے جو وعدے کیے جاتے ہیں (سب) سچے ہیں.');
INSERT INTO "junagarri" VALUES(51,6,'51:6','اور بیشک انصاف ہونے والا ہے.');
INSERT INTO "junagarri" VALUES(51,7,'51:7','قسم ہے راہوں والے آسمان کی. ');
INSERT INTO "junagarri" VALUES(51,8,'51:8','یقیناً تم مختلف بات میں پڑے ہوئے ہو. ');
INSERT INTO "junagarri" VALUES(51,9,'51:9','اس سے وہی باز رکھا جاتا ہے جو پھیر دیا گیا ہو.');
INSERT INTO "junagarri" VALUES(51,10,'51:10','بے سند باتیں کرنے والے غارت کر دیئے گئے.');
INSERT INTO "junagarri" VALUES(51,11,'51:11','جو غفلت میں ہیں اور بھولے ہوئے ہیں.');
INSERT INTO "junagarri" VALUES(51,12,'51:12','پوچھتے ہیں کہ یوم جزا کب ہوگا؟');
INSERT INTO "junagarri" VALUES(51,13,'51:13','ہاں یہ وه دن ہے کہ یہ آگ پر تپائے جائیں گے. ');
INSERT INTO "junagarri" VALUES(51,14,'51:14','اپنی فتنہ پردازی کا مزه چکھو ، یہی ہے جس کی تم جلدی مچا رہے تھے.');
INSERT INTO "junagarri" VALUES(51,15,'51:15','بیشک تقویٰ والے لوگ بہشتوں اور چشموں میں ہوں گے.');
INSERT INTO "junagarri" VALUES(51,16,'51:16','ان کے رب نے جو کچھ انہیں عطا فرمایا ہے اسے لے رہے ہوں گے وه تو اس سے پہلے ہی نیکوکار تھے.');
INSERT INTO "junagarri" VALUES(51,17,'51:17','وه رات کو بہت کم سویا کرتے تھے. ');
INSERT INTO "junagarri" VALUES(51,18,'51:18','اور وقت سحر استغفار کیا کرتے تھے. ');
INSERT INTO "junagarri" VALUES(51,19,'51:19','اور ان کے مال میں مانگنے والوں کا اور سوال سے بچنے والوں کا حق تھا. ');
INSERT INTO "junagarri" VALUES(51,20,'51:20','اور یقین والوں کے لئے تو زمین میں بہت سی نشانیاں ہیں.');
INSERT INTO "junagarri" VALUES(51,21,'51:21','اور خود تمہاری ذات میں بھی، تو کیا تم دیکھتے نہیں ہو.');
INSERT INTO "junagarri" VALUES(51,22,'51:22','اور تمہاری روزی اور جو تم سے وعده کیا جاتا ہے سب آسمان میں ہے. ');
INSERT INTO "junagarri" VALUES(51,23,'51:23','آسمان وزمین کے پروردگار کی قسم! کہ یہ بالکل برحق ہے ایسا ہی جیسے کہ تم باتیں کرتے ہو.');
INSERT INTO "junagarri" VALUES(51,24,'51:24','کیا تجھے ابراہیم (علیہ السلام) کے معزز مہمانوں کی خبر بھی پہنچی ہے؟ ');
INSERT INTO "junagarri" VALUES(51,25,'51:25','وه جب ان کے ہاں آئے تو سلام کیا، ابراہیم نے جواب سلام دیا (اور کہا یہ تو) اجنبی لوگ ہیں. ');
INSERT INTO "junagarri" VALUES(51,26,'51:26','پھر (چپ چاپ جلدی جلدی) اپنے گھر والوں کی طرف گئے اور ایک فربہ بچھڑے (کا گوشت) لائے.');
INSERT INTO "junagarri" VALUES(51,27,'51:27','اور اسے ان کے پاس رکھا اور کہا آپ کھاتے کیوں نہیں. ');
INSERT INTO "junagarri" VALUES(51,28,'51:28','پھر تو دل ہی دل میں ان سے خوفزده ہوگئے انہوں نے کہا آپ خوف نہ کیجئے ۔ اور انہوں نے اس (حضرت ابراہیم) کو ایک علم والے لڑکے کی بشارت دی.');
INSERT INTO "junagarri" VALUES(51,29,'51:29','پس ان کی بیوی آگے بڑھی اور حیرت میں آکر اپنے منھ پر ہاتھ مار کر کہا کہ میں تو بوڑھیا ہوں اور ساتھ ہی بانجھ.');
INSERT INTO "junagarri" VALUES(51,30,'51:30','انہوں نے کہا ہاں تیرے پروردگار نے اسی طرح فرمایا ہے، بیشک وه حکیم وعلیم ہے. ');
INSERT INTO "junagarri" VALUES(51,31,'51:31','(حضرت ابرہیم علیہ السلام) نے کہا کہ اللہ کے بھیجے ہوئے (فرشتو!) تمہارا کیا مقصد ہے؟ ');
INSERT INTO "junagarri" VALUES(51,32,'51:32','انہوں نے جواب دیا کہ ہم گناه گار قوم کی طرف بھیجے گئے ہیں. ');
INSERT INTO "junagarri" VALUES(51,33,'51:33','تاکہ ہم ان پر مٹی کے کنکر برسائیں. ');
INSERT INTO "junagarri" VALUES(51,34,'51:34','جو تیرے رب کی طرف سے نشان زده ہیں، ان حد سے گزر جانے والوں کے لیے. ');
INSERT INTO "junagarri" VALUES(51,35,'51:35','پس جتنے ایمان والے وہاں تھے ہم نے انہیں نکال لیا. ');
INSERT INTO "junagarri" VALUES(51,36,'51:36','اور ہم نے وہاں مسلمانوں کا صرف ایک ہی گھر پایا. ');
INSERT INTO "junagarri" VALUES(51,37,'51:37','اور وہاں ہم نے ان کے لیے جو درد ناک عذاب کا ڈر رکھتے ہیں ایک (کامل) علامت چھوڑی. ');
INSERT INTO "junagarri" VALUES(51,38,'51:38','موسیٰ (علیہ السلام کے قصے) میں (بھی ہماری طرف سے تنبیہ ہے) کہ ہم نے اسے فرعون کی طرف کھلی دلیل دے کر بھیجا.');
INSERT INTO "junagarri" VALUES(51,39,'51:39','پس اس نےاپنے بل بوتے پر منھ موڑا اور کہنے لگا یہ جادوگر ہے یا دیوانہ ہے.');
INSERT INTO "junagarri" VALUES(51,40,'51:40','بالآخر ہم نے اسے اور اس کے لشکروں کو اپنے عذاب میں پکڑ کر دریا میں ڈال دیا وه تھا ہی ملامت کے قابل. ');
INSERT INTO "junagarri" VALUES(51,41,'51:41','اسی طرح عادیوں میں بھی (ہماری طرف سے تنبیہ ہے) جب کہ ہم نے ان پر خیر وبرکت سے خالی آندھی بھیجی.');
INSERT INTO "junagarri" VALUES(51,42,'51:42','وه جس جس چیز پر گرتی تھی اسے بوسیده ہدی کی طرح (چورا چورا) کردیتی تھی. ');
INSERT INTO "junagarri" VALUES(51,43,'51:43','اور ثمود (کے قصے) میں بھی (عبرت) ہے جب ان سے کہا گیا کہ تم کچھ دنوں تک فائده اٹھا لو. ');
INSERT INTO "junagarri" VALUES(51,44,'51:44','لیکن انہوں نے اپنے رب کے حکم سے سرتابی کی جس پر انہیں ان کے دیکھتے دیکھتے (تیز وتند) کڑاکے نے ہلاک کر دیا.');
INSERT INTO "junagarri" VALUES(51,45,'51:45','پس نہ تو وه کھڑے ہو سکے اور نہ بدلہ لے سکے. ');
INSERT INTO "junagarri" VALUES(51,46,'51:46','اور نوح (علیہ السلام) کی قوم کا بھی اس سے پہلے (یہی حال ہو چکا تھا) وه بھی بڑے نافرمان لوگ تھے. ');
INSERT INTO "junagarri" VALUES(51,47,'51:47','آسمان کو ہم نے (اپنے) ہاتھوں سے بنایا ہے اور یقیناً ہم کشادگی کرنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(51,48,'51:48','اور زمین کو ہم نے فرش بنا دیاہے پس ہم بہت ہی اچھے بچھانے والے ہیں.');
INSERT INTO "junagarri" VALUES(51,49,'51:49','اور ہر چیز کو ہم نے جوڑا جوڑا پیداکیا ہے تاکہ تم نصیحت حاصل کرو. ');
INSERT INTO "junagarri" VALUES(51,50,'51:50','پس تم اللہ کی طرف دوڑ بھاگ (یعنی رجوع) کرو ، یقیناً میں تمہیں اس کی طرف سے صاف صاف تنبیہ کرنے والا ہوں.');
INSERT INTO "junagarri" VALUES(51,51,'51:51','اور اللہ کے ساتھ کسی اور کو معبود نہ ٹھراؤ۔ بیشک میں تمہیں اس کی طرف سے کھلا ڈرانے والا ہوں. ');
INSERT INTO "junagarri" VALUES(51,52,'51:52','اسی طرح جو لوگ ان سے پہلے گزرے ہیں ان کے پاس جو بھی رسول آیا انہوں نے کہہ دیا کہ یا تو یہ جادوگر ہے یا دیوانہ ہے.');
INSERT INTO "junagarri" VALUES(51,53,'51:53','کیایہ اس بات کی ایک دوسرے کو وصیت کرتے گئے ہیں. ');
INSERT INTO "junagarri" VALUES(51,54,'51:54','(نہیں) بلکہ یہ سب کے سب سرکش ہیں ۔ تو آپ ان سے منھ پھیر لیں آپ پر کوئی ملامت نہیں.');
INSERT INTO "junagarri" VALUES(51,55,'51:55','اور نصیحت کرتے رہیں یقیناً یہ نصیحت ایمان والوں کو نفع دے گی. ');
INSERT INTO "junagarri" VALUES(51,56,'51:56','میں نے جنات اورانسانوں کو محض اسی لیے پیدا کیا ہے کہ وه صرف میری عبادت کریں. ');
INSERT INTO "junagarri" VALUES(51,57,'51:57','نہ میں ان سے روزی چاہتا ہوں نہ میری یہ چاہت ہے کہ یہ مجھے کھلائیں. ');
INSERT INTO "junagarri" VALUES(51,58,'51:58','اللہ تعالیٰ تو خود ہی سب کا روزی رساں توانائی والا اور زور آور ہے.');
INSERT INTO "junagarri" VALUES(51,59,'51:59','پس جن لوگوں نے ظلم کیا ہے انہیں بھی ان کے ساتھیوں کے حصہ کے مثل حصہ ملے گا ، لہٰذا وه مجھ سے جلدی طلب نہ کریں. ');
INSERT INTO "junagarri" VALUES(51,60,'51:60','پس خرابی ہے منکروں کو ان کے اس دن کی جس کا وعده دیئے جاتے ہیں.');
INSERT INTO "junagarri" VALUES(52,1,'52:1','قسم ہے طور کی. ');
INSERT INTO "junagarri" VALUES(52,2,'52:2','اور لکھی ہوئی کتاب کی. ');
INSERT INTO "junagarri" VALUES(52,3,'52:3','جو جھلی کے کھلے ہوئے ورق میں ہے. ');
INSERT INTO "junagarri" VALUES(52,4,'52:4','اورآباد گھر کی. ');
INSERT INTO "junagarri" VALUES(52,5,'52:5','اور اونچی چھت کی. ');
INSERT INTO "junagarri" VALUES(52,6,'52:6','اور بھڑکائے ہوئے سمندر کی. ');
INSERT INTO "junagarri" VALUES(52,7,'52:7','بیشک آپ کے رب کا عذاب ہو کر رہنے والا ہے.');
INSERT INTO "junagarri" VALUES(52,8,'52:8','اسے کوئی روکنے والا نہیں. ');
INSERT INTO "junagarri" VALUES(52,9,'52:9','جس دن آسمان تھرتھرانے لگے گا. ');
INSERT INTO "junagarri" VALUES(52,10,'52:10','اور پہاڑ چلنے پھرنے لگیں گے.');
INSERT INTO "junagarri" VALUES(52,11,'52:11','اس دن جھٹلانے والوں کو (پوری) خرابی ہے.');
INSERT INTO "junagarri" VALUES(52,12,'52:12','جو اپنی بیہوده گوئی میں اچھل کود کر رہے ہیں. ');
INSERT INTO "junagarri" VALUES(52,13,'52:13','جس دن وه دھکے دے دے کر آتش جہنم کی طرف لائیں جائیں گے.');
INSERT INTO "junagarri" VALUES(52,14,'52:14','یہی وه آتش دوزخ ہے جسے تم جھوٹ بتلاتے تھے. ');
INSERT INTO "junagarri" VALUES(52,15,'52:15','(اب بتاؤ) کیا یہ جادو ہے ؟ یا تم دیکھتے ہی نہیں ہو. ');
INSERT INTO "junagarri" VALUES(52,16,'52:16','جاؤ دوزخ میں اب تمہارا صبر کرنا اور نہ کرنا تمہارے لیے یکساں ہے تمہیں فقط تمہارے کیے کا بدلہ دیا جائے گا.');
INSERT INTO "junagarri" VALUES(52,17,'52:17','یقیناً پرہیزگار لوگ جنتوں میں اور نعمتوں میں ہیں. ');
INSERT INTO "junagarri" VALUES(52,18,'52:18','جو انہیں ان کے رب نے دے رکھی ہیں اس پر خوش خوش ہیں ، اوران کے پروردگار نے انہیں جہنم کے عذاب سے بھی بچا لیا ہے.');
INSERT INTO "junagarri" VALUES(52,19,'52:19','تم مزے سے کھاتے پیتے رہو ان اعمال کے بدلے جو تم کرتے تھے. ');
INSERT INTO "junagarri" VALUES(52,20,'52:20','برابر بچھے ہوئے شاندار تختے پر تکیے لگائے ہوئے ۔ اور ہم نے ان کے نکاح بڑی بڑی آنکھوں والی (حوروں) سے کر دیئے ہیں.');
INSERT INTO "junagarri" VALUES(52,21,'52:21','اور جو لوگ ایمان لائے اور ان کی اولاد نے بھی ایمان میں ان کی پیروی کی ہم ان کی اولاد کو ان تک پہنچا دیں گے اور ان کے عمل سے ہم کچھ کم نہ کریں گے ، ہر شخص اپنے اپنے اعمال کا گروی ہے. ');
INSERT INTO "junagarri" VALUES(52,22,'52:22','ہم ان کے لیے میوے اورمرغوب گوشت کی ریل پیل کردیں گے. ');
INSERT INTO "junagarri" VALUES(52,23,'52:23','(خوش طبعی کے ساتھ) ایک دوسرے سے جام (شراب) کی چھینا جھپٹی کریں گے جس شراب کے سرور میں تو بیہوده گوئی ہوگی نہ گناه. ');
INSERT INTO "junagarri" VALUES(52,24,'52:24','اور ان کے اردگرد ان کے نو عمر غلام چل پھر رہے ہوں گے، گویا کہ وه موتی تھے جو ڈھکے رکھے تھے. ');
INSERT INTO "junagarri" VALUES(52,25,'52:25','اور آپس میں ایک دوسرے کی طرف متوجہ ہو کر سوال کریں گے. ');
INSERT INTO "junagarri" VALUES(52,26,'52:26','کہیں گے کہ اس سے پہلے ہم اپنے گھر والوں کے درمیان بہت ڈرا کرتے تھے. ');
INSERT INTO "junagarri" VALUES(52,27,'52:27','پس اللہ تعالیٰ نے ہم پر بڑا احسان کیا اور ہمیں تیز وتند گرم ہواؤں کے عذاب سے بچا لیا. ');
INSERT INTO "junagarri" VALUES(52,28,'52:28','ہم اس سے پہلے ہی اس کی عبادت کیا کرتے تھے ، بیشک وه محسن اور مہربان ہے.');
INSERT INTO "junagarri" VALUES(52,29,'52:29','تو آپ سمجھاتے رہیں کیونکہ آپ اپنے رب کے فضل سے نہ تو کاہن ہیں نہ دیوانہ. ');
INSERT INTO "junagarri" VALUES(52,30,'52:30','کیا کافر یوں کہتے ہیں کہ یہ شاعر ہے ہم اس پر زمانے کے حوادث (یعنی موت) کا انتظار کر رہے ہیں. ');
INSERT INTO "junagarri" VALUES(52,31,'52:31','کہہ دیجئے! تم منتظر رہو میں بھی تمہارے ساتھ انتظار کرنے والوں میں ہوں. ');
INSERT INTO "junagarri" VALUES(52,32,'52:32','کیا ان کی عقلیں انہیں یہی سکھاتی ہیں ؟ یا یہ لوگ ہی سرکش ہیں. ');
INSERT INTO "junagarri" VALUES(52,33,'52:33','کیا یہ کہتے ہیں کہ اس نبی نے (قرآن) خود گھڑ لیا ہے، واقعہ یہ ہے کہ وه ایمان نہیں لاتے. ');
INSERT INTO "junagarri" VALUES(52,34,'52:34','اچھا اگر یہ سچے ہیں تو بھلا اس جیسی ایک (ہی) بات یہ (بھی) تو لے آئیں. ');
INSERT INTO "junagarri" VALUES(52,35,'52:35','کیا یہ بغیر کسی (پیدا کرنے والے) کے خود بخود پیدا ہوگئے ہیں ؟ یا یہ خود پیدا کرنے والے ہیں؟ ');
INSERT INTO "junagarri" VALUES(52,36,'52:36','کیا انہوں نے ہی آسمانوں اور زمین کو پیدا کیا ہے؟ بلکہ یہ یقین نہ کرنے والے لوگ ہیں. ');
INSERT INTO "junagarri" VALUES(52,37,'52:37','یا کیا ان کے پاس تیرے رب کے خزانے ہیں ؟ یا (ان خزانوں کے) یہ داروغہ ہیں. ');
INSERT INTO "junagarri" VALUES(52,38,'52:38','یا کیا ان کے پاس کوئی سیڑھی ہے جس پر چڑھ کر سنتے ہیں ؟ (اگر ایسا ہے) تو ان کا سننے والا کوئی روشن دلیل پیش کرے.');
INSERT INTO "junagarri" VALUES(52,39,'52:39','کیا اللہ کی تو سب لڑکیاں ہیں اور تمہارے ہاں لڑکے ہیں؟');
INSERT INTO "junagarri" VALUES(52,40,'52:40','کیا تو ان سے کوئی اجرت طلب کرتا ہے کہ یہ اس کے تاوان سے بوجھل ہو رہے ہیں. ');
INSERT INTO "junagarri" VALUES(52,41,'52:41','کیا ان کے پاس علم غیب ہے جسے یہ لکھ لیتے ہیں؟ ');
INSERT INTO "junagarri" VALUES(52,42,'52:42','کیا یہ لوگ کوئی فریب کرنا چاہتے ہیں ؟ تو یقین کرلیں کہ فریب خورده کافر ہی ہیں. ');
INSERT INTO "junagarri" VALUES(52,43,'52:43','کیا اللہ کے سوا ان کا کوئی معبود ہے؟ (ہرگز نہیں) اللہ تعالیٰ ان کے شرک سے پاک ہے.');
INSERT INTO "junagarri" VALUES(52,44,'52:44','اگر یہ لوگ آسمان کے کسی ٹکڑے کو گرتا ہوا دیکھ لیں تب بھی کہہ دیں کہ یہ تہ بہ تہ بادل ہے. ');
INSERT INTO "junagarri" VALUES(52,45,'52:45','تو انہیں چھوڑ دے یہاں تک کہ انہیں اس دن سے سابقہ پڑے جس میں یہ بے ہوش کر دیئے جائیں گے.');
INSERT INTO "junagarri" VALUES(52,46,'52:46','جس دن انہیں ان کا مکر کچھ کام نہ دے گا اور نہ وه مدد کیے جائیں گے.');
INSERT INTO "junagarri" VALUES(52,47,'52:47','بیشک ظالموں کے لیے اس کے علاوه اور عذاب بھی ہیں ۔ لیکن ان لوگوں میں سے اکثر بےعلم ہیں. ');
INSERT INTO "junagarri" VALUES(52,48,'52:48','تو اپنے رب کے حکم کے انتظار میں صبر سے کام لے، بیشک تو ہماری آنکھوں کے سامنے ہے۔ صبح کو جب تو اٹھے اپنے رب کی پاکی اور حمد بیان کر.');
INSERT INTO "junagarri" VALUES(52,49,'52:49','اور رات کو بھی اس کی تسبیح پڑھ اور ستاروں کے ڈوبتے وقت بھی. ');
INSERT INTO "junagarri" VALUES(53,1,'53:1','قسم ہے ستارے کی جب وه گرے. ');
INSERT INTO "junagarri" VALUES(53,2,'53:2','کہ تمہارے ساتھی نے نہ راه گم کی ہے نہ وه ٹیڑھی راه پر ہے. ');
INSERT INTO "junagarri" VALUES(53,3,'53:3','اور نہ وه اپنی خواہش سے کوئی بات کہتے ہیں.');
INSERT INTO "junagarri" VALUES(53,4,'53:4','وه تو صرف وحی ہے جو اتاری جاتی ہے. ');
INSERT INTO "junagarri" VALUES(53,5,'53:5','اسے پوری طاقت والے فرشتے نے سکھایا ہے.');
INSERT INTO "junagarri" VALUES(53,6,'53:6','جو زور آور ہے پھر وه سیدھا کھڑا ہو گیا. ');
INSERT INTO "junagarri" VALUES(53,7,'53:7','اور وه بلند آسمان کے کناروں پر تھا. ');
INSERT INTO "junagarri" VALUES(53,8,'53:8','پھر نزدیک ہوا اور اتر آیا. ');
INSERT INTO "junagarri" VALUES(53,9,'53:9','پس وه دو کمانوں کے بقدر فاصلہ ره گیا بلکہ اس سے بھی کم. ');
INSERT INTO "junagarri" VALUES(53,10,'53:10','پس اس نے اللہ کے بندے کو وحی پہنچائی جو بھی پہنچائی.');
INSERT INTO "junagarri" VALUES(53,11,'53:11','دل نے جھوٹ نہیں کہا جسے (پیغمبر نے) دیکھا. ');
INSERT INTO "junagarri" VALUES(53,12,'53:12','کیا تم جھگڑا کرتے ہو اس پر جو (پیغمبر) دیکھتے ہیں.');
INSERT INTO "junagarri" VALUES(53,13,'53:13','اسے تو ایک مرتبہ اور بھی دیکھا تھا.');
INSERT INTO "junagarri" VALUES(53,14,'53:14','سدرةالمنتہیٰ کے پاس. ');
INSERT INTO "junagarri" VALUES(53,15,'53:15','اسی کے پاس جنہ الماویٰ ہے. ');
INSERT INTO "junagarri" VALUES(53,16,'53:16','جب کہ سدره کو چھپائے لیتی تھی وه چیز جو اس پر چھا رہی تھی. ');
INSERT INTO "junagarri" VALUES(53,17,'53:17','نہ تو نگاه بہکی نہ حد سے بڑھی. ');
INSERT INTO "junagarri" VALUES(53,18,'53:18','یقیناً اس نے اپنے رب کی بڑی بڑی نشانیوں میں سے بعض نشانیاں دیکھ لیں. ');
INSERT INTO "junagarri" VALUES(53,19,'53:19','کیا تم نے لات اور عزیٰ کو دیکھا.');
INSERT INTO "junagarri" VALUES(53,20,'53:20','اور منات تیسرے پچھلے کو. ');
INSERT INTO "junagarri" VALUES(53,21,'53:21','کیا تمہارے لیے لڑکے اور اللہ کے لیے لڑکیاں ہیں؟ ');
INSERT INTO "junagarri" VALUES(53,22,'53:22','یہ تو اب بڑی بےانصافی کی تقسیم ہے. ');
INSERT INTO "junagarri" VALUES(53,23,'53:23','دراصل یہ صرف نام ہیں جو تم نے اور تمہارے باپ دادوں نے ان کے رکھ لیے ہیں اللہ نے ان کی کوئی دلیل نہیں اتاری۔ یہ لوگ تو صرف اٹکل کے اور اپنی نفسانی خواہشوں کے پیچھے پڑے ہوئے ہیں۔ اور یقیناً ان کے رب کی طرف سے ان کے پاس ہدایت آ چکی ہے.');
INSERT INTO "junagarri" VALUES(53,24,'53:24','کیا ہر شخص جو آرزو کرے اسے میسر ہے؟ ');
INSERT INTO "junagarri" VALUES(53,25,'53:25','اللہ ہی کے ہاتھ ہے یہ جہان اور وه جہان. ');
INSERT INTO "junagarri" VALUES(53,26,'53:26','اور بہت سے فرشتے آسمانوں میں ہیں جن کی سفارش کچھ بھی نفع نہیں دے سکتی مگر یہ اور بات ہے کہ اللہ تعالیٰ اپنی خوشی اور اپنی چاہت سے جس کے لیے چاہے اجازت دے دے. ');
INSERT INTO "junagarri" VALUES(53,27,'53:27','بیشک جو لوگ آخرت پر ایمان نہیں رکھتے وه فرشتوں کا زنانہ نام مقرر کرتے ہیں.');
INSERT INTO "junagarri" VALUES(53,28,'53:28','حالانکہ انہیں اس کا کوئی علم نہیں وه صرف اپنے گمان کے پیچھے پڑے ہوئے ہیں اور بیشک وہم (و گمان) حق کے مقابلے میں کچھ کام نہیں دیتا.');
INSERT INTO "junagarri" VALUES(53,29,'53:29','تو آپ اس سے منھ موڑ لیں جو ہماری یاد سے منھ موڑے اور جن کا اراده بجز زندگانیٴ دنیا کے اور کچھ نہ ہو.');
INSERT INTO "junagarri" VALUES(53,30,'53:30','یہی ان کے علم کی انتہا ہے۔ آپ کا رب اس سے خوب واقف ہے جو اس کی راه سے بھٹک گیا ہے اور وہی خوب واقف ہےاس سے بھی جو راه یافتہ ہے.');
INSERT INTO "junagarri" VALUES(53,31,'53:31','اور اللہ ہی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے تاکہ اللہ تعالیٰ برے عمل کرنے والوں کو ان کے اعمال کا بدلہ دے اور نیک کام کرنے والوں کو اچھا بدلہ عنایت فرمائے. ');
INSERT INTO "junagarri" VALUES(53,32,'53:32','ان لوگوں کو جو بڑے گناہوں سے بچتے ہیں اور بے حیائی سے بھی ۔ سوائے کسی چھوٹے سے گناه کے ۔ بیشک تیرا رب بہت کشاده مغفرت والا ہے، وه تمہیں بخوبی جانتا ہے جبکہ اس نے تمہیں زمین سے پیدا کیا اور جبکہ تم اپنی ماؤں کے پیٹ میں بچے تھے ۔ پس تم اپنی پاکیزگی آپ بیان نہ کرو ، وہی پرہیزگاروں کو خوب جانتا ہے.');
INSERT INTO "junagarri" VALUES(53,33,'53:33','کیا آپ نے اسے دیکھا جس نے منھ موڑ لیا.');
INSERT INTO "junagarri" VALUES(53,34,'53:34','اور بہت کم دیا اور ہاتھ روک لیا. ');
INSERT INTO "junagarri" VALUES(53,35,'53:35','کیا اسے علم غیب ہے کہ وه (سب کچھ) دیکھ رہا ہے؟ ');
INSERT INTO "junagarri" VALUES(53,36,'53:36','کیا اسے اس چیز کی خبر نہیں دی گئی جو موسیٰ (علیہ السلام) کے.');
INSERT INTO "junagarri" VALUES(53,37,'53:37','اور وفادار ابراہیم (علیہ السلام) کے صحیفوں میں تھا.');
INSERT INTO "junagarri" VALUES(53,38,'53:38','کہ کوئی شخص کسی دوسرے کا بوجھ نہ اٹھائے گا.');
INSERT INTO "junagarri" VALUES(53,39,'53:39','اور یہ کہ ہر انسان کے لیے صرف وہی ہے جس کی کوشش خود اس نے کی. ');
INSERT INTO "junagarri" VALUES(53,40,'53:40','اور یہ کہ بیشک اس کی کوشش عنقریب دیکھی جائے گی. ');
INSERT INTO "junagarri" VALUES(53,41,'53:41','پھر اسے پورا پورا بدلہ دیا جائے گا.');
INSERT INTO "junagarri" VALUES(53,42,'53:42','اور یہ کہ آپ کے رب ہی کی طرف پہنچنا ہے.');
INSERT INTO "junagarri" VALUES(53,43,'53:43','اور یہ کہ وہی ہنساتا ہے اور وہی رلاتا ہے.');
INSERT INTO "junagarri" VALUES(53,44,'53:44','اور یہ کہ وہی مارتا ہے اور جلاتا ہے.');
INSERT INTO "junagarri" VALUES(53,45,'53:45','اور یہ کہ اسی نے جوڑا یعنی نر ماده پیدا کیا ہے.');
INSERT INTO "junagarri" VALUES(53,46,'53:46','نطفہ سے جبکہ وه ٹپکایا جاتا ہے.');
INSERT INTO "junagarri" VALUES(53,47,'53:47','اور یہ کہ اسی کے ذمہ دوباره پیدا کرنا ہے.');
INSERT INTO "junagarri" VALUES(53,48,'53:48','اور یہ کہ وہی مالدار بناتا ہے اور سرمایہ دیتا ہے. ');
INSERT INTO "junagarri" VALUES(53,49,'53:49','اور یہ کہ وہی شعریٰ (ستارے) کا رب ہے. ');
INSERT INTO "junagarri" VALUES(53,50,'53:50','اور یہ کہ اس نے عاد اول کو ہلاک کیا ہے. ');
INSERT INTO "junagarri" VALUES(53,51,'53:51','اور ثمود کو بھی (جن میں سے) ایک کو بھی باقی نہ رکھا.');
INSERT INTO "junagarri" VALUES(53,52,'53:52','اور اس سے پہلے قوم نوح کو، یقیناً وه بڑے ظالم اور سرکش تھے.');
INSERT INTO "junagarri" VALUES(53,53,'53:53','اور مؤتفکہ (شہر یا الٹی ہوئی بستیوں کو) اسی نے الٹ دیا. ');
INSERT INTO "junagarri" VALUES(53,54,'53:54','پھر اس پر چھا دیا جو چھایا. ');
INSERT INTO "junagarri" VALUES(53,55,'53:55','پس اے انسان تو اپنے رب کی کس کس نعمت کے بارے میں جھگڑے گا؟ ');
INSERT INTO "junagarri" VALUES(53,56,'53:56','یہ (نبی) ڈرانے والے ہیں پہلے ڈرانے والوں میں سے.');
INSERT INTO "junagarri" VALUES(53,57,'53:57','آنے والی گھڑی قریب آ گئی ہے.');
INSERT INTO "junagarri" VALUES(53,58,'53:58','اللہ کے سوا اس کا (وقت معین پر کھول) دکھانے والا اور کوئی نہیں.');
INSERT INTO "junagarri" VALUES(53,59,'53:59','پس کیا تم اس بات سے تعجب کرتے ہو. ');
INSERT INTO "junagarri" VALUES(53,60,'53:60','اور ہنس رہے ہو؟ روتے نہیں؟');
INSERT INTO "junagarri" VALUES(53,61,'53:61','(بلکہ) تم کھیل رہے ہو.');
INSERT INTO "junagarri" VALUES(53,62,'53:62','اب اللہ کے سامنے سجدے کرو اور (اسی) کی عبادت کرو. ');
INSERT INTO "junagarri" VALUES(54,1,'54:1','قیامت قریب آ گئی اور چاند پھٹ گیا. ');
INSERT INTO "junagarri" VALUES(54,2,'54:2','یہ اگر کوئی معجزه دیکھتے ہیں تو منھ پھیر لیتے ہیں اور کہہ دیتے ہیں کہ یہ پہلے سے چلا آتا ہوا جادو ہے. ');
INSERT INTO "junagarri" VALUES(54,3,'54:3','انہوں نے جھٹلایا اور اپنی خواہشوں کی پیروی کی اور ہر کام ٹھہرے ہوئے وقت پر مقرر ہے. ');
INSERT INTO "junagarri" VALUES(54,4,'54:4','یقیناً ان کے پاس وه خبریں آچکی ہیں جن میں ڈانٹ ڈپٹ (کی نصیحت) ہے. ');
INSERT INTO "junagarri" VALUES(54,5,'54:5','اور کامل عقل کی بات ہے لیکن ان ڈراؤنی باتوں نے بھی کچھ فائده نہ دیا. ');
INSERT INTO "junagarri" VALUES(54,6,'54:6','پس (اے نبی) تم ان سے اعراض کرو جس دن ایک پکارنے والا ناگوار چیز کی طرف پکارے گا. ');
INSERT INTO "junagarri" VALUES(54,7,'54:7','یہ جھکی آنکھوں قبروں سے اس طرح نکل کھڑے ہوں گے کہ گویا وه پھیلا ہوا ٹڈی دل ہے. ');
INSERT INTO "junagarri" VALUES(54,8,'54:8','پکارنے والے کی طرف دوڑتے ہوں گے اور کافر کہیں گے یہ دن تو بہت سخت ہے.');
INSERT INTO "junagarri" VALUES(54,9,'54:9','ان سے پہلے قوم نوح نے بھی ہمارے بندے کو جھٹلایا تھا اور یہ دیوانہ بتلا کر جھڑک دیا گیا تھا. ');
INSERT INTO "junagarri" VALUES(54,10,'54:10','پس اس نے اپنے رب سے دعا کی کہ میں بے بس ہوں تو میری مدد کر.');
INSERT INTO "junagarri" VALUES(54,11,'54:11','پس ہم نے آسمان کے دروازوں کو زور کے مینہ سے کھول دیا. ');
INSERT INTO "junagarri" VALUES(54,12,'54:12','اور زمین سے چشموں کو جاری کر دیا پس اس کام کے لیے جو مقدر کیا گیا تھا (دونوں) پانی جمع ہو گئے. ');
INSERT INTO "junagarri" VALUES(54,13,'54:13','اور ہم نے اسے تختوں اور کیلوں والی (کشتی) پر سوار کر لیا. ');
INSERT INTO "junagarri" VALUES(54,14,'54:14','جو ہماری آنکھوں کے سامنے چل رہی تھی۔ بدلہ اس کی طرف سے جس کا کفر کیا گیا تھا.');
INSERT INTO "junagarri" VALUES(54,15,'54:15','اور بیشک ہم نے اس واقعہ کو نشانی بنا کر باقی رکھا پس کوئی ہے نصیحت حاصل کرنے والا. ');
INSERT INTO "junagarri" VALUES(54,16,'54:16','بتاؤ میرا عذاب اور میری ڈرانے والی باتیں کیسی رہیں؟');
INSERT INTO "junagarri" VALUES(54,17,'54:17','اور بیشک ہم نے قرآن کو سمجھنے کے لیے آسان کر دیا ہے ۔ پس کیا کوئی نصیحت حاصل کرنے والا ہے؟');
INSERT INTO "junagarri" VALUES(54,18,'54:18','قوم عاد نے بھی جھٹلایا پس کیسا ہوا میرا عذاب اور میری ڈرانے والی باتیں.');
INSERT INTO "junagarri" VALUES(54,19,'54:19','ہم نے ان پر تیز وتند مسلسل چلنے والی ہوا، ایک پیہم منحوس دن میں بھیج دی. ');
INSERT INTO "junagarri" VALUES(54,20,'54:20','جو لوگوں کو اٹھا اٹھا کر دے پٹختی تھی، گویا کہ وه جڑ سے کٹے ہوئے کھجور کے تنے ہیں. ');
INSERT INTO "junagarri" VALUES(54,21,'54:21','پس کیسی رہی میری سزا اور میرا ڈرانا؟');
INSERT INTO "junagarri" VALUES(54,22,'54:22','یقیناً ہم نے قرآن کو نصیحت کے لیے آسان کر دیا ہے، پس کیا ہے کوئی نصیحت حاصل کرنے والا؟');
INSERT INTO "junagarri" VALUES(54,23,'54:23','قوم ثمود نے ڈرانے والوں کو جھٹلایا.');
INSERT INTO "junagarri" VALUES(54,24,'54:24','اور کہنے لگے کیا ہمیں میں سے ایک شخص کی ہم فرمانبرداری کرنے لگیں؟ تب تو ہم یقیناً غلطی اور دیوانگی میں پڑے ہوئے ہوں گے. ');
INSERT INTO "junagarri" VALUES(54,25,'54:25','کیا ہمارے سب کے درمیان صرف اسی پر وحی اتاری گئی؟ نہیں بلکہ وه جھوٹا اور شیخی خور ہے. ');
INSERT INTO "junagarri" VALUES(54,26,'54:26','اب سب جان لیں گے کل کو کہ کون جھوٹا اور شیخی خور تھا؟ ');
INSERT INTO "junagarri" VALUES(54,27,'54:27','بیشک ہم ان کی آزمائش کے لیے اونٹنی بھیجیں گے پس (اے صالح) توان کا منتظر ره اور صبر کر. ');
INSERT INTO "junagarri" VALUES(54,28,'54:28','ہاں انہیں خبر کر دے کہ پانی ان میں تقسیم شده ہے ، ہرایک اپنی باری پر حاضر ہوگا. ');
INSERT INTO "junagarri" VALUES(54,29,'54:29','انہوں نے اپنے ساتھی کو آواز دی جس نے (اونٹنی پر) وار کیا اور (اس کی) کوچیں کاٹ دیں.');
INSERT INTO "junagarri" VALUES(54,30,'54:30','پس کیونکر ہوا میرا عذاب اور میرا ڈرانا.');
INSERT INTO "junagarri" VALUES(54,31,'54:31','ہم نے ان پر ایک چیخ بھیجی پس ایسے ہو گئے جیسے باڑ بنانے والے کی روندی ہوئی گھاس. ');
INSERT INTO "junagarri" VALUES(54,32,'54:32','اور ہم نے نصیحت کے لیے قرآن کو آسان کر دیا ہے پس کیا ہے کوئی جو نصیحت قبول کرے.');
INSERT INTO "junagarri" VALUES(54,33,'54:33','قوم لوط نے بھی ڈرانے والوں کی تکذیب کی.');
INSERT INTO "junagarri" VALUES(54,34,'54:34','بیشک ہم نے ان پر پتھر برسانے والی ہوا بھیجی سوائے لوط (علیہ السلام) کے گھر والوں کے، انہیں ہم نے سحر کے وقت نجات دے دی. ');
INSERT INTO "junagarri" VALUES(54,35,'54:35','اپنے احسان سے ہر ہر شکر گزار کو ہم اسی طرح بدلہ دیتے ہیں.');
INSERT INTO "junagarri" VALUES(54,36,'54:36','یقیناً (لوط علیہ السلام) نے انہیں ہماری پکڑ سے ڈرایا تھا لیکن انہوں نے ڈرانے والوں کے بارے میں (شک وشبہ اور) جھگڑا کیا. ');
INSERT INTO "junagarri" VALUES(54,37,'54:37','اور ان (لوط علیہ السلام) کو ان کے مہمانوں کے بارے میں پھسلایا پس ہم نے ان کی آنکھیں اندھی کردیں ، (اور کہہ دیا) میرا عذاب اور میرا ڈرانا چکھو.');
INSERT INTO "junagarri" VALUES(54,38,'54:38','اور یقینی بات ہے کہ انہیں صبح سویرے ہی ایک جگہ پکڑنے والے مقرره عذاب نے غارت کر دیا. ');
INSERT INTO "junagarri" VALUES(54,39,'54:39','پس میرے عذاب اور میرے ڈراوے کا مزه چکھو.');
INSERT INTO "junagarri" VALUES(54,40,'54:40','اور یقیناً ہم نے قرآن کو پند ووعظ کے لیے آسان کر دیا ہے ۔ پس کیا کوئی ہے نصحیت پکڑنے والا.');
INSERT INTO "junagarri" VALUES(54,41,'54:41','اور فرعونیوں کے پاس بھی ڈرانے والے آئے. ');
INSERT INTO "junagarri" VALUES(54,42,'54:42','انہوں نے ہماری تمام نشانیاں جھٹلائیں پس ہم نے انہیں بڑے غالب قوی پکڑنے والے کی طرح پکڑ لیا. ');
INSERT INTO "junagarri" VALUES(54,43,'54:43','(اے قریشیو!) کیا تمہارے کافر ان کافروں سے کچھ بہتر ہیں ؟ یا تمہارے لیے اگلی کتابوں میں چھٹکارا لکھا ہوا ہے؟ ');
INSERT INTO "junagarri" VALUES(54,44,'54:44','یا یہ کہتے ہیں کہ ہم غلبہ پانے والی جماعت ہیں. ');
INSERT INTO "junagarri" VALUES(54,45,'54:45','عنقریب یہ جماعت شکست دی جائےگی اور پیٹھ دے کر بھاگے گی. ');
INSERT INTO "junagarri" VALUES(54,46,'54:46','بلکہ قیامت کی گھڑی ان کے وعدے کے وقت ہے اور قیامت بڑی سخت اور کڑوی چیز ہے. ');
INSERT INTO "junagarri" VALUES(54,47,'54:47','بیشک گناه گار گمراہی میں اور عذاب میں ہیں.');
INSERT INTO "junagarri" VALUES(54,48,'54:48','جس دن وه اپنے منھ کے بل آگ میں گھسیٹے جائیں گے (اوران سے کہا جائے گا) دوزخ کی آگ لگنے کے مزے چکھو. ');
INSERT INTO "junagarri" VALUES(54,49,'54:49','بیشک ہم نے ہر چیز کو ایک (مقرره) اندازے پر پیدا کیا ہے. ');
INSERT INTO "junagarri" VALUES(54,50,'54:50','اور ہمارا حکم صرف ایک دفعہ (کا ایک کلمہ) ہی ہوتا ہے جیسے آنکھ کا جھپکنا.');
INSERT INTO "junagarri" VALUES(54,51,'54:51','اور ہم نے تم جیسے بہتیروں کو ہلاک کر دیا ہے ، پس کوئی ہے نصیحت لینے والا.');
INSERT INTO "junagarri" VALUES(54,52,'54:52','جوکچھ انہوں نے (اعمال) کیے ہیں سب نامہٴ اعمال میں لکھے ہوئے ہیں. ');
INSERT INTO "junagarri" VALUES(54,53,'54:53','(اسی طرح) ہر چھوٹی بڑی بات بھی لکھی ہوئی ہے. ');
INSERT INTO "junagarri" VALUES(54,54,'54:54','یقیناً ہمارا ڈر رکھنے والے جنتوں اور نہروں میں ہوں گے. ');
INSERT INTO "junagarri" VALUES(54,55,'54:55','راستی اور عزت کی بیٹھک میں قدرت والے بادشاه کے پاس. ');
INSERT INTO "junagarri" VALUES(55,1,'55:1','رحمٰن نے.');
INSERT INTO "junagarri" VALUES(55,2,'55:2','قرآن سکھایا. ');
INSERT INTO "junagarri" VALUES(55,3,'55:3','اسی نے انسان کو پیدا کیا. ');
INSERT INTO "junagarri" VALUES(55,4,'55:4','اور اسے بولنا سکھایا. ');
INSERT INTO "junagarri" VALUES(55,5,'55:5','آفتاب اور ماہتاب (مقرره) حساب سے ہیں. ');
INSERT INTO "junagarri" VALUES(55,6,'55:6','اور ستارے اور درخت دونوں سجده کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(55,7,'55:7','اسی نے آسمان کو بلند کیا اور اسی نے ترازو رکھی. ');
INSERT INTO "junagarri" VALUES(55,8,'55:8','تاکہ تم تولنے میں تجاوز نہ کرو. ');
INSERT INTO "junagarri" VALUES(55,9,'55:9','انصاف کے ساتھ وزن کو ٹھیک رکھو اور تول میں کم نہ دو.');
INSERT INTO "junagarri" VALUES(55,10,'55:10','اور اسی نے مخلوق کے لیے زمین بچھا دی.');
INSERT INTO "junagarri" VALUES(55,11,'55:11','جس میں میوے ہیں اور خوشے والے کھجور کے درخت ہیں. ');
INSERT INTO "junagarri" VALUES(55,12,'55:12','اور بھس والا اناج ہے ۔ اور خوشبودار پھول ہیں.');
INSERT INTO "junagarri" VALUES(55,13,'55:13','پس (اے انسانو اور جنو!) تم اپنے پروردگار کی کس کس نعمت کو جھٹلاؤ گے؟ ');
INSERT INTO "junagarri" VALUES(55,14,'55:14','اس نےانسان کو بجنے والی مٹی سے پیدا کیا جو ٹھیکری کی طرح تھی. ');
INSERT INTO "junagarri" VALUES(55,15,'55:15','اور جنات کو آگ کے شعلے سے پیدا کیا. ');
INSERT INTO "junagarri" VALUES(55,16,'55:16','پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟ ');
INSERT INTO "junagarri" VALUES(55,17,'55:17','وه رب ہے دونوں مشرقوں اور دونوں مغربوں کا ');
INSERT INTO "junagarri" VALUES(55,18,'55:18','تو (اے جنو اورانسانو!) تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,19,'55:19','اس نے دو دریا جاری کر دیے جو ایک دوسرے سے مل جاتے ہیں.');
INSERT INTO "junagarri" VALUES(55,20,'55:20','ان دونوں میں ایک آڑ ہے کہ اس سے بڑھ نہیں سکتے. ');
INSERT INTO "junagarri" VALUES(55,21,'55:21','پس اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,22,'55:22','ان دونوں میں سے موتی اور مونگے برآمد ہوتے ہیں. ');
INSERT INTO "junagarri" VALUES(55,23,'55:23','پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟ ');
INSERT INTO "junagarri" VALUES(55,24,'55:24','اور اللہ ہی کی (ملکیت میں) ہیں وه جہاز جو سمندروں میں پہاڑ کی طرح بلند (چل پھر رہے) ہیں. ');
INSERT INTO "junagarri" VALUES(55,25,'55:25','پس (اے انسانو اور جنو!) تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟ ');
INSERT INTO "junagarri" VALUES(55,26,'55:26','زمین پر جو ہیں سب فنا ہونے والے ہیں.');
INSERT INTO "junagarri" VALUES(55,27,'55:27','صرف تیرے رب کی ذات جو عظمت اور عزت والی ہے باقی ره جائے گی.');
INSERT INTO "junagarri" VALUES(55,28,'55:28','پھرتم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟ ');
INSERT INTO "junagarri" VALUES(55,29,'55:29','سب آسمان وزمین والے اسی سے مانگتے ہیں ۔ ہر روز وه ایک شان میں ہے. ');
INSERT INTO "junagarri" VALUES(55,30,'55:30','پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟ ');
INSERT INTO "junagarri" VALUES(55,31,'55:31','(جنوں اور انسانوں کے گروہو!) عنقریب ہم تمہاری طرف پوری طرح متوجہ ہو جائیں گے. ');
INSERT INTO "junagarri" VALUES(55,32,'55:32','پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤگے؟');
INSERT INTO "junagarri" VALUES(55,33,'55:33','اے گروه جنات و انسان! اگر تم میں آسمانوں اور زمین کے کناروں سے باہر نکل جانے کی طاقت ہے تو نکل بھاگو ! بغیر غلبہ اور طاقت کے تم نہیں نکل سکتے. ');
INSERT INTO "junagarri" VALUES(55,34,'55:34','پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,35,'55:35','تم پر آگ کے شعلے اور دھواں چھوڑا جائے گا پھر تم مقابلہ نہ کر سکو گے. ');
INSERT INTO "junagarri" VALUES(55,36,'55:36','پھر اپنے رب کی نعمتوں میں سے کس کس نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,37,'55:37','پس جب کہ آسمان پھٹ کر سرخ ہو جائے جیسے کہ سرخ چمڑه. ');
INSERT INTO "junagarri" VALUES(55,38,'55:38','پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,39,'55:39','اس دن کسی انسان اورکسی جن سے اس کے گناہوں کی پرسش نہ کی جائے گی. ');
INSERT INTO "junagarri" VALUES(55,40,'55:40','پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,41,'55:41','گناه گار صرف حلیہ ہی سے پہچان لیے جائیں گے اور ان کی پیشانیوں کے بال اور قدم پکڑ لیے جائیں گے. ');
INSERT INTO "junagarri" VALUES(55,42,'55:42','پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,43,'55:43','یہ ہے وه جہنم جسے مجرم جھوٹا جانتے تھے.');
INSERT INTO "junagarri" VALUES(55,44,'55:44','اس کے اور کھولتے ہوئے گرم پانی کے درمیان چکر کھائیں گے. ');
INSERT INTO "junagarri" VALUES(55,45,'55:45','پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,46,'55:46','اور اس شخص کے لیے جو اپنے رب کے سامنے کھڑا ہونے سے ڈرا دو جنتیں ہیں. ');
INSERT INTO "junagarri" VALUES(55,47,'55:47','پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,48,'55:48','(دونوں جنتیں) بہت سی ٹہنیوں اور شاخوں والی ہیں. ');
INSERT INTO "junagarri" VALUES(55,49,'55:49','پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,50,'55:50','ان دونوں (جنتوں) میں دو بہتے ہوئے چشمے ہیں. ');
INSERT INTO "junagarri" VALUES(55,51,'55:51','پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,52,'55:52','ان دونوں جنتوں میں ہر قسم کے میوؤں کی دو قسمیں ہوگی. ');
INSERT INTO "junagarri" VALUES(55,53,'55:53','پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,54,'55:54','جنتی ایسے فرشوں پر تکیہ لگائے ہوئے ہوں گے جن کے استر دبیز ریشم کے ہوں گے ، اور ان دونوں جنتوں کے میوے بالکل قریب ہوں گے. ');
INSERT INTO "junagarri" VALUES(55,55,'55:55','پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,56,'55:56','وہاں (شرمیلی) نیچی نگاه والی حوریں ہیں جنہیں ان سے پہلے کسی جن وانس نے ہاتھ نہیں لگایا. ');
INSERT INTO "junagarri" VALUES(55,57,'55:57','پس اپنے پالنے والے کی کس کس نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,58,'55:58','وه حوریں مثل یاقوت اور مونگے کے ہوں گی. ');
INSERT INTO "junagarri" VALUES(55,59,'55:59','پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,60,'55:60','احسان کا بدلہ احسان کے سوا کیا ہے. ');
INSERT INTO "junagarri" VALUES(55,61,'55:61','پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,62,'55:62','اور ان کے سوا دو جنتیں اور ہیں. ');
INSERT INTO "junagarri" VALUES(55,63,'55:63','پس تم اپنے پرورش کرنے والے کی کس کس نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,64,'55:64','جو دونوں گہری سبز سیاہی مائل ہیں. ');
INSERT INTO "junagarri" VALUES(55,65,'55:65','بتاؤ اب اپنے پروردگار کی کس کس نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,66,'55:66','ان میں دو (جوش سے) ابلنے والے چشمے ہیں. ');
INSERT INTO "junagarri" VALUES(55,67,'55:67','پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,68,'55:68','ان دونوں میں میوے اور کھجور اور انار ہوں گے. ');
INSERT INTO "junagarri" VALUES(55,69,'55:69','کیا اب بھی رب کی کسی نعمت کی تکذیب تم کرو گے؟');
INSERT INTO "junagarri" VALUES(55,70,'55:70','ان میں نیک سیرت خوبصورت عورتیں ہیں. ');
INSERT INTO "junagarri" VALUES(55,71,'55:71','پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,72,'55:72','(گوری رنگت کی) حوریں جنتی خیموں میں رہنے والیاں ہیں. ');
INSERT INTO "junagarri" VALUES(55,73,'55:73','پس (اے انسانو اور جنو!) تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟');
INSERT INTO "junagarri" VALUES(55,74,'55:74','ان کو ہاتھ نہیں لگایا کسی انسان یا جن نے اس سے قبل.');
INSERT INTO "junagarri" VALUES(55,75,'55:75','پس اپنے پروردگار کی کون کون سی نعمت کے ساتھ تم تکذیب کرتے ہو؟');
INSERT INTO "junagarri" VALUES(55,76,'55:76','سبز مسندوں اور عمده فرشوں پر تکیہ لگائے ہوئے ہوں گے. ');
INSERT INTO "junagarri" VALUES(55,77,'55:77','پس (اے جنو اور انسانو!) تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟ ');
INSERT INTO "junagarri" VALUES(55,78,'55:78','تیرے پروردگار کا نام بابرکت ہے جو عزت وجلال والا ہے.');
INSERT INTO "junagarri" VALUES(56,1,'56:1','جب قیامت قائم ہو جائے گی. ');
INSERT INTO "junagarri" VALUES(56,2,'56:2','جس کے واقع ہونے میں کوئی جھوٹ نہیں.');
INSERT INTO "junagarri" VALUES(56,3,'56:3','وه پست کرنے والی اور بلند کرنے والی ہوگی. ');
INSERT INTO "junagarri" VALUES(56,4,'56:4','جبکہ زمین زلزلہ کے ساتھ ہلا دی جائے گی.');
INSERT INTO "junagarri" VALUES(56,5,'56:5','اور پہاڑ بالکل ریزه ریزه کر دیے جائیں گے. ');
INSERT INTO "junagarri" VALUES(56,6,'56:6','پھر وه مثل پراگنده غبار کے ہو جائیں گے.');
INSERT INTO "junagarri" VALUES(56,7,'56:7','اور تم تین جماعتوں میں ہو جاؤ گے. ');
INSERT INTO "junagarri" VALUES(56,8,'56:8','پس داہنے ہاتھ والے کیسے اچھے ہیں داہنے ہاتھ والے. ');
INSERT INTO "junagarri" VALUES(56,9,'56:9','اور بائیں ہاتھ والے کیا حال ہے بائیں ہاتھ والوں کا. ');
INSERT INTO "junagarri" VALUES(56,10,'56:10','اور جو آگے والے ہیں وه تو آگے والے ہی ہیں. ');
INSERT INTO "junagarri" VALUES(56,11,'56:11','وه بالکل نزدیکی حاصل کیے ہوئے ہیں.');
INSERT INTO "junagarri" VALUES(56,12,'56:12','نعمتوں والی جنتوں میں ہیں.');
INSERT INTO "junagarri" VALUES(56,13,'56:13','(بہت بڑا) گروه تو اگلے لوگوں میں سے ہوگا.');
INSERT INTO "junagarri" VALUES(56,14,'56:14','اور تھوڑے سے پچھلے لوگوں میں سے. ');
INSERT INTO "junagarri" VALUES(56,15,'56:15','یہ لوگ سونے کے تاروں سے بنے ہوئے تختوں پر.');
INSERT INTO "junagarri" VALUES(56,16,'56:16','ایک دوسرے کےسامنے تکیہ لگائے بیٹھے ہوں گے. ');
INSERT INTO "junagarri" VALUES(56,17,'56:17','ان کے پاس ایسے لڑکے جو ہمیشہ (لڑکے ہی ) رہیں گے آمدورفت کریں گے.');
INSERT INTO "junagarri" VALUES(56,18,'56:18','آبخورے اور جگ لے کر اور ایسا جام لے کر جو بہتی ہوئی شراب سے پر ہو.');
INSERT INTO "junagarri" VALUES(56,19,'56:19','جس سے نہ سر میں درد ہو نہ عقل میں فتور آئے. ');
INSERT INTO "junagarri" VALUES(56,20,'56:20','اور ایسے میوے لیے ہوئے جو ان کی پسند کے ہوں.');
INSERT INTO "junagarri" VALUES(56,21,'56:21','اور پرندوں کے گوشت جو انہیں مرغوب ہوں.');
INSERT INTO "junagarri" VALUES(56,22,'56:22','اور بڑی بڑی آنکھوں والی حوریں.');
INSERT INTO "junagarri" VALUES(56,23,'56:23','جو چھپے ہوئے موتیوں کی طرح ہیں. ');
INSERT INTO "junagarri" VALUES(56,24,'56:24','یہ صلہ ہے ان کے اعمال کا.');
INSERT INTO "junagarri" VALUES(56,25,'56:25','نہ وہاں بکواس سنیں گے اور نہ گناه کی بات.');
INSERT INTO "junagarri" VALUES(56,26,'56:26','صرف سلام ہی سلام کی آواز ہوگی. ');
INSERT INTO "junagarri" VALUES(56,27,'56:27','اور داہنے ہاتھ والے کیا ہی اچھے ہیں داہنے ہاتھ والے. ');
INSERT INTO "junagarri" VALUES(56,28,'56:28','وه بغیرکانٹوں کی بیریوں.');
INSERT INTO "junagarri" VALUES(56,29,'56:29','اور تہ بہ تہ کیلوں.');
INSERT INTO "junagarri" VALUES(56,30,'56:30','اور لمبے لمبے سایوں. ');
INSERT INTO "junagarri" VALUES(56,31,'56:31','اور بہتے ہوئے پانیوں.');
INSERT INTO "junagarri" VALUES(56,32,'56:32','اور بکثرت پھلوں میں.');
INSERT INTO "junagarri" VALUES(56,33,'56:33','جو نہ ختم ہوں نہ روک لیے جائیں. ');
INSERT INTO "junagarri" VALUES(56,34,'56:34','اور اونچے اونچے فرشوں میں ہوں گے. ');
INSERT INTO "junagarri" VALUES(56,35,'56:35','ہم نے ان کی (بیویوں کو) خاص طور پر بنایا ہے.');
INSERT INTO "junagarri" VALUES(56,36,'56:36','اور ہم نے انہیں کنواریاں بنا دیا ہے. ');
INSERT INTO "junagarri" VALUES(56,37,'56:37','محبت والیاں اور ہم عمر ہیں. ');
INSERT INTO "junagarri" VALUES(56,38,'56:38','دائیں ہاتھ والوں کے لیے ہیں.');
INSERT INTO "junagarri" VALUES(56,39,'56:39','جم غفیر ہے اگلوں میں سے. ');
INSERT INTO "junagarri" VALUES(56,40,'56:40','اور بہت بڑی جماعت ہے پچھلوں میں سے. ');
INSERT INTO "junagarri" VALUES(56,41,'56:41','اور بائیں ہاتھ والے کیا ہیں بائیں ہاتھ والے ');
INSERT INTO "junagarri" VALUES(56,42,'56:42','گرم ہوا اور گرم پانی میں (ہوں گے).');
INSERT INTO "junagarri" VALUES(56,43,'56:43','اورسیاه دھوئیں کے سائے میں. ');
INSERT INTO "junagarri" VALUES(56,44,'56:44','جو نہ ٹھنڈا ہے نہ فرحت بخش. ');
INSERT INTO "junagarri" VALUES(56,45,'56:45','بیشک یہ لوگ اس سے پہلے بہت نازوں میں پلے ہوئے تھے. ');
INSERT INTO "junagarri" VALUES(56,46,'56:46','اور بڑے بڑے گناہوں پر اصرار کرتے تھے.');
INSERT INTO "junagarri" VALUES(56,47,'56:47','اور کہتے تھے کہ کیا جب ہم مر جائیں گے اور مٹی اور ہڈی ہو جائیں گے تو کیا ہم پھر دوباره اٹھا کھڑے کیے جائیں گے.');
INSERT INTO "junagarri" VALUES(56,48,'56:48','اور کیا ہمارے اگلے باپ دادا بھی؟ ');
INSERT INTO "junagarri" VALUES(56,49,'56:49','آپ کہہ دیجئے کہ یقیناً سب اگلے اور پچھلے.');
INSERT INTO "junagarri" VALUES(56,50,'56:50','ضرور جمع کئے جائیں گے ایک مقرر دن کے وقت.');
INSERT INTO "junagarri" VALUES(56,51,'56:51','پھر تم اے گمراہو جھٹلانے والو!');
INSERT INTO "junagarri" VALUES(56,52,'56:52','البتہ کھانے والے ہو تھوہر کا درخت.');
INSERT INTO "junagarri" VALUES(56,53,'56:53','اور اسی سے پیٹ بھرنے والے ہو. ');
INSERT INTO "junagarri" VALUES(56,54,'56:54','پھر اس پر گرم کھولتا پانی پینے والے ہو.');
INSERT INTO "junagarri" VALUES(56,55,'56:55','پھر پینے والے بھی پیاسے اونٹوں کی طرح. ');
INSERT INTO "junagarri" VALUES(56,56,'56:56','قیامت کے دن ان کی مہمانی یہ ہے. ');
INSERT INTO "junagarri" VALUES(56,57,'56:57','ہم ہی نے تم سب کو پیدا کیا ہے پھر تم کیوں باور نہیں کرتے؟ ');
INSERT INTO "junagarri" VALUES(56,58,'56:58','اچھا پھر یہ تو بتلاؤ کہ جو منی تم ٹپکاتے ہو.');
INSERT INTO "junagarri" VALUES(56,59,'56:59','کیا اس کا (انسان) تم بناتے ہو یا پیدا کرنے والے ہم ہی ہیں؟ ');
INSERT INTO "junagarri" VALUES(56,60,'56:60','ہم ہی نے تم میں موت کو متعین کر دیا ہے اور ہم اس سے ہارے ہوئے نہیں ہیں. ');
INSERT INTO "junagarri" VALUES(56,61,'56:61','کہ تمہاری جگہ تم جیسے اور پیدا کر دیں اور تمہیں نئے سرے سے اس عالم میں پیدا کریں جس سے تم (بالکل) بےخبر ہو. ');
INSERT INTO "junagarri" VALUES(56,62,'56:62','تمہیں یقینی طور پر پہلی دفعہ کی پیدائش معلوم ہی ہے پھر کیوں عبرت حاصل نہیں کرتے؟ ');
INSERT INTO "junagarri" VALUES(56,63,'56:63','اچھا پھر یہ بھی بتلاؤ کہ تم جو کچھ بوتے ہو.');
INSERT INTO "junagarri" VALUES(56,64,'56:64','اسے تم ہی اگاتے ہو یا ہم اگانے والے ہیں. ');
INSERT INTO "junagarri" VALUES(56,65,'56:65','اگر ہم چاہیں تواسے ریزه ریزه کر ڈالیں اور تم حیرت کے ساتھ باتیں بناتے ہی ره جاؤ. ');
INSERT INTO "junagarri" VALUES(56,66,'56:66','کہ ہم پر تو تاوان ہی پڑ گیا. ');
INSERT INTO "junagarri" VALUES(56,67,'56:67','بلکہ ہم بالکل محروم ہی ره گئے.');
INSERT INTO "junagarri" VALUES(56,68,'56:68','اچھا یہ بتاؤ کہ جس پانی کو تم پیتے ہو.');
INSERT INTO "junagarri" VALUES(56,69,'56:69','اسے بادلوں سے بھی تم ہی اتارتے ہو یا ہم برساتے ہیں؟');
INSERT INTO "junagarri" VALUES(56,70,'56:70','اگر ہماری منشا ہو تو ہم اسے کڑوا زہر کردیں پھر تم ہماری شکرگزاری کیوں نہیں کرتے؟ ');
INSERT INTO "junagarri" VALUES(56,71,'56:71','اچھا ذرا یہ بھی بتاؤ کہ جو آگ تم سلگاتے ہو.');
INSERT INTO "junagarri" VALUES(56,72,'56:72','اس کے درخت کو تم نے پیدا کیا ہے یا ہم اس کے پیدا کرنے والے ہیں؟ ');
INSERT INTO "junagarri" VALUES(56,73,'56:73','ہم نے اسے سبب نصیحت اور مسافروں کے فائدے کی چیز بنایا ہے. ');
INSERT INTO "junagarri" VALUES(56,74,'56:74','پس اپنے بہت بڑے رب کے نام کی تسبیح کیا کرو.');
INSERT INTO "junagarri" VALUES(56,75,'56:75','پس میں قسم کھاتا ہوں ستاروں کے گرنے کی. ');
INSERT INTO "junagarri" VALUES(56,76,'56:76','اور اگرتمہیں علم ہو تو یہ بہت بڑی قسم ہے.');
INSERT INTO "junagarri" VALUES(56,77,'56:77','کہ بیشک یہ قرآن بہت بڑی عزت والا ہے. ');
INSERT INTO "junagarri" VALUES(56,78,'56:78','جو ایک محفوظ کتاب میں درج ہے ');
INSERT INTO "junagarri" VALUES(56,79,'56:79','جسے صرف پاک لوگ ہی چھو سکتے ہیں. ');
INSERT INTO "junagarri" VALUES(56,80,'56:80','یہ رب العالمین کی طرف سےاترا ہوا ہے.');
INSERT INTO "junagarri" VALUES(56,81,'56:81','پس کیا تم ایسی بات کو سرسری (اور معمولی) سمجھ رہے ہو؟ ');
INSERT INTO "junagarri" VALUES(56,82,'56:82','اور اپنے حصے میں یہی لیتے ہو کہ جھٹلاتے پھرو.');
INSERT INTO "junagarri" VALUES(56,83,'56:83','پس جبکہ روح نرخرے تک پہنچ جائے.');
INSERT INTO "junagarri" VALUES(56,84,'56:84','اور تم اس وقت آنکھوں سے دیکھتے رہو. ');
INSERT INTO "junagarri" VALUES(56,85,'56:85','ہم اس شخص سے بہ نسبت تمہارے بہت زیاده قریب ہوتے ہیں لیکن تم نہیں دیکھ سکتے. ');
INSERT INTO "junagarri" VALUES(56,86,'56:86','پس اگر تم کسی کے زیرفرمان نہیں.');
INSERT INTO "junagarri" VALUES(56,87,'56:87','اور اس قول میں سچے ہو تو (ذرا) اس روح کو تو لوٹاؤ. ');
INSERT INTO "junagarri" VALUES(56,88,'56:88','پس جو کوئی بارگاه الٰہی سے قریب کیا ہوا ہوگا. ');
INSERT INTO "junagarri" VALUES(56,89,'56:89','اسے تو راحت ہے اور غذائیں ہیں اور آرام والی جنت ہے.');
INSERT INTO "junagarri" VALUES(56,90,'56:90','اور جو شحص داہنے (ہاتھ) والوں میں سے ہے. ');
INSERT INTO "junagarri" VALUES(56,91,'56:91','تو بھی سلامتی ہے تیرے لیے کہ تو داہنے والوں میں سے ہے.');
INSERT INTO "junagarri" VALUES(56,92,'56:92','لیکن اگر کوئی جھٹلانے والوں گمراہوں میں سے ہے. ');
INSERT INTO "junagarri" VALUES(56,93,'56:93','تو کھولتے ہوئے گرم پانی کی مہمانی ہے.');
INSERT INTO "junagarri" VALUES(56,94,'56:94','اور دوزخ میں جانا ہے.');
INSERT INTO "junagarri" VALUES(56,95,'56:95','یہ خبر سراسر حق اور قطعاً یقینی ہے.');
INSERT INTO "junagarri" VALUES(56,96,'56:96','پس تواپنے عظیم الشان پروردگار کی تسبیح کر. ');
INSERT INTO "junagarri" VALUES(57,1,'57:1','آسمانوں اور زمین میں جو ہے (سب) اللہ کی تسبیح کر رہے ہیں ، وه زبردست باحکمت ہے.');
INSERT INTO "junagarri" VALUES(57,2,'57:2','آسمانوں اور زمین کی بادشاہت اسی کی ہے ، وہی زندگی دیتا ہے اور موت بھی اور وه ہر چیز پر قادر ہے.');
INSERT INTO "junagarri" VALUES(57,3,'57:3','وہی پہلے ہے اور وہی پیچھے، وہی ظاہر ہے اور وہی مخفی ، اور وه ہر چیز کو بخوبی جاننے والا ہے.');
INSERT INTO "junagarri" VALUES(57,4,'57:4','وہی ہے جس نے آسمانوں اور زمین کو چھ دن میں پیدا کیا پھر عرش پر مستوی ہو گیا۔ وه (خوب) جانتا ہےاس چیز کو جو زمین میں جائے اور جو اس سے نکلے اور جو آسمان سے نیچے آئے اور جو کچھ چڑھ کر اس میں جائے ، اور جہاں کہیں تم ہو وه تمہارے ساتھ ہے اور جو تم کر رہے ہو اللہ دیکھ رہا ہے.');
INSERT INTO "junagarri" VALUES(57,5,'57:5','آسمانوں کی اور زمین کی بادشاہی اسی کی ہے۔ اور تمام کام اسی کی طرف لوٹائے جاتے ہیں.');
INSERT INTO "junagarri" VALUES(57,6,'57:6','وہی رات کو دن میں لے جاتا ہے اور وہی دن کو رات میں داخل کر دیتا ہے اور سینوں کے بھیدوں کا وه پورا عالم ہے.');
INSERT INTO "junagarri" VALUES(57,7,'57:7','اللہ پر اور اس کے رسول پر ایمان لے آؤ اور اس مال میں سے خرچ کرو جس میں اللہ نے تمہیں (دوسروں کا) جانشین بنایا ہے پس تم میں سے جو ایمان لائیں اور خیرات کریں انہیں بہت بڑا ثواب ملے گا.');
INSERT INTO "junagarri" VALUES(57,8,'57:8','تم اللہ پر ایمان کیوں نہیں لاتے؟ حالانکہ خود رسول تمہیں اپنے رب پر ایمان لانےکی دعوت دے رہا ہے اور اگر تم مومن ہو تو وه تو تم سے مضبوط عہد وپیمان بھی لے چکا ہے. ');
INSERT INTO "junagarri" VALUES(57,9,'57:9','وه (اللہ) ہی ہے جو اپنے بندے پر واضح آیتیں اتارتا ہے تاکہ وه تمہیں اندھیروں سے نور کی طرف لے جائے یقیناً اللہ تعالیٰ تم پر نرمی کرنے والا رحم کرنے والا ہے.');
INSERT INTO "junagarri" VALUES(57,10,'57:10','تمہیں کیا ہو گیا ہے جو تم اللہ کی راه میں خرچ نہیں کرتے؟ دراصل آسمانوں اور زمینوں کی میراث کا مالک (تنہا) اللہ ہی ہے تم میں سے جن لوگوں نے فتح سے پہلے فی سبیل اللہ دیا ہے اور قتال کیا ہے وه (دوسروں کے) برابر نہیں ، بلکہ ان سے بہت بڑے درجے کے ہیں جنہوں نے فتح کے بعد خیراتیں دیں اور جہاد کیے ۔ ہاں بھلائی کا وعده تو اللہ تعالیٰ کاان سب سے ہے جو کچھ تم کر رہے ہو اس سے اللہ خبردار ہے.');
INSERT INTO "junagarri" VALUES(57,11,'57:11','کون ہے جو اللہ تعالیٰ کو اچھی طرح قرض دے پھر اللہ تعالیٰ اسے اس کے لیے بڑھاتا چلا جائے اور اس کے لیے پسندیده اجر ثابت ہو جائے. ');
INSERT INTO "junagarri" VALUES(57,12,'57:12','(قیامت کے دن تو دیکھے گا کہ مومن مردوں اور عورتوں کا نور ان کے آگے آگے اور ان کے دائیں دوڑ رہا ہوگا آج تمہیں ان جنتوں کی خوش خبری ہے جن کے نیچے نہریں جاری ہیں جن میں ہمیشہ کی رہائش ہے۔ یہ ہے بڑی کامیابی. ');
INSERT INTO "junagarri" VALUES(57,13,'57:13','اس دن منافق مرد وعورت ایمان والوں سے کہیں گے کہ ہمارا انتظار تو کرو کہ ہم بھی تمہارے نور سے کچھ روشنی حاصل کر لیں ۔ جواب دیا جائے گا کہ تم اپنے پیچھے لوٹ جاؤ اور روشنی تلاش کرو۔ پھر ان کے اور ان کے درمیان ایک دیوار حائل کر دی جائے گی جس میں دروازه بھی ہوگا۔ اس کے اندرونی حصہ میں تو رحمت ہوگی اور باہر کی طرف عذاب ہوگا. ');
INSERT INTO "junagarri" VALUES(57,14,'57:14','یہ چلا چلا کر ان سے کہیں گے کہ کیا ہم تمھارے ساتھ نہ تھے وه کہیں گے کہ ہاں تھے تو سہی لیکن تم نے اپنے آپ کو فتنہ میں پھنسا رکھا تھا اور انتظار میں ہی رہے اور شک و شبہ کرتے رہے اور تمہیں تمہاری فضول تمناؤں نے دھوکے میں ہی رکھا یہاں تک کہ اللہ کا حکم آ پہنچا اور تمہیں اللہ کے بارے میں دھوکہ دینے والے نے دھوکے میں ہی رکھا. ');
INSERT INTO "junagarri" VALUES(57,15,'57:15','الغرض، آج تم سے نہ فدیہ (اور نہ بدلہ) قبول کیا جائے گا اور نہ کافروں سے تم (سب) کا ٹھکانا دوزخ ہے۔ وہی تمہاری رفیق ہے اور وه برا ٹھکانا ہے.');
INSERT INTO "junagarri" VALUES(57,16,'57:16','کیا اب تک ایمان والوں کے لیے وقت نہیں آیا کہ ان کے دل ذکر الٰہی سے اور جو حق اتر چکا ہے اس سے نرم ہو جائیں اور ان کی طرح نہ ہو جائیں جنہیں ان سے پہلے کتاب دی گئی تھی پھر جب ان پر ایک زمانہ دراز گزر گیا تو ان کے دل سخت ہو گئے اور ان میں بہت سے فاسق ہیں. ');
INSERT INTO "junagarri" VALUES(57,17,'57:17','یقین مانوکہ اللہ ہی زمین کو اس کی موت کے بعد زنده کر دیتا ہے۔ ہم نے تو تمہارے لیے اپنی آیتیں بیان کردیں تاکہ تم سمجھو.');
INSERT INTO "junagarri" VALUES(57,18,'57:18','بیشک صدقہ دینے والے مرد اور صدقہ دینے والی عورتیں اور جو اللہ کو خلوص کے ساتھ قرض دے رہے ہیں۔ ان کے لیے یہ بڑھایا جائے گا اور ان کے لیے پسندیده اجر وثواب ہے. ');
INSERT INTO "junagarri" VALUES(57,19,'57:19','اللہ اوراس کے رسول پر جو ایمان رکھتے ہیں وہی لوگ اپنے رب کے نزدیک صدیق اور شہید ہیں ان کے لیے ان کا اجر اور ان کا نور ہے، اور جو لوگ کفر کرتے ہیں اور ہماری آیتوں کوجھٹلاتے ہیں وه جہنمی ہیں.');
INSERT INTO "junagarri" VALUES(57,20,'57:20','خوب جان رکھو کہ دنیا کی زندگی صرف کھیل تماشا زینت اور آپس میں فخر (و غرور) اور مال واولاد میں ایک کا دوسرے سے اپنے آپ کو زیاده بتلانا ہے، جیسے بارش اور اس کی پیداوار کسانوں کو اچھی معلوم ہوتی ہے پھر جب وه خشک ہو جاتی ہے تو زرد رنگ میں اس کو تم دیکھتے ہو پھر وه بالکل چورا چورا ہو جاتی ہے اور آخرت میں سخت عذاب اور اللہ کی مغفرت اور رضامندی ہے اور دنیا کی زندگی بجز دھوکے کے سامان کے اور کچھ بھی تو نہیں. ');
INSERT INTO "junagarri" VALUES(57,21,'57:21','(آؤ) دوڑو اپنے رب کی مغفرت کی طرف اور اس جنت کی طرف جس کی وسعت آسمان وزمین کی وسعت کے برابر ہے یہ ان کے لیے بنائی ہے جو اللہ پر اور اس کے رسولوں پر ایمان رکھتے ہیں۔ یہ اللہ کا فضل ہے جسے چاہے دے اور اللہ بڑے فضل والا ہے. ');
INSERT INTO "junagarri" VALUES(57,22,'57:22','نہ کوئی مصیبت دنیا میں آتی ہے نہ (خاص) تمہاری جانوں میں ، مگر اس سے پہلے کہ ہم اس کو پیدا کریں وه ایک خاص کتاب میں لکھی ہوئی ہے ، یہ (کام) اللہ تعالیٰ پر (بالکل) آسان ہے.');
INSERT INTO "junagarri" VALUES(57,23,'57:23','تاکہ تم اپنے سے فوت شده کسی چیز پر رنجیده نہ ہو جایا کرو اور نہ عطا کرده چیز پر اترا جاؤ ، اور اترانے والے شیخی خوروں کو اللہ پسند نہیں فرماتا.');
INSERT INTO "junagarri" VALUES(57,24,'57:24','جو (خود بھی) بخل کریں اور دوسروں کو (بھی) بخل کی تعلیم دیں۔ سنو! جو بھی منھ پھیرے اللہ بےنیاز اور سزاوار حمد وثنا ہے.');
INSERT INTO "junagarri" VALUES(57,25,'57:25','یقیناً ہم نے اپنے پیغمبروں کو کھلی دلیلیں دے کر بھیجا اور ان کے ساتھ کتاب اور میزان (ترازو) نازل فرمایا تاکہ لوگ عدل پر قائم رہیں۔ اور ہم نے لوہے کو اتارا جس میں سخت ہیبت وقوت ہے اور لوگوں کے لیے اور بھی (بہت سے) فائدے ہیں اور اس لیے بھی کہ اللہ جان لے کہ اس کی اور اس کے رسولوں کی مدد بےدیکھے کون کرتا ہے ، بیشک اللہ قوت والا اور زبردست ہے. ');
INSERT INTO "junagarri" VALUES(57,26,'57:26','بیشک ہم نے نوح اور ابراہیم (علیہ السلام) کو (پیغمبر بنا کر) بھیجا اور ہم نے ان دونوں کی اولاد میں پیغمبری اور کتاب جاری رکھی تو ان میں سے کچھ تو راه یافتہ ہوئے اور ان میں سے اکثر بہت نافرمان رہے.');
INSERT INTO "junagarri" VALUES(57,27,'57:27','ان کے بعد پھر بھی ہم اپنے رسولوں کو پے در پے بھیجتے رہے اور ان کے بعد عیسیٰ بن مریم (علیہ السلام) کو بھیجا اور انہیں انجیل عطا فرمائی اور ان کے ماننے والوں کے دلوں میں شفقت اور رحم پیدا کردیا ہاں رہبانیت (ترک دنیا) تو ان لوگوں نے ازخود ایجاد کرلی تھی ہم نے ان پر اسے واجب نہ کیا تھا سوائے اللہ کی رضاجوئی کے ۔ سو انہوں نے اس کی پوری رعایت نہ کی ، پھر بھی ہم نے ان میں سے جو ایمان لائے تھے۔ انہیں ان کا اجر دیا اور ان میں زیاده تر لوگ نافرمان ہیں.');
INSERT INTO "junagarri" VALUES(57,28,'57:28','اے وه لوگو جو ایمان لائے ہو! اللہ سے ڈرتے رہا کرو اور اس کے رسول پر ایمان لاؤ اللہ تمہیں اپنی رحمت کا دوہرا حصہ دے گا اور تمہیں نور دے گا جس کی روشنی میں تم چلو پھرو گے اور تمہارے گناه بھی معاف فرمادے گا، اللہ بخشنے والا مہربان ہے.');
INSERT INTO "junagarri" VALUES(57,29,'57:29','یہ اس لیے کہ اہل کتاب جان لیں کہ اللہ کے فضل کے کسی حصہ پر بھی انہیں اختیار نہیں اور یہ کہ (سارا) فضل اللہ ہی کے ہاتھ ہے وه جسے چاہے دے، اور اللہ ہے ہی بڑے فضل والا.');
INSERT INTO "junagarri" VALUES(58,1,'58:1','یقیناً اللہ تعالیٰ نے اس عورت کی بات سنی جو تجھ سے اپنے شوہر کے بارے میں تکرار کر رہی تھی اور اللہ کے آگے شکایت کر رہی تھی، اللہ تعالیٰ تم دونوں کے سوال وجواب سن رہا تھا ، بیشک اللہ تعالیٰ سننے دیکھنے والا ہے.');
INSERT INTO "junagarri" VALUES(58,2,'58:2','تم میں سے جو لوگ اپنی بیویوں سے ظہار کرتے ہیں (یعنی انہیں ماں کہہ بیٹھتے ہیں) وه دراصل ان کی مائیں نہیں بن جاتیں، ان کی مائیں تو وہی ہیں جن کے بطن سے وه پیدا ہوئے ، یقیناً یہ لوگ ایک نامعقول اور جھوٹی بات کہتے ہیں۔ بیشک اللہ تعالیٰ معاف کرنے والا اور بخشنے والا ہے. ');
INSERT INTO "junagarri" VALUES(58,3,'58:3','جو لوگ اپنی بیویوں سے ظہار کریں پھر اپنی کہی ہوئی بات سے رجوع کرلیں تو ان کے ذمہ آپس میں ایک دوسرے کو ہاتھ لگانے سے پہلے ایک غلام آزاد کرنا ہے، اس کے ذریعہ تم نصیحت کیے جاتے ہو۔ اور اللہ تعالیٰ تمہارے تمام اعمال سے باخبر ہے.');
INSERT INTO "junagarri" VALUES(58,4,'58:4','ہاں جو شخص نہ پائے اس کے ذمہ دو مہینوں کے لگاتار روزے ہیں اس سے پہلے کہ ایک دوسرے کو ہاتھ لگائیں اور جس شخص کو یہ طاقت بھی نہ ہو اس پر ساٹھ مسکینوں کا کھانا کھلانا ہے۔ یہ اس لیے کہ تم اللہ کی اور اس کے رسول کی حکم برداری کرو، یہ اللہ تعالیٰ کی مقرر کرده حدیں ہیں اور کفار ہی کے لیے درد ناک عذاب ہے.');
INSERT INTO "junagarri" VALUES(58,5,'58:5','بیشک جو لوگ اللہ اور اس کے رسول کی مخالفت کرتے ہیں وه ذلیل کیے جائیں گے جیسے ان سے پہلے کے لوگ ذلیل کیے گئے تھے ، اور بیشک ہم واضح آیتیں اتار چکے ہیں اور کافروں کے لیے تو ذلت والا عذاب ہے.');
INSERT INTO "junagarri" VALUES(58,6,'58:6','جس دن اللہ تعالیٰ ان سب کو اٹھائے گا پھر انہیں ان کے کیے ہوئے عمل سے آگاه کرے گا، جسے اللہ نے شمار رکھا ہے اور جسے یہ بھول گئے تھے ، اور اللہ تعالیٰ ہر چیز سے واقف ہے. ');
INSERT INTO "junagarri" VALUES(58,7,'58:7','کیا تو نے نہیں دیکھا کہ اللہ آسمانوں کی اور زمین کی ہر چیز سے واقف ہے۔ تین آدمیوں کی سرگوشی نہیں ہوتی مگر اللہ ان کا چوتھا ہوتا ہے اور نہ پانچ کی مگر ان کا چھٹا وه ہوتا ہے اور نہ اس سے کم کی اور نہ زیاده کی مگر وه ساتھ ہی ہوتا ہے جہاں بھی وه ہوں ، پھر قیامت کے دن انہیں ان کے اعمال سے آگاه کرے گا بیشک اللہ تعالیٰ ہر چیز سے واقف ہے.');
INSERT INTO "junagarri" VALUES(58,8,'58:8','کیا تو نے ان لوگوں کو نہیں دیکھا؟ جنہیں کانا پھوسی سے روک دیا گیا تھا وه پھر بھی اس روکے ہوئے کام کو دوباره کرتے ہیں اور آپس میں گناه کی اور ظلم وزیادتی کی اور نافرمانیٴ پیغمبر کی سرگوشیاں کرتے ہیں ، اور جب تیرے پاس آتے ہیں تو تجھے ان لفظوں میں سلام کرتے ہیں جن لفظوں میں اللہ تعالیٰ نے نہیں کہا اور اپنے دل میں کہتے ہیں کہ اللہ تعالیٰ ہمیں اس پر جو ہم کہتے ہیں سزا کیوں نہیں دیتا ، ان کے لیے جہنم کافی (سزا) ہے جس میں یہ جائیں گے ، سو وه برا ٹھکانا ہے.');
INSERT INTO "junagarri" VALUES(58,9,'58:9','اے ایمان والو! تم جب سرگوشی کرو تو یہ سرگوشیاں گناه اور ظلم (زیادتی) اور نافرمانیٴ پیغمبر کی نہ ہوں ، بلکہ نیکی اور پرہیزگاری کی باتوں پر سرگوشی کرو اور اس اللہ سے ڈرتے رہو جس کے پاس تم سب جمع کیے جاؤ گے.');
INSERT INTO "junagarri" VALUES(58,10,'58:10','(بری) سرگوشیاں، پس شیطانی کام ہے جس سے ایمان داروں کو رنج پہنچے ۔ گو اللہ تعالیٰ کی اجازت کے بغیر وه انہیں کوئی نقصان نہیں پہنچا سکتا۔ اور ایمان والوں کو چاہئے کہ اللہ ہی پر بھروسہ رکھیں. ');
INSERT INTO "junagarri" VALUES(58,11,'58:11','اے مسلمانو! جب تم سے کہا جائے کہ مجلسوں میں ذرا کشادگی پیدا کرو تو تم جگہ کشاده کر دو اللہ تمہیں کشادگی دے گا ، اور جب کہا جائے کہ اٹھ کھڑے ہو جاؤ تو تم اٹھ کھڑے ہو جاؤ اللہ تعالیٰ تم میں سے ان لوگوں کے جو ایمان لائے ہیں اور جو علم دیئے گئے ہیں درجے بلند کر دے گا ، اور اللہ تعالیٰ (ہر اس کام سے) جو تم کر رہے ہو (خوب) خبردار ہے.');
INSERT INTO "junagarri" VALUES(58,12,'58:12','اے مسلمانو! جب تم رسول ﴿صلی اللہ علیہ وسلم﴾ سے سرگوشی کرنا چاہو تو اپنی سرگوشی سے پہلے کچھ صدقہ دے دیا کرو یہ تمہارے حق میں بہتر اور پاکیزه تر ہے ، ہاں اگر نہ پاؤ تو بیشک اللہ تعالیٰ بخشنے والا مہربان ہے.');
INSERT INTO "junagarri" VALUES(58,13,'58:13','کیا تم اپنی سرگوشی سے پہلے صدقہ نکالنے سے ڈر گئے؟ پس جب تم نے یہ نہ کیا اور اللہ تعالیٰ نے بھی تمہیں معاف فرما دیا تو اب (بخوبی) نمازوں کو قائم رکھو زکوٰة دیتے رہا کرو اور اللہ تعالیٰ کی اور اس کے رسول کی تابعداری کرتے رہو ۔ تم جو کچھ کرتے ہو اس (سب) سے اللہ (خوب) خبردار ہے.');
INSERT INTO "junagarri" VALUES(58,14,'58:14','کیا تو نے ان لوگوں کو نہیں دیکھا؟ جنہوں نے اس قوم سے دوستی کی جن پر اللہ غضبناک ہوچکا ہے ، نہ یہ (منافق) تمہارے ہی ہیں نہ ان کے ہیں باوجود علم کے پھر بھی جھوٹ پر قسمیں کھا رہے ہیں. ');
INSERT INTO "junagarri" VALUES(58,15,'58:15','اللہ تعالیٰ نے ان کے لیے سخت عذاب تیار کر رکھا ہے، تحقیق جو کچھ یہ کر رہے ہیں برا کر رہے ہیں. ');
INSERT INTO "junagarri" VALUES(58,16,'58:16','ان لوگوں نے تو اپنی قسموں کو ڈھال بنا رکھا ہے اور لوگوں کو اللہ کی راه سے روکتے ہیں ان کے لیے رسوا کرنے والا عذاب ہے.');
INSERT INTO "junagarri" VALUES(58,17,'58:17','ان کے مال اور ان کی اولاد اللہ کے ہاں کچھ کام نہ آئیں گی۔ یہ تو جہنمی ہیں ہمیشہ ہی اس میں رہیں گے.');
INSERT INTO "junagarri" VALUES(58,18,'58:18','جس دن اللہ تعالیٰ ان سب کو اٹھا کھڑا کرے گا تو یہ جس طرح تمہارے سامنے قسمیں کھاتے ہیں (اللہ تعالیٰ) کے سامنے بھی قسمیں کھانے لگیں گے اور سمجھیں گے کہ وه بھی کسی (دلیل) پر ہیں ، یقین مانو کہ بیشک وہی جھوٹے ہیں.');
INSERT INTO "junagarri" VALUES(58,19,'58:19','ان پر شیطان نے غلبہ حاصل کرلیا ہے ، اور انہیں اللہ کا ذکر بھلا دیا ہے یہ شیطانی لشکر ہے۔ کوئی شک نہیں کہ شیطانی لشکر ہی خسارے والا ہے. ');
INSERT INTO "junagarri" VALUES(58,20,'58:20','بیشک اللہ تعالیٰ کی اور اس کے رسول کی جو لوگ مخالفت کرتے ہیں وہی لوگ سب سے زیاده ذلیلوں میں ہیں. ');
INSERT INTO "junagarri" VALUES(58,21,'58:21','اللہ تعالیٰ لکھ چکا ہے کہ بیشک میں اور میرے پیغمبر غالب رہیں گے۔ یقیناً اللہ تعالیٰ زور آور اور غالب ہے. ');
INSERT INTO "junagarri" VALUES(58,22,'58:22','اللہ تعالیٰ پر اور قیامت کے دن پر ایمان رکھنے والوں کو آپ اللہ اور اس کے رسول کی مخالفت کرنے والوں سے محبت رکھتے ہوئے ہرگز نہ پائیں گے گو وه ان کے باپ یا ان کے بیٹے یا ان کے بھائی یا ان کے کنبہ (قبیلے) کے (عزیز) ہی کیوں نہ ہوں ۔ یہی لوگ ہیں جن کے دلوں میں اللہ تعالیٰ نے ایمان کو لکھ دیا ہے اور جن کی تائید اپنی روح سے کی ہے اور جنہیں ان جنتوں میں داخل کرے گا جن کے نیچے نہریں بہہ رہی ہیں جہاں یہ ہمیشہ رہیں گے، اللہ ان سے راضی ہے اور یہ اللہ سے خوش ہیں یہی لوگ اللہ کا گروہ ہیں،آگاه رہو! بے شک الله کا گروہ ہی کامیاب ہونے والا ہے . ');
INSERT INTO "junagarri" VALUES(59,1,'59:1','آسمانوں اور زمین کی ہر چیز اللہ تعالیٰ کی پاکی بیان کرتی ہے، اور وه غالب باحکمت ہے.');
INSERT INTO "junagarri" VALUES(59,2,'59:2','وہی ہے جس نے اہل کتاب میں سے کافروں کو ان کے گھروں سے پہلے حشر کے وقت نکالا ، تمہارا گمان (بھی) نہ تھا کہ وه نکلیں گے اور وه خود (بھی) سمجھ رہے تھے کہ ان کے (سنگین) قلعے انہیں اللہ (کے عذاب) سے بچا لیں گے پس ان پر اللہ (کا عذاب) ایسی جگہ سے آپڑا کہ انہیں گمان بھی نہ تھا اور ان کے دلوں میں اللہ نے رعب ڈال دیا وه اپنے گھروں کو اپنے ہی ہاتھوں اجاڑ رہے تھے اور مسلمانوں کے ہاتھوں (برباد کروا رہے تھے ) پس اے آنکھوں والو! عبرت حاصل کرو. ');
INSERT INTO "junagarri" VALUES(59,3,'59:3','اور اگر اللہ تعالیٰ نے ان پر جلا وطنی کو مقدر نہ کر دیا ہوتا تو یقیناً انہیں دنیا ہی میں عذاب دیتا ، اور آخرت میں (تو) ان کے لیے آگ کا عذاب ہے ہی.');
INSERT INTO "junagarri" VALUES(59,4,'59:4','یہ اس لیے کہ انہوں نے اللہ تعالیٰ کی اور اس کے رسول کی مخالفت کی اور جو بھی اللہ کی مخالفت کرے گا تو اللہ تعالیٰ بھی سخت عذاب کرنے والا ہے.');
INSERT INTO "junagarri" VALUES(59,5,'59:5','تم نے کھجوروں کے جو درخت کاٹ ڈالے یا جنہیں تم نے ان کی جڑوں پر باقی رہنے دیا۔ یہ سب اللہ تعالیٰ کے فرمان سے تھا اور اس لیے بھی کہ فاسقوں کو اللہ تعالیٰ رسوا کرے. ');
INSERT INTO "junagarri" VALUES(59,6,'59:6','اور ان کا جو مال اللہ تعالیٰ نے اپنے رسول کے ہاتھ لگایا ہے جس پر نہ تو تم نے اپنے گھوڑے دوڑائے ہیں اور نہ اونٹ بلکہ اللہ تعالیٰ اپنے رسول کو جس پر چاہے غالب کر دیتا ہے ، اور اللہ تعالیٰ ہر چیز پر قادر ہے.');
INSERT INTO "junagarri" VALUES(59,7,'59:7','بستیوں والوں کا جو (مال) اللہ تعالیٰ تمہارے لڑے بھڑے بغیر اپنے رسول کے ہاتھ لگائے وه اللہ کا ہے اور رسول کا اور قرابت والوں کا اور یتیموں مسکینوں کا اور مسافروں کا ہے تاکہ تمہارے دولت مندوں کے ہاتھ میں ہی یہ مال گردش کرتا نہ ره جائے اور تمہیں جو کچھ رسول دے لے لو، اور جس سے روکے رک جاؤ اور اللہ تعالیٰ سے ڈرتے رہا کرو، یقیناً اللہ تعالیٰ سخت عذاب والا ہے.');
INSERT INTO "junagarri" VALUES(59,8,'59:8','(فیء کا مال) ان مہاجر مسکینوں کے لیے ہے جو اپنے گھروں سے اور اپنے مالوں سے نکال دیئے گئے ہیں وه اللہ کے فضل اور اس کی رضامندی کے طلب گار ہیں اور اللہ تعالیٰ کی اور اس کے رسول کی مدد کرتے ہیں یہی راست باز لوگ ہیں. ');
INSERT INTO "junagarri" VALUES(59,9,'59:9','اور (ان کے لیے) جنہوں نے اس گھر میں (یعنی مدینہ) اور ایمان میں ان سے پہلے جگہ بنالی ہے اور اپنی طرف ہجرت کرکے آنے والوں سے محبت کرتے ہیں اور مہاجرین کو جو کچھ دے دیا جائے اس سے وه اپنے دلوں میں کوئی تنگی نہیں رکھتے بلکہ خود اپنے اوپر انہیں ترجیح دیتے ہیں گو خود کو کتنی ہی سخت حاجت ہو (بات یہ ہے) کہ جو بھی اپنے نفس کے بخل سے بچایا گیا وہی کامیاب (اور بامراد) ہے. ');
INSERT INTO "junagarri" VALUES(59,10,'59:10','اور (ان کے لیے) جو ان کے بعد آئیں جو کہیں گے کہ اے ہمارے پروردگار ہمیں بخش دے اور ہمارے ان بھائیوں کو بھی جو ہم سے پہلے ایمان لاچکے ہیں اور ایمان داروں کی طرف سے ہمارے دل میں کینہ (اور دشمنی) نہ ڈال ، اے ہمارے رب بیشک تو شفقت ومہربانی کرنے والا ہے.');
INSERT INTO "junagarri" VALUES(59,11,'59:11','کیا تو نے منافقوں کو نہ دیکھا؟ کہ اپنے اہل کتاب کافر بھائیوں سے کہتے ہیں اگر تم جلا وطن کیے گئے تو ضرور بالضرور ہم بھی تمہارے ساتھ نکل کھڑے ہوں گے اور تمہارے بارے میں ہم کبھی بھی کسی کی بات نہ مانیں گے اور اگر تم سے جنگ کی جائے گی تو بخدا ہم تمہاری مدد کریں گے ، لیکن اللہ تعالیٰ گواہی دیتا ہے کہ یہ قطعاً جھوٹے ہیں. ');
INSERT INTO "junagarri" VALUES(59,12,'59:12','اگر وه جلاوطن کیے گئے تو یہ ان کے ساتھ نہ جائیں گے اور اگر ان سے جنگ کی گئی تو یہ ان کی مدد (بھی) نہ کریں گے اور اگر (بالفرض) مدد پر آبھی گئے تو پیٹھ پھیر کر (بھاگ کھڑے) ہوں گے پھر مدد نہ کیے جائیں گے. ');
INSERT INTO "junagarri" VALUES(59,13,'59:13','(مسلمانو! یقین مانو) کہ تمہاری ہیبت ان کے دلوں میں بہ نسبت اللہ کی ہیبت کے بہت زیاده ہے، یہ اس لیے کہ یہ بے سمجھ لوگ ہیں. ');
INSERT INTO "junagarri" VALUES(59,14,'59:14','یہ سب مل کر بھی تم سے لڑ نہیں سکتے ہاں یہ اور بات ہے کہ قلعہ بند مقامات میں ہوں یا دیواروں کی آڑ میں ہوں ، ان کی لڑائی تو ان میں آپس میں ہی بہت سخت ہے گو آپ انہیں متحد سمجھ رہے ہیں لیکن ان کے دل دراصل ایک دوسرے سے جدا ہیں ۔ اس لیے کہ یہ بےعقل لوگ ہیں. ');
INSERT INTO "junagarri" VALUES(59,15,'59:15','ان لوگوں کی طرح جو ان سے کچھ ہی پہلے گزرے ہیں جنہوں نے اپنے کام کا وبال چکھ لیا اور جن کے لیے المناک عذاب (تیار) ہے. ');
INSERT INTO "junagarri" VALUES(59,16,'59:16','شیطان کی طرح کہ اس نے انسان سے کہا کفر کر، جب وه کفر کر چکا تو کہنے لگا میں تو تجھ سے بری ہوں ، میں تو اللہ رب العالمین سے ڈرتا ہوں. ');
INSERT INTO "junagarri" VALUES(59,17,'59:17','پس دونوں کا انجام یہ ہوا کہ آتش (دوزخ) میں ہمیشہ کے لیے گئے اور ظالموں کی یہی سزا ہے. ');
INSERT INTO "junagarri" VALUES(59,18,'59:18','اے ایمان والو! اللہ سے ڈرتے رہو اور ہر شخص دیکھ (بھال) لے کہ کل (قیامت) کے واسطے اس نے (اعمال کا) کیا (ذخیره) بھیجا ہے ۔ اور (ہر وقت) اللہ سے ڈرتے رہو۔ اللہ تمہارے سب اعمال سے باخبر ہے. ');
INSERT INTO "junagarri" VALUES(59,19,'59:19','اور تم ان لوگوں کی طرح مت ہو جانا جنہوں نے اللہ (کے احکام) کو بھلا دیا تو اللہ نے بھی انہیں اپنی جانوں سے غافل کر دیا ، اور ایسے ہی لوگ نافرمان (فاسق) ہوتے ہیں.');
INSERT INTO "junagarri" VALUES(59,20,'59:20','اہل نار اور اہل جنت (باہم) برابر نہیں ۔ جو اہل جنت ہیں وہی کامیاب ہیں (اور جو اہل نار ہیں وه ناکام ہیں). ');
INSERT INTO "junagarri" VALUES(59,21,'59:21','اگر ہم اس قرآن کو کسی پہاڑ پر اتارتے تو تو دیکھتا کہ خوف الٰہی سے وه پست ہوکر ٹکڑے ٹکڑے ہو جاتا ہم ان مثالوں کو لوگوں کے سامنے بیان کرتے ہیں تاکہ وه غور وفکر کریں. ');
INSERT INTO "junagarri" VALUES(59,22,'59:22','وہی اللہ ہے جس کے سوا کوئی معبود نہیں، چھپے کھلے کا جاننے والا مہربان اور رحم کرنے والا.');
INSERT INTO "junagarri" VALUES(59,23,'59:23','وہی اللہ ہے جس کے سوا کوئی معبود نہیں، بادشاه، نہایت پاک، سب عیبوں سے صاف، امن دینے والا، نگہبان، غالب زورآور، اور بڑائی والا، پاک ہے اللہ ان چیزوں سے جنہیں یہ اس کا شریک بناتے ہیں.');
INSERT INTO "junagarri" VALUES(59,24,'59:24','وہی اللہ ہے پیدا کرنے والا وجود بخشنے والا ، صورت بنانے والا، اسی کے لیے (نہایت) اچھے نام ہیں ، ہر چیز خواه وه آسمانوں میں ہو خواه زمین میں ہو اس کی پاکی بیان کرتی ہے ، اور وہی غالب حکمت والا ہے. ');
INSERT INTO "junagarri" VALUES(60,1,'60:1','اے وه لوگو جو ایمان لائے ہو! میرے اور (خود) اپنے دشمنوں کو اپنا دوست نہ بناؤ تم تو دوستی سے ان کی طرف پیغام بھیجتے ہو اور وه اس حق کے ساتھ جو تمہارے پاس آچکا ہے کفر کرتے ہیں، پیغمبر کو اور خود تمہیں بھی محض اس وجہ سے جلاوطن کرتے ہیں کہ تم اپنے رب پر ایمان رکھتے ہو ، اگر تم میری راه میں جہاد کے لیے اور میری رضا مندی کی طلب میں نکلتے ہو (تو ان سے دوستیاں نہ کرو )، تم ان کے پاس محبت کا پیغام پوشیده پوشیده بھیجتے ہو اور مجھے خوب معلوم ہے جو تم نے چھپایا اور وه بھی جو تم نے ظاہر کیا، تم میں سے جو بھی اس کام کو کرے گا وه یقیناً راه راست سے بہک جائے گا. ');
INSERT INTO "junagarri" VALUES(60,2,'60:2','اگر وه تم پر کہیں قابو پالیں تو وه تمہارے (کھلے) دشمن ہو جائیں اور برائی کے ساتھ تم پر دست درازی اور زبان درازی کرنے لگیں اور (دل سے) چاہنے لگیں کہ تم بھی کفر کرنے لگ جاؤ. ');
INSERT INTO "junagarri" VALUES(60,3,'60:3','تمہاری قرابتیں، رشتہداریاں، اور اولاد تمہیں قیامت کے دن کام نہ آئیں گی ، اللہ تعالیٰ تمہارے درمیان فیصلہ کر دے گا اور جو کچھ تم کر رہے ہو اسے اللہ خوب دیکھ رہا ہے.');
INSERT INTO "junagarri" VALUES(60,4,'60:4','(مسلمانو!) تمہارے لیے حضرت ابراہیم میں اور ان کے ساتھیوں میں بہترین نمونہ ہے ، جبکہ ان سب نے اپنی قوم سے برملا کہہ دیا کہ ہم تم سے اور جن جن کی تم اللہ کے سوا عبادت کرتے ہو ان سب سے بالکل بیزار ہیں ۔ ہم تمہارے (عقائد کے) منکر ہیں جب تک تم اللہ کی وحدانیت پر ایمان نہ لاؤ ہم میں تم میں ہمیشہ کے لیے بغض وعداوت ظاہر ہوگئی لیکن ابراہیم کی اتنی بات تو اپنے باپ سے ہوئی تھی کہ میں تمہارے لیے استغفار ضرور کروں گا اور تمہارے لیے مجھے اللہ کے سامنے کسی چیز کا اختیار کچھ بھی نہیں۔ اے ہمارے پروردگار تجھی پر ہم نے بھروسہ کیا ہے اور تیری ہی طرف رجوع کرتے ہیں اور تیری ہی طرف لوٹنا ہے.');
INSERT INTO "junagarri" VALUES(60,5,'60:5','اے ہمارے رب! تو ہمیں کافروں کی آزمائش میں نہ ڈال اور اے ہمارے پالنے والے ہماری خطاؤں کو بخش دے، بیشک تو ہی غالب، حکمت والا ہے.');
INSERT INTO "junagarri" VALUES(60,6,'60:6','یقیناً تمہارے لیے ان میں اچھا نمونہ (اور عمده پیروی ہے خاص کر) ہر اس شخص کے لیے جو اللہ کی اور قیامت کے دن کی ملاقات کی امید رکھتا ہو ، اور اگر کوئی روگردانی کرے تو اللہ تعالیٰ بالکل بےنیاز ہے اور سزا وار حمد وثنا ہے.');
INSERT INTO "junagarri" VALUES(60,7,'60:7','کیا عجب کہ عنقریب ہی اللہ تعالیٰ تم میں اور تمہارے دشمنوں میں محبت پیدا کر دے ۔ اللہ کو سب قدرتیں ہیں اور اللہ (بڑا) غفور رحیم ہے.');
INSERT INTO "junagarri" VALUES(60,8,'60:8','جن لوگوں نے تم سے دین کے بارے میں لڑائی نہیں لڑی اور تمہیں جلاوطن نہیں کیا ان کے ساتھ سلوک واحسان کرنے اور منصفانہ بھلے برتاؤ کرنے سے اللہ تعالیٰ تمہیں نہیں روکتا ، بلکہ اللہ تعالیٰ تو انصاف کرنے والوں سے محبت کرتا ہے. ');
INSERT INTO "junagarri" VALUES(60,9,'60:9','اللہ تعالیٰ تمہیں صرف ان لوگوں کی محبت سے روکتا ہے جنہوں نے تم سے دین کے بارے میں لڑائیاں لڑیں اور تمہیں شہر سے نکال دیئے اور شہر سے نکالنے والوں کی مدد کی جو لوگ ایسے کفار سے محبت کریں وه (قطعاً) ظالم ہیں. ');
INSERT INTO "junagarri" VALUES(60,10,'60:10','اے ایمان والو! جب تمہارے پاس مومن عورتیں ہجرت کرکے آئیں تو تم ان کا امتحان لو ۔ دراصل ان کے ایمان کو بخوبی جاننے والا تو اللہ ہی ہے لیکن اگر وه تمہیں ایمان والیاں معلوم ہوں تو اب تم انہیں کافروں کی طرف واپس نہ کرو، یہ ان کے لیے حلال نہیں اور نہ وه ان کے لیے حلال ہیں ، اور جو خرچ ان کافروں کا ہوا ہو وه انہیں ادا کردو ، ان عورتوں کو ان کے مہر دے کر ان سے نکاح کر لینے میں تم پر کوئی گناه نہیں اور کافر عورتوں کی ناموس اپنے قبضہ میں نہ رکھو اور جو کچھ تم نے خرچ کیا ہو ، مانگ لو اور جو کچھ ان کافروں نے خرچ کیا ہو وه بھی مانگ لیں یہ اللہ کا فیصلہ ہے جو تمہارے درمیان کر رہا ہے ، اللہ تعالیٰ بڑے علم (اور) حکمت والا ہے.');
INSERT INTO "junagarri" VALUES(60,11,'60:11','اور اگر تمہاری کوئی بیوی تمہارے ہاتھ سے نکل جائے اور کافروں کے پاس چلی جائے پھر تمہیں اس کے بدلے کا وقت مل جائے تو جن کی بیویاں چلی گئی ہیں انہیں ان کے اخراجات کے برابر ادا کر دو، اور اس اللہ تعالیٰ سے ڈرتے رہو جس پر تم ایمان رکھتے ہو.');
INSERT INTO "junagarri" VALUES(60,12,'60:12','اے پیغمبر! جب مسلمان عورتیں آپ سے ان باتوں پر بیعت کرنے آئیں کہ وه اللہ کے ساتھ کسی کو شریک نہ کریں گی، چوری نہ کریں گی، زنا کاری نہ کریں گی، اپنی اولاد کو نہ مار ڈالیں گی اور کوئی ایسا بہتان نہ باندھیں گی جو خود اپنے ہاتھوں پیروں کے سامنے گھڑ لیں اور کسی نیک کام میں تیری بےحکمی نہ کریں گی تو آپ ان سے بیعت کر لیا کریں ، اور ان کے لیے اللہ سے مغفرت طلب کریں بیشک اللہ تعالیٰ بخشنے اور معاف کرنے والا ہے.');
INSERT INTO "junagarri" VALUES(60,13,'60:13','اے مسلمانو! تم اس قوم سے دوستی نہ رکھو جن پر اللہ کا غضب نازل ہوچکا ہے جو آخرت سے اس طرح مایوس ہوچکے ہیں جیسے کہ مرده اہل قبر سے کافر ناامید ہیں. ');
INSERT INTO "junagarri" VALUES(61,1,'61:1','زمین وآسمان کی ہر ہر چیز اللہ تعالیٰ کی پاکی بیان کرتی ہے اور وہی غالب حکمت والا ہے.');
INSERT INTO "junagarri" VALUES(61,2,'61:2','اے ایمان والو ! تم وه بات کیوں کہتے ہو جو کرتے نہیں.');
INSERT INTO "junagarri" VALUES(61,3,'61:3','تم جو کرتے نہیں اس کا کہنا اللہ تعالیٰ کو سخت ناپسند ہے. ');
INSERT INTO "junagarri" VALUES(61,4,'61:4','بیشک اللہ تعالیٰ ان لوگوں سے محبت کرتا ہے جو اس کی راه میں صف بستہ جہاد کرتے ہیں گویا وه سیسہ پلائی ہوئی عمارت ہیں. ');
INSERT INTO "junagarri" VALUES(61,5,'61:5','اور (یاد کرو) جبکہ موسیٰ نے اپنی قوم سے کہا اے میری قوم کے لوگو! تم مجھے کیوں ستا رہے ہو حالانکہ تمہیں (بخوبی) معلوم ہے کہ میں تمہاری جانب اللہ کا رسول ہوں پس جب وه لوگ ٹیڑھے ہی رہے تو اللہ نے ان کے دلوں کو (اور) ٹیڑھا کر دیا ، اور اللہ تعالیٰ نافرمان قوم کو ہدایت نہیں دیتا.');
INSERT INTO "junagarri" VALUES(61,6,'61:6','اور جب مریم کے بیٹے عیسیٰ نے کہا اے (میری قوم)، بنی اسرائیل! میں تم سب کی طرف اللہ کا رسول ہوں مجھ سے پہلے کی کتاب تورات کی میں تصدیق کرنے والا ہوں اور اپنے بعد آنے والے ایک رسول کی میں تمہیں خوشخبری سنانے والا ہوں جنکا نام احمد ہے ۔ پھر جب وه ان کے پاس کھلی دلیلیں لائے تو یہ کہنے لگے، یہ تو کھلا جادو ہے. ');
INSERT INTO "junagarri" VALUES(61,7,'61:7','اس شخص سے زیاده ظالم اور کون ہوگا جو اللہ پر جھوٹ (افترا) باندھے حالانکہ وه اسلام کی طرف بلایا جاتا ہے اور اللہ ایسے ظالموں کو ہدایت نہیں کرتا.');
INSERT INTO "junagarri" VALUES(61,8,'61:8','وه چاہتے ہیں کہ اللہ کے نور کو اپنے منھ سے بجھا دیں اور اللہ اپنے نور کو کمال تک پہنچانے والا ہے گو کافر برا مانیں. ');
INSERT INTO "junagarri" VALUES(61,9,'61:9','وہی ہے جس نے اپنے رسول کو ہدایت اور سچا دین دے کر بھیجا تاکہ اسے اور تمام مذاہب پر غالب کر دے اگرچہ مشرکین ناخوش ہوں. ');
INSERT INTO "junagarri" VALUES(61,10,'61:10','اے ایمان والو! کیا میں تمہیں وه تجارت بتلا دوں جو تمہیں درد ناک عذاب سے بچا لے؟');
INSERT INTO "junagarri" VALUES(61,11,'61:11','اللہ تعالیٰ پر اور اس کے رسول پر ایمان لاؤ اور اللہ کی راه میں اپنے مال اور اپنی جانوں سے جہاد کرو۔ یہ تمہارے لیے بہتر ہے اگر تم میں علم ہو.');
INSERT INTO "junagarri" VALUES(61,12,'61:12','اللہ تعالیٰ تمہارے گناه معاف فرما دے گا اور تمہیں ان جنتوں میں پہنچائے گا جن کے نیچے نہریں جاری ہوں گی اور صاف ستھرے گھروں میں جو جنت عدن میں ہوں گے، یہ بہت بڑی کامیابی ہے.');
INSERT INTO "junagarri" VALUES(61,13,'61:13','اور تمہیں ایک دوسری (نعمت) بھی دے گا جسے تم چاہتے ہو وه اللہ کی مدد اور جلد فتح یابی ہے ، ایمان والوں کو خوشخبری دے دو. ');
INSERT INTO "junagarri" VALUES(61,14,'61:14','اے ایمان والو! تم اللہ تعالیٰ کے مددگار بن جاؤ ۔ جس طرح حضرت مریم کے بیٹے حضرت عیسیٰ نے حواریوں سے فرمایا کہ کون ہے جو اللہ کی راه میں میرا مددگار بنے ؟ حواریوں نے کہا ہم اللہ کی راه میں مددگار ہیں، پس بنی اسرائیل میں سے ایک جماعت تو ایمان لائی اور ایک جماعت نے کفر کیا تو ہم نے مومنوں کی انکے دشمنوں کے مقابلہ میں مدد کی پس وه غالب آ گئے. ');
INSERT INTO "junagarri" VALUES(62,1,'62:1','(ساری چیزیں) جو آسمانوں اور زمین میں ہیں اللہ تعالیٰ کی پاکی بیان کرتی ہیں (جو) بادشاه نہایت پاک (ہے) غالب وباحکمت ہے.');
INSERT INTO "junagarri" VALUES(62,2,'62:2','وہی ہے جس نے ناخوانده لوگوں میں ان ہی میں سے ایک رسول بھیجا جو انہیں اس کی آیتیں پڑھ کر سناتا ہے اور ان کو پاک کرتا ہے اور انہیں کتاب وحکمت سکھاتا ہے۔ یقیناً یہ اس سے پہلے کھلی گمراہی میں تھے.');
INSERT INTO "junagarri" VALUES(62,3,'62:3','اور دوسروں کے لیے بھی انہیں میں سے جو اب تک ان سے نہیں ملے۔ اور وہی غالب باحکمت ہے.');
INSERT INTO "junagarri" VALUES(62,4,'62:4','یہ اللہ کا فضل ہے جسے چاہے اپنا فضل دے اور اللہ تعالیٰ بہت بڑے فضل کا مالک ہے.');
INSERT INTO "junagarri" VALUES(62,5,'62:5','جن لوگوں کو تورات پر عمل کرنے کا حکم دیا گیا پھر انہوں نے اس پر عمل نہیں کیا ان کی مثال اس گدھے کی سی ہے جو بہت سی کتابیں لادے ہو ۔ اللہ کی باتوں کو جھٹلانے والوں کی بڑی بری مثال ہے اور اللہ (ایسے) ظالم قوم کو ہدایت نہیں دیتا.');
INSERT INTO "junagarri" VALUES(62,6,'62:6','کہہ دیجئے کہ اے یہودیو! اگر تمہارا دعویٰ ہے کہ تم اللہ کے دوست ہو دوسرے لوگوں کے سوا تو تم موت کی تمنا کرو اگر تم سچے ہو. ');
INSERT INTO "junagarri" VALUES(62,7,'62:7','یہ کبھی بھی موت کی تمنا نہ کریں گے بوجہ ان اعمال کے جو اپنے آگے اپنے ہاتھوں بھیج رکھے ہیں اور اللہ ظالموں کو خوب جانتا ہے. ');
INSERT INTO "junagarri" VALUES(62,8,'62:8','کہہ دیجئے! کہ جس موت سے تم بھاگتے پھرتے ہو وه تو تمہیں پہنچ کر رہے گی پھر تم سب چھپے کھلے کے جاننے والے (اللہ) کی طرف لوٹائے جاؤ گے اور وه تمہیں تمہارے کیے ہوئے تمام کام بتلا دے گا.');
INSERT INTO "junagarri" VALUES(62,9,'62:9','اے وه لوگو جو ایمان لائے ہو! جمعہ کے دن نماز کی اذان دی جائے تو تم اللہ کے ذکر کی طرف دوڑ پڑو اور خرید وفروخت چھوڑ دو ۔ یہ تمہارے حق میں بہت ہی بہتر ہے اگر تم جانتے ہو.');
INSERT INTO "junagarri" VALUES(62,10,'62:10','پھر جب نماز ہو چکے تو زمین میں پھیل جاؤ اور اللہ کا فضل تلاش کرو اور بکثرت اللہ کا ذکر کیا کرو تاکہ تم فلاح پالو.');
INSERT INTO "junagarri" VALUES(62,11,'62:11','اور جب کوئی سودا بکتا دیکھیں یا کوئی تماشا نظر آجائے تو اس کی طرف دوڑ جاتے ہیں اور آپ کو کھڑا ہی چھوڑ دیتے ہیں ۔ آپ کہہ دیجئے کہ اللہ کے پاس جو ہے وه کھیل اور تجارت سے بہتر ہے ۔ اور اللہ تعالیٰ بہترین روزی رساں ہے. ');
INSERT INTO "junagarri" VALUES(63,1,'63:1','تیرے پاس جب منافق آتے ہیں تو کہتے ہیں کہ ہم اس بات کے گواه ہیں کہ بیشک آپ اللہ کے رسول ہیں ، اور اللہ جانتا ہے کہ یقیناً آپ اس کے رسول ہیں ۔ اور اللہ گواہی دیتا ہے کہ یہ منافق قطعاً جھوٹے ہیں. ');
INSERT INTO "junagarri" VALUES(63,2,'63:2','انہوں نے اپنی قسموں کو ڈھال بنا رکھا ہے پس اللہ کی راه سے رک گئے بیشک برا ہے وه کام جو یہ کر رہے ہیں.');
INSERT INTO "junagarri" VALUES(63,3,'63:3','یہ اس سبب سے ہے کہ یہ ایمان لاکر پھر کافر ہوگئے پس ان کے دلوں پر مہر کر دی گئی۔ اب یہ نہیں سمجھتے.');
INSERT INTO "junagarri" VALUES(63,4,'63:4','جب آپ انہیں دیکھ لیں تو ان کے جسم آپ کو خوشنما معلوم ہوں ، یہ جب باتیں کرنے لگیں تو آپ ان کی باتوں پر (اپنا) کان لگائیں ، گویا کہ یہ لکڑیاں ہیں دیوار کے سہارے سے لگائی ہوئیں ، ہر (سخت) آواز کو اپنے خلاف سمجھتے ہیں ۔ یہی حقیقی دشمن ہیں ان سے بچو اللہ انہیں غارت کرے کہاں سے پھرے جاتے ہیں.');
INSERT INTO "junagarri" VALUES(63,5,'63:5','اور جب ان سے کہا جاتا ہے کہ آؤ تمہارے لیے اللہ کے رسول استغفار کریں تو اپنے سر مٹکاتے ہیں اور آپ دیکھیں گے کہ وه تکبر کرتے ہوئے رک جاتے ہیں. ');
INSERT INTO "junagarri" VALUES(63,6,'63:6','ان کے حق میں آپ کا استغفار کرنا اور نہ کرنا دونوں برابر ہے ۔ اللہ تعالیٰ انہیں ہر گز نہ بخشے گا ۔ بیشک اللہ تعالیٰ (ایسے) نافرمان لوگوں کو ہدایت نہیں دیتا.');
INSERT INTO "junagarri" VALUES(63,7,'63:7','یہی وه ہیں جو کہتے ہیں کہ جو لوگ رسول اللہ کے پاس ہیں ان پر کچھ خرچ نہ کرو یہاں تک کہ وه ادھر ادھر ہو جائیں اور آسمان وزمین کے کل خزانے اللہ تعالیٰ کی ملکیت ہیں لیکن یہ منافق بے سمجھ ہیں. ');
INSERT INTO "junagarri" VALUES(63,8,'63:8','یہ کہتے ہیں کہ اگر ہم اب لوٹ کر مدینہ جائیں گے تو عزت والا وہاں سے ذلت والے کو نکال دے گا ۔ سنو! عزت تو صرف اللہ تعالیٰ کے لیے اور اس کے رسول کے لیے اور ایمان داروں کے لیے ہے لیکن یہ منافق جانتے نہیں. ');
INSERT INTO "junagarri" VALUES(63,9,'63:9','اے مسلمانو! تمہارے مال اور تمہاری اولاد تمہیں اللہ کے ذکر سے غافل نہ کر دیں ۔ اور جو ایسا کریں وه بڑے ہی زیاں کار لوگ ہیں.');
INSERT INTO "junagarri" VALUES(63,10,'63:10','اور جو کچھ ہم نے تمہیں دے رکھا ہے اس میں سے (ہماری راه میں) اس سے پہلے خرچ کرو کہ تم میں سے کسی کو موت آ جائے تو کہنے لگے اے میرے پروردگار! مجھے تو تھوڑی دیر کی مہلت کیوں نہیں دیتا؟ کہ میں صدقہ کروں اور نیک لوگوں میں سے ہو جاؤں.');
INSERT INTO "junagarri" VALUES(63,11,'63:11','اور جب کسی کا مقرره وقت آجاتا ہے پھر اسے اللہ تعالیٰ ہر گز مہلت نہیں دیتا اور جو کچھ تم کرتے ہو اس سے اللہ تعالیٰ بخوبی باخبر ہے.');
INSERT INTO "junagarri" VALUES(64,1,'64:1','(تمام چیزیں) جو آسمانوں اور زمین میں ہیں اللہ کی پاکی بیان کرتی ہیں اسی کی سلطنت ہے اور اسی کی تعریف ہے ، اور وه ہر چیز پر قادر ہے.');
INSERT INTO "junagarri" VALUES(64,2,'64:2','اسی نے تمہیں پیدا کیا ہے سو تم میں سے بعضے تو کافر ہیں اور بعض ایمان والے ہیں، اور جو کچھ تم کر رہے ہو اللہ تعالیٰ خوب دیکھ رہا ہے. ');
INSERT INTO "junagarri" VALUES(64,3,'64:3','اسی نے آسمانوں کو اور زمین کو عدل وحکمت سے پیدا کیا ، اسی نے تمہاری صورتیں بنائیں اور بہت اچھی بنائیں اور اسی کی طرف لوٹنا ہے. ');
INSERT INTO "junagarri" VALUES(64,4,'64:4','وه آسمان وزمین کی ہر ہر چیز کا علم رکھتا ہے اور جو کچھ تم چھپاؤ اور جو ظاہر کرو وه (سب کو) جانتا ہے۔ اللہ تو سینوں کی باتوں تک کو جاننے والا ہے. ');
INSERT INTO "junagarri" VALUES(64,5,'64:5','کیا تمہارے پاس اس سے پہلے کے کافروں کی خبر نہیں پہنچی؟ جنہوں نے اپنے اعمال کا وبال چکھ لیا اور جن کے لیے دردناک عذاب ہے. ');
INSERT INTO "junagarri" VALUES(64,6,'64:6','یہ اس لیے کہ ان کے پاس ان کے رسول واضح دلائل لے کر آئے تو انہوں نے کہہ دیا کہ کیا انسان ہماری رہنمائی کرے گا ؟ پس انکار کر دیا اور منھ پھیر لیا اور اللہ نے بھی بےنیازی کی ، اور اللہ تو ہے ہی بہت بےنیاز سب خوبیوں والا. ');
INSERT INTO "junagarri" VALUES(64,7,'64:7','ان کافروں نے خیال کیا ہے کہ دوباره زنده نہ کیے جائیں گے ۔ آپ کہہ دیجئے کہ کیوں نہیں اللہ کی قسم! تم ضرور دوباره اٹھائے جاؤ گے پھر جو تم نے کیا ہے اس کی خبر دیئے جاؤ گے اور اللہ پر یہ بالکل ہی آسان ہے. ');
INSERT INTO "junagarri" VALUES(64,8,'64:8','سو تم اللہ پر اور اس کے رسول پر اور اس نور پر جسے ہم نے نازل فرمایا ہے ایمان لاؤ اور اللہ تعالیٰ تمہارے ہر عمل پر باخبر ہے.');
INSERT INTO "junagarri" VALUES(64,9,'64:9','جس دن تم سب کو اس جمع ہونے کے دن جمع کرے گا وہی دن ہے ہار جیت کا اور جو شخص اللہ پر ایمان لاکر نیک عمل کرے اللہ اس سے اس کی برائیاں دور کر دے گا اور اسے جنتوں میں داخل کرے گا جن کے نیچے نہریں بہہ رہی ہیں جن میں وه ہمیشہ ہمیشہ رہیں گے۔ یہی بہت بڑی کامیابی ہے.');
INSERT INTO "junagarri" VALUES(64,10,'64:10','اور جن لوگوں نے کفر کیا اور ہماری آیتوں کو جھٹلایا وہی (سب) جہنمی ہیں (جو) جہنم میں ہمیشہ رہیں گے، وه بہت برا ٹھکانا ہے.');
INSERT INTO "junagarri" VALUES(64,11,'64:11','کوئی مصیبت اللہ کی اجازت کے بغیر نہیں پہنچ سکتی ، جو اللہ پر ایمان لائے اللہ اس کے دل کو ہدایت دیتا ہے اور اللہ ہر چیز کو خوب جاننے والا ہے.');
INSERT INTO "junagarri" VALUES(64,12,'64:12','(لوگو) اللہ کا کہنا مانو اور رسول کا کہنا مانو۔ پس اگر تم اعراض کرو تو ہمارے رسول کے ذمہ صرف صاف صاف پہنچا دینا ہے. ');
INSERT INTO "junagarri" VALUES(64,13,'64:13','اللہ کے سوا کوئی معبود برحق نہیں اور مومنوں کو اللہ ہی پر توکل رکھنا چاہئے. ');
INSERT INTO "junagarri" VALUES(64,14,'64:14','اے ایمان والو! تمہاری بعض بیویاں اور بعض بچے تمہارے دشمن ہیں پس ان سے ہوشیار رہنا اور اگر تم معاف کر دو اور درگزر کر جاؤ اور بخش دو تو اللہ تعالیٰ بخشنے والا مہربان ہے. ');
INSERT INTO "junagarri" VALUES(64,15,'64:15','تمہارے مال اور اولاد تو سراسر تمہاری آزمائش ہیں ۔ اور بہت بڑا اجر اللہ کے پاس ہے. ');
INSERT INTO "junagarri" VALUES(64,16,'64:16','پس جہاں تک تم سے ہوسکے اللہ سے ڈرتے رہو اور سنتے اور مانتے چلے جاؤ اور اللہ کی راه میں خیرات کرتے رہو جو تمہارے لیے بہتر ہے اور جو شخص اپنے نفس کی حرص سے محفوظ رکھا جائے وہی کامیاب ہے.');
INSERT INTO "junagarri" VALUES(64,17,'64:17','اگر تم اللہ کو اچھا قرض دو گے (یعنی اس کی راه میں خرچ کرو گے ) تو وه اسے تمہارے لیے بڑھاتا جائے گا اور تمہارے گناه بھی معاف فرما دے گا ۔ اللہ بڑا قدر دان بڑا بردبار ہے. ');
INSERT INTO "junagarri" VALUES(64,18,'64:18','وه پوشیده اور ظاہر کا جاننے والا ہے زبردست حکمت والا (ہے).');
INSERT INTO "junagarri" VALUES(65,1,'65:1','اے نبی! (اپنی امت سے کہو کہ) جب تم اپنی بیویوں کو طلاق دینا چاہو تو ان کی عدت (کے دنوں کے آغاز) میں انہیں طلاق دو اور عدت کا حساب رکھو ، اور اللہ سے جو تمہارا پروردگار ہے ڈرتے رہو، نہ تم انہیں ان کے گھروں سے نکالو اور نہ وه (خود) نکلیں ہاں یہ اور بات ہے کہ وه کھلی برائی کر بیٹھیں ، یہ اللہ کی مقرر کرده حدیں ہیں، جو شخص اللہ کی حدوں سے آگے بڑھ جائے اس نے یقیناً اپنے اوپر ظلم کیا ، تم نہیں جانتے شاید اس کے بعد اللہ تعالیٰ کوئی نئی بات پیدا کر دے. ');
INSERT INTO "junagarri" VALUES(65,2,'65:2','پس جب یہ عورتیں اپنی عدت پوری کرنے کے قریب پہنچ جائیں تو انہیں یا تو قاعده کے مطابق اپنے نکاح میں رہنے دو یا دستور کے مطابق انہیں الگ کر دو اور آپس میں سے دو عادل شخصوں کو گواه کر لو اور اللہ کی رضامندی کے لیے ٹھیک ٹھیک گواہی دو ۔ یہی ہے وه جس کی نصیحت اسے کی جاتی ہے جو اللہ پر اور قیامت کے دن پر ایمان رکھتا ہو اور جو شخص اللہ سے ڈرتا ہے اللہ اس کے لیے چھٹکارے کی شکل نکال دیتا ہے. ');
INSERT INTO "junagarri" VALUES(65,3,'65:3','اور اسے ایسی جگہ سے روزی دیتا ہے جس کا اسے گمان بھی نہ ہو اور جو شخص اللہ پر توکل کرے گا اللہ اسے کافی ہوگا۔ اللہ تعالیٰ اپنا کام پورا کرکے ہی رہے گا ۔ اللہ تعالیٰ نے ہر چیز کا ایک اندازه مقرر کر رکھا ہے. ');
INSERT INTO "junagarri" VALUES(65,4,'65:4','تمہاری عورتوں میں سے جو عورتیں حیض سے ناامید ہو گئی ہوں، اگر تمہیں شبہ ہو تو ان کی عدت تین مہینے ہے اور ان کی بھی جنہیں حیض آنا شروع ہی نہ ہوا ہو اور حاملہ عورتوں کی عدت ان کے وضع حمل ہے اور جو شخص اللہ تعالیٰ سے ڈرے گا اللہ اس کے (ہر) کام میں آسانی کر دے گا.');
INSERT INTO "junagarri" VALUES(65,5,'65:5','یہ اللہ کا حکم ہے جو اس نے تمہاری طرف اتارا ہے اور جو شخص اللہ سے ڈرے گا اللہ اس کے گناه مٹا دے گا اور اسے بڑا بھاری اجر دے گا.');
INSERT INTO "junagarri" VALUES(65,6,'65:6','تم اپنی طاقت کے مطابق جہاں تم رہتے ہو وہاں ان (طلاق والی) عورتوں کو رکھو اور انہیں تنگ کرنے کے لیے تکلیف نہ پہنچاؤ اور اگر وه حمل سے ہوں تو جب تک بچہ پیدا ہولے انہیں خرچ دیتے رہا کرو پھر اگر تمہارے کہنے سے وہی دودھ پلائیں تو تم انہیں ان کی اجرت دے دو اور باہم مناسب طور پر مشوره کر لیا کرو اور اگر تم آپس میں کشمکش کرو تو اس کے کہنے سے کوئی اور دودھ پلائے گی. ');
INSERT INTO "junagarri" VALUES(65,7,'65:7','کشادگی والے کو اپنی کشادگی سے خرچ کرنا چاہئے اور جس پر اس کے رزق کی تنگی کی گئی ہو اسے چاہئے کہ جو کچھ اللہ تعالیٰ نے اسے دے رکھا ہے اسی میں سے (اپنی حسب حیثیت) دے، کسی شخص کو اللہ تکلیف نہیں دیتا مگر اتنی ہی جتنی طاقت اسے دے رکھی ہے ، اللہ تنگی کے بعد آسانی وفراغت بھی کر دے گا. ');
INSERT INTO "junagarri" VALUES(65,8,'65:8','اور بہت سی بستی والوں نے اپنے رب کے حکم سے اور اس کے رسولوں سے سرتابی کی تو ہم نے بھی ان سے سخت حساب کیا اور انہیں عذاب دیا ان دیکھا (سخت) عذاب. ');
INSERT INTO "junagarri" VALUES(65,9,'65:9','پس انہوں نے اپنے کرتوت کا مزه چکھ لیا اور انجام کار ان کا خساره ہی ہوا.');
INSERT INTO "junagarri" VALUES(65,10,'65:10','ان کے لیے اللہ تعالیٰ نے سخت عذاب تیار کر رکھا ہے، پس اللہ سے ڈرو اے عقل مند ایمان والو۔ یقیناً اللہ نے تمہاری طرف نصیحت اتار دی ہے.');
INSERT INTO "junagarri" VALUES(65,11,'65:11','(یعنی) رسول جو تمہیں اللہ کے صاف صاف احکام پڑھ سناتا ہے تاکہ ان لوگوں کو جو ایمان لائیں اور نیک عمل کریں وه تاریکیوں سے روشنی کی طرف لے آئے ، اور جو شخص اللہ پر ایمان لائے اور نیک عمل کرے اللہ اسے ایسی جنتوں میں داخل کرے گا جس کے نیچے نہریں جاری ہیں جن میں یہ ہمیشہ ہمیشہ رہیں گے۔ بیشک اللہ نے اسے بہترین روزی دے رکھی ہے.');
INSERT INTO "junagarri" VALUES(65,12,'65:12','اللہ وه ہے جس نے سات آسمان بنائے اور اسی کے مثل زمینیں بھی ۔ اس کا حکم ان کے درمیان اترتا ہے تاکہ تم جان لو کہ اللہ ہر چیز پر قادر ہے۔ اور اللہ تعالیٰ نے ہر چیز کو بہ اعتبار علم گھیر رکھا ہے. ');
INSERT INTO "junagarri" VALUES(66,1,'66:1','اے نبی! جس چیز کو اللہ نے آپ کے لیے حلال کر دیا ہے اسے آپ کیوں حرام کرتے ہیں ؟ (کیا) آپ اپنی بیویوں کی رضامندی حاصل کرنا چاہتے ہیں اور اللہ بخشنے والا رحم کرنے والا ہے.');
INSERT INTO "junagarri" VALUES(66,2,'66:2','تحقیق کہ اللہ تعالیٰ نے تمہارے لیے قسموں کو کھول ڈالنا مقرر کر دیا ہے اور اللہ تمہارا کارساز ہے اور وہی (پورے) علم والا، حکمت والا ہے.');
INSERT INTO "junagarri" VALUES(66,3,'66:3','اور یاد کر جب نبی نے اپنی بعض عورتوں سے ایک پوشیده بات کہی ، پس جب اس نے اس بات کی خبر کر دی اور اللہ نے اپنے نبی کو اس پر آگاه کر دیا تو نبی نے تھوڑی سی بات تو بتا دی اور تھوڑی سی ٹال گئے ، پھر جب نبی نے اپنی اس بیوی کو یہ بات بتائی تو وه کہنے لگی اس کی خبر آپ کو کس نے دی ۔ کہا سب جاننے والے پوری خبر رکھنے والے اللہ نے مجھے یہ بتلایا ہے. ');
INSERT INTO "junagarri" VALUES(66,4,'66:4','(اے نبی کی دونوں بیویو!) اگر تم دونوں اللہ کے سامنے توبہ کرلو (تو بہت بہتر ہے ) یقیناً تمہارے دل جھک پڑے ہیں اور اگر تم نبی کے خلاف ایک دوسرے کی مدد کرو گی پس یقیناً اس کا کارساز اللہ ہے اور جبریل ہیں اور نیک اہل ایمان اور ان کے علاوه فرشتے بھی مدد کرنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(66,5,'66:5','اگر وه (پیغمبر) تمہیں طلاق دے دیں تو بہت جلد انہیں ان کا رب! تمہارے بدلے تم سے بہتر بیویاں عنایت فرمائے گا ، جو اسلام والیاں، ایمان والیاں اللہ کے حضور جھکنے والیاں توبہ کرنے والیاں، عبادت بجا لانے والیاں روزے رکھنے والیاں ہوں گی بیوه اور کنواریاں. ');
INSERT INTO "junagarri" VALUES(66,6,'66:6','اے ایمان والو! تم اپنے آپ کو اور اپنے گھر والوں کو اس آگ سے بچاؤ جس کا ایندھن انسان ہیں اور پتھر جس پر سخت دل مضبوط فرشتے مقرر ہیں جنہیں جو حکم اللہ تعالیٰ دیتا ہے اس کی نافرمانی نہیں کرتے بلکہ جو حکم دیا جائے بجا لاتے ہیں.');
INSERT INTO "junagarri" VALUES(66,7,'66:7','اے کافرو! آج تم عذر وبہانہ مت کرو۔ تمہیں صرف تمہارے کرتوت کا بدلہ دیا جارہا ہے.');
INSERT INTO "junagarri" VALUES(66,8,'66:8','اے ایمان والو! تم اللہ کے سامنے سچی خالص توبہ کرو ۔ قریب ہے کہ تمہارا رب تمہارے گناه دور کر دے اور تمہیں ایسی جنتوں میں داخل کرے جن کے نیچے نہریں جاری ہیں۔ جس دن اللہ تعالیٰ نبی کو اور ایمان والوں کو جو ان کے ساتھ ہیں رسوا نہ کرے گا۔ ان کا نور ان کے سامنے اور ان کے دائیں دوڑ رہا ہوگا۔ یہ دعائیں کرتے ہوں گے اے ہمارے رب ہمیں کامل نور عطا فرما اور ہمیں بخش دے یقیناً تو ہر چیز پر قادر ہے.');
INSERT INTO "junagarri" VALUES(66,9,'66:9','اے نبی! کافروں اور منافقوں سے جہاد کرو اور ان پر سختی کرو ان کا ٹھکانا جہنم ہے اور وه بہت بری جگہ ہے.');
INSERT INTO "junagarri" VALUES(66,10,'66:10','اللہ تعالیٰ نے کافروں کے لیے نوح کی اور لوط کی بیوی کی مثال بیان فرمائی یہ دونوں ہمارے بندوں میں سے دو (شائستہ اور) نیک بندوں کے گھر میں تھیں، پھر ان کی انہوں نے خیانت کی پس وه دونوں (نیک بندے) ان سے اللہ کے (کسی عذاب کو) نہ روک سکے اور حکم دے دیا گیا (اے عورتوں) دوزخ میں جانے والوں کے ساتھ تم دونوں بھی چلی جاؤ. ');
INSERT INTO "junagarri" VALUES(66,11,'66:11','اور اللہ تعالیٰ نے ایمان والوں کے لیے فرعون کی بیوی کی مثال بیان فرمائی جبکہ اس نے دعا کی کہ اے میرے رب! میرے لیے اپنے پاس جنت میں مکان بنا اور مجھے فرعون سے اور اس کے عمل سے بچا اور مجھے ظالم لوگوں سے خلاصی دے.');
INSERT INTO "junagarri" VALUES(66,12,'66:12','اور (مثال بیان فرمائی) مریم بنت عمران کی جس نے اپنے ناموس کی حفاظت کی پھر ہم نے اپنی طرف سے اس میں جان پھونک دی اور (مریم) اس نے اپنے رب کی باتوں اور اس کی کتابوں کی تصدیق کی اور عبادت گزاروں میں سے تھی. ');
INSERT INTO "junagarri" VALUES(67,1,'67:1','بہت بابرکت ہے وه (اللہ) جس کے ہاتھ میں بادشاہی ہے اور جو ہر چیز پر قدرت رکھنے والا ہے.');
INSERT INTO "junagarri" VALUES(67,2,'67:2','جس نے موت اور حیات کو اس لیے پیدا کیاکہ تمہیں آزمائے کہ تم میں سے اچھے کام کون کرتا ہے ، اور وه غالب (اور) بخشنے والا ہے.');
INSERT INTO "junagarri" VALUES(67,3,'67:3','جس نے سات آسمان اوپر تلے بنائے۔ (تو اے دیکھنے والے) اللہ رحمٰن کی پیدائش میں کوئی بے ضابطگی نہ دیکھے گا ، دوباره (نظریں ڈال کر) دیکھ لے کیا کوئی شگاف بھی نظر آرہا ہے. ');
INSERT INTO "junagarri" VALUES(67,4,'67:4','پھر دوہرا کر دو دو بار دیکھ لے تیری نگاه تیری طرف ذلیل (و عاجز) ہو کر تھکی ہوئی لوٹ آئے گی. ');
INSERT INTO "junagarri" VALUES(67,5,'67:5','بیشک ہم نے آسمان دنیا کو چراغوں (ستاروں) سے آراستہ کیا اور انہیں شیطانوں کے مارنے کا ذریعہ بنا دیا اور شیطانوں کے لیے ہم نے (دوزخ کا جلانے والا) عذاب تیار کر دیا.');
INSERT INTO "junagarri" VALUES(67,6,'67:6','اور اپنے رب کے ساتھ کفر کرنے والوں کے لیے جہنم کا عذاب ہے اور وه کیا ہی بری جگہ ہے.');
INSERT INTO "junagarri" VALUES(67,7,'67:7','جب اس میں یہ ڈالے جائیں گے تو اس کی بڑے زور کی آواز سنیں گے اور وه جوش مار رہی ہوگی. ');
INSERT INTO "junagarri" VALUES(67,8,'67:8','قریب ہے کہ (ابھی) غصے کے مارے پھٹ جائے ، جب کبھی اس میں کوئی گروه ڈالا جائے گا اس سے جہنم کے داروغے پوچھیں گے کہ کیا تمہارے پاس ڈرانے والا کوئی نہیں آیا تھا؟ ');
INSERT INTO "junagarri" VALUES(67,9,'67:9','وه جواب دیں گے کہ بیشک آیا تھا لیکن ہم نے اسے جھٹلایا اور ہم نے کہا کہ اللہ تعالیٰ نےکچھ بھی نازل نہیں فرمایا۔ تم بہت بڑی گمراہی میں ہی ہو. ');
INSERT INTO "junagarri" VALUES(67,10,'67:10','اور کہیں گے اگر ہم سنتے ہوتے یا عقل رکھتے ہوتے تو دوزخیوں میں (شریک) نہ ہوتے. ');
INSERT INTO "junagarri" VALUES(67,11,'67:11','پس انہوں نے اپنے جرم کا اقبال کر لیا ۔ اب یہ دوزخی دفع ہوں (دور ہوں). ');
INSERT INTO "junagarri" VALUES(67,12,'67:12','بیشک جو لوگ اپنے پروردگار سےغائبانہ طور پر ڈرتے رہتے ہیں ان کے لیے بخشش ہے اور بڑا ثواب ہے. ');
INSERT INTO "junagarri" VALUES(67,13,'67:13','تم اپنی باتوں کو چھپاؤ یا ظاہر کرو وه تو سینوں کی پوشیدگی کو بھی بخوبی جانتا ہے. ');
INSERT INTO "junagarri" VALUES(67,14,'67:14','کیا وہی نہ جانے جس نے پیدا کیا ؟ پھر وه باریک بین اور باخبر بھی ہو. ');
INSERT INTO "junagarri" VALUES(67,15,'67:15','وه ذات جس نے تمہارے لیے زمین کو پست ومطیع کردیا تاکہ تم اس کی راہوں میں چلتے پھرتے رہو اور اللہ کی روزیاں کھاؤ (پیو ) اسی کی طرف (تمہیں) جی کر اٹھ کھڑا ہونا ہے.');
INSERT INTO "junagarri" VALUES(67,16,'67:16','کیا تم اس بات سے بے خوف ہوگئے ہو کہ آسمانوں والا تمہیں زمین میں دھنسا دے اور اچانک زمین لرزنے لگے. ');
INSERT INTO "junagarri" VALUES(67,17,'67:17','یا کیا تم اس بات سے نڈر ہوگئے ہو کہ آسمانوں والا تم پر پتھر برسادے ؟ پھر تو تمہیں معلوم ہو ہی جائے گا کہ میرا ڈرانا کیسا تھا. ');
INSERT INTO "junagarri" VALUES(67,18,'67:18','اوران سے پہلے لوگوں نے بھی جھٹلایا تھا تو دیکھو ان پر میرا عذاب کیسا کچھ ہوا؟');
INSERT INTO "junagarri" VALUES(67,19,'67:19','کیا یہ اپنے اوپر پر کھولے ہوئے اور (کبھی کبھی) سمیٹے ہوئے (اڑنے والے) پرندوں کو نہیں دیکھتے ، انہیں (اللہ) رحمٰن ہی (ہوا وفضا میں) تھامے ہوئے ہے ۔ بیشک ہر چیز اس کی نگاه میں ہے. ');
INSERT INTO "junagarri" VALUES(67,20,'67:20','سوائے اللہ کے تمہارا وه کون سا لشکر ہے جو تمہاری مدد کرسکے کافر تو سراسر دھوکے ہی میں ہیں. ');
INSERT INTO "junagarri" VALUES(67,21,'67:21','اگر اللہ تعالیٰ اپنی روزی روک لے تو بتاؤ کون ہے جو پھر تمہیں روزی دے گا ؟ بلکہ (کافر) تو سرکشی اور بدکنے پر اڑگئے ہیں. ');
INSERT INTO "junagarri" VALUES(67,22,'67:22','اچھا وه شخص زیاده ہدایت والا ہے جو اپنے منھ کے بل اوندھا ہو کر چلے یا وه جو سیدھا (پیروں کے بل) راه راست پر چلا ہو؟ ');
INSERT INTO "junagarri" VALUES(67,23,'67:23','کہہ دیجئے کہ وہی (اللہ) ہے جس نے تمہیں پیدا کیا اور تمہارے کان آنکھیں اور دل بنائے تم بہت ہی کم شکر گزاری کرتے ہو. ');
INSERT INTO "junagarri" VALUES(67,24,'67:24','کہہ دیجئے! کہ وہی ہے جس نے تمہیں زمین میں پھیلا دیا اور اس کی طرف سے تم اکٹھے کیے جاؤ گے. ');
INSERT INTO "junagarri" VALUES(67,25,'67:25','(کافر) پوچھتے ہیں کہ وه وعده کب ظاہر ہوگا اگر تم سچے ہو (تو بتاؤ؟) ');
INSERT INTO "junagarri" VALUES(67,26,'67:26','آپ کہہ دیجئے کہ اس کا علم تو اللہ ہی کو ہے ، میں تو صرف کھلے طور پر آگاه کر دینے والا ہوں. ');
INSERT INTO "junagarri" VALUES(67,27,'67:27','جب یہ لوگ اس وعدے کو قریب تر پالیں گے اس وقت ان کافروں کے چہرے بگڑ جائیں گے اور کہہ دیا جائے گا کہ یہی ہے جسے تم طلب کیا کرتے تھے. ');
INSERT INTO "junagarri" VALUES(67,28,'67:28','آپ کہہ دیجئے! اچھا اگر مجھے اور میرے ساتھیوں کو اللہ تعالیٰ ہلاک کردے یا ہم پر رحم کرے (بہر صورت یہ تو بتاؤ) کہ کافروں کو دردناک عذاب سے کون بچائے گا؟ ');
INSERT INTO "junagarri" VALUES(67,29,'67:29','آپ کہہ دیجئے! کہ وہی رحمٰن ہے ہم تو اس پر ایمان لاچکے اور اسی پر ہمارا بھروسہ ہے ۔ تمہیں عنقریب معلوم ہو جائے گا کہ صریح گمراہی میں کون ہے؟ ');
INSERT INTO "junagarri" VALUES(67,30,'67:30','آپ کہہ دیجئے! کہ اچھا یہ تو بتاؤ کہ اگر تمہارے (پینے کا) پانی زمین میں اتر جائے تو کون ہے جو تمہارے لیے نتھرا ہوا پانی لائے؟ ');
INSERT INTO "junagarri" VALUES(68,1,'68:1','ن ، قسم ہے قلم کی اور اس کی جو کچھ کہ وه (فرشتے) لکھتے ہیں. ');
INSERT INTO "junagarri" VALUES(68,2,'68:2','تو اپنے رب کے فضل سے دیوانہ نہیں ہے. ');
INSERT INTO "junagarri" VALUES(68,3,'68:3','اور بے شک تیرے لیے بے انتہا اجر ہے.*');
INSERT INTO "junagarri" VALUES(68,4,'68:4','اور بیشک تو بہت بڑے (عمده) اخلاق پر ہے. ');
INSERT INTO "junagarri" VALUES(68,5,'68:5','پس اب تو بھی دیکھ لے گا اور یہ بھی دیکھ لیں گے. ');
INSERT INTO "junagarri" VALUES(68,6,'68:6','کہ تم میں سے کون فتنہ میں پڑا ہوا ہے.');
INSERT INTO "junagarri" VALUES(68,7,'68:7','بیشک تیرا رب اپنی راه سے بہکنے والوں کو خوب جانتا ہے، اور وه راه یافتہ لوگوں کو بھی بخوبی جانتا ہے.');
INSERT INTO "junagarri" VALUES(68,8,'68:8','پس تو جھٹلانے والوں کی نہ مان. ');
INSERT INTO "junagarri" VALUES(68,9,'68:9','وه تو چاہتے ہیں کہ تو ذرا ڈھیلا ہو تو یہ بھی ڈھیلے پڑ جائیں. ');
INSERT INTO "junagarri" VALUES(68,10,'68:10','اور تو کسی ایسے شخص کا بھی کہا نہ ماننا جو زیاده قسمیں کھانے والا.');
INSERT INTO "junagarri" VALUES(68,11,'68:11','بے وقار، کمینہ، عیب گو، چغل خور.');
INSERT INTO "junagarri" VALUES(68,12,'68:12','بھلائی سے روکنے والا حد سے بڑھ جانے والا گنہگار.');
INSERT INTO "junagarri" VALUES(68,13,'68:13','گردن کش پھر ساتھ ہی بے نسب ہو. ');
INSERT INTO "junagarri" VALUES(68,14,'68:14','اس کی سرکشی صرف اس لیے ہے کہ وه مال والا اور بیٹوں والا ہے. ');
INSERT INTO "junagarri" VALUES(68,15,'68:15','جب اس کے سامنے ہماری آیتیں پڑھی جاتی ہیں تو کہہ دیتا ہے کہ یہ تو اگلوں کے قصے ہیں.');
INSERT INTO "junagarri" VALUES(68,16,'68:16','ہم بھی اس کی سونڈ (ناک) پر داغ دیں گے. ');
INSERT INTO "junagarri" VALUES(68,17,'68:17','بیشک ہم نے انہیں اسی طرح آزما لیا جس طرح ہم نے باغ والوں کو آزمایا تھا جبکہ انہوں نے قسمیں کھائیں کہ صبح ہوتے ہی اس باغ کے پھل اتار لیں گے. ');
INSERT INTO "junagarri" VALUES(68,18,'68:18','اور انشاءاللہ نہ کہا.');
INSERT INTO "junagarri" VALUES(68,19,'68:19','پس اس پر تیرے رب کی جانب سے ایک بلا چاروں طرف گھوم گئی اور یہ سو ہی رہے تھے. ');
INSERT INTO "junagarri" VALUES(68,20,'68:20','پس وه باغ ایسا ہو گیا جیسے کٹی ہوئی کھیتی. ');
INSERT INTO "junagarri" VALUES(68,21,'68:21','اب صبح ہوتے ہی انہوں نے ایک دوسرے کو آوازیں دیں.');
INSERT INTO "junagarri" VALUES(68,22,'68:22','کہ اگر تمہیں پھل اتارنے ہیں تو اپنی کھیتی پر سویرے ہی سویرے چل پڑو.');
INSERT INTO "junagarri" VALUES(68,23,'68:23','پھر یہ سب چپکے چپکے یہ باتیں کرتے ہوئے چلے. ');
INSERT INTO "junagarri" VALUES(68,24,'68:24','کہ آج کے دن کوئی مسکین تمہارے پاس نہ آنے پائے.*');
INSERT INTO "junagarri" VALUES(68,25,'68:25','اور لپکے ہوئے صبح صبح گئے۔ (سمجھ رہے تھے) کہ ہم قابو پاگئے. ');
INSERT INTO "junagarri" VALUES(68,26,'68:26','جب انہوں نے باغ دیکھا تو کہنے لگے یقیناً ہم راستہ بھول گئے.');
INSERT INTO "junagarri" VALUES(68,27,'68:27','نہیں نہیں بلکہ ہماری قسمت پھوٹ گئی. ');
INSERT INTO "junagarri" VALUES(68,28,'68:28','ان سب میں جو بہتر تھا اس نے کہا کہ میں تم سے نہ کہتا تھا کہ تم اللہ کی پاکیزگی کیوں نہیں بیان کرتے؟ ');
INSERT INTO "junagarri" VALUES(68,29,'68:29','تو سب کہنے لگے ہمارا رب پاک ہے بیشک ہم ہی ظالم تھے. ');
INSERT INTO "junagarri" VALUES(68,30,'68:30','پھر وه ایک دوسرے کی طرف رخ کر کے آپس میں ملامت کرنے لگے.');
INSERT INTO "junagarri" VALUES(68,31,'68:31','کہنے لگے ہائے افسوس! یقیناً ہم سرکش تھے.');
INSERT INTO "junagarri" VALUES(68,32,'68:32','کیا عجب ہے کہ ہمارا رب ہمیں اس سے بہتر بدلہ دے دے ہم تو اب اپنے رب سے ہی آرزو رکھتے ہیں.');
INSERT INTO "junagarri" VALUES(68,33,'68:33','یوں ہی آفت آتی ہے اور آخرت کی آفت بہت بڑی ہے۔ کاش انہیں سمجھ ہوتی. ');
INSERT INTO "junagarri" VALUES(68,34,'68:34','پرہیزگاروں کے لیے ان کے رب کے پاس نعمتوں والی جنتیں ہیں.');
INSERT INTO "junagarri" VALUES(68,35,'68:35','کیا ہم مسلمانوں کو مثل گناه گاروں کے کردیں گے. ');
INSERT INTO "junagarri" VALUES(68,36,'68:36','تمہیں کیا ہوگیا، کیسے فیصلے کر رہے ہو؟');
INSERT INTO "junagarri" VALUES(68,37,'68:37','کیا تمہارے پاس کوئی کتاب ہے جس میں تم پڑھتے ہو؟');
INSERT INTO "junagarri" VALUES(68,38,'68:38','کہ اس میں تمہاری من مانی باتیں ہوں؟');
INSERT INTO "junagarri" VALUES(68,39,'68:39','یا تم نے ہم سے کچھ قسمیں لی ہیں؟ جو قیامت تک باقی رہیں کہ تمہارے لیے وه سب ہے جو تم اپنی طرف سے مقرر کر لو. ');
INSERT INTO "junagarri" VALUES(68,40,'68:40','ان سے پوچھو تو کہ ان میں سے کون اس بات کا ذمہدار (اور دعویدار) ہے؟ ');
INSERT INTO "junagarri" VALUES(68,41,'68:41','کیا ان کے کوئی شریک ہیں؟ تو چاہئے کہ اپنے اپنے شریکوں کو لے آئیں اگر یہ سچے ہیں. ');
INSERT INTO "junagarri" VALUES(68,42,'68:42','جس دن پنڈلی کھول دی جائے گی اور سجدے کے لیے بلائے جائیں گے تو (سجده) نہ کر سکیں گے. ');
INSERT INTO "junagarri" VALUES(68,43,'68:43','نگاہیں نیچی ہوں گی اور ان پر ذلت و خواری چھارہی ہوگی ، حالانکہ یہ سجدے کے لیے (اس وقت بھی) بلائے جاتے تھے جب کہ صحیح سالم تھے. ');
INSERT INTO "junagarri" VALUES(68,44,'68:44','پس مجھے اور اس کلام کو جھٹلانے والے کو چھوڑ دے ہم انہیں اس طرح آہستہ آہستہ کھینچیں گے کہ انہیں معلوم بھی نہ ہوگا. ');
INSERT INTO "junagarri" VALUES(68,45,'68:45','اور میں انہیں ڈھیل دوں گا، بیشک میری تدبیر بڑی مضبوط ہے. ');
INSERT INTO "junagarri" VALUES(68,46,'68:46','کیا تو ان سے کوئی اجرت چاہتا ہے جس کے تاوان سے یہ دبے جاتے ہیں. ');
INSERT INTO "junagarri" VALUES(68,47,'68:47','یا کیا ان کے پاس علم غیب ہے جسے وه لکھتے ہوں. ');
INSERT INTO "junagarri" VALUES(68,48,'68:48','پس تو اپنے رب کے حکم کا صبر سے (انتظار کر ) اور مچھلی والے کی طرح نہ ہو جا جب کہ اس نے غم کی حالت میں دعا کی. ');
INSERT INTO "junagarri" VALUES(68,49,'68:49','اگر اسے اس کے رب کی نعمت نہ پالیتی تو یقیناً وه برے حالوں میں چٹیل میدان میں ڈال دیا جاتا. ');
INSERT INTO "junagarri" VALUES(68,50,'68:50','اسے اس کے رب نے پھر نوازا اور اسے نیک کاروں میں کر دیا. ');
INSERT INTO "junagarri" VALUES(68,51,'68:51','اور قریب ہے کہ کافر اپنی تیز نگاہوں سے آپ کو پھسلا دیں ، جب کبھی قرآن سنتے ہیں اور کہہ دیتے ہیں یہ تو ضرور دیوانہ ہے. ');
INSERT INTO "junagarri" VALUES(68,52,'68:52','در حقیقت یہ (قرآن) تو تمام جہان والوں کے لیے سراسر نصیحت ہی ہے. ');
INSERT INTO "junagarri" VALUES(69,1,'69:1','ثابت ہونے والی. ');
INSERT INTO "junagarri" VALUES(69,2,'69:2','ثابت ہونے والی کیا ہے؟ ');
INSERT INTO "junagarri" VALUES(69,3,'69:3','اور تجھے کیا معلوم ہے کہ وه ثابت شده کیا ہے؟ ');
INSERT INTO "junagarri" VALUES(69,4,'69:4','اس کھڑکا دینے والی کو ثمود اور عاد نے جھٹلا دیا تھا ');
INSERT INTO "junagarri" VALUES(69,5,'69:5','(جس کے نتیجہ میں) ثمود تو بے حد خوفناک (اور اونچی) آواز سے ہلاک کردیئے گئے. ');
INSERT INTO "junagarri" VALUES(69,6,'69:6','اور عاد بیحد تیز وتند ہوا سے غارت کردیئے گئے. ');
INSERT INTO "junagarri" VALUES(69,7,'69:7','جسے ان پر لگاتار سات رات اور آٹھ دن تک (اللہ نے) مسلط رکھا پس تم دیکھتے کہ یہ لوگ زمین پر اس طرح گر گئے جیسے کہ کھجور کے کھوکھلے تنے ہوں. ');
INSERT INTO "junagarri" VALUES(69,8,'69:8','کیا ان میں سے کوئی بھی تجھے باقی نظر آرہا ہے؟');
INSERT INTO "junagarri" VALUES(69,9,'69:9','فرعون اور اس سے پہلے کے لوگ اور جن کی بستیاں الٹ دی گئی ، انہوں نے بھی خطائیں کیں.');
INSERT INTO "junagarri" VALUES(69,10,'69:10','اور اپنے رب کے رسول کی نافرمانی کی (بالآخر) اللہ نے انہیں (بھی) زبردست گرفت میں لے لیا. ');
INSERT INTO "junagarri" VALUES(69,11,'69:11','جب پانی میں طغیانی آگئی تو اس وقت ہم نے تمہیں کشتی میں چڑھا لیا. ');
INSERT INTO "junagarri" VALUES(69,12,'69:12','تاکہ اسے تمہارے لیے نصیحت اور یادگار بنادیں ، اور (تاکہ) یاد رکھنے والے کان اسے یاد رکھیں. ');
INSERT INTO "junagarri" VALUES(69,13,'69:13','پس جب کہ صور میں ایک پھونک پھونکی جائے گی. ');
INSERT INTO "junagarri" VALUES(69,14,'69:14','اور زمین اور پہاڑ اٹھا لیے جائیں گے اور ایک ہی چوٹ میں ریزه ریزه کر دیے جائیں گے.');
INSERT INTO "junagarri" VALUES(69,15,'69:15','اس دن ہو پڑنے والی (قیامت) ہو پڑے گی.');
INSERT INTO "junagarri" VALUES(69,16,'69:16','اور آسمان پھٹ جائے گا اور اس دن بالکل بودا ہوجائے گا. ');
INSERT INTO "junagarri" VALUES(69,17,'69:17','اس کے کناروں پر فرشتے ہوں گے ، اور تیرے پروردگار کا عرش اس دن آٹھ (فرشتے) اپنے اوپر اٹھائے ہوئے ہوں گے. ');
INSERT INTO "junagarri" VALUES(69,18,'69:18','اس دن تم سب سامنے پیش کیے جاؤ گے، تمہارا کوئی بھید پوشیده نہ رہے گا.');
INSERT INTO "junagarri" VALUES(69,19,'69:19','سو جسے اس کا نامہٴ اعمال اس کے دائیں ہاتھ میں دیا جائے گا تو وه کہنے لگے گا کہ لو میرا نامہٴ اعمال پڑھو. ');
INSERT INTO "junagarri" VALUES(69,20,'69:20','مجھے تو کامل یقین تھا کہ مجھے اپنا حساب ملنا ہے. ');
INSERT INTO "junagarri" VALUES(69,21,'69:21','پس وه ایک دل پسند زندگی میں ہوگا.');
INSERT INTO "junagarri" VALUES(69,22,'69:22','بلند وبالا جنت میں. ');
INSERT INTO "junagarri" VALUES(69,23,'69:23','جس کے میوے جھکے پڑے ہوں گے. ');
INSERT INTO "junagarri" VALUES(69,24,'69:24','(ان سے کہا جائے گا) کہ مزے سے کھاؤ، پیو اپنے ان اعمال کے بدلے جو تم نے گزشتہ زمانے میں کیے. ');
INSERT INTO "junagarri" VALUES(69,25,'69:25','لیکن جسے اس (کے اعمال) کی کتاب اس کے بائیں ہاتھ میں دی جائے گی، وه تو کہے گا کہ کاش کہ مجھے میری کتاب دی ہی نہ جاتی. ');
INSERT INTO "junagarri" VALUES(69,26,'69:26','اور میں جانتا ہی نہ کہ حساب کیا ہے. ');
INSERT INTO "junagarri" VALUES(69,27,'69:27','کاش! کہ موت (میرا) کام ہی تمام کر دیتی. ');
INSERT INTO "junagarri" VALUES(69,28,'69:28','میرے مال نے بھی مجھے کچھ نفع نہ دیا.');
INSERT INTO "junagarri" VALUES(69,29,'69:29','میرا غلبہ بھی مجھ سے جاتا رہا. ');
INSERT INTO "junagarri" VALUES(69,30,'69:30','(حکم ہوگا) اسے پکڑ لو پھر اسے طوق پہنادو.');
INSERT INTO "junagarri" VALUES(69,31,'69:31','پھر اسے دوزخ میں ڈال دو. ');
INSERT INTO "junagarri" VALUES(69,32,'69:32','پھر اسے ایسی زنجیر میں جس کی پیمائش ستر ہاتھ کی ہے جکڑ دو. ');
INSERT INTO "junagarri" VALUES(69,33,'69:33','بیشک یہ اللہ عظمت والے پرایمان نہ رکھتا تھا. ');
INSERT INTO "junagarri" VALUES(69,34,'69:34','اور مسکین کے کھلانے پر رغبت نہ دلاتا تھا. ');
INSERT INTO "junagarri" VALUES(69,35,'69:35','پس آج اس کا نہ کوئی دوست ہے.');
INSERT INTO "junagarri" VALUES(69,36,'69:36','اور نہ سوائے پیپ کے اس کی کوئی غذا ہے. ');
INSERT INTO "junagarri" VALUES(69,37,'69:37','جسے گناه گاروں کے سوا کوئی نہیں کھائے گا. ');
INSERT INTO "junagarri" VALUES(69,38,'69:38','پس مجھے قسم ہے ان چیزوں کی جنہیں تم دیکھتے ہو.');
INSERT INTO "junagarri" VALUES(69,39,'69:39','اور ان چیزوں کی جنہیں تم نہیں دیکھتے. ');
INSERT INTO "junagarri" VALUES(69,40,'69:40','کہ بیشک یہ (قرآن) بزرگ رسول کا قول ہے. ');
INSERT INTO "junagarri" VALUES(69,41,'69:41','یہ کسی شاعر کا قول نہیں (افسوس) تمہیں بہت کم یقین ہے.');
INSERT INTO "junagarri" VALUES(69,42,'69:42','اور نہ کسی کاہن کا قول ہے ، (افسوس) بہت کم نصیحت لے رہے ہو. ');
INSERT INTO "junagarri" VALUES(69,43,'69:43','(یہ تو) رب العالمین کا اتارا ہوا ہے. ');
INSERT INTO "junagarri" VALUES(69,44,'69:44','اور اگر یہ ہم پر کوئی بھی بات بنا لیتا. ');
INSERT INTO "junagarri" VALUES(69,45,'69:45','تو البتہ ہم اس کا داہنا ہاتھ پکڑ لیتے. ');
INSERT INTO "junagarri" VALUES(69,46,'69:46','پھر اس کی شہ رگ کاٹ دیتے. ');
INSERT INTO "junagarri" VALUES(69,47,'69:47','پھر تم میں سے کوئی بھی مجھے اس سے روکنے والا نہ ہوتا. ');
INSERT INTO "junagarri" VALUES(69,48,'69:48','یقیناً یہ قرآن پرہیزگاروں کے لیے نصیحت ہے. ');
INSERT INTO "junagarri" VALUES(69,49,'69:49','ہمیں پوری طرح معلوم ہے کہ تم میں سے بعض اس کے جھٹلانے والے ہیں.');
INSERT INTO "junagarri" VALUES(69,50,'69:50','بیشک (یہ جھٹلانا) کافروں پر حسرت ہے. ');
INSERT INTO "junagarri" VALUES(69,51,'69:51','اور بیشک (و شبہ) یہ یقینی حق ہے. ');
INSERT INTO "junagarri" VALUES(69,52,'69:52','پس تو اپنے رب عظیم کی پاکی بیان کر. ');
INSERT INTO "junagarri" VALUES(70,1,'70:1','ایک سوال کرنے والے نے اس عذاب کا سوال کیا جو واضح ہونے والا ہے.');
INSERT INTO "junagarri" VALUES(70,2,'70:2','کافروں پر، جسے کوئی ہٹانے والا نہیں.');
INSERT INTO "junagarri" VALUES(70,3,'70:3','اس اللہ کی طرف سے جو سیڑھیوں والا ہے. ');
INSERT INTO "junagarri" VALUES(70,4,'70:4','جس کی طرف فرشتے اور روح چڑھتے ہیں ایک دن میں جس کی مقدار پچاس ہزار سال کی ہے. ');
INSERT INTO "junagarri" VALUES(70,5,'70:5','پس تو اچھی طرح صبر کر.');
INSERT INTO "junagarri" VALUES(70,6,'70:6','بیشک یہ اس (عذاب) کو دور سمجھ رہے ہیں. ');
INSERT INTO "junagarri" VALUES(70,7,'70:7','اور ہم اسے قریب ہی دیکھتے ہیں. ');
INSERT INTO "junagarri" VALUES(70,8,'70:8','جس دن آسمان مثل تیل کی تلچھٹ کے ہو جائے گا.*');
INSERT INTO "junagarri" VALUES(70,9,'70:9','اور پہاڑ مثل رنگین اون کے ہو جائیں گے. ');
INSERT INTO "junagarri" VALUES(70,10,'70:10','اور کوئی دوست کسی دوست کو نہ پوچھے گا.');
INSERT INTO "junagarri" VALUES(70,11,'70:11','(حالانکہ) ایک دوسرے کو دکھا دیئے جائیں گے ، گناهگار اس دن کے عذاب کے بدلے فدیے میں اپنے بیٹوں کو.');
INSERT INTO "junagarri" VALUES(70,12,'70:12','اپنی بیوی کو اور اپنے بھائی کو.');
INSERT INTO "junagarri" VALUES(70,13,'70:13','اور اپنے کنبے کو جو اسے پناه دیتا تھا.');
INSERT INTO "junagarri" VALUES(70,14,'70:14','اور روئے زمین کے سب لوگوں کو دینا چاہے گا تاکہ یہ اسے نجات دلا دے. ');
INSERT INTO "junagarri" VALUES(70,15,'70:15','(مگر) ہرگز یہ نہ ہوگا، یقیناً وه شعلہ والی (آگ) ہے. ');
INSERT INTO "junagarri" VALUES(70,16,'70:16','جو منھ اور سر کی کھال کھینچ لانے والی ہے. ');
INSERT INTO "junagarri" VALUES(70,17,'70:17','وه ہر اس شخص کو پکارے گی جو پیچھے ہٹتا اور منھ موڑتا ہے.');
INSERT INTO "junagarri" VALUES(70,18,'70:18','اور جمع کرکے سنبھال رکھتا ہے. ');
INSERT INTO "junagarri" VALUES(70,19,'70:19','بیشک انسان بڑے کچے دل والا بنایا گیا ہے. ');
INSERT INTO "junagarri" VALUES(70,20,'70:20','جب اسے مصیبت پہنچتی ہے تو ہڑبڑا اٹھتا ہے.');
INSERT INTO "junagarri" VALUES(70,21,'70:21','اور جب راحت ملتی ہے تو بخل کرنے لگتا ہے.');
INSERT INTO "junagarri" VALUES(70,22,'70:22','مگر وه نمازی.');
INSERT INTO "junagarri" VALUES(70,23,'70:23','جو اپنی نماز پر ہمیشگی کرنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(70,24,'70:24','اور جن کے مالوں میں مقرره حصہ ہے. ');
INSERT INTO "junagarri" VALUES(70,25,'70:25','مانگنے والوں کا بھی اور سوال سے بچنے والوں کا بھی. ');
INSERT INTO "junagarri" VALUES(70,26,'70:26','اور جو انصاف کے دن پر یقین رکھتے ہیں. ');
INSERT INTO "junagarri" VALUES(70,27,'70:27','اور جو اپنے رب کے عذاب سے ڈرتے رہتے ہیں. ');
INSERT INTO "junagarri" VALUES(70,28,'70:28','بیشک ان کے رب کا عذاب بے خوف ہونے کی چیز نہیں. ');
INSERT INTO "junagarri" VALUES(70,29,'70:29','اور جو لوگ اپنی شرم گاہوں کی (حرام سے) حفاظت کرتے ہیں.');
INSERT INTO "junagarri" VALUES(70,30,'70:30','ہاں ان کی بیویوں اور لونڈیوں کے بارے میں جن کے وه مالک ہیں انہیں کوئی ملامت نہیں. ');
INSERT INTO "junagarri" VALUES(70,31,'70:31','اب جو کوئی اس کے علاوه (راه) ڈھونڈے گا توایسے لوگ حد سے گزر جانے والے ہوں گے.');
INSERT INTO "junagarri" VALUES(70,32,'70:32','اور جو اپنی امانتوں کا اور اپنے قول و قرار کا پاس رکھتے ہیں. ');
INSERT INTO "junagarri" VALUES(70,33,'70:33','اور جو اپنی گواہیوں پر سیدھے اور قائم رہتے ہیں. ');
INSERT INTO "junagarri" VALUES(70,34,'70:34','اور جو اپنی نمازوں کی حفاظت کرتے ہیں.');
INSERT INTO "junagarri" VALUES(70,35,'70:35','یہی لوگ جنتوں میں عزت والے ہوں گے.');
INSERT INTO "junagarri" VALUES(70,36,'70:36','پس کافروں کو کیاہو گیا ہے کہ وه تیری طرف دوڑتے آتے ہیں.');
INSERT INTO "junagarri" VALUES(70,37,'70:37','دائیں اور بائیں سے گروه کے گروه.. ');
INSERT INTO "junagarri" VALUES(70,38,'70:38','کیا ان میں سے ہر ایک کی توقع یہ ہے کہ وه نعمتوں والی جنت میں داخل کیا جائے گا؟');
INSERT INTO "junagarri" VALUES(70,39,'70:39','(ایسا) ہرگز نہ ہوگا ۔ ہم نے انہیں اس (چیز) سے پیدا کیا ہے جسے وه جانتے ہیں. ');
INSERT INTO "junagarri" VALUES(70,40,'70:40','پس مجھے قسم ہے مشرقوں اور مغربوں کے رب کی (کہ) ہم یقیناً قادر ہیں.');
INSERT INTO "junagarri" VALUES(70,41,'70:41','اس پر کہ ان کے عوض ان سے اچھے لوگ لے آئیں اور ہم عاجز نہیں ہیں. ');
INSERT INTO "junagarri" VALUES(70,42,'70:42','پس تو انہیں جھگڑتا کھیلتا چھوڑ دے یہاں تک کہ یہ اپنے اس دن سے جاملیں جس کا ان سے وعده کیا جاتا ہے.');
INSERT INTO "junagarri" VALUES(70,43,'70:43','جس دن یہ قبروں سے دوڑتے ہوئے نکلیں گے، گویا کہ وه کسی جگہ کی طرف تیز تیز جا رہے ہیں. ');
INSERT INTO "junagarri" VALUES(70,44,'70:44','ان کی آنکھیں جھکی ہوئی ہوں گی ، ان پر ذلت چھا رہی ہوگی ، یہ ہے وه دن جس کا ان سے وعده کیا جاتا تھا. ');
INSERT INTO "junagarri" VALUES(71,1,'71:1','یقیناً ہم نے نوح (علیہ السلام) کو ان کی قوم کی طرف بھیجا کہ اپنی قوم کو ڈرا دو (اور خبردار کردو) اس سے پہلے کہ ان کے پاس دردناک عذاب آجائے. ');
INSERT INTO "junagarri" VALUES(71,2,'71:2','(نوح علیہ السلام نے) کہا اے میری قوم! میں تمہیں صاف صاف ڈرانے والا ہوں. ');
INSERT INTO "junagarri" VALUES(71,3,'71:3','کہ تم اللہ کی عبادت کرو اور اسی سے ڈرو اور میرا کہا مانو. ');
INSERT INTO "junagarri" VALUES(71,4,'71:4','تو وه تمہارے گناه بخش دے گا اور تمہیں ایک وقت مقرره تک چھوڑ دے گا ۔ یقیناً اللہ کا وعده جب آجاتا ہے تو مؤخر نہیں ہوتا ۔ کاش کہ تمہیں سمجھ ہوتی. ');
INSERT INTO "junagarri" VALUES(71,5,'71:5','(نوح علیہ السلام نے) کہا اے میرے پرورگار! میں نے اپنی قوم کو رات دن تیری طرف بلایا ہے. ');
INSERT INTO "junagarri" VALUES(71,6,'71:6','مگر میرے بلانے سے یہ لوگ اور زیاده بھاگنے لگے. ');
INSERT INTO "junagarri" VALUES(71,7,'71:7','میں نے جب کبھی انہیں تیری بخشش کے لیے بلایا انہوں نے اپنی انگلیاں اپنے کانوں میں ڈال لیں اور اپنے کپڑوں کو اوڑھ لیا اور اڑ گئے اور بڑا تکبر کیا. ');
INSERT INTO "junagarri" VALUES(71,8,'71:8','پھر میں نے انہیں بآواز بلند بلایا.');
INSERT INTO "junagarri" VALUES(71,9,'71:9','اور بیشک میں نےان سے علانیہ بھی کہا اور چپکے چپکے بھی. ');
INSERT INTO "junagarri" VALUES(71,10,'71:10','اور میں نے کہا کہ اپنے رب سے اپنے گناه بخشواؤ (اور معافی مانگو) وه یقیناً بڑا بخشنے والا ہے. ');
INSERT INTO "junagarri" VALUES(71,11,'71:11','وه تم پر آسمان کو خوب برستا ہوا چھوڑ دے گا. ');
INSERT INTO "junagarri" VALUES(71,12,'71:12','اور تمہیں خوب پے درپے مال اور اولاد میں ترقی دے گا اور تمہیں باغات دے گا اور تمہارے لیے نہریں نکال دے گا. ');
INSERT INTO "junagarri" VALUES(71,13,'71:13','تمہیں کیا ہوگیا ہے کہ تم اللہ کی برتری کا عقیده نہیں رکھتے. ');
INSERT INTO "junagarri" VALUES(71,14,'71:14','حالانکہ اس نے تمہیں طرح طرح سے پیدا کیا ہے.');
INSERT INTO "junagarri" VALUES(71,15,'71:15','کیا تم نہیں دیکھتے کہ اللہ تعالیٰ نے اوپر تلے کس طرح سات آسمان پیدا کر دیئے ہیں. ');
INSERT INTO "junagarri" VALUES(71,16,'71:16','اور ان میں چاند کو جگمگاتا بنایا ہے اور سورج کو روشن چراغ بنایا ہے. ');
INSERT INTO "junagarri" VALUES(71,17,'71:17','اور تم کو زمین سے ایک (خاص اہتمام سے) اگایا ہے (اور پیدا کیا ہے).');
INSERT INTO "junagarri" VALUES(71,18,'71:18','پھر تمہیں اسی میں لوٹا لے جائے گا اور (ایک خاص طریقہ) سے پھر نکالے گا. ');
INSERT INTO "junagarri" VALUES(71,19,'71:19','اور تمہارے لیے زمین کو اللہ تعالیٰ نے فرش بنادیا ہے. ');
INSERT INTO "junagarri" VALUES(71,20,'71:20','تاکہ تم اس کی کشاده راہوں میں چلو پھرو. ');
INSERT INTO "junagarri" VALUES(71,21,'71:21','نوح (علیہ السلام) نے کہا اے میرے پروردگار! ان لوگوں نے میری تو نافرمانی کی اور ایسوں کی فرمانبرداری کی جن کے مال واولاد نے ان کو (یقیناً) نقصان ہی میں بڑھایا ہے. ');
INSERT INTO "junagarri" VALUES(71,22,'71:22','اور ان لوگوں نے بڑا سخت فریب کیا. ');
INSERT INTO "junagarri" VALUES(71,23,'71:23','اور کہا انہوں نے کہ ہرگز اپنے معبودوں کو نہ چھوڑنا اور نہ ود اور سواع اور یغوث اور یعوق اور نسر کو (چھوڑنا). ');
INSERT INTO "junagarri" VALUES(71,24,'71:24','اور انہوں نے بہت سے لوگوں کو گمراه کیا (الٰہی) تو ان ظالموں کی گمراہی اور بڑھا. ');
INSERT INTO "junagarri" VALUES(71,25,'71:25','یہ لوگ بہ سبب اپنے گناہوں کے ڈبو دیئے گئے اور جہنم میں پہنچا دیئے گئے اور اللہ کے سوا اپنا کوئی مددگار انہوں نے نہ پایا.');
INSERT INTO "junagarri" VALUES(71,26,'71:26','اور (حضرت) نوح (علیہ السلام) نے کہا کہ اے میرے پالنے والے! تو روئے زمین پر کسی کافر کو رہنے سہنے والا نہ چھوڑ. ');
INSERT INTO "junagarri" VALUES(71,27,'71:27','اگر تو انہیں چھوڑ دے گا تو (یقیناً) یہ تیرے (اور) بندوں کو (بھی) گمراه کر دیں گے اور یہ فاجروں اور ڈھیٹ کافروں ہی کو جنم دیں گے.');
INSERT INTO "junagarri" VALUES(71,28,'71:28','اے میرے پروردگار! تو مجھے اور میرے ماں باپ اور جو بھی ایمان کی حالت میں میرے گھر میں آئے اور تمام مومن مردوں اور عورتوں کو بخش دے اور کافروں کو سوائے بربادی کے اور کسی بات میں نہ بڑھا. ');
INSERT INTO "junagarri" VALUES(72,1,'72:1','(اے محمد صلی اللہ علیہ وسلم) آپ کہہ دیں کہ مجھے وحی کی گئی ہے کہ جنوں کی ایک جماعت نے (قرآن) سنا اور کہا کہ ہم نے عجیب قرآن سنا ہے. ');
INSERT INTO "junagarri" VALUES(72,2,'72:2','جو راه راست کے طرف رہنمائی کرتا ہے ۔ ہم اس پر ایمان لا چکے (اب) ہم ہرگز کسی کو بھی اپنے رب کا شریک نہ بنائیں گے.');
INSERT INTO "junagarri" VALUES(72,3,'72:3','اور بیشک ہمارے رب کی شان بڑی بلند ہے نہ اس نے کسی کو (اپنی) بیوی بنایا ہے نہ بیٹا. ');
INSERT INTO "junagarri" VALUES(72,4,'72:4','اور یہ کہ ہم میں سے بیوقوف [یعنی ابلیس] اللہ کے بارے میں خلاف حق باتیں کہا کرتا تھا. ');
INSERT INTO "junagarri" VALUES(72,5,'72:5','اور ہم تو یہی سمجھتے رہے کہ ناممکن ہے کہ انسانوں اور جنات اللہ پر جھوٹی باتیں لگائیں. ');
INSERT INTO "junagarri" VALUES(72,6,'72:6','بات یہ ہے کہ چند انسان بعض جنات سے پناه طلب کیا کرتے تھے جس سے جنات اپنی سرکشی میں اور بڑھ گئے. ');
INSERT INTO "junagarri" VALUES(72,7,'72:7','اور (انسانوں) نے بھی تم جنوں کی طرح گمان کر لیا تھا کہ اللہ کسی کو نہ بھیجے گا (یا کسی کو دوباره زنده نہ کرے گا). ');
INSERT INTO "junagarri" VALUES(72,8,'72:8','اور ہم نے آسمان کو ٹٹول کر دیکھا تو اسے سخت چوکیداروں اور سخت شعلوں سے پر پایا. ');
INSERT INTO "junagarri" VALUES(72,9,'72:9','اس سے پہلے ہم باتیں سننے کے لیے آسمان میں جگہ جگہ بیٹھ جایا کرتے تھے ۔ اب جو بھی کان لگاتا ہے وه ایک شعلے کو اپنی تاک میں پاتا ہے. ');
INSERT INTO "junagarri" VALUES(72,10,'72:10','ہم نہیں جانتے کہ زمین والوں کے ساتھ کسی برائی کا اراده کیا گیا ہے یا ان کے رب کا اراده ان کے ساتھ بھلائی کا ہے. ');
INSERT INTO "junagarri" VALUES(72,11,'72:11','اور یہ کہ (بیشک) بعض تو ہم میں نیکو کار ہیں اور بعض اس کے برعکس بھی ہیں، ہم مختلف طریقوں سے بٹے ہوئے ہیں. ');
INSERT INTO "junagarri" VALUES(72,12,'72:12','اور ہم نے سمجھ لیا کہ ہم اللہ تعالیٰ کو زمین میں ہرگز عاجز نہیں کرسکتے اور نہ ہم بھاگ کر اسے ہرا سکتے ہیں.');
INSERT INTO "junagarri" VALUES(72,13,'72:13','ہم تو ہدایت کی بات سنتے ہی اس پر ایمان لائے چکے اور جو بھی اپنے رب پر ایمان لائے گا اسے نہ کسی نقصان کا اندیشہ ہے نہ ظلم وستم کا. ');
INSERT INTO "junagarri" VALUES(72,14,'72:14','ہاں ہم میں بعض تو مسلمان ہیں اور بعض بےانصاف ہیں پس جو فرماں بردار ہوگئے انہوں نے تو راه راست کا قصد کیا.');
INSERT INTO "junagarri" VALUES(72,15,'72:15','اور جو ظالم ہیں وه جہنم کا ایندھن بن گئے. ');
INSERT INTO "junagarri" VALUES(72,16,'72:16','اور (اے نبی یہ بھی کہہ دو) کہ اگر لوگ راه راست پر سیدھے رہتے تو یقیناً ہم انہیں بہت وافر پانی پلاتے.');
INSERT INTO "junagarri" VALUES(72,17,'72:17','تاکہ ہم اس میں انہیں آزمالیں ، اور جو شخص اپنے پروردگار کے ذکر سے منھ پھیر لے گا تو اللہ تعالیٰ اسے سخت عذاب میں مبتلا کردے گا. ');
INSERT INTO "junagarri" VALUES(72,18,'72:18','اور یہ کہ مسجدیں صرف اللہ ہی کے لئے خاص ہیں پس اللہ تعالیٰ کے ساتھ کسی اور کو نہ پکارو. ');
INSERT INTO "junagarri" VALUES(72,19,'72:19','اور جب اللہ کا بنده اس کی عبادت کے لیے کھڑا ہوا تو قریب تھا کہ وه بھیڑ کی بھیڑ بن کر اس پر پل پڑیں. ');
INSERT INTO "junagarri" VALUES(72,20,'72:20','آپ کہہ دیجئے کہ میں تو صرف اپنے رب ہی کو پکارتا ہوں اور اس کے ساتھ کسی کو شریک نہیں کرتا. ');
INSERT INTO "junagarri" VALUES(72,21,'72:21','کہہ دیجئے کہ مجھے تمہارے کسی نقصان نفع کا اختیار نہیں. ');
INSERT INTO "junagarri" VALUES(72,22,'72:22','کہہ دیجئے کہ مجھے ہرگز کوئی اللہ سے بچا نہیں سکتا اور میں ہرگز اس کے سوا کوئی جائے پناه بھی پا نہیں سکتا.');
INSERT INTO "junagarri" VALUES(72,23,'72:23','البتہ (میرا کام) اللہ کی بات اور اس کے پیغامات (لوگوں کو) پہنچا دینا ہے ، (اب) جو بھی اللہ اور اس کے رسول کی نہ مانے گا اس کے لیے جہنم کی آگ ہے جس میں ایسے لوگ ہمیشہ رہیں گے.');
INSERT INTO "junagarri" VALUES(72,24,'72:24','(ان کی آنکھ نہ کھلے گی) یہاں تک کہ اسے دیکھ لیں جس کا ان کو وعده دیا جاتا ہے پس عنقریب جان لیں گے کہ کس کا مددگار کمزور اور کس کی جماعت کم ہے. ');
INSERT INTO "junagarri" VALUES(72,25,'72:25','کہہ دیجئے کہ مجھے معلوم نہیں کہ جس کا وعده تم سے کیا جاتا ہے وه قریب ہے یا میرا رب اس کے لیے دور کی مدت مقرر کرے گا. ');
INSERT INTO "junagarri" VALUES(72,26,'72:26','وه غیب کا جاننے والا ہے اور اپنے غیب پر کسی کو مطلع نہیں کرتا.');
INSERT INTO "junagarri" VALUES(72,27,'72:27','سوائے اس پیغمبر کے جسے وه پسند کرلے لیکن اس کے بھی آگے پیچھے پہرے دار مقرر کردیتا ہے. ');
INSERT INTO "junagarri" VALUES(72,28,'72:28','تاکہ ان کے اپنے رب کے پیغام پہنچا دینے کا علم ہو جائے اللہ تعالیٰ نے ان کے آس پاس (کی تمام چیزوں) کا احاطہ کر رکھا ہے اور ہر چیز کی گنتی شمار کر رکھا ہے. ');
INSERT INTO "junagarri" VALUES(73,1,'73:1','اے کپڑے میں لپٹنے والے. ');
INSERT INTO "junagarri" VALUES(73,2,'73:2','رات (کے وقت نماز) میں کھڑے ہوجاؤ مگر کم.');
INSERT INTO "junagarri" VALUES(73,3,'73:3','آدھی رات یا اس سے بھی کچھ کم کرلے.');
INSERT INTO "junagarri" VALUES(73,4,'73:4','یا اس پر بڑھا دے اور قرآن کو ٹھہر ٹھہر کر (صاف) پڑھا کر. ');
INSERT INTO "junagarri" VALUES(73,5,'73:5','یقیناً ہم تجھ پر بہت بھاری بات عنقریب نازل کریں گے. ');
INSERT INTO "junagarri" VALUES(73,6,'73:6','بیشک رات کا اٹھنا دل جمعی کے لیے انتہائی مناسب ہے اور بات کو بہت درست کر دینے والا ہے. ');
INSERT INTO "junagarri" VALUES(73,7,'73:7','یقیناً تجھے دن میں بہت شغل رہتا ہے. ');
INSERT INTO "junagarri" VALUES(73,8,'73:8','تو اپنے رب کے نام کا ذکر کیا کر اور تمام خلائق سے کٹ کر اس کی طرف متوجہ ہوجا. ');
INSERT INTO "junagarri" VALUES(73,9,'73:9','مشرق ومغرب کا پروردگار جس کے سوا کوئی معبود نہیں، تو اسی کو اپنا کار ساز بنالے.');
INSERT INTO "junagarri" VALUES(73,10,'73:10','اور جو کچھ وه کہیں تو سہتا ره اور وضعداری کے ساتھ ان سے الگ تھلگ ره.');
INSERT INTO "junagarri" VALUES(73,11,'73:11','اور مجھے اور ان جھٹلانے والے آسوده حال لوگوں کو چھوڑ دے اور انہیں ذرا سی مہلت دے.');
INSERT INTO "junagarri" VALUES(73,12,'73:12','یقیناً ہمارے ہاں سخت بیڑیاں ہیں اور سلگتی ہوئی جہنم ہے.');
INSERT INTO "junagarri" VALUES(73,13,'73:13','اور حلق میں اٹکنے والا کھانا ہے اور درد دینے والا عذاب ہے. ');
INSERT INTO "junagarri" VALUES(73,14,'73:14','جس دن زمین اور پہاڑ تھرتھرا جائیں گے اور پہاڑ مثل بھربھری ریت کے ٹیلوں کے ہوجائیں گے. ');
INSERT INTO "junagarri" VALUES(73,15,'73:15','بیشک ہم نے تمہاری طرف بھی تم پر گواہی دینے والا رسول بھیج دیا ہے جیسے کہ ہم نے فرعون کے پاس رسول بھیجا تھا.');
INSERT INTO "junagarri" VALUES(73,16,'73:16','تو فرعون نے اس رسول کی نافرمانی کی تو ہم نے اسے سخت (وبال کی) پکڑ میں پکڑ لیا. ');
INSERT INTO "junagarri" VALUES(73,17,'73:17','تم اگر کافر رہے تو اس دن کیسے پناه پاؤ گے جو دن بچوں کو بوڑھا کردے گا. ');
INSERT INTO "junagarri" VALUES(73,18,'73:18','جس دن آسمان پھٹ جائے گا اللہ تعالیٰ کا یہ وعده ہو کر ہی رہنے والا ہے. ');
INSERT INTO "junagarri" VALUES(73,19,'73:19','بیشک یہ نصیحت ہے پس جو چاہے اپنے رب کی طرف راه اختیار کرے.');
INSERT INTO "junagarri" VALUES(73,20,'73:20','آپ کا رب بخوبی جانتا ہے کہ آپ اور آپ کے ساتھ کے لوگوں کی ایک جماعت قریب دو تہائی رات کے اور آدھی رات کے اور ایک تہائی رات کے تہجد پڑھتی ہے اور رات دن کا پورا اندازه اللہ تعالیٰ کو ہی ہے ، وه (خوب) جانتا ہے کہ تم اسے ہرگز نہ نبھا سکو گے پس اس نے تم پر مہربانی کی لہٰذا جتنا قرآن پڑھنا تمہارے لیے آسان ہو اتنا ہی پڑھو ، وه جانتا ہے کہ تم میں بعض بیمار بھی ہوں گے، بعض دوسرے زمین میں چل پھر کر اللہ تعالیٰ کا فضل (یعنی روزی بھی) تلاش کریں گے اور کچھ لوگ اللہ تعالیٰ کی راه میں جہاد بھی کریں گے ، سو تم بہ آسانی جتنا قرآن پڑھ سکو پڑھو اور نماز کی پابندی رکھو اور زکوٰة دیتے رہا کرو اور اللہ تعالیٰ کو اچھا قرض دو ۔ اور جو نیکی تم اپنے لیے آگے بھیجو گے اسے اللہ تعالیٰ کے ہاں بہتر سے بہتر اور ثواب میں بہت زیاده پاؤ گے اللہ تعالیٰ سے معافی مانگتے رہو۔ یقیناً اللہ تعالیٰ بخشنے والا. مہربان ہے');
INSERT INTO "junagarri" VALUES(74,1,'74:1','اے کپڑا اوڑھنے والے. ');
INSERT INTO "junagarri" VALUES(74,2,'74:2','کھڑا ہوجا اور آگاه کردے. ');
INSERT INTO "junagarri" VALUES(74,3,'74:3','اور اپنے رب ہی کی بڑائیاں بیان کر.');
INSERT INTO "junagarri" VALUES(74,4,'74:4','اپنے کپڑوں کو پاک رکھا کر. ');
INSERT INTO "junagarri" VALUES(74,5,'74:5','ناپاکی کو چھوڑ دے. ');
INSERT INTO "junagarri" VALUES(74,6,'74:6','اور احسان کرکے زیاده لینے کی خواہش نہ کر. ');
INSERT INTO "junagarri" VALUES(74,7,'74:7','اور اپنے رب کی راه میں صبر کر.');
INSERT INTO "junagarri" VALUES(74,8,'74:8','پس جب کہ صور میں پھونک ماری جائے گی.');
INSERT INTO "junagarri" VALUES(74,9,'74:9','تو وه دن بڑا سخت دن ہوگا.');
INSERT INTO "junagarri" VALUES(74,10,'74:10','جو کافروں پر آسان نہ ہوگا. ');
INSERT INTO "junagarri" VALUES(74,11,'74:11','مجھے اور اسے چھوڑ دے جسے میں نے اکیلا پیدا کیا ہے. ');
INSERT INTO "junagarri" VALUES(74,12,'74:12','اور اسے بہت سا مال دے رکھا ہے.');
INSERT INTO "junagarri" VALUES(74,13,'74:13','اور حاضر باش فرزند بھی. ');
INSERT INTO "junagarri" VALUES(74,14,'74:14','اور میں نے اسے بہت کچھ کشادگی دے رکھی ہے. ');
INSERT INTO "junagarri" VALUES(74,15,'74:15','پھر بھی اس کی چاہت ہے کہ میں اسے اور زیاده دوں. ');
INSERT INTO "junagarri" VALUES(74,16,'74:16','نہیں نہیں، وه ہماری آیتوں کا مخالف ہے. ');
INSERT INTO "junagarri" VALUES(74,17,'74:17','عنقریب میں اسے ایک سخت چڑھائی چڑھاؤں گا. ');
INSERT INTO "junagarri" VALUES(74,18,'74:18','اس نے غور کرکے تجویز کی. ');
INSERT INTO "junagarri" VALUES(74,19,'74:19','اسے ہلاکت ہو کیسی (تجویز) سوچی؟');
INSERT INTO "junagarri" VALUES(74,20,'74:20','وه پھر غارت ہو کس طرح اندازه کیا. ');
INSERT INTO "junagarri" VALUES(74,21,'74:21','اس نے پھر دیکھا. ');
INSERT INTO "junagarri" VALUES(74,22,'74:22','پھر تیوری چڑھائی اور منھ بنایا. ');
INSERT INTO "junagarri" VALUES(74,23,'74:23','پھر پیچھے ہٹ گیا اور غرور کیا. ');
INSERT INTO "junagarri" VALUES(74,24,'74:24','اور کہنے لگا تو یہ صرف جادو ہے جو نقل کیا جاتا ہے. ');
INSERT INTO "junagarri" VALUES(74,25,'74:25','سوائے انسانی کلام کے کچھ بھی نہیں ہے.');
INSERT INTO "junagarri" VALUES(74,26,'74:26','میں عنقریب اسے دوزخ میں ڈالوں گا.');
INSERT INTO "junagarri" VALUES(74,27,'74:27','اور تجھے کیا خبر کہ دوزخ کیا چیز ہے؟ ');
INSERT INTO "junagarri" VALUES(74,28,'74:28','نہ وه باقی رکھتی ہے نہ چھوڑتی ہے. ');
INSERT INTO "junagarri" VALUES(74,29,'74:29','کھال کو جھلسا دیتی ہے.');
INSERT INTO "junagarri" VALUES(74,30,'74:30','اور اس میں انیس (فرشتے مقرر) ہیں. ');
INSERT INTO "junagarri" VALUES(74,31,'74:31','ہم نے دوزخ کے داروغے صرف فرشتے رکھے ہیں۔ اور ہم نے ان کی تعداد صرف کافروں کی آزمائش کے لیے مقرر کی ہے تاکہ اہل کتاب یقین کرلیں ، اوراہل ایمان کے ایمان میں اضافہ ہو جائے اور اہل کتاب اور اہل ایمان شک نہ کریں اور جن کے دلوں میں بیماری ہے وه اور کافر کہیں کہ اس بیان سے اللہ تعالیٰ کی کیا مراد ہے ؟ اسی طرح اللہ تعالیٰ جسے چاہتا ہے گمراه کرتا ہے اور جسے چاہتا ہے ہدایت دیتا ہے ۔ تیرے رب کے لشکروں کو اس کے سوا کوئی نہیں جانتا ، یہ تو کل بنی آدم کے لیے سراسر پند ونصیحت ہے. ');
INSERT INTO "junagarri" VALUES(74,32,'74:32','سچ کہتا ہوں قسم ہے چاند کی. ');
INSERT INTO "junagarri" VALUES(74,33,'74:33','اور رات کی جب وه پیچھے ہٹے.');
INSERT INTO "junagarri" VALUES(74,34,'74:34','اور صبح کی جب کہ روشن ہو جائے.');
INSERT INTO "junagarri" VALUES(74,35,'74:35','کہ (یقیناً وه جہنم) بڑی چیزوں میں سے ایک ہے. ');
INSERT INTO "junagarri" VALUES(74,36,'74:36','بنی آدم کو ڈرانے والی.');
INSERT INTO "junagarri" VALUES(74,37,'74:37','(یعنی) اسے جو تم میں سے آگے بڑھنا چاہے یا پیچھے ہٹنا چاہے. ');
INSERT INTO "junagarri" VALUES(74,38,'74:38','ہر شخص اپنے اعمال کے بدلے میں گروی ہے. ');
INSERT INTO "junagarri" VALUES(74,39,'74:39','مگر دائیں ہاتھ والے. ');
INSERT INTO "junagarri" VALUES(74,40,'74:40','کہ وه بہشتوں میں (بیٹھے ہوئے) گناه گاروں سے.');
INSERT INTO "junagarri" VALUES(74,41,'74:41','سوال کرتے ہوں گے. ');
INSERT INTO "junagarri" VALUES(74,42,'74:42','تمہیں دوزخ میں کس چیز نے ڈالا.');
INSERT INTO "junagarri" VALUES(74,43,'74:43','وه جواب دیں گے کہ ہم نمازی نہ تھے.');
INSERT INTO "junagarri" VALUES(74,44,'74:44','نہ مسکینوں کو کھانا کھلاتے تھے. ');
INSERT INTO "junagarri" VALUES(74,45,'74:45','اور ہم بحث کرنے والے (انکاریوں) کا ساتھ دے کر بحث مباحثہ میں مشغول رہا کرتے تھے. ');
INSERT INTO "junagarri" VALUES(74,46,'74:46','اور روز جزا کو جھٹلاتے تھے.');
INSERT INTO "junagarri" VALUES(74,47,'74:47','یہاں تک کہ ہمیں موت آگئی. ');
INSERT INTO "junagarri" VALUES(74,48,'74:48','پس انہیں سفارش کرنے والوں کی سفارش نفع نہ دے گی. ');
INSERT INTO "junagarri" VALUES(74,49,'74:49','انہیں کیا ہو گیا ہے؟ کہ نصیحت سے منھ موڑ رہے ہیں.');
INSERT INTO "junagarri" VALUES(74,50,'74:50','گویا کہ وه بِدکے ہوئے گدھے ہیں.');
INSERT INTO "junagarri" VALUES(74,51,'74:51','جو شیر سے بھاگے ہوں. ');
INSERT INTO "junagarri" VALUES(74,52,'74:52','بلکہ ان میں سے ہر شخص چاہتا ہے کہ اسے کھلی ہوئی کتابیں دی جائیں. ');
INSERT INTO "junagarri" VALUES(74,53,'74:53','ہرگز ایسا نہیں (ہوسکتا بلکہ) یہ قیامت سے بے خوف ہیں. ');
INSERT INTO "junagarri" VALUES(74,54,'74:54','سچی بات تو یہ ہے کہ یہ (قرآن) ایک نصیحت ہے. ');
INSERT INTO "junagarri" VALUES(74,55,'74:55','اب جو چاہے اس سے نصیحت حاصل کرے.');
INSERT INTO "junagarri" VALUES(74,56,'74:56','اور وه اس وقت نصیحت حاصل کریں گے جب اللہ تعالیٰ چاہے ، وه اسی لائق ہے کہ اس سے ڈریں اور اس لائق بھی کہ وه بخشے. ');
INSERT INTO "junagarri" VALUES(75,1,'75:1','میں قسم کھاتا ہوں قیامت کے دن کی. ');
INSERT INTO "junagarri" VALUES(75,2,'75:2','اور قسم کھاتا ہوں اس نفس کی جو ملامت کرنے والا ہو. ');
INSERT INTO "junagarri" VALUES(75,3,'75:3','کیا انسان یہ خیال کرتا ہے کہ ہم اس کی ہڈیاں جمع کریں گے ہی نہیں. ');
INSERT INTO "junagarri" VALUES(75,4,'75:4','ہاں ضرور کریں گے ہم تو قادر ہیں کہ اس کی پور پور تک درست کردیں. ');
INSERT INTO "junagarri" VALUES(75,5,'75:5','بلکہ انسان تو چاہتا ہے کہ آگے آگے نافرمانیاں کرتا جائے. ');
INSERT INTO "junagarri" VALUES(75,6,'75:6','پوچھتا ہے کہ قیامت کا دن کب آئے گا. ');
INSERT INTO "junagarri" VALUES(75,7,'75:7','پس جس وقت کہ نگاه پتھرا جائے گی. ');
INSERT INTO "junagarri" VALUES(75,8,'75:8','اور چاند بے نور ہو جائے گا. ');
INSERT INTO "junagarri" VALUES(75,9,'75:9','اور سورج اور چاند جمع کردیئے جائیں گے. ');
INSERT INTO "junagarri" VALUES(75,10,'75:10','اس دن انسان کہے گا کہ آج بھاگنے کی جگہ کہاں ہے؟ ');
INSERT INTO "junagarri" VALUES(75,11,'75:11','نہیں نہیں کوئی پناہ گاه نہیں. ');
INSERT INTO "junagarri" VALUES(75,12,'75:12','آج تو تیرے پروردگار کی طرف ہی قرار گاه ہے. ');
INSERT INTO "junagarri" VALUES(75,13,'75:13','آج انسان کو اس کے آگے بھیجے ہوئے اور پیچھے چھوڑے ہوئے سے آگاه کیا جائے گا. ');
INSERT INTO "junagarri" VALUES(75,14,'75:14','بلکہ انسان خود اپنے اوپر آپ حجت ہے. ');
INSERT INTO "junagarri" VALUES(75,15,'75:15','اگر چہ کتنے ہی بہانے پیش کرے. ');
INSERT INTO "junagarri" VALUES(75,16,'75:16','(اے نبی) آپ قرآن کو جلدی (یاد کرنے) کے لیے اپنی زبان کو حرکت نہ دیں.');
INSERT INTO "junagarri" VALUES(75,17,'75:17','اس کا جمع کرنا اور (آپ کی زبان سے) پڑھنا ہمارے ذمہ ہے. ');
INSERT INTO "junagarri" VALUES(75,18,'75:18','ہم جب اسے پڑھ لیں تو آپ اس کے پڑھنے کی پیروی کریں. ');
INSERT INTO "junagarri" VALUES(75,19,'75:19','پھر اس کا واضح کر دینا ہمارے ذمہ ہے. ');
INSERT INTO "junagarri" VALUES(75,20,'75:20','نہیں نہیں تم جلدی ملنے والی (دنیا) کی محبت رکھتے ہو.');
INSERT INTO "junagarri" VALUES(75,21,'75:21','اور آخرت کو چھوڑ بیٹھے ہو. ');
INSERT INTO "junagarri" VALUES(75,22,'75:22','اس روز بہت سے چہرے تروتازه اور بارونق ہوں گے.');
INSERT INTO "junagarri" VALUES(75,23,'75:23','اپنے رب کی طرف دیکھتے ہوں گے. ');
INSERT INTO "junagarri" VALUES(75,24,'75:24','اور کتنے چہرے اس دن (بد رونق اور) اداس ہوں گے. ');
INSERT INTO "junagarri" VALUES(75,25,'75:25','سمجھتے ہوں گے کہ ان کے ساتھ کمر توڑ دینے والا معاملہ کیا جائے گا.');
INSERT INTO "junagarri" VALUES(75,26,'75:26','نہیں نہیں جب روح ہنسلی تک پہنچے گی.');
INSERT INTO "junagarri" VALUES(75,27,'75:27','اور کہا جائے گا کہ کوئی جھاڑ پھونک کرنے والا ہے؟');
INSERT INTO "junagarri" VALUES(75,28,'75:28','اور جان لیا اس نے کہ یہ وقت جدائی ہے. ');
INSERT INTO "junagarri" VALUES(75,29,'75:29','اور پنڈلی سے پنڈلی لپٹ جائے گی. ');
INSERT INTO "junagarri" VALUES(75,30,'75:30','آج تیرے پروردگار کی طرف چلنا ہے.');
INSERT INTO "junagarri" VALUES(75,31,'75:31','اس نے نہ تو تصدیق کی نہ نماز ادا کی. ');
INSERT INTO "junagarri" VALUES(75,32,'75:32','بلکہ جھٹلایا اور روگردانی کی. ');
INSERT INTO "junagarri" VALUES(75,33,'75:33','پھر اپنے گھر والوں کے پاس اتراتا ہوا گیا. ');
INSERT INTO "junagarri" VALUES(75,34,'75:34','افسوس ہے تجھ پر حسرت ہے تجھ پر.');
INSERT INTO "junagarri" VALUES(75,35,'75:35','وائے ہے اور خرابی ہے تیرے لیے. ');
INSERT INTO "junagarri" VALUES(75,36,'75:36','کیا انسان یہ سمجھتا ہے کہ اسے بیکار چھوڑ دیا جائے گا. ');
INSERT INTO "junagarri" VALUES(75,37,'75:37','کیا وه ایک گاڑھے پانی کا قطره نہ تھا جو ٹپکایا گیا تھا؟');
INSERT INTO "junagarri" VALUES(75,38,'75:38','پھر وه لہو کا لوتھڑا ہوگیا پھر اللہ نے اسے پیدا کیا اور درست بنا دیا. ');
INSERT INTO "junagarri" VALUES(75,39,'75:39','پھر اس سے جوڑے یعنی نر وماده بنائے.');
INSERT INTO "junagarri" VALUES(75,40,'75:40','کیا (اللہ تعالیٰ) اس (امر) پر قادر نہیں کہ مردے کو زنده کردے. ');
INSERT INTO "junagarri" VALUES(76,1,'76:1','یقیناً گزرا ہے انسان پر ایک وقت زمانے میں جب کہ یہ کوئی قابل ذکر چیز نہ تھا.');
INSERT INTO "junagarri" VALUES(76,2,'76:2','بیشک ہم نے انسان کو ملے جلے نطفے سے امتحان کے لیے پیدا کیا اور اس کو سنتا دیکھتا بنایا. ');
INSERT INTO "junagarri" VALUES(76,3,'76:3','ہم نے اسے راه دکھائی اب خواه وه شکر گزار بنے خواه ناشکرا. ');
INSERT INTO "junagarri" VALUES(76,4,'76:4','یقیناً ہم نے کافروں کے لیے زنجیریں اور طوق اور شعلوں والی آگ تیار کر رکھی ہے. ');
INSERT INTO "junagarri" VALUES(76,5,'76:5','بیشک نیک لوگ وه جام پئیں گے جس کی آمیزش کافور کی ہے. ');
INSERT INTO "junagarri" VALUES(76,6,'76:6','جو ایک چشمہ ہے ۔ جس سے اللہ کے بندے پئیں گے اس کی نہریں نکال لے جائیں گے (جدھر چاہیں).');
INSERT INTO "junagarri" VALUES(76,7,'76:7','جو نذر پوری کرتے ہیں اور اس دن سے ڈرتے ہیں جس کی برائی چاروں طرف پھیل جانے والی ہے. ');
INSERT INTO "junagarri" VALUES(76,8,'76:8','اور اللہ تعالیٰ کی محبت میں کھانا کھلاتے ہیں مسکین، یتیم اور قیدیوں کو.');
INSERT INTO "junagarri" VALUES(76,9,'76:9','ہم تو تمہیں صرف اللہ تعالیٰ کی رضامندی کے لیے کھلاتے ہیں نہ تم سے بدلہ چاہتے ہیں نہ شکر گزاری.');
INSERT INTO "junagarri" VALUES(76,10,'76:10','بیشک ہم اپنے پروردگار سے اس دن کا خوف کرتے ہیں جو اداسی اور سختی والا ہوگا.');
INSERT INTO "junagarri" VALUES(76,11,'76:11','پس انہیں اللہ تعالیٰ نے اس دن کی برائی سے بچا لیا اور انہیں تازگی اور خوشی پہنچائی. ');
INSERT INTO "junagarri" VALUES(76,12,'76:12','اور انہیں ان کے صبر کے بدلے جنت اور ریشمی لباس عطا فرمائے.');
INSERT INTO "junagarri" VALUES(76,13,'76:13','یہ وہاں تختوں پر تکیے لگائے ہوئے بیٹھیں گے۔ نہ وہاں آفتاب کی گرمی دیکھیں گے نہ جاڑے کی سختی. ');
INSERT INTO "junagarri" VALUES(76,14,'76:14','ان جنتوں کے سائے ان پر جھکے ہوئے ہوں گے اور ان کے (میوے اور) گچھے نیچے لٹکائے ہوئے ہوں گے. ');
INSERT INTO "junagarri" VALUES(76,15,'76:15','اور ان پر چاندی کے برتنوں اور ان جاموں کا دور کرایا جائے گا جو شیشے کے ہوں گے. ');
INSERT INTO "junagarri" VALUES(76,16,'76:16','شیشے بھی چاندی کے جن کو (ساقی نے) اندازے سے ناپ رکھا ہوگا. ');
INSERT INTO "junagarri" VALUES(76,17,'76:17','اور انہیں وہاں وه جام پلائے جائیں گے جن کی آمیزش زنجبیل کی ہوگی. ');
INSERT INTO "junagarri" VALUES(76,18,'76:18','جنت کی ایک نہر سے جس کا نام سلسبیل ہے. ');
INSERT INTO "junagarri" VALUES(76,19,'76:19','اور ان کے ارد گرد گھومتے پھرتے ہوں گے وه کم سن بچے جو ہمیشہ رہنے والے ہیں جب تو انہیں دیکھے تو سمجھے کہ وه بکھرے ہوئے سچے موتی ہیں. ');
INSERT INTO "junagarri" VALUES(76,20,'76:20','تو وہاں جہاں کہیں بھی نظر ڈالے گا سراسر نعمتیں اور عظیم الشان سلطنت ہی دیکھے گا.');
INSERT INTO "junagarri" VALUES(76,21,'76:21','ان کے جسموں پر سبز باریک اور موٹے ریشمی کپڑے ہوں گے اور انہیں چاندی کے کنگن کا زیور پہنایا جائے گا ۔ اور انہیں ان کا رب پاک صاف شراب پلائے گا.');
INSERT INTO "junagarri" VALUES(76,22,'76:22','(کہا جائے گا) کہ یہ ہے تمہارے اعمال کا بدلہ اور تمہاری کوشش کی قدر کی گئی.');
INSERT INTO "junagarri" VALUES(76,23,'76:23','بیشک ہم نے تجھ پر بتدریج قرآن نازل کیا ہے. ');
INSERT INTO "junagarri" VALUES(76,24,'76:24','پس تو اپنے رب کے حکم پر قائم ره اور ان میں سے کسی گنہگار یا ناشکرے کا کہا نہ مان. ');
INSERT INTO "junagarri" VALUES(76,25,'76:25','اور اپنے رب کے نام کا صبح وشام ذکر کیا کر. ');
INSERT INTO "junagarri" VALUES(76,26,'76:26','اور رات کے وقت اس کے سامنے سجدے کر اور بہت رات تک اس کی تسبیح کیا کر. ');
INSERT INTO "junagarri" VALUES(76,27,'76:27','بیشک یہ لوگ جلدی ملنے والی (دنیا) کو چاہتے ہیں اور اپنے پیچھے ایک بڑے بھاری دن کو چھوڑے دیتے ہیں. ');
INSERT INTO "junagarri" VALUES(76,28,'76:28','ہم نے انہیں پیدا کیا اور ہم نے ہی ان کے جوڑ اور بندھن مضبوط کیے اور ہم جب چاہیں ان کے عوض ان جیسے اوروں کو بدل لائیں. ');
INSERT INTO "junagarri" VALUES(76,29,'76:29','یقیناً یہ تو ایک نصیحت ہے پس جو چاہے اپنے رب کی راه لے لے. ');
INSERT INTO "junagarri" VALUES(76,30,'76:30','اور تم نہ چاہو گے مگر یہ کہ اللہ تعالیٰ ہی چاہے بیشک اللہ تعالیٰ علم والا باحکمت ہے. ');
INSERT INTO "junagarri" VALUES(76,31,'76:31','جسے چاہے اپنی رحمت میں داخل کرلے، اور ظالموں کے لیے اس نے دردناک عذاب تیار کر رکھا ہے.*');
INSERT INTO "junagarri" VALUES(77,1,'77:1','دل خوش کن چلتی ہواؤں کی قسم. ');
INSERT INTO "junagarri" VALUES(77,2,'77:2','پھر زور سے جھونکا دینے والیوں کی قسم.*');
INSERT INTO "junagarri" VALUES(77,3,'77:3','پھر (ابر کو) ابھار کر پراگنده کرنے والیوں کی قسم.');
INSERT INTO "junagarri" VALUES(77,4,'77:4','پھر حق وباطل کو جدا جدا کر دینے والے. ');
INSERT INTO "junagarri" VALUES(77,5,'77:5','اور وحی لانے والے فرشتوں کی قسم. ');
INSERT INTO "junagarri" VALUES(77,6,'77:6','جو (وحی) الزام اتارنے یا آگاه کردینے کے لیے ہوتی ہے. ');
INSERT INTO "junagarri" VALUES(77,7,'77:7','جس چیز کا تم سے وعده کیا جاتا ہے وہ یقیناً ہونے والی ہے. ');
INSERT INTO "junagarri" VALUES(77,8,'77:8','پس جب ستارے بے نور کردئے جائیں گے. ');
INSERT INTO "junagarri" VALUES(77,9,'77:9','اور جب آسمان توڑ پھوڑ دیا جائے گا.');
INSERT INTO "junagarri" VALUES(77,10,'77:10','اور جب پہاڑ ٹکڑے ٹکڑے کر کے اڑا دیئے جائیں گے. ');
INSERT INTO "junagarri" VALUES(77,11,'77:11','اور جب رسولوں کو وقت مقرره پر لایا جائے گا. ');
INSERT INTO "junagarri" VALUES(77,12,'77:12','کس دن کے لیے (ان سب کو) مؤخر کیا گیا ہے؟ ');
INSERT INTO "junagarri" VALUES(77,13,'77:13','فیصلے کے دن کے لیے. ');
INSERT INTO "junagarri" VALUES(77,14,'77:14','اور تجھے کیا معلوم کہ فیصلے کا دن کیا ہے؟');
INSERT INTO "junagarri" VALUES(77,15,'77:15','اس دن جھٹلانے والوں کی خرابی ہے. ');
INSERT INTO "junagarri" VALUES(77,16,'77:16','کیا ہم نے اگلوں کو ہلاک نہیں کیا؟');
INSERT INTO "junagarri" VALUES(77,17,'77:17','پھر ہم ان کے بعد پچھلوں کو لائے. ');
INSERT INTO "junagarri" VALUES(77,18,'77:18','ہم گنہگاروں کے ساتھ اسی طرح کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(77,19,'77:19','اس دن جھٹلانے والوں کے لیے ویل (افسوس) ہے.');
INSERT INTO "junagarri" VALUES(77,20,'77:20','کیا ہم نے تمہیں حقیر پانی سے (منی سے) پیدا نہیں کیا.');
INSERT INTO "junagarri" VALUES(77,21,'77:21','پھر ہم نے اسے مضبوط ومحفوظ جگہ میں رکھا. ');
INSERT INTO "junagarri" VALUES(77,22,'77:22','ایک مقرره وقت تک. ');
INSERT INTO "junagarri" VALUES(77,23,'77:23','پھر ہم نے اندازه کیا اور ہم کیا خوب اندازه کرنے والے ہیں.');
INSERT INTO "junagarri" VALUES(77,24,'77:24','اس دن تکذیب کرنے والوں کی خرابی ہے.');
INSERT INTO "junagarri" VALUES(77,25,'77:25','کیا ہم نے زمین کو سمیٹنے والی نہیں بنایا؟');
INSERT INTO "junagarri" VALUES(77,26,'77:26','زندوں کو بھی اور مردوں کو بھی. ');
INSERT INTO "junagarri" VALUES(77,27,'77:27','اور ہم نے اس میں بلند وبھاری پہاڑ بنادیے اور تمہیں سیراب کرنے والا میٹھا پانی پلایا.');
INSERT INTO "junagarri" VALUES(77,28,'77:28','اس دن جھٹلانے والوں کے لیے وائے اور افسوس ہے.');
INSERT INTO "junagarri" VALUES(77,29,'77:29','اس دوزخ کی طرف جاؤ جسے تم جھٹلاتے رہے تھے. ');
INSERT INTO "junagarri" VALUES(77,30,'77:30','چلو تین شاخوں والے سائے کی طرف. ');
INSERT INTO "junagarri" VALUES(77,31,'77:31','جو در اصل نہ سایہ دینے والا ہے اور نہ شعلے سے بچاسکتا ہے. ');
INSERT INTO "junagarri" VALUES(77,32,'77:32','یقیناً دوزخ چنگاریاں پھینکتی ہے جو مثل محل کے ہیں. ');
INSERT INTO "junagarri" VALUES(77,33,'77:33','گویا کہ وه زرد اونٹ ہیں. ');
INSERT INTO "junagarri" VALUES(77,34,'77:34','آج ان جھٹلانے والوں کی درگت ہے.');
INSERT INTO "junagarri" VALUES(77,35,'77:35','آج (کا دن) وه دن ہے کہ یہ بول بھی نہ سکیں گے. ');
INSERT INTO "junagarri" VALUES(77,36,'77:36','نہ انہیں معذرت کی اجازت دی جائے گی. ');
INSERT INTO "junagarri" VALUES(77,37,'77:37','اس دن جھٹلانے والوں کی خرابی ہے.*');
INSERT INTO "junagarri" VALUES(77,38,'77:38','یہ ہے فیصلے کا دن ہم نے تمہیں اور اگلوں کو سب کو جمع کرلیا ہے. ');
INSERT INTO "junagarri" VALUES(77,39,'77:39','پس اگر تم مجھ سے کوئی چال چل سکتے ہو تو چل لو. ');
INSERT INTO "junagarri" VALUES(77,40,'77:40','وائے ہے اس دن جھٹلانے والوں کے لیے.');
INSERT INTO "junagarri" VALUES(77,41,'77:41','بیشک پرہیزگار لوگ سایوں میں ہیں اور بہتے چشموں میں.');
INSERT INTO "junagarri" VALUES(77,42,'77:42','اور ان میووں میں جن کی وه خواہش کریں. ');
INSERT INTO "junagarri" VALUES(77,43,'77:43','(اے جنتیو!) کھاؤ پیو مزے سےاپنے کیے ہوئے اعمال کے بدلے. ');
INSERT INTO "junagarri" VALUES(77,44,'77:44','یقیناً ہم نیکی کرنے والوں کو اسی طرح جزا دیتے ہیں. ');
INSERT INTO "junagarri" VALUES(77,45,'77:45','اس دن سچا نہ جاننے والوں کے لیے ویل (افسوس) ہے. ');
INSERT INTO "junagarri" VALUES(77,46,'77:46','(اے جھٹلانے والو) تم دنیا میں تھوڑا سا کھا لو اور فائده اٹھا لو بیشک تم گنہگار ہو. ');
INSERT INTO "junagarri" VALUES(77,47,'77:47','اس دن جھٹلانے والوں کے لیے سخت ہلاکت ہے.');
INSERT INTO "junagarri" VALUES(77,48,'77:48','ان سے جب کہا جاتا ہے کہ رکوع کر لو تو نہیں کرتے. ');
INSERT INTO "junagarri" VALUES(77,49,'77:49','اس دن جھٹلانے والوں کی تباہی ہے. ');
INSERT INTO "junagarri" VALUES(77,50,'77:50','اب اس قرآن کے بعد کس بات پر ایمان لائیں گے؟ ');
INSERT INTO "junagarri" VALUES(78,1,'78:1','یہ لوگ کس چیز کے بارے میں پوچھ گچھ کر رہے ہیں. ');
INSERT INTO "junagarri" VALUES(78,2,'78:2','اس بڑی خبر کے متعلق.');
INSERT INTO "junagarri" VALUES(78,3,'78:3','جس کے بارے میں یہ اختلاف کر رہے ہیں. ');
INSERT INTO "junagarri" VALUES(78,4,'78:4','یقیناً یہ عنقریب جان لیں گے.*');
INSERT INTO "junagarri" VALUES(78,5,'78:5','پھر بالیقین انہیں بہت جلد معلوم ہو جائے گا. ');
INSERT INTO "junagarri" VALUES(78,6,'78:6','کیا ہم نے زمین کو فرش نہیں بنایا؟ ');
INSERT INTO "junagarri" VALUES(78,7,'78:7','اور پہاڑوں کو میخیں (نہیں بنایا؟) ');
INSERT INTO "junagarri" VALUES(78,8,'78:8','اور ہم نے تمہیں جوڑا جوڑا پیدا کیا. ');
INSERT INTO "junagarri" VALUES(78,9,'78:9','اور ہم نے تمہاری نیند کو آرام کا سبب بنایا. ');
INSERT INTO "junagarri" VALUES(78,10,'78:10','اور رات کو ہم نے پرده بنایا. ');
INSERT INTO "junagarri" VALUES(78,11,'78:11','اور دن کو ہم نے وقت روزگار بنایا. ');
INSERT INTO "junagarri" VALUES(78,12,'78:12','اور تمہارے اوپر ہم نے سات مضبوط آسمان بنائے. ');
INSERT INTO "junagarri" VALUES(78,13,'78:13','اور ایک روشن چراغ (سورج) بنایا. ');
INSERT INTO "junagarri" VALUES(78,14,'78:14','اور بدلیوں سے ہم نے بکثرت بہتا ہوا پانی برسایا. ');
INSERT INTO "junagarri" VALUES(78,15,'78:15','تاکہ اس سے اناج اور سبزه اگائیں. ');
INSERT INTO "junagarri" VALUES(78,16,'78:16','اور گھنے باغ (بھی اگائیں). ');
INSERT INTO "junagarri" VALUES(78,17,'78:17','بیشک فیصلہ کے دن کا وقت مقرر ہے. ');
INSERT INTO "junagarri" VALUES(78,18,'78:18','جس دن کہ صور میں پھونکا جائے گا۔ پھر تم فوج در فوج چلے آؤ گے. ');
INSERT INTO "junagarri" VALUES(78,19,'78:19','اور آسمان کھول دیا جائے گا تو اس میں دروازے ہی دروازے ہو جائیں گے. ');
INSERT INTO "junagarri" VALUES(78,20,'78:20','اور پہاڑ چلائے جائیں گے پس وه سراب ہو جائیں گے. ');
INSERT INTO "junagarri" VALUES(78,21,'78:21','بیشک دوزخ گھات میں ہے. ');
INSERT INTO "junagarri" VALUES(78,22,'78:22','سرکشوں کا ٹھکانہ وہی ہے.');
INSERT INTO "junagarri" VALUES(78,23,'78:23','اس میں وه مدتوں تک پڑے رہیں گے. ');
INSERT INTO "junagarri" VALUES(78,24,'78:24','نہ کبھی اس میں خنکی کا مزه چکھیں گے، نہ پانی کا.');
INSERT INTO "junagarri" VALUES(78,25,'78:25','سوائے گرم پانی اور (بہتی) پیپ کے. ');
INSERT INTO "junagarri" VALUES(78,26,'78:26','(ان کو) پورا پورا بدلہ ملے گا. ');
INSERT INTO "junagarri" VALUES(78,27,'78:27','انہیں تو حساب کی توقع ہی نہ تھی. ');
INSERT INTO "junagarri" VALUES(78,28,'78:28','اور بے باکی سے ہماری آیتوں کی تکذیب کرتے تھے.');
INSERT INTO "junagarri" VALUES(78,29,'78:29','ہم نے ہر ایک چیز کو لکھ کر شمار کر رکھا ہے. ');
INSERT INTO "junagarri" VALUES(78,30,'78:30','اب تم (اپنے کیے کا) مزه چکھو ہم تمہارا عذاب ہی بڑھاتے رہیں گے. ');
INSERT INTO "junagarri" VALUES(78,31,'78:31','یقیناً پرہیزگار لوگوں کے لئے کامیابی ہے. ');
INSERT INTO "junagarri" VALUES(78,32,'78:32','باغات ہیں اور انگور ہیں. ');
INSERT INTO "junagarri" VALUES(78,33,'78:33','اور نوجوان کنواری ہم عمر عورتیں ہیں. ');
INSERT INTO "junagarri" VALUES(78,34,'78:34','اور چھلکتے ہوئے جام ہیں. ');
INSERT INTO "junagarri" VALUES(78,35,'78:35','وہاں نہ تو وه بیہوده باتیں سنیں گے اور نہ جھوٹی باتیں سنیں گے. ');
INSERT INTO "junagarri" VALUES(78,36,'78:36','(ان کو) تیرے رب کی طرف سے (ان کے نیک اعمال کا)یہ بدلہ ملے گا جو کافی انعام ہوگا. ');
INSERT INTO "junagarri" VALUES(78,37,'78:37','(اس رب کی طرف سے ملے گا جو کہ) آسمانوں کا اور زمین کا اور جو کچھ ان کے درمیان ہے ان کا پروردگار ہے اور بڑی بخشش کرنے والا ہے۔ کسی کو اس سے بات چیت کرنے کا اختیار نہیں ہوگا. ');
INSERT INTO "junagarri" VALUES(78,38,'78:38','جس دن روح اور فرشتے صفیں باندھ کر کھڑے ہوں گے تو کوئی کلام نہ کر سکے گا مگر جسے رحمٰن اجازت دے دے اور وه ٹھیک بات زبان سے نکالے. ');
INSERT INTO "junagarri" VALUES(78,39,'78:39','یہ دن حق ہے اب جو چاہے اپنے رب کے پاس (نیک اعمال کر کے) ٹھکانا بنالے. ');
INSERT INTO "junagarri" VALUES(78,40,'78:40','ہم نے تمہیں عنقریب آنے والے عذاب سے ڈرا دیا (اور چوکنا کر دیا) ہے ۔ جس دن انسان اپنے ہاتھوں کی کمائی کو دیکھ لے گا اور کافر کہے گا کہ کاش! میں مٹی ہو جاتا. ');
INSERT INTO "junagarri" VALUES(79,1,'79:1','ڈوب کر سختی سے کھینچنے والوں کی قسم! ');
INSERT INTO "junagarri" VALUES(79,2,'79:2','بند کھول کر چھڑا دینے والوں کی قسم! ');
INSERT INTO "junagarri" VALUES(79,3,'79:3','اور تیرنے پھرنے والوں کی قسم! ');
INSERT INTO "junagarri" VALUES(79,4,'79:4','پھر دوڑ کر آگے بڑھنے والوں کی قسم! ');
INSERT INTO "junagarri" VALUES(79,5,'79:5','پھر کام کی تدبیر کرنے والوں کی قسم! ');
INSERT INTO "junagarri" VALUES(79,6,'79:6','جس دن کانپنے والی کانپے گی. ');
INSERT INTO "junagarri" VALUES(79,7,'79:7','اس کے بعد ایک پیچھے آنے والی (پیچھے پیچھے) آئے گی. ');
INSERT INTO "junagarri" VALUES(79,8,'79:8','(بہت سے) دل اس دن دھڑکتے ہوں گے. ');
INSERT INTO "junagarri" VALUES(79,9,'79:9','جن کی نگاہیں نیچی ہوں گی. ');
INSERT INTO "junagarri" VALUES(79,10,'79:10','کہتے ہیں کہ کیا ہم پہلی کی سی حالت کی طرف پھر لوٹائے جائیں گے؟ ');
INSERT INTO "junagarri" VALUES(79,11,'79:11','کیا اس وقت جب کہ ہم بوسیده ہڈیاں ہو جائیں گے؟ ');
INSERT INTO "junagarri" VALUES(79,12,'79:12','کہتے ہیں کہ پھر تو یہ لوٹنا نقصان ده ہے ');
INSERT INTO "junagarri" VALUES(79,13,'79:13','(معلوم ہونا چاہئے) وه تو صرف ایک (خوفناک) ڈانٹ ہے.');
INSERT INTO "junagarri" VALUES(79,14,'79:14','کہ (جس کے ظاہر ہوتے ہی) وه ایک دم میدان میں جمع ہو جائیں گے. ');
INSERT INTO "junagarri" VALUES(79,15,'79:15','کیا موسیٰ (علیہ السلام) کی خبر تمہیں پہنچی ہے؟');
INSERT INTO "junagarri" VALUES(79,16,'79:16','جب کہ انہیں ان کے رب نے پاک میدان طویٰ میں پکارا. ');
INSERT INTO "junagarri" VALUES(79,17,'79:17','(کہ) تم فرعون کے پاس جاؤ اس نے سرکشی اختیار کر لی ہے. ');
INSERT INTO "junagarri" VALUES(79,18,'79:18','اس سے کہو کہ کیا تو اپنی درستگی اور اصلاح چاہتا ہے. ');
INSERT INTO "junagarri" VALUES(79,19,'79:19','اور یہ کہ میں تجھے تیرے رب کی راه دکھاؤں تاکہ تو (اس سے) ڈرنے لگے. ');
INSERT INTO "junagarri" VALUES(79,20,'79:20','پس اسے بڑی نشانی دکھائی. ');
INSERT INTO "junagarri" VALUES(79,21,'79:21','تو اس نے جھٹلایا اور نافرمانی کی. ');
INSERT INTO "junagarri" VALUES(79,22,'79:22','پھر پلٹا دوڑ دھوپ کرتے ہوئے. ');
INSERT INTO "junagarri" VALUES(79,23,'79:23','پھر سب کو جمع کرکے پکارا. ');
INSERT INTO "junagarri" VALUES(79,24,'79:24','تم سب کا رب میں ہی ہوں.');
INSERT INTO "junagarri" VALUES(79,25,'79:25','تو (سب سے بلند وبالا) اللہ نے بھی اسے آخرت کے اور دنیا کے عذاب میں گرفتار کرلیا. ');
INSERT INTO "junagarri" VALUES(79,26,'79:26','بیشک اس میں اس شخص کے لئے عبرت ہے جو ڈرے. ');
INSERT INTO "junagarri" VALUES(79,27,'79:27','کیا تمہارا پیدا کرنا زیاده دشوار ہے یا آسمان کا ؟ اللہ تعالیٰ نے اسے بنایا.');
INSERT INTO "junagarri" VALUES(79,28,'79:28','اس کی بلندی اونچی کی پھر اسے ٹھیک ٹھاک کر دیا. ');
INSERT INTO "junagarri" VALUES(79,29,'79:29','اسکی رات کو تاریک بنایا اور اس کے دن کو نکالا. ');
INSERT INTO "junagarri" VALUES(79,30,'79:30','اور اس کے بعد زمین کو (ہموار) بچھا دیا. ');
INSERT INTO "junagarri" VALUES(79,31,'79:31','اس میں سے پانی اور چاره نکالا.');
INSERT INTO "junagarri" VALUES(79,32,'79:32','اور پہاڑوں کو (مضبوط) گاڑ دیا.');
INSERT INTO "junagarri" VALUES(79,33,'79:33','یہ سب تمہارے اور تمہارے جانوروں کے فائدے کے لئے (ہیں).');
INSERT INTO "junagarri" VALUES(79,34,'79:34','پس جب وه بڑی آفت (قیامت) آجائے گی.');
INSERT INTO "junagarri" VALUES(79,35,'79:35','جس دن کہ انسان اپنے کیے ہوئے کاموں کو یاد کرے گا.');
INSERT INTO "junagarri" VALUES(79,36,'79:36','اور (ہر) دیکھنے والے کے سامنے جہنم ظاہر کی جائے گی. ');
INSERT INTO "junagarri" VALUES(79,37,'79:37','تو جس (شخص) نے سرکشی کی (ہوگی). ');
INSERT INTO "junagarri" VALUES(79,38,'79:38','اور دنیوی زندگی کو ترجیح دی (ہوگی). ');
INSERT INTO "junagarri" VALUES(79,39,'79:39','اس کا ٹھکانا جہنم ہی ہے. ');
INSERT INTO "junagarri" VALUES(79,40,'79:40','ہاں جو شخص اپنے رب کے سامنے کھڑے ہونے سے ڈرتا رہا ہوگا اور اپنے نفس کو خواہش سے روکا ہوگا. ');
INSERT INTO "junagarri" VALUES(79,41,'79:41','تو اس کا ٹھکانا جنت ہی ہے. ');
INSERT INTO "junagarri" VALUES(79,42,'79:42','لوگ آپ سے قیامت کے واقع ہونے کا وقت دریافت کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(79,43,'79:43','آپ کو اس کے بیان کرنے سے کیا تعلق؟ ');
INSERT INTO "junagarri" VALUES(79,44,'79:44','اس کے علم کی انتہا تو اللہ کی جانب ہے.');
INSERT INTO "junagarri" VALUES(79,45,'79:45','آپ تو صرف اس سے ڈرتے رہنے والوں کو آگاه کرنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(79,46,'79:46','جس روز یہ اسے دیکھ لیں گے تو ایسا معلوم ہوگا کہ صرف دن کا آخری حصہ یا اول حصہ ہی (دنیا میں) رہے ہیں. ');
INSERT INTO "junagarri" VALUES(80,1,'80:1','وه ترش رو ہوا اور منھ موڑ لیا.');
INSERT INTO "junagarri" VALUES(80,2,'80:2','(صرف اس لئے) کہ اس کے پاس ایک نابینا آیا. ');
INSERT INTO "junagarri" VALUES(80,3,'80:3','تجھے کیا خبر شاید وه سنور جاتا. ');
INSERT INTO "junagarri" VALUES(80,4,'80:4','یا نصیحت سنتا اور اسے نصیحت فائده پہنچاتی.');
INSERT INTO "junagarri" VALUES(80,5,'80:5','جو بے پرواہی کرتا ہے. ');
INSERT INTO "junagarri" VALUES(80,6,'80:6','اس کی طرف تو تو پوری توجہ کرتا ہے. ');
INSERT INTO "junagarri" VALUES(80,7,'80:7','حالانکہ اس کے نہ سنورنے سے تجھ پر کوئی الزام نہیں. ');
INSERT INTO "junagarri" VALUES(80,8,'80:8','اور جو شخص تیرے پاس دوڑتا ہوا آتا ہے. ');
INSERT INTO "junagarri" VALUES(80,9,'80:9','اور وه ڈر (بھی) رہا ہے. ');
INSERT INTO "junagarri" VALUES(80,10,'80:10','تو اس سے بےرخی برتتا ہے. ');
INSERT INTO "junagarri" VALUES(80,11,'80:11','یہ ٹھیک نہیں قرآن تو نصیحت (کی چیز) ہے.');
INSERT INTO "junagarri" VALUES(80,12,'80:12','جو چاہے اس سے نصیحت لے. ');
INSERT INTO "junagarri" VALUES(80,13,'80:13','(یہ تو) پر عظمت صحیفوں میں (ہے). ');
INSERT INTO "junagarri" VALUES(80,14,'80:14','جو بلند وبالا اور پاک صاف ہے. ');
INSERT INTO "junagarri" VALUES(80,15,'80:15','ایسے لکھنے والوں کے ہاتھوں میں ہے. ');
INSERT INTO "junagarri" VALUES(80,16,'80:16','جو بزرگ اور پاکباز ہیں. ');
INSERT INTO "junagarri" VALUES(80,17,'80:17','اللہ کی مار انسان پر کیسا ناشکرا ہے. ');
INSERT INTO "junagarri" VALUES(80,18,'80:18','اسے اللہ نے کس چیز سے پیدا کیا.');
INSERT INTO "junagarri" VALUES(80,19,'80:19','اسے) ایک نطفہ سے پیدا کیا،پھراس کا اندازہ مقررکیا . ');
INSERT INTO "junagarri" VALUES(80,20,'80:20','پھر اس کے لئے راستہ آسان کیا. ');
INSERT INTO "junagarri" VALUES(80,21,'80:21','پھر اسے موت دی اور پھر قبر میں دفن کیا.');
INSERT INTO "junagarri" VALUES(80,22,'80:22','پھر جب چاہے گا اسے زنده کر دے گا. ');
INSERT INTO "junagarri" VALUES(80,23,'80:23','ہرگز نہیں*۔ اس نے اب تک اللہ کے حکم کی بجا آوری نہیں کی.');
INSERT INTO "junagarri" VALUES(80,24,'80:24','انسان کو چاہئے کہ اپنے کھانے کو دیکھے. ');
INSERT INTO "junagarri" VALUES(80,25,'80:25','کہ ہم نے خوب پانی برسایا.');
INSERT INTO "junagarri" VALUES(80,26,'80:26','پھر پھاڑا زمین کو اچھی طرح.');
INSERT INTO "junagarri" VALUES(80,27,'80:27','پھر اس میں سے اناج اگائے.');
INSERT INTO "junagarri" VALUES(80,28,'80:28','اور انگور اور ترکاری.');
INSERT INTO "junagarri" VALUES(80,29,'80:29','اور زیتون اور کھجور.');
INSERT INTO "junagarri" VALUES(80,30,'80:30','اور گنجان باغات.');
INSERT INTO "junagarri" VALUES(80,31,'80:31','اور میوه اور (گھاس) چاره (بھی اگایا). ');
INSERT INTO "junagarri" VALUES(80,32,'80:32','تمہارے استعمال وفائدے کے لئے اور تمہارے چوپایوں کے لئے.');
INSERT INTO "junagarri" VALUES(80,33,'80:33','پس جب کہ کان بہرے کر دینے والی (قیامت) آجائے گی. ');
INSERT INTO "junagarri" VALUES(80,34,'80:34','اس دن آدمی اپنے بھائی سے بھاگے گا.');
INSERT INTO "junagarri" VALUES(80,35,'80:35','اور اپنی ماں اور اپنے باپ سے.');
INSERT INTO "junagarri" VALUES(80,36,'80:36','اور اپنی بیوی اور اپنی اولاد سے بھاگے گا.');
INSERT INTO "junagarri" VALUES(80,37,'80:37','ان میں سے ہر ایک کو اس دن ایسی فکر (دامن گیر) ہوگی جو اس کے لئے کافی ہوگی. ');
INSERT INTO "junagarri" VALUES(80,38,'80:38','اس دن بہت سے چہرے روشن ہوں گے.');
INSERT INTO "junagarri" VALUES(80,39,'80:39','(جو) ہنستے ہوئے اور ہشاش بشاش ہوں گے. ');
INSERT INTO "junagarri" VALUES(80,40,'80:40','اور بہت سے چہرے اس دن غبار آلود ہوں گے.');
INSERT INTO "junagarri" VALUES(80,41,'80:41','جن پر سیاہی چڑھی ہوئی ہوگی. ');
INSERT INTO "junagarri" VALUES(80,42,'80:42','وه یہی کافر بدکردار لوگ ہوں گے. ');
INSERT INTO "junagarri" VALUES(81,1,'81:1','جب سورج لپیٹ لیا جائے گا.');
INSERT INTO "junagarri" VALUES(81,2,'81:2','اور جب ستارے بے نور ہو جائیں گی. ');
INSERT INTO "junagarri" VALUES(81,3,'81:3','اور جب پہاڑ چلائے جائیں گے. ');
INSERT INTO "junagarri" VALUES(81,4,'81:4','اور جب دس ماه کی حاملہ اونٹنیاں چھوڑ دی جائیں. ');
INSERT INTO "junagarri" VALUES(81,5,'81:5','اور جب وحشی جانور اکھٹے کیے جائیں گے. ');
INSERT INTO "junagarri" VALUES(81,6,'81:6','اور جب سمندر بھڑکائے جائیں گے. ');
INSERT INTO "junagarri" VALUES(81,7,'81:7','اور جب جانیں (جسموں سے) ملا دی جائیں گی. ');
INSERT INTO "junagarri" VALUES(81,8,'81:8','اور جب زنده گاڑی ہوئی لڑکی سے سوال کیا جائے گا.');
INSERT INTO "junagarri" VALUES(81,9,'81:9','کہ کس گناه کی وجہ سے وه قتل کی گئی؟ ');
INSERT INTO "junagarri" VALUES(81,10,'81:10','اور جب نامہٴ اعمال کھول دیئے جائیں گے. ');
INSERT INTO "junagarri" VALUES(81,11,'81:11','اور جب آسمان کی کھال اتار لی جائے گی. ');
INSERT INTO "junagarri" VALUES(81,12,'81:12','اور جب جہنم بھڑکائی جائے گی.');
INSERT INTO "junagarri" VALUES(81,13,'81:13','اور جب جنت نزدیک کر دی جائے گی.');
INSERT INTO "junagarri" VALUES(81,14,'81:14','تو اس دن ہر شخص جان لے گا جو کچھ لے کر آیا ہوگا. ');
INSERT INTO "junagarri" VALUES(81,15,'81:15','میں قسم کھاتا ہوں پیچھے ہٹنے والے.');
INSERT INTO "junagarri" VALUES(81,16,'81:16','چلنے پھرنے والے چھپنے والے ستاروں کی. ');
INSERT INTO "junagarri" VALUES(81,17,'81:17','اور رات کی جب جانے لگے. ');
INSERT INTO "junagarri" VALUES(81,18,'81:18','اور صبح کی جب چمکنے لگے. ');
INSERT INTO "junagarri" VALUES(81,19,'81:19','یقیناً یہ ایک بزرگ رسول کا کہا ہوا ہے. ');
INSERT INTO "junagarri" VALUES(81,20,'81:20','جو قوت والا ہے ، عرش والے (اللہ) کے نزدیک بلند مرتبہ ہے.');
INSERT INTO "junagarri" VALUES(81,21,'81:21','جس کی (آسمانوں میں) اطاعت کی جاتی ہے اورامین ہے.');
INSERT INTO "junagarri" VALUES(81,22,'81:22','اور تمہارا ساتھی دیوانہ نہیں ہے. ');
INSERT INTO "junagarri" VALUES(81,23,'81:23','اس نے اس (فرشتے) کو آسمان کے کھلے کنارے پر دیکھا بھی ہے. ');
INSERT INTO "junagarri" VALUES(81,24,'81:24','اور یہ غیب کی باتوں کو بتلانے میں بخیل بھی نہیں. ');
INSERT INTO "junagarri" VALUES(81,25,'81:25','اور یہ قرآن شیطان مردود کا کلام نہیں. ');
INSERT INTO "junagarri" VALUES(81,26,'81:26','پھر تم کہاں جا رہے ہو. ');
INSERT INTO "junagarri" VALUES(81,27,'81:27','یہ تو تمام جہان والوں کے لئے نصیحت نامہ ہے.');
INSERT INTO "junagarri" VALUES(81,28,'81:28','(بالخصوص) اس کے لئے جو تم میں سے سیدھی راه پر چلنا چاہے.');
INSERT INTO "junagarri" VALUES(81,29,'81:29','اور تم بغیر پروردگار عالم کے چاہے کچھ نہیں چاه سکتے. ');
INSERT INTO "junagarri" VALUES(82,1,'82:1','جب آسمان پھٹ جائے گا. ');
INSERT INTO "junagarri" VALUES(82,2,'82:2','اور جب ستارے جھڑ جائیں گے.');
INSERT INTO "junagarri" VALUES(82,3,'82:3','اور جب سمندر بہہ نکلیں گے. ');
INSERT INTO "junagarri" VALUES(82,4,'82:4','اور جب قبریں (شق کر کے) اکھاڑ دی جائیں گی. ');
INSERT INTO "junagarri" VALUES(82,5,'82:5','(اس وقت) ہر شخص اپنے آگے بھیجے ہوئے اور پیچھے چھوڑے ہوئے (یعنی اگلے پچھلے اعمال) کو معلوم کر لے گا. ');
INSERT INTO "junagarri" VALUES(82,6,'82:6','اے انسان! تجھے اپنے رب کریم سے کس چیز نے بہکایا. ');
INSERT INTO "junagarri" VALUES(82,7,'82:7','جس (رب نے) تجھے پیدا کیا، پھر ٹھیک ٹھاک کیا ، پھر (درست اور) برابر بنایا. ');
INSERT INTO "junagarri" VALUES(82,8,'82:8','جس صورت میں چاہا تجھے جوڑ دیا. ');
INSERT INTO "junagarri" VALUES(82,9,'82:9','ہرگز نہیں بلکہ تم تو جزا وسزا کے دن کو جھٹلاتے ہو. ');
INSERT INTO "junagarri" VALUES(82,10,'82:10','یقیناً تم پر نگہبان عزت والے.');
INSERT INTO "junagarri" VALUES(82,11,'82:11','لکھنے والے مقرر ہیں.');
INSERT INTO "junagarri" VALUES(82,12,'82:12','جوکچھ تم کرتے ہو وه جانتے ہیں. ');
INSERT INTO "junagarri" VALUES(82,13,'82:13','یقیناً نیک لوگ (جنت کے عیش وآرام اور) نعمتوں میں ہوں گے.');
INSERT INTO "junagarri" VALUES(82,14,'82:14','اور یقیناً بدکار لوگ دوزخ میں ہوں گے. ');
INSERT INTO "junagarri" VALUES(82,15,'82:15','بدلے والے دن اس میں جائیں گے. ');
INSERT INTO "junagarri" VALUES(82,16,'82:16','وه اس سے کبھی غائب نہ ہونے پائیں گے. ');
INSERT INTO "junagarri" VALUES(82,17,'82:17','تجھے کچھ خبر بھی ہے کہ بدلے کا دن کیا ہے.');
INSERT INTO "junagarri" VALUES(82,18,'82:18','میں پھر (کہتا ہوں کہ) تجھے کیا معلوم کہ جزا (اور سزا) کا دن کیا ہے. ');
INSERT INTO "junagarri" VALUES(82,19,'82:19','(وه ہے) جس دن کوئی شخص کسی شخص کے لئے کسی چیز کا مختار نہ ہوگا، اور (تمام تر) احکام اس روز اللہ کے ہی ہوں گے. ');
INSERT INTO "junagarri" VALUES(83,1,'83:1','بڑی خرابی ہے ناپ تول میں کمی کرنے والوں کی.');
INSERT INTO "junagarri" VALUES(83,2,'83:2','کہ جب لوگوں سے ناپ کر لیتے ہیں تو پورا پورا لیتے ہیں.');
INSERT INTO "junagarri" VALUES(83,3,'83:3','اور جب انہیں ناپ کر یا تول کر دیتے ہیں تو کم دیتے ہیں. ');
INSERT INTO "junagarri" VALUES(83,4,'83:4','کیا انہیں اپنے مرنے کے بعد جی اٹھنے کا خیال نہیں.');
INSERT INTO "junagarri" VALUES(83,5,'83:5','اس عظیم دن کے لئے.');
INSERT INTO "junagarri" VALUES(83,6,'83:6','جس دن سب لوگ رب العالمین کے سامنے کھڑے ہوں گے. ');
INSERT INTO "junagarri" VALUES(83,7,'83:7','یقیناً بدکاروں کا نامہٴ اعمال سِجِّينٌ میں ہے. ');
INSERT INTO "junagarri" VALUES(83,8,'83:8','تجھے کیا معلوم سِجِّينٌ کیا ہے؟');
INSERT INTO "junagarri" VALUES(83,9,'83:9','(یہ تو) لکھی ہوئی کتاب ہے.');
INSERT INTO "junagarri" VALUES(83,10,'83:10','اس دن جھٹلانے والوں کی بڑی خرابی ہے.');
INSERT INTO "junagarri" VALUES(83,11,'83:11','جو جزا وسزا کے دن کو جھٹلاتے رہے.');
INSERT INTO "junagarri" VALUES(83,12,'83:12','اسے صرف وہی جھٹلاتا ہےجو حد سے آگے نکل جانے والا (اور) گناه گار ہوتا ہے.');
INSERT INTO "junagarri" VALUES(83,13,'83:13','جب اس کے سامنے ہماری آیتیں پڑھی جاتی ہیں تو کہہ دیتا ہے کہ یہ اگلوں کے افسانے ہیں. ');
INSERT INTO "junagarri" VALUES(83,14,'83:14','یوں نہیں بلکہ ان کے دلوں پر ان کےاعمال کی وجہ سے زنگ (چڑھ گیا) ہے. ');
INSERT INTO "junagarri" VALUES(83,15,'83:15','ہرگز نہیں یہ لوگ اس دن اپنے رب سےاوٹ میں رکھے جائیں گے. ');
INSERT INTO "junagarri" VALUES(83,16,'83:16','پھر یہ لوگ بالیقین جہنم میں جھونکے جائیں گے.');
INSERT INTO "junagarri" VALUES(83,17,'83:17','پھر کہہ دیا جائے گا کہ یہی ہے وه جسے تم جھٹلاتے رہے.');
INSERT INTO "junagarri" VALUES(83,18,'83:18','لرگزنہیں،بےشک نیکوکاروں کے اعمال نامے علییں میں ہیں . ');
INSERT INTO "junagarri" VALUES(83,19,'83:19','اورتجھے کیا پتہ کہ عِلِّیین کیا ہے؟');
INSERT INTO "junagarri" VALUES(83,20,'83:20','(وه تو) لکھی ہوئی کتاب ہے.');
INSERT INTO "junagarri" VALUES(83,21,'83:21','مقرب (فرشتے) اس کا مشاہده کرتے ہیں.');
INSERT INTO "junagarri" VALUES(83,22,'83:22','یقیناً نیک لوگ (بڑی) نعمتوں میں ہوں گے.');
INSERT INTO "junagarri" VALUES(83,23,'83:23','مسہریوں پر بیٹھے دیکھ رہے ہوں گے.');
INSERT INTO "junagarri" VALUES(83,24,'83:24','تو ان کے چہروں سے ہی نعمتوں کی تروتازگی پہچان لے گا. ');
INSERT INTO "junagarri" VALUES(83,25,'83:25','یہ لوگ سربمہر خالص شراب پلائے جائیں گے. ');
INSERT INTO "junagarri" VALUES(83,26,'83:26','جس پر مشک کی مہر ہوگی، سبقت لے جانے والوں کو اسی میں سبقت کرنی چاہئے. ');
INSERT INTO "junagarri" VALUES(83,27,'83:27','اور اس کی آمیزش تسنیم کی ہوگی. ');
INSERT INTO "junagarri" VALUES(83,28,'83:28','(یعنی) وه چشمہ جس کا پانی مقرب لوگ پیئں گے.');
INSERT INTO "junagarri" VALUES(83,29,'83:29','گنہگار لوگ ایمان والوں کی ہنسی اڑایا کرتے تھے. ');
INSERT INTO "junagarri" VALUES(83,30,'83:30','اور ان کے پاس سے گزرتے ہوئے آپس میں آنکھ کےاشارے کرتے تھے. ');
INSERT INTO "junagarri" VALUES(83,31,'83:31','اور جب اپنے والوں کی طرف لوٹتے تو دل لگیاں کرتے تھے. ');
INSERT INTO "junagarri" VALUES(83,32,'83:32','اور جب انہیں دیکھتے تو کہتے یقیناً یہ لوگ گمراه (بے راه) ہیں. ');
INSERT INTO "junagarri" VALUES(83,33,'83:33','یہ ان پر پاسبان بنا کر تو نہیں بھیجے گئے. ');
INSERT INTO "junagarri" VALUES(83,34,'83:34','پس آج ایمان والے ان کافروں پر ہنسیں گے. ');
INSERT INTO "junagarri" VALUES(83,35,'83:35','تختوں پر بیٹھے دیکھ رہے ہوں گے.');
INSERT INTO "junagarri" VALUES(83,36,'83:36','کہ اب ان منکروں نے جیسا یہ کرتے تھے پورا پورا بدلہ پالیا. ');
INSERT INTO "junagarri" VALUES(84,1,'84:1','جب آسمان پھٹ جائے گا. ');
INSERT INTO "junagarri" VALUES(84,2,'84:2','اور اپنے رب کے حکم پر کان لگائے گا اور اسی کے لائق وه ہے. ');
INSERT INTO "junagarri" VALUES(84,3,'84:3','اور جب زمین (کھینچ کر) پھیلا دی جائے گی. ');
INSERT INTO "junagarri" VALUES(84,4,'84:4','اور اس میں جو ہے اسے وه اگل دے گی اور خالی ہو جائے گی. ');
INSERT INTO "junagarri" VALUES(84,5,'84:5','اور اپنے رب کے حکم پر کان لگائے گی اور اسی کے لائق وه ہے.');
INSERT INTO "junagarri" VALUES(84,6,'84:6','اے انسان! تو اپنے رب سے ملنے تک یہ کوشش اور تمام کام اور محنتیں کرکے اس سے ملاقات کرنے والا ہے. ');
INSERT INTO "junagarri" VALUES(84,7,'84:7','تو (اس وقت) جس شخص کے داہنے ہاتھ میں اعمال نامہ دیا جائے گا.');
INSERT INTO "junagarri" VALUES(84,8,'84:8','اس کا حساب تو بڑی آسانی سے لیا جائے گا. ');
INSERT INTO "junagarri" VALUES(84,9,'84:9','اور وه اپنے اہل کی طرف ہنسی خوشی لوٹ آئے گا. ');
INSERT INTO "junagarri" VALUES(84,10,'84:10','ہاں جس شخص کا اعمال نامہ اس کی پیٹھ کے پیچھے سے دیا جائے گا.');
INSERT INTO "junagarri" VALUES(84,11,'84:11','تو وه موت کو بلانے لگے گا. ');
INSERT INTO "junagarri" VALUES(84,12,'84:12','اور بھڑکتی ہوئی جہنم میں داخل ہوگا.');
INSERT INTO "junagarri" VALUES(84,13,'84:13','یہ شخص اپنے متعلقین میں (دنیا میں) خوش تھا. ');
INSERT INTO "junagarri" VALUES(84,14,'84:14','اس کا خیال تھا کہ اللہ کی طرف لوٹ کر ہی نہ جائے گا. ');
INSERT INTO "junagarri" VALUES(84,15,'84:15','کیوں نہیں ، حالانکہ اس کا رب اسے بخوبی دیکھ رہا تھا. ');
INSERT INTO "junagarri" VALUES(84,16,'84:16','مجھے شفق کی قسم ! اور رات کی!');
INSERT INTO "junagarri" VALUES(84,17,'84:17','اور اس کی جمع کرده چیزوں کی قسم.');
INSERT INTO "junagarri" VALUES(84,18,'84:18','اور چاند کی جب کہ وه کامل ہو جاتا ہے. ');
INSERT INTO "junagarri" VALUES(84,19,'84:19','یقیناً تم ایک حالت سے دوسری حالت پر پہنچو گے. ');
INSERT INTO "junagarri" VALUES(84,20,'84:20','انہیں کیا ہو گیا کہ ایمان نہیں لاتے.');
INSERT INTO "junagarri" VALUES(84,21,'84:21','اور جب ان کے پاس قرآن پڑھا جاتا ہے تو سجده نہیں کرتے. ');
INSERT INTO "junagarri" VALUES(84,22,'84:22','بلکہ جنہوں نے کفر کیا وه جھٹلا رہے ہیں. ');
INSERT INTO "junagarri" VALUES(84,23,'84:23','اور اللہ تعالیٰ خوب جانتا ہے جو کچھ یہ دلوں میں رکھتے ہیں. ');
INSERT INTO "junagarri" VALUES(84,24,'84:24','انہیں المناک عذابوں کی خوشخبری سنا دو.');
INSERT INTO "junagarri" VALUES(84,25,'84:25','ہاں ایمان والوں اور نیک اعمال والوں کو بے شمار اور نہ ختم ہونے والا اجر ہے.');
INSERT INTO "junagarri" VALUES(85,1,'85:1','برجوں والے آسمان کی قسم! ');
INSERT INTO "junagarri" VALUES(85,2,'85:2','وعده کیے ہوئے دن کی قسم! ');
INSERT INTO "junagarri" VALUES(85,3,'85:3','حاضر ہونے والے اور حاضر کئے گئے کی قسم! ');
INSERT INTO "junagarri" VALUES(85,4,'85:4','(کہ) خندقوں والے ہلاک کیے گئے. ');
INSERT INTO "junagarri" VALUES(85,5,'85:5','وه ایک آگ تھی ایندھن والی. ');
INSERT INTO "junagarri" VALUES(85,6,'85:6','جب کہ وه لوگ اس کے آس پاس بیٹھے تھے. ');
INSERT INTO "junagarri" VALUES(85,7,'85:7','اور مسلمانوں کے ساتھ جو کر رہے تھے اس کو اپنے سامنے دیکھ رہے تھے.');
INSERT INTO "junagarri" VALUES(85,8,'85:8','یہ لوگ ان مسلمانوں (کے کسی اور گناه کا) بدلہ نہیں لے رہے تھے، سوائے اس کے کہ وه اللہ غالب لائق حمد کی ذات پر ایمان لائے تھے. ');
INSERT INTO "junagarri" VALUES(85,9,'85:9','جس کے لئے آسمان وزمین کا ملک ہے۔ اور اللہ تعالیٰ ہرچیز کو دیکھ رہا ہے.');
INSERT INTO "junagarri" VALUES(85,10,'85:10','بیشک جن لوگوں نے مسلمان مردوں اور عورتوں کو ستایا پھر توبہ (بھی) نہ کی تو ان کے لئے جہنم کا عذاب ہے اور جلنے کا عذاب ہے.');
INSERT INTO "junagarri" VALUES(85,11,'85:11','بیشک ایمان قبول کرنے والوں اور نیک کام کرنے والوں کے لئے وه باغات ہیں۔ جن کے نیچے بہریں بہہ رہی ہیں۔ یہی بڑی کامیابی ہے.');
INSERT INTO "junagarri" VALUES(85,12,'85:12','یقیناً تیرے رب کی پکڑ بڑی سخت ہے. ');
INSERT INTO "junagarri" VALUES(85,13,'85:13','وہی پہلی مرتبہ پیدا کرتا ہے اور وہی دوباره پیدا کرے گا. ');
INSERT INTO "junagarri" VALUES(85,14,'85:14','وه بڑا بخشش کرنے والا اور بہت محبت کرنے والا ہے.');
INSERT INTO "junagarri" VALUES(85,15,'85:15','عرش کا مالک عظمت والا ہے. ');
INSERT INTO "junagarri" VALUES(85,16,'85:16','جو چاہے اسے کر گزرنے والا ہے. ');
INSERT INTO "junagarri" VALUES(85,17,'85:17','تجھے لشکروں کی خبر بھی ملی ہے؟*');
INSERT INTO "junagarri" VALUES(85,18,'85:18','(یعنی) فرعون اور ثمود کی. ');
INSERT INTO "junagarri" VALUES(85,19,'85:19','(کچھ نہیں) بلکہ کافر تو جھٹلانے میں پڑے ہوئے ہیں.');
INSERT INTO "junagarri" VALUES(85,20,'85:20','اور اللہ تعالیٰ بھی انہیں ہر طرف سے گھیرے ہوئے ہے. ');
INSERT INTO "junagarri" VALUES(85,21,'85:21','بلکہ یہ قرآن ہے بڑی شان والا.');
INSERT INTO "junagarri" VALUES(85,22,'85:22','لوح محفوظ میں (لکھا ہوا). ');
INSERT INTO "junagarri" VALUES(86,1,'86:1','قسم ہے آسمان کی اور اندھیرے میں روشن ہونے والے کی.');
INSERT INTO "junagarri" VALUES(86,2,'86:2','تجھے معلوم بھی ہے کہ وه رات کو نمودار ہونے والی چیز کیا ہے؟');
INSERT INTO "junagarri" VALUES(86,3,'86:3','وه روشن ستاره ہے. ');
INSERT INTO "junagarri" VALUES(86,4,'86:4','کوئی ایسا نہیں جس پر نگہبان فرشتہ نہ ہو. ');
INSERT INTO "junagarri" VALUES(86,5,'86:5','انسان کو دیکھنا چاہئے کہ وه کس چیز سے پیدا کیا گیا ہے.');
INSERT INTO "junagarri" VALUES(86,6,'86:6','وه ایک اچھلتے پانی سے پیدا کیا گیا ہے. ');
INSERT INTO "junagarri" VALUES(86,7,'86:7','جو پیٹھ اور سینے کے درمیان سے نکلتا ہے. ');
INSERT INTO "junagarri" VALUES(86,8,'86:8','بیشک وه اسے پھیر لانے پر یقیناً قدرت رکھنے والا ہے. ');
INSERT INTO "junagarri" VALUES(86,9,'86:9','جس دن پوشیده بھیدوں کی جانچ پڑتال ہوگی. ');
INSERT INTO "junagarri" VALUES(86,10,'86:10','تو نہ ہوگا اس کے پاس کچھ زور نہ مدددگار. ');
INSERT INTO "junagarri" VALUES(86,11,'86:11','بارش والے آسمان کی قسم! ');
INSERT INTO "junagarri" VALUES(86,12,'86:12','اور پھٹنے والی زمین کی قسم! ');
INSERT INTO "junagarri" VALUES(86,13,'86:13','بیشک یہ (قرآن) البتہ دو ٹوک فیصلہ کرنے والا کلام ہے. ');
INSERT INTO "junagarri" VALUES(86,14,'86:14','یہ ہنسی کی (اور بے فائده) بات نہیں. ');
INSERT INTO "junagarri" VALUES(86,15,'86:15','البتہ کافر داؤ گھات میں ہیں. ');
INSERT INTO "junagarri" VALUES(86,16,'86:16','اور میں بھی ایک چال چل رہا ہوں. ');
INSERT INTO "junagarri" VALUES(86,17,'86:17','تو کافروں کو مہلت دے انہیں تھوڑے دنوں چھوڑ دے.');
INSERT INTO "junagarri" VALUES(87,1,'87:1','اپنے بہت ہی بلند اللہ کے نام کی پاکیزگی بیان کر. ');
INSERT INTO "junagarri" VALUES(87,2,'87:2','جس نے پیدا کیا اور صحیح سالم بنایا. ');
INSERT INTO "junagarri" VALUES(87,3,'87:3','اور جس نے (ٹھیک ٹھاک) اندازه کیا اور پھر راه دکھائی. ');
INSERT INTO "junagarri" VALUES(87,4,'87:4','اور جس نے تازه گھاس پیدا کی. ');
INSERT INTO "junagarri" VALUES(87,5,'87:5','پھر اس نے اس کو (سکھا کر) سیاه کوڑا کر دیا. ');
INSERT INTO "junagarri" VALUES(87,6,'87:6','ہم تجھے پڑھائیں گے پھر تو نہ بھولے گا. ');
INSERT INTO "junagarri" VALUES(87,7,'87:7','مگر جو کچھ اللہ چاہے۔ وه ظاہر اور پوشیده کو جانتا ہے. ');
INSERT INTO "junagarri" VALUES(87,8,'87:8','ہم آپ کے لئے آسانی پیدا کر دیں گے. ');
INSERT INTO "junagarri" VALUES(87,9,'87:9','تو آپ نصیحت کرتے رہیں اگر نصیحت کچھ فائده دے. ');
INSERT INTO "junagarri" VALUES(87,10,'87:10','ڈرنے والا تو نصیحت لے گا. ');
INSERT INTO "junagarri" VALUES(87,11,'87:11','(ہاں) بد بخت اس سے گریز کرے گا. ');
INSERT INTO "junagarri" VALUES(87,12,'87:12','جو بڑی آگ میں جائے گا.');
INSERT INTO "junagarri" VALUES(87,13,'87:13','جہاں پھر نہ وه مرے گا نہ جئےگا ، (بلکہ حالت نزع میں پڑا رہے گا).');
INSERT INTO "junagarri" VALUES(87,14,'87:14','بیشک اس نے فلاح پالی جو پاک ہوگیا. ');
INSERT INTO "junagarri" VALUES(87,15,'87:15','اور جس نے اپنے رب کا نام یاد رکھا اور نماز پڑھتا رہا.');
INSERT INTO "junagarri" VALUES(87,16,'87:16','لیکن تم تو دنیا کی زندگی کو ترجیح دیتے ہو.');
INSERT INTO "junagarri" VALUES(87,17,'87:17','اور آخرت بہت بہتر اور بہت بقا والی ہے. ');
INSERT INTO "junagarri" VALUES(87,18,'87:18','یہ باتیں پہلی کتابوں میں بھی ہیں.');
INSERT INTO "junagarri" VALUES(87,19,'87:19','(یعنی) ابراہیم اور موسیٰ کی کتابوں میں.');
INSERT INTO "junagarri" VALUES(88,1,'88:1','کیا تجھے بھی چھپا لینے والی (قیامت) کی خبر پہنچی ہے. ');
INSERT INTO "junagarri" VALUES(88,2,'88:2','اس دن بہت سے چہرے ذلیل ہوں گے. ');
INSERT INTO "junagarri" VALUES(88,3,'88:3','(اور) محنت کرنے والے تھکے ہوئے ہوں گے. ');
INSERT INTO "junagarri" VALUES(88,4,'88:4','وه دہکتی ہوئی آگ میں جائیں گے.');
INSERT INTO "junagarri" VALUES(88,5,'88:5','اور نہایت گرم چشمے کا پانی ان کو پلایا جائے گا. ');
INSERT INTO "junagarri" VALUES(88,6,'88:6','ان کے لئے سوائے کانٹے دار درختوں کے اور کچھ کھانے کو نہ ہوگا. ');
INSERT INTO "junagarri" VALUES(88,7,'88:7','جو نہ موٹا کرے گا نہ بھوک مٹائے گا.');
INSERT INTO "junagarri" VALUES(88,8,'88:8','بہت سے چہرے اس دن تروتازه اور (آسوده حال) ہوں گے.');
INSERT INTO "junagarri" VALUES(88,9,'88:9','اپنی کوشش پر خوش ہوں گے.');
INSERT INTO "junagarri" VALUES(88,10,'88:10','بلند وبالا جنتوں میں ہوں گے.');
INSERT INTO "junagarri" VALUES(88,11,'88:11','جہاں کوئی بیہوده بات نہیں سنیں گے.');
INSERT INTO "junagarri" VALUES(88,12,'88:12','جہاں بہتا ہوا چشمہ ہوگا.');
INSERT INTO "junagarri" VALUES(88,13,'88:13','(اور) اس میں اونچے اونچے تخت ہوں گے.');
INSERT INTO "junagarri" VALUES(88,14,'88:14','اور آبخورے رکھے ہوئے (ہوں گے).');
INSERT INTO "junagarri" VALUES(88,15,'88:15','اور ایک قطار میں لگے ہوئے تکیے ہوں گے.');
INSERT INTO "junagarri" VALUES(88,16,'88:16','اور مخملی مسندیں پھیلی پڑی ہوں گی. ');
INSERT INTO "junagarri" VALUES(88,17,'88:17','کیا یہ اونٹوں کو نہیں دیکھتے کہ وه کس طرح پیدا کیے گئے ہیں. ');
INSERT INTO "junagarri" VALUES(88,18,'88:18','اور آسمان کو کہ کس طرح اونچا کیا گیا ہے. ');
INSERT INTO "junagarri" VALUES(88,19,'88:19','اور پہاڑوں کی طرف کہ کس طرح گاڑ دیئے گئے ہیں. ');
INSERT INTO "junagarri" VALUES(88,20,'88:20','اور زمین کی طرف کہ کس طرح بچھائی گئی ہے. ');
INSERT INTO "junagarri" VALUES(88,21,'88:21','پس آپ نصیحت کر دیا کریں (کیونکہ) آپ صرف نصیحت کرنے والے ہیں. ');
INSERT INTO "junagarri" VALUES(88,22,'88:22','آپ کچھ ان پر داروغہ نہیں ہیں. ');
INSERT INTO "junagarri" VALUES(88,23,'88:23','ہاں! جو شخص روگردانی کرے اور کفر کرے.');
INSERT INTO "junagarri" VALUES(88,24,'88:24','اسے اللہ تعالیٰ بہت بڑا عذاب دے گا. ');
INSERT INTO "junagarri" VALUES(88,25,'88:25','بیشک ہماری طرف ان کا لوٹنا ہے.');
INSERT INTO "junagarri" VALUES(88,26,'88:26','پھر بیشک ہمارے ذمہ ہے ان سے حساب لینا. ');
INSERT INTO "junagarri" VALUES(89,1,'89:1','قسم ہے فجر کی! ');
INSERT INTO "junagarri" VALUES(89,2,'89:2','اور دس راتوں کی! ');
INSERT INTO "junagarri" VALUES(89,3,'89:3','اور جفت اور طاق کی! ');
INSERT INTO "junagarri" VALUES(89,4,'89:4','اور رات کی جب وه چلنے لگے. ');
INSERT INTO "junagarri" VALUES(89,5,'89:5','کیا ان میں عقلمند کے واسطے کافی قسم ہے. ');
INSERT INTO "junagarri" VALUES(89,6,'89:6','کیا آپ نے نہیں دیکھا کہ آپ کے رب نے عادیوں کے ساتھ کیا کیا. ');
INSERT INTO "junagarri" VALUES(89,7,'89:7','ستونوں والے ارم کے ساتھ. ');
INSERT INTO "junagarri" VALUES(89,8,'89:8','جس کی مانند (کوئی قوم) ملکوں میں پیدا نہیں کی گئی. ');
INSERT INTO "junagarri" VALUES(89,9,'89:9','اور ثمودیوں کے ساتھ جنہوں نے وادی میں بڑے بڑے پتھر تراشے تھے. ');
INSERT INTO "junagarri" VALUES(89,10,'89:10','اور فرعون کے ساتھ جو میخوں والا تھا. ');
INSERT INTO "junagarri" VALUES(89,11,'89:11','ان سبھوں نے شہروں میں سر اٹھا رکھا تھا.');
INSERT INTO "junagarri" VALUES(89,12,'89:12','اور بہت فساد مچا رکھا تھا.');
INSERT INTO "junagarri" VALUES(89,13,'89:13','آخر تیرے رب نے ان سب پر عذاب کا کوڑا برسایا. ');
INSERT INTO "junagarri" VALUES(89,14,'89:14','یقیناً تیرا رب گھات میں ہے. ');
INSERT INTO "junagarri" VALUES(89,15,'89:15','انسان (کا یہ حال ہے کہ) جب اسے اس کا رب آزماتا ہے اور عزت ونعمت دیتا ہے تو وه کہنے لگتا ہے کہ میرے رب نے مجھے عزت دار بنایا. ');
INSERT INTO "junagarri" VALUES(89,16,'89:16','اور جب وه اس کو آزماتا ہے اس کی روزی تنگ کر دیتا ہے تو وه کہنے لگتا ہے کہ میرے رب نے میری اہانت کی (اور ذلیل کیا). ');
INSERT INTO "junagarri" VALUES(89,17,'89:17','ایسا ہرگز نہیں بلکہ (بات یہ ہے) کہ تم (ہی) لوگ یتیموں کی عزت نہیں کرتے. ');
INSERT INTO "junagarri" VALUES(89,18,'89:18','اور مسکینوں کے کھلانے کی ایک دوسرے کو ترغیب نہیں دیتے.');
INSERT INTO "junagarri" VALUES(89,19,'89:19','اور (مردوں کی) میراث سمیٹ سمیٹ کر کھاتے ہو. ');
INSERT INTO "junagarri" VALUES(89,20,'89:20','اور مال کو جی بھر کر عزیز رکھتے ہو. ');
INSERT INTO "junagarri" VALUES(89,21,'89:21','یقیناً جس وقت زمین کوٹ کوٹ کر برابر کر دی جائے گی.');
INSERT INTO "junagarri" VALUES(89,22,'89:22','اور تیرا رب (خود) آجائے گا اور فرشتے صفیں باندھ کر (آ جائیں گے). ');
INSERT INTO "junagarri" VALUES(89,23,'89:23','اور جس دن جہنم بھی لائی جائے گی اس دن انسان کو سمجھ آئے گی مگر آج اس کے سمجھنے کا فائده کہاں؟ ');
INSERT INTO "junagarri" VALUES(89,24,'89:24','وه کہے گا کہ کاش کہ میں نے اپنی اس زندگی کے لئے کچھ پیشگی سامان کیا ہوتا. ');
INSERT INTO "junagarri" VALUES(89,25,'89:25','پس آج اللہ کے عذاب جیسا عذاب کسی کا نہ ہوگا.');
INSERT INTO "junagarri" VALUES(89,26,'89:26','نہ اسکی قید وبند جیسی کسی کی قید وبند ہوگی. ');
INSERT INTO "junagarri" VALUES(89,27,'89:27','اے اطمینان والی روح.');
INSERT INTO "junagarri" VALUES(89,28,'89:28','تو اپنے رب کی طرف لوٹ چل اس طرح کہ تو اس سے راضی وه تجھ سے خوش.');
INSERT INTO "junagarri" VALUES(89,29,'89:29','پس میرے خاص بندوں میں داخل ہو جا.');
INSERT INTO "junagarri" VALUES(89,30,'89:30','اور میری جنت میں چلی جا.');
INSERT INTO "junagarri" VALUES(90,1,'90:1','میں اس شہر کی قسم کھاتا ہوں. ');
INSERT INTO "junagarri" VALUES(90,2,'90:2','اور آپ اس شہر میں مقیم ہیں. ');
INSERT INTO "junagarri" VALUES(90,3,'90:3','اور (قسم ہے) انسانی باپ اور اولاد کی. ');
INSERT INTO "junagarri" VALUES(90,4,'90:4','یقیناً ہم نے انسان کو (بڑی) مشقت میں پیدا کیا ہے. ');
INSERT INTO "junagarri" VALUES(90,5,'90:5','کیا یہ گمان کرتا ہے کہ یہ کسی کے بس میں ہی نہیں؟ ');
INSERT INTO "junagarri" VALUES(90,6,'90:6','کہتا (پھرتا) ہے کہ میں نے تو بہت کچھ مال خرچ کر ڈالا. ');
INSERT INTO "junagarri" VALUES(90,7,'90:7','کیا (یوں) سمجھتا ہے کہ کسی نے اسے دیکھا (ہی) نہیں؟ ');
INSERT INTO "junagarri" VALUES(90,8,'90:8','کیا ہم نے اس کی دو آنکھیں نہیں بنائیں. ');
INSERT INTO "junagarri" VALUES(90,9,'90:9','اور زبان اور دو ہونٹ (نہیں بنائے). ');
INSERT INTO "junagarri" VALUES(90,10,'90:10','ہم نے دکھا دیئے اس کو دونوں راستے. ');
INSERT INTO "junagarri" VALUES(90,11,'90:11','سو اس سے نہ ہو سکا کہ گھاٹی میں داخل ہوتا. ');
INSERT INTO "junagarri" VALUES(90,12,'90:12','اور کیا سمجھا کہ گھاٹی ہے کیا؟.');
INSERT INTO "junagarri" VALUES(90,13,'90:13','کسی گردن (غلام ،لونڈی) کو آزاد کرنا.');
INSERT INTO "junagarri" VALUES(90,14,'90:14','یا بھوک والے دن کھانا کھلانا.');
INSERT INTO "junagarri" VALUES(90,15,'90:15','کسی رشتہ دار یتیم کو.');
INSERT INTO "junagarri" VALUES(90,16,'90:16','یا خاکسار مسکین کو. ');
INSERT INTO "junagarri" VALUES(90,17,'90:17','پھر ان لوگوں میں سے ہو جاتا جو ایمان لاتے اور ایک دوسرے کو صبر کی اور رحم کرنے کی وصیت کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(90,18,'90:18','یہی لوگ ہیں دائیں بازو والے (خوش بختی والے).');
INSERT INTO "junagarri" VALUES(90,19,'90:19','اور جن لوگوں نے ہماری آیتوں کے ساتھ کفر کیا یہ کم بختی والے ہیں.');
INSERT INTO "junagarri" VALUES(90,20,'90:20','انہی پر آگ ہوگی جو چاروں طرف سے گھیری ہوئی ہوگی.');
INSERT INTO "junagarri" VALUES(91,1,'91:1','قسم ہے سورج کی اور اس کی دھوپ کی. ');
INSERT INTO "junagarri" VALUES(91,2,'91:2','قسم ہے چاند کی جب اس کے پیچھے آئے. ');
INSERT INTO "junagarri" VALUES(91,3,'91:3','قسم ہے دن کی جب سورج کو نمایاں کرے. ');
INSERT INTO "junagarri" VALUES(91,4,'91:4','قسم ہے رات کی جب اسے ڈھانپ لے. ');
INSERT INTO "junagarri" VALUES(91,5,'91:5','قسم ہے آسمان کی اور اس کے بنانے کی. ');
INSERT INTO "junagarri" VALUES(91,6,'91:6','قسم ہے زمین کی اور اسے ہموار کرنے کی. ');
INSERT INTO "junagarri" VALUES(91,7,'91:7','قسم ہے نفس کی اور اسے درست بنانے کی. ');
INSERT INTO "junagarri" VALUES(91,8,'91:8','پھر سمجھ دی اس کو بدکاری کی اور بچ کر چلنے کی. ');
INSERT INTO "junagarri" VALUES(91,9,'91:9','جس نے اسے پاک کیا وه کامیاب ہوا. ');
INSERT INTO "junagarri" VALUES(91,10,'91:10','اور جس نے اسے خاک میں ملا دیا وه ناکام ہوا. ');
INSERT INTO "junagarri" VALUES(91,11,'91:11','(قوم) ثمود نے اپنی سرکشی کے باعث جھٹلایا. ');
INSERT INTO "junagarri" VALUES(91,12,'91:12','جب ان میں کا بڑا بدبخت اٹھ کھڑا ہوا. ');
INSERT INTO "junagarri" VALUES(91,13,'91:13','انہیں اللہ کے رسول نے فرما دیا تھا کہ اللہ تعالیٰ کی اونٹنی اور اس کے پینے کی باری کی (حفاظت کرو). ');
INSERT INTO "junagarri" VALUES(91,14,'91:14','ان لوگوں نے اپنے پیغمبر کو جھوٹا سمجھ کر اس اونٹنی کی کوچیں کاٹ دیں ، پس ان کے رب نے ان کے گناہوں کے باعث ان پر ہلاکت ڈالی اور پھر ہلاکت کو عام کر دیا اور اس بستی کو برابر کردیا. ');
INSERT INTO "junagarri" VALUES(91,15,'91:15','وه نہیں ڈرتا اس کے تباه کن انجام سے. ');
INSERT INTO "junagarri" VALUES(92,1,'92:1','قسم ہے رات کی جب چھا جائے. ');
INSERT INTO "junagarri" VALUES(92,2,'92:2','اور قسم ہے دن کی جب روشن ہو. ');
INSERT INTO "junagarri" VALUES(92,3,'92:3','اور قسم ہے اس ذات کی جس نے نر وماده کو پیدا کیا. ');
INSERT INTO "junagarri" VALUES(92,4,'92:4','یقیناً تمہاری کوشش مختلف قسم کی ہے. ');
INSERT INTO "junagarri" VALUES(92,5,'92:5','جس نے دیا (اللہ کی راه میں) اور ڈرا (اپنے رب سے). ');
INSERT INTO "junagarri" VALUES(92,6,'92:6','اور نیک بات کی تصدیق کرتا رہے گا. ');
INSERT INTO "junagarri" VALUES(92,7,'92:7','تو ہم بھی اس کو آسان راستے کی سہولت دیں گے. ');
INSERT INTO "junagarri" VALUES(92,8,'92:8','لیکن جس نے بخیلی کی اور بے پرواہی برتی. ');
INSERT INTO "junagarri" VALUES(92,9,'92:9','اور نیک بات کی تکذیب کی. ');
INSERT INTO "junagarri" VALUES(92,10,'92:10','تو ہم بھی اس کی تنگی ومشکل کے سامان میسر کر دیں گے. ');
INSERT INTO "junagarri" VALUES(92,11,'92:11','اس کا مال اسے (اوندھا) گرنے کے وقت کچھ کام نہ آئے گا. ');
INSERT INTO "junagarri" VALUES(92,12,'92:12','بیشک راه دکھا دینا ہمارے ذمہ ہے. ');
INSERT INTO "junagarri" VALUES(92,13,'92:13','اور ہمارے ہی ہاتھ آخرت اور دنیا ہے. ');
INSERT INTO "junagarri" VALUES(92,14,'92:14','میں نے تو تمہیں شعلے مارتی ہوئی آگ سے ڈرا دیا ہے.');
INSERT INTO "junagarri" VALUES(92,15,'92:15','جس میں صرف وہی بدبخت داخل ہوگا.');
INSERT INTO "junagarri" VALUES(92,16,'92:16','جس نے جھٹلایا اور (اس کی پیروی سے) منھ پھیر لیا. ');
INSERT INTO "junagarri" VALUES(92,17,'92:17','اور اس سے ایسا شخص دور رکھا جائے گا جو بڑا پرہیزگار ہو گا. ');
INSERT INTO "junagarri" VALUES(92,18,'92:18','جو پاکی حاصل کرنے کے لئے اپنا مال دیتا ہے. ');
INSERT INTO "junagarri" VALUES(92,19,'92:19','کسی کا اس پر کوئی احسان نہیں کہ جس کا بدلہ دیا جا رہا ہو. ');
INSERT INTO "junagarri" VALUES(92,20,'92:20','بلکہ صرف اپنے پروردگار بزرگ وبلند کی رضا چاہنے کے لئے. ');
INSERT INTO "junagarri" VALUES(92,21,'92:21','یقیناً وه (اللہ بھی) عنقریب رضامند ہو جائے گا. ');
INSERT INTO "junagarri" VALUES(93,1,'93:1','قسم ہے چاشت کے وقت کی. ');
INSERT INTO "junagarri" VALUES(93,2,'93:2','اور قسم ہے رات کی جب چھا جائے. ');
INSERT INTO "junagarri" VALUES(93,3,'93:3','نہ تو تیرے رب نے تجھے چھوڑا ہے اور نہ وه بیزار ہو گیا ہے. ');
INSERT INTO "junagarri" VALUES(93,4,'93:4','یقیناً تیرے لئے انجام آغاز سے بہتر ہوگا. ');
INSERT INTO "junagarri" VALUES(93,5,'93:5','تجھے تیرا رب بہت جلد (انعام) دے گا اور تو راضی (وخوش) ہو جائے گا. ');
INSERT INTO "junagarri" VALUES(93,6,'93:6','کیا اس نے تجھے یتیم پا کر جگہ نہیں دی. ');
INSERT INTO "junagarri" VALUES(93,7,'93:7','اور تجھے راه بھولا پا کر ہدایت نہیں دی. ');
INSERT INTO "junagarri" VALUES(93,8,'93:8','اور تجھے نادار پاکر تونگر نہیں بنا دیا؟ ');
INSERT INTO "junagarri" VALUES(93,9,'93:9','پس یتیم پر تو بھی سختی نہ کیا کر. ');
INSERT INTO "junagarri" VALUES(93,10,'93:10','اور نہ سوال کرنے والے کو ڈانٹ ڈپٹ. ');
INSERT INTO "junagarri" VALUES(93,11,'93:11','اور اپنے رب کی نعمتوں کو بیان کرتا ره. ');
INSERT INTO "junagarri" VALUES(94,1,'94:1','کیا ہم نے تیرا سینہ نہیں کھول دیا. ');
INSERT INTO "junagarri" VALUES(94,2,'94:2','اور تجھ پر سے تیرا بوجھ ہم نے اتار دیا. ');
INSERT INTO "junagarri" VALUES(94,3,'94:3','جس نے تیری پیٹھ توڑ دی تھی.');
INSERT INTO "junagarri" VALUES(94,4,'94:4','اور ہم نے تیرا ذکر بلند کر دیا. ');
INSERT INTO "junagarri" VALUES(94,5,'94:5','پس یقیناً مشکل کے ساتھ آسانی ہے.');
INSERT INTO "junagarri" VALUES(94,6,'94:6','بیشک مشکل کے ساتھ آسانی ہے. ');
INSERT INTO "junagarri" VALUES(94,7,'94:7','پس جب تو فارغ ہو تو عبادت میں محنت کر. ');
INSERT INTO "junagarri" VALUES(94,8,'94:8','اور اپنے پروردگار ہی کی طرف دل لگا. ');
INSERT INTO "junagarri" VALUES(95,1,'95:1','قسم ہے انجیر کی اور زیتون کی.');
INSERT INTO "junagarri" VALUES(95,2,'95:2','اور طور سِینِین کی. ');
INSERT INTO "junagarri" VALUES(95,3,'95:3','اور اس امن والے شہر کی. ');
INSERT INTO "junagarri" VALUES(95,4,'95:4','یقیناً ہم نے انسان کو بہترین صورت میں پیدا کیا. ');
INSERT INTO "junagarri" VALUES(95,5,'95:5','پھر اسے نیچوں سے نیچا کر دیا. ');
INSERT INTO "junagarri" VALUES(95,6,'95:6','لیکن جو لوگ ایمان لائے اور (پھر) نیک عمل کیے تو ان کے لئے ایسا اجر ہے جو کبھی ختم نہ ہوگا. ');
INSERT INTO "junagarri" VALUES(95,7,'95:7','پس تجھے اب روز جزا کے جھٹلانے پر کون سی چیز آماده کرتی ہے. ');
INSERT INTO "junagarri" VALUES(95,8,'95:8','کیا اللہ تعالیٰ (سب) حاکموں کا حاکم نہیں ہے. ');
INSERT INTO "junagarri" VALUES(96,1,'96:1','پڑھ اپنے رب کے نام سے جس نے پیدا کیا. ');
INSERT INTO "junagarri" VALUES(96,2,'96:2','جس نے انسان کو خون کے لوتھڑے سے پیدا کیا. ');
INSERT INTO "junagarri" VALUES(96,3,'96:3','تو پڑھتا ره تیرا رب بڑے کرم والا ہے. ');
INSERT INTO "junagarri" VALUES(96,4,'96:4','جس نے قلم کے ذریعے (علم) سکھایا. ');
INSERT INTO "junagarri" VALUES(96,5,'96:5','جس نے انسان کو وه سکھایا جسے وه نہیں جانتا تھا.');
INSERT INTO "junagarri" VALUES(96,6,'96:6','سچ مچ انسان تو آپے سے باہر ہو جاتا ہے.');
INSERT INTO "junagarri" VALUES(96,7,'96:7','اس لئے کہ وه اپنے آپ کو بے پروا (یا تونگر) سمجھتا ہے.');
INSERT INTO "junagarri" VALUES(96,8,'96:8','یقیناً لوٹنا تیرے رب کی طرف ہے.');
INSERT INTO "junagarri" VALUES(96,9,'96:9','(بھلا) اسے بھی تو نے دیکھا جو بندے کو روکتا ہے.');
INSERT INTO "junagarri" VALUES(96,10,'96:10','جبکہ وه بنده نماز ادا کرتا ہے. ');
INSERT INTO "junagarri" VALUES(96,11,'96:11','بھلا بتلا تو اگر وه ہدایت پر ہو. ');
INSERT INTO "junagarri" VALUES(96,12,'96:12','یا پرہیز گاری کا حکم دیتا ہو. ');
INSERT INTO "junagarri" VALUES(96,13,'96:13','بھلا دیکھو تو اگر یہ جھٹلاتا ہو اور منھ پھیرتا ہو تو. ');
INSERT INTO "junagarri" VALUES(96,14,'96:14','کیا اس نے نہیں جانا کہ اللہ تعالیٰ اسے خوب دیکھ رہا ہے. ');
INSERT INTO "junagarri" VALUES(96,15,'96:15','یقیناً اگر یہ باز نہ رہا تو ہم اس کی پیشانی کے بال پکڑ کر گھسیٹیں گے. ');
INSERT INTO "junagarri" VALUES(96,16,'96:16','ایسی پیشانی جو جھوٹی خطا کار ہے. ');
INSERT INTO "junagarri" VALUES(96,17,'96:17','یہ اپنی مجلس والوں کو بلالے.');
INSERT INTO "junagarri" VALUES(96,18,'96:18','ہم بھی (دوزخ کے) پیادوں کو بلالیں گے. ');
INSERT INTO "junagarri" VALUES(96,19,'96:19','خبردار! اس کا کہنا ہرگز نہ ماننا اور سجده کر اور قریب ہو جا.*');
INSERT INTO "junagarri" VALUES(97,1,'97:1','یقیناً ہم نے اسے شب قدر میں نازل فرمایا. ');
INSERT INTO "junagarri" VALUES(97,2,'97:2','تو کیا سمجھا کہ شب قدر کیا ہے؟ ');
INSERT INTO "junagarri" VALUES(97,3,'97:3','شب قدر ایک ہزار مہینوں سے بہتر ہے. ');
INSERT INTO "junagarri" VALUES(97,4,'97:4','اس (میں ہر کام) کے سر انجام دینے کو اپنے رب کے حکم سے فرشتے اور روح (جبرائیل) اترتے ہیں. ');
INSERT INTO "junagarri" VALUES(97,5,'97:5','یہ رات سراسر سلامتی کی ہوتی ہے اور فجر کے طلوع ہونے تک (رہتی ہے).');
INSERT INTO "junagarri" VALUES(98,1,'98:1','اہل کتاب کے کافر اور مشرک لوگ جب تک کہ ان کے پاس ظاہر دلیل نہ آجائے باز رہنے والے نہ تھے (وه دلیل یہ تھی کہ).');
INSERT INTO "junagarri" VALUES(98,2,'98:2','اللہ تعالیٰ کا ایک رسول جو پاک صحیفے پڑھے. ');
INSERT INTO "junagarri" VALUES(98,3,'98:3','جن میں صحیح اور درست احکام ہوں. ');
INSERT INTO "junagarri" VALUES(98,4,'98:4','اہل کتاب اپنے پاس ظاہر دلیل آجانے کے بعد ہی (اختلاف میں پڑ کر) متفرق ہوگئے. ');
INSERT INTO "junagarri" VALUES(98,5,'98:5','انہیں اس کے سوا کوئی حکم نہیں دیا گیا کہ صرف اللہ کی عبادت کریں اسی کے لئے دین کو خالص رکھیں۔ ابراہیم حنیف کے دین پر اور نماز کو قائم رکھیں اور زکوٰة دیتے رہیں یہی ہے دین سیدھی ملت کا. ');
INSERT INTO "junagarri" VALUES(98,6,'98:6','بیشک جو لوگ اہل کتاب میں کافر ہوئے اور مشرکین سب دوزخ کی آگ میں (جائیں گے) جہاں وه ہمیشہ (ہمیشہ) رہیں گے۔ یہ لوگ بدترین خلائق ہیں. ');
INSERT INTO "junagarri" VALUES(98,7,'98:7','بیشک جو لوگ ایمان لائےاور نیک عمل کیے یہ لوگ بہترین خلائق ہیں. ');
INSERT INTO "junagarri" VALUES(98,8,'98:8','ان کا بدلہ ان کے رب کے پاس ہمیشگی والی جنتیں ہیں جن کے نیچے نہریں بہہ رہی ہیں جن میں وه ہمیشہ ہمیشہ رہیں گے۔ اللہ تعالیٰ ان سے راضی ہوا اور یہ اس سے راضی ہوئے ۔ یہ ہے اس کے لئے جو اپنے پروردگار سے ڈرے. ');
INSERT INTO "junagarri" VALUES(99,1,'99:1','جب زمین پوری طرح جھنجھوڑ دی جائے گی. ');
INSERT INTO "junagarri" VALUES(99,2,'99:2','اور اپنے بوجھ باہر نکال پھینکے گی. ');
INSERT INTO "junagarri" VALUES(99,3,'99:3','انسان کہنے لگے گا کہ اسے کیا ہوگیا؟ ');
INSERT INTO "junagarri" VALUES(99,4,'99:4','اس دن زمین اپنی سب خبریں بیان کردے گی. ');
INSERT INTO "junagarri" VALUES(99,5,'99:5','اس لئے کہ تیرے رب نے اسے حکم دیا ہوگا. ');
INSERT INTO "junagarri" VALUES(99,6,'99:6','اس روز لوگ مختلف جماعتیں ہو کر (واپس) لوٹیں گے تاکہ انہیں ان کے اعمال دکھا دیئے جائیں. ');
INSERT INTO "junagarri" VALUES(99,7,'99:7','پس جس نے ذره برابر نیکی کی ہوگی وه اسے دیکھ لے گا. ');
INSERT INTO "junagarri" VALUES(99,8,'99:8','اور جس نے ذره برابر برائی کی ہوگی وه اسے دیکھ لے گا. ');
INSERT INTO "junagarri" VALUES(100,1,'100:1','ہانپتے ہوئے دوڑنے والے گھوڑوں کی قسم! ');
INSERT INTO "junagarri" VALUES(100,2,'100:2','پھر ٹاپ مار کر آگ جھاڑنے والوں کی قسم! ');
INSERT INTO "junagarri" VALUES(100,3,'100:3','پھر صبح کے وقت دھاوا بولنے والوں کی قسم. ');
INSERT INTO "junagarri" VALUES(100,4,'100:4','پس اس وقت گرد وغبار اڑاتے ہیں ');
INSERT INTO "junagarri" VALUES(100,5,'100:5','پھر اسی کے ساتھ فوجیوں کے درمیان گھس جاتے ہیں. ');
INSERT INTO "junagarri" VALUES(100,6,'100:6','یقیناً انسان اپنے رب کا بڑا ناشکرا ہے. ');
INSERT INTO "junagarri" VALUES(100,7,'100:7','اور یقیناً وه خود بھی اس پر گواه ہے. ');
INSERT INTO "junagarri" VALUES(100,8,'100:8','یہ مال کی محبت میں بھی بڑا سخت ہے. ');
INSERT INTO "junagarri" VALUES(100,9,'100:9','کیا اسے وه وقت معلوم نہیں جب قبروں میں جو (کچھ) ہے نکال لیا جائے گا. ');
INSERT INTO "junagarri" VALUES(100,10,'100:10','اور سینوں کی پوشیده باتیں ظاہر کر دی جائیں گی. ');
INSERT INTO "junagarri" VALUES(100,11,'100:11','بیشک ان کا رب اس دن ان کے حال سے پورا باخبر ہوگا. ');
INSERT INTO "junagarri" VALUES(101,1,'101:1','کھڑکھڑا دینے والی.*');
INSERT INTO "junagarri" VALUES(101,2,'101:2','کیا ہے وه کھڑکھڑا دینے والی.');
INSERT INTO "junagarri" VALUES(101,3,'101:3','تجھے کیا معلوم کہ وه کھڑ کھڑا دینے والی کیا ہے.');
INSERT INTO "junagarri" VALUES(101,4,'101:4','جس دن انسان بکھرے ہوئے پروانوں کی طرح ہو جائیں گے. ');
INSERT INTO "junagarri" VALUES(101,5,'101:5','اور پہاڑ دھنے ہوئے رنگین اون کی طرح ہو جائیں گے. ');
INSERT INTO "junagarri" VALUES(101,6,'101:6','پھر جس کے پلڑے بھاری ہوں گے. ');
INSERT INTO "junagarri" VALUES(101,7,'101:7','وه تو دل پسند آرام کی زندگی میں ہوگا. ');
INSERT INTO "junagarri" VALUES(101,8,'101:8','اور جس کے پلڑے ہلکے ہوں گے. ');
INSERT INTO "junagarri" VALUES(101,9,'101:9','اس کا ٹھکانا ہاویہ ہے. ');
INSERT INTO "junagarri" VALUES(101,10,'101:10','تجھے کیا معلوم کہ وه کیا ہے. ');
INSERT INTO "junagarri" VALUES(101,11,'101:11','وه تند وتیز آگ (ہے). ');
INSERT INTO "junagarri" VALUES(102,1,'102:1','زیادتی کی چاہت نے تمہیں غافل کردیا. ');
INSERT INTO "junagarri" VALUES(102,2,'102:2','یہاں تک کہ تم قبرستان جا پہنچے. ');
INSERT INTO "junagarri" VALUES(102,3,'102:3','ہرگز نہیں تم عنقریب معلوم کر لو گے.');
INSERT INTO "junagarri" VALUES(102,4,'102:4','ہرگز نہیں پھر تمہیں جلد علم ہو جائے گا. ');
INSERT INTO "junagarri" VALUES(102,5,'102:5','ہرگز نہیں اگر تم یقینی طور پر جان لو. ');
INSERT INTO "junagarri" VALUES(102,6,'102:6','تو بیشک تم جہنم دیکھ لو گے. ');
INSERT INTO "junagarri" VALUES(102,7,'102:7','اور تم اسے یقین کی آنکھ سے دیکھ لو گے. ');
INSERT INTO "junagarri" VALUES(102,8,'102:8','پھر اس دن تم سے ضرور بالضرور نعمتوں کا سوال ہوگا. ');
INSERT INTO "junagarri" VALUES(103,1,'103:1','زمانے کی قسم. ');
INSERT INTO "junagarri" VALUES(103,2,'103:2','بیشک (بالیقین) انسان سرتا سر نقصان میں ہے. ');
INSERT INTO "junagarri" VALUES(103,3,'103:3','سوائے ان لوگوں کے جو ایمان لائے اور نیک عمل کیے اور (جنہوں نے) آپس میں حق کی وصیت کی اور ایک دوسرے کو صبر کی نصیحت کی. ');
INSERT INTO "junagarri" VALUES(104,1,'104:1','بڑی خرابی ہے ہر ایسے شخص کی جو عیب ٹٹولنے والا غیبت کرنے والا ہو. ');
INSERT INTO "junagarri" VALUES(104,2,'104:2','جو مال کو جمع کرتا جائے اور گنتا جائے. ');
INSERT INTO "junagarri" VALUES(104,3,'104:3','وه سمجھتا ہے کہ اس کا مال اس کے پاس سدا رہے گا. ');
INSERT INTO "junagarri" VALUES(104,4,'104:4','ہرگز نہیں یہ تو ضرور توڑ پھوڑ دینے والی آگ میں پھینک دیا جائے گا. ');
INSERT INTO "junagarri" VALUES(104,5,'104:5','اور تجھے کیا معلوم کہ ایسی آگ کیا ہوگی؟ ');
INSERT INTO "junagarri" VALUES(104,6,'104:6','وه اللہ تعالیٰ کی سلگائی ہوئی آگ ہوگی.');
INSERT INTO "junagarri" VALUES(104,7,'104:7','جو دلوں پر چڑھتی چلی جائے گی. ');
INSERT INTO "junagarri" VALUES(104,8,'104:8','وہ ان پر ہر طرف سے بند کی ہوئی ہوگی. ');
INSERT INTO "junagarri" VALUES(104,9,'104:9','بڑے بڑے ستونوں میں.');
INSERT INTO "junagarri" VALUES(105,1,'105:1','کیا تو نے نہ دیکھا کہ تیرے رب نے ہاتھی والوں کے ساتھ کیا کیا؟ ');
INSERT INTO "junagarri" VALUES(105,2,'105:2','کیا ان کے مکر کو بےکار نہیں کردیا؟ ');
INSERT INTO "junagarri" VALUES(105,3,'105:3','اور ان پر پرندوں کے جھنڈ کے جھنڈ بھیج دیئے. ');
INSERT INTO "junagarri" VALUES(105,4,'105:4','جو انہیں مٹی اور پتھر کی کنکریاں مار رہے تھے. ');
INSERT INTO "junagarri" VALUES(105,5,'105:5','پس انہیں کھائے ہوئے بھوسے کی طرح کردیا. ');
INSERT INTO "junagarri" VALUES(106,1,'106:1','قریش کے مانوس کرنے کے لئے.');
INSERT INTO "junagarri" VALUES(106,2,'106:2','(یعنی) انہیں جاڑے اور گرمی کے سفر سے مانوس کرنے کے لئے ۔ (اس کے شکریہ میں).');
INSERT INTO "junagarri" VALUES(106,3,'106:3','پس انہیں چاہئے کہ اسی گھر کے رب کی عبادت کرتے رہیں.');
INSERT INTO "junagarri" VALUES(106,4,'106:4','جس نے انہیں بھوک میں کھانا دیا اور ڈر (اور خوف) میں امن (وامان) دیا. ');
INSERT INTO "junagarri" VALUES(107,1,'107:1','کیا تو نے (اسے بھی) دیکھا جو (روز) جزا کو جھٹلاتا ہے؟ ');
INSERT INTO "junagarri" VALUES(107,2,'107:2','یہی وه ہے جو یتیم کو دھکے دیتا ہے. ');
INSERT INTO "junagarri" VALUES(107,3,'107:3','اور مسکین کو کھلانے کی ترغیب نہیں دیتا. ');
INSERT INTO "junagarri" VALUES(107,4,'107:4','ان نمازیوں کے لئے افسوس (اور ویل نامی جہنم کی جگہ) ہے.');
INSERT INTO "junagarri" VALUES(107,5,'107:5','جو اپنی نماز سے غافل ہیں. ');
INSERT INTO "junagarri" VALUES(107,6,'107:6','جو ریاکاری کرتے ہیں. ');
INSERT INTO "junagarri" VALUES(107,7,'107:7','اور برتنے کی چیز روکتے ہیں. ');
INSERT INTO "junagarri" VALUES(108,1,'108:1','یقیناً ہم نے تجھے (حوض) کوثر (اور بہت کچھ) دیا ہے. ');
INSERT INTO "junagarri" VALUES(108,2,'108:2','پس تو اپنے رب کے لئے نماز پڑھ اور قربانی کر. ');
INSERT INTO "junagarri" VALUES(108,3,'108:3','یقیناً تیرا دشمن ہی لاوارث اور بے نام ونشان ہے. ');
INSERT INTO "junagarri" VALUES(109,1,'109:1','آپ کہہ دیجئے کہ اے کافرو! ');
INSERT INTO "junagarri" VALUES(109,2,'109:2','نہ میں عبادت کرتا ہوں اس کی جس کی تم عبادت کرتے ہو.');
INSERT INTO "junagarri" VALUES(109,3,'109:3','نہ تم عبادت کرنے والے ہو اس کی جس کی میں عبادت کرتا ہوں.');
INSERT INTO "junagarri" VALUES(109,4,'109:4','اور نہ میں عبادت کروں گا جس کی تم عبادت کرتے ہو.');
INSERT INTO "junagarri" VALUES(109,5,'109:5','اور نہ تم اس کی عبادت کرنے والے ہو جس کی میں عبادت کر رہا ہوں. ');
INSERT INTO "junagarri" VALUES(109,6,'109:6','تمہارے لئے تمہارا دین ہے اور میرے لئے میرا دین ہے. ');
INSERT INTO "junagarri" VALUES(110,1,'110:1','جب اللہ کی مدد اور فتح آجائے.*');
INSERT INTO "junagarri" VALUES(110,2,'110:2','اور تو لوگوں کو اللہ کے دین میں جوق در جوق آتا دیکھ لے. ');
INSERT INTO "junagarri" VALUES(110,3,'110:3','تو اپنے رب کی تسبیح کرنے لگ حمد کے ساتھ اور اس سے مغفرت کی دعا مانگ، بیشک وه بڑا ہی توبہ قبول کرنے والا ہے. ');
INSERT INTO "junagarri" VALUES(111,1,'111:1','ابولہب کے دونوں ہاتھ ٹوٹ گئے اور وه (خود) ہلاک ہو گیا. ');
INSERT INTO "junagarri" VALUES(111,2,'111:2','نہ تو اس کا مال اس کے کام آیا اور نہ اس کی کمائی. ');
INSERT INTO "junagarri" VALUES(111,3,'111:3','وه عنقریب بھڑکنے والی آگ میں جائے گا.');
INSERT INTO "junagarri" VALUES(111,4,'111:4','اور اس کی بیوی بھی (جائے گی،) جو لکڑیاں ڈھونے والی ہے. ');
INSERT INTO "junagarri" VALUES(111,5,'111:5','اس کی گردن میں پوست کھجور کی بٹی ہوئی رسی ہوگی. ');
INSERT INTO "junagarri" VALUES(112,1,'112:1','آپ کہہ دیجئے کہ وه اللہ تعالیٰ ایک (ہی) ہے.');
INSERT INTO "junagarri" VALUES(112,2,'112:2','اللہ تعالیٰ بے نیاز ہے. ');
INSERT INTO "junagarri" VALUES(112,3,'112:3','نہ اس سے کوئی پیدا ہوا نہ وه کسی سے پیدا ہوا. ');
INSERT INTO "junagarri" VALUES(112,4,'112:4','اور نہ کوئی اس کا ہمسر ہے. ');
INSERT INTO "junagarri" VALUES(113,1,'113:1','آپ کہہ دیجئے! کہ میں صبح کے رب کی پناه میں آتا ہوں. ');
INSERT INTO "junagarri" VALUES(113,2,'113:2','ہر اس چیز کے شر سے جو اس نے پیدا کی ہے. ');
INSERT INTO "junagarri" VALUES(113,3,'113:3','اور اندھیری رات کی تاریکی کے شر سے جب اس کا اندھیرا پھیل جائے. ');
INSERT INTO "junagarri" VALUES(113,4,'113:4','اور گره (لگا کر ان) میں پھونکنے والیوں کے شر سے (بھی). ');
INSERT INTO "junagarri" VALUES(113,5,'113:5','اور حسد کرنے والے کی برائی سے بھی جب وه حسد کرے. ');
INSERT INTO "junagarri" VALUES(114,1,'114:1','آپ کہہ دیجئے! کہ میں لوگوں کے پروردگار کی پناه میں آتا ہوں. ');
INSERT INTO "junagarri" VALUES(114,2,'114:2','لوگوں کے مالک کی (اور).');
INSERT INTO "junagarri" VALUES(114,3,'114:3','لوگوں کے معبود کی (پناه میں). ');
INSERT INTO "junagarri" VALUES(114,4,'114:4','وسوسہ ڈالنے والے پیچھے ہٹ جانے والے کے شر سے. ');
INSERT INTO "junagarri" VALUES(114,5,'114:5','جو لوگوں کے سینوں میں وسوسہ ڈالتا ہے.');
INSERT INTO "junagarri" VALUES(114,6,'114:6','(خواه) وه جن میں سے ہو یا انسان میں سے. ');
