INSERT INTO "kannada" VALUES(1,1,'1:1','ಪರಮ ದಯಾಮಯನೂ ಕೃಪಾನಿಧಿಯೂ ಆದ ಅಲ್ಲಾಹನ ನಾಮದೊಂದಿಗೆ ,');
INSERT INTO "kannada" VALUES(1,2,'1:2','ಸಕಲ ಲೋಕ ಪರಿಪಾಲಕನಾದ ಅಲ್ಲಾಹನಿಗೇ ಸರ್ವಸ್ತುತಿ .');
INSERT INTO "kannada" VALUES(1,3,'1:3','ಸರ್ವದಯಾಳು, ವಿಶೇಷ ಕೃಪಾನಿಧಿ.');
INSERT INTO "kannada" VALUES(1,4,'1:4','ಪ್ರತಿಫಲದ ದಿನದ ಅಧಿಪತಿ.');
INSERT INTO "kannada" VALUES(1,5,'1:5','ನಿನ್ನನ್ನೇ ನಾವು ಆರಾಧಿಸುತ್ತೇವೆ , ನಿನ್ನಲ್ಲೇ ನಾವು ಸಹಾಯ ಬೇಡುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(1,6,'1:6','ನೇರವಾದ ದಾರಿಯಲ್ಲಿ ನಮ್ಮನ್ನು ನೆಲೆನಿಲ್ಲಿಸು .');
INSERT INTO "kannada" VALUES(1,7,'1:7','ನೀನು ಅನುಗ್ರಹಿಸಿದವರ ಮಾರ್ಗದಲ್ಲಿ, ಕ್ರೋಧಕ್ಕೊಳಗಾದವರ ಮಾರ್ಗದಲಿ ್ಲ ಅಲ್ಲ; ದಾರಿ ತಪ್ಪಿದವರದ್ದೂ ಅಲ್ಲ. (ಆಮೀನ್) ');
INSERT INTO "kannada" VALUES(2,1,'2:1','ಅಲಿಫ್ ಲಾಮ್ ಮೀಮ್ ');
INSERT INTO "kannada" VALUES(2,2,'2:2','ಆ ಗ್ರಂಥ . ಅದರಲ್ಲಿ ಸಂಶಯವೇ ಇಲ್ಲ. ಭಕ್ತರಿಗೆ ಪಥದರ್ಶಕ.');
INSERT INTO "kannada" VALUES(2,3,'2:3','ಪರೋಕ್ಷದಲ್ಲಿ ನಂಬುವ, ನಮಾಝನ್ನು ನೆಲೆಗೊಳಿಸುವ ಮತ್ತು ಅವರಿಗೆ ನಾವು ನೀಡಿದುದರಿಂದ ಖರ್ಚು ಮಾಡುವ (ಭಕ್ತರಿಗೆ).');
INSERT INTO "kannada" VALUES(2,4,'2:4','ನಿಮಗೆ ಅವತೀರ್ಣಗೊಂಡದ್ದರಲ್ಲೂ ನಿಮಗಿಂತ ಮುಂಚೆ ಅವತೀರ್ಣಗೊಂಡದ್ದರಲ್ಲೂ ವಿಶ್ವಾಸವುಳ್ಳ ಭಕ್ತರವರು. ಪರಲೋಕದ ಕುರಿತು ಧೃಢ ವ್ರತರಾದವರು.');
INSERT INTO "kannada" VALUES(2,5,'2:5','ಅಂಥವರು ತಮ್ಮ ಪ್ರಭುವಿನಿಂದ ಸತ್ಪಥದರ್ಶಿತರು. ಆ ಭಕ್ತರೇ ಜಯಶೀಲರು.');
INSERT INTO "kannada" VALUES(2,6,'2:6','ನೀವು ಮುನ್ನೆಚ್ಚರಿಕೆ ಕೊಟ್ಟರೂ ಕೊಡದಿದ್ದರೂ ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಅದು ಸಮಾನ. ಅವರು ಖಂಡಿತ ವಿಶ್ವಾಸವಿಡಲಾರರು.');
INSERT INTO "kannada" VALUES(2,7,'2:7','ಅವರ ಹೃದಯಗಳ ಮೇಲೆ ಮತ್ತು ಅವರ ಶ್ರವಣಗಳ ಮೇಲೆ ಅಲ್ಲಾಹನು ಮುದ್ರೆಯೊತ್ತಿರು ವನು . ಅವರ ಕಣ್ಣುಗಳ ಮೇಲೆ ಪೊರೆ ಬಾಧಿಸಿದೆ. ಅವರಿಗೆ ಘೋರ ಶಿಕ್ಷೆ ಕಾದಿದೆ.');
INSERT INTO "kannada" VALUES(2,8,'2:8','‘ನಾವು ಅಲ್ಲಾಹ್ ಹಾಗೂ ಪಾರತ್ರಿಕ ದಿನವನ್ನು ನಂಬಿದ್ದೇವೆ’ ಎನ್ನುವ ಕೆಲವರಿದ್ದಾರೆ. ಅವರು ವಿಶ್ವಾಸಿಗಳಲ್ಲ .');
INSERT INTO "kannada" VALUES(2,9,'2:9','ಅವರು, ಅಲ್ಲಾಹು ಮತ್ತು ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನು ವಂಚಿಸುತ್ತಾರೆ. ಸತ್ಯದಲ್ಲಿ ತಮ್ಮ ಶರೀರಗಳನ್ನೇ ಹೊರತು ಅವರು ವಂಚಿಸುವುದಿಲ್ಲ. ಮತ್ತು ಅವರದನ್ನು ತಿಳಿಯುತ್ತಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,10,'2:10','ಅವರ ಹೃದಯಗಳಲ್ಲಿ ರೋಗವಿದೆ. ನಂತರ ಅಲ್ಲಾಹನು ಆ ರೋಗವನ್ನು ಉಲ್ಬಣಗೊಳಿಸಿದನು . ಅವರು ಸುಳ್ಳು ಹೇಳುವವರಾಗಿದ್ದರಿಂದ ಅವರಿಗೆ ಯಾತನಾಮಯ ಶಿಕ್ಷೆ ಕಾದಿದೆ.');
INSERT INTO "kannada" VALUES(2,11,'2:11','ಅವರಿಗೆ `ಭೂಮಿಯಲ್ಲಿ ನೀವು ಗೊಂದಲವೆಬ್ಬಿಸ ಬೇಡಿ’ ಎಂದು ಉಪದೇಶ ನೀಡಿದರೆ ಅವರು ‘ನಾವು ಸುಧಾರಕರು ಮಾತ್ರ, ಎನ್ನುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(2,12,'2:12','ತಿಳಿಯಿರಿ, ಅವರೇ ನಿಜದಲ್ಲಿ ಗೊಂದಲಕಾರರು. ಆದರೆ ಆ ಪ್ರಜ್ಞೆ ಅವರಿಗಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,13,'2:13','ಜನರು ಸತ್ಯವಿಶ್ವಾಸ ತಾಳಿದಂತೆ ನೀವೂ ಸತ್ಯ ವಿಶ್ವಾಸ ತಾಳಿರಿ’ ಎಂದು ಅವರಿಗೆ ಹೇಳಲಾದರೆ `ಮೂರ್ಖರು ನಂಬಿದಂತೆ ನಾವು ನಂಬಬೇಕೆ?’ ಎನ್ನುತ್ತಾರೆ. ತಿಳಿಯಿರಿ, ವಾಸ್ತವದಲ್ಲಿ ಅವರೇ ಮೂರ್ಖರು. ಆದರೆ ಅವರಿಗದು ಗೊತ್ತಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,14,'2:14','ವಿಶ್ವಾಸಿಗಳನ್ನು ಅವರು ಕಂಡರೆ, ‘ನಾವು ನಂಬಿದ್ದೇವೆ’ ಎನ್ನುತ್ತಾರೆ. ಅವರ ಪಿಶಾಚಿಗಳ ಬಳಿ ಪ್ರತ್ಯೇಕವಾಗಿರುವ ಸಂದರ್ಭದಲ್ಲಿ; ‘ನಾವು ನಿಮ್ಮ ಜೊತೆಯೇ ಇದ್ದೇವೆ, ಅವರನ್ನು ನಾವು ಕೇವಲ ಪರಿಹಾಸ ಮಾಡುತ್ತಿದ್ದೇವೆ.’ ಎನ್ನುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(2,15,'2:15','ನಿಜದಲ್ಲಿ ಅಲ್ಲಾಹನು ಅವರ ಪರಿಹಾಸ್ಯಕ್ಕೆ ಸಜೆ ನೀಡುವನು . ತಮ್ಮ ದುರಾಚಾರದಲ್ಲಿ ಅಂಡಲೆಯುವಂತೆ ಅಲ್ಲಾಹನು ಅವರನ್ನು ಸಡಿಲು ಬಿಡುವನು.');
INSERT INTO "kannada" VALUES(2,16,'2:16','ಅವರು ಸನ್ಮಾರ್ಗದ ಬದಲಿಗೆ ದುರ್ಮಾರ್ಗವನ್ನು ಖರೀದಿಸಿಕೊಂಡವರು. ಆದ್ದರಿಂದ ಅವರ ವ್ಯವಹಾರ ಲಾಭಕರವಾಗಿಲ್ಲ. ಅವರು ಸನ್ಮಾರ್ಗ ಪ್ರಾಪ್ತರೂ ಆಗಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,17,'2:17','ಅವರ ಉದಾಹರಣೆ ಕಿಚ್ಚು ಹಚ್ಚಿದವನ ಉದಾ ಹರಣೆಯಂತಿದೆ. ತನ್ನ ಸುತ್ತಮುತ್ತಲಿನ ಕಿಚ್ಚು ಬೆಳಗಿದಾಗ ಅಲ್ಲಾಹನು ಅವರ ಬೆಳಕನ್ನು ಕೊಂಡು ಹೋದನು. ಅವರು ದೃಷ್ಟಿ ಕಳಕೊಂಡು ಕಾರಿರುಳಲ್ಲಿ ದಾರಿ ಕಾಣದವರಂತೆ ಅವರನ್ನು ಬಿಟ್ಟುಬಿಟ್ಟನು ');
INSERT INTO "kannada" VALUES(2,18,'2:18','ಕಿವುಡರು, ಮೂಗರು, ಕುರುಡರು ಅವರು. ಆದ್ದರಿಂದ ಅವರು ಮರಳಿ ಬರಲಾರರು.');
INSERT INTO "kannada" VALUES(2,19,'2:19','ಅಥವಾ ಅವರ ಉಪಮೆ, ಆಕಾಶದಿಂದ ಸುರಿಯುವ ಜಡಿಮಳೆಯಂತಿದೆ. ಅದರಲ್ಲಿ ದಟ್ಟ ಕಾರಿರುಳು, ಗುಡುಗು ಮಿಂಚುಗಳೂ ಇವೆ. ಸಿಡಿಲೆರಗಿಸುವ ಮೃತ್ಯು ಭಯದಿಂದ ಅವರು ತಮ್ಮ ಕಿವಿಗಳಲ್ಲಿ ತಮ್ಮ ಬೆರಳುಗಳನ್ನು ತೂರಿಸಿಕೊಳ್ಳುತ್ತಾರೆ. ಅಲ್ಲಾಹನು ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು (ಜ್ಞಾನ ಮತ್ತು ಸಾಮಥ್ರ್ಯಗಳಿಂದ) ಆವರಿಸಿಕೊಂಡಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(2,20,'2:20','ಮಿಂಚು ಅವರ ಕಣ್ಣುಗಳನ್ನು ಕೋರೈಸುವಂತಿದೆ. ಮಿಂಚು ಪಳ್ಳನೆ ಬೆಳಗಿದಾಗ ಆ ಬೆಳಕಿನಲ್ಲಿ ಅವರು ನಡೆಯುತ್ತಾರೆ. ಮಿಂಚು ನಿಂತು ಇರುಳಾವರಿಸಿದಾಗ ನಿಂತು ಬಿಡುತ್ತಾರೆ. ಅಲ್ಲಾಹನು ಇಚ್ಛಿಸುತ್ತಿದ್ದರೆ ಅವರ ದೃಕ್‍ಶ್ರವಣಗಳನ್ನು ಹಿಂತೆಗೆದು ಕೊಳ್ಳುತ್ತಿದ್ದನು. ಖಂಡಿತಾ ಅವನು ಎಲ್ಲ ವಸ್ತುವಿನ ಮೇಲೆ ಸರ್ವ ಸಮರ್ಥನು .');
INSERT INTO "kannada" VALUES(2,21,'2:21','ಮನುಷ್ಯರೇ, ನಿಮ್ಮನ್ನು ಹಾಗೂ ನಿಮ್ಮ ಪೂರ್ವಜರನ್ನು ಸೃಷ್ಟಿಸಿದ ನಿಮ್ಮ ಪ್ರಭುವಿಗೆ ಆರಾಧಿಸಿರಿ . ನೀವು ಜಾಗೃತಿಯುಳ್ಳವರಾಗುವಿರಿ.');
INSERT INTO "kannada" VALUES(2,22,'2:22','ನಿಮಗೆ ಇಳೆಯನ್ನು ಹಾಸಿಗೆ , ನಭವನ್ನು ಛಾವಣಿ ಮಾಡಿಕೊಟ್ಟವನು; ಅವನು ನಭದಿಂದ ಮಳೆಯನ್ನು ಇಳಿಸಿದನು. ಆ ಮೂಲಕ ನಿಮಗೆ ಉಪಭೋಗಕ್ಕೆ ಫಲಗಳನ್ನು ಹೊರಡಿಸಿದನು. ನೀವು ಇದೆಲ್ಲ ತಿಳಿದವರಾಗಿರುತ್ತ ಅವನಿಗೆ ಸಮಾನರನ್ನು ಕಲ್ಪಿಸದಿರಿ .');
INSERT INTO "kannada" VALUES(2,23,'2:23','ನಮ್ಮ ದಾಸನಿಗೆ ನಾವಿಳಿಸಿದ ಈ ಗ್ರಂಥದ ಬಗ್ಗೆ ನೀವು ಶಂಕಿತರಾಗಿದ್ದರೆ , ತತ್ಸಮಾನ ಅಧ್ಯಾಯವೊಂದನ್ನು ರಚಿಸಿ ತನ್ನಿರಿ. ನೀವು ಸತ್ಯವಂತರಾಗಿದ್ದರೆ, ಅಲ್ಲಾಹನ ಹೊರತು ನಿಮ್ಮ ಸಾಕ್ಷಿದಾರರನ್ನೂ ನೆರವಿಗೆ ಕರೆದುಕೊಳ್ಳಿರಿ .');
INSERT INTO "kannada" VALUES(2,24,'2:24','ನೀವು ಅದನ್ನು ಮಾಡದಿದ್ದರೆ, ಖಂಡಿತ ನೀವು ಮಾಡಲಾರಿರಿ . ಮನುಷ್ಯರನ್ನೂ ಕಲ್ಲುಗಳನ್ನೂ ಇಂಧನವಾಗಿ ಉರಿಸಲ್ಪಡುವ ನರಕದ ಬಗ್ಗೆ ಜಾಗ್ರತೆಯಿರಲಿ! ಅದನ್ನು ನಿಷೇಧಿಗಳಿಗೆ ಕಾದಿರಿಸಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(2,25,'2:25','ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿ, ಸತ್ಕರ್ಮ ಕೈಗೊಂಡವರಿಗೆ, ತಳಭಾಗದಲ್ಲಿ ನದಿಗಳು ಹರಿಯುತ್ತಿರುವ ಉದ್ಯಾನ ಗಳ ಸುವಾರ್ತೆಯನ್ನು ನೀಡಿರಿ. ಅವರಿಗಲ್ಲಿ ಆಹಾರವಾಗಿ ಹಣ್ಣುಗಳು ದೊರೆತಾಗ, ಈ ಮೊದಲು ಸಿಕ್ಕಿದ ಹಣ್ಣು ಇದೇ ತಾನೇ? ಎನ್ನುವರು . ಅವರಿಗೆ ಸಾದೃಶ್ಯವಾದ ಹಣ್ಣುಗಳನ್ನು ನೀಡಲಾ ಗುವುದು. ಅಲ್ಲಿ ಸಂಶುದ್ಧೆಯರಾದ ನಲ್ಲೆಯರೂ ಅವರಿಗಿರುವರು. ಅವರಲ್ಲಿ ನಿತ್ಯನಿವಾಸಿಗಳು.');
INSERT INTO "kannada" VALUES(2,26,'2:26','ತೃಣ ಸೊಳ್ಳೆಯ ಯಾ ಅದಕ್ಕಿಂತ ಮೇಲಿನ ವಸ್ತುಗಳ ಉಪಮೆ ನೀಡಲು ಅಲ್ಲಾಹನಿಗೆ ಸಂಕೋಚವಿಲ್ಲ, ಅದು ಪ್ರಭುವಿನ ಕಡೆಯ ಸತ್ಯವೆಂಬುದನ್ನು ಸತ್ಯನಂಬಿಗರು ಬಲ್ಲರು. ಇಂತಹ ಉಪಮೆಗಳಿಂದ ಅಲ್ಲಾಹನ ಉದ್ದೇಶವಾದರೂ ಏನು ಎಂದು ನಿಷೇಧಿಗಳು ಕುಚೋದ್ಯ ಮಾಡುತ್ತಾರೆ . ಇಂಥ ಉಪಮೆಗಳಿಂದ ಅಲ್ಲಾಹನು ಅನೇಕರಿಗೆ ಸತ್ಯದರ್ಶನವನ್ನು ಮಾಡುತ್ತಾನೆ. ಅನೇಕರಿಗೆ ಪಥ ವಿಮುಖತೆಯನ್ನು ಕೊಡುತ್ತಾನೆ. ಈ ಮೂಲಕ ಅಲ್ಲಾಹನು ಪಥಭ್ರಷ್ಟಗೊಳಿಸುವುದಿಲ್ಲ. ಅಧರ್ಮಿಗಳನ್ನು ಹೊರತು .');
INSERT INTO "kannada" VALUES(2,27,'2:27','ಆ ಅಧರ್ಮಿಗಳು ಯಾರೆಂದರೆ ಅಲ್ಲಾಹನ ಒಪ್ಪಂದವನ್ನು ದೃಢಪಡಿಸಿದ ಬಳಿಕ ಅದನ್ನು ಭಂಜಿಸುವ, ಅಲ್ಲಾಹನು ಬೆಸೆಯುವಂತೆ ಆಜ್ಞಾಪಿ ಸಿದ್ದನ್ನು ಬೇರ್ಪಡಿಸುವ ಹಾಗೂ ಭೂಮಿಯಲ್ಲಿ ಕ್ಷೋಭೆ ಹರಡುವವರು. ಅವರೇ ಹತಭಾಗ್ಯರು.');
INSERT INTO "kannada" VALUES(2,28,'2:28','ಅಲ್ಲಾಹನನ್ನು ನೀವು ಹೇಗೆ ಧಿಕ್ಕರಿಸುತ್ತಿರುವಿರಿ? ನೀವು ನಿರ್ಜೀವಿಗಳಾಗಿದ್ದಿರಿ. ಆಮೇಲೆ ಅವನು ನಿಮ್ಮನ್ನು ಜೀವಂತಗೊಳಿಸಿದನು. ಮುಂದೆ ನಿಮ್ಮನ್ನು ಅವನು ಮೃತ್ಯುಗೊಳಿಸುವನು. ಮತ್ತೆ ನಿಮಗೆ ಜೀವಕೊಡುವನು. ನಂತರ ಆತನ ಕಡೆಗೇ ನಿಮ್ಮನ್ನು ಮರಳಿಸಲಾಗುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(2,29,'2:29','ಆತನು ಭೂಮಿಯಲ್ಲಿರುವುದನ್ನೆಲ್ಲ ನಿಮಗಾಗಿ ಸೃಷ್ಟಿಸಿದವನು . ತದನಂತರ ಊಧ್ರ್ವಲೋಕಕ್ಕೆ ಚಿತ್ತವಿರಿಸಿ ಸಪ್ತಗಗನಗಳನ್ನಾಗಿ ಸರಿಪಡಿಸಿದನು. ಅವನು ಸರ್ವಜ್ಞನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(2,30,'2:30','ಪೃಥ್ವಿಯಲ್ಲಿ ನಾನು ಓರ್ವ ಪ್ರತಿನಿಧಿಯನ್ನು ನೇಮಿಸಲಿದ್ದೇನೆಂದು ನಿಮ್ಮ ಪ್ರಭು ಮಲಕ್ ಗಳೊಂದಿಗೆ ಹೇಳಿದಾಗ, ‘ಅಲ್ಲಿ ಪಿತೂರಿ, ರಕ್ತಪಾತ ನಡೆಸುವವರನ್ನು ನೇಮಿಸುವೆಯಾ? ನಿನಗೆ ಸ್ತುತಿ ಕೀರ್ತನೆ ಮಾಡಲು ಹಾಗೂ ನಿನ್ನ ಪರಿಶುದ್ಧತೆಯ ವಾಚನಕ್ಕೆ ನಾವಿದ್ದೇವಲ್ಲಾ?’ ಎಂದು ಮಲಕ್‍ಗಳು ಕೇಳಿದರು . ಅದಕ್ಕೆ ಅಲ್ಲಾಹನು; “ನೀವರಿಯದ್ದನ್ನು ನಾನರಿಯುತ್ತಿದ್ದೇನೆ” ಎಂದು ಹೇಳಿದನು.');
INSERT INTO "kannada" VALUES(2,31,'2:31','ಆದಮರಿಗೆ ಸರ್ವವಸ್ತುಗಳ ಹೆಸರುಗಳನ್ನು ಅಲ್ಲಾಹನು ಕಲಿಸಿದನು . ಅವೆಲ್ಲವನ್ನೂ ಮಲಕ್‍ಗಳೆದುರು ಪ್ರದರ್ಶಿಸುತ್ತಾ, ನಿಮ್ಮ ವಾದವು ದಿಟವಾಗಿದ್ದರೆ ಇವುಗಳ ಹೆಸರುಗಳನ್ನು ಹೇಳಿರಿ ಎಂದನು.');
INSERT INTO "kannada" VALUES(2,32,'2:32','ಮಲಕ್‍ಗಳು ‘ನೀನು ಪರಮ ಪರಿಶುದ್ಧನು. ನೀನು ನಮಗೆ ಕಲಿಸಿಕೊಡದ ಜ್ಞಾನ ನಮಗಿಲ್ಲ, ಪರಮ ಜ್ಞಾನಿಯೂ ಪರಮತಂತ್ರಜ್ಞನೂ ನೀನೇ!’ ಎಂದರು');
INSERT INTO "kannada" VALUES(2,33,'2:33','ಆಗ ಅಲ್ಲಾಹನು ಆದಮರನ್ನು ಕರೆದು, ‘ಇವುಗಳ ಹೆಸರುಗಳನ್ನು ಇವರಿಗೆ ಹೇಳಿಕೊಡು’ ಎಂದನು. ಆದಮರು ಅವುಗಳ ನಾಮಗಳನ್ನು ಅವರಿಗೆ ಹೇಳಿಕೊಟ್ಟರು. ಆಗ ಮಲಕ್‍ಗಳಲ್ಲಿ ಅಲ್ಲಾಹನೆಂದನು, ಭುವಿ - ನಭಗಳ ಸರ್ವ ರಹಸ್ಯ ಗಳನ್ನೂ , ನೀವು ಬಹಿರಂಗಗೊಳಿಸುವುದನ್ನೂ ಮರೆಮಾಚುತ್ತಿದ್ದುದನ್ನೂ ಬಲ್ಲವನು ನಾನು ಎಂದು ನಿಮಗೆ ನಾನು ಹೇಳಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(2,34,'2:34','ನಂತರ ‘ಆದಮರಿಗೆ ಪ್ರಣಾಮ ಸಲ್ಲಿಸಿರಿ’ ಎಂದು ನಾವು ಮಲಕ್‍ಗಳಿಗೆ ಆಜ್ಞಾಪಿಸಿದಾಗ, ಸರ್ವ ಮಲಕ್‍ಗಳೂ ಆದಮರಿಗೆ ಪ್ರಣಾಮ ಮಾಡಿದರು. ಇಬ್‍ಲೀಸನ ಹೊರತು. ಅವನು ನಿರಾಕರಿಸಿದನು. ಗರ್ವಿಷ್ಠನಾದನು. ಆತ ಸತ್ಯ ನಿಷೇಧಿಗಳ ಕೂಟದಲ್ಲಾಗಿದ್ದನು.');
INSERT INTO "kannada" VALUES(2,35,'2:35','ಆ ಬಳಿಕ ನಾವು ಆದಮರೊಡನೆ, ಸ್ವರ್ಗದಲ್ಲಿ ಸಪತ್ನೀಕನಾಗಿ ನೆಲೆಸು. ನೀವಿಬ್ಬರೂ ಎಲ್ಲಿಂದಲೂ ಮನ ಬಂದಂತೆ ಭುಜಿಸಿರಿ, ಈ ಮರದ ಬಳಿ ಮಾತ್ರ ಸುಳಿಯದಿರಿ, ಅನ್ಯಥಾ ನೀವಿಬ್ಬರು ಅತಿಕ್ರಮಿಗಳ ಕೂಟಕ್ಕೆ ಸೇರುವಿರಿ . ಎಂದೆವು.');
INSERT INTO "kannada" VALUES(2,36,'2:36','ಆದರೆ ಸೈತಾನನು ಅವರನ್ನು ಅದರಿಂದ ತಪ್ಪಿಸಿದನು. ಅವರಿಬ್ಬರೂ ಅನುಭವಿಸುತ್ತಿದ್ದ ಸುಖ ಸ್ಥಿತಿಯಿಂದ ಇಬ್ಬರನ್ನೂ ಹೊರಹಾಕಿಸಿದನು, ಆಗ, ‘ಇಳಿದು ಹೋಗಿರಿ, ನೀವಿನ್ನು ಪರಸ್ಪರ ವೈರಿಗಳು. ಭೂಮಿಯಲ್ಲಿ ಒಂದು ನಿರ್ಧಿಷ್ಠಾವಧಿ ವಾಸ್ತವ್ಯ ಮತ್ತು ಜೀವನಾನುಕೂಲ ನಿಮಗಿರುವುದು’ ಎಂದು ನಾವು ಆಜ್ಞಾಪಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(2,37,'2:37','ತರುವಾಯ ಆದಮರು ತನ್ನ ಪ್ರಭುವಿನಿಂದ ಕೆಲವು ವಚನಗಳನ್ನು ಗ್ರಹಿಸಿಕೊಂಡರು . ಆಗ ಪ್ರಭು ಅವರಿಗೆ ಮನ್ನಿಸಿದನು. ಅವನು ದಯಾನಿಧಿಯೂ ಕ್ಷಮಾಶೀಲನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(2,38,'2:38','ನಾವು ಹೇಳಿದೆವು; ನೀವೆಲ್ಲರೂ ಇಲ್ಲಿಂದ ಇಳಿದು ಹೋಗಿರಿ. ನನ್ನ ಕಡೆಯಿಂದ ಮಾರ್ಗದರ್ಶನವು ನಿಮಗೆ ಬಂದರೆ, ಯಾರು ನನ್ನ ಮಾರ್ಗ ದರ್ಶನವನ್ನು ಅನುಸರಿ ಸುತ್ತಾರೆ, ಅವರಿಗೆ ಯಾವುದೇ ಭಯ, ವ್ಯಸನಗಳಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,39,'2:39','ಸತ್ಯವನ್ನು ಧಿಕ್ಕರಿಸಿ ನಮ್ಮ ಕುರುಹುಗಳನ್ನು ಹುಸಿಗೊಳಿಸಿದವರು ನರಕದವರಾಗುವರು. ಅವರದರಲ್ಲಿ ನಿತ್ಯ ನಿವಾಸಿಗಳು.');
INSERT INTO "kannada" VALUES(2,40,'2:40','ಇಸ್ರಾಈಲ್ ಸಂತತಿಗಳೇ! ನಿಮಗೆ ನಾನಿತ್ತ ಅನುಗ್ರಹವನ್ನು ಜ್ಞಾಪಿಸಿಕೊಳ್ಳಿರಿ . ನನ್ನಲ್ಲಿ ನೀವಿತ್ತ ಕರಾರನ್ನೂ ಪೂರೈಸಿರಿ. ನಾನು ನಿಮಗಿತ್ತ ಕರಾರನ್ನೂ ಪೂರೈಸುವೆನು. ನನ್ನನ್ನು ಮಾತ್ರ ಭಯಪಡಿರಿ.');
INSERT INTO "kannada" VALUES(2,41,'2:41','ನಿಮ್ಮ ಬಳಿಯಲ್ಲಿರುವ ದಿವ್ಯ ಗ್ರಂಥವನ್ನು ಸೃಷ್ಟೀಕರಿಸುವ ನಾನಿತ್ತ ಈ ವೇದ ಗ್ರಂಥದಲ್ಲಿ ವಿಶ್ವಾಸವಿಡಿರಿ. ಇದನ್ನು ಧಿಕ್ಕರಿಸಿದ ಮೊದಲಿಗರು ನೀವಾಗದಿರಿ. ನನ್ನ ವಚನಗಳನ್ನು ತುಚ್ಛ ಬೆಲೆಗೆ ಮಾರಬೇಡಿರಿ . ನನ್ನನ್ನು ಮಾತ್ರ ಭಯಪಡಿರಿ.');
INSERT INTO "kannada" VALUES(2,42,'2:42','ಸತ್ಯವನ್ನು ಮಿಥ್ಯಕ್ಕೆ ಕಲಬೆರಕೆ ಮಾಡಬೇಡಿರಿ. ನೀವು ತಿಳಿದವರಾಗಿದ್ದುಕೊಂಡೇ ಸತ್ಯವನ್ನು ಮುಚ್ಚಿಡದಿರಿ.');
INSERT INTO "kannada" VALUES(2,43,'2:43','ನಮಾಝ್ ನೆಲೆಗೊಳಿಸಿರಿ, ಝಕಾತ್ ನೀಡಿರಿ. ಶಿರವಂದನೆ ಮಾಡುವವರ ಜೊತೆಗೆ ನೀವೂ ಶಿರವಂದನೆ ಮಾಡಿರಿ.');
INSERT INTO "kannada" VALUES(2,44,'2:44','ಜನರಿಗೆ ಹಿತೋಪದೇಶ ನೀಡುತ್ತಾ ಸ್ವತಃ ನಿಮ್ಮನ್ನು ಮರೆತು ಬಿಡುತ್ತೀರಾ? ನೀವು ಗ್ರಂಥವನ್ನು ಓದುತ್ತಲಿರುವಿರಿ. ನಿಮಗೆ ಯೋಚಿಸಬಾರದೆ?');
INSERT INTO "kannada" VALUES(2,45,'2:45','ಸಹನೆ ಮತ್ತು ನಮಾಝ್‍ನಿಂದ ನೆರವು ಬೇಡಿರಿ . ಭಯಚಿತ್ತರಲ್ಲದವರಿಗೆ ನಮಾಝ್ ಬಹಳ ಭಾರವೇರಿದ್ದಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(2,46,'2:46','ಪ್ರಭುವಿನ ಭೇಟಿಯ ಪ್ರಜ್ಞೆ ಇರುವ ಮತ್ತು ಆತನ ಕಡೆಗೇ ನಮ್ಮ ವಾಪಸಾತಿಯೆಂದು ನಂಬಿರುವ ಭಕ್ತರವರು.');
INSERT INTO "kannada" VALUES(2,47,'2:47','ಇಸ್ರಾಈಲ ಸಂತತಿಗಳೇ, ನಿಮಗೆ ನಾನಿತ್ತ ಕೊಡುಗೆಗಳನ್ನೂ ನಿಮ್ಮನ್ನು ನಾನು ವಿಶ್ವದಲ್ಲೇ ಶ್ರೇಷ್ಠರಾಗಿ ಮಾಡಿದ್ದನ್ನೂ ನೆನಪಿಸಿಕೊಳ್ಳಿರಿ ');
INSERT INTO "kannada" VALUES(2,48,'2:48','ಒಬ್ಬನಿಂದ ಮತ್ತೊಬ್ಬನಿಗೆ ಉಪಕಾರ ಸಿಗದ, ಯಾವನಿಂದಲೂ ಶಿಫಾರಸು ಸ್ವೀಕೃತವಾಗದ, ಯಾರಿಂದಲೂ ಬದಲಿ ಪರಿಹಾರ ಸ್ವೀಕರಿಸದ, ಯಾರಿಗೂ ಸಹಾಯ ದೊರಕದ ಒಂದು ದಿನದ ಬಗ್ಗೆ ನಿಮಗೆ ಎಚ್ಚರವಿರಲಿ.');
INSERT INTO "kannada" VALUES(2,49,'2:49','ಫಿರ್‍ಔನನ ಕೂಟದವರು ನಿಮಗೆಸಗುತ್ತಿದ್ದ ಕ್ರೂರ ಹಿಂಸೆಗಳಿಂದ ನಿಮ್ಮನ್ನು ನಾವು ಪಾರು ಮಾಡಿದ್ದನ್ನು ನೆನಪಿಸಿಕೊಳ್ಳಿರಿ. ಅವರು ನಿಮ್ಮ ಗಂಡು ಮಕ್ಕಳನ್ನು ಕೊಲ್ಲುತ್ತಿದ್ದರು. ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಬಿಟ್ಟು ಬಿಡುತ್ತಿದ್ದರು. ಆ ಹಿಂಸಾಚಾರವು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ನಿಮಗೊಂದು ಘೋರ ಸತ್ವಪರೀಕ್ಷೆಯಾಗಿತ್ತು.');
INSERT INTO "kannada" VALUES(2,50,'2:50','ನಾವು ಕಡಲನ್ನು ಒಡೆದು ನಿಮ್ಮನ್ನು ರಕ್ಷಿಸಿದ ಹಾಗೂ ನಿಮ್ಮ ಕಣ್ಣೆದುರೇ ಫಿರ್‍ಔನನ ತಂಡವನ್ನು ಜಲ ಸಮಾಧಿ ಮಾಡಿದ ಘಟನೆಯನ್ನು ಸ್ಮರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(2,51,'2:51','ನಾವು ಮೂಸಾರಿಗೆ ನಲ್ವತ್ತು ರಾತ್ರಿಗಳ ಅವಧಿ ನಿಗದಿಪಡಿಸಿದಾಗ ಅವರ ಅಭಾವದಲ್ಲಿ ನೀವು ಕರುವಿಗೆ ಆರಾಧಿಸಿದಿರಿ. ನೀವು ಅಕ್ರಮಿಗಳಾದಿರಿ.');
INSERT INTO "kannada" VALUES(2,52,'2:52','ಆದರೂ ಅದರ ಬಳಿಕ ನೀವು ಕೃತಜ್ಞರಾಗುವಿರೆಂದು ನಿಮಗೆ ನಾವು ಕ್ಷಮಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(2,53,'2:53','ನಿಮ್ಮ ಸನ್ಮಾರ್ಗ ಪ್ರಾಪ್ತಿಗೆಂದು ಮೂಸಾರಿಗೆ ವೇದಗ್ರಂಥವನ್ನೂ, ವಿವೇಚಕ ಜ್ಞಾನವನ್ನೂ ದಯಪಾಲಿಸಿದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(2,54,'2:54','ಮೂಸಾರವರು ತನ್ನ ಜನಾಂಗದೊಡನೆ ಹೇಳಿದರು. “ನನ್ನ ಜನರೇ! ಕರುವಿಗೆ ಆರಾಧಿಸುವ ಮೂಲಕ ನಿಮಗೆ ನೀವೇ ಅನ್ಯಾಯವೆಸಗಿದ್ದೀರಿ. ಅಲ್ಲಾಹ ನೆಡೆಗೆ ಪಶ್ಚಾತ್ತಾಪಪಡಿರಿ. ನಿಮ್ಮನ್ನು ನೀವೇ ಕೊಲ್ಲಿರಿ. (ಮರಣದಂಡನೆಗೆ ಸಿದ್ಧರಾಗಿರಿ.) ಅದು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಬಳಿ ನಿಮಗೆ ಶ್ರೇಯಸ್ಕರ”. ಹಾಗೆ ಅಲ್ಲಾಹನು ನಿಮ್ಮ ತೌಬಾ ಸ್ವೀಕರಿಸಿದನು. ಅವನು ಪಶ್ಚಾತ್ತಾಪವನ್ನು ಬಹುವಾಗಿ ಸ್ವೀಕರಿಸುವವನೂ ಕರುಣಾವಾರಿಧಿಯೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(2,55,'2:55','ಓ ಮೂಸಾ! ಅಲ್ಲಾಹನನ್ನು ಕಣ್ಣಾರೆ ಕಾಣದೆ ನಾವು ನಿನ್ನಲ್ಲಿ ವಿಶ್ವಾಸವಿಡೆವು ಎಂದು ನೀವು ಹೇಳಿದಿರಿ. ಆಗ ನೀವು ನೋಡು ನೋಡುತ್ತಿದ್ದಂತೆಯೇ ನಿಮ್ಮ ಮೇಲೆ ಸಿಡಿಲೆರಗಿತು.');
INSERT INTO "kannada" VALUES(2,56,'2:56','ಹಾಗೆ ಸತ್ತ ನಿಮಗೆ ನಾವು ಮರುಜೀವಕೊಟ್ಟೆವು. ನೀವು ಋಣಭಾವವುಳ್ಳವರಾಗುವಿರೆಂದು.');
INSERT INTO "kannada" VALUES(2,57,'2:57','ನಿಮ್ಮ ಮೇಲೆ ನಾವು ಮೋಡಗಳಿಂದ ನೆರಳನ್ನು ನೀಡಿದೆವು. ಮಧು (ಮನ್ನ್) ಮತ್ತು ಕಾಡಪಕ್ಷಿ (ಸಲ್ವಾ) ಎಂಬೆರಡು ಬಗೆಯ ಆಹಾರವನ್ನು ನೀಡಿದೆವು. ನಾವು ನಿಮಗೆ ನೀಡಿರುವ ಉತ್ತಮ ಆಹಾರವನ್ನು ಭುಜಿಸಿರಿ ಎಂದೆವು. ಆದರೆ ಅವರೆಸಗಿದ ಅನ್ಯಾಯದ ಫಲ ನಮಗೆ ಬಾಧಿಸಲಿಲ್ಲ. ಪರಂತು, ಅವರಿಗೆ ಅವರೇ ಅನ್ಯಾಯ ಮಾಡಿಕೊಂಡರು.');
INSERT INTO "kannada" VALUES(2,58,'2:58','ನಾವು ನಿಮ್ಮೊಡನೆ “ಈ ನಗರಕ್ಕೆ ಪ್ರವೇಶಿಸಿರಿ, ನಿರಾಳವಾಗಿ ತಿಂದುಂಡು ಬದುಕಿರಿ, ನಗರಕ್ಕೆ ಪ್ರವೇಶಿಸುವಾಗ ಶಿರವಂದನೆ ಮಾಡಿರಿ. ಪ್ರವೇಶ ದ್ವಾರದಲ್ಲಿ ನಿಂತುಕೊಂಡು ಪಶ್ಚಾತ್ತಾಪದ ವಚನವನ್ನು ಹೇಳಿರಿ. ನಾವು ನಿಮ್ಮ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸುತ್ತೇವೆ. ಸಚ್ಚರಿತರಿಗೆ ನಾವು ಇನ್ನಷ್ಟು ವರ್ಧಿಸುತ್ತೇವೆ” ಎಂದ ಸಂದರ್ಭ(ವನ್ನು ಸ್ಮರಿಸಿರಿ).');
INSERT INTO "kannada" VALUES(2,59,'2:59','ಆದರೆ ಆ ಧಿಕ್ಕಾರಿಗಳು ಅವರಿಗೆ ಹೇಳಲಾದ ಆದೇಶ ವನ್ನು ಅದಲು ಬದಲು ಮಾಡಿಕೊಂಡರು. ಆದ್ದರಿಂದ ನಾವು ಅಕ್ರಮಿಗಳ ಮೇಲೆ ಅವರ ಅಪರಾಧದ ಫಲವಾಗಿ ಬಾನಿಂದ ವಿಪತ್ತನ್ನು ಇಳಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(2,60,'2:60','ಮೂಸಾರವರು ತನ್ನ ಜನರಿಗಾಗಿ ಅಲ್ಲಾಹನಲ್ಲಿ ನೀರಿಗೆ ಕೋರಿಕೆ ಸಲ್ಲಿಸಿದರು. ಆಗ ನಾವು “ಆ ಬಂಡೆಗೆ ದಂಡದಿಂದ ಬಡಿಯಿರಿ” ಎಂದು ಮೂಸಾರಿಗೆ ಹೇಳಿದೆವು. ಅವರು ಬಡಿದಾಗ ಬಂಡೆಯಿಂದ ಹನ್ನೆರಡು ಒರತೆಗಳು ಚಿಮ್ಮಿದುವು. ಪ್ರತಿಯೊಂದು ಗೋತ್ರದವರು ಜಲ ಪಡೆಯುವ ಸ್ಥಳವನ್ನು ನಿರ್ಣಯಿಸಿಕೊಂಡರು . ಅಲ್ಲಾಹನ ಆಹಾರದಿಂದ (ಉದಾರತೆಯಿಂದ ದೊರೆತುದನ್ನು) ತಿನ್ನಿರಿ, ಕುಡಿಯಿರಿ, ಭೂಮಿ ಮೇಲೆ ಕ್ಷೋಭೆ ಹರಡದಿರಿ ಎಂದವರಿಗೆ ಹೇಳಿದೆವು.');
INSERT INTO "kannada" VALUES(2,61,'2:61','ಮೂಸಾರವರೇ, ‘ಒಂದೇ ವಿಧ ಆಹಾರವನ್ನು ನಾವು ಸಹಿಸಲಾರೆವು, ಆದ್ದರಿಂದ ನೆಲದಲ್ಲಿ ಬೆಳೆ ಯುವ ಹರಿವೆ, ಸೌತೆ, ಗೋಧಿ, ಹೆಸರುಕಾಳು, ನೀರುಳ್ಳಿಗಳನ್ನು ಒದಗಿಸುವಂತೆ ನಿಮ್ಮ ಪ್ರಭುವಿನಲ್ಲಿ ಅಪೇಕ್ಷಿಸಿರಿ ಎಂದು ನೀವು ಮೂಸಾರಲ್ಲಿ ಹೇಳಿದಾಗ ಅವರು, ಮೇಲ್ಮಟ್ಟದ ಬದಲಿಗೆ ಕೆಳಮಟ್ಟದ್ದನ್ನು ಬಯಸುತ್ತಿದ್ದೀರಾ?’ ಹಾಗಾದರೆ ‘ನೀವು ನಗರಕ್ಕೆ ಹೋಗಿರಿ. ನೀವು ಬಯಸಿದ್ದು ಅಲ್ಲಿ ಇದೆ’ ಎಂದರು. ಹಾಗೆ ಅವರಿಗೆ ಅಪಮಾನ ಮತ್ತು ದಾರಿದ್ರ್ಯದ ಮುದ್ರೆಯೊತ್ತಲಾಗಿದೆ. ಅವರು ಅಲ್ಲಾಹನ ಕ್ರೋಧಕ್ಕೆ ತುತ್ತಾದರು. ಇದೆಲ್ಲವೂ ಅವರು ಅಲ್ಲಾಹನ ಪ್ರಮಾಣಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದ ಹಾಗೂ ಅನ್ಯಾಯ ವಾಗಿ ಪ್ರವಾದಿಗಳನ್ನು ವಧಿಸಿದ ಹಾಗೂ ಪಾಪವೆ ಸಗಿದ ಮತ್ತು ಹದ್ದು ಮೀರಿದುದರ ಫಲವಾಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(2,62,'2:62','ಸತ್ಯವಿಶ್ವಾಸಿಗಳು, ಯಹೂದಿಗಳು, ಕ್ರೈಸ್ತರು, ಸಬಯನರು ಯಾರೇ ಆಗಲಿ ಅಲ್ಲಾಹು ಮತ್ತು ಪರಲೋಕದಲ್ಲಿ ನಂಬಿಕೆಯಿಟ್ಟು ಸುಕೃತಗಳನ್ನು ಮಾಡಿದರೆ ಪ್ರಭುವಿನ ಬಳಿ ಪುಣ್ಯವಿದೆ. ಅವರಿಗೆ ಭಯ, ವ್ಯಸನಗಳಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,63,'2:63','ನಿಮ್ಮ ಮೇಲೆ ‘ಥೂರ್’ ಂ ಬೆಟ್ಟವನ್ನು ಎತ್ತಿ ಹಿಡಿದು, ನಾವಿತ್ತ ಆದೇಶಗಳನ್ನು ಪರಿಶ್ರಮಪೂರ್ವಕ ಅನುಸರಿಸಿರಿ, ಅದರ ಸಾರವನ್ನು ಸ್ಮರಿಸಿಕೊಳ್ಳಿರಿ. ನೀವು ಧರ್ಮಾತ್ಮರಾಗುವಿರಿ’ ಎಂದು ನಿಮ್ಮಿಂದ ಧೃಢಪ್ರತಿಜ್ಞೆ ಪಡಕೊಂಡ ಸಂದರ್ಭ(ವನ್ನು ಸ್ಮರಿಸಿರಿ).');
INSERT INTO "kannada" VALUES(2,64,'2:64','ತರುವಾಯ ನೀವು ಪ್ರತಿಜ್ಞೆಯಿಂದ ವಿಮುಖರಾದಿರಿ. ನಿಮಗೆ ಅಲ್ಲಾಹನ ಔದಾರ್ಯ ಹಾಗೂ ಕೃಪೆ ಇರದಿದ್ದಲ್ಲಿ ನಾಶ ನಷ್ಟಕ್ಕೆ ಗುರಿಯಾಗುತ್ತಿದ್ದಿರಿ.');
INSERT INTO "kannada" VALUES(2,65,'2:65','ಶನಿವಾರದ ನಿಯಮವನ್ನು ಉಲ್ಲಂಘಿಸಿದ ನಿಮ್ಮ ಪೈಕಿಯವರ ಬಗ್ಗೆ ನಿಮಗೆ ಗೊತ್ತು. ‘ನಿಂದ್ಯರಾದ ಕಪಿಗಳಾಗಿರಿ’ ಎಂದು ನಾವವರಿಗೆ ವಿಧಿ ಕೊಟ್ಟೆವು.');
INSERT INTO "kannada" VALUES(2,66,'2:66','ಆ ಘಟನೆಯನ್ನು ನಾವು ಭಾವೀ, ವರ್ತಮಾನಗಳ ಜನರಿಗೆ ಒಂದು ಪಾಠವೂ ಧರ್ಮಾತ್ಮರಿಗೊಂದು ಉಪದೇಶವೂ ಆಗಿ ಮಾಡಿದೆವು.');
INSERT INTO "kannada" VALUES(2,67,'2:67','ಮೂಸಾರವರು ತನ್ನ ಜನರಲ್ಲಿ ‘ನೀವೊಂದು ಹಸುವನ್ನು ಬಲಿ ಕೊಡಬೇಕೆಂದು ಅಲ್ಲಾಹನು ನಿಮಗೆ ಆಜ್ಞಾಪಿಸುತ್ತಾನೆ’ ಎಂದಾಗ ‘ನಮ್ಮನ್ನು ಗೇಲಿ ಮಾಡುತ್ತಿರುವಿರಾ?’ ಎಂದವರು ಕೇಳಿದರು. ಆಗ ಮೂಸಾ, ಅಲ್ಲಾಹನಿಗೆ ಶರಣು. ನಾನು ಗೇಲಿ ಮಾಡುವಂತಹ ಮೂಢನಲ್ಲ.’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(2,68,'2:68','ಯಾವ ತರದ ಹಸುವೆಂದು ನಿಮ್ಮ ಪ್ರಭು ತಿಳಿಸುವಂತೆ ಹೇಳಿ ಎಂದವರು ಹೇಳಿದರು. ಆಗ ಮೂಸಾ ತೀರಾ ಎಳೆಯದೂ ಮುದಿಯೂ ಅಲ್ಲ. ನಡುಪ್ರಾಯದ್ದೆಂದು ಅವನು ಹೇಳುತ್ತಾನೆ. ಆ ಪ್ರಕಾರ ನೀವು ಕಾರ್ಯಶೀಲರಾಗಿರಿ ಎಂದರು.');
INSERT INTO "kannada" VALUES(2,69,'2:69','ನಿಮ್ಮ ಪ್ರಭುವಿನಲ್ಲಿ ನಮಗಾಗಿ ಕೇಳಿ, ಅವನು ಅದರ ಬಣ್ಣವನ್ನು ವ್ಯಕ್ತಗೊಳಿಸಲಿ ಎಂದವರು ಹೇಳಿದರು. ಆಗ ಮೂಸಾ, ‘ನೋಡುಗರಿಗೆ ಕೌತುಕ ಹುಟ್ಟಿಸುವಂತಹ ಬಂಗಾರದ ಬಣ್ಣದ ಹಸುವೆಂದು ಅವನು ಹೇಳುತ್ತಿದ್ದಾನೆ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(2,70,'2:70','ಯಾವ ತರದ ಹಸುವೆಂದು ಇನ್ನಷ್ಟು ಸ್ಪಷ್ಟಗೊಳಿಸಲಿ, ಒಂದಕ್ಕೊಂದು ಹೋಲುವ ಗೋವುಗಳಿಂದ ನಾವು ಪೇಚಿಗೀಡಾಗಿದ್ದೇವೆ, ಅಲ್ಲಾಹು ಇಚ್ಚಿಸಿದರೆ ನಮಗೆ ನೇರ ದಾರಿ ಸಿಗುತ್ತದೆ’ ಎಂದವರು ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(2,71,'2:71','ಆಗ ಮೂಸಾರವರು ‘ಹೊಲ ಉಳಲೋ ಕೃಷಿಗೆ ನೀರೆತ್ತಲೋ ಬಳಸದ, ಸರ್ವಾಂಗಪೂರ್ಣವೂ, ಕಲೆರಹಿತವೂ ಆದ ಹಸು ಎಂದು ಅಲ್ಲಾಹು ಹೇಳುತ್ತಿದ್ದಾನೆ.’ ಎಂದರು. ಆಗ ಅವರೆಂದರು; ಇದೀಗ ಸರಿಯಾದ ಮಾಹಿತಿ ಕೊಟ್ಟಿರಿ. ಅಂತೂ ಅವರು ಆ ದನವನ್ನು ಬಲಿ ಕೊಟ್ಟರು. ಅವರಿಗದು ಅಸಾಧ್ಯಕ್ಕೆ ಸನಿಹವಾಗಿತ್ತು .');
INSERT INTO "kannada" VALUES(2,72,'2:72','ನೀವು ಒಬ್ಬನ ಕೊಲೆ ಮಾಡಿ ಪರಸ್ಪರ ಆರೋಪ ಹೊರಿಸಿ ನುಣುಚಿಕೊಳ್ಳುತ್ತಿದ್ದ ಸಂದರ್ಭ. ನಿಮ್ಮ ರಹಸ್ಯವನ್ನು ಅಲ್ಲಾಹನು ಬಯಲಿಗೆಳೆಯ ಲಿಚ್ಛಿಸಿದ್ದನು ');
INSERT INTO "kannada" VALUES(2,73,'2:73','ಆದ್ದರಿಂದ ನಾವು ಹೇಳಿದೆವು. ದನದ ಒಂದು ಅಂಗದಿಂದ ಮೃತನ ದೇಹಕ್ಕೆ ಹೊಡೆಯಿರಿ. ಈ ರೀತಿ ಅಲ್ಲಾಹನು ಮೃತರಿಗೆ ಮರುಜೀವ ಕೊಡುತ್ತಾನೆ. ನೀವು ವಿವೇಚಿಸುವ ಸಲುವಾಗಿ ಆತನು ತನ್ನ ದೃಷ್ಟಾಂತಗಳನ್ನು ತೋರಿಸಿ ಕೊಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(2,74,'2:74','ಆಮೇಲೂ ನಿಮ್ಮ ಮನಸ್ಸು ಕಠಿಣವಾಯಿತು. ಬಂಡೆಯಂತೆ ಅಥವಾ ಅದಕ್ಕಿಂತಲೂ ಕಠೋರ. ಯಾಕೆಂದರೆ ಕೆಲವು ಬಂಡೆಗಳಿಂದ ನೀರೊರತೆಗಳು ಚಿಮ್ಮುತ್ತವೆ. ಕೆಲವು ಬಂಡೆಗಳು ಒಡೆದು ಅದರೊಳಗಿಂದ ಜಲ ಹೊರಬರುತ್ತದೆ. ಕೆಲವು ಕಲ್ಲುಗಳು ದೇವಭಯದಿಂದ ಕೆಳಗುರುಳಿ ಬೀಳುತ್ತವೆ. ನಿಮ್ಮ ಕೃತ್ಯಗಳ ಬಗ್ಗೆ ಅಲ್ಲಾಹನು ಅಶ್ರದ್ಧನಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,75,'2:75','ಇಂತಹ ಜನರು ನಿಮ್ಮ ಕರೆಗೆ ಸ್ಪಂದಿಸಿ ಸತ್ಯವಿಶ್ವಾಸ ತಾಳುವರೆಂದು ನೀವು ಆಶಿಸುತ್ತೀರಾ? ಅವರ ಪೈಕಿ ಒಂದು ವಿಭಾಗದವರು ಅಲ್ಲಾಹನ ವಚನವನ್ನು ಕೇಳಿ ಅದನ್ನು ಅರ್ಥ ಮಾಡಿಕೊಂಡ ಬಳಿಕ ಕೂಡಾ ತಿಳಿದುಕೊಂಡೇ ತಿದ್ದುಪಡಿ ಮಾಡಿದರು .');
INSERT INTO "kannada" VALUES(2,76,'2:76','ಅವರು ವಿಶ್ವಾಸಿಗಳನ್ನು ಭೇಟಿಯಾದರೆ ‘ನಾವೂ ವಿಶ್ವಾಸಿಗಳು’ ಎನ್ನುತ್ತಾರೆ. ಆದರೆ ಅವರು ಪರಸ್ಪರ ಏಕಾಂತದಲ್ಲಿ ಕೂತಾಗ, “ಅಲ್ಲಾಹು ನಿಮಗೆ ವ್ಯಕ್ತಗೊಳಿಸಿದ ವಿಚಾರಗಳನ್ನು ಮುಸ್ಲಿಮರಿಗೆ ಹೇಳುತ್ತಿದ್ದೀರಾ? ಅವರದನ್ನು ಪ್ರಭುವಿನ ಬಳಿ ನಮಗೆ ಪ್ರತಿಕೂಲ ಸಾಕ್ಷಿ ಮಾಡಿಯಾರು, ನೀವು ಯೋಚಿಸುತ್ತಿಲ್ಲವೇ? ಎಂದು ಎಚ್ಚರಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(2,77,'2:77','ಅವರು ಹೊರಗಡೆ ಹೇಳುತ್ತಿರುವುದನ್ನೂ ಒಳಗಡೆ ಮುಚ್ಚಿಡುವುದನ್ನೂ ಅಲ್ಲಾಹನು ತಿಳಿಯುತ್ತಾನೆ ಎಂಬುದು ಅವರಿಗೆ ಗೊತ್ತಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(2,78,'2:78','ಅವರಲ್ಲಿ ನಿರಕ್ಷರಿಗಳಿದ್ದಾರೆ. ಅವರಿಗೆ ವೇದಗ್ರಂಥ ತಿಳಿಯದು. ಅವರು ಕೇವಲ ಊಹೆಯನ್ನಷ್ಟೇ ಅವಲಂಬಿಸಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(2,79,'2:79','ಸ್ವಹಸ್ತಗಳಿಂದ ಗ್ರಂಥ ಬರೆದು ತುಚ್ಛ ಬೆಲೆಯನ್ನು ಪಡೆಯುವ ಸಲುವಾಗಿ ‘ಇದು ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ಬಂದಿದೆ’ ಎನ್ನುವವರಿಗೆ ವಿನಾಶ ಕಾದಿದೆ. ಅವರ ಹಸ್ತ ಲಿಖಿತಗಳಿಂದ ಅವರಿಗೆ ವಿನಾಶವಿದೆ. ಮತ್ತು ಅವರ ಸಂಪಾದನೆಗಳ ಕಾರಣದಿಂದಲೂ ಅವರಿಗೆ ವಿನಾಶವಿದೆ.');
INSERT INTO "kannada" VALUES(2,80,'2:80','“ಅವರು, ಕೆಲವೇ ಕೆಲವು ದಿನಗಳ ಹೊರತು ನಮಗೆ ನರಕಾಗ್ನಿ ತಟ್ಟುವುದಿಲ್ಲ” ಎನ್ನುತ್ತಾರೆ. ಕೇಳಿರಿ, ನೀವು ಅಂತಹ ಭರವಸೆಯನ್ನು ಅಲ್ಲಾಹನಿಂದ ಪಡೆದಿದ್ದೀರೋ, ಹಾಗೆ ಪಡೆದಿದ್ದರೆ ಅವನು ಮಾತು ತಪ್ಪಲಾರ ಅಥವಾ ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲದ್ದನ್ನು ಅಲ್ಲಾಹನ ಮೇಲೆ ಹೊರಿಸಿ ಹೇಳುತ್ತೀರೋ?');
INSERT INTO "kannada" VALUES(2,81,'2:81','ಹಾಗಲ್ಲ, ಯಾರು ಪಾಪವೆಸಗುತ್ತಾರೆ, ಮತ್ತು ಪಾಪಗಳು ಯಾರನ್ನು ಸುತ್ತಿಕೊಂಡಿರುತ್ತವೆ, ಅವರೇ ನರಕದವರು. ಅವರದರಲ್ಲಿ ಚಿರನಿವಾಸಿಗಳು.');
INSERT INTO "kannada" VALUES(2,82,'2:82','ಸತ್ಯವಿಶ್ವಾಸವಿಟ್ಟು ಸುಕೃತ ಕೈಗೊಂಡವರು ಸ್ವರ್ಗದವರು. ಅವರದರಲ್ಲಿ ಚಿರವಾಸಿಗಳು.');
INSERT INTO "kannada" VALUES(2,83,'2:83','“ಅಲ್ಲಾಹನಿಗಲ್ಲದೆ ಅನ್ಯರಿಗೆ ಆರಾಧಿಸದಿರಿ, ಹೆತ್ತವರೊಡನೆ ಉದಾರಿಗಳಾಗಿರಿ, ಬಂಧುಗಳು, ತಬ್ಬಲಿಗಳು ಹಾಗೂ ಬಡವರಿಗೆ ಉಪಕಾರ ಮಾಡಿರಿ, ಜನರೊಡನೆ ಹಿತವಾಗಿ ನುಡಿಯಿರಿ, ನಮಾಝ್ ಸ್ಥಿರಗೊಳಿಸಿರಿ, ಝಕಾತ್ ಪಾವತಿಸಿರಿ” ಎಂದು ನಾವು ಇಸ್ರಾಈಲ್ ಸಂತತಿಗಳಿಂದ ಪ್ರತಿಜ್ಞೆ ಪಡೆದ ಸಂದರ್ಭ(ವನ್ನು ಸ್ಮರಿಸಿರಿ). ಆದರೆ ನಿಮ್ಮ ಪೈಕಿ ಕೆಲವರ ಹೊರತು ನೀವೆಲ್ಲರೂ ನಿರ್ಲಕ್ಷ್ಯ ವಹಿಸುತ್ತಾ ವಿಮುಖರಾದಿರಿ.');
INSERT INTO "kannada" VALUES(2,84,'2:84','ನೀವು ಪರಸ್ಪರ ರಕ್ತ ಹರಿಸಬಾರದು, ಸ್ವಜನರನ್ನು ಅವರ ಮನೆಗಳಿಂದ ಹೊರಹಾಕಬಾರದು ಎಂದು ನಿಮ್ಮೊಡನೆ ವಚನ ತೆಗೆದುಕೊಂಡ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ, ನೀವದಕ್ಕೆ ಒಪ್ಪಿಕೊಂಡಿರಿ ಹಾಗೂ ನೀವೇ ಅದಕ್ಕೆ ಸಾಕ್ಷಿಗಳು.');
INSERT INTO "kannada" VALUES(2,85,'2:85','ತರುವಾಯ ನೀವೀಗ ಪರಸ್ಪರ ಕೊಲೆ ನಡೆಸು ತ್ತಿದ್ದೀರಿ. ನಿಮ್ಮದೇ ಒಂದು ವಿಭಾಗದವರ ಮೇಲೆ ಅನ್ಯಾಯ, ವೈರದ ವಿಷಯದಲ್ಲಿ ಪರಸ್ಪರ ಸಹಕಾರ ನೀಡುತ್ತಾ ಜನರನ್ನು ನಿವಾಸಗಳಿಂದ ಹೊರದ ಬ್ಬುತ್ತಿದ್ದೀರಿ. ಅವರು ಯುದ್ಧ ಕೈದಿಗಳಾದಾಗ ಮುಕ್ತಿ ಶುಲ್ಕ ತೆತ್ತು ನೀವೇ ಅವರನ್ನು ಬಂಧಮುಕ್ತಗೊ ಳಿಸುತ್ತಿರುವಿರಿ. ವಾಸ್ತವದಲ್ಲಿ ಅವರನ್ನು ಹೊರ ದಬ್ಬುವುದೇ ನಿಮಗೆ ನಿಷಿದ್ಧವಾಗಿತ್ತು. ನೀವೇನು ವೇದದ ಕೆಲವಲ್ಲಿ ವಿಶ್ವಾಸ ತಾಳಿ ಕೆಲವು ಅಂಶಗಳನ್ನು ಧಿಕ್ಕರಿಸುತ್ತೀರಾ? ನಿಮ್ಮಲ್ಲಿ ಹಾಗೆ ಮಾಡುವವರಿಗೆ ಇಹದಲ್ಲಿ ದೊರೆಯುವ ಫಲವು ಅಪಮಾನವಲ್ಲದೆ ಮತ್ತೇನೂ ಅಲ್ಲ. ಪರದಲ್ಲಂತೂ ಕಠಿಣತಮ ಶಿಕ್ಷೆಗೆ ಅವರು ಗುರಿಯಾಗುವರು. ನಿಮ್ಮ ಕೃತ್ಯಗಳ ಬಗ್ಗೆ ಅಲ್ಲಾಹನು ಅಶ್ರದ್ಧನಲ್ಲ');
INSERT INTO "kannada" VALUES(2,86,'2:86','ಅವರೇ ಪರಲೋಕದ ಬದಲಿಗೆ ಇಹದ ಬಾಳನ್ನು ಕೊಂಡುಕೊಂಡವರು. ಅವರಿಗೆ ಶಿಕ್ಷೆ ಸಡಿಲ ವಾಗದು. ಯಾವ ನೆರವೂ ಅವರಿಗೆ ದೊರೆಯದು.');
INSERT INTO "kannada" VALUES(2,87,'2:87','ಮೂಸಾರವರಿಗೆ ನಾವು ವೇದಗ್ರಂಥವನ್ನು ನೀಡಿದೆವು. ಅವರ ಅನಂತರ ನಿರಂತರವಾಗಿ ಪ್ರವಾದಿಗಳನ್ನು ಕಳುಹಿಸಿದೆವು. ಮರ್ಯಮಳ ಪುತ್ರ ಈಸಾರವರಿಗೆ ಸುವ್ಯಕ್ತ ಪ್ರಮಾಣಗಳನ್ನು ನೀಡಿದೆವು. ಪರಿಶುದ್ಧಾತ್ಮರ ಮೂಲಕ ಅವರಿಗೆ ಬಲವೃದ್ಧಿ ನೀಡಿದೆವು. ನೀವಾದರೋ ನಿಮ್ಮ ತನ್ನಿಚ್ಛೆಗೆ ವಿರೋಧ ವಾದ ಆದರ್ಶಗಳೊಂದಿಗೆ ಪ್ರವಾದಿಗಳು ಬಂದಾಗ ಲೆಲ್ಲಾ ಅಹಂಭಾವ ತೋರಿದಿರಿ. ಕೆಲವರನ್ನು ಸುಳ್ಳು ಮಾಡಿದಿರಿ. ಇನ್ನು ಕೆಲವರನ್ನು ನೀವು ಹತ್ಯೆಗೈದಿರಿ.');
INSERT INTO "kannada" VALUES(2,88,'2:88','‘ನಮ್ಮ ಹೃದಯಗಳು ಮುಚ್ಚಲ್ಪಟ್ಟಿವೆ’ ಎಂದವರು ಹೇಳಿದರು. ವಾಸ್ತವ ಅದಲ್ಲ. ಅವರ ಸತ್ಯನಿಷೇಧದ ಕಾರಣಕ್ಕೆ ಅವರಿಗೆ ಅಲ್ಲಾಹನ ಶಾಪ ತಟ್ಟಿದೆ. ಆದ್ದರಿಂದ ಅವರ ಪೈಕಿ ಕೆಲವರು ಮಾತ್ರ ವಿಶ್ವಾಸಿಗಳಾಗುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(2,89,'2:89','ಅವರ ಕೈವಶವಿದ್ದ ವೇದಗ್ರಂಥವನ್ನು ದೃಢೀಕರಿಸುವ ಒಂದು ಗ್ರಂಥ (ಖುರ್‍ಆನ್) ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ಅವರಿಗೆ ಬಂದಾಗ (ಅವರದನ್ನು ತಳ್ಳಿ ಹಾಕಿದರು). ಆದರೆ ಇದಕ್ಕೂ ಮುನ್ನ ಅವರು ಸತ್ಯನಿಷೇಧಿಗಳ ವಿರುದ್ಧ ಜಯಕ್ಕಾಗಿ ಪ್ರಾರ್ಥಿಸುತ್ತಿದ್ದರು . ಆದರೆ ಅವರಿಗೆ ಮೊದಲೇ ಗೊತ್ತಿದ್ದ ವೇದಗ್ರಂಥ ಬಂದಾಗ ನಿಷೇಧಿಸಿಬಿಟ್ಟರು. ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಅಲ್ಲಾಹನ ಶಾಪವಿದೆ.');
INSERT INTO "kannada" VALUES(2,90,'2:90','ಅಲ್ಲಾಹನು ಅವತೀರ್ಣಗೊಳಿಸಿದ ವೇದಗ್ರಂಥದ ನಿಷೇಧಕ್ಕೆ ತಮ್ಮನ್ನೇ ಮಾರಿಕೊಂಡ ಅವರ ಈ ವ್ಯವಹಾರ ಅದೆಷ್ಟು ನಿಕೃಷ್ಟ! ಅಲ್ಲಾಹನು ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಅನುಗ್ರಹಿಸುವುದರಲ್ಲಿ ಅವರಿಗಿರುವ ಮತ್ಸರವೇ ಇದಕ್ಕೆ ಕಾರಣ . ಆದ್ದರಿಂದ ಅವರು ದುಪ್ಪಟ್ಟು ಕ್ರೋಧಕ್ಕೆ ತುತ್ತಾದರು. ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಅವಮಾನಕರ ಶಿಕ್ಷೆ ಕಾದಿದೆ .');
INSERT INTO "kannada" VALUES(2,91,'2:91','‘ಅಲ್ಲಾಹು ಅವತೀರ್ಣಗೊಳಿಸಿದ ವಿಚಾರವನ್ನು ನಂಬಿರಿ’ ಎಂದು ಅವರಿಗೆ ಹೇಳಿದರೆ, “ನಮ್ಮ ಮೇಲೆ ಅವತೀರ್ಣಗೊಂಡದ್ದನ್ನು ಮಾತ್ರ ನಾವು ನಂಬುತ್ತಿದ್ದೇವೆ” ಎನ್ನುತ್ತಾರೆ. ಅದರ ಹೊರತಾದುದನ್ನು ನಿಷೇಧಿಸುತ್ತಾರೆ. ನಿಜದಲ್ಲಿ ಈ ದೇವವಾಣಿಯು ಅವರ ಬಳಿಯಿರುವ ಗ್ರಂಥವನ್ನು ಪುಷ್ಠೀಕರಿಸುವ ಸತ್ಯವಾಗಿದೆ. (ಪ್ರವಾದಿಯರೇ) ಕೇಳಿರಿ, ನೀವು (ನಿಮ್ಮ ವೇದವನ್ನು ಮಾತ್ರ) ನಂಬಿದವರಾಗಿದ್ದರೆ, ಇದಕ್ಕಿಂತ ಮುಂಚೆ ಅಲ್ಲಾಹನ ಪ್ರವಾದಿಗಳನ್ನು ವಧಿಸಿದ್ದೇಕೆ?');
INSERT INTO "kannada" VALUES(2,92,'2:92','ಸ್ಪಷ್ಟ ಪುರಾವೆಗಳೊಂದಿಗೆ ನಿಮ್ಮ ಬಳಿಗೆ ಮೂಸಾ ಬಂದರು. ಆದರೂ ಅವರ ಉಪಸ್ಥಿತಿಯಲ್ಲಿ ನೀವು ಅನ್ಯಾಯವಾಗಿ ಕರುವಿಗೆ ಪೂಜಿಸಿದಿರಿ.');
INSERT INTO "kannada" VALUES(2,93,'2:93','ನಿಮ್ಮ ಮೇಲೆ ನಾವು ತೂರ್ ಬೆಟ್ಟವನ್ನು ಎತ್ತಿ ಹಿಡಿದು ನಿಮ್ಮಿಂದ ಧೃಢ ಪ್ರತಿಜ್ಞೆ ಮಾಡಿಸಲಾದ ಸಂದರ್ಭ (ವನ್ನು ನೆನೆಸಿರಿ). ‘ನಿಮಗೆ ನಾವಿತ್ತುದನ್ನು ಬಲವಾಗಿ ಅವಲಂಬಿಸಿರಿ. ನನ್ನ ಆದೇಶಗಳನ್ನು ಆಲಿಸಿರಿ’ ಎಂದು ನಾವು ಹೇಳಿದಾಗ ಅವರು, ‘ನಾವು ಆಲಿಸಿದೆವು, ಆದರೆ ತಿರಸ್ಕರಿಸಿದೆವು’ ಎಂದರು. ಸತ್ಯನಿಷೇಧದ ಫಲವಾಗಿ ಅವರ ಮನದಲ್ಲಿ ಕರು ನೆಲೆಯೂರಿತ್ತು . ಹೇಳಿರಿ; ನೀವು ವಿಶ್ವಾಸಿಗಳಾಗಿದ್ದರೆ ನಿಮ್ಮ ವಿಶ್ವಾಸವು ನಿಮಗೆ ಅದೆಷ್ಟು ನೀಚವಾದುದನ್ನು ಆಜ್ಞಾಪಿಸುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(2,94,'2:94','ಪರಲೋಕವು ಇತರರಿಗಲ್ಲ, ನಿಮಗೆ ಮಾತ್ರ ಎಂಬ ನಿಮ್ಮ ವಾದವು ನಿಜವಾಗಿದ್ದರೆ ನೀವು ಮರಣಕ್ಕಾಗಿ ಹಾತೊರೆಯಿರಿ ಎಂದವರಲ್ಲಿ ಹೇಳಿರಿ.');
INSERT INTO "kannada" VALUES(2,95,'2:95','ಆದರೆ ಅವರ ಕೈಗಳು ಎಸಗಿದ ಪಾಪಗಳ ಪರಿಣಾಮವಾಗಿ ಅವರು ಎಂದಿಗೂ ಮರಣವನ್ನು ಖಂಡಿತ ಆಶಿಸಲಾರರು. ಅಲ್ಲಾಹನು ಅಕ್ರಮಿಗಳ ಕುರಿತು ಚೆನ್ನಾಗಿ ಬಲ್ಲವನು.');
INSERT INTO "kannada" VALUES(2,96,'2:96','ನಿಜದಲ್ಲಿ ಅವರು ಜನರ ಪೈಕಿ ಬಹು ದೇವಾರಾಧ ಕರಿಗಿಂತಲೂ ಹೆಚ್ಚು ಜೀವನದಲ್ಲಿ ಆಶೆ ಬುರು ಕರಾಗಿರುವುದನ್ನು ನೀವು ಕಾಣುವಿರಿ. ಸಾವಿರ ವರ್ಷ ಬದುಕಬೇಕೆಂದು ಅವರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬರೂ ಆಶಿಸುತ್ತಾರೆ. ಆದರೆ ದೀರ್ಘಾಯುಷ್ಯವು ಅವರನ್ನು ಶಿಕ್ಷೆಯಿಂದ ಪಾರು ಮಾಡದು. ಅವರ ಕೃತ್ಯಗಳನ್ನೆಲ್ಲ ಅಲ್ಲಾಹನು ಸೂಕ್ಷ್ಮವಾಗಿ ಕಾಣುತ್ತಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(2,97,'2:97','ಹೇಳಿರಿ, ಯಾರು ಜಿಬ್ರೀಲರ ಶತ್ರುವಾಗಿರುವರೋ, ಅಂಥವರು ತಿಳಿದಿರಲಿ, ಪೂರ್ವವೇದಗಳನ್ನು ದೃಢೀಕರಿಸುವ, ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಸರಿದಾರಿ ಸೂಚಕ ಹಾಗೂ ಸುವಾರ್ತೆಯಾಗಿರುವ ಈ ಸಂದೇಶವನ್ನು ಅಲ್ಲಾಹನ ಅಪ್ಪಣೆ ಮೇರೆಗೆ ನಿಮ್ಮ ಮನದಾಳಕ್ಕೆ ಅವಾಹನೆ ಮಾಡಿದವರು ಜಿಬ್ರೀಲರು ಎಂದು .');
INSERT INTO "kannada" VALUES(2,98,'2:98','ಯಾರಾದರೂ ಅಲ್ಲಾಹು, ಅವನ ಮಲಾಇಕತ್, ಆತನ ಪ್ರವಾದಿಗಳು, ಜಿಬ್‍ರೀಲ್ ಹಾಗೂ ವಿೂಖಾಈಲರ ಶತ್ರುವಾಗಿದ್ದರೆ, ತಿಳಿದಿರಲಿ. ಅಲ್ಲಾಹನು ಸತ್ಯನಿಷೇಧಿಗಳ ವೈರಿಯಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(2,99,'2:99','ಸುಸ್ಪಷ್ಟ ಪ್ರಮಾಣಗಳನ್ನು ನಿಮಗೆ ನಾವು ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದೇವೆ. ಇವುಗಳನ್ನು ನಿಷೇಧಿಸುವವರು ದುಷ್ಕರ್ಮಿಗಳು ಮಾತ್ರ.');
INSERT INTO "kannada" VALUES(2,100,'2:100','ಅವರು ಒಪ್ಪಂದ ಮಾಡಿಕೊಂಡಾಗಲೆಲ್ಲ ಅವರಲ್ಲೊಂದು ವಿಭಾಗ ಅದನ್ನು ಮುರಿದಿದ್ದಾರೆ. ಹೌದು, ಅವರ ಪೈಕಿ ಹೆಚ್ಚಿನವರು ವಿಶ್ವಾಸಿಗಳಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,101,'2:101','ಅವರ ಬಳಿಯಿರುವ ಸತ್ಯಗಳನ್ನು ಪುಷ್ಠೀಕರಿಸುತ್ತಾ ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ಪ್ರವಾದಿಯೊಬ್ಬರ ಆಗಮನವಾದಾಗ (ಅಲ್ಲಾಹನಿಂದ) ವೇದ ಪ್ರಾಪ್ತರಾದವರಿಂದಲೇ ಒಂದು ಪಂಗಡದವರು ಏನೋ ಗೊತ್ತಿಲ್ಲದ ಭಾವದಲ್ಲಿ ಅಲ್ಲಾಹನ ಗ್ರಂಥವನ್ನು ಬೆನ್ನ ಹಿಂದೆ ಎಸೆದರು.');
INSERT INTO "kannada" VALUES(2,102,'2:102','ಸುಲೈಮಾನರ ರಾಜತ್ವದ ಹೆಸರಲ್ಲಿ ಶೈತಾನರು ವಾಚಿಸುತ್ತಿದ್ದುದನ್ನು ಇವರು ಅನುಗಮಿಸಿದರು. ಸುಲೈಮಾನರು ‘ಕುಫ್ರ್’ (ದೇವನಿಷೇಧ) ಮಾಡಿಲ್ಲ. ಕುಫ್ರ್ ಮಾಡಿದ್ದು ಜನರಿಗೆ ವಾಮಾಚಾರ ಕಲಿಸುತ್ತಿದ್ದ ಶೈತಾನರು. ಬಾಬಿಲೋನಿಯಾದಲ್ಲಿ ಹಾರೂತ್, ಮಾರೂತ್ ಎಂಬೆರಡು ಮಲಕ್‍ಗಳಿಗೆ ಅವಾಹಿಸಲಾಗಿದ್ದುದರ ಬೆನ್ನ ಹಿಂದೆ ಇವರು ಬಿದ್ದರು. ‘ನಾವೊಂದು ಸತ್ವಪರೀಕ್ಷೆ, ನೀನು ಭ್ರಷ್ಟನಾಗದಿರು’ ಎಂದು ಹೇಳದೆ ಆ ಮಲಕ್‍ಗಳು ಯಾವೊಬ್ಬನಿಗೂ ಮಾಟ ಕಲಿಸಲಿಲ್ಲ . ಹಾಗೆ ಅವರಿಬ್ಬರಿಂದ ಸತಿ-ಪತಿಯರಲ್ಲಿ ಒಡಕು ಹುಟ್ಟಿಸುವಂತಹದ್ದನ್ನು ಅವರು ಕಲಿಯುವರು. ಆದರೆ ಅದರಿಂದ ಅಲ್ಲಾಹನ ಅನುಮತಿಯ ವಿನಾ ಯಾವೊಬ್ಬನನ್ನೂ ಪೀಡಿ ಸಲು ಅವರಿಂದಾಗದು. ಅವರಿಗೆ ಸ್ವತಃ ಗುಣ ವಲ್ಲದ ಹಾಗೂ ಅವಗುಣವಾಗುವುದನ್ನು ಅವರು ಕಲಿಯುತ್ತಿದ್ದರು. ಅದನ್ನು ಕೊಂಡು ಕೊಂಡವನಿಗೆ ಪರಲೋಕದಲ್ಲಿ ಯಾವುದೇ ಭಾಗ್ಯವಿಲ್ಲ ವೆಂಬುದೂ ಅವರಿಗೆ ಗೊತ್ತು. ಅವರು ತಮ್ಮ ಸ್ವಹಿತದ ಬದಲಿಗೆ ಕೊಂಡುಕೊಂಡುದು ಅದೆಷ್ಟು ನಿಕೃಷ್ಟ ! ಅವರಿಗಿದು ಮನದಟ್ಟಾಗಿದ್ದರೆ!');
INSERT INTO "kannada" VALUES(2,103,'2:103','(ಇದಕ್ಕಿಂತ) ಅವರು ಸತ್ಯವಿಶ್ವಾಸ ಮತ್ತು ಧರ್ಮ ಶ್ರದ್ಧೆಯನ್ನು ಪಾಲಿಸುತ್ತಿದ್ದರೆ, ಅಲ್ಲಾಹನ ಬಳಿಯಿಂದ ಅವರಿಗೊದಗುತ್ತಿದ್ದ ಸತ್ಫಲ ಎಷ್ಟು ಶ್ರೇಷ್ಠವಿತ್ತು. ಅವರಿಗದು ಗ್ರಾಹ್ಯವಾಗಿದ್ದರೆ ಎಷ್ಟು ಚೆನ್ನಾಗಿತ್ತು.');
INSERT INTO "kannada" VALUES(2,104,'2:104','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನೀವು ರಾಇನಾ ಎನ್ನಬೇಡಿ. ಉನ್‍ಳುರ್ನಾ ಅನ್ನಿರಿ. ಗಮನವಿಟ್ಟು ಆಲಿಸಿರಿ. ನಿಷೇಧಿಗಳಿಗೆ ಯಾತನಾಮಯ ಶಿಕ್ಷೆಯಿದೆ ');
INSERT INTO "kannada" VALUES(2,105,'2:105','ವೇದದವರೂ ಬಹುದೇವೋಪಾಸಕರೂ ನಿಮಗೆ ಪ್ರಭುವಿನಿಂದ ಯಾವುದೇ ಗುಣಾವರೋಹಣವನ್ನು ಇಷ್ಟಪಡುವುದಿಲ್ಲ. ಅಲ್ಲಾಹನು ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ತನ್ನ ವರದಾನಕ್ಕೆ ಆರಿಸಿಕೊಳ್ಳುತ್ತಾನೆ. ಅಲ್ಲಾಹನು ಘನವೆತ್ತ ಉದಾರಿಯು.');
INSERT INTO "kannada" VALUES(2,106,'2:106','ನಾವು ಯಾವುದೇ ವಚನವನ್ನು ರದ್ದುಪಡಿಸಿದರೆ ಅಥವಾ ವಿಸ್ಮರಣೆ ಮಾಡಿಸಿದ್ದರೆ ಅದಕ್ಕಿಂತ ಶ್ರೇಷ್ಟವಾದುದನ್ನು ಇಲ್ಲವೇ ಸಮಾನವಾದುದನ್ನು ತರುತ್ತೇವೆ. ಅಲ್ಲಾಹನು ಸರ್ವಸಮರ್ಥನೆಂಬುದು ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲವೇ? ');
INSERT INTO "kannada" VALUES(2,107,'2:107','ಭೂಮ್ಯಾಕಾಶಗಳ ಒಡೆತನ ಅಲ್ಲಾಹನದ್ದಾಗಿದ್ದು ಅವನ ಹೊರತು ನಿಮಗೆ ರಕ್ಷಕನೂ ಸಹಾಯಕನೂ ಯಾರು ಇಲ್ಲವೆಂಬುದು ನಿಮಗೆ ತಿಳಿದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(2,108,'2:108','ಹಿಂದೆ ಮೂಸಾರಲ್ಲಿ ಪ್ರಶ್ನಿಸಲ್ಪಟ್ಟಂತೆ ನೀವೂ ನಿಮ್ಮ ಪ್ರವಾದಿಯೊಂದಿಗೆ ಪ್ರಶ್ನಿಸಲಿಚ್ಛಿಸುತ್ತಿದ್ದೀರಾ? ಸತ್ಯವಿಶ್ವಾಸದ ಬದಲಿಯಾಗಿ ಅವಿಶ್ವಾಸವನ್ನು ಕೈಗೆತ್ತಿಕೊಂಡವನು ಖಂಡಿತವಾಗಿಯೂ ಸರಿದಾರಿ ಯಿಂದ ದೂರ ಸರಿದನು .');
INSERT INTO "kannada" VALUES(2,109,'2:109','ನೀವು ಸತ್ಯವಿಶ್ವಾಸ ಸ್ವೀಕರಿಸಿದ ನಂತರ ನಿಮ್ಮನ್ನು ಸತ್ಯನಿಷೇಧಕ್ಕೆ ಮರಳಿಸಬೇಕೆಂದು ವೇದದವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಇಚ್ಛಿಸುತ್ತಿದ್ದಾರೆ. ಸತ್ಯ ಗೊತ್ತಿದ್ದೂ ಕೂಡಾ ಅವರು ಹೀಗೆ ಮಾಡುತ್ತಿರುವುದು ಅವರ ಸ್ವಂತದ ಮತ್ಸರದಿಂದಾಗಿದೆ. ಅಲ್ಲಾಹನು ಕ್ರಮ (ಆದೇಶ) ಕೈಗೊಳ್ಳುವವರೆಗೆ ಕ್ಷಮಿಸಿರಿ, ಮನ್ನಿಸಿರಿ. ಖಂಡಿತ ಅಲ್ಲಾಹನು ಸರ್ವಸಮರ್ಥನಿರುವನು.');
INSERT INTO "kannada" VALUES(2,110,'2:110','ನಮಾಝನ್ನು ಖಾಯಂಗೊಳಿಸಿರಿ. ಝಕಾತ್ ಪಾವತಿಸಿರಿ. ನಿಮಗಾಗಿ ನೀವು ಮುಂಗಡವಾಗಿ ಮಾಡಿಡುವ ಪುಣ್ಯಗಳನ್ನು ಅಲ್ಲಾಹನ ಬಳಿ ಪಡೆಯಲಿರುವಿರಿ. ನಿಮ್ಮ ಪ್ರವರ್ತಿಗಳನ್ನು ಅಲ್ಲಾಹನು ಚೆನ್ನಾಗಿ ಕಾಣುತ್ತಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(2,111,'2:111','ಯಹೂದನೋ ಕ್ರೈಸ್ತನೋ ಆಗದೆ ಸ್ವರ್ಗಪ್ರವೇಶ ಅಸಾಧ್ಯವೆಂದು ಅವರು ಹೇಳುತ್ತಾರೆ. ಅದು ಅವರ ವ್ಯಾಮೋಹ. ‘ನೀವು ಸತ್ಯವಂತರಾಗಿದ್ದರೆ ನಿಮ್ಮ ಪ್ರಮಾಣಗಳನ್ನು ತನ್ನಿರಿ’ ಎಂದು ಹೇಳಿರಿ.');
INSERT INTO "kannada" VALUES(2,112,'2:112','ಹಾಗಲ್ಲ. ಯಾವನು ಧರ್ಮಪರಾಯಣನಾಗಿ ಅಲ್ಲಾಹನತ್ತ ಶರಣೋನ್ಮುಖನಾಗುತ್ತಾನೋ, ಅವನಿಗೆ ತನ್ನೊಡೆಯನ ಬಳಿ ಪ್ರತಿಫಲವಿದೆ. ಅವರಿಗೆ ಭಯ, ವ್ಯಸನಗಳಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,113,'2:113','ಕ್ರೈಸ್ತರಿಗೆ ಯಾವುದೇ ನೆಲೆಗಟ್ಟಿಲ್ಲವೆಂದು ಯಹೂದ್ಯರು ಹೇಳುತ್ತಾರೆ. ಯಹೂದ್ಯರಿಗೆ ಯಾವುದೇ ನೆಲೆಗಟ್ಟಿಲ್ಲವೆಂದು ಕ್ರೈಸ್ತರು ಹೇಳುತ್ತಾರೆ. ವಸ್ತುತಃ ಉಭಯತ್ರರೂ ವೇದ ಓದುತ್ತಲೂ ಇದ್ದಾರೆ. ಗ್ರಂಥಜ್ಞಾನ ಇಲ್ಲದವರೂ ಕೂಡಾ ಇವರಂತೆಯೇ ಹೇಳಿದ್ದಾರೆ. ಇವರು ಹೊಂದಿರುವ ಭಿನ್ನಾಭಿಪ್ರಾಯ ವಿವಾದಗಳಿಗೆ ಪರಲೋಕದಲ್ಲಿ ಅಲ್ಲಾಹನು ತೀರ್ಪು ನೀಡುವನು.');
INSERT INTO "kannada" VALUES(2,114,'2:114','ಅಲ್ಲಾಹನ ಮಸೀದಿಗಳಲ್ಲಿ ಆತನ ನಾಮ ಸ್ಮರಣೆಯನ್ನು ತಡೆಯುವ ಹಾಗೂ ಅವುಗಳ ಅವನತಿಗೆ ಪ್ರಯತ್ನಿಸಿದವರಿಗಿಂತ ದೊಡ್ಡ ಅಕ್ರಮಿಗಳು ಬೇರಿಲ್ಲ. ಅವರು ನಿಜದಲ್ಲಿ ಭಯಚಿತ್ತರಾಗದೆ ಮಸೀದಿಯೊಳಗೆ ಪ್ರವೇಶಿಸ ಬಾರದವರು. ಅವರಿಗೆ ಇಹದಲ್ಲಿ ನಿಂದ್ಯತೆಯೂ, ಪರದಲ್ಲಿ ಘೋರ ಶಿಕ್ಷೆಯೂ ಕಾದಿದೆ.');
INSERT INTO "kannada" VALUES(2,115,'2:115','ಪೂರ್ವಪಶ್ಚಿಮಗಳು ಅಲ್ಲಾಹನವು. ನೀವು ಯಾವ ಕಡೆ ಮುಖ ಮಾಡಿದರೂ ಅಲ್ಲಿ ಅಲ್ಲಾಹನ ಅನುಗ್ರಹ ಸಾನಿಧ್ಯವಿದೆ. ಅಲ್ಲಾಹನು ಪರಮವಿಶಾಲನು. ಪರಮತಜ್ಞನು.');
INSERT INTO "kannada" VALUES(2,116,'2:116','ಅಲ್ಲಾಹನು ಪುತ್ರರಿಗೆ ಜನ್ಮಕೊಟ್ಟಿರುತ್ತಾನೆ ಎಂದರವರು. ಅಲ್ಲಾಹನು ಪರಿಶುದ್ಧನು. ಭುವಿ- ಗಗನಗಳ ಅಧಿಪತಿ ಆತ. ಎಲ್ಲವೂ ಆತನಿಗೆ ವಿಧೇಯ.');
INSERT INTO "kannada" VALUES(2,117,'2:117','ಅವನು ಆಗಸಗಳು ಮತ್ತು ಭೂಮಿಯ ಮೂಲ ನಿರ್ಮಾತೃ. ತಾನೊಂದನ್ನು ನಿರ್ಧರಿಸಿದರೆ ‘ಆಗು’ ಎನ್ನುವನು. ಆಗ ಅದು ಸೃಷ್ಟಿಯಾಗುವುದು .');
INSERT INTO "kannada" VALUES(2,118,'2:118','ಅಲ್ಲಾಹು ನಮ್ಮೊಡನೆ ಯಾಕೆ ಮಾತಾಡುವುದಿಲ್ಲ ಅಥವಾ ಏನಾದರೊಂದು ಪುರಾವೆಯನ್ನು ಯಾಕೆ ನೀಡುವುದಿಲ್ಲ? ಎಂದು ಅಜ್ಞಾನಿಗಳು ಕೇಳುತ್ತಾರೆ. ಇದೇ ಮಾತನ್ನು ಇವರ ಹಿಂದಿನವರೂ ಹೇಳಿದ್ದರು. ಇವರೆಲ್ಲರ ಅಂತರಂಗವು ಒಂದೇ ತೆರನಾಗಿ ಬಿಟ್ಟಿದೆ. ವಿಶ್ವಾಸ ದೃಢತೆಯಿರುವವರಿಗೆ ನಾವು ದೃಷ್ಟಾಂತಗಳನ್ನು ಸ್ಪಷ್ಟಪಡಿಸಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(2,119,'2:119','ನಿಶ್ಚಯವಾಗಿಯೂ ನಾವು ನಿಮ್ಮನ್ನು ಸತ್ಯ ದೊಂದಿಗೆ ಸುವಾರ್ತೆಗಾರನಾಗಿ, ಮುನ್ನೆಚ್ಚರಿಕೆದಾ ರನಾಗಿ ಕಳುಹಿಸಿರುತ್ತೇವೆ. ನರಕದವರ ಕುರಿತು ನಿಮ್ಮನ್ನು ಪ್ರಶ್ನಿಸಲಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,120,'2:120','ಯಹೂದ್ಯರು ಮತ್ತು ಕ್ರೈಸ್ತರು, ಅವರ ಧರ್ಮವನ್ನು ನೀವು ಅನುಸರಿಸುವವರೆಗೂ ನಿಮ್ಮ ಬಗ್ಗೆ ತೃಪ್ತರಾಗರು. ಅಲ್ಲಾಹನ ದಾರಿಯೇ ನಿಜವಾದ ದಾರಿ ಎಂದವರಿಗೆ ಹೇಳಿರಿ. ನಿಮಗೆ ದಿವ್ಯಜ್ಞಾನ ದೊರೆತ ನಂತರವೂ ನೀವು ಅವರ ಇಚ್ಚೆಗಳನ್ನು ಅನುಸರಿಸಿದಲ್ಲಿ ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆಯಿಂದ ಪಾರು ಮಾಡುವ ರಕ್ಷಕನಾಗಲಿ, ಸಹಾಯಕನಾಗಲಿ ನಿಮಗಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,121,'2:121','ನಾವು ದಯಪಾಲಿಸಿದ ಗ್ರಂಥವನ್ನು ಓದುವ ಕ್ರಮದಂತೆ ಓದಿದವರು ಅದರಲ್ಲಿ ವಿಶ್ವಾಸ ಹೊಂದುತ್ತಾರೆ . ಅದರಲ್ಲಿ ಯಾರು ಅವಿಶ್ವಾಸ ವಿಡುತ್ತಾರೋ ಅವರೇ ಪರಾಜಿತರು.');
INSERT INTO "kannada" VALUES(2,122,'2:122','ಓ ಇಸ್ರಾಈಲ್ ಸಂತತಿಗಳೇ! ನಿಮಗೆ ನಾನಿತ್ತ ನೇಮತ್ತನ್ನು ಜ್ಞಾಪಿಸಿಕೊಳ್ಳಿರಿ. (ನಿಮ್ಮ ಕಾಲದ) ಜಗತ್ತಿನ ಎಲ್ಲ ಜನಾಂಗಗಳ ಪೈಕಿ ನಿಮಗೆ ನಾನು ಶ್ರೇಷ್ಠತೆ ದಯಪಾಲಿಸಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(2,123,'2:123','ಯಾವನಿಂದಲೂ ಯಾವನಿಗೂ ಫಲ ದೊರೆಯದ, ಯಾರಿಂದಲೂ ಏನೇ ಪರಿಹಾರವನ್ನು ಸ್ವೀಕರಿಸದ, ಯಾರ ಶಿಫಾರಸ್ಸೂ ಪ್ರಯೋಜನ ವಾಗದ ಮತ್ತು ಯಾರಿಗೂ ನೆರವು ಸಿಗದಂತಹ ಒಂದು ದಿನದ ಬಗ್ಗೆ ಜಾಗ್ರತೆಯಿರಲಿ.');
INSERT INTO "kannada" VALUES(2,124,'2:124','ಇಬ್ರಾಹೀಮರನ್ನು ತನ್ನ ಪ್ರಭು ಕೆಲವು ಆದೇಶ ವಾಕ್ಯಗಳ ಮೂಲಕ ಪರೀಕ್ಷಿಸಿದ ಸಂದರ್ಭ. ಅವರು ಅದರಲ್ಲಿ ತೇರ್ಗಡೆಯಾದರು. ಅಲ್ಲಾಹು ಹೇಳಿದನು; ನಾನು ನಿಮ್ಮನ್ನು ಜನರಿಗೆ ನಾಯಕನಾಗಿ ಮಾಡುತ್ತೇನೆ. ಅವರು ವಿನಂತಿಸಿಕೊಂಡರು. ‘ನನ್ನ ಸಂತತಿಗಳಿಗೂ ನೀಡಬೇಕು.’ ಅಲ್ಲಾಹು ಹೇಳಿದನು. ‘ನನ್ನ ವಾಗ್ದಾನವು ದುಷ್ಕರ್ಮಿಗಳಿಗೆ ದೊರೆಯದು’ .');
INSERT INTO "kannada" VALUES(2,125,'2:125','ಕಅಬಾ ಭವನವನ್ನು ಜನರ ಶ್ರದ್ಧಾಕೇಂದ್ರ ಮತ್ತು ಶಾಂತಿನಿಕೇತನವಾಗಿ ನಾವು ನಿಶ್ಚಯಿಸಿದ ಸಂದರ್ಭ. ‘ಇಬ್ರಾಹೀಮರು ನಿಂತ ಜಾಗವನ್ನು ನಮಾಝ್‍ನ ತಾಣವಾಗಿ ಮಾಡಿರಿ’ ಎಂದು ನಾವು ಆದೇಶ ಕೊಟ್ಟೆವು. ನನ್ನ ಮಂದಿರವನ್ನು ಪ್ರದಕ್ಷಿಣೆಗಾರರು, ಧ್ಯಾನಾಸೀನರು, ಶಿರವಂದಕರು ಹಾಗೂ ಸಾಷ್ಟಾಂಗವೆರಗುವವರ ಸಲುವಾಗಿ ಸಂಶುದ್ಧ ಗೊಳಿಸಿರಿ ಎಂದು ಇಬ್ರಾಹೀಮ್ ಮತ್ತು ಇಸ್ಮಾಈಲರಿಗೆ ವಚನಾದೇಶ ನೀಡಿದೆವು.');
INSERT INTO "kannada" VALUES(2,126,'2:126','ಪ್ರಭೂ! ಈ ಪ್ರದೇಶವನ್ನು ಅಭಯ ನಗರವಾಗಿ ಮಾಡು. ಇಲ್ಲಿನ ನಿವಾಸಿಗಳ ಪೈಕಿ ಅಲ್ಲಾಹು ಮತ್ತು ಅಂತಿಮ ದಿನದಲ್ಲಿ ವಿಶ್ವಾಸವಿಡುವವರಿಗೆ ಫಲಕಾಯಿಗಳನ್ನು ಆಹಾರವಾಗಿ ನೀಡು’ ಎಂದು ಇಬ್ರಾಹೀಮರು ಪ್ರಾರ್ಥಿಸಿದ ಸಂದರ್ಭ. ಆಗ ಅಲ್ಲಾಹನು ‘ಅವಿಶ್ವಾಸಿಗಳಿಗೂ ತಾತ್ಕಾಲಿಕ ಜೀವನಾಧಾರ ಕೊಡುವೆನು. ಆಮೇಲೆ ಅವನನ್ನು ನರಕ ಶಿಕ್ಷೆಗೆ ತಳ್ಳಿ ಬಿಡುವೆನು, ಬಹಳ ನಿಕೃಷ್ಟ ನೆಲೆಯದು’ ಎಂದನು.');
INSERT INTO "kannada" VALUES(2,127,'2:127','ಇಬ್ರಾಹೀಮ್ ಮತ್ತು ಇಸ್ಮಾಈಲರು ಕಅಬಾದ ಅಸ್ಥಿವಾರವನ್ನು ಏರಿಸುತ್ತಿದ್ದ ಸಂದರ್ಭದಲ್ಲಿ ‘ನಮ್ಮ ಪ್ರಭುವೇ! ಈ ಸೇವೆಯನ್ನು ನಮ್ಮಿಂದ ಸ್ವೀಕರಿಸು, ನೀನು ಪರಮಶ್ರೇಷ್ಠನೂ, ಪರಮ ಜ್ಞಾನಿಯೂ ಆಗಿರುವೆ’ ಎಂದು ಪ್ರಾರ್ಥಿಸಿದರು.');
INSERT INTO "kannada" VALUES(2,128,'2:128','ನಮ್ಮ ಪ್ರಭೂ! ನಮ್ಮಿಬ್ಬರನ್ನು ನಿನ್ನ ಆಜ್ಞಾನು ವರ್ತಿಗಳಾಗಿ ಸ್ವೀಕರಿಸು, ನಮ್ಮ ಸಂತತಿ ಗಳಿಂದಲೇ ನಿನ್ನ ಆಜ್ಞಾನು ಸಾರಿಗಳಾದ ಒಂದು ಸಮುದಾಯವನ್ನು ರೂಪಿಸು. ನಮಗೆ ನಮ್ಮ ಉಪಾಸನಾ ಕ್ರಮಗಳನ್ನು ಕಾಣಿಸಿಕೊಡು. ನಮ್ಮ ಮರುಗುವಿಕೆಯನ್ನು ಸ್ವೀಕರಿಸು. ನೀನು ಪರಮ ಪಾಪನಾಶಕನೂ ದಯಾವಾರಿಧಿಯೂ ಆಗಿರುವೆ');
INSERT INTO "kannada" VALUES(2,129,'2:129','ನಮ್ಮ ಪ್ರಭೂ! ಇವರಿಗೆ ಇವರಿಂದಲೇ ಓರ್ವ ಪ್ರವಾದಿಯನ್ನು ನೇಮಿಸು. ಆ ಪ್ರವಾದಿಯು ಜನರಿಗೆ ನಿನ್ನ ವಚನಾಮೃತಗಳನ್ನು ಓದಿ ಹೇಳಲಿ. ವೇದವನ್ನೂ ಸುಜ್ಞಾನವನ್ನೂ ಅವರಿಗೆ ಕಲಿಸಲಿ. ಅವರನ್ನು ಸಂಸ್ಕರಿಸಲಿ. ನಿಜವಾಗಿಯೂ ನೀನು ಅಜೇಯ. ಪರಮಾಂತಜ್ರ್ಞಾನಿ.');
INSERT INTO "kannada" VALUES(2,130,'2:130','ತನ್ನನ್ನು ತಾನೇ ಮೂಢನಾಗಿ ಮಾಡಿಕೊಂಡವನ ಹೊರತು ಇನ್ಯಾರೂ ಇಬ್ರಾಹೀಮರ ಪಥದಿಂದ ವಿಮುಖನಾಗಲಾರ. ಇಬ್ರಾಹೀಮರನ್ನು ಇಹದಲ್ಲಿ ನಾವು ಸವಿಶೇಷ ಚುನಾಯಿತರನ್ನಾಗಿ ಮಾಡಿದ್ದೆವು. ಪರದಲ್ಲಿ ಅವರು ಸಾತ್ವಿಕರಲ್ಲೊಬ್ಬರು.');
INSERT INTO "kannada" VALUES(2,131,'2:131','ಅವರಲ್ಲಿ ಅವರ ಪ್ರಭುವು ‘ಸರ್ವಾತ್ಮನಾ ವಿಧೇಯನಾಗು’ ಎಂದು ಹೇಳಿದಾಗ ನಾನಿದೋ ವಿಶ್ವಗಳೊಡೆಯನಿಗೆ ಸಂಪೂರ್ಣ ವಿಧೇಯನಾದೆ ಎಂದರು .');
INSERT INTO "kannada" VALUES(2,132,'2:132','ಇದೇ ಪ್ರತಿಜ್ಞೆಯನ್ನು ಇಬ್ರಾಹೀಮರು ತನ್ನ ಸಂತತಿಗಳಿಗೆ ಉಪದೇಶಿಸಿದರು. ಯಅï ಖೂಬರು ಇದನ್ನೇ ಹೇಳುತ್ತಾ, ‘ನನ್ನ ಮಕ್ಕಳೇ, ಅಲ್ಲಾಹನು ನಿಮಗೆ ಈ ಧರ್ಮವನ್ನು ಆಯ್ಕೆಮಾಡಿರುತ್ತಾನೆ. ಆದ್ದರಿಂದ ನೀವು ಮುಸ್ಲಿಮರಾಗದೆ ಮರಣ ಹೊಂದುವಂ ತಾಗಬಾರದು’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(2,133,'2:133','ಯಅïಖೂಬರ ಮರಣಾಸನ್ನ ವೇಳೆ ನೀವಲ್ಲಿ ಹಾಜರಿದ್ದಿರೇನು? ಅವರು ತನ್ನ ಮಕ್ಕಳಲ್ಲಿ, ‘ನನ್ನ ನಂತರ ನೀವು ಯಾರಿಗೆ ಆರಾಧಿಸುವಿರಿ?’ ಎಂದು ಕೇಳಿದಾಗ ‘ನಿಮ್ಮ ಹಾಗೂ ನಿಮ್ಮ ಪಿತೃರಾದ ಇಬ್ರಾಹೀಮ್, ಇಸ್ಮಾಈಲ್, ಇಸ್ಹಾಖ್‍ರವರ ದೇವನಾದ ಏಕದೇವನಿಗೇ ಆರಾಧಿಸುವೆವು. ನಾವು ಅವನಿಗೇ ಶರಣಾಗತ ರಾದವರು’ ಎಂದು ಮಕ್ಕಳು ಉತ್ತರಿಸಿದ್ದರು.');
INSERT INTO "kannada" VALUES(2,134,'2:134','ಆ ಸಮುದಾಯದವರು ಗತಿಸಿ ಹೋದರು. ಅವರ ಸಾಧನೆಗೆ ಅವರಿಗೆ ಪುಣ್ಯವಿದೆ. ನಿಮ್ಮ ಸಾಧನೆಗೆ ನಿಮಗೆ ಪುಣ್ಯ ಂ. ಅವರು ಏನು ಮಾಡುತ್ತಿದ್ದರೆಂದು ನಿಮ್ಮಲ್ಲಿ ಪ್ರಶ್ನಿಸಲಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,135,'2:135','‘ನೀವು ಯಹೂದ್ಯರೋ ಕ್ರೈಸ್ತರೋ ಆಗಿರಿ. ಹಾಗಾದರೆ ನೀವು ಸನ್ಮಾರ್ಗ ಪ್ರಾಪ್ತರಾಗುವಿರಿ’ ಎಂದವರು ಹೇಳಿದರು. ಹೇಳಿರಿ; ‘ಅಲ್ಲ, ಏಕದೇವ ವ್ರತರಾದ ಇಬ್ರಾಹೀಮರ ಪಥವನ್ನು ಅನುಸರಿಸ ಬೇಕು. ಅವರು ಬಹುದೇವಾರಾಧಕರಾಗಿರಲಿಲ್ಲ’.');
INSERT INTO "kannada" VALUES(2,136,'2:136','ಅಲ್ಲಾಹನಲ್ಲಿ, ನಮಗೆ ಅವತೀರ್ಣವಾದುದರಲ್ಲಿ, ಇಬ್ರಾಹೀಮ್, ಇಸ್ಮಾಈಲ್, ಇಸ್‍ಹಾಖ್, ಯಅïಖೂಬ್ ಹಾಗೂ ಅವರ ವಂಶ ಪರಂಪರೆಯವರಿಗೆ ಅವತೀರ್ಣವಾದುದರಲ್ಲಿ, ಮೂಸಾ, ಈಸಾರವರಿಗೂ ಮತ್ತಿತರ ಪ್ರವಾದಿಗಳಿಗೂ ಅವರ ಪ್ರಭುವಿನಿಂದ ದೊರೆತಿರುವ ಆದರ್ಶಗಳಲ್ಲಿ ನಾವು ವಿಶ್ವಾಸವಿಟ್ಟಿರುವೆವು, ಪ್ರವಾದಿಗಳ ಪೈಕಿ ಯಾರೊಬ್ಬರಿಗೂ ನಾವು ಭೇದ ಬಗೆಯುವುದಿಲ್ಲ . ನಾವು ಅಲ್ಲಾಹನ ಆದೇಶಕ್ಕೆ ಬದ್ಧರಾಗಿದ್ದೇವೆ’ ಎಂದು ಘೋಷಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(2,137,'2:137','ನೀವು ವಿಶ್ವಾಸವಿಟ್ಟಂತೆಯೇ ಅವರೂ ವಿಶ್ವಾಸವಿಟ್ಟರೆ ಅವರು ಖಂಡಿತ ಸತ್ಫಥ ಪ್ರಾಪ್ತರು. ಅವರು ವಿಮುಖರಾದರೆ, ಅವರೇ ಒಡಕಿನವರು. ಆದ್ದರಿಂದ ಅವರಿಂದ ನಿಮ್ಮನ್ನು ಪಾರುಮಾಡಲು ಅಲ್ಲಾಹು ಸಾಕು. ಆತನು ಪರಮ ಶ್ರೋತೃನು. ಪರಮ ತಜ್ಞನು.');
INSERT INTO "kannada" VALUES(2,138,'2:138','ಅಲ್ಲಾಹನ ಧರ್ಮದ ನಿಜ ಬಣ್ಣವನ್ನು ನಮಗವನು ನೀಡಿರುತ್ತಾನೆ. ಅಲ್ಲಾಹನಿಗಿಂತ ಶ್ರೇಷ್ಟ ವರ್ಣದಾತರು ಯಾರಿದ್ದಾರೆ? ನಾವು ಆತನಿಗೆ ಮಾತ್ರ ಆರಾಧಿಸುವೆವು (ಎಂದು ಹೇಳಿರಿ).');
INSERT INTO "kannada" VALUES(2,139,'2:139','ಅಲ್ಲಾಹನ ವಿಷಯದಲ್ಲಿ ನಮ್ಮೊಂದಿಗೆ ವಾದ ಮಾಡುತ್ತಿರುವಿರಾ? ಆತನು ನಮ್ಮ - ನಿಮ್ಮ ಪ್ರಭು. ನಮಗೆ ನಮ್ಮ ಕರ್ಮಗಳು, ನಿಮಗೆ ನಿಮ್ಮ ಕರ್ಮಗಳು. ಆದರೆ, ಆತನಿಗೆ ಅಕಳಂಕ ನಿಷ್ಠರಾಗುವವರು ನಾವು’ ಎಂದು ಹೇಳಿರಿ.');
INSERT INTO "kannada" VALUES(2,140,'2:140','ಇಬ್ರಾಹೀಮ್, ಇಸ್ಮಾಈಲ್, ಇಸ್ಹಾಖ್, ಯಅಖೂಬ್ ಹಾಗೂ ಸಂತತಿಗಳು ಜೂದರು ಅಥವಾ ಕ್ರೈಸ್ತರಾಗಿದ್ದಾರೆಂದು ನೀವು ಹೇಳುತ್ತೀರಾ? “ಹೆಚ್ಚು ತಿಳಿದವರು ನೀವೋ, ಅಲ್ಲಾಹನೋ?” ಎಂದು ಪ್ರಶ್ನಿಸಿರಿ. ತನ್ನಲ್ಲಿರುವ ದೇವದತ್ತ ಸಾಕ್ಷ್ಯವನ್ನು ಅಡಗಿಸಿಟ್ಟವನಿಗಿಂತ ದೊಡ್ಡ ದುಷ್ಕರ್ಮಿ ಯಾರು? ನಿಮ್ಮ ಪ್ರವರ್ತಿಗಳ ಬಗ್ಗೆ ಅಲ್ಲಾಹನು ನಿರ್ಲಕ್ಷ್ಯನಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,141,'2:141','ಗತಿಸಿದ ಸಮುದಾಯವದು. ಅವರು ಮಾಡಿದ್ದು ಅವರಿಗೆ. ನೀವು ಮಾಡಿದ್ದು ನಿಮಗೆ. ಅವರು ಮಾಡಿದುದರ ಬಗ್ಗೆ ನಿಮ್ಮನ್ನು ಪ್ರಶ್ನಿಸಲಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,142,'2:142','ಇವರು ಮೊದಲ ‘ಖಿಬ್‍ಲಾ’ವನ್ನು ಬದಲಾಯಿ ಸಿದ್ದು ಯಾಕೆ? ಎಂದು ಮೂಢ ಜನರು ಕೇಳುವರು. ಹೇಳಿರಿ; ‘ಪೂರ್ವ-ಪಶ್ಚಿಮ ಅಲ್ಲಾಹನದ್ದೇ. ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಸರಿದಾರಿ ತೋರುತ್ತಾನೆ’ .');
INSERT INTO "kannada" VALUES(2,143,'2:143','ಅದೇ ರೀತಿ ನೀವು ಜನರಿಗೆ ಸಾಕ್ಷಿಗಳಾಗಲಿಕ್ಕೂ ರಸೂಲರು ನಿಮ್ಮ ಮೇಲೆ ಸಾಕ್ಷಿಯಾಗಲಿಕ್ಕೂ ನಿಮ್ಮನ್ನೊಂದು ನೀತಿನಿಷ್ಠ ಸಮುದಾಯವಾಗಿ ಮಾಡಿರುತ್ತೇವೆ . ಮೊದಲು ನೀವು ಮುಖ ಮಾಡು ತ್ತಿದ್ದ ಖಿಬ್ಲಾವನ್ನು ನಾನು ನಿಶ್ಚೈಸಿದ್ದುದು ಪ್ರವಾದಿ ಯಿಂದ ವಿಮುಖರಾಗಿ ಹಿಂದೆ ಸರಿಯುವವರಿಂದ ಪ್ರವಾದಿಯನ್ನು ಅನುಸರಿಸುವವರನ್ನು ಬೇರ್ಪಟ್ಟು ತಿಳಿಯಲು ಮಾತ್ರವಾಗಿತ್ತು. ಸನ್ಮಾರ್ಗದರ್ಶಿತ ರಲ್ಲದವರಿಗೆ ಖಿಬ್ಲಾ ಬದಲಾವಣೆ ಒಂದು ಗಂಭೀರ ವಿಚಾರವಾಗಿತ್ತು. ಆ ಅವಧಿಯ ನಿಮ್ಮ ವಿಶ್ವಾಸ ವನ್ನು (ನಮಾಝನ್ನು) ಂ ಅಲ್ಲಾಹನು ವ್ಯರ್ಥ ಗೊಳಿಸುವುದಿಲ್ಲ. ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಜನರ ಮೇಲೆ ಅತ್ಯುದಾರಿ, ಪರಮ ದಯಾಳು');
INSERT INTO "kannada" VALUES(2,144,'2:144','ನಿಮ್ಮ ಮುಖವು ಆಕಾಶದತ್ತ ದೃಷ್ಟಿ ನೆಟ್ಟಿರುವುದನ್ನು ನಾವು ಗಮನಿಸಿದ್ದೇವೆ. ಆದ್ದರಿಂದ ನೀವು ಇಷ್ಟಪಡುವ ‘ಖಿಬ್‍ಲಾ’ದತ್ತ ನಿಮ್ಮನ್ನು ತಿರುಗಿಸುತ್ತಿದ್ದೇವೆ . ಹಾಗಾಗಿ ನಿಮ್ಮ ಮುಖವನ್ನು ಮಸ್ಜಿದುಲ್ ಹರಾಮಿನ ಕಡೆಗೆ ತಿರುಗಿಸಿರಿ. ನೀವು ಎಲ್ಲಿದ್ದರೂ ಅದರ ಕಡೆಗೆ ಮುಖ ಮಾಡಿರಿ. ಈ ಕ್ರಮವು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯ ಸತ್ಯವೆಂಬುದನ್ನು ವೇದಗ್ರಂಥ ದೊರೆತವರು ಚೆನ್ನಾಗಿ ಬಲ್ಲರು. ಅವರ ಕೃತ್ಯಗಳ ಬಗ್ಗೆ ಅಲ್ಲಾಹನು ಅಶ್ರದ್ಧನಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,145,'2:145','ಗ್ರಂಥದವರಿಗೆ ನೀವು ಸಕಲ ಪ್ರಮಾಣಗಳನ್ನು ಒದಗಿಸಿದರೂ ಕೂಡಾ ಅವರು ನಿಮ್ಮ ‘ಖಿಬ್‍ಲಾ’ ವನ್ನು ಅನುಸರಿಸುವವರಲ್ಲ. ನೀವೂ ಅವರ ‘ಖಿಬ್‍ಲಾ’ವನ್ನು ಅನುಸರಿಸುವವರಾಗಲಾರಿರಿ. ಅವರು ಕೂಡಾ ಪರಸ್ಪರ ಒಂದು ವಿಭಾಗದವರ ‘ಖಿಬಾ’್ಲವನ್ನು ಮತ್ತೊಂದು ವಿಭಾಗ ಅನುಸರಿಸುವವರಲ್ಲ. ನಿಮಗೆ ಜ್ಞಾನ ದೊರೆತ ನಂತರವೂ ಅವರ ಸ್ವೇಚ್ಛೆಗಳನ್ನು ಅನುಸರಿಸಿದರೆ ಅಕ್ರಮಿಗಳ ಕೂಟಕ್ಕೆ ಸೇರುವಿರಿ.');
INSERT INTO "kannada" VALUES(2,146,'2:146','ವೇದದವರು ಮುಹಮ್ಮದರನ್ನು ತಮ್ಮ ಮಕ್ಕಳನ್ನು ಅರಿಯುವಂತೆ ಸ್ಪಷ್ಟವಾಗಿ ಅರಿತವರು . ಅವರಲ್ಲೊಂದು ವಿಭಾಗವು ತಿಳಿದುಕೊಂಡೇ ಸತ್ಯವನ್ನು ಮುಚ್ಚಿಡುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(2,147,'2:147','ನಿಮ್ಮ ಪ್ರಭುವಿನಿಂದ ಬಂದ ಸತ್ಯವಿದು. ಆದ್ದರಿಂದ ನೀವು ಸಂಶಯಾತ್ಮರ ಕೂಟಕ್ಕೆ ಸೇರದಿರಿ.');
INSERT INTO "kannada" VALUES(2,148,'2:148','ಪ್ರತಿಯೊಬ್ಬರಿಗೂ ಒಂದಿಲ್ಲೊಂದು ದಿಶೆ ಇದ್ದು ಅದಕ್ಕೆ ಅಭಿಮುಖನಾಗುತ್ತಾನೆ. ನೀವು ಸುಕೃತಗಳತ್ತ ತತ್ಪರತೆಯಿಂದ ಮುನ್ನಡೆಯಿರಿ. ನೀವೆಲ್ಲಿದ್ದರೂ ನಿಮ್ಮೆಲ್ಲರನ್ನೂ ಅಲ್ಲಾಹನು ಹಾಜರುಪಡಿಸುವನು. ಖಂಡಿತ ಅಲ್ಲಾಹನು ಸರ್ವಸಮರ್ಥನಿರುವನು.');
INSERT INTO "kannada" VALUES(2,149,'2:149','ನೀವು ಎತ್ತ ಹೊರಟಿದ್ದರೂ ಮಸ್ಜಿದುಲ್ ಹರಾಮಿನತ್ತ ಅಭಿಮುಖರಾಗಿರಿ. ನಿಜವಾಗಿಯೂ ಇದು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯ ಪರಮ ಸತ್ಯ. ನಿಮ್ಮ ಕೃತ್ಯಗಳ ಕುರಿತು ಅಲ್ಲಾಹನು ಅಪ್ರಜ್ಞನಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,150,'2:150','ನೀವು ಎತ್ತ ಹೊರಟಿದ್ದರೂ ಮಸ್ಜಿದುಲ್ ಹರಾಮಿನ ಕಡೆಗೆ ಅಭಿಮುಖರಾಗಿರಿ. (ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ) ನೀವು ಎಲ್ಲಿರುವಿರಾದರೂ ಆ ದಿಕ್ಕಿಗೆ ನಿಮ್ಮ ಮುಖ ತಿರುಗಿಸಿರಿ. ಇದು ಅವರ ಪೈಕಿ ಕೆಲವು ಮಂದಿ (ತರ್ಕಕ್ಕೆ ನಿಲ್ಲುವ) ದುರಾತ್ಮರ ಹೊರತು, ಉಳಿದ ಜನರಿಗೆ ನಿಮ್ಮ ವಿರುದ್ಧ ಯಾವುದೇ ಪುರಾವೆ (ಸಾಕ್ಷಿ) ದೊರೆಯದಿರಲು . ನೀವು ಅವರಿಗೆ ಭಯಪಡಬೇಡಿರಿ. ಪ್ರತಿಯಾಗಿ ನನಗೆ ಭಯಪಡಿರಿ. ಇದು ನನ್ನ ವರದಾನವನ್ನು ನಿಮಗೆ ನಾನು ಪೂರ್ಣಗೊಳಿಸಲು ಹಾಗೂ ನೀವು ಸತ್ಪಥಿಕರಾಗಲು.');
INSERT INTO "kannada" VALUES(2,151,'2:151','ನನ್ನ ವರದಾನವನ್ನು ನಾನು ಪೂರ್ಣಗೊಳಿಸುವುದು ನಿಮ್ಮಿಂದಲೇ ಓರ್ವ ರಸೂಲರನ್ನು ನಿಮಗಾಗಿ ಕಳುಹಿಸಿ ಪೂರ್ಣಗೊಳಿಸಿದ ರೀತಿಯಲ್ಲಾಗಿದೆ. ಆ ರಸೂಲರು ನಮ್ಮ ಸದ್ವಚನಗಳನ್ನು ನಿಮಗೆ ಓದಿ ಕೊಡುತ್ತಾರೆ. ನಿಮ್ಮನ್ನು ಶುಚಿಗೊಳಿಸುತ್ತಾರೆ. ನಿಮಗೆ ದಿವ್ಯಗ್ರಂಥವನ್ನೂ ತತ್ವಜ್ಞಾನವನ್ನೂ ಕಲಿಸುತ್ತಾರೆ. ನಿಮಗೆ ತಿಳಿಯದ ವಿಚಾರಗಳನ್ನು ತಿಳಿಯಪಡಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(2,152,'2:152','ಆದ್ದರಿಂದ ನೀವು ನನ್ನನ್ನು ಸ್ಮರಿಸಿರಿ. ನಿಮ್ಮನ್ನು ನಾನೂ ಸ್ಮರಿಸುವೆನು. ನನಗೆ ಕೃತಜ್ಞರಾಗಿರಿ. ಕೃತಘ್ನರಾಗದಿರಿ.');
INSERT INTO "kannada" VALUES(2,153,'2:153','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ಸಹನೆ ಮತ್ತು ನಮಾಜ್‍ನ ಮೂಲಕ ಸಹಾಯಪೇಕ್ಷೆ ಮಾಡಿಕೊಳ್ಳಿರಿ. ನಿಜವಾಗಿ ಅಲ್ಲಾಹನಿರುವುದು ಸಹನಶೀಲರೊಡನೆ.');
INSERT INTO "kannada" VALUES(2,154,'2:154','ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಹತರಾದವರನ್ನು ಮೃತರೆನ್ನಬೇಡಿರಿ. ಅವರು ಸಜೀವರು . ಆ ಕುರಿತು ನೀವು ಪ್ರಜ್ಞಾಶೂನ್ಯರು ಂ.');
INSERT INTO "kannada" VALUES(2,155,'2:155','ಏನಾದರೊಂದು ಭಯ, ಹಸಿವು, ಧನಹಾನಿ, ಪ್ರಾಣಹಾನಿ, ಫಲೋತ್ಪನ್ನಗಳ ಕೊರತೆಗಳ ಮೂಲಕ ನಾವು ನಿಮ್ಮನ್ನು ಖಂಡಿತ ಪರೀಕ್ಷಿಸು ತ್ತಿರುತ್ತೇವೆ. ಸಹನಶೀಲರಿಗೆ ಶುಭವಾರ್ತೆ ನೀಡಿರಿ.');
INSERT INTO "kannada" VALUES(2,156,'2:156','ಅಂಥವರಿಗೆ ವಿಪತ್ತೇನಾದರೂ ಬಾಧಿಸಿದರೆ, (ಆಪತ್ಕಾಲದಲ್ಲಿ) ಅವರು ‘ನಿಜವಾಗಿಯೂ ನಾವು ಅಲ್ಲಾಹನಿಗೆ ಇರುವವರು, ಅವನೆಡೆಗೆ ನಿರ್ಗಮಿಸುವವರು’ ಅನ್ನುವರು .');
INSERT INTO "kannada" VALUES(2,157,'2:157','ಅಂತಹವರಿಗೆ ತಮ್ಮ ಪ್ರಭುವಿನಿಂದ ವರದಾನ ಗಳೂ ಕೃಪೆಯೂ ಪ್ರಾಪ್ತ. ಅವರೇ ಸತ್ಪಥಿüಕರು.');
INSERT INTO "kannada" VALUES(2,158,'2:158','ಖಂಡಿತವಾಗಿಯೂ ಸಫಾ ಮತ್ತು ಮರ್ವಾ ಅಲ್ಲಾಹನ ಕುರುಹುಗಳಲ್ಲಿ ಸೇರಿವೆ. ಆದುದರಿಂದ ಕಅಬಾದಲ್ಲಿ ಹಜ್-ಉಮ್ರಾ ನಿರ್ವಹಿಸುವವರು ಅವುಗಳ ನಡುವೆ ಪಥಸಂಚಲನೆ (ಸಅïಯ್) ಗೈಯುವುದು ತಪ್ಪಲ್ಲ . ಯಾರಾದರೂ ಸ್ವಪ್ರೇರಿತನಾಗಿ ಸುಕೃತ ಮಾಡಿದರೆ, (ಅದು ವ್ಯರ್ಥವಲ್ಲ. ಏಕೆಂದರೆ) ಖಂಡಿತ ಅಲ್ಲಾಹನು ಕೃತಜ್ಞನೂ ಅಭಿಜ್ಞನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(2,159,'2:159','ನಾವು ಅವತೀರ್ಣಗೊಳಿಸಿದ ಸುವ್ಯಕ್ತ ದೃಷ್ಟಾಂತಗಳು ಮತ್ತು ಸತ್ಯದರ್ಶನವನ್ನು ಜನರಿಗೋಸ್ಕರ ನಾವು ಗ್ರಂಥದಲ್ಲಿ ವಿವರಿಸಿದ ನಂತರವೂ ಮರೆ ಮಾಚುವವರನ್ನು ಅಲ್ಲಾಹನು ಶಪಿಸುವನು. ಶಪಿಸುವ ಇತರರೂ ಶಪಿಸುವರು.');
INSERT INTO "kannada" VALUES(2,160,'2:160','ಆದರೆ ತಪ್ಪಿಗೆ ಮರುಗುವ, (ಕರ್ಮವನ್ನು) ಸುಧಾರಿಸಿಕೊಳ್ಳುವ ಮತ್ತು ಮುಚ್ಚಿಟ್ಟದ್ದನ್ನು ಹೊರಗೆಡಹುವವರಿಗೆ ನಾನು ಕ್ಷಮಿಸುತ್ತೇನೆ. ನಾನು ಪರಮ ಕ್ಷಮಾದಾನಿ, ಕರುಣಾವಾರಿಧಿ.');
INSERT INTO "kannada" VALUES(2,161,'2:161','ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸಿದ ಮತ್ತು ಅವಿಶ್ವಾಸಿ ಗಳಾಗಿಯೇ ಮರಣ ಹೊಂದಿದವರಿಗೆ ಅಲ್ಲಾಹು, ಮಲಾಇಕತ್ ಹಾಗೂ ಸರ್ವ ಜನರ ಶಾಪವಿದೆ .');
INSERT INTO "kannada" VALUES(2,162,'2:162','ಅವರು ಶಿಕ್ಷೆಯಲ್ಲಿ ಶಾಶ್ವತರು. ಅವರಿಗೆ ಶಿಕ್ಷೆಯನ್ನು ಸರಳಗೊಳಿಸಲಾಗುವುದಿಲ್ಲ, ಬಿಡುವನ್ನೂ ನೀಡಲಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,163,'2:163','ನಿಮ್ಮ ಆರಾಧ್ಯನು ಏಕೈಕ ದೇವನು . ಆತನ ವಿನಾ ಅನ್ಯ ಆರಾಧ್ಯನಿಲ್ಲ. ಅವನು ಇಹದಲ್ಲಿ ಎಲ್ಲರಿಗೂ ದಯಾಳು. ಪರದಲ್ಲಿ ನಿಷ್ಠರಿಗೆ ಮಾತ್ರ ಕೃಪಾಳು.');
INSERT INTO "kannada" VALUES(2,164,'2:164','ಧರೆ-ಗಗನಗಳ ಸೃಷ್ಟಿ, ಹಗಲಿರುಳುಗಳ ಭೇದ, ಜನೋಪಯೋಗಿ ಸರಕು ಹೇರಿಕೊಂಡು ಕಡಲಲ್ಲಿ ತೇಲುವ ಹಡಗು, ಮೇಲಿಂದ ಅಲ್ಲಾಹನಿಳಿಸುವ ಜಲದಿಂದ ನಿರ್ಜೀವಗೊಂಡಿದ್ದ ಇಳೆ ಸಜೀವಗೊಳ್ಳುವ ಪರಿ, ಧರೆಯಲ್ಲಿ ಹರಡಿದ ಚರಜೀವಿಗಳು, ಗಾಳಿಗಳ ಗತಿ-ವಿಗತಿ ಹಾಗೂ ನಭ-ಭುವಿಗಳ ನಡುವಣ ನಿಯಂತ್ರಿತ ಮೇಘ ರಾಶಿಗಳಲ್ಲಿ ಚಿಂತಿಸುವ ಜನರಿಗೆ ದೃಷ್ಟಾಂತಗಳಿವೆ ');
INSERT INTO "kannada" VALUES(2,165,'2:165','ಕೆಲವರು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಸಹದೇವರು ಗಳನ್ನು ನಿಶ್ಚೈಸಿರುತ್ತಾರೆ. ಅಲ್ಲಾಹನನ್ನು ಪ್ರೀತಿಸುವಂತೆ ಅವುಗಳನ್ನು ಪ್ರೀತಿಸುತ್ತಾರೆ. ವಸ್ತುತಃ ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಅಲ್ಲಾಹನನ್ನು ಅತ್ಯಧಿಕ ಪ್ರೀತಿಸುತ್ತಾರೆ . ಅಕ್ರಮಿಗಳು ಶಿಕ್ಷೆಯನ್ನು ಕಾಣುವ ಸಂದರ್ಭದಲ್ಲಿ ಸರ್ವಶಕ್ತಿಯೂ ಅಲ್ಲಾಹನದ್ದು ಮತ್ತು ಅವನು ಕಠಿಣವಾಗಿ ಶಿಕ್ಷಿಸುವವನೆಂದು ಅವರು ಕಂಡರಿಯುತ್ತಿದ್ದರೆ !');
INSERT INTO "kannada" VALUES(2,166,'2:166','ಮುಂದಾಳುಗಳು ಅನುಯಾಯಿಗಳನ್ನು ಕೈಬಿಡುವ ಹಾಗೂ ಅವರು ಶಿಕ್ಷೆಯನ್ನು ಕಾಣುವ ಮತ್ತು ಪರಸ್ಪರ ಸಂಬಂಧ ಕಡಿದು ಬೀಳುವ ಸಂದರ್ಭ. (ಅದು)');
INSERT INTO "kannada" VALUES(2,167,'2:167','ಇಹಲೋಕಕ್ಕೆ ಮರಳುವ ಅವಕಾಶ ನಮಗಿದ್ದಿದ್ದರೆ ಇವರೀಗ ಕೈಬಿಟ್ಟಂತೆ ನಾವೂ ಅವರನ್ನು ಕೈ ಬಿಡುತ್ತಿದ್ದೆವು ಎಂದು ಅನುಯಾಯಿಗಳು ಹೇಳುವರು. ಹಾಗೆ ಅವರ ಕೃತ್ಯಗಳನ್ನು ಅವರಿಗೆ ಶೋಕಗಳನ್ನಾಗಿ ಅಲ್ಲಾಹನು ಕಾಣಿಸಿ ಕೊಡುತ್ತಾನೆ. ಅವರು ನರಕದಿಂದ ಎಂದೂ ಹೊರ ಹೋಗುವವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,168,'2:168','ಜನರೇ ! ಭೂಮಿಯಲ್ಲಿರುವ ಧರ್ಮಸಮ್ಮತ ಹಾಗೂ ಶುದ್ಧವಾದವುಗಳನ್ನು ಭಕ್ಷಿಸಿರಿ. ಶೈತಾನನ ಹೆಜ್ಜೆಗಳನ್ನು ಅನುಸರಿಸದಿರಿ. ಅವನು ನಿಮ್ಮ ಬದ್ಧ ವೈರಿ.');
INSERT INTO "kannada" VALUES(2,169,'2:169','ಕೇಡು, ಹೀನವೃತ್ತಿ ಹಾಗೂ ಅಲ್ಲಾಹನ ಕುರಿತು ನಿಮಗೆ ಅರಿವಿಲ್ಲದ್ದನ್ನು ಹೇಳುವುದಕ್ಕೆ ಅವನು ನಿಮಗೆ ಪ್ರಚೋದಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(2,170,'2:170','ಅಲ್ಲಾಹನು ಅವತೀರ್ಣಗೊಳಿಸಿದ ಆದರ್ಶಗಳನ್ನು ಅನುಸರಿಸಿರಿ ಎಂದು ಅವರಿಗೆ ಹೇಳಿದರೆ “ನಾವು ನಮ್ಮ ಪೂರ್ವಿಕರನ್ನು ಯಾವ ನಡವಳಿಕೆಯಲ್ಲಿ ಕಂಡಿದ್ದೆವೋ ಅದನ್ನೇ ಅನುಸರಿಸುತ್ತೇವೆ” ಎನ್ನುತ್ತಾರೆ. ಅವರ ಪೂರ್ವಿಕರು ವಿಚಾರ ಶೂನ್ಯರೂ ದಾರಿ ತಪ್ಪಿದವರೂ ಆಗಿದ್ದರೂ ಕೂಡಾ ಇವರು ಅವರನ್ನು ಅನುಸರಿ ಸುತ್ತಾರೇನು ? ');
INSERT INTO "kannada" VALUES(2,171,'2:171','ಸತ್ಯ ನಿಷೇಧಿಗಳ ಉಪಮೆಯು ಕಿರಿಚಾಟ ಬೊಬ್ಬೆಗಳ ಹೊರತು ಇನ್ನೇನೂ ಕೇಳಿಸದಂಥ ಪ್ರಾಣಿಯನ್ನು ಕೂಗುವ ಕುರುಬನಂತಿದೆ . ಅವರು ಕಿವುಡರು, ಮೂಗರು, ಕುರುಡರು. ಅವರು ಚಿಂತಿಸರು.');
INSERT INTO "kannada" VALUES(2,172,'2:172','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ ! ನಿಮಗೆ ನಾವಿತ್ತ ಶುದ್ಧ ವಸ್ತುಗಳಿಂದ ಭುಜಿಸಿರಿ. ನೀವು ಅಲ್ಲಾಹನಿಗೆ ಮಾತ್ರ ಆರಾಧಿಸುವವರಾಗಿದ್ದರೆ ಆತನಿಗೆ ಋಣಭಾವವುಳ್ಳವರಾಗಿರಿ .');
INSERT INTO "kannada" VALUES(2,173,'2:173','ಅಲ್ಲಾಹನು ನಿಮಗೆ ನಿಷೇಧಿಸಿದ್ದು ಸತ್ತ ಪ್ರಾಣಿಗಳು , ರಕ್ತ, ಹಂದಿ ಮಾಂಸ ಹಾಗೂ ಅಲ್ಲಾಹೇತರರ ನಾಮವನ್ನುಚ್ಚರಿಸಿ ಕಡಿದ ಪ್ರಾಣಿಯ ಮಾಂಸವನ್ನಾಗಿದೆ . ಆದರೆ ಯಾವನಾದರೂ ಆಗ್ರಹವಿಲ್ಲದೆ, ಮಿತಿ ಮೀರದೆ ನಿರ್ಬಂಧಿತನಾದರೆ ಅವನಿಗೆ ಪಾಪವಿಲ್ಲ . ಖಂಡಿತ ಅಲ್ಲಾಹನು ಪರಮ ಕ್ಷಮಾದಾನಿ. ಪರಮ ದಯಾಳು.');
INSERT INTO "kannada" VALUES(2,174,'2:174','ಅಲ್ಲಾಹನು ಅವತೀರ್ಣಗೊಳಿಸಿದ ಗ್ರಂಥವನ್ನು ಮರೆಮಾಚಿ ಅದರ ಬದಲಿಗೆ ತುಚ್ಛ ಸಂಪಾದನೆ ಮಾಡುತ್ತಿರುವವರು ತಮ್ಮ ಉದರಕ್ಕೆ ಅಗ್ನಿಯನ್ನೇ ತುಂಬುತ್ತಿದ್ದಾರೆ . ಪರಲೋಕದಲ್ಲಿ ಅಲ್ಲಾಹನು ಅವರೊಂದಿಗೆ ಮಾತಾಡಲಾರ. ಅವರನ್ನು ಪಾಪಗಳಿಂದ ನಿರ್ಮಲಗೊಳಿಸಲಾರ. ಅವರಿಗೆ ಯಾತನಾಮಯ ಸಜೆ ಕಾದಿದೆ.');
INSERT INTO "kannada" VALUES(2,175,'2:175','ಅವರು ಸನ್ಮಾರ್ಗಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ದುರ್ಮಾರ್ಗ ವನ್ನೂ ಕ್ಷಮೆಗೆ ಪ್ರತಿಯಾಗಿ ಸಜೆಯನ್ನೂ ಕೊಂಡು ಕೊಂಡಿದ್ದಾರೆ. ನರಕಾಗ್ನಿಯ ಮೇಲೆ ಅವರ ಸೈರಣೆ ಆಶ್ಚರ್ಯವೇ ಸರಿ !');
INSERT INTO "kannada" VALUES(2,176,'2:176','ಹೀಗೆ ಯಾಕೆಂದರೆ ಅಲ್ಲಾಹನು ದಿವ್ಯಗ್ರಂಥವನ್ನು ಸತ್ಯದೊಂದಿಗೆ ರವಾನಿಸಿರುತ್ತಾನೆ. ದಿವ್ಯಗ್ರಂಥದ ಬಗ್ಗೆ ಭಿನ್ನರಾದವರು ಸತ್ಯದಿಂದ ದೂರವಾದ ಭಿನ್ನಾಭಿಪ್ರಾಯದಲ್ಲಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(2,177,'2:177','ಹಿತವಿರುವುದು ಪೂರ್ವ-ಪಶ್ಚಿಮೋನ್ಮುಖತೆಯಲ್ಲಲ್ಲ. ಅಲ್ಲಾಹು, ಪರಲೋಕ, ಮಲಕ್, ದೇವಗ್ರಂಥ ಹಾಗೂ ಪ್ರವಾದಿಗಳಲ್ಲಿ ವಿಶ್ವಾಸ ಹಾಗೂ ತಾನು ಪ್ರೀತಿಸುವ ಧನವನ್ನು ಬಂಧುಗಳು, ತಬ್ಬಲಿಗಳು, ಬಡವರು, ಸಂಚಾರಿಗಳು, ಅಪೇಕ್ಷಕರು ಹಾಗೂ ಜೀತಮುಕ್ತಿಗಾಗಿ ವ್ಯಯ ಮತ್ತು ನಿಯತ ನಮಾಜ್ ನಿರ್ವಹಣೆ, ಝಕಾತ್ ಪಾವತಿ, ಕೊಟ್ಟ ವಚನದ ಪಾಲನೆ, ದಟ್ಟ ದಾರಿದ್ರ್ಯ, ರೋಗ ಹಾಗೂ ಧರ್ಮಸಮರಗಳಲ್ಲಿ ಸಂಯಮದಲ್ಲಾಗಿದೆ ಹಿತ. ಇವರೇ ಸತ್ಯವ್ರತರು. ಇವರೇ ಧರ್ಮಾತ್ಮರು.');
INSERT INTO "kannada" VALUES(2,178,'2:178','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ಕೊಲೆಯಾದವರ ಪರ ಪ್ರತೀಕಾರವನ್ನು ನಿಮಗೆ ನಿಯಮ ಬದ್ಧಗೊಳಿಸಲಾಗಿದೆ. ಸ್ವತಂತ್ರನ ಕೊಲೆಗೆ ಸ್ವತಂತ್ರ, ಗುಲಾಮನ ಕೊಲೆಗೆ ಗುಲಾಮ, ಹೆಣ್ಣಿನ ಕೊಲೆಗೆ ಹೆಣ್ಣು ಕೊಲೆಯಾಗಬೇಕು. ಆದರೆ ಹಂತಕನಿಗೆ ತನ್ನ ಸಹೋದರನಿಂದ ಏನಾದರೂ ಕ್ಷಮೆ ದೊರೆತರೆ ಉತ್ತಮ ವಿಧಾನವನ್ನು ಅನುಸರಿಸಬೇಕು. ಕೊಲೆಗಾರನೂ ಉತ್ತಮ ರೀತಿಯಲ್ಲಿ ದಂಡ ತೆರಬೇಕು . ಇದು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ದೊರಕುವ ಸೌಲಭ್ಯ ಹಾಗೂ ದಯೆ. ಇಷ್ಟಾದ ಮೇಲೂ ವಿೂರಿ ನಡೆದವನಿಗೆ ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆಯಿದೆ.');
INSERT INTO "kannada" VALUES(2,179,'2:179','ಓ ಬುದ್ಧಿಯುಳ್ಳವರೇ ! ನಿಮಗೆ ಪ್ರತೀಕಾರದಲ್ಲಿ ಜೀವನವಿದೆ . ನೀವು ಕೊಲೆಪಾತಕದಿಂದ ದೂರ ನಿಲ್ಲುವ ಸಲುವಾಗಿ. (ಈ ಶಾಸನ)');
INSERT INTO "kannada" VALUES(2,180,'2:180','ನೀವು ಪ್ರತಿಯೊಬ್ಬರೂ ಮರಣಾಸನ್ನರಾದಾಗ ಅವನು ಸೊತ್ತು ಬಿಟ್ಟು ಹೋಗುತ್ತಿದ್ದರೆ ತಾಯಿತಂದೆ ಹಾಗೂ ನಿಕಟ ಬಂಧುಗಳಿಗೆ ನೀತಿ ಪ್ರಕಾರ ಉಯಿಲು ಮಾಡುವುದು ನಿಮ್ಮ ಮೇಲೆ ಕಡ್ಡಾಯ. ಶ್ರದ್ಧಾಳುಗಳ ಕರ್ತವ್ಯವಿದು ');
INSERT INTO "kannada" VALUES(2,181,'2:181','ಯಾರಾದರೂ ಉಯಿಲನ್ನು ಆಲಿಸಿದ ಬಳಿಕ ಅದನ್ನು ಬದಲಾಯಿಸಿದರೆ ಅದರ ಪಾಪವು ಬದಲಾಯಿಸಿದವರ ಮೇಲೆ. ಅಲ್ಲಾಹನು ಎಲ್ಲ ಆಲಿಸುವವನು. ಎಲ್ಲ ಬಲ್ಲವನು.');
INSERT INTO "kannada" VALUES(2,182,'2:182','ಆದರೆ ಉಯಿಲು ಮಾಡಿದವನು ಅದರಲ್ಲಿ ಪಕ್ಷಪಾತ ಅಥವಾ ದೋಷವೆಸಗುವ ಭೀತಿಯಿಂದ ಸಂಬಂಧಿಕರ ನಡುವೆ ಸಂಧಾನ ನಡೆಸಿದರೆ ಅವನ ಮೇಲೆ ಪಾಪವಿಲ್ಲ. ಅಲ್ಲಾಹನು ಯಥೇಚ್ಛ ಮನ್ನಿಸುವವನು. ಪರಮದಯಾಳು.');
INSERT INTO "kannada" VALUES(2,183,'2:183','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನಿಮ್ಮ ಪೂರ್ವಜರಿಗೆ ಕಡ್ಡಾಯ ಗೊಳಿಸಲಾದಂತೆಯೇ ನಿಮಗೂ ವ್ರತವನ್ನು ಕಡ್ಡಾಯಗೊಳಿಸಲಾಗಿದೆ. ನೀವು ಭಕ್ತರಾಗಲೆಂದು.');
INSERT INTO "kannada" VALUES(2,184,'2:184','ನಿರ್ಧಿಷ್ಟ ದಿನಗಳ ವ್ರತ. ನಿಮ್ಮಲ್ಲಾರಾದರೂ ರೋಗಿ ಅಥವಾ ಯಾತ್ರಿಕನಾಗಿದ್ದರೆ ಬೇರೆ ದಿವಸಗಳಲ್ಲಿ ಸಂಖ್ಯೆ ಭರ್ತಿ ಮಾಡಬೇಕು. ಉಪವಾಸಕ್ಕೆ ಸಾಧ್ಯವಾಗದವರು ಓರ್ವ ಬಡವನಿಗೆ ಆಹಾರ ದಾನ ತೆರಬೇಕು. ಸ್ವಪ್ರೇರಣೆಯಿಂದ ಹೆಚ್ಚಿಗೆ ನೀಡಿದರೆ ಅದವರಿಗೆ ಉತ್ತಮ. ಆದರೆ ವ್ರತಾಚರಣೆಯೇ ನಿಮಗೆ ಹೆಚ್ಚು ಸಂಗತ. ನೀವು ತಿಳಿವುಳ್ಳವರಾಗಿದ್ದರೆ.');
INSERT INTO "kannada" VALUES(2,185,'2:185','ಜನರಿಗೆ ದಾರಿದೀಪವಾದ, ಸತ್ಯದರ್ಶನದ ವ್ಯಕ್ತ ಮಾಹಿತಿಗಳು ಹಾಗೂ ಸತ್ಯಾಸತ್ಯ ವಿವೇಚನೆಯುಳ್ಳ ಖುರ್‍ಆನ್ ಅವತೀರ್ಣವಾದ ಮಾಸವೇ ರಮಳಾನ್ . ಆದ್ದರಿಂದ ಆ ತಿಂಗಳಲ್ಲಿ ಹಾಜರಿದ್ದವರು ವ್ರತಾಚರಿ ಸಬೇಕು . ಯಾವನಾದರೂ ರೋಗಿಯಾಗಿದ್ದರೆ ಇಲ್ಲವೇ ಪ್ರಯಾಣದಲ್ಲಿದ್ದರೆ ಆತನು ಬೇರೆ ದಿನಗಳಲ್ಲಿ ಸಂಖ್ಯೆ ಭರ್ತಿ ಮಾಡಲಿ. ಅಲ್ಲಾಹನು ನಿಮಗೆ ಸರಳತೆಯನ್ನು ಬಯಸುತ್ತಾನೆ. ಕ್ಲಿಷ್ಟತೆ ಬಯಸುವುದಿಲ್ಲ. ಇದು ನೀವು ಸಂಖ್ಯೆ ಭರ್ತಿಗೊಳಿಸುವ ಮತ್ತು ನಿಮಗೆ ಸತ್ಯದರ್ಶನ ನೀಡಿದ್ದಕ್ಕೆ ಅಲ್ಲಾಹನ ಪರಮೋನ್ನತಿಯನ್ನು ಕೊಂಡಾಡುವ ಸಲುವಾಗಿ. ಮತ್ತು ನೀವು ಕೃತಜ್ಞರಾಗುವಿರೆಂದು .');
INSERT INTO "kannada" VALUES(2,186,'2:186','ನನ್ನ ದಾಸರು ನನ್ನ ಕುರಿತು ನಿಮ್ಮಲ್ಲಿ ಕೇಳಿದರೆ, ನಾನಿದೋ ಪಕ್ಕದಲ್ಲೇ ಇದ್ದೇನೆ. ಪ್ರಾರ್ಥಿಸುವವನು ನನ್ನಲ್ಲಿ ಪಾರ್ಥಿಸಿದರೆ ಅವನ ಪ್ರಾರ್ಥನೆಗೆ ಉತ್ತರಿಸುವೆನು. ಆದ್ದರಿಂದ ಅವರು ನನ್ನ ಕರೆಗೆ ಓಗೊಡಲಿ. ನನ್ನಲ್ಲಿ ವಿಶ್ವಾಸವಿಡಲಿ. ಅವರು ಸನ್ಮಾರ್ಗಿಗಳಾಗಬಹುದು .');
INSERT INTO "kannada" VALUES(2,187,'2:187','ವ್ರತಮಾಸದ ನಿಶೆಯಲ್ಲಿ ನಿಮಗೆ ಸತಿ ಸಂಪರ್ಕ ಸಮ್ಮತ. ಅವರು ನಿಮ್ಮ ಉಡುಗೆ. ನೀವು ಅವರ ಉಡುಗೆ. ನೀವು ಸ್ವವಂಚನೆ ಯೆಸಗುತ್ತಿದ್ದುದು ಅಲ್ಲಾಹನಿಗೆ ಗೊತ್ತು. ಆತನು ನಿಮ್ಮ ಪಶ್ಚಾತ್ತಾಪವನ್ನು ಸ್ವೀಕರಿಸಿದ್ದಾನೆ. ನಿಮಗೆ ಕ್ಷಮಿಸಿದ್ದಾನೆ. ಇನ್ನು ಮುಂದೆ ಅವರೊಂದಿಗೆ ಸಂಪರ್ಕ ಬೆಳೆಸಿರಿ. ಅಲ್ಲಾಹು ನಿಮಗೆ ದಾಖಲಿಸಿ ರುವುದನ್ನು ಬಯಸಿಕೊಳ್ಳಿರಿ. ಪ್ರಭಾತದ ಬಿಳಿನೂಲು ಕರಿನೂಲಿಂದ ನಿಮಗೆ ಬೇರ್ಪಟ್ಟು ಕಾಣುವವರೆಗೆ ತಿನ್ನಿರಿ ಮತ್ತು ಕುಡಿಯಿರಿ. ಆಮೇಲೆ ರಾತ್ರಿಯವರೆಗೂ ವ್ರತವನ್ನು ಪೂರ್ಣ ಗೊಳಿಸಿರಿ. ಆದರೆ ನೀವು ಮಸೀದಿಯಲ್ಲಿ ಧ್ಯಾನಾ ಸೀನರಾಗಿರುವಾಗ ಸ್ತ್ರೀ ಸಂಪರ್ಕ ಸಲ್ಲದು. ಇವು ಅಲ್ಲಾಹನ ಮೇರೆಗಳು. ಅವುಗಳ ಬಳಿ ತಲು ಪದಿರಿ. ಈ ಪ್ರಕಾರ ಅಲ್ಲಾಹು ತನ್ನ ನಿಯಮಗಳನ್ನು ಜನರಿಗೆ ವಿವರಿಸಿ ಕೊಡುತ್ತಾನೆ. ಅವರು ಭಕ್ತರಾಗಬೇಕೆಂದು.');
INSERT INTO "kannada" VALUES(2,188,'2:188','ನೀವು ಪರಸ್ಪರ ಅನ್ಯಾಯವಾಗಿ ಧನಾಪಹರಣ ಮಾಡದಿರಿ. ಜನರ ಧನದಿಂದ ತಿಳಿದೂ ತಿಳಿದೂ ವಂಚಿಸಿ ತಿನ್ನುವುದಕ್ಕಾಗಿ ಅಧಿಕಾರಿಗಳನ್ನು ಸಮೀಪಿಸಬೇಡಿರಿ .');
INSERT INTO "kannada" VALUES(2,189,'2:189','(ಓ ಪೈಗಂಬರರೇ,) ಚಂದ್ರನ ಬಗ್ಗೆ ಅವರು ನಿಮ್ಮೊಡನೆ ಕೇಳುತ್ತಿದ್ದಾರೆ. ಅದು ಜನರಿಗೆ ಕಾಲಗಣನೆ ಹಾಗೂ ಹಜ್ಜ್‍ಗಾಗಿ ಇದೆ. ಮನೆಗಳ ಮೇಲ್ಗಡೆಯಿಂದ ಪ್ರವೇಶಿಸುವುದರಲ್ಲಿ ಹಿತವಿಲ್ಲ. (ನೀವು ನಿಮ್ಮ ಮನೆಗಳಿಗೆ ಅವುಗಳ ಹಿಂಬದಿಗಳಿಂದ ಪ್ರವೇಶಿಸುವುದು ಪುಣ್ಯವಲ್ಲ) ದೇವಭಯದಲ್ಲೇ ಹಿತ. ದೇವಭಯವಿರಿಸಿ ಕೊಳ್ಳುವವನೇ ಪುಣ್ಯವಂತ. ಮನೆಗಳಿಗೆ ಅವುಗಳ ಬಾಗಿಲುಗಳಿಂದಲೇ ಪ್ರವೇಶಿಸಿರಿ . ಅಲ್ಲಾಹನ ಭಯವಿರಿಸಿಕೊಳ್ಳಿ. ನೀವು ಜಯಪ್ರಾಪ್ತರಾಗುವಿರಿ.');
INSERT INTO "kannada" VALUES(2,190,'2:190','ನಿಮ್ಮೊಡನೆ ಯುದ್ಧ ಮಾಡುವವರೆದುರು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ನೀವೂ ಯುದ್ಧ ಮಾಡಿರಿ . ಆದರೆ ಮೇರೆ ವಿೂರದಿರಿ. ಅತಿಕ್ರಮಿಗಳನ್ನು ಅಲ್ಲಾಹನು ಇಷ್ಟಪಡುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,191,'2:191','ಅವರನ್ನು ಕಂಡಲ್ಲಿ ವಧಿಸಿರಿ. ನಿಮ್ಮನ್ನವರು ಹೊರ ಹಾಕಿದ್ದಲ್ಲಿಂದ ನೀವೂ ಅವರನ್ನು ಹೊರಹಾಕಿರಿ. ಕ್ಷೋಭೆ ಹತ್ಯೆಗಿಂತಲೂ ಕಠಿಣ. ಮಸ್ಜಿದುಲ್ ಹರಾಮಿನ ಬಳಿ ಅವರು ನಿಮ್ಮೊಂದಿಗೆ ಯುದ್ಧ ಕ್ಕಿಳಿಯುವವರೆಗೂ ನೀವು ಯುದ್ಧಕ್ಕಿಳಿಯಬೇಡಿರಿ. ಅವರು ಯುದ್ಧಕ್ಕಿಳಿದರೆ ಅವರನ್ನು ವಧಿಸಿರಿ. ಅದುವೇ ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಪ್ರತಿಫಲ.');
INSERT INTO "kannada" VALUES(2,192,'2:192','ಇನ್ನವರು ಯುದ್ಧ ವಿರಾಮ ಮಾಡುವುದಾದರೆ ತಿಳಿಯಿರಿ, ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಪರಮ ಕ್ಷಮಾದಾನಿ. ಪರಮ ದಯಾಳು.');
INSERT INTO "kannada" VALUES(2,193,'2:193','ಅಧರ್ಮ ನಶಿಸಿ ದೇವಧರ್ಮ ಸಂಸ್ಥಾಪನೆ ಯಾಗುವವರೆಗೂ ನೀವವರ ವಿರುದ್ಧ ಯುದ್ಧ ಮಾಡಿರಿ. ಆದರೆ ಅವರು ವಿರಮಿಸಿದರೆ ಅತಿಕ್ರಮಣವಿಲ್ಲ. ಅಕ್ರಮಿಗಳ ಮೇಲೆ ಹೊರತು.');
INSERT INTO "kannada" VALUES(2,194,'2:194','ಸಮರ ನಿಷೇಧಿತ ಮಾಸಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ಸಮರ ನಿಷೇಧಿತ ಮಾಸ . ಪಾವಿತ್ರ್ಯಾಭಂಗಕ್ಕೆ ಪಾವಿತ್ರ್ಯಾ ಭಂಗದ ಮುಯ್ಯಿ. ನಿಮ್ಮ ಮೇಲೆ ಅವರು ಎಲ್ಲೆ ಮೀರಿದಂತೆ ನೀವು ಅವರ ಮೇಲೆ ಎಲ್ಲೆ ಮೀರಿರಿ. ಅಲ್ಲಾಹನ ಬಗ್ಗೆ ಜಾಗೃತಿಯಿರಲಿ. ಅಲ್ಲಾಹನು ಧರ್ಮಾತ್ಮರ ಸಂಗಡವಿರುವನು ಎಂಬುದು ನಿಮಗೆ ತಿಳಿದಿರಲಿ.');
INSERT INTO "kannada" VALUES(2,195,'2:195','ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಧನ ವ್ಯಯಿಸಿರಿ. ನಿಮಗೆ ನೀವೇ ಅನಾಹುತ ತಂದುಕೊಳ್ಳಬೇಡಿರಿ. ಸಂಭಾವಿತರಾಗಿರಿ. ಸಂಭಾವಿತರನ್ನು ಅಲ್ಲಾಹನು ಪ್ರೀತಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(2,196,'2:196','ಅಲ್ಲಾಹನಿಗಾಗಿ ಹಜ್-ಉಮ್ರಃ ಪೂರ್ತಿಗೊಳಿಸಿರಿ. ನೀವು ತಡೆಯಲ್ಪಟ್ಟರೆ ಸಾಧ್ಯವಿರುವ ಬಲಿದಾನದಿಂದ ಪರಿಹಾರ ಮಾಡಿರಿ . ಬಲಿಮೃಗ ಗಮ್ಯ ಸೇರುವವರೆಗೂ ಕೇಶಮುಂಡನ ಮಾಡದಿರಿ. ಆದರೆ ರೋಗ ಅಥವಾ ಶಿರ ಬಾಧೆಯಿಂದ ಅನಿವಾರ್ಯ ಮುಂಡನ ನಡೆಸಿದರೆ ವ್ರತ ಅಥವಾ ಬಲಿದಾನದ ಮೂಲಕ ಪ್ರಾಯಶ್ಚಿತ್ತ ಮಾಡಿಕೊಳ್ಳಿರಿ . ನೀವು ಶತ್ರು ಭಯದಿಂದ ಮುಕ್ತರಾಗಿರುವ ಸ್ಥಿತಿಯಲ್ಲಿ ಉಮ್ರಾ ಮಾಡಿ ಹಜ್ಜ್‍ವರೆಗೆ ಇಹ್ರಾಮ್ ಮುಕ್ತತೆಯ ಅನುಕೂಲವನ್ನು ಬಳಸಿಕೊಂಡಲ್ಲಿ ಸಾಧ್ಯವಿರುವ ಬಲಿದಾನ ಕಡ್ಡಾಯ. ಬಲಿದಾನಕ್ಕೆ ಅನನುಕೂಲ ವಾದಲ್ಲಿ ಹಜ್ಜ್‍ನಲ್ಲಿರುವಾಗ ಮೂರು ವ್ರತ, ಸ್ವನಿವಾಸಕ್ಕೆ ಮರಳಿದ ಬಳಿಕ ಏಳು ವ್ರತದಂತೆ ಒಟ್ಟು ಪೂರ್ಣ ಹತ್ತು ದಿವಸಗಳ ವ್ರತಾಚರಣೆ ಕಡ್ಡಾಯ. ಮಸ್ಜಿದುಲ್ ಹರಾಮಿನ ಪ್ರಜೆಗಳಿಗೆ ಈ ನಿಯಮ ಭಾಧಕವಲ್ಲ. ಅಲ್ಲಾಹನಲ್ಲಿ ಶ್ರದ್ಧೆಯಿರಲಿ. ಅಲ್ಲಾಹನು ಶಿಕ್ಷೆಯಲ್ಲಿ ಉಗ್ರನೆಂಬುದು ತಿಳಿದಿರಲಿ.');
INSERT INTO "kannada" VALUES(2,197,'2:197','ಹಜ್ಜ್ ನಿರ್ವಹಣೆಯ ಸಮಯವು ಗೊತ್ತಿರುವ ಕೆಲವು ಮಾಸಗಳು . ಆ ತಿಂಗಳುಗಳಲ್ಲಿ ಹಜ್ಜ್‍ನಲ್ಲಿ ತೊಡಗಿಸಿಕೊಂಡವನಿಗೆ ಮೈಥುನ ಹಾಗೂ ದುರ್ನಡತೆ ವಜ್ರ್ಯ. ಹಜ್ಜ್ ವೇಳೆ ವಾಗ್ವಾದ ಸಲ್ಲ. ನಿಮ್ಮ ಯಾವುದೇ ಪುಣ್ಯ ಕಾರ್ಯವಿರಲಿ ಅದು ಅಲ್ಲಾಹನ ಗಮನದಲ್ಲಿರುತ್ತದೆ. ಯಾತ್ರಾ ವೆಚ್ಚವನ್ನು ಜೊತೆ ಗಿಟ್ಟುಕೊಳ್ಳಿ. ಅತ್ಯಂತ ಶ್ರೇಷ್ಟವಾದ ಯಾತ್ರಾವೆಚ್ಚವು ದೇವನಿಷ್ಠೆಯೇ ಆಗಿರುತ್ತದೆ. ಜಾಣಮತಿಗಳೇ ! ನನ್ನನ್ನು ಭಯಪಡಿರಿ.');
INSERT INTO "kannada" VALUES(2,198,'2:198','ಹಜ್ಜ್‍ಯಾತ್ರೆಯಲ್ಲಿ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಅನುಗ್ರಹ (ಲೌಕಿಕ ಸಂಪಾದನೆ)ವನ್ನು ಬಯಸುವುದರಲ್ಲಿ ತಪ್ಪಿಲ್ಲ. ನೀವು ಅರಫಾದಿಂದ ನಿರ್ಗಮಿಸಿದರೆ ಮಶ್‍ಅರುಲ್ ಹರಾಮ್‍ನ ಬಳಿ ಅಲ್ಲಾಹನ ಸ್ಮರಣೆ ಮಾಡಿರಿ . ನಿಮಗೆ ಆತನು ನೀಡಿದ ಪಥ ದರ್ಶನಕ್ಕಾಗಿ ಆತನನ್ನು ಸ್ಮರಿಸಿರಿ. ಈ ಮೊದಲು ನೀವು ಖಂಡಿತ ದಾರಿಸಿಗದ ವರಾಗಿದ್ದಿರಿ.');
INSERT INTO "kannada" VALUES(2,199,'2:199','ಆ ಮೇಲೆ ಜನರು ನಿರ್ಗಮಿಸುವಲ್ಲಿಂದ ನೀವೂ ನಿರ್ಗಮಿಸಿರಿ . ಅಲ್ಲಾಹನಲ್ಲಿ ಪಾಪಮುಕ್ತಿ ಬೇಡಿರಿ. ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಪಾಪ ನಿವಾರಕನು. ಮಹಾಕೃಪಾಳು.');
INSERT INTO "kannada" VALUES(2,200,'2:200','ಹಾಗೆ ನೀವು ಹಜ್ಜ್ ನಿರ್ವಹಿಸಿದ ನಂತರ ಹಿಂದೆ ನಿಮ್ಮ ಪೂರ್ವಜರ ಗುಣಗಾನ ಮಾಡುತ್ತಿದ್ದಂತೆ ಮಾತ್ರವಲ್ಲ, ಅದಕ್ಕೂ ಹೆಚ್ಚಾಗಿ ಅಲ್ಲಾಹನನ್ನು ಸ್ಮರಿಸಿರಿ. ‘ನಮ್ಮ ಪ್ರಭು, ನಮಗೆ ಇಹದಲ್ಲಿ ಕೊಡು’ ಎಂದು ಮಾತ್ರ ಪಾರ್ಥಿಸುವ ಕೆಲವು ಜನರಿದ್ದಾರೆ. ಅಂಥವರಿಗೆ ಪರಲೋಕದಲ್ಲಿ ಯಾವುದೇ ಭಾಗ್ಯವಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,201,'2:201','‘ನಮ್ಮ ಪ್ರಭೂ, ನಮಗೆ ಇಹದಲ್ಲೂ ಹಿತ ನೀಡು, ಪರದಲ್ಲೂ ಹಿತ ನೀಡು, ನರಕದಿಂದ ನಮ್ಮನ್ನು ಪಾರು ಮಾಡು’ ಎಂದು ಪ್ರಾರ್ಥಿಸುವವರಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(2,202,'2:202','ಅಂಥವರಿಗೆ ಅವರ ಸಾಧನೆಯ ಸೌಭಾಗ್ಯ ಪ್ರಾಪ್ತವಿದೆ. ಅಲ್ಲಾಹನು ಶೀಘ್ರ ತಪಾಸಣೆಗಾರನು.');
INSERT INTO "kannada" VALUES(2,203,'2:203','ಎಣಿಸಲ್ಪಟ್ಟ ದಿನಗಳಲ್ಲಿ ಅಲ್ಲಾಹನ ಸ್ಮರಣೆ ಮಾಡಿರಿ. ಯಾರಾದರೂ ಎರಡೇ ದಿನಗಳಲ್ಲಿ ತ್ವರೆ ಮಾಡಿದರೂ ಪರವಾಗಿಲ್ಲ. ತಡಮಾಡುವು ದಕ್ಕೂ ಅಭ್ಯಂತರವಿಲ್ಲ. ಇದು ತತ್ವ ನಿಷ್ಠೆಯುಳ್ಳವರಿಗೆ. ನೀವು ಅಲ್ಲಾಹನಿಗೆ ಹೆದರಿರಿ. ನೀವು ಆತನ ಬಳಿ ಜಮಾಯಿಸಲಿದ್ದೀರಿ ಎಂಬುದು ತಿಳಿದಿರಲಿ.');
INSERT INTO "kannada" VALUES(2,204,'2:204','ಜನರ ಪೈಕಿ ಒಬ್ಬನ ಮಾತು ಇಹದ ಬಾಳಲ್ಲಿ ನಿಮಗೆ ಆಕರ್ಷಕವೆನಿಸುತ್ತದೆ. ತಾನು ಪ್ರಾಮಾ ಣಿಕನೆಂದು ಆತ ಅಲ್ಲಾಹನ ಆಣೆ ಹಾಕುತ್ತಾನೆ. ವಾಸ್ತವದಲ್ಲಿ ಆತ ವೈರಿಗಳಲ್ಲಿ ಅತ್ಯಂತ ನಿಷ್ಠುರನು.');
INSERT INTO "kannada" VALUES(2,205,'2:205','(ನಿಮ್ಮ ಬಳಿಯಿಂದ) ಅವನು ಹೊರಟು ಹೋದರೆ ಭೂಮಿ ಮೇಲೆ ಕೇಡು ಹರಡಲೂ, ಕೃಷಿ ನಾಶ, ವಂಶನಾಶಕ್ಕೂ ಶತ ಪ್ರಯತ್ನ ಮಾಡುತ್ತಾನೆ. ವಾಸ್ತವ ದಲ್ಲಿ ಅಲ್ಲಾಹನು ಕೇಡನ್ನು ಇಷ್ಟಪಡುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(2,206,'2:206','ಆತನಲ್ಲಿ ‘ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡು’ ಅಂದರೆ ಅವನಿಗೆ ತನ್ನ ದುರಭಿಮಾನವು ಪಾಪಕ್ಕೆ ತಳ್ಳುತ್ತದೆ. ಅವನಿಗೆ ನರಕವು ಸಾಕು. ಅದೆಂತಹ ನೀಚವಾದ ಶಯನಗೃಹ !');
INSERT INTO "kannada" VALUES(2,207,'2:207','ಅಲ್ಲಾಹನ ಒಲವಿಗಾಗಿ ಆತ್ಮಾರ್ಪಣೆ ಮಾಡುವ ಮನುಷ್ಯರೂ ಇದ್ದಾರೆ. ಅಲ್ಲಾಹನು ದಾಸರಲ್ಲಿ ಬಹಳ ದಯೆಯುಳ್ಳವನು.');
INSERT INTO "kannada" VALUES(2,208,'2:208','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ಇಸ್ಲಾಮಿನಲ್ಲಿ ಪೂರ್ಣವಾಗಿ ಪ್ರವೇಶಿಸಿರಿ. ಶೈತಾನನ ಹೆಜ್ಜೆಗಳನ್ನು ಅನುಸರಿಸದಿರಿ. ಖಂಡಿತ ಅವನು ನಿಮ್ಮ ಬದ್ಧ ವೈರಿ .');
INSERT INTO "kannada" VALUES(2,209,'2:209','ಸ್ಪಷ್ಟ ರುಜುವಾತುಗಳು ನಿಮ್ಮ ಕೈ ಸೇರಿದ ಬಳಿಕವೂ ನೀವು ತಪ್ಪಿ ನಡೆದರೆ ಅಲ್ಲಾಹನು ಪರಮ ಪ್ರತಾಪಿ, ಯುಕ್ತಿಪೂರ್ಣ ಎಂಬುದು ನಿಮಗೆ ತಿಳಿದಿರಲಿ.');
INSERT INTO "kannada" VALUES(2,210,'2:210','ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆಯು ದಟ್ಟ ಕರಿಮೋಡಗಳ ನಡುವೆ ಮಲಾಇಕತ್‍ಗಳೊಂದಿಗೆ ಎರಗಿ ತೀರ್ಪು ಜಾರಿಯಾಗುವುದನ್ನು ಅವರು ನಿರೀಕ್ಷಿಸುತ್ತಿರುವರೇನು? ಸರ್ವ ಸಂಗತಿಗಳ ನಿರ್ಗಮನವು ಅಲ್ಲಾಹನ ಕಡೆಗೇ .');
INSERT INTO "kannada" VALUES(2,211,'2:211','ಇಸ್ರಾಈಲ್ ಸಂತತಿಗಳೊಂದಿಗೆ ಕೇಳಿರಿ. ನಾವು ಅವರಿಗೆ ಎಷ್ಟು ಸ್ಪಷ್ಟ ಪುರಾವೆಗಳನ್ನು ಕೊಟ್ಟೆವು. ಅಲ್ಲಾಹನ ಅನುಗ್ರಹ ಒದಗಿಬಂದ ಬಳಿಕ ಅದನ್ನು (ಸತ್ಯನಿಷೇಧಕ್ಕೆ) ಬದಲಾಯಿಸಿಕೊಳ್ಳುವುದಾದರೆ; ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಘನಘೋರ ದಂಡನೆಗಾರನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(2,212,'2:212','ಅವಿಶ್ವಾಸಿಗಳಿಗೆ ಲೌಕಿಕ ಜೀವನವನ್ನು ಚಂದ ಣಿಸಿಕೊಡಲಾಗಿದೆ. ವಿಶ್ವಾಸಿಗಳನ್ನು ಅವ ಪರಿಹಾಸ್ಯ ಮಾಡುತ್ತಾರೆ. ಆದರೆ ಪರಲೋಕದಲ್ಲಿ ವಿಶ್ವಾಸಿಗಳು ಈ ಅವಿಶ್ವಾಸಿಗಳಿಗಿಂತ ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಲೆಕ್ಕವಿಲ್ಲದೆ ಕೊಡುತ್ತಾನೆ. ಉನ್ನತ ರಾಗಿರುವರು. ಅಲ್ಲಾಹನು ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಲೆಕ್ಕವಿಲ್ಲದೆ ಕೊಡುತ್ತಾನೆ. ಂ');
INSERT INTO "kannada" VALUES(2,213,'2:213','ಜನರು ಏಕಸಮಾಜವಾಗಿದ್ದರು. ಅವರಲ್ಲಿ ಛಿದ್ರತೆಯುಂಟಾದಾಗ ಸುವಾರ್ತೆ ಮತ್ತು ಮುನ್ನೆಚ್ಚರಿಕೆ ನೀಡುವ ಪ್ರವಾದಿಗಳನ್ನು ಅಲ್ಲಾಹನು ಕಳುಹಿಸಿದನು. ಜನರ ವಿವಾದಗಳಿಗೆ ತೀರ್ಪು ನೀಡಲು ಸತ್ಯಸಂಧ ಗ್ರಂಥವನ್ನು ಅವರೊಂದಿಗೆ ರವಾನಿಸಿದನು. ಆದರೆ ಆ ಜನರು ಆ ಗ್ರಂಥ ಕೈಸೇರಿ ಸ್ಪಷ್ಟ ದಾಖಲೆಗಳು ಲಭ್ಯವಾದ ನಂತರ ಕೂಡಾ ಪರಸ್ಪರ ಮಾತ್ಸರ್ಯದ ದೆಸೆಯಿಂದ ಭಿನ್ನಾಭಿಪ್ರಾಯ ಹೊಂದಿದರು. ಹಾಗೆ ಅವರು ಭಿನ್ನಮತ ಹೊಂದಿದ್ದ ಸರಿಯಾದ ಆದರ್ಶಕ್ಕೆ ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳನ್ನು ಅಲ್ಲಾಹನು ತನ್ನ ಅನುಮತಿ ಪ್ರಕಾರ ಹಚ್ಚಿದನು. ಅಲ್ಲಾಹನು ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಸರಿದಾರಿ ತೋರುವನು.');
INSERT INTO "kannada" VALUES(2,214,'2:214','ನಿಮ್ಮ ಪೂರ್ವಿಕರ ಕಟು ಅನುಭವ ನಿಮಗೂ ಬಾರದೆ ಸ್ವರ್ಗಪ್ರವೇಶ ಸಾಧ್ಯವೆಂದು ಭಾವಿಸುತ್ತಿದ್ದೀರಾ? ಅವರಿಗೆ ಕಡುದಾರಿದ್ರ್ಯಗಳೂ ಕಠಿಣ ರೋಗಗಳೂ ಬಾಧಿಸಿದ್ದುವು. ಪ್ರವಾದಿಗಳೂ ವಿಶ್ವಾಸಿಗಳೂ ‘ಅಲ್ಲಾಹನ ನೆರವು ಯಾವಾಗ ?’ ಎಂದು ಕೇಳುವಷ್ಟು ಕಷ್ಟಕೋಟಲೆಗಳಿಂದ ಕಂಪಿಸಲ್ಪಟ್ಟಿದ್ದರು. ಅಲ್ಲಾಹನ ಸಹಾಯ ಸನ್ನಿಹಿತವಾಗಿದೆ. (ಎಂದವರಿಗೆ ಸಾಂತ್ವನ ನೀಡಲಾಗಿತ್ತು) .');
INSERT INTO "kannada" VALUES(2,215,'2:215','(ನಬಿಯೇ) ಅವರು ಏನನ್ನು, ಯಾರಿಗೆ ದಾನ ಮಾಡಬೇಕೆಂದು ನಿಮ್ಮಲ್ಲಿ ಕೇಳುತ್ತಿದ್ದಾರೆ. ಹೇಳಿರಿ; ಯಾವ ಮೌಲ್ಯವನ್ನು ನೀಡಿದರೂ ಫಲವಿದೆ. ಹೆತ್ತವರು, ಬಂಧುಗಳು, ಅನಾಥರು, ನಿರ್ಗತಿಕರು ಹಾಗೂ ಸಂಚಾರಿಗಳಿಗೆ ನೀಡಬಹುದು. (ದಾನವಾಗಲಿ ಮತ್ತೇನಾಗಲಿ) ಯಾವ ಒಳಿತನ್ನು ನೀವು ಮಾಡುವುದಾದರೂ ಅಲ್ಲಾಹನು ಅದನ್ನು ಖಂಡಿತ ಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(2,216,'2:216','ಧರ್ಮಯುದ್ಧವು ನಿಮಗೆ ಅನಿಷ್ಟಕರವಾಗಿದ್ದರೂ ನಿಮಗದನ್ನು ಶಾಸನಗೊಳಿಸಲಾಗಿದೆ. ಏನಾದರೊಂದು ನಿಮಗೆ ಅಪ್ರಿಯವಾಗಿರಬಹುದು. ನಿಜದಲ್ಲಿ ಅದು ನಿಮಗೆ ಗುಣಕರವಾಗಿರಬಹುದು. ಯಾವುದಾದರೊಂದು ನಿಮಗೆ ಪ್ರಿಯವಾಗಿರಬಹುದು. ನಿಜದಲ್ಲಿ ಅದು ನಿಮಗೆ ಕೇಡಾಗಿರ ಬಹುದು. ಅಲ್ಲಾಹನು ಬಲ್ಲವನು. ನೀವಲ್ಲ ಬಲ್ಲವರು .');
INSERT INTO "kannada" VALUES(2,217,'2:217','ಪವಿತ್ರ ಮಾಸದಲ್ಲಿ ಸಮರಕ್ಕಿಳಿಯುವ ಕುರಿತು ಅವರು ನಿಮ್ಮಲ್ಲಿ ಪ್ರಶ್ನಿಸುತ್ತಿದ್ದಾರೆ. ಹೇಳಿರಿ; ಆ ಮಾಸದಲ್ಲಿ ಸಮರವು ಮಹಾಪರಾಧ ನಿಜ. ಆದರೆ ಅಲ್ಲಾಹನ ಮಾರ್ಗಕ್ಕೆ ಅಡ್ಡಿಪಡಿಸುವುದು, ಅವನಿಗೆ ದ್ರೋಹ ವೆಸಗುವುದು, ಮಸ್ಜಿದುಲ್ ಹರಾಮ್‍ನಿಂದ ತಡೆಯುವುದು, ಅಲ್ಲಿನ ನಿವಾಸಿಗಳನ್ನು ಹೊರಹಾಕುವುದು ಅಲ್ಲಾಹನ ದೃಷ್ಟಿಯಲ್ಲಿ ಅದಕ್ಕಿಂತಲೂ ಘೋರ ಅಪರಾಧವಾಗಿದೆ . ಅರಾಜಕತೆಯು ಕೊಲೆಗಿಂತ ಗಂಭೀರ. ಅವರಿಗೆ ಸಾಧ್ಯವಾದರೆ ನಿಮ್ಮನ್ನು ನಿಮ್ಮ ಧರ್ಮದಿಂದ ವಿಮುಖಗೊಳಿಸುವವರೆಗೂ ನಿಮ್ಮೆದುರು ಯುದ್ಧ ಮಾಡುತ್ತಲೇ ಇರುವರು. ನಿಮ್ಮಿಂದ ಯಾವನು ಸ್ವಧರ್ಮದಿಂದ ಮರಳಿ ಅಧರ್ಮಿಯಾಗಿ ಸಾಯು ತ್ತಾನೋ ಅಂತಹವನ ಸುಕಾರ್ಯಗಳು ಇಹ - ಪರದಲ್ಲೂ ಫಲಹೀನವಾಗುವುವು. ಅವರು ನರಕದವರು. ಅದರಲ್ಲಿ ಅವರು ಚಿರವಾಸಿಗಳು.');
INSERT INTO "kannada" VALUES(2,218,'2:218','ಸತ್ಯವಿಶ್ವಾಸವಿಟ್ಟವರು, ಧರ್ಮದ ಅನಿವಾ ರ್ಯತೆಗಾಗಿ ದೇಶತ್ಯಾಗ ಮಾಡಿದವರು ಹಾಗೂ ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಧರ್ಮಯುದ್ಧ ಮಾಡಿದವರೇ ಅಲ್ಲಾಹನ ಕೃಪಾಕಾಂಕ್ಷಿಗಳು . ಅಲ್ಲಾಹನು ತುಂಬ ಕ್ಷಮಾಶೀಲನು. ಪರಮ ದಯಾನಿಧಿ.');
INSERT INTO "kannada" VALUES(2,219,'2:219','(ನಬಿಯೇ) ಮದ್ಯಪಾನ ಮತ್ತು ಜೂಜಾಟದ ಕುರಿತು ಅವರು ನಿಮ್ಮಲ್ಲಿ ಪ್ರಶ್ನಿಸುತ್ತಿದ್ದಾರೆ. ಹೇಳಿರಿ; ಅವೆರಡರಲ್ಲೂ ಗಂಭೀರ ದೋಷವಿದೆ. ಅವೆರಡರಲ್ಲಿ ಜನರಿಗೆ ಕೆಲವು ಪ್ರಯೋಜನ ಗಳಿದ್ದರೂ ಅವುಗಳ ದೋಷವು ಪ್ರಯೋಜನಕ್ಕಿಂತ ಆಪತ್ಕಾರಿ. ದಾನ ಎಷ್ಟು ಮಾಡ ಬೇಕೆಂದು ಅವರು ನಿಮ್ಮಲ್ಲಿ ಕೇಳುತ್ತಿದ್ದಾರೆ. ‘ಅವಶ್ಯಕ್ಕೆ ಮೀರಿದ್ದನ್ನು’ ಅಂತ ಹೇಳಿರಿ ಂ. ಈ ರೀತಿ ಅಲ್ಲಾಹನು ಸ್ಪಷ್ಟ ಮಾತುಗಳನ್ನು ವಿವರಿಸುತ್ತಾನೆ. ನೀವು ಇಹ-ಪರ ವಿಚಾರಗಳಲ್ಲಿ ಚಿಂತನಾಶೀಲರಾಗಿರಬೇಕೆಂದು.');
INSERT INTO "kannada" VALUES(2,220,'2:220','ತಬ್ಬಲಿಗಳ ಬಗ್ಗೆ ಅವರು ನಿಮ್ಮಲ್ಲಿ ಕೇಳುತ್ತಿದ್ದಾರೆ. ಹೇಳಿರಿ; ಅವರ ಸೊತ್ತುಗಳ ಅಭಿವೃದ್ಧಿ ಮಾಡುವುದು ಶ್ರೇಯಸ್ಕರ. ಅವರ ವೆಚ್ಚವನ್ನು ನಿಮ್ಮ ವೆಚ್ಚದೊಂದಿಗೆ ಬೆರೆಸಿ ಸಹಬಾಳ್ವೆ ನಡೆಸುವುದಾದರೆ ಅವರು ನಿಮ್ಮ ಸೋದರರೇ ತಾನೆ? ದುರುದ್ದೇಶದವನನ್ನು ಸದುದ್ದೇಶದವನಿಂದ ಬೇರ್ಪಡಿಸಿ ತಿಳಿಯ ಬಲ್ಲವನಾಗಿದ್ದಾನೆ ಅಲ್ಲಾಹು. ಅಲ್ಲಾಹನು ಇಚ್ಛಿಸಿದ್ದರೆ ನಿಮಗೆ ಪ್ರಯಾಸ ಕೊಡುತ್ತಿದ್ದನು. ಅಲ್ಲಾಹನು ಪ್ರತಾಪಿಯು, ಯುಕ್ತಿಪೂರ್ಣನು.');
INSERT INTO "kannada" VALUES(2,221,'2:221','ಬಹುದೇವ ವಿಶ್ವಾಸಿನಿಯರನ್ನು ಅವರು ಸತ್ಯ ವಿಶ್ವಾಸಿನಿಯಾಗುವ ತನಕ ನೀವು ಲಗ್ನವಾಗದಿರಿ. ಸತ್ಯವಿಶ್ವಾಸಿನಿ ದಾಸಿಯು ಬಹುದೇವ ವಿಶ್ವಾಸಿನಿಯಾದ ಸ್ವತಂತ್ರ ಹೆಣ್ಣಿಗಿಂತ ಉತ್ತಮಳು. ಅವಳು ನಿಮಗೆ ಕೌತುಕ ಹುಟ್ಟಿಸಿದರೂ ಸರಿ. ಬಹುದೇವ ವಿಶ್ವಾಸಿಗಳಿಗೆ ಅವರು ಸತ್ಯವಿಶ್ವಾಸಿಯಾಗುವವರೆಗೂ ನಿಮ್ಮ ಸ್ತ್ರೀಯರನ್ನು ಲಗ್ನ ಮಾಡಿಕೊಡಬೇಡಿ. ಸತ್ಯವಿಶ್ವಾಸಿ ಗುಲಾಮನು ಬಹುದೇವ ವಿಶ್ವಾಸಿಯಾದ ಸ್ವತಂತ್ರ ಪುರುಷನಿಗಿಂತ ಶ್ರೇಷ್ಟ. ಅವನು ನಿಮಗೆ ಕೌತುಕವನ್ನುಂಟು ಮಾಡಿದರೂ ಸರಿ. ಅವರು ನರಕಕ್ಕೆ ಕರೆಯುತ್ತಾರೆ. ಅಲ್ಲಾಹನು ತನ್ನ ಇರಾದೆಯಂತೆ ಸ್ವರ್ಗಕ್ಕೂ ದೋಷಮುಕ್ತಿಗೂ ಕರೆಯುತ್ತಾನೆ. ಆತನು ಜನರಿಗೆ ತನ್ನ ನೀತಿಗಳನ್ನು ವಿಶದಪಡಿಸುತ್ತಿದ್ದಾನೆ. ಅವರು ಪ್ರಜ್ಞಾವಂತರಾಗಲು.');
INSERT INTO "kannada" VALUES(2,222,'2:222','ಆರ್ತವದ ಕುರಿತು ಅವರು ನಿಮ್ಮಲ್ಲಿ ಕೇಳು ತ್ತಿದ್ದಾರೆ. ಹೇಳಿರಿ; ಅದೊಂದು ಮಾಲಿನ್ಯ. ಆರ್ತವ ದೆಸೆಯಲ್ಲಿ ಸ್ತ್ರೀಯರಿಂದ ದೂರವಿರಿ. ಅವರು ಶುಚಿಯಾಗುವವರೆಗೂ ಮೈಥುನಕ್ಕೆ ಸಮೀಪಿ ಸದಿರಿ. ಅವರು ಶುಚಿಯಾದರೆ ಅಲ್ಲಾಹನ ನಿಯಮ ನಿಷ್ಠೆಯೊಂದಿಗೆ ಅವರನ್ನು ಸಮೀಪಿಸಿರಿ. ಅಲ್ಲಾಹನು ಪಶ್ಚಾತಾಪಿಗಳನ್ನು ಇಷ್ಟಪಡುತ್ತಾನೆ. ನಿರ್ಮಲರನ್ನೂ ಇಷ್ಟ ಪಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(2,223,'2:223','ನಿಮ್ಮ ಮಹಿಳೆಯರು ನಿಮ್ಮ ಹೊಲ. ನಿಮ್ಮ ಹೊಲಕ್ಕೆ ನಿಮ್ಮಿಚ್ಛೆಯಂತೆ ಹೋಗಿ. ಸ್ವಹಿತಕ್ಕಾಗಿ ಧಾರ್ಮಿಕ ಪೂರ್ವವಿಧಿಗಳನ್ನು ಪಾಲಿಸಿರಿ. ಅಲ್ಲಾಹನ ವಿಧಿ-ನಿಷೇಧಗಳಿಗೆ ಬದ್ಧರಾಗಿರಿ . ಆತನನ್ನು ನೀವು ಭೇಟಿಯಾಗಲಿದ್ದೀರೆಂಬುದು ತಿಳಿದಿರಲಿ. (ಓ ಪ್ರವಾದಿಯವರೇ) ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಸುವಾರ್ತೆ ಕೊಡಿರಿ.');
INSERT INTO "kannada" VALUES(2,224,'2:224','ಹಿತಕಾರ್ಯ, ದೇವಭಕ್ತಿ, ಜನರೆಡೆಯಲ್ಲಿ ರಾಜಿ ಕಾರ್ಯಗಳಿಗೆ ನೀವು ಅಲ್ಲಾಹನ ಮೇಲೆ ಹಾಕಿದ ಆಣೆ ಅಡ್ಡಿಯಾಗದಿರಲಿ . ಅಲ್ಲಾಹನು ಎಲ್ಲ ಆಲಿಸುವವನು. ಎಲ್ಲ ಬಲ್ಲವನು.');
INSERT INTO "kannada" VALUES(2,225,'2:225','ಅನುದ್ದಿಷ್ಠ ಆಣೆಗಳಿಗೆ ಅಲ್ಲಾಹ್ ನಿಮ್ಮನ್ನು ದಂಡಿಸುವುದಿಲ್ಲ. ಆದರೆ ಅವನು ಉದ್ದೇಶಪೂರ್ವಕ ಆಣೆಗಳಿಗೆ ನಿಮ್ಮನ್ನು ದಂಡಿಸುವನು. ಅಲ್ಲಾಹನು ಕ್ಷಮಾಶೀಲನು, ಸಂಯಮಿಯು.');
INSERT INTO "kannada" VALUES(2,226,'2:226','ಮಡದಿಯೊಂದಿಗೆ ದೇಹಸಂಪರ್ಕ ಮಾಡೆನೆಂದು ಆಣೆ ಹಾಕಿದವರಿಗೆ ನಾಲ್ಕು ಮಾಸಗಳ ವಾಯಿದೆಯಿದೆ . ಆ ಅವಧಿಯೊಳಗೆ ಹಿಂತೆಗೆದು ಕೊಂಡರೆ ಖಂಡಿತ ಅಲ್ಲಾಹನು ಪಾಪನಾಶಕ, ಮಹಾಕರುಣಿ.');
INSERT INTO "kannada" VALUES(2,227,'2:227','ಇನ್ನು ಅವರು ತಲಾಖ್‍ಗೇ ನಿರ್ಧರಿಸಿದಲ್ಲಿ ಖಂಡಿತ ಅಲ್ಲಾಹನು ಎಲ್ಲ ಆಲಿಸುವವನು. ಎಲ್ಲ ಬಲ್ಲವನು.');
INSERT INTO "kannada" VALUES(2,228,'2:228','ವಿವಾಹ ವಿಚ್ಛೇದಿತೆಯರಿಗೆ ಮೂರು ಆರ್ತವದ ವರೆಗೆ ನಿರೀಕ್ಷಣಾ ಅವಧಿಯಾಗಿರುತ್ತದೆ . ಅವರಿಗೆ ಅಲ್ಲಾಹು ಮತ್ತು ಪರಲೋಕದಲ್ಲಿ ನಂಬಿಕೆಯಿದ್ದಲ್ಲಿ ತಮ್ಮ ಗರ್ಭಾಶಯದಲ್ಲಿರುವ ಅಲ್ಲಾಹನ ಸೃಷ್ಟಿಯನ್ನು ಬಚ್ಚಿಡುವುದು ಸಮ್ಮತವಲ್ಲ. ಸಂಬಂಧ ಸುಧಾರಿಸುವ ಇರಾದೆ ಇದ್ದಲ್ಲಿ ಈ ಕಾಲಾವಧಿಯೊಳಗೆ ಮರಳಿ ಸ್ವೀಕರಿಸುವ ಅರ್ಹತೆ ಪತಿಯಂದಿರಿಗಿದೆ. ಸ್ತ್ರೀಯರಿಗೆ ಭಾಧ್ಯತೆಗ ಳಿರುವಂತೆಯೇ ನ್ಯಾಯೋಚಿತ ಹಕ್ಕುಗಳೂ ಇವೆ. ಆದರೆ ಪುರುಷರಿಗೆ ಅವರಿಗಿಂತ ಒಂದು ಪದವಿ ಮೇಲಿದೆ. ಅಲ್ಲಾಹನು ಪ್ರತಾಪಿಯು. ಯುಕ್ತಿಯುಕ್ತನು.');
INSERT INTO "kannada" VALUES(2,229,'2:229','ಹಿಂತೆಗೆದುಕೊಳ್ಳಲು ಅವಕಾಶವಿರುವ ತಲಾಖ್ ಎರಡು ಬಾರಿ . ಆದ್ದರಿಂದ ನ್ಯಾಯೋಚಿತ ವಾಗಿ ಸಂಬಂಧ ಉಳಿಸಿಕೊಳ್ಳುವುದು ಅಥವಾ ಹಿತಕರ ರೀತಿಯಲ್ಲಿ ಬಿಡುಗಡೆ ಮಾಡುವುದು . ಪತಿಯಂದಿರು ಮಡದಿಗೆ ಹಿಂದೆ ನೀಡಿದ್ದ ಯಾವೊಂದನ್ನೂ ಮರು ಸ್ವಾಧೀನಪಡಿಸಿ ಕೊಳ್ಳುವುದು ಸಮ್ಮತವಲ್ಲ. ಇದು ಅವರಿಬ್ಬರೂ ಅಲ್ಲಾಹನ ವಿಧಿಯ ಹದ್ದನ್ನು ಮೀರುವ ಭೀತಿ ಇಲ್ಲದಿದ್ದರೆ ಮಾತ್ರ. ಅಲ್ಲಾಹನ ವಿಧಿಯ ಮೇರೆಗಳನ್ನು ದಂಪತಿಗಳು ಮೀರುವರೆಂದು ಭಯಾಶಂಕೆ ನಿಮಗಿದ್ದಲ್ಲಿ ಅವಳು ಪರಿಹಾರ ನೀಡಿ ಮುಕ್ತಳಾಗುವುದರಲ್ಲಿ ಈರ್ವರಿಗೂ ತಪ್ಪಿಲ್ಲ . ಇವು ಅಲ್ಲಾಹನ ನಿಯಮಗಳು. ಇವನ್ನು ಮೀರದಿರಿ. ಅಲ್ಲಾಹನ ಎಲ್ಲೆಗಳನ್ನು ಯಾರು ಮೀರುತ್ತಾರೋ ಅವರೇ ಅಕ್ರಮಿಗಳು.');
INSERT INTO "kannada" VALUES(2,230,'2:230','ಇನ್ನು (ಮೂರನೆಯ) ತಲಾಖನ್ನು ಹೇಳಿದರೆ ಅನಂತರ ಅವಳು ಇನ್ನೊಬ್ಬ ಪತಿಗೆ ಲಗ್ನವಾಗದೆ ಅವಳು ಇವನಿಗೆ ಧರ್ಮಸಮ್ಮತವಾಗಲಾರಳು. ಎರಡನೆ ಪತಿ ಅವಳಿಗೆ ವಿಚ್ಛೇಧನೆ ಕೊಟ್ಟರೆ ಹಾಗೂ ಅಲ್ಲಾಹನ ಮೇರೆಗಳನ್ನು ಈರ್ವರೂ ಪಾಲಿಸುವರೆಂಬ ಯೋಚನೆ ಅವರಿಗಿದ್ದಲ್ಲಿ ಮೊದಲನೆ ವಿವಾಹ ಸಂಬಂಧವನ್ನು ಮರುಸ್ಥಾಪನೆ ಮಾಡಬಹುದಾಗಿದೆ . ಮನನ ಮಾಡಿಕೊಳ್ಳುವ ಜನರಿಗೆ ಅಲ್ಲಾಹನು ವಿಶದಪಡಿಸುತ್ತಿರುವ ಆತನ ಮೇರೆಗಳಿವು.');
INSERT INTO "kannada" VALUES(2,231,'2:231','ನೀವು ವಿಚ್ಛೇಧನೆ ನೀಡಿ ಅವರ ನಿರೀಕ್ಷಣಾ ಕಾಲಾವಧಿ ಮುಕ್ತಾಯದ ಹಂತದಲ್ಲಾದರೆ ನ್ಯಾಯೋಚಿತವಾಗಿ ವಿಚ್ಛೇಧನೆ ರದ್ದುಪಡಿಸಿ ಜೊತೆಯಾಗಿರಿ. ಅಥವಾ ನ್ಯಾಯೋಚಿತ ರೀತಿಯಲ್ಲಿ ಬಿಡುಗಡೆ ಮಾಡಿರಿ. ಪೀಡಿಸುವ ಸಲುವಾಗಿ ಹಿಡಿದಿಟ್ಟುಕೊಳ್ಳುವ ದ್ರೋಹವನ್ನು ಮಾಡದಿರಿ. ಹಾಗೆ ಮಾಡುವವನು ತನಗೆ ತಾನೇ ಅನ್ಯಾಯವೆಸಗುತ್ತಾನೆ. ಅಲ್ಲಾಹನ ಕಾಯ್ದೆಗಳನ್ನು ವ್ಯಂಗ್ಯ ಮಾಡದಿರಿ. ನಿಮಗಿತ್ತ ಅಲ್ಲಾಹನ ಅನುಗ್ರಹಗಳು ಹಾಗೂ ನಿಮಗೆ ರವಾನಿಸಿದ ದೇವ ಗ್ರಂಥ, ಮತ್ತು ಉಪದೇಶಾ ತ್ಮಕ ತತ್ವಜ್ಞಾನಗಳನ್ನು ನೆನಪಿಸಿಕೊಳ್ಳಿರಿ. ಅಲ್ಲಾಹನ ಬಗ್ಗೆ ಎಚ್ಚರವಿರಲಿ. ಅವನು ಎಲ್ಲ ಬಲ್ಲವನೆಂಬುದು ನಿಮಗೆ ತಿಳಿದಿರಲಿ.');
INSERT INTO "kannada" VALUES(2,232,'2:232','ನೀವು ಸ್ತ್ರೀಯರಿಗೆ ವಿಚ್ಛೇಧನೆ ಕೊಟ್ಟ ಬಳಿಕ ಅವರ (ವಿಚ್ಛೇಧಿತೆಯರ) ಕಾಲಾವಧಿ ತಲುಪಿದರೆ ಅವರಿಬ್ಬರೂ ಧರ್ಮಬದ್ಧವಾಗಿ ತೃಪ್ತರಾಗಿ ಸಂಬಂಧ ಮರುಸ್ಥಾಪಿಸುವುದಕ್ಕೆ ನೀವು ಅಡ್ಡಿಪಡಿಸದಿರಿ. ನಿಮ್ಮ ಪೈಕಿ ಅಲ್ಲಾಹ್ ಮತ್ತು ಪರಲೋಕದಲ್ಲಿ ನಂಬಿಕೆಯುಳ್ಳವರಿಗೆ ನೀಡುತ್ತಿರುವ ಉಪದೇಶವಿದು. ಇದು ನಿಮಗೆ ಬಹಳ ಶುದ್ಧ ಹಾಗೂ ಅಕಳಂಕಿತ ಮಾರ್ಗ. ಅಲ್ಲಾಹನಿಗೆ ಗೊತ್ತು. ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,233,'2:233','ಅಮ್ಮಂದಿರು ತಮ್ಮ ಶಿಶುಗಳಿಗೆ ಪೂರ್ಣವಾದ ಎರಡು ವರ್ಷ ಎದೆ ಹಾಲುಣಿಸಬೇಕು. ಇದು ಪೂರ್ಣಾವಧಿ ಹಾಲುಣಿಸಬೇಕೆಂಬ ಇರಾದೆಯುಳ್ಳ ಸತಿ ಅಥವಾ ಪತಿಯ ಪರವಾಗಿ ನೀಡಬೇಕಾದ ತೀರ್ಪು. ಆ ಕಾಲಾವಧಿಯಲ್ಲಿ ಅವಳು ವಿಚ್ಛೇಧಿತೆಯಾಗಿದ್ದರೆ ಅವಳಿಗೆ ನ್ಯಾಯೋಚಿತ ಅನ್ನ-ವಸ್ತ್ರಗಳ ಪೂರೈಕೆಯು ಮಗುವಿನ ತಂದೆಯ ಬಾಧ್ಯತೆ. ಅಸಾಧ್ಯ ಹೊಣೆಗಾರಿಕೆಯನ್ನು ಯಾರಿಗೂ ವಿಧಿಸ ಲಾಗುವುದಿಲ್ಲ. ತನ್ನ ಮಗುವಿನ ದೆಸೆಯಿಂದ ಯಾವ ತಾಯಿಯೂ ತೊಂದರೆ ಗೊಳಗಾಗ ಬಾರದು. ಯಾವ ತಂದೆಯೂ ತನ್ನ ಮಗುವಿನ ಕಾರಣದಿಂದ ತೊಂದರೆಗೊಳ ಗಾಗಬಾರದು. ವಾರೀಸುದಾರನಿಗೂ ಇದೇ ಬಾಧ್ಯತೆಯುಂಟು. ಇನ್ನು ತಾಯಿ ತಂದೆ ಪರಸ್ಪರ ತೃಪ್ತಿ ಹಾಗೂ ಸಮಾಲೋಚಿತ ಒಮ್ಮತದಿಂದ ಅವಧಿ ಪೂರ್ವದಲ್ಲೇ ಹಾಲು ಬಿಡಿಸುವುದಕ್ಕೆ ಅಭ್ಯಂತರ ವಿಲ್ಲ. ಇನ್ನು ಬೇರೆ ಸ್ತ್ರೀಗೆ ನ್ಯಾಯೋಚಿತ ವೇತನ ನೀಡಿ ಹಾಲುಣಿಸುವ ಉದ್ದೇಶ ಪತಿಗಿದ್ದರೆ ಅಭ್ಯಂತರವಿಲ್ಲ , ಅಲ್ಲಾಹ ನನ್ನು ಭಯಪಟ್ಟುಕೊಳ್ಳಿರಿ. ನಿಮ್ಮ ಕೃತ್ಯಗಳ ಮೇಲೆ ಅಲ್ಲಾಹನ ದೃಷ್ಟಿಯಿರುವುದು ತಿಳಿದಿರಿ.');
INSERT INTO "kannada" VALUES(2,234,'2:234','ನಿಮ್ಮ ಪೈಕಿ ತೀರಿಕೊಂಡವರ ಅಗಲಿತ ಪತ್ನಿಯರು ನಾಲ್ಕು ಮಾಸ, ಹತ್ತು ದಿವಸಗಳ ಇದ್ದತ್ ಅನುಸರಿಸಬೇಕು. ಈ ಕಾಲಾವಧಿ ಮುಗಿದರೆ ಅವರಿಗೆ ಸ್ವಂತ ವಿಷಯದಲ್ಲಿ ಯಥಾಸ್ಥಿತಿಯಂತೆ ನಡೆದುಕೊಳ್ಳಲು ನೀವು ಅವಕಾಶ ಮಾಡಿಕೊಡುವುದರಲ್ಲಿ ತಪ್ಪಿಲ್ಲ. ನಿಮ್ಮ ನಡವಳಿಕೆಯ ಬಗ್ಗೆ ಅಲ್ಲಾಹನು ನಿಗಾ ಇಟ್ಟಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(2,235,'2:235','ಇದ್ದತ್‍ನಲ್ಲಿರುವವಳನ್ನು ಕಾಲಾವಧಿ ಮುಗಿದ ನಂತರ ಮದುವೆಯಾಗುವ ಇಚ್ಛೆಯುಳ್ಳವರು ಆ ಬಗ್ಗೆ ಅವಳಿಗೆ ಸೂಚ್ಯವಾಗಿ ತಿಳಿಸುವುದಾಗಲಿ, ಮನದಲ್ಲೇ ಇಂಗಿತವನ್ನಿಟ್ಟುಕೊಳ್ಳುವುದಾಗಲಿ ನಿಮಗೆ ನಿಷೇಧಿಸಿಲ್ಲ. ಮುಂದೆ ಅವಳೊಂದಿಗೆ ಆ ಪ್ರಸ್ತಾಪ ನೀವು ಮಾಡಿಯೇ ಮಾಡುತ್ತೀರೆಂದು ಅಲ್ಲಾಹನಿಗೆ ಗೊತ್ತು. ಆದರೆ ಇಂತಹ ಧರ್ಮ ಸಮ್ಮತ ವಿಧಾನ ಅನುಸರಿಸಬಹುದೇ ಹೊರತು ಸ್ಪಷ್ಟವಾಗಿ ವಿವಾಹದ ವಾಗ್ದಾನ ನೀಡಬಾರದು. ಲಿಖಿತ ಕಾನೂನು ರೀತ್ಯಾ ನಿಶ್ಚಿತವಾದ ಈ ಇದ್ದತ್‍ನ ಕಾಲಾವಧಿ ಮುಗಿಯುವವರೆಗೂ ವಿವಾಹ ಬಂಧನದ ನಿರ್ಧಾರ ಮಾಡಬಾರದು. ನಿಮ್ಮ ಅಂತರಂಗವನ್ನು ಅಲ್ಲಾಹನು ಬಲ್ಲವನೆಂದು ತಿಳಿದಿರಲಿ, ಅವನನ್ನು ಭಯಪಡಿರಿ. ಅಲ್ಲಾಹನು ಅತ್ಯಧಿಕ ಕ್ಷಮಾಶೀಲನೂ ಪರಮಸಂಯಮಿಯೂ ಆಗಿದ್ದಾನೆ ಎಂಬುದು ನಿಮಗೆ ತಿಳಿದಿರಲಿ.');
INSERT INTO "kannada" VALUES(2,236,'2:236','ಸ್ತ್ರೀಯ ದೇಹಸ್ಪರ್ಶ ಅಥವಾ ಮಹ್ರ್ ನಿಶ್ಚಯಕ್ಕೆ ಮುನ್ನ ನೀವು ತಲಾಖ್ ಕೊಟ್ಟರೆ ಮಹ್ರ್ ಪಾವತಿ ನಿಮ್ಮ ಹೊಣೆಯಲ್ಲ. ಆದರೂ ಸ್ಥಿತಿವಂತ ತನ್ನ ಬಲದಂತೆ, ಬಡವ ತನ್ನ ಯೋಗ್ಯತೆಯಂತೆ ಅವಳಿಗೆ ಉಡುಗೊರೆ ಕೊಡಬೇಕು. ಧಾರ್ಮಿಕ ವಾಡಿಕೆಯಂತೆ ಈ ಉಡುಗೊರೆ ದಾನವು ಸಂಭಾವಿತರ ಬಾಧ್ಯತೆಯಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(2,237,'2:237','ಅವರಿಗೆ ನೀವು ಒಂದು ಮೊತ್ತವನ್ನು ಮಹ್ರ್ ನಿಶ್ಚೈಸಿದ್ದು ಅವರ ದೇಹಸ್ಪರ್ಶಕ್ಕೆ ಮುನ್ನ ನೀವು ಅವರಿಗೆ ತಲಾಖ್ ನೀಡಿದರೆ ನಿಶ್ಚಿತ ಮೊತ್ತದ ಅರ್ಧಾಂಶವನ್ನು ಅವರಿಗೆ ಪಾವತಿಸ ತಕ್ಕದ್ದು. ಪತ್ನಿಯಂದಿರು ರಿಯಾಯಿತಿ ತೋರಿ ಇಡೀ ಮೊತ್ತವನ್ನು ಬಿಟ್ಟುಕೊಟ್ಟರೆ ಅಥವಾ ವಿವಾಹ ಸಂಬಂಧವನ್ನು ಕೈಯಲ್ಲಿರಿಸಿಕೊಂಡವ (ಗಂಡ)ನು ಮನ್ನಾ ಮಾಡಿದರೆ ಹೊರತು. ಆದರೆ ನೀವು ಉದಾರತೆ ತೋರುವುದು ಧರ್ಮ ನಿಷ್ಟೆಗೆ ನಿಕಟವಾದ ಅಂಶ. ನೀವು ಪರಸ್ಪರ ಉದಾರಿ ಗಳಾಗುವುದನ್ನು ಮರೆಯದಿರಿ. ನಿಮ್ಮ ಪ್ರವರ್ತಿಗಳನ್ನು ಅಲ್ಲಾಹನು ಕಾಣುತ್ತಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(2,238,'2:238','ಐದು ಹೊತ್ತಿನ ನಮಾಝ್, ವಿಶೇಷತಃ ಅವುಗಳ ಪೈಕಿ `ಅಲ್‍ವುಸ್ತಾ’ ನಮಾಝನ್ನು ಕ್ಲಪ್ತ ಸಮಯಕ್ಕೆ ತಪ್ಪದೆ ನಿರ್ವಹಿಸಿರಿ. ನಮಾಝ್‍ನಲ್ಲಿ ಅಲ್ಲಾಹನಿಗೆ ವಿಧೇಯರಾಗಿ ನಿಂತುಕೊಳ್ಳಿರಿ. ');
INSERT INTO "kannada" VALUES(2,239,'2:239','ನೀವು ಭಯಗ್ರಸ್ಥರಾಗಿದ್ದರೆ ನಡೆದುಕೊಂಡಾಗಲಿ, ಸವಾರರಾಗಿದ್ದುಕೊಂಡಾಗಲಿ ನಿರ್ವಹಿಸಿರಿ. ಮುಂದೆ ನೀವು ನಿರ್ಭೀತರಾದರೆ ನಿಮಗೆ ತಿಳಿಯದಿದ್ದಾಗ ಅಲ್ಲಾಹು ಹೇಗೆ ಕಲಿಸಿಕೊಟ್ಟಿ ರುವನೋ ಅದೇ ಪ್ರಕಾರ ಯಥಾವತ್ತಾಗಿ ನಮಾಝ್ ನಿರ್ವಹಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(2,240,'2:240','ನಿಮ್ಮಲ್ಲಿ ಪತ್ನಿಯರನ್ನು ಅಗಲಿ ಮೃತರಾಗುವವರು ತಮ್ಮ ಪತ್ನಿಯರಿಗೆ ಒಂದು ವರ್ಷದವರೆಗೆ ಜೀವನಾಂಶಕ್ಕೆ ಹಾಗೂ ತನ್ನ ಮನೆಯಲ್ಲಿ ಸೂತಕ ವಾಸದ ಅನುಕೂಲ ಒದಗಿಸುವಂತೆ ಮರಣ ಶಾಸನ ಮಾಡಿಡತಕ್ಕದ್ದು . ಅವರು ಸ್ವಯಂ ಹೊರಗೆ ಹೋದಲ್ಲಿ ಅವರು ಮಾಡುವ ನ್ಯಾಯಯುತ ಕಾರ್ಯಗಳ ಹೊಣೆ ನಿಮ್ಮ ಮೇಲಿರುವುದಿಲ್ಲ. ಅಲ್ಲಾಹನು ಪ್ರತಾಪಶಾಲಿಯೂ ತಂತ್ರಜ್ಞನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(2,241,'2:241','ವಿಚ್ಛೇಧಿತೆಯರಿಗೆ ಸಾಧ್ಯವಿರುವ ಉಡುಗೊರೆ ಕೊಡುವುದು ದೇವಭಕ್ತರ ಬಾಧ್ಯತೆಯಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(2,242,'2:242','ಈ ರೀತಿ ಅಲ್ಲಾಹನು ತನ್ನ ನಿಯಮಗಳನ್ನು ನಿಮಗೆ ವಿವರಿಸಿ ಕೊಡುತ್ತಾನೆ. ನೀವು ಚಿಂತಿಸುವವರಾಗಲೆಂದು.');
INSERT INTO "kannada" VALUES(2,243,'2:243','ಸಾವಿರಗಟ್ಟಲೆ ಜನರಿದ್ದೂ ಸಾವಿನ ಭೀತಿಯಿಂದ ತಮ್ಮ ನಿವಾಸಗಳಿಂದ ಹೊರಹೋದವರನ್ನು ನೀವು ಕಂಡಿಲ್ಲವೇ ? ಆಗ ಅಲ್ಲಾಹನು ‘ನೀವು ಸಾಯಿರಿ’ ಎಂದು ಅವರಿಗೆ ಹೇಳಿದನು. ನಂತರ ಅವರಿಗೆ ಜೀವ ನೀಡಿದನು. ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಮನುಷ್ಯರ ಮೇಲೆ ತುಂಬ ಉದಾರಿ. ಆದರೆ ಬಹುಸಂಖ್ಯಾತ ಜನರು ಕೃತಘ್ನರು.');
INSERT INTO "kannada" VALUES(2,244,'2:244','ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಯುದ್ಧ ಮಾಡಿರಿ. ಅಲ್ಲಾಹನು ಎಲ್ಲ ಕೇಳುಗನೂ ಎಲ್ಲ ಬಲ್ಲವನೂ ಎಂಬುದು ನಿಮಗೆ ತಿಳಿದಿರಲಿ.');
INSERT INTO "kannada" VALUES(2,245,'2:245','ಅಲ್ಲಾಹನಿಗೆ ಉತ್ತಮ ಸಾಲ ನೀಡುವವನು ಯಾರಿದ್ದಾನೆ ? ಹಾಗಾದರೆ ಅದನ್ನು ಅಲ್ಲಾಹನು ಧಾರಾಳ ಪಟ್ಟು ವರ್ಧನೆಗೊಳಿಸುತ್ತಾನೆ. ಅಲ್ಲಾಹನು ಕುಗ್ಗಿಸುತ್ತಾನೆ, ಹಿಗ್ಗಿಸುತ್ತಾನೆ. ನಿಮ್ಮ ನಿರ್ಗಮನವು ಆತನ ಕಡೆಗೇ ಇದೆ.');
INSERT INTO "kannada" VALUES(2,246,'2:246','ಮೂಸಾರ ನಂತರ ಇಸ್ರಾಈಲ್ ಸಂತತಿಗಳ ಒಂದು ವಿಭಾಗದ ಕಡೆಗೆ ನೀವು ನೋಡಿಲ್ಲವೇ ? ತಮ್ಮೊಬ್ಬರು ಪ್ರವಾದಿಯೊಂದಿಗೆ ‘ನಮಗೊಬ್ಬ ರಾಜನನ್ನು ನಿಯೋಗಿಸಿರಿ. ನಾವು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಹೋರಾಡುತ್ತೇವೆ’ ಎಂದು ಹೇಳಿದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ. ಆಗ ಪ್ರವಾದಿಯು ‘ಯುದ್ದ ಘೋಷಣೆಯಾದ ನಂತರ ನೀವು ಯುದ್ಧ ಮಾಡದೆ ಇರುವ ಸಾಧ್ಯತೆ ಇದೆಯಲ್ಲ?’ ಎಂದು ಕೇಳಿದರು. ಅದಕ್ಕವರು `ನಮ್ಮನ್ನು ಹೊರಹಾಕಿ. ನಮ್ಮ ಮನೆ-ಮಕ್ಕಳಿಂದ ದೂರ ಮಾಡಲಾಗಿ ರುವಾಗ ನಾವು ಯುದ್ಧ ಮಾಡದೆ ಇರುತ್ತೇವೆಯೇ? ಎಂದು ಉತ್ತರಿಸಿದರು. ಆದರೆ ಅವರಿಗೆ ಯುದ್ಧಕ್ಕೆ ವಿಧಿಸಲ್ಪಟ್ಟಾಗ ಕೆಲವರನ್ನು ಮಾತ್ರ ಹೊರತುಪಡಿಸಿ ಉಳಿದವರೆಲ್ಲ ವಿಮುಖ ರಾದರು. ದುಷ್ಕರ್ಮಿ ಗಳನ್ನು ಅಲ್ಲಾಹನು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(2,247,'2:247','ಅವರಿಗೆ ಅವರ ಪ್ರವಾದಿ ಹೇಳಿದರು; ‘ಅಲ್ಲಾಹನು ನಿಮಗೆ ‘ಥಾಲೂತ’ರನ್ನು ರಾಜನಾಗಿ ನೇಮಿಸಿರುತ್ತಾನೆ. ‘ಆಗ ಅವರು ಹೇಳಿದರು; ‘ನಮ್ಮನ್ನಾಳಲು ಅವನಿಗೇನು ಯೋಗ್ಯತೆಯಿದೆ? ಅವನಿಗಿಂತ ನಾವೇ ಆಳಲು ಯೋಗ್ಯರು. ಅವನಿಗೆ ಧನ ಸಂಪತ್ತು ಕೂಡಾ ಇಲ್ಲ!’ ಆಗ ಪ್ರವಾದಿ ಹೇಳಿದರು; ‘ಅಲ್ಲಾಹನು ನಿಮಗೆ ಬದಲಾಗಿ ಅವನನ್ನು ಆಯ್ಕೆ ಮಾಡಿರುತ್ತಾನೆ. ಥಾಲೂತರಿಗೆ ಜ್ಞಾನ ಸಂಪತ್ತು ಮತ್ತು ಶಾರೀರಿಕ ಸಾಮಥ್ರ್ಯವನ್ನು ಹೆಚ್ಚಿಗೆ ನೀಡಿರುತ್ತಾನೆ. ತಾನುದ್ದೇಶಿಸಿದವರಿಗೆ ಅಲ್ಲಾಹನು ಅಧಿಕಾರ ಕೊಡುತ್ತಾನೆ. ಅಲ್ಲಾಹನು ಅತಿ ವಿಶಾಲನು, ಎಲ್ಲ ಬಲ್ಲವನು .');
INSERT INTO "kannada" VALUES(2,248,'2:248','ಅವರ ಪ್ರವಾದಿಯು ಪುನಃ ಹೇಳಿದರು; ‘ಥಾಲೂತರ ಅಧಿಕಾರ ಗ್ರಹಣದ ರುಜುವಾತಾಗಿ ಮಲಕ್‍ಗಳು ಪೆಟ್ಟಿಗೆಯನ್ನು ವಹಿಸಿಕೊಂಡು ಬರುವರು. ಅದರಲ್ಲಿ ನಿಮ್ಮ ಪಾಲಕಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಶಾಂತಿಯಿದೆ. ಅದರೊಳಗೆ ಮೂಸಾರವರ ಮನೆತನಗಳವರ ಹಾಗೂ ಹಾರೂನರ ಮನೆತನಗಳವರ ಅವಶೇಷಗಳಿವೆ . ನೀವು ವಿಶ್ವಾಸಿಗಳಾಗಿದ್ದರೆ ಇದರಲ್ಲಿ ನಿಮಗೆ ಮಹತ್ವದ ಪುರಾವೆಯಿದೆ’.');
INSERT INTO "kannada" VALUES(2,249,'2:249','ಥಾಲೂತರು ಸೈನ್ಯಸಮೇತ ಹೊರಟಾಗ ಹೇಳಿದರು, ‘ಅಲ್ಲಾಹನು ನಿಮ್ಮನ್ನು ಒಂದು ನದಿಯ ಮೂಲಕ ಪರೀಕ್ಷಿಸುವನು. ಅದರ ನೀರಿನಿಂದ ಯಾರು ಕುಡಿಯುತ್ತಾರೆ, ಅವನು ನನ್ನವನಲ್ಲ. ಅದರ ರುಚಿ ನೋಡದವನು ಮಾತ್ರ ನನ್ನವನು. ಆದರೆ ತನ್ನ ಕೈಯಿಂದ ಒಂದು ಬೊಗಸೆ ನೀರನ್ನು ಮಾತ್ರ ಎತ್ತಿಕೊಂಡವನ ಹೊರತು. (ಅಂಗೈ ತುಂಬ ನೀರಿಗಿಂತ ಹೆಚ್ಚಿಗೆ ಕುಡಿಯದವನು ನನ್ನವನು) ಆದರೆ ನದಿ ತಲುಪಿದಾಗ ಸ್ವಲ್ಪ ಜನರ ಹೊರತು ಉಳಿದವರೆಲ್ಲರೂ ಧಾರಾಳ ಕುಡಿದುಬಿಟ್ಟರು. ಹಾಗೆ ಥಾಲೂತ್ ಮತ್ತು ಅವರ ಜೊತೆಗಿರುವ (ಒಂದು ಅಂಗೈ ಮಾತ್ರ ಕುಡಿದ) ವಿಶ್ವಾಸಿಗಳು ನದಿದಾಟಿ ಹೋದಾಗ, (ನಿಯಮೋಲ್ಲಂಘನೆ ಮಾಡಿ ನದಿ ದಾಟಲಾಗದೆ ಅಲ್ಲೇ) ಉಳಿದವರು ‘ನಮಗೆ ಇಂದು ಜಾಲೂತ್ ಮತ್ತು ಆತನ ಸೈನ್ಯದ ಎದುರು ಯುದ್ದ ಮಾಡಲು ಶಕ್ತಿಯಿಲ್ಲ’ ಎಂದು ಹೇಳಿಬಿಟ್ಟರು. ಆದರೆ ಅಲ್ಲಾಹುವನ್ನು ಸಂಧಿಸಲಿರುವ ಬಗ್ಗೆ ದೃಢವಿಶ್ವಾಸವುಳ್ಳವರು (ನಿಯಮಾನುಸಾರ ನದಿ ದಾಟಿದವರು) ಹೇಳಿದರು; ಅಲ್ಲಾಹನ ಕೃಪೆಯಿಂದ ಅದೆಷ್ಟು ಚಿಕ್ಕ ತಂಡಗಳು ದೊಡ್ಡ ತಂಡಗಳನ್ನು ಸೋಲಿಸಿವೆ. ಅಲ್ಲಾಹನಿರುವುದು ಸಂಯಮಿಗಳ ಜೊತೆಗೆ’.');
INSERT INTO "kannada" VALUES(2,250,'2:250','ಹಾಗೆ ಜಾಲೂತ್ ಮತ್ತು ಆತನ ಸೈನ್ಯವನ್ನು ಅವರು ಎದುರುಗೊಂಡಾಗ, ‘ನಮ್ಮ ಪ್ರಭು ! ನಮಗೆ ತಾಳ್ಮೆಯನ್ನು ನೀಡು. ನಮ್ಮ ಪಾದಗಳನ್ನು ಅಚಲಗೊಳಿಸು. ಸತ್ಯನಿಷೇಧಿಗಳೆದುರು ನಮಗೆ ನೆರವಾಗು’ ಎಂದು ಪ್ರಾರ್ಥಿಸಿದರು.');
INSERT INTO "kannada" VALUES(2,251,'2:251','ಹಾಗೆ ಅವರು ಅಲ್ಲಾಹನ ಕೃಪೆಯಿಂದ ವೈರಿಗಳನ್ನು ಹಿಮ್ಮೆಟ್ಟಿಸಿದರು. ದಾವೂದರು ಜಾಲೂತನನ್ನು ವಧಿಸಿದರು. ಅವರಿಗೆ ಅಲ್ಲಾಹನು ರಾಜತ್ವ, ತತ್ವಜ್ಞಾನಗಳನ್ನು ದಯಪಾಲಿಸಿದನು. ಅಲ್ಲದೆ ತಾನಿಚ್ಛಿಸಿದ ಬೇರೆ ಅನೇಕ ಜ್ಞಾನಗಳನ್ನು ನೀಡಿದನು. ಕೆಲವರನ್ನು ಕೆಲವರ ಮುಖೇನ ಅಲ್ಲಾಹನು ತಡೆಯದಿರುತ್ತಿದರೆ ಭೂಮಿ ನಾಶವಾಗುತ್ತಿತ್ತು. ಆದರೆ ಆತನು ಲೋಕದ ಜನರ ಮೇಲೆ ಉದಾರಿಯಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(2,252,'2:252','ಅಲ್ಲಾಹನ ದೃಷ್ಟಾಂತಗಳಿವು, ನಾವು ಇವುಗಳನ್ನು ನಿಮಗೆ ಸಸತ್ಯ ಬೋಧಿಸುತ್ತಿದ್ದೇವೆ. ನಿಸ್ಸಂದೇಹ ನೀವು ಸಂದೇಶವಾಹಕರಲ್ಲೊಬ್ಬರು.');
INSERT INTO "kannada" VALUES(2,253,'2:253','ಆ ಸಂದೇಶವಾಹಕರಲ್ಲಿ ಕೆಲವರಿಗೆ ಕೆಲವರಿಗಿಂತ ಶ್ರೇಷ್ಠತೆಯನ್ನು ದಯಪಾಲಿಸಿರುತ್ತೇವೆ. ಅಲ್ಲಾಹನು ಖುದ್ದು ಮಾತನಾಡಿದವರೂ ಅವರಲ್ಲಿದ್ದಾರೆ. ಕೆಲವರನ್ನು ಉನ್ನತ ಪದವಿಗೇರಿಸಿದ್ದಾನೆ. ಮರ್ಯಮರ ಪುತ್ರ ಈಸಾರವರಿಗೆ ಸ್ಪಷ್ಟ ದೃಷ್ಟಾಂತಗಳನ್ನು ನೀಡಿದ್ದೇವೆ ಮತ್ತು ಪವಿತ್ರಾತ್ಮದ ಮೂಲಕ ಅವರಿಗೆ ಬಲ ನೀಡಿದ್ದೇವೆ. ಅಲ್ಲಾಹು ಇಚ್ಛಿಸುತ್ತಿದ್ದರೆ ಆ ಪ್ರವಾದಿಗಳ ಅನಂತರ ಸ್ಪಷ್ಟ ರುಜುವಾತುಗಳಿದ್ದೂ ಕೂಡಾ ಜನರು ಪರಸ್ಪರ ಕಾದಾಡುತ್ತಿರಲಿಲ್ಲ. ಆದರೆ ಅವರು ಭಿನ್ನ ವಿಭಿನ್ನರಾದರು. ಕೆಲವರು ಸಧರ್ಮಿಗಳಾದರು. ಕೆಲವರು ಅಧರ್ಮಿಗಳಾದರು. ಅಲ್ಲಾಹನಿಚ್ಛಿಸಿದರೆ ಅವರು ಪರಸ್ಪರ ಹೊಡೆದಾಡುತ್ತಿರಲಿಲ್ಲ. ಆದರೆ ಅಲ್ಲಾಹನು ತಾನಿಚ್ಛಿಸಿದ್ದನ್ನು ಮಾಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(2,254,'2:254','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ವ್ಯಾಪಾರ, ಗೆಳೆತನ, ಶಿಫಾರಸ್ಸು ಯಾವುದೂ ಇಲ್ಲದ ಒಂದು ದಿನ ಬರುವ ಮುನ್ನವೇ ಈಗ ನಿಮಗೆ ನಾವಿತ್ತುದರಿಂದ ಧರ್ಮಕಾರ್ಯಕ್ಕೆ ವೆಚ್ಚ ಮಾಡಿರಿ. ಸತ್ಯ ನಿಷೇಧಿಗಳು, ಅವರೇ ಅಕ್ರಮಿಗಳು.');
INSERT INTO "kannada" VALUES(2,255,'2:255','ಅಲ್ಲಾಹನ ಹೊರತು ಅನ್ಯದೇವನಿಲ್ಲ. ಆತನು ಚಿರಂಜೀವಿ. ಸಮಸ್ತ ವಿಶ್ವದ ಸಮರ್ಥ ನಿಯಂತ್ರಕ. ಆತನಿಗೆ ತೂಕಡಿಕೆಯಿಲ್ಲ, ನಿದ್ದೆಯೂ ಇಲ್ಲ. ಭೂಮ್ಯಾಕಾಶಗಳಲ್ಲಿರುವ ಸರ್ವದರ ಸರ್ವಾಧಿಪತಿ, ಆತನ ಅಪ್ಪಣೆ ವಿನಹ ಆತನಲ್ಲಿ ಶಿಫಾರಸು ಮಾಡುವ ಸಮರ್ಥ ಯಾರೂ ಇಲ್ಲ . ಸೃಷ್ಟಿಗಳ ಪ್ರತ್ಯಕ್ಷ - ಪರೋಕ್ಷಗಳನ್ನೆಲ್ಲ ಬಲ್ಲವನು. ಆತನ ಜ್ಞಾನದ ಪ್ರತಿಯೊಂದು ಅಂಶವೂ ಸೃಷ್ಟಿಗಳಿಗೆ ನಿಗೂಡ, ಆತನು ತಿಳಿಸಿಕೊಡ ಲಿಚ್ಚಿಸಿದ್ದು ಹೊರತು. ಆತನ ಕುರ್ಸೀ ಭೂಮ್ಯಾಕಾಶಗಳ ವ್ಯಾಪ್ತಿಯನ್ನು ಮೀರಿಸಿದೆ. ಆಕಾಶ ಭೂಮಿಗಳ ರಕ್ಷಣೆ ಆತನಿಗೆ ಭಾರವಲ್ಲ, ಅವನು ಪರಮೋನ್ನತನು, ಪರಮ ಗಣ್ಯನು.');
INSERT INTO "kannada" VALUES(2,256,'2:256','ಧರ್ಮ ಸ್ವೀಕೃತಿಗೆ ಬಲಾತ್ಕಾರವಿಲ್ಲ . ಸತ್ಯವು ಮಿಥ್ಯೆಯಿಂದ ವ್ಯಕ್ತಗೊಂಡಿದೆ. ಂiÀi ತಾಗೂತನ್ನು ಂ ನಿರಾಕರಿಸಿ ಅಲ್ಲಾಹನಲ್ಲಿ ವಿಶ್ವಾಸ ವಿರಿಸುತ್ತಾನೋ ಆತನು ಕಡಿದುಕೊಳ್ಳದ ಸುಭದ್ರ');
INSERT INTO "kannada" VALUES(2,257,'2:257','ಅಲ್ಲಾಹನು ವಿಶ್ವಾಸಿಗಳ ಸಹಾಯಕ. ಅವರನ್ನವನು ಇರುಳುಗಳಿಂದ ಬೆಳಕಿನತ್ತ ಸಾಗಿಸುತ್ತಾನೆ . ಸತ್ಯನಿಷೇಧಿಗಳ ಸಹಾಯಕರು ತಾಗೂತ್‍ಗಳು. ಅವು ಅವರನ್ನು ಬೆಳಕಿನಿಂದ ಇರುಳುಗಳತ್ತ ಸಾಗಿಸುತ್ತವೆ. ಅವರು ನರಕವಾಸಿಗಳು. ಅವರದರಲ್ಲಿ ಚಿರವಾಸಿಗಳು.');
INSERT INTO "kannada" VALUES(2,258,'2:258','ಇಬ್ರಾಹೀಮರೊಂದಿಗೆ ತನ್ನ ಪ್ರಭುವಿನ ವಿಚಾರದಲ್ಲಿ ವಾಗ್ವಾದ ನಡೆಸಿದವನ ಕಡೆಗೆ ನೀವು ಗಮನಹರಿಸಿಲ್ಲವೇ? ಆತನಿಗೆ ಅಲ್ಲಾಹನು ಕೊಟ್ಟ ಅರಸೊತ್ತಿಗೆಯು ಅದಕ್ಕೆ ಪ್ರಚೋದನೆಯೊ ದಗಿಸಿತ್ತು. ‘ನನ್ನ ಪ್ರಭುವು ಜೀವನ್ಮರಣಗಳ ದಾಯಕ’ ಎಂದರು ಇಬ್ರಾಹೀಮರು. ಆಗ ‘ನಾನು ಜೀವ ನೀಡುತ್ತೇನೆ, ಸಂಹಾರ ಮಾಡುತ್ತೇನೆ’ ಎಂದನಾತ. ಆಗ ಇಬ್ರಾಹೀಮರು, ನನ್ನ ಪ್ರಭುವು ಸೂರ್ಯನನ್ನು ಪೂರ್ವದಿಂದ ತರುತ್ತಾನೆ, ನೀನದನ್ನು ಪಶ್ಚಿಮದಿಂದ ತಾ’ ಎಂದರು. ಆಗ ಆ ಸತ್ಯನಿಷೇಧಿ ಸೋತು ಹೈರಾಣಾದ. ದುಷ್ಕರ್ಮಿಗಳಾದ ಜನತೆಗೆ ಅಲ್ಲಾಹನು ಸತ್ಯದರ್ಶನ ನೀಡುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,259,'2:259','ಅಥವಾ ಕೃಷಿ, ಫಲೋತ್ಪನ್ನಗಳ ಚಪ್ಪರಗಳೆಲ್ಲ ಕುಸಿದುಬಿದ್ದು ಸರ್ವನಾಶ ಹೊಂದಿದ್ದ ಒಂದು ಗ್ರಾಮದ ಮೂಲಕ ಓರ್ವನು ಸಂಚಾರಗೈದ ವೃತ್ತಾಂತದತ್ತ ಗಮನ ಹರಿಸಿಲ್ಲವೇ ? ಈ ರೀತಿ ಸರ್ವನಾಶ ಹೊಂದಿದ ಗ್ರಾಮವನ್ನು ಇನ್ನು ಅಲ್ಲಾಹನು ಸಜೀವಗೊಳಿಸುವುದಾದರೂ ಹೇಗೆ? ಎಂದು ಆ ವ್ಯಕ್ತಿ ಉದ್ಗರಿಸಿದನು. ಆ ವ್ಯಕ್ತಿಯನ್ನು ಕೂಡಲೇ ಅಲ್ಲಾಹನು ಮೃತ್ಯುವಶಗೊಳಿಸಿ ನೂರು ವರ್ಷಗಳ ಕಾಲ ಹಾಗೇ ಬಿಟ್ಟು ಬಿಟ್ಟು ಅನಂತರ ಜೀವ ಕೊಟ್ಟನು. ಇಲ್ಲಿ ನೀನು ಎಷ್ಟು ಕಾಲ ನೆಲೆಸಿದ್ದೆ ? ಎಂದು ಕೇಳಿದನು ಅಲ್ಲಾಹು. ಒಂದು ಇಡೀ ದಿನ ಅಥವಾ ಭಾಗಶಃ ದಿನ ಎಂದನಾತ. ಆಗ ಅಲ್ಲಾಹನೆಂದನು, `ಅಲ್ಲ, ನೀನು ನೂರು ವರ್ಷ ನೆಲೆಸಿರುವೆ. ನೀನು ಅಂದು ಜೊತೆಗೆ ಇಟ್ಟು ಕೊಂಡಿದ್ದ ಅನ್ನ-ಪಾನೀಯಗಳನ್ನು ನೋಡು, ಅದು ಹಾಳಾಗಿಲ್ಲ. ನಿನ್ನ ಕತ್ತೆಯನ್ನು ನೋಡು, ಅಲ್ಲಾಹನ ಪುನರ್ ಸೃಷ್ಟಿಯ ಸಾಮಥ್ರ್ಯಕ್ಕೆ ನಿನ್ನನ್ನು ನಾನು ರುಜುವಾತನ್ನಾಗಿ ಮಾಡುತ್ತೇನೆ. ಸತ್ತು ದ್ರವಿಸಿದ ನಿನ್ನ ಕತ್ತೆಯ ಎಲುಬು ಚೂರುಗಳನ್ನು ನೋಡು, ಹೇಗೆ ಎಬ್ಬಿಸುತ್ತಿದ್ದೇವೆ, ಹೇಗೆ ಅದಕ್ಕೆ ಮಾಂಸವನ್ನು ಧಾರಣೆ ಮಾಡುತ್ತೇವೆ ಎಂಬುದನ್ನು. ಇದೆಲ್ಲವನ್ನು ದೃಕ್ ಸಾಕ್ಷಿಯಾಗಿ ಕಂಡಾಗ ಆತನು ಉದ್ಗರಿಸಿದನು. ‘ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಸರ್ವ ಸಮರ್ಥನೆಂಬುದನ್ನು ನಾನು ಕಂಡರಿತೆ!’');
INSERT INTO "kannada" VALUES(2,260,'2:260','ಪ್ರಭು! ನೀನು ಮೃತರನ್ನು ಬದುಕಿಸುವುದು ಹೇಗೆಂದು ನನಗೆ ತೋರಿಸಿ ಕೊಡು! ಎಂದು ಇಬ್ರಾಹೀಮರು ಹೇಳಿದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ. ನಿಮಗೆ ನಂಬಿಕೆ ಬಂದಿಲ್ಲವೇ? ಎಂದು ಕೇಳಿದನು ಅಲ್ಲಾಹು. ನಂಬಿಕೆಯಿದೆ. ಆದರೆ ಕಣ್ಣಾರೆ ಕಂಡು ಮನದಣಿಯಲು ಕೇಳಿದೆ ಎಂದರು ಇಬ್ರಾಹೀಮರು. ಅಲ್ಲಾಹನೆಂದನು; ಹಾಗಾದರೆ ನಾಲ್ಕು ಪಕ್ಷಿಗಳನ್ನು ಹಿಡಿಯಿರಿ. ಆಮೇಲೆ ಅವುಗಳನ್ನು ನಿಮ್ಮತ್ತ ಚೆನ್ನಾಗಿ ಒಲಿಸಿ ತುಂಡರಿಸಿರಿ. ನಂತರ ಅವುಗಳನ್ನು ಹಿಡಿದು ಮಾಂಸ ಮಾಡಿ ಒಟ್ಟಾಗಿ ಬೆರೆಸಿ ನಾಲ್ಕು ಬೆಟ್ಟಗಳ ಮೇಲೆ ಪಾಲುಮಾಡಿರಿ. ನಂತರ ಕರೆದು ನೋಡಿರಿ. ಬಹಳ ವೇಗದಲ್ಲಿ ಅವು ನಿಮ್ಮ ಬಳಿ ಬರುತ್ತವೆ . ಅಲ್ಲಾಹನು ಮಹಾ ಪ್ರತಾಪಿ, ಪರಮ ತಂತ್ರಜ್ಞಾನಿ ಎಂಬುದು ತಿಳಿದಿರಲಿ.');
INSERT INTO "kannada" VALUES(2,261,'2:261','ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ತಮ್ಮ ಸಂಪತ್ತನ್ನು ಖರ್ಚು ಮಾಡುವವರ ಉಪಮೆ, ಒಂದು ಧಾನ್ಯ ಮೊಳೆತು ಅದರಿಂದ ಏಳು ಟಿಸಿಲೊಡೆದು ಪ್ರತಿಯೊಂದು ಟಿಸಿಲಲ್ಲಿ ತಲಾ ನೂರು ಧಾನ್ಯಗಳು ಬೆಳೆದಂತೆ. ಅಲ್ಲಾಹನು ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ದ್ವಿಗುಣಗೊಳಿಸುತ್ತಾನೆ. ಅಲ್ಲಾಹನು ವಿಶಾಲನು. ಪರಮ ತಜ್ಞನು.');
INSERT INTO "kannada" VALUES(2,262,'2:262','ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ತಮ್ಮ ಸಂಪತ್ತನ್ನು ವ್ಯಯ ಮಾಡುವ ಮತ್ತು ವ್ಯಯ ಮಾಡಿದ್ದನ್ನು ಎತ್ತಿ ಹೇಳದ ಹಾಗೂ ಹೆಮ್ಮೆ, ಪೀಡನೆಗಳನ್ನು ಮುಂದುವರಿಸದವರಿಗೆ, ಅವರ ದೇವನ ಬಳಿ ಯೋಗ್ಯ ಪ್ರತಿಫಲವಿದೆ. ಅಂತಹವರಿಗೆ ಭಯವಿಲ್ಲ. ವ್ಯಸನವಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,263,'2:263','ನಲ್ನುಡಿ ಹಾಗೂ ಕ್ಷಮಾಪಣೆಯು ಕೊಟ್ಟ ನಂತರ ಕಿರುಕುಳ ನೀಡುವ ದಾನಕ್ಕಿಂತ ಲೇಸು. ಅಲ್ಲಾಹನು ನಿರಪೇಕ್ಷನು. ಸಮಾಧಾನಿಯು.');
INSERT INTO "kannada" VALUES(2,264,'2:264','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನಿಮ್ಮ ದಾನದ ಫಲಗಳನ್ನು ಎತ್ತಿ ಹೇಳಿಕೆ ಹಾಗೂ ಕಿರುಕುಳದ ಮೂಲಕ ವ್ಯರ್ಥಗೊಳಿಸದಿರಿ. ಜನರೆದುರು ಮಿಥ್ಯತ್ವಕ್ಕಾಗಿ ಧನ ವ್ಯಯಿಸುವವನಂತೆ. ಆತನಿಗೆ ಅಲ್ಲಾಹು ಮತ್ತು ಪರಲೋಕದಲ್ಲಿ ವಿಶ್ವಾಸವಿಲ್ಲ. ಅವನ ಉದಾಹರಣೆ ಮೇಲ್ಮೈ ನುಣುಪಾದ ಬಂಡೆಗಲ್ಲಿನಂತೆ. ಅದರ ಮೇಲೆ ಮಣ್ಣು ಇದೆ. ಅದರ ಮೇಲೆ ಜಡಿಮಳೆ ಬಿದ್ದು ಸಂಪೂರ್ಣ ತೊಳೆದು ಹೋದಂತೆ ಅವರು ತಮ್ಮ ಕರ್ಮಫಲದಿಂದ ಯಾವುದನ್ನೂ ಪಡೆಯುವಲ್ಲಿ ಸಂಪೂರ್ಣ ವಿಫಲರು. ಅಲ್ಲಾಹನು ದ್ರೋಹಿಗಳಾದ ಜನರಿಗೆ ಸತ್ಯದರ್ಶನ ಮಾಡುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,265,'2:265','ಅಲ್ಲಾಹನ ತೃಪ್ತಿಯ ಬಯಕೆ ಹಾಗೂ ಆತ್ಮ ನಿಶ್ಚಯದಿಂದ ಧನವ್ಯಯ ಮಾಡುವವರ ಉಪಮೆಯು ಎತ್ತರದ ಸಮತಟ್ಟಿನಲ್ಲಿರುವ ಒಂದು ತೋಟದಂತೆ. ಅದಕ್ಕೆ ಸಮೃದ್ಧ ಮಳೆ ದೊರೆತು ಇಳುವರಿ ಇಮ್ಮಡಿಯಾಗುತ್ತದೆ. ಒಂದು ವೇಳೆ ಸಮೃದ್ಧ ಮಳೆ ಆಗದಿದ್ದರೆ ತುಂತುರು ಮಳೆಯೇ ಅದಕ್ಕೆ ಸಾಕಾಗುತ್ತದೆ. ನಿಮ್ಮ ಕೃತ್ಯಗಳ ಮೇಲೆ ಅಲ್ಲಾಹನ ನಿಗಾ ಇದ್ದೇ ಇದೆ.');
INSERT INTO "kannada" VALUES(2,266,'2:266','ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನಿಗೆ ಖರ್ಜೂರ ಮತ್ತು ದ್ರಾಕ್ಷೆಯ ಒಂದು ತೋಟವಿದೆ. ಅದರ ತಳಭಾಗದಲ್ಲಿ ಹೊಳೆಗಳು ಹರಿಯುತ್ತಿವೆ. ಎಲ್ಲ ವಿಧ ಫಲೋತ್ಪನ್ನ ಗಳು ಆ ತೋಟದಲ್ಲಿ ಬೆಳೆಯುತ್ತವೆ. ಆತನಿಗೆ ಪ್ರಾಯವೂ ಆಗಿದೆ. ದುರ್ಬಲರಾದ ಪುಟ್ಟ ಪುಟ್ಟ ಮಕ್ಕಳೂ ಇದ್ದಾರೆ. ಅಷ್ಟರಲ್ಲಿ ಬೆಂಕಿಯಿಂದ ಕೂಡಿದ ಭೀಕರ ಗಾಳಿಯೊಂದು ಬೀಸಿ ತೋಟವನ್ನು ಸುಟ್ಟು ಬಿಡುತ್ತದೆ. ಇಂತಹ ಅನುಭವ ನಿಮಗೆ ಬರಲು ನೀವು ಇಚ್ಛಿಸುವಿರಾ? ನೀವು ಯೋಚಿಸುವವರಾಗಲೆಂದು ಅಲ್ಲಾಹನು ಈ ರೀತಿ ನಿಮಗೆ ಪ್ರಮಾಣಗಳನ್ನು ವಿವರಿಸಿ ಕೊಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(2,267,'2:267','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನೀವು ಸಂಪಾದಿಸಿದ ಉತ್ತಮ ಸೊತ್ತುಗಳಿಂದಲೂ ಭೂಮಿಯಲ್ಲಿ ನಾವು ಹೊರಡಿಸಿದ (ಸ್ವಾಭಾವಿಕವಾದ) ಉತ್ತಮ ಬೆಳೆಗಳಿಂದಲೂ ಖರ್ಚು ಮಾಡಿರಿ. (ಝಕಾತ್ ನೀಡಿರಿ.) ಯಾರಾದರೂ ಕೆಳಮಟ್ಟದ ಮಾಲುಗಳನ್ನು ನಿಮಗೆ ಕೊಟ್ಟರೆ ಕಣ್ಣು ಮುಚ್ಚಿ ಪಡೆಯುವಿರೇ ಹೊರತು ತೃಪ್ತಿಯಿಂದ ಪಡಕೊಳ್ಳಲಾರಿರಿ. ಅಂತಹ ಗುಣಮಟ್ಟವಿಲ್ಲದ ಮಾಲುಗಳನ್ನು ನೀವೂ ಇತರರಿಗೆ ಪ್ರತ್ಯೇಕಿಸಿಡ ಬೇಡಿರಿ. (ಝಕಾತ್ ಕೊಡಬೇಡಿರಿ.) ಅಲ್ಲಾಹನು ಪರಮೈಶ್ವರ್ಯವಂತ ಹಾಗೂ ಪರಮ ಶ್ಲಾಘ್ಯ ಎಂಬುದು ನಿಮಗೆ ತಿಳಿದಿರಲಿ.');
INSERT INTO "kannada" VALUES(2,268,'2:268','ಶೈತಾನನು ನಿಮಗೆ ದಾರಿದ್ರ್ಯದ ಭೀತಿ ಹುಟ್ಟಿ ಸುತ್ತಾನೆ. ಲೋಭಿತನಕ್ಕೆ ಪ್ರಚೋದನೆ ನೀಡುತ್ತಾನೆ. ಅಲ್ಲಾಹನಾದರೋ ತನ್ನ ವತಿಯಿಂದ ದೋಷ ಮುಕ್ತಿಯ ಹಾಗೂ ಔದಾರ್ಯದ ಭರವಸೆ ಕೊಡುತ್ತಿದ್ದಾನೆ. ಅಲ್ಲಾಹನು ವಿಶಾಲಚಿತ್ತನು, ಸರ್ವಜ್ಞನು.');
INSERT INTO "kannada" VALUES(2,269,'2:269','ಅವನು ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಫಲವತ್ತಾದ ಜ್ಞಾನ ನೀಡುತ್ತಾನೆ, ಯಾವನಿಗೆ ಫಲವತ್ತಾದ ಜ್ಞಾನ ಪ್ರಾಪ್ತವಾಯಿತೋ ಆತನಿಗೆ ಧಾರಾಳ ಸೌಭಾಗ್ಯ ಪ್ರಾಪ್ತವಾದಂತೆಯೇ ಸರಿ. ಬುದ್ಧಿವಂತರಿಗೆ ಮಾತ್ರವೇ ಸಾರೋಪದೇಶ ಫಲಿಸುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(2,270,'2:270','ನೀವು (ಝಕಾತ್ ಅಥವಾ ಐಚ್ಛಿಕ ದಾನದ ರೂಪದಲ್ಲಿ) ಯಾವುದನ್ನೇ ವ್ಯಯಿಸಿದರೂ ಅಥವಾ ಯಾವುದೇ ಹರಕೆ ಮಾಡಿ ಸಂದಾಯಿಸಿದ್ದರೂ ಅದನ್ನು ಅಲ್ಲಾಹನು ಖಂಡಿತ ತಿಳಿಯುತ್ತಾನೆ. ದುರಾಕ್ರಮಿಗಳಿಗೆ ಯಾವ ಸಹಾಯಕರೂ ಇರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,271,'2:271','ನೀವು ಸ್ವಪ್ರೇರಿತ ದಾನವನ್ನು ಪ್ರತ್ಯಕ್ಷಗೊಳಿಸಿದರೆ ಅದು ಚೆನ್ನವೇ. ಇನ್ನದನ್ನು ಗುಪ್ತಗೊಳಿಸಿ ಬಡವರಿಗೆ ಕೊಡುವುದಾದರೆ ನಿಮಗದು ಅತ್ಯಂತ ಶ್ರೇಯಸ್ಕರ. ನಿಮ್ಮ ಪಾಪಗಳಿಂದ ಅಲ್ಲಾಹನು ಪರಿಹಾರ ಮಾಡುವನು. ಅಲ್ಲಾಹನು ನಿಮ್ಮ ಪ್ರವರ್ತಿಗಳ ಬಾಹ್ಯಾಂತರ್ಯಗಳನ್ನು ಬಲ್ಲವನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(2,272,'2:272','ಜನರನ್ನು ನೇರ ದಾರಿಗೆ ಹಚ್ಚುವುದು ನಿಮ್ಮ ಹೊಣೆಯಲ್ಲ. ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ನೇರ ದಾರಿಗೆ ಹಚ್ಚುವುದು ಅಲ್ಲಾಹು. ನೀವು ನೀಡಿದ ದಾನ ನಿಮಗೇ ನೀಡಿಕೊಂಡ ದಾನ. ಅಲ್ಲಾಹನ ಒಲವನ್ನು ಮಾತ್ರ ಬಯಸಿ ದಾನ ನೀಡಿರಿ. ನಿಮ್ಮ ದಾನದ ಸಂಪೂರ್ಣ ಫಲ ನಿಮಗೆ ಲಭ್ಯ. ನೀವು ಅನೀತಿಗೊಳಗಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,273,'2:273','ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಬಂಧಿತರಾದ ಬಡವರಿಗೆ ದಾನಗಳು ಸಂದಾಯವಾಗಲಿ. ಜೀವನಾಧಾರ ಕ್ಕಾಗಿ ಹೊರಗಿಳಿಯಲು ಅವರಿಗೆ ಸಾಧ್ಯವಾಗು ತ್ತಿಲ್ಲ. ಅವರ ಸ್ವಾಭಿಮಾನದಿಂದಾಗಿ ಅವರ ಸ್ಥಿತಿ ಗೊತ್ತಿಲ್ಲದವರು ಅವರನ್ನು ಸ್ಥಿತಿವಂತರೆಂದು ಭಾವಿಸುತ್ತಾರೆ. ಅವರು ಯಾರೆಂದು ಕುರು ಹುಗಳಿಂದ ನಿಮಗೆ ತಿಳಿಯ ಬಹುದಾಗಿದೆ. ಜನ ರಲ್ಲಿ ಅವರೆಂದಿಗೂ ಕಾಡಿ ಬೇಡುವವರಲ್ಲ. ನೀವಿತ್ತ ದಾನವನ್ನು ಖಂಡಿತ ಅಲ್ಲಾಹು ಅರಿತು ಕೊಳ್ಳುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(2,274,'2:274','ಇರುಳೂ ಹಗಲೂ ಗುಪ್ತವಾಗಿಯೂ ಜಾಹೀ ರಾಗಿಯೂ ತಮ್ಮ ಧನಗಳನ್ನು ದಾನ ನೀಡು ವವರಿಗೆ ಅವರ ಪ್ರಭುವಿನ ಬಳಿ ಪ್ರತಿಫಲವಿದೆ. ಅವರಿಗೆ ಯಾವ ಭಯವೂ ಇರದು. ಅವರು ದುಃಖಿಸಬೇಕಾಗಿಯೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,275,'2:275','ಬಡ್ಡಿ ಭಕ್ಷಕರು (ತಮ್ಮ ಸಮಾಧಿಗಳಿಂದ) ಶೈತಾನನ ಸೋಂಕಿನಿಂದ ಮತಿಹೀನರಾದವರಂತಲ್ಲದೆ ಎದ್ದೇಳಲಾರರು. ಇದು ಯಾಕೆಂದರೆ ಅವರು `ವ್ಯಾಪಾರವು ಬಡ್ಡಿಯಂತೆ’ ಎಂದಿದ್ದಾರೆ. ನಿಜದಲ್ಲಿ ಅಲ್ಲಾಹನು ವ್ಯಾಪಾರವನ್ನು ಧರ್ಮಬದ್ಧ ಗೊಳಿಸಿರುತ್ತಾನೆ. ಬಡ್ಡಿಯನ್ನು ನಿಷಿದ್ಧ ಗೊಳಿಸಿರುತ್ತಾನೆ. ತನ್ನ ಪ್ರಭುವಿನಿಂದ ಉಪದೇಶ ತಲುಪಿ ಬಡ್ಡಿ ಭಕ್ಷಣೆಯಿಂದ ದೂರ ಸರಿದವರಿಗೆ ಹಿಂದಿನ ಭಕ್ಷಣೆಯನ್ನು ಮನ್ನಾ ಮಾಡಲಾಗುವುದು. ಅವನ ವಿಚಾರವು ಅಲ್ಲಾಹನಿಗೆ ಸೇರಿದ್ದು. ಯಾವನಾದರೂ ಇನ್ನೂ ಆವರ್ತಿಸಿದರೆ ಅಂಥವರು ನರಕ ನಿವಾಸಿಗಳು. ಅದರಲ್ಲವರು ಶಾಶ್ವತರು .');
INSERT INTO "kannada" VALUES(2,276,'2:276','ಅಲ್ಲಾಹನು ಬಡ್ಡಿಯನ್ನು ಅಳಿಸಿ ಹಾಕುತ್ತಾನೆ. ದಾನಗಳನ್ನು ಪೋಷಿಸುತ್ತಾನೆ. ಎಲ್ಲ ಧಿಕ್ಕಾರಿಗಳು ಹಾಗೂ ಮಹಾಪಾಪಿಗಳನ್ನು ಅಲ್ಲಾಹನು ಅನಿಷ್ಟಪಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(2,277,'2:277','ಸತ್ಯವಿಶ್ವಾಸ ಹೊಂದಿ ಪುಣ್ಯ ಕಾರ್ಯಗಳನ್ನು ನಿರ್ವಹಿಸಿದವರು, ನಮಾಝನ್ನು ಖಾಯಂ ಮಾಡಿಕೊಂಡವರು, ಝಕಾತ್ ಪಾವತಿಸುವವರು ಯಾರೋ ಅವರಿಗೆ ತಮ್ಮ ನಾಥನ ಬಳಿ ಪ್ರತಿಫಲವಿದೆ. ಅವರಿಗೆ ಯಾವ ಭಯವೂ ಇಲ್ಲ. ಅವರು ದುಃಖಿತರೂ ಅಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,278,'2:278','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ಅಲ್ಲಾಹನ ವಿಧಿ-ನಿಷೇಧ ಗಳಿಗೆ ಬದ್ಧರಾಗಿರಿ. ಬಾಕಿಯಿರುವ ಬಡ್ಡಿಯನ್ನು ತ್ಯಜಿಸಿರಿ. ನೀವು ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳಾಗಿದ್ದರೆ.');
INSERT INTO "kannada" VALUES(2,279,'2:279','ಹೀಗೆ ನೀವು ಮಾಡದಿದ್ದರೆ ನಿಮ್ಮೆದುರು ಅಲ್ಲಾಹು ಮತ್ತು ಅವರ ರಸೂಲರು ಯುದ್ಧ ಸಾರುವುದನ್ನು ಅರಿತುಕೊಳ್ಳಿರಿ. ನೀವು ಬಾಕಿ ಬಡ್ಡಿ ವಸೂಲಿ ಯಿಂದ ವಿಮುಖರಾದರೆ ಅಸಲು ವಸೂಲಾತಿಗೆ ನಿಮಗೆ ಅನುಮತಿಯಿದೆ. ನೀವು ಅನ್ಯಾಯ ವೆಸಗದವರೂ ಅನ್ಯಾಯಕ್ಕೊಳಗಾಗದವರೂ ಆಗಬೇಕು.');
INSERT INTO "kannada" VALUES(2,280,'2:280','ಸಾಲಗಾರ ಸಾಲ ಪಾವತಿಗೆ ಪ್ರಯಾಸದಲ್ಲಿದ್ದರೆ ಅನುಕೂಲವಾಗುವವರೆಗೂ ಅವಧಿ ವಿಸ್ತರಿಸಿರಿ. ನೀವು ಅದನ್ನು ಮನ್ನಾ ಮಾಡುವುದಾದರೆ ನಿಮಗದು ಶ್ರೇಯಸ್ಸು. ನೀವು ತಿಳಿದವರಾಗಿದ್ದರೆ');
INSERT INTO "kannada" VALUES(2,281,'2:281','ನಿಮ್ಮನ್ನು ಅಲ್ಲಾಹನ ಕಡೆಗೆ ವಾಪಾಸು ಕರೆಸಿಕೊಳ್ಳುವ ಒಂದು ದಿನದ ಬಗ್ಗೆ ಎಚ್ಚರದಲ್ಲಿರಿ. ಪ್ರತಿಯೊಬ್ಬರಿಗೂ ಮಾಡಿದ ಕರ್ಮದ ಫಲವನ್ನು ಅಲ್ಲಿ ನೀಡಲಾಗುವುದು. ಯಾರಿಗೂ ಅನೀತಿ ತೋರಲಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(2,282,'2:282','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನೀವು ನಿಗದಿತ ಅವಧಿಗೆ ಸಾಲದ ವ್ಯವಹಾರ ಮಾಡಿಕೊಂಡರೆ ಅದನ್ನು ಬರೆದಿಡಿರಿ. ನಿಮ್ಮ ನಡುವೆ ನೀತಿ ನಿಷ್ಠವಾಗಿ ಬರಹಗಾರನೊಬ್ಬ ಅದನ್ನು ದಾಖಲಿಸಿಕೊಳ್ಳಲಿ. ಅಲ್ಲಾಹನು ಕಲಿಸಿದ ಪ್ರಕಾರ ಬರಹಗಾರನು ಬರೆದುಕೊಡುವುದನ್ನು ನಿರಾಕರಿಸಬಾರದು. ಆತ ಬರೆಯಲಿ, ಸಾಲಗಾರನು ಹೇಳಿಕೊಡಲಿ. ಹೇಳಿಕೊಡುವಾಗ ಏನನ್ನೂ ಕಡಿಮೆಗೊಳಿಸದಿರಲಿ. ಆ ವಿಷಯದಲ್ಲಿ ತನ್ನ ಪ್ರಭುವಿನ ಭಯವಿರಲಿ. ಇನ್ನು ಸಾಲಗಾರನು ಮಂದಮತಿಯೋ, ದುರ್ಬಲ ನೋ, ಹೇಳಿಕೊಡಲು ಅಸಾಧ್ಯವಾದವನೋ ಆಗಿದ್ದಲ್ಲಿ ಆತನ ರಕ್ಷಕ ನೀತಿ ನಿಷ್ಟವಾಗಿ ಹೇಳಿಕೊಡಲಿ. ನಿಮ್ಮ ಪೈಕಿ ಇಬ್ಬರು ಪುರುಷರನ್ನು ಸಾಕ್ಷಿ ನಿಲ್ಲಿಸಿರಿ. ಇಬ್ಬರು ಪುರುಷರ ಅಭಾವವಿದ್ದರೆ ಸಾಕ್ಷಿಗೆ ನೀವು ತೃಪ್ತಿಪಡುವ ಓರ್ವ ಪುರುಷ ಹಾಗೂ ಇಬ್ಬರು ಸ್ತ್ರೀಯರು ಸಾಕ್ಷಿಗಳಾಗಲಿ. ಸ್ತ್ರೀಯರು ಇಬ್ಬರು ಯಾಕೆಂದರೆ ಒಬ್ಬಳು ಮರೆತರೆ ಮತ್ತೊ ಬ್ಬಳು ನೆನಪಿಸಲು ಅನುಕೂಲವಾಗುತ್ತದೆ. ಸಾಕ್ಷಿಗೆ ಕರೆದಾಗ ಸಾಕ್ಷಿಯಾಗಲು ನಿರಾಕರಿಸಬೇಡಿ. ಅವಧಿಯವರೆಗೆ ಬರೆಯಲು ಉದಾಸೀನ ತೋರ ಬೇಡಿ. ಮೊತ್ತ ಕಡಿಮೆಯಿರಲಿ, ಹೆಚ್ಚಿರಲಿ. ಇದು ಅಲ್ಲಾಹನ ಬಳಿ ಅತ್ಯಂತ ನೀತಿ ನಿಷ್ಠವಾದುದು ಹಾಗೂ ಸಾಕ್ಷಿಗೆ ಅತ್ಯಂತ ಉಪಕಾರಿಯಾದುದು. ಸಂದೇಹ ನಿವಾರಣೆಗೆ ಅತ್ಯಂತ ನಿಕಟವಾದುದು. ನಿಮ್ಮ ನಡುವೆ ರೊಕ್ಕ ವ್ಯಾಪಾರ ನಡೆಯುವಾಗ ಬರೆದಿಡಬೇಕಾಗಿಲ್ಲ. ಆದರೆ ವ್ಯಾಪಾರ ಕುದುರಿದರೆ ಬರೆದಿಡಬೇಕಾಗಿಲ್ಲ. ಆದರೆ ವ್ಯಾಪಾರ ಕುದುರಿದರೆ ಸಾಕ್ಷಿ ನಿಲ್ಲಿಸುವುದು ಶ್ರೇಯಸ್ಕರ. ಬರಹಗಾರನೋ, ಸಾಕ್ಷಿಯೋ ತೊಂದರೆಗೊಳಗಾಗಬಾರದು. ಈ ನಿಷೇಧಿತ ಕಾರ್ಯಗಳನ್ನು ನೀವು ಮಾಡಿದರೆ ನಿಜವಾಗಿಯೂ ಅದು ನಿಮಗೇ ಬಾಧಿಸುವ ಪಾಪ. ಅಲ್ಲಾಹನ ವಿಧಿ-ನಿಷೇಧಗಳಿಗೆ ಬದ್ಧರಾಗಿರಿ. ಅಲ್ಲಾಹನು ನಿಮಗೆ ಕಲಿಸಿ ಕೊಡುತ್ತಾನೆ. ಅಲ್ಲಾಹನು ಸರ್ವಜ್ಞನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(2,283,'2:283','ಇನ್ನು ನೀವು ಪ್ರಯಾಣದಲ್ಲಿದ್ದು ಬರಹಗಾರ ಸಿಗದೆ ಇದ್ದಲ್ಲಿ ಒತ್ತೆಯಿಟ್ಟುಕೊಳ್ಳಿ. ಹಾಗೆ ವಿಶ್ವಾಸದಿಂದ ಒತ್ತೆ ಇಟ್ಟರೆ ಅದನ್ನು ಪಡಕೊಂಡವನು ವಿಶ್ವಾಸ ನಿಷ್ಠೆ ತೋರಬೇಕು. ತನ್ನ ರಕ್ಷಕನಾದ ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಬೇಕು. ಸಾಕ್ಷ್ಯವನ್ನು ಮುಚ್ಚಿಡ ಬಾರದು. ಮುಚ್ಚಿಡುವವನ ಮನಸ್ಸು ಪಾಪ ಕಲುಷಿತ. ನಿಮ್ಮ ವರ್ತನೆಗಳನ್ನೆಲ್ಲ ಅಲ್ಲಾಹನು ಚೆನ್ನಾಗಿ ಬಲ್ಲವನು.');
INSERT INTO "kannada" VALUES(2,284,'2:284','ಆಕಾಶಗಳು ಮತ್ತು ಭೂಮಿಯಲ್ಲಿರುವುದೆಲ್ಲ ಅಲ್ಲಾಹನದು. ನಿಮ್ಮ ಅಂತರಂಗದಲ್ಲಿರುವುದನ್ನು ನೀವು ಬಹಿರಂಗಗೊಳಿಸಿದರೂ ಗುಪ್ತವಾಗಿಟ್ಟರೂ ಅಲ್ಲಾಹನು ವಿಚಾರಣೆ ಮಾಡಿಯೇ ಮಾಡುತ್ತಾನೆ. ಅನಂತರ ಅವನು ತಾನಿಚ್ಛಿಸುವವರಿಗೆ ಕ್ಷಮಿಸುವನು. ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ಶಿಕ್ಷಿಸುವನು. ಅಲ್ಲಾಹನು ಸರ್ವ ಸಮರ್ಥನು.');
INSERT INTO "kannada" VALUES(2,285,'2:285','ರಸೂಲರು ತನ್ನ ಪ್ರಭುವಿನಿಂದ ತನಗೆ ಅವತೀರ್ಣ ಗೊಳಿಸಲ್ಪಟ್ಟುದರಲ್ಲಿ ವಿಶ್ವಾಸ ಹೊಂದಿರುತ್ತಾರೆ. ಸತ್ಯವಿಶ್ವಾಸಿಗಳೂ ಕೂಡಾ. ಅವರೆಲ್ಲರೂ ಅಲ್ಲಾಹ್, ಆತನ ಮಲಾಇಕತ್, ಆತನ ಗ್ರಂಥಗಳು ಹಾಗೂ ಆತನ ಪ್ರವಾದಿಗಳಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸುತ್ತಾರೆ. “ಪ್ರವಾದಿಗಳ ಮಧ್ಯೆ ನಾವು ತಾರತಮ್ಯ ತೋರಲಾರೆವು. ನಾವು ಆಲಿಸಿದೆವು, ಅನುಸರಿಸಿದೆವು. ನಮ್ಮ ಒಡೆಯನೇ! ತಮಗೆ ದೋಷಮುಕ್ತಿ ನೀಡು. ನಮ್ಮ ನಿರ್ಗಮನ ನಿನ್ನ ಕಡೆಗೇ” ಎಂದೆನ್ನುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(2,286,'2:286','ಅಲ್ಲಾಹನು ಯಾರ ಮೇಲೂ ಅವನ ತಾಕತ್ತಿಗೆ ಮೀರಿದ ಹೊಣೆ ಒಪ್ಪಿಸುವುದಿಲ್ಲ. ಪ್ರತಿ ದೇಹಕ್ಕೂ ತಾನು ಮಾಡಿದ್ದಕ್ಕೆ ಸತ್ಫಲವಿದೆ. ಅದು ಎಸಗಿದ್ದಕ್ಕೆ ದುಷ್ಫಲವೂ ಇದೆ. ನಮ್ಮೊಡೆಯನೇ! ನಮ್ಮ ಮರೆವು ಅಥವಾ ಪ್ರಮಾದದಿಂದ ಸಂಭವಿಸುವ ತಪ್ಪುಗಳಿಗೆ ನಮ್ಮನ್ನು ಶಿಕ್ಷಿಸದಿರು. ನಮ್ಮ ನಾಥನೇ! ಪೂರ್ವಿಕರ ಮೇಲೆ ಹೊರಿಸಿದಂತಹ ಹೊರೆಯನ್ನು ನಮಗೆ ಹೊರಿಸದಿರು. ನಮ್ಮ ಯಜಮಾನನೇ, ನಮಗೆ ಅಸಾಧ್ಯವಾದುದನ್ನು ನಮಗೆ ಹೊರಿಸದಿರು. ನಮಗೆ ಮಾಫಿ ನೀಡು. ನಮಗೆ ಕ್ಷಮಿಸು! ನಮಗೆ ದಯೆ ತೋರು . ನೀನೇ ನಮ್ಮ ಯಜಮಾನ. ಸತ್ಯನಿಷೇಧಿಗಳಾದ ಜನರ ವಿರುದ್ಧ ನಮಗೆ ನೆರವಾಗು! ');
INSERT INTO "kannada" VALUES(3,1,'3:1','ಅಲಿಫ್ ಲಾಮ್ ಮೀಮ್');
INSERT INTO "kannada" VALUES(3,2,'3:2','ಅಲ್ಲಾಹನಲ್ಲದೆ ಬೇರೆ ದೇವನಿಲ್ಲ. ಅವನು ಚಿರಂಜೀವಿ. ಸ್ಥಿರನಿತ್ಯ ನಿಯಂತ್ರಕ.');
INSERT INTO "kannada" VALUES(3,3,'3:3','(ಓ ಪ್ರವಾದಿಯರೇ) ಅವನು ಈ ಗ್ರಂಥವನ್ನು ಸತ್ಯಸಂಧ ವಾಗಿ, ಹಿಂದಿನ ವೇದಗ್ರಂಥಗಳ ದೃಢೀಕರಣವಾಗಿ ನಿಮಗೆ ಅವತೀರ್ಣಗೊಳಿಸಿದನು. ಇದಕ್ಕೂ ಮುನ್ನ ಜನರಿಗೆ ಮಾರ್ಗ ದರ್ಶನಕ್ಕಾಗಿ ತೌರಾತ್ ಹಾಗೂ ಇಂಜೀಲನ್ನು ಅವನು ಅವತೀರ್ಣ ಗೊಳಿಸಿದ್ದನು.');
INSERT INTO "kannada" VALUES(3,4,'3:4','ಮತ್ತು ವಿವೇಚಕ ಗ್ರಂಥವನ್ನೂ ಅವನು ಅವತೀರ್ಣಗೊಳಿಸಿದನು. ಯಾರು ಅಲ್ಲಾಹನ ಪುರಾವೆಗಳನ್ನು ನಿಷೇಧಿಸುತ್ತಾರೆ, ಅವರಿಗೆ ಕಠೋರ ಶಿಕ್ಷೆಯಿದೆ. ಅಲ್ಲಾಹನು ಪ್ರತಾಪಶಾಲಿಯೂ ದಂಡಕನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(3,5,'3:5','ಭೂಮಿ, ಆಕಾಶದಲ್ಲಿರುವ ಯಾವುದೇ ವಸ್ತು ಅಲ್ಲಾಹನಿಗೆ ಮರೆಯಾಗಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(3,6,'3:6','ಅವನು ತಾನಿಚ್ಛಿಸಿದಂತೆ ಗರ್ಭಕೋಶಗಳಲ್ಲಿ ನಿಮ್ಮನ್ನು ರೂಪಿಸುತ್ತಾನೆ. ಅವನಲ್ಲದೆ ಬೇರೆ ಆರಾಧ್ಯನಿಲ್ಲ. ಅವನು ಪರಮ ಪ್ರತಾಪಿಯು, ಯುಕ್ತಿಪೂರ್ಣನು.');
INSERT INTO "kannada" VALUES(3,7,'3:7','ಅವನು ಈ ಗ್ರಂಥವನ್ನು ನಿಮಗೆ ಅವತೀರ್ಣಗೊಳಿಸಿ ದನು. ಇದರಲ್ಲಿ ಪ್ರಮಾಣ ಸ್ಪಷ್ಟ ಶ್ಲೋಕಗಳಿವೆ. ಅವೇ ಗ್ರಂಥದ ಮೂಲ. ಇದರಲ್ಲಿ ಅವ್ಯಕ್ತವಾದ ಕೆಲವು ಶ್ಲೋಕ ಗಳೂ ಇವೆ . ಕೆಲವು ವಿಕೃತ ಮನಸ್ಕರು ಪಿತೂರಿ ಎಬ್ಬಿಸಲು ಮತ್ತು ಸ್ವಂತ ವ್ಯಾಖ್ಯಾನ ನೀಡಲಿಚ್ಛಿಸಿ ಅಂಥವುಗಳ ಹಿಂದೆ ಬೀಳುತ್ತಾರೆ. ಆದರೆ ಅದರ ನಿಜಾರ್ಥ ಅಲ್ಲಾಹನ ಹೊರತು ಇನ್ನಾರಿಗೂ ತಿಳಿಯದು. ಆಳ ಜ್ಞಾನವುಳ್ಳವರು ``ಎಲ್ಲವೂ ನಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದಲೇ ಬಂದಿದ್ದು, ನಾವು ಅವುಗಳನ್ನು ಸರಿಯಾಗಿ ನಂಬಿದ್ದೇವೆ’’ ಎನ್ನುತ್ತಾರೆ. ಬುದ್ಧಿವಂತರು ಮಾತ್ರ ಯೋಚಿಸಿ ಗ್ರಹಿಸಬಲ್ಲರು.');
INSERT INTO "kannada" VALUES(3,8,'3:8','“ಓ ನಮ್ಮ ಪಾಲಕ ಪ್ರಭು! ನೀನು ನಮ್ಮನ್ನು ನೇರ ದಾರಿಗೆ ಸೇರಿಸಿದ ಬಳಿಕ ನಮ್ಮ ಹೃದಯಗಳನ್ನು ತಪ್ಪಿಸದಿರು. ನಿನ್ನ ಕಡೆಯಿಂದ ನಮಗೆ ಅನುಗ್ರಹವನ್ನು ದಯಪಾಲಿಸು. ನೀನು ಪರಮದಾನಿಯೆಂಬುದು ದಿಟ.');
INSERT INTO "kannada" VALUES(3,9,'3:9','ಓ ನಮ್ಮ ಪಾಲಕ ಪ್ರಭು! ನೀನು ಮನುಷ್ಯರನ್ನು ಒಂದು ಗೂಡಿಸಲಿರುವ ದಿನದ ಬಗ್ಗೆ ನಮಗೆ ಯಾವ ಸಂಶಯವೂ ಉಳಿದಿಲ್ಲ. ಅಲ್ಲಾಹನು ಖಂಡಿತ ವಚನಭಂಗ ಮಾಡಲಾರನು”.');
INSERT INTO "kannada" VALUES(3,10,'3:10','ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ತಮ್ಮ ಸಂಪತ್ತುಗಳಾಗಲಿ ಸಂತಾನಗಳಾಗಲಿ ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆಯಿಂದ ಪಾರು ಮಾಡಲು ಸ್ವಲ್ಪವೂ ಫಲಕಾರಿಯಾಗದು. ಅವರೇ ನರಕದ ಉರುವಲುಗಳು.');
INSERT INTO "kannada" VALUES(3,11,'3:11','ಇವರ ಅವಸ್ಥೆ ನಮ್ಮ ಪುರಾವೆಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದ ಫಿರ್‍ಔನನ ಜನಾಂಗ ಹಾಗೂ ಅವರ ಪೂರ್ವಜರ ಅವಸ್ಥೆಯಂತಿದೆ. ಅವರ ಪಾಪಗಳ ಕಾರಣದಿಂದ ಅಲ್ಲಾಹನು ಅವರನ್ನು ಶಿಕ್ಷಿಸಿದನು. ಅಲ್ಲಾಹನು ಕಠಿಣವಾಗಿ ಶಿಕ್ಷಿಸುವವನು.');
INSERT INTO "kannada" VALUES(3,12,'3:12','ಸತ್ಯನಿಷೇಧಕರಿಗೆ ಹೇಳಿರಿ, ನೀವು ಸೋಲುವಿರಿ. ಬಹಳ ನಿಕೃಷ್ಟ ತಾಣವಾದ ನರಕಕ್ಕೆ ನೀವು ಅಟ್ಟಲ್ಪಡುವಿರಿ.');
INSERT INTO "kannada" VALUES(3,13,'3:13','ಪರಸ್ಪರ ಎದುರುಗೊಂಡ ಎರಡು ತಂಡಗಳಲ್ಲಿ ನಿಮಗೆ ಪಾಠವಿದೆ. ಒಂದು ತಂಡವು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಹೋರಾಡಿತು. ಇನ್ನೊಂದು ತಂಡ ಸತ್ಯನಿಷೇಧಿಗಳಾಗಿದ್ದು ಇವರು ಅವರಿಗೆ ಇಮ್ಮಡಿಯಾಗಿ ಗೋಚರಿಸಿದರು. ಅಲ್ಲಾಹನು ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ಸಹಾಯ ಮಾಡಿ ಬಲಿಷ್ಠ ಗೊಳಿಸುತ್ತಾನೆ. ಕಣ್ಣುಳ್ಳವರಿಗೆ ಇದರಲ್ಲಿ ಮಹತ್ತರ ಪಾಠವುಂಟು .');
INSERT INTO "kannada" VALUES(3,14,'3:14','ಸ್ತ್ರೀಯರು, ಪುತ್ರರು, ರಾಶಿ ಹಾಕಲಾದ ಬೆಳ್ಳಿ ಬಂಗಾರಗಳು, ಉತ್ತಮ ತಳಿಯ ಕುದುರೆಗಳು, ಜಾನುವಾರುಗಳು, ಕೃಷಿ ಜಮೀನುಗಳು ಇವೇ ಮುಂತಾದ ಕಾಮ್ಯ ವಸ್ತುಗಳ ಪ್ರೀತಿಯನ್ನು ಮನುಷ್ಯರಿಗೆ ಅಲಂಕಾರವಾಗಿ ಕಾಣಿಸಲಾಗಿದೆ. (ಆದರೆ) ಇವೆಲ್ಲವೂ ಕೇವಲ ಐಹಿಕ ಜೀವನದ ಸಾಮಗ್ರಿಗಳು ಮಾತ್ರ. ಅತ್ಯುತ್ತಮವಾದ ಅಂತ್ಯ ಫಲವು ಅಲ್ಲಾಹನ ಬಳಿಯಾಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(3,15,'3:15','(ಪ್ರವಾದಿಯವರೇ!) ಹೇಳಿರಿ, ಇವುಗಳಿಗಿಂತ ಉತ್ತಮವಾದವುಗಳನ್ನು ನಾನು ನಿಮಗೆ ಹೇಳಿ ಕೊಡಲೇ? ಧರ್ಮನಿಷ್ಠ ಭಕ್ತರಿಗೆ ಅವರ ಪ್ರಭುವಿನ ಬಳಿ ಕೆಲವು ಉದ್ಯಾನಗಳಿವೆ. ಅವುಗಳ ತಳ ಭಾಗದಲ್ಲಿ ನೀರ್ಝರಿಗಳು ಹರಿಯುತ್ತಿವೆ. ಅವರು ಅದರಲ್ಲಿ ಶಾಶ್ವತರು. ಅಲ್ಲಿ ಅವರಿಗೆ ನಿರ್ಮಲರಾದ ಮಡದಿಯರಿರುವರು. ಅಲ್ಲಾಹನ ಒಲುಮೆಯೂ ಅವರಿಗೆ ಇದೆ. ಅಲ್ಲಾಹನು ತನ್ನ ದಾಸರನ್ನು ಚೆನ್ನಾಗಿ ಕಾಣುವವನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(3,16,'3:16','ಅವರು ಪ್ರಾರ್ಥಿಸುತ್ತಾರೆ; ನಮ್ಮ ಪ್ರಭು! ಖಂಡಿತ ನಾವು ವಿಶ್ವಾಸವಿಟ್ಟಿದ್ದೇವೆ. ಆದ್ದರಿಂದ ನಮ್ಮ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸು. ನಮ್ಮನ್ನು ನರಕ ಶಿಕ್ಷೆಯಿಂದ ರಕ್ಷಿಸು.');
INSERT INTO "kannada" VALUES(3,17,'3:17','ಅವರು ಸಹನಾಶೀಲರು, ಸತ್ಯನಿಷ್ಠರು, ವಿನಯಾನು ಸರಣೆಯುಳ್ಳವರು, (ಉತ್ತಮ ಮಾರ್ಗದಲ್ಲಿ) ವೆಚ್ಚ ಮಾಡುವವರು, ರಾತ್ರಿಯ ಅಂತಿಮ ಯಾಮದಲ್ಲಿ ಪಾಪವಿಮೋಚನೆಗಾಗಿ ಪ್ರಾರ್ಥಿಸುವವರು.');
INSERT INTO "kannada" VALUES(3,18,'3:18','ತಾನಲ್ಲದೆ ಬೇರೆ ಆರಾಧ್ಯನಿಲ್ಲವೆನ್ನುವುದಕ್ಕೆ ಅಲ್ಲಾಹನು ಸಾಕ್ಷ್ಯವಹಿಸಿದ್ದಾನೆ (ಸ್ಪಷ್ಟ ಪುರಾವೆಗಳ ಮೂಲಕ) ಮಲಕ್‍ಗಳೂ ಜ್ಞಾನವಂತರೂ ಅದಕ್ಕೆ ಸಾಕ್ಷ್ಯ ವಹಿಸಿರುತ್ತಾರೆ . ಅವನು ನೀತಿ ಪಾಲಿಸುವವನು. ಅವನಲ್ಲದೆ ಬೇರೆ ಆರಾಧ್ಯನಿಲ್ಲ. ಆತನು ಅಜೇಯನು, ಯುಕ್ತಿಪೂರ್ಣನು.');
INSERT INTO "kannada" VALUES(3,19,'3:19','ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನ ಬಳಿ ಸ್ವೀಕೃತವಾಗುವ ಧರ್ಮ ಇಸ್ಲಾಮ್ ಮಾತ್ರ. ವೇದಗ್ರಂಥ ನೀಡಲ್ಪಟ್ಟವರು ಅವರಿಗೆ (ಧರ್ಮ) ಜ್ಞಾನ ನೀಡಲ್ಪಟ್ಟ ನಂತರವಲ್ಲದೆ ಭಿನ್ನಾಭಿಪ್ರಾಯ ಹೊಂದಲಿಲ್ಲ. ಂ ಅದು ಅವರ ನಡುವಿನ ಹಗೆ-ಮತ್ಸರ ನಿಮಿತ್ತವಾ ಗಿತ್ತು. ಅಲ್ಲಾಹನ ವಚನಗಳನ್ನು ಯಾರು ನಿಷೇಧಿ ಸುತ್ತಾರೆ, ಖಂಡಿತ ಅಲ್ಲಾಹನು ವಿಚಾರಣೆಯಲ್ಲಿ ಅತಿ ಶೀಘ್ರನು.');
INSERT INTO "kannada" VALUES(3,20,'3:20','ಇನ್ನೂ ಅವರು ನಿಮ್ಮೊಡನೆ ವಾಗ್ವಾದ ಮಾಡಿದರೆ ನೀವು ಹೇಳಿರಿ - ‘ನಾನು ನನ್ನನ್ನೇ ಅಲ್ಲಾಹನಿಗೆ ಸಂಪೂರ್ಣವಾಗಿ ಅರ್ಪಿಸಿದ್ದೇನೆ. ನನ್ನನ್ನು ಹಿಂಬಾಲಿಸಿದವರೂ. (ಹಾಗೆಯೇ) ಗ್ರಂಥದವ ರೊಂದಿಗೆ ಹಾಗೂ ನಿರಕ್ಷರಿಗಳೊಂದಿಗೆ (ಅರಬ ರೊಂದಿಗೆ) ಕೇಳಿರಿ, ‘ನೀವು ಅಲ್ಲಾಹನಿಗೆ ಶರಣಾಗಿದ್ದೀರಾ?’ ಹಾಗೆ ಅವರು ಶರಣಾದರೆ, ಅವರು ಖಂಡಿತ ಸತ್ಪಥ ಪಡೆದರು. ಇನ್ನು ಅವರು ಹಿಂಜರಿದರೆ ನಿಮ್ಮ ಬಾಧ್ಯತೆ (ದಿವ್ಯ ಸಂದೇಶ ವನ್ನು) ತಲುಪಿಸಿ ಕೊಡುವುದು ಮಾತ್ರ. ಅಲ್ಲಾಹನು ತನ್ನ ದಾಸರನ್ನು ಚೆನ್ನಾಗಿ ವೀಕ್ಷಿಸುತ್ತಿರುವನು.');
INSERT INTO "kannada" VALUES(3,21,'3:21','ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹನ ದೃಷ್ಟಾಂತಗಳನ್ನು ನಿಷೇಧಿಸುವ, ಪ್ರವಾದಿಗಳನ್ನು ಅನ್ಯಾಯವಾಗಿ ಕೊಲ್ಲುವ, ಜನರಿಗೆ ನೀತಿ ನ್ಯಾಯದ ಉಪದೇಶ ಕೊಡುವ ಜನರನ್ನು ವಧಿಸುವವರು ಯಾರೋ, ಅವರಿಗೆ ಭಾರೀ ಯಾತನಾಮಯ ಶಿಕ್ಷೆ ಕಾದಿದೆ ಎಂಬ ‘ಸುವಾರ್ತೆ’ಯನ್ನು ಕೊಡಿರಿ .');
INSERT INTO "kannada" VALUES(3,22,'3:22','ಅವರು ಇಹದಲ್ಲೂ ಪರದಲ್ಲೂ ತಮ್ಮ ಕರ್ಮಗಳು ವಿಫಲಗೊಂಡವರು. ಅವರಿಗೆ ಯಾರೂ ಸಹಾಯಕರಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(3,23,'3:23','ವೇದಗ್ರಂಥದ ಭಾಗ್ಯವನ್ನು ನೀಡಲ್ಪಟ್ಟ ಕೆಲವರನ್ನು ನೀವು ಕಂಡಿಲ್ಲವೇ? ಅವರ ನಡುವೆ ತೀರ್ಪು ನೀಡಲು ಅಲ್ಲಾಹನ ಗ್ರಂಥದ ಕಡೆಗೆ ಅವರನ್ನು ಆಹ್ವಾನಿಸಲಾಗುತ್ತದೆ. ಆದರೂ ಕೂಡಾ ಅವರಲ್ಲಿ ಒಂದು ವಿಭಾಗವು ಅವಗಣನೆಯಿಂದ ಹಿಂಜರಿಯುತ್ತಿದೆ .');
INSERT INTO "kannada" VALUES(3,24,'3:24','‘ಕೆಲವೇ ದಿವಸಗಳ ಹೊರತು ನರಕಾಗ್ನಿ ನಮ್ಮನ್ನು ಸ್ಪರ್ಶಿಸದು’ ಎಂದು ಅವರು ಹೇಳಿದುದರ ಕಾರಣವಿದು. ತಮ್ಮ ಮತದಲ್ಲಿ ತಾವೇ ಸೃಷ್ಟಿಸಿಕೊಂಡಿರುವ ವಾದಗಳು (ಅವರ ಧರ್ಮ ಕಾರ್ಯದಲ್ಲಿ) ಅವರನ್ನು ವಂಚಿಸಿ ಬಿಟ್ಟಿವೆ.');
INSERT INTO "kannada" VALUES(3,25,'3:25','ಆದರೆ ಒಂದು ದಿವಸ ನಾವು ಅವರನ್ನು ಒಂದು ಗೂಡಿಸುವಾಗ ಅವರ ಅವಸ್ಥೆ ಏನಾದೀತು ? ಆ ದಿನದ ಆಗಮನದ ಬಗ್ಗೆ ಯಾವ ಸಂಶಯವೂ ಇಲ್ಲ. ಅಂದು ಪ್ರತಿಯೊಂದು ಆತ್ಮಕ್ಕೂ ಅದರ ದುಡಿಮೆಯ ಪ್ರತಿಫಲವನ್ನು ಪೂರ್ಣವಾಗಿ ಕೊಡಲಾಗುವುದು. ಅವರಿಗೆ ಏನೂ ಅನ್ಯಾಯವಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(3,26,'3:26','ಹೇಳಿರಿ; ರಾಜತ್ವದ ಒಡೆಯನಾದ ಅಲ್ಲಾಹನೇ! ನೀನು ಇಚ್ಛಿಸಿದವರಿಗೆ ನೀನು ರಾಜತ್ವ ಕೊಡುತ್ತೀಯೆ, ನೀನಿಚ್ಛಿಸಿದವರಿಂದ ರಾಜತ್ವವನ್ನು ತೆಗೆದು ಹಾಕುತ್ತೀಯೆ. ನೀನು ಉದ್ದೇಶಿಸಿದವರಿಗೆ ನೀನು ಪ್ರತಿಷ್ಠೆ ಕೊಡುತ್ತೀಯೆ. ನೀನುದ್ದೇಶಿಸಿ ದವರನ್ನು ನಿಕೃಷ್ಟಗೊಳಿಸುತ್ತೀಯೆ. ಒಳಿತು ನಿನ್ನ ಅಧೀನದಲ್ಲಿದೆ. ಖಂಡಿತವಾಗಿಯೂ ನೀನು ಸರ್ವ ಸಮರ್ಥನಾಗಿರುವೆ.');
INSERT INTO "kannada" VALUES(3,27,'3:27','(ಹೇಳಿರಿ) : ‘ನೀನು ರಾತ್ರಿಯನ್ನು ಹಗಲೊಳಗೆ ನುಸುಳಿಸುತ್ತೀಯೆ. ಹಗಲನ್ನು ರಾತ್ರಿಯೊಳಗೂ ನುಸುಳಿಸುತ್ತೀಯೆ . ನಿರ್ಜೀವಿಯಿಂದ ಜೀವಿಯನ್ನು ನೀನು ಹೊರಡಿಸುತ್ತೀಯೆ. ಜೀವಿಯಿಂದ ನಿರ್ಜೀವಿಯನ್ನು ನೀನು ಹೊರಡಿಸುತ್ತೀಯೆ . ನೀನು ಇಚ್ಛಿಸಿದವರಿಗೆ ಜೀವನಾಧಾರವನ್ನು ಲೆಕ್ಕವಿಲ್ಲದೆ ಕೊಡುತ್ತೀಂi');
INSERT INTO "kannada" VALUES(3,28,'3:28','ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಹೊರತು ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ಆತ್ಮೀಯ ಮಿತ್ರರನ್ನಾಗಿ ಮಾಡಬಾರದು. ಯಾರಾದರೂ ಅದನ್ನು ಮಾಡಿದರೆ ಅವನಿಗೆ ಅಲ್ಲಾಹನ ಜೊತೆ ಯಾವುದೇ ಸಂಬಂಧವಿರುವುದಿಲ್ಲ. ಆದರೆ ನೀವು ಅವರನ್ನು ಜಾಗ್ರತೆ ಮಾಡುವ ವಿಧದಲ್ಲಿ ಜಾಗ್ರತೆ ಮಾಡಿದರೆ ಹೊರತು . ಅಲ್ಲಾಹನು ತನ್ನ (ಶಿಕ್ಷೆಯ) ಬಗ್ಗೆ ನಿಮಗೆ ಎಚ್ಚರಿಕೆ ನೀಡುತ್ತಿದ್ದಾನೆ. ನಿಮ್ಮ ಮರಳುವಿಕೆ ಅಲ್ಲಾಹನ ಕಡೆಗೆ ಇದೆ.');
INSERT INTO "kannada" VALUES(3,29,'3:29','ಓ ಪ್ರವಾದಿಯರೇ, ಹೇಳಿರಿ: ‘ನಿಮ್ಮ ಹೃದಯ ಗಳಲ್ಲಿರುವುದನ್ನು ನೀವು ಮರೆಮಾಚಿದರೂ ಅಥವಾ ಬಹಿರಂಗಗೊಳಿಸಿದರೂ ಅಲ್ಲಾಹನು ಅದನ್ನು ಅರಿಯುವನು. ಆಕಾಶಗಳಲ್ಲಿ ಮತ್ತು ಭೂಮಿ ಯಲ್ಲಿರುವುದನ್ನು ಅವನು ತಿಳಿಯುವನು. ಅಲ್ಲಾಹನು ಎಲ್ಲ ವಸ್ತುವಿನ ಮೇಲೆ ಸಾಮಥ್ರ್ಯವುಳ್ಳವನು.');
INSERT INTO "kannada" VALUES(3,30,'3:30','ಪ್ರತಿಯೊಬ್ಬ ವ್ಯಕ್ತಿಯೂ ತಾನು ಒಳಿತಾಗಿ ಮಾಡಿರುವ ಕರ್ಮಫಲವನ್ನು ತನ್ನ ಮುಂದೆ ಪ್ರತ್ಯಕ್ಷವಾಗಿ ಕಾಣುವ ದಿವಸ. ಕೆಡುಕು ಮಾಡಿದ ವ್ಯಕ್ತಿಯು; “ಇದರ (ದುಷ್ಕಾರ್ಯದ) ಮತ್ತು ನನ್ನ ನಡುವೆ ಖಂಡಿತವಾಗಿಯೂ ಬಹಳ ದೂರದ ಅಂತರ ಇದ್ದಿದ್ದರೆ” ಎಂದು ಆಸೆಪಡುವನು. ಅಲ್ಲಾಹನು ತನ್ನ ಶಿಕ್ಷೆಯ ಬಗ್ಗೆ ನಿಮ್ಮನ್ನು ಎಚ್ಚರಿಸುತ್ತಿದ್ದಾನೆ. ಅಲ್ಲಾಹನು ತನ್ನ ದಾಸರಲ್ಲಿ ಬಹಳ ದಯೆ ಯುಳ್ಳವನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(3,31,'3:31','(ನಬಿಯೇ) ಹೇಳಿರಿ; ನೀವು ಅಲ್ಲಾಹನನ್ನು ಪ್ರೀತಿಸುವಿರಾದರೆ, ನನ್ನನ್ನು ಅನುಗಮಿಸಿರಿ. ಆಗ ಅಲ್ಲಾಹನು ನಿಮ್ಮನ್ನು ಪ್ರೀತಿಸುವನು. ನಿಮ್ಮ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸುವನು. ಅಲ್ಲಾಹನು ಮಹಾ ಪಾಪ ಪರಿಹಾರಿಯೂ, ಕರುಣಾಮಯಿಯೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(3,32,'3:32','ಹೇಳಿರಿ; ನೀವು ಅಲ್ಲಾಹು ಹಾಗೂ ದೇವದೂತರನ್ನು ಅನುಸರಿಸಿರಿ. ಇನ್ನು ಅವರು ವಿಮುಖರಾದರೆ ಅಲ್ಲಾಹು ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ಮೆಚ್ಚುವುದಿಲ್ಲ; ಖಂಡಿತ.');
INSERT INTO "kannada" VALUES(3,33,'3:33','ಖಂಡಿತವಾಗಿಯೂ ಆದಮ್, ನೂಹ್, ಇಬ್ರಾಹೀಮರ ಕುಟುಂಬ ಮತ್ತು ಇಮ್ರಾನ್ ಕುಟುಂಬವನ್ನು ಉತ್ಕøಷ್ಟರಾಗಿ ಅಲ್ಲಾಹು ಆಯ್ಕೆ ಮಾಡಿರುವನು .');
INSERT INTO "kannada" VALUES(3,34,'3:34','ಕೆಲವರು ಕೆಲವರ ಸಂತತಿಗಳಾಗಿದ್ದುಕೊಂಡು. ಅಲ್ಲಾಹು ಸರ್ವಶ್ರುತನೂ, ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(3,35,'3:35','ಇಮ್ರಾನರ ಪತ್ನಿ ಹೇಳಿದ ಸಂದರ್ಭ.(ವನ್ನು ನೆನೆಸಿರಿ). ನನ್ನ ರಕ್ಷಕನೇ ! ನನ್ನ ಹೊಟ್ಟೆಯಲ್ಲಿರುವ ಶಿಶುವನ್ನು ನಾನು ನಿನಗೆ ಮೀಸಲಾಗಿ ಹರಕೆ ಇಟ್ಟಿದ್ದೇನೆ ಂ. ನನ್ನಿಂದ ನೀನು ಸ್ವೀಕರಿಸು. ಖಂಡಿತವಾಗಿಯೂ ನೀನು ಸರ್ವಶ್ರುತನೂ, ಸರ್ವಜ್ಞನೂ ಆಗಿರುವೆ.');
INSERT INTO "kannada" VALUES(3,36,'3:36','ಆದರೆ ಅವಳು ಹೆತ್ತಾಗ ಹೇಳಿದಳು, ರಕ್ಷಕನೇ! ನಾನು ಹೆತ್ತದ್ದು ಒಂದು ಹೆಣ್ಣು ಮಗುವನ್ನು! ಅವಳು ಹೆತ್ತಿರುವುದೇನೆಂದು ಅಲ್ಲಾಹನು ಹೆಚ್ಚು ಬಲ್ಲವನೇ ಆಗಿರುತ್ತಾನೆ. ಗಂಡು ಮಗು ಹೆಣ್ಣು ಮಗುವಿನಂತಲ್ಲ. ತಾನವಳಿಗೆ ಮರ್ಯಮ್ ಎಂದು ಹೆಸರಿಟ್ಟಿದ್ದೇನೆ. ಅವಳನ್ನೂ ಅವಳ ಸಂತತಿಯನ್ನೂ ಬಹಿಷ್ಕøತ ಶೈತಾನನ ಕೇಡಿನಿಂದ ರಕ್ಷಿಸುವ ಸಲುವಾಗಿ ನಿನ್ನಲ್ಲಿ ನಾನು ಅಭಯ ಬೇಡುತ್ತಿದ್ದೇನೆ .');
INSERT INTO "kannada" VALUES(3,37,'3:37','ಕೊನೆಗೆ ಆ ಮಗುವನ್ನು ಅವಳ ಪ್ರಭು ಬಹಳ ಚೆನ್ನಾಗಿ ಸ್ವೀಕರಿಸಿದನು. ಉತ್ಕøಷ್ಟ ರೀತಿಯಲ್ಲಿ ಅವಳನ್ನು ಬೆಳೆಸಿದನು, ಅವಳ ಪರಿಪಾಲನೆಯನ್ನು ‘ಝಕರಿಯ್ಯಾ’ರಿಗೆ ವಹಿಸಿಕೊಟ್ಟನು. ಝಕರಿಯ್ಯಾರವರು ಮಿಹ್‍ರಾಬ್‍ನಲ್ಲಿ ಆಕೆಯ ಬಳಿ ಪ್ರವೇಶಿಸಿದಾಗಲೆಲ್ಲ ಏನಾದರೊಂದು ಭಕ್ಷ್ಯವನ್ನು ಅವಳ ಬಳಿ ಕಾಣುತ್ತಿದ್ದರು. ಅವರು ಕೇಳಿದರು, ‘ಮರ್ಯಮಳೇ! ಇದು ನಿನಗೆ ಎಲ್ಲಿಂದ ಸಿಗುತ್ತದೆ’. ಅವಳು ಹೇಳಿದಳು; ‘ಇದು ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ಸಿಕ್ಕಿದೆ. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನು ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಆಹಾರವನ್ನು ಯಾವುದೇ ಲೆಕ್ಕವಿಲ್ಲದೆ ನೀಡುವನು’ .');
INSERT INTO "kannada" VALUES(3,38,'3:38','ಅಲ್ಲಿ ಝಕರಿಯ್ಯಾರವರು ತನ್ನ ಪ್ರಭುವಿನಲ್ಲಿ ಪ್ರಾರ್ಥಿಸುತ್ತಾ ಹೇಳಿದರು; ನನ್ನ ಪ್ರಭೂ! ನಿನ್ನ ವತಿಯಿಂದ ನನಗೆ ಉತ್ತಮವಾದ ಸಂತಾನವನ್ನು ಪ್ರದಾನ ಮಾಡು. ಖಂಡಿತವಾಗಿಯೂ ನೀನು ಪ್ರಾರ್ಥನೆಯನ್ನು ಆಲಿಸುವವನಾಗಿರುವಿ.');
INSERT INTO "kannada" VALUES(3,39,'3:39','ತನ್ನಿಮಿತ್ತ ಅವರು ಮಿಹ್‍ರಾಬ್(ಮಸೀದಿ)ನಲ್ಲಿ ನಮಾಝ್ ಮಾಡುತ್ತಾ ನಿಂತಿರುವಾಗ ಮಲಕ್‍ಗಳು ಅವರನ್ನು ಕರೆದು ಹೇಳಿದರು; ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನು ನಿಮಗೆ ‘ಯಹ್ಯಾ’ (ಎಂಬ ಮಗುವನ್ನು ಪ್ರದಾನ ಮಾಡುವ) ಬಗ್ಗೆ ಶುಭ ವಾರ್ತೆ ಕೊಡುತ್ತಿದ್ದಾನೆ ಂ. ಅವನು (ಆ ಮಗುವು) ಅಲ್ಲಾಹ ನಿಂದಿರುವ ಒಂದು ವಚನವನ್ನು ಸತ್ಯಗೊಳಿಸು ವವನೂ ನೇತಾರನೂ ಸ್ತ್ರೀ ಮೋಹ ಮುಕ್ತನೂ ಸಜ್ಜನರಲ್ಲಿ ಸೇರಿದ ಪ್ರವಾದಿಯೂ ಆಗುವನು.');
INSERT INTO "kannada" VALUES(3,40,'3:40','ಅವರು (ಝಕರಿಯ್ಯಾ ) ಕೇಳಿದರು: ನನ್ನ ರಕ್ಷಕನೇ! ನನಗೆ ಒಬ್ಬ ಪುತ್ರ ಉಂಟಾಗುವುದು ಹೇಗೆ ? ನನಗೆ ವೃದ್ಧಾಪ್ಯ ತಲಪಿದೆ. ನನ್ನ ಮಡದಿ ಬಂಜೆಯಾಗಿದ್ದಾಳೆ ! ಆಗ ಅಲ್ಲಾಹು ಹೇಳಿದನು ; ಸಂಗತಿ ಹಾಗೆಯೇ ಆಗುತ್ತದೆ. ಅಲ್ಲಾಹನು ತಾನುದ್ದೇಶಿಸಿದ್ದನ್ನು ಮಾಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(3,41,'3:41','ಅವರು (ಝಕರಿಯಾ ) ಹೇಳಿದರು; ನನ್ನ ಪಾಲಕನೇ ! ನನಗೆ ನೀನೊಂದು ಸಂಕೇತವನ್ನು ಕೊಡು. (ಆಗ ಅಲ್ಲಾಹನು) ಹೇಳಿದನು; ``ಮೂರು ದಿವಸಗಳ ಕಾಲ ಜನರೊಡನೆ ನೀವು ಸಂಜ್ಞೆ ಮೂಲಕವಲ್ಲದೆ ಮಾತಾಡದಿರುವುದೇ ನಿಮಗೆ ನಾನು ಒದಗಿಸುವ ಸಂಕೇತ. ನೀವು ನಿಮ್ಮ ಪ್ರಭುವನ್ನು ಹೆಚ್ಚು ಹೆಚ್ಚಾಗಿ ಸ್ಮರಿಸಿರಿ. ಸಂಜೆ-ಮುಂಜಾನೆಗಳಲ್ಲಿ ಅವನ ಪರಿಶುದ್ಧತೆಯನ್ನು ಕೊಂಡಾಡಿರಿ''''.');
INSERT INTO "kannada" VALUES(3,42,'3:42','ಮಲಕ್‍ಗಳು ಹೇಳಿದ ಸಂದರ್ಭ. ಓ ಮರ್ಯಮ್! ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನು ನಿಮ್ಮನ್ನು ವಿಶಿಷ್ಟವಾಗಿ ಆರಿಸಿರುವನು. ಶುದ್ಧಿಗೊಳಿಸಿರುವನು. ಹಾಗೂ ಲೋಕದ ಸ್ತ್ರೀಯರಲ್ಲಿ ನಿಮ್ಮನ್ನು ಉನ್ನತ ಸ್ಥಾನಕ್ಕೆ ಚುನಾಯಿಸಿರುವನು .');
INSERT INTO "kannada" VALUES(3,43,'3:43','ಓ ಮರ್ಯಮ್ ! ನಿಮ್ಮ ಪ್ರಭುವಿಗೆ ಭಕ್ತಿಯೊಂದಿಗೆ ಶರಣಾಗಿರಿ. ಅವನಿಗೆ ಸಾಷ್ಟಾಂಗವೆರಗಿರಿ. ತಲೆ ಬಾಗುವವರ ಜೊತೆ ತಲೆ ಬಾಗಿರಿ .');
INSERT INTO "kannada" VALUES(3,44,'3:44','(ಓ ಪ್ರವಾದಿಯವರೇ!) ಇವೆಲ್ಲವೂ ನಾವು ನಿಮಗೆ ಬೋಧನೆ ನೀಡುತ್ತಿರುವ ಅಗೋಚರ ವಾರ್ತೆಗಳು. ಮರ್ಯಮ್‍ರ ಪಾಲನೆಯನ್ನು ಯಾರು ವಹಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ನಿರ್ಧರಿಸಲು ಅವರು ತಮ್ಮ ಲೆಕ್ಕಣಿಗಳನ್ನು (ನದಿಗೆ) ಹಾಕಿದಾಗ ನೀವು ಅಲ್ಲಿ ಇರಲಿಲ್ಲ. ಅವರು ಪರಸ್ಪರ ತರ್ಕ ಮಾಡಿ ದಾಗಲೂ ನೀವು ಅವರ ನಡುವೆ ಇರಲಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(3,45,'3:45','ಮಲಕ್‍ಗಳು ಹೇಳಿದ (ಇನ್ನೊಂದು) ಸಂದರ್ಭ (ವನ್ನು ಸ್ಮರಿಸಿರಿ.) ಓ ಮರ್ಯಮ್ ! ಅಲ್ಲಾಹನು ತನ್ನ ಕಡೆಯಿಂದ ಒಂದು ವಚನದ (ಕಾರಣದಿಂದುಂಟಾಗುವ ಮಗುವಿನ ಜನನದ) ಕುರಿತು ನಿಮಗೆ ಶುಭವಾರ್ತೆ ಕೊಡುತ್ತಿದ್ದಾನೆ. ಆ ಮಗುವಿನ ಹೆಸರು `ಮರ್ಯಮಳ ಪುತ್ರ ಈಸಾ ಮಸೀಹ್'' ಎಂದಾಗಿರುವುದು. ಅವನು ಇಹಪರಗಳಲ್ಲಿ ಗಣ್ಯನೂ ಅಲ್ಲಾಹನ ನಿಕಟರಲ್ಲಿ ಸೇರಿದವನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(3,46,'3:46','ಅವನು ತೊಟ್ಟಿಲಲ್ಲಿರುವಾಗಲೂ ನಡು ವಯಸ್ಕನಾಗಿರುವಾಗಲೂ ಜನರಲ್ಲಿ (ತತ್ವೋಪ ದೇಶವನ್ನು) ಮಾತನಾಡುವನು. ಅವನು ಸಜ್ಜನರ ಕೂಟದವನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(3,47,'3:47','ಅವಳು ಕೇಳಿದಳು; `ಪ್ರಭೂ! ನನಗೆ ಮಗು ವುಂಟಾಗುವುದು ಹೇಗೆ? ನನ್ನನ್ನು ಯಾವನೇ ಪುರುಷ ಸ್ಪರ್ಶಿಸಿಲ್ಲವಲ್ಲ?'' ಅಲ್ಲಾಹು ಹೇಳಿದನು; `ಅದು ಹಾಗೆಯೇ ಸಂಭವಿಸುವುದು.'' ಅಲ್ಲಾಹು ತಾನುದ್ದೇಶಿಸಿದ್ದನ್ನು ಸೃಷ್ಟಿಸುತ್ತಾನೆ. ಅವನು ಒಂದು ಕಾರ್ಯವನ್ನು ಉದ್ದೇಶಿಸಿದರೆ ಅದರೊಂದಿಗೆ `ಆಗು'' ಎಂದಷ್ಟೇ ಹೇಳುತ್ತಾನೆ. ಆಗ ಅದು ಉಂಟಾಗುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(3,48,'3:48','ಆ ಪುತ್ರನಿಗೆ (ಈಸಾರವರಿಗೆ) ಅಲ್ಲಾಹು ಗ್ರಂಥ ಹಾಗೂ ತತ್ವ ಜ್ಞಾನವನ್ನೂ ತೌರಾತ್ ಮತ್ತು ಇಂಜೀಲನ್ನೂ ಕಲಿಸುವನು.');
INSERT INTO "kannada" VALUES(3,49,'3:49','ಇಸ್ರಾಈಲ ಸಂತತಿಗಳಿಗೆ ಓರ್ವ ಪ್ರವಾದಿಯನ್ನಾಗಿ ಯೂ ಮಾಡುವನು . (ಅವರು ಇಸ್ರಾಈಲರೊಂದಿಗೆ ಹೀಗೆ ಹೇಳುವರು:) `ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಒಂದು ದೃಷ್ಟಾಂತದೊಂದಿಗೆ ನಿಮ್ಮ ಬಳಿ ನಾನು ಬಂದಿದ್ದೇನೆ.'' (ದೃಷ್ಟಾಂತವೆಂದರೆ) ನಿಮಗೆ ನಾನು ಕಲಸು ಮಣ್ಣಿಂದ ಪಕ್ಷಿಯನ್ನು ರೂಪುಗೊಳಿಸಿ, ಆಮೇಲೆ ನಾನದಕ್ಕೆ ಊದುತ್ತೇನೆ. ಆಗ ಅದು ಅಲ್ಲಾ ಹನ ಆಜ್ಞೆಯಿಂದ ಜೀವಂತ ಪಕ್ಷಿಯಾಗುವುದು . ನಾನು ಅಲ್ಲಾಹನ ಅನುಮತಿಯಿಂದ ಜನ್ಮತಃ ಕುರುಡರನ್ನೂ ಪಾಂಡು ರೋಗಿಗಳನ್ನೂ ಗುಣಪಡಿಸುತ್ತೇನೆ. ಹಾಗೂ ಮೃತರನ್ನು ಜೀವಂತಗೊಳಿಸುತ್ತೇನೆ. ನೀವು ತಿನ್ನುವುದನ್ನೂ ನಿಮ್ಮ ಮನೆಗಳಲ್ಲಿ ನೀವು ಸಂಗ್ರಹಿಸಿಟ್ಟಿರುವುದನ್ನೂ ನಿಮಗೆ ನಾನು ತಿಳಿಸಿಕೊಡುತ್ತೇನೆ. ಇದರಲ್ಲಿ ನಿಮಗೆ ಖಂಡಿತ ವಾಗಿಯೂ ದೃಷ್ಟಾಂತವಿದೆ. ನೀವು ವಿಶ್ವಾಸವಿರಿ ಸುವಿರಾದರೆ.');
INSERT INTO "kannada" VALUES(3,50,'3:50','ನನಗಿಂತ ಮುಂಚಿನದಾಗಿರುವ ತೌರಾತನ್ನು ದೃಢಗೊಳಿಸುವವನಾಗಿಯೂ ಮತ್ತು ನಿಮಗೆ ನಿಷೇಧಿಸಲಾಗಿದ್ದ ಕೆಲವನ್ನು ಸಮ್ಮತಾರ್ಹ ಗೊಳಿಸಲಿಕ್ಕಾಗಿಯೂ (ನಾನು ಬಂದಿರುತ್ತೇನೆ.) ನಿಮ್ಮ ಪ್ರಭುವಿನ ಒಂದು ದೃಷ್ಟಾಂತವನ್ನು ನಿಮ್ಮಲ್ಲಿಗೆ ನಾನು ತಂದಿರುತ್ತೇನೆ. ಆದ್ದರಿಂದ ಅಲ್ಲಾಹನಿಗೆ ಭಯಪಡಿರಿ. ನನ್ನನ್ನು ಅನುಸರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(3,51,'3:51','ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನು ನನ್ನ ಮತ್ತು ನಿಮ್ಮ ಪ್ರಭುವಾಗಿರುವನು. ಆದ್ದರಿಂದ ಅವನನ್ನು ನೀವು ಆರಾಧಿಸಿರಿ. ಇದುವೇ ನೇರ ಮಾರ್ಗ .');
INSERT INTO "kannada" VALUES(3,52,'3:52','ಕೊನೆಗೆ ಈಸಾ ರವರು ಜನರಿಂದ ಸತ್ಯ ನಿಷೇಧವನ್ನು ಕಂಡರಿತಾಗ ಅವರು ಹೇಳಿದರು; ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ನನಗೆ ನೆರವಾಗುವವರು ಯಾರಿದ್ದಾರೆ? ಆಗ ಹವಾರಿಗಳು ಹೇಳಿದರು ; ‘ನಾವು ಅಲ್ಲಾಹನ ಸಹಾಯಕರು. ನಾವು ಅಲ್ಲಾಹನಲ್ಲಿ ದೃಢ ನಂಬಿಕೆ ಇಟ್ಟಿದ್ದೇವೆ. ನಾವು ಅಲ್ಲಾಹನಿಗೆ ಪೂರ್ಣವಿಧೇಯರಾದವರೆಂದು ತಾವು ಸಾಕ್ಷ್ಯ ವಹಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(3,53,'3:53','ನಮ್ಮ ಪ್ರಭೂ ! ನೀನು ಅವತೀರ್ಣಗೊಳಿಸಿದುದರಲ್ಲಿ ನಾವು ನಂಬಿಕೆ ಇರಿಸಿದ್ದೇವೆ. ನಿನ್ನ ಈ ದೂತರನ್ನು ಅನುಸರಿಸಿದ್ದೇವೆ. ನಮ್ಮನ್ನು ನೀನು (ಸತ್ಯಕ್ಕೆ) ಸಾಕ್ಷಿಗಳಾದವರ ಕೂಟಕ್ಕೆ ಸೇರಿಸು’ .');
INSERT INTO "kannada" VALUES(3,54,'3:54','ಅವರು (ಇಸ್ರೇಲಿ ಜನಾಂಗದ ಅವಿಶ್ವಾಸಿಗಳು) ಗೂಢತಂತ್ರ ಪ್ರಯೋಗಿಸಿದರು. ಅಲ್ಲಾಹನು (ಅವರ ಗೂಢತಂತ್ರಕ್ಕೆ) ಪ್ರತಿಕ್ರಮ ಹೂಡಿದನು. ಪ್ರತಿಕ್ರಮಿಗಳಲ್ಲಿ ಅಲ್ಲಾಹನು ಪರಮ ನಿಪುಣನು .');
INSERT INTO "kannada" VALUES(3,55,'3:55','ಅಲ್ಲಾಹನು ಹೇಳಿದ ಸಂದರ್ಭ(ವನ್ನು ನೆನೆಸಿರಿ). ಓ ಈಸಾ ! ನಿಶ್ಚಯವಾಗಿಯೂ ನಿನ್ನನ್ನು ನಾನು ಹಿಡಿಯುವೆನು . ನನ್ನಲ್ಲಿಗೆ ಎತ್ತುವೆನು . ಸತ್ಯ ನಿಷೇಧಿಗಳಿಂದ ನಿನ್ನನ್ನು ದೂರಗೊಳಿಸುವೆನು. ನಿನ್ನ ಅನುಯಾಯಿಗಳನ್ನು ಪುನರುತ್ಥಾನದ ದಿನದ ವರೆಗೂ ಸತ್ಯನಿಷೇಧಿಗಳಿಗಿಂತ ಉನ್ನತರನ್ನಾಗಿ ಮಾಡುವೆನು. ಮುಂದೆ ನಿಮ್ಮ ವಾಪಾಸಾತಿ ನನ್ನೆಡೆಗೇ ಇರುವುದು. ಆಗ ನಾನು ನೀವು ಇಲ್ಲಿ ಪರಸ್ಪರ ಬೇಧ ತಳೆದ ವಿಷಯದಲ್ಲಿ ತೀರ್ಪು ಕೊಡುವೆನು.');
INSERT INTO "kannada" VALUES(3,56,'3:56','ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ನಾನು ಇಹದಲ್ಲೂ ಪರದಲ್ಲೂ ಉಗ್ರ ಶಿಕ್ಷೆಗೊಳಪಡಿಸುವೆನು. ಅವರಿಗೆ ಸಹಾಯಕರಾಗಿ ಯಾರೂ ಇರಲಾರರು.');
INSERT INTO "kannada" VALUES(3,57,'3:57','ಸತ್ಯ ನಂಬಿಕೆ ತಾಳಿ, ಪುಣ್ಯ ಕಾರ್ಯಗಳನ್ನು ಯಾರು ಮಾಡುತ್ತಾರೋ ಅವರ ಪ್ರತಿಫಲವನ್ನು ಅವನು ಅವರಿಗೆ ಪೂರ್ತಿ ಮಾಡಿಕೊಡುವನು. ಅಲ್ಲಾಹು ಅಕ್ರಮಿಗಳನ್ನು ಇಷ್ಟಪಡುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(3,58,'3:58','ಇದು ತಮಗೆ ನಾವು ಓದಿ ಹೇಳುತ್ತಿರುವ ಪುರಾವೆ ಗಳು ಹಾಗೂ ಸತ್ವ ಸಂಪೂರ್ಣವಾದ ಬೋಧನೆಗಳು.');
INSERT INTO "kannada" VALUES(3,59,'3:59','ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹನ ಬಳಿ ಈಸಾರವರ ಅವಸ್ಥೆ ಆದಮರ ಅವಸ್ಥೆಯಂತೆ. ಅವರನ್ನು ಅವನು ಮಣ್ಣಿನಿಂದ ಸೃಷ್ಟಿಸಿದನು. ಆಮೇಲೆ ಮನುಷ್ಯನಾಗು ಎಂದು ಅವರೊಂದಿಗೆ ಹೇಳಿದನು. ಆಗ ಅವರು ಉಂಟಾದರು ಂ.');
INSERT INTO "kannada" VALUES(3,60,'3:60','(ಇದು) ತಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯ ಸತ್ಯ. ಆದ್ದರಿಂದ ತಾವು ಸಂಶಯ ಪಡುವವರ ಕೂಟಕ್ಕೆ ಸೇರಿ ಹೋಗದಿರಿ.');
INSERT INTO "kannada" VALUES(3,61,'3:61','ಇನ್ನು ನಿಮಗೆ ಈ ಜ್ಞಾನ ಬಂದ ಬಳಿಕವೂ ಯಾರಾದರೂ (ಈಸಾ ನಬಿ(ಅ)ಯವರ) ವಿಷಯದಲ್ಲಿ ನಿಮ್ಮಲ್ಲಿ ತರ್ಕಕ್ಕಿಳಿದರೆ ನೀವು ಹೇಳಿರಿ; `ನೀವು ಬನ್ನಿರಿ. ನಮ್ಮ ಮಕ್ಕಳನ್ನೂ ನಿಮ್ಮ ಮಕ್ಕಳನ್ನೂ ನಮ್ಮ ಸ್ತ್ರೀಯರನ್ನೂ, ನಿಮ್ಮ ಸ್ತ್ರೀಯರನ್ನೂ ಕರೆಯೋಣ. ನಾವು ಹಾಜರಾಗೋಣ. ಆಮೇಲೆ ನಮ್ಮಲ್ಲಿ ಯಾರು ಅಸತ್ಯವಾದಿಗಳೋ ಅವರಿಗೆ ಅಲ್ಲಾಹುವಿನ ಶಾಪ ತಟ್ಟಲಿ ಎಂದು ಪ್ರಾರ್ಥಿಸೋಣ'' ');
INSERT INTO "kannada" VALUES(3,62,'3:62','ಖಂಡಿತ ಇದು ಯಥಾರ್ಥ ವಾರ್ತೆ. ಅಲ್ಲಾಹನಲ್ಲದೆ ಬೇರೆ ಯಾವುದೇ ಆರಾಧ್ಯನಿಲ್ಲ. ಅವನು ಅಜೇಯನೂ ಮಹಾ ತಂತ್ರಜ್ಞಾನಿಯೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(3,63,'3:63','ಇನ್ನು ಅವರು (ಸತ್ಯವಿಶ್ವಾಸದಿಂದ ) ವಿಮುಖರಾದರೆ ಖಂಡಿತ! ಅಲ್ಲಾಹನು ಗೊಂದಲಕಾರಿಗಳನ್ನು ಚೆನ್ನಾಗಿ ಬಲ್ಲವನೇ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(3,64,'3:64','ಹೇಳಿರಿ ; `ಓ ವೇದದವರೇ! ನಮ್ಮ-ನಿಮ್ಮೆಡೆಯಲ್ಲಿ ಸಮನಾದ ಒಂದು ವಚನ ತತ್ವದ ಕಡೆಗೆ ನೀವು ಬನ್ನಿರಿ. (ಅದೇನೆಂದರೆ) ಅಲ್ಲಾಹನ ಹೊರತು ನಾವು ಬೇರೆ ಯಾರಿಗೂ ಆರಾಧಿಸಬಾರದು. ಅವನೊಂದಿಗೆ ಬೇರೆ ಯಾವುದನ್ನೂ ಭಾಗಿಯಾಗಿಸದಿರೋಣ. ಅಲ್ಲಾಹನ ಹೊರತು ನಮ್ಮಲ್ಲಿ ಕೆಲವರು ಕೆಲವರನ್ನು ಪ್ರಭುಗಳನ್ನಾಗಿ ಮಾಡದಿರೋಣ'' . (ಎಂಬ ತತ್ವದ ಕಡೆಗೆ). ಇನ್ನೂ ಅವರು ವಿಮುಖರಾದರೆ ಹೇಳಿರಿ; `ಖಂಡಿತವಾಗಿಯೂ ನಾವು ಅಲ್ಲಾಹನಿಗೆ ಪೂರ್ಣ ಶರಣಾಗಿದ್ದೇವೆ ಎನ್ನುವುದಕ್ಕೆ ನೀವು ಸಾಕ್ಷಿಗಳಾಗಿರಿ’.');
INSERT INTO "kannada" VALUES(3,65,'3:65','ಓ ವೇದದವರೇ! ಇಬ್ರಾಹೀಮರ ವಿಷಯದಲ್ಲಿ ನೀವು ಯಾಕೆ ತರ್ಕಿಸುತ್ತಿದ್ದೀರಿ? ತೌರಾತ್ ಮತ್ತು ಇಂಜೀಲ್ ಅವತೀರ್ಣವಾದುದೇ ಅವರ ನಂತರ ತಾನೇ ? ನೀವು ಚಿಂತಿಸುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(3,66,'3:66','ಓ ಜನರೇ! ನಿಮಗೆ ಅಲ್ಪ ಸ್ವಲ್ಪ ಗೊತ್ತೇ ಇರುವ ವಿಷಯದಲ್ಲಿ ನೀವು ತರ್ಕ ಮಾಡಿದ್ದೀರಿ. ಹಾಗಿರುವಾಗ ನೀವು ಯಾವುದೇ ಅರಿವು ಇಲ್ಲದ ವಿಷಯದಲ್ಲಿ ಏಕೆ ತರ್ಕಿಸುತ್ತಿದ್ದೀರಿ ? ಅಲ್ಲಾಹನಿಗೆ ಗೊತ್ತಿದೆ. ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(3,67,'3:67','ಇಬ್‍ರಾಹೀಮರು ಯಹೂದರೋ, ಕ್ರೈಸ್ತರೋ ಆಗಿರಲಿಲ್ಲ. ಆದರೆ ಅವರು ವಕ್ರ ಮಾರ್ಗಗಳನ್ನು ತ್ಯಜಿಸಿ ಸತ್ಯಮಾರ್ಗವನ್ನು ಅನುಸರಿಸಿದ ನಿರ್ಮಲಚಿತ್ತದ ಮುಸ್ಲಿಮರಾಗಿದ್ದರು. ಅವರು ಬಹುದೇವಾರಾಧಕರಲ್ಲೂ ಸೇರಿದವರಾಗಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(3,68,'3:68','ನಿಶ್ಚಯವಾಗಿಯೂ ಜನರಲ್ಲಿ ಇಬ್ರಾಹೀಮರೊಂದಿಗೆ ಹೆಚ್ಚು ಸಂಬಂಧವುಳ್ಳವರು ಯಾರೆಂದರೆ ಅವರನ್ನು ಅನುಸರಿಸಿದವರು ಹಾಗೂ ಈ ಪ್ರವಾದಿ ಮತ್ತು ಸತ್ಯವಿಶ್ವಾಸಿಗಳು . ಅಲ್ಲಾಹನು ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ರಕ್ಷಾಧಿಕಾರಿಯಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(3,69,'3:69','ವೇದದವರಲ್ಲಿ ಒಂದು ಪಂಗಡವು ನಿಮ್ಮನ್ನು ದಾರಿ ತಪ್ಪಿಸಲು ಆಶಿಸುತ್ತಿದ್ದಾರೆ. ಆದರೆ ವಾಸ್ತವದಲ್ಲಿ ಅವರು ತಮಗೆ ತಾವೇ ದಾರಿತಪ್ಪಿಸಿಕೊಳ್ಳುತ್ತಿದ್ದಾರೆ. ಇದನ್ನು ಅವರು ಗ್ರಹಿಸಿಕೊಳ್ಳುವುದಿಲ್ಲ ಅಷ್ಟೆ!');
INSERT INTO "kannada" VALUES(3,70,'3:70','ಓ ವೇದದವರೇ! ಸತ್ಯಕ್ಕೆ ದೃಕ್ಸಾಕ್ಷಿಗಳಾಗಿಯೂ ಕೂಡಾ ನೀವು ಯಾಕೆ ಅಲ್ಲಾಹನ ದೃಷ್ಟಾಂತಗಳನ್ನು ನಿಷೇಧಿಸುತ್ತಿದ್ದೀರಿ?');
INSERT INTO "kannada" VALUES(3,71,'3:71','ಓ ಗ್ರಂಥದವರೇ! ನೀವು ತಿಳಿದೂ ತಿಳಿದೂ ಸತ್ಯ ವನ್ನು ಅಸತ್ಯದೊಂದಿಗೆ ಯಾಕೆ ಬೆರೆಸುತ್ತಿದ್ದೀರಿ? ಸತ್ಯವನ್ನು ಯಾಕೆ ಮುಚ್ಚಿಡುತ್ತಿದ್ದೀರಿ?');
INSERT INTO "kannada" VALUES(3,72,'3:72','ವೇದದವರಲ್ಲಿ ಒಂದು ವಿಭಾಗದವರು (ಸ್ವಂತ ಅನುಯಾಯಿಗಳೊಂದಿಗೆ) ಹೇಳಿದರು; ಈ ವಿಶ್ವಾಸಿಗಳಿಗೆ ಅವತೀರ್ಣವಾದುದನ್ನು ನೀವು ಹಗಲಿನ ಆರಂಭದಲ್ಲಿ ವಿಶ್ವಾಸವಿಡಿರಿ. ದಿನದ ಕೊನೆಯಲ್ಲಿ ಅದನ್ನು ನಿಷೇಧಿಸಿರಿ. ಹಾಗಾದರೆ ಅವರು ಮರಳಿ ಬರಬಹುದು .');
INSERT INTO "kannada" VALUES(3,73,'3:73','ನಿಮ್ಮ ಧರ್ಮವನ್ನು ಅನುಸರಿಸುವವರನ್ನು ಹೊರತು ಬೇರೆ ಯಾರನ್ನೂ ನಂಬಬೇಡಿರಿ (ಎಂದೂ ಅವರು ಹೇಳಿದರು) ಖಂಡಿತವಾಗಿಯೂ ಮಾರ್ಗದರ್ಶನವು ಅಲ್ಲಾಹನದ್ದು ಮಾತ್ರ ಎಂದು ಹೇಳಿರಿ. (ವೇದದವರು ತಮ್ಮ ಅನುಯಾಯಿಗಳಿಗೆ ತಮ್ಮ ಹೇಳಿಕೆಯನ್ನು ಹೀಗೆ ಮುಂದುವರಿಸಿದರು ನಿಮ್ಮ ಯಹೂದೀ ಧರ್ಮವನ್ನು ಅನುಸರಿಸಿದವರಿಗೆ ಸಿಕ್ಕಂತಹ ಮಾನ್ಯತೆ ಬೇರೆ ಯಾರಿಗೂ ಸಿಗ ಬಹುದೆಂದು ಭಾವಿಸಬೇಡಿರಿ. ಅವರು (ಮುಸ್ಲಿಮರು) ನಿಮ್ಮ ವಿರುದ್ಧ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಬಳಿ ಜಯಗಳಿಸುವರೆಂದೂ ಭಾವಿಸಬೇಡಿರಿ. (ಪ್ರವಾದಿಯವರೇ !) ಹೇಳಿರಿ; ನಿಶ್ಚಯವಾಗಿಯೂ ಔದಾರ್ಯವಿರುವುದು ಅಲ್ಲಾಹನ ವಶದಲ್ಲಿ. ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಅದನ್ನವನು ಕೊಡುತ್ತಾನೆ. ಅಲ್ಲಾಹನು ಪರಮ ವಿಶಾಲನೂ ಪರಮ ತಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(3,74,'3:74','ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ತನ್ನ ಅನುಗ್ರಹದಿಂದ ಪ್ರತ್ಯೇಕವಾಗಿ ಕೊಡುತ್ತಾನೆ. ಅಲ್ಲಾಹನು ಮಹಾ ಔದಾರ್ಯ ವಂತನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(3,75,'3:75','ನೀವು ಒಂದು (ಚಿನ್ನ ಬೆಳ್ಳಿಯ) ರಾಶಿಯನ್ನೇ ಅವರ ಬಳಿ ವಿಶ್ವಾಸವಿಟ್ಟು ಕೊಟ್ಟಿದ್ದರೆ ಅದನ್ನು ವಾಪಾಸು ಕೊಡುವಂತಹ ವ್ಯಕ್ತಿಗಳೂ ವೇದದವರ ಕೂಟದಲ್ಲಿದ್ದಾರೆ. ಆದರೆ ಅವರಲ್ಲಿ ಮತ್ತೆ ಕೆಲವರಿದ್ದಾರೆ. ನೀವು ಅವರ ಬಳಿ ಒಂದು ಬೆಳ್ಳಿ ನಾಣ್ಯವನ್ನು ನಂಬಿ ಇಟ್ಟರೂ ನೀವು ಅವರ ಬಳಿ ಹಠ ಹಿಡಿದು ನಿಂತರೆ ಹೊರತು ನಿಮಗೆ ವಾಪಾಸು ಕೊಡಲಾರರು. ಅದು ಯಾಕೆಂದರೆ ಈ ನಿರಕ್ಷರಿಗಳ (ಅರಬರ) ವಿಷಯದಲ್ಲಿ ನಮಗೆ ಶಿಕ್ಷೆ ದೊರಕಲು ಯಾವ ಮಾರ್ಗವೂ ಇಲ್ಲವೆಂದು ಅವರು ಹೇಳಿದ್ದಾರೆ. ಅವರು ಅರಿತು ಕೊಂಡೇ ಅಲ್ಲಾಹನ ಹೆಸರಲ್ಲಿ ಸುಳ್ಳು ಹೇಳುತ್ತಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(3,76,'3:76','ನಿಜ ಸಂಗತಿ ಹಾಗಲ್ಲ. ಯಾರು ತನ್ನ ಕರಾರನ್ನು ಪೂರ್ತಿಗೊಳಿಸುತ್ತಾರೋ ಹಾಗೂ ದೇವಭಕ್ತಿ ಇರಿಸುತ್ತಾರೋ ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಧರ್ಮನಿಷ್ಟರನ್ನು ಪ್ರೀತಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(3,77,'3:77','ಅಲ್ಲಾಹನೊಂದಿಗೆ ಮಾಡಿಕೊಂಡ ಕರಾರು ಮತ್ತು (ಅಲ್ಲಾಹನ ಹೆಸರಲ್ಲಿ) ಸ್ವತಃ ಹಾಕಿದ ಆಣೆಗಳನ್ನು ತುಚ್ಛ ಬೆಲೆಗೆ ಮಾರುವವರಿಗೆ ಪರಲೋಕದಲ್ಲಿ ಯಾವುದೇ ಭಾಗ್ಯ (ಪಾಲು) ಖಂಡಿತ ಇಲ್ಲ. ಪರಲೋಕದಲ್ಲಿ ಅಲ್ಲಾಹನು ಅವರೊಂದಿಗೆ ಮಾತನ್ನೇ ಆಡಲಾರನು. ಅವರತ್ತ ಕರುಣೆಯಿಂದ ನೋಡುವುದೂ ಇಲ್ಲ. ಅವರನ್ನು (ಪಾಪ ಮಾಲಿನ್ಯಗಳಿಂದ) ಶುದ್ಧಿಗೊಳಿಸುವುದೂ ಇಲ್ಲ . ಅವರಿಗೆ ಯಾತನಾಮಯ ಶಿಕ್ಷೆ ಕಾದಿದೆ.');
INSERT INTO "kannada" VALUES(3,78,'3:78','ಖಂಡಿತವಾಗಿಯೂ ವೇದದವರಲ್ಲಿ ಕೆಲವರಿದ್ದಾರೆ. ವೇದವನ್ನು ಅವರು ನಾಲಿಗೆ ಕೊಂಕಿಸಿ ಓದುತ್ತಾರೆ ! ಅದು ಕೂಡ ಗ್ರಂಥದಲ್ಲಿದ್ದುದೆಂದು ನೀವು ಭಾವಿಸಲಿಕ್ಕಾಗಿ. ವಾಸ್ತವದಲ್ಲಿ ಅದು ಗ್ರಂಥದಲ್ಲಿರುವುದಿಲ್ಲ. ‘ಇದು ಅಲ್ಲಾಹನ ಕಡೆಯದ್ದು’ ಎಂದು ಅವರು ಹೇಳುತ್ತಾರೆ. ನಿಜದಲ್ಲಿ ಅದು ಅಲ್ಲಾಹನ ಕಡೆಯದ್ದಾಗಿರುವುದಿಲ್ಲ. ತಿಳಿದುಕೊಂಡೇ ಅವರು ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಳ್ಳು ಹೇಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(3,79,'3:79','ಅಲ್ಲಾಹನು ವೇದ ಗ್ರಂಥವನ್ನೂ ಸುಜ್ಞಾನವನ್ನೂ ಪ್ರವಾದಿತ್ವವನ್ನೂ ಕೊಟ್ಟಂತಹ ಒಬ್ಬ ಮನುಷ್ಯನು ‘ನೀವು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು (ನನಗೆ ಆರಾಧಿಸುವ) ನನ್ನ ದಾಸರಾಗಿರಿ''. ಎಂದು ಜನರಲ್ಲಿ ಹೇಳುವನೆನ್ನುವುದು ತಕ್ಕುದಲ್ಲ. ಪರಂತು; `ನೀವು ವೇದಗ್ರಂಥವನ್ನು ಓದುವವರೂ ಕಲಿಸುವವರೂ ಆಗಿರುವುದರಿಂದ ನೀವು ಸುಜ್ಞಾನಿಗಳಾಗಿರಿ'' ಎಂದಷ್ಟೇ ಅವನು ಹೇಳುವನು.');
INSERT INTO "kannada" VALUES(3,80,'3:80','ದೇವಚರರನ್ನೂ ಪ್ರವಾದಿಗಳನ್ನೂ ಆರಾಧ್ಯರನ್ನಾಗಿ ಮಾಡಲು ಆ ಪ್ರವಾದಿ ನಿಮಗೆ ಆಜ್ಞಾಪಿಸ ಲಾರರು. ನೀವು ಪರಿಪೂರ್ಣವಾಗಿ ಅಲ್ಲಾಹನಿಗೆ ಅನುಸರಿಸುವವರಾದ ಬಳಿಕ ಸತ್ಯ ನಿಷೇಧವನ್ನು ಅವರು ನಿಮಗೆ ಆದೇಶಿಸುವರೇ? ');
INSERT INTO "kannada" VALUES(3,81,'3:81','ಅಲ್ಲಾಹನು ಪ್ರವಾದಿಗಳಿಂದ ಕರಾರು ತೆಗೆದುಕೊಂಡ ಸಂದರ್ಭ(ವನ್ನು ನೆನೆಸಿರಿ). `ನಿಮಗೆ ನಾನು ಗ್ರಂಥ ಮತ್ತು ಸುಜ್ಞಾನವನ್ನು ಕೊಟ್ಟ ಬಳಿಕ ನಿಮ್ಮಲ್ಲಿರುವುದನ್ನು ದೃಢಪಡಿಸುತ್ತಾ ಓರ್ವ ದೂತರು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದರೆ ಅಗತ್ಯವಾಗಿ ನೀವು ಅವರಲ್ಲಿ ವಿಶ್ವಾಸ ತಾಳಬೇಕು ಹಾಗೂ ಅವರಿಗೆ ಸಹಾಯ ಮಾಡಬೇಕು. (ಎಂದು ಹೇಳಿದ ಬಳಿಕ) ಅವನು (ಅಲ್ಲಾಹು) ಕೇಳಿದನು ; `ನೀವು ಇದನ್ನು ಒಪ್ಪಿಕೊಂಡಿರಾ? ನನ್ನ ಕರಾರನ್ನು ಪಾಲಿಸುವ ಹೊಣೆಯನ್ನು ವಹಿಸಿಕೊಂಡಿರಾ ?’ ಅವರು ಹೇಳಿದರು; ‘ನಾವು ಒಪ್ಪಿ ಕೊಂಡೆವು’. (ಆಗ ಅಲ್ಲಾಹು ಹೇಳಿದನು) ‘ಹಾಗಾದರೆ ನೀವು ಸಾಕ್ಷ್ಯ ವಹಿಸಿರಿ. ನಾನೂ ಕೂಡಾ ನಿಮ್ಮ ಜೊತೆ ಸಾಕ್ಷಿ’ ');
INSERT INTO "kannada" VALUES(3,82,'3:82','ಇನ್ನು ಇದರ ನಂತರ ಯಾರು ವಿಮುಖರಾಗು ತ್ತಾರೋ ಅವರೇ ಧರ್ಮಭ್ರಷ್ಟರು.');
INSERT INTO "kannada" VALUES(3,83,'3:83','ಅವರು ಅಲ್ಲಾಹನ ಧರ್ಮವನ್ನು ಹೊರತು ಬೇರೆ ಯಾವುದನ್ನಾದರೂ ಬಯಸುತ್ತಿರುವರೇ? ವಾಸ್ತವದಲ್ಲಿ ಆಕಾಶಗಳಲ್ಲಿರುವವರೂ ಭೂಮಿ ಯಲ್ಲಿರುವವರೂ ಮನಃಪೂರ್ವಕವಾಗಿ ಅಥವಾ ಅನಿವಾರ್ಯವಾಗಿ ಅವನಿಗೆ ಶರಣಾಗಿದ್ದಾರೆ. ಅವರೆಲ್ಲರೂ ಅವನ ಬಳಿಗೇ ಮರಳುವವರು.');
INSERT INTO "kannada" VALUES(3,84,'3:84','ಹೇಳಿರಿ. ಅಲ್ಲಾಹನಲ್ಲಿ ನಾವು ವಿಶ್ವಾಸವಿಟ್ಟೆವು. ನಮಗೆ ಅವತೀರ್ಣವಾದುದರಲ್ಲಿಯೂ ಇಬ್‍ರಾಹೀಮ್, ಇಸ್ಮಾಈಲ್, ಇಸ್ಹಾಖ್, ಯಅïಖೂಬ್ ಹಾಗೂ ಅವರ ಸಂತತಿಗಳಿಗೆ ಅವತೀರ್ಣವಾದುದರಲ್ಲಿಯೂ ಮೂಸಾ, ಈಸಾ ಹಾಗೂ ಇತರೆಲ್ಲ ಪ್ರವಾದಿಗಳಿಗೂ ಅವರ ಪ್ರಭುವಿನಿಂದ ದೊರೆತುದರಲ್ಲಿಯೂ ನಾವು ವಿಶ್ವಾಸವಿಟ್ಟೆವು. ಅವರಲ್ಲಿ ಯಾರ ಬಗ್ಗೆಯೂ ನಾವು ತಾರತಮ್ಯ ಕಲ್ಪಿಸುವುದಿಲ್ಲ. ನಾವು ಅವನಿಗೆ ಸಂಪೂರ್ಣ ಶರಣಾಗತರು.');
INSERT INTO "kannada" VALUES(3,85,'3:85','ಇಸ್ಲಾಮ್ ಅಲ್ಲದೆ ಬೇರೆ ಯಾವುದೇ ಮತವನ್ನು ಯಾರಾದರೂ ಬಯಸಿದರೆ ಅವನಿಂದ ಅದನ್ನು ಖಂಡಿತ ಸ್ವೀಕರಿಸಲಾಗುವುದಿಲ್ಲ. ಪರಲೋಕದಲ್ಲಿ ಅವನು ನಷ್ಟವಂತರ ಸಾಲಿಗೆ ಸೇರುವನು.');
INSERT INTO "kannada" VALUES(3,86,'3:86','ಸತ್ಯವಿಶ್ವಾಸ (ಈಮಾನ್)ವನ್ನು ಅವಲಂಬಿಸಿದ ಬಳಿಕವೂ ಅಲ್ಲಾಹನ ದೂತರು ಸತ್ಯವಂತರೆಂದು ಸಾಕ್ಷ್ಯ ವಹಿಸಿದ ಬಳಿಕವೂ ಸುವ್ಯಕ್ತ ಪುರಾವೆಗಳು ಅವರಿಗೆ ಬಂದ ಬಳಿಕವೂ ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸಿದ ಒಂದು ಜನತೆಯನ್ನು ಅಲ್ಲಾಹನು ಸತ್ಯದ ಹಾದಿಗೆ ತರುವುದಾದರೂ ಹೇಗೆ? ಅಕ್ರಮಿಗಳಾದ ಜನತೆಯನ್ನು ಅಲ್ಲಾಹನು ನೇರಮಾರ್ಗಕ್ಕೆ ತರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(3,87,'3:87','ಇವರ ಮೇಲೆ ಅಲ್ಲಾಹು, ಮಲಕ್‍ಗಳು ಹಾಗೂ ಎಲ್ಲ ಜನರ ಶಾಪವಿದೆ ಎನ್ನುವುದೇ ಇಂತಹವರಿಗೆ ಪ್ರತಿಫಲವಾಗಿದೆ .');
INSERT INTO "kannada" VALUES(3,88,'3:88','ಅದರಲ್ಲವರು ಶಾಶ್ವತರಾಗಿರುವರು. ಅವರಿಗೆ ಶಿಕ್ಷೆ ಹಗುರಗೊಳಿಸಲಾಗುವುದಿಲ್ಲ. ಅವರಿಗೆ ಅವಧಿಯನ್ನೂ ಕೊಡಲಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(3,89,'3:89','ಆದರೆ ಆಮೇಲೆ ಪಶ್ಚಾತ್ತಾಪ ಪಟ್ಟು ತಮ್ಮನ್ನು ಸುಧಾರಿಸಿಕೊಂಡವರ ಹೊರತು. ಅಲ್ಲಾಹನು ಬಹಳ ಹೆಚ್ಚು ಕ್ಷಮಿಸುವವನೂ ಪರಮ ದಯಾಪರನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(3,90,'3:90','ಖಂಡಿತವಾಗಿಯೂ ಸತ್ಯವಿಶ್ವಾಸ ತಾಳಿದ ಬಳಿಕ ಕಾಫಿರಾದವರು ಹಾಗೂ ಅವಿಶ್ವಾಸವನ್ನು ಮತ್ತಷ್ಟು ಹೆಚ್ಚಿಸಿಕೊಂಡವರ ಪಶ್ಚಾತ್ತಾಪವು ಖಂಡಿತ ಸ್ವೀಕೃತವಲ್ಲ. ಅವರೇ ದಾರಿ ತಪ್ಪಿದವರು.');
INSERT INTO "kannada" VALUES(3,91,'3:91','ನಿಶ್ಚಯವಾಗಿಯೂ ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸುತ್ತಾ ಸತ್ಯನಿಷೇಧಿಗಳಾಗಿಯೇ ಸತ್ತವರು ಭೂಮಿ ತುಂಬ ಚಿನ್ನವನ್ನು ದಂಡವಾಗಿ ಕೊಟ್ಟರೂ ಕೂಡಾ ಅವರಲ್ಲೊಬ್ಬ ನಿಂದಲೂ ಅದನ್ನು ಖಂಡಿತವಾಗಿಯೂ ಸ್ವೀಕರಿಸಲಾಗದು. ಅವರಿಗೆ ತೀವ್ರ ಯಾತನೆಯ ಸಜೆ ಕಾದಿದೆ. ಅವರಿಗೆ ಯಾವುದೇ ಸಹಾಯಕರು ಇರಲಾರರು.');
INSERT INTO "kannada" VALUES(3,92,'3:92','ನಿಮಗೆ ಪ್ರಿಯವಾದ ವಸ್ತುಗಳಿಂದ ನೀವು ವೆಚ್ಚ ಮಾಡುವವರೆಗೂ ನೀವು ಪುಣ್ಯ ಪಡೆಯಲಾರಿರಿ. ನೀವು ಏನೇ ವೆಚ್ಚ ಮಾಡಿದರೂ ಅಲ್ಲಾಹನು ಅದರ ಬಗ್ಗೆ ಖಂಡಿತವಾಗಿಯೂ ಚೆನ್ನಾಗಿ ಬಲ್ಲವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(3,93,'3:93','ಭಕ್ಷ್ಯ ವಸ್ತುಗಳೆಲ್ಲವೂ ಬನೂ ಇಸ್ರಾಯೀಲರಿಗೆ ಗ್ರಾಹ್ಯವಾಗಿದ್ದವು. ತೌರಾತ್ ಇಳಿಸಲ್ಪಡುವ ಮುನ್ನ ಇಸ್ರಾಈಲ್ (ಯಅïಖೂಬ್ ನಬಿ) ತನಗೆ ನಿಷಿದ್ಧಗೊಳಿಸಿದ್ದು ಹೊರತು. (ಪ್ರವಾದಿಯರೇ !) ನೀವು ಹೇಳಿರಿ; ನೀವು ತೌರಾತನ್ನು ತಂದು ಅದನ್ನೊಮ್ಮೆ ಓದಿರಿ. ನೀವು ಸತ್ಯವಾದಿಗಳಾಗಿದ್ದರೆ!');
INSERT INTO "kannada" VALUES(3,94,'3:94','ಇದರ ನಂತರವೂ ಯಾರಾದರೂ ಅಲ್ಲಾಹನ ಮೇಲೆ ಕಟ್ಟುಕತೆ ಹೊರಿಸಿದರೆ ಅವರೇ ಅಕ್ರಮಿಗಳು.');
INSERT INTO "kannada" VALUES(3,95,'3:95','ನೀವು ಹೇಳಿರಿ; ಅಲ್ಲಾಹು ಹೇಳಿದುದು ಸತ್ಯ. ಆದ್ದರಿಂದ ಮಿಥ್ಯ ದಾರಿಗಳನ್ನು ತ್ಯಜಿಸಿ ಸತ್ಯ ದಾರಿಯಲ್ಲಿ ಚಲಿಸುತ್ತಿರುವ ಇಬ್ರಾಹೀಮರ ಮಾರ್ಗ ವನ್ನು ಅನುಸರಿಸಿರಿ. ಅವರು ಬಹು ದೇವಾರಾಧಕರ ಕೂಟಕ್ಕೆ ಸೇರಿದವರಾಗಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(3,96,'3:96','(ಅಲ್ಲಾಹನಿಗೆ ಆರಾಧಿಸಲೆಂದು) ಖಂಡಿತವಾಗಿ ಯೂ ಮನುಷ್ಯರಿಗಾಗಿ ಸ್ಥಾಪಿತವಾದ ಮೊಟ್ಟ ಮೊದಲ ಭವನವು ಬಕ್ಕಃ(ಮಕ್ಕಾ)ದಲ್ಲಿರುವುದೇ ಆಗಿರುತ್ತದೆ. ಅದು ಅನುಗ್ರಹೀತವೂ ಲೋಕದ ವರಿಗೆ ಮಾರ್ಗದರ್ಶಕವೂ ಆಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(3,97,'3:97','ಅದರಲ್ಲಿ ಸ್ಪಷ್ಟವಾದ ದೃಷ್ಟಾಂತಗಳಿವೆ. ಇಬ್ರಾಹೀಮ್ ಮಖಾಮ್ (ಅದರಲ್ಲೊಂದು) . ಈ ಮಸ್ಜಿದ್‍ಗೆ ಪ್ರವೇಶಿಸಿದವರು ನಿರ್ಭಯರಾಗುತ್ತಾರೆ . ಅಲ್ಲಿಗೆ ತಲಪುವ ತಾಕತ್ತು ಉಳ್ಳ ಮನುಷ್ಯರಿಗೆ ಆ ಗೃಹದಲ್ಲಿ ಹಜ್ಜ್ ಮಾಡುವುದು ಅಲ್ಲಾಹನೊಂ ದಿಗಿನ ಬಾಧ್ಯತೆಯಾಗಿದೆ. ಇನ್ನು ಯಾರಾದರೂ ನಿಷೇಧಿಸಿದರೆ ಅಲ್ಲಾಹನು ಖಂಡಿತವಾಗಿಯೂ ಲೋಕದವರಿಂದ ಆಶ್ರಯ ನಿರಪೇಕ್ಷನಾಗಿ ರುವನು.');
INSERT INTO "kannada" VALUES(3,98,'3:98','ಓ ಗ್ರಂಥದವರೇ! ಅಲ್ಲಾಹನ ವಚನಗಳನ್ನು ನೀವು ಯಾಕೆ ನಿಷೇಧಿಸುತ್ತಿದ್ದೀರಿ. ನೀವು ಮಾಡುತ್ತಿರುವ ಕರ್ಮಗಳ ಮೇಲೆ ಅಲ್ಲಾಹನು ಸಾಕ್ಷಿಯಾಗಿರುತ್ತಾನೆ ಎಂದು ಹೇಳಿರಿ (ಪ್ರವಾದಿಯರೇ!)');
INSERT INTO "kannada" VALUES(3,99,'3:99','ಹೇಳಿರಿ; ಓ ಗ್ರಂಥದವರೇ, ನೀವು ತಿಳಿದವ ರಾಗಿದ್ದೂ ಕೂಡಾ ವಕ್ರ ಮಾರ್ಗವನ್ನು ಬಯಸುತ್ತಾ ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ ವಿಶ್ವಾಸಿಗಳನ್ನು ಯಾಕೆ ತಡೆಯುತ್ತಿದ್ದೀರಿ? ನಿಮ್ಮ ಕೃತ್ಯಗಳ ಬಗ್ಗೆ ಅಲ್ಲಾಹನು ಖಂಡಿತ ಅಶ್ರದ್ಧನಲ್ಲ.');
INSERT INTO "kannada" VALUES(3,100,'3:100','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ಗ್ರಂಥ ನೀಡಲ್ಪಟ್ಟವರಲ್ಲಿ ಒಂದು ಪಂಗಡವನ್ನು ನೀವು ಅನುಸರಿಸುವು ದಾದರೆ ನಿಮ್ಮನ್ನು ಅವರು ನಿಮ್ಮ ಸತ್ಯವಿಶ್ವಾಸದ ನಂತರ ಅವಿಶ್ವಾಸಿಗಳನ್ನಾಗಿ ತಿರುಗಿಸಿಬಿಡುವರು.');
INSERT INTO "kannada" VALUES(3,101,'3:101','ನೀವು ಹೇಗೆ ಅವಿಶ್ವಾಸಿಗಳಾಗುತ್ತೀರಿ? ನಿಮಗೆ ಅಲ್ಲಾಹನ ವಚನಗಳನ್ನು ಓದಿ ಹೇಳಲಾಗುತ್ತಿದೆ. ಅವನ ರಸೂಲರು ನಿಮ್ಮೊಂದಿಗೇ ಇದ್ದಾರೆ. ಯಾರು ಅಲ್ಲಾಹನ ಮಾರ್ಗದರ್ಶನವನ್ನು ಭದ್ರವಾಗಿ ಹಿಡಿದುಕೊಳ್ಳುವರೋ ಖಂಡಿತವಾಗಿಯೂ ಅವರು ಋುಜುಮಾರ್ಗದಲ್ಲಿ ಸೇರಿಸಲ್ಪಟ್ಟವರಾಗುವರು.');
INSERT INTO "kannada" VALUES(3,102,'3:102','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ದೇವಭಕ್ತಿ (ತಖ್‍ವ) ಇರಿಸಿಕೊಳ್ಳ ಬೇಕಾದ ಕ್ರಮ ಪ್ರಕಾರ ನೀವು ಅಲ್ಲಾಹನಲ್ಲಿ ಭಕ್ತಿ ಇರಿಸಿಕೊಳ್ಳಿರಿ. ಮುಸ್ಲಿಮರಾ ಗಿಯಲ್ಲದೆ ನೀವು ಖಂಡಿತ ಮರಣ ಹೊಂದ ಬಾರದು .');
INSERT INTO "kannada" VALUES(3,103,'3:103','ನೀವೆಲ್ಲರೂ ಒಗ್ಗಟ್ಟಾಗಿ ಅಲ್ಲಾಹನ ಪಾಶ (ಧರ್ಮ) ವನ್ನು ಗಟ್ಟಿಯಾಗಿ ಹಿಡಿದುಕೊಳ್ಳಿರಿ. ನೀವು ಭಿನ್ನರಾಗದಿರಿ . ನಿಮಗೆ ಅಲ್ಲಾಹನು ನೀಡಿದ ಅನುಗ್ರಹವನ್ನು ಜ್ಞಾಪಿಸಿಕೊಳ್ಳಿರಿ. ನೀವು ಪರಸ್ಪರ ಶತ್ರುಗಳಾಗಿದ್ದಾಗ ಆತನು ನಿಮ್ಮ ಮನಸ್ಸುಗಳನ್ನು ಜೋಡಿಸಿದನು. ಹಾಗಾಗಿ ನೀವು ಆತನ ಅನುಗ್ರಹದಿಂದ ಸೋದರರಾದಿರಿ. ನೀವು ಅಗ್ನಿ ಪ್ರಪಾತದ ಅಂಚಿನಲ್ಲಿದ್ದಿರಿ. ಅದರಿಂದ ನಿಮ್ಮನ್ನು ಆತನು ರಕ್ಷಿಸಿದನು. ಈ ರೀತಿ ಅಲ್ಲಾಹನು ಆತನ ದೃಷ್ಟಾಂತಗಳನ್ನು ವಿವರಿಸಿ ಕೊಡುತ್ತಾನೆ. ನೀವು ಸತ್ಯ ಮಾರ್ಗ ಹೊಂದಲೆಂದು.');
INSERT INTO "kannada" VALUES(3,104,'3:104','ಒಳಿತಿನ ಕಡೆಗೆ ಕರೆಯುವ, ಒಳ್ಳೆಯ ಕಾರ್ಯ ಗಳಿಗೆ ಉಪದೇಶ ಕೊಡುವ, ಕೆಟ್ಟದ್ದನ್ನು ವಿರೋಧಿ ಸುವ ಒಂದು ವಿಭಾಗ ಜನರು ನಿಮ್ಮಲ್ಲಿ ಅಗತ್ಯ ವಾಗಿ ಇರಬೇಕು. ನಿಜವಾಗಿಯೂ ಅವರೇ ಜಯ ಶಾಲಿಗಳು.');
INSERT INTO "kannada" VALUES(3,105,'3:105','ಸ್ಪಷ್ಟವಾದ ಪುರಾವೆಗಳು ತಮಗೆ ಬಂದ ಬಳಿಕವೂ (ಧರ್ಮದಲ್ಲಿ) ಪರಸ್ಪರ ಭಿನ್ನರಾಗಿ ವಿವಿಧ ಪಕ್ಷಗಳಾದವರಂತೆ ನೀವಾಗಬಾರದು. ಅವರಿಗೆ ಖಂಡಿತವಾಗಿಯೂ ಭಾರೀ ಶಿಕ್ಷೆಯು ಕಾದಿದೆ.');
INSERT INTO "kannada" VALUES(3,106,'3:106','ಕೆಲವು ಮುಖಗಳು ಬಿಳಿಯಾಗುವ ಮತ್ತು ಕೆಲವು ಮುಖಗಳು ಕಪ್ಪಾಗುವ ದಿನ (ಆ ಶಿಕ್ಷೆ ಅನುಭವಕ್ಕೆ ಬರುವುದು). ಮುಖವು ಕಪ್ಪಾ ದವರಲ್ಲಿ (ಹೀಗೆಂದು ಹೇಳಲಾಗುವುದು;) ನೀವು ಸತ್ಯವಿಶ್ವಾಸ ತಾಳಿದ ನಂತರ ನೀವು ಸತ್ಯ ನಿಷೇಧಿಗಳಾಗಲಿಲ್ಲವೇ? ಆದ್ದರಿಂದ ನೀವು ಅವಿಶ್ವಾಸಿಯಾದುದರ ಫಲವಾಗಿ ಶಿಕ್ಷೆಯನ್ನು ಅನುಭವಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(3,107,'3:107','ಮುಖಗಳು ಬಿಳಿಯಾದವರಾರೋ ಅವರು ಅಲ್ಲಾಹನ ಕೃಪೆಯಲ್ಲಿರುತ್ತಾರೆ. ಅವರು ಅದರಲ್ಲಿ ಶಾಶ್ವತರಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(3,108,'3:108','ಇದೆಲ್ಲವೂ ಅಲ್ಲಾಹನ ಪುರಾವೆಗಳು. ಇದನ್ನು ನಾವು ನಿಮಗೆ ಸತ್ಯಸಮೇತ ಓದಿ ಕೇಳಿಸುತ್ತಿದ್ದೇವೆ. ಅಲ್ಲಾಹನು ಲೋಕದವರನ್ನು ಅಕ್ರಮಿಸಲು ಉದ್ದೇಶಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(3,109,'3:109','ಆಕಾಶಗಳಲ್ಲಿರುವುದೂ ಭೂಮಿಯಲ್ಲಿರುವುದೂ ಎಲ್ಲವೂ ಅಲ್ಲಾಹನಿಗೆ ಸೇರಿದ್ದು. ಎಲ್ಲ ಕಾರ್ಯ ಗಳೂ (ಕಟ್ಟಕಡೆಗೆ) ಅಲ್ಲಾಹನ ಕಡೆಗೇ ಮರಳು ವುದು.');
INSERT INTO "kannada" VALUES(3,110,'3:110','ಮಾನವರ ಒಳಿತಿಗಾಗಿ ನೇಮಿಸಲ್ಪಟ್ಟ ಸಮುದಾ ಯಗಳ ಪೈಕಿ ನೀವು ಅತ್ಯುತ್ತಮ ರಾಗಿರುವಿರಿ. ನೀವು ಒಳಿತನ್ನು ಆಜ್ಞಾಪಿಸುತ್ತೀರಿ. ಕೆಡುಕನ್ನು ವಿರೋಧಿಸುತ್ತೀರಿ. ಅಲ್ಲಾಹನಲ್ಲಿ ವಿಶ್ವಾಸವಿಡುತ್ತೀರಿ. ವೇದದವರು ವಿಶ್ವಾಸ ತಾಳಿದ್ದರೆ ಅದು ಅವರಿಗೆ ಒಳ್ಳೆಯದಿತ್ತು. ಅವರ ಕೂಟದಲ್ಲೂ ಸತ್ಯವಿಶ್ವಾಸ ತಾಳಿದವರೂ ಇದ್ದಾರೆ. ಆದರೆ ಅವರಲ್ಲಿ ಬಹು ಮಂದಿ ಧರ್ಮದ್ರೋಹಿಗಳಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(3,111,'3:111','ನಿಮಗೆ ಅವರು ಸ್ವಲ್ಪ ಕಿರುಕುಳ ಕೊಡಬಹುದೇ ಹೊರತು ನಿಮಗೆ ಏನೊಂದು ಹಾನಿ ಮಾಡಲೂ ಅವರಿಂದಾಗದು. ನಿಮ್ಮೊಂದಿಗೆ ಅವರು ಯುದ್ಧ ಮಾಡಿದರೆ ಹಿಂಜರಿದು ಓಡುವರು. ಆಮೇಲೆ (ನಿಮ್ಮ ವಿರುದ್ಧ) ಅವರಿಗೆ ಸಹಾಯ ದೊರೆಯದು');
INSERT INTO "kannada" VALUES(3,112,'3:112','ಅವರು ಎಲ್ಲಿದ್ದರೂ ಅವರಲ್ಲಿ ಹೀನತ್ವವು ಮುದ್ರಿತವಾಗಿರುವುದು. ಅಲ್ಲಾಹನಿಂದ ಮತ್ತು ಜನರಿಂದ ಒಂದು ಆಶ್ರಯದ ಹೊರತು (ಅವರಿಗೆ ರಕ್ಷೆಯಿಲ್ಲ). ಅವರು ಅಲ್ಲಾಹನ ಸಿಟ್ಟಿಗೆ ಪಾತ್ರರಾಗಿದ್ದಾರೆ. ದಾರಿದ್ರ್ಯವೂ ಅವರಲ್ಲಿ ಮುದ್ರಿತವಾಗಿದೆ. ಅವರು ಅಲ್ಲಾಹುವಿನ ವಚನಗಳನ್ನು ಧಿಕ್ಕರಿಸುತ್ತಲೂ ಪ್ರವಾದಿಗಳನ್ನು ಅನ್ಯಾಯವಾಗಿ ಕೊಲ್ಲುತ್ತಲೂ ಬಂದುದರಿಂದ (ಈ ದುರವಸ್ಥೆ ಅವರಿಗೆ ಒದಗಿದೆ.) ಇದು ಅವರು ಆಜ್ಞೋಲ್ಲಂಘನೆ ಮಾಡಿದ ಹಾಗೂ ಮೇರೆ ಮೀರಿದುದರ ಫಲವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(3,113,'3:113','ಅವರು (ವೇದದವರು) ಎಲ್ಲರೂ ಸಮಾನರಲ್ಲ. ವೇದದವರ ಪೈಕಿ ಸತ್ಯದಲ್ಲಿ ನೆಲೆಗೊಂಡವರೂ ಇದ್ದಾರೆ. ಅವರು ರಾತ್ರಿಯ ತಾಸುಗಳಲ್ಲಿ ಸಾಷ್ಟಾಂಗವೆರಗುತ್ತಾ ಅಲ್ಲಾಹನ ವಚನಗಳನ್ನು ಓದುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(3,114,'3:114','ಅಲ್ಲಾಹು ಹಾಗೂ ಅಂತ್ಯ ದಿನದಲ್ಲಿ ಅವರು ನಂಬುತ್ತಾರೆ. ಒಳಿತನ್ನು ಆಜ್ಞಾಪಿಸುತ್ತಾರೆ. ಕೆಡುಕಿನಿಂದ ತಡೆಯುತ್ತಾರೆ. ಒಳಿತುಗಳಲ್ಲಿ ಮುನ್ನುಗ್ಗಿ ಧಾವಿಸುತ್ತಾರೆ. ಅಂಥವರು ಸಜ್ಜನರಲ್ಲಿ ಸೇರಿದವರು.');
INSERT INTO "kannada" VALUES(3,115,'3:115','ಅವರು ಮಾಡುವ ಯಾವುದೇ ಒಳಿತನ್ನೂ ನಿಷೇಧಿಸಲಾಗುವುದಿಲ್ಲ. ಅಲ್ಲಾಹನು ಧರ್ಮನಿಷ್ಠರ ಬಗ್ಗೆ ಸರ್ವ ಜ್ಞಾನಿಯು.');
INSERT INTO "kannada" VALUES(3,116,'3:116','ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ತಮ್ಮ ಸೊತ್ತಾಗಲಿ, ಸಂತಾನವಾಗಲಿ ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆಯಿಂದ ಕಿಂಚಿತ್ತೂ ರಕ್ಷಿಸಲಾರವು. ಅಂಥವರು ನರಕದವರು. ಅವರು ಅದರಲ್ಲಿ ಶಾಶ್ವತರು.');
INSERT INTO "kannada" VALUES(3,117,'3:117','ಅವರು ಈ ಲೌಕಿಕ ಜೀವನದಲ್ಲಿ ಖರ್ಚು ಮಾಡು ವುದರ ಉಪಮೆ ಒಂದು ಗಾಳಿಯಂತೆ. ಆ ಗಾಳಿಯಲ್ಲಿ ಭೀಕರ ಶೀತವಿದೆ. ಸ್ವಯಂ ದೇಹಗಳನ್ನು ದ್ರೋಹಿಸಿಕೊಂಡ ಒಂದು ಜನಾಂಗದ ಕೃಷಿಯನ್ನು ಅದು ಬಾಧಿಸಿ ಅದನ್ನು ಸರ್ವನಾಶ ಮಾಡಿತು. ಅಲ್ಲಾಹು ಅವರನ್ನು ಅಕ್ರಮಿಸಲಿಲ್ಲ. ಆದರೆ ಅವರೇ ತಮ್ಮ ದೇಹಗಳನ್ನು ಅಕ್ರಮಿಸಿ ಕೊಂಡರು.');
INSERT INTO "kannada" VALUES(3,118,'3:118','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನಿಮ್ಮವರಲ್ಲದವರನ್ನು ನಿಮ್ಮ ರಹಸ್ಯಗಳಿಗೆ ತಾಣವನ್ನಾಗಿ ಮಾಡಬೇಡಿರಿ. ಅವರು ನಿಮಗೆ ಕೇಡು ಬಗೆಯುವುದಕ್ಕೆ ಹೇಸಲಾರರು. ನೀವು ತಾಪತ್ರಯಕ್ಕೀಡಾಗುವು ದನ್ನು ಅವರು ಇಷ್ಟಪಡುತ್ತಾರೆ. ನಿಮ್ಮ ಮೇಲಿನ ಅವರ ತೀವ್ರ ಕ್ರೋಧವು ಅವರ ಬಾಯಿಂದ ಪ್ರಕಟವಾಗಿದೆ. ಅದಕ್ಕಿಂತ ಹೆಚ್ಚಿನ ವೈರವನ್ನು ಮನಸ್ಸಲ್ಲಿ ಅಡಗಿಸಿಟ್ಟು ಕೊಂಡಿದ್ದಾರೆ. ನಿಮಗೆ ನಾವು ಮಾಹಿತಿಗಳನ್ನು ವಿವರಿಸಿ ಕೊಟ್ಟಿರುತ್ತೇವೆ. ನೀವು ಆಲೋಚಿಸುವುದಾದರೆ!');
INSERT INTO "kannada" VALUES(3,119,'3:119','ನೋಡಿ! ನೀವು ಅವರನ್ನು ಪ್ರೀತಿಸುವಿರಾದರೂ ಅವರು ನಿಮ್ಮನ್ನು ಪ್ರೀತಿಸುವುದಿಲ್ಲ. ನೀವು ಎಲ್ಲ ವೇದ ಗ್ರಂಥಗಳಲ್ಲಿ ನಂಬಿಕೆ ಇರಿಸುತ್ತೀರಿ. (ಆದರೆ ಅವರು ನಿಮ್ಮ ವೇದದಲ್ಲಿ ನಂಬಿಕೆ ಹೊಂದುವುದಿಲ್ಲ.) ಅವರು ನಿಮ್ಮನ್ನು ಕಾಣ ಸಿಕ್ಕಾಗ ‘ನಾವು ಕೂಡಾ ವಿಶ್ವಾಸವಿಟ್ಟಿದ್ದೇವೆ’ ಎನ್ನುತ್ತಾರೆ. ನಿಮ್ಮಿಂದ ಬೇರ್ಪಟ್ಟಾಗ ನಿಮ್ಮ ಮೇಲಿನ ಕಠಿಣ ಸಿಟ್ಟಿನಿಂದ ಅವರ ಬೆರಳುಗಳನ್ನು ಕಚ್ಚಿಕೊಳ್ಳುತ್ತಾರೆ. “ನಿಮ್ಮ ಕೋಪದಿಂದಲೇ ನೀವು ಸಾಯಿರಿ” ಎಂದು ನೀವು ಹೇಳಿರಿ! ಖಂಡಿತ ಅಲ್ಲಾಹನು ಮನದೊಳಗಿರುವುದನ್ನು ಬಲ್ಲವನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(3,120,'3:120','ನಿಮಗೇನಾದರೂ ಹಿತ ಉಂಟಾದರೆ ಅದು ಅವರಿಗೆ ದುಃಖವನ್ನುಂಟು ಮಾಡುತ್ತದೆ. ನಿಮಗೇ ನಾದರೂ ಕೇಡು ತಟ್ಟಿದರೆ ಅದರಿಂದ ಅವರು ಆನಂದ ಪಡುತ್ತಾರೆ. ನೀವು ಸಹನೆ ಪಾಲಿಸಿದರೆ ಮತ್ತು ಅಲ್ಲಾಹನಲ್ಲಿ ಭಯಭಕ್ತಿಯಿಂದಿದ್ದರೆ ಅವರ ಕುತಂತ್ರವು ನಿಮಗೆ ಯಾವುದೇ ಹಾನಿಯನ್ನು ಮಾಡಲಾರದು. ಅವರ ಕೃತ್ಯಗಳನ್ನೆಲ್ಲ ಅಲ್ಲಾಹು ಆವರಿಸಿ ತಿಳಿಯುತ್ತಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(3,121,'3:121','(ಪ್ರವಾದಿಯವರೇ!) ತಮ್ಮ ಮನೆಯಿಂದ ತಾವು ನಸುಕಿನಲ್ಲಿ ಹೊರಟು ಯುದ್ಧಕ್ಕಾಗಿ ಸತ್ಯವಿಶ್ವಾಸಿ ಗಳನ್ನು ಮರ್ಮಸ್ಥಾನಗಳಲ್ಲಿ ಅಣಿಗೊಳಿಸಿದ ಸಂದರ್ಭ . ಅಲ್ಲಾಹನು ಚೆನ್ನಾಗಿ ಆಲಿಸುವವನೂ ಚೆನ್ನಾಗಿ ತಿಳಿಯುವವನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(3,122,'3:122','ನಿಮ್ಮಲ್ಲಿದ್ದ ಎರಡು ಸಂಘಗಳು ಹೆದರಿ ಹಿಂಜರಿಯ ಲೆತ್ನಿಸಿದ ಸಂದರ್ಭ . ಆದರೆ ಅಲ್ಲಾಹನು ಎರಡು ಸಂಘಗಳ ರಕ್ಷಕನಾಗಿದ್ದಾನೆ. ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳು ಅಲ್ಲಾಹನಲ್ಲಿ ಭಾರವನ್ನರ್ಪಿಸಲಿ.');
INSERT INTO "kannada" VALUES(3,123,'3:123','ನೀವು ದುರ್ಬಲರಾಗಿದ್ದಾಗ ಅಲ್ಲಾಹನು ನಿಮಗೆ ಬದ್‍ರ್‍ನಲ್ಲಿ ಖಂಡಿತ ಸಹಾಯ ಮಾಡಿದನು. ಆದ್ದರಿಂದ ನೀವು ಅಲ್ಲಾಹನಲ್ಲಿ ಭಕ್ತಿಯಿರಿಸಿರಿ. ನೀವು ಕೃತಜ್ಞತೆ ತೋರಿದವರಾಗಲು.');
INSERT INTO "kannada" VALUES(3,124,'3:124','`ನಿಮ್ಮ ಪ್ರಭುವು ಮೂರು ಸಾವಿರ ಮಲಕ್‍ಗಳನ್ನು ಇಳಿಸಿ ಸಹಾಯ ಮಾಡಿದರೆ ನಿಮಗದು ಸಾಲದೇ? ಎಂದು ನೀವು (ಪ್ರವಾದಿಯವರು) ಸತ್ಯವಿಶ್ವಾಸಿಗಳಲ್ಲಿ ಹೇಳುತ್ತಿದ್ದ ಸಂದರ್ಭ.');
INSERT INTO "kannada" VALUES(3,125,'3:125','ಹೌದು. ಅದು ಸಾಕು. (ಆದರೆ ಅಷ್ಟೇ ಅಲ್ಲ;) ನೀವು ಸಹನೆ ತಾಳುವುದಾದರೆ ಮತ್ತು ಅಲ್ಲಾಹನಲ್ಲಿ ಭಕ್ತಿಯಿರಿಸುವುದಾದರೆ ಅವರು (ವೈರಿಗಳು) ಈ ಕ್ಷಣದಲ್ಲೇ ನಿಮ್ಮ ಮೇಲೆ ಆಕ್ರಮಿಸಲು ಬಂದರೂ ಯುದ್ಧದ ವಿಶೇಷ ಚಿಹ್ನೆಗಳಿರುವ ಐದು ಸಾವಿರ ಮಲಕ್‍ಗಳ ಮೂಲಕ ನಿಮ್ಮ ಪ್ರಭುವು ನಿಮಗೆ ಸಹಾಯ ಮಾಡುವನು.');
INSERT INTO "kannada" VALUES(3,126,'3:126','ಅಲ್ಲಾಹನು ಹೀಗೆ ಮಾಡಿದ್ದು ನಿಮಗೊಂದು ಶುಭವಾರ್ತೆಯಾಗಿಯೂ ಅದರಿಂದ ನಿಮ್ಮ ಮನಸ್ಸಿಗೆ ಶಾಂತಿ ಸಿಗಲೆಂದೂ ಆಗಿದೆ. (ವಾಸ್ತವದಲ್ಲಿ) ಸಹಾಯವು ಪ್ರತಾಪಿಯೂ ಪರಮ ಯುಕ್ತಿವಂತನೂ ಆದ ಅಲ್ಲಾಹನಿಂದ ಮಾತ್ರವಾಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(3,127,'3:127','ಇದು ಸತ್ಯನಿಷೇಧಿಗಳ ಒಂದು ವಿಭಾಗವನ್ನು ನಾಶಪಡಿಸಲು ಅಥವಾ ಅವರು ಸೋತು ನಿರಾಶೆಯಿಂದ ಹಿಮ್ಮೆಟ್ಟಿ ನಿಂದ್ಯಗೊಳಿಸಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(3,128,'3:128','(ಪ್ರವಾದಿಯರೇ) ಕಾರ್ಯಗಳಲ್ಲಿ ತಮಗೆ ಯಾವುದೇ ಅಧಿಕಾರವಿರುವುದಿಲ್ಲ. ಒಂದೋ ಅವರ ಪಶ್ಚಾತ್ತಾಪವನ್ನು ಅಲ್ಲಾಹು ಸ್ವೀಕರಿಸಬಹುದು ಅಥವಾ ಅವರನ್ನು ಶಿಕ್ಷಿಸಬಹುದು. ನಿಜವಾಗಿಯೂ ಅವರು ಅಕ್ರಮಿಗಳು .');
INSERT INTO "kannada" VALUES(3,129,'3:129','ಆಕಾಶಗಳು ಮತ್ತು ಭೂಮಿಯಲ್ಲಿರುವುದೆಲ್ಲವೂ ಅಲ್ಲಾಹನದ್ದು. ಆತನು ಉದ್ದೇಶಿಸಿದವರಿಗೆ ಕ್ಷಮೆ ನೀಡುವನು. ತಾನುದ್ದೇಶಿಸಿದವರಿಗೆ ಶಿಕ್ಷೆ ನೀಡುವನು. ಅಲ್ಲಾಹನು ಪರಮ ಕ್ಷಮಾಶೀಲನೂ ದಯಾಳುವೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(3,130,'3:130','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ ! ನೀವು ಇಮ್ಮಡಿ-ಗಿಮ್ಮಡಿ ಬಡ್ಡಿ ತಿನ್ನದಿರಿ. ಅಲ್ಲಾಹನಲ್ಲಿ ಭಯಭಕ್ತಿಯಿರಿಸಿಕೊಳ್ಳಿರಿ. ನೀವು ವಿಜಯ ಹೊಂದಬಹುದು.');
INSERT INTO "kannada" VALUES(3,131,'3:131','ಸತ್ಯನಿಷೇಧಿಗಳಿಗಾಗಿ ಕಾದಿರಿಸಲಾದಂತಹ ನರಕದ ಬಗ್ಗೆ ನೀವು ಎಚ್ಚರಿಕೆ ವಹಿಸಿಕೊಳ್ಳಿರಿ.');
INSERT INTO "kannada" VALUES(3,132,'3:132','ನೀವು ಅಲ್ಲಾಹು ಮತ್ತು ರಸೂಲರನ್ನು ಅನುಸರಿಸಿರಿ. ಹಾಗಾದರೆ ನೀವು ಅನುಗ್ರಹೀತ ರಾಗುವಿರಿ.');
INSERT INTO "kannada" VALUES(3,133,'3:133','ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕ್ಷಮಾದಾನ ಹಾಗೂ ಭೂಮಿ ಮತ್ತು ಆಕಾಶಗಳಷ್ಟು ವಿಶಾಲವಾದ ಸ್ವರ್ಗದ ಕಡೆಗೆ ಧಾವಿಸಿ ಮುನ್ನುಗ್ಗಿರಿ. ಸ್ವರ್ಗವನ್ನು ಧರ್ಮನಿಷ್ಟ ರಿಗಾಗಿ ಕಾದಿರಿಸಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(3,134,'3:134','ಅವರು ಸಂತೋಷದ ಸಮಯದಲ್ಲೂ ಕಷ್ಟದ ಸಮಯದಲ್ಲೂ ದಾನ-ಧರ್ಮ ಮಾಡುವವರು. ಕೋಪವನ್ನು ನುಂಗುವವರು ಮತ್ತು ಜನರಿಗೆ ಕ್ಷಮೆ ತೋರುವವರು ಆಗಿರುತ್ತಾರೆ. ಸತ್ಕರ್ಮಿಗಳನ್ನು ಅಲ್ಲಾಹು ಇಷ್ಟಪಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(3,135,'3:135','ಅವರು ಅಶ್ಲೀಲ ಕೃತ್ಯವನ್ನು ಮಾಡಿದರೆ ಅಥವಾ (ಪಾಪಗಳ ಮೂಲಕ) ತಮಗೆ ತಾವೇ ದ್ರೋಹ ಮಾಡಿಕೊಂಡರೆ ಅಲ್ಲಾಹನನ್ನು ನೆನೆಸುತ್ತಾರೆ, ಕೂಡಲೇ ತಮ್ಮ ಪಾಪಗಳಿಗೆ ಕ್ಷಮೆ ಬೇಡುತ್ತಾರೆ. ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸುವವನು ಅಲ್ಲಾಹನಲ್ಲದೆ ಬೇರೆ ಯಾರಿದ್ದಾರೆ ? ಅವರು ತಿಳಿದೂ ತಮ್ಮ ಪಾಪ ಕೃತ್ಯಗಳ ಮೇಲೆ ಸ್ಥಿರವಾಗಿರುವವರಲ್ಲ .');
INSERT INTO "kannada" VALUES(3,136,'3:136','ಅವರ ಪ್ರತಿಫಲವಾದರೋ ಅವರ ಪ್ರಭುವಿನಿಂದ ಕ್ಷಮಾದಾನವೂ ತಳಭಾಗದಲ್ಲಿ ನದಿಗಳು ಹರಿಯುವ ಸ್ವರ್ಗಗಳೂ ಆಗಿವೆ. ಅದರಲ್ಲಿ ಅವರು ಶಾಶ್ವತರು. ಆ ಸತ್ಕರ್ಮಿಗಳ ಪ್ರತಿಫಲ ಅದೆಷ್ಟು ಚೆನ್ನಾಗಿದೆ!');
INSERT INTO "kannada" VALUES(3,137,'3:137','ನಿಮಗೂ ಹಿಂದೆ ಅನೇಕ (ದೈವಿಕ) ಕಾರ್ಯಾಚರಣೆಗಳು ನಡೆದಿವೆ. ಆದ್ದರಿಂದ ನೀವು ಭೂಮಿಯಲ್ಲಿ ಸಂಚರಿಸಿರಿ ಹಾಗೂ ಸತ್ಯವನ್ನು ಸುಳ್ಳಾಗಿಸಿದವರ ಪರ್ಯವಸಾನ ಏನಾಯಿತೆನ್ನುವುದನ್ನು ನೋಡಿರಿ.');
INSERT INTO "kannada" VALUES(3,138,'3:138','ಇದು (ಖುರ್‍ಆನ್) ಎಲ್ಲ ಜನರಿಗೆ ಇರುವ ವಿವರಣೆಯೂ ಸೂಕ್ಷ್ಮಮತಿಗಳಿಗೆ ಮಾರ್ಗದರ್ಶನ ಹಾಗೂ ಸದುಪದೇಶವಾಗಿದೆ .');
INSERT INTO "kannada" VALUES(3,139,'3:139','ನೀವು ದುರ್ಬಲರೋ ದುಃಖಿತರೋ ಆಗದಿರಿ. ನೀವು ನಿಜವಾದ ವಿಶ್ವಾಸಿಗಳಾಗಿದ್ದರೆ ನೀವೇ ಉನ್ನತರು.');
INSERT INTO "kannada" VALUES(3,140,'3:140','ಇಂದು ನಿಮಗೊಂದು ಗಾಯ ತಟ್ಟಿದ್ದರೆ ಅದೇ ತರದ ಗಾಯ ಅವರಿಗೂ ಖಂಡಿತ ತಟ್ಟಿತ್ತು. ಆ (ಜಯಾಪಜಯಗಳ) ದಿವಸಗಳನ್ನು ನಾವು ಜನರೆಡೆಯಲ್ಲಿ ಬದಲಿಸುತ್ತಿರುತ್ತೇವೆ . ಇದು (ಕಪಟಿಗಳಿಂದ) ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನು ಅಲ್ಲಾಹನು ಬೇರ್ಪಡಿಸಿ ಗುರುತಿಸಲು ಹಾಗೂ ನಿಮ್ಮಲ್ಲಿ ಕೆಲವರನ್ನು ಹುತಾತ್ಮರನ್ನಾಗಿ ಮಾಡಲಿಕ್ಕೂ ಆಗಿದೆ. ಅಲ್ಲಾಹನು ಅಕ್ರಮಿಗಳನ್ನು ಇಷ್ಟಪಡುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(3,141,'3:141','ಮತ್ತು ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನು ಅಲ್ಲಾಹನು ಶುದ್ಧೀಕರಿಸಲಿಕ್ಕೂ ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ನಾಶಪಡಿಸಲಿಕ್ಕೂ ಆಗಿದೆ.');
INSERT INTO "kannada" VALUES(3,142,'3:142','ಅದಲ್ಲ; ನಿಮ್ಮ ಪೈಕಿ ಯುದ್ಧ ಮಾಡಿದವರು ಹಾಗೂ (ರಣಾಂಗಣದಲ್ಲಿ) ಸಹನೆ ಪಾಲಿಸಿದವರನ್ನು ಅಲ್ಲಾಹನು ಪರೀಕ್ಷಿಸದೆ ಸ್ವರ್ಗಕ್ಕೆ ನೀವು ಹೋಗುವಿರೆಂದು ಭಾವಿಸಿದ್ದೀರಾ?');
INSERT INTO "kannada" VALUES(3,143,'3:143','ನೀವು ಮರಣವನ್ನು ಎದುರುಗೊಳ್ಳುವ ಮುನ್ನ (ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ) ಮರಣವನ್ನು ಬಯಸುವವರಾಗಿದ್ದಿರಿ. ಇದೀಗ ನೀವು ನೋಡುತ್ತಿರುವಂತೆಯೇ ಅದನ್ನು ನೀವು ಕಂಡಿರಿ. (ಆದರೂ ನೀವು ಯಾಕೆ ಓಡಿ ಹೋದಿರಿ ?) ');
INSERT INTO "kannada" VALUES(3,144,'3:144','ಮುಹಮ್ಮದರು ಅಲ್ಲಾಹನ ಓರ್ವ ದೂತರು ಮಾತ್ರ. ಅವರಿಗೆ ಮೊದಲೂ ಕೆಲವು ದೂತರು ಆಗಿ ಹೋಗಿದ್ದಾರೆ. ಆದರೆ ಅವರು ಮೃತರಾದರೆ ಅಥವಾ ಹತರಾದರೆ ನೀವು ಹಿಂಜರಿದು ಓಡುವಿರೇನು? ಯಾರಾದರೂ ಹಿಂಜರಿದು ಓಡುವುದಾದರೆ ಅದರಿಂದ ಅಲ್ಲಾಹನಿಗೆ ಯಾವ ದೋಷವೂ ಇರುವುದಿಲ್ಲ. ಕೃತಜ್ಞತೆಯುಳ್ಳವರಿಗೆ ಅಲ್ಲಾಹು ತಕ್ಕ ಪ್ರತಿಫಲವನ್ನು ಕೊಡಲಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(3,145,'3:145','ಅಲ್ಲಾಹನ ವಿಧಿಯ ಹೊರತು ಯಾವನಿಗೂ ಮರಣ ಸಂಭವಿಸದು. ಅದು ನಿರ್ಧರಿತ ಅವಧಿಯಾಗಿದೆ. ಯಾವನು ಇಹದ ಫಲವನ್ನು ಬಯಸುತ್ತಾನೋ ಅವನಿಗೆ ಅದನ್ನು ನಾವು ಕೊಡುತ್ತೇವೆ. ಯಾವನು ಪರದ ಫಲವನ್ನು ಬಯಸುತ್ತಾನೋ ಅವನಿಗೆ ನಾವು ಅದರಿಂದ ಕೊಡುತ್ತೇವೆ. ಕೃತಜ್ಞತೆ ಯುಳ್ಳವರಿಗೆ ತಕ್ಕುದಾದ ಫಲವನ್ನು ನಾವು ಕೊಡಲಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(3,146,'3:146','ಎಷ್ಟೆಷ್ಟು ಪ್ರವಾದಿಗಳ ಜೊತೆಯಲ್ಲಿ ಅನೇಕ ಕೂಟಗಳು ಯುದ್ಧ ಮಾಡಿವೆ! ಹಾಗೆಯೇ ಅವರಿಗೆ ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಬಾಧಿಸಿದ ಕಷ್ಟಗಳಿಂದ ಅವರೇನೂ ಅಂಜಿರಲಿಲ್ಲ. ದುರ್ಬಲರೂ ಆಗಿರಲಿಲ್ಲ. (ವೈರಿಗಳಿಗೆ) ಶರಣಾಗಲಿಲ್ಲ. ಅಲ್ಲಾಹನು ಸಹನಶೀಲರನ್ನು ಪ್ರೀತಿಸುತ್ತಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(3,147,'3:147','`ನಮ್ಮ ಪ್ರಭೂ ! ನಮ್ಮ ಪಾಪಗಳನ್ನೂ ನಮ್ಮ ಕಾರ್ಯಗಳಲ್ಲಿ ಬಂದ ಅತಿರೇಕಗಳನ್ನೂ ಕ್ಷಮಿಸು ! (ಯುದ್ಧದಲ್ಲಿ) ನಮ್ಮ ಪಾದಗಳನ್ನು ನೆಲೆಯೂರಿಸು. ಅವಿಶ್ವಾಸಿಗಳ ವಿರುದ್ಧ ನಮಗೆ ಸಹಾಯ ಮಾಡು'' ಎಂಬ ಮಾತಲ್ಲದೆ ಬೇರೆ ಮಾತು ಅವರಿಂದ ಬಂದಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(3,148,'3:148','ಆದ್ದರಿಂದ ಅವರಿಗೆ ಐಹಿಕ ಪ್ರತಿಫಲವನ್ನೂ ಶ್ರೇಷ್ಠವಾದ ಪಾರತ್ರಿಕ ಫಲವನ್ನೂ ಅಲ್ಲಾಹನು ಕೊಟ್ಟನು. ಅಲ್ಲಾಹನು ಒಳಿತು ಮಾಡುವವರನ್ನು ಪ್ರೀತಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(3,149,'3:149','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನೀವು ಅವಿಶ್ವಾಸಿಗಳಿಗೆ ಅನುಸರಿಸಿದರೆ ಅವರು ನಿಮ್ಮನ್ನು ಪೂರ್ವ ಸ್ಥಿತಿಗೆ ಮರಳಿಸುವರು. ಆಗ ನೀವು ನಷ್ಟ ಹೊಂದಿದವರಾಗಿ ಹಿಂತಿರುಗುವಿರಿ.');
INSERT INTO "kannada" VALUES(3,150,'3:150','ಆದರೆ ಅಲ್ಲಾಹನು ನಿಮ್ಮ ರಕ್ಷಾಧಿಪತಿಯಾಗಿರು ವನು. ಅವನು ಸಹಾಯಕರಲ್ಲಿ ಉತ್ತಮ ನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(3,151,'3:151','ಸತ್ಯನಿಷೇಧಿಗಳ ಹೃದಯದಲ್ಲಿ ಸದ್ಯದಲ್ಲೇ ನಾವು ಭೀತಿಯನ್ನು ಹುಟ್ಟಿಸುವೆವು. ಅಲ್ಲಾಹು ತನ್ನ (ಸಹಭಾಗಿಗಳೆಂದು) ಯಾವುದೇ ಆಧಾರವನ್ನು ಅವತೀರ್ಣಗೊಳಿಸಿರದಂತಹ ವಸ್ತುಗಳನ್ನು ಅವರು ಸಹಭಾಗಿಗಳಾಗಿ ಮಾಡಿದುದರ ಫಲವಿದು. ಅವರ ವಾಸಸ್ಥಳ ನರಕವಾಗಿದೆ. ಅಕ್ರಮಿಗಳ ನಿವಾಸ ಅದೆಷ್ಟು ನೀಚ!');
INSERT INTO "kannada" VALUES(3,152,'3:152','ಅಲ್ಲಾಹನ ಅನುಮತಿ ಪ್ರಕಾರ ಅವರೊಂದಿಗೆ ನೀವು ಹೋರಾಡುತ್ತಿರುವಾಗ ಆತನು ತನ್ನ ಸಹಾಯದ ವಾಗ್ದಾನವನ್ನು ಚೆನ್ನಾಗಿ ಪಾಲಿಸಿದನು. ಹಾಗಿದ್ದೂ ನಿಮಗೆ ಇಷ್ಟವಾದುದನ್ನು ಅಲ್ಲಾಹನು ನಿಮಗೆ ತೋರಿಸಿಕೊಟ್ಟ ನಂತರವೂ ನೀವು ಭೀರುತ್ವ ತೋರಿದಿರಿ. (ಪ್ರವಾದಿಯವರ) ಆದೇಶದ ವಿಷಯದಲ್ಲಿ ಭಿನ್ನರಾದಿರಿ ಹಾಗೂ ಆದೇಶಕ್ಕೆ ವಿರೋಧವಾಗಿ ವರ್ತಿಸಿದಿರಿ. (ಆದ್ದರಿಂದ ಅವನು ತನ್ನ ಸಹಾಯವನ್ನು ಹಿಂತೆಗೆದುಕೊಂಡನು.) ನಿಮ್ಮ ಕೂಟದಲ್ಲಿ ಇಹಲೋಕವನ್ನು ಬಯಸುವವರೂ ಪರಲೋಕವನ್ನು ಬಯಸುವವರೂ ಇದ್ದಾರೆ. ತರುವಾಯ ನಿಮ್ಮನ್ನು ಪರೀಕ್ಷಿ ಸಲೋಸುಗ ನಿಮ್ಮನ್ನು ಅವನು ಶತ್ರುಗಳಿಂದ ಹಿಂಜರಿಸಿದನು. ಖಂಡಿತ ವಾಗಿಯೂ ಅಲ್ಲಾಹನು ನಿಮ್ಮನ್ನು ಕ್ಷಮಿಸಿದ್ದಾನೆ. ಅಲ್ಲಾಹನು ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳ ಮೇಲೆ ಬಹಳ ಔದಾರ್ಯವುಳ್ಳವನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(3,153,'3:153','ಯಾರತ್ತಲೂ ತಿರುಗಿ ನೋಡದೆ ನೀವು ಓಡಿ ಹೋದ ಸಂದರ್ಭ(ವನ್ನು ಸ್ಮರಿಸಿರಿ). ಅಲ್ಲಾಹನ ದೂತರು ನಿಮ್ಮನ್ನು ಹಿಂದಿನಿಂದ ಕರೆಯುತ್ತಿದ್ದರು. ಆದ್ದರಿಂದ ಮೇಲಿಂದ ಮೇಲೆ ದುಃಖವನ್ನು ನಿಮಗವನು ಪ್ರತಿಫಲವಾಗಿ ಕೊಟ್ಟನು . (ಆದರೆ) ವಿಪತ್ತು ಸಂಭವಿಸಿದುದಕ್ಕೂ ವಸ್ತುಗಳು ನಷ್ಟವಾದುದಕ್ಕೂ ನೀವು ದುಃಖಿತರಾಗದಿರಲೆಂದು (ಅವನು ನಿಮಗೆ ಕ್ಷಮಿಸಿದನು). ಅಲ್ಲಾಹನು ನೀವು ಮಾಡುತ್ತಿದ್ದುದನ್ನು ಸೂಕ್ಷ್ಮವಾಗಿ ಅರಿಯುವವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(3,154,'3:154','ದುಃಖದ ನಂತರ ನಿಮ್ಮ ಮೇಲೆ ಅವನೊಂದು ಶಾಂತಿಯನ್ನು ಅಂದರೆ ಒಂದು ನಿದ್ದೆಯ ಮಂಪರನ್ನು ಇಳಿಸಿದನು. ಅದು ನಿಮ್ಮ ಪೈಕಿ ಒಂದು ವಿಭಾಗದವರನ್ನು ಆವರಿಸಿತ್ತು. ಆದರೆ ಇನ್ನೊಂದು ವಿಭಾಗದವರ ಸ್ವಂತ ಆತ್ಮಗಳು ಅವರನ್ನು ಚಿಂತೆಗೀಡುಮಾಡಿದ್ದುವು. ಅಲ್ಲಾಹನ ಬಗ್ಗೆ ಅಸತ್ಯವಾದ ಅಜ್ಞಾನಜನ್ಯ ಯೋಚನೆ ಗಳನ್ನು ಅವರು ಮಾಡಿಬಿಟ್ಟರು. ನಮಗೆ ನೆರವಿನ ಕಾರ್ಯದಲ್ಲಿ ಏನಾದರೂ ಇದೆಯೇ ಎಂದು ಅವರು ಕೇಳುತ್ತಿದ್ದರು. ತಾವು ಹೇಳಿರಿ; `ಖಂಡಿತವಾಗಿಯೂ ಕಾರ್ಯಗಳೆಲ್ಲವೂ ಅಲ್ಲಾಹನಿಗೇ ಇವೆ. “ತಮಗೆ ಎದುರಿಗೆ ತೋರಿಸದಂತಹ ಕೆಲವು ವಿಷಯಗಳನ್ನು ಅವರು ಒಳಗೆ ಅವಿತಿರಿ ಸಿಕೊಂಡಿರುತ್ತಾರೆ. ‘ನಮಗೆ ಸ್ವಾತಂತ್ರ್ಯವಿದ್ದಿದ್ದರೆ ಇಲ್ಲಿ ನಾವು ವಧಿಸಲ್ಪಡುತ್ತಿರಲಿಲ್ಲ’ ಎಂದವರು ಹೇಳುತ್ತಿದ್ದರು. ತಾವು, ಹೇಳಿರಿ; `ನೀವು ನಿಮ್ಮ ಮನೆಗಳಲ್ಲಿ ಕೂತಿದ್ದರೂ ಯಾರ ಮೇಲೆ ಸಾವಿನ ವಿಧಿ ಬಂದಿದೆಯೋ ಅವರು ಸಾಯುವ ಜಾಗಕ್ಕೆ ತಲುಪಿಯೇ ತೀರುತ್ತಿದ್ದರು. ಇದು ಅಲ್ಲಾಹನು ನಿಮ್ಮ ಹೃದಯಗಳಲ್ಲಿರುವುದನ್ನು ಪರೀಕ್ಷಿಸಲು ಮತ್ತು ನಿಮ್ಮ ಹೃದಯದ ಮಾಲಿನ್ಯವನ್ನು ಶುದ್ದೀಕರಿಸಲು ಆಗಿದೆ. ಹೃದಯಗಳಲ್ಲಿರುವುದನ್ನು ಅಲ್ಲಾಹನು ಚೆನ್ನಾಗಿ ಬಲ್ಲವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(3,155,'3:155','ಎರಡು ಬಣಗಳು ಎದುರುಬದುರಾದ ಆ ದಿನದಂದು ನಿಮ್ಮ ಕೂಟದಿಂದ ಹಿಂಜರಿದು ಓಡಿದವರು, ಅವರು ಮಾಡಿದ ಕೆಲವು ದೋಷ ಕೃತ್ಯಗಳ ಕಾರಣದಿಂದ ನಿಜವಾಗಿಯೂ ಶೈತಾನನು ಅವರನ್ನು ದಾರಿ ತಪ್ಪಿಸಿದನು . ಆದರೆ ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಅವರಿಗೆ ಮಾಫಿ ಕೊಟ್ಟಿರುವನು. ಅಲ್ಲಾಹನು ನಿಜವಾಗಿಯೂ ಬಹಳ ಕ್ಷಮಾದಾನಿಯೂ ಬಹಳ ಹೆಚ್ಚು ಸಹನೆಯುಳ್ಳವನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(3,156,'3:156','ಓ ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳೇ! ನೀವು ಅವಿಶ್ವಾಸಿಗಳಂತೆ ಆಗಬಾರದು. ಅವರು ತಮ್ಮ ಸೋದರರ ಬಗ್ಗೆ ಅವರು ಭೂಮಿಯಲ್ಲಿ ಯಾತ್ರೆ ಹೋದಾಗ ಅಥವಾ ಯುದ್ದಕ್ಕೆ ಹೋಗಿದ್ದಾಗ (ಮರಣಗೊಂಡಿದ್ದರೆ) `ಅವರು ತಮ್ಮ ಬಳಿ ಇರುತ್ತಿದ್ದರೆ ಸಾಯುತ್ತಿರಲಿಲ್ಲ ಅಥವಾ ಕೊಲ್ಲಲ್ಪಡುತ್ತಿರಲಿಲ್ಲ'' ಎಂದು ಹೇಳಿದರು. ಇದು ಅವರ ಹೃದಯದಲ್ಲಿ ಅಲ್ಲಾಹನು ಅದನ್ನೊಂದು ಖೇದವನ್ನಾಗಿ ಉಳಿಸಲಿಕ್ಕಾಗಿದೆ. ಅಲ್ಲಾಹನು ಜೀವಕೊಡುತ್ತಾನೆ ಮತ್ತು ಮರಣಗೊಳಿಸುತ್ತಾನೆ. ನೀವು ಮಾಡುತ್ತಿರುವುದರ ಬಗ್ಗೆ ಅಲ್ಲಾಹು ಸೂಕ್ಷ್ಮದರ್ಶಕನು.');
INSERT INTO "kannada" VALUES(3,157,'3:157','ನೀವು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಕೊಲ್ಲಲ್ಪಟ್ಟರೂ ಅಥವಾ (ಬೇರೆ ವಿಧದಲ್ಲಿ) ನೀವು ತೀರಿದರೂ, ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನಿಂದ ದೊರೆಯುವ ಕ್ಷಮಾದಾನ ಮತ್ತು ಅನುಗ್ರಹವು ಅವರು ಶೇಖರಿಸುವುದಕ್ಕಿಂತ ಶ್ರೇಷ್ಠವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(3,158,'3:158','ನೀವು (ಸಹಜವಾಗಿ) ಮರಣ ಹೊಂದಿದರೂ ಅಥವಾ ಕೊಲ್ಲಲ್ಪಟ್ಟರೂ ನೀವು ಒಂದುಗೂಡಿಸ ಲ್ಪಡುವುದು ಅಲ್ಲಾಹನೆಡೆಗೇ ಆಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(3,159,'3:159','(ಪ್ರವಾದಿಯವರೇ!) ನೀವು ಅವರ ಜೊತೆ ಮೃದುವಾಗಿ ವರ್ತಿಸಿದ್ದು ಅಲ್ಲಾಹನ ಕಾರುಣ್ಯದಿಂದ ಮಾತ್ರವಾಗಿದೆ. ನೀವು ಒರಟರೂ, ಕಠಿಣ ಹೃದಯಿಯೂ ಆಗಿದ್ದರೆ ನಿಮ್ಮ ಸುತ್ತಲಿಂದ ಅವರು ಚದುರಿ ಹೋಗುತ್ತಿದ್ದರು. ಆದ್ದರಿಂದ ಅವರಿಗೆ ಮಾಫಿಕೊಡಿರಿ. ಅವರ ಪರವಾಗಿ ಪಾಪ ಮುಕ್ತಿ ಬೇಡಿರಿ. ಅವರ ಜೊತೆಗೆ (ಭಾವೀ) ಕಾರ್ಯಗಳಲ್ಲಿ ಸಮಾಲೋಚನೆ ನಡೆಸಿರಿ. ನೀವು ನಿರ್ಧಾರ ಮಾಡಿದ ಮೇಲೆ ಅಲ್ಲಾಹನ ಮೇಲೆ ಭಾರವರ್ಪಿಸಿರಿ. ಅಲ್ಲಾಹನು, ತನ್ನಲ್ಲಿ ಭಾರವರ್ಪಿಸುವವರನ್ನು ಖಂಡಿತ ಪ್ರೀತಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(3,160,'3:160','ಅಲ್ಲಾಹನು ನಿಮಗೆ ಸಹಾಯ ಮಾಡಿದರೆ ಮತ್ತೆ ನಿಮ್ಮ ಮೇಲೆ ಜಯ ಸಾಧಿಸುವವನಿಲ್ಲ. ಆತನು ನಿಮ್ಮನ್ನು ಕೈ ಬಿಟ್ಟರೆ ಆತನಲ್ಲದೆ ನಿಮಗೆ ಸಹಾಯಕರು ಯಾರಿದ್ದಾರೆ ? ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಅಲ್ಲಾಹನಲ್ಲಿ ಭಾರವನ್ನರ್ಪಿಸಿಕೊಳ್ಳಲಿ.');
INSERT INTO "kannada" VALUES(3,161,'3:161','ಯಾವುದೇ ಒಬ್ಬ ಪ್ರವಾದಿ (ಯುದ್ಧಾಸ್ತಿಯಲ್ಲಿ) ವಂಚಿಸುವರೆನ್ನುವುದು ಸಂಭವನೀಯವಲ್ಲ. ಯಾವನಾದರೂ ವಂಚಿಸಿ ತೆಗೆದುಕೊಂಡರೆ ಅವನು ವಂಚಿಸಿ ತೆಗೆದುಕೊಂಡದ್ದನ್ನು ಪುನರುತ್ಥಾನ ದಿನದಂದು ತರುವನು. ಆಮೇಲೆ ಪ್ರತಿಯೊಬ್ಬರಿಗೂ ಅವರವರು ಮಾಡಿದುದರ ಫಲವನ್ನು ಪೂರ್ತಿಯಾಗಿ ಕೊಡಲಾಗುವುದು. ಅವರಿಗೆ ಅನ್ಯಾಯ ಮಾಡಲಾಗುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(3,162,'3:162','ಅಲ್ಲಾಹನ ಸಂತೃಪ್ತಿಯನ್ನು ಹಿಂಬಾಲಿಸಿದವನು ಅಲ್ಲಾಹನ ಕ್ರೋಧ ಪಡೆದು ವಾಪಾಸಾದವನಂತೆ ಆಗುವನೆ ? ಅವನ ನಿವಾಸವು ನರಕವಾಗಿರುವುದು. ಅದೆಷ್ಟು ನಿಕೃಷ್ಟ ವಾಸಸ್ಥಾನ !');
INSERT INTO "kannada" VALUES(3,163,'3:163','ಅವರು (ಈ ಎರಡು ತರದವರು) ಅಲ್ಲಾಹನ ಬಳಿ ವಿಭಿನ್ನ ದರ್ಜೆಗಳಲ್ಲಿರುವವರು. ಅವರು ಮಾಡುತ್ತಿರುವುದನ್ನು ಅಲ್ಲಾಹನು ಚೆನ್ನಾಗಿ ಕಾಣುವವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(3,164,'3:164','ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಅವರಿಂದಲೇ ಒಬ್ಬ ದೂತರನ್ನು ನಿಯೋಗಿಸುವ ಮೂಲಕ ಅಲ್ಲಾಹನು ಖಂಡಿತವಾಗಿಯೂ ಬಲು ದೊಡ್ಡ ಉಪಕಾರವನ್ನು ಮಾಡಿದ್ದಾನೆ. ಆ ದೂತರು ಅವರಿಗೆ ಆತನ ವಚನಗಳನ್ನು ಓದಿ ಹೇಳುತ್ತಾರಲ್ಲದೆ ಅವರನ್ನು ಸಂಸ್ಕರಿಸುತ್ತಾರೆ. ಅವರಿಗೆ ಗ್ರಂಥವನ್ನೂ ತತ್ವಜ್ಞಾನವನ್ನೂ ಕಲಿಸುತ್ತಾರೆ. ಇದಕ್ಕೆ (ದೂತರ ಆಗಮನಕ್ಕೆ) ಮುನ್ನ ಅವರು ನಿಜವಾಗಿಯೂ ಸ್ಪಷ್ಟ ದಾರಿಗೇಡಿ ನಲ್ಲಿದ್ದರು.');
INSERT INTO "kannada" VALUES(3,165,'3:165','ನಿಮಗೆ (ಉಹ್ದ್‍ನಲ್ಲಿ) ಒಂದು ವಿಪತ್ತು ಸಂಭವಿ ಸಿದ್ದರೂ ನೀವು ಅದರ ಇಮ್ಮಡಿಯಾಗಿ ಅವರಿಗೆ (ಬದ್ರ್‍ನಲ್ಲಿ) ಕೊಟ್ಟಿದ್ದೀರಿ. (ಹಾಗಿದ್ದೂ) ಇದು ಹೇಗೆ ಸಂಭವಿಸಿತೆಂದು ಕೇಳುತ್ತಿದ್ದೀರಾ? ಹೇಳಿರಿ; ಇದು ನಿಮ್ಮಿಂದಲೇ ಬಂದಿದೆ. ಅಲ್ಲಾಹನು ಸರ್ವ ಸಮರ್ಥ ನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(3,166,'3:166','ಎರಡು ತಂಡಗಳು ಎದುರುಗೊಂಡ ದಿನದಂದು (ಉಹ್ದ್‍ನಲ್ಲಿ) ನಿಮಗೆ ಸಂಭವಿಸಿದ್ದು ಅಲ್ಲಾಹನ ವಿಧಿ ಪ್ರಕಾರವಾಗಿದ್ದು ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನು (ಬೇರ್ಪಡಿಸಿ) ತಿಳಿಯಲಿಕ್ಕಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(3,167,'3:167','ಮತ್ತು ಕಪಟಿಗಳನ್ನು ಅವನು (ವಿಂಗಡಿಸಿ) ಅರಿಯಲೂ ಆಗಿದೆ. ‘ಬನ್ನಿ! ಅಲ್ಲಾಹನ ಮಾರ್ಗ ದಲ್ಲಿ ಯುದ್ದ ಮಾಡಿರಿ. ಇಲ್ಲವೆ ಪ್ರತಿರೋಧಿಸಿರಿ'' ಎಂದು ಅವರಲ್ಲಿ ಆದೇಶಿಸಿದಾಗ, ಯುದ್ಧವನ್ನು ನಾವು ಬಲ್ಲವರಾಗಿದ್ದರೆ ನಾವು ನಿಮ್ಮ ಹಿಂದೆ ಬರುತ್ತಿದ್ದೆವು ಎಂದವರು ಹೇಳಿದರು. ಅಂದು ಅವರು ಸತ್ಯವಿಶ್ವಾಸಕ್ಕಿಂತ ಹೆಚ್ಚಾಗಿ ಅವಿಶ್ವಾಸಕ್ಕೆ ನಿಕಟವಾಗಿದ್ದರು. ಅವರ ಮನಸ್ಸಲ್ಲಿ ಇಲ್ಲದಿದ್ದು ದನ್ನು ಅವರ ಬಾಯಿಂದ ಹೇಳುತ್ತಿದ್ದರು. ಅವರು ಮರೆಮಾಚುತ್ತಿರುವುದನ್ನು ಅಲ್ಲಾಹನು ಚೆನ್ನಾಗಿ ಅರಿಯುವವ ನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(3,168,'3:168','ಅವರು (ಕಪಟಿಗಳು) ಕುಳಿತು ಬಿಟ್ಟು (ಯುದ್ಧಕ್ಕೆ ಹೋದ) ತಮ್ಮ ಸಹೋದರರ ಬಗ್ಗೆ; `ನಮ್ಮ ಮಾತನ್ನು ಅವರು ಕೇಳಿದ್ದರೆ ಕೊಲೆಯಾಗುತ್ತಿರಲಿಲ್ಲ’ ಎಂದು ಹೇಳಿದವರು. ನೀವು ಹೇಳಿರಿ; `ನೀವು ಸತ್ಯವಾದಿಗಳಾಗಿದ್ದರೆ ನಿಮ್ಮ ಸ್ವಂತ ದೇಹಗ');
INSERT INTO "kannada" VALUES(3,169,'3:169','ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ವಧಿಸಲ್ಪಟ್ಟವರನ್ನು ಮರಣ ಹೊಂದಿದವರೆಂದು ನೀವು ಖಂಡಿತ ಭಾವಿಸಬಾರದು. ಅವರು ತಮ್ಮ ಪ್ರಭುವಿನ ಬಳಿ ಜೀವಂತವಾಗಿದ್ದಾರೆ. ಅವರಿಗೆ ಆಹಾರ ಕೊಡಲಾಗುತ್ತಿದೆ .');
INSERT INTO "kannada" VALUES(3,170,'3:170','ಅಲ್ಲಾಹನು ತನ್ನ ಔದಾರ್ಯದಿಂದ ಅವರಿಗೆ ಕೊಟ್ಟಿರುವುದರಲ್ಲಿ ಅವರು ಸಂತುಷ್ಟರಾಗಿರುವರು. ತಮ್ಮ ಹಿಂದಿನಿಂದ ಅವರತ್ತ ತಲುಪದಿರುವವರ ಬಗೆಗೆ ಅವರಿಗೇನೂ ಭಯಪಡಲಿಕ್ಕಾಗಲಿ, ದುಃಖಿಸಲಿಕ್ಕಾಗಲಿ ಇರುವುದಿಲ್ಲವೆಂದು ಅವರು ಸಂತೋಷಪಡುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(3,171,'3:171','ಅಲ್ಲಾಹನ ಕಡೆಯ ಅನುಗ್ರಹ ಮತ್ತು ಔದಾರ್ಯ ದಲ್ಲಿಯೂ ಅಲ್ಲಾಹನು ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಪ್ರತಿಫಲವನ್ನು ವ್ಯರ್ಥಗೊಳಿಸಲಾರನು ಎನ್ನುವುದರಲ್ಲಿಯೂ ಅವರು ಆಹ್ಲಾದಗೊಳ್ಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(3,172,'3:172','ಇನ್ನು (ಉಹ್ದ್‍ನಲ್ಲಿ) ಗಾಯಗೊಂಡ ಬಳಿಕವೂ ಅಲ್ಲಾಹು ಮತ್ತು ರಸೂಲರ ಕರೆಗೆ ಓಗೊಟ್ಟವರು . ಅವರ ಪೈಕಿ ಸತ್ಕರ್ಮಿಗಳು ಹಾಗೂ ದೋಷ ಬಾಧೆಯ ಬಗ್ಗೆ ಎಚ್ಚರಿಕೆ ವಹಿಸಿದವರಿಗೆ ಮಹತ್ತಾದ ಪ್ರತಿಫಲವಿದೆ.');
INSERT INTO "kannada" VALUES(3,173,'3:173','(ಮಕ್ಕಾದ ಶತ್ರುಗಳಾದ) `ಜನರು ನಿಮ್ಮ ವಿರುದ್ಧ ಒಗ್ಗಟ್ಟಾಗಿದ್ದಾರೆ. ಆದ್ದರಿಂದ ನೀವು ಅವರಿಗೆ ಭಯಪಡಿರಿ'' ಎಂದು ಜನರು ಅವರಿಗೆ ಹೇಳಿದರು. ಅದು ಅವರ ವಿಶ್ವಾಸವನ್ನು ಮತ್ತಷ್ಟು ಹೆಚ್ಚಿಸಿತು. ‘ನಮಗೆ ಅಲ್ಲಾಹು ಸಾಕು, ಭರವಸೆ ಇಡಲು ಅವನು ಎಷ್ಟೊಂದು ಉತ್ತಮನು!’ ಎಂದು ಅವರು ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(3,174,'3:174','ಹಾಗೆ ಅವರು ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ಅನುಗ್ರಹ ಮತ್ತು ಔದಾರ್ಯದೊಂದಿಗೆ ಮರಳಿದರು. ಅವರಿಗೆ ಯಾವ ಹಾನಿಯೂ ತಟ್ಟಲಿಲ್ಲ. ಅವರು ಅಲ್ಲಾಹನ ಸಂತೃಪ್ತಿಯನ್ನು ಅನುಸರಿಸಿದರು. ಅಲ್ಲಾಹನು ಭಾರೀ ಔದಾರ್ಯವುಳ್ಳವನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(3,175,'3:175','ಖಂಡಿತವಾಗಿಯೂ ಶೈತಾನನು, ತನ್ನ ಮಿತ್ರರ ಬಗ್ಗೆ ಭೀತಿ ಹುಟ್ಟಿಸುತ್ತಾನೆ. ಆದ್ದರಿಂದ ನೀವು ಅವರಿಗೆ ಹೆದರಬೇಡಿರಿ. ನೀವು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಾ ಗಿದ್ದರೆ ನನಗೆ ಭಯಪಡಿರಿ.');
INSERT INTO "kannada" VALUES(3,176,'3:176','ಅವಿಶ್ವಾಸಕ್ಕೆ ಧಾವಿಸಿ ಹೋಗುತ್ತಿರುವವರು ನಿಮಗೆ ದುಃಖವನ್ನುಂಟುಮಾಡದಿರಲಿ. ಅವರು ಅಲ್ಲಾಹನಿಗೆ ಯಾವ ಹಾನಿಯನ್ನೂ ಮಾಡಲಾರರು. ಅವರಿಗೆ ಪರಲೋಕದಲ್ಲಿ ಯಾವುದೇ ಪಾಲು ಕೊಡದಿರಲು ಅಲ್ಲಾಹು ಉದ್ದೇಶಿಸಿದ್ದಾನೆ. ಅವರಿಗೆ ಕಠೋರ ಶಿಕ್ಷೆ ಕಾದಿದೆ.');
INSERT INTO "kannada" VALUES(3,177,'3:177','ಸತ್ಯವಿಶ್ವಾಸದ ಬದಲಿಗೆ ಸತ್ಯನಿಷೇಧವನ್ನು ಕೊಂಡುಕೊಂಡವರು ಖಂಡಿತ ಅಲ್ಲಾಹನಿಗೆ ಯಾವ ಹಾನಿಯನ್ನೂ ಮಾಡಲಾರರು. ಅವರಿಗೆ ಯಾತನಾಮಯ ಶಿಕ್ಷೆಯು ಕಾದಿದೆ.');
INSERT INTO "kannada" VALUES(3,178,'3:178','ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ನಾವು ಸಡಿಲು ಬಿಟ್ಟು ಕೊಡುತ್ತಿರುವುದು ಅವರಿಗೆ ಹಿತವೆಂದು ಅವರು ಖಂಡಿತ ಭಾವಿಸಬಾರದು. ನಾವು ಅವರ ಪಾಪ ಹೆಚ್ಚಿಸಲೆಂದೇ ಅವರಿಗೆ ಕಾಲಾವಕಾಶ ಕೊಡುತ್ತಿದ್ದೇವೆ. ಅವರಿಗೆ ಅಪಮಾನಕರವಾದ ಶಿಕ್ಷೆ ಕಾದಿದೆ.');
INSERT INTO "kannada" VALUES(3,179,'3:179','ವಿಶುದ್ಧರಿಂದ ಅಶುದ್ಧರನ್ನು ಬೇರ್ಪಡಿಸದೆ ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನು ನಿಮ್ಮ ಇಂದಿನ ಸ್ಥಿತಿಯಲ್ಲಿ ಅಲ್ಲಾಹು ಉಳಿಸುವುದಿಲ್ಲ. ಅದೃಶ್ಯಕಾರ್ಯಗಳನ್ನು ಅಲ್ಲಾಹು ನಿಮಗೆ ಬಹಿರಂಗಗೊಳಿಸಿ ಕೊಡುವುದೂ ಇಲ್ಲ. ಆದರೆ ತನ್ನ ದೂತರಿಂದ ತಾನುದ್ದೇಶಿಸು ವವರನ್ನು ಅಲ್ಲಾಹು ಆರಿಸಿಕೊಳ್ಳುತ್ತಾನೆ . ಆದ್ದರಿಂದ ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ರಸೂಲರಲ್ಲಿ ವಿಶ್ವಾಸ ಇಡಿರಿ. ನೀವು ವಿಶ್ವಾಸವಿಟ್ಟರೆ, ದೇವ ಭಕ್ತಿ ಇರಿಸಿದರೆ ನಿಮಗೆ ಮಹತ್ತರವಾದ ಪ್ರತಿಫಲವಿದೆ.');
INSERT INTO "kannada" VALUES(3,180,'3:180','ಅಲ್ಲಾಹನು ತನ್ನ ಔದಾರ್ಯದಿಂದ ಕೊಟ್ಟಂತಹ ಧನದಲ್ಲಿ ಜಿಪುಣತೆ ತೋರುವವರು ಈ ಜಿಪುಣತನವು ಅವರಿಗೆ ಒಳ್ಳೆಯದೆಂದು ಖಂಡಿತ ಭಾವಿಸಬಾರದು. ಪರಂತು, ಅದವರಿಗೆ ಬಹಳ ದೋಷಕರವಾಗಿದೆ. ಅವರು ಲೋಭ ತೋರಿಸಿದ ಧನವನ್ನು ಪುನರುತ್ಥಾನ ದಿನದಲ್ಲಿ ಅವರ ಕೊರಳಿಗೆ ಮಾಲೆ ಹಾಕಲಾಗುವುದು . ಆಕಾಶಗಳು ಮತ್ತು ಭೂಮಿಯ ಅಧಿಕಾರ ಅಲ್ಲಾಹನದ್ದು. ನಿಮ್ಮ ಪ್ರವೃತ್ತಿಗಳ ಬಗ್ಗೆ ಅಲ್ಲಾಹನು ಸೂಕ್ಷ್ಮ ಜ್ಞಾನಿಯಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(3,181,'3:181','`ಅಲ್ಲಾಹು ಬಡವನಾಗಿರುವನು. ನಾವು ಧನಿಕರಾಗಿದ್ದೇವೆ'' ಎಂದು ಹೇಳುವವರ ಮಾತನ್ನು ಅಲ್ಲಾಹು ಖಂಡಿತ ಆಲಿಸಿರುತ್ತಾನೆ . ಅವರ ಈ ಹೇಳಿಕೆಯನ್ನೂ ಅನ್ಯಾಯವಾಗಿ ಅವರು ಪ್ರವಾದಿಗಳನ್ನು ವಧಿಸಿದ್ದನ್ನೂ ನಾವು ದಾಖಲೆಗೊಳಿಸಿಡುತ್ತೇವೆ. ‘ಕರಟಿಸುವ(ನರಕದ) ಶಿಕ್ಷೆಯ ರುಚಿ ನೋಡಿರಿ’ ಎಂದು ನಾವು ಅವರಲ್ಲಿ ಹೇಳಲಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(3,182,'3:182','ಅದು (ಈ ಶಿಕ್ಷೆ) ನಿಮ್ಮ ಕೈಗಳು ಮಾಡಿದುದರ ಫಲ. ಅಲ್ಲಾಹು ತನ್ನ ದಾಸರಿಗೆ ಯಾವತ್ತೂ ಖಂಡಿತ ಅನ್ಯಾಯವೆಸಗುವವನಲ್ಲ.');
INSERT INTO "kannada" VALUES(3,183,'3:183','‘ಬಲಿಯನ್ನು ನಮ್ಮ ಮುಂದಿರಿಸಿ ಆ ಬಲಿಯನ್ನು ಅಗ್ನಿ ತಿನ್ನುವವರೆಗೆ ಯವುದೇ ಪ್ರವಾದಿಯ ಮೇಲೆ ನಾವು ನಂಬಿಕೆ ಇರಿಸಬಾರದೆಂದು ಅಲ್ಲಾಹು ನಮ್ಮಲ್ಲಿ ಕರಾರು ಮಾಡಿರುತ್ತಾನೆ’ ಎಂದು ಹೇಳಿದವರೊಡನೆ ನೀವು ಹೇಳಿರಿ; ‘ನನಗೆ ಮೊದಲು ಅದೆಷ್ಟೋ ಪ್ರವಾದಿಗಳು ಸ್ಪಷ್ಟ ಪುರಾವೆಗಳನ್ನೂ ನೀವು ಹೇಳಿದ್ದನ್ನೂ ನಿಮ್ಮಲ್ಲಿಗೆ ತಂದಿದ್ದಾರೆ. ನೀವು ಸತ್ಯವಾದಿಗಳಾಗಿದ್ದರೆ ಯಾಕೆ ಅವರನ್ನು ಹತ್ಯೆ ಮಾಡಿದಿರಿ?’');
INSERT INTO "kannada" VALUES(3,184,'3:184','ಅವರು ನಿಮ್ಮನ್ನು ನಿಷೇಧಿಸಿದರೆ (ನೀವು ದುಃಖಿಸ ಬೇಡಿರಿ; ಯಾಕೆಂದರೆ) ನಿಮ್ಮ ಮೊದಲು ವ್ಯಕ್ತ ಪುರಾವೆಗಳೊಂದಿಗೂ, ವೇದ ಪುಸ್ತಕಗಳು ಹಾಗೂ ಬೆಳಕು ಬೀರುತ್ತಿರುವ ಗ್ರಂಥದೊಂದಿಗೂ ಬಂದಿದ್ದ ಅನೇಕ ಪ್ರವಾದಿಗಳನ್ನು ಇವರು ನಿಷೇಧಿಸಿರುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(3,185,'3:185','ಎಲ್ಲ ದೇಹವೂ ಮರಣವನ್ನು ಸವಿಯುವುದು. ನಿಮ್ಮ ಪ್ರತಿಫಲಗಳನ್ನು ಪುನರುತ್ಥಾನ ದಿನದಂದು ನಿಮಗೆ ಪೂರ್ತಿಯಾಗಿ ಕೊಡಲಾಗುತ್ತದೆ. ಅಂದು ಯಾರನ್ನು ನರಕದಿಂದ ದೂರ ಮಾಡಲಾಗುವುದೋ ಹಾಗೂ ಯಾರಿಗೆ ಸ್ವರ್ಗಕ್ಕೆ ಪ್ರವೇಶಗೊಡಲಾಗುವುದೋ ಖಂಡಿತವಾಗಿಯೂ ಅವನು ಜಯಶಾಲಿಯಾದನು. ಐಹಿಕ ಜೀವನವು ಖಂಡಿತವಾಗಿಯೂ ವಂಚನೆಯ ಸರಕಾಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(3,186,'3:186','ಖಂಡಿತವಾಗಿಯೂ ನೀವು ನಿಮ್ಮ ಧನ ಮತ್ತು ನಿಮ್ಮ ದೇಹಗಳಲ್ಲಿ ಪರೀಕ್ಷೆಗೊಳಗಾಗುವಿರಿ. ನಿಮಗೆ ಮೊದಲು ವೇದ ಕೊಡಲ್ಪಟ್ಟವರಿಂದಲೂ ಬಹುದೇವಾರಾಧಕರಿಂದಲೂ ಹೆಚ್ಚು ನೋಯಿಸುವ ಆಕ್ಷೇಪಗಳನ್ನು ನೀವು ಕೇಳಲಿರುವಿರಿ. (ಆಗ) ನೀವು ಸಹನೆ ತೋರಿದರೆ ಮತ್ತು ದೇವ ಭಕ್ತಿ ಅವಲಂಬಿಸಿದರೆ ಖಂಡಿತವಾಗಿಯೂ ಅದು ಕೃತ ನಿಶ್ಚಯದ ಕಾರ್ಯಗಳಿಗೆ ಸೇರಿದೆ.');
INSERT INTO "kannada" VALUES(3,187,'3:187','ವೇದ ಕೊಡಲ್ಪಟ್ಟವರಿಂದ ಅಲ್ಲಾಹು ಕರಾರು ಮಾಡಿಕೊಂಡ ಸಂದರ್ಭ(ವನ್ನು ಜ್ಞಾಪಿಸಿಕೊಳ್ಳಿರಿ). ನೀವು ಈ ಗ್ರಂಥವನ್ನು ಜನರಿಗೆ ಖಂಡಿತ ವಿವರಿಸಿ ಕೊಡಬೇಕು ; ಅಡಗಿಸಿಡಬಾರದು (ಎಂದಾಗಿತ್ತು ಕರಾರು). ಆದರೆ ಅವರದನ್ನು ತಮ್ಮ ಬೆನ್ನ ಹಿಂದಕ್ಕೆ ಎಸೆದರಲ್ಲದೆ ಅದರ ಬದಲಿಗೆ ತುಚ್ಛ ಬೆಲೆಯನ್ನು ಪಡಕೊಂಡರು ! ಅವರು ಕೊಂಡುಕೊಳ್ಳುವುದು ಎಷ್ಟು ನಿಕೃಷ್ಟವಾದದ್ದು.');
INSERT INTO "kannada" VALUES(3,188,'3:188','ತಾವು ಮಾಡಿದುದರಲ್ಲಿ ಹೆಮ್ಮೆಪಡುವ ಹಾಗೂ ತಾವು ಮಾಡದವುಗಳಲ್ಲಿ ಪ್ರಶಂಶಿಸಲ್ಪಡಲು ಬಯಸುವವರು ಶಿಕ್ಷೆಯಿಂದ ಸುರಕ್ಷಿತರೆಂದು ಖಂಡಿತ ನೀವು ಭಾವಿಸಬಾರದು. ಅವರಿಗೆ ವೇದನಾಜನಕ ಶಿಕ್ಷೆಯಿದೆ.');
INSERT INTO "kannada" VALUES(3,189,'3:189','ಆಕಾಶಗಳು ಮತ್ತು ಭೂಮಿಯ ಅಧಿಪತ್ಯ ಅಲ್ಲಾಹನದ್ದೇ ಆಗಿದೆ. ಅಲ್ಲಾಹನು ಸರ್ವದರಲ್ಲೂ ಪರಮ ಸಮರ್ಥನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(3,190,'3:190','ನಿಶ್ಚಯವಾಗಿಯೂ ಆಕಾಶಗಳ ಮತ್ತು ಭೂಮಿಯ ಸೃಷ್ಟಿ ಹಾಗೂ ರಾತ್ರಿ-ಹಗಲುಗಳ ಬದಲಾವಣೆಯಲ್ಲಿ ಬುದ್ಧಿಯುಳ್ಳವರಿಗೆ ಧಾರಾಳ ದೃಷ್ಟಾಂತಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(3,191,'3:191','ಅವರು ನಿಂತುಕೊಂಡೂ ಕುಳಿತುಕೊಂಡೂ (ಎಲ್ಲ ಅವಸ್ಥೆಗಳಲ್ಲೂ) ಅಲ್ಲಾಹುವನ್ನು ಸ್ಮರಿಸುವವರಾಗಿದ್ದಾರೆ. ಅವರು ಆಕಾಶಗಳು ಮತ್ತು ಭೂಮಿಯ ಸೃಷ್ಟಿಯ ಬಗ್ಗೆ ಚಿಂತನೆ ಮಾಡುತ್ತಾರೆ. (ಅಲ್ಲದೆ ಅವರು ಹೇಳುತ್ತಾರೆ;) `ನಮ್ಮ ಪ್ರಭೂ ! ಇದೆಲ್ಲವನ್ನೂ ನೀನು ವೃಥಾ ಸೃಷ್ಟಿಸಲಿಲ್ಲ. ನೀನು ಪರಮ ಪರಿಶುದ್ಧನು. ಆದ್ದರಿಂದ ನಮ್ಮನ್ನು ನೀನು ನರಕ ಶಿಕ್ಷೆಯಿಂದ ಪಾರು ಮಾಡು .');
INSERT INTO "kannada" VALUES(3,192,'3:192','ನಮ್ಮ ಪ್ರಭೂ! ಯಾವನನ್ನಾದರೂ ನೀನು ನರಕಕ್ಕೆ ಪ್ರವೇಶ ಮಾಡಿಸುವುದಾದರೆ ಖಂಡಿತವಾಗಿಯೂ ನೀನು ಅವನನ್ನು ತುಂಬ ನಿಂದ್ಯಗೊಳಿಸಿದೆ. ಅಕ್ರಮಿಗಳಿಗೆ ಸಹಾಯಕರಾಗಿ ಯಾರೂ ಇರುವುದೇ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(3,193,'3:193','ನಮ್ಮ ಪ್ರಭೂ! ‘ನಿಮ್ಮ ಪ್ರಭುವಿನಲ್ಲಿ ವಿಶ್ವಾಸ ತಾಳಿರಿ’ ಎಂದು ಸತ್ಯವಿಶ್ವಾಸಕ್ಕೆ ಆಹ್ವಾನ ಕೊಡುವ ಓರ್ವರ ಕರೆಯನ್ನು ನಾವು ಖಂಡಿತವಾಗಿಯೂ ಕೇಳಿಸಿ ಕೊಂಡಿದ್ದೇವೆ. ಹಾಗೂ ನಾವು ವಿಶ್ವಾಸವಿಟ್ಟಿದ್ದೇವೆ. ಆದ್ದರಿಂದ ಪ್ರಭುವೇ ! ನಮ್ಮ ಪಾಪಗಳನ್ನು ನಮಗೆ ಕ್ಷಮಿಸು. ನಮ್ಮ ಕೆಡುಕುಗಳನ್ನು ನಮ್ಮಿಂದ ಪರಿಹರಿಸು. ಪುಣ್ಯಾತ್ಮರ ಕೂಟದಲ್ಲಿ ನಮ್ಮನ್ನು ಮರಣಗೊಳಿಸು.');
INSERT INTO "kannada" VALUES(3,194,'3:194','ನಮ್ಮ ಪ್ರಭೂ! ನಿನ್ನ ಸಂದೇಶವಾಹಕರುಗಳ ಮೂಲಕ ನೀನು ನಮಗೆ ವಾಗ್ದಾನವಿತ್ತುದನ್ನು ನಮಗೆ ದಯಪಾಲಿಸು. ಪುನರುತ್ಥಾನ ದಿನದಂದು ನಮ್ಮನ್ನು ನಿಂದನೆಗೀಡುಮಾಡದಿರು. ನಿಶ್ಚಯ ವಾಗಿಯೂ ನೀನು ವಾಗ್ದಾನವನ್ನು ಮೀರು ವವನಲ್ಲ’.');
INSERT INTO "kannada" VALUES(3,195,'3:195','ಆಗ ಅವರ ಪ್ರಭುವು ಅವರಿಗೆ ಉತ್ತರವಿತ್ತನು; ನಿಮ್ಮ ಪೈಕಿ ಪುರುಷನಿರಲಿ, ಸ್ತ್ರೀ ಇರಲಿ ಯಾವನೇ ಒಬ್ಬ ಪ್ರವರ್ತಕನ ಪ್ರವೃತ್ತಿಯನ್ನು ನಾನು ನಿಷ್ಫಲ ಗೊಳಿಸುವುದಿಲ್ಲ. ನಿಮ್ಮಲ್ಲಿ ಕೆಲವರು ಮತ್ತೆ ಕೆಲವರಿಂದ ಜನಿಸಿದವರು . ಆದ್ದರಿಂದ (ನನಗಾಗಿ) ಸ್ವದೇಶವನ್ನು ತ್ಯಜಿಸಿದವರು, ಸ್ವಂತ ಮನೆ ಗಳಿಂದ ಹೊರದಬ್ಬಲ್ಪಟ್ಟವರು, ನನ್ನ ಮಾರ್ಗದಲ್ಲಿ ಮರ್ದನ ಕ್ಕೊಳಗಾದವರು, ಯುದ್ಧ ಮಾಡಿದವರು ಹಾಗೂ ಮಡಿದವರಿಗೆಲ್ಲ ಖಂಡಿತ ಅವರ ಪಾಪಗಳನ್ನು ನಾನು ಕ್ಷಮಿಸುವೆನು. ಅಲ್ಲದೆ; ತಳ ಭಾಗದಲ್ಲಿ ನದಿಗಳು ಹರಿಯುತ್ತಿರುವ ಸ್ವರ್ಗ ಧಾಮಗಳಿಗೆ ಖಂಡಿತವಾಗಿಯೂ ನಾನು ಅವ ರನ್ನು ಪ್ರವೇಶಗೊಳಿಸುವೆನು. (ಇದು) ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ಪ್ರತಿಫಲವಾಗಿದೆ. ಉತ್ಕøಷ್ಟ ಪ್ರತಿಫಲ ಇರುವುದು ಅಲ್ಲಾಹನ ಬಳಿ ಮಾತ್ರ.');
INSERT INTO "kannada" VALUES(3,196,'3:196','ಸತ್ಯನಿಷೇಧಿಗಳು ನಾಡುಗಳಾದ್ಯಂತ ವಿಹರಿಸುತ್ತಿರುವುದು ಯಾವತ್ತೂ ತಮ್ಮನ್ನು ವಂಚನೆಗೀಡು ಮಾಡದಿರಲಿ.');
INSERT INTO "kannada" VALUES(3,197,'3:197','(ಇದು) ಬಹಳ ತುಚ್ಛವಾದ ಒಂದು ಜೀವನ ಸುಖ ಮಾತ್ರ. ಆಮೇಲೆ ಅವರ ವಾಸಸ್ಥಾನ ನರಕವಾಗಿದೆ. ಅದು ಎಷ್ಟೊಂದು ನೀಚವಾದ ವಾಸಸ್ಥಾನ !');
INSERT INTO "kannada" VALUES(3,198,'3:198','ಆದರೆ ತಮ್ಮ ಪ್ರಭುವಿನಲ್ಲಿ ಭಕ್ತಿಯಿರಿಸಿ ಕೊಂಡವರಾರೋ, ಅವರಿಗೆ ಕೆಳಭಾಗದಲ್ಲಿ ನದಿಗಳು ಹರಿಯುತ್ತಿರುವ ಸ್ವರ್ಗಧಾಮಗಳಿವೆ. ಅದರಲ್ಲಿ ಅವರು ಶಾಶ್ವತರು. ಅದು (ಅವರಿಗೆ) ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ಒಂದು ಅತಿಥಿ ಸತ್ಕಾರ. ಅವನ ಬಳಿಯ ಪ್ರತಿಫಲವೇ ಗುಣವಂತರಿಗೆ (ಐಹಿಕ ಸುಖಕ್ಕಿಂತ) ಎಷ್ಟೋ ಉತ್ತಮವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(3,199,'3:199','ವೇದದವರ ಕೂಟದಲ್ಲಿ; ಅಲ್ಲಾಹು ಮತ್ತು ನಿಮಗೆ ಅವತೀರ್ಣವಾದುದರಲ್ಲಿ ಹಾಗೂ ಅವರಿಗೆ ಅವತೀರ್ಣವಾದುದರಲ್ಲಿ ನಂಬಿಕೆ ತಾಳುವವರಿದ್ದಾರೆ. ಅವರು ಅಲ್ಲಾಹನನ್ನು ಭಯ ಪಡುವವರಾಗಿದ್ದು ಅಲ್ಲಾಹನ ವಚನಗಳನ್ನು ತುಚ್ಛ ಬೆಲೆಗೆ ಮಾರುವುದಿಲ್ಲ. ಅವರಿಗೆ ಅವರ ಪ್ರಭುವಿನ ಬಳಿ ಅವರ ಪ್ರತಿಫಲವುಂಟು. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನು ವಿಚಾರಣೆ ನಡೆಸುವುದರಲ್ಲಿ ಅತಿ ಶೀಘ್ರನು.');
INSERT INTO "kannada" VALUES(3,200,'3:200','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನೀವು ಸಹನೆಯನ್ನು ಅವಲಂಬಿಸಿರಿ. ಸಹನೆಯಲ್ಲಿ ಇತರರನ್ನು ಮೀರಿಸಿರಿ. ಪ್ರತಿರೋಧಕ್ಕೆ ಸನ್ನದ್ಧರಾಗಿ ನಿಲ್ಲಿರಿ. ಅಲ್ಲಾಹನಲ್ಲಿ ಭಯವಿರಿಸಿಕೊಳ್ಳಿರಿ. ನೀವು ಜಯಶಾಲಿಗಳಾಗಲಿಕ್ಕಾಗಿ .');
INSERT INTO "kannada" VALUES(4,1,'4:1','ಜನರೇ, ನೀವು ನಿಮ್ಮ ಪಾಲಕ ಪ್ರಭುವನ್ನು ಭಯಪಡಿರಿ. ಅವನು ನಿಮ್ಮನ್ನು ಒಂದೇ ವ್ಯಕ್ತಿಯಿಂದ ಸೃಷ್ಟಿಸಿದನು. ಅದೇ ವ್ಯಕ್ತಿಯಿಂದ ಅದರ ಜೋಡಿಯನ್ನು ಉಂಟು ಮಾಡಿದನು ಮತ್ತು ಅವರೆರಡರಿಂದ ಅನೇಕಾನೇಕ ಸ್ತ್ರೀ-ಪುರುಷರನ್ನು ಹಬ್ಬಿಸಿದನು. ನೀವು ಯಾರ ಹೆಸರನ್ನೆತ್ತಿ ಪರಸ್ಪರ (ಹಕ್ಕು ಬಾಧ್ಯತೆಗಳನ್ನು) ಕೇಳುತ್ತೀರೋ ಆ ಅಲ್ಲಾಹನ ಮತ್ತು ಕುಟುಂಬ ಸಂಬಂಧದ ಬಗ್ಗೆ ಜಾಗೃತೆ ವಹಿಸಿ. ಅವನು ಖಂಡಿತವಾಗಿಯೂ ನಿಮ್ಮ ಮೇಲೆ ಮೇಲ್ನೋಟವನ್ನಿರಿಸಿಕೊಂಡಿ ರುವನು.');
INSERT INTO "kannada" VALUES(4,2,'4:2','ತಬ್ಬಲಿಗಳ ಸೊತ್ತುಗಳನ್ನು ಅವರಿಗೆ ಕೊಡಿರಿ. ಒಳ್ಳೆಯದ್ದಕ್ಕೆ ಕೆಟ್ಟದ್ದನ್ನು ನೀವು ಬದಲಿಸಿಕೊಳ್ಳ ಬೇಡಿರಿ. ನಿಮ್ಮ ಸೊತ್ತನ್ನು ಅವರ ಸೊತ್ತಿಗೆ ಸೇರಿಸಿ ಕಬಳಿಸಲೂ ಬೇಡಿರಿ. ಇದು ಅತ್ಯಂತ ಘೋರಪಾತಕ.');
INSERT INTO "kannada" VALUES(4,3,'4:3','ಅನಾಥರೊಂದಿಗೆ ನ್ಯಾಯವಾಗಿ ನಡೆದುಕೊಳ್ಳಲು ಸಾಧ್ಯವಾಗದೆಂಬ ಆಶಂಕೆ ನೀವಿಷ್ಟಪಡುವ ಸ್ತ್ರೀಯರಿಂದ ಈರ್ವರನ್ನೋ, ಮೂವರನ್ನೋ, ನಾಲ್ವರನ್ನೋ ವಿವಾಹಮಾಡಿ ಕೊಳ್ಳಿರಿ. ಆದರೆ ಅವರೊಂದಿಗೆ (ಬಹುಪತ್ನಿಯರ ಮಧ್ಯೆ) ನೀತಿ ಪಾಲಿಸಲಾರಿರಿ ಎಂದು ನೀವು ಭಯಪಟ್ಟರೆ ಒಬ್ಬಳನ್ನು ಮಾತ್ರ ವಿವಾಹವಾಗಿರಿ ಅಥವಾ ನೀವು ಮಾಲಕತ್ವ ಹೊಂದಿದ ಗುಲಾಮ ಸ್ತ್ರೀಯರನ್ನು ಸ್ವೀಕರಿಸಿಕೊಳ್ಳಿರಿ. ಇದು ನೀವು ಅನೀತಿ ಮಾಡದಿರಲು ಅತ್ಯಂತ ಯೋಗ್ಯ ಮಾರ್ಗವಾಗಿದೆ .');
INSERT INTO "kannada" VALUES(4,4,'4:4','ಸ್ತ್ರೀಯರಿಗೆ ಅವರ ಮಹರನ್ನು (ವಧು ಧನ) ಅವಶ್ಯವಾಗಿ ಕೊಡಿರಿ. ಆದರೆ ಅದರಲ್ಲಿ ಏನಾದರೂ ಅವರು ಸ್ವಮನಸ್ಸಿನಿಂದ ನಿಮಗೆ ಬಿಟ್ಟುಕೊಟ್ಟರೆ ನೀವು ಅದನ್ನು ಸಂತೋಷದಿಂದ ಅನುಭೋಗಿಸಿ ಕೊಳ್ಳಬಹುದು ');
INSERT INTO "kannada" VALUES(4,5,'4:5','ಅಲ್ಲಾಹು ನಿಮಗೆ ಮೇಲ್ನೋಟ ವಹಿಸಿಕೊಟ್ಟಿರುವ ನಿಮ್ಮ ಧನವನ್ನು ವ್ಯವಹಾರ ಯೋಗ್ಯರಲ್ಲದವರಿಗೆ ಬಿಟ್ಟು ಕೊಡಬೇಡಿರಿ . ಆದರೆ ಅದರಿಂದ ಅವರಿಗೆ ಉಣ್ಣಲಿಕ್ಕೂ ಉಡಲಿಕ್ಕೂ ಕೊಡಿರಿ. ಮತ್ತು ಅವರೊಂದಿಗೆ ಒಳ್ಳೆಯ ಮಾತನ್ನು ಆಡಿರಿ.');
INSERT INTO "kannada" VALUES(4,6,'4:6','ಅನಾಥರನ್ನು ಅವರು ವಿವಾಹ ಯೋಗ್ಯ ವಯಸ್ಸಿನವರಾಗುವವರೆಗೂ ಪರಿಶೀಲಿಸುತ್ತಲೇ ಇರಿ. ಅನಂತರ (ಅವರು ವಿವಾಹ ಪ್ರಾಯ ತಲುಪಿದಾಗ) ಅವರಲ್ಲಿ ಯೋಗ್ಯತೆ ಕಂಡು ಬಂದರೆ ಅವರ ಸೊತ್ತನ್ನು ಅವರಿಗೆ ಕೊಟ್ಟು ಬಿಡಿರಿ. ಅವರು ದೊಡ್ಡವರಾಗುವ (ತಮ್ಮ ಹಕ್ಕನ್ನು ಕೇಳುವ) ಭಯದಿಂದ ನೀವು ಅದನ್ನು ಹಕ್ಕು ಇಲ್ಲದೆ ಹಾಗೂ ಅವಸರದಿಂದ ವೆಚ್ಚ ಮಾಡಬೇಡಿ. ಅನಾಥರ ರಕ್ಷಕನು ಧನಿಕನಾಗಿದ್ದರೆ ಅವನು ತನ್ನ ಸ್ವಂತ ಅಗತ್ಯಕ್ಕೆ ಅನಾಥರ ಸೊತ್ತನ್ನು ಬಳಸದಿರಲಿ, ದರಿದ್ರನಾಗಿದ್ದರೆ ವಾಡಿಕೆಯನುಸಾರ ಬಳಸಿ ಕೊಳ್ಳಲಿ. ಅನಂತರ ಅವರ ಸೊತ್ತನ್ನು ಅವರಿಗೆ ಬಿಟ್ಟು ಕೊಡುವಾಗ ನೀವು ಅವರ ಕಡೆಯಿಂದ ಸಾಕ್ಷಿ ನಿಲ್ಲಿಸಿರಿ. ಲೆಕ್ಕ ಪರಿಶೋಧನೆ ಮಾಡಲು ಅಲ್ಲಾಹು ಸಾಕು.');
INSERT INTO "kannada" VALUES(4,7,'4:7','ಮಾತಾಪಿತರೂ ಆಪ್ತ ಬಂಧುಗಳೂ ಬಿಟ್ಟು ಹೋದ ಸಂಪತ್ತಿನಲ್ಲಿ ಪುರುಷರಿಗೆ ಒಂದು ಪಾಲು ಇದೆ. ತಾಯಿ ತಂದೆಯರೂ ನಿಕಟ ಬಂಧುಗಳೂ ಬಿಟ್ಟು ಹೋದ ಧನದಲ್ಲಿ ಸ್ತ್ರೀಯರಿಗೂ ಪಾಲು ಇದೆ. ಆ ಧನ ಕಡಿಮೆಯಿರಲಿ. ಹೆಚ್ಚು ಇರಲಿ, ನಿಶ್ಚಿತ ಪಾಲು ಇದೆ.');
INSERT INTO "kannada" VALUES(4,8,'4:8','ಪಾಲಾಗುವ ಸಂದರ್ಭದಲ್ಲಿ ಹಕ್ಕುದಾರರಲ್ಲದ ಸಂಬಂಧಿಕರು, ಅನಾಥರು ಮತ್ತು ನಿರ್ಗತಿಕರು ಅಲ್ಲಿ ಹಾಜರಾದಲ್ಲಿ ಅವರಿಗೂ ಏನಾದರೂ ಕೊಡಿರಿ ಸಂಬಂಧಿಕರು, ಅನಾಥರು ಮತ್ತು ನಿರ್ಗತಿಕರು ಅಲ್ಲಿ ಹಾಜರಾದಲ್ಲಿ ಅವರಿಗೂ ಏನಾದರೂ ಕೊಡಿರಿ . ಅವರೊಂದಿಗೆ ಒಳ್ಳೆಯ ಮಾತನ್ನು ಆಡಿರಿ.');
INSERT INTO "kannada" VALUES(4,9,'4:9','ತಮ್ಮ ಹಿಂದೆ ಬಲಹೀನರಾದ ಸಂತಾನಗಳನ್ನು ಉಳಿಸಿ ಮರಣ ಹೊಂದುತ್ತಿದ್ದರೆ ಅವರ ವಿಷಯದಲ್ಲಿ ಚಿಂತೆಗೀಡಾಗುವವರು (ಅನಾಥ ಮಕ್ಕಳ ವಿಷಯದಲ್ಲಿ) ಭಯಪಟ್ಟುಕೊಳ್ಳಲಿ. ಹಾಗೆ ಅವರು ಅಲ್ಲಾಹನಲ್ಲಿ ಭಯವಿರಿಸಿಕೊಳ್ಳಲಿ. ಹಾಗೂ ಮರಣಾಸನ್ನನಿಗೆ (ಉಳಿದವರು) ಸರಿಯಾದ ಉಪದೇಶ ಮಾಡಲಿ.');
INSERT INTO "kannada" VALUES(4,10,'4:10','ಅನಾಥರ ಸೊತ್ತನ್ನು ಅಕ್ರಮವಾಗಿ ತಿನ್ನುವವರು ಖಂಡಿತವಾಗಿಯೂ ತಮ್ಮ ಹೊಟ್ಟೆಯನ್ನು ಬೆಂಕಿಯಿಂದ ತುಂಬಿಸುತ್ತಾರೆ. ಅವರು ನರಕದಜ್ವಲಿಸುವ ಅಗ್ನಿಯಲ್ಲಿ ಖಂಡಿತ ಪ್ರವೇಶಿಸುವರು.');
INSERT INTO "kannada" VALUES(4,11,'4:11','ನಿಮ್ಮ ಮಕ್ಕಳ ವಿಷಯದಲ್ಲಿ ಅಲ್ಲಾಹನು ನಿಮಗೆ ಆದೇಶ ನೀಡುತ್ತಿದ್ದಾನೆ : ಪುರುಷನಿಗೆ ಇಬ್ಬರು ಸ್ತ್ರೀಯರಿಗೆ ಸಮಾನವಾದ ಪಾಲಿದೆ . ಇನ್ನು ಎರಡಕ್ಕಿಂತ ಹೆಚ್ಚು ಪುತ್ರಿಯರು ಮಾತ್ರವಿದ್ದಲ್ಲಿ ಅವರಿಗೆ ಮೃತನು ಉಳಿಸಿದ ಸೊತ್ತಿನಿಂದ ಮೂರನೆಯ ಎರಡು ಪಾಲು ಕೊಡಲಾಗುವುದು. ಮತ್ತು ಒಬ್ಬಳೇ ಮಗಳಿದ್ದರೆ ಅವಳಿಗೆ ಅರ್ಧಾಂಶ ಇದೆ. ಮೃತನು ಮಕ್ಕಳಿದ್ದವನಾಗಿದ್ದರೆ ಮಾತಾಪಿತರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬರಿಗೂ ಸೊತ್ತಿನ ಆರನೆಯ ಒಂದಂಶ ಸಿಗಬೇಕು. ಅವನು ಮಕ್ಕಳಿಲ್ಲದವನಾಗಿದ್ದು ಮಾತಾಪಿತರೇ ಅವನ ವಾರೀಸುದಾರರಾಗಿದ್ದಲ್ಲಿ ತಾಯಿಗೆ ಮೂರನೆಯ ಒಂದಂಶ ಕೊಡಬೇಕು. ಮೂರನೆಯ ಒಂದಂಶ ಕೊಡಬೇಕು. (ಉಳಿದದ್ದು ತಂದೆಗೆ). ಮೃತನಿಗೆ ಒಂದಕ್ಕಿಂತ ಹೆಚ್ಚು ಸಹೋದರರಿದ್ದರೆ ತಾಯಿಗೆ ಆರರಲ್ಲೊಂದು. ಇವೆಲ್ಲವೂ ಉಯಿಲು ಅಥವಾ ಸಾಲ ತೀರಿಸಿದ ನಂತರ. ನಿಮ್ಮ ಹೆತ್ತವರು ಮತ್ತು ನಿಮ್ಮಮಕ್ಕಳ ಪೈಕಿ ನಿಮಗೆ ಹೆಚ್ಚು ಉಪಕಾರಿಗಳಾ ಗುವವರು ಯಾರು ಎಂಬುದನ್ನು ನೀವು ಅರಿಯಲಾರಿರಿ. ಆದ್ದರಿಂದ ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದನಿಶ್ಚಯವಾದ ಈ ವಾರೀಸು ಹಿಸೆಯನ್ನುನಿಮಗೆ ಶಾಸನ ಗೊಳಿಸಲಾಗಿದೆ. ಅಲ್ಲಾಹನುಸರ್ವಜ್ಞನೂ ನಿಪುಣ ನಿಯಂತ್ರಕನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(4,12,'4:12','ನಿಮ್ಮ ಪತ್ನಿಯರು ಮಕ್ಕಳಿಲ್ಲದವರಾಗಿದ್ದರೆ ಅವರು ಬಿಟ್ಟುಹೋದುದರಲ್ಲಿ ನಿಮಗೆ ಅರ್ಧಪಾಲು ಇದೆ. ಇನ್ನು ಅವರಿಗೆ ಮಕ್ಕಳಿದ್ದಲ್ಲಿ ಅವರು ಬಿಟ್ಟು ಹೋದುದರಲ್ಲಿ ನಾಲ್ಕನೆಯ ಒಂದಂಶ ನಿಮಗಿದೆ. ಅವರು ಮಾಡಿದ್ದ ಉಯಿಲು ಅಥವಾ ಸಾಲವನ್ನು ತೀರಿಸಿದ ನಂತರದ ಸೊತ್ತಿನಿಂದ. ನಿಮಗೆ ಮಕ್ಕಳಿಲ್ಲದಿದ್ದಲ್ಲಿ ನೀವು ಬಿಟ್ಟು ಹೋದುದರಲ್ಲಿ ಅವರಿಗೆ (ಮಡದಿಯರಿಗೆ) ನಾಲ್ಕರಲ್ಲಿ ಒಂದಂಶವಿದೆ. ಇನ್ನು ನಿಮಗೆ ಮಕ್ಕಳಿದ್ದಲ್ಲಿ ನೀವು ಉಳಿಸಿ ಹೋದುದರಲ್ಲಿ ಅವರಿಗೆ ಎಂಟನೇ ಒಂದು ಪಾಲು ಇದೆ. ನೀವು ಮಾಡುವ ಉಯಿಲು ಮತ್ತು ಸಾಲವನ್ನು ಕಳೆದು. ಇನ್ನು ವಾರೀಸು ಬಿಟ್ಟು ಹೋದ ಪುರುಷ ಅಥವಾ ಸ್ತ್ರೀಯು ತಂದೆಯೂ ಮಕ್ಕಳೂ ಇಲ್ಲದವರಾಗಿದ್ದು ಆ ಮೃತರಿಗೆ (ತಾಯಿ ಕಡೆಯ) ಓರ್ವ ಸಹೋದರ ಯಾ ಓರ್ವ ಸಹೋದರಿ ಇದ್ದಲ್ಲಿ ಅವರಿಬ್ಬರಿಗೂ ತಲಾ ಆರರಲ್ಲಿ ಒಂದಂಶ ಇದೆ. ಇನ್ನು ಅವರು ಒಂದಕ್ಕಿಂತ ಹೆಚ್ಚಾಗಿದ್ದಲ್ಲಿ ಅವರೆಲ್ಲರೂ ಸೊತ್ತಿನ ಮೂರನೇ ಒಂದು ಅಂಶಕ್ಕೆ ಸಮಾನ ಹಕ್ಕುದಾರರಾಗುತ್ತಾರೆ. ಇದು ಮೃತನು ವಾರೀ ಸುದಾರರಿಗೆ ದ್ರೋಹವೆಸಗುವ ಉದ್ದೇಶವಿಲ್ಲದೆ ಮಾಡಿದ ಉಯಿಲು ಮತ್ತು ಸಾಲವನ್ನು ಪೂರೈಸಿದ ನಂತರವಾಗಿದೆ. ಇದು ಅಲ್ಲಾಹನ ಉಪದೇಶಗಳಾಗಿವೆ. ಅಲ್ಲಾಹನು ಪರಮ ತಜ್ಞನೂ ಪರಮ ಸಹನೆಯುಳ್ಳವನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,13,'4:13','ಇವು ಅಲ್ಲಾಹನು ನಿಶ್ಚಯಿಸಿದ ಮೇರೆಗಳು. ಅಲ್ಲಾಹನ ಮತ್ತು ಅವನ ಸಂದೇಶವಾಹಕರ ಅನುಸರಣೆ ಮಾಡುವವನಿಗೆ ತಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆಗಳು ಹರಿಯುತ್ತಿರುವ ಸ್ವರ್ಗೋದ್ಯಾನ ಗಳಲ್ಲಿ ಅಲ್ಲಾಹನು ಪ್ರವೇಶ ಕೊಡುವನು. ಅದರಲ್ಲಿ ಅವರು ಶಾಶ್ವತರು. ಅದು ಘನವೆತ್ತ ವಿಜಯವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(4,14,'4:14','ಯಾರು ಅಲ್ಲಾಹು ಮತ್ತು ರಸೂಲರನ್ನು ಧಿಕ್ಕರಿಸಿ ಅವನ ಎಲ್ಲೆಗಳನ್ನು ಮೀರುತ್ತಾರೋ, ಅವರನ್ನು ಅವನು ನರಕಕ್ಕೆ ಪ್ರವೇಶಗೊಳಿಸುವನು. ಅದರಲ್ಲಿ ಅವರು ಶಾಶ್ವತರು. ಅವರಿಗೆ ನಿಂದನಾರ್ಹ ಶಿಕ್ಷೆಯಿದೆ.');
INSERT INTO "kannada" VALUES(4,15,'4:15','ನಿಮ್ಮ ಸ್ತ್ರೀಯರ ಪೈಕಿ ಯಾರಾದರೂ ನೀಚ ವೃತ್ತಿ (ವ್ಯಭಿಚಾರ) ಕೈಗೊಂಡಲ್ಲಿ ಅವರಿಗೆ ಪ್ರತಿಯಾಗಿ ನಿಮ್ಮಿಂದ ನಾಲ್ಕು ಸಾಕ್ಷಿಗಳನ್ನು ಹಾಜರುಗೊಳಿಸಿರಿ. ಅವರು ಸಾಕ್ಷಿ ನಿಂತರೆ ಆಸ್ತ್ರೀಯರನ್ನು ಅವರ ಮರಣವು ಅವರನ್ನು ಕೊನೆಗೊಳಿಸುವವರೆಗೆ ಅಥವಾ ಅವರ ಬಗ್ಗೆ ಅಲ್ಲಾಹು ಒಂದು ದಾರಿ ತೋರಿಸುವವರೆಗೆ ಮನೆಗಳಲ್ಲಿ ತಡೆದು ನಿಲ್ಲಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(4,16,'4:16','ನಿಮ್ಮ ಪೈಕಿ ಈ ಪಾಪಕಾರ್ಯವೆಸಗುವ ಈರ್ವರಿಗೂ ಕಿರುಕುಳ ಕೊಡಿರಿ. ಅವರು ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು ಸುಧಾರಿಸಿಕೊಂಡರೆ ಅವರನ್ನು ಬಿಟ್ಟು ಬಿಡಿರಿ. ಅಲ್ಲಾಹನು ಬಹಳ ಹೆಚ್ಚು ಪಶ್ಚಾತ್ತಾಪ ಸ್ವೀಕರಿಸುವವನೂ ಪರಮದಯಾಳುವೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(4,17,'4:17','ಅಜ್ಞಾನದಿಂದ ಕುಕರ್ಮಗಳನ್ನು ಮಾಡಿ ಅನಂತರ ಶೀಘ್ರವೇ ತೌಬಾ ಮಾಡುವವರ ತೌಬಾ ಮಾತ್ರ ಅಲ್ಲಾಹನಿಂದ ಸ್ವೀಕೃತ. ಅಲ್ಲಾಹನು ಇಂತಹವರಿಗೆ ಪಾಪಮುಕ್ತಿ ನೀಡುವನು. ಅಲ್ಲಾಹು ಸರ್ವಜ್ಞನೂ ಯುಕ್ತಿ ಪೂರ್ಣನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,18,'4:18','ಆದರೆ ಪಾಪಗಳನ್ನು ಮಾಡುತ್ತಲೇ ಇದ್ದು ಕೊನೆಗೆ ಅವರಿಗೆ ಮರಣ ಸಮೀಪಿಸಿದಾಗ `ಇದೋ ನಾನೀಗ ತೌಬಾ ಮಾಡಿದೆ’ ಎಂದು ಹೇಳುವವರಿಗೆ ದೋಷ ಮುಕ್ತಿ ಇರುವುದಿಲ್ಲ. ಅದೇ ರೀತಿಯಲ್ಲಿ ಕೊನೆ ಯುಸಿರಿನ ತನಕವೂ ಕಾಫಿರ್ ಆಗಿರುವವರಿಗೂ ತೌಬಾ ಇಲ್ಲ. ಅಂತಹವರಿಗೆ ವೇದನಾಜನಕ ಶಿಕ್ಷೆಯನ್ನು ನಾವು ಸಿದ್ಧಗೊಳಿಸಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(4,19,'4:19','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ಸ್ತ್ರೀಯರನ್ನು ಉತ್ತರಾಧಿಕಾರ ಸೊತ್ತಿನಂತೆ ಕೈವಶ ಇರಿಸಿಕೊಳ್ಳುವುದು ನಿಮಗೆ ಸಮ್ಮತವಲ್ಲ. ನೀವು ಅವರಿಗೆ ಕೊಟ್ಟಿರು ವುದರಿಂದ ಮಹ್ರ್‍ನ ಒಂದಂಶವನ್ನಾದರೂ ಅಪಹರಿಸಲಿಕ್ಕಾಗಿ ನೀವು ಅವರನ್ನು ತಡೆದು ನಿಲ್ಲಿಸಬೇಡಿರಿ. ಅವರು ಸ್ಪಷ್ಟವಾದ ಏನಾದರೂ ಕುಕರ್ಮವನ್ನು ಮಾಡಿದರೆ ಹೊರತು. ಅವರೊಂದಿಗೆ ಉತ್ತಮ ರೀತಿಯಲ್ಲಿ ವರ್ತಿಸಿರಿ. ನಿಮಗೆ ಅವರು ಸಹ್ಯವಾಗದಿದ್ದರೆ (ಕ್ಷಮಿಸಿರಿ. ಯಾಕೆಂದರೆ) ನೀವು ಯಾವುದೇ ಒಂದು ವಸ್ತುವನ್ನು ಮೆಚ್ಚದಿರುವಾಗಲೂ ಆ ವಸ್ತುವಿನಲ್ಲಿ ಅಲ್ಲಾಹು ಧಾರಾಳ ಒಳಿತನ್ನು ಇಟ್ಟಿರಲೂಬಹುದು.');
INSERT INTO "kannada" VALUES(4,20,'4:20','ಒಬ್ಬಳು ಸಂಗಾತಿಯ ಸ್ಥಾನದಲ್ಲಿ ಇನ್ನೊಬ್ಬಳು ಸಂಗಾತಿಯನ್ನು ಬದಲಾಯಿಸಲು ನೀವು ಇಚ್ಛಿಸುವಿರಾದರೆ ಅವರಲ್ಲೊಬ್ಬಳಿಗೆ ನೀವು ಧನದ ರಾಶಿಯನ್ನೇ ನೀಡಿದ್ದರೂ ಕೂಡಾ ಅದರಿಂದ ಏನನ್ನೂ ವಶಪಡಿಸಿಕೊಳ್ಳಬೇಡಿರಿ. ನೀವು ಅಕ್ರಮ ಹಾಗೂ ಸ್ಪಷ್ಟ ಅಪರಾಧದ ಮೂಲಕ ಅದನ್ನು ವಶಪಡಿಸಿ ಕೊಳ್ಳುತ್ತೀರೇನು?');
INSERT INTO "kannada" VALUES(4,21,'4:21','ನೀವು ಪರಸ್ಪರ ರಸಾನಂದವನ್ನು ಸವಿದ ಹಾಗೂ ಅವರ ಪರವಾಗಿ ನಿಮ್ಮಿಂದ ಒಂದು ಪ್ರಬಲ ಕರಾರನ್ನು ಪಡೆದುಕೊಂಡಿರುವ ನೀವು ಅದನ್ನು ಹೇಗೆ ಮರಳಿ ಪಡೆಯುತ್ತೀರಿ? (ಅದು ಯಾವತ್ತೂ ನ್ಯಾಯವಲ್ಲ) ');
INSERT INTO "kannada" VALUES(4,22,'4:22','ನಿಮ್ಮ ಪಿತರು ವಿವಾಹ ಮಾಡಿಕೊಂಡಿದ್ದ ಸ್ತ್ರೀಯರನ್ನು ನೀವು ವಿವಾಹವಾಗಬೇಡಿರಿ. ಆದರೆ ಈ ಹಿಂದೆ ಕಳೆದುದು ಹೊರತು (ಅದನ್ನು ಕ್ಷಮಿಸಲಾಗಿದೆ) ವಾಸ್ತವದಲ್ಲಿ ಇದೊಂದು ಅಶ್ಲೀಲ ಕಾರ್ಯವೂ ಕೋಪ ಹೇತುವೂ ಕೆಟ್ಟ ಮಾರ್ಗವೂ ಆಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(4,23,'4:23','ನಿಮ್ಮ ಮಾತೆಯರು, ನಿಮ್ಮ ಪುತ್ರಿಯರು, ನಿಮ್ಮ ಸಹೋದರಿಯರು, ನಿಮ್ಮ ಪಿತೃ ಸಹೋದರಿಯರು, ನಿಮ್ಮ ಮಾತೃ ಸಹೋದರಿಯರು, ಸಹೋದರ ಪುತ್ರಿಯರು, ಸಹೋದರಿ ಪುತ್ರಿಯರು, ನಿಮಗೆ ಎದೆ ಹಾಲುಣಿಸಿದ ಸಾಕು ತಾಯಂದಿರು, ಸ್ತನಪಾನ ಸಂಬಂಧದ ನಿಮ್ಮ ಸಹೋದರಿಯರು, ನಿಮ್ಮ ಪತ್ನಿಯರ ಮಾತೆಯರು, ನೀವು (ದೈಹಿಕ) ಸಂಬಂಧ ಹೊಂದಿದ ನಿಮ್ಮ ಪತ್ನಿಯರಲ್ಲಿ ಉಂಟಾಗಿದ್ದ ನಿಮ್ಮ ಸಂರಕ್ಷಣೆಯಲ್ಲಿರುವ ನಿಮ್ಮ ಸಾಕು ಪುತ್ರಿಯರು ನಿಮಗೆ (ವಿವಾಹ) ನಿಷಿದ್ಧಗೊಳಿ ಸಲಾಗಿದೆ. ಆದರೆ ಅವರೊಂದಿಗೆ (ಮಡದಿಯ ರೊಂದಿಗೆ) ಂ ನೀವು ದೇಹ ಸಂಪರ್ಕ ನಡೆಸಿಲ್ಲದಿದ್ದರೆ ಅವರ ಪುತ್ರಿಯರು ನಿಮಗೆ ನಿಷಿದ್ಧವಲ್ಲ. (ಮಡದಿಯನ್ನು ತೊರೆದರೆ ಅವಳ ಪುತ್ರಿ ಸಮ್ಮತ). ನಿಮ್ಮ ಸ್ವಂತ ಪುತ್ರರ ಮಡದಿಯರೂ, ಇಬ್ಬರು ಸಹೋದರಿಯರನ್ನು ನೀವು ಏಕಕಾಲಕ್ಕೆ ಮಡದಿಯರನ್ನಾಗಿ ಇಟ್ಟುಕೊಳ್ಳುವುದೂ (ನಿಮಗೆ ನಿಷಿದ್ಧಗೊಳಿಸಲಾಗಿದೆ). ಹಿಂದೆ ಆಗಿ ಹೋದು ದರ ಹೊರತು. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಬಹಳ ಕ್ಷಮಿಸುವವನೂ, ಪರಮ ದಯಾಳುವೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,24,'4:24','ಇತರರ ವಿವಾಹ ಬಂಧನದಲ್ಲಿರುವ ಸ್ತ್ರೀಯರೂ ನಿಮಗೆ ನಿಷಿದ್ಧರಾಗಿದ್ದಾರೆ. ಆದರೆ ಗುಲಾಮ ಸ್ತ್ರೀಯರು ಇದಕ್ಕೆ ಹೊರತಾಗಿರುತ್ತಾರೆ. ಇದು ನಿಮ್ಮ ಮೇಲೆ ಅಲ್ಲಾಹು ನಿರ್ಬಂಧಗೊಳಿಸಿರುವ ಕಾನೂನು. ಇವರ ಹೊರತಾಗಿರುವ ಸ್ತ್ರೀಯರನ್ನು ನೀವು ನಿಮ್ಮ ಸಂಪತ್ತಿನ ಮೂಲಕ ಲಗ್ನವಾಗ ಬಯಸುವುದು ಧರ್ಮಬದ್ಧಗೊಳಿಸಲಾಗಿದೆ. ಸ್ವಚ್ಛಂದ ಲೈಂಗಿಕತೆ ಸಲ್ಲದು. ನೀವು ಅವರೊಂದಿಗೆ ದಾಂಪತ್ಯ ಜೀವನದ ಸುಖ ವುಣ್ಣುವುದರ ಪ್ರತಿಫಲವಾಗಿ ಅವರ ಮಹ್ರ್‍ನ್ನು ಕಡ್ಡಾಯವಾಗಿ ಅವರಿಗೆ ಕೊಡಿರಿ. ವಿವಾಹಧನ ನಿಶ್ಚಯಿಸಿಕೊಂಡ ಅನಂತರ ಪರಸ್ಪರ ತೃಪ್ತಿಪಟ್ಟು ಕೊಂಡು ವಿನಾಯಿತಿ ತೋರುವುದರಲ್ಲಿ ದೋಷವಿಲ್ಲ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹು ಸರ್ವಜ್ಞನೂ ಮಹಾ ಧೀಮಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,25,'4:25','ನಿಮ್ಮಲ್ಲಾರಾದರೂ ಸತ್ಯವಿಶ್ವಾಸಿನಿಯರಾದ ಸ್ವತಂತ್ರ ಸ್ತ್ರೀಯರನ್ನು ವಿವಾಹ ಮಾಡಿಕೊಳ್ಳುವಷ್ಟು ಸ್ಥಿತಿವಂತನಲ್ಲದಿದ್ದರೆ, ನಿಮ್ಮ ಸ್ವಾಧೀನದಲ್ಲಿರುವ ವಿಶ್ವಾಸಿನಿಯರಾದ ದಾಸಿಯರ ಪೈಕಿ ಯಾರ ನ್ನಾದರೂ ವಿವಾಹ ಮಾಡಿಕೊಳ್ಳಲಿ. ಅಲ್ಲಾಹು ನಿಮ್ಮ ವಿಶ್ವಾಸಗಳನ್ನು ಚೆನ್ನಾಗಿ ಬಲ್ಲವನಾಗಿ ರುತ್ತಾನೆ. ನೀವು ಪರಸ್ಪರ ಒಂದೇ ವರ್ಗಕ್ಕೆ ಸೇರಿದವರು. ಆದುದರಿಂದ ನೀವು ಬಹಿರಂಗ ವಾಗಿಯೂ ಗುಪ್ತವಾಗಿಯೂ ವ್ಯಭಿಚಾರ ಮಾಡದ, ಸನ್ನಡತೆಯುಳ್ಳ ದಾಸಿಯರನ್ನು ಅವರ ಪಾಲಕರ ಅನುಮತಿಯಿಂದ ವಿವಾಹ ಮಾಡಿಕೊಳ್ಳಿರಿ. ಮತ್ತು ಉತ್ತಮ ರೀತಿಯಲ್ಲಿ ಅವರ ವಿವಾಹ ಮೌಲ್ಯವನ್ನು ಅವರಿಗೆ ಸಲ್ಲಿಸಿರಿ. ವಿವಾಹದ ನಂತರ ಅವರು ವ್ಯಭಿಚಾರಕ್ಕಿಳಿದರೆ, ಅವರಿಗೆ ಸ್ವತಂತ್ರ ಸ್ತ್ರೀಯರಿಗೆ ನಿಶ್ಚಯಿಸಲ್ಪ ಟ್ಟಿರುವ ಶಿಕ್ಷೆಯ ಅರ್ಧಾಂಶ ಶಿಕ್ಷೆಯ ವಿಧಿ ಇದೆ. ಇದು (ಗುಲಾಮಳ ವಿವಾಹಕ್ಕೆ ಅನುಮತಿಯು) ನಿಮ್ಮ ಪೈಕಿ, ವಿವಾಹವಾಗದಿರುವ ಕಾರಣದಿಂದ ಅನೈತಿಕಕ್ಕಿಳಿಯುವ ಭಯ ಇರುವವರಿಗಾಗಿದೆ. ಆದರೆ ನೀವು ತಾಳ್ಮೆ ವಹಿಸಿದರೆ ನಿಮಗೆ ಇನ್ನಷ್ಟು ಉತ್ತಮ. ಅಲ್ಲಾಹ್ ಕ್ಷಮಿಸುವಾತನೂ ದಯಾ ನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,26,'4:26','ಅಲ್ಲಾಹ್ ನಿಮಗಿಂತ ಮುಂಚಿನವರ ಉತ್ತಮ ಸಂಪ್ರದಾಯಗಳನ್ನು ನಿಮಗೆ ವಿವರಿಸಿ ಕೊಡಲಿಕ್ಕೂ ಅವುಗಳಂತೆ ನಿಮ್ಮನ್ನು ನಡೆಸಲಿಕ್ಕೂ ನಿಮ್ಮ ಪಶ್ಚಾತ್ತಾಪವನ್ನು ಸ್ವೀಕರಿಸಲಿಕ್ಕೂ ಉದ್ದೇ ಶಿಸುತ್ತಾನೆ. ಅಲ್ಲಾಹ್ ಸರ್ವಜ್ಞನೂ ಮಹಾಧೀ ಮಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,27,'4:27','ಅಲ್ಲಾಹನು ನಿಮ್ಮ ಪಶ್ಚಾತ್ತಾಪವನ್ನು ಸ್ವೀಕರಿಸಲು ಉದ್ದೇಶಿಸುತ್ತಾನೆ. ಆದರೆ ತನ್ನ ದೇಹೇಚ್ಛೆಗಳನ್ನು ಹಿಂಬಾಲಿಸುವವರು, ನೀವು ಸನ್ಮಾರ್ಗವನ್ನು ಬಿಟ್ಟು ಬಹುದೂರ ತೊಲಗಬೇಕೆಂದು ಇಚ್ಚಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(4,28,'4:28','ಅಲ್ಲಾಹ್ ನಿಮಗೆ ನಿಯಮಗಳನ್ನು ಹಗುರ ಗೊಳಿಸ ಬೇಕೆಂದು ಉದ್ದೇಶಿಸುತ್ತಾನೆ. ಏಕೆಂದರೆ ಮನುಷ್ಯ ನನ್ನು ದುರ್ಬಲನಾಗಿ ಸೃಷ್ಟಿಸಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(4,29,'4:29','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ಪರಸ್ಪರ ಸಂತೃ ಪ್ತಿಯೊಂದಿಗೆ ನಡೆಸುವ ವ್ಯಾಪಾರ ಮೂಲಕ ವಲ್ಲದೆ ನಿಮ್ಮ ಸ್ವತ್ತುಗಳನ್ನು ಅನ್ಯಾಯವಾಗಿ ನೀವು ಪರಸ್ಪರ ತಿನ್ನದಿರಿ. ನಿಮ್ಮನ್ನು ನೀವೇ ವಧಿಸಿಕೊಳ್ಳಬೇಡಿರಿ. ಅಲ್ಲಾಹು ನಿಮ್ಮ ಮೇಲೆ ಕೃಪೆಯುಳ್ಳವನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(4,30,'4:30','ಯಾರಾದರೂ ಅಕ್ರಮ, ಅತಿರೇಕದಿಂದ ಹಾಗೆ ಮಾಡಿದರೆ ಅವನನ್ನು ನಾವು ಖಂಡಿತ ನರಕಾ ಗ್ನಿಗೆ ತಳ್ಳಿ ಬಿಡುವೆವು. ಇದು ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಲಭವಾಗಿರುವುದು.');
INSERT INTO "kannada" VALUES(4,31,'4:31','ನಿಮಗೆ ನಿಷೇಧಿಸಲಾದ ಘೋರ ಪಾಪಗಳನ್ನು ನೀವು ವರ್ಜಿಸುತ್ತಲಿದ್ದರೆ, ನಿಮ್ಮ ಚಿಕ್ಕ ಪುಟ್ಟ ಅಪರಾಧಗಳನ್ನು ನಾವು ಅಳಿಸಿ ಹಾಕುವೆವು . ಮತ್ತು ನಿಮ್ಮನ್ನು ಗೌರವಪೂರ್ಣ ಸ್ಥಾನಕ್ಕೆ ಪ್ರವೇಶಗೊಳಿಸುವೆವು.');
INSERT INTO "kannada" VALUES(4,32,'4:32','ನಿಮ್ಮಲ್ಲಿ ಕೆಲವರಿಗೆ ಬೇರೆ ಕೆಲವರಿಗಿಂತ ಅಲ್ಲಾಹನು ಹೆಚ್ಚು ಕೊಟ್ಟಿರುವುದಕ್ಕೆ ದುರಾಸೆ ಪಡಬೇಡಿರಿ. ಪುರುಷರು ಮಾಡಿದುದರ ಫಲ ಅವರಿಗೆ ಇದೆ. ಸ್ತ್ರೀಯರು ಮಾಡಿದುದರ ಫಲ ಅವರಿಗೂ ಇದೆ. ಅಲ್ಲಾಹನಲ್ಲಿ ಅವನ ಅನುಗ್ರಹದಿಂದ ಬೇಡಿರಿ. ಅಲ್ಲಾಹನು ಎಲ್ಲ ಕಾರ್ಯಗಳ ಬಗ್ಗೆ ಖಂಡಿತ ವಾಗಿಯೂ ಚೆನ್ನಾಗಿ ಅರಿಯುವವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,33,'4:33','ಪ್ರತಿಯೊಬ್ಬರಿಗೂ ಅವರು ಮಾತಾಪಿತರೂ ಸಮೀಪದ ಬಂಧುಗಳೂ ಬಿಟ್ಟುಹೋದ ಸೊತ್ತುಗಳನ್ನು ನೀಡಬೇಕಾದ ಹಕ್ಕುದಾರರನ್ನು ನಾವು ನಿಶ್ಚಯಿಸಿರುತ್ತೇವೆ. ನೀವು ವಚನ ಕೊಟ್ಟವರಿದ್ದರೆ ಅವರ ಪಾಲನ್ನು ಅವರಿಗೆ ಕೊಡಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಸಕಲ ವಸ್ತುಗಳ ಬಗೆಗೆ ಪ್ರತ್ಯಕ್ಷಜ್ಞಾನಿಯಾಗಿ ರುವನು.');
INSERT INTO "kannada" VALUES(4,34,'4:34','ಪುರುಷರು ಸ್ತ್ರೀಯರ ಮೇಲ್ವಿಚಾರಕರಾಗಿದ್ದಾರೆ. ಅವರಲ್ಲಿ ಕೆಲವರನ್ನು ಕೆಲವರಿಗಿಂತ ಶ್ರೇಷ್ಠತೆ ಗೊಳಿಸಿದುದರಿಂದಲೂ ಪುರುಷರು ತಮ್ಮ ಸಂಪತ್ತನ್ನು ಖರ್ಚು ಮಾಡುವುದರಿಂದಲೂ (ಹೀಗೆ ನಿಶ್ಚಯಿಸಲಾಗಿದೆ) . ಆದ್ದರಿಂದ ಭಕ್ತೆಯರಾದ ಸ್ತ್ರೀಯರೆಂದರೆ ಅನುಸರಣೆ ಯುಳ್ಳವರೂ ಪತಿಯ ಅನುಪಸ್ಥಿತಿಯಲ್ಲಿ ಅಲ್ಲಾಹನು ರಕ್ಷಣೆಯ ಹೊಣೆಯನ್ನು ಪತಿಯರಿಗೆ ಹೊರಿಸಿದಂತೆಯೇ ಪತಿಯ ಹಕ್ಕನ್ನು ಕಾಯ್ದುಕೊಳ್ಳುವವರೂ ಆಗಿರುತ್ತಾರೆ. ನಿಮ್ಮ ಸ್ತ್ರೀಯರಿಂದ ಆಜ್ಞೋಲ್ಲಂಘನೆಯ ಆಶಂಕೆ ನಿಮಗಿದ್ದರೆ ನೀವು ಅವರಿಗೆ ಉಪದೇಶ ಕೊಡಿರಿ. (ಅದು ಫಲಿಸದಿದ್ದರೆ) ಮಲಗುವಲ್ಲಿ ಅವರನ್ನು ಬಿಟ್ಟು ದೂರವಿರಿ. (ಅದೂ ಫಲಕಾರಿಯಾಗದಿದ್ದರೆ) ಅವರಿಗೆ ಹೊಡೆಯಿರಿ. ಹಾಗೆ ಅವರು ನಿಮಗೆ ಅನುಸರಿಸಿದರೆ ಆಮೇಲೆ ಅವರ ಮೇಲೆ (ತಪ್ಪು ಹೊರಿಸಲು) ಬೇರೆ ಮಾರ್ಗ ಹುಡುಕ ಬೇಡಿರಿ. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಮಹಾ ಮಹಿಮನೂ ಪರಮೋನ್ನತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,35,'4:35','ಪತಿ ಪತ್ನಿಯರ ನಡುವೆ ಒಡಕು ಮೂಡುವುದಾಗಿ ನೀವು ಭಯಪಟ್ಟರೆ ಅವನ ಕುಟುಂಬದಿಂದ ಒಬ್ಬ ಮಧ್ಯಸ್ಥನನ್ನೂ ಅವಳ ಬಂಧುಗಳಿಂದ ಒಬ್ಬ ಮಧ್ಯಸ್ಥನನ್ನೂ ನಿಯುಕ್ತಿಗೊಳಿಸಿರಿ. ಎರಡೂ ಕಡೆಯವರು ಸಾಮರಸ್ಯವನ್ನು ಉದ್ದೇಶಿಸುವು ದಾದರೆ ಅಲ್ಲಾಹು ಅವರ ನಡುವೆ ಸಾಮರಸ್ಯದ ಹಾದಿಯನ್ನು ತೆರೆಯುವನು. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಸರ್ವಜ್ಞನೂ ಮಹಾ ಸೂಕ್ಷ್ಮಜ್ಞಾನಿ ಯೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(4,36,'4:36','ನೀವು ಅಲ್ಲಾಹನಿಗೆ ಇಬಾದತ್ ಮಾಡಿರಿ. ಅವನೊಂದಿಗೆ ಯಾರನ್ನೂ ಸಹಭಾಗಿಯನ್ನಾಗಿ ಮಾಡಬೇಡಿರಿ. ಮಾತಾಪಿತರೊಂದಿಗೆ ಸೌಜನ್ಯದಿಂದ ವರ್ತಿಸಿರಿ. ಸಂಬಂಧಿಕರೊಂದಿಗೂ, ತಬ್ಬಲಿಗಳೊಂದಿಗೂ, ಬಡವರಿಗೊಂದಿಗೂ ಬಂಧುಗಳಾದ ನೆರಹೊರೆಯವರೊಂದಿಗೂ, ಅನ್ಯ ನೆರೆಹೊರೆಯವರೊಂದಿಗೂ, ಪಕ್ಕದ ಸಂಗಾತಿ ಯೊಂದಿಗೂ, ಸಂಚಾರನಿರತರೊಂದಿಗೂ ಹಾಗೂ ನಿಮ್ಮ ಸ್ವಾಧೀನದಲ್ಲಿರುವ ದಾಸರೊಂದಿಗೂ (ಉತ್ತಮವಾಗಿ ವ್ಯವಹರಿಸಿರಿ). ಖಂಡಿತವಾಗಿ ಯೂ ದುರಹಂಕಾರ ಹೊಂದಿರುವ ಗರ್ವಿಷ್ಟನನ್ನೂ ಹೆಮ್ಮೆಪಡುವವನನ್ನೂ ಅಲ್ಲಾಹ್ ಮೆಚ್ಚುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(4,37,'4:37','ಸ್ವತಃ ಲೋಭ ತೋರುತ್ತಾ ಜನರಿಗೆ ಲೋಭತ ನವನ್ನು ಬೋಧಿಸುವವರಿಗೂ ಅಲ್ಲಾಹು ತನ್ನ ಅನುಗ್ರಹದಿಂದ ಅವರಿಗೆ ಕೊಟ್ಟದ್ದನ್ನು ಬಚ್ಚಿಡು ವವರಿಗೂ (ಉಗ್ರ ಮುನ್ನೆಚ್ಚರಿಕೆ ನೀಡ ಲಾಗುತ್ತಿದೆ). ಕೃತಘ್ನರಿಗೆ ನಿಂದ್ಯವಾದ ಶಿಕ್ಷೆ ಯನ್ನು ನಾವು ಸಿದ್ಧಗೊಳಿಸಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(4,38,'4:38','ಜನರಿಗೆ ತೋರಿಸುವ ಸಲುವಾಗಿ ಮಾತ್ರ ತಮ್ಮ ಸಂಪತ್ತನ್ನು ಖರ್ಚು ಮಾಡುವವರಿಗೂ ಕೂಡಾ. ವಾಸ್ತವದಲ್ಲಿ ಅವರು ಅಲ್ಲಾಹುವಿನಲ್ಲಿ ಮತ್ತು ಪರಲೋಕದಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸುವುದಿಲ್ಲ. ಯಾರ ಸಂಗಾತಿ ಶೈತಾನನಾಗಿರುತ್ತಾನೋ ಅವನು ದುಷ್ಟ ಸಂಗಾತಿಯೇ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,39,'4:39','ಅಲ್ಲಾಹು ಮತ್ತು ಪರಲೋಕದಲ್ಲಿ ವಿಶ್ವಾಸವಿಡುತ್ತಿ ದ್ದರೆ ಹಾಗೂ ಅಲ್ಲಾಹು ಅವರಿಗೆ ನೀಡಿದುದರಿಂದ ಖರ್ಚು ವೆಚ್ಚ ಮಾಡುವುದಾದರೆ ಅವರಿಗೇನು ತೊಂದರೆಯಿತ್ತು? ಅವರ ಬಗ್ಗೆ ಅಲ್ಲಾಹು ಚೆನ್ನಾಗಿ ಅರಿಯುವವನೇ ಆಗಿರುವನು. (ಆದ್ದರಿಂದ ಉತ್ತಮ ಪ್ರತಿಫಲ ನೀಡುತ್ತಿದ್ದನು)');
INSERT INTO "kannada" VALUES(4,40,'4:40','ವಾಸ್ತವದಲ್ಲಿ ಅಲ್ಲಾಹನು ಖಂಡಿತ ಅಣು ತೂಕ ದಷ್ಟು ಕೂಡಾ ಅಕ್ರಮವೆಸಗುವುದಿಲ್ಲ. ಯಾರಾದ ರೂ ಒಂದು ಪುಣ್ಯ ಕಾರ್ಯ ಮಾಡಿದರೆ ಅದನ್ನ ವನು ಇಮ್ಮಡಿಗೊಳಿಸುತ್ತಾನೆ. ಮತ್ತು ತನ್ನ ವತಿ ಯಿಂದ ಹಿರಿದಾದ ಪ್ರತಿಫಲ ವನ್ನು ಕೊಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,41,'4:41','ಪ್ರತಿಯೊಂದು ಸಮುದಾಯದಿಂದ ನಾವು ಒಂ ದೊಂದು ಸಾಕ್ಷಿಯನ್ನು ಹಾಜರುಪಡಿಸುವ ಮತ್ತು (ಪ್ರವಾದಿಯವರೇ!) ನಿಮ್ಮನ್ನು ನಾವು ಅವರ ಮೇಲೆ ಸಾಕ್ಷಿಯನ್ನಾಗಿ ಹಾಜರುಪಡಿಸುವ ಸಂದರ್ಭ (ಅವಿಶ್ವಾಸಿಗಳ ಪರಿಸ್ಥಿತಿ) ಹೇಗಿರ ಬಹುದು? ');
INSERT INTO "kannada" VALUES(4,42,'4:42','ಸತ್ಯನಿಷೇಧಿಸಿದವರು ಹಾಗೂ ಸಂದೇಶವಾಹಕರ ಆಜ್ಞೋಲ್ಲಂಘನೆ ಮಾಡಿದವರು, ಆ ಸಂದರ್ಭದಲ್ಲಿ ತಮ್ಮನ್ನು ಭೂಮಿಗೆ ಮಣ್ಣು ಮಾಡಿ ಸಮತಟ್ಟು ಗೊಳಿಸಿದ್ದರೆ ಚೆನ್ನಾಗಿತ್ತೆಂದು ಆಸೆಪಡುವರು. ಅವರು ಅಲ್ಲಿ ಯಾವುದನ್ನೂ ಅಲ್ಲಾಹನಿಂದ ಅಡಗಿಸಲು ಶಕ್ತರಾಗಲಾರರು.');
INSERT INTO "kannada" VALUES(4,43,'4:43','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ಅಮಲಿನಲ್ಲಿದ್ದರೆ ನೀವು ಹೇಳುತ್ತಿರುವುದೇನೆಂದು ಪ್ರಜ್ಞೆ ನಿಮಗೆ ಬರುವವರೆಗೂ ಮತ್ತು ನೀವು ಜನಾಬತ್‍ನಲ್ಲಿದ್ದು ದಾರಿಹೋಕನಲ್ಲದಿದ್ದರೆ ಸ್ನಾನ ಮಾಡು ವವರೆಗೂ ನಮಾಝ್‍ಗೆ ಸಮೀಪಿಸಬೇಡಿರಿ. ಇನ್ನು ನೀವು ರೋಗಿಗಳಾದರೆ ಅಥವಾ ಪ್ರಯಾಣದಲ್ಲಿದ್ದರೆ ಅಥವಾ ಬಹಿರ್ದೆಸೆ ಮುಗಿಸಿ ಬಂದಿದ್ದರೆ ಅಥವಾ ನೀವು ಸ್ತ್ರೀ ಸ್ಪರ್ಶ ಮಾಡಿದ್ದು ನಿಮಗೆ ನೀರು ಸಿಗದಿದ್ದಲ್ಲಿ ಶುದ್ಧ ಮಣ್ಣನ್ನು ತೆಗೆದುಕೊಳ್ಳಿರಿ. ಹಾಗೂ ನಿಮ್ಮ ಮುಖಗಳಿಗೂ ಕೈಗಳಿಗೂ ಸವರಿರಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಬಹಳ ಮಾಪಿ ಕೊಡುವವನೂ ಬಹಳ ಹೆಚ್ಚು ಮನ್ನಿಸುವವನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(4,44,'4:44','ವೇದಗ್ರಂಥದ ಭಾಗ್ಯ ನೀಡಲ್ಪಟ್ಟಿದ್ದವರನ್ನು ನೀವು ನೋಡಿದಿರಾ? ಅವರು ಪಥಭ್ರಷ್ಟತೆಯನ್ನು ಖರೀದಿಸಿಕೊಳ್ಳುವವರಾಗಿದ್ದಾರೆ. ಅಲ್ಲದೆ ನೀವೂ ಪಥಭ್ರಷ್ಟರಾಗಬೇಕೆಂದು ಬಯಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(4,45,'4:45','ಅಲ್ಲಾಹನು ನಿಮ್ಮ ಶತ್ರುಗಳನ್ನು ಚೆನ್ನಾಗಿ ಅರಿತಿರುವನು. ನಿಮ್ಮ ಸಂರಕ್ಷಣೆಗೆ ಅಲ್ಲಾಹುಸಾಕು. ನಿಮ್ಮ ಸಹಾಯಕ್ಕೆ ಅಲ್ಲಾಹನು ಸಾಕು.');
INSERT INTO "kannada" VALUES(4,46,'4:46','ಯಹೂದಿಯರಲ್ಲಿ ಕೆಲವರು ವೇದದ ಮಾತುಗಳನ್ನು ಅವುಗಳ ಸ್ಥಾನದಿಂದ ಬದಲಾಯಿಸುವವರಿದ್ದಾರೆ. “ನಾವು ಆಲಿಸಿದೆವು, ಆದರೆ ತಿರಸ್ಕರಿಸಿದೆವು. ನಿನಗೆ ಕೇಳಿಸದೆಯೇ ಇರಲಿ’ ಎಂದೂ ಧರ್ಮನಿಂದನೆ ಹಾಗೂ ನಾಲಗೆಯಿಂದ ವ್ಯಂಗ್ಯೋಕ್ತಿಯಾಗಿ ‘ರಾಇನಾ’ (ನಮ್ಮನ್ನು ಗಮನಹರಿಸಿರಿ) ಎಂದೂ ಅವರು (ನಬಿಯವರಲ್ಲಿ) ಹೇಳುತ್ತಾರೆ. ಆದರೆ ‘ನಾವು ಆಲಿಸಿದ್ದೇವೆ. ಅನುಸರಿಸಿದ್ದೇವೆ. ನೀವು ಕೇಳಿರಿ, ಹಾಗೂ ‘ಉನ್‍ಳುರ್ನಾ’ (ನಮ್ಮ ಕಾರ್ಯದಲ್ಲಿ ಗಮನಹರಿಸಿರಿ) ಎಂದು ಅವರು ಹೇಳುತ್ತಿದ್ದರೆ ಅದು ಅವರಿಗೆ ಗುಣಕರವೂ ನೀತಿಯುಕ್ತವೂ ಆಗುತ್ತಿತ್ತು. ಆದರೆ ಅವರ ಸತ್ಯನಿಷೇಧದ ಫಲವಾಗಿ ಅಲ್ಲಾಹು ಅವರನ್ನು ಶಪಿಸಿದ್ದಾನೆ. ಆದ್ದರಿಂದ ಅವರ ಪೈಕಿ ಸ್ವಲ್ಪ ಜನ ಮಾತ್ರವೇ ವಿಶ್ವಾಸವಿಡುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(4,47,'4:47','ಓ ಗ್ರಂಥ ನೀಡಲ್ಪಟ್ಟವರೇ, ನಿಮ್ಮ ಬಳಿ ಮೊದಲೇ ಇದ್ದ ಗ್ರಂಥವನ್ನು ಸಮರ್ಥಿಸುತ್ತಾ ನಾವು ಅವತೀರ್ಣಗೊಳಿಸಿದುದರಲ್ಲಿ (ಖುರ್‍ಆನಿನಲ್ಲಿ) ನೀವು ವಿಶ್ವಾಸ ತಾಳಿರಿ. ನಾವು ಮುಖಗಳನ್ನು (ಕಣ್ಣು, ಮೂಗುಗಳನ್ನು ಸವರಿ) ಸಮತಟ್ಟು ಗೊಳಿಸಿ ನೇರ ಹಿಂದಕ್ಕೆ ತಿರುಗಿಸುವ ಮುನ್ನ . ಅಥವಾ ಶನಿವಾರದವರನ್ನು ಶಪಿಸಿರುವಂತೆ ಅವರನ್ನು ನಾವು ಶಪಿಸುವ ಮುನ್ನ. ಅಲ್ಲಾಹು ವಿನ ತೀರ್ಮಾನ ಬಳಕೆಗೆ ಬಂದೇ ತೀರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(4,48,'4:48','ತನಗೆ ಇತರರನ್ನು ದೈವಿಕ ಸಹಭಾಗಿಗಳನ್ನಾಗಿ ಮಾಡುವುದನ್ನು ಅಲ್ಲಾಹನು ಖಂಡಿತ ಕ್ಷಮಿಸುವುದಿಲ್ಲ. ಅದಲ್ಲದ ಪಾಪಗಳನ್ನು ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಕ್ಷಮಿಸುವನು. ಯಾವನು ಅಲ್ಲಾಹನೊಂದಿಗೆ ಇತರರನ್ನು ದೈವಿಕ ಸಹಭಾಗಿಯನ್ನಾಗಿ ಮಾಡಿದನೋ ಖಂಡಿತವಾ ಗಿಯೂ ಅವನು ಭಯಂಕರವಾದ ಪಾಪವನ್ನು ಸೃಷ್ಟಿಸಿಕೊಂಡನು ');
INSERT INTO "kannada" VALUES(4,49,'4:49','ಆತ್ಮಪ್ರಶಂಸೆ ಮಾಡಿಕೊಳ್ಳುವವರನ್ನು ನೀವು ಕಂಡಿಲ್ಲವೇ ? ಹಾಗಲ್ಲ ; ತಾನುದ್ದೇಶಿಸಿದವರನ್ನು ಪರಿಶುದ್ಧಗೊಳಿಸುವುದು ಅಲ್ಲಾಹು. ಅವರಿಗೆ ಎಳ್ಳಷ್ಟೂ ದ್ರೋಹ ಮಾಡಲಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(4,50,'4:50','ಅವರು ಅಲ್ಲಾಹನ ಮೇಲೆ ಹೇಗೆ ಸುಳ್ಳಾರೋಪ ಸೃಷ್ಟಿಸುತ್ತಾರೆಂಬುದನ್ನು ನೋಡು ! ಪ್ರತ್ಯಕ್ಷ ಪಾಪಕ್ಕೆ ಇದೊಂದೇ ಸಾಕು !');
INSERT INTO "kannada" VALUES(4,51,'4:51','ವೇದಗ್ರಂಥದ ಭಾಗ್ಯ ನೀಡಲ್ಪಟ್ಟವರನ್ನು ಕಂಡೆಯಾ? ಅವರು ಜಿಬ್‍ತ್ ಮತ್ತು ತಾಗೂತ್‍ಗಳಲ್ಲಿ ನಂಬಿಕೆ ಇಡುತ್ತಾರೆ. ‘ಮುಅïಮಿನ್‍ಗಳಿಗಿಂತ ಹೆಚ್ಚು ಸನ್ಮಾರ್ಗ ಪ್ರಾಪಿಸಿದವರು ನೀವೇ’ ಎಂದು ಸತ್ಯನಿಷೇಧಿ ಗಳೊಂದಿಗೆ ಹೇಳುತ್ತಾರೆ !');
INSERT INTO "kannada" VALUES(4,52,'4:52','ಅಲ್ಲಾಹನು ಶಪಿಸಿರುವುದು ಇವರನ್ನೇ. ಯಾವನಿಗೆ ಅಲ್ಲಾಹನ ಶಾಪ ತಟ್ಟಿತೋ ಅವನಿಗೆ ನೆರವಾಗು ವವನನ್ನು ನೀನು ಕಾಣಲಾರೆ.');
INSERT INTO "kannada" VALUES(4,53,'4:53','ಏನು ರಾಜಾಧಿಕಾರದಲ್ಲಿ ಏನಾದರೊಂದು ಪಾಲು ಅವರಿಗಿದೆಯೇ ? ಹಾಗಿದ್ದರೆ ಜನರಿಗೆ ಎಳ್ಳಷ್ಟನ್ನೂ ಅವರು ಕೊಡುತ್ತಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(4,54,'4:54','ಮತ್ತೇನು ಅಲ್ಲಾಹನು ಇತರರಿಗೆ ತನ್ನ ಔದಾರ್ಯದಿಂದ ಕೊಟ್ಟಿದ್ದಕ್ಕೆ ಅವರ ಮೇಲೆ ಇವರು ಅಸೂಯೆ ಪಡುತ್ತಾರೋ? ಖಂಡಿತವಾ ಗಿಯೂ ನಾವು ಇಬ್‍ರಾಹೀಮರ ಮನೆತನದ ವರಿಗೆ ಗ್ರಂಥ ಮತ್ತು ತತ್ವಜ್ಞಾನವನ್ನು ಕೊಟ್ಟಿದ್ದೇವೆ ಮತ್ತು ಘನವೆತ್ತ ಅಧಿಪತ್ಯವನ್ನೂ ಅವರಿಗೆ ನಾವು ಕರುಣಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(4,55,'4:55','ಅವರ ಸಂತತಿಯಿಂದ (ಪ್ರವಾದಿಯವರಲ್ಲಿ) ವಿಶ್ವಾಸವಿರಿಸಿದವರೂ ಅವರಿಂದ ವಿಮುಖ ರಾದವರೂ ಇದ್ದಾರೆ. ಹೊತ್ತಿ ಉರಿಯಲು ನರಕ ಸಾಕು!');
INSERT INTO "kannada" VALUES(4,56,'4:56','ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳನ್ನು ನಿಷೇಧಿಸಿದವರನ್ನು ಖಂಡಿತವಾಗಿಯೂ ನಾವು ಮುಂದೆ ನರಕದಲ್ಲಿ ಪ್ರವೇಶಗೊಳಿಸುವೆವು. ಅವರ ಚರ್ಮಗಳು ಸುಟ್ಟು ಕರಿದಾಗಲೆಲ್ಲ ಅದೇ ಜಾಗದಲ್ಲಿ ಬೇರೆ ಚರ್ಮಗಳನ್ನು ಅವರಿಗೆ ನಾವು ಬದಲಿಸುತ್ತಾ ಇರುವೆವು. ಅವರು ಶಿಕ್ಷೆಯ ರುಚಿ ನೋಡು ತ್ತಿರಲೆಂದು. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಪ್ರತಾಪಶಾಲಿಯೂ ಯುಕ್ತಿವಂತನೂ ಆಗಿರುತ್ತಾನೆ.’');
INSERT INTO "kannada" VALUES(4,57,'4:57','ಸತ್ಯವಿಶ್ವಾಸ ತಾಳಿ ಸತ್ಕಾರ್ಯಗಳನ್ನು ಕೈಗೊಂಡವರನ್ನು ಮುಂದೆ ನಾವು ಕೆಳಗಡೆ ನದಿಗಳು ಹರಿಯುತ್ತಿರುವ ಸ್ವರ್ಗಧಾಮಗಳಲ್ಲಿ ಪ್ರವೇಶಗೊಳಿಸುವೆವು. ಅದರಲ್ಲಿ ಅವರು ಎಂದೆಂದಿಗೂ ಶಾಶ್ವತ ನಿವಾಸಿಗಳಾಗಿರುವರು. ಅಲ್ಲಿ ಅವರಿಗೆ ಪರಿಶುದ್ಧೆಯರಾದ ಸಂಗಾತಿಯರಿದ್ದಾರೆ. ನಾವು ಅವರನ್ನು ನಿತ್ಯ ನೆರಳಲ್ಲಿ ಪ್ರವೇಶಗೊಳಿಸುವೆವು.');
INSERT INTO "kannada" VALUES(4,58,'4:58','ಅಮಾನತ್ತುಗಳನ್ನು ಅವುಗಳ ಹಕ್ಕುದಾರರಿಗೆ ಒಪ್ಪಿಸಬೇಕೆಂದೂ ನೀವು ಜನರೆಡೆಯಲ್ಲಿ ವಿಧಿಸಿದರೆ ನ್ಯಾಯಬದ್ಧವಾಗಿ ವಿಧಿಸಬೇಕೆಂದೂ ಅಲ್ಲಾಹನು ನಿಮಗೆ ಆಜ್ಞೆ ವಿಧಿಸುತ್ತಾನೆ . ಅಲ್ಲಾಹು ನಿಮಗೆ ಅತ್ಯುತ್ತಮ ಉಪದೇಶವನ್ನೀ ಯುತ್ತಾನೆ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹು ಸರ್ವಶ್ರುತನೂ ಸರ್ವ ವೀಕ್ಷಕನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,59,'4:59','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನೀವು ಅಲ್ಲಾಹನನ್ನು ಅನುಸರಿಸಿರಿ. ಅಲ್ಲಾಹುವಿನ ರಸೂಲರನ್ನು ಹಾಗೂ ನಿಮ್ಮಲ್ಲಿನ ಆದೇಶಾಧಿಕಾರಿಗಳನ್ನೂ ಅನುಸರಿಸಿರಿ. ಇನ್ನು ಏನಾದರೊಂದು ವಿಷಯದಲ್ಲಿ ನಿಮ್ಮ ಮಧ್ಯೆ ಅಭಿಪ್ರಾಯ ಬೇಧ ತಲೆದೋರಿದರೆ ಅದನ್ನು ಅಲ್ಲಾಹನು ಮತ್ತು ರಸೂಲರ ಕಡೆಗೆ ಹೊರಳಿಸಿರಿ. ನೀವು ಅಲ್ಲಾಹು ಮತ್ತು ಪರಲೋಕದಲ್ಲಿ ನಂಬಿಕೆ ಉಳ್ಳವರಾಗಿದ್ದರೆ. ಅದುವೇ ಅತ್ಯಂತ ಹಿತಕರವೂ ಉತ್ತಮ ಅಂತ್ಯ ಫಲವನ್ನು ತರುವುದೂ ಆಗಿದೆ.');
INSERT INTO "kannada" VALUES(4,60,'4:60','(ಓ ಪೈಗಂಬರರೇ,) ನಿಮ್ಮ ಮೇಲೆ ಅವತೀರ್ಣ ಗೊಳಿಸಲ್ಪಟ್ಟ ಗ್ರಂಥದ ಮೇಲೂ ನಿಮಗಿಂತ ಮೊದಲು ಅವತೀರ್ಣಗೊಳಿಸಲ್ಪಟ್ಟ ಗ್ರಂಥಗಳ ಮೇಲೂ ನಾವು ವಿಶ್ವಾಸವಿಟ್ಟಿದ್ದೇವೆ ಎಂದು ವಾದಿಸುವ ಒಂದು ತಂಡವನ್ನು ನೀವು ಕಂಡಿಲ್ಲವೇ? `ತಾಗೂತ್’ ಬಳಿಯಿಂದ ತೀರ್ಪು ಪಡೆಯಲು ಅವರು ಉದ್ದೇಶಿಸುತ್ತಾರೆ. ವಾಸ್ತವದಲ್ಲಿ ಅದನ್ನು ನಿಷೇಧಿಸಬೇಕೆಂದು ಅವರ ಮೇಲೆ ಆಜ್ಞಾಪಿಸಲಾಗಿದೆ. ಶೈತಾನನು ಅವರನ್ನು ದಾರಿ ತಪ್ಪಿಸಿ ಸತ್ಯದಿಂದ ಬಲು ದೂರ ಕೊಂಡೊಯ್ಯಲು ಉದ್ದೇಶಿಸುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(4,61,'4:61','ಅಲ್ಲಾಹನು ಅವತೀರ್ಣಗೊಳಿಸಿದುದರ ಕಡೆಗೆ ಬನ್ನಿರಿ ಮತ್ತು ಸಂದೇಶವಾಹಕರ ಕಡೆಗೆ ಬನ್ನಿರಿ ಎಂದು ಅವರಿಗೆ ಹೇಳಲಾದಾಗ ಆ ಕಪಟ ವಿಶ್ವಾಸಿಗಳು ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವುದರಿಂದ ತೀವ್ರವಾಗಿ ಜಾರಿಕೊಳ್ಳುತ್ತಿರುವುದನ್ನು ನೀವು ಕಾಣುವಿರಿ.');
INSERT INTO "kannada" VALUES(4,62,'4:62','ಆದರೆ ಅವರ ಕೈಗಳು ಮಾಡಿದ (ಪಾಪಗಳ) ಫಲವಾಗಿ ಅವರಿಗೇನಾದರೂ ವಿಪತ್ತು ಸಂಭವಿಸಿ ಆ ಬಳಿಕ ಅವರು ತಮ್ಮ ಬಳಿ ಬಂದು “ನಾವು ಒಳಿತು ಮತ್ತು ಸಾಮರಸ್ಯವನ್ನಲ್ಲದೆ ಬೇರೇನನ್ನೂ ಉದ್ದೇಶಿಸಿರಲಿಲ್ಲ” ಎಂದು ಅಲ್ಲಾಹನ ಹೆಸರಲ್ಲಿ ಆಣೆ ಹಾಕುತ್ತಾ ಹೇಳುವ ಸಂದರ್ಭದಲ್ಲಿ ಅವರ ಸ್ಥಿತಿ ಏನಾದೀತು ?');
INSERT INTO "kannada" VALUES(4,63,'4:63','ಅವರ ಹೃದಯಗಳಲ್ಲಿರುವುದನ್ನು ಅಲ್ಲಾಹು ಅರಿಯುತ್ತಾನೆ. ಆದ್ದರಿಂದ ಅವರನ್ನು ಅವಗಣಿಸಿರಿ. ಅವರಿಗೆ ಉಪದೇಶಿಸಿರಿ ಮತ್ತು ಅವರಿಗೆ ಮನ ಮುಟ್ಟುವ ಸಾರೋಪದೇಶ ನೀಡಿರಿ.');
INSERT INTO "kannada" VALUES(4,64,'4:64','ಅಲ್ಲಾಹನ ಆಜ್ಞೆ ಪ್ರಕಾರ ಅನುಸರಿಸಲ್ಪಡ ಲಿಕ್ಕಾಗಿಯೇ ಹೊರತು ಯಾವನೊಬ್ಬ ಸಂದೇಶವಾಹಕನನ್ನೂ ನಾವು ಆರಿಸಿಲ್ಲ. ತಮ್ಮ ಮೇಲೆ ತಾವೇ ಅಕ್ರಮ ವೆಸಗಿರುವ ಅವರು ಆಮೇಲೆ ನಿಮ್ಮ ಸನ್ನಿಧಿಗೆ ಬಂದು ಅಲ್ಲಾಹನಲ್ಲಿ ಕ್ಷಮಾಯಾಚನೆ ಮಾಡಿದ್ದರೆ ಮತ್ತು ರಸೂಲರೂ ಅವರಿಗಾಗಿ ಕ್ಷಮೆ ಬೇಡುತ್ತಿದ್ದರೆ, ಖಂಡಿತವಾ ಗಿಯೂ ಅಲ್ಲಾಹನು ಪಶ್ಚಾತ್ತಾಪ ಸ್ವೀಕರಿಸು ವವನೂ ದಯಾನಿಧಿಯೂ ಆಗಿರುವುದನ್ನು ಅವರು ಕಾಣುತ್ತಿದ್ದರು .');
INSERT INTO "kannada" VALUES(4,65,'4:65','(ಓ ಪ್ರವಾದಿಯರೇ,) ನಿಮ್ಮ ಪ್ರಭುವಿನಾಣೆ! ಅವರು ತಮ್ಮೊಳಗೆ ವಿವಾದಗಳಲ್ಲಿ ನಿಮ್ಮನ್ನು ತೀರ್ಪುಗಾರರಾಗಿ ಒಪ್ಪಿಕೊಂಡು ಬಳಿಕ ನೀವು ಕೊಟ್ಟ ತೀರ್ಪಿನ ಬಗೆಗೆ ತಮ್ಮ ಮನಸ್ಸಿನಲ್ಲೂ ಯಾವುದೇ ಸಂಕೋಚಪಡದೆ ಅದನ್ನು ಸರ್ವ ಸಂಪೂರ್ಣವಾಗಿ ಒಪ್ಪಿ ಕೊಳ್ಳುವವರೆಗೂ ಅವರು ಖಂಡಿತ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಾಗಲಾರರು .');
INSERT INTO "kannada" VALUES(4,66,'4:66','(ಅಲ್ಲಾಹುವಿನ ಮಾರ್ಗದಲ್ಲಿ) `ನಿಮ್ಮ ಜೀವಗ ಳನ್ನು ಬಲಿಯರ್ಪಿಸಿರಿ ಅಥವಾ ನಿಮ್ಮ ಸ್ವಂತ ಮನೆಗಳನ್ನು ತೊರೆದು ಹೋಗಿರಿ’ ಎಂದು ನಾವು ಅವರಲ್ಲಿ ಆಜ್ಞಾಪಿಸಿದ್ದರೆ ಅವರಲ್ಲಿ ಕೆಲವರು ಮಾತ್ರವೇ ಹೊರತು ಉಳಿದವರು ಅದನ್ನು ಪಾಲಿ ಸುತ್ತಿರಲಿಲ್ಲ. ಅವರಿಗೆ ಉಪದೇಶ ನೀಡಲ್ಪಡುವು ದನ್ನು ಅವರು ಮಾಡಿದ್ದಿದ್ದರೆ ಅದು ಅವರಿಗೆ ಬಹಳ ಕ್ಷೇಮಕರವೂ (ಅವರ ವಿಶ್ವಾಸವನ್ನು) ಮತ್ತಷ್ಟು ನೆಲೆಯೂರಿಸುವ ಕ್ರಮವೂ ಆಗಿರುತ್ತಿತ್ತು.');
INSERT INTO "kannada" VALUES(4,67,'4:67','ಹಾಗಿರುತ್ತಿದ್ದರೆ ನಮ್ಮ ಕಡೆಯಿಂದ ಮಹತ್ತರವಾದ ಪ್ರತಿಫಲವನ್ನು ಖಂಡಿತವಾಗಿಯೂ ನಾವು ಅವರಿಗೆ ದಯಪಾಲಿಸುತ್ತಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(4,68,'4:68','ಮತ್ತು ಅವರನ್ನು ಸರಿದಾರಿಗೆ ಖಂಡಿತ ಹಚ್ಚುತ್ತಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(4,69,'4:69','ಅಲ್ಲಾಹು ಮತ್ತು ಸಂದೇಶವಾಹಕರನ್ನು ಅನು ಸರಿಸು ವವರು (ಪರಲೋಕದಲ್ಲಿ) ಅಲ್ಲಾಹನಿಂದ ಅನುಗ್ರಹ ಹೊಂದಿದ ಪ್ರವಾದಿಗಳು, ಸಿದ್ಧೀಕರು, ಶುಹದಾಗಳು ಹಾಗೂ ಸಾಲಿಹೀನ್‍ಗಳ ಜೊತೆಯಲ್ಲಿರುವರು. ಅವರೇ ಶ್ರೇಷ್ಠ ಸಹವಾಸಿಗಳು .');
INSERT INTO "kannada" VALUES(4,70,'4:70','ಇದು ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ಒದಗುವ ಶ್ರೇಷ್ಠ ಔದಾರ್ಯವಾಗಿದೆ. ಎಲ್ಲವನ್ನೂ ಅರಿಯುವವ ನಾಗಿ ಅಲ್ಲಾಹು ಸಾಕು.');
INSERT INTO "kannada" VALUES(4,71,'4:71','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಸದಾ ಸನ್ನದ್ಧ ಸ್ಥಿತಿ ಯಲ್ಲಿರಿ. (ಸಂದರ್ಭ ಬಂದಾಗ) ಸಣ್ಣ ತಂಡಗಳಾಗಿ ಅಥವಾ ಎಲ್ಲರೂ ಒಂದಾಗಿ ಯುದ್ದಕ್ಕೆ ಹೊರಡಿರಿ.');
INSERT INTO "kannada" VALUES(4,72,'4:72','ಆದರೆ ಯುದ್ಧದಿಂದ ಖಂಡಿತ ಹಿಂಜರಿಯುವ ವನು ನಿಮ್ಮ ಕೂಟದಲ್ಲಿ ಇದ್ದಾನೆ. ನಿಮ್ಮ ಮೇಲೆ ವಿಪತ್ತೇನಾದರೂ ಬಂದರೆ; “ನಾನು ಅವರ ಜೊತೆಗೆ ಇಲ್ಲದಂತಾದುದು ಅಲ್ಲಾಹು ನನಗೆ ನೀಡಿದ ಅನುಗ್ರಹ” ಎಂದು ಹೇಳುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,73,'4:73','ಇನ್ನು ಅಲ್ಲಾಹನಿಂದ ಏನಾದರೂ ಅನುಗ್ರಹ ನಿಮಗೆ ದೊರೆತರೆ ಅವನು ಮತ್ತು ನಿಮ್ಮ ನಡುವೆ (ಈ ಮುಂಚೆ) ಯಾವುದೇ ಸ್ನೇಹ ಸಂಬಂಧ ಇಲ್ಲದಂತಹ ಭಾವದಲ್ಲಿ ಹೇಳುತ್ತಾನೆ. `ನಾನೂ ಅವರ ಜೊತೆ ಸೇರಿಕೊಂಡಿದ್ದರೆ ನನಗೂ ಮಹ ತ್ತರವಾದ ಗೆಲುವು ಸಂಪಾದಿಸಬಹುದಿತ್ತು ಎಂದು!’');
INSERT INTO "kannada" VALUES(4,74,'4:74','ಪರಲೋಕಕ್ಕೆ ಬೇಕಾಗಿ ಇಹಲೋಕದ ಬಾಳನ್ನು ಮಾರುವವರು ಅಲ್ಲಾಹುವಿನ ಮಾರ್ಗದಲ್ಲಿ ಯುದ್ಧ ಮಾಡಲಿ. ಯಾವನಾದರೂ ಅಲ್ಲಾಹುವಿನ ಮಾರ್ಗದಲ್ಲಿ ಯುದ್ಧ ನಡೆಸಿದರೆ ಅವರು ಮಡಿ ಯಲಿ ಅಥವಾ ಜೈಸಲಿ. ಅವನಿಗೆ ನಾವು ಮಹತ್ತ ರವಾದ ಪುಣ್ಯವನ್ನು ಪ್ರತಿಫಲವಾಗಿ ಕೊಡಲಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(4,75,'4:75','ಯಾಕೆ ನೀವು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಯುದ್ಧ ಮಾಡುತ್ತಿಲ್ಲ ? “ಓ ನಮ್ಮ ಪ್ರಭೂ, ಅಕ್ರಮಿಗಳು ವಾಸಿಸುವ ಈ ನಾಡಿನಿಂದ ನಮ್ಮನ್ನು ಪಾರು ಮಾಡು. ನಿನ್ನ ಕಡೆಯಿಂದ ಓರ್ವ ರಕ್ಷಾಧಿಕಾರಿ ಯನ್ನು ನಮಗೆ ನಿಯೋಗಿಸು. ನಿನ್ನ ಕಡೆ ಯಿಂದ ಓರ್ವ ಸಹಾಯಕನನ್ನು ನಮಗೆ ನೇಮಿಸು’ ಎಂದು ಬಲಹೀನರಾದ ಗಂಡಸರೂ ಹೆಂಗಸರೂ ಮಕ್ಕಳೂ ಪ್ರಾರ್ಥಿಸುತ್ತಿರುವಾಗ !');
INSERT INTO "kannada" VALUES(4,76,'4:76','ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಅಲ್ಲಾಹುವಿನ ಮಾರ್ಗದಲ್ಲಿ ಯುದ್ಧ ಮಾಡುತ್ತಾರೆ. ಅವಿಶ್ವಾಸಿಗಳು ಶೈತಾನನ ಮಾರ್ಗದಲ್ಲಿ ಯುದ್ಧ ಮಾಡುತ್ತಾರೆ. ಆದ್ದರಿಂದ ನೀವು ಶೈತಾನನ ಮಿತ್ರರ ವಿರುದ್ಧ ಯುದ್ದ ಮಾಡಿರಿ. ಖಂಡಿತವಾಗಿಯೂ ಶೈತಾನನ ಕುತಂತ್ರವು ಬಲು ದುರ್ಬಲವಾದುದು.');
INSERT INTO "kannada" VALUES(4,77,'4:77','ನಿಮ್ಮ ಕೈಗಳನ್ನು (ಯುದ್ಧ ಮಾಡದೆ) ತಡೆದು ನಿಲ್ಲಿಸಿರಿ. ನಮಾಜನ್ನು ಕ್ಲಪ್ತವಾಗಿ ನಡೆಸಿರಿ. ಝಕಾತ್ ಕೊಡಿರಿ.’ ಎಂದು ಆಜ್ಞೆ ಕೊಡಲಾಗಿದ್ದ ಜನರನ್ನು ತಾವು ನೋಡಿಲ್ಲವೇ? ಹಾಗೆ ಅವರ ಮೇಲೆ ಯುದ್ಧ ಕಡ್ಡಾಯಗೊಳಿಸಲಾದಾಗ ಅವರಲ್ಲೊಂದು ತಂಡವು ಮನುಷ್ಯರನ್ನು ಹೆದರುತ್ತಾರೆ, ಅಲ್ಲಾಹುವಿಗೆ ಹೆದರುವಂತೆ! ಅಥವಾ ಅದಕ್ಕೂ ಹೆಚ್ಚಾಗಿ ಹೆದರುತ್ತಾರೆ. `ಪ್ರಭೂ! ನೀನೇಕೆ ನಮಗೆ ಯುದ್ಧ ಕಡ್ಡಾಯಗೊಳಿಸಿದೆ? ಸ್ವಲ್ಪ ಕಾಲಾವಕಾಶವನ್ನೇಕೆ ನೀಡಲಿಲ್ಲ?”ಎಂದು ಕೇಳುತ್ತಾರೆ . ಅವರೊಡನೆ ಹೇಳಿರಿ, ಐಹಿಕ ಜೀವನದ ಬಂಡವಾಳವು ಅತ್ಯಲ್ಪವಾಗಿದೆ ಮತ್ತು ದೇವಭಯವುಳ್ಳವನಿಗೆ ಪರಲೋಕದ ಬಾಳೇ ಸರ್ವಶ್ರೇಷ್ಠ. ನಿಮಗೆ ಸ್ವಲ್ಪವೂ ಅನ್ಯಾಯ ವಾಗಲಾರದು.');
INSERT INTO "kannada" VALUES(4,78,'4:78','ನೀವು ಎಲ್ಲಿದ್ದರೂ, ಭದ್ರವಾಗಿ ನಿರ್ಮಿಸಲಾದ ಕೋಟೆಗಳೊಳಗೆ ಇದ್ದರೂ ಸಹ ಮರಣವು ನಿಮ್ಮನ್ನು ತಲುಪುತ್ತದೆ. ಅವರಿಗೇನಾದರೂ ಒಳಿತು ದೊರೆತರೆ “ಇದು ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ” ಎನ್ನುತ್ತಾರೆ. ನಷ್ಟವುಂಟಾದರೆ, ಇದು ನಿಮ್ಮಿಂದಾಗಿ (ನಬಿಯವರಿಂದಾಗಿ) ಉಂಟಾಗಿರುತ್ತದೆ ಎನ್ನುತ್ತಾರೆ. ನೀವು ಹೇಳಿರಿ; ಎಲ್ಲವೂ ಅಲ್ಲಾಹುವಿನ ಕಡೆ ಯಿಂದಲೇ ! ಈ ಜನರಿಗೇನಾಗಿದೆ? ಯಾವುದೇ ವರ್ತಮಾನ ವನ್ನು ಇವರು ಗ್ರಹಿಸಿಕೊಳ್ಳುವತ್ತ ತಲುಪುವುದೇ ಇಲ್ಲವಲ್ಲ ?');
INSERT INTO "kannada" VALUES(4,79,'4:79','(ಓ ಮಾನವಾ!) ನಿನಗೊದಗುವ ಪ್ರತಿಯೊಂದು ಒಳಿತೂ ಅಲ್ಲಾಹನ ಕೃಪೆಯಿಂದ ಒದಗುತ್ತದೆ ಮತ್ತು ನಿನಗೊದಗುವ ಪ್ರತಿಯೊಂದು ವಿಪತ್ತೂ ನಿನ್ನ ಕರ್ಮದಿಂದಾಗಿರುತ್ತದೆ. (ಪ್ರವಾದಿಯ ವರೇ!) ನಿಮ್ಮನ್ನು ನಾವು ಮನುಷ್ಯರಿಗೆ ದೂತ ರಾಗಿ ಕಳುಹಿಸಿದ್ದೇವೆ, ಇದಕ್ಕೆ ಸಾಕ್ಷಿ ಅಲ್ಲಾಹು ಸಾಕು’.');
INSERT INTO "kannada" VALUES(4,80,'4:80','ಯಾವನು ರಸೂಲರಿಗೆ ಅನುಸರಿಸುತ್ತಾನೋ ಖಂಡಿತವಾಗಿಯೂ ಅವನು ಅಲ್ಲಾಹುವನ್ನು ಅನುಸರಿಸಿದನು. ಇನ್ನು ಯಾವನಾದರೂ ವಿಮು ಖನಾಗುವುದಾದರೆ ನಿಮ್ಮನ್ನು ನಾನು ಕಳುಹಿಸಿದ್ದು ಅವರಿಗೆ ಕಾವಲುಗಾರನಾಗಿಯಲ್ಲ ! ');
INSERT INTO "kannada" VALUES(4,81,'4:81','ಅವರು ಮುಖತಃ, ‘ನಾವು ಆಜ್ಞಾಪಾಲಕರು’ ಎನ್ನುತ್ತಾರೆ. ಆದರೆ ನಿಮ್ಮನ್ನು ಬಿಟ್ಟಗಲಿದಾಗ ಅವರಲ್ಲೊಂದು ಪಂಗಡವು ಅವರ ಮಾತುಗಳ ವಿರುದ್ಧ ಒಳಸಂಚು ನಡೆಸುತ್ತದೆ. ಅವರ ಗೂಢಾ ಲೋಚನೆಗಳನ್ನು ಅಲ್ಲಾಹು ದಾಖಲಿಸುತ್ತಾನೆ. ಆದ್ದರಿಂದ ನೀವು ಅವರನ್ನು ನಿರ್ಲಕ್ಷಿಸಿರಿ. ನೀವು ಅಲ್ಲಾಹುವಿನಲ್ಲಿ ಭರವಸೆ ಇಡಿರಿ. ಭರವಸೆ ಇಡಲು ಅಲ್ಲಾಹನೇ ಸಾಕು .');
INSERT INTO "kannada" VALUES(4,82,'4:82','ಅವರು ಖುರ್‍ಆನಿನ ಬಗ್ಗೆ ಚಿಂತನೆ ಮಾಡುವು ದಿಲ್ಲವೇ? ಇದು ಅಲ್ಲಾಹನ ಹೊರತು ಇನ್ನಾರ ಕಡೆಯಿಂದಾದರೂ ಆಗಿರುತ್ತಿದ್ದರೆ ಅದರಲ್ಲಿ ಅವರು ಬಹಳ ವಿರೋಧಾಭಾಸಗಳನ್ನು ಕಾಣು ತ್ತಿದ್ದರು .');
INSERT INTO "kannada" VALUES(4,83,'4:83','ಅವರ ಬಳಿಗೆ ಶಾಂತಿ ಅಥವಾ ಭೀತಿಯ ಒಂದು ಸುದ್ದಿ ಸಿಕ್ಕಿದರೆ ಅದನ್ನವರು ಪ್ರಚಾರ ಮಾಡುತ್ತಾರೆ. ಆದರೆ ಅದನ್ನವರು ರಸೂಲರ ಮತ್ತು ಅವರ ಪೈಕಿ ಕಾರ್ಯಪ್ರಜ್ಞೆ ಇರುವವರ ಬಳಿಗೆ ಒಯ್ದಿ ದ್ದರೆ ವಿಷಯದ ಜಾಡು ಹುಡುಕುವವರು ಸತ್ಯಾ ವಸ್ಥೆಯನ್ನು ಅವರಿಂದ ಅರಿತುಕೊಳ್ಳಬಹುದಿತ್ತು. ನಿಮ್ಮ ಮೇಲೆ ಅಲ್ಲಾಹನ ಅನುಗ್ರಹವೂ ಕೃಪೆ ಯೂ ಇಲ್ಲದಿರುತ್ತಿದ್ದರೆ ಕೆಲವೇ ಮಂದಿಯ ಹೊರ ತು ನೀವೆಲ್ಲ ಶೈತಾನನನ್ನು ಅನುಸರಿಸುತ್ತಿದ್ದಿರಿ.');
INSERT INTO "kannada" VALUES(4,84,'4:84','ಆದ್ದರಿಂದ (ಪ್ರವಾದಿಯವರೇ!) ನೀವು ಅಲ್ಲಾಹು ವಿನ ಮಾರ್ಗದಲ್ಲಿ ಯುದ್ದ ಮಾಡಿರಿ. ನಿಮ್ಮ ಮೇಲೆ ನಿಮ್ಮದೇ ಹೊರತು ಇತರರ ಹೊಣೆ ಇರುವುದಿಲ್ಲ. ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನೂ (ಧರ್ಮ ಯುದ್ದಕ್ಕೆ) ಪ್ರೇರೇಪಿಸಿರಿ. ಸತ್ಯನಿಷೇಧಿಗಳ ಆಕ್ರಮಣವನ್ನು ಅಲ್ಲಾಹು ತಡೆಯಲಿರುವನು. ಅಲ್ಲಾಹನ ಆಕ್ರಮಣ ಅತಿ ಕಠೋರ ಮತ್ತು ಅವನ ಶಿಕ್ಷೆ ಅತ್ಯಂತ ಕಠಿಣ.');
INSERT INTO "kannada" VALUES(4,85,'4:85','ಯಾವನಾದರೂ ಒಂದು ಒಳ್ಳೆಯ ಶಿಫಾರಸ್ಸು ಮಾಡಿದರೆ ಅದರಿಂದ ಒಂದು ಪಾಲು ಪುಣ್ಯ ಅವನಿಗಿದೆ. ಯಾವನಾದರೂ ಕೆಟ್ಟ ಶಿಫಾರಸ್ಸು ಮಾಡಿದರೆ ಅದರಲ್ಲೂ ಒಂದು ಪಾಲು ಪಾಪ ಅವನಿಗಿದೆ. ಅಲ್ಲಾಹನು ಎಲ್ಲ ಕಾರ್ಯಗಳನ್ನು ನಿರ್ಣಯಿಸುವವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,86,'4:86','ನಿಮಗೆ ಒಂದು ಅಭಿವಾದ್ಯದಿಂದ ಕಾಣಿಕೆ ನೀಡ ಲ್ಪಟ್ಟರೆ ಅದಕ್ಕಿಂತಲೂ ಉತ್ತಮ ರೀತಿಯಿಂದ ಮರ ಳಿ ಕಾಣಿಕೆ ನೀಡಿರಿ. ಅಥವಾ ಅದೇ ರೀತಿಯಲ್ಲಿ ಪ್ರತಿ ಕಾಣಿಕೆ ನೀಡಿರಿ . ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾ ಹನು ಸಕಲ ವಿಷಯಗಳ ಲೆಕ್ಕವಿಡುವವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,87,'4:87','ಅಲ್ಲಾಹು - ಆತನ ಹೊರತು ಬೇರೆ ಆರಾ ಧ್ಯನಿಲ್ಲ. ಸಂದೇಹವೇ ಇಲ್ಲದ ಪುನರುತ್ಥಾನ ದಿನದಂದು ಅವನು ಖಂಡಿತ ನಿಮ್ಮನ್ನೆಲ್ಲ ಒಟ್ಟುಗೂಡಿಸು ವನು. ಅಲ್ಲಾಹನ ಮಾತಿಗಿಂತ ಅಧಿಕ ಸತ್ಯ ಮಾತು ಇನ್ನಾರದಾಗಿರಲು ಸಾಧ್ಯ?');
INSERT INTO "kannada" VALUES(4,88,'4:88','ಮುನಾಫಿಕರ ವಿಷಯದಲ್ಲಿ ನಿಮ್ಮೊಳಗೆ ಎರಡಭಿಪ್ರಾಯಗಳು ಕಂಡುಬರಲು ಕಾರಣ ವಾದರೂ ಏನು? ವಸ್ತುತಃ ಅವರು ಗಳಿಸಿದ ಕೇಡುಗಳ ಕಾರಣವಾಗಿ ಅಲ್ಲಾಹು ಅವರನ್ನು ತಳ್ಳಿ ಹಾಕಿರುತ್ತಾನೆ. ಅಲ್ಲಾಹನು ಸನ್ಮಾರ್ಗ ನೀಡದಾತನಿಗೆ ನೀವು ಸನ್ಮಾರ್ಗ ನೀಡಲಿ ಚ್ಛಿಸುತ್ತೀರಾ? ಆದರೆ ಯಾವನನ್ನು ಅಲ್ಲಾಹನು ದಾರಿ ತಪ್ಪಿಸಿರುವನೋ ಅವನಿಗೆ ದಾರಿ ನೀನು ಕಾಣಲಾರೆ.');
INSERT INTO "kannada" VALUES(4,89,'4:89','ನೀವೂ ಅವರೂ ಸಮಾನರಾಗಲೆಂದು ಅವರು ಸತ್ಯನಿಷೇಧಿಗಳಾದಂತೆಯೇ ನೀವೂ ಸತ್ಯನಿಷೇ ಧಿಗಳಾಗಬೇಕೆಂದು ಅವರಿಚ್ಛಿ ಸುತ್ತಾರೆ. ಆದುದರಿಂದ ಅವರು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಹಿಜ್ರತ್ ಮಾಡಿ ಬರುವವರೆಗೂ ನೀವು ಅವರಲ್ಲಿ ಯಾರನ್ನೂ ನಿಮ್ಮ ಮಿತ್ರರನ್ನಾಗಿ ಮಾಡಿ ಕೊಳ್ಳಬೇಡಿರಿ. ಅವರು ಹಿಜ್ರತಿ ನಿಂದ ಹಿಂಜರಿದರೆ ಅವರನ್ನು ಸಿಕ್ಕಿದಲ್ಲಿ ಹಿಡಿಯಿರಿ ಮತ್ತು ವಧಿಸಿರಿ. ಅವರಲ್ಲಿ ಯಾರನ್ನೂ ನಿಮ್ಮ ಮಿತ್ರ ಹಾಗೂ ಸಹಾಯಕರನ್ನಾಗಿ ಮಾಡ ಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(4,90,'4:90','ಆದರೆ ನಿಮ್ಮೊಂದಿಗೆ ಒಪ್ಪಂದ ಮಾಡಿಕೊಂಡಿರುವ ಜನಾಂಗದೊಂದಿಗೆ ಹೋಗಿ ಸೇರಿಕೊಂಡವರ ಹೊರತು. ಅದೇ ರೀತಿ ಯುದ್ಧದಿಂದ ಜಿಗುಪ್ಸೆ ಹೊಂದಿ ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವ, ನಿಮ್ಮ ವಿರುದ್ಧ ವಾಗಲೀ ತಮ್ಮ ಜನಾಂಗದ ವಿರುದ್ಧವಾಗಲೀ ಯುದ್ಧ ಮಾಡಲು ಇಚ್ಛಿಸದಂತಹವರೂ ಹೊರತಾಗಿರುತ್ತಾರೆ. ಅಲ್ಲಾಹನು ಇಚ್ಛಿಸುತ್ತಿದ್ದರೆ ಅವರನ್ನು ನಿಮ್ಮ ಮೇಲೆ ಎರಗಿಸುತ್ತಿದ್ದನು ಮತ್ತು ಅವರೂ ನಿಮ್ಮ ವಿರುದ್ಧ ಹೋರಾಡುತ್ತಿದ್ದರು. ಆದುದರಿಂದ ಅವರು ನಿಮ್ಮ ವಿರುದ್ಧ ಯುದ್ಧ ಮಾಡದೆ ದೂರವುಳಿದು ಬಿಡುವುದಾದರೆ ಮತ್ತು ನಿಮ್ಮೆದುರು ಸಂಧಿ - ಸಮಾಧಾನಗಳ ಹಸ್ತ ಚಾಚಿದರೆ ಅಲ್ಲಾಹನು ನಿಮಗೆ ಅವರ ಮೇಲೆ ಕೈ ಮಾಡುವ ಯಾವ ಮಾರ್ಗವನ್ನೂ ಇರಿಸಿ ರುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(4,91,'4:91','ಬೇರೆ ಕೆಲವು ಜನರನ್ನು ನೀವು ಕಾಣುವಿರಿ. ಅವರು ನಿಮ್ಮಿಂದಲೂ ಸ್ವಂತ ಕೋಮಿನ ವರಿಂದಲೂ ಅಭಯವನ್ನು ಉದ್ದೇಶಿಸುತ್ತಾರೆ. ಕ್ಷೋಭೆಗಳಿಗೆ ಅವರನ್ನು ಎಳೆದು ಹಾಕಿದಾಗ ಅವರು ಅದರಲ್ಲಿ ಅಧೋಮುಖರಾಗಿ ಬಿದ್ದು ಬೀಳುತ್ತಾರೆ. ಆದರೆ ಇಂತಹವರು ನಿಮ್ಮಿಂದ ದೂರ ಸರಿದು ನಿಲ್ಲದಿದ್ದರೆ, ನಿಮ್ಮೊಂದಿಗೆ ಶಾಂತಿ ಸಂಧಾನಕ್ಕೆ ಸಿದ್ಧರಾಗದಿದ್ದರೆ, ತಮ್ಮ ಕೈಗಳನ್ನು (ನಿಮಗೆ ಕಿರುಕುಳ ಕೊಡುವುದರಿಂದ) ತಡೆದು ನಿಲ್ಲಿಸದಿದ್ದರೆ ಅವರನ್ನು ನೀವು ಕಂಡ ಕಂಡಲ್ಲಿ ಹಿಡಿಯಿರಿ ಮತ್ತು ವಧಿಸಿರಿ. ನಾವು ನಿಮಗೆ ಅವರ ಮೇಲೆ ವ್ಯಕ್ತವಾದ ಅಧಿಕಾರವನ್ನು ನೀಡಿರುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(4,92,'4:92','ಸತ್ಯವಿಶ್ವಾಸಿಯೊಬ್ಬನು ಇನ್ನೊಬ್ಬ ಸತ್ಯವಿಶ್ವಾಸಿಯನ್ನು ಪ್ರಮಾದದಿಂದಾಗಿ ಹೊರತು ವಧಿಸುವುದು ಸರಿಯಲ್ಲ. ಯಾರಾದರೂ ಪ್ರಮಾದವಶಾತ್ ಸತ್ಯವಿಶ್ವಾಸಿಯೊಬ್ಬ ನನ್ನು ವಧಿಸಿಬಿಟ್ಟರೆ ಅದರ ಪ್ರಾಯಶ್ಚಿತ್ತವು ಒಬ್ಬ ಸತ್ಯ ವಿಶ್ವಾಸಿ ವ್ಯಕ್ತಿಯನ್ನು ದಾಸ್ಯದಿಂದ ಮುಕ್ತಗೊಳಿ ಸುವುದು ಮತ್ತು ವಧೆಯಾದವನ ವಾರೀಸುದಾರರು ಪರಿಹಾರ ಧನವನ್ನು ಕ್ಷಮಿಸದಿದ್ದರೆ ಅದನ್ನು ಅವರಿಗೆ ಕೊಡುವುದು. ಆದರೆ ಹತನಾದವನು ಮುಸ್ಲಿಮ ನಾಗಿದ್ದು ಅವನು ನಿಮ್ಮ ಶತ್ರು ಜನಾಂಗದವನಾ ಗಿದ್ದರೆ, ಅದರ ಪ್ರಾಯಶ್ಚಿತ್ತವು ಓರ್ವ ಸತ್ಯವಿಶ್ವಾಸಿ ಗುಲಾಮನನ್ನು ಮುಕ್ತಗೊಳಿಸುವುದು. ಅವನು ನಿಮ್ಮೊಂದಿಗೆ ಒಪ್ಪಂದ ಮಾಡಿಕೊಂಡು ಮುಸ್ಲಿಮೇತರ ಜನಾಂಗದ ವ್ಯಕ್ತಿಯಾಗಿದ್ದರೆ ಅವನ ವಾರೀಸುದಾ ರರಿಗೆ ಒಪ್ಪಿಸುವ ಪರಿಹಾರಧನ ಕೊಡ ಬೇಕು. ಹಾಗೂ ಓರ್ವ ಸತ್ಯವಿಶ್ವಾಸಿ ಗುಲಾಮನ ದಾಸ್ಯ ಮುಕ್ತಿ ಮಾಡಬೇಕು. ಗುಲಾಮನನ್ನು ಪಡೆಯಲಾ ಗದವನು ನಿರಂತರ ಎರಡು ಮಾಸಗಳ ವ್ರತ ಕೈಗೊಳ್ಳ ಬೇಕು. ಇದು ಅಲ್ಲಾಹುವಿನ ಕಡೆಯಿಂದ ನಿಮ್ಮ ಪಶ್ಚಾತ್ತಾಪದ ಕ್ರಮ . ಅಲ್ಲಾಹು ಸರ್ವಜ್ಞನೂ ಯುಕ್ತಿವಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,93,'4:93','ಒಬ್ಬ ಸತ್ಯವಿಶ್ವಾಸಿಯನ್ನು ಉದ್ದೇಶಪೂರ್ವಕವಾಗಿ ವಧಿಸಿದವನಿಗೆ ಅದರ ಪ್ರತಿಫಲ ನರಕವೇ ಆಗಿದ್ದು ಅವನು ಅದರಲ್ಲಿ ಸದಾಕಾಲ ಶಾಶ್ವತ ನೆಲೆಸುವನು . ಅಲ್ಲಾಹನು ಅವನ ಮೇಲೆ ಕ್ರೋಧ ತಾಳುವನು. ಅವನನ್ನು ಶಪಿಸುವನು. ಅತ್ಯಂತ ಕಠಿಣವಾದ ಶಿಕ್ಷೆಯನ್ನು ಅವನಿಗೆ ಅಲ್ಲಾಹು ಸಿದ್ಧಗೊಳಿಸಿರುವನು.');
INSERT INTO "kannada" VALUES(4,94,'4:94','ಓ ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳೇ ! ಅಲ್ಲಾಹುವಿನ ಮಾರ್ಗದಲ್ಲಿ ನೀವು (ಯುದ್ಧಕ್ಕೆ) ಹೊರಟರೆ ಸೂಕ್ಷ್ಮಾವಸ್ಥೆಯನ್ನು ಶೋಧಿಸಿ ಗ್ರಹಿಸಿಕೊಳ್ಳಿರಿ. ನಿಮಗೆ ಸಲಾಮ್ ಅರ್ಪಿಸಿದವನಿಗೆ ತಕ್ಷಣ, ಭೌತಿಕ ಜೀವನ ದಾಸೆಯಿಂದ; ನೀನು ಸತ್ಯವಿಶ್ವಾಸಿಯಲ್ಲ ಎನ್ನಬೇಡಿರಿ . ಅಲ್ಲಾಹನ ಬಳಿ ನಿಮಗೆ ಬೇಕಾದ ಧಾರಾಳ ಸರಕುಗಳಿವೆ. ನಿಮ್ಮ ಅವಸ್ಥೆ ಕೂಡಾ ಮೊದಲು ಹಾಗೇ ಇತ್ತು. ಬಳಿಕ ನಿಮ್ಮ ಮೇಲೆ ಅಲ್ಲಾಹನು ಉಪಕಾರ ಮಾಡಿದನು. ಆದುದರಿಂದ ಚೆನ್ನಾಗಿ ಪರ್ಯಾಲೋಚಿಸಿರಿ. ನೀವು ಮಾಡುತ್ತಿರುವುದನ್ನೆಲ್ಲ ಅಲ್ಲಾಹನು ಚೆನ್ನಾಗಿ ಬಲ್ಲವನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(4,95,'4:95','ಪ್ರತಿಬಂಧಕವೇನೂ ಇಲ್ಲದೆಯೇ ಯುದ್ಧಕ್ಕೆ ಹೋಗದೆ ಕೂತುಕೊಳ್ಳುವ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೂ ಅಲ್ಲಾಹುವಿನ ಮಾರ್ಗದಲ್ಲಿ ಸ್ವಂತ ಸೊತ್ತು ಮತ್ತು ದೇಹ ಬಳಸಿ ಯುದ್ದ ಮಾಡುವವರೂ ಸಮಾನರಾಗಲಾರರು. ತಮ್ಮ ತನು-ಧನಗಳಿಂದ ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಯುದ್ಧ ಮಾಡುವವರಿಗೆ (ಪ್ರತಿಬಂಧ ಮೂಲಕ) ಹೋಗದೆ ಕೂತವರಿಗಿಂತ ಉನ್ನತ ದರ್ಜೆಯನ್ನು ಅಲ್ಲಾಹು ಕೊಡ ಮಾಡಿದ್ದಾನೆ. ಎರಡೂ ವಿಭಾಗಕ್ಕೆ ಅಲ್ಲಾಹನು ಒಳ್ಳೆಯದನ್ನು ವಾಗ್ದಾನ ಮಾಡಿರುತ್ತಾನೆ. ಆದರೆ ಯೋಧರಿಗೆ ಯುದ್ಧಕ್ಕೆ ಹೋಗದೆ ಕೂತವರಿಗಿಂತ ಹೆಚ್ಚಿನ ಪ್ರತಿಫಲಗಳ ಮೂಲಕ ಅಲ್ಲಾಹು ಅನುಗ್ರಹಿಸಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,96,'4:96','ಅಂದರೆ ಅವನ ಕಡೆಯಿಂದ ಉನ್ನತ ದರ್ಜೆ ಗಳನ್ನೂ ದೋಷ ಮುಕ್ತಿಯನ್ನೂ ಕಾರುಣ್ಯ ವನ್ನೂ ನೀಡುವನು. ಅಲ್ಲಾಹನು ಅತ್ಯಂತ ಹೆಚ್ಚು ದೋಷಮುಕ್ತಿ ಕೊಡುವವನೂ ಪರಮ ದಯಾವಂತನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(4,97,'4:97','ಸ್ವಂತಕ್ಕೆ ದ್ರೋಹ ಮಾಡಿಕೊಂಡವರನ್ನು ಮಲಕ್‍ಗಳು ಮೃತ್ಯುಗೊಳಪಡಿಸುವಾಗ ಅವರೊಂದಿಗೆ ಮಲಕ್‍ಗಳು ನಿಶ್ಚಯವಾಗಿ ಯೂ ಕೇಳುವರು; `ನೀವು ಏನು ಮಾಡುತ್ತಿದ್ದೀರಿ ?’ ಆಗ ಅವರು; `ನಮ್ಮ ನಾಡಿನಲ್ಲಿ ನಾವು ಬಲಹೀನಗೊಳಿಸಲ್ಪಟ್ಟಿದ್ದೆವು.’ ಆಗ ಮಲಕ್ ಗಳು; `ಅಲ್ಲಾಹನ ಭೂಮಿ ವಿಶಾಲವಿರಲಿಲ್ಲವೇ? ಅದರಲ್ಲಿ ಎಲ್ಲಿಗಾದರೂ ದೇಶತ್ಯಾಗ ಮಾಡಿ ಹೊರಟು ಹೋಗಬಾರದಿತ್ತೇ? ಎಂದು ಕೇಳುವರು. ಇಂತಹವರ ವಾಸಸ್ಥಳವು ನರಕವೇ ಆಗಿದೆ. ಅದು ಅತ್ಯಂತ ಕೆಟ್ಟ ವಾಸಸ್ಥಾನ.');
INSERT INTO "kannada" VALUES(4,98,'4:98','ಆದರೆ ದೇಶ ತ್ಯಾಗಕ್ಕೆ ಸಾಧ್ಯವಾಗದ ಯಾವುದೇ ಮಾರ್ಗೋಪಾಯಗಳನ್ನು ಕೈ ಗೊಳ್ಳಲು ಅಸಮರ್ಥರಾದ ಪುರುಷರೂ ಸ್ತ್ರೀಯರೂ ಮಕ್ಕಳೂ ಹೊರತು.');
INSERT INTO "kannada" VALUES(4,99,'4:99','ಅಲ್ಲಾಹನು ಅವರಿಗೆ ಕ್ಷಮಾದಾನ ಕೊಡ ಬಹುದು . ಅಲ್ಲಾಹನು ತುಂಬ ಸಹನಾ ಶೀಲನೂ ಬಹಳ ಕ್ಷಮಾದಾನಿಯೂ ಆಗಿರು ವನು.');
INSERT INTO "kannada" VALUES(4,100,'4:100','ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಹಿಜ್‍ರಃ ಹೋಗು ವವನು ಭೂಮಿಯಲ್ಲಿ ಧಾರಾಳ ಅಭಯ ಸ್ಥಾನಗಳನ್ನೂ ವಿಶಾಲತೆಯನ್ನೂ ಕಾಣುವನು. ಯಾವನಾದರೂ ತನ್ನ ಮನೆಯಿಂದ ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ರಸೂಲರ ಕಡೆಗೆ ವಲಸೆ ಹೊರಟಿದ್ದು ನಂತರ ಅವನಿಗೆ ಮರಣ ಸಂಭ ವಿಸಿದರೆ ಖಂಡಿತವಾಗಿಯೂ ಅವನ ಪ್ರತಿಫಲ ಅಲ್ಲಾಹನ ಬಳಿ ಸ್ಥಿರವಾಗಿಬಿಟ್ಟಿತು. ಅಲ್ಲಾಹನು ಬಹಳ ಕ್ಷಮಾದಾನಿಯೂ ಪರಮ ದಯಾ ಮಯನೂ ಆಗಿರುತ್ತಾನೆ ');
INSERT INTO "kannada" VALUES(4,101,'4:101','ನೀವು ಪ್ರಯಾಣದಲ್ಲಿರುವಾಗ ಸತ್ಯನಿಷೇಧಿಗಳು ನಿಮ್ಮನ್ನು ಉಪದ್ರವಿಸುವರೆಂದು ಭಯವಿದ್ದರೆ, ನಮಾಜನ್ನು ನೀವು ಕಸ್ರ್ ಮಾಡುವುದರಲ್ಲಿ ನಿಮ್ಮ ತಪ್ಪಿಲ್ಲ . ಖಂಡಿತವಾಗಿಯೂ ಸತ್ಯ ನಿಷೇಧಿಗಳು ನಿಮ್ಮ ಪ್ರತ್ಯಕ್ಷ ಶತ್ರುಗಳಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(4,102,'4:102','(ಓ ಪೈಗಂಬರರೇ,) ನೀವು ಮುಸಲ್ಮಾನ ರೊಂದಿಗೆ ಇದ್ದು ಅವರಿಗೆ ನಮಾಝ್ ಮಾಡಿಸಲು ನಿಂತಾಗ ಅವರಲ್ಲೊಂದು ತಂಡವು ನಿಮ್ಮ ಜೊತೆ ನಿಲ್ಲಲಿ. ತಮ್ಮ ಆಯುಧಗಳನ್ನು ಹಿಡಿದುಕೊಳ್ಳಲಿ. ಅವರು ನಮಾಜು ಮುಗಿ ಸಿದರೆ ನಿಮ್ಮ ಹಿಂಭಾಗದಲ್ಲಿ ಹೋಗಿ ನಿಲ್ಲಲಿ. ನಮಾಜು ಮಾಡಿರದ ಇನ್ನೊಂದು ತಂಡವು ನಿಮ್ಮ ಸಂಗಡ ನಮಾಝ್ ಮಾಡಲಿ. ಅವರೂ ಜಾಗರೂಕರಾಗಿರಲಿ, ತಮ್ಮ ಆಯುಧಗಳನ್ನು ಹಿಡಿದುಕೊಳ್ಳಲಿ. ನಿಮ್ಮ ಆಯುಧಗಳು ಮತ್ತು ನಿಮ್ಮ ಸರಕುಗಳ ಬಗ್ಗೆ ನೀವು ಅಶ್ರದ್ಧರಾದರೆ ನಿಮ್ಮ ಮೇಲೆ ಹಠಾತ್ ದಾಳಿ ಮಾ ಸತ್ಯನಿಷೇಧಿಗಳು ಆಗ್ರಹಿಸಿದ್ದಾರೆ. ಒಂದು ವೇಳೆ ಮಳೆÉಯ ಕಾರಣದಿಂದ ನಿಮಗೆ ತೊಂದರೆ ಯುಂಟಾದರೆ ಅಥವಾ ನೀವು ರೋಗಗ್ರಸ್ತರಾದರೆ ಆಯುಧಗಳನ್ನು ಕೆಳಗಿಡುವುದರಲ್ಲಿ ನಿಮ್ಮ ತಪ್ಪಿಲ್ಲ, ಆಗಲೂ ಜಾಗ್ರತರಾಗಿರಿ . ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಸತ್ಯ ನಿಷೇಧಿಗಳಿಗೆ ನಿಂದನಾಮಯ ಶಿಕ್ಷೆಯನ್ನು ಸಿದ್ಧಪಡಿಸಿಟ್ಟಿರುತ್ತಾನೆ. ಸಿದ್ಧಪಡಿಸಿಟ್ಟಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,103,'4:103','ನೀವು ನಮಾಜನ್ನು ನೆರವೇರಿಸಿದರೆ ನಂತರ ನೀವು ನಿಂತುಕೊಂಡೂ, ಕುಳಿತುಕೊಂಡೂ ಅಥವಾ ಮಲಗಿಕೊಂಡೂ (ಸದಾ ಸಮಯ) ಅಲ್ಲಾಹನನ್ನು ಸ್ಮರಿಸಿರಿ. ಂ ಅನಂತರ ಶಾಂತತೆ ಪ್ರಾಪ್ತವಾದ ಬಳಿಕ ನಮಾಜನ್ನು ಅದರ ಕ್ರಮದಂತೆ ಪಾಲಿಸಿರಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ನಮಾಜು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಸಮಯ ನಿರ್ಣಯಿತವಾದ ಒಂದು ಕಡ್ಡಾಯ ಕರ್ತವ್ಯವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(4,104,'4:104','ಶತ್ರು ಪಕ್ಷದವರನ್ನು ಬೆನ್ನಟ್ಟಿ ಹುಡುಕಿಕೊಂಡು ಹೋಗುವುದರಲ್ಲಿ ನೀವು ನಿರಾಸಕ್ತಿ ತೋರ ಬಾರದು. ನೀವು ನೋವು ಅನುಭವಿಸುತ್ತಿದ್ದರೆ ನೀವು ನೋವು ಅನುಭವಿಸುವಂತೆಯೇ ಅವರೂ ನೋವು ಅನುಭವಿಸಲಿದ್ದಾರೆ. ಅವರು ಆಗ್ರಹಪಡದ ಸಾಧನೆಗಳನ್ನು ನೀವು ಅಲ್ಲಾಹುವಿನಿಂದ ಅಗ್ರಹಿಸುತ್ತಿದ್ದೀರಿ. ಅಲ್ಲಾಹನು ಪರಮ ತಜ್ಞನೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,105,'4:105','(ಓ ಪೈಗಂಬರರೇ,) ಅಲ್ಲಾಹು ನಿಮಗೆ ತೋರಿಸಿ ಕೊಟ್ಟ ಪ್ರಕಾರ ಜನರ ನಡುವೆ ತೀರ್ಪು ನೀಡಲಿಕ್ಕಾಗಿ ನಾವು ನಿಮ್ಮ ಮೇಲೆ ಈ ಗ್ರಂಥ ವನ್ನು ಸತ್ಯ ಸಹಿತ ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದೇವೆ. ನೀವು ವಂಚಕರ ಪರವಾಗಿ ವಾದಿಸುವವರಾ ಗಬಾರದು .');
INSERT INTO "kannada" VALUES(4,106,'4:106','ಪಾಪ ವಿಮೋಚನೆಗಾಗಿ ಅಲ್ಲಾಹನನ್ನು ಪ್ರಾರ್ಥಿಸಿರಿ. ಅವನು ಅತ್ಯಂತ ಕ್ಷಮಾಶೀಲನೂ, ಪರಮ ದಯಾಳುವೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(4,107,'4:107','ಆತ್ಮವಂಚನೆ ಮಾಡುವವರ ಪರವಾಗಿ ನೀವು ವಾದಿಸಬೇಡಿರಿ. ವಿಶ್ವಾಸ ಘಾತಕನೂ, ಘೋರ ಅಪರಾಧಿಯೂ ಆದವನನ್ನು ಅಲ್ಲಾಹು ಖಂಡಿತ ಮೆಚ್ಚುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(4,108,'4:108','ಅವರು ಜನರಿಂದ (ನಾಚಿಕೆಯಿಂದ) ಮರೆ ಮಾಚುತ್ತಾರೆ. ಆದರೆ ಅಲ್ಲಾಹುವಿನಿಂದ ಮರೆ ಮಾಚುತ್ತಿಲ್ಲ. ಅಲ್ಲಾಹನಾದರೋ ಅವನು ತೃಪ್ತಿಪಡದ ಗೂಢಾಲೋಚನೆಯಲ್ಲಿ ಅವರು ತೊಡಗಿರುವಾಗ ಅವರ ಸಂಗಡ ಇರುತ್ತಾನೆ. ಅವರ ಎಲ್ಲ ಕೃತ್ಯಗಳ ಬಗ್ಗೆ ಅಲ್ಲಾಹನು ಪರಿಪೂರ್ಣವಾಗಿ ಅರಿಯುವವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,109,'4:109','(ಓ ಜನರೇ!) ನೀವು ಇಹಲೋಕ ಜೀವನದಲ್ಲಿ ಅವರಿಗಾಗಿ (ಈ ಅಪರಾಧಿಗಳ ಪರವಾಗಿ) ವಾದಿಸಿದಿರಿ. ಆದರೆ ಪರಲೋಕದಲ್ಲಿ ಅವರ ಪರವಾಗಿ ಅಲ್ಲಾಹನೊಂದಿಗೆ ವಾದಿಸುವವರು ಯಾರಿದ್ದಾರೆ ? ಅವರ ಕಾರ್ಯ ವಹಿಸಿಕೊಳ್ಳಲು ಯಾರಾದರೂ ಇರುತ್ತಾರೆಯೇ?');
INSERT INTO "kannada" VALUES(4,110,'4:110','ಯಾವನಾದರೂ ಒಂದು ಸಣ್ಣ ಅಪರಾಧ ಮಾಡಿದರೆ ಅಥವಾ ದೊಡ್ಡ ಅಪರಾಧ ಮಾಡಿ ತನ್ನನ್ನೇ ತಾನು ಅಕ್ರಮಿಸಿಕೊಂಡಿದ್ದರೆ ಆಮೇಲೆ ಅಲ್ಲಾಹನಲ್ಲಿ ಪಾಪ ಮುಕ್ತಿಗೆ ಬೇಡಿದರೆ ಅಲ್ಲಾಹು ಕ್ಷಮಾದಾನಿಯೂ ಕರುಣಾನಿಧಿಯೂ ಆಗಿರುವುದನ್ನು ಅವನು ಕಾಣುವನು.');
INSERT INTO "kannada" VALUES(4,111,'4:111','ಯಾವನು ಯಾವುದಾದರೂ ಪಾಪ ಮಾಡು ತ್ತಾನೋ ಖಂಡಿತವಾಗಿಯೂ ಅವನು ತನ್ನ ಸ್ವಂತಕ್ಕೆ ಅನ್ಯಾಯವೆಸಗುತ್ತಾನೆ. ಅಲ್ಲಾಹನು ಎಲ್ಲ ತಿಳಿಯುವವನೂ ಪರಮ ಯುಕ್ತಿಗಾರನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(4,112,'4:112','ಯಾವನಾದರೂ ಒಂದು ಸಣ್ಣ ತಪ್ಪು ಅಥವಾ ದೊಡ್ಡ ಅಪರಾಧ ಎಸಗಿ ಅದನ್ನು ಓರ್ವ ನಿರಪರಾಧಿಯ ಮೇಲೆ ಹೊರಿಸಿದರೆ ಖಂಡಿತವಾಗಿಯೂ ಆತ ಒಂದು ಘೋರ ಸುಳ್ಳಾರೋಪವನ್ನೂ ಪ್ರತ್ಯಕ್ಷ ಪಾಪವನ್ನೂ ಹೊತ್ತುಕೊಂಡನು.');
INSERT INTO "kannada" VALUES(4,113,'4:113','(ಓ ಪೈಗಂಬರರೇ,) ಅಲ್ಲಾಹನ ಔದಾರ್ಯ ಮತ್ತು ದಯೆ ನಿಮ್ಮ ಮೇಲೆ ಇಲ್ಲದಿರುತ್ತಿದ್ದರೆ, ಅವರಲ್ಲೊಂದು ಪಂಗಡವು ನಿಮ್ಮನ್ನು ದಾರಿ ತಪ್ಪಿಸಲು ನಿರ್ಧರಿಸಿಯೇ ಬಿಟ್ಟಿತ್ತು. ವಸ್ತುತಃ ಅವರು ತಮ್ಮನ್ನು ತಾವೇ ದಾರಿ ತಪ್ಪಿಸಿ ಕೊಳ್ಳುತ್ತಿದ್ದಾರೆ. ನಿಮಗೆ ಏನೊಂದು ಉಪದ್ರವ ವನ್ನೂ ಕೊಡಲು ಅವರಿಂದಾಗದು. ಅಲ್ಲಾಹನು ನಿಮಗೆ ಗ್ರಂಥ ಹಾಗೂ ಯುಕ್ತಿಯನ್ನು ಅವತೀ ರ್ಣಗೊಳಿಸಿರುತ್ತಾನೆ ಮತ್ತು ನಿಮಗೆ ತಿಳಿಯ ದಿದ್ದುದನ್ನು ಕಲಿಸಿದ್ದಾನೆ. ಅಲ್ಲಾಹನು ನಿಮ್ಮ ಮೇಲೆ ತೋರಿದ ಔದಾರ್ಯ ಬಹಳ ಹಿರಿದಾ ದುದು.');
INSERT INTO "kannada" VALUES(4,114,'4:114','ಜನರ ಅಧಿಕ ಗುಪ್ತ ಸಮಾಲೋಚನೆಗಳಲ್ಲಿ ಯಾವ ಗುಣವೂ ಇರುವುದಿಲ್ಲ. ಆದರೆ ದಾನ ಧರ್ಮ ಮಾಡಲು, ಇತರ ಸತ್ಕಾರ್ಯಗಳನ್ನು ನಿರ್ವಹಿಸಲು ಅಥವಾ ಜನರೆಡೆಯಲ್ಲಿ ಸಾಮರಸ್ಯ ಉಂಟು ಮಾಡಲು ನಡೆಸುವ ಗುಪ್ತ ಸಮಾಲೋಚನೆಯ ಹೊರತು. ಯಾವನಾದರೂ ಅಲ್ಲಾಹನ ಸಂಪ್ರೀತಿಯ ಆಸೆಯಿಂದ ಹೀಗೆ ಮಾಡಿದರೆ ಅವನಿಗೆ ನಾವು ಹಿರಿದಾದ ಪ್ರತಿಫಲ ನೀಡುವೆವು.');
INSERT INTO "kannada" VALUES(4,115,'4:115','ಯಾರು ಸನ್ಮಾರ್ಗವು ತನಗೆ ವ್ಯಕ್ತವಾದ ಬಳಿಕವೂ ದೇವದೂತರಿಗೆ ಎದುರಾಗುತ್ತಾನೆ, ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಮಾರ್ಗದ ಹೊರತು ಅನ್ಯ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುತ್ತಾನೆ, ಅವನು ತಿರುಗಿದ ಭಾಗಕ್ಕೆ ನಾವು ಅವನನ್ನು ತಿರುಗಿಸುವೆವು. ಮತ್ತು ಅವನನ್ನು ನಾವು ನರಕಕ್ಕೆ ನೂಕಿ ಬಿಡುವೆವು. ಎಷ್ಟು ನಿಕೃಷ್ಟ ತಾಣವದು! ');
INSERT INTO "kannada" VALUES(4,116,'4:116','ಅಲ್ಲಾಹನು ತನಗೆ `ಶಿರ್ಕ್’ ಮಾಡಲ್ಪಡುವು ದನ್ನು ಖಂಡಿತ ಕ್ಷಮಿಸುವುದಿಲ್ಲ. ಅದರ ಹೊರತಾದುದನ್ನು ತಾನಿಚ್ಛಿಸುವವರಿಗೆ ಕ್ಷಮಿಸುವನು. ಯಾವನು ಅಲ್ಲಾಹನಿಗೆ ‘ಶಿರ್ಕ್’ ಮಾಡುತ್ತಾನೋ ಅವನು ಋಜು ಮಾರ್ಗ ದಿಂದ ದಾರಿ ತಪ್ಪಿ ಬಹಳ ದೂರ ಸಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,117,'4:117','ಅವರು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಕೆಲವು ಸ್ತ್ರೀ ವಿಗ್ರಹಗಳನ್ನು ಮಾತ್ರ ಆರಾಧಿಸುತ್ತಿದ್ದಾರೆ. ಅವರು ಆರಾಧಿಸುವುದು ಧಿಕ್ಕಾರಿಯಾದ ಶೈತಾನನನ್ನೇ ಹೊರತು ಇನ್ನಾರನ್ನೂ ಅಲ್ಲ .');
INSERT INTO "kannada" VALUES(4,118,'4:118','ಅಲ್ಲಾಹನು ಅವನನ್ನು (ಶೈತಾನನ್ನು) ಶಪಿಸಿ ರುತ್ತಾನೆ. `ನಿನ್ನ ದಾಸರಿಂದ ಒಂದು ನಿಶ್ಚಿತ ಪಾಲನ್ನು ನಾನು ಸ್ವಾಧೀನಪಡಿಸಿ ಕೊಳ್ಳುವೆನು’ ಎಂದು ಅವನು ಹೇಳಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,119,'4:119','ಖಂಡಿತ ನಾನವರನ್ನು ದಾರಿ ತಪ್ಪಿಸುವೆನು. ಖಂಡಿತವಾಗಿಯೂ ನಾನವರಿಗೆ ವ್ಯಾಮೋಹ ಹುಟ್ಟಿಸುವೆನು. ಖಂಡಿತವಾಗಿಯೂ ನಾನವ ರಿಗೆ ಅಪ್ಪಣೆ ಕೊಡುವೆನು. ಆಗ ಅವರು ಜಾನು ವಾರುಗಳ ಕಿವಿಗಳನ್ನು ಹರಿಯುವರು. ಅವರಿಗೆ ನಾನು ಆಜ್ಞೆ ಕೊಡುವೆನು. ಆಗ ಅವರು ಅಲ್ಲಾಹುವಿನ ಸೃಷ್ಟಿಯನ್ನು ವಿರೂಪಗೊ ಳಿಸುವರು (ಎಂದು ಶೈತಾನನು ಹೇಳಿರುತ್ತಾನೆ.) ಯಾವನು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಶೈತಾನನನ್ನು ರಕ್ಷಾಧಿಕಾರಿಯನ್ನಾಗಿ ಮಾಡುತ್ತಾನೋ ಖಂಡಿತ ಅವನು ಸ್ಪಷ್ಟವಾದ ನಷ್ಟಕ್ಕೆ ಗುರಿ ಯಾದನು.');
INSERT INTO "kannada" VALUES(4,120,'4:120','ಶೈತಾನನು ಅವರಿಗೆ ವಾಗ್ದಾನಗಳನ್ನು ನೀಡು ತ್ತಾನೆ. ಅವರಿಗೆ ವ್ಯಾಮೋಹ ಹುಟ್ಟಿಸುತ್ತಾನೆ. ಆದರೆ ಶೈತಾನನ ಎಲ್ಲ ವಾಗ್ದಾನಗಳು ವಂಚನೆಯ ವಿನಾ ಇನ್ನೇನೂ ಅಲ್ಲ.');
INSERT INTO "kannada" VALUES(4,121,'4:121','ಅಂಥವರ ವಾಸಸ್ಥಳ ನರಕವೇ ಆಗಿದೆ. ಅದರಿಂದ ರಕ್ಷಣೆ ಹೊಂದುವ ಯಾವುದೇ ಅಭಯ ಸ್ಥಳವನ್ನು ಅವರು ಹೊಂದಲಾರರು.');
INSERT INTO "kannada" VALUES(4,122,'4:122','ಸತ್ಯವಿಶ್ವಾಸವನ್ನಿರಿಸಿಕೊಂಡು ಸತ್ಕರ್ಮ ಗಳನ್ನು ಕೈಕೊಂಡವರಿಗೆ ತಳಭಾಗದಲ್ಲಿ ನದಿಗಳು ಹರಿಯುವಂತಹ ಸ್ವರ್ಗೋದ್ಯಾನ ಗಳಲ್ಲಿ ನಾವು ಪ್ರವೇಶ ಕೊಡುವೆವು. ಅವರು ಅಲ್ಲಿ ಸದಾಕಾಲ ವಾಸಿಸುವರು. ಇದು ಅಲ್ಲಾ ಹನ ದಿಟವಾದ ವಾಗ್ದಾನ. ಮಾತಿನಲ್ಲಿ ಅಲ್ಲಾಹ ನಿಗಿಂತ ಸತ್ಯವಂತರು ಯಾರಿದ್ದಾರೆ ?');
INSERT INTO "kannada" VALUES(4,123,'4:123','ಈ ವಾಗ್ದಾನ ಫಲವು ನಿಮ್ಮ ವ್ಯಾಮೋಹದಂತೆ ಇರುವುದಿಲ್ಲ. ಗ್ರಂಥದವರ ವ್ಯಾಮೋಹದಂ ತೆಯೂ ಅಲ್ಲ. ಕೇಡು ಮಾಡಿದವನಿಗೆ ಅದರ ಪ್ರತಿಫಲ ಖಂಡಿತ ಕೊಡಲಾಗುವುದು. ಅಲ್ಲಾಹುವಿನ ಹೊರತು ಯಾವನೇ ರಕ್ಷಾಧಿಕಾರಿಯನ್ನೂ ಸಹಾಯಕನನ್ನೂ ಅವನು ಪಡೆಯಲಾರ.');
INSERT INTO "kannada" VALUES(4,124,'4:124','ಪುರುಷನಿರಲಿ ಅಥವಾ ಸ್ತ್ರೀ ಇರಲಿ ಯಾರು ಸತ್ಯ ವಿಶ್ವಾಸಿಯಾಗಿದ್ದುಕೊಂಡು ಸತ್ಕಾರ್ಯ ಗಳನ್ನು ಕೈಗೊಳ್ಳುತ್ತಾರೆ, ಅವರು ಸ್ವರ್ಗಕ್ಕೆ ಪ್ರವೇಶಿಸುವರು. ಅವರಿಗೆ ಲವಲೇಶವೂ ಅನ್ಯಾಯವಾಗದು .');
INSERT INTO "kannada" VALUES(4,125,'4:125','ಸುಕೃತನಾಗಿದ್ದುಕೊಂಡು ತನ್ನನ್ನು ಅಲ್ಲಾಹು ವಿಗೆ ಸಂಪೂರ್ಣ ಶರಣಾಗಿಸಿಕೊಂಡಿರುವ ಹಾಗೂ ಸತ್ಯದಲ್ಲಿ ಅಚಲವಾಗಿದ್ದ ಇಬ್‍ರಾಹೀ ಮರ ಹಾದಿಯನ್ನು ಅನುಸರಿಸಿಕೊಂಡು ಬರುವವನಿ ಗಿಂತ ಉತ್ತಮ ಧರ್ಮನಿಷ್ಠ ಇನ್ನಾರಿದ್ದಾನೆ? ಇಬ್ರಾಹೀಮರನ್ನು ಅಲ್ಲಾಹು ಆಪ್ತ ಮಿತ್ರನನ್ನಾಗಿ ಮಾಡಿ ಕೊಂಡಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,126,'4:126','ಆಕಾಶಗಳಲ್ಲಿರುವುದೂ ಭೂಮಿಯಲ್ಲಿರುವುದೂ ಎಲ್ಲವೂ ಅಲ್ಲಾಹನದ್ದು. ಅಲ್ಲಾಹನ ಅರಿವು ಮತ್ತು ಸ್ವಾಧೀನ ಎಲ್ಲವನ್ನೂ ಆವರಿಸಿಕೊಂಡಿರುತ್ತದೆ ');
INSERT INTO "kannada" VALUES(4,127,'4:127','ಮಹಿಳೆಯರ ವಿಷಯದಲ್ಲಿ ಅವರು ನಿಮ್ಮಲ್ಲಿ ವಿಧಿ ಕೇಳುತ್ತಾರೆ. ಹೇಳಿರಿ; ಅವರ (ಮಹಿಳೆಯರ) ಕಾರ್ಯದಲ್ಲಿ ಅಲ್ಲಾಹು ನಿಮಗೆ ತೀರ್ಪು ಕೊಡುತ್ತಾನೆ. ಈ ಗ್ರಂಥದಲ್ಲೂ ವಿಧಿ ನೀಡುತ್ತಾನೆ. ಅವರಿಗೆ ನಿರ್ಣಯಿತ ಹಕ್ಕನ್ನು ಕೊಡದೆ ನೀವು ವಿವಾಹ ಮಾಡಿಕೊಳ್ಳಲು ಇಚ್ಛಿಸುವ ಅನಾಥ ಸ್ತ್ರೀಯರು ಮತ್ತು ಬಲಹೀನರಾದ ಮಕ್ಕಳ ಸಂಬಂಧವಾಗಿಯೂ (ತೀರ್ಪು ಕೊಡುತ್ತಾನೆ). ಅನಾಥರೊಂದಿಗೆ ನ್ಯಾಯದಿಂದ ವರ್ತಿಸಬೇಕೆಂಬ ಆಜ್ಞೆಯನ್ನೂ (ನೀಡುತ್ತಿದ್ದಾನೆ). ನೀವು ಯಾವುದೇ ಒಳಿತು ಮಾಡುವುದಿದ್ದರೂ ಅಲ್ಲಾಹು ಅದನ್ನು ಚೆನ್ನಾಗಿ ಅರಿಯುವವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,128,'4:128','ಓರ್ವ ಸ್ತ್ರೀಗೆ ತನ್ನ ಪತಿಯಿಂದ ವಿರಸ ಅಥವಾ ನಿರ್ಲಕ್ಷ್ಯದ ಭಯವಿದ್ದರೆ, ಅವರು (ಪತಿ ಮತ್ತು ಪತ್ನಿ) ಪರಸ್ಪರ ಸಂಧಾನ ಮಾಡಿಕೊಳ್ಳುವುದರಲ್ಲಿ ಅವರ ತಪ್ಪಿಲ್ಲ. ಸಂಧಾನವೇ ಉತ್ತಮ. ಲೋಭತನವು ಮನುಷ್ಯರ ಆತ್ಮದಲ್ಲಿ ಅಂಟಿಕೊಂಡಿದೆ. ನೀವು (ಸ್ತ್ರೀಯರೊಂದಿಗೆ) ಉತ್ತಮವಾಗಿ ವರ್ತಿಸಿದರೆ ಹಾಗೂ ಸೂಕ್ಷ್ಮತೆ ಪಾಲಿಸಿದರೆ ಖಂಡಿತವಾಗಿಯೂ ನೀವು ಮಾಡುತ್ತಿರುವುದರ ಬಗ್ಗೆ ಅಲ್ಲಾಹನು ಸೂಕ್ಷ್ಮವಾಗಿ ಅರಿಯುವವನೇ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,129,'4:129','ನೀವು ಎಷ್ಟೇ ಆಗ್ರಹಿಸಿದರೂ ಪತ್ನಿಯರ ನಡುವೆ (ಪ್ರೀತಿಯಲ್ಲಿ) ಸಮಾನತೆ ಪಾಲಿಸಲು ನಿಮ್ಮಿಂದ ಸಾಧ್ಯವಾಗದು. ಆದ್ದರಿಂದ ನೀವು ಒಬ್ಬಳ ಕಡೆ ಪೂರ್ಣ ವಾಲಿಕೊಂಡು ಇನ್ನೊಬ್ಬಳನ್ನು ತ್ರಿಶಂಕು ಸ್ಥಿತಿಯಲ್ಲಿರುವಂತೆ ಉಪೇಕ್ಷಿಸದಿರಿ. ನೀವು (ಪತ್ನಿಯರ ನಡುವೆ ನ್ಯಾಯಪಾಲನೆ ಯಿಂದ) ಸುಧಾರಿಸಿಕೊಂಡರೆ, ಮತ್ತು ಸೂಕ್ಷ್ಮತೆ ಪಾಲಿಸಿದರೆ ಅಲ್ಲಾಹು ಕ್ಷಮಿಸುವವನೂ ದಯಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(4,130,'4:130','ಇನ್ನು ಅವರಿಬ್ಬರೂ ಬೇರ್ಪಡುವುದಾದರೆ, ಅಲ್ಲಾಹು ತನ್ನ ವಿಶಾಲ ಔದಾರ್ಯದಿಂದ ಅವರಿಬ್ಬರನ್ನೂ ಸ್ವಾಶ್ರಯಗೊಳಿಸುವನು . ಅಲ್ಲಾಹನು ಪರಮ ವಿಶಾಲನೂ ಯುಕ್ತಿ ಪೂರ್ಣನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,131,'4:131','ಆಕಾಶಗಳಲ್ಲೂ ಭೂಮಿಯಲ್ಲೂ ಇರುವುದೆಲ್ಲವೂ ಅಲ್ಲಾಹನದ್ದು. ನೀವು ಅಲ್ಲಾಹುವಿನಲ್ಲಿ ಭಯವಿರಿಸಿಕೊಳ್ಳಿರಿ ಎಂದು ನಿಮಗೂ ನಿಮ್ಮ ಹಿಂದೆ ಗ್ರಂಥ ನೀಡಲ್ಪಟ್ಟವರಿಗೂ ನಾವು ಆದೇಶಿಸಿದ್ದೇವೆ. ನೀವು ಆದೇಶ ನಿರಾಕರಿಸಿದರೆ (ಅಲ್ಲಾಹುವಿಗೆ ಯಾವ ನಷ್ಟವೂ ಇಲ್ಲ. ಯಾಕೆಂದರೆ) ಆಕಾಶಗಳಲ್ಲಿರುವ ಹಾಗೂ ಭೂಮಿಯಲ್ಲಿರುವುದೆಲ್ಲವೂ ಅಲ್ಲಾಹನದ್ದೇ ಆಗಿವೆ. ಅಲ್ಲಾಹನು ಪರಾಶ್ರಯ ನಿರಪೇಕ್ಷನೂ ಸ್ತುತ್ಯರ್ಹನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(4,132,'4:132','ಆಕಾಶಗಳಲ್ಲಿ ಮತ್ತು ಭೂಮಿಯಲ್ಲಿರುವುದೆಲ್ಲ ಅಲ್ಲಾಹನದ್ದು. ಅದಕ್ಕೆ ಸಾಕ್ಷಿಯಾಗಿ ಅಲ್ಲಾಹನೇ ಸಾಕು.');
INSERT INTO "kannada" VALUES(4,133,'4:133','ಮನುಷ್ಯರೇ! ಅವನು ಉದ್ದೇಶಿಸಿದರೆ ನಿಮ್ಮನ್ನು ನಿರ್ವಂಶ ಮಾಡಿ ಇನ್ನೊಂದು ಜನ ವಿಭಾಗ ವನ್ನು ತರುವನು. ಅಲ್ಲಾಹನು ಅದಕ್ಕೆ ಸಂಪೂರ್ಣ ಸಾಮಥ್ರ್ಯವುಳ್ಳವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,134,'4:134','ಯಾವನಾದರೂ ಐಹಿಕ ಪ್ರತಿಫಲವನ್ನು ಇಚ್ಛಿಸುವುದಾದರೆ (ಅವನ ಕೃತ್ಯ ಬಲು ಹೀನ ವಾದುದು). ಇಹಲೋಕ ಮತ್ತು ಪರಲೋಕದ ಪ್ರತಿಫಲ ಅಲ್ಲಾಹನ ಬಳಿ ಉಂಟು. ಅಲ್ಲಾಹನು ಸರ್ವಶ್ರುತನೂ ಸರ್ವವೀಕ್ಷಕನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,135,'4:135','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನೀವು ಅಲ್ಲಾಹನಿಗಾಗಿ ಸತ್ಯಸಾಕ್ಷಿಗಳಾಗಿ ಕಟ್ಟುನಿಟ್ಟಾಗಿ ನೀತಿಯನ್ನು ಪಾಲಿಸುವವರಾಗಿರಿ. ಅದು ನಿಮ್ಮ ಸ್ವಂತದ್ದರ ವಿರುದ್ಧವಾದರೂ ಅಥವಾ ನಿಮ್ಮ ತಾಯಿ ತಂದೆಯರು, ಆಪ್ತ ಬಂಧುಗಳ ವಿರುದ್ಧವಾದರೂ ಸರಿ. ಕಕ್ಷಿಯು ಧನಿಕನಿದ್ದರೂ ಬಡವನಿದ್ದರೂ ಅವರೊಂದಿಗೆ ಹೆಚ್ಚು ಸಂಬಂಧ ಹೊಂದಿದವನು ಅಲ್ಲ್ಲಾಹನಾಗಿರುತ್ತಾನೆ. ಆದ್ದರಿಂದ ನೀವು ನ್ಯಾಯ ಪಾಲಿಸದೆ ತನ್ನಿಚ್ಛೆಯನ್ನು ಅನುಸರಿಸದಿರಿ. ನೀವು (ಸಾಕ್ಷ್ಯವನ್ನು) ತಿರುಚಿದರೆ ಅಥವಾ ಸಾಕ್ಷಿ ಹೇಳದೆ ತಪ್ಪಿಸಿ ಕೊಂಡರೆ ಅಲ್ಲಾಹು ನಿಮ್ಮ ಕೃತ್ಯಗಳ ಬಗ್ಗೆ ಚೆನ್ನಾಗಿ ಅರಿಯುವವನಾಗಿದ್ದಾನೆ. (ಅದರ ತಕ್ಕ ಶಿಕ್ಷೆಯನ್ನು ಕೊಡುವವನಾಗಿದ್ದಾನೆ.)');
INSERT INTO "kannada" VALUES(4,136,'4:136','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನೀವು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ದೂತರಲ್ಲಿ ಹಾಗೂ ಅವನು ತನ್ನ ದೂತರಿಗೆ ಅವತೀರ್ಣಗೊಳಿಸಿದ ವೇದಗ್ರಂಥ ದಲ್ಲಿ ಮತ್ತು ಅದಕ್ಕೆ ಮೊದಲು ಅವತೀರ್ಣಗೊಳಿಸಿದ ವೇದಗ್ರಂಥಗಳಲ್ಲಿ ವಿಶ್ವಾಸ ತಾಳಿರಿ . ಯಾವನಾದರೂ ಅಲ್ಲಾಹನನ್ನೂ ಆತನ ದೇವಚ ರರನ್ನೂ ಅವನ ವೇದಗ್ರಂಥಗಳನ್ನೂ ಅವನ ದೂತರನ್ನೂ ಪರಲೋಕವನ್ನೂ ನಿಷೇಧಿಸಿದರೆ ಅವನು ದಾರಿಗೆಟ್ಟು ಸತ್ಯದಿಂದ ಬಲುದೂರ ಸಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,137,'4:137','ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಸ್ವೀಕರಿಸಿ ನಂತರ ನಿಷೇಧಿಸಿ ಪುನಃ ವಿಶ್ವಾಸವಿಟ್ಟು, ಪುನಃ ನಿಷೇಧಿಸಿ ಆಮೇಲೆ ನಿರಾಕರಣೆಯನ್ನೇ ಹೆಚ್ಚಿಸಿಕೊಂಡವರನ್ನು ಅಲ್ಲಾಹು ಎಂದೆಂದಿಗೂ ಕ್ಷಮಿಸಲಾರನು ಮತ್ತು ಅವರಿಗೆ ಸನ್ಮಾರ್ಗದರ್ಶನ ಮಾಡಿಸ ಲಾರನು .');
INSERT INTO "kannada" VALUES(4,138,'4:138','ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನು ಬಿಟ್ಟು ಸತ್ಯನಿಷೇಧಿ ಗಳನ್ನು ಮಿತ್ರರನ್ನಾಗಿ ಮಾಡಿಕೊಂಡ ಮುನಾಫಿಕರಿಗೆ ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆ ಸಿದ್ಧವಿದೆಯೆಂಬ ಸುವಾರ್ತೆಯನ್ನು ಅರುಹಿ ಬಿಡಿರಿ.');
INSERT INTO "kannada" VALUES(4,139,'4:139','ಇವರೇನು ಅವರ ಕಡೆಯಿಂದ ಮಾನ್ಯತೆಯನ್ನು ನಿರೀಕ್ಷಿಸುವರೇನು? ವಸ್ತುತಃ ಪ್ರತಿಷ್ಟೆಯು ಸರ್ವ ಸಂಪೂರ್ಣವಾಗಿ ಅಲ್ಲಾಹನಿಗೇ ಆಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(4,140,'4:140','ಅಲ್ಲಾಹನ ಸೂಕ್ತಗಳ ವಿರುದ್ಧ ಅವಿಶ್ವಾಸದ ಮಾತುಗಳನ್ನಾಡುತ್ತಿರುವುದನ್ನೂ ಅವು ಅಣಕಿಸಲ್ಪಡುತ್ತಿರುವುದನ್ನೂ ನೀವು ಕೇಳಿದಾಗ, ಅವರು ಬೇರೆ ಮಾತನ್ನಾರಂಭಿಸುವವರೆಗೂ ಅಲ್ಲಿ ಕುಳಿತು ಕೊಳ್ಳಬೇಡಿರಿ. ಅನ್ಯಥಾ ನೀವೂ ಅವರಂತೆಯೇ ಆಗುತ್ತೀರಿ ಎಂದು ಅಲ್ಲಾಹು ನಿಮಗೆ ಈ ಗ್ರಂಥದಲ್ಲಿ ಮೊದಲೇ ಅಪ್ಪಣೆ ಕೊಟ್ಟಿದ್ದಾನೆ. ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಕಪಟ ವಿಶ್ವಾಸಿಗಳನ್ನೂ ಸತ್ಯನಿಷೇಧಿಗಳನ್ನೂ ನರಕದಲ್ಲಿ ಒಟ್ಟುಗೂಡಿಸುವವನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(4,141,'4:141','ಈ ಕಪಟವಿಶ್ವಾಸಿಗಳು ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ನಿರೀಕ್ಷೆಯಲ್ಲಿರುತ್ತಾರೆ. ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ನಿಮಗೆ ವಿಜಯ ಪ್ರಾಪ್ತಿಯಾದರೆ ಇವರು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದು, ನಾವೂ ನಿಮ್ಮೊಂದಿಗೆ ಇರಲಿಲ್ಲವೇ? ಎನ್ನುವರು. ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಮೇಲುಗೈ ಯಾದರೆ, ನಿಮ್ಮ ವಿರುದ್ಧ ನಮಗೆ ತಾಕತ್ತಿದ್ದು ಕೂಡಾ (ಯುದ್ಧ ಮಾಡದೆ) ನಿಮ್ಮನ್ನು ಬಿಟ್ಟು ಬಿಡಲಿಲ್ಲವೇ ? ನಿಮ್ಮನ್ನು ಮುಸಲ್ಮಾನರಿಂದ ನಾವು ರಕ್ಷಿಸಲಿಲ್ಲವೇ? ಎಂದು ಸತ್ಯನಿಷೇಧಿಗಳೊಂದಿಗೆ ಇವರು ಹೇಳುತ್ತಾರೆ. ಆದುದರಿಂದ ನಿಮ್ಮ ಮತ್ತು ಅವರ ಪ್ರಕರಣದ ತೀರ್ಮಾನವನ್ನು ಅಲ್ಲಾಹು ಪುನರುತ್ಥಾನದ ದಿನದಂದು ಮಾಡುವನು. ಅಲ್ಲಾಹನು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆದುರಾಗಿ ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ವಿಜಯದ ಯಾವ ದಾರಿಯನ್ನು ಇರಿಸಿರುವುದೇ ಇಲ್ಲ .');
INSERT INTO "kannada" VALUES(4,142,'4:142','ಕಪಟಿಗಳು (ಅವರ ಭ್ರಮೆ ಪ್ರಕಾರ) ಅಲ್ಲಾಹ ನನ್ನು ವಂಚಿಸುತ್ತಿದ್ದಾರೆ . ಅಲ್ಲಾಹನು ಅವರ ವಂಚನೆಗೆ ತಕ್ಕ ಶಿಕ್ಷೆ ಕೊಡುವವ ನಾಗಿರುತ್ತಾನೆ. ಅವರು ನಮಾಜಿಗಾಗಿ ನಿಲ್ಲುವಾಗ ಉದಾಸೀನರಾಗಿಯೂ ಜನರಿಗೆ ತೋರಿಕೆಗಾಗಿಯೂ ಎದ್ದು ನಿಲ್ಲುತ್ತಾರೆ. ಸ್ವಲ್ಪವೇ ಹೊರತು ಅವರು ನಮಾಝ್ ಮಾಡುವುದೂ ಇಲ್ಲ!');
INSERT INTO "kannada" VALUES(4,143,'4:143','ಅವೆರಡರ (ಈಮಾನ್ - ಕುಫ್ರ್) ನಡುವೆ ಅವರು ಎಡತಾಕುವ ಸ್ಥಿತಿಯಲ್ಲಿ. (ಅವರು ತೋರಿಕೆಯ ನಮಾಝ್ ಮಾಡುವರು). ಅವರು ಇವರ ಕೂಟಕ್ಕೂ ಅಲ್ಲ, ಅವರ ಕೂಟಕ್ಕೂ ಅಲ್ಲ. ಯಾವನನ್ನು ಅಲ್ಲಾಹು ದಾರಿ ತಪ್ಪಿಸು ತ್ತಾನೋ ಅವನಿಗೆ ಸರಿದಾರಿಯನ್ನು ನೀನು ಕಾಣಲಾರೆ.');
INSERT INTO "kannada" VALUES(4,144,'4:144','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ಸತ್ಯವಿಶ್ವಾಸಿ ಗಳನ್ನು ಬಿಟ್ಟು ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ನಿಮ್ಮ ಆಪ್ತ ಮಿತ್ರರನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳಬೇಡಿರಿ. ನಿಮ್ಮ ವಿರುದ್ಧ ಅಲ್ಲಾಹುವಿಗೆ ಸ್ಪಷ್ಟ ಆಧಾರ ಉಂಟು ಮಾಡಲು ನೀವು ಬಯಸುತ್ತೀರಾ?');
INSERT INTO "kannada" VALUES(4,145,'4:145','ಕಪಟವಿಶ್ವಾಸಿಗಳು ನರಕದ ಅತ್ಯಂತ ಕೆಳಗಿನ ತಳದಲ್ಲಿರುವರು. ಅವರಿಗೆ ಯಾವನೇ ಒಬ್ಬ ಸಹಾಯಕನನ್ನು ನೀವು ಕಾಣಲಾರಿರಿ .');
INSERT INTO "kannada" VALUES(4,146,'4:146','ಅವರ ಪೈಕಿ ಪಶ್ವಾತ್ತಾಪ ಪಟ್ಟು ಮರಳಿದ, ತಮ್ಮ ಕರ್ಮ ವಿಧಾನವನ್ನು ಸರಿಪಡಿಸಿಕೊಂಡ, ಅಲ್ಲಾಹುವನ್ನು ಭದ್ರವಾಗಿ ಹಿಡಿದುಕೊಂಡ ಮತ್ತು ತಮ್ಮ ಧರ್ಮವನ್ನು ಅಲ್ಲಾಹನಿಗೇ ನಿಷ್ಕ ಳಂಕಗೊಳಿಸಿದ ಜನರು ಇದರಿಂದ ಹೊರತಾಗಿ ದ್ದಾರೆ. ಅವರು ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಜೊತೆಗಿರುವರು . ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಅಲ್ಲಾಹನು ಖಂಡಿ ತವಾಗಿಯೂ ಮಹತ್ವದ ಸತ್ಫಲವನ್ನು ದಯ ಪಾಲಿಸಲಿರುವನು.');
INSERT INTO "kannada" VALUES(4,147,'4:147','ನೀವು ಅಲ್ಲಾಹುವಿಗೆ ಕೃತಜ್ಞತೆ ತೋರಿದ್ದರೆ ಹಾಗೂ ವಿಶ್ವಾಸತಾಳಿದ್ದರೆ ಯಾವ ಶಿಕ್ಷೆಯನ್ನೂ ಅಲ್ಲಾಹನು ನಿಮಗೆ ಕೊಡಲಾರನು. ಅಲ್ಲಾಹು ಕೃತಜ್ಞತೆಯುಳ್ಳವನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,148,'4:148','ಕೆಟ್ಟ ಮಾತುಗಳನ್ನು ಬಹಿರಂಗಗೊಳಿಸುವು ದನ್ನು ಅಲ್ಲಾಹು ಮೆಚ್ಚುವುದಿಲ್ಲ. ಆದರೆ ಮರ್ದಿತನು ಇದಕ್ಕೆ ಹೊರತಾಗಿದ್ದಾನೆ . ಅಲ್ಲಾಹು ಸರ್ವಶ್ರುತನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,149,'4:149','ಆದರೂ ನೀವು ಯಾವುದಾದರೂ ಸತ್ಕಾರ್ಯವನ್ನು ಬಹಿರಂಗಗೊಳಿಸಿದರೆ ಇಲ್ಲವೇ ರಹಸ್ಯ ವಾಗಿ ಇರಿಸಿದರೆ ಅಥವಾ ಏನಾದರೊಂದು ತಪ್ಪನ್ನು ಕ್ಷಮಿಸುವುದಾದರೆ (ಬಹಳ ಉತ್ತಮ. ಯಾಕೆಂದರೆ) ಅಲ್ಲಾಹು ಬಹಳ ಹೆಚ್ಚು ಕ್ಷಮಿಸುವವನೂ ಪರಮ ಶಕ್ತನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(4,150,'4:150','ಅಲ್ಲಾಹು ಹಾಗೂ ಅವನ ಸಂದೇಶವಾಹಕರನ್ನು ನಿಷೇಧಿಸುವ, ಅಲ್ಲಾಹು ಹಾಗೂ ಅವನ ಸಂದೇ ಶ ವಾಹಕರ ನಡುವೆ ತಾರತಮ್ಯ ಮಾಡಬಯ ಸುವ, `ಕೆಲವರ ಮೇಲೆ ವಿಶ್ವಾಸ ತಾಳುತ್ತೇವೆ. ಕೆಲವರನ್ನು ನಾವು ನಿಷೇಧಿಸುತ್ತೇವೆ’ ಎಂದು ಹೇಳುವ ಮತ್ತು ಅವೆರಡರ (ವಿಶ್ವಾಸ ಮತ್ತು ಅವಿಶ್ವಾಸದ) ನಡುವೆ ಇನ್ನೊಂದು ಮಾರ್ಗ ವನ್ನು ಅವಲಂಬಿಸಲು ಉದ್ದೇಶಿಸುವವರು.');
INSERT INTO "kannada" VALUES(4,151,'4:151','ಅವರೇ ನಿಜವಾದ ಸತ್ಯನಿಷೇಧಿಗಳು. ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಅವಮಾನಕರ ಶಿಕ್ಷೆಯನ್ನು ನಾವು ಸಿದ್ಧಗೊಳಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(4,152,'4:152','ಅಲ್ಲಾಹನಲ್ಲಿ ಹಾಗೂ ಅವನ ಸಂದೇಶವಾಹಕ ರಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸುವ ಮತ್ತು ಅವರ ಮಧ್ಯೆ ಅಂತರ ಕಲ್ಪಿಸದವರಾರೋ ಅವರಿಗೆ ತಮ್ಮ ಪ್ರತಿಫಲವನ್ನು ಅಲ್ಲಾಹು ಕೊಡಲಿರುವನು. ಅಲ್ಲಾಹನು ಅತ್ಯಂತ ಹೆಚ್ಚು ದೋಷಮುಕ್ತಿ ನೀಡುವವನೂ, ಪರಮ ದಯಾಳುವೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(4,153,'4:153','ಬಾನಲೋಕದಿಂದ ನಾವು ಗ್ರಂಥವನ್ನು ಇಳಿಸಿ ಕೊಡಬೇಕೆಂದು ಗ್ರಂಥದವರು ತಮ್ಮಲ್ಲಿ ಕೇಳು ತ್ತಾರೆ. ಖಂಡಿತವಾಗಿಯೂ ಇವರು ಮೂಸಾರವ ರಲ್ಲಿ ಇದಕ್ಕೂ ದೊಡ್ಡದನ್ನು ಕೇಳಿದ್ದರು. `ಅಲ್ಲಾ ಹುವನ್ನು ನಮಗೆ ಕಣ್ಣಾರೆ ತೋರಿಸಿಕೊಡು’ ಎಂದು ಅವರು ಕೇಳಿದ್ದರು! ಆಗ ಅವರ ಅಕ್ರ ಮದ ಫಲವಾಗಿ ಒಮ್ಮೆಗೇ ಅವರ ಮೇಲೆ ಸಿಡಿ ಲೆರಗಿತ್ತು. ನಂತರ ಅವರಿಗೆ ಪ್ರತ್ಯಕ್ಷ ದೃಷ್ಟಾಂತ ಇದ್ದೂ ಕೂಡ ಕರುವನ್ನು ತಮ್ಮ ಆರಾಧ್ಯ ವಸ್ತುವನ್ನಾಗಿ ಮಾಡಿಕೊಂಡರು. ಇಷ್ಟಾಗಿಯೂ ನಾವು ಅವರನ್ನು ಮನ್ನಿಸಿದೆವು. ನಾವು ಮೂಸಾ ರವರಿಗೆ ನಿಜವಾಗಿಯೂ ಒಂದು ಪ್ರತ್ಯಕ್ಷ ಅಧಿಕಾರವನ್ನು ಕೊಟ್ಟಿದ್ದೆವು’.');
INSERT INTO "kannada" VALUES(4,154,'4:154','ಕರಾರು ಒಪ್ಪಂದ ಮಾಡಲೆಂದು `ಥೂರ್’ ಪರ್ವತವನ್ನು ನಾವು ಅವರ ಮೇಲೆ ಎತ್ತಿದೆವು. ನಾವು ಅವರೊಂದಿಗೆ; `ತಲೆತಗ್ಗಿಸಿಕೊಂಡು ನಗರದ ಬಾಗಿಲಲ್ಲಿ ಪ್ರವೇಶಿಸಿರಿ’ ಎಂದು ಹೇಳಿದೆವು. ಶನಿವಾರ ದಿನದಂದು ನೀವು ಹದ್ದುಮೀರ ಬೇಡಿರೆಂದು ನಾವು ಅವರಿಗೆ ಶಾಸನ ವಿಧಿಸಿದೆವು. ಭದ್ರವಾದ ಒಂದು ಕರಾರನ್ನು ನಾವು ಅವರಿಂದ ತೆಗೆದುಕೊಂಡೆವು.');
INSERT INTO "kannada" VALUES(4,155,'4:155','ಕೊನೆಗೆ ಅವರು ಕರಾರನ್ನು ಮುರಿದ, ಅಲ್ಲಾ ಹನ ದೃಷ್ಟಾಂತಗಳನ್ನು ತಿರಸ್ಕರಿಸಿದ, ಪ್ರವಾದಿ ಗಳನ್ನು ಅನ್ಯಾಯವಾಗಿ ಕೊಂದ, ಹಾಗೂ `ನಮ್ಮ ಹೃದಯಗಳು ಮುಚ್ಚಿವೆ’ ಎಂದು ಹೇಳಿದ ಕಾರಣಕ್ಕೆ. (ಅವರನ್ನು ನಾನು ಶಪಿಸಿದೆ.) ಅವರ ಅವಿಶ್ವಾಸದ ಫಲದಿಂದ ಅವರ ಹೃದಯಗಳಿಗೆ ಅಲ್ಲಾಹು ಮೊಹರು ಹಾಕಿದ್ದಾನೆ. ಆದ್ದರಿಂದ ಅವ ರಿಂದ ಸ್ವಲ್ಪ ಜನರೇ ಹೊರತು ಯಾರೂ ವಿಶ್ವಾಸ ತಾಳುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(4,156,'4:156','ಅವರು ತಾಳಿದ ಸತ್ಯನಿಷೇಧ ಹಾಗೂ ಅವರು ಮರ್ಯಮರ ಮೇಲೆ ಹೊರಿಸಿದ ಗಂಭೀರ ಸುಳ್ಳಾರೋಪದಿಂದಾಗಿ (ಅವರು ಅಭಿಶಪ್ತ ರಾದರು)');
INSERT INTO "kannada" VALUES(4,157,'4:157','ಮರ್ಯಮರ ಪುತ್ರರೂ ಅಲ್ಲಾಹುವಿನ ದೂತರೂ ಆದ ಈಸಾ ಮಸೀಹ್‍ನನ್ನು ನಾವು ಕೊಂದಿದ್ದೇವೆ ಎಂದು ಅವರು ಹೇಳಿದ ಸಲುವಾಗಿಯೂ, (ಅವರನ್ನು ಶಪಿಸಿದೆವು). ವಾಸ್ತವದಲ್ಲಿ ಅವರು ಈಸಾ ಮಸೀಹ್‍ರನ್ನು ಕೊಲ್ಲಲಿಲ್ಲ. ಶಿಲುಬೆಗೇರಿಸಲೂ ಇಲ್ಲ. ಆದರೆ ವಾಸ್ತವ ಅವರಿಗೆ ಅಸ್ಪಷ್ಟವಾಗಿತ್ತು. ಅವರ ವಿಷಯದಲ್ಲಿ ಭಿನ್ನಾಭಿಪ್ರಾಯ ತೋರಿದವರೂ ವಾಸ್ತವದಲ್ಲಿ ಸಂಶಯಗ್ರಸ್ತರಾಗಿರುತ್ತಾರೆ. ಕೇವಲ ಗುಮಾನಿಯನ್ನೇ ಅನುಸರಿಸುವುದು ಹೊರತು ಆ ವಿಷಯದಲ್ಲಿ ಅವರಿಗೆ ಯಾವ ಅರಿವೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(4,158,'4:158','ನಿಜವಾಗಿ ಅಲ್ಲಾಹನು ಅವರನ್ನು ತನ್ನ ಕಡೆಗೆ ಎತ್ತಿಕೊಂಡನು . ಅಲ್ಲಾಹು ಮಹಾ ಪ್ರತಾಪ ಶಾಲಿಯೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(4,159,'4:159','ಗ್ರಂಥದವರ ಪೈಕಿ ಯಾರೂ ತನ್ನ ಮರಣಕ್ಕೆ ಮುಂಚೆ ಈಸಾರವರಲ್ಲಿ ವಿಶ್ವಾಸವಿಡದೆ ಉಳಿಯಲಿಕ್ಕಿಲ್ಲ . ಪುನರುತ್ಥಾನದಿನದಂದು ಈಸಾರವರು ಇವರ ವಿರುದ್ಧ ಸಾಕ್ಷಿ ಹೇಳುವರು.');
INSERT INTO "kannada" VALUES(4,160,'4:160','ಜೂದ ಮತದವರ ಅನ್ಯಾಯದ ಹಾಗೂ ಜನರನ್ನು ಅಲ್ಲಾಹುವಿನ ಮಾರ್ಗದಿಂದ ಅವರು ತೀವ್ರವಾಗಿ ತಡೆದುದರ ಫಲವಾಗಿ ಅವರಿಗೆ ಧರ್ಮ ಸಮ್ಮತವಾಗಿದ್ದ ಹಲವು ಒಳ್ಳೆಯ ವಸ್ತುಗಳನ್ನು ನಾವು ಅವರಿಗೆ ನಿಷಿದ್ಧಗೊಳಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(4,161,'4:161','ಅವರಿಗೆ ನಿಷಿದ್ಧಗೊಳಿಸಿದ್ದ ಬಡ್ಡಿಯನ್ನು ಅವರು ವಸೂಲು ಮಾಡುತ್ತಲೂ ಜನರ ಸೊತ್ತುಗಳನ್ನು ಅವರು ಅನ್ಯಾಯವಾಗಿ ಭೋಗಿಸುತ್ತಲೂ ಇದ್ದ ಕಾರಣದಿಂದ (ಅವರಿಗೆ ಸಮ್ಮತವಾಗಿದ್ದುದನ್ನು ನಾವು ನಿಷೇಧಿಸಿದ್ದೇವೆ.) ಅವರ ಪೈಕಿ ಸತ್ಯ ನಿಷೇಧಿಗಳಿಗೆ ನಾವು ಯಾತನಾಮಯ ಶಿಕ್ಷೆಯನ್ನು ಸಿದ್ಧಗೊಳಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(4,162,'4:162','ಆದರೆ ಅವರಲ್ಲಿ ಆಳ ಜ್ಞಾನವುಳ್ಳವರು ಮತ್ತು ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ನಿಮ್ಮ ಮೇಲೆ ಅವತೀರ್ಣ ಗೊಳಿಸಲ್ಪಟ್ಟ ಹಾಗೂ ನಿಮಗಿಂತ ಮೊದಲು ಅವತೀರ್ಣಗೊಳಿಸಲ್ಪಟ್ಟ ವೇದಗ್ರಂಥಗಳಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸುತ್ತಾರೆ. ನಮಾಝನ್ನು ಕೃತ್ಯ ನಿಷ್ಠೆಯಿಂದ ನಿರ್ವಹಿಸುವವರವರು. ಅವರು ಝಕಾತ್ ಕೊಡುವವರೂ ಅಲ್ಲಾಹು ಮತ್ತು ಪರಲೋಕದಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸುವವರೂ ಆಗಿರುವರು. ಖಂಡಿತವಾಗಿಯೂ ಅವರಿಗೆ ಘನವೆತ್ತ ಪ್ರತಿಫಲವನ್ನು ನಾವು ಕೊಡಲಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(4,163,'4:163','ಓ ಪೈಗಂಬರರೇ, ನೂಹ್ ಮತ್ತು ಅನಂತರದ ಪ್ರವಾದಿಗಳ ಕಡೆಗೆ ನಾವು ವಹ್ಯ್ ಕಳುಹಿ ಸಿದಂತೆಯೇ ನಿಮ್ಮ ಕಡೆಗೂ ವಹ್ಯ್ ಕಳುಹಿ ಸಿರುತ್ತೇವೆ. ಇಬ್‍ರಾಹೀಮ್, ಇಸ್ಮಾಈಲ್, ಇಸ್ಹಾಕ್, ಯಅïಕೂಬ್ ಹಾಗೂ ಅವರ ವಂಶದವರಿಗೆ ಮತ್ತು ಈಸಾ, ಅಯ್ಯೂಬ್, ಯೂನುಸ್, ಹಾರೂನ್, ಸುಲೈಮಾನ್ ರವರಿಗೂ ವಹ್ಯ್ ಕಳುಹಿಸಿದಂತೆಯೇ. ದಾವೂದರಿಗೆ ನಾವು ಝಬೂರ್ ನೀಡಿದೆವು.');
INSERT INTO "kannada" VALUES(4,164,'4:164','ಅದೇ ರೀತಿ ನಿಮಗೆ ನಾವು ಈ ಮೊದಲು ನಿಜವಾಗಿಯೂ ಹಲವು ಪ್ರವಾದಿಗಳ ಚರಿತ್ರೆ ತಿಳಿಸಿಕೊಟ್ಟಿದ್ದೇವೆ. ಹಲವು ಪ್ರವಾದಿಗಳ ಚರಿತ್ರೆಯನ್ನು ನಿಮಗೆ ನಾವು ಹೇಳಿಲ್ಲ . ಮೂಸಾರೊಂದಿಗೆ ಅಲ್ಲಾಹನು ಸ್ಪಷ್ಟವಾಗಿ ಮಾತಾಡಿರುವನು.');
INSERT INTO "kannada" VALUES(4,165,'4:165','ಅಂದರೆ (ಅವರನ್ನು) ಸುವಾರ್ತೆ ಕೊಡುವ, ಮುನ್ನೆಚ್ಚರಿಕೆ ಕೊಡುವ ಪ್ರವಾದಿಗಳಾಗಿ (ಕಳುಹಿಸಲಾಗಿದೆ). ಅದು ಅವರ ನಂತರ ಅಲ್ಲಾಹುವಿನ ಸನ್ನಿಧಾನದಲ್ಲಿ ಜನರಿಗೆ ನೆವಕ್ಕೆ ಅವಕಾಶವಿಲ್ಲದಿರಲಿಕ್ಕಾಗಿತ್ತು. ಅಲ್ಲಾಹನು ಪರಮಾಜೇಯನೂ ಪರಮಯುಕ್ತಿವಂತನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(4,166,'4:166','ಆದರೆ ತಮಗೆ ಅಲ್ಲಾಹನು ಅವತೀರ್ಣ ಗೊಳಿಸಿದುದಕ್ಕೆ ಅಲ್ಲಾಹನೇ ಸಾಕ್ಷ್ಯವಹಿಸು ತ್ತಾನೆ. ಅದರ ಬಗ್ಗೆ ಸರಿಯಾದ ಅರಿವು ಇದ್ದು ಕೊಂಡೇ ಅಲ್ಲಾಹನು ಅದನ್ನು ಅವತೀರ್ಣ ಗೊಳಿಸಿರುತ್ತಾನೆ. ಮಲಕ್‍ಗಳೂ ಇದಕ್ಕೆ ಸಾಕ್ಷ್ಯವಹಿಸುತ್ತಾರೆ. ಸಾಕ್ಷಿಯಾಗಿ ಅಲ್ಲಾಹನೇ ಧಾರಾಳ ಸಾಕು.');
INSERT INTO "kannada" VALUES(4,167,'4:167','ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸಿದವರು ಹಾಗೂ ಅಲ್ಲಾ ಹುವಿನ ಮಾರ್ಗದಿಂದ ಜನರನ್ನು ತಡೆದವರು ಖಂಡಿತ ದಾರಿಗೆಟ್ಟು ಬಲುದೂರ ಸಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(4,168,'4:168','ಹೀಗೆ ಸತ್ಯನಿಷೇಧ ತಾಳಿದ ಹಾಗೂ ಅಕ್ರಮ, ಅನ್ಯಾಯವೆಸಗಿದ ಜನರನ್ನು ಅಲ್ಲಾಹನು ಖಂಡಿತ ಕ್ಷಮಿಸಲಾರನು. ರಕ್ಷೆಯ ಮಾರ್ಗಕ್ಕೆ ಅವರನ್ನು ಸೇರಿಸಲಾರನು .');
INSERT INTO "kannada" VALUES(4,169,'4:169','ಅವರಿಗೆ ನರಕದ ವಿನಾ ಬೇರಾವ ಮಾರ್ಗ ವನ್ನೂ ತೋರಿಸಲಾರನು. ಅವರು ಅದರಲ್ಲೇ ಎಂದೆಂದಿಗೂ ಶಾಶ್ವತವಾಗಿ ನರಳುವರು. ಅಲ್ಲಾಹುವಿಗೆ ಇದು ಬಹಳ ಸುಲಭ ವಾಗಿರುತ್ತದೆ .');
INSERT INTO "kannada" VALUES(4,170,'4:170','ಓ ಜನರೇ, ಈ ಸಂದೇಶವಾಹಕರು ನಿಮ್ಮ ಬಳಿಗೆ ನಿಮ್ಮ ಪಾಲಕ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಸತ್ಯವನ್ನು ತಂದಿದ್ದಾರೆ. ಆದ್ದರಿಂದ ನೀವು ವಿಶ್ವಾಸ ತಾಳಿರಿ. ಅದು ನಿಮಗೆ ಗುಣಕರ. ಇನ್ನು ನೀವು ನಿಷೇಧಿಸಿದರೆ ಆಗಸಗಳಲ್ಲಿ ಮತ್ತು ಭೂಮಿಯಲ್ಲಿರುವುದೆಲ್ಲ ಅಲ್ಲಾಹನದ್ದೇ ಆಗಿದೆ. (ಆದ್ದರಿಂದ ನಿಮ್ಮ ನಿಷೇಧದಿಂದ ಅವನಿಗೆ ಬಾಧಕವಿಲ್ಲ). ಅಲ್ಲಾಹನು ಸರ್ವಜ್ಞನೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(4,171,'4:171','ಓ ಗ್ರಂಥದವರೇ! ನಿಮ್ಮ ಧರ್ಮದಲ್ಲಿ ನೀವು ಹದ್ದುಮೀರಿ ವರ್ತಿಸದಿರಿ. ಅಲ್ಲಾಹುವಿನ ಮೇಲೆ ಸತ್ಯವಲ್ಲದ್ದನ್ನು ಹೇಳದಿರಿ. ಮರ್ಯಮರ ಪುತ್ರ ಈಸಾ ಮಸೀಹರು ಅಲ್ಲಾಹುವಿನ ದೂತರಾಗಿರುವರು. ಮರ್ಯಮರಿಗೆ ಆತನು ಕಳುಹಿಸಿ ಕೊಟ್ಟ ವಚನವೂ ಆತನ ಕಡೆಯ ಒಂದು ಆತ್ಮವೂ ಆಗಿರುವರು. ಆದ್ದರಿಂದ ಅಲ್ಲಾಹು ಮತ್ತು (ಈಸಾ ಸಮೇತ) ಆತನ ಪ್ರವಾದಿಗಳಲ್ಲಿ ವಿಶ್ವಾಸ ತಾಳಿರಿ. `ದೇವರು ಮೂವರಿದ್ದಾರೆ’ ಎಂದು ನೀವು ಹೇಳಬೇಡಿರಿ. (ಹಾಗೆ ಹೇಳುವುದನ್ನು) ನೀವು ನಿಲ್ಲಿಸಿರಿ. ಅದುವೇ ನಿಮಗೆ ಕ್ಷೇಮ. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಏಕದೇವನಾಗಿರುವನು. ಸಂತಾನ ವುಂಟಾಗುವುದರಿಂದ ಅವನು ಪರಿಶುದ್ಧನಾಗಿರುವನು. ಆಕಾಶಗಳಲ್ಲಿಯೂ ಭೂಮಿಯಲ್ಲಿಯೂ ಇರುವುದೆಲ್ಲ ಆತನ ಒಡೆತನಕ್ಕೆ ಸೇರಿವೆ. ಇದಕ್ಕೆಲ್ಲ ಪ್ರಮಾಣವಾಗಿ ಅಲ್ಲಾಹು ಸಾಕು.');
INSERT INTO "kannada" VALUES(4,172,'4:172','ಈಸಾ ಮಸೀಹರು ತಾನು ಅಲ್ಲಾಹನ ದಾಸ ಎಂದೆನಿಸಿಕೊಳ್ಳಲು ಖಂಡಿತ ಸಂಕೋಚಪಡ ಲಿಲ್ಲ. ಅತ್ಯಂತ ನಿಕಟವರ್ತಿ ದೇವಚರರೂ ಈ ಬಗ್ಗೆ ಸಂಕೋಚಪಡುವುದಿಲ್ಲ. ಯಾವನಾದರೂ ಅಲ್ಲಾಹನ ದಾಸ್ಯದ (ವಿಧೇಯತ್ವದ) ಬಗ್ಗೆ ಅಪಮಾನವೆಂದು ಬಗೆದು ಸಂಕೋಚಪಟ್ಟರೆ ಮತ್ತು ಅಹಂಭಾವ ತೋರಿದರೆ, ಅವರೆಲ್ಲರನ್ನೂ ಅಲ್ಲಾಹುವಿನ ಕಡೆಗೆ ಒಟ್ಟುಗೂಡಿಸಲಿಕ್ಕಿರುವುದು (ನೆನಪಿರಲಿ).');
INSERT INTO "kannada" VALUES(4,173,'4:173','ಆದರೆ ಸತ್ಯ ನಂಬಿಕೆ ಹೊಂದಿ ಸುಕೃತಗಳನ್ನು ಮಾಡಿದವರಿಗೆ ಅದರ ಪ್ರತಿಫಲಗಳನ್ನು ಅವನು ಪೂರ್ತಿ ಮಾಡಿಕೊಡುವನು. ಅಲ್ಲದೆ ತನ್ನ ಔದಾರ್ಯದಿಂದ ಅವರಿಗೆ ಮತ್ತಷ್ಟು ಜಾಸ್ತಿ ಕೊಡುವನು. ಅವನಿಗೆ ಶರಣಾಗುವುದನ್ನು ಅವಮಾನವೆಂದು ಬಗೆದವರಿಗೆ ಹಾಗೂ ಪೊಗರು ತೋರುವವರಿಗೆ ಅವನು ಯಾತನಾ ಮಯ ಶಿಕ್ಷೆಯನ್ನು ಕೊಡುವನು. ಅವರಿಗೆ ಅಲ್ಲಾಹನ ಹೊರತು ಬೇರೆ ಓರ್ವ ರಕ್ಷಕನೋ ಸಹಾಯಕನೋ ಸಿಗಲಾರರು.');
INSERT INTO "kannada" VALUES(4,174,'4:174','ಓ ಜನರೇ, ನಿಮ್ಮ ಪಾಲಕ ಪ್ರಭುವಿನ ವತಿ ಯಿಂದ ನಿಮ್ಮ ಬಳಿಗೆ ಸಾಕ್ಷ್ಯಧಾರವು ಬಂದಿರುತ್ತದೆ. ಸುವ್ಯಕ್ತ ಪ್ರಕಾಶವನ್ನು ನಿಮಗೆ ನಾವು ಕಳುಹಿಸಿದ್ದೇವೆ .');
INSERT INTO "kannada" VALUES(4,175,'4:175','ಆದ್ದರಿಂದ ಯಾರು ಅಲ್ಲಾಹುವಿನಲ್ಲಿ ನಂಬಿಕೆ ಇರಿಸುತ್ತಾರೋ ಮತ್ತು ಆತನನ್ನೇ ಭದ್ರವಾಗಿ ಅವಲಂಬಿಸುತ್ತಾರೋ ಅವರಿಗೆ ಅಲ್ಲಾಹನು ತನ್ನ ಒಲವು ಹಾಗೂ ಅನುಗ್ರಹಗಳಿಗೆ ಪ್ರವೇಶ ಕೊಡುವನು. ಮತ್ತು ತನ್ನ ಕಡೆಗೆ ಬರುವ ನೇರ ಹಾದಿಯಲ್ಲಿ ಅವರನ್ನು ನಡೆಸುವನು.');
INSERT INTO "kannada" VALUES(4,176,'4:176','ಓ ಪೈಗಂಬರರೇ, ಜನರು ನಿಮ್ಮೊಡನೆ ಧರ್ಮ ವಿಧಿ ಕೇಳುತ್ತಾರೆ. ಹೇಳಿರಿ : ಅಲ್ಲಾಹು ನಿಮಗೆ ‘ಕಲಾಲಾ’ದ ವಿಷಯದಲ್ಲಿ ಧರ್ಮವಿಧಿ ಕೊಡುತ್ತಾನೆ. ಯಾರಾದರೂ ಮಕ್ಕಳಿಲ್ಲದೆ ಮೃತಪಟ್ಟರೆ ಮತ್ತು ಅವನಿಗೆ ಓರ್ವ ಸಹೋದರಿ ಇದ್ದರೆ, ಅವನು ಬಿಟ್ಟು ಹೋದ ಸೊತ್ತಿನ ಅರ್ಧ ಪಾಲು ಅವಳಿಗಿದೆ. ಅವಳಿಗೆ (ಮೃತ ಸಹೋದರಿಗೆ) ಮಕ್ಕಳಿಲ್ಲದಿದ್ದರೆ ಸಹೋದರನು ಅವಳ ಸೊತ್ತಿಗೆ ವಾರಸುದಾರನಾಗುತ್ತಾನೆ. ಮೃತನ ವಾರೀಸುದಾರರಾಗಿ ಇಬ್ಬರು ಸಹೋದರಿಯರಿದ್ದರೆ ಸಹೋದರನು ಬಿಟ್ಟು ಹೋದ ಸೊತ್ತಿನ ಮೂರರಲ್ಲೆರಡಂಶ ಅವರಿಬ್ಬರ ಪಾಲಿಗಿದೆ. ಅನೇಕ ಮಂದಿ ಸಹೋದರ ಸಹೋ ದರಿಯರಿದ್ದರೆ ಎರಡು ಸ್ತ್ರೀಯರ ಪಾಲಿಗೆ ಸಮಾ ನವಾದುದು ಓರ್ವ ಪುರುಷನ ಪಾಲಿಗಿದೆ. ನೀವು ದಾರಿತಪ್ಪಿ ಹೋಗದಿರಲೆಂದು ನಿಮಗೆ ಅಲ್ಲಾಹು ವಿಶಧೀಕರಿಸುತ್ತಾನೆ. ಅಲ್ಲಾಹ್ ಪ್ರತಿ ಯೊಂದು ವಿಚಾರದಲ್ಲಿ ಪರಮ ತಜ್ಞನಾ ಗಿರುವನು.');
INSERT INTO "kannada" VALUES(5,1,'5:1','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಕರಾರುಗಳನ್ನು ಪಾಲಿಸಿರಿ. ನಿಮಗೆ ಜಾನುವಾರುಗಳನ್ನು ಅನುವದನೀಯ ಗೊಳಿಸಲಾಗಿದೆ. ಇನ್ನು ಓದಿ ಕೇಳಿಸಲಾಗುವ ಪ್ರಾಣಿಗಳ ಹೊರತು - ಆದರೆ ಇಹ್ರಾಮಿನಲ್ಲಿ ರುತ್ತ ನೀವು ಬೇಟೆಯಾಡುವುದನ್ನು ಧರ್ಮಸಮ್ಮತ ಗೊಳಿಸಬೇಡಿರಿ. ನಿಸ್ಸಂದೇಹವಾಗಿಯೂ ಅಲ್ಲಾಹು ತಾನಿಚ್ಛಿಸುವುದನ್ನು ವಿಧಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(5,2,'5:2','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಅಲ್ಲಾಹನ ಚಿಹ್ನೆಗಳನ್ನು ಅನಾದರಿಸಬೇಡಿರಿ. ಪವಿತ್ರ ಮಾಸವನ್ನೂ ಹರಮಿನ ಭಾಗಕ್ಕೆ ಕೊಂಡುಹೋಗುವ ಬಲಿ ಪ್ರಾಣಿಗಳನ್ನೂ ಹರಮಿನಲ್ಲಿ ಕಡಿಯುವ ಗುರು ತಿಗಾಗಿ ಕೊರಳಲ್ಲಿ ಪಟ್ಟಿಯಿರುವ ಪ್ರಾಣಿಗಳನ್ನೂ ತಮ್ಮ ಪ್ರಭುವಿನ ಅನುಗ್ರಹ ಹಾಗೂ ಅವನ ಸಂತೃಪ್ತಿಯನ್ನರಸುತ್ತ ಪವಿತ್ರಾಲಯದ ಕಡೆ ಹೋಗುತ್ತಿರುವವರನ್ನೂ ಅನಾದರಿಸಬಾರದು. ಆದರೆ ಇಹ್ರಾಮಿನ ಸ್ಥಿತಿಯಿಂದ ವಿರಮಿಸಿದರೆ ನೀವು ಬೇಟೆಯಾಡಿರಿ . ಒಂದು ಕೂಟವು ನಿಮ್ಮನ್ನು ಮಸ್ಜಿದುಲ್ ಹರಾಮ್‍ನಿಂದ ತಡೆಯಿತು ಎಂಬ ಕಾರಣಕ್ಕೆ ನಿಮಗಿರುವ ಕೋಪವು ಅವರೊಂದಿಗೆ ಅತಿರೇಕವನ್ನೆಸಗಲು ನಿಮ್ಮನ್ನು ಉದ್ರೇಕಿಸದಿರಲಿ . ಪುಣ್ಯ ಹಾಗೂ ದೇವ ಭಯದಲ್ಲಿ ನೀವು ಪರಸ್ಪರ ಸಹಾಯ ಮಾಡಿರಿ. ಪಾಪ ಹಾಗೂ ಅತಿರೇಕದಲ್ಲಿ ನೀವು ಪರಸ್ಪರ ಸಹಾಯ ಮಾಡ ಬೇಡಿರಿ. ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ. ಖಂಡಿತ ಅಲ್ಲಾಹು ಕಠಿಣ ದಂಡಕನು.');
INSERT INTO "kannada" VALUES(5,3,'5:3','ಶವ , ರಕ್ತ, ಹಂದಿ ಮಾಂಸ ಹಾಗೂ ಅಲ್ಲಾಹನ ಹೊರತು ಇತರರ ಹೆಸರಲ್ಲಿ ಚೂರಿ ಹಾಕಲಾದ ಪ್ರಾಣಿ , ಉಸಿರುಗಟ್ಟಿ ಅಥವಾ ಪೆಟ್ಟುತಾಗಿ ಅಥವಾ ಎತ್ತರದಿಂದ ಬಿದ್ದು ಅಥವಾ ಘರ್ಷಿಸಲ್ಪಟ್ಟು ಅಥವಾ ಕ್ರೂರ ಮೃಗ ತಿಂದುಳಿದ ಸತ್ತ ಪ್ರಾಣಿ - ಆದರೆ ಪ್ರಾಣ ಹೋಗುವ ಮುನ್ನ ನೀವು ಚೂರಿ ಹಾಕಿದ ಪ್ರಾಣಿಯ ಹೊರತು - ಹಾಗೂ ವಿಗ್ರಹಗಳಿಗೆ ಬಲಿ ಕೊಡಲ್ಪಟ್ಟ ಪ್ರಾಣಿ , ಮತ್ತು ಬಾಣಗಳಿಂದ ಹಂಚಿ ಕೊಂಡದ್ದು ನಿಮಗೆ ನಿಷಿದ್ಧಗೊಳಿಸಲಾಗಿದೆ. ಇವೆಲ್ಲ ಅಧರ್ಮ ಕಾರ್ಯಗಳು. ಇಂದು ಸತ್ಯ ನಿಷೇಧಿಗಳು ನಿಮ್ಮ ಧರ್ಮದ ಬೆಳವಣಿಗೆಯಿಂದ ಸಂಪೂರ್ಣ ನಿರಾಶರಾಗಿದ್ದಾರೆ. ಆದುದರಿಂದ ನೀವು ಅವರನ್ನು ಭಯಪಡಬೇಡಿರಿ. ನನ್ನನ್ನು ಭಯಪಡಿರಿ. ಇಂದು ನಾನು ನಿಮ್ಮ ಧರ್ಮವನ್ನು ನಿಮಗಾಗಿ ಪರಿಪೂರ್ಣಗೊಳಿಸಿದ್ದೇನೆ. ನನ್ನ ಅನು ಗ್ರಹವನ್ನು ನಿಮ್ಮ ಮೇಲೆ ಸಂಪೂರ್ಣ ಗೊಳಿಸಿದ್ದೇನೆ ಮತ್ತು ಇಸ್ಲಾಮನ್ನು ನಿಮ್ಮ ಧರ್ಮವನ್ನಾಗಿ ಅಂಗೀಕರಿಸಿದ್ದೇನೆ. ಆದರೆ ಪಾಪದ ಕಡೆಗೆ ಮನಪೂರ್ವಕ ಒಲವಿಲ್ಲದೆ ಹಸಿವೆಯಿಂದ ನಿರ್ಬಂಧಿತ ನಾಗಿ ಯಾರಾದರೂ ಅವುಗಳನ್ನು ತಿಂದು ಬಿಟ್ಟರೆ, ಖಂಡಿತ ಅಲ್ಲಾಹು ಮಹಾಕ್ಷಮಾಶೀಲನೂ ದಯಾ ನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(5,4,'5:4','ಅವರಿಗೆ ಏನೇನೆಲ್ಲ ಅನುವದಿಸಲಾಗಿದೆ ಎಂದು ಅವರು ಕೇಳುತ್ತಾರೆ. ಹೇಳಿರಿ; ನಿಮಗೆ ಎಲ್ಲಾ ಉತ್ತಮ ವಸ್ತುಗಳನ್ನು ಧರ್ಮಸಮ್ಮತಗೊಳಿಸ ಲಾಗಿವೆ. ಅಲ್ಲಾಹು ನೀಡಿದ ಜ್ಞಾನದ ಆಧಾರದಲ್ಲಿ ನೀವು ಬೇಟೆಯಾಡಲು ಕಲಿಸಿಕೊಟ್ಟು ಪಳಗಿದ ಬೇಟೆ ಪ್ರಾಣಿಗಳು ನಿಮಗಾಗಿ ಬೇಟೆಯಾಡಿ ತಂದ ಪ್ರಾಣಿಗಳೂ ಧರ್ಮಸಮ್ಮತ. ಅವುಗಳನ್ನು ಭುಜಿಸಿರಿ. ಆದರೆ ಅದರ ಮೇಲೆ ಅಲ್ಲಾಹನ ನಾಮವನ್ನು ಉಚ್ಚರಿಸಿರಿ. ಅಲ್ಲಾಹನ ಕುರಿತು ಭಯಪಡಿರಿ. ಅಲ್ಲಾಹನು ವಿಚಾರಣೆಯಲ್ಲಿ ಅತಿ ಶೀಘ್ರನು.');
INSERT INTO "kannada" VALUES(5,5,'5:5','ಈ ದಿನ ಉತ್ತಮ ವಸ್ತುಗಳನ್ನು ನಿಮಗೆ ಧರ್ಮ ಸಮ್ಮತಗೊಳಿಸಲಾಗಿದೆ. ಗ್ರಂಥದವರ ಆಹಾರ ನಿಮಗೂ -ನಿಮ್ಮ ಆಹಾರ ಅವರಿಗೂ ಸಮ್ಮತ ವಾಗಿದೆ. ಸತ್ಯವಿಶ್ವಾಸಿ ಸ್ವತಂತ್ರ ಸ್ತ್ರೀಯರೂ ನಿಮ ಗಿಂತ ಮುನ್ನ ವೇದ ಗ್ರಂಥ ನೀಡಲ್ಪಟ್ಟವರ ಸ್ವತಂತ್ರ ಸ್ತ್ರೀಯರೂ ನೀವು ಪರಸ್ಯ - ರಹಸ್ಯ ಅನೈತಿಕ ಸಂಬಂಧ ಹೊಂದದೆ ಮದುವೆ ಮಾಡಿಕೊಂಡು ಅವರಿಗೆ ಅವರ ವಿವಾಹ ಮೌಲ್ಯ ನೀಡುವುದಾದರೆ ನಿಮಗೆ ಸಮ್ಮತವಾಗುತ್ತಾರೆ . ಯಾವನು ಸತ್ಯ ವಿಶ್ವಾಸವನ್ನು ತಿರಸ್ಕರಿಸು ತ್ತಾನೋ ಅವನ ಸುಕೃತ ನಿಷ್ಪಲವಾಗುವುದು. ಪರ ಲೋಕದಲ್ಲಿ ಅವನು ನತದೃಷ್ಟರ ಕೂಟದಲ್ಲಾಗುವನು.');
INSERT INTO "kannada" VALUES(5,6,'5:6','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ನಮಾಝಿಗೆಂದು ಹೊರಟಾಗ ನಿಮ್ಮ ಮುಖಗಳನ್ನು ಮತ್ತು ಕೈಗಳನ್ನು ಮೊಳಗಂಟಿನ ಸಮೇತ ತೊಳೆದು ಕೊಳ್ಳಿರಿ. ತಲೆಯನ್ನು ಸವರಿರಿ ಮತ್ತು ಪಾದ ಕಾಲು ಗಳನ್ನು ಮಣಿಗಂಟು ಸಮೇತ ತೊಳೆದುಕೊಳ್ಳಿರಿ . ನೀವು ಜನಾಬತ್ (ದೊಡ್ಡ ಅಶುದ್ಧಿ) ಸ್ಥಿತಿಯಲ್ಲಿದ್ದರೆ ಶುದ್ಧರಾಗಿಕೊಳ್ಳಿರಿ . ನೀವು ರೋಗಿಗಳಾಗಿದ್ದರೆ ಅಥವಾ ಪ್ರಯಾಣದಲ್ಲಿದ್ದರೆ ಅಥವಾ ನಿಮ್ಮಲ್ಲಾರಾದರೂ ಮಲಮೂತ್ರ ವಿಸರ್ಜನೆ ಮಾಡಿ ಬಂದರೆ ಅಥವಾ ನೀವು ಸ್ತ್ರೀಯರನ್ನು ಸ್ಪರ್ಶಿಸಿದ್ದರೆ ನೀರು ಸಿಗದ ಪರಿಸ್ಥಿತಿಯಲ್ಲಿ ಶುದ್ಧ ಮಣ್ಣನ್ನು ಬಯಸಿರಿ. (ಅದರ ಮೇಲೆ ಹಸ್ತಗಳನ್ನು ಬಡಿದು) ನಿಮ್ಮ ಮುಖ ಮತ್ತು ಕೈಗಳ ಮೇಲೆ ಸವರಿಕೊಳ್ಳಿರಿ . ನಿಮಗೆ ಯಾವುದೇ ತೊಂದರೆ ನೀಡಲು ಅಲ್ಲಾಹು ಇಚ್ಛಿಸುವುದಿಲ್ಲ. ಬದಲಾಗಿ ಅವನು ನಿಮ್ಮನ್ನು ಪರಿಶುದ್ಧಗೊಳಿಸಲಿಕ್ಕೂ ನಿಮ್ಮ ಮೇಲೆ ತನ್ನ ಅನ ಗ್ರಹವನ್ನು ಪರಿಪೂರ್ಣಗೊಳಿಸಲಿಕ್ಕೂ ಇಚ್ಛಿಸುತ್ತಾನೆ. ನೀವು ಕೃತಜ್ಞರಾಗುವಿರೆಂದು.');
INSERT INTO "kannada" VALUES(5,7,'5:7','ಅಲ್ಲಾಹನು ನಿಮಗೆ ದಯಪಾಲಿಸಿದ ಅನುಗ್ರಹವನ್ನು ಸ್ಮರಿಸಿರಿ ಮತ್ತು `ನಾವು ಆಲಿಸಿದೆವು ಮತ್ತು ಅನುಸರಿಸಿದೆವು ಎಂದು ನೀವು ಹೇಳಿದ ಸಂದರ್ಭದಲ್ಲಿ ಅಲ್ಲಾಹನು ನಿಮ್ಮಿಂದ ಪಡೆದ ಬಲವಾದ ಕರಾರನ್ನು ನೆನಪಿಸಿಕೊಳ್ಳಿರಿ . ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ. ಅಲ್ಲಾಹನು ಹೃದಯಗಳ ರಹಸ್ಯಗಳನ್ನು ತಿಳಿದಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(5,8,'5:8','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ಅಲ್ಲಾಹನಿಗಾಗಿ ಸತ್ಯದಲ್ಲೇ ನೆಲೆನಿಲ್ಲುವವರೂ ನ್ಯಾಯದ ಸಾಕ್ಷ್ಯ ವಹಿಸುವವರೂ ಆಗಿರಿ . ಒಂದು ವಿಭಾಗದವರ ಮೇಲಿನ ದ್ವೇಷವು ನಿಮ್ಮನ್ನು ನ್ಯಾಯ ಪಾಲಿಸದಂತೆ ರೇಗಿಸದಿರಲಿ. ನೀವು ನ್ಯಾಯಪಾಲಿಸಿರಿ, ಇದು ದೇವಭಯಕ್ಕೆ ಹೆಚ್ಚು ನಿಕಟವಾಗಿರುತ್ತದೆ. ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ. ನೀವು ಮಾಡುತ್ತಿರುವುದನ್ನೆಲ್ಲ ಅಲ್ಲಾಹನು ಖಂಡಿತ ಅರಿಯುವವನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(5,9,'5:9','ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಸ್ವೀಕರಿಸಿ ಸತ್ಕರ್ಮ ಮಾಡುವವರಿಗೆ ಪಾಪ ಮುಕ್ತಿ ಹಾಗೂ ಮಹತ್ತಾದ ಪ್ರತಿಫಲವನ್ನು ಅಲ್ಲಾಹನು ವಾಗ್ದಾನ ಮಾಡಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(5,10,'5:10','ಆದರೆ, ಯಾರು ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸಿ ನಮ್ಮ ನಿದರ್ಶನಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವರೋ, ಅವರೇ ನರಕವಾಸಿಗಳು.');
INSERT INTO "kannada" VALUES(5,11,'5:11','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಒಂದು ವಿಭಾಗವು ನಿಮ್ಮ ಮೇಲೆ ಕೈ ಮಾಡಲು ಯತ್ನಿಸಿದಾಗ ಅಲ್ಲಾ ಹನು ಅವರ ಕೈಗಳನ್ನು ನಿಮ್ಮಿಂದ ತಡೆಯುವ ಮೂಲಕ ನಿಮ್ಮ ಮೇಲೆ ಮಾಡಿದ ಅನುಗ್ರಹವನ್ನು ಸ್ಮರಿಸಿರಿ . ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ. ಸತ್ಯವಿಶ್ವಾಸವುಳ್ಳವರು ಅಲ್ಲಾಹನ ಮೇಲೆಯೇ ಭರವಸೆಯನ್ನಿರಿಸಿಕೊಳ್ಳಲಿ.');
INSERT INTO "kannada" VALUES(5,12,'5:12','ಅಲ್ಲಾಹನು ಇಸ್‍ರಾಈಲ ಸಂತತಿಗಳಿಂದ ಕರಾರನ್ನು ಪಡೆದನು . ನಾವು ಅವರಿಂದ ಹನ್ನೆರಡು ಮೇಲ್ವಿಚಾರಕರನ್ನು ನೇಮಕ ಮಾಡಿದೆವು. ಅಲ್ಲಾಹು ಅವರೊಡನೆ ಹೇಳಿದನು; ಖಂಡಿತ ನಾನು ನಿಮ್ಮೊಂದಿಗಿದ್ದೇನೆ. ನೀವು ನಮಾಝನ್ನು ನೆಲೆಗೊಳಿಸಿದರೆ, ಝಕಾತ್ ನೀಡಿದರೆ, ನನ್ನ ದೂತರ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸಿದರೆ, ಅವರಿಗೆ ಸಹಾಯ ಮಾಡಿದರೆ ಮತ್ತು ಅಲ್ಲಾಹನಿಗೆ ಉತ್ತಮ ಸಾಲವನ್ನು ನೀಡುತ್ತಲಿದ್ದರೆ, ನಿಶ್ಚಯ ವಾಗಿಯೂ ನಾನು ನಿಮ್ಮ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿ ಸುವೆನು. ಮಾತ್ರವಲ್ಲದೆ ತಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆ ಗಳು ಹರಿಯುತ್ತಿರುವ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ ನಿಮಗೆ ಖಂಡಿತ ಪ್ರವೇಶ ಕೊಡುವೆನು. ಆದರೆ, ಅನಂತರ (ಈ ಕರಾರಿನ ಬಳಿಕ) ನಿಮ್ಮಲ್ಲಾ ರಾದರೂ ಧಿಕ್ಕರಿಸಿದರೆ ಖಂಡಿತವಾಗಿಯೂ ಅವನು ನೇರ ದಾರಿಯಿಂದ ತಪ್ಪಿ ಹೋದನು’’.');
INSERT INTO "kannada" VALUES(5,13,'5:13','ಅನಂತರ ಅವರು ತಮ್ಮ ಕರಾರನ್ನು ಮುರಿದ ಕಾರಣಕ್ಕಾಗಿ ನಾವು ಅವರನ್ನು ಶಪಿಸಿದೆವು ಮತ್ತು ಅವರ ಹೃದಯಗಳನ್ನು ಕಠಿಣಗೊಳಿಸಿದೆವು. ಅವರು ವಚನಗಳನ್ನು ಅವುಗಳ ಸ್ಥಾನದಿಂದ ಪಲ್ಲಟಗೊಳಿಸುತ್ತಾರೆ. ಆ ಗ್ರಂಥದಲ್ಲಿ ಅವರಿಗೆ ಆದೇಶಿಸಲಾಗಿದ್ದ ಜ್ಞಾನದ ಒಂದು ಹಿಸೆಯನ್ನು ಅವರು ಮರೆತು ಬಿಟ್ಟಿದ್ದಾರೆ. ಅವರು ಎಸಗುತ್ತಿರುವ ವಂಚನೆಯನ್ನು ನೀವು ಪ್ರತ್ಯಕ್ಷ ಕಾಣುತ್ತಲಿರುವಿರಿ. ಅವರ ಪೈಕಿ ಸ್ವಲ್ಪ ಜನರ ಹೊರತು . ಅವರನ್ನು ಕ್ಷಮಿಸಿರಿ ಮತ್ತು ಅವರನ್ನು ನಿರ್ಲಕ್ಷಿಸಿರಿ . ಅಲ್ಲಾಹನು ಗುಣವಂತರನ್ನು ಪ್ರೀತಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(5,14,'5:14','ನಾವು ಕ್ರೈಸ್ತರು ಎಂದು ಹೇಳಿಕೊಂಡಿದ್ದವರಿಂದಲೂ ನಾವು ಕರಾರನ್ನು ಪಡೆದಿದ್ದೆವು. ಅವರೂ ಕೂಡಾ ಅವರಿಗೆ ನೀಡಲಾಗಿದ್ದ ಪಾಠದ ಒಂದು ಅಂಶವನ್ನು ಮರೆತುಬಿಟ್ಟರು. ತರುವಾಯ ನಾವು ಅವರ ನಡುವೆ ಅಂತ್ಯ ದಿನದ ತನಕವೂ ಹಗೆತನ ಮತ್ತು ದ್ವೇಷವನ್ನು ಬೆಸೆದೆವು . ಅವರಿಗೆ ಅವರು ಮಾಡುತ್ತಿದ್ದುದರ ಬಗ್ಗೆ ಅಲ್ಲಾಹನು ವಿವರ ನೀಡಲಿರುವನು.');
INSERT INTO "kannada" VALUES(5,15,'5:15','ಓ ಗ್ರಂಥದವರೇ, ನೀವು ವೇದಗ್ರಂಥದಿಂದ ಮರೆಮಾಚಿದ್ದ ಅನೇಕ ವಿಷಯಗಳನ್ನು ಬಹಿರಂಗಗೊಳಿಸುತ್ತ ನಮ್ಮ ದೂತರು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದಿರುತ್ತಾರೆ ಮತ್ತು ಅವರು ಅನೇಕ ವಿಷಯಗಳನ್ನು ಮಾಪಿ ಮಾಡುತ್ತಾರೆ . ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ಒಂದು ಪ್ರಕಾಶ ಮತ್ತು ಒಂದು ಸುವ್ಯಕ್ತ ಗ್ರಂಥವು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದಿದೆ.');
INSERT INTO "kannada" VALUES(5,16,'5:16','ಅದರ ಮೂಲಕ ಅಲ್ಲಾಹನು ತನ್ನ ಸಂತೃಪ್ತಿಯನ್ನು ಅನುಸರಿಸುವವರಿಗೆ ಶಾಂತಿಯ ಮಾರ್ಗಗಳಿಗೆ ಹಚ್ಚುತ್ತಾನೆ . ಹಾಗೂ ತನ್ನ ಇಚ್ಛೆಯಿಂದ ಅವರನ್ನು ಕತ್ತಲುಗಳಿಂದ ಬೆಳಕಿನ ಕಡೆಗೆ ಒಯ್ಯುತ್ತಾನೆ . ಅವರನ್ನು ನೇರವಾದ ದಾರಿಗೆ ಮಾರ್ಗದರ್ಶನ ನೀಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(5,17,'5:17','ಮರ್ಯಮರ ಪುತ್ರ `ಮಸೀಹರೇ `ಅಲ್ಲಾಹು’ ಎಂದು ಹೇಳಿದವರು ಖಂಡಿತವಾಗಿಯೂ ಕಾಫಿರ್ ಆದರು . ಓ ಪೈಗಂಬರರೇ, ನೀವು ಅವರೊಡನೆ ಕೇಳಿರಿ ‘ಅಲ್ಲಾಹನು ಮರ್ಯಮರ ಪುತ್ರ ಮಸೀಹರನ್ನು, ಅವರ ಮಾತೆಯನ್ನು ಮತ್ತು ಭೂಮಿಯಲ್ಲಿರುವ ಸರ್ವರನ್ನು ನಾಶ ಮಾಡಲಿಚ್ಛಿಸಿದರೆ ಅವನನ್ನು ತಡೆಯುವ ಶಕ್ತಿ ಯಾರಿಗಿದೆ? ಭೂಮಿ-ಆಕಾಶಗಳ ಮತ್ತು ಅವುಗಳ ನಡುವಿನ ಎಲ್ಲದರ ಅಧಿಪತ್ಯ ಅಲ್ಲಾಹನದ್ದಾಗಿದೆ. ಅವನು ತಾನಿಚ್ಛಿಸಿದುದನ್ನು ಸೃಷ್ಟಿಸುತ್ತಾನೆ. ಅಲ್ಲಾಹನು ಎಲ್ಲ ವಸ್ತುಗಳ ಮೇಲೆ ಸರ್ವ ಸಮರ್ಥನು.');
INSERT INTO "kannada" VALUES(5,18,'5:18','ನಾವು ಅಲ್ಲಾಹನ ಪುತ್ರರೂ ಪ್ರೀತಿ ಪಾತ್ರರೂ ಆಗಿರುತ್ತೇವೆ ಎಂದು ಜೂದರೂ ಕ್ರೈಸ್ತರೂ ಹೇಳುತ್ತಾರೆ. ನೀವು ಕೇಳಿರಿ; ಹಾಗಾದರೆ ಅವನು ನಿಮ್ಮನ್ನು ನಿಮ್ಮ ಪಾಪಗಳಿಗಾಗಿ ಶಿಕ್ಷಿಸುವುದೇಕೆ? ಹಾಗಲ್ಲ; ನೀವು ಅಲ್ಲಾಹನ ಸೃಷ್ಟಿಗಳಿಗೆ ಸೇರಿದ ಮನುಷ್ಯರು. ಅವನು ತಾನಿಚ್ಛಿಸಿದ ವರನ್ನು ಕ್ಷಮಿಸುತ್ತಾನೆ ಮತ್ತು ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ಶಿಕ್ಷಿಸುತ್ತಾನೆ. ಭೂಮಿ-ಆಕಾಶಗಳು ಹಾಗೂ ಅವುಗಳ ನಡುವೆ ಇರುವ ಎಲ್ಲವೂ ಅಲ್ಲಾಹನ ಆಧಿಪತ್ಯದಲ್ಲಿದ್ದು ಆತನೆಡೆಗೇ ಮರಳ ಬೇಕಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(5,19,'5:19','ಓ ಗ್ರಂಥದವರೇ, ಪ್ರವಾದಿಗಳ ಆಗಮನ ನಿಲುಗಡೆಯಾಗಿದ್ದ ಬಳಿಕ ಇದೀಗ ನಮ್ಮ ಸಂದೇಶವಾಹಕರು ನಿಮ್ಮ ಬಳಿಗೆ ಸ್ಪಷ್ಟ ಮಾಹಿತಿ ಖಂಡಿತ ಕೊಡಲು ಬಂದಿರುತ್ತಾರೆ . ಯಾವುದೇ ಸುವಾರ್ತೆಗಾರನೂ ಮುನ್ನೆಚ್ಚರಿಕೆದಾರನೂ ನೀಡುವವರು ನಮಗೆ ಬಂದಿಲ್ಲ ಎಂದು ನೀವು ಹೇಳದಿರಲೆಂದು. ಈಗ ಸುವಾರ್ತೆ ಮತ್ತು ಮುನ್ನೆಚ್ಚರಿಕೆ ಕೊಡುವವರು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದಿದ್ದಾರೆ. ಅಲ್ಲಾಹು ಸಕಲ ಕಾರ್ಯಗಳಿಗೆ ಸರ್ವ ಸಮರ್ಥನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(5,20,'5:20','ಮೂಸಾರವರು ತಮ್ಮ ಜನಾಂಗದವರಿಗೆ ಹೇಳಿದ ಸಂದರ್ಭ. ಓ ನನ್ನ ಜನಾಂಗದವರೇ, ಅಲ್ಲಾಹನು ನಿಮಗೆ ದಯಪಾಲಿಸಿದ ಅನುಗ್ರಹಗಳನ್ನು ಸ್ಮರಿಸಿರಿ. ಅವನು ನಿಮ್ಮಲ್ಲಿ ಪ್ರವಾದಿಗಳನ್ನು ನಿಯೋಜಿಸಿದನು. ನಿಮ್ಮನ್ನು ರಾಜರನ್ನಾಗಿ ಮಾಡಿದನು . ಜಗತ್ತಿನಲ್ಲಿ ಯಾರಿಗೂ ಕೊಟ್ಟಿ ರದ ಉಪಕಾರವನ್ನು ನಿಮಗೆ ನೀಡಿದನು.');
INSERT INTO "kannada" VALUES(5,21,'5:21','ಓ ನನ್ನ ಜನಾಂಗವೇ, ಅಲ್ಲಾಹನು ನಿಮಗೆ ದಾಖಲು ಮಾಡಿದ (ವಾಗ್ದಾನ ಮಾಡಿರುವ) ಪವಿತ್ರ ಭೂಮಿಗೆ ನೀವು ಪ್ರವೇಶಿಸಿರಿ. ಹಿಂಜರಿಯಬೇಡಿರಿ. ಅನ್ಯಥಾ ನೀವು ನಷ್ಟಗಾರರಾಗಿ ಮರಳುವಿರಿ.');
INSERT INTO "kannada" VALUES(5,22,'5:22','ಅವರು ಹೇಳಿದರು; ಓ ಮೂಸಾ, ಅಲ್ಲಿ ಮಹಾ ಬಲಾಢ್ಯ ಜನರಿದ್ದಾರೆ. ಅವರು ಅಲ್ಲಿಂದ ಹೊರಟು ಹೋಗುವವರೆಗೂ ನಾವು ಅಲ್ಲಿಗೆ ಪ್ರವೇಶಿಸುವ ಪ್ರಶ್ನೆಯೇ ಇಲ್ಲ. ಅವರು ಅಲ್ಲಿಂದ ಹೊರಟು ಹೋದರೆ ನಾವು ಖಂಡಿತ ಪ್ರವೇಶಿಸಲು ಸಿದ್ಧರಿದ್ದೇವೆ’’.');
INSERT INTO "kannada" VALUES(5,23,'5:23','ಆಗ ಅಲ್ಲಾಹನ ಭಕ್ತದಾಸರಿಗೆ ಸೇರಿದ ಹಾಗೂ ಅಲ್ಲಾಹನ ಅನುಗ್ರಹಗಳು ಸಿದ್ಧಿಸಿದ ಇಬ್ಬರು ಪುರುಷರು ಹೇಳಿದರು; ‘ನೀವು ದೇಶದ ಹೆಬ್ಬಾಗಿಲ ಮೂಲಕ ಪ್ರವೇಶಿಸಿರಿ. ನೀವು ಒಳ ಪ್ರವೇಶ ಮಾಡಿದರೆ, ನೀವೇ ವಿಜಯಿಗಳಾಗು ವಿರಿ. ನೀವು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಾಗಿದ್ದರೆ ಅಲ್ಲಾಹನ ಮೇಲೆ ಭರವಸೆ ಇಡಿರಿ’.');
INSERT INTO "kannada" VALUES(5,24,'5:24','ಆದರೆ, ಅವರು ಹೇಳಿದರು; ಓ ಮೂಸಾ, ಅವರು ಅಲ್ಲಿರುವ ತನಕ ನಾವು ಎಂದಿಗೂ ಅಲ್ಲಿಗೆ ಪ್ರವೇ ಶಿಸಲಾರೆವು. ಆದ್ದರಿಂದ ನೀವೂ ನಿಮ್ಮ ಪ್ರಭೂ ಹೋಗಿ ಅಲ್ಲಿ ಯುದ್ಧ ಮಾಡಿರಿ, ನಾವಿಲ್ಲಿ ಕುಳಿತಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(5,25,'5:25','ಆಗ ಮೂಸಾ, ಹೇಳಿದರು; ‘ಓ ನನ್ನ ಪ್ರಭೂ, ನನ್ನನ್ನು ಮತ್ತು ನನ್ನ ಸಹೋದರನನ್ನು ಹೊರತು ಇನ್ಯಾರನ್ನೂ ನನಗೆ ನಿಯಂತ್ರಿಸಲು ಸಾಧ್ಯವಾಗುತ್ತಿಲ್ಲ. ಆದುದರಿಂದ ನೀನು ನಮ್ಮ ಮತ್ತು ಈ ಧರ್ಮ ಧಿಕ್ಕಾರಿಗಳ ನಡುವೆ ತೀರ್ಪು ನೀಡು’.');
INSERT INTO "kannada" VALUES(5,26,'5:26','ಅಲ್ಲಾಹನು ಹೇಳಿದನು; ಹಾಗಾದರೆ ಆ ಭೂಮಿ ಯನ್ನು ಅವರ ಮೇಲೆ ನಲ್ವತ್ತು ವರುಷಗಳವರೆಗೆ ನಿಷಿದ್ಧಗೊಳಿಸಲಾಗಿದೆ. ಅವರು ಆ ಭೂಮಿಯಲ್ಲಿ ಅಂಡಲೆಯುತ್ತಿರಲಿ. ಧಿಕ್ಕಾರಿಗಳಾದ ಆ ಜನರ ಬಗ್ಗೆ ನೀವು ಕಳವಳಗೊಳ್ಳದಿರಿ’’.');
INSERT INTO "kannada" VALUES(5,27,'5:27','(ಓ ಪೈಗಂಬರರೇ,) ಆದಮರ ಈರ್ವರು ಪುತ್ರರ ವೃತ್ತಾಂತವನ್ನು ಇವರಿಗೆ ಸತ್ಯಸಮೇತ ಓದಿ ಹೇಳಿರಿ. ಅವರಿಬ್ಬರೂ ಬಲಿಯರ್ಪಿಸಿದರು. ಅವರಿ ಬ್ಬರ ಪೈಕಿ ಒಬ್ಬನ ಬಲಿದಾನವು ಸ್ವೀಕೃತವಾಯಿತು ಮತ್ತು ಇನ್ನೊಬ್ಬನಿಂದ ಸ್ವೀಕೃತವಾಗಲಿಲ್ಲ. ಅವ ನು, (ಸ್ವೀಕೃತವಾಗದವನು) ‘ಖಂಡಿತವಾಗಿಯೂ ನಾನು ನಿನ್ನನ್ನು ವಧಿಸುವೆನು’ ಎಂದನು. ಆಗ ಇವನು ಹೇಳಿದನು; ಅಲ್ಲಾಹು ಭಯಭಕ್ತಿಯುಳ್ಳ ವರ ಬಲಿದಾನವನ್ನು ಮಾತ್ರ ಸ್ವೀಕರಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(5,28,'5:28','ನೀನು ನನ್ನನ್ನು ವಧಿಸಲು ಕೈಯೆತ್ತಿದರೂ ನಿನ್ನನ್ನು ವಧಿಸುವುದಕ್ಕಾಗಿ ನಿನ್ನ ವಿರುದ್ಧ ನಾನೆಂದೂ ಕೈ ಎತ್ತಲಾರೆ. ನಾನು ಲೋಕಪಾಲಕನಾದ ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(5,29,'5:29','ನನ್ನ ವಧೆಯ ಪಾಪಕ್ಕೂ ನಿನ್ನ ಬೇರೆ ಪಾಪಕ್ಕೂ ನೀನೇ ಹೊಣೆಗಾರನಾಗಬೇಕೆಂದೂ ನೀನು ನರಕ ವಾಸಿಯಾಗಬೇಕೆಂದೂ ನಾನು ಬಯಸುತ್ತೇನೆ. ಅಕ್ರಮಿಗಳ ಪ್ರತಿಫಲ ಅದುವೇ ಆಗಿದೆ’.');
INSERT INTO "kannada" VALUES(5,30,'5:30','ಕೊನೆಗೆ ತನ್ನ ಸಹೋದರನ ಹತ್ಯೆಗೆ ಆತನ ಮನಸ್ಸು ಒಪ್ಪಿತು. ಮತ್ತು ಅವನು ಇವನನ್ನು ವಧಿಸಿ ನಷ್ಟವಂತರ ಕೂಟಕ್ಕೆ ಸೇರಿ ಹೋದನು .');
INSERT INTO "kannada" VALUES(5,31,'5:31','ಅನಂತರ ಅಲ್ಲಾಹನು ಒಂದು ಕಾಗೆಯನ್ನು ಕಳುಹಿಸಿದನು . ಅದು ಅವನ ಸಹೋದರನ ಮೃತ ದೇಹವನ್ನು ಹೇಗೆ ಮರೆ ಮಾಡಬೇಕೆಂದು ತೋರಿಸಿ ಕೊಡಲಿಕ್ಕಾಗಿ ನೆಲವನ್ನು ಅಗೆಯ ತೊಡ ಗಿತು. ಇದನ್ನು ಕಂಡು ಅವನು, ‘ಓ ನನ್ನ ಕಷ್ಟವೇ! ನನ್ನ ಸಹೋದರನ ಕಳೇಬರವನ್ನು ಮರೆ ಮಾಚಲು ಈ ಕಾಗೆಯಂತೆಯೂ ನಾನು ಆಗದಾ ದೆನಲ್ಲ !’ ಎಂದನು. ಅನಂತರ ಅವನು ತುಂಬ ಖೇದಗೊಂಡವನಾದನು .');
INSERT INTO "kannada" VALUES(5,32,'5:32','ಇನ್ನೊಬ್ಬನ ಕೊಲೆ ಮಾಡದ ಅಥವಾ ಭೂಮಿ ಯಲ್ಲಿ ಕ್ಷೋಭೆಯನ್ನುಂಟು ಮಾಡದ ಒಬ್ಬನನ್ನು ಯಾರಾದರೂ ವಧಿಸಿದರೆ ಅವನು ಇಡೀ ಮಾನವ ಜನಾಂಗವನ್ನೇ ಕೊಂದವನಂತೆ. ಒಬ್ಬನ ಜೀವ ಉಳಿಸಿದರೆ ಇಡೀ ಮಾನವ ಕೋಟಿಗೆ ಜೀವದಾನ ಮಾಡಿದಂತೆ ಎಂಬ ಸಿದ್ಧಾಂತವನ್ನು ನಾವು ಇಸ್‍ರಾಈಲ್ ವಂಶಜರಿಗೆ ಈ ಕಾರಣದಿಂದ ವಿಧಿಸಿದ್ದೆವು. ನಮ್ಮ ದೂತರು ಸುವ್ಯಕ್ತ ರುಜು ವಾತುಗಳ ಸಹಿತ ಅವರ ಬಳಿಗೆ ಬಂದಿದ್ದರೂ ಅವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಭೂಮಿಯಲ್ಲಿ ಅತಿರೇಕವೆ ಸಗುವವರಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(5,33,'5:33','ಅಲ್ಲಾಹು ಹಾಗೂ ಅವನ ದೂತರ ವಿರುದ್ಧ ಸಮರ ಹೂಡುವ ಮತ್ತು ಭೂಮಿಯಲ್ಲಿ ಕೇಡು ಮಾಡಲಿಕ್ಕಾಗಿ ಶ್ರಮಿಸುತ್ತಿರುವವರಿಗೆ ಇರುವ ಪ್ರತಿಫಲವು ಅವರನ್ನು ವಧಿಸುವುದು, ಶಿಲುಬೆಗೇರಿಸುವುದು, ಅವರ ಕೈಯನ್ನೂ ಕಾಲನ್ನೂ ವಿರುದ್ಧ ದಿಕ್ಕುಗಳಿಂದ ಕಡಿದು ಹಾಕುವುದು, ಅವರನ್ನು ಗಡೀಪಾರು ಮಾಡುವುದು ಆಗಿರುತ್ತದೆ . ಇದು ಇಹಲೋಕದಲ್ಲಿ ಅವರಿಗಿರುವ ಅಪಮಾನ. ಪರಲೋಕದಲ್ಲಿ ಅವರಿಗೆ ಇದಕ್ಕಿಂತಲೂ ಕಠಿಣ ಶಿಕ್ಷೆ ಕಾದಿದೆ.');
INSERT INTO "kannada" VALUES(5,34,'5:34','ಆದರೆ ಅವರ ವಿರುದ್ಧ ಶಿಕ್ಷಾ ಕ್ರಮ ಕೈಗೊಳ್ಳಲು ನಿಮಗೆ ಸಾಧ್ಯವಾಗುವ ಮುಂಚೆಯೇ ಪಶ್ಚಾ ತ್ತಾಪಟ್ಟು ಮರಳಿದವರು ಇದರಿಂದ ಹೊರ ತಾಗಿದ್ದಾರೆ . ಅಲ್ಲಾಹು ಖಂಡಿತ ಕ್ಷಮಾಶೀಲನೂ, ದಯಾ ನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆಂದು ನೀವು ತಿಳಿಯಿರಿ.');
INSERT INTO "kannada" VALUES(5,35,'5:35','ಓ ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳೇ, ಅಲ್ಲಾಹನನ್ನು ಭಯ ಪಡಿರಿ. ಅವನ ಸಾಮೀಪ್ಯದ ಮಾರ್ಗವನ್ನು ಹುಡುಕಿರಿ . ಮತ್ತು ಅವನ ಮಾರ್ಗದಲ್ಲಿ ಹೋರಾಡಿರಿ. ನಿಮಗೆ ವಿಜಯ ಪ್ರಾಪ್ತಿಯಾಗಬಹುದು.');
INSERT INTO "kannada" VALUES(5,36,'5:36','ಪುನರುತ್ಥಾನ ದಿನದ ಶಿಕ್ಷೆಯಿಂದ ಪಾರಾಗಲು ಪ್ರಾಯಶ್ಚಿತವಾಗಿ ಸತ್ಯನಿಷೇಧಿಗಳ ಕೈವಶದಲ್ಲಿ ಭೂಮಿಯ ಸಕಲ ಸಂಪತ್ತೂ ಅದರೊಂದಿಗೆ ಮತ್ತೂ ಅಷ್ಟೇ ಇದ್ದರೂ ಅವರಿಂದ ಅದು ಸ್ವೀಕೃತ ವಾಗಲಾರದು. ಮತ್ತು ಅವರಿಗೆ ವೇದನಾ ಯುಕ್ತ ಶಿಕ್ಷೆಯಿದೆ.');
INSERT INTO "kannada" VALUES(5,37,'5:37','ಅವರು ನರಕಾಗ್ನಿಯಿಂದ ಹೊರಹೋಗಲು ಇಚ್ಛಿಸುವರು. ಆದರೆ, ಅವರಿಗೆ ಹೊರಬರಲು ಸಾಧ್ಯವಾಗದು. ಅವರಿಗೆ ಶಾಶ್ವತವಾದ ಶಿಕ್ಷೆಯಿದೆ.');
INSERT INTO "kannada" VALUES(5,38,'5:38','ಕಳ್ಳತನ ಮಾಡಿದ ಪುರುಷರು ಮತ್ತು ಕಳ್ಳತನ ಮಾಡಿದ ಸ್ತ್ರೀಯರ ಕೈಗಳನ್ನು ಕಡಿಯಿರಿ - ಇದು ಅವರ ಗಳಿಕೆಯ ಫಲ ಮತ್ತು ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ಒಂದು ದಂಡನೆ . ಅಲ್ಲಾಹು ಮಹಾ ಪ್ರತಾಪಿಯೂ ಯುಕ್ತಿವಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(5,39,'5:39','ಆದರೆ ಯಾರಾದರೂ ಅಕ್ರಮವೆಸಗಿದ ಬಳಿಕ ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು ಸುಧಾರಿಸಿಕೊಂಡರೆ, ಅಲ್ಲಾ ಹನು ನಿಶ್ಚಯ ಅವನ ಪಶ್ಚಾತ್ತಾಪವನ್ನು ಸ್ವೀಕರಿ ಸುವನು. ಅಲ್ಲಾಹು ಅತ್ಯಂತ ಕ್ಷಮಾಶೀಲನೂ, ಮಹಾ ದಯಾವಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(5,40,'5:40','ಅಲ್ಲಾಹನಿಗೇ ಭೂಮಿ-ಆಕಾಶಗಳ ಆಧಿಪತ್ಯ ವೆಂಬುದನ್ನು ನೀವು ತಿಳಿದಿಲ್ಲವೇ? ಅವನು ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ಶಿಕ್ಷಿಸುತ್ತಾನೆ ಮತ್ತು ತಾನುದ್ದೇಶಿಸಿದವರಿಗೆ ಕ್ಷಮಿಸುತ್ತಾನೆ. ಅಲ್ಲಾಹನು ಸರ್ವ ವಿಷಯಕ್ಕೂ ಸರ್ವ ಸಮರ್ಥನು.');
INSERT INTO "kannada" VALUES(5,41,'5:41','ಓ ರಸೂಲರೇ, ನಾವು ವಿಶ್ವಾಸವಿಟ್ಟೆವು ಎಂದು ಬಾಯಿ ಮಾತಿನಲ್ಲಿ ಹೇಳುವ ಹಾಗೂ ಹೃದಯದಲ್ಲಿ ವಿಶ್ವಾಸವಿಲ್ಲದ ಹಾಗೂ ಯಹೂದಿಗಳ ಪೈಕಿ ಒಂದು ವಿಭಾಗದವರು ಸತ್ಯನಿಷೇಧಕ್ಕೆ ಧಾವಿಸುತ್ತಿರುವ ಅವಸ್ಥೆ ನಿಮ್ಮನ್ನು ದುಃಖ ಕ್ಕೀಡುಮಾಡದಿರಲಿ . ಅವರು ಸುಳ್ಳು ವಾರ್ತೆಗಳಿಗೆ ಅತಿಯಾಗಿ ಕಿವಿಗೊಡುತ್ತಾರೆ. ನಿಮ್ಮ ಬಳಿಗೆ ಬಾರದಿರುವ ಇನ್ನೊಂದು ವಿಭಾಗದವರ ಪರವಾಗಿ ನಿಮ್ಮಿಂದಲೂ ಅತಿಯಾಗಿ ಕಿವಿಗೊಡುತ್ತಾರೆ. ಅವರು, ದೇವಗ್ರಂಥದ ವಚನಗಳನ್ನು ಸ್ಥಾನಪಲ್ಲಟಗೊಳಿಸುತ್ತಾರೆ. ಮತ್ತು ಜನರೊಡನೆ ‘ನಿಮಗೆ ಇಂತದೇ ಆಜ್ಞೆ ದೊರೆತರೆ ಒಪ್ಪಿಕೊಳ್ಳಿರಿ, ಇಲ್ಲವಾದರೆ ಅದರ ಬಗ್ಗೆ ಜಾಗ್ರತೆ ವಹಿಸಿರಿ’ ಎಂದು ಹೇಳುತ್ತಾರೆ. ಯಾರನ್ನು ಅಲ್ಲಾಹನು ದಾರಿ ತಪ್ಪಿಸಲಿಚ್ಚಿಸು ವನೋ, ಅದನ್ನು ಅವನಿಂದ ತಡೆಯುವ ಶಕ್ತಿ ಯನ್ನು ನೀವು ಅಲ್ಲಾಹ ನಿಂದ ಪಡೆಯಲಾರಿರಿ . ಅಲ್ಲಾಹನು ಇಂತಹವರ ಹೃದಯಗಳನ್ನು ಶುದ್ಧಗೊಳಿಸಲು ಇಚ್ಚಿಸಿಲ್ಲ. ಇವರಿಗೆ ಇಹಲೋಕ ದಲ್ಲಿ ಅವಮಾನ ಮತ್ತು ಪರಲೋಕದಲ್ಲಿ ಘೋರ ಶಿಕ್ಷೆ ಕಾದಿದೆ.');
INSERT INTO "kannada" VALUES(5,42,'5:42','ಇವರು ಸುಳ್ಳು ವಾರ್ತೆಗಳನ್ನು ಧಾರಾಳ ಆಲಿಸು ವವರು ಮತ್ತು ನಿಷಿದ್ಧ ಆಹಾರವನ್ನು ಯಥೇಚ್ಛ ತಿನ್ನುವವರಾಗಿದ್ದಾರೆ. ಆದುದರಿಂದ (ಮೊಕದ್ದಮೆ ಸಹಿತ) ಇವರು ನಿಮ್ಮ ಮುಂದೆ ಬಂದರೆ, ಅವರ ಮಧ್ಯೆ ವಿಧಿ ನೀಡಿರಿ ಇಲ್ಲವೇ ಅವರನ್ನು ಅವಗಣಿಸಿರಿ. (ಈ ಎರಡು ಅಧಿಕಾರವನ್ನು ನಿಮಗೆ ಕೊಡ ಲಾಗುತ್ತದೆ.) ನೀವು ಅವಗಣಿಸಿದರೆ ಇವರು ನಿಮಗೆ ಯಾವ ಕೇಡನ್ನೂ ತರಲಾರರು ಮತ್ತು ನೀವು ತೀರ್ಪು ನೀಡುವುದಿದ್ದರೆ, ನ್ಯಾಯ ನಿಷ್ಠೆಯಿಂದಲೇ ತೀರ್ಪು ನೀಡಿರಿ. ಅಲ್ಲಾಹು ಖಂಡಿತ ನ್ಯಾಯ ನಿಷ್ಠರನ್ನು ಮೆಚ್ಚುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(5,43,'5:43','ಅಲ್ಲಾಹನ ತೀರ್ಪು ಇರುವ `ತೌರಾತ್'' ಅವರ ಬಳಿಯಲ್ಲಿದ್ದೂ ಕೂಡಾ ನಂತರ ಅವರು ಆ ತೀರ್ಪನ್ನು ಕಡೆಗಣಿಸುತ್ತಿರುವಾಗ (ಅದೇ ತೀರ್ಪನ್ನು ಕೊಡುವ) ನಿಮ್ಮನ್ನು ತೀರ್ಪುಗಾರನನ್ನಾಗಿ ಮಾಡುವುದು ಅವರಿಗೆ ಹೇಗೆ ಸಾಧ್ಯವಾದೀತು? ನಿಜದಲ್ಲಿ ಅವರು ವಿಶ್ವಾಸಿಗಳೇ ಅಲ್ಲ .');
INSERT INTO "kannada" VALUES(5,44,'5:44','ಖಂಡಿತ ನಾವು ತೌರಾತನ್ನು ಅವತೀರ್ಣ ಗೊಳಿ ಸಿದ್ದೆವು. ಅದರಲ್ಲಿ ಮಾರ್ಗದರ್ಶನ ಮತ್ತು ಪ್ರಕಾಶವಿತ್ತು . ದೇವ ವಿಧೇಯರಾದ ಎಲ್ಲ ಪ್ರವಾದಿಗಳೂ ಪುಣ್ಯ ಪುರುಷರೂ, ವಿದ್ವಾಂಸರೂ ಅದರ ಪ್ರಕಾರವೇ ಯಹೂದ್ಯರ ವ್ಯಾಜ್ಯಗಳನ್ನು ಇತ್ಯರ್ಥಗೊಳಿಸುತ್ತಿದ್ದರು. ಏಕೆಂದರೆ ಅವರಿಗೆ ಅಲ್ಲಾಹನ ವೇದಗ್ರಂಥದ ರಕ್ಷಣೆಯನ್ನು ವಹಿಸಿಕೊಡಲಾಗಿತ್ತು. ಅವರು ಅದಕ್ಕೆ ಸಾಕ್ಷಿಗಳಾಗಿದ್ದರು . ಆದುದರಿಂದ (ಯಹೂದಿ ಪುರೋ ಹಿತರೇ) ನೀವು ಜನರನ್ನು ಹೆದರಬೇಡಿರಿ, ನನ್ನನ್ನು ಭಯಪಡಿರಿ ಮತ್ತು ನನ್ನ ವಚನಗಳ ಬದಲಿಗೆ ಜಜುಬಿ ಬೆಲೆಯನ್ನು ಪಡೆಯಬೇಡಿರಿ. ಅಲ್ಲಾಹನು ಅವತೀರ್ಣಗೊಳಿಸಿದ ಕಾನೂನಿನ ಪ್ರಕಾರ ತೀರ್ಮಾನ ಮಾಡದವರು ಯಾರೋ ಅವರೇ ಸತ್ಯನಿಷೇಧಿಗಳು.');
INSERT INTO "kannada" VALUES(5,45,'5:45','ಜೀವಕ್ಕೆ ಜೀವ, ಕಣ್ಣಿಗೆ ಕಣ್ಣು, ಮೂಗಿಗೆ ಮೂಗು, ಕಿವಿಗೆ ಕಿವಿ, ಹಲ್ಲಿಗೆ ಹಲ್ಲು ಮತ್ತು ಗಾಯಗಳಿಗೆ ಸರಿ ಸಮಾನ ಪ್ರತೀಕಾರವೆಂದು (ತೌರಾತ್‍ನಲ್ಲಿ) ಅವರ ಮೇಲೆ ನಾವು ವಿಧಿಸಿದ್ದೆವು . ಆದರೆ ಯಾವನಾದರೂ ಅದಕ್ಕೆ ಕ್ಷಮೆ ನೀಡಿದರೆ ಅದು ಅವನ ಪಾಲಿಗೆ ಪ್ರಾಯಶ್ಚಿತ್ತವಾಗುವುದು. ಅಲ್ಲಾಹ ನಿಂದ ಅವತೀರ್ಣಗೊಳಿಸಿದ (ಕಾನೂನಿನ) ಪ್ರಕಾರ ತೀರ್ಮಾನ ಮಾಡದವರು ಯಾರೋ ಅವರೇ ಅಕ್ರಮಿಗಳು.');
INSERT INTO "kannada" VALUES(5,46,'5:46','ಆ ಪ್ರವಾದಿಗಳ ಹಿಂದೆಯೇ ಮರ್ಯಮರ ಪುತ್ರ ಈಸಾರನ್ನು ನಾವು ಕಳುಹಿಸಿದೆವು. ಅವರು ತಮ್ಮ ಮುಂದೆ ಇದ್ದ ತೌರಾತನ್ನು ದೃಢಪಡಿಸುವವರಾಗಿದ್ದರು . ನಾವು ಅವರಿಗೆ ಇಂಜೀಲನ್ನು ನೀಡಿದೆವು. ಅದರಲ್ಲಿ ಸನ್ಮಾರ್ಗ ದರ್ಶನ ಮತ್ತು ಪ್ರಕಾಶವಿತ್ತು. ಅದು ಅದರ ಹಿಂದೆ ಇದ್ದ ತೌರಾತನ್ನು ದೃಢಪಡಿಸುವ ಸ್ಥಿತಿಯಲ್ಲೂ ದೇವಭಯವುಳ್ಳವರಿಗೆ ಮಾರ್ಗದರ್ಶನ ಮತ್ತು ಉಪದೇಶವಾಗಿಯೂ ಅವತೀರ್ಣವಾಗಿತ್ತು.');
INSERT INTO "kannada" VALUES(5,47,'5:47','ಇಂಜೀಲಿನವರು ಅಲ್ಲಾಹನು ಅದರಲ್ಲಿ ಅವತೀರ್ಣಗೊಳಿಸಿದ ಕಾನೂನಿನ ಪ್ರಕಾರ ತೀರ್ಮಾನ ಕೈಕೊಳ್ಳಲಿ (ಎಂದು ಅವರಿಗೆ ನಮ್ಮ ಅಪ್ಪಣೆ ಇತ್ತು.) ಅಲ್ಲಾಹನು ಅವತೀರ್ಣಗೊಳಿಸಿದ ಕಾನೂ ನಿನ ಪ್ರಕಾರ ತೀರ್ಮಾನ ಕೈಕೊಳ್ಳದವರು ಯಾರೋ ಅವರೇ ಕರ್ಮಭ್ರಷ್ಟರು .');
INSERT INTO "kannada" VALUES(5,48,'5:48','(ಓ ಪೈಗಂಬರರೇ,) ನಾವು ಈ ಗ್ರಂಥವನ್ನು ಸತ್ಯ ಸಮೇತ ನಿಮಗೆ ಇಳಿಸಿದೆವು. ಅದು ಅವರ ಹಿಂದಿನ ಗ್ರಂಥಗಳನ್ನು ದೃಢಪಡಿಸುವಂತಹದೂ ಅದರ ಸಾಕ್ಷಿಯೂ ಆಗಿರುತ್ತದೆ. ಆದುದರಿಂದ ನೀವು ಅಲ್ಲಾಹನು ಅವತೀರ್ಣಗೊಳಿಸಿದ (ಕಾನೂನಿನ) ಪ್ರಕಾರ ಅವರ ನಡುವೆ ತೀರ್ಮಾ ನಿಸಿರಿ. ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದಿರುವ ಸತ್ಯವನ್ನು ಬಿಟ್ಟು, ಅವರ ತನ್ನಿಚ್ಛೆಗಳಿಗೆ ಅನುಸರಿಸಬೇಡಿರಿ. (ಓ ಸಮುದಾಯವೇ) ನಾವು ನಿಮ್ಮ ಪೈಕಿ ಪ್ರತಿಯೊಬ್ಬನಿಗೆ ಒಂದು ಧರ್ಮಶಾಸ್ತ್ರ ಮತ್ತು ಕರ್ಮಮಾರ್ಗವನ್ನು ನಿಶ್ಚಯಿಸಿದೆವು. ಅಲ್ಲಾಹನು ಇಚ್ಛಿಸುತ್ತಿದ್ದರೆ ನಿಮ್ಮೆಲ್ಲರನ್ನು ಒಂದೇ ಒಂದು ಸಮುದಾಯವನ್ನಾಗಿ ಮಾಡುತ್ತಿದ್ದನು. ಆದರೆ ಅವನು ನಿಮ್ಮನ್ನು ಪರೀಕ್ಷಿಸಲಿಕ್ಕಾಗಿ ವಿಭಿನ್ನ ನಡಾವಳಿ ಕ್ರಮಗಳನ್ನು ಕೊಟ್ಟನು. ಆದುದರಿಂದ ಸತ್ಕರ್ಮಗಳಲ್ಲಿ ಪರಸ್ಪರ ಸ್ಪರ್ಧಿಸಿ ಮುನ್ನುಗ್ಗಿರಿ. ನಿಮ್ಮೆಲ್ಲರ ಮರಳುವಿಕೆ ಅಲ್ಲಾಹನ ಕಡೆಗೇ ಆಗಿರುತ್ತದೆ. ಆಗ ನೀವು ಭಿನ್ನಾಭಿಪ್ರಾಯ ವಿರಿಸಿ ಕೊಂಡಿದ್ದ ವಿಷಯಗಳ ನೈಜತೆಯನ್ನು ಅವನು ನಿಮಗೆ ತೋರಿಸಿಕೊಡುವನು .');
INSERT INTO "kannada" VALUES(5,49,'5:49','ಆದುದರಿಂದ (ಓ ಪೈಗಂಬರರೇ,) ಅಲ್ಲಾಹನು ಅವತೀರ್ಣಗೊಳಿಸಿದ (ಕಾನೂನಿನ) ಪ್ರಕಾರ ಇವರ ನಡುವೆ ವಿಧಿ ನೀಡಿರಿ. ಮತ್ತು ಇವರ ತನ್ನಿಚ್ಚೆಗಳನ್ನು ಅನುಸರಿಸಬೇಡಿ. ಅಲ್ಲಾಹನು ನಿಮ್ಮ ಮೇಲೆ ಅವತೀರ್ಣಗೊಳಿಸಿದ ಒಂದಿಷ್ಟು ಆದೇಶದಿಂದಾದರೂ ಅವರು ನಿಮ್ಮನ್ನು ದಾರಿತಪ್ಪಿಸದಂತೆ ಬಹಳ ಜಾಗರೂಕರಾಗಿರಿ. ಇವರು ಇದನ್ನು (ನಿಮ್ಮ ತೀರ್ಪನ್ನು) ಸ್ವೀಕರಿಸದೆ ಹಿಂಜರಿದರೆ ಅಲ್ಲಾಹನು ಇವರ ಕೆಲವು ಪಾಪಗಳ ನಿಮಿತ್ತ ಇವರಿಗೆ ಅನಾಹುತಗಳನ್ನು ತಂದೊಡ್ಡಲು ಬಯಸುತ್ತಾನೆಂಬುದನ್ನು ತಿಳಿದಿರಿ. ಖಂಡಿತ ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಧಿಕ್ಕಾರಿಗಳಾಗಿರುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(5,50,'5:50','ಇವರು ಜಾಹಿಲಿಯ್ಯಾದ (ಅಜ್ಞಾನದ) ತೀರ್ಮಾ ನವನ್ನು ಬಯಸುತ್ತಾರೆಯೇ? ದೃಢ ವಿಶ್ವಾಸಿಗಳಾದ ಜನರಿಗೆ ಅಲ್ಲಾಹನಿಗಿಂತ ಶ್ರೇಷ್ಠ ತೀರ್ಮಾನ ಮಾಡುವವನು ಯಾರಿದ್ದಾರೆ? ');
INSERT INTO "kannada" VALUES(5,51,'5:51','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ಯಹೂದಿಯ ರನ್ನೂ ಕ್ರೈಸ್ತರನ್ನೂ ನಿಮ್ಮ ಆಪ್ತಮಿತ್ರರನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳ ಬೇಡಿರಿ. ಇವರು ಪರಸ್ಪರ ಆಪ್ತರು. ನಿಮ್ಮಲ್ಲಾರಾದರೂ ಅವರನ್ನು ಆಪ್ತಮಿತ್ರರನ್ನಾಗಿ ಮಾಡಿಕೊಂಡರೆ ಖಂಡಿತ ಅವನೂ ಅವರೊಂದಿಗೆ ಸೇರಿದವನಾಗಿರುತ್ತಾನೆ. ಅಲ್ಲಾಹು ಅಕ್ರಮಿ ಜನಾಂಗಕ್ಕೆ ಖಂಡಿತ ಮಾರ್ಗದರ್ಶನ ಮಾಡುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(5,52,'5:52','ಹೃದಯಗಳಲ್ಲಿ (ಕಾಪಟ್ಯದ) ರೋಗವುಳ್ಳವರು ಅವರಲ್ಲೇ ಮೈತ್ರಿ ಬೆಳೆಸಲು ತ್ವರೆ ಮಾಡುವುದನ್ನು ನೀವು ಕಾಣುತ್ತೀರಿ . ಅವರು ‘ನಮಗೇನಾದರೂ ವಿಪತ್ತು ಸಂಭವಿಸಿತೇನೋ ಎಂದು ನಮಗೆ ಭಯವಾಗುತ್ತದೆ’ ಎನ್ನುತ್ತಾರೆ. ಅಲ್ಲಾಹು ನಿಮಗೆ ಪೂರ್ಣ ವಿಜಯ ದಯಪಾಲಿಸಬಹುದು ಅಥವಾ ತನ್ನ ಕಡೆಯಿಂದ ಯಾವುದೇ ಕ್ರಮ ಕೈಗೊಳ್ಳಬಹುದು. ಆಗ ಇವರು ತಮ್ಮ ಹೃದಯದೊಳಗೆ ಬಚ್ಚಿಟ್ಟು ಕೊಂಡಿರುವ ಕಾಪಟ್ಯದ ಬಗ್ಗೆ ಖೇದಪಡಬಹುದು.');
INSERT INTO "kannada" VALUES(5,53,'5:53','ಆಗ, ಅಲ್ಲಾಹನ ಮೇಲೆ ದೃಢವಾದ ಪ್ರಮಾಣ ಮಾಡುತ್ತ ‘ನಾವು ನಿಮ್ಮೊಂದಿಗೆ ಇದ್ದೇವೆಂದು ಹೇಳಿದವರು ಇವರೇನು?’ ಎಂದು ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳು ಕೇಳುವರು. ಅವರ ಕರ್ಮಗಳು ನಿಷ್ಫಲಗೊಂಡವು ಮತ್ತು ಅವರು ಪರಾಜಿತರೂ ಆಗಿಬಿಟ್ಟರು.');
INSERT INTO "kannada" VALUES(5,54,'5:54','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನಿಮ್ಮಲ್ಲಾರಾದರೂ ಧರ್ಮದಿಂದ ವಿಮುಖನಾದರೆ ಅಲ್ಲಾಹನ ಪ್ರೀತಿಗೆ ಪಾತ್ರರಾದ ಹಾಗೂ ಅಲ್ಲಾಹನನ್ನು ಪ್ರೀತಿಸುವ ಇನ್ನೊಂದು ಜನವಿಭಾಗವನ್ನು ಅಲ್ಲಾಹು ಸೃಷ್ಟಿಸುವನು. ಅವರು ಸತ್ಯವಿಶ್ವಾಸಿ ಗಳೊಂದಿಗೆ ವಿನಮ್ರರೂ ಸತ್ಯನಿಷೇಧಿಗಳೊಂದಿಗೆ ಕಠೋರರೂ ಆಗಿರುವರು . ಅವರು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಹೋರಾಟ ನಡೆಸುವರು. ಯಾವನೇ ವಿಮರ್ಶಕನ ವಿಮರ್ಶೆಗೆ ಅಂಜಲಾರರು. ಇದು ಅಲ್ಲಾಹನ ಮಹಾ ಅನುಗ್ರಹ. ಇದನ್ನು ಅವನು ತನಗಿಷ್ಟ ಬಂದವರಿಗೆ ದಯಪಾಲಿಸುತ್ತಾನೆ. ಅಲ್ಲಾಹನು ಅತಿ ವಿಶಾಲನೂ, (ಮಹಾ ಉದಾರಿಯೂ) ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(5,55,'5:55','ನಿಮ್ಮ ಸಹಾಯಿ ವಾಸ್ತವದಲ್ಲಿ ಅಲ್ಲಾಹು, ಆತನ ದೂತರು ಮತ್ತು ವಿನಮ್ರರಾಗಿ ನಮಾಝನ್ನು ಸಂಸ್ಥಾಪಿಸುವ ಹಾಗೂ ಝಕಾತ್ ಕೊಡುವ ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಮಾತ್ರ');
INSERT INTO "kannada" VALUES(5,56,'5:56','ಅಲ್ಲಾಹನನ್ನೂ ಅವನ ದೂತರನ್ನೂ ಮತ್ತು ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನು ತನ್ನ ಸಹಾಯಕರನ್ನಾಗಿ ಮಾಡಿಕೊಂಡವನು ಅಲ್ಲಾಹನ ಪಕ್ಷವೇ ಜಯಶೀಲವೆಂದು ಅರಿತಿರಬೇಕು .');
INSERT INTO "kannada" VALUES(5,57,'5:57','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನಿಮಗಿಂತ ಮೊದಲು ವೇದಗ್ರಂಥ ನೀಡಲ್ಪಟ್ಟವರ ಪೈಕಿ, ನಿಮ್ಮ ಧರ್ಮ ವನ್ನು ಪರಿಹಾಸ್ಯ ಹಾಗೂ ವಿನೋದಗಳ ಸಾಧನವನ್ನಾಗಿ ಮಾಡಿಕೊಂಡವರನ್ನೂ ಇತರ ಬಹುದೇ ವಾರಾಧಕರನ್ನೂ ನಿಮ್ಮ ಆಪ್ತ ಮಿತ್ರರನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳಬೇಡಿರಿ. ನೀವು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಾಗಿದ್ದರೆ ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ.');
INSERT INTO "kannada" VALUES(5,58,'5:58','ನೀವು ನಮಾಝಿಗಾಗಿ ಕರೆ (ಅದ್ಸಾನ್) ಕೊಟ್ಟರೆ ಅವರು ವಿನೋದವನ್ನಾಗಿ ಮಾಡುತ್ತಾರೆ. ಅವರು ಬುದ್ಧಿ ವಿವೇಕವನ್ನು ಬಳಸದ ಜನಾಂಗವಾದುದರಿಂದಲೇ ಹಾಗೆ ಮಾಡುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(5,59,'5:59','ಹೇಳಿರಿ, `ಓ ಗ್ರಂಥದವರೇ, ನಾವು ಅಲ್ಲಾಹನ ಮೇಲೆ ಮತ್ತು ನಮ್ಮ ಕಡೆಗೆ ಅವತೀರ್ಣಗೊಂಡ ಹಾಗೂ ನಮಗಿಂತ ಮೊದಲು ಅವತೀರ್ಣವಾಗಿದ್ದ ಗ್ರಂಥಗಳ ಮೇಲೆ ವಿಶ್ವಾಸವಿಟ್ಟಿರುತ್ತೇವೆ ಎನ್ನುವುದು ಹಾಗೂ ನಿಮ್ಮ ಪೈಕಿ ಹೆಚ್ಚಿನವರು ಧರ್ಮ ಧಿಕ್ಕಾರಿಗಳೂ ಆಗಿದ್ದಾರೆ ಎನ್ನುವುದೇ ಹೊರತು ನಮ್ಮ ಮೇಲೆ ಹಗೆ ಸಾಧಿಸಲು ನಿಮಗೆ ಬೇರೇನು ಕಾರಣವಿದೆ?');
INSERT INTO "kannada" VALUES(5,60,'5:60','ಹೇಳಿರಿ-ಅಲ್ಲಾಹನ ಬಳಿ ಇದಕ್ಕೂ ಹೆಚ್ಚು ದುಷ್ಟ ಕರವಾದ ಪ್ರತಿಫಲವುಳ್ಳವರ ಬಗ್ಗೆ ನಾನು ನಿಮಗೆ ತೋರಿಸಿ ಕೊಡಲೇ ? ಅಲ್ಲಾಹನ ಶಾಪ ಹಾಗೂ ಕೋಪಕ್ಕೊಳಗಾದವರು, ಕಪಿಗಳಾಗಿಯೂ ಹಂದಿಗಳಾಗಿಯೂ ರೂಪಾಂತರಿಸಲ್ಪಟ್ಟವರು ಮತ್ತು ದುರ್ಮೂರ್ತಿಗಳನ್ನು ಆರಾಧಿಸಿದವರು. ಅವರ ನೆಲೆಯು ಕೆಟ್ಟದಾಗಿರುತ್ತದೆ ಮತ್ತು ಅವರು ಸರಿದಾರಿಯಿಂದ ತೀವ್ರ ವ್ಯತಿ ಚಲಿಸಿದವರಾಗಿರುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(5,61,'5:61','ಅವರು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದರೆ `ನಾವು ವಿಶ್ವಾಸ ವಿಟ್ಟಿದ್ದೇವೆ’ ಎನ್ನುತ್ತಾರೆ. ವಾಸ್ತವದಲ್ಲಿ ಅವರು ಸತ್ಯನಿಷೇಧದೊಂದಿಗೇ ಬಂದಿದ್ದರು ಮತ್ತು ಸತ್ಯನಿಷೇಧದೊಂದಿಗೇ ಮರಳಿ ಹೋದರು. ಅವರು ತಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ಬಚ್ಚಿಟ್ಟು ಕೊಂಡಿರುವುದನ್ನೂ ಅಲ್ಲಾಹು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(5,62,'5:62','ಅವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಪಾಪ ಹಾಗೂ ಅತಿಕ್ರಮ ಕಾರ್ಯಗಳಲ್ಲಿ ಮತ್ತು ನಿಷಿದ್ಧ ವಸ್ತುಗಳ ಭೋಜನದಲ್ಲಿ ಸ್ಪರ್ಧಿಸುತ್ತಿರುವುದನ್ನು ನೀವು ಕಾಣುತ್ತೀರಿ . ಅವರು ಮಾಡುತ್ತಿರುವ ಕೃತ್ಯಗಳು ಅತ್ಯಂತ ನೀಚ.');
INSERT INTO "kannada" VALUES(5,63,'5:63','ಅವರು ಪಾಪದ ಮಾತುಗಳನ್ನಾಡುವುದರಿಂದಲೂ ಮತ್ತು ನಿಷಿದ್ಧವಾದುದನ್ನು ತಿನ್ನುವುದ ರಿಂದಲೂ ಅವರ ಆಧ್ಯಾತ್ಮ ಗುರುಗಳೂ, ವಿದ್ವಾಂಸರೂ ತಡೆಯುವುದಿಲ್ಲವೇಕೆ? ಅವರು ಮಾಡುತ್ತಿರುವ ಕೃತ್ಯವು ನಿಜಕ್ಕೂ ಅತ್ಯಂತ ನಿಕೃಷ್ಟ .');
INSERT INTO "kannada" VALUES(5,64,'5:64','‘ಅಲ್ಲಾಹನ ಕೈಗಳಿಗೆ ಬೇಡಿ ತೊಡಿಸಲ್ಪಟ್ಟಿವೆ’ ಎಂದು ಯಹೂದಿಯರು ಹೇಳುತ್ತಾರೆ. ಬೇಡಿ ತೊಡಿಸಲ್ಪಟ್ಟುದು ಅವರ ಕೈಗಳು. ಅವರ ಹೇಳಿಕೆಯ ಕಾರಣದಿಂದಾಗಿ ಅವರು ಶಪಿತರಾದರು. ಅಲ್ಲಾಹನ ಕೊಡುಗೈ (ದಾನವು) ಅತಿ ವಿಶಾಲವಿದ್ದು ಅವನು ತನ್ನಿಚ್ಛೆ ಪ್ರಕಾರ ವ್ಯಯಿಸುತ್ತಾನೆ . ನಿಮ್ಮ ಪಾಲಕ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ನಿಮ್ಮ ಮೇಲೆ ಅವತೀರ್ಣಗೊಂಡಿರುವ ವಾಣಿಯು ಅವರ ಪೈಕಿ ಹೆಚ್ಚಿನವರ ಅಕ್ರಮ ಹಾಗೂ ಸತ್ಯನಿಷೇಧವನ್ನು ಹೆಚ್ಚಿಸುತ್ತಿವೆ. ನಾವು ಅವರ ನಡುವೆ ಲೋಕಾಂತ್ಯದವರೆಗೆ ವೈರ ಹಾಗೂ ವಿದ್ವೇಷಗಳನ್ನಿರಿಸಿದ್ದೇವೆ. ಅವರು ಯುದ್ಧದ ಕಿಡಿಯನ್ನು ಹೊತ್ತಿಸಿದಾಗಲೆಲ್ಲ ಅಲ್ಲಾಹು ಅದನ್ನು ನಂದಿಸಿಬಿಡುತ್ತಾನೆ. ಅವರು ಭೂಮಿಯಲ್ಲಿ ಕ್ಷೋಭೆ ಹಬ್ಬಿಸಲು ತೀವ್ರ ಯತ್ನಿಸುತ್ತಾರೆ. ಅಲ್ಲಾಹನು ಗೊಂದಲಕಾರಿಗಳನ್ನು ಮೆಚ್ಚುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(5,65,'5:65','ಗ್ರಂಥದವರು ವಿಶ್ವಾಸವಿಟ್ಟಿದ್ದರೆ ಹಾಗೂ ಸೂಕ್ಷ್ಮತೆ ಪಾಲಿಸುತ್ತಿದ್ದರೆ, ನಾವು ಅವರ ಕೆಡುಕುಗಳನ್ನು ಅಳಿಸಿ ಹಾಕುತ್ತಿದ್ದೆವು. ಮತ್ತು ಅವರಿಗೆ ಅನುಗ್ರಹ ಪೂರ್ಣವಾದ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಿಗೆ ಪ್ರವೇಶ ಕೊಡುತ್ತಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(5,66,'5:66','ಅವರು ತೌರಾತ್, ಇಂಜೀಲ್ ಮತ್ತು ಅವರ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಅವರಿಗೆ ಇಳಿಸಿಕೊಡ ಲಾದ ಇತರ ಆಜ್ಞೆಗಳನ್ನು ಅವರು ಯಥಾಸ್ಥಿತಿ ಅನುಸರಿಸಿದ್ದರೆ ಅವರಿಗಾಗಿ ಆಹಾರವು ತಮ್ಮ ಮೇಲ್ಭಾಗದಿಂದಲೂ ಕಾಲಡಿಗಳಿಂದಲೂ ಉಕ್ಕಿ ಬರುತ್ತಿತ್ತು. ಅವರಲ್ಲಿ ಕೆಲವರು ನೀತಿವಂತರೂ ಇದ್ದಾರೆ. ಆದರೆ ಅವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಮಹಾ ದುಷ್ಕರ್ಮಿಗಳಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(5,67,'5:67','ಓ ದೂತರೇ, ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ನಿಮಗೆ ಅವತೀರ್ಣಗೊಳಿಸಲ್ಪಟ್ಟುದನ್ನು ಜನರಿಗೆ ತಲುಪಿಸಿರಿ. ತಲುಪಿಸದಿದ್ದರೆ ನೀವು ಅವನ ದೌತ್ಯವನ್ನು ನೆರವೇರಿಸಿದವರಾಗಲಾರಿರಿ. ಅಲ್ಲಾಹು ನಿಮ್ಮನ್ನು ಜನರ ಕೇಡಿನಿಂದ ರಕ್ಷಿಸುತ್ತಾನೆ. ಅವನು ಸತ್ಯನಿಷೇಧಿಗಳಾದ ಜನಾಂಗ ವನ್ನು ಸತ್ಯಪಥಕ್ಕೆ ಸೇರಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(5,68,'5:68','ಓ ಗ್ರಂಥದವರೇ, ನೀವು ತೌರಾತನ್ನೂ ಇಂಜೀ ಲನ್ನೂ ನಿಮಗೆ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಅವತೀರ್ಣವಾದ ಇತರ ವೇದಗಳನ್ನೂ ಪಾಲಿಸುವ ತನಕ ನಿಮಗೆ (ನಿಮ್ಮ ವಿಶ್ವಾಸಾಚಾರ ಗಳಿಗೆ) ಯಾವ ತಳಹದಿಯೂ ಇಲ್ಲ. ನಿಮ್ಮ ಮೇಲೆ ಅವತೀರ್ಣವಾದ ಖುರ್‍ಆನ್ ಅವರಲ್ಲಿ ಅನೇಕರ ಅತಿಕ್ರಮ ಸ್ವಭಾವವನ್ನೂ ಸತ್ಯ ದಿಕ್ಕಾ ರವನ್ನೂ ಹೆಚ್ಚಿಸಿ ಬಿಟ್ಟಿದೆ. ಆದ್ದರಿಂದ ಸತ್ಯ ನಿಷೇಧಿಗಳಾದ ಆ ಜನರ ಬಗ್ಗೆ ದುಃಖಿಸಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(5,69,'5:69','ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿರಲಿ, ಯಹೂದಿಯರಿರಲಿ, ಸಬಯನರಿರಲಿ, ಕ್ರೈಸ್ತರಿರಲಿ, ಅವರ ಪೈಕಿ ಯಾರು ಅಲ್ಲಾಹನ ಮೇಲೆ ಮತ್ತು ಪರಲೋಕದ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸಿಕೊಂಡು ಸತ್ಕರ್ಮಗಳನ್ನು ಮಾಡುತ್ತಾರೋ ಅವರಿಗೆ ಯಾವ ಭಯವೂ ಇಲ್ಲ. ವ್ಯಥೆಯೂ ಇಲ್ಲ .');
INSERT INTO "kannada" VALUES(5,70,'5:70','ಇಸ್‍ರಾಈಲ ಸಂತತಿಗಳೊಂದಿಗೆ ನಾವು ಕರಾರನ್ನು ಪಡೆದೆವು. ಅವರ ಕಡೆಗೆ ಅನೇಕ ಪ್ರವಾದಿಗಳನ್ನು ಕಳುಹಿಸಿಕೊಟ್ಟೆವು. ಆದರೆ, ಅವರ ಮನಸ್ಸಿಗೆ ಒಪ್ಪದ ಸಂದೇಶಗಳೊಂದಿಗೆ ಅವರ ಬಳಿ ಸಂದೇಶವಾಹಕರು ಬಂದಾಗಲೆಲ್ಲಾ ಅವರು ಕೆಲವರನ್ನು ಸುಳ್ಳಾಗಿಸಿದರು, ಇನ್ನು ಕೆಲವರನ್ನು ವಧಿಸಿಬಿಟ್ಟರು.');
INSERT INTO "kannada" VALUES(5,71,'5:71','ಯಾವ ಪರೀಕ್ಷಣವೂ ಉಂಟಾಗದೆಂದು ಅವರು ಭಾವಿಸಿಕೊಂಡರು. ಆದುದರಿಂದ ಅವರು ಕುರುಡರೂ ಕಿವುಡರೂ ಆಗಿ ಬಿಟ್ಟರು. ಆ ಬಳಿಕ ಅಲ್ಲಾಹನು ಅವರ ಪಶ್ಚಾತ್ತಾಪವನ್ನು ಸ್ವೀಕರಿಸಿದನು. ಆ ಬಳಿಕವೂ ಅವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಕುರುಡರೂ, ಕಿವುಡರೂ ಆಗುತ್ತಾ ಹೋದರು. ಅಲ್ಲಾಹು ಅವರ ಎಲ್ಲ ಕೃತ್ಯಗಳನ್ನು ಕಾಣುವವನು.');
INSERT INTO "kannada" VALUES(5,72,'5:72','ಮರ್ಯಮರ ಪುತ್ರ ಮಸೀಹರೇ ಅಲ್ಲಾಹನೆಂದು ಹೇಳಿದವರು ಖಂಡಿತವಾಗಿಯೂ ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸಿದ್ದಾರೆ. ವಸ್ತುತಃ ಮಸೀಹರು, ಹೀಗೆ ಹೇಳಿದ್ದರು; ಓ ಇಸ್‍ರಾಈಲ ಸಂತತಿಗಳೇ, ನನ್ನ ಪ್ರಭುವೂ ನಿಮ್ಮ ಪ್ರಭುವೂ ಆಗಿರುವ ಅಲ್ಲಾ ಹನಿಗೆ ಆರಾಧನೆ ಸಲ್ಲಿಸಿರಿ. ಯಾರು ಅಲ್ಲಾಹನಿಗೆ ಸಹಭಾಗಿತ್ವ ಕಲ್ಪಿಸುತ್ತಾನೋ, ಅವನಿಗೆ ಅಲ್ಲಾಹು ಸ್ವರ್ಗವನ್ನು ನಿಷಿದ್ಧಗೊಳಿಸಿರುತ್ತಾನೆ ಮತ್ತು ಅವನ ನಿವಾಸವು ನರಕವಾಗಿ ರುವುದು. ಅಕ್ರಮಿಗಳಿಗೆ ಯಾರೂ ಸಹಾಯಕರಿಲ್ಲ’’.');
INSERT INTO "kannada" VALUES(5,73,'5:73','‘ಅಲ್ಲಾಹನು ಮೂವರಲ್ಲೊಬ್ಬನು’ ಎಂದು ಹೇಳಿ ದವರು ಖಂಡಿತವಾಗಿಯೂ ಕಾಫಿರರಾಗಿದ್ದಾರೆ . ಏಕದೇವನಲ್ಲದೆ ಬೇರೆ ದೇವನಿಲ್ಲ. ಇವರು ತಮ್ಮ ಇಂತಹ ಮಾತುಗಳಿಂದ ದೂರ ಸರಿಯದಿದ್ದರೆ, ಆ ಕಾಫಿರರಿಗೆ ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆ ಬಾಧಿಸುವುದು.');
INSERT INTO "kannada" VALUES(5,74,'5:74','ಇವರಿಗೆ ಅಲ್ಲಾಹನ ಕಡೆಗೆ ಮರಳಬಾರದೇ? ತಮ್ಮ ಪಾಪಕ್ಕೆ ಅವನೊಡನೆ ಕ್ಷಮಾಯಾಚನೆ ಮಾಡಬಾರದೇ? ಅಲ್ಲಾಹನು ಮಹಾ ಕ್ಷಮಾ ಶೀಲನೂ ಪರಮದಯಾಳುವೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(5,75,'5:75','ಮರ್ಯಮರ ಪುತ್ರ ಮಸೀಹರು ಕೇವಲ ಓರ್ವ ಪ್ರವಾದಿಯಾಗಿದ್ದರು. ಅವರಿಗಿಂತ ಮುಂಚೆ ಅನೇಕ ಪ್ರವಾದಿಗಳು ಗತಿಸಿಹೋಗಿದ್ದರು. ಅವರ ಮಾತೆ ಪರಮ ಸತ್ಯನಿಷ್ಠೆ. ಅವರಿಬ್ಬರೂ ಆಹಾರ ಸೇವಿಸುತ್ತಿದ್ದರು. ನಾವು ಯಾವ ರೀತಿ ಇವರಿಗೆ ದೃಷ್ಟಾಂತಗಳನ್ನು ವಿವರಿಸುತ್ತಿದ್ದೇವೆ ಎಂಬುದನ್ನು ನೋಡಿರಿ. ಹೀಗಿದ್ದೂ ಇವರು ಹೇಗೆ ಬೆನ್ನು ತಿರುಗಿಸುತ್ತಿದ್ದಾರೆಂಬುದನ್ನು ನೋಡಿರಿ.');
INSERT INTO "kannada" VALUES(5,76,'5:76','ಹೇಳಿರಿ-ನೀವು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ನಿಮಗೆ ಯಾವುದೇ ಗುಣವನ್ನಾಗಲಿ ದೋಷವನ್ನಾಗಲಿ ತರಲು ಸಾಧ್ಯವಿಲ್ಲದವುಗಳಿಗೆ ಆರಾಧಿಸುತ್ತೀರಾ ? ವಸ್ತುತಃ ಸರ್ವವನ್ನೂ ಆಲಿಸುವವನೂ ಸರ್ವ ವನ್ನೂ ಅರಿಯುವವನೂ ಅಲ್ಲಾಹನೇ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(5,77,'5:77','ಹೇಳಿರಿ-"ಓ ಗ್ರಂಥದವರೇ, ಸತ್ಯಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ಎಲ್ಲೆ ಮೀರಿ ಹೋಗದಿರಿ. ನಿಮಗಿಂತ ಮುಂಚೆ ದಾರಿ ತಪ್ಪಿರುವ ಹಾಗೂ ಅನೇಕರನ್ನು ದಾರಿ ತಪ್ಪಿಸಿರುವ ಮತ್ತು ಸ್ವತಃ ಸತ್ಯ ಮಾರ್ಗದಿಂದ ವ್ಯತಿಚಲಿಸಿದ ಒಂದು ಜನತೆಯ ತನ್ನಿಚ್ಛೆಯನ್ನು ಅನುಸರಿಸ ಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(5,78,'5:78','ಇಸ್‍ರಾಈಲ ಸಂತಾನದವರ ಪೈಕಿ ಕಾಫಿರಾದ ವರು ದಾವೂದ್ ಮತ್ತು ಮರ್ಯಮರ ಪುತ್ರ `ಈಸಾ''ರ ನಾಲಗೆಯಲ್ಲಿ ಶಪಿಸಲ್ಪಟ್ಟರು. ಇದು ಅವರ ಧಿಕ್ಕಾರ ಮತ್ತು ಹದ್ದುಮೀರಿದ ವರ್ತನೆಯ ಫಲವಾಗಿತ್ತು.');
INSERT INTO "kannada" VALUES(5,79,'5:79','ಅವರು ಮಾಡುತ್ತಿದ್ದ ನಿಷಿದ್ಧ ಕಾರ್ಯಗಳಿಂದ ಪರಸ್ಪರ ತಡೆಯುತ್ತಿರಲಿಲ್ಲ. ಅವರು ಕೈಕೊಂಡ ಕೃತ್ಯವು ಬಹಳ ಹೀನಾಯವಾಗಿತ್ತು.');
INSERT INTO "kannada" VALUES(5,80,'5:80','ನೀವು ಅವರಲ್ಲಿ ಸತ್ಯನಿಷೇಧಿಗಳ ಗೆಳೆತನ ಮಾಡುವ ಅನೇಕರನ್ನು ಕಾಣುತ್ತೀರಿ. ಅವರು ಸ್ವಂತಕ್ಕಾಗಿ ಸಿದ್ಧಪಡಿಸಿಕೊಂಡಿರುವುದರ ಪರಿ ಣಾಮ ಅತ್ಯಂತ ನಿಕೃಷ್ಟವಾಗಿರುತ್ತದೆ. ಅಂದರೆ ಅಲ್ಲಾಹನು ಅವರ ಮೇಲೆ ಅತ್ಯಂತ ಕ್ರೋಧಗೊಂ ಡಿರುತ್ತಾನೆ ಎಂಬುದು. ಮತ್ತು ಶಿಕ್ಷೆಯಲ್ಲಿ ಅವರು ಶಾಶ್ವತ ನಿವಾಸಿಗಳು.');
INSERT INTO "kannada" VALUES(5,81,'5:81','ಅವರು ಅಲ್ಲಾಹು, ಪ್ರವಾದಿ ಮತ್ತು ಅವರ ಮೇಲೆ ಅವತೀರ್ಣಗೊಂಡುದುದನ್ನು ನಂಬುವವರಾಗಿ ದ್ದರೆ, ಆ ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ತಮ್ಮ ಆಪ್ತಮಿತ್ರರನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳುತ್ತಿರಲಿಲ್ಲ. ಆದರೆ ಅವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಕರ್ಮಭ್ರಷ್ಟರಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(5,82,'5:82','ಜನರ ಪೈಕಿ ನೀವು ಯಹೂದಿಯರನ್ನೂ `ಮು ಶ್ರಿಕ''ರನ್ನೂ ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಮೇಲೆ ಹಗೆತ ನದಲ್ಲಿ ಅತ್ಯುಗ್ರರನ್ನಾಗಿ ಕಾಣುವಿರಿ. ‘ನಾವು ನಸಾರಾಗಳು’ ಎಂದು ಹೇಳಿದ್ದವರನ್ನು ನೀವು ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಮೇಲಿನ ದಯೆಯಲ್ಲಿ ಅವರಿ ಗಿಂತ ಸಮೀಪದವರನ್ನಾಗಿ ಕಾಣುವಿರಿ. ಏಕೆಂದರೆ ಅವರ ಪೈಕಿ ಧರ್ಮ ಭಕ್ತ ವಿದ್ವಾಂಸರೂ ವಿರಕ್ತ ಸಾಧುಗಳೂ ಇದ್ದಾರೆ ಮತ್ತು ಅವರು ಅಹಂಭಾವ ತೋರುವವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(5,83,'5:83','ಅವರು ದೂತರ ಮೇಲೆ ಅವತೀರ್ಣಗೊಂಡ (ಈ ವಾಣಿಯನ್ನು) ಆಲಿಸುವಾಗ ಸತ್ಯವನ್ನು ಗ್ರಹಿಸಿದ ಫಲವಾಗಿ ಅವರ ಕಣ್ಣುಗಳು ಕಂಬನಿ ಹರಿಸುತ್ತಿರುವುದನ್ನು ನೀವು ಕಾಣುತ್ತೀರಿ. ಅವರು ಹೇಳುತ್ತಾರೆ; `ಓ ನಮ್ಮ ಪ್ರಭೂ, ನಾವು ವಿಶ್ವಾಸವಿರಿಸಿದೆವು, ಆದ್ದರಿಂದ ಸತ್ಯಕ್ಕೆ ಸಾಕ್ಷ್ಯ ವಹಿಸುವವರ ಕೂಟದಲ್ಲಿ ನಮ್ಮನ್ನು ದಾಖಲಿಸು’’.');
INSERT INTO "kannada" VALUES(5,84,'5:84','ನಮ್ಮ ಪ್ರಭುವು ನಮ್ಮನ್ನು ಸಜ್ಜನ ಭಕ್ತರ ಸಾಲಲ್ಲಿ ಸೇರಿಸಿಕೊಳ್ಳಬೇಕೆಂದು ನಾವು ಆಗ್ರಹಿಸುತ್ತಿ ರುವಾಗ ಅಲ್ಲಾಹು ಹಾಗೂ ನಮಗೆ ಬಂದಿರುವ ಸತ್ಯದಲ್ಲಿ ನಂಬದಿರಲು ನಮಗೆ ಹೇಗೆ ಸಾಧ್ಯ? (ಎಂದೂ ಹೇಳುತ್ತಾರೆ)');
INSERT INTO "kannada" VALUES(5,85,'5:85','ಅವರು ಹಾಗೆ ಹೇಳಿದ ಕಾರಣದಿಂದ ಅವರಿಗೆ ತಳಭಾಗದಲ್ಲಿ ನದಿಗಳು ಹರಿಯುತ್ತಿರುವ ಸ್ವರ್ಗೋದ್ಯಾನಗಳನ್ನು ಅಲ್ಲಾಹನು ದಯ ಪಾಲಿಸಿದನು. ಅವರು ಅಲ್ಲಿ ಸದಾಕಾಲ ವಾಸಿ ಸುವರು. ಸುಕೃತರಿಗೆ ನೀಡುವ ಪ್ರತಿಫಲವಿದು.');
INSERT INTO "kannada" VALUES(5,86,'5:86','ಆದರೆ ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸಿ ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದವರು, ಅವರು ನರಕದವರು.');
INSERT INTO "kannada" VALUES(5,87,'5:87','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಅಲ್ಲಾಹನು ನಿಮಗೆ ಧರ್ಮಬದ್ಧಗೊಳಿಸಿರುವ ಉತ್ತಮ ವಸ್ತುಗಳನ್ನು ನಿಷಿದ್ಧ ಗೊಳಿಸಿಕೊಳ್ಳಬೇಡಿರಿ ಮತ್ತು ಹದ್ದುಮೀರ ಬೇಡಿರಿ. ಹದ್ದು ಮೀರುವವರನ್ನು ಅಲ್ಲಾಹು ಮೆಚ್ಚುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(5,88,'5:88','ಅಲ್ಲಾಹನು ನಿಮಗೆ ನೀಡಿರುವ ಹಲಾಲ್ ಮತ್ತು ಉತ್ತಮ ಆಹಾರವನ್ನು ಉಣ್ಣಿರಿ, ಮತ್ತು ಯಾವ ಅಲ್ಲಾಹನ ಮೇಲೆ ನೀವು ನಂಬಿಕೆಯಿರಿಸುವಿರೋ ಅವನ ಬಗ್ಗೆ ಜಾಗ್ರತೆ ವಹಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(5,89,'5:89','ಉದ್ದೇಶಪೂರ್ವಕವಲ್ಲದ ನಿಮ್ಮ ಶಪಥಗಳಿಗಾಗಿ ಅಲ್ಲಾಹನು ನಿಮ್ಮನ್ನು ಶಿಕ್ಷಿಸುವುದಿಲ್ಲ. ಆದರೆ ನೀವು ಮನಪೂರ್ವಕ ಮಾಡುವ ಶಪಥಗಳ ಕುರಿತು ಅವನು ಶಿಕ್ಷಿಸುವನು. (ಮನಪೂರ್ವಕ ಶಪಥ ಮಾಡಿ ಅದನ್ನು ಉಲ್ಲಂಘಿಸಿದರೆ) ಪ್ರಾಯಶ್ಚಿತ್ತವಾಗಿ ಹತ್ತು ಮಂದಿ ಬಡವರಿಗೆ, ನೀವು ನಿಮ್ಮ ಸಂಸಾರಕ್ಕೆ ಉಣಿಸುವ ಮಧ್ಯಮ ನೆಲೆಯ ಆಹಾರ ಕೊಡಿರಿ ಅಥವಾ ಅವರಿಗೆ ಬಟ್ಟೆ ಯುಡಿಸಿರಿ ಅಥವಾ ಒಬ್ಬ ಗುಲಾಮನನ್ನು ದಾಸ್ಯಮುಕ್ತಿಗೊಳಿಸಿರಿ. ಇದು ಅಸಾಧ್ಯವಾದ ವನು ಮೂರು ದಿನಗಳ ವ್ರತ ಕೈಗೊಳ್ಳಬೇಕು . ನೀವು ಆಣೆ ಹಾಕಿ ಹೇಳಿದರೆ, ನಿಮ್ಮ ಶಪಥಗಳ ಉಲ್ಲಂಘನೆಗಿರುವ ಪ್ರಾಯಶ್ಚಿತ್ತವಿದು. ನಿಮ್ಮ ಶಪಥಗಳ ಬಗ್ಗೆ ಎಚ್ಚರಿಕೆ ವಹಿಸಿರಿ . ನೀವು ಕೃತಜ್ಞತೆಯುಳ್ಳವರಾಗಲೆಂದು ಅಲ್ಲಾಹನು ತನ್ನ ಆದೇಶಗಳನ್ನು ಹೀಗೆ ನಿಮಗೆ ವಿವರಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(5,90,'5:90','ಓ ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳೇ, ಮದ್ಯ, ಜೂಜು, ಪ್ರತಿಷ್ಠಾಪಿತ ವಿಗ್ರಹಗಳು ಮತ್ತು ಪ್ರಶ್ನೆ ಪರಿಹಾರದ ಬಾಣಗಳೂ ಪೈಶಾಚಿಕವಾದ ಹೊಲಸು ಕೃತ್ಯಗಳಾಗಿವೆ. ಅವುಗಳನ್ನು ವರ್ಜಿಸಿರಿ, ನೀವು ಜಯಶಾಲಿ ಗಳಾಗಲೆಂದು .');
INSERT INTO "kannada" VALUES(5,91,'5:91','ಶೈತಾನನು ಮದ್ಯ ಮತ್ತು ಜೂಜಿನ ಮೂಲಕ ನಿಮ್ಮೊಳಗೆ ವೈರತ್ವ ಮತ್ತು ದ್ವೇಷವನ್ನುಂಟು ಮಾಡಲಿಕ್ಕೂ ನಿಮ್ಮನ್ನು ಅಲ್ಲಾಹನ ಸ್ಮರಣೆ ಮತ್ತು ನಮಾಝಿನಿಂದ ತಡೆಯಲಿಕ್ಕೂ ಉದ್ದೇಶಿಸುತ್ತಾನೆ. ಆದ್ದರಿಂದ ನೀವು ಅವುಗಳಿಂದ ದೂರವಿರಿ.');
INSERT INTO "kannada" VALUES(5,92,'5:92','ನೀವು ಅಲ್ಲಾಹನಿಗೆ ಅನುಸರಿಸಿರಿ. ಅವನ ದೂತರಿಗೆ ಅನುಸರಿಸಿರಿ. ಆದರೆ ನೀವು (ಅವರ ಆದೇಶಗಳನ್ನು ಮೀರುವುದರ ಬಗ್ಗೆ) ಜಾಗ್ರತೆ ವಹಿಸಿರಿ. ಇನ್ನು ನೀವು ವಿಮುಖರಾದರೆ ನಮ್ಮ ದೂತರಿಗೆ ಸ್ಪಷ್ಟ ಬೋಧನೆಯ ಕರ್ತವ್ಯ ಮಾತ್ರವೇ ಇರುವುದೆಂದು ಅರಿತುಕೊಳ್ಳಿರಿ.');
INSERT INTO "kannada" VALUES(5,93,'5:93','ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿದ ಹಾಗೂ ಸತ್ಕರ್ಮಗಳನ್ನು ಕೈಗೊಂಡವರು ನಿಷಿದ್ಧ ಸೊತ್ತು ಹಿಂದೆ ತಿಂದಿದ್ದರೆ, ದೋಷವಿಲ್ಲ. ಇನ್ನು ಮುಂದೆ ಅವರು ಈ ವಸ್ತು ಗಳಿಂದ ದೂರವಿದ್ದರೆ ಹಾಗೂ `ಈಮಾನ್''ನಲ್ಲೇ ಸ್ಥಿರವಾಗಿದ್ದರೆ, ಸತ್ಕರ್ಮಗಳನ್ನು ಮಾಡಿದರೆ, ಅನಂತರ ನಿಷಿದ್ಧನಿಗ್ರಹ ಹಾಗೂ ಸತ್ಯವಿಶ್ವಾಸದಲ್ಲಿ ಅಚಲರಾದರೆ ಆ ಬಳಿಕ ಜಾಗ್ರತೆ ವಹಿಸಿದರೆ, ಸುಕೃತಗಳನ್ನು ಮಾಡಿದರೆ ಅಲ್ಲಾಹು ಸಜ್ಜನರನ್ನು ಪ್ರೀತಿಸುವನು.');
INSERT INTO "kannada" VALUES(5,94,'5:94','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನಿಮ್ಮಲ್ಲಿ ಯಾರು ಅಲ್ಲಾ ಹನನ್ನು ಅದೃಶ್ಯಾವಸ್ಥೆಯಲ್ಲಿ ಭಯ ಪಡುತ್ತಾ ನೆಂದು ನೋಡಿ ತಿಳಿಯಲಿಕ್ಕಾಗಿ ಅವನು ನಿಮ್ಮ ಕೈಗಳಿಂದ ಮತ್ತು ಭರ್ಚಿಗಳಿಂದ ಬೇಟೆ ಯಾಡಿ ಹಿಡಿಯಬಹುದಾದ ಕೆಲವು ಬೇಟೆ ಮೃಗಗಳ ಮೂಲಕ ನಿಮ್ಮನ್ನು ಪರೀಕ್ಷೆ ಗೊಳಪಡಿಸಿದನು. ಇದರ ಅನಂತರವೂ ಯಾರಾದರೂ ಅತಿಕ್ರಮ ವೆಸಗಿದರೆ ಅವರಿಗೆ ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆಯಿದೆ.');
INSERT INTO "kannada" VALUES(5,95,'5:95','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಇಹ್ರಾಮಿನ ಸ್ಥಿತಿಯಲ್ಲಿ ಬೇಟೆ ಪ್ರಾಣಿಗಳನ್ನು ಕೊಲ್ಲಬೇಡಿರಿ . ನಿಮ್ಮ ಲ್ಲಾರಾದರೂ ಮನಪೂರ್ವಕ ಅದನ್ನು ಕೊಂದರೆ, ಅವನು ತಾನು ಮಾಡಿದ ಕುಕೃತ್ಯದ ಸವಿಯನ್ನು ಅನುಭವಿಸುವಂತಾಗಲು ಅವನು ಕೊಂದ ಪ್ರಾಣಿಗೆ ಸಮಾನವೆಂದು ನಿಮ್ಮ ಪೈಕಿ ಇಬ್ಬರು ನೀತಿವಂತರು ತೀರ್ಮಾನಿಸಿದ ಪಶುವನ್ನು ಕಅಬಾಕ್ಕೆ ತಲುಪಿಸಲ್ಪಡುವ ಬಲಿ ಮೃಗವಾಗಿ ನೀಡಬೇಕು . ಇಲ್ಲವಾದರೆ ಪ್ರಾಯಶ್ಚಿತ್ತ ರೂಪದಲ್ಲಿ ಕೆಲವು ಮಂದಿ ಬಡವರಿಗೆ ಅನ್ನದಾನ ಅಥವಾ ಅದಕ್ಕೆ ಸಮಾನವಾದ ಉಪವಾಸ ಕೈಗೊಳ್ಳಬೇಕು. ಹಿಂದೆ ಆಗಿ ಹೋದುದನ್ನು ಅಲ್ಲಾಹು ಕ್ಷಮಿಸಿರುತ್ತಾನೆ. ಆದರೆ, ಇನ್ನು ಯಾರಾದರೂ ಈ ಕೃತ್ಯವನ್ನು ಪುನರಾ ವರ್ತಿಸಿದರೆ, ಅಲ್ಲಾಹು ಅವನಿಂದ ಪ್ರತೀಕಾರ ಕ್ರಮಕೈಗೊಳ್ಳುವನು. ಅಲ್ಲಾಹು ಮಹಾ ಪ್ರತಾ ಪಶಾಲಿಯೂ ಪ್ರತೀಕಾರ ಪಡೆಯಲು ಶಕ್ತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(5,96,'5:96','ನಿಮಗೂ ಇತರ ಸಂಚಾರಿಗಳಿಗೂ ಅನುಭವಿಸ ಲಿಕ್ಕಾಗಿ ಸಮುದ್ರದ ಬೇಟೆ ಮತ್ತು ಅದರ ಆಹಾರವು ನಿಮಗೆ ಧರ್ಮಬದ್ಧಗೊಳಿಸಲಾಗಿದೆ . ಆದರೆ ನೀವು ಇಹ್ರಾಮಿನ ಸ್ಥಿತಿಯಲ್ಲಿರುವ ತನಕ ನೆಲದ ಬೇಟೆಯು ನಿಮಗೆ ನಿಷಿದ್ಧವಾಗಿದೆ. ಆದುದರಿಂದ ಯಾರ ಕಡೆಗೆ ನಿಮ್ಮೆಲ್ಲರನ್ನು ಒಂದುಗೂಡಿಸಲಾಗುವುದೋ ಆ ಅಲ್ಲಾಹನ ಬಗ್ಗೆ ಎಚ್ಚರ ವಹಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(5,97,'5:97','ಅಲ್ಲಾಹನು ಪವಿತ್ರ ಭವನವಾದ ಕಅಬಃವನ್ನು ಜನರ ಪಾಲಿಗೆ ಒಂದು ಆಸರೆಯನ್ನಾಗಿ ಮಾಡಿದನು ಮತ್ತು ವಿಶುದ್ಧ ತಿಂಗಳನ್ನೂ ಬಲಿ ಮೃಗಗಳನ್ನೂ ಅವುಗಳ ಕೊರಳಿಗೆ ಕಟ್ಟಿದ ಗುರುತಿನ ತೊಗಲು ಪಟ್ಟಿಗಳನ್ನೂ. ಇದು ಅಲ್ಲಾಹನು ಭೂಮಿ-ಆಕಾಶಗಳಲ್ಲಿರುವುದನ್ನೆಲ್ಲ ಖಂಡಿತ ಅರಿಯುವವನೆಂದೂ ನಿಶ್ಚಯವಾಗಿ ಅಲ್ಲಾಹನು ಎಲ್ಲ ವಸ್ತುಗಳ ಪರಿಜ್ಞಾನಿಯೆಂದೂ ನೀವು ತಿಳಿದುಕೊಳ್ಳಲಿಕ್ಕಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(5,98,'5:98','ನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆಂಬುದನ್ನು ನೀವು ತಿಳಿದುಕೊಳ್ಳಿರಿ.');
INSERT INTO "kannada" VALUES(5,99,'5:99','ದೇವದೂತರ ಮೇಲೆ ಸಂದೇಶವನ್ನು ತಲುಪಿ ಸುವ ಹೊಣೆ ಮಾತ್ರವಿದೆ. ನೀವು ಬಹಿರಂಗ ಗೊಳಿಸುವುದನ್ನೂ, ಬಚ್ಚಿಡುವುದನ್ನೂ ಅಲ್ಲಾ ಹನು ಅರಿಯುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(5,100,'5:100','ಓ ದೂತರೇ, ಹೇಳಿರಿ, ಕೆಟ್ಟದ್ದೂ ಒಳ್ಳೆಯದ್ದೂ ಸಮಾನವಲ್ಲ . ಕೆಟ್ಟವುಗಳ ಹೆಚ್ಚಳ ನಿಮ್ಮನ್ನು ಮೋಹಗೊಳಿಸಿದ್ದರೂ ಸರಿ. ಆದುದರಿಂದ ಓ ಬುದ್ಧಿಜೀವಿಗಳೇ, ನೀವು ಅಲ್ಲಾಹನಿಗೆ ಭಯ ಪಡಿರಿ. ನೀವು ಜಯ ಗಳಿಸಲೆಂದು.');
INSERT INTO "kannada" VALUES(5,101,'5:101','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ವಿಪರೀತ ಪ್ರಶ್ನೆ ಕೇಳ ಬೇಡಿರಿ. ಎಲ್ಲ ಪ್ರಶ್ನೆಗಳಿಗೆ ನಿಮಗೆ ಉತ್ತರ ಅನಾ ವರಣಗೊಳಿಸಲ್ಪಟ್ಟರೆ ನಿಮಗೇ ತೊಂದರೆ. ಖುರ್ ಆನ್ ಅವತೀರ್ಣಗೊಳ್ಳುತ್ತಿರುವಾಗ ವಿಪರೀತ ಪ್ರಶ್ನೆ ಕೇಳಿದರೆ, ಅವನ್ನು ನಿಮಗೆ ಪ್ರಕಟಗೊಳಿ ಸಲಾಗುತ್ತದೆ. (ನೀವು ಇಷ್ಟರ ತನಕ ಪ್ರಶ್ನೆ ಕೇಳಿ ರುವುದನ್ನು) ಅಲ್ಲಾಹು ಕ್ಷಮಿಸಿರುವನು, ಅವನು ಕ್ಷಮಾಶೀಲನೂ ಸಹನಶೀಲನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(5,102,'5:102','ನಿಮಗೆ ಮೊದಲಿನ ಒಂದು ಜನಾಂಗವು ಹೀಗೆ ವಿಪರೀತ ಪ್ರಶ್ನೆಗಳನ್ನು ಕೇಳಿತ್ತು. ನಂತರ ಅವರು ಅವುಗಳನ್ನು ಉಲ್ಲಂಘಿಸಿದರು.');
INSERT INTO "kannada" VALUES(5,103,'5:103','ಯಾವುದೇ ಬಹೀರಃವನ್ನಾಗಲೀ ಸಾಇಬಃವನ್ನಾ ಗಲೀ ವಸೀಲವನ್ನಾಗಲಿ ಹಾಮವನ್ನಾಗಲಿ ಅಲ್ಲಾ ಹು ನಿಶ್ಚಯಿಸಿಲ್ಲ. ಆದರೆ ಸತ್ಯನಿಷೇಧಿಗಳು ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಳ್ಳಾರೋಪ ಹೊರಿಸುತ್ತಾರೆ ಮತ್ತು ಅವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಚಿಂತಿಸುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(5,104,'5:104','`ಅಲ್ಲಾಹನು ಅವತೀರ್ಣಗೊಳಿಸಿದ ಶಾಸನದ ಕಡೆಗೂ ಆತನ ಸಂದೇಶವಾಹಕರ ಕಡೆಗೂ ಬನ್ನಿರಿ ಎಂದು ಅವರಿಗೆ ಹೇಳಿದಾಗ ಅವರು, ನಮ್ಮ ಪೂರ್ವಿಕರು ನಡೆದು ಬಂದಿರುವುದಾಗಿ ತೋರಿದ ಮಾರ್ಗವೇ ನಮಗೆ ಸಾಕು ಎಂದು ಹೇಳುತ್ತಾರೆ. ಅವರ ಪೂರ್ವ ಪಿತೃರು ಏನೂ ಅರಿತಿರದಿದ್ದರೂ ಮತ್ತು ಅವರು ಸನ್ಮಾರ್ಗ ಹೊಂದಿಲ್ಲದಿದ್ದರೂ (ಇವರು ಅವರನ್ನೇ ಅನುಕರಿಸುತ್ತಿರು ವರೇ?)');
INSERT INTO "kannada" VALUES(5,105,'5:105','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ನಿಮ್ಮ ಬಗ್ಗೆ ಗಮನಿಸಿಕೊಳ್ಳಿರಿ. ನೀವು ಸ್ವತಃ ಸತ್ಪಥದಲ್ಲಿದ್ದರೆ ದಾರಿಗೆಟ್ಟವರು ನಿಮಗೇನೂ ದೋಷ ಮಾಡಲಾರರು . ನಿಮ್ಮ ಮರಳುವಿಕೆ ಅಲ್ಲಾಹನ ಕಡೆಗೇ ಆಗಿರುತ್ತದೆ. ನೀವು ಮಾಡುತ್ತಿದ್ದುದರ ಕುರಿತು ಅವನು ನಿಮಗೆ ವಿವರಿಸಿ ಕೊಡುವನು.');
INSERT INTO "kannada" VALUES(5,106,'5:106','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನಿಮ್ಮಲ್ಲೊಬ್ಬನಿಗೆ ಮರಣ ಸನ್ನಿಹಿತವಾದಾಗ, ಅಂದರೆ ವಸಿಯ್ಯತ್ ಮಾಡುವಾಗ ನಿಮ್ಮ ನಡುವಿನ ವಿವಾದಕ್ಕೆ ಸಾಕ್ಷ್ಯ ಯಾವುದೆಂದರೆ ನಿಮ್ಮ ಧರ್ಮದವರೇ ಆದ ಇಬ್ಬರು ನ್ಯಾಯನಿಷ್ಟರು. ನೀವು ಯಾತ್ರೆಯಲ್ಲಿದ್ದು ಮಾರಣಾಂತಿಕ ಆಪತ್ತು ಬಂದರೆ ಸಾಕ್ಷ್ಯವು ಅನ್ಯ ಧರ್ಮೀಯರಾದ ಇಬ್ಬರು. ನಿಮಗೆ ಸಂದೇಹ ಬಂದರೆ ಇವರಿಬ್ಬರನ್ನೂ (ಅಸರ್) ನಮಾಝ್‍ನ ಬಳಿಕ ತಡೆದು ನಿಲ್ಲಿ ಸಲಾಗಿ; “ಅವನು ನಮ್ಮ ಬಂಧು-ಬಳಗದ ವನೇ ಆಗಿದ್ದರೂ ನಾವು ಇದರಿಂದ ತುಚ್ಛ ಮೌಲ್ಯ ದಕ್ಕಿಸಿಕೊಳ್ಳುವುದೋ ಅಲ್ಲಾಹನ ಸಾಕ್ಷಿ ಯನ್ನು ಮುಚ್ಚಿಡುವುದೋ ಮಾಡಲಾರೆವು. ಹಾಗೆ ಮಾಡಿದರೆ ನಾವು ಪಾಪಿಗಳು” ಎಂದು ಅವರಿಬ್ಬರೂ ಅಲ್ಲಾಹನ ಹೆಸರಲ್ಲಿ ಆಣೆ ಹಾಕಿ ಹೇಳತಕ್ಕದ್ದು.');
INSERT INTO "kannada" VALUES(5,107,'5:107','ಇನ್ನು ಅವರೀರ್ವರು (ವಂಚನೆ ಮಾಡಿ) ಪಾಪಕ್ಕೆ ಹಕ್ಕುದಾರರಾಗಿರುವುದು ಬಯಲಾದರೆ ಅವರಿಬ್ಬರ ಸ್ಥಾನದಲ್ಲಿ ಸೊತ್ತಿನ ಹಕ್ಕುದಾರರ ಹೆಚ್ಚು ಹತ್ತಿರದ ಸಂಬಂಧಿಕರಿಬ್ಬರು ನಿಂತು ಕೊಂಡು; “ಇವರಿಬ್ಬರ ಸಾಕ್ಷ್ಯಕ್ಕಿಂತ ನಮ್ಮ ಸಾಕ್ಷ್ಯ ಹೆಚ್ಚು ಸತ್ಯ. ನಾವು ನೀತಿಯ ಎಲ್ಲೆ ಮೀರಿಲ್ಲ, ಮೀರಿದರೆ ನಾವು ಅಕ್ರಮಿಗಳು” ಎಂದು ಅಲ್ಲಾಹನಲ್ಲಿ ಆಣೆ ಹಾಕಿ ಹೇಳಬೇಕು.');
INSERT INTO "kannada" VALUES(5,108,'5:108','ಅವರು ಸಾಕ್ಷ್ಯವನ್ನು ನ್ಯಾಯಬದ್ಧವಾಗಿ ನಿರ್ವಹಿಸಲು ಹಾಗೂ ಇವರ ಆಣೆಯ ನಂತರ ಅವರ ಆಣೆ ತಳ್ಳಲ್ಪಡುವುದನ್ನು ಭಯಪಡಲು ಕಾಯಿದೆಗಳನ್ನು ತರಲಾಗಿದೆ. ಅಲ್ಲಾಹನಿಗೆ ಭಯಪಡಿರಿ. ಆಲಿಸಿರಿ. ಕರ್ಮಭ್ರಷ್ಟ ಜನತೆಗೆ ಅಲ್ಲಾಹನು ಮಾರ್ಗದರ್ಶನ ಮಾಡುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(5,109,'5:109','ಅಲ್ಲಾಹನು ಸಂದೇಶವಾಹಕರನ್ನೆಲ್ಲ ಒಟ್ಟು ಗೂಡಿಸುವ ಹಾಗೂ ನಿಮಗೆ ಏನು ಉತ್ತರ ಸಿಕ್ಕಿತೆಂದು ಕೇಳುವ ಆ ದಿನ ಅವರು, ``ನಮಗೇನೂ ಅರಿವಿಲ್ಲ, ಅಗೋಚರ ಜ್ಞಾನಗಳ ನ್ನೆಲ್ಲ ಬಲ್ಲವನು ನೀನೇ ತಾನೇ?’’ ಎಂದೇ ಉತ್ತರಿಸುವರು .');
INSERT INTO "kannada" VALUES(5,110,'5:110','ಅಲ್ಲಾಹು ಹೇಳಲಿರುವ ಸಂದರ್ಭ(ವನ್ನು ಊಹಿಸಿರಿ). ಓ ಮರ್ಯಮರ ಪುತ್ರ ಈಸಾ, ನಾನು ನಿಮಗೂ ನಿಮ್ಮ ಮಾತೆಗೂ ದಯಪಾಲಿಸಿದ್ದ ಉಪಕಾರವನ್ನು ಜ್ಞಾಪಿಸಿರಿ. ನೀವು ತೊಟ್ಟಿಲಲ್ಲಿಯೂ ವೃದ್ಧಾಪ್ಯದಲ್ಲಿಯೂ ಜನರೊಂದಿಗೆ (ತತ್ವೋಪ ದೇಶಗಳನ್ನು) ಮಾತಾಡುತ್ತಿರುವಾಗಲೂ ನಿಮ್ಮನ್ನು ನಾನು ಪವಿತ್ರಾತ್ಮನಿಂದ ಬಲಗೊಳಿಸಿದ್ದೆ. ನಾನು ನಿಮಗೆ ಬರಹ ಮತ್ತು ತತ್ವಜ್ಞಾನ ಹಾಗೂ ತೌರಾತ್ ಮತ್ತು ಇಂಜೀಲನ್ನು ಕಲಿಸಿ ಕೊಟ್ಟಿದ್ದೆ. ನೀವು ನನ್ನ ಅಪ್ಪಣೆಯಿಂದ ಕಲಸು ಮಣ್ಣಿನಿಂದ ಪಕ್ಷಿಯ ರೂಪವನ್ನು ಸೃಷ್ಟಿಸುತ್ತಿದ್ದಿರಿ ಮತ್ತು ಅದಕ್ಕೆ ನೀವು ಊದಿದಾಗ ನನ್ನ ಅಪ್ಪಣೆಯಿಂದ ಅದು ಪಕ್ಷಿಯಾಗಿ ಮಾರ್ಪಡುತ್ತಿತ್ತು. ನೀವು ಹುಟ್ಟು ಕುರುಡರನ್ನೂ ಪಾಂಡುರೋಗಿಗಳನ್ನೂ ನನ್ನ ಅಪ್ಪಣೆಯಿಂದ ವಾಸಿ ಮಾಡುತ್ತಿದ್ದಿರಿ. ಮೃತರನ್ನು ನನ್ನ ಅಪ್ಪಣೆಯಿಂದ ನೀವು ಸಮಾ ಧಿಗಳಿಂದ ಹೊರತರುತ್ತಿದ್ದಿರಿ . ಇಸ್‍ರಾಈಲ ಸಂತತಿಗಳ ಕಡೆಗೆ ಸುವ್ಯಕ್ತ ದೃಷ್ಟಾಂತಗಳನ್ನು ನೀವು ಕೊಂಡುಹೋದಾಗ ಅವರ ಪೈಕಿ ಸತ್ಯನಿಷೇಧಿಗಳು ಇದು ಸ್ಪಷ್ಟ ವಾಮಾಚಾರ ಎಂದಾಗ; ನಿಮ್ಮನ್ನು ಅವರಿಂದ ರಕ್ಷಿಸಿದೆನು.');
INSERT INTO "kannada" VALUES(5,111,'5:111','ನನ್ನ ಮೇಲೂ ನನ್ನ ಸಂದೇಶವಾಹಕರ ಮೇಲೂ ವಿಶ್ವಾಸವಿಡಿರೆಂದು ಹವಾರಿಗಳಿಗೆ ನಾನು ತಿಳಿಸಿದ ಸಂದರ್ಭ . “ನಾವು ವಿಶ್ವಾಸವಿರಿಸಿದ್ದೇವೆ. ನಾವು ಮುಸ್ಲಿಮರಾಗಿರುವ ಬಗ್ಗೆ ನೀನು ಸಾಕ್ಷ್ಯವಹಿಸು” ಎಂದು ಅವರು ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(5,112,'5:112','ಹವಾರಿಗಳು; “ಓ ಮರ್ಯಮರ ಪುತ್ರ ಈಸಾ, ನಿಮ್ಮ ಪ್ರಭು ನಮ್ಮ ಮೇಲೆ ಆಕಾಶದಿಂದ ಊಟದ ಹಾಸನ್ನು ಇಳಿಸಬಲ್ಲನೇ ?” ಎಂದು ಕೇಳಿದಾಗ ಈಸಾ ಹೇಳಿದರು; ನೀವು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಾಗಿದ್ದರೆ (ಇಂತಹ ಪ್ರಶ್ನೆಗಳ ಬಗ್ಗೆ) ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ .');
INSERT INTO "kannada" VALUES(5,113,'5:113','ಶಾಂತಿಯುಂಟಾಗಲಿಕ್ಕೂ ತಾವು ನಮ್ಮೊಡನೆ ಹೇಳಿದುದೆಲ್ಲ ಸತ್ಯವೆಂದು ನಮಗೆ ದೃಢ ವಾಗುವುದಕ್ಕೂ ನಾವು ಅದರ ಬಗ್ಗೆ ಸಾಕ್ಷಿ ಗಳಾಗಿರಲಿಕ್ಕೂ ಬಯಸುತ್ತೇವೆ” ಎಂದರು.');
INSERT INTO "kannada" VALUES(5,114,'5:114','ಆಗ ಮರ್ಯಮರ ಪುತ್ರ ಈಸಾರವರು ಹೀಗೆ ಪ್ರಾರ್ಥಿಸಿದರು: ನಮ್ಮ ಪ್ರಭುವಾದ ಓ ಅಲ್ಲಾಹ್, ಆಕಾಶದಿಂದ ನಮಗೊಂದು ಊಟದ ಹಾಸನ್ನು ಇಳಿಸು, ಅದು (ಇಳಿಸುವ ದಿನ) ನಮಗೂ ನಮ್ಮ ಸಮಕಾಲೀನರಿಗೂ ಮುಂದಿನವರಿಗೂ ಸಂತಸದ ಹಬ್ಬವಾಗಲಿ . ಅಲ್ಲದೆ ನಿನ್ನ ಕಡೆಯಿಂದ ಒಂದು ದೃಷ್ಟಾಂತವೂ ಆಗಿರಲಿ. ನಮಗೆ ಅದನ್ನು ದಯಪಾಲಿಸು. ನೀನು ಪರಮ ಶ್ರೇಷ್ಠದಾಯಕನು.');
INSERT INTO "kannada" VALUES(5,115,'5:115','ಅಲ್ಲಾಹು ಹೇಳಿದನು: `ನಾನು ಅದನ್ನು ನಿಮ್ಮ ಮೇಲೆ ಖಂಡಿತ ಇಳಿಸುವವನಿದ್ದೇನೆ. ಆದರೆ ಅದರ ನಂತರ ನಿಮ್ಮ ಪೈಕಿ ಸತ್ಯನಿಷೇಧ ಕೈಕೊಂಡವನಿಗೆ ಜಗತ್ತಿನಲ್ಲಿ ಬೇರಾರಿಗೂ ಕೊಡದಂತಹ ಶಿಕ್ಷೆ ಕೊಡುವೆನು’ .');
INSERT INTO "kannada" VALUES(5,116,'5:116','ಅಲ್ಲಾಹನು (ಹೀಗೆ ಕೇಳುವ ಸಂದರ್ಭ) `ಓ ಮರ್ಯಮರ ಪುತ್ರ ಈಸಾ, ಅಲ್ಲಾಹನ ಹೊರತು ನನ್ನನ್ನೂ ನನ್ನ ತಾಯಿಯನ್ನೂ ಎರಡು ದೇವರುಗಳನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳಿರೆಂದು ನೀವು ಜನರಿಗೆ ಹೇಳಿದ್ದಿರಾ ?. ಆಗ ಅವರು (ಈಸಾ) ಉತ್ತರಿಸುವರು : ಅಲ್ಲಾಹನೇ, ನೀನು ಪರಮ ಪಾವನನು. ನನಗೆ ಹೇಳಲು ಹಕ್ಕಿಲ್ಲ ದಂತಹ ಮಾತನ್ನು ಹೇಳುವುದು ನನಗೆ ಯಾವತ್ತೂಲಾಯಕ್ಕಲ್ಲ. ನಾನು ಹಾಗೆ ಹೇಳಿ ರುತ್ತಿದ್ದರೆ ಅದು ಖಂಡಿತ ನಿನಗೆ ತಿಳಿದಿರುತ್ತಿತ್ತು. ನನ್ನ ಮನಸ್ಸಿ ನಲ್ಲಿರುವುದು ನಿನಗೆ ಗೊತ್ತಿದೆ. ನಿನ್ನ ಮನಸ್ಸಿ ನಲ್ಲಿರುವುದು ನನಗೆ ಗೊತ್ತಿಲ್ಲ. ಖಂಡಿತಾ ಅಗೋಚರಗಳ ಪೂರ್ಣಜ್ಞಾನಿ ನೀನು ಮಾತ್ರ.');
INSERT INTO "kannada" VALUES(5,117,'5:117','ನನಗೆ ನೀನು ಆಜ್ಞಾಪಿಸಿದ, ಅಂದರೆ `ನನ್ನ ಪ್ರಭುವೂ ನಿಮ್ಮ ಪ್ರಭುವೂ ಆದ ಅಲ್ಲಾಹನಿಗೆ ಆರಾಧಿಸಿರಿ’’ ಎಂದೇ ಹೊರತು ಬೇರೇನನ್ನೂ ನಾನು ಅವರಿಗೆ ಹೇಳಿಲ್ಲ. ಅವರ ಜೊತೆ ನಾನು ಇದ್ದಾಗ ಅವರ ಮೇಲ್ನೋಟ ವಹಿಸುತ್ತಿದ್ದೆ. ಆದರೆ ನನ್ನನ್ನು ನೀನು (ಆಗಸಕ್ಕೆ) ಪೂರ್ತಿಯಾಗಿ ಎತ್ತಿಕೊಂಡ ಬಳಿಕ ನೀನೇ ಅವರ ಮೇಲ್ವಿಚಾರ ಕನಾಗಿದ್ದೆ . ಎಲ್ಲ ವಸ್ತುವಿನ ಮೇಲೆ ನೀನು ಪರಮ ದೃಕ್ಸಾಕ್ಷಿಯಾಗಿರುವಿ.');
INSERT INTO "kannada" VALUES(5,118,'5:118','ಬಿಟ್ಟರೆ ನಿಶ್ಚಯವಾಗಿಯೂ ನೀನು ಅಜೇಯನೂ, ಯುಕ್ತಿವಂತನೂ ಆಗಿರುವೆ.');
INSERT INTO "kannada" VALUES(5,119,'5:119','ದಿನವಿದು. ಅವರಿಗೆ ತಳಭಾಗದಲ್ಲಿ ನದಿಗಳು ಹರಿಯುತ್ತಿರುವ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಿವೆ. ಅಲ್ಲಿ ಅವರು ಎಂದೆಂದಿಗೂ ಚಿರವಾಸಿಗಳು. ಅಲ್ಲಾಹು ಅವರಿಂದ ತೃಪ್ತನಾದನು ಮತ್ತು ಅವರು ಅವನಿಂದ ತೃಪ್ತರಾದರು. ಇದೇ ಘನವೆತ್ತ ಯಶಸ್ಸು.');
INSERT INTO "kannada" VALUES(5,120,'5:120','ಆಕಾಶಗಳು ಮತ್ತು ಭೂಮಿ ಹಾಗೂ ಅವುಗಳಲ್ಲಿರುವ ಸರ್ವಸ್ವದ ಒಡೆತನ ಅಲ್ಲಾಹನಿಗೆ ಮಾತ್ರವಿದೆ. ಅವನು ಸಕಲ ವಸ್ತುಗಳ ಮೇಲೆ ಸರ್ವ ಸಮರ್ಥನು.');
INSERT INTO "kannada" VALUES(6,1,'6:1','ಆಕಾಶಗಳನ್ನೂ ಭೂಮಿಯನ್ನೂ ಸೃಷ್ಟಿಸಿದ, ಇರುಳುಗಳನ್ನೂ ಪ್ರಕಾಶವನ್ನೂ ಉಂಟುಮಾಡಿದ ಅಲ್ಲಾಹನಿಗೇ ಸರ್ವಸ್ತೊತ್ರಗಳು, ಅದಾಗ್ಯೂ ಸತ್ಯನಿಷೇಧಿಗಳು ತಮ್ಮ ಪಾಲಕ ಪ್ರಭುವಿಗೆ ಸಮಾನರನ್ನು ಕಲ್ಪಿಸಿ ಆರಾಧಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(6,2,'6:2','ಅವನು ನಿಮ್ಮನ್ನು ಕೊಜೆಮಣ್ಣಿನಿಂದ ಸೃಷ್ಟಿಸಿದವನು . ನಂತರ ನಿಮಗಾಗಿ ಜೀವನದ ಒಂದು ಕಾಲಾವಧಿಯನ್ನು ನಿರ್ಣಯಿಸಿದವನು. ಅವನ ಬಳಿ ನಿರ್ಣಯಿಸಲ್ಪಟ್ಟ ಇನ್ನೊಂದು ಕಾಲಾವಧಿಯೂ ಇದೆ . ಆದರೆ ನೀವು ಸಂಶಯಗ್ರಸ್ತರಾಗಿದ್ದೀರಿ.');
INSERT INTO "kannada" VALUES(6,3,'6:3','ಅವನೇ ಆಕಾಶಗಳಲ್ಲೂ ಭೂಮಿಯಲ್ಲೂ ಅಲ್ಲಾಹು. ನಿಮ್ಮ ರಹಸ್ಯವನ್ನೂ ಬಹಿರಂಗವನ್ನೂ ಬಲ್ಲವನು. ನೀವು ಸಂಪಾದಿಸುವ ಒಳಿತು - ಕೆಡುಕುಗಳನ್ನೂ ಚೆನ್ನಾಗಿ ತಿಳಿಯಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(6,4,'6:4','ಅವರ ಪ್ರಭುವಿನ ನಿದರ್ಶನಗಳ ಪೈಕಿ ಯಾವುದೇ ನಿದರ್ಶನ ಅವರ ಬಳಿ ಬಂದರೂ ಅವರು ಅದರಿಂದ ವಿಮುಖರಾಗದಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(6,5,'6:5','ಆಮೇಲೆ ಅವರ ಬಳಿಗೆ ಸತ್ಯಪೂರ್ಣ ಗ್ರಂಥ ಬಂದಾಗಲೂ ಅದನ್ನು ಅವರು ಸುಳ್ಳಾಗಿಸಿದರು. ಅವರು ಯಾವುದನ್ನು ಪರಿಹಾಸ್ಯ ಮಾಡಿ ಕೊಂಡಿದ್ದರೋ ಅದರ ಅಂತಿಮ ಸಮಾಚಾರಗಳು ಸದ್ಯದಲ್ಲೇ ಅವರಿಗೆ ಸಿಗಲಿವೆ.');
INSERT INTO "kannada" VALUES(6,6,'6:6','ಇವರಿಗಿಂತ ಮುಂಚೆ ಎಷ್ಟೋ ಜನಾಂಗಗಳನ್ನೂ ನಾವು ನಾಶಗೊಳಿಸಿದ್ದನ್ನು ಇವರು ಕಂಡಿಲ್ಲವೇ? ನಾವು ನಿಮಗೆ ಅಧೀನಗೊಳಿಸದ ಶಕ್ತಿ ಸವಲತ್ತುಗಳನ್ನು ಅವರಿಗೆ ಭೂಮಿಯಲ್ಲಿ ಅಧೀನಗೊಳಿಸಿ ಕೊಟ್ಟಿದ್ದೆವು. ಅವರ ಮೇಲೆ ಆಕಾಶದಿಂದ ಧಾರಾಳವಾಗಿ ಮಳೆ ಸುರಿಸಿದ್ದೆವು. ಅವರ ನಿವಾಸಗಳ ತಳಭಾಗದಲ್ಲಿ ನದಿಗಳನ್ನು ಹರಿಸಿದೆವು. ಕೊನೆಗೆ ನಾವು ಅವರ ಪಾಪಗಳ ಫಲವಾಗಿ ಅವರನ್ನು ನಾಶಪಡಿಸಿ ಅವರ ಸ್ಥಾನದಲ್ಲಿ ಹೊಸ ಜನಾಂಗಗಳನ್ನು ಎಬ್ಬಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(6,7,'6:7','ಓ ಪೈಗಂಬರರೇ, ನಾವು ನಿಮ್ಮ ಮೇಲೆ ಕಾಗದ ದಲ್ಲಿ ಬರೆದ ಒಂದು ಗ್ರಂಥವನ್ನು ಅವತೀರ್ಣಗೊಳಿಸುತ್ತಿದ್ದರೆ ಮತ್ತು ಜನರು ಅದನ್ನು ಕೈಗ ಳಿಂದ ಮುಟ್ಟಿಯೂ ನೋಡಿಕೊಳ್ಳುತ್ತಿದ್ದರೆ ಆಗಲೂ ಸತ್ಯನಿಷೇಧಿಗಳು ಇದು ವ್ಯಕ್ತವಾದ ಮಾಟ ವಲ್ಲದೆ ಇನ್ನೇನೂ ಅಲ್ಲವೆಂದೇ ಹೇಳುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(6,8,'6:8','ಈ ಪ್ರವಾದಿಯ ಮೇಲೆ ಒಬ್ಬ ಮಲಕ್ ಏಕೆ ಇಳಿಸಲ್ಪಡಲಿಲ್ಲವೆಂದು ಇವರು ಕೇಳುತ್ತಾರೆ. ನಾವು ದೇವಚರರನ್ನು ಇಳಿಸುತ್ತಿದ್ದರೆ, ತಕ್ಷಣವೇ ಶಿಕ್ಷೆಯ ತೀರ್ಮಾನ ನಡೆಯುತ್ತಿತ್ತು. ಅನಂತರ ಇವರಿಗೆ ಅವಧಿ ಕೊಡಲಾಗುತ್ತಿರಲಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(6,9,'6:9','ಒಂದು ವೇಳೆ ನಾವು ದೇವಚರನನ್ನು ಇಳಿಸುತ್ತಿ ದ್ದರೂ ಅವನನ್ನು ಮನುಷ್ಯನಾಗಿಯೇ ಇಳಿಸುತ್ತಿದ್ದೆವು. ಈಗ ಇವರು ಸಿಲುಕಿ ಬಿದ್ದಿರುವ ಸಂಶಯದಲ್ಲೇ ಆಗಲೂ ಅವರನ್ನು ನಾವು ಬೀಳಿ ಸುತ್ತಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(6,10,'6:10','ನಿಮಗಿಂತ ಮುಂಚೆಯೂ ಅನೇಕ ಮಂದಿ ಸಂದೇಶವಾಹಕರು ಪರಿಹಾಸ್ಯಕ್ಕೊಳಗಾಗಿದ್ದಾರೆ. ಆದರೆ, ಕೊನೆಗೆ ಅವರು ಪರಿಹಾಸ್ಯ ಮಾಡುತ್ತಿದ್ದ ಶಿಕ್ಷೆಯೇ ಅವರ ಮೇಲೆ ಬಂದೆರಗಿತು.');
INSERT INTO "kannada" VALUES(6,11,'6:11','ನೀವು ಭೂಮಿಯ ಮೇಲೆ ಸಂಚರಿಸಿರಿ. ಆಮೇಲೆ ಸತ್ಯನಿಷೇಧಿಗಳ ಪರ್ಯಾವಸಾನವೇನಾಯಿತೆಂಬುದನ್ನು ನೋಡಿರೆಂದು ಇವರೊಡನೆ ಹೇಳಿರಿ .');
INSERT INTO "kannada" VALUES(6,12,'6:12','ಭೂಮಿ-ಆಕಾಶಗಳಲ್ಲಿರುವುದೆಲ್ಲ ಯಾರದೆಂದು ಇವರೊಡನೆ ಕೇಳಿರಿ. ಎಲ್ಲವೂ ಅಲ್ಲಾಹನದೇ ಎಂದು ಹೇಳಿರಿ. ಕರುಣೆಯನ್ನು ಅವನು ತನ್ನ ಬಾಧ್ಯತೆಯಾಗಿ ಬರೆದಿದ್ದಾನೆ. ಅವನು ಪುನರು ತ್ಥಾನ ದಿನದಂದು ನಿಮ್ಮೆಲ್ಲರನ್ನೂ ಖಂಡಿತವಾಗಿ ಯೂ ಒಟ್ಟು ಗೂಡಿಸುವನು. ಇದರಲ್ಲಿ ಸಂದೇಹವೇ ಇಲ್ಲ. ಆದರೆ, ತಮ್ಮನ್ನು ತಾವೇ ನಷ್ಟಕ್ಕೊಡ್ಡಿದವರು ನಂಬುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(6,13,'6:13','ಇರುಳಲ್ಲೂ ಹಗಲಲ್ಲೂ ಅಡಗಿದ ಎಲ್ಲವೂ ಅಲ್ಲಾಹ ನದ್ದು . ಅವನು ಸರ್ವಶ್ರುತನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(6,14,'6:14','ನಾನು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಇನ್ನಾರನ್ನಾದರೂ ನನ್ನ ಸಂರಕ್ಷಕನಾಗಿ ಮಾಡಿಕೊಳ್ಳಬೇಕೆ? ಅವನಾ ದರೋ ಭೂಮಿ-ಆಕಾಶಗಳ ಕರ್ತೃ. ಅವನು ಉಣಿಸುವವನು, ಉಣಿಸಲ್ಪಡುವವನಲ್ಲ. ಹೇಳಿರಿ - ಮೊತ್ತ ಮೊದಲು ಅವನ ಮುಂದೆ ನಾನು ಶರಣಾಗತನಾಗಬೇಕೆಂದೂ ನಾನು ಬಹುದೇವಾ ರಾಧಕರ ಕೂಟಕ್ಕೆ ಸೇರಬಾರದೆಂದೂ ನನಗೆ ಆಜ್ಞಾಪಿಸಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(6,15,'6:15','ಹೇಳಿರಿ - ನಾನು ನನ್ನ ಪ್ರಭುವಿನ ಆಜ್ಞೋಲ್ಲಂ ಘನೆ ಮಾಡಿದರೆ ಒಂದು ಘೋರ ದಿನದ ಶಿಕ್ಷೆ ಯನ್ನು ಭಯಪಡುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(6,16,'6:16','ಅಂದು ಯಾವನ ಪಾಲಿಗೆ ಶಿಕ್ಷೆಯನ್ನು ತಡೆಯ ಲಾಗುವುದೋ ಅವನಿಗೆ ನಿಜಕ್ಕೂ ಅಲ್ಲಾಹು ದಯೆ ತೋರಿದನು . ಇದುವೇ ಪ್ರತ್ಯಕ್ಷವಾದ ವಿಜಯ ವಾಗಿರುತ್ತದೆ');
INSERT INTO "kannada" VALUES(6,17,'6:17','ಅಲ್ಲಾಹನು ನಿಮಗೆ ಯಾವುದೇ ವಿಪತ್ತ (ಕೆಡು ಕು)ನ್ನು ಮುಟ್ಟಿಸಿದರೆ, ಅದನ್ನು ನಿವಾರಿಸಲು ಅವನ ಹೊರತು ಇನ್ನಾರೂ ಇಲ್ಲ. ಅವನು ನಿಮಗೆ ಒಳಿತನ್ನುಂಟು ಮಾಡಿದರೆ, ಅವನು ಸಕಲ ವಸ್ತುಗಳ ಮೇಲೆ ಸರ್ವ ಸಮರ್ಥನು .');
INSERT INTO "kannada" VALUES(6,18,'6:18','ಅವನು ತನ್ನ ದಾಸರ ಮೇಲೆ ಸಂಪೂರ್ಣ ಅಧಿಕಾರವುಳ್ಳವನು. ಅವನು ಧೀಮಂತನೂ ಸೂಕ್ಷ್ಮಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(6,19,'6:19','ಕೇಳಿರಿ, ಅತ್ಯಂತ ಹಿರಿಯ ಸಾಕ್ಷಿ ಯಾವುದು? ಹೇಳಿರಿ, ನನ್ನ ಮತ್ತು ನಿಮ್ಮ ನಡುವೆ ಅಲ್ಲಾಹನು ಸಾಕ್ಷಿಯಾಗಿದ್ದಾನೆ. ಮತ್ತು ಖುರ್‍ಆನ್ ನಿಮಗೂ ಇನ್ನು ಇದು ಯಾರಿಗೆಲ್ಲ ತಲುಪುವುದೋ ಅವರೆಲ್ಲರಿಗೂ ಎಚ್ಚರಿಕೆ ನೀಡಲಿಕ್ಕಾಗಿ ನನ್ನ ಕಡೆಗೆ ದಿವ್ಯ ಸಂದೇಶವಾಗಿ ನೀಡಲಾಗಿದೆ ಎಂದು ಹೇಳಿರಿ. ಅಲ್ಲಾಹನೊಂದಿಗೆ ಇತರ ಆರಾಧ್ಯರೂ ಇರುವರೆಂದು ನಿಜಕ್ಕೂ ನೀವು ಸಾಕ್ಷ್ಯ ನೀಡ ಬಲ್ಲಿರಾ ? ಅವರಿಗೆ ಹೇಳಿರಿ, ನಾನು ಎಷ್ಟು ಮಾತ್ರ ಕ್ಕೂ ಅದಕ್ಕೆ ಸಾಕ್ಷ್ಯ ಹೇಳಲಾರೆ. ಹೇಳಿರಿ, ನಿಶ್ಚ ಯವಾಗಿಯೂ ಅವನು ಏಕಮಾತ್ರ ಆರಾಧ್ಯನು ಮತ್ತು ನೀವು ಅಲ್ಲಾಹನಿಗೆ ಪಾಲುಗಾರಿಕೆ ಮಾಡು ವುದರಿಂದ ನಾನು ತೀರಾ ಮುಕ್ತನಾ ಗಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(6,20,'6:20','ನಾವು ಯಾರಿಗೆ ಗ್ರಂಥ ನೀಡಿದ್ದೇವೆಯೋ ಅವರು ತಮ್ಮ ಮಕ್ಕಳನ್ನು ಅರಿಯುವಂತೆ ಪ್ರವಾದಿಯರನ್ನು ಅರಿತ್ತಿದ್ದಾರೆ . ಆದರೆ ಸ್ವಯಂನಷ್ಟ ಮಾಡಿಕೊಂಡವರು ಯಾವತ್ತೂ ವಿಶ್ವಾಸ ತಾಳಲಾರರು.');
INSERT INTO "kannada" VALUES(6,21,'6:21','ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಳ್ಳಾರೋಪ ಹೊರಿಸುವವನಿಗಿಂತ ಅಥವಾ ಅಲ್ಲಾಹನ ನಿದರ್ಶನಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವವನಿಗಿಂತ ದೊಡ್ಡ ಅನ್ಯಾಯಗಾರ ಯಾರಿದ್ದಾನೆ? ಅನ್ಯಾಯಗಾರರು ಖಂಡಿತ ಜಯಗಳಿಸರು.');
INSERT INTO "kannada" VALUES(6,22,'6:22','ನಾವು ಇವರನ್ನೆಲ್ಲ ಒಟ್ಟುಗೂಡಿಸುವ ಮತ್ತು ಮುಶ್ರಿಕರೊಡನೆ, ನೀವು ನಿಮ್ಮ ದೇವರೆಂದು ವಾದಿಸುತ್ತಿದ್ದ ನಿಮ್ಮ ಸಹಭಾಗಿಗಳು ಈಗ ಎಲ್ಲಿದ್ದಾರೆ? ಎಂದು ಪ್ರಶ್ನಿಸುವ ದಿನವನ್ನು ಸ್ಮರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(6,23,'6:23','ಅನಂತರ ಅವರಿಗೆ, `ಓ ನಮ್ಮ ಪ್ರಭೂ, ಅಲ್ಲಾಹ ನಾಣೆ, ನಾವು ಮುಶ್ರಿಕರಾಗಿರಲಿಲ್ಲ, ಎನ್ನುವ ಉಪಾಯವಲ್ಲದೆ ಇನ್ನೇನೂ ಇರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(6,24,'6:24','ನೋಡಿರಿ, ಅವರು ತಾವೇ ತಮ್ಮ ಮೇಲೆ ಹೇಗೆ ಸುಳ್ಳನ್ನು ಸೃಷ್ಟಿಸಿಕೊಂಡರು, ಅವರು ನಿರ್ಮಿಸಿದ ದೇವರುಗಳೆಲ್ಲ ಅವರನ್ನು ಬಿಟ್ಟು ಹೇಗೆ ಕಣ್ಮರೆ ಯಾದರು!');
INSERT INTO "kannada" VALUES(6,25,'6:25','ಅವರಲ್ಲಿ ಕೆಲವರು ನಿಮ್ಮ ಮಾತನ್ನು ಕಿವಿಗೊಟ್ಟು ಕೇಳುತ್ತಾರೆ. ಆದರೆ, ನಾವು ಅವರ ಹೃದಯಗಳ ಮೇಲೆ ಮರೆ ಹಾಕಿರುತ್ತೇವೆ. ಇದರಿಂದಾಗಿ ಅವರು ಅದನ್ನು ಸ್ವಲ್ಪವೂ ಗ್ರಹಿಸುವುದಿಲ್ಲ ಮತ್ತು ಅವರ ಕಿವಿಗಳಲ್ಲಿ ತಡೆ ಇಟ್ಟಿದ್ದೇವೆ. ಆದ್ದರಿಂದ ಅವರು ಎಲ್ಲ ನಿದರ್ಶನವನ್ನು ಕಂಡರೂ ಅದರ ಮೇಲೆ ವಿಶ್ವಾಸವಿಡಲಾರರು. ಆ ಸತ್ಯನಿಷೇಧಿಗಳು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದು ನಿಮ್ಮೊಡನೆ ತರ್ಕಿಸುತ್ತ ಇವೆಲ್ಲ ಪೂರ್ವಿಕರ ಕಾಡು ಹರಟೆಗಳೇ ವಿನಾ ಇನ್ನೇನೂ ಅಲ್ಲ ಎಂದು ಹೇಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(6,26,'6:26','ಅವರು ಅದರಿಂದ ಜನರನ್ನು ತಡೆಯುತ್ತಾರೆ ಸ್ವತಃ ತಾವೂ ಅದರಿಂದ ದೂರ ನಿಲ್ಲುತ್ತಾರೆ. ಈ ಮೂಲಕ ಅವರು ಸ್ವಂತವನ್ನಲ್ಲದೆ ಬೇರೇನನ್ನೂ ನಾಶ ಮಾಡುವುದಿಲ್ಲ. ಆದರೆ ಅವರಿಗೆ ಅದರ ಪ್ರಜ್ಞೆ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(6,27,'6:27','ಅವರು ನರಕಾಗ್ನಿಯ ಕಿನಾರೆಯಲ್ಲಿ ನಿಲ್ಲಿಸಲಾ ಗುವುದನ್ನು ನೀವು ಕಂಡರೆ, ಆಗ ಅವರು, ಭೂಲೋಕಕ್ಕೆ ನಮ್ಮನ್ನು ವಾಪಾಸು ಕಳಿಸಲಾದರೆ ನಾವು ನಮ್ಮ ಪ್ರಭುವಿನ ನಿದರ್ಶನಗಳನ್ನು ಸುಳ್ಳಾಗಿಸದೆ, ವಿಶ್ವಾಸವಿಟ್ಟವರ ಕೂಟದಲ್ಲಿ ಸೇರಬಹುದಿತ್ತು ಎಂದು ಹೇಳುವರು.');
INSERT INTO "kannada" VALUES(6,28,'6:28','ಹಾಗಲ್ಲ, ವಾಸ್ತವದಲ್ಲಿ ಅವರು ಮರೆಮಾಚಿದ್ದು ಆಗ ಅವರ ಮುಂದೆ ಪ್ರತ್ಯಕ್ಷವಾಯಿತೆಂದು ಮಾತ್ರ. ಅನ್ಯಥಾ ಅವರನ್ನು ಗತ ಜೀವನದ ಕಡೆಗೆ ಹಿಂದಿರುಗಿಸಿದರೆ ಅವರಿಗೆ ಯಾವುದನ್ನು ನಿಷೇಧಿಸಲಾಗಿತ್ತೋ ಅದಕ್ಕೇ ಮರಳುವರು. ನಿಜವಾಗಿಯೂ ಅವರು ಸುಳ್ಳರು.');
INSERT INTO "kannada" VALUES(6,29,'6:29','ನಮ್ಮ ಈ ಇಹಲೋಕದ ಜೀವನವಲ್ಲದೆ ಬೇರೇನೂ ಇಲ್ಲ. ನಾವು ಸತ್ತ ಮೇಲೆ ಪುನರ್ಜೀವಗೊ ಳಿಸಲಾಗುವುದಿಲ್ಲ ಎಂದು ಇವರು ಹೇಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(6,30,'6:30','ಇವರು ತಮ್ಮ ಪ್ರಭುವಿನ ಮುಂದೆ ನಿಲ್ಲಿಸ ಲಾಗುವುದನ್ನು ನೀವು ಕಂಡಿದ್ದರೆ! ಆಗ ಇವರ ಪ್ರಭು ಇವರೊಡನೆ, ‘ಇದು ಸತ್ಯವಲ್ಲವೇ?’ ಎಂದು ಕೇಳುವನು, ‘ಹೌದು ನಮ್ಮ ಪ್ರಭುವಿನಾಣೆ ಸತ್ಯ’ ಎಂದು ಇವರು ಹೇಳುವರು. ಅವನು, ಹಾಗಾದರೆ ನೀವು ಅವಿಶ್ವಾಸ ತಾಳಿದ ಕಾರಣಕ್ಕಾಗಿ ಯಾತನೆಯ ಸವಿಯನ್ನುಣ್ಣಿರಿ ಎನ್ನುವನು.');
INSERT INTO "kannada" VALUES(6,31,'6:31','ಅಲ್ಲಾಹನನ್ನು ಬೇಟಿಯಾಗಲಿಕ್ಕಿರುವುದನ್ನು ಸುಳ್ಳಾಗಿಸುವವರು ನಷ್ಟ ಹೊಂದಿದರು. ಆ ಘಳಿಗೆಯು ಹಠಾತ್ತನೆ ಬಂದು ಬಿಡುವಾಗ, ಅಯ್ಯೋ, ನಮ್ಮ ಕಷ್ಟವೇ, ಇಹದಲ್ಲಿ ನಾವು ಅಸಡ್ಡೆ ತೋರಿದೆವಲ್ಲಾ ಎಂದು ಗೋಳಿಡುವರು. ಇವರು ತಮ್ಮ ಬೆನ್ನ ಮೇಲೆ ಪಾಪಗಳ ಮೂಟೆಯನ್ನು ಹೊತ್ತುಕೊಳ್ಳುವರು . ತಿಳಿಯಿರಿ, ಎಷ್ಟು ನಿಕೃಷ್ಟ ಹೊರೆಯನ್ನು ಇವರು ಹೊತ್ತಿರುವರು!');
INSERT INTO "kannada" VALUES(6,32,'6:32','ಇಹಲೋಕದ ಜೀವನವು ಒಂದು ಆಟ ಮತ್ತು ವಿನೋದ ಮಾತ್ರವಾಗಿದೆ. ವಾಸ್ತವದಲ್ಲಿ ಧರ್ಮ ನಿಷ್ಟರಿಗೆ ಪರಲೋಕದ ನಿವಾಸವೇ ಶ್ರೇಷ್ಟ. ನೀವು ವಿಚಾರ ಮಾಡುವುದಿಲ್ಲವೇ? ');
INSERT INTO "kannada" VALUES(6,33,'6:33','(ಓ ಪೈಗಂಬರರೇ,) ಇವರು ಸೃಷ್ಟಿಸಿ ಹೇಳುತ್ತಿರುವ ಮಾತುಗಳಿಂದ ನಿಮಗೆ ತುಂಬ ನೋವಾಗಿದೆಯೆಂದು ನಮಗೆ ಗೊತ್ತಿದೆ. ಆದರೆ ಇವರು ನಿರಾಕರಿಸುತ್ತಿರುವುದು ನಿಮ್ಮನ್ನಲ್ಲ. ಅಕ್ರಮಿಗಳು ವಾಸ್ತವದಲ್ಲಿ ಅಲ್ಲಾಹನ ನಿದರ್ಶನಗಳನ್ನು ನಿರಾಕರಿಸುತ್ತಿದ್ದಾರೆ .');
INSERT INTO "kannada" VALUES(6,34,'6:34','ನಿಮಗಿಂತ ಹಿಂದಿನ ಅನೇಕ ಸಂದೇಶವಾಹಕರನ್ನು ಖಂಡಿತ ಸುಳ್ಳಾಗಿಸಲಾಗಿದೆ. ಆದರೆ ಅವರು ತಮ್ಮನ್ನು ಸುಳ್ಳಾಗಿಸಿದ್ದನ್ನೂ ಉಪದ್ರವಿಸಿದ್ದನ್ನೂ ಸಹಿಸಿಕೊಂಡರು. ಕೊನೆಗೆ ಅವರಿಗೆ ನಮ್ಮ ಸಹಾಯ ಒದಗಿತು. ಅಲ್ಲಾಹನ ವಚನಗಳನ್ನು ಬದಲಾಯಿಸುವವನು ಯಾರೂ ಇಲ್ಲ. (ಪ್ರವಾದಿಗಳ ವೃತ್ತಾಂತಗಳಿಂದ) ಮನ ತಣಿಸುವ ಕೆಲವು ವಿಚಾರಗಳು ಖಂಡಿತ ನಿಮಗೆ ತಲುಪಿರುತ್ತವೆ.');
INSERT INTO "kannada" VALUES(6,35,'6:35','ಇವರ ಅಶ್ರದ್ಧೆಯು ನಿಮಗೆ ಅಸಹನೀಯವಾಗಿ ದ್ದಲ್ಲಿ ಸಾಧ್ಯವಿದ್ದರೆ ನೆಲದಲ್ಲೊಂದು ಸುರಂಗವನ್ನು ತೋಡಿರಿ ಅಥವಾ ಆಕಾಶಕ್ಕೆ ಏಣಿಯಿಡಿರಿ ಮತ್ತು ಇವರ ಬಳಿಗೆ ದೃಷ್ಟಾಂತವೇನಾದರೂ ತಂದು ಕೊಡಲು ಪ್ರಯತ್ನಿಸಿರಿ . (ನಿಮಗದು ಸಾಧ್ಯವಿಲ್ಲ. ಆದ್ದರಿಂದ ಸಹನೆಯೇ ಸಂಗತ) ಅಲ್ಲಾಹು ಇಚ್ಚಿಸುತ್ತಿದ್ದರೆ ಇವರೆಲ್ಲರನ್ನೂ ಸನ್ಮಾರ್ಗದಲ್ಲಿ ಒಟ್ಟುಗೂಡಿಸಲು ಶಕ್ತನಾಗಿದ್ದನು. ಆದುದರಿಂದ ನೀವು ಖಂಡಿತ ಅಜ್ಞಾನಿಗಳ ಕೂಟಕ್ಕೆ ಸೇರಬಾರದು .');
INSERT INTO "kannada" VALUES(6,36,'6:36','ಮನಸಾರೆ ಕೇಳಿಸಿಕೊಳ್ಳುವವರು ಮಾತ್ರ ತಮ್ಮ ಕರೆಗೆ ಓಗೊಡುತ್ತಾರೆ. ಸತ್ತವರನ್ನು ಅಲ್ಲಾಹು ಸಮಾಧಿಗಳಿಂದ ಜೀವ ಕೊಟ್ಟು ಎಬ್ಬಿಸುವನು. ತರುವಾಯ ಆತನ ಕಡೆಗೆ ಅವರನ್ನು ಮರಳಿ ತರಲಾಗುವುದು .');
INSERT INTO "kannada" VALUES(6,37,'6:37','ಈ ಪ್ರವಾದಿಗೆ ಅವನ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಒಂದು ದೃಷ್ಟಾಂತವೇಕೆ ಇಳಿಸಲಾಗಿಲ್ಲ ಎಂದು ಇವರು ಕೇಳುತ್ತಾರೆ. ಹೇಳಿರಿ, ದೃಷ್ಟಾಂತವನ್ನಿಳಿಸಲು ಅಲ್ಲಾಹನು ಸಂಪೂರ್ಣ ಶಕ್ತನಾಗಿದ್ದಾನೆ. ಆದರೆ ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ತಿಳಿಗೇಡಿಗಳಾಗಿದ್ದಾರೆ .');
INSERT INTO "kannada" VALUES(6,38,'6:38','ಭೂಮಿಯ ಮೇಲೆ ಚಲಿಸುವ ಪ್ರಾಣಿಗಳೂ, ಎರಡು ರೆಕ್ಕೆಗಳಲ್ಲಿ ಹಾರುವ ಪಕ್ಷಿಗಳೂ ಖಂಡಿತ ನಿಮ್ಮಂತೆಯೇ ಇರುವ ವರ್ಗಗಳು. (ಕರ್ಮ) ಗ್ರಂಥದಲ್ಲಿ ಯಾವುದೇ ಲೋಪವನ್ನು ನಾವು ಮಾಡಿಲ್ಲ. ಅನಂತರ ಇವೆಲ್ಲ ತಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಗೆ ಒಟ್ಟುಗೂಡಿಸಲ್ಪಡುತ್ತವೆ.');
INSERT INTO "kannada" VALUES(6,39,'6:39','ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವವರು ಕಿವುಡರೂ ಮೂಗರೂ ಆಗಿರುತ್ತಾರೆ. ಅವರು ಅಂಧಕಾರಗಳಲ್ಲಿ ಬಿದ್ದಿರುತ್ತಾರೆ. ಅಲ್ಲಾಹನು ತಾನುದ್ದೇಶಿಸಿದವರನ್ನು ದಾರಿ ತಪ್ಪಿಸುತ್ತಾನೆ ಮತ್ತು ತನಗಿಷ್ಟ ಬಂದವರನ್ನು ಸರಿ ದಾರಿಗೆ ತರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(6,40,'6:40','ಹೇಳಿರಿ - ನಿಮ್ಮ ಮೇಲೆ ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆ ಇಳಿದು ಬಂದರೆ ಅಥವಾ ನಿಮ್ಮ ಕೊನೆಯ ಘಳಿಗೆಯು ಬಂದರೆ ನೀವು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಇತರರನ್ನು ಕರೆಯುತ್ತೀರಾ? ನೀವು ಸತ್ಯವಾದಿಗಳಾಗಿದ್ದರೆ (ನನಗೆ ಹೇಳಿರಿ.)');
INSERT INTO "kannada" VALUES(6,41,'6:41','ಇಲ್ಲ, ಆಗ ನೀವು ಅಲ್ಲಾಹನನ್ನೇ ಪ್ರಾರ್ಥಿಸುವಿರಿ. ಅನಂತರ ಅವನಿಚ್ಚಿಸಿದರೆ ಅದನ್ನು ನಿಮ್ಮ ಮೇಲಿನಿಂದ ನಿವಾರಿಸುತ್ತಾನೆ. ನೀವು ಅಲ್ಲಾಹನಿಗೆ ಸ್ವಯಂ ಕಲ್ಪಿಸಿಕೊಂಡಿರುವ ಭಾಗೀದಾರರನ್ನು ಆ ವೇಳೆ ಮರೆತು ಬಿಡುತ್ತೀರಿ .');
INSERT INTO "kannada" VALUES(6,42,'6:42','ನಿಮಗಿಂತ ಮೊದಲು ಅನೇಕ ಜನಾಂಗಗಳಿಗೆ ನಾವು ದೂತರನ್ನು ಕಳುಹಿಸಿದ್ದೇವೆ. ಆ ಜನಾಂಗಗಳು ವಿನಯದಿಂದ ನಮ್ಮ ಮುಂದೆ ಬಾಗುವಂತೆ ಸಂಕಷ್ಟಗಳನ್ನೂ, ವಿಪತ್ತುಗಳನ್ನೂ ಕೊಟ್ಟು ಅವರನ್ನು ಶಿಕ್ಷಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(6,43,'6:43','ನಮ್ಮ ಕಡೆಯಿಂದ ಅವರ ಮೇಲೆ ಶಿಕ್ಷೆ ತಟ್ಟಿದರೂ ಅವರೇಕೆ ವಿನಯ ತೋರಲಿಲ್ಲ? ಆದರೆ ಅವರ ಹೃದಯಗಳು ಕಠಿಣವಾದುವು ಮತ್ತು ಅವರು ಮಾಡುತ್ತಿರುವ ಪಾಪಕರ್ಮಗಳನ್ನು ಶೈತಾನನು ಅವರಿಗೆ ಚಂದ ಕಾಣಿಸಿದನು.');
INSERT INTO "kannada" VALUES(6,44,'6:44','ಅನಂತರ ಅವರು, ತಮಗೆ ನೆನಪಿಸಲಾದ ನೀತಿ ಪಾಠವನ್ನು ಮರೆತು ಬಿಟ್ಟಾಗ ನಾವು ಅವರಿಗಾಗಿ ಸರ್ವ ಶ್ರೇಯಸ್ಸುಗಳ ಬಾಗಿಲುಗಳನ್ನು ತೆರೆದು ಬಿಟ್ಟೆವು. ಅವರಿಗೆ ದಯಪಾಲಿಸಲ್ಪಟ್ಟ ಸುಖ ಸವಲತ್ತುಗಳಲ್ಲಿ ಅವರು ಚೆನ್ನಾಗಿ ಮಗ್ನರಾಗಿದ್ದಾಗ (ಹರ್ಷಾತಿರೇಕದಲ್ಲಿ ತಲ್ಲೀನರಾಗಿದ್ದಾಗ) ನಾವು ಹಠಾತ್ತನೆ ಅವರನ್ನು ಹಿಡಿದುಕೊಂಡೆವು . ಆಗ ಅವರು ಯಾವುದೇ ಒಳಿತಿನ ಬಗ್ಗೆ ನಿರಾಶರಾದರು.');
INSERT INTO "kannada" VALUES(6,45,'6:45','ಹೀಗೆ ಅಕ್ರಮವೆಸಗಿದವರ ಮೂಲವೇ ಕಡಿದು ಹಾಕಲ್ಪಟ್ಟಿತು. ಸರ್ವ ಸ್ತುತಿಯು ಸರ್ವಲೋಕಗಳ ಪಾಲಕ ಪ್ರಭುವಾದ ಅಲ್ಲಾಹನಿಗೆ ,');
INSERT INTO "kannada" VALUES(6,46,'6:46','ಓ ಪೈಗಂಬರರೇ, ಕೇಳಿರಿ. ಅಲ್ಲಾಹನು ನಿಮ್ಮ ದೃಷ್ಟಿಯನ್ನೂ ಶ್ರವಣವನ್ನೂ ನಿಮ್ಮಿಂದ ಕಸಿದು ಕೊಂಡು ನಿಮ್ಮ ಹೃದಯಗಳಿಗೆ ಮೊಹರು ಹಾಕಿದರೆ ಅವುಗಳನ್ನು ನಿಮಗೆ ಮರಳಿ ಕೊಡಿಸಲು ಅಲ್ಲಾಹನ ಹೊರತು ಇನ್ನಾವ ದೇವನಿದ್ದಾನೆ? ನಾವು ನಮ್ಮ ನಿದರ್ಶನಗಳನ್ನು ಹೇಗೆ ವಿವರಿಸುತ್ತೇವೆ ಮತ್ತು ಇವರು ಅವುಗಳನ್ನು ನಂಬದೆ ಹೇಗೆ ವಿಮುಖರಾಗುತ್ತಾರೆಂಬುದನ್ನು ನೋಡಿರಿ.');
INSERT INTO "kannada" VALUES(6,47,'6:47','ಹೇಳಿರಿ - ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆ ನಿಮ್ಮ ಮೇಲೆ ಅಕಸ್ಮಾತ್ ಅಥವಾ ಪ್ರತ್ಯಕ್ಷವಾಗಿ ಬಂದೆರಗಿದರೆ ಅಕ್ರಮಿಗಳಾದ ಜನಾಂಗವನ್ನು ಹೊರತುಪಡಿಸಿ ಇನ್ನಾರಾದರೂ ನಾಶ ಹೊಂದುವರೇ ಎಂದು ಯೋಚಿಸಿದಿರಾ?');
INSERT INTO "kannada" VALUES(6,48,'6:48','ನಾವು ಎಲ್ಲ ಪ್ರವಾದಿಗಳನ್ನು ಸಜ್ಜನರಿಗೆ ಸುವಾರ್ತೆ ನೀಡುವವರೂ ದುರ್ಜನರಿಗೆ ಎಚ್ಚರಿಕೆ ಕೊಡುವವರೂ ಆಗಿ ಮಾತ್ರ ಕಳುಹಿಸುತ್ತೇವೆ. ಆ ಬಳಿಕ ಯಾರು ಅವರನ್ನು ನಂಬುತ್ತಾರೆ ಮತ್ತು ತಮ್ಮ ಕರ್ಮವನ್ನು ಉತ್ತಮಗೊಳಿಸುತ್ತಾರೆ, ಅವರಿಗೆ ಯಾವ ಭಯವೂ ಇಲ್ಲ. ಅವರು ದುಃಖಿಸುವವರೂ ಅಲ್ಲ .');
INSERT INTO "kannada" VALUES(6,49,'6:49','ನಮ್ಮ ನಿದರ್ಶನಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವವರು ತಮ್ಮ ದುಷ್ಕರ್ಮಗಳ ಫಲವಾಗಿ ಶಿಕ್ಷೆ ಅನುಭವಿಸಿಯೇ ತೀರುವರು.');
INSERT INTO "kannada" VALUES(6,50,'6:50','(ಓ ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ `ನನ್ನಲ್ಲಿ ಅಲ್ಲಾಹನ ಖಜಾನೆಗಳಿವೆಯೆಂದು ನಾನು ನಿಮ್ಮೊಡನೆ ಹೇಳುವುದಿಲ್ಲ. ನಾನು ಪರೋಕ್ಷ ಜ್ಞಾನಿಯೂ ಅಲ್ಲ . ನಾನು ದೇವಚರನೆಂದೂ ಹೇಳುವುದಿಲ್ಲ. ನನ್ನ ಮೇಲೆ ಅವತೀರ್ಣಗೊಳ್ಳುತ್ತಿರುವ ದಿವ್ಯ ವಾಣಿಯ ಹೊರತು ಇನ್ನೇನನ್ನೂ ನಾನು ಅನುಸರಿಸುವುದಿಲ್ಲ. ಕೇಳಿರಿ; ಕುರುಡನೂ ದೃಷ್ಟಿಯುಳ್ಳವನೂ ಸಮಾನರಾಗುವರೇ? ನೀವು ಯೋಚಿಸು ವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(6,51,'6:51','(ಓ ಪೈಗಂಬರರೇ,) ಅಲ್ಲಾಹನ ಹೊರತು ಯಾವುದೇ ಸಹಾಯಕರು ಅಥವಾ ಶಿಫಾರಸು ದಾರರು ಇಲ್ಲದಂತಹ ಪರಿಸ್ಥಿತಿಯಲ್ಲಿ ಅವರನ್ನು ಅವರ ಪ್ರಭುವಿನ ಬಳಿ ಒಟ್ಟು ಸೇರಿಸ ಲ್ಪಡುವುದನ್ನು ಭಯಪಡುವವರಿಗೆ ನೀವು ಇದರ ಮೂಲಕ ಉಪದೇಶ ನೀಡಿರಿ. ಅವರು ಜಾಗ್ರತೆವಹಿಸಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(6,52,'6:52','ಯಾರು ತಮ್ಮ ಪ್ರಭುವಿನ ಒಲವನ್ನು ಬಯಸಿ ಸಂಜೆ ಮುಂಜಾನೆಗಳಲ್ಲಿ ಆತನನ್ನು ಆರಾಧಿಸು ತ್ತಾರೋ ಅವರನ್ನು ನಿಮ್ಮಿಂದ ದೂರಕ್ಕೆ ಅಟ್ಟಬೇ ಡಿರಿ. ಅವರ ವಿಚಾರಣೆಯ ಯಾವ ಹೊಣೆಯೂ ನಿಮಗಿಲ್ಲ. ತಮ್ಮ ವಿಚಾರಣೆಯ ಹೊಣೆ ಅವರ ಮೇಲೂ ಇಲ್ಲ. ಹೀಗಿದ್ದರೂ ನೀವು ಅವರನ್ನು ದೂರಕ್ಕೆ ಅಟ್ಟಿದರೆ ನೀವು ಅಕ್ರಮಿಗಳ ಕೂಟಕ್ಕೆ ಸೇರುವಿರಿ.');
INSERT INTO "kannada" VALUES(6,53,'6:53','ನಾವು ಇದೇ ಪ್ರಕಾರ ಅವರ ಪೈಕಿ ಕೆಲವರನ್ನು ಕೆಲವರ ಮೂಲಕ ಪರೀಕ್ಷೆಗೊಳಪಡಿಸಿದ್ದೇವೆ . ಇದು ನಮ್ಮ ಪೈಕಿ ಅಲ್ಲಾಹು ಅನುಗ್ರಹಿಸಿದ್ದು ಇವರ ಮೇಲೆಯೇ? ಎಂದು ಸರದಾರರು ಕೇಳುವಂತಾಗಲಿಕ್ಕಾಗಿ. ಹೌದು, ಅಲ್ಲಾಹನು ಕೃತಜ್ಞತೆಯುಳ್ಳವರನ್ನು ಚೆನ್ನಾಗಿ ಅರಿತವನಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(6,54,'6:54','ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳ ಮೇಲೆ ವಿಶ್ವಾಸವಿಡುವವರು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದಾಗ ನೀವು ಹೇಳಿರಿ- ನಿಮ್ಮ ಮೇಲೆ ಶಾಂತಿ ಇರಲಿ. ನಿಮ್ಮ ಪ್ರಭು ಕಾರು ಣ್ಯವನ್ನು ತನ್ನ ಮೇಲೆ (ಬಾದ್ಯತೆಯನ್ನಾಗಿ) ವಿಧಿ ಸಿಕೊಂಡಿದ್ದಾನೆ. ಅಂದರೆ ನಿಮ್ಮಲ್ಲಾರಾದರೂ ತಿಳಿಗೇಡಿತನದಿಂದ ಏನಾದರೂ ತಪ್ಪು ಮಾಡಿ, ಆ ಬಳಿಕ ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟರೆ ಮತ್ತು ತನ್ನನ್ನು ಸುಧಾರಿಸಿ ಕೊಂಡರೆ, ಅವನು ಪರಮ ಕ್ಷಮಾ ದಾನಿಯೂ, ಪರಮದಯಾಳುವೂ ಆಗಿರುವನು .');
INSERT INTO "kannada" VALUES(6,55,'6:55','ಈ ರೀತಿ ಅಪರಾಧಿಗಳ ಮಾರ್ಗವು ಸ್ಪಷ್ಟವಾಗಲಿ ಕ್ಕಾಗಿ ನಮ್ಮ ಪುರಾವೆಗಳನ್ನು ನಾವು ವಿಶದೀಕರಿ ಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(6,56,'6:56','ಓ ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ - ನೀವು ಅಲ್ಲಾಹನ ಹೊರತು ಇತರ ಯಾರನ್ನು ಆರಾಧಿಸುತ್ತಿರು ವಿರೋ ಅವರ ಆರಾಧನೆ ಮಾಡುವುದನ್ನು ನನಗೆ ನಿಷೇಧಿಸಲಾಗಿದೆ. ಹೇಳಿರಿ _ ನಾನು ನಿಮ್ಮ ತನ್ನಿಚ್ಛೆಗಳ ಅನುಸರಣೆ ಮಾಡಲಾರೆನು. ನಾನು ಹಾಗೆ ಮಾಡಿದರೆ ದಾರಿಗೆಡುವೆನು, ಹಾಗೂ ಸತ್ಪಥಿಕರ ಕೂಟಕ್ಕೆ ಸೇರಲಾರೆನು.');
INSERT INTO "kannada" VALUES(6,57,'6:57','ಹೇಳಿರಿ - ನಾನು ನನ್ನ ಪ್ರಭುವಿನ ವತಿಯಿಂದ ಒಂದು ಪ್ರತ್ಯಕ್ಷ ಪ್ರಮಾಣದ ಮೇಲಿರುತ್ತೇನೆ. ಆದರೆ ನೀವು ಅದನ್ನು ಸುಳ್ಳಾಗಿಸಿ ಬಿಟ್ಟಿರುವಿರಿ. ನೀವು ತವಕಪಡುತ್ತಿರುವ ಕಾರ್ಯ (ಶಿಕ್ಷೆ) ನನ್ನ ಬಳಿ ಇಲ್ಲ. (ಅದರ) ತೀರ್ಮಾನದ ಅಧಿಕಾರ ಅಲ್ಲಾಹನಿಗೆ ಮಾತ್ರವಿದೆ. ಅವನು ಸತ್ಯವನ್ನು ವಿವರಿಸುತ್ತಾನೆ. ಅವನು ಸರ್ವ ಶ್ರೇಷ್ಠ ತೀಪು ಗಾರನು.');
INSERT INTO "kannada" VALUES(6,58,'6:58','ಹೇಳಿರಿ-ನೀವು ತವಕಪಡುತ್ತಿರುವ ವಸ್ತು ನನ್ನ ಬಳಿ ಇರುತ್ತಿದ್ದರೆ ನನ್ನ ಮತ್ತು ನಿಮ್ಮ ನಡುವೆ ಈಗಾಗಲೇ ತೀರ್ಮಾವಾಗಿ ಬಿಡುತ್ತಿತ್ತು. ಅಕ್ರಮಿಗಳ ಬಗ್ಗೆ ಅಲ್ಲಾಹನು ಚೆನ್ನಾಗಿ ಅರಿತಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(6,59,'6:59','ಪರೋಕ್ಷದ ಕೀಲಿಗಳು ಅವನ ಬಳಿಯಲ್ಲೇ ಇವೆ. ಅವುಗಳನ್ನು ಅವನ ಹೊರತು ಇನ್ನಾರೂ ಅರಿಯರು. ನೆಲ-ಜಲದಲ್ಲಿರುವ ಎಲ್ಲವನ್ನೂ ಅವನು ತಿಳಿಯುತ್ತಾನೆ. ಅವನರಿಯದೆ ಒಂದು ಎಲೆ ಕೂಡಾ ಉದುರಿ ಬೀಳದು. ಭೂಮಿಯ ಅಂಧಕಾರಗಳಲ್ಲಿ ಹುದುಗಿರುವ ಒಂದು ಧಾನ್ಯವಿರಲಿ, ಹಸಿಯೋ, ಒಣವೋ ಆದ ಯಾವುದೇ ವಸ್ತುವಿರಲಿ, ಒಂದು ತೆರೆದ ಗ್ರಂಥ ದಲ್ಲಿ ಲಿಖಿತಗೊಳ್ಳದೇ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(6,60,'6:60','ಅವನೇ ರಾತ್ರಿ ಕಾಲದಲ್ಲಿ (ನಿದ್ರಿಸುವಾಗ) ನಿಮ್ಮ ಪ್ರಾಣಹರಣ ಮಾಡುತ್ತಾನೆ ಮತ್ತು ಹಗಲಲ್ಲಿ ನೀವು ಮಾಡುತ್ತಿರುವುದನ್ನೆಲ್ಲ ಅವನು ಅರಿಯುತ್ತಾನೆ. ಅನಂತರ ನಿಶ್ಚಿತ ಜೀವನಾವಧಿಯು ಪೂರ್ಣ ಗೊಳ್ಳಲಿಕ್ಕಾಗಿ ನಿಮ್ಮನ್ನು ನಿದ್ದೆಯಿಂದ ಎಬ್ಬಿಸು ತ್ತಾನೆ. ಕೊನೆಗೆ ನಿಮ್ಮ ಮರಳುವಿಕೆಯು ಆತನ ಕಡೆಗೇ ಇದೆ. ಅನಂತರ, ನೀವು ಮಾಡುತ್ತಿದ್ದ ಕರ್ಮಗಳ ಬಗ್ಗೆ ಅವನು ನಿಮಗೆ ತಿಳಿಸುವನು.');
INSERT INTO "kannada" VALUES(6,61,'6:61','ಅವನು ತನ್ನ ದಾಸರ ಮೇಲೆ ಪರಮಾಧಿಕಾರ ವುಳ್ಳವನಾಗಿದ್ದಾನೆ ಮತ್ತು ನಿಮ್ಮ ಮೇಲೆ ಮೇ ಲ್ನೋಟ ವಿರಿಸಲು ಕಾವಲುಗಾರ (ದೇವಚರ) ರನ್ನು ನೇಮಕ ಮಾಡಿ ಕಳುಹಿಸುತ್ತಾನೆ. ಹಾಗೆ ನಿಮ್ಮಲ್ಲಿ ಯಾರಿಗಾದರೂ ಮರಣ ಬಂದರೆ, ನಮ್ಮ ದೂತರು (ದೇವಚರರು) ಅವನನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಮೃತ್ಯುಗೊಳಿಸುತ್ತಾರೆ. ಅವರು ಸ್ವಲ್ಪವೂ ಕರ್ತವ್ಯ ಲೋಪ ಮಾಡುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(6,62,'6:62','ತರುವಾಯ ಸರ್ವರನ್ನು ತಮ್ಮ ನೈಜ ಯಜ ಮಾನನಾದ ಅಲ್ಲಾಹನ ಕಡೆಗೆ ಹಿಂದಿರುಗಿ ಸಲಾಗುತ್ತದೆ. ತಿಳಿಯಿರಿ. ತೀರ್ಪಿನ ಅಧಿಕಾರವು ಅವನಿಗೆ ಮಾತ್ರವಿದೆ. ಅವನು ಲೆಕ್ಕ ಪರಿಶೋ ಧಕರಲ್ಲಿ ಅತಿ ಶೀಘ್ರನು.');
INSERT INTO "kannada" VALUES(6,63,'6:63','(ಓ ಪೈಗಂಬರರೇ,) ಕೇಳಿರಿ - ಈ ದುರಂ ತದಿಂದ ಅಲ್ಲಾಹು ನಮ್ಮನ್ನು ಪಾರು ಮಾಡಿಬಿಟ್ಟರೆ ಖಂಡಿತ ನಾವು ಕೃತಜ್ಞರ ಕೂಟಕ್ಕೆ ಸೇರುತ್ತೇವೆ ಎಂದು ನೀವು ಅವನಲ್ಲಿ ದೈನ್ಯತೆಯಿಂದಲೂ ರಹಸ್ಯವಾಗಿಯೂ ಪ್ರಾರ್ಥಿಸುವ ಸಮಯದಲ್ಲಿ ನಿಮ್ಮನ್ನು ನೆಲ-ಜಲಗಳ ಅಂಧಕಾರಗಳಿಂದ ರಕ್ಷಿಸುವವನು ಯಾರು?');
INSERT INTO "kannada" VALUES(6,64,'6:64','ಹೇಳಿರಿ - ಅಲ್ಲಾಹನು, ನಿಮ್ಮನ್ನು ಇದರಿಂದಲೂ ಸಕಲ ವಿಪತ್ತುಗಳಿಂದಲೂ ಪಾರು ಮಾಡುತ್ತಾನೆ. ಇಷ್ಟಾಗಿಯೂ ನೀವು ಇತರರನ್ನು ಅವನ ಭಾಗೀದಾರರನ್ನಾಗಿ ಮಾಡುತ್ತೀರಿ .');
INSERT INTO "kannada" VALUES(6,65,'6:65','ಹೇಳಿರಿ - ನಿಮ್ಮ ಮೇಲ್ಭಾಗದಿಂದ ಅಥವಾ ನಿಮ್ಮ ಪಾದಗಳ ಕೆಳಗಿಂದ ನಿಮ್ಮ ಮೇಲೆ ಶಿಕ್ಷೆ ಎರಗಿಸ ಲಿಕ್ಕೂ ಅಥವಾ ನಿಮ್ಮನ್ನು ಪಂಗಡಗಳನ್ನಾಗಿ ವಿಭಾಗಿಸಿ, ಆಶಯ ಗೊಂದಲವನ್ನುಂಟು ಮಾಡಲಿಕ್ಕೂ ಒಂದು ಪಂಗಡಕ್ಕೆ ಇನ್ನೊಂದು ಪಂಗಡದ ಆಕ್ರಮಣದ ರುಚಿಯನ್ನು ಉಣಿಸಲಿಕ್ಕೂ ಅವನು ಶಕ್ತನಾಗಿದ್ದಾನೆ. ಇವರು ಗ್ರಹಿಸಿ ಕೊಳ್ಳಲೆಂದು ಯಾವ ರೀತಿಯಲ್ಲಿ ನಾವು ನಮ್ಮ ರುಜುವಾತುಗಳನ್ನು ಅವರಿಗೆ ನಿರೂಪಿಸುತ್ತೇವೆ ಎಂಬುದನ್ನು ನೋಡಿರಿ.');
INSERT INTO "kannada" VALUES(6,66,'6:66','(ಪ್ರವಾದಿಯರೇ) ನಿಮ್ಮ ಜನಾಂಗವು ಇದು ಪರಮ ಸತ್ಯವಾಗಿರುತ್ತಲೇ ಇದನ್ನು ನಿಷೇ ಧಿಸಿಬಿಟ್ಟಿದೆ. ಹೇಳಿರಿ; ನಾನು ನಿಮ್ಮ ಮೇಲೆ ಜವಾಬ್ದಾರಿ ಹೊತ್ತವನಲ್ಲ .');
INSERT INTO "kannada" VALUES(6,67,'6:67','ಪ್ರತಿಯೊಂದು ವೃತ್ತಾಂತವೂ ನಿಜವಾಗಿ ಪರಿಣಮಿಸುವ ಒಂದು ಕಾಲವಿದೆ. ಸದ್ಯದಲ್ಲೇ ನಿಮಗೆ ಅದು ತಿಳಿದು ಬರುವುದು .');
INSERT INTO "kannada" VALUES(6,68,'6:68','(ಓ ಪೈಗಂಬರರೇ,) ನಮ್ಮ ವಚನಗಳಲ್ಲಿ ಅಕ್ರಮ ವಾಗಿ ಮುಳುಗೇಳುವವರನ್ನು ನೀವು ಕಂಡರೆ, ಅವರು ಅದನ್ನು ಬಿಟ್ಟು ಬೇರೆ ಮಾತುಕತೆಗಳಲ್ಲಿ ತೊಡಗುವವರೆಗೂ ಅವರ ಬಳಿಯಿಂದ ದೂರಸರಿಯಿರಿ. ಒಂದು ವೇಳೆ ಶೈತಾನನು ನಿಮ್ಮನ್ನು ಮರವೆಗೆ ಒಳಪಡಿಸಿದರೆ ನೆನಪಾದ ಬಳಿಕ ಅಕ್ರಮಿಗಳಾದ ಆ ಜನರ ಜೊತೆಗೆ ಕುಳಿತು ಕೊಳ್ಳಬೇಡಿರಿ .');
INSERT INTO "kannada" VALUES(6,69,'6:69','ಅವರ (ಅಕ್ರಮಿಗಳ) ಲೆಕ್ಕ ನೋಡುವ ಯಾವುದೇ ಹೊಣೆಗಾರಿಕೆ ಧರ್ಮನಿಷ್ಟರ ಮೇಲಿಲ್ಲ. ಆದರೆ ಅವರು ಭಕ್ತಿ ಶ್ರದ್ಧೆಯಿಂದ ಧರ್ಮನಿಷ್ಟರಾಗಿ ಬಾಳ ಲಿಕ್ಕಾಗಿ ಅವರಿಗೆ ನೆನಪಿಸಿಕೊಡುವ ಕರ್ತವ್ಯವಿದೆ.');
INSERT INTO "kannada" VALUES(6,70,'6:70','ತಮ್ಮ ಧರ್ಮವನ್ನು ಆಟ-ವಿನೋದವನ್ನಾಗಿ ಮಾಡಿಕೊಂಡವರು ಮತ್ತು ಇಹಜೀವನದಿಂದ ಮೋಸ ಹೋದವರನ್ನೂ ಬಿಟ್ಟುಬಿಡಿರಿ. ಆದರೆ ಪ್ರತಿಯೊಬ್ಬ ದೇಹಿಯೂ ತನ್ನ ಸ್ವಂತ ಕರ್ಮದ ಫಲವಾಗಿ ನಾಶಕ್ಕೆ ಸಿಲುಕದಂತೆ ಈ ಖುರ್‍ಆನ್ ಮೂಲಕ ಬೋಧನೆ ನೀಡಿರಿ. ಅಲ್ಲಾಹನ ಹೊರತು ಆ ದೇಹಿಗೆ ಯಾವ ರಕ್ಷಕಮಿತ್ರನೂ ಶಿಫಾರಸು ದಾರನೂ ಇರಲಾರನು. ಅವನು ಪರಿಹಾರವಾಗಿ ಸರ್ವ ದಂಡವನ್ನು ತೆತ್ತರೂ ಅದನ್ನೂ ಅವನಿಂದ ಸ್ವೀಕರಿಸಲಾಗದು. ಏಕೆಂದರೆ ಇಂಥವರು ತಮ್ಮ ಸ್ವಂತ ಗಳಿಕೆಯ ಫಲವಾಗಿ ನಾಶಕ್ಕೆ ತಳ್ಳಲ್ಪಡುವರು. ಅವರಿಗೆ ಅವರ ಸತ್ಯನಿಷೇಧದ ಪ್ರತಿಫಲವಾಗಿ ಕುದಿಯುತ್ತಿರುವ ಪಾನಕ ಮತ್ತು ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆ ಕಾದಿದೆ .');
INSERT INTO "kannada" VALUES(6,71,'6:71','(ಓ ಪೈಗಂಬರರೇ,) ಕೇಳಿರಿ: ನಾವು ಅಲ್ಲಾಹನ ನ್ನು ಬಿಟ್ಟು, ನಮಗೆ ಯಾವುದೇ ಉಪಕಾರವನ್ನಾ ಗಲಿ, ಹಾನಿಯನ್ನಾಗಲಿ ಉಂಟು ಮಾಡಲಾಗದ ವಸ್ತುಗಳಿಗೆ ಆರಾಧಿಸಬೇಕೆ? ಅಲ್ಲಾಹನು ನಮ್ಮನ್ನು ಸರಿದಾರಿಗೆ ಸೇರಿಸಿದ ಬಳಿಕ ನಾವೀಗ (ಬಹು ದೇವಾರಾಧಕರಾಗಿ) ಹಿಂದಕ್ಕೆ ಮರಳಿ ಬರಬೇಕೆ? ಶೈತಾನರಿಂದ ಭೂಮಿಯಲ್ಲಿ ತಳ್ಳಿ ಬೀಳಿಸಲ್ಪಟ್ಟು ಹೈರಾಣಾಗಿದ್ದಾಗ ‘ಇತ್ತ ಬಾ’ ಎಂದು ಸರಿ ದಾರಿಗೆ ಮಿತ್ರರು ಕರೆದಾಗಲೂ ಬರಲಾಗದವನಂತೆ! (ನಾವೂ ಆಗಬೇಕೆ ?) . ಹೇಳಿರಿ-ನಿಜಕ್ಕೂ ಕೇವಲ ಅಲ್ಲಾಹನ ಮಾರ್ಗ ದರ್ಶನವೇ ನಿಜವಾದ ಮಾರ್ಗದರ್ಶನ. ವಿಶ್ವದೊ ಡೆಯನಿಗೆ ವಿಧೇಯ ರಾಗಬೇಕೆಂದು ನಮಗೆ ಆಜ್ಞಾಪಿಸಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(6,72,'6:72','ನಮಾಝನ್ನು ಸಂಸ್ಥಾಪಿಸಿರಿ ಮತ್ತು ಅವನನ್ನು ಭಯಪಟ್ಟು ನಿಷ್ಠರಾಗಿ ಬಾಳಿರಿ (ಎಂದೂ ಆಜ್ಞಾ ಪಿಸಲಾಗಿದೆ) ನೀವು ಅವನ ಕಡೆಗೇ ಒಟ್ಟು ಗೂಡಿಸಲ್ಪಡುವಿರಿ.');
INSERT INTO "kannada" VALUES(6,73,'6:73','ಅವನೇ ಆಕಾಶ-ಭೂಮಿಗಳನ್ನು ಸತ್ಯಪೂರ್ಣ ವಾಗಿ ಸೃಷ್ಟಿಸಿದವನು. ಅವನು ‘ಉಂಟಾಗು’ ಎನ್ನುವ ದಿನ ಅದು ಉಂಟಾಗುವುದು. ಅವನ ವಚನವು ಪರಮ ಸತ್ಯ . ಕಹಳೆಯಲ್ಲಿ ಊದಲಾಗುವ ದಿನ ಆದಿಪತ್ಯ ಅವನದ್ದೇ ಆಗಿರುವುದು. ಅವನು ಪರೋಕ್ಷ ಮತ್ತು ಪ್ರತ್ಯಕ್ಷ ಜ್ಞಾನಿ, ಅವನು ಧೀಮಂತ ಮತ್ತು ಸೂಕ್ಷ್ಮಜ್ಞನು.');
INSERT INTO "kannada" VALUES(6,74,'6:74','ಇಬ್‍ರಾಹೀಮರು, ತನ್ನ ತಂದೆ ಆಝರನೊಡನೆ, ಹೇಳಿದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ; ನೀವು ವಿಗ್ರಹಗಳನ್ನು ದೇವರನ್ನಾಗಿ ಮಾಡುತ್ತೀರಾ? ನಿಮ್ಮನ್ನೂ ನಿಮ್ಮ ಜನಾಂಗವನ್ನೂ ನಾನು ವ್ಯಕ್ತ ವಾದದಾರಿ ಗೇಡಿನಲ್ಲಿ ಕಾಣುತ್ತಿರುವೆನು.');
INSERT INTO "kannada" VALUES(6,75,'6:75','ಇದೇ ಪ್ರಕಾರ ನಾವು ಇಬ್‍ರಾಹೀಮರಿಗೆ ಭೂಮಿ-ಆಕಾಶಗಳ ಆಧಿಪತ್ಯವನ್ನು ತೋರಿಸಿಕೊಟ್ಟೆವು. ಅವರು ದೃಢವಿಶ್ವಾಸಿಗಳಲ್ಲಿ ಸೇರಬೇಕೆಂದು .');
INSERT INTO "kannada" VALUES(6,76,'6:76','ಅವರ ಮೇಲೆ ರಾತ್ರಿ ಆವರಿಸಿದಾಗ ಅವರೊಂದು ನಕ್ಷತ್ರವನ್ನು ಕಂಡರು. ಇದು ನನ್ನ ಪ್ರಭು ಎಂದರು. ಆದರೆ ಅದು ಅಸ್ತಮಿಸಿದಾಗ, ನಾನು ಅಸ್ತಮಿಸುವವನನ್ನು ಇಷ್ಟಪಡುವುದಿಲ್ಲ ಎಂದರು.');
INSERT INTO "kannada" VALUES(6,77,'6:77','ಅನಂತರ ಚಂದ್ರನು ಬೆಳಗಿ ಕಂಡಾಗ, ಇದು ನನ್ನ ಪ್ರಭು ಎಂದರು. ಅದೂ ಅಸ್ತಮಿಸಿದಾಗ ನನ್ನ ಪ್ರಭು ನನಗೆ ಮಾರ್ಗದರ್ಶನ ನೀಡದಿ ರುತ್ತಿದ್ದರೆ ನಾನೂ ದುರ್ಮಾರ್ಗದಲ್ಲಿ ಅಲೆಯು ವವರ ಕೂಟಕ್ಕೆ ಸೇರಿಬಿಡುತ್ತಿದ್ದೆ’’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(6,78,'6:78','ತರುವಾಯ ಸೂರ್ಯನನ್ನು ಉದಯಿಸಿ ಕಂಡಾಗ; ಇದೇ ನನ್ನ ಪ್ರಭು; ಇದು ಅತ್ಯಂತ ದೊಡ್ಡದು ಎಂದರು. ಆದರೆ ಅದೂ ಅಸ್ತಮಿಸಿದಾಗ ಇಬ್‍ರಾಹೀಮರು `ಓ ನನ್ನ ಜನಾಂಗವೇ, ನೀವು ಅಲ್ಲಾಹನಿಗೆ ಸಹಭಾಗಿತ್ವ ಕಲ್ಪಿಸುವುದರಿಂದ ನಾನು ದೂರವಾಗಿದ್ದೇನೆ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(6,79,'6:79','ನಾನು ಸತ್ಯಮಾರ್ಗದಲ್ಲಿ ದೃಢವಾಗಿದ್ದುಕೊಂಡು ನನ್ನ ಮುಖವನ್ನು ಭೂಮಿ - ಆಕಾಶಗಳನ್ನು ಸೃಷ್ಟಿಸಿದವನ ಕಡೆಗೆ ತಿರುಗಿಸಿದೆನು. ನಾನು ಬಹುದೇವಾರಾಧಕರಲ್ಲಿ ಸೇರಿದವನಲ್ಲ.');
INSERT INTO "kannada" VALUES(6,80,'6:80','ಅವರ ಜನಾಂಗವು ಅವರೊಂದಿಗೆ ವಾಗ್ವಾದ ಮಾಡಿತು. ಆಗ ಅವರು ನೀವು ಅಲ್ಲಾಹನ ವಿಷ ಯದಲ್ಲಿ ನನ್ನೊಡನೆ ವಾಗ್ವಾದ ನಡೆಸುವುದೇ? ವಸ್ತುತಃ ಅಲ್ಲಾಹನು ನನಗೆ ಸನ್ಮಾರ್ಗದರ್ಶನ ಮಾಡಿರುತ್ತಾನೆ ಮತ್ತು ನೀವು ಅವನಿಗೆ ಪಾಲು ಸೇರಿಸುವ ಯಾವುದನ್ನೂ ನಾನು ಭಯಪಡು ವುದಿಲ್ಲ. ನನ್ನ ಪ್ರಭು ಇಚ್ಛಿಸುವ ಹೊರತು ಯಾವುದೂ ನನಗೆ ಸಂಭವಿಸುವುದಿಲ್ಲ. ನನ್ನ ಪ್ರಭುವಿನ ಜ್ಞಾನವು ಪ್ರತಿಯೊಂದು ವಸ್ತುವನ್ನೂ ಆವರಿಸಿಕೊಂಡಿದೆ. ನೀವು ಯೋಚಿಸಲಾರಿರಾ?');
INSERT INTO "kannada" VALUES(6,81,'6:81','ನೀವು (ಅಲ್ಲಾಹನೊಂದಿಗೆ) ಪಾಲು ಸೇರಿಸಿದ ವಸ್ತುಗಳನ್ನು ನಾನೇಕೆ ಭಯಪಡಲಿ? ನೀವು ಅಲ್ಲಾಹನು ಯಾವ ಆಧಾರ ಪ್ರಮಾಣವನ್ನೂ ಇಳಿಸದ ವಸ್ತುಗಳನ್ನು ಅಲ್ಲಾಹನೊಂದಿಗೆ ದೇವತ್ವದಲ್ಲಿ ಸಹಭಾಗಿಗಳನ್ನಾಗಿ ಮಾಡಲು ಭಯಪಡುವುದಿಲ್ಲ. ನಮ್ಮೆರಡು ಪಂಗಡಗಳ ಪೈಕಿ ಹೆಚ್ಚು ನಿರ್ಭಯತೆಗೆ ಅರ್ಹರು ಯಾರು ? ನೀವು ಬಲ್ಲವರಾಗಿದ್ದರೆ ಹೇಳಿರಿ.');
INSERT INTO "kannada" VALUES(6,82,'6:82','ವಿಶ್ವಾಸವಿಟ್ಟು ತಮ್ಮ ವಿಶ್ವಾಸದಲ್ಲಿ ಅಕ್ರಮವನ್ನು ಕಲಬೆರಕೆ ಮಾಡದವರು ಯಾರೋ ಅವರಿಗೇ ನಿರ್ಭಯತ್ವ ಇದೆ ಮತ್ತು ಅವರು ಸನ್ಮಾರ್ಗಪ್ರಾಪ್ತರು.');
INSERT INTO "kannada" VALUES(6,83,'6:83','ಇಬ್‍ರಾಹೀಮರಿಗೆ ಅವರ ಜನಾಂಗದ ವಿರುದ್ಧ ನಾವು ದಯಪಾಲಿಸಿದ್ದ ನಮ್ಮ ನ್ಯಾಯ ಪ್ರಮಾಣ ಇದೇ ಆಗಿದೆ. ನಾವು ಇಚ್ಛಿಸಿದವರಿಗೆ ಉನ್ನತ ಪದವಿಗಳನ್ನು ದಯಪಾಲಿಸುತ್ತೇವೆ. ನಿಜವಾಗಿಯೂ ನಿಮ್ಮ ಪ್ರಭು ಪರಮ ತಂತ್ರಜ್ಞನೂ, ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(6,84,'6:84','ಇಬ್‍ರಾಹೀಮರಿಗೆ ನಾವು ಇಸ್‍ಹಾಖ್ ಮತ್ತು ಯಅಖೂಬರನ್ನು ದಾನ ನೀಡಿದೆವು. ಇಬ್ಬರಿಗೂ ಸನ್ಮಾರ್ಗದರ್ಶನ ಮಾಡಿದೆವು. ಅದಕ್ಕಿಂತ ಮುಂಚೆ ನೂಹರಿಗೂ ನಾವು ಸನ್ಮಾರ್ಗದರ್ಶನ ಮಾಡಿದೆವು. ಅವರದೇ ಪೀಳಿಗೆಯಿಂದ ನಾವು ದಾವೂದ್, ಸುಲೈಮಾನ್, ಅಯ್ಯೂಬ್, ಯೂಸುಫ್, ಮೂಸಾ ಮತ್ತು ಹಾರೂನರಿಗೆ ಸನ್ಮಾರ್ಗ ದರ್ಶನ ಮಾಡಿದೆವು. ಈ ರೀತಿ ನಾವು ಸಜ್ಜನರಿಗೆ ಪ್ರತಿಫಲ ನೀಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(6,85,'6:85','ಝಕರಿಯ್ಯಾ, ಯಹ್ಯಾ, ಈಸಾ ಮತ್ತು ಇಲ್ಯಾಸರಿಗೂ ನಾವು ಸತ್ಪಥದರ್ಶನ ನೀಡಿದೆವು. ಅವರೆಲ್ಲರೂ ಸಜ್ಜನರಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(6,86,'6:86','ಇಸ್ಮಾಈಲ್, ಅಲ್‍ಯಸಅï, ಯೂನುಸ್ ಮತ್ತು ಲೂಥರಿಗೂ ನಾವು ಸತ್ಪಥದರ್ಶನ ಮಾಡಿದೆವು. ಅವರೆಲ್ಲರನ್ನು ಲೋಕದ ಜನರಿಗಿಂತ ಶ್ರೇಷ್ಠಗೊಳಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(6,87,'6:87','ಇವರ ಪಿತೃರಿಂದ, ಸಂತತಿಗಳಿಂದ, ಸಹೋದರರಿಂದ ಅನೇಕರನ್ನು ನಾವು ಶ್ರೇಷ್ಠಗೊಳಿಸಿದೆವು. ಅವರನ್ನು ನಾವು ವಿಶಿಷ್ಟರಾಗಿ ಆಯ್ದುಕೊಂಡೆವು ಮತ್ತು ಅವರಿಗೆ ಸನ್ಮಾರ್ಗದರ್ಶನ ನೀಡಿದೆವು.');
INSERT INTO "kannada" VALUES(6,88,'6:88','ಅಲ್ಲಾಹನ ಮಾರ್ಗದರ್ಶನವಿದು. ತನ್ಮೂಲಕ ಅವನು ತನ್ನ ದಾಸರ ಪೈಕಿ ತಾನುದ್ದೇಶಿಸಿ ದವರಿಗೆ ಸನ್ಮಾರ್ಗದರ್ಶನ ಮಾಡುತ್ತಾನೆ. ಆದರೆ, ಅವರೆಲ್ಲಾದರೂ (ಅಲ್ಲಾಹನಿಗೆ) ಪಾಲುಸೇರಿಸುತ್ತಿದ್ದರೆ ಅವರು ಮಾಡಿದ್ದೆಲ್ಲ ವ್ಯರ್ಥವಾಗುತ್ತಿತ್ತು.');
INSERT INTO "kannada" VALUES(6,89,'6:89','ನಾವು ಗ್ರಂಥ, ಶಾಸನ ಮತ್ತು ಪ್ರವಾದಿತ್ವ ನೀಡಿ ಅನುಗ್ರಹಿಸಿದ್ದವರು ಅವರು . ಇನ್ನು ಇವರು ಅದನ್ನು ನಿಷೇಧಿಸುತ್ತಿರುವವರಾದರೆ, ನಾವು ಅದನ್ನು ನಿಷೇಧಿಸದ ಇನ್ನೊಂದು ಜನತೆಗೆ ಇದನ್ನು ಒಪ್ಪಿಸಲಿದ್ದೇವೆ .');
INSERT INTO "kannada" VALUES(6,90,'6:90','ಅವರೇ ಅಲ್ಲಾಹನ ವತಿಯಿಂದ ಸನ್ಮಾರ್ಗ ಹೊಂದಿದವರು. ಆದ್ದರಿಂದ ನೀವು ಅವರ ಸತ್ಪಥವನ್ನು ಅನುಸರಿಸಿರಿ . ಇದಕ್ಕಾಗಿ ನಿಮ್ಮಿಂದ ಯಾವುದೇ ಪ್ರತಿಫಲವನ್ನು ನಾನು ಕೇಳುವುದಿಲ್ಲ. ಇದು ಲೋಕದ ಜನರಿಗೆ ಒಂದು ಉಪದೇಶವಲ್ಲದೆ ಇನ್ನೇನೂ ಅಲ್ಲ ಎಂದು ಹೇಳಿರಿ. (ಪೈಗಂಬರರೇ!)');
INSERT INTO "kannada" VALUES(6,91,'6:91','ಯಾವ ಮಾನವನ ಮೇಲೂ ಅಲ್ಲಾಹನು ಏನನ್ನೂ ಅವತೀರ್ಣಗೊಳಿಸಿಲ್ಲ ಎಂದು ಹೇಳಿದ ಸಂದರ್ಭ ದಲ್ಲಿ ಅಲ್ಲಾಹನನ್ನು ಅರಿಯಬೇಕಾದ ಕ್ರಮದಲ್ಲಿ ಅವರು ಅರಿತುಕೊಳ್ಳಲಿಲ್ಲ. ಕೇಳಿರಿ - ಮಾನ ವರಿಗೆ ಸತ್ಯಪ್ರಕಾಶವೂ ಮಾರ್ಗದರ್ಶನವೂ ಆಗಿ ಮೂಸಾರವರು ತಂದ ಗ್ರಂಥವನ್ನು ಅವತೀರ್ಣ ಗೊಳಿಸಿದ್ದು ಯಾರು? ಅದನ್ನು ನೀವು ಕಾಗದ ತುಂಡುಗಳಲ್ಲಿ ದಾಖಲಿಸಿ ಕೆಲವನ್ನು ತೋರಿಸಿ ಹೆಚ್ಚಿನದನ್ನು ಬಚ್ಚಿಡುತ್ತೀರಿ. ಅದರ ಮೂಲಕ ನೀವೂ ನಿಮ್ಮ ಪೂರ್ವಜರೂ ತಿಳಿದಿರದ ಅನೇಕ ಜ್ಞಾನವನ್ನು ನಿಮಗೆ ಕಲಿಸಲಾಗಿತ್ತು. ಅದನ್ನು ಅವತೀರ್ಣಗೊಳಿಸಿದವನು ಅಲ್ಲಾಹು ಎಂದು ಹೇಳಿಬಿಡಿರಿ. ಅನಂತರ ಅವರನ್ನು ತಮ್ಮ ಕುತರ್ಕಗಳೊಂದಿಗೆ ಆಡುತ್ತಿರಲು ಬಿಡಿರಿ.');
INSERT INTO "kannada" VALUES(6,92,'6:92','ಇದು ನಾವು ಇಳಿಸಿರುವ ಅನುಗ್ರಹೀತ ಗ್ರಂಥ ವಾಗಿದೆ, ಅದು ಇದಕ್ಕಿಂತ ಹಿಂದಿನ ವೇದ ಗ್ರಂಥವನ್ನು ದೃಢೀಕರಿಸುತ್ತದೆ. ಇದರ ಮೂಲಕ ನೀವು ನಾಡುಗಳ ತಾಯಿ (ಮಕ್ಕಾ) ಮತ್ತು ಇದರ ಸುತ್ತ ಮುತ್ತಲಿನ ಜನರಿಗೆ ಎಚ್ಚರಿಕೆ ನೀಡಲಿಕ್ಕಾಗಿ ಇದು ಅವತೀರ್ಣಗೊಂಡಿದೆ. ಪರಲೋಕದಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸುವವರು ಇದರಲ್ಲೂ ವಿಶ್ವಾಸ ವಿರಿಸುತ್ತಾರೆ. ತಮ್ಮ ನಮಾಝನ್ನು ಅವರು ಕಟ್ಟುನಿಟ್ಟಾಗಿ ಪಾಲಿಸುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(6,93,'6:93','ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಳ್ಳಾರೋಪ ಹೊರಿಸುವ ಅಥವಾ ತನಗೆ ಯಾವುದೇ ದಿವ್ಯಸಂದೇಶ ಅವತೀರ್ಣವಾಗಿರದಿದ್ದರೂ ನನಗೆ ದಿವ್ಯವಾಣಿ ಬಂದಿದೆಯೆನ್ನುವವನಿಗಿಂತ ಮತ್ತು ಅಲ್ಲಾಹನು ಅವತೀರ್ಣಗೊಳಿಸಿರುವುದಕ್ಕೆ ಸಮಾನವಾದು ದನ್ನು ನಾನೂ ಅವತೀರ್ಣಗೊಳಿಸಬಲ್ಲೆ ಎಂದು ಹೇಳಿದವನಿಗಿಂತ ದೊಡ್ಡ ಅಕ್ರಮಿ ಯಾರಿದ್ದಾನೆ? ಅಂತಹ ಅಕ್ರಮಿಗಳನ್ನು, ಅವರು ಮರಣ ಸಂಕಟದಲ್ಲಿ ಮುಳುಗಿ ಹೋಗುವ ಮತ್ತು ದೇವಚರರು ಕೈ ಚಾಚುತ್ತ, ‘ನೀವು ನಿಮ್ಮ ಪ್ರಾಣವನ್ನು ಹೊರ ತೆಗೆಯಿರಿ. ನೀವು ಅಲ್ಲಾಹನ ಮೇಲೆ ಸತ್ಯವಲ್ಲದ್ದನ್ನು ಹೇಳುತ್ತಿದ್ದುದರ ಹಾಗೂ ಅವನ ದೃಷ್ಟಾಂತಗಳನ್ನು ನೀವು ಅಹಂಕರಿಸಿ ತಳ್ಳಿ ಹಾಕುತ್ತಿದ್ದುದರ ಫಲವಾಗಿ ಇಂದು ನಿಮಗೆ ಹೀನಾಯ ಶಿಕ್ಷೆ ನೀಡಲಾಗುವುದು’ ಎಂದು ಹೇಳುತ್ತಿರುವ ದೃಶ್ಯವನ್ನು ತಾವು ಕಂಡಿದ್ದರೆ! ');
INSERT INTO "kannada" VALUES(6,94,'6:94','(ಅಲ್ಲಾಹು ಹೇಳುವನು;) ನಾವು ನಿಮ್ಮನ್ನು ಪ್ರಥಮ ಬಾರಿ ಸೃಷ್ಟಿಸಿದಂತೆಯೇ ನೀವೀಗ ಒಬ್ಬಂಟಿಗರಾಗಿ ನಮ್ಮ ಬಳಿ ಬಂದಿರುವಿರಿ. ನಾವು ನಿಮಗೆ (ಇಹಲೋಕದಲ್ಲಿ) ಕೊಟ್ಟಿದ್ದುದನ್ನೆಲ್ಲ ನಿಮ್ಮ ಬೆನ್ನ ಹಿಂದೆಯೇ ನೀವು ಬಿಟ್ಟು ಬಂದಿರಿ. ನಿಮ್ಮ ಕಾರ್ಯದಲ್ಲಿ (ಅಲ್ಲಾಹನ) ಪಾಲುದಾರರೆಂದು ನೀವು ಕಲ್ಪಿಸಿದ್ದ ಆ ನಿಮ್ಮ ಶಿಫಾರಸ್ಸುದಾರರನ್ನು ನಿಮ್ಮ ಜೊತೆಗೆ ನಾವು ಇಲ್ಲಿ ಕಾಣುತ್ತಿಲ್ಲ. ನಿಮ್ಮ ಪರಸ್ಪರ ಸಂಬಂಧವು ಮುರಿದು ಹೋಗಿದೆ. ನೀವು ಕಲ್ಪಿಸಿ ಕೊಂಡಿದ್ದುದೆಲ್ಲವೂ ನಿಮ್ಮಿಂದ ಮಾಯವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(6,95,'6:95','ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಧಾನ್ಯ ಮತ್ತು ಬೀಜದ ಫಲಗಳನ್ನು (ಸಸ್ಯಗಳಿಂದ) ಚಿಗುರಿಸುವವನು. ಅವನೇ ನಿರ್ಜೀವಿಯಿಂದ ಸಜೀವಿಯನ್ನು ಹೊರ ತೆಗೆಯುತ್ತಾನೆ ಮತ್ತು ಸಜೀವಿಯಿಂದ ನಿರ್ಜೀವಿಯನ್ನು ಹೊರತರುವವ ನಾಗಿರುತ್ತಾನೆ . ಅವನೇ ಅಲ್ಲಾಹು. ಹೀಗಿರುತ್ತ ಹೇಗೆ ನಿಮ್ಮನ್ನು ದಾರಿ ತಪ್ಪಿಸಲಾಗುತ್ತಿದೆ?');
INSERT INTO "kannada" VALUES(6,96,'6:96','ಅವನೇ ಪ್ರಭಾತವನ್ನು ಸೀಳಿ ತರುವವನು. ಅವನೇ ಇರುಳನ್ನು ನೆಮ್ಮದಿಯ ವಿಶ್ರಾಂತಿ ಕಾಲವ ನ್ನಾಗಿ ಮಾಡುವವನು. ಅವನೇ ಸೂರ್ಯಚಂದ್ರರನ್ನು ಕಾಲ ಗಣನೆಯನ್ನಾಗಿ ಮಾಡಿದವನು. ಮಹಾ ಪ್ರತಾಪಿಯೂ ಸರ್ವಜ್ಞನೂ ಆದ ಅಲ್ಲಾಹನ ನಿರ್ಣಯವಿದು.');
INSERT INTO "kannada" VALUES(6,97,'6:97','ಅವನೇ ನಿಮಗೆ ನಕ್ಷತ್ರಗಳನ್ನು ನೆಲ - ಜಲಗಳ ಅಂಧಕಾರಗಳಲ್ಲಿ ದಾರಿ ತಿಳಿಯುವ ಸಾಧನಗಳನ್ನಾಗಿ ಮಾಡಿದನು. ಜ್ಞಾನವುಳ್ಳವರಿಗೆ ನಾವು ನಿದರ್ಶನಗಳನ್ನು ವಿವರಿಸಿ ಕೊಟ್ಟಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(6,98,'6:98','ಅವನೇ ನಿಮ್ಮನ್ನು ಒಂದೇ ವ್ಯಕ್ತಿಯಿಂದ ಸೃಷ್ಟಿಸಿ ದವನು. ಪ್ರತಿಯೊಬ್ಬನಿಗೂ ಒಂದು ಸ್ಥಿರ ತಂಗುದಾಣವೂ ನಿಕ್ಷೇಪ ಕೇಂದ್ರವೂ (ಕಾಯ್ದಿಡಲಾಗುವ ಸುರಕ್ಷಿತ ಸ್ಥಳ) ಇದೆ . ಗ್ರಹಿಸಿ ಅರ್ಥ ಮಾಡಿಕೊಳ್ಳುವ ಜನರಿಗೆ ಪುರಾವೆಗಳನ್ನು ನಾವು ಅಭಿವ್ಯಕ್ತಗೊಳಿಸಿರುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(6,99,'6:99','ಅವನೇ ಆಕಾಶದಿಂದ ನೀರನ್ನು ಇಳಿಸಿದವನು . ಅದರ ಮೂಲಕ ನಾವು ಎಲ್ಲ ವಿಧದ ಸಸ್ಯಗಳನ್ನು ಉತ್ಪಾದಿಸುತ್ತೇವೆ. ಆ ಮೇಲೆ ಅದರಿಂದ ಹಸಿರನ್ನು ಹೊರತರುತ್ತೇವೆ. ಆ ಬಳಿಕ ಅವುಗಳಿಂದ ದಟ್ಟವಾದ ಧಾನ್ಯಗಳನ್ನು ಹೊರತರುತ್ತೇವೆ. ಮತ್ತು ಖರ್ಜೂರ ಮರದಿಂದ ಅಂದರೆ ಅದರ ಗೊನೆಯಿಂದ ತುಂಬಿ ಬಾಗಿದ ಫಲಗಳ ಗೊಂಚಲುಗಳನ್ನು ಉಂಟು ಮಾಡುತ್ತೇವೆ. (ಹಾಗೆಯೇ) ದ್ರಾಕ್ಷೆ ತೋಟಗಳನ್ನೂ ಎಲೆಗಳು ಪರಸ್ಪರ ಹೋಲಿಕೆಯಿರುವ ಹಣ್ಣುಗಳಲ್ಲಿ ಹೋಲಿಕೆಯಿಲ್ಲದ ಆಲಿವ್ ಹಾಗೂ ದಾಳಿಂಬೆ ಹಣ್ಣುಗಳ ತೋಟಗಳನ್ನು ನಾವು ಉತ್ಪಾದಿಸು ತ್ತೇವೆ. ಅವು ಫಲ ಬಿಡುವಾಗ ಅವುಗಳಲ್ಲಿ ಫಲ ಬರುವ ಮತ್ತು ಪಕ್ವವಾಗುವ ಪರಿಯನ್ನು ನೋಡಿರಿ. ವಿಶ್ವಾಸವಿಡುವ ಜನರಿಗೆ ಇವುಗಳಲ್ಲಿ ನಿದರ್ಶನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(6,100,'6:100','ಅವರು ಜಿನ್ನ್‍ಗಳನ್ನು ಅಲ್ಲಾಹನ ಪಾಲುದಾರ ರನ್ನಾಗಿ ಮಾಡಿದರು. ವಸ್ತುತಃ ಅವನು ಅವ ರನ್ನು ಸೃಷ್ಟಿಸಿದನು. ಆದರೆ ಅವರು ಯಾವುದೇ ಜ್ಞಾನವಿಲ್ಲದೆ ಅವನಿಗೆ ಪುತ್ರ ಮತ್ತು ಪುತ್ರಿಯ ರನ್ನು ಆರೋಪಿಸಿದರು. ವಸ್ತುತಃ ಅವನು ಇವರು ಹೇಳುತ್ತಿರುವ ವರ್ಣನೆಗಳಿಂದ ಮೀರಿದ ಪರಿಶುದ್ಧನೂ, ಉನ್ನತನೂ ಆಗಿರುತ್ತಾನೆ ');
INSERT INTO "kannada" VALUES(6,101,'6:101','ಅವನು ಭೂಮಿ-ಆಕಾಶಗಳನ್ನು ಪೂರ್ವ ಮಾದರಿಯಿಲ್ಲದೆ ಸೃಷ್ಟಿಸಿದ ಕರ್ತೃ. ಅವನಿಗೊಬ್ಬ ಬಾಳಸಂಗಾತಿಯೇ ಇಲ್ಲದಿರುವಾಗ ಅವನಿಗೆ ಮಗನಾಗುವುದು ಹೇಗೆ? ಅವನು ಎಲ್ಲ ವಸ್ತುಗಳನ್ನು ಸೃಷ್ಟಿಸಿದನು ಮತ್ತು ಅವನು ಸಕಲ ವಸ್ತುಗಳ ಜ್ಞಾನವುಳ್ಳವನು.');
INSERT INTO "kannada" VALUES(6,102,'6:102','ಅವನೇ ಅಲ್ಲಾಹು. ನಿಮ್ಮ ಪ್ರಭು. ಅವನ ಹೊರತು ಅನ್ಯ ಆರಾಧ್ಯನಿಲ್ಲ. ಸಕಲ ವಸ್ತುಗಳ ಸೃಷ್ಟಿಕರ್ತ. ಆದುದರಿಂದ ನೀವು ಅವನನ್ನು ಆರಾಧಿಸಿರಿ. ಅವನು ಸಕಲ ವಸ್ತುಗಳ ಹೊಣೆಗಾರನು.');
INSERT INTO "kannada" VALUES(6,103,'6:103','ಕಣ್ಣುಗಳು ಅವನನ್ನು ಕಾಣಲಾರವು. ಆದರೆ ಅವನು ಕಣ್ಣುಗಳನ್ನು ಕಾಣುತ್ತಾನೆ. ಅವನು ಅತ್ಯಂತ ಸೌಮ್ಯನೂ, ಸೂಕ್ಷ್ಮಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(6,104,'6:104','(ಹೇಳಿರಿ ನಬಿಯವರೇ!) ನಿಮ್ಮ ಬಳಿಗೆ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಕಣ್ತೆರೆಸುವ ಪುರಾವೆಗಳು ಬಂದಿವೆ . ಯಾವನು ಅದನ್ನು ಕಂಡರಿಯುತ್ತಾನೆ, ಅದರ ಗುಣ ಅವನಿಗೇ ಸಿಗುವುದು. ಯಾರು ಅದರ ಬಗ್ಗೆ ಕುರುಡಾಗುತ್ತಾನೆ, ಅದರ ಕೇಡು ಅವನಿಗೇ ಸಲ್ಲುವುದು. ನಾನು ನಿಮ್ಮ ಕಾವಲುಗಾರನೇನೂ ಅಲ್ಲ .');
INSERT INTO "kannada" VALUES(6,105,'6:105','ಇದೇ ಪ್ರಕಾರ ನಾವು ನಿದರ್ಶನಗಳನ್ನು ವಿವಿಧ ರೂಪದಲ್ಲಿ ವಿವರಿಸುತ್ತೇವೆ. ಇದು ಏಕೆಂದರೆ, ನೀವು (ವೈದಿಕರಿಂದ) ಕಲಿತು ಬಂದಿದ್ದೀರಿ ಎಂದು ಅವಿಶ್ವಾಸಿಗಳು ಹೇಳುವುದಕ್ಕಾಗಿಯೂ ಹಾಗೂ ಜ್ಞಾನ ಹೊಂದಿದವರ ಮೇಲೆ ವಿಷಯ ವನ್ನು ನಾವು ವಿವರಿಸಿ ಕೊಡುವುದಕ್ಕಾಗಿಯೂ ಆಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(6,106,'6:106','(ಓ ಪೈಗಂಬರರೇ,) ನಿಮ್ಮ ಪ್ರಭುವಿನಿಂದ ನಿಮ್ಮ ಕಡೆಗೆ ಅವತೀರ್ಣಗೊಂಡ ದಿವ್ಯ ಸಂದೇಶವನ್ನು ಅನುಸರಿಸಿರಿ. ಅವನಲ್ಲದೆ ಬೇರೆ ದೇವನಿಲ್ಲ ಮತ್ತು ಬಹು ದೇವಾರಾಧಕರನ್ನು ನಿರ್ಲಕ್ಷಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(6,107,'6:107','ಅಲ್ಲಾಹನು ಇಚ್ಛಿಸುತ್ತಿದ್ದರೆ ಅವರು ಬಹು ದೇವಾರಾಧನೆ ಮಾಡುತ್ತಿರಲಿಲ್ಲ. ನಾವು ನಿಮ್ಮನ್ನು ಅವರ ಕಾವಲುಗಾರನನ್ನಾಗಿ ನೇಮಿಸಿಲ್ಲ ಮತ್ತು ನೀವು ಅವರ ಹೊಣೆಗಾರರೂ ಅಲ್ಲ.');
INSERT INTO "kannada" VALUES(6,108,'6:108','ಅವರು ಅಲ್ಲಾಹನ ಹೊರತು ಯಾರನ್ನು ಆರಾಧಿಸುತ್ತಿರುವರೋ ಅವುಗಳನ್ನು ತೆಗಳಬೇಡಿರಿ. ಇದರಿಂದ ಅವರು ಜ್ಞಾನವಿಲ್ಲದೆ ವೈರತ್ವದಿಂದ ಅಲ್ಲಾಹನನ್ನು ತೆಗಳುವರು. ನಾವು ಇದೇ ಪ್ರಕಾರ ಪ್ರತಿಯೊಂದು ಸಮುದಾಯಕ್ಕೆ ಅದರ ಕರ್ಮವನ್ನು ಚೆಲುವಾಗಿಸಿದ್ದೇವೆ. ಅನಂತರ ಅವರಿಗೆ ತಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಗೇ ಮರಳಿ ಸಾಗಲಿಕ್ಕಿದೆ. ಅವರೇನು ಮಾಡುತ್ತಿದ್ದರೆಂ ಬುದನ್ನು ಆಗ ಅವರಿಗೆ ಅವನು ತೋರಿಸಿ ಕೊಡುವನು.');
INSERT INTO "kannada" VALUES(6,109,'6:109','ನಮ್ಮ ಮುಂದೆ ಯಾವುದೇ ದೃಷ್ಟಾಂತವು ಬಂದು ಬಿಟ್ಟರೆ ನಾವು ಅದರ ಮೇಲೆ ವಿಶ್ವಾಸ ವಿಡುವೆವೆಂದು ಇವರು ಅಲ್ಲಾಹನ ಮೇಲೆ ತಮಗೆ ಸಾಧ್ಯವಾದ ಮಟ್ಟಿಗೆ ಆಣೆ ಹಾಕುತ್ತ ಬಹಳ ದೃಢವಾಗಿ ಹೇಳುತ್ತಾರೆ. (ಓ ಪೈಗಂಬರರೇ,) ದೃಷ್ಟಾಂತಗಳು ಅಲ್ಲಾಹನ ಬಳಿ ಇವೆ ಎಂದು ಹೇಳಿರಿ. (ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ !) ಅವು ಬಂದರೂ ಕೂಡ ಇವರು ವಿಶ್ವಾಸವಿರಿಸುವುದಿಲ್ಲವೆಂದು ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(6,110,'6:110','ಇವರು ಮೊದಲ ಬಾರಿ ಇದರ (ಖುರ್‍ಆನಿನ) ಮೇಲೆ ವಿಶ್ವಾಸವಿಡದಂತೆಯೇ (ಈಗಲೂ) ನಾವು ಇವರ ಹೃದಯಗಳನ್ನೂ ದೃಷ್ಟಿಗಳನ್ನೂ ತಿರುಗಿಸಿ ಬಿಡುತ್ತಿದ್ದೇವೆ. ತಮ್ಮ ದಿಕ್ಕಾರದಲ್ಲೇ ಅಲೆದಾಡುತ್ತಿರಲು ನಾವು ಅವರನ್ನು ಬಿಟ್ಟು ಬಿಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(6,111,'6:111','ನಾವು ಇವರ ಮೇಲೆ ದೇವಚರರನ್ನು ಇಳಿಸು ತ್ತಿದ್ದರೂ ಮೃತರು ಇವರೊಡನೆ ಮಾತಾಡುತ್ತಿ ದ್ದರೂ ಇವರ ಕಣ್ಣ ಮುಂದೆ ಸರ್ವ ವಸ್ತುಗಳನ್ನು ಗುಂಪು ಗುಂಪಾಗಿ ಒಟ್ಟುಗೂಡಿಸುತ್ತಿದ್ದರೂ ಅಲ್ಲಾಹು ಇಚ್ಛಿಸದ ಹೊರತು ಇವರು ವಿಶ್ವಾಸ ವಿಡುತ್ತಿರಲಿಲ್ಲ. ಆದರೆ ಅವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಅಜ್ಞಾನಿಗಳು .');
INSERT INTO "kannada" VALUES(6,112,'6:112','ಇದೇ ಪ್ರಕಾರ ಪ್ರತಿಯೊಬ್ಬ ಪ್ರವಾದಿಗೂ ಮಾನವ ಹಾಗೂ ಜಿನ್ನ್‍ಗಳ ಪೈಕಿ ಶೈತಾನರನ್ನು ಶತ್ರುವಾಗಿ ನಿಯಮಿಸಿದ್ದೇವೆ. ಅವರು ವಂಚಿಸುವ ಸಲುವಾಗಿ ಮೋಡಿ ಮಾತುಗಳನ್ನು ಪರಸ್ಪರ ವಿನಿಮಯ ಮಾಡಿಕೊಳ್ಳುತ್ತಾರೆ. ನಿಮ್ಮ ಪ್ರಭು ಇಚ್ಛಿಸುತ್ತಿದ್ದರೆ ಅವರು ಹಾಗೆ ಮಾಡುತ್ತಿರಲಿಲ್ಲ. ಆದ್ದರಿಂದ ಅವರನ್ನೂ ಅವರ ಸ್ವಕಲ್ಪಿತ ಆರೋಪಗಳನ್ನೂ ನಿರ್ಲಕ್ಷಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(6,113,'6:113','ಪರಲೋಕದಲ್ಲಿ ವಿಶ್ವಾಸವಿಡದವರ ಹೃದಯಗ1⁄4ನ್ನು ಸಮ್ಮೋಹಕ ಮಾತುಗಳ ಕಡೆಗೆ ಆಕರ್ಷಿಸಲು ಹಾಗೂ ಅವರು ಅದರಿಂದ ತೃಪ್ತರಾಗಲು ಮತ್ತು ಅವರ ಪಾಪದ ಫಲ ಅನುಭವಿಸುವಂತಾಗಲು.');
INSERT INTO "kannada" VALUES(6,114,'6:114','ವಿಧಿಕರ್ತನನ್ನಾಗಿ ನಾನು ಅಲ್ಲಾಹು ಅಲ್ಲದವ ರನ್ನು ಸ್ವೀಕರಿಸಬೇಕೆ? ವಸ್ತುತಃ ಅವನು ವಿವರ ಪೂರ್ಣವಾದ ಗ್ರಂಥವನ್ನು ನಿಮ್ಮ ಕಡೆಗೆ ಅವತೀರ್ಣಗೊಳಿಸಿರುತ್ತಾನೆ. ನಿಮಗಿಂತ ಮೊದಲು ಗ್ರಂಥ ನೀಡಲ್ಪಟ್ಟವರು, ಈ ಗ್ರಂಥವು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದಲೇ ಸತ್ಯಸಂಧವಾಗಿ ಅವತೀರ್ಣಗೊಂಡಿದೆಯೆಂಬುದನ್ನು ತಿಳಿದಿರುತ್ತಾರೆ. ಆದುದರಿಂದ ನೀವು ಸಂಶಯಗ್ರಸ್ತರಲ್ಲಿ ಸೇರಬೇಡಿರಿ .');
INSERT INTO "kannada" VALUES(6,115,'6:115','ನಿಮ್ಮ ಪ್ರಭುವಿನ ವಚನವು ಸತ್ಯ ಮತ್ತು ನ್ಯಾಯ ನೀತಿಯಿಂದ ಪರಿಪೂರ್ಣವಾಗಿದೆ. ಅವನ ವಚ ನಗಳನ್ನು ಯಾರೂ ಬದಲಾವಣೆ ಮಾಡುವವರಿಲ್ಲ. ಅವನು ಸರ್ವಶ್ರುತನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(6,116,'6:116','(ಓ ಪೈಗಂಬರರೇ,) ನೀವು ಭೂಮಿಯಲ್ಲಿರುವ ಹೆಚ್ಚಿನಾಂಶ ಜನರನ್ನು ಅನುಸರಿಸಿದರೆ ಅವರು ನಿಮ್ಮನ್ನು ಅಲ್ಲಾಹನ ಧರ್ಮ ಮಾರ್ಗದಿಂದ ತಪ್ಪಿಸಿಬಿಡುವರು. ಕೇವಲ ಊಹಾಪೋಹಗಳ ಹೊರತು ಇನ್ನೇನನ್ನೂ ಅವರು ಅನುಸರಿಸುತ್ತಿಲ್ಲ. ಕೇವಲ ಅನುಮಾನಗಳನ್ನೇ ಹೊರತು ಅವರು ಹೇಳುತ್ತಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(6,117,'6:117','ವಾಸ್ತವದಲ್ಲಿ ತನ್ನ ಮಾರ್ಗದಿಂದ ತಪ್ಪಿಹೋದ ವನು ಯಾರೆಂದು ನಿಮ್ಮ ಪ್ರಭು ಅರಿಯುವನು. ಸನ್ಮಾರ್ಗ ಹೊಂದಿದವರ ಬಗ್ಗೆ ಚೆನ್ನಾಗಿ ಅರಿಯುವವನೂ ಅವನೇ.');
INSERT INTO "kannada" VALUES(6,118,'6:118','ನೀವು ಅಲ್ಲಾಹನ ವಚನಗಳಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸುತ್ತೀರಾದರೆ ಅಲ್ಲಾಹನ ನಾಮ ಉಚ್ಚರಿಸಲ್ಪಟ್ಟಿ ರುವ ಪ್ರಾಣಿಯ ಮಾಂಸವನ್ನು ತಿನ್ನಿರಿ.');
INSERT INTO "kannada" VALUES(6,119,'6:119','ಅಲ್ಲಾಹನ ನಾಮ ಉಚ್ಚರಿಸಲ್ಪಟ್ಟ ಪ್ರಾಣಿಗಳ ಮಾಂಸವನ್ನು ನೀವು ತಿನ್ನದಿರಲು ಕಾರಣ ವೇನು? ನಿಜವಾಗಿ ಅನ್ಯ ಮಾರ್ಗವಿಲ್ಲದ ವಿಷಮ ಸ್ಥಿತಿಗೆ ನೀವು ಸಿಲುಕಿದ ಸಂದರ್ಭದ ಹೊರತು ಇತರ ಎಲ್ಲ ಸ್ಥಿತಿಗಳಲ್ಲಿಯೂ ಅಲ್ಲಾಹನು ನಿಮಗೆ ನಿಷೇಧಿಸಿರುವ ವಸ್ತುಗಳ ವಿವರವನ್ನು ನಿಮಗೆ ತಿಳಿಸಿಕೊಡಲಾಗಿದೆ. ನಿಶ್ಚಯವಾಗಿಯೂ ಹೆಚ್ಚಿ ನವರು ಜ್ಞಾನವಿಲ್ಲದೆ ಕೇವಲ ತನ್ನಿಚ್ಛೆಯಂತೆ ಜನರನ್ನು ದಾರಿ ತಪ್ಪಿಸುತ್ತಾರೆ. ಹದ್ದು ಮೀರು ವವರ ಕುರಿತು ನಿಮ್ಮ ಪ್ರಭು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(6,120,'6:120','ನೀವು ಪ್ರತ್ಯಕ್ಷ ಮತ್ತು ಪರೋಕ್ಷ ಪಾಪಗಳಿಂದ ದೂರವಿರಿ . ಪಾಪಗಳಿಸುವವರು ತಮ್ಮಗಳಿಕೆಗೆ ತಕ್ಕ ಪ್ರತಿಫಲ ಪಡೆದೇ ತೀರುವರು.');
INSERT INTO "kannada" VALUES(6,121,'6:121','ಅಲ್ಲಾಹನ ನಾಮ ಉಚ್ಚರಿಸಲ್ಪಡದ (ವದಿ üಸಲ್ಪಟ್ಟ) ಪ್ರಾಣಿಯ ಮಾಂಸ ತಿನ್ನಬೇಡಿರಿ . ನಿಜಕ್ಕೂ ಅದು ಅಧರ್ಮ. ಈ ಬಗ್ಗೆ ನಿಮ್ಮೊಡನೆ ವಾಗ್ವಾದ ಮಾಡಲಿಕ್ಕಾಗಿ ಶೈತಾನರು ತಮ್ಮ ಆಪ್ತರಿಗೆ ಪ್ರಚೋದನೆ ಕೊಡುವರು. ನೀವು ಅವರನ್ನು ಅನುಸರಿಸಿದರೆ, ಖಂಡಿತವಾಗಿಯೂ ನೀವು ಬಹುದೇವಾರಾಧಕರಲ್ಲಾಗುವಿರಿ .');
INSERT INTO "kannada" VALUES(6,122,'6:122','ಹಿಂದೆ ಮೃತನಾಗಿದ್ದು, ಅನಂತರ ನಾವು ಜೀವ ತುಂಬಿ ಜನರ ಮಧ್ಯೆ ನಡೆಯಲು ಬೇಕಾದ ಬೆಳಕನ್ನು ದಯಪಾಲಿಸಿದ ಓರ್ವ ವ್ಯಕ್ತಿಯು, ಹೊರ ಬರಲಾಗದೆ ಅಂಧಕಾರದಲ್ಲಿ ಬಿದ್ದು ಕೊಂಡಿರುವ ವ್ಯಕ್ತಿಯಂತೆ ಆಗಬಲ್ಲನೇ? ಈ ರೀತಿ ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಅವರ ಕರ್ಮಗಳನ್ನು ಸುಂದರವಾಗಿ ಕಾಣಿಸುವಂತೆ ಮಾಡಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(6,123,'6:123','ಇದೇ ರೀತಿ ನಾವು ಪ್ರತಿಯೊಂದು ನಾಡಿನಲ್ಲಿ ದುಷ್ಟರನ್ನು ಮುಖಂಡರನ್ನಾಗಿ ಮಾಡಿದ್ದೇವೆ. ಅವರು ಅಲ್ಲಿ ಒಳಸಂಚು ಹೂಡುವರು. ವಾಸ್ತವದಲ್ಲಿ ಅವರು ತಮ್ಮ ಮೇಲೆಯೇ ಒಳಸಂಚು ಹೂಡುತ್ತಾರೆ. ಆದರೆ ಅವರಿಗೆ ಅದರ ಪ್ರಜ್ಞೆ ಇರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(6,124,'6:124','ಅವರ ಮುಂದೆ ಯಾವುದೇ ನಿದರ್ಶನ ಬಂದಾಗ, `ಅಲ್ಲಾಹನ ದೂತರಿಗೆ ನೀಡಲಾದಂಥ ದಿವ್ಯ ಬೋಧನೆ ನಮಗೂ ನೀಡಲಾಗುವ ತನಕ ನಾವು ವಿಶ್ವಾಸವಿರಿಸಲಾರೆವು ಎಂದು ಅವರು ಹೇಳುತ್ತಾರೆ. ತನ್ನ ದಿವ್ಯ ಬೋಧನೆಯ ದೌತ್ಯ ವನ್ನು ಎಲ್ಲಿ ಸ್ಥಾಪಿಸ ಬೇಕೆಂದು ಅಲ್ಲಾಹು ಬಹಳ ಚೆನ್ನಾಗಿ ಬಲ್ಲನು . ಈ ಅಪರಾಧಿಗಳಿಗೆ ತಮ್ಮ ಪಿತೂರಿಯ ಫಲವಾಗಿ ಅಲ್ಲಾಹನ ಕಡೆ ಯಿಂದ ನಿಂದೆಯೂ ಉಗ್ರ ಶಿಕ್ಷೆಯೂ ಬಾಧಿಸಲಿದೆ.');
INSERT INTO "kannada" VALUES(6,125,'6:125','ಆದರೆ ಅಲ್ಲಾಹು ಯಾರಿಗೆ ಸನ್ಮಾರ್ಗ ನೀಡಲಿ ಚ್ಛಿಸುತ್ತಾನೋ ಅವನ ಹೃದಯವನ್ನು ಇಸ್ಲಾಮಿಗಾಗಿ ತೆರೆದು ಬಿಡುತ್ತಾನೆ ಮತ್ತು ಯಾರನ್ನು ದಾರಿತಪ್ಪಿಸಲಿಚ್ಛಿಸುತ್ತಾನೋ ಅವನ ಹೃದಯ ವನ್ನು ತೀವ್ರ ಇಕ್ಕಟ್ಟು ಮಾಡಿ ಸಂಕೀರ್ಣಗೊಳಿ ಸುತ್ತಾನೆ. ಅವನು ಆಕಾಶಕ್ಕೆ ಏರಿ ಹೋಗುವವ ನಂತೆ. ಇದೇ ಪ್ರಕಾರ ಅಲ್ಲಾಹನು ಕೇಡನ್ನು ವಿಶ್ವಾಸವಿಡದವರ ಮೇಲೆ ಹಾಕುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(6,126,'6:126','ಇದು ನಿಮ್ಮ ಪ್ರಭುವಿನ ನೇರ ಮಾರ್ಗವಾಗಿರುತ್ತದೆ. ಯೋಚಿಸಿ ಅರ್ಥಮಾಡಿಕೊಳ್ಳುವ ಜನರಿಗೆ ನಾವು ದೃಷ್ಟಾಂತಗಳನ್ನು ನಿರೂಪಿಸಿದ್ದೇವೆ .');
INSERT INTO "kannada" VALUES(6,127,'6:127','ಅವರ ಪ್ರಭುವಿನ ಬಳಿ ಅವರಿಗಾಗಿ ಶಾಂತಿ ಸದನವಿದೆ. ಅವನು ಅವರ ಸಂರಕ್ಷಕನಾಗಿರುತ್ತಾನೆ. ಇದು ಅವರು ಮಾಡಿದ ಸುಕೃತಗಳ ಫಲ.');
INSERT INTO "kannada" VALUES(6,128,'6:128','ಅಲ್ಲಾಹನು ಅವರೆಲ್ಲರನ್ನು ಒಟ್ಟುಗೂಡಿಸುವ ದಿನ (ಜಿನ್ನ್‍ಗಳನ್ನುದ್ದೇಶಿಸಿ,) ಓ ಖೇಚರ ವರ್ಗವೇ ನೀವು ಮಾನವರಲ್ಲಿ ಅನೇಕರನ್ನು ದಾರಿ ತಪ್ಪಿಸಿದ್ದೀರಿ ಎಂದು ಹೇಳುವನು. ಮನುಷ್ಯರ ಪೈಕಿ ಅವರ ಆಪ್ತ ಮಿತ್ರರಾದವರು `ಪ್ರಭೂ, ನಮ್ಮಲ್ಲಿ ಕೆಲವರು ಬೇರೆ ಕೆಲವರನ್ನು ಸುಖಕ್ಕೆ ಬಳಸಿಕೊಂಡರು . ನೀನು ನಮಗಾಗಿ ನಿಶ್ಚಯಿಸಿದ್ದ ಅವಧಿ ನಮಗೆ ತಲುಪಿತು ಎಂದು ಹೇಳುವರು. ಆಗ ಅಲ್ಲಾಹು ಹೇಳುವನು; ನರಕಾಗ್ನಿಯೇ ನಿಮ್ಮ ವಾಸಸ್ಥಾನ ವಾಗಿದ್ದು ಅದರಲ್ಲಿ ನೀವು ಸ್ಥಿರವಾಸಿಗಳು. ಅಲ್ಲಾಹು ಇಚ್ಛಿಸಿದ ಸಮಯದ ಹೊರತು . ನಿಶ್ಚಯವಾಗಿಯೂ ನಿಮ್ಮ ಪ್ರಭು ಮಹಾ ಧೀಮಂತನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(6,129,'6:129','ಈ ರೀತಿ ಅಕ್ರಮಿಗಳ ಕೃತ್ಯಗಳಿಗೆ ಪ್ರತಿಫಲ ವಾಗಿ ನಾವು ಅವರನ್ನು ಪರಸ್ಪರರ ಆತ್ಮ ಮಿತ್ರರ ನ್ನಾಗಿ ಮಾಡುವೆವು.');
INSERT INTO "kannada" VALUES(6,130,'6:130','ಓ ಜಿನ್ನ್ ಮತ್ತು ಮಾನವ ವರ್ಗವೇ, ನಿಮಗೆ ನನ್ನ ಸಂದೇಶಗಳನ್ನು ವಿವರಿಸುತ್ತ ಇಂದಿನ ನಿಮ್ಮ ಭೇಟಿಯ ಕುರಿತು ನಿಮ್ಮನ್ನು ಎಚ್ಚರಿಸ ಲಿಕ್ಕಾಗಿ ನಿಮ್ಮ ಬಳಿಗೆ ನಿಮ್ಮಿಂದಲೇ ಪ್ರವಾದಿ ಗಳು ಬಂದಿರಲಿಲ್ಲವೇ? ಅವರು, `ಹೌದು, ನಮ್ಮ ವಿರುದ್ಧವೇ ನಾವು ಸಾಕ್ಷಿ ಹೇಳುತ್ತೇವೆ ಎನ್ನುವರು. ಇಹಲೋಕ ಜೀವನ ಅವರನ್ನು ವಂಚಿಸಿತು. ಅವರು ಸತ್ಯನಿಷೇಧಿಗಳಾಗಿದ್ದಾರೆಂದು ಸ್ವತಃ ಅವರ ವಿರುದ್ಧವೇ ಅವರು ಸಾಕ್ಷಿಗಳಾಗುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(6,131,'6:131','ನಿಮ್ಮ ಪ್ರಭು, ಒಂದು ನಾಡಿನ ನಿವಾಸಿಗಳನ್ನು ಅವರಿಗೆ ಮಾಹಿತಿ ಕೊಡದ ಸ್ಥಿತಿಯಲ್ಲಿ ಅವರ ಅನ್ಯಾಯದ ಫಲವಾಗಿ ನಾಶಗೊಳಿಸುವವನಾಗಿರಲಿಲ್ಲ ಎಂಬ ಕಾರಣಕ್ಕಾಗಿ ದೂತರನ್ನು ನಿಯೋಗಿಸಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(6,132,'6:132','ಪ್ರತಿಯೊಬ್ಬನಿಗೆ ತನ್ನ ಕರ್ಮಕ್ಕನುಸಾರ ಪದವಿಗಳುಂಟು. ನಿಮ್ಮ ಪ್ರಭು ಜನರ ಕರ್ಮಗಳ ಬಗ್ಗೆ ಅಲಕ್ಷ್ಯನಲ್ಲ.');
INSERT INTO "kannada" VALUES(6,133,'6:133','ನಿಮ್ಮ ಪ್ರಭು ಸ್ವಯಂ ಪರ್ಯಾಪ್ತನು ಮತ್ತು ದಯಾವತ್ಸಲನು. ಅವನಿಚ್ಛಿಸಿದರೆ ನಿಮ್ಮನ್ನು ತೊಲಗಿಸಿ ಬೇರೆ ಜನಾಂಗದ ಸಂತತಿಗಳಿಂದ ನಿಮ್ಮನ್ನು ಉತ್ಪತ್ತಿ ಮಾಡಿದಂತೆ ನಿಮ್ಮ ಬಳಿಕ ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ನಿಮ್ಮ ಪ್ರತಿನಿಧಿ ಮಾಡುವನು.');
INSERT INTO "kannada" VALUES(6,134,'6:134','ನಿಮಗೆ ಮುನ್ನೆಚ್ಚರಿಕೆ ಕೊಡಲಾದ ಶಿಕ್ಷೆಯು ಖಂಡಿತ ಬರುತ್ತದೆ. ನೀವು ಅಲ್ಲಾಹನನ್ನು ಖಂಡಿತ ಮಣಿಸಲಾರಿರಿ.');
INSERT INTO "kannada" VALUES(6,135,'6:135','(ಓ ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ : ಜನರೇ, ನೀವು ನಿಮ್ಮ ಅವಸ್ಥೆಗೆ ತಕ್ಕಂತೆ ಕಾರ್ಯನಿರತರಾಗಿರಿ. ನಾನೂ ನನ್ನ ಅವಸ್ಥೆಗೆ ತಕ್ಕ ಕಾರ್ಯನಿರತ ನಾಗಿರುತ್ತೇನೆ. ಅಂತಿಮ ಬೀಡು ಯಾರಿಗೆ ಹಿತ ಕರವಾಗಿರುವುದೆಂಬುದನ್ನು ನೀವು ತಿಳಿಯ ಲಿರುವಿರಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಕ್ರಮಿಗಳು ಎಂದಿಗೂ ಯಶಸ್ಸು ಪಡೆಯಲಾರರು.');
INSERT INTO "kannada" VALUES(6,136,'6:136','ಇವರು ಅಲ್ಲಾಹನಿಗೆ, ಸ್ವತಃ ಆತನೇ ಉತ್ಪಾ ದಿಸಿದ ಕೃಷಿ ಮತ್ತು ಜಾನುವಾರುಗಳಲ್ಲಿ ಒಂದಂಶವನ್ನು ನಿಶ್ಚಯಿಸಿದ್ದಾರೆ. (ಇನ್ನೊಂದು ಅಂಶವನ್ನು ಮೂರ್ತಿಗಳಿಗೆ ನಿಶ್ಚಯಿಸಿದ್ದಾರೆ.) ಆಮೇಲೆ ಇದು ಅಲ್ಲಾಹನಿಗೆ ಮತ್ತು ಇದು ನಮ್ಮ ದೈವಿಕ ಭಾಗೀದಾರರಿಗೆ ಎಂದು ದುರ್ವಾದ ಹೊಂದುತ್ತಾರೆ. ಇವರು ತಮ್ಮ ಸಹಭಾಗಿಗಳಿಗೆ ನಿಶ್ಚಯಿಸಿದ ಪಾಲು ಅಲ್ಲಾಹನಿಗೆ ತಲುಪು ವುದಿಲ್ಲ. ಆದರೆ ಅಲ್ಲಾಹನಿಗೆ ನಿಶ್ಚಯಿಸಿದ ಪಾಲು ಇವರ ಸಹಭಾಗಿಗಳಿಗೆ ತಲುಪುತ್ತದೆ. ಎಂತಹ ಕೆಟ್ಟ ತೀರ್ಮಾನವಿದು!');
INSERT INTO "kannada" VALUES(6,137,'6:137','ಇದೇ ಪ್ರಕಾರ ಬಹುದೇವಾರಾಧಕರಲ್ಲಿ ಹೆಚ್ಚಿನವರಿಗೆ ಅವರ ಸಹಭಾಗಿಗಳು ತಮ್ಮ ತಮ್ಮ ಮಕ್ಕಳ ಹತ್ಯೆಯನ್ನು ಸುಂದರವಾಗಿ ತೋರಿಸಿದ್ದಾರೆ. ಇದು ಅವರನ್ನು ನಾಶಗೊಳಿಸಲಿಕ್ಕಾಗಿ ಮತ್ತು ಅವರಿಗೆ ಅವರ ಧರ್ಮವನ್ನು ಗೊಂದಲಗೊಳಿಸಲಿಕ್ಕಾಗಿ, ಅಲ್ಲಾಹ ನಿಚ್ಛಿಸುತ್ತಿದ್ದರೆ ಇವರು ಹಾಗೆ ಮಾಡುತ್ತಿರಲಿಲ್ಲ. ಆದುದರಿಂದ ಅವರನ್ನೂ ಅವರ ಕಟ್ಟುಕತೆಗಳನ್ನೂ ಅವಗಣಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(6,138,'6:138','ಇವು ನಿಷೇಧ ಹೇರಿ ತಡೆದಿರಿಸಲಾದ ಜಾನುವಾರು ಮತ್ತು ಬೆಳೆಗಳು. ಇವುಗಳನ್ನು ನಾವು ಇಚ್ಛಿಸಿದವರ ಹೊರತು ಬೇರಾರೂ ತಿನ್ನಬಾರದು ಎಂದು ಅರ್ಥಹೀನ ವಾದದ ಪ್ರಕಾರ ಅವರು ಹೇಳುತ್ತಿದ್ದಾರೆ. ಹಾಗೆಯೇ ಕೆಲವು ಜಾನುವಾ ರುಗಳಲ್ಲಿ ಸವಾರಿಯನ್ನೂ ನಿಷೇಧಿಸಲಾಗಿದೆ. ಅಲ್ಲದೆ ಅಲ್ಲಾಹನ ನಾಮವನ್ನುಚ್ಚರಿಸದೆ ಕೊಯ್ದ ಜಾನುವಾರುಗಳೂ ಇರುತ್ತವೆ. ಇವೆಲ್ಲವೂ ಅವರು ಅಲ್ಲಾಹನ ಮೇಲೆ ಹೊರಿಸಿರುವ ಆರೋಪಗಳು. ಅಲ್ಲಾಹನು ಅವರಿಗೆ ಈ ಸುಳ್ಳಾರೋಪಗಳ ಪ್ರತಿಫಲವನ್ನು ನೀಡಲಿರುವನು.');
INSERT INTO "kannada" VALUES(6,139,'6:139','ಈ ನಿಷೇಧಿತ ಜಾನುವಾರುಗಳ ಹೊಟ್ಟೆ ಯೊಳಗಿರುವುದು ನಮ್ಮ ಪುರುಷರಿಗೆ ಸಮ್ಮತವೂ, ನಮ್ಮ ಸ್ತ್ರೀಯರಿಗೆ ಅದು ನಿಷಿದ್ಧವೂ ಆಗಿರುತ್ತದೆಂದೂ ಅದು ಸತ್ತರೆ ಮಾತ್ರ ಅದರಲ್ಲಿ ಎಲ್ಲರೂ ಪಾಲುದಾರರು ಎಂದೂ ಅವರು ಹೇಳುತ್ತಾರೆ. ಅವರ ಈ ವರ್ಣನೆಗೆ ತಕ್ಕ ಪ್ರತಿ ಫಲವನ್ನು ಅಲ್ಲಾಹನು ಅವರಿಗೆ ಕೊಡುವನು. ನಿಶ್ಚಯವಾಗಿಯೂ ಅವನು ಯುಕ್ತಿವಂತನೂ, ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(6,140,'6:140','ನಿಶ್ಚಯವಾಗಿಯೂ ಯಾವುದೇ ಜ್ಞಾನವಿಲ್ಲದೆ ಮೌಢ್ಯತನದಿಂದ ತಮ್ಮ ಮಕ್ಕಳನ್ನು ವಧಿಸಿ ದವರೂ ಅಲ್ಲಾಹನು ಒದಗಿಸಿದ ಅನ್ನಾಹಾರ ವನ್ನು ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಳ್ಳು ಹೊರಿಸಿ ನಿಷಿದ್ಧ ಗೊಳಿಸಿಕೊಂಡವರೂ ನಷ್ಟ ಹೊಂದಿದರು. ನಿಶ್ಚಯವಾಗಿಯೂ ಅವರು ಪಥಭ್ರಷ್ಟರಾದರು. ಅವರು ಸನ್ಮಾರ್ಗ ಹೊಂದುವವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(6,141,'6:141','ಚಪ್ಪರದಲ್ಲಿ ಹರಡುವ ಹಾಗೂ ಚಪ್ಪರದಲ್ಲಿ ಹರಡದಂತಹ ತೋಟಗಳನ್ನೂ ತರಹೇವಾರಿ ಖರ್ಜೂರ ತೋಟಗಳನ್ನೂ ಫಲೋತ್ಪನ್ನಗಳನ್ನೂ ಪರಸ್ಪರ ಹೋಲಿಕೆಯಿರುವ ಹಾಗೂ ಹೋಲಿಕೆಯಿಲ್ಲದ ದಾಳಿಂಬೆ ಮತ್ತು ಒಲಿವ್ ಹಣ್ಣುಗಳ ನ್ನೂ ಸೃಷ್ಟಿಸಿದವನು ಅಲ್ಲಾಹನೇ. ಅವು ಫಲ ಕೊಟ್ಟಾಗ ಫಲಗಳನ್ನು ತಿನ್ನಿರಿ. ಅವುಗಳ ಕೊಯ್ಲಿನ ದಿನ ಅದರ ಹಕ್ಕನ್ನು ಸಂದಾಯ ಮಾಡಿರಿ. ಆದರೆ ದುವ್ರ್ಯಯ ಮಾಡಬೇಡಿರಿ. ದುವ್ರ್ಯಯ ಮಾಡುವವರನ್ನು ಅಲ್ಲಾಹನು ಮೆಚ್ಚುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(6,142,'6:142','ಜಾನುವಾರುಗಳಲ್ಲಿ ಹೊರೆ ಹೊರುವುದನ್ನೂ ಕೊಯ್ದು ತಿನ್ನುವಂಥದ್ದನ್ನೂ ಉಂಟು ಮಾಡಿದವನು ಅವನೇ. ಅಲ್ಲಾಹನು ನಿಮಗೆ ದಯಪಾಲಿಸಿದ ವಸ್ತುಗಳಿಂದ ತಿನ್ನಿರಿ. ಶೈತಾನನ ಹೆಜ್ಜೆಯನ್ನು ಅನುಸರಿಸಬೇಡಿರಿ. ನಿಜವಾಗಿಯೂ ಅವನು ನಿಮ್ಮ ಪ್ರತ್ಯಕ್ಷ ಶತ್ರು.');
INSERT INTO "kannada" VALUES(6,143,'6:143','ಅಂದರೆ ಎಂಟು ವರ್ಗಗಳನ್ನು ಅವನು ಸೃಷ್ಟಿಸಿದ್ದಾನೆ. ತುಪ್ಪದ ಆಡು ಜಾತಿಯಿಂದ ಎರಡು (ಗಂಡು-ಹೆಣ್ಣು) ಮತ್ತು ಮೇಕೆ ಆಡಿನ ಜಾತಿಯಿಂದ ಎರಡು. (ಓ ಪೈಗಂಬರರೇ,) ಕೇಳಿರಿ- ಅಲ್ಲಾಹನು ನಿಷೇಧಿಸಿರುವುದು ಈ ಎರಡು ವರ್ಗದಿಂದ ಗಂಡನ್ನೇ ಅಥವಾ ಹೆಣ್ಣನ್ನೆ? ಅಥವಾ ಹೆಣ್ಣು ವರ್ಗಗಳ ಹೊಟ್ಟೆಯೊಳಗಿರುವ ಮರಿಗಳನ್ನೇ? ನೀವು ಸತ್ಯವಂತರಾಗಿದ್ದರೆ ಸರಿಯಾದ ಜ್ಞಾನದೊಂದಿಗೆ ನನಗೆ ತಿಳಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(6,144,'6:144','ಒಂಟೆಯ ಎರಡು ಜಾತಿಯನ್ನು ಮತ್ತು ದನದ ಎರಡು ಜಾತಿಯನ್ನು (ಅವನು ಸೃಷ್ಟಿಸಿದನು) ಕೇಳಿರಿ: ಅಲ್ಲಾಹು ನಿಷೇಧಿಸಿರುವುದು ಆ ಎರಡು ಜಾತಿಗಳ ಪೈಕಿ ಗಂಡನ್ನೇ ಅಥವಾ ಹೆಣ್ಣನ್ನೇ ಅಥವಾ ಹೆಣ್ಣು ಜಾತಿಯ ಹೊಟ್ಟೆ ಯೊಳಗಿರುವ ಕರುಗಳನ್ನೇ? ಅಲ್ಲಾಹನು ಇವುಗಳನ್ನು ಭೋಧಿಸಿದ ಸಂದರ್ಭದಲ್ಲಿ ನೀವು ಉಪಸ್ಥಿತರಿದ್ದಿರಾ? ಯಾವುದೇ ಜ್ಞಾನವಿಲ್ಲದೆ ಜನರನ್ನು ತಪ್ಪು ದಾರಿಗೆಳೆಯಲಿಕ್ಕಾಗಿ ಅಲ್ಲಾ ಹನ ಮೇಲೆ ಮಿಥ್ಯಾರೋಪ ಹೊರಿಸುವವನಿಗಿಂತ ದೊಡ್ಡ ಅಕ್ರಮಿ ಯಾರು? ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಅಕ್ರಮಿಗಳಾದ ಜನಾಂಗವನ್ನು ನೇರದಾರಿಗೆ ಸೇರಿಸುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(6,145,'6:145','(ಓ ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ - ನನಗೆ ಬಂದಿರುವ ದಿವ್ಯ ಸಂದೇಶದಲ್ಲಿ ಭಕ್ಷಕನಿಗೆ ಭಕ್ಷ್ಯ ನಿಷಿದ್ಧವಾದುದು ಏನೂ ಇಲ್ಲ. ಅದು ಶವ ಅಥವಾ ಹರಿಯುವ ರಕ್ತ ಅಥವಾ ಹಂದಿ ಮಾಂಸವಾಗಿದ್ದರೆ ಹೊರತು. ಏಕೆಂದರೆ ಅದು ಮ್ಲೇಚ್ಚವಾಗಿದೆ. ಇಲ್ಲವೆ ಅಲ್ಲಾಹು ಅಲ್ಲದವರ ನಾಮೋಚ್ಚಾರ ಮಾಡಿ ಕೊಯ್ಯಲಾದ ಧರ್ಮ ವಿರುದ್ಧ ಪ್ರಾಣಿಯನ್ನು ಹೊರತುಪಡಿಸಿ. ಆದರೆ ಒಬ್ಬನು ಇದನ್ನು ತಿನ್ನಲು ನಿರ್ಭಂಧಿತನಾದರೆ, ಅವನು ಆಜ್ಞೋಲ್ಲಂಘನೆಯ ಉದ್ದೇಶವಿಲ್ಲದ ಮತ್ತು ಅಗತ್ಯದ ಮೇರೆಯನ್ನು ಮೀರದವನೂ ಆಗಿದ್ದರೆ ನಿಶ್ಚಯವಾಗಿಯೂ ನಿಮ್ಮ ಪ್ರಭು ಕ್ಷಮಾಶೀಲನೂ, ದಯಾಳುವೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(6,146,'6:146','ಯಹೂದಿಗಳ ಮೇಲೆ ನಾವು ಉಗುರುಳ್ಳ ಎಲ್ಲ ಪ್ರಾಣಿಗಳನ್ನು ನಿಷೇಧಿಸಿದ್ದೆವು. ದನ ಮತ್ತು ಆಡಿನ ಕೊಬ್ಬನ್ನೂ ನಾವು ಅವರಿಗೆ ನಿಷೇಧಿಸಿದ್ದೆವು. ಅವೆರಡರ ಬೆನ್ನು ಹಾಗೂ ಜಠರದ ಮೇಲಿರುವ ಕೊಬ್ಬಿನ ಹೊರತು. ಅವರ ಅನ್ಯಾಯಕ್ಕಾಗಿ ಅವರಿಗೆ ನಾವು ಕೊಟ್ಟ ಶಿಕ್ಷೆಯಿದು . ನಿಜವಾಗಿಯೂ ನಾವು ಸತ್ಯವಂತರೇ ಹೌದು.');
INSERT INTO "kannada" VALUES(6,147,'6:147','ಅವರು ನಿಮ್ಮನ್ನು ಸುಳ್ಳಾಗಿಸಿದರೆ, ಅವರಿಗೆ ಹೇಳಿರಿ- ನಿಮ್ಮ ಪ್ರಭು ವಿಶಾಲ ಕರುಣೆಯು ಳ್ಳವನು. ಆದರೆ ಅವನ ಶಿಕ್ಷೆಯನ್ನು ಅಪರಾಧಿಗಳಿಂದ ತಡೆಯಲಾಗದು.');
INSERT INTO "kannada" VALUES(6,148,'6:148','ಅಲ್ಲಾಹನು ಇಚ್ಚಿಸುತ್ತಿದ್ದರೆ ನಾವೂ ನಮ್ಮ ಪೂರ್ವಿಕರೂ ಬಹುದೇವಾರಾಧನೆ ಮಾಡುತ್ತಿರ ಲಿಲ್ಲ ಮತ್ತು ನಾವು ಯಾವ ವಸ್ತುಗಳನ್ನೂ ನಿಷಿದ್ಧ ಗೊಳಿಸುತ್ತಿರಲಿಲ್ಲ ಎಂದು ಮುಶ್ರಿಕರು ಹೇಳು ವರು . ಇವರಿಗಿಂತ ಹಿಂದಿನವರೂ ನಮ್ಮ ಯಾತನೆಯನ್ನು ಸವಿಯುವವರೆಗೂ ಪ್ರವಾದಿ ಗಳನ್ನು ಇದೇ ರೀತಿ ಸುಳ್ಳಾಗಿಸಿದ್ದರು. ಹೇಳಿರಿ - ನಿಮ್ಮ ಹತ್ತಿರ ನಮಗಾಗಿ ಹೊರತರುವ ಜ್ಞಾನ ವೇನಾದರೂ ಇದೆಯೇ? ನೀವು ಕೇವಲ ಗುಮಾನಿಯಂತೆ ನಡೆಯುತ್ತೀರಿ ಮತ್ತು ನೀವು ಬರೇ ಸುಳ್ಳನ್ನು ಹೇಳುತ್ತಿರುವಿರಿ.');
INSERT INTO "kannada" VALUES(6,149,'6:149','ಹೇಳಿರಿ: ಪರಿಪೂರ್ಣ ಆಧಾರ ಪ್ರಮಾಣವು ಅಲ್ಲಾಹನಲ್ಲಿದೆ. ಅವನು ಇಚ್ಛಿಸುತ್ತಿದ್ದರೆ ಖಂಡಿತ ವಾಗಿಯೂ ನಿಮ್ಮೆಲ್ಲರನ್ನು ಸನ್ಮಾರ್ಗಕ್ಕೆ ಸೇರಿಸುತ್ತಿದ್ದನು.');
INSERT INTO "kannada" VALUES(6,150,'6:150','ಹೇಳಿರಿ `ಅಲ್ಲಾಹನೇ ಈ ವಸ್ತುಗಳನ್ನು ನಿಷಿದ್ಧ ಗೊಳಿಸಿರುತ್ತಾನೆಂದು ಸಾಕ್ಷ್ಯ ಹೇಳುವ ನಿಮ್ಮ ಸಾಕ್ಷಿಗಳನ್ನು ತನ್ನಿರಿ. ಅವರು ಸಾಕ್ಷಿ ನುಡಿದರೆ ನೀವು ಅವರೊಂದಿಗೆ ಸಾಕ್ಷಿ ಹೇಳಬಾರದು. ನಮ್ಮ ನಿದರ್ಶನಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವ, ಪರಲೋಕದಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸದ ಮತ್ತು ಇತರರನ್ನು ತಮ್ಮ ಪ್ರಭುವಿಗೆ ಸಮಾನರಾಗಿ ಮಾಡುವವರ ತನ್ನಿಚ್ಛೆಗಳನ್ನು ನೀವು ಅನುಸರಿ ಸಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(6,151,'6:151','(ಓ ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ: `ಬನ್ನಿರಿ, ನಿಮ್ಮ ಪ್ರಭು ನಿಮ್ಮ ಮೇಲೆ ನಿಷಿದ್ಧಗೊಳಿಸಿದ್ದನ್ನು ನಾನು ಓದಿ ಹೇಳುವೆನು. ಅವನೊಂದಿಗೆ ಯಾವುದನ್ನೂ ಸಹಭಾಗಿಯನ್ನಾಗಿ ಮಾಡ ಬೇಡಿರಿ.ಮಾತಾಪಿತರಿಗೆ ಗುಣವನ್ನು ಮಾಡಿರಿ ಮತ್ತು ನಿಮ್ಮ ಮಕ್ಕಳನ್ನು ದಾರಿದ್ರ್ಯದ ಭಯದಿಂದ ವಧಿಸಬೇಡಿರಿ. ನಾವು ನಿಮಗೂ ಅವರಿ ಗೂ ಆಹಾರವನ್ನು ನೀಡುತ್ತೇವೆ . ಮತ್ತು ಅಶ್ಲೀಲ ವಿಷಯಗಳನ್ನು ಸಮೀಪಿಸಬೇಡಿರಿ. ಅದು ಪ್ರತ್ಯಕ್ಷವಿರಲಿ ಪರೋಕ್ಷವಿರಲಿ, ಅಲ್ಲಾಹನು ಮಾನ್ಯತೆ ಕಲ್ಪಿಸಿದ ಯಾವ ಜೀವವನ್ನೂ ನ್ಯಾ ಯವಾದ ಕಾರಣವಿಲ್ಲದೆ ಕೊಲೆ ಮಾಡ ಬೇಡಿರಿ. ನೀವು ಯೋಚಿಸಿ ಗ್ರಹಿಸಲಿಕ್ಕಾಗಿ ಅವನು ನಿಮಗೆ ಈ ಆದೇಶಗಳನ್ನು ನೀಡಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(6,152,'6:152','ಅನಾಥರು ಪ್ರಬುದ್ಧರಾಗುವ ತನಕ ಅತ್ಯುತ್ತಮ ರೀತಿಯ ಹೊರತು ಅವರ ಸೊತ್ತಿನ ಬಳಿ ಸುಳಿ ಯಬೇಡಿರಿ. ಅಳತೆ ಮತ್ತು ತೂಕವನ್ನು ನೀತಿ ಯಂತೆ ಪಾಲಿಸಿರಿ. ನಾವು ಯಾವನಿಗೂ ಆತನ ಶಕ್ತಿಗೆ ಮೀರಿದ್ದನ್ನು ಹೇರುವುದಿಲ್ಲ. ನೀವು ಮಾತಾಡುವಾಗ ನ್ಯಾಯಪಾಲಿಸಿರಿ. ಅವರು ಆಪ್ತ ಬಂಧುಗಳಾಗಿದ್ದರೂ ಸರಿ. ಅಲ್ಲಾಹನ ಕರಾರನ್ನು ಪೂರ್ತಿಗೊಳಿಸಿರಿ. ನೀವು ಚಿಂತಿಸಲೆಂದು ಅಲ್ಲಾಹನು ಈ ಉಪದೇಶವನ್ನು ನಿಮಗೆ ನೀಡಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(6,153,'6:153','ಇದೇ ನನ್ನ ಋಜುವಾದ ಮಾರ್ಗ. ಅದನ್ನು ಹಿಂಬಾಲಿಸಿರಿ. ಅದರ ವಿರುದ್ಧ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯದಿರಿ. ಏಕೆಂದರೆ ಅವು ಅವನ ಧರ್ಮ ಮಾರ್ಗದಿಂದ ನಿಮ್ಮನ್ನು ಶಿಥಿಲಗೊಳಿಸು ವುದು. ನೀವು ಭಯಭಕ್ತಿಯಿಂದ ಬದುಕಲಿಕ್ಕಾಗಿ ಅವನು ಈ ಆಜ್ಞೆಯನ್ನು ನೀಡಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(6,154,'6:154','ಅನಂತರ ನಾವು ಮೂಸಾರಿಗೆ ಗ್ರಂಥ ವನ್ನು ದಯಪಾಲಿಸಿದ್ದೆವು. ಇದನ್ನು ಅನುಸರಿಸಿ ಸನ್ನಡೆ ಕೈಗೊಳ್ಳುವವರಿಗೆ ಅನುಗ್ರಹವನ್ನು ಪರಿಪೂರ್ಣಗೊಳಿಸಲಿಕ್ಕಾಗಿಯೂ ಅಗತ್ಯವುಳ್ಳ ಪ್ರತಿಯೊಂದು ವಿಷಯದ ವಿವರಣೆ ನೀಡಲಿಕ್ಕಾಗಿಯೂ ಮಾರ್ಗದರ್ಶನ ಹಾಗೂ ವರದಾನವಾಗಿ ಬಂದಿದೆ. ಅದು ಇಸ್ರಾಈಲ್ ಜನಾಂಗವು ತಮ್ಮ ಪ್ರಭುವಿನ ಭೇಟಿಯ ಮೇಲೆ ವಿಶ್ವಾಸ ಕಲ್ಪಿಸುವ ಸಲುವಾಗಿ.');
INSERT INTO "kannada" VALUES(6,155,'6:155','ಮಂಗಳದಾಯಕವಾದ ಈ ಗ್ರಂಥವನ್ನು ನಾವೇ ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದೇವೆ. ಆದುದರಿಂದ ನೀವು ಇದನ್ನು ಅನುಸರಿಸಿ ನಡೆಯಿರಿ. ಮತ್ತು ಜಾಗ್ರತೆ ಪಾಲಿಸಿರಿ. ನೀವು ಅನುಗ್ರಹಕ್ಕೆ ಪಾತ್ರರಾಗುವಿರಿ.');
INSERT INTO "kannada" VALUES(6,156,'6:156','ನಮಗಿಂತ ಮುಂಚಿನ ಎರಡು ಪಂಗಡಗಳಿಗೆ ಮಾತ್ರ ವೇದಗ್ರಂಥವನ್ನು ಇಳಿಸಲಾಗಿತ್ತು. ಅದನ್ನು ಓದಿ ಕಲಿಯಲು ನಮಗೆ ತಿಳಿದಿರಲಿಲ್ಲ ಎಂದೂ ಈಗ ನೀವು ಹೇಳಿ ಬಿಡದಿರಲಿಕ್ಕಾಗಿ. (ಇದನ್ನು ಅವತೀರ್ಣಗೊಳಿಸಲಾಗಿದೆ)');
INSERT INTO "kannada" VALUES(6,157,'6:157','ನಮ್ಮ ಮೇಲೆ ಗ್ರಂಥ ಅವತೀರ್ಣಗೊಳಿಸ ಲಾಗುತ್ತಿದ್ದರೆ ನಾವು ಅವರಿಗಿಂತಲೂ ಹೆಚ್ಚು ಸನ್ಮಾರ್ಗಿಗಳಾಗುತ್ತಿದ್ದೆವೆಂಬ ಮಾತನ್ನು ನೀವು ಹೇಳದಿರಲಿಕ್ಕಾಗಿ. ನಿಮ್ಮ ಬಳಿಗೆ ನಿಮ್ಮ ಪ್ರಭು ವಿನ ಕಡೆಯಿಂದ ಒಂದು ಸುವ್ಯಕ್ತ ಪ್ರಮಾಣ, ಮಾರ್ಗದರ್ಶನ ಮತ್ತು ಕೃಪೆಯು ಬಂದಿರು ತ್ತದೆ. ಅದಾಗ್ಯೂ ಅಲ್ಲಾಹನ ದೃಷ್ಟಾಂತಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವ ಮತ್ತು ಅವುಗಳಿಂದ ವಿಮುಖ ನಾಗುವವನಿಗಿಂತ ದೊಡ್ಡ ಅಕ್ರಮಿ ಯಾರಿ ದ್ದಾರೆ? ನಮ್ಮ ನಿದರ್ಶನಗಳಿಂದ ವಿಮುಖರಾ ಗುವವರಿಗೆ ಅವರ ವಿಮುಖತೆಯ ಫಲವಾಗಿ ನಾವು ಅತ್ಯಂತ ಕೆಟ್ಟ ಶಿಕ್ಷೆಯನ್ನು ಕೊಡಲಿದ್ದೇವೆ .');
INSERT INTO "kannada" VALUES(6,158,'6:158','ಅವರ ಬಳಿ ದೇವಚರರು ಬಂದು ನಿಲ್ಲಬೇ ಕೆಂದು ಅಥವಾ ನಿಮ್ಮ ಪ್ರಭು (ವಿನ ಶಿಕ್ಷೆ) ಇಳಿ ದು ಬರಬೇಕೆಂದು ಅಥವಾ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕೆಲವು ದೃಷ್ಟಾಂತಗಳು ತೋರಿಬರಬೇಕೆಂದು ಇವರು ನಿರೀಕ್ಷಿಸುತ್ತಿರುವರೇ? ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕೆಲವು ನಿದರ್ಶನಗಳು ಬರುವ ದಿನ, ಅದಕ್ಕೆ ಮೊದಲೇ ಸತ್ಯವಿಶ್ವಾಸ ಹೊಂದಿರದ ಅಥವಾ ಸತ್ಯವಿಶ್ವಾಸದ ಜೊತೆಗೆ ಏನಾದರೂ ಪುಣ್ಯ ಸಂಪಾದಿಸಿರದ ಯಾವನಿಗೂ ತನ್ನ ವಿಶ್ವಾಸ ಫಲಕಾರಿಯಾಗದು. (ಓ ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ: ನೀವು ಕಾದು ನೋಡಿರಿ. ಖಂಡಿತಾ ನಾವೂ ಕಾದಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(6,159,'6:159','ತಮ್ಮ ಧರ್ಮವನ್ನು ವಿಂಗಡಿಸಿ ವಿಭಿನ್ನ ಪಕ್ಷ ಗಳಲ್ಲಿ ಹರಿಹಂಚಾದವರೊಂದಿಗೆ ನಿಶ್ಚಯವಾ ಗಿಯೂ ನಿಮಗೆ ಯಾವ ಸಂಬಂಧವೂ ಇಲ್ಲ. ಅವರ ಕಾರ್ಯ ಅಲ್ಲಾಹನಿಗೆ ಬಿಟ್ಟದ್ದು. ಅವರು ಮಾಡುತ್ತಿದ್ದುದರ ಕುರಿತು ಅವನೇ ಅವರಿಗೆ ವಿವರ ಕೊಡುವನು .');
INSERT INTO "kannada" VALUES(6,160,'6:160','ಯಾರಾದರೂ ಸುಕೃತವನ್ನು ತಂದರೆ, ಆತನಿಗೆ ಅದರ ಹತ್ತು ಪಟ್ಟು ಪುಣ್ಯ ಫಲವಿದೆ. ಯಾರಾ ದರೂ ಪಾಪವನ್ನು ತಂದರೆ, ಅದರಷ್ಟೇ ಹೊರತು ಪ್ರತಿಫಲ ಕೊಡಲಾಗುವುದಿಲ್ಲ. ಯಾರ ಮೇಲೂ ಅನ್ಯಾಯವಾಗದು .');
INSERT INTO "kannada" VALUES(6,161,'6:161','(ಓ ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ "ನನ್ನ ಪ್ರಭು ನನಗೆ ನೇರ ಮಾರ್ಗವನ್ನು ತೋರಿಸಿದ್ದಾನೆ. ಯಾವುದೇ ವಕ್ರತೆಯಿಲ್ಲದ ಅತ್ಯಂತ ಸರಿಯಾದ ಧರ್ಮ, ಋಜುಮಾನಸರಾದ ಇಬ್‍ರಾಹೀಮರು ಕೈಗೊಂಡ ಮಾರ್ಗ - ಅವರಂತೂ ಬಹು ದೇವಾರಾಧಕರಾಗಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(6,162,'6:162','ಹೇಳಿರಿ; ನನ್ನ ನಮಾಝ್, ನನ್ನ ಬಲಿ ಕರ್ಮ ನನ್ನ ಜೀವನ ಮತ್ತು ನನ್ನ ಮರಣ ಎಲ್ಲವೂ ಸರ್ವಲೋಕ ಪಾಲಕನಾದ ಅಲ್ಲಾಹನಿಗೆ ಮೀಸಲು.');
INSERT INTO "kannada" VALUES(6,163,'6:163','ಆತನಿಗೆ ಯಾರೂ ಪಾಲುದಾರರಿಲ್ಲ. ಇದನ್ನೇ ನನಗೆ ಆದೇಶಿಸಲಾಗಿದೆ. ನಾನು ಮುಸ್ಲಿಮ (ಶರಣಾಗತ)ರಲ್ಲಿ ಪ್ರಥಮನಾಗಿರುತ್ತೇನೆ .');
INSERT INTO "kannada" VALUES(6,164,'6:164','ಹೇಳಿರಿ: ಅಲ್ಲಾಹನೇ ಸಕಲ ವಸ್ತುಗಳ ಪ್ರಭುವಾ ಗಿರುವಾಗ, ನಾನು ಅವನ ಹೊರತು ಇತರರನ್ನು ಪ್ರಭುವಾಗಿ ಅರಸಬೇಕೇ? ಪ್ರತಿಯೊಬ್ಬನೂ ತಾನು ಸಂಪಾದಿಸುತ್ತಿರುವುದಕ್ಕೆ ಸ್ವತಃ ತಾನೇ ಹೊಣೆಗಾರನಾಗಿರುತ್ತಾನೆ. ಯಾವ ಹೊರೆ ಹೊರು ವಾತನೂ ಇನ್ನೊಬ್ಬನ ಹೊರೆಯನ್ನು ಹೊರುವುದಿಲ್ಲ . ಆ ಬಳಿಕ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಗೇ ನಿಮ್ಮ ನಿರ್ಗಮನ. ಆಗ ಅವನು ನೀವು ಭಿನ್ನಾ ಭಿಪ್ರಾಯ ಹೊಂದಿದ್ದ ಸಂಗತಿಗಳ ಬಗ್ಗೆ ನಿಮಗೆ ಮಾಹಿತಿ ಕೊಡುವನು.');
INSERT INTO "kannada" VALUES(6,165,'6:165','ಅವನೇ ನಿಮ್ಮನ್ನು ಭೂಮಿಯ ಪ್ರತಿನಿಧಿಗಳನ್ನಾಗಿ ಮಾಡಿದನು ಮತ್ತು ನಿಮ್ಮಲ್ಲಿ ಕೆಲವರಿಗೆ ಕೆಲವರಿಗಿಂತ ಉನ್ನತ ಪದವಿಗಳನ್ನು ನೀಡಿದನು. ಅವನು ನಿಮಗೆ ಕೊಟ್ಟಿರುವುದರಲ್ಲಿ ನಿಮ್ಮನ್ನು ಪರೀಕ್ಷಿಸಲಿಕ್ಕಾಗಿ ನಿಶ್ಚಯವಾಗಿಯೂ ನಿಮ್ಮ ಪ್ರಭು ಶಿಕ್ಷೆ ಕೊಡುವುದರಲ್ಲಿ ಅತಿ ಶೀಘ್ರನು ಮತ್ತು ಅತ್ಯಂತ ಕ್ಷಮಾಶೀಲನೂ ಕೃಪಾ ನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(7,1,'7:1','ಅಲಿಫ್ ಲಾಮ್ಮೀಮ್ ಸ್ವಾದ್ .');
INSERT INTO "kannada" VALUES(7,2,'7:2','ಇದು ನಿಮ್ಮೆಡೆಗೆ ಅವತೀರ್ಣಗೊಳಿಸಲಾದ ದಿವ್ಯ ಗ್ರಂಥ. ಆದುದರಿಂದ (ಓ ಪೈಗಂಬರರೇ) ಇದರಿಂದ ನಿಮ್ಮ ಹೃದಯದಲ್ಲಿ ಅಳುಕುಂಟಾಗದಿರಲಿ . ಈ ಗ್ರಂಥದ ಉದ್ದೇಶವು ನೀವು ಇದರ ಮೂಲಕ ಎಚ್ಚರಿಕೆ ನೀಡಬೇಕು ಮತ್ತು ವಿಶ್ವಾಸಿಗಳಿಗೆ ಉಪದೇಶ ನೀಡಬೇಕು ಎನ್ನುವುದಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(7,3,'7:3','(ಓ ಜನರೇ,) ನಿಮ್ಮ ಪ್ರಭುವಿನಿಂದ ಅವತೀರ್ಣ ಗೊಂಡುದನ್ನು (ಖುರ್‍ಆನನ್ನು) ಅನುಸರಿಸಿರಿ ಮತ್ತು ಅವನನ್ನು ಬಿಟ್ಟು ಇತರ ರಕ್ಷಕ ಮಿತ್ರರನ್ನಾಗಿ ಮಾಡಿ ಅನುಸರಿಸಬೇಡಿರಿ. ಆದರೆ ನೀವು ಬಹಳ ಕಡಿಮೆ ಯೋಚಿಸುತ್ತಿರುವಿರಿ.');
INSERT INTO "kannada" VALUES(7,4,'7:4','ಎಷ್ಟೋ ನಾಡುಗಳನ್ನು ನಾವು ನಾಶಪಡಿಸಿದೆವು. ಆಗ ನಾವು ಅವರಿಗೆ ವಿಧಿಸಿದ ಶಿಕ್ಷೆಯು ರಾತ್ರಿ ಯೋ, ಮಧ್ಯಾಹ್ನ ನಿದ್ದೆಯ ವೇಳೆಯೋ ಬಂದೆರಗಿತು.');
INSERT INTO "kannada" VALUES(7,5,'7:5','ಆ ರೀತಿ ನಮ್ಮ ಶಿಕ್ಷೆ ಅವರಿಗೆ ಬಂದಾಗ `ನಿಜಕ್ಕೂ ನಾವು ಅಕ್ರಮಿಗಳಾಗಿದ್ದೆವಲ್ಲ’ ಎನ್ನುವುದಾಗಿತ್ತು ಅವರ ಆರ್ತ ನುಡಿ.');
INSERT INTO "kannada" VALUES(7,6,'7:6','ಆದ್ದರಿಂದ ಯಾರ ಕಡೆಗೆ ನಾವು ಸಂದೇಶ ವಾಹಕರನ್ನು ಕಳುಹಿಸಿದ್ದೇವೆಯೋ ಅವರನ್ನೆಲ್ಲಾ ನಾವು ಖಂಡಿತ ವಿಚಾರಣೆಗೊಳಪಡಿಸಲಿದ್ದೇವೆ. ಮತ್ತು ಸಂದೇಶವಾಹಕರನ್ನೂ (ಅವರ ದೌತ್ಯ ನಿರ್ವಹಣೆಯ ಬಗ್ಗೆ) ವಿಚಾರಣೆ ನಡೆಸಲಿದ್ದೇವೆ .');
INSERT INTO "kannada" VALUES(7,7,'7:7','ಆಗ ನಾವು ಸಂಪೂರ್ಣ ಜ್ಞಾನದೊಂದಿಗೆ ಎಲ್ಲವನ್ನೂ ಅವರಿಗೆ ವಿವರಿಸುವೆವು. ನಾವೆಂದೂ ಅಗೋಚರವಾಗಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(7,8,'7:8','ನೀತಿ ನಿಷ್ಠೆಯ ತೂಕವು ಅಂದು ನಡೆಯಲಿರುವುದು. ಯಾರ ತೂಕವು ಭಾರ ತೂಗುವುದೋ ಅವರೇ ಜಯಜೀತರು.');
INSERT INTO "kannada" VALUES(7,9,'7:9','ಮತ್ತು ಯಾರ ಭಾರವು ಕಡಿಮೆಯಾಯಿತೋ ಅವರೇ ಸ್ವಯಂ ತಮ್ಮನ್ನು ನಷ್ಟಕ್ಕೊಳಪಡಿಸಿಕೊಂಡವರು. ಅವರು ನಮ್ಮ ನಿದರ್ಶನಗಳನ್ನು ನಿಷೇಧಿಸುತ್ತಿದ್ದ ಕಾರಣವಾಗಿ.');
INSERT INTO "kannada" VALUES(7,10,'7:10','ನಾವು ನಿಮಗೆ ಭೂಮಿಯಲ್ಲಿ ಎಲ್ಲ ಸೌಕರ್ಯ ಗಳನ್ನೂ ಒದಗಿಸಿದೆವು. ಹಾಗೂ ಎಲ್ಲ ಜೀವನಾವ ಶ್ಯಕ ವಸ್ತುಗಳನ್ನು ಸಿದ್ಧಗೊಳಿಸಿದೆವು. ಆದರೆ ನೀವು ಅತ್ಯಲ್ಪವೇ ಕೃತಜ್ಞತೆ ತೋರಿಸುತ್ತಿರುವಿರಿ.');
INSERT INTO "kannada" VALUES(7,11,'7:11','ನಿಜಕ್ಕೂ ನಿಮ್ಮನ್ನು ನಾವು ಸೃಷ್ಟಿಸಿದೆವು. ಅನಂತರ ನಾವು ನಿಮಗೆ ರೂಪ ಕೊಟ್ಟೆವು. ನಂತರ ಆದಮ ನಿಗೆ ಸಾಷ್ಟಾಂಗವೆರಗಿರಿ ಎಂದು ದೇವಚರ ರೊಡನೆ ಹೇಳಿದೆವು. ಆಗ ಎಲ್ಲರೂ ಸಾಷ್ಟಾಂಗವೆರಗಿದರು. ಆದರೆ ಇಬ್ಲೀಸನು ಸಾಷ್ಟಾಂಗ ಮಾಡುವವರೊಂದಿಗೆ ಸೇರಲಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(7,12,'7:12','ಅಲ್ಲಾಹನು ಕೇಳಿದ; ‘ನಾನು ನಿನಗೆ ಆದೇಶ ಕೊಟ್ಟಿದ್ದಾಗ, ಸುಜೂದ್ ಮಾಡುವುದರಿಂದ ನಿನ್ನನ್ನು ತಡೆದ ವಿಷಯ ಯಾವುದು?’ ಆಗ ಇಬ್ಲೀಸನು, ‘ನಾನು ಅವನಿಗಿಂತ ಶ್ರೇಷ್ಟನಾಗಿರುತ್ತೇನೆ. ನೀನು ನನ್ನನ್ನು ಅಗ್ನಿಯಿಂದ ಮತ್ತು ಅವನನ್ನು ಕೊಜೆ ಮಣ್ಣಿನಿಂದ ಸೃಷ್ಟಿಸಿರುತ್ತಿ’ ಎಂದು ಹೇಳಿದನು.');
INSERT INTO "kannada" VALUES(7,13,'7:13','ಆಗ ಅಲ್ಲಾಹನು ಆಜ್ಞಾಪಿಸಿದ : ನೀನು ಇಲ್ಲಿಂದ ಇಳಿದು ಹೋಗು. ಇಲ್ಲಿ ಮೇಲ್ಮೆಯ ಅಹಂಕಾರ ಪಡುವುದು ನಿನಗೆ ಯುಕ್ತವಲ್ಲ. ಆದ್ದರಿಂದ ತೊಲಗು, ಖಂಡಿತ ನೀನು ನಿಂದ್ಯರಲ್ಲೊಬ್ಬನಾಗಿರುವಿ .');
INSERT INTO "kannada" VALUES(7,14,'7:14','ಆಗ ಅವನು, ‘ಜನರನ್ನು ಇನ್ನೊಮ್ಮೆ ಎಚ್ಚರಗೊಳಿಸಲ್ಪಡುವ ದಿನದವರೆಗೆ ನನಗೆ ಅವಧಿ ಕೊಡು’ ಎಂದನು.');
INSERT INTO "kannada" VALUES(7,15,'7:15','ಆಗ ಅಲ್ಲಾಹನ ನುಡಿ : ಖಂಡಿತ ‘ಅವಧಿ ಮುಂದುವರಿಸಲ್ಪಟ್ಟವರ ಕೂಟದಲ್ಲಿ ನೀನೂ ಒಬ್ಬನಾಗಿದ್ದೀಯಾ’.');
INSERT INTO "kannada" VALUES(7,16,'7:16','ಆಗ ಅವನು, ‘ಹಾಗಾದರೆ ನೀನು ನನ್ನನ್ನು ಪಥ ಭ್ರಷ್ಟತೆಗೊಳಪಡಿಸಿದಂತೆಯೇ ಖಂಡಿತ ನಾನು ನಿನ್ನ ನೇರಮಾರ್ಗದಲ್ಲಿ ಈ ಮಾನವರ ನಡೆಗೆ ತಡೆಯೊಡ್ಡುತ್ತಿರುವೆನು.');
INSERT INTO "kannada" VALUES(7,17,'7:17','ಅವರ ಮುಂದಿನಿಂದಲೂ ಅವರ ಹಿಂದಿನಿಂದಲೂ ಅವರ ಬಲಭಾಗದಿಂದಲೂ ಎಡಭಾಗದಿಂದಲೂ ಅವರನ್ನು ಅಡ್ಡಗಟ್ಟುವೆನು. ನೀನು ಅವರ ಪೈಕಿ ಬಹುಮಂದಿಯನ್ನು ಕೃತಜ್ಞರನ್ನಾಗಿ ಕಾಣಲಾರೆ’ ಎಂದನು.');
INSERT INTO "kannada" VALUES(7,18,'7:18','ಅಲ್ಲಾಹನು ಹೀಗೆಂದನು: “ನೀನು ಇಲ್ಲಿಂದ ನಿಂದ್ಯನೂ ಬಹಿಷ್ಕøತನೂ ಆಗಿ ತೊಲಗು. ಅವರ ಪೈಕಿ ಯಾರು ನಿನ್ನನ್ನು ಅನುಸರಿಸುತ್ತಾರೋ ನಿಮ್ಮ ನ್ನೆಲ್ಲ ನಿಸ್ಸಂದೇಹವಾಗಿ ನರಕಕ್ಕೆ ತಳ್ಳುವೆನು .');
INSERT INTO "kannada" VALUES(7,19,'7:19','ಓ ಆದಮ್, ನೀನು ಮತ್ತು ನಿನ್ನ ಪತ್ನಿ - ಇಬ್ಬರೂ ಈ ಸ್ವರ್ಗದಲ್ಲಿ ವಾಸಿಸಿರಿ. ನೀವಿಲ್ಲಿ ನಿಮಗೆ ಇಷ್ಟ ಬಂದುದನ್ನು ಉಣ್ಣಿರಿ. ಆದರೆ ಈ ವೃಕ್ಷದ ಬಳಿ ಮಾತ್ರ ಸುಳಿಯಬಾರದು. ತಪ್ಪಿದರೆ ನೀವಿಬ್ಬರೂ ಅಕ್ರಮಿಗಳ ಸಾಲಿಗೆ ಸೇರುವಿರಿ”.');
INSERT INTO "kannada" VALUES(7,20,'7:20','ಅನಂತರ, ಅವರಿಂದ ಮುಚ್ಚಲ್ಪಟ್ಟ ಅವರ ಲಜ್ಜಾಂ ಗಗಳನ್ನು ಅವರಿಗೆ ಅನಾವರಣ ಗೊಳಿಸಲೆಂದು ಶೈತಾನನು ಅವರಿಬ್ಬರನ್ನು ಚಾಂಚಲ್ಯಕ್ಕೊಳ ಪಡಿಸಿದನು. ಅವನು ಅವರೊಡನೆ, ‘ನಿಮ್ಮ ಪ್ರಭು ನಿಮ್ಮನ್ನು ಈ ವೃಕ್ಷದಿಂದ ತಡೆದಿರುವುದು ನೀವಿ ಬ್ಬರು ದೇವಚರರಾಗಿ ಬಿಡಬಾರದು ಅಥವಾ ನೀವು ಶಾಶ್ವತವಾಗಿ ನೆಲೆಸುವಂತಾಗಬಾರದು ಎಂಬ ಕಾರಣದಿಂದ ಮಾತ್ರ’ ಎಂದನು.');
INSERT INTO "kannada" VALUES(7,21,'7:21','ಖಂಡಿತಾ ನಾನು ನಿಮ್ಮ ನೈಜ ಹಿತಚಿಂತಕ ನಾಗಿದ್ದೇನೆಂದು ಅವರೊಡನೆ ಆಣೆ ಹಾಕಿ ಹೇಳಿದನು .');
INSERT INTO "kannada" VALUES(7,22,'7:22','ಹೀಗೆ ಅವನು ಅವರಿಬ್ಬರನ್ನೂ ಮೋಸದಿಂದ ಬೀಳಿಸಿದನು. ಕೊನೆಗೆ ಅವರಿಬ್ಬರೂ ಆ ವೃಕ್ಷ (ಫಲ)ವನ್ನು ಸವಿದಾಗ ಅವರ ಗುಪ್ತಾಂಗಗಳು ಅವರಿಬ್ಬರಿಗೆ ಪರಸ್ಪರ ಕಾಣಿಸಿಕೊಂಡವು ಮತ್ತು ಅವರಿಬ್ಬರೂ ತಮ್ಮ ದೇಹಗಳಿಗೆ ಸ್ವರ್ಗದ ಎಲೆಗ ಳನ್ನು ಒತ್ತಿ ಹಿಡಿದು ಕೊಂಡರು. ಆಗ ಅವರ ಪ್ರಭು ಅವರನ್ನು ಕರೆದು, ‘ನಾನು ನಿಮ್ಮನ್ನು ಈ ವೃಕ್ಷದಿಂದ ತಡೆದಿರಲಿಲ್ಲವೇ? ಹಾಗೂ ಶೈತಾ ನನು ನಿಮ್ಮ ಪ್ರತ್ಯಕ್ಷ ಶತ್ರುವೆಂದು ನಿಮಗೆ ಹೇಳಿ ರಲಿಲ್ಲವೇ?’ ಎಂದು ಕೇಳಿದನು.');
INSERT INTO "kannada" VALUES(7,23,'7:23','ಅವರಿಬ್ಬರೂ, ‘ಓ ನಮ್ಮ ಪ್ರಭೂ, ನಾವು ಆತ್ಮ ದ್ರೋಹ ಮಾಡಿಕೊಂಡೆವು, ಆದ್ದರಿಂದ ನೀನು ನಮ್ಮನ್ನು ಕ್ಷಮಿಸದಿದ್ದರೆ ಹಾಗೂ ನಮ್ಮ ಮೇಲೆ ಕೃಪೆ ತೋರದಿದ್ದರೆ ನಿಶ್ಚಯವಾಗಿಯೂ ನಾವು ನಷ್ಟ ಹೊಂದುವವರ ಸಾಲಿಗೆ ಸೇರುವೆವು’ ಎಂದು ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(7,24,'7:24','ಆಗ ಅಲ್ಲಾಹನು ಹೇಳಿದನು; ‘ಇಳಿದು ಬಿಡಿರಿ, ನೀವು ಪರಸ್ಪರ ಶತ್ರುಗಳಾಗಿರುತ್ತೀರಿ ಮತ್ತು ನಿಮಗಾಗಿ ಒಂದು ನಿರ್ದಿಷ್ಟ ಕಾಲಾವಧಿಯವರೆಗೆ ಭೂಮಿಯಲ್ಲಿ ವಾಸಸ್ಥಾನ ಮತ್ತು ಸುಖ ಭೋಗಗಳಿವೆ .');
INSERT INTO "kannada" VALUES(7,25,'7:25','`ಅಲ್ಲಿ ನೀವು ಬಾಳುವಿರಿ. ಆಮೇಲೆ ಸಾಯುವಿರಿ. ಕೊನೆಗೆ ಅಲ್ಲಿಂದಲೇ ನಿಮ್ಮನ್ನು ಎಬ್ಬಿಸಲಾಗು ವುದು’ ಎಂದೂ ಹೇಳಿದನು.');
INSERT INTO "kannada" VALUES(7,26,'7:26','ಓ ಆದಮನ ಸಂತತಿಯೇ, ನಿಮ್ಮ ದೇಹದ ಗುಹ್ಯ ಭಾಗಗಳನ್ನು ಮರೆಸುವ ಉಡುಪನ್ನೂ, ಅಲಂಕಾರದ ಮೇಲುಡುಪನ್ನೂ ನಿಮಗೆ ನಾವು ಇಳಿಸಿರುತ್ತೇವೆ. ಆದರೆ ಭಯ ಭಕ್ತಿಯ ಉಡು ಪೆಂದರೆ ಅದೇ ಉತ್ತಮ. ಇದು ಅಲ್ಲಾಹನ ದೃಷ್ಟಾಂತಗಳಲ್ಲೊಂದು. ಅವರು ಪಾಠ ಕಲಿತು ವಿಶ್ವಾಸ ವಿರಿಸಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(7,27,'7:27','ಓ ಆದಮರ ಸಂತತಿಯೇ, ಶೈತಾನನು ನಿಮ್ಮ ಮಾತಾಪಿತರನ್ನು ಅವರ ಲಜ್ಜಾಂಗಗಳು ಅವರಿಗೆ ಗೋಚರಿಸುವಂತೆ ಮಾಡಲು ಅವರ ಉಡುಪ ನ್ನು ಅವರ ಮೇಲಿಂದ ಕಳಚಿದ ಸ್ಥಿತಿಯಲ್ಲಿ ಸ್ವರ್ಗದಿಂದ ಹೊರಹಾಕಿದಂತೆ ನಿಮ್ಮನ್ನು ದಾರಿ ತಪ್ಪಿಸದಿರಲಿ. ಅವನು ಮತ್ತು ಅವನ ಪಡೆಗಳು ನಿಮ್ಮನ್ನು ನೋಡುವರು. ಆದರೆ ನೀವು ಅವರನ್ನು ನೋಡಲಾಗದ ಸ್ಥಿತಿಯಲ್ಲಿ. ನಾವು ಶೈತಾನರನ್ನು ಅವಿಶ್ವಾಸಿಗಳ ಮಿತ್ರರಾಗಿ ಮಾಡಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(7,28,'7:28','ಇವರು ಯಾವುದಾದರೊಂದು ಅಶ್ಲೀಲ ಕಾರ್ಯ ವೆಸಗಿದಾಗ, ‘ನಮ್ಮ ಪೂರ್ವಿಕರು ಹೀಗೆ ಮಾಡು ವುದನ್ನು ನಾವು ಕಂಡಿದ್ದೇವೆ ಮತ್ತು ಅಲ್ಲಾಹನೇ ನಮಗೆ ಹೀಗೆ ಮಾಡುವ ಆದೇಶ ಕೊಟ್ಟಿದ್ದಾನೆ’ ಎಂದು ಹೇಳುತ್ತಾರೆ. ಹೇಳಿರಿ; ಅಲ್ಲಾಹನು ಅಶ್ಲೀಲ ಕಾರ್ಯಗಳನ್ನು ಮಾಡುವಂತೆ ಆದೇಶ ಕೊಡಲಾರ . ಅಲ್ಲಾಹನ ಮೇಲೆ ನೀವೇಕೆ ನಿಮಗರಿವಿಲ್ಲದನ್ನು ಹೇಳುತ್ತೀರಿ?');
INSERT INTO "kannada" VALUES(7,29,'7:29','(ಓ ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ. ನನ್ನ ಪ್ರಭು ನ್ಯಾಯಪಾಲನೆಯ ಆಜ್ಞೆಯನ್ನಿತ್ತಿದ್ದಾನೆ . ಪ್ರತಿಯೊಂದು ಸಾಷ್ಟಾಂಗದ ಸಂದರ್ಭದಲ್ಲಿ ನಿಮ್ಮ ಮುಖಗಳನ್ನು ಸ್ಥಿರಗೊಳಿಸಬೇಕೆಂದೂ ಧರ್ಮವನ್ನು (ಶಿರ್ಕ್‍ನಿಂದ) ನಿಷ್ಕಳಂಕಗೊಳಿಸಿ ಅವನನ್ನೇ ಆರಾಧಿಸಬೇಕೆಂದೂ ಅವನ ಆಜ್ಞೆಯಾಗಿರುತ್ತದೆ . ನಿಮ್ಮನ್ನು ಪ್ರಥಮವಾಗಿ ಹೇಗೆ ಸೃಷ್ಟಿಸಲಾಯಿತೋ ಅದೇ ಸ್ಥಿತಿಯಲ್ಲಿ ನಿಮ್ಮನ್ನು ಮರಳಿಸಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(7,30,'7:30','ಅವನು ಒಂದು ವಿಭಾಗವನ್ನು ನೇರ ಮಾರ್ಗಕ್ಕೆ ಹಚ್ಚಿದನು. ಇನ್ನೊಂದು ವಿಭಾಗದ ಮೇಲೆ ಪಥಭ್ರಷ್ಟತೆ ನಿಜಗೊಂಡಿದೆ. ಏಕೆಂದರೆ, ಅವರು ಅಲ್ಲಾಹನ ಬದಲಿಗೆ ಶೈತಾನರನ್ನು ತಮ್ಮ ಮಿತ್ರರಾಗಿ ಮಾಡಿಕೊಂಡಿರುತ್ತಾರೆ ಹಾಗೂ ತಾವು ನೇರ ಮಾರ್ಗದಲ್ಲಿದ್ದೇವೆಂದು ಅವರು ಭಾವಿಸಿ ಕೊಂಡಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(7,31,'7:31','ಓ ಆದಮರ ಸಂತತಿಯವರೇ, ಎಲ್ಲ ನಮಾಝ್‍ಗಳ ವೇಳೆ ನಿಮ್ಮ ಗೌಪ್ಯ ಪಾಲನೆಯನ್ನು ಅನುಸರಿಸಿರಿ . ಉಣ್ಣಿರಿ, ಕುಡಿಯಿರಿ ಮತ್ತು ದುವ್ರ್ಯಯ ಮಾಡದಿರಿ. ದುವ್ರ್ಯಯ ಮಾಡುವವರನ್ನು ಅಲ್ಲಾಹನು ಮೆಚ್ಚುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(7,32,'7:32','(ಓ ಪೈಗಂಬರರೇ,) ಕೇಳಿರಿ, ಅಲ್ಲಾಹನು ತನ್ನ ದಾಸರಿಗಾಗಿ ಹೊರತೆಗೆದಿರುವ ಅಲಂಕಾರ ವನ್ನೂ ಉತ್ತಮ ಆಹಾರ ಸಾಮಾಗ್ರಿಗಳನ್ನೂ ನಿಷಿದ್ಧ ಗೊಳಿಸಿದವನಾರು? ಹೇಳಿರಿ, ಇವು ಇಹ ಜೀವನದಲ್ಲಿ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಸೇರಿವೆ. ಮತ್ತು ಪುನರುತ್ಥಾನ ದಿನದಲ್ಲಿ ಇವು ಕೇವಲ ಅವರಿಗೆ ಮಾತ್ರವಿರುತ್ತವೆ. ಇದೇ ರೀತಿಯಲ್ಲಿ ನಾವು ಜ್ಞಾನವುಳ್ಳವರಿಗೆ ಪುರಾವೆಗಳನ್ನು ಸುಸ್ಪಷ್ಟವಾಗಿ ವಿವರಿಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(7,33,'7:33','ಹೇಳಿರಿ : ನಿಜವಾಗಿಯೂ ನನ್ನ ಪ್ರಭುವು ನಿಷಿದ್ಧ ಗೊಳಿಸಿರುವುದು ಪ್ರತ್ಯಕ್ಷ ಮತ್ತು ರಹಸ್ಯವಾದ ನೀಚ ಕಾರ್ಯಗಳನ್ನು, ಪಾಪ ಕರ್ಮಗಳನ್ನು, ನ್ಯಾಯೋಚಿತವಲ್ಲದ ಅತಿಕ್ರಮಣವನ್ನು, ಅಲ್ಲಾಹನಿಂದ ಯಾವುದೇ ಪುರಾವೆ ಇಳಿದಿಲ್ಲದವುಗಳನ್ನು ಅವನಿಗೆ ಸಹಭಾಗಿ ಮಾಡುವುದನ್ನು ಮತ್ತು ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲದ ವಿಚಾರವನ್ನು ಅಲ್ಲಾಹನ ಹೆಸರಲ್ಲಿ ಹೇಳುವುದನ್ನಾಗಿದೆ .');
INSERT INTO "kannada" VALUES(7,34,'7:34','ಪ್ರತಿಯೊಂದು ಸಮುದಾಯಕ್ಕೂ ಒಂದು ನಿಗದಿತ ಗಡುವು ಇದೆ. ಆ ಗಡುವು ಬಂದು ಬಿಟ್ಟರೆ, ನಂತರ ಅದನ್ನು ಅವರ ಪಾಲಿಗೆ ಒಂದು ಕ್ಷಣದ ಮಟ್ಟಿಗೂ ಹಿಂದೂಡಲಾಗುವುದಿಲ್ಲ, ಮುಂದೂಡ ಲಾಗುವುದೂ ಇಲ್ಲ ');
INSERT INTO "kannada" VALUES(7,35,'7:35','ಓ ಆದಮರ ಸಂತತಿಯವರೇ! ನಿಮ್ಮಿಂದಲೇ ಪ್ರವಾದಿಗಳು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದು ನಮ್ಮ ಪುರಾವೆಗಳನ್ನು ಓದಿ ಹೇಳುವ ಸ್ಥಿತಿಯಲ್ಲಿ ಯಾರು ಶಿರ್ಕ್‍ನಿಂದ ಮುಕ್ತರಾಗಿ ಸುಕೃತದಿಂದ ಸುಧಾರಣೆಗೊಳ್ಳುತ್ತಾರೋ ಅವರ ಮೇಲೆ ಯಾವುದೇ ಭಯವಿಲ್ಲ. ಅವರು ದುಃಖಕ್ಕೀಡಾಗುವವರೂ ಅಲ್ಲ.');
INSERT INTO "kannada" VALUES(7,36,'7:36','ನಮ್ಮ ನಿದರ್ಶನಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವವರು ಮತ್ತು ಅವುಗಳ ವಿರುದ್ಧ ದರ್ಪ ತೋರುವವರೇ ನರಕಾಗ್ನಿಯವರಾಗಿದ್ದು ಅಲ್ಲಿ ಅವರು ಸದಾಕಾಲ ವಾಸಿಸುವರು.');
INSERT INTO "kannada" VALUES(7,37,'7:37','ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಳ್ಳನ್ನು ಹೊರಿಸುವವನಿ ಗಿಂತ ಅಥವಾ ಅಲ್ಲಾಹನ ವಚನಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವವನಿಗಿಂತ ದೊಡ್ಡ ಅಕ್ರಮಿ ಇನ್ನಾರು? ಇಂಥವರು ನಮ್ಮ ದೂತರು (ದೇವಚರರು) ಅವರ ಪ್ರಾಣ ತೆಗೆಯಲು ಬಂದು ತಲಪುವವರೆಗೂ ವಿಧಿಲಿಖಿತ ಪ್ರಕಾರ ತಮಗಿರುವ ಇಹದ ಪಾಲನ್ನು ಪಡೆಯುತ್ತಲೇ ಇರುವರು. ಆಗ ದೇವಚರರು ಅವರೊಡನೆ, ‘ನೀವು ಅಲ್ಲಾಹನ ಹೊರತಾಗಿ ಆರಾಧಿಸುತ್ತಿದ್ದವರು ಎಲ್ಲಿದ್ದಾರೆ?’ ಎಂದು ಕೇಳುವರು, ‘ಅವರು ನಮ್ಮಿಂದ ಕಾಣೆಯಾಗಿದ್ದಾರೆ’ ಎಂದು ಇವರು ಹೇಳುವರು. ಅವರು ನಿಜಕ್ಕೂ ಸತ್ಯ ನಿಷೇಧಿಗಳಾಗಿದ್ದರೆಂದು ಅವರು ತಮ್ಮ ವಿರುದ್ಧ ತಾವೇ ಸಾಕ್ಷಿ ಹೇಳುವರು.');
INSERT INTO "kannada" VALUES(7,38,'7:38','ಅಲ್ಲಾಹನು ಹೇಳುವನು; ನಿಮಗಿಂತ ಮುಂಚೆ ಗತಿಸಿ ಹೋದ ಖೇಚರ ಮತ್ತು ಮಾನವ ಸಮುದಾಯಗಳ ಕೂಟದಲ್ಲಿ ನೀವು ನರಕಾಗ್ನಿಗೆ ಪ್ರವೇಶಿಸಿರಿ. ಪ್ರತಿಯೊಂದು ಸಮುದಾಯವೂ ನರಕಾಗ್ನಿಯೊಳಗೆ ಪ್ರವೇಶಿಸುವಾಗ ತನಗಿಂತ ಮುಂಚಿನ ಸೋದರ ಸಮುದಾಯವನ್ನು ಶಪಿಸುವುದು. ಹೀಗೆ ಎಲ್ಲರೂ ಅಲ್ಲಿ ಒಟ್ಟು ಸೇರಿದಾಗ ಅವರ ಪೈಕಿ ಹಿಂದಿನವರು ಅವರ ಮುಂಚಿನ ಸಮುದಾಯದ ಬಗ್ಗೆ, ಹೀಗೆ ಹೇಳುವರು; “ಓ ನಮ್ಮ ಪ್ರಭೂ, ನಮ್ಮನ್ನು ಪಥಭ್ರಷ್ಟರಾಗಿ ಮಾಡಿದವರು ಇವರೇ. ಆದುದರಿಂದ ಇವರಿಗೆ ನರಕಾಗ್ನಿಯಿಂದ ಇಮ್ಮಡಿ ಶಿಕ್ಷೆ ಕೊಡು”. ಆಗ ಅಲ್ಲಾಹನು `ಎಲ್ಲರಿಗೂ ಇಮ್ಮಡಿ ಶಿಕ್ಷೆಯಿದೆ. ಆದರೆ ನಿಮಗೆ ತಿಳಿದಿರುವುದಿಲ್ಲ’ ಎನ್ನುವನು .');
INSERT INTO "kannada" VALUES(7,39,'7:39','ಅವರ ಮೊದಲಿನ ಸಮುದಾಯವು ಅನಂತರದ ಸಮುದಾಯದೊಡನೆ, ‘ನಿಮಗೆ ನಮಗಿಂತ ಶ್ರೇಷ್ಠತೆಯೇನೂ ಇರುವುದಿಲ್ಲ’ ಎನ್ನುವುದು. ನಿಮ್ಮ ಗಳಿಕೆಯ ಫಲವಾಗಿ ಈಗ ಶಿಕ್ಷೆಯನ್ನು ಅನುಭವಿಸಿರಿ (ಎಂದು ಅಲ್ಲಾಹನು ಹೇಳು ವನು) .');
INSERT INTO "kannada" VALUES(7,40,'7:40','ನಮ್ಮ ನಿದರ್ಶನಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದ ಮತ್ತು ಅವುಗಳ ವಿರುದ್ಧ ಅಹಂಕಾರ ತೋರಿದ ಜನರ ಪಾಲಿಗೆ ಆಕಾಶದ ಬಾಗಿಲುಗಳನ್ನು ತೆರೆಯಲಾಗುವುದಿಲ್ಲ . ಸೂಜಿಯ ರಂಧ್ರದೊಳಗೆ ಒಂಟೆ ಪ್ರವೇಶಿಸುವವರೆಗೆ ಅವರು ಸ್ವರ್ಗಕ್ಕೆ ಪ್ರವೇಶಿಸಲಾರರು. ಅಪರಾಧಿಗಳಿಗೆ ನಾವು ಇಂಥದೇ ಪ್ರತಿಫಲ ಕೊಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(7,41,'7:41','ಅವರಿಗೆ ಬೆಂಕಿಯ ಹಾಸಿಗೆ ಇರುವುದು. ಅವರ ಮೇಲ್ಭಾಗದಿಂದ ಬೆಂಕಿಯ ಹೊದಿಕೆಯೂ ಇರುವುದು. ನಾವು ಅಕ್ರಮಿಗಳಿಗೆ ಇಂಥದೇ ಪ್ರತಿಫಲ ಕೊಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(7,42,'7:42','ಸತ್ಯವಿಶ್ವಾಸವನ್ನಿರಿಸಿದ ಮತ್ತು ಸತ್ಕರ್ಮವೆಸಗಿ ದವರು-ನಾವು ಪ್ರತಿಯೊಬ್ಬನನ್ನು ಅವನ ಶಕ್ತಿ ಗನುಸಾರ ಮಾತ್ರ ಹೊಣೆಗಾರನನ್ನಾಗಿ ಮಾಡುತ್ತೇವೆ - ಅವರೇ ಸ್ವರ್ಗದವರು. ಅಲ್ಲಿ ಅವರು ಸದಾಕಾಲ ನೆಲೆಸುವರು.');
INSERT INTO "kannada" VALUES(7,43,'7:43','ಅವರ ಹೃದಯಗಳಲ್ಲಿ ಇರುವ ಪರಸ್ಪರ ಹಗೆತನವನ್ನು ನಾವು ನಿವಾರಿಸುವೆವು . ಅವರ ಕೆಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆಗಳು ಹರಿಯುತ್ತಿರುವುವು. “ನಮ ಗೆ ಈ ಮಾರ್ಗವನ್ನು ತೋರಿಸಿಕೊಟ್ಟ ಅಲ್ಲಾಹನಿಗೇ ಸರ್ವಸ್ತುತಿಯು. ಅಲ್ಲಾಹನು ನಮಗೆ ಮಾರ್ಗ ದರ್ಶನ ನೀಡದಿರುತ್ತಿದ್ದರೆ, ನಾವು ಸ್ವಯಂ ಮಾರ್ಗದರ್ಶನ ಹೊಂದುತ್ತಿರಲಿಲ್ಲ. ನಮ್ಮ ಪ್ರಭು ವಿನ ದೂತರು ನಿಜಕ್ಕೂ ಸತ್ಯವನ್ನೇ ತಂದಿದ್ದರು” ಎಂದು ಅವರು ಹೇಳುವರು. ಆಗ, “ನಿಮ್ಮನ್ನು ವಾರೀಸುದಾರರಾಗಿ ಮಾಡಲ್ಪಟ್ಟಿರುವ ಈ ಸ್ವರ್ಗವು ನೀವು ಮಾಡುತ್ತಿದ್ದ ಕರ್ಮಗಳ ಪ್ರತಿಫಲವಾಗಿ ನಿಮಗೆ ಸಿಕ್ಕಿದೆ” ಎಂದು ಕೂಗಿ ಹೇಳಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(7,44,'7:44','ಸ್ವರ್ಗದವರು ನರಕದವರನ್ನು ಕೂಗಿ ಹೀಗೆ ಹೇಳುವರು, “ನಮ್ಮ ಪ್ರಭು ನಮಗೆ ವಾಗ್ದಾನ ಮಾಡಿದ ಪ್ರತಿಫಲವು ನಿಜವಾಗಿದೆ. ಅದನ್ನು ನಾವು ಪಡೆದಿದ್ದೇವೆ. ಆದರೆ ನಿಮ್ಮ ಪ್ರಭು ನಿಮಗೆ ವಾಗ್ದಾನ ಮಾಡಿದ್ದನ್ನು (ಶಿಕ್ಷೆ) ನೀವು ಪಡಕೊಂಡಿರಾ?” ಅವರು ಹೌದೆನ್ನುವರು. ಆಗ ಅವರ ನಡುವೆ ಘೋಷಿಸುವವನೊಬ್ಬನು “ದ್ರೋಹಿಗಳಿಗೆ ಅಲ್ಲಾಹನ ಶಾಪವಿರಲಿ” ಎಂದು ಘೋಷಿಸುವನು.');
INSERT INTO "kannada" VALUES(7,45,'7:45','ಅವರು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ ಜನರನ್ನು ತಡೆಯುತ್ತಿದ್ದರು. ಅದಕ್ಕೆ ವಕ್ರತೆಯನ್ನು ಹುಡುಕುತ್ತಿದ್ದರು ಹಾಗೂ ಪರಲೋಕವನ್ನು ನಿರಾಕರಿಸುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(7,46,'7:46','ಇವೆರಡೂ ವರ್ಗಗಳ ಮಧ್ಯೆ ಒಂದು ತಡೆಯಿ ರುವುದು. ‘ಅಅïರಾಫ್’ನಲ್ಲಿ (ಸ್ವರ್ಗದ ಗಡಿಯ ಬಳಿ) ಕೆಲವರಿರುವರು. ಇವರು ಪ್ರತಿಯೊಬ್ಬನನ್ನು ಅವನ ಮುಖ ಮುದ್ರೆಯಿಂದಲೇ ಗುರುತು ಹಿಡಿಯುವರು. ಅವರು ಸ್ವರ್ಗದವರೊಡನೆ, ‘ನಿಮಗೆ ಶುಭವಾಗಲಿ’ ಎಂದು ಕೂಗಿ ಹೇಳುವರು. ಇವರು ಸ್ವರ್ಗದೊಳಗೆ ಪ್ರವೇಶಿಸಲಿಲ್ಲ. ಆದರೆ ಅವರು ಆಶೆಪಡುವರು ');
INSERT INTO "kannada" VALUES(7,47,'7:47','ಇವರ ದೃಷ್ಟಿಗಳು ನರಕದವರ ಕಡೆಗೆ ತಿರುಗಿದಾಗ ಇವರು, “ಓ ನಮ್ಮ ಪ್ರಭೂ, ನಮ್ಮನ್ನು ಈ ಅಕ್ರಮಿ ಗಳೊಂದಿಗೆ ಸೇರಿಸಬೇಡ” ಎಂದು ಹೇಳುವರು.');
INSERT INTO "kannada" VALUES(7,48,'7:48','ಅನಂತರ ಈ ತ್ರಿಶಂಕು ಸ್ಥಿತಿಯವರು ನರಕದ ಕೆಲವು ಜನರನ್ನು ಅವರ ಲಕ್ಷಣಗಳಿಂದಲೇ ಗುರುತು ಹಿಡಿದು, ಕೂಗಿ ಹೇಳುವರು; “ನಿಮ್ಮ (ಐಹಿಕ) ಶೇಖರಣೆಯೂ ನೀವು ಅಹಂಕಾರಪಡುತ್ತಿದ್ದ ವಸ್ತುಗಳೂ ಇಂದು ನಿಮಗೆ ಯಾವ ಪ್ರಯೋಜನಕ್ಕೆ ಬಂದಿವೆ?');
INSERT INTO "kannada" VALUES(7,49,'7:49','(ದುರ್ಬಲ ಮುಸಲ್ಮಾನರನ್ನು ಬೊಟ್ಟು ಮಾಡಿ ಅವರು ಕೇಳುವರು;) ಇವರಿಗೆ ಅಲ್ಲಾಹನು ಯಾವುದೇ ಅನುಗ್ರಹವನ್ನು ನೀಡಲಾರನೆಂದು ನೀವು ಆಣೆ ಹಾಕಿ ಹೇಳುತ್ತಿದ್ದುದು ಇವರ ಕುರಿತೇ? ಸ್ವರ್ಗದಲ್ಲಿ ಪ್ರವೇಶಿಸಿರಿ. ನಿಮಗೆ ಭಯವೂ ಇಲ್ಲ ವ್ಯಥೆಯೂ ಇಲ್ಲ ಎಂದು (ಅವರಿಗೆ ಹೇಳಲಾಗಿದೆ).');
INSERT INTO "kannada" VALUES(7,50,'7:50','ನರಕದವರು ಸ್ವರ್ಗದವರನ್ನು ಕೂಗಿ, “ನಮ್ಮ ಮೇಲೆ ಒಂದಿಷ್ಟು ನೀರನ್ನು ಸುರಿದುಬಿಡಿರಿ ಅಥವಾ ಅಲ್ಲಾಹನು ನಿಮಗೆ ನೀಡಿದ ಆಹಾರದಿಂದ ಸ್ವಲ್ಪ ನೀಡಿರಿ” ಎನ್ನುವರು. ಆದರೆ “ಅವೆರ ಡನ್ನೂ ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಅಲ್ಲಾಹನು ನಿಸ್ಸಂದೇಹವಾಗಿ ನಿಷಿದ್ಧಗೊಳಿಸಿದ್ದಾನೆ” ಎಂದು ಅವರು ಪ್ರತಿಕ್ರಯಿಸುವರು.');
INSERT INTO "kannada" VALUES(7,51,'7:51','‘ಅವರು ತಮ್ಮ ಧರ್ಮವನ್ನು ವಿನೋದ ಹಾಗೂ ಆಟವನ್ನಾಗಿ ಪರಿಗಣಿಸಿದ್ದರು. ಐಹಿಕ ಬದುಕು ಅವರನ್ನು ವಂಚಿಸಿತು. ಅವರು ಇಂದಿನ ಭೇಟಿ ಯನ್ನು ಮರೆತುಬಿಟ್ಟಂತೆಯೇ ಮತ್ತು ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳನ್ನು ನಿರಾಕರಿಸಿದಂತೆಯೇ ಇಂದು ನಾವೂ ಅವರನ್ನು ಮರೆತು ಬಿಡುತ್ತೇವೆ’ .');
INSERT INTO "kannada" VALUES(7,52,'7:52','ನಿಸ್ಸಂದೇಹವಾಗಿ ನಾವು ಅವರಿಗೊಂದು ಗ್ರಂಥ ನೀಡಿದೆವು. ಪ್ರಜ್ಞಾಪೂರ್ವಕವಾಗಿ ಅದನ್ನು ಅವರಿಗೆ ವಿವರಿಸಿಕೊಟ್ಟೆವು; ವಿಶ್ವಾಸವಿಡುವವರಿಗೆ ಮಾರ್ಗದರ್ಶನವೂ, ಕಾರುಣ್ಯ ಪರವೂ ಆಗತಕ್ಕ ವಿಧದಲ್ಲಿ.');
INSERT INTO "kannada" VALUES(7,53,'7:53','ಅದರ ಮುನ್ನೆಚ್ಚರಿಕೆಯ ಪರಿಣಾಮವನ್ನಲ್ಲದೆ ಇನ್ನೇನನ್ನೂ ಅವರು ನಿರೀಕ್ಷಿಸುತ್ತಿಲ್ಲವೆ ? ಈ ಹಿಂದೆ ಅದನ್ನು ಮರೆತು ಬಿಟ್ಟವರು ಅದರ ಪರಿಣಾಮವು ಬಂದೆರಗಿದ ದಿನ ಹೀಗೆ ಹೇಳುವರು: “ನಿಜಕ್ಕೂ ನಮ್ಮ ಪ್ರಭುವಿನ ದೂತರು ಸತ್ಯವನ್ನೇ ತಂದಿದ್ದರು. ಆದ್ದರಿಂದ ನಮ್ಮ ಪರವಾಗಿ ಶಿಫಾರಸ್ಸು ಮಾಡಲು ನಮಗೆ ಯಾರಾದರೂ ಶಿಫಾ ರಸ್ಸುದಾರರು ಸಿಗುವರೇ? ಅಥವಾ ನಾವು ಹಿಂದೆ ಮಾಡುತ್ತಿದ್ದ ಕರ್ಮಕ್ಕೆ ಬದಲು ಇನ್ನು ಬೇರೆ ಕರ್ಮವೆಸಗಲಿಕ್ಕಾಗಿ ನಮ್ಮನ್ನು ಇನ್ನೊಮ್ಮೆ ಹಿಂದಕ್ಕೆ ಕಳುಹಿಸಲಾಗುವುದೇ?” ಅವರು ತಮ್ಮನ್ನು ತಾವೇ ನಷ್ಟಕ್ಕೆ ಗುರಿಪಡಿಸಿಕೊಂಡರು. ಅವರು ಹೆಣೆದುಕೊಂಡಿದ್ದ ಎಲ್ಲ ಸುಳ್ಳುಗಳೂ ಇಂದು ಅವರಿಂದ ಮಾಯವಾಗಿವೆ .');
INSERT INTO "kannada" VALUES(7,54,'7:54','ಆರು ದಿನಗಳಲ್ಲಿ ಭೂಮಿ-ಆಕಾಶಗಳನ್ನು ಸೃಷ್ಟಿಸಿದ ಅಲ್ಲಾಹನೇ ಆಗಿರುವನು ನಿಮ್ಮ ರಕ್ಷಕನು . ತರುವಾಯ ಅವನಿಗೆ ಸಂಗತವಾದ ವಿಧದಲ್ಲಿ ಅರ್ಶ್‍ನ ಮೇಲೆ ಇಸ್ತಿವಾ ಹೊಂದಿದನು . ರಾತ್ರಿ ಯನ್ನು ಹಗಲು ಬೆಂಬತ್ತಿ ಬರುವಂತೆ ಅವನು ಇರುಳನ್ನು ಹಗಲಿಗೆ ಮುಚ್ಚುವನು . ಅವನು ಸೂರ್ಯನನ್ನೂ ಚಂದ್ರನನ್ನೂ ನಕ್ಷತ್ರಗಳನ್ನೂ ಅವನ ಆಜ್ಞೆಗೆ ವಿಧೇಯವಾಗಿ ಸೃಷ್ಟಿಸಿದನು. ತಿಳಿಯಿರಿ! ಅವನದೇ ಸೃಷ್ಟಿ ಹಾಗೂ ಅಪ್ಪಣೆ ! ಸರ್ವಲೋಕಗಳ ಪ್ರಭುವಾಗಿರುವ ಅಲ್ಲಾಹನು ಮಹಾ ಸಮೃದ್ಧನು.');
INSERT INTO "kannada" VALUES(7,55,'7:55','ಭಯ ವಿನಯದೊಂದಿಗೆ ಮತ್ತು ರಹಸ್ಯವಾಗಿ ನಿಮ್ಮ ಪ್ರಭುವನ್ನು ಪ್ರಾರ್ಥಿಸಿರಿ. ಅವನು ಹದ್ದು ಮೀರುವವರನ್ನು ಖಂಡಿತ ಮೆಚ್ಚುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(7,56,'7:56','ಭೂಮಿಯಲ್ಲಿ ಸುಧಾರಣೆಯಾದ ಬಳಿಕ ಕ್ಷೋಭೆಯನ್ನು ಹರಡಬೇಡಿರಿ. ಭಯವುಳ್ಳವರಾಗಿಯೂ, ಬಯಕೆಯುಳ್ಳವರಾಗಿಯೂ ಅವನಲ್ಲಿ ಪ್ರಾರ್ಥಿಸಿರಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹನ ಕೃಪೆಯು ಸಜ್ಜನರ ಸಮೀಪದಲ್ಲಿದೆ.');
INSERT INTO "kannada" VALUES(7,57,'7:57','ತನ್ನ ಅನುಗ್ರಹದ (ವರ್ಷಧಾರೆಗೆ) ಮುಂದಾಗಿ ಸಂತೋಷ ವಾರ್ತೆಯನ್ನರುಹಿ ಮಾರುತಗಳನ್ನು ಕಳುಹಿಸುವವನು ಅಲ್ಲಾಹನೇ ಆಗಿರುತ್ತಾನೆ. ಆಮೇಲೆ ಆ ಮಾರುತಗಳು ಘನೀಕೃತ ಮೇಘಗ ಳನ್ನು ವಹಿಸಿಕೊಂಡಾಗ ನಾವು ನಿರ್ಜೀವ ನಾಡಿಗೆ ಜಲಸೇಚನ ಮಾಡುತ್ತೇವೆ. ಹಾಗೆ ನಾಡಿಗೆ ನೀರುಣಿಸಿ ಆ ನೀರಿನಿಂದ ನಾನಾ ವಿಧ ಫಲ ಬೆಳೆಗಳನ್ನು ನಾವು ಉತ್ಪಾದಿಸುತ್ತೇವೆ. ಮೃತ ರನ್ನು ಸಜೀವಗೊಳಿಸಿ ನಾವು ಎಬ್ಬಿಸುವುದು ಅದೇ ರೀತಿಯಲ್ಲಾಗಿದೆ. ನೀವು ಚಿಂತಿಸಲಿಕ್ಕಾಗಿ (ಈ ವಿಚಾರವನ್ನು ಹೇಳಲಾಗಿದೆ).');
INSERT INTO "kannada" VALUES(7,58,'7:58','ಫಲವತ್ತಾದ ನೆಲದಲ್ಲಿ ಶ್ರೇಷ್ಠ ಸಸ್ಯ ಉತ್ಪಾದನೆ ಯಾಗುತ್ತದೆ. ಆ ನೆಲದೊಡೆಯನ ಅನುಮತಿಯಿಂದ . ಕೆಟ್ಟ ನೆಲದಿಂದಲೋ, ಕೆಟ್ಟ ಬೆಳೆಯ ನ್ನಲ್ಲದೆ ಬೇರೇನೂ ನಿರೀಕ್ಷಿಸಲಾಗದು. ಅದೇ ರೀತಿ ಕೃತಜ್ಞರಾದವರಿಗೆ ನಾವು ದೃಷ್ಟಾಂತಗಳನ್ನು ನಿರೂಪಿಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(7,59,'7:59','ನೂಹರನ್ನು ಅವರ ಜನಾಂಗದ ಕಡೆಗೆ ನಾವು ಕಳುಹಿಸಿದೆವು . ಅವರು ಹೇಳಿದರು, `ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧವರೇ, ನೀವು ಅಲ್ಲಾಹನನ್ನು ಆರಾಧಿಸಿರಿ. ಅವನ ಹೊರತು ನಿಮಗೆ ಬೇರೊಬ್ಬ ಆರಾಧ್ಯನಿಲ್ಲ. ನಿಮ್ಮ ಪಾಲಿಗೆ ಒಂದು ಭಯಾನಕ ದಿನದ ಯಾತನೆಯ ಕುರಿತು ನಾನು ಭಯಪಡುತ್ತಿದ್ದೇನೆ’.');
INSERT INTO "kannada" VALUES(7,60,'7:60','ಅವರ ಜನಾಂಗದ ಮುಂದಾಳುಗಳು ಹೇಳಿದರು: ‘ನಮಗಂತೂ ನೀನು ಸುವ್ಯಕ್ತ ಪಥಭ್ರ ಷ್ಟತೆಯಲ್ಲಿ ಸಿಲುಕಿರುವಂತೆ ಕಾಣಿಸುತ್ತಿರುವೆ’.');
INSERT INTO "kannada" VALUES(7,61,'7:61','ನೂಹರ ಪ್ರತಿಕ್ರಿಯೆ - `ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧ ವರೇ, ನನ್ನಲ್ಲಿ ಯಾವುದೇ ಪಥಭ್ರಷ್ಟತೆ ಇರು ವುದಿಲ್ಲ . ನಿಜವಾಗಿಯೂ ನಾನು ಸರ್ವಲೋಕ ಪಾಲಕನ ದೂತನಾಗಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(7,62,'7:62','ನಿಮಗೆ ನನ್ನ ಪ್ರಭುವಿನ ಸಂದೇಶಗಳನ್ನು ತಲುಪಿಸುತ್ತಿದ್ದೇನೆ. ನಾನು ನಿಮ್ಮ ಹಿತಚಿಂತಕನಾಗಿರುತ್ತೇನೆ. ನಿಮಗೆ ತಿಳಿಯದಿರುವುದು ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ನನಗೆ ತಿಳಿದಿದೆ.');
INSERT INTO "kannada" VALUES(7,63,'7:63','ನಿಮ್ಮ ಪೈಕಿ ಓರ್ವ ವ್ಯಕ್ತಿಯ ಮೂಲಕ ನಿಮ್ಮ ಪ್ರಭುವಿನಿಂದ ಉಪದೇಶ ಬರುವುದು ಹಾಗೂ ನಿಮಗೆ ಮುನ್ನೆಚ್ಚರಿಕೆ ಹಾಗೂ ದೇವಭಯಕ್ಕೆ ಕರೆ ನೀಡಿ ನಿಮಗೆ ದೇವನೊಲುಮೆ ದೊರೆಯು ವಂತಾಗುವುದು ನಿಮಗೆ ಆಶ್ಚರ್ಯದ ವಿಷಯವೇ?');
INSERT INTO "kannada" VALUES(7,64,'7:64','ಆದರೆ ಅವರು ಇವರ ಮಾತನ್ನು ನಿಷೇಧಿಸಿ ಬಿಟ್ಟರು. ಹಾಗಾಗಿ ನಾವು ಇವರನ್ನು ಹಾಗೂ ಇವರ ಜೊತೆ ನಾವೆಯಲ್ಲಿದ್ದವರನ್ನು (ಮುಳುಗು ವಿಕೆಯಿಂದ) ರಕ್ಷಿಸಿದೆವು ಮತ್ತು ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದ್ದವರನ್ನು ಮುಳುಗಿಸಿ ಬಿಟ್ಟೆವು. ವಾಸ್ತವದಲ್ಲಿ ಅವರು (ಸತ್ಯದ ವಿರುದ್ಧ) ಕುರುಡರಾದ ಜನಾಂಗವಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(7,65,'7:65','`ಆದ್'' ಜನಾಂಗದ ಕಡೆಗೆ ಅವರ ಸೋದರ `ಹೂದ''ರನ್ನು ನಾವು ಕಳುಹಿಸಿದೆವು . ಅವರು ಹೇಳಿದರು; `ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧವರೇ, ನೀವು ಅಲ್ಲಾಹನನ್ನು ಆರಾಧಿಸಿರಿ. ಅವನಲ್ಲದೆ ನಿಮಗೆ ಬೇರೊಬ್ಬ ದೇವನಿಲ್ಲ. ನೀವು ಜಾಗೃತರಾಗುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(7,66,'7:66','ಅವರ ಜನಾಂಗದ ಸತ್ಯನಿಷೇಧಿ ಮುಂದಾಳುಗಳು ಹೇಳಿದರು; ನಾವು ನಿನ್ನನ್ನು ಮೂರ್ಖ ತನದಲ್ಲಿ ಕಾಣುತ್ತೇವೆ. ನೀನು ಸುಳ್ಳುಗಾರನೆಂದು ನಾವು ಭಾವಿಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(7,67,'7:67','ಅವರು ಹೇಳಿದರು; `ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧವರೇ, ನನ್ನಲ್ಲಿ ಮೂರ್ಖತನವಿಲ್ಲ. ನಿಜ ವಾಗಿ ನಾನು ಸರ್ವಲೋಕಪಾಲಕನ ದೂತನಾಗಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(7,68,'7:68','ನಿಮಗೆ ನನ್ನ ರಕ್ಷಕ ಪ್ರಭುವಿನ ಸಂದೇಶಗಳನ್ನು ತಲಪಿಸುತ್ತೇನೆ ಮತ್ತು ನಿಮಗೆ ನಾನು ಪ್ರಾಮಾ ಣಿಕ ಹಿತೈಷಿಯಾಗಿರುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(7,69,'7:69','ನಿಮ್ಮನ್ನು ಎಚ್ಚರಿಸಲು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದಿರುವ ಬೋಧನೆಯು ನಿಮ್ಮದೇ ನಡುವಿನ ಒಬ್ಬ ವ್ಯಕ್ತಿಯ ಬಳಿಗೆ ಬಂದಿರುವ ಬಗ್ಗೆ ನೀವು ಅಚ್ಚರಿಪಡುತ್ತೀರಾ? ನಿಮ್ಮ ಪ್ರಭು ನೂಹರ ಜನಾಂಗದ ತರುವಾಯ ನಿಮ್ಮನ್ನು ಉತ್ತರಾಧಿಕಾರಿಗಳನ್ನಾಗಿ ಮಾಡಿದ್ದನ್ನೂ ನಿಮಗೆ ಸೃಷ್ಟಿಯಲ್ಲಿ ವಿಶಾಲತೆಯನ್ನು ಹೆಚ್ಚಿಸಿದ್ದನ್ನೂ ನೆನಪಿಸಿರಿ. ಆದ್ದರಿಂದ ಅಲ್ಲಾಹನ ವರದಾನಗಳನ್ನು ನೆನೆಯಿರಿ. ನೀವು ಯಶಸ್ವಿಯಾಗಬಹುದು.');
INSERT INTO "kannada" VALUES(7,70,'7:70','ಅವರು ಕೇಳಿದರು; `ನಾವು ಏಕಮಾತ್ರ ಅಲ್ಲಾಹನನ್ನು ಆರಾಧಿಸಬೇಕು ಮತ್ತು ನಮ್ಮ ಪೂರ್ವಿ ಕರು ಆರಾಧಿಸುತ್ತಿದ್ದುದನ್ನು ಬಿಟ್ಟುಬಿಡಬೇಕೆಂದು ನೀನು ನಮಗೆ ಹೇಳಲು ಬಂದಿರುವೆಯಾ? ನಿಜಕ್ಕೂ ನೀನು ಸತ್ಯವಂತನಾಗಿದ್ದರೆ ನೀನು ನಮಗೆ ಭೀತಿ ಹುಟ್ಟಿಸುತ್ತಿರುವ ಆ ಯಾತನೆಯನ್ನು ಇಲ್ಲಿಗೆ ತರಿಸು, ನೋಡೋಣ’.');
INSERT INTO "kannada" VALUES(7,71,'7:71','ಅವರು ಹೇಳಿದರು; ನಿಮ್ಮ ಮೇಲೆ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯ ಶಿಕ್ಷೆಯೂ, ಕ್ರೋಧವೂ ನಿಜವಾಗಿಯೂ ಸ್ಥಿರಪಟ್ಟಿದೆ. ನೀವು ಮತ್ತು ನಿಮ್ಮ ಪೂರ್ವಿಕರು ಇಟ್ಟುಕೊಂಡಂತಹ, ಅಲ್ಲಾಹನು ಯಾವುದೇ ಆಧಾರ ಪ್ರಮಾಣವನ್ನು ಅವತರಣಗೊಳಿಸದಂತಹ ವಿಗ್ರಹಗಳ ಹೆಸರುಗಳ ತಳಹದಿಯಲ್ಲಿ ನೀವು ನನ್ನೊಂದಿಗೆ ಜಗಳ ವಾಡುತ್ತೀರಾ? ನೀವೂ ಕಾಯುತ್ತಿರಿ. ನಿಮ್ಮೊಂದಿಗೆ ಖಂಡಿತಾ ನಾನೂ ಕಾಯುತ್ತಿರುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(7,72,'7:72','ಕೊನೆಗೆ ಹೂದ್ ಮತ್ತು ಅವರ ಸಂಗಡಿಗರನ್ನು ನಮ್ಮ ಕಾರುಣ್ಯದಿಂದ ನಾವು ರಕ್ಷಿಸಿದೆವು ಮತ್ತು ನಮ್ಮ ನಿದರ್ಶನಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದ ಮತ್ತು ವಿಶ್ವಾಸವಿರಿಸದವರ ಬೇರನ್ನೇ ಕಡಿಸಿಬಿಟ್ಟೆವು.');
INSERT INTO "kannada" VALUES(7,73,'7:73','`ಸಮೂದ್'' ಜನಾಂಗದ ಕಡೆಗೆ ನಾವು ಅವರ ಸಹೋದರ ಸಾಲಿಹರನ್ನು ಕಳುಹಿಸಿದೆವು . ಅವರು ಹೇಳಿದರು; ‘ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧವರೇ, ನೀವು ಅಲ್ಲಾಹನನ್ನು ಆರಾಧಿಸಿರಿ. ಅವನ ಹೊರತು ನಿಮಗೆ ಯಾವ ಆರಾಧ್ಯನೂ ಇಲ್ಲ. ನಿಮ್ಮ ಬಳಿಗೆ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಸ್ಪಷ್ಟ ಪ್ರಮಾಣ ಬಂದಿರುತ್ತದೆ. ಅಲ್ಲಾಹನ ಈ ಒಂಟೆಯು ನಿಮಗೊಂದು ದೃಷ್ಟಾಂತ. ಅಲ್ಲಾಹನ ಭೂಮಿಯಲ್ಲಿ ಇದನ್ನು ಮೇಯಲು ಬಿಡಿರಿ. ಅದಕ್ಕೆ ಯಾವುದೇ ವಿಧ ತೊಂದರೆ ನೀಡಬೇಡಿರಿ. ತೊಂದರೆ ನೀಡಿದರೆ ಒಂದು ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆ ನಿಮ್ಮನ್ನು ಆಕ್ರಮಿಸುವುದು .');
INSERT INTO "kannada" VALUES(7,74,'7:74','ಅಲ್ಲಾಹನು `ಆದ್'' ಜನಾಂಗದ ತರುವಾಯ ನಿಮ್ಮನ್ನು ಉತ್ತರಾಧಿಕಾರಿಗಳನ್ನಾಗಿ ಮಾಡಿದುದನ್ನು ಸ್ಮರಿಸಿರಿ. ನಿಮ್ಮನ್ನು ಅವನು ನಾಡಿನಲ್ಲಿ ವಾಸಗೊಳಿಸಿದನು. ನೀವು ಅದರ ಸಮತಟ್ಟಾದ ಪ್ರದೇಶಗಳಲ್ಲಿ ಭವ್ಯ ಭವನಗಳನ್ನು ಕಟ್ಟುತ್ತಿರುವಿರಿ. ಮತ್ತು ಅದರ ಪರ್ವತಗಳನ್ನು ನಿವಾಸಗ ಳಾಗಿ ಕೊರೆಯುತ್ತಿರುವಿರಿ. ಹಾಗಾಗಿ ಆ ಅಲ್ಲಾ ಹನ ಅನುಗ್ರಹಗಳನ್ನು ಸ್ಮರಿಸಿರಿ. ನೀವು ಕಿಡಿ ಗೇಡಿಗಳಾಗಿ ಭೂಮಿಯಲ್ಲಿ ಕೇಡು ಹರಡದಿರಿ.');
INSERT INTO "kannada" VALUES(7,75,'7:75','ಅವರ ಜನಾಂಗದ ದುರಹಂಕಾರಿ ನಾಯಕರು, ವಿಶ್ವಾಸವಿಟ್ಟಿದ್ದ ದುರ್ಬಲ ವರ್ಗದವರೊಡನೆ ಹೇಳಿದರು; - ಸಾಲಿಹರು ತಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆ ಯಿಂದ ನಿಯುಕ್ತರಾದ ದೂತರೆಂದು ನಿಜಕ್ಕೂ ನೀವು ಅರಿತಿರುವಿರಾ? ಅವರು ಹೇಳಿದರು; `ನಿಶ್ಚ ಯವಾಗಿಯೂ ಅವರು ಯಾವ ಸಂದೇಶದೊಂದಿಗೆ ಕಳುಹಿಸಲ್ಪಟ್ಟಿರುತ್ತಾರೋ ಅದನ್ನು ನಾವು ನಂಬುತ್ತೇವೆ’.');
INSERT INTO "kannada" VALUES(7,76,'7:76','ಆ ದುರಹಂಕಾರಿಗಳು ಹೇಳಿದರು; ‘ನೀವು ವಿಶ್ವಾಸವಿರಿಸಿರುವುದನ್ನು ನಾವು ನಿಷೇಧಿಸುತ್ತೇವೆ’.');
INSERT INTO "kannada" VALUES(7,77,'7:77','ತರುವಾಯ ಅವರು ಆ ಒಂಟೆಯ ಕತ್ತು ಕೊಯ್ದರು. ತಮ್ಮ ಪ್ರಭುವಿನ ಆಜ್ಞೆಯನ್ನು ಧಿಕ್ಕರಿಸಿದರು. ಅವರು ಹೀಗೆಂದರು; ಓ ಸಾಲಿಹ್, ನೀನು ನಮಗೆ ಬೆದರಿಕೆ ಹಾಕುತ್ತಿರುವ ಆ ಶಿಕ್ಷೆಯನ್ನು ನಮಗೆ ತಂದೊಡ್ಡು! ನೀನು ಪ್ರವಾದಿಗಳಲ್ಲೊಬ್ಬನಾಗಿದ್ದರೆ !');
INSERT INTO "kannada" VALUES(7,78,'7:78','ಆಗ ಅವರಿಗೆ ಭೂಕಂಪನ ಬಾಧಿಸಿತು. ಅವರು ತಮ್ಮ ಊರಲ್ಲಿ ಮಂಡಿಯೂರಿದ ಸ್ಥಿತಿಯಲ್ಲಿ ಸತ್ತು ಬಿದ್ದರು !');
INSERT INTO "kannada" VALUES(7,79,'7:79','ಆಗ ಸ್ವಾಲಿಹರು ಅಲ್ಲಿಂದ ನಿರ್ಗಮಿಸುತ್ತಾ `ಓ ನನ್ನ ಜನಾಂಗವೇ, ನಾನು ನನ್ನ ಪ್ರಭುವಿನ ಸಂದೇಶವನ್ನು ನಿಮಗೆ ತಲುಪಿಸಿದೆ. ನಿಮ ಗಾಗಿ ನಾನು ಹಿತೋಪದೇಶ ನೀಡಿದೆ. ಆದರೆ ಹಿತೋಪದೇಶಕರನ್ನು ನೀವು ಮೆಚ್ಚುವುದಿಲ್ಲ’ ಎಂದರು .');
INSERT INTO "kannada" VALUES(7,80,'7:80','ಲೂಥರು ತಮ್ಮ ಜನಾಂಗದವರನ್ನುದ್ದೇಶಿಸಿ ಹೇಳಿದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ. ನೀವು ನಿಮಗಿಂತ ಮುಂಚೆ ಜಗತ್ತಿನಲ್ಲಿ ಇನ್ನಾರೂ ಮಾಡದಿರುವಂಥ ನೀಚ ಕಾರ್ಯಕ್ಕೆ ಹೋಗುತ್ತಿರುವಿರಾ?');
INSERT INTO "kannada" VALUES(7,81,'7:81','ನೀವು ಸ್ತ್ರೀಯರನ್ನು ಬಿಟ್ಟು ಪುರುಷರ ಬಳಿಗೆ ಕಾಮಾಭಿಲಾಷೆಯಿಂದ ಹೋಗುತ್ತಿರುವಿರಿ. ವಾಸ್ತವದಲ್ಲಿ ನೀವು ಹದ್ದು ಮೀರಿದ ಜನಾಂಗವಾಗಿರುವಿರಿ.');
INSERT INTO "kannada" VALUES(7,82,'7:82','`ಇವರನ್ನು ನಿಮ್ಮ ನಾಡಿನಿಂದ ಹೊರ ದಬ್ಬಿರಿ. ಭಾರೀ ಪರಿಶುದ್ಧರಿವರು! ಎನ್ನುವುದರವಿನಾ ಆ ಜನಾಂಗಕ್ಕೆ ಬೇರೆ ಉತ್ತರವಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(7,83,'7:83','ಕೊನೆಗೆ ನಾವು ಲೂಥರನ್ನೂ ಅವರ ಮನೆಯವರನ್ನೂ - ರಕ್ಷಿಸಿ ಒಯ್ದೆವು. ಅವರ ಪತ್ನಿಯ ಹೊರತಾಗಿ. ಅವಳು ನಾಶ ಹೊಂದುವವರ ಕೂಟದವಳಾಗಿದ್ದಳು.');
INSERT INTO "kannada" VALUES(7,84,'7:84','ಆಮೇಲೆ ಅವರ ಮೇಲೆ ನಾವು (ಸುಡುಗಲ್ಲಿನ) ಮಳೆಗರೆದೆವು . ಆಗ ಆ ಅಪರಾಧಿಗಳ ಪಾಡೇ ನಾಯಿತೆಂದು ನೋಡಿರಿ.');
INSERT INTO "kannada" VALUES(7,85,'7:85','ಮದ್‍ಯನರ ಕಡೆಗೆ ಅವರ ಸಹೋದರ `ಶುಐಬ್''ರನ್ನು ಕಳುಹಿಸಿದೆವು . ಅವರು ಹೇಳಿದರು `ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧವರೇ, ನೀವು ಅಲ್ಲಾಹ ನನ್ನು ಆರಾಧಿಸಿರಿ. ಅವನ ಹೊರತು ನಿಮಗೆ ಬೇರೊಬ್ಬ ಆರಾಧ್ಯನಿಲ್ಲ. ನಿಮಗೆ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಸುವ್ಯಕ್ತ ನಿದರ್ಶನ ಬಂದಿರುತ್ತದೆ. ಆದುದರಿಂದ ಅಳತೆ ಮತ್ತು ತೂಕ ವನ್ನು ಸಂಪೂರ್ಣಗೊಳಿಸಿರಿ. ಜನರಿಗೆ ಅವರ ವಸ್ತು ಗಳನ್ನು ಕಡಿತಗೊಳಿಸಿಕೊಡಬೇಡಿರಿ ಮತ್ತು ಭೂಮಿಯಲ್ಲಿ ಸುಧಾರಣೆಯಾದ ಬಳಿಕ ಕ್ಷೋಭೆಯನ್ನುಂಟು ಮಾಡಬೇಡಿರಿ. ಇದುವೇ ನಿಮಗೆ ಶ್ರೇಯಸ್ಸು. ನೀವು ನಿಜಕ್ಕೂ ವಿಶ್ವಾಸಿಗಳಾಗಿದ್ದರೆ.');
INSERT INTO "kannada" VALUES(7,86,'7:86','ಜನರನ್ನು ಬೆದರಿಸುತ್ತ ಎಲ್ಲ ದಾರಿಗಳಲ್ಲಿ (ದೋಚಲು) ಕೂರಬೇಡಿರಿ. ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ ತಡೆಯಬೇಡಿರಿ. ಸತ್ಯದ ದಾರಿಯಲ್ಲಿ ವಕ್ರತೆಯನ್ನು ಹುಡುಕ ಬೇಡಿರಿ. ಕಡಿಮೆ ಸಂಖ್ಯೆಯಲ್ಲಿದ್ದ ನಿಮ್ಮನ್ನು ಅಲ್ಲಾಹು ಬಹು ಸಂಖ್ಯೆಯವರನ್ನಾಗಿ ಮಾಡಿ ದ್ದನ್ನು ಸ್ಮರಿಸಿರಿ. ಕೆಟ್ಟವರ ಪರ್ಯವಸಾನ ಹೇಗಾಗಿ ದೆಯೆಂಬುದನ್ನು ನೀವು ಆಲೋಚಿಸಿ ನೋಡಿರಿ.');
INSERT INTO "kannada" VALUES(7,87,'7:87','ಅಲ್ಲಾಹನು ಅವತೀರ್ಣಗೊಳಿಸಿದ ಸಂದೇಶಗ ಳಲ್ಲಿ ನಿಮ್ಮ ಪೈಕಿ ಕೆಲವರು ವಿಶ್ವಾಸವಿಡುತ್ತಲೂ, ಇನ್ನು ಕೆಲವರು ವಿಶ್ವಾಸವಿಡದೆಯೂ ಇರುವು ದಾದರೆ ಅಲ್ಲಾಹನು ನಮ್ಮ ನಡುವೆ ಒಂದು ತೀರ್ಮಾನ ಮಾಡುವವರೆಗೂ ಸಹನೆಯೊಂದಿಗೆ ಕಾಯುತ್ತಿರಿ. ಅವನು ಪರಮ ಶ್ರೇಷ್ಠ ತೀರ್ಪು ಗಾರನು.');
INSERT INTO "kannada" VALUES(7,88,'7:88','ಅವರ ಜನಾಂಗದ ಅಹಂಭಾವಿಗಳಾದ ನೇತಾರರು ಅವರೊಡನೆ ಹೇಳಿದರು. `ಓ ಶುಐಬ್, ನಾವು ನಿನ್ನನ್ನೂ ನಿನ್ನೊಂದಿಗೆ ವಿಶ್ವಾಸ ವಿಟ್ಟವ ರನ್ನೂ ನಮ್ಮ ನಾಡಿನಿಂದ ಖಂಡಿತ ಹೊರಹಾ ಕುವೆವು. ಅದಲ್ಲದಿದ್ದರೆ ನೀವು ನಮ್ಮ ಧರ್ಮಕ್ಕೆ ಮರಳಿ ಬರಲೇ ಬೇಕು’. ಶುಐಬರು ಕೇಳಿದರು; ನಾವು ನಿಮ್ಮ ಧರ್ಮವನ್ನು ಅನಿಷ್ಟಪಡುತ್ತಿರುವಾಗ ಅದಕ್ಕೆ ಮರಳುವುದೇ?');
INSERT INTO "kannada" VALUES(7,89,'7:89','ನಿಮ್ಮ ಧರ್ಮದಿಂದ ಅಲ್ಲಾಹನು ನಮ್ಮನ್ನು ಮುಕ್ತ ಗೊಳಿಸಿರುವಾಗ ನಾವು ಅದಕ್ಕೆ ಮರಳಿ ಬಂದರೆ ನಾವು ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಳ್ಳನ್ನು ಸೃಷ್ಟಿ ಸುವವರಾಗುವೆವು. ನಮ್ಮ ರಕ್ಷಕನಾದ ಅಲ್ಲಾ ಹನು ಇಚ್ಛಿಸುವ ಹೊರತಾಗಿ ನಮಗಂತೂ ಅದರ ಕಡೆಗೆ ಮರಳಲು ಎಂದಿಗೂ ಸಾಧ್ಯವಾಗದು . ನಮ್ಮ ಪ್ರಭುವಿನ ಜ್ಞಾನವು ಸಕಲ ವಸ್ತುಗಳನ್ನಾವರಿಸಿ ಕೊಂಡಿದೆ. ನಾವು ಅವನ ಮೇಲೆಯೇ ಭರವಸೆಯನ್ನಿರಿಸಿದ್ದೇವೆ. ಓ ನಮ್ಮ ಪ್ರಭೂ, ನಮ್ಮ ಮತ್ತು ನಮ್ಮ ಜನಾಂಗದ ನಡುವೆ ನೀನು ಸತ್ಯ ಪ್ರಕಾರ ತೀರ್ಮಾನ ಮಾಡಿಬಿಡು. ನೀನು ತೀರ್ಪುಗಾರರಲ್ಲಿ ಅತ್ಯುತ್ತಮನಾಗಿರುವಿ’.');
INSERT INTO "kannada" VALUES(7,90,'7:90','ಅವರ ಜನಾಂಗದ ಸತ್ಯನಿಷೇಧಿಗಳಾದ ಮುಂದಾಳುಗಳು ತಮ್ಮೊಳಗೆ ಹೀಗೆ ಹೇಳಿದರು. `ನೀವು ಶುಐಬರ ಅನುಸರಣೆ ಮಾಡಿದರೆ ನಿಸ್ಸಂದೇಹವಾಗಿ ನಷ್ಟವಂತರಾಗುವಿರಿ’.');
INSERT INTO "kannada" VALUES(7,91,'7:91','ಆಗ ಒಂದು ಭೀಕರ ವಿಪತ್ತು ಬಂದು ಅವರನ್ನು ಆಕ್ರಮಿಸಿತು. ಅದರ ಫಲವಾಗಿ ಅವರೆಲ್ಲರೂ ತಮ್ಮ ಮನೆಗಳಲ್ಲಿ ಮಂಡಿಯೂರಿ ಮೃತ್ಯು ವಶವಾದರು.');
INSERT INTO "kannada" VALUES(7,92,'7:92','ಶುಐಬರನ್ನು ನಿಷೇಧಿಸಿದವರು ತಮ್ಮ ಮನೆ ಗಳಲ್ಲಿ ವಾಸಿಸಲೇ ಇಲ್ಲವೋ ಎಂಬಂತೆ ಅಳಿದು ಹೋದರು. ಅಂತೂ ಶುಐಬರನ್ನು ನಿಷೇಧಿಸಿದವರೇ ನಷ್ಟವಂತರಾದರು.');
INSERT INTO "kannada" VALUES(7,93,'7:93','ಶುಐಬರು, ಅವರಿಂದ ದೂರ ಸರಿದರು. `ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧವರೇ, ನಾನು ನನ್ನ ಪ್ರಭುವಿನ ಸಂದೇಶಗಳನ್ನು ನಿಮಗೆ ತಲಪಿಸಿದೆ. ನಿಮಗೆ ಹಿತವನ್ನೇ ಬಯಸಿದೆ. ಇದೀಗ ನಾನು ಸತ್ಯವನ್ನು ನಿರಾಕರಿಸಿದ ಜನಾಂಗಕ್ಕಾಗಿ ಖೇದ ಪಡುವುದೇನಿದೆ?’');
INSERT INTO "kannada" VALUES(7,94,'7:94','ಒಂದು ನಾಡಿಗೆ ಓರ್ವ ಪ್ರವಾದಿಯನ್ನು ಕಳುಹಿ ಸಿದರೆ ಆ ನಾಡಿನ ಜನರನ್ನು ಬಡತನ, ರೋಗಗಳಿಂದ ನಾವು ಸಂಕಷ್ಟಗಳಿಗೊಳಪಡಿಸದೆ ಬಿಟ್ಟಿರಲಿಲ್ಲ. ಕಾರಣ - ಅವರಲ್ಲಿ ವಿಧೇಯತೆ ಉಂಟಾಗಲೆನ್ನುವುದು.');
INSERT INTO "kannada" VALUES(7,95,'7:95','ತರುವಾಯ ನಾವು ಕಷ್ಟದ ಸ್ಥಾನದಲ್ಲಿ ಸುಖವನ್ನು ಬದಲಿಸಿಕೊಟ್ಟೆವು. ಕೊನೆಗೆ ಅವರು ಚೆನ್ನಾಗಿ ಅಭಿವೃದ್ಧಿ ಹೊಂದಿದರು. ಆಗ ಅವರು, ನಮ್ಮ ಪೂರ್ವಿಕರಿಗೂ ಕಷ್ಟ - ಸುಖಗಳು ಬರುತ್ತಲೇ ಇದ್ದುವು ಎಂದು ಹೇಳತೊಡಗಿದರು. ಆಗ ಅವರಿಗರಿವಾಗದಂತೆ ನಾವು ಅವರನ್ನು ಹಠಾತ್ತನೆ ಶಿಕ್ಷಿಸಿದೆವು .');
INSERT INTO "kannada" VALUES(7,96,'7:96','ಆ ನಾಡಿನವರು ವಿಶ್ವಾಸವಿರಿಸುತ್ತಿದ್ದರೆ ಮತ್ತು ಜಾಗ್ರತೆ ಪಾಲಿಸುತ್ತಿದ್ದರೆ, ನಾವು ಅವರಿಗೆ ಆಕಾಶ ಮತ್ತು ಭೂಮಿಯಿಂದ ಸಮೃದ್ಧಿಯ ಬಾಗಿಲುಗಳನ್ನು ತೆರೆದು ಕೊಡುತ್ತಿದ್ದೆವು. ಆದರೆ ಅವರು ನಿಷೇಧಿಸಿದರು. ಆದುದರಿಂದ ಅವರ ಪ್ರವೃತ್ತಿಗಳಿಗನುಸಾರವಾಗಿ ಅವರನ್ನು ನಾವು ಶಿಕ್ಷಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(7,97,'7:97','ಆ ನಾಡಿನವರು ರಾತ್ರೆ ನಿದ್ರಿಸುತ್ತಿದ್ದಾಗ ನಮ್ಮ ಶಿಕ್ಷೆಯು ಬಂದೆರಗುವ ಬಗ್ಗೆ ಅವರು ನಿರ್ಭೀತ ರಾಗಿರುವರೇ?');
INSERT INTO "kannada" VALUES(7,98,'7:98','ಅಥವಾ ಆ ನಾಡಿನವರು ಹಗಲು ಹೊತ್ತಿನಲ್ಲಿ ಕ್ರೀಡಾ ಮಗ್ನರಾಗಿದ್ದಾಗ ನಮ್ಮ ಶಿಕ್ಷೆಯು ಎರ ಗುವ ಬಗ್ಗೆ ಅವರೇನು ನಿಶ್ಚಿಂತರಾಗಿರುವರೇ?');
INSERT INTO "kannada" VALUES(7,99,'7:99','ಅವರು ಅಲ್ಲಾಹನ ನಿಗೂಢ ಕಾರ್ಯತಂತ್ರದ ಬಗ್ಗೆ ನಿರ್ಭೀತರಾಗಿರುವರೇ? ವಾಸ್ತವದಲ್ಲಿ ನಷ್ಟ ಕ್ಕೊಳಗಾದ ಜನಾಂಗ ಮಾತ್ರವೇ ಅಲ್ಲಾಹನ ನಿಗೂಢ ತಂತ್ರದಿಂದ ನಿರ್ಭೀತವಾಗಿರುವುದು ');
INSERT INTO "kannada" VALUES(7,100,'7:100','ಭೂಮಿಯ ಪೂರ್ವ ನಿವಾಸಿಗಳ ಅನಂತರ ಅದರ ವಾರೀಸುದಾರರಾಗಿರುವವರಿಗೆ ನಾವಿಚ್ಛಿ ಸಿದರೆ ಅವರ ಪಾಪಗಳಿಗಾಗಿ ಅವರನ್ನು ಶಿಕ್ಷಿಸ ಬಲ್ಲೆವೆಂಬ ಪ್ರಜ್ಞೆಯು ಅವರನ್ನು ನೇರ ದಾರಿಗೆ ಹಚ್ಚಲಾರದೇ? ನಾವು ಅವರ ಹೃದಯಗಳ ಮೇಲೆ ಮುದ್ರೆಯೊತ್ತುತ್ತೇವೆ. ಅನಂತರ ಅವರು ಏನನ್ನೂ ಆಲಿಸುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(7,101,'7:101','ಆ ಪ್ರದೇಶಗಳ ಚರಿತ್ರೆಗಳಿಂದ ಕೆಲವನ್ನು ತಮ ಗೆ ನಾವು ವಿವರಿಸುತ್ತೇವೆ. ತಮ್ಮ ದೂತರು ಸುವ್ಯಕ್ತ ದೃಷ್ಟಾಂತಗಳ ಸಹಿತ ಅವರ ಬಳಿ ತಲುಪಿದರು. ಆಗ ಅವರು ಮೊದಲು ನಿಷೇಧಿಸಿದುದನ್ನು ಪುನಃ ಸ್ವೀಕರಿಸುವವರಾಗಿರಲಿಲ್ಲ. ನಾವು ಇದೇ ರೀತಿಯಲ್ಲಿ ಸತ್ಯನಿಷೇಧಿಗಳ ಹೃದಯಗಳ ಮೇಲೆ ಅಲ್ಲಾಹನು ಮುದ್ರೆಯೊತ್ತಿ ಬಿಡುವನು.');
INSERT INTO "kannada" VALUES(7,102,'7:102','ನಾವು ಅವರ ಪೈಕಿ ಹೆಚ್ಚಿನವರಲ್ಲಿ ಯಾವ ಕರಾರು ಪಾಲನೆಯನ್ನೂ ಕಾಣಲಿಲ್ಲ. ಮಾತ್ರವಲ್ಲ ಹೆಚ್ಚಿನವರು ಧಿಕ್ಕಾರಿಗಳಾಗಿರುವುದನ್ನೇ ಕಂಡೆವು.');
INSERT INTO "kannada" VALUES(7,103,'7:103','ಬಳಿಕ ಆ ಜನಾಂಗಗಳ ತರುವಾಯ ನಾವು ಮೂಸಾರನ್ನು ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳ ಸಹಿತ ಫಿರ್‍ಔನ್ ಮತ್ತು ಅವರ ಜನಾಂಗದ ಸರದಾರರ ಬಳಿಗೆ ಕಳುಹಿಸಿದೆವು. ಆದರೆ ಅವರು ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳನ್ನು ನಿಷೇಧಿಸಿದರು. ಆ ಕೆಡುಕರ ಪರಿಣಾಮವೇನಾಯಿತೆಂದು ನೋಡಿರಿ.');
INSERT INTO "kannada" VALUES(7,104,'7:104','ಮೂಸಾ ಹೇಳಿದರು; `ಓ ಫಿರ್‍ಔನ್, ನಾನು ಸರ್ವಲೋಕ ಪಾಲಕನ ಕಡೆಯಿಂದ ನಿಯುಕ್ತ ನಾದ ದೂತನಾಗಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(7,105,'7:105','ಅಲ್ಲಾಹನ ನಾಮವೆತ್ತಿ ಸತ್ಯದ ಹೊರತು ಇನ್ನೇನನ್ನೂ ಹೇಳದಿರುವುದೇ ಸಮಂಜಸ ವಾಗಿರುವುದು. ನಾನು ನಿಮ್ಮ ಬಳಿಗೆ ನನ್ನ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಸುವ್ಯಕ್ತ ಪ್ರಮಾಣ ದೊಂದಿಗೆ ಬಂದಿರುತ್ತೇನೆ. ಆದುದರಿಂದ ನೀನು ಬನೂ ಇಸ್‍ರಾಈಲರನ್ನು ನನ್ನ ಸಂಗಡ ಕಳುಹಿಸಿ ಕೊಡು .');
INSERT INTO "kannada" VALUES(7,106,'7:106','ಫಿರ್‍ಔನನು ಹೇಳಿದನು. `ನೀನು ದೃಷ್ಟಾಂತ ದೊಂದಿಗೆ ಬಂದಿದ್ದರೆ ಅದನ್ನು ಮುಂದಿಡು. ನೀನು ಸತ್ಯವಂತನಾಗಿದ್ದರೆ !');
INSERT INTO "kannada" VALUES(7,107,'7:107','ಮೂಸಾ ತಮ್ಮ ದಂಡವನ್ನು ಎಸೆದರು. ಹಠಾತ್ತನೆ ಅದೊಂದು ಜೀವಂತ ಹೆಬ್ಬಾವಾಗಿ ಮಾರ್ಪಟ್ಟಿತು.');
INSERT INTO "kannada" VALUES(7,108,'7:108','ಅವರು ತಮ್ಮ ಜೇಬಿನಿಂದ ಕೈ ಹೊರತೆಗೆದರು. ಆಗ ಅದು ಪ್ರೇಕ್ಷಕರ ಮುಂದೆ ಬೆಳ್ಳಗೆ ಹೊಳೆಯಿತು.');
INSERT INTO "kannada" VALUES(7,109,'7:109','ಫಿರ್‍ಔನನ ಜನಾಂಗದ ಸರದಾರರು ಪರಸ್ಪರ ಹೇಳಿದರು; ಖಂಡಿತ ಇವನೊಬ್ಬ ನಿಪುಣ ಮಾಟಗಾರನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(7,110,'7:110','ಅವನು ನಿಮ್ಮನ್ನು ನಿಮ್ಮ ನಾಡಿನಿಂದ ಹೊರ ಹಾಕಲಿಚ್ಛಿಸುತ್ತಿದ್ದಾನೆ. ಈಗ ನೀವೇನು ಆದೇಶ ಕೊಡುವಿರಿ?');
INSERT INTO "kannada" VALUES(7,111,'7:111','ಅವರು ಹೇಳಿದರು; ತಾವು ಅವನಿಗೂ ಅವನ ಸಹೋದರನಿಗೂ ಕಾಲಾವಕಾಶ ನೀಡಿರಿ. ಹಾಗೂ ನಗರಗಳಲ್ಲಿ ಜನರನ್ನು ಒಗ್ಗೂಡಿಸಲು ಆಳುಗಳನ್ನು ಕಳುಹಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(7,112,'7:112','ಅವರು ತಮ್ಮ ಬಳಿಗೆ ಎಲ್ಲ ನಿಪುಣ ಮಾಟಗಾರರನ್ನು ಕರೆದು ತರಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(7,113,'7:113','ಅಂತೆಯೇ ಮಾಟಗಾರರು ಫಿರ್‍ಔನನ ಬಳಿಗೆ ಬಂದರು. ಅವರು ಹೀಗೆ ಹೇಳಿದರು; ‘ನಾವು ಗೆದ್ದರೆ ನಮಗೆ ಪ್ರತಿಫಲವಿರುವುದು ಖಂಡಿತ ತಾನೇ?');
INSERT INTO "kannada" VALUES(7,114,'7:114','ಫಿರ್‍ಔನನು ಹೇಳಿದನು; ಹೌದು. ಜೊತೆಗೇ ನೀವು ನಮ್ಮ ನಿಕಟವರ್ತಿಗಳೂ ಆಗುವಿರಿ.');
INSERT INTO "kannada" VALUES(7,115,'7:115','ಮಾಟಗಾರರು ಹೇಳಿದರು; “ಓ ಮೂಸಾ, ಒಂದೋ ಮೊದಲು ನೀವು ನಿಮ್ಮ ದಂಡವನ್ನು ಎಸೆಯಿರಿ. ಇಲ್ಲವೇ ನಾವೇ ಎಸೆಯುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(7,116,'7:116','ಮೂಸಾರವರು, “ನೀವೇ ಎಸೆಯಿರಿ” ಎಂ ದರು. ಅವರು ತಮ್ಮ (ದಂಡ ಹಾಗೂ ಹಗ್ಗಗ ಳನ್ನು) ಎಸೆದಾಗ ಜನರ ದೃಷ್ಟಿಗಳನ್ನು ವಶೀಕ ರಿಸಿದರು. ಮತ್ತು ಜನರನ್ನು ಭಯ ಗ್ರಸ್ತಗೊ ಳಿಸಿದರು. ಅವರು ಭಾರೀ ದೊಡ್ಡ ಮಾಟವನ್ನೇ ಪ್ರಕಟಿಸಿದರು.');
INSERT INTO "kannada" VALUES(7,117,'7:117','ಆಗ ನಾವು ಮೂಸಾರಿಗೆ ಸಂದೇಶ ಕೊಟ್ಟೆವು. ‘ದಂಡವನ್ನು ಎಸೆಯಿರಿ’ ಎಂದು. ಅದನ್ನು ಎಸೆದೊಡನೆ ಅದು ಅವರ ಕೃತ್ರಿಮ ಮಾಟ ಸೃಷ್ಟಿ ಗಳನ್ನೆಲ್ಲ ನುಂಗುತ್ತಾ ಸಾಗಿತು.');
INSERT INTO "kannada" VALUES(7,118,'7:118','ಹೀಗೆ ಸತ್ಯ ಸತ್ಯವಾಗಿ ಪರಿಣಮಿಸಿತು. ಅವರ ಕೃತಕ ಸೃಷ್ಟಿ ಮಿಥ್ಯವಾಯಿತು.');
INSERT INTO "kannada" VALUES(7,119,'7:119','ಫಿರ್‍ಔನ್ ಮತ್ತು ಅವನ ಸಂಗಡಿಗರು ಸ್ಪರ್ಧಾ ರಂಗದಲ್ಲಿ ಪರಾಜಿತರಾಗಿ ಅವಮಾನಗೊಳ್ಳು ವಂತಾಯಿತು.');
INSERT INTO "kannada" VALUES(7,120,'7:120','ಮಾಟಗಾರರು ಸಾಷ್ಟಾಂಗವೆರಗುವಂತೆ ಬೀಳಿ ಸಲ್ಪಟ್ಟರು.');
INSERT INTO "kannada" VALUES(7,121,'7:121','“ನಾವು ಸರ್ವಲೋಕಪಾಲಕನನ್ನು ನಂಬಿದೆವು.');
INSERT INTO "kannada" VALUES(7,122,'7:122','ಮೂಸಾ ಮತ್ತು ಹಾರೂನ್ ನಂಬುವ ಪ್ರಭು ವನ್ನು” ಎಂದರವರು.');
INSERT INTO "kannada" VALUES(7,123,'7:123','ಫಿರ್‍ಔನನು ಹೇಳಿದನು; `ನಾನು ನಿಮಗೆ ಅಪ್ಪಣೆಕೊಡುವ ಮುನ್ನ ನೀವು ಅವನ ಮೇಲೆ ವಿಶ್ವಾಸವಿಟ್ಟಿರಾ? ಈ ನಾಡಿನಿಂದ ಇಲ್ಲಿಯ ನಿವಾಸಿಗಳನ್ನು ಹೊರಗಟ್ಟುವುದಕ್ಕಾಗಿ ನೀವು ಇತ್ತಂಡದವರು ಸೇರಿ ಹೂಡಿದ ಒಳಸಂಚಾಗಿತ್ತು ಇದು. ಇದರ ಪರಿಣಾಮವನ್ನು ನೀವು ಆಮೇಲೆ ತಿಳಿಯಲಿರುವಿರಿ.');
INSERT INTO "kannada" VALUES(7,124,'7:124','ನಾನು ನಿಮ್ಮ ಕೈಗಳನ್ನೂ ಕಾಲುಗಳನ್ನೂ ವಿರುದ್ಧ ದಿಕ್ಕಿನಿಂದ ಕಡಿಸಿ ಬಿಡುವೆನು. ಅನಂತರ ನಿಮ್ಮೆಲ್ಲರನ್ನು ಶಿಲುಬೆಗೇರಿಸುವೆನು ಖಂಡಿತ’.');
INSERT INTO "kannada" VALUES(7,125,'7:125','ಅವರು ಪ್ರತಿಕ್ರಿಯಿಸಿದರು; ‘ನಾವಂತು ನಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಗೇ ಮರಳುವವರಾಗಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(7,126,'7:126','ನಮ್ಮ ಪ್ರಭುವಿನ ದೃಷ್ಟಾಂತಗಳು ನಮಗೆ ಬಂದಾಗ ನಾವು ಅವುಗಳ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿ ಸಿದೆವೆಂಬ ಕಾರಣಕ್ಕಾಗಿಯೇ ವಿನಾ ನೀನು ನಮ್ಮನ್ನು ಶಿಕ್ಷಿಸುತ್ತಿಲ್ಲ. ಓ ನಮ್ಮ ಪ್ರಭೂ, ನಮಗೆ ಸಹನೆಯನ್ನು ಸುರಿಸಿಕೊಡು. ಮುಸ್ಲಿ ಮರಾಗಿರುವ ಸ್ಥಿತಿಯಲ್ಲಿ ನೀನು ನಮ್ಮನ್ನು ಮರಣಗೊಳಿಸು’ .');
INSERT INTO "kannada" VALUES(7,127,'7:127','ಫಿರ್‍ಔನನ ಜನಾಂಗದ ಸರದಾರರು ಹೀಗೆಂದರು ; ‘ದೇಶದಲ್ಲಿ ಗೊಂದಲಗಳನ್ನು ಹಬ್ಬಿಸು ತ್ತಿರುವ ಹಾಗೂ ನಿನ್ನನ್ನೂ ನಿನ್ನ ಆರಾಧ್ಯರ ಆರಾಧನೆಯನ್ನು ತೊರೆಯಲುದ್ದೇಶಿಸಿದ ಮೂಸಾ ಮತ್ತು ಅವನ ಜನಾಂಗವನ್ನು ಸುಮ್ಮನೆ ಬಿಟ್ಟು ಬಿಡುವೆಯಾ?’ ಫಿರ್‍ಔನನು ಹೇಳಿದನು; ನಾವು ಅವರ (ಇಸ್ರಾಈಲರ) ಗಂಡು ಮಕ್ಕಳನ್ನು ಕೊಲೆ ಮಾಡಿಸುವೆವು. ಮತ್ತು ಅವರ ಹೆಣ್ಮಕ್ಕಳನ್ನು ಜೀವಂತವಿರಲು ಬಿಡುವೆವು. ನಿಸ್ಸಂದೇಹವಾಗಿ ನಾವು ಅವರ ಮೇಲೆ ಅಧಿಕಾರ ಹೊಂದಿರುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(7,128,'7:128','ಮೂಸಾ ತಮ್ಮ ಜನಾಂಗದೊಡನೆ ಹೇಳಿದರು; ನೀವು ಅಲ್ಲಾಹನೊಡನೆ ಸಹಾಯ ಯಾಚಿಸಿರಿ ಮತ್ತು ತಾಳ್ಮೆವಹಿಸಿರಿ. ಭೂಮಿ ಅಲ್ಲಾಹನದು. ಅವನು ತನ್ನ ದಾಸರ ಪೈಕಿ ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ಅದರ ವಾರಿಸುದಾರರಾಗಿ ಮಾಡುತ್ತಾನೆ. ಅಂತಿಮ ವಿಜಯವು ಧರ್ಮನಿಷ್ಠರ ಪರವಾಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(7,129,'7:129','ಅವರ ಜನಾಂಗದವರು ಹೇಳಿದರು; ನೀವು ನಮ್ಮ ಬಳಿಗೆ (ದೂತರಾಗಿ) ಬರುವುದಕ್ಕೆ ಮುಂಚೆ ಯೂ ನೀವು ಬಂದ ಬಳಿಕವೂ ನಾವು ಮರ್ಧಿಸಲ್ಪಟ್ಟೆವು. ಆಗ ಮೂಸಾ ಹೇಳಿದರು; - ನಿಮ್ಮ ಪ್ರಭು ಬಹುಶಃ ನಿಮ್ಮ ಶತ್ರುವನ್ನು ನಾಶಗೊಳಿಸಿ ಯಾನು. ಹಾಗೂ ನಿಮ್ಮನ್ನು ಭೂಮಿಯಲ್ಲಿ ಪ್ರತಿನಿಧಿಗಳನ್ನಾಗಿ ಮಾಡುವನು. ಆಗ ಅವನು ನೀವು ಹೇಗೆ ವರ್ತಿಸುತ್ತೀರೆಂದು ನೋಡುವನು.');
INSERT INTO "kannada" VALUES(7,130,'7:130','ಫಿರ್‍ಔನನ ಜನರನ್ನು ನಾವು ಅನೇಕ ವರ್ಷಗಳ ವರೆಗೆ ಬರಗಾಲ ಮತ್ತು ಫಲಗಳ ಇಳಿಮುಖದ ಮೂಲಕ ಶಿಕ್ಷಿಸಿದೆವು. ಅವರು ಯೋಚಿಸಿ ಗ್ರಹಿಸಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(7,131,'7:131','ಆದರೆ, ಅವರಿಗೆ ಸುಭಿಕ್ಷೆ ಬಂದಾಗ ಅವರು, ‘ನಾವು ಇದಕ್ಕೆ ಅರ್ಹರು’ ಎನ್ನುವರು. ಕೆಡುಕು ಬಂದಾಗ ‘ಇದು ಮೂಸಾ ಮತ್ತು ಅವರ ಸಂಗ ಡಿಗರ ಅಪಶಕುನ’ ಎನ್ನುವರು. ಆದರೆ ಅವರ ಅಪಶಕುನವು ಅಲ್ಲಾಹನ ಬಳಿಯಲ್ಲೇ ಇದೆ. ಆ ಕುರಿತು ಅವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಅರಿಯು ವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(7,132,'7:132','ಅವರು ಮೂಸಾರೊಡನೆ, ‘ನೀನು ನಮ್ಮನ್ನು ವಶೀಕರಿಸಲು ಎಂತಹ ದೃಷ್ಟಾಂತವನ್ನು ತಂದರೂ ನಾವು ನಿನ್ನನ್ನು ನಂಬುವವರಲ್ಲ’ ಎಂದರು .');
INSERT INTO "kannada" VALUES(7,133,'7:133','ಆಗ ನಾವು ಅವರ ಕಡೆಗೆ ಜಲ ಪ್ರವಾಹ, ಕತ್ತರಿ ಗಿಳಿ, ಕೀಟ, ಕಪ್ಪೆಗಳು ಹಾಗೂ (ನೀರಿನಲ್ಲಿ) ರಕ್ತವನ್ನು ವ್ಯಕ್ತವಾದ ದೃಷ್ಟಾಂತಗಳನ್ನಾಗಿ ಕಳುಹಿಸಿದೆವು. ಆದರೆ, ಅವರು ಅಹಂಕಾರ ವೆಸಗುತ್ತಲೇ ಸಾಗಿದರು. ಅವರು ದುಷ್ಟ ಜನಾಂಗವಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(7,134,'7:134','ಅವರಿಗೆ ಶಿಕ್ಷೆ ಬಾಧಿಸಿದಾಗ ಅವರು ಹೇಳಿದರು; `ಓ ಮೂಸಾ, ನಿನ್ನ ಪ್ರಭು ನಿನಗೆ ಕೊಟ್ಟ ವಚನದ ಆಧಾರದಲ್ಲಿ ನಮಗಾಗಿ ಅವನಲ್ಲಿ ಪ್ರಾರ್ಥಿಸು. ನೀನು ನಮ್ಮ ಮೇಲಿಂದ ಈ ವಿಪತ್ತನ್ನು ನಿವಾರಿಸಿ ಬಿಟ್ಟರೆ ನಾವು ನಿನ್ನ ಧರ್ಮದಲ್ಲಿ ವಿಶ್ವಾ ಸವಿರಿಸುವೆವು ಮತ್ತು ಬನೂಇಸ್‍ರಾಈಲರನ್ನು ನಿನ್ನೊಂದಿಗೆ ಕಳುಹಿಸುವೆವು’.');
INSERT INTO "kannada" VALUES(7,135,'7:135','ಅಂತೆಯೇ ಅವರು ತಲುಪಲಿರುವ ಒಂದು ನಿಶ್ಚಿತ ಅವಧಿಯವರೆಗೆ ಶಿಕ್ಷೆಯನ್ನು ನಾವು ದೂರೀಕರಿಸಿದಾಗ ಅವರು ವಚನ ಭಂಗ ಮಾಡಿದರು.');
INSERT INTO "kannada" VALUES(7,136,'7:136','ಹಾಗೆ ನಾವು ಅವರನ್ನು ಶಿಕ್ಷಿಸಿದೆವು. ಅವರನ್ನು ಸಮುದ್ರದಲ್ಲಿ ಮುಳುಗಿಸಿಬಿಟ್ಟೆವು. ಏಕೆಂದರೆ, ಅವರು ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿ ದ್ದರು ಮತ್ತು ಅವುಗಳ ಬಗ್ಗೆ ಅಲಕ್ಷ್ಯರಾಗಿದ್ದರು .');
INSERT INTO "kannada" VALUES(7,137,'7:137','ದುರ್ಬಲಗೊಳಿಸಲ್ಪಟ್ಟಿದ್ದ ಆ ಜನರನ್ನು ನಾವು ಸಮೃದ್ಧಿಯನ್ನಿರಿಸಿದ್ದ ಭೂಭಾಗದ ಪೂರ್ವ- ಪಶ್ಚಿಮಗಳ ವಾರೀಸುದಾರರನ್ನಾಗಿ ಮಾಡಿದೆವು. ಈ ರೀತಿಯಲ್ಲಿ ಇಸ್‍ರಾಈಲ ಸಂತತಿಗ ಳಿಗೆ ನೀಡಿದ್ದ ನಿನ್ನ ಪ್ರಭುವಿನ ಉತ್ತಮ ವಾಗ್ದಾನವು ನೆರವೇರಿತು. ಅವರು ತಾಳ್ಮೆ ವಹಿಸಿದ್ದಕ್ಕಾಗಿ! ಫಿರ್‍ಔನ್ ಮತ್ತು ಅವನ ಜನಾಂಗದವರು ನಿರ್ಮಿಸಿದ್ದ ಕಾಮಗಾರಿ ಹಾಗೂ ಕಟ್ಟಡಗಳನ್ನೆಲ್ಲ ನಾವು ಸರ್ವನಾಶ ಮಾಡಿಬಿಟ್ಟೆವು.');
INSERT INTO "kannada" VALUES(7,138,'7:138','ನಾವು ಇಸ್‍ರಾಈಲ ಸಂತತಿಗಳನ್ನು ಸಮು ದ್ರದಿಂದ ದಾಟಿಸಿದೆವು . ಅನಂತರ ಅವರು ಮುಂದೆ ಸಾಗಿದರು. ದಾರಿಯಲ್ಲಿ ತಮ್ಮ ವಿಗ್ರಹಗಳ ಮುಂದೆ ಜಪ ಕೂತ್ತಿದ್ದ ಒಂದು ಜನಾಂಗದ ಬಳಿಗೆ ತಲಪಿದಾಗ, ‘ಓ ಮೂಸಾ, ಇವರಿಗೆ ದೇವರು ಇರುವಂತೆ ನಮಗೂ ಒಂದು ದೇವರನ್ನು ಮಾಡಿಕೊಡು’ ಎಂದರು. ಮೂಸಾ ಹೇಳಿದರು, ‘ನಿಜಕ್ಕೂ ನೀವು ಅಜ್ಞಾನಿಗಳು .');
INSERT INTO "kannada" VALUES(7,139,'7:139','ಇವರು ತಮ್ಮ ಧರ್ಮದಲ್ಲಿ ನಾಶವಾಗುವವರು. ಮತ್ತು ಇವರು ಮಾಡುತ್ತಿರುವುದು ಕೇವಲ ವ್ಯರ್ಥ ಕಾರ್ಯ’.');
INSERT INTO "kannada" VALUES(7,140,'7:140','ಮೂಸಾ ಹೇಳಿದರು; ನಾನು ನಿಮಗೆ ಅಲ್ಲಾಹ ನಲ್ಲದ ಬೇರೊಬ್ಬ ದೇವನನ್ನು ಹುಡುಕಬೇಕೇ? (ಸಮಕಾಲೀನ) ಸರ್ವ ಜಗತ್ತಿನವರಿಗಿಂತ ನಿಮಗೆ ಶ್ರೇಷ್ಟತೆ ಪ್ರದಾನ ಮಾಡಿದವನು ಅಲ್ಲಾ ಹನೇ ಆಗಿದ್ದಾನಲ್ಲ?');
INSERT INTO "kannada" VALUES(7,141,'7:141','ನಿಮ್ಮನ್ನು ಕಠಿಣವಾಗಿ ದಂಡಿಸುತ್ತಿದ್ದ ಫಿರ್‍ಔ ನನ ಕೂಟದಿಂದ ನಾವು ನಿಮ್ಮನ್ನು ರಕ್ಷಿಸಿದ ಸಂದರ್ಭ. ಅವರು ನಿಮ್ಮ ಗಂಡು ಮಕ್ಕಳನ್ನು ವಧಿಸುತ್ತಿದ್ದರು ಮತ್ತು ನಿಮ್ಮ ಹೆಣ್ಮಕ್ಕಳನ್ನು ಬದುಕಲು ಬಿಡುತ್ತಿದ್ದರು. ಅದರಲ್ಲಿ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ನಿಮಗೊಂದು ದೊಡ್ಡ ಪರೀಕ್ಷೆ ಇತ್ತು.');
INSERT INTO "kannada" VALUES(7,142,'7:142','ನಾವು ಮೂಸಾರವರಿಗೆ ಮೂವತ್ತು ರಾತ್ರಿಗಳ ವಾಗ್ದಾನವಿತ್ತೆವು. ಹತ್ತು ದಿನಗಳನ್ನೂ ಸೇರಿಸಿ ಅದನ್ನು ಪೂರ್ತಿ ಮಾಡಿದೆವು. ಹೀಗೆ ಅವರ ಪ್ರಭು ನಿಶ್ಚಯಿಸಿದ್ದ ನಲ್ವತ್ತು ರಾತ್ರಿಗಳ ಕಾಲಾ ವಧಿ ಪೂರ್ಣಗೊಂಡಿತು. ಮೂಸಾ (ಸಂಭಾಷಣೆಗೆ ಪರ್ವತದ ಕಡೆಗೆ ಹೋಗುವ ವೇಳೆ) ತಮ್ಮ ಸಹೋದರ ಹಾರೂನರೊಡನೆ, ‘ನನ್ನ ಪರವಾಗಿ ನೀನು ನನ್ನ ಜನಾಂಗದ ಪ್ರಾತಿನಿಧ್ಯ ವನ್ನು ವಹಿಸಿಕೊಳ್ಳಬೇಕು. ಒಳ್ಳೆಯ ಕಾರ್ಯ ವೆಸಗಬೇಕು ಕೇಡುಂಟು ಮಾಡುವವರ ಮಾರ್ಗವನ್ನು ಅನುಸರಿಸಬಾರದು’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(7,143,'7:143','ನಮ್ಮ ನಿಶ್ಚಿತ ಸಮಯಕ್ಕೆ ಸರಿಯಾಗಿ ಅವರು ತಲಪಿದಾಗ ಅವರ ಪ್ರಭು ಅವರೊಡನೆ ಮಾತನಾಡಿದನು. ಅವರು, `ಓ ನನ್ನ ಪ್ರಭೂ! ನನಗೆ ನಿನ್ನ ದರ್ಶನ ನೀಡು. ನಿನ್ನನ್ನು ನಾನು ನೋಡುವಂತಾಗಲಿ’ ಎಂದು ಭಿನ್ನವಿಸಿದಾಗ, ಅಲ್ಲಾಹನು ‘ನೀನು ನನ್ನನ್ನು ನೋಡಲಾರೆ. ಆದರೆ ನೀನು ಆ ಪರ್ವತವನ್ನು ನೋಡು. ಅದು ತನ್ನ ಸ್ಥಾನದಲ್ಲಿ ಸ್ಥಿರವಾಗಿ ನಿಂತರೆ ನಂತರ ನೀನು ನನ್ನನ್ನು ನೋಡಬಲ್ಲೆ’ ಎಂದನು. ಹಾಗೆ ಅವರ ಪ್ರಭು ಪರ್ವತಕ್ಕೆ ದರ್ಶನ ನೀಡಿದಾಗ ಅದು ಧೂಳೀಪಟವಾಯಿತು. ಮೂಸಾ ಮೂರ್ಛೆ ತಪ್ಪಿ ಬಿದ್ದರು. ಬಂದಾಗ ಹೀಗೆಂದರು, ‘ನಿನ್ನ ಪರಮ ಪರಿಶುದ್ಧತೆಯನ್ನು ನಾನು ಕೊಂಡಾಡು ತ್ತಿದ್ದೇನೆ. ನಾನು ನಿನ್ನಲ್ಲಿ ಪಶ್ಚಾತ್ತಾಪ ಪಡುತ್ತೇನೆ ಮತ್ತು ಎಲ್ಲರಿಗಿಂತಲೂ ಮೊದಲು ವಿಶ್ವಾಸವಿಡು ವವನು ನಾನಾಗಿರುತ್ತೇನೆ’.');
INSERT INTO "kannada" VALUES(7,144,'7:144','ಅಲ್ಲಾಹನು `ಓ ಮೂಸಾ, ನನ್ನ ಸಂದೇಶ ರವಾನೆಗೂ ನನ್ನೊಂದಿಗೆ ನೇರ ಸಂಭಾಷಣೆಗೂ ಎಲ್ಲರಿಗಿಂತಲೂ ಮಿಗಿಲಾಗಿ ನಿಮ್ಮನ್ನು ಆರಿಸಿ ಕೊಂಡಿದ್ದೇನೆ. ಆದ ಕಾರಣ ನಾವು ನಿಮಗೆ ಕೊಟ್ಟುದನ್ನು ಸ್ವೀಕರಿಸಿರಿ. ಕೃತಜ್ಞರ ಸಾಲಿಗೆ ಸೇರಿರಿ’ ಎಂದನು .');
INSERT INTO "kannada" VALUES(7,145,'7:145','ಎಲ್ಲ ಕಾರ್ಯಗಳಿಗೆ ಸಂಬಂಧಿಸಿದ ತತ್ವೋಪ ದೇಶ ಗಳನ್ನೂ, ಎಲ್ಲ ವಿಚಾರಗಳಿಗೆ ಕುರಿತಾದ ವಿಷದೀಕರಣಗಳನ್ನೂ ಅವರಿಗೆ ನಾವು ಫಲಕಗಳ ಮೇಲೆ ಬರೆದುಕೊಟ್ಟೆವು. ಮತ್ತು ಹೀಗೆಂದೆವು: ಈ ಮಾರ್ಗದರ್ಶನಗಳನ್ನು ಬಿಗಿಯಾಗಿ ಹಿಡಿದುಕೊಳ್ಳಿರಿ ಮತ್ತು ಇವುಗಳ ಅತ್ಯಂತ ಉತ್ತಮ ಕಾರ್ಯಗಳನ್ನು ಸ್ವೀಕರಿಸುವಂತೆ ನಿಮ್ಮ ಜನಾಂಗಕ್ಕೆ ಅಪ್ಪಣೆ ಕೊಡಿರಿ. ಧಿಕ್ಕಾರಿಗಳ ನಿವಾಸವನ್ನು ಸದ್ಯವೇ ನಿಮಗೆ ತೋರಿಸಿಕೊಡುವೆನು .');
INSERT INTO "kannada" VALUES(7,146,'7:146','ಅನ್ಯಾಯವಾಗಿ ಭೂಮಿಯಲ್ಲಿ ಅಹಂಕಾರ ಪಡುವವರನ್ನು ನನ್ನ ನಿದರ್ಶನಗಳಿಂದ ತಿರುಗಿಸಿಬಿಡುವೆನು. ಅವರು ಎಲ್ಲ ನಿದರ್ಶನಗಳನ್ನು ಕಂಡರೂ ಅವುಗಳಲ್ಲಿ ವಿಶ್ವಾಸವಿಡು ವವರಲ್ಲ. ಅವರು ನೇರ ಮಾರ್ಗವನ್ನು ಕಂಡರೂ ಅದನ್ನವರು ತಮ್ಮ ಮಾರ್ಗವನ್ನಾಗಿ ಸ್ವೀಕರಿಸಲಾರರು ಮತ್ತು ವಾಮಮಾರ್ಗವನ್ನು ಕಂಡರೆ ಅದನ್ನು ಮಾರ್ಗವಾಗಿ ಸ್ವೀಕರಿಸು ವರು. ಹೀಗೆ ಅವರನ್ನು ತಿರುಗಿಸಿ ಬಿಟ್ಟದ್ದು ಅವರು ನಮ್ಮ ನಿದರ್ಶನಗಳನ್ನು ನಿಷೇಧಿಸಿದ ಮತ್ತು ಅವುಗಳ ಬಗ್ಗೆ ಅಲಕ್ಷ್ಯರಾಗಿದ್ದ ಕಾರಣದಿಂದಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(7,147,'7:147','ನಮ್ಮ ನಿದರ್ಶನಗಳನ್ನೂ ಪರಲೋಕದ ಭೇಟಿಯನ್ನೂ ನಿಷೇಧಿಸಿದವರ ಕರ್ಮಗಳು ನಿಷ್ಫಲ ವಾಗುವುವು. ಅವರ ಕರ್ಮಫಲವಲ್ಲದೆ ಬೇರೆ ಫಲ ಅವರಿಗೆ ನೀಡಲಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(7,148,'7:148','ಮೂಸಾರವರು ಹೋದ ನಂತರ ಜನಾಂಗದ ವರು ತಮ್ಮ ಆಭರಣಗಳಿಂದ ತಯಾರಿಸಿದ ಸ್ವರ ಹೊರಡಿಸುವ ಒಂದು ಕರುವಿನ ದೇಹವನ್ನು ದೇವರನ್ನಾಗಿ ಮಾಡಿದರು. ಅದು ಅವರೊಡನೆ ಮಾತಾಡುವುದಿಲ್ಲವೆಂದೂ ಮಾರ್ಗದರ್ಶನ ಮಾಡುವುದಿಲ್ಲವೆಂದೂ ಅವರು ಕಾಣದಾದರೇ? ಆದರೂ ಅವರು ಅದನ್ನು ಆರಾಧ್ಯವನ್ನಾಗಿ ಮಾಡಿಕೊಂಡರು. ಹಾಗೆ ಅವರು ಅಕ್ರಮಿ ಗಳಾದರು.');
INSERT INTO "kannada" VALUES(7,149,'7:149','ಅನಂತರ ತಾವು ದಾರಿ ತಪ್ಪಿದ್ದೇವೆನ್ನುವುದು ಅವರಿಗೆ ಮನವರಿಕೆಯಾಗಿ ದುಃಖಿಸಬೇಕಾಗಿ ಬಂದ ಸಂದರ್ಭದಲ್ಲಿ ಅವರು “ನಮ್ಮ ಪ್ರಭು ನಮ್ಮ ಮೇಲೆ ಕೃಪೆ ತೋರದಿದ್ದರೆ ಮತ್ತು ನಮ್ಮನ್ನು ಕ್ಷಮಿಸದಿದ್ದರೆ ನಾವು ಹತಭಾಗ್ಯರ ಸಾಲಿಗೆ ಸೇರುವೆವು” ಎಂದು ಹೇಳತೊಡ ಗಿದರು.');
INSERT INTO "kannada" VALUES(7,150,'7:150','ಕುಪಿತರಾಗಿಯೂ, ದುಃಖಿತರಾಗಿಯೂ ಹಿಂದಿ ರುಗಿದ ಮೂಸಾರವರು; `ನನ್ನ ಅನುಪಸ್ಥಿತಿಯಲ್ಲಿ ನೀವು ಅತ್ಯಂತ ಕೆಟ್ಟ ರೀತಿಯಲ್ಲಿ ನನ್ನನ್ನು ಪ್ರತಿನಿಧೀಕರಿಸಿದಿರಿ! ನಿಮ್ಮ ಪ್ರಭುವಿನ ಆಜ್ಞೆಗೆ ಕಾಯದೆ ಅವಸರಪಟ್ಟಿರಲ್ಲವೇ?’ ಎಂದರು. ಫಲಕಗಳನ್ನು ಎಸೆದು ಬಿಟ್ಟರು ಮತ್ತು ತಮ್ಮ ಸಹೋದರನ ತಲೆಗೂದಲನ್ನು ಹಿಡಿದು ತನ್ನೆಡೆಗೆ ಎಳೆದರು. ಹಾರೂನರು “ಓ ನನ್ನ ಮಾತೆಯ ಪುತ್ರನೇ, ಇವರು ನನ್ನನ್ನು ದುರ್ಬಲನನ್ನಾಗಿ ಮಾಡಿಬಿಟ್ಟರು. ನನ್ನನ್ನು ಕೊಂದು ಬಿಡುವುದರಲ್ಲಿದ್ದರು. ಆದ್ದರಿಂದ ಶತ್ರುಗಳಿಗೆ ನನ್ನ ಮೇಲೆ ನಗುವ ಸಂದರ್ಭ ಕೊಡಬೇಡ ಮತ್ತು ಅಕ್ರಮಿ ಜನಾಂಗದೊಂದಿಗೆ ನನ್ನನ್ನು ಸೇರಿಸಬೇಡ” ಎಂದರು .');
INSERT INTO "kannada" VALUES(7,151,'7:151','ಆಗ ಮೂಸಾ! ‘ಓ ನನ್ನ ಪ್ರಭೂ, ನನ್ನನ್ನೂ ನನ್ನ ಸಹೋದರನನ್ನೂ ಕ್ಷಮಿಸು. ನಿನ್ನ ಕರುಣಾ ಕಟಾಕ್ಷದಲ್ಲಿ ನಮ್ಮನ್ನು ಸೇರಿಸು. ನೀನು ಕರುಣಾಳುಗಳಲ್ಲಿ ಅತಿ ಶ್ರೇಷ್ಟ ಕರುಣಾಳು’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(7,152,'7:152','ಪಶುವಿನ ಕರುವನ್ನು ಆರಾಧ್ಯನಾಗಿಸಿದವರಿಗೆ ಅವರ ಪ್ರಭುವಿನ ಕ್ರೋಧ ತಟ್ಟುವುದು. ಐಹಿಕ ಜೀವನದಲ್ಲಿ ನಿಂದೆ ಬಾಧಿಸುವುದು. ಅದೇ ರೀತಿ ಸುಳ್ಳಾರೋಪಗಾರರನ್ನು ನಾವು ಶಿಕ್ಷಿಸುವೆವು .');
INSERT INTO "kannada" VALUES(7,153,'7:153','ದುಷ್ಕರ್ಮಗಳನ್ನೆಸಗಿ ಅನಂತರ ಅದರಿಂದ ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು (ಅಲ್ಲಾಹನಲ್ಲಿ) ವಿಶ್ವಾಸ ಸ್ವೀಕರಿಸಿದವರು. ನಿಶ್ಚಯವಾಗಿಯೂ ಅದರ (ಪಶ್ಚಾತ್ತಾಪದ) ಬಳಿಕ ನಿನ್ನ ಪ್ರಭು ಕ್ಷಮಿಸುವವನೂ ಕರುಣಾಮಯಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(7,154,'7:154','ಮೂಸಾರವರ ಕೋಪ ತಣಿದಾಗ ಅವರು ಆ ಫಲಕಗಳನ್ನು ಎತ್ತಿಕೊಂಡರು. ತಮ್ಮ ಪ್ರಭುವನ್ನು ಭಯಪಡುವವರಿಗೆ ಅದರಲ್ಲಿ ಮಾರ್ಗದರ್ಶನ ಮತ್ತು ಅನುಗ್ರಹ ಲಿಖಿತ ಗೊಂಡಿತ್ತು.');
INSERT INTO "kannada" VALUES(7,155,'7:155','ನಮ್ಮ ನಿಶ್ಚಿತ ಸಮಯಕ್ಕೆ ಸರಿಯಾಗಿ ತಮ್ಮ ಜನಾಂಗದ ಎಪ್ಪತ್ತು ಮಂದಿಯನ್ನು ಮೂಸಾ ಆರಿಸಿಕೊಂಡರು . ಒಂದು ಪ್ರಚಂಡ ಭೂಕಂಪವು ಅವರನ್ನು ಬಾಧಿಸಿದಾಗ ಮೂಸಾರು ಹೀಗೆಂದರು; “ಓ ನನ್ನ ಪ್ರಭೂ, ನೀನಿಚ್ಚಿಸುತ್ತಿದ್ದರೆ ಇವರನ್ನೂ ನನ್ನನ್ನೂ ಈ ಮೊದಲೇ ನಾಶಪಡಿಸಬಹುದಿತ್ತು. ನಮ್ಮ ಪೈಕಿ ಕೆಲವು ತಿಳಿಗೇಡಿಗಳು ಮಾಡಿದ್ದ ಅಪರಾಧಕ್ಕಾಗಿ ನಮ್ಮನ್ನು ನಾಶಗೊಳಿಸುವೆಯಾ? ಇದು ನಿನ್ನ ಪರೀಕ್ಷೆಯೇ ಸರಿ. ನೀನು ಇದರ ಮೂಲಕ ನಿನಗಿಷ್ಟ ಬಂದವರನ್ನು ಪಥಭ್ರಷ್ಟಗೊಳಿಸುತ್ತೀ ಮತ್ತು ನಿನಗಿಷ್ಟ ಬಂದವರಿಗೆ ಸನ್ಮಾರ್ಗದರ್ಶನ ನೀಡುತ್ತೀ. ನೀನೇ ನಮ್ಮ ಉಸ್ತುವಾರಿದಾರನು. ನಮ್ಮನ್ನು ಕ್ಷಮಿಸು. ನಮಗೆ ಕೃಪೆದೋರು, ನೀನು ಕ್ಷಮಿಸುವವರಲ್ಲಿ ಅತಿ ಶ್ರೇಷ್ಠನಾಗಿರುವೆ.');
INSERT INTO "kannada" VALUES(7,156,'7:156','ಈ ಇಹಲೋಕದಲ್ಲೂ ಪರಲೋಕದಲ್ಲೂ ನಮಗೆ ಒಳಿತನ್ನು ಲಿಖಿತಗೊಳಿಸು. ನಾವು ನಿನ್ನ ಕಡೆಗೆ ಮರಳಿದ್ದೇವೆ”. ಅವನು (ಅಲ್ಲಾಹು) ಹೇಳಿದನು; ನನ್ನ ಶಿಕ್ಷೆಯನ್ನು ನಾನಿಚ್ಚಿಸಿ ದವರಿಗೆ ತಗಲಿಸುತ್ತೇನೆ. ಆದರೆ ನನ್ನ ಕರುಣೆಯು ಸರ್ವ ವಸ್ತುವನ್ನೂ ವ್ಯಾಪಿಸಿ ಕೊಂಡಿದೆ . (ಅಲ್ಲಾಹನನ್ನು) ಭಯಪಟ್ಟು ಜೀವಿಸುವ, ಝಕಾತ್ ಕೊಡುವ ಮತ್ತು ನನ್ನ ನಿದರ್ಶನಗಳ ಮೇಲೆ ವಿಶ್ವಾಸ ವಿರಿಸುವ ವರಿಗಾಗಿ (ಪರಲೋಕದಲ್ಲಿ) ಆ ಕರುಣೆಯನ್ನು ನಾನು ಲಿಖಿತಗೊಳಿಸುವೆನು.');
INSERT INTO "kannada" VALUES(7,157,'7:157','ಅವರೆಂದರೆ, ತಮ್ಮ ಬಳಿಯಲ್ಲಿರುವ (ಪ್ರವಾದಿ ಮಹಿಮೆಯನ್ನು) ಬರೆಯಲಾದ `ತೌರಾತ್'' ಮತ್ತು `ಇನ್‍ಜೀಲ್''ಗಳಲ್ಲಿ ಪ್ರಸ್ತಾಪಿಸಲ್ಪಟ್ಟಿರುವ ದೇವದೂತ ನಿರಕ್ಷರಿ ಪ್ರವಾದಿಯ ಅನುಸರಣೆಯನ್ನು ಸ್ವೀಕರಿಸಿ ದವರು . ಇವರು ಅವರಿಗೆ ಸತ್ಕರ್ಮದ ಆದೇಶ ನೀಡುತ್ತಾರೆ ಮತ್ತು ದುಷ್ಕøತ್ಯದಿಂದ ತಡೆಯುತ್ತಾರೆ. ಅವರಿಗೆ ಶುದ್ಧ ವಸ್ತುಗಳನ್ನು ಅನುವದನೀಯಗೊಳಿಸುತ್ತಾರೆ ಮತ್ತು ಅಶುದ್ಧ ವಸ್ತುಗಳನ್ನು ನಿಷಿದ್ಧಗೊಳಿಸುತ್ತಾರೆ. ಅವರ ಮೇಲೆ ಹೇರಲಾದ ಹೊರೆಗಳನ್ನು ಇಳಿಸುತ್ತಾರೆ . ಅವರಿಗೆ ಬಿಗಿಯಲಾದ ಸಂಕೋಲೆಗಳನ್ನು ಬಿಡಿಸುತ್ತಾರೆ. ಆದುದರಿಂದ ಇವರ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸುವವರು, ಇವರನ್ನು ಗೌರವಿಸು ವವರು, ಇವರಿಗೆ ಸಹಾಯ ಮಾಡುವವರು ಮತ್ತು ಇವರೊಂದಿಗೆ ಇಳಿಸಲಾದ ಪ್ರಕಾಶ ವನ್ನು (ಖುರ್‍ಆನನ್ನು) ಅನುಸರಿಸುವವರು ಯಾರೋ ಅವರೇ ಜಯಜೀತರು.');
INSERT INTO "kannada" VALUES(7,158,'7:158','(ಓ ನಬಿಯರೇ,) ಹೇಳಿರಿ ; `ಓ ಜನರೇ, ನಾನು ನಿಮ್ಮೆಲ್ಲರ ಕಡೆಗೆ ಭೂಮಿ-ಆಕಾಶಗಳ ಆಧಿಪತ್ಯ ಹೊಂದಿರುವ ಅಲ್ಲಾಹನ ದೂತ ನಾಗಿರುತ್ತೇನೆ . ಅವನ ಹೊರತು ಆರಾಧ್ಯ ನಿಲ್ಲ. ಅವನೇ ಜೀವನ ನೀಡುವವನು. ಅವನೇ ಮರಣ ಕೊಡುವವನು. ಹಾಗಾಗಿ ಅಲ್ಲಾಹ ನಲ್ಲೂ, ಮತ್ತು ಅಲ್ಲಾಹು ಹಾಗೂ ಅವನ ವಚ ನಗಳಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸಿದ ನಿರಕ್ಷರಿ ಪ್ರವಾದಿ ಯಾಗಿರುವ ಅವನ ಸಂದೇಶವಾಹಕರಲ್ಲೂ ನೀವು ವಿಶ್ವಾಸವಿರಿಸಿರಿ ಮತ್ತು ಅವರನ್ನು ಅನು ಸರಿಸಿರಿ. ನಿಮಗೆ ಸನ್ಮಾರ್ಗ ಪ್ರಾಪ್ತಿಯಾಗುವುದು.');
INSERT INTO "kannada" VALUES(7,159,'7:159','ಸತ್ಯಾನುಸಾರ ಮಾರ್ಗದರ್ಶನಗೈಯುತ್ತಲೂ, ಅದಕ್ಕನುಸರಿಸಿ ನೀತಿ ಪಾಲಿಸುತ್ತಲೂ ಇರುವ ಒಂದು ತಂಡ ಮೂಸಾ (ನಬಿಯವ)ರ ಜನ ಸಮುದಾಯದಲ್ಲಿದೆ.');
INSERT INTO "kannada" VALUES(7,160,'7:160','ನಾವು ಆ ಜನಾಂಗವನ್ನು ಹನ್ನೆರಡು ಗೋತ್ರ ಗಳಾಗಿ ವಿಭಾಗಿಸಿ, ಪಂಗಡಗಳಾಗಿ ರೂಪಿಸಿದ್ದೆವು. ಆ ಜನಾಂಗವು ಮೂಸಾರೊಡನೆ ಕುಡಿಯಲು ನೀರು ಕೇಳಿದಾಗ ಬಂಡೆಯ ಮೇಲೆ ತಮ್ಮ ದಂಡದಿಂದ ಹೊಡೆಯುವಂತೆ ನಾವು ದಿವ್ಯ ಸಂದೇಶ ನೀಡಿದೆವು. ಆ ಪ್ರಕಾರ ಆ ಬಂಡೆಯಿಂದ ಹನ್ನೆರಡು ಚಿಲುಮೆಗಳು ಉಕ್ಕಿ ಹರಿದವು. ಪ್ರತಿಯೊಂದು ಪಂಗಡವು ನೀರು ಪಡೆಯಬೇಕಾದ ಸ್ಥಳವನ್ನು ಅರಿತು ಕೊಂಡಿತು . ನಾವು ಅವರ ಮೇಲೆ ಮೋಡದ ನೆರಳನ್ನು ನೀಡಿದೆವು. ಅವರಿಗೆ `ಮನ್ನ್'' (ಫಲಾಹಾರ) ಮತ್ತು `ಸಲ್ವಾ'' (ಕಾಡಪಕ್ಷಿ ಮಾಂಸ) ಇಳಿಸಿದೆವು. ನಾವು ನಿಮಗೆ ದಯಪಾಲಿಸಿದ ಶುದ್ಧ ವಸ್ತುಗಳನ್ನು ಉಣ್ಣಿರಿ ಎಂದೆವು. ಅವರು ನಮ್ಮನ್ನು ದ್ರೋಹಿಸಲಿಲ್ಲ. ನಿಜವಾಗಿ ಅವರು ತಮ್ಮನ್ನೇ ದ್ರೋಹಿಸುವವ ರಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(7,161,'7:161','ನೀವು ಈ ಪಟ್ಟಣದಲ್ಲಿ ವಾಸಿಸಿರಿ; ನೀವು ಬಯಸುವ ಕಡೆಯಿಂದ ಆಹಾರ ಪಡೆಯಿರಿ ಮತ್ತು `ಹಿತ್ತಃ'' (ಪಾಪ ಮುಕ್ತಿ) ಎನ್ನುತ್ತ ಪಟ್ಟಣದ ದ್ವಾರದೊಳಗೆ ಶಿರಬಾಗುತ್ತ ಪ್ರವೇಶಿಸಿರಿ, ನಾವು ನಿಮ್ಮ ಅಪರಾಧಗಳನ್ನು ಕ್ಷಮಿಸುವೆವು ಮತ್ತು ಸದಾಚಾರಿಗಳಿಗೆ ಇನ್ನಷ್ಟು ಪ್ರತಿಫಲವನ್ನು ಹೆಚ್ಚಿಸಿಕೊಡುವೆವು ಎಂದು ಅವರೊಡನೆ ಹೇಳಿದ್ದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(7,162,'7:162','ಆದರೆ ಅವರ ಪೈಕಿ ಅಕ್ರಮಿಗಳಾಗಿದ್ದವರು ತಮಗೆ ಆದೇಶಿಸಲಾದ ಮಾತನ್ನು ಬದಲಿಸಿ ಬೇರೆ ಮಾತನ್ನು ತಂದರು. ಆಗ ನಾವು ಅವರ ಅಕ್ರಮದ ಪ್ರತಿಫಲವಾಗಿ ಆಕಾಶದಿಂದ ಕಠಿಣ ವಾದ ಶಿಕ್ಷೆಯನ್ನು ಅವರ ಮೇಲೆ ಇಳಿಸಿದೆವು .');
INSERT INTO "kannada" VALUES(7,163,'7:163','(ಓ ಪೈಗಂಬರರೇ,) ಸಮುದ್ರ ತೀರದಲ್ಲಿದ್ದ ಆ ನಾಡಿನವರ ಕುರಿತು ಇವರನ್ನು ವಿಚಾರಿಸಿರಿ . ಅಲ್ಲಿಯವರು ಪ್ರತಿ ಶನಿವಾರ ಅಲ್ಲಾ ಹನ ಆಜ್ಞೆಗಳನ್ನು ಉಲ್ಲಂಘಿಸುತ್ತಿದ್ದರು. ಅವರ ಶನಿವಾರ ದಿನವೇ ಮೀನುಗಳು ಉಕ್ಕಿ ಮೇಲೆ ಬರುತ್ತಿದ್ದುವು ಮತ್ತು ಶನಿವಾರದ ಹೊರತು ಇತರ ದಿನಗಳಲ್ಲಿ ಬರುತ್ತಿರಲಿಲ್ಲ. ಅವರ ಆಜ್ಞೋ ಲ್ಲಂಘನೆಯ ಕಾರಣ ಅವರನ್ನು ನಾವು ಈ ರೀತಿ ಪರೀಕ್ಷೆಗೊಳಪಡಿಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(7,164,'7:164','ಅವರ ಪೈಕಿ ಒಂದು ಪಂಗಡವು `ಅಲ್ಲಾಹನು ನಾಶಗೊಳಿಸಲಿರುವ ಅಥವಾ ಕಠಿಣ ಶಿಕ್ಷೆಗೊಳ ಪಡಿಸಲಿರುವ ಒಂದು ಜನಾಂಗಕ್ಕೆ ನೀವೇಕೆ ಉಪದೇಶ ನೀಡುತ್ತೀರಿ? ಎಂದು ಕೇಳಿದ ಸಂದರ್ಭವೂ ಸ್ಮರಣೀಯ. ಅವರು ಹೀಗೆ ಉತ್ತ ರಿಸಿದರು; ‘ನಾವು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಸನ್ನಿಧಿಯಲ್ಲಿ ನಿರಪರಾಧಿತ್ವವನ್ನು ಸಾಬೀತುಪಡಿಸುವುದಕ್ಕಾಗಿಯೂ ಅವರು ಜಾಗರೂಕರಾಗುವುದಕ್ಕಾಗಿಯೂ ಹೀಗೆ ಮಾಡುತ್ತೇವೆ’.');
INSERT INTO "kannada" VALUES(7,165,'7:165','ಅವರಿಗೆ ನೀಡಲಾಗಿದ್ದ ಉಪದೇಶವನ್ನು ಅವರು ಸಂಪೂರ್ಣ ಮರೆತು ಬಿಟ್ಟಾಗ ಕೆಡುಕಿ ನಿಂದ ತಡೆಯುತ್ತಿದ್ದವರನ್ನು ನಾವು ರಕ್ಷಿಸಿದೆವು ಮತ್ತು ಅಕ್ರಮವೆಸಗುತ್ತಿದ್ದ ಜನರನ್ನು ಅವರ ಆಜ್ಞೋಲ್ಲಂಘನೆಗಾಗಿ ಕಠಿಣ ಶಿಕ್ಷೆಗೊಳ ಪಡಿಸಿದೆವು .');
INSERT INTO "kannada" VALUES(7,166,'7:166','ಅಂತೆಯೇ ತಮಗೆ ನಿಷೇಧಿಸಲ್ಪಟ್ಟಿದ್ದ ವಿಚಾರ ಗಳನ್ನು ಧಿಕ್ಕರಿಸಿ ಕಾರ್ಯಪ್ರವೃತ್ತರಾದಾಗ ಅವರಿಗೆ ನಾವು ಹೇಳಿದೆವು; ‘ನೀವು ನೀಚರಾದ ಕಪಿಗಳಾಗಿರಿ’.');
INSERT INTO "kannada" VALUES(7,167,'7:167','ಪುನರುತ್ಥಾನ ದಿನದವರೆಗೂ ಅವರಿಗೆ (ಅವರ ವಂಶದವರಿಗೆ) ಕಠಿಣ ಶಿಕ್ಷೆ ಕೊಡುವಂಥವರನ್ನು ಕಳುಹಿಸುತ್ತಲೇ ಇರುವೆನೆಂದು ನಿಮ್ಮ ಪ್ರಭು ಘೋಷಿಸಿದುದನ್ನು ಸ್ಮರಿಸಿರಿ . ನಿಶ್ಚಯ ವಾಗಿಯೂ ನಿಮ್ಮ ಪ್ರಭು ಶಿಕ್ಷೆ ಕೊಡುವುದರಲ್ಲಿ ಅತಿ ಶೀಘ್ರನು ಮತ್ತು ನಿಶ್ಚಯವಾಗಿಯೂ ಕ್ಷಮಾಶೀಲನೂ ಕರುಣಾಮಯಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(7,168,'7:168','ನಾವು ಅವರನ್ನು ಭೂಮಿಯ ಮೇಲೆ ಹಲವು ಜನ ಪಂಗಡಗಳನ್ನಾಗಿ ವಿಭಜಿಸಿದೆವು. ಅವರ ಪೈಕಿ ಸಜ್ಜನರೂ, ದುರ್ಜನರೂ ಇರುವರು. ಸನ್ಮಾರ್ಗದ ಕಡೆಗೆ ಅವರು ಮರಳಲೆಂದು ನಾವು ಅವರನ್ನು ಅನುಕೂಲ ಮತ್ತು ಪ್ರತಿಕೂಲ ಪರಿಸ್ಥಿತಿಗಳನ್ನೊಡ್ಡಿ ಪರೀಕ್ಷೆಗೊಳಪಡಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(7,169,'7:169','ಅವರ ನಂತರ ವೇದಗ್ರಂಥಗಳ ವಾರೀಸುದಾರ ರಾಗಿ ಮತ್ತೊಂದು ಕೂಟದವರು ಬಂದರು. ಅವರು ಈ ಅಧೋಲೋಕದ ತುಚ್ಛ ಲಾಭಗಳನ್ನು ಪಡೆಯುತ್ತಿದ್ದರು. ನಮಗೆ ಕ್ಷಮೆ ದೊರೆಯುತ್ತದೆ ಎಂದೂ ಹೇಳುತ್ತಿದ್ದರು. ಅದೇ ವೇಳೆ ಅದಕ್ಕೆ ಸಮಾನವಾದ ಲಾಭವು ಪುನಃ ಅವರ ಮುಂದೆ ಬಂದಾಗ ಅವರು ಅದನ್ನು ಪಡೆಯುವರು. ಅಲ್ಲಾಹನ ಹೆಸರಲ್ಲಿ ಸತ್ಯವನ್ನು ಮಾತ್ರ ಹೇಳಬೇಕೆಂದು ಗ್ರಂಥದ ಪ್ರಮಾಣವನ್ನು ಅವರಿಂದ ಪಡೆಯಲಾಗಿಲ್ಲವೇ? ಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತಗೊಂಡಿರುವುದನ್ನು ಅವರು ಸ್ವತಃ ಓದಿರುತ್ತಾರೆ. ಧರ್ಮೋಚಿತವಾಗಿ ನಡೆಯುವವ ರಿಗೆ ಪರಲೋಕ ವಾಸವೇ ಅತ್ಯುತ್ತಮ ವಾದುದು. ನೀವು ಯೋಚಿಸುತ್ತಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(7,170,'7:170','ಗ್ರಂಥವನ್ನು ಅನುಸರಿಸುವವರಿಗೆ ಮತ್ತು ನಮಾಝನ್ನು ಸಂಸ್ಥಾಪಿಸಿಕೊಂಡವರಿಗೆ - ಖಂಡಿತ ಸದಾಚಾರಿಗಳ ಸತ್ಫಲವನ್ನು ವ್ಯರ್ಥ ಗೊಳಿಸಲಾರೆವು.');
INSERT INTO "kannada" VALUES(7,171,'7:171','ಅವರ ಮೇಲೆ ಕೊಡೆಯಂತೆ ನಾವು ಒಂದು ಪರ್ವತವನ್ನು ಎತ್ತಿ ಹಿಡಿಯುತ್ತಲೂ, ಅದು ತಮ್ಮ ಮೇಲೆ ಬೀಳಬಹುದೆಂದು ಅವರು ಶಂಕಿಸಿಯೂ ಇದ್ದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ. ಆಗ ನಾವು ಅವರೊಡನೆ ‘ನಿಮಗೆ ನಾವು ನೀಡಿದ ಗ್ರಂಥವನ್ನು ಬಿಗಿಯಾಗಿ ಹಿಡಿಯಿರಿ ಮತ್ತು ಅದರಲ್ಲಿ ದಾಖಲಾಗಿರುವುದನ್ನು ಗ್ರಹಿಸಿರಿ. ನೀವು ಧರ್ಮನಿಷ್ಟರಾಗುವಿರಿ’ ಎಂದು ಹೇಳಿದ್ದೆವು .');
INSERT INTO "kannada" VALUES(7,172,'7:172','(ಓ ಪೈಗಂಬರರೇ,) ನಿಮ್ಮ ಪ್ರಭು ಆದಮರ ಪೀಳಿಗೆಯಿಂದ ಅವರ ಸಂತತಿಯನ್ನು ಹೊರ ತಂದಿದ್ದ ಹಾಗೂ ಅವರನ್ನು ಸ್ವತಃ ಅವರ ಮೇಲೆ ಸಾಕ್ಷಿಗಳಾಗಿ ಮಾಡುತ್ತಾ `ನಾನು ನಿಮ್ಮ ಪ್ರಭುವಲ್ಲವೇ?’ ಎಂದು ಕೇಳಿದ್ದ ಸಂದರ್ಭವನ್ನು ಜನರಿಗೆ ನೆನಪಿಸಿರಿ. ಆಗ ಅವರು, ‘ಹೌದು, ಖಂಡಿತವಾಗಿಯೂ ನೀನೇ ನಮ್ಮ ಪ್ರಭು. ನಾವು ಇದಕ್ಕೆ ಸಾಕ್ಷ್ಯವಹಿಸುತ್ತೇವೆ’ ಎಂದರು . ನೀವು ಪುನರುತ್ಥಾನ ದಿನದಂದು ‘ನಾವು ಈ ಬಗ್ಗೆ ಅಶೃದ್ಧರಾಗಿದ್ದೆವು’ ಎಂದು ಹೇಳಿ ಬಿಡಬಾರದೆಂದು ');
INSERT INTO "kannada" VALUES(7,173,'7:173','ಅಥವಾ ನಮಗಿಂತ ಮುಂಚೆ ನಮ್ಮ ಪೂರ್ವಿ ಕರು ಶಿರ್ಕ್ ಮಾಡಿದ್ದರು, ನಾವು ಅವರ ನಂತರ ಜನಿಸಿ ಬಂದವರು. ಹಾಗಾಗಿ ಮಿಥ್ಯಾಚಾರಿಗಳು ಮಾಡಿದ ಅಪರಾಧಕ್ಕಾಗಿ ನೀನು ನಮ್ಮನ್ನು ಶಿಕ್ಷಿಸುವೆಯಾ? ಎಂದು ಕೇಳಿಬಿಡಬಾರದೆಂದು ನಾವು ಹೀಗೆ ಮಾಡಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(7,174,'7:174','ಈ ರೀತಿ ನಾವು ದೃಷ್ಟಾಂತಗಳನ್ನು ಸುಸ್ಪಷ್ಟ ವಾಗಿ ವಿವರಿಸುತ್ತೇವೆ. ಇವರು ಮರಳಿ ಬರಬೇಕೆಂಬ ಉದ್ದೇಶದಿಂದ.');
INSERT INTO "kannada" VALUES(7,175,'7:175','(ಓ ಪೈಗಂಬರರೇ,) ಒಬ್ಬನ ವೃತ್ತಾಂತವನ್ನು ಅವರಿಗೆ ಓದಿ ಹೇಳಿರಿ. ಆತನಿಗೆ ನಮ್ಮ ಅನೇಕ ದೃಷ್ಟಾಂತಗಳನ್ನು ನೀಡಿದ್ದೆವು. ಆದರೆ ಅವನು ಅದರಿಂದ ಕಳಚಿಕೊಂಡನು. ಶೈತಾ ನನು ಆತನನ್ನು ಅನುಸರಿಸಿದನು. ಹಾಗೆ ಆತ ಪಥಭ್ರಷ್ಟರಲ್ಲೊಬ್ಬನಾಗಿದ್ದನಷ್ಟೇ.');
INSERT INTO "kannada" VALUES(7,176,'7:176','ನಾವು ಉದ್ದೇಶಿಸಿದ್ದರೆ ಆ ದೃಷ್ಟಾಂತಗಳ ಮೂಲಕ ಅವನಿಗೆ ಔನ್ನತ್ಯ ದಯಪಾಲಿಸು ತ್ತಿದ್ದೆವು. ಆದರೆ ಅವನು ಇಹಲೋಕದೆಡೆಗೇ ವಾಲುತ್ತಾ ತನ್ನಿಚ್ಛೆಯನ್ನೇ ಹಿಂಬಾಲಿಸಿದನು. ಅದರ ಪರಿಣಾಮವಾಗಿ ಅವನ ಪರಿಸ್ಥಿತಿಯು ನಾಯಿಯಂತಾಯಿತು. ನೀವು ಅದನ್ನು ಅಟ್ಟಿ ದರೆ ಅದು ತನ್ನ ನಾಲಗೆಯನ್ನು ಹೊರಚಾಚುತ್ತದೆ. ಅದನ್ನು ಅಟ್ಟದೆ ಬಿಟ್ಟು ಬಿಟ್ಟರೂ ಅದು ನಾಲಗೆ ಹೊರಚಾಚಿ ಕೊಂಡಿರುವುದು. ಇದುವೇ ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳನ್ನು ನಿಷೇಧಿಸುವವರಿಗೆ ಉಪಮೆ . ನೀವು ಈ ವೃತ್ತಾಂತಗಳನ್ನು ಇವರಿಗೆ ಹೇಳುತ್ತಲಿರಿ. ಇವರು ಚಿಂತಿಸುವಂತಾಗಲಿ.');
INSERT INTO "kannada" VALUES(7,177,'7:177','ನಮ್ಮ ನಿದರ್ಶನಗಳನ್ನು ನಿಷೇಧಿಸಿದವರ ಉದಾಹರಣೆಯು ಅತ್ಯಂತ ನಿಕೃಷ್ಟವಾಗಿದೆ. ಅವರು ತಮ್ಮ ಮೇಲೆ ತಾವೇ ಅಕ್ರಮವೆಸಗುತ್ತಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(7,178,'7:178','ಅಲ್ಲಾಹನು ಯಾರಿಗೆ ಸನ್ಮಾರ್ಗದರ್ಶನ ಮಾಡುತ್ತಾನೋ ಅವನಿಗೇ ಸನ್ಮಾರ್ಗ ಪ್ರಾಪ್ತವಾಗುತ್ತದೆ. ಮತ್ತು ಅಲ್ಲಾಹನು ಯಾರನ್ನು ದಾರಿ ತಪ್ಪಿಸುತ್ತಾನೋ ಅವರೇ ಹತಭಾಗ್ಯರು.');
INSERT INTO "kannada" VALUES(7,179,'7:179','ಅನೇಕ ಜಿನ್ನ್ ಮತ್ತು ಮನುಷ್ಯರನ್ನು ನಾವು ನರಕಕ್ಕಾಗಿಯೇ ಕಾದಿರಿಸಿದ್ದೇವೆ. ಅವರಿಗೆ ಹೃದಯಗಳಿವೆ. ಆದರೆ ಅವರು ಅದರಿಂದ ವಿವೇಚಿ ಸುವುದಿಲ್ಲ. ಅವರಿಗೆ ಕಣ್ಣುಗಳಿವೆ. ಆದರೆ ಅವರು ಆ ಮೂಲಕ ನೋಡುವುದಿಲ್ಲ. ಅವರಿಗೆ ಕಿವಿ ಗಳಿವೆ. ಆದರೆ ಅವರು ಅವುಗಳಿಂದ ಆಲಿಸುವು ದಿಲ್ಲ. ಅವರು ಪ್ರಾಣಿಗಳಂತಿದ್ದಾರೆ, ಮಾತ್ರವಲ್ಲ ಅವುಗಳಿಗಿಂತಲೂ ಪಥಭ್ರಷ್ಟರು. ಅವರೇ ಪ್ರಜ್ಞಾ ಶೂನ್ಯರಾದವರು.');
INSERT INTO "kannada" VALUES(7,180,'7:180','ಅಲ್ಲಾಹನಿಗೆ ಅತಿ ವಿಶಿಷ್ಟ ನಾಮಗಳಿವೆ. ಹಾಗಾಗಿ ಆ ನಾಮಗಳಿಂದಲೇ ಅವನನ್ನು ಕರೆ ಯಿರಿ. ಅವನ ನಾಮಗಳಲ್ಲಿ ಅಕೃತ್ಯವೆಸಗು ವವರನ್ನು ಬಿಟ್ಟು ಬಿಡಿರಿ . ಅವರ ಕರ್ಮದ ಪ್ರತಿಫಲವನ್ನು ಅವರೇ ಪಡೆಯಲಿರುವರು.');
INSERT INTO "kannada" VALUES(7,181,'7:181','ಋುಜುವಾಗಿ ಸತ್ಪಥ ಹೊಂದುವ ಹಾಗೂ ತನ್ಮೂಲಕ ನೀತಿ ಪಾಲಿಸುವ ಒಂದು ಸಮುದಾಯವು ನಮ್ಮ ಸೃಷ್ಟಿಯಲ್ಲಿದೆ.');
INSERT INTO "kannada" VALUES(7,182,'7:182','ನಮ್ಮ ಋುಜುವಾತುಗಳನ್ನು ನಿಷೇಧಿಸಿದವರನ್ನು ಅವರಿಗೆ ಅರಿವೇ ಆಗದಂತೆ ನಾವು ಮೆಲ್ಲ ಮೆಲ್ಲನೆ ವಿನಾಶದ ಕಡೆಗೆ ಒಯ್ಯುವೆವು.');
INSERT INTO "kannada" VALUES(7,183,'7:183','ನಾನು ಅವರಿಗೆ ಅವಕಾಶ ನೀಡುತ್ತೇನೆ. ನಿಸ್ಸಂದೇಹವಾಗಿಯೂ ನನ್ನ ತಂತ್ರವು ಅತ್ಯಂತ ಅಬೇಧ್ಯವಾದುದು.');
INSERT INTO "kannada" VALUES(7,184,'7:184','ಅವರೆಂದೂ ಚಿಂತಿಸಿಲ್ಲವೇ? ಅವರ ಸಂಗಡಿಗನಿಗೆ ಯಾವುದೇ ಮತಿಭ್ರಮಣೆ ಇಲ್ಲವೆಂದೂ, ಇವರು ಪ್ರತ್ಯಕ್ಷ ಮುನ್ನೆಚ್ಚರಿಕೆದಾರ ಮಾತ್ರ ವೆಂದೂ?');
INSERT INTO "kannada" VALUES(7,185,'7:185','ಭೂಮಿ - ಆಕಾಶಗಳ ಆಧಿಪತ್ಯದಲ್ಲೂ, ಅಲ್ಲಾ ಹನು ಸೃಷ್ಟಿಸಿರುವ ಸರ್ವ ವಸ್ತುವಿನಲ್ಲೂ ಎಂದಾ ದರೂ ಅವರು ವಿವೇಚಿಸಲಿಲ್ಲವೇ? ಅವರ ಅವಧಿ ಹತ್ತಿರವಾಗುತ್ತಿರಬಹುದು ಎಂದೂ! ಈ ಖುರ್ ಆನನ್ನು ಬಿಟ್ಟು ಯಾವ ವರ್ತಮಾನದಲ್ಲಿ ಅವರು ವಿಶ್ವಾಸವಿಡಲಿದ್ದಾರೆ?');
INSERT INTO "kannada" VALUES(7,186,'7:186','ಅಲ್ಲಾಹನು ಯಾರನ್ನಾದರೂ ದಾರಿತಪ್ಪಿಸಿದರೆ ಅವನನ್ನು ಸರಿದಾರಿಗೆ ತರುವವರಿಲ್ಲ ಮತ್ತು ಅಲ್ಲಾಹನು ಅವರನ್ನು ಅವರ ದುರ್ಮಾರ್ಗದಲ್ಲೇ ಅಲೆದಾಡುತ್ತಿರಲು ಬಿಟ್ಟುಬಿಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(7,187,'7:187','‘ಲೋಕದ ಅವಸಾನ ಘಳಿಗೆ ಯಾವಾಗ?’ ಎಂದು ಇವರು ನಿಮ್ಮೊಡನೆ ಕೇಳುತ್ತಾರೆ. ‘ಅದರ ಜ್ಞಾನವು ನನ್ನ ಪ್ರಭುವಿನ ಬಳಿಯೇ ಇದೆ. ಅದನ್ನು ಅದರ ನಿಶ್ಚಿತ ಸಮಯದಲ್ಲಿ ಅವನಲ್ಲದೆ ಪ್ರಕಟಗೊಳಿಸುವುದಿಲ್ಲ. ಭೂಮಿ - ಆಕಾಶಗಳಲ್ಲಿ ಅದು ಅತ್ಯಂತ ಕಠಿಣ ಕಾಲವಾಗಿರುವುದು. ಅದು ನಿಮ್ಮ ಮೇಲೆ ಹಠಾತ್ತನೆ ಬರುವುದು’ ಎಂದು ಹೇಳಿರಿ. ನೀವು ಅದರ ಬಗ್ಗೆ ಕೇಳಿ ತಿಳಿಯುವಲ್ಲಿ ತೀವ್ರ ಆಸಕ್ತರೋ ಶೋಧನೆ ಯಲ್ಲೇ ಬಿದ್ದಿರುವಿರೋ ಎಂಬಂತೆ ಇವರು ಅದರ ಕುರಿ ತಾಗಿ ನಿಮ್ಮೊಡನೆ ಪ್ರಶ್ನಿಸುತ್ತಾರೆ. ಹೇಳಿರಿ; ಅದರ ಅರಿವು ಅಲ್ಲಾಹನಿಗೆ ಮಾತ್ರವಿದೆ. ಆದರೆ ಹೆಚ್ಚಿನ ವರು ಈ ವಾಸ್ತವಿಕತೆಯ ಕುರಿತು ಅರಿಯಲಾರರು.');
INSERT INTO "kannada" VALUES(7,188,'7:188','(ಓ ಪೈಗಂಬರರೇ,) ಹೀಗೆ ಹೇಳಿರಿ : ಅಲ್ಲಾಹನ ಉದ್ದೇಶದ ಹೊರತಾಗಿ ನನ್ನ ಸ್ವಂತಕ್ಕೆ ಒಂದು ಉಪಕಾರವೋ, ಅಪಕಾರವೋ ಮಾಡುವ ಸ್ವಾತಂತ್ರ್ಯ ನನಗಿಲ್ಲ . ಅದೃಶ್ಯ ವಿಚಾರಗಳ ಕುರಿತು ಜ್ಞಾನವಿರುತ್ತಿದ್ದರೆ ನಾನು ಬಹಳಷ್ಟು ಹಿತವನ್ನು ಸಂಗ್ರಹಿಸಿಡುತ್ತಿದ್ದೆ . ಮತ್ತು ನನಗೆ ಯಾವ ಹಾನಿಯೂ ತಟ್ಟುತ್ತಿರಲಿಲ್ಲ. ನಾನು ಕೇವಲ ಎಚ್ಚರಿಕೆ ಕೊಡುವವನೂ ವಿಶ್ವಾಸಿಗಳಾದ ಜನತೆಗೆ ಸುವಾರ್ತೆ ನೀಡುವವನೂ ಆಗಿರುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(7,189,'7:189','ಒಂದೇ ಶರೀರದಿಂದ ನಿಮ್ಮನ್ನು ಸೃಷ್ಟಿಸಿದವನು ಅವನು. ಆ ಶರೀರದಿಂದಲೇ ಅದರ ಜೋಡಿ ಯನ್ನೂ ಅದರೊಂದಿಗೆ ಮಿಲನಗೊಳ್ಳುವುದಕ್ಕಾಗಿ ಅವನು ಸೃಷ್ಟಿಸಿದನು. ಅಂತೆಯೇ ಆ ಜೋಡಿ ಜೊತೆ ಸೇರಿದಾಗ ಆಕೆಗೆ ಲಘು ಗರ್ಭ ಧಾರಣೆಯಾಯಿತು. ಅವಳು ಅದನ್ನು ಹೊತ್ತು ಕೊಂಡೇ ನಡೆದಳು. ಅದು ಭಾರವಾದಾಗ ಅವರಿಬ್ಬರು ತಮ್ಮ ಪ್ರಭುವಾದ ಅಲ್ಲಾಹನೊಡನೆ, ‘ನೀನು ನಮಗೆ ಉತ್ತಮ ಮಗುವನ್ನಿತ್ತರೆ ನಾವು ನಿನಗೆ ಕೃತಜ್ಞರಾಗುವೆವು’ ಎಂದು ಪ್ರಾರ್ಥಿಸಿದರು.');
INSERT INTO "kannada" VALUES(7,190,'7:190','ಆದರೆ ಅಲ್ಲಾಹನು ಅವರಿಗೊಂದು ಉತ್ತಮ ಮಗುವನ್ನು ನೀಡಿದಾಗ ಅವರು ಅವನ ಕೊಡುಗೆಯಲ್ಲೇ ಇತರರನ್ನು ಅವನ ಸಹಭಾಗಿಗಳಾಗಿ ಮಾಡಿದರು. ಅಲ್ಲಾಹನು, ಅವರು ಸಹಭಾಗಿಗಳಾಗಿಸುವ ವಿಷಯಗಳಿಂದ ಪರಮ ಮುಕ್ತನಾಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(7,191,'7:191','ಯಾವುದನ್ನೂ ಸೃಷ್ಟಿಸಲಾಗದ ಮತ್ತು ಸ್ವತಃ ಸೃಷ್ಟಿಸಲ್ಪಟ್ಟವುಗಳನ್ನು ಅಲ್ಲಾಹನ ಆರಾಧನೆ ಯಲ್ಲಿ ಸಹಭಾಗಿಗಳಾಗಿ ಮಾಡುತ್ತಿದ್ದಾರೆಯೇ?');
INSERT INTO "kannada" VALUES(7,192,'7:192','ಅವು ಇವರಿಗೆ ಸಹಾಯ ಮಾಡಲಾರವು. ಸ್ವತಃ ತಮಗೆ ಸಹಾಯ ಮಾಡಿಕೊಳ್ಳಲೂ ಅವು ಅಶಕ್ತವಾಗಿವೆ.');
INSERT INTO "kannada" VALUES(7,193,'7:193','ನೀವು ಅವರನ್ನು ಸನ್ಮಾರ್ಗದ ಕಡೆಗೆ ಬರಲು ಕರೆಕೊಟ್ಟರೆ ಅವರು ನಿಮ್ಮನ್ನು ಹಿಂಬಾಲಿಸರು. ನೀವು ಅವರನ್ನು ಕರೆದರೂ ಮೌನ ವಹಿಸಿದರೂ ಎರಡೂ ನಿಮ್ಮ ಮಟ್ಟಿಗೆ ಸರಿಸಮಾನವಿರುವುದು.');
INSERT INTO "kannada" VALUES(7,194,'7:194','ಅಲ್ಲಾಹನ ಹೊರತಾಗಿ ನೀವು ಆರಾಧಿಸುವ ಯಾರೇ ಆದರೂ ಅವರು ನಿಮ್ಮಂತಿರುವ ದಾಸ ರೇ ಆಗಿರುತ್ತಾರೆ. (ಅವರು ಆರಾಧ್ಯರುಗಳೆಂಬ ವಾದದಲ್ಲಿ) ನೀವು ಸತ್ಯವಂತರಾಗಿದ್ದರೆ ನೀವು ಅವರನ್ನೊಮ್ಮೆ ಪ್ರಾರ್ಥಿಸಿ. ನಿಮ್ಮ ಪ್ರಾರ್ಥನೆಗೆ ಉತ್ತರ ಕೊಡುತ್ತಾರೋ ನೋಡಿರಿ.');
INSERT INTO "kannada" VALUES(7,195,'7:195','ಅವರಿಗೆ ನಡೆಯಲು ಕಾಲುಗಳಿವೆಯೇ? ಅವ ರಿಗೆ ಹಿಡಿಯಲು ಕೈಗಳಿವೆಯೇ? ಅವರಿಗೆ ನೋ ಡಲು ಕಣ್ಣುಗಳಿವೆಯೇ? ಅವರಿಗೆ ಕೇಳಲು ಕಿವಿಗಳಿವೆಯೇ? (ಓ ಪೈಗಂಬರರೇ ಇವ ರೊಡನೆ) ಹೇಳಿರಿ; ನಿಮ್ಮ ಭಾಗೀದಾರ ರನ್ನು ಕರೆದು ಕೊಳ್ಳಿರಿ. ತರುವಾಯ ನೀವು ನನ್ನ ವಿರುದ್ಧ ಏನೇ ಕುತಂತ್ರ ಪ್ರಯೋಗಿಸಿರಿ ಮತ್ತು ನನಗೆ ಕಿಂಚಿತ್ತೂ ಕಾಲಾವಕಾಶ ಕೊಡಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(7,196,'7:196','ನಿಸ್ಸಂದೇಹವಾಗಿ ಗ್ರಂಥವನ್ನು ಅವತೀರ್ಣ ಗೊಳಿಸಿದ ಅಲ್ಲಾಹನೇ ನನ್ನ ಉಸ್ತುವಾರಿದಾರನು. ಅವನು ಸಜ್ಜನರನ್ನು ಸಂರಕ್ಷಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(7,197,'7:197','ನೀವು ಅಲ್ಲಾಹನ ಹೊರತಾಗಿ ಯಾರನ್ನು ಆರಾಧಿಸುತ್ತೀರೋ ಅವರು ನಿಮಗೆ ಸಹಾಯ ಮಾಡುವ ಶಕ್ತಿಯನ್ನು ಹೊಂದಿಲ್ಲ ಮತ್ತು ತಮಗೆ ಸ್ವಯಂ ಸಹಾಯಕರಾಗಲೂ ಅವರಿಂದಾಗದು.');
INSERT INTO "kannada" VALUES(7,198,'7:198','ನೀವು ಅವರನ್ನು ನೇರ ಮಾರ್ಗಕ್ಕೆ ಕರೆದರೆ ನಿಮ್ಮ ಮಾತನ್ನು ಅವರು ಕೇಳಲಾರರು. ಅವರು ನಿಮ್ಮೆಡೆಗೆ ನೋಡುತ್ತಿರುವಂತೆ ತೋರುತ್ತದೆ. ಆದರೆ ವಾಸ್ತವದಲ್ಲಿ ಅವರು ಏನನ್ನೂ ನೋಡು ವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(7,199,'7:199','(ಓ ಪೈಗಂಬರರೇ,) ಕ್ಷಮೆ ಕೈಗೊಳ್ಳಿರಿ . ಒಳಿತನ್ನು ಬೋಧಿಸಿರಿ ಮತ್ತು ತಿಳಿಗೇಡಿಗಳಿಂದ ವಿಮುಖರಾಗಿರಿ.');
INSERT INTO "kannada" VALUES(7,200,'7:200','ಶೈತಾನನಿಂದ ಯಾವುದೇ ದುರ್ಭೋದನೆ ನಿಮಗೆ ಸೋಂಕಿದರೆ ಅಲ್ಲಾಹನ ಅಭಯ ಯಾಚಿಸಿರಿ. (ಮಾತನ್ನು) ಆಲಿಸುವವನೂ (ಪ್ರವೃತ್ತಿಯನ್ನು) ಅರಿಯುವವನೂ ಆಗಿರು ತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(7,201,'7:201','ವಾಸ್ತವದಲ್ಲಿ ಧರ್ಮನಿಷ್ಟರಾದವರಿಗೆ ಶೈತಾನ ನಿಂದ ದುರಾಲೋಚನೆಗಳು ಸೋಂಕಿದರೆ ಅವರು ತಕ್ಷಣ ಅಲ್ಲಾಹನನ್ನು ಸ್ಮರಿಸುತ್ತಾರೆ. ಆಗ ಅವರಿಗೆ ಸತ್ಯವು ಸ್ಪಷ್ಟವಾಗಿ ಗೋಚರವಾಗುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(7,202,'7:202','ಶೈತಾನರು ತಮ್ಮ ಸಹೋದರರನ್ನು ದಾರಿಗೇ ಡಿನಲ್ಲಿ ದೀರ್ಘಗೊಳಿಸುತ್ತಾರೆ. ಆಮೇಲೆ ಈ ಸಹೋದರರು ದಾರಿಗೇಡಿನಿಂದ ಮುಕ್ತರಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(7,203,'7:203','(ಓ ಪೈಗಂಬರರೇ,) ನೀವು ಅವರ ಮುಂದೆ (ಅವರು ಕೇಳಿದ) ಯಾವುದಾದರೂ ನಿದರ್ಶ ನವನ್ನು ತರದಿದ್ದರೆ ‘ನೀವು ಸ್ವತಃ ಅದನ್ನು ನಿರ್ಮಿಸಲಿಲ್ಲವೇಕೆ?’ ಎಂದು ಇವರು ಕೇಳುತ್ತಾರೆ. ಹೇಳಿರಿ; ನನ್ನ ಪ್ರಭು ನನಗೆ ಅವತೀರ್ಣಗೊಳಿಸಿದ ಸಂದೇಶ ವಚನಗಳನ್ನು ಮಾತ್ರ ನಾನು ಅನುಸರಿಸುತ್ತೇನೆ. ಇವು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಬಂದ ಅಂತಜ್ರ್ಞಾನ ಮತ್ತು ಅವುಗಳನ್ನು ನಂಬುವ ಜನರಿಗೆ ಮಾರ್ಗದರ್ಶನ ಮತ್ತು ಅನುಗ್ರಹವಾಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(7,204,'7:204','ಖುರ್‍ಆನ್ ಓದಲ್ಪಟ್ಟಾಗ ನೀವು ಅದನ್ನು ಲಕ್ಷ್ಯವಿಟ್ಟು ಕೇಳಿರಿ ಮತ್ತು ಮೌನವಾಗಿರಿ. ನೀವು ಅನುಗ್ರಹೀತರಾಗಬಹುದು.');
INSERT INTO "kannada" VALUES(7,205,'7:205','(ಓ ಪೈಗಂಬರರೇ,) ದೈನ್ಯತೆ ಹಾಗೂ ಭಯದೊಂದಿಗೆ ನಿಮ್ಮ ಪ್ರಭುವನ್ನು ಮನದಲ್ಲಿ ಧ್ಯಾನಿಸಿರಿ. ನುಡಿಯಲ್ಲಿ ಮಿತವಾದ ಧ್ವನಿಯಲ್ಲಿ ಸಂಜೆ ಮುಂಜಾನೆ ಸ್ಮರಿಸಿರಿ. ಅಶೃದ್ಧರ ಸಾಲಿಗೆ ಸೇರದಿರಿ.');
INSERT INTO "kannada" VALUES(7,206,'7:206','ನಿಮ್ಮ ಪ್ರಭುವಿನ ಬಳಿ ಇರುವವರು (ದೇವ ಚರರು) ಅವನ ಆರಾಧನೆಯಿಂದ ವಿಮುಖ ರಾಗಿ ಅಹಂಕರಿಸುವುದಿಲ್ಲ. ಅವರು ಅವನ ಪರಿಶುದ್ಧತೆಯನ್ನು ಕೊಂಡಾಡುತ್ತಾರೆ. ಮತ್ತು ಅವನ ಮುಂದೆ ಸಾಷ್ಟಾಂಗವೆರಗುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(8,1,'8:1','(ನಬಿಯೇ) ಅವರು ನಿಮ್ಮೊಡನೆ ಯುದ್ಧಾಸ್ತಿಯ ಕುರಿತು ಪ್ರಶ್ನಿಸುತ್ತಾರೆ. ಹೇಳಿರಿ; ಯುದ್ಧಾಸ್ತಿಯು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ರಸೂಲರದ್ದು. ಆದುದರಿಂದ ನೀವು ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ ಮತ್ತು ಪರಸ್ಪರ ಸಂಬಂಧಗಳನ್ನು ಸರಿಪಡಿಸಿರಿ. ನೀವು ವಿಶ್ವಾಸಿಗಳಾಗಿದ್ದರೆ ಅಲ್ಲಾಹು ಹಾಗೂ ಅವನ ರಸೂಲರನ್ನು ಅನುಸರಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(8,2,'8:2','ನಿಜವಾಗಿಯೂ ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಯಾರೆಂದರೆ ಅಲ್ಲಾಹನ ಬಗ್ಗೆ ಪ್ರಸ್ತಾಪಿಸಲಾದರೆ ಅವರ ಹೃದಯ ನಡುಗುವುದು. ಅವರ ಮುಂದೆ ಅಲ್ಲಾಹನ ನಿದರ್ಶನಗಳನ್ನು ಓದಲ್ಪಟ್ಟಾಗ ಅದು ಅವರ ವಿಶ್ವಾಸವನ್ನು ವೃದ್ಧಿಗೊಳಿಸುವುದು ಮತ್ತು ಅವರು ತಮ್ಮ ಪ್ರಭುವಿನ ಮೇಲೆ ಭರವಸೆಯಿಡುವವರು .');
INSERT INTO "kannada" VALUES(8,3,'8:3','ಅವರು ನಮಾಝನ್ನು ಸಂಸ್ಥಾಪಿಸುವವರು ಮತ್ತು ನಾವು ಅವರಿಗೆ ನೀಡಿದುದರಿಂದ ಖರ್ಚು ಮಾಡುವವರು.');
INSERT INTO "kannada" VALUES(8,4,'8:4','ಇಂಥವರೇ ನಿಜವಾದ ಸತ್ಯವಿಶ್ವಾಸಿಗಳು. ಅವರಿಗಾಗಿ ಅವರ ಪ್ರಭುವಿನ ಬಳಿ ಉನ್ನತ ಸ್ಥಾನಗಳಿವೆ. ಪಾಪ ಮುಕ್ತಿಯೂ ಅತ್ಯುತ್ತಮ ಜೀವನಾನುಕೂಲವೂ ಇದೆ.');
INSERT INTO "kannada" VALUES(8,5,'8:5','ನಿಮ್ಮ ಪ್ರಭು ನಿಮ್ಮನ್ನು ನಿಮ್ಮ ನಿವಾಸದಿಂದ ನ್ಯಾಯೋಚಿತವಾಗಿ ಹೊರ ತಂದಿದ್ದನು. ವಿಶ್ವಾಸಿಗಳ ಪೈಕಿ ಒಂದು ಪಂಗಡಕ್ಕೆ ಇದು ಅಪ್ರಿಯವಾಗಿತ್ತು.');
INSERT INTO "kannada" VALUES(8,6,'8:6','ಆ ನ್ಯಾಯವು ಸ್ಪಷ್ಟವಾಗಿದ್ದೂ ಕೂಡ ಅವರು ಅದರ ಔಚಿತ್ಯದ ಕುರಿತು ನಿಮ್ಮೊಂದಿಗೆ ತರ್ಕಿಸುತ್ತಿದ್ದರು. ಅವರನ್ನು ನೋಡುತ್ತಿದ್ದಂತೆಯೇ ಮರಣಕ್ಕೆ ಎಳೆದೊಯ್ಯುತ್ತಿರುವಂತಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(8,7,'8:7','ಎರಡು ತಂಡಗಳ ಪೈಕಿ ಒಂದು ನಿಮಗೆ ಸಿಗು ವುದೆಂದು ಅಲ್ಲಾಹು ನಿಮಗೆ ವಾಗ್ದಾನ ಮಾಡು ತ್ತಿದ್ದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ. ನಿರಾಯುಧ ತಂಡವು ನಿಮಗೆ ಸಿಗಬೇಕೆಂದು ನೀವು ಇಚ್ಛಿಸುತ್ತಿದ್ದಿರಿ. ಆದರೆ ಅಲ್ಲಾಹು ಇಚ್ಛಿಸಿದ್ದು ತನ್ನ ಪೂರ್ವವಚನಗಳಿಂದ ಸತ್ಯವಾದುದನ್ನು ನಿಜ ಮಾಡಿ ತೋರಿಸಬೇಕೆಂದೂ ಸತ್ಯನಿಷೇಧಿಗಳ ಬುಡವನ್ನೇ ಕಿತ್ತು ಹಾಕಬೇಕೆಂದೂ ಆಗಿತ್ತು .');
INSERT INTO "kannada" VALUES(8,8,'8:8','ಇದು ಅಪರಾಧಿಗಳಿಗೆ ಎಷ್ಟೇ ಅಪ್ರಿಯವಾಗಿದ್ದರೂ ಸತ್ಯವನ್ನು ಸತ್ಯಗೊಳಿಸಲಿಕ್ಕೂ ಮಿಥ್ಯವನ್ನು ಮಿಥ್ಯಗೊಳಿಸಲಿಕ್ಕೂ ಆಗಿತ್ತು.');
INSERT INTO "kannada" VALUES(8,9,'8:9','ನೀವು ನಿಮ್ಮ ಪ್ರಭುವಿನೊಡನೆ ಸಹಾಯ ಬೇಡುತ್ತಿದ್ದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ. ನಾನು ಒಂದು ಸಾವಿರ ದೇವಚರರನ್ನು ಸರದಿ ಪ್ರಕಾರ ಕಳುಹಿಸಿ ಕೊಟ್ಟು ನಿಮಗೆ ಸಹಾಯ ಮಾಡುತ್ತೇನೆಂದು ಅವನು ಉತ್ತರಿಸಿದನು.');
INSERT INTO "kannada" VALUES(8,10,'8:10','ಈ ವ್ಯವಸ್ಥೆಯನ್ನು ಮಾಡಿದ್ದು ನಿಮಗೆ ಸುವಾರ್ತೆಗೆ ಹಾಗೂ ಅದರಿಂದ ನಿಮ್ಮ ಮನಸ್ಸಿನ ಶಾಂತಿಗೆ ಮಾತ್ರವಾಗಿತ್ತು. ನಿಜದಲ್ಲಿ ಸಹಾಯವು ಅಲ್ಲಾಹನನಿಂದಲ್ಲದೆ ಬೇರಿಲ್ಲ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹು ಮಹಾ ಪ್ರತಾಪಿಯೂ ಪರಮ ಧೀಮಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(8,11,'8:11','ಅಲ್ಲಾಹನು ತನ್ನ ಕಡೆಯಿಂದ ನಿಮ್ಮ ನಿರ್ಭ ಯತೆಗಾಗಿ ನಿದ್ರಾ ಮಂಪರನ್ನು ಆವರಣಗೊಳಿಸಿದ್ದ ಹಾಗೂ ನಿಮ್ಮ ಶುಚೀಕರಣ ಮತ್ತು ಶೈತಾನನ ಮಾಲಿನ್ಯವನ್ನು ನಿಮ್ಮಿಂದ ನಿವಾರಿಸಲು ಮತ್ತು ನಿಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ಧೈರ್ಯ ತುಂಬಲಿಕ್ಕೂ ತನ್ಮೂಲಕ ನಿಮ್ಮ ಪಾದಗಳನ್ನು ನೆಲೆಯೂರಿಸಲಿಕ್ಕೂ ಆಕಾಶದಿಂದ ನಿಮ್ಮ ಮೇಲೆ ಮಳೆ ಸುರಿಸುತ್ತಿದ್ದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(8,12,'8:12','ನಿಮ್ಮ ಪ್ರಭು ದೇವಚರರಿಗೆ, ನಾನು ನಿಮ್ಮೊಂದಿಗೆ ಇದ್ದೇನೆ. ಆದ್ದರಿಂದ ನೀವು ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನು ಸುಸ್ಥಿರಗೊಳಿಸಿರಿ. ಸತ್ಯನಿಷೇಧಿಗಳ ಹೃದಯ ಗಳಲ್ಲಿ ಭೀತಿಯನ್ನು ಹುಟ್ಟಿಸಲಿದ್ದೇನೆ. ನೀವು ಅವರ ಶಿರಚ್ಛೇದನ ಮಾಡಿರಿ ಮತ್ತು ಅವರ ಕೈಕಾಲುಗಳ ಬೆರಳುಗಳನ್ನು ಕತ್ತರಿಸಿರಿ ಎಂದು ಸೂಚನೆ ಕೊಡುತ್ತಿದ್ದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(8,13,'8:13','ಇದು ಅಲ್ಲಾಹ್ ಮತ್ತು ಅವನ ಸಂದೇಶವಾ ಹಕರಿಗೆ ಅವರು ಎದುರಾದುದರಿಂದ, ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ಸಂದೇಶವಾಹಕನನ್ನು ವಿರೋಧಿಸಿದವನನ್ನು ಅಲ್ಲಾಹು ಅತ್ಯಂತ ಕಠಿಣವಾಗಿ ದಂಡಿಸುವವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(8,14,'8:14','ಇದು ನಿಮಗಿರುವ ಶಿಕ್ಷೆ. ಇದನ್ನು ಸವಿಯಿರಿ. ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ನರಕದ ಶಿಕ್ಷೆ ಇದೆ (ಯೆಂಬುದು ನಿಮಗೆ ತಿಳಿದಿರಲಿ.)');
INSERT INTO "kannada" VALUES(8,15,'8:15','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಸತ್ಯನಿಷೇಧಿಗಳು ಯುದ್ಧ ಸನ್ನಾಹರಾಗಿ ಜಮಾವಣೆಗೊಂಡ ಸ್ಥಿತಿಯಲ್ಲಿ ಎದುರುಗೊಂಡರೆ ಅವರಿಂದ ಹಿಮ್ಮೆಟ್ಟಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(8,16,'8:16','ಯುದ್ಧ ತಂತ್ರಕ್ಕಾಗಿ ಅಥವಾ (ಸ್ವಂತ) ಸೈನ್ಯ ದೊಂದಿಗೆ ಹೋಗಿ ಸೇರಿಕೊಳ್ಳಲಿಕ್ಕಾಗಿ ಹೊರತು ಯಾರಾದರೂ (ಶತ್ರುಗಳ ಎದುರಿನಿಂದ) ಹಿಮ್ಮೆಟ್ಟಿದರೆ ಅವನು ಅಲ್ಲಾಹನ ಕ್ರೋಧದೊಂದಿಗೆ ಮರಳಿದನು. ಅವನ ವಾಸಸ್ಥಾನವು ನರಕವಾಗಿ ರುವುದು. ಅದು ಹಿಂತೆರಳುವ ಅತಿ ನಿಕೃಷ್ಟ ತಾಣ.');
INSERT INTO "kannada" VALUES(8,17,'8:17','ನೀವು ಅವರನ್ನು ವಧಿಸಲಿಲ್ಲ. ನಿಜವಾಗಿ ಅವರನ್ನು ವಧಿಸಿದ್ದು ಅಲ್ಲಾಹು (ಓ ಪೈಗಂಬರರೇ,) ನೀವು ಎಸೆದಾಗ ನಿಜದಲ್ಲಿ ಎಸೆದದ್ದು ನೀವಲ್ಲ. ನಿಜವಾಗಿ ಅಲ್ಲಾಹನು ಎಸೆದನು . ಇದು ಅಲ್ಲಾಹು ತನ್ನ ಕಡೆಯಿಂದ ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನು ಒಂದು ಅತ್ಯುತ್ತಮ ಪರೀಕ್ಷೆಯಿಂದ ಪರೀಕ್ಷಿಸಲಿಕ್ಕಾಗಿತ್ತು. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹು ಸರ್ವಶ್ರುತನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(8,18,'8:18','ಈ ಪರೀಕ್ಷೆಯು ನಿಶ್ಚಯವಾಗಿಯೂ ಸತ್ಯ. ಅಲ್ಲಾಹನು ಸತ್ಯನಿಷೇಧಿಗಳ ಕುತಂತ್ರಗಳನ್ನು ದುರ್ಬಲಗೊಳಿಸುವವನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(8,19,'8:19','(ಸತ್ಯನಿಷೇಧಿಗಳೇ) ನೀವು ಬಯಸುತ್ತೀರಾದರೆ, ಇದೋ ಸತ್ಯಕ್ಕೆ ವಿಜಯ ಬಂದು ಬಿಟ್ಟಿದೆ. ಇನ್ನಾದರೂ (ಸತ್ಯನಿಷೇಧವನ್ನು) ತೊರೆದರೆ ನಿಮಗೇ ಒಳಿತು. ಮುಂದೆ ಇದನ್ನೇ ಪುನರಾ ವರ್ತಿಸಿದರೆ ನಾವೂ ಇದೇ ಶಿಕ್ಷೆಯನ್ನು ಪುನರಾ ವರ್ತಿಸುವೆವು. ನಿಮ್ಮ ಜನಬಲವು ಅದೆಷ್ಟೇ ದೊಡ್ಡದಿದ್ದರೂ ನಿಮಗೇನೂ ಪ್ರಯೋಜ ನವಾಗದು. ಅಲ್ಲಾಹು ಸತ್ಯವಿಶ್ವಾಸಿಗಳೊಂದಿಗೆ ಇದ್ದಾನೆ .');
INSERT INTO "kannada" VALUES(8,20,'8:20','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ರಸೂಲರನ್ನು ಅನುಸರಿಸಿರಿ. ನೀವು (ಸತ್ಯ ಸಂದೇಶವನ್ನು) ಕೇಳುತ್ತ ಅವರಿಂದ ವಿಮುಖ ರಾಗಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(8,21,'8:21','ಆಲಿಸದೆಯೇ ನಾವು ಆಲಿಸಿದೆವು ಎನ್ನುವವರಂತೆ ನೀವಾಗಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(8,22,'8:22','ಯೋಚಿಸಿ ಅರ್ಥ ಮಾಡಿಕೊಳ್ಳದ ಕಿವುಡ, ಮೂಕ ಜನರೇ ಅಲ್ಲಾಹನ ಬಳಿ ಅತ್ಯಂತ ಕೀಳ್ತರದ ಜಂತುಗಳು.');
INSERT INTO "kannada" VALUES(8,23,'8:23','ಅವರಲ್ಲಿ ಕಿಂಚಿತ್ತಾದರೂ ಒಳಿತಿದೆಯೆಂದು ಅಲ್ಲಾಹು ತಿಳಿಯುತ್ತಿದ್ದರೆ ನಿಶ್ಚಯವಾಗಿಯೂ ಅವನು ಅವರಿಗೆ (ಗ್ರಹಿಸುವಂತೆ) ಕೇಳಿಸು ತ್ತಿದ್ದನು. ಅವನು ಅವರಿಗೆ ಕೇಳಿಸುತ್ತಿದ್ದರೂ ಅವರು ಅವಗಣಿಸಿ ತಿರುಗಿ ಬಿಡುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(8,24,'8:24','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನಿಮಗೆ ಚಿರಂಜೀವ ನೀಡುವಂಥ ವಿಷಯದ ಕಡೆಗೆ ಅಲ್ಲಾಹು ಮತ್ತು ರಸೂಲರು ನಿಮ್ಮನ್ನು ಕರೆದಾಗ ಓಗೊಡಿರಿ. ಅಲ್ಲಾಹು ಮನುಷ್ಯನ ಮತ್ತು ಅವನ ಹೃದಯದ ನಡುವೆ ಮರೆ ಇಡುವನೆಂದೂ ನೀವು ಅವನ ಕಡೆಗೆ ಒಟ್ಟುಗೂಡಿಸಲ್ಪಡುವಿರೆಂದೂ ಅರಿತು ಕೊಳ್ಳಿರಿ.');
INSERT INTO "kannada" VALUES(8,25,'8:25','ಯಾವ ಶಿಕ್ಷೆಯು ನಿಮ್ಮ ಪೈಕಿ ಅಕ್ರಮವೆಸಗಿ ದವರಿಗೆ ಮಾತ್ರ ಸೀಮಿತವಾಗಿರಲಾರದೋ ಆ ಪರೀಕ್ಷೆಯನ್ನು ಭಯಪಡಿರಿ. ಅಲ್ಲಾಹು ಉಗ್ರದಂಡಕನೆಂಬುದು ನಿಮಗೆ ತಿಳಿದಿರಲಿ .');
INSERT INTO "kannada" VALUES(8,26,'8:26','ನೀವು ಅಲ್ಪ ಸಂಖ್ಯಾತರಾಗಿದ್ದು ಭೂಮಿಯಲ್ಲಿ ನಿಮ್ಮನ್ನು ಬಲಹೀನರೆಂದು ಪರಿಗಣಿಸಲ್ಪಡುತ್ತಿದ್ದ ಸಂದರ್ಭವನ್ನು ಜ್ಞಾಪಿಸಿರಿ. ಜನರು ನಿಮ್ಮನ್ನು ಎಗರಿಸಿ ಬಿಡುತ್ತಾರೆಂದು ಹೆದರುತ್ತಲಿದ್ದೀರಿ. ಅನಂತರ ಅಲ್ಲಾಹನು ನಿಮಗೆ ಆಶ್ರಯವನ್ನು ಒದಗಿಸಿದನು. ತನ್ನ ಸಹಾಯದಿಂದ ನಿಮ್ಮನ್ನು ಬಲಪಡಿಸಿದನು ಮತ್ತು ನಿಮಗೆ ಉತ್ತಮ ಜೀವನಾನುಕೂಲ ಒದಗಿಸಿದನು. ನೀವು ಕೃತಜ್ಞರಾಗಬಹುದೆಂದು.');
INSERT INTO "kannada" VALUES(8,27,'8:27','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಅಲ್ಲಾಹು ಮತ್ತು ರಸೂಲರನ್ನು ವಂಚಿಸಬೇಡಿರಿ ಮತ್ತು ನಿಮ್ಮ ಅಮಾನತ್ತುಗಳಲ್ಲಿ ತಿಳಿದುಕೊಂಡೇ ವಿದ್ರೋಹ ವೆಸಗಬೇಡಿರಿ .');
INSERT INTO "kannada" VALUES(8,28,'8:28','ನಿಮ್ಮ ಸೊತ್ತುಗಳು ಮತ್ತು ನಿಮ್ಮ ಸಂತತಿಗಳು ಪರೀಕ್ಷಾಸಾಧನಗಳೆಂದೂ ಘನವೆತ್ತ ಪ್ರತಿಫಲವು ಅಲ್ಲಾಹನ ಬಳಿಯೆಂದೂ ತಿಳಿದುಕೊಳ್ಳಿರಿ.');
INSERT INTO "kannada" VALUES(8,29,'8:29','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ದೇವಭಯ ವನ್ನಿರಿಸಿಕೊಂಡರೆ ಅಲ್ಲಾಹು ನಿಮಗೊಂದು ವಿವೇಚನೆಯ ಮಾರ್ಗವನ್ನು ಒದಗಿಸುವನು . ನಿಮ್ಮ ದೋಷಗಳನ್ನು ನಿಮ್ಮಿಂದ ಪರಿಹರಿ ಸುವನು. ಮತ್ತು ನಿಮಗೆ ಕ್ಷಮಿಸುವನು. ಅಲ್ಲಾ ಹನು ಪರಮಗಣ್ಯ ಪರಮೋಧಾರಿ.');
INSERT INTO "kannada" VALUES(8,30,'8:30','ಸತ್ಯನಿಷೇಧಿಗಳು ನಿಮ್ಮನ್ನು ಸೆರೆಹಿಡಿಯಲಿಕ್ಕೆ ಅಥವಾ ವಧಿಸಲಿಕ್ಕೆ ಅಥವಾ ಹೊರಹಾಕಲಿಕ್ಕೆ ಗೂಢಾಲೋಚನೆ ನಡೆಸುತ್ತಿದ್ದ ಸಂದರ್ಭವೂ ಸ್ಮರಣೀಯವಾಗಿದೆ . ಅವರು ತಮ್ಮ ಕುತಂತ್ರ ಗಳನ್ನು ಹೂಡುತ್ತಿದ್ದರು. ಅಲ್ಲಾಹು ಪ್ರತಿತಂತ್ರ ಹೂಡುತ್ತಿದ್ದನು ಪ್ರತಿತಂತ್ರದಲ್ಲಿ ಅಲ್ಲಾಹನು ಪರಮ ನಿಪುಣನು.');
INSERT INTO "kannada" VALUES(8,31,'8:31','ಅವರಿಗೆ ನಮ್ಮ ವಚನಗಳನ್ನು ಓದಿ ಕೇಳಿಸಿ ದಾಗ ಅವರು, ಹೌದು ನಾವು ಕೇಳಿದೆವು. ನಾವು ಇಚ್ಛಿಸಿದರೆ ಇದೇ ರೀತಿ ನಾವೂ ಸೃಷ್ಟಿಸಿ ಹೇಳ ಬಲ್ಲೆವು. ಇವು ಪೂರ್ವಿಕರ ಕಟ್ಟುಕಥೆಗಳಲ್ಲದೆ ಇನ್ನೇನೂ ಅಲ್ಲ ಎನ್ನುತ್ತಿದ್ದರು .');
INSERT INTO "kannada" VALUES(8,32,'8:32','ಓ ಅಲ್ಲಾಹ್, ಇದು ನಿಜವಾಗಿಯೂ ನಿನ್ನ ಕಡೆ ಯಿಂದ ಬಂದ ಸತ್ಯವಾಗಿದ್ದರೆ ನಮ್ಮ ಮೇಲೆ ಆಕಾಶದಿಂದ ಕಲ್ಲಿನ ಮಳೆಗರೆಸು ಅಥವಾ ನಮಗೆ ಇನ್ನಾವುದೇ ವೇದನಾತ್ಮಕ ಶಿಕ್ಷೆಯನ್ನು ತಾ ಎಂದು ಅವರು (ಅವಿಶ್ವಾಸಿಗಳು) ಹೇಳಿದ್ದ ಮಾತೂ ಜ್ಞಾಪಕದಲ್ಲಿದೆ.');
INSERT INTO "kannada" VALUES(8,33,'8:33','ನೀವು ಅವರಲ್ಲಿ ಇರುವಾಗ ಅಲ್ಲಾಹು ಅವರನ್ನು ಶಿಕ್ಷಿಸಲಾರ. ಅವರು ಕ್ಷಮಾಯಾಚನೆ ಮಾಡುತ್ತಿರುವಾಗಲೂ ಅಲ್ಲಾಹು ಅವರನ್ನು ಶಿಕ್ಷಿಸಲಾರ.');
INSERT INTO "kannada" VALUES(8,34,'8:34','ಮಸ್ಜಿದುಲ್ ಹರಾಮ್''ನ ದಾರಿಯನ್ನು ಜನರಿಗೆ ತಡೆಯುತ್ತಿರುವ ಇವರನ್ನು ಅಲ್ಲಾಹು ಶಿಕ್ಷೆಗೊಳಪಡಿಸದಿರಲು ಕಾರಣವಿಲ್ಲ. ವಸ್ತುತಃ ಅವರು ಆ ಮಸ್ಜಿದ್‍ನ ಅರ್ಹ ಉಸ್ತುವಾರಿದಾ ರರಲ್ಲ. ಅದರ ಅರ್ಹ ಉಸ್ತುವಾರಿದಾರರು ಧರ್ಮನಿಷ್ಠರು ಮಾತ್ರವೇ ಆಗಬಲ್ಲರು. ಆದರೆ ಹೆಚ್ಚಿನವರು ಇದನ್ನು ತಿಳಿದು ಕೊಳ್ಳುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(8,35,'8:35','ಕಅïಬಾ ಭವನದ ಬಳಿ ಅವರ ಪ್ರಾರ್ಥನೆ ಕೇವಲ ಸಿಳ್ಳು ಹೊಡೆಯುವುದು, ಚಪ್ಪಾಳೆ ಹೊಡೆಯು ವುದು ಹೊರತು ಇನ್ನೇನೂ ಆಗಿರಲಿಲ್ಲ. ಆದುದರಿಂದ ಇದೋ ನೀವು ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸಿದುದರ ಫಲವಾಗಿ ಶಿಕ್ಷೆಯನ್ನು ಸವಿಯಿರಿ.');
INSERT INTO "kannada" VALUES(8,36,'8:36','ಸತ್ಯನಿಷೇಧಿಗಳು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ ಜನರನ್ನು ತಡೆಯಲಿಕ್ಕಾಗಿ ತಮ್ಮ ಸಂಪತ್ತನ್ನು ವಿನಿಯೋಗಿಸುತ್ತಾರೆ. ಇನ್ನೂ ವಿನಿಯೋಗಿಸಲಿದ್ದಾರೆ. ಕೊನೆಗೆ ಅದು ಅವರಿಗೆ ವಿಷಾದಕ್ಕೆ ಕಾರಣವಾಗುವುದು. ಅನಂತರ ಅವರು ಪರಾಜಿತರಾಗುವರು. ಸತ್ಯನಿಷೇಧಿಗಳು ನರಕದ ಕಡೆಗೆ ಅಟ್ಟಲ್ಪಡುವರು.');
INSERT INTO "kannada" VALUES(8,37,'8:37','ಇದು ಅಲ್ಲಾಹು ಮಾಲಿನ್ಯವನ್ನು ನೈರ್ಮಲ್ಯದಿಂದ ಬೇರ್ಪಡಿಸಲೂ ಎಲ್ಲಾ ವಿಧದ ಮಾಲಿನ್ಯಗಳನ್ನು ಪೇರಿಸಿಟ್ಟು ರಾಶಿ ಮಾಡಿ ನರಕಕ್ಕೆ ತಳ್ಳಿ ಬಿಡಲೂ ಆಗಿರುತ್ತದೆ. ನಿಜವಾಗಿ ನಷ್ಟ ಹೊಂದಿದವರು ಇವರೇ.');
INSERT INTO "kannada" VALUES(8,38,'8:38','(ಓ ಪೈಗಂಬರರೇ,) ಈ ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಹೇಳಿರಿ:- ಅವರು (ವೈರತ್ವದಿಂದ) ವಿರಮಿಸಿದರೆ ಅವರಿಂದ ಕಳೆದು ಹೋದ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸಿ ಬಿಡಲಾಗುವುದು. ಆದರೆ ಇವರು ಹಿಂ ದಿನ ಅವಸ್ಥೆಗೆ ಮರಳಿದರೆ ಪೂರ್ವಿಕರ ಮೇಲಿನ ನಮ್ಮ ಕಾರ್ಯಾಚರಣೆ ಗತಿಸಿ ಹೋಗಿದೆ ತಾನೇ?');
INSERT INTO "kannada" VALUES(8,39,'8:39','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಕ್ಷೋಭೆ ಅಳಿದು ಹೋಗು ವವರೆಗೂ ಧರ್ಮವು ಸಂಪೂರ್ಣವಾಗಿ ಅಲ್ಲಾಹನಿ ಗಾಗಿ ಆಗುವವರೆಗೂ ಸತ್ಯನಿಷೇಧಿಗಳ ವಿರುದ್ಧ ಹೋರಾಡಿರಿ. ಇನ್ನು ಅವರು ವಿರಮಿಸಿದರೆ ಅಲ್ಲಾಹು ಅವರ ಕರ್ಮಗಳನ್ನು ವೀಕ್ಷಿಸುವವ ನಾಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(8,40,'8:40','ಅವರು (ಸತ್ಯದಿಂದ) ಹಿಂಜರಿದರೆ ಅಲ್ಲಾಹು ನಿಮ್ಮ ಸಂರಕ್ಷಕನೆಂದೂ ಅವನು ಅತ್ಯಂತ ಶ್ರೇಷ್ಟ ರಕ್ಷಕನೂ ಸಹಾಯಕನೂ ಆಗಿರುತ್ತಾನೆಂದೂ ತಿಳಿದುಕೊಳ್ಳಿರಿ .');
INSERT INTO "kannada" VALUES(8,41,'8:41','ತಿಳಿಯಿರಿ! ನೀವು ಗಳಿಸಿದ ಸಮರಾರ್ಜಿತ ಸೊತ್ತಿನ ಐದನೇ ಒಂದಂಶವು ಅಲ್ಲಾಹನಿಗೆ, ಅವನ ಸಂದೇಶವಾಹಕರಿಗೆ, (ಅವರ) ಸಂಬಂಧಿಕರಿಗೆ, ಅನಾಥರಿಗೆ, ನಿರ್ಗತಿಕರಿಗೆ ಮತ್ತು ಪ್ರಯಾಣಿಕರಿಗಾಗಿರುತ್ತದೆ. ನೀವು ಅಲ್ಲಾಹನ ಮೇಲೆ ಮತ್ತು ಸತ್ಯಾಸತ್ಯದ ಇತ್ಯರ್ಥದ ದಿನ ಅರ್ಥಾತ್ ಎರಡು ಪಕ್ಷಗಳು ಎದುರ್ಗೊಂಡ ದಿನ (ಬದ್‍ರ್‍ದಿನ) ನಾವು ನಮ್ಮ ದಾಸನ ಮೇಲೆ ಅವತೀರ್ಣಗೊಳಿಸಿದುದರ ಮೇಲೆ ವಿಶ್ವಾಸವಿಟ್ಟಿದ್ದರೆ . ಅಲ್ಲಾಹು ಪ್ರತಿಯೊಂದು ವಿಷಯದಲ್ಲಿ ಸಾಮಥ್ರ್ಯವುಳ್ಳವನು.');
INSERT INTO "kannada" VALUES(8,42,'8:42','ನೀವು ಹತ್ತಿರದ ಕಣಿವೆಯಲ್ಲೂ ಅವರು ದೂರದ ಕಣಿವೆಯಲ್ಲೂ ಮತ್ತು ವರ್ತಕರು ನಿಮಗಿಂತ ಕೆಳಗಡೆ ಇದ್ದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ. (ನಿಮ್ಮ ಮತ್ತು ಅವರ ನಡುವೆ) (ಯುದ್ಧದ) ನಿಶ್ಚ ಯವಾಗಿರುತ್ತಿದ್ದರೆ, ನೀವು ಆ ನಿಶ್ಚಯಕ್ಕೆ ಎದುರಾಗುತ್ತಿದ್ದಿರಿ. ಆದರೆ ಉಂಟಾಗಬೇಕಾದ ಕಾರ್ಯ ವನ್ನು ಅಲ್ಲಾಹು ವಿಧಿಸಲಿಕ್ಕಾಗಿತ್ತು ಇದು. ನಾಶ ವಾದವರು ಪ್ರತ್ಯಕ್ಷ ಪ್ರಮಾಣದೊಂದಿಗೆ ನಾಶ ಹೊಂದಲೂ ಬದುಕಿದವರು ಪ್ರತ್ಯಕ್ಷ ಪ್ರಮಾಣದೊಂದಿಗೆ ಬದುಕಲೂಬೇಕಾಗಿ. (ಇದೆಲ್ಲ ಸಂಭವಿಸಿತು.) ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹು ಸರ್ವಶ್ರುತನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(8,43,'8:43','(ಓ ಸಂದೇಶವಾಹಕರೇ,) ಅಲ್ಲಾಹನು ಅವರನ್ನು ನಿಮಗೆ ಸ್ವಪ್ನದಲ್ಲಿ ಕಡಿಮೆ ಸಂಖ್ಯೆಯಲ್ಲಿ ತೋರಿಸುತ್ತಿದ್ದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ. ಅವನು ನಿಮಗೆ ಅವರ ಸಂಖ್ಯೆಯನ್ನು ಅಧಿಕವಾಗಿ ತೋರಿಸುತ್ತಿದ್ದರೆ, ಖಂಡಿತ ನೀವು ಧೈರ್ಯಗೆಡುತ್ತಿದ್ದಿರಿ ಮತ್ತು (ಯುದ್ಧದ) ವಿಷಯದಲ್ಲಿ ಭಿನ್ನಾಭಿಪ್ರಾಯ ಹೊಂದುತ್ತಿದ್ದೀರಿ. ಆದರೆ ಅಲ್ಲಾಹನೇ (ನಿಮ್ಮನ್ನು ಇದರಿಂದ) ರಕ್ಷಿಸಿದನು. ನಿಶ್ಚಯವಾಗಿಯೂ ಅವನು ಹೃದ್ಗತವನ್ನು ಅರಿಯುವವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(8,44,'8:44','ಸಂಗ್ರಾಮದ ವೇಳೆ ನಿಮ್ಮ ದೃಷ್ಟಿಯಲ್ಲಿ ಅವರನ್ನು (ಶತ್ರುಗಳನ್ನು) ಕಡಿಮೆಯಾಗಿಯೂ ಅವರ ದೃಷ್ಟಿಯಲ್ಲಿ ನಿಮ್ಮನ್ನು ಕಡಿಮೆಯಾಗಿಯೂ ಅಲ್ಲಾಹನು ತೋರಿಸಿಕೊಟ್ಟ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ. ಸಂಭವಿಸಬೇಕಾದ ಕಾರ್ಯವನ್ನು ಅಲ್ಲಾಹನು ನೆರವೇರಿಸಲಿಕ್ಕಾಗಿ, ಸಕಲ ಕಾರ್ಯಗಳೂ ಅಲ್ಲಾಹನ ಕಡೆಗೆ ಮರಳುತ್ತವೆ.');
INSERT INTO "kannada" VALUES(8,45,'8:45','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ಯಾವುದೇ (ಸೈನ್ಯ) ತಂಡವನ್ನು ಎದುರುಗೊಂಡರೆ ನೀವು ಸುಸ್ಥಿರರಾಗಿರಿ ಮತ್ತು ಅಲ್ಲಾಹನನ್ನು ಅತಿ ಹೆಚ್ಚಾಗಿ ಸ್ಮರಿಸಿರಿ. ಹಾಗಾದರೆ ನಿಮಗೆ ಜಯದ ನಿರೀಕ್ಷೆಯಿಡಬಹುದು.');
INSERT INTO "kannada" VALUES(8,46,'8:46','ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ರಸೂಲರನ್ನು ಅನು ಸರಿಸಿರಿ. ಪರಸ್ಪರ ಜಗಳಾಡಬೇಡಿರಿ. ಅನ್ಯಥಾ ನಿಮ್ಮಲ್ಲಿ ಅಂಜಿಕೆ ಹುಟ್ಟುವುದು. ನಿಮ್ಮ ಕೆಚ್ಚು ಕೊಚ್ಚಿ ಹೋಗುವುದು. ಸಹನೆಯಿಂದ ವರ್ತಿಸಿರಿ. ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹನಿರುವುದು ಸಹನಶೀಲರೊಂದಿಗೆ.');
INSERT INTO "kannada" VALUES(8,47,'8:47','ಅಹಂಭಾವದಿಂದಲೂ ಜನರಿಗೆ ತಮ್ಮ ಠೀವಿಯನ್ನು ತೋರಿಸುತ್ತಲೂ ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ ಜನರನ್ನು ತಡೆಯುತ್ತಲೂ ತಮ್ಮ ನಿವಾಸಗಳಿಂದ ಹೊರಟು ಬಂದವರಂತೆ ನೀವಾಗಬಾರದು. ಅವರ ಕೃತ್ಯಗಳನ್ನು ಅಲ್ಲಾಹನು ಆವರಿಸಿ ಅರಿತಿರುವನು.');
INSERT INTO "kannada" VALUES(8,48,'8:48','ಇಂದು ನಿಮ್ಮನ್ನು ಸೋಲಿಸುವ ಜನರೇ ಇಲ್ಲ, ಖಂಡಿತ ನಾನು ನಿಮ್ಮ ಜೊತೆಗೆ ಇದ್ದೇನೆ. ಎನ್ನುತ್ತಾ ಶೈತಾನನು ಅವರಿಗೆ ತಮ್ಮ ಕುಕೃತ್ಯ ಗಳನ್ನು ಚಂದಗಾಣಿಸಿ ಕೊಟ್ಟ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ.ಆದರೆ ಇತ್ತಂಡಗಳು ಪರಸ್ಪರ ಎದುರುಗೊಂಡಾಗ ಅವನು ಹಿಮ್ಮೆಟ್ಟಿ ಓಡಿದನು ಮತ್ತು ‘ನನ್ನ ಮತ್ತು ನಿಮ್ಮ ನಡುವೆ ಒಡನಾಟವಿಲ್ಲ. ನೀವು ಕಾಣ ದ್ದನ್ನು ನಾನು ಕಾಣುತ್ತಿದ್ದೇನೆ. ನನಗೆ ಅಲ್ಲಾಹನ ಭಯವಾಗುತ್ತದೆ. ಮತ್ತು ಅಲ್ಲಾಹು ಅತ್ಯಂತ ಕಠಿಣವಾಗಿ ಶಿಕ್ಷಿಸುವವನು’ ಎಂದು ಹೇಳಿದನು .');
INSERT INTO "kannada" VALUES(8,49,'8:49','ಇವರನ್ನು (ಮುಸ್ಲಿಮರನ್ನು) ಇವರ ಧರ್ಮವು ವಂಚಿಸಿದೆ ಎಂದು ಕಪಟಿಗಳೂ ಹೃದಯಗಳಲ್ಲಿ ರೋಗ ತಗಲಿದವರೂ ಹೇಳುತ್ತಿದ್ದ ಸಂದರ್ಭ ವನ್ನು ಸ್ಮರಿಸಿರಿ . ವಸ್ತುತಃ ಯಾರಾದರೂ ಅಲ್ಲಾಹನ ಮೇಲೆ ಭರವಸೆಯನ್ನಿಟ್ಟರೆ (ಅವನು ಸೋಲ ಲಾರನು) ಅಲ್ಲಾಹು ಮಹಾಪ್ರತಾಪಿಯೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(8,50,'8:50','ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ಅವರ ಮುಖಗಳು ಹಾಗೂ ನಿತಂಬಗಳಿಗೆ ಹೊಡೆಯುತ್ತಾ ಮಲಕ್‍ಗಳು ಸಾಯಿಸುತ್ತಿದ್ದ ಅವಸ್ಥೆಯನ್ನು ನೀವು ಕಾಣಬೇಕಿತ್ತು. ಇದೋ ಈಗ ಸುಡುವ ಶಿಕ್ಷೆಯನ್ನು ಸವಿಯಿರಿ . (ಎನ್ನುತ್ತಾ!)');
INSERT INTO "kannada" VALUES(8,51,'8:51','ನಿಮ್ಮ ಕೈಗಳು ಈ ಮೊದಲು ಎಸಗಿದ ಪಾಪಗಳ ಪ್ರತಿಫಲವಿದು. ಅಲ್ಲಾಹು ತನ್ನ ದಾಸರ ಮೇಲೆ ಅಕ್ರ ಮವೆಸಗುವವನಲ್ಲ ಎಂಬ ಕಾರಣಕ್ಕಾಗಿದೆ ಇದು.');
INSERT INTO "kannada" VALUES(8,52,'8:52','ಇದು ಫಿರ್‍ಔನನ ಜನಾಂಗ ಮತ್ತು ಅವರ ಪೂರ್ವಿಕರ ರೂಡಿಯಂತೆ. ಅವರು ಅಲ್ಲಾಹನ ನಿದರ್ಶನಗಳನ್ನು ನಿರಾಕರಿಸಿದರು. ಆಗ ಅಲ್ಲಾ ಹನು ಅವರ ಪಾಪಗಳಿಗಾಗಿ ಅವರನ್ನು ಶಿಕ್ಷಿಸಿದನು. ಅಲ್ಲಾಹನು ಪರಮ ಬಲಿಷ್ಟನೂ ಉಗ್ರವಾಗಿ ದಂಡಿಸುವವನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(8,53,'8:53','ಇದೇಕೆಂದರೆ ಅಲ್ಲಾಹನು ಯಾವುದೇ ಜನಾಂಗಕ್ಕೆ ಕೊಡಮಾಡಿದ ಅನುಕೂಲವನ್ನು ಆ ಜನಾಂಗ ಸ್ವತಃ ಬದಲಾಯಿಸಿಕೊಳ್ಳುವವರೆಗೂ ಅಲ್ಲಾಹನು ಬದಲಾಯಿಸುವವನಾಗಿರಲಿಲ್ಲ. (ಅಂತಹ ರೂಢಿ ಇರುವುದಿಲ್ಲ). ಅಲ್ಲಾಹು ಸರ್ವ ಶ್ರುತನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(8,54,'8:54','ಫಿರ್‍ಔನನ ಜನಾಂಗ ಮತ್ತು ಅವರ ಪೂರ್ವ ಜನಾಂಗದವರ ಸಂಪ್ರದಾಯದಂತೆ ಅವರು ತಮ್ಮ ಪ್ರಭುವಿನ ನಿದರ್ಶನಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದರು. ಆಗ ಅವರ ಪಾಪಗಳ ಫಲವಾಗಿ ಅವರನ್ನು ನಾಶಗೊಳಿಸಿದೆವು. ಫಿರ್‍ಔನನ ಜನಾಂಗವನ್ನು ಮುಳುಗಿಸಿದೆವು. ಅವರೆಲ್ಲ ದುಷ್ಕರ್ಮಿಗಳಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(8,55,'8:55','ಸತ್ಯವನ್ನು ಧಿಕ್ಕರಿಸಿದ ಹಾಗೂ ಎಂದಿಗೂ ಸತ್ಯ ಪ್ರಾಪ್ತರಾಗದವರು ಅಲ್ಲಾಹನ ಬಳಿ ಅತ್ಯಂತ ನೀಚ ಜಂತುಗಳು.');
INSERT INTO "kannada" VALUES(8,56,'8:56','ಅವರ ಪೈಕಿ ನಿಮ್ಮೊಡನೆ ಒಪ್ಪಂದ ಮಾಡಿಕೊಂಡ ವರು. ಅವರು ಪ್ರತಿ ಸಲವೂ ಅದನ್ನು ಮುರಿಯು ತ್ತಾರೆ ಅವರು ಅಲ್ಲಾಹನ ಬಗ್ಗೆ ಜಾಗೃತೆ ವಹಿಸುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(8,57,'8:57','ಅವರು ಯುದ್ಧದಲ್ಲಿ ನಿಮಗೆ ಮುಖಾಮುಖಿ ಯಾದಾಗ ಅವರ ಬೆನ್ನಹಿಂದಿನವರಿಂದ ಅವರನ್ನು ಬೇರ್ಪಡಿಸಿ (ಬೆನ್ನ ಹಿಂದಿನವರಿಗೆ) ಒಂದು ಪಾಠವಾಗತಕ್ಕ ರೀತಿಯಲ್ಲಿ ಶಿಕ್ಷೆ ನೀಡಿರಿ.');
INSERT INTO "kannada" VALUES(8,58,'8:58','ನಿಮಗೆ ಯಾವುದೇ ಜನಾಂಗದಿಂದ ವಂಚನೆಯ ಆಶಂಕೆಯಿದ್ದರೆ ಅವರೊಂದಿಗಿನ ಕರಾರನ್ನು ಅವರೆಡೆಗೇ ತತ್ಸಮಾನ ರೀತಿಯಲ್ಲಿ ಎಸೆದು ಬಿಡಿರಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹು ವಂಚಕರನ್ನು ಮೆಚ್ಚುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(8,59,'8:59','ಸತ್ಯನಿಷೇಧಿಗಳು ಅಲ್ಲಾಹನನ್ನು ಹಿಂದಿಕ್ಕುವರೆಂದು ಖಂಡಿತ ಭಾವಿಸಬಾರದು. ಖಂಡಿತವಾಗಿಯೂ ಅವರು ಅವನನ್ನು ಸೋಲಿಸಲಾರರು.');
INSERT INTO "kannada" VALUES(8,60,'8:60','ನೀವು ನಿಮ್ಮಿಂದ ಸಾಧ್ಯವಾದಷ್ಟು ಹೆಚ್ಚು ಶಕ್ತಿಯನ್ನು ಹಾಗೂ ಕಟ್ಟಿ ಅಣಿಗೊಳಿಸಿದ ಪಡೆ ಕುದುರೆಗಳನ್ನು ಅವರೊಡನೆ ಹೋರಾಟ ನಡೆಸಲಿಕ್ಕಾಗಿ ಸಜ್ಜುಗೊಳಿಸಿರಿ. ಇದರ ಮೂಲಕ ಅಲ್ಲಾಹನ ಶತ್ರು, ನಿಮ್ಮ ಶತ್ರು ಮತ್ತು ನಿಮಗೆ ಅರಿವಿಲ್ಲದ ಹಾಗೂ ಅಲ್ಲಾಹನಿಗೆ ಅರಿವಿರುವ ಇತರ ಶತ್ರುಗಳನ್ನು ಭೀತಿಗೊಳಿಸಲಿಕ್ಕಾಗಿ, ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ನೀವು ಏನೇ ಖರ್ಚು ಮಾಡಿದರೂ ಅದರ ಸಂಪೂರ್ಣ ಪ್ರತಿಫಲವನ್ನು ನಿಮಗೆ ಕೊಡಲಾಗುವುದು. ನಿಮ್ಮೊಂದಿಗೆ ಎಷ್ಟು ಮಾತ್ರ ಕ್ಕೂ ಅನ್ಯಾಯ ಮಾಡಲಾಗದು .');
INSERT INTO "kannada" VALUES(8,61,'8:61','(ಓ ಪೈಗಂಬರರೇ,) ಅವರು ಶಾಂತಿ ಸಂಧಾನ ಗಳ ಕಡೆಗೆ ವಾಲಿದರೆ ನೀವೂ ಅದಕ್ಕೆ ವಾಲಿರಿ ಮತ್ತು ಅಲ್ಲಾಹನ ಮೇಲೆ ಭರವಸೆಯನ್ನಿರಿಸಿರಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅವನೇ ಸರ್ವ ಶ್ರುತನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(8,62,'8:62','ಅವರು ನಿಮ್ಮನ್ನು ವಂಚಿಸಲು ಬಯಸಿದರೆ ನಿಮಗೆ ಅಲ್ಲಾಹು ಸಾಕು. ಅವನು ತನ್ನ ಸಹಾ ಯದಿಂದಲೂ ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಮೂಲಕವೂ ನಿಮಗೆ ಬಲ ನೀಡಿರುವನು.');
INSERT INTO "kannada" VALUES(8,63,'8:63','ಅವರ (ಸತ್ಯವಿಶ್ವಾಸಿಗಳ) ಹೃದಯಗಳನ್ನು ಅವನು ಪರಸ್ಪರ ಜೋಡಿಸಿದವನು. ನೀವು ಭೂಮಿಯಲ್ಲಿರುವ ಸಕಲ ಸಂಪತ್ತನ್ನು ಖರ್ಚು ಮಾಡಿದರೂ ಇವರ ಹೃದಯಗಳನ್ನು ಜೋಡಿಸ ಲಾಗುತ್ತಿರಲಿಲ್ಲ. ಆದರೆ ಅವರೆಡೆಯಲ್ಲಿ ಅಲ್ಲಾಹನು ಅನ್ಯೋನ್ಯತೆ ನೆಲೆಗೊಳಿಸಿದನು. ನಿಶ್ಚಯವಾಗಿಯೂ ಅವನು ಮಹಾಪ್ರತಾಪಿಯೂ ಪರಮ ತಂತ್ರಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(8,64,'8:64','ಓ ಪೈಗಂಬರರೇ, ನಿಮಗೆ ಅಲ್ಲಾಹು ಮತ್ತು ನಿಮ್ಮನ್ನು ಅನುಸರಿಸಿದ ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಸಾಕು .');
INSERT INTO "kannada" VALUES(8,65,'8:65','ಓ ಪೈಗಂಬರರೇ, ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನು ಯುದ್ದಕ್ಕೆ ಪ್ರೇರೇಪಿಸಿರಿ. ನಿಮ್ಮಲ್ಲಿ ಇಪ್ಪತ್ತು ಮಂದಿ ಸಹನ ಶೀಲರಿದ್ದರೆ ಅವರು ಇನ್ನೂರು ಮಂದಿಯ ವಿರುದ್ಧ ಜಯಗಳಿಸುವರು ಮತ್ತು ನಿಮ್ಮಲ್ಲಿ (ಇಂತಹ) ನೂರು ಮಂದಿ ಇದ್ದರೆ ಸತ್ಯನಿಷೇಧಿಗಳ ಸಾವಿರ ಮಂದಿಯನ್ನು ಜೈಸಬಲ್ಲರು. ಏಕೆಂದರೆ ಅವರು ತಿಳಿಗೇಡಿಗಳು.');
INSERT INTO "kannada" VALUES(8,66,'8:66','ಈಗ ಅಲ್ಲಾಹನು ನಿಮ್ಮ ಭಾರವನ್ನು ಹಗುರ ಗೊಳಿಸಿದನು. ನಿಮ್ಮ ದೌರ್ಬಲ್ಯವನ್ನೂ ಅರಿತನು. ಆದುದರಿಂದ ನಿಮ್ಮಲ್ಲಿ ನೂರು ಮಂದಿ ಸಹನಶೀಲರಿದ್ದರೆ ಅವರು ಇನ್ನೂರರ ಮೇಲೂ ಸಾವಿರ ಮಂದಿ ಇಂಥವರಿದ್ದರೆ ಎರಡು ಸಾವಿರದ ಮೇಲೂ ಅಲ್ಲಾಹನ ನೆರವಿನಿಂದ ವಿಜಯಿಗಳಾಗುವರು. ಅಲ್ಲಾಹು ಸಹನಶೀಲರ ಜೊತೆಗಿದ್ದಾನೆ .');
INSERT INTO "kannada" VALUES(8,67,'8:67','ಭೂಮಿಯಲ್ಲಿ ಪ್ರಾಬಲ್ಯ ಸಾಧಿಸುವವರೆಗೂ ತನ್ನ ಬಳಿ ಯುದ್ದ ಸೆರೆಯಾಳುಗಳಿರುವುದು ಓರ್ವ ಪ್ರವಾದಿಗೆ ಶೋಭಿಸುವುದಿಲ್ಲ. ನೀವು ಐಹಿಕ ಲಾಭಗಳನ್ನು ಬಯಸುತ್ತೀರಿ. ಅಲ್ಲಾಹನು ಪರಲೋಕವನ್ನು ಬಯಸುತ್ತಾನೆ. ಅಲ್ಲಾಹು ಮಹಾಪ್ರತಾಪಿಯೂ ಯುಕ್ತಿವಂತನೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(8,68,'8:68','ಅಲ್ಲಾಹನಿಂದ ಮೊದಲೇ ಲಿಖಿತಗೊಂಡಿಲ್ಲದಿರುತ್ತಿದ್ದರೆ, ನೀವು ಪಡೆದುದರ ಫಲವಾಗಿ ನಿಮಗೆ ಘೋರ ಶಿಕ್ಷೆ ಸಿಗುತ್ತಿತ್ತು .');
INSERT INTO "kannada" VALUES(8,69,'8:69','ಆದುದರಿಂದ ನೀವು ಗಳಿಸಿರುವ ಧರ್ಮಬದ್ಧವೂ ಶುದ್ಧವೂ ಆಗಿರುವ ಸೊತ್ತನ್ನು ಭುಜಿಸಿರಿ. ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹ್ ಕ್ಷಮಾಶೀಲನೂ ಕರುಣಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(8,70,'8:70','ಓ ಪೈಗಂಬರರೇ, ನಿಮ್ಮ ವಶದಲ್ಲಿರುವ ಸೆರೆಯಾಳುಗಳಿಗೆ ಹೇಳಿರಿ; ನಿಮ್ಮ ಹೃದಯಗಳಲ್ಲೇ ನಾದರೂ ಒಳಿತು ಇರುವುದೆಂದು ಅಲ್ಲಾಹು ತಿಳಿದರೆ ಅವನು ನಿಮ್ಮಿಂದ ಪಡೆದುದಕ್ಕಿಂತಲೂ ಉತ್ತಮವಾದುದನ್ನು ನಿಮಗೆ ನೀಡುವನು ಮತ್ತು ನಿಮ್ಮ ಅಪರಾಧಗಳನ್ನು ಕ್ಷಮಿಸುವನು. ಅಲ್ಲಾಹು ಕ್ಷಮಾಶೀಲನೂ ಕೃಪಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(8,71,'8:71','ಆದರೆ ನಿಮ್ಮನ್ನು ವಂಚಿಸುವ ಇರಾದೆ ಅವರಿಗಿದ್ದರೆ (ಚಿಂತೆ ಬೇಡ). ಅವರು ಇದಕ್ಕಿಂತ ಮುಂಚೆ ಅಲ್ಲಾಹನನ್ನು ವಂಚಿಸಿದ್ದಾರೆ. ಇದರ ಫಲವಾಗಿಯೇ ಅವರನ್ನು ನಿಮ್ಮ ವಶಕ್ಕೆ ಒಪ್ಪಿಸಿದನು. ಅಲ್ಲಾಹು ಸರ್ವಜ್ಞನೂ ಯುಕ್ತಿವಂತನೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(8,72,'8:72','ಸತ್ಯವಿಶ್ವಾಸ ಸ್ವೀಕರಿಸಿದವರೂ, ದೇಶತ್ಯಾಗ ಮಾಡಿದವರೂ ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ತಮ್ಮ ಸೊತ್ತು ಹಾಗೂ ದೇಹಗಳಿಂದ ಹೋರಾಡಿದವರೂ (ಹಿಜ್ರತ್ ಮಾಡಿದವರಿಗೆ) ಆಶ್ರಯ ಮತ್ತು ಸಹಾಯವನ್ನು ಕೊಟ್ಟವರೂ ವಾಸ್ತವದಲ್ಲಿ ಅವರು ಪರಸ್ಪರ ಬಂಧುಮಿತ್ರರು. ವಿಶ್ವಾಸವಿರಿಸಿದರೂ ದೇಶತ್ಯಾಗ ಮಾಡದೆ ಇದ್ದವರು ದೇಶ ತ್ಯಾಗ ಮಾಡಿ ಬರುವವರೆಗೂ ಅವರನ್ನು ರಕ್ಷಿಸುವ ಯಾ ವುದೇ ಹೊಣೆ ನಿಮಗಿಲ್ಲ. ಅವರು ಧರ್ಮ ಕಾರ್ಯದಲ್ಲಿ ನಿಮ್ಮಿಂದ ಸಹಾಯ ಕೋರಿದರೆ ಅವರಿಗೆ ಸಹಾಯ ಮಾಡಬೇಕಾದುದು ನಿಮ್ಮ ಹೊಣೆಯಾಗಿರುತ್ತದೆ. ಆದರೆ, ಇದು (ಸಹಾಯವು) ನಿಮ್ಮೊಂದಿಗೆ ಒಪ್ಪಂದ ಮಾಡಿಕೊಂಡವರ ವಿರುದ್ಧವಾಗಿರಬಾರದು. ನೀವು ಮಾಡುತ್ತಿರುವುದನ್ನು ಅಲ್ಲಾಹು ವೀಕ್ಷಿಸುತ್ತಾನೆ ');
INSERT INTO "kannada" VALUES(8,73,'8:73','ಸತ್ಯನಿಷೇಧಿಗಳು ಪರಸ್ಪರ ಬಂಧುಗಳು. ನೀವು ಹಾಗೆ ಮಾಡದಿದ್ದರೆ ಭೂಮಿಯಲ್ಲಿ ಕ್ಷೋಭೆಯೂ ಗಂಭೀರ ನಾಶವೂ ಉಂಟಾದೀತು .');
INSERT INTO "kannada" VALUES(8,74,'8:74','ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಅವಲಂಬಿಸಿದ, ದೇಶತ್ಯಾಗ ಮಾಡಿದ ಹಾಗೂ ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಧರ್ಮ ಯುದ್ಧ ಮಾಡಿದವರು ಮತ್ತು ಆಶ್ರಯವನ್ನೂ ಸಹಾಯವನ್ನೂ ನೀಡಿದವರು ಯಾರೋ ಅವರೇ ನಿಜವಾದ ಸತ್ಯವಿಶ್ವಾಸಿಗಳು. ಅವರಿಗೆ ಪಾಪ ಮುಕ್ತಿಯೂ ಗೌರವಪೂರ್ಣ ಆಹಾರವೂ ಇದೆ.');
INSERT INTO "kannada" VALUES(8,75,'8:75','ನಂತರ ಸತ್ಯವಿಶ್ವಾಸವಿಟ್ಟವರು, ದೇಶತ್ಯಾಗ ಮಾಡಿ ಬಂದವರು ಮತ್ತು ನಿಮ್ಮ ಜೊತೆಗೆ ಯುದ್ಧ ಮಾಡಿದವರು ನಿಮ್ಮೊಂದಿಗೆ ಸೇರಿದ್ದಾರೆ ಂ. ಆದರೆ ಅಲ್ಲಾಹನ ಗ್ರಂಥದಲ್ಲಿ ರಕ್ತ ಸಂಬಂಧಿಕರು (ವಾರೀಸು ಹಕ್ಕಿಗೆ) ಪರಸ್ಪರ ಹೆಚ್ಚು ಹಕ್ಕುದಾರರು. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹು ಸಕಲ ವಸ್ತುಗಳ ಬಗ್ಗೆ ಅಗಾಧ ಜ್ಞಾನಿಯಾಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(9,1,'9:1','(ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ) ನೀವು ಒಪ್ಪಂದ ಮಾಡಿ ಕೊಂಡಿದ್ದ ಬಹುದೇವ ವಿಶ್ವಾಸಿಗಳಿಗೆ ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ರಸೂಲರಿಂದ ಒಪ್ಪಂದ ರದ್ಧತಿಯ ಘೋಷಣೆಯಿದು .');
INSERT INTO "kannada" VALUES(9,2,'9:2','(ಓ ಬಹುದೇವ ವಿಶ್ವಾಸಿಗಳೇ) ಆದ್ದರಿಂದ ಇನ್ನು ನಾಲ್ಕು ತಿಂಗಳು ಭೂಮಿಯಲ್ಲಿ ಸಂಚರಿಸಿಕೊಳ್ಳಿರಿ. ಖಂಡಿತವಾಗಿಯೂ ನೀವು ಅಲ್ಲಾಹನನ್ನು ಪರಾಜಿತಗೊಳಿಸಲಾರಿರೆಂಬುದನ್ನೂ ಅಲ್ಲಾಹನು ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ಅವಮಾನಗೊಳಿಸಲಿರುವನೆಂಬುದನ್ನೂ ಚೆನ್ನಾಗಿ ತಿಳಿದುಕೊಳ್ಳಿರಿ .');
INSERT INTO "kannada" VALUES(9,3,'9:3','ಅಲ್ಲಾಹನೂ ಅವನ ಸಂದೇಶವಾಹಕರೂ ಬಹುದೇವ ವಿಶ್ವಾಸಿಗಳಿಂದ ಹೊಣೆ ಮುಕ್ತರಾಗಿರುವರೆಂದು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ರಸೂಲರ ಕಡೆಯಿಂದ ಎಲ್ಲಾ ಜನರಿಗಾಗಿ ಈ ಮಹಾ ಹಜ್ಜ್‍ನ ದಿನದಂದು ಮಾಡಲಾಗುತ್ತಿರುವ ಸಾರ್ವತ್ರಿಕ ಘೋಷಣೆಯಿದು. ಆದರೆ (ಬಹುದೇವ ವಿಶ್ವಾಸಿಗಳೇ) ನೀವು ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು ಮರಳುವುದಾದರೆ ಅದು ನಿಮಗೆ ಒಳಿತು. ಇನ್ನು ನೀವು ವಿಮುಖರಾಗುವುದಾದರೆ ಖಂಡಿತವಾಗಿಯೂ ನೀವು ಅಲ್ಲಾಹನನ್ನು ಪರಾಜಿತ ಗೊಳಿಸಲಾರಿರೆಂದು ಚೆನ್ನಾಗಿ ತಿಳಿದುಕೊಳ್ಳಿರಿ. (ಪ್ರವಾದಿಯವರೇ) ವೇದನಾಯುಕ್ತವಾದ ಶಿಕ್ಷೆಯ ಬಗ್ಗೆ ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ನೀವು `ಸಂತೋಷ ವಾರ್ತೆ’ ತಿಳಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(9,4,'9:4','ನಿಮ್ಮೊಂದಿಗೆ ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡು ನಂತರ ತಮ್ಮ ಒಪ್ಪಂದ ಪಾಲನೆಯಲ್ಲಿ ಯಾವುದೇ ಲೋಪ ವನ್ನು ಮಾಡದೆಯೂ ನಿಮ್ಮ ವಿರುದ್ಧ ಯಾರಿಗೂ ನೆರವಾಗದೆಯೂ ಇರುವ ಬಹುದೇವಾರಾಧಕರ ಹೊರತು. ಆದ್ದರಿಂದ ಅವರ ಒಪ್ಪಂದವನ್ನು ಅವಧಿಯವರೆಗೆ ಪಾಲಿಸಿರಿ. ನಿಜವಾಗಿಯೂ ಜಾಗ್ರತೆಯುಳ್ಳ ಧರ್ಮನಿಷ್ಠರನ್ನು ಅಲ್ಲಾಹನು ಮೆಚ್ಚುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(9,5,'9:5','ನಿಷಿದ್ಧ (ನಾಲ್ಕು) ಮಾಸಗಳು ಮುಗಿದರೆ (ಒಪ್ಪಂ ದವನ್ನು ಮುರಿದ) ಆ ಮುಶ್ರಿಕರನ್ನು ಕಂಡಲ್ಲಿ ವಧಿಸಿರಿ. ಅವರನ್ನು ಸೆರೆಹಿಡಿಯಿರಿ. ಅವರನ್ನು ಸುತ್ತುವರಿಯಿರಿ. ಎಲ್ಲ ಕಾವಲು ಸ್ಥಳಗಳಲ್ಲಿ ಅವರಿಗೆ ಹೊಂಚು ಹಾಕಿ ಕುಳಿತುಕೊಳ್ಳಿರಿ . ಇನ್ನು ಅವರು ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು ಮರಳಿದರೆ, ನಮಾಜನ್ನು ನೆಲೆಗೊಳಿಸಿದರೆ, ಝಕಾತ್ ನೀಡಿದರೆ ಅವರನ್ನು ಅವರ ಪಾಡಿಗೆ ಬಿಟ್ಟು ಬಿಡಿರಿ. ಅಲ್ಲಾಹನು ಪರಮ ಕ್ಷಮಾಶೀಲನೂ ಪರಮ ದಯಾಳುವೂ ಆಗಿರುವನು .');
INSERT INTO "kannada" VALUES(9,6,'9:6','ಮುಶ್ರಿಕರ ಪೈಕಿ ಯಾರಾದರೂ ನಿಮ್ಮೊಡನೆ ಅಭಯ ಕೇಳಿದರೆ ಅವನಿಗೆ ಅಭಯ ಕೊಡಿರಿ. ಅಲ್ಲಾಹನ ವಚನವನ್ನು ಅವನು ಕೇಳಿಸಿಕೊಳ್ಳುವ ಸಲುವಾಗಿ. ನಂತರ ಅವನನ್ನು ಅವನ ಅಭಯ ಸ್ಥಾನಕ್ಕೆ ತಲುಪಿಸಿರಿ. ಅವರು ತಿಳುವಳಿಕೆ ಯಿಲ್ಲದವರಾದುದರಿಂದ ಹೀಗೆ ಮಾಡಬೇಕಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(9,7,'9:7','ಮಸ್ಜಿದ್ ಹರಾಮ್‍ನ ಬಳಿ ನಿಮ್ಮೊಂದಿಗೆ ಒಪ್ಪಂದ ಮಾಡಿಕೊಂಡವರ ಹೊರತು ಇತರ ಮುಶ್ರಿಕರಿಗೆ ಅಲ್ಲಾಹುವಿನ ಬಳಿ ಹಾಗೂ ಅವನ ರಸೂಲರ ಬಳಿ ಒಡಂಬಡಿಕೆ ನೆಲೆಗೊಳ್ಳುವುದು ಹೇಗೆ ? ಆದ್ದರಿಂದ ಅವರು ನಿಮ್ಮೊಂದಿಗೆ ನ್ಯಾಯ ಪಾಲಿಸಿದರೆ ನೀವೂ ಅವರೊಂದಿಗೆ ನ್ಯಾಯ ಪಾಲಿಸಿರಿ . ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಜಾಗ್ರತೆಯುಳ್ಳವರನ್ನು ಪ್ರೀತಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(9,8,'9:8','ಅವರೊಂದಿಗೆ ಒಪ್ಪಂದವಿರುವುದಾದರೂ ಹೇಗೆ? ಅವರು ನಿಮ್ಮ ವಿರುದ್ಧ ಜಯ ಗಳಿಸಿದರೆ ನಿಮ್ಮೊಂದಿಗಿರುವ ರಕ್ತ ಸಂಬಂಧವನ್ನಾಗಲೀ, ಒಡಂಬಡಿಕೆಯನ್ನಾಗಲಿ ಅವರು ಪರಿಗಣಿಸುವವರಲ್ಲ! ಅವರು ಬರೇ ಬಾಯಿ ಮಾತುಗಳಿಂದ ನಿಮ್ಮನ್ನು ಮೆಚ್ಚಿಸಲು ಯತ್ನಿಸುತ್ತಾರೆ. ಅವರ ಮನಸ್ಸುಗಳು ಅದನ್ನು ನಿಷೇಧಿಸುತ್ತವೆ. ಅವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಕರ್ಮಭ್ರಷ್ಟರಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(9,9,'9:9','ಅವರು ಅಲ್ಲಾಹನ ಆಯತ್‍ಗಳ ಬದಲಿಗೆ ತುಚ್ಚ ಬೆಲೆಯನ್ನು ಪಡಕೊಂಡರು. ಅನಂತರ ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ ಜನರನ್ನು ತಡೆದರು. ಅವರು ಮಾಡು ತ್ತಿರುವುದು ಅತ್ಯಂತ ನಿಕೃಷ್ಟ ಕಾರ್ಯವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(9,10,'9:10','ಒಬ್ಬ ಸತ್ಯವಿಶ್ವಾಸಿಯ ವಿಷಯದಲ್ಲಿ ಅವರು ಬಾಂಧವ್ಯವನ್ನಾಗಲಿ ಒಪ್ಪಂದದ ಹೊಣೆಯನ್ನಾ ಗಲಿ ಪರಿಗಣಿಸುವುದಿಲ್ಲ. ಅವರೇ ಅತಿಕ್ರಮಿಗಳು.');
INSERT INTO "kannada" VALUES(9,11,'9:11','ಆದರೂ ಅವರು ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು ನಮಾಜು ನೆಲೆಗೊಳಿಸಿದರೆ, ಝಕಾತ್ ನೀಡಿದರೆ ಅವರು ಧರ್ಮದಲ್ಲಿ ನಿಮ್ಮ ಸೋದರರು. ಗ್ರಹಿಸುವ ಜನರಿಗೆ ನಾವು ನಮ್ಮ ಪುರಾವೆಗಳನ್ನು ವಿವರಿಸುತ್ತಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(9,12,'9:12','ಒಡಂಬಡಿಕೆ ಮಾಡಿಕೊಂಡ ಬಳಿಕ ಅವರು ಪುನಃ ತಮ್ಮ ಶಪಥಗಳನ್ನು ಮುರಿದು ನಿಮ್ಮ ಧರ್ಮವನ್ನು ಆಕ್ಷೇಪಿಸಿದರೆ ಆ ಸತ್ಯನಿಷೇಧಿಗಳ ಮುಂದಾಳುಗಳೊಂದಿಗೆ ಯುದ್ಧ ಮಾಡಿರಿ. ಏಕೆಂದರೆ, ನಿಜವಾಗಿಯೂ ಅವರಿಗೆ ಯಾವುದೇ ಒಪ್ಪಂದ ಅಸ್ತಿತ್ವದಲ್ಲಿಲ್ಲ. ಅವರು ಸತ್ಯನಿಷೇಧವನ್ನು ವರ್ಜಿಸಲೂಬಹುದು .');
INSERT INTO "kannada" VALUES(9,13,'9:13','ತಮ್ಮ ಒಪ್ಪಂದವನ್ನು ಉಲ್ಲಂಘಿಸಿದ ಹಾಗೂ ರಸೂಲರನ್ನು ನಾಡಿನಿಂದ ಹೊರ ಹಾಕಲು ಉದ್ದೇಶ ವಿಟ್ಟ ಜನತೆಯ ವಿರುದ್ಧ ನೀವು ಯುದ್ಧ ಮಾಡುವುದಿಲ್ಲವೇ? ಆರಂಭದಲ್ಲಿ ಯುದ್ಧವನ್ನು ಆರಂಭಿಸಿದವರು ಅವರೇ. ನೀವು ಅವರಿಗೆ ಹೆದರುತ್ತೀರಾ? ನೀವು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಾಗಿದ್ದರೆ ಭಯ ಪಡಲು ಹೆಚ್ಚು ಹಕ್ಕುದಾರನು ಅಲ್ಲಾಹನಾ ಗಿರುವನು.');
INSERT INTO "kannada" VALUES(9,14,'9:14','ನೀವು ಅವರ ವಿರುದ್ಧ ಯುದ್ಧ ಮಾಡಿರಿ. ನಿಮ್ಮ ಕೈಯ್ಯಾರೆ ಅಲ್ಲಾಹು ಅವರಿಗೆ ಶಿಕ್ಷೆ ಕೊಡಿಸುವನು, ಅವರನ್ನು ನಿಂದ್ಯಗೊಳಿಸುವನು. ಅವರ ವಿರುದ್ಧ ನಿಮಗೆ ನೆರವು ನೀಡುವನು. ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳಾದ ಜನತೆಯ ಹೃದಯಗಳನ್ನು ತಣಿಸುವನು.');
INSERT INTO "kannada" VALUES(9,15,'9:15','ಅವರ ಹೃದಯಗಳ ಬೇಗುದಿಯನ್ನು ಹೋಗಲಾಡಿಸುವನು ಮತ್ತು ತಾನಿಚ್ಛಿಸಿದವರ ಪಶ್ಚಾತ್ತಾಪವನ್ನು ಅಲ್ಲಾಹು ಸ್ವೀಕರಿಸುವನು. ಅಲ್ಲಾಹನು ಸರ್ವಜ್ಞನೂ ಪರಮ ಯುಕ್ತಿವಂತನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(9,16,'9:16','ವಸ್ತುತಃ ನಿಮ್ಮ ಪೈಕಿ ಅತಿಯಾಗಿ ಪರಿಶ್ರಮಿಸಿದ ವರು ಯಾರೆಂದೂ ಅಲ್ಲಾಹನನ್ನೂ ಸಂದೇಶವಾಹ ಕರನ್ನೂ ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನೂ ಹೊರತು ಅನ್ಯರೊಂದಿಗೆ ಒಳ ಸಂಬಂಧ ಮಾಡಿಕೊಳ್ಳದವರಾರೆಂದೂ ಅಲ್ಲಾಹು ಕಂಡರಿಯದೆ ನಿಮ್ಮನ್ನು ಸುಮ್ಮನೆ ಉಪೇಕ್ಷಿಸುವನೆಂದು ನೀವು ಭಾವಿಸಿ ಕೊಂಡಿದ್ದೀರಾ? ನಿಮ್ಮ ಪ್ರವೃತ್ತಿಗಳ ಬಗ್ಗೆ ಅಲ್ಲಾಹನು ಚೆನ್ನಾಗಿ ಬಲ್ಲವನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(9,17,'9:17','ಸತ್ಯನಿಷೇಧಕ್ಕೆ ಸ್ವಯಂ ಸಾಕ್ಷ್ಯವಹಿಸಿರುವ ಬಹುದೇವವಿಶ್ವಾಸಿಗಳಿಗೆ ಅಲ್ಲಾಹುವಿನ ಮಸೀದಿಗಳ ಪರಿಪಾಲನೆಗೆ ಅವಕಾಶ ಇರುವುದಿಲ್ಲ. ಅವರ ಸತ್ಕರ್ಮಗಳೆಲ್ಲ ವ್ಯರ್ಥವಾದುವು. ಅವರು ನರಕದಲ್ಲಿ ಶಾಶ್ವತರು.');
INSERT INTO "kannada" VALUES(9,18,'9:18','ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹು ಮತ್ತು ಪರಲೋಕದಲ್ಲಿ ವಿಶ್ವಾಸವಿರುವವರು ಹಾಗೂ ನಮಾಝ್ ಸ್ಥಿರಗೊಳಿಸುವವರು, ಝಕಾತ್ ಕೊಡುವವರು ಹಾಗೂ ಅಲ್ಲಾಹನ ಹೊರತು ಇನ್ನಾರನ್ನೂ ಭಯಪಡದವರು ಮಾತ್ರವೇ ಅಲ್ಲಾಹನ ಮಸ್ಜಿದ್‍ಗಳನ್ನು ಪರಿಪಾಲಿಸಬಲ್ಲರು. ಅಂತಹವರೇ ಸತ್ಪಥ ಪ್ರಾಪ್ತಿಗೆ ಸಾಧ್ಯತೆಯುಳ್ಳವರು .');
INSERT INTO "kannada" VALUES(9,19,'9:19','ಹಜ್ಜ್ ಯಾತ್ರಿಕರಿಗೆ ಜಲಪಾನ ಮಾಡಿಸುವುದನ್ನು ಮತ್ತು ಮಸ್ಜಿದುಲ್ ಹರಾಮ್‍ನ ಸೇವೆಯನ್ನು ನೀವು ಅಲ್ಲಾಹು ಮತ್ತು ಪರಲೋಕದಲ್ಲಿ ವಿಶ್ವಾಸವಿಟ್ಟು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಹೋರಾಟ ನಡೆಸಿದವನ ಕರ್ಮಕ್ಕೆ ಸರಿಸಮಾನವೆಂದು ಪರಿಗಣಿಸಿದಿರಾ? ಅವರು ಅಲ್ಲಾಹುವಿನ ಬಳಿ ಸಮಾನರಾಗುವುದಿಲ್ಲ. ಅಧರ್ಮಿಗಳಾದ ಜನರನ್ನು ಅಲ್ಲಾಹನು ಸನ್ಮಾರ್ಗಕ್ಕೆ ತರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(9,20,'9:20','ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಕೈಕೊಂಡ, ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಮನೆ ಮಾರುಗಳನ್ನು ಬಿಟ್ಟ ಮತ್ತು ತನುಧನಗಳಿಂದ ಧರ್ಮಯುದ್ಧ ಮಾಡಿದವರು ಅಲ್ಲಾಹುವಿನ ಬಳಿ ಅತಿ ಮಹತ್ವವಾದ ಪದವಿಯು ಳ್ಳವರಾಗಿರುವರು. ಅವರೇ ವಿಜಯಿಗಳು .');
INSERT INTO "kannada" VALUES(9,21,'9:21','ಅವರ ಪ್ರಭು ಅವರಿಗೆ ತನ್ನ ಕೃಪೆ, ಸಂಪ್ರೀತಿ ಮತ್ತು ಸ್ವರ್ಗಗಳ ಸುವಾರ್ತೆ ನೀಡುತ್ತಾನೆ. ಆ ಸ್ವರ್ಗದಲ್ಲಿ ಅವರಿಗೆ ಚಿರ ಸೌಭಾಗ್ಯವಿದೆ.');
INSERT INTO "kannada" VALUES(9,22,'9:22','ಅದರಲ್ಲಿ ಅವರು ಎಂದೆಂದಿಗೂ ಚಿರವಾಸಿಗಳು. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹುವಿನ ಬಳಿ ಮಹತ್ತರ ವಾದ ಪ್ರತಿಫಲವಿದೆ.');
INSERT INTO "kannada" VALUES(9,23,'9:23','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನಿಮ್ಮ ತಾಯಿ ತಂದೆ ಯರು ಹಾಗೂ ನಿಮ್ಮ ಸಹೋದರರು ಸತ್ಯ ವಿಶ್ವಾಸದ ಬದಲಿಗೆ ಸತ್ಯನಿಷೇಧವನ್ನು ಇಷ್ಟ ಪಟ್ಟರೆ ಅವರನ್ನು ನೀವು ಬಂಧುಗಳೆಂದು ಪರಿಗಣಿಸಬೇಡಿರಿ. ನಿಮ್ಮ ಪೈಕಿ ಯಾರಾದರೂ ಅವರನ್ನು ಬಂಧುಗಳನ್ನಾಗಿ ಮಾಡಿಕೊಂಡರೆ ಅವರು ಖಂಡಿತ ಅಕ್ರಮಿಗಳು.');
INSERT INTO "kannada" VALUES(9,24,'9:24','(ಓ ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ - ನಿಮ್ಮ ತಾಯಿ ತಂದೆಯರು, ನಿಮ್ಮ ಮಕ್ಕಳು, ನಿಮ್ಮ ಸಹೋ ದರರು, ನಿಮ್ಮ ಸಂಗಾತಿಗಳು, ನಿಮ್ಮ ಇತರ ಬಂಧುಗಳು, ನೀವು ಸಂಪಾದಿಸಿದ ಸೊತ್ತುಗಳು ಬಿಕರಿಯಾಗದೆ ಉಳಿಯುವುದೆಂದು ನೀವು ಹೆದರುತ್ತಿರುವ ನಿಮ್ಮ ವ್ಯಾಪಾರ ಸರಕುಗಳು, ನೀವು ತೃಪ್ತಿಪಡುತ್ತಿರುವ ಮನೆಗಳು ನಿಮ್ಮ ಪಾಲಿಗೆ ಅಲ್ಲಾಹನಿಗಿಂತ, ಅವನ ರಸೂಲ್ ಹಾಗೂ ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಧರ್ಮಯುದ್ಧಕ್ಕಿಂತ ನಿಮಗೆ ಹೆಚ್ಚು ಪ್ರಿಯವಾಗಿದ್ದಲ್ಲಿ ಅಲ್ಲಾಹನು ತನ್ನ ನಿರ್ಣಯವನ್ನು ಜಾರಿಗೆ ತರುವವರೆಗೂ ನೀವು ಕಾದುಕೊಳ್ಳಿರಿ. ಅಧರ್ಮಿಗಳಾದ ಜನತೆಯನ್ನು ಅಲ್ಲಾಹನು ಸನ್ಮಾರ್ಗಕ್ಕೆ ಸೇರಿಸುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(9,25,'9:25','ಅಲ್ಲಾಹನು ನಿಮಗೆ ಅನೇಕ ಕಡೆಗಳಲ್ಲಿ ಖಂಡಿತ ನೆರವಾಗಿರುತ್ತಾನೆ. ಹುನೈನ್ ಯುದ್ಧದ ದಿನ ಅಂ ದರೆ ನಿಮ್ಮ ಸಂಖ್ಯಾಬಲವು ನಿಮ್ಮನ್ನು ಹೆಮ್ಮೆ ಗೀಡು ಮಾಡಿದ ಸಂದರ್ಭ(ವನ್ನು ಜ್ಞಾಪಿಸಿ ಕೊಳ್ಳಿ). ಆದರೆ ಆ ಸಂಖ್ಯಾಬಲವು ನಿಮಗೆ ಯಾವ ಪ್ರಯೋಜನಕ್ಕೂ ಬರಲಿಲ್ಲ. ಭೂಮಿ ವಿಶಾ ಲವಾಗಿದ್ದು ಕೂಡ ನಿಮಗದು ಇಕ್ಕಟ್ಟಾಗಿ ಅನು ಭವಕ್ಕೆ ಬಂದಿತು. ತರುವಾಯ ನೀವು ಹಿಂಜರಿದು ಪಲಾಯನ ಮಾಡಿದಿರಿ .');
INSERT INTO "kannada" VALUES(9,26,'9:26','ಅನಂತರ ಅಲ್ಲಾಹನು ತನ್ನ ರಸೂಲ್ ಹಾಗೂ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಶಾಂತಿಯನ್ನು ಇಳಿಸಿದನು. ನೀವು ಕಂಡಿರದ ಒಂದು ಸೈನ್ಯವನ್ನು ಇಳಿಸಿದನು. ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ಶಿಕ್ಷಿಸಿದನು . ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸುವವರಿಗೆ ಇದೇ ಪ್ರತಿಫಲ.');
INSERT INTO "kannada" VALUES(9,27,'9:27','ತರುವಾಯ ಅಲ್ಲಾಹನು ತಾನಿಚ್ಛಿಸಿದವರ ಪಶ್ಚಾತ್ತಾಪವನ್ನು ಸ್ವೀಕರಿಸುವನು. ಅಲ್ಲಾಹನು ಬಹಳ ಹೆಚ್ಚು ಕ್ಷಮಾದಾನಿಯೂ ಪರಮದಯಾಳುವೂ ಆಗಿರುವನು .');
INSERT INTO "kannada" VALUES(9,28,'9:28','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ಮುಶ್ರಿಕರು ನಿಜವಾಗಿಯೂ ಅಶುದ್ಧರು . ಆದ್ದರಿಂದ ಈ ವರ್ಷದ ಬಳಿಕ ಅವರು ಮಸ್ಜಿದುಲ್ ಹರಾಮಿನತ್ತ ಸುಳಿಯಬಾರದು . ನಿಮಗೆ ದಾರಿದ್ರ್ಯದ ಭಯವಿದ್ದರೆ ಅಲ್ಲಾಹ ನಿಚ್ಛಿಸಿದರೆ ಮುಂದೆ ಅಲ್ಲಾಹನು ತನ್ನ ಔದಾರ್ಯದಿಂದ ನಿಮ್ಮನ್ನು ಅನುಕೂಲಸ್ಥರನ್ನಾಗಿ ಮಾಡುವನು. ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಸರ್ವಜ್ಞನೂ ಪರಮ ತಂತ್ರಜ್ಞನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(9,29,'9:29','ವೇದ ನೀಡಲ್ಪಟ್ಟವರ ಪೈಕಿ ಅಲ್ಲಾಹು ಹಾಗೂ ಪರಲೋಕದಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸದ, ಅಲ್ಲಾಹು ಹಾಗೂ ಅವನ ರಸೂಲರು ನಿಷೇಧಿಸಿದ್ದನ್ನು ನಿಷಿದ್ಧಗೊಳಿಸದ ಮತ್ತು ಸತ್ಯಧರ್ಮವನ್ನು ತಮ್ಮ ಧರ್ಮವನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳದ ಜನರು ಯಾರೋ ಅವರು ಇಸ್ಲಾಮೀ ಆಡಳಿತಕ್ಕೆ ಬದ್ಧರಾಗಿ ತಮ್ಮ ಕೈಯಿಂದಲೇ ತಲೆಗಂದಾಯ ಕೊಡುವವರೆಗೂ ಯುದ್ಧ ಮಾಡಿರಿ.');
INSERT INTO "kannada" VALUES(9,30,'9:30','ಉಝೈರ್ ಅಲ್ಲಾಹನ ಪುತ್ರ ಎಂದು ಯಹೂದಿ ಗಳು ಹೇಳಿದರು. ಅಲ್-ಮಸೀಹ್ (ಯೇಸು) ಅಲ್ಲಾಹುವಿನ ಪುತ್ರ ಎಂದು ಕ್ರೈಸ್ತರೂ ಹೇಳಿದರು. ಹಿಂದಿನ ಸತ್ಯನಿಷೇಧಿಗಳ ಮಾತನ್ನು ಅನುಕರಿಸಿ ತಮ್ಮ ಬಾಯಿಗಳಿಂದ ಹೇಳುತ್ತಿರುವ ಮಾತುಗಳಿವು . ಅಲ್ಲಾಹು ಅವರನ್ನು ಶಪಿಸಿರುತ್ತಾನೆ. (ಸತ್ಯವನ್ನು ಬಿಟ್ಟು) ಅವರು ಹೇಗೆ ತಿರುಗಿಸಲ್ಪಡುತ್ತಿದ್ದಾರೆ ?');
INSERT INTO "kannada" VALUES(9,31,'9:31','ಅವರು ಅಲ್ಲಾಹನನ್ನು ಹೊರತು ತಮ್ಮ ವಿದ್ವಾಂಸರನ್ನೂ ಸನ್ಯಾಸಿಗಳನ್ನೂ ಹಾಗೂ ಮರ್ಯಮಳ ಪುತ್ರ ಅಲ್-ಮಸೀಹರನ್ನೂ ಪ್ರಭುಗಳನ್ನಾಗಿ ಮಾಡಿಕೊಂಡಿದ್ದಾರೆ . ಒಬ್ಬನೇ ಒಬ್ಬ ದೇವನಿಗೆ ಆರಾಧಿಸಲು ಮಾತ್ರವೇ ಅವರ ಮೇಲೆ ಆದೇಶಿಸಲಾಗಿತ್ತು. ಅವನಲ್ಲದೆ ಬೇರೆ ಆರಾಧ್ಯನಿಲ್ಲ. ಅವರು ಮಾಡುತ್ತಿರುವ ದೇವ ಸಹಭಾಗಿತ್ವದಿಂದ ಅಲ್ಲಾಹು ಪರಮ ಪರಿಶುದ್ಧ ನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(9,32,'9:32','ಅವರು ಅಲ್ಲಾಹನ ಪ್ರಕಾಶವನ್ನು ಬಾಯಲ್ಲಿ ಊದಿ ನಂದಿಸಲು ಇಚ್ಛಿಸುತ್ತಾರೆ. ಆದರೆ ಅಲ್ಲಾ ಹನು ತನ್ನ ಪ್ರಕಾಶವನ್ನು ಪರಿಪೂರ್ಣಗೊಳಿಸದೆ ಬಿಡುವವನಲ್ಲ. ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಅಸಹನೀಯವಾದರೂ ಸರಿ .');
INSERT INTO "kannada" VALUES(9,33,'9:33','ಸನ್ಮಾರ್ಗ ಹಾಗೂ ಸತ್ಯಧರ್ಮದೊಂದಿಗೆ ತನ್ನ ದೂತನನ್ನು ಕಳುಹಿಸಿದ್ದು ಅವನೇ ಆಗಿರುವನು. ಅದನ್ನು ಸಕಲ ಧರ್ಮಗಳ ಮೇಲೆ ಉತ್ಥಾನ ಗೊಳಿಸಲಿಕ್ಕಾಗಿ. ಬಹುದೇವಾರಾಧಕರು ಅನಿಷ್ಟಪಟ್ಟರೂ ಸರಿ.');
INSERT INTO "kannada" VALUES(9,34,'9:34','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಗ್ರಂಥದವರ ಅಧಿಕಾಂಶ ವಿದ್ವಾಂಸರೂ ಪುರೋಹಿತರೂ ಜನರ ಸಂಪತ್ತನ್ನು ಅನುಚಿತ ವಿಧಾನಗಳಿಂದ ಕಬಳಿಸುತ್ತಾರೆ ಮತ್ತು ಅವರನ್ನು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ ತಡೆಯುತ್ತಾರೆ. ಚಿನ್ನವನ್ನೂ ಬೆಳ್ಳಿಯನ್ನೂ ನಿಧಿಯಾಗಿ ಸಂಗ್ರಹಿಸಿಟ್ಟು ಅವುಗಳನ್ನು ದೇವಮಾರ್ಗದಲ್ಲಿ ಖರ್ಚು ಮಾಡದವರಿಗೆ ಯಾತನಾಮಯ ಶಿಕ್ಷೆಯ ಸುವಾರ್ತೆ ಕೊಡಿರಿ.');
INSERT INTO "kannada" VALUES(9,35,'9:35','ಅಂದು ಅದನ್ನು ನರಕದಲ್ಲಿ ಕಾಯಿಸಿ ಅವರ ಹಣೆಗಳು ಹಾಗೂ ಪಾಶ್ರ್ವಗಳಿಗೆ ಮತ್ತು ಅವರ ಬೆನ್ನುಗಳಿಗೆ ಬರೆಹಾಕಲಾಗುವುದು. ‘ನೀವು ನಿಮಗಾಗಿ ಕೂಡಿಟ್ಟ ಸಂಪತ್ತು ಇದುವೇ, ನೀವು ಕೂಡಿಹಾಕಿದ್ದ ಸಂಪತ್ತನ್ನು ಈಗ ಸವಿಯಿರಿ'' ಎನ್ನಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(9,36,'9:36','ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹನ ಬಳಿ ಮಾಸಗಳ ಲೆಕ್ಕವು ಭೂಮ್ಯಾಕಾಶಗಳನ್ನು ಸೃಷ್ಟಿಸಿದ ದಿನ ತಾನು ವಿಧಿಸಿದ ಕ್ರಮದಲ್ಲಿ ಹನ್ನೆರಡಾಗಿರುತ್ತದೆ. ಅವುಗಳ ಪೈಕಿ ನಾಲ್ಕು ತಿಂಗಳುಗಳು ನಿಷಿದ್ಧವಾಗಿವೆ. ಇದೇ ಸರಿಯಾದ ನಿಯಮ . ಆದುದರಿಂದ ಈ ನಾಲ್ಕು ತಿಂಗಳುಗಳಲ್ಲಿ ನಿಮ್ಮ ಮೇಲೆ ದ್ರೋಹ ಮಾಡಿಕೊಳ್ಳಬೇಡಿರಿ. ಬಹುದೈವ ವಿಶ್ವಾಸಿಗಳು ಎಲ್ಲರೂ ನಿಮ್ಮ ವಿರುದ್ಧ ಯುದ್ಧ ಮಾಡುವಂತೆ ನೀವು ಎಲ್ಲರೂ ಅವರ ವಿರುದ್ಧ ಯುದ್ಧ ಮಾಡಿರಿ . ಅಲ್ಲಾಹನು ಜಾಗ್ರತೆ ಪಾಲಿ ಸುವವರ ಜೊತೆಗಿರುವನು ಅನ್ನುವುದು ನಿಮಗೆ ತಿಳಿದಿರಲಿ.');
INSERT INTO "kannada" VALUES(9,37,'9:37','ನಿಜವಾಗಿಯೂ ‘ನಸೀಅï’ (ಅಂದರೆ ಒಂದು ತಿಂಗಳಿನ ಪವಿತ್ರತೆಯನ್ನು ಇನ್ನೊಂದು ತಿಂಗ ಳಿಗೆ ಬದಲಾಯಿಸುವುದು) ಸತ್ಯನಿಷೇಧದಲ್ಲಿ ವರ್ಧನೆಯಾಗಿರುತ್ತದೆ. ಆ ಸತ್ಯನಿಷೇಧಿಗಳು ಆ ಮೂಲಕ ದಾರಿಗೇಡಿಗೀಡಾಗುತ್ತಾರೆ. ಅಲ್ಲಾಹನು ನಿಷಿದ್ಧಗೊಳಿಸಿದ ಮಾಸಗಳ ಎಣಿಕೆಯನ್ನು ಹೊಂದಿಸಿಕೊಳ್ಳುವ ಸಲುವಾಗಿಯೂ ಹಾಗೂ ಅಲ್ಲಾಹನು ಹರಾಮ್‍ಗೊಳಿಸಿದುದನ್ನು ಹಲಾಲನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳಲಿಕ್ಕಾಗಿಯೂ ಒಂದು ವರ್ಷ, ಒಂದು ತಿಂಗಳನ್ನು ಹಲಾಲ್ (ಧರ್ಮ ಬದ್ಧ) ಗೊಳಿಸಿಕೊಳ್ಳುತ್ತಾರೆ ಮತ್ತು ಇನ್ನೊಂದು ವರ್ಷ ಅದನ್ನು ಹರಾಮ್‍ಗೊಳಿಸಿಕೊಳ್ಳುತ್ತಾರೆ . ಅವರ ದುಷ್ಕರ್ಮಗಳನ್ನು ಅವರಿಗೆ ಮನೋಹರ ಗೊಳಿಸಲಾಗಿದೆ. ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ಅಲ್ಲಾ ಹನು ಸತ್ಪಥಕ್ಕೆ ಸೇರಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(9,38,'9:38','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನಿಮಗೆ ಏನಾಗಿದೆ? ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಹೊರಡಲು ನಿಮ್ಮೊಡನೆ ಹೇಳಿದಾಗ ನೀವು ಭೂಮಿಗೆ ಅಮರಿಕೊಂಡ ದ್ದೇಕೆ? ನೀವು ಪರಲೋಕಕ್ಕೆ ಪ್ರತಿಯಾಗಿ ಐಹಿಕ ಜೀವನವನ್ನು ಮೆಚ್ಚಿಕೊಂಡಿರಾ? ಆದರೆ ಪರಲೋಕಕ್ಕೆ ಹೋಲಿಸಿದರೆ ಇಹದ ಜೀವನ ಸುಖ ಬಹಳ ತುಚ್ಛವಾದುದು.');
INSERT INTO "kannada" VALUES(9,39,'9:39','ನೀವು ಹೊರಡದಿದ್ದರೆ ಅಲ್ಲಾಹನು ನಿಮಗೆ ವೇದನಾತ್ಮಕ ಶಿಕ್ಷೆ ಕೊಡುವನು ಮತ್ತು ನಿಮ್ಮ ಬದಲಿಗೆ ತಾನೊಂದು ಜನಾಂಗವನ್ನು ಸ್ಥಾಪಿಸುವನು . ಅವನಿಗೆ ಯಾವುದೇ ದೋಷ ವನ್ನು ನೀವು ತರಲಾರಿರಿ. ಅಲ್ಲಾಹನು ಸರ್ವ ಸಮರ್ಥನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(9,40,'9:40','ನೀವು ಪ್ರವಾದಿಯವರಿಗೆ ಸಹಾಯ ಮಾಡದಿದ್ದರೆ ಚಿಂತಿಲ್ಲ. (ಅಲ್ಲಾಹನು ನೆರವಾಗುವನು). ಸತ್ಯ ನಿಷೇಧಿಗಳು ಅವರನ್ನು ಹೊರ ಹಾಕಿದಾಗ, ಅವರು ಇಬ್ಬರ ಪೈಕಿ ಎರಡನೆಯವರಾಗಿದ್ದಾಗ, ಅವರಿಬ್ಬರೂ ಗುಹೆಯೊಳಗಿದ್ದಾಗ, ಅವರು ತಮ್ಮ ಸಂಗಡಿಗನೊಂದಿಗೆ, `ವ್ಯಥೆಪಡಬೇಡಿ. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನು ನಮ್ಮೊಡ ನಿದ್ದಾನೆ’ ಎಂದು ಹೇಳಿದ್ದಾಗ ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹನು ನೆರವಾಗಿದ್ದಾನೆ . ಹಾಗೆ ಅಲ್ಲಾಹನು ತನ್ನ ಕಡೆಯಿಂದ ಅವರಿಗೆ ಶಾಂತಿಯನ್ನು ಇಳಿಸಿದನು . ನೀವು ಕಂಡಿರದ ಸೇನೆಗಳಿಂದ ಅವರಿಗೆ ಬಲ ಕೊಟ್ಟನು . ಸತ್ಯನಿಷೇಧಿಗಳ ವಚನವನ್ನು ಕೆಳಮಟ್ಟಕ್ಕಿಳಿಸಿದನು. ಅಲ್ಲಾಹನ ವಚನವೇ ಉನ್ನತವಾದುದು . ಅಲ್ಲಾಹನು ಪ್ರತಾಪಶಾಲಿಯೂ ಮಹಾತಂತ್ರಜ್ಞನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(9,41,'9:41','ಹಗುರವಾದ ಸ್ಥಿತಿಯಲ್ಲಾಗಲಿ ಭಾರವಾದ ಸ್ಥಿತಿಯಲ್ಲಾಗಲಿ ಹೊರಡಿರಿ . ನಿಮ್ಮ ಸೊತ್ತುಗಳು ಹಾಗೂ ದೇಹಗಳಿಂದ ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲಿ ಯುದ್ಧ ಮಾಡಿರಿ. ಅದು ನಿಮಗೆ ಶ್ರೇಷ್ಟ. ನೀವು ಅರಿವುಳ್ಳವರಾಗಿದ್ದರೆ.');
INSERT INTO "kannada" VALUES(9,42,'9:42','(ಓ ಪೈಗಂಬರರೇ,) ಲಾಭವು ಸುಲಭವಾಗಿ ಸಿಗುತ್ತಿದ್ದರೆ ಮತ್ತು ಪ್ರಯಾಣವು ಸುಗಮವಾಗಿರುತ್ತಿ ದ್ದರೆ ಅವರು ನಿಶ್ಚಯವಾಗಿಯೂ ನಿಮ್ಮನ್ನು ಅನು ಸರಿಸುತ್ತಿದ್ದರು. ಆದರೆ ಮಾರ್ಗವು ಅವರಿಗೆ ದೂರ ವಾಗಿಬಿಟ್ಟಿತು. `ಸಾಧ್ಯವಿರುತ್ತಿದ್ದರೆ ನಾವು ನಿಮ್ಮ ಜೊತೆ ಹೊರಟು ಬರುತ್ತಿದ್ದೆವು’ ಎಂದವರು ಅಲ್ಲಾಹ ನಾಣೆ ಹಾಕುತ್ತ ಹೇಳಲಿರುವರು . ಅವರು ತಮ್ಮ ನ್ನು ತಾವೇ ವಿನಾಶಕ್ಕೊಡ್ಡುತ್ತಿರುವರು. ಅವರು ಸುಳ್ಳುಗಾರರೆಂದು ಅಲ್ಲಾಹು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(9,43,'9:43','(ಓ ಪೈಗಂಬರರೇ,) ಅಲ್ಲಾಹು ನಿಮ್ಮನ್ನು ಕ್ಷಮಿಸಿದ್ದಾನೆ. ನೀವು ಅವರಿಗೇಕೆ ಅನುಮತಿ ನೀಡಿದಿರಿ? ಸತ್ಯ ಹೇಳಿದವರು ಯಾರೆಲ್ಲವೆಂದು ತಮಗೆ ಸ್ಪಷ್ಟವಾಗುವವರೆಗೆ ಹಾಗೂ ಸುಳ್ಳು ಹೇಳುವವರನ್ನು ಸರಿಯಾಗಿ ನೀವು ತಿಳಿದುಕೊಳ್ಳುವವರೆಗೆ (ಯಾಕೆ ತಾವು ಕಾಯಲಿಲ್ಲ ?) .');
INSERT INTO "kannada" VALUES(9,44,'9:44','ಅಲ್ಲಾಹು ಮತ್ತು ಪರಲೋಕದಲ್ಲಿ ನಂಬಿಕೆ ಇರುವವರು ತಮ್ಮ ಸೊತ್ತುಗಳು ಹಾಗೂ ದೇಹಗಳ ಮೂಲಕ ಧರ್ಮಯುದ್ಧ ಮಾಡುವುದರಿಂದ ತಪ್ಪಿಸಿಕೊಳ್ಳಲು ನಿಮ್ಮಲ್ಲಿ ಅನುಮತಿ ಕೇಳಲಾರರು. ಅಲ್ಲಾಹು ಧರ್ಮನಿಷ್ಠರನ್ನು ಚೆನ್ನಾಗಿ ಬಲ್ಲವನಾಗಿರುತ್ತಾನೆ');
INSERT INTO "kannada" VALUES(9,45,'9:45','ಅಲ್ಲಾಹು ಮತ್ತು ಅಂತಿಮ ದಿನದ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸದವರು ಮಾತ್ರ ಇಂತಹ ನಿವೇದನೆಗಳನ್ನು ಮಾಡುತ್ತಾರೆ. ಅವರ ಹೃದಯಗಳು ಸಂಶಯಗ್ರಸ್ತವಾಗಿವೆ. ಅವರು ತಮ್ಮ ಸಂಶಯಗಳಲ್ಲೇ ಅಂಡೆಲೆಯುತ್ತಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(9,46,'9:46','ಯುದ್ಧಕ್ಕೆ ಹೊರಡಲು ಉದ್ದೇಶವಿದ್ದಿದ್ದರೆ ಅದಕ್ಕಾಗಿ ಕೆಲವು ಉಪಕರಣಗಳನ್ನು ಅವರು ಸಿದ್ಧಪಡಿಸಿಕೊಳ್ಳುತ್ತಿದ್ದರು. ಆದರೆ ಯುದ್ದಕ್ಕಾಗಿ ಅವರು ಹೊರಡುವುದನ್ನು ಅಲ್ಲಾಹು ಅನಿಷ್ಟಪ ಟ್ಟನು. ಹಾಗಾಗಿ ಅವರಿಗೆ ಅವನು ಉದಾಸೀನ ವನ್ನುಂಟು ಮಾಡಿದನು. (ಯುದ್ದಕ್ಕೆ ಹೊರಡದೆ) ಕೂತು ಕೊಳ್ಳುವವರ ಜೊತೆಗೆ ನೀವೂ ಕೂತು ಕೊಳ್ಳಿರಿ ಎಂದು ಅವರಲ್ಲಿ ಹೇಳಲಾಯಿತು.');
INSERT INTO "kannada" VALUES(9,47,'9:47','ಅವರು ನಿಮ್ಮ ಜೊತೆ ಹೊರಟಿದ್ದರೆ ನಿಮಗೆ ಕೇಡನ್ನಲ್ಲದೆ ಮತ್ತೇನನ್ನೂ ಅವರು ಹೆಚ್ಚಿಸುತ್ತಿರಲಿಲ್ಲ. ಅವರು ನಿಮ್ಮ ನಡುವೆ ಪಿತೂರಿಯ ಇಚ್ಛೆಯಿಂದ ಓಡಾಡುತ್ತಿದ್ದರು. ಅವರ ಮಾತು ಕೇಳುವ ಕೆಲವರು ನಿಮ್ಮ ಕೂಟದಲ್ಲಿದ್ದಾರೆ . ಅಂತಹ ಅಕ್ರಮಿಗಳ ಬಗ್ಗೆ ಅಲ್ಲಾಹನು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(9,48,'9:48','ನಿಜವಾಗಿಯೂ ಅವರು ಇದಕ್ಕೆ ಮೊದಲು ಕ್ಷೋಭೆಯುಂಟು ಮಾಡಲು ಪ್ರಯತ್ನಿಸಿದ್ದರು. ಅವರ ಇಚ್ಛೆಗೆದುರಾಗಿ ಸತ್ಯವು ಪ್ರಕಟಗೊಂಡು ಅಲ್ಲಾಹನ ಕಾರ್ಯ ಜಯಪ್ರದವಾಗುವವರೆಗೂ ಅವರು ನಿಮ್ಮನ್ನು ಸೋಲಿಸಲಿಕ್ಕಾಗಿ ಅನೇಕ ವಿಧದ ಕುತಂತ್ರಗಳನ್ನು ನಡೆಸಿದ್ದರು');
INSERT INTO "kannada" VALUES(9,49,'9:49','`ನನಗೆ ಅನುಮತಿ ಕೊಡಿ, ನನ್ನನ್ನು `ಫಿತ್ನಾ’ ದಲ್ಲಿ ಸಿಲುಕಿಸಬೇಡಿ’ ಎಂದು ತಮ್ಮಲ್ಲಿ ಹೇಳು ವ ಕೆಲವರು ಅವರ (ಕಪಟಿಗಳ) ಕೂಟದಲ್ಲಿ ಇದ್ದಾರೆ. ತಿಳಿಯಿರಿ, ಅವರು ಫಿತ್ನಾದಲ್ಲೇ ಬಿದ್ದು ಬಿಟ್ಟಿದ್ದಾರೆ, ನರಕವು ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ನಿಜವಾಗಿಯೂ ಆವರಿಸಿದೆ.');
INSERT INTO "kannada" VALUES(9,50,'9:50','ನಿಮಗೆ ಹಿತವಾದರೆ ಅವರಿಗೆ ಖೇದವಾಗುತ್ತದೆ. ನಿಮಗೆ ಕಷ್ಟ ಬಂದರೆ `ನಮ್ಮ ಜಾಗ್ರತೆಯನ್ನು ನಾವು ಮೊದಲೇ ಮಾಡಿಕೊಂಡಿದ್ದೇವೆ’ ಎನ್ನುತ್ತಾ ಆನಂದಪಟ್ಟುಕೊಂಡು ಅವರು ತಪ್ಪಿಸಿ ಕೊಳ್ಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(9,51,'9:51','ಹೇಳಿರಿ-ಅಲ್ಲಾಹನು ನಮಗಾಗಿ ಲಿಖಿತಗೊಳಿಸಿ ದುದರ ಹೊರತು ಬೇರೇನೂ ನಮಗೆ ಬಾಧಿಸದು. ಅವನು ನಮ್ಮ ರಕ್ಷಾಧಿಕಾರಿ. ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳು ಅಲ್ಲಾಹುವಿನಲ್ಲಿ ಭಾರವರ್ಪಿಸಿಕೊಳ್ಳಲಿ.');
INSERT INTO "kannada" VALUES(9,52,'9:52','ಹೇಳಿರಿ. ಅತ್ಯಂತ ಶ್ರೇಷ್ಟವಾದ ಎರಡು ಕಾರ್ಯಗಳಲ್ಲಿ ಒಂದನ್ನಲ್ಲದೆ ಮತ್ತೇನನ್ನು ನೀವು ನಮ್ಮಲ್ಲಿ ನಿರೀಕ್ಷಿಸುತ್ತಿರುವಿರಿ? ನಾವಾದರೋ ಅಲ್ಲಾಹನು ನಿಮ್ಮನ್ನು ಸ್ವತಃ ತನ್ನ ಕಡೆಯಿಂದ ಅಥವಾ ನಮ್ಮ ಕೈಯ್ಯಾರೆ ಶಿಕ್ಷೆ ಕೊಡುವುದನ್ನೇ ನಿಮ್ಮ ಬಗ್ಗೆ ನಿರೀಕ್ಷಿಸುತ್ತಿದ್ದೇವೆ. ಆದ್ದರಿಂದ ನಿರೀಕ್ಷಿಸುತ್ತಲಿರಿ, ನಾವೂ ನಿಮ್ಮೊಂದಿಗೆ ನಿರೀಕ್ಷಿ ಸುತ್ತಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(9,53,'9:53','ಅವರೊಡನೆ ಹೇಳಿರಿ. ನೀವು ಸ್ವಇಚ್ಚೆಯಿಂದ ಇಲ್ಲವೇ ಬಲವಂತದಿಂದ ಖರ್ಚು ಮಾಡಿರಿ. ಹೇಗಿದ್ದರೂ ಅದು ನಿಮ್ಮಿಂದ ಸ್ವೀಕರಿಸಲ್ಪಡಲಾರದು. ಏಕೆಂದರೆ ನೀವು ಕರ್ಮಭ್ರಷ್ಟ ಜನಾಂಗವಾಗಿರುತ್ತೀರಿ.');
INSERT INTO "kannada" VALUES(9,54,'9:54','ಅವರು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ರಸೂಲರನ್ನು ನಿಷೇಧಿಸಿದರು. ಅವರು ನಮಾಝಿಗೆ ಆಲಸ್ಯ ದೊಂದಿಗೆ ಬರುತ್ತಾರೆ. ಮತ್ತು ದೇವ ಮಾರ್ಗದಲ್ಲಿ ಒಲ್ಲದ ಮನಸ್ಸಿನಿಂದ ಖರ್ಚು ಮಾಡುತ್ತಾರೆ ಎಂಬುದರ ಹೊರತು ಅವರ ಸಂಪತ್ತು ಸ್ವೀಕರಿ ಸಲ್ಪಡದಿರಲು ಇನ್ನಾವ ಕಾರಣವೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(9,55,'9:55','ಇವರ ಸಂಪತ್ತು ಮತ್ತು ಸಂತತಿಗಳು ತಮ್ಮನ್ನು ಪರವಶಗೊಳಿಸದಿರಲಿ. ಅಲ್ಲಾಹು ಇವುಗಳ ಮೂಲಕ ಅವರಿಗೆ ಇಹಜೀವನದಲ್ಲೇ ಶಿಕ್ಷೆ ಕೊಡ ಬೇಕೆಂದೂ ಸತ್ಯನಿಷೇಧಿಗಳಾಗಿಯೇ ಇವರು ಪ್ರಾಣ ಬಿಡಬೇಕೆಂದೂ ಇಚ್ಛಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(9,56,'9:56','ನಾವು ನಿಮ್ಮವರೇ ಆಗಿರುತ್ತೇವೆಂದು ಅಲ್ಲಾಹ ನಾಣೆ ಹಾಕುತ್ತ ಅವರು ಹೇಳುತ್ತಾರೆ. ವಾಸ್ತವದಲ್ಲಿ ಅವರು ಎಷ್ಟು ಮಾತ್ರಕ್ಕೂ ನಿಮ್ಮ ಕೂಟದವರಲ್ಲ. ನಿಜಕ್ಕೂ ಅವರು ಅಂಜಿ ತತ್ತರಿಸುವ ಜನಾಂಗ.');
INSERT INTO "kannada" VALUES(9,57,'9:57','ಅವರೆಲ್ಲಾದರೂ ಅಭಯಸ್ಥಾನ ಅಥವಾ ಗುಹೆಗಳು ಅಥವಾ ಅಡಗು ತಾಣವನ್ನು ಕಂಡುಕೊಂಡಿದ್ದರೆ, ಓಡಿ ಹೋಗಿ ಅದರೊಳಗೆ ಅವಿತುಕೊಳ್ಳುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(9,58,'9:58','ಓ ಪೈಗಂಬರರೇ, ಅವರಲ್ಲಿ ಕೆಲವರು ದಾನ ವಿತರಣಾ ಕಾರ್ಯದ ಬಗ್ಗೆ ನಿಮ್ಮನ್ನು ಆಕ್ಷೇ ಪಿಸುತ್ತಾರೆ. ಆ ಸೊತ್ತಿನಿಂದ ಅವರಿಗೇನಾದರೂ ಕೊಟ್ಟು ಬಿಟ್ಟರೆ ತೃಪ್ತರಾಗುತ್ತಾರೆ. ಕೊಡದಿದ್ದರೆ ಸಿಟ್ಟಾಗುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(9,59,'9:59','ಅಲ್ಲಾಹ್ ಮತ್ತು ರಸೂಲರು ಅವರಿಗೆ ಕೊಟ್ಟು ದರಲ್ಲಿ ಅವರು ತೃಪ್ತಿಪಟ್ಟು, ‘ಅಲ್ಲಾಹು ನಮಗೆ ಸಾಕು. ಅಲ್ಲಾಹನು ತನ್ನ ಅನುಗ್ರಹದಿಂದ ಕೊಡ ಲಿರುವನು ಮತ್ತು ಅವನ ರಸೂಲರೂ ನಮಗೆ ದಯಪಾಲಿಸುವರು. ನಾವು ಅಲ್ಲಾಹನ ಕಡೆಗೇ ಆಗ್ರಹಿಸಿಕೊಂಡವರು’ ಎನ್ನುತ್ತಿದ್ದರೆ ಅದೆಷ್ಟು ಉತ್ತಮವಾಗುತ್ತಿತ್ತು.');
INSERT INTO "kannada" VALUES(9,60,'9:60','ವಾಸ್ತವದಲ್ಲಿ ಝಕಾತಿನ ಧನ ಕಡು ಬಡವರಿಗೆ, ಬಡವರಿಗೆ, ಝಕಾತ್ ಸಂಬಂಧಿತ ಕಾರ್ಮಿ ಕರಿಗೆ, ಮನವೊಲಿಸಬೇಕಾದವರಿಗೆ, ದಾಸ್ಯ ಮುಕ್ತಿಗೆ, ಸಾಲಗಾರರಿಗೆ, ಸನ್ನದ್ಧ ಧರ್ಮ ಯೋಧರಿಗೆ ಹಾಗೂ ಯಾತ್ರಿಕರಿಗೆ ಮಾತ್ರ ಹಕ್ಕು ಳ್ಳದ್ದಾಗಿರುತ್ತದೆ . ಇದು ಅಲ್ಲಾಹನಿಂದ ವಿಧಿಸಲಾದ ನಿಮಯ. ಅಲ್ಲಾಹು ಸರ್ವಜ್ಞನೂ ಮಹಾ ಧೀಮಂತನೂ ಆಗಿರುತ್ತಾನೆ');
INSERT INTO "kannada" VALUES(9,61,'9:61','ಇವರಲ್ಲಿ ಕೆಲವರು ತಮ್ಮ ಮಾತುಗಳಿಂದ ಪ್ರವಾದಿವರ್ಯರಿಗೆ ಕೀಟಲೆ ಕೊಡುತ್ತಾರೆ . ಮತ್ತು ‘ಇವನೊಬ್ಬ ಕಿವಿ ಸೋಲುವ ವ್ಯಕ್ತಿ’ ಎನ್ನುತ್ತಾರೆ. ಹೇಳಿರಿ; `ಹೌದು, ಅವರು ನಿಮಗೆ ಹಿತವಾದುದನ್ನು ಕೇಳಿಸಿಕೊಳ್ಳುವವರು’ (ನೀವು ಹೇಳುವಂತೆ ಎಲ್ಲವನ್ನೂ ಕೇಳುವಹಿತ್ತಾಳೆ ಕಿವಿಯವರಲ್ಲ). ಅವರು ಅಲ್ಲಾಹುವನ್ನೂ ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳನ್ನೂ ನಂಬುತ್ತಾರೆ. ನಿಮ್ಮ ಪೈಕಿ ಸತ್ಯ ವಿಶ್ವಾಸವಿಟ್ಟವರಿಗೆ ಅವರು ವರದಾನವಾಗಿರುವರು. ಅಲ್ಲಾಹನ ರಸೂಲರಿಗೆ ಕೀಟಲೆ ಕೊಡು ವವರಿಗೆ ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆಯಿದೆ.');
INSERT INTO "kannada" VALUES(9,62,'9:62','ನಿಮ್ಮನ್ನು ತೃಪ್ತಿ ಪಡಿಸುವ ಸಲುವಾಗಿ ಅವರು ನಿಮ್ಮಲ್ಲಿ ಅಲ್ಲಾಹುವಿನ ಮೇಲೆ ಆಣೆ ಹಾಕುತ್ತಾರೆ. ಅವರು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಾಗಿದ್ದಲ್ಲಿ ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ರಸೂಲರನ್ನು ತೃಪ್ತಿಪಡಿಸುವುದು ಹೆಚ್ಚಿನ ಬಾಧ್ಯತೆಯಾಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(9,63,'9:63','ಅಲ್ಲಾಹು ಮತ್ತು ರಸೂಲರನ್ನು ವಿರೋಧಿಸುವ ವನಿಗೆ ನರಕಾಗ್ನಿ ಕಾದಿದ್ದು ಅದರಲ್ಲಿ ಅವನು ಶಾಶ್ವತನೆಂದೂ ಅವರು ತಿಳಿಯಲಿಲ್ಲವೇ? ಅದು ಅತಿ ದೊಡ್ಡ ಅಪಮಾನವಾಗಿರುತ್ತದೆ');
INSERT INTO "kannada" VALUES(9,64,'9:64','ತಮ್ಮ ಮನಸ್ಸಲ್ಲಿರುವುದನ್ನು ಸ್ಪಷ್ಟಪಡಿಸುವಂತಹ ಖುರ್‍ಆನ್ ಅಧ್ಯಾಯ ಇಳಿದುಬರುವ ಬಗ್ಗೆ ಕಪಟಿಗಳು ಹೆದರುತ್ತಾರೆ. (ಓ ಪೈಗಂಬರರೇ, ಅವರೊಡನೆ) ಹೇಳಿರಿ, ನೀವು ಅಪಹಾಸ್ಯ ಮಾಡಿರಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ನೀವು ಭಯಪಡು ವುದನ್ನು ಅಲ್ಲಾಹು ಖಂಡಿತ ಹೊರತರುವನು.');
INSERT INTO "kannada" VALUES(9,65,'9:65','ನೀವು ಅವರಲ್ಲಿ ವಿಚಾರಿಸಿದರೆ; `ನಾವು ಬರೇ ಒಂದು ಆಟ, ವಿನೋದದ ಮಾತನ್ನಾಡುತ್ತಿದ್ದೆವು’ ಎಂದವರು ಹೇಳುತ್ತಾರೆ. ಅವರೊಡನೆ ಹೇಳಿರಿ; ಅಲ್ಲಾಹು, ಅವನ ವಚನಗಳು ಮತ್ತು ಅವನ ರಸೂಲರೊಂದಿಗೇ ನೀವು ಹಾಸ್ಯ ಮಾಡುತ್ತಿರುವಿರಾ? ');
INSERT INTO "kannada" VALUES(9,66,'9:66','ನೀವು ನೆಪಗಳನ್ನು ಹೇಳಬೇಡಿ. ನೀವು ವಿಶ್ವಾಸ ವಿಟ್ಟ ಬಳಿಕ ನಿಷೇಧಿಸಿರುತ್ತೀರಿ . ನಿಮ್ಮ ಪೈಕಿ ಒಂದು ವಿಭಾಗಕ್ಕೆ ನಾವು ಕ್ಷಮೆ ನೀಡಿದರೆ ಮತ್ತೊಂದು ವಿಭಾಗಕ್ಕೆ ಶಿಕ್ಷೆ ಕೊಡುತ್ತೇವೆ. ಯಾಕೆಂದರೆ ಅವರು ಅಪರಾಧಿಗಳಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(9,67,'9:67','ಕಪಟ ಪುರುಷರೂ ಕಪಟಿ ಸ್ತ್ರೀಯರೂ ಪರಸ್ಪರ ಅನ್ಯೋನರು. ಅವರು ಕೆಡುಕಿನ ಆಜ್ಞೆ ಕೊಡುತ್ತಾರೆ. ಒಳಿತುಗಳಿಂದ ತಡೆಯುತ್ತಾರೆ ಮತ್ತು ತಮ್ಮ ಕೈಗಳನ್ನು ಸತ್ಕಾರ್ಯಗಳಿಂದ ತಡೆದಿಡು ತ್ತಾರೆ. ಅವರು ಅಲ್ಲಾಹನನ್ನು ಉಪೇಕ್ಷಿಸಿದರು. ಆಗ ಅಲ್ಲಾಹನೂ ಅವರನ್ನು ಉಪೇಕ್ಷಿಸಿದನು . ನಿಜವಾಗಿಯೂ ಕಪಟಿಗಳು ಧರ್ಮ ಧಿಕ್ಕಾರಿಗಳು.');
INSERT INTO "kannada" VALUES(9,68,'9:68','ಕಪಟ ವಿಶ್ವಾಸಿಗಳು, ಕಪಟಿ ವಿಶ್ವಾಸಿನಿಗಳು ಹಾಗೂ ಕಾಫಿರರಿಗೆ ನರಕಾಗ್ನಿಯನ್ನು ಅಲ್ಲಾಹನು ವಾಗ್ದಾನ ಮಾಡಿರುವನು. ಅವರದರಲ್ಲಿ ಶಾಶ್ವತರು. ಅದೇ ಅವರಿಗೆ ತಕ್ಕುದಾದುದು. ಅಲ್ಲಾಹು ಅವರನ್ನು ಶಪಿಸಿರುವನು. ಅವರಿಗೆ ಸ್ಥಿರವಾದ ಶಿಕ್ಷೆಯಿದೆ ');
INSERT INTO "kannada" VALUES(9,69,'9:69','(ಕಪಟಿಗಳೇ!) ನೀವು ನಿಮ್ಮ ಹಿಂದಿನವರಂತೆಯೇ ಇದ್ದೀರಿ. ಅವರು ನಿಮಗಿಂತ ಶಕ್ತರೂ ಸೊತ್ತು- ಸಂತಾನಗಳಲ್ಲಿ ನಿಮಗಿಂತ ಸಮೃದ್ಧರೂ ಆಗಿದ್ದರು. ಅವರು ತಮ್ಮ ಹಿಸೆಯಿಂದ ಸುಖ ಅನುಭವಿಸಿದರು. ನಿಮ್ಮ ಹಿಂದಿನವರು ಅವರ ಹಿಸೆಯಿಂದ ಸುಖ ಅನುಭವಿಸಿದಂತೆ ನೀವು ಕೂಡಾ ನಿಮ್ಮ ಪಾಲಿನಿಂದ ಸುಖ ಅನುಭವಿಸಿದಿರಿ. ಅವರು ವಿನೋದಗಳಲ್ಲಿ ಪ್ರವೇಶಿಸಿದಂತೆ ನೀವೂ ವಿನೋದಗಳಲ್ಲಿ ಪ್ರವೇಶಿಸಿದಿರಿ. ಅವರ ಕರ್ಮಗಳೆಲ್ಲ ಇಹದಲ್ಲೂ ಪರದಲ್ಲೂ ನಿಷ್ಫಲವಾದುವು. ಅವರೇ ನಷ್ಟವಂತರು.');
INSERT INTO "kannada" VALUES(9,70,'9:70','ಅವರಿಗೆ ಹಿಂದೆ ಗತಿಸಿ ಹೋದ ನೂಹರ ಜನಾಂಗ, ಆದ್, ಸಮೂದ್, ಇಬ್‍ರಾಹೀಮರ ಜನಾಂಗ, ಮದ್‍ಯನದವರು ಹಾಗೂ ಕವುಚಿ ಹಾಕಲ್ಪಟ್ಟ ನಾಡಿನವರ ಚರಿತ್ರೆ ತಲುಪಿಲ್ಲವೇ? ಅವರ ರಸೂಲರು ಅವರ ಬಳಿ ಪ್ರತ್ಯಕ್ಷ ಪ್ರಮಾಣಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಬಂದರು. ಅಲ್ಲಾಹನು ಅವರಿಗೆ ಅನ್ಯಾಯ ಮಾಡಿಲ್ಲ. ಅವರು ಸ್ವತಃ ತಮ್ಮ ಮೇಲೆ ಅನ್ಯಾಯ ಮಾಡಿಕೊಂಡರು.');
INSERT INTO "kannada" VALUES(9,71,'9:71','ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಹಾಗೂ ಸತ್ಯವಿಶ್ವಾಸಿನಿಯರು ಪರಸ್ಪರ ಬಂಧುಗಳು. ಅವರು ಒಳಿತುಗಳ ಅಪ್ಪಣೆ ಕೊಡುತ್ತಾರೆ. ಮತ್ತು ಕೆಡುಕುಗಳಿಂದ ತಡೆಯು ತ್ತಾರೆ. ನಮಾಝ್ ಸಂಸ್ಥಾಪಿಸುತ್ತಾರೆ, ಝಕಾತ್ ಕೊಡುತ್ತಾರೆ ಮತ್ತು ಅಲ್ಲಾಹು ಹಾಗೂ ಅವನ ರಸೂಲರ ಅನುಸರಣೆ ಮಾಡುತ್ತಾರೆ. ಅವರಿಗೆ ಅಲ್ಲಾಹು ದಯೆ ತೋರಲಿರುವನು. ಖಂಡಿತ ಅಲ್ಲಾಹನು ಮಹಾಪ್ರತಾಪಿಯೂ ಯುಕ್ತಿ ಪೂರ್ಣ ನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(9,72,'9:72','ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಹಾಗೂ ಸತ್ಯವಿಶ್ವಾಸಿನಿಗಳಿಗೆ ಅಲ್ಲಾಹನು ಸ್ವರ್ಗೋದ್ಯಾನಗಳನ್ನು ವಾಗ್ದಾನ ಮಾಡಿರುತ್ತಾನೆ. ಅವುಗಳ ತಳಭಾಗದಲ್ಲಿ ನದಿಗಳು ಹರಿಯುತ್ತವೆ. ಅವರದರಲ್ಲಿ ಸ್ಥಿರವಾಸಿಗಳು ಶಾಶ್ವತ ವಾಸದ ಸ್ವರ್ಗಗಳಲ್ಲಿ ಉತ್ಕøಷ್ಟ ಭವನ ಗಳನ್ನು (ಅಲ್ಲಾಹು ವಾಗ್ದಾನ ಮಾಡಿರುತ್ತಾನೆ). ಅಲ್ಲಾಹುವಿನ ತೃಪ್ತಿ ಅತ್ಯಂತ ಹಿರಿದು. ಅದುವೇ ಮಹಾವಿಜಯ.');
INSERT INTO "kannada" VALUES(9,73,'9:73','ಓ ನಬಿಯವರೇ! ಸತ್ಯ ನಿಷೇಧಿಗಳು ಹಾಗೂ ಕಪಟಿಗಳ ವಿರುದ್ಧ ಯುದ್ಧ ಮಾಡಿರಿ. ಅವರೊಂದಿಗೆ ಕಠಿಣವಾಗಿ ವರ್ತಿಸಿರಿ. ಅವರ ನಿವಾಸ ನರಕವಾಗಿದೆ ಮತ್ತು ಆ ಆಶ್ರಯ ತಾಣವು ಬಹಳ ನಿಕೃಷ್ಟ.');
INSERT INTO "kannada" VALUES(9,74,'9:74','ನಾವು ಆ ಮಾತನ್ನು ಹೇಳಲಿಲ್ಲವೆಂದು ಅವರು ಅಲ್ಲಾಹುವಿನ ಮೇಲೆ ಆಣೆ ಹಾಕುತ್ತಿದ್ದಾರೆ . ನಿಜವಾಗಿಯೂ ಅವರು ಸತ್ಯನಿಷೇಧದ ಮಾತನ್ನು ಹೇಳಿದ್ದಾರೆ. ವಿಧೇಯತೆ ಪ್ರದರ್ಶಿಸಿದ ನಂತರ ಸತ್ಯನಿಷೇಧ ಹೊಂದಿದ್ದಾರೆ. ಅವರಿಗೆ ಸಾಧ್ಯ ವಾಗದಂತಹ ಕಾರ್ಯವನ್ನು ಮಾಡಲು ನಿರ್ಧರಿಸಿಯೂ ಇದ್ದಾರೆ . ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ರಸೂಲರು ಅಲ್ಲಾಹುವಿನ ಔದಾರ್ಯದಿಂದ ಅವರನ್ನು ಸಂಪನ್ನಗೊಳಿಸಿದ್ದಲ್ಲದೆ ಅವರ ವಿರೋಧಕ್ಕೆ ಬೇರೆ ಕಾರಣವಿಲ್ಲ. ಅವರು ಪಶ್ವಾತ್ತಾಪಪಟ್ಟರೆ ಅದು ಅವರಿಗೇ ಉತ್ತಮವಾದುದು. ವಿಮುಖರಾಗಿ ಹಿಂಜರಿದರೆ ಅಲ್ಲಾಹು ಅವರಿಗೆ ಇಹಪರದಲ್ಲಿ ವೇದನಾಜನಕ ಶಿಕ್ಷೆ ಕೊಡುವನು. ಭೂಮಿಯಲ್ಲಿ ಯಾವುದೇ ರಕ್ಷಕನೋ ಸಹಾಯಕನೋ ಅವರಿಗಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(9,75,'9:75','ಅಲ್ಲಾಹನು ತನ್ನ ಅನುಗ್ರಹದಿಂದ ಏನಾದರೂ ಕೊಟ್ಟರೆ ನಾವು ನಿಜವಾಗಿಯೂ ದಾನಧರ್ಮ ಮಾಡುವೆವು, ಸಚ್ಚರಿತರ ಕೂಟಕ್ಕೆ ಸೇರುವೆವು ಎಂದು ಅಲ್ಲಾಹುವಿನಲ್ಲಿ ವಾಗ್ದಾನ ಮಾಡಿ ದವರು ಅವರ (ಕಪಟಿಗಳ) ಕೂಟದಲ್ಲಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(9,76,'9:76','ಆದರೆ, ಅಲ್ಲಾಹನು ಅವರಿಗೆ ತನ್ನ ಕೃಪೆಯಿಂದ ಕೊಟ್ಟಾಗ ಅವರು ಅದರಲ್ಲಿ ಲೋಭ ತೋರಿದರು. ಕರಾರಿನ ಗೊಡವೆಯಿಂದ ವಿಮುಖರಾದರು. ನಗಣ್ಯಗೊಳಿಸಿ ಹಿಂತಿರುಗಿದರು.');
INSERT INTO "kannada" VALUES(9,77,'9:77','ಪರಿಣಾಮವಾಗಿ ಅವರು ಅಲ್ಲಾಹನೊಡನೆ ಮಾಡಿದ ಕರಾರುಭಂಗಕ್ಕಾಗಿಯೂ ಅವರು ಸುಳ್ಳು ಹೇಳಿದುದಕ್ಕಾಗಿಯೂ ಅಲ್ಲಾಹನನ್ನು ಅವರು ಅಭಿಮುಖೀಕರಿಸುವ ದಿನದವರೆಗೆ ಅವನು ಅವರ ಹೃದಯದಲ್ಲಿ (ಊರಿನಿಂತ) ಕಾಪಟ್ಯ ವನ್ನು ಅವರ ಪರ್ಯವಸಾನವಾಗಿ ಮಾಡಿದನು.');
INSERT INTO "kannada" VALUES(9,78,'9:78','ಅವರ ರಹಸ್ಯಗಳು ಹಾಗೂ ಗೂಢಾಲೋಚನೆ ಗಳನ್ನು ಅಲ್ಲಾಹು ಅರಿಯುವನೆಂದೂ ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಅದೃಶ್ಯಗಳನ್ನು ಚೆನ್ನಾಗಿ ಬಲ್ಲವನೆಂದೂ ಅವರು ಗ್ರಹಿಸಿಕೊಳ್ಳಲಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(9,79,'9:79','ಧಾರಾಳವಾಗಿ ಐಚ್ಚಿಕದಾನ ಮಾಡುತ್ತಿರುವ ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳ ಕೂಟದ ಕೆಲವರನ್ನೂ ಕಷ್ಟಪಟ್ಟು ದುಡಿದ ಸ್ವಲ್ಪ ಸಂಪಾದನೆಯಿಂದ ದಾನ ಮಾಡುವವರನ್ನೂ ಅಣಕಿಸುವ ಹಾಗೂ ಪರಿಹಾಸ ಮಾಡುವವರಿಗೆ ಅವರ ಪರಿಹಾಸಕ್ಕೆ ತಕ್ಕುದಾದ ಶಿಕ್ಷೆಯನ್ನು ಅಲ್ಲಾಹು ಕೊಡುತ್ತಾನೆ. ಅವರಿಗೆ ಯಾತನಾಮಯ ಶಿಕ್ಷೆ ಕಾದಿದೆ.');
INSERT INTO "kannada" VALUES(9,80,'9:80','(ಓ ಪೈಗಂಬರರೇ,) ನೀವು ಅವರಿಗಾಗಿ ನನ್ನಲ್ಲಿ ಕ್ಷಮಾಯಾಚನೆ ಮಾಡಿರಿ ಅಥವಾ ಕ್ಷಮಾ ಯಾಚನೆ ಮಾಡದಿರಿ. ಆದರೆ ಅವರಿಗಾಗಿ ನೀವು ಎಪ್ಪತ್ತು ಸಲ ಕ್ಷಮಾಯಾಚನೆ ಮಾಡಿದರೂ ಅಲ್ಲಾಹು ಅವರಿಗೆ ಖಂಡಿತ ಕ್ಷಮಿಸಲಾರನು. ಏಕೆಂದರೆ ಅವರು ಅಲ್ಲಾಹು ಹಾಗೂ ಅವರ ರಸೂಲರನ್ನು ನಿಷೇಧಿಸಿದ್ದಾರೆ. ಧರ್ಮಧಿಕ್ಕಾರಿ ಜನತೆಯನ್ನು ಅಲ್ಲಾಹು ಸರಿದಾರಿಗೆ ತರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(9,81,'9:81','(ತಬೂಕ್ ಯುದ್ಧದಿಂದ ಗೈರು ಹಾಜರಾತಿಗೆ) ಹಿಂದೆ ಉಳಿದಿರಲು ವಿನಾಯಿತಿ ನೀಡಲ್ಪಟ್ಟವರು ಅಲ್ಲಾಹು ಹಾಗೂ ಅವನ ರಸೂಲರ ಆದೇಶಕ್ಕೆ ವಿರುದ್ಧವಾಗಿ ತಾವು ಕುಳಿತಿರುವ ಬಗ್ಗೆ ಸಂತ ಸಪಟ್ಟರು. ಧನ ಮತ್ತು ತನುಗಳಿಂದ ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಹೋರಾಟ ನಡೆಸುವುದನ್ನು ಅವರು ಅನಿಷ್ಟಪಟ್ಟರು. ಅವರು ಜನರೊಡನೆ ‘ಸೆಖೆ ಕಾಲ ದಲ್ಲಿ ಯುದ್ದಕ್ಕೆ ಹೊರಡಬೇಡಿರಿ’ ಎಂದು ಹೇಳಿ ದರು. ಹೇಳಿರಿ, ನರಕಾಗ್ನಿಯು ಇದಕ್ಕಿಂತಲೂ ಕಠಿಣ ಸೆಖೆಯುಳ್ಳದ್ದು. ಅದನ್ನವರು ಗ್ರಹಿಸಿ ಕೊಂಡಿದ್ದರೆ, (ಯುದ್ದಕ್ಕೆ ಹೊರಡುತ್ತಿದ್ದರು)');
INSERT INTO "kannada" VALUES(9,82,'9:82','ಅವರು ಸ್ವಲ್ಪ ನಗಾಡಲಿ. ಹೆಚ್ಚು ಅಳಲಿ. ಅವರು ಸಂಪಾದಿಸಿದುದರ ಪ್ರತಿಫಲವಾಗಿ.');
INSERT INTO "kannada" VALUES(9,83,'9:83','ಅಲ್ಲಾಹು ನಿಮ್ಮನ್ನು ಅವರ ಒಂದು ವಿಭಾಗದ ಬಳಿಗೆ ಮರಳಿಸಿದರೆ ಹಾಗೂ (ಇನ್ನೊಂದು ಯುದ್ಧಕ್ಕೆ) ಹೊರಡಲು ಅವರು ತಮ್ಮಲ್ಲಿ ಅನು ಮತಿ ಕೇಳಿದರೆ; `ನೀವು ನನ್ನ ಜೊತೆ ಯಾವತೂ ಹೊರಡಬೇಡಿ. ನನ್ನ ಜೊತೆ ಯಾವ ಶತ್ರುವಿನ ವಿರುದ್ಧವೂ ನೀವು ಯುದ್ಧ ಮಾಡಬೇಡಿರಿ, ನೀವು ಮೊದಲ ಸಲವೇ ಕುಳಿತಿರುವುದನ್ನೇ ತೃಪ್ತಿಪಟ್ಟಿದ್ದೀರಿ, ಆದ್ದರಿಂದ ಹಿಂದುಳಿದವರ ಜೊತೆಗೇ ನೀವೂ ಕೂತುಕೊಳ್ಳಿರಿ’ ಎಂದು ಹೇಳಿರಿ.');
INSERT INTO "kannada" VALUES(9,84,'9:84','ಅವರ ಪೈಕಿ ಯಾವನು ಮರಣ ಹೊಂದಿದರೂ ಅವನಿಗೆ ತಾವು (ಮಯ್ಯಿತ್) ನಮಾಜು ಮಾಡ ಬೇಡಿರಿ. ಅವನ ಸಮಾಧಿ ಪಕ್ಕದಲ್ಲಿ ನಿಲ್ಲಬೇಡಿರಿ . ಏಕೆಂದರೆ ಅವರು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ರಸೂಲರನ್ನು ನಿಷೇಧಿಸಿದರು. ಅವರು ಕರ್ಮಭ್ರಷ್ಟರಾಗಿರುವ ಸ್ಥಿತಿಯಲ್ಲಿ ಸತ್ತಿದ್ದಾರೆ');
INSERT INTO "kannada" VALUES(9,85,'9:85','ಅವರ ಸೊತ್ತುಗಳೂ ಸಂತಾನಗಳೂ ನಿಮ್ಮನ್ನು ವಿಸ್ಮಯಗೊಳಿಸದಿರಲಿ . ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹು ಅವುಗಳ ಮೂಲಕ ಅವರನ್ನು ಇಹ ಲೋಕದಲ್ಲಿ ಶಿಕ್ಷಿಸಲಿಕ್ಕೂ ಅವರ ಪ್ರಾಣಗಳು ಸತ್ಯನಿಷೇಧಾ ವಸ್ಥೆಯಲ್ಲಿ ಹೊರಟು ಹೋಗುವಂತಾಗಲಿಕ್ಕೂ ನಿರ್ಧರಿಸಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(9,86,'9:86','ಅಲ್ಲಾಹನ ಮೇಲೆ ವಿಶ್ವಾಸವಿಡಿರಿ ಮತ್ತು ಅವನ ರಸೂಲರ ಜೊತೆಗೆ ಯುದ್ಧ ಮಾಡಿರಿ ಎಂಬ ಆದೇಶದ ಒಂದು ಅಧ್ಯಾಯ ಅವತೀರ್ಣಗೊಂಡಾಗ ಅವರಲ್ಲಿದ್ದ ಸ್ಥಿತಿವಂತರೇ ನಿಮ್ಮೊಡನೆ ಯುದ್ಧಕ್ಕೆ ಗೈರು ಹಾಜರಾತಿಗೆ ತಮ್ಮಲ್ಲಿ ಅನುಮತಿ ಕೇಳಿದರು. ನಮ್ಮನ್ನು ಬಿಟ್ಟು ಬಿಡಿರಿ. (ಯುದ್ಧಕ್ಕೆ ಬಾರದೆ) ಕುಳಿತಿರುವವರೊಂದಿಗೆ ನಾವಿರುತ್ತೇವೆ ಎಂದವರು ಹೇಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(9,87,'9:87','ಅವರು ಮನೆಯಲ್ಲಿ ಕುಳಿತುಕೊಂಡಿರುವ ಮಹಿಳೆಯರೊಂದಿಗೆ ಸೇರುವುದನ್ನು ಅವರು ತೃಪ್ತಿಪಟ್ಟರು. ಅವರ ಹೃದಯಗಳಿಗೆ ಮುದ್ರೆಯೊತ ಲ್ಪಟ್ಟಿದೆ. ಆದ್ದರಿಂದ ಅವರು ಗ್ರಹಿಸಿಕೊಳ್ಳುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(9,88,'9:88','ಆದರೆ ಅಲ್ಲಾಹನ ರಸೂಲರು ಹಾಗೂ ಅವರ ಜೊತೆಗೆ ಸತ್ಯವಿಶ್ವಾಸವಿಟ್ಟವರು ತಮ್ಮ ಸೊತ್ತು ಗಳನ್ನೂ ದೇಹಗಳನ್ನೂ ಬಳಸಿ ಯುದ್ಧ ಮಾಡಿದರು. ಅವರಿಗೆ ಹಲವು ವಿಧ ಗುಣಫಲಗಳಿವೆ. ಅವರೇ ಜಯಶಾಲಿಗಳು.');
INSERT INTO "kannada" VALUES(9,89,'9:89','ಅಲ್ಲಾಹನು ಅವರಿಗಾಗಿ ಸ್ವರ್ಗೋದ್ಯಾನಗಳನ್ನು ಸಿದ್ಧಮಾಡಿಟ್ಟಿದ್ದಾನೆ. ಅದರ ತಳಭಾಗದಲ್ಲಿ ನದಿಗಳು ಹರಿಯುತ್ತಿವೆ. ಅವರು ಅದರಲ್ಲಿ ಶಾಶ್ವತ ನೆಲೆಸುವರು. ಇದುವೇ ಮಹತ್ತಾದ ವಿಜಯ.');
INSERT INTO "kannada" VALUES(9,90,'9:90','ನಿಜವಾದ ನೆವ ಇರುವ ಗ್ರಾಮೀಣರು (ಯುದ್ಧಕ್ಕೆ ಗೈರುಹಾಜರಿಗೆ) ಸಮ್ಮತಿಗಾಗಿ ಬಂದರು. ಅಲ್ಲಾಹು ಹಾಗೂ ಅವನ ರಸೂಲರಲ್ಲಿ ಸುಳ್ಳು ಹೇಳಿದವರು ಕೂತುಬಿಟ್ಟರು. ಅವರ ಪೈಕಿ ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ವೇದನಾಜನಕ ಶಿಕ್ಷೆ ಬಾಧಿಸಲಿದೆ.');
INSERT INTO "kannada" VALUES(9,91,'9:91','ಬಲಹೀನರು, ರೋಗಿಗಳು ಹಾಗೂ ವೆಚ್ಚಕ್ಕೆ ಗತಿ ಯಿಲ್ಲದವರಿಗೆ ಯಾವುದೇ ಪಾಪವಿಲ್ಲ. ಅವರು ಅಲ್ಲಾಹು ಹಾಗೂ ಅವನ ರಸೂಲರಿಗೆ ನಿಷ್ಠರಾಗಿದ್ದರೆ (ಇಂತಹ) ಸುಕೃತರ ಮೇಲೆ (ಆರೋಪಕ್ಕೆ) ಯಾವ ದಾರಿಯೂ ಇಲ್ಲ, ಅಲ್ಲಾಹನು ಪರಮ ಕ್ಷಮಾ ದಾನಿಯೂ ಪರಮ ದಯಾಳುವೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(9,92,'9:92','ವಾಹನದಲ್ಲಿ ಕೂರಿಸಿಕೊಂಡು ಹೋಗಲು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದ ಸಂದರ್ಭದಲ್ಲಿ; `ನಿಮ್ಮನ್ನು ಹತ್ತಿಸಿ ಒಯ್ಯಲು ತಕ್ಕ ವಾಹನ ನನ್ನ ಬಳಿ ಇಲ್ಲ’ ಎಂದು ತಾವು ಹೇಳಿದಾಗ ವಿವಶರಾಗಿ ಹೊರಟು ಹೋದ ವರಿಗೂ ಪಾಪವಿಲ್ಲ. ವೆಚ್ಚಕ್ಕೆ ಏನೂ ಒದಗದೇ ಹೋಯಿತಲ್ಲ ಎಂಬ ದುಃಖದಿಂದ ಅವರ ಕಣ್ಣುಗಳಿಂದ ಅಶ್ರುಧಾರೆ ಇಳಿಯುತ್ತಿತ್ತು.');
INSERT INTO "kannada" VALUES(9,93,'9:93','ಆದರೆ ಧನಿಕರಾಗಿರುವ ಸ್ಥಿತಿಯಲ್ಲೂ (ಧರ್ಮ ಯುದ್ಧದಲ್ಲಿ ಭಾಗವಹಿಸುವುದರಿಂದ) ತಮ್ಮಲ್ಲಿ ವಿನಾಯಿತಿಗೆ ಅನುಮತಿ ಕೇಳಿದವರ ಮೇಲೆ ಯೇ ಆಕ್ಷೇಪಕ್ಕೆ ದಾರಿ ಇರುವುದು. ಹಿಂದುಳಿದ ವರ ಜೊತೆಯಾಗುವುದರಲ್ಲಿ ಅವರು ತೃಪ್ತಿಪಟ್ಟರು. ಅವರ ಹೃದಯಗಳಿಗೆ ಅಲ್ಲಾಹು ಮುದ್ರೆಯೊತ್ತಿರುವನು. ಆದುದರಿಂದ ಅವರಿಗೆ ತಿಳಿಯುವುದಿಲ್ಲ');
INSERT INTO "kannada" VALUES(9,94,'9:94','ನೀವು ಹಿಂದಿರುಗಿ ಅವರ ಬಳಿಗೆ ಬಂದಾಗ ನಿಮ್ಮ ಬಳಿ ಅವರು ನೆಪಗಳನ್ನು ಹೇಳುವರು. ಹೇಳಿರಿ; `ನೆಪ ಹೇಳಬೇಡಿ. ನಿಮ್ಮನ್ನು ನಾವು ಖಂಡಿತ ನಂಬುವುದಿಲ್ಲ. ನಿಮ್ಮ ಕೆಲವು ವಾರ್ತೆಗಳನ್ನು ಅಲ್ಲಾಹು ನಮಗೆ ತಲುಪಿಸಿರುತ್ತಾನೆ. ಇನ್ನು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ರಸೂಲರು ನಿಮ್ಮ ವರ್ತನೆಯನ್ನು ನೋಡುವರು. ನಂತರ ದೃಶ್ಯಾದೃಶ್ಯಗಳನ್ನು ಬಲ್ಲವನ ಬಳಿಗೆ ನಿಮ್ಮನ್ನು ಮರಳಿಸಲಾಗುವುದು. ಆಗ ಅವನು ನಿಮ್ಮ ಇಲ್ಲಿನ ಪ್ರವರ್ತಿಗಳ ಬಗ್ಗೆ ನಿಮಗೆ ಮಾಹಿತಿ ನೀಡಲಿರುವನು.');
INSERT INTO "kannada" VALUES(9,95,'9:95','ಅವರ ಬಳಿಗೆ ನೀವು ವಾಪಾಸಾದರೆ ಅವರನ್ನು ನೀವು ನಿರ್ಲಕ್ಷಿಸಬೇಕೆಂದು ಅಲ್ಲಾಹುವಿನ ಮೇಲೆ ಆಣೆ ಹಾಕಿ ನಿಮ್ಮಲ್ಲಿ ಕೇಳಿಕೊಳ್ಳುವರು. ನೀವು ಅವರನ್ನು ನಿರ್ಲಕ್ಷಿಸಿರಿ. ನಿಶ್ಚಯ ಅವರು ಅಶುದ್ಧರು. ಅವರ ಅಂತಿಮ ಗಮ್ಯವು ನರಕವಾಗಿರು ವುದು ಅವರು ಮಾಡಿದುದರ ಫಲವಾಗಿ.');
INSERT INTO "kannada" VALUES(9,96,'9:96','ವಸ್ತುತಃ ನೀವು ಅವರ ಬಗ್ಗೆ ತೃಪ್ತಿ ಹೊಂದುವ ಸಲುವಾಗಿ ನಿಮ್ಮಲ್ಲಿ ಅವರು ಆಣೆ ಹಾಕುವರು. ಆದರೆ ನೀವು ಅವರ ಬಗ್ಗೆ ಸಂತೃಪ್ತರಾದರೂ ಕೂಡಾ ಆ ಭ್ರಷ್ಟ ಜನತೆಯನ್ನು ಅಲ್ಲಾಹು ತೃಪ್ತಿಪಡಲಾರನು.');
INSERT INTO "kannada" VALUES(9,97,'9:97','ಗ್ರಾಮೀಣ ಅರಬರು ಸತ್ಯನಿಷೇಧ ಹಾಗೂ ಕಾಪಟ್ಯದಲ್ಲಿ ಹೆಚ್ಚು ಕಠಿಣರು . ಅಲ್ಲಾಹನು ತನ್ನ ರಸೂಲರ ಮೇಲೆ ಅವತೀರ್ಣಗೊಳಿಸಿದ ಧರ್ಮದ ಮೇರೆಗಳ ಬಗೆಗೆ ಅಜ್ಞರಾಗಿರುವುದಕ್ಕೆ ಹೆಚ್ಚು ಯೋಗ್ಯತೆಯುಳ್ಳವರು. ಅಲ್ಲಾಹನು ಸರ್ವಜ್ಞನೂ ಪರಮ ತಂತ್ರಜ್ಞಾನಿಯೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(9,98,'9:98','ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ವೆಚ್ಚ ಮಾಡುವುದನ್ನು ನಷ್ಟವಾಗಿ ಕಾಣುವ ಹಾಗೂ ನಿಮಗೆ ಕಾಲ ಚಕ್ರದ ಉರುಳಾಟವನ್ನು (ಏನಾದರೊಂದು ಅನಾಹುತ ಬರುವುದನ್ನು) ನಿರೀಕ್ಷಿಸುವ ಕೆಲವು ಗ್ರಾಮೀಣರಿದ್ದಾರೆ. ವಾಸ್ತವದಲ್ಲಿ ಕೆಡುಕಿನ ಚಕ್ರವು ಅವರ ಮೇಲೆಯೇ ಉರುಳಲಿದೆ. ಅಲ್ಲಾಹನು ಸರ್ವಶ್ರುತನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(9,99,'9:99','ಅಲ್ಲಾಹು ಮತ್ತು ಪರಲೋಕದಲ್ಲಿ ನಂಬಿಕೆ ಇರುವ ಹಾಗೂ ಧರ್ಮಕಾರ್ಯಕ್ಕೆ ಮಾಡಿದ ವೆಚ್ಚವನ್ನು ಅಲ್ಲಾಹನ ಸಾಮಿಪ್ಯ ಮತ್ತು ರಸೂಲರ ಕಡೆಯಿಂದ ಕೃಪಾಶೀರ್ವಾದ ಪಡೆ ಯುವ ಮಾರ್ಗವನ್ನಾಗಿ ಪರಿಗಣಿಸುವವರೂ ಗ್ರಾಮೀಣರ ಕೂಟದಲ್ಲಿದ್ದಾರೆ. ತಿಳಿಯಿರಿ. ಅದು ನಿಜಕ್ಕೂ ಪುಣ್ಯ ಕಾರ್ಯ . ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹು ಅವರನ್ನು ತನ್ನ ಕರುಣೆಯೊಳಗೆ ಪ್ರವೇಶ ಗೊಳಿಸುವನು. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹು ಕ್ಷಮಾಶೀಲನೂ ಪರಮ ದಯಾಳುವೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(9,100,'9:100','ಮುಹಾಜಿರ್ ಮತ್ತು ಅನ್ಸಾರರಲ್ಲಿ ಪ್ರಥಮವಾಗಿ ಮುಂದೆ ಬಂದವರಿಂದಲೂ ನಿಷ್ಕಳಂಕವಾಗಿ ಸುಕೃತದ ಮೂಲಕ ಅವರನ್ನು ಅನುಸರಿಸಿದವರಿಂದಲೂ ಅಲ್ಲಾಹು ಪ್ರಸನ್ನನಾದನು. ಅಲ್ಲಾಹನ (ಪ್ರತಿಫಲದ) ಬಗ್ಗೆ ಅವರೂ ಪ್ರಸನ್ನರಾದರು . ಅಲ್ಲಾಹು ಅವರಿಗಾಗಿ ತಳಭಾಗ ದಲ್ಲಿ ನದಿಗಳು ಹರಿಯುತ್ತಿರುವ ಸ್ವರ್ಗೋದ್ಯಾನಗಳನ್ನು ಸಿದ್ಧಪಡಿಸಿಟ್ಟಿರುವನು. ಅವರು ಅದರಲ್ಲಿ ಶಾಶ್ವತ ನೆಲೆಸುವರು. ಅದುವೇ ಮಹತ್ತರವಾದ ವಿಜಯ.');
INSERT INTO "kannada" VALUES(9,101,'9:101','ನಿಮ್ಮ ಪರಿಸರದ ಗ್ರಾಮೀಣರಲ್ಲಿ ಹಾಗೂ ಮದೀನಾ ನಿವಾಸಿಗಳಲ್ಲೂ ಕಪಟಿಗಳಿದ್ದಾರೆ. ಅವರು ಕಾಪಟ್ಯದಲ್ಲಿ ಪಳಗಿದವರು. ನೀವು ಅವರನ್ನು ಅರಿತಿಲ್ಲ. ನಾವು ಅವರನ್ನು ಅರಿತಿದ್ದೇವೆ. ನಾವು ಅವರಿಗೆ ಎರಡು ಸಲ ಂ ಶಿಕ್ಷೆ ಕೊಡಲಿದ್ದೇವೆ. ನಂತರ ಅವರು ಅತ್ಯಂತ ಕಠಿಣ ಶಿಕ್ಷೆಗೆ ತಳ್ಳಲ್ಪಡುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(9,102,'9:102','ತಮ್ಮ ಅಪರಾಧಗಳನ್ನು ಒಪ್ಪಿಕೊಂಡ ಬೇರೆ ಕೆಲವರಿದ್ದಾರೆ. ಅವರು ಸುಕೃತಗಳನ್ನೂ ದುಷ್ಕøತಗಳನ್ನೂ ಬೆರೆಸಿಕೊಂಡಿದ್ದಾರೆ ಃ. ಅಲ್ಲಾಹು ಅವರ ತಪ್ಪೊಪ್ಪಿಗೆಯನ್ನು ಮನ್ನಿಸುವ ಸಾಧ್ಯತೆ ಇದೆ. ಅಲ್ಲಾಹನು ಪರಮ ಕ್ಷಮಾಶೀಲನೂ ಪರಮ ದಯಾಳುವೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(9,103,'9:103','(ಓ ಪೈಗಂಬರರೇ,) ನೀವು ಅವರ ಸೊತ್ತು ಗಳಿಂದ ದಾನವನ್ನು ಸ್ವೀಕರಿಸಿರಿ. ಆ ಮೂಲಕ ತಾವು ಅವರನ್ನು ಶುದ್ಧೀಕರಿಸಿರಿ ಹಾಗೂ ಅವ ರನ್ನು ಅಭಿವೃದ್ಧಿಪಡಿಸಿರಿ. ಮತ್ತು ಅವರಿಗಾಗಿ ಪ್ರಾರ್ಥಿಸಿರಿ. ಏಕೆಂದರೆ, ನಿಮ್ಮ ಪ್ರಾರ್ಥನೆಯು ಅವರ ಮನಶ್ಶಾಂತಿಗೆ ಕಾರಣವಾಗುವುದು . ಅಲ್ಲಾಹು ಸರ್ವಶ್ರುತನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(9,104,'9:104','ಅಲ್ಲಾಹನೇ ತನ್ನ ದಾಸರ ಪಶ್ಚಾತ್ತಾಪವನ್ನು ಸ್ವೀಕರಿಸುತ್ತಾನೆ ಮತ್ತು ಅವರ ದಾನಗಳನ್ನು ಪಡೆಯುತ್ತಾನೆ ಎಂದೂ ಖಂಡಿತ ಅಲ್ಲಾಹನೇ ಅತ್ಯಧಿಕ ಕ್ಷಮಿಸುವವನೂ, ಪರಮ ದಯಾಳುವೂ ಆಗಿರುತ್ತಾನೆಂದೂ ಅವರಿಗೆ ತಿಳಿದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(9,105,'9:105','(ಓ ಪೈಗಂಬರರೇ,) ಅವರೊಡನೆ ಹೇಳಿರಿ, `ನೀವು ಕಾರ್ಯವೆಸಗಿರಿ. ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ರಸೂಲ್ ಹಾಗೂ ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ನಿಮ್ಮ ಪ್ರವರ್ತಿಯನ್ನು ಕಾಣುವರು . ನಂತರ ನಿಮ್ಮನ್ನು ದೃಶ್ಯಾದೃಶ್ಯಗಳನ್ನು ಬಲ್ಲವನ ಕಡೆಗೆ ಮರಳಿಸಲಾಗುವುದು. ಆಗ ನಿಮ್ಮ (ಇಲ್ಲಿನ) ಪ್ರವರ್ತಿಗಳ ಬಗ್ಗೆ ಆತನು ನಿಮಗೆ ವಿವರ ನೀಡುವನು’ ಎಂದು.');
INSERT INTO "kannada" VALUES(9,106,'9:106','ಅಲ್ಲಾಹುವಿನ ಆದೇಶಕ್ಕೆ ಕಾದಿರಿಸಲಾದ ಬೇರೊಂದು ವಿಭಾಗದವರಿದ್ದಾರೆ. ಒಂದೋ ಅಲ್ಲಾಹು ಅವರಿಗೆ ಶಿಕ್ಷೆ ಕೊಡಬಹುದು. ಅಥವಾ ಅವರ ತೌಬಾವನ್ನು ಸ್ವೀಕರಿಸಬಹುದು. ಅಲ್ಲಾಹನು ಸರ್ವಜ್ಞನೂ ಪರಮ ಯುಕ್ತಿವಂತನೂ ಆಗಿರುವನು');
INSERT INTO "kannada" VALUES(9,107,'9:107','ಕಪಟಿಗಳಲ್ಲಿ ಒಂದು ವಿಭಾಗದವರು ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳಿಗೆ ಪೀಡನೆ ಕೊಡುವ ಉದ್ದೇಶದಿಂದ ಹಾಗೂ ಸತ್ಯನಿಷೇಧಕ್ಕಾಗಿ ಮತ್ತು ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳ ನಡುವೆ ಬಿಕ್ಕಟ್ಟು ಸೃಷ್ಟಿಸಲು ಹಾಗೂ ಈ ಮೊದಲು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ರಸೂಲರ ವಿರುದ್ಧ ಸಮರ ಹೂಡಿದವರಿಗೆ ಅಡಗು ದಾಣವನ್ನು ಒದಗಿಸುವ ಸಲುವಾಗಿ ಒಂದು ಮಸೀದಿಯನ್ನು ನಿರ್ಮಿಸಿದ್ದಾರೆ. ‘ನಾವು ಒಳ್ಳೆಯದ್ದನ್ನಲ್ಲದೆ ಬೇರೇನನ್ನೂ ಬಯಸಲಿಲ್ಲ’ ಎಂದವರು ಪ್ರಮಾಣ ಮಾಡುತ್ತ ಹೇಳುವರು. ಆದರೆ ಅವರು ಸುಳ್ಳುಗಾರರೆಂಬುದಕ್ಕೆ ಅಲ್ಲಾ ಹನು ಸಾಕ್ಷಿಯಾಗಿದ್ದಾನೆ .');
INSERT INTO "kannada" VALUES(9,108,'9:108','ತಾವು ಅದರಲ್ಲಿ ಒಮ್ಮೆಯೂ ನಮಾಜು ಮಾಡ ಬಾರದು. ಮೊದಲ ದಿನವೇ ಭಕ್ತಿಯ ಮೇಲೆ ಅಸ್ಥಿವಾರ ಹಾಕಲಾದ ಮಸೀದಿಯೇ ತಾವು ನಮಾಜು ಮಾಡಲು ಯೋಗ್ಯವಾದುದು. ಪರಿಶುದ್ಧತೆಯನ್ನು ಇಷ್ಟಪಡುವ ಪುರುಷರು ಆ ಮಸೀದಿಯಲ್ಲಿದ್ದಾರೆ. ಪರಿಶುದ್ಧರನ್ನು ಅಲ್ಲಾಹನು ಇಷ್ಟಪಡುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(9,109,'9:109','ತನ್ನ ಕಟ್ಟಡದ ಬುನಾದಿಯನ್ನು ಅಲ್ಲಾಹನ ಭಯ ಮತ್ತು ಅವನ ಸಂಪ್ರೀತಿಯ ಮೇ ಲಿರಿಸಿದವನು ಉತ್ತಮನೋ ಅಥವಾ ತನ್ನ ಕಟ್ಟಡದ ಬುನಾದಿಯನ್ನು ಕುಸಿದು ಬೀಳಲು ಸಿದ್ಧವಾಗಿರುವ ಒಂದು ಅಸ್ಥಿರ ಅಂಚಿನಲ್ಲಿ ಕಟ್ಟಿಸಿದ್ದು ಅದರ ಸಮೇತ ನರಕದ ಅಗ್ನಿಗೆ ಹರಿದು ಬಿದ್ದವನೋ ಯಾರು ಉತ್ತಮನು? ಅಲ್ಲಾಹನು ಅಕ್ರಮಿಗಳಾದ ಜನತೆಗೆ ನೇರ್ಮಾರ್ಗ ಕೊಡುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(9,110,'9:110','ಅವರು ನಿರ್ಮಿಸಿದ ಕಟ್ಟಡವು ಅವರ ಹೃದಯದಲ್ಲಿ ಸದಾ ಸಂದೇಹದ ಮೂಲವಾಗಿ ಉಳಿಯುವುದು. ಅವರ ಹೃದಯಗಳು ಚೂರು ಚೂರಾಗಬೇಕೇ ವಿನಹ (ಅವರಿಗೆ ಪಾರಾಗುವ ಯಾವ ದಾರಿಯೂ ಇಲ್ಲ). ಅಲ್ಲಾಹನು ಸರ್ವಜ್ಞನೂ ಯುಕ್ತಿ ಪÀÇರ್ಣನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(9,111,'9:111','ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಸತ್ಯವಿಶ್ವಾಸಿ ಗಳಿಂದ ಅವರ ತನು-ಧನಗಳನ್ನು ಸ್ವರ್ಗದ ಬದಲಿಗೆ ಖರೀದಿ ಮಾಡಿರುತ್ತಾನೆ. ಅವರು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಹೋರಾಡುತ್ತಾರೆ. ವಧಿಸುತ್ತಾರೆ ಮತ್ತು ವಧಿಸಲ್ಪಡುತ್ತಾರೆ. ತೌರಾತ್, ಇಂಜೀಲ್ ಹಾಗೂ ಖುರ್‍ಆನಿನಲ್ಲಿ ಅಲ್ಲಾಹು ಮಾಡಿದ (ಸ್ವರ್ಗದ) ವಾಗ್ದಾನವು ಅಲ್ಲಾಹನ ಹೊಣೆಯಲ್ಲಿ ದೃಢವಾಗಿಯೇ ಇದೆ. ಅಲ್ಲಾಹನಿಗಿಂತ ಮಿಗಿಲಾಗಿ ವಾಗ್ದಾನ ಪೂರೈ ಸುವವರು ಯಾರಿದ್ದಾರೆ? ಆದುದರಿಂದ ನೀವು ಆತನೊಂದಿಗೆ ಮಾಡಿದ ಈ ವ್ಯವಹಾರಕ್ಕಾಗಿ ಸಂತೋಷಪಡಿರಿ. ಅತ್ಯಂತ ಘನವೆತ್ತ ವಿಜಯ ವೆಂದರೆ ಇದುವೇ .');
INSERT INTO "kannada" VALUES(9,112,'9:112','ಅವರು ಅಲ್ಲಾಹನ ಕಡೆಗೆ ಪಶ್ಚಾತ್ತಾಪಪಟು ಮರಳುವವರು, ಅವನ ಆರಾಧನೆ ಮಾಡುವವರು, ಅವನ ಗುಣಗಾನ ಮಾಡುವವರು, ಅವನಿಗಾಗಿ ವ್ರತ ಕೈಗೊಳ್ಳುವವರು, ಅವನಿಗೆ ರುಕೂಅï ಮತ್ತು ಸುಜೂದ್ ಮಾಡುವವರು (ನಮಾಜು ಮಾಡುವವರು), ಒಳಿತಿನ ಆಜ್ಞೆ ನೀಡುವವರು, ಕೆಡುಕನ್ನು ವಿರೋಧಿಸುವವರು ಮತ್ತು ಅಲ್ಲಾಹನ ಮೇರೆಗಳನ್ನು ಪಾಲಿಸುವವರೂ ಆಗಿರುವರು. (ಓ ಪೈಗಂಬರರೇ,) ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಶುಭ ಸುದ್ದಿ ನೀಡಿರಿ.');
INSERT INTO "kannada" VALUES(9,113,'9:113','ಪ್ರವಾದಿಗೂ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೂ ಮುಶ್ರಿಕರು ನರಕಕ್ಕೆ ಅರ್ಹರೆಂಬ ವಿಷಯ ವ್ಯಕ್ತವಾದ ಬಳಿಕ ಅವರು ಆಪ್ತ ಬಂಧುಗಳೇ ಆಗಿದ್ದರೂ ಅವರ ಕ್ಷಮೆಗಾಗಿ ಪ್ರಾರ್ಥಿಸುವುದು ಅನುವದನೀಯವಲ್ಲ.');
INSERT INTO "kannada" VALUES(9,114,'9:114','ಆದರೆ ಇಬ್‍ರಾಹೀಮರು ತನ್ನ ತಂದೆಯವರ ಪರವಾಗಿ ಕ್ಷಮಾಯಾಚನೆ ಮಾಡಿದ್ದು ತಂದೆಯವರೊಂದಿಗೆ ಅವರು ಮಾಡಿದ್ದ ಒಂದು ವಾಗ್ದಾನದ ಕಾರಣದಿಂದಾಗಿತ್ತು. ತಂದೆಯು ಅಲ್ಲಾಹುವಿನ ಶತ್ರುವೆಂದು ಖಚಿತವಾದಾಗ ಅವರು ಆತನಿಂದ ಸಂಬಂಧ ಕಳಚಿಕೊಂಡರು. (ಕ್ಷಮಾಯಾಚನೆ ಬಿಟ್ಟು ಬಿಟ್ಟರು) . ನಿಜವಾಗಿಯೂ ಇಬ್ರಾಹೀಮರು ಬಹಳ ಉದಾರಚಿತ್ತರೂ ಸಂಯಮಿಯೂ ಆಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(9,115,'9:115','ಪಾಲಿಸ ಬೇಕಾದುದನ್ನು ಸ್ಪಷ್ಟಪಡಿಸುವವರೆಗೆ ಅವರನ್ನು ದಾರಿ ತಪ್ಪಿಸುವ ರೂಢಿ ಅಲ್ಲಾಹುವಿಗೆ ಇಲ್ಲ . ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹು ಎಲ್ಲಾ ವಿಷಯಗಳನ್ನು ಚೆನ್ನಾಗಿ ಬಲ್ಲವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(9,116,'9:116','ಭೂಮಿ-ಆಕಾಶಗಳ ಪ್ರಭುತ್ವವು ಅಲ್ಲಾಹನದ್ದು . ಅವನು ಬದುಕಿಸುತ್ತಾನೆ, ಸಾಯಿಸುತ್ತಾನೆ. ಅಲ್ಲಾಹನ ಹೊರತು ನಿಮಗೆ ಯಾವುದೇ ರಕ್ಷಕನಾಗಲಿ, ಮಿತ್ರನಾಗಲಿ ಖಂಡಿತ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(9,117,'9:117','ಅಲ್ಲಾಹನು ಪ್ರವಾದಿಯವರಿಗೂ ಕಷ್ಟ ಕಾಲದಲ್ಲಿ ಅವರಿಗೆ ಅನುಸರಿಸಿದ ಮುಹಾಜಿರ್ ಮತ್ತು ಅನ್ಸಾರರಿಗೂ ಒಲಿದಿರುತ್ತಾನೆ. ಅವರ ಪೈಕಿ ಕೆಲವರ ಹೃದಯಗಳು ಚಂಚಲಗೊಳ್ಳುವಂತಿದ್ದರೂ (ಅವರು ಪ್ರವಾದಿ ಯವರನ್ನು ಅನುಸರಿಸಿದರು). ಆಮೇಲೆ ಅಲ್ಲಾಹನು ಅವರನ್ನು ಕ್ಷಮಿಸಿದನು . ಅಲ್ಲಾಹು ಅವರ ಪಾಲಿಗೆ ಅತ್ಯಧಿಕ ದಯೆಯುಳ್ಳವನೂ ಬಹಳ ಕರುಣೆಯುಳ್ಳವನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(9,118,'9:118','ಕ್ಷಮಾಪಣೆ ಮುಂದೂಡಲ್ಪಟ್ಟಿದ್ದ ಆ ಮೂವ ರನ್ನೂ ಅವನು ಕ್ಷಮಿಸಿದನು. ಭೂಮಿಯು ವಿಶಾಲವಾಗಿದ್ದರೂ ಅದು ಅವರ ಪಾಲಿಗೆ ಇಕ್ಕ ಟ್ಟಾಯಿತು. ಅವರ ಮನಸ್ಸುಗಳೇ ಸಂಕುಚಿ ತವಾದವು. ಅಲ್ಲಾಹುವಿನ ಕೋಪದಿಂದ ಪಾರಾಗಲು ಅಲ್ಲಾಹುವಿನ ಕಡೆಗೆ ಶರಣಾಗುವುದಲ್ಲದೆ ಬೇರೆ ದಾರಿಯಿಲ್ಲವೆಂದು ಅವರು ಅರಿತು ಕೊಂಡರು. ಆಗ ಅವರು ತನ್ನ ಕಡೆಗೆ ಖೇದ ಪಟ್ಟು ಮರಳಿಬರಲೆಂದು ಮರುಗುವ ಯೋಗ ವನ್ನು ಅಲ್ಲಾಹನು ಅವರಿಗೆ ಕರುಣಿಸಿದನು. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹು ತೌಬಾವನ್ನು ಅತ್ಯಂತ ಹೆಚ್ಚು ಸ್ವೀಕರಿಸುವವನೂ , ಪರಮದಯಾಳುವೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(9,119,'9:119','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ! ನೀವು ಅಲ್ಲಾಹುವನ್ನು ಭಯಪಡಿರಿ. ಹಾಗೂ ಸತ್ಯನಿಷ್ಠರ ಜೊತೆ ಕೂಡಿ ಕೊಳ್ಳಿರಿ .');
INSERT INTO "kannada" VALUES(9,120,'9:120','ಮದೀನಾದವರು ಹಾಗೂ ಅದರ ಸುತ್ತು ಮುತ್ತಲ ನಿವಾಸಿಗಳಾದ ಹಳ್ಳಿಗರು ಅಲ್ಲಾಹನ ರಸೂಲರನ್ನು ತೊರೆದು ನಿಲ್ಲುವ ಹಾಗೂ ರಸೂಲರ ಜೀವಕ್ಕಿಂತ ತಮ್ಮ ಜೀವದಲ್ಲಿ ಆಗ್ರಹ ಹೊಂದಲು ಯಾವತ್ತೂ ನ್ಯಾಯವಿಲ್ಲ. ಇದು ಯಾಕೆಂದರೆ ಅಲ್ಲಾಹುವಿನ ಮಾರ್ಗದಲ್ಲವರು ಅನುಭವಿಸುವ ದಾಹ, ಕ್ಲೇಶ, ಹಸಿವು ಹಾಗೂ ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ದೋಷವುಂಟಾಗುವ ವಿಧ ದಲ್ಲಿ ಯಾವುದೇ ನೆಲದಲ್ಲಿ ಅವರಿಡುವ ಹೆಜ್ಜೆ, ಯಾವುದೇ ಶತ್ರುವಿ ನಿಂದ ಅವರು ಅನುಭ ವಿಸುವ ತೊಂದರೆ ಅವರ ಪಾಲಿಗೆ ಸುಕೃತವಾಗಿ ದಾಖಲಾಗುತ್ತದೆ. ನಿಜ ವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಸುಕೃತರ ಪುಣ್ಯಫಲವನ್ನು ಯಾವತ್ತೂ ವಿಫಲಗೊಳಿಸಲಾರನು .');
INSERT INTO "kannada" VALUES(9,121,'9:121','ಅವರು ಯಾವುದೇ ಸಣ್ಣ ಪುಟ್ಟ ಅಥವಾ ದೊಡ್ಡ ದಾದ ವೆಚ್ಚ ಮಾಡಿದರೂ (ನೀಡುವ ಎಷ್ಟೇ ಕನಿಷ್ಠ-ಗರಿಷ್ಠ ಸಂಭಾವನೆಗಳಿರಲಿ) ಯಾವುದೇ ಪರ್ವತ ಕಣಿವೆಯನ್ನು ನಡೆದು ದಾಟಿದರೂ ಅವರಿಗಾಗಿ ಅದನ್ನೆಲ್ಲ ಬರೆಯದೆ ಇರುವುದಿಲ್ಲ. ಅವರು ಮಾಡುವ ಸುಕೃತಗಳಿಗೆ ಅತ್ಯಂತ ಶ್ರೇಷ್ಠ ಮಟ್ಟದಲ್ಲಿ ಅಲ್ಲಾಹು ಪ್ರತಿಫಲ ನೀಡುವಸಲುವಾಗಿ.');
INSERT INTO "kannada" VALUES(9,122,'9:122','ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಸರ್ವರೂ ಯುದ್ಧಕ್ಕೆ ಹೋಗ ಬೇಕಾಗಿಲ್ಲ. ಅವರ ಪೈಕಿ ಪ್ರತಿಯೊಂದು ವರ್ಗದವರಿಂದ ಒಂದೊಂದು ವಿಭಾಗ ಮಾತ್ರ ಯಾಕೆ ಹೋಗಬಾರದು? ಉಳಿದವರು ಧರ್ಮಜ್ಞಾನ ಕಲಿತುಕೊಂಡು ಯುದ್ಧದಿಂದ ವಾಪಾಸು ಬಂದ ವರಿಗೆ ಎಚ್ಚರಿಕೆ ನೀಡುವ ಸಲುವಾಗಿ, ತನ್ಮೂಲಕ ಅವರು ಜಾಗ್ರತೆ ಪಾಲಿಸಲಿಕ್ಕಾಗಿ .');
INSERT INTO "kannada" VALUES(9,123,'9:123','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನಿಮ್ಮ ಸ್ವಂತದವರಾದ ಸತ್ಯನಿಷೇಧಿಗಳೊಂದಿಗೆ ಯುದ್ಧ ಮಾಡಿರಿ. ಅವರು ನಿಮ್ಮಲ್ಲಿ ಕಾಠಿಣ್ಯವನ್ನು ಕಾಣಬೇಕು . ಅಲ್ಲಾಹು ಸೂಕ್ಷ್ಮತೆ ಪಾಲಿಸುವವರ ಜೊತೆಗೆ ಇದ್ದಾನೆಂಬುದನ್ನು ತಿಳಿದುಕೊಳ್ಳಿರಿ.');
INSERT INTO "kannada" VALUES(9,124,'9:124','ಒಂದು ಹೊಸ ಅಧ್ಯಾಯ ಅವತೀರ್ಣಗೊಳ್ಳು ವಾಗ ಅವರಲ್ಲಿ ಕೆಲವರು ಕೇಳುವರು; ಇದು ನಿಮ್ಮ ಪೈಕಿ ಯಾರ ವಿಶ್ವಾಸವನ್ನು ಹೆಚ್ಚಿಸಿತು? ಎಂದು. ಆದರೆ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗಂತೂ, ಅದವರ ವಿಶ್ವಾಸವನ್ನೇ ಹೆಚ್ಚಿಸಿತು. ಮತ್ತು ಅವರು ಅದರಿಂದ ಸಂತುಷ್ಟರಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(9,125,'9:125','ಆದರೆ ಹೃದಯಗಳಲ್ಲಿ ರೋಗವಿದ್ದವರು, ಅವರ ಹಿಂದಿನ ಮಾಲಿನ್ಯದ ಮೇಲೆ ಇನ್ನೊಂದು ಮಾಲಿನ್ಯವನ್ನು ವೃದ್ಧಿಸಿತು ಮತ್ತು ಅವರು ಸತ್ಯನಿಷೇಧಿಗಳಾಗಿಯೇ ಸತ್ತು ಹೋದರು.');
INSERT INTO "kannada" VALUES(9,126,'9:126','ಇವರನ್ನು ಪ್ರತಿವರ್ಷವೂ ಒಂದೆರಡು ಸಲ ಪರೀಕ್ಷೆಗೆ ಗುರಿಪಡಿಸಲಾಗುತ್ತಿರುವುದನ್ನು ಇವರು ಕಾಣುವುದಿಲ್ಲವೇ? ಆದರೆ ಇಷ್ಟಾಗಿಯೂ ಅವರು ಪಶ್ಚಾತ್ತಾಪಪಡುವುದೂ ಇಲ್ಲ. ಪಾಠ ಕಲಿಯುವುದೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(9,127,'9:127','ಯಾವುದಾದರೂ ಅಧ್ಯಾಯ ಅವತೀರ್ಣಗೊಳ್ಳು ವಾಗ ನಿಮ್ಮನ್ನಾರಾದರೂ ನೋಡುತ್ತಿಲ್ಲವಷ್ಟೇ ಎಂದು ಇವರು (ಪರಸ್ಪರ ಕಣ್ಣುಸನ್ನೆಗಳಿಂದಲೇ ಕೇಳುತ್ತ) ಪರಸ್ಪರ ನೋಡುವರು. ಅನಂತರ ತಿರುಗಿ ಹೋಗುವರು. ಅಲ್ಲಾಹನು ಇವರ ಹೃದಯಗಳನ್ನು (ಸತ್ಯದಿಂದ) ತಿರುಗಿಸಿ ಬಿಟ್ಟಿರು ತ್ತಾನೆ. ಏಕೆಂದರೆ ಇವರು ಏನನ್ನೂ ಗ್ರಹಿಸಿ ಕೊಳ್ಳದ ಜನಾಂಗ.');
INSERT INTO "kannada" VALUES(9,128,'9:128','ನಿಮ್ಮ ಬಳಿಗೆ ನಿಮ್ಮ ಸ್ವಂತದಿಂದಲೇ ಇರುವ ಓರ್ವ ದೂತರು ಬಂದಿರುತ್ತಾರೆ. ಅವರು ನಿಮ್ಮ ಕಷ್ಟಕ್ಕೆ ಬಹಳ ನೊಂದುಕೊಳ್ಳುವವರು ಮತ್ತು ನಿಮ್ಮ ಸತ್ಪಥ ಪ್ರಾಪ್ತಿಗೆ ಹಂಬಲಿಸುವವರು. ಅವರು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ವತ್ಸಲರೂ ಕರುಣಾಳುವೂ ಆಗಿರುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(9,129,'9:129','(ಓ ಪೈಗಂಬರರೇ,) ಇನ್ನವರು ನಿಮ್ಮಿಂದ ವಿಮು ಖರಾಗುತ್ತಾರಾದರೆ ಇವರೊಡನೆ ಹೀಗೆ ಹೇಳಿಬಿಡಿರಿ. ನನಗೆ ಅಲ್ಲಾಹನೇ ಸಾಕು. ಅವನ ಹೊರತು ಅನ್ಯ ಆರಾಧ್ಯನಿಲ್ಲ. ನಾನು ಅವನ ಮೇಲೆಯೇ ಭರವಸೆಯನ್ನಿರಿಸಿದ್ದೇನೆ ಮತ್ತು ಅವನು ಘನವೆತ್ತ ಅರ್ಶ್‍ನ ಪ್ರಭುವು .');
INSERT INTO "kannada" VALUES(10,1,'10:1','ಅಲಿಫ್ ಲಾಮ್ ರಾ, ಇವು ಸತ್ವಪೂರ್ಣ ಗ್ರಂಥದಸೂಕ್ತಗಳು.');
INSERT INTO "kannada" VALUES(10,2,'10:2','ಜನರಿಗೆ ಎಚ್ಚರಿಕೆ ನೀಡಲಿಕ್ಕೂ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಅವರ ಪ್ರಭುವಿನ ಬಳಿ ಸತ್ಯದ ಸ್ಥಾನವಿದೆ ಎಂಬ ಸುವಾರ್ತೆ ನೀಡಲಿಕ್ಕೂ ಅವರೊಳಗಿಂದಲೇ ಓರ್ವ ಮನುಷ್ಯನ ಕಡೆಗೆ ನಾವು ಸಂದೇಶ ಕಳುಹಿಸಿರುವುದು ಜನರಿಗೆ ಆಶ್ಚರ್ಯವಾಯಿತೇ? ಸತ್ಯನಿಷೇಧಿಗಳು ಹೇಳಿದರು - `ಇವನೊಬ್ಬ ಪ್ರತ್ಯಕ್ಷವಾದ ಮಾಟಗಾರನೇ ಹೌದು’ .');
INSERT INTO "kannada" VALUES(10,3,'10:3','ಆಕಾಶಗಳನ್ನೂ ಭೂಮಿಯನ್ನೂ ಆರು ದಿನಗಳಲ್ಲಿ ಸೃಷ್ಟಿಸಿದ ಅಲ್ಲಾಹನೇ ನಿಮ್ಮ ಪ್ರಭು. ನಂತರ ಅವನಿಗೆ ಸಂಗತವಾಗುವ ವಿಧದಲ್ಲಿ ಅರ್ಶ್‍ನ ಮೇಲೆ ಇಸ್ತಿವಾ ಹೊಂದಿ ಕಾರ್ಯಗಳನ್ನು ನಿಯಂತ್ರಿಸುತ್ತಿರುವನು. ಅವನ ಅನುಮತಿಯ ಹೊರತಾಗಿ ಶಿಫಾರಸು ಮಾಡುವವನು ಯಾವನೂ ಇಲ್ಲ. ಅವನೇ ನಿಮ್ಮ ಪ್ರಭುವಾದ ಅಲ್ಲಾಹು. ಆದುದರಿಂದ ನೀವು ಅವನನ್ನು ಆರಾಧಿಸಿರಿ. ನೀವು ಯೋಚಿಸಿ ಅರ್ಥ ಮಾಡಿಕೊಳ್ಳುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(10,4,'10:4','ಅವನೆಡೆಗೇ ನಿಮ್ಮೆಲ್ಲರ ನಿರ್ಗಮನ. ಇದು ಅಲ್ಲಾಹನ ಸತ್ಯ ವಾಗ್ದಾನ. ನಿಶ್ಚಯವಾಗಿಯೂ ಸೃಷ್ಟಿಯ ಆರಂಭವನ್ನು ಅವನೇ ಮಾಡುತ್ತಾನೆ. ಅನಂತರ ಅವನೇ ಇನ್ನೊಮ್ಮೆ ಸೃಷ್ಟಿಸುವನು. ಸತ್ಯ ವಿಶ್ವಾಸವಿರಿಸಿ ಸತ್ಕರ್ಮವೆಸಗುವವರಿಗೆ ನ್ಯಾಯೋ ಚಿತ ಪ್ರತಿಫಲ ನೀಡುವುದಕ್ಕಾಗಿ. ಸತ್ಯ ನಿಷೇಧಿಗಳಿಗೆ ಕುದಿಯುವ ಪಾನೀಯ ಹಾಗೂ ವೇದನಾ ಯುಕ್ತ ಶಿಕ್ಷೆಯನ್ನು ಅವರ ಸತ್ಯನಿಷೇಧಕ್ಕೆ ಪ್ರತಿಫಲವಾಗಿ ನೀಡಲಾಗುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(10,5,'10:5','ಅವನೇ ಸೂರ್ಯನನ್ನು ಪ್ರಕಾಶಮಾನವನ್ನಾಗಿ ಮಾಡಿದವನು. ಚಂದ್ರನಿಗೆ ಹೊಳಪನ್ನು ಕೊಟ್ಟವನು. ನೀವು ವರ್ಷಗಳ ಎಣಿಕೆಗಳನ್ನೂ ತಾರೀಕುಗಳ ಗಣನೆಯನ್ನೂ ತಿಳಿದುಕೊಳ್ಳಲಿಕ್ಕಾಗಿ ಅದಕ್ಕೆ (ವೃದ್ಧಿ ಕ್ಷಯಗಳ) ಘಟ್ಟಗಳನ್ನೂ ಸರಿಯಾಗಿ ನಿರ್ಣಯಿಸಿಕೊಟ್ಟವನು. ಒಂದು ನಿಜವಾದ ಗುರಿಯಿಲ್ಲದೆ ಅಲ್ಲಾಹು ಏನನ್ನೂ ಸೃಷ್ಟಿಸಲಿಲ್ಲ. ಅವನು ಅರಿವುಳ್ಳವರಿಗೆ ತನ್ನ ದೃಷ್ಟಾಂತಗಳನ್ನು ವಿವರಿಸಿ ಕೊಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(10,6,'10:6','ಖಂಡಿತವಾಗಿಯೂ ರಾತ್ರಿಹಗಲುಗಳ ವ್ಯತ್ಯಾಸದಲ್ಲೂ ಅಲ್ಲಾಹನು ಭೂಮಿ-ಆಕಾಶಗಳಲ್ಲಿ ಸೃಷ್ಟಿಸಿರುವ ಪ್ರತಿಯೊಂದು ವಸ್ತುವಿನಲ್ಲಿಯೂ ಸೂಕ್ಷತೆ ಪಾಲಿಸುವ ಜನರಿಗೆ ನಿದರ್ಶನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(10,7,'10:7','ನಮ್ಮನ್ನು ಭೇಟಿಯಾಗುವ ನಿರೀಕ್ಷೆಯಿಲ್ಲದ, ಐಹಿಕ ಜೀವನದಿಂದ ತೃಪ್ತರಾಗಿರುವ ಹಾಗೂ ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳಿಂದ ಯಾವ ಜನರು ಅಲಕ್ಷ್ಯರಾಗಿರುವರೋ');
INSERT INTO "kannada" VALUES(10,8,'10:8','ಅವರ ಅಂತಿಮ ವಾಸಸ್ಥಾನವು ಘೋರ ನರಕವಾಗಿರುವುದು. ಇದು ಅವರು ಎಸಗಿದ ಕೆಡುಕುಗಳಫಲವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(10,9,'10:9','ಖಂಡಿತವಾಗಿಯೂ ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿ ಸತ್ಕರ್ಮಗಳನ್ನು ಮಾಡುತ್ತಿದ್ದವರನ್ನು ಅವರ ಪ್ರಭು ಅವರ ಸತ್ಯವಿಶ್ವಾಸದ ನಿಮಿತ್ತವಾಗಿ ಸನ್ಮಾರ್ಗದಲ್ಲಿ ನಡೆಸುವನು; ಅನುಗ್ರಹಭರಿತ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ ಅವರಿಗೆ ವಾಸಸ್ಥಾನವಿರುವುದು. ಅದರ ತಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆಗಳು ಹರಿಯುತ್ತಿರುವುವು.');
INSERT INTO "kannada" VALUES(10,10,'10:10','ಅಲ್ಲಿ ಅವರ ಪ್ರಾರ್ಥನೆಯು `ಓ ಅಲ್ಲಾಹ್, ನೀನುಪರಿಶುದ್ಧನು, ನಾವು ನಿನ್ನ ಸಂಕೀರ್ತನೆ ಮಾಡುವೆವು’ ಎಂದಾಗಿರುತ್ತದೆ. ಅವರ ಪರಸ್ಪರ ಅಭಿವಾದನ ‘ಸಲಾಮ್’ ಎಂದಾಗಿರುವುದು. ಅವರ ಪ್ರಾರ್ಥನೆಯ ಮಾತಿನ ಕೊನೆಯ ನುಡಿಯು; ಸರ್ವಸ್ತುತಿ ಸ್ತೋತ್ರಗಳೂ ಸಕಲ ಲೋಕಗಳ ಪ್ರಭುವಾದ ಅಲ್ಲಾಹನಿಗೆ ಎಂದೇ ಆಗಿರುವುದು .');
INSERT INTO "kannada" VALUES(10,11,'10:11','ಜನರು ಹಿತಕ್ಕೆ ತವಕಪಡುವಂತೆ ಅವರಿಗೆ ಕೇಡುಂಟುಮಾಡಲು ಅಲ್ಲಾಹನು ತವಕಪಡುತ್ತಿದ್ದರೆ, ಅವರ ಜೀವನಾವಧಿಯನ್ನು ಮುಗಿಸಿ ಬಿಡಲಾ ಗುತ್ತಿತ್ತು. ಆದುದರಿಂದ ನಮ್ಮ ಭೇಟಿಯನ್ನು ನಿರೀಕ್ಷಿಸದವರನ್ನು ನಾವು ಅವರ ಧಿಕ್ಕಾರದಲ್ಲಿ ಅಲೆದಾಡುತ್ತಿರಲು ಬಿಟ್ಟುಬಿಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(10,12,'10:12','ಮನುಷ್ಯನು ನಿಂತೂ ಕುಳಿತೂ ಮಲಗಿಯೂ ನಮ್ಮೊಡನೆ ಮೊರೆಯಿಡುತ್ತಾನೆ. ಆದರೆ ನಾವು ಅವನ ಕಷ್ಟವನ್ನು ದೂರ ಮಾಡಿದರೆ, ಅವನಿಗೆ ತಟ್ಟಿದ ಸಂಕಷ್ಟದ ವಿಷಯದಲ್ಲಿ ನಮ್ಮೊಡನೆ ಅವನು ಮೊರೆಯಿಡಲೇ ಇಲ್ಲವೋ ಎಂಬಂತೆ ನಡೆದು ಬಿಡುತ್ತಾನೆ. ಹೀಗೆ ಅತಿಕ್ರಮಿಸಿ ನಡೆಯುವವರಿಗೆ ಅವರ ಕೃತ್ಯಗಳನ್ನು ಮನೋಹರಗೊಳಿಸಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(10,13,'10:13','(ಓ ಜನರೇ;) ನಿಮಗಿಂತ ಮುಂಚಿನ ಜನಾಂ ಗಗಳನ್ನು ಅವರು ಅಕ್ರಮವೆಸಗಿದಾಗ ನಾವು ನಾಶ ಮಾಡಿದ್ದೇವೆ. ಅವರ ಸಂದೇಶವಾಹಕರು ಸುವ್ಯಕ್ತ ಪ್ರಮಾಣಗಳನ್ನು ಅವರ ಬಳಿಗೆ ತಂದರು. ಆದರೂ ಅವರು ವಿಶ್ವಾಸವಿಡಲಿಲ್ಲ. ನಾವು ಅಪರಾಧಿಗಳಿಗೆ ಪ್ರತಿಫಲವನ್ನು ಇದೇ ರೀತಿಯಲ್ಲಿ ಕೊಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(10,14,'10:14','ನೀವು ಹೇಗೆ ವರ್ತಿಸುತ್ತೀರೆಂದು ನೋಡಲಿಕ್ಕಾಗಿ ನಾವು ಅವರ ತರುವಾಯ ನಿಮ್ಮನ್ನು ಭೂಮಿ ಯಲ್ಲಿ ಪ್ರತಿನಿಧಿಯನ್ನಾಗಿ ಮಾಡಿದೆವು.');
INSERT INTO "kannada" VALUES(10,15,'10:15','ನಮ್ಮ ಸುವ್ಯಕ್ತ ಪುರಾವೆಗಳನ್ನು ಅವರಿಗೆ ಓದಿ ಕೇಳಿಸುವಾಗ, ನಮ್ಮನ್ನು ಭೇಟಿಯಾಗುವ ನಿರೀಕ್ಷೆಯನ್ನಿರಿಸದವರು, ಇದರ ಬದಲಿಗೆ ಇನ್ನೊಂದು ಖುರ್‍ಆನನ್ನು ತನ್ನಿರಿ, ಇಲ್ಲವೆ ಇದರಲ್ಲಿ ಸ್ವಲ್ಪ ತಿದ್ದುಪಡಿ ಮಾಡಿರಿ ಎಂದು ಹೇಳುತ್ತಾರೆ . (ಓ ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ ನನ್ನ ಕಡೆಯಿಂದ ಅದನ್ನು ಮಾರ್ಪಾಟು ಮಾಡುವುದು ನನಗೆ ಸಲ್ಲದು. ನನಗೆ ಕಳುಹಿಸಲ್ಪಡುವ ಸಂದೇಶಗಳನ್ನು ಮಾತ್ರ ನಾನು ಅನುಸರಿಸುತ್ತೇನೆ. ನಾನು ನನ್ನ ಪ್ರಭುವಿಗೆ ಧಿಕ್ಕಾರ ತೋರಿದರೆ ಒಂದು ಭಯಾನಕ ದಿನದ ಶಿಕ್ಷೆಯನ್ನು ನಾನು ಭಯಪಡುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(10,16,'10:16','ಹೇಳಿರಿ - ಅಲ್ಲಾಹನು (ಇದನ್ನು ಅವತೀರ್ಣ ಗೊಳಿಸದಿರಲು) ಬಯಸಿದ್ದರೆ, ನಾನು ನಿಮಗೆ ಈ ಖುರ್‍ಆನನ್ನು ಓದಿ ಕೇಳಿಸುತ್ತಿರಲಿಲ್ಲ. ಮತ್ತು ಅವನು ನಿಮಗೆ ಇದನ್ನು ತಿಳಿಸುತ್ತಲೂ ಇರಲಿಲ್ಲ. ನಾನು ಇದಕ್ಕೆ ಮುಂಚೆ ನಿಮ್ಮ ನಡುವೆ ಕೆಲವು ಕಾಲ ಕಳೆದಿರುವೆನಲ್ಲ? ನೀವೇನು ಯೋಚಿಸುವುದಿಲ್ಲವೇ? ');
INSERT INTO "kannada" VALUES(10,17,'10:17','ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಳ್ಳು ಸೃಷ್ಟಿಸಿ ಹೊರಿಸುವವನಿಗಿಂತ ಅಥವಾ ವಚನಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವವನಿಗಿಂತ ದೊಡ್ಡ ಅಕ್ರಮಿ ಯಾರಿದ್ದಾರೆ? ನಿಶ್ಚಯವಾಗಿಯೂ ಅಪರಾಧಿಗಳು ಎಂದೂ ವಿಜಯಹೊಂದಲಾರರು.');
INSERT INTO "kannada" VALUES(10,18,'10:18','ಅವರು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಅವರಿಗೆ ಉಪದ್ರವವನ್ನಾಗಲಿ ಉಪಕಾರವನ್ನಾಗಲಿ ಉಂಟು ಮಾಡ ದವುಗಳನ್ನು ಆರಾಧಿಸುತ್ತಾರೆ. ‘ಆ ಆರಾಧ್ಯರು ಅಲ್ಲಾಹನ ಬಳಿ ನಮ್ಮ ಶಿಫಾರಸ್ಸುಗಾರರು’ ಎಂದು ಅವರು ಹೇಳುತ್ತಾರೆ . (ಓ ಪೈಗಂಬ ರರೇ,) ಹೇಳಿರಿ, ‘ಅಲ್ಲಾಹನು ಆಕಾಶಗಳಲ್ಲಾಗಲಿ ಭೂಮಿಯಲ್ಲಾಗಲಿ ತಿಳಿಯದಂತಹ ಸುದ್ದಿಯನ್ನು ನೀವು ಅವನಿಗೆ ತಿಳಿಸುತ್ತೀರಾ?’ ಇವರು ಸಹಭಾಗಿಗಳನ್ನಾಗಿ ಮಾಡುತ್ತಿರುವವುಗಳಿಂದ ಅವನು ಅತಿ ಪಾವನನೂ ಉನ್ನತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(10,19,'10:19','ಮಾನವರು ಒಂದೇ ಸಮುದಾಯವಾಗಿದ್ದರು. ಅನಂತರ ಅವರು ಭಿನ್ನಾಭಿಪ್ರಾಯ ತಾಳಿದರು . ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಒಂದು ವಚನವು ಮೊದಲೇ ಇಲ್ಲದಿರುತ್ತಿದ್ದರೆ ಅವರು ಪರಸ್ಪರ ಭಿನ್ನಾಭಿಪ್ರಾಯ ವಿರಿಸುತ್ತಿರುವ ವಿಷಯದಲ್ಲಿ ಅವರ ನಡುವೆ (ಈಗಾಗಲೇ) ತೀರ್ಮಾನ ಮಾಡಿ ಬಿಡಲಾಗುತ್ತಿತ್ತು.');
INSERT INTO "kannada" VALUES(10,20,'10:20','ಈ ಪ್ರವಾದಿಯ ಮೇಲೆ ಇವನ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಒಂದು ದೃಷ್ಟಾಂತವೇಕೆ ಇಳಿಸಿಕೊಡಲಾ ಗಿಲ್ಲವೆಂದು ಇವರು ಕೇಳುತ್ತಾರೆ. ಅವರೊಡನೆ, ಹೇಳಿರಿ, ಅದೃಶ್ಯ ಜ್ಞಾನ ಅಲ್ಲಾಹನಿಗೆ ಮಾತ್ರವಿದೆ. ಆದ್ದರಿಂದ ನೀವು ಕಾಯುತ್ತಿರಿ, ನಾನೂ ನಿಮ್ಮೊಂದಿಗೆ ಕಾದುಕೊಂಡಿರುತ್ತೇನೆ ಎಂದು.');
INSERT INTO "kannada" VALUES(10,21,'10:21','ಜನರಿಗೆ ಕಷ್ಟತಟ್ಟಿದ ಬಳಿಕ ಕಾರುಣ್ಯದ ಸವಿ ಯನ್ನುಣಿಸಿದಾಗ ಆ ಕೂಡಲೆ ಅವರು ನಮ್ಮ ನಿದರ್ಶನಗಳ ಬಗ್ಗೆ ಕುತಂತ್ರಗಳನ್ನು ಆರಂಭಿಸಿ ಬಿಡುತ್ತಾರೆ. ಹೇಳಿರಿ; ಅಲ್ಲಾಹು ತಂತ್ರಗಾರಿ ಕೆಯಲ್ಲಿ ಅತಿ ಶೀಘ್ರನು. ನಮ್ಮ ದೂತರು ನಿಮ್ಮ ಎಲ್ಲ ಕುತಂತ್ರಗಳನ್ನೂ ಬರೆದಿಡುತ್ತಿದ್ದಾರೆ ಎಂದು.');
INSERT INTO "kannada" VALUES(10,22,'10:22','ನಿಮ್ಮನ್ನು ನೆಲ-ಜಲಗಳಲ್ಲಿ ಚಲಾಯಿಸುವವನೇ ಅವನು. ನೀವು ನಾವೆಗಳಲ್ಲಿರುತ್ತ ಅನುಕೂಲಕರ ಮಾರುತವು ಯಾತ್ರಿಕರನ್ನು ಕೊಂಡೊಯ್ಯುತ್ತ ಆನಂದ ತುಂದಿಲರಾಗಿರುವಾಗ ಹಠಾತ್ತನೆ ಸುಂಟರ ಗಾಳಿಯು ಅವರಿಗೆ ಬಲವಾಗಿ ಬೀಸಿ ಎಲ್ಲ ಕಡೆ ಗಳಿಂದಲೂ ತೆರೆಗಳು ಅಪ್ಪಳಿಸುತ್ತಿರುವಾಗ ಮತ್ತು ಅಪಾಯ ಆವರಿಸಿದೆ ಎಂದವರು ಭಾವಿಸುತ್ತಿರುವಾಗ ಎಲ್ಲರೂ ತಮ್ಮ ಪ್ರಾರ್ಥನೆಯನ್ನು ಅಲ್ಲಾಹನಿಗೆ ಮಾತ್ರ ನಿಷ್ಕಳಂಕ ವಾಗಿರಿಸಿಕೊಂಡು ಅವನೊಡನೆ, ನೀನು ನಮ್ಮನ್ನು ಇದರಿಂದ ಪಾರು ಮಾಡಿದರೆ ನಾವು ಕೃತಜ್ಞರಲ್ಲಾಗುವೆವು ಎಂದು ಪ್ರಾರ್ಥಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(10,23,'10:23','ಆದರೆ, ಅವನು (ಅಲ್ಲಾಹು) ಅವರನ್ನು ರಕ್ಷಿಸಿದಾಗ ಅವರು ನ್ಯಾಯವಿಲ್ಲದೆ ಭೂಮಿಯಲ್ಲಿ ಬಂಡಾಯವೇಳುತ್ತಾರೆ. ಓ ಜನರೇ, ನಿಮ್ಮ ಈ ಬಂಡಾಯವು ನಿಮ್ಮ ವಿರುದ್ಧವೇ ಆಗಿದೆ. ಐಹಿಕ ಜೀವನದ ಸುಖಾನಂದ ಮಾತ್ರ ಇದರಿಂದ ಸಿಗುತ್ತದೆ. ಆದರೆ ಅನಂತರ ನಿಮಗೆ ನಮ್ಮ ಕಡೆಗೆ ಮರಳಿ ಬರಲಿಕ್ಕಿದೆ. ಆಗ ನೀವು ಮಾಡುತ್ತಿದ್ದುದನ್ನು ನಾವು ನಿಮಗೆ ತಿಳಿಸಲಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(10,24,'10:24','ನಾವು ಆಕಾಶದಿಂದ ಮಳೆಗೆರೆದು, ಆ ಮೂಲಕ ಜನರೂ, ಜಾನುವಾರುಗಳೂ ತಿನ್ನುವ ಭೂಮಿ ಯ ಬೆಳೆಯು ಹುಲುಸಾಗಿ ಬೆಳೆಯಿತು. ಕೊನೆಗೆ ಭೂಮಿಯು ಅದರ ಅಲಂಕಾರ ಹೊದ್ದು ಮಿರುಗಿ ಬೆಳಗಿತು. ಹಾಗೂ ಅವುಗಳ ಮಾಲೀಕರು ನಾವೀಗ ಇದರಿಂದ ಫಲ ಹೊಂದಲು ಸಮರ್ಥರಾಗಿದ್ದೇವೆಂದು ಭಾವಿಸುತ್ತಿದ್ದಾಗ ಹಠಾತ್ತನೆ ರಾತ್ರೆ ಅಥವಾ ಹಗಲಲ್ಲಿ ನಮ್ಮ ಅಪ್ಪಣೆ ಬಂದು ಬಿಟ್ಟಿತು ಮತ್ತು ನಿನ್ನೆ ಅಲ್ಲೇನೂ ಇರಲೇ ಇಲ್ಲವೋ ಎಂಬಂತೆ ನಾವು ಅದನ್ನು ಅಳಿಸಿ ಬಿಟ್ಟೆವು. ಕೇವಲ ಹೀಗಿದೆ ಐಹಿಕ ಜೀವನದ ಉಪಮೆ. ವಿವೇಚಿಸುವ ಜನರಿಗೆ ನಾವು ಈ ರೀತಿಯಲ್ಲಿ ದೃಷ್ಟಾಂತಗಳನ್ನು ಸ್ಪಷ್ಟಪಡಿಸುತ್ತಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(10,25,'10:25','ಅಲ್ಲಾಹು ನಿಮ್ಮನ್ನು ಶಾಂತಿ ಸದನಕ್ಕೆ ಕರೆಯುತ್ತಿದ್ದಾನೆ. ಅವನು ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ಸನ್ಮಾರ್ಗಕ್ಕೆ ಸೇರಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(10,26,'10:26','ಸುಕೃತ ಮಾಡಿದವರಿಗೆ ಅತ್ಯಂತ ಉತ್ತಮ ಪ್ರತಿಫಲವೂ ಇನ್ನೂ ಹೆಚ್ಚಿನ ಅನುಗ್ರಹವೂ ಇದೆ. ಅವರಿಗೆ ಕಳಂಕವೋ, ನಿಂದೆಯೋ ಆವರಿಸಲಾರದು. ಅವರೇ ಸ್ವರ್ಗದವರು. ಅಲ್ಲಿ ಅವರು ಶಾಶ್ವತರು.');
INSERT INTO "kannada" VALUES(10,27,'10:27','ಪಾಪ ಗಳಿಸಿದವರು ಅವರ ಪಾಪಗಳ ತಕ್ಕಂತೆ ಪ್ರತಿಫಲವು ಅದಕ್ಕೆ ಸಮಾನವಾದುದೇ ಆಗಿದೆ. ಅವಮಾನವು ಅವರನ್ನು ಆವರಿಸುವುದು. ಅವರನ್ನು ಅಲ್ಲಾಹನಿಂದ ರಕ್ಷಿಸುವವನಾರೂ ಇರಲಾರನು. ಕಾರಿರುಳ ತುಂಡುಗಳಿಂದ ಅವರ ಮುಖಗಳನ್ನು ಮುಚ್ಚಿದಂತಿದೆ. ಅವರೇ ನರಕದವರು. ಅದರಲ್ಲಿ ಅವರು ಶಾಶ್ವತರು.');
INSERT INTO "kannada" VALUES(10,28,'10:28','ಅವರೆಲ್ಲರನ್ನೂ ನಾವು ಒಟ್ಟಾಗಿ ಸೇರಿಸುವ ಮತ್ತು ಬಹುದೇವಾರಾಧಕರೊಡನೆ, ನೀವೂ ನೀವು ಮಾಡಿಕೊಂಡ ಸಹಭಾಗಿಗಳೂ ಅಲ್ಲೇ ನಿಲ್ಲಿರೆಂದು ಹೇಳುವ ದಿನ. ಆ ಬಳಿಕ ನಾವು ಅವರನ್ನು ಪರಸ್ಪರ ಬೇರ್ಪಡಿಸುವೆವು. ಅವರ ಸಹಭಾಗಿಗಳು ಹೇಳುವರು, ನೀವು ನಮ್ಮನ್ನು ಆರಾಧಿಸುತ್ತಿರಲಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(10,29,'10:29','ಆದ್ದರಿಂದ ನಮ್ಮ ಮತ್ತು ನಿಮ್ಮ ನಡುವೆ ಸಾಕ್ಷಿಯಾಗಿ ಅಲ್ಲಾಹನೇ ಸಾಕು. ನಿಮ್ಮ ಆರಾಧನೆಯ ಬಗ್ಗೆ ನಾವು ಸಂಪೂರ್ಣ ಅಲಕ್ಷರಾಗಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(10,30,'10:30','ಅಲ್ಲಿ ಪ್ರತಿಯೊಂದು ದೇಹವು ತಾನು ಮೊದಲೇ ಮಾಡಿದ ಕರ್ಮದ ಫಲವನ್ನು ಅನುಭವಿಸಿ ತಿಳಿಯುವುದು. ಅವರು ತಮ್ಮ ನೈಜ ಒಡೆ ಯನಾದ ಅಲ್ಲಾಹನ ಕಡೆಗೆ ಮರಳಿಸಲ್ಪಡುವರು ಮತ್ತು ಅವರು ಸೃಷ್ಟಿಸಿಕೊಂಡದ್ದೆಲ್ಲವೂ ಅವರಿಂದ ಕಾಣೆಯಾಗುವುವು .');
INSERT INTO "kannada" VALUES(10,31,'10:31','ಕೇಳಿರಿ- ಆಕಾಶಗಳಿಂದಲೂ ಭೂಮಿಯಿಂದಲೂ ನಿಮಗೆ ಆಹಾರ ನೀಡುವವನು ಯಾರು? ಶ್ರವಣ ಮತ್ತು ದೃಷ್ಟಿಗಳನ್ನು ಅಧೀನಗೊಳಿಸಿದ್ದು ಯಾರು? ನಿರ್ಜಿವಿಯಿಂದ ಸಜೀವಿಯನ್ನು ಸಜೀವಿಯಿಂದ ನಿರ್ಜೀವಿಯನ್ನು ಹೊರತರುವುದು ಯಾರು? ಕಾರ್ಯವನ್ನು ನಿಯಂತ್ರಿಸುವುದು ಯಾರು? ಅಲ್ಲಾಹು ಎಂದೇ ಅವರು ಹೇಳುವರು. ಹಾಗಾದರೆ ಹೇಳಿರಿ; ಇಷ್ಟಾದರೂ ನೀವು ಜಾಗೃತರಾಗುವುದಿಲ್ಲವೇ? ');
INSERT INTO "kannada" VALUES(10,32,'10:32','ಅವನೇ ನಿಮ್ಮ ಯಥಾರ್ಥ ಪ್ರಭುವಾದ ಅಲ್ಲಾಹು. ಹೀಗಿದ್ದು ಸತ್ಯದ ಬಳಿಕ ಪಥ ಭ್ರಷ್ಟತೆಯ ವಿನಾ ಬೇರೇನು ಉಳಿದಿದೆ? ಹೇಗೆ ನೀವು ತಪ್ಪಿಸಲ್ಪಡುತ್ತಿರುವಿರಿ?');
INSERT INTO "kannada" VALUES(10,33,'10:33','ಆ ಪ್ರಕಾರ ಕರ್ಮಭ್ರಷ್ಟರ ವಿಷಯದಲ್ಲಿ, ಅವರು ವಿಶ್ವಾಸವಿರಿಸಲಾರರೆಂಬ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಮಾತು ನಿಜವಾಯಿತು.');
INSERT INTO "kannada" VALUES(10,34,'10:34','(ಪ್ರವಾದಿಯವರೇ) ಹೇಳಿರಿ; ಸೃಷ್ಟಿಯ ಆರಂಭ ಮಾಡಿ, ನಂತರ ಅದನ್ನು ಪುನರಾವರ್ತಿಸುವ ಯಾರಾದರೂ ನೀವು ಸಹಭಾಗಿಗಳಾಗಿ ಸೇರಿಸಿ ದವರ ಕೂಟದಲ್ಲಿ ಇದ್ದಾರೇನು? ಹೇಳಿರಿ - ಸೃಷ್ಟಿಯನ್ನು ಆರಂಭಿಸುವವನೂ ಅದನ್ನು ಪುನರಾ ವರ್ತಿಸುವವನೂ ಕೇವಲ ಅಲ್ಲಾಹನೇ ಆಗಿರುವನು. ಹೀಗಿರುತ್ತ ನೀವು ಹೇಗೆ ತಪ್ಪಿಸಲ್ಪ ಡುತ್ತಿರುವಿರಿ?');
INSERT INTO "kannada" VALUES(10,35,'10:35','ಹೇಳಿರಿ, ಸತ್ಯದ ಕಡೆಗೆ ಮಾರ್ಗದರ್ಶನ ನೀಡುವ ಯಾರಾದರೂ ನೀವಿರಿಸಿಕೊಂಡ ಸಹ ಭಾಗಿಗಳ ಕೂಟದಲ್ಲಿ ಇದ್ದಾರೇನು? ಹೇಳಿರಿ - ಅಲ್ಲಾಹನೇ ಸತ್ಯಕ್ಕೆ ದಾರಿ ತೋರುತ್ತಾನೆ. ಹೀಗಿದ್ದು ಸತ್ಯದ ಕಡೆಗೆ ಮಾರ್ಗದರ್ಶನ ಮಾಡು ವವನೋ ಅಥವಾ ತನಗೆ ಮಾರ್ಗದರ್ಶನ ಮಾಡಿಸದಿದ್ದರೆ ಸ್ವತಃ ಸನ್ಮಾರ್ಗ ಹೊಂದದವನೋ ಯಾರು ಅನುಸರಣೆಗೆ ಹೆಚ್ಚು ಅರ್ಹನು? ನಿಮ ಗೇನಾಗಿದೆ? ಹೇಗೆ ನೀವು ತೀರ್ಮಾನ ಕೈಗೊಳ್ಳುತ್ತೀರಿ?”');
INSERT INTO "kannada" VALUES(10,36,'10:36','ಅವರ ಪೈಕಿ ಹೆಚ್ಚಿನವರು ಊಹೆಯನ್ನು ಮಾತ್ರ ಹಿಂಬಾಲಿಸುತ್ತಾರೆ. ವಸ್ತುತಃ ಊಹೆಯು ಸತ್ಯದ ಸ್ಥಾನದಲ್ಲಿ ಒಂದಿಷ್ಟೂ ಪರ್ಯಾಪ್ತವಲ್ಲ. ಅಲ್ಲಾಹನು ಇವರು ಮಾಡುತ್ತಿರುವುದನ್ನೆಲ್ಲಾ ಚೆನ್ನಾಗಿ ಅರಿಯುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(10,37,'10:37','ಈ ಖುರ್‍ಆನ್ ಅಲ್ಲಾಹನಲ್ಲದವರಿಂದ ನಿರ್ಮಿಸಲಾಗಿಲ್ಲ. ನಿಜವಾಗಿ ಅದು ಹಿಂದೆ ಬಂದಿದ್ದ ದಿವ್ಯ ಸಂದೇಶದ ದೃಢೀಕರಣವೂ ದಿವ್ಯ ಗ್ರಂಥದ ವಿವರಣೆಯೂ ಆಗಿರುತ್ತದೆ. ಅದರಲ್ಲಿ ಯಾವ ಸಂದೇಹವೂ ಇಲ್ಲ. ಅದು ಸರ್ವಲೋಕಾಧಿಪತಿಯ ಕಡೆಯಿಂದ ಬಂದಿದೆ.');
INSERT INTO "kannada" VALUES(10,38,'10:38','ಅದಲ್ಲ, ಇದನ್ನು ಪೈಗಂಬರರು ಸ್ವತಃ ರಚಿಸಿ ಕೊಂಡರೆಂದು ಇವರು ಹೇಳುತ್ತಾರೆಯೇ? ಹೇಳಿರಿ; ನೀವು (ನಿಮ್ಮ ಈ ಆಪಾದನೆಯ ಬಗ್ಗೆ) ಸತ್ಯವಾದಿಗಳಾಗಿದ್ದರೆ ಇದಕ್ಕೆ ಸಮಾನವಾದ ಒಂದು ಅಧ್ಯಾಯವನ್ನು ರಚಿಸಿ ತನ್ನಿರಿ ಮತ್ತು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಯಾರನ್ನು ನಿಮಗೆ ಕರೆಯಲು ಸಾಧ್ಯವಿದೆಯೋ ಅವರನ್ನೆಲ್ಲ ಕರೆದುಕೊಳ್ಳಿರಿ .');
INSERT INTO "kannada" VALUES(10,39,'10:39','ಆದರೆ ಅದರ (ಖುರ್‍ಆನಿನ) ಜ್ಞಾನವನ್ನು ಅವರು ಸೂಕ್ಷ್ಮವಾಗಿ ತಿಳಿದಿಲ್ಲದ ಕಾರಣ ಅದರ ಭವಿಷ್ಯ ಫಲಗಳು ಅವರಿಗೆ ಬಾರದಿರುವ ಸ್ಥಿತಿಯಲ್ಲಿ ಇವರು ಸುಳ್ಳಾಗಿಸಿದರು. ಇದೇ ರೀತಿಯಲ್ಲಿ ಇವರ ಪೂರ್ವಿಕರೂ ಸುಳ್ಳಾಗಿಸಿದ್ದರು. ಆದ್ದರಿಂದ ಆ ಅಕ್ರಮಿಗಳ ಗತಿಯೇನಾಯಿತೆಂದು ನೋಡಿರಿ.');
INSERT INTO "kannada" VALUES(10,40,'10:40','ಇದರಲ್ಲಿ (ಖುರ್‍ಆನಿನಲ್ಲಿ) ಕೆಲವರು ವಿಶ್ವಾಸವಿರಿಸುವರು ಮತ್ತು ಇನ್ನು ಕೆಲವರು ವಿಶ್ವಾಸವಿರಿಸಲಾರರು. ನಿಮ್ಮ ಪ್ರಭುವು ಗೊಂದಲಕಾರಿಗಳನ್ನು ಚೆನ್ನಾಗಿ ಬಲ್ಲವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(10,41,'10:41','ಇವರು ನಿಮ್ಮನ್ನು ಸುಳ್ಳಾಗಿಸುತ್ತಿದ್ದರೆ ಹೇಳಿರಿ;- `ನನ್ನ ಕರ್ಮ ನನಗೆ, ನಿಮ್ಮ ಕರ್ಮ ನಿಮಗೆ. ನಾನು ಮಾಡುತ್ತಿರುವ ಕರ್ಮದ ಹೊಣೆಯಿಂದ ನೀವು ಮುಕ್ತರು. ನೀವು ಮಾಡುತ್ತಿರುವ ಕರ್ಮದ ಹೊಣೆಯಿಂದ ನಾನು ಮುಕ್ತನು.');
INSERT INTO "kannada" VALUES(10,42,'10:42','ಅವರ ಪೈಕಿ ನಿಮ್ಮ ಕಡೆಗೆ ಶೃದ್ಧೆಯಿಂದ ಕೇಳಿಸಿಕೊಳ್ಳುವವರೂ ಇದ್ದಾರೆ. ಆದರೆ ಕಿವುಡರು ಏನನ್ನೂ ಗ್ರಹಿಸದಿದ್ದರೂ ನೀವು ಅವರಿಗೆ ಕೇಳಿಸುವಿರಾ? ');
INSERT INTO "kannada" VALUES(10,43,'10:43','ಅವರ ಪೈಕಿ ನಿಮ್ಮತ್ತ ದಿಟ್ಟಿಸಿ ನೋಡುವವರೂ ಇದ್ದಾರೆ. ಆದರೆ ಕುರುಡರಿಗೆ ಏನೂ ಕಾಣದಿದ್ದರೂ ನೀವು ಅವರನ್ನು ಸನ್ಮಾರ್ಗಕ್ಕೆ ಹಚ್ಚುವಿರಾ?');
INSERT INTO "kannada" VALUES(10,44,'10:44','ವಾಸ್ತವದಲ್ಲಿ ಅಲ್ಲಾಹ್ ಜನರಿಗೆ ಎಳ್ಳಷ್ಟೂ ಅನೀತಿ ತೋರಲಾರನು. ಆದರೆ ಜನರು ಪರಸ್ಪರ ಅನೀತಿ ತೋರುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(10,45,'10:45','ಅಲ್ಲಾಹ್ ಅವರನ್ನು ಒಟ್ಟು ಸೇರಿಸುವ ದಿನದಂದು ಹಗಲಿನ ಒಂದು ಕ್ಷಣಕಾಲ ಮಾತ್ರ (ಇಹಲೋಕದಲ್ಲಿ) ತಾವು ತಂಗಿದ್ದಂತೆ ತೋರುವುದು. ಅವರು ಪರಸ್ಪರ ಗುರುತಿಸುವರು. ಅಲ್ಲಾಹನ ಭೇಟಿಯನ್ನು ಸುಳ್ಳಾಗಿಸಿದವರು ನಿಜಕ್ಕೂ ನಷ್ಟ ಹೊಂದಿದರು. ಅವರು ಸತ್ಪಥಿಕರಾಗಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(10,46,'10:46','(ನಬಿಯರೇ) ಅವರಿಗೆ ನಾವು ತಾಕೀತು ಮಾಡು ತ್ತಿರುವ ಶಿಕ್ಷೆಗಳಲ್ಲಿ ಕೆಲವನ್ನು ನಾವು (ನಿಮ್ಮ ಜೀವಮಾನದಲ್ಲೇ) ನಿಮಗೆ ತೋರಿಸಬಹುದು ಅಥವಾ ಅದಕ್ಕಿಂತ ಮುಂಚೆಯೇ ನಿಮ್ಮನ್ನು ಮರಣಗೊಳಿಸಬಹುದು. ಹೇಗಿದ್ದರೂ ಇವರು ನಮ್ಮ ಕಡೆಗೆ ಬರಲಿಕ್ಕಿದೆ. ನಂತರ ಇವರು ಮಾಡುತ್ತಿರುವುದಕ್ಕೆಲ್ಲ ಅಲ್ಲಾಹು ಸಾಕ್ಷಿಯಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(10,47,'10:47','ಪ್ರತಿಯೊಂದು ಸಮುದಾಯಕ್ಕೂ ಓರ್ವ ಸಂದೇಶವಾಹಕರಿರುತ್ತಾನೆ. ಒಂದು ಸಮುದಾಯದ ಬಳಿಗೆ ಅದರ ಸಂದೇಶವಾಹಕರು ಬಂದಾಗ ಅವರ ನಡುವೆ ನ್ಯಾಯಪೂರ್ಣವಾಗಿ ವಿಧಿಸಲಾ ಗುತ್ತದೆ. ಅವರ ಮೇಲೆ ಅಕ್ರಮವೆಸಗಲಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(10,48,'10:48','ಅವರು (ಸತ್ಯನಿಷೇಧಿಗಳು) ಹೇಳುತ್ತಾರೆ; ಈ ತಾಕೀತು ನೆರವೇರುವುದು ಯಾವಾಗ? ನೀವು ಸತ್ಯವಂತರಾಗಿದ್ದರೆ ಹೇಳಿರಿ.');
INSERT INTO "kannada" VALUES(10,49,'10:49','ಹೇಳಿರಿ, ಸ್ವತಃ ನನ್ನ ಲಾಭ-ಹಾನಿಗಳೂ ನನ್ನ ಅಧೀನದಲ್ಲಿಲ್ಲ. ಅಲ್ಲಾಹನು ಇಚ್ಛಿಸಿದ್ದು ಹೊರತು. ಪ್ರತಿಯೊಂದು ಸಮುದಾಯಕ್ಕೂ ಒಂದು ನಿಶ್ಚಿತ ಕಾಲಾವಧಿ ಇದೆ. ಆ ಕಾಲಾವಧಿ ಮುಗಿದಾಗ ಕ್ಷಣ ಮಾತ್ರವೂ ಹಿಂದೆ ಬರಲಾರರು, ಮುಂದೆ ಯೂ ಸರಿಯಲಾರರು.');
INSERT INTO "kannada" VALUES(10,50,'10:50','(ಪ್ರವಾದಿಯವರೇ) ಹೇಳಿರಿ; - ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆ ಯು ರಾತ್ರೆ ಅಥವಾ ಹಗಲಲ್ಲಿ ಹಠಾತ್ತನೆ ಬಂದೆರ ಗಿದ ನಂತರ ಶಿಕ್ಷಾರ್ಹರು ಆ ಶಿಕ್ಷೆಯ ವಿಚಾರ ದಲ್ಲಿ ತವಕಪಡುವ ಅಗತ್ಯವಾದರೂ ಏನಿದೆ?');
INSERT INTO "kannada" VALUES(10,51,'10:51','ಇನ್ನು ಅದು (ಶಿಕ್ಷೆ) ನಿಮ್ಮ ಮೇಲೆ ಬಂದೆರಗುವಾಗಲೇ ನೀವು ಅದನ್ನು ನಂಬುವಿರಾ? ನೀವು ಈ ಶಿಕ್ಷೆಗೆ ತ್ವರೆ ಮಾಡುತ್ತಿದ್ದೀರಲ್ಲ. ಇದೀಗ ನಂಬಿ ಫಲವುಂಟೇ?');
INSERT INTO "kannada" VALUES(10,52,'10:52','ಆ ಬಳಿಕ ಅಕ್ರಮಿಗಳೊಡನೆ ಹೇಳಲಾಗುವುದು. ನೀವು ಶಾಶ್ವತ ಶಿಕ್ಷೆಯನ್ನು ಸವಿಯಿರಿ. ನೀವು ಗಳಿಸುತ್ತಿದ್ದುದರ ಪ್ರತಿಫಲದ ಹೊರತು ಇನ್ನಾವ ಪ್ರತಿಫಲವನ್ನು ನಿಮಗೆ ಕೊಡಲಾಗುವುದೇ?');
INSERT INTO "kannada" VALUES(10,53,'10:53','`ನೀವು ಹೇಳುತ್ತಿರುವುದು ನಿಜಕ್ಕೂ ಸತ್ಯವೇ? ಎಂದು ಅವರು ಕೇಳುತ್ತಾರೆ. ಹೇಳಿರಿ; `ನನ್ನ ಪ್ರಭುವಿನಾಣೆ, ಇದು ಖಂಡಿತವಾಗಿಯೂ ಸತ್ಯ. ತಡೆದು ಸೋಲಿಸುವ ಸಾಮಥ್ರ್ಯ ನಿಮಗಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(10,54,'10:54','ಅಕ್ರಮವೆಸಗಿದ ಪ್ರತಿಯೊಬ್ಬನ ಬಳಿ ಭೂಮಿಯ ಸಕಲ ಸಂಪತ್ತು ಇದ್ದರೂ ಅವನು ಅದನ್ನೆಲ್ಲ ಪ್ರಾಯಶ್ಚಿತ್ತವಾಗಿ ಕೊಡುತ್ತಿದ್ದನು . ಅವರು ಆ ಶಿಕ್ಷೆಯನ್ನು ಕಂಡಾಗ ಖೇದವನ್ನು ಮನದಲ್ಲೇ ಬಚ್ಚಿಡುವರು. ಆದರೆ ಅವರ ನಡುವೆ ನ್ಯಾಯೋಚಿತವಾದ ತೀರ್ಮಾನ ಕೈಕೊಳ್ಳಲಾ ಗುವುದು. ಅವರ ಮೇಲೆ ಯಾವ ಅಕ್ರಮವೂ ಆಗಲಾರದು.');
INSERT INTO "kannada" VALUES(10,55,'10:55','ತಿಳಿಯಿರಿ! ಆಕಾಶಗಳಲ್ಲೂ ಭೂಮಿಯಲ್ಲೂ ಇರುವುದೆಲ್ಲವೂ ಅಲ್ಲಾಹನದೇ. ತಿಳಿಯಿರಿ ಅಲ್ಲಾಹನ ವಾಗ್ದಾನ ಸತ್ಯವಾದುದು. ಆದರೆ ಹೆಚ್ಚಿನವರು ಅರಿತಿರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(10,56,'10:56','ಅವನೇ ಜೀವದಾನ ಮಾಡುತ್ತಾನೆ. ಅವನೇ ಮರಣ ಕೊಡುತ್ತಾನೆ. ಮತ್ತು ಅವನ ಕಡೆಗೇ ನಿಮ್ಮನ್ನು ಮರಳಿಸಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(10,57,'10:57','ಜನರೇ, ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಸದುಪದೇಶ ಹಾಗೂ ಮನಸ್ಸುಗಳೊಳಗಿನ ಕಾಯಿಲೆ ಗಳಿಗೆ ಶಮನವೂ ನಿಮಗೆ ಬಂದಿದೆ. ಮತ್ತು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಸನ್ಮಾರ್ಗದರ್ಶನವೂ ಕೃಪೆಯೂ ಆಗಿರುತ್ತದೆ .');
INSERT INTO "kannada" VALUES(10,58,'10:58','(ಓ ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ; ಇದನ್ನು ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದು ಅಲ್ಲಾಹನ ಔದಾರ್ಯ ಮತ್ತು ಕೃಪೆಯಾಗಿರುತ್ತದೆ. ಈ ಮೂಲಕ ಜನರು ಸಂತೋಷಪಟ್ಟುಕೊಳ್ಳಲಿ . ಅದು ಅವರು ಶೇಖರಿಸುತ್ತಿರುವ ಎಲ್ಲ ವಸ್ತುಗಳಿಗಿಂತಲೂ ಉತ್ತಮವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(10,59,'10:59','(ಓ ಪೈಗಂಬರರೇ!) ಇವರೊಡನೆ ಹೇಳಿರಿ; `ಅಲ್ಲಾಹನು ನಿಮಗಾಗಿ ಇಳಿಸಿದ್ದ ಆಹಾರದ ಪೈಕಿ ನೀವಾಗಿಯೇ ಕೆಲವನ್ನು ನಿಷೇಧಗೊಳಿಸಿದಮತ್ತು ಕೆಲವನ್ನು ಅನುವದನೀಯಗೊಳಿಸಿ ದುದರ ಬಗ್ಗೆ ನೀವು ವಿವೇಚಿಸಿದ್ದೀರಾ ? ಕೇಳಿರಿ; ಅಲ್ಲಾಹನು ನಿಮಗೆ ಇದರ ಅನುಮತಿ ನೀಡಿದ್ದನೋ ಅಥವಾ ನೀವು ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಳ್ಳಾರೋಪ ಹೊರಿಸುತ್ತಿರುವಿರೋ?');
INSERT INTO "kannada" VALUES(10,60,'10:60','ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಳ್ಳಾರೋಪ ಹೊರಿಸುವವರ ಊಹೆಯು ನಿರ್ಣಾಯಕ ದಿನದಂದು ಹೇಗಿರುವುದು? ಅಲ್ಲಾಹನು ಜನರ ಮೇಲೆ ಔದಾರ್ಯದಾತನು. ಆದರೆ ಹೆಚ್ಚಿನವರು ಕೃತಜ್ಞತೆ ತೋರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(10,61,'10:61','(ಓ ಪೈಗಂಬರರೇ,) ನೀವು ಯಾವುದೇ ಕಾರ್ಯದಲ್ಲಿ ಏರ್ಪಟ್ಟಿದ್ದರೂ ಖುರ್‍ಆನಿನಿಂದ ಅದರ ಬಗ್ಗೆ ಏನನ್ನು ಕೇಳಿಸುತ್ತಿದ್ದರೂ ಮತ್ತು (ಓ, ಜನರೇ,) ನೀವೂ ಯಾವುದೇ ಕರ್ಮವನ್ನು ಮಾಡುತ್ತಲಿದ್ದರೂ ಆ ವೇಳೆ ನಿಮ್ಮ ಮೇಲೆ ಸಾಕ್ಷಿಯಾಗಿ ನಾವು ಇರದೇ ಇಲ್ಲ. ನಿಮ್ಮ ಪ್ರಭುವಿನ ದೃಷ್ಟಿಯಿಂದ ಅಣುತೂಕದಷ್ಟೂ ಮರೆಯಾಗಿಲ್ಲ. ಭೂಮಿಯಲ್ಲೂ ಆಕಾಶದಲ್ಲೂ ಅಣುವಿಗಿಂತ ಸಣ್ಣದಿರಲಿ, ದೊಡ್ಡದಿರಲಿ ಎಲ್ಲವೂ ಸ್ಪಷ್ಟ ಗ್ರಂಥದಲ್ಲಿ ದಾಖಲುಗೊಂಡಿದೆ .');
INSERT INTO "kannada" VALUES(10,62,'10:62','ತಿಳಿಯಿರಿ, ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹನ ಔಲಿಯಾಗಳಿಗೆ ಯಾವತ್ತೂ ಭಯವಿಲ್ಲ. ಅವರು ದುಃಖಿಸುವವರೂ ಅಲ್ಲ.');
INSERT INTO "kannada" VALUES(10,63,'10:63','ಅವರು ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿಕೊಂಡವರು ಮತ್ತು ಸೂಕ್ಷತೆ ಪಾಲಿಸುವವರು.');
INSERT INTO "kannada" VALUES(10,64,'10:64','ಇಹ-ಪರ ಜೀವನದಲ್ಲ್ಲಿ ಅವರಿಗೆ ಭಾರೀ ಶುಭವಾರ್ತೆ ಇದೆ . ಅಲ್ಲಾಹನ ವಚನಗಳಿಗೆ ಬದಲಾವಣೆಯಿಲ್ಲ. ಅದೇ ಅತಿ ಹಿರಿದಾದ ಯಶಸ್ಸು.');
INSERT INTO "kannada" VALUES(10,65,'10:65','(ಓ ಪೈಗಂಬರರೇ,) ಅವರ ಮಾತು ನಿಮ್ಮನ್ನು ದುಃಖಕ್ಕೀಡು ಮಾಡದಿರಲಿ, ಪ್ರತಿಷ್ಟೆಯು ಸರ್ವ ಸಂಪೂರ್ಣವಾಗಿ ಅಲ್ಲಾಹನದ್ದು. ಅವನುಸರ್ವಶ್ರುತನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(10,66,'10:66','ತಿಳಿಯಿರಿ - ಆಕಾಶಗಳಲ್ಲಿರುವವರಿರಲಿ ಅಥವಾ ಭೂಮಿಯ ಮೇಲಿರುವವರಿರಲಿ ಎಲ್ಲರೂ ಅಲ್ಲಾಹನ ಅಧೀನರು, ಅಲ್ಲಾಹನ ಹೊರತು (ಇತರರನ್ನು) ಆರಾಧಿಸುತ್ತಿರುವವರು (ನೈಜ) ಸಹಭಾಗಿಗಳನ್ನು ಹಿಂಬಾಲಿಸುತ್ತಿಲ್ಲ. ಅವರು ಬರೇ ಊಹೆಯನ್ನಷ್ಟೇ ಹಿಂಬಾಲಿಸುತ್ತಾರೆ. ಅವರು ಅದರಲ್ಲಿ ಸುಳ್ಳನ್ನೇ ಅನುಸರಿಸುತ್ತಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(10,67,'10:67','ನೀವು ಪ್ರಶಾಂತಿಯನ್ನು ಪಡೆಯಲಾಗುವಂತೆ ನಿಮಗಾಗಿ ರಾತ್ರಿಯನ್ನು ಮಾಡಿದವನೂ ಪ್ರಕಾಶಮಾನವಾಗಿ ಹಗಲನ್ನು ಮಾಡಿದವನೂ ಅಲ್ಲಾಹನೇ ಆಗಿರುತ್ತಾನೆ. ಇದರಲ್ಲಿ ಆಲಿಸುವ ಜನರಿಗೆ ದೃಷ್ಪಾಂತಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(10,68,'10:68','ಅಲ್ಲಾಹನು ತನಗೋರ್ವ ಪುತ್ರನನ್ನು ಮಾಡಿ ಕೊಂಡಿದ್ದಾನೆಂದು ಅವರು ಹೇಳಿದರು. ಅದ ರಿಂದ ಅವನು ಮುಕ್ತನು. ಪರಿಶುದ್ಧನು. ಪರಾಶ್ರಯ ರಹಿತನು. ಭೂಮಿ ಮತ್ತು ಆಕಾಶಗಳಲ್ಲಿರುವುದೆಲ್ಲವೂ ಅವನದ್ದು. ಇದಕ್ಕೆ (ದೇವನಿಗೆ ಸಂತಾನವಿದೆ ಎಂಬ ಮಾತಿಗೆ) ನಿಮ್ಮ ಬಳಿ ಯಾವುದೇ ಆಧಾರ ಪ್ರಮಾಣ ವಿಲ್ಲ. ಅಲ್ಲಾಹನ ಬಗ್ಗೆ ನಿಮಗೆ ತಿಳಿಯದಿದ್ದುದನ್ನು ಹೇಳುವಿರಾ?');
INSERT INTO "kannada" VALUES(10,69,'10:69','(ಓ ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ; ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಳ್ಳಾರೋಪ ಹೊರಿಸುವವರು ಯಶಸ್ವಿಯಾಗಲಾರರು.');
INSERT INTO "kannada" VALUES(10,70,'10:70','(ಅವರಿಗಿರುವುದು) ಇಹಲೋಕ ಜೀವನದ ಸುಖಾನುಭವ ಮಾತ್ರ. ಆಮೇಲೆ ಅವರು ನಮ್ಮ ಕಡೆಗೆ ಮರಳಬೇಕಾಗಿದೆ ಅನಂತರ ಅವರು ಸತ್ಯನಿಷೇಧ ಹೊಂದಿದ್ದ ಕಾರಣಕ್ಕಾಗಿ ನಾವು ಅವರನ್ನು ಘೋರ ಯಾತನೆಗೆ ಗುರಿಪಡಿಸುವೆವು.');
INSERT INTO "kannada" VALUES(10,71,'10:71','ಇವರಿಗೆ ನೂಹರ ಸಮಾಚಾರ ಓದಿ ಕೇಳಿಸಿರಿ. ಅವರು ತಮ್ಮ ಜನಾಂಗಕ್ಕೆ ಹೇಳಿದ ಸಂದರ್ಭ. ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧವರೇ, ನನ್ನ ಸಾನಿಧ್ಯವೂ ಅಲ್ಲಾಹನ ದೃಷ್ಟಾಂತಗಳ ಕುರಿತ ನನ್ನ ವಿಜ್ಞಾಪನೆಯೂ ನಿಮಗೆ ದೊಡ್ಡ ಭಾರವಾಗಿರುವುದಾದರೆ ಅಲ್ಲಾಹನ ಮೇಲೆ ನಾನು ಭರವಸೆಯಿಸಿದ್ದೇನೆ. ನಿಮ್ಮ ಸಹಭಾಗಿ ಗಳನ್ನು ಜತೆಗೂಡಿಸಿಕೊಂಡು ಒಂದು ಒಮ್ಮತದ ನಿರ್ಧಾರಕ್ಕೆ ಬನ್ನಿರಿ. ಅನಂತರ ಅದನ್ನು ನನ್ನ ವಿರುದ್ಧ ಕಾರ್ಯಾಚರಣೆಗೆ ತನ್ನಿರಿ ನನಗೆ ಕಾಲಾವಕಾಶ ಕೊಡಲೇ ಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(10,72,'10:72','ಇನ್ನು ನೀವು (ನನ್ನ ಉಪದೇಶದಿಂದ) ವಿಮುಖರಾದರೆ, ನಾನು ನಿಮ್ಮಿಂದ ಯಾವುದೇ ಪ್ರತಿಫಲವನ್ನು ಕೇಳಿಲ್ಲ. ನನ್ನ ಪ್ರತಿಫಲವು ಅಲ್ಲಾಹನಲ್ಲಿದೆ. ನಾನು (ಅಲ್ಲಾಹನಿಗೆ) ವಿಧೇಯರಾದವರಲ್ಲಿ ಸೇರುವಂತೆ ನನಗೆ ಆಜ್ಞಾಪಿಸಲಾಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(10,73,'10:73','ಆದರೆ ಆ ಜನರು ಅವರನ್ನು ಸುಳ್ಳಾಗಿಸಿದರು. ಆಗ ನಾವು ಅವರನ್ನೂ ಅವರ ಸಂಗಡ ನಾವೆಯಲ್ಲಿದ್ದವರೆಲ್ಲರನ್ನೂ ರಕ್ಷಿಸಿದೆವು. ಅವರನ್ನು ನಾವು ಭೂಮಿಯಲ್ಲಿ ಉತ್ತರಾಧಿಕಾರಿಗಳಾಗಿ ಮಾಡಿದೆವು ಮತ್ತು ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದವರನ್ನು ಮುಳುಗಿಸಿಬಿಟ್ಟೆವು. ಆದುದರಿಂದ ಎಚ್ಚರಿಕೆ ನೀಡಲ್ಪಟ್ಟಿದ್ದವರ ಗತಿಯೇನಾಯಿತೆಂದು ನೋಡಿಕೊಳ್ಳಿರಿ.');
INSERT INTO "kannada" VALUES(10,74,'10:74','ಮತ್ತೆ ಅವರ (`ನೂಹ’ರ) ತರುವಾಯ ಅನೇಕ ಸಂದೇಶವಾಹಕರನ್ನು ಆಯಾ ಜನಾಂಗದ ಕಡೆಗೆ ನಾವು ಕಳುಹಿಸಿದೆವು. ಅವರ ಬಳಿಗೆ ದೃಷ್ಟಾಂತಗಳ ಸಹಿತ ಅವರು ಬಂದರು. ಆದರೆ ಅವರು ಈ ಮುಂಚೆ ಯಾವುದನ್ನು ಸುಳ್ಳಾಗಿಸಿ ದರೋ ಅದರಲ್ಲಿ ನಂಬಲು ಅವರು ಸಿದ್ದರಿರಲಿಲ್ಲ. ಹದ್ದು ಮೀರಿ ಸಾಗುವವರ ಹೃದಯಗಳ ಮೇಲೆ ಅದೇ ಪ್ರಕಾರ ನಾವು ಮುದ್ರೆಯೊತ್ತಿ ಬಿಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(10,75,'10:75','ಮತ್ತೆ ಅವರ ನಂತರ, ನಾವು ನಮ್ಮ ನಿದರ್ಶನಗಳ ಸಹಿತ ಫಿರ್‍ಔನ್ ಮತ್ತು ಅವನ ಸರದಾರರ ಕಡೆಗೆ ಮೂಸಾ ಹಾಗೂ ಹಾರೂನರನ್ನು ನಿಯೋಗಿಸಿದೆವು. ಆದರೆ ಅವರು ದರ್ಪವನ್ನು ತೋರಿದರು. ಅವರು ಅಪರಾಧಿ ಜನಾಂಗವಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(10,76,'10:76','ಹಾಗೆ ನಮ್ಮ ಕಡೆಯಿಂದ ಸತ್ಯವು ಅವರಿಗೆ ಬಂದಾಗ ‘ಇದು ಸುವ್ಯಕ್ತ ಜಾಲವಿದ್ಯೆಯಾಗಿದೆ’ ಎಂದರವರು.');
INSERT INTO "kannada" VALUES(10,77,'10:77','`ಮೂಸಾ’ರು ಹೇಳಿದರು, ಸತ್ಯವು ನಿಮಗೆ ಬಂದು ಮುಟ್ಟಿದಾಗ ಅದನ್ನು (ಜಾಲವಿದ್ಯೆ ಯೆಂದು) ಹೇಳುತ್ತೀರಾ? ಇದು ಜಾಲವಿದ್ಯೆ ಯೇ? ವಸ್ತುತಃ ಜಾಲವಿದ್ಯೆಗಾರರು ಯಶಸ್ವಿಗಳಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(10,78,'10:78','ಅವರು ಹೇಳಿದರು, ನಮ್ಮ ಪೂರ್ವಿಕರು ಯಾವುದರಲ್ಲಿ ನೆಲೆಗೊಂಡಿದ್ದನ್ನು ನಾವು ಕಂಡಿದ್ದೇವೋ ಅದರಿಂದ ನಮ್ಮನ್ನು ತಿರುಗಿಸಿ ಬಿಡಲಿಕ್ಕಾಗಿಯೂ ಭೂಮಿಯಲ್ಲಿ ನಿಮಗೆ ಹಿರಿಮೆ ಸಂಸ್ಥಾಪಿಸಲಿಕ್ಕಾಗಿಯೂ ನೀನು ಬಂದಿರುವೆಯಾ? ನಾವು ನಿಮ್ಮಿಬ್ಬರನ್ನು ನಂಬುವವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(10,79,'10:79','ಫಿರ್‍ಔನನು ಹೇಳಿದನು, ‘ಪ್ರತಿಯೊಬ್ಬ ನುರಿತ ಮಾಟಗಾರನನ್ನು ನೀವು ನನ್ನ ಮುಂದೆ ತನ್ನಿರಿ’.');
INSERT INTO "kannada" VALUES(10,80,'10:80','ಹಾಗೆ ಮಾಟಗಾರರು ಬಂದಾಗ ಮೂಸಾ, ‘ನೀವು ಎಸೆಯಬೇಕಾದುದನ್ನು ಎಸೆಯಿರಿ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(10,81,'10:81','ಅವರು ಎಸೆದಾಗ ಮೂಸಾರು ಹೇಳಿದರು, ‘ನೀವೀಗ ತಂದದ್ದು ಜಾದುವಾಗಿರುತ್ತದೆ. ಖಂಡಿತ ಅಲ್ಲಾಹು ಇದನ್ನು ಮಿಥ್ಯಗೊಳಿಸುವನು. ಅಲ್ಲಾಹು ಕಿಡಿಗೇಡಿಗಳ ಪ್ರವೃತ್ತಿಯನ್ನು ಸರಿಯಾಗಲು ಬಿಡುವುದಿಲ್ಲ’.');
INSERT INTO "kannada" VALUES(10,82,'10:82','ಅಲ್ಲಾಹು ತನ್ನ ವಚನಗಳ ಮೂಲಕ ಸತ್ಯವನ್ನು ಸತ್ಯವನ್ನಾಗಿಯೇ ಮಾಡಿ ತೋರಿಸುವನು. ಇದರಿಂದ ಅಪರಾಧಿಗಳಿಗೆ ಎಷ್ಟೇ ಅನಿಷ್ಟಕರವಾದರೂ ಸರಿಯೇ.');
INSERT INTO "kannada" VALUES(10,83,'10:83','ಆದರೆ ಮೂಸಾರನ್ನು ಆ ಜನಾಂಗದ ವಂಶದಿಂದ ಒಂದು ವಿಭಾಗದ ಹೊರತು ಇನ್ನಾರೂ ವಿಶ್ವಾಸವಿರಿಸಲಿಲ್ಲ . (ಅದು ಕೂಡಾ)ಫಿರ್‍ಔನಹಾಗೂ ತಮ್ಮ ಜನಾಂಗದ ಮುಖಂಡರು ಯಾತನೆಗೊಳಪಡಿಸುವರೇನೋ ಎಂಬ ಭಯದೊಂದಿಗಾಗಿತ್ತು. ಫಿರ್‍ಔನನು ಭೂಮಿಯ ಮೇಲೆ ಪ್ರಾಬಲ್ಯ ಹೊಂದಿದವನೂ ಹದ್ದುಮೀರಿದವನೂ ಆಗಿದ್ದನು.');
INSERT INTO "kannada" VALUES(10,84,'10:84','ಮೂಸಾ ಹೇಳಿದರು; ನನ್ನ ಜನರೇ, ನಿಜಕ್ಕೂ ಅಲ್ಲಾಹನ ಮೇಲೆ ವಿಶ್ವಾಸವಿಡುತ್ತೀರಾದರೆ ಅವನ ಮೇಲೆ ಭರವಸೆಯನ್ನಿರಿಸಿರಿ - ನೀವು ನಿಜಕ್ಕೂ ಅವನಿಗೆ ಶರಣಾಗತರಾಗಿದ್ದರೆ.');
INSERT INTO "kannada" VALUES(10,85,'10:85','ಆಗ ಅವರು ಹೇಳಿದರು; ನಾವು ಅಲ್ಲಾಹನ ಮೇಲೆಯೇ ಭರವಸೆಯನ್ನಿರಿಸಿದ್ದೇವೆ. ಓ ನಮ್ಮ ಪ್ರಭೂ, ನಮ್ಮನ್ನು ಈ ಅಕ್ರಮಿ ಜನಾಂಗಕ್ಕೆ ಪರೀಕ್ಷಾ ಸಾಧನವನ್ನಾಗಿ ಮಾಡದಿರು. (ಮರ್ದನಕ್ಕೆ ಒಳಪಡಿಸದಿರು)');
INSERT INTO "kannada" VALUES(10,86,'10:86','ನಿನ್ನ ಕೃಪೆಯಿಂದ ನಮ್ಮನ್ನು ಸತ್ಯನಿಷೇಧಿ ಜನಾಂಗದಿಂದ ರಕ್ಷಿಸು.');
INSERT INTO "kannada" VALUES(10,87,'10:87','ಮೂಸಾ ಮತ್ತು ಅವರ ಸಹೋದರನಿಗೆ ನಾವು ಹೀಗೆ ಸೂಚನೆ ಕೊಟ್ಟೆವು. ನೀವಿಬ್ಬರೂ ನಿಮ್ಮ ಜನರಿಗಾಗಿ ಈಜಿಪ್ಟ್‍ನಲ್ಲಿ ಕೆಲವು (ಪ್ರತ್ಯೇಕ)ನಿವೇಶನಗಳನ್ನು ಒದಗಿಸಿರಿ, ನಿಮ್ಮ ಆ ನಿವೇಶನಗಳನ್ನು ಕಿಬ್ಲಃವನ್ನಾಗಿ ಮಾಡಿರಿ, ಂ ನಮಾಝನ್ನು ಸಂಸ್ಥಾಪಿಸಿರಿ ಮತ್ತು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಶುಭವಾರ್ತೆ ನೀಡಿರಿ.');
INSERT INTO "kannada" VALUES(10,88,'10:88','ಮೂಸಾರವರು ಹೇಳಿದರು, ಓ ನಮ್ಮ ಪ್ರಭೂ, ನೀನು ಫಿರ್‍ಔನ್ ಮತ್ತು ಅವನ ಸರದಾರರಿಗೆ ಲೌಕಿಕ ಜೀವನದಲ್ಲಿ ಅಲಂಕಾರ ಹಾಗೂ ಸಂಪ ತ್ತನ್ನು ಕೊಟ್ಟಿರುವಿ. ಓ ಪ್ರಭೂ, ಈ ಸಿರಿಸಂಪತ್ತು ಅವರು ಜನರನ್ನು ನಿನ್ನ ಮಾರ್ಗದಿಂದ ತಪ್ಪಿಸಲು ಕಾರಣವಾಗಿದೆ. ಓ ಪ್ರಭೂ, ಅವರ ಸಂಪತ್ತನ್ನು ನಾಶಪಡಿಸು ಮತ್ತು ಅವರು ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆಯನ್ನು ಕಣ್ಣಾರೆ ಕಾಣುವವರೆಗೂ ಅವರು ವಿಶ್ವಾಸವಿರಿಸದಂತೆ ಅವರ ಹೃದಯಗಳ ಮೇಲೆ ಕಾಠಿಣ್ಯ ನೀಡು.');
INSERT INTO "kannada" VALUES(10,89,'10:89','ಅಲ್ಲಾಹ್ ಹೇಳಿದನು; ನಿಮ್ಮಿಬ್ಬರ ಪ್ರಾರ್ಥನೆಯು ಸ್ವೀಕರಿಸಲ್ಪಟ್ಟಿದೆ. ಆದ್ದರಿಂದ ನೀವು ದೃಢ ಚಿತ್ತರಾಗಿರಿ, ಅಜ್ಞಾನಿಗಳ ಮಾರ್ಗವನ್ನೆಂದಿಗೂ ಅನುಸರಿಸ ಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(10,90,'10:90','ಇಸ್‍ರಾಈಲ ಸಂತತಿಯನ್ನು ನಾವು ಸಮುದ್ರ ದಾಟಿಸಿಕೊಂಡೊಯ್ದೆವು. ಅನಂತರ ಫಿರ್‍ಔನನೂ ಅವನ ಸೇನೆಯೂ ದಿಕ್ಕಾರ ಹಾಗೂ ವೈರದಿಂದ ಅವರನ್ನು ಬೆನ್ನಟ್ಟಿದರು . ಕೊನೆಗೆ ಫಿರ್‍ಔನನು ಮುಳುಗುತ್ತಿರುವಾಗ ಹೀಗೆಂದನು, ಇಸ್‍ರಾ ಈಲ ಸಂತತಿಯು ವಿಶ್ವಾಸವಿಟ್ಟಿರುವ ದೇವನ ಹೊರತು ಅನ್ಯ ದೇವನಿಲ್ಲವೆಂದು ನಾನು ವಿಶ್ವಾಸವಿಟ್ಟೆನು. ಮತ್ತು ನಾನೂ ಶರಣಾಗತರಾದವರಲ್ಲಿ ಸೇರಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(10,91,'10:91','(ಅಲ್ಲಾಹು ಹೇಳಿದನು;) ಈಗ ವಿಶ್ವಾಸ ವಿಡುತ್ತೀಯಾ? ವಸ್ತುತಃ ಇದಕ್ಕೆ ಮುಂಚಿನವರೆಗೂ ನೀನು ದಿಕ್ಕರಿಸುತ್ತಿದ್ದೆ ಮತ್ತು ಕಿಡಿಗೇಡಿಗಳಲ್ಲಿ ಒಬ್ಬನಾಗಿದ್ದೆ.');
INSERT INTO "kannada" VALUES(10,92,'10:92','ನೀನು ಮುಂದಿನ ತಲೆಮಾರುಗಳಿಗೆ ಒಂದು ದೃಷ್ಟಾಂತವಾಗಿರುವಂತೆ ಇಂದು ನಿನ್ನ ಶವವನ್ನು ಸುರಕ್ಷಿತವಾಗಿರಿಸುವೆವು. ನಿಜಕ್ಕೂ ಜನರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳ ಬಗ್ಗೆ ಅಲಕ್ಷ್ಯರಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(10,93,'10:93','ಇಸ್‍ರಾಈಲ ಸಂತತಿಗಳಿಗೆ ಯೋಗ್ಯವಾದ ಒಂದು ನಿವೇಶನದಲ್ಲಿ ನಾವು ನಿವಾಸವನ್ನೊದಗಿಸಿದೆವು. ವಿಶಿಷ್ಟವಾದ ವಸ್ತುಗಳಿಂದ ಅವರಿಗೆ ನಾವು ಆಹಾರ ನೀಡಿದೆವು. ಮತ್ತು ಅವರಿಗೆ ಅತ್ಯುತ್ಕøಷ್ಟ ಜೀವನಾಧಾರಗಳನ್ನು ದಯಪಾಲಿಸಿದೆವು ಅದರೆ, ಅವರು ದಿವ್ಯಜ್ಞಾನ ಹೊಂದಿದ ಬಳಿಕವೂ ಭಿನ್ನಾಭಿಪ್ರಾಯ ತಾಳಿದರು. ನಿಶ್ಚಯವಾಗಿಯೂ ನಿನ್ನ ಪ್ರಭು ಪುನರು ತ್ಥಾನ ದಿನ ಅವರ ನಡುವೆ ಅವರು ಭಿನ್ನಾಭಿಪ್ರಾಯ ತಾಳುತ್ತಿದ್ದ ವಿಷಯದಲ್ಲಿ ತೀರ್ಪು ನೀಡುವನು.');
INSERT INTO "kannada" VALUES(10,94,'10:94','ನಾವು ನಿಮಗೆ ಅವತೀರ್ಣಗೊಳಿಸಿದ (ಸನ್ಮಾರ್ಗ ದರ್ಶನದ) ಬಗೆಗೆ ನಿಮಗೆ ಯಾವುದೇ ಸಂಶಯವಿದ್ದರೆ, ನಿಮಗಿಂತ ಮುಂಚೆಯೇ ಗ್ರಂಥವನ್ನು ಓದುತ್ತಾ ಬಂದವರನ್ನು ವಿಚಾರಿಸಿರಿ. ವಾಸ್ತವದಲ್ಲಿ ನಿಮ್ಮ ಬಳಿಗೆ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಸತ್ಯವೇ ಬಂದಿರುತ್ತದೆ. ಆದುದರಿಂದ ಸಂಶಯಗ್ರಸ್ತರಲ್ಲಿ ಸೇರಬೇಡಿರಿ .');
INSERT INTO "kannada" VALUES(10,95,'10:95','ಅಲ್ಲಾಹನ ವಚನಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿ ತಳ್ಳಿ ಹಾಕಿದವರಲ್ಲೂ ನೀವಾಗಬೇಡಿರಿ. ಅನ್ಯಥಾ ನೀವು ನಷ್ಟವಂತರಲ್ಲಿ ಸೇರುವಿರಿ.');
INSERT INTO "kannada" VALUES(10,96,'10:96','ಓoಟಿe');
INSERT INTO "kannada" VALUES(10,97,'10:97','ವಾಸ್ತವದಲ್ಲಿ ಯಾರ ಬಗ್ಗೆ ನಿನ್ನ ಪ್ರಭುವಿನ (ಶಿಕ್ಷೆಯ) ವಚನವು ನಿಜವಾಯಿತೋ ಅವರು ಎಲ್ಲ ನಿದರ್ಶನಗಳು ಬಂದರೂ ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆಯನ್ನು ಕಣ್ಣಾರೆ ಕಾಣುವವರೆಗೂ ವಿಶ್ವಾಸವಿರಿಸಲಾರರು.');
INSERT INTO "kannada" VALUES(10,98,'10:98','ಯಾವುದೇ ಒಂದು ನಾಡಿನವರು ಶಿಕ್ಷೆಯ ಮೊದಲು ವಿಶ್ವಾಸವಿಟ್ಟು ಅದರ ವಿಶ್ವಾಸವು ಅದಕ್ಕೆ ಫಲಕಾರಿಯಾದುದುಂಟೇ? ಯೂನುಸರ ಜನಾಂಗದ ಹೊರತು. ಆ ಜನಾಂಗವು ಸತ್ಯ ವಿಶ್ವಾಸ ಸ್ವೀಕರಿಸಿದಾಗ ನಾವು ಅವರಿಂದ ಇಹಜೀವನದ ಅಪಮಾನಕರ ಶಿಕ್ಷೆಯನ್ನು ನೀಗಿಸಿ ಬಿಟ್ಟೆವು ಮತ್ತು ಅದಕ್ಕೆ ಒಂದು ನಿರ್ದಿಷ್ಟ ಕಾಲಾವಧಿಯವರೆಗೆ ನಾವು ಅವರಿಗೆ ಸೌಖ್ಯವನ್ನು ಕೊಟ್ಟೆವು .');
INSERT INTO "kannada" VALUES(10,99,'10:99','ನಿಮ್ಮ ಪ್ರಭು ಇಚ್ಚಿಸಿರುತ್ತಿದ್ದರೆ, ಭೂವಾಸಿಗಳೆಲ್ಲರೂ ಒಟ್ಟಾಗಿ ವಿಶ್ವಾಸವಿಡುತ್ತಿದ್ದರು. ಹೀಗಿ ರುವಾಗ ಜನರೆಲ್ಲರೂ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಾಗಬೇಕೆಂದು ನೀವು ಒತ್ತಾಯಪಡಿಸುವಿರಾ?');
INSERT INTO "kannada" VALUES(10,100,'10:100','ಯಾರೊಬ್ಬನೂ ಅಲ್ಲಾಹನ ಅನುಮತಿಯ ಹೊರತು ವಿಶ್ವಾಸವಿಡಲಾರನು. ಚಿಂತಿಸದವರ ಮೇಲೆ ಅಲ್ಲಾಹು ಸಜೆಯನ್ನು ಹೇರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(10,101,'10:101','ಹೇಳಿರಿ; ಭೂಮಿ ಮತ್ತು ಆಕಾಶಗಳಲ್ಲಿರುವುದರತ್ತ ನೋಡಿರಿ. ವಿಶ್ವಾಸವಿರಿಸಿಕೊಳ್ಳದ ಜನಾಂಗಕ್ಕೆ ದೃಷ್ಟಾಂತಗಳೂ ಎಚ್ಚರಿಕೆಗಳೂ ಏನೂ ಪ್ರಯೋಜನ ವಾಗಲಾರವು !');
INSERT INTO "kannada" VALUES(10,102,'10:102','ಇವರು ಇವರಿಗಿಂತ ಮುಂಚಿನವರು ಕಂಡಿದ್ದ ದುರ್ದಿನಗಳನ್ನೇ ಕಾಣಬೇಕೆಂದು ನಿರೀಕ್ಷಿಸುತ್ತಾರೇನು? ಹೇಳಿರಿ; ನಿರೀಕ್ಷಿಸುತ್ತಿರಿ, ನಾನೂ ನಿಮ್ಮೊಂದಿಗೆ ನಿರೀಕ್ಷಿಸುತ್ತಿರುತ್ತೇನೆ.’');
INSERT INTO "kannada" VALUES(10,103,'10:103','ಆ ಬಳಿಕ ನಾವು ನಮ್ಮ ಸಂದೇಶವಾಹಕರನ್ನು ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನೂ ರಕ್ಷಿಸಿಕೊಳ್ಳುತ್ತೇವೆ. ಇದೇ ಪ್ರಕಾರ ನಮ್ಮ ಮೇಲಿನ ಒಂದು ಹೊಣೆಯಾಗಿ ನಾವು ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನು ರಕ್ಷಿಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(10,104,'10:104','(ಓ ಪೈಗಂಬರರೇ) ಹೇಳಿರಿ, ಓ ಜನರೇ, ನನ್ನ ಧರ್ಮದ ಬಗೆಗೆ ನೀವು ಸಂಶಯಗ್ರಸ್ತರಾಗಿದ್ದರೆ, ಇತ್ತ ಕೇಳಿರಿ! ನೀವು ಅಲ್ಲಾಹನ ಹೊರತು ಯಾರನ್ನು ಆರಾಧಿಸುತ್ತೀರೋ ಅವರನ್ನು ನಾನು ಆರಾಧಿಸುತ್ತಿಲ್ಲ. ಬದಲಾಗಿ ನಿಮ್ಮನ್ನು ಮೃತ್ಯು ಗೊಳಿಸುವ ಅಲ್ಲಾಹನನ್ನು ಮಾತ್ರ ನಾನು ಆರಾಧಿಸುತ್ತೇನೆ. ನಾನು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಲ್ಲಾಗಿರ ಬೇಕೆಂದು ಆಜ್ಞಾಪಿಸಲ್ಪಟ್ಟಿರುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(10,105,'10:105','ಏಕಾಗ್ರಚಿತ್ತನಾಗಿ ನಿಮ್ಮ ಮುಖವನ್ನು ಧರ್ಮ ದಲ್ಲಿ ಸರಿಯಾಗಿ ನೆಲೆಗೊಳಿಸಿಕೊಳ್ಳಬೇಕೆಂದೂ ಖಂಡಿತಾ ನೀವು ಬಹುದೇವಾರಾಧಕರಲ್ಲಾಗಬಾರದೆಂದೂ ನನ್ನೊಡನೆ ಆಜ್ಞಾಪಿಸಲಾಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(10,106,'10:106','ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು, ನಿಮಗೆ ಉಪಕಾರವನ್ನಾಗಲಿ ಉಪದ್ರವವನ್ನಾಗಲಿ ಉಂಟು ಮಾಡದ ವಸ್ತುಗಳನ್ನು ನೀವು ಆರಾಧಿಸಬೇಡಿರಿ. ನೀವು ಹಾಗೆ ಮಾಡಿದರೆ ಖಂಡಿತವಾಗಿಯೂ ಅಕ್ರಮಿಗಳಲ್ಲಾಗುವಿರಿ .');
INSERT INTO "kannada" VALUES(10,107,'10:107','ಅಲ್ಲಾಹು ನಿಮ್ಮನ್ನು ಯಾವುದೇ ಕಷ್ಟಕ್ಕೆ ಗುರಿಪಡಿಸಿದರೆ, ಅವನ ಹೊರತು ಆ ಕಷ್ಟವನ್ನು ನಿವಾರಿಸುವವನು ಇನ್ನಾರೂ ಇಲ್ಲ. ಮತ್ತು ಅವನು ನಿಮಗೆ ಹಿತವನ್ನು ಬಯಸಿದರೆ ಅವನ ಔದಾ ರ್ಯವನ್ನು ತಡೆಯುವವನೂ ಯಾರೂ ಇಲ್ಲ. ಅವನು ತನ್ನ ದಾಸರ ಪೈಕಿ ತನಗಿಷ್ಟ ಬಂದವ ರಿಗೆ ಅನುಗ್ರಹ ತಲುಪಿಸುತ್ತಾನೆ. ಅವನು ಕ್ಷಮಾಶೀಲನೂ ದಯಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(10,108,'10:108','ಹೇಳಿರಿ, ಜನರೇ, ನಿಮ್ಮ ಬಳಿಗೆ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಪರಮಸತ್ಯವು ಬಂದಿದೆ. ಆದ್ದರಿಂದ ಯಾರು ಸನ್ಮಾರ್ಗ ಸ್ವೀಕರಿಸುವನೋ ಅವನು ತನ್ನ ಹಿತಕ್ಕಾಗಿಯೇ ಸನ್ಮಾರ್ಗ ಸ್ವೀಕರಿಸುತ್ತಾನೆ ಮತ್ತು ಯಾರು ಪಥಭ್ರಷ್ಟನಾಗಿರುವನೋ ಅವನು ತನ್ನ ವಿರುದ್ಧವೇ ದಾರಿತಪ್ಪಿದನು. ನಾನು ನಿಮ್ಮ ಮೇಲೆ ಜವಾಬ್ದಾರನಲ್ಲ.');
INSERT INTO "kannada" VALUES(10,109,'10:109','(ಓ ಪೈಗಂಬರರೇ), ನಿಮ್ಮ ಕಡೆಗೆ ಬಿತ್ತರಿಸಲಾಗುವ ದಿವ್ಯವಾಣಿಯನ್ನು ಅನುಸರಿಸಿರಿ. ಮತ್ತು ಅಲ್ಲಾಹನು ತೀರ್ಮಾನ ಮಾಡುವವ ರೆಗೂ ತಾಳ್ಮೆ ವಹಿಸಿರಿ. ಅವನು ಪರಮ ಶ್ರೇಷ್ಠ ತೀರ್ಪುಗಾರನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(11,1,'11:1','ಅಲಿಫ್ ಲಾಮ್ ರಾ, ಇದು ಮಹದ್ಗ್ರಂಥ. ಇದರ ಸೂಕ್ತಗಳು ಸುದೃಢಗೊಳಿಸಲ್ಪಟ್ಟಿವೆ. ನಂತರ ವಿಶಧೀಕರಿಸಲ್ಪಟ್ಟಿವೆ . ಯುಕ್ತಿವಂತನೂ ಸೂಕ್ಷ್ಮಜ್ಞನೂ ಆದ ಅಲ್ಲಾಹನ ವತಿಯಿಂದ ಇದು ಬಂದಿದೆ.');
INSERT INTO "kannada" VALUES(11,2,'11:2','ಅಲ್ಲಾಹನ ಹೊರತು ಇನ್ನಾರನ್ನೂ ಆರಾಧಿಸ ಬೇಡಿರಿ. ನಾನು ಅವನ ಕಡೆಯಿಂದ ನಿಮಗೆ ಎಚ್ಚರಿಕೆ ಕೊಡುವವನೂ ಸುವಾರ್ತೆ ನೀಡುವವನೂ ಆಗಿರುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(11,3,'11:3','ನೀವು ನಿಮ್ಮ ಪ್ರಭುವಿನೊಡನೆ ಕ್ಷಮೆ ಯಾಚಿಸಿರಿ. ತರುವಾಯ ಖೇದಪಟ್ಟುಕೊಂಡು ಅವನತ್ತ ಮರಳಿರಿ. ಆಗ ಅವನು ಒಂದು ನಿಶ್ಚಿತ ಕಾಲಾ ವಧಿಯವರೆಗೆ ನಿಮಗೆ ಉತ್ತಮ ಆರಾಮವನ್ನು ನೀಡುವನು ಮತ್ತು ಪ್ರತಿಯೊಬ್ಬ ಉದಾರಿಗೆ ತನ್ನ ಔದಾರ್ಯದ ಫಲವನ್ನು ನೀಡುವನು . ಆದರೆ ನೀವು ವಿಮುಖರಾದರೆ ನಿಮ್ಮ ಪಾಲಿಗೆ ಒಂದು ಮಹಾ ದಿನದ ಶಿಕ್ಷೆಯನ್ನು ಭಯಪಡುತ್ತಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(11,4,'11:4','ಅಲ್ಲಾಹನ ಕಡೆಗೆ ನಿಮ್ಮ ನಿರ್ಗಮನ ಅವನು ಸರ್ವಸಮರ್ಥನು. (ಮುಂತಾದ ವಿಶಧೀಕರಣಗಳಿಂದ ಈ ಗ್ರಂಥವು ವಿಶಧೀಕರಿಸಲ್ಪಟ್ಟಿದೆ)');
INSERT INTO "kannada" VALUES(11,5,'11:5','ಗಮನಿಸಿರಿ, ಇವರು ಅಲ್ಲಾಹನಿಂದ ಮರೆ ಮಾಡಲಿಕ್ಕಾಗಿ ತಮ್ಮ ಎದೆಗಳನ್ನು ಮುದುಡಿಸಿ ಕೊಳ್ಳುತ್ತಾರೆ. ತಿಳಿಯಿರಿ, ಅವರು ತಮ್ಮನ್ನು ವಸ್ತ್ರಗಳಿಂದ ಮುಚ್ಚಿಕೊಂಡಾಗಲೂ ಕೂಡಾ ಇವರು ರಹಸ್ಯವಾಗಿಡುವುದನ್ನೂ ಪರಸ್ಯಗೊಳಿಸುವುದನ್ನು ಅವನು ತಿಳಿಯುತ್ತಾನೆ. ಅವನಂತೂ ಇವರ ಎದೆಯಂತರಾಳವನ್ನು ಬಲ್ಲನು .');
INSERT INTO "kannada" VALUES(11,6,'11:6','ಭೂಮಿಯಲ್ಲಿರುವ ಒಂದೇ ಒಂದು ಚರಜೀವಿ ಕೂಡಾ ಅಲ್ಲಾಹನ ಅನ್ನದ ಹೊಣೆಗಾರಿಕೆಯಿಂದ ಹೊರತಾಗಿಲ್ಲ. ಅವುಗಳ ವಾಸಸ್ಥಳ ಮತ್ತು ಸುರಕ್ಷಿತ ಸ್ಥಳವನ್ನು ಅಲ್ಲಾಹನು ಬಲ್ಲನು. ಎಲ್ಲವೂ ಒಂದು ಸುಸ್ಪಷ್ಟ ಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತಗೊಂಡಿದೆ.');
INSERT INTO "kannada" VALUES(11,7,'11:7','ಭೂಮಿ ಮತ್ತು ಆಕಾಶಗಳನ್ನು ಆರು ದಿನಗಳಲ್ಲಿ ಸೃಷ್ಟಿಸಿದವನು ಅವನೇ. ಅದಕ್ಕಿಂತ ಮುಂಚೆ ಅವನ ಅರ್ಶ್ ನೀರಿನ ಮೇಲಿತ್ತು . ನಿಮ್ಮಲ್ಲಿ ಯಾರು ಸುಕರ್ಮದಲ್ಲಿ ಅತ್ಯಂತ ಉತ್ತಮನು ಎಂದು ನಿಮ್ಮನ್ನು ಪರೀಕ್ಷಿಸಲಿಕ್ಕಾಗಿ. (ಓ ಪೈಗಂ ಬರರೇ,) ಮರಣಾನಂತರ ನೀವು ಇನ್ನೊಮ್ಮೆ ಎಬ್ಬಿಸಲ್ಪಡುವಿರೆಂದು ಹೇಳಿದಾಗ ಸತ್ಯನಿಷೇ ಧಿಗಳು, “ಇದು ಸುಸ್ಪಷ್ಟ ಮಾಟಗಾರಿಕೆ ಮಾತ್ರ” ಎಂದು ಹೇಳಿಬಿಡುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(11,8,'11:8','ಒಂದು ನಿರ್ದಿಷ್ಟ ಕಾಲಾವಧಿಯವರೆಗೆ ಅವರಿಂದ ನಾವು ಶಿಕ್ಷೆಯನ್ನು ತಡೆದಿರಿಸಿದಾಗ, ಅದನ್ನು ಯಾವ ವಸ್ತು ತಡೆಹಿಡಿಯಿತು? ಎಂದು ಸತ್ಯ ನಿಷೇಧಿಗಳು ಕೇಳುತ್ತಾರೆ. ತಿಳಿಯಿರಿ ಆ ಶಿಕ್ಷೆಯ ಸಮಯ ಬಂತೆಂದರೆ ಅದನ್ನು ಯಾರಿಂದಲೂ ತಡೆಯಲಾಗದು. ಅವರು ಯಾವುದನ್ನು ಪರಿಹಾಸ್ಯ ಮಾಡುತ್ತಿರುವರೋ ಅದೇ ಅವರನ್ನು ಮುತ್ತಿಕೊಳ್ಳುವುದು.');
INSERT INTO "kannada" VALUES(11,9,'11:9','ಮಾನವನಿಗೆ ನಾವು ನಮ್ಮಿಂದ ಯಾವುದೇ ಕೃಪೆಯನ್ನು ರುಚಿ ತೋರಿಸಿ ಅನಂತರ ಅದನ್ನು ಅವನಿಂದ ತೆಗೆದು ಹಾಕಿದರೆ ಅವನು ತೀವ್ರ ನಿರಾಶನೂ ತೀವ್ರ ಕೃತಘ್ನನೂ ಆಗುವನು.');
INSERT INTO "kannada" VALUES(11,10,'11:10','ಅವನಿಗೆ ತಟ್ಟಿದ್ದ ಕಷ್ಟದ ಅನಂತರ ನಾವು ಅವನಿಗೆ ಅನುಗ್ರಹಗಳ ಸವಿಯನ್ನುಣಿಸಿದರೆ ಅವನು ತನ್ನ ಎಲ್ಲ ಕೆಡುಕುಗಳೂ ನನ್ನಿಂದ ತೊಲಗಿದುವು ಎಂದು ಹೇಳಿಕೊಳ್ಳುತ್ತಾನೆ. ಅವನು ಅತಿ ಹರ್ಷಿತನೂ ಅಹಂಕಾರಿಯೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(11,11,'11:11','ತಾಳ್ಮೆ ವಹಿಸಿದ ಮತ್ತು ಸತ್ಕರ್ಮವೆಸಗಿದವರ ಹೊರತು. ಅವರಿಗೆ ಪಾಪ ವಿಮೋಚನೆಯೂ, ಮಹತ್ವಪೂರ್ಣ ಪ್ರತಿಫಲವೂ ಇರುವುದು.');
INSERT INTO "kannada" VALUES(11,12,'11:12','ಓ ಪೈಗಂಬರರೇ, `ಈ ವ್ಯಕ್ತಿಯ ಮೇಲೊಂದು ನಿಧಿಯನ್ನೇಕೆ ಇಳಿಸಲಾಗಲಿಲ್ಲ? ಅಥವಾ ಇವನ ಸಂಗಡ ದೇವಚರನೋರ್ವನೇಕೆ ಬರಲಿಲ್ಲ? ಎಂದು ತಮ್ಮ ಬಗ್ಗೆ ಅವರಾಡುವ ಮಾತುಗಳಿಂದಾಗಿ ನಿಮಗೆ ನೀಡಲಾಗುವ ದಿವ್ಯಸಂದೇಶ ಗಳಿಂದ ಕೆಲವನ್ನು ನೀವು ಉಪೇಕ್ಷಿಸಲೂಬಹುದು. ನಿಮ್ಮ ಮನಸ್ಸು ಇಕ್ಕಟ್ಟಾಗಲೂ ಬಹುದು. ನೀವು ಕೇವಲ ಎಚ್ಚರಿಕೆ ಕೊಡುವವರಾಗಿದ್ದೀರಿ. ಅಲ್ಲಾಹನು ಎಲ್ಲದರ ಮೇಲೆ ನಿಗಾ ಇಟ್ಟವನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(11,13,'11:13','ಪೈಗಂಬರವರು ಈ ಗ್ರಂಥವನ್ನು ತಾವೇ ಸೃಷ್ಠಿ ಮಾಡಿಕೊಂಡರೆಂದು ಇವರು ಹೇಳುತ್ತಾರೆಯೇ! ಹೇಳಿರಿ; ಹಾಗಾದರೆ ಇದರಂತಹ ಹತ್ತು ಸೂರಃಗಳನ್ನು ಸೃಷ್ಟಿಸಿ ತನ್ನಿರಿ ಮತ್ತು ಅಲ್ಲಾಹನ ಹೊರತು ನಿಮಗೆ ಕರೆಯಲು ಸಾಧ್ಯವಿದ್ದವರನ್ನೆಲ್ಲ ಕರೆದು ಕೊಳ್ಳಿರಿ. ನೀವು ಸತ್ಯವಾದಿಗಳಾ ಗಿದ್ದರೆ.');
INSERT INTO "kannada" VALUES(11,14,'11:14','ಅವರು ನಿಮಗೆ ಉತ್ತರಿಸದಿದ್ದರೆ, ಇದು ಅಲ್ಲಾಹನ ಜ್ಞಾನದಿಂದ ಅವತೀರ್ಣಗೊಂಡಿದೆ ಮತ್ತು ಅವನ ಹೊರತು ಅನ್ಯ ದೇವನಿಲ್ಲವೆಂದು ಚೆನ್ನಾಗಿ ಅರಿತು ಕೊಳ್ಳಿರಿ. ಇನ್ನಾದರೂ ನೀವು ಮುಸ್ಲಿಮರಾಗುವಿರಾ?');
INSERT INTO "kannada" VALUES(11,15,'11:15','ಯಾರು ಲೌಕಿಕ ಜೀವನ ಮತ್ತು ಅದರ ಸೊಬಗನ್ನು ಬಯಸುವರೋ ಅವರ ಕರ್ಮಗಳ ಪ್ರತಿ ಫಲವನ್ನು ನಾವು ಇಲ್ಲೇ ಕೊಟ್ಟುಬಿಡುತ್ತೇವೆ ಅದರಲ್ಲಿ ಅವರಿಗೆ ಯಾವ ಕೊರತೆಯನ್ನೂ ಮಾಡುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(11,16,'11:16','ಆದರೆ ಇಂಥವರಿಗೆ ಪರಲೋಕದಲ್ಲಿ ನರಕಾಗ್ನಿಯ ಹೊರತು ಇನ್ನೇನೂ ಇಲ್ಲ. ಇವರು ಇಲ್ಲಿ ನಿರ್ಮಿಸಿದ್ದೆಲ್ಲವೂ ಅಳಿದು ಹೋಯಿತು ಮತ್ತು ಅವರು ಮಾಡಿದ್ದೆಲ್ಲವೂ ನಿರರ್ಥಕವಾಯಿತು.');
INSERT INTO "kannada" VALUES(11,17,'11:17','ಒಬ್ಬರು ತನ್ನ ಪ್ರಭುವಿನಿಂದ ದೊರೆತ ಸ್ಪಷ್ಟ ಪ್ರಮಾಣವನ್ನು ಅವಲಂಬಿಸಿದ್ದು ಬಳಿಕ ಪ್ರಭು ವಿನ ಕಡೆಯಿಂದ ಒಂದು ಸಾಕ್ಷ್ಯವೂ ಅದನ್ನು ಓದಿ ಹೇಳುತ್ತಿರುವುದು. ಮತ್ತು ಅದಕ್ಕೆ ಮುಂಚೆ ಮಾರ್ಗದರ್ಶಿ ಹಾಗೂ ಕೃಪೆಯಾಗಿ ಬಂದಿದ್ದ ಮೂಸಾರ ಗ್ರಂಥವೂ ಸಾಕ್ಷ್ಯವಾಗಿ ಇದೆ. (ಇಂತಹ ವ್ಯಕ್ತಿಯು ಹಾಗೆ ಇಲ್ಲದವರಂತಾಗುವರೇ? ಇಲ್ಲ) ಅಂಥವರು ಖಂಡಿತ ಇದರಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸುವರು . ಎಲ್ಲ ಸಂಘಗಳ ಪೈಕಿ, ಯಾವನಾದರೂ ಇದನ್ನು ನಿಷೇಧಿಸಿದರೆ ಅವನಿಗೆ ವಾಗ್ದತ್ತ ಸ್ಥಾನವು ನರಕಾಗ್ನಿ ಯಾಗಿರುತ್ತದೆ. ಆದುದರಿಂದ ಇದರ ಬಗ್ಗೆ ನೀವು ಯಾವುದೇ ಸಂದೇಹಕ್ಕೊಳಗಾಗಬೇಡಿರಿ. ಖಂಡಿತ ಇದು ನಿಮ್ಮ ಪ್ರಭುವಿನಿಂದ ಬಂದಿರುವ ಪರಮ ಸತ್ಯ. ಆದರೆ ಹೆಚ್ಚಿನವರು ವಿಶ್ವಾಸವಿರಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(11,18,'11:18','ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಳ್ಳಾರೋಪ ಸೃಷ್ಟಿಸಿದವನಿ ಗಿಂತ ದೊಡ್ಡ ಅಕ್ರಮಿ ಇನ್ನಾರಿದ್ದಾರೆ? ಇಂಥ ವರು ತಮ್ಮ ಪ್ರಭುವಿನ ಮುಂದೆ ತರಲ್ಪಡುವರು ಮತ್ತು ತಮ್ಮ ಪ್ರಭುವಿನ ಮೇಲೆ ಸುಳ್ಳಾರೋಪ ಹೊರಿಸಿದವರು ಇವರೇ ಎಂದು ಸಾಕ್ಷಿದಾರರು ಹೇಳುವರು. ತಿಳಿದಿರಲಿ! ಅಕ್ರಮಿಗಳ ಮೇಲೆ ಅಲ್ಲಾಹನ ಶಾಪವಿದೆ.');
INSERT INTO "kannada" VALUES(11,19,'11:19','ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ ಜನರನ್ನು ತಡೆಯುವ, ಮತ್ತು ಆ ಮಾರ್ಗವನ್ನು ವಕ್ರಗೊಳಿಸ ಬಯಸುವವರು ಅವರು. ಅವರೇ ಪರಲೋಕವನ್ನು ನಿಷೇಧಿಸುವವರು.');
INSERT INTO "kannada" VALUES(11,20,'11:20','ಅವರು ಭೂಮಿಯಲ್ಲಿ ಅಲ್ಲಾಹನನ್ನು ಪರಾಜಿತ ಗೊಳಿಸಲು ಶಕ್ತರಾಗಿದ್ದಿಲ್ಲ. ಮತ್ತು ಅಲ್ಲಾಹನ ಹೊರತು ಅವರಿಗಾರೂ ಸಹಾಯಕರೂ ಇರಲಿಲ್ಲ. ಅವರಿಗೆ ಇಮ್ಮಡಿ ಶಿಕ್ಷೆ ಕೊಡಲಾಗುವುದು. ಅವರು ಸತ್ಯವನ್ನು ಕೇಳದಾದರು ಮತ್ತು ಕಂಡರಿಯದಾದರು.');
INSERT INTO "kannada" VALUES(11,21,'11:21','ಅವರು ತಮ್ಮನ್ನು ತಾವೇ ನಷ್ಟಕ್ಕೆ ಗುರಿಪಡಿಸಿ ಕೊಂಡವರು. ಅವರು (ಅಲ್ಲಾಹನ ಮೇಲೆ) ಕೃತಕವಾಗಿ ಸೃಷ್ಟಿಸುತ್ತಿದ್ದ ಮಿಥ್ಯ ವಾದವು ಅವರಿಂದ ಕಳೆದುಹೋಯಿತು.');
INSERT INTO "kannada" VALUES(11,22,'11:22','ನಿಸ್ಸಂದೇಹ! ಅವರೇ ಪರಲೋಕದಲ್ಲಿ ಅತ್ಯಧಿಕ ನಷ್ಟ ಹೊಂದಿದವರು.');
INSERT INTO "kannada" VALUES(11,23,'11:23','ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿದ, ಸತ್ಕರ್ಮವೆಸಗಿದ ಹಾಗೂ ತಮ್ಮ ಪ್ರಭುವಿನೆಡೆಗೆ ಶಾಂತಚಿತ್ತರಾದವರು ಯಾರೋ ಅವರೇ ನಿಶ್ಚಯವಾಗಿಯೂ ಸ್ವರ್ಗದವರು. ಅವರು ಅದರಲ್ಲಿ ಸದಾಕಾಲ ವಾಸಿಸುವರು.');
INSERT INTO "kannada" VALUES(11,24,'11:24','ಈ ಎರಡು ವರ್ಗದವರ ಉಪಮೆ ಕುರುಡನೂ ಕಿವುಡನೂ ಆಗಿರುವ ಒಬ್ಬನಂತೆಯೂ, ದೃಷ್ಟಿ ಮತ್ತು ಶ್ರವಣವುಳ್ಳ ಇನ್ನೊಬ್ಬನಂತೆಯೂ ಆಗಿದೆ. ಇವರಿಬ್ಬರೂ ಉಪಮೆಯಲ್ಲಿ ಸಮಾನರಾಗ ಬಲ್ಲರೇ? ನೀವು ಯೋಚಿಸುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(11,25,'11:25','ನೂಹರನ್ನು ಅವರ ಜನಾಂಗದ ಕಡೆಗೆ ನಾವು ಕಳುಹಿಸಿದ್ದೆವು. (ನೂಹರು ಇಂತೆಂದರು,) ನಾನು ನಿಮಗೆ ಸುವ್ಯಕ್ತ ಮುನ್ನೆಚ್ಚರಿಕೆದಾರನು.');
INSERT INTO "kannada" VALUES(11,26,'11:26','ನೀವು ಅಲ್ಲಾಹನ ಹೊರತು ಇನ್ಯಾರಿಗೂ ಆರಾಧಿಸ ಬೇಡಿರಿ. ಅನ್ಯಥಾ ನಿಮಗೆ ವೇದನಾಯುಕ್ತ ದಿನದ ಶಿಕ್ಷೆಯನ್ನು ನಾನು ಭಯಪಡುತ್ತಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(11,27,'11:27','ಆಗ ಅವರ ಜನಾಂಗದವರ ಪೈಕಿ ಅವಿಶ್ವಾಸ ತಾಳಿದ ಸರದಾರರು ಹೀಗೆಂದರು- ನೀವು ನಮ್ಮಂತಹ ಒಬ್ಬ ಮನುಷ್ಯನಾಗಿಯೇ ಹೊರತು ನಿಮ್ಮನ್ನು ನಾವು ಕಾಣುತ್ತಿಲ್ಲ. ನಮ್ಮ ಜನಾಂಗದಲ್ಲಿ ಕೆಳವರ್ಗದವರು ಯೋಚನೆಯಿಲ್ಲದೆ ನಿಮ್ಮನ್ನು ಅನುಸರಿಸುತ್ತಿರುವುದನ್ನು ನಾವು ಕಾಣುತ್ತೇವೆ. ನಿಮಗೆ ನಮಗಿಂತ ಯಾವ ಶ್ರೇಷ್ಟ ತೆಯನ್ನೂ ನಾವು ಕಾಣುವುದಿಲ್ಲ. ಮಾತ್ರವಲ್ಲ ನಾವು ನಿಮ್ಮನ್ನು ಸುಳ್ಳುಗಾರನೆಂದೇ ಭಾವಿಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(11,28,'11:28','ಅವರು ಹೇಳಿದರು. ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧ ವರೇ, ನೀವೇ ಯೋಚಿಸಿರಿ, ನಾನು ನನ್ನ ಪ್ರಭು ವಿನ ಕಡೆಯಿಂದ ಒಂದು ಸ್ಪಷ್ಟ ಆಧಾರ ಪ್ರಮಾಣ ವನ್ನು ಅವಲಂಬಿಸಿದವನಾಗಿದ್ದು ಅವನು ತನ್ನ ವತಿಯಿಂದ ನನಗೆ ಕೃಪೆಯನ್ನೂ ಪ್ರದಾನ ಮಾಡಿರುವನು. ಆದರೆ ಅದು ನಿಮ್ಮನ್ನು ಕುರುಡರನ್ನಾಗಿ ಸಿತು. ಹೀಗಿರುವಾಗ ನೀವದನ್ನು ಅನಿಷ್ಟಪಡುವ ಸ್ಥಿತಿಯಲ್ಲಿ ನಿಮ್ಮನ್ನು ಅದಕ್ಕೆ ಬಲವಂತ ಎಳೆದು ತರಲು ನನ್ನಿಂದ ಸಾಧ್ಯವೇ?');
INSERT INTO "kannada" VALUES(11,29,'11:29','ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧವರೇ, ನಾನು ಇದರ ಹೆಸರಲ್ಲಿ ನಿಮ್ಮಿಂದ ಧನ ಕೇಳುವುದಿಲ್ಲ. ನನ್ನ ಪ್ರತಿಫಲ ಅಲ್ಲಾಹನಲ್ಲಿದೆ. ನಂಬಿದವರನ್ನು ಹೊರ ದಬ್ಬಿ ಅಟ್ಟಿ ಓಡಿಸುವವನೂ ನಾನಲ್ಲ. ಅವರು ತಾವೇ ತಮ್ಮ ಪ್ರಭುವಿನ ಸನ್ನಿಧಿಗೆ ಹೋಗುವ ವರು. ಆದರೆ ನೀವು ತಿಳಿಗೇಡಿತನದಿಂದ ವರ್ತಿ ಸುತ್ತಿರುವುದನ್ನು ನಾನು ಕಾಣುತ್ತಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(11,30,'11:30','ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧವರೇ, ನಾನು ಇವರನ್ನು ಅಟ್ಟಿ ಓಡಿಸಿದರೆ ಅಲ್ಲಾಹನ ಹಿಡಿ ತದಿಂದ ನನ್ನನ್ನು ಕಾಪಾಡುವವರು ಯಾರು? ನೀವು ಯೋಚಿಸುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(11,31,'11:31','ನನ್ನ ಬಳಿ ಅಲ್ಲಾಹನ ಭಂಡಾರವಿದೆಯೆಂದು ನಾನು ನಿಮಗೆ ಹೇಳುವುದಿಲ್ಲ. ನಾನು ಪರೋಕ್ಷ ವನ್ನು ಅರಿಯುತ್ತಿಲ್ಲ. ನಾನೊಬ್ಬ ದೇವಚರನೆಂದೂ ನಾನು ಹೇಳುತ್ತಿಲ್ಲ. ನಿಮ್ಮ ದೃಷ್ಟಿಗೆ ಕೀಳಾಗಿ ಕಾಣಿಸುತ್ತಿರುವವರಿಗೆ ಅಲ್ಲಾಹನು ಯಾವುದೇ ಒಳಿತನ್ನು ನೀಡಲಾರನೆಂದೂ ನಾನು ಹೇಳಲಾರೆ. ಅವರ ಅಂತಃಕರಣದ ಸ್ಥಿತಿಯನ್ನು ಅಲ್ಲಾಹನೇ ಚೆನ್ನಾಗಿ ಬಲ್ಲನು. ಆಗ ನಾನು (ಹಾಗೆ ಹೇಳಿಬಿಟ್ಟರೆ) ಅಕ್ರಮಿಗಳಲ್ಲಿ ಸೇರಿ ಹೋದೇನು.');
INSERT INTO "kannada" VALUES(11,32,'11:32','ಅವರು ಹೀಗೆಂದರು `ಓ ನೂಹ್! ನೀವು ನಮ್ಮೊ ಡನೆ ವಾಗ್ವಾದ ನಡೆಸಿದಿರಿ, ತುಂಬಾ ನಡೆಸಿದಿರಿ. ಇನ್ನು ನೀವು ಸತ್ಯವಾದಿಯಾಗಿದ್ದರೆ ನೀವು ನಮಗೆ ಹೆದರಿಸುತ್ತಿರುವ ಆ ಶಿಕ್ಷೆಯನ್ನು ಬರ ಮಾಡಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(11,33,'11:33','ನೂಹ್ ಹೀಗೆ ಹೇಳಿದರು; `ಅಲ್ಲಾಹನಿಚ್ಛಿಸಿದರೆ ಅವನೇ ಅದನ್ನು ತರುವನು. ನೀವು ಆತನನ್ನು ಸೋಲಿಸಲಾರಿರಿ.');
INSERT INTO "kannada" VALUES(11,34,'11:34','ಅಲ್ಲಾಹನು ನಿಮ್ಮನ್ನು ದಾರಿ ತಪ್ಪಿಸಲಿಚ್ಛಿಸಿರು ವಾಗ ನನ್ನ ಉಪದೇಶ ನಿಮಗೆ ಫಲ ಕೊಡಲಾರದು. ಅವನೇ ನಿಮ್ಮ ಪ್ರಭು ಮತ್ತು ಅವನಲ್ಲಿಗೇ ನೀವು ಮರಳಲಿಕ್ಕಿದ್ದೀರಿ .');
INSERT INTO "kannada" VALUES(11,35,'11:35','ಇದನ್ನು ಈ ವ್ಯಕ್ತಿ ಸ್ವಯಂ ರಚಿಸಿಕೊಂಡಿದ್ದಾ ನೆಂದು ಇವರು ಹೇಳುತ್ತಿದ್ದಾರೇನು? ಹೇಳಿರಿ, ಇದನ್ನು ನಾನೇ ರಚಿಸಿದ್ದಾದರೆ ನನ್ನ ಅಪರಾಧದ ಹೊಣೆ ನನ್ನ ಮೇಲೆ ಇದೆ ಮತ್ತು ನೀವು ಮಾಡು ತ್ತಿರುವ ಅಪರಾಧದಿಂದ ನಾನು ಮುಕ್ತನಾಗಿರುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(11,36,'11:36','ನಿಮ್ಮ ಜನಾಂಗದಲ್ಲಿ ಈಗಾಗಲೇ ವಿಶ್ವಾಸ ಸ್ವೀಕ ರಿಸಿದವರ ಹೊರತು ಇನ್ನಾರೂ ಸ್ವೀಕರಿಸುವವರಿಲ್ಲ. ಅವರ ಕುಕೃತ್ಯಗಳಿಗಾಗಿ ಬೇಸರಿಸಬೇಡಿ ಎಂದು ನೂಹರಿಗೆ ಸಂದೇಶ ನೀಡಲಾಯಿತು.');
INSERT INTO "kannada" VALUES(11,37,'11:37','ನಮ್ಮ ಮೇಲ್ನೋಟ ಂ ಹಾಗೂ ನಮ್ಮ ಆದೇಶ ಪ್ರಕಾರ ಒಂದು ನಾವೆಯನ್ನು ಕಟ್ಟಿರಿ. ಅಕ್ರಮಿಗಳ ಪರವಾಗಿ ನನ್ನಲ್ಲಿ ಮಾತಾಡಬಾರದು. ನಿಶ್ಚಯವಾ ಗಿಯೂ ಅವರೆಲ್ಲರೂ ಮುಳುಗಿ ನಾಶವಾಗಲಿರುವವರು.');
INSERT INTO "kannada" VALUES(11,38,'11:38','ನೂಹರು ನಾವೆಯನ್ನು ನಿರ್ಮಿಸುತ್ತಿದ್ದರು. ಅವರ ಜನಾಂಗದ ಸರದಾರರು ಅವರ ಬಳಿಯಿಂದ ಹಾದು ಹೋಗುತ್ತಿದ್ದಾಗ ಅವರನ್ನು ಗೇಲಿ ಮಾಡುತಿ ್ತದ್ದರು. ನೂಹರು ಹೇಳಿದರು, `ನೀವು ನಮ್ಮನ್ನು ಗೇಲಿ ಮಾಡುವುದಾದರೆ ನೀವು ಗೇಲಿ ಮಾಡಿದಂತೆಯೇ ನಾವೂ ನಿಮ್ಮನ್ನು ಗೇಲಿ ಮಾಡುತ್ತಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(11,39,'11:39','ಅಪಮಾನಕರ ಶಿಕ್ಷೆಯು ಯಾರ ಮೇಲೆರಗುತ್ತದೆ, ಮತ್ತು ಸ್ಥಿರವಾದ ಶಿಕ್ಷೆ ಯಾರ ಮೇಲೆ ಬೀಳುತ್ತದೆ ಎಂದು ಸದ್ಯದಲ್ಲೇ ನಿಮಗೆ ತಿಳಿದುಬರುವುದು.');
INSERT INTO "kannada" VALUES(11,40,'11:40','ಕೊನೆಗೆ ನಮ್ಮ ಆಜ್ಞೆ ಬಂತು. ತಂದೂರಿ ಒಲೆ ಯಿಂದ ನೀರು ಚಿಮ್ಮಿತು . ಆಗ ನಾವು ಹೇಳಿ ದೆವು; ಎಲ್ಲವುಗಳ ಎರಡೆರಡು ಜೊತೆಗಳನ್ನು ಮತ್ತು ಯಾರ ಬಗ್ಗೆ ಪೂರ್ವನಿರ್ಧಾರವಾಗಿದೆ ಯೋ ಅವರ ಹೊರತು ನಿಮ್ಮ ಕುಟುಂಬದವ ರನ್ನೂ ಧರ್ಮ ವಿಶ್ವಾಸಿಗಳನ್ನೂ ಹತ್ತಿಸಿಕೊಳ್ಳಿರಿ. ನಿಜಕ್ಕೂ ನೂಹರ (ಪ್ರವಾದಿತ್ವದ) ಮೇಲೆ ವಿಶ್ವಾಸ ವಿಟ್ಟವರು ಕೆಲವೇ ಕೆಲವರು ಮಾತ್ರವಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(11,41,'11:41','ನೂಹರು ಹೇಳಿದರು, ಇದರಲ್ಲಿ ಹತ್ತಿಕೊಳ್ಳಿರಿ, ಇದು ಚಲಿಸುವುದೂ ನಿಲ್ಲುವುದೂ ಅಲ್ಲಾಹನ ಹೆಸರಲ್ಲಿ. ನನ್ನ ಪ್ರಭು ಅತ್ಯಂತ ಕ್ಷಮಾಶೀಲನೂ ಕರುಣಾಮಯನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(11,42,'11:42','ನಾವೆಯು ಇವರನ್ನು ಹೊತ್ತುಕೊಂಡು ಪರ್ವ ತದಂಥ ತೆರೆ ಮಾಲೆಗಳ ನಡುವೆ ಸಂಚರಿಸಿತು. ನೂಹರ ಮಗನು ದೂರದಲ್ಲಿದ್ದನು. ನೂಹರು ಕೂಗಿ ಕರೆದು; ಮಗನೇ! ನಮ್ಮ ಜೊತೆ ಹಡಗ ನ್ನು ಹತ್ತಿಕೋ, ಸತ್ಯನಿಷೇಧಿಗಳೊಂದಿಗೆ ಸೇರ ಬೇಡ ಎಂದರು .');
INSERT INTO "kannada" VALUES(11,43,'11:43','ಅವನು ಹೇಳಿದ; ನಾನು ಒಂದು ಪರ್ವತದ ಮೇಲೆ ಹತ್ತಿಕೊಳ್ಳುತ್ತೇನೆ. ಅದು ನನ್ನನ್ನು ನೀರಿನಿಂದ ರಕ್ಷಿಸುವುದು, ಆಗ ನೂಹರು, ಅಲ್ಲಾಹನು ಅನುಗ್ರಹಿಸಿದವರ ಹೊರತು ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆಯಿಂದ ಇಂದು ರಕ್ಷಣೆಯಿಲ್ಲ ಎಂದರು. ಅಷ್ಟರಲ್ಲಿ ಒಂದು ಅಲೆಯು ಅವರಿಬ್ಬರ ನಡುವೆ ಅಡ್ಡ ಬಂತು. ಅವನೂ ಮುಳುಗಿದವರಲ್ಲಿ ಸೇರಿದನು.');
INSERT INTO "kannada" VALUES(11,44,'11:44','ಓ ಭೂಮಿಯೇ, ನಿನ್ನ ನೀರನ್ನು ಇಂಗಿಸು. ಮತ್ತು ಓ ಆಕಾಶವೇ, ಮಳೆ ನಿಲ್ಲಿಸು! ಎಂದು ಅಪ್ಪಣೆಯಾಯಿತು. ಆ ಪ್ರಕಾರ ನೀರು ಬತ್ತಿತು. ಆಜ್ಞೆ ಈಡೇರಿತು. ನೌಕೆಯು `ಜೂದಿ’ಯಲ್ಲಿ ತಂಗಿತು. ಅಕ್ರಮಿ ಜನಾಂಗಕ್ಕೆ ನಾಶ ಎನ್ನಲಾಯಿತು.');
INSERT INTO "kannada" VALUES(11,45,'11:45','ನೂಹರು ತಮ್ಮ ಪ್ರಭುವನ್ನು ಕೂಗಿ ಕರೆದರು, ಪ್ರಭೂ, ನನ್ನ ಮಗನು ತನ್ನ ಕುಟುಂಬದವರಲ್ಲಾ ಗಿರುತ್ತಾನೆ. ನಿನ್ನ ವಾಗ್ದಾನವು ಸತ್ಯ ಮತ್ತು ನೀನು ಪರಮ ತೀರ್ಪುಗಾರನಾಗಿರುವೆ.');
INSERT INTO "kannada" VALUES(11,46,'11:46','ಅಲ್ಲಾಹು ಹೇಳಿದನು; ಓ ನೂಹ್, ಅವನು ಖಂಡಿತ ನಿನ್ನ ಕುಟುಂಬದವನಲ್ಲ. ಅವನೊಂದು ದುಷ್ಟ ಕರ್ಮ ಮಾತ್ರವಾಗಿರುವನು . ಆದುದ ರಿಂದ ನೀನು ನಿನಗೆ ತಿಳಿದಿಲ್ಲದ ವಿಚಾರವನ್ನು ನನ್ನೊಡನೆ ನಿವೇದಿಸಬೇಡ. ನೀನು ಅಜ್ಞಾನಿಗಳ ಸಾಲಿಗೆ ಸೇರಬಾರದೆಂದು ನಿನಗೆ ನಾನು ಖಂಡಿತ ಉಪದೇಶಿಸುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(11,47,'11:47','ಅವರು (ನೂಹ್) ಹೇಳಿದರು; ಓ ನನ್ನ ಪ್ರಭೂ, ನನಗೆ ಅರಿವಿಲ್ಲದ ವಿಷಯವನ್ನು ನಿನ್ನಿಂದ ಬೇಡುವುದರ ವಿರುದ್ಧ ನಾನು ನಿನ್ನ ಅಭಯ ಯಾಚಿಸುತ್ತೇನೆ. ನೀನು ನನಗೆ ಕ್ಷಮೆ ನೀಡದಿದ್ದರೆ ಹಾಗೂ ನನ್ನ ಮೇಲೆ ದಯೆ ತೋರದಿದ್ದರೆ ನಾನು ನತದೃಷ್ಟರ ಸಾಲಿಗೆ ಸೇರುವೆನು’.');
INSERT INTO "kannada" VALUES(11,48,'11:48','ಹೀಗೆ ಹೇಳಲಾಯಿತು; ಓ ನೂಹ್, ನಿನ್ನ ಮೇಲೆ ಮತ್ತು ನಿನ್ನೊಂದಿಗಿರುವ ಸಮುದಾಯಗಳ ಮೇಲೆ ನಮ್ಮ ಕಡೆಯಿಂದ ಬಂದ ಶಾಂತಿ ಮತ್ತು ಸಮೃದ್ಧಿಗಳೊಂದಿಗೆ ಇಳಿದು ಬಿಡು. ಕೆಲವು ಸಮುದಾಯಗಳಿಗೆ ಸ್ವಲ್ಪ ಕಾಲದವರೆಗೆ ಆರಾಮ ನೀಡುವೆವು. ಅನಂತರ ಅವರಿಗೆ ನಮ್ಮ ಕಡೆ ಯಿಂದ ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆ ತಟ್ಟುವುದು.');
INSERT INTO "kannada" VALUES(11,49,'11:49','(ಓ ಪೈಗಂಬರರೇ,) ನಿಮಗೆ ಸಂದೇಶವಾಗಿ ನೀಡುತ್ತಿರುವ ಅದೃಶ್ಯದ ವರ್ತಮಾನಗಳಿವು. ಇದಕ್ಕೆ ಮುಂಚೆ ಇವುಗಳನ್ನು ನೀವಾಗಲಿ ನಿಮ್ಮ ಜನಾಂಗವಾಗಲಿ ಅರಿತಿರಲಿಲ್ಲ. ಆದುದರಿಂದ ತಾಳ್ಮೆ ವಹಿಸಿರಿ. ನಿಜವಾಗಿಯೂ ಉತ್ತಮ ಪರ್ಯ ವಸಾನವು ಭಕ್ತರಿಗಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(11,50,'11:50','`ಆದ್’ ಜನಾಂಗದೆಡೆಗೆ ಅವರ ಬಂಧು ಹೂದರನ್ನು ನಾವು ಕಳುಹಿಸಿದೆವು. ಅವರು ಹೀಗೆಂದರು, ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧವರೇ, ಅಲ್ಲಾಹ ನನ್ನು ಆರಾಧಿಸಿ. ಅವನ ಹೊರತು ಇನ್ನಾವ ಆರಾಧ್ಯನೂ ನಿಮಗಿಲ್ಲ. ನೀವು ಕೇವಲ ಸುಳ್ಳು ಸೃಷ್ಟಿ ಮಾಡಿ ಕೊಂಡಿರುತ್ತೀರಿ.');
INSERT INTO "kannada" VALUES(11,51,'11:51','ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧವರೇ, ಇದರ ಹೆಸರಲ್ಲಿ ನಾನು ನಿಮ್ಮಿಂದೇನೂ ಪ್ರತಿಫಲ ಕೇಳುವುದಿಲ್ಲ. ನನ್ನ ಪ್ರತಿಫಲವು ನನ್ನನ್ನು ಸೃಷ್ಟಿಸಿದವನ ಬಳಿ ಇದೆ. ನೀವು ಯೋಚಿಸುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(11,52,'11:52','ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧವರೇ, ನಿಮ್ಮ ಪ್ರಭು ವಿನೊಡನೆ ಕ್ಷಮೆ ಯಾಚಿಸಿರಿ ಮತ್ತು ಪಶ್ಚಾತ್ತಾ ಪಪಟ್ಟು ಆತನ ಕಡೆಗೆ ಮರಳಿರಿ. ಅವನು ನಿಮ್ಮ ಮೇಲೆ ಆಕಾಶದಿಂದ ಸಮೃದ್ಧವಾಗಿ ಮಳೆ ಸುರಿ ಸುವನು ಮತ್ತು ನಿಮ್ಮ ಶಕ್ತಿಗೆ ಇನ್ನಷ್ಟು ಶಕ್ತಿಯ ನ್ನು ಹೆಚ್ಚಿಸಿ ಕೊಡುವನು. ಅಪರಾಧಿಗಳಾಗಿ ವಿಮುಖರಾಗಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(11,53,'11:53','ಅವರು ಹೇಳಿದರು; ಓ ಹೂದ್, ನೀವು ನಮ್ಮ ಬಳಿಗೆ ಸ್ಪಷ್ಟ ಪುರಾವೆಯನ್ನೇನೂ ತಂದಿಲ್ಲ. ನಿಮ್ಮ ಮಾತನ್ನು ಕೇಳಿ ನಾವು ನಮ್ಮ ದೇವ ರುಗಳನ್ನು ತ್ಯಜಿಸಿ ಬಿಡುವವರೇನಲ್ಲ. ನಿಮ್ಮ ಮೇಲೆ ನಾವು ವಿಶ್ವಾಸವಿಡಲಾರೆವು.');
INSERT INTO "kannada" VALUES(11,54,'11:54','ನಮ್ಮ ದೇವರುಗಳ ಪೈಕಿ ಕೆಲವು ದೇವರುಗಳ ಅವಕೃಪೆಗೆ ತುತ್ತಾಗಿ ನಿನ್ನ ಬುದ್ಧಿ ಕೆಟ್ಟಿದೆ ಎಂದೇ ನಮಗೆ ಹೇಳಲಿಕ್ಕಿರುವುದು. ಹೂದ್ ಹೇಳಿದರು: ನಾನು ಅಲ್ಲಾಹನ ಸಾಕ್ಷಿಯಾಗಿ ಹೇಳುತ್ತಿದ್ದೇನೆ. ನೀವೂ ಇದಕ್ಕೆ ಸಾಕ್ಷಿಗಳಾಗಿರಿ ನೀವು ಅಲ್ಲಾಹನ ಹೊರತು ದೇವತ್ವದಲ್ಲಿ ಪಾಲುದಾರರಾಗಿಸಿ ಕೊಂ ಡಿರುವ ಎಲ್ಲದರಿಂದ ನಾನು ಸಂಬಂಧ ಕಳೆದು ಕೊಂಡಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(11,55,'11:55','ಈಗ ನೀವೆಲ್ಲ ಸೇರಿ ನನ್ನ ವಿರುದ್ಧ ಸಂಚು ಹೂಡಿರಿ. ನಂತರ ನನಗೆ ಸ್ವಲ್ಪವೂ ಕಾಲಾವಕಾಶ ಕೊಡಬೇಡಿರಿ .');
INSERT INTO "kannada" VALUES(11,56,'11:56','ನಾನು, ನನ್ನ ಪ್ರಭುವೂ ನಿಮ್ಮ ಪ್ರಭುವೂ ಆಗಿರುವ ಅಲ್ಲಾಹನ ಮೇಲೆ ಅಚಲ ಭರವಸೆ ಇಟ್ಟಿದ್ದೇನೆ. ಚಲಿಸುತ್ತಿರುವ ಪ್ರತಿಯೊಂದು ಜೀವಿಯ ಜುಟ್ಟು ಅವನ ಕೈಯಲ್ಲಿದೆ. ನಿಶ್ಚಯವಾಗಿಯೂ ನನ್ನ ಪ್ರಭು ಸ್ಥಿರವಾದ ಸನ್ಮಾರ್ಗದಲ್ಲಿದ್ದಾನೆ .');
INSERT INTO "kannada" VALUES(11,57,'11:57','ನೀವು ವಿಮುಖರಾಗುವುದಾದರೆ, ನಾನು ಯಾವ ಸಂದೇಶವನ್ನು ಕೊಟ್ಟು ನಿಮ್ಮ ಬಳಿಗೆ ಕಳುಹಿ ಸಲ್ಪಟ್ಟಿದ್ದೆನೋ ಅದನ್ನು ನಾನು ನಿಮಗೆ ತಲುಪಿ ಸಿದ್ದೇನೆ. ಇನ್ನು ನನ್ನ ಪ್ರಭು ಇನ್ನೊಂದು ಜನಾಂಗವನ್ನು ನಿಮ್ಮ ಉತ್ತರಾಧಿಕಾರಿಯಾಗಿ ಎಬ್ಬಿಸುವನು. ನೀವು ಅವನಿಗೆ ಯಾವ ಹಾನಿಯನ್ನೂ ಮಾಡಲಾರಿರಿ. ಖಂಡಿತವಾಗಿಯೂ ನನ್ನ ಪ್ರಭು ಸಕಲ ವಸ್ತುಗಳ ಸಂರಕ್ಷಕನು.');
INSERT INTO "kannada" VALUES(11,58,'11:58','ನಮ್ಮ ಆಜ್ಞೆ ಬಂದಾಗ ನಾವು ನಮ್ಮ ಕೃಪೆ ಯಿಂದ ಹೂದರನ್ನೂ ಅವರ ಮೇಲೆ ವಿಶ್ವಾಸ ವಿರಿಸಿದವರನ್ನೂ ರಕ್ಷಿಸಿದೆವು ಮತ್ತು ನಾವು ಅವ ರನ್ನು ಕಠಿಣ ಶಿಕ್ಷೆಯಿಂದ ಪಾರುಗೊಳಿಸಿದೆವು');
INSERT INTO "kannada" VALUES(11,59,'11:59','ಇವರೇ ಆದ್ ಜನಾಂಗದವರು. ಇವರು ತಮ್ಮ ಪ್ರಭುವಿನ ನಿದರ್ಶನಗಳನ್ನು ನಿಷೇಧಿಸಿದರು, ಅವನ ದೂತರುಗಳನ್ನು ಧಿಕ್ಕರಿಸಿದರು. ಹಠವಾದಿಗಳಾದ ಎಲ್ಲ ಸ್ವೇಚ್ಛಾಧಿಪತಿಗಳ ಆದೇಶವನ್ನು ಅವರು ಅನುಸರಿಸಿದರು.');
INSERT INTO "kannada" VALUES(11,60,'11:60','ಇಹಲೋಕದಲ್ಲಿಯೂ ಪುನರುತ್ಥಾನ ದಿನದಲ್ಲೂ ಶಾಪ ಅವರನ್ನು ಹಿಂಬಾಲಿಸುವುದು. ತಿಳಿದಿರಲಿ. ಆದ್ ಜನಾಂಗವು ತನ್ನ ಪ್ರಭುವನ್ನು ನಿರಾಕರಿಸಿತು. ತಿಳಿದಿರಲಿ! ಹೂದ್‍ರ ಜನಾಂಗದವರಾದ ಆದ್ ದೇವಾನುಗ್ರಹದಿಂದ ದೂರವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(11,61,'11:61','ಸಮೂದರ ಕಡೆಗೆ ಅವರ ಸಹೋದರ ಸಾಲಿಹರ ನ್ನು (ನಾವು ಕಳುಹಿಸಿದೆವು). ಅವರು ಹೇಳಿದರು: ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧವರೇ, ನೀವು ಅಲ್ಲಾಹ ನನ್ನು ಆರಾಧಿಸಿರಿ. ಅವನ ಹೊರತು ಬೇರಾವ ಆರಾಧ್ಯನೂ ನಿಮಗಿಲ್ಲ. ಅವನೇ ನಿಮ್ಮನ್ನು ಮಣ್ಣಿ ನಿಂದ ಸೃಷ್ಟಿಸಿದನು ಮತ್ತು ಅದರಲ್ಲಿ ನೆಲೆಗೊ ಳಿಸಿದನು. ಆದುದರಿಂದ ನೀವು ಅವನೊಡನೆ ಕ್ಷಮೆಯಾಚಿಸಿರಿ ಮತ್ತು ಆತನೆಡೆಗೆ ಖೇದಪಟ್ಟು ಮರಳಿರಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ನನ್ನ ಪ್ರಭು ಸೃಷ್ಟಿ ಗಳಿಗೆ ನಿಕಟನೂ (ಪ್ರಾರ್ಥನೆಗಳಿಗೆ) ಉತ್ತರ ಕೊಡುವವನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(11,62,'11:62','ಅವರು ಹೀಗೆ ಹೇಳಿದರು; `ಓ ಸಾಲಿಹ್, ಈ ಮೊದಲು ನೀನು ನಮ್ಮಡೆಯಲ್ಲಿ ಅಪೇಕ್ಷಣೀಯ ನಾಗಿದ್ದೆ. ನೀನು ಇದೀಗ ನಮ್ಮ ಪೂರ್ವಿಕರು ಆರಾಧಿಸಿ ಕೊಂಡಿದ್ದವುಗಳನ್ನು ಪೂಜಿಸುವುದ ರಿಂದ ನಮ್ಮನ್ನು ತಡೆಯುತ್ತೀಯಾ? ನೀನು ಯಾವ ದಾರಿಗೆ ನಮ್ಮನ್ನು ಕರೆಯುತ್ತಿರುವಿಯೋ ಅದರ ಬಗ್ಗೆ ನಮಗೆ ತೀವ್ರ ಸಂದೇಹವಿದೆ.');
INSERT INTO "kannada" VALUES(11,63,'11:63','ಸಾಲಿಹ್ ಹೀಗೆಂದರು, ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧ ವರೇ, ನೀವು ಯೋಚಿಸಿದಿರಾ? ನಾನು ನನ್ನ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಸುವ್ಯಕ್ತ ಪ್ರಮಾಣ ವನ್ನಿರಿಸಿಕೊಂಡಿದ್ದರೆ ಹಾಗೂ ಅವನ ಕಡೆಯಿಂ ದಿರುವ ಕೃಪೆಯನ್ನೂ ಅವನು ನನಗೆ ದಯಪಾಲಿಸಿದ್ದರೆ, ಆ ಬಳಿಕ ನಾನು ಅವನ ಆಜ್ಞೋಲ್ಲಂಘನೆ ಮಾಡಿದರೆ, ನನ್ನನ್ನು ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆಯಿಂದ ರಕ್ಷಿಸಿ ನೆರವಾಗುವವರಾರು? ನೀವು ನನ್ನನ್ನು ಇನ್ನಷ್ಟು ನಷ್ಟಕ್ಕೆ ಗುರಿಪಡಿಸುವಿರೇ ವಿನಾ ಇನ್ನೇನೂ ಮಾಡಲಾರಿರಿ.');
INSERT INTO "kannada" VALUES(11,64,'11:64','ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧವರೇ, ಇದೋ ನಿಮಗೊಂದು ನಿದರ್ಶನವಾಗಿ ಅಲ್ಲಾಹನ ಒಂಟೆ. ಇದನ್ನು ಅಲ್ಲಾಹನ ಭೂಮಿಯಲ್ಲಿ ಸ್ವತಂತ್ರವಾಗಿ ನಡೆದು ಮೇಯಲು ಬಿಡಿರಿ. ಕೇಡು ಬಗೆಯಲು ಅದನ್ನು ಸಮೀಪಿಸಬೇಡಿರಿ. ಅನ್ಯಥಾ ಶೀಘ್ರವೇ ನಿಮ್ಮನ್ನು ಶಿಕ್ಷೆ ಬಾದಿಸುವುದು.');
INSERT INTO "kannada" VALUES(11,65,'11:65','ಆದರೆ ಅವರು ಅದನ್ನು ತರಿದು ಕೊಂದರು. ಆಗ ಸಾಲಿಹ್‍ರು ಹೇಳಿದರು; ನೀವಿನ್ನು ಬರೇ ಮೂರು ದಿನಗಳ ತನಕ ನಿಮ್ಮ ಮನೆಗಳಲ್ಲಿ ಸುಖವಾಗಿ ವಾಸಿಸಿರಿ. ಸುಳ್ಳಾಗದಂತಹ ವಾಗ್ದಾನವಿದು.');
INSERT INTO "kannada" VALUES(11,66,'11:66','ಕೊನೆಗೆ ನಮ್ಮ ಆಜ್ಞೆ ಬಂದಾಗ ನಾವು ನಮ್ಮ ಕೃಪೆಯಿಂದ ಸಾಲಿಹರನ್ನೂ ಅವರ ಮೇಲೆ ವಿಶ್ವಾಸವಿಟ್ಟವರನ್ನೂ ರಕ್ಷಿಸಿದೆವು ಮತ್ತು ಆ ದಿನದ ಅಪಮಾನದಿಂದ ಅವರನ್ನು ಕಾಪಾಡಿದೆವು. ನಿಶ್ಚಯವಾಗಿಯೂ ನಿಮ್ಮ ಪ್ರಭುವೇ ಪ್ರಬಲನೂ ಅಜೇಯನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(11,67,'11:67','ಅಕ್ರಮವೆಸಗಿದವರನ್ನು ಒಂದು ಘೋರ ಶಬ್ಧವು ಹಿಡಿಯಿತು. ಅವರು ಅವರ ಮನೆಗಳಲ್ಲಿ ಬೋರಲಾಗಿ ಸತ್ತು ಬಿದ್ದರು!');
INSERT INTO "kannada" VALUES(11,68,'11:68','ಅವರು ಅಲ್ಲಿ ವಾಸಿಸಿರಲೇ ಇಲ್ಲವೆಂಬಂತೆ (ಅವರು ನಾಶವಾದರು). ತಿಳಿದಿರಲಿ! ಸಮೂದರು ತಮ್ಮ ಪ್ರಭುವನ್ನು ನಿಷೇಧಿಸಿದರು. ತಿಳಿದಿರಲಿ! ಸಮೂದರು ದೇವಾನುಗ್ರಹದಿಂದ ವಿದೂರವಾದರು.');
INSERT INTO "kannada" VALUES(11,69,'11:69','ನಮ್ಮ ದೂತರು ಇಬ್‍ರಾಹೀಮರ ಬಳಿ ಸುವಾರ್ತೆಯೊಂದಿಗೆ ಬಂದರು. ನಿಮಗೆ ಸಲಾಮ್ ಎಂದರು. ಇಬ್‍ರಾಹೀಮರು ನಿಮಗೂ ಸಲಾಮ್ ಎಂದರು. ತಡವಾಗಲಿಲ್ಲ. ಅವರು ಹುರಿದ ಒಂದು ಕರುವನ್ನು ತಂದು ಅವರ (ಭೋಜ ನಕ್ಕಾಗಿ) ಮುಂದಿಟ್ಟರು.');
INSERT INTO "kannada" VALUES(11,70,'11:70','ಆದರೆ ಅವರ ಕೈಗಳು ಆಹಾರದ ಕಡೆಗೆ ಮುಟ್ಟ ದಿರುವುದನ್ನು ಕಂಡಾಗ ಅವರಿಗೆ ಅತಿಥಿಗಳ ಬಗ್ಗೆ ಅಸಹನೆ ತೋರಿತು. ಮತ್ತು ತಣ್ಣನೆ ಭಯ ಆವರಿಸಿತು. ಅವರು ಹೀಗೆಂದರು; `ಹೆದರ ಬೇಡಿರಿ, ನಾವು ಲೂಥರ ಜನಾಂಗದ ಕಡೆಗೆ ಕಳುಹಿಸಲ್ಪಟ್ಟವರು.');
INSERT INTO "kannada" VALUES(11,71,'11:71','ಇಬ್‍ರಾಹೀಮರ ಪತ್ನಿಯೂ ನಿಂತಿದ್ದರು. ಅವರು ಇದನ್ನು ಕೇಳಿ ನಕ್ಕು ಬಿಟ್ಟರು. ಅನಂತರ ನಾವು ಆಕೆಗೆ ಇಸ್‍ಹಾಖ್ ಮತ್ತು ಇಸ್‍ಹಾಖರ ತರುವಾಯ ಯಅïಖೂಬರ ಬಗೆಗೂ ಸುವಾರ್ತೆ ನೀಡಿದೆವು.');
INSERT INTO "kannada" VALUES(11,72,'11:72','ಅವರು ಹೇಳಿದರು: `ಅಯ್ಯೋ’, ನಾನು ಹಣ್ಣು ಮುದುಕಿಯೂ ನನ್ನ ಈ ಪತಿಯಾದರೋ ಕಡು ವೃದ್ಧರೂ ಆಗಿರುವಾಗ ನನ್ನಲ್ಲಿ ಮಕ್ಕಳಾಗುವರೇ? ಇದು ಅತ್ಯಂತ ಸೋಜಿಗದ ವಿಷಯ!');
INSERT INTO "kannada" VALUES(11,73,'11:73','(ದೇವಚರರು) ಹೇಳಿದರು; ಅಲ್ಲಾಹನ ಕಾರ್ಯದ ಬಗ್ಗೆ ಆಶ್ಚರ್ಯಪಡುತ್ತೀರಾ? ಓ ಮನೆಯವರೇ, ನಿಮ್ಮ ಮೇಲೆ ಅಲ್ಲಾಹನ ದಯೆ ಮತ್ತು ಸಮೃದ್ಧಿ ಉಂಟಾಗಲಿ. ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹು ಪರಮ ಸ್ತುತ್ಯರ್ಹನೂ ಮಹಿಮಾವಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(11,74,'11:74','ಹೀಗೆ ಇಬ್‍ರಾಹೀಮರ ಭಯ ನೀಗಿದಾಗ ಮತ್ತು ಅವರಿಗೆ ಸುವಾರ್ತೆ ಸಿಕ್ಕಿದ ನಂತರ ಅವರು ನಮ್ಮೊಡನೆ ಲೂಥ್ ಜನಾಂಗದ ಕುರಿತು ವಾಗ್ವಾದ ನಡೆಸಿದರು .');
INSERT INTO "kannada" VALUES(11,75,'11:75','ನಿಜಕ್ಕೂ ಇಬ್‍ರಾಹೀಮರು ಸಹನಶೀಲರೂ ಅನುಕಂಪವುಳ್ಳವರೂ ಕೋಮಲ ಹೃದಯಿಯೂ ಪಶ್ಚಾತ್ತಾಪವುಳ್ಳವರೂ ಆಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(11,76,'11:76','`ಓ ಇಬ್‍ರಾಹೀಮರೇ, ಈ ತರ್ಕದಿಂದ ಬಿಟ್ಟು ನಿಲ್ಲಿರಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಅಪ್ಪಣೆ ಬಂದು ಬಿಟ್ಟಿದೆ. ಇನ್ನು ಅವರಿಗೆ ರದ್ದು ಪಡಿಸಲಾಗದಂತಹ ಶಿಕ್ಷೆ ಬಂದೇ ತೀರುವುದು.');
INSERT INTO "kannada" VALUES(11,77,'11:77','ನಮ್ಮ ದೂತರು `ಲೂಥ’ರ ಬಳಿಗೆ ಬಂದಾಗ ಅವರ ಕಾರಣಕ್ಕೆ ಅವರು ತುಂಬಾ ಬೇಸರಗೊಂಡರು ಮತ್ತು ಅವರ ಮನಸ್ಸು ಕುಗ್ಗಿ ಹೋಯಿತು. ಇದೊಂದು ಕಠಿಣ ದಿನ ಎಂದರವರು .');
INSERT INTO "kannada" VALUES(11,78,'11:78','ಅವರ ಜನಾಂಗದವರು ಅವರ ಮನೆಯ ಕಡೆಗೆ ಓಡುತ್ತ ಬಂದರು. ಅವರು ಮುಂಚಿನಿಂದಲೇ ದುಶ್ಚಟವಿದ್ದವರಾಗಿದ್ದರು. ಅವರೊಡನೆ ಲೂಥ್ ಹೀಗೆಂದರು, ಓ ನನ್ನ ಜನಾಂಗದವರೇ, ಇದೋ ನನ್ನ ಹೆಣ್ಣು ಮಕ್ಕಳು . ಇವರು ನಿಮಗೆ ಶುದ್ಧರಾದವರು. (ಅವರನ್ನು ನೀವು ವಿವಾಹವಾಗ ಬಹು ದಲ್ಲ?) ಆದ್ದರಿಂದ ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ. ನನ್ನ ಅತಿಥಿಗಳನ್ನು ಅಪಮಾನಿಸುವ ಮೂಲಕ ನನ್ನನ್ನು ಅಪಮಾನಿಸಬೇಡಿರಿ. ನಿಮ್ಮಲ್ಲಿ ವಿವೇಕವಿರುವ ಒಬ್ಬ ಪುರುಷನೂ ಇಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(11,79,'11:79','ಅವರು ಹೇಳಿದರು, ನಿಮ್ಮ ಪುತ್ರಿಯರಲ್ಲಿ ನಮ ಗೇನೂ ಅವಶ್ಯಕತೆ ಇಲ್ಲವೆಂಬುದು ನಿಮಗೆ ಗೊತ್ತೇ ಇದೆ. ನಾವು ಇಚ್ಛಿಸುವುದೇನೆಂಬುದೂ ನಿಮಗೆ ತಿಳಿದಿದೆ.');
INSERT INTO "kannada" VALUES(11,80,'11:80','ಲೂಥ್ ಹೇಳಿದರು; ನಿಮ್ಮನ್ನು ತಡೆಯುವ ಶಕ್ತಿ ನನಗಿರುತ್ತಿದ್ದರೆ ! ಅಥವಾ ಬಲಿಷ್ಠ ಸಂಘದೆಡೆಗೆ ನಾನು ಆಶ್ರಯ ಪಡೆಯುತ್ತಿದ್ದರೆ, (ನಿಮ್ಮನ್ನು ನಾನು ಮಟ್ಟ ಹಾಕುತ್ತಿದ್ದೆ).');
INSERT INTO "kannada" VALUES(11,81,'11:81','ಆಗ ದೇವಚರರು ಹೇಳಿದರು; `ಓ ಲೂಥ್, ನಾವು ನಿಮ್ಮ ಪ್ರಭುವಿನ ದೂತರಾಗಿರುತ್ತೇವೆ. ನಿಮ್ಮ ಕಡೆಗೆ (ಕೇಡುಂಟು ಮಾಡಲು) ಅವರಿಗೆ ಖಂಡಿ ತ ಬರಲಾಗದು. ಆದ್ದರಿಂದ ರಾತ್ರಿಯ ಒಂದು ಯಾಮದಲ್ಲಿ ನಿಮ್ಮ ಕುಟುಂಬದವರನ್ನು ಸಂ ಗಡ ಕರೆದು ಕೊಂಡು ಯಾತ್ರೆ ಹೊರಡಿರಿ. ನಿಮ್ಮಲ್ಲಿ ಯಾರೊಬ್ಬರೂ ಹಿಂದೆ ತಿರುಗಿ ನೋಡಬಾರದು. ಆದರೆ ನಿಮ್ಮ ಪತ್ನಿಯನ್ನು ಹೊರತು. ಏಕೆಂದರೆ ಇವರಿಗೆ ತಲುಪಿದ ಶಿಕ್ಷೆ ಅವಳಿಗೂ ತಲುಪು ವುದು. ಅವರ ವಿನಾಶಕ್ಕಾಗಿ ನಿಶ್ಚಯಿಸಲಾದ ಅವಧಿಯು ಪ್ರಾತಃಕಾಲವಾಗಿದೆ. ಪ್ರಾತಃಕಾಲ ಹತ್ತಿರವೇ ಇದೆಯಲ್ಲ!');
INSERT INTO "kannada" VALUES(11,82,'11:82','ಹೀಗೆ ನಮ್ಮ ಆಜ್ಞೆ ಬಂದಾಗ ನಾವು ಆ ನಾಡನ್ನು ಬುಡಮೇಲು ಮಾಡಿಬಿಟ್ಟೆವು ಮತ್ತು ಅವರ ಮೇಲೆ ಸುಡುಕಲ್ಲುಗಳನ್ನು ಎಡೆಬಿಡದೆ ಮಳೆಗರೆದೆವು.');
INSERT INTO "kannada" VALUES(11,83,'11:83','ಅವು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಬಳಿ ಗುರುತು ಹಾಕ ಲಾದ ಕಲ್ಲುಗಳಾಗಿದ್ದುವು . ಆ ನಾಡು ಈ ಅಕ್ರಮಿಗಳಿಂದ ದೂರವಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(11,84,'11:84','ನಾವು `ಮದ್‍ಯನ್’ ಜನಾಂಗದ ಕಡೆಗೆ ಅವರ ಸಹೋದರ ಶುಐಬರನ್ನು ಕಳುಹಿಸಿದೆವು. ಅವರು ಹೇಳಿದರು; ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧವರೇ, ನೀವು ಅಲ್ಲಾಹನನ್ನು ಆರಾಧಿಸಿರಿ. ಅವನ ಹೊರ ತು ನಿಮಗೆ ಬೇರೆ ಆರಾಧ್ಯನಿಲ್ಲ. ನೀವು ಅಳತೆ ಮತ್ತು ತೂಕದಲ್ಲಿ ಕಡಿತ ಮಾಡಬೇಡಿರಿ. ಇಂದು ನಾನು ನಿಮ್ಮನ್ನು ಉತ್ತಮ ಸ್ಥಿತಿಯಲ್ಲಿ ಕಾಣುತ್ತಿ ದ್ದೇನೆ. ಆದರೆ ನಿಮ್ಮ ಮೇಲೆ, ಸಂಪೂರ್ಣ ಆವರಿ ಸಿಕೊಳ್ಳುವ ಶಿಕ್ಷೆಯ ದಿನವೊಂದು ಎರಗುವುದನ್ನು ನಾನು ಭಯಪಡುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(11,85,'11:85','ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧವರೇ, ನ್ಯಾಯೋಚಿತ ವಾಗಿ ಅಳೆಯಿರಿ ಮತ್ತು ತೂಗಿರಿ. ಜನರಿಗೆ ಅವರ ವಸ್ತುಗಳನ್ನು ಕಡಿತಗೊಳಿಸಿ ಕೊಡಬೇಡಿರಿ ಮತ್ತು ಭೂಮಿಯಲ್ಲಿ ಕ್ಷೋಭೆ ಹರಡುತ್ತ ಅಲೆಯ ಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(11,86,'11:86','ನೀವು ನಿಜಕ್ಕೂ ನಂಬಿಕೆಯುಳ್ಳವರಾಗಿದ್ದರೆ ಅಲ್ಲಾಹನು ನೀಡಿದ ಉಳಿತಾಯವೇ (ಅನ್ಯಾಯದ ಸಂಪಾದನೆಗಿಂತ) ನಿಮಗೆ ಉತ್ತಮ. ನಾನೇನೂ ನಿಮ್ಮ ಕಾವಲುಗಾರನಲ್ಲ.');
INSERT INTO "kannada" VALUES(11,87,'11:87','ಅವರು ಹೇಳಿದರು; `ಓ ಶುಐಬ್, ನಾವು ನಮ್ಮ ಪೂರ್ವಿಕರು ಆರಾಧಿಸುತ್ತಿದ್ದುದನ್ನು ತ್ಯಜಿಸ ಬೇಕೆಂದಾಗಲಿ, ನಮ್ಮ ಸೊತ್ತುಗಳಲ್ಲಿ ನಮಗೆ ಇಷ್ಟ ಬಂದ ಪ್ರಕಾರ ಮಾಡಬಾರದೆಂದಾಗಲಿ ನಿನ್ನ ನಮಾಝ್ ನಿನಗೆ ಆಜ್ಞಾಪಿಸುತ್ತಿದೆಯೇ? ನೀನೊಬ್ಬನು ಮಾತ್ರ ಮಹಾ ಸೌಜನ್ಯಶೀಲನೂ ಸನ್ಮಾರ್ಗನಿಷ್ಟನೂ ಆಗಿಬಿಟ್ಟಿರುವೆ!');
INSERT INTO "kannada" VALUES(11,88,'11:88','ಶುಐಬ್ ಹೇಳಿದರು; ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧವರೇ, ನೀವು ವಿವೇಚಿಸಿ ನೋಡಿದಿರಾ? ನಾನು ನನ್ನ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಒಂದು ಸುಸ್ಪಷ್ಟ ಪ್ರಮಾಣವನ್ನು ಅವಲಂಬಿಸಿದ್ದು, ಅವನು ನನಗೆ ತನ್ನ ಕಡೆಯಿಂದ ಉತ್ತಮ ಜೀವನಾಧಾರವನ್ನೂ ದಯಪಾಲಿಸಿದ್ದರೆ, (ನನಗೆ ಸತ್ಯವನ್ನು ಅಡಗಿಸಿಡಲು ಹೇಗೆ ಸಾಧ್ಯ?) ನಾನು ನಿಮ್ಮನ್ನು ಯಾವುದರಿಂದ ತಡೆಯುತ್ತಿರು ವೆನೋ ಅದನ್ನು ನಾನೇ ಮಾಡಲು ಇಚ್ಚಿಸುವು ದಿಲ್ಲ. ನಾನು ನನ್ನಿಂದ ಸಾಧ್ಯವಾಗುವಷ್ಟು ಸುಧಾರಣೆ ಮಾಡಲಿಚ್ಛಿಸುತ್ತೇನೆ. ಅಲ್ಲಾಹನ ಮುಖೇನ ಮಾತ್ರ ನನಗೆ ಅದರ ಯೋಗ ಬಂದಿದೆ. ನಾನು ಅವನ ಮೇಲೆಯೇ ಭರವಸೆ ಯನ್ನಿರಿಸಿದ್ದೇನೆ ಮತ್ತು ಅವನತ್ತಲೇ ನಾನು ದೈನ್ಯತೆಯಿಂದ ಮರಳುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(11,89,'11:89','ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧವರೇ, ನೂಹರ ಜನಾಂಗ ಅಥವಾ ಹೂದರ ಜನಾಂಗ ಅಥವಾ ಸಾಲಿಹರ ಜನಾಂಗಕ್ಕೆ ಬಾಧಿಸಿದಂತಹ ವಿನಾ ಶವು ನಿಮಗೂ ಬಾಧಿಸಲು ನನ್ನೊಂದಿಗಿರುವ ನಿಮ್ಮ ವಿರೋಧವು ಕಾರಣವಾಗದಿರಲಿ. ಲೂಥರ ಜನಾಂಗವು ನಿಮಗಿಂತ ಹೆಚ್ಚೇನೂ ದೂರವಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(11,90,'11:90','ನೀವು ನಿಮ್ಮ ಪ್ರಭುವಿನೊಡನೆ ಕ್ಷಮೆ ಬೇಡಿರಿ. ಅವನ ಕಡೆಗೆ ಖೇದಿಸಿ ಮರಳಿರಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ನನ್ನ ಪ್ರಭು ಕೃಪಾನಿಧಿಯೂ (ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳಿಗೆ) ಪ್ರಿಯಕರನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(11,91,'11:91','ಅವರು ಹೀಗೆ ಹೇಳಿದರು; `ಓ ಶುಐಬ್, ನಿನ್ನ ಹೆಚ್ಚಿನ ಮಾತುಗಳು ನಮಗೆ ಅರ್ಥವೇ ಆಗು ವುದಿಲ್ಲ. ನೀನು ನಮ್ಮೊಳಗೆ ಓರ್ವ ನಿಂದ್ಯನಾಗಿ ರುವುದನ್ನು ನಾವು ಕಾಣುತ್ತೇವೆ. ನಿನ್ನ ಸಂಸಾರ ವಿಲ್ಲದಿರುತ್ತಿದ್ದರೆ ನಿನ್ನನ್ನು ನಾವು ಕಲ್ಲೆಸೆದು ಕೊಂದು ಹಾಕುತ್ತಿದ್ದೆವು. ನೀನು ನಮಗಿಂತ ಮಾನ್ಯನೇನೂ ಅಲ್ಲ.');
INSERT INTO "kannada" VALUES(11,92,'11:92','ಶುಐಬ್ ಹೇಳಿದರು; ಅಲ್ಲಾಹನನ್ನು ಬೆನ್ನ ಹಿಂದೆ ತಳ್ಳಿ ಬಿಟ್ಟ ನಿಮಗೆ ನನ್ನ ಕುಟುಂಬ ಪರಿವಾರವು ನಿಮ್ಮ ದೃಷ್ಟಿಯಲ್ಲಿ ಅಲ್ಲಾಹನಿಗಿಂತಲೂ ದೊಡ್ಡ ದಾಯಿತೆ? ನಿಮ್ಮ ಕೃತ್ಯಗಳನ್ನು ಅಲ್ಲಾಹನು ಸಂಪೂರ್ಣ ಬಲ್ಲವನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(11,93,'11:93','ಓ ನನ್ನ ಜನಾಂಗ ಬಾಂಧವರೇ, ನೀವು ನಿಮ್ಮ ನಿಲುವು ಪ್ರಕಾರ ಕಾರ್ಯವೆಸಗಿರಿ, ನಾನು ನನ್ನ ನಿಲುವು ಪ್ರಕಾರ ಕಾರ್ಯವೆಸಗುತ್ತಿರುವೆನು. ಯಾರ ಮೇಲೆ ಅಪಮಾನಕರ ಶಿಕ್ಷೆ ಬರುತ್ತದೆ ಮತ್ತು ಯಾರು ಸುಳ್ಳುಗಾರರು ಎಂಬುದು ನಿಮಗೆ ಶೀಘ್ರವೇ ತಿಳಿದು ಬರುವುದು. ನೀವೂ (ನಿಮ್ಮ ಅಂತಿಮ ಗತಿಯನ್ನು) ನಿರೀಕ್ಷಿಸಿರಿ. ನಾನೂ ನಿಮ್ಮೊಂದಿಗೆ ನಿರೀಕ್ಷಿಸುತ್ತೇನೆ .');
INSERT INTO "kannada" VALUES(11,94,'11:94','ಕೊನೆಗೆ ನಮ್ಮ ಆದೇಶ ಬಂದಾಗ ನಾವು ನಮ್ಮ ಕಡೆಯ ಕಾರುಣ್ಯದ ಮೇರೆಗೆ ಶುಐಬ್ ಮತ್ತು ಅವರ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಾದ ಸಂಗಡಿಗರನ್ನು ರಕ್ಷಿಸಿದೆವು ಮತ್ತು ಅಕ್ರಮವೆಸಗಿದವರನ್ನು ಒಂದು ಘೋರ ಶಬ್ಧವು ಅಕ್ರಮಿಸಿತು. ಹಾಗೆ ಬೆಳಗಾದಾಗ ಅವರು ತಮ್ಮ ವಸತಿಗಳಲ್ಲೇ ಬೋರಲಾಗಿ ಸತ್ತು ಬಿದ್ದಿದ್ದರು.');
INSERT INTO "kannada" VALUES(11,95,'11:95','ಅವರು ಅಲ್ಲಿ ನೆಲೆಸಿಯೇ ಇರಲಿಲ್ಲವೋ ಎಂಬಂತೆ (ಸರ್ವನಾಶವಾದರು). ತಿಳಿದಿರಲಿ ಸಮೂದ್ ಜನಾಂಗವು ಕೃಪೆಯಿಂದ ದೂರವಾದಂತೆ ಮದ್‍ಯನಿನವರೂ ದೂರವಾದರು.');
INSERT INTO "kannada" VALUES(11,96,'11:96','ಮೂಸಾರನ್ನು ನಮ್ಮ ನಿದರ್ಶನಗಳೊಂದಿಗೂ ಸುವ್ಯಕ್ತ ಆಧಾರದೊಂದಿಗೂ ನಾವು ಕಳುಹಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(11,97,'11:97','ಫಿರ್‍ಔನ್ ಮತ್ತು ಅವನ ಸರದಾರರ ಕಡೆಗೆ. ಆದರೆ, ಅವರು (ಸರದಾರರು) ಫಿರ್‍ಔನನ ಆಜ್ಞೆ ಯನ್ನು ಅನುಸರಿಸಿದರು. ವಸ್ತುತಃ ಫಿರ್‍ಔನನ ಆಜ್ಞೆಯು ವಿವೇಕಪೂರ್ಣವಾಗಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(11,98,'11:98','ಪುನರುತ್ಥಾನ ದಿನದಂದು ಅವನು ತನ್ನ ಜನಾಂ ಗದ ಮುಂಚೂಣಿಯಲ್ಲಿದ್ದುಕೊಂಡು ಅವರನ್ನು ನರಕದ ಕಡೆಗೆ ಒಯ್ಯುವನು. ಅವರು ತಲುಪುವ ಸ್ಥಾನ ಅದೆಷ್ಟು ನಿಕೃಷ್ಟ.');
INSERT INTO "kannada" VALUES(11,99,'11:99','ಇಹಲೋಕದಲ್ಲೂ ಪುನರುತ್ಥಾನದಂದೂ ಶಾಪವು ಅವರ ಹಿಂದೆಯೇ ಅನುಗಮಿಸಿತು. ಒಂದನ್ನೊಂದು ಉಲ್ಬಣಗೊಳಿಸಿದ ಈ ಶಾಪದ ಕೊಡುಗೆ ಅದೆಷ್ಟು ನಿಕೃಷ್ಟ.');
INSERT INTO "kannada" VALUES(11,100,'11:100','ನಾವು ನಿಮಗೆ ವಿವರಿಸಿ ಹೇಳುವ ಕೆಲವು ನಾಡುಗಳ ವೃತ್ತಾಂತಗಳಿವು . ಇವುಗಳಲ್ಲಿ ಕೆಲವು ನಾಡುಗಳು ಈಗಲೂ ನೆಲೆನಿಂತಿವೆ. ಇನ್ನು ಕೆಲವು ನಿರ್ಮೂಲನಗೊಂಡಿವೆ.');
INSERT INTO "kannada" VALUES(11,101,'11:101','ನಾವು ಅವರ ಮೇಲೆ ಅಕ್ರಮವೆಸಗಲಿಲ್ಲ. ಅವರು ತಾವೇ ತಮ್ಮ ಮೇಲೆ ಅಕ್ರಮವೆಸಗಿ ಕೊಂಡರು. ನಿಮ್ಮ ಪ್ರಭುವಿನ ಆಜ್ಞೆ ಬಂದಾಗ ಅವರು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಆರಾಧಿಸುತ್ತಿದ್ದ ಅವರ ದೇವರುಗಳು ಅವರ ರಕ್ಷಣೆಗೆ ಬರಲಿಲ್ಲ. ಅವರಿಗೆ ವಿನಾಶವನ್ನು ಹೊರತು ಇನ್ನೇನನ್ನೂ ಅವರು ಹೆಚ್ಚಿಸಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(11,102,'11:102','ನಿಮ್ಮ ಪ್ರಭು ಯಾವುದಾದರೂ ಅಕ್ರಮಿ ನಾಡನ್ನು ಹಿಡಿಯುವಾಗ ಅವನ ಹಿಡಿತ ಹೀಗೆಯೇ ಇರು ತ್ತದೆ. (ಯಾವ ವಸ್ತುವೂ ಅದನ್ನು ತಡೆಯದು) ವಾಸ್ತವದಲ್ಲಿ ಅವನ ಹಿಡಿತವು ಬಹಳ ವೇದನಾಯುಕ್ತವೂ ಕಠಿಣವೂ ಆಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(11,103,'11:103','ಪರಲೋಕ ಶಿಕ್ಷೆಯನ್ನು ಭಯಪಡುವವರಿಗೆ ಅದರಲ್ಲಿ ನಿದರ್ಶನವಿದೆ. ಅದು ಸಕಲ ಜನರೂ ಒಟ್ಟುಗೂಡುವ ಒಂದು ದಿನವಾಗಿರುವುದು. ಸರ್ವರೂ ಹಾಜರಾಗಿ ಸಾಕ್ಷಿಯಾಗುವ ದಿನವದು .');
INSERT INTO "kannada" VALUES(11,104,'11:104','ನಿಶ್ಚಿತ ಕಾಲಾವಧಿಗೆ ಹೊರತು ನಾವು ಅದನ್ನು ವಿಳಂಬ ಮಾಡುತ್ತಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(11,105,'11:105','ಅದು ಬಂದಾಗ ಆತನ ಅನುಮತಿ ಇಲ್ಲದೆ ಯಾರೊಬ್ಬನೂ ಮಾತೆತ್ತಲಾರ. ಅಂದು ಅವರಲ್ಲಿ ನಿರ್ಭಾಗ್ಯ ವಂತರೂ ಸೌಭಾಗ್ಯವಂತರೂ ಇರುವರು.');
INSERT INTO "kannada" VALUES(11,106,'11:106','ನಿರ್ಭಾಗ್ಯವಂತರು ನರಕಕ್ಕೆ ಹೋಗುವರು. ಅದರಲ್ಲವರು ಆಕ್ರಂಧನ ಮಾಡುತ್ತಲೂ ಮುಸಿಮುಸಿ ಅಳುತ್ತಲೂ ಇರುವರು.');
INSERT INTO "kannada" VALUES(11,107,'11:107','ಆಕಾಶಗಳೂ ಭೂಮಿಯೂ ನೆಲೆ ನಿಂತಿರುವ ವರೆಗೂ ಅವರು ಅದರಲ್ಲಿ ನಿತ್ಯ ವಾಸಿಗಳಾಗಿರುವರು . ನಿಮ್ಮ ಪ್ರಭು ಇಚ್ಛಿಸಿದ್ದು ಹೊರತು . ನಿಶ್ಚಯವಾಗಿಯೂ ನಿಮ್ಮ ಪ್ರಭು ತಾನು ಬಯಸಿದ್ದನ್ನು ಮಾಡುವನು.');
INSERT INTO "kannada" VALUES(11,108,'11:108','ಸೌಭಾಗ್ಯ ಸಿದ್ದಿಸಿದವರು - ಅವರು ಸ್ವರ್ಗಕ್ಕೆ ಹೋಗುವರು. ಅವರು ಆಕಾಶಗಳೂ ಭೂಮಿಯೂ ನೆಲೆನಿಂತಿರುವವರೆಗೂ ಅದರಲ್ಲಿ ಸದಾ ವಾಸಿಸುತ್ತಿರುವರು. ನಿಮ್ಮ ಪ್ರಭು ಇಚ್ಛಿಸಿದ್ದು ಹೊರತು. ಎಂದೂ ಮುಗಿದು ಹೋಗದ ಕೊಡುಗೆ ಅದು.');
INSERT INTO "kannada" VALUES(11,109,'11:109','ಆದುದರಿಂದ (ಓ ಪೈಗಂಬರರೇ,) ಇವರು ಆರಾಧನೆ ಮಾಡುತ್ತಿರುವವುಗಳ ಬಗ್ಗೆ ನೀವೆಂದೂ ಆಶಂಖೆಗೊಳಗಾಗಬೇಡಿರಿ. ಮುಂಚೆ ಇವರ ಪೂರ್ವಿಕರು ಆರಾಧಿಸಿಕೊಂಡು ಬಂದಂತೆ ಇವರೂ ಆರಾದಿಸುತ್ತಲಿದ್ದಾರೆ ಅಷ್ಟೆ ಮತ್ತು ಅವರಿಗೆ ಅವರ ಪಾಲನ್ನು ಕಡಿತಗೊಳಿಸದೆ ಸಂಪೂರ್ಣವಾಗಿ ಕೊಡುವೆವು.');
INSERT INTO "kannada" VALUES(11,110,'11:110','ಮೂಸಾರಿಗೆ ನಾವು ಗ್ರಂಥ ದಯಪಾಲಿಸಿದ್ದೆವು. ಆಗ ಅದರ ಬಗೆಗೂ ಭಿನ್ನಾಭಿಪ್ರಾಯ ತಲೆದೋ ರಿತು. ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಒಂದು ವಚನ ಮೊದಲೇ ನಿರ್ಧರಿಸಲ್ಪಡದಿರುತ್ತಿದ್ದರೆ ಅವರ ನಡುವೆ ತೀರ್ಮಾನ ಮಾಡಿಬಿಡಲಾಗು ತ್ತಿತ್ತು. ಖಂಡಿತ ಅವರು ಈ ಬಗ್ಗೆ ಅವಿಶ್ವಾಸಜ ನಕವಾದ ಸಂಶಯದಲ್ಲಿರುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(11,111,'11:111','ಅವರ ಪೈಕಿ ಪ್ರತಿಯೊಬ್ಬರಿಗೂ ನಿಮ್ಮ ಪ್ರಭು ಅವರ ಕರ್ಮಗಳ ಸಂಪೂರ್ಣ ಪ್ರತಿಫಲ ಕೊಟ್ಟೇ ತೀರುವನು. ನಿಶ್ಚಯವಾಗಿಯೂ ಅವನು ಅವರ ಸಕಲ ಕರ್ಮಗಳನ್ನು ಸೂಕ್ಷವಾಗಿ ಬಲ್ಲವನು.');
INSERT INTO "kannada" VALUES(11,112,'11:112','ಆದುದರಿಂದ (ಓ ಪೈಗಂಬರರೇ,) ನಿಮಗೆ ಆಜ್ಞಾಪಿಸಲಾಗಿರುವ ಪ್ರಕಾರ ನೀವೂ ನಿಮ್ಮ ಜೊತೆಗೆ ಮರಳಿದ ಸಂಗಡಿಗರೂ ನೇರ ಮಾರ್ಗದಲ್ಲಿ ಸ್ಥಿರವಾಗಿ ನೆಲೆ ನಿಲ್ಲಿರಿ. ಅಲ್ಲಾಹನಿತ್ತ ಮೇರೆ ಯನ್ನು ಮೀರಬೇಡಿರಿ. ನಿಮ್ಮ ಪ್ರಭು ನೀವು ಮಾಡುತ್ತಿರುವುದನ್ನೆಲ್ಲ ವೀಕ್ಷಿಸುತ್ತಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(11,113,'11:113','ಅಕ್ರಮಿಗಳ ಕಡೆಗೆ ವಾಲಬೇಡಿರಿ. ಅನ್ಯಥಾ ನರಕಾಗ್ನಿ ನಿಮಗೆ ತಟ್ಟುವುದು. ಅಲ್ಲಾಹನ ಹೊರತು ರಕ್ಷಕರಾರೂ ನಿಮಗಿಲ್ಲ. ನಂತರ ನಿಮಗೆ ಎಲ್ಲಿಂದಲೂ ನೆರವು ಸಿಗದು.');
INSERT INTO "kannada" VALUES(11,114,'11:114','ಹಗಲಿನ ಉಭಯ ಕೊನೆಗಳಲ್ಲೂ ರಾತ್ರೆಯ ಪ್ರಥಮ ಯಾಮಗಳಲ್ಲೂ ನಮಾಝನ್ನು ಸಂಸ್ಥಾಪಿಸಿರಿ . ವಾಸ್ತವದಲ್ಲಿ ಪುಣ್ಯ ಕಾರ್ಯಗಳು ಪಾಪ ಕಾರ್ಯಗಳನ್ನು ಹೋಗಲಾಡಿಸುತ್ತವೆ. ಇದು ಯೋಚನಾ ಬದ್ಧರಿಗೊಂದು ಯೋಚನಾರ್ಹ ಬೋಧನೆಯಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(11,115,'11:115','ತಾಳ್ಮೆಯಿಂದಿರಿ, ಅಲ್ಲಾಹು ಸುಕೃತರ ಪ್ರತಿಫಲ ವನ್ನೆಂದೂ ವ್ಯರ್ಥಗೊಳಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(11,116,'11:116','ಭೂಮಿಯಲ್ಲಿ ಕ್ಷೋಭೆ ನಡೆಸದಂತೆ ಜನರನ್ನು ತಡೆಯುವಂತಹ ಒಂದು ಧರ್ಮಬದ್ಧ ಕೂಟವು ನಿಮ್ಮ ಹಿಂದಿನ ಕಾಲಗಳಲ್ಲಿ ಇದ್ದಿರ ಲಿಲ್ಲ. ನಾವು ಆ ಜನಾಂಗಗಳಿಂದ ರಕ್ಷಿಸಿದಂತಹ ಸ್ವಲ್ಪ ಜನರ ಹೊರತು ಅಕ್ರಮಿಗಳು ಅವರಿಗೆ ಧಾರಾಳವಾಗಿ ದೊರೆತ ಸುಖ ಭೋಗಗಳನ್ನೇ ಹಿಂಬಾಲಿಸುತ್ತಿದ್ದರು. ಅವರು ಅಪರಾಧಿಗ ಳಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(11,117,'11:117','ನಾಡುಗಳ ನಿವಾಸಿಗಳು ಉತ್ತಮರಾಗಿದ್ದಲ್ಲಿ ನಿನ್ನ ಪ್ರಭು ಆ ನಾಡುಗಳನ್ನು ಅನ್ಯಾಯವಾಗಿ ನಾಶಗೊಳಿಸುವವನಲ್ಲ .');
INSERT INTO "kannada" VALUES(11,118,'11:118','ನಿಶ್ಚಯವಾಗಿಯೂ ನಿಮ್ಮ ಪ್ರಭು ಇಚ್ಛಿಸುತ್ತಿದ್ದರೆ ಮಾನವರನ್ನು ಒಂದೇ ಸಮುದಾಯವನ್ನಾಗಿ ಮಾಡುತ್ತಿದ್ದನು. ಆದರೆ ಅವರು ಭಿನ್ನಮತಿಗಳಾಗಿಯೇ ಮುಂದುವರಿಯುವರು .');
INSERT INTO "kannada" VALUES(11,119,'11:119','ನಿಮ್ಮ ಪ್ರಭು ಕೃಪೆ ತೋರಿದವರ ಹೊರತು. ಇದಕ್ಕಾಗಿಯೇ ಅವನು ಅವರನ್ನು ಸೃಷ್ಟಿಸಿರುತ್ತಾನೆ. ಜಿನ್ನ್‍ಗಳು ಮತ್ತು ಮನುಷ್ಯರ ಪೈಕಿ ಉಭಯ ವಿಭಾಗದಿಂದೆಲ್ಲ ನರಕವನ್ನು ತುಂಬಿಸುವೆನೆಂಬ ನಿನ್ನ ಪ್ರಭುವಿನ ವಚನವು ಪೂರ್ಣಗೊಂಡಿದೆ.');
INSERT INTO "kannada" VALUES(11,120,'11:120','(ಓ ಪೈಗಂಬರರೇ,) ಸಂದೇಶವಾಹಕರ ವೃತ್ತಾಂ ತಗಳಿಂದ ನಿಮ್ಮ ಮನಸ್ಸನ್ನು ಬಲಪಡಿಸುವ ವಿಚಾರವನ್ನು ನಿಮಗೆ ನಾವು ವಿವರಿಸಿದ್ದೇವೆ. ಇವುಗಳಿಂದ ನಿಮಗೆ ಪರಮಾರ್ಥ ಜ್ಞಾನವೂ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಉಪದೇಶ ಹಾಗೂ ಜಾಗೃತಿ ಲಭಿಸಿತು.');
INSERT INTO "kannada" VALUES(11,121,'11:121','ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ನಿರಾಕರಿಸಿದವರಲ್ಲಿ ಹೇಳಿರಿ - ನೀವು ನಿಮ್ಮ ನಿಲುವು ಪ್ರಕಾರ ಕಾರ್ಯವೆಸಗಿರಿ ಮತ್ತು ನಾವು ಕಾರ್ಯ ವೆಸಗುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(11,122,'11:122','ಅಂತಿಮ ಪರಿಣಾಮವನ್ನು ನೀವೂ ನಿರೀಕ್ಷಿಸಿರಿ. ನಾವೂ ನಿರೀಕ್ಷಿಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(11,123,'11:123','ಆಕಾಶಗಳ ಹಾಗೂ ಭೂಮಿಯ ಅದೃಶ್ಯವು ಅಲ್ಲಾಹನ ಅಧೀನದಲ್ಲಿದೆ. ಸಕಲ ಕಾರ್ಯಗಳೂ ಆತನ ಕಡೆಗೇ ಮರಳಿಸಲ್ಪಡುತ್ತವೆ. ಆದುದರಿಂದ (ಓ ಪೈಗಂಬರರೇ,) ನೀವು ಅವನನ್ನು ಆರಾಧಿಸಿರಿ. ಅವನ ಮೇಲೆ ಭರವಸೆಯನ್ನಿಡಿರಿ. ನೀವು ಮಾಡುತ್ತಿರುವುದರ ಬಗೆಗೆ ನಿಮ್ಮ ಪ್ರಭು ಅಶ್ರದ್ಧನಲ್ಲ.');
INSERT INTO "kannada" VALUES(12,1,'12:1','ಅಲಿಫ್ ಲಾಮ್ ರಾ. ಸುವ್ಯಕ್ತ ವಿವರಣೆಯ ಗ್ರಂಥದ ಸೂಕ್ತಗಳಿವು.');
INSERT INTO "kannada" VALUES(12,2,'12:2','ನೀವು ಯೋಚಿಸಿ ಗ್ರಹಿಸುವಂತಾಗಲು ನಾವು ಇದನ್ನು ಅರಬಿ ಭಾಷೆಯಲ್ಲಿರುವ ಖುರ್‍ಆನನ್ನಾಗಿ ಅವತೀರ್ಣಗೊಳಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(12,3,'12:3','ನಿಮಗೆ ನಮ್ಮ ಈ ಖುರ್‍ಆನ್ ಅವತರಣಿಕೆ ಯಿಂದ ಅತ್ಯಂತ ಸುಂದರವಾದ ಒಂದು ಚರಿತ್ರೆ ಯನ್ನು ಪ್ರಸ್ತುತಪಡಿಸುತ್ತಿದ್ದೇವೆ. ಇದಕ್ಕಿಂತ ಮುಂಚೆ ಈ ಚರಿತ್ರೆಯ ಬಗ್ಗೆ ನೀವು ಅಪ್ರಜ್ಞರಲ್ಲಾಗಿದ್ದಿರಿ .');
INSERT INTO "kannada" VALUES(12,4,'12:4','ಯೂಸುಫರು ತಮ್ಮ ಪಿತರೊಡನೆ ಹೇಳಿದ ಸಂದರ್ಭ. `ಅಪ್ಪಾ, ಹನ್ನೊಂದು ನಕ್ಷತ್ರಗಳೂ ಸೂರ್ಯನೂ ಚಂದ್ರನೂ ನನಗೆ ಸಾಷ್ಟಾಂಗವೆರಗುತ್ತಿರುವುದಾಗಿ ನಾನು ಸ್ವಪ್ನದಲ್ಲಿ ಕಂಡಿರುತ್ತೇನೆ’ .');
INSERT INTO "kannada" VALUES(12,5,'12:5','ಆಗ ಅವರ ತಂದೆ ಇಂತೆಂದರು, ‘ನನ್ನ ಮುದ್ದು ಮಗನೇ, ನಿನ್ನ ಈ ಸ್ವಪ್ನವನ್ನು ನಿನ್ನ ಸಹೋದರರಿಗೆ ವಿವರಿಸಿ ಕೊಡಬೇಡ. ಅನ್ಯಥಾ ಅವರು ನಿನ್ನ ವಿರುದ್ಧ ಒಳಸಂಚು ನಡೆಸುವರು. ವಾಸ್ತವದಲ್ಲಿ ಶೈತಾನನು ಮಾನವನ ಪ್ರತ್ಯಕ್ಷ ಶತ್ರುವಾಗಿರುವನು .');
INSERT INTO "kannada" VALUES(12,6,'12:6','ಅದೇ ಪ್ರಕಾರ (ಸ್ವಪ್ನದಲ್ಲಿ ಕಂಡಂತೆ) ನಿನ್ನ ಪ್ರಭುವು ನಿನ್ನನ್ನು ಆಯ್ದುಕೊಳ್ಳುವನು. ನಿನಗೆ ಸ್ವಪ್ನಗಳ ವ್ಯಾಖ್ಯಾನವನ್ನು ಕಲಿಸಿ ಕೊಡುವನು ಮತ್ತು ಇದಕ್ಕೆ ಮುಂಚೆ ನಿನ್ನ ಪೂರ್ವಜರಾಗಿದ್ದ ಇಬ್‍ರಾಹೀ ಮ್ ಮತ್ತು ಇಸ್‍ಹಾಖರ ಮೇಲೆ ಅವನು ತನ್ನ (ಪ್ರವಾದಿತ್ವದ) ಅನುಗ್ರಹವನ್ನು ಪೂರ್ಣಗೊಳಿಸಿ ದಂತೆ ನಿನ್ನ ಮೇಲೂ ಯಅïಖೂಬರ ವಂಶಜರ ಮೇಲೂ ಆ ಅನುಗ್ರಹವನ್ನು ಪೂರ್ಣಗೊಳಿಸುವನು. ನಿಶ್ಚಯವಾಗಿಯೂ ನಿನ್ನ ಪ್ರಭು ಸರ್ವಜ್ಞನೂ ಯುಕ್ತಿವಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(12,7,'12:7','ಯೂಸುಫ್ ಮತ್ತು ಅವರ ಸಹೋದರರ ಚರಿತ್ರೆ ಯಲ್ಲಿ ಆ ಚರಿತ್ರೆಯ ಬಗ್ಗೆ ಪ್ರಶ್ನಿಸುವವರಿಗೆ ಖಂಡಿತ ನಿದರ್ಶನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(12,8,'12:8','ಅವರ ಸಹೋದರರು ಪರಸ್ಪರ ಹೇಳಿದರು; ಯೂಸುಫ್ ಹಾಗೂ ಅವನ ಸಹೋದರನೇ ನಮ್ಮ ತಂದೆಗೆ ನಮ್ಮೆಲ್ಲರಿಗಿಂತ ಹೆಚ್ಚು ಪ್ರೀತಿ. ನಾವಂತೂ ಬಲ ಪ್ರಾಪ್ತಿಯುಳ್ಳ ಒಂದು ಕೂಟವಾಗಿರುತ್ತೇವೆ. ವಾಸ್ತವದಲ್ಲಿ ನಮ್ಮ ಪಿತೃವರ್ಯರು ಸ್ಪಷ್ಟ ಪ್ರಮಾದದಲ್ಲಿದ್ದಾರೆ .');
INSERT INTO "kannada" VALUES(12,9,'12:9','ನೀವು ಯೂಸುಫನನ್ನು ಕೊಂದು ಹಾಕಿರಿ, ಇಲ್ಲವೆ ಎಲ್ಲಾದರೂ ದೂರ ಸ್ಥಳಕ್ಕೆ ಎಸೆದುಬಿಡಿರಿ. ಹಾಗಾದರೆ ನಿಮ್ಮ ತಂದೆಯ ಒಲವು ಕೇವಲ ನಿಮ್ಮ ಕಡೆಗೇ ಇರುವುದು. ಈ ಕಾರ್ಯವನ್ನು ಮಾಡಿಯಾದ ಬಳಿಕ ನಿಮಗೆ ಪಾಪ ಮುಕ್ತರಾಗಬಹುದು.');
INSERT INTO "kannada" VALUES(12,10,'12:10','ಆಗ ಅವರಲ್ಲೊಬ್ಬನು ಹೇಳಿದನು `ಯೂಸುಫ ನನ್ನು ಕೊಲ್ಲಬೇಡಿರಿ. ತಂದೆಯವರಿಂದ ಅವನನ್ನು ಬೇರ್ಪಡಿಸಬೇಕೆಂದಿದ್ದರೆ ಯಾವುದಾದರೊಂದು ಆಳವಾದ ಬಾವಿಯಲ್ಲಿ ಹಾಕಿಬಿಡಿರಿ. ಯಾರಾದರೂ ಪ್ರಯಾಣಿಕರು ಅವನನ್ನು ಎತ್ತಿಕೊಂಡು ಹೋಗುತ್ತಾರೆ’ .');
INSERT INTO "kannada" VALUES(12,11,'12:11','ಬಳಿಕ ಅವರು ತಮ್ಮ ತಂದೆಯವರ ಬಳಿಸಾಗಿ ಹೀಗೆಂದರು; ನಮ್ಮ ಅಪ್ಪಾ, ನಿಮಗೇನಾಗಿದೆ? ನೀವು ಯೂಸುಫನ ಬಗೆಗೆ ನಮ್ಮನ್ನೇಕೆ ನಂಬುವುದಿಲ್ಲ? ವಸ್ತುತಃ ನಾವು ಅವನ ನೈಜ ಹಿತಾಕಾಂಕ್ಷಿಗಳಾಗಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(12,12,'12:12','ನಾಳೆ ಅವನನ್ನು ನಮ್ಮ ಸಂಗಡ (ಮರುಭೂ ಮಿಗೆ) ಕಳುಹಿಸಿರಿ. ಅವನು ಹಣ್ಣುಗಳನ್ನು ತಿಂದು ಆನಂದಪಡಲಿ. ಹಾಗೂ ಆಟವಾಡಲಿ. ಖಂಡಿತ ವಾಗಿಯೂ ನಾವು ಅವನ ರಕ್ಷಕರಾಗಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(12,13,'12:13','ಆಗ ತಂದೆ ಹೇಳಿದರು, ‘ನೀವು ಅವನನ್ನು ಕರೆದೊಯ್ಯುವುದು ನನಗೆ ಬಹಳ ಬೇಸರ ತರುತ್ತಿದೆ. ನೀವು ಅವನಿಂದ ಅಶ್ರದ್ಧೆಯಲ್ಲಿರುವಾಗ ಅವನ ನ್ನು ತೋಳ ಹಿಡಿದುತಿಂದೀತೆಂಬ ಭಯ ನನ್ನ ನ್ನು ಕಾಡುತ್ತಿದೆ’.');
INSERT INTO "kannada" VALUES(12,14,'12:14','ಆಗ ಅವರು, ‘ನಾವು ಒಂದು ಬಲಿಷ್ಟ ತಂಡವೇ ಆಗಿರುವಾಗ, ನಾವಿರುತ್ತ ಅವನನ್ನು ತೋಳ ಹಿಡಿದು ತಿಂದರೆ, ನಾವು ನಿಜಕ್ಕೂ ನಷ್ಟವಂತರಾಗುತ್ತೇವೆ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(12,15,'12:15','ಕೊನೆಗೆ ಅವರು ಯೂಸುಫರನ್ನು ಕರೆದೊಯ್ದರು. ಒಂದು ಆಳವಾದ ಬಾವಿಗೆ ತಳ್ಳಿಬಿಡಬೇಕೆಂದು ಒಮ್ಮತದಿಂದ ನಿರ್ಧರಿಸಿದರು. ಆಗ ನಾವು ಯೂಸುಫರಿಗೆ; “ಇವರ ಈ ವರ್ತನೆಯ ಬಗ್ಗೆ ಇವರಿಗೆ ನೀನು ವಿವರಿಸಿ ಕೊಡುವ ಕಾಲ ಬರಲಿದೆ. ಆದರೆ ಈಗ ಇವರಿಗೆ ಅದೇನೂ ತಿಳಿದಿರು ವುದಿಲ್ಲ” ಎಂದು ಸಂದೇಶ ಕಳುಹಿಸಿದೆವು .');
INSERT INTO "kannada" VALUES(12,16,'12:16','ಸಂಜೆ ಹೊತ್ತಿಗೆ ಅವರು ಅಳುತ್ತ ತಮ್ಮ ತಂದೆಯ ಬಳಿಗೆ ಬಂದರು.');
INSERT INTO "kannada" VALUES(12,17,'12:17','‘ಅಪ್ಪಾ, ನಾವು ಬಿಲ್ಲು ಪಂದ್ಯಾಟದಲ್ಲಿ ನಿರತರಾಗಿದ್ದೆವು. ಯೂಸುಫನನ್ನು ನಮ್ಮ ಸಾಮಾನುಗಳ ಹತ್ತಿರ ಬಿಟ್ಟಿದ್ದೆವು. ಅಷ್ಟರಲ್ಲಿ ತೋಳ ಬಂದು ಅವನನ್ನು ತಿಂದು ಹಾಕಿತು. ನಾವು ಸತ್ಯವಂತರಾಗಿದ್ದರೂ ತಾವು ನಮ್ಮ ಮಾತನ್ನು ನಂಬಲಾರಿರಿ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(12,18,'12:18','ಅವರು ಯೂಸುಫರ ಅಂಗಿಗೆ ಸುಳ್ಳು ರಕ್ತ ಹಚ್ಚಿ ತಂದಿದ್ದರು. ಯಅïಖೂಬರು ಹೇಳಿದರು; `ಆದರೆ ಈ ಕೃತ್ಯವನ್ನು ನಿಮ್ಮ ದೇಹೇಚ್ಛೆಯು ಚಂದಗಾಣಿಸಿಬಿಟ್ಟಿತು. ಚಿಂತಿಲ್ಲ, ನಾನು ಚೆನ್ನಾಗಿ ಸಹಿಸಿಕೊಳ್ಳುವೆನು. ನಿಮ್ಮ ಮಾತುಗಳ ನಿಜಸ್ಥಿತಿಯ ಬಗ್ಗೆ ಸಹಾಯ ಬೇಡಲು ಅಲ್ಲಾಹನೇ ಅರ್ಹನು.');
INSERT INTO "kannada" VALUES(12,19,'12:19','ಅತ್ತ ಪ್ರವಾಸಿಗರ ತಂಡವೊಂದು ಬಂದಿತು. ಅದು ನೀರು ಸೇದುವವನನ್ನು (ನೀರು ತರಲು) ಕಳುಹಿಸಿತು. ಅವನು ತೊಟ್ಟಿಯನ್ನು ಬಾವಿಗೆ ಇಳಿಸಿದಾಗ ‘ಶುಭವಾಗಲಿ! ಇದೋ ಇಲ್ಲೊಬ್ಬ ಬಾಲಕ ! ಎಂದು ಕೂಗಿದನು. (ಇದು ತಿಳಿದು ಸಹೋದರರು ಬಂದು) ಯೂಸುಫರ ನಿಜಸ್ಥಿತಿ ಬಚ್ಚಿಟ್ಟು ಅವರನ್ನು ಸರಕನ್ನಾಗಿ ಮಾಡಿಕೊಂ ಡರು. ಅವರು ಮಾಡುತ್ತಿದ್ದುದನ್ನು ಅಲ್ಲಾಹು ಬಲ್ಲವನಾಗಿದ್ದನು.');
INSERT INTO "kannada" VALUES(12,20,'12:20','ಕೊನೆಗೆ ಸಹೋದರರು ಕೆಲವೇ ದಿರ್‍ಹಮ್‍ಗಳ ತುಚ್ಛ ಬೆಲೆಗೆ ಯೂಸುಫರನ್ನು (ಯಾತ್ರಾ ತಂಡದ ವರಿಗೆ) ಮಾರಿಬಿಟ್ಟರು. ಅವರು ಯೂಸುಫರ ಬಗ್ಗೆ ನಿರ್ಲಕ್ಷಿತರಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(12,21,'12:21','ಈಜಿಪ್ತಿನಿಂದ ಅವರನ್ನು ಖರೀದಿಸಿದ ವ್ಯಕ್ತಿ ಯು ತನ್ನ ಪತ್ನಿಯೊಡನೆ, ‘ಇವನ ವಾಸವನ್ನು ಚೆನ್ನಾಗಿರಿಸು. ಇವನು ನಮಗೆ ಫಲಕಾರಿಯಾ ಗಲೂ ಬಹುದು ಅಥವಾ ನಾವು ಇವನನ್ನು ದತ್ತು ಪುತ್ರನನ್ನಾಗಿಯೂ ಮಾಡಿಕೊಳ್ಳಬಹುದು’ ಎಂದನು. ಈ ರೀತಿ ನಾವು ಯೂಸುಫರಿಗೆ ಭೂ ಭಾಗದಲ್ಲಿ ನೆಲೆಯೂರಲು ಅವಕಾಶ ಮಾಡಿ ಕೊಟ್ಟೆವು ಮತ್ತು ಅವರಿಗೆ ಸ್ವಪ್ನ ವ್ಯಾಖ್ಯಾನವನ್ನು ತಿಳಿಸಿಕೊಡುವ ವ್ಯವಸ್ಥೆ ಮಾಡಿದೆವು. ಅಲ್ಲಾಹು ತನ್ನ ಕಾರ್ಯವನ್ನು ಯಶಸ್ವಿಯಾಗಿ ಮಾಡಲು ಸುಶಕ್ತನು. ಆದರೆ ಹೆಚ್ಚಿನವರು ತಿಳಿಯುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(12,22,'12:22','ಯೂಸುಫರು ತಮ್ಮ ತುಂಬು ಯೌವನಕ್ಕೆ ತಲುಪಿದಾಗ ನಾವು ಅವರಿಗೆ ತತ್ವಜ್ಞಾನ ಮತ್ತು ಜ್ಞಾನವನ್ನು ನೀಡಿದೆವು. ಸಜ್ಜನರಿಗೆ ನಾವು ಈ ರೀತಿ ಪ್ರತಿಫಲ ನೀಡುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(12,23,'12:23','ಯೂಸುಫರು ವಾಸಿಸುತ್ತಿದ್ದ ಮನೆಯೊಡತಿ ಯೂಸುಫರ ದೇಹವನ್ನು ಬಯಸಿದಳು. ಒಂದು ದಿನ ಕದಗಳನ್ನು ಮುಚ್ಚಿ, `ಇತ್ತ ಬಾ’ ಎಂದಳು. ಆಗ ಯೂಸುಫರು; ‘ಅಲ್ಲಾಹನಲ್ಲಿ ಶರಣು! ನನ್ನ ಪ್ರಭು ನನ್ನ ವಾಸವನ್ನು ಸುಸ್ಥಿತಿಯಲ್ಲಿಟ್ಟಿದ್ದಾನೆ. ಅಕ್ರಮಿಗಳು ಎಂದೂ ಯಶಸ್ವಿಯಾಗಲಾರರು’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(12,24,'12:24','ಆಕೆ ಅವರಿಂದ ದೇಹ ಸುಖಕ್ಕೆ ನಿಶ್ಚಯ ಮಾಡಿ ಬಿಟ್ಟಿದ್ದಳು. ಆದರೆ ತಮ್ಮ ಪ್ರಭುವಿನ ದೃಷ್ಟಾಂತವನ್ನು ಯೂಸುಫರು ಕಾಣದಿರುತ್ತಿದ್ದರೆ ಅವರೂ ಅವಳನ್ನು ಬಯಸುತ್ತಿದ್ದರು . ಹೀಗಾದುದು ನಾವು ಅವರಿಂದ ಅನೈತಿಕತೆಯ ವಾಂಛೆಯನ್ನು ನಿವಾರಿಸುವ ಸಲುವಾಗಿ. ಅವರು ನಮ್ಮ ನಿರ್ಮಲ ದಾಸರಲ್ಲೊಬ್ಬರಾಗಿದ್ದರು .');
INSERT INTO "kannada" VALUES(12,25,'12:25','ಅವರಿಬ್ಬರೂ ಬಾಗಿಲಿನತ್ತ ಓಡಿದರು. ಅವಳು ಹಿಂದಿನಿಂದ ಯೂಸುಫರ ಅಂಗಿಯನ್ನು ಹರಿದಳು. ಅವರಿಬ್ಬರೂ ಬಾಗಿಲ ಬಳಿ ಅವಳ ಪತಿ ಉಪಸ್ಥಿತನಿದ್ದುದನ್ನು ಕಂಡರು. ಆಕೆ ಹೇಳಿದಳು, `ನಿಮ್ಮ ಮನೆಯಾಕೆಯ ಮೇಲೆ ಕೇಡು ಬಗೆಯಲು ಬಂದ ಇವನಿಗೆ ತಕ್ಕ ಶಿಕ್ಷೆ ಸೆರೆಮನೆಯಲ್ಲಿ ಬಂದಿಸಿಡುವುದು ಅಥವಾ ಕಠಿಣ ಸಜೆ’.');
INSERT INTO "kannada" VALUES(12,26,'12:26','ಯೂಸುಫರು, ‘ಇವಳೇ ನನ್ನನ್ನು ಸೆಳೆಯಲು ಯತ್ನಿಸಿದ್ದು’ ಎಂದರು. ಅವಳ ಬಂಧುವೊಬ್ಬನು ಸಾಕ್ಷ್ಯ ಪ್ರಸ್ತುತಪಡಿಸಿದನು . ‘ಇವನ ಅಂಗಿ ಎದುರಿನಿಂದ ಹರಿದಿದ್ದರೆ ಇವಳದ್ದು ಸತ್ಯ. ಈತ ಸುಳ್ಳುಗಾರ.');
INSERT INTO "kannada" VALUES(12,27,'12:27','ಇವನ ಅಂಗಿ ಹಿಂಭಾಗದಿಂದ ಹರಿದಿದ್ದರೆ ಸ್ತ್ರಿಯು ಸುಳ್ಳುಗಾರ್ತಿ ಮತ್ತು ಇವನು ಸತ್ಯವಂತ’ .');
INSERT INTO "kannada" VALUES(12,28,'12:28','ಆಕೆಯ ಪತಿಯು ಯೂಸುಫರ ಅಂಗಿಯು ಹಿಂಭಾಗದಿಂದ ಹರಿದಿದ್ದುದನ್ನು ಕಂಡಾಗ ಇಂತೆಂದನು; ‘ಇದು ಸ್ತ್ರೀಯರಾದ ನಿಮ್ಮ ಕುತಂತ್ರ. ನಿಜಕ್ಕೂ ನಿಮ್ಮ ಕುತಂತ್ರ ಬಹಳ ಗಂಭೀರವಾಗಿದೆ .');
INSERT INTO "kannada" VALUES(12,29,'12:29','ಯೂಸುಫ್ ! ಈ ವಿಷಯವನ್ನು ಕಡೆಗಣಿಸು. (ಓ ಸ್ತ್ರೀಯೇ) ನೀನು ನಿನ್ನ ತಪ್ಪಿಗಾಗಿ ಕ್ಷಮೆ ಯಾಚಿಸು. ವಾಸ್ತವದಲ್ಲಿ ನೀನೇ ತಪ್ಪು ಗಾರ್ತಿಯಾಗಿದ್ದಿ’.');
INSERT INTO "kannada" VALUES(12,30,'12:30','ನಗರದ ಕೆಲವು ಮಹಿಳೆಯರು ಹೇಳಿದರು. ‘ರಾಜನ ಪತ್ನಿ ತನ್ನ ತರುಣ ಗುಲಾಮನಲ್ಲಿ ಅನುರಕ್ತಳಾಗಿದ್ದಾಳೆ. ಅವಳಿಗೆ ಪ್ರೇಮಾಂಧತೆ ಬಾಧಿಸಿದೆ. ಆಕೆ ತಪ್ಪು ದಾರಿ ಹಿಡಿದಿದ್ದಾಳೆಂದೇ ನಮ್ಮ ಭಾವನೆ’.');
INSERT INTO "kannada" VALUES(12,31,'12:31','ಅವಳು ಅವರ ಈ ನಿಂದನೆಯ ಮಾತುಗಳನ್ನು ಕೇಳಿದಾಗ ಅವರಿಗೆ ಆಹ್ವಾನ ನೀಡಿದಳು. ಅವರಿ ಗಾಗಿ ಒಂದು ಔತಣವೇರ್ಪಡಿಸಿದಳು. ಔತಣ ದಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬರಿಗೂ ಹಣ್ಣು ಕೊಯ್ಯಲು ಒಂದೊಂದು ಚಾಕುವನ್ನು ಕೊಟ್ಟಳು. ಅವಳು ಯೂಸುಫರಿಗೆ ‘ನೀನು ಅವರ ಮುಂದೆ ಹೊರಟು ಬಾ’ ಎಂದಳು. ಆ ಸ್ತ್ರೀಯರು ಯೂಸುಫರನ್ನು ಕಂಡಾಗ ದಂಗಾದರು. ಮತ್ತು ತಮ್ಮ ಕೈಗಳನ್ನೇ ಕೊಯ್ದುಕೊಂಡರು. ‘ಅಲ್ಲಾಹು ಎಷ್ಟು ಪರಿಶುದ್ಧನು! ಈತನು ಮನುಷ್ಯ ಖಂಡಿತ ಅಲ್ಲ. ಇದಾರೋ ಸನ್ಮಾನ್ಯ ದೇವಚರನೇ ಇರಬೇಕು’ ಎಂದರವರು.');
INSERT INTO "kannada" VALUES(12,32,'12:32','ಆಕೆ ಹೇಳಿದಳು, ‘ಯಾರ ವಿಷಯವಾಗಿ ನೀವು ನನ್ನ ಕುರಿತು ಆಕ್ಷೇಪದ ಮಾತುಗಳನ್ನೆತ್ತಿದ್ದೀರೋ ಆ ವ್ಯಕ್ತಿ ಇವನೇ. ನಿಜಕ್ಕೂ ನಾನು ಇವನನ್ನು ವಶೀಕರಿಸಲು ಪ್ರಯತ್ನಿಸಿದ್ದೆ. ಆದರೆ ಇವನು ತಪ್ಪಿಸಿಕೊಂಡನು. ಇನ್ನು ಇವನು ನನ್ನ ಅಪ್ಪಣೆ ಯಂತೆ ನಡೆಯದಿದ್ದರೆ, ಸೆರೆಮನೆ ಸೇರುವನು ಮತ್ತು ನಿಂದ್ಯರಲ್ಲಾಗುವನು’ .');
INSERT INTO "kannada" VALUES(12,33,'12:33','ಯೂಸುಫ್ ಹೇಳಿದರು, ‘ಓ ನನ್ನ ಪ್ರಭೂ, ಇವರು ನನ್ನನ್ನು ಕರೆಯುವ ಕಾರ್ಯಕ್ಕಿಂತ ನನಗೆ ಕಾರಾಗೃಹವೇ ವಾಸಿ. ನೀನು ಇವರ ಕುತಂತ್ರಗಳಿಂದ ನನ್ನನ್ನು ಪಾರು ಮಾಡದೇ ಹೋದರೆ ನಾನು ಅವರ ಬಲೆಯಲ್ಲಿ ಸಿಕ್ಕಿ ಬೀಳುವೆನು ಮತ್ತು ಅಜ್ಞಾನಿಗಳಲ್ಲಿ ಸೇರಿಹೋಗುವೆನು’ .');
INSERT INTO "kannada" VALUES(12,34,'12:34','ಆಗ ಅವರ ಪ್ರಭು ಅವರ ಪ್ರಾರ್ಥನೆಯನ್ನು ಸ್ವೀಕರಿಸಿದನು ಮತ್ತು ಆ ಸ್ತ್ರೀಯರ ಕುತಂತ್ರಗಳನ್ನು ಅವರಿಂದ ನೀಗಿಸಿದನು. ಅವನು ಪರಮಶ್ರೋತೃನೂ ಪರಮ ತಜ್ಞನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(12,35,'12:35','ತರುವಾಯ (ಯೂಸುಫರ ನಿರ್ದೋಷಿತ್ವದ ಬಗ್ಗೆ) ಅವರಿಗೆ ಪುರಾವೆ ದೊರೆತ ನಂತರವೂ ಒಂದು ಕಾಲಾವಧಿಯವರೆಗೆ ಇವರನ್ನು ಸೆರೆಯಲ್ಲಿಡಬೇಕೆಂದು ಅವರಿಗೆ ತೋರಿತು .');
INSERT INTO "kannada" VALUES(12,36,'12:36','ಕಾರಾಗೃಹದೊಳಗೆ ಇನ್ನಿಬ್ಬರು ಗುಲಾಮರೂ ಬಂದು ಸೇರಿದರು. ಒಂದು ದಿನ ಅವರಲ್ಲೊಬ್ಬನು ‘ನಾನು ದ್ರಾಕ್ಷ ಮದ್ಯ ಹಿಂಡುತ್ತಿರುವುದಾಗಿ ಕನಸು ಕಂಡೆನು’ ಎಂದನು. ಇನ್ನೊಬ್ಬನು, ‘ನನ್ನ ತಲೆಯ ಮೇಲೆ ರೊಟ್ಟಿಯನ್ನು ಹೊತ್ತುಕೊಂಡಿದ್ದೆ. ಪಕ್ಷಿಗಳು ಅದನ್ನು ಕುಕ್ಕಿ ತಿನ್ನುತ್ತಿರುವುದಾಗಿ ಕನಸು ಕಂಡೆನು’ ಎಂದನು. ಅವರಿಬ್ಬರೂ ‘ನಮಗೆ ಇದರ ವ್ಯಾಖ್ಯಾನ ತಿಳಿಸಿಕೊಡಿರಿ. ನೀವೊಬ್ಬ ಸಜ್ಜನರಾಗಿರುವುದನ್ನು ನಾವು ಕಾಣುತ್ತೇವೆ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(12,37,'12:37','ಯೂಸುಫ್ ಹೇಳಿದರು; ‘ನಿಮಗಿಬ್ಬರಿಗೂ (ಕನಸಿನಲ್ಲಿ) ನೀಡಲಾದ ಆಹಾರದ ವ್ಯಾಖ್ಯಾನ ವನ್ನು ಅದು ನಿಮಗೆ ಬಂದು ತಲುಪುವ ಮುಂಚೆಯೇ ನಿಮಗೆ ನಾನು ಹೇಳಬಲ್ಲೆ. ಇದು ನನ್ನ ಪ್ರಭು ನನಗೆ ಕೊಟ್ಟಿರುವ ಜ್ಞಾನಗಳಲ್ಲೊಂದು . ನಾನು ಅಲ್ಲಾಹನ ಮೇಲೆ ವಿಶ್ವಾಸವಿಡದವರ ಮತ್ತು ಪರಲೋಕವನ್ನು ನಿರಾಕರಿಸುವವರ ಮಾರ್ಗವನ್ನು ತ್ಯಜಿಸಿರುವೆನು.');
INSERT INTO "kannada" VALUES(12,38,'12:38','ನನ್ನ ಪೂರ್ವಜರಾಗಿರುವ ಇಬ್‍ರಾಹೀಮ್, ಇಸ್‍ಹಾಖ್ ಮತ್ತು ಯಅïಖೂಬರ ಮಾರ್ಗ ವನ್ನು ನಾನು ಅವಲಂಬಿಸಿದ್ದೇನೆ . ಅಲ್ಲಾಹ ನೊಂದಿಗೆ ಇತರರನ್ನು ಸಹಭಾಗಿಗಳನ್ನಾಗಿಸುವುದು ನಮಗೆ ಸಲ್ಲದು. ಇದು (ಸನ್ಮಾರ್ಗ) ನಮ್ಮ ಮೇಲೆ ಮತ್ತು ಮಾನವರ ಮೇಲೆ ಅಲ್ಲಾಹನ ಅನುಗ್ರಹವಾಗಿದೆ. ಆದರೆ ಜನರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಕೃತಜ್ಞತೆ ಸಲ್ಲಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(12,39,'12:39','ಓ ಸೆರೆಮನೆಯ ನನ್ನ ಸಂಗಾತಿಗಳೇ, ಬೇರೆ ಬೇರೆ ವಿಧದ ದೇವರುಗಳು ಲೇಸೋ ಅಥವಾ ಸರ್ವಾಧಿಕಾರಿಯಾದ ಏಕ ಅಲ್ಲಾಹನೋ?');
INSERT INTO "kannada" VALUES(12,40,'12:40','ಅಲ್ಲಾಹನ ಹೊರತು ನೀವು ಆರಾಧಿಸುತ್ತಿರುವ ಬೇರೆ ಬೇರೆ ವಸ್ತುಗಳೆಲ್ಲ ನೀವೂ ನಿಮ್ಮ ಪೂರ್ವಿ ಕರೂ ಹಾಕಿಕೊಂಡಿರುವ ಬರೇ ಹೆಸರುಗಳು ಮಾತ್ರ. ಅಲ್ಲಾಹನು ಅವುಗಳ ಬಗ್ಗೆ ಯಾವ ಆಧಾರವನ್ನೂ ಇಳಿಸಿಲ್ಲ. ಆಜ್ಞಾಧಿಕಾರವು ಅಲ್ಲಾಹನಿಗೆ ಮಾತ್ರ. ಅವನ ಹೊರತು ಬೇರೆ ಯಾರಿಗೂ ಆರಾ ಧನೆ ಸಲ್ಲಿಸಬಾರದೆಂದು ಅವನ ಕಟ್ಟಪ್ಪಣೆ ಇದೆ. ಇದೇ ಅಪ್ಪಟ ಋಜು ಮಾರ್ಗ. ಆದರೆ ಹೆಚ್ಚಿನ ಜನರು ಅರಿಯುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(12,41,'12:41','ಓ ಸೆರೆಮನೆಯ ನನ್ನ ಸಂಗಾತಿಗಳೇ, ನಿಮ್ಮ ಸ್ವಪ್ನದ ಫಲವೇನೆಂದರೆ; ನಿಮ್ಮಲ್ಲೋರ್ವನು ತನ್ನ ರಾಜನಿಗೆ ಮದ್ಯ ಸುರಿದು ಕೊಡುವ ಹುದ್ದೆಗೆ ಮರಳುವನು. ಇನ್ನೊಬ್ಬನು ಗಲ್ಲಿಗೇರಿಸಲ್ಪಡು ವನು ಮತ್ತು ಪಕ್ಷಿಗಳು ಅವನ ತಲೆಯನ್ನು ಕುಕ್ಕಿ ಕುಕ್ಕಿ ತಿನ್ನಲಿವೆ. ನೀವು ಕೇಳಿದ ಸ್ವಪ್ನ ವ್ಯಾಖ್ಯಾ ನದ ಫಲ ಅಲ್ಲಾಹನ ಬಳಿ ದಾಖಲಾಯಿತು.');
INSERT INTO "kannada" VALUES(12,42,'12:42','ಅವರಿಬ್ಬರ ಪೈಕಿ ಬಿಡುಗಡೆಯಾಗುವನೆಂದು ತಾನು ಭಾವಿಸಿದ ವ್ಯಕ್ತಿಯೊಡನೆ ಯೂಸುಫ್‍ರು, ‘ನಿನ್ನ ಪ್ರಭುವಿನ ಬಳಿ ನನ್ನ ಬಗ್ಗೆ ತಿಳಿಸು’ ಎಂದು ಹೇಳಿದರು . ಆದರೆ ಅವನು ತನ್ನ ಯಜಮಾನ ನೊಡನೆ ಯೂಸುಫರ ಪ್ರಸ್ತಾಪವೆತ್ತಲು ಶೈತಾನ ನು ಅವನಿಗೆ ಮರೆವೆಗೊಳಿಸಿದ್ದನು. ಹೀಗೆ ಅವರು ಅನೇಕ ವರ್ಷಗಳವರೆಗೆ ಸೆರೆಮನೆಯಲ್ಲೇ ಕಳೆದರು.');
INSERT INTO "kannada" VALUES(12,43,'12:43','(ಒಂದು ದಿನ) ಅರಸನು, ‘ನಾನು ಸ್ವಪ್ನದಲ್ಲಿ ಏಳು ಕೊಬ್ಬಿದ ಹಸುಗಳನ್ನು ಏಳು ಬಡಕಲು ಹಸು ಗಳು ತಿನ್ನುತ್ತಿರುವುದನ್ನೂ ಧಾನ್ಯದ ಏಳುತೆನೆ ಗಳು ಹಸಿರಾಗಿದ್ದು ಇತರ ಏಳು ತೆನೆಗಳು ಒಣಗಿರುವುದನ್ನು ಕಂಡಿರುತ್ತೇನೆ. ಓ ನನ್ನ ಆಸ್ಥಾನದ ವರೇ, ನೀವು ಸ್ವಪ್ನಗಳ ಫಲ ಬಲ್ಲವರಾಗಿದ್ದರೆ, ನನಗೆ ಈ ಸ್ವಪ್ನದ ಬಗ್ಗೆ ತೀರ್ಪು ನೀಡಿರಿ’ ಎಂದನು .');
INSERT INTO "kannada" VALUES(12,44,'12:44','ಆಗ ಅವರು ‘ಇದು ಕನಸುಗಳ ಕಂತೆ. ಇಂತಹ ಕಂತೆ ಕನಸುಗಳ ಫಲ ನಮಗೆ ತಿಳಿಯದು’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(12,45,'12:45','ಆ ಇಬ್ಬರು ಸೆರೆಯಾಳುಗಳ ಪೈಕಿ ಬಿಡುಗಡೆ ಹೊಂದಿ ದವನು ದೀರ್ಘಕಾಲದ ತರುವಾಯ (ಇದೀಗ ಯೂಸುಫರನ್ನು) ನೆನಪಿಸಿಕೊಳ್ಳುತ್ತ `ನಿಮಗೆ ಇದರ ಫಲ ನಾನು ತಿಳಿಸಿಕೊಡುತ್ತೇನೆ. ನನ್ನನ್ನು (ಯೂಸುಫರ ಬಳಿಗೆ) ಕಳುಹಿಸಿರಿ’ ಎಂದನು.');
INSERT INTO "kannada" VALUES(12,46,'12:46','(ಅವನು ಯೂಸುಫರ ಬಳಿ ಹೋಗಿ ಇಂತೆಂ ದನು;) ‘ಓ ಸತ್ಯಸಂಧರಾದ ಯೂಸುಫರೇ, ಏಳು ಕೊಬ್ಬಿದ ಹಸುಗಳನ್ನು ಏಳು ಕೃಶವಾದ ಹಸುಗಳು ತಿನ್ನುತ್ತಿವೆ ಮತ್ತು ಏಳು ತೆನೆಗಳು ಹಸುರಾಗಿಯೂ ಇನ್ನು ಏಳು ತೆನೆಗಳು ಒಣಗಿಯೂ ಇವೆ. ಈ ಸ್ವಪ್ನದ ಫಲವನ್ನು ನನಗೆ ಹೇಳಿರಿ. ಹಾಗಾದರೆ ನಾನು ಆ ವಿವರದೊಂದಿಗೆ ಅವರ ಬಳಿ ಮರಳುತ್ತೇನೆ. ಅವರಿಗೆ ನಿಜಸ್ಥಿತಿ ತಿಳಿಯಬಹುದು’.');
INSERT INTO "kannada" VALUES(12,47,'12:47','ಯೂಸುಫ್ ಹೇಳಿದರು ‘ಏಳು ವರ್ಷಗಳ ಕಾಲ ನೀವು ನಿರಂತರವಾಗಿ ಕೃಷಿ ಮಾಡಿರಿ. ಆಮೇಲೆ ನೀವು ಕೊಯ್ಯುವ ಬೆಳೆಯಿಂದ ನಿಮ್ಮ ಆಹಾರಕ್ಕೆ ಬೇಕಾದಷ್ಟು ಅಂಶವನ್ನು ತೆಗೆದು ಉಳಿದುದನ್ನು ಅದರ ತೆನೆಗಳಲ್ಲೇ ಇರಗೊಡಿರಿ.');
INSERT INTO "kannada" VALUES(12,48,'12:48','ಅನಂತರ ಏಳು ವರ್ಷಗಳ ಕಾಲ ಬರಗಾಲ ಬರ ಲಿವೆ. ಆ ವರ್ಷಗಳು ಅಂದಿಗಾಗಿ ನೀವು ಮೊದಲೇ ಸಂಗ್ರಹಿಸಿಟ್ಟುದನ್ನು ತಿಂದು ಮುಗಿಸಲಿವೆ. ನೀವು ಜೋಪಾನವಾಗಿರಿಸಿದ ಅಲ್ಪಾಂಶವನ್ನು ಹೊರತುಪಡಿಸಿ.');
INSERT INTO "kannada" VALUES(12,49,'12:49','ಅನಂತರ ಬರುವ ಇನ್ನೊಂದು ವರ್ಷದಲ್ಲಿ ಜನ ರಿಗೆ ಸಮೃದ್ಧಿ ಸಿಗುವುದು. ಮತ್ತು ಅವರು ದ್ರಾಕ್ಷಾರಸ ಹಿಂಡುವರು .');
INSERT INTO "kannada" VALUES(12,50,'12:50','ಅರಸನು, ‘ಅವರನ್ನು ನನ್ನ ಬಳಿಗೆ ಕರೆ ತನ್ನಿರಿ’ ಎಂದನು. ಆದರೆ ರಾಜದೂತನು ಯೂಸುಫರ ಬಳಿಗೆ ಬಂದಾಗ ಯೂಸುಫರು ಹೇಳಿದರು ‘ನಿನ್ನ ರಾಜನ ಬಳಿಗೆ ನೀನು ಹಿಂದಿರುಗಿ ಹೋಗು ಮತ್ತು ಅಂದು ತಮ್ಮ ಕೈಗಳನ್ನು ಕೊಯ್ದುಕೊಂಡ ಮಹಿ ಳೆಯರ ಅಭಿಪ್ರಾಯವೇನೆಂದು ಅವನೊಡನೆ ಕೇಳು. ನನ್ನ ಪ್ರಭು ಅವರ ಕುತಂತ್ರಗಳನ್ನು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು’.');
INSERT INTO "kannada" VALUES(12,51,'12:51','ಅರಸನು (ಆ ಸ್ತ್ರೀಯರನ್ನು ತನ್ನ ಬಳಿಗೆ ಕರೆಸಿ) ‘ನೀವು ಯೂಸುಫರನ್ನು ವಶೀಕರಿಸಲು ಕುತಂತ್ರವನ್ನು ಹೆಣೆದಾಗ ನಿಮಗಾದ ಅನುಭವವೇನು?’ ಎಂದು ಕೇಳಿದನು. ಅವರು ‘ಅಲ್ಲಾಹು ಪರಿಶುದ್ಧನು. ಅವರಲ್ಲಿ ಯಾವ ದೋಷವನ್ನೂ ನಾವು ಅರಿತಿಲ್ಲ’ ಎಂದರು. ಅಝೀಝನ ಪತ್ನಿ ಯು, ‘ಈಗ ಸತ್ಯವು ಬಯಲಾಗಿದೆ. ಅವರನ್ನು ಪುಸಲಾಯಿಸಲು ನಾನೇ ಪ್ರಯತ್ನಿಸಿದ್ದೆ. ಖಂಡಿತವಾಗಿಯೂ ಅವರು ಸತ್ಯಸಂಧರು’ ಎಂದು ಹೇಳಿದಳು.');
INSERT INTO "kannada" VALUES(12,52,'12:52','(ಯೂಸುಫ್ ಹೇಳಿದರು,) ‘ಇದರ ಉದ್ದೇಶವು ನಾನು ಯಜಮಾನರಿಗೆ ಅವರ ಅಭಾವದಲ್ಲಿ ದ್ರೋಹವೆಸಗಲಿಲ್ಲವೆಂದೂ ಅಲ್ಲಾಹು ದ್ರೋಹಿಗಳ ಕುತಂತ್ರವನ್ನು ಸಫಲಗೊಳಿಸಲು ಬಿಡುವುದಿಲ್ಲವೆಂದೂ ಯಜಮಾನರು ತಿಳಿದುಕೊಳ್ಳಬೇಕು ಎಂಬುದಾಗಿದೆ’.');
INSERT INTO "kannada" VALUES(12,53,'12:53','ನನ್ನ ಮನಸ್ಸು ನಿರ್ದೋಷಿಯೆಂದು ನಾನೇನೂ ಹೇಳಿಕೊಳ್ಳಲಾರೆ. ನನ್ನ ಪ್ರಭುವಿನ ಕೃಪೆಯಿಲ್ಲದೆ ಹೋದರೆ ಮನಸ್ಸು ಕೇಡಿನ ಕಡೆಗೇ ಪ್ರೇರೇಪಿಸುತ್ತದೆ . ನಿಶ್ಚಯವಾಗಿಯೂ ನನ್ನ ಪ್ರಭು ಕ್ಷಮಾಶೀಲನೂ ಕೃಪಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(12,54,'12:54','ಅರಸನು ಹೇಳಿದನು, ಅವನನ್ನು ನನ್ನ ಬಳಿಗೆ ಕರೆ ತನ್ನಿರಿ, ನಾನು ಅವನನ್ನು ಉನ್ನತ ಅಧಿಕಾರಿಯಾಗಿ ನೇಮಿಸಲಿದ್ದೇನೆ. ಯೂಸುಫರು ಅವ ನೊಡನೆ ಮಾತುಕತೆ ನಡೆಸಿದಾಗ ಅವನು ಹೀಗೆಂದನು, ‘ನೀವು ಇಂದಿನಿಂದ ನಮ್ಮ ಬಳಿ ಉನ್ನತ ಸ್ಥಾನೀಯರೂ ವಿಶ್ವಾಸಯೋಗ್ಯರೂ ಆಗಿರುವಿರಿ’ .');
INSERT INTO "kannada" VALUES(12,55,'12:55','ಯೂಸುಫ್ ಹೇಳಿದರು, ‘ಹಾಗಾದರೆ ದೇಶದ ಹಣಕಾಸಿನ ಖಾತೆಯನ್ನು ನನ್ನ ವಶಕ್ಕೆ ಬಿಡಿರಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ನಾನು ರಕ್ಷಣೆ ಮಾಡುವವನೂ ತಿಳುವಳಿಕೆಯುಳ್ಳವನೂ ಆಗಿರುತ್ತೇನೆ’ .');
INSERT INTO "kannada" VALUES(12,56,'12:56','ಹೀಗೆ ನಾವು ಯೂಸುಫರಿಗೆ ಆ ಭೂಭಾಗದಲ್ಲಿ ತಾನಿಚ್ಛಿಸಿದಲ್ಲಿ ವಾಸ್ತವ್ಯ ಹೂಡಲು ಅಧಿಕಾರ ವನ್ನು ಸುಗಮಗೊಳಿಸಿದೆವು . ನಾವು ನಮಗಿ ಷ್ಟವಿದ್ದವರಿಗೆ ನಮ್ಮ ಕೃಪೆಯನ್ನು ಕರುಣಿಸುತ್ತೇವೆ. ಸದ್ಭಕ್ತರ ಪ್ರತಿಫಲವನ್ನು ನಾವು ನಷ್ಟಗೊಳಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(12,57,'12:57','ಸತ್ಯವಿಶ್ವಾಸವನ್ನಿರಿಸಿ, ದೇವಭಯದೊಂದಿಗೆ ಸೂಕ್ಷ್ಮತೆ ಪಾಲಿಸಿದವರಿಗೆ ಪರಲೋಕದ ಪ್ರತಿಫಲವು ಇನ್ನಷ್ಟು ಉತ್ತಮವಾಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(12,58,'12:58','ಯೂಸುಫರ ಸಹೋದರರು (ಆಹಾರ ಧಾನ್ಯ ವನ್ನು ತರಲು) ಈಜಿಫ್ತಿಗೆ ಬಂದರು ಮತ್ತು ಯೂಸುಫರ ಮುಂದೆ ಹಾಜರಾದರು. ಯೂಸುಫರಿಗೆ ಸಹೋದರರ ಗುರುತು ಸಿಕ್ಕಿತು. ಆದರೆ ಸಹೋದರರಿಗೆ ಯೂಸುಫರ ಗುರುತು ಸಿಗಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(12,59,'12:59','ಅವರ ಸರಂಜಾಮುಗಳನ್ನು ಕಟ್ಟಿ ಅವರನ್ನು ಯಾತ್ರೆಗೆ ಅಣಿಗೊಳಿಸಿದಾಗ ಯೂಸುಫರು ಹೀಗೆ ಹೇಳಿದರು, ‘ನಿಮ್ಮ ಮಲ ಸಹೋದರನನ್ನು ನನ್ನ ಬಳಿಗೆ ಕರೆತರಬೇಕು. ನಾನು ಧಾನ್ಯಗ ಳನ್ನು ಕೊಂಡುಹೋಗಲು ಬರುವ ಪ್ರತಿಯೊಬ್ಬರಿಗೆ ನೀತಿಪೂರ್ವಕವಾಗಿ ಆಹಾರ ಧಾನ್ಯ ಗಳನ್ನು ಅಳತೆ ಪಾತ್ರೆ ತುಂಬಿ ಹಂಚುತ್ತಿರುವುದು ನೀವು ಗಮನಿಸುತ್ತಿಲ್ಲವೇ? ಅಲ್ಲದೆ ನಾನು ಎಷ್ಟು ಉತ್ತಮ ಅತಿಥೇಯನಾಗಿದ್ದೇನೆಂಬುದನ್ನೂ ಕಾಣುತ್ತೀರಲ್ಲವೆ?’');
INSERT INTO "kannada" VALUES(12,60,'12:60','ಮುಂದಿನ ಬಾರಿ ನೀವು ಅವನನ್ನು ಜತೆಯಲ್ಲಿ ಕರೆತರದಿದ್ದರೆ ನನ್ನಲ್ಲಿ ನಿಮಗೆ ಧಾನ್ಯವಿಲ್ಲ. ಮಾತ್ರವಲ್ಲ ನೀವು ಇತ್ತ ಬರುವುದೇ ಬೇಡ’ .');
INSERT INTO "kannada" VALUES(12,61,'12:61','ಅವರು ಹೇಳಿದರು, ‘ತಂದೆಯವರು ಅವನನ್ನು ಕಳುಹಿಸಲು ಒಪ್ಪುವಂತೆ ಮಾಡಲು ಪ್ರಯತ್ನಿಸುವೆವು. ಹೇಗಾದರೂ ಮಾಡಿ ತಮ್ಮನನ್ನು ಕರಕೊಂಡು ಬರುತ್ತೇವೆ’ .');
INSERT INTO "kannada" VALUES(12,62,'12:62','ಯೂಸುಫರು ಸೇವಕರಲ್ಲಿ ಸಹೋದರರು ಧಾನ್ಯದ ಬೆಲೆಯಾಗಿ ಕೊಟ್ಟ ಬೆಳ್ಳಿ ನಾಣ್ಯವನ್ನು ಅವರ ಅರಿವಿಗೆ ಬಾರದಂತೆ ಧಾನ್ಯದ ಚೀಲಕ್ಕೆ ಹಾಕಲು ಸೂಚಿಸಿದರು. ಸಹೋದರರು ತಮ್ಮ ಕುಟುಂಬದ ಕಡೆ ಮರಳಿದಾಗ ಅದು ಗೊತ್ತಾಗಿ ಮರಳಿ ಬರುವ ಸಲುವಾಗಿ.');
INSERT INTO "kannada" VALUES(12,63,'12:63','ಅವರು ತಮ್ಮ ತಂದೆಯವರ ಬಳಿಗೆ ಮರಳಿದಾಗ ಹೀಗೆಂದರು, ‘ಅಪ್ಪಾ, ನಮಗೆ ಧಾನ್ಯವನ್ನು ಅಳೆದು ಕೊಡಲು ನಿರಾಕರಿಸಲಾಗಿದೆ. ಆದುದ ರಿಂದ ನಮಗೆ ಧಾನ್ಯವನ್ನು ಅಳೆದು ಕೊಡುವಂತಾಗಲು ತಾವು ನಮ್ಮ ಸಂಗಡ ನಮ್ಮ ಸಹೋದ ರನನ್ನು ಕಳುಹಿಸಿಕೊಡಿರಿ. ಅವನ ರಕ್ಷಣೆಗೆ ನಾವು ಹೊಣೆಗಾರರು’');
INSERT INTO "kannada" VALUES(12,64,'12:64','ಆಗ ತಂದೆಯು ಹೇಳಿದರು; `ಇವನ ಅಣ್ಣನ ವಿಷಯದಲ್ಲಿ ಮುಂಚೆ ನಾನು ನಿಮ್ಮನ್ನು ನಂಬಿದಂತೆಯೇ ಹೊರತು ಇವನ ವಿಷಯದಲ್ಲೂ ನನಗೆ ನಿಮ್ಮನ್ನು ನಂಬಲಾದೀತೇ ? ಅಲ್ಲಾಹನೇ ಅತ್ಯುತ್ತಮ ರಕ್ಷಕನು ಮತ್ತು ಅವನು ಎಲ್ಲರಿಗಿಂತಲೂ ಹೆಚ್ಚು ಕರುಣಾಳುವಾಗಿರುತ್ತಾನೆ’.');
INSERT INTO "kannada" VALUES(12,65,'12:65','ಅವರು ತಮ್ಮ ಸರಕನ್ನು ಬಿಚ್ಚಿದಾಗ ಅವರು ಕೊಟ್ಟ ಮೊಬಲಗು ಅವರಿಗೆ ಹಿಂದಿರುಗಿಸಲಾಗಿರುವುದನ್ನು ಕಂಡು, ಅಪ್ಪಾ, ನಮಗೆ ಇನ್ನೇನು ಬೇಕು? ಇದೋ, ನಮ್ಮ ಹಣವನ್ನೂ ನಮಗೆ ಹಿಂದಿರುಗಿ ಸಲಾಗಿದೆ. (ತಾವು ನಮ್ಮ ತಮ್ಮನನ್ನು ಕಳುಹಿಸಿ ಕೊಟ್ಟರೆ) ನಮ್ಮ ಕುಟುಂಬಕ್ಕೆ ಬೇಕಾದ ಆಹಾರ ಧಾನ್ಯ ತರುವೆವು. ನಮ್ಮ ತಮ್ಮನನ್ನು ಜಾಗೃತೆಯಿಂದ ನೋಡಿ ಕೊಳ್ಳುವೆವು. ಮತ್ತು ಒಂದು ಒಂಟೆ ಹೊರೆಯನ್ನು ಹೆಚ್ಚುವರಿಯಾಗಿ ಪಡೆಯುತ್ತೇವೆ. ಅದು (ರಾಜನಿಂದ ಸಿಗುವ) ಬಹಳ ಅನಾಯಾಸದ ತೂಕ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(12,66,'12:66','ಅವರ ತಂದೆ ಹೇಳಿದರು ‘ನಿಮ್ಮನ್ನು ವಿಪತ್ತುಗಳು ಆವರಿಸದ ಹೊರತು ಅವನನ್ನು ನನ್ನ ಬಳಿಗೆ ಮರಳಿ ಕರೆತರುವಿರೆಂದು ನೀವು ನನಗೆ ಅಲ್ಲಾ ಹನ ಹೆಸರಲ್ಲಿ ಖಾತರಿಯನ್ನು ನೀಡುವ ತನಕ ನಾನು ಅವನನ್ನು ನಿಮ್ಮ ಸಂಗಡ ಕಳುಹಿಸಿ ಕೊಡ ಲಾರೆ’. ಅವರು ಆ ಬಗ್ಗೆ ಖಾತರಿ ನೀಡಿದಾಗ ಅವರು ಹೇಳಿದರು, ‘ನಮ್ಮ ಈ ಹೇಳಿಕೆಗೆ ಅಲ್ಲಾಹನೇ ಸಾಕ್ಷಿ’.');
INSERT INTO "kannada" VALUES(12,67,'12:67','ಅವರು ಹೇಳಿದರು, ‘ಓ ನನ್ನ ಮಕ್ಕಳೇ, ನೀವು ಈಜಿಪ್ತಿನ ರಾಜಧಾನಿಯೊಳಗೆ ಒಂದೇ ದ್ವಾರದ ಮೂಲಕ ಪ್ರವೇಶ ಮಾಡದಿರಿ. ಬೇರೆ ಬೇರೆ ದ್ವಾರ ಗಳಿಂದ ಪ್ರವೇಶ ಮಾಡಿರಿ. ಆದರೆ ಅಲ್ಲಾಹನ ವಿಧಿಯಿಂದ ನಿಮ್ಮನ್ನು ನಾನು ತಪ್ಪಿಸಲಾರೆ. ಆಜ್ಞಾ ಧಿಕಾರವು ಅಲ್ಲಾಹನಿಗಲ್ಲದೆ ಬೇರಾರಿಗೂ ಇಲ್ಲ. ಅವನ ಮೇಲೆಯೇ ನಾನು ಭರವಸೆಯನ್ನಿರಿಸಿ ದ್ದೇನೆ ಮತ್ತು ಭರವಸೆಯನ್ನಿರಿಸುವವರು ಅವನ ಮೇಲೆಯೇ ಇರಿಸಲಿ’.');
INSERT INTO "kannada" VALUES(12,68,'12:68','ಅವರು ತಮ್ಮ ತಂದೆಯ ಆದೇಶದಂತೆ ನಗರ ದೊಳಗೆ (ಬೇರೆ ಬೇರೆಯಾಗಿ) ಪ್ರವೇಶಿಸಿದಾಗ ಅದು ಅಲ್ಲಾಹನ ವಿದಿಯಿಂದ ಅವರನ್ನು ತಡೆಯಲಿಲ್ಲ. ಯಅïಖೂಬರು ತಮ್ಮ ಮನಸ್ಸಿನಲ್ಲಿದ್ದ ಒಂದು ಅಂಜಿಕೆಯನ್ನು ದೂರ ಮಾಡ ಬಯ ಸಿದ್ದರಷ್ಟೆ. ನಿಸ್ಸಂದೇಹವಾಗಿಯೂ ನಾವು ನೀಡಿದ ಶಿಕ್ಷಣದಿಂದ ಅವರು ಸುಶಿಕ್ಷಿತರಾಗಿದ್ದರು. ಆದರೆ ಹೆಚ್ಚಿನವರು ಪರಮಾರ್ಥವನ್ನು ತಿಳಿಯುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(12,69,'12:69','ಇವರು ಯೂಸುಫರ ಸನ್ನಿಧಿಗೆ ತಲುಪಿದಾಗ ಯೂಸುಫರು ತಮ್ಮ ತಮ್ಮನನ್ನು ಒತ್ತಟ್ಟಿಗೆ ಕರೆದು, ‘ನಿನ್ನ ಆ ಸಹೋದರನೇ ನಾನು. ಆದ್ದರಿಂದ ಇವರು ಈ ತನಕ ಮಾಡಿದ ಕೃತ್ಯಗಳ ಬಗ್ಗೆ ನೀನು ದುಃಖಿಸಬೇಡ’ ಎಂದರು .');
INSERT INTO "kannada" VALUES(12,70,'12:70','ಯೂಸುಫ್ ತಮ್ಮ ಸಹೋದರರ ಸರಕುಗಳನ್ನು ಕಟ್ಟಿ ಅವರನ್ನು ಯಾನಕ್ಕೆ ಸಿದ್ಧಗೊಳಿಸಿದಾಗ ತಮ್ಮ ತಮ್ಮನ ಸರಕಿನಲ್ಲಿ ಅಳತೆ ಪಾತ್ರೆಯನ್ನು ಇಟ್ಟುಬಿಟ್ಟರು. ತರುವಾಯ ಕೂಗುವವನೊಬ್ಬನು, ‘ಓ ಯಾತ್ರಿಕ ತಂಡದವರೇ, ನೀವು ಕಳ್ಳರಿದ್ದೀರಿ’ ಎಂದು ಕೂಗಿದನು.');
INSERT INTO "kannada" VALUES(12,71,'12:71','ಅವರು (ಅವನತ್ತ) ತಿರುಗಿ, ‘ನಿಮ್ಮದೇನು ಕಳೆದು ಹೋಗಿದೆ?’ ಎಂದು ಕೇಳಿದರು.');
INSERT INTO "kannada" VALUES(12,72,'12:72','ಆಗ ಅವರು ಹೇಳಿದರು; ‘ರಾಜನ ಅಳತೆ ಪಾತ್ರೆಯು ಕಳೆದು ಹೋಗಿದೆ. ಅದನ್ನು ತಂದು ಕೊಟ್ಟವನಿಗೆ ಒಂದು ಒಂಟೆ ಹೊರುವಷ್ಟು ಧಾನ್ಯವಿದೆ. ಇದಕ್ಕೆ ನಾನು ಜವಾಬ್ದಾರನಾಗಿದ್ದೇನೆ’.');
INSERT INTO "kannada" VALUES(12,73,'12:73','ಆ ಸಹೋದರರು, ‘ಅಲ್ಲಾಹನಾಣೆ! ನಾವು ಈ ದೇಶದಲ್ಲಿ ಕೇಡು ಮಾಡಲು ಬಂದಿಲ್ಲವೆಂದೂ ನಾವು ಕಳ್ಳರಲ್ಲವೆಂದೂ ನೀವು ಚೆನ್ನಾಗಿ ಬಲ್ಲಿರಿ’ ಎಂದರು .');
INSERT INTO "kannada" VALUES(12,74,'12:74','‘ನೀವು ಸುಳ್ಳುಗಾರರಾದರೆ ಆ ಸುಳ್ಳಿಗೇನು ಶಿಕ್ಷೆ?’ ಎಂದು ಅವರು ಕೇಳಿದರು.');
INSERT INTO "kannada" VALUES(12,75,'12:75','ಅದಕ್ಕೆ ಅವರು, ‘ಅದಕ್ಕಿರುವ ಶಿಕ್ಷೆಯಿಷ್ಟೇ, ಯಾರ ಸರಕಿನಲ್ಲಿ ಅದು ಸಿಗುವುದೋ ಆತ ವಶವಾಗು ವುದೇ ತಕ್ಕ ಶಿಕ್ಷೆ. ನಾವು ಅಕ್ರಮಿಗಳಿಗೆ ಈ ಕ್ರಮ ದಲ್ಲೇ ಶಿಕ್ಷೆ ಕೊಡುತ್ತೇವೆ’, ಎಂದು ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(12,76,'12:76','ಆಗ ಯೂಸುಫ್ ತಮ್ಮ ಸಹೋದರನ ಸರಕಿನ ಚೀಲಕ್ಕಿಂತ ಮುಂಚೆ ಇತರ ಸಹೋದರರ ಚೀಲಗಳನ್ನು ತಪಾಸಣೆ ನಡೆಸಿದರು. ಆ ಬಳಿಕ ತನ್ನ ತಮ್ಮನ ಚೀಲದಿಂದ ಅದನ್ನು (ಅಳತೆ ಪಾತ್ರೆ ಯನ್ನು) ಹೊರತೆಗೆದರು. ಹೀಗೆ ನಾವು ಯೂಸು ಫರಿಗೆ ಉಪಾಯವನ್ನು ಕಲಿಸಿಕೊಟ್ಟೆವು . ಅಲ್ಲಾ ಹನು ಇಚ್ಛಿಸಿದ ಹೊರತು ರಾಜಧರ್ಮಶಾಸನದಲ್ಲಿ ತನ್ನ ತಮ್ಮನನ್ನು ವಶಪಡಿಸಿಕೊಳ್ಳುವ ಅವಕಾಶವಿರುತ್ತಿರಲಿಲ್ಲ. ನಾವು ನಮಗಿಷ್ಟ ಬಂದ ವರ ಸ್ಥಾನವನ್ನು ಉನ್ನತಗೊಳಿಸುತ್ತೇವೆ. ಪ್ರತಿಯೊಬ್ಬ ಜ್ಞಾನಿಗೂ ಮೇಲಾಗಿ ಓರ್ವ ಸರ್ವಜ್ಞನಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(12,77,'12:77','ಆ ಸಹೋದರರು ಹೇಳಿದರು, ಈತನು ಕದ್ದರೆ ಆಶ್ಚರ್ಯವಿಲ್ಲ. ಇದಕ್ಕಿಂತ ಮುಂಚೆ ಇವನ ಅಣ್ಣ ಯೂಸುಫನೂ ಕದ್ದಿದ್ದಾನೆ. ಯೂಸುಫ್, ಅದನ್ನು ತನ್ನ ಮನದಲ್ಲಿ ಗುಟ್ಟಾಗಿಟ್ಟರು. ಹೊರಗೆ ತೋರ್ಪಡಿಸಿಕೊಳ್ಳಲಿಲ್ಲ. ‘ಮಹಾ ನೀಚರು ನೀವು! ನೀವು ಹೊರಿಸುತ್ತಿರುವ ಅಪವಾದದ ನಿಜ ಸ್ಥಿತಿಯನ್ನು ಅಲ್ಲಾಹು ಚೆನ್ನಾಗಿ ಬಲ್ಲವನಾಗಿರುತ್ತಾನೆ’ ಎಂದಿಷ್ಟೇ (ಸ್ವಗತಃ) ಹೇಳಿಕೊಂಡರು.');
INSERT INTO "kannada" VALUES(12,78,'12:78','ಅವರು ಹೇಳಿದರು, ‘ಓ ರಾಜರೇ, ಇವನ ತಂದೆ ತುಂಬಾ ವಯೋವೃದ್ಧರು. ತಾವು ಇವನ ಬದಲಿಗೆ ನಮ್ಮ ಪೈಕಿ ಯಾರನ್ನಾದರೂ ತಡೆದಿರಿಸಿಕೊಳ್ಳಿರಿ. ನಾವು ನಿಮ್ಮನ್ನು ಮಹಾ ಸಜ್ಜನರನ್ನಾಗಿ ಕಾಣು ತ್ತೇವೆ’.');
INSERT INTO "kannada" VALUES(12,79,'12:79','ಆಗ ಯೂಸುಫ್ ಹೇಳಿದರು, ‘ಅಲ್ಲಾಹನಲ್ಲಿ ಶರಣು! ನಾವು ನಮ್ಮ ಸೊತ್ತನ್ನು ಯಾರಲ್ಲಿ ಕಂಡೆವೋ ಅವನನ್ನು ಬಿಟ್ಟು ಇನ್ನೊಬ್ಬನನ್ನು ಇರಿಸಿಕೊಂಡರೆ ನಾವು ಅಕ್ರಮಿಗಳಾಗುವೆವು’ .');
INSERT INTO "kannada" VALUES(12,80,'12:80','ಸಹೋದರರು ಬಿನ್ಯಾಮನ ಬಗ್ಗೆ ನಿರಾಶರಾ ದಾಗ ಅವರು ಒತ್ತಟ್ಟಿಗೆ ಹೋಗಿ ಪರಸ್ಪರ ಸಮಾಲೋಚಿಸಿದರು. ಅವರಲ್ಲಿ ಎಲ್ಲರಿಗೂ ಹಿರಿಯನಾಗಿದ್ದವನು ಹೇಳಿದನು, “(ತಮ್ಮನನ್ನು ಸುರಕ್ಷಿತ ವಾಗಿ ಮರಳಿಸುವ ಬಗ್ಗೆ) ನಿಮ್ಮ ತಂದೆ ನಿಮ್ಮೊಡನೆ ಅಲ್ಲಾಹನ ಹೆಸರಲ್ಲಿ ಕರಾರು ಪಡೆದಿರುವುದು ನಿಮಗೆ ತಿಳಿದಿಲ್ಲವೇ? ಇದಕ್ಕೆ ಮುಂಚೆ ಯೂಸುಫನ ವಿಷಯದಲ್ಲಿ ನೀವು ಎಡವಟ್ಟು ಮಾಡಿಕೊಂಡಿದ್ದೀರಲ್ಲ? ಆದ್ದರಿಂದ ನನ್ನ ತಂದೆಯವರು ನನಗೆ ಅನುಮತಿ ಕೊಡುವವರೆಗೆ ಅಥವಾ ಅಲ್ಲಾಹನೇ ನನ್ನ ಬಗ್ಗೆ ಇತ್ಯರ್ಥ ಮಾಡುವವರೆಗೆ ನಾನು ಈ ನೆಲದಿಂದ ಎಷ್ಟು ಮಾತ್ರಕ್ಕೂ ಕದಲಲಾರೆ. ಅಲ್ಲಾಹು ಅತ್ಯುತ್ತಮ ತೀರ್ಪುಗಾರನು.');
INSERT INTO "kannada" VALUES(12,81,'12:81','ನೀವು ತಂದೆಯ ಬಳಿ ಮರಳಿ ಹೋಗಿ ಹೇಳಿರಿ; ‘ಅಪ್ಪಾ, ನಿಮ್ಮ ಮಗನು ಕದ್ದಿದ್ದಾನೆ. ಅವನು ಕಳವು ಮಾಡುವುದನ್ನು ನಾವು ನೋಡಲಿಲ್ಲ. ನಮಗೆ ಗೊತ್ತಿರುವುದನ್ನು ಮಾತ್ರ ನಾವು ಹೇಳು ತ್ತೇವೆ. ಅದೃಶ್ಯ ಸಂಗತಿಗಳ ಬಗ್ಗೆ ಮೇಲ್ನೋಟ ವಹಿಸಲು ನಮ್ಮಿಂದ ಸಾಧ್ಯವಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(12,82,'12:82','ಬೇಕಾದರೆ ತಾವು ನಾವಿದ್ದ ನಾಡಿನ ಜನರಲ್ಲಿ ಹಾಗೂ ನಾವು ಬಂದ ಯಾತ್ರಾ ತಂಡದೊಂದಿಗೆ ವಿಚಾರಿಸಿ ನೋಡಿರಿ. ನಾವು ಸಂಪೂರ್ಣ ಸತ್ಯವಾದಿಗಳು’ ಎಂದು”.');
INSERT INTO "kannada" VALUES(12,83,'12:83','ತಂದೆಯು ಹೇಳಿದರು; ‘ಅಲ್ಲ, ನಿಮ್ಮ ಚಿತ್ತಗಳು ನಿಮಗೆ ಇನ್ನೊಂದು ದೊಡ್ಡ ವಿಷಯವನ್ನು ಚಂದ ಗಾಣಿಸಿಕೊಟ್ಟಿವೆ. ಆದ್ದರಿಂದ ಎಲ್ಲವನ್ನೂ ಚೆನ್ನಾಗಿ ತಾಳಿಕೊಳ್ಳುವೆನು. ಅಲ್ಲಾಹು ಅವರೆಲ್ಲರನ್ನೂ ತಂದು ನನ್ನೊಂದಿಗೆ ಸೇರಿಸಿಡುವ ಭರವಸೆ ನನಗಿದೆ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹು ಸರ್ವಜ್ಞನೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುವನು’.');
INSERT INTO "kannada" VALUES(12,84,'12:84','ಅನಂತರ ಅವರು ಅವರಿಂದ (ಬೇರೊಂದು ದಿಕ್ಕಿಗೆ) ತಿರುಗಿ ಬಿಟ್ಟರು. ಮತ್ತು ‘ಅಕಟಾ! ಯೂ ಸುಫ್! ನನ್ನ ದುಃಖವೇ’ ಎಂದು ಮರುಗಿದರು. ಅಂತರಂಗದಲ್ಲಿ ಸಂಕಟವನ್ನು ಅದುಮಿಡುತ್ತಿ ದ್ದರು. ಅತ್ತು ಅತ್ತು ಅವರ ಕಣ್ಣುಗಳು ಬಿಳುಪೇರಿ ಬಿಟ್ಟಿದ್ದುವು.');
INSERT INTO "kannada" VALUES(12,85,'12:85','ಮಕ್ಕಳು ಹೇಳಿದರು, `ಅಲ್ಲಾಹನಾಣೆ ! ತಾವಂತು ಯೂಸುಫನನ್ನೇ ನೆನೆಸುತ್ತ ದುಃಖದಿಂದ ಕರಗಿ ಬಿಟ್ಟಿದ್ದೀರಿ. ಅಥವಾ ತಮ್ಮ ಜೀವವನ್ನೇ ನಾಶ ಗೊಳಿಸಿ ಕೊಳ್ಳುವಿರಿ’.');
INSERT INTO "kannada" VALUES(12,86,'12:86','ಅವರು ಹೀಗೆ ಹೇಳಿದರು; ನಾನು ನನ್ನ ಅಳಲು ಮತ್ತು ದುಃಖದ ಮೊರೆಯನ್ನು ಅಲ್ಲಾಹನ ಹೊರತು ಇನ್ನಾರಲ್ಲೂ ಇಡುವುದಿಲ್ಲ. ಅಲ್ಲಾಹನಿಂದ ನೀವು ತಿಳಿಯದ ಕೆಲವು ಕಾರ್ಯಗಳನ್ನು ನಾನುತಿಳಿದುಕೊಂಡಿದ್ದೇನೆ');
INSERT INTO "kannada" VALUES(12,87,'12:87','ಓ ನನ್ನ ಮಕ್ಕಳೇ, ನೀವು ಹೋಗಿ ಯೂಸುಫನ ನ್ನೂ ಅವನ ತಮ್ಮನನ್ನೂ ಪತ್ತೆ ಹಚ್ಚಿರಿ. ಅಲ್ಲಾಹನ ಅನುಗ್ರಹದ ಬಗ್ಗೆ ನಿರಾಶರಾಗಬೇಡಿರಿ. ಅವನ ಅನುಗ್ರಹದ ಬಗ್ಗೆ ಸತ್ಯನಿಷೇಧಿಗಳು ಮಾತ್ರ ನಿರಾಶರಾಗುತ್ತಾರೆ’ .');
INSERT INTO "kannada" VALUES(12,88,'12:88','ತರುವಾಯ ಅವರು ಯೂಸುಫರ ಮುಂದೆ ಹಾಜರಾಗಿ ಹೀಗೆ ಬಿನ್ನವಿಸಿದರು, ‘ಓ ಪ್ರಭು!, ನಮಗೂ ನಮ್ಮ ಕುಟುಂಬಕ್ಕೂ ಕ್ಷಾಮ ಬಾಧಿಸಿದೆ. ನಾವು ಅಗ್ಗದ ಮಾಲನ್ನು ತಂದಿದ್ದೇವೆ. ಆದ್ದರಿಂದ ತಾವು ನಮಗೆ ಪೂರ್ತಿ ಅಳೆದು ಧಾನ್ಯ ನೀಡಿರಿ ಮತ್ತು ನಮಗೆ ದಾನ ಮಾಡಿರಿ. ಅಲ್ಲಾಹು ಉದಾರ ಮತಿಗಳಿಗೆ ಪ್ರತಿಫಲ ನೀಡುತ್ತಾನೆ’.');
INSERT INTO "kannada" VALUES(12,89,'12:89','ಅವರು ಹೇಳಿದರು; ನೀವು ಅಜ್ಞಾನಿಗಳಾಗಿದ್ದಾಗ ಯೂಸುಫ್ ಮತ್ತು ಅವನ ತಮ್ಮನ ವಿಷಯದಲ್ಲಿ ಹೇಗೆ ವರ್ತಿಸಿದ್ದಿರೆಂದೂ ನಿಮಗೆ ತಿಳಿದಿದೆಯೇ?');
INSERT INTO "kannada" VALUES(12,90,'12:90','ಅವರು (ಬೆರಗಾಗಿ) ಕೇಳಿದರು; ‘ನಿಜವಾಗಿಯೂ ನೀವೇ ಯೂಸುಫರೇ?’ ಅವರು ಹೇಳಿದರು; ‘ಹೌದು!, ನಾನು ಯೂಸುಫ್ ಮತ್ತು ಇವನು ನನ್ನ ತಮ್ಮ. ಅಲ್ಲಾಹನು ನಮ್ಮ ಮೇಲೆ ಔದಾರ್ಯ ತೋರಿದ್ದಾನೆ. ವಾಸ್ತವದಲ್ಲಿ ಯಾರು ಸೂಕ್ಷ್ಮತೆ ಪಾಲಿಸುತ್ತಾರೆ ಮತ್ತು ಕ್ಷಮಿಸುತ್ತಾರೆ ಆ ಸಜ್ಜನರ ಪ್ರತಿಫಲವನ್ನು ಅಲ್ಲಾಹನು ವ್ಯರ್ಥಗೊಳಿಸುವುದಿಲ್ಲ’.');
INSERT INTO "kannada" VALUES(12,91,'12:91','ಅವರು ಹೇಳಿದರು, ‘ಅಲ್ಲಾಹನಾಣೆ! ನಿಮಗೆ ಅಲ್ಲಾಹು ನಮಗಿಂತ ಶ್ರೇಷ್ಟತೆ ದಯಪಾಲಿಸಿ ದ್ದಾನೆ. ನಿಜಕ್ಕೂ ನಾವು ತಪ್ಪಿತಸ್ಥರಾಗಿದ್ದೆವು’.');
INSERT INTO "kannada" VALUES(12,92,'12:92','ಆಗ ಯೂಸುಫರೆಂದರು; ‘ಇಂದು ನಿಮ್ಮ ಮೇಲೆ ಯಾವುದೇ ದೋಷಾರೋಪಣೆ ಇಲ್ಲ. ಅಲ್ಲಾಹು ನಿಮ್ಮನ್ನು ಕ್ಷಮಿಸಲಿ. ಅವನು ಪರಮ ಶ್ರೇಷ್ಟ ಕಾರುಣ್ಯವಂತನು');
INSERT INTO "kannada" VALUES(12,93,'12:93','‘ನನ್ನ ಈ ಅಂಗಿಯನ್ನು ಕೊಂಡು ಹೋಗಿರಿ. ನನ್ನ ತಂದೆಯವರ ಮುಖದ ಮೇಲೆ ಹಾಕಿ ಬಿಡಿರಿ. ಅವರ ದೃಷ್ಟಿ ಮರಳಿ ಬರುವುದು ಮತ್ತು ನಿಮ್ಮ ಕುಟುಂಬ ಸಮೇತ ನನ್ನ ಬಳಿಗೆ ಕರೆತನ್ನಿರಿ’.');
INSERT INTO "kannada" VALUES(12,94,'12:94','ಈ ತಂಡವು (ಈಜಿಪ್ಟ್‍ನಿಂದ) ಹೊರಟಾಗ ಅವರ ತಂದೆಯು ಹೇಳಿದರು; ‘ನನಗೆ ಯೂಸುಫನ ಪರಿಮಳ ಹೊಮ್ಮಿ ಬರುತ್ತಿದೆ . ಆದರೆ ನೀವು ನನಗೆ ಬುದ್ಧಿಭ್ರಮೆ ಬಾಧಿಸಿದೆ ಎಂದು ಹೇಳದಿದ್ದರೆ (ನಿಮಗಿದನ್ನು ನಂಬಬಹುದು).');
INSERT INTO "kannada" VALUES(12,95,'12:95','ಆಗ ಮನೆಯವರು, ‘ಅಲ್ಲಾಹನಾಣೆ, ನೀವಿನ್ನೂ ಅದೇ ಹಳೆಯ ಪ್ರಮಾದದಲ್ಲೇ ಬಿದ್ದಿರುವಿರಿ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(12,96,'12:96','ತರುವಾಯ ಸುವಾರ್ತೆ ತರುವವನು ಬಂದಾಗ ಅವನು ಯೂಸುಫರ ಅಂಗಿಯನ್ನು ಯಅïಖೂ ಬರ ಮುಖದ ಮೇಲೆ ಹಾಕಿದನು. ಕೂಡಲೇ ಅವರ ದೃಷ್ಟಿ ಮರಳಿ ಬಂತು. ಆಗ ಅವರು ಹೇಳಿದರು, ‘ನಾನು ನಿಮ್ಮಲ್ಲಿ ಹೇಳುತ್ತಿರಲಿಲ್ಲವೇ? ನಾನು ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ನಿಮಗೆ ತಿಳಿಯದುದನ್ನು ತಿಳಿಯುತ್ತೇನೆ’ .');
INSERT INTO "kannada" VALUES(12,97,'12:97','ಆಗ ಅವರೆಲ್ಲರೂ ‘ಅಪ್ಪಾ, ತಾವು ನಮ್ಮ ಪಾಪ ಗಳಿಗಾಗಿ ಕ್ಷಮೆ ಯಾಚಿಸಿರಿ. ನಿಜಕ್ಕೂ ನಾವು ತಪ್ಪಿತಸ್ಥರಾಗಿದ್ದೆವು’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(12,98,'12:98','ಆಗ ಅವರು ಹೇಳಿದರು, ನಾನು ನನ್ನ ಪ್ರಭುವಿನೊಡನೆ ನಿಮ್ಮನ್ನು ಕ್ಷಮಿಸಲಿಕ್ಕಾಗಿ ಪ್ರಾರ್ಥಿಸಲಿರುವೆನು. ಅವನು ಮಹಾಕ್ಷಮಾಶೀಲನೂ ಕೃಪಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ’.');
INSERT INTO "kannada" VALUES(12,99,'12:99','ಅನಂತರ ಅವರು, ಯೂಸುಫರ ಬಳಿಗೆ ತಲುಪಿದಾಗ ಅವರು ತಮ್ಮ ಮಾತಾಪಿತರನ್ನು ತನ್ನೆಡೆಗೆ ಅಪ್ಪಿಕೊಂಡರು. ಅವರು ಹೇಳಿದರು; ‘ನೀವೆಲ್ಲರೂ ಈಜಿಪ್ತಿಗೆ ಪ್ರವೇಶಿಸಿರಿ. ಅಲ್ಲಾಹನಿಚ್ಚಿಸಿದರೆ ನೀವು ನಿರ್ಭಯರು’.');
INSERT INTO "kannada" VALUES(12,100,'12:100','ಅವರು ತಮ್ಮ ಮಾತಾಪಿತರನ್ನು ರಾಜಪೀಠದಲ್ಲಿ ಹತ್ತಿಸಿ ಕುಳ್ಳಿರಿಸಿಕೊಂಡರು. ಎಲ್ಲರೂ ಅವರ ಮುಂದೆ ತಲೆ ಬಾಗಿದರು . ಆಗ ಯೂಸುಫ್ ಹೇಳಿದರು; ‘ಅಪ್ಪಾ, ನಾನು ಹಿಂದೆ ಕಂಡಿದ್ದ ಸ್ವಪ್ನದ ವ್ಯಾಖ್ಯಾನವಿದು. ನನ್ನ ಪ್ರಭು ಅದನ್ನು ಸತ್ಯವಾಗಿ ನೆರೆವೇರಿಸಿದನು. ನನ್ನನ್ನು ಸೆರೆಮನೆಯಿಂದ ಬಿಡಿಸಿದ ಸಂರ್ಭದಲ್ಲೂ ನನ್ನ ಮತ್ತು ನನ್ನ ಸಹೋದರರ ನಡುವೆ ಶೈತಾನನು ಪಿತೂರಿಯನ್ನುಂಟು ಮಾಡಿದ ಬಳಿಕ ಹಳ್ಳಿಗಾಡಿನಿಂದ ಅವನು ನಿಮ್ಮೆಲ್ಲರನ್ನೂ (ನನ್ನ ಬಳಿ) ತಂದು ಸೇರಿಸಿದ ಸಂದರ್ಭದಲ್ಲೂ ಅವನು ನನಗೆ ಉಪಕಾರ ಮಾಡಿದ್ದಾನೆ. ವಾಸ್ತವದಲ್ಲಿ ನನ್ನ ಪ್ರಭು ತಾನಿಚ್ಛಿಸುವ ಕಾರ್ಯಗಳನ್ನು ಸೂಕ್ಷ್ಮವಾಗಿ ನಿಯಂತ್ರಿಸುತ್ತಾನೆ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅವನು ಸರ್ವಜ್ಞನೂ ಧೀಮಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(12,101,'12:101','ಓ ನನ್ನ ಪ್ರಭೂ, ನೀನು ನನಗೆ ರಾಜಾಧಿಕಾರ ಕೊಟ್ಟೆ. ಓ ಆಕಾಶಗಳ ಮತ್ತು ಭೂಮಿಯ ನಿರ್ಮಾಪಕನೇ! ಇಹದಲ್ಲೂ ಪರದಲ್ಲೂ ನೀನೇ ನನ್ನ ರಕ್ಷಕನು. ನೀನು ನನ್ನನ್ನು ಮುಸ್ಲಿಮನ ನ್ನಾಗಿ ಮೃತ್ಯುಗೊಳಿಸು ಮತ್ತು ನಿನ್ನ ಸಜ್ಜನ ದಾಸರ ಸಾಲಿಗೆ ಸೇರಿಸು’.');
INSERT INTO "kannada" VALUES(12,102,'12:102','(ಓ ಪೈಗಂಬರರೇ,) ಇದು ನಿಮ್ಮ ಮೇಲೆ ನಾವು ಸಂದೇಶ ನೀಡುತ್ತಿರುವ ಅದೃಶ್ಯ ವೃತ್ತಾಂತಗಳಿಗೆ ಸೇರಿದೆ. ಅನ್ಯಥಾ ಯೂಸುಫರ ಸಹೋದರರು ಪರಸ್ಪರ ಒಮ್ಮತದೊಂದಿಗೆ ಕುತಂತ್ರ ಹೂಡಿದಾಗ ನೀವು ಅಲ್ಲಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(12,103,'12:103','ಆದರೆ ನೀವು ಎಷ್ಟೇ ಆಗ್ರಹಿಸಿದರೂ ಈ ಜನರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ವಿಶ್ವಾಸ ಹೊಂದುವವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(12,104,'12:104','ವಸ್ತುತಃ ನೀವು ಅವರಿಂದ ಇದರ ಹೆಸರಲ್ಲಿ ಯಾವ ಪ್ರತಿಫಲವನ್ನೂ ಕೇಳುವುದಿಲ್ಲ. ಇದು ಜಗತ್ತಿನವರಿಗೊಂದು ಉಪದೇಶ ಮಾತ್ರ.');
INSERT INTO "kannada" VALUES(12,105,'12:105','ಆಕಾಶಗಳಲ್ಲೂ ಭೂಮಿಯಲ್ಲೂ ಎಷ್ಟೋ ನಿದರ್ಶನಗಳಿವೆ. ಅವುಗಳನ್ನು ಅವಗಣಿಸುತ್ತ ಅವರು ಅವುಗಳ ಹತ್ತಿರದಲ್ಲೇ ಹಾದು ಹೋಗುತ್ತಿರುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(12,106,'12:106','ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಅಲ್ಲಾಹನನ್ನು, ಅವ ನೊಂದಿಗೆ ಇತರರನ್ನು ಸಹಭಾಗಿಗಳನ್ನಾಗಿಸಿ ಕೊಂಡೇ ನಂಬುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(12,107,'12:107','ಅಲ್ಲಾಹನಿಂದ ಅವರನ್ನು ಆವರಿಸಿಬಿಡುವ ಯಾತನೆಯು ಅವರಿಗೆ ಬಂದೆರಗುವ ಬಗ್ಗೆ ಅಥವಾ ಅವರಿಗೆ ತಿಳಿಯದಂತೆ, ಹಠಾತ್ತನೆ ಅಂತ್ಯದಿನ ಅವರ ಮೇಲೆ ಬಂದು ತಲುಪುವ ಬಗ್ಗೆ ಇವರೇನು ನಿಶ್ಚಿಂತರಾಗಿರುವರೇ?');
INSERT INTO "kannada" VALUES(12,108,'12:108','(ಪೈಗಂಬರರೇ) ಹೇಳಿರಿ; ಇದೇ ನನ್ನ ಮಾರ್ಗ. ನಾನು ಅಲ್ಲಾಹನ ಕಡೆಗೆ ಕರೆ ನೀಡುತ್ತಿದ್ದೇನೆ. ನಾನೂ ನನ್ನ ಸಂಗಾತಿಗಳೂ ಸ್ಪಷ್ಟವಾದ ಆಧಾರದಲ್ಲಿದ್ದೇವೆ. ಅಲ್ಲಾಹು ಅತ್ಯಂತ ಪಾವನನು. ಸಹಭಾಗಿಗಳಾಗಿಸುವವರೊಂದಿಗೆ ನಾನು ಸೇರಿದವನಲ್ಲ.');
INSERT INTO "kannada" VALUES(12,109,'12:109','(ಓ ಪೈಗಂಬರರೇ,) ನಾಡುಗಳಲ್ಲಿ ವಾಸಿಸುತ್ತಿದ್ದ ಪುರುಷರನ್ನು ಮಾತ್ರವೇ ನಿಮಗಿಂತ ಮುಂಚೆ ನಾವು ದಿವ್ಯ ಸಂದೇಶದೊಂದಿಗೆ ಪ್ರವಾದಿಗಳಾಗಿ ಕಳುಹಿಸಿದ್ದೇವೆ. ಅವರು (ಅವಿಶ್ವಾಸಿ ಗಳು) ಭೂಮಿಯಲ್ಲಿ ಸಂಚರಿಸಿ ಇವರಿಗಿಂತ ಮುಂಚಿನ ಜನಾಂಗಗಳ ಪರಿಣಾಮವು ಏನಾಗಿತ್ತೆಂದು ನೋಡಲಿಲ್ಲವೇ? ದೇವನಿಷ್ಠರಿಗೆ ಪರ ಲೋಕ ಗೇಹವು ಇನ್ನಷ್ಟು ಉತ್ತಮ. ನೀವು ಯೋಚಿಸಲಾರಿರಾ?');
INSERT INTO "kannada" VALUES(12,110,'12:110','ಕೊನೆಗೆ ಪ್ರವಾದಿಗಳು ನಿರಾಶೆಗೊಂಡಾಗ ಹಾಗೂ ಅವರೊಡನೆ ಹೇಳಿದ್ದು ಸುಳ್ಳಾಗಿತ್ತೆಂದೂ ಜನರು ಭಾವಿಸಿದಾಗ ಹಠಾತ್ತನೆ ನಮ್ಮ ಸಹಾಯವು ಪ್ರವಾದಿಗಳಿಗೆ ಬಂದು ತಲುಪಿತು. ಆಗ ನಾವಿಚ್ಛಿಸಿದವರನ್ನು ರಕ್ಷಿಸಲಾಯಿತು. ಅಪರಾಧಿ ಜನಾಂಗದಿಂದ ನಮ್ಮ ಸಜೆಯು ಎಷ್ಟಕ್ಕೂ ಸರಿಯಲಾರದು.');
INSERT INTO "kannada" VALUES(12,111,'12:111','ಅವರ ಚರಿತ್ರೆ ಕಥೆಗಳಲ್ಲಿ ಬುದ್ಧಿಜೀವಿಗಳಿಗೆ ಖಂಡಿತ ಪಾಠವಿದೆ. ಈ ಖುರ್‍ಆನ್ ಕೃತಕ ವಾಗಿ ಸೃಷ್ಟಿಸಲಾಗುವ ವೃತ್ತಾಂತವಲ್ಲ. ಪರಂತು ಅದಕ್ಕಿಂತ ಮುಂಚೆ ಬಂದಿರುವ ಗ್ರಂಥಗಳ ದೃಢೀಕರಣವೂ ಪ್ರತಿಯೊಂದು ವಿಷಯದ ವಿವರಣೆಯೂ ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಕೈಗೊಂಡ ಜನರಿಗೆ ಸನ್ಮಾರ್ಗದರ್ಶನವೂ ಕಾರುಣ್ಯವೂ ಆಗಿರುತ್ತದೆ');
INSERT INTO "kannada" VALUES(13,1,'13:1','ಅಲಿಫ್ ಲಾಮ್ಮೀಮ್ ರಾ, ಇವು ದೇವಗ್ರಂಥದ ಸೂಕ್ತಗಳು, ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ನಿಮಗೆ ಅವತೀರ್ಣಗೊಳಿಸಲ್ಪಟ್ಟದ್ದು (ಖುರ್‍ಆನ್) ಪರಮ ಸತ್ಯ. ಆದರೆ, ಜನರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ವಿಶ್ವಾಸವಿರಿಸುತ್ತಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(13,2,'13:2','ಆಕಾಶಗಳನ್ನು ನೀವು ಕಾಣುವ ಆಧಾರವಿಲ್ಲದೆಯೇ ಏರಿಸಿ ನಿಲ್ಲಿಸಿದವನು ಅವನೇ . ಅನಂತರ ಅವನಿಗೆ ಸಂಗತವಾದ ವಿಧದಲ್ಲಿ ಅರ್ಶ್‍ನ ಮೇಲೆ ಇಸ್ತಿವಾ ಹೊಂದಿದನು. ಅವನು ಸೂರ್ಯನನ್ನೂ ಚಂದ್ರನನ್ನೂ ನಿಯಂತ್ರಿಸಿದನು. ಪ್ರತಿಯೊಂದು ವಸ್ತುವೂ ಒಂದು ನಿರ್ದಿಷ್ಟ ಕಾಲದವರೆಗೆ ನಡೆಯು ತ್ತಿರುತ್ತದೆ. ಕಾರ್ಯವನ್ನು ಅವನೇ ನಿಯಂತ್ರಿಸು ತ್ತಿದ್ದಾನೆ. ನೀವು ನಿಮ್ಮ ಪ್ರಭುವನ್ನು ಭೇಟಿಯಾಗಲಿರುವ ಬಗ್ಗೆ ದೃಢ ನಂಬಿಕೆ ತಾಳಲಿಕ್ಕಾಗಿ ಅವನು ದೃಷ್ಟಾಂತಗಳನ್ನು ವಿವರಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(13,3,'13:3','ಅವನೇ ಭೂಮಿಯನ್ನು ವಿಸ್ತರಿಸಿದವನೂ ಅದರಲ್ಲಿ ಊರಿನಿಂತ ಪರ್ವತಗಳನ್ನು ಮತ್ತು ನದಿಗಳನ್ನು ಉಂಟುಮಾಡಿದವನೂ. ಅವನೇ ಎಲ್ಲ ವಿಧದ ಫಲ ಮೂಲಗಳಿಂದ ಎರಡು ಜೋಡಿಗಳನ್ನು ಸೃಷ್ಟಿಸಿದವನು. ಅವನು ಹಗಲಿನ ಮೇಲೆ ಇರುಳನ್ನು ಅಚ್ಛಾದಿಸುತ್ತಾನೆ. ನಿಶ್ಚಯವಾಗಿಯೂ ಆಲೋಚಿಸುವ ವರಿಗೆ ಅದರಲ್ಲಿ ಅನೇಕ ದೃಷ್ಟಾಂತಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(13,4,'13:4','ಭೂಮಿಯಲ್ಲಿ ಒಂದಕ್ಕೊಂದು ತಾಗಿಕೊಂಡಿರುವ ಬೇರೆಬೇರೆ ಭೂಭಾಗಗಳಿವೆ. ದ್ರಾಕ್ಷಾ ತೋಟಗಳಿವೆ. ಹೊಲಗಳಿವೆ. ಕವಲೊಡೆದ ಖರ್ಜೂರ ವೃಕ್ಷಗಳೂ, ಕವಲೊಡೆಯದವುಗಳೂ ಇವೆ. ಒಂದೇ ಜಲದಿಂದ ಅವುಗಳಿಗೆ ನೀರುಣಿಸಲಾಗುತ್ತದೆ. ರುಚಿಯಲ್ಲಿ ಕೆಲ ವನ್ನು ಇನ್ನು ಕೆಲವದಕ್ಕಿಂತ ನಾವು ಶ್ರೇಷ್ಟಗೊಳಿಸುತ್ತೇವೆ. ಖಂಡಿತ, ಇದರಲ್ಲಿ ವಿವೇಚಿಸುವ ಜನರಿಗೆ ಅನೇಕ ದೃಷ್ಟಾಂತಗಳಿವೆ .');
INSERT INTO "kannada" VALUES(13,5,'13:5','ನೀವು ಆಶ್ಚರ್ಯಪಡುವುದಾದರೆ ಅವರ ಈ ಮಾತು ಬಹಳ ಆಶ್ಚರ್ಯವೇ ಸರಿ. ‘ನಾವು ಮಣ್ಣಾಗಿ ಹೋದ ಬಳಿಕ ಪುನರಪಿ ಸೃಷ್ಟಿಸಲ್ಪಡುವೆವೋ?’ ಅವರು ತಮ್ಮ ಪ್ರಭುವನ್ನು ನಿಷೇಧಿಸಿದವರು. ಅವರು ಕೊರಳಲ್ಲಿ ಕೋಳ ಬಿದ್ದವರು. ಅವರೇ ನರಕದವರು ಮತ್ತು ನರಕದಲ್ಲಿ ಸದಾ ಉಳಿಯುವವರು.');
INSERT INTO "kannada" VALUES(13,6,'13:6','ಅವರು ಒಳಿತಿಗಿಂತ (ಅನುಗ್ರಹಕ್ಕಿಂತ) ಮೊದಲು ಕೆಡುಕಿಗಾಗಿ (ಶಿಕ್ಷೆಗಾಗಿ) ದುಡುಕುತ್ತಿದ್ದಾರೆ. ಅವರಿಗಿಂತ ಮುಂಚೆ ತತ್ಸಮಾನ ಶಿಕ್ಷೆಗಳು ಗತಿಸಿ ಹೋಗಿವೆ. ವಾಸ್ತವದಲ್ಲಿ ನಿನ್ನ ಪ್ರಭು ಜನರು ಅತಿರೇಕವೆ ಸಗುತ್ತಿದ್ದರೂ ಕೂಡ ಅವರಿಗೆ ಕ್ಷಮಾದಾನ ಮಾಡುವವನು. ಖಂಡಿತಾ ನಿನ್ನ ಪ್ರಭು (ದಿಕ್ಕಾರಿಗಳನ್ನು) ಘೋರ ಶಿಕ್ಷೆಗೆ ಗುರಿಪಡಿಸುವವನು.');
INSERT INTO "kannada" VALUES(13,7,'13:7','ಸತ್ಯನಿಷೇಧಿಗಳು - ‘ಈ ವ್ಯಕ್ತಿಯ ಮೇಲೆ ಇವನ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಯಾವುದೇ ದೃಷ್ಟಾಂತವನ್ನು ಇಳಿಸಲಾಗಿಲ್ಲವೇಕೆ ?’ ಎಂದು ಕೇಳುತ್ತಾರೆ. (ಪ್ರವಾದಿಯವರೇ) ನೀವು ಕೇವಲ ಎಚ್ಚರಿಕೆ ಕೊಡುವವರು ಮಾತ್ರ. ಮತ್ತು ಪ್ರತಿ ಯೊಂದು ಜನಾಂಗಕ್ಕೂ ಓರ್ವ ಮಾರ್ಗದರ್ಶಕ ನಿದ್ದಾನೆ .');
INSERT INTO "kannada" VALUES(13,8,'13:8','ಅಲ್ಲಾಹನು ಪ್ರತಿಯೊರ್ವ ಸ್ತ್ರೀಯು ಧರಿಸುವ ಗರ್ಭವನ್ನು ಅರಿತಿರುತ್ತಾನೆ. ಗರ್ಭಾಶಯದಲ್ಲಾಗುವ ವೃದ್ಧಿ-ಕ್ಷಯಗಳನ್ನೂ ಅರಿತಿರುತ್ತಾನೆ. ಮತ್ತು ಪ್ರತಿಯೊಂದು ವಸ್ತುವಿಗೂ ಅವನಲ್ಲೊಂದು ಪ್ರಮಾಣ ನಿಶ್ಚಯವಾಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(13,9,'13:9','ಅವನು ಎಲ್ಲ ದೃಶ್ಯಾದೃಶ್ಯಗಳ ಜ್ಞಾನಿ, ಅವನು ಅತಿ ಮಹಾನನು. ಚಿರೋನ್ನತನು.');
INSERT INTO "kannada" VALUES(13,10,'13:10','ನಿಮ್ಮ ಪೈಕಿ ಮೆಲುದನಿಯಿಂದ ಮಾತಾಡಿದವ ನಿರಲಿ, ಸ್ವರವೆತ್ತಿ ಮಾತಾಡಿದವನಿರಲಿ, ನಿಶಾಂ ಧಕಾರದಲ್ಲಿ ಅಡಗಿದವನಿರಲಿ ಮತ್ತು ಹಗಲಿನ ಬೆಳಕಿನಲ್ಲಿ ನಡೆಯುವವನಿರಲಿ ಅವನ ಜ್ಞಾನದ ಮಟ್ಟಿಗೆ ಎಲ್ಲರೂ ಸರಿಸಮಾನರು.');
INSERT INTO "kannada" VALUES(13,11,'13:11','ಮಾನವನಿಗೆ ಅವನ ಎದುರಿನಿಂದಲೂ ಹಿಂದಿನಿಂದಲೂ ಪಾಳಿ ಪ್ರಕಾರ ಬಂದು ಅಲ್ಲಾಹನ ಅಣತಿ ಪ್ರಕಾರ ಕಾವಲು ಕಾಯುವವರು (ದೇವ ಚರರು) ಇದ್ದಾರೆ. ಯಾವುದೇ ಜನಾಂಗವು ಸ್ವತಃ ತಾನೇ ತನ್ನ ಸ್ಥಿತಿಯನ್ನು ಬದಲಾಯಿಸಿ ಕೊಳ್ಳುವ ವರೆಗೂ ಖಂಡಿತಾ ಅಲ್ಲಾಹನು ಅವರ ಸ್ಥಿತಿಯನ್ನು ಬದಲಾಯಿಸುವುದಿಲ್ಲ. ಅಲ್ಲಾಹನು ಒಂದು ಜನಾಂಗಕ್ಕೆ ಯಾವುದೇ ಕೇಡುಂಟು ಮಾಡುವ ನಿರ್ಧಾರ ಕೈಗೊಂಡರೆ ಅದನ್ನು ಯಾರಿಂದಲೂ ತಡೆಯಲಾಗದು. ಅಲ್ಲಾಹನ ಹೊರತು ಅವರಿಗೆ ಉಸ್ತುವಾರಿದಾರನಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(13,12,'13:12','ಭಯ ಹಾಗೂ ಆಸೆಯನ್ನು ಹುಟ್ಟಿಸಿ ನಿಮ್ಮ ಮುಂದೆ ಮಿಂಚುಗಳನ್ನು ಮಿಂಚಿಸುವವನು ಅವನೇ. ಘನೀಕೃತ ಮೇಘಗಳನ್ನು ಉಂಟು ಮಾಡುವವನೂ ಅವನೇ.');
INSERT INTO "kannada" VALUES(13,13,'13:13','ಸಿಡಿಲಬ್ಬರವು (ಗುಡುಗು) ಸ್ತೋತ್ರದೊಂದಿಗೆ ಅವನ ಕೀರ್ತನೆ ಮಾಡುತ್ತದೆ . ದೇವಚರರು ಅವನಿಗೆ ಭೀತರಾಗಿ ಅವನ ಕೀರ್ತನೆ ಮಾಡು ತ್ತಾರೆ. ಅವನು ಸಿಡಿಲುಗಳನ್ನು ರವಾನಿಸುತ್ತಾನೆ. ತಾನಿಚ್ಚಿಸಿದವರ ಮೇಲೆ ಅದನ್ನು ತಾಕಿಸುತ್ತಲೂ ಇರುತ್ತಾನೆ. ಅವರಾದರೋ ಅಲ್ಲಾಹನ ವಿಚಾರದಲ್ಲಿ ವಾಗ್ವಾದದಲ್ಲಿರುತ್ತಾರೆ. ಅವನಾ ದರೋ ಕಠಿನ ದಂಡಕನು .');
INSERT INTO "kannada" VALUES(13,14,'13:14','ಅವನಿಗೆ ಸತ್ಯವಾದ ಕರೆ (ಏಕದೇವೋಪಾ ಸನೆಯ ವಚನ ಘೋಷಣೆ) ಇದೆ . ಅವನನ್ನು ಬಿಟ್ಟು ಇವರು ಆರಾಧಿಸುತ್ತಿರುವ ಇತರರು ಇವರಿಗೆ ಯಾವ ಉತ್ತರವನ್ನೂ ಕೊಡಲಾರರು. ನೀರು ತನ್ನ ಬಾಯಿಗೆ (ಸ್ವಯಂ) ಬರಲಿಕ್ಕಾಗಿ ನೀರಿದ್ದ ಕಡೆಗೆ ತನ್ನ ಕೈಗಳೆರಡನ್ನೂ ಚಾಚಿಕೊಂ ಡವನಂತೆ (ಇರುವ ಉತ್ತರದ) ಹೊರತು. ವಸ್ತುತಃ ನೀರು ಅವನ ಬಾಯಿಗೆ ತಲಪಲಾರದು. ಸತ್ಯ ನಿಷೇಧಿಗಳ ಪ್ರಾರ್ಥನೆಯು ವ್ಯರ್ಥವಲ್ಲದೇ ಇನ್ನೇನೂ ಅಲ್ಲ.');
INSERT INTO "kannada" VALUES(13,15,'13:15','ಆಕಾಶಗಳಲ್ಲೂ ಭೂಮಿಯಲ್ಲೂ ಇರುವವರು ಮನಸ್ಸಾರೆ ಇಲ್ಲವೇ ಅನಿವಾರ್ಯತೆಯಿಂದ ಅಲ್ಲಾಹ ನಿಗೆ ಸಾಷ್ಟಾಂಗವೆರಗುತ್ತಿರುವರು. ಅವರ ನೆರಳುಗಳು ಬೆಳಗು ಬೈಗುಗಳಲ್ಲಿ ಅವನ ಮುಂದೆ ಬಾಗುತ್ತವೆ .');
INSERT INTO "kannada" VALUES(13,16,'13:16','(ಪ್ರವಾದಿಯವರೇ) ಕೇಳಿರಿ; ಆಕಾಶಗಳ ಮತ್ತು ಭೂಮಿಯ ಪ್ರಭು ಯಾರು? - ಹೇಳಿರಿ; ಅಲ್ಲಾಹನು. ಹಾಗಿದ್ದರೆ ಕೇಳಿರಿ; ‘ಹಾಗಿದ್ದೂ ನೀವು ಅವನನ್ನು ಬಿಟ್ಟು ಸ್ವತಃ ತಮಗೇ ಒಳಿತು ಕೆಡು ಕುಗಳನ್ನು ಸ್ವಾಧೀನಪಡಿಸದವುಗಳನ್ನು ರಕ್ಷಕ ಮಿತ್ರರಾಗಿ ಸ್ವೀಕರಿಸುವಿರಾ?’ ಹೇಳಿರಿ; ‘ಕುರುಡನೂ ದೃಷ್ಟಿಯುಳ್ಳವನೂ ಸರಿಸಮಾನರೇ? ಕತ್ತಲುಗಳೂ ಬೆಳಕೂ ಸರಿಸಮಾನವೇ? ಅದಲ್ಲ, ಅಲ್ಲಾಹನ ಹೊರತು ಇವರು ಮಾಡಿಕೊಂಡಿರುವ ಸಹಭಾಗಿಗಳೂ ಅಲ್ಲಾಹನು ಸೃಷ್ಟಿಸುವಂತೆ ಸೃಷ್ಟಿ ನಡೆಸಿ, ಕೊನೆಗೆ (ಎರಡು ವಿಭಾಗದ) ಸೃಷ್ಟಿಯು ಅವರಿಗೆ ಅಸ್ಪಷ್ಟವಾಗಿ ಬಿಟ್ಟಿತೇ?’ ಹೇಳಿರಿ; ಸಕಲ ವಸ್ತುಗಳ ಸೃಷ್ಟಿಕರ್ತನು ಅಲ್ಲಾಹು ಮಾತ್ರ. ಅವನು ಏಕೈಕನೂ ಅಜೇಯನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(13,17,'13:17','ಅವನು ಆಕಾಶದಿಂದ ಮಳೆ ಸುರಿಸಿದನು. ಆಗ ಪರ್ವತ ತಪ್ಪಲುಗಳು ಅವುಗಳ ವಿಶಾಲತೆಗೆ ಹೊಂದಿ ಹರಿದವು. ಹರಿವ ನೀರು ಮೇಲ್ಗಡೆ ನೊರೆಯನ್ನು ವಹಿಸಿಕೊಂಡಿತು. ಇದೇ ತರದ ನೊರೆಯು ಆಭರಣ ಹಾಗೂ ಉಪಕರಣಗಳನ್ನು ಮಾಡಲಿಕ್ಕಾಗಿ ಅವರು ಬೆಂಕಿಯಲ್ಲಿ ಕರಗಿಸುವ ಲೋಹದಿಂದಲೂ ಏಳುತ್ತದೆ. ಇದೇ ಪ್ರಕಾರ ಅಲ್ಲಾಹನು ಸತ್ಯ ಮತ್ತು ಅಸತ್ಯಗಳನ್ನು ಉಪಮಿಸುತ್ತಾನೆ. ಅಂದ ಮೇಲೆ ನೊರೆಯು ತ್ಯಾಜ್ಯವಾಗಿ ಹೋಗುತ್ತದೆ. ಮಾನವನಿಗೆ ಉಪಯುಕ್ತವಾದುದು ಭೂಮಿಯಲ್ಲಿ ತಂಗಿ ಕೊಳ್ಳುತ್ತದೆ. ಹೀಗೆ ಅಲ್ಲಾಹನು ಉಪಮೆಗಳನ್ನು ವಿವರಿಸಿ ಹೇಳುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(13,18,'13:18','ತಮ್ಮ ಪ್ರಭುವಿನ ಕರೆಗೆ ಓಗೊಟ್ಟವರಿಗೆ ಉತ್ತಮ ಪ್ರತಿಫಲವಿದೆ (ಸ್ವರ್ಗವಿದೆ). ಅವನ ಕರೆಗೆ ಓ ಗೊಡದವರು, ಭೂಮಿಯ ಸಕಲ ಸಂಪತ್ತಿನ ಒಡೆಯ ರಾಗಿದ್ದರೂ ಅಷ್ಟೇ ಇನ್ನೂ ಒದಗಿಸಿಕೊಂ ಡರೂ ಅದೆಲ್ಲವನ್ನೂ (ಅಲ್ಲಾಹನಿಂದ ಪಾರಾಗಲಿಕ್ಕಾಗಿ) ಅವರು ಪ್ರಾಯಶ್ಚಿತ್ತವಾಗಿ ತೆರುತ್ತಿದ್ದರು. ಅವರಿಗೆ ಕೆಟ್ಟ ರೀತಿಯ ವಿಚಾರಣೆಯಿದೆ. ಮತ್ತು ಅವರ ತಾಣವು ನರಕವಾಗಿದೆ. ಅತ್ಯಂತ ನಿಕೃಷ್ಟ ವಾಸ್ತವ್ಯವದು.');
INSERT INTO "kannada" VALUES(13,19,'13:19','ನಿಮ್ಮ ಪ್ರಭುವಿನಿಂದ ನಿಮ್ಮ ಮೇಲೆ ಅವತೀರ್ಣ ಗೊಳಿಸಲಾದ ಈ ಗ್ರಂಥವನ್ನು ಸತ್ಯವೆಂದು ತಿಳಿಯುವ ವ್ಯಕ್ತಿಯೊಬ್ಬನು, ಅಂಧನಾಗಿರುವ ವ್ಯಕ್ತಿಯಂತೆ ಆಗಬಲ್ಲನೆ? ಖಂಡಿತಾ ಬುದ್ಧಿ ಜೀವಿಗಳು ಮಾತ್ರ ಉಪದೇಶ ಸ್ವೀಕರಿಸುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(13,20,'13:20','ಅವರು ಅಲ್ಲಾಹನ ಪ್ರತಿಜ್ಞೆಯನ್ನು ಪಾಲಿಸುತ್ತಾರೆ. ಒಪ್ಪಂದವನ್ನು ಮುರಿದು ಬಿಡುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(13,21,'13:21','ಅಲ್ಲಾಹನು (ಯಾವ ಸಂಬಂಧಗಳನ್ನು) ಬೆಸೆ ಯಲು ಆಜ್ಞೆ ಕೊಟ್ಟಿರುವನೋ ಅವುಗಳನ್ನು ಬೆಸೆಯುತ್ತಾರೆ. ತಮ್ಮ ಪ್ರಭುವನ್ನು ಭಯಪಡುತ್ತಾರೆ. ಕೆಟ್ಟ ರೀತಿಯ ವಿಚಾರಣೆಯನ್ನು ಹೆದರುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(13,22,'13:22','ಅವರು ತಮ್ಮ ಪ್ರಭುವಿನ ಸಂಪ್ರೀತಿಯನ್ನು ಬಯಸಿ ಕ್ಷಮೆ ಪಾಲಿಸುತ್ತಾರೆ. ನಮಾಝನ್ನು ಸಂಸ್ಥಾಪಿಸುತ್ತಾರೆ. ನಾವು ನೀಡಿರುವ ಸಂಪತ್ತಿನಿಂದ ಬಹಿರಂಗವಾಗಿಯೂ ರಹಸ್ಯವಾಗಿಯೂ ವ್ಯಯಿಸುತ್ತಾರೆ. ಕೆಡುಕುಗಳನ್ನು ಒಳಿತುಗಳಿಂದ ತಡೆಗಟ್ಟುತ್ತಾರೆ. ಅವರಿಗೇ ಇರುವುದು ಶ್ರೇಷ್ಠ ಪರ್ಯಾವಸಾನದ ಬೀಡು.');
INSERT INTO "kannada" VALUES(13,23,'13:23','ಅಂದರೆ ಶಾಶ್ವತ ವಾಸಕ್ಕಿದ್ದ ಸ್ವರ್ಗೋದ್ಯಾನಗಳು, ಅವರೂ ಅವರ ಪೂರ್ವಿಕರು, ಅವರ ಪತ್ನಿಯರು ಹಾಗೂ ಅವರ ಸಂತತಿಗಳ ಪೈಕಿ ಸಜ್ಜನರಾದ ವರೂ ಅವುಗಳಲ್ಲಿ ಪ್ರವೇಶಿಸುವರು . ದೇವಚ ರರು ಎಲ್ಲಾ ಕವಾಟಗಳಿಂದಲೂ ಅವರ ಬಳಿ (ಸ್ವಾಗತಿಸಲು) ಬರುವರು.');
INSERT INTO "kannada" VALUES(13,24,'13:24','‘ನಿಮ್ಮ ಸಹನೆಯ ಫಲವಾಗಿ ನಿಮಗೆ ಶಾಂತಿ ಇರಲಿ’. (ಎನ್ನುತ್ತಾ). ಅಂತಿಮ ಗೃಹವು (ಸ್ವರ್ಗ) ಅದೆಷ್ಟು ಉತ್ತಮ! ');
INSERT INTO "kannada" VALUES(13,25,'13:25','ಅಲ್ಲಾಹನ ಪ್ರತಿಜ್ಞೆಯನ್ನು ಅದು ಸ್ಥಿರಬದ್ಧವಾದ ಬಳಿಕ ಮುರಿದುಬಿಡುವವರು, ಅಲ್ಲಾಹನು ಜೋಡಿ ಸಲು ಆಜ್ಞೆ ಕೊಟ್ಟಂತಹ ಸಂಬಂಧಗಳನ್ನು ಮುರಿ ಯುವವರು ಮತ್ತು ಭೂಮಿಯ ಮೇಲೆ ಕೇಡು ಮಾಡು ವವರು ಯಾರೋ ಅವರಿಗೆ ಶಾಪವಿದೆ. ಅವರಿಗೆ (ಪರಲೋಕದಲ್ಲಿ) ಅತ್ಯಂತ ಕೆಟ್ಟ ನಿವಾಸವಿದೆ.');
INSERT INTO "kannada" VALUES(13,26,'13:26','ಅಲ್ಲಾಹನು ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಜೀವನಾಧಾರ ವನ್ನು ವಿಶಾಲಗೊಳಿಸುತ್ತಾನೆ. ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಪರಿಮಿತಗೊಳಿಸುತ್ತಾನೆ. ಇವರು ಇಹಜೀವನದಲ್ಲಿ ಆನಂದಗೊಂಡಿದ್ದಾರೆ. ವಸ್ತುತಃ ಇಹಜೀವನ ವು ಪರಲೋಕದ ಜೀವನದ ಮುಂದೆ ಅತಿ ತುಚ್ಛ ಸುಖಾನುಭವವೇ ಹೊರತು ಬೇರೇನೂ ಅಲ್ಲ .');
INSERT INTO "kannada" VALUES(13,27,'13:27','ನಿಷೇಧಿಸಿದವರು, (ಪ್ರವಾದಿಯನ್ನುದ್ದೇಶಿಸಿ) ‘ಇವನ ಮೇಲೆ ಇವನ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಯಾವುದೇ ನಿದರ್ಶನವೇಕೆ ಅವತೀರ್ಣಗೊಳ್ಳಲಿಲ್ಲ?’ ಎಂದು ಕೇಳುತ್ತಾರೆ. (ಪ್ರವಾದಿಯವರೇ) ಹೇಳಿರಿ ‘ಅಲ್ಲಾಹನು ನಿಶ್ಚಯವಾಗಿಯೂ ತಾನಿಚ್ಛಿ ಸಿದವರನ್ನು ದಾರಿ ತಪ್ಪಿಸುತ್ತಾನೆ ಮತ್ತು ಅವನೆಡೆಗೆ ಮರಳಿದವರನ್ನು ತನ್ನ ಧರ್ಮದೆಡೆಗೆ ದಾರಿಯನ್ನು ತೋರಿಸುತ್ತಾನೆ’.');
INSERT INTO "kannada" VALUES(13,28,'13:28','ಅರ್ಥಾತ್ ವಿಶ್ವಾಸವಿರಿಸಿದ ಮತ್ತು ಅಲ್ಲಾಹನ ಸ್ಮರಣೆಯಿಂದ ಹೃದಯಗಳು ಶಾಂತಗೊಂಡವರನ್ನು. ತಿಳಿಯಿರಿ : ಅಲ್ಲಾಹನ ಸ್ಮರಣೆಯಿಂದಲೇ ಹೃದಯಗಳು ಶಾಂತಗೊಳ್ಳುತ್ತವೆ.');
INSERT INTO "kannada" VALUES(13,29,'13:29','ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಸ್ವೀಕರಿಸಿ ಸತ್ಕರ್ಮವೆಸಗಿದವರು ಯಾರೋ ಅವರಿಗೆ ಮಂಗಳವೂ ಶ್ರೇಷ್ಠ ನಿರ್ಗಮನವೂ ಇದೆ.');
INSERT INTO "kannada" VALUES(13,30,'13:30','(ಓ ಪೈಗಂಬರರೇ,) ಅದೇ ಪ್ರಕಾರ ನಿಮ್ಮನ್ನು ನಾವು ಒಂದು ಸಮುದಾಯಕ್ಕೆ ರಸೂಲರಾಗಿ ಮಾಡಿ ಕಳುಹಿಸಿರುತ್ತೇವೆ. ಅದಕ್ಕೂ ಮುನ್ನ ಅನೇಕ ಸಮುದಾಯಗಳು ಗತಿಸಿ ಹೋಗಿವೆ. ನಾವು ನಿಮಗೆ ದಿವ್ಯಸಂದೇಶವಾಗಿ ಅವತೀರ್ಣ ಗೊಳಿಸುತ್ತಿರುವ ಸಂದೇಶವನ್ನು ಅವರಿಗೆ ಓದಿ ಹೇಳಲಿಕ್ಕಾಗಿ (ನಿಮ್ಮನ್ನು ಕಳುಹಿಸಿದ್ದೇನೆ). ಅವರು ಪರಮ ಕರುಣಾಮಯಿ ಅಲ್ಲಾಹನನ್ನು ನಿಷೇಧಿಸುತ್ತಾರೆ. ಹೇಳಿರಿ, ‘ಅವನೇ ನನ್ನ ಪಾಲಕ ಪ್ರಭು, ಅವನ ಹೊರತು ಆರಾಧ್ಯನಾರೂ ಇಲ್ಲ. ನಾನು ಅವನ ಮೇಲೆಯೇ ಭರವಸೆಯನ್ನಿರಿಸಿದ್ದೇನೆ. ಅವನೆಡೆಗೇ ನನ್ನ ನಿರ್ಗಮನ’.');
INSERT INTO "kannada" VALUES(13,31,'13:31','ಪಾರಾಯಣ ಮಾಡಲಾಗುವ ಒಂದು ಗ್ರಂಥ (ಖುರ್‍ಆನ್)ದ ಮೂಲಕ ಪರ್ವತಗಳನ್ನು ಚಲಿ ಸುವಂತೆಯೂ ಅಥವಾ ಭೂಮಿಯನ್ನು ಸೀಳಿ ಬಿಡುವಂತೆಯೂ ಅಥವಾ ಮೃತರೊಂದಿಗೆ ಮಾತಾಡುವಂತೆಯೂ ಮಾಡಲಾಗುತ್ತಿದ್ದರೂ (ಅವರು ನಂಬುತ್ತಿರಲಿಲ್ಲ) ನಿಜವಾಗಿ ಸಕಲ ಕಾರ್ಯಗಳ ಪರಮಾಧಿಕಾರ ಅಲ್ಲಾಹನ ನಿಯಂ ತ್ರಣದಲ್ಲಿದೆ. ಅಲ್ಲಾಹು ಇಚ್ಛಿಸುತ್ತಿದ್ದರೆ ಸಕಲ ಮಾನವರನ್ನೂ ಸರಿದಾರಿಗೆ ಸೇರಿಸುತ್ತಿದ್ದನೆಂದು ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ತಿಳಿಯಲಿಲ್ಲವೇ? ಸತ್ಯನಿಷೇಧಿ ಗಳಿಗೆ ತಮ್ಮ ಕರ್ಮದ ಫಲವಾಗಿ ಯಾವುದೇ ವಿಪತ್ತು ಬಾದಿಸುತ್ತಲೇ ಇರುವುದು ಅಥವಾ ಅವರ ಊರ ಸಮೀಪದಲ್ಲೇ ನೀವು (ಸೈನ್ಯ ಸಮೇತ) ಬಂದಿಳಿಯಲಿದ್ದೀರಿ. ಅಲ್ಲಾಹನ ವಾಗ್ದಾನ ಬಂದು ತಲುಪುವ ತನಕ. ನಿಶ್ಚಯ ಅಲ್ಲಾಹನು ತನ್ನ ವಾಗ್ದಾನವನ್ನು ಉಲ್ಲಂಘಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(13,32,'13:32','ನಿಮಗಿಂತ ಮೊದಲೂ ಅನೇಕ ದೂತರು ಪರಿ ಹಾಸ್ಯ ಕ್ಕೊಳಗಾಗಿದ್ದಾರೆ. ಆದರೆ ನಾನು ಸತ್ಯ ನಿಷೇಧಿಗಳಿಗೆ ಸಮಯವನ್ನು ಅವಕಾಶ ಮಾಡಿ ಕೊಟ್ಟೆನು. ನಂತರ ಅವರನ್ನು ಬಿಗಿ ಹಿಡಿದೆನು. ಆಗ ನನ್ನ ಶಿಕ್ಷೆ ಹೇಗಿತ್ತು!');
INSERT INTO "kannada" VALUES(13,33,'13:33','ಪ್ರತಿಯೊಬ್ಬನ ಗಳಿಕೆಯ ಮೇಲೆ ಮೇಲ್ನೋಟ ವಹಿಸಿರುವವನೇ? (ಏನೇನೂ ತಿಳಿಯದ ವಿಗ್ರಹ ಗಳಂತೆ?). ಅವರು ಅಲ್ಲಾಹುವಿಗೆ ಭಾಗೀದಾರರನ್ನು ನಿಶ್ಚಯಿಸಿಕೊಂಡಿದ್ದಾರೆ. (ಓ ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ - ನೀವು ಅವರ ಹೆಸರನ್ನು ಹೇಳಿ ಕೊಡಿರಿ. ಅದಲ್ಲ; ಅಲ್ಲಾಹನು ತನ್ನ ಭೂಮಿಯಲ್ಲಿ ತಿಳಿದಿರದಂತಹ ಒಂದು ವಿಷಯವನ್ನು ನೀವು ಅವನಿಗೆ ತಿಳಿಸುತ್ತೀರಾ? ಅಥವಾ ನೀವು ತಿರುಳಿಲ್ಲದ ಮಾತನ್ನು ಹೇಳಿಬಿಡುತ್ತೀg? ವಾಸ್ತವದಲ್ಲಿ ಸತ್ಯ ನಿಷೇಧಿಗಳಿಗೆ ಅವರ ಕುತಂತ್ರವನ್ನು ಮನೋ ಹರಗೊಳಿಸಲಾಗಿದೆ. ಮತ್ತು ಸನ್ಮಾರ್ಗದಿಂದ ಅವರನ್ನು ತಡೆಯಲಾಗಿದೆ. ಯಾರನ್ನು ಅಲ್ಲಾಹು ದಾರಿ ತಪ್ಪಿಸುತ್ತಾನೆ; ಅವನಿಗಾರೂ ಮಾರ್ಗ ದರ್ಶಕನಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(13,34,'13:34','ಇಂತಹವರಿಗೆ ಇಹಜೀವನದಲ್ಲಿ ಶಿಕ್ಷೆ ಇದೆ ಮತ್ತು ಪರಲೋಕದ ಶಿಕ್ಷೆ ಇದಕ್ಕಿಂತಲೂ ಕಠಿಣವಾಗಿದೆ. ಅವರನ್ನು ಅಲ್ಲಾಹನಿಂದ (ಶಿಕ್ಷೆಯಿಂದ) ರಕ್ಷಿಸುವವರು ಯಾರೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(13,35,'13:35','ದೇವಭಯವಿರಿಸಿಕೊಂಡವರಿಗೆ ವಾಗ್ದಾನ ಮಾಡಲಾಗಿರುವ ಸ್ವರ್ಗದ ಸ್ಥಿತಿ ಹೀಗಿದೆ : ಅದರ ತಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆಗಳು ಹರಿಯುತ್ತಿವೆ. ಅದರಲ್ಲಿನ ಭೋಜನವು ಅನಶ್ವರ. ನೆರಳೂ! ಅದೇ ದೇವಭಯವುಳ್ಳವರ ಪರಮ ಗಮ್ಯ ತಾಣ. ಸತ್ಯನಿಷೇಧಿಗಳ ಪರಮಗಮ್ಯವು ನರಕಾಗ್ನಿಯೇ ಆಗಿದೆ.');
INSERT INTO "kannada" VALUES(13,36,'13:36','(ಓ ಪೈಗಂಬರರೇ,) ನಾವು (ಹಿಂದೆ) ಯಾರಿಗೆ ಗ್ರಂಥ ದಯಪಾಲಿಸಿದ್ದೆವೋ ಅವರು ನಿಮಗೆ ನಾವು ಅವತೀರ್ಣಗೊಳಿಸಿರುವ (ಖುರ್‍ಆನ್) ಗ್ರಂಥದಿಂದ ಸಂತುಷ್ಟರಾಗಿದ್ದಾರೆ. ಇದರ ಕೆಲವು ಭಾಗಗಳನ್ನು ನಿಷೇಧಿಸುವವರು ಸಖ್ಯ ಗುಂಪುಗಳಲ್ಲಿ ಇದ್ದಾರೆ. ಹೇಳಿರಿ; ನನಗೆ ಅಲ್ಲಾಹನನ್ನು ಆರಾಧಿಸುವಂತೆಯೂ ಅವನಿಗೆ ಪಾಲು ಸೇರಿಸದಂತೆಯೂ ಆಜ್ಞೆ ನೀಡಲಾಗಿದೆ. ಆತನ ಕಡೆಗೇ ನಾನು ಕರೆ ನೀಡುತ್ತೇನೆ ಮತ್ತು ನನ್ನ ಮರಳುವಿಕೆಯೂ ಆತನ ಕಡೆಗೇ ಇದೆ.');
INSERT INTO "kannada" VALUES(13,37,'13:37','ಇದೇ ಪ್ರಕಾರ (ಖುರ್‍ಆನನ್ನು) ಅರಬಿ ಭಾಷೆಯಲ್ಲಿರುವ ಒಂದು ನ್ಯಾಯ ಪ್ರಮಾಣವಾಗಿ ಇದನ್ನು ನಿಮಗೆ ಅವತೀರ್ಣಗೊಳಿಸಿರುತ್ತೇವೆ. ನಿಮಗೆ ಜ್ಞಾನ ಬಂದ ಬಳಿಕವೂ ನೀವು ಜನರ ತನ್ನಿಚ್ಛೆಗಳನ್ನನುಸರಿಸಿದರೆ, ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆ ಯಿಂದ ರಕ್ಷಿಸುವ ಯಾವುದೇ ಸಹಾಯಕನಾಗಲಿ, ರಕ್ಷಕನಾಗಲಿ ನಿಮಗೆ ಇರಲಾರರು .');
INSERT INTO "kannada" VALUES(13,38,'13:38','ನಿಮಗಿಂತ ಮುಂಚೆಯೂ ನಾವು ಅನೇಕ ದೂತರನ್ನು ಕಳುಹಿಸಿದ್ದೆವು. ನಾವು ಅವರಿಗೆ ಪತ್ನಿಯರನ್ನೂ ಮಕ್ಕಳನ್ನೂ ನೀಡಿದ್ದೆವು. ಯಾವ ದೂತನಿಗೂ ಅಲ್ಲಾಹನ ಅನುಮತಿಯಿಲ್ಲದೆ ಯಾವುದೇ ದೃಷ್ಟಾಂತವನ್ನು ತರಲಾಗದು . ಪ್ರತಿಯೊಂದು ಅವಧಿಗೂ ಒಂದು (ಪ್ರಮಾಣ) ಗ್ರಂಥವಿದೆ.');
INSERT INTO "kannada" VALUES(13,39,'13:39','ಅಲ್ಲಾಹು ತಾನಿಚ್ಛಿಸಿದ್ದನ್ನು ಅಳಿಸುತ್ತಾನೆ. ಮತ್ತು ಉಳಿಸುತ್ತಾನೆ. `ಮೂಲಗ್ರಂಥ’ ಅವನ ಬಳಿಯಲ್ಲೇ ಇದೆ .');
INSERT INTO "kannada" VALUES(13,40,'13:40','(ಓ ಪೈಗಂಬರರೇ,) ನಾವು ಇವರಿಗೆ ಕೊಡುತ್ತಿರುವ ಮುನ್ನೆಚ್ಚರಿಕೆಯಲ್ಲಿ (ಶಿಸ್ತು ಕ್ರಮಗಳಲ್ಲಿ) ಕೆಲವನ್ನು ನಾವು ನಿಮಗೆ ತೋರಿಸಿದರೂ ಅಥವಾ ಅದು ಪ್ರಕಟವಾಗುವುದಕ್ಕೆ ಮುಂಚೆ ನಿಮ್ಮನ್ನು ಎತ್ತಿಕೊಂಡರೂ (ಮೃತ್ಯುಗೊಳಿಸಿದರೂ) ಹೇಗಿ ದ್ದರೂ ಸಂದೇಶವನ್ನು ಮುಟ್ಟಿಸುವುದು ಮಾತ್ರ ನಿಮ್ಮ ಹೊಣೆ. ಅವರ ಲೆಕ್ಕಾಚಾರ ಪಡೆಯುವುದು ನಮ್ಮ ಬಾಧ್ಯತೆ.');
INSERT INTO "kannada" VALUES(13,41,'13:41','ನಾವು (ಅವರ) ಭೂಮಿಗೆ ಹೋಗಿ ಅದರ ನಾನಾ ದಿಕ್ಕುಗಳಿಂದಲೂ ಅದನ್ನು ಮುದುಡಿಸುತ್ತ ಬರುತ್ತಿರುವುದನ್ನು ಅವರು ನೋಡುತ್ತಿಲ್ಲವೆ? ಅಲ್ಲಾಹು ವಿಧಿಸುತ್ತಾನೆ. ಅವನ ತೀರ್ಮಾನವನ್ನು ತಿದ್ದುಪಡಿ ಮಾಡಬಲ್ಲವನು ಯಾರೂ ಇಲ್ಲ. ಅವನು ವಿಚಾರಣೆಯಲ್ಲಿ ಅತಿ ಶೀಘ್ರನು.');
INSERT INTO "kannada" VALUES(13,42,'13:42','ಇವರಿಗಿಂತ ಮುಂಚೆ ಗತಿಸಿದವರೂ ಕುತಂತ್ರಗಳನ್ನು ಹೂಡಿದ್ದರು. ಆದರೆ ನಿಜವಾದ ತಂತ್ರವು ಸರ್ವ ಸಂಪೂರ್ಣವಾಗಿ ಅಲ್ಲಾಹನಿಗಿದೆ. ಪ್ರತಿಯೊಬ್ಬ ದೇಹಿಯು ಪ್ರವರ್ತಿಸುವುದನ್ನು ಅವನು ತಿಳಿಯುತ್ತಾನೆ. ಲೋಕದ ಶುಭ ಪರ್ಯಾವಸಾನವು ಯಾರಿಗೆ ಎಂಬುದನ್ನು ಸತ್ಯನಿಷೇಧಿಯು ತಿಳಿಯಲಿರುವನು.');
INSERT INTO "kannada" VALUES(13,43,'13:43','ನೀವು ದೇವದೂತರಲ್ಲವೆಂದು ಸತ್ಯನಿಷೇಧಿಗಳು ಹೇಳುತ್ತಾರೆ. ಹೇಳಿರಿ; `ನನ್ನ ಮತ್ತು ನಿಮ್ಮ ನಡುವೆ ಸಾಕ್ಷಿಯಾಗಿ ಅಲ್ಲಾಹನೇ ಸಾಕು. ಮತ್ತು ಯಾರ ಬಳಿ ದಿವ್ಯ ಗ್ರಂಥದ ಜ್ಞಾನವಿದೆಯೋ ಅವರೂ (ಸಾಕು.)');
INSERT INTO "kannada" VALUES(14,1,'14:1','ಅಲಿಫ್ ಲಾಮ್ ರಾ, (ಓ ಮುಹಮ್ಮದ್‍ರೇ,) ಜನರನ್ನು ಅವರ ಪ್ರಭುವಿನ ಅನುಜ್ಞೆಯಂತೆ ನೀವು ಅಂಧಕಾರಗಳಿಂದ ಪ್ರಕಾಶದ ಕಡೆಗೆ ಹೊರತರಲಿಕ್ಕಾಗಿ ನಿಮಗೆ ನಾವು ಅವತೀರ್ಣಗೊಳಿಸಿರುವ ಗ್ರಂಥವಿದು. ಅರ್ಥಾತ್, ಮಹಾ ಪ್ರತಾಪಿಯೂ ಸ್ವಯಂ ಸ್ತುತ್ಯರ್ಹನೂ ಆಗಿರುವ ಅಲ್ಲಾಹನ ಮಾರ್ಗದೆಡೆಗೆ.');
INSERT INTO "kannada" VALUES(14,2,'14:2','ಆಕಾಶಗಳ ಹಾಗೂ ಭೂಮಿಯ ಸಕಲ ಸೃಷ್ಟಿಗಳ ಮಾಲಕನಾದ ಅಲ್ಲಾಹನ (ಮಾರ್ಗದೆಡೆಗೆ). ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಅತ್ಯಂತ ಕಠಿಣ ವಿನಾಶಕಾರಿ ಶಿಕ್ಷೆಯಿದೆ.');
INSERT INTO "kannada" VALUES(14,3,'14:3','ಅರ್ಥಾತ್ ಪಾರತ್ರಿಕ ಜೀವನಕ್ಕಿಂತ ಐಹಿಕ ಜೀವನವನ್ನು ಹೆಚ್ಚಾಗಿ ಪ್ರೀತಿಸುವ, ಜನರನ್ನು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ ತಡೆಯುವ, ಮತ್ತು ಈ ಮಾರ್ಗಕ್ಕೆ ವಕ್ರತೆಯನ್ನು ಬಯಸುವ ಜನರಿಗೆ. ಇವರು (ಸತ್ಯದಿಂದ) ಬಹು ದೂರದ ದುರ್ಮಾರ್ಗದಲ್ಲಿದ್ದಾರೆ .');
INSERT INTO "kannada" VALUES(14,4,'14:4','ಯಾವ ಪ್ರವಾದಿಯನ್ನೂ ಅವರ ಜನತೆಯ ಭಾಷೆಯಲ್ಲಲ್ಲದೆ ನಾವು ಕಳುಹಿಸಿಲ್ಲ, ಅವರಿಗೆ ಸ್ಪಷ್ಟವಾಗಿ ವಿವರಿಸುವ ಸಲುವಾಗಿ. ಅಲ್ಲಾಹು ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ಪಥಭ್ರಷ್ಟಗೊಳಿಸುತ್ತಾನೆ ಮತ್ತು ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಸನ್ಮಾರ್ಗದರ್ಶನ ನೀಡುತ್ತಾನೆ. ಅವನು ಮಹಾ ಪ್ರತಾಪಶಾಲಿಯೂ ಧೀಮಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(14,5,'14:5','ನಿಮ್ಮ ಜನಾಂಗವನ್ನು ಅಂಧಕಾರಗಳಿಂದ ಹೊರತೆಗೆದು ಪ್ರಕಾಶಕ್ಕೆ ತರಬೇಕೆಂದೂ ಅವರಿಗೆ ಅಲ್ಲಾಹನ (ಅನುಗ್ರಹದ) ದಿನಗಳ ಬಗ್ಗೆ ನೆನಪಿಸಬೇಕೆಂದೂ ನಾವು ಮೂಸಾರನ್ನು ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳೊಂದಿಗೆ ಕಳುಹಿಸಿದ್ದೆವು. ಪ್ರತಿಯೊಬ್ಬ ಸಹನಶೀಲನಿಗೂ ಕೃತಜ್ಞನಿಗೂ ಅದರಲ್ಲಿ ಖಂಡಿತ ನಿದರ್ಶನಗಳಿವೆ');
INSERT INTO "kannada" VALUES(14,6,'14:6','ಮೂಸಾ ತನ್ನ ಜನಾಂಗದೊಡನೆ ಹೇಳಿದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ. ನಿಮಗೆ ಕಠಿಣ ಶಿಕ್ಷೆಯ ಅನುಭವ ಕೊಡುತ್ತಿದ್ದ ಹಾಗೂ ನಿಮ್ಮ ಗಂಡು ಮಕ್ಕಳನ್ನು ವಧಿಸಿಬಿಡುವ ಮತ್ತು ನಿಮ್ಮ ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಜೀವಂತ ಬಿಟ್ಟು ಬಿಡುತ್ತಿದ್ದ ಫಿರ್‍ಔನನ ಜನಾಂಗದಿಂದ ನಿಮ್ಮನ್ನು ರಕ್ಷಿಸಿದ ಸಂದರ್ಭದಲ್ಲಿ ಅಲ್ಲಾಹು ನಿಮ್ಮ ಮೇಲೆ ಮಾಡಿದ ಅನುಗ್ರಹವನ್ನು ನೆನಪಿಡಿರಿ. ಇದರಲ್ಲಿ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಮಹಾ ಪರೀಕ್ಷೆಯಿತ್ತು.');
INSERT INTO "kannada" VALUES(14,7,'14:7','ನೀವು ಕೃತಜ್ಞರಾಗಿದ್ದರೆ ನಾನು ನಿಮಗೆ (ಅನುಗ್ರಹವನ್ನು) ಹೆಚ್ಚಿಸುವೆನೆಂದೂ ನೀವು ಕೃತಘ್ನತೆ ತೋರಿದರೆ ನನ್ನ ಶಿಕ್ಷೆಯು ಅತ್ಯಂತ ಕಠಿಣವಾಗಿದೆಯೆಂದೂ ನಿಮ್ಮ ಪ್ರಭು ಘೋಷಿಸಿದ ಸಂದರ್ಭ ವನ್ನು ಸ್ಮರಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(14,8,'14:8','ಮೂಸಾ ಹೇಳಿದರು; ನೀವೂ ಭೂಮಿಯ ಮೇಲೆ ವಾಸಿಸುವವರೆಲ್ಲರೂ ಕೃತಘ್ನತೆ ತೋರಿದರೂ ಅಲ್ಲಾಹು ನಿರಪೇಕ್ಷನೂ ಸ್ವಯಂ ಸ್ತುತ್ಯರ್ಹನೂ ಆಗಿರುತ್ತಾನೆ (ಎಂಬುದನ್ನು ತಿಳಿದುಕೊಳ್ಳಿರಿ.)');
INSERT INTO "kannada" VALUES(14,9,'14:9','ನೂಹರ ಜನಾಂಗ, ಆದ್, ಸಮೂದ್ ಮತ್ತು ಅಲ್ಲಾಹನ ಹೊರತು ಇನ್ನಾರೂ ತಿಳಿಯದ ಅವರ ನಂತರದ ಹಲವಾರು ಜನಾಂಗಗಳೂ ಸೇರಿದಂತೆ ನಿಮ್ಮ ಪೂರ್ವಿಕರ ಸಮಾಚಾರಗಳು ನಿಮಗೆ ತಲುಪಲಿಲ್ಲವೇ? ಅವರ ಸಂದೇಶವಾಹಕರು ಅವರ ಬಳಿಗೆ ಸುವ್ಯಕ್ತ ನಿದರ್ಶನಗಳನ್ನು ತಂದಾಗ ಅವರು ತಮ್ಮ ಕೈಯನ್ನು ಬಾಯೊಳಗೆ ಒತ್ತಿಕೊಂಡು ಹೇಳಿದರು; ‘ಯಾವ ಸಂದೇಶ ಸಹಿತ ನೀವು ಕಳು ಹಿಸಲ್ಪಟ್ಟಿರುವಿರೋ ಅದನ್ನು ನಾವು ನಿಷೇಧಿಸುತ್ತೇವೆ ಮತ್ತು ನೀವು ನಮಗೆ ಯಾವುದರ ಕಡೆಗೆ ಕರೆ ನೀಡುತ್ತೀರೋ ಅದರ ಬಗ್ಗೆ ನಾವು ಕಳವಳಕಾರಿಯಾದ ಸಂಶಯಕ್ಕೊಳಗಾಗಿದ್ದೇವೆ’ .');
INSERT INTO "kannada" VALUES(14,10,'14:10','ಅವರ ದೂತರು ಹೇಳಿದರು; ಆಕಾಶಗಳ ಮತ್ತು ಭೂಮಿಯ ಸೃಷ್ಟಿಕರ್ತನಾದ ಅಲ್ಲಾಹನ ಬಗ್ಗೆ ನಿಮಗೆ ಸಂಶಯವೆ? ಅವನು ನಿಮ್ಮ ಅಪರಾಧಗಳನ್ನು ಕ್ಷಮಿಸಲಿಕ್ಕಾಗಿ ಮತ್ತು ನಿಮಗೆ ಒಂದು ನಿಶ್ಚಿತ ಅವಧಿಯ ತನಕ ಸಮಯಾವಕಾಶ ನೀಡಲಿಕ್ಕಾಗಿ ನಿಮ್ಮನ್ನು ಕರೆಯುತ್ತಿದ್ದಾನೆ. ಅವರು (ಜನರು) ಹೇಳಿದರು; ‘ನೀವು ನಮ್ಮ ಹಾಗಿರುವ ಮನುಷ್ಯರಲ್ಲದೆ ಇನ್ನೇನೂ ಅಲ್ಲ. ನಮ್ಮ ಪೂರ್ವಿಕರು ಆರಾಧಿಸುತ್ತಾ ಬಂದಿರುವವುಗಳಿಂದ ನೀವು ನಮ್ಮನ್ನು ತಡೆಯಲಿಚ್ಛಿಸುತ್ತೀರಿ. ಆದ್ದರಿಂದ ನಮಗೆ ಸುವ್ಯಕ್ತ ಆಧಾರ ಪ್ರಮಾಣವನ್ನು ತನ್ನಿರಿ’.');
INSERT INTO "kannada" VALUES(14,11,'14:11','ಅವರ ದೂತರು ಅವರೊಡನೆ ಹೇಳಿದರು; ‘ನಿಜಕ್ಕೂ ನಾವು ನಿಮ್ಮಂತಹ ಮನುಷ್ಯರಲ್ಲದೆ ಇನ್ನೇ ನೂ ಅಲ್ಲ . ಆದರೆ ಅಲ್ಲಾಹು ತನ್ನ ದಾಸರ ಪೈಕಿ ತಾನಿಚ್ಚಿಸಿದವರನ್ನು ಅನುಗ್ರಹಿಸುತ್ತಾನೆ. ಅಲ್ಲಾಹನ ಅನುಜ್ಞೆ ಯಿಂದಲೇ ಹೊರತು ನಿಮಗೆ ಯಾವುದೇ ಆಧಾರ ಪ್ರಮಾಣ ತರಲು ನಮ್ಮಿಂದಾಗದು . ಮತ್ತು ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಅಲ್ಲಾಹನ ಮೇಲೆಯೇ ಭರವಸೆ ಇಡಲಿ.');
INSERT INTO "kannada" VALUES(14,12,'14:12','ಅಲ್ಲಾಹು ನಮ್ಮನ್ನು ನಮ್ಮ ಮಾರ್ಗಗಳಲ್ಲಿ ಸೇರಿಸಿರುವಾಗ ನಾವು ಅವನ ಮೇಲೆ ಏಕೆ ಭರವಸೆ ಇಡಬಾರದು? ನೀವು ನಮಗೆ ನೀಡುತ್ತಿರುವ ಹಿಂಸೆಯನ್ನು ನಾವು ಖಂಡಿತ ಸಹಿಸಿಕೊಳ್ಳುವೆವು. ಭರವಸೆಯಿರಿಸುವವರು ಅಲ್ಲಾಹನ ಮೇಲೆಯೇ ಭರವಸೆ ಇರಿಸಿಕೊಳ್ಳಲಿ.');
INSERT INTO "kannada" VALUES(14,13,'14:13','ಸತ್ಯನಿಷೇಧಿಗಳು ತಮ್ಮ ಸಂದೇಶವಾಹಕರೊಡನೆ, ಹೇಳಿದರು; ‘ನಾವು ನಿಮ್ಮನ್ನು ನಮ್ಮ ನಾಡಿನಿಂದ ಹೊರಕ್ಕಟ್ಟಿ ಬಿಡುವೆವು ಇಲ್ಲವೇ ನೀವು ನಮ್ಮ ಮತಕ್ಕೆ ಮರಳಿ ಬರಬೇಕು. ‘ಆಗ ಅವರ ಪ್ರಭು ಅವರ ಮೇಲೆ ಸಂದೇಶ ನೀಡಿದನು; ‘ನಾವು ಈ ಅಕ್ರಮಿಗಳನ್ನು ಖಂಡಿತ ನಾಶಗೊಳಿಸುವೆವು.');
INSERT INTO "kannada" VALUES(14,14,'14:14','ಮತ್ತು ಆ ಬಳಿಕ ನಿಮ್ಮನ್ನು ಭೂಮಿಯಲ್ಲಿ ನೆಲೆ ಗೊಳಿಸುವೆವು. ಇದು ನನ್ನ ಸಾನಿಧ್ಯದ ಬಗ್ಗೆ ಭಯವಿರಿಸಿಕೊಂಡ ಹಾಗೂ ನನ್ನ ಎಚ್ಚರಿಕೆಗೆ ಹೆದರಿದವನಿಗೆ ಇರುವ ಅನುಗ್ರಹ ');
INSERT INTO "kannada" VALUES(14,15,'14:15','ಅವರು (ದೂತರು) (ಅಲ್ಲಾಹನಲ್ಲಿ) sಸಹಾಯ ಬೇಡಿದರು. ಪ್ರತಿಯೊಬ್ಬ ಅಹಂಕಾರಿ, ಹಠಮಾರಿಯೂ ಪರಾಜಿತನಾದನು.');
INSERT INTO "kannada" VALUES(14,16,'14:16','ಅವನ ಮುಂದೆ ನರಕಾಗ್ನಿ ಇದೆ. ಅಲ್ಲಿ ಅವನಿಗೆ ಕೀವು ರಕ್ತ ಮಿಶ್ರಿತ ದುರ್ಜಲವನ್ನು ಕುಡಿಸಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(14,17,'14:17','ಅದನ್ನು ಅವನು ನುಂಗಲೆತ್ನಿಸುವನು. ಅದನ್ನು ಗಂಟಲಿನಿಂದ ಇಳಿಸಲು ಪ್ರಯಾಸವಾಗುವುದು. ಮೃತ್ಯು ಎಲ್ಲ ಕಡೆಗಳಿಂದಲೂ ಅವನನ್ನು ಆವರಿಸಿರುವುದು. ಆದರೆ ಅವನು ಸಾಯಲಾರ. ಅದರ ಮುಂದೆ ಒಂದು ಘೋರ ಶಿಕ್ಷೆಯೂ ಇದೆ.');
INSERT INTO "kannada" VALUES(14,18,'14:18','ತಮ್ಮ ಪ್ರಭುವನ್ನು ನಿಷೇಧಿಸಿದವರ ಕರ್ಮಗಳ ಉದಾಹರಣೆಯು ಬೂದಿಯಂತೆ . ಒಂದು ಬಿರುಗಾಳಿಯ ದಿನದ ಚಂಡಮಾರುತವು ಅದನ್ನು ಹಾರಿಸಿತು. ಅವರು ತಮ್ಮ ಗಳಿಕೆಯಿಂದ ಯಾವ ಪ್ರತಿಫಲವನ್ನೂ ಪಡೆಯಲು ಶಕ್ತರಾಗರು. ಇದೇ (ನಿವಾರಣೆ) ವಿದೂರವಾದ ವಿನಾಶ.');
INSERT INTO "kannada" VALUES(14,19,'14:19','ಅಲ್ಲಾಹನು ಆಕಾಶಗಳನ್ನು ಮತ್ತು ಭೂಮಿ ಯನ್ನು ಸತ್ಯಪೂರ್ಣವಾಗಿ ಸೃಷ್ಟಿಸಿರುವುದನ್ನು ನೀವು ಕಾಣುವುದಿಲ್ಲವೇ? ಅವನು ಇಚ್ಛಿಸಿದರೆ ನಿಮ್ಮನ್ನು ಅಳಿಸಿ ಒಂದು ಹೊಸ ಸೃಷ್ಟಿಯನ್ನು (ನಿಮ್ಮ ಸ್ಥಾನದಲ್ಲಿ) ತರಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(14,20,'14:20','ಅಲ್ಲಾಹನ ಮೇಲೆ ಅದೇನೂ ಪ್ರಯಾಸವಲ್ಲ.');
INSERT INTO "kannada" VALUES(14,21,'14:21','ಅವರೆಲ್ಲರೂ ಒಟ್ಟಾಗಿ ಅಲ್ಲಾಹನ ಮುಂದೆ ಪ್ರತ್ಯಕ್ಷರಾಗುವಾಗ ಅಶಕ್ತರು (ಅನುಯಾಯಿಗಳು), ಅಹಂಭಾವಿಗಳಾಗಿದ್ದವರೊಡನೆ, (ಮುಖಂಡ ರೊಡನೆ) ‘ನಾವು ನಿಮ್ಮ ಅನುಯಾಯಿಗಳಾಗಿ ದ್ದೆವು. ಆದ್ದರಿಂದ ನೀವು ನಮ್ಮನ್ನು ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆಯಿಂದ ಸ್ವಲ್ಪವಾದರೂ ರಕ್ಷಿಸಬಲ್ಲಿರಾ?’ ಎಂದು ಕೇಳುವರು. ಅವರು (ಮುಖಂಡರು) ಹೇಳುವರು; ‘ಅಲ್ಲಾಹನು ನಮ್ಮನ್ನು ಮೋಕ್ಷದ ದಾರಿಗೆ ಸೇರಿಸುತ್ತಿದ್ದರೆ ನಾವು ನಿಮ್ಮನ್ನೂ ಮೋಕ್ಷದ ದಾರಿಗೆ ಕರೆಯುತ್ತಿದ್ದೆವು. ನಮ್ಮ ಮಟ್ಟಿಗೆ ನಾವು ಪ್ರಲಾಪಿಸಿದರೂ ಸಹಿಸಿಕೊಂಡರೂ ಒಂದೇ. ನಮಗೆ ರಕ್ಷಣೆಯ ಯಾವ ದಾರಿಯೂ ಇಲ್ಲ’.');
INSERT INTO "kannada" VALUES(14,22,'14:22','ಕಾರ್ಯವು ತೀರ್ಮಾನವಾದಾಗ ಶೈತಾನನು ಹೇಳುವನು; “ಖಂಡಿತ ಅಲ್ಲಾಹನು ನಿಮಗೆ ಸತ್ಯ ವಾದ ವಾಗ್ದಾನವನ್ನೇ ನೀಡಿದನು. ನಾನೂ ನಿಮಗೆ ವಾಗ್ದಾನವನ್ನು ಮಾಡಿದ್ದೆನು. ಆದರೆ ಅದನ್ನು (ನಾನು ನಿಮಗೆ ಮಾಡಿದ್ದ ವಾಗ್ದಾನ) ನಾನು ಉಲ್ಲಂಘಿಸಿದ್ದೇನೆ. ನಿಮ್ಮ ಮೇಲೆ ನನಗೆ ಅಧಿಕಾರವೇನೂ ಇರಲಿಲ್ಲ. ನಾನು ನಿಮ್ಮನ್ನು ಕರೆದೆನು. ಆಗ ನೀವು ನನ್ನ ಕರೆಗೆ ಓಗೊಟ್ಟಿರಿ ಅಷ್ಟೇ, ಆದ್ದರಿಂದ ನನ್ನನ್ನು ಹಳಿಯಬೇಡಿರಿ. ನಿಮ್ಮನ್ನೇ ನೀವು ಹಳಿದುಕೊಳ್ಳಿರಿ. ನಾನು ನಿಮಗೆ ನೆರವು ನೀಡಲಾರೆ. ನೀವೂ ನನಗೆ ನೆರವು ನೀಡಲಾರಿರಿ. ನೀವು ನನ್ನನ್ನು ದೇವತ್ವದಲ್ಲಿ ಭಾಗೀದಾರನನ್ನಾಗಿ ಮಾಡಿದ್ದನ್ನು ನಾನು ಈಗ ನಿಷೇಧಿಸಿದ್ದೇನೆ” . ಅಕ್ರಮಿಗಳಿಗೆ ಖಂಡಿತ ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆಯುಂಟು.');
INSERT INTO "kannada" VALUES(14,23,'14:23','ಸತ್ಯವಿಶ್ವಾಸ ಸ್ವೀಕರಿಸಿದ ಮತ್ತು ಸತ್ಕರ್ಮವೆಸಗಿದವರು ತಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆಗಳು ಹರಿಯುತ್ತಿರುವ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ ಪ್ರವೇಶ ಗೊಳಿಸಲ್ಪಡುವರು. ಅಲ್ಲಿ ಅವರು ತಮ್ಮ ಪ್ರಭುವಿನ ಅನುಮತಿ ಪ್ರಕಾರ ಸದಾ ವಾಸಿಸುತ್ತಿರುವರು. ಅಲ್ಲಿ ಅವರ ಶುಭಾಶಯವು ಸಲಾಮ್ ಆಗಿರುವುದು.');
INSERT INTO "kannada" VALUES(14,24,'14:24','ಅಲ್ಲಾಹನು ಪವಿತ್ರ ವಚನಕ್ಕೆ ಹೇಗೆ ಉಪಮೆ ಕೊಟ್ಟಿರುತ್ತಾನೆಂದು ನೀವು ನೋಡುತ್ತಿಲ್ಲವೇ? ಅದು ಒಂದು ಉತ್ತಮ ಜಾತಿಯ ಮರದಂತೆ, ಅದರ ಬುಡವು ಭದ್ರವಾಗಿದೆ. ಅದರ ಗೆಲ್ಲುಗಳು ಆಕಾಶಕ್ಕೆ ಏರಿವೆ.');
INSERT INTO "kannada" VALUES(14,25,'14:25','ಅದು ತನ್ನ ಪ್ರಭುವಿನ ಅನುಜ್ಞೆಯಂತೆ ಸದಾ ಕಾಲ ತನ್ನ ಫಲಗಳನ್ನು ಕೊಡುತ್ತಿದೆ. ಜನರು ಇವುಗಳಿಂದ ಪಾಠ ಕಲಿಯಲೆಂದು ಅಲ್ಲಾಹನು ಉದಾಹರಣೆಗಳನ್ನು ಕೊಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(14,26,'14:26','ಕೆಟ್ಟ ವಚನದ ಉಪಮೆ - ಒಂದು ಕೆಟ್ಟ ಜಾತಿಯ ಮರದಂತಿದೆ. ಅದು ಭೂತಳದಿಂದ ಕಿತ್ತೆ ಸೆಯಲ್ಪಡುತ್ತದೆ. ಅದಕ್ಕೆ ಯಾವುದೇ ಸ್ಥಿರತೆ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(14,27,'14:27','ಇಹಜೀವನದಲ್ಲೂ ಪರದಲ್ಲೂ ಸುಸ್ಥಿರ ವಚನದ ಮೂಲಕ ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನು ಅಲ್ಲಾಹು ಸ್ಥಿರ ಗೊಳಿಸುವನು . ಮತ್ತು ಅಕ್ರಮಿಗಳನ್ನು ಅಲ್ಲಾಹನು ದಾರಿಗೆಡಿಸುತ್ತಾನೆ. ಅಲ್ಲಾಹನು ತಾನಿಚ್ಛಿಸಿದ್ದನ್ನು ಮಾಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(14,28,'14:28','ಅಲ್ಲಾಹನ ಅನುಗ್ರಹವನ್ನು ಕೃತಘ್ನತೆಯನ್ನಾಗಿ ಬದಲಾಯಿಸಿ, ತಮ್ಮ ಜನಾಂಗವನ್ನು ವಿನಾಶದ ಗೃಹಕ್ಕೆ ತಳ್ಳಿಬಿಟ್ಟವರನ್ನು ನೀವು ಕಂಡಿರಾ?');
INSERT INTO "kannada" VALUES(14,29,'14:29','ಅರ್ಥಾತ್ ನರಕಾಗ್ನಿಗೆ. ಅದರಲ್ಲಿ ಅವರು ಕರಟುವರು. ಅದು ಅತ್ಯಂತ ನಿಕೃಷ್ಟ ತಾಣ.');
INSERT INTO "kannada" VALUES(14,30,'14:30','ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ ಜನರನ್ನು ತಪ್ಪಿಸಲಿಕ್ಕಾಗಿ ಅವರು ಅಲ್ಲಾಹನಿಗೆ ಕೆಲವು ಭಾಗೀದಾರ ರನ್ನು ಮಾಡಿಕೊಂಡರು. ಹೇಳಿರಿ; ನೀವು ಸುಖಪಟ್ಟುಕೊಳ್ಳಿರಿ. ನಿಜವಾಗಿಯೂ ನಿಮ್ಮ ನಿರ್ಗಮ ನವು ನರಕಕ್ಕೇ ಆಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(14,31,'14:31','(ಓ ಪೈಗಂಬರರೇ,) ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಸ್ವೀಕರಿಸಿದ ನನ್ನ ದಾಸರೊಡನೆ ಹೇಳಿರಿ; ಅವರು ನಮಾಝನ್ನು ಸಂಸ್ಥಾಪಿಸಲಿ. ಯಾವುದೇ ಕ್ರಯ ವಿಕ್ರ ಯವೂ ಗೆಳೆತನವೂ ಪ್ರಯೋಜನಕ್ಕೆ ಬಾರದ ಆ ದಿನ ಬರುವುದಕ್ಕೆ ಮುಂಚೆಯೇ ನಾವು ಅವರಿಗೆ ನೀಡಿದ ಸಂಪತ್ತಿನಿಂದ ರಹಸ್ಯವಾಗಿಯೂ ಬಹಿರಂಗವಾಗಿಯೂ ಖರ್ಚು ಮಾಡಲಿ.');
INSERT INTO "kannada" VALUES(14,32,'14:32','ಅಲ್ಲಾಹನೇ ಆಕಾಶಗಳನ್ನೂ ಭೂಮಿಯನ್ನೂ ಸೃಷ್ಟಿಸಿದವನು. ಆಕಾಶದಿಂದ ಮಳೆ ಸುರಿಸಿದ ವನು, ಅದರ ಮೂಲಕ ನಿಮಗೆ ಉಪಜೀವನಕ್ಕಾಗಿ ಫಲ ಮೂಲಗಳನ್ನು ಉತ್ಪಾದಿಸಿದವನು, ಅವನು ತನ್ನ ಆಜ್ಞೆ ಪ್ರಕಾರ ಸಮುದ್ರದಲ್ಲಿ ಸಂಚರಿಸುವಂತೆ ಹಡಗನ್ನು ನಿಮ್ಮ ನಿಯಂತ್ರಣಕ್ಕೆ ಕೊಟ್ಟನು ಮತ್ತು ನಿಮಗಾಗಿ ನದಿಗಳನ್ನೂ ನಿಯಂತ್ರಿಸಿದನು.');
INSERT INTO "kannada" VALUES(14,33,'14:33','ಅವನು ನಿರಂತರವಾಗಿ ಚಲಿಸುತ್ತಿರುವ ಸೂರ್ಯ ಚಂದ್ರರನ್ನು ನಿಮಗಾಗಿ ನಿಯಂತ್ರಿಸಿದನು ಮತ್ತು ರಾತ್ರೆ ಹಗಲುಗಳನ್ನು ನಿಮಗಾಗಿ ನಿಯಂತ್ರಿಸಿದನು.');
INSERT INTO "kannada" VALUES(14,34,'14:34','ಅವನು ನೀವು ಬೇಡಿದುದರಿಂದೆಲ್ಲ ನಿಮಗೆ ಕೊಟ್ಟನು, ನೀವು ಅಲ್ಲಾಹನ ಅನುಗ್ರಹಗಳನ್ನು ಎಣಿಕೆ ಮಾಡಲಿಚ್ಛಿಸಿದರೆ ಅದನ್ನು ನೀವು ಎಣಿಸಿ ಮುಗಿಸಲಾರಿರಿ. ವಾಸ್ತವದಲ್ಲಿ ಮಾನವನು ಮಹಾ ಅಕ್ರಮಿಯೂ ಕೃತಘ್ನನೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(14,35,'14:35','ಇಬ್‍ರಾಹೀಮರು ಪ್ರಾರ್ಥಿಸಿದ ಸಂದರ್ಭವನ್ನು ನೆನೆಯಿರಿ. ‘ಓ ನನ್ನ ಪ್ರಭೂ, ಈ ನಾಡನ್ನು ನಿರ್ಭೀತವನ್ನಾಗಿ ಮಾಡು ಮತ್ತು ನಾವು ವಿಗ್ರಹಾರಾಧನೆ ಮಾಡದಂತೆ ನನ್ನನ್ನೂ ನನ್ನ ಮಕ್ಕಳನ್ನೂ ರಕ್ಷಿಸು.');
INSERT INTO "kannada" VALUES(14,36,'14:36','ಓ ನನ್ನ ಪ್ರಭೂ, ಈ ವಿಗ್ರಹಗಳು ಅನೇಕರನ್ನು ದಾರಿಗೆಡಿಸಿವೆ. ಆದ್ದರಿಂದ ಯಾರು ನನ್ನನ್ನು ಅನು ಸರಿಸುತ್ತಾರೆ, ಅವನು ನನ್ನವನು. ಯಾರಾದರೂ ನನಗೆ ವಿರುದ್ಧವಾದರೆ ನಿಶ್ಚಯವಾಗಿಯೂ ನೀನು ಕ್ಷಮಾಶೀಲನೂ ಕೃಪಾನಿಧಿಯೂ ಆಗಿರುತ್ತಿ.');
INSERT INTO "kannada" VALUES(14,37,'14:37','ಓ ನಮ್ಮ ಪ್ರಭೂ, ನಾನು ನನ್ನ ಸಂತತಿಯಿಂದ (ಕೆಲವರನ್ನು) ಒಂದು ಬಂಜರು ಕಣಿವೆಯಲ್ಲಿ ನಿನ್ನ ಪವಿತ್ರ ಗೇಹದ (ಕಅïಬಾದ) ಬಳಿ ತಂದು ನೆಲೆಗೊಳಿಸಿದ್ದೇನೆ. ಓ ನಮ್ಮ ಪ್ರಭೂ, ಇವರು ನಮಾಝನ್ನು ಸಂಸ್ಥಾಪಿಸಲೆಂದು (ಹೀಗೆ ಮಾಡಿ ದ್ದೇನೆ.) ಆದುದರಿಂದ ನೀನು ಜನರಲ್ಲಿ ಕೆಲವರ ಮನಸ್ಸುಗಳನ್ನು ಇವರ ಕಡೆಗೆ ಒಲಿಯುವಂತೆ ಮಾಡು ಮತ್ತು ಇವರಿಗೆ ಹಣ್ಣು, ಫಲಗಳನ್ನು ನೀಡು. ಇವರು ಕೃತಜ್ಞರಾಗಲೂಬಹುದು');
INSERT INTO "kannada" VALUES(14,38,'14:38','ಓ ನಮ್ಮ ಪ್ರಭೂ, ನಾವು ಅಡಗಿಸುವುದನ್ನೂ ಬಹಿರಂಗಪಡಿಸುವುದನ್ನೂ ನೀನು ಬಲ್ಲವನಾಗಿರುತ್ತಿ. ಭೂಮಿಯಲ್ಲಾಗಲಿ ಆಕಾಶದಲ್ಲಾಗಲಿ ಯಾವುದೇ ವಸ್ತು ಅಲ್ಲಾಹನಿಂದ ಅಡಗಿರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(14,39,'14:39','ವೃದ್ಧಾಪ್ಯದಲ್ಲಿ ನನಗೆ ಇಸ್ಮಾಈಲ್ ಮತ್ತು ಇಸ್‍ಹಾಖರನ್ನು ದಯಪಾಲಿಸಿದ ಅಲ್ಲಾಹನಿಗೆ ಸರ್ವಸ್ತುತಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ನನ್ನ ಪ್ರಭು ಪ್ರಾರ್ಥನೆಯನ್ನು ಚೆನ್ನಾಗಿ ಆಲಿಸುವವನು.');
INSERT INTO "kannada" VALUES(14,40,'14:40','ಓ ನನ್ನ ಪ್ರಭೂ, ನೀನು ನನ್ನನ್ನು ನಮಾಝ್ ಸಂಸ್ಥಾಪಿಸುವವನನ್ನಾಗಿ ಮಾಡು. ನನ್ನ ಸಂತತಿಯಿಂದಲೂ (ಹಾಗೆ ಮಾಡು). ಓ ನಮ್ಮ ಪ್ರಭೂ, ನನ್ನ ಪ್ರಾರ್ಥನೆಯನ್ನು ಸ್ವೀಕರಿಸು.');
INSERT INTO "kannada" VALUES(14,41,'14:41','ಓ ನಮ್ಮ ಪ್ರಭೂ, ವಿಚಾರಣೆ ನಡೆಯುವ ದಿನ ನನ್ನನ್ನೂ ನನ್ನ ಮಾತಾಪಿತರನ್ನೂ ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳನ್ನೂ ಕ್ಷಮಿಸಿ ಬಿಡು’ .');
INSERT INTO "kannada" VALUES(14,42,'14:42','ಅಕ್ರಮಿಗಳು ಮಾಡುತ್ತಿರುವುದರ ಬಗ್ಗೆ ಅಲ್ಲಾಹು ಅಶ್ರದ್ಧನಾಗಿರುವನೆಂದು ನೀವು ಭಾವಿಸಬೇಡಿರಿ. ಬಿಡುಗಣ್ಣುಗಳ ದಿನಕ್ಕಾಗಿ ಅವರಿಗೆ ಅಲ್ಲಾಹನು ಕಾಲಾವಕಾಶ ಕೊಡುತ್ತಿದ್ದಾನೆ ಅಷ್ಟೆ.');
INSERT INTO "kannada" VALUES(14,43,'14:43','(ಅಂದು) ಅವರು ತಲೆಯನ್ನು ಮೇಲೆತ್ತಿಕೊಂಡು ಧಾವಿಸುತ್ತಿರುವರು. ಅವರ ದೃಷ್ಟಿಗಳು ಅವರ ಕಡೆಗೆ ಮರಳಿ ಬರಲೊಲ್ಲದು. ಅವರ ಮನಸ್ಸುಗಳು ಶೂನ್ಯವಾಗಿರುವವು.');
INSERT INTO "kannada" VALUES(14,44,'14:44','(ಓ ಪೈಗಂಬರರೇ,) ಶಿಕ್ಷೆಯು ಬಂದೆರಗುವ ಆ ದಿವಸದ ಬಗ್ಗೆ ನೀವು ಜನರಿಗೆ ಎಚ್ಚರಿಕೆ ಕೊಡಿರಿ. ಆಗ ಅಕ್ರಮಿಗಳು, ‘ಓ ನಮ್ಮ ಪ್ರಭೂ, ನಮಗೆ ಇನ್ನೂ ಕೊಂಚ ಕಾಲಾವಕಾಶವನ್ನು ಕೊಟ್ಟು ಬಿಡು. ಹತ್ತಿರದ ಒಂದು ಅವಧಿಯವರೆಗೆ ನಮಗೆ ನೀನು ಸಮಯವನ್ನು ಮುಂದೂಡು. ನಾವು ನಿನ್ನ ಕರೆಗೆ ಓಗೊಡುವೆವು ಮತ್ತು ಸಂದೇಶವಾ ಹಕರನ್ನು ಅನುಸರಿಸುವೆವು’ ಎನ್ನುವರು. ನಿಮಗೆ ಯಾವುದೇ ಸ್ಥಳಾಂತರವಿಲ್ಲವೆಂದು ಇದಕ್ಕೆ ಮುಂಚೆ ನೀವು ಆಣೆ ಹಾಕುತ್ತ ಹೇಳುತ್ತಿದ್ದಿರಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(14,45,'14:45','ವಸ್ತುತಃ ತಮ್ಮ ಮೇಲೆ ತಾವೇ ಅಕ್ರಮವೆಸಗಿದ್ದ ಆ ಜನಾಂಗಗಳ ನಿವಾಸಗಳಲ್ಲಿ ನೀವು ವಾಸಿಸಿದ್ದಿರಿ. ನಾವು ಅವರೊಂದಿಗೆ ಹೇಗೆ ವರ್ತಿಸಿದ್ದೇವೆಂಬುದು ನಿಮಗೆ ಸ್ಪಷ್ಟವಾಗಿದೆ ಕೂಡಾ. ನಾವು ನಿಮಗೆ ಉದಾಹರಣೆಗಳನ್ನು ವಿವರಿಸಿ ಕೊಟ್ಟಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(14,46,'14:46','ಅವರು ತಮ್ಮ ಎಲ್ಲಾ ಕುತಂತ್ರಗಳನ್ನು ಹೆಣೆದರು. ಅವರ ಪ್ರತಿಯೊಂದು ಕುತಂತ್ರದ ಪ್ರತಿತಂತ್ರ ಅಲ್ಲಾಹನ ಬಳಿಯಲ್ಲಿತ್ತು. ಅವರ ಕುತಂತ್ರದಿಂದ ಪರ್ವತಗಳು ಸರಿಯಲಾರದು.');
INSERT INTO "kannada" VALUES(14,47,'14:47','ಆದುದರಿಂದ (ಓ ಪೈಗಂಬರರೇ) ಅಲ್ಲಾಹನು, ತನ್ನ ಸಂದೇಶವಾಹಕರೊಡನೆ ಮಾಡಿದ ವಾಗ್ದಾನಗಳನ್ನು ಉಲ್ಲಂಘಿಸುವನೆಂದು ಭಾವಿಸಬೇಡಿರಿ, ವಾಸ್ತವದಲ್ಲಿ ಅಲ್ಲಾಹನು ಮಹಾಪ್ರತಾಪಿಯೂ ಪ್ರತೀಕಾರ ಪಡೆಯುವವನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(14,48,'14:48','ಭೂಮಿ ಈ ಭೂಮಿಯಲ್ಲದ ಇನ್ನೊಂದಾಗಿಯೂ ಮತ್ತು ಆಕಾಶಗಳೂ ಬದಲಾಯಿಸಲ್ಪಡುವ ಹಾಗೂ ಏಕನೂ ಪ್ರಚಂಡನೂ ಆದ ಅಲ್ಲಾಹನ ಮುಂದೆ ಸರ್ವರೂ ಪ್ರತ್ಯಕ್ಷ ಹಾಜರಾಗಲಿರುವ ದಿನ .');
INSERT INTO "kannada" VALUES(14,49,'14:49','ಅಂದು ಅಪರಾಧಿಗಳು ಸಂಕೋಲೆಗಳಿಂದ ಬಿಗಿಯಲ್ಪಟ್ಟಿರುವುದನ್ನು ನೀವು ನೋಡುವಿರಿ .');
INSERT INTO "kannada" VALUES(14,50,'14:50','ಅವರ ಉಡುಪು ಲಾಕ್ಷದಿಂದಾಗಿರುವುದು. ಅವರ ಮುಖಗಳ ಮೇಲೆ ಬೆಂಕಿ ಆವರಿಸುತ್ತಿರುವುದು.');
INSERT INTO "kannada" VALUES(14,51,'14:51','ಪ್ರತಿಯೊಂದು ಜೀವಿಗೂ ಅದರ ಕರ್ಮಫಲವನ್ನು ಅಲ್ಲಾಹು ಕೊಡಲಿಕ್ಕಾಗಿ. (ಅವರು ಹಾಜರಾಗ ಲಿರುವರು). ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹನು ವಿಚಾರಣೆ ನಡೆಸುವುದರಲ್ಲಿ ಅತಿ ಶೀಘ್ರನು.');
INSERT INTO "kannada" VALUES(14,52,'14:52','ಇದು ಜನರಿಗಾಗಿ ಒಂದು ಸ್ಪಷ್ಟ ಸಂದೇಶ. ಇದರ ಮೂಲಕ ಅವರು ಎಚ್ಚರಿಸಲ್ಪಡಲು. ಅಲ್ಲಾಹನು ಏಕನಾದ ದೇವನು ಎಂಬುದನ್ನು ಅವರು ತಿಳಿದುಕೊಳ್ಳಲಿಕ್ಕಾಗಿಯೂ ಬುದ್ದಿವಂತರು ಯೋಚಿಸಿ ಅರ್ಥಮಾಡಿಕೊಳ್ಳಲೆಂದೂ (ಇದು ಕಳುಹಿಸಲ್ಪಟ್ಟಿರುತ್ತದೆ) .');
INSERT INTO "kannada" VALUES(15,1,'15:1','ಅಲಿಫ್ ಲಾಮ್ ರಾ, ದೇವಗ್ರಂಥ ಹಾಗೂ ಸುವ್ಯಕ್ತ ಖುರ್‍ಆನಿನ ಸೂಕ್ತಗಳಿವು.');
INSERT INTO "kannada" VALUES(15,2,'15:2','‘ನಾವೂ ಮುಸ್ಲಿಮರಾಗಿದ್ದರೆ ಎಷ್ಟು ಚೆನ್ನಾಗಿತ್ತು’ ಎಂದು ಸತ್ಯನಿಷೇಧಿಗಳು ತೀವ್ರ ಆಶೆಪಡುವರು.');
INSERT INTO "kannada" VALUES(15,3,'15:3','ತಾವು ಅವರನ್ನು ಬಿಟ್ಟುಬಿಡಿರಿ. ಅವರು ತಿನ್ನಲಿ, ಸುಖಪಡಲಿ, ಹುಸಿ ಭರವಸೆಯಲ್ಲಿ ಮೈಮರೆಯಲಿ. ನಂತರ ಅವರು ತಿಳಿದುಕೊಳ್ಳುವರು.');
INSERT INTO "kannada" VALUES(15,4,'15:4','ಒಂದು ನಿರ್ದಿಷ್ಟ ಅವಧಿಯನ್ನು ಲಿಖಿತಗೊಳಿಸದೆ ಯಾವುದೇ ನಾಡನ್ನು ನಾವು ನಾಶಗೊಳಿಸಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(15,5,'15:5','ಯಾವ ಜನಾಂಗವೂ ತನಗೆ ನಿಶ್ಚಯಿಸಲ್ಪಟ್ಟ ಕಾಲಕ್ಕಿಂತ ಮುಂದೆ ಹೋಗದು. (ಕಾಲಕ್ಕಿಂತ) ಅವರು ಹಿಂದೆ ಹೋಗರು.');
INSERT INTO "kannada" VALUES(15,6,'15:6','ಅವರು (ಅವಿಶ್ವಾಸಿಗಳು) ಹೇಳುತ್ತಾರೆ, ಓ, ಉದ್ಭೋಧೆ ಅವತೀರ್ಣಗೊಂಡವನೇ! ಖಂಡಿತ ನೀನೊಬ್ಬ ಹುಚ್ಚನಾಗಿರುವಿ.');
INSERT INTO "kannada" VALUES(15,7,'15:7','ನೀನು ಸತ್ಯವಾದಿಯಾಗಿದ್ದರೆ ನಮ್ಮ ಬಳಿ ದೇವಚ ರರನ್ನೇಕೆ ತರುವುದಿಲ್ಲ ?');
INSERT INTO "kannada" VALUES(15,8,'15:8','ನ್ಯಾಯವಾದ ಕಾರಣವಿಲ್ಲದೆ ನಾವು ದೇವಚರರನ್ನು ಇಳಿಸುವುದಿಲ್ಲ. ಮತ್ತು ಆಗ ಅವರಿಗೆ (ಸತ್ಯ ನಿಷೇಧಿಗಳಿಗೆ) ಕಾಲಾವಕಾಶ ನೀಡಲಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(15,9,'15:9','ನಿಶ್ಚಯವಾಗಿಯೂ ನಾವು ಈ ಉದ್ಭೋ ದನೆಯನ್ನು ಅವತೀರ್ಣಗೊಳಿಸಿರುತ್ತೇವೆ. ಸ್ವತಃ ನಾವೇ ಅದರ ರಕ್ಷಕರೂ ಆಗಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(15,10,'15:10','(ಓ ಪೈಗಂಬರರೇ), ನಾವು ನಿಮಗಿಂತ ಮುಂಚೆ ಪೂರ್ವಿಕರ ಅನೇಕ ಪಂಗಡಗಳಲ್ಲಿ ಸಂದೇಶವಾ ಹಕರನ್ನು ಕಳುಹಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(15,11,'15:11','ಯಾವುದೇ ಸಂದೇಶವಾಹಕ ಅವರ ಬಳಿ ಬಂದಾಗ ಎಂದೂ ಪರಿಹಾಸ್ಯ ಮಾಡದೆ ಬಿಡಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(15,12,'15:12','ಅದೇ ರೀತಿಯಲ್ಲಿ ಅಪರಾಧಿಗಳ ಹೃದಯದೊಳಗೆ ಅದನ್ನು (ಪರಿಹಾಸ್ಯವನ್ನು) ನಾವು ಹಾಯಿಸುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(15,13,'15:13','ಅವರು ಪ್ರವಾದಿಯವರಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸುವುದಿಲ್ಲ. ಪೂರ್ವಿಕರಲ್ಲಿ (ಅಲ್ಲಾಹನ) ಕ್ರಮ ಗತಿಸಿ ಹೋಗಿದೆ.');
INSERT INTO "kannada" VALUES(15,14,'15:14','ನಾವು ಅವರಿಗೆ ಆಕಾಶದಿಂದ ದ್ವಾರವೊಂದನ್ನು ತೆರೆದು, ಅವರು ಅದರ ಮೂಲಕ ಏರಿ ಹೋಗುತ್ತಿದ್ದರೆ,');
INSERT INTO "kannada" VALUES(15,15,'15:15','“ನಮ್ಮ ಕಣ್ಣುಗಳು ಮತ್ತೇರಿವೆ ಅಷ್ಟೆ. ಅಲ್ಲದೆ ನಾವು ಮಾಟಕ್ಕೊಳಗಾಗಿದ್ದೇವೆ” ಎಂದೇ ಅವರು ಹೇಳುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(15,16,'15:16','ಆಕಾಶದಲ್ಲಿ ನಾವು ಅನೇಕ ಭ್ರಮಣ ಪಥಗಳನ್ನು ಮಾಡಿರುತ್ತೇವೆ . ಅವುಗಳನ್ನು ನೋಡುವವರಿಗಾಗಿ ಅಲಂಕರಿಸಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(15,17,'15:17','ಮತ್ತು ಬಹಿಷ್ಕøತರಾದ ಎಲ್ಲ ಶೈತಾನರಿಂದ ಅವುಗಳನ್ನು ಕಾಪಾಡಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(15,18,'15:18','ಒಂದಿಷ್ಟು ಕದ್ದು ಕೇಳುವವನ ಹೊರತು, ಅವನು ಕದ್ದಾಲಿಸಲು ಪ್ರಯತ್ನಿಸುವಾಗ ಪ್ರತ್ಯಕ್ಷವಾದ ಒಂದು ಅಗ್ನಿಜ್ವಾಲೆಯು ಅವನನ್ನು ಬೆನ್ನಟ್ಟುತ್ತದೆ .');
INSERT INTO "kannada" VALUES(15,19,'15:19','ನಾವು ಭೂಮಿಯನ್ನು ಹರಡಿದೆವು . ಅದರಲ್ಲಿ ಊರಿನಿಂತ ಪರ್ವತಗಳನ್ನು ನಾಟಿದೆವು. ಸರಿ ಯಾಗಿ ನಿಶ್ಚಿತ ಪ್ರಮಾಣದದಲ್ಲಿ ಬೆಳೆಸಿದೆವು.');
INSERT INTO "kannada" VALUES(15,20,'15:20','ಮತ್ತು ಅದರಲ್ಲಿ ನಿಮಗಾಗಿಯೂ ನೀವು ಅನ್ನ ನೀಡದಂತಹ ಅನೇಕ ಸೃಷ್ಟಿಗಳಿಗೂ ಜೀವನ ಸಾಧನಗಳನ್ನು ಒದಗಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(15,21,'15:21','ಯಾವುದೇ ವಸ್ತುವಿನ ಭಂಡಾರ ನಮ್ಮ ಬಳಿ ಇಲ್ಲದಿಲ್ಲ. ಅದನ್ನು ನಾವು ಒಂದು ನಿಗದಿತ ಪ್ರಮಾಣದಲ್ಲಿ ಮಾತ್ರ ಇಳಿಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(15,22,'15:22','ಜಲಾಂಶವನ್ನು ಹೊತ್ತ ಮಾರುತಗಳನ್ನು ನಾವೇ ಕಳುಹಿಸುತ್ತೇವೆ . ಹಾಗೆ ಆಕಾಶದಿಂದ ನೀರನ್ನು ಸುರಿಸುತ್ತೇವೆ. ಮತ್ತು ಅದನ್ನು ನಿಮಗೆ ಕುಡಿಸುತ್ತೇವೆ. ನೀವೇನೂ ಅದರ ಸಂಗ್ರಾಹಕರಾಗಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(15,23,'15:23','ನಿಜವಾಗಿಯೂ ನಾವು ಬದುಕಿಸುತ್ತೇವೆ. ಮೃತ ಗೊಳಿಸುತ್ತೇವೆ. ನಾವೇ ಎಲ್ಲರ ವಾರೀಸುದಾರರು.');
INSERT INTO "kannada" VALUES(15,24,'15:24','ನಿಮ್ಮ ಪೈಕಿ ಈ ಮುಂಚೆ ಗತಿಸಿ ಹೋದವರನ್ನೂ ನಾವು ಬಲ್ಲೆವು. ಮುಂದೆ ಬರಲಿರುವವರನ್ನೂ ನಾವು ಬಲ್ಲೆವು .');
INSERT INTO "kannada" VALUES(15,25,'15:25','ನಿಶ್ಚಯವಾಗಿಯೂ ನಿಮ್ಮ ಪ್ರಭು ಅವರೆಲ್ಲರನ್ನೂ ಒಟ್ಟುಗೂಡಿಸುವನು. ಅವನು ಮಹಾಯುಕ್ತಿವಂತನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(15,26,'15:26','ನಾವು ಮಾನವನನ್ನು ಹಳಸಿದ ಕಪ್ಪು ಒಣಗಲು ಕೊಜೆ ಮಣ್ಣಿನಿಂದ ಸೃಷ್ಟಿಸಿದೆವು .');
INSERT INTO "kannada" VALUES(15,27,'15:27','ಅದಕ್ಕಿಂತ ಮುಂಚೆ `ಜಿನ್ನ್’ಅನ್ನು ಧೂಮರಹಿತ ಅಗ್ನಿಯ ಜ್ವಾಲೆಯಿಂದ ಸೃಷ್ಟಿಸಿದೆವು .');
INSERT INTO "kannada" VALUES(15,28,'15:28','ನಿಮ್ಮ ಪ್ರಭು ದೇವಚರರೊಡನೆ, ಹೀಗೆ ಹೇಳಿದ ಸಂದರ್ಭ; ‘ನಾನು ಹಳಸಿದ ಕಪ್ಪು ಒಣಗಲು ಕೊಜೆ ಮಣ್ಣಿನಿಂದ ಒಬ್ಬ ಮಾನವನನ್ನು ಸೃಷ್ಟಿ ಸುವವನಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(15,29,'15:29','ನಾನು ಅವನನ್ನು ಸಂಪೂರ್ಣಗೊಳಿಸಿ ಅವನೊಳಗೆ ನನ್ನ ಆತ್ಮದಿಂದ ಊದಿದರೆ ನೀವೆಲ್ಲರೂ ಅವನಿಗೆ ಸಾಷ್ಟಾಂಗವೆರಗಿರಿ’.');
INSERT INTO "kannada" VALUES(15,30,'15:30','ಆ ಪ್ರಕಾರ ಎಲ್ಲ ದೇವಚರರೂ ಸಾಷ್ಟಾಂಗವೆರಗಿದರು.');
INSERT INTO "kannada" VALUES(15,31,'15:31','ಇಬ್‍ಲೀಸನ ಹೊರತು. ಸಾಷ್ಟಾಂಗವೆರಗಿದವರ ಜೊತೆ ಸೇರಲು ಅವನು ನಿರಾಕರಿಸಿದನು.');
INSERT INTO "kannada" VALUES(15,32,'15:32','ಅಲ್ಲಾಹು ಕೇಳಿದನು, ಓ ಇಬ್‍ಲೀಸ್, ನೀನು ಸಾಷ್ಟಾಂಗವೆರಗಿದವರೊಂದಿಗೆ ಸೇರದಿರಲು ಕಾರಣವೇನು?');
INSERT INTO "kannada" VALUES(15,33,'15:33','ಅವನು ಹೇಳಿದನು; ‘ನೀನು ಹಳಸಿದ ಕಪ್ಪು ಒಣಗಲು ಕೊಜೆ ಮಣ್ಣಿನಿಂದ ಸೃಷ್ಟಿಸಿದ ಈ ಮಾನವನಿಗೆ ನಾನು ಸಾಷ್ಟಾಂಗವೆರಗಲಾರೆ’.');
INSERT INTO "kannada" VALUES(15,34,'15:34','ಅಲ್ಲಾಹು ಹೇಳಿದನು; ‘ಇಲ್ಲಿಂದ ಹೊರಗೆ ನಡೆ, ನಿಜವಾಗಿಯೂ ನೀನು ಬಹಿಷ್ಕøತನು.');
INSERT INTO "kannada" VALUES(15,35,'15:35','ನಿರ್ಣಾಯಕ ದಿನದವರೆಗೂ ನಿನ್ನ ಮೇಲೆ ನನ್ನ ಶಾಪವಿದೆ’.');
INSERT INTO "kannada" VALUES(15,36,'15:36','ಅವನು, ಹೇಳಿದನು; ‘ಓ ನನ್ನ ಪ್ರಭೂ, ಹಾಗಿದ್ದರೆ ಅವರು ಎಬ್ಬಿಸಲ್ಪಡುವ ದಿನದವರೆಗೆ ನನಗೆ ಕಾಲಾವಕಾಶ ನೀಡು’.');
INSERT INTO "kannada" VALUES(15,37,'15:37','ಆಗ, ಅಲ್ಲಾಹನು ಹೇಳಿದನು ‘ನೀನು ಕಾಲಾವ ಕಾಶವನ್ನು ನೀಡಲಾಗಿರುವವರ ಸಾಲಿಗೆ ಸೇರಿರುವೆ.');
INSERT INTO "kannada" VALUES(15,38,'15:38','ಆ ನಿಶ್ಚಿತ ಸಮಯದವರೆಗೆ’.');
INSERT INTO "kannada" VALUES(15,39,'15:39','ಅವನೆಂದನು, ‘ಓ ನನ್ನ ಪ್ರಭೂ, ನೀನು ನನ್ನನ್ನು ದಾರಿಗೆಡಿಸಿದಂತೆಯೇ ಭೂಮಿಯಲ್ಲಿ ಅವರಿಗೆ ನಾನು (ದುಷ್ಪøವರ್ತಿಗಳನ್ನು) ಅಲಂಕೃತವಾಗಿ ತೋರಿಸಿಕೊಡುವೆನು ಮತ್ತು ಅವರೆಲ್ಲರನ್ನೂ ನಾನು ದಾರಿಗೆಡಿಸಿಬಿಡುವೆನು ಖಂಡಿತ.');
INSERT INTO "kannada" VALUES(15,40,'15:40','ಅವರ ಪೈಕಿ ನಿನ್ನ ನಿಷ್ಕಂಳಕರಾದ ದಾಸರನ್ನು ಹೊರತು’ .');
INSERT INTO "kannada" VALUES(15,41,'15:41','ಅವನು (ಅಲ್ಲಾಹು) ಹೇಳಿದನು; ‘ನೇರವಾಗಿ ನನ್ನನ್ನು ತಲಪುವ ಮಾರ್ಗವಿದು .');
INSERT INTO "kannada" VALUES(15,42,'15:42','ನಿಶ್ಚಯವಾಗಿಯೂ ನನ್ನ (ನಿಷ್ಟಾವಂತ) ದಾಸರ ಮೇಲೆ ನಿನಗೆ ಅಧಿಕಾರವಿಲ್ಲ. ನಿನ್ನನ್ನು ಅನುಸರಿಸುವ ಪಥಭ್ರಷ್ಟರ ಮೇಲೆಯೇ ಹೊರತು.');
INSERT INTO "kannada" VALUES(15,43,'15:43','ನಿಶ್ಚಯವಾಗಿಯೂ ನರಕವು ಅವರೆಲ್ಲರಿಗೂ ನಿಶ್ಚಯಿಸಲಾದ ಸ್ಥಾನವಾಗಿದೆ’.');
INSERT INTO "kannada" VALUES(15,44,'15:44','ಅದಕ್ಕೆ (ನರಕಕ್ಕೆ) ಏಳು ದ್ವಾರಗಳಿವೆ. ಪ್ರತಿಯೊಂದು ದ್ವಾರಕ್ಕೆ ನಿಗಧಿಪಡಿಸಲಾದ ಒಂದೊಂದು ವಿಭಾಗವಿದೆ .');
INSERT INTO "kannada" VALUES(15,45,'15:45','ಧರ್ಮನಿಷ್ಟರು ಉದ್ಯಾನಗಳಲ್ಲಿಯೂ ಚಿಲುಮೆ ಗಳಲ್ಲಿಯೂ ಇರುವರು.');
INSERT INTO "kannada" VALUES(15,46,'15:46','ನಿರ್ಭಯರಾಗಿ ಶಾಂತಿಯಿಂದ ಅದರಲ್ಲಿ ಪ್ರವೇಶಿಸಿರಿ (ಎಂದು ಅವರಿಗೆ ಹೇಳಲಾಗುವುದು.)');
INSERT INTO "kannada" VALUES(15,47,'15:47','ಅವರ ಹೃದಯಗಳಿಂದ ವಿದ್ವೇಷವನ್ನು ನಾವು ತೆಗೆದುಬಿಡುವೆವು. ಅವರು ಪರಸ್ಪರ ಸಹೋದ ರರಾಗಿ ಅಭಿಮುಖರಾಗಿ ಆಸನಗಳಲ್ಲಿ ಕುಳಿತು ಕೊಳ್ಳುವರು.');
INSERT INTO "kannada" VALUES(15,48,'15:48','ಅವರಿಗೆ ಅಲ್ಲಿ ಯಾವುದೇ ಕ್ಲೇಶ ತಟ್ಟದು. ಮತ್ತು ಅವರು ಅಲ್ಲಿಂದ ಹೊರಹಾಕಲ್ಪಡುವುದೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(15,49,'15:49','(ಓ ಪೈಗಂಬರರೇ,) ನಾನು ಮಹಾ ಕ್ಷಮಾಶೀಲನೂ ಕೃಪಾನಿಧಿಯೂ ಆಗಿರುತ್ತೇನೆಂದು ನನ್ನ ದಾಸರಿಗೆ ತಿಳಿಸಿಬಿಡಿರಿ.');
INSERT INTO "kannada" VALUES(15,50,'15:50','ನನ್ನ ಶಿಕ್ಷೆಯೂ ಅತ್ಯಂತ ವೇದನಾಯುಕ್ತ ವಾಗಿದೆ. (ಎಂದೂ ತಿಳಿಸಿಬಿಡಿರಿ).');
INSERT INTO "kannada" VALUES(15,51,'15:51','ಅವರಿಗೆ ಇಬ್‍ರಾಹೀಮರ ಅತಿಥಿಗಳ ವೃತ್ತಾಂತವನ್ನೂ ತಿಳಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(15,52,'15:52','ಅವರು ಇವರ ಬಳಿ ಬಂದು, ‘ಸಲಾಮ್ (ನಿಮಗೆ ಶಾಂತಿ ಇರಲಿ)’ ಎಂದರು. ಆಗ ಇವರು, ‘ನಾವು ನಿಮ್ಮ ಬಗ್ಗೆ ಹೆದರಿದ್ದೇವೆ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(15,53,'15:53','ಆಗ ಅವರು, ಹೆದರಬೇಡಿರಿ, ನಾವು ನಿಮಗೆ ಓರ್ವ ಜ್ಞಾನಿಯಾದ ಪುತ್ರನ ಸುವಾರ್ತೆ ನೀಡುತ್ತೇವೆ’. ಎಂದರು.');
INSERT INTO "kannada" VALUES(15,54,'15:54','‘ನನಗೆ ವೃದ್ಧಾಪ್ಯ ತಟ್ಟಿದ ಮೇಲೆ ನೀವು ನನಗೆ ಸುವಾರ್ತೆ ನೀಡುತ್ತೀರಾ? ನೀವು ಯಾವುದರ ಬಗ್ಗೆ ಸುವಾರ್ತೆ ನೀಡುತ್ತಿರುವಿರಿ’ ಎಂದು ಇಬ್ರಾಹೀಮರು ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(15,55,'15:55','ಆಗ ಅವರು, ಹೇಳಿದರು; ‘ನಾವು ನಿಮಗೆ ಸತ್ಯವಾದ ಸುವಾರ್ತೆಯನ್ನೇ ನೀಡುತ್ತಿದ್ದೇವೆ. ನೀವು ನಿರಾಶರಲ್ಲಿ ಸೇರಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(15,56,'15:56','‘ತಮ್ಮ ಪ್ರಭುವಿನ ಕಾರುಣ್ಯದ ಬಗ್ಗೆ ಪಥ ಭ್ರಷ್ಟರಲ್ಲದೆ ಯಾರು ನಿರಾಶರಾಗುತ್ತಾರೆ’ ಎಂದು ಇಬ್ರಾಹೀಮರು ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(15,57,'15:57','‘ಓ ದೇವದೂತರೇ, ನಿಮ್ಮ (ಮುಖ್ಯ) ವಿಷಯವೇನು?’ ಎಂದು ಕೇಳಿದರು.');
INSERT INTO "kannada" VALUES(15,58,'15:58','ಅವರು, ಹೇಳಿದರು; ‘ನಾವು ಒಂದು ಅಪರಾಧಿ ಜನಾಂಗದ ಕಡೆಗೆ ಕಳುಹಿಸಲ್ಪಟ್ಟಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(15,59,'15:59','ಆದರೆ `ಲೂಥ’ರ ಕುಟುಂಬದವರು ಇದಕ್ಕೆ ಹೊರ ತಾಗಿದ್ದಾರೆ. ಅವರೆಲ್ಲರನ್ನೂ ನಾವು ರಕ್ಷಿಸುವೆವು.');
INSERT INTO "kannada" VALUES(15,60,'15:60','`ಲೂಥ’ರ ಪತ್ನಿಯ ಹೊರತು; ಆಕೆ ನಾಶಕ್ಕೀಡಾಗುವವರಲ್ಲಿ ಸೇರಿದವಳೆಂದು ನಾವು ಲೆಕ್ಕ ಹಾಕಿದ್ದೇವೆ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(15,61,'15:61','ಅನಂತರ ಆ ದೂತರು ಲೂಥರ ಕುಟುಂಬದಲ್ಲಿಗೆ ತಲಪಿದಾಗ');
INSERT INTO "kannada" VALUES(15,62,'15:62','‘ನೀವು ನನಗೆ ಅಪರಿಚಿತರಾಗಿ ತೋರುತ್ತೀರಿ’ ಎಂದು ಅವರು ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(15,63,'15:63','ಆಗ ಅವರು, (ದೂತರು) ಹೇಳಿದರು; ‘ಅಲ್ಲ, ಯಾವುದರ ಬಗ್ಗೆ ಇವರು (ಜನರು) ಸಂದೇಹ ಪಡುತ್ತಿದ್ದರೋ ಅದನ್ನು ವಹಿಸಿಕೊಂಡು ನಾವು ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(15,64,'15:64','ನಾವು ಸತ್ಯಸಹಿತ ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದಿರುತ್ತೇವೆ ಮತ್ತು ನಾವು ಸತ್ಯವನ್ನೇ ಹೇಳುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(15,65,'15:65','ಆದುದರಿಂದ ನೀವು ರಾತ್ರಿಯಿಂದ ಸ್ವಲ್ಪ ಉಳಿದಿರುವಾಗಲೇ ನಿಮ್ಮ ಕುಟುಂಬವನ್ನು ಕರಕೊಂಡು ಹೊರಟು ಹೋಗಿರಿ ಮತ್ತು ನೀವು ಅವರ ಹಿಂದೆಯೇ ಅನುಗಮಿಸಿರಿ. ನಿಮ್ಮ ಪೈಕಿ ಯಾರೂ ಹಿಂತಿರುಗಿ ನೋಡಬಾರದು. ನಿಮಗೆ ಆಜ್ಞಾಪಿಸಲಾದ ಭಾಗಕ್ಕೆ ನೇರವಾಗಿ ಸಾಗಿರಿ’. ಎಂದು ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(15,66,'15:66','ಆ ಕಾರ್ಯ ಅಂದರೆ ಬೆಳಗಾಗುವುದರೊಳಗಾಗಿ ಈ ಮಂದಿಗಳ ಬುಡವನ್ನೇ ಕಿತ್ತೊಗೆಯಲಾಗುವುದೆಂಬ ಕಾರ್ಯವನ್ನು ನಾವು ಅವರಿಗೆ ನಿಖರವಾಗಿ ತಿಳಿಸಿಬಿಟ್ಟೆವು.');
INSERT INTO "kannada" VALUES(15,67,'15:67','ನಗರದವರು ಸಂತೋಷದಿಂದ ಲೂಥರ ಮನೆಗೆ ಧಾವಿಸಿ ಬಂದರು.');
INSERT INTO "kannada" VALUES(15,68,'15:68','ಲೂಥರು ಹೇಳಿದರು, ‘ಇವರು ನನ್ನ ಅತಿಥಿಗಳು, ನನ್ನನ್ನು ಅವಮಾನಿಸಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(15,69,'15:69','ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ, ನನ್ನನ್ನು ನಿಂದಿಸಬೇಡಿರಿ’.');
INSERT INTO "kannada" VALUES(15,70,'15:70','ಆಗ ಅವರು, ‘ಜನರಿಗೆ ಆತಿಥ್ಯ ನೀಡಬಾರದು ಎಂದು ನಾವು ನಿಮ್ಮನ್ನು ತಡೆ ಹೇರಿಲ್ಲವೇ?’ ಎಂದು ಕೇಳಿದರು .');
INSERT INTO "kannada" VALUES(15,71,'15:71','ಲೂಥರು ಹೇಳಿದರು: ‘ಇದೋ ನನ್ನ ಪುತ್ರಿಯರು. ನೀವು ಆಸೆ ತೀರಿಸುವವರಾಗಿದ್ದರೆ (ಅವರನ್ನು ಮದುವೆಯಾಗಬಹುದಲ್ಲ?) .');
INSERT INTO "kannada" VALUES(15,72,'15:72','ನಿಮ್ಮ ಜೀವದಾಣೆ! ಆಗ ಆ ಜನರುಮದೋ ನ್ಮತ್ತರಾಗಿ ಮೈಮರೆತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(15,73,'15:73','ಹಾಗೆ ಸೂರ್ಯೋದಯದ ಹೊತ್ತಲ್ಲಿ ಉಷಃಕಾಲವಾದೊಡನೆ ಒಂದು ಘೋರ ಶಬ್ದವು ಅವರನ್ನು ಹಿಡಿಯಿತು.');
INSERT INTO "kannada" VALUES(15,74,'15:74','ಹಾಗೆ ನಾವು ಆ ನಾಡನ್ನು ಬುಡಮೇಲು ಮಾಡಿ ಬಿಟ್ಟೆವು ಮತ್ತು ಅವರ ಮೇಲೆ ಸುಡುಮಣ್ಣಿನ ಕಲ್ಲುಗಳ ಮಳೆಗರೆದೆವು.');
INSERT INTO "kannada" VALUES(15,75,'15:75','ವಿಚಾರಶೀಲರಿಗೆ ಅದರಲ್ಲಿ ಅನೇಕ ನಿದರ್ಶನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(15,76,'15:76','ಅದು (ಆ ಪ್ರದೇಶವು) (ಇಂದಿಗೂ) ನೆಲೆನಿಂತಿರುವ ಒಂದು ಮಾರ್ಗದಲ್ಲೇ ಇದೆ .');
INSERT INTO "kannada" VALUES(15,77,'15:77','ಇದರಲ್ಲಿ ನಿಶ್ಚಯವಾಗಿಯೂ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಒಂದು ನಿದರ್ಶನವಿದೆ.');
INSERT INTO "kannada" VALUES(15,78,'15:78','ಐಕಃದ ಜನರು ಅಕ್ರಮಿಗಳಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(15,79,'15:79','ನಾವು ಅವರ ಮೇಲೆ ಶಿಸ್ತುಕ್ರಮ ಕೈಗೊಂಡೆವು. ಈ ಎರಡೂ ಪ್ರದೇಶವೂ ತೆರೆದ ಮಾರ್ಗದಲ್ಲೇ ಇವೆ .');
INSERT INTO "kannada" VALUES(15,80,'15:80','ಖಂಡಿತ ಹಿಜ್ರ್‍ನವರೂ ಸಂದೇಶವಾಹಕರನ್ನು ಸುಳ್ಳಾಗಿಸಿದ್ದರು .');
INSERT INTO "kannada" VALUES(15,81,'15:81','ನಮ್ಮ ನಿದರ್ಶನಗಳನ್ನು ಅವರಿಗೆ ನಾವು ನೀಡಿದೆವು. ಆದರೆ ಅವರು ಅವೆಲ್ಲವನ್ನೂ ಅವಗಣಿಸುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(15,82,'15:82','ಅವರು ಪರ್ವತಗಳಿಂದ ಬಂಡೆಗಳನ್ನು ಕೊರೆದು ಮನೆಗಳನ್ನು ಮಾಡಿ ನಿರ್ಭಯರಾಗಿ ಕಳೆಯುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(15,83,'15:83','ಹೀಗಿರುತ್ತ ಬೆಳಗಾಗುತ್ತಲೇ ಒಂದು ಪ್ರಚಂಡ ಶಬ್ಧವು ಅವರನ್ನು ಹಿಡಿದು ಬಿಟ್ಟಿತು.');
INSERT INTO "kannada" VALUES(15,84,'15:84','ಅವರ ಸಂಪಾದನೆಯು ಅವರ ಉಪಯೋಗಕ್ಕೆ ಬರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(15,85,'15:85','ಭೂಮಿ-ಆಕಾಶಗಳನ್ನೂ ಅವುಗಳ ನಡುವೆ ಇರು ವುದೆಲ್ಲವನ್ನೂ ನಾವು ವ್ಯರ್ಥವಾಗಿ ಸೃಷ್ಟಿಸಲಿಲ್ಲ ಮತ್ತು ಅಂತ್ಯ ಘಳಿಗೆಯೂ ಖಂಡಿತ ಬರಲಿದೆ. ಆದುದರಿಂದ ಓ ಪೈಗಂಬರರೇ, ಅವರ ಬಗ್ಗೆ ನೀವು ನಿರ್ಲಿಪ್ತರಾಗಿ ನಿರ್ಲಕ್ಯವಹಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(15,86,'15:86','ನಿಶ್ಚಯವಾಗಿಯೂ ನಿಮ್ಮ ಪ್ರಭು ಎಲ್ಲರ ಸೃಷ್ಟಿ ಕರ್ತನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(15,87,'15:87','ಪುನರಾವರ್ತಿಸಲ್ಪಡುವ ಸಪ್ತ ಸೂಕ್ತಗಳನ್ನೂ ಮಹತ್ತಾದ ಖುರ್‍ಆನನ್ನೂ ನಾವು ನಿಮಗೆ ದಯಪಾಲಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(15,88,'15:88','ಅವರ (ಅವಿಶ್ವಾಸಿಗಳ)ಲ್ಲಿರುವ ಜನರಿಗೆ ಅನೇಕ ವಿಧಗಳಲ್ಲಿ ನಾವು ಕೊಟ್ಟಿರುವ ಲೌಕಿಕ ಸುಖ ಭೋಗಗಳ ಕಡೆಗೆ ನೀವು ಕಣ್ಣೆತ್ತಿ ನೋಡಬೇಡಿರಿ ಮತ್ತು ಅವರಿಗಾಗಿ ವ್ಯಾಕುಲಗೊಳ್ಳದಿರಿ. ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳಿಗೆ ನೀವು ವಿನಯಾನ್ವಿತರಾಗಿರಿ.');
INSERT INTO "kannada" VALUES(15,89,'15:89','ನಾನು ಸುಸ್ಪಷ್ಟವಾಗಿ ಎಚ್ಚರಿಕೆ ನೀಡುವವನು ಎಂದು ಹೇಳಿರಿ.');
INSERT INTO "kannada" VALUES(15,90,'15:90','ವಿಂಗಡನೆಗಾರರ ಮೇಲೆ ನಾವು ಕಳುಹಿಸಿದ್ದಂತೆ.');
INSERT INTO "kannada" VALUES(15,91,'15:91','ಅಂದರೆ ಖುರ್‍ಆನನ್ನು ವಿಭಿನ್ನ ಭಾಗಗಳಾಗಿ ಮಾರ್ಪಡಿಸಿದವರ ಮೇಲೆ.');
INSERT INTO "kannada" VALUES(15,92,'15:92','ನಿಮ್ಮ ಪ್ರಭುವಿನಾಣೆ! ನಾವು ಅವರೆಲ್ಲರೊಡನೆ ವಿಚಾರಿಸಿಯೇ ತೀರುವೆವು.');
INSERT INTO "kannada" VALUES(15,93,'15:93','ಅವರು ಮಾಡುತ್ತಲಿದ್ದ ಕರ್ಮದ ಬಗ್ಗೆ.');
INSERT INTO "kannada" VALUES(15,94,'15:94','ಆದುದರಿಂದ (ಓ ಪೈಗಂಬರರೇ,) ನಿಮಗೆ ಆಜ್ಞಾ ಪಿಸಲಾಗುತ್ತಿರುವ ಆದೇಶಗಳನ್ನು ಘಂಟಾ ಘೋಷವಾಗಿ ಸಾರಿ ಬಿಡಿರಿ ಮತ್ತು ಬಹುದೇವ ವಿಶ್ವಾಸಿಗಳನ್ನು ಅವಗಣಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(15,95,'15:95','ಪರಿಹಾಸ್ಯ ಮಾಡುವವರಿಂದ ನಿಮ್ಮನ್ನು ರಕ್ಷಿಸಲು ನಾವು ಸಾಕು.');
INSERT INTO "kannada" VALUES(15,96,'15:96','ಅಲ್ಲಾಹನೊಂದಿಗೆ ಬೇರೆ ದೇವರನ್ನು ಸ್ಥಾಪಿಸುವವರವರು. ಮುಂದೆ ಅವರಿಗೆ ಗೊತ್ತಾಗಲಿದೆ.');
INSERT INTO "kannada" VALUES(15,97,'15:97','ಅವರಾಡುತ್ತಿರುವ (ಸುಳ್ಳು ಹಾಗೂ ಅಪಹಾಸ್ಯದ) ಮಾತುಗಳಿಂದ ನಿಮ್ಮ ಎದೆ ಇಕ್ಕಟ್ಟಾಗುತ್ತಿದೆಯೆಂಬುದನ್ನು ನಾವು ಬಲ್ಲೆವು.');
INSERT INTO "kannada" VALUES(15,98,'15:98','ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕೀರ್ತನೆಯೊಂದಿಗೆ ಅವನನ್ನು ಸ್ತುತಿಸಿರಿ. ಸಾಷ್ಟಾಂಗವೆರಗುವವರಲ್ಲಿ ಸೇರಿರಿ.');
INSERT INTO "kannada" VALUES(15,99,'15:99','ಖಚಿತವಾದ ಕಾರ್ಯ (ಮರಣ) ನಿಮಗೆ ಬರುವ ವರೆಗೂ ನೀವು ನಿಮ್ಮ ಪ್ರಭುವಿಗೆ ಆರಾಧಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(16,1,'16:1','ಅಲ್ಲಾಹನ ಆಜ್ಞೆ ಬಂದು ಬಿಟ್ಟಿತು. ಇನ್ನು ಅದನ್ನು ಅವಸರಪಡಿಸಬೇಡಿರಿ. ಅವರು (ಅಲ್ಲಾಹನಿಗೆ ಇತರರನ್ನು) ಪಾಲು ಸೇರಿಸುವುದರಿಂದ ಅಲ್ಲಾಹು ಪರಿಶುದ್ಧನು. ಮಹೋನ್ನತನು.');
INSERT INTO "kannada" VALUES(16,2,'16:2','ಅವನು ತನ್ನ ದಾಸರ ಪೈಕಿ ತಾನಿಚ್ಛಿಸಿದವರ ಮೇಲೆ ತನ್ನ ಅನುಜ್ಞೆಯಿಂದ ದಿವ್ಯಸಂದೇಶದೊಂದಿಗೆ ದೇವಚರರನ್ನು ಅವತೀರ್ಣಗೊಳಿಸುತ್ತಾನೆ. ‘ನನ್ನ ಹೊರತು ಯಾರೂ ಆರಾಧ್ಯನಿಲ್ಲ. ಆದುದರಿಂದ ನೀವು ನನ್ನನ್ನು ಭಯಪಡಿರಿ ಎಂದು ಜನರನ್ನು ಎಚ್ಚರಿಸಿರಿ’ ಎಂದು.');
INSERT INTO "kannada" VALUES(16,3,'16:3','ಅವನು ಆಕಾಶಗಳನ್ನೂ ಭೂಮಿಯನ್ನೂ ಸತ್ಯ ಪೂರ್ಣವಾಗಿ ಸೃಷ್ಟಿಸಿರುತ್ತಾನೆ. ಇವರು ಮಾಡುವ ದೇವ ಸಹಭಾಗಿತ್ವ ಕಲ್ಪನೆಗಿಂತ ಅವನು ಮಹೋನ್ನತನು.');
INSERT INTO "kannada" VALUES(16,4,'16:4','ಅವನು ಮಾನವನನ್ನು ಒಂದು ವೀರ್ಯಾಣುವಿನಿಂದ ಸೃಷ್ಟಿಸಿದನು. ಅದೇ ಮಾನವ ಪ್ರತ್ಯಕ್ಷ ಜಗಳಗಂಟನಾಗಿ ಮಾರ್ಪಟ್ಟನು .');
INSERT INTO "kannada" VALUES(16,5,'16:5','ಅವನು ಆಡು, ದನ, ಒಂಟೆಗಳನ್ನು ನಿಮಗಾಗಿ ಸೃಷ್ಟಿಸಿದನು. ಅವುಗಳಲ್ಲಿ ನಿಮಗೆ ಬೆಚ್ಚನೆಯ ಉಣ್ಣೆಗಳೂ ಇತರ ಪ್ರಯೋಜನಗಳೂ ಇವೆ. ಅವುಗಳಿಂದಲೇ ನೀವು ತಿನ್ನುತ್ತೀರಿ.');
INSERT INTO "kannada" VALUES(16,6,'16:6','ನೀವು ಸಂಜೆ ಅವುಗಳನ್ನು ಹಟ್ಟಿಗೆ ಮರಳಿ ತರುವಾಗಲೂ ಮುಂಜಾನೆ ಮೇವಿಗೆ ಬಿಡುವಾಗಲೂ ನಿಮಗೆ ಅವುಗಳಲ್ಲಿ ಸೊಬಗಿದೆ.');
INSERT INTO "kannada" VALUES(16,7,'16:7','ದೈಹಿಕ ಕ್ಲೇಷವಿಲ್ಲದೆ ನಿಮಗೆ ತಲುಪಲಾಗದಂತಹ ಸ್ಥಳಗಳಿಗೆ ಅವು ನಿಮ್ಮ ಹೊರೆಯನ್ನು ಹೊತ್ತು ಕೊಂಡು ಹೋಗುತ್ತವೆ. ವಾಸ್ತವದಲ್ಲಿ ನಿಮ್ಮ ಪ್ರಭು ಮಹಾವತ್ಸಲನೂ ದಯಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(16,8,'16:8','ಅವನು ಕುದುರೆಗಳನ್ನೂ ಹೇಸರಕತ್ತೆಗಳನ್ನೂ ಕತ್ತೆಗಳನ್ನೂ ನಿಮ್ಮ ಸವಾರಿಗಾಗಿಯೂ (ನಿಮಗೆ ವಾಹನವಾಗಿ ಬಳಸಲು) ಸೊಗಸಿಗಾಗಿಯೂ ಸೃಷ್ಟಿಸಿದನು. ನಿಮಗೆ ತಿಳಿಯದ ಅನೇಕ (ಅದ್ಭುತ) ವಸ್ತುಗಳನ್ನು ಅವನು ಸೃಷ್ಟಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(16,9,'16:9','ಅಡ್ಡ ಮಾರ್ಗಗಳೂ ಇರುವಾಗ ನೇರ ಮಾರ್ಗವನ್ನು ತೋರಿಸುವ ಹೊಣೆ ಅಲ್ಲಾಹನ ಮೇಲೆಯೇ ಇದೆ. ಅವನು ಇಚ್ಛಿಸುತ್ತಿದ್ದರೆ ನಿಮ್ಮೆಲ್ಲರನ್ನೂ ನೇರದಾರಿಗೆ ಸೇರಿಸುತ್ತಿದ್ದನು.');
INSERT INTO "kannada" VALUES(16,10,'16:10','ಅವನೇ ನಿಮಗಾಗಿ ಆಕಾಶದಿಂದ ನೀರನ್ನು ಸುರಿಸಿದವನು. ಅದರಿಂದಲೇ ನಿಮ್ಮ ಕುಡಿಯುವ ನೀರು. ಮತ್ತು ಅದರಿಂದಲೇ ಸಸ್ಯ, ಮರಗಳು. ಅದರಿಂದ ನೀವು ನಿಮ್ಮ ಪ್ರಾಣಿಗಳನ್ನು ಮೇಯಿ ಸುತ್ತಿರುವಿರಿ.');
INSERT INTO "kannada" VALUES(16,11,'16:11','ಅವನು ಈ ನೀರಿನ ಮೂಲಕ ಕೃಷಿ, ಝೈತೂನ್, ಖರ್ಜೂರ, ದ್ರಾಕ್ಷೆ ಮತ್ತು ಇತರ ನಾನಾ ವಿಧದ ಫಲಗಳನ್ನು ಉತ್ಪಾದಿಸುತ್ತಾನೆ. ಯೋಚಿ ಸುವವರಿಗೆ ನಿಶ್ಚಯವಾಗಿಯೂ ಇದರಲ್ಲಿ ನಿದರ್ಶನವಿದೆ .');
INSERT INTO "kannada" VALUES(16,12,'16:12','ಅವನು ನಿಮಗಾಗಿ ಇರುಳನ್ನೂ ಹಗಲನ್ನೂ ಸೂರ್ಯನನ್ನೂ ಚಂದ್ರನನ್ನೂ ನಕ್ಷತ್ರಗಳನ್ನೂ ಅವನ ಇರಾದೆಯಂತೆ ನಿಯಂತ್ರಿಸಿರುತ್ತಾನೆ. ವಿವೇಚಿಸುವ ಜನರಿಗೆ ಇದರಲ್ಲಿ ನಿಜವಾಗಿಯೂ ಅನೇಕ ನಿದರ್ಶನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(16,13,'16:13','ಅವನು ನಿಮಗಾಗಿ ಭೂಮಿಯಲ್ಲಿ ಸೃಷ್ಟಿಸಿರುವ ವಿವಿಧ ಬಣ್ಣಗಳ ವಸ್ತುಗಳನ್ನೂ ಕೂಡಾ. (ಅವನು ನಿಮಗೆ ವಿಧೇಯಗೊಳಿಸಿರುತ್ತಾನೆ). ಮನಗಾಣುವವರಿಗೆ ಖಂಡಿತವಾಗಿಯೂ ಇದರಲ್ಲಿ ನಿದರ್ಶನವಿದೆ.');
INSERT INTO "kannada" VALUES(16,14,'16:14','ನಿಮಗೆ ತಾಜಾ ಮಾಂಸ ಪಡೆದು ತಿನ್ನಲಿಕ್ಕೂ ನೀವು ಧರಿಸುವ ಆಭರಣಗಳನ್ನು ಹೊರತೆಗೆಯಲಿಕ್ಕೂ ಕಡಲನ್ನು ವಿಧೇಯಗೊಳಿಸಿದವನು ಅವನೇ. ನಾವೆಯು ಕಡಲನ್ನು ಸೀಳುತ್ತ ಸಾಗುತ್ತಿರುವುದನ್ನು ನೀವು ನೋಡುತ್ತೀರಿ. ನೀವು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಅನುಗ್ರಹದಿಂದ ಅರಸಲಿಕ್ಕೂ ಅವನಿಗೆ ಕೃತಜ್ಞರಾಗಲಿಕ್ಕೂ.');
INSERT INTO "kannada" VALUES(16,15,'16:15','ಭೂಮಿಯು ನಿಮ್ಮೊಂದಿಗೆ ಅಲುಗಾಡದಂತೆ ಅವನು ಭೂಮಿಯಲ್ಲಿ ಊರಿನಿಂತ ಪರ್ವತಗಳನ್ನು ನಾಟಿರುತ್ತಾನೆ . ನದಿಗಳನ್ನು ಸೀಳಿರುತ್ತಾನೆ. ಮತ್ತು ಮಾರ್ಗಗಳನ್ನು ಮಾಡಿರುತ್ತಾನೆ. ನೀವು ದಾರಿ ಹಿಡಿಯುವ ಸಲುವಾಗಿ.');
INSERT INTO "kannada" VALUES(16,16,'16:16','(ಇದಲ್ಲದೆ) ಮಾರ್ಗಗಳನ್ನು ತೋರಿಸುವ ಕುರು ಹುಗಳನ್ನೂ, ಮತ್ತು ಜನರು ನಕ್ಷತ್ರಗಳಿಂದಲೂ ದಾರಿಗಾಣುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(16,17,'16:17','ಹೀಗಿರುವಾಗ ಸೃಷ್ಟಿಸುವಾತನು ಏನನ್ನೂ ಸೃಷ್ಟಿಸದವರಿಗೆ ಸಮಾನನೆ? ನೀವು ಯೋಚಿಸುವುದಿಲ್ಲವೆ?');
INSERT INTO "kannada" VALUES(16,18,'16:18','ನೀವು ಅಲ್ಲಾಹನ ಅನುಗ್ರಹಗಳನ್ನು ಎಣಿಸುವುದಾದರೆ ನಿಮಗೆ ಅದರ ಲೆಕ್ಕತೆಗೆಯಲು ಸಾಧ್ಯವಿಲ್ಲ. ವಾಸ್ತವದಲ್ಲಿ ಅವನು ಮಹಾ ಕ್ಷಮಾಶೀಲನೂ ಕೃಪಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(16,19,'16:19','ನೀವು ರಹಸ್ಯಗೊಳಿಸುವುದನ್ನೂ ಬಹಿರಂಗಗೊಳಿಸುವುದನ್ನೂ ಅಲ್ಲಾಹನು ಬಲ್ಲವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(16,20,'16:20','ಇವರು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಇತರ ಯಾರನ್ನು ಆರಾಧಿಸುತ್ತಿರುವರೋ ಅವರು ಯಾವುದೇ ವಸ್ತುವನ್ನು ಸೃಷ್ಟಿಸಲಿಲ್ಲ. ನಿಜವಾಗಿ ಅವರು ಸ್ವತಃ ಸೃಷ್ಟಿಸಲ್ಪಟ್ಟವರು.');
INSERT INTO "kannada" VALUES(16,21,'16:21','ಅವರು ಮೃತರು, ಜೀವಂತರಲ್ಲ. ಸೃಷ್ಟಿಗಳನ್ನು ಯಾವಾಗ ಮರಳಿ ಎಬ್ಬಿಸಲಾಗುವುದೆಂದು ಅವರಿಗೆ ತಿಳಿದಿರುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(16,22,'16:22','ನಿಮ್ಮ ಆರಾಧ್ಯನು ಕೇವಲ ಏಕ ಆರಾಧ್ಯನು. ಆದರೆ ಪರಲೋಕದ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸದವರ ಹೃದಯಗಳು (ಏಕದೇವತ್ವವನ್ನು) ನಿರಾಕರಿಸಿವೆ. ಮತ್ತು ಅವರು ಗರ್ವಿಷ್ಟರಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(16,23,'16:23','ಸಂಶಯಬೇಡ, ಅವರು ರಹಸ್ಯಗೊಳಿಸುವ ಮತ್ತು ಬಹಿರಂಗಗೊಳಿಸುವ ಸರ್ವಕರ್ಮಗಳನ್ನೂ ಅಲ್ಲಾಹು ಅರಿಯುತ್ತಾನೆ. ಅಹಂಭಾವಿಗಳನ್ನು ಖಂಡಿತ ಅಲ್ಲಾಹನು ಮೆಚ್ಚುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(16,24,'16:24','ನಿಮ್ಮ ಪ್ರಭು ಅವತೀರ್ಣಗೊಳಿಸಿರುವುದು ಏನನ್ನು ಎಂದು ಅವರೊಡನೆ ಕೇಳಲಾದರೆ, ‘ಅದೆ ಲ್ಲಾ ಹಿಂದಿನವರ ಹರಟೆ ಪುರಾಣಗಳು’ ಎಂದು ಇವರು ಹೇಳುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(16,25,'16:25','ಜನರನ್ನು ದಾರಿಗೆಡಿಸಿದ ಪಾಪದ ಹೊರೆಯನ್ನು ಪೂರ್ಣವಾಗಿಯೂ ಯಾವುದೇ ಜ್ಞಾನವಿಲ್ಲದೆ ಇವರು ದಾರಿತಪ್ಪಿಸಿದವರ ಪಾಪದ ಹೊರೆಯನ್ನು ಆಂಶಿಕವಾಗಿಯೂ ಅಂತ್ಯದಿನದಲ್ಲಿ ಇವರು ಹೊತ್ತುಕೊಳ್ಳಬೇಕಾದ ಅವಸ್ಥೆಗೆ ಈ ಮಾತು ಇವರನ್ನು ತಲುಪಿಸುತ್ತದೆ. ತಿಳಿಯಿರಿ; ಇವರು ಹೇರಿಕೊಳ್ಳುತ್ತಿರುವ ಹೊರೆ ಅದೆಷ್ಟು ಕೆಟ್ಟದು!');
INSERT INTO "kannada" VALUES(16,26,'16:26','ಇವರಿಗಿಂತ ಮುಂಚಿನವರೂ ಕುತಂತ್ರಗಳನ್ನು ಹೂಡಿರುತ್ತಾರೆ. ಆದರೆ ಅಲ್ಲಾಹನು ಅವರ ಕಟ್ಟಡಗಳನ್ನು ಬುಡದಿಂದಲೇ ಕಿತ್ತೆಸೆದನು. ಹಾಗೆ ಮೇಲಿಂದ ಅದರ ಮೇಲ್ಛಾವಣಿ ಅವರ ಮೇಲೆಯೇ ಮಗುಚಿಬಿತ್ತು. ಅವರು ಊಹಿಸಿಯೂ ಇರದಂಥ ದಿಕ್ಕಿನಿಂದ ಅವರಿಗೆ ಶಿಕ್ಷೆ ಬಂತು.');
INSERT INTO "kannada" VALUES(16,27,'16:27','ಮುಂದೆ ಪುನರುತ್ಥಾನ ದಿನದಂದು ಅಲ್ಲಾಹನು ಅವರನ್ನು ಅಪಮಾನಗೊಳಿಸುವನು. ‘ನನ್ನ ಯಾವ ಸಹಭಾಗಿಗಳಿಗೋಸ್ಕರ ನೀವು ತರ್ಕಿಸುತ್ತಿದ್ದಿರೊ ಅವರೀಗ ಎಲ್ಲಿದ್ದಾರೆ?’ ಎಂದು ಅವರೊಡನೆ ಕೇಳುವನು. ಜ್ಞಾನ ಪ್ರಾಪ್ತವಾಗಿದ್ದವರು ಹೇಳುವರು, ನಿಶ್ಚಯವಾಗಿಯೂ ಇಂದು ಅಪಮಾನವೂ ದುರ್ದೆಶೆಯೂ ಸತ್ಯನಿಷೇಧಿಗಳಿಗಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(16,28,'16:28','ತಮ್ಮ ಮೇಲೆ ತಾವೇ ದ್ರೋಹವೆಸಗಿದ ಸತ್ಯ ನಿಷೇಧಿಗಳವರು. ಅವರನ್ನು ದೇವಚರರು ಮೃತ ಪಡಿಸುತ್ತಾರೆ. ನಾವೇನೂ ದುಷ್ಕøತ್ಯ ವೆಸಗುತ್ತಿರಲಿಲ್ಲ ಎನ್ನುತ್ತಾ ಆಗ ಅವರು ಶರಣಾಗುತ್ತಾರೆ. ಹಾಗಲ್ಲ ; ನೀವು ಮಾಡುತ್ತಿದ್ದ ದುಷ್ಕøತ್ಯಗಳನ್ನು ಅಲ್ಲಾಹು ಚೆನ್ನಾಗಿ ಬಲ್ಲವನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(16,29,'16:29','ಆದ್ದರಿಂದ ನರಕದ ದ್ವಾರಗಳ ಮೂಲಕ ಪ್ರವೇಶಿಸಿರಿ. ಅದರಲ್ಲಿ ಶಾಶ್ವತರಾಗುವ ಸ್ಥಿತಿಯಲ್ಲಿ. ಅಹಂಕಾರಿಗಳ ವಾಸಸ್ಥಳವು ಅತ್ಯಂತ ನಿಕೃಷ್ಟವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(16,30,'16:30','ನಿಮ್ಮ ಪ್ರಭು ಏನನ್ನು ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದಾನೆಂದು ದೇವನಿಷ್ಟರೊಡನೆ ಕೇಳಲಾಯಿತು. ಅವರು, ಹೇಳಿದರು, ‘ಉತ್ತಮವಾದುದನ್ನು’ ಎಂದು. ಸಜ್ಜನರಿಗೆ ಇಹಲೋಕದಲ್ಲೂ ಒಳಿತಿದೆ ಮತ್ತು ಪರಲೋಕ ಭವನವಂತೂ ಅತ್ಯುತ್ತಮವೇ ಆಗಿದೆ. ಧರ್ಮನಿಷ್ಟರ ಭವನ ಬಹಳ ಚೆನ್ನಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(16,31,'16:31','ಶಾಶ್ವತ ನಿವಾಸದ ಉದ್ಯಾನವನಗಳು. ಅದಕ್ಕೆ ಅವರು ಪ್ರವೇಶಿಸುವರು. ಅವುಗಳ ತಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆಗಳು ಹರಿಯುತ್ತಿರುವುವು. ಅವರಿಗೆ ಅವರು ಇಚ್ಛಿಸುವುದೆಲ್ಲವೂ ಅಲ್ಲಿವೆ. ಇದೇ ಪ್ರಕಾರ ಅಲ್ಲಾಹನು ಧರ್ಮನಿಷ್ಟರಿಗೆ ಪ್ರತಿಫಲ ಕೊಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(16,32,'16:32','ಆ ಧರ್ಮನಿಷ್ಠರು ಎಂತವರೆಂದರೆ ದೇವಚರರು ಅವರನ್ನು ಪರಿಶುದ್ಧರಾದ ಸ್ಥಿತಿಯಲ್ಲಿ ಮರಣ ಗೊಳಿಸುತ್ತಾರೆ. ‘ನಿಮ್ಮ ಮೇಲೆ ಶಾಂತಿ ಇರಲಿ. ನಿಮ್ಮ ಕರ್ಮಗಳ ಪ್ರತಿಫಲವಾಗಿ ಸ್ವರ್ಗದೊಳಗೆ ಪ್ರವೇಶಿಸಿರಿ’ ಎನ್ನುವರು.');
INSERT INTO "kannada" VALUES(16,33,'16:33','ತಮ್ಮ ಬಳಿ ದೇವಚರರು ಬರಲಿ ಅಥವಾ ನಿನ್ನ ಪ್ರಭುವಿನ ಆಜ್ಞೆಯು ಬರಲಿ ಎಂಬುದನ್ನೇ ಇವರು ನಿರೀಕ್ಷಿಸುತ್ತಿರುವರೇನು? ಇದೇ ಪ್ರಕಾರ ಇವರಿಗಿಂತ ಮುಂಚಿನವರೂ ಮಾಡಿದ್ದರು. (ಕೊನೆಗೆ ನಾಶವಾಗಿದ್ದರು). ಅಲ್ಲಾಹನು ಅವರಿಗೆ ಅಕ್ರಮವೆಸಗಲಿಲ್ಲ. ವಾಸ್ತವದಲ್ಲಿ ಅವರು ತಮಗೆ ತಾವೇ ಅಕ್ರಮವೆಸಗಿಕೊಂಡಿದ್ದರು.');
INSERT INTO "kannada" VALUES(16,34,'16:34','ಅವರ ದುಷ್ಕರ್ಮಗಳ ದುಷ್ಪರಿಣಾಮಗಳು ಅವರಿಗೆ ತಟ್ಟಿದುವು. ಅವರು ಯಾವುದನ್ನು ಪರಿಹಾಸ್ಯ ಮಾಡುತ್ತಿದ್ದರೋ ಅದೇ ಅವರನ್ನು ಸುತ್ತುವರಿಯಿತು.');
INSERT INTO "kannada" VALUES(16,35,'16:35','ಬಹುದೇವ ವಿಶ್ವಾಸಿಗಳು ಹೇಳಿದರು; ‘ಅಲ್ಲಾಹ ನು ಇಚ್ಚಿಸುತ್ತಿದ್ದರೆ ನಾವಾಗಲಿ ನಮ್ಮ ಹಿರಿಯರಾಗಲಿ ಅವನ ಹೊರತು ಬೇರೆ ಯಾವುದನ್ನೂ ಆರಾಧಿಸುತ್ತಿರಲಿಲ್ಲ ಮತ್ತು ಅವನ ಅಪ್ಪಣೆಯ ವಿನಾ ಯಾವುದನ್ನೂ ನಿಷಿದ್ಧಗೊಳಿಸುತ್ತಿರಲಿಲ್ಲ ಎಂದು . ಹೀಗೆಯೇ ಇವರಿಗಿಂತ ಮುಂಚಿನ ವರೂ ಮಾಡಿದ್ದರು. ಆದರೆ ದೇವದೂತರ ಮೇಲೆ ಸುಸ್ಪಷ್ಟವಾಗಿ ಸಂದೇಶ ತಲುಪಿಸುವ ಹೊರತು ಬೇರಾವ ಹೊಣೆಗಾರಿಕೆಯೂ ಇರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(16,36,'16:36','ಪ್ರತಿಯೊಂದು ಸಮುದಾಯದಲ್ಲಿ ನಾವು ದೂತ ರನ್ನು ಕಳುಹಿಸಿದೆವು. ಅಲ್ಲಾಹನನ್ನು ಆರಾಧಿಸಿರಿ. ಮತ್ತು ದುರ್ಮೂರ್ತಿಗಳಿಂದ ದೂರವಿರಿ (ಎಂದು). ಅನಂತರ ಅಲ್ಲಾಹನು ಕೆಲವರಿಗೆ ಸನ್ಮಾರ್ಗದರ್ಶನ ಮಾಡಿಸಿದನು. ಮತ್ತು ಕೆಲವರ ಮೇಲೆ ಪಥಭ್ರಷ್ಟತೆ ನಿಜಗೊಂಡಿತು. ಆದ್ದರಿಂದ ನೀವು ಭೂಮಿಯಲ್ಲಿ ಸಂಚರಿಸಿರಿ. ಸುಳ್ಳಾಗಿಸಿ ದವರ ಅಂತಿಮ ಪರಿಣಾಮವೇನಾಗಿದೆಯೆಂಬು ದನ್ನು ನೋಡಿರಿ.');
INSERT INTO "kannada" VALUES(16,37,'16:37','(ಓ ಪೈಗಂಬರರೇ,) ನೀವು ಅವರ ಸನ್ಮಾರ್ಗ ದರ್ಶನಕ್ಕಾಗಿ ಹಂಬಲಿಸಿದರೂ (ನಿಮಗದು ಸಾಧ್ಯವಿಲ್ಲ). ಏಕೆಂದರೆ ಅಲ್ಲಾಹನು ಯಾರನ್ನು ಪಥಭ್ರಷ್ಟ ಗೊಳಿಸುತ್ತಾನೋ ಅವನನ್ನು ಸನ್ಮಾರ್ಗಕ್ಕೆ ಸೇರಿಸಲಾರ. ಅವರಿಗೆ ಸಹಾಯಕರೇ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(16,38,'16:38','ಮೃತರನ್ನು ಅಲ್ಲಾಹನು ಪುನರುತ್ಥಾನಗೊಳಿಸಲಾರನೆಂದು ಅವರು ದೃಢವಾಗಿ ಅಲ್ಲಾಹನ ಮೇಲೆ ಆಣೆ ಹಾಕುವರು; ಆದರೆ ಪುನರುತ್ಥಾನವು ಅಲ್ಲಾಹನು ತನಗೆ ತಾನೇ ವಾಗ್ದಾನ ಮಾಡಿ ಕೊಂಡ ಸತ್ಯ. ಆದರೆ ಹೆಚ್ಚಿನವರು ತಿಳಿಯುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(16,39,'16:39','ಯಾವ ವಿಷಯದ ಬಗ್ಗೆ ಇವರು ಭಿನ್ನಾಭಿಪ್ರಾಯ ತಾಳಿರುವರೋ ಅದನ್ನು ಅಲ್ಲಾಹ್ ಇವರಿಗೆ ಸ್ಪಷ್ಟ ಪಡಿಸಲಿಕ್ಕಾಗಿ ಮತ್ತು ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ತಾವು ಸುಳ್ಳುಗಾರರೆಂದು ಅರಿಯುವಂತಾಗಲಿಕ್ಕಾಗಿ. (ಪುನರುತ್ಥಾನ ನೀಡಿಯೇ ನೀಡುತ್ತಾನೆ)');
INSERT INTO "kannada" VALUES(16,40,'16:40','ನಾವು ಯಾವುದೇ ವಸ್ತುವಿನ ಅಸ್ತಿತ್ವ ಬಯಸಿದರೆ ಅದಕ್ಕೆ ನಾವು `ಆಗು’ ಎಂದು ಹೇಳುವುದೊಂದೇ ನಮ್ಮ ವಚನ. ಆಗ ಅದು ಆಗಿ ಬಿಡುತ್ತದೆ .');
INSERT INTO "kannada" VALUES(16,41,'16:41','ತುಳಿತಕ್ಕೊಳಗಾದ ನಂತರ ಅಲ್ಲಾಹನಿಗಾಗಿ ಸ್ವಂತ ನಾಡನ್ನು ತ್ಯಜಿಸಿ ಹೊರಟವರಿಗೆ ನಾವು ಇಹದಲ್ಲೇ ಉತ್ತಮ ವಾಸಸ್ಥಾನವನ್ನು ನೀಡು ವೆವು. ಪರಲೋಕದ ಪ್ರತಿಫಲವೂ ಅತ್ಯಂತ ಹಿರಿದಾಗಿದೆ. ಅವರು (ಅವಿಶ್ವಾಸಿಗಳು) ಅರಿತು ಕೊಂಡಿದ್ದರೆ!');
INSERT INTO "kannada" VALUES(16,42,'16:42','ಅವರು ಸಂಕಷ್ಟವನ್ನು ಸಹಿಸಿದವರು ಹಾಗೂ ತಮ್ಮ ಪ್ರಭುವಿನ ಮೇಲೆ ಭಾರವರ್ಪಿಸಿದವರು .');
INSERT INTO "kannada" VALUES(16,43,'16:43','(ಓ ಪೈಗಂಬರರೇ,) ದಿವ್ಯಸಂದೇಶದೊಂದಿಗೆ ನಾನು ಸಂದೇಶವಾಹಕರನ್ನು ಕಳುಹಿಸಿದಾ ಗಲೆಲ್ಲ ಪುರುಷರನ್ನೇ ಕಳುಹಿಸಿದ್ದೇವೆ. (ಓ ನಿಷೇ ಧಿಗಳೇ!) ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲದಿದ್ದರೆ ಬಲ್ಲವರಲ್ಲಿ ಕೇಳಿರಿ .');
INSERT INTO "kannada" VALUES(16,44,'16:44','ಗತ ಸಂದೇಶವಾಹಕರಿಗೆ ನಾವು ಪ್ರತ್ಯಕ್ಷ ಪ್ರಮಾಣಗಳನ್ನೂ ಗ್ರಂಥಗಳನ್ನೂ ಕೊಟ್ಟು ಕಳು ಹಿಸಿದ್ದೆವು ಮತ್ತು ಈಗ ಈ ಉದ್ಭೋದನೆಯನ್ನು ನಿಮಗೂ ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದೇವೆ, ನೀವು ಅವರಿಗಾಗಿ ಅವತೀರ್ಣವಾದ ಬೋಧನೆಯನ್ನು ವಿವರಿಸಿ ಕೊಡುವ ಹಾಗೂ ಅವರು ಯೋಚಿಸುವ ಸಲುವಾಗಿ.');
INSERT INTO "kannada" VALUES(16,45,'16:45','ಹೀನ ಕುತಂತ್ರಗಳನ್ನು ಹೆಣೆಯುವವರು, ತಮ್ಮನ್ನು ಅಲ್ಲಾಹನು ಭೂಮಿಯೊಳಗೆ ಹೂತು ಹೋಗುವಂತೆ ಮಾಡುವುದರಿಂದ ಅಥವಾ ಅನಿ ರೀಕ್ಷಿತವಾಗಿ ಶಿಕ್ಷೆಯು ಬಂದೆರಗುವುದರಿಂದ ನಿರ್ಭೀತರಾಗಿರುವರೇ?');
INSERT INTO "kannada" VALUES(16,46,'16:46','ಅಥವಾ ಅವರು ಅತ್ತಿತ್ತ ನಡೆದಾಡುವಾಗ ಅವರನ್ನು ನಾಶಗೊಳಿಸುವುದರಿಂದ ಅವರು ನಿರ್ಭಯರೇ? ಅದರಿಂದ ಅವರು ತಡೆಯಲು ಶಕ್ತರಲ್ಲ.');
INSERT INTO "kannada" VALUES(16,47,'16:47','ಅಥವಾ ಅವರು ಸ್ವಲ್ಪ ಸ್ವಲ್ಪವಾಗಿ ವಿನಾಶಕ್ಕೆ ತಲುಪುವುದರ ಬಗ್ಗೆ ನಿರ್ಭೀತರಾಗಿರುವರೇ? ಖಂಡಿತ ನಿಮ್ಮ ಪ್ರಭು ಮಹಾ ದಯಾಳುವೂ ಕರುಣಾಶೀಲನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(16,48,'16:48','ಅಲ್ಲಾಹನು ಸೃಷ್ಟಿಸಿರುವ ವಸ್ತುಗಳ ನೆರಳು ಎಡಕ್ಕೂ ಬಲಕ್ಕೂ ವಾಲುತ್ತಾ ಶರಣಾಗುತ್ತಾ ಅಲ್ಲಾಹನಿಗೆ ಸಾಷ್ಟಾಂಗವೆರಗುತ್ತಿರುವುದನ್ನು ಇವರು ನೋಡುವುದಿಲ್ಲವೇ? ');
INSERT INTO "kannada" VALUES(16,49,'16:49','ಆಕಾಶಗಳಲ್ಲಿಯೂ ಭೂಮಿಯಲ್ಲೂ ಇರುವ ಸರ್ವಜೀವ ಜಾಲಗಳೂ ದೇವಚರರೂ ಸಕಲವೂ ಅಲ್ಲಾಹನಿಗೆ ಸಾಷ್ಟಾಂಗವೆರಗುತ್ತವೆ. ಅವು ಅಹಂಭಾವ ತೋರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(16,50,'16:50','ತಮ್ಮ ಮೇಲೆ ಸರ್ವಾಧಿಕಾರವುಳ್ಳ ಸರ್ವ ಸಮ ರ್ಥನಾದ ತಮ್ಮ ಪ್ರಭುವನ್ನು ಅವರು ಭಯ ಪಡುತ್ತಾರೆ. ಕೊಟ್ಟ ಅಪ್ಪಣೆಯನ್ನು ಪಾಲಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(16,51,'16:51','ಇಬ್ಬರು ಆರಾಧ್ಯರನ್ನು ಸ್ವೀಕರಿಸಬೇಡಿರಿ, ಅವನೊಬ್ಬನೇ ಆರಾಧ್ಯನು, ಆದುದರಿಂದ ನನ್ನನ್ನು ಮಾತ್ರ ಭಯಪಡಿರಿ ಎಂದು ಅಲ್ಲಾಹನು ಅಜ್ಞಾಪಿಸಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(16,52,'16:52','ಆಕಾಶಗಳಲ್ಲೂ ಭೂಮಿಯಲ್ಲೂ ಇರುವ ಸಕಲವೂ ಅವನ ಒಡೆತನಕ್ಕೆ ಸೇರಿವೆ. ಖಾಯಂ ಅನು ಸರಣಿಯೂ ಅವನಿಗೇ ಸೇರಿದೆ. ಹೀಗಿರುತ್ತ ನೀವು ಅಲ್ಲಾಹನಲ್ಲದವರಲ್ಲಿ ಭಕ್ತಿ ತೋರುತ್ತೀರಾ?');
INSERT INTO "kannada" VALUES(16,53,'16:53','ನಿಮ್ಮಲ್ಲಿರುವ ಪ್ರತಿಯೊಂದು ಅನುಗ್ರಹವೂ ಅಲ್ಲಾಹನಿಂದಲೇ ಬಂದಿದೆ. ನಿಮಗೆ ಹಾನಿ ತಟ್ಟಿದರೆ ನೀವು ರಕ್ಷಣೆಗಾಗಿ ಅವನಲ್ಲೇ ಮೊರೆಯಿಡುತ್ತೀರಿ.');
INSERT INTO "kannada" VALUES(16,54,'16:54','ಆದರೆ ಅಲ್ಲಾಹನು ನಿಮ್ಮಿಂದ ಸಂಕಷ್ಟವನ್ನು ನಿವಾರಿಸಿದರೆ ನಿಮ್ಮಲ್ಲೊಂದು ವಿಭಾಗವು ತನ್ನ ಪ್ರಭುವಿಗೆ ಇತರರನ್ನು ಸಹಭಾಗಿಗಳನ್ನಾಗಿ ಮಾಡುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(16,55,'16:55','ಇದು ಅಲ್ಲಾಹನ ಅನುಗ್ರಹಕ್ಕೆ ಕೃತಘ್ನತೆ ತೋರಲಿಕ್ಕಾಗಿ. ಆದ್ದರಿಂದ ನೀವು ಸುಖ ಅನುಭವಿಸಿರಿ! ನಂತರ ನಿಮಗೆ ತಿಳಿಯುವುದು!');
INSERT INTO "kannada" VALUES(16,56,'16:56','ನಾವು ಅವರಿಗೆ ನೀಡಿದ ಸಂಪತ್ತಿನಿಂದ ಒಂದಂ ಶವನ್ನು ಯಾವ ವಸ್ತುಗಳ ಬಗ್ಗೆ (ಗುಣದೋಷ ನೀಡುವ ಶಕ್ತಿಯಿಲ್ಲವೆಂದು) ಅವರು ಸ್ವತಃ ಅರಿತಿಲ್ಲವೋ ಅಂಥ ವಸ್ತುಗಳಿಗೆ (ವಿಗ್ರಹಗಳಿಗೆ) ನೀಡುತ್ತಾರೆ. ಅಲ್ಲಾಹನಾಣೆ, ನೀವು ಕಟ್ಟುವ ಸುಳ್ಳುಗಳ ಬಗ್ಗೆ ಖಂಡಿತವಾಗಿಯೂ ನಿಮ್ಮನ್ನು ಪ್ರಶ್ನಿಸಲಾಗುವುದು .');
INSERT INTO "kannada" VALUES(16,57,'16:57','ಅವರು (ಸ್ವತಃ ಅನಿಷ್ಟಪಡುವ) ಪುತ್ರಿಯರನ್ನು ಅಲ್ಲಾಹನಿಗೆ ನಿಶ್ಚಯಿಸುತ್ತಾರೆ. ಅವನು ಪರಮಪಾವನನು. ಅವರು ಸ್ವತಃ ಇಷ್ಟಪಡುವುದನ್ನು (ಗಂಡು ಮಕ್ಕಳನ್ನು) ತಮಗಾಗಿ ನಿಶ್ಚಯಿಸಿ ಕೊಳ್ಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(16,58,'16:58','ಅವರ ಪೈಕಿ ಯಾರಿಗಾದರೂ ಹೆಣ್ಣು ಮಗು ಹುಟ್ಟಿದ ಸುವಾರ್ತೆ ನೀಡಲಾದರೆ, ಅವನು ದುಃಖಿತನಾಗಿ ಅವನ ಮುಖ ಕಳೆಗುಂದಿ ಹೋಗುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(16,59,'16:59','ಅವನಿಗೆ ದೊರೆತ ಶುಭವಾರ್ತೆಯ ಅಪಮಾನದಿಂದಾಗಿ ಅವನು ಜನರಿಂದ ತಲೆಮರೆಸಿ ಕೊಳ್ಳು ತ್ತಾನೆ. ಅಪಮಾನದೊಂದಿಗೆ ಮಗುವನ್ನಿರಿಸಿ ಕೊಳ್ಳಲೇ ಅಥವಾ ಮಣ್ಣಿನಲ್ಲಿ ಹುಗಿದು ಬಿಡಲೇ ಎಂದು ಯೋಚಿಸುತ್ತಾನೆ. ತಿಳಿಯಿರಿ; ಅವರು ಅಲ್ಲಾಹನ ಬಗ್ಗೆ ಕೈಗೊಳ್ಳುವ ನಿರ್ಣಯ ಅತಿ ಕೆಟ್ಟದು.');
INSERT INTO "kannada" VALUES(16,60,'16:60','ಪರಲೋಕದಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸದವರಿಗೆ ಕೆಟ್ಟ ವಿಶೇಷಣವಿದೆ. ಅಲ್ಲಾಹನಿಗೆ ಅತ್ಯುನ್ನತ ಗುಣವಿಶೇಷಣವಿದೆ. ಅವನೇ ಮಹಾಪ್ರತಾಪಿಯೂ ಮಹಾ ಯುಕ್ತಿವಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(16,61,'16:61','ಅಲ್ಲಾಹನು ಜನರನ್ನು ಅವರ ಅಕ್ರಮಗಳಿಗೆ ಪ್ರತಿಯಾಗಿ ತಕ್ಷಣ ಹಿಡಿದು ಶಿಕ್ಷಿಸುತ್ತಿದ್ದರೆ ಭೂಮಿಯ ಮೇಲೆ ಒಂದೇ ಒಂದು ಜೀವಿಯನ್ನೂ ಬಿಡುತ್ತಿರಲಿಲ್ಲ. ಆದರೆ ಒಂದು ನಿಶ್ಚಿತ ಅವಧಿಯ ತನಕ ಶಿಕ್ಷೆಯನ್ನು ಮುಂದೂಡುತ್ತಾನೆ. ಅವರ ಅವಧಿ ಬಂದರೆ ಒಂದು ಕ್ಷಣವೂ ಹಿಂದೆ ಮುಂದೆ ಆಗುವಂತಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(16,62,'16:62','ಅವರು ತಮಗಿಷ್ಟವಿಲ್ಲದನ್ನು ಅಲ್ಲಾಹನಿಗೆ ನಿಶ್ಚಯಿಸುತ್ತಾರೆ. ಹಾಗಿದ್ದೂ ಒಳಿತು ತಮಗೇ ಎಂದು ಅವರ ನಾಲಗೆಗಳು ಸುಳ್ಳಾಡುತ್ತಿವೆ . ಸತ್ಯವಾಗಿಯೂ ಅವರಿಗಿರುವುದು ನರಕಾಗ್ನಿ ಮಾತ್ರ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅವರು ಎಲ್ಲರಿಗಿಂತಲೂ ಮೊದಲು ಅದಕ್ಕೆ ಪ್ರವೇಶ ಮಾಡುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(16,63,'16:63','(ಓ ಪೈಗಂಬರರೇ,) ಅಲ್ಲಾಹನಾಣೆ! ನಾವು ನಿಮಗಿಂತ ಮೊದಲಿನ ಸಮುದಾಯಗಳಿಗೆ ಸಂದೇಶವಾಹಕರನ್ನು ಕಳುಹಿಸಿದ್ದೇವೆ. ಶೈತಾನನು ಅವರ ದುಷ್ಕರ್ಮಗಳನ್ನು ಅವರಿಗೆ ಮನೋಹರವನ್ನಾಗಿ ಮಾಡಿ ತೋರಿಸಿದನು. ಇಹದಲ್ಲಿ ಆ ಶೈತಾನನೇ ಅವರ ಸಹಾಯಕ. ಅವರಿಗೆ (ಪರಲೋಕದಲ್ಲಿ) ಯಾತನಾಮಯ ಶಿಕ್ಷೆ ಇದೆ.');
INSERT INTO "kannada" VALUES(16,64,'16:64','ಅವರು ಹೊಂದಿರುವ ಭಿನ್ನಾಭಿಪ್ರಾಯಗಳ ವಸ್ತುಸ್ಥಿತಿಯನ್ನು ಅವರಿಗೆ ವಿವರಿಸಲಿಕ್ಕಾಗಿಯೇ ನಾವು ಈ ಗ್ರಂಥವನ್ನು ನಿಮಗೆ ಅವತೀರ್ಣ ಗೊಳಿಸಿರುತ್ತೇವೆ. ಹಾಗೂ ಸತ್ಯವಿಶ್ವಾಸ ಹೊಂದುವ ಜನತೆಗೆ ಮಾರ್ಗದರ್ಶಕ ಹಾಗೂ ಅನುಗ್ರಹವಾಗಿ ಇದು ಅವತೀರ್ಣಗೊಂಡಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(16,65,'16:65','ಅಲ್ಲಾಹನು ಆಕಾಶದಿಂದ ಜಲವನ್ನು ಸುರಿಸಿದನು. ನಿರ್ಜೀವವಾಗಿದ್ದ ಭೂಮಿಯನ್ನು ಆ ಮೂಲಕ ಜೀವಂತಗೊಳಿಸಿದನು. ಸತ್ಯವನ್ನು ಆಲಿಸುವ ಜನತೆಗೆ ಇದರಲ್ಲಿ ಖಂಡಿತ ನಿದರ್ಶನವಿದೆ.');
INSERT INTO "kannada" VALUES(16,66,'16:66','ಜಾನುವಾರುಗಳಲ್ಲಿ ನಿಮಗೆ ಖಂಡಿತ ಪಾಠವಿದೆ. ಅವುಗಳ ಹೊಟ್ಟೆಯೊಳಗಿರುವ ಸೆಗಣಿ ಮತ್ತು ರಕ್ತದ ನಡುವಿನಿಂದ ಶುದ್ಧ ಹಾಲನ್ನು ಕುಡಿ ಯುವವರಿಗೆ ಸುಗಮ ಪೇಯವನ್ನಾಗಿ ನಾವು ನಿಮಗೆ ಕುಡಿಸುತ್ತಿದ್ದೇವೆ .');
INSERT INTO "kannada" VALUES(16,67,'16:67','ಖರ್ಜೂರದ ಮರಗಳಿಂದಲೂ ದ್ರಾಕ್ಷೆಯ ಬಳ್ಳಿಗಳಿಂದಲೂ (ನಾವು ನಿಮಗೆ ಕುಡಿಸುತ್ತೇವೆ.) ಅದರಿಂದ ನೀವು ಮಾದಕ ಪೇಯವನ್ನೂ ಉತ್ತಮ ಆಹಾರವನ್ನೂ ಸಿದ್ಧಪಡಿಸುವಿರಿ . ನಿಶ್ಚಯವಾಗಿಯೂ ಚಿಂತನಾಶೀಲರಿಗೆ ಇದರಲ್ಲಿ ನಿದರ್ಶನವಿದೆ.');
INSERT INTO "kannada" VALUES(16,68,'16:68','‘ಪರ್ವತಗಳಲ್ಲೂ ಮರಗಳಲ್ಲೂ ಮನುಷ್ಯರು ನಿನಗಾಗಿ ನಿರ್ಮಿಸುವ ಗೂಡುಗಳಲ್ಲೂ ಮನೆಗಳನ್ನು ಕಟ್ಟು ಎಂದು ನಿಮ್ಮ ಪ್ರಭುವು ಜೇನು ನೊಣಕ್ಕೆ ದಿವ್ಯ ಸಂದೇಶವನ್ನು ನೀಡಿದನು ');
INSERT INTO "kannada" VALUES(16,69,'16:69','ಆಮೇಲೆ ಎಲ್ಲಾ ವಿಧದ ಫಲಗಳಿಂದ ರಸವನ್ನು ಹೀರು, ನಿನ್ನ ಪ್ರಭು ಸುಗಮ ವಿಧೇಯಗೊಳಿಸಿದ ದಾರಿಗಳಲ್ಲಿ ವಿಧೇಯನಾಗಿ ಚಲಿಸುತ್ತಿರು’ ಆ ನೊಣದ ಹೊಟ್ಟೆಯಿಂದ ವಿವಿಧ ಬಣ್ಣಗಳ ಪೇಯ ಹೊರಡುತ್ತದೆ. ಅದರಲ್ಲಿ ಜನರಿಗೆ ಶಮನವಿದೆ. ನಿಶ್ಚಯವಾಗಿಯೂ ಚಿಂತನಶೀಲರಿಗೆ ಇದರಲ್ಲಿ ನಿದರ್ಶನವಿದೆ.');
INSERT INTO "kannada" VALUES(16,70,'16:70','. ಅಲ್ಲಾಹನು ನಿಮ್ಮನ್ನು ಸೃಷ್ಟಿಸಿದನು. ಆಮೇಲೆ ಅವನು ನಿಮಗೆ ಮರಣ ಕೊಡುತ್ತಾನೆ. ಮತ್ತು ನಿಮ್ಮಲ್ಲಿ ಕೆಲವರು ತಿಳಿದ ಅನಂತರವೂ ಏನೂ ತಿಳಿಯದವರಾಗುವಂತೆ ಅತಿ ವೃದ್ಧಾಪ್ಯಕ್ಕೆ ಮರಳಿಸಲ್ಪಡುತ್ತಾರೆ. ವಾಸ್ತವದಲ್ಲಿ ಅಲ್ಲಾಹನು ಸರ್ವಜ್ಞನೂ ಸರ್ವಶಕ್ತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(16,71,'16:71','ಅಲ್ಲಾಹನು ನಿಮ್ಮ ಪೈಕಿ ಕೆಲವರಿಗೆ ಕೆಲವರಿಗಿಂತ ಸಂಪತ್ತಿನಲ್ಲಿ ಶ್ರೇಷ್ಠತೆಯನ್ನು ದಯಪಾಲಿಸಿದ್ದಾನೆ. ಆದರೆ ಶ್ರೇಷ್ಟತೆ ದೊರೆತವರು ತಮ್ಮ ಸಂಪತ್ತನ್ನು ತಮ್ಮ ದಾಸರಿಗೆ ಬಿಟ್ಟುಕೊಟ್ಟು ಅವರನ್ನು ಅದರಲ್ಲಿ ಸಮಾನಗೊಳಿಸಲಾರರು. ಹಾಗಿದ್ದು ಕೂಡಾ ಅವರು ಅಲ್ಲಾಹನ ಅನುಗ್ರಹಕ್ಕೆ ದ್ರೋಹವೆಸಗುವುದೇ? ');
INSERT INTO "kannada" VALUES(16,72,'16:72','ಅಲ್ಲಾಹನು ನಿಮಗಾಗಿ ನಿಮ್ಮ ವರ್ಗದಿಂದಲೇ ಜೋಡಿಗಳನ್ನುಂಟು ಮಾಡಿದನು ಮತ್ತು ನಿಮ್ಮ ಜೋಡಿಗಳಿಂದ ಅವನು ನಿಮಗೆ ಪುತ್ರರನ್ನೂ ಪೌತ್ರರನ್ನೂ ದಯಪಾಲಿಸಿದನು. ಮತ್ತು ವಿಶಿಷ್ಟ ವಸ್ತುಗಳಿಂದ ಅವನು ನಿಮಗೆ ಸಂಪತ್ತನ್ನು ಕೊಟ್ಟನು. ಆದರೂ ಕೂಡಾ ಅವರು ಅಸತ್ಯದಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸುತ್ತಿದ್ದಾರಲ್ಲ? ಅಲ್ಲಾಹನ ಅನುಗ್ರಹಗಳಿಗೆ ದ್ರೋಹ ಬಗೆಯುತ್ತಾರಲ್ಲ ?');
INSERT INTO "kannada" VALUES(16,73,'16:73','ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಆಕಾಶಗಳಿಂದಾಗಲೀ ಭೂಮಿಯಿಂದಾಗಲೀ ಅವರಿಗೆ ಜೀವನಾಧಾರ ವನ್ನು ಅಧೀನಗೊಳಿಸಿ ಕೊಡದ ಮತ್ತು ಯಾವು ದಕ್ಕೂ ಸಮರ್ಥರಾಗದಂತಹ ಇತರರನ್ನು ಅವರು ಆರಾಧಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(16,74,'16:74','ಆದುದರಿಂದ ಅಲ್ಲಾಹನಿಗೆ ಉಪಮೆಗಳನ್ನು ಕಲ್ಪಿಸಬೇಡಿರಿ. ಅಲ್ಲಾಹನಿಗೆ ತಿಳಿದಿದೆ, ನಿಮಗೆ ತಿಳಿದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(16,75,'16:75','ಪರಾಧೀನನಾದ, ಯಾವುದಕ್ಕೂ ಸಮರ್ಥನಾಗದ (ಸ್ವಂತ ಅಧಿಕಾರವಿಲ್ಲದ) ಒಬ್ಬ ಗುಲಾಮ ಮತ್ತು ನಮ್ಮ ಕಡೆಯಿಂದ ಉತ್ತಮ ಜೀವನಾಧಾರವನ್ನು ನೀಡಿದ್ದು ಅದರಿಂದ ರಹಸ್ಯವಾಗಿಯೂ ಬಹಿರಂಗವಾಗಿಯೂ ಖರ್ಚು ಮಾಡುತ್ತಿರುವ ಇನ್ನೊಬ್ಬ ವ್ಯಕ್ತಿಯನ್ನು ಅಲ್ಲಾಹನು ಉಪಮೆ ಮಾಡಿರುತ್ತಾನೆ. ಇವರಿಬ್ಬರು ಸಮಾನರಾಗಿರುವರೇ? ಸಕಲ ಸ್ತೋತ್ರಗಳೂ ಅಲ್ಲಾಹನಿಗೆ. ಆದರೆ ಹೆಚ್ಚಿನವರು ಅರಿಯುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(16,76,'16:76','ಅಲ್ಲಾಹನು ಇಬ್ಬರು ಪುರುಷರ ಉಪಮೆ ಕೊಡುತ್ತಾನೆ; ಅವರಲ್ಲಿ ಒಬ್ಬನು ಯಾವುದಕ್ಕೂ ಸಮರ್ಥನಲ್ಲದ ಮೂಕನು. ಅವನು ತನ್ನ ಯಜಮಾನನಿಗೆ ಹೊರೆಯಾಗಿದ್ದಾನೆ. ಅವನು ಇವನನ್ನು ಎತ್ತ ಕಳುಹಿಸಿದರೂ ಯಾವ ಒಳಿತನ್ನೂ ಅವನು ತರುವುದಿಲ್ಲ. ಅವನೂ, ಸ್ವತಃ ನೇರಮಾರ್ಗದಲ್ಲಿ ನೆಲೆ ನಿಂತಿದ್ದು ನ್ಯಾಯದ ಆಜ್ಞೆ ಕೊಡುವವನೂ ಸಮಾನರಾಗಿರುವರೇ?');
INSERT INTO "kannada" VALUES(16,77,'16:77','ಭೂಮಿ-ಆಕಾಶಗಳ ಅದೃಶ್ಯ ಜ್ಞಾನ ಅಲ್ಲಾಹನಿಗೆ ಮಾತ್ರವಿದೆ. ಪ್ರಳಯದ ಸಂಭವವು ರೆಪ್ಪೆ ಬಡಿ ಯುವಷ್ಟು ಅಥವಾ ಅದಕ್ಕಿಂತಲೂ ಸುಲಭವಿದೆ. ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಎಲ್ಲ ವಸ್ತುವಿನ ಮೇಲೆ ಪರಮ ಸಮರ್ಥನು.');
INSERT INTO "kannada" VALUES(16,78,'16:78','ನೀವು ಏನೂ ಅರಿಯದ ಸ್ಥಿತಿಯಲ್ಲಿ ಅಲ್ಲಾಹು ನಿಮ್ಮನ್ನು ನಿಮ್ಮ ತಾಯಂದಿರ ಹೊಟ್ಟೆಗಳಿಂದ ಹೊರತಂದನು. ಅವನು ನಿಮಗೆ ಶ್ರವಣ ಶಕ್ತಿಯನ್ನೂ ದೃಷ್ಟಿಗಳನ್ನೂ ಹೃದಯಗಳನ್ನೂ ನೀಡಿ ದನು. ನೀವು ಕೃತಜ್ಞತೆಯುಳ್ಳವರಾಗಲು.');
INSERT INTO "kannada" VALUES(16,79,'16:79','ಬಾನಿನ ಅಂತರಿಕ್ಷದಲ್ಲಿ ನಿಯಂತ್ರಣಕ್ಕೊಳಗಾಗಿರುವ ಹಾರುವ ಹಕ್ಕಿಗಳ ಕಡೆಗೆ ಅವರು ನೋಡಲಿಲ್ಲವೇ? ಅವುಗಳನ್ನು ಅಲ್ಲಾಹನ ಹೊರತು ಇನ್ನಾರೂ ಹವೆಯಲ್ಲಿ ಹಿಡಿದಿರಿಸುವುದಿಲ್ಲ. ಸತ್ಯ ವಿಶ್ವಾಸವಿರಿಸುವವರಿಗೆ ಇದರಲ್ಲಿ ನಿದರ್ಶನ ಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(16,80,'16:80','ಅಲ್ಲಾಹನು ನಿಮಗಾಗಿ ನಿಮ್ಮ ಮನೆಗಳನ್ನು ವಿಶ್ರಾಂತಿಯ ತಾಣವನ್ನಾಗಿ ಮಾಡಿದನು. ಪ್ರಾಣಿಗಳ ಚರ್ಮದಿಂದ ನಿಮಗಾಗಿ ಮನೆಗಳ ನ್ನು ಮಾಡಿದನು. ನೀವು ಪ್ರಯಾಣ ಮಾಡುವ ದಿನ ಮತ್ತು ತಂಗುವ ದಿನ ಅವುಗಳನ್ನು ಅನಾ ಯಾಸ ಬಳಸುತ್ತೀರಿ. ಅಲ್ಲಾಹನು ಅವುಗಳತು ಪ್ಪಳ, ಉಣ್ಣೆ ಮತ್ತು ರೋಮಗಳಿಂದ ಉಪಕರಣ ಗಳನ್ನೂ ಒಂದು ನಿರ್ದಿಷ್ಟ ಅವಧಿಯವರೆಗೆ ನಿಮ್ಮ ಉಪಯೋಗಕ್ಕೆ ಬರುವಂತಹ ವಸ್ತು ಗಳನ್ನೂ ಸೃಷ್ಟಿಸಿದನು.');
INSERT INTO "kannada" VALUES(16,81,'16:81','ಅಲ್ಲಾಹನು ತಾನು ಸೃಷ್ಟಿಸಿದ ವಸ್ತುಗಳಿಂದ ನಿಮಗಾಗಿ ನೆರಳಿನ ವ್ಯವಸ್ಥೆ ಮಾಡಿದನು. ಪರ್ವತ ಗಳಲ್ಲಿ ನಿಮಗಾಗಿ ಅಭಯ ಕೇಂದ್ರಗಳನ್ನು ಮಾಡಿದನು. ನಿಮಗೆ ಉಷ್ಣದಿಂದ ರಕ್ಷಿಸತಕ್ಕ ಉಡುಪನ್ನೂ ಪರಸ್ಪರರೊಳಗಿನ ಆಕ್ರಮಣದಲ್ಲಿ ನಿಮಗೆ ರಕ್ಷಣೆ ನೀಡುವಂತಹ ಕವಚಗಳನ್ನೂ ದಯಪಾಲಿಸಿದನು. ಹೀಗೆ ಅವನು ನಿಮ್ಮ ಮೇಲೆ ತನ್ನ ಅನುಗ್ರಹವನ್ನು ಪೂರ್ತಿಗೊಳಿಸುತ್ತಾನೆ. ನೀವು (ಅವನಿಗೆ) ವಿಧೇಯರಾಗಲೆಂದು.');
INSERT INTO "kannada" VALUES(16,82,'16:82','ಇನ್ನೂ ಅವರು ವಿಮುಖರಾಗುತ್ತಾರಾದರೆ, (ಓ ಪೈಗಂಬರರೇ,) ಸಂದೇಶವನ್ನು ಸುವ್ಯಕ್ತವಾಗಿ ತಲುಪಿಸುವುದಷ್ಟೇ ನಿಮ್ಮ ಹೊಣೆಯಾಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(16,83,'16:83','ಅವರು ಅಲ್ಲಾಹನ ಅನುಗ್ರಹವನ್ನು ಅರಿಯುತ್ತಾರೆ; ಅನಂತರ ಅದನ್ನು ನಿರಾಕರಿಸುತ್ತಾರೆ. ಅವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಕೃತಘ್ನರಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(16,84,'16:84','ಪ್ರತಿಯೊಂದು ಸಮುದಾಯದಿಂದ ಒಬ್ಬೊಬ್ಬ ಸಾಕ್ಷಿಯನ್ನು ನಾವು ಎದ್ದೇಳಿಸುವ ದಿನ. ನಂತರ ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ನೆಪವೊಡ್ಡಲು ಅವಕಾಶ ನೀಡಲಾಗುವುದಿಲ್ಲ. ದೈವಿಕ ತೃಪ್ತಿ ಹುಡುಕಲೂ ಅವಕಾಶ ನೀಡಲಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(16,85,'16:85','ಅಕ್ರಮಿಗಳು ಶಿಕ್ಷೆಯನ್ನು ಕಂಡಾಗ ಅವರಿಂದ ಅದನ್ನು ಸ್ವಲ್ಪವೂ ಹಗುರಗೊಳಿಸಲಾಗದು ಮತ್ತು ಯಾವುದೇ ಕಾಲಾವಕಾಶವನ್ನೂ ಕೊಡಲಾಗದು.');
INSERT INTO "kannada" VALUES(16,86,'16:86','ಬಹುದೇವಾರಾಧನೆ ಮಾಡಿದವರು ತಾವು ನಿಶ್ಚ ಯಿಸಿ ಕೊಂಡಿದ್ದ ಸಹಭಾಗಿಗಳನ್ನು (ಪರಲೋಕ ದಲ್ಲಿ) ಕಂಡಾಗ, ‘ಓ ನಮ್ಮ ಪ್ರಭೂ, ನಿನ್ನನ್ನು ಬಿಟ್ಟು ನಾವು ಆರಾಧಿಸುತ್ತಿದ್ದ ಸಹಭಾಗಿಗಳು ಇವರೇ’ ಎಂದು ಹೇಳುವರು . ಆಗ ಅವರು (ಸಹಭಾಗಿಗಳು) “ನೀವು ಸುಳ್ಳುಗಾರರು” ಎಂಬ ಉತ್ತರವನ್ನು ಅವರಿಗೆ ಕೊಡುವರು.');
INSERT INTO "kannada" VALUES(16,87,'16:87','ಆ ದಿನ ಅವರೆಲ್ಲರೂ ಅಲ್ಲಾಹನೆಡೆಗೆ ಶರಣು ಹೋಗುವರು ಮತ್ತು ಅವರ ಸುಳ್ಳು ಸೃಷ್ಟನೆಗಳೆಲ್ಲ ಅವರನ್ನು ಬಿಟ್ಟು ದೂರವಾಗುವುವು.');
INSERT INTO "kannada" VALUES(16,88,'16:88','ನಿಷೇಧಿಸುವವರಿಗೆ ಮತ್ತು ಅಲ್ಲಾಹನ ಮಾರ್ಗ ದಿಂದ (ಜನರನ್ನು) ತಡೆಯುವವರಿಗೆ ಅವರು ಮಾಡಿದ ಪಿತೂರಿಗಾಗಿ ನಾವು ಶಿಕ್ಷೆಯ ಮೇಲೆ ಶಿಕ್ಷೆ ನೀಡುವೆವು.');
INSERT INTO "kannada" VALUES(16,89,'16:89','ಪ್ರತಿಯೊಂದು ಸಮುದಾಯದಲ್ಲಿ ಅದರ ವಿರುದ್ಧ ಸಾಕ್ಷ್ಯ ಹೇಳುವ ಒಬ್ಬ ಸಾಕ್ಷಿದಾರನನ್ನು ಅದರೊಳಗಿಂದಲೇ ಎಬ್ಬಿಸುವ, ಮತ್ತು ಇವರ ವಿರುದ್ಧ ಸಾಕ್ಷ್ಯ ಹೇಳಲು ನಾವು ನಿಮ್ಮನ್ನು ತರುವದಿನ (ವನ್ನು ಸ್ಮರಿಸಿರಿ). ಎಲ್ಲ ವಸ್ತುವಿನ ಸುವ್ಯಕ್ತ ವಿವರಣೆಯಾಗಿಯೂ ಆಜ್ಞಾನುಸರಣೆ ಮಾಡುವವರಿಗೆ ಮಾರ್ಗದರ್ಶನವಾಗಿಯೂ ಕೃಪೆ ಹಾಗೂ ಸುವಾರ್ತೆಯಾಗಿಯೂ ನಿಮಗೆ ನಾವು ಈ ಗ್ರಂಥವನ್ನು ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(16,90,'16:90','ಅಲ್ಲಾಹನು ನ್ಯಾಯ, ಪರೋಪಕಾರ ಹಾಗೂ ಕುಟುಂಬಸ್ಥರಿಗೆ ಔದಾರ್ಯ ತೋರುವ ಆಜ್ಞೆ ಯನ್ನು ನೀಡುತ್ತಿದ್ದಾನೆ. ಮತ್ತು ಅಶ್ಲೀಲ ಕಾರ್ಯ, ದುರಾಚಾರ, ಅತಿಕ್ರಮದಿಂದ ನಿಮ್ಮನ್ನು ವಿರೋಧಿಸುತ್ತಿದ್ದಾನೆ. ಅವನು ನಿಮಗೆ ಉಪದೇಶ ನೀಡುತ್ತಾನೆ. ನೀವು ಉಪದೇಶ ಸ್ವೀಕರಿಸುವವರಾಗಲು .');
INSERT INTO "kannada" VALUES(16,91,'16:91','ನೀವು ಅಲ್ಲಾಹನ ಹೆಸರಲ್ಲಿ ಕರಾರು ಮಾಡಿದ್ದರೆ ಅದನ್ನು ಪಾಲಿಸಿರಿ. ನೀವು ಅಲ್ಲಾಹನನ್ನು ನಿಮ್ಮ ಮೇಲೆ ಜಾಮೀನು ನಿಲ್ಲಿಸಿಕೊಂಡು ಪ್ರತಿಜ್ಞೆಯನ್ನು ದೃಢಪಡಿಸಿದ ಬಳಿಕ ಅದನ್ನು ಮುರಿಯಬೇಡಿರಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ನಿಮ್ಮ ಸಕಲ ಕರ್ಮ ಗಳನ್ನೂ ಅಲ್ಲಾಹನು ತಿಳಿದಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(16,92,'16:92','ನೂಲು ಹೊಸೆದು ಅದು ಗಟ್ಟಿಯಾದ ಬಳಿಕ ಅದನ್ನು ಪುನಃ ಅರಳೆಯನ್ನಾಗಿ ಬಿಚ್ಚುವ ಒಬ್ಬಳಂತೆ ನೀವಾಗಬಾರದು. ಒಂದು ವಿಭಾಗದವರಿಗಿಂತ ಇನ್ನೊಂದು ವಿಭಾಗದವರು ಹೆಚ್ಚು ಪ್ರಭಾವಿಗಳಾದರೆ (ಅವರೊಡನೆ ಕೈ ಜೋಡಿಸುತ್ತಾ) ನಿಮ್ಮ ಪ್ರತಿಜ್ಞೆಯನ್ನು ನಿಮ್ಮೊಳಗಿನ ಕುಟಿಲತೆಗಾಗಿ ಬಳಸುತ್ತಿರುವಿರಾ? ಅಲ್ಲಾಹನು ಈ ಪ್ರತಿಜ್ಞೆಗಳ ಮೂಲಕ ನಿಮ್ಮನ್ನು ಪರೀಕ್ಷೆಗೊಳಪಡಿಸುತ್ತಾನೆ. ನೀವು ಯಾವ ವಿಷಯದಲ್ಲಿ ಭಿನ್ನಾಭಿಪ್ರಾಯವನ್ನಿರಿಸಿ ಕೊಂಡಿರೋ, ಅದನ್ನು ಪುನರುತ್ಥಾನ ದಿನದಂದು ಅವನು ನಿಮಗೆ ಸ್ಪಷ್ಟಗೊಳಿಸುವನು.');
INSERT INTO "kannada" VALUES(16,93,'16:93','ಅಲ್ಲಾಹನು ಇಚ್ಛಿಸಿದ್ದರೆ ಅವನು ನಿಮ್ಮೆಲ್ಲರನ್ನೂ ಒಂದೇ ಧಾರ್ಮಿಕ ಸಮುದಾಯವನ್ನಾಗಿ ಮಾಡುತ್ತಿದ್ದನು. ಆದರೆ ಅವನು ತಾನಿಚ್ಚಿಸಿದವರನ್ನು ದಾರಿಗೆಡಿಸುತ್ತಾನೆ ಹಾಗೂ ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ಸನ್ಮಾರ್ಗಕ್ಕೆ ಸೇರಿಸುತ್ತಾನೆ. ಖಂಡಿತವಾಗಿಯೂ ನಿಮ್ಮ ಕರ್ಮಗಳ ಬಗ್ಗೆ ನಿಮ್ಮನ್ನು ವಿಚಾರಣೆಗೊಳ ಪಡಿಸಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(16,94,'16:94','ನೀವು ನಿಮ್ಮ ಪ್ರತಿಜ್ಞೆಗಳನ್ನು ಪರಸ್ಪರರನ್ನು ವಂಚಿಸುವ ಮಾರ್ಗವನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳಬೇ ಡಿರಿ. ಹಾಗಾದರೆ (ಇಸ್ಲಾಮಿನಲ್ಲಿ) ನಿಮ್ಮ ಪಾದ ಗಳು ಸ್ಥಿರಗೊಂಡ ಬಳಿಕ ಕಾಲು ಜಾರಿ ಬೀಳುವಿರಿ. ನೀವು ಜನರನ್ನು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ ತಡೆದ ಅಪರಾಧಕ್ಕಾಗಿ ಕೇಡನ್ನು ಅನುಭವಿಸುವಿರಿ. ಹಾಗೂ (ಪರಲೋಕದಲ್ಲಿ) ಘೋರ ಶಿಕ್ಷೆ ಯೂ ನಿಮಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(16,95,'16:95','ಅಲ್ಲಾಹನ ಕರಾರಿನ ಬದಲಿಗೆ ಅಲ್ಪ ಬೆಲೆಯನ್ನು ಖರೀದಿಸಬೇಡಿರಿ. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನ ಬಳಿಯಲ್ಲಿರುವುದು ನಿಮಗೆ ಹೆಚ್ಚು ಉತ್ತಮವಾಗಿದೆ; ನೀವು ಅದನ್ನು ತಿಳಿದವರಾಗಿದ್ದರೆ (ಕರಾರನ್ನು ಮುರಿಯಬೇಡಿರಿ).');
INSERT INTO "kannada" VALUES(16,96,'16:96','ನಿಮ್ಮ ಬಳಿಯಿರುವುದು ಅಳಿದು(ಮುಗಿದು) ಹೋಗಲಿದೆ. ಅಲ್ಲಾಹನ ಬಳಿಯಲ್ಲಿರುವುದು ಮಾತ್ರ ಬಾಕಿ ಉಳಿದಿರುವುದು. ನಿಶ್ಚಯವಾಗಿಯೂ ಕ್ಷಮೆ ಕೈಗೊಂಡವರಿಗೆ ಅವರ ಪ್ರತಿಫಲವನ್ನು ಅವರ ಉತ್ತಮ ಕರ್ಮಗಳಿಗನುಸಾರ ನಾವು ನೀಡುವೆವು.');
INSERT INTO "kannada" VALUES(16,97,'16:97','ಪುರುಷನಾಗಿರಲಿ ಸ್ತ್ರೀಯಾಗಿರಲಿ ಯಾವನು ಸತ್ಯ ವಿಶ್ವಾಸಿಯಾಗಿದ್ದುಕೊಂಡು ಸತ್ಕರ್ಮವೆಸಗುವನೋ ಅವನಿಗೆ ನಾವು ಪರಿಶುದ್ಧ ಜೀವ ನವನ್ನು ದಯ ಪಾಲಿಸುವೆವು. ಮತ್ತು ಅಂತಹವರಿಗೆ ಅವರ ಉತ್ತಮ ಕರ್ಮಗಳಿಗನುಸಾರ ಪ್ರತಿಫಲ ನೀಡುವೆವು .');
INSERT INTO "kannada" VALUES(16,98,'16:98','ನೀವು ಖುರ್‍ಆನನ್ನು ಪಠಿಸುವುದಾದರೆ ದೈವಿಕ ಅವಕೃಪೆಗೊಳಗಾದ ಶೈತಾನನಿಂದ ಅಲ್ಲಾಹನಲ್ಲಿ ಅಭಯ ಯಾಚಿಸಿಕೊಳ್ಳಿರಿ .');
INSERT INTO "kannada" VALUES(16,99,'16:99','ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸುವವರ ಹಾಗೂ ತಮ್ಮ ಪ್ರಭುವಿನ ಮೇಲೆ ಭರವಸೆಯನ್ನಿರಿಸುವವರ ಮೇಲೆ ಅವನಿಗೆ (ಶೈತಾನನಿಗೆ) ಯಾವುದೇ ಅಧಿಕಾರ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(16,100,'16:100','ಅವನ ಅಧಿಕಾರವಿರುವುದು ಅವನನ್ನು ತಮ್ಮ ಸಂರಕ್ಷಕನನ್ನಾಗಿ ಮಾಡಿಕೊಳ್ಳುವವರ ಮೇಲೆ ಮತ್ತು ಅಲ್ಲಾಹನಿಗೆ ಸಹಭಾಗಿಗಳನ್ನಾಗಿ ಮಾಡುವವರ ಮೇಲೆ ಮಾತ್ರ.');
INSERT INTO "kannada" VALUES(16,101,'16:101','ನಾವು ಒಂದು ವೇದವಾಕ್ಯದ ಸ್ಥಾನದಲ್ಲಿ ಇನ್ನೊಂದು ವೇದವಾಕ್ಯವನ್ನು ಬದಲಿಸಿದಾಗ ಏನನ್ನು ಅವತೀರ್ಣಗೊಳಿಸಬೇಕೆಂದು ಅಲ್ಲಾಹನೇ ಚೆನ್ನಾಗಿ ಬಲ್ಲವನಾಗಿರುವಾಗ “ನೀನೇ ಸೃಷ್ಟಿಸಿ ಹೇಳುತ್ತಿರುವೆ” ಎಂದು ಇವರು (ಪ್ರವಾದಿಯವರಲ್ಲಿ) ಹೇಳುತ್ತಾರೆ. ಅವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಅರಿತಿರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(16,102,'16:102','ಹೇಳಿರಿ; ವಿಶ್ವಾಸವಿರಿಸಿದವರನ್ನು ಅಚಲ ಗೊಳಿಸಲಿಕ್ಕಾಗಿಯೂ ವಿಧೇಯರಾಗಿ ಬಾಳುವವರಿಗೆ ಮಾರ್ಗದರ್ಶನ ಹಾಗೂ ಸುವಾರ್ತೆಯಾಗಿಯೂ ಇದನ್ನು ಪವಿತ್ರಾತ್ಮನು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಸಸತ್ಯ ಅವತೀರ್ಣಗೊಳಿಸಿದನು.');
INSERT INTO "kannada" VALUES(16,103,'16:103','ಈ ವ್ಯಕ್ತಿಗೆ (ಪ್ರವಾದಿಗೆ) ಒಬ್ಬನು ಕಲಿಸಿ ಕೊಡು ತ್ತಾನೆಂದು ನಿಮ್ಮ ಕುರಿತು ಅವರು ಹೇಳುತ್ತಾರೆಂದು ನಮಗೆ ಖಂಡಿತ ಗೊತ್ತಿದೆ. ಅವರು ಯಾರ ಕಡೆಗೆ ಬೊಟ್ಟು ಮಾಡುತ್ತಿರುವರೋ ಅವನ ಭಾಷೆ ಅನರಬಿ! ಈ ಖುರ್‍ಆನಾದರೋ ಸುಸ್ಪಷ್ಟ ಅರಬೀ ಭಾಷೆಯಾಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(16,104,'16:104','ಅಲ್ಲಾಹನ ನಿದರ್ಶನಗಳಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸದವರನ್ನು ಅಲ್ಲಾಹನು ಸನ್ಮಾರ್ಗಕ್ಕೆ ಸೇರಿಸುವುದಿಲ್ಲ. ಇಂತಹವರಿಗೆ ಯಾತನಾಮಯ ಶಿಕ್ಷೆ ಇದೆ.');
INSERT INTO "kannada" VALUES(16,105,'16:105','ಅಲ್ಲಾಹನ ನಿದರ್ಶನಗಳಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸದವರೇ ಸುಳ್ಳು ಸೃಷ್ಟಿ ಮಾಡುತ್ತಿದ್ದಾರೆ. ನಿಜಕ್ಕೂ ಸುಳ್ಳುಗಾರರು ಅವರೇ!');
INSERT INTO "kannada" VALUES(16,106,'16:106','ನಂಬಿದ ಬಳಿಕ ಯಾವನು ಅಲ್ಲಾಹನಲ್ಲಿ ಅವಿಶ್ವಾಸ ತಾಳುತ್ತಾನೋ (ಅವನಿಗೆ ಘೋರ ತಾಕೀತು ಇದೆ). ಆದರೆ ತಮ್ಮ ಹೃದಯವು ನಂಬಿಕೆಯಲ್ಲಿ ಶಾಂತವಾಗಿದ್ದು ಬಲಾತ್ಕರಿಸಲ್ಪಟ್ಟವನ ಹೊರತು. ಆದರೆ ತೆರೆದ ಮನಸ್ಸಿನಲ್ಲಿ ಅವಿ ಶ್ವಾಸ ಕೈಗೊಂಡವರ ಮೇಲೆ ಅಲ್ಲಾಹನಕೋಪವಿದೆ. ಅವರಿಗೆ ಘೋರ ಶಿಕ್ಷೆಯೂ ಇದೆ.');
INSERT INTO "kannada" VALUES(16,107,'16:107','ಇದೇಕೆಂದರೆ ಅವರು ಇಹಲೋಕ ಜೀವನ ವನ್ನು ಪರಲೋಕಕ್ಕಿಂತ ಮಿಗಿಲಾಗಿ ಪ್ರೀತಿಸಿದರು. ಅಲ್ಲಾಹನು ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಸನ್ಮಾರ್ಗ ನೀಡುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(16,108,'16:108','ಅವರೇ ಹೃದಯಗಳಿಗೂ ಶ್ರವಣಗಳಿಗೂ ದೃಷ್ಟಿಗಳ ಮೇಲೂ ಅಲ್ಲಾಹು ಮುದ್ರೆ ಹಾಕಿದಂತಹವರು. ಅವರೇ ಅಶೃದ್ಧರು.');
INSERT INTO "kannada" VALUES(16,109,'16:109','ಸಂಶಯವಿಲ್ಲ. ಅವರೇ ಪರಲೋಕದಲ್ಲಿ ನಷ್ಟ ಹೊಂದುವವರು .');
INSERT INTO "kannada" VALUES(16,110,'16:110','ತರುವಾಯ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಅನುಗ್ರಹವು ಪೀಡನೆಗೊಳಗಾದ ನಂತರ ಹಿಜ್‍ರಾ ಹೊರಟು ಆನಂತರ ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಹೋರಾಡಿದ ಮತ್ತು ಸಹನೆ ಪಾಲಿಸಿದವರಿಗೆ ಇರುವುದು. ಅದರ ನಂತರ ಖಂಡಿತ ನಿನ್ನ ಪ್ರಭುವು ಕ್ಷಮಾ ದಾನಿಯೂ ಅನುಗ್ರಹಿಯೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(16,111,'16:111','ಪ್ರತಿಯೊಬ್ಬರೂ ತನಗಾಗಿ ವಾದಿಸುತ್ತಾ ಬರುವ ಹಾಗೂ ಪ್ರತಿಯೊಬ್ಬರಿಗೂ ಕರ್ಮಫಲವನ್ನು ಕೊಡುವ ದಿನದಂದು ಯಾರ ಮೇಲೂ ಅಕ್ರಮವಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(16,112,'16:112','ಸುರಕ್ಷಿತ ಹಾಗೂ ಶಾಂತವಾದ ಒಂದು ನಗರ ವನ್ನು ಅಲ್ಲಾಹು ಉದಾಹರಣೆ ಮಾಡುತ್ತಾನೆ. ಎಲ್ಲಾ ಕಡೆಗಳಿಂದಲೂ ಅದಕ್ಕೆ ಹೇರಳ ಜೀವ ನಾಧಾರ ದೊರಕುತ್ತಿದೆ. ಆಮೇಲೆ ಅದು ಅಲ್ಲಾಹನ ಅನುಗ್ರಹಗಳಿಗೆ ದ್ರೋಹ ಬಗೆಯಿತು. ಆಗ ಅಲ್ಲಾಹನು ಅದರ ನಿವಾಸಿಗಳಿಗೆ ಅವರ ದುಷ್ಕøತ್ಯಗಳ ಫಲವಾಗಿ ಹಸಿವೆ ಮತ್ತು ಭಯದ ವಸ್ತ್ರವನ್ನು ತೊಡಿಸಿದನು.');
INSERT INTO "kannada" VALUES(16,113,'16:113','ನಿಜವಾಗಿಯೂ ಅವರ ಕೂಟದಿಂದಲೇ ಓರ್ವ ದೂತರು ಅವರ ಬಳಿಗೆ ಬಂದರು. ಆದರೆ, ಅವರು ಅವರನ್ನು ಸುಳ್ಳಾಗಿಸಿದರು. ಕೊನೆಗೆ ಅವರು ಅಕ್ರಮಿಗಳಾಗಿದ್ದ ಸ್ಥಿತಿಯಲ್ಲಿ ಶಿಕ್ಷೆಯು ಅವರನ್ನು ಹಿಡಿಯಿತು .');
INSERT INTO "kannada" VALUES(16,114,'16:114','ಆದುದರಿಂದ ಅಲ್ಲಾಹನು ನಿಮಗೆ ದಯ ಪಾಲಿಸಿರುವ ಧರ್ಮಬದ್ಧ ಹಾಗೂ ಶುದ್ಧವಾದ ಆಹಾರವನ್ನು ಭುಜಿಸಿರಿ. ಮತ್ತು ಅಲ್ಲಾಹನ ಅನುಗ್ರಹಕ್ಕೆ ಕೃತಜ್ಞತೆ ಸಲ್ಲಿಸಿರಿ. ನೀವು ಅವನನ್ನೇ ಆರಾಧಿಸುವವರಾಗಿದ್ದರೆ.');
INSERT INTO "kannada" VALUES(16,115,'16:115','ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹನು ನಿಮಗೆ ನಿಷಿದ್ಧ ಗೊಳಿಸಿರುವುದು ಶವ, ರಕ್ತ, ಹಂದಿ ಮಾಂಸ ಮತ್ತು ಅಲ್ಲಾಹು ಅಲ್ಲದವರ ನಾಮ ಉಚ್ಛರಿಸಲ್ಪಟ್ಟದ್ದನ್ನು. ಆದರೆ ಯಾರಾದರೂ (ತಿನ್ನಲು) ನಿರ್ಭಂಧಿತನಾದರೆ, ಅವನು ಅದರ ಅಪೇಕ್ಷೆ ಇಲ್ಲದವನೂ ಮೇರೆ ಮೀರದವನೂ ಆಗಿದ್ದರೆ, ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಕ್ಷಮಾಶೀಲನೂ ಕೃಪಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(16,116,'16:116','ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಳ್ಳಾರೋಪದ ಪ್ರೇರಣೆಯಿಂದ ನಿಮ್ಮ ನಾಲಗೆಗಳು ಸುಳ್ಳನ್ನು ಬಣ್ಣಿಸುತ್ತಿರುವ ದೆಸೆಯಿಂದಾಗಿ ‘ಇದು ಹಲಾಲ್, ಇದು ಹರಾಮ್’ ಎಂದು ನೀವು ಹೇಳದಿರಿ. ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಳ್ಳು ಸೃಷ್ಟಿಸುವವರು ಯಶಸ್ವಿಯಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(16,117,'16:117','ಅವರ ಸುಖ ಭೋಗ ಕೇವಲ ಅಲ್ಪಾವಧಿಯದು. ಅವರಿಗೆ (ಪರಲೋಕದಲ್ಲಿ) ಯಾತನಾಮಯ ಶಿಕ್ಷೆ ಇದೆ.');
INSERT INTO "kannada" VALUES(16,118,'16:118','ಮುಂಚೆ ನಾವು ನಿಮಗೆ ವಿವರಿಸಿದ ವಸ್ತುಗಳನ್ನು ಯಹೂದಿಯರಿಗೆ ನಿಷಿದ್ಧಗೊಳಿಸಿದ್ದೆವು. ನಾವು ಅವರ ಮೇಲೆ ಅಕ್ರಮಮಾಡಿರಲಿಲ್ಲ. ಆದರೆ ಅವರೇ ಸ್ವತಃ ತಮ್ಮ ಮೇಲೆ ಅಕ್ರಮ ವೆಸಗಿಕೊಂಡರು.');
INSERT INTO "kannada" VALUES(16,119,'16:119','ಮತ್ತೆ ನಿನ್ನ ಪ್ರಭುವು ಅಜ್ಞಾನದಿಂದಾಗಿ ದುಷ್ಕ ರ್ಮಗಳನ್ನು ಮಾಡಿ ಅನಂತರ ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು ಮರಳಿ ಕರ್ಮಗಳನ್ನು ಸರಿಪಡಿಸಿಕೊಂಡವರಿಗೆ ನಿಶ್ಚಯವಾಗಿಯೂ ನಿನ್ನ ಪ್ರಭು ಅದರ ನಂತರ (ಅಜ್ಞಾನ ಮತ್ತು ಪಶ್ಚಾತ್ತಾಪದ ಅನಂತರ) ಕ್ಷಮಾಶೀಲನೂ ದಯಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(16,120,'16:120','ಖಂಡಿತ ಇಬ್‍ರಾಹೀಮರು ಅಲ್ಲಾಹನ ಅಜ್ಞಾ ನುವರ್ತಿಯಾದ, ಸನ್ಮಾರ್ಗದಲ್ಲಿ (ತಪ್ಪದೆ) ನೆಲೆಗೊಳ್ಳುವ ಒಂದು ಸಮುದಾಯವಾಗಿದ್ದರು. ಅವರು ಮುಶ್ರಿಕರ ಕೂಟದವರಾಗಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(16,121,'16:121','ಅವರು ಅಲ್ಲಾಹನ ಅನುಗ್ರಹಗಳಿಗೆ ಕೃತಜ್ಞತೆ ಸಲ್ಲಿಸುವವರಾಗಿದ್ದರು. ಅಲ್ಲಾಹನು ಅವರನ್ನು ಆಯ್ದುಕೊಂಡನು ಮತ್ತು ನೇರ ಮಾರ್ಗಕ್ಕೆ ಕೊಂಡೊಯ್ದನು .');
INSERT INTO "kannada" VALUES(16,122,'16:122','ಇಹಲೋಕದಲ್ಲಿ ಅವರಿಗೆ ನಾವು ಶ್ರೇಯಸ್ಸನ್ನು ದಯಪಾಲಿಸಿದೆವು ಮತ್ತು ಪರಲೋಕದಲ್ಲಿ ಅವರು ನಿಶ್ಚಯವಾಗಿಯೂ ಸಜ್ಜನರಲ್ಲಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(16,123,'16:123','ಅನಂತರ ನೀವು ಸನ್ಮಾರ್ಗದಲ್ಲಿ ಅಚಲರಾದ ಇಬ್‍ರಾಹೀಮರ ಪಥವನ್ನು ಅನುಸರಿಸಲಿ ಕ್ಕಾಗಿ ನಾವು ನಿಮ್ಮ ಕಡೆಗೆ ದಿವ್ಯಸಂದೇಶ ಕಳುಹಿಸಿದೆವು. ಅವರು ಬಹುದೇವವಿಶ್ವಾಸಿಗಳಲ್ಲಾಗಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(16,124,'16:124','‘ಸಬ್ತ್’ ದಿನಾಚರಣೆಯನ್ನು ನಿಶ್ಚಯಿಸಲಾಗಿರುವುದು ಅದರ ವಿಧಿಗಳ ಕುರಿತು ಭಿನ್ನಾ ಭಿಪ್ರಾಯ ತಾಳಿದವರ ಮೇಲೆಯೇ ಆಗಿದ್ದಿತು. ಅವರು ಭಿನ್ನಾಭಿಪ್ರಾಯ ಹೊಂದಿದ್ದ ವಿಷಯದಲ್ಲಿ ನಿಮ್ಮ ಪ್ರಭು ಪುನರುತ್ಥಾನ ದಿನ ಅವರ ನಡುವೆ ತೀರ್ಮಾನಿಸಿ ಬಿಡುವನು.');
INSERT INTO "kannada" VALUES(16,125,'16:125','(ಓ ಪೈಗಂಬರರೇ,) ಯುಕ್ತಿ ಹಾಗೂ ಸದುಪದೇಶದ ಮೂಲಕ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಮಾರ್ಗಕ್ಕೆ ಜನರನ್ನು ಆಹ್ವಾನಿಸಿರಿ. ಅವರೊಂದಿಗೆ ಅತ್ಯುತ್ತಮ ರೀತಿಯಿಂದ ಸಂವಾದ ನಡೆಸಿರಿ, ನಿಮ್ಮ ಪ್ರಭು ತನ್ನ ಮಾರ್ಗದಿಂದ ಭ್ರಷ್ಟರಾದವರ ಬಗ್ಗೆ ಚೆನ್ನಾಗಿ ಬಲ್ಲನು. ನೇರ ಮಾರ್ಗದಲ್ಲಿದ್ದವರ ಬಗ್ಗೆಯೂ ನಿಮ್ಮ ಪ್ರಭು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(16,126,'16:126','ನೀವು ಪ್ರತೀಕಾರವೆಸಗುವುದಿದ್ದರೆ ನಿಮ್ಮ ಮೇಲೆ ಅತಿರೇಕವೆಸಗಲಾಗಿದ್ದಷ್ಟು ಮಾತ್ರ ಪ್ರತೀಕಾರ ಎಸಗಿರಿ. ಆದರೆ ನೀವು ತಾಳ್ಮೆವಹಿಸಿದರೆ ನಿಶ್ಚಯವಾಗಿಯೂ ಅದುವೇ ಸಹನ ಶೀಲರಿಗೆ ಅತ್ಯುತ್ತಮ .');
INSERT INTO "kannada" VALUES(16,127,'16:127','(ಓ ಪೈಗಂಬರರೇ,) ಸಹನೆ ಪಾಲಿಸಿರಿ. ನಿಮ್ಮ ಸಹನೆಯು ಅಲ್ಲಾಹನ ಅನುಗ್ರಹದಿಂದಲೇ ಸಾಧ್ಯವಾಗಿದೆ. ಸತ್ಯನಿಷೇಧಿಗಳ ಬಗ್ಗೆ ವ್ಯಥೆಗೊಳ್ಳದಿರಿ. ಮತ್ತು ಅವರ ಕುತಂತ್ರಗಳಿಂದ ಎದೆ ಗುಂದಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(16,128,'16:128','ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಧರ್ಮ ನಿಷ್ಠರೊಂದಿಗೂ ಸದ್ಭಕ್ತರೊಂದಿಗೂ ಇರುವನು .');
INSERT INTO "kannada" VALUES(17,1,'17:1','ತನ್ನ ದಾಸನನ್ನು ಒಂದು ರಾತ್ರಿಯಲ್ಲಿ ಮಸ್ಜಿದುಲ್ ಹರಾಮ್‍ನಿಂದ , ನಾವು ಪರಿಸರವನ್ನು ಅನುಗ್ರಹಿತ ಗೊಳಿಸಿರುವಂತಹ ಅಖ್ಸಾ ಮಸೀದಿಯ ಕಡೆಗೆ ನಿಶಾಯಾನ ಮಾಡಿಸಿದವನು ಪರಮ ಪಾವನನು. ನಮ್ಮ ಕೆಲವು ದೃಷ್ಟಾಂತಗಳನ್ನು ಅವರಿಗೆ ನಾವು ತೋರಿಸಿಕೊಡಲಿಕ್ಕಾಗಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅವನು (ಅಲ್ಲಾಹು) ಸರ್ವಶ್ರುತನೂ ಸರ್ವವೀಕ್ಷಕನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(17,2,'17:2','ಮೂಸಾರವರಿಗೆ ನಾವು ಗ್ರಂಥ ನೀಡಿದ್ದೆವು. ನನ್ನ ಹೊರತು ಇನ್ನಾವ ಕಾರ್ಯ ನಿರ್ವಾಹಕನನ್ನೂ ನೀವು ಸ್ವೀಕರಿಸಬೇಡಿರಿ ಎಂಬ ಆಜ್ಞೆಯೊಂದಿಗೆ ಅದನ್ನು ಬನೀಇಸ್ರಾಈಲ ಸಂತತಿಗಳ ಸನ್ಮಾರ್ಗ ದರ್ಶನವನ್ನಾಗಿ ಮಾಡಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(17,3,'17:3','ನೂಹರ ಸಂಗಡ ನಾವು ನಾವೆಯಲ್ಲಿ ಹತ್ತಿಸಿದ್ದವರ ಸಂತತಿಗಳನ್ನು. ನಿಜಕ್ಕೂ ನೂಹ್ ಓರ್ವ ಕೃತಜ್ಞ ದಾಸರಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(17,4,'17:4','ನಾವು ಇಸ್ರಾಈಲ ಸಂತತಿಗಳಿಗೆ ಈ ರೀತಿ ವಿಧಿಸಿದ್ದೆವು; ನೀವು ಎರಡು ಬಾರಿ ಭೂಮಿಯಲ್ಲಿ ಗೊಂದಲವನ್ನುಂಟುಮಾಡುವಿರಿ ಹಾಗೂ ಭಾರೀ ಮೇಲ್ಮೆಯನ್ನು ತೋರುವಿರಿ ಎಂದು .');
INSERT INTO "kannada" VALUES(17,5,'17:5','ಹಾಗೆ ಆ ಎರಡು ಸಂದರ್ಭಗಳ ಪೈಕಿ ಒಂದನೆಯದಕ್ಕೆ ನಿಶ್ಚಯಿಸಲಾದ (ಶಿಕ್ಷೆಯ) ಸಮಯವಾದರೆ ಉಗ್ರ ಪರಾಕ್ರಮಿಶಾಲಿಗಳಾದ ನಮ್ಮ ಕೆಲವು ದಾಸರನ್ನು ನಿಮ್ಮ ವಿರುದ್ಧ ಎಬ್ಬಿಸಿದೆವು. ಅವರು ನಿಮ್ಮ ನಾಡುಗಳಿಗೆ ನುಗ್ಗಿ ನಿಮ್ಮನ್ನು ಹುಡುಕುತ್ತಾ ಹೋದರು. ಇದು ನೆರವೇರಲಾಗುವ ಒಂದು ವಾಗ್ದಾನವಾಗಿತ್ತು.');
INSERT INTO "kannada" VALUES(17,6,'17:6','ಅನಂತರ ನಾವು ನಿಮಗೆ ಅವರ ಮೇಲೆ ಪ್ರಾಬಲ್ಯವನ್ನು ಮರಳಿಸಿ ಕೊಟ್ಟೆವು. ನಿಮ್ಮನ್ನು ಸಂಪತ್ತು ಹಾಗೂ ಸಂತತಿಗಳಿಂದ ಅಭಿವೃದ್ಧಿ ಗೊಳಿಸಿದೆವು. ನಿಮ್ಮನ್ನು ಹೆಚ್ಚು ಸಂಖ್ಯಾ ಬಲವುಳ್ಳವರಾಗಿ ಮಾಡಿದೆವು.');
INSERT INTO "kannada" VALUES(17,7,'17:7','ನೀವು ಸತ್ಕರ್ಮ ಮಾಡಿದ್ದರೆ ಅದು ನಿಮ್ಮ ಹಿತಕ್ಕಾಗಿಯೇ ಮಾಡಿದ್ದೀರಿ, ನೀವು ದುಷ್ಕರ್ಮ ವೆಸಗಿದ್ದರೆ ಅದರ ಕೇಡು ನಿಮಗೇ ಇರುವುದು. ತರುವಾಯ ಎರಡನೆಯ ವಾಗ್ದಾನದ (ಶಿಕ್ಷೆಯ) ಕಾಲ ಬಂದಾಗ ನಿಮ್ಮ ಮುಖಗಳನ್ನು ಭಂಗಗೊಳಿಸುವಂತೆಯೂ ಮೊದಲ ಬಾರಿಗೆ ಆರಾಧನಾಲಯಕ್ಕೆ ಪ್ರವೇ ಶಿಸಿದಂತೆ ಪುನಃ ಪ್ರವೇಶಿಸುವಂತೆಯೂ ಅವರು ಜೈಸಿದ್ದನ್ನೆಲ್ಲ ನಾಶಗೊಳಿಸುವಂತೆಯೂ (ನಾವು ನಿಮ್ಮ ಮೇಲೆ ಶತ್ರುಗಳನ್ನು ಹೇರಿಬಿಟ್ಟೆವು.)');
INSERT INTO "kannada" VALUES(17,8,'17:8','ನಿಮ್ಮ ಪ್ರಭು ನಿಮ್ಮ ಮೇಲೆ ದಯೆತೋರಲೂಬಹುದು. ಆದರೆ ನೀವು ಪುನರಾವರ್ತಿಸಿದರೆ ನಾವು (ನಮ್ಮ ಶಿಕ್ಷೆಯನ್ನು) ಪುನರಾವರ್ತಿಸುವೆವು. ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ನಾವು ನರಕವನ್ನು ಸೆರೆ ಮನೆಯನ್ನಾಗಿ ಮಾಡಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(17,9,'17:9','ವಾಸ್ತವದಲ್ಲಿ ಈ ಖುರ್‍ಆನ್ ಅತ್ಯಂತ ನೇರ ಮಾರ್ಗವನ್ನು ತೋರಿಸಿಕೊಡುತ್ತದೆ. ಸತ್ಕರ್ಮವೆಸಗಿದ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ದೊಡ್ಡ ಸತ್ಫಲವಿದೆಯೆಂಬ ಸುವಾರ್ತೆಯನ್ನು ನೀಡುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(17,10,'17:10','ಪರಲೋಕದಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸದವರು ಯಾರೋ, ನಾವು ಅವರಿಗಾಗಿ ವೇದನಾಜನಕ ಶಿಕ್ಷೆಯನ್ನು ಸಿದ್ಧಗೊಳಿಸಿದ್ದೇವೆ ಎಂದು (ತಿಳಿಸುತ್ತದೆ.)');
INSERT INTO "kannada" VALUES(17,11,'17:11','ಮಾನವನು ಒಳಿತಿಗೆ ಪ್ರಾರ್ಥಿಸುವಂತೆ ಕೆಡುಕಿಗೂ ಪ್ರಾರ್ಥಿಸುತ್ತಾನೆ. ಮನುಷ್ಯನು ಮಹಾ ದುಡುಕು ಸ್ವಭಾವದವನು.');
INSERT INTO "kannada" VALUES(17,12,'17:12','ಇರುಳು ಮತ್ತು ಹಗಲನ್ನು ನಾವು ಎರಡು ನಿದ ರ್ಶನಗಳಾಗಿ ಮಾಡಿರುತ್ತೇವೆ. ನೀವು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಅನುಗ್ರಹವನ್ನು ಅರಸಿಕೊಳ್ಳಲಾಗುವಂ ತೆಯೂ ಸಂವತ್ಸರಗಳ ಹಾಗೂ ಸಮಯಗಳ ಗಣನೆಯನ್ನು ಮಾಡಿಕೊಳ್ಳುವಂತೆಯೂ ನಾವು ಇರುಳಿನ ನಿದರ್ಶನವನ್ನು ಮಬ್ಬಾಗಿಯೂ ಹಗಲಿನ ನಿದರ್ಶನವನ್ನು ಪ್ರಕಾಶಮಾನವಾಗಿಯೂ ಮಾಡಿದೆವು. ನಾವು ಪ್ರತಿಯೊಂದು ವಸ್ತುವನ್ನೂ ಚೆನ್ನಾಗಿ ನಿರೂಪಿಸಿಟ್ಟಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(17,13,'17:13','ಪ್ರತಿಯೊಬ್ಬ ಮನುಷ್ಯನ ಶಕುನವನ್ನು ನಾವು ಅವನ ಕೊರಳಿಗೇ ಕಟ್ಟಿರುತ್ತೇವೆ. ಪುನರುತ್ಥಾನ ದಿನದಂದು ನಾವು ಅವನಿಗಾಗಿ ಒಂದು ಗ್ರಂಥ ವನ್ನು ಹೊರಡಿಸುವೆವು. ಅವನು ಅದನ್ನು ತೆರೆದಂತೆ ಕಾಣುವನು.');
INSERT INTO "kannada" VALUES(17,14,'17:14','ನಿನ್ನ ಕರ್ಮಗ್ರಂಥವನ್ನು ಓದು. ಇಂದು ನಿನ್ನ ಲೆಕ್ಕ ತೆಗೆಯಲು ನೀನೇ ಸಾಕು. (ಎಂದು ಅವನಿಗೆ ಹೇಳಲಾಗುವುದು)');
INSERT INTO "kannada" VALUES(17,15,'17:15','ಯಾರು ಸನ್ಮಾರ್ಗ ಸ್ವೀಕರಿಸಿಕೊಳ್ಳುವನೋ ಅವ ನು ತನ್ನ ಸ್ವಂತ ಒಳಿತಿಗಾಗಿ ಸನ್ಮಾರ್ಗ ಸ್ವೀಕರಿಸಿದನು. ಯಾರು ಪಥಭ್ರಷ್ಟನಾಗುವನೋ, ಅವನು ತನ್ನ ಮೇಲೆ ಕೇಡಾಗಿ ಪಥಭ್ರಷ್ಟನಾದನು. ಪಾಪದ ಹೊರೆ ಹೊರುವ ಯಾವನೂ ಇನ್ನೊಬ್ಬನ ಹೊರೆಯನ್ನು ಹೊರಲಾರನು ಂ. ಓರ್ವ ದೂತ ನನ್ನು ಕಳುಹಿಸಿಕೊಡುವವರೆಗೆ ನಾವು ಯಾರ ನ್ನೂ ಶಿಕ್ಷಿಸುವವರಲ್ಲ ಃ.');
INSERT INTO "kannada" VALUES(17,16,'17:16','ನಾವು ಒಂದು ನಾಡನ್ನು ನಾಶಗೊಳಿಸಲಿಚ್ಛಿಸಿ ದಾಗ ಅಲ್ಲಿನ ಸುಖಲೋಲುಪರಿಗೆ (ಸರ ದಾರರಿಗೆ) ಅಪ್ಪಣೆ ಕೊಡುತ್ತೇವೆ. ಆದರೆ ಅವರು ಆ ಅಣತಿಯ ವಿಚಾರದಲ್ಲಿ ಆಜ್ಞೋಲ್ಲಂಘನೆ ಮಾಡುತ್ತಾರೆ. ಆಗ (ಯಾತನೆಯ) ವಚನವು ಆ ನಾಡಿನ ಮೇಲೆ ನಿಜವಾಗುತ್ತದೆ. ನಾವು ಅದನ್ನು ಸಂಪೂರ್ಣ ನಾಶಮಾಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(17,17,'17:17','ನೂಹರ ಅನಂತರ ಅದೆಷ್ಟು ತಲೆಮಾರುಗಳನ್ನು ನಾವು ನಾಶಗೊಳಿಸಿದ್ದೇವೆ. ತನ್ನ ದಾಸರ ಪಾಪ ಗಳ ಬಗೆಗೆ ಸೂಕ್ಷ್ಮವಾಗಿ ಅರಿಯಲಿಕ್ಕೂ ಸರ್ವ ವನ್ನು ವೀಕ್ಷಿಸಲಿಕ್ಕೂ ನಿನ್ನ ಪ್ರಭುವೇ ಸಾಕು.');
INSERT INTO "kannada" VALUES(17,18,'17:18','ಯಾರಾದರೂ ಇಹದಲ್ಲೇ ತ್ವರಿತ ಗತಿಯ ಫಲವನ್ನು ಉದ್ದೇಶವಿಟ್ಟರೆ ತ್ವರಿತ ಫಲವನ್ನು ಯಾರಿಗೆ ಕೊಡಬೇಕೆಂದು ನಾವು ಇಚ್ಛಿಸುವೆವೋ ಅವನಿಗೆ ಕೊಡುವೆವು. ಅನಂತರ ಅವನಿಗೆ ನಾವು ನರಕವನ್ನು ನಿಶ್ಚಯಿಸುತ್ತೇವೆ. ಅದರಲ್ಲಿ ಅವನು ನಿಂದ್ಯನಾಗಿ ಹಾಗೂ (ದೇವಕೃಪೆಯಿಂದ) ಬಹಿಷ್ಕøತನಾಗಿ ಸೇರಿ ಬಿಡುವನು.');
INSERT INTO "kannada" VALUES(17,19,'17:19','ಯಾರಾದರೂ ಪರಲೋಕವನ್ನು ಬಯಸಿದರೆ ಮತ್ತು ಸತ್ಯವಿಶ್ವಾಸಿಯಾಗಿದ್ದು ಕೊಂಡು ಅದಕ್ಕಾಗಿ ಶ್ರಮಿಸಬೇಕಾದ ರೀತಿಯಲ್ಲಿ ಶ್ರಮ ಪಟ್ಟರೆ ಅಂಥವರ ಪರಿಶ್ರಮವು ಕೃತಜ್ಞಾರ್ಹವಾಗುವುದು.');
INSERT INTO "kannada" VALUES(17,20,'17:20','ಇವರಿಗೂ ಅವರಿಗೂ ಎಲ್ಲರಿಗೂ ನಾವು ನಿನ್ನ ಪ್ರಭುವಿನ ಕೊಡುಗೆಯಿಂದ ನೆರವಾಗುತ್ತೇವೆ. ನಿನ್ನ ಪ್ರಭುವಿನ ಕೊಡುಗೆಯು ಯಾರಿಂದಲೂ ತಡೆದಿರಿಸಲ್ಪಡುವಂತಹದ್ದಲ್ಲ.');
INSERT INTO "kannada" VALUES(17,21,'17:21','ನಾವು ಅವರಲ್ಲಿ ಕೆಲವರಿಗೆ ಕೆಲವರಿಗಿಂತ ಹೇಗೆ ಶ್ರೇಷ್ಟತೆ ಪ್ರದಾನ ಮಾಡಿರುತ್ತೇವೆ ಎಂಬುದನ್ನು ನೋಡಿರಿ. ಪರಲೋಕವು ಸ್ಥಾನದಲ್ಲಿ ಅತ್ಯಂತ ಘನವೆತ್ತುದು. ಅತ್ಯಂತ ಅಮೋಘವಾದುದು.');
INSERT INTO "kannada" VALUES(17,22,'17:22','ನೀನು ಅಲ್ಲಾಹನೊಂದಿಗೆ ಬೇರಾರನ್ನೂ ಆರಾಧ್ಯನಾಗಿ ಮಾಡದಿರು. ಅನ್ಯಥಾ ನಿಂದ್ಯನಾಗಿ, ಅಸಹಾಯಕನಾಗಿ ಕುಳಿತುಕೊಳ್ಳುವಿ.');
INSERT INTO "kannada" VALUES(17,23,'17:23','ನೀವು ಕೇವಲ ಅವನೊಬ್ಬನಿಗೇ ಹೊರತು ಇನ್ನಾರಿಗೂ ಖಂಡಿತ ಆರಾಧಿಸಬಾರದು, ಮಾತಾ ಪಿತರೊಡನೆ ಸೌಜನ್ಯದಿಂದ ವರ್ತಿಸಿರಿ ಎಂದು ನಿನ್ನ ಪ್ರಭು ವಿಧಿಸಿರುವನು. ಅವರ ಪೈಕಿ ಒಬ್ಬರು ಅಥವಾ ಅವರಿಬ್ಬರೂ ವೃದ್ಧರಾಗಿ ನಿನ್ನ ಬಳಿಯಲ್ಲಿದ್ದರೆ, ಅವರಿಗೆ `ಛೆ’ ಎಂದು ಹೇಳದಿರು. ಅವರನ್ನು ಜರೆಯದಿರು. ಅವರೊಂದಿಗೆ ಗೌರವ ಪೂರ್ವಕವಾಗಿ ಮಾತನಾಡು.');
INSERT INTO "kannada" VALUES(17,24,'17:24','ಕರುಣೆಯೊಂದಿಗೆ ವಿನಯದ ರೆಕ್ಕೆಯನ್ನು ಅವರ ಮುಂದೆ ತಗ್ಗಿಸಿರಿ. ಓ ನನ್ನ ಪ್ರಭೂ, ಇವರು ನನ್ನ ಚಿಕ್ಕಂದಿನಲ್ಲಿ (ದಯಾ ವಾತ್ಸಲ್ಯಗಳಿಂದ) ನನ್ನನ್ನು ಸಾಕಿ ಸಲಹಿದಂತೆಯೇ ನೀನು ಅವರ ಮೇಲೆ ಕೃಪೆ ತೋರು ಎಂದು ಪ್ರಾರ್ಥಿಸು.');
INSERT INTO "kannada" VALUES(17,25,'17:25','ನಿಮ್ಮ ಅಂತರಂಗದಲ್ಲಿರುವುದನ್ನು ನಿಮ್ಮ ಪ್ರಭು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು. ನೀವು ಸಜ್ಜನರಾಗಿದ್ದರೆ, (ದೇವಾನುಸರಣೆಯ ಕಡೆಗೆ) ಆಗ್ರಹಪೂರ್ವಕ ಮರಳಿ ಬರುವವರನ್ನು ಅವನು ಕ್ಷಮಿಸುವವನಾ ಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(17,26,'17:26','ಸಂಬಂಧಿಕನಿಗೆ ಅವನ ಹಕ್ಕನ್ನೂ ಬಡವರಿಗೂ ಪ್ರಯಾಣಿಕನಿಗೂ ಅವನವನ ಹಕ್ಕನ್ನೂ ಕೊಡು. ದುಂದುವೆಚ್ಚ ಮಾಡದಿರು.');
INSERT INTO "kannada" VALUES(17,27,'17:27','ದುಂದುಗಾರರು ಶೈತಾನನ ಸೋದರರಾಗಿರುತ್ತಾರೆ. ಶೈತಾನನು ತನ್ನ ಪ್ರಭುವಿಗೆ ಅತ್ಯಂತ ಕೃತಘ್ನನು. ');
INSERT INTO "kannada" VALUES(17,28,'17:28','ನಿನ್ನ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ನೀನು ಆಗ್ರಹಿಸುತ್ತಿರುವ ಕೃಪೆಯನ್ನು ಅರಸುತ್ತ ನಿನಗೆ ಅವರಿಂದ ತಪ್ಪಿಸಿಕೊಳ್ಳಬೇಕೆಂದಾದರೆ, ಅವರಿಗೆ ನಯವಾದ ಮಾತನ್ನೇ ಹೇಳು.');
INSERT INTO "kannada" VALUES(17,29,'17:29','ನಿನ್ನ ಕೈಯನ್ನು ಕೊರಳಿಗೆ ಕಟ್ಟಿ ಹಾಕಬೇಡ ಮತ್ತು ಅದನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ತೆರೆದಿಡಲೂ ಬೇಡ. ಹಾಗಾದರೆ ನೀನು ನಿಂದ್ಯನೂ ನಿರ್ಗತಿಕನೂ ಆಗುವಿ.');
INSERT INTO "kannada" VALUES(17,30,'17:30','ನಿನ್ನ ಪ್ರಭು ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಜೀವನಾಧಾರವನ್ನು ವಿಶಾಲಗೊಳಿಸುತ್ತಾನೆ. ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಸಂಕುಚಿತಗೊಳಿಸುತ್ತಾನೆ. ಅವನು ತನ್ನದಾಸರ ಬಗ್ಗೆ ಸೂಕ್ಷ್ಮವಾಗಿ ಅರಿತವನೂ ಅವರ ಮೇಲೆ ನಿಗಾ ಇಟ್ಟವನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(17,31,'17:31','ಬಡತನದ ಭೀತಿಯಿಂದ ನಿಮ್ಮ ಮಕ್ಕಳನ್ನು ಕೊಲ್ಲಬೇಡಿರಿ. ನಾವು ಅವರಿಗೂ ನಿಮಗೂ ಆಹಾರ ನೀಡುವೆವು. ವಾಸ್ತವದಲ್ಲಿ ಅವರ ವಧೆಯು ಒಂದು ಘೋರ ಅಪರಾಧವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(17,32,'17:32','ನೀವು ವ್ಯಭಿಚಾರದ ಹತ್ತಿರ ಸುಳಿಯಬೇಡಿರಿ. ಅದು ಅತಿಹೀನ ಕಾರ್ಯ ಮತ್ತು ಅತ್ಯಂತ ಕೆಟ್ಟ ಮಾರ್ಗ.');
INSERT INTO "kannada" VALUES(17,33,'17:33','ಅಲ್ಲಾಹನು ಮಾನ್ಯತೆ ಕಲ್ಪಿಸಿರುವ ಜೀವವನ್ನು ನ್ಯಾಯದಿಂದಲ್ಲದೆ ಕೊಲ್ಲಬೇಡಿರಿ. ಯಾರಾದರೂ ಅಕ್ರಮವಾಗಿ ವಧಿಸಲ್ಪಟ್ಟರೆ ಅವನ ವಾರೀಸುದಾರನಿಗೆ ಪ್ರತೀಕಾರ ಪಡೆಯುವ ಅಧಿಕಾರನ್ನು ನಾವು ನೀಡಿರುತ್ತೇವೆ. ಆದರೆ ಅವನು ಪ್ರತೀಕಾರದ ಕೊಲೆಯ ವಿಚಾರದಲ್ಲಿ ಮಿತಿಮೀರಬಾರದು. ನಿಶ್ಚ ಯವಾಗಿಯೂ ಅವನಿಗೆ ಸಹಾಯ ಮಾಡಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(17,34,'17:34','ಅನಾಥನ ಸೊತ್ತನ್ನು ಅತ್ಯಂತ ಉತ್ತಮ ರೀತಿಯಿಂದಲ್ಲದೆ ಸಮೀಪಿಸಬಾರದು. ಅವನು ಪ್ರಾಪ್ತ ವಯಸ್ಸಿಗೆ ತಲುಪುವ ತನಕ. ಮತ್ತು ನೀವು ಕರಾರನ್ನು ಪಾಲಿಸಿರಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ಕರಾರಿನ ಕುರಿತು ನೀವು ವಿಚಾರಣೆಗೊಳಗಾಗುವಿರಿ.');
INSERT INTO "kannada" VALUES(17,35,'17:35','ಅಳತೆ ಮಾಡಿ ಕೊಡುವಾಗ ಪೂರ್ಣ ಅಳೆದು ಕೊಡಿರಿ. ಸರಿಯಾದ ತಕ್ಕಡಿಯಲ್ಲಿ ತೂಗಿರಿ. ಇದು ಒಳ್ಳೆಯ ಕ್ರಮ ಮತ್ತು ಪರಿಣಾಮದ ದೃಷ್ಟಿಯಿಂದಲೂ ಇದೇ ಉತ್ತಮ.');
INSERT INTO "kannada" VALUES(17,36,'17:36','ನಿನಗೆ ಜ್ಞಾನವಿಲ್ಲದಂತಹ ವಿಚಾರದ ಬೆನ್ನು ಹತ್ತದಿರು. ನಿಶ್ಚಯವಾಗಿಯೂ ಶ್ರವಣ, ದೃಷ್ಟಿ ಮತ್ತು ಮನಸ್ಸು ಇವೆಲ್ಲವುಗಳ ವಿಚಾರಣೆ ನಡೆಯಲಿದೆ.');
INSERT INTO "kannada" VALUES(17,37,'17:37','ಭೂಮಿಯ ಮೇಲೆ ದರ್ಪದಿಂದ ನಡೆಯದಿರು. ನೀನು ಭೂಮಿಯನ್ನು ಸೀಳಲಾರೆ ಮತ್ತು ಪರ್ವತಗಳ ಎತ್ತರಕ್ಕೂ ತಲುಪಲಾರೆ.');
INSERT INTO "kannada" VALUES(17,38,'17:38','ಇದೆಲ್ಲವೂ ನಿನ್ನ ಪ್ರಭುವಿನ ಬಳಿ ಅಪ್ರಿಯವಾದ ಕೆಡುಕುಗಳು.');
INSERT INTO "kannada" VALUES(17,39,'17:39','ನಿಮ್ಮ ಪ್ರಭು ನಿಮ್ಮ ಕಡೆಗೆ ಬೋಧನೆ ನೀಡಿದ ಯುಕ್ತಿ ಪೂರ್ಣ ಮಾತುಗಳಿವು. ಅಲ್ಲಾಹನೊಂದಿಗೆ ಇನ್ನಾವ ಆರಾಧ್ಯನನ್ನು ಮಾಡಿಕೊಳ್ಳಬೇಡ. ಅನ್ಯಥಾ ನೀನು ನಿಂದ್ಯನಾಗಿ (ಅಲ್ಲಾಹನ ಕೃಪೆಯಿಂದ) ಬಹಿಷ್ಕøತನಾಗಿ ನರಕದಲ್ಲಿ ತಳ್ಳಲ್ಪಡುವೆ.');
INSERT INTO "kannada" VALUES(17,40,'17:40','ನಿಮ್ಮ ಪ್ರಭು ನಿಮಗೆ ಪುತ್ರರನ್ನು ವಿಶೇಷವಾಗಿ ಕೊಟ್ಟನೆ? ಮತ್ತು ತನಗಾಗಿ ದೇವಚರರನ್ನು ಪುತ್ರಿಯರನ್ನಾಗಿ ಮಾಡಿಕೊಂಡನೇ? ನಿಶ್ಚಯವಾಗಿಯೂ ನೀವು ಒಂದು ಘೋರ ಮಾತನ್ನಾಡುತ್ತಿರುವಿರಿ. ');
INSERT INTO "kannada" VALUES(17,41,'17:41','ಅವರು ಜಾಗೃತರಾಗಲೆಂದು ನಾವು ಈ ಖುರ್‍ಆನ್‍ನಲ್ಲಿ ವಿಧವಿಧವಾಗಿ ವಿವರಿಸಿದ್ದೇವೆ. ಆದರೆ ಅದು ಅವರಿಗೆ ಸತ್ಯದಿಂದ ಪಲಾಯನ (ಜಿಗುಪ್ಸೆ)ವನ್ನಲ್ಲದೆ ಹೆಚ್ಚಿಸಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(17,42,'17:42','(ಓ ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ; ಇವರು ಹೇಳುವ ಪ್ರಕಾರ ಅಲ್ಲಾಹನ ಸಂಗಡ ಇತರ ದೇವರೂ ಇರುತ್ತಿದ್ದರೆ ಅರ್ಶ್‍ನ ಅಧಿಪತಿಯ ಕಡೆಗೆ ಅವರು (ಇತರ ದೇವರು) ಯಾವುದಾದರೂ ಮಾರ್ಗವನ್ನು ಹುಡುಕುತ್ತಿದ್ದರು .');
INSERT INTO "kannada" VALUES(17,43,'17:43','ಅವನೆಷ್ಟು ಪರಿಶುದ್ಧನು! ಇವರು ಹೇಳುತ್ತಿರುವ ಮಾತುಗಳಿಂದ ಮೀರಿ ನಿಂತ ಅವನು ಬಹಳ ಘನವೆತ್ತ ಔನ್ನತ್ಯವನ್ನು ಪಡೆದಿರುವನು.');
INSERT INTO "kannada" VALUES(17,44,'17:44','ಸಪ್ತಗಗನಗಳೂ ಭೂಮಿಯೂ ಅವುಗಳಲ್ಲಿರುವ ಸಕಲವೂ ಅವನ ಪಾವಿತ್ರ್ಯವನ್ನು ಕೊಂಡಾಡು ತ್ತಿವೆ. ಅವನ ಸ್ತುತಿಯೊಂದಿಗೆ ಅವನ ಪಾವಿತ್ರ್ಯ ವನ್ನು ಕೊಂಡಾಡದ ಯಾವ ವಸ್ತುವೂ ಇಲ್ಲ. ಆದರೆ ನೀವು ಅವುಗಳ ಕೀರ್ತನೆಯನ್ನು ಗ್ರಹಿಸು ವುದಿಲ್ಲ. ವಾಸ್ತವದಲ್ಲಿ ಅವನು ಅತ್ಯಂತ ಸಹನ ಶೀಲನೂ ಕ್ಷಮಾ ದಾನಿಯೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(17,45,'17:45','ನೀವು ಖುರ್‍ಆನ್ ಪಠಿಸುವಾಗ ನಿಮ್ಮ ಮತ್ತು ಪರಲೋಕದಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸದವರ ನಡುವೆ ಅದೃಶ್ಯ ಮರೆಯೊಂದನ್ನು ನಾವು ಹಾಕಿ ಬಿಡುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(17,46,'17:46','ಅವರು ಏನನ್ನೂ ಗ್ರಹಿಸದಂತಾಗಲು ಅವರ ಹೃದಯಗಳ ಮೇಲೆ ಮುಚ್ಚಲವನ್ನು ಮತ್ತು ಅವರ ಕಿವಿಗಳಲ್ಲಿ ಭಾರವನ್ನು (ಗಂಟನ್ನು) ಹಾಕಿ ಬಿಡುತ್ತೇವೆ . ನೀವು ಖುರ್‍ಆನ್‍ನಲ್ಲಿ ಕೇವಲ ನಿಮ್ಮ ಪ್ರಭುವನ್ನು ಮಾತ್ರ ಪ್ರಸ್ತಾಪಿಸುವಾಗ ಅವರು ಜಿಗುಪ್ಸೆಯಿಂದ ಬೆನ್ನು ತಿರುಗಿಸಿ ಹೋಗುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(17,47,'17:47','ಅವರು ಕಿವಿಗೊಟ್ಟು ಆಲಿಸುವಾಗ ನಿಜಕ್ಕೂ ಅವರು ಏನನ್ನಾಲಿಸುತ್ತಾರೆಂಬುದನ್ನೂ ಅವರು ಪರಸ್ಪರ ಗುಪ್ತ ಮಾತುಗಳನ್ನಾಡುತ್ತಿರುವ ಸಂದರ್ಭದಲ್ಲಿ; “ಓರ್ವ ಮಾಟ ಬಾಧಿತ ವ್ಯಕ್ತಿಯನ್ನೇ ಹೊರತು ನೀವು ಹಿಂಬಾಲಿಸುತ್ತಿಲ್ಲ” ಎಂದೂ ಈ ಅಕ್ರಮಿಗಳು ಪರಸ್ಪರ ಹೇಳಿಕೊಳ್ಳುವ ಸಂದರ್ಭವನ್ನೂ ನಾವು ಚೆನ್ನಾಗಿ ಬಲ್ಲೆವು.');
INSERT INTO "kannada" VALUES(17,48,'17:48','ನೋಡಿರಿ, ಇವರು ನಿಮ್ಮ ಮೇಲೆ ಹೇಗೆ ಉಪಮೆ ಗಳನ್ನು ಕಟ್ಟುತ್ತಾರೆ? ಹೀಗೆ ಇವರು ಪಥಭ್ರಷ್ಟರಾದರು. ಆದ್ದರಿಂದ ಇವರಿಗೆ ಯಾವುದೇ ದಾರಿ ಹಿಡಿಯಲು ಸಾಧ್ಯವಾಗುತ್ತಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(17,49,'17:49','ಅವರು ಹೇಳಿದರು; ನಾವು ಬರೇ ಎಲುಬುಗಳಾಗಿ ಮತ್ತು ಅವಶೇಷಗಳಾಗಿ ಬಿಟ್ಟ ಮೇಲೆ ನಮ್ಮ ನ್ನು ಮತ್ತೆ ಹೊಸದಾಗಿ ಹುಟ್ಟಿಸಿ ಎಬ್ಬಿಸಲಾಗುವುದೇ?');
INSERT INTO "kannada" VALUES(17,50,'17:50','(ಪ್ರವಾದಿಯರೇ) ಹೇಳಿರಿ; `ನೀವು ಶಿಲೆ ಅಥವಾ ಕಬ್ಬಿಣವೇ ಆಗಿರಿ.');
INSERT INTO "kannada" VALUES(17,51,'17:51','ಅಥವಾ ನಿಮ್ಮ ಮನಸ್ಸುಗಳಲ್ಲಿ ಬಹಳ ದೊಡ್ಡ ವಿಷಯವಾಗಿ ತೋರುವ ಯಾವುದೇ ಸೃಷ್ಟಿಯಾ ಗಿರಿ (ಆಗಲೂ ನೀವು ಪುನರುಜ್ಜೀವನ ಗೊಳಿಸಲ್ಪಡುವಿರಿ.) ಆಗ ನಮ್ಮನ್ನು ಪುನಃ ಜೀವಂತಗೊಳಿ ಸುವವನಾರು? ಎಂದು ಅವರು ಕೇಳುವರು. ‘ನಿಮ್ಮನ್ನು ಪ್ರಥಮ ಬಾರಿ ಸೃಷ್ಟಿಸಿದವನೇ’ ಎಂದು ಹೇಳಿರಿ. ಆಗ ಅವರು ನಿಮ್ಮೆಡೆಗೆ (ನೋಡಿ) ತಲೆಯಾಡಿಸುತ್ತ ‘ಅದು (ಪುನರು ತ್ಥಾನ) ಯಾವಾಗ?’ ಎಂದು ಕೇಳುವರು. ನೀವು ಹೇಳಿರಿ. ‘ಅದು ಹತ್ತಿರವೇ ಇರಬಹುದು’.');
INSERT INTO "kannada" VALUES(17,52,'17:52','ಅವನು ನಿಮ್ಮನ್ನು ಕೂಗಿ ಕರೆಯುವ ಮತ್ತು ನೀವು ಅವನನ್ನು ಸ್ತುತಿಸುತ್ತಾ ಅವನ ಕರೆಗೆ ಓಗೊಡುವ ದಿನ. (ಆಗ) ತುಸು ಹೊತ್ತು ಮಾತ್ರವೇ (ಇಹಲೋಕದಲ್ಲಿ) ನೀವು ನೆಲೆಸಿದ್ದೀರಿ ಎಂದು ಭಾವಿಸುವಿರಿ.');
INSERT INTO "kannada" VALUES(17,53,'17:53','(ಓ ಪೈಗಂಬರರೇ,) ನನ್ನ ದಾಸರೊಡನೆ ಹೇಳಿರಿ; ಅವರು ಅತ್ಯುತ್ತಮ ಮಾತನ್ನೇ ಆಡಲಿ. ವಸ್ತುತಃ ಶೈತಾನನು ಅವರ ನಡುವೆ ಪಿತೂರಿ ಎಬ್ಬಿಸುತ್ತಾನೆ. ಖಂಡಿತ ಶೈತಾನನು ಮಾನವನ ಪ್ರತ್ಯಕ್ಷ ಶತ್ರುವಾಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(17,54,'17:54','ನಿಮ್ಮ ಪ್ರಭು ನಿಮ್ಮನ್ನು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು. ಅವನು ಇಚ್ಛಿಸಿದರೆ ನಿಮ್ಮ ಮೇಲೆ ಕೃಪೆ ತೋರುವನು. ಇಚ್ಛಿಸಿದರೆ ಶಿಕ್ಷಿಸುವನು. (ಓ ಪೈಗಂಬರರೇ) ನಾವು ನಿಮ್ಮನ್ನು ಅವರ ಮೇಲೆ ಮೇಲ್ನೋಟಗಾರನಾಗಿ ಮಾಡಿ ಕಳುಹಿಸಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(17,55,'17:55','ನಿಮ್ಮ ಪ್ರಭು ಆಕಾಶಗಳಲ್ಲಿ ಮತ್ತು ಭೂಮಿಯಲ್ಲಿ ರುವವರ ಬಗ್ಗೆ ಚೆನ್ನಾಗಿ ಅರಿಯುತ್ತಾನೆ. ನಾವು ಕೆಲವು ಪ್ರವಾದಿಗಳಿಗೆ ಬೇರೆ ಕೆಲವರಿಗಿಂತ ಶ್ರೇಷ್ಟ ತೆಯನ್ನು ನೀಡಿದ್ದೇವೆ. ದಾವೂದರಿಗೆ ನಾವು ಝಬೂರನ್ನು ಕೊಟ್ಟಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(17,56,'17:56','(ಪ್ರವಾದಿಯವರೇ) ಹೇಳಿರಿ, `ನೀವು ಯಾರನ್ನು ಅಲ್ಲಾಹನ ಹೊರತು (ಆರಾಧ್ಯರೆಂದು) ವಾದಿಸು ತ್ತೀರೊ ಅವರನ್ನು ಕರೆದು ನೋಡಿರಿ. ಅವರು ನಿಮ್ಮ ಯಾವ ಸಂಕಷ್ಟವನ್ನೂ ನೀಗಿಸುವ ಮತ್ತು (ಇತರರಿಗೆ) ಬದಲಿಸುವ ಶಕ್ತಿಯನ್ನು ಅಧೀನ ಗೊಳಿಸಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(17,57,'17:57','ಇವರು ಯಾರನ್ನು (ಆರಾಧ್ಯರೆಂದು) ಕರೆಯುತ್ತಿರುವರೋ ಅವರ ಪೈಕಿ ಅಲ್ಲಾಹನಿಗೆ ಅತ್ಯಂತ ನಿಕಟರೂ ಕೂಡಾ ತಮ್ಮ ಪ್ರಭುವಿನೆಡೆಗೆ (ಅನು ಸರಣೆಯ ಮೂಲಕ) ಸಾಮಿಪ್ಯವನ್ನು ಅರಸುತ್ತಾರೆ. ಅವರು ಅವನ ಕೃಪೆಯನ್ನು ಆಗ್ರಹಿಸುವವರೂ ಅವನ ಶಿಕ್ಷೆಯನ್ನು (ಇತರರಂತೆ) ಭಯ ಪಡುವವರೂ ಆಗಿರುತ್ತಾರೆ. ವಾಸ್ತವದಲ್ಲಿ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಶಿಕ್ಷೆಯು ಭಯಪಡಬೇಕಾದುದೇ ಆಗಿದೆ.');
INSERT INTO "kannada" VALUES(17,58,'17:58','ಪುನರುತ್ಥಾನದ ದಿನಕ್ಕಿಂತ ಮುಂಚೆ ನಾವು ನಾಶ ಗೊಳಿಸದ ಅಥವಾ ಕಠಿಣವಾಗಿ ಶಿಕ್ಷಿಸದ ನಾಡೊಂದೂ ಇಲ್ಲ. ಇದು ಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತಗೊಂಡಿದೆ.');
INSERT INTO "kannada" VALUES(17,59,'17:59','ಮುಂಚಿನವರು ನಿದರ್ಶನಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದ್ದ ರೆಂಬುದರ ಹೊರತು ನಮ್ಮನ್ನು ಆ ನಿದರ್ಶನಗಳ ನ್ನು ಕಳುಹಿಸದಂತೆ ತಡೆಯಲಿಲ್ಲ. ನಾವು ಸಮೂದರಿಗೆ ಪ್ರತ್ಯಕ್ಷ ನಿದರ್ಶನವಾಗಿ ಒಂದು ಒಂಟೆಯನ್ನು ನೀಡಿದೆವು. ಅವರು ಅದರ ಮೇಲೆ ಅಕ್ರಮವೆಸಗಿದರು. ನಾವು ನಿದರ್ಶನಗಳನ್ನು ಕಳುಹಿಸುವುದು (ಜನರಿಗೆ) ಭಯಹುಟ್ಟಿ (ನಂಬಿ) ಸಲು ಮಾತ್ರವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(17,60,'17:60','(ಓ ಪೈಗಂಬರರೇ,) ನಿಮ್ಮ ಪ್ರಭು ಜನರನ್ನು ಸುತ್ತುವರಿದಿದ್ದಾನೆಂದು ನಾವು ನಿಮ್ಮೊಡನೆ ಹೇಳಿದ್ದುದನ್ನು ಸ್ಮರಿಸಿರಿ. ನಾವು ನಿಮಗೆ ತೋರಿಸಿಕೊಟ್ಟ ಆ (ನಿಶಾಯಾನದ) ದರ್ಶನವನ್ನೂ ಖುರ್‍ಆನ್‍ನಲ್ಲಿ ಶಪಿಸಿರುವ (ಝಖ್ಹೂಮ್) ವೃಕ್ಷವನ್ನೂ ನಾವು ಅವರಿಗೊಂದು ಪರೀಕ್ಷಾ ಸಾಧನವನ್ನಾಗಿ ಮಾಡಿದ್ದೇವೆ. ನಾವು ಅವರನ್ನು ಭಯಪಡಿಸುತ್ತೇವೆ. ಆದರೆ, ಅದು (ಭಯಪಡಿಸು ವಿಕೆಯು) ಅವರ ಧಿಕ್ಕಾರವನ್ನಲ್ಲದೆ ಇನ್ನೇನನ್ನೂ ಹೆಚ್ಚಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(17,61,'17:61','ನೀವು `ಆದಮರಿಗೆ ಸಾಷ್ಟಾಂಗವೆರಗಿರಿ’ ಎಂದು ನಾವು ದೇವಚರರೊಡನೆ ಹೇಳಿದ ಸಂದರ್ಭ (ಸ್ಮರಣೀಯ). ಆಗ ಅವರು ಸಾಷ್ಟಾಂಗವೆರಗಿದರು. ಇಬ್ಲೀಸನ ಹೊರತು. ಅವನು ‘ನೀನು ಕೊಜೆ ಮಣ್ಣಿನಿಂದ ಸೃಷ್ಟಿಸಿದವನ ಮಂದೆ ನಾನು ಸಾಷ್ಟಾಂಗವೆರಗಬೇಕೆ ?’ ಎಂದು ಕೇಳಿದನು.');
INSERT INTO "kannada" VALUES(17,62,'17:62','. (ಮುಂದುವರಿಯುತ್ತ) ‘ನೀನು ನನಗಿಂತ ಹೆಚ್ಚು ಗೌರವಿಸಿದ ಇವನು ಯಾರೆಂದು ನನಗೆ ತಿಳಿಸಿಕೊಡು, ನೀನು ನನಗೆ ಪುನರುತ್ಥಾನದ ದಿನದವರೆಗೆ ಕಾಲಾವಕಾಶ ಕೊಟ್ಟರೆ ನಾನು ಇವನ ಸಂತತಿಯನ್ನು ಮೂಲೋತ್ಪಾಟನೆಗೊಳಿಸಿ ಬಿಡುವೆನು. ಸ್ವಲ್ಪ ಮಂದಿಯ ಹೊರತು’ ಎಂದು ಹೇಳಿದನು .');
INSERT INTO "kannada" VALUES(17,63,'17:63','ಅಲ್ಲಾಹ್ ಹೇಳಿದನು; ‘ಹೋಗು, ಅವರ ಪೈಕಿ ಯಾರೆಲ್ಲ ನಿನ್ನನ್ನು ಅನುಸರಿಸುವರೋ ನಿಮಗೆಲ್ಲರಿಗೂ ನರಕವೇ ಪ್ರತಿಫಲ. ಹೌದು, ಪರಿಪೂರ್ಣ ಪ್ರತಿಫಲ.');
INSERT INTO "kannada" VALUES(17,64,'17:64','ನಿನ್ನ ಸ್ವರದಿಂದ ನಿನಗೆ ಸಾಧ್ಯವಿರುವವರನ್ನೆಲ್ಲ ಹುರಿದುಂಬಿಸು. ಅವರ ಮೇಲೆ ನಿನ್ನ ಅಶ್ವಪಡೆ ಯನ್ನೂ ಕಾಲಾಳು ಸೇನೆಯನ್ನೂ ಕೂಗಿ ಎಳೆದು ತಾ. ಸಂಪತ್ತು ಮತ್ತು ಸಂತಾನಗಳಲ್ಲಿ ಅವರೊಂದಿಗೆ ಭಾಗಿಯಾಗು. ಮತ್ತು ಅವರಿಗೆ ವಾಗ್ದಾನಗಳನ್ನು ಕೊಡು. ಶೈತಾನನು ಅವರಿಗೆ ವಂಚನೆಯ ವಿನಾ ವಾಗ್ದಾನ ಮಾಡಲಾರ.');
INSERT INTO "kannada" VALUES(17,65,'17:65','ನಿಶ್ಚಯವಾಗಿಯೂ ನನ್ನ ದಾಸರ ಮೇಲೆ ನಿನಗೇನೂ ಅಧಿಕಾರ ಪ್ರಾಪ್ತವಾಗದು. ಅವರ ಕಾರ್ಯನಿ ರ್ವಾಹಕನಾಗಿ ನಿನ್ನ ಪ್ರಭುವೇ ಸಾಕು.');
INSERT INTO "kannada" VALUES(17,66,'17:66','ನಿಮಗಾಗಿ ಸಮುದ್ರದಲ್ಲಿ ನಾವೆಯನ್ನು ನಡೆಸು ವವನೇ ನಿಮ್ಮ ಪ್ರಭು. ನೀವು ಅವನ ಔದಾರ್ಯ ದಿಂದ ಅರಸಲಿಕ್ಕಾಗಿ. ವಾಸ್ತವದಲ್ಲಿ ಅವನು ನಿಮ್ಮ ಮೇಲೆ ಅತ್ಯಂತ ಕರಣೆಯುಳ್ಳವನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(17,67,'17:67','ಸಮುದ್ರದಲ್ಲಿ ನಿಮಗೆ ಗಂಡಾಂತರ ಎದುರಾದರೆ, ಅವನೊಬ್ಬನ ಹೊರತು ಇತರ ಯಾರನ್ನೆಲ್ಲ ನೀವು ಕೂಗಿ ಆರಾಧಿಸುತ್ತಿದ್ದಿರೋ ಅವರೆಲ್ಲ ಮಾಯವಾಗುವರು. ಆದರೆ ಅವನು ನಿಮ್ಮನ್ನು ರಕ್ಷಿಸಿ ದಡಕ್ಕೆ ಸೇರಿಸಿದಾಗ ನೀವು (ಏಕದೇವ ವಿಶ್ವಾಸದಿಂದ) ವಿಮುಖರಾಗುತ್ತೀರಿ. ಮಾನವನು ಮಹಾಕೃತಘ್ನನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(17,68,'17:68','ಅಲ್ಲಾಹ್ ನಿಮ್ಮನ್ನು ನೆಲದಲ್ಲೇ ಹೂತು ಬಿಡುವ ಅಥವಾ ನಿಮ್ಮ ಮೇಲೆ ಕಲ್ಲಿನ ಮಳೆಗೆರೆಯುವ ಬಗ್ಗೆ ಮತ್ತು ನೀವು ನಿಮಗಾಗಿ ಯಾವುದೇ ರಕ್ಷಕನನ್ನು ಪಡೆಯಲಾರಿರಿ ಎಂಬ ಬಗ್ಗೆ ನೀವು ನಿರ್ಭಯರಾಗಿರುವಿರಾ?');
INSERT INTO "kannada" VALUES(17,69,'17:69','ಅಥವಾ ಅಲ್ಲಾಹ್ ನಿಮ್ಮನ್ನು ಇನ್ನೊಂದು ಬಾರಿ ಸಮುದ್ರಕ್ಕೆ ಮರಳಿ ಕೊಂಡುಹೋಗಿ ಆಮೇಲೆ ಪ್ರಚಂಡ ಬಿರುಗಾಳಿಯನ್ನು ಕಳುಹಿಸಿ ನಿಮ್ಮ ಕೃತಘ್ನತೆಗೆ ಪ್ರತಿಫಲವಾಗಿ ನಿಮ್ಮನ್ನು ಮುಳುಗಿಸಿ ಬಿಡುವ ಹಾಗೂ ತರುವಾಯ ಈ ಕಾರ್ಯದ ಬಗ್ಗೆ ನಿಮ್ಮ ಪರವಾಗಿ ನಮ್ಮನ್ನು ವಿಚಾರಣೆ ನಡೆಸತಕ್ಕ ಸಹಾಯಕನಾರೂ ನಿಮಗೆ ಸಿಗಲಾರ ಎಂಬ ಬಗ್ಗೆ ನೀವು ನಿರ್ಭಯರಾಗಿರುವಿರಾ?');
INSERT INTO "kannada" VALUES(17,70,'17:70','ನಾವು ಆದಮರ ಸಂತತಿಯನ್ನು ಗೌರವಿಸಿದ್ದೇವೆ. ಅವರಿಗೆ ನೆಲ, ಜಲಗಳಲ್ಲಿ ವಾಹನಗಳನ್ನು ಒದಗಿಸಿದ್ದೇವೆ. ಅವರಿಗೆ ಶುದ್ಧ ವಸ್ತುಗಳಿಂದ ಆಹಾರ ನೀಡಿದ್ದೇವೆ. ನಮ್ಮ ಅನೇಕ ಸೃಷ್ಟಿಗಳಿಗಿಂತ ಒಂದು ಶ್ರೇಷ್ಠತೆಯನ್ನು ನೀಡಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(17,71,'17:71','ನಾವು ಪ್ರತಿಯೊಂದು ಮನುಷ್ಯ ಕೂಟವನ್ನೂ ಅದರ ಮುಂದಾಳುವಿನ ಸಂಗಡ ಕರೆಯುವ ದಿನದ ಬಗ್ಗೆ ಸ್ಮರಿಸಿರಿ. ಆಗ ಯಾರಿಗೆ ತಮ್ಮ ಕರ್ಮಪತ್ರ ಬಲಗೈಯಲ್ಲಿ ಕೊಡಲ್ಪಟ್ಟಿತೋ ಅವರು ತಮ್ಮ ಕರ್ಮ ಪತ್ರವನ್ನು ಓದುವರು. ಅವರ ಮೇಲೆ ಎಳ್ಳಷ್ಟೂ ಅನ್ಯಾಯವಾಗದು .');
INSERT INTO "kannada" VALUES(17,72,'17:72','ಯಾವನು ಇಹಲೋಕದಲ್ಲಿ ಕುರುಡನಾಗುತ್ತಾನೋ ಅವನು ಪರಲೋಕದಲ್ಲೂ ಕುರುಡನಾಗುವನು. ಮತ್ತು ಅತಿಹೆಚ್ಚು ದಾರಿ ತಪ್ಪುವನು .');
INSERT INTO "kannada" VALUES(17,73,'17:73','(ಓ ಪೈಗಂಬರರೇ,) ನಾವು ನಿಮಗೆ ಬೋಧಿಸಿ ಕೊಟ್ಟ ದಿವ್ಯಸಂದೇಶದಿಂದ ಅವರು ನಿಮ್ಮನ್ನು ತಿರುಗಿಸಿ ಬಿಡಲು ಸಿದ್ಧರಾಗಿದ್ದಾರೆ. ನೀವು ನಮ್ಮ ಹೆಸರಲ್ಲಿ ಬೇರೆ ಏನನ್ನಾದರೂ ಕೃತ್ರಿಮ ನಡೆಸಲೆಂದು. (ನೀವು ಹಾಗೆ ಮಾಡುತ್ತಿದ್ದರೆ) ಅವರು ನಿಮ್ಮನ್ನು ತಮ್ಮ ಆಪ್ತಮಿತ್ರನಾಗಿ ಮಾಡಿ ಕೊಳ್ಳುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(17,74,'17:74','ನಾವು ನಿಮ್ಮನ್ನು (ಸತ್ಯದ ಮೇಲೆ) ಸ್ಥಿರ ಸುರಕ್ಷಿ ತರನ್ನಾಗಿ ಮಾಡದಿರುತ್ತಿದ್ದರೆ ನೀವು ಅವರ ಕಡೆಗೆ ಸ್ವಲ್ಪವಾದರೂ ವಾಲಿ ಬಿಡುತ್ತಿದ್ದಿರಿ .');
INSERT INTO "kannada" VALUES(17,75,'17:75','ನೀವು ಹಾಗೆ ಮಾಡುತ್ತಿದ್ದರೆ ನಾವು ನಿಮಗೆ ಇಹಲೋಕದ ಇಮ್ಮಡಿ ಶಿಕ್ಷೆಯನ್ನೂ ಪರ ಲೋಕದ ಇಮ್ಮಡಿ ಶಿಕ್ಷೆಯನ್ನೂ ಉಣಿಸುತ್ತಿದ್ದೆವು. ಅನಂತರ ನೀವು ನಮ್ಮ ವಿರುದ್ಧ ಯಾವ ಸಹಾಯಕನನ್ನೂ ಪಡೆಯುತ್ತಿರಲಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(17,76,'17:76','ಇವರು ನಿಮ್ಮನ್ನು ಈ ನಾಡಿನಿಂದ ಕಿತ್ತು ಹಾಕಿ ಇಲ್ಲಿಂದ ಹೊರಕ್ಕಟ್ಟಿ ಬಿಡಲು ಸಜ್ಜಾಗಿದ್ದಾರೆ. ಇವರು ಹೀಗೆ ಮಾಡಿದರೆ ನಿಮ್ಮ ನಂತರ ಇವರು ಇಲ್ಲಿ ಹೆಚ್ಚು ಕಾಲ ನೆಲೆಸಲಾರರು.');
INSERT INTO "kannada" VALUES(17,77,'17:77','ಇದು ನಾವು ನಿಮಗಿಂತ ಮುಂಚೆ ರವಾನಿಸಿದ ನಮ್ಮ ಸಂದೇಶವಾಹಕರ ವಿಷಯದಲ್ಲಿ ತೆಗೆದುಕೊಳ್ಳುತ್ತಿದ್ದ ಕಾರ್ಯವಿಧಾನ. ನಮ್ಮ ಕಾರ್ಯ ವಿಧಾನದಲ್ಲಿ ನೀವು ಯಾವ ಬದಲಾವಣೆಯನ್ನೂ ಕಾಣಲಾರಿರಿ .');
INSERT INTO "kannada" VALUES(17,78,'17:78','ಸೂರ್ಯನು (ಮಧ್ಯಾಹ್ನದಿಂದ) ಸರಿದಾಗಿನಿಂದ ರಾತ್ರಿ ಕತ್ತಲಾವರಿಸುವ ತನಕ ನಮಾಝನ್ನು ಮತ್ತು (ಖುರ್‍ಆನ್ ಪಠಿತ) ಪ್ರಾತಃ ನಮಾ ಝನ್ನು ಸಂಸ್ಥಾಪಿಸಿರಿ . ಪ್ರಾತಃ ನಮಾಝ್ ಪ್ರಮಾಣಿತ ವಾಗಿರುತ್ತದೆ .');
INSERT INTO "kannada" VALUES(17,79,'17:79','ರಾತ್ರಿಯಿಂದ ಅಲ್ಪ ಸಮಯ ನಿದ್ದೆಯಿಂದ ಎಚ್ಚೆತ್ತು ಖುರ್‍ಆನ್ ಪಠಣದೊಂದಿಗೆ ತಹಜ್ಜುದ್ ನಮಾಝ್ ನಿರ್ವಹಿಸಿರಿ . ಇದು ನಿಮಗೆ ಹೆಚ್ಚುವರಿ ಪುಣ್ಯ ಕರ್ಮವಾಗಿದೆ. ನಿಮ್ಮ ಪ್ರಭು ನಿಮ್ಮನ್ನು ಪ್ರಶಂಸಿತ ಸ್ಥಾನಕ್ಕೆ ನಿಯೋಗಿಸುವನು ');
INSERT INTO "kannada" VALUES(17,80,'17:80','`ಓ ನನ್ನ ಪ್ರಭೂ! ಸತ್ಯದ ಪ್ರವೇಶ ಸ್ಥಾನಕ್ಕೆ ನನ್ನನ್ನು ಪ್ರವೇಶಗೊಳಿಸು, ಸತ್ಯವನ್ನು ಹೊರಗೆಡಹುವ ಸ್ಥಾನದಿಂದ ನನ್ನನ್ನು ಹೊರ ಡಿಸು. ನಿನ್ನ ಕಡೆಯಿಂದ ನನಗೆ ಸಹಾಯಕವಾದ ಒಂದು ಅಧಿಕಾರ ಶಕ್ತಿಯನ್ನು ಒದಗಿಸು’ ಎಂದು ಪ್ರಾರ್ಥಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(17,81,'17:81','‘ಸತ್ಯ ಬಂತು. ಮಿಥ್ಯ ಅಳಿಯಿತು. ಮಿಥ್ಯವು ಖಂಡಿತ ಅಳಿದು ಹೋಗುವುದು’ ಎಂದು ಘೋಷಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(17,82,'17:82','ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಗುಣೌಷಧ ಮತ್ತು ಕಾರು ಣ್ಯವಾದ ಖುರ್‍ಆನನ್ನು ನಾವು ಅವತೀರ್ಣಗೊಳಿಸಿರುತ್ತೇವೆ. ಆದರೆ ಅಕ್ರಮಿಗಳಿಗೆ ಅದು ನಷ್ಟವನ್ನೇ ಹೊರತು ಇನ್ನೇನನ್ನೂ ವರ್ಧಿಸುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(17,83,'17:83','ಮಾನವನಿಗೆ ನಾವು ಅನುಗ್ರಹಿಸಿದರೆ ಅವನು ಬೆನ್ನುತಿರುಗಿಸಿ ದೂರ ಸರಿಯುತ್ತಾನೆ. ಅವನಿಗೆ ಸ್ವಲ್ಪವೇನಾದರೂ ಕಷ್ಟ ಬಾಧಿಸಿದರೆ (ದೇವಕೃಪೆ ಯಿಂದ) ನಿರಾಶನಾಗುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(17,84,'17:84','. (ಓ ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ. ‘ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನದೇ ಕ್ರಮದ ಕಾರ್ಯವೆಸಗುತ್ತಾನೆ. ಆದರೆ ಅತ್ಯಂತ ನೇರ ಮಾರ್ಗದಲ್ಲಿ ಯಾರಿದ್ದಾನೆ ಎಂಬು ದನ್ನು ನಿಮ್ಮ ಪ್ರಭು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು’.');
INSERT INTO "kannada" VALUES(17,85,'17:85','ಇವರು ನಿಮ್ಮೊಡನೆ ಆತ್ಮದ ಕುರಿತು ಪ್ರಶ್ನಿಸುತ್ತಾರೆ. ಹೇಳಿರಿ; ‘ಆತ್ಮವು ನನ್ನ ಪ್ರಭುವಿನ ಕಾರ್ಯಕ್ಕೆ ಸೇರಿದೆ. ಜ್ಞಾನದ ಅಲ್ಪಾಂಶವನ್ನು ಮಾತ್ರ ನಿಮಗೆ ಕೊಡಲಾಗಿದೆ’.');
INSERT INTO "kannada" VALUES(17,86,'17:86','(ಓ ಪೈಗಂಬರರೇ,) ನಾವಿಚ್ಛಿಸಿದರೆ ನಾವು ನಿಮಗೆ ಕೊಟ್ಟ ಸಂದೇಶವನ್ನು ಹಿಂತೆಗೆದು ಕೊಳ್ಳುತ್ತಿದ್ದೆವು. ಅನಂತರ ನೀವು ನಮಗೆ ಎದುರಾಗಿ ಭರವಸೆಯನ್ನಿರಿಸುವಂತಹ ಯಾವ ಕಾರ್ಯ ನಿರ್ವಾಹಕನನ್ನೂ ಹೊಂದಲಾರಿರಿ.');
INSERT INTO "kannada" VALUES(17,87,'17:87','ಇದು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕೃಪೆಯಿಂದಲೇ ದೊರೆತಿದೆ. ನಿಮ್ಮ ಮೇಲೆ ಅವನ ಅನುಗ್ರಹವು ಮಹತ್ತರವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(17,88,'17:88','ಹೇಳಿರಿ-`ಮನುಷ್ಯನೂ ಜಿನ್ನ್‍ಗಳೂ ಎಲ್ಲರೂ ಒಟ್ಟಾಗಿ ಈ ಖುರ್‍ಆನ್‍ಗೆ ಸಮಾನವಾದುದನ್ನು ತರಲು ಪ್ರಯತ್ನಿಸಿದರೆ ಅವರೆಲ್ಲರೂ ಪರಸ್ಪರ ಸಹಾಯಕರಾದರೂ ಅವರಿಗೆ ಅದನ್ನು ತರಲಿಕ್ಕಾಗದು’.');
INSERT INTO "kannada" VALUES(17,89,'17:89','ಈ ಖುರ್‍ಆನ್‍ನಲ್ಲಿ ಜನರಿಗೆ ನಾನಾ ವಿಧ ಉಪಮೆಗಳಿಂದ ವಿವರಿಸಿದೆವು. ಆದರೆ ಜನರಲ್ಲಿ ಹೆಚ್ಚಿ ನವರು ನಿಷೇಧಿಸುವುದರಲ್ಲೇ ತಲ್ಲೀನರಾದರು.');
INSERT INTO "kannada" VALUES(17,90,'17:90','ಅವರು ಹೇಳಿದರು; `ನೀವು ಈ ಭೂಮಿಯಿಂದ ನಮಗಾಗಿ ಒಂದು ಚಿಲುಮೆಯನ್ನು ಹರಿಸುವ ವರೆಗೂ ನಾವು ನಿಮ್ಮನ್ನು ನಂಬಲಾರೆವು.');
INSERT INTO "kannada" VALUES(17,91,'17:91','ಅಥವಾ ನಿಮಗೆ ಖರ್ಜೂರಗಳ ಮತ್ತು ದ್ರಾಕ್ಷೆಗಳ ಒಂದು ತೋಟ ಉಂಟಾಗಿ ನೀವು ಅದರ ನಡುವೆ ತುಂಬಿ ಹರಿಯುವ ಕಾಲುವೆಗಳನ್ನು ಹರಿಸುವವರೆಗೂ.');
INSERT INTO "kannada" VALUES(17,92,'17:92','ಅಥವಾ ನಿಮ್ಮ ವಾದದಂತೆ ಆಕಾಶವನ್ನು ತುಂಡು ತುಂಡಾಗಿ ಮಾಡಿ ನಮ್ಮ ಮೇಲೆ ಬೀಳಿ ಸುವವರೆಗೂ ಇಲ್ಲವೇ ಅಲ್ಲಾಹನನ್ನೂ ದೇವಚರ ರನ್ನೂ ಮುಖಾಮುಖಿ ನಮ್ಮ ಮುಂದೆ ತರುವವರೆಗೂ.');
INSERT INTO "kannada" VALUES(17,93,'17:93','ಅಥವಾ ನಿಮಗೆ ಒಂದು ಚಿನ್ನದ ಮನೆ ಉಂಟಾ ಗುವವರೆಗೂ ಅಥವಾ ನೀವು ಆಕಾಶಕ್ಕೆ (ಏಣಿ ಇಟ್ಟು) ಏರಿಹೋಗುವವರೆಗೂ. ನಮಗೆ ಓದ ಬಹುದಾದ ಒಂದು ಗ್ರಂಥವನ್ನು ನಮ್ಮ ಬಳಿಗೆ ನೀವು ಇಳಿಸಿಕೊಡುವವರೆಗೆ ನಿಮ್ಮ ಏರುವಿಕೆಯನ್ನು ನಾವು ನಂಬಲಾರೆವು - (ಓ ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ; ‘ನನ್ನ ಪ್ರಭು ಪರಮ ಪಾವನನು. ನಾನೊಬ್ಬ ದೂತನಾದ ಮಾನವನೇ ಹೊರತು ಇನ್ನೇನಾದರೂ ಆಗಿರುವೆನೇ?’ ');
INSERT INTO "kannada" VALUES(17,94,'17:94','ಜನರರಿಗೆ ಸನ್ಮಾರ್ಗದರ್ಶನ ಬಂದಾಗಲೆಲ್ಲ - ಅದರ ಮೇಲೆ ವಿಶ್ವಾಸವಿಡದಂತೆ ಜನರನ್ನು ತಡೆ ದುದು ಅಲ್ಲಾಹನು ಮಾನವನನ್ನು ಸಂದೇಶವಾ ಹಕನಾಗಿ ಕಳುಹಿಸಿದನೇ? ಎಂಬ ಅವರ ಮಾತು ಮಾತ್ರವಾಗಿದ್ದಿತು.');
INSERT INTO "kannada" VALUES(17,95,'17:95','ಹೇಳಿರಿ; ‘ಭೂಮಿಯ ಮೇಲೆ ಶಾಂತರಾಗಿ ನಡೆ ಯುವ ದೇವಚರರು ಇರುತ್ತಿದ್ದರೆ ನಾವು ಖಂಡಿ ತವಾಗಿಯೂ ಅವರ ಮೇಲೆ ಆಕಾಶದಿಂದ ಒಬ್ಬ ದೇವಚರನನ್ನೇ ದೂತನನ್ನಾಗಿ ಮಾಡಿ ಕಳುಹಿಸುತ್ತಿದ್ದೆವು’.');
INSERT INTO "kannada" VALUES(17,96,'17:96','ಹೇಳಿರಿ; ‘ಭೂಮಿಯ ಮೇಲೆ ಶಾಂತರಾಗಿ ನಡೆ ಯುವ ದೇವಚರರು ಇರುತ್ತಿದ್ದರೆ ನಾವು ಖಂಡಿ ತವಾಗಿಯೂ ಅವರ ಮೇಲೆ ಆಕಾಶದಿಂದ ಒಬ್ಬ ದೇವಚರನನ್ನೇ ದೂತನನ್ನಾಗಿ ಮಾಡಿ ಕಳುಹಿಸುತ್ತಿದ್ದೆವು’.');
INSERT INTO "kannada" VALUES(17,97,'17:97','ಯಾರಿಗೆ ಅಲ್ಲಾಹ್ ಸನ್ಮಾರ್ಗವೀಯುತ್ತಾನೋ ಅವನೇ ಸನ್ಮಾರ್ಗ ಪ್ರಾಪ್ತನು. ಅವನು ಯಾರನ್ನು ಪಥಭ್ರಷ್ಟಗೊಳಿಸುವನೋ ಅವರಿಗೆ ಅವನ ಹೊರತು ಯಾವ ರಕ್ಷಕ ಮಿತ್ರರನ್ನೂ ನೀವು ಪಡೆಯಲಾರಿರಿ. ಪುನರುತ್ಥಾನದಿನ ಕುರುಡರಾಗಿ ಯೂ ಮೂಕರಾಗಿಯೂ ಕಿವುಡರಾಗಿಯೂ ಮಾಡಿ ಇವರ ಮುಖಗಳನ್ನು ನೆಲಕಚ್ಚಿಸುತ್ತಾ ನಾವು ಇವರನ್ನು ಒಟ್ಟು ಸೇರಿಸುವೆವು. ಅವರ ವಾಸಸ್ಥಳ ನರಕವಾಗಿದೆ. ಅದರ ಅಗ್ನಿಯು ಮಂದವಾದಾಗಲೆಲ್ಲ ನಾವು ಅದರ ಜ್ವಾಲೆಯನ್ನು ಇನ್ನಷ್ಟು ತೀಕ್ಷ್ಣಗೊಳಿಸುವೆವು.');
INSERT INTO "kannada" VALUES(17,98,'17:98','ನಮ್ಮ ನಿದರ್ಶನಗಳನ್ನು ನಿಷೇಧಿಸಿ ‘ನಾವು ಎಲುಬುಗಳೂ ದ್ರವಿಸಿದ ಮೂಳೆಗಳೂ ಆಗಿ ಹೋದ ಬಳಿಕ ನಮ್ಮನ್ನು ಹೊಸದಾಗಿ ಸೃಷ್ಟಿಸಿ ಎಬ್ಬಿಸಲಾಗುವುದೇ?’ ಎಂದು ಅವರು ಹೇಳಿದುದರ ಪ್ರತಿಫಲವಿದು.');
INSERT INTO "kannada" VALUES(17,99,'17:99','ಆಕಾಶಗಳನ್ನೂ ಭೂಮಿಯನ್ನೂ ಸೃಷ್ಟಿಸಿದ ಅಲ್ಲಾಹನು ಖಂಡಿತವಾಗಿಯೂ ಇವರಂಥವರ ನ್ನು ಸೃಷ್ಟಿಸಲು ಶಕ್ತನೆಂದು ಇವರು ಅರ್ಥ ಮಾಡಿಕೊಳ್ಳಲಿಲ್ಲವೇ? ಅವನು ಇವರಿಗೆ (ಮರಣ ಹಾಗೂ ಪುನರುತ್ಥಾನಕ್ಕಾಗಿ) ಒಂದು ಸಮಯವನ್ನು ನಿಶ್ಚಯಿಸಿಟ್ಟಿದ್ದಾನೆ. ಅದರಲ್ಲಿ ಸಂಶಯವಿಲ್ಲ. ಆದರೆ ಅದರ ನಿಷೇಧವನ್ನೇ ಹೊರತು ಅಕ್ರಮಿಗಳು ಬೇರೇನನ್ನೂ ಒಪ್ಪಿಕೊಳ್ಳಲಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(17,100,'17:100','ಓ ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ; `ನನ್ನ ಪ್ರಭುವಿನ ಕೃಪಾಭಂಡಾರಗಳು ನಿಮ್ಮ ಒಡೆತನದಲ್ಲಿ ಇದ್ದು ಬಿಡುತ್ತಿದ್ದರೆ, ಖರ್ಚಾಗಿ ಹೋದೀತೆಂಬ ಭಯದಿಂದ ಅವುಗಳನ್ನು ಖಂಡಿತ (ಜಿಪುಣತೆಯಿಂದ) ನೀವು ತಡೆದಿಡುತ್ತಿದ್ದಿರಿ’. ನಿಜಕ್ಕೂ ಮಾನವನು ಬಹಳ ಕೃಪಣನೇ ಹೌದು .');
INSERT INTO "kannada" VALUES(17,101,'17:101','ಮೂಸಾರವರಿಗೆ ನಾವು ಪ್ರತ್ಯಕ್ಷವಾದ ಒಂಬತ್ತು ನಿದರ್ಶನಗಳನ್ನು ದಯಪಾಲಿಸಿದ್ದೆವು. ಅವರು (ಮೂಸಾರವರು) ಅವರಲ್ಲಿಗೆ ಬಂದಾಗ ಫಿರ್‍ಔನನು ಓ ಮೂಸಾ, ಖಂಡಿತವಾಗಿಯೂ ನೀ ನೊಬ್ಬ ಮಾಟ ಬಾಧಿತ ಮನುಷ್ಯನಾಗಿ ನಾನು ಭಾವಿಸುತ್ತೇನೆಂದು ಹೇಳಿದ್ದ ಸಂದರ್ಭದ ಬಗ್ಗೆ ಇಸ್ರಾಈಲ ಸಂತತಿಗಳಲ್ಲಿ ನೀವು ಕೇಳಿ ನೋಡಿರಿ .');
INSERT INTO "kannada" VALUES(17,102,'17:102','ಮೂಸಾ ಹೇಳಿದರು; ‘ಈ ನಿದರ್ಶನಗಳನ್ನು ಅಂತರ್ದರ್ಶಿ ಪಾಠಗಳಾಗಿ ಆಕಾಶಗಳ ಮತ್ತು ಭೂಮಿಯ ಪ್ರಭುವಿನ ಹೊರತು ಇನ್ನಾರೂ ಇಳಿಸಿಲ್ಲವೆಂದು ನೀನು ಚೆನ್ನಾಗಿ ತಿಳಿದಿರುವೆ. ಓ ಫಿರ್‍ಔನ್! ನೀನೊಬ್ಬ ಹತಭಾಗಿ ಎಂದೇ ನಾನು ಭಾವಿಸಿದ್ದೇನೆ’.');
INSERT INTO "kannada" VALUES(17,103,'17:103','ಕೊನೆಗೆ ಫಿರ್‍ಔನನು (ಮೂಸಾ ಮತ್ತು ಬನೀ ಇಸ್ರಾಈಲರನ್ನು) ರಾಜ್ಯದಿಂದ ಹೊರ ಗಟ್ಟಬೇಕೆಂದು ನಿರ್ಧರಿಸಿದನು. ಆದರೆ ನಾವು ಅವನನ್ನೂ ಅವನ ಸಂಗಡಿಗರನ್ನೂ ಒಟ್ಟಾಗಿ ಮುಳುಗಿಸಿ ನಾಶ ಮಾಡಿದೆವು.');
INSERT INTO "kannada" VALUES(17,104,'17:104','ಅವನ (ನಾಶದ) ಬಳಿಕ ಇಸ್ರಾಈಲ ಸಂತತಿಗಳೊಡನೆ ಹೀಗೆ ಹೇಳಿದೆವು; ‘ಭೂಮಿಯಲ್ಲಿ ನೆಲೆಸಿರಿ. ಮುಂದೆ ಪರಲೋಕದ ವಾಗ್ದಾನದ ಸಮಯ ಬಂದಾಗ ನಾವು ನಿಮ್ಮೆಲ್ಲರನ್ನೂ ಒಟ್ಟಾಗಿ ತರುವೆವು.');
INSERT INTO "kannada" VALUES(17,105,'17:105','ಸತ್ಯಾಧಿಷ್ಠಿತವಾಗಿಯೇ ನಾವು ಈ ಖುರ್‍ಆನನ್ನು ಅವತೀರ್ಣಗೊಳಿಸಿರುತ್ತೇವೆ. ಇದು ಸತ್ಯ ಗರ್ಭಿತವಾಗಿಯೇ ಅವತೀರ್ಣಗೊಂಡಿದೆ. (ಓ ಪೈಗಂಬರರೇ!) ಸುವಾರ್ತೆ ನೀಡಲಿಕ್ಕಾಗಿ ಮತ್ತು ಎಚ್ಚರಿಕೆ ಕೊಡಲಿಕ್ಕಾಗಿಯೇ ವಿನಾ ನಿಮ್ಮನ್ನು ನಾವು ಕಳುಹಿಸಿರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(17,106,'17:106','ನೀವು ಜನರಿಗೆ ಸಾವಕಾಶವಾಗಿ ಓದಿಕೊಡಲಿ ಕ್ಕಾಗಿ ಖುರ್‍ಆನನ್ನು ನಾವು (ಹಲವು ಭಾಗಗಳಾಗಿ) ವಿಂಗಡಿಸಿದ್ದೇವೆ. ಮತ್ತು ಇದನ್ನು ನಾವು ಹಂತಹಂತವಾಗಿ (ಸಂದರ್ಭೋಚಿತವಾಗಿ) ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(17,107,'17:107','(ಓ ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ; ನೀವು ಇದನ್ನು (ಖುರ್‍ಆನನ್ನು) ನಂಬಿರಿ ಇಲ್ಲವೇ ನಂಬದಿರಿ. ಇದಕ್ಕೆ ಮುಂಚೆ ಜ್ಞಾನ ಕೊಡಲ್ಪಟ್ಟವರಿಗೆ ಇದನ್ನು ಓದಿ ಹೇಳಿದಾಗ ಅವರು ತಲೆಬಾಗಿ ಅಧೋಮುಖಿಗಳಾಗಿ ದೊಪ್ಪನೆ ಸಾಷ್ಟಾಂಗವೆರಗಿ ಬಿಡುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(17,108,'17:108','ಅವರು ಹೇಳುವರು; ನಮ್ಮ ಪ್ರಭು ಪರಮ ಪಾವನನು. ನಮ್ಮ ಪ್ರಭುವಿನ ವಾಗ್ದಾನ ಪೂರ್ಣಗೊಳ್ಳಲೇಬೇಕು.');
INSERT INTO "kannada" VALUES(17,109,'17:109','ಅವರು ಅಳುತ್ತಾ ಅಧೋಮುಖಿಗಳಾಗಿ ಬೀಳುತ್ತಾರೆ ಮತ್ತು ಅದು (ಖುರ್‍ಆನ್) ಅವರ ಭಯ ವಿನಯವನ್ನು ಇನ್ನಷ್ಟು ವರ್ಧಿಸುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(17,110,'17:110','(ಓ ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ! ನೀವು ಅಲ್ಲಾಹು ಎಂದು ಕರೆಯಿರಿ ಅಥವಾ ರಹ್ಮಾನ್ ಎಂದು ಕರೆಯಿರಿ. ಯಾವ ಹೆಸರಿಂದ ಕರೆದರೂ ಅವನಿಗೆ ಉತ್ಕøಷ್ಟನಾಮಗಳೇ ಇವೆ . ನಿಮ್ಮ ನಮಾಝನ್ನು (ಪಠಣವನ್ನು) ಬಹಳ ಉಚ್ಚ ಸ್ವರದಲ್ಲಾಗಲಿ ತೀರಾ ಮೆಲುದನಿಯಲ್ಲಾಗಲಿ ನಿರ್ವಹಿಸಬೇಡಿರಿ - ಅವೆರಡರ ನಡುವೆ ಮಧ್ಯಮ ನೀತಿ ಅನುಸರಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(17,111,'17:111','ಹೇಳಿರಿ! ಸರ್ವಸ್ತುತಿಗಳೂ ಅಲ್ಲಾಹನಿಗೇ ಮೀಸಲು. ಅವನು ಸಂತಾನವನ್ನು ಮಾಡಿ ಕೊಳ್ಳಲಿಲ್ಲ. ಅವನ ಪ್ರಭುತ್ವದಲ್ಲಿ (ದೇವತ್ವದಲ್ಲಿ) ಯಾರೂ ಸಹಭಾಗಿಯಿಲ್ಲ. ರಕ್ಷಕನ ಅಗತ್ಯ ಬರುವಂತಹ ನಿಂದ್ಯತೆಯೇ ಅವನಿಗಿಲ್ಲ. ಅವನ ಹಿರಿಮೆಯನ್ನು ಪರಿಪೂರ್ಣವಾದ ರೀತಿಯಲ್ಲಿ ಕೊಂಡಾಡಿರಿ .');
INSERT INTO "kannada" VALUES(18,1,'18:1','ಅಲ್ಲಾಹನಿಗೆ ಸರ್ವಸ್ತುತಿಗಳು, ಅವನು ತನ್ನ ದಾಸನ ಮೇಲೆ ಈ ಗ್ರಂಥವನ್ನು ಅವತೀರ್ಣಗೊಳಿಸಿದನು. ಅದರಲ್ಲಿ ಯಾವುದೇ ವೈರುಧ್ಯವನ್ನು ಅವನು ಮಾಡಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(18,2,'18:2','ಋುಜುವಾದ ಗ್ರಂಥವಿದು. ಅವನು ತನ್ನ ಕಡೆಯಿಂದ ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಕಠಿಣ ಶಿಕ್ಷೆಯ ಬಗ್ಗೆ ತಾಕೀತು ಕೊಡಲಿಕ್ಕಾಗಿ ಮತ್ತು ಸತ್ಕರ್ಮವೆಸಗುವ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಶ್ರೇಷ್ಠ ಪ್ರತಿಫಲವಿದೆಯೆಂಬ ಸುವಾರ್ತೆ ನೀಡಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(18,3,'18:3','ಅಲ್ಲಿ ಅವರು ಚಿರಕಾಲ ವಾಸಿಸುವರು.');
INSERT INTO "kannada" VALUES(18,4,'18:4','ಮತ್ತು ಅಲ್ಲಾಹನಿಗೆ ಮಕ್ಕಳು ಇದ್ದಾರೆ ಎಂಬ ಮಾತನ್ನಾಡುವವರಿಗೆ ಶಿಕ್ಷೆಯ ಎಚ್ಚರಿಕೆ ನೀಡಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(18,5,'18:5','ಇವರ ಬಾಯಿಂದ ಹೊರಡುತ್ತಿರುವ ಪ್ರಸ್ತುತ ಮಾತು ಬಹಳ ಘೋರವಾಗಿದ್ದು ಇವರಿಗಾಗಲಿ, ಇವರ ಹಿರಿಯರಿಗಾಗಲಿ ಈ ಬಗ್ಗೆ ಯಾವುದೇ ಅರಿವಿಲ್ಲ. ಅವರು ಕೇವಲ ಸುಳ್ಳು ಮಾತಿನ ವಿನಾ (ಇನ್ನಾ ವುದೇ ಸತ್ಯವಿಚಾರವನ್ನು) ಹೇಳುವವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(18,6,'18:6','(ಓ ನಬಿಯೇ,) ಖುರ್‍ಆನನ್ನು ಇವರು ನಂಬದಿದ್ದಲ್ಲಿ ಇವರ ಬೆನ್ನ ಹಿಂದೆ ವ್ಯಥೆಯಿಂದ ತಮ್ಮ ಜೀವವನ್ನು ನಾಶಮಾಡಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(18,7,'18:7','ಜನರಲ್ಲಿ ಯಾರು ಉತ್ತಮರಾಗಿ ಬಾಳುತ್ತಾರೆಂದು ಪರೀಕ್ಷಿಸಲಿಕ್ಕೆಂದೇ ನಾವು ಭೂಮಿ ಮೇಲಿನ ವಸ್ತುವನ್ನು ಅದಕ್ಕೊಂದು ಅಲಂಕಾರವನ್ನಾಗಿ ಮಾಡಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(18,8,'18:8','ಆದರೆ ಕೊನೆಗೆ ನಾವು ಅದರಲ್ಲಿರುವ ಸರ್ವವಸ್ತು ಗಳನ್ನು ನೆಲಸಮಗೊಳಿಸಿ ಬಂಜರು ನೆಲವನ್ನಾಗಿ ಮಾಡಲಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(18,9,'18:9','ಅಲ್ಲ, ಗುಹೆ ಹಾಗೂ ರಖೀಮ್‍ನವರು ಮಾತ್ರ ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳಲ್ಲಿ ವಿಸ್ಮಯವಾದುದೆಂದು ತಾವು ಭಾವಿಸಿರುವಿರಾ ?');
INSERT INTO "kannada" VALUES(18,10,'18:10','ಯುವಕರು ಗುಹೆಯೊಳಗೆ ಆಶ್ರಯ ಪಡೆದ ಸಂದರ್ಭವನ್ನು ನೆನಪಿಸಿರಿ. ಆಗ ಅವರು ಪ್ರಾರ್ಥಿಸಿದರು `ಓ ನಮ್ಮ ಪ್ರಭೂ, ನಿನ್ನ ಪಾಲಿನ ಮಹತ್ತರವಾದ ಕೃಪೆಯನ್ನು ನಮಗೆ ದಯಪಾಲಿಸು. ನಮ್ಮ ಸರ್ವ ಕಾರ್ಯಗಳಲ್ಲಿ ನಮಗೆ ಸನ್ಮಾರ್ಗವನ್ನೇ ಅಣಿಗೊಳಿಸು’.');
INSERT INTO "kannada" VALUES(18,11,'18:11','ನಂತರ ಆ ಗುಹೆಯಲ್ಲಿ ಅನೇಕ ವರ್ಷಗಳವರೆಗೆ ಅವರನ್ನು ನಾವು ಗಾಢ ನಿದ್ರೆಗೆ ತಳ್ಳಿದೆವು.');
INSERT INTO "kannada" VALUES(18,12,'18:12','ತರುವಾಯ ಅವರು ಗುಹೆಯಲ್ಲಿ ತಂಗಿದ ಕಾಲವನ್ನು ಸರಿಯಾಗಿ ಗಣಿಸುವುದು ಎರಡು ಕಕ್ಷಿದಾರರಲ್ಲಿ ಯಾರೆಂದು ತಿಳಿಯತಕ್ಕ ರೀತಿಯಲ್ಲಿ ನಾವು ಅವರನ್ನು ಎಬ್ಬಿಸಿದೆವು .');
INSERT INTO "kannada" VALUES(18,13,'18:13','(ಓ ನಬಿಯೇ,) ನಾವು ಅವರ ವೃತ್ತಾಂತವನ್ನು ತಮಗೆ ನೈಜ ರೂಪದಲ್ಲಿ ತಿಳಿಸುತ್ತೇವೆ. ಅವರು ತಮ್ಮ ಪ್ರಭುವಿನ ಮೇಲೆ ನಂಬಿಕೆಯಿರಿಸಿದ್ದ ಕೆಲವು ಯುವಕರಾಗಿದ್ದರು . ಅವರಿಗೆ ನಾವು ಸನ್ಮಾರ್ಗ ಪ್ರಜ್ಞೆಯನ್ನು ಹೆಚ್ಚಿಸಿಕೊಟ್ಟೆವು.');
INSERT INTO "kannada" VALUES(18,14,'18:14','ಅವರು (ರಾಜನ ಮುಂದೆ) ಎದ್ದು ನಿಂತಾಗ ಅವರ ಹೃದಯಗಳಿಗೆ ನಾವು ಬಲ ನೀಡಿದೆವು. ಅವರು ಹೇಳಿದರು; ಭೂಮ್ಯಾಕಾಶಗಳ ಪ್ರಭುವೇ ನಮ್ಮ ಪ್ರಭುವಾಗಿದ್ದು, ನಾವು ಅವನನ್ನು ಬಿಟ್ಟು ಬೇರೆ ಯಾರನ್ನೂ ದೇವರನ್ನಾಗಿ ಮಾಡಿ ಆರಾಧಿಸಲಾರೆವು. ಹಾಗೇನಾದರೂ ನಾವು ಮಾಡಿದರೆ ನಮ್ಮದು ಅತಿರೇಕದ ಮಾತಾದೀತು.');
INSERT INTO "kannada" VALUES(18,15,'18:15','ಈ ನಮ್ಮ ಜನಾಂಗವು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಹಲವು ಆರಾಧ್ಯರುಗಳನ್ನು ನಿರ್ಮಿಸಿದೆ. ಆದರೆ ಅವರಿಗೆ ಆರಾಧನೆ ಸಲ್ಲಿಸುವ ಬಗ್ಗೆ ಸ್ಪಷ್ಟವಾದ ಯಾವುದೇ ಪುರಾವೆಯನ್ನು ಅವರೇಕೆ ತಂದಿಲ್ಲ? ಆದ್ದರಿಂದ ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಳ್ಳನ್ನು ಸೃಷ್ಟಿಸಿ ಹೇಳುವವನಿಗಿಂತ ದೊಡ್ಡ ಅಕ್ರಮವೆಸಗುವವನು ಬೇರೆ ಇರುವನೇ?');
INSERT INTO "kannada" VALUES(18,16,'18:16','ನೀವೀಗ ಅವರಿಂದಲೂ ಅಲ್ಲಾಹನ ಹೊರತು ಅವರ ಆರಾಧ್ಯ ಮೂರ್ತಿಗಳಿಂದಲೂ ಬೇರ್ಪ ಟ್ಟಿರುವಾಗ ಇನ್ನು ಆ ಗುಹೆಯತ್ತ ಅಭಯ ಪಡೆಯಿರಿ. ನಿಮ್ಮ ಪ್ರಭು ತನ್ನ ಅನುಗ್ರಹವನ್ನು ನಿಮಗೆ ವಿಸ್ತರಿಸಿ ಕೊಡುವನು. ನಿಮ್ಮ ಕಾರ್ಯದಲ್ಲಿ ಬೇಕಾದ ಸವಲತ್ತುಗಳನ್ನು ಒದಗಿಸಿ ಕೊಡುವನು. (ಎಂದವರು ಪರಸ್ಪರ ಹೇಳಿಕೊಂಡರು).');
INSERT INTO "kannada" VALUES(18,17,'18:17','ಸೂರ್ಯನು ಉದಯಿಸುವಾಗ ಅವರ ಗುಹೆಯನ್ನು ಬಿಟ್ಟು ಬಲಭಾಗಕ್ಕೆ ಸರಿಯುವಂತೆಯೂ ಅಸ್ತ ಮಿಸುವಾಗ ಎಡದಲ್ಲಾಗಿ ಅವರನ್ನು ಹಾದು ಹೋಗುವಂತೆಯೂ ತಾವು ಕಾಣುವಿರಿ. ಅವರಾದರೋ ಗುಹೆಯೊಳಗೆ ಅನುಕೂಲಕರ ಜಾಗದಲ್ಲಿದ್ದರು. ಇದೊಂದು ಅಲ್ಲಾಹನ ನಿದರ್ಶನ. ಅಲ್ಲಾಹನು ಯಾರಿಗೆ ದಾರಿ ತೋರುತ್ತಾನೆ, ಅವನು ನೇರ ಹಾದಿ ಹಿಡಿದನು. ಅಲ್ಲಾಹನು ಯಾರನ್ನು ದಾರಿ ತಪ್ಪಿಸುತ್ತಾನೆ, ಆತನಿಗೆ ದಾರಿ ತೋರುವ ಬೇರೆ ಯಾವುದೇ ಮಿತ್ರನನ್ನು ತಾವು ಕಾಣಲಾರಿರಿ.');
INSERT INTO "kannada" VALUES(18,18,'18:18','ಅವರು ಎಚ್ಚರವಿದ್ದಾರೆಂದು ತಾವು ಭಾವಿಸುವಿರಿ. ಆದರೆ ಅವರು ನಿದ್ರಿಸುತ್ತಿದ್ದರು. ನಾವು ಅವರನ್ನು ಬಲಕ್ಕೂ ಎಡಕ್ಕೂ ಮಗ್ಗುಲು ಬದಲಿಸುತ್ತಿದ್ದೆವು. ಅವರ ನಾಯಿಯು ಹಜಾರದಲ್ಲಿ ತನ್ನ ಮೊಣಕೈ ಗಳನ್ನು ಹರಡಿ ಕುಳಿತಿತ್ತು. ತಾವು ಅವರನ್ನು ನೋಡಿದ್ದಲ್ಲಿ ಹಿಂದಿರುಗಿ ಓಡುತ್ತಿದ್ದಿರಿ ಹಾಗೂ ಅವರಿಂದಾಗಿ ಭಯದಲ್ಲಿ ತುಂಬಿ ಹೋಗುತ್ತಿದ್ದಿರಿ .');
INSERT INTO "kannada" VALUES(18,19,'18:19','ಅದೇ ರೀತಿ ಅವರನ್ನು ನಾವು (ನಿದ್ದೆಯಿಂದ) ಎಬ್ಬಿಸಿದೆವು . ಅವರು ಪರಸ್ಪರ ಪ್ರಶ್ನಿಸುವ ಸ್ಥಿತಿಗೆ ತಲುಪಿದರು. ಅವರಲ್ಲೊಬ್ಬನು; ನೀವು ಎಷ್ಟು ಕಾಲ (ಇಲ್ಲಿ ನಿದ್ದೆಯಲ್ಲಿ) ತಂಗಿದಿರಿ? ಎಂದು ಕೇಳಿದನು. ಅದಕ್ಕೆ ಉಳಿದವರು ‘ಒಂದು ದಿನ ಅಥವಾ ದಿನದ ಕೊಂಚ ಹೊತ್ತು (ನಿದ್ರೆಯಲ್ಲಿ) ತಂಗಿದೆವು’ ಎಂದರು. (ಆದರೆ ಅದನ್ನು ಸರಿಯಾಗಿ ನಿರ್ಣಯಿಸಲು ಅವರಿಗೆ ಸಾಧ್ಯವಾಗಲಿಲ್ಲ.) ಅವರು ಹೇಳಿದರು ‘ನೀವು (ನಿದ್ರಿಸಿ) ತಂಗಿದ ಕಾಲಾವಧಿಯ ಬಗ್ಗೆ ಹೆಚ್ಚು ಬಲ್ಲವನು ನಿಮ್ಮ ಪ್ರಭುವೇ ಆಗಿರುವನು. ಆದ್ದರಿಂದ ನಿಮ್ಮಲ್ಲಿ ಒಬ್ಬನನ್ನು ನಿಮ್ಮ ಈ ಬೆಳ್ಳಿನಾಣ್ಯದೊಂದಿಗೆ ಪೇಟೆಗೆ ಕಳುಹಿಸಿರಿ. ಧರ್ಮಬದ್ಧ ಆಹಾರ ಎಲ್ಲಿ ಸಿಗುವುದೆಂದು ಅವನು ನೋಡಿ ಕೊಂಡು ಅದರಿಂದ ಸ್ವಲ್ಪ ಆಹಾರವನ್ನು ತರಲಿ. ಅವನು ನಯವಾಗಿ ವರ್ತಿಸಲಿ. ನಿಮ್ಮ ಬಗ್ಗೆ ಯಾವುದೇ ಮಾಹಿತಿಯನ್ನು ಅವನು ಕೊಡಬಾರದು’ .');
INSERT INTO "kannada" VALUES(18,20,'18:20','ನಿಮ್ಮ ಮೇಲೆ ಅವರು ಏರಿಬಂದರೆ ನಿಮ್ಮನ್ನು ಅವರು ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲುವರು. ಇಲ್ಲವೇ ಅವರ ಮಾರ್ಗಕ್ಕೆ ಬಲವಂತದಿಂದ ಮರಳಿ ಕೊಂಡೊಯ್ಯುವರು. ನೀವೇನಾದರೂ ಮರಳಿ ಹೋದರೆ ಖಂಡಿತ ನೀವು ಯಶಸ್ವಿಯಾಗಲಾರಿರಿ’.');
INSERT INTO "kannada" VALUES(18,21,'18:21','ಅದೇ ರೀತಿ ಅಲ್ಲಾಹನ ವಾಗ್ದಾನವು ಸತ್ಯವೆಂದೂ ಅಂತ್ಯದಿನದಲ್ಲಿ ಯಾವುದೇ ಸಂಶಯವಿಲ್ಲವೆಂದೂ ಜನರಿಗೆ ತಿಳಿಯುವಂತಾಗಲು ನಾವು ಅವರಿಗೆ ಇವರನ್ನು ತೋರಿಸಿಕೊಟ್ಟೆವು. ಗುಹಾವಾಸಿಗಳ ಕಾರ್ಯದಲ್ಲಿ ಅವರು ತರ್ಕಿಸುತ್ತಿದ್ದಾಗ ಕೆಲವರು ‘ಇವರ ಮೇಲೊಂದು ಕಟ್ಟಡವನ್ನು ಕಟ್ಟಿಬಿಡಿರಿ. ಇವರ ಬಗ್ಗೆ ಇವರ ಪ್ರಭುವೇ ಚೆನ್ನಾಗಿ ಬಲ್ಲವನು’ ಎಂದರು. ಆದರೆ ಅವರ ಕಾರ್ಯಗಳಲ್ಲಿ ಪ್ರಾಬಲ್ಯ ಸಾಧಿಸಿದವರು, ‘ನಾವು ಇವರ ಹತ್ತಿರ ದಲ್ಲೊಂದು ಮಸೀದಿಯನ್ನು ನಿರ್ಮಿಸಿ ಬಿಡು ತ್ತೇವೆ'' ಎಂದರು 7.');
INSERT INTO "kannada" VALUES(18,22,'18:22','ಇದೀಗ ಕೆಲವರು; ಅವರು ಮೂವರೆಂದೂ ನಾಲ್ಕನೆಯದು ಅವರ ನಾಯಿಯೆಂದೂ ಇನ್ನು ಕೆಲವರು ಅವರು ಐವರೆಂದೂ ಆರನೆಯದು ಅವರ ನಾಯಿಯೆಂದೂ ಹೇಳುತ್ತಾರೆ. ಹಾಗೆಂದು ಕೇವಲ ಊಹೆಯ ಆಧಾರದಲ್ಲಿ ಹೇಳುತ್ತಾರೆ. ಅವರು ಏಳು ಜನರೆಂದೂ ಎಂಟನೆಯದು ಅವರ ನಾಯಿ ಯೆಂದೂ ಬೇರೆ ಕೆಲವರು ಹೇಳುತ್ತಾರೆ . ಹೇಳಿರಿ. `ನನ್ನ ಪ್ರಭು ಅವರ ಸಂಖ್ಯೆಯ ಬಗ್ಗೆ ಚೆನ್ನಾಗಿ ಬಲ್ಲನು. ಕೆಲವು ಜನರಿಗಲ್ಲದೆ ಅವರ ಬಗ್ಗೆ ತಿಳಿದಿಲ್ಲ, ಅಂದ ಮೇಲೆ ಅವರ ಬಗ್ಗೆ ಸ್ಪಷ್ಟವಾದ ವಿಚಾರದ ಹೊರತು ವಾದಿಸಬೇಡಿರಿ. ಅವರ ಬಗ್ಗೆ ಯಾರಲ್ಲೂ ತೀರ್ಪು ಕೇಳಬೇಡಿರಿ .');
INSERT INTO "kannada" VALUES(18,23,'18:23','ಯಾವುದರ ಬಗ್ಗೆಯೂ ಅದನ್ನು ನಾಳೆ ಖಂಡಿತ ಮಾಡುವೆನು ಎಂದು ಹೇಳಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(18,24,'18:24','`ಇನ್‍ಶಾ ಅಲ್ಲಾಹ್'' ಎಂದು ಹೇಳದೆ ನಿಮಗದು ಮರೆತು ಬಿಟ್ಟರೆ (ನೆನಪಾದಾಗ) ನಿಮ್ಮ ಪ್ರಭುವನ್ನು ಸ್ಮರಿಸು. “ನನ್ನ ಪ್ರಭು ಇದಕ್ಕಿಂತ ಹತ್ತಿರದ ನೇರ ಮಾರ್ಗದಲ್ಲಿ ನನ್ನನ್ನು ಮುನ್ನಡೆಸುವ ಭರವಸೆಯಿದೆ'' ಎಂದು ಹೇಳಿ ಬಿಡಿರಿ .');
INSERT INTO "kannada" VALUES(18,25,'18:25','ಅವರು ತಮ್ಮ ಗುಹೆಯಲ್ಲಿ ಮುನ್ನೂರು ವರ್ಷಕಾಲ ತಂಗಿದ್ದರು ಹಾಗೂ ಒಂಬತ್ತು ವರ್ಷವನ್ನು ಹೆಚ್ಚಿಸಿದರು .');
INSERT INTO "kannada" VALUES(18,26,'18:26','ಹೇಳಿರಿ; `ಅವರು ತಂಗಿದ ಕಾಲದ ಬಗ್ಗೆ ಅಲ್ಲಾ ಹನೇ ಹೆಚ್ಚಾಗಿ ಬಲ್ಲವನು . ಆಕಾಶ ಭೂಮಿಗಳ ಅದೃಶ್ಯವು ಆತನಿಗೇ ಮೀಸಲಾಗಿದೆ. ಅವನ ದೃಷ್ಟಿ ಅತ್ಯಂತ ತೀಕ್ಷ್ಣವೂ ಶ್ರವಣ ಅತ್ಯಂತ ಸೂಕ್ಷ್ಮವೂ ಆಗಿದೆ. ಆತನ ಹೊರತು ಅವರಿಗೆ ಯಾವ ರಕ್ಷಕನೂ ಇಲ್ಲ. ಆತ ತನ್ನ ಆಜ್ಞಾಧಿಕಾರದಲ್ಲಿ ಯಾರನ್ನೂ ಜೊತೆಗೂಡಿಸಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(18,27,'18:27','(ಓ ಪ್ರವಾದಿವರ್ಯರೇ,) ತಮ್ಮ ಪ್ರಭುವಿನ ಗ್ರಂಥದಿಂದ ಬೋಧಿಸಲಾದ ವಿಷಯವನ್ನು ತಾವು ಓದಿರಿ. ದೇವ ವಾಕ್ಯವನ್ನು ಬದಲಿಸುವ ಶಕ್ತಿ ಯಾರಿಗೂ ಇಲ್ಲ. ಅವನ ಹೊರತು ಇನ್ನಾರಿಂದಲೂ ಯಾವುದೇ ಅಭಯ ಸ್ಥಾನವನ್ನು ತಾವು ಪಡೆಯಲಾರಿರಿ .');
INSERT INTO "kannada" VALUES(18,28,'18:28','ತಮ್ಮ ಪ್ರಭುವಿನ ಸಂತೃಪ್ತಿಯನ್ನು ಬಯಸಿ ಬೆಳ ಗು ಬೈಗುಗಳಲ್ಲಿ ದೇವೋಪಾಸನೆ ಮಾಡುತ್ತಿರುವ ಜನರ ಜತೆಗೆ ನೀವು ಸಹನೆಯೊಂದಿಗೆ ಇರಿ . ಇಹಜೀವನದ ಸೊಬಗನ್ನು ಬಯಸಿ ಅವರಿಂದ ತಮ್ಮ ಕಣ್ಣುಗಳು ತಪ್ಪದಿರಲಿ. ನಮ್ಮ ಸ್ಮರಣೆಯಿಂದ ಯಾರ ಹೃದಯವನ್ನು ನಾವು ಅಲಕ್ಷಿಸಿ ದ್ದೇವೆಯೋ ಮತ್ತು ಯಾವನು ತನ್ನಿಚ್ಚೆಯ ಹಿಂದೆ ಬಿದ್ದು ತನ್ನ ಕಾರ್ಯವು ಹತೋಟಿಗೆ ಸಿಗುವುದಿಲ್ಲವೋ ಅಂಥ ವ್ಯಕ್ತಿಯನ್ನು ತಾವು ಅನುಸರಿಸ ಬೇಡಿರಿ .');
INSERT INTO "kannada" VALUES(18,29,'18:29','ಹೇಳಿರಿ, ಇದು (ಖುರ್‍ಆನ್) ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಬಂದ ಸತ್ಯವಾಗಿದ್ದು ಇಷ್ಟವಿದ್ದವರು ನಂಬಲಿ, ಬೇಡದವರು ನಿರಾಕರಿಸಲಿ . ನಿಜವಾ ಗಿಯೂ ಅಕ್ರಮಿಗಳಿಗಾಗಿ ನಾವು ನರಕಾಗ್ನಿಯ ನ್ನು ಸಜ್ಜುಗೊಳಿಸಿದ್ದೇವೆ. ಅದರ ಆವರಣವು ಅವರನ್ನು ಆವರಿಸಿದೆ. ಅವರು ನೆರವು ಕೇಳಿದರೆ ಮುಖವನ್ನೇ ವಿಕೃತಿಗೊಳಿಸಿಬಿಡುವ ಹಾಗೂ ಎಣ್ಣೆ ಡಬ್ಬದ ತಳದಲ್ಲಿರುವ ಜಿಡ್ಡಿನಂತಹ ದುರ್ಜಲವನ್ನು ಅವರಿಗೆ ನೀಡಲಾಗುವುದು. ಅದೆಷ್ಟು ನಿಕೃ ಷ್ಟವಾದ ಪೇಯ! ಅದೆಷ್ಟು ಕೆಟ್ಟ ತಂಗುದಾಣ!');
INSERT INTO "kannada" VALUES(18,30,'18:30','ಯಾರು ಸತ್ಯವಿಶ್ವಾಸ ತಾಳಿ ಸತ್ಕರ್ಮಗಳನ್ನು ಕೈಗೊಳ್ಳುತ್ತಾರೋ ನಾವು ಸತ್ಕರ್ಮಿಗಳ ಪ್ರತಿ ಫಲವನ್ನು ವ್ಯರ್ಥಗೊಳಿಸಲಾರೆವು.');
INSERT INTO "kannada" VALUES(18,31,'18:31','ಅವರಿಗಾಗಿ ತಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆ ಹರಿಯುತ್ತಿರುವ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಿವೆ. ಅಲ್ಲಿ ಅವರಿಗೆ ಬಂಗಾರದ ಕಂಕಣವನ್ನು ತೊಡಿಸಲಾಗುವುದು. ಹಸಿರು ಬಣ್ಣದ ತೆಳು ಹಾಗೂ ದೊರಗಿನ ರೇಷ್ಮೆ ಬಟ್ಟೆಗಳನ್ನು ಅವರು ಉಟ್ಟಿರುತ್ತಾರೆ. ಅಲ್ಲಿ ಅಲಂಕೃತ ಒರಗುಮಂಚದಲ್ಲಿ ಒರಗಿ ಹಾಯಾಗಿರುತ್ತಾರೆ. ಅದೆಷ್ಟು ಉತ್ತಮ ಪ್ರತಿಫಲ! ಅದೆಷ್ಟು ಉತ್ತಮ ವಿಶ್ರಾಂತಿಧಾಮ.!');
INSERT INTO "kannada" VALUES(18,32,'18:32','(ನಬಿವರ್ಯರೇ,) ತಾವು ಅವರಿಗೆ ಇಬ್ಬರ ಬಗ್ಗೆ ಒಂದು ಉಪಮೆಯನ್ನು ಹೇಳಿರಿ. ಅವರಲ್ಲೊಬ್ಬ ನಿಗೆ ನಾವು ದ್ರಾಕ್ಷೆಯ ಎರಡು ತೋಟಗಳನ್ನು ಕೊಟ್ಟೆವು. ಅವೆರಡನ್ನು ಖರ್ಜೂರ ಮರಗಳಿಂದ ಸುತ್ತುವರಿಸಿದೆವು. ಅವೆರಡರ ಮಧ್ಯೆ ಕೃಷಿಯನ್ನು ಬೆಳೆಸಿದೆವು.');
INSERT INTO "kannada" VALUES(18,33,'18:33','ಆ ಎರಡು ತೋಟಗಳು ಹೇರಳ ಫಲ ಕೊಟ್ಟಿತು. ಯಾವುದೇ ಕೊರತೆಯನ್ನು ಅದು ಮಾಡಲಿಲ್ಲ. ಆ ಎರಡು ತೋಟಗಳ ನಡುವೆ ನಾವು ಕಾಲುವೆಯನ್ನು ಹರಿಸಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(18,34,'18:34','ಅವನಿಗೆ ಬೇರೆಯೂ ಸೊತ್ತು ಇತ್ತು. ಒಮ್ಮೆ ಅವನು ತನ್ನ ಸಂಗಡಿಗನೊಂದಿಗೆ ವಾಗ್ವಾದ ಮಾಡುತ್ತಾ; “ನಾನು ನಿನಗಿಂತ ಹೆಚ್ಚು ಶ್ರೀಮಂತನೂ ನಿನಗಿಂತ ಜನಬಲವುಳ್ಳವನೂ ಆಗಿರುತ್ತೇನೆ” ಎಂದನು.');
INSERT INTO "kannada" VALUES(18,35,'18:35','ಅವನು ತನ್ನ ತೋಟಕ್ಕೆ ಹೊಕ್ಕನು. ಅವನು ಸತ್ಯನಿಷೇಧಿಯಾಗಿದ್ದುಕೊಂಡೇ ಹೇಳಿದನು; `ಇದು ಎಂದಾದರೂ ನಾಶವಾದೀತೆಂದು ನಾನು ಭಾವಿಸುವುದಿಲ್ಲ’.');
INSERT INTO "kannada" VALUES(18,36,'18:36','ಪ್ರಳಯ ಉಂಟಾದೀತೆಂದು ನಾನು ಭಾವಿಸುವುದಿಲ್ಲ. ಬಹುಶಃ ನನ್ನ ಪ್ರಭುವಿನ ಕಡೆಗೆ ನನ್ನನ್ನು ಮರಳಿಸಲಾದರೂ ಇದಕ್ಕಿಂತ ಉತ್ತಮವಾದ ನಿರ್ಗಮನವನ್ನು ಪಡೆಯುವೆನು.''');
INSERT INTO "kannada" VALUES(18,37,'18:37','ಅವನೊಂದಿಗೆ ವಾಗ್ವಾದ ಮಾಡುತ್ತಾ ಜೊತೆಗಾರನು ಕೇಳಿದನು. `ಮಣ್ಣಿನಿಂದಲೂ ಆ ಬಳಿಕ ವೀರ್ಯದಿಂದಲೂ ನಿನ್ನನ್ನು ಸೃಷ್ಟಿಸಿ ನಂತರ ನಿನ್ನನ್ನು ಓರ್ವ ಸಂಪೂರ್ಣ ಮನುಷ್ಯನನ್ನಾಗಿ ರೂಪುಗೊಳಿಸಿದ ಪ್ರಭುವನ್ನು ನೀನು ನಿರಾಕರಿಸುವೆಯಾ?');
INSERT INTO "kannada" VALUES(18,38,'18:38','ಆದರೆ ಅಲ್ಲಾಹನು ನನ್ನ ಪ್ರಭುವಾಗಿದ್ದು ನಾನು ನನ್ನ ಪ್ರಭುವಿನೊಂದಿಗೆ ಯಾರನ್ನೂ ಜೊತೆ ಸೇರಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(18,39,'18:39','ನೀನು ನಿನ್ನ ತೋಟಕ್ಕೆ ಹೊಕ್ಕಾಗ `ಮಾಶಾ ಅಲ್ಲಾಹ್ ಲಾ ಖುವ್ವತ ಇಲ್ಲಾ ಬಿಲ್ಲಾಹ್'' (ಕೈಗೂಡುವುದು ದೇವೇಚ್ಛೆ ಮಾತ್ರವಾಗಿದ್ದು ಅಲ್ಲಾ ಹನಿಂದ ಹೊರತು ಯಾವುದೇ ಶಕ್ತಿಯಿಲ್ಲ) ಎಂದೇಕೆ ಹೇಳಿಲ್ಲ? ಸೊತ್ತು ಸಂತತಿಗಳಲ್ಲಿ ನಾನು ನಿನಗಿಂತ ಕೀಳಾಗಿ ಕಾಣುತ್ತಿದ್ದರೆ,');
INSERT INTO "kannada" VALUES(18,40,'18:40','ನಿನ್ನ ತೋಟಕ್ಕಿಂತ ಉತ್ತಮವಾದುದನ್ನು ನನ್ನ ಪ್ರಭು ನನಗೆ ಕೊಡಲಿಕ್ಕೂ ನಿನ್ನ ತೋಟಕ್ಕೆ ಆಕಾಶದಿಂದ ವಿಪತ್ತನ್ನು ಕಳುಹಿಸಿ ಅದನ್ನು ಜಾರುವ ನೆಲವನ್ನಾಗಿ ಮಾಡಲಿಕ್ಕೂ ಸಮರ್ಥನಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(18,41,'18:41','ಅಥವಾ ಅದರ ನೀರು ನಿನಗೆಂದೂ ಹುಡುಕಿ ತೆಗೆಯಲು ಸಾಧ್ಯವಿಲ್ಲದ ಮಟ್ಟಿಗೆ ನೆಲಕ್ಕೆ ಇಂಗಿ ಹೋಗಲೂಬಹುದು.''');
INSERT INTO "kannada" VALUES(18,42,'18:42','ಕೊನೆಗೆ ಆತನ ಫಲಗಳು ಸರ್ವನಾಶವಾದವು. ಅವನು ತನ್ನ ತೋಟಗಳು ತಡಿಕೆಗಳ ಮೇಲೆ ಮಗುಚಿ ಬಿದ್ದಾಗ ತಾನು ಅದಕ್ಕಾಗಿ ಮಾಡಿದ ವೆಚ್ಚವನ್ನು ನೆನೆಸಿ ಮರುಗಿದನು. `ನಾನು ನನ್ನ ಪ್ರಭುವಿನೊಂದಿಗೆ ಯಾರನ್ನೂ ಜೊತೆ ಸೇರಿಸದೇ ಇರುತ್ತಿದ್ದರೆ ನನಗೆ ಈ ಪರಿಸ್ಥಿತಿ ಬರುತ್ತಿರಲಿಲ್ಲ.'' ಎಂದನು.');
INSERT INTO "kannada" VALUES(18,43,'18:43','. ಅಲ್ಲಾಹನ ಹೊರತು ಸಹಾಯ ಮಾಡಬಲ್ಲ ಯಾವ ತಂಡವೂ ಆತನಿಗೆ ಒದಗಿಬರಲಿಲ್ಲ. ಅವನು ಸ್ವಂತ ಸಹಾಯವನ್ನೂ ಪಡೆಯದಾದನು.');
INSERT INTO "kannada" VALUES(18,44,'18:44','ಅಲ್ಲಿ ಅಧಿಪತ್ಯವು ಸತ್ಯಪೂರ್ಣವಾದ ಅಲ್ಲಾಹನಿಗೆ ಮಾತ್ರ. ಅವನು ಅತ್ಯುತ್ತಮ ಪ್ರತಿಫಲವನ್ನೂ ಫಲಿತಾಂಶವನ್ನೂ ನೀಡಬಲ್ಲವನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(18,45,'18:45','(ಓ ಪ್ರವಾದಿವರ್ಯರೇ,) ಇಹಜೀವನದ ಉಪಮೆ ಯನ್ನು ಅವರಿಗೆ ವಿವರಿಸಿ ಕೊಡಿರಿ. ಅದು ಆಕಾಶ ದಿಂದ ನಾವು ಸುರಿಸಿ ಕೊಡುವ ಮಳೆ ನೀರಿನಂತಿದೆ. ತನ್ಮೂಲಕ ಭೂಮಿಯ ಬೆಳೆಯು ದಟ್ಟವಾಗಿ ಬೆಳೆಯುತ್ತದೆ. ನಂತರ ಅದು ಗಾಳಿಯಲ್ಲಿ ಹಾರಿ ಹೋಗುವ ಹೊಟ್ಟಾಗಿ ಬಿಡುತ್ತದೆ. ಅಲ್ಲಾಹು ಸಕಲ ವಸ್ತುಗಳ ಮೇಲೆ ಸರ್ವ ಸಮರ್ಥನು.');
INSERT INTO "kannada" VALUES(18,46,'18:46','ಸೊತ್ತು ಸಂತಾನವು ಲೌಕಿಕ ಜೀವನದ ಶೃಂಗಾರ. ಶಾಶ್ವತವಾಗಿ ಉಳಿಯುವ ಪುಣ್ಯ ಕಾರ್ಯ ಗಳೇ ತಮ್ಮ ಪ್ರಭುವಿನ ಬಳಿ ಸತ್ಫಲ ಹಾಗೂ ಆಸೆಪಡಲು ಯೋಗ್ಯವಾದುದು .');
INSERT INTO "kannada" VALUES(18,47,'18:47','ಪರ್ವತಗಳನ್ನು ನಾವು ಹಾರಿಸುವ ದಿನವನ್ನು ನೆನಪಿಸಿರಿ. ಅಂದು ಭೂಮಿಯನ್ನು ತಾವು ಬಟ್ಟ ಬಯಲಿನಂತೆ ಕಾಣುವಿರಿ. ಯಾರೊಬ್ಬನನ್ನೂ ಬಿಟ್ಟು ಹೋಗದಂತೆ ಸರ್ವ ಜನರನ್ನು ನಾವಲ್ಲಿ ಒಟ್ಟುಗೂಡಿಸಲಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(18,48,'18:48','ತಮ್ಮ ಪ್ರಭುವಿನ ಸನ್ನಿಧಿಗೆ ಅವರೆಲ್ಲರನ್ನು ಸಾಲು ಸಾಲಾಗಿ ತರಲಾಗುವುದು. (ಓ ಪುನರು ತ್ಥಾನ ನಿಷೇಧಿಗಳೇ,) ಪ್ರಥಮ ಬಾರಿಗೆ ನಾವು ನಿಮ್ಮನ್ನು ಸೃಷ್ಟಿಸಿದ ಪ್ರಕಾರ ಇದೀಗ ನಮ್ಮೆಡೆಗೆ ನೀವು ಬಂದಿರುವಿರಿ. ಆದರೆ ಪುನರುತ್ಥಾನಕ್ಕಾಗಿ ಯಾವುದೇ ವಾಗ್ದತ್ತ ಸಮಯವನ್ನು ನಾವು ನಿಶ್ಚಯಿಸಿಲ್ಲವೆಂದು ನೀವು ಬಗೆಯುತ್ತಿದ್ದಿರಿ.');
INSERT INTO "kannada" VALUES(18,49,'18:49','ಅಂದು ಪ್ರತಿಯೊಬ್ಬರ ಕೈಗಳಿಗೆ ಕರ್ಮಪತ್ರವನ್ನು ವಿತರಿಸಲಾಗುವುದು. ಆಗ ಅದರಲ್ಲಿರುವ ತಮ್ಮ ಕರ್ಮಗಳ ಬಗ್ಗೆ ಅಪರಾಧಿಗಳು ಅಂಜಿ ತತ್ತರಿಸು ವುದನ್ನು ತಾವು ಕಾಣುವಿರಿ. ಆಗ ಅವರು ಹೇಳು ವರು `ಅಯ್ಯೋ, ನಮ್ಮ ನಾಶವೇ, ಇದೆಂಥ ಗ್ರಂಥ! ಯಾವುದೇ ಸಣ್ಣ ಹಾಗೂ ದೊಡ್ಡ ವಿಷಯವನ್ನು ಇದರಲ್ಲಿ ಕರಾರುವಾಕ್ಕಾಗಿ ಲಿಖಿತಗೊಳಿಸದೆ ಬಿಟ್ಟಿಲ್ಲವಲ್ಲ. ಅವರು ಏನೆಲ್ಲ ಮಾಡಿದ್ದಾರೆ, ಅವ ನ್ನೆಲ್ಲ ಅವರು ಕಣ್ಣಾರೆ ಕಾಣುವರು. ತಮ್ಮ ಪ್ರಭು ಯಾರಿಗೂ ಕಿಂಚಿತ್ತೂ ಅನ್ಯಾಯ ಮಾಡಲಾರ.');
INSERT INTO "kannada" VALUES(18,50,'18:50','`ನೀವು ಆದಮರಿಗೆ ಸುಜೂದ್ ಮಾಡಿರಿ'' ಎಂದು ನಾವು ದೇವಚರರಿಗೆ ಹೇಳಿದಾಗ ಇಬ್ಲೀಸನ ಹೊರತು ಎಲ್ಲರೂ ಸುಜೂದ್ ಮಾಡಿದರು. ಅವನು ಖೇಚರ ವರ್ಗದವನಾಗಿದ್ದನು. ತನ್ನಿ ಮಿತ್ತ ಅವನು ತನ್ನ ಪ್ರಭುವಿನ ಆಜ್ಞೆಯನ್ನು ಧಿಕ್ಕರಿಸಿದನು. (ಓ ಜನರೇ,) ಹೀಗಿರುವಾಗ ನನ್ನನ್ನು ಬಿಟ್ಟು ಅವನನ್ನೂ ಅವನ ಸಂತಾನವನ್ನೂ ನೀವು ಆಪ್ತರನ್ನಾಗಿ ಮಾಡುವುದು ಸರಿಯೇ? ನಿಜದಲ್ಲಿ ಅವರು ನಿಮ್ಮ ಶತ್ರುಗಳು. ಅಕ್ರಮಿಗಳಿಗೆ ಬದಲಿಯಾಗಿ ಅತ್ಯಂತ ಕೆಟ್ಟ ಫಲವೇ ದಕ್ಕಿದೆ.');
INSERT INTO "kannada" VALUES(18,51,'18:51','ಭೂಮಿ ಮತ್ತು ಆಕಾಶಗಳ ಸೃಷ್ಟಿಯಲ್ಲಿ ಸ್ವತಃ ಅವರ ಸೃಷ್ಟಿಯಲ್ಲಿ ಕೂಡ ಅವರಾರನ್ನು ನಾನು ಸಾಕ್ಷಿಗಳನ್ನಾಗಿ ಮಾಡಿಲ್ಲ. ದಾರಿಗೆಡಿಸುವ ಪಿಶಾಚಿಗಳನ್ನು ನನ್ನ ಸಹಾಯಕರಾಗಿ ನಾನು ನಿಯ ಮಿಸಿಯೂ ಇಲ್ಲ . (ಹೀಗಿರುವಾಗ ನನ್ನ ಆರಾಧನೆಯಲ್ಲಿ ಅಂಥವರನ್ನು ಸಹಭಾಗಿಗೊಳಿಸಿದ್ದು ನ್ಯಾಯವೇ?)');
INSERT INTO "kannada" VALUES(18,52,'18:52','`ನೀವು ಬಗೆದ ನನ್ನ ಸಹಭಾಗಿಗಳನ್ನು ಕರೆ ಯಿರಿ'' ಎಂದು ಅಲ್ಲಾಹು (ಬಹುದೇವಾರಾಧಕರಿಗೆ) ಹೇಳುವ ದಿನವನ್ನು ನೆನಪಿಸಿ. ಆಗ ಅವರು ಕರೆದು ನೋಡುವರು. ಆದರೆ ಅವರಿಗೆ ಉತ್ತರ ಕೊಡಲಾರರು. ನಾವು ಅವರ ನಡುವೆ ದುರ್ಗಮ ಸ್ಥಳವನ್ನು ನಿಶ್ಚಯಿಸಿ ಕೊಡುವೆವು .');
INSERT INTO "kannada" VALUES(18,53,'18:53','ಅಪರಾಧಿಗಳು ನರಕವನ್ನು ಕಣ್ಣಾರೆ ಕಾಣುವರು ಮತ್ತು ನಾವು ಅದರಲ್ಲಿ ಬೀಳುವೆವೆಂದು ಅವರಿಗೆ ಮನದಟ್ಟಾಗುವುದು. ಅದರಿಂದ ಪಾರಾಗುವ ಯಾವುದೇ ದಾರಿಯನ್ನು ಅವರು ಕಂಡು ಕೊಳ್ಳಲಾರರು.');
INSERT INTO "kannada" VALUES(18,54,'18:54','ನಾವು ಈ ಖುರ್‍ಆನಿನಲ್ಲಿ ಜನರಿಗೆ ಎಲ್ಲ ತರದ ಉಪಮೆಗಳನ್ನು ವಿವಿಧ ರೂಪದಲ್ಲಿ ವಿವರಿಸಿ ದ್ದೇವೆ. ಆದರೆ ಮನುಷ್ಯನು ಅಧಿಕ ವಿಷಯಗ ಳಲ್ಲೂ ಹಠಮಾರಿ ಧೋರಣೆಯನ್ನು ಹೊಂದಿರುವನು.');
INSERT INTO "kannada" VALUES(18,55,'18:55','ಮನುಷ್ಯರನ್ನು ಮಾರ್ಗದರ್ಶನ ಬಂದಾಗ ಅದನ್ನು ನಂಬುವುದರಿಂದ ಹಾಗೂ ಅವರ ಪ್ರಭು ವಿನೊಡನೆ ಕ್ಷಮಾಯಾಚನೆ ಮಾಡುವುದರಿಂದ ಅವರನ್ನು ತಡೆದದ್ದು, ಗತಕಾಲದ ಜನಾಂಗಗಳ ಗತಿ ಬರುವುದು ಅಥವಾ ಮುಖಾಮುಖಿ ಶಿಕ್ಷೆ ಅವರಿಗೆ ಬರುವುದು ಎಂಬ ಅವರ ವಿಧಿ ಲಿಖಿತ ವಾಗಿತ್ತು.');
INSERT INTO "kannada" VALUES(18,56,'18:56','ಸುವಾರ್ತೆ ಹಾಗೂ ಮುನ್ನೆಚ್ಚರಿಕೆ ಕೊಡುವವ ರನ್ನಾಗಿಯೇ ಹೊರತು ಸಂದೇಶವಾಹಕರನ್ನು ನಾವು ನಿಯೋಜಿಸುತ್ತಿಲ್ಲ. ಆದರೆ ಸತ್ಯನಿಷೇಧಿಗಳು ಸತ್ಯವನ್ನು ದಮನಿಸಲು ಮಿಥ್ಯ ಮಾರ್ಗದ ಮೂಲಕ ವಾದ ಹೂಡುತ್ತಾರೆ. ಅವರು ನನ್ನ ದೃಷ್ಟಾಂತಗಳನ್ನೂ ಅವರಿಗೆ ನೀಡಲಾದ ಮುನ್ನೆಚ್ಚರಿಕೆಯನ್ನೂ ಪರಿಹಾಸ್ಯವನ್ನಾಗಿ ಮಾಡಿದ್ದಾರೆ .');
INSERT INTO "kannada" VALUES(18,57,'18:57','ತನ್ನ ಪ್ರಭುವಿನ ದೃಷ್ಟಾಂತಗಳ ಬಗ್ಗೆ ಬೋಧನೆ ನೀಡಿಯೂ ಯಾರು ಅದರಿಂದ ವಿಮುಖನಾಗು ತ್ತಾನೆ ಮತ್ತು ತನ್ನ ಸ್ವಂತ ಕೈಗಳಿಂದ ಮಾಡಿದ ಪಾಪ ಕೃತ್ಯಗಳನ್ನು ಮರೆತು ಬಿಡುತ್ತಾನೆ, ಅವನಿಗಿಂತ ದೊಡ್ಡ ಅಕ್ರಮಿ ಬೇರೆ ಯಾರೂ ಇಲ್ಲ. ಖಂಡಿತ ನಾವು ಅವರ ಹೃದಯಗಳಿಗೆ ಖುರ್ ಆನ್ ಗ್ರಹಿಸದಂತೆ ಮಾಡುವ ಪರದೆಯನ್ನು ಮತ್ತು ಅವರ ಕಿವಿಗಳಿಗೆ ಕಿವುಡನ್ನು ಇರಿಸಿದ್ದೇವೆ. ಈ ಕಾರಣದಿಂದ ನೀವು ಅವರನ್ನು ನೇರದಾರಿಗೆ ಕರೆದರೆ ಅವರೆಂದಿಗೂ ಸನ್ಮಾರ್ಗವನ್ನು ಪಡೆಯಲಾರರು.');
INSERT INTO "kannada" VALUES(18,58,'18:58','ಮಹಾಕ್ಷಮಾಶೀಲನಾದ ನಿಮ್ಮ ಪ್ರಭುವು ಕರು ಣಾಮಯನೂ ಆಗಿರುತ್ತಾನೆ. ಮಾಡಿದ ತಪ್ಪಿಗೆ ತಕ್ಕ ಶಿಕ್ಷೆಯನ್ನು (ಇಹಲೋಕದಲ್ಲೇ) ಕೊಡುವುದಿದ್ದರೆ ಅವನು ಶಿಕ್ಷೆಯನ್ನು ತ್ವರಿತ ಗೊಳಿಸುತಿದ್ದನು. ಆದರೆ ಶಿಕ್ಷಾಕ್ರಮ ಕೈಗೊಳ್ಳಲು ನಿಶ್ಚಿತ ಅವಧಿಯನ್ನು ನಿಗದಿಪಡಿಸಲಾಗಿದೆ. ಆ ಅವಧಿ ತಲುಪಿಬಿಟ್ಟರೆ ಅದನ್ನು ತಪ್ಪಿಸಿ ಪಾರಾಗು ವ ಅಭಯ ಸ್ಥಾನವನ್ನು ಅವರು ಪಡೆಯಲಾರರು.');
INSERT INTO "kannada" VALUES(18,59,'18:59','ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸಿ ಅನ್ಯಾಯವೆಸಗಿದಾಗ ನಾವು ಅದೆಷ್ಟೋ ನಾಡಿನ ನಿವಾಸಿಗಳನ್ನು ನಾಶ ಮಾಡಿದ್ದೇವೆ. ಅವರ ನಾಶಕ್ಕೆ ನಾವು ನಿಶ್ಚಿತ ಅವಧಿಯನ್ನು ನಿಗದಿಪಡಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(18,60,'18:60','`ಎರಡು ಸಾಗರ ಸಂಗಮಿಸುವ ಸ್ಥಾನಕ್ಕೆ ತಲು ಪುವವರೆಗೆ ನಾನು ಯಾತ್ರೆಯನ್ನು ಮುಂದುವರಿ ಸುವೆನು. ಇಲ್ಲವೇ ನಾನು ದೀರ್ಘಕಾಲದವರೆಗೆ ನಡೆಯುತ್ತಲೇ ಇರುವೆನು'' ಎಂದು ಪ್ರವಾದಿ ಮೂಸಾ, ತಮ್ಮ ಜವಾನನೊಡನೆ ಹೇಳಿದ ಘಟನೆಯನ್ನು ನೆನಪಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(18,61,'18:61','ಅವರಿಬ್ಬರು ಕೂಡುಸಾಗರಕ್ಕೆ ತಲುಪಿದಾಗ ತಮ್ಮ ಮೀನನ್ನು ಜೊತೆಗೊಯ್ಯಲು ಮರೆತು ಬಿಟ್ಟರು. ಆಗ ಅದು ಸಮುದ್ರದಲ್ಲಿ ಸುರಂಗ ದಾರಿಯನ್ನು ಹಿಡಿಯಿತು.');
INSERT INTO "kannada" VALUES(18,62,'18:62','ತರುವಾಯ ಅವರು ಆ ಸ್ಥಳವನ್ನು ಬಿಟ್ಟು ಮುಂದೆ ಸಾಗಿದಾಗ ತಮ್ಮ ಜವಾನನೊಡನೆ `ನಮ್ಮ ಬೆಳಗಿನ ತಿಂಡಿ ನೀಡು, ಇಂದು ನಾವು ನಡೆದು ಸುಸ್ತಾಗಿದ್ದೇವೆ'' ಎಂದರು.');
INSERT INTO "kannada" VALUES(18,63,'18:63','ಆಗ ಜವಾನನು, `ಗಮನಿಸುವಿರಾ? ನಾವು ಆ ಬಂಡೆಗಲ್ಲಿನಲ್ಲಿ ತಂಗಿದ್ದಾಗ ನಾನು ಮೀನನ್ನು ಮರೆತುಬಿಟ್ಟಿದ್ದೆ. ಅದನ್ನು ತಮ್ಮಲ್ಲಿ ಹೇಳದಂತೆ ಶೈತಾನನು ನನ್ನನ್ನು ಮರೆವೆಗೊಳಿಸಿದ್ದನು. ಮೀನು ಸಮುದ್ರದಲ್ಲಿ ವಿಸ್ಮಯದ ದಾರಿಯನ್ನು ಹಿಡಿಯಿತು'' ಎಂದನು.');
INSERT INTO "kannada" VALUES(18,64,'18:64','ಆಗ ಮೂಸಾ, `ನಾವು ಇದುವರೆಗೂ ಹುಡುಕುತ್ತಿರುವ ಸ್ಥಳ ಅದುವೇ'' ಎಂದರು. ನಂತರ ಅವರಿ ಬ್ಬರು ತಮ್ಮ ಕಾಲು ಹೆಜ್ಜೆಯ ಗುರುತು ಹಿಡಿದು ಹಿಂದೆ ನಡೆದರು.');
INSERT INTO "kannada" VALUES(18,65,'18:65','ಆಗ ಅವರು ನಮ್ಮ ದಾಸರ ಪೈಕಿ ಓರ್ವ ದಾಸನನ್ನು ಕಂಡರು. ಆ ದಾಸನಿಗೆ ನಮ್ಮ ಪಾಲಿನ ಅನುಗ್ರಹವನ್ನು ಕೊಟ್ಟಿದ್ದೆವು. ಅಲ್ಲದೆ ನಮ್ಮ ವತಿಯಿಂದ ವಿಶೇಷ ಜ್ಞಾನವನ್ನು ಕಲಿಸಿದ್ದೆವು .');
INSERT INTO "kannada" VALUES(18,66,'18:66','ಮೂಸಾ ಅವರೊಡನೆ; ‘ನಿಮಗೆ ಕಲಿಸಿ ಕೊಡಲಾಗಿರುವ ಸನ್ಮಾರ್ಗ ಜ್ಞಾನವನ್ನು ನನಗೂ ಕಲಿಸಿ ಕೊಡಲೆಂದು ನಾನು ನಿಮ್ಮ ಜೊತೆ ಬರಲೇ’ ಎಂದು ಕೇಳಿದರು.');
INSERT INTO "kannada" VALUES(18,67,'18:67','ಆಗ ಅವರು ಹೇಳಿದರು, ‘ನನ್ನ ಜೊತೆ ನಿಲ್ಲಲು ತಮಗೆ ಖಂಡಿತ ಸಹನೆ ಬರಲಾರದು’.');
INSERT INTO "kannada" VALUES(18,68,'18:68','‘ತಾವು ಗ್ರಹಿಸದ ವಿಷಯದ ಬಗ್ಗೆ ತಾವು ಹೇಗೆ ಸಹಿಸುವಿರಿ ?’ ');
INSERT INTO "kannada" VALUES(18,69,'18:69','ಆಗ ಮೂಸಾ `ಇನ್‍ಶಾ ಅಲ್ಲಾಹ್, ತಾವು ನನ್ನನ್ನು ಸಹನೆಯುಳ್ಳವನಾಗಿ ಕಾಣುವಿರಿ. ನಾನು ತಮ್ಮ ಯಾವುದೇ ಆದೇಶವನ್ನು ಉಲ್ಲಂಘಿಸಲಾರೆ'' ಎಂದರು.');
INSERT INTO "kannada" VALUES(18,70,'18:70','ಆಗ ಅವರು, ‘ತಾವು ನನ್ನ ಜತೆ ಬರುವುದಾದರೆ ನಾನು ಏನೇ ಮಾಡಿದರೂ ನಾನೇ ನಿಮಗೆ ವಿವರಿಸಿ ಕೊಡುವವರೆಗೆ ಅದರ ಬಗ್ಗೆ ಏನನ್ನೂ ಕೇಳಬಾರದು’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(18,71,'18:71','ತರುವಾಯ ಅವರಿಬ್ಬರೂ (ಕಡಲತೀರದಲ್ಲಿ ನಡೆಯುತ್ತ) ಸಾಗಿದರು. ಕೊನೆಗೆ ಅವರು (ಅಲ್ಲೇ ಹಾದು ಹೋಗುತ್ತಿದ್ದ) ಹಡಗಿಗೆ ಹತ್ತಿದರು. (ನಡು ಗಡಲಿಗೆ ತಲುಪಿದಾಗ) ಆ ವ್ಯಕ್ತಿ ಹಡಗಿನ ಹಲಗೆಯನ್ನು ಕಡಿದು ತೆಗೆದರು. ಆಗ ಮೂಸಾ ಆ ವ್ಯಕ್ತಿಗೆ ಹೇಳಿದರು. ‘ನೀವು ಹಡಗಿನ ಜನರನ್ನು ಮುಳುಗಿಸಿ ಬಿಡಬೇಕೆಂದು ಇದನ್ನು ಕಡಿದು ತೆಗೆದಿರಾ? ನಿಜಕ್ಕೂ ತಾವು ಬಲು ದೊಡ್ಡ ತಪ್ಪನ್ನೇ ಮಾಡಿದಿರಿ’.');
INSERT INTO "kannada" VALUES(18,72,'18:72','ಆಗ ಅವರು; ‘ನನ್ನ ಜತೆಗೆ ನಿಮಗೆ ಸಹನೆ ಪಾಲಿಸಲು ಸಾಧ್ಯವಿಲ್ಲವೆಂದು ನಾನು ಮೊದಲೇ ಹೇಳಿರಲಿಲ್ಲವೇ?’ ಎಂದು ಕೇಳಿದರು.');
INSERT INTO "kannada" VALUES(18,73,'18:73','ಆಗ ಮೂಸಾ, ‘ಮರೆತು ಹೇಳಿದುದಕ್ಕಾಗಿ ನನ್ನ ಮೇಲೆ ಕ್ರಮ ಕೈಗೊಳ್ಳಬೇಡಿರಿ . ನನ್ನ ವಿಷಯ ದಲ್ಲಿ ನಿಷ್ಟುರವಾಗಿ ವರ್ತಿಸಬೇಡಿರಿ. ಕನಿಕರ ತೋರಿರಿ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(18,74,'18:74','ಅನಂತರ ಅವರಿಬ್ಬರೂ (ಹಡಗಿನಿಂದಿಳಿದು) ನಡೆಯುತ್ತ ಸಾಗಿದರು. ಕೊನೆಗೆ ಅವರು ಒಬ್ಬ ಬಾಲಕನನ್ನು ಕಂಡರು. ಕೂಡಲೇ ಆ ವ್ಯಕ್ತಿ ಅವ ನನ್ನು ಕೊಲೆ ಮಾಡಿದರು. ಮೂಸಾ ಹೇಳಿ ದರು. ‘ಪ್ರಾಯ ತುಂಬದ ಮುಗ್ಧ ಪ್ರಾಣವೊಂದನ್ನು ಮರಣ ದಂಡನೆಯ ಹೊರತು ಹತ್ಯೆ ಮಾಡಿದಿರಲ್ಲ ವೇ? ನಿಜಕ್ಕೂ ನೀವು ನೀಚ ಕಾರ್ಯವನ್ನೆಸಗಿದಿರಿ’.');
INSERT INTO "kannada" VALUES(18,75,'18:75','ಆಗ ಅವರು ‘ನನ್ನ ಜೊತೆಗೆ ನಿಮಗೆ ಸಹನೆ ವಹಿಸಲು ಸಾಧ್ಯವಿಲ್ಲವೆಂದು ನಾನು ಮೊದಲೇ ಹೇಳಿರಲಿಲ್ಲವೇ?’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(18,76,'18:76','ಆಗ ಮೂಸಾ ‘ಇನ್ನು ಮುಂದೆ ಏನಾದರೂ ನಿಮ್ಮಲ್ಲಿ ನಾನು ಕೇಳಿದರೆ ನನ್ನೊಂದಿಗೆ ಸಹ ವಾಸ ಮಾಡದಿರಿ. ನನ್ನಿಂದ ನಿಮಗೆ (ನನ್ನನ್ನು ಬೇರ್ಪಡಿಸುವ) ಧಾರಾಳ ಕಾರಣ ಸಿಕ್ಕಿದೆ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(18,77,'18:77','ಅನಂತರ ಅವರು ಮುಂದೆ ಸಾಗಿದರು. ಕೊನೆಗೆ ಒಂದು ಗ್ರಾಮಕ್ಕೆ ಬಂದರು. ಅಲ್ಲಿಯ ಜನರಲ್ಲಿ ಊಟ ಕೇಳಿದರು. ಆದರೆ ಇವರನ್ನು ಸತ್ಕರಿಸಲು ಅವರು ನಿರಾಕರಿಸಿದರು. ಆಗ ಅಲ್ಲೊಂದು ಕುಸಿದು ಬೀಳಲಿಕ್ಕಾದ ಗೋಡೆಯನ್ನು ಕಂಡರು. ಆ ವ್ಯಕ್ತಿ ಅದನ್ನು ಸರಿಪಡಿಸಿ ನಿಲ್ಲಿಸಿದರು. ಮೂಸಾ ಹೇಳಿದರು; ‘ನಿಮಗೆ ಇದನ್ನು ಸರಿ ಮಾಡಲೇಬೇಕೆಂದಿದ್ದರೆ ಮಜೂರಿಯನ್ನು ಪಡೆಯಬಹುದಿತ್ತಲ್ಲ?’');
INSERT INTO "kannada" VALUES(18,78,'18:78','ಆಗ ಅವರು ಹೀಗೆ ಹೇಳಿದರು. ‘ನನ್ನ ಹಾಗೂ ನಿಮ್ಮ ನಡುವೆ ಬೇರ್ಪಡುವ ಸಮಯವಿದು. ಇನ್ನು ನಾನು ನಿಮಗೆ ತಾಳಿಕೊಳ್ಳಲಾಗದ ವಿಷಯಗಳ ಸೃಷ್ಟೀಕರಣವನ್ನು ವಿವರಿಸಿ ಕೊಡುತ್ತೇನೆ .');
INSERT INTO "kannada" VALUES(18,79,'18:79','ಆ ಹಡಗು ಕಡಲಲ್ಲಿ ದುಡಿದು ಹೊಟ್ಟೆ ಹೊರೆಯುತ್ತಿದ್ದ ಬಡವರದ್ದಾಗಿತ್ತು. ಮುಂದೆ ಹೋಗುವಾಗ ದಾರಿಯಲ್ಲಿ ಅಕ್ರಮಿಯಾದ ರಾಜನೊಬ್ಬನಿದ್ದು ಅವನು (ಕೇಡಿಲ್ಲದ) ಎಲ್ಲ ಹಡಗುಗಳನ್ನು ಬಲ ವಂತವಾಗಿ ವಶಪಡಿಸಿಕೊಳ್ಳುತ್ತಿದ್ದನು. ಆದ್ದರಿಂದ ನಾನದನ್ನು ಕೇಡು ಮಾಡಲು ಬಯಸಿದೆ.');
INSERT INTO "kannada" VALUES(18,80,'18:80','ಆ ಬಾಲಕನ ತಂದೆ ತಾಯಿಗಳು ಸತ್ಯ ವಿಶ್ವಾಸಿ ಗಳಾಗಿದ್ದರು. ಅವನು ಬೆಳೆದು ದೊಡ್ಡವನಾದರೆ ತನ್ನ ಹೆತ್ತವರನ್ನು ಅತಿಕ್ರಮ ಹಾಗೂ ಸತ್ಯ ನಿಷೇಧಕ್ಕೆ ಬಲವಂತವಾಗಿ ದೂಡಿಹಾಕುವ ಭಯ ನಮಗೆ ಕಾಡಿತು.');
INSERT INTO "kannada" VALUES(18,81,'18:81','ಹೀಗಾಗಿ ಆತನಿಗಿಂತ ಶುದ್ಧ ಚಾರಿತ್ರ್ಯವಂತನೂ ನಿಕಟ ದಯಾವಂತನೂ ಆದ ಸಂತಾನವನ್ನು ಅವರ ಪ್ರಭು ಅವರಿಗೆ ಕರುಣಿಸುವಂತೆ ನಾವು ಬಯಸಿದೆವು.');
INSERT INTO "kannada" VALUES(18,82,'18:82','ಆ ಗೋಡೆಯು ಪಟ್ಟಣದ ಇಬ್ಬರು ಅನಾಥ ಮಕ್ಕಳದ್ದಾಗಿತ್ತು. ಅದರ ಅಡಿಯಲ್ಲಿ ಮಕ್ಕಳಿಗಾಗಿ ಕೂಡಿಟ್ಟ ನಿಕ್ಷೇಪವಿತ್ತು. ಅವರ ತಂದೆ ಓರ್ವ ಸಜ್ಜನ ವ್ಯಕ್ತಿಯಾಗಿದ್ದರು . ಆದ್ದರಿಂದ ಅವರಿಬ್ಬರು ಪ್ರೌಢಾವಸ್ಥೆಗೆ ತಲುಪಿದ ಮೇಲೆ ನಿಧಿಯನ್ನು ಅವರು ಹೊರತೆಗೆಯಲೆಂದು ನಿಮ್ಮ ಪ್ರಭು ಬಯಸಿದ್ದನು. ಇದು ನಿಮ್ಮ ರಕ್ಷಕನ ಮಹತ್ತರವಾದ ಕೃಪೆಯ ಫಲ. ನಾನಿದನ್ನು ನನ್ನ ಸ್ವಂತ ಅಭಿಪ್ರಾಯ ಪ್ರಕಾರ ಮಾಡಿಲ್ಲ. ಇವಿಷ್ಟು ನಿಮಗೆ ತಾಳಿಕೊಳ್ಳಲಾಗದ ವಿಷಯಗಳ ಸಾರಾಂಶ’ .');
INSERT INTO "kannada" VALUES(18,83,'18:83','(ಓ ಪ್ರವಾದಿವರ್ಯರೇ,) ದ್ಸುಲ್ಖರ್ನೈನ್‍ರ ಬಗ್ಗೆ ಇವರು ನಿಮ್ಮಲ್ಲಿ ಪ್ರಶ್ನಿಸುತ್ತಾರೆ. ಅವರ ಚರಿತ್ರೆ ಯಿಂದ ಕೆಲವು ವಿವರವನ್ನು ನಾನು ನಿಮಗೆ ಓದಿ ಕೊಡುವೆನೆಂದು ಹೇಳಿರಿ.');
INSERT INTO "kannada" VALUES(18,84,'18:84','ನಾವು ಅವರಿಗೆ ಭೂಮಿಯಲ್ಲಿ ಪ್ರಭುತ್ವವನ್ನು ದಯಪಾಲಿಸಿದ್ದೆವು. ಅವರಿಗೆ ಅಗತ್ಯವಾದ ಎಲ್ಲ ಕಾರ್ಯಕ್ಕೂ ದಾರಿಯನ್ನು ಸುಗಮಗೊಳಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(18,85,'18:85','ಒಮ್ಮೆ ಅವರು (ಪಶ್ಚಿಮ) ಮಾರ್ಗಕ್ಕೆ ಹೊರಟರು.');
INSERT INTO "kannada" VALUES(18,86,'18:86','ಕೊನೆಗೆ ಸೂರ್ಯಾಸ್ತದ ಸ್ಥಳಕ್ಕೆ ತಲುಪಿದಾಗ ಕಪ್ಪು ಕೆಸರಿನ ಚಿಲುಮೆಯಲ್ಲಿ ಸೂರ್ಯನು ಮುಳು ಗುವಂತೆ ಅವರಿಗೆ ಕಂಡಿತು. ಅಲ್ಲೊಂದು ಜನಾಂ ಗವನ್ನೂ ಅವರು ಕಂಡರು. ನಾವು ಅವರಿಗೆ ಹೀಗೆ ಹೇಳಿದೆವು. ‘ಓ ದ್ಸುಲ್ ಖರ್ನೈನ್, ಒಂದೋ ಅವರಿಗೆ ಶಿಕ್ಷೆ ನೀಡಿರಿ, ಇಲ್ಲವೇ ಉತ್ತಮ ಕ್ರಮ ಕೈಗೊಳ್ಳಿರಿ’.');
INSERT INTO "kannada" VALUES(18,87,'18:87','ಆಗ ಅವರು ಹೇಳಿದರು, ‘ಯಾರು ಅನ್ಯಾಯ ವೆಸಗುತ್ತಾರೆ, ಅವನನ್ನು ನಾವು ಶಿಕ್ಷಿಸಲಿದ್ದೇವೆ. ನಂತರ ಅವನನ್ನು ತನ್ನ ಪ್ರಭುವಿನ ಕಡೆಗೆ ಮರಳಿಸಲಾಗುವುದು. ಆಗ ಪ್ರಭು ಆತನಿಗೆ ಇನ್ನಷ್ಟು ಕಠಿಣ ಶಿಕ್ಷೆ ಕೊಡುವನು’.');
INSERT INTO "kannada" VALUES(18,88,'18:88','ಆದರೆ ಯಾರು ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿ, ಸತ್ಕರ್ಮ ಕೈಗೊಳ್ಳುತ್ತಾರೆ ಅಂಥವರಿಗೆ ಅತ್ಯಂತ ಉತ್ಕøಷ್ಟವಾದ ಪ್ರತಿಫಲವಿದೆ. ನಂತರ ಆತನಿಗೆ ಸರಳವಾದ ಆದೇಶಗಳನ್ನು ನಾವು ನೀಡಲಿದ್ದೇವೆ’.');
INSERT INTO "kannada" VALUES(18,89,'18:89','ಅನಂತರ ಅವರು ಇನ್ನೊಂದು ದಾರಿಗೆ ಸಾಗಿದರು.');
INSERT INTO "kannada" VALUES(18,90,'18:90','ಕೊನೆಗೆ ಸೂರ್ಯನು ಉದಯಿಸುವ ಪೂರ್ವ ಸೀಮೆಗೆ ತಲುಪಿದರು. ಅಲ್ಲಿ ಅವರಿಗೆ ಬಿಸಿಲ ದಗೆಯನ್ನು ತಡೆಯುವ ಯಾವುದೇ ಮರೆಯನ್ನು ನಾವು ವ್ಯವಸ್ಥೆಗೊಳಿಸದಂಥ ಜನಾಂಗದ ಮೇಲೆ ಉದಯಿಸುತ್ತಿರುವ ಸೂರ್ಯನನ್ನು ಕಂಡರು.');
INSERT INTO "kannada" VALUES(18,91,'18:91','ದ್ಸುಲ್‍ಖರ್ನೈನ್‍ರ ವಿಷಯ ನಾವು ಹೇಳಿದಂತೆಯೇ ಇದೆ. ಅವರ ಬಳಿ ಇರುವುದರ ಬಗ್ಗೆ ನಮಗೆ ಸಂಪೂರ್ಣ ಜ್ಞಾನವಿದೆ.');
INSERT INTO "kannada" VALUES(18,92,'18:92','ಅನಂತರ ಅವರು ಇನ್ನೊಂದು ದಾರಿಯಲ್ಲಿ ನಡೆದರು.');
INSERT INTO "kannada" VALUES(18,93,'18:93','ಕೊನೆಗೆ ಎರಡು ಪರ್ವತಗಳ ನಡುವೆ ತಲುಪಿ ದಾಗ ಅದರ ಹತ್ತಿರದಲ್ಲಾಗಿ ಮಾತನ್ನು ಸರಿ ಯಾಗಿ ಗ್ರಹಿಸದ ಜನಾಂಗವನ್ನು ಕಂಡರು.');
INSERT INTO "kannada" VALUES(18,94,'18:94','ಆಗ ಅವರು ‘ಓ ದುಲ್‍ಖರ್ನೈನ್, ಯಅï ಜೂಜ್ ಮತ್ತು ಮಅïಜೂಜರು ಈ ಭೂಪ್ರ ದೇಶದಲ್ಲಿ ಅನ್ಯಾಯವೆಸಗುತ್ತಿದ್ದಾರೆ. ಆದ್ದರಿಂದ ನಮ್ಮ ಹಾಗೂ ಅವರ ನಡುವೆ ಒಂದು ತಡೆ ಗೋಡೆಯನ್ನು ನಿರ್ಮಿಸಿಕೊಡುವ ಷರತ್ತಿನ ಮೇರೆಗೆ ನಾವು ನಿಮಗೆ ಅಪಾರ ಮೊತ್ತವನ್ನು ಪ್ರತಿಫಲವಾಗಿ ನಿಶ್ಚಯಿಸಿ ಕೊಡಲೇ?’ ಎಂದು ಕೇಳಿದರು.');
INSERT INTO "kannada" VALUES(18,95,'18:95','ಅದಕ್ಕೆ ಅವರು ‘ನನ್ನ ಪ್ರಭು ನನ್ನ ವಶಕ್ಕೆ ಕೊಟ್ಟಿರುವ ಅನುಗ್ರಹವೇ ನನಗೆ ನೀವು ಕೊಡುವ ಪ್ರತಿಫಲಕ್ಕಿಂತ ಲೇಸು. ಆದರೆ ನೀವು ನನಗೆ ಶಕ್ತಿ ಕೊಟ್ಟು ಸಹಾಯ ಮಾಡಿರಿ. ಅವರ ಮತ್ತು ನಿಮ್ಮ ನಡುವೆ ಬಲಿಷ್ಟವಾದ ತಡೆಗೋಡೆಯನ್ನು ನಾನು ನಿರ್ಮಿಸಿ ಕೊಡುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(18,96,'18:96','ನನಗೆ ಕಬ್ಬಿಣದ ತುಂಡುಗಳನ್ನು ತಂದು ಕೊಡಿರಿ’ ಎಂದರು. ಹೀಗೆ ಎರಡು ಪರ್ವತಗಳ ನಡುವೆ ಸಮಾನವಾಗಿ ಆ ಗೋಡೆ ಎತ್ತರಕ್ಕೆ ಏರಿದಾಗ ‘ನೀವು ಬೆಂಕಿಯುರಿಸಿ ಊದಿರಿ’ ಎಂದರು. ಕೊನೆಗೆ ಕಬ್ಬಿಣವನ್ನು ಬೆಂಕಿಯ ಕೆಂಡ ದಂತೆ ಮಾಡಿದಾಗ ಅವರು ‘ಕುದಿಯುವ ತಾಮ್ರ ವನ್ನು ತನ್ನಿರಿ, ಅದನ್ನು ಇದರ ಮೇಲೆ ಹೊಯ್ದು ಬಿಡುತ್ತೇನೆ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(18,97,'18:97','ನಂತರ ಈ ತಡೆಯನ್ನೇರಿ ಬರಲು ಅವರಿಗೆ (ಯಅïಜೂಜ್ - ಮಅïಜೂಜರಿಗೆ) ಸಾಧ್ಯ ವಾಗಲಿಲ್ಲ. ಅದಕ್ಕೆ ಕನ್ನ ಕೊರೆದು ಹೊರಬರಲು ಅವರು ಅಸಮರ್ಥರಾದರು.');
INSERT INTO "kannada" VALUES(18,98,'18:98','ದುಲ್‍ಖರ್ನೈನ್ ಹೇಳಿದರು. ‘ಈ ತಡೆಯು ನನ್ನ ಪ್ರಭುವಿನಿಂದ ಲಭಿಸಿದ ಒಂದು ಅನುಗ್ರಹವಾ ಗಿದ್ದು ಮುಂದೆ ಪ್ರಭುವಿನ ನಿಶ್ಚಿತ ಅವಧಿ ಬಂದಾಗ ಅವನು ಇದನ್ನು ನೆಲಸಮಗೊಳಿಸುವನು. ನನ್ನ ಪ್ರಭುವಿನ ನಿಶ್ಚಯವು ಅಚಲವಾಗಿರುತ್ತದೆ’.');
INSERT INTO "kannada" VALUES(18,99,'18:99','ಅಂದು ಒಬ್ಬರ ಮೇಲೆ ಇನ್ನೊಬ್ಬರನ್ನು ಪರಸ್ಪರ ತೆರೆಗಳು ಅಪ್ಪಳಿಸಿ ಏರಿಬರುವಂತೆ ಬಿಟ್ಟು ಬಿಡಲಿದ್ದೇವೆ. ಕಹಳೆ ಮೊಳಗಿದ ಮೇಲೆ ಅವರೆಲ್ಲರನ್ನು ಒಟ್ಟುಗೂಡಿಸಲಿದ್ದೇವೆ .');
INSERT INTO "kannada" VALUES(18,100,'18:100','ಸತ್ಯನಿಷೇಧಿಗಳ ಮುಂದೆ ಅಂದು ನಾವು ನರಕವನ್ನು ಪ್ರದರ್ಶಿಸಲಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(18,101,'18:101','ತಮ್ಮ ಕಣ್ಣುಗಳಿಗೆ ಪರದೆ ಬಿದ್ದು ನನ್ನ ಬೋಧನೆಯಿಂದ ವಿಮುಖರಾಗಿದ್ದ ಹಾಗೂ ಅದನ್ನು ಕಿವಿಗೊಟ್ಟು ಆಲಿಸಲು ಸಾಧ್ಯವಾಗದೇ ಹೋದ.');
INSERT INTO "kannada" VALUES(18,102,'18:102','ಸತ್ಯನಿಷೇಧಿಗಳು ನನ್ನನ್ನು ಬಿಟ್ಟು ನನ್ನ ದಾಸರನ್ನು ತಮ್ಮ ರಕ್ಷಕರನ್ನಾಗಿ ಮಾಡಲು ಬಯಸಿ ದ್ದಾರೆಯೇ? ಖಂಡಿತವಾಗಿಯೂ ನಾವು ಅವಿಶ್ವಾಸಿಗಳಿಗೆ ಸತ್ಕರಿಸಲು ನರಕವನ್ನು ಸಜ್ಜು ಗೊಳಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(18,103,'18:103','(ನಬಿಯವರೇ) ಹೇಳಿರಿ, ಕರ್ಮಫಲವು ಅತ್ಯಂತ ಹೆಚ್ಚು ನಷ್ಟವಾದವರ ಬಗ್ಗೆ ನಿಮಗೆ ತಿಳಿಸಿ ಕೊಡಲೇ?');
INSERT INTO "kannada" VALUES(18,104,'18:104','ಅಂದರೆ ಐಹಿಕ ಜೀವನದಲ್ಲಿ ಅವರ ಎಲ್ಲ ಪ್ರಯ ತ್ನಗಳು ವ್ಯರ್ಥವಾದವು. ಆದರೆ ಅವರು ತಾವು ಒಳ್ಳೆಯ ಕೆಲಸವನ್ನೇ ಮಾಡುತ್ತಿರುವುದಾಗಿ ತಪ್ಪು ತಿಳಿದಿರುವರು.');
INSERT INTO "kannada" VALUES(18,105,'18:105','ಅವರು ತಮ್ಮ ಪ್ರಭುವಿನ ದೃಷ್ಟಾಂತಗಳಲ್ಲಿ ಹಾಗೂ ಆತನ ಸಾನಿಧ್ಯದಲ್ಲಿ ಬೇಟಿಯಾಗುವ ಬಗ್ಗೆ ಅವಿಶ್ವಾಸ ತಾಳಿದವರು. ಅಂತಲೇ ಅವರ ಸಕಲ ಕರ್ಮಗಳು ನುಚ್ಚುನೂರಾದವು. ಆದ್ದರಿಂದ ಪುನರುತ್ಥಾನದ ದಿನದಲ್ಲಿ ಯಾವುದೇ ತೂಕವನ್ನು ಅವರಿಗೆ ನಾವು ಕಲ್ಪಿಸಲಾರೆವು.');
INSERT INTO "kannada" VALUES(18,106,'18:106','ಅವರು ಸತ್ಯ ನಿಷೇಧ ತಾಳಿ ನನ್ನ ವಚನಗಳನ್ನು ಮತ್ತು ನನ್ನ ಸಂದೇಶವಾಹಕರನ್ನು ಪರಿಹಾಸ್ಯ ಮಾಡಿದ ಕಾರಣಕ್ಕೆ ಅವರಿಗೆ ಸರಿಯಾದ ಪ್ರತಿಫಲ ನರಕ ಶಿಕ್ಷೆಯಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(18,107,'18:107','ಆದರೆ ಸತ್ಯವಿಶ್ವಾಸ ತಾಳಿ, ಸತ್ಕರ್ಮ ಕೈಗೊಂಡ ವಿಶ್ವಾಸಿಗಳ ನಿವಾಸವಾಗಿ ಫಿರ್‍ದೌಸ್‍ನ ಸ್ವರ್ಗಾರಾಮಗಳಿವೆ .');
INSERT INTO "kannada" VALUES(18,108,'18:108','ಅವರು ಅಲ್ಲಿ ಸದಾ ಕಾಲ ನೆಲೆಸುವರು. ಅಲ್ಲಿಂದ ಬಿಟ್ಟು ಬೇರೆಡೆಗೆ ಹೋಗಲು ಅವರೆಂದೂ ಬಯಸಲಾರರು.');
INSERT INTO "kannada" VALUES(18,109,'18:109','(ನಬಿಯೇ) ಹೇಳಿರಿ, ಸಮುದ್ರದ ಜಲವು ನನ್ನ ಪ್ರಭುವಿನ ವಚನಗಳನ್ನು ಬರೆಯುವ ಮಸಿ ಯಾಗಿದ್ದರೆ ನನ್ನ ಪ್ರಭುವಿನ ವಚನಗಳು ಮುಗಿಯುವ ಮುನ್ನ ಸಮುದ್ರಜಲವು ಮುಗಿದು ಬಿಡುತ್ತಿತ್ತು. ಅದಕ್ಕೆ ಸಮಾನವಾದ ಇನ್ನೊಂದು ಸಮುದ್ರವನ್ನು ನಾವು ಸಹಾಯಕ್ಕಾಗಿ ಕೊಂಡು ಬಂದರೂ ಸರಿಯೇ .');
INSERT INTO "kannada" VALUES(18,110,'18:110','(ನಬಿಯೇ) ಹೇಳಿರಿ, ನಾನು ನಿಮ್ಮಂತಿರುವ ಓರ್ವ ಮನುಷ್ಯ . ನಿಮ್ಮ ಆರಾಧ್ಯನು ಏಕಮಾತ್ರ ಆರಾಧ್ಯನೆಂದು ನನಗೆ ಸಂದೇಶ ನೀಡಲಾಗಿದೆ. ಆದ್ದರಿಂದ ಯಾರಿಗೆ ಪ್ರಭುವಿನ ಭೇಟಿ ಮಾಡುವ ಹಂಬಲವಿದೆಯೋ ಅವನು ಸತ್ಕರ್ಮವನ್ನು ಮಾಡಲಿ ಹಾಗೂ ತನ್ನ ಪ್ರಭುವಿನ ಆರಾಧನೆಯಲ್ಲಿ ಆತನೊಂದಿಗೆ ಯಾರನ್ನೂ ಜೊತೆಗೂಡಿಸದಿರಲಿ.');
INSERT INTO "kannada" VALUES(19,1,'19:1','ಕಾಫ್ ಹಾ ಯಾ ಐನ್ ಸ್ವಾದ್');
INSERT INTO "kannada" VALUES(19,2,'19:2','ಇದು ನಿಮ್ಮ ಪ್ರಭು ತನ್ನ ದಾಸ ಝಕರಿಯ್ಯಾರ ಮೇಲೆ ತೋರಿದ ಕರುಣೆಯ ಕುರಿತ ಪ್ರಸ್ತಾಪ.');
INSERT INTO "kannada" VALUES(19,3,'19:3','ಅವರು ತಮ್ಮ ಪ್ರಭುವನ್ನು ರಹಸ್ಯವಾಗಿ ಕೂಗಿ ಪ್ರಾರ್ಥಿಸಿದಾಗ,');
INSERT INTO "kannada" VALUES(19,4,'19:4','ಅವರು ಹೇಳಿದರು, "ಓ ನನ್ನ ಪ್ರಭೂ, ನನ್ನ ಎಲುಬುಗಳು ದುರ್ಬಲಗೊಂಡಿವೆ. ತಲೆಯು ನರೆಯಿಂದ ಮಿನುಗಿದೆ. ಓ ನನ್ನ ಪ್ರಭೂ, ನಿನ್ನನ್ನು ಪ್ರಾರ್ಥಿಸಿ ನಾನೆಂದೂ ಉತ್ತರ ಸಿಗದೆ ನಿರಾಶನಾಗಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(19,5,'19:5','ನನಗೆ ನನ್ನ ನಂತರದ ಹಿಂಬಾಲಿಗರ ಕುರಿತಂತೆ ಖಂಡಿತ ಭಯವಾಗುತ್ತಿದೆ. ನನ್ನ ಪತ್ನಿ ಬಂಜೆಯಾಗಿರುತ್ತಾಳೆ. ಆದ್ದರಿಂದ ನೀನು ನಿನ್ನ ಕಡೆಯಿಂದ ಓರ್ವ ಉತ್ತರಾಧಿಕಾರಿಯನ್ನು ನನಗೆ ಕರುಣಿಸು .');
INSERT INTO "kannada" VALUES(19,6,'19:6','ಅವನು ನನ್ನ ವಾರೀಸುದಾರನೂ, ಯಅïಖೂಬರ ವಂಶದ ವಾರೀಸುದಾರನೂ ಆಗಲಿ, ಓ ನನ್ನ ಪ್ರಭೂ, ಅವನನ್ನು ನಿನ್ನ ತೃಪ್ತಿ ಪಾತ್ರನನ್ನಾಗಿ ಮಾಡು”.');
INSERT INTO "kannada" VALUES(19,7,'19:7','"ಓ ಝಕರಿಯ್ಯಾ, ನಾವು ನಿಮಗೆ ಓರ್ವ ಗಂಡು ಮಗುವಿನ ಬಗ್ಗೆ ಸುವಾರ್ತೆ ನೀಡುತ್ತೇವೆ. ಅವನ ಹೆಸರು ಯಹ್ಯಾ ಎಂದಾಗಿದ್ದು ನಾವು ಇದಕ್ಕೆ ಮುಂಚೆ ಈ ಹೆಸರಿನಿಂದ ಬೇರೆ ಯಾರನ್ನೂ ಕರೆದಿಲ್ಲ”.');
INSERT INTO "kannada" VALUES(19,8,'19:8','ಆಗ ಅವರು - "ಓ ನನ್ನ ಪ್ರಭೂ, ನನ್ನ ಪತ್ನಿಯು ಬಂಜೆಯಾಗಿದ್ದು ನಾನು ಅತಿ ಮುಪ್ಪಿನಿಂದ ಅಡರಿ ಹೋಗಿರುವಾಗ ನನಗೆ ಮಗು ಹುಟ್ಟುವು ದಾದರೂ ಹೇಗೆ?" ಎಂದು ಕೇಳಿದರು.');
INSERT INTO "kannada" VALUES(19,9,'19:9','ಆಗ ಅಲ್ಲಾಹನು "ಅದು ಹಾಗೆಯೇ ಆಗುವುದು” ಎಂದನು. “ನನಗೆ ಇದೊಂದು ಸುಲಭದ ವಿಷಯವಾಗಿದ್ದು ಇದಕ್ಕೆ ಮುಂಚೆ ನೀವು ಏನೂ ಆಗಿಲ್ಲದಿದ್ದಾಗ ನಾನು ನಿಮ್ಮನ್ನು ಸೃಷ್ಟಿಸಿದ್ದೆನಲ್ಲವೇ?” ಎಂದು ನಿಮ್ಮ ಪ್ರಭು ಹೇಳಿದನು.');
INSERT INTO "kannada" VALUES(19,10,'19:10','ಆಗ ಝಕರಿಯ್ಯಾ, "ಓ ನನ್ನ ಪ್ರಭೂ, ನನ ಗೊಂದು ಸಂಕೇತವನ್ನು ನಿಶ್ಚಯಿಸಿಕೊಡು" ಎಂದಾಗ "ಯಾವುದೇ ಕೇಡಿಲ್ಲದೆ ಮೂರು ದಿನ ಜನರೊಡನೆ ನಿಮಗೆ ಮಾತನಾಡಲಾಗದಿರುವುದೇ ನಿಮ್ಮ ಸಂಕೇತ" ಎಂದು ಅಲ್ಲಾಹು ಹೇಳಿದನು .');
INSERT INTO "kannada" VALUES(19,11,'19:11','ಆ ಪ್ರಕಾರ ಅವರು ಪ್ರಾರ್ಥನಾ ಮಂದಿರದಿಂದ ತನ್ನ ಜನಾಂಗದ ಕಡೆಗೆ ಹೊರಟರು. ಬೆಳಗು ಬೈಗುಗಳಲ್ಲಿ ದೇವ ಕೀರ್ತನೆ ಮಾಡಿರೆಂದು ಅವರು ಜನರಿಗೆ ಸಂಜ್ಞೆಯ ಮೂಲಕ ಸೂಚಿಸಿದರು.');
INSERT INTO "kannada" VALUES(19,12,'19:12','``ಓ ಯಹ್ಯಾ! ವೇದಗ್ರಂಥವನ್ನು ಬಿಗಿಹಿಡಿಯಿರಿ”. (ಎಂದು ಅಲ್ಲಾಹನು ಹೇಳಿದನು). ನಾವು ಅವರಿಗೆ ತಮ್ಮ ಬಾಲ್ಯದಲ್ಲೇ ವೇದಜ್ಞಾನವನ್ನು ನೀಡಿದೆವು .');
INSERT INTO "kannada" VALUES(19,13,'19:13','ನಮ್ಮ ಕಡೆಯಿಂದ ಅವರಿಗೆ ದಯಾವಾತ್ಸಲ್ಯ ಹಾಗೂ ಪಾವಿತ್ರ್ಯವನ್ನು ನೀಡಿದೆವು. ಅವರು ಮಹಾ ಧರ್ಮನಿಷ್ಠರಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(19,14,'19:14','ಅವರು ಮಾತಾಪಿತರಿಗೆ ವಿಧೇಯರಾಗಿದ್ದರು. ಅವರು ಅನುಸರಣೆಗೆಟ್ಟ ಅಹಂಕಾರಿಯಾಗಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(19,15,'19:15','ಅವರು ಜನಿಸಿದ ದಿನವೂ ಅವರು ಮರಣ ಹೊಂದುವ ದಿನವೂ ಅವರನ್ನು ಪುನಃ ಜೀವಂತಗೊಳಿಸಿ ಎಬ್ಬಿಸಲ್ಪಡುವ ದಿನವೂ ಅವರ ಮೇಲೆ ಶಾಂತಿ ಇರುವುದು .');
INSERT INTO "kannada" VALUES(19,16,'19:16','(ಓ ಪೈಗಂಬರರೇ,) ಈ ಗ್ರಂಥದಲ್ಲಿ ಮರ್ಯಮರ ವಿಷಯ ಪ್ರಸ್ತಾಪಿಸಿರಿ. ಅವರು ತನ್ನವರಿಂದ ಪೂರ್ವದಿಕ್ಕಿಗೆ ಹೋಗಿ ನೆಲೆಸಿದಾಗ.');
INSERT INTO "kannada" VALUES(19,17,'19:17','ತರವಾಯ ಆಕೆ ಅವರಿಂದ ಮರೆಯಾಗಿ ತೆರೆಯನ್ನಿಳಿಸಿದರು. ಆಗ ನಾವು ಅವರ ಬಳಿಗೆ ನಮ್ಮ ದೂತನನ್ನು ಕಳುಹಿಸಿದೆವು. ಅವನು ಅವರಿಗೆ ಓರ್ವ ಪೂರ್ಣ ಮನುಷ್ಯನಾಗಿ ರೂಪಾಂತರ ಗೊಂಡನು.');
INSERT INTO "kannada" VALUES(19,18,'19:18','ಮರ್ಯಮ್ ಥಟ್ಟನೆ, “ನಾನು ನಿನ್ನಿಂದ ದಯಾ ಮಯನಾದ ಅಲ್ಲಾಹನ ಅಭಯ ಯಾಚಿಸುತ್ತೇನೆ. ನೀನೊಬ್ಬ ತತ್ವನಿಷ್ಠೆಯುಳ್ಳವನಾಗಿದ್ದರೆ ನನ್ನಿಂದ ದೂರಸರಿ”, ಎಂದರು .');
INSERT INTO "kannada" VALUES(19,19,'19:19','ಆಗ ಅವನು, "ನಾನು ನಿಮ್ಮ ಪ್ರಭುವಿನ ದೂತ ನಾಗಿದ್ದು ನಿಮಗೆ ಓರ್ವ ಪವಿತ್ರ ಗಂಡು ಮಗುವನ್ನು ಕೊಡಲಿಕ್ಕಾಗಿ ಬಂದಿರುತ್ತೇನೆ" ಎಂದನು.');
INSERT INTO "kannada" VALUES(19,20,'19:20','ಆಗ ಮರ್ಯಮ್, "ನನ್ನನ್ನು ಮನುಷ್ಯರ್ಯಾರೂ ಮುಟ್ಟದಿರುವಾಗ ನನ್ನಲ್ಲಿ ಗಂಡು ಮಗು ಹುಟ್ಟುವುದು ಹೇಗೆ? ನಾನೇನೂ ದುರಾಚಾರಿ ಸ್ತ್ರೀಯಲ್ಲ" ಎಂದರು.');
INSERT INTO "kannada" VALUES(19,21,'19:21','ಆಗ ದೇವಚರನು, "ಹಾಗೆಯೇ ಆಗುವುದು. ಇದು ನನಗೆ ಬಹಳ ಸುಲಭವಾಗಿದ್ದು ಆ ಮಗು ವನ್ನು ಜನರಿಗೆ ಒಂದು ನಿದರ್ಶನವನ್ನಾಗಿ ಮತ್ತು ನನ್ನ ಕಡೆಯಿಂದ ಒಂದು ಕರುಣೆಯ ನ್ನಾಗಿ ಮಾಡಲಿಕ್ಕೋಸ್ಕರ ನಾನು ಹಾಗೆ ಮಾಡುತ್ತೇನೆ ಎಂದು ನಿಮ್ಮ ಪ್ರಭು ಹೇಳುತ್ತಾನೆ. ಈ ಕಾರ್ಯ ಮೊದಲೇ ನಿರ್ಧರಿಸಲಾಗಿದೆ” ಎಂದನು.');
INSERT INTO "kannada" VALUES(19,22,'19:22','ತರುವಾಯ ಮರ್ಯಮರು ಆ ಮಗುವಿನ ಗರ್ಭ ಧರಿಸಿದರು. ನಂತರ ಅವರು ಆ ಗರ್ಭ ವನ್ನು ಹೊತ್ತುಕೊಂಡು ದೂರದ ಒಂದು ಸ್ಥಳಕ್ಕೆ ಹೋಗಿ ನೆಲೆಸಿದರು.');
INSERT INTO "kannada" VALUES(19,23,'19:23','ತರುವಾಯ ಹೆರಿಗೆ ನೋವು ಅವರನ್ನು ಒಂದು ಖರ್ಜೂರದ ಮರದೆಡೆಗೆ ತಲುಪಿಸಿತು. ಅವರು, ``ಅಯ್ಯೋ, ನಾನು ಇದಕ್ಕಿಂತ ಮುಂಚೆ ಸತ್ತು ಹೋಗಿದ್ದರೆ ಮತ್ತು ಹೇಳ ಹೆಸರಿಲ್ಲದಂತೆ ಅಳಿದು ಹೋಗಿದ್ದರೆ ಎಷ್ಟು ಚೆನ್ನಾಗಿತ್ತು'''' ಎಂದರು.');
INSERT INTO "kannada" VALUES(19,24,'19:24','ದೇವಚರನು ಕೆಳಭಾಗದಿಂದ ಅವರನ್ನು ಕೂಗಿ ಇಂತೆಂದನು -“ದುಃಖಿಸಬೇಡಿರಿ, ನಿಮ್ಮ ಪ್ರಭು ನಿಮ್ಮ ತಳಭಾಗದಲ್ಲೊಂದು ಚಿಲುಮೆಯನ್ನು ಹರಿಸಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(19,25,'19:25','ನೀವು ಖರ್ಜೂರದ ಮರದ ಕಾಂಡವನ್ನು ನಿಮ್ಮೆಡೆಗೆ ಹಿಡಿದು ಅಲುಗಾಡಿಸಿರಿ. ಆಗ ನಿಮ್ಮ ಮೇಲೆ ಬಲಿತು ಪಕ್ವವಾದ ಖರ್ಜೂರದ ಹಣ್ಣು ಗಳು ಉದುರಿ ಬೀಳುತ್ತವೆ.');
INSERT INTO "kannada" VALUES(19,26,'19:26','ಆಮೇಲೆ ನೀವು ತಿನ್ನಿರಿ, ಕುಡಿಯಿರಿ, ಕಣ್ಮನ ಗಳನ್ನು ತಣಿಸಿರಿ. ನೀವು ಮನುಷ್ಯರಲ್ಲಿ ಯಾರ ನ್ನಾದರೂ ಕಂಡರೆ ಅವರಿಗೆ ಹೀಗೆ ಹೇಳಿರಿ; “ನಾನು ಕರುಣಾ ನಿಧಿಯಾದ ಅಲ್ಲಾಹನಿಗಾಗಿ ಮೌನ ವೃತದ ಹರಕೆ ಹೊತ್ತಿರುವುದರಿಂದ ಇಂದು ನಾನು ಯಾರಲ್ಲೂ ಮಾತಾಡಲಾರೆ”.');
INSERT INTO "kannada" VALUES(19,27,'19:27','ಆ ನಂತರ ಆಕೆ ಆ ಮಗುವನ್ನೆತ್ತಿಕೊಂಡು ತನ್ನ ಜನಾಂಗದ ಬಳಿಗೆ ಸಾಗಿದಳು. ಆಗ ಜನ ರೆಂದರು, “ಓ ಮರ್ಯಮ್! ನೀನು ಘೋರ ಪಾಪವನ್ನು ಮಾಡಿ ಬಂದಿದ್ದೀಯಾ!');
INSERT INTO "kannada" VALUES(19,28,'19:28','ಓ ಹಾರೂನರಿಗೆ ಸಾಟಿಯಾದ ಸಹೋದರಿ! ನಿನ್ನ ತಂದೆ ಕೆಟ್ಟವನಾಗಿರಲಿಲ್ಲ. ನಿನ್ನ ತಾಯಿಯೂ ದುಶ್ಯೀಲೆಯಾಗಿರಲಿಲ್ಲ”.');
INSERT INTO "kannada" VALUES(19,29,'19:29','ಆಗ ಮರ್ಯಮ್, ಮಗುವಿನ ಕಡೆಗೆ ಸಂಜ್ಞೆ ಮಾಡಿದರು. ಆಗ ಜನರು ಕೇಳಿದರು; “ತೊಟ್ಟಿ ಲಲ್ಲಿ ಬಿದ್ದಿರುವ ಮಗುವಿನಲ್ಲಿ ಹೇಗೆ ನಾವು ಮಾತ ನಾಡಬೇಕು’’.');
INSERT INTO "kannada" VALUES(19,30,'19:30','ಮಗು ಹೇಳಿತು; “ನಾನು ಅಲ್ಲಾಹನ ದಾಸ, ಅವನು ನನಗೆ ಗ್ರಂಥ ನೀಡಿದನುಂ. ಪ್ರವಾದಿಯನ್ನಾಗಿ ಮಾಡಿದನು.');
INSERT INTO "kannada" VALUES(19,31,'19:31','ನಾನೆಲ್ಲಿದ್ದರೂ ನನ್ನನ್ನು ಅನುಗ್ರಹೀತನಾಗಿರು ವಂತೆ ಮಾಡಿದನು. ನನ್ನ ಬಾಳಿನುದ್ದಕ್ಕೂ ನಮಾಝ್ ಮಾಡಲು ಮತ್ತು ಝಕಾತ್ ಪಾವತಿಸಲು ನನಗೆ ಆಜ್ಞೆಯಿತ್ತಿರುವನು.');
INSERT INTO "kannada" VALUES(19,32,'19:32','ನನ್ನನ್ನು ಅವನು ನನ್ನ ಮಾತೆಯ ಗುಣಸೇವ ಕನಾಗಿ ಮಾಡಿದನೇ ಹೊರತು ನನ್ನನ್ನು ಅದೃಷ್ಟಹೀನ, ನಿಷ್ಠುರನಾಗಿ ಮಾಡಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(19,33,'19:33','ನಾನು ಜನಿಸಿದ ದಿನ, ತೀರಿ ಹೋಗುವ ದಿನ ಹಾಗೂ ಪುನಃ ಜೀವಂತಗೊಳಿಸಿ ಎಬ್ಬಿಸಲ್ಪಡುವ ದಿನ ನನ್ನ ಮೇಲೆ ದಿವ್ಯ ಶಾಂತಿ ಇರುವುದು”.');
INSERT INTO "kannada" VALUES(19,34,'19:34','ಇವರೇ ಮರ್ಯಮರ ಪುತ್ರ ಈಸಾ. ಜನರು ಯಾವ ವಿಷಯದಲ್ಲಿ ಸಂಶಯಪಡುತ್ತಿರುವರೋ ಅದರ ಕುರಿತಾಗಿರುವ ಸತ್ಯಾಂಶವಿದು.');
INSERT INTO "kannada" VALUES(19,35,'19:35','ಯಾರನ್ನಾದರೂ ಮಗನಾಗಿ ಮಾಡಿಕೊಳ್ಳುವುದು ಅಲ್ಲಾಹನಿಗೆ ಭೂಷಣವಲ್ಲ. ಅವನು ಪರಿಶುದ್ಧನು. ಅವನು, ಏನಾದರೂ ಆಗಬೇಕೆಂದು ತೀರ್ಮಾ ನಿಸಿದರೆ `ಆಗು’ ಎನ್ನುತ್ತಾನೆ. ಆಗಲೇ ಅದು ಆಗಿಬಿಡುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(19,36,'19:36','(ಈಸಾ ಹೀಗೆ ಹೇಳಿದ್ದರು;) “ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹು ನನ್ನ ಪ್ರಭುವೂ ನಿಮ್ಮ ಪ್ರಭುವೂ ಆಗಿರುತ್ತಾನೆ. ಆದುದರಿಂದ ನೀವು ಅವನನ್ನೇ ಆರಾಧಿಸಿರಿ ಃ. ಇದೇ ನೇರ ಮಾರ್ಗ.”');
INSERT INTO "kannada" VALUES(19,37,'19:37','ಆದರೆ ಆ ಬಳಿಕ ಅವರ ನಡುವೆ ವಿವಿಧ ಪಂಗಡ ಪರಸ್ಪರ ಭಿನ್ನಾಭಿಪ್ರಾಯ ತಾಳಿತು. ಆದುದರಿಂದ ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಅವರು ಹಾಜರಾಗಲಿರುವ ಒಂದು ಘೋರ ದಿನದಲ್ಲಿ ವಿನಾಶಕಾರಿ ಶಿಕ್ಷೆಯಿದೆ.');
INSERT INTO "kannada" VALUES(19,38,'19:38','ಅವರು ನಮ್ಮ ಮುಂದೆ ಹಾಜರಾಗುವ ಆ ದಿನ ಅವರ ಶ್ರವಣ ಹಾಗೂ ದೃಕ್‍ಶಕ್ತಿಗಳು ಅದೆಷ್ಟು ಪ್ರಖರವಾಗಿರುವುದು! ಆದರೆ ಇಂದು ಈ ಅಕ್ರ ಮಿಗಳು ಸುವ್ಯಕ್ತವಾದ ದುರ್ಮಾರ್ಗದಲ್ಲಿ ಬಿದ್ದಿರುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(19,39,'19:39','(ಓ ಪೈಗಂಬರರೇ,) ವ್ಯಥೆಯ ಆ ದಿನದ ಬಗ್ಗೆ ಅವರಿಗೆ ಮುನ್ನೆಚ್ಚರಿಕೆ ಕೊಡಿರಿ. ಶಿಕ್ಷೆಯ ತೀರ್ಪು ಬಂದಾಗ! ಅವರಾದರೋ ಆ ಬಗ್ಗೆ ನಿರ್ಲಕ್ಷ್ಯರೂ ಅವಿಶ್ವಾಸಿಗಳೂ ಆಗಿರುವರು!');
INSERT INTO "kannada" VALUES(19,40,'19:40','ಈ ಭೂಮಿ ಹಾಗೂ ಇದರಲ್ಲಿರುವ ಸಕಲ ವಸ್ತು ಗಳನ್ನು ನಾವೇ ವಾರೀಸು ಹಕ್ಕಾಗಿ ತೆಗೆದು ಕೊಳ್ಳಲಿದ್ದೇವೆ. ಎಲ್ಲರೂ ನಮ್ಮ ಕಡೆಗೆ ಮರಳಿ ಸಲ್ಪಡುವರು.');
INSERT INTO "kannada" VALUES(19,41,'19:41','ಈ ಗ್ರಂಥದಲ್ಲಿ ಇಬ್ರಾಹೀಮರ ಬಗ್ಗೆ ಪ್ರಸ್ತಾಪಿಸಿರಿ. ಖಂಡಿತ, ಅವರೊಬ್ಬ ಶ್ರೇಷ್ಠ ಸತ್ಯ ಸಂಧರೂ ಪ್ರವಾದಿಯೂ ಆಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(19,42,'19:42','ಅವರು ತಮ್ಮ ತಂದೆಯನ್ನುದ್ದೇಶಿಸಿ ಹೀಗೆ ಹೇಳಿದರು. “ಅಪ್ಪಾ, ಆಲಿಸಲಾಗದ, ನೋಡಲಾಗದ ಮತ್ತು ತಮ್ಮ ಯಾವ ಕೆಲಸಕ್ಕೂ ಬಾರದ ವಸ್ತುಗಳನ್ನು (ವಿಗ್ರಹಗಳನ್ನು) ತಾವೇಕೆ ಆರಾಧಿಸುತ್ತೀರಿ?');
INSERT INTO "kannada" VALUES(19,43,'19:43','ಅಪ್ಪಾ, ತಮಗೆ ಬಂದಿರದ ಜ್ಞಾನ ನನಗೆ ಬಂದಿದೆ. ತಾವು ನನ್ನನ್ನು ಅನುಸರಿಸಿರಿ. ನಾನು ತಮಗೆ ನೇರ ಮಾರ್ಗವನ್ನು ತೋರಿಸಿಕೊಡುವೆನು.');
INSERT INTO "kannada" VALUES(19,44,'19:44','ಅಪ್ಪಾ ! ತಾವು ಶೈತಾನನನ್ನು ಆರಾಧಿಸಬೇಡಿರಿ. ಶೈತಾನನು ದಯಾನಿಧಿಯಾದ ಅಲ್ಲಾಹನ ಆಜ್ಞೋಲ್ಲಂಘಕನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(19,45,'19:45','ಅಪ್ಪಾ! ತಾವು ಕರುಣಾಳು ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆಗೆ ಗುರಿಯಾಗುವಿರಿ ಹಾಗೂ ಶೈತಾನನ ಸಂಗಾತಿಯಾಗುವಿರಿ ಎಂಬ ಭಯ ನನಗಿದೆ” .');
INSERT INTO "kannada" VALUES(19,46,'19:46','ಆಗ ತಂದೆಯು “ಇಬ್ರಾಹೀಮ್, ನೀನೇನು ನನ್ನ ಆರಾಧ್ಯ ದೇವರನ್ನು ಕೈಬಿಡುವೆಯಾ ? ನೀನು ಇದರಿಂದ ಹಿಂಜರಿಯದಿದ್ದರೆ ನಾನು ನಿನ್ನನ್ನು ಕಲ್ಲೆಸೆದು ಸಾಯಿಸುವೆನು. ದೀರ್ಘಕಾಲ ನೀನು ನನ್ನಿಂದ ತೊಲಗಿಬಿಡು” ಎಂದನು .');
INSERT INTO "kannada" VALUES(19,47,'19:47','ಆಗ ಇಬ್‍ರಾಹೀಮರು,``ತಮಗೆ ಸಲಾಮ್, ತಮ್ಮನ್ನು ಕ್ಷಮಿಸಲಿಕ್ಕಾಗಿ ನಾನು ನನ್ನ ಪ್ರಭು ವಿನಲ್ಲಿ ಪ್ರಾರ್ಥಿಸುವೆನು . ನನ್ನ ಪ್ರಭು ನನ್ನ ಮೇಲೆ ಅತ್ಯಧಿಕ ಕೃಪೆಯುಳ್ಳವನು.');
INSERT INTO "kannada" VALUES(19,48,'19:48','ನಾನು ತಮ್ಮನ್ನೂ ತಾವೆಲ್ಲರೂ ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಪೂಜಿಸುತ್ತಿರುವ ವಸ್ತುಗಳನ್ನೂ ಬಿಟ್ಟಗಲಿ ಹೋಗುತ್ತೇನೆ. ನಾನಂತೂ ನನ್ನ ಪ್ರಭುವನ್ನು ಆರಾಧಿಸುವೆನು. ನಾನು ನನ್ನ ಪ್ರಭುವನ್ನು ಆರಾಧಿಸಿ ದೆಸೆಗೆಡಲಾರೆ ಎಂದು ನಾನು ಭರವಸೆ ತಾಳಿದ್ದೇನೆ” ಎಂದರು.');
INSERT INTO "kannada" VALUES(19,49,'19:49','ಆ ಪ್ರಕಾರ ಇಬ್‍ರಾಹೀಮರು ಆ ಜನರನ್ನೂ ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಅವರು ಪೂಜಿಸುತ್ತಿರುವ ಆರಾಧ್ಯರನ್ನೂ ಬಿಟ್ಟಗಲಿದಾಗ ನಾವು ಅವರಿಗೆ ಇಸ್‍ಹಾಖ್ ಮತ್ತು ಯಅïಖೂಬರನ್ನು ಪುತ್ರದಾನ ಮಾಡಿದೆವು. ಮತ್ತು ಪ್ರತಿಯೊಬ್ಬನನ್ನೂ ಪ್ರವಾದಿಯನ್ನಾಗಿ ಮಾಡಿದೆವು.');
INSERT INTO "kannada" VALUES(19,50,'19:50','ಅವರಿಗೆ ನಮ್ಮ ಕರುಣೆಯಿಂದವರ ನೀಡಿದೆವು ಮತ್ತು ಸಮುನ್ನತವಾದ ಸತ್ಕೀರ್ತಿಯನ್ನು ಅವರಿಗೆ ದಯಪಾಲಿಸಿದೆವು .');
INSERT INTO "kannada" VALUES(19,51,'19:51','ಈ ಗ್ರಂಥದಲ್ಲಿ ಮೂಸಾರ ಕುರಿತು ಪ್ರಸ್ತಾಪಿಸಿರಿ. ಅವರೊಬ್ಬ ನಿರ್ಮಲ ಭಕ್ತರಾಗಿದ್ದರು. ಮತ್ತು ದೇವದೂತ ಹಾಗೂ ಪ್ರವಾದಿಯಾಗಿದ್ದರು .');
INSERT INTO "kannada" VALUES(19,52,'19:52','ನಾವು ಅವರನ್ನು `ಥೂರ್ ಸೀನಾ’ ಪರ್ವತದ ಬಲಗಡೆಯಿಂದ ಕರೆದೆವು. ಅಲ್ಲಾಹನಲ್ಲಿ ಸಂಭಾಷಣೆಗಾರನೆಂಬ ನೆಲೆಯಲ್ಲಿ ಅವರಿಗೆ ಸಾಮಿ ಪ್ಯಪ್ರದಾನ ಮಾಡಿದೆವು.');
INSERT INTO "kannada" VALUES(19,53,'19:53','ನಮ್ಮ ಕೃಪೆಯಿಂದ ಅವರ ಸಹೋದರ ಹಾರೂನರನ್ನು ಪ್ರವಾದಿಯಾಗಿ ಅವರಿಗೆ ನಾವು ಪ್ರಧಾನ ಮಾಡಿದೆವು.');
INSERT INTO "kannada" VALUES(19,54,'19:54','ಈ ಗ್ರಂಥದಲ್ಲಿ ಇಸ್ಮಾಈಲರ ಕುರಿತು ಪ್ರಸ್ತಾಪಿಸಿರಿ. ಅವರು ವಚನಪಾಲಕರಾಗಿದ್ದರು ಮತ್ತು ಸಂದೇಶವಾಹಕ ಹಾಗೂ ಪ್ರವಾದಿ ಆಗಿದ್ದರು');
INSERT INTO "kannada" VALUES(19,55,'19:55','ಅವರು ತನ್ನ ಜನಾಂಗದವರಿಗೆ ನಮಾಝ್ ಮತ್ತು ಝಕಾತ್‍ನ ಆಜ್ಞೆ ನೀಡಿದರು. ಅವರು ತನ್ನ ಪ್ರಭುವಿನ ಬಳಿ ಸಂತೃಪ್ತಿಗೆ ಪಾತ್ರರಾಗಿದ್ದರು .');
INSERT INTO "kannada" VALUES(19,56,'19:56','ಈ ಗ್ರಂಥದಲ್ಲಿ ಇದ್ರೀಸರ ಕುರಿತು ಪ್ರಸ್ತಾಪಿಸಿರಿ, ಅವರೊಬ್ಬ ಸತ್ಯಸಂಧರೂ ಪ್ರವಾದಿಯೂ ಆಗಿದ್ದರು .');
INSERT INTO "kannada" VALUES(19,57,'19:57','ನಾವು ಅವರನ್ನು ಉನ್ನತ ಸ್ಥಾನಕ್ಕೆ ಏರಿಸಿದೆವು .');
INSERT INTO "kannada" VALUES(19,58,'19:58','ಅವರೆಲ್ಲರೂ ಅಲ್ಲಾಹನು ಅನುಗ್ರಹಿಸಿದ ಪ್ರವಾದಿಗಳು. ಆದಮರ ಸಂತತಿಯಿಂದಲೂ ನಾವು ನೂಹರೊಂದಿಗೆ ನಾವೆಯಲ್ಲಿ ಏರಿಸಿದ್ದವ ರಿಂದಲೂ ಇಬ್‍ರಾಹೀಮರ ಹಾಗೂ ಇಸ್ರಾಈಲರ (ಯಅïಖೂಬರ) ಸಂತತಿಯಿಂದಲೂ ನಾವು ಸನ್ಮಾರ್ಗದರ್ಶನ ಮಾಡಿ ಆರಿಸಿ ತೆಗೆದವರಲ್ಲೂ ಅವರು ಒಳಗೊಂಡಿದ್ದರು. ಅವರಿಗೆ ಕರುಣಾನಿಧಿ ಅಲ್ಲಾಹನ ವಚನಗಳನ್ನು ಓದಿ ಹೇಳಿದಾಗ ಅವರು ಅಳುತ್ತಾ ಸಾಷ್ಟಾಂ ಗವೆರಗುತ್ತಿದ್ದರು .');
INSERT INTO "kannada" VALUES(19,59,'19:59','ಅವರ ಬಳಿಕ ಅವರ ಉತ್ತರಾಧಿಕಾರಿಗಳಾಗಿ ಬಂದವರು ನಮಾಝನ್ನು ವ್ಯರ್ಥಗೊಳಿಸಿದರು ಮತ್ತು ದೇಹೇಚ್ಛೆಗಳನ್ನು ಅನುಸರಿಸಿದರು. ಆದುದರಿಂದ ಅವರು ನರಕದ ಒಂದು ಪ್ರಪಾತದಲ್ಲಿ ನೆಲೆಗೊಳ್ಳಲಿರುವರು .');
INSERT INTO "kannada" VALUES(19,60,'19:60','ಆದರೆ ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು ಸತ್ಯವಿಶ್ವಾಸ ಸ್ವೀಕರಿಸಿ, ಸತ್ಕರ್ಮ ಕೈಗೊಂಡವರ ಹೊರತು. ಅಂಥವರು ಸ್ವರ್ಗದಲ್ಲಿ ಪ್ರವೇಶ ಪಡೆಯುವರು. ಅವರಿಗೆ ಒಂದಿಷ್ಟೂ ಅನ್ಯಾಯವಾಗದು.');
INSERT INTO "kannada" VALUES(19,61,'19:61','ಪರಮ ದಯಾಮಯನು ತನ್ನ ದಾಸರಿಗೆ ಪರೋಕ್ಷವಾಗಿ ವಾಗ್ದಾನ ಮಾಡಿರುವ ಸ್ಥಿರವಾಸದ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ (ಪ್ರವೇಶ ಪಡೆಯುವರು). ನಿಜವಾಗಿಯೂ ಅವನ ವಾಗ್ದಾನ ಪೂರ್ಣ ಗೊಂಡೇ ತೀರುವುದು.');
INSERT INTO "kannada" VALUES(19,62,'19:62','ಅಲ್ಲಿ ಅವರು ಸಲಾಮ್ ಅಲ್ಲದೆ, ಯಾವುದೇ, ನಿರರ್ಥಕ ಮಾತುಗಳನ್ನು ಆಲಿಸಲಾರರು. ಅಲ್ಲಿ ಅವರ ಆಹಾರವು ಬೆಳಗುಬೈಗುಗಳಲ್ಲಿ ನಿರಂತರವಿರುವುದು.');
INSERT INTO "kannada" VALUES(19,63,'19:63','ನಾವು ನಮ್ಮ ದಾಸರ ಪೈಕಿ ಧರ್ಮನಿಷ್ಠ ಭಕ್ತರಿಗೆ ವಾರೀಸು ಹಕ್ಕಾಗಿ ನೀಡುವ ಸ್ವರ್ಗವಿದು.');
INSERT INTO "kannada" VALUES(19,64,'19:64','(ಓ ಪೈಗಂಬರರೇ,) ನಾವು (ದೇವಚರರು) ನಿಮ್ಮ ಪ್ರಭುವಿನ ಅಪ್ಪಣೆ ಇಲ್ಲದೆ ಇಳಿದು ಬರುವುದಿಲ್ಲ. ನಮ್ಮ ಮುಂದೆ, ನಮ್ಮ ಹಿಂದೆ ಮತ್ತು ಅವುಗಳ ನಡುವೆ ಏನೆಲ್ಲ ಇರುವುದೋ ಅವೆಲ್ಲವೂ ಅವನದ್ದು. ನಿಮ್ಮ ಪ್ರಭು ಮರೆತು ಬಿಡುವವನಲ್ಲ .');
INSERT INTO "kannada" VALUES(19,65,'19:65','ಅವನು ಆಕಾಶಗಳ, ಭೂಮಿಯ ಮತ್ತು ಅವುಗಳ ನಡುವೆಯಿರುವ ಸರ್ವಸ್ವಗಳ ಪ್ರಭು. ಆದು ದರಿಂದ ನೀವು ಅವನಿಗೆ ಆರಾಧಿಸಿರಿ. ಅವನ ಆರಾಧನೆಗಾಗಿ ಸಹನೆಯಿಂದ ಸ್ಥಿರವಾಗಿರಿ . ಅವನಿಗೆ ಸಾಟಿಯಾದ ಬೇರೆ ಯಾರ ನ್ನಾದರೂ ತಾವು ತಿಳಿದಿದ್ದೀರಾ?');
INSERT INTO "kannada" VALUES(19,66,'19:66','“ನಾನು ಸತ್ತು ಹೋದ ಮೇಲೆ ನಿಜಕ್ಕೂ ಜೀವಂತವಾಗಿ ಹೊರ ತರಲ್ಪಡುವೆನೇ?” ಎಂದು ಮಾನ ವನು ಪ್ರಶ್ನಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(19,67,'19:67','ಮಾನವನು ಮೊದಲು ಏನೂ ಆಗಿಲ್ಲದಿದ್ದಾಗ ನಾವು ಅವನನ್ನು ಸೃಷ್ಟಿಸಿದ್ದು ಅವನಿಗೆ ನೆನಪಿಲ್ಲವೇ? ');
INSERT INTO "kannada" VALUES(19,68,'19:68','ನಿಮ್ಮ ಪ್ರಭುವಿನಾಣೆ, ನಾವು ಇವರನ್ನೂ ಶೈತಾನರನ್ನೂ ಒಟ್ಟು ಸೇರಿಸಿ ಆ ಬಳಿಕ ಮುಗ್ಗರಿಸಿ ಬಿದ್ದ ಸ್ಥಿತಿಯಲ್ಲಿ ನರಕದ ಸುತ್ತಲೂ ಅವರನ್ನು ತಂದು ಹಾಜರುಗೊಳಿಸುವೆವು .');
INSERT INTO "kannada" VALUES(19,69,'19:69','ಅನಂತರ ಎಲ್ಲ ಗುಂಪುಗಳಿಂದಲೂ ಪರಮ ದಯಾಮಯನಾದ ಅಲ್ಲಾಹನ ವಿರುದ್ಧ ಹೆಚ್ಚು ದಿಕ್ಕಾರ ತೋರಿದವರ ಪೈಕಿ ಪ್ರತಿಯೊಬ್ಬನನ್ನೂ ಬೇರ್ಪಡಿಸಿ ನಿಲ್ಲಿಸುವೆವು.');
INSERT INTO "kannada" VALUES(19,70,'19:70','ಇವರ ಪೈಕಿ ನರಕಕ್ಕೆ ಸೇರಿ ಉರಿಯಲು ಅತಿ ಹೆಚ್ಚು ಅರ್ಹರು ಯಾರೆಂದು ನಾವು ತಿಳಿದಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(19,71,'19:71','ನಿಮ್ಮ ಪೈಕಿ ನರಕವನ್ನು ಹಾದು ಹೋಗದವರು ಯಾರೂ ಇಲ್ಲ . ಇದು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಮೇಲೆ ಪೂರ್ಣಗೊಳಿಸಬೇಕಾದ ಕಡ್ಡಾಯ ಬಾಧ್ಯತೆಯಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(19,72,'19:72','ಬಳಿಕ ನಾವು ಧರ್ಮನಿಷ್ಟರನ್ನು ರಕ್ಷಿಸಿಕೊಳ್ಳುವೆವು. ಅಕ್ರಮಿಗಳನ್ನು ಮುಗ್ಗರಿಸಿ ಬಿದ್ದ ಸ್ಥಿತಿಯಲ್ಲಿ ಅದರಲ್ಲೇ ಬಿಟ್ಟು ಬಿಡುವೆವು.');
INSERT INTO "kannada" VALUES(19,73,'19:73','ಇವರ ಮೇಲೆ ನಮ್ಮ ಸುವ್ಯಕ್ತ ವಚನಗಳನ್ನು ಓದಿ ಹೇಳಿದಾಗ ಸತ್ಯನಿಷೇಧಿಗಳು ಸತ್ಯ ವಿಶ್ವಾ ಸಿಗಳೊಡನೆ, “ನಮ್ಮೀ ಎರಡು ಪಂಗಡಗಳ ಪೈಕಿ ಶ್ರೇಷ್ಠ ನಿವಾಸ ಮತ್ತು ಸಭೆಯಲ್ಲಿ ಯಾರು ಉತ್ಕøಷ್ಟರು?’’ ಎಂದು ಕೇಳುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(19,74,'19:74','ಇವರಿಗಿಂತ ಮುಂಚೆ ಸಾಧನಾನುಕೂಲತೆಗಳಲ್ಲಿ ಹಾಗೂ ತೋರಿಕೆಯ ಸುಖಾಡಂಬರಗಳಲ್ಲಿ ಇವರಿಗಿಂತಲೂ ಮಿಗಿಲಾಗಿದ್ದ ಎಷ್ಟೋ ಜನಾಂಗಗಳನ್ನು ನಾಶಗೊಳಿಸಿರುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(19,75,'19:75','ಹೇಳಿರಿ : ಯಾರು ದುರ್ಮಾರ್ಗದಲ್ಲಿದ್ದಾರೆ, ಅಂಥ ವರಿಗೆ ಮಾಡಲ್ಪಟ್ಟ ಮುನ್ನೆಚ್ಚರಿಕೆಯನ್ನು ಅವರು ಕಣ್ಣಾರೆ ಕಂಡುಕೊಳ್ಳುವವರೆಗೂ ಪರಮ ದಯಾಮಯನಾದ ಅಲ್ಲಾಹನು ಅವಧಿಯನ್ನು ವಿಸ್ತರಿಸುವನು. ಅದು ಯಾತನೆಯಿರಲಿ ಅಥವಾ ಅಂತಿಮ ಘಳಿಗೆಯಿರಲಿ- ಆಗ ಸ್ಥಾನದಲ್ಲಿ ಕೆಟ್ಟ ವರು ಮತ್ತು ಸೈನ್ಯದಲ್ಲಿ ದುರ್ಬಲರು ಯಾರೆಂದು ಗೊತ್ತಾಗುವುದು .');
INSERT INTO "kannada" VALUES(19,76,'19:76','ಸನ್ಮಾರ್ಗವನ್ನು ಕೈಗೊಂಡವರಿಗೆ ಅಲ್ಲಾಹನು ಅವರ ಸನ್ಮಾರ್ಗದಲ್ಲಿ ವೃದ್ಧಿಯನ್ನು ದಯಪಾ ಲಿಸುತ್ತಾನೆ. ಸ್ಥಿರ ಸತ್ಕರ್ಮಗಳೇ ಸತ್ಫಲ ಹಾಗೂ ಭಕ್ತನಿಗೆ ಮರಳಿ ದೊರೆಯುವ ದೃಷ್ಟಿಯಿಂದ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಬಳಿ ಶ್ರೇಷ್ಠ .');
INSERT INTO "kannada" VALUES(19,77,'19:77','ನಮ್ಮ ವಚನಗಳನ್ನು ನಿರಾಕರಿಸಿದ ಹಾಗೂ “ನನಗೆ ಸಂಪತ್ತು ಮತ್ತು ಸಂತತಿಯನ್ನು ಖಂಡಿತ ನೀಡಲಾಗುವುದು” ಎಂದು ಹೇಳಿದ ವ್ಯಕ್ತಿಯನ್ನು ನೀವು ಕಂಡಿರಾ?');
INSERT INTO "kannada" VALUES(19,78,'19:78','ಅವನು ಅದೃಶ್ಯವನ್ನು ಕಣ್ಣಾರೆ ಕಂಡನೇ? ಅಥವಾ ಅವನು ಕರುಣಾನಿಧಿಯಾದ ಅಲ್ಲಾಹನ ಬಳಿ ಏನಾದರೂ ಕರಾರನ್ನು ಮಾಡಿಕೊಂಡನೇ?');
INSERT INTO "kannada" VALUES(19,79,'19:79','ಹಾಗಲ್ಲ, ಅವನು ಹೇಳುತ್ತಿರುವುದನ್ನು ನಾವು ಬರೆದಿಟ್ಟುಕೊಳ್ಳುವೆವು ಮತ್ತು ಅವನಿಗೆ ಶಿಕ್ಷೆಯನ್ನು ಇನ್ನಷ್ಟು ಹೆಚ್ಚಿಸುವೆವು.');
INSERT INTO "kannada" VALUES(19,80,'19:80','ಅವನು ಹೇಳುತ್ತಿರುವುದನ್ನು ನಾವೇ ವಾರೀಸು ಪಡೆಯುವೆವು. ಮತ್ತು ಅವನು ಒಬ್ಬಂಟಿಗನಾಗಿ ನಮ್ಮ ಮುಂದೆ ಹಾಜರಾಗುವನು.');
INSERT INTO "kannada" VALUES(19,81,'19:81','ಅವರು ಇವರಿಗೆ ಬಲ ವೃದ್ಧಿಗಾಗಿ ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಇತರ ಕೆಲವು ದೇವರುಗಳನ್ನು ಮಾಡಿ ಕೊಂಡರು.');
INSERT INTO "kannada" VALUES(19,82,'19:82','ಹಾಗಲ್ಲ, ಅವರು ಇವರ ಆರಾಧನೆಯನ್ನು ನಿರಾಕರಿಸುವರು. ಮಾತ್ರವಲ್ಲ ಇವರ ವಿರುದ್ಧ ತಿರುಗಿಬೀಳುವರು.');
INSERT INTO "kannada" VALUES(19,83,'19:83','ನಾವು ಸತ್ಯ ನಿಷೇಧಿಗಳ ಮೇಲೆ, ಅವರನ್ನು ಚೆನ್ನಾಗಿ ಕೆರಳಿಸುವ ಶೈತಾನರನ್ನು ಛೂ ಬಿಟ್ಟಿರು ವುದನ್ನು ನೀವು ಕಂಡಿಲ್ಲವೇ? ');
INSERT INTO "kannada" VALUES(19,84,'19:84','ಆದುದರಿಂದ ಇವರ ಮೇಲೆ ನೀವು ಅವಸರ ಪಡಬೇಡಿ. ನಾವು ಇವರ ದಿನಗಳನ್ನು ಎಣಿಕೆ ಮಾಡುತ್ತಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(19,85,'19:85','ನಾವು ಧರ್ಮನಿಷ್ಟರನ್ನು ಕರುಣಾಮಯನಾದ ಅಲ್ಲಾಹನೆಡೆಗೆ ಸವಾರರನ್ನಾಗಿ ಒಟ್ಟುಗೂಡಿಸುವ ದಿನ,');
INSERT INTO "kannada" VALUES(19,86,'19:86','ಅಪರಾಧಿಗಳನ್ನು ದಾಹ ಪರವಶರಾಗಿ ನರಕದ ಕಡೆಗೆ ಕಾಲ್ನಡೆಯಾಗಿ ಅಟ್ಟಿಸಿಕೊಂಡು ಹೋಗುವ ದಿನ ಬರಲಿದೆ.');
INSERT INTO "kannada" VALUES(19,87,'19:87','ಕರುಣಾಳು ಅಲ್ಲಾಹನ ಬಳಿ ಕರಾರನ್ನು ಮಾಡಿ ಕೊಂಡವನ ಹೊರತು ಬೇರೆ ಯಾರೂ (ಅಂದು) ಶಿಫಾರಸ್ಸನ್ನು ಒಡೆತನಗೊಳಿಸುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(19,88,'19:88','ಕರುಣಾಳು ಅಲ್ಲಾಹನು ಸಂತಾನವನ್ನು ಮಾಡಿ ದನೆಂದು ಅವರು ಹೇಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(19,89,'19:89','(ಓ ಜನರೇ,) ಬಹಳ ಗಂಭೀರವಾದ ಆರೋ ಪವನ್ನೇ ನೀವು ತಂದಿರುವಿರಿ.');
INSERT INTO "kannada" VALUES(19,90,'19:90','ಆದ್ದರಿಂದ ಆಕಾಶಗಳು ಸಿಡಿದು ಬೀಳುವ, ಭೂಮಿ ಬಿರಿಯುವ ಮತ್ತು ಪರ್ವತಗಳು ಹರಿದು ಬೀಳುವ ಸಂಭವವಿದ್ದೀತು.');
INSERT INTO "kannada" VALUES(19,91,'19:91','ಕೃಪಾಳುವಾದ ಅಲ್ಲಾಹನಿಗೆ ಸಂತಾನವಿದೆಯೆಂದು ಅವರು ವಾದಿಸಿದ ಕಾರಣಕ್ಕೆ.');
INSERT INTO "kannada" VALUES(19,92,'19:92','ಸಂತಾನವನ್ನು ಮಾಡಿಕೊಳ್ಳುವುದು ಕೃಪಾಳುವಾದ ಅಲ್ಲಾಹನಿಗೆ ಸಂಗತವಲ್ಲ .');
INSERT INTO "kannada" VALUES(19,93,'19:93','ಭೂಮಿ-ಆಕಾಶಗಳಲ್ಲಿರುವವರೆಲ್ಲರೂ ಕರುಣಾಳು ಅಲ್ಲಾಹನೆಡೆಗೆ ಕೇವಲ ದಾಸರಾಗಿ ಬರುವವರು.');
INSERT INTO "kannada" VALUES(19,94,'19:94','ಅವನು ಎಲ್ಲರನ್ನೂ ನಿಖರವಾಗಿ ತಿಳಿದನು. ಅವನು ಅವರನ್ನು ಎಣಿಕೆ ಮಾಡಿಟ್ಟನು.');
INSERT INTO "kannada" VALUES(19,95,'19:95','ಪುನರುತ್ಥಾನದ ದಿನ ಎಲ್ಲರೂ ಅವನ ಮುಂದೆ ಒಂಟಿಯಾಗಿ ಹಾಜರಾಗುವರು .');
INSERT INTO "kannada" VALUES(19,96,'19:96','ನಿಶ್ಚಯವಾಗಿಯೂ ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿ ಸತ್ಕ ರ್ಮವೆಸಗಿದವರಿಗಾಗಿ ಜನರ ಹೃದಯಗಳಲ್ಲಿ ಕಾರುಣ್ಯವಂತನಾದ ಅಲ್ಲಾಹನು ಸದ್ಯವೇ ಪ್ರೀತಿಯನ್ನು ಉಂಟುಮಾಡುವನು .');
INSERT INTO "kannada" VALUES(19,97,'19:97','ಆದುದರಿಂದ (ಓ ಪೈಗಂಬರರೇ,) ನೀವು ಭಯ ಭಕ್ತಿಯುಳ್ಳ ಧರ್ಮನಿಷ್ಟರಿಗೆ ಸುವಾರ್ತೆ ಯನ್ನೀಯಲಿಕ್ಕಾಗಿ ಹಾಗೂ ಹಠಮಾರಿ ಜನಾಂಗಕ್ಕೆ ಎಚ್ಚರಿಕೆ ಕೊಡಲಿಕ್ಕಾಗಿ ನಾವು ಈ ವಾಣಿಯನ್ನು ನಿಮ್ಮ ಭಾಷೆಯಲ್ಲಿ ಸರಳಗೊಳಿಸಿ ರುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(19,98,'19:98','ಇವರಿಗಿಂತ ಮುಂಚೆ ನಾವು ಎಷ್ಟೋ ಜನಾಂಗಗಳನ್ನು ನಾಶಗೊಳಿಸಿದ್ದೇವೆ. ಇಂದು ನಿಮಗೆ ಅವರಲ್ಲಿ ಯಾರನ್ನಾದರೂ ಕಾಣಿಸುತ್ತಿದೆಯೇ ಅಥವಾ ಅವರ ಮೆಲುದನಿಯಾದರೂ ಕೇಳಿಸುತ್ತಿದೆಯೇ ? ');
INSERT INTO "kannada" VALUES(20,1,'20:1','ತ್ವಾ ಹಾ .');
INSERT INTO "kannada" VALUES(20,2,'20:2','ಖುರ್‍ಆನನ್ನು ನಿಮ್ಮ ಮೇಲೆ ನಾವು ಅವತೀರ್ಣ ಗೊಳಿಸಿರುವುದು ನಿಮ್ಮನ್ನು ಕಷ್ಟಪಡಿಸಲಿಕ್ಕಲ್ಲ.');
INSERT INTO "kannada" VALUES(20,3,'20:3','ದೇವನನ್ನು ಭಯಪಡುವ ಪ್ರತಿಯೊಬ್ಬನಿಗೆ ಬೋಧಿಸಿ ನೆನಪಿಸಲು ಮಾತ್ರ .');
INSERT INTO "kannada" VALUES(20,4,'20:4','ಭೂಮಿಯನ್ನೂ ಉನ್ನತ ಆಕಾಶಗಳನ್ನೂ ಸೃಷಿಸಿದವನ ಕಡೆಯಿಂದ ಇದು ಅವತೀರ್ಣ ಗೊಂಡಿದೆ.');
INSERT INTO "kannada" VALUES(20,5,'20:5','ದಯಾಮಯನಾದ ಅಲ್ಲಾಹನು ತನಗೆ ಸಂಗತವಾದ ವಿಧದಲ್ಲಿ ಅರ್ಶ್‍ನ ಮೇಲೆ ಇಸ್ತಿವಾ ಹೊಂದಿದನು .');
INSERT INTO "kannada" VALUES(20,6,'20:6','ಅವನು, ಆಕಾಶಗಳಲ್ಲೂ ಭೂಮಿಯಲ್ಲೂ ಅವೆರಡರ ನಡುವೆಯೂ ಹಾಗೂ ಮಣ್ಣಿನಡಿಯಲ್ಲಿಯೂ ಇರುವ ಸಕಲ ವಸ್ತುಗಳ ಒಡೆಯನು.');
INSERT INTO "kannada" VALUES(20,7,'20:7','ನೀವು ನಿಮ್ಮ ಮಾತನ್ನು ಉಚ್ಚ ಸ್ವರದಲ್ಲೇ ಹೇಳಿದರೂ ಅವನು ರಹಸ್ಯವನ್ನೂ ಅತ್ಯಂತ ಸುಪ್ತವಾದುದನ್ನೂ ಅರಿಯುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(20,8,'20:8','ಅಲ್ಲಾಹು, ಅವನ ಹೊರತು ಅನ್ಯದೇವನಿಲ್ಲ. ಅವನಿಗೆ ಅತ್ಯುತ್ತಮ ನಾಮಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(20,9,'20:9','ನಿಮಗೆ ಮೂಸಾರ ವೃತ್ತಾಂತ ತಲುಪಿದೆಯೇ?');
INSERT INTO "kannada" VALUES(20,10,'20:10','ಅವರು ಒಂದು ಬೆಂಕಿಯನ್ನು ಕಂಡಾಗ ತನ್ನ ಪತ್ನಿಯೊಡನೆ; “ನೀವಿಲ್ಲಿ ನಿಲ್ಲಿರಿ, ನಾನೊಂದು ಬೆಂಕಿಯನ್ನು ಕಂಡಿದ್ದೇನೆ. ಬಹುಶಃ ಅದರಿಂದ ನಿಮಗೆ ನಾನು ಕೆಂಡವೊಂದನ್ನು ತರಬಲ್ಲೆ. ಅಥವಾ ಆ ಬೆಂಕಿಯ ಬಳಿ ಮಾರ್ಗದರ್ಶನವನ್ನು ಪಡೆಯ ಬಲ್ಲೆ” ಎಂದು ಹೇಳಿದ ಸಂದರ್ಭ.');
INSERT INTO "kannada" VALUES(20,11,'20:11','ಅಲ್ಲಿಗೆ ತಲಪಿದಾಗ ಹೀಗೆ ಕರೆಯಲಾಯಿತು. - ‘ಓ ಮೂಸಾ,');
INSERT INTO "kannada" VALUES(20,12,'20:12','ನಾನು ನಿಮ್ಮ ಪ್ರಭು. ಆದ್ದರಿಂದ ನಿಮ್ಮ ಪಾದರಕ್ಷೆಗಳನ್ನು ಕಳಚಿರಿ. ನೀವು ಪವಿತ್ರ `ಥುವಾ’ ಕಣಿವೆಯಲ್ಲಿದ್ದೀರಿ.');
INSERT INTO "kannada" VALUES(20,13,'20:13','ನಾನು ನಿಮ್ಮನ್ನು (ಪ್ರವಾದಿತ್ವಕ್ಕಾಗಿ) ಆರಿಸಿ ಕೊಂಡಿದ್ದೇನೆ. ಆದ್ದರಿಂದ ನಿಮಗೆ ಕೊಡಲಾಗುವ ದಿವ್ಯ ಸಂದೇಶವನ್ನು ಚೆನ್ನಾಗಿ ಆಲಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(20,14,'20:14','ನಿಶ್ಚಯ ನಾನೇ ಅಲ್ಲಾಹು, ನನ್ನ ಹೊರತು ಬೇರೆ ದೇವನಿಲ್ಲ. ಆದುದರಿಂದ ನೀವು ನನಗೆ ಆರಾಧಿಸಿರಿ. ನನ್ನ ಸ್ಮರಣೆಗಾಗಿ ನಮಾಝನ್ನು ಕ್ರಮಪ್ರಕಾರ ನೆಲೆಗೊಳಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(20,15,'20:15','ಅಂತಿಮ ಘಳಿಗೆಯು ಖಂಡಿತ ಬರಲಿದೆ. ಪ್ರತಿಯೊಬ್ಬ ಜೀವಿಯೂ ತನ್ನ ದುಡಿಮೆಗೆ ತಕ್ಕ ಪ್ರತಿಫಲ ಪಡೆಯಲಿಕ್ಕಾಗಿ ನಾನು ಅದರ ಸಮಯವನ್ನು ರಹಸ್ಯವಾಗಿಡಬಯಸಿದ್ದೇನೆ ');
INSERT INTO "kannada" VALUES(20,16,'20:16','ಆದುದರಿಂದ ಆ ಕೊನೆ ತಾಸಿನ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸದವನು ಮತ್ತು ತನ್ನ ಸ್ವೇಚ್ಛೆಗಳ ಬೆನ್ನು ಹತ್ತಿದವನು ನಿಮ್ಮನ್ನು ಆ ಘಳಿಗೆಯ ವಿಶ್ವಾಸದಿಂದ ತಡೆಯದಿರಲಿ. ಅನ್ಯಥಾ ನೀವು ವಿನಾಶದಲ್ಲಿ ಬೀಳುವಿರಿ.');
INSERT INTO "kannada" VALUES(20,17,'20:17','ಓ ಮೂಸಾ, ನಿಮ್ಮ ಬಲಗೈಯಲ್ಲಿರುವುದೇನು?”');
INSERT INTO "kannada" VALUES(20,18,'20:18','ಮೂಸಾ ಹೀಗೆ ಉತ್ತರಿಸಿದರು - “ಇದು ನನ್ನ ದಂಡ, ನಾನು ಇದನ್ನು ಊರಿಕೊಂಡು ನಡೆಯುತ್ತೇನೆ. ಇದರಿಂದ ಆಡುಗಳಿಗೆ ಮರದ ಎಲೆಗಳನ್ನು ಹೊಡೆದು ಬೀಳಿಸುತ್ತೇನೆ. ಇದರಿಂದ ನಾನು ಬೇರೆ ಅನೇಕ ಪ್ರಯೋಜನಗಳನ್ನು ಪಡೆಯುತ್ತೇನೆ.”');
INSERT INTO "kannada" VALUES(20,19,'20:19','ಆಗ ಅಲ್ಲಾಹನು, “ಓ ಮೂಸಾ, ಅದನ್ನು ಕೆಳಗೆ ಹಾಕಿರಿ” ಎಂದನು.');
INSERT INTO "kannada" VALUES(20,20,'20:20','ಆಗ ಅವರು ಕೆಳಗೆ ಹಾಕಿದರು - ಹಠಾತ್ತನೆ ಅದೊಂದು ವೇಗವಾಗಿ ಓಡುತ್ತಿರುವ ಹಾವಾಗಿ ಮಾರ್ಪಟ್ಟಿತು .');
INSERT INTO "kannada" VALUES(20,21,'20:21','ಅಲ್ಲಾಹು ಹೇಳಿದ; “ಅದನ್ನು ಹಿಡಿದುಕೊಳ್ಳಿ. ಹೆದರಬೇಡ. ನಾವು ಅದನ್ನು ಪೂರ್ವಸ್ಥಿತಿಗೇ ತರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(20,22,'20:22','ನಿಮ್ಮ ಕೈಯನ್ನು ನಿಮ್ಮ ಪಕ್ಕಕ್ಕೆ ಸೇರಿಸಿಡಿ. ಆಗದು ಯಾವುದೇ ಕೇಡಿಲ್ಲದೆ ಹೊಳೆಯುತ್ತಾ ಹೊರ ಬರುವುದು . ಇದು ಎರಡನೆಯ ನಿದರ್ಶನ.');
INSERT INTO "kannada" VALUES(20,23,'20:23','ಏಕೆಂದರೆ ನಾವು ನಿಮಗೆ ನಮ್ಮ ದೊಡ್ಡ ನಿದರ್ಶನಗಳನ್ನು ತೋರಿಸಲಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(20,24,'20:24','ಇನ್ನು ನೀವು ಫಿರ್‍ಔನನ ಬಳಿಗೆ ಹೋಗಿರಿ. ಅವನು ಅತಿಕ್ರಮಿಯಾಗಿದ್ದಾನೆ”.');
INSERT INTO "kannada" VALUES(20,25,'20:25','ಮೂಸಾ ಹೀಗೆ ಹೇಳಿದರು; “ಓ ನನ್ನ ಪ್ರಭೂ, ನನ್ನ ಹೃದಯವನ್ನು ವಿಶಾಲಗೊಳಿಸು.');
INSERT INTO "kannada" VALUES(20,26,'20:26','ನನ್ನ ಕಾರ್ಯವನ್ನು ನನಗೆ ಸುಲಭಗೊಳಿಸು.');
INSERT INTO "kannada" VALUES(20,27,'20:27','ನನ್ನ ನಾಲಗೆಯ ತೊಡಕನ್ನು ನೀಗಿಸು .');
INSERT INTO "kannada" VALUES(20,28,'20:28','ಜನರು ನನ್ನ ಮಾತನ್ನು ಗ್ರಹಿಸಲು.');
INSERT INTO "kannada" VALUES(20,29,'20:29','ನನಗಾಗಿ ನನ್ನದೇ ಕುಟುಂಬದಿಂದ ಒಬ್ಬ ಸಹಾಯಕನನ್ನು ನೇಮಿಸು.');
INSERT INTO "kannada" VALUES(20,30,'20:30','ನನ್ನ ಸಹೋದರರಾಗಿರುವ ಹಾರೂನರನ್ನು.');
INSERT INTO "kannada" VALUES(20,31,'20:31','ಅವರ ಮೂಲಕ ನನ್ನ ಕೈ ಬಲಪಡಿಸು .');
INSERT INTO "kannada" VALUES(20,32,'20:32','ಅವರನ್ನು ನನ್ನ ಕರ್ತವ್ಯದಲ್ಲಿ ಸಹಭಾಗಿಯಾಗಿ ಮಾಡು.');
INSERT INTO "kannada" VALUES(20,33,'20:33','ನಿನ್ನ ಪಾವಿತ್ರ್ಯವನ್ನು ನಮಗೆ ಬಹಳವಾಗಿ ಕೊಂಡಾಡಲು,');
INSERT INTO "kannada" VALUES(20,34,'20:34','ಹಾಗೂ ಬಹಳವಾಗಿ ನಿನ್ನನ್ನು ಸ್ಮರಿಸಲು.');
INSERT INTO "kannada" VALUES(20,35,'20:35','ನೀನು ಸದಾ ನಮ್ಮ ಸ್ಥಿತಿಯನ್ನು ಚೆನ್ನಾಗಿ ಕಾಣುತ್ತಿರುವಿ');
INSERT INTO "kannada" VALUES(20,36,'20:36','ಅಲ್ಲಾಹು ಹೇಳಿದನು ``ಓ ಮೂಸಾ, ನೀವು ಕೇಳಿ ಕೊಂಡದ್ದನ್ನು ನಿಮಗೆ ಕೊಡಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(20,37,'20:37','ಇನ್ನೊಂದು ಬಾರಿ ಕೂಡ ನಿಮ್ಮ ಮೇಲೆ ನಾವು ಅನುಗ್ರಹ ತೋರಿದ್ದುಂಟು.');
INSERT INTO "kannada" VALUES(20,38,'20:38','ಸೂಚಿಸಬೇಕಾದ ಸಂಗತಿಯನ್ನು ನಾವು ನಿಮ್ಮ ತಾಯಿಗೆ ಸೂಚಿಸಿದ ಸಂದರ್ಭವನ್ನೂ ಸ್ಮರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(20,39,'20:39','“ಅಂದರೆ ನೀನು ಈ ಮಗುವನ್ನು ಪೆಟ್ಟಿಗೆ ಯೊಳಗೆ ಇರಿಸು. ಪೆಟ್ಟಿಗೆಯನ್ನು ನದಿಯಲ್ಲಿ ತೇಲಿ ಬಿಡು. ನದಿಯು ಅದನ್ನು ದಡಕ್ಕೆ ಎಸೆಯುವುದು . ಅದನ್ನು ನನ್ನ ಶತ್ರು ಹಾಗೂ ಈ ಮಗುವಿನ ಶತ್ರುವಾದ ಒಬ್ಬನು ಎತ್ತಿಕೊಳ್ಳುವನು” ಎಂಬುದಾಗಿ. ನಾನು ನನ್ನ ಕಡೆಯಿಂದ ನಿಮ್ಮ ಮೇಲೆ ಪ್ರೀತಿಯನ್ನು ಹಾಕಿದೆನು ಮತ್ತು ನನ್ನ ಸಂರಕ್ಷಣೆಯಲ್ಲೇ ಂ ನಿಮ್ಮ ಆರೈಕೆಯ ವ್ಯವಸ್ಥೆ ಮಾಡಿದೆನು.');
INSERT INTO "kannada" VALUES(20,40,'20:40','ನಿಮ್ಮ ಸಹೋದರಿ ನಡೆದು ಹೋಗಿ, “ಈ ಶಿಶುವನ್ನು ಅತ್ಯುತ್ತಮ ರೀತಿಯಿಂದ ಆರೈಕೆ ಮಾಡಬಲ್ಲವಳನ್ನು ನಿಮಗೆ ತೋರಿಸಿ ಕೊಡಲೇ?” ಎಂದು ಕೇಳಿದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ. ಹೀಗೆ ನಿಮ್ಮ ತಾಯಿಯ ಕಣ್ತಣಿಯಲು ಹಾಗೂ ಅವಳು ದುಃಖಿಸದಿರಲು ನಾವು ನಿಮ್ಮನ್ನು ಪುನಃ ನಿಮ್ಮ ತಾಯಿಯ ಬಳಿಗೆ ತಲುಪಿಸಿದ್ದೇವೆ. ಇನ್ನೊಂದು ಸಂದರ್ಭದಲ್ಲಿ ನೀವು ಒಬ್ಬನನ್ನು ವಧಿಸಿ ಬಿಟ್ಟಿದ್ದೀರಿ. ನಾವು ನಿಮ್ಮನ್ನು ಆ ದುಃಖದಿಂದ ಪಾರುಗೊಳಿಸಿದ್ದೇವೆ. ನಿಮ್ಮನ್ನು ವಿವಿಧ ಪರೀಕ್ಷಣಗಳಿಗೆ ವಿಧೇಯಗೊಳಿಸಿದ್ದೇವೆ. ನೀವು ಮದ್ಯನದವರ ಜೊತೆ ಅನೇಕ ವರುಷ ನೆಲೆಸಿದಿರಿ. ಅನಂತರ, ಓ ಮೂಸಾ! ನೀವು ನಿಶ್ಚಿತ ಸ್ಥಾನಕ್ಕೆ ತಲುಪಿರುವಿರಿ.');
INSERT INTO "kannada" VALUES(20,41,'20:41','ನಾನು ನಿಮ್ಮನ್ನು ನನ್ನ ಕಾರ್ಯಕ್ಕಾಗಿ ಆಯ್ದು ಕೊಂಡಿರುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(20,42,'20:42','ನೀವು ಮತ್ತು ನಿಮ್ಮ ಸಹೋದರ ನನ್ನ ನಿದರ್ಶನಗಳೊಂದಿಗೆ ಹೋಗಿರಿ. ನೀವು ನನ್ನ ಸ್ಮರಣೆಯಲ್ಲಿ ಲೋಪ ಮಾಡಬೇಡಿರಿ .');
INSERT INTO "kannada" VALUES(20,43,'20:43','ನೀವಿಬ್ಬರೂ ಫಿರ್‍ಔನನ ಬಳಿಗೆ ಹೋಗಿರಿ, ಅವನು ಅತಿಕ್ರಮಿಯಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(20,44,'20:44','ಅವನಲ್ಲಿ ನೀವಿಬ್ಬರು ನಯವಾಗಿ ಮಾತನಾಡಿರಿ. ಬಹುಶಃ ಅವನು ಉಪದೇಶ ಸ್ವೀಕರಿಸಿಯಾನು ಅಥವಾ ಭಯಪಟ್ಟಾನು.');
INSERT INTO "kannada" VALUES(20,45,'20:45','ಆಗ ಇಬ್ಬರೂ - “ಓ ನಮ್ಮ ಪ್ರಭೂ, ಅವನು ನಮ್ಮ ಮೇಲೆ ಅತಿರೇಕವೆಸಗುವ ಅಥವಾ ನಮ್ಮ ಮೇಲೆ ಮುಗಿಬೀಳುವ ಆತಂಕ ನಮಗಿದೆ” ಎಂದರು.');
INSERT INTO "kannada" VALUES(20,46,'20:46','ಆಗ, ಅಲ್ಲಾಹನು ಹೇಳಿದನು; “ಹೆದರಬೇಡಿರಿ, ನಾನು ನಿಮ್ಮ ಸಂಗಡವಿದ್ದೇನೆ, ಎಲ್ಲವನ್ನೂ ಆಲಿಸುತ್ತಿದ್ದೇನೆ ಮತ್ತು ವೀಕ್ಷಿಸುತ್ತಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(20,47,'20:47','ಅವನ ಬಳಿಗೆ ಹೋಗಿರಿ. ಹೀಗೆ ಹೇಳಿರಿ, `ನಾವಿ ಬ್ಬರೂ ನಿನ್ನ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಕಳುಹಿ ಸಲ್ಪಟ್ಟ ದೂತರು. ಆದ್ದರಿಂದ ಇಸ್‍ರಾಈಲ ಮಕ್ಕ ಳನ್ನು ನಮ್ಮ ಜೊತೆ ಕಳುಹಿಸಿ ಕೊಡಬೇಕು. ಅವರಿಗೆ ಕಿರುಕುಳ ಕೊಡಬಾರದು, ನಿನ್ನ ಬಳಿಗೆ ನಿನ್ನ ಪ್ರಭುವಿನ ನಿದರ್ಶನವನ್ನು ನಾವು ತಂದಿರುತ್ತೇವೆ. ಸನ್ಮಾರ್ಗವನ್ನು ಅನುಸರಿಸಿ ನಡೆದವನಿಗೆ ಶಾಂತಿ ಸಂರಕ್ಷಣೆಯಿದೆ.');
INSERT INTO "kannada" VALUES(20,48,'20:48','ಸುಳ್ಳಾಗಿಸುವವನಿಗೂ ವಿಮುಖನಾದನಿಗೂ ಶಿಕ್ಷೆ ಇದೆಯೆಂದು ನಮಗೆ ದಿವ್ಯವಾಣಿಯ ಮೂಲಕ ತಿಳಿಸಿಕೊಡಲಾಗಿದೆ”.');
INSERT INTO "kannada" VALUES(20,49,'20:49','ಆಗ ಫಿರ್‍ಔನ್ ಹೀಗೆ ಹೇಳಿದನು, “ಸರಿ, ಹಾಗಾದರೆ ನಿಮ್ಮಿಬ್ಬರ ಪ್ರಭು ಯಾರು, ಮೂಸಾ?”');
INSERT INTO "kannada" VALUES(20,50,'20:50','ಮೂಸಾ ಹೇಳಿದರು, “ಪ್ರತಿಯೊಂದು ವಸ್ತುವಿಗೆ ಅದರದೇ ರೂಪ ಕೊಟ್ಟು ಆ ಬಳಿಕ ಅದಕ್ಕೆ ದಾರಿ ತೋರಿದವನೇ ನಮ್ಮ ಪ್ರಭು” .');
INSERT INTO "kannada" VALUES(20,51,'20:51','ಫಿರ್‍ಔನ್ ಹೇಳಿದನು; “ಹಾಗಾದರೆ ಗತ ಪೀಳಿಗೆಗಳ ಸ್ಥಿತಿ ಏನಾಗಿತ್ತು ?”');
INSERT INTO "kannada" VALUES(20,52,'20:52','ಮೂಸಾ ಹೇಳಿದರು, “ಅದರ ಜ್ಞಾನವು ನನ್ನ ಪ್ರಭುವಿನ ಬಳಿಯಿರುವ ಒಂದು ಗ್ರಂಥದಲ್ಲಿ ದಾಖಲಿಸಲಾಗಿದೆ. ನನ್ನ ಪ್ರಭು ತಪ್ಪಲಾರ. ಮತ್ತು ಮರೆಯಲಾರ”.');
INSERT INTO "kannada" VALUES(20,53,'20:53','ನಿಮಗಾಗಿ ಭೂಮಿಯನ್ನು ತೊಟ್ಟಿಲಾಗಿ ಮಾಡಿ ದವನೂ ಅದರಲ್ಲಿ ನಿಮಗೆ ಸಂಚರಿಸಲು ಹಲವು ದಾರಿಗಳನ್ನು ತೆರೆದವನೂ ಆಕಾಶದಿಂದ ನೀರನ್ನು ಸುರಿಸಿದವನೂ ಅವನೇ. ಅನಂತರ ಅದರ ಮೂಲಕ ವಿವಿಧ ಸಸ್ಯಗಳಿಂದ ತರತರದ ಜೋಡಿಗಳನ್ನು ನಾವು ಹೊರತರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(20,54,'20:54','ಅದರಿಂದ ನೀವೂ ತಿನ್ನಿರಿ. ನಿಮ್ಮ ಜಾನುವಾರು ಗಳನ್ನೂ ಮೇಯಿಸಿರಿ. ಬುದ್ಧಿವಂತರಿಗೆ ಖಂಡಿತ ಇದರಲ್ಲಿ ಅನೇಕ ನಿದರ್ಶನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(20,55,'20:55','ಇದೇ ಮಣ್ಣಿನಿಂದ ನಾವು ನಿಮ್ಮನ್ನು ಸೃಷ್ಟಿಸಿದ್ದೇವೆ . ಇದರೊಳಕ್ಕೆ ನಿಮ್ಮನ್ನು ಮರಳಿ ಒಯ್ಯುತ್ತೇವೆ. ಇದರಿಂದಲೇ ನಾವು ನಿಮ್ಮನ್ನು ಪುನಃ ಹೊರತರುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(20,56,'20:56','ನಾವು ಫಿರ್‍ಔನನಿಗೆ ನಮ್ಮ ಎಲ್ಲ ನಿದರ್ಶನ ಗಳನ್ನೂ ತೋರಿಸಿದೆವು. ಆದರೆ ಅವನು ಸುಳ್ಳಾಗಿಸಿದನು ಮತ್ತು ನಿರಾಕರಿಸಿದನು.');
INSERT INTO "kannada" VALUES(20,57,'20:57','ಅವನು ಹೀಗೆ ಕೇಳಿದನು; “ಓ ಮೂಸಾ, ನೀನು ನಿನ್ನ ಯಕ್ಷಿಣಿಯ ಬಲದಿಂದ ನಮ್ಮನ್ನು ನಮ್ಮ ದೇಶದಿಂದ ಹೊರದಬ್ಬಲು ಬಂದಿದ್ದೀಯಾ?');
INSERT INTO "kannada" VALUES(20,58,'20:58','ಹಾಗಾದರೆ, ನಾವೂ ನಿನಗೆದುರು ಅಂತಹುದೇ ಮಾಟವನ್ನು ತರುತ್ತೇವೆ. ಆದ್ದರಿಂದ ನಮ್ಮ - ನಿನ್ನ ನಡುವೆ ಒಂದು ಅವಧಿಯನ್ನು ಗೊತ್ತು ಮಾಡು. ನಾವಾಗಲಿ ನೀನಾಗಲಿ ಈ ಕರಾರನ್ನು ಉಲ್ಲಂಘಿಸ ಬಾರದು. ಅದು ನಮಗೆಲ್ಲರಿಗೂ ಸಮಾನ ರೀತಿಯಲ್ಲಿ ಸೌಕರ್ಯವಿರುವ ಒಂದು ಸ್ಥಳದಲ್ಲಾಗಿರಲಿ”.');
INSERT INTO "kannada" VALUES(20,59,'20:59','ಮೂಸಾ ಹೇಳಿದರು; “ಉತ್ಸವದ ದಿನವೇ ನಿಮಗೆ ನಿಗದಿತ ಅವಧಿ, ಆದ್ದರಿಂದ ಹೊತ್ತೇರಿದಾಗ ಜನರು ಒಟ್ಟು ಸೇರಲಿ” .');
INSERT INTO "kannada" VALUES(20,60,'20:60','ಫಿರ್‍ಔನನು ಮರಳಿ ತನ್ನ ಎಲ್ಲ ಕುತಂತ್ರಗಳನ್ನು ಒಟ್ಟುಗೂಡಿಸಿದನು ಮತ್ತು ಸ್ಪರ್ಧೆಗೆ ಇಳಿದನು.');
INSERT INTO "kannada" VALUES(20,61,'20:61','ಮೂಸಾ ರು ಅವರನ್ನು ತನ್ನ ಬಳಿ ಕರೆದು “ನಿಮಗೆ ಮಹಾನಾಶ!, ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಳ್ಳಾರೋಪಗಳನ್ನು ಹೊರಿಸಬೇಡಿರಿ. ಅನ್ಯಥಾ ಅವನು ಘೋರ ಶಿಕ್ಷೆಯ ಮೂಲಕ ನಿಮ್ಮನ್ನು ನಿರ್ವಂಶ ಮಾಡುವನು. ಮಿಥ್ಯಾರೋಪವನ್ನು ಹೊರಿಸಿದವನು, ಖಂಡಿತ ಭಗ್ನನಾದನು.');
INSERT INTO "kannada" VALUES(20,62,'20:62','ಇದನ್ನು ಕೇಳಿದಾಗ ಅವರೊಳಗೆ ಭಿನ್ನಾಭಿಪ್ರಾಯ ತಲೆದೋರಿತು. ಅವರು ರಹಸ್ಯವಾಗಿ ಪರಸ್ಪರ ಸಮಾಲೋಚಿಸಿದರು.');
INSERT INTO "kannada" VALUES(20,63,'20:63','ಕೊನೆಗೆ ಅವರು (ಹೀಗೆ)ಹೇಳಿದರು. “ಇವರಿ ಬ್ಬರೂ ಕೇವಲ ಯಕ್ಷಿಣಿಗಾರರು. ಇವರ ಉದ್ದೇಶವು ತಮ್ಮ ಯಕ್ಷಿಣಿಯ ಬಲದಿಂದ ನಿಮ್ಮನ್ನು ನಿಮ್ಮ ನಾಡಿನಿಂದ ಹೊರಹಾಕುವುದು ಮತ್ತು ನಿಮ್ಮ ಅತಿ ಶ್ರೇಷ್ಟವಾದ ಮಾರ್ಗವನ್ನು ಕೊನೆಗಾಣಿಸುವುದು.');
INSERT INTO "kannada" VALUES(20,64,'20:64','ಆದುದರಿಂದ ನಿಮ್ಮ ಸಕಲ ತಂತ್ರಗಳನ್ನೂ ಇಂದು ಒಟ್ಟುಗೂಡಿಸಿರಿ. ಒಂದೇ ಅಣಿಯಾಗಿ ರಂಗಕ್ಕೆ ಇಳಿಯಿರಿ. ಎದುರು ಪಕ್ಷದವರನ್ನು ಯಾರು ಸೋಲಿಸಿ ಬಿಡುತ್ತಾರೆ, ಅವರಿಂದು ಖಂಡಿತ ಗೆಲುವು ಸಾಧಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(20,65,'20:65','ಅವರು ಹೇಳಿದರು; “ಓ ಮೂಸಾ, ಒಂದೋ ನೀವು ಮೊದಲು ಎಸೆಯಿರಿ ಅಥವಾ ಮೊದಲು ನಾವು ಎಸೆಯುತ್ತೇವೆ” .');
INSERT INTO "kannada" VALUES(20,66,'20:66','ಆಗ ಮೂಸಾ “ಇಲ್ಲ, ನೀವೇ ಎಸೆಯಿರಿ” ಎಂದರು . ಅಷ್ಟರಲ್ಲಿ ಅವರ ಹಗ್ಗಗಳೂ ಅವರ ದಂಡಗಳೂ ಚಲಿಸುತ್ತಿರುವಂತೆ ಅವರ ಇಂದ್ರ ಜಾಲದ ಬಲದಿಂದ ಮೂಸಾರಿಗೆ ಭಾಸವಾಯಿತು .');
INSERT INTO "kannada" VALUES(20,67,'20:67','ಅಷ್ಟರಲ್ಲಿ ಮೂಸಾ ತನ್ನ ಮನದೊಳಗೇ ತಣ್ಣನೆ ಹೆದರಿದರು.');
INSERT INTO "kannada" VALUES(20,68,'20:68','ಆಗ ನಾವು ಹೇಳಿದೆವು; “ಹೆದರಬೇಡಿರಿ, ನೀವೇ ಗೆಲ್ಲುವಿರಿ.');
INSERT INTO "kannada" VALUES(20,69,'20:69','ನಿಮ್ಮ ಬಲ ಕೈಯಲ್ಲಿರುವ ದಂಡವನ್ನು ಕೆಳಗೆ ಹಾಕಿರಿ. ಅದು ಅವರ ಕಣ್ಣು ಕಟ್ಟಿದ ವಸ್ತುಗಳನ್ನು ನುಂಗಿ ಹಾಕುವುದು. ಅವರು ಮಾಡಿದ್ದೆಲ್ಲ ಕೇವಲ ಜಾದೂಗಾರನ ಕುತಂತ್ರವಾಗಿದ್ದು ಜಾದೂಗಾರನು ಎಲ್ಲೇ ಬರಲಿ, ಅವನೆಂದೂ ಯಶಸ್ವಿಯಾಗಲಾರ”.');
INSERT INTO "kannada" VALUES(20,70,'20:70','ಕೊನೆಗೆ ಎಲ್ಲ ಜಾದೂಗಾರರೂ ಸಾಷ್ಟಾಂಗವೆರ ಗುತ್ತ ನೆಲಕ್ಕೆ ಬಿದ್ದರು. ಅವರು ಸಾರಿದರು; “ನಾವು ಮೂಸಾ ಮತ್ತು ಹಾರೂನರ ಪ್ರಭುವಿನ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸಿದ್ದೇವೆ’’.');
INSERT INTO "kannada" VALUES(20,71,'20:71','ಫಿರ್‍ಔನ್ (ಹೀಗೆ) ಹೇಳಿದನು; “ನಾನು ನಿಮಗೆ ಅನುಮತಿ ಕೊಡುವುದಕ್ಕೆ ಮುಂಚೆಯೇ ನೀವು ಅವನ ಮೇಲೆ ವಿಶ್ವಾಸವಿಟ್ಟಿರಾ ? ಇವನು ನಿಮಗೆ ಯಕ್ಷಿಣಿ ವಿದ್ಯೆಯನ್ನು ಕಲಿಸಿದ್ದ ಗುರು ಎಂದು ತಿಳಿಯಿತು. ಆದ್ದರಿಂದ ನಾನು ನಿಮ್ಮ ಕೈಕಾಲು ಗಳನ್ನು ವಿರುದ್ಧ ದಿಕ್ಕುಗಳಿಂದ ಕತ್ತರಿಸಿ ಹಾಕು ತ್ತೇನೆ. ಖರ್ಜೂರ ವೃಕ್ಷದ ಕಾಂಡಗಳ ಮೇಲೆ ನಿಮ್ಮನ್ನು ಶಿಲುಬೆಗೇರಿಸುತ್ತೇನೆ. ಆಗ ನಮ್ಮಿಬ್ಬರ ಪೈಕಿ ಯಾರ ಶಿಕ್ಷೆ ಕಠಿಣ ಹಾಗೂ ದೀರ್ಘವಾದುದೆಂದು ನಿಮಗೆ ಚೆನ್ನಾಗಿ ತಿಳಿಯುವುದು” .');
INSERT INTO "kannada" VALUES(20,72,'20:72','ಜಾದೂಗಾರರು ಹೀಗೆ ಉತ್ತರಕೊಟ್ಟರು, “ನಮಗೆ ಬಂದು ಸಿಕ್ಕಿದ ಪ್ರತ್ಯಕ್ಷ ಪ್ರಮಾಣಗಳು ಹಾಗೂ ನಮ್ಮನ್ನು ಸೃಷ್ಟಿಸಿದ ಅಲ್ಲಾಹನಿಗಿಂತ ಹೆಚ್ಚು ಪ್ರಾಶ ಸ್ತ್ಯವನ್ನು ನಿನಗೆ ಖಂಡಿತ ಕೊಡಲಾರೆವು. ಆದ್ದ ರಿಂದ ನೀನೇನು ವಿಧಿಸುತ್ತೀಯೋ ಅದನ್ನು ವಿಧಿಸು. ನೀನು ಇಹಲೋಕ ಜೀವನದ ತೀರ್ಮಾನ ಮಾತ್ರ ಮಾಡಬಲ್ಲೆ.');
INSERT INTO "kannada" VALUES(20,73,'20:73','ನಮ್ಮ ಅಪರಾಧಗಳನ್ನೂ ನೀನು ನಮ್ಮ ಮೇಲೆ ಬಲವಂತದಿಂದ ಮಾಡಿಸಿದ ಇಂದ್ರಜಾಲವನ್ನೂ ಕ್ಷಮಿಸಲಿಕ್ಕಾಗಿ ನಾವು ನಮ್ಮ ಪ್ರಭುವಿನ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸಿದ್ದೇವೆ. ಅಲ್ಲಾಹನೇ ಉತ್ತಮನು ಮತ್ತು ಅವನೇ ಚಿರಂತನನೂ ಆಗಿರುತ್ತಾನೆ” .');
INSERT INTO "kannada" VALUES(20,74,'20:74','ತನ್ನ ಪ್ರಭುವಿನ ಸನ್ನಿಧಿಗೆ ಯಾರು ಅಪರಾಧಿಯಾಗಿ ಹಾಜರಾಗುತ್ತಾನೋ ಅವನಿಗೆ ಖಂಡಿತ ನರಕವಿದೆ. ಅದರಲ್ಲಿ ಅವನು ಸಾಯಲಿಕ್ಕೂ ಇಲ್ಲ, ಬದುಕಲಿಕ್ಕೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(20,75,'20:75','ಯಾರು ತನ್ನ ಪ್ರಭುವಿನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಸತ್ಕರ್ಮ ವೆಸಗಿದ ಸತ್ಯ ವಿಶ್ವಾಸಿಯ ನೆಲೆಯಲ್ಲಿ ಹಾಜ ರಾಗುತ್ತಾನೋ ಅಂಥವರಿಗೆ ಉನ್ನತ ಸ್ಥಾನ ಮಹಿಮೆಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(20,76,'20:76','ಅಂದರೆ, ಚಿರವಾಸದ ಉದ್ಯಾನಗಳು. ಅವುಗಳ ತಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆಗಳು ಹರಿಯುತ್ತಿವೆ. ಅವರು ಅವುಗಳಲ್ಲಿ ಸದಾಕಾಲ ನೆಲೆಸುವರು. ಪರಿಶುದ್ಧತೆಯನ್ನು ಪಡೆದವರ ಪ್ರತಿಫಲವಿದು.');
INSERT INTO "kannada" VALUES(20,77,'20:77','ನಾವು ಮೂಸಾರಿಗೆ ಹೀಗೆ ಸಂದೇಶ ನೀಡಿದೆವು. “ನೀವು ರಾತ್ರೋರಾತ್ರಿ ನನ್ನ ದಾಸ ರನ್ನು ಕರೆದು ಕೊಂಡು ಹೋಗಿರಿ. ಅವರಿಗೆ ಸಮುದ್ರದಲ್ಲಿ ಶುಷ್ಕ ದಾರಿಯನ್ನು ಮಾಡಿರಿ . ಯಾರಾದರೂ ಬೆನ್ನಟ್ಟಿ ಹಿಡಿಯುವರೆಂಬ ಭಯವಾಗಲಿ ಮುಳುಗಿ ಹೋದೀತೆಂಬ ಆತಂಕವಾಗಲಿ ನಿಮಗೆ ಬೇಡ”.');
INSERT INTO "kannada" VALUES(20,78,'20:78','ಫಿರ್‍ಔನನು ತನ್ನ ಸೇನೆಯ ಸಹಿತ ಬೆನ್ನಟ್ಟಿ ಬಂದನು. ತರುವಾಯ ಸಮುದ್ರವು ಅವರನ್ನು ಆವರಿಸಬೇಕಾಗಿದ್ದ ರೀತಿಯಲ್ಲಿ ಸಂಪೂರ್ಣವಾಗಿ ಆವರಿಸಿತು.');
INSERT INTO "kannada" VALUES(20,79,'20:79','ಫಿರ್‍ಔನ್ ತನ್ನ ಜನಾಂಗವನ್ನು ದಾರಿಗೆಡಿಸಿ ದನು. ಸರಿಯಾದ ದಾರಿಯನ್ನು ತೋರಿಸಲೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(20,80,'20:80','ಓ ಇಸ್ರಾಈಲ ಮಕ್ಕಳೇ, ನಾವು ನಿಮ್ಮನ್ನು ನಿಮ್ಮ ಶತ್ರುವಿನಿಂದ ರಕ್ಷಿಸಿದ್ದೇವೆ. ಥೂರ್‍ಸೀನಾ ಪರ್ವತದ ಬಲಗಡೆಯನ್ನು ನಿಮಗೆ ನಾವು ವಾಗ್ದಾನ ಮಾಡಿದ್ದೇವೆ. ನಿಮ್ಮ ಮೇಲೆ ಮನ್ನ ಮತ್ತು ಸಲ್ವವನ್ನು ಇಳಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(20,81,'20:81','ನಾವು ನೀಡಿದ ಶುದ್ಧ ಆಹಾರವನ್ನು ಉಣ್ಣಿರಿ. ಅದರಲ್ಲಿ ಅತಿಕ್ರಮಿಸಬೇಡಿರಿ. ಅನ್ಯಥಾ ನಿಮ್ಮ ಮೇಲೆ ನನ್ನ ಕೋಪವೆರಗುವುದು. ಯಾರ ಮೇಲೆ ನನ್ನ ಕೋಪವೆರಗಿತೋ ಅವನು (ನರಕಕ್ಕೆ) ಪತನಗೊಂಡನು.');
INSERT INTO "kannada" VALUES(20,82,'20:82','ಆದರೆ ಪಶ್ಚಾತ್ತಾಪ ಪಟ್ಟು ವಿಶ್ವಾಸವಿರಿಸಿ, ಸತ್ಕರ್ಮ ಮಾಡಿ, ಆ ಬಳಿಕ ನೇರ ಮಾರ್ಗದಲ್ಲೇ ನಡೆಯು ತ್ತಿರುವವನನ್ನು ನಾನು ಅತಿಯಾಗಿ ಕ್ಷಮಿಸು ತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(20,83,'20:83','“ಓ ಮೂಸಾ ನೀವು ನಿಮ್ಮ ಜನಾಂಗವನ್ನು ಬಿಟ್ಟು ಇಷ್ಟು ಅವಸರದಲ್ಲಿ ಏಕೆ ಬಂದಿರಿ ?”');
INSERT INTO "kannada" VALUES(20,84,'20:84','ಅವರು ಇಂತೆಂದರು; “ಅವರು ನನ್ನ ಬೆನ್ನ ಹಿಂದೆಯೇ ಬರುತ್ತಿದ್ದಾರೆ. ಪ್ರಭೂ, ನಿನ್ನ ಒಲವನ್ನು ಬಯಸಿ ನಿನ್ನ ಹತ್ತಿರಕ್ಕೆ ಅವಸರದಲ್ಲಿ ಬಂದಿದ್ದೇವೆ”.');
INSERT INTO "kannada" VALUES(20,85,'20:85','ಆಗ ಅಲ್ಲಾಹು ಹೇಳಿದನು, “ಹಾಗಿದ್ದರೆ ನೀವು ಬಂದ ನಂತರ ನಾವು ನಿಮ್ಮ ಜನಾಂಗವನ್ನು ಪರೀಕ್ಷೆಗೆ ಗುರಿಪಡಿಸಿದ್ದೇವೆ. ಸಾಮಿರೀ ಅವರನ್ನು ತಪ್ಪು ದಾರಿಗೆಳೆದಿದ್ದಾನೆ” .');
INSERT INTO "kannada" VALUES(20,86,'20:86','ಮೂಸಾ ಅತ್ಯಂತ ಕುಪಿತ ಹಾಗೂ ವ್ಯಾಕುಲ ಚಿತ್ತರಾಗಿ ತನ್ನ ಜನಾಂಗದ ಕಡೆಗೆ ಮರಳಿ ಹೋಗಿ ಹೀಗೆ ಕೇಳಿದರು; “ಓ ನನ್ನ ಜನಾಂಗವೇ, ನಿಮ್ಮ ಪ್ರಭು ನಿಮಗೆ ಉತ್ತಮ ವಾಗ್ದಾನ ಗಳನ್ನು ಮಾಡಿರಲಿಲ್ಲವೇ? ನಂತರ ನಿಮಗೆ ಹೆಚ್ಚು ಕಾಲ ತಗುಲಿತೇ? ಅಥವಾ ನೀವು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕ್ರೋಧವೇ ನಿಮ್ಮ ಮೇಲೆರ ಗುವುದನ್ನು ಉದ್ದೇಶವಿಟ್ಟಿರುವಿರಾ? ಹಾಗೆಂದು ನೀವು ನನಗೆ ಕೊಟ್ಟಿದ್ದ ವಚನವನ್ನು ಭಂಗ ಗೊಳಿಸಿದಿರಾ?” ');
INSERT INTO "kannada" VALUES(20,87,'20:87','ಅವರು ಹೀಗೆಂದರು - “ನಾವು ನಿಮಗೆ ಕೊಟ್ಟಿದ್ದ ವಚನವನ್ನು ನಮ್ಮ ಸ್ವಂತ ಅಣತಿಯಿಂದ ಭಂಗ ಗೊಳಿಸಲಿಲ್ಲ. ನಾವು ಜನರ ಆಭರಣಗಳ ಹೊರೆಯಿಂದ ಹೇರಲ್ಪಟ್ಟಿದ್ದೆವು. ಹಾಗೆ ನಾವು ಅವು ಗಳನ್ನು ಬೆಂಕಿಗೆ ಎಸೆದೆವು. ಅದೇ ರೀತಿಯಲ್ಲಿ ಸಾಮಿರಿಯೂ ತನ್ನ ಬಳಿಯಿರುವುದನ್ನು ಬೆಂಕಿಗೆ ಹಾಕಿದನು.');
INSERT INTO "kannada" VALUES(20,88,'20:88','ಆಮೇಲೆ ಅವನು ಅವರಿಗಾಗಿ ಒಂದು ಕರುವಿನ ರೂಪವನ್ನು ಮಾಡಿ ಹೊರತಂದನು. ಅದರಿಂದ ಎತ್ತಿನಂತಹ ಧ್ವನಿ ಹೊರಡುತ್ತಿತ್ತು. ಆಗ ಜನರು, “ಇದುವೇ ನಿಮ್ಮ ದೇವರು ಮತ್ತು ಮೂಸಾರ ದೇವರು. ಮೂಸಾ ಇದನ್ನು ಮರೆತುಬಿಟ್ಟಿದ್ದಾರೆ” ಎಂದು ಕೊಂಡರು .');
INSERT INTO "kannada" VALUES(20,89,'20:89','ಆದರೆ ಆ ಕರುವು ಮಾತಿಗೆ ಉತ್ತರಿಸುವುದಿಲ್ಲ ಎಂದಾಗಲಿ ಅವರಿಗೆ ಗುಣ-ದೋಷಗಳನ್ನು ಕೊಡುವ ಯಾವ ಅಧಿಕಾರವನ್ನೂ ಪಡೆದಿಲ್ಲ ಎಂದಾಗಲಿ ಅವರು ಯೋಚಿಸದಾದರು .');
INSERT INTO "kannada" VALUES(20,90,'20:90','ಹಾರೂನರು (ಮೂಸಾ)ರು ಬರುವುದಕ್ಕಿಂತ) ಮೊದಲೇ ಅವರೊಡನೆ ಸಾರಿ ಹೇಳಿದ್ದರು; “ಓ ಜನರೇ, ನೀವು ಇದರ ಕಾರಣದಿಂದ ಕ್ಷೋಭೆಗೆ ಗುರಿಯಾಗಿದ್ದೀರಿ. ಪರಮ ದಯಾಳು ವಾದ ಅಲ್ಲಾಹನೇ ನಿಮ್ಮ ಪ್ರಭು, ಆದುದರಿಂದ ನೀವು ನನ್ನನ್ನು ಅನುಸರಿಸಿರಿ. ನನ್ನ ಆಜ್ಞೆಯನ್ನು ಪಾಲಿಸಿರಿ.”');
INSERT INTO "kannada" VALUES(20,91,'20:91','ಆದರೆ ಜನರು - “ಮೂಸಾ ನಮ್ಮ ಬಳಿಗೆ ಮರಳಿ ಬರುವವರೆಗೂ ನಾವು ಇದರ ಪೂಜೆಯನ್ನೇ ಮಾಡುತ್ತೇವೆ” ಎಂದು ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(20,92,'20:92','(ಆಗ ಧಾವಿಸಿ ಬಂದ) ಮೂಸಾ ಕೇಳಿದರು; “ಓ ಹಾರೂನ್, ಇವರು ದಾರಿ ತಪ್ಪುತ್ತಿರುವುದನ್ನು ನೀವು ಕಂಡಾಗ ನಿಮ್ಮನ್ನು ತಡೆದವರಾರು?');
INSERT INTO "kannada" VALUES(20,93,'20:93','ಅವರು ನನ್ನ ಮಾರ್ಗದರ್ಶನವನ್ನು ಅನುಸರಿಸದಂತೆ ನೀವು ಮನಸ್ಸಾರೆ ನನ್ನ ಅಪ್ಪಣೆ ವಿರುದ್ಧ ನಡೆದಿರಾ?”');
INSERT INTO "kannada" VALUES(20,94,'20:94','ಹಾರೂನರು,-‘ಓ ನನ್ನ ಮಾತೆಯ ಪುತ್ರನೇ! ನನ್ನ ಗಡ್ಡವನ್ನು ಹಿಡಿಯಬೇಡ. ನನ್ನ ತಲೆ ಕೂದ ಲನ್ನೂ ಎಳೆಯಬೇಡ. ‘ಇಸ್ರಾಈಲ್ ಜನಾಂಗದ ನಡುವೆ ನೀನು ಒಡಕುಂಟು ಮಾಡಿದೆ, ನನ್ನ ಮಾತ ನ್ನು ಕೇಳಲಿಲ್ಲ’ ಎಂದು ನೀನು ನನ್ನ ಬಗ್ಗೆ ಹೇಳುವಿ’ ಎಂದು ನಾನು ಭಯಪಟ್ಟೆ’ ಎಂದು ಉತ್ತರಿಸಿದರು.');
INSERT INTO "kannada" VALUES(20,95,'20:95','ಆಗ ಮೂಸಾ, “ಓ ಸಾಮಿರೀ, ನಿನ್ನ ಸಂಗತಿ ಯೇನು? ಎಂದು ಕೇಳಿದರು.');
INSERT INTO "kannada" VALUES(20,96,'20:96','ಅವನು, “ಇವರಿಗೆ ಕಾಣಿಸದಿದ್ದ ಕೆಲವು ವಿಚಾರ ವನ್ನು ನಾನು ಕಂಡೆನು. ಆದುದರಿಂದ ದೂತರ ಪಾದದಡಿಯಿಂದ ನಾನು ಮಣ್ಣಿನ ಹಿಡಿಯನ್ನು ತೆಗೆದು ಅದಕ್ಕೆ ಹಾಕಿದೆ. ನನ್ನ ಮನಸ್ಸು ನನಗೆ ಹೀಗೆಯೇ ಚಂದವಾಗಿ ಕಾಣಿಸಿತು” ಎಂದನು .');
INSERT INTO "kannada" VALUES(20,97,'20:97','ಮೂಸಾ ಹೀಗೆ ಹೇಳಿದರು, “ಹಾಗಾದರೆ ಇಲ್ಲಿಂದ ಹೊರಟು ಹೋಗು. ನಿನ್ನ ಜೀವಮಾನವಿಡೀ “ನನ್ನನ್ನು ಮುಟ್ಟಬೇಡಿರಿ” ಎಂದು ಹೇಳುತ್ತಿರು . ನಿನಗೆ ನಿಗದಿ ಮಾಡಲಾದ ದಿನವೊಂದಿದೆ. ಅದು ನಿನಗೆ ಉಲ್ಲಂಘಿಸಲ್ಪಡಲಾರದು. ನೀನು ಪೂಜಿಸಿದ ಈ ಆರಾಧ್ಯ ವಸ್ತುವನ್ನು ನೋಡು, ನಾವಿನ್ನು ಇದನ್ನು ಸುಟ್ಟು ಹಾಕಿ, ಆಮೇಲೆ ಅದನ್ನು ಹುಡಿ ಮಾಡಿ ಸಮುದ್ರದಲ್ಲಿ ಹರಡಿ ಬಿಡುವೆವು.');
INSERT INTO "kannada" VALUES(20,98,'20:98','ಜನರೇ, ನಿಮ್ಮ ದೇವನು ಏಕಮಾತ್ರ ಅಲ್ಲಾಹನಾಗಿದ್ದು ಅವನ ಹೊರತು ಅನ್ಯ ದೇವನಿಲ್ಲ. ಅವನ ಜ್ಞಾನವು ಸಕಲ ವಸ್ತುಗಳನ್ನು ವ್ಯಾಪಿಸಿ ಕೊಂಡಿದೆ”.');
INSERT INTO "kannada" VALUES(20,99,'20:99','ಓ ಪೈಗಂಬರರೇ, ಈ ರೀತಿ ನಾವು ನಿಮಗೆ ಕೆಲವು ಗತ ವೃತ್ತಾಂತವನ್ನು ತಿಳಿಸುತ್ತೇವೆ. ನಮ್ಮ ಕಡೆಯಿಂದ ನೀತಿ ಭೋಧೆಯನ್ನು (ಖುರ್‍ಆನ್) ನಿಮಗೆ ನಾವು ಕೊಟ್ಟಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(20,100,'20:100','ಯಾರು ಇದರಿಂದ ವಿಮುಖನಾಗುತ್ತಾನೋ, ಅವನು ಪುನರುತ್ಥಾನದ ದಿನ ಕಠಿಣವಾದ ಪಾಪಗಳ ಹೊರೆ ಖಂಡಿತ ಹೊರುವನು.');
INSERT INTO "kannada" VALUES(20,101,'20:101','ಅದರಲ್ಲಿ ಅವರು ಸದಾ ಕಾಲ ಇರುವರು. ಅಂತ್ಯ ದಿನ, ಅವರು ಹೊರಬೇಕಾದ ಹೊರೆಯು ಅವರಿಗೆ ಅತ್ಯಂತ ಅಸಹನೀಯವೆನಿಸುವುದು.');
INSERT INTO "kannada" VALUES(20,102,'20:102','ಅಂದರೆ ಸೂರ್ ಕಹಳೆ ಊದಲಾಗುವ ದಿನ’ ನಾವು ಅಪರಾಧಿಗಳನ್ನು ನೀಲನೇತ್ರದವರನ್ನಾಗಿ ಒಟ್ಟುಗೂಡಿಸಲಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(20,103,'20:103','ಅವರು ಪರಸ್ಪರ ಪಿಸು ಮಾತಿನಲ್ಲಿ, “ನೀವು ಭೂಲೋಕದಲ್ಲಿ ಬಹುಶಃ ಕೇವಲ ಹತ್ತು ದಿನಗಳನ್ನು ಮಾತ್ರ ಕಳೆದಿರುವಿರಿ’’ ಎನ್ನುವರು.');
INSERT INTO "kannada" VALUES(20,104,'20:104','ಆಗ ಅವರಲ್ಲಿ ಅತ್ಯಧಿಕ ಅಂದಾಜು ಮಾಡು ವಾತನು “ನಿಮ್ಮ ಭೂಲೋಕದ ಜೀವನಾವಧಿಯು ಒಂದು ದಿನ ಮಾತ್ರವಿತ್ತು” ಎಂದು ಹೇಳುವುದನ್ನು ನಾವು ಚೆನ್ನಾಗಿ ಬಲ್ಲೆವು .');
INSERT INTO "kannada" VALUES(20,105,'20:105','ಪರ್ವತಗಳ ಬಗ್ಗೆ ಇವರು ನಿಮ್ಮಲ್ಲಿ ಕೇಳುತ್ತಾರೆ. ಆಗ ಹೇಳಿರಿ, ನನ್ನ ಪ್ರಭು ಅವುಗಳನ್ನು ಧೂಳನ್ನಾಗಿ ಮಾಡಿ ಗಾಳಿಯಲ್ಲಿ ಹಾರಿಸಿ ಬಿಡುವನು.');
INSERT INTO "kannada" VALUES(20,106,'20:106','ಆಮೇಲೆ ಅವುಗಳನ್ನು ಸಮತಟ್ಟಾದ ಬಟ್ಟ ಬಯಲನ್ನಾಗಿ ಮಾಡುವನು.');
INSERT INTO "kannada" VALUES(20,107,'20:107','ಯಾವುದೇ ಏರು-ತಗ್ಗುಗಳನ್ನು ಅಲ್ಲಿ ನೀನು ಕಾಣಲಾರೆ.');
INSERT INTO "kannada" VALUES(20,108,'20:108','ಅಂದು ಜನರು ಕೂಗಿ ಕರೆಯುವವನ ಹಿಂದೆ ಯಾವುದೇ ವಕ್ರತೆಯಿಲ್ಲದೆ ನೇರವಾಗಿ ಸಾಗಿ ಬರುವರು. ಸದ್ದು-ಗದ್ದಲಗಳು ಕರುಣಾನಿಧಿಯಾದ ಅಲ್ಲಾಹನ ಮುಂದೆ ಅಡಗಿ ಹೋಗಿವೆ. ಕಾಲ ಸಪ್ಪಳದ ಹೊರತು ಇನ್ನೇನನ್ನೂ ನೀವು ಕೇಳಲಾರಿರಿ .');
INSERT INTO "kannada" VALUES(20,109,'20:109','ಅಂದು ಕರುಣಾನಿಧಿಯಾದ ಅಲ್ಲಾಹನು ಯಾರಿಗೆ ಅನುಮತಿ ನೀಡುವನೋ ಮತ್ತು ಯಾರ ಮಾತನ್ನು ಕೇಳಲು ತೃಪ್ತಿಪಡುವನೋ ಅವರ ಹೊರತು ಯಾರ ಶಿಫಾರಸ್ಸೂ ಫಲಕಾರಿಯಾಗದು.');
INSERT INTO "kannada" VALUES(20,110,'20:110','ಅವನು ಜನರ ಮುಂದಿನ ಮತ್ತು ಹಿಂದಿನ ಎಲ್ಲ ಸ್ಥಿತಿಯನ್ನೂ ಅರಿಯುತ್ತಾನೆ. ಆದರೆ ಇತ ರರಿಗೆ ಅದರ ಬಗ್ಗೆ ಯಾವುದೇ ಜ್ಞಾನವಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(20,111,'20:111','ಚಿರಂತನನೂ ಸರ್ವ ನಿಯಾಮಕನೂ ಆದ ಅಲ್ಲಾಹನ ಮುಂದೆ ಮುಖಗಳು ಬಾಗಿವೆ. ಆಗ ಅಕ್ರಮದ ಪಾಪ ಭಾರ ಹೊತ್ತಿರುವವನು ನಿರಾಶನಾಗುವನು .');
INSERT INTO "kannada" VALUES(20,112,'20:112','ಯಾರು, ಸತ್ಯ ವಿಶ್ವಾಸಿಯಾಗಿದ್ದುಕೊಂಡು, ಸತ್ಕರ್ಮಗಳನ್ನು ಮಾಡುತ್ತಾನೋ ಆತನಿಗೆ ಯಾವುದೇ ಅನ್ಯಾಯ ಮತ್ತು ಹಕ್ಕುಚ್ಯುತಿಯ ಭಯವಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(20,113,'20:113','(ಓ ಪೈಗಂಬರರೇ,) ಈ ರೀತಿ ನಾವು ಇದನ್ನು ಅರಬೀ ಭಾಷೆಯ ಖುರ್‍ಆನ್ ಆಗಿ ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದೇವೆ. ಇದರಲ್ಲಿ ವಿವಿಧ ರೂಪದಲ್ಲಿ ಎಚ್ಚರಿಕೆಯನ್ನು ಆವರ್ತಿಸಿದ್ದೇವೆ. ಇವರು ದುರ್ಮಾರ್ಗದಿಂದ ದೂರವಿರಲಿಕ್ಕಾಗಿ ಅಥವಾ ಅದರ ಮೂಲಕ ಇವರಲ್ಲಿ ಪ್ರಜ್ಞೆ ಮೂಡಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(20,114,'20:114','ಅಲ್ಲಾಹನು ಮಹೋನ್ನತನು, ಯಥಾರ್ಥ ಸಾಮ್ರಾಟನು. ನಿಮ್ಮ ಮೇಲೆ ಖುರ್‍ಆನಿನ ಬೋಧ ನೆಯು ಸಂಪೂರ್ಣವಾಗಿ ಅವತೀರ್ಣಗೊಳ್ಳುವು ದಕ್ಕೆ ಮುಂಚೆ ಅದನ್ನು ಪಠಿಸುವುದ ರಲ್ಲಿ ದುಡು ಕಬೇಡಿರಿ . “ಓ ನನ್ನ ಪ್ರಭೂ ನನಗೆ ಇನ್ನಷ್ಟು ಜ್ಞಾನ ದಯಪಾಲಿಸು” ಎಂದು ಪ್ರಾರ್ಥಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(20,115,'20:115','ನಾವು ಇದಕ್ಕೆ ಮುಂಚೆ ಆದಮರಿಗೆ ಅಪ್ಪಣೆ ಕೊಟ್ಟಿದ್ದೆವು . ಆದರೆ ಅವರು ಮರೆತರು. ನಾವು ಅವರಲ್ಲಿ ಸ್ಥಿರಚಿತ್ತವನ್ನು ಕಾಣಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(20,116,'20:116','ದೇವಚರರೊಡನೆ ಆದಮರಿಗೆ ಸಾಷ್ಟಾಂಗವೆರಗಿರೆಂದು ನಾವು ಆಜ್ಞಾಪಿಸಿದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ. ಆಗ ಅವರೆಲ್ಲರೂ ಸಾಷ್ಟಾಂಗವೆರಗಿದರು. ಆದರೆ ಇಬ್‍ಲೀಸನು ಮಾತ್ರ ನಿರಾ ಕರಿಸಿದನು.');
INSERT INTO "kannada" VALUES(20,117,'20:117','ಆಗ ನಾವು ಆದಮರಿಗೆ, “ಇವನು ನಿಮ್ಮ ಮತ್ತು ನಿಮ್ಮ ಪತ್ನಿಯ ಶತ್ರು. ಆದ್ದರಿಂದ ಇವನು ನಿಮ್ಮಿಬ್ಬರನ್ನೂ ಸ್ವರ್ಗದಿಂದ ಹೊರಹಾಕಿಸದಿರಲಿ. ಹಾಗಾದರೆ ನೀವು ಕಷ್ಟಕ್ಕೀಡಾಗುವಿರಿ.');
INSERT INTO "kannada" VALUES(20,118,'20:118','ನೀವಿಲ್ಲಿ ಹಸಿದಿರಬೇಕಾಗಿಲ್ಲ. ವಿವಸ್ತ್ರರಾಗ ಬೇಕಾಗಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(20,119,'20:119','ಬಾಯಾರಬೇಕಾಗಿಲ್ಲ. ಬಿಸಿಲು ತಟ್ಟಬೇಕಿಲ್ಲ” ಎಂದೆವು.');
INSERT INTO "kannada" VALUES(20,120,'20:120','ಆದರೆ ಶೈತಾನನು ಅವರನ್ನು ಪುಸಲಾಯಿಸಿ ದನು. “ಓ ಆದಮ್, ನಾನು ನಿಮಗೆ ಅಮರಜೀವನವೂ ಅನಂತ ಸಾಮ್ರಾಜ್ಯವೂ ಸಿಗುವಂತಹ ವೃಕ್ಷವನ್ನು ತೋರಿಸಲೇ?” ಎಂದು ಕೇಳಿದನು.');
INSERT INTO "kannada" VALUES(20,121,'20:121','ಕೊನೆಗೆ ಇಬ್ಬರೂ (ಆದಮ್ ಮತ್ತು ಹವ್ವಾ) ಆ ಮರದ ಫಲವನ್ನು ತಿಂದರು. ಆಗ ಅವರ ಗುಪ್ತಾಂಗಗಳು ಪರಸ್ಪರ ಗೋಚರವಾದುವು . ಕೂಡಲೇ ಇಬ್ಬರೂ ಸ್ವರ್ಗದ ಎಲೆಗಳಿಂದ ಮೈ ಮುಚ್ಚಿಕೊಳ್ಳಲು ಪ್ರಯತ್ನಿಸಿದರು. ಆದಮರು ತಮ್ಮ ಪ್ರಭುವಿನ ಆಜ್ಞೋಲ್ಲಂಘನೆ ಮಾಡಿದರು ಮತ್ತು ನಿರಾಶೆಗೊಳಗಾದರು .');
INSERT INTO "kannada" VALUES(20,122,'20:122','ಅನಂತರ ಅವರ ಪ್ರಭು ಅವರನ್ನು ಪುನೀತರಾಗಿ ಆಯ್ಕೆ ಮಾಡಿದನು. ಅವರ ಪಶ್ಚಾತ್ತಾಪ ಸ್ವೀಕರಿಸಿದನು. ಅವರಿಗೆ ಸನ್ಮಾರ್ಗದರ್ಶನ ಮಾಡಿದನು.');
INSERT INTO "kannada" VALUES(20,123,'20:123','ಅಲ್ಲಾಹು ಹೇಳಿದನು, “ನೀವಿಬ್ಬರೂ (ನಿಮ್ಮ ಪರಿವಾರ ಸಮೇತ) ಇಲ್ಲಿಂದ ಇಳಿದು ಹೋಗಿರಿ. ನಿಮ್ಮಲ್ಲಿ ಕೆಲವರು ಕೆಲವರಿಗೆ ಶತ್ರುಗ ಳಾಗಿರುವಿರಿ. ಮುಂದೆ ನನ್ನ ವತಿಯಿಂದ ಮಾರ್ಗದರ್ಶನ ತಲುಪಿದರೆ, ನನ್ನ ಆ ಮಾರ್ಗ ದರ್ಶನವನ್ನು ಅನುಸರಿಸಿದವನು ದಾರಿಗೆಡಲಾ ರನು. ಪರಲೋಕದಲ್ಲಿ ಸಂಕಷ್ಟಕ್ಕೆ ಒಳಗಾಗ ಲಾರನು” .');
INSERT INTO "kannada" VALUES(20,124,'20:124','ಯಾರು ನನ್ನ ಉದ್ಬೋಧ (ಖುರ್‍ಆನ್)ದಿಂದ ವಿಮುಖನಾಗುತ್ತಾನೋ ಅವನ ಜೀವನವು ಭೂಲೋಕದಲ್ಲಿ ಸಂಕೀರ್ಣವಾಗುವುದು . ಪುನರುತ್ಥಾನದ ದಿನ ನಾವು ಅವನನ್ನು ಕುರು ಡನನ್ನಾಗಿ ಮಾಡಿ ಎಬ್ಬಿಸುತ್ತೇವೆ”.');
INSERT INTO "kannada" VALUES(20,125,'20:125','ಅವನು, “ಓ ನನ್ನ ಪ್ರಭೂ, ಹಿಂದೆ ನಾನು ದೃಷ್ಟಿ ಯುಳ್ಳವನಾಗಿದ್ದೆ. ನನ್ನನ್ನು ಇಲ್ಲೇಕೆ ಕುರು ಡನಾಗಿ ಎಬ್ಬಿಸಿದೆ?” ಎಂದು ಕೇಳುವನು.');
INSERT INTO "kannada" VALUES(20,126,'20:126','ಆಗ ಅಲ್ಲಾಹು ಹೀಗೆ ಹೇಳುವನು. ``ಸಂಗತಿ ಹಾಗಿದೆ. ಆದರೆ ನಮ್ಮ ನಿದರ್ಶನಗಳು ನಿನ್ನ ಬಳಿಗೆ ಬಂದಾಗ ನೀನು ಮರೆತಿದ್ದೆ. ಹಾಗೆಯೇ (ನೀನು ಮರೆತಂತೆ) ಇಂದು ನೀನು ಮರೆಯಲ್ಪಡುತ್ತಿರುವೆ”.');
INSERT INTO "kannada" VALUES(20,127,'20:127','ಹೀಗೆ ಧಿಕ್ಕಾರದಲ್ಲಿ ಮಿತಿ ಮೀರುವ ಹಾಗೂ ತನ್ನ ಪ್ರಭುವಿನ ನಿದರ್ಶನಗಳ ಮೇಲೆ ವಿಶ್ವಾಸ ವಿರಿಸದವನಿಗೆ (ಭೂಲೋಕದಲ್ಲಿ) ನಾವು ಪ್ರತಿ ಫಲ ಕೊಡುತ್ತೇವೆ. ಪರಲೋಕದ ಶಿಕ್ಷೆಯು ಅತಿ ಕಠೋರವೂ ಅತಿ ಶಾಶ್ವತವೂ ಆಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(20,128,'20:128','ಇವರಿಗಿಂತ ಮುಂಚೆ ನಾವು ಅದೆಷ್ಟೋ ಜನಾಂ ಗಗಳನ್ನು ನಾಶಗೊಳಿಸಿದ್ದೇವೆ. ಅವರ ನಿವಾಸ ಗಳ ದಾರಿಯಾಗಿ ಇವರು ಸಂಚರಿಸುತ್ತಾರೆ. ಇದರಿಂದ ಇವರಿಗೆ ಮಾರ್ಗದರ್ಶನ ಸಿಗಲಿಲ್ಲವೇ? ಖಂಡಿತ ಬುದ್ಧಿಶಾಲಿಗಳಿಗೆ ಇದರಲ್ಲಿ ಅನೇಕ ನಿದರ್ಶನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(20,129,'20:129','ನಿನ್ನ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಮೊದಲೇ ನಿರ್ಧರಿತವಾದ ಒಂದು ವಚನ ಹಾಗೂ ನಿಗದಿ ತ ಅವಧಿಯು ಇಲ್ಲದಿರುತ್ತಿದ್ದರೆ, ವಿನಾಶ ಕಾರ್ಯವು (ಇಹದಲ್ಲೆ) ಇವರಿಗೆ ಅನಿವಾರ್ಯವಾಗುತ್ತಿತ್ತು .');
INSERT INTO "kannada" VALUES(20,130,'20:130','ಆದುದರಿಂದ (ಓ ಪೈಗಂಬರರೇ,) ಅವರ ಮಾತುಗಳ ಬಗ್ಗೆ ತಾಳ್ಮೆ ವಹಿಸಿರಿ. ಸೂರ್ಯೋ ದಯ ಹಾಗೂ ಸೂರ್ಯಾಸ್ತಮಾನಕ್ಕೆ ಮುಂಚೆ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಸ್ತುತಿಸ್ತೋತ್ರದೊಂದಿಗೆ ಅವನ ಪಾವಿತ್ರ್ಯವನ್ನು ಕೊಂಡಾಡಿರಿ. ರಾತ್ರಿ ವೇಳೆಗಳಲ್ಲಿಯೂ ಹಗಲಿನ ಅಂಚುಗಳಲ್ಲಿಯೂ ಅವನ ಪಾವಿತ್ರ್ಯವನ್ನು ಸ್ತುತಿಸಿರಿ . ಇದರಿಂದ ನೀವು ಸಂತೃಪ್ತಿ ಪಡೆಯುವಿರಿ.');
INSERT INTO "kannada" VALUES(20,131,'20:131','ಇವರ ಪೈಕಿ ಹಲವರಿಗೆ ನಾವು ಸುಖ ನೀಡಿದ ಐಹಿಕ ಬದುಕಿನ ವಿವಿಧ ಮೋಡಿಗಳತ್ತ ಕಣ್ಣೆತ್ತಿ ನೋಡದಿರಿ . ಏಕೆಂದರೆ ಅವುಗಳನ್ನು ಅವರಿಗೆ ನಾವು ಕೊಟ್ಟಿದ್ದೇವೆ. ನಿಮ್ಮ ಪ್ರಭು ನೀಡುವ ಪಾರತ್ರಿಕ ಜೀವನ ಸುಖವು ಶ್ರೇಷ್ಟವೂ ಶಾಶ್ವತವೂ ಆಗಿದೆ.');
INSERT INTO "kannada" VALUES(20,132,'20:132','ನಿಮ್ಮ ಕುಟುಂಬದವರಿಗೆ ನಮಾಝಿನ ಅಪ್ಪಣೆ ಕೊಡಿರಿ. ಅದರಲ್ಲಿ ಬರುವ ಕಷ್ಟಗಳನ್ನು ಸಹಿಸಿರಿ. ನಾವು ನಿಮ್ಮಿಂದ ಆಹಾರವನ್ನು ಕೇಳುವುದಿಲ್ಲ. ನಾವೇ ನಿಮಗೆ ಆಹಾರ ಕೊಡುತ್ತಿದ್ದೇವೆ. ಉತ್ತಮ ಪರ್ಯವಸಾನ ಭಕ್ತರಿಗಾಗಿದೆ .');
INSERT INTO "kannada" VALUES(20,133,'20:133','‘ಅವರೇಕೆ ತಮ್ಮ ರಕ್ಷಕನ ಕಡೆಯಿಂದ ಒಂದು ಅತಿಮಾನುಷ ದೃಷ್ಠಾಂತವನ್ನು ಕೊಂಡು ಬಂದಿಲ್ಲ?’ ಎಂದು ಅವರು ಪ್ರಶ್ನಿಸುತ್ತಾರೆ. ಪೂರ್ವವೇದ ಗ್ರಂಥಗಳಲ್ಲಿರುವುದನ್ನು ಪುಷ್ಟೀಕರಿಸುತ್ತಿರುವ ಖುರ್‍ಆನ್ ದೃಷ್ಟಾಂತವಾಗಿ ಅವರ ಬಳಿ ಬಂದಿದೆಯಲ್ಲ! ');
INSERT INTO "kannada" VALUES(20,134,'20:134','ಪ್ರವಾದಿಯವರ ಆಗಮನಕ್ಕೆ ಮುನ್ನ ನಾವು ಇವರನ್ನು ಶಿಕ್ಷೆಯಿಂದ ನಾಶಗೊಳಿಸುತ್ತಿದ್ದರೆ, “ಓ ನಮ್ಮ ಪ್ರಭೂ, ನೀನು ನಮ್ಮ ಬಳಿಗೆ ಓರ್ವ ಸಂದೇಶವಾಹಕನನ್ನೇಕೆ ಕಳುಹಿಸಲಿಲ್ಲ? ನಾವು ನಿಂದ್ಯರೂ ನೀಚರೂ ಆಗುವುದಕ್ಕೆ ಮುಂಚೆಯೇ ನಾವು ನಿನ್ನ ಪುರಾವೆಗಳ ಅನುಸರಣೆ ಮಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದೆವಲ್ಲ!” ಎಂದು ಇವರು ಹೇಳುತ್ತಿದ್ದರು .');
INSERT INTO "kannada" VALUES(20,135,'20:135','(ಓ ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ; ‘ಪ್ರತಿಯೊಬ್ಬನೂ ಕರ್ಮಫಲದ ನಿರೀಕ್ಷೆಯಲ್ಲೇ ಇದ್ದಾನೆ, ಆದ್ದರಿಂದ ನೀವು ನಿರೀಕ್ಷಿಸುತ್ತಲಿರಿ, ಯಾರು ನೇರವಾದ ಮಾರ್ಗದಲ್ಲಿ ನೆಲೆಗೊಂಡವರು, ಯಾರು ಸನ್ಮಾರ್ಗ ಪಡೆದವರು ಎಂದು ಸದ್ಯವೇ ನಿಮಗೆ ತಿಳಿದು ಬರುವುದು’ ');
INSERT INTO "kannada" VALUES(21,1,'21:1','ಜನರಿಗೆ ತಮ್ಮ ವಿಚಾರಣೆ ಸಮೀಪಿಸಿದೆ. ಆದರೆ ಅವರು ಅಶ್ರದ್ಧೆಯಿಂದ ವಿಮುಖರಾಗುತ್ತಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(21,2,'21:2','ಅವರು, ತಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ತಮಗೆ ಬರುವ ಯಾವುದೇ ಹೊಸ ಉಪದೇಶವನ್ನು ಕೇವಲ ಪರಿಹಾಸ್ಯ ಭಾವದಿಂದ ಕೇಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(21,3,'21:3','ಅವರ ಹೃದಯಗಳು ಪ್ರಜ್ಞಾಹೀನವಾಗಿವೆ. “ಇವನು ನಿಮ್ಮಂತೆಯೇ ಇರುವ ಓರ್ವ ಮನುಷ್ಯನಲ್ಲವೇ? ಹೀಗಿರುವಾಗ ನೀವು ಕಣ್ಣಾರೆ ಕಾಣುತ್ತ ಜಾದುವಿನ ಕಡೆಗೆ ಹೋಗುವಿರಾ?” ಎಂದು ಅಕ್ರಮಿಗಳು ಪರಸ್ಪರ ಪಿಸುಗುಟ್ಟಿದರು .');
INSERT INTO "kannada" VALUES(21,4,'21:4','ಪ್ರವಾದಿಯು ಹೇಳಿದರು; “ನನ್ನ ಪ್ರಭು ಆಕಾಶ ಮತ್ತು ಭೂಮಿಯಲ್ಲಿರುವ ಪ್ರತಿಯೊಂದು ಮಾತನ್ನೂ ಅರಿಯುತ್ತಾನೆ. ಅವನು ಸರ್ವಶ್ರುತನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.”');
INSERT INTO "kannada" VALUES(21,5,'21:5','ಆದರೆ ಅಕ್ರಮಿಗಳು; “ಇದು ಕೇವಲ ಕೆಟ್ಟ ಕನ ಸುಗಳು. ಅಲ್ಲ, ಇದನ್ನು ಇವನೇ ಹೆಣೆದಿರುವನು. ಮಾತ್ರವಲ್ಲ, ಇವನೊಬ್ಬ ಕವಿ. ಅನ್ಯಥಾ ಹಿಂದಿನವರು ನಿದರ್ಶನಗಳೊಂದಿಗೆ ಕಳುಹಿಸಲ್ಪಟ್ಟಿರು ವಂತೆ ಇವನೂ ನಮಗೆ ಒಂದು ನಿದರ್ಶನ ತರಲಿ” ಎಂದು ಹೇಳಿದರು .');
INSERT INTO "kannada" VALUES(21,6,'21:6','ಆದರೆ ಇವರಿಗಿಂತ ಮುಂಚೆ ನಾವು ನಾಶಗೊಳಿಸಿದ ಯಾವ ನಾಡೂ ನಮ್ಮ ದೃಷ್ಟಾಂತವನ್ನು ನೋಡಿ ವಿಶ್ವಾಸವಿರಿಸಲಿಲ್ಲ. ಹೀಗಿರುವಾಗ ಇವರು ವಿಶ್ವಾಸವಿರಿಸುವರೇ? ');
INSERT INTO "kannada" VALUES(21,7,'21:7','(ಪೈಗಂಬರರೇ,) ನಿಮಗಿಂತ ಮುಂಚೆ ಪುರುಷರನ್ನು ಮಾತ್ರವೇ ನಾವು ಪ್ರವಾದಿಗಳಾಗಿ ಕಳುಹಿಸಿದ್ದು. ಅವರಿಗೆ ನಾವು ಸಂದೇಶ ಕೊಡುತ್ತಿದ್ದೆವು. ಬೇಕಾದರೆ ನಿಮಗೆ ಗೊತ್ತಿಲ್ಲದಿದ್ದರೆ ಗ್ರಂಥದವರಲ್ಲಿ ಕೇಳಿರಿ.');
INSERT INTO "kannada" VALUES(21,8,'21:8','ಅವರನ್ನು (ಸಂದೇಶವಾಹಕರನ್ನು) ನಾವು ಆಹಾರ ಸೇವಿಸದಂತಹ ಶರೀರವನ್ನಾಗಿ ಮಾಡಿರಲಿಲ್ಲ. ಅವರು ಸದಾಕಾಲ ಬದುಕುವವರೂ ಆಗಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(21,9,'21:9','ನಂತರ ಅವರಿಗೆ ಮಾಡಿದ ನಮ್ಮ ವಾಗ್ದಾನವನ್ನು ನಾವು ಪೂರೈಸಿದ್ದೇವೆ. ಅವರನ್ನೂ ನಾವಿಚ್ಛಿಸಿದ ಇತರರನ್ನೂ ರಕ್ಷಿಸಿದ್ದೇವೆ. ಮೀರಿ ನಡೆದವರನ್ನು ನಾಶ ಮಾಡಿದ್ದೇವೆ .');
INSERT INTO "kannada" VALUES(21,10,'21:10','ಜನರೇ, ನಾವು ನಿಮ್ಮದೇ ಉದ್ಭೋದೆಯುಳ್ಳ ಒಂದು ಗ್ರಂಥವನ್ನು ನಿಮ್ಮ ಕಡೆಗೆ ಕಳುಹಿಸಿದ್ದೇವೆ. ನೀವು ಯೋಚಿಸುವುದಿಲ್ಲವೇ?”');
INSERT INTO "kannada" VALUES(21,11,'21:11','ಎಷ್ಟೋ ಅಕ್ರಮಿ ನಾಡುಗಳನ್ನು ನಾವು ಧ್ವಂಸ ಮಾಡಿದ್ದೇವೆ. ಅವರ ಬಳಿಕ ಬೇರೊಂದು ಹೊಸ ಜನಾಂಗವನ್ನು ನಿರ್ಮಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(21,12,'21:12','ಅವರಿಗೆ ನಮ್ಮ ಶಿಕ್ಷೆಯ ಸ್ಪರ್ಶವಾದಾಗ ಅಲ್ಲಿಂದ ಓಡ ಹತ್ತಿದರು. .');
INSERT INTO "kannada" VALUES(21,13,'21:13','(ಆಗ ಹೇಳಲಾಯಿತು:) “ಓಡಬೇಡಿರಿ, ನೀವು ಸುಖಿಸುತ್ತಿದ್ದ ಭೋಗದ ಕಡೆಗೆ ಮತ್ತು ನಿಮ್ಮ ನಿವಾಸಗಳಿಗೇ ಮರಳಿ ಹೋಗಿರಿ, ನಿಮ್ಮಲ್ಲಿ ಏನಾದರೂ ಪ್ರಶ್ನಿಸಲಾದೀತು” .');
INSERT INTO "kannada" VALUES(21,14,'21:14','ಆಗ ಅವರು, “ಅಯ್ಯೋ, ನಮ್ಮ ನಾಶವೇ! ನಿಜ ಕ್ಕೂ ನಾವು ಅಪರಾಧಿಗಳಾಗಿದ್ದೆವು” ಎಂದರು.');
INSERT INTO "kannada" VALUES(21,15,'21:15','ಹಾಗೆ ನಾವು ಅವರನ್ನು ಕೊಯ್ದು ತೆಗೆದ ಪೈರಾಗಿ, ಕಿಡಿಯಾರಿದ ಕೆಂಡದ ಬೂದಿಯಾಗಿ ಮಾಡು ವವರೆಗೂ ಅವರು ರೋದಿಸುತ್ತಲೇ ಇದ್ದರು .');
INSERT INTO "kannada" VALUES(21,16,'21:16','ಆಕಾಶವನ್ನೂ ಭೂಮಿಯನ್ನೂ ಅವುಗಳ ನಡುವೆ ಇರುವುದನ್ನೂ ಕ್ರೀಡೆಯಾಡಲು ನಾವು ಸೃಷ್ಟಿಸಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(21,17,'21:17','ನಾವು ವಿನೋದವನ್ನು ಉಂಟು ಮಾಡ ಬಯಸಿದ್ದರೆ ನಾವದನ್ನು ನಮ್ಮ ಕಡೆಯಿಂದಲೇ ಮಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದೆವು. (ಆದರೆ ಹಾಗೆ) ನಾವು ಮಾಡಲಾರೆವು .');
INSERT INTO "kannada" VALUES(21,18,'21:18','ಆದರೆ ನಾವು ಮಿಥ್ಯದ ಮೇಲೆ ಸತ್ಯದಿಂದ ಹೊಡೆಯುತ್ತೇವೆ. ಅದು ಮಿಥ್ಯದ ಮರ್ಮಕ್ಕೆ ತಾಗುತ್ತದೆ. ಆಗ ಅದು ಅಳಿದು ಹೋಗುತ್ತದೆ . (ಸತ್ಯನಿಷೇಧಿಗಳೇ,) ನೀವು ಅಲ್ಲಾಹನ ಬಗ್ಗೆ ಸೃಷ್ಟಿಸುವ ಮಾತುಗಳಿಂದ ನಿಮಗೆ ವಿನಾಶ ಕಾದಿದೆ.');
INSERT INTO "kannada" VALUES(21,19,'21:19','ಆಕಾಶಗಳಲ್ಲೂ ಭೂಮಿಯಲ್ಲೂ ಇರುವುದೆಲ್ಲವೂ ಅಲ್ಲಾಹನದ್ದು. ಅವನ ಬಳಿಯಲ್ಲಿರುವವರು (ದೇವಚರರು) ಅವನ ಆರಾಧನೆಯನ್ನು ಬಿಟ್ಟು ದರ್ಪ ತೋರುವುದಿಲ್ಲ. ಬಳಲುವುದೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(21,20,'21:20','ಹಗಲು ಇರುಳೂ ಅವನ ಕೀರ್ತನೆ ಮಾಡುತ್ತಾರೆ, ದಣಿಯುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(21,21,'21:21','ಅದಲ್ಲ, ಇವರು ಭೂಮಿಯಿಂದ ನಿರ್ಮಿಸಿದಂತಹ ದೇವರುಗಳು ಸತ್ತವರನ್ನು ಜೀವಂತ ಎಬ್ಬಿಸಬಲ್ಲರೇ?');
INSERT INTO "kannada" VALUES(21,22,'21:22','ಭೂಮಿ-ಆಕಾಶಗಳಲ್ಲಿ (ಏಕೈಕ) ಅಲ್ಲಾಹನ ಹೊರತು ಇತರ ದೇವರುಗಳೂ ಇರುತ್ತಿದ್ದರೆ ಅವೆರಡೂ ಹದಗೆಡುತ್ತಿತ್ತು . ಆದುದರಿಂದ ಅರ್ಶ್‍ನ ಪ್ರಭುವಾಗಿರುವ ಅಲ್ಲಾಹನು, ಇವರು ಹೊರಿಸುವ ಆರೋಪಗಳಿಂದ ಅದೆಷ್ಟು ಪರಿಶುದ್ಧನು!.');
INSERT INTO "kannada" VALUES(21,23,'21:23','ಅವನು ಮಾಡುವ ಕಾರ್ಯದ ಬಗ್ಗೆ ಆತನಲ್ಲಿ ಪ್ರಶ್ನಿಸಲಾಗುವುದಿಲ್ಲ. ಆದರೆ ಉಳಿದವರೆಲ್ಲರೂ ಪ್ರಶ್ನಿಸಲ್ಪಡುವರು.');
INSERT INTO "kannada" VALUES(21,24,'21:24','ಇವರು ಅವನನ್ನು ಬಿಟ್ಟು ಇತರ ದೇವರುಗಳನ್ನು ಮಾಡಿಕೊಂಡರೇ? (ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ; “ನಿಮ್ಮ ಪುರಾವೆಯನ್ನು ತನ್ನಿರಿ. ಇದೇ ನನ್ನ ಜೊತೆಯಿದ್ದವರ ಗ್ರಂಥ ಮತ್ತು ನನಗಿಂತ ಮುಂಚಿನವರ ಗ್ರಂಥ”. ಆದರೆ ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನ ವರು ನಿಜವನ್ನು ಅರಿತಿಲ್ಲ. ಆದುದರಿಂದ ಅವರು ವಿಮುಖರಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(21,25,'21:25','“ನನ್ನ ಹೊರತು ಬೇರೆ ಆರಾಧ್ಯರಿಲ್ಲ. ಆದ್ದರಿಂದ ನೀವು ನನ್ನನ್ನೇ ಆರಾಧಿಸಿರಿ” ಎಂಬ ಸಂದೇ ಶವನ್ನು ನೀಡದೆ ತಮಗೆ ಮುಂಚೆ ಯಾವುದೇ ದೂತರನ್ನು ನಾವು ಕಳುಹಿಸಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(21,26,'21:26','ಕರುಣಾಮಯನಾದ ಅಲ್ಲಾಹನು ಮಕ್ಕಳನ್ನು ಉಂಟು ಮಾಡಿರುವನೆಂದು ಇವರು ಹೇಳುತ್ತಾರೆ. ಅಲ್ಲಾಹು ಪರಮ ಪಾವನನು. ವಾಸ್ತವದಲ್ಲಿ ಅವರು (ದೇವಚರರು) ಆದರಣೀಯ ದಾಸರಾಗಿರುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(21,27,'21:27','ಅವರು ಅವನ ಮಾತಿನ ನಡುವೆ ಬಾಯಿ ಹಾಕಲಾರರು. ಅವರು ಅವನ ಅಪ್ಪಣೆ ಪ್ರಕಾರ ಮಾತ್ರಕಾರ್ಯವೆಸಗುತ್ತಾರೆ');
INSERT INTO "kannada" VALUES(21,28,'21:28','ಅವರ ಮುಂದಿರುವುದನ್ನೂ ಅವರ ಹಿಂದಿರು ವುದನ್ನೂ ಅವನು ಬಲ್ಲನು. ಅಲ್ಲಾಹು ತೃಪ್ತಿಪಟ್ಟವರಿಗಲ್ಲದೆ ಅವರು ಶಿಫಾರಸ್ಸು ಮಾಡುವುದಿಲ್ಲ. ಅವರು ಆತನ ಭಯದಿಂದ ತತ್ತರಿಸುವವರು.');
INSERT INTO "kannada" VALUES(21,29,'21:29','ಅಲ್ಲಾಹನ ಹೊರತು ನಾನೂ ಒಬ್ಬ ದೇವನೆಂದು ಅವರ ಪೈಕಿ ಯಾರಾದರೂ ಹೇಳಿದರೆ ಅವನಿಗೆ ನಾವು ನರಕಾಗ್ನಿಯ ಪ್ರತಿಫಲ ಕೊಡುವೆವು. ಅಕ್ರಮಿಗಳಿಗೆ ನಾವು ಇದೇ ರೀತಿಯ ಪ್ರತಿಫಲ ಕೊಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(21,30,'21:30','ಆಕಾಶಗಳೂ ಭೂಮಿಯೂ ಮೊದಲು ಕೂಡಿ ಕೊಂಡಿದ್ದು ಅನಂತರ ನಾವು ಅವುಗಳನ್ನು ಬೇರ್ಪಡಿಸಿದ್ದನ್ನೂ ಪ್ರತಿಯೊಂದು ಜೀವಿಯನ್ನು ನೀರಿನಿಂದ ಸೃಷ್ಟಿಸಿದ್ದನ್ನೂ ಸತ್ಯನಿಷೇಧಿಗಳು ಕಾಣಲಿಲ್ಲವೇ? ಅವರಿನ್ನೂ ವಿಶ್ವಾಸ ವಿರಿಸುವುದಿಲ್ಲವೇ? ');
INSERT INTO "kannada" VALUES(21,31,'21:31','ಭೂಮಿ ಮನುಷ್ಯರೊಂದಿಗೆ ವಾಲದಂತೆ ಅದರಲ್ಲಿ ನಾವು ಅಚಲವಾದ ಪರ್ವತಗಳನ್ನು ನಾಟಿದ್ದೇವೆ. ಅವರು ತಮ್ಮ ನಿರ್ಧಿಷ್ಟ ಗುರಿಯನ್ನು ತಲುಪುವಂತಾಗಲು ಪರ್ವತಗಳಲ್ಲಿ ವಿಶಾಲ ವಾದ ಮಾರ್ಗಗಳನ್ನು ಮಾಡಿದ್ದೇವೆ .');
INSERT INTO "kannada" VALUES(21,32,'21:32','ಆಕಾಶವನ್ನು ಒಂದು ಸುರಕ್ಷಿತವಾದ ಮೇಲು ಛಾವಣೆಯನ್ನಾಗಿ ನಾವು ಮಾಡಿದ್ದೇವೆ. ಆದರೆ ಇವರು ಅದರ ನಿದರ್ಶನಗಳಿಂದ ವಿಮುಖರಾಗುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(21,33,'21:33','ಅವನು ರಾತ್ರಿ ಹಗಲುಗಳನ್ನು ಉಂಟು ಮಾಡಿದವನು. ಸೂರ್ಯ ಚಂದ್ರನನ್ನು ಸೃಷ್ಟಿಸಿದವನು. ಎಲ್ಲವೂ ಕಕ್ಷೆಯಲ್ಲಿ ತೇಲುತ್ತಿವೆ.');
INSERT INTO "kannada" VALUES(21,34,'21:34','(ಪೈಗಂಬರರೇ,) ನಿಮಗೆ ಮುಂಚೆ ಯಾವ ಮನುಷ್ಯನಿಗೂ ನಾವು ಅಮರತ್ವವನ್ನು ನೀಡಲಿಲ್ಲ. ಹೀಗಿದ್ದು ನೀವು ಮರಣ ಹೊಂದಿದರೂ ಕೂಡಾ ಇವರೇನು ಶಾಶ್ವತರೇ? ');
INSERT INTO "kannada" VALUES(21,35,'21:35','ಪ್ರತಿಯೊಂದು ಜೀವಿಯೂ ಮರಣದ ಸವಿಯು ಣ್ಣಲಿದೆ. ನಿಮ್ಮನ್ನು ನಾವು ಕಷ್ಟ - ಸುಖಗಳಿಂದ ಪರೀಕ್ಷಿಸುತ್ತಿದ್ದೇವೆ, ಕೊನೆಗೆ ನೀವು ನಮ್ಮ ಕಡೆಗೇ ಮರಳಲಿದ್ದೀರಿ .');
INSERT INTO "kannada" VALUES(21,36,'21:36','“ನಿಮ್ಮ ದೇವರುಗಳನ್ನು ಪ್ರಸ್ತಾವಿಸುತ್ತಿರುವವನು ಇವನೋ?” ಎನ್ನುತ್ತ ಸತ್ಯನಿಷೇಧಿಗಳು ನಿಮ್ಮನ್ನು ಕಂಡಾಗ ಪರಿಹಾಸ್ಯ ಮಾಡದೇ ಬಿಡುವುದಿಲ್ಲ. ಅವರೇ ಕರುಣಾನಿಧಿಯಾದ ಅಲ್ಲಾಹನ ಉದ್ಭೋದೆಯನ್ನು ತಿರಸ್ಕರಿಸುವವರು .');
INSERT INTO "kannada" VALUES(21,37,'21:37','ಮಾನವನನ್ನು ದುಡುಕಿನಿಂದ ಸೃಷ್ಟಿಸಲಾಗಿದೆ. ನನ್ನ ನಿದರ್ಶನಗಳನ್ನು ನಿಮಗೆ ನಾನು ತೋರಿಸಿ ಕೊಡಲಿದ್ದೇನೆ. ಆದ್ದರಿಂದ ನನ್ನೊಡನೆ ದುಡುಕಬೇಡಿರಿ .');
INSERT INTO "kannada" VALUES(21,38,'21:38','“ನೀವು ಸತ್ಯವಂತರಾಗಿದ್ದರೆ ಈ ವಾಗ್ದಾನ ಯಾವಾಗ?” ಎಂದು ಇವರು ಕೇಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(21,39,'21:39','ಸತ್ಯನಿಷೇಧಿಗಳು ತಮ್ಮ ಮುಖಗಳಿಂದಾಗಲಿ ತಮ್ಮ ಬೆನ್ನುಗಳಿಂದಾಗಲಿ ನರಕಾಗ್ನಿಯನ್ನು ತಡೆದಿರಿಸಲಾಗದ ಹಾಗೂ ಅವರಿಗೆ ಸಹಾಯ ನೀಡಲಾಗದ ಸಂದರ್ಭದ ಬಗ್ಗೆ ಅರಿಯುತ್ತಿದ್ದರೆ! (ಅವರು ಹೀಗೆ ದುಡುಕುತ್ತಿರಲಿಲ್ಲ).');
INSERT INTO "kannada" VALUES(21,40,'21:40','ಆದರೆ ಆ ವಿಪತ್ತು ದಿಢೀರನೆ ಅವರ ಮೇಲೆರಗು ವುದು. ಆಗ ಅವರನ್ನು ಅದು ಕಂಗೆಡಿಸುವುದು. ಅವರಿಗೆ ಅದನ್ನು ತಡೆಯಲು ಸಾಧ್ಯವಾಗದು ಮತ್ತು ಸಮಯಾವಕಾಶವನ್ನೂ ಕೊಡಲಾಗದು.');
INSERT INTO "kannada" VALUES(21,41,'21:41','(ನಬಿಯವರೇ) ನಿಮಗಿಂತ ಮುಂಚೆಯೂ ಸಂದೇಶ ವಾಹಕರನ್ನು ಪರಿಹಾಸ್ಯ ಮಾಡಲಾ ಗಿದೆ. ಆದರೆ ಅವರನ್ನು ಗೇಲಿ ಮಾಡಿದವರಿಗೆ ಅವರು ಪರಿಹಾಸ್ಯ ಮಾಡುತ್ತಿದ್ದುದೇ ಬಂದೆರಗಿತು ಂ.');
INSERT INTO "kannada" VALUES(21,42,'21:42','(ಪೈಗಂಬರರೇ,) ಕೇಳಿರಿ, ನಿಮ್ಮನ್ನು ಹಗಲೂ ರಾತ್ರಿಯೂ ಕರುಣಾಮಯನಾದ ಅಲ್ಲಾಹನಿಂದ ರಕ್ಷಿಸುವವನು ಯಾರು? ಆದರೆ ಇವರು ತಮ್ಮ ಪ್ರಭುವಿನ ಸ್ಮರಣೆಯಿಂದ ವಿಮುಖರಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(21,43,'21:43','ನಮ್ಮ ವಿರುದ್ಧ ಇವರನ್ನು ತಡೆದು ರಕ್ಷಿಸುವ ದೇವ ರುಗಳು ಇವರಿಗಿದ್ದಾರೆಯೇನು? ಆ ದೇವರುಗಳು ಸ್ವಯಂ ತಮಗೇ ಸಹಾಯ ಮಾಡಿಕೊಳ್ಳಲಾರರು ಮತ್ತು ನಮ್ಮಿಂದ ಅವರು ಸಹಾಯ ಪಡಕೊಳ್ಳಲಾರರು.');
INSERT INTO "kannada" VALUES(21,44,'21:44','ವಾಸ್ತವದಲ್ಲಿ ನಾವು ಇವರಿಗೂ ಇವರ ಪೂರ್ವ ಜರಿಗೂ ಜೀವನ ಸವಲತ್ತುಗಳನ್ನು ಕೊಟ್ಟಿದ್ದೆವು. ಹೀಗೆ ಅವರ ಜೀವನಾವಧಿ ದೀರ್ಘವಾಯಿತು. ಆದರೆ ನಾವು ಭೂಮಿಯನ್ನು ಅದರ ವಿವಿಧ ದಿಸೆಗಳಿಂದ ಕಡಿತಗೊಳಿಸುತ್ತ ಬರುತ್ತಿರುವುದು ಇವರು ಕಾಣುವುದಿಲ್ಲವೇ? ಹೀಗಿರುವಾಗ ಇವರು ಮೇಲುಗೈ ಸಾಧಿಸುವರೇ? ');
INSERT INTO "kannada" VALUES(21,45,'21:45','ನಾನಂತು ಅಲ್ಲಾಹನ ಸಂದೇಶದ ಆಧಾರದಲ್ಲೇ ನಿಮಗೆ ಎಚ್ಚರಿಕೆ ಕೊಡುತ್ತಿದ್ದೇನೆಂದು ಇವರೊಡನೆ ಹೇಳಿರಿ. ಆದರೆ ಕಿವುಡರಿಗೆ ಎಷ್ಟು ಎಚ್ಚರಿಸಿದರೂ ಅವರು ಕರೆಯನ್ನು ಕೇಳುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(21,46,'21:46','ನಿನ್ನ ಪ್ರಭುವಿನ ಶಿಕ್ಷೆಯಿಂದ ಇವರಿಗೆ ಕಿಂಚಿತ್ತಾದರೂ ಸೋಂಕಿ ಬಿಟ್ಟರೆ. “ಅಯ್ಯೋ ನಮ್ಮ ನಾಶವೇ! ನಿಜಕ್ಕೂ ನಾವು ಅಪರಾಧಿಗಳೇ ಆಗಿದ್ದೆವು” ಎಂದು ಖಂಡಿತ ಹೇಳುವರು.');
INSERT INTO "kannada" VALUES(21,47,'21:47','ಪುನರುತ್ಥಾನ ದಿನ ನಾವು ನಿಖರವಾಗಿ ತೂಗುವ ನ್ಯಾಯ ತಕ್ಕಡಿಯನ್ನಿಟ್ಟು ಬಿಡುವೆವು. ಆಗ ಯಾರ ಮೇಲೂ ಸ್ವಲ್ಪವೂ ಅಕ್ರಮವಾಗದು. ಮನುಷ್ಯನ ಕರ್ಮವು ಸಾಸಿವೆ ಕಾಳಿನಷ್ಟಾಗಿದ್ದರೂ ನಾವು ಅದನ್ನು ಮುಂದೆ ತರುವೆವು . ಲೆಕ್ಕ ಪರಿಶೋಧಕರಾಗಿ ನಾವೇ ಸಾಕು.');
INSERT INTO "kannada" VALUES(21,48,'21:48','ಈ ಮುಂಚೆ ಮೂಸಾ ಮತ್ತು ಹಾರೂನರಿಗೆ ಸತ್ಯ-ಅಸತ್ಯಗಳ ವಿವೇಚನೆ ಹಾಗೂ ಬೆಳಕನ್ನೂ ಧರ್ಮನಿಷ್ಠರಿಗಿರುವ ಉದ್ಬೋಧನೆಯನ್ನೂ ನಾವು ನೀಡಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(21,49,'21:49','ತಮ್ಮ ಪ್ರಭುವನ್ನು ಅದೃಶ್ಯದಲ್ಲಿ ಭಯಪಡುವ ಹಾಗೂ (ವಿಚಾರಣೆಯ) ಆ ಘಳಿಗೆಯ ಬಗ್ಗೆ ಸದಾ ಭೀತರಾಗಿರುವ (ಧರ್ಮನಿಷ್ಟರಿಗಾಗಿ).');
INSERT INTO "kannada" VALUES(21,50,'21:50','ಇದು (ಖುರ್‍ಆನ್) ನಾವು ಅವತೀರ್ಣಗೊಳಿಸಿದ ಅನುಗ್ರಹೀತ ಉದ್ಬೋಧೆಯಾಗಿದೆ. ನೀವೇನು ಇದನ್ನು ನಿರಾಕರಿಸುವಿರಾ?');
INSERT INTO "kannada" VALUES(21,51,'21:51','ಅದಕ್ಕಿಂತಲೂ ಮುಂಚೆ ನಾವು ಇಬ್‍ರಾಹೀಮ ರಿಗೆ ಅವರ ಸನ್ಮಾರ್ಗವನ್ನು ದಯಪಾಲಿಸಿದ್ದೆವು ಮತ್ತು ನಾವು ಅವರನ್ನು ಚೆನ್ನಾಗಿ ಅರಿತಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(21,52,'21:52','ಅವರು ತಮ್ಮ ತಂದೆ ಹಾಗೂ ತಮ್ಮ ಜನಾಂಗ ದೊಡನೆ, “ನೀವು ಭಜನೆ ಕೂರುತ್ತಿರುವ ಈ ವಿಗ್ರಹಗಳು ಎಂತಹವು?” ಎಂದು ಕೇಳಿದ್ದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(21,53,'21:53','ಆಗ ಅವರು, ‘`ನಮ್ಮ ಪೂರ್ವಜರು ಇವುಗಳನ್ನು ಪೂಜಿಸುತ್ತಿರುವುದನ್ನು ನಾವು ಕಂಡಿದ್ದೇವೆ” ಎಂದು ಉತ್ತರಕೊಟ್ಟರು.');
INSERT INTO "kannada" VALUES(21,54,'21:54','ಇವರು, “ನೀವೂ ನಿಮ್ಮ ಪೂರ್ವಜರೂ ಸ್ಪಷ್ಟ ದುರ್ಮಾರ್ಗದಲ್ಲಿದ್ದೀರಿ” ಎಂದಾಗ');
INSERT INTO "kannada" VALUES(21,55,'21:55','ಅವರು, “ನೀನೊಂದು ಸತ್ಯ ವಿಚಾರವನ್ನು ತಂದಿದ್ದೀಯಾ? ಅಥವಾ ಸುಮ್ಮನೆ ಮೋಜು ಮಾಡುತ್ತಿರುವೆಯಾ ?” ಎಂದು ಕೇಳಿದರು.');
INSERT INTO "kannada" VALUES(21,56,'21:56','ಆಗ ಇವರು, “ಸುಮ್ಮನೆಯಲ್ಲ, ವಾಸ್ತವದಲ್ಲಿ ಭೂಮಿ-ಆಕಾಶಗಳನ್ನು ಸೃಷ್ಟಿಸಿದ ಪ್ರಭುವೇ ನಿಮ್ಮ ಪ್ರಭುವಾಗಿರುವನು. ಈ ಬಗೆಗೆ ನಾನು ನಿಮ್ಮ ಮುಂದೆ ಸಾಕ್ಷ್ಯ ವಹಿಸುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(21,57,'21:57','ಅಲ್ಲಾಹನಾಣೆ, ನೀವು ಹಿಂತಿರುಗಿ ಹೋದ ನಂತರ ನಾನು ಖಂಡಿತವಾಗಿಯೂ ನಿಮ್ಮ ವಿಗ್ರಹಗಳನ್ನು ನಾಶ ಮಾಡಲು ಪ್ರಯತ್ನಿಸುವೆನು” ಎಂದರು.');
INSERT INTO "kannada" VALUES(21,58,'21:58','ಆ ಪ್ರಕಾರ ಇವರು ಅವರನ್ನು (ವಿಗ್ರಹಗಳನ್ನು) ತುಂಡು ತುಂಡಾಗಿ ಮುರಿದು ಹಾಕಿದರು ಮತ್ತು ಅದರ ಕಡೆಗೆ ಅವರು ಮರಳಿ ಬರಲೆಂದು ಅವರಲ್ಲಿ ದೊಡ್ಡ ವಿಗ್ರಹವನ್ನು ಬಿಟ್ಟುಬಿಟ್ಟರು .');
INSERT INTO "kannada" VALUES(21,59,'21:59','ಅವರು “ನಮ್ಮ ದೇವರುಗಳಿಗೆ ಈ ಗತಿ ಮಾಡಿ ದವನಾರು? ಅವನೊಬ್ಬ ಮಹಾ ಅಕ್ರಮಿಯಾಗಿರುವನು” ಎಂದರು.');
INSERT INTO "kannada" VALUES(21,60,'21:60','ಕೆಲವರು ಹೀಗೆ ಹೇಳಿದರು; “ಇಬ್‍ರಾಹೀಮ್ ಎಂದು ಕರೆಯಲ್ಪಡುವ ಯುವಕನೊಬ್ಬ ಅವರ ಬಗ್ಗೆ ಹೀಗಳೆಯುತ್ತಿದ್ದುದನ್ನು ನಾವು ಕೇಳಿದ್ದೇವೆ’’.');
INSERT INTO "kannada" VALUES(21,61,'21:61','ಆಗ ಅವರು, “ಹಾಗಾದರೆ ಅವನನ್ನು ಹಿಡಿದು ಜನರೆದುರು ಕರೆತನ್ನಿ. ಅವರು ಸಾಕ್ಷಿ ಹೇಳುವವ ರಾಗಲಿ” ಎಂದರು.');
INSERT INTO "kannada" VALUES(21,62,'21:62','“ಓ ಇಬ್ರಾಹೀಮ್! ನಮ್ಮ ದೇವರುಗಳನ್ನು ಹೀಗೆ ಮಾಡಿರುವುದು ನೀನೋ?” ಎಂದು ಕೇಳಿದರು.');
INSERT INTO "kannada" VALUES(21,63,'21:63','ಅವರು “ಅಲ್ಲ, ಅದನ್ನು ಅವರ ಈ ಹಿರಿಯನು ಮಾಡಿರುತ್ತಾನೆ. ಅವರು ಮಾತಾಡುವುದಿದ್ದರೆ ಅವರನ್ನೇ ಕೇಳಿಕೊಳ್ಳಿರಿ” ಎಂದರು.');
INSERT INTO "kannada" VALUES(21,64,'21:64','ಆಗ ಅವರು ತಮ್ಮ ಅಂತಃಕರಣದ ಕಡೆಗೆ ಮರ ಳಿದರು. ಆಮೇಲೆ ತಮ್ಮೊಳಗೆ “ನಿಜಕ್ಕೂ ನೀವೇ ಅಕ್ರಮಿಗಳು” ಎಂದರು.');
INSERT INTO "kannada" VALUES(21,65,'21:65','ನಂತರ ಅವರನ್ನು ಪೂರ್ವಸ್ಥಿತಿಗೆ ಮರಳಿಸಲಾಯಿತು. “ಇವರು ಮಾತನಾಡುವುದಿಲ್ಲ ವೆಂದು ನಿನಗೆ ಗೊತ್ತಿದೆ.” ಎಂದರು.');
INSERT INTO "kannada" VALUES(21,66,'21:66','ಆಗ ಇಬ್‍ರಾಹೀಮ್, “ಹಾಗಾದರೆ ನೀವು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ನಿಮಗೆ ಯಾವುದೇ ಉಪಕಾರ ಮಾಡದ ಹಾಗೂ ಉಪದ್ರವಿಸದ ವಸ್ತುಗಳನ್ನು ಪೂಜಿಸುತ್ತಿರುವಿರಲ್ಲ?');
INSERT INTO "kannada" VALUES(21,67,'21:67','ಛೇ, ನಿಮಗೆ ಮತ್ತು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ನೀವು ಪೂಜಿಸುತ್ತಿರುವ ನಿಮ್ಮ ಆರಾದ್ಯ ವಸ್ತುಗಳಿಗೆ ಧಿಕ್ಕಾರವಿರಲಿ. ನೀವೇನು ಯೋಚಿ ಸುವುದಿಲ್ಲವೇ?”ಎಂದರು.');
INSERT INTO "kannada" VALUES(21,68,'21:68','ಆಗ ಅವರು “ಈತನ ಮೇಲೆ ನೀವು ಯಾವುದೇ ಕ್ರಮ ಕೈಗೊಳ್ಳುವುದಾದರೆ, ಇವನನ್ನು ಸುಟ್ಟುಹಾಕಿರಿ. ನಿಮ್ಮ ದೇವರುಗಳಿಗೆ ನೆರವಾಗಿರಿ” ಎಂದರು .');
INSERT INTO "kannada" VALUES(21,69,'21:69','ನಾವು ಹೇಳಿದೆವು; “ಓ ಅಗ್ನಿಯೇ, ಇಬ್‍ರಾ ಹೀಮ್‍ರ ಪಾಲಿಗೆ ತಣ್ಣಗಾಗು, ಸುರಕ್ಷೆಯಾಗು” .');
INSERT INTO "kannada" VALUES(21,70,'21:70','ಅವರು ಇಬ್‍ರಾಹೀಮರಿಗೆ ಕೇಡು ಮಾಡಲು ಬಯಸಿದ್ದರು. ಆದರೆ ಅವರನ್ನು ಅತ್ಯಂತ ನಷ್ಟ ವಂತರನ್ನಾಗಿ ನಾವು ಮಾಡಿದೆವು.');
INSERT INTO "kannada" VALUES(21,71,'21:71','ಅವರನ್ನೂ ಲೂಥರನ್ನೂ ಲೋಕವಾಸಿಗಳಿಗೆ ನಾವು ಸಮೃದ್ಧಿಯನ್ನಿರಿಸಿದ ಪ್ರದೇಶಕ್ಕೆ ಕಳುಹಿಸಿ ನಾವು ರಕ್ಷಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(21,72,'21:72','ಅವರಿಗೆ ನಾವು ಇಸ್‍ಹಾಖರನ್ನು, ಹೆಚ್ಚುವರಿಯಾಗಿ ಯಅïಖೂಬರನ್ನೂ ದಾನ ನೀಡಿದೆವು ಮತ್ತು ಅವರನ್ನೆಲ್ಲ ಸಜ್ಜನರಾಗಿ ಮಾಡಿದೆವು.');
INSERT INTO "kannada" VALUES(21,73,'21:73','ಅವರನ್ನು ನಾವು ನಮ್ಮ ಅಪ್ಪಣೆಯಂತೆ ಮಾರ್ಗ ದರ್ಶನ ನೀಡುವ ನಾಯಕರನ್ನಾಗಿ ಮಾಡಿದೆವು . ಸತ್ಕರ್ಮವೆಸಗಲು, ನಮಾಝ್ ಸಂಸ್ಥಾಪಿಸಲು ಮತ್ತು ಝಕಾತ್ ನೀಡಲು ಅವರಿಗೆ ನಾವು ಸಂದೇಶ ನೀಡಿದೆವು. ಅವರು ನಮ್ಮ ಉಪಾಸಕರಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(21,74,'21:74','ಲೂಥರಿಗೆ ನಾವು ನ್ಯಾಯವಿಧಿ ಮತ್ತು ಜ್ಞಾನವನ್ನು ದಯಪಾಲಿಸಿದೆವು. ಅವರನ್ನು ಕೆಟ್ಟ ಕೆಲಸ ಮಾಡುತ್ತಿದ್ದ ಆ ನಾಡಿನಿಂದ ಪಾರು ಮಾಡಿದೆವು. ವಾಸ್ತವದಲ್ಲಿ ಅವರು ಅತ್ಯಂತ ದುಷ್ಟರಾದ ದುರ್ವೃತ್ತ ಜನಾಂಗವಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(21,75,'21:75','ಲೂಥರನ್ನು ನಾವು ನಮ್ಮ ಕೃಪೆಯಲ್ಲಿ ಪ್ರವೇಶ ಗೊಳಿಸಿದೆವು. ಅವರು ಸಜ್ಜನರಲ್ಲಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(21,76,'21:76','ನೂಹರನ್ನು ಸ್ಮರಿಸಿರಿ. ಮುಂಚೆ ಅವರು ಕೂಗಿ ಪ್ರಾರ್ಥಿಸಿದ ಸಂದರ್ಭ. ನಾವು ಅವರಿಗೆ ಉತ್ತರಿಸಿ ದೆವು. ಅವರನ್ನೂ ಅವರ ಮನೆಯವರನ್ನೂ ಮಹಾ ಸಂಕಷ್ಟದಿಂದ ಪಾರುಗೊಳಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(21,77,'21:77','ನಮ್ಮ ನಿದರ್ಶನಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದ ಜನಾಂಗದಿಂದ ಅವರನ್ನು ಪಾರುಗೊಳಿಸಿ ಸಹಾಯ ಮಾಡಿದೆವು. ಅವರು ಬಹಳ ಕೆಟ್ಟ ಜನಾಂಗವಾಗಿದ್ದರು. ಆದುದರಿಂದ ನಾವು ಅವೆರಲ್ಲರನ್ನೂ ಮುಳುಗಿಸಿ ಕೊಂದೆವು.');
INSERT INTO "kannada" VALUES(21,78,'21:78','ದಾವೂದ್ ಮತ್ತು ಸುಲೈಮಾನರನ್ನು ಸ್ಮರಿಸಿರಿ. ರಾತ್ರಿ ವೇಳೆ ಒಂದು ಜನಾಂಗದ ಆಡುಗಳು ಮೇಯ್ದು ಕೃಷಿ ನಾಶ ಮಾಡಿದ ಒಂದು ಹೊಲದ ಮೊಕದ್ದಮೆಯಲ್ಲಿ (ಅವರಿಬ್ಬರೂ) ತೀರ್ಪು ನೀಡುತ್ತಿದ್ದ ಸಂದರ್ಭ. ಅವರ ನ್ಯಾಯ ತೀರ್ಮಾನಕ್ಕೆ ನಾವು ಸಾಕ್ಷ್ಯ ವಹಿಸುತ್ತಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(21,79,'21:79','ಅದರ ಕುರಿತಾಗಿ ಸುಲೈಮಾನರಿಗೆ ನಾವು ಮನವರಿಕೆ ಮಾಡಿಕೊಟ್ಟೆವು. ನ್ಯಾಯ ವಿಧಿ ಮತ್ತು ಜ್ಞಾನವನ್ನು ನಾವು ಇಬ್ಬರಿಗೂ ದಯ ಪಾಲಿಸಿದ್ದೆವು . ನಾವು ದಾವೂದರ ಜೊತೆಗೆ ಅಲ್ಲಾಹನ ಪಾವಿತ್ರ್ಯವನ್ನು ಕೊಂಡಾಡುವ ಸ್ಥಿತಿಯಲ್ಲಿ ಪರ್ವತಗಳನ್ನೂ ಪಕ್ಷಿಗಳನ್ನೂ ಅಧೀನಗೊಳಿಸಿಕೊಟ್ಟೆವು, ಇದನ್ನು ಮಾಡಿದವರು ನಾವೇ ಆಗಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(21,80,'21:80','ನೀವು ಎದುರಿಸುವ ಯುದ್ಧದ ವಿಪತ್ತುಗಳಿಂದ ನಿಮ್ಮನ್ನು ರಕ್ಷಿಸಲಿಕ್ಕಾಗಿ ಬಳಸುವ ಎದೆಗವಚದ ನಿರ್ಮಾಣವನ್ನು ನಾವು ಅವರಿಗೆ ಕಲಿಸಿದ್ದೆವು . ಹೀಗಿರುವಾಗ ನೀವು ಕೃತಜ್ಞರಾಗುವಿರಾ?');
INSERT INTO "kannada" VALUES(21,81,'21:81','ನಾವು ಸುಲೈಮಾನರಿಗೆ ಬಲವಾಗಿ ಬೀಸುವ ಗಾಳಿಯನ್ನು ಅಧೀನಗೊಳಿಸಿಕೊಟ್ಟೆವು. ನಾವು ಅನುಗ್ರಹಿಸಿದ ಪ್ರದೇಶಕ್ಕೆ ಅದು ಅವರ ಅಪ್ಪಣೆ ಪ್ರಕಾರ ಚಲಿಸುತ್ತಿತ್ತು. ನಾವು ಸಕಲ ವಸ್ತುಗಳ ಜ್ಞಾನವುಳ್ಳವರಾಗಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(21,82,'21:82','ಶೈತಾನರ ಪೈಕಿ ಅವರಿಗಾಗಿ ಮುಳುಗೇಳುವ ಕೆಲವರನ್ನು ನಾವು ಅವರ ನಿಯಂತ್ರಣಕ್ಕೆ ತಂದು ಕೊಟ್ಟಿದ್ದೆವು. ಇದಕ್ಕೆ ಹೊರತಾಗಿ ಇತರ ಕೆಲಸಗಳನ್ನೂ ಅವರು ಮಾಡುತ್ತಿದ್ದರು. ಇವರೆಲ್ಲರ ಸಂರಕ್ಷಕರು ನಾವೇ ಆಗಿದ್ದೆವು .');
INSERT INTO "kannada" VALUES(21,83,'21:83','ಅಯ್ಯೂಬರನ್ನು ಸ್ಮರಿಸಿರಿ. ಅವರು, “ನನಗೆ ರೋಗ ಹಿಡಿದಿದೆ, ನೀನು ಕರುಣಾಳುಗಳಲ್ಲಿ ಅತ್ಯಂತ ಕರುಣಾಳು ಆಗಿರುವಿ” ಎಂದು ತಮ್ಮ ಪ್ರಭುವನ್ನು ಕೂಗಿ ಕರೆದ ಸಂದರ್ಭ .');
INSERT INTO "kannada" VALUES(21,84,'21:84','ಆಗ ನಾವು ಅವರಿಗೆ ಉತ್ತರಕೊಟ್ಟೆವು. ಅವರಿಗೆ ಬಾಧಿಸಿದ ಸಂಕಷ್ಟವನ್ನು ದೂರೀಕರಿಸಿದೆವು. ಅವರಿಗೆ ಸ್ವಂತ ಮನೆಯವರನ್ನೂ ಅವರೊಂದಿಗೆ ಅಷ್ಟೇ ಮಂದಿ ಬೇರೆಯವರನ್ನೂ ಕೊಟ್ಟೆವು. ನಮ್ಮ ಬಳಿಯಿಂದ ವಿಶೇಷ ಕಾರುಣ್ಯವೂ ಆರಾಧಕರಿಗೆ ಒಂದು ಸ್ಮರಣೆಯೂ ಆಗಲೆಂದು.');
INSERT INTO "kannada" VALUES(21,85,'21:85','ಇಸ್ಮಾಈಲ್, ಇದ್ರೀಸ್ ಮತ್ತು ದ್ಸುಲ್‍ಕಿಫ್ಲ್‍ರನ್ನೂ ಸ್ಮರಿಸಿರಿ. ಇವರೆಲ್ಲರೂ ಸಹನಶೀಲರಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(21,86,'21:86','ಇವರನ್ನು ನಾವು ನಮ್ಮ ಕೃಪೆಯಲ್ಲಿ ಸೇರಿಸಿಕೊಂಡೆವು. ಖಂಡಿತ ಅವರು ಸಜ್ಜನರಲ್ಲಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(21,87,'21:87','ಮತ್ಸ್ಯದವರನ್ನು (ಪ್ರವಾದಿ ಯೂನುಸ್) ಸ್ಮರಿಸಿರಿ. ಅವರು ಕೋಪಗೊಂಡು ಹೊರಟು ಹೋಗಿದ್ದ ಸಂದರ್ಭ. ಅವರನ್ನು ನಾವು ಇಕ್ಕಟ್ಟಿಗೆ ಸಿಲುಕಿಸಲಾರೆವೆಂದು ಅವರು ಭಾವಿಸಿದ್ದರು . ಕೊನೆಗೆ ಅವರು ಅಂಧಕಾರದೊಳಗಿಂದ ಕೂಗಿ ಪ್ರಾರ್ಥಿಸಿದರು, “(ಪ್ರಭು)-ನಿನ್ನ ಹೊರತು ಅನ್ಯ ಆರಾಧ್ಯನಿಲ್ಲ. ನೀನು ಪರಮ ಪಾವನನು. ನಿಶ್ಚಯ, ನಾನು ಅಕ್ರಮಿಗಳ ಗಣಕ್ಕೆ ಸೇರಿ ಹೋದೆ”.');
INSERT INTO "kannada" VALUES(21,88,'21:88','ಆಗ ನಾವು ಅವರಿಗೆ ಉತ್ತರ ಕೊಟ್ಟೆವು. ಅವರನ್ನು ದುಃಖದಿಂದ ಪಾರುಗೊಳಿಸಿದೆವು. ಇದೇ ರೀತಿ ನಾವು ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನು ರಕ್ಷಿಸಿಕೊಳ್ಳುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(21,89,'21:89','`ಝಕರಿಯ್ಯಾರನ್ನು ಸ್ಮರಿಸಿರಿ. ಅವರು, ‘ನನ್ನ ಪ್ರಭೂ, ನನ್ನನ್ನು ಏಕಾಂಗಿಯಾಗಿ ಬಿಟ್ಟು ಬಿಡ ಬೇಡ. ವಾರೀಸುದಾರರ ಪೈಕಿ ನೀನೇ ಉತ್ತಮನಾಗಿರುವಿ’ ಎಂದು ತನ್ನ ಪ್ರಭುವನ್ನು ಕೂಗಿ ಕರೆದ ಸಂದರ್ಭ.');
INSERT INTO "kannada" VALUES(21,90,'21:90','ಆಗ ನಾವು ಅವರಿಗೆ ಉತ್ತರಿಸಿದೆವು. ಅವರಿಗೆ (ಪುತ್ರ) ಯಹ್ಯಾರನ್ನು ನೀಡಿದೆವು. ಅವರ ಪತ್ನಿಯನ್ನು ಗರ್ಭ ಧಾರಣೆಗೆ ಪ್ರಾಪ್ತಗೊಳಿಸಿದೆವು. ಇವರು (ಪ್ರವಾದಿ ಗಳು) ಪುಣ್ಯ ಕಾರ್ಯಗಳಲ್ಲಿ ಉತ್ಸುಕರಾಗಿದ್ದರು. ನಮ್ಮನ್ನು ಒಲವು ಹಾಗೂ ಭಯದೊಂದಿಗೆ ಪ್ರಾರ್ಥಿಸುತ್ತಿದ್ದರು. ನಮ್ಮ ಮುಂದೆ ಭಕ್ತಿಯಿಂದ ವಿನಯ ತೋರುತ್ತಿದ್ದರು');
INSERT INTO "kannada" VALUES(21,91,'21:91','ತನ್ನ ಗೌಪ್ಯವನ್ನು ಕಾಪಾಡಿದ ಆ ಸ್ತ್ರೀಯನ್ನು (ಮರ್ಯಮರನ್ನು) ಸ್ಮರಿಸಿರಿ. ನಮ್ಮ ಆತ್ಮದ (ಜಿಬ್ರೀಲ) ಮೂಲಕ ಅವಳಲ್ಲಿ ನಾವು ಊದಿದೆವು. ಅವಳನ್ನೂ ಅವಳ ಪುತ್ರನನ್ನೂ (ಈಸಾರನ್ನು) ಜಗತ್ತಿಗೆ ನಿದರ್ಶನವಾಗಿ ಮಾಡಿದೆವು .');
INSERT INTO "kannada" VALUES(21,92,'21:92','ಓ ಜನರೇ! ಇದು ನಿಮ್ಮ ಧರ್ಮ. ಏಕೈಕ ಧರ್ಮ. ನಾನು ನಿಮ್ಮ ಪ್ರಭುವಾಗಿದ್ದೇನೆ. ಆದುದರಿಂದ ನೀವು ನನಗೆ ಆರಾಧಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(21,93,'21:93','ಆದರೆ ಅವರು ಪರಸ್ಪರ ತಮ್ಮ ಕಾರ್ಯವನ್ನು ಛಿನ್ನಭಿನ್ನ ಮಾಡಿದರು . ಎಲ್ಲರೂ ನಮ್ಮ ಕಡೆಗೇ ಮರಳುವವರು.');
INSERT INTO "kannada" VALUES(21,94,'21:94','ಆದ್ದರಿಂದ ಯಾರು ಸತ್ಯವಿಶ್ವಾಸಿಯಾಗಿದ್ದು ಕೊಂಡು ಸತ್ಕರ್ಮವೆಸಗುತ್ತಾರೋ ಅವನ ಪ್ರಯತ್ನ ಫಲವನ್ನು ನಿಷೇಧಿಸಲಾಗದು. ನಾವು ಅದನ್ನು ದಾಖಲಿಸುತ್ತಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(21,95,'21:95','ನಾವು ನಾಶ ಮಾಡಿರುವ ಯಾವುದೇ ನಾಡಿನ ಜನರು ಪುನಃ ಮರಳಿ ಬರುವುದು ಸಾಧ್ಯವಿಲ್ಲ ಎಂಬುದು ಖಂಡಿತ .');
INSERT INTO "kannada" VALUES(21,96,'21:96','ಹಾಗೆ ಯಅïಜೂಜ, ಮಅïಜೂಜರು ಬಿಡು ಗಡೆ ಗೊಳಿಸಲ್ಪಡುವ ತನಕ - ಅವರು ಎಲ್ಲ ಎತ್ತರದ ಸ್ಥಳಗಳಿಂದ ನುಗ್ಗಿ ಬರುವರು.');
INSERT INTO "kannada" VALUES(21,97,'21:97','ಸತ್ಯ ವಾಗ್ದಾನ ಸಮಯ ಸನ್ನಿಹಿತವಾದರೆ, ಸತ್ಯ ನಿಷೇಧಿಗಳ ತೆರೆದ ಕಣ್ಣುಗಳು ಮೇಲಕ್ಕೆ ದಿಟ್ಟಿಸಿ ನಿಲ್ಲುವುದು. ಅವರು ಹೇಳುವರು; “ಅಯ್ಯೋ, ನಮ್ಮ ನಾಶವೇ! ನಾವು ಇದರಿಂದ ಅಲಕ್ಷ್ಯರಾಗಿದ್ದೆವು. ಅಲ್ಲ, ನಿಜವಾಗಿ ನಾವು ಅಕ್ರಮಿಗಳಾಗಿದ್ದೆವು”.');
INSERT INTO "kannada" VALUES(21,98,'21:98','ಸತ್ಯ ನಿಷೇಧಿಗಳೇ, ನೀವು ಮತ್ತು ಅಲ್ಲಾಹ ನನ್ನು ಬಿಟ್ಟು ನೀವು ಪೂಜಿಸುವ ಆರಾಧ್ಯ ವಸ್ತುಗಳು ನರಕಾಗ್ನಿಯ ಇಂಧನಗಳು, ನೀವೂ ಅದಕ್ಕೆ ಸೇರಬೇಕಾದವರು.');
INSERT INTO "kannada" VALUES(21,99,'21:99','ಅವುಗಳು ನಿಜಕ್ಕೂ ದೇವರಾಗಿರುತ್ತಿದ್ದರೆ ಅವು ನರಕಕ್ಕೆ ಹೋಗುತ್ತಿರಲಿಲ್ಲ. ಎಲ್ಲರೂ ಅದರಲ್ಲಿ ಶಾಶ್ವತರು.');
INSERT INTO "kannada" VALUES(21,100,'21:100','ಅಲ್ಲಿ ಅವರು ನಿಡುಸುಯ್ಯುವರು. ಅಲ್ಲಿ ಅವರಿಗೇನೂ ಕೇಳಿಸದು .');
INSERT INTO "kannada" VALUES(21,101,'21:101','ಯಾರಿಗೆ ಮೊದಲೇ ನಮ್ಮಿಂದ ಉತ್ತಮ ಸ್ಥಾನ ಪ್ರಾಪ್ತವಾಗಿದೆಯೋ ಅವರು ನರಕದಿಂದ ದೂರವಾಗುವರು.');
INSERT INTO "kannada" VALUES(21,102,'21:102','ಅದರ ಮೆಲು ದನಿಯನ್ನೂ ಅವರು ಕೇಳಲಾರರು. ಅವರು ತಮ್ಮ ಮನಮೆಚ್ಚುವ ಅನುಗ್ರಹ ಗಳ ನಡುವೆ ಶಾಶ್ವತವಾಗಿ ನೆಲೆಸುವರು.');
INSERT INTO "kannada" VALUES(21,103,'21:103','ಅತಿ ಭಯಂಕರ ಭಯಭೀತಿಯು ಅವರನ್ನು ವ್ಯಥೆಗೊಳಿಸದು. ಇದು ನಿಮಗೆ ವಾಗ್ದಾನ ಮಾಡಲಾಗಿದ್ದ ಅದೇ ನಿಮ್ಮ ದಿನ ಎನ್ನುತ್ತಾ ದೇವಚರರು ಅವರನ್ನು ಇದಿರ್ಗೊಳ್ಳುವರು.');
INSERT INTO "kannada" VALUES(21,104,'21:104','ಬರೆಯಲಾದ ಹೊತ್ತಗೆಯ ಹಾಳೆಗಳನ್ನು ಸುತ್ತುವ ಹಾಗೆ ನಾವು ಆಕಾಶವನ್ನು ಸುರುಟು ವ ದಿನವನ್ನು ನೆನೆಸಿರಿ. ಮೊದಲು ಸೃಷ್ಟಿಯ ಆರಂಭ ಮಾಡಿದಂತೆ ಅದನ್ನು ನಾವು ಪುನರ್ ಸೃಷ್ಟಿಸುತ್ತೇವೆ. ಇದು ನಮ್ಮ ಮೇಲಿನ ವಾಗ್ದಾನ ವಾಗಿದ್ದು ಈ ಕಾರ್ಯವನ್ನು ನಾವು ಖಂಡಿತ ಮಾಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(21,105,'21:105','ನಮ್ಮ ಸಜ್ಜನ ದಾಸರು ಭೂಮಿಯನ್ನು ವಾರೀಸು ಪಡೆಯಲಿದ್ದಾರೆಂದು ಮೂಲ ಪ್ರಮಾಣದಲ್ಲಿ ಉಲ್ಲೇಖಿಸಿದ ಬಳಿಕ ನಾವು ವೇದಗ್ರಂಥದಲ್ಲಿ ಬರೆದಿರುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(21,106,'21:106','ಇದರಲ್ಲಿ ದೇವೋಪಾಸಕರಿಗೆ ಒಂದು ದೊಡ್ಡ ಸಂದೇಶವಿದೆ');
INSERT INTO "kannada" VALUES(21,107,'21:107','(ಪೈಗಂಬರರೇ,) ನಾವು ನಿಮ್ಮನ್ನು ಸಕಲ ಜಗತ್ತಿಗೆ ಅನುಗ್ರಹವಾಗಿ ಮಾಡಿ ಕಳುಹಿಸಿರುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(21,108,'21:108','ಹೇಳಿರಿ. “ನಿಮ್ಮ ದೇವನು ಏಕೈಕ ದೇವನೆಂದು ನನ್ನ ಬಳಿಗೆ ದಿವ್ಯ ಸಂದೇಶ ಬಂದಿದೆ. ಆದ್ದ ರಿಂದ ಅವನನ್ನು ನೀವು ಅನುಸರಿಸುವಿರಾ?”');
INSERT INTO "kannada" VALUES(21,109,'21:109','ಇನ್ನು ಅವರು ವಿಮುಖರಾದರೆ ಹೇಳಿರಿ, “ನಾನು ಸರಿಯಾದ ರೀತಿಯಲ್ಲಿ ನಿಮಗೆ ಎಚ್ಚರಿಕೆ ನೀಡಿ ರುತ್ತೇನೆ. ಆದರೆ ನಿಮಗೆ ಮಾಡಲಾದ ವಾಗ್ದಾ ನವು ಹತ್ತಿರವಿದೆಯೋ ಅಥವಾ ದೂರವಿದೆಯೋ ಎಂದು ನಾನರಿಯೆನು.');
INSERT INTO "kannada" VALUES(21,110,'21:110','ನೀವು ಜೋರಾಗಿ ಹೇಳುವುದನ್ನೂ ಅವನು ಅರಿಯುತ್ತಾನೆ. ಗುಟ್ಟಾಗಿ ಹೇಳುವುದನ್ನೂ ಅವನು ಅರಿಯುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(21,111,'21:111','ನಾನು ತಿಳಿದಿಲ್ಲ. ಪ್ರಾಯಶಃ ಈ ವಿಳಂಬವು ನಿಮಗೊಂದು ಪರೀಕ್ಷೆಯಾಗಿದೆ ಮತ್ತು ನಿಮಗೆ ಒಂದು ನಿರ್ದಿಷ್ಟ ಕಾಲದವರೆಗೆ ಸುಖಿಸುವ ಅವಕಾಶ ಕೊಡಲಾಗುತ್ತಿದೆ” .');
INSERT INTO "kannada" VALUES(21,112,'21:112','ಸಂದೇಶವಾಹಕರು ಹೇಳಿದರು, “ಓ ನನ್ನ ಪ್ರಭೂ, ಸತ್ಯವಾದ ನೆಲೆಯಲ್ಲಿ ತೀರ್ಪು ಕಲ್ಪಿಸು . ದಯಾಮಯನಾದ ಅಲ್ಲಾಹನೇ ನಮ್ಮ ಪ್ರಭು. ನೀವು (ಸತ್ಯ ನಿಷೇಧಿಗಳು) ವರ್ಣಿಸುತ್ತಿರುವ ಮಾತುಗಳ ವಿರುದ್ಧ ಅವನು ಸಹಾಯ ಕೇಳಲ್ಪಡಬೇಕಾದವನು’’');
INSERT INTO "kannada" VALUES(22,1,'22:1','ಓ ಜನರೇ, ನಿಮ್ಮ ಪ್ರಭುವಿನ ಶಿಕ್ಷೆಯನ್ನು ಭಯಪಡಿರಿ. ನಿಜಕ್ಕೂ ಅಂತ್ಯ ದಿನದ ಪ್ರಕಂಪನವು ಭಯಂಕರ ವಿಷಯವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(22,2,'22:2','ನೀವು ಅದನ್ನು ಕಾಣುವ ದಿನ ಮೊಲೆ ಹಾಲುಣಿ ಸುವ ಎಲ್ಲ ಸ್ತ್ರೀಯೂ ಮೊಲೆ ಕುಡಿಯುವ ತನ್ನ ಶಿಶುವನ್ನು ಮರೆತು ಬಿಡುವಳು. ಗರ್ಭ ಹೊತ್ತಿರು ವುದೆಲ್ಲವೂ ತನ್ನ ಗರ್ಭವನ್ನು ಹೆತ್ತು ಬಿಡುವುದು. ಜನರನ್ನು ನೀವು ಉನ್ಮತ್ತರಾಗಿ ಕಾಣುವಿರಿ. ನಿಜವಾಗಿ ಅವರು ಉನ್ಮತ್ತರಲ್ಲ. ಆದರೆ ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆಯು ಕಠಿಣವಾಗಿರುವುದು.');
INSERT INTO "kannada" VALUES(22,3,'22:3','ಜನರಲ್ಲಿ ಕೆಲವರು ಸರಿಯಾದ ಜ್ಞಾನವಿಲ್ಲದೆ ಅಲ್ಲಾಹನ ಬಗ್ಗೆ ವಾದ-ವಿವಾದ ನಡೆಸುತ್ತಾರೆ. ಎಲ್ಲ ದಿಕ್ಕಾರಿ ಶೈತಾನರನ್ನು ಅನುಸರಿಸುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(22,4,'22:4','ಯಾರಾದರೂ ಅವನನ್ನು ತನ್ನ ಮಿತ್ರನಾಗಿ ಮಾಡಿ ಕೊಂಡರೆ ಅವನನ್ನು ಆತನು ದಾರಿ ತಪ್ಪಿಸಿ ಬಿಡುವನು ಮತ್ತು ಹೊತ್ತಿ ಉರಿಯುವ ನರಕದ ಶಿಕ್ಷೆಯತ್ತ ದಾರಿ ತೋರಿಸುವನು ಎಂದು ಶೈತಾನನ ವಿಧಿಯಲ್ಲಿ ಬರೆದಿದೆ .');
INSERT INTO "kannada" VALUES(22,5,'22:5','ಜನರೇ, ನಿಮಗೆ ಪುನರುತ್ಥಾನದ ಬಗ್ಗೆ ಸಂಶಯ ವಿದ್ದರೆ, ಇದೋ ನೋಡಿರಿ, ನಾವು ನಿಮ್ಮನ್ನು ಮಣ್ಣಿನಿಂದಲೂ, ಅನಂತರ ವೀರ್ಯದಿಂದಲೂ ಆ ಮೇಲೆ ರಕ್ತ ಪಿಂಡದಿಂದಲೂ, ಆ ಬಳಿಕ ಪೂರ್ಣ ಹಾಗೂ ಅಪೂರ್ಣ ಮಾಂಸ ಪಿಂಡದಿಂದಲೂ ಸೃಷ್ಟಿ ಸಿದ್ದೇವೆ. ನಿಮಗೆ ನಮ್ಮ ಸಾಮಥ್ರ್ಯದ ಸಂಪೂರ್ಣ ತೆಯನ್ನು ಸ್ಪಷ್ಟಪಡಿಸಿಕೊಡಲಿಕ್ಕಾಗಿ (ನಾವು ಇದನ್ನು ತಿಳಿಸುತ್ತಿದ್ದೇವೆ). ನಾವು ಉದ್ದೇಶಿಸು ವುದನ್ನು (ವೀರ್ಯವನ್ನು) ಒಂದು ನಿರ್ದಿಷ್ಟ ಅವಧಿ ಯ ತನಕ ಗರ್ಭಾಶಯದಲ್ಲಿ ತಡೆದಿರಿಸುತ್ತೇವೆ. ಅನಂತರ ನಿಮ್ಮನ್ನು ಒಂದು ಶಿಶುವಾಗಿ ಹೊರ ತರುತ್ತೇವೆ. (ನಂತರ) ನೀವು ಯೌವ್ವನದ ಪೂರ್ಣ ದೆಸೆಗೆ ತಲುಪಲಿಕ್ಕಾಗಿ (ನಿಮ್ಮನ್ನು ಬೆಳೆಸಿಕೊಂಡು ಬರುತ್ತೇವೆ). ನಿಮ್ಮಲ್ಲಿ ಕೆಲವರು ಮೊದಲೇ ಅಸು ನೀಗುತ್ತಾರೆ. ಇನ್ನು ಕೆಲವರನ್ನು ಅರಿವಿನ ಬಳಿಕ ಏನೂ ತಿಳಿಯದಂತೆ ವಾರ್ದಕ್ಯದ ಅತಿ ಕೆಳಹಂತಕ್ಕೆ ತಿರುಗಿಸಿ ಬಿಡಲಾಗುತ್ತದೆ. ಭೂಮಿ ಒಣಗಿರುವುದನ್ನು ನೀವು ನೋಡುತ್ತೀರಿ. ಅನಂತರ ನಾವು ಅದರ ಮೇಲೆ ಮಳೆಗೆರೆದಾಗ ಅದು ಒಮ್ಮೆ ಲೇ ನಲಿದು ಮೊಳೆತು ಎಲ್ಲ ಬಗೆಯ ಸೊಗಸಾದ ಸಸ್ಯಗಳನ್ನು ಹೊರಗೆಡಹುವುದು.');
INSERT INTO "kannada" VALUES(22,6,'22:6','ಇದು ಏಕೆಂದರೆ, ಅಲ್ಲಾಹನೇ ಪರಮ ಸತ್ಯ. ಅವನು ಮೃತರನ್ನು ಜೀವಂತಗೊಳಿಸುತ್ತಾನೆ ಮತ್ತು ಸರ್ವ ವಸ್ತುವಿನ ಮೇಲೆ ಸಮರ್ಥನು ಎಂಬುದಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(22,7,'22:7','ಅಲ್ಲದೆ, ಅಂತಿಮ ಘಳಿಗೆಯು ಬಂದೇ ತೀರು ವುದು. ಇದರಲ್ಲಿ ಯಾವುದೇ ಸಂಶಯವಿಲ್ಲ. ಅಲ್ಲಾ ಹನು, ಸಮಾಧಿಗಳ ಒಳಗಿದ್ದವರನ್ನು ಖಂಡಿತ ಎಬ್ಬಿಸುವನು.');
INSERT INTO "kannada" VALUES(22,8,'22:8','ಯಾವುದೇ ಜ್ಞಾನ, ಸನ್ಮಾರ್ಗದರ್ಶನ ಮತ್ತು ಬೆಳಕು ನೀಡುವ ಗ್ರಂಥವಿಲ್ಲದೆ ಅಲ್ಲಾಹನ ವಿಷಯದಲ್ಲಿ ಜಗಳಾಡುವ ಕೆಲವು ಮನುಷ್ಯರಿದ್ದಾರೆ .');
INSERT INTO "kannada" VALUES(22,9,'22:9','(ದರ್ಪದಿಂದ) ಕೊರಳನ್ನು ಸೆಟೆಯುತ್ತಾ ಜನರನ್ನು ದೇವಮಾರ್ಗದಿಂದ ತಪ್ಪಿಸಲಿಕ್ಕಾಗಿ (ಅವರು ಹೀಗೆ ಮಾಡುತ್ತಾರೆ.) ಅಂಥವನಿಗೆ ಈ ಲೋಕದಲ್ಲಿ ಅಪಮಾನವಿದೆ. ಪುನರುತ್ಥಾನದ ದಿನ ನಾವು ಅವನಿಗೆ ಸುಟ್ಟು ಹಾಕುವ ನರಕಾಗ್ನಿ ಯ ಶಿಕ್ಷೆಯ ಸವಿಯನ್ನು ಉಣಿಸುವೆವು.');
INSERT INTO "kannada" VALUES(22,10,'22:10','ನಿನ್ನ ಸ್ವಂತ ಕೈಗಳು ನಿನಗಾಗಿ ಮೊದಲೇ ಮಾಡಿ ಟ್ಟ ಕರ್ಮದ ಫಲವಿದು. ಅನ್ಯಥಾ ಅಲ್ಲಾಹನು, ತನ್ನ ದಾಸರ ಮೇಲೆ ಅಕ್ರಮವೆಸಗುವವನಲ್ಲ.');
INSERT INTO "kannada" VALUES(22,11,'22:11','ಅಂಚಿನಲ್ಲಿ ನಿಂತುಕೊಂಡು ಧರ್ಮದಲ್ಲಿ ದೃಢ ವಿಶ್ವಾಸವಿಲ್ಲದೆ ಅಲ್ಲಾಹನ ಆರಾಧನೆ ಮಾಡುವ ವನೂ ಜನರಲ್ಲಿದ್ದಾನೆ . ಒಳಿತಾದರೆ ಅದರಿಂದ ಶಾಂತನಾಗುತ್ತಾನೆ. ಏನಾದರೂ ಸಂಕಷ್ಟ ಬಂದರೆ ಹಿಂದಕ್ಕೆ ತಿರುಗಿ ಬೀಳುತ್ತಾನೆ. ಅವನ ಪಾಲಿಗೆ ಇಹಲೋಕವೂ, ಪರಲೋಕವೂ ನಷ್ಟ ವಾಯಿತು. ಇದುವೇ ಸುಸ್ಪಷ್ಟ ನಷ್ಟ.');
INSERT INTO "kannada" VALUES(22,12,'22:12','ಅವನು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಅವನಿಗೆ ಯಾವುದೇ ಹಾನಿ ಮಾಡದ, ಯಾವುದೇ ಫಲ ನೀಡದ ವಸ್ತುವನ್ನು ಪೂಜಿಸುತ್ತಾನೆ. ಇದುವೇ ಸತ್ಯಕ್ಕೆ ದೂರವಾದ ದುರ್ಮಾರ್ಗ.');
INSERT INTO "kannada" VALUES(22,13,'22:13','ಯಾರ ಹಾನಿಯು ಅವರ ಲಾಭಕ್ಕಿಂತ ಹತ್ತಿರವಿದೆಯೋ ಅವರನ್ನು ಅವನು ಆರಾಧಿಸುತ್ತಾನೆ . ಅವರೆಷ್ಟು ನಿಕೃಷ್ಟ ರಕ್ಷಕರು. ಮತ್ತು ಎಷ್ಟು ನಿಕೃಷ್ಟ ಮಿತ್ರರು');
INSERT INTO "kannada" VALUES(22,14,'22:14','ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿ ಸತ್ಕರ್ಮವೆಸಗಿದವರನ್ನು ಅಲ್ಲಾಹನು ತಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆಗಳು ಹರಿ ಯುತ್ತಿರುವ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ ಪ್ರವೇಶ ಗೊಳಿಸುವನು. ಅಲ್ಲಾಹು ತಾನಿಚ್ಛಿಸಿದ್ದನ್ನು ಮಾಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(22,15,'22:15','ಅಲ್ಲಾಹನು, ಇಹ-ಪರಗಳಲ್ಲಿ ಅವರಿಗೆ (ಪ್ರವಾದಿವರ್ಯರಿಗೆ) ಸಹಾಯ ಮಾಡಲಾರನೆಂದು ಯಾರಾದರೂ ಭಾವಿಸಿದ್ದರೆ, ಅವನು ಆಕಾಶಕ್ಕೆ ಒಂದು ಹಗ್ಗವನ್ನು ಬಿಗಿಯಲಿ. ಆಮೇಲೆ ನೇಣು ಬಿಗಿದು ಕೊರಳು ಕೊಯ್ಯಲಿ . ಅನಂತರ ಅವನ ತಂತ್ರವು ತನ್ನನ್ನು ಕೋಪಿಸುವ ವಿಷಯವನ್ನು ರದ್ದುಗೊಳಿಸುತ್ತದೋ ಎಂದು ನೋಡಿಕೊಳ್ಳಲಿ.');
INSERT INTO "kannada" VALUES(22,16,'22:16','ಆ ಪ್ರಕಾರ ನಾವು ಈ ಖುರ್‍ಆನನ್ನು ಸುಸ್ಪಷ್ಟವಾದ ಪುರಾವೆಗಳೆಂಬ ನೆಲೆಯಲ್ಲಿ ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದೇವೆ. ಅಲ್ಲಾಹನು ತಾನಿಚ್ಚಿಸಿದವರನ್ನು ನೇರ ದಾರಿಗೆ ಸೇರಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(22,17,'22:17','ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿದವರು, ಯಹೂದಿಗಳಾದ ವರು, ಸಾಬಿಈಗಳು, ಕ್ರೈಸ್ತರು, ಅಗ್ನಿ ಆರಾಧ ಕರು ಮತ್ತು ಬಹುದೇವಾರಾಧಕರು ಇವರೆಲ್ಲರ ನಡುವೆ ಅಲ್ಲಾಹನು ಪುನರುತ್ಥಾನದ ದಿನ ತೀರ್ಪು ಕಲ್ಪಿಸುವನು. ಪ್ರತಿಯೊಂದು ವಿಷಯಕ್ಕೂ ಅಲ್ಲಾಹನು ಸಾಕ್ಷಿಯಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(22,18,'22:18','ಆಕಾಶಗಳಲ್ಲಿರುವವರು, ಭೂಮಿಯಲ್ಲಿರುವವರು, ಸೂರ್ಯ, ಚಂದ್ರ, ನಕ್ಷತ್ರಗಳು, ಪರ್ವತಗಳು, ಮರಗಳು, ಜೀವಜಂತುಗಳು ಮತ್ತು ಬಹುತೇಕ ಮನುಷ್ಯರು ಅಲ್ಲಾಹನಿಗೆ ಸಾಷ್ಟಾಂಗವೆರಗುತ್ತಿರು ವುದನ್ನು ನೀವು ನೋಡುತ್ತಿಲ್ಲವೇ? ಅನೇಕ ಜನರಿಗೆ ನಮ್ಮ ಶಿಕ್ಷೆಯು ಬಾದಿಸಿದೆ. ಅಲ್ಲಾಹು ಯಾರನ್ನು ನಿಂದಿಸಿಬಿಡುವನೋ ಅವನನ್ನು ಯಾರೂ ಗೌರವಿಸುವವರಿಲ್ಲ. ಅಲ್ಲಾಹನು ತಾನಿಚ್ಛಿಸಿದ್ದನ್ನು ಮಾಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(22,19,'22:19','ತಮ್ಮ ಪ್ರಭುವಿನ ವಿಷಯದಲ್ಲಿ ಪರಸ್ಪರ ಕಚ್ಚಾಡು ತ್ತಿರುವ ಎರಡು ಭಿನ್ನ ಪಂಗಡಗಳಿವು . ಇವರಲ್ಲಿ ಸತ್ಯವಿಷೇಧಿಗಳಿಗೆ ನರಕಾಗ್ನಿಯ ಉಡುಪನ್ನು ಅಳೆದು ಕತ್ತರಿಸಲಾಗಿದೆ. ಅವರ ತಲೆಗಳ ಮೇಲೆ ಕುದಿಯುವ ನೀರನ್ನು ಸುರಿಸಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(22,20,'22:20','ಅದರಿಂದ ಅವರ ಹೊಟ್ಟೆಯೊಳಗಿರುವ ಭಾಗಗಳನ್ನು ಕರಗಿಸಲಾಗುತ್ತವೆ. ಚರ್ಮಗಳನ್ನು ಸುಡಲಾಗುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(22,21,'22:21','ಅವರಿಗೆ (ತಲೆಗೆ ಬಡಿಯಲು) ಕಬ್ಬಿಣದ ಗದೆಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(22,22,'22:22','ಅವರು ದುಃಖದ ನಿಮಿತ್ತ ನರಕದಿಂದ ಹೊರ ಬರಲು ಪ್ರಯತ್ನಿಸಿದಾಗಲೆಲ್ಲ ‘ಸುಡುವ ಶಿಕ್ಷೆಯ ರುಚಿಯನ್ನು ಸವಿಯಿರಿ’ ಎನ್ನುತ್ತಾ ಪುನಃ ಅದರೊಳಗೇ ಮರಳಿಸಲ್ಪಡುವರು .');
INSERT INTO "kannada" VALUES(22,23,'22:23','ಅಲ್ಲಾಹನು ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿದವರನ್ನು ಮತ್ತು ಸತ್ಕರ್ಮವೆಸಗಿದವರನ್ನು ಉದ್ಯಾನಗಳಲ್ಲಿ ಪ್ರವೇ ಶಗೊಳಿಸುವನು. ತಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆಗಳು ಹರಿಯುತ್ತಿವೆ. ಅಲ್ಲಿ ಅವರಿಗೆ ಚಿನ್ನ ಹಾಗೂ ಮುತ್ತುಗಳ ಕಂಕಣಗಳನ್ನು ತೊಡಿಸಲಾಗುವುದು. ಅಲ್ಲಿ ಅವರ ಉಡುಪು ರೇಶ್ಮೆಯದ್ದಾಗಿರುವುದು .');
INSERT INTO "kannada" VALUES(22,24,'22:24','ಅವರನ್ನು ಪವಿತ್ರ ವಾಕ್ಯದ ಕಡೆಗೆ ಕೊಂಡೊಯ್ಯಲ್ಪಟ್ಟಿತ್ತು. ಮತ್ತು ಅವರು ಸ್ತುತ್ಯರ್ಹ ಮಾರ್ಗಕ್ಕೆ ಹಚ್ಚಲ್ಪಟ್ಟಿದ್ದರು.');
INSERT INTO "kannada" VALUES(22,25,'22:25','ಸತ್ಯನಿಷೇಧ ಕೈಗೊಂಡವರು, ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ ತಡೆಯುವವರು ಮತ್ತು ಸ್ಥಳೀಯರಿರಲಿ, ಹೊರಗಿನಿಂದ ಬಂದವರಿರಲಿ, ಎಲ್ಲರಿಗೂ ನಾವು ಸಮಾನ ಹಕ್ಕುಗಳನ್ನು ನೀಡಿದ ಮಸ್ಜಿದುಲ್ ಹರಾಮ್‍ನ ಸಂದರ್ಶನದಿಂದ ಜನರನ್ನು ತಡೆ ಯುತ್ತಿರುವವರು ಶಿಕ್ಷಾರ್ಹರು . ಅಲ್ಲಿ (ಮಸ್ಜಿ ದುಲ್ ಹರಾಮ್‍ನಲ್ಲಿ) ಯಾರಾದರೂ ಅಕ್ರಮ ವಾಗಿ ಅತಿರೇಕವನ್ನು ಬಯಸಿದರೆ, ಅವನಿಗೆ ನಾವು ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆಯ ರುಚಿಯನ್ನು ಉಣ್ಣಿಸುವೆವು.');
INSERT INTO "kannada" VALUES(22,26,'22:26','ಇಬ್‍ರಾಹೀಮ್ ರಿಗೆ ಕಅïಬಾ ಭವನದ ಸ್ಥಳವನ್ನು ನಾವು ಗೊತ್ತುಪಡಿಸಿ ಕೊಟ್ಟ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ. “ನನ್ನ ಜೊತೆ ಯಾವುದೇ ವಸ್ತುವನ್ನೂ ಸಹಭಾಗಿಯಾಗಿ ಮಾಡದಿರಿ. ಪ್ರದಕ್ಷಿಣೆ ಮಾಡುವವರಿಗಾಗಿಯೂ (ನಮಾಝಿಗೆ) ನಿಲ್ಲುವವರಿಗಾಗಿಯೂ ರುಕೂಅï- ಸುಜೂದ್ ಮಾಡುವವರಿಗಾಗಿಯೂ ನನ್ನ ಮಂದಿರವನ್ನು ಶುಚಿಯಾಗಿಡಿರಿ.');
INSERT INTO "kannada" VALUES(22,27,'22:27','ಹಜ್ಜ್‍ಗಾಗಿ ಜನರಿಗೆ ಕರೆ ನೀಡಿರಿ . ಅವರು ಕಾಲ್ನಡಿಗೆಯಾಗಿಯೂ ದೂರದ ದಾರಿಗಳಿಂದ ಬಳಲಿದ ಒಂಟೆಗಳಲ್ಲಿ ಸವಾರಿ ಮಾಡಿಕೊಂಡೂ ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವಂತಾಗಲಿ”.');
INSERT INTO "kannada" VALUES(22,28,'22:28','ಅವರಿಗೆ ಪ್ರಯೋಜನಕಾರಿಯಾದ ಕೆಲವು ರಂಗಗಳಲ್ಲಿ ಸನ್ನಿಹಿತರಾಗಲು ಮತ್ತು ಅಲ್ಲಾಹನು ಅವರಿಗೆ ದಯಪಾಲಿಸಿರುವ ಜಾನುವಾರು ಮೃಗಗಳ ಮೇಲೆ ನಿಗದಿತ ದಿನಗಳಲ್ಲಿ ಅಲ್ಲಾಹನ ನಾಮವನ್ನು ಉಚ್ಛರಿಸಲಿಕ್ಕಾಗಿ. ಆದ್ದರಿಂದ ಆ ಜಾನುವಾರು (ಮಾಂಸ)ಗಳಿಂದ ನೀವು ತಿನ್ನಿರಿ ಮತ್ತು ಕಡು ಬಡವರಿಗೆ ತಿನ್ನಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(22,29,'22:29','ಆ ಬಳಿಕ ಅವರು ತಮ್ಮ ಕಶ್ಮಲಗಳನ್ನು ಹೋಗ ಲಾಡಿಸಲಿ. ತಮ್ಮ ಹರಕೆಗಳನ್ನು ಪೂರೈಸಲಿ ಮತ್ತು ಪುರಾತನ ಕಅïಬಾ ಭವನಕ್ಕೆ ಪ್ರದಕ್ಷಿಣೆ ಬರಲಿ .');
INSERT INTO "kannada" VALUES(22,30,'22:30','ಇವಿಷ್ಟು ಹಜ್ಜ್‍ನ ವಿಷಯ. ಯಾರು ಅಲ್ಲಾಹನು ಪಾವನಗೊಳಿಸಿದವುಗಳನ್ನು ಗೌರವಿಸುತ್ತಾನೋ ಅದು ಅವನ ಪ್ರಭುವಿನ ಬಳಿ ಅವನಿಗೇ ಉತ್ತಮ ವಾಗಿರುವುದು . ಜಾನುವಾರುಗಳನ್ನು ನಿಮಗೆ ಧರ್ಮ ಸಮ್ಮತಗೊಳಿಸಲಾಗಿದೆ. ಆದರೆ ನಿಮಗೆ ಓದಿ ಹೇಳಲಾದ ಕೆಲವು ಪ್ರಾಣಿಗಳ ಹೊರತು . ಆದ್ದರಿಂದ ವಿಗ್ರಹಗಳೆಂಬ ಮಾಲಿನ್ಯವನ್ನು ವರ್ಜಿಸಿರಿ. ಅಸತ್ಯ ಮಾತುಗಳನ್ನು ತೊರೆಯಿರಿ.');
INSERT INTO "kannada" VALUES(22,31,'22:31','ಅಲ್ಲಾಹನಿಗೆ ಸಹಭಾಗಿತ್ವವನ್ನು ಕಲ್ಪಿಸದೆ, ಅವನಿಗೆ ಏಕಧರ್ಮನಿಷ್ಠರಾಗಿ. ಯಾರು, ಅಲ್ಲಾಹ ನಿಗೆ ಸಹಭಾಗಿತ್ವವನ್ನು ಕಲ್ಪಿಸುತ್ತಾನೋ ಅವನು ಆಕಾಶದಿಂದ ಕೆಳಗೆ ಬಿದ್ದವನಂತಿದ್ದು ಅವನನ್ನು ಪಕ್ಷಿಗಳು ಕಿತ್ತುಕೊಂಡು ಹೋಗುವಂತೆ ಅಥವಾ ಗಾಳಿಯು ಅವನನ್ನು ದೂರ ಸ್ಥಳಕ್ಕೆ ಒಯ್ದು ಬಿಡುವಂತೆ ಇದೆ.');
INSERT INTO "kannada" VALUES(22,32,'22:32','ಅದನ್ನು ಗ್ರಹಿಸಿರಿ. ಯಾರು ಅಲ್ಲಾಹನ ಲಾಂಛನ ಗಳನ್ನು ಗೌರವಿಸುತ್ತಾರೋ ನಿಜಕ್ಕೂ ಅದು ಹೃದ ಯಗಳ ಭಕ್ತಿಯಿಂದಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(22,33,'22:33','ಒಂದು ನಿಶ್ಚಿತ ಕಾಲದವರೆಗೆ ಅವುಗಳಲ್ಲಿ (ಬಲಿ ಮೃಗಗಳಲ್ಲಿ) ನಿಮಗೆ ಪ್ರಯೋಜನ ಪಡೆಯುವ ಹಕ್ಕಿದೆ . ಅನಂತರ ಅವುಗಳನ್ನು ಬಲಿದಾನ ಮಾಡುವ ಸ್ಥಾನವು ಪುರಾತನ ಕಅಬಾ ಭವನದ ಸಮೀಪವಿದೆ.');
INSERT INTO "kannada" VALUES(22,34,'22:34','ನಾವು ಪ್ರತಿಯೊಂದು ಸಮುದಾಯಕ್ಕೆ ಬಲಿ ಕರ್ಮವನ್ನು ನಿಶ್ಚಯಿಸಿದ್ದೇವೆ. ಅವರಿಗೆ ಅಲ್ಲಾಹನು ದಯಪಾಲಿಸಿರುವ ಪ್ರಾಣಿಗಳ ಮೇಲೆ ಅವರು ಅಲ್ಲಾಹನ ನಾಮವನ್ನು ಪ್ರಕೀರ್ತಿಸಲಿಕ್ಕಾಗಿ. ನಿಮ್ಮ ದೇವನು ಏಕಮಾತ್ರ ದೇವನು. ಆದುದರಿಂದ ನೀವು ಅವನನ್ನು ಅನುಸರಿಸಿ ಬಾಳಿರಿ. (ಪೈಗಂಬರರೇ,) ವಿನಯಶೀಲರಿಗೆ ಶುಭವಾರ್ತೆ ನೀಡಿರಿ.');
INSERT INTO "kannada" VALUES(22,35,'22:35','(ಅವರು ಯಾರೆಂದರೆ) ಅಲ್ಲಾಹನನ್ನು ಪ್ರಸ್ತಾಪಿ ಸಲಾದರೆ, ತಮ್ಮ ಹೃದಯಗಳು ಕಂಪಿಸುವವರು, ತಮಗೆ ಬಾಧಿಸುವ ಸಂಕಷ್ಟಗಳ ಮೇಲೆ ತಾಳ್ಮೆ ವಹಿಸುವವರು, ನಮಾಝನ್ನು ಸಂಸ್ಥಾಪಿಸುವವರು. ನಾವು ಅವರಿಗೆ ನೀಡಿದ ಜೀವನಾಧಾರದಿಂದ ವ್ಯಯಿಸುವವರು .');
INSERT INTO "kannada" VALUES(22,36,'22:36','(ಬಲಿದಾನದ) ಒಂಟೆಗಳನ್ನು ನಿಮಗಾಗಿ ನಾವು ಅಲ್ಲಾಹನ ಧರ್ಮ ಲಾಂಛನಗಳಾಗಿ ಮಾಡಿದ್ದೇವೆ. ನಿಮಗೆ ಅವುಗಳಲ್ಲಿ ಒಳಿತಿದೆ. ಆದುದರಿಂದ ಕೊಯ್ಯಲು ಸಾಲಾಗಿ ನಿಲ್ಲಿಸಿದ ನೆಲೆಯಲ್ಲಿ ಅವು ಗಳ ಮೇಲೆ ಅಲ್ಲಾಹನ ನಾಮವನ್ನು ಉಚ್ಚರಿಸಿರಿ . ಅವುಗಳ ಬೆನ್ನುಗಳು (ಅವುಗಳು ಪ್ರಾಣ ಬಿಟ್ಟು) ನೆಲಕ್ಕೆ ತಾಗಿದರೆ (ಅಡ್ಡ ಬಿದ್ದರೆ) ಅವು ಗಳಿಂದ ಸ್ವತಃ ತಿನ್ನಿರಿ ಹಾಗೂ ಯಾಚಿಸದೆ ಇದ್ದುದರಲ್ಲಿ ಸಂತೃಪ್ತಿಪಡುವವರಿಗೂ ಬೇಡಿಕೆ ಮುಂದಿಟ್ಟವರಿಗೂ ತಿನ್ನಿಸಿರಿ. ಈ ರೀತಿಯಲ್ಲಿ ಅವುಗಳನ್ನು ನಿಮಗಾಗಿ ನಾವು ನಿಯಂತ್ರಿಸಿ ಕೊಟ್ಟಿದ್ದೇವೆ. ನೀವು ಕೃತಜ್ಞರಾಗಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(22,37,'22:37','ಅವುಗಳ ಮಾಂಸವಾಗಲಿ ರಕ್ತವಾಗಲಿ ಅಲ್ಲಾಹ ನಿಗೆ ತಲುಪುವುದಿಲ್ಲ. ಆದರೆ ನಿಮ್ಮ ಭಕ್ತಿಯು ಅವನಿಗೆ ತಲುಪುತ್ತದೆ. ಅದೇ ಪ್ರಕಾರ ಅವನು ನಿಮಗೆ ಸನ್ಮಾರ್ಗವನ್ನು ದಯಪಾಲಿಸಿದ್ದಕ್ಕೋ ಸುಗ, ಅಲ್ಲಾಹನನ್ನು ನೀವು ಪ್ರಕೀರ್ತಿಸಲಿಕ್ಕಾಗಿ ಅವನು ಅವುಗಳನ್ನು ನಿಮಗಾಗಿ ನಿಯಂತ್ರಿಸಿ ಕೊಟ್ಟಿದ್ದಾನೆ. (ಪೈಗಂಬರರೇ,) ಸದ್ಭಕ್ತರಿಗೆ ಸುವಾರ್ತೆ ನೀಡಿರಿ.');
INSERT INTO "kannada" VALUES(22,38,'22:38','ಅಲ್ಲಾಹನು ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನು (ಶತ್ರುಗಳಿಂದ) ತಡೆದು ರಕ್ಷಣೆ ಮಾಡುತ್ತಾನೆ. ಯಾವುದೇ ವಿಶ್ವಾಸ ಘಾತುಕ ಸತ್ಯನಿಷೇಧಿಯನ್ನು ಅಲ್ಲಾಹು ಖಂಡಿತ ಮೆಚ್ಚಲಾರ.');
INSERT INTO "kannada" VALUES(22,39,'22:39','ಯುದ್ಧಕ್ಕೊಳಗಾದವರಿಗೆ, ಅವರು ಮರ್ದಿತರು ಎಂಬ ಕಾರಣಕ್ಕೆ ಪ್ರತಿರೋಧಿಸುವ ಅನುಮತಿ ನೀಡಲಾಗಿದೆ . ನಿಶ್ಚಯ, ಅಲ್ಲಾಹನು ಅವರಿಗೆ ಸಹಾಯ ಮಾಡಲು ಸಮರ್ಥನು.');
INSERT INTO "kannada" VALUES(22,40,'22:40','ಯಾವುದೇ ನ್ಯಾಯವಿಲ್ಲದೆ, “ನಮ್ಮ ಪ್ರಭು ಅಲ್ಲಾಹು” ಎಂದಿಷ್ಟೇ ಹೇಳಿದ ಮಾತ್ರಕ್ಕೆ ತಮ್ಮ ಮನೆಗಳಿಂದ ಹೊರ ಹಾಕಲ್ಪಟ್ಟವರು. ಅವರು ಜನರಲ್ಲಿ ಕೆಲವರನ್ನು ಬೇರೆ ಕೆಲವರ ಮೂಲಕ ಅಲ್ಲಾಹನು ತಡೆಯದಿರುತ್ತಿದ್ದರೆ ಅಲ್ಲಾಹನ ನಾಮವನ್ನು ಅತಿ ಹೆಚ್ಚಾಗಿ ಸ್ಮರಿಸಲಾಗುತ್ತಿರುವ ಮಠ, ಆಶ್ರಮಗಳೂ ಇಗರ್ಜಿಗಳೂ ಪ್ರಾರ್ಥನಾ ಮಂದಿರಗಳೂ ಮಸೀದಿಗಳೂ ಧ್ವಂಸಗೊಳಿಸಲ್ಪಡುತ್ತಿದ್ದವು. ಯಾರು ಅಲ್ಲಾಹನಿಗೆ ಸಹಾಯ ಮಾಡುತ್ತಾರೆ, ಅವರಿಗೆ ಅವನು ಖಂಡಿತ ಸಹಾಯ ಮಾಡುತ್ತಾನೆ. ಅಲ್ಲಾಹು ಶಕ್ತಿವಂತನೂ ಪ್ರಬಲನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(22,41,'22:41','ಇವರಿಗೆ (ಈ ಮರ್ದಿತರಿಗೆ) ನಾವು ಭೂಮಿಯಲ್ಲಿ ಶಕ್ತಿ-ಸ್ವಾಧೀನವನ್ನು ಕೊಟ್ಟರೆ ಇವರು ನಮಾಝ್ ಸಂಸ್ಥಾಪಿಸುತ್ತಾರೆ. ಝಕಾತ್ ಕೊಡುತ್ತಾರೆ. ಒಳಿತನ್ನು ಆಜ್ಞಾಪಿಸುತ್ತಾರೆ ಮತ್ತು ಕೆಡುಕಿನಿಂದ ತಡೆಯುತ್ತಾರೆ. ಎಲ್ಲ ಕಾರ್ಯಗಳ ಪರ್ಯಾವಸಾನವು ಅಲ್ಲಾಹನಿಗೆ ಸೇರಿದ್ದು.');
INSERT INTO "kannada" VALUES(22,42,'22:42','ಪೈಗಂಬರರೇ, ಅವರು (ಸತ್ಯನಿಷೇಧಿಗಳು) ನಿಮ್ಮನ್ನು ಸುಳ್ಳಾಗಿಸುತ್ತಿದ್ದರೆ, ಅವರಿಗೂ ಮುನ್ನ ನೂಹರ ಜನಾಂಗ, ಆದ್, ಸಮೂದ್''ರ ಜನಾಂಗವು ತಮ್ಮ ಪ್ರವಾದಿಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(22,43,'22:43','ಇಬ್‍ರಾಹೀಮರ ಜನಾಂಗವು, ಲೂಥರ ಜನಾಂಗವು');
INSERT INTO "kannada" VALUES(22,44,'22:44','ಮತ್ತು ಮದ್‍ಯನ ನಿವಾಸಿಗಳು ತಮ್ಮ ಪ್ರವಾದಿ ಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದ್ದಾರೆ. ಮೂಸಾರು ಕೂಡಾ ಸುಳ್ಳನೆಂದು ಆರೋಪಿಸಲ್ಪಟ್ಟರು. ನಾನು ಸತ್ಯ ನಿಷೇಧಿಗಳಿಗೆ ಕಾಲಾವಕಾಶ ನೀಡಿದೆನು, ಅನಂತರ ಹಿಡಿದು ಶಿಕ್ಷಿಸಿದೆ. ನನ್ನ ಪ್ರತಿರೋಧ ಹೇಗಿತ್ತೆಂದು ನೋಡಿದಿರಾ?');
INSERT INTO "kannada" VALUES(22,45,'22:45','ಅದೆಷ್ಟು ಅಪರಾಧೀ ನಾಡುಗಳನ್ನು ನಾವು ನಾಶ ಗೊಳಿಸಿದ್ದೇವೆ! ಆಗ ಅವುಗಳು ತಮ್ಮ ಛಾವಣಿಗಳ ಮೇಲೆ ಮುರಿದು ಬಿದ್ದಿವೆ. ಎಷ್ಟೋ ಬಾವಿಗಳೂ, ಎಷ್ಟೋ ಅರಮನೆಗಳೂ ಪಾಳು ಬಿದ್ದಿವೆ!');
INSERT INTO "kannada" VALUES(22,46,'22:46','ಅವರಿಗೆ ಭೂಮಿಯಲ್ಲಿ ಸಂಚರಿಸಬಾರದೇ? ಹಾಗಿದ್ದಲ್ಲಿ ಚಿಂತಿಸುವ ಹೃದಯಗಳು ಅಥವಾ ಕೇಳಿಸಿಕೊಳ್ಳುವ ಕಿವಿಗಳು ಅವರದ್ದಾಗುತ್ತಿದ್ದವು. ಖಂಡಿತವಾಗಿಯೂ ವಾಸ್ತವವೇನೆಂದರೆ, ಕಣ್ಣುಗಳು ಕುರುಡಾಗುವುದಿಲ್ಲ. ಆದರೆ ಎದೆಗೂಡಿನೊ ಳಗಣ ಹೃದಯಗಳು ಕುರುಡಾಗುತ್ತವೆ.');
INSERT INTO "kannada" VALUES(22,47,'22:47','(ಪ್ರವಾದಿಯವರೇ) ಇವರು ಶಿಕ್ಷೆಗಾಗಿ ತ್ವರೆ ಮಾಡುತ್ತಾರೆ. ಆದರೆ ಅಲ್ಲಾಹು ತನ್ನ ವಾಗ್ದಾನ ವನ್ನು ಉಲ್ಲಂಘಿಸಲಾರ. ಆದರೆ ತಮ್ಮ ಪ್ರಭುವಿನ ಬಳಿಯ ಒಂದು ದಿನವು ನೀವು ಲೆಕ್ಕ ಮಾಡುವ ಸಾವಿರ ವರ್ಷಗಳಿಗೆ ಸಮಾನವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(22,48,'22:48','ಎಷ್ಟೋ ನಾಡುಗಳಿದ್ದುವು. ಅವರು ಅಕ್ರಮ ವೆಸಗುತ್ತಿರುವಂತೆಯೇ ಅವರಿಗೆ ನಾನು ಕಾಲಾವಕಾಶ ನೀಡಿದೆನು, ನಂತರ ಅವರನ್ನು ಹಿಡಿದು ಶಿಕ್ಷಿಸಿದೆನು. ನನ್ನ ಕಡೆಗೇ ಅವರ ಮರಳಿಕೆ ಕೂಡಾ!');
INSERT INTO "kannada" VALUES(22,49,'22:49','(ಪೈಗಂಬರರೇ) ಹೇಳಿರಿ; “ಓ ಜನರೇ, ನಾನು ನಿಮಗೆ ಸುಸ್ಪಷ್ಟ ಎಚ್ಚರಿಕೆ ಕೊಡುವವನು ಮಾತ್ರವಾಗಿರುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(22,50,'22:50','ಆದ್ದರಿಂದ ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸುವ ಮತ್ತು ಸತ್ಕರ್ಮವೆಸಗುವ ಜನರಿಗೆ ಪಾಪಮುಕ್ತಿಯೂ ಗೌರವಪೂರ್ಣ ಔತಣವೂ ಇವೆ');
INSERT INTO "kannada" VALUES(22,51,'22:51','ನಮ್ಮ ಹಿಡಿತದಿಂದ ಪಾರಾಗಬಲ್ಲೆವೆಂಬ ಭ್ರಮೆ ಯಿಂದ ನಮ್ಮ ವಚನಗಳಲ್ಲಿ ಗೊಂದಲವೆಬ್ಬಿಸಲು ಪ್ರಯತ್ನಿಸು ವವರು ನರಕಾಗ್ನಿಯವರು”.');
INSERT INTO "kannada" VALUES(22,52,'22:52','(ಪೈಗಂಬರರೇ,) ನಿಮಗಿಂತ ಮುಂಚೆ ಯಾವು ದೇ ದೂತರು ಮತ್ತು ಪ್ರವಾದಿಯನ್ನು ನಾವು ಕಳುಹಿಸಿದ್ದು ಅವರೇನಾದರೂ ಇಚ್ಛಿಸಿ ದಾಗ ಅವರ ಸದಿಚ್ಛೆಯಲ್ಲಿ ಶೈತಾನನು ದುರ್ಭೋದನೆಯನ್ನು ಎಳೆದು ಹಾಕದೆ ಇರಲಿಲ್ಲ. ಆದರೆ ಶೈತಾನನು ಎಳೆದು ಹಾಕಿದ್ದನ್ನು ಅಲ್ಲಾಹನು ಅಳಿಸುತ್ತಾನೆ. ತನ್ನ ವಚನಗಳನ್ನು ಅಚಲಗೊಳಿಸುತ್ತಾನೆ. ಅಲ್ಲಾಹು ಸರ್ವಜ್ಞನೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(22,53,'22:53','ಹೃದಯಗಳಿಗೆ (ಕಾಪಟ್ಯದ) ರೋಗ ತಗಲಿದವರಿಗೆ ಹಾಗೂ ಕಠಿಣ ಹೃದಯದವರಿಗೆ ಶೈತಾನನು ಎಳೆದು ಹಾಕಿದ ದುರ್ಮಂತ್ರಗಳನ್ನು ಪರೀಕ್ಷಾ ಸಾಧನವನ್ನಾಗಿ ಮಾಡುವುದೇ ಅದರ ಫಲ. ನಿಜಕ್ಕೂ ಅಕ್ರಮಿಗಳು ಸತ್ಯದಿಂದ ಬಹು ದೂರವಾದ ಭಿನ್ನತೆಗೆ ಒಳಗಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(22,54,'22:54','ಜ್ಞಾನವನ್ನು ಪಡೆದವರು ಇದು ತಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಬಂದಿರುವ ಸತ್ಯವೆಂದು ಅರಿತು ಕೊಂಡು ಇದರಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸಲಿ ಮತ್ತು ಈ ಮೂಲಕ ಅವರ ಹೃದಯಗಳು ಇದರ ಮುಂದೆ ವಿನಯ ತೋರ್ಪಡಿಸಲಿ ಎಂದು. ಅಲ್ಲಾಹನು ವಿಶ್ವಾಸವಿರಿಸುವವರ ಪಥದ ದರ್ಶಕನು ಖಂಡಿತ.');
INSERT INTO "kannada" VALUES(22,55,'22:55','ತಮಗೆ ಅಂತ್ಯ ದಿನ ಹಠಾತ್ತನೆ ಬರುವವರೆಗೂ ಇಲ್ಲವೇ ಒಂದು ಅಹಿತಕರ ದಿನದ ಶಿಕ್ಷೆ ಅವರ ಮೇಲೆ ಎರಗುವವರೆಗೂ ಸತ್ಯನಿಷೇಧಿಗಳು ಇದರ (ಖುರ್‍ಆನಿನ) ಬಗ್ಗೆ ಸಂದೇಹದಲ್ಲೇ ಬಿದ್ದು ಕೊಂಡಿರುವರು.');
INSERT INTO "kannada" VALUES(22,56,'22:56','ಅಂದಿನ ಪ್ರಭುತ್ವ ಅಲ್ಲಾಹನದ್ದಾಗಿದ್ದು ಅವನು ಇವರ ನಡುವೆ ತೀರ್ಮಾನ ಮಾಡುವನು. ಆದ್ದರಿಂದ ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿ, ಸತ್ಕರ್ಮವೆಸಗಿದ ಜನರು ಅನುಗ್ರಹೀತ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿರುವರು.');
INSERT INTO "kannada" VALUES(22,57,'22:57','ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸಿದ ಮತ್ತು ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದ ಜನರಿಗೆ ಅಪಮಾನಕರ ಶಿಕ್ಷೆಯುಂಟು.');
INSERT INTO "kannada" VALUES(22,58,'22:58','ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಊರು ಬಿಟ್ಟು ವಲಸೆ ಹೋಗಿ, ತರುವಾಯ ಶತ್ರುಗಳಿಂದ ವಧಿಸಲ್ಪಟ್ಟವರು ಅಥವಾ ಮೃತರಾದವರು, ಅವರಿಗೆ ಅಲ್ಲಾಹನು ಶ್ರೇಷ್ಠ ಕೊಡುಗೆ (ಸ್ವರ್ಗ)ಯನ್ನು ದಯಪಾಲಿಸುವನು. ಅಲ್ಲಾಹನು ಪರಮ ಶ್ರೇಷ್ಠ ದಾಯಕನು.');
INSERT INTO "kannada" VALUES(22,59,'22:59','ಅವರು ಸಂತುಷ್ಟರಾಗುವಂತಹ ಒಂದು ಪ್ರವೇಶ ಸ್ಥಾನಕ್ಕೆ (ಸ್ವರ್ಗಕ್ಕೆ) ಅವನು ಅವರನ್ನು ಸೇರಿಸಿ ಬಿಡುವನು. ಅಲ್ಲಾಹನು ಸರ್ವಜ್ಞನೂ ಸಹನ ಶೀಲನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(22,60,'22:60','ಇವಿಷ್ಟು ವಿಷಯದ ನೆಲೆ. ಒಬ್ಬನು ತನಗೆ ಕೈ ಗೊಂಡ ಶಿಕ್ಷಾ ಕ್ರಮಕ್ಕೆ ಸಮಾನವಾದ ರೀತಿಯಲ್ಲಿ ಸೇಡು ತೀರಿಸಿದರೆ, ತದನಂತರ ಅವನ ಮೇಲೆ ಅತಿರೇಕವೂ ನಡೆದಿದ್ದರೆ ಅಲ್ಲಾಹನು ಅವನಿಗೆ ಖಂಡಿತ ಸಹಾಯ ಮಾಡುವನು. ಅಲ್ಲಾಹನು ಅತ್ಯಂತ ಕ್ಷಮಾಶೀಲನೂ ಪಾಪ ಹರನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(22,61,'22:61','ಇದು ಅಲ್ಲಾಹನು ಹಗಲಿನಲ್ಲಿ ರಾತ್ರಿಯನ್ನೂ ರಾತ್ರಿಯಲ್ಲಿ ಹಗಲನ್ನೂ ಒಳದಾಟಿಸುತ್ತಾನೆ ಮತ್ತು ಅಲ್ಲಾಹನು ಸರ್ವಶ್ರುತನೂ ಸರ್ವ ವೀಕ್ಷಕನೂ ಆಗಿರುತ್ತಾನೆ ಎಂಬ ಕಾರಣಕ್ಕಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(22,62,'22:62','ಇದು ಅಲ್ಲಾಹನೇ ಪರಮ ಸತ್ಯ. ಅವನನ್ನು ಬಿಟ್ಟು ಇವರು ಆರಾಧಿಸುವ ವಸ್ತುವೇ ಮಿಥ್ಯ. ಅಲ್ಲಾಹನೇ ಸರ್ವೋನ್ನತನೂ ಮಹಾನನೂ ಆಗಿರುತ್ತಾನೆ ಎಂಬ ಕಾರಣಕ್ಕಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(22,63,'22:63','ಅಲ್ಲಾಹು ಆಕಾಶದಿಂದ ನೀರು ಸುರಿಸುವುದನ್ನೂ ಅದರಿಂದಾಗಿ ಭೂಮಿಯು ಹಸಿರಾಗುವುದನ್ನೂ ನೀವು ನೋಡುತ್ತಿಲ್ಲವೇ? ಖಂಡಿತ ಅಲ್ಲಾಹನು ಮೃದುಗುಣಿಯೂ ಸೂಕ್ಷ್ಮಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(22,64,'22:64','ಆಕಾಶಗಳಲ್ಲಿರುವುದೂ ಭೂಮಿಯಲ್ಲಿರುವುದೂ ಅವನದ್ದು. ಅಲ್ಲಾಹನು ಖಂಡಿತ ನಿರಪೇಕ್ಷನೂ ಸ್ತುತ್ಯರ್ಹನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(22,65,'22:65','ಅಲ್ಲಾಹನು ನಿಮಗೆ ಭೂಮಿಯಲ್ಲಿರುವುದನ್ನೂ ಅವನ ಅಪ್ಪಣೆಯಂತೆ ಸಮುದ್ರದಲ್ಲಿ ಚಲಿಸುವ ನಾವೆಯನ್ನೂ ಅಧೀನಗೊಳಿಸಿ ಕೊಟ್ಟಿರುವುದನ್ನು ನೀವು ಕಾಣುತ್ತಿಲ್ಲವೇ? ಅವನ ಅಪ್ಪಣೆಯಿಲ್ಲದೆ ಆಕಾಶವು ಭೂಮಿಯ ಮೇಲೆ ಬೀಳದಂತೆ ತಡೆದು ನಿಲ್ಲಿಸುತ್ತಾನೆ. ಅಲ್ಲಾಹನು ಜನರೊಂದಿಗೆ ಮಹಾವತ್ಸಲನೂ ಅತ್ಯಂತ ದಯಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(22,66,'22:66','ನಿಮ್ಮನ್ನು ಬದುಕಿಸುವವನೂ, ನಂತರ ನಿಮಗೆ ಮರಣ ಕೊಡುವವನೂ, ತದನಂತರ ನಿಮ್ಮನ್ನು ಪುನಃ ಜೀವಂತಗೊಳಿಸುವವನೂ ಅವನೇ. ನಿಜಕ್ಕೂ ಮಾನವನು ಮಹಾ ಕೃತಘ್ನನಾಗಿರು ವನು.');
INSERT INTO "kannada" VALUES(22,67,'22:67','ಪ್ರತಿಯೊಂದು ಸಮುದಾಯಕ್ಕೂ ಒಂದು ಆರಾ ಧನಾ ಸಾಂಗ್ಯವನ್ನು ನಾವು ನಿಶ್ಚಯಿಸಿದ್ದೇವೆ. ಅದನ್ನು ಅದು ಆಚರಿಸುತ್ತದೆ. ಆದುದರಿಂದ (ಪೈಗಂಬರರೇ,) ಈ ವಿಷಯದಲ್ಲಿ ಅವರು ನಿಮ್ಮೊಡನೆ ಜಗಳಾಡದಿರಲಿ . ನೀವು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಗೆ ಕರೆ ನೀಡಿರಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ನೀವು ನೇರವಾದ ಸನ್ಮಾರ್ಗದಲ್ಲಿದ್ದೀರಿ.');
INSERT INTO "kannada" VALUES(22,68,'22:68','ಇನ್ನು ಮುಂದೆ ಅವರು ನಿಮ್ಮಲ್ಲಿ ಜಗಳಾಡಿದರೆ ಹೇಳಿರಿ, ``ಅಲ್ಲಾಹನು ನೀವು ಮಾಡುತ್ತಿರುವುದನ್ನು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(22,69,'22:69','ಅಲ್ಲಾಹನು ನೀವು ಭಿನ್ನಾಭಿಪ್ರಾಯ ತಾಳಿಕೊಂಡಿದ್ದ ವಿಷಯದಲ್ಲಿ ಪುನರುತ್ಥಾನದ ದಿನ ನಿಮ್ಮ ನಡುವೆ ತೀರ್ಮಾನ ಮಾಡುವನು”.');
INSERT INTO "kannada" VALUES(22,70,'22:70','ಆಕಾಶ - ಭೂಮಿಯಲ್ಲಿರುವ ಪ್ರತಿಯೊಂದನ್ನು ಅಲ್ಲಾಹನು ಖಂಡಿತ ಬಲ್ಲನೆಂಬುದು ನಿಮಗೆ ತಿಳಿಯದೇ? ಅದು ಒಂದು ಗ್ರಂಥದಲ್ಲಿದೆ. ಅಲ್ಲಾಹನ ಪಾಲಿಗೆ ಇದು ಬಾರಿ ಸುಲಭದ ಸಂಗತಿ.');
INSERT INTO "kannada" VALUES(22,71,'22:71','ಇವರು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಅವನು ಯಾವುದೇ ಆಧಾರವನ್ನಿಳಿಸದ ಹಾಗೂ ಆ ಬಗ್ಗೆ ಸ್ವತಃ ಅವರಿಗೇ ಯಾವುದೇ ಜ್ಞಾನವಿಲ್ಲದ ವಸ್ತುಗಳನ್ನು ಆರಾಧಿಸುತ್ತಾರೆ. ಅಕ್ರಮಿಗಳಿಗೆ ಯಾರೂ ಸಹಾಯಕರಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(22,72,'22:72','ಇವರ ಮೇಲೆ ನಮ್ಮ ಸುಸ್ಪಷ್ಟ ವಚನಗಳನ್ನು ಓದಲಾದರೆ ಸತ್ಯನಿಷೇಧಿಗಳ ಮುಖಗಳಲ್ಲಿ ಅನಿಷ್ಟತೆಯನ್ನು ನೀವು ಕಾಣುವಿರಿ . ಇವರಿಗೆ ನಮ್ಮ ವಚನಗಳನ್ನು ಓದಿ ಹೇಳುವವರ ಮೇಲೆ ಇವರು ಕೈ ಮಾಡಲು ಮುಂದಾಗುವರು. ಹೇಳಿರಿ, ನಿಮಗೆ ಇದಕ್ಕಿಂತಲೂ ಅನಿಷ್ಟ ತೋರು ವುದನ್ನು ನಾನು ನಿಮಗೆ ಹೇಳಲೇ? ಅದೇ ನರಕಾಗ್ನಿ. ಅಲ್ಲಾಹನು ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಅದನ್ನೇ ವಾಗ್ದಾನ ಮಾಡಿದ್ದಾನೆ. ಅವರು ಮರಳಿ ಸೇರುವ ಸ್ಥಳ ಅತ್ಯಂತ ನಿಕೃಷ್ಟವಾಗಿದೆ”.');
INSERT INTO "kannada" VALUES(22,73,'22:73','ಓ ಜನರೇ, ಒಂದು ಉದಾಹರಣೆ ಕೊಡಲಾಗುತ್ತಿದೆ. ಗಮನವಿಟ್ಟು ಕೇಳಿರಿ; ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ನೀವು ಯಾರನ್ನು ಆರಾಧಿಸುತ್ತೀರೋ ಅವರು ಒಂದು ನೊಣವನ್ನೂ ಸೃಷ್ಟಿಸಲಾರರು. ಅದಕ್ಕಾಗಿ ಅವರು ಒಟ್ಟು ಸೇರಿದರೂ ಕೂಡಾ. ಇನ್ನು ನೊಣವು ಅವರಿಂದ ಏನಾದರೂ ಕಸಿದು ಕೊಂಡೊ ಯ್ದರೆ ಅದರಿಂದ ಅದನ್ನು ಬಿಡಿಸಿಕೊಳ್ಳಲಿಕ್ಕೂ ಅವರಿಂದಾ ಗದು. ಅಪೇಕ್ಷಕನೂ, (ಆರಾಧಕನೂ) ಅಪೇಕ್ಷಿತನೂ ದುರ್ಬಲರೇ ಸರಿ .');
INSERT INTO "kannada" VALUES(22,74,'22:74','ಇವರು ಅಲ್ಲಾಹನ ಮಹತ್ವಕ್ಕೆ ತಕ್ಕಂತೆ ಅವನನ್ನು ಗೌರವಿಸಲಿಲ್ಲ. ಅಲ್ಲಾಹನು ಶಕ್ತಿಪೂರ್ಣನೂ ಮಹಾ ಪ್ರತಾಪಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(22,75,'22:75','ದೇವಚರರಿಂದಲೂ ಮನುಷ್ಯರಿಂದಲೂ ಅಲ್ಲಾಹು ದೂತರನ್ನು ಆಯ್ದುಕೊಳ್ಳುತ್ತಾನೆ . ಅಲ್ಲಾಹ ನು ಸರ್ವಶ್ರುತನೂ ಸರ್ವವೀಕ್ಷಕನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(22,76,'22:76','ಜನರ ಮುಂದೆ ಮತ್ತು ಹಿಂದಿರುವ ಎಲ್ಲವನ್ನೂ ಅವನು ಬಲ್ಲನು. ಕಾರ್ಯಗಳನ್ನು ಅವನ ಕಡೆಗೇ ಮರಳಿಸಲಾಗುತ್ತವೆ .');
INSERT INTO "kannada" VALUES(22,77,'22:77','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ‘ರುಕೂಅï ಮಾಡಿರಿ, ಸುಜೂದ್ ಮಾಡಿರಿ (ಅರ್ಥಾತ್ ನಮಾಝ್ ಮಾಡಿರಿ). ಮತ್ತು ನಿಮ್ಮ ಪ್ರಭುವನ್ನು ಆರಾಧಿಸಿರಿ. ಸತ್ಕರ್ಮವನ್ನು ಮಾಡಿರಿ. ನೀವು ವಿಜಯಿಗಳಾಗುವಿರಿ.');
INSERT INTO "kannada" VALUES(22,78,'22:78','ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಹೋರಾಡಬೇಕಾದ ರೀತಿಯಲ್ಲಿ ಹೋರಾಡಿರಿ. ಅವನು ತನ್ನ ಧರ್ಮಕ್ಕಾಗಿ ನಿಮ್ಮನ್ನು ಆರಿಸಿಕೊಂಡಿದ್ದಾನೆ. ಧರ್ಮದಲ್ಲಿ ನಿಮ್ಮ ಮೇಲೆ ಯಾವುದೇ ಇಕ್ಕಟ್ಟನ್ನು ಉಂಟುಮಾಡಿಲ್ಲ. ನಿಮ್ಮ ಪಿತಾಮಹರಾದ ಇಬ್‍ರಾಹೀಮರ ಮಾರ್ಗವನ್ನು ಅವಲಂಬಿಸಿರಿ. ಇದಕ್ಕೆ ಮೊದಲೂ ಈ ವೇದದಲ್ಲೂ ಅಲ್ಲಾಹು ನಿಮಗೆ `ಮುಸ್ಲಿಮರು’ ಎಂಬ ಹೆಸರಿಟ್ಟಿರುವನು . ಇದು ಸಂದೇಶವಾಹಕರು ನಿಮ್ಮ ಮೇಲೆ ಸಾಕ್ಷಿಗಳಾಗಲಿಕ್ಕಾಗಿ ಮತ್ತು ನೀವು ಜನರ ಮೇಲೆ ಸಾಕ್ಷಿಗಳಾಗಲಿಕ್ಕಾಗಿ ಆಗಿರುತ್ತದೆ. ಆದುದರಿಂದ ನಮಾಝನ್ನು ಕ್ರಮ ಪ್ರಕಾರ ಕೈಗೊಳ್ಳಿರಿ. ಝಕಾತ್ ಕೊಡಿರಿ ಮತ್ತು ಅಲ್ಲಾಹನ ಮೂಲಕ ನೀವು ಬಲಿಷ್ಟರಾಗಿರಿ. ಅವನೇ ನಿಮ್ಮ ರಕ್ಷಕಮಿತ್ರ. ಆದ್ದರಿಂದ ಅವನು ಅತಿ ಶ್ರೇಷ್ಟ ರಕ್ಷಕಮಿತ್ರನು, ಅತ್ಯುತ್ತಮ ಸಹಾಯಕನು. ಂ');
INSERT INTO "kannada" VALUES(23,1,'23:1','ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಖಂಡಿತ ಜಯಶೀಲರಾದರು.');
INSERT INTO "kannada" VALUES(23,2,'23:2','ಅವರು ತಮ್ಮ ನಮಾಝಿನಲ್ಲಿ ಭಯಭಕ್ತಿಯುಳ್ಳವರು .');
INSERT INTO "kannada" VALUES(23,3,'23:3','ನಿರರ್ಥಕ ಕಾರ್ಯಗಳಿಂದ ವಿಮುಖರಾದವರು.');
INSERT INTO "kannada" VALUES(23,4,'23:4','ಕಡ್ಡಾಯ ದಾನವನ್ನು ಪಾವತಿಸುವವರು.');
INSERT INTO "kannada" VALUES(23,5,'23:5','ತಮ್ಮ ಗುಪ್ತಾಂಗಗಳನ್ನು ರಕ್ಷಿಸಿಕೊಳ್ಳುವವರು.');
INSERT INTO "kannada" VALUES(23,6,'23:6','ತಮ್ಮ ಪತ್ನಿಯರು ಹಾಗೂ ತಮ್ಮ ಅಧೀನವಿರುವ ದಾಸಿಯರ ಹೊರತು. ಇಂಥವರು ಖಂಡಿತ ದೂಷಣೀಯರಲ್ಲ .');
INSERT INTO "kannada" VALUES(23,7,'23:7','ಆದರೆ ಯಾರು, ಇದರಾಚೆಗೆ ದಾಟಿ ಹೋಗುತ್ತಾರೆ, ಅವರೇ ಅತಿರೇಕಿಗಳು.');
INSERT INTO "kannada" VALUES(23,8,'23:8','ತಮ್ಮಲ್ಲಿ ವಿಶ್ವಾಸವಿಟ್ಟು ಒಪ್ಪಿಸಲಾದ ಸೊತ್ತುಗಳನ್ನು ಹಾಗೂ ತಮ್ಮ ವಾಗ್ದಾನಗಳನ್ನು ಕಾಪಾ ಡುವವರು.');
INSERT INTO "kannada" VALUES(23,9,'23:9','ತಮ್ಮ ನಮಾಝ್‍ಗಳಲ್ಲಿ ಸಮಯ ನಿಷ್ಠೆ ಪಾಲಿಸುವವರು .');
INSERT INTO "kannada" VALUES(23,10,'23:10','ಇವರೇ ವಾರೀಸುದಾರರು.');
INSERT INTO "kannada" VALUES(23,11,'23:11','ಅಂದರೆ ಫಿರ್‍ದೌಸ್(ಮಹೋನ್ನತ ಸ್ವರ್ಗ) ಅನ್ನು ವಾರೀಸು ಹಕ್ಕಾಗಿ ಪಡೆಯುವವರು. ಅವರು ಅದರಲ್ಲಿ ನಿತ್ಯ ವಾಸಿಗಳು.');
INSERT INTO "kannada" VALUES(23,12,'23:12','ಮಾನವನನ್ನು ಆವೆ ಮಣ್ಣಿನ ಸತ್ವದಿಂದ ನಾವು ಸೃಷ್ಟಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(23,13,'23:13','ಆಮೇಲೆ ಆತನನ್ನು ವೀರ್ಯದ ಕಣವಾಗಿ ಒಂದು ಸುರಕ್ಷಿತ ಸ್ಥಳದಲ್ಲಿ (ಗರ್ಭಾಶಯದಲ್ಲಿ) ಇರಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(23,14,'23:14','ಅನಂತರ ಆ ವೀರ್ಯಾಣುವನ್ನು ರಕ್ತಪಿಂಡವ ನ್ನಾಗಿ ಮಾಡಿದೆವು. ನಂತರ ಆ ರಕ್ತ ಪಿಂಡವನ್ನು ಮಾಂಸ ಪಿಂಡವನ್ನಾಗಿ ಮಾಡಿದೆವು. ಆಮೇಲೆ ಆ ಮಾಂಸ ಪಿಂಡವನ್ನು ಎಲುಬುಗಳಾಗಿ ಮಾಡಿದೆವು. ತರುವಾಯ ಎಲುಬುಗಳ ಮೇಲೆ ಮಾಂಸ ತೊಡಿಸಿದೆವು. ಆ ಬಳಿಕ ಅದನ್ನೊಂದು ಬೇರೆಯೇ ಸೃಷ್ಟಿಯನ್ನಾಗಿ ಬೆಳೆಸಿಕೊಂಡು ಬಂದೆವು. ಅತ್ಯಂತ ಉತ್ತಮ ಸೃಷ್ಟಿಕರ್ತನಾದ ಅಲ್ಲಾಹನು ಮಹಾ ಸಮೃದ್ಧನು.');
INSERT INTO "kannada" VALUES(23,15,'23:15','ನಂತರ ಹೀಗೆ ಜನಿಸಿದ ಬಳಿಕ ನೀವು ಮರಣ ಹೊಂದುವಿರಿ.');
INSERT INTO "kannada" VALUES(23,16,'23:16','ಮತ್ತೆ ಪುನರುತ್ಥಾನದ ದಿನ ಖಂಡಿತವಾಗಿಯೂ ನೀವು ಎಬ್ಬಿಸಲ್ಪಡುವಿರಿ.');
INSERT INTO "kannada" VALUES(23,17,'23:17','ನಾವು ನಿಮ್ಮ ಮೇಲ್ಭಾಗದಲ್ಲಿ ಏಳು (ಆಕಾಶ) ಪಥಗಳನ್ನು ರಚಿಸಿದೆವು . ಸೃಷ್ಟಿಯ ಬಗ್ಗೆ ನಾವು ಅಶೃದ್ಧರಾಗಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(23,18,'23:18','ನಾವು ಆಕಾಶದಿಂದ ನಿರ್ದಿಷ್ಟ ಪ್ರಮಾಣದಲ್ಲಿ ಮಳೆ ನೀರನ್ನು ಸುರಿಸಿದೆವು ಮತ್ತು ಅದನ್ನು ನೆಲದಲ್ಲಿ ತಂಗಿಸಿದೆವು. ಆ ನೀರನ್ನು ಬತ್ತಿಸಲು ಖಂಡಿತ ನಾವು ಶಕ್ತರು .');
INSERT INTO "kannada" VALUES(23,19,'23:19','ಆ ನೀರಿನ ಮೂಲಕ ಖರ್ಜೂರ ಮತ್ತು ದ್ರಾಕ್ಷಾ ತೋಟಗಳನ್ನು ನಿಮಗಾಗಿ ನಾವು ಉತ್ಪಾದಿಸಿದೆವು. ಈ ಭೂಮಿಯಲ್ಲಿ ನಿಮಗಾಗಿ ಅನೇಕ ಫಲಗಳಿವೆ ಮತ್ತು ನೀವು ಅವುಗಳಿಂದ ತಿನ್ನುತ್ತೀರಿ.');
INSERT INTO "kannada" VALUES(23,20,'23:20','ಸೀನಾಯಿ ಪರ್ವತದಲ್ಲಿ ಬೆಳೆಯುವ ಒಂದು ಜಾತಿಯ ಮರವನ್ನೂ ನಾವು ಸೃಷ್ಟಿಸಿದೆವು . ಅದು ಎಣ್ಣೆಯನ್ನೂ ಉಣ್ಣುವವರಿಗಾಗಿ ಕೂಟು ಪದಾರ್ಥವನ್ನೂ ಉತ್ಪಾದಿಸುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(23,21,'23:21','ಜಾನುವಾರುಗಳಲ್ಲಿ ನಿಮಗೆ ಖಂಡಿತ ದೊಡ್ಡ ಪಾಠವಿದೆ. ಅವುಗಳ ಉದರದೊಳಗಿಂದ ನಾವು ನಿಮಗೆ (ಹಾಲು) ಕುಡಿಸುತ್ತೇವೆ. ನಿಮಗೆ ಅವು ಗಳಲ್ಲಿ ಅನೇಕ ಪ್ರಯೋಜನಗಳಿವೆ. ಅವುಗಳನ್ನು ನೀವು ತಿನ್ನುತ್ತೀರಿ.');
INSERT INTO "kannada" VALUES(23,22,'23:22','ಅವುಗಳ ಮೇಲೂ ನಾವೆಗಳ ಮೇಲೂ ನಿಮ್ಮನ್ನು ವಹಿಸಲಾಗುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(23,23,'23:23','ನೂಹರನ್ನು ಅವರ ಜನಾಂಗದ ಕಡೆಗೆ ನಾವು ದೂತರಾಗಿ ನಿಯೋಗಿಸಿದೆವು. ಅವರು ಹೇಳಿ ದರು; “ನನ್ನ ಜನಾಂಗದವರೇ, ನೀವು ಅಲ್ಲಾಹನನ್ನು ಆರಾಧಿಸಿರಿ. ನಿಮಗೆ ಅವನ ಹೊರತು ಬೇರೆ ಆರಾಧ್ಯನಿಲ್ಲ. ಹೀಗಿರುತ್ತ ಆತನನ್ನು ನೀವು ಭಯಪಡುವು ದಿಲ್ಲವೇ?” ');
INSERT INTO "kannada" VALUES(23,24,'23:24','ಆದರೆ ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸಿದ ಅವರ ಜನಾಂಗದ ಸರದಾರರು ಹೀಗೆ ಹೇಳಿದರು; “ಇವನು ನಿಮ್ಮಂತೆಯೇ ಇರುವ ಕೇವಲ ಒಬ್ಬ ಮನುಷ್ಯ. ನಿಮ್ಮ ಮೇಲೆ ಹಿರಿಮೆ ಸಾಧಿಸುವುದೇ ಇವನ ಉದ್ದೇಶ. ಅಲ್ಲಾಹನು ದೂತನನ್ನು ಕಳುಹಿ ಸಲಿಚ್ಛಿಸಿದರೆ ದೇವಚರರನ್ನು ಕಳುಹಿಸುತ್ತಿದ್ದನು. ನಮ್ಮ ಪೂರ್ವಿಕ ಪಿತಾಮಹರಲ್ಲೇನೂ ಇಂಥ ವಿಷ ಯವನ್ನು ನಾವು ಕೇಳಿಯೇ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(23,25,'23:25','ಇವನೊಬ್ಬ ಹುಚ್ಚು ಹಿಡಿದ ವ್ಯಕ್ತಿಯೇ ಸರಿ. ಆದ್ದರಿಂದ ಈತನ ಬಗ್ಗೆ ಸ್ವಲ್ಪ ಕಾಲ ಕಾದು ನೋಡಿರಿ”.');
INSERT INTO "kannada" VALUES(23,26,'23:26','ನೂಹ್ ಹೇಳಿದರು; “ನನ್ನ ಪ್ರಭೂ, ಇವರು ನನ್ನನ್ನು ಸುಳ್ಳಾಗಿಸಿರುವುದರಿಂದ ನನಗೆ ನೀನು ಸಹಾಯ ಮಾಡು.”');
INSERT INTO "kannada" VALUES(23,27,'23:27','ಆಗ ಅವರಿಗೆ ನಾವು (ಈ ರೀತಿ) ಸಂದೇಶ ಕೊಟ್ಟೆವು. “ನಮ್ಮ ಮೇಲ್ನೋಟದಲ್ಲಿ ಮತ್ತು ನಮ್ಮ ನಿರ್ದೇಶ ಪ್ರಕಾರ ನಾವೆಯನ್ನು ನಿರ್ಮಿಸಿರಿ. ಅನಂತರ ನಮ್ಮ ಆಜ್ಞೆ ಬಂದಾಗ ಹಾಗೂ ತಂದೂರಿ ಒಲೆಯಲ್ಲಿ ನೀರೊರತೆ ಹರಿದಾಗ ಪ್ರತಿ ಯೊಂದು ಜಾತಿಯ ಪ್ರಾಣಿಗಳಿಂದ ಒಂದೊಂದು ಜೊತೆಯನ್ನೂ (ಒಂದು ಗಂಡು, ಒಂದು ಹೆಣ್ಣಿ ನಂತೆ) ತಮ್ಮ ಕುಟುಂಬವನ್ನೂ ಅದರಲ್ಲಿ ಹತ್ತಿ ಸಿರಿ. ಅವರ ಪೈಕಿ ಯಾರ ವಿರುದ್ಧ ಶಿಕ್ಷೆಯ ವಚನ ಪೂರ್ವಗತವಾಗಿದೆಯೋ ಅವರನ್ನು ಬಿಟ್ಟು. ಅಕ್ರ ಮಿಗಳ ವಿಷಯದಲ್ಲಿ ನನ್ನಲ್ಲಿ ಮಾತಾಡಬೇಡಿರಿ. ಇವರು ಖಂಡಿತ ಮುಳುಗಿ ನಾಶ ಹೊಂದಲಿ ದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(23,28,'23:28','ಅನಂತರ ನೀವು ನಿಮ್ಮ ಜೊತೆಗಾರರ ಸಹಿತ ನಾವೆಯ ಮೇಲೇರಿ ಕುಳಿತರೆ, “ಅಕ್ರಮಿ ಜನಾಂ ಗದಿಂದ ನಮ್ಮನ್ನು ಪಾರುಗೊಳಿಸಿದ ಅಲ್ಲಾಹನಿಗೆ ಸರ್ವಸ್ತುತಿಗಳು” ಎಂದು ಹೇಳಿರಿ”.');
INSERT INTO "kannada" VALUES(23,29,'23:29','“ನನ್ನ ಪ್ರಭೂ, ನನ್ನನ್ನು ಅನುಗ್ರಹ ಸಮೃದ್ಧಿ ಯಿರುವ ನಿಲ್ದಾಣವೊಂದರಲ್ಲಿ ಇಳಿಸು, ನೀನು ಇಳಿಸಿ ಕೊಡುವವರಲ್ಲಿ ಅತ್ಯಂತ ಉತ್ತಮನು” ಎಂದು ಪ್ರಾರ್ಥಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(23,30,'23:30','ಈ ಘಟನೆಯಲ್ಲಿ ದೊಡ್ಡ ನಿದರ್ಶನಗಳಿವೆ. ನಾವು ಮನುಷ್ಯರನ್ನು ಪರೀಕ್ಷಿಸಿ ನೋಡಿಯೇ ತೀರುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(23,31,'23:31','ನಂತರ ಅವರ ಬಳಿಕ ಇನ್ನೊಂದು ಜನಾಂಗ ವನ್ನು ನಾವು ನಿರ್ಮಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(23,32,'23:32','ಆಗ ಅವರಿಂದಲೇ ಒಬ್ಬ ಸಂದೇಶವಾಹಕರನ್ನು ಅವರಿಗೆ ನಾವು ಕಳುಹಿಸಿದೆವು. (ಅವರು ಹೇಳಿದರು): “ನೀವು ಅಲ್ಲಾಹನನ್ನು ಆರಾಧಿಸಿರಿ. ಅವನ ಹೊರತು ಇನ್ನಾವ ಆರಾಧ್ಯನೂ ನಿಮಗಿಲ್ಲ. ಹೀಗಿರುತ್ತ ನೀವು ಅವನನ್ನು ಭಯಪಡುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(23,33,'23:33','ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸಿದ, ಪರಲೋಕದ ಸಂದರ್ಶ ನವನ್ನು ಸುಳ್ಳಾಗಿಸಿದ, ಲೌಕಿಕ ಜೀವನದಲ್ಲಿ ನಮ್ಮ ಸುಖ ಸವಲತ್ತುಗಳನ್ನು ಅನುಭವಿಸಿದ ಆ ಜನಾಂಗದ ಸರದಾರರು (ಹೀಗೆ) ಹೇಳಿದರು. “ಈತನು ನಿಮ್ಮಂತೆಯೇ ಇರುವ ಒಬ್ಬ ಕೇವಲ ಮನುಷ್ಯನೇ ಹೊರತು ಬೇರೇನಲ್ಲ. ನೀವು ತಿನ್ನು ವುದನ್ನೇ ಇವನೂ ತಿನ್ನುತ್ತಿದ್ದಾನೆ. ನೀವು ಕುಡಿ ಯುವುದನ್ನೇ ಇವನೂ ಕುಡಿಯುತ್ತಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(23,34,'23:34','ನಿಮ್ಮಂತೆಯೇ ಇರುವ ಒಬ್ಬ ಮನುಷ್ಯನಿಗೆ ನೀವು ವಿಧೇಯರಾದರೆ ನೀವು ದೇವರಾಣೆಗೂ ನಷ್ಟಕ್ಕೊಳಗಾಗುವಿರಿ.');
INSERT INTO "kannada" VALUES(23,35,'23:35','ನೀವು ಸತ್ತು ಮಣ್ಣಾಗಿ, ಅಸ್ಥಿಪಂಜರವಾಗಿ ಬಿಟ್ಟ ಬಳಿಕ ಪುನಃ ನಿಮ್ಮನ್ನು ಹೊರ ತೆಗೆಯ ಲಾಗುವುದೆಂದು ಇವನು ನಿಮಗೆ ತಾಕೀತು ನೀಡುತ್ತಿರುವನೇ?');
INSERT INTO "kannada" VALUES(23,36,'23:36','ನಿಮಗೆ ನೀಡಲಾಗುತ್ತಿರುವ ಈ ತಾಕೀತು ನಿಜಗೊಳ್ಳುವುದು ಬಲು ದೂರದ ಮಾತು.');
INSERT INTO "kannada" VALUES(23,37,'23:37','ನಾವು ಸಾಯುವ ಮತ್ತು ಬದುಕುವ ಈ ಲೋಕದ ಜೀವನ ಮಾತ್ರವಲ್ಲದೆ ಬೇರೆ ಜೀವನವಿಲ್ಲ. ಖಂಡಿತವಾಗಿಯೂ ನಾವು ಯಾವತ್ತೂ ಪುನರುಜ್ಜೀವನ ಪಡೆಯುವವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(23,38,'23:38','ಈತ ಅಲ್ಲಾಹನ ಹೆಸರಲ್ಲಿ ಸುಳ್ಳು ಸೃಷ್ಟಿಸುವ ವ್ಯಕ್ತಿ. ನಾವು ಇವನನ್ನು ನಂಬುವವರಲ್ಲ”.');
INSERT INTO "kannada" VALUES(23,39,'23:39','ಸಂದೇಶವಾಹಕರು ಹೇಳಿದರು, “ನನ್ನ ಪ್ರಭೂ, ಇವರು ನನ್ನನ್ನು ಸುಳ್ಳಾಗಿಸಿರುವುದರಿಂದ ನೀನು ನನಗೆ ಸಹಾಯ ಮಾಡು.”');
INSERT INTO "kannada" VALUES(23,40,'23:40','ಅಲ್ಲಾಹು ಹೇಳಿದನು “ಸ್ವಲ್ಪ ಸಮಯದಲ್ಲೇ ಇವರು ಖೇದಕ್ಕೊಳಗಾಗಲಿದ್ದಾರೆ.”');
INSERT INTO "kannada" VALUES(23,41,'23:41','ಕೊನೆಗೆ ನ್ಯಾಯ ಪ್ರಕಾರ ಒಂದು ಭಯಂಕರ ಅಟ್ಟಹಾಸವು ಅವರನ್ನು ಬಿಗಿಹಿಡಿಯಿತು. ಆಗ ಅವರನ್ನು ನಾವು ಒಣಕಲು ಗಿಡಗಳಂತೆ ಮಾಡಿ ಬಿಟ್ಟೆವು. ಅಕ್ರಮಿ ಜನಾಂಗ (ಅಲ್ಲಾಹನ ಕಾರುಣ್ಯದಿಂದ) ತೊಲಗಲಿ!');
INSERT INTO "kannada" VALUES(23,42,'23:42','ನಂತರ ನಾವು ಅವರ ಬಳಿಕ ಬೇರೆ ಜನಾಂಗಗಳನ್ನು ಉತ್ಪಾದಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(23,43,'23:43','ಯಾವ ಜನಾಂಗವೂ ತನ್ನ ಅವಧಿಗಿಂತ ಮುಂದೆ ಹೋಗಿಲ್ಲ. ಹಿಂದೆ ಉಳಿಯಲೂ ಇಲ್ಲ .');
INSERT INTO "kannada" VALUES(23,44,'23:44','ಅನಂತರ ನಮ್ಮ ಸಂದೇಶವಾಹಕರನ್ನು ನಾವು ನಿರಂತರವಾಗಿ ಕಳುಹಿಸಿದೆವು. ಪ್ರತಿಯೊಂದು ಜನಾಂಗಕ್ಕೂ ಅದರ ಸಂದೇಶವಾಹಕರು ಬಂದಾಗಲೆಲ್ಲ ಆ ಜನಾಂಗವು ತಮ್ಮ ಸಂದೇಶ ವಾಹಕರನ್ನು ಸುಳ್ಳಾಗಿಸಿತು. ಹೀಗಾಗಿ ಒಂದರ ಹಿಂದೆ ಇನ್ನೊಂದರಂತೆ ಅವರನ್ನು ನಾವು ನಾಶ ಮಾಡಿದೆವು. ಹೀಗೆ ಅವರನ್ನು ಇತಿಹಾಸಗಳನ್ನಾಗಿ ನಾವು ಮಾಡಿದೆವು. ಸತ್ಯದಲ್ಲಿ ನಂಬಿಕೆಯಿರಿಸದ ಜನಾಂಗವು (ಅಲ್ಲಾಹನ ಕಾರುಣ್ಯ ದಿಂದ) ದೂರವಾಗಲಿ.');
INSERT INTO "kannada" VALUES(23,45,'23:45','ಅನಂತರ ಮೂಸಾ ಮತ್ತು ಅವರ ಸಹೋದರ ಹಾರೂನರನ್ನು ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳು ಹಾಗೂ ಸುವ್ಯಕ್ತ ಪ್ರಮಾಣ ಸಹಿತ ನಾವು ಕಳುಹಿಸಿದೆವು . .');
INSERT INTO "kannada" VALUES(23,46,'23:46','ಫಿರ್‍ಔನ್ ಹಾಗೂ ಅವನ ಆಸ್ಥಾನಿಕರ ಕಡೆಗೆ, ಆದರೆ ಅವರು ಅಹಂಕಾರ ತೋರಿದರು. ಅವರು ದಬ್ಬಾಳಿಕೆ ನಡೆಸುತ್ತಿರುವ ಜನಾಂಗವಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(23,47,'23:47','ಆಗ ಅವರು ಹೇಳಿದರು; “ನಮ್ಮಂತೆಯೇ ಇರುವ ಇಬ್ಬರು ವ್ಯಕ್ತಿಗಳ ಮೇಲೆ ನಾವು ವಿಶ್ವಾಸ ವಿರಿಸಬೇಕೆ? ಅದರಲ್ಲೂ, ಅವರಿಬ್ಬರ ಜನಾಂಗವು ನಮ್ಮ ಉಪಾಸಕರಾಗಿರುವಾಗ? ');
INSERT INTO "kannada" VALUES(23,48,'23:48','ಹಾಗೆ ಇಬ್ಬರನ್ನು ಅವರು ಸುಳ್ಳಾಗಿಸಿಬಿಟ್ಟರು. ಹೀಗಾಗಿ ಅವರು ನಾಶ ಹೊಂದುವವರಲ್ಲಿ ಸೇರಿ ಹೋದರು.');
INSERT INTO "kannada" VALUES(23,49,'23:49','ಅವರು ಸನ್ಮಾರ್ಗದರ್ಶನ ಪಡೆಯಲೆಂದು ಮೂಸಾರಿಗೆ ನಾವು ವೇದ ಗ್ರಂಥವನ್ನು ದಯ ಪಾಲಿಸಿದೆವು .');
INSERT INTO "kannada" VALUES(23,50,'23:50','ಮರ್ಯಮರ ಪುತ್ರ (ಈಸಾ) ಮತ್ತು ಅವರ ಮಾತೆಯನ್ನು ನಾವು ಒಂದು ದೃಷ್ಟಾಂತವನ್ನಾಗಿ ಮಾಡಿದೆವು. ಅವರಿಬ್ಬರಿಗೂ ವಾಸ ಯೋಗ್ಯ, ನೀರು ಹರಿಯುವ, ಒಂದು ಎತ್ತರದ ಭೂಮಿಯಲ್ಲಿ ನಾವು ಅಭಯ ನೀಡಿದೆವು.');
INSERT INTO "kannada" VALUES(23,51,'23:51','ಸಂದೇಶವಾಹಕರುಗಳೇ! ಶುದ್ಧ ವಸ್ತುಗಳಿಂದ ನೀವು ಉಣ್ಣಿರಿ. ಸತ್ಕರ್ಮವನ್ನು ಮಾಡಿರಿ. ನೀವು ಮಾಡುವುದನ್ನು ನಾನು ಚೆನ್ನಾಗಿ ಅರಿಯುತ್ತೇನೆ .');
INSERT INTO "kannada" VALUES(23,52,'23:52','ನಿಶ್ಚಯವಾಗಿಯೂ ಇದು ನಿಮ್ಮ ಧರ್ಮ - ಏಕೈಕ ಧರ್ಮ. ನಾನು ನಿಮ್ಮ ಪ್ರಭುವಾಗಿರುತ್ತೇನೆ. ಆದುದರಿಂದ ನನ್ನನ್ನು ಭಯಪಡಿರಿ.');
INSERT INTO "kannada" VALUES(23,53,'23:53','ಆದರೆ ಜನರು ತಮ್ಮತಮ್ಮಲ್ಲಿ ಹರಿಹಂಚಾಗಿ ತಮ್ಮ ಧರ್ಮವನ್ನು ತುಂಡು ಮಾಡಿಬಿಟ್ಟರು. ಪ್ರತಿಯೊಂದು ವರ್ಗವೂ ತನ್ನ ಧರ್ಮತುಂಡು ಗಳಿಂದ ಹರ್ಷಗೊಂಡಿತು.');
INSERT INTO "kannada" VALUES(23,54,'23:54','ಆದ್ದರಿಂದ ಒಂದು ನಿಶ್ಚಿತ ಅವಧಿಯವರೆಗೆ ಅವರನ್ನು ತಮ್ಮ ದುರ್ಮಾರ್ಗದಲ್ಲಿ ಬಿಟ್ಟುಬಿಡಿರಿ.');
INSERT INTO "kannada" VALUES(23,55,'23:55','ಧನ - ಸಂತಾನಗಳ ಮೂಲಕ ನಾವು ಇವರಿಗೆ ಸಹಾಯ ಮಾಡುತ್ತಿರುವುದನ್ನು ಅವರು ಭಾವಿಸಿದ್ದಾರೆಯೇ?');
INSERT INTO "kannada" VALUES(23,56,'23:56','ಒಳಿತುಗಳನ್ನು ದೃತಿಯಿಂದ ಮಾಡಿಕೊಡಲಾಗುತ್ತಿದೆಯೆಂದು. ಆದರೆ ಅವರು (ವಾಸ್ತವಿಕತೆಯನ್ನು) ಗ್ರಹಿಸುತ್ತಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(23,57,'23:57','ನಿಜದಲ್ಲಿ ತಮ್ಮ ಪ್ರಭುವಿನ ಭಯದಿಂದ ನಡುಗುವವರು,');
INSERT INTO "kannada" VALUES(23,58,'23:58','ತಮ್ಮ ಪ್ರಭುವಿನ ದೃಷ್ಟಾಂತಗಳಲ್ಲಿ ವಿಶ್ವಾಸ ವಿರಿಸುವವರು,');
INSERT INTO "kannada" VALUES(23,59,'23:59','ತಮ್ಮ ಪ್ರಭುವಿಗೆ ಯಾರನ್ನೂ ಭಾಗೀದಾರರಾಗಿ ಮಾಡದವರು.');
INSERT INTO "kannada" VALUES(23,60,'23:60','ದಾನ ಕೊಡುವುದೇನಿದ್ದರೂ ತಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಗೆ ಮರಳಲಿಕ್ಕಿದೆಯೆಂಬ ಪ್ರಜ್ಞೆಯಿಂದ ಹೃದಯ ಕಂಪಿಸುತ್ತಿರುವ ಸ್ಥಿತಿಯಲ್ಲಿ ಕೊಡುವವರು .');
INSERT INTO "kannada" VALUES(23,61,'23:61','ಅವರೇ ಒಳಿತುಗಳ ಕಡೆಗೆ ಧಾವಂತಪಡುವವರು ಮತ್ತು ಅದರೆಡೆಗೆ ಇತರರಿಗಿಂತ ಸ್ಪರ್ಧಿಸಿ ಮುನ್ನ ಡೆಯುವವರು.');
INSERT INTO "kannada" VALUES(23,62,'23:62','ನಾವು ಯಾವ ವ್ಯಕ್ತಿಗೂ ಅವನ ಶಕ್ತಿಗೆ ಮೀರಿ ದ್ದನ್ನು ಹೇರುವುದಿಲ್ಲ. ನಮ್ಮ ಬಳಿಯಲ್ಲಿ ಸತ್ಯ ಸಂಧವಾಗಿ ಮಾತಾಡುವ ಒಂದು ಗ್ರಂಥವಿದೆ . ಜನರ ಮೇಲೆ ಒಂದಿಷ್ಟೂ ಅಕ್ರಮವೆಸಗಲಾಗದು.');
INSERT INTO "kannada" VALUES(23,63,'23:63','ಆದರೆ ಇವರ ಹೃದಯಗಳು ಈ ವಿಷಯದ ಬಗ್ಗೆ ಅನಾಸ್ಥೆ ತಾಳಿವೆ. ಇವರ ಕರ್ಮಗಳು (ಮೇಲೆ ಹೇಳಲಾಗಿರುವ) ಆ ಕ್ರಮದಿಂದ ಹೊರತಾಗಿವೆ. ಅದನ್ನು ಅವರು ಮಾಡುತ್ತಲೇ ಇರುವರು .');
INSERT INTO "kannada" VALUES(23,64,'23:64','ನಾವು ಅವರ ಪೈಕಿ ಸುಖಲೋಲುಪರನ್ನು ಶಿಕ್ಷೆಯಲ್ಲಿ ಹಿಡಿದಾಗ ಅವರು ರೋಧಿಸುವರು .');
INSERT INTO "kannada" VALUES(23,65,'23:65','(ಆಗ ಹೇಳಲಾಗುವುದು;) ಇಂದು ನೀವು ರೋಧಿಸಿ ಫಲವಿಲ್ಲ. ನಿಮಗೆ ನಮ್ಮಿಂದ ಯಾವ ಸಹಾಯವೂ ಖಂಡಿತ ಸಿಗಲಾರದು.');
INSERT INTO "kannada" VALUES(23,66,'23:66','(ಏಕೆಂದರೆ) ನನ್ನ ವಚನಗಳನ್ನು ನಿಮಗೆ ಓದಿ ಹೇಳಲಾಗುತ್ತಿದ್ದಾಗ ನೀವು ಹಿಂದಿರುಗಿ ಓಡುತ್ತಿದ್ದಿರಿ.');
INSERT INTO "kannada" VALUES(23,67,'23:67','ಅದರ (ಕಅïಬಾ ಭವನದ) ಬಗ್ಗೆ ಮೇಲ್ಮೆ ತೋರುತ್ತ, ರಾತ್ರಿ ಹೊತ್ತು ಪಟ್ಟಾಂಗ ಹೊಡೆಯುತ್ತ ಖುರ್‍ಆನನ್ನು ಕೈಬಿಡುತ್ತಿದ್ದೀರಿ .');
INSERT INTO "kannada" VALUES(23,68,'23:68','(ಖುರ್‍ಆನ್) ವಚನದ ಬಗ್ಗೆ ಇವರು ಯೋಚಿಸಲಿಲ್ಲವೆ? ಅಥವಾ ಇವರ ಪೂರ್ವಜರ ಬಳಿಗೆ ಬಂದಿರದಂತಹ ವಿಷಯವೇನಾದರೂ ಇವರಿಗೆ ಬಂದಿದೆಯೇ?');
INSERT INTO "kannada" VALUES(23,69,'23:69','ಅಥವಾ ಇವರಿಗೆ ತಮ್ಮ ಸಂದೇಶವಾಹಕರ ಸರಿಯಾದ ಪರಿಚಯವಿಲ್ಲದ ಕಾರಣದಿಂದ ಅವರನ್ನು ನಿರಾಕರಿಸುತ್ತಿರುವರೇ?');
INSERT INTO "kannada" VALUES(23,70,'23:70','ಅಥವಾ ಅವರಿಗೆ ಹುಚ್ಚು ಹಿಡಿದಿದೆ ಎಂದು ಇವರು ಹೇಳುತ್ತಿರುವರೇ? ಆದರೆ, ಅವರು ಇವರಿಗೆ ಸತ್ಯ ವನ್ನೇ ತಂದಿದ್ದಾರೆ. ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಸತ್ಯವನ್ನು ಅಸಹ್ಯಪಡುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(23,71,'23:71','ಈ ಸತ್ಯವು ಅವರ ತನ್ನಿಚ್ಚೆಗಳನ್ನು ಹಿಂಬಾಲಿ ಸುತ್ತಿದ್ದರೆ ಭೂಮಿ-ಆಕಾಶಗಳು ಮತ್ತು ಅವುಗಳ ಲ್ಲಿರುವವರು ಹದಗೆಡುತ್ತಿದ್ದರು . ಆದರೆ ನಾವು ಅವರಿಗಿರುವ ಉಪದೇಶ (ಖುರ್‍ಆನ್) ವನ್ನು ಅವರ ಬಳಿಗೆ ತಂದಿದ್ದೇವೆ. ಆಗ ಅವರು ತಮ್ಮ ಉಪದೇಶದಿಂದ ವಿಮುಖರಾದರು.');
INSERT INTO "kannada" VALUES(23,72,'23:72','ಅಥವಾ ನೀವು ಅವರಿಂದೇನಾದರೂ ಪ್ರತಿಫಲವನ್ನು ಕೇಳುತ್ತಿರುವಿರಾ? ಆದರೆ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಪ್ರತಿ ಫಲವೇ ಶ್ರೇಷ್ಟವಾಗಿದೆ ಮತ್ತು ಕೊಡುವವರಲ್ಲಿ ಅವನೇ ಅತ್ಯುತ್ತಮನು.');
INSERT INTO "kannada" VALUES(23,73,'23:73','ಖಂಡಿತ ನೀವು ಅವರನ್ನು ಸನ್ಮಾರ್ಗದ ಕಡೆಗೆ ಕರೆಯುತ್ತಿರುವಿರಿ.');
INSERT INTO "kannada" VALUES(23,74,'23:74','ಆದರೆ, ಪರಲೋಕದಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸದವರು ಸನ್ಮಾರ್ಗದಿಂದ ಖಂಡಿತ ತಪ್ಪಿ ಹೋಗುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(23,75,'23:75','ಇವರಿಗೆ ನಾವು ದಯೆ ತೋರಿದರೆ ಹಾಗೂ ಇವರಿಗೆ ಬಾಧಿಸಿದ ಸಂಕಷ್ಟವನ್ನು ದೂರೀಕರಿ ಸಿದರೆ ಇವರು ತಮ್ಮ ಅತಿಕ್ರಮದಲ್ಲಿ ಅಂಧವಾಗಿ ವಿಹರಿಸುತ್ತ ಅಂಡಲೆಯುವರು.');
INSERT INTO "kannada" VALUES(23,76,'23:76','ಶಿಕ್ಷೆಯ ಮೂಲಕ ನಾವು ಇವರನ್ನು ಹಿಡಿದು ಬಿಟ್ಟೆವು. ಆದರೂ ಇವರು ತಮ್ಮ ಪ್ರಭುವಿಗೆ ಮಣಿ ಯಲಿಲ್ಲ ಮತ್ತು ಅವನ ಮೊರೆ ಹೋಗಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(23,77,'23:77','ಕಠಿಣ ಶಿಕ್ಷೆಯ ಬಾಗಿಲನ್ನು ಅವರ ಮೇಲೆ ನಾವು ತೆರೆದು ಬಿಟ್ಟಾಗ, ಇವರು ಅದರಲ್ಲಿ (ಪ್ರತಿಯೊಂದು ಶುಭದ ಬಗ್ಗೆ) ನಿರಾಶರಾಗಿ ಬಿಡುವರು .');
INSERT INTO "kannada" VALUES(23,78,'23:78','ಅವನೇ ನಿಮಗೆ ಶ್ರವಣ, ದೃಷ್ಟಿ ಮತ್ತು ಹೃದಯವನ್ನು ಸೃಷ್ಟಿಸಿ ಕೊಟ್ಟವನು. ಆದರೆ ನೀವು ಕೃತಜ್ಞರಾಗುವುದು ಬಹಳ ಕಡಿಮೆ.');
INSERT INTO "kannada" VALUES(23,79,'23:79','ಅವನೇ ನಿಮ್ಮನ್ನು ಭೂಮಿಯ ಮೇಲೆ ಸೃಷ್ಟಿಸಿ ಹರಡಿದವನು. ಅವನ ಕಡೆಗೇ ನೀವು ಒಟ್ಟುಗೂಡಿಸಲ್ಪಡುವಿರಿ.');
INSERT INTO "kannada" VALUES(23,80,'23:80','ಅವನೇ ಜೀವ ನೀಡುತ್ತಾನೆ ಮತ್ತು ಅವನೇ ಮರಣ ಕೊಡುತ್ತಾನೆ. ಹಗಲು ರಾತ್ರಿಯ ಮಾರ್ಪಾ ಡು ಅವನ ಆಜ್ಞೆ ಪ್ರಕಾರವಿದೆ. ಹೀಗಿದ್ದು ನೀವೇಕೆ ಚಿಂತಿಸುವುದಿಲ್ಲ?');
INSERT INTO "kannada" VALUES(23,81,'23:81','ಆದರೆ ಇವರು ಇವರ ಪೂರ್ವಜರು ಹೇಳಿದಂ ತೆಯೇ ಹೇಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(23,82,'23:82','ಇವರು ಹೇಳುತ್ತಾರೆ; “ನಾವು ಸತ್ತು ಮಣ್ಣು ಮತ್ತು ಅಸ್ಥಿಯಾಗಿ ಮಾರ್ಪಟ್ಟ ಬಳಿಕ ನಮ್ಮನ್ನು ಪುನಃ ಜೀವಂತಗೊಳಿಸಿ ಎಬ್ಬಿಸಲಾದೀತೇ?');
INSERT INTO "kannada" VALUES(23,83,'23:83','ನಮಗಿಂತ ಮುಂಚೆ ನಮ್ಮ ಪೂರ್ವಜರಲ್ಲೂ ಇದನ್ನೇ ವಾಗ್ದಾನ ಮಾಡಲಾಗಿತ್ತು. ಇವೆಲ್ಲ ಪೂರ್ವಿಕರ ಕಟ್ಟುಕತೆಗಳಲ್ಲದೆ ಬೇರೇನೂ ಅಲ್ಲ”.');
INSERT INTO "kannada" VALUES(23,84,'23:84','(ಪ್ರವಾದಿಯರೇ) ಕೇಳಿರಿ; “ನೀವು ಬಲ್ಲವರಾಗಿ ದ್ದರೆ ಈ ಭೂಮಿ ಮತ್ತು ಇದರಲ್ಲಿರುವವರು ಯಾರದ್ದು?');
INSERT INTO "kannada" VALUES(23,85,'23:85','ಅವರು `ಅಲ್ಲಾಹನದು’ ಎನ್ನುವರು. “ಹಾಗಾದರೆ ನೀವೇಕೆ ಚಿಂತಿಸುವುದಿಲ್ಲ?” ಎಂದು ಕೇಳಿರಿ.');
INSERT INTO "kannada" VALUES(23,86,'23:86','ಕೇಳಿರಿ; ‘ಏಳು ಆಕಾಶಗಳ ಮತ್ತು ಮಹಾ ಅರ್ಶ್‍ನ ಒಡೆಯನು ಯಾರು?’');
INSERT INTO "kannada" VALUES(23,87,'23:87','ಅವರು `ಅಲ್ಲಾಹನು’ ಎನ್ನುವರು. ಕೇಳಿರಿ; ನೀವು ಭಯಪಡುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(23,88,'23:88','ಕೇಳಿರಿ, “ಎಲ್ಲ ವಸ್ತುವಿನ ಪ್ರಭುತ್ವವು ಯಾರ ಕೈಯಲ್ಲಿದೆ? ಅವನು ಆಶ್ರಯ ನೀಡುತ್ತಾನೆ. ಅವನಿಗೆ ಆಶ್ರಯ ನೀಡಲಾಗದು. ನೀವು ತಿಳಿದವರಾಗಿದ್ದರೆ ಹೇಳಿರಿ.”');
INSERT INTO "kannada" VALUES(23,89,'23:89','ಅವರು ‘ಅಲ್ಲಾಹನದ್ದು’ ಎಂದು ಹೇಳುವರು . “ಮತ್ತೆ ಹೇಗೆ ನೀವು ಮೋಸ ಹೋಗುವಿರಿ?” ಎಂದು ಕೇಳಿರಿ .');
INSERT INTO "kannada" VALUES(23,90,'23:90','ಅಲ್ಲ, ಸತ್ಯವನ್ನು ನಾವು ಅವರ ಮುಂದೆ ತಂದಿದ್ದೇವೆ. ಆದರೆ ಅವರು ಸುಳ್ಳುಗಾರರು .');
INSERT INTO "kannada" VALUES(23,91,'23:91','ಅಲ್ಲಾಹನು ಮಕ್ಕಳನ್ನು ಮಾಡಿಲ್ಲ. ಅವನ ಜತೆ ಬೇರೆ ದೇವನೂ ಇಲ್ಲ. ಇರುತ್ತಿದ್ದರೆ ಪ್ರತಿಯೊಬ್ಬ ದೇವನೂ ತನ್ನ ಸೃಷ್ಟಿಯನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿ ಬೇರೆ ನಿಲ್ಲುತ್ತಿದ್ದನು. ಆ ಬಳಿಕ ಅವರು ಒಬ್ಬರ ಮೇಲೆ ಇನ್ನೊಬ್ಬರು ದಂಡೆತ್ತಿ ಬರುತ್ತಿದ್ದರು . ಇವರು ನೀಡುವ ವರ್ಣನೆಗಳಿಂದ ಅಲ್ಲಾಹು ಪರಿಶುದ್ಧನು.');
INSERT INTO "kannada" VALUES(23,92,'23:92','ಅವನು ದೃಶ್ಯ - ಅದೃಶ್ಯಗಳ ಜ್ಞಾನಿ. ಇವರು ಕಲ್ಪಿಸುತ್ತಿರುವ ಬಹುದೇವತ್ವದಿಂದ ಅವನು ಮೀರಿ ನಿಂತವನು.');
INSERT INTO "kannada" VALUES(23,93,'23:93','(ದೂತರೇ,) ಹೀಗೆ ಪ್ರಾರ್ಥಿಸಿರಿ, “ನನ್ನ ಪ್ರಭೂ, ಇವರಿಗೆ ಎಚ್ಚರಿಕೆ ನೀಡಲಾಗುತ್ತಿರುವ ಶಿಕ್ಷೆಯನ್ನು ನೀನು ಒಂದು ವೇಳೆ ನನ್ನ ಕಣ್ಣೆದುರಲ್ಲೇ ಕಾಣಿಸುವುದಾದರೆ,');
INSERT INTO "kannada" VALUES(23,94,'23:94','ನನ್ನ ಪ್ರಭೂ, ನನ್ನನ್ನು ಅಕ್ರಮಿ ಜನಾಂಗದಲ್ಲಿ ಸೇರಿಸದಿರು” .');
INSERT INTO "kannada" VALUES(23,95,'23:95','ಖಂಡಿತ ನಾವು ಅವರಿಗೆ ಎಚ್ಚರಿಕೆ ನೀಡುತ್ತಿರುವುದನ್ನು ನಿಮಗೆ ತೋರಿಸಿಕೊಡುವುದಕ್ಕೆ ಸಂಪೂರ್ಣ ಸಮರ್ಥರು.');
INSERT INTO "kannada" VALUES(23,96,'23:96','(ದೂತರೇ,) ಕೆಡುಕನ್ನು ಅತ್ಯುತ್ತಮ ವಿಧಾನದಿಂದ ನೀಗಿಸಿರಿ . ಅವರು ವರ್ಣಿಸಿ ಹೇಳುತ್ತಿರುವುದರ ಕುರಿತು ನಾವು ಚೆನ್ನಾಗಿ ಬಲ್ಲೆವು.');
INSERT INTO "kannada" VALUES(23,97,'23:97','ಹೀಗೆ ಪ್ರಾರ್ಥಿಸಿರಿ, “ಪ್ರಭೂ, ಶೈತಾನರ ಕೆಟ್ಟ ಆಮಿಷಗಳಿಂದ ನಾನು ನಿನ್ನ ಅಭಯ ಯಾಚಿಸುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(23,98,'23:98','ಪ್ರಭೂ, ಅವರು ನನ್ನ ಬಳಿ ಹಾಜರಾಗುವುದರಿಂದಲೂ ನಾನು ನಿನ್ನ ಅಭಯ ಯಾಚಿಸುತ್ತೇನೆ” .');
INSERT INTO "kannada" VALUES(23,99,'23:99','ಇವರಲ್ಲಿ ಯಾರಿಗಾದರೂ ಮರಣ ಬಂದಾಗ, “ನನ್ನ ಪ್ರಭೂ, (ಇಹ ಜೀವನಕ್ಕೆ) ನನ್ನನ್ನು ಮರಳಿಸಿ ಬಿಡು.');
INSERT INTO "kannada" VALUES(23,100,'23:100','ನಾನು ವ್ಯರ್ಥಗೊಳಿಸಿದುದರ ಬದಲಿಗೆ ಸತ್ಕರ್ಮವನ್ನು ಮಾಡಬಲ್ಲೆನು”, ಆದರೆ ಎಷ್ಟು ಮಾತ್ರಕ್ಕೂ ಮರಳಿಸಲಾಗದು. ಇದು ಕೇವಲ ಅವನಾಡುವ ಒಂದು ಮಾತು ಮಾತ್ರ. ಅವರನ್ನು ಪುನರುಜ್ಜೀವಗೊಳಿಸುವ ದಿನದವರೆಗೆ ಅವರ ಹಿಂದೆ ಬಲವಾದ ತಡೆ ಇದೆ .');
INSERT INTO "kannada" VALUES(23,101,'23:101','ಕಹಳೆ ಊದಲ್ಪಟ್ಟಾಗ ಅಂದು ಅವರ ನಡುವೆ ಯಾವುದೇ ರಕ್ತ ಸಂಬಂಧವಿಲ್ಲ. ಅವರು ಪರಸ್ಪರ ವಿಚಾರಿಸಿಕೊಳ್ಳಲಾರರು.');
INSERT INTO "kannada" VALUES(23,102,'23:102','ಆಗ ಯಾರ ಸತ್ಕರ್ಮದ ತೂಕವು ಭಾರವಾಗಿರುವುದೋ ಅವರೇ ವಿಜಯಿ ಗಳಾಗುವರು.');
INSERT INTO "kannada" VALUES(23,103,'23:103','ಯಾರ ಸತ್ಕರ್ಮದ ತಟ್ಟೆ ಹಗುರವಿರುವುದೋ ಅವರು ತಮ್ಮನ್ನು ತಾವೇ ನಷ್ಟಕ್ಕೀಡು ಮಾಡಿ ದರು. ಅವರು ನರಕದಲ್ಲಿ ಶಾಶ್ವತರು.');
INSERT INTO "kannada" VALUES(23,104,'23:104','ನರಕಾಗ್ನಿಯು ಅವರ ಮುಖವನ್ನೇ ಸುಟ್ಟು ಹಾಕುವುದು . ಅವರ ಮೇಲ್ದುಟಿ ಮೇಲೇರಿ ಹಲ್ಲು ಕಿಸಿಯುವ ವಿಕಾರತೆಯಲ್ಲಿರುವರು .');
INSERT INTO "kannada" VALUES(23,105,'23:105','“ನನ್ನ ವಚನಗಳನ್ನು ನಿಮಗೆ ಓದಿ ಹೇಳಿದಾಗ ಅದನ್ನು ನೀವು ಸುಳ್ಳಾಗಿಸುತ್ತಿದ್ದೀರಲ್ಲವೇ?” (ಎಂದು ಕೇಳಲಾಗುವುದು)');
INSERT INTO "kannada" VALUES(23,106,'23:106','ಆಗ ಅವರು, “ನಮ್ಮ ಪ್ರಭೂ, ನಮ್ಮ ದುರದೃಷ್ಟವು ನಮ್ಮ ಮೇಲೆ ಪ್ರಾಬಲ್ಯ ಸಾಧಿಸಿತು. ನಾವು ದಾರಿ ತಪ್ಪಿದ ಜನಾಂಗವಾಗಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(23,107,'23:107','ನಮ್ಮ ಪ್ರಭೂ, ನಮ್ಮನ್ನು ಇಲ್ಲಿಂದ ಹೊರ ಹಾಕು. ಇನ್ನು ನಾವು ತಪ್ಪು ದಾರಿಗೆ ಮರಳಿದರೆ ಅಕ್ರಮಿಗಳಾಗುವೆವು” ಎನ್ನುವರು.');
INSERT INTO "kannada" VALUES(23,108,'23:108','ಆಗ “ಇಲ್ಲೇ ನಿಂದ್ಯರಾಗಿ ಬಿದ್ದು ಕೊಂಡಿರಿ. ನನ್ನೊಡನೆ ಮಾತನಾಡಬೇಡಿರಿ.” (ಎಂದು ಅಲ್ಲಾಹನು ಹೇಳುವನು.)');
INSERT INTO "kannada" VALUES(23,109,'23:109','ನನ್ನ ದಾಸರಲ್ಲಿ ಒಂದು ವಿಭಾಗ, “ನಮ್ಮ ಪ್ರಭೂ, ನಾವು ವಿಶ್ವಾಸವಿರಿಸಿದ್ದೇವೆ. ಆದ್ದರಿಂದ ನಮ್ಮನ್ನು ಕ್ಷಮಿಸು. ನಮಗೆ ದಯೆ ತೋರು. ನೀನು ಅತ್ಯುತ್ತಮ ದಯಾವಂತನು ಎಂದು ಹೇಳುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(23,110,'23:110','ಆಗ ನೀವು ಅವರನ್ನು ಅಪಹಾಸ್ಯ ಮಾಡಿದಿರಿ, ಎಷ್ಟರವರೆಗೆಂದರೆ ಅವರ ಅಪಹಾಸ್ಯ ಮಾಡುವ ಕೆಲಸವು ನಿಮಗೆ ನನ್ನ ಸ್ಮರಣೆಯನ್ನೇ ಮರೆಯುವಂತೆ ಮಾಡಿತು. ಅವರನ್ನು ಕಂಡು ನೀವು ನಗುತ್ತಿದ್ದೀರಿ.');
INSERT INTO "kannada" VALUES(23,111,'23:111','ನಾನು ಅವರಿಗೆ ಉತ್ತಮ ಪ್ರತಿಫಲ ಕೊಟ್ಟಿರು ವೆನು. ಅವರು ಸಹನೆ ಪಾಲಿಸಿದ್ದಕ್ಕಾಗಿ, ನಿಜವಾಗಿಯೂ ಅವರು ಜಯಶಾಲಿಗಳು.');
INSERT INTO "kannada" VALUES(23,112,'23:112','ಅಲ್ಲಾಹನು ಅವರೊಡನೆ; “ಭೂಮಿಯಲ್ಲಿ ನೀವು ಎಷ್ಟು ವರ್ಷ ವಾಸಿಸಿದಿರಿ?” ಎಂದು ಕೇಳುವನು.');
INSERT INTO "kannada" VALUES(23,113,'23:113','ಅವರು “ಒಂದು ದಿನ ಅಥವಾ ದಿನದ ಸ್ವಲ್ಪ ಸಮಯ ನಾವು ಅಲ್ಲಿ ವಾಸಿಸಿದ್ದೆವು. ಎಣಿಕೆ ಮಾಡುವವರನ್ನು ಕೇಳಿ ನೋಡು” ಎನ್ನುವರು.');
INSERT INTO "kannada" VALUES(23,114,'23:114','ಅವನು ಹೇಳುವನು, `ನೀವು (ಭೂಲೋಕದಲ್ಲಿ) ಸ್ವಲ್ಪ ಸಮಯ ಮಾತ್ರ ತಂಗಿದ್ದೀರಿ. ನೀವು ಇದನ್ನು ಆಗ ತಿಳಿದಿರುತ್ತಿದ್ದರೆ! (ಈ ದುರವಸ್ಥೆ ಬರುತ್ತಿರಲಿಲ್ಲ) .');
INSERT INTO "kannada" VALUES(23,115,'23:115','ನಾವು ನಿಮ್ಮನ್ನು ನಿರರ್ಥಕವಾಗಿ ಸೃಷ್ಟಿಸಿದ್ದೇ ವೆಂದೂ ನಿಮಗೆ ನಮ್ಮ ಕಡೆಗೆ ಮರಳಲಿಕ್ಕೆ ಇಲ್ಲವೆಂದೂ ಭಾವಿಸಿದ್ದೀರಾ?”');
INSERT INTO "kannada" VALUES(23,116,'23:116','ಅಲ್ಲಾಹನು ಪರಮೋನ್ನತನು, ಯಥಾರ್ಥ ಸಾಮ್ರಾಟನು. ಅವನ ಹೊರತು ಬೇರೆ ದೇವನಿಲ್ಲ. ಗೌರವಾನ್ವಿತ ಅರ್ಶ್‍ನ ಒಡೆಯನು.');
INSERT INTO "kannada" VALUES(23,117,'23:117','ಯಾವನು ಅಲ್ಲಾಹನ ಜೊತೆಗೆ ಬೇರೆ ಆರಾಧ್ಯ ನನ್ನು ಆರಾಧಿಸುತ್ತಾನೋ, ಆತನ ಬಳಿ ಅದಕ್ಕೆ ಪುರಾವೆ ಇಲ್ಲ . ಅವನ ಪ್ರಭುವಿನ ಬಳಿಯೇ ಆತನ ಲೆಕ್ಕಾಚಾರವಿದೆ. ಸತ್ಯನಿಷೇಧಿಗಳು ಖಂಡಿತ ಯಶಸ್ವಿಯಾಗಲಾರರು.');
INSERT INTO "kannada" VALUES(23,118,'23:118','(ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ; “ನನ್ನ ಪ್ರಭು, ನೀನು ಕ್ಷಮಿಸು ಮತ್ತು ಕರುಣೆ ತೋರು; ನೀನು ಕರುಣಾಳುಗಳಲ್ಲಿ ಅತ್ಯಂತ ಉತ್ತಮನಾಗಿರುವಿ.');
INSERT INTO "kannada" VALUES(24,1,'24:1','ಇದೊಂದು ಮಹತ್ತರ ಅಧ್ಯಾಯ, ಇದನ್ನು ನಾವು ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದೇವೆ, ಇದರ ವಿಧಿ ನಿಯಮಗಳನ್ನು ಕಡ್ಡಾಯಗೊಳಿಸಿದ್ದೇವೆ . ನೀವು ಯೋಚಿಸಲಿಕ್ಕಾಗಿ, ಇದರಲ್ಲಿ ನಾವು ಸುಸ್ಪಷ್ಟವಾದ ವಿವಿಧ ಆದೇಶಗಳನ್ನು ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(24,2,'24:2','ವ್ಯಭಿಚಾರಿ ಮತ್ತು ವ್ಯಭಿಚಾರಿಣಿ ಇವರಿಬ್ಬರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬರಿಗೂ ನೂರು ಛಡಿಯೇಟು ಕೊಡಿರಿ. ನೀವು ಅಲ್ಲಾಹು ಹಾಗೂ ಅಂತಿಮ ದಿವಸದಲ್ಲಿ ವಿಶ್ವಾಸವುಳ್ಳವರಾಗಿದ್ದರೆ ಅಲ್ಲಾಹನ ಕಾನೂನು ಕ್ರಮ ಕೈಗೊಳ್ಳುವ ವಿಷಯದಲ್ಲಿ ಅವರಿಬ್ಬರಲ್ಲಿ ಯಾವುದೇ ಕನಿಕರವು ನಿಮ್ಮನ್ನು ತಡೆಹಿಡಿಯದಿರಲಿ. ಅವರಿಗೆ ಶಿಕ್ಷೆ ಕೊಡುವಾಗ ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಒಂದು ಸಮೂಹ ಉಪಸ್ಥಿತವಿರಲಿ .');
INSERT INTO "kannada" VALUES(24,3,'24:3','ವ್ಯಭಿಚಾರಿಯು ವ್ಯಭಿಚಾರಿಣಿಯನ್ನೋ ಅಥವಾ ಬಹುದೇವ ವಿಶ್ವಾಸಿನಿಯನ್ನೋ ಹೊರತು ವಿವಾಹವಾಗಲಾರನು. ವ್ಯಭಿಚಾರಿಣಿಯನ್ನು ವ್ಯಭಿಚಾರಿ ಅಥವಾ ಬಹುದೈವ ವಿಶ್ವಾಸಿಯ ಹೊರತು ವಿವಾಹವಾಗಲಾರನು. ಇದನ್ನು ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳ ಮೇಲೆ ನಿಷಿದ್ಧಗೊಳಿಸಲಾಗಿದೆ .');
INSERT INTO "kannada" VALUES(24,4,'24:4','ಶೀಲವತಿಯರ ಮೇಲೆ ಹಾದರದ ಆರೋಪ ಹೊರಿಸಿ, ಆ ಬಳಿಕ ನಾಲ್ವರು ಸಾಕ್ಷಿದಾರರನ್ನು ತರದವರಿಗೆ ಎಂಬತ್ತು ಛಡಿಯೇಟು ಕೊಡಿರಿ. ಅವರ ಸಾಕ್ಷ್ಯವನ್ನು ಎಂದಿಗೂ ಸ್ವೀಕರಿಸಬೇಡಿರಿ. ಅವರೇ ಅಧರ್ಮಿಗಳು.');
INSERT INTO "kannada" VALUES(24,5,'24:5','ಅದರ ಬಳಿಕ ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು ತಮ್ಮ ಬದುಕನ್ನು ಸುಧಾರಿಸಿಕೊಂಡವರ ಹೊರತು . ಅಲ್ಲಾಹನು ಅತ್ಯಂತ ಕ್ಷಮಾಶೀಲನೂ ಪರಮ ದಯಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(24,6,'24:6','ತಮ್ಮ ಪತ್ನಿಯರ ಮೇಲೆ ವ್ಯಭಿಚಾರದ ಆರೋಪ ಹೊರಿಸುವವರು, ತಮ್ಮ ಬಳಿ ಸ್ವಯಂ ತಾವಲ್ಲದೆ ಬೇರೆ ಯಾರೂ ಸಾಕ್ಷಿದಾರರಿಲ್ಲದಿದ್ದರೆ (ಶಿಕ್ಷೆಯಿಂದ ಪಾರಾಗಬೇಕಾದರೆ) ಅವರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನು ನಾಲ್ಕು ಬಾರಿ ಅಲ್ಲಾಹನ ಆಣೆ ಹಾಕಿ ತಾನು ದೋಷಾ ರೋಪದಲ್ಲಿ ಸತ್ಯವಂತನೆಂದು ಸಾಕ್ಷ್ಯ ಹೇಳಬೇಕು.');
INSERT INTO "kannada" VALUES(24,7,'24:7','ಐದನೇ ಬಾರಿ, `ತಾನು ಸುಳ್ಳಾರೋಪಿಯಾಗಿದ್ದರೆ ತನ್ನ ಮೇಲೆ ಅಲ್ಲಾಹನ ಶಾಪವಿರಲಿ’ ಎನ್ನಬೇಕು.');
INSERT INTO "kannada" VALUES(24,8,'24:8','ಆಕೆ ನಾಲ್ಕು ಬಾರಿ ಅಲ್ಲಾಹನ ಆಣೆ ಹಾಕಿ ಈತನು ಸುಳ್ಳಾರೋಪಿಯೆಂದು ಸಾಕ್ಷ್ಯ ಹೇಳಿದರೆ ವ್ಯಭಿಚಾರದ ಶಿಕ್ಷೆಯನ್ನು ಅದು ಆಕೆಯಿಂದ ಪಾರು ಗೊಳಿಸುವುದು.');
INSERT INTO "kannada" VALUES(24,9,'24:9','ಐದನೇ ಬಾರಿ, `ಆತನು ಸತ್ಯವಂತನಾಗಿದ್ದರೆ ನನ್ನ ಮೇಲೆ ಅಲ್ಲಾಹನ ಕ್ರೋಧವೆರಗಲಿ’ ಎಂದು ಆಕೆ ಹೇಳಬೇಕು .');
INSERT INTO "kannada" VALUES(24,10,'24:10','ಅಲ್ಲಾಹನ ಔದಾರ್ಯವೂ ಅವನ ಕೃಪೆಯೂ ನಿಮ್ಮ ಮೇಲೆ ಇಲ್ಲದಿರುತ್ತಿದ್ದರೆ ಮತ್ತು ಅಲ್ಲಾಹನು ಪಶ್ಚಾತ್ತಾಪ ಸ್ವೀಕರಿಸುವವನೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರದಿರುತ್ತಿದ್ದರೆ (ಅವನು ನಿಜಸ್ಥಿತಿಯನ್ನು ಬಹಿರಂಗಗೊಳಿಸುತ್ತಿದ್ದನು ಮತ್ತು ತಪ್ಪಿತಸ್ಥರನ್ನು ತ್ವರಿತವಾಗಿ ಶಿಕ್ಷಿಸುತ್ತಿದ್ದನು.) ');
INSERT INTO "kannada" VALUES(24,11,'24:11','ಈ ಸುಳ್ಳಾರೋಪವನ್ನು ತಂದವರು ನಿಮ್ಮಿಂದಲೇ ಇರುವ ಒಂದು ಸಂಘದವರು. (ಆಯಿಶಾರ ಕುಟುಂಬವೇ!) ಇದು ನಿಮ್ಮ ಪಾಲಿಗೆ ಕೆಟ್ಟದೆಂದು ಭಾವಿಸಬೇಡಿ. ಅಲ್ಲ, ಇದು ನಿಮಗೆ ಹಿತಕರವಾಗಿದೆ. ಅವರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನು ತಾನೆಸಗಿದ ತಪ್ಪಿಗೆ ಶಿಕ್ಷೆ ಅನುಭವಿಸುವನು. ಅವರಲ್ಲಿ ಇದರ ನಾಯಕತ್ವ ವಹಿಸಿಕೊಂಡವನಾರೋ, ಆತನಿಗೆ ಉಗ್ರ ಶಿಕ್ಷೆಯುಂಟು .');
INSERT INTO "kannada" VALUES(24,12,'24:12','ನೀವು ಅದನ್ನು ಕೇಳಿದಾಗ ಸತ್ಯವಿಶ್ವಾಸಿ ಸ್ತ್ರೀ- ಪುರುಷರು ತಮ್ಮ ಬಗ್ಗೆ ಪರಸ್ಪರ ಸದ್ಭಾವನೆಯ ನ್ನೇಕೆ ಇರಿಸಲಿಲ್ಲ ಮತ್ತು ಇದು ಅಪ್ಪಟ ಸುಳ್ಳಾರೋಪವೆಂದು ಏಕೆ ಹೇಳಲಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(24,13,'24:13','ಅವರು (ಆರೋಪ ಹೊರಿಸಿದವರು) ನಾಲ್ಕು ಸಾಕ್ಷಿ ದಾರರನ್ನೇಕೆ ತರಲಿಲ್ಲ? ಆದರೆ ಅವರು ಸಾಕ್ಷಿಗಳನ್ನು ತಾರದಿರುವುದರಿಂದ ಅಲ್ಲಾಹನ ಬಳಿ ಅವರೇ ಸುಳ್ಳುಗಾರರು.');
INSERT INTO "kannada" VALUES(24,14,'24:14','ಇಹಲೋಕ ಮತ್ತು ಪರಲೋಕದಲ್ಲಿ ನಿಮ್ಮ ಮೇಲೆ ಅಲ್ಲಾಹನ ಔದಾರ್ಯವೂ ಕೃಪೆಯೂ ಇಲ್ಲದಿರುತ್ತಿ ದ್ದರೆ, ನೀವು ಹರಿಯಬಿಟ್ಟ ಸುಳ್ಳು ವದಂತಿಯ ಫಲವಾಗಿ ಭಯಂಕರ ಶಿಕ್ಷೆ ನಿಮಗೆ ತಟ್ಟುತ್ತಿತ್ತು.');
INSERT INTO "kannada" VALUES(24,15,'24:15','ನೀವದನ್ನು ನಾಲಗೆಯಿಂದ ನಾಲಗೆಗೆ ಸಾಗಿ ಸುತ್ತ ನಿಮಗೆ ಯಾವುದೇ ಅರಿವಿಲ್ಲದ ವಿಷಯ ವನ್ನು ನಿಮ್ಮ ಬಾಯಿಯಿಂದ ಹೇಳುತ್ತಿದ್ದಾಗ (ಶಿಕ್ಷೆ ಎರಗುತ್ತಿತ್ತು). ಅದನ್ನು ಒಂದು ಕ್ಷುಲ್ಲಕ ವಿಷಯವೆಂದು ನೀವು ಭಾವಿಸುತ್ತಿದ್ದೀರಿ. ಆದರೆ ಅದು ಅಲ್ಲಾಹನ ಬಳಿ ಅತ್ಯಂತ ಗಂಭೀರ ವಿಷಯವಾಗಿತ್ತು .');
INSERT INTO "kannada" VALUES(24,16,'24:16','ನೀವು ಅದನ್ನು ಕೇಳಿದಾಗ, “ನಾವು ಹೀಗೆ ಮಾತಾ ಡಿಕೊಳ್ಳುವುದು ನಮಗೆ ಭೂಷಣವಲ್ಲ. (ಓ ಅಲ್ಲಾಹ್) ನೀನು ಪರಮ ಪಾವನನು . ಇದೊಂದು ಗಂಭೀರ ಅಪವಾದವಾಗಿದೆ” ಎಂದೇಕೆ ಹೇಳಲಿಲ್ಲ?');
INSERT INTO "kannada" VALUES(24,17,'24:17','ನೀವು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಾಗಿದ್ದರೆ ಇಂತಹ ಕೆಲಸ ಇನ್ನೆಂದೂ ಮಾಡಬೇಡಿರಿ ಎಂದು ಅಲ್ಲಾಹನು ನಿಮಗೆ ಉಪದೇಶ ಕೊಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(24,18,'24:18','ಅಲ್ಲಾಹು ನಿಮಗೆ ಆದೇಶಗಳನ್ನು ಸ್ಪಷ್ಟವಾಗಿ ವಿವರಿಸಿ ಕೊಡುತ್ತಾನೆ. ಅವನು ಸರ್ವಜ್ಞನೂ ಯುಕ್ತಿ ಪೂರ್ಣನು ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(24,19,'24:19','ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ನಡುವೆ ಅಶ್ಲೀಲತೆ ಹರಡು ವುದನ್ನು ಇಷ್ಟಪಡುವವರಿಗೆ ಇಹಲೋಕದಲ್ಲೂ ಪರಲೋಕದಲ್ಲೂ ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆಯುಂಟ . ಅಲ್ಲಾಹು ಬಲ್ಲವನು; ನೀವು ಬಲ್ಲವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(24,20,'24:20','ಅಲ್ಲಾಹನ ಔದಾರ್ಯ ಮತ್ತು ಅವನ ಕೃಪೆ ನಿಮ್ಮ ಮೇಲೆ ಇಲ್ಲದಿರುತ್ತಿದ್ದರೆ ಮತ್ತು ಅಲ್ಲಾಹನು ಮಹಾವತ್ಸಲನೂ ದಯಾನಿಧಿಯೂ ಅಗಿಲ್ಲದಿರುತ್ತಿದ್ದರೆ (ಅವನು ನಿಮಗೆ ಶಿಕ್ಷೆಯನ್ನು ತ್ವರಿತಗೊಳಿಸುತ್ತಿದ್ದನು.)');
INSERT INTO "kannada" VALUES(24,21,'24:21','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಶೈತಾನನ ಹೆಜ್ಜೆ ಗುರುತು ಗಳನ್ನು ಅನುಸರಿಸಬೇಡಿರಿ. ಯಾರು ಶೈತಾನನ ಹೆಜ್ಜೆ ಗುರುತುಗಳನ್ನು ಅನುಸರಿಸುತ್ತಾರೆ, ಅವನು ಆತನಿಗೆ ನೀಚ ವೃತ್ತಿ ಹಾಗೂ ದುರಾ ಚಾರದ ಆಜ್ಞೆಯನ್ನೇ ಕೊಡುವನು . ನಿಮ್ಮ ಮೇಲೆ ಅಲ್ಲಾಹನ ಔದಾರ್ಯ ಮತ್ತು ಕೃಪೆಯಿಲ್ಲದಿರುತ್ತಿದ್ದರೆ, ನಿಮ್ಮ ಪೈಕಿ ಒಬ್ಬನೂ ಯಾವತ್ತೂ ಪರಿಶುದ್ಧನಾಗಲಾರ. ಆದರೆ ಅಲ್ಲಾಹನು ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ಪರಿಶುದ್ಧಗೊಳಿ ಸುತ್ತಾನೆ. ಅಲ್ಲಾಹು ಸರ್ವಶ್ರುತನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(24,22,'24:22','ನಿಮ್ಮಲ್ಲಿ ಶ್ರೇಷ್ಟರು ಮತ್ತು ಆರ್ಥಿಕ ಸಾಮಥ್ರ್ಯ ವುಳ್ಳವರು ತಮ್ಮ ಸಂಬಂಧಿಕರಿಗೂ ದರಿದ್ರರಿಗೂ ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ (ಸ್ವದೇಶದಿಂದ) ಪಲಾಯನ ಮಾಡುವವರಿಗೂ ಧನಸಹಾಯ ಮಾಡುವುದಿಲ್ಲವೆಂದು (ಏನನ್ನೂ ಕೊಡಲಾರೆನೆಂದು) ಪ್ರತಿಜ್ಞೆ ಮಾಡಬಾರದು. ಅವರು ಮಾಫಿ ಕೊಡಲಿ, ರಿಯಾಯಿತಿ ತೋರಲಿ. ಅಲ್ಲಾಹು ನಿಮ್ಮನ್ನು ಕ್ಷಮಿಸಬೇಕೆಂದು ನೀವು ಇಚ್ಛಿಸುವುದಿಲ್ಲವೇ? ಅಲ್ಲಾಹನು, ಅತ್ಯಂತ ಕ್ಷಮಾಶೀಲನೂ ಕೃಪಾ ನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(24,23,'24:23','ನೀಚ ವೃತ್ತಿಗಳಿಂದ ಅಶೃದ್ಧರಾದ ಪತಿವ್ರತ ಸತ್ಯ ವಿಶ್ವಾಸಿನಿಯರ ಮೇಲೆ ಹಾದರದ ಆರೋಪ ಹೊರಿಸುವವರನ್ನು ಈ ಲೋಕದಲ್ಲೂ ಪರಲೋಕದಲ್ಲೂ ಶಪಿಸಲಾಗಿದೆ. ಖಂಡಿತ ಅವರಿಗಾಗಿ ಭಯಂಕರ ಶಿಕ್ಷೆ ಇದೆ .');
INSERT INTO "kannada" VALUES(24,24,'24:24','ಅವರು ತಮ್ಮ ಕೃತ್ಯಗಳ ಬಗ್ಗೆ ತಮ್ಮ ನಾಲಗೆಗಳೂ ಕೈಕಾಲುಗಳೂ ಸಾಕ್ಷ್ಯ ಹೇಳಲಿರುವ ದಿನವದು.');
INSERT INTO "kannada" VALUES(24,25,'24:25','ಅಂದು ಅಲ್ಲಾಹನು, ಅವರಿಗೆ ನ್ಯಾಯಯುಕ್ತ ಪ್ರತಿಫಲವನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಕೊಡುವನು. ಅಲ್ಲಾಹನೇ ಸ್ಪಷ್ಟವಾದ ಪರಮಸತ್ಯನು (ನೀತಿ ಪಾಲಕನು) ಎಂಬುದು ಅವರಿಗೆ ತಿಳಿಯುವುದು.');
INSERT INTO "kannada" VALUES(24,26,'24:26','ನಡತೆಗೆಟ್ಟ ಸ್ತ್ರೀಯರು ನಡತೆಗೆಟ್ಟ ಪುರುಷರಿಗೂ, ನಡತೆಗೆಟ್ಟ ಪುರುಷರು ನಡತೆಗೆಟ್ಟ ಸ್ತ್ರೀಯರಿಗೂ ಅನುಯೋಜ್ಯರು. ಗುಣವಂತೆ ಸ್ತ್ರೀಯರು ಗುಣವಂತ ಪುರುಷರಿಗೂ, ಗುಣವಂತ ಪುರುಷರು ಗುಣವಂತೆ ಸ್ತ್ರೀಯರಿಗೂ ಅನುಯೋಜ್ಯರು . ಇವರು ಆಡುತ್ತಿರುವ ಮಾತುಗಳಿಂದ ಅವರು ಮುಕ್ತರು. ಅವರಿಗೆ ಪಾಪ ಮುಕ್ತಿಯೂ ಸನ್ಮಾನ್ಯ ಜೀವನಾಧಾರವೂ ಇದೆ.');
INSERT INTO "kannada" VALUES(24,27,'24:27','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನಿಮ್ಮ ಮನೆಗಳ ಹೊರತು ಇತರರ ಮನೆಗಳಿಗೆ ಅವರ ಒಪ್ಪಿಗೆ ಪಡೆಯದೆ ಹಾಗೂ ಮನೆಯವರಿಗೆ ಸಲಾಮ್ ಹೇಳದೆ ಪ್ರವೇಶಿಸಬೇಡಿ. ಇದೇ ನಿಮಗೆ ಉತ್ತಮ. ನಿಮಗೆ ನೆನಪಿಸಲಿಕ್ಕಾಗಿ ಇದನ್ನು ಅವತೀರ್ಣಗೊಳಿಸಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(24,28,'24:28','ಇನ್ನು (ಒಪ್ಪಿಗೆ ನೀಡಲು) ಅಲ್ಲಿ ಯಾರೂ ಕಾಣಿ ಸದಿದ್ದರೆ ನಿಮಗೆ ಅನುಮತಿ ಸಿಗುವವರೆಗೂ ಅದರೊಳಗೆ ಪ್ರವೇಶಿಸಬೇಡಿರಿ. ಮರಳಿ ಹೋಗಿರೆಂದು ನಿಮ್ಮೊಡನೆ ಹೇಳಲಾದರೆ ಮರಳಿರಿ. ಇದು ನಿಮ್ಮ ಪಾಲಿಗೆ ಹೆಚ್ಚು ಪರಿಶುದ್ಧವಾದ ಕ್ರಮ. ನೀವು ಮಾಡುತ್ತಿರುವುದನ್ನು ಅಲ್ಲಾಹನು ಚೆನ್ನಾಗಿ ತಿಳಿದಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(24,29,'24:29','ನಿಮಗೆ (ವಿಶ್ರಾಂತಿಯಂಥ ಏನಾದರೂ) ಉಪಯೋಗಕ್ಕಾಗಿ ಜನವಾಸವಿಲ್ಲದ ಮನೆಗಳಿಗೆ ಪ್ರವೇಶಿಸುವುದರಲ್ಲಿ ಅಡ್ಡಿಯಿಲ್ಲ. ನೀವು ಪ್ರಕಟಗೊಳಿಸುವುದನ್ನೂ ಬಚ್ಚಿಡುವುದನ್ನೂ ಅಲ್ಲಾಹು ಅರಿಯುವನು.');
INSERT INTO "kannada" VALUES(24,30,'24:30','(ಪೈಗಂಬರರೇ,) ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಹೇಳಿರಿ; ಅವರು ತಮ್ಮ ದೃಷ್ಟಿಗಳನ್ನು ತಗ್ಗಿಸಿಕೊಳ್ಳಲಿ , ತಮ್ಮ ಗುಪ್ತಾಂಗಗಳನ್ನು ರಕ್ಷಿಸಿಕೊಳ್ಳಲಿ. ಇದು ಅವರಿಗೆ ಹೆಚ್ಚು ಪರಿಶುದ್ಧವಾದ ಕ್ರಮ! ಅಲ್ಲಾಹನು ಅವರು ಮಾಡುತ್ತಿರುವುದರ ಬಗ್ಗೆ ಸೂಕ್ಷ್ಮ ಜ್ಞಾನಿಯು.');
INSERT INTO "kannada" VALUES(24,31,'24:31','(ಪೈಗಂಬರರೇ,) ಸತ್ಯವಿಶ್ವಾಸಿನಿಯರಿಗೆ ಹೇಳಿರಿ. ಅವರು ತಮ್ಮ ದೃಷ್ಟಿಗಳನ್ನು ತಗ್ಗಿಸಿಕೊಳ್ಳಲಿ, ತಮ್ಮ ಗುಪ್ತಾಂಗಗಳನ್ನು ರಕ್ಷಿಸಿಕೊಳ್ಳಲಿ. ತಮ್ಮ ಸೌಂದರ್ಯದಿಂದ ಪ್ರಕಟವಾಗುವ ಭಾಗ ವನ್ನು ಹೊರತುಪಡಿಸಿ ತೋರಿಸದಿರಲಿ . ತಮ್ಮ ಎದೆಯ ಮೇಲೆ ತಮ್ಮ ಮುಖ ಪರದೆಯನ್ನು ಎಳೆದು ಹಾಕಲಿ . ಪತಿಯರು, ತಂದೆಯರು, ಪತಿಯ ತಂದೆಯರು, ತಮ್ಮ ಪುತ್ರರು, ಪತಿಯರ ಪುತ್ರರು, ಸಹೋದರರು, ಸಹೋದರ ಪುತ್ರರು, ಸಹೋದರಿಯರ ಪುತ್ರರು, ಆಪ್ತ ಸ್ತ್ರೀಯರು, ತಮ್ಮ ದಾಸಿಯರು, ಯಾವುದೇ ಲೈಂಗಿಕ ಮೋಹ ವಿಲ್ಲದ ಅಧೀನ ಸೇವಕರು ಮತ್ತು ಇನ್ನೂ ಸ್ತ್ರೀಯರ ಗೋಪ್ಯತೆಯ ಬಗ್ಗೆ ಅರಿಯುವ ಪ್ರಾಯ ವಾಗದ ಮಕ್ಕಳು, ಇವಿಷ್ಟು ಜನರನ್ನು ಹೊರತು ಪಡಿಸಿ ಇನ್ನಾರಿಗೂ ತಮ್ಮ ಸೌಂದರ್ಯವನ್ನು ತೋರ್ಪಡಿಸದಿರಲಿ. ಅವರು, ತಾವು ಮುಚ್ಚಿಟ್ಟ ಅಲಂಕಾರವನ್ನು ತಿಳಿಯುವಂತೆ ತಮ್ಮ ಪಾದಗಳನ್ನು ಬಡಿದು ನಡೆಯದಿರಲಿ. ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳೇ! ನೀವೆಲ್ಲರೂ ಅಲ್ಲಾಹನೆಡೆಗೆ ಪಶ್ಚಾತ್ತಾಪ ಪಟ್ಟು ಮರಳಿರಿ. ನಿಮಗೆ ಯಶಸ್ಸು ಪ್ರಾಪ್ತವಾದೀತು.');
INSERT INTO "kannada" VALUES(24,32,'24:32','ನಿಮ್ಮಲ್ಲಿ ಎಣೆಗಳಿಲ್ಲದ ಒಂಟಿಯಾಗಿರುವ ಸ್ತ್ರೀ- ಪುರುಷರಿಗೂ ಮತ್ತು ನಿಮ್ಮ ದಾಸ-ದಾಸಿ ಯರಲ್ಲಿ ಸಜ್ಜನರಾಗಿರುವವರಿಗೂ ವಿವಾಹ ಮಾಡಿಕೊಡಿರಿ. ಅವರು ಬಡವರಾಗಿದ್ದರೆ ಅಲ್ಲಾ ಹನು ತನ್ನ ಔದಾರ್ಯದಿಂದ ಅವರನ್ನು ಶ್ರೀಮಂತ ಗೊಳಿಸುವನು. ಅಲ್ಲಾಹನು ಅತಿ ವಿಶಾಲನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(24,33,'24:33','ವಿವಾಹಾನುಕೂಲತೆ ಒದಗದವರು ಅಲ್ಲಾಹನು ತನ್ನ ಔದಾರ್ಯದಿಂದ ಅವರನ್ನು ಶ್ರೀಮಂತ ಗೊಳಿಸುವವರೆಗೂ ಚಾರಿತ್ರ್ಯವನ್ನು ಕಾಯ್ದು ಕೊಳ್ಳಲಿ. ನಿಮ್ಮ ಗುಲಾಮರ ಪೈಕಿ ಮುಕ್ತಿ ಕರಾರಿಗಾಗಿ ನಿವೇದಿಸಿ ಕೊಳ್ಳುವವರಿಗೆ, ಅವರಲ್ಲಿ ಒಳಿತಿದೆ ಎಂದು ನಿಮಗೆ ತಿಳಿದು ಬಂದರೆ ಮುಕ್ತಿ ಕರಾರು ಬರೆದುಕೊಡಿರಿ. ಅಲ್ಲಾಹನು ನಿಮಗೆ ಕೊಟ್ಟಿರುವ ಸಂಪತ್ತಿನಿಂದ ಅವರಿಗೆ ಕೊಡಿರಿ. ನಿಮ್ಮ ದಾಸಿಯರು ಸ್ವತಃ ಚಾರಿತ್ರ್ಯ ಶುದ್ಧಿಯನ್ನು ಬಯಸುವಾಗ ಲೌಕಿಕ ಲಾಭವನ್ನು ಗಳಿಸಲಿಕ್ಕಾಗಿ ಅವರನ್ನು ವೇಶ್ಯಾವೃತ್ತಿಗೆ ಬಲಾತ್ಕರಿಸ ಬೇಡಿರಿ . ಅವರನ್ನು ಯಾರಾದರೂ ಬಲಾತ್ಕರಿಸಿದರೆ ಈ ಬಲಾತ್ಕಾರದ ನಂತರ ಖಂಡಿತ ಅಲ್ಲಾಹನು, ಕ್ಷಮಾದಾನಿಯೂ ದಯಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(24,34,'24:34','ಸುಸ್ಪಷ್ಟವಾದ ಸೂಕ್ತಗಳನ್ನೂ ನಿಮಗಿಂತ ಮುಂಚೆ ಗತಿಸಿದ ಜನಾಂಗಗಳ ಕೆಲವು ಅದ್ಭುತ ದೃಷ್ಟಾಂತಗಳನ್ನೂ ದೇವ ಭಯವಿರಿಸಿಕೊಳ್ಳು ವವರಿಗೆ ಉಪದೇಶವನ್ನೂ ನಾವು ನಿಮಗೆ ಇಳಿಸಿ ಕೊಟ್ಟಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(24,35,'24:35','ಅಲ್ಲಾಹನು ಭೂಮಿ-ಆಕಾಶಗಳ ಪ್ರಕಾಶದಾತ ನಾಗಿರುವನು. (ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಹೃದಯದಲ್ಲಿ) ಅವನು ಹಚ್ಚಿದ ಪ್ರಕಾಶದ ಉಪಮೆ ಒಂದು ಗೋಡೆ ಗೂಡಿನಂತಿದೆ. ಅದರಲ್ಲಿ ಒಂದು ದೀಪ ವಿದೆ. ದೀಪವು ಒಂದು ಗಾಜಿನೊಳಗಿದೆ. ಗಾಜು ಮುತ್ತಿನಂತೆ ಹೊಳೆಯುವ ತಾರೆಯಂತಿದೆ. ಪೂರ್ವದಲ್ಲಾಗಲಿ ಪಶ್ಚಿಮದಲ್ಲಾಗಲಿ ಬೆಳೆಯದ (ಹೆಚ್ಚು ಉಷ್ಣವೋ ತಂಪೋ ತಾಗದ ಮದ್ಯಮ ವಾತಾವರಣದ ಪ್ರದೇಶದಲ್ಲಿರುವ) ಅನುಗ್ರಹೀತ ಇಪ್ಪೆಯ ಒಂದು ಮರದ ಎಣ್ಣೆಯಿಂದ ಅದನ್ನು ಬೆಳಗಿಸಲಾಗುತ್ತದೆ. ಅದರ ಎಣ್ಣೆಗೆ ಬೆಂಕಿ ತಾಗದಿದ್ದರೂ ಅದು ತಾನಾಗಿಯೇ ಪ್ರಜ್ವಲಿಸುತ್ತದೆ. ಹೀಗೆ ಪ್ರಕಾಶದ ಮೇಲೆ ಪ್ರಕಾಶ . ಅಲ್ಲಾಹನು ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ತನ್ನ ಪ್ರಕಾಶದೆಡೆಗೆ (ಸತ್ಯದೆಡೆಗೆ) ಒಯ್ಯುತ್ತಾನೆ . ಅವನು ಜನರಿಗೆ ಉಪಮೆಗಳನ್ನು ವಿವರಿಸಿ ಕೊಡುತ್ತಾನೆ. ಅಲ್ಲಾಹನು ಸಕಲ ವಸ್ತುಗಳನ್ನು ಚೆನ್ನಾಗಿ ಬಲ್ಲವನು.');
INSERT INTO "kannada" VALUES(24,36,'24:36','');
INSERT INTO "kannada" VALUES(24,37,'24:37','ಉನ್ನತಗೊಳಿಸಲು ಹಾಗೂ ಅವುಗಳಲ್ಲಿ ತನ್ನ ನಾಮಸ್ಮರಣೆ ಮಾಡಲು ಅಲ್ಲಾಹನು ಅನುಮತಿ ನೀಡಿರುವಂತಹ ಕೆಲವು ಭವನಗಳಲ್ಲಿ (ಮಸೀದಿಗಳಲ್ಲಿ) ಬೆಳಗು ಬೈಗುಗಳಲ್ಲಿ ಕೆಲವು ಪುರುಷರು ಅವನ ಪವಿತ್ರ ಕೀರ್ತನೆ ಮಾಡುತ್ತಿರುವರು . ವ್ಯಾಪಾರ ಮತ್ತು ಕ್ರಯ ವಿಕ್ರಯಗಳು ಅಲ್ಲಾಹನ ಸ್ಮರಣೆಯಿಂದಲೂ ನಮಾಝಿನ ಸಂಸ್ಥಾಪನೆಯಿಂದಲೂ ಝಕಾತಿನ ಪಾವತಿಯಿಂದಲೂ ಅವರನ್ನು ತಡೆಯುವುದಿಲ್ಲ . ಹೃದಯಗಳು ಮತ್ತು ದೃಷ್ಟಿಗಳು ಪಲ್ಲಟಗೊಳ್ಳುವ ಆ ಒಂದು ದಿನವನ್ನು ಅವರು ಭಯಪಡುತ್ತಿರುವರು.');
INSERT INTO "kannada" VALUES(24,38,'24:38','ಅಲ್ಲಾಹನು ಅವರ ಉತ್ತಮ ಕರ್ಮದ ಫಲಗಳನ್ನು ಅವರಿಗೆ ಕೊಡಲಿಕ್ಕಾಗಿ ಮತ್ತು ತನ್ನ ಅನುಗ್ರಹದಿಂದ ಅವರಿಗೆ ಹೆಚ್ಚಿಸಲಿಕ್ಕಾಗಿ ಅವರು ಹೀಗೆ ಮಾಡುತ್ತಾರೆ. ಅಲ್ಲಾಹು ತಾನಿಚ್ಚಿಸಿದವರಿಗೆ ಲೆಕ್ಕವಿಲ್ಲದೆ ನೀಡುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(24,39,'24:39','ಸತ್ಯನಿಷೇಧವನ್ನು ಕೈಗೊಂಡವರ ಕರ್ಮಗಳು ನಿರ್ಜಲ ಮರುಭೂಮಿಯಲ್ಲಿರುವ ಮರೀಚಿಕೆ ಯಂತಿದೆ. ಅದನ್ನು ಬಾಯಾರಿದವನು ನೀರೆಂದು ಭ್ರಮಿಸುವನು. ಆದರೆ ಅಲ್ಲಿಗೆ ತಲುಪಿದಾಗ ಅಲ್ಲೇನನ್ನೂ ಕಾಣುವುದಿಲ್ಲ. ಸತ್ಯನಿಷೇಧಿಯು ತನ್ನ ಕರ್ಮಗಳ ಬಳಿ ಅಲ್ಲಾಹನನ್ನು ಕಂಡನು. ಆಗ ಅಲ್ಲಾಹನು ಅದರ ಲೆಕ್ಕವನ್ನು ಸಂಪೂರ್ಣ ವಾಗಿ (ಇಹದಲ್ಲೇ) ತೀರಿಸಿದನು. ಅಲ್ಲಾಹನು, ಅತಿ ಶೀಘ್ರ ಲೆಕ್ಕಿಗನು .');
INSERT INTO "kannada" VALUES(24,40,'24:40','ಅಥವಾ ಅವರ ಕರ್ಮಗಳು ಒಂದು ಆಳವಾದ ಸಾಗರದಲ್ಲಿ ಅಂಧಕಾರದಂತಿದೆ. ಮೇಲೊಂದು ತೆರೆ ಆವರಿಸಿದೆ. ಅದರ ಮೇಲೆ ಇನ್ನೊಂದು ತೆರೆಯಿದ್ದು ಅದರ ಮೇಲೆ ಮೋಡ ಆವರಿಸಿಕೊಂಡಿದೆ. ಅಂಧಕಾರದ ಮೇಲೆ ಅಂಧಕಾರ ಕವಿದಿದೆ . ಮನುಷ್ಯನು ತನ್ನ ಕೈಯನ್ನು ಹೊರ ಚಾಚಿದರೆ ಅದೂ ಅವನಿಗೆ ಕಾಣಿಸದು. ಅಲ್ಲಾಹನು ಯಾರಿಗೆ ಪ್ರಕಾಶವನ್ನು ಸ್ಥಾಪಿಸಲಿಲ್ಲವೋ ಅವನಿಗೆ ಬೇರಾವ ಪ್ರಕಾಶವೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(24,41,'24:41','ಭೂಮಿ-ಆಕಾಶಗಳಲ್ಲಿ ಇರುವವರು, ವಾಯು ಮಂಡಲದಲ್ಲಿ ರೆಕ್ಕೆಬಿಚ್ಚಿ ಹಾರುತ್ತಿರುವ ಹಕ್ಕಿಗಳೂ ಅಲ್ಲಾಹನ ಪ್ರಕೀರ್ತನೆ ಮಾಡುತ್ತಿರುವುದನ್ನು ನೀವು ನೋಡುತ್ತಿಲ್ಲವೇ? ಎಲ್ಲವೂ ತನ್ನ ಪ್ರಾರ್ಥನೆ ಮತ್ತು ಕೀರ್ತನೆಯನ್ನು ಅರಿತಿದೆ. ಅವರು ಮಾಡುತ್ತಿರುವುದನ್ನು ಅಲ್ಲಾಹನು ಚೆನ್ನಾಗಿ ಬಲ್ಲವನು.');
INSERT INTO "kannada" VALUES(24,42,'24:42','ಭೂಮಿ-ಆಕಾಶಗಳ ಆದಿಪತ್ಯವು ಅಲ್ಲಾಹನದ್ದು. ಅವನ ಕಡೆಗೆ ನಿರ್ಗಮನವಿದೆ.');
INSERT INTO "kannada" VALUES(24,43,'24:43','ಅಲ್ಲಾಹನು ಮೋಡವನ್ನು ಮೆಲ್ಲ ಮೆಲ್ಲನೆ ಚಲಾ ಯಿಸುವುದನ್ನೂ ಅನಂತರ ಅದರ ತುಂಡು ಗಳನ್ನು ಪರಸ್ಪರ ಜೋಡಿಸುವುದನ್ನೂ ತರು ವಾಯ ಅದನ್ನು ಒತ್ತೊತ್ತಾಗಿ ರಾಶಿ ಹಾಕುವು ದನ್ನೂ ನೀವು ನೋಡುತ್ತಿಲ್ಲವೇ? ಆ ಬಳಿಕ ಅದರ ಎಡೆಯಿಂದ ಮಳೆಯು ಹನಿಯಾಗಿ ಬೀಳುತ್ತಿರು ವುದನ್ನು ನೀವು ನೋಡುತ್ತೀರಿ. ಅವನು ಆಕಾಶ ದಿಂದ ಅಂದರೆ ಅದರಲ್ಲಿರುವ ಮೇಘ ಪರ್ವತ ಗಳಿಂದ ಆಲಿಕಲ್ಲುಗಳನ್ನು ಬೀಳಿಸುತ್ತಾನೆ. ಅದರಿಂದ ತಾನಿಚ್ಚಿಸುವವರಿಗೆ ಮುಟ್ಟಿಸುತ್ತಾನೆ. ಮತ್ತು ತಾನಿಚ್ಚಿಸುವವರಿಂದ ಅದನ್ನು ತಪ್ಪಿಸು ತ್ತಾನೆ. ಅದರ ಮಿಂಚಿನ ಹೊಳಪು ಕಣ್ಣುಗಳನ್ನು ಕೋರೈಸಿ ಬಿಡುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(24,44,'24:44','ಅಲ್ಲಾಹು ರಾತ್ರಿ ಹಗಲುಗಳ ಪರಿವರ್ತನೆಯನ್ನು ಮಾಡುತ್ತಿರುತ್ತಾನೆ. ಇದರಲ್ಲಿ ಯೋಚಿಸುವವರಿಗೆ ಖಂಡಿತ ಪಾಠವಿದೆ.');
INSERT INTO "kannada" VALUES(24,45,'24:45','ಅಲ್ಲಾಹನು ಪ್ರತಿಯೊಂದು ಜೀವಿಯನ್ನು ನೀರಿ ನಿಂದ ಸೃಷ್ಟಿಸಿದನು. ಆ ಪೈಕಿ ಕೆಲವು ಹೊಟ್ಟೆಯ ಮೇಲೆ ಎಳೆದು ನಡೆದರೆ, ಇನ್ನು ಕೆಲವು ಎರಡು ಕಾಲುಗಳ ಮೇಲೂ ಮತ್ತೆ ಕೆಲವು ನಾಲ್ಕು ಕಾಲುಗಳ ಮೇಲೂ ನಡೆಯುತ್ತವೆ. ಅವನು ತಾನಿಚ್ಚಿಸಿ ದ್ದನ್ನು ಸೃಷ್ಟಿಸುತ್ತಾನೆ. ನಿಜಕ್ಕೂ ಅಲ್ಲಾಹನು ಸರ್ವ ವಸ್ತುಗಳ ಮೇಲೆ ಸಾಮಥ್ರ್ಯವುಳ್ಳವನು .');
INSERT INTO "kannada" VALUES(24,46,'24:46','ಸತ್ಯವನ್ನು ವ್ಯಕ್ತವಾಗಿ ತಿಳಿಸುವ ಸೂಕ್ತಗಳನ್ನು ನಾವು ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದೇವೆ. ಅಲ್ಲಾಹನು ನೇರ ಮಾರ್ಗದ ಕಡೆಗೆ ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ಸೇರಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(24,47,'24:47','‘ನಾವು ಅಲ್ಲಾಹು ಮತ್ತು ಸಂದೇಶವಾಹಕರಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸಿದ್ದೇವೆ ಮತ್ತು ನಾವು ಅವರನ್ನು ಅನುಸರಿಸಿದ್ದೇವೆ’ ಎಂದು ಅವರು ಹೇಳುತ್ತಾರೆ. ಆದರೆ ಅನಂತರ ಅವರಲ್ಲೊಂದು ಪಂಗಡ ವಿಮುಖವಾಗಿ ಬಿಡುತ್ತದೆ. ಇಂಥವರು ಖಂಡಿತ ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳಲ್ಲ.');
INSERT INTO "kannada" VALUES(24,48,'24:48','ಅವರ ನಡುವೆ ತೀರ್ಪು ಕಲ್ಪಿಸಲಿಕ್ಕಾಗಿ ಅವರನ್ನು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ಸಂದೇಶವಾಹಕರ ಕಡೆಗೆ ಕರೆಯಲಾದರೆ ಅವರಲ್ಲೊಂದು ಪಂಗಡ ಹಿಂಜರಿಯುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(24,49,'24:49','ಆದರೆ ಸತ್ಯವು ಅವರ ಪರವಾಗಿದ್ದರೆ ಅತ್ಯಂತ ವಿಧೇಯರಾಗಿ ದೂತರ ಬಳಿಗೆ ಓಡಿ ಬರುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(24,50,'24:50','ಅವರ ಹೃದಯಗಳಲ್ಲಿ (ಅವಿಶ್ವಾಸದ) ರೋಗ ವಿದೆಯೇ? ಅಥವಾ ಅವರು (ಪ್ರವಾದಿತ್ವದಲ್ಲಿ) ಸಂಶಯಪಟ್ಟಿರುವರೇ? ಅಥವಾ ಅಲ್ಲಾಹು ಮತ್ತು ದೇವದೂತರು ಅವರ ಮೇಲೆ ಅನ್ಯಾಯ ವೆಸಗು ವರೆಂದು ಭಯಪಡುತ್ತಿರುವರೇ? ಅದೇನೂ ಅಲ್ಲ. ವಾಸ್ತವದಲ್ಲಿ ಅವರೇ ಅಕ್ರಮಿಗಳು.');
INSERT INTO "kannada" VALUES(24,51,'24:51','ಅವರ ನಡುವೆ ತೀರ್ಪು ಕಲ್ಪಿಸಲು ಅವರನ್ನು ಅಲ್ಲಾಹು ಮತ್ತು ರಸೂಲರ ಕಡೆಗೆ ಕರೆಯಲಾದರೆ ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ‘ನಾವು ಆಲಿಸಿದೆವು ಮತ್ತು ಅನುಸರಿಸಿದೆವು’ ಎಂದು ಹೇಳುವರು. ಅವರೇ ವಿಜಯಿಗಳು.');
INSERT INTO "kannada" VALUES(24,52,'24:52','ಯಾರು, ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ದೂತರನ್ನು ಅನುಸರಿಸುತ್ತಾರೆ, ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡು ತ್ತಾರೆ ಮತ್ತು ಅವನ ಆಜ್ಞೋಲ್ಲಂಘನೆಯಿಂದ ದೂರವಿರುತ್ತಾರೆ, ಅವರೇ ವಿಜಯಿಗಳು.');
INSERT INTO "kannada" VALUES(24,53,'24:53','(ಪೈಗಂಬರರೇ,) ತಾವು ಏನಾದರೂ ಅವರಿಗೆ ಅಪ್ಪಣೆ ಕೊಟ್ಟರೆ ನಾವು ಅದಕ್ಕಾಗಿ ಹೊರಟು ಬರು ವೆವು ಎಂದು ಕಪಟ ವಿಶ್ವಾಸಿಗಳು ಬಲವಾದ ಭಾಷೆಯಲ್ಲಿ ಅಲ್ಲಾಹನನ್ನು ಮುಂದಿರಿಸಿ, ಆಣೆ ಹಾಕುತ್ತಾರೆ. ಹೀಗೆ ಹೇಳಿರಿ; “ಆಣೆ ಹಾಕ ಬೇಡಿರಿ. ನಿಮ್ಮ ವಿಧೇಯತೆಯು ಗೊತ್ತಾದ ವಿಷಯ. ನಿಮ್ಮ ಕೃತ್ಯಗಳ ಬಗ್ಗೆ ಅಲ್ಲಾಹನು ಸೂಕ್ಷ್ಮ ಜ್ಞನಾಗಿದ್ದಾನೆ”.');
INSERT INTO "kannada" VALUES(24,54,'24:54','ಹೇಳಿರಿ, ಅಲ್ಲಾಹು ಮತ್ತು ದೂತರನ್ನು ಅನು ಸರಿಸಿರಿ. ಇನ್ನು ನೀವು ವಿಮುಖರಾದರೆ ದೂತ ರಿಗೆ ಹೊರಿಸಲಾದ ಕರ್ತವ್ಯಗಳಿಗೆ ಮಾತ್ರ ಅವರು ಜವಾಬ್ದಾರರು. ಆದರೆ ನಿಮ್ಮ ಮೇಲೆ ಹೊರಿಸಲಾದ ಕರ್ತವ್ಯಗಳಿಗೆ ನೀವೇ ಜವಾಬ್ದಾರರು. ನೀವು ದೂತರನ್ನು ಅನುಸರಿಸಿದರೆ ನೀವು ಸನ್ಮಾರ್ಗ ಹೊಂದುವಿರಿ. ಸುವ್ಯಕ್ತವಾಗಿ ಆದೇಶಗಳನ್ನು ತಲುಪಿಸಿಕೊಡುವುದೇ ದೂತರ ಹೊಣೆ .');
INSERT INTO "kannada" VALUES(24,55,'24:55','ನಿಮ್ಮ ಪೈಕಿ ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿ ಸತ್ಕರ್ಮವೆಸಗಿದ ಜನರಿಗೆ ಅವರಿಗಿಂತ ಮುಂಚಿನವರಿಗೆ ಪ್ರಾತಿ ನಿಧ್ಯವನ್ನು ಕಲ್ಪಿಸಿದಂತೆಯೇ ಈ ಭೂಮಿಯಲ್ಲಿ ಅವರಿಗೂ ಪ್ರಾತಿನಿಧ್ಯ ಕಲ್ಪಿಸುತ್ತೇನೆಂದೂ ತಾನು ಅವರಿಗಾಗಿ ತೃಪ್ತಿಪಟ್ಟಿರುವ ಅವರ ಧರ್ಮವನ್ನು ಬಲಿಷ್ಟವಾಗಿ ಸ್ಥಾಪಿಸುತ್ತೇನೆಂದೂ ಅವರ ಭಯದ ನಂತರ ಶಾಂತಿಯನ್ನು ಮಾರ್ಪಡಿಸಿ ಬಿಡುತ್ತೇನೆಂದೂ ಅಲ್ಲಾಹನು ವಾಗ್ದಾನ ಮಾಡಿರುವನು. ಅವರು ನನ್ನನ್ನು ಮಾತ್ರ ಆರಾಧಿಸಲಿ ಮತ್ತು ನನ್ನೊಂದಿಗೆ ಯಾರನ್ನೂ ಸಹಭಾಗಿಗಳಾಗಿ ಮಾಡದಿರಲಿ, ಎಂಬ ಉದ್ದೇಶದಿಂದ ಹೀಗೆ ಮಾಡಲಾಗಿತ್ತು. ಆದರೆ ಈ ಅನುಗ್ರಹ ಲಭಿಸಿದ ಬಳಿಕ ಯಾರು ಕೃತಘ್ನರಾಗುವರೋ ಅವರೇ ಧರ್ಮಭ್ರಷ್ಟರು.');
INSERT INTO "kannada" VALUES(24,56,'24:56','ನಮಾಝನ್ನು ಸಂಸ್ಥಾಪಿಸಿರಿ, ಝಕಾತ್ ಕೊಡಿರಿ ಮತ್ತು ದೂತರನ್ನು ಅನುಸರಿಸಿರಿ. ನಿಮ್ಮ ಮೇಲೆ ದಯೆ ಇರುವುದು.');
INSERT INTO "kannada" VALUES(24,57,'24:57','ಸತ್ಯನಿಷೇಧಿಗಳು ಭೂಮಿಯಲ್ಲಿ ಅಲ್ಲಾಹನನ್ನು ಪರಾಜಿತಗೊಳಿಸಬಲ್ಲರೆಂದು ತಪ್ಪು ತಿಳಿಯ ಬೇಡಿ. ನರಕವೇ ಅವರ ನೆಲೆ ಮತ್ತು ಅದು ಅತ್ಯಂತ ಕೆಟ್ಟ ನೆಲೆಯಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(24,58,'24:58','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನಿಮ್ಮ ಸ್ವಾಧೀನಕ್ಕೆ ಬಂದವರು (ದಾಸ-ದಾಸಿಯರು) ಮತ್ತು ಇನ್ನೂ ಪ್ರಬು ದ್ಧರಾಗದ ನಿಮ್ಮ ಮಕ್ಕಳು ಮೂರು ಹೊತ್ತುಗಳಲ್ಲಿ ನಿಮ್ಮ ಬಳಿಗೆ ಬರುವಾಗ ಅನುಮತಿ ಪಡೆದು ಬರಲಿ. ಫಜ್ರ್ ನಮಾಝಿಗಿಂತ ಮುಂಚೆ, ಮಧ್ಯಾಹ್ನ ವಿಶ್ರಾಂತಿಗಾಗಿ ನೀವು ಉಡುಪುಗ ಳನ್ನು ಕಳಚಿಡುವ ಸಮಯದಲ್ಲಿ ಮತ್ತು ಇಷಾ ನಮಾಝಿನ ಅನಂತರ. ಇವು ಮೂರು ನಿಮ್ಮ ಗೌಪ್ಯತೆಯ ಸಮಯವಾಗಿದೆ. ಇತರ ಸಮಯಗಳಲ್ಲಿ ಅವರು ಅನುಮತಿ ಪಡೆಯದೆ ಬಂದರೆ ನಿಮ್ಮ ಮೇಲಾಗಲಿ ಅವರ ಮೇಲಾಗಲಿ ಏನೂ ದೋಷವಿಲ್ಲ . ಅವರು ನಿಮ್ಮಲ್ಲಿ - ಅಂದರೆ ನಿಮ್ಮಲ್ಲಿ ಕೆಲವರು ಕೆಲವರಲ್ಲಿ - (ಶುಶ್ರೂಷೆ ಮತ್ತಿತರ ಅಗತ್ಯಗಳಿಗಾಗಿ) ಸುತ್ತುಗಟ್ಟಿ ನಿಲ್ಲು ವವರು ತಾನೆ? ಈ ರೀತಿ ಅಲ್ಲಾಹನು ತನ್ನ ಆದೇ ಶಗಳನ್ನು ನಿಮಗಾಗಿ ವಿವರಿಸುತ್ತಾನೆ. ಅವನು ಸರ್ವಜ್ಞನೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(24,59,'24:59','ಇನ್ನು ನಿಮ್ಮ ಮಕ್ಕಳು ಪ್ರಬುದ್ಧರಾದಾಗ ಅವರ ಹಿರಿಯರು ಅನುಮತಿ ಪಡೆಯುವಂತೆಯೇ ಅವರೂ ಅನುಮತಿ ಪಡೆದು ಬರಲಿ . ಈ ರೀತಿಯಲ್ಲಿ ಅಲ್ಲಾಹನು ತನ್ನ ಪುರಾವೆಗಳನ್ನು ನಿಮಗೆ ವಿವರಿಸಿ ಹೇಳುತ್ತಾನೆ. ಅಲ್ಲಾಹನು ಸರ್ವಜ್ಞನೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(24,60,'24:60','ವಿವಾಹವಾಗಲು ಬಯಸದೆ ವೃದ್ಧೆಯರಾದ ಸ್ತ್ರೀ ಯರು ತಮ್ಮ ಪರ್ದಾಚಾದರಗಳನ್ನು ತೆಗೆದಿರಿ ಸಿದರೆ ಅವರ ಮೇಲೇನೂ ದೋಷವಿಲ್ಲ. ಆದರೆ ಒಡವೆ ಅಲಂಕಾರವನ್ನು ಅವರು ಪ್ರದರ್ಶಿಸಬಾರದು. ಪರ್ದಾಚಾದರವನ್ನು ಕಳಚಿ ಇಡದಿರುವುದೇ ಅವರ ಮಟ್ಟಿಗೆ ಉತ್ತಮವಾಗಿದೆ. ಅಲ್ಲಾಹನು ಎಲ್ಲವನ್ನು ಆಲಿಸುತ್ತಾನೆ ಮತ್ತು ಅರಿಯುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(24,61,'24:61','ನೀವು ನಿಮ್ಮ ಮನೆಗಳಲ್ಲಿ, ನಿಮ್ಮ ತಂದೆ - ತಾತಂದಿರ ಮನೆಗಳಲ್ಲಿ, ನಿಮ್ಮ ತಾಯಂದಿರ ಮನೆಗಳಲ್ಲಿ ಅಥವಾ ನಿಮ್ಮ ಸಹೋದರರ ಮನೆಗಳಲ್ಲಿ ಅಥವಾ ನಿಮ್ಮ ಸಹೋದರಿಯರ ಮನೆಗಳಲ್ಲಿ ಅಥವಾ ನಿಮ್ಮ ಸೋದರತ್ತೆಯಂದಿರ ಮನೆಗಳಲ್ಲಿ ಅಥವಾ ನಿಮ್ಮ ಸೋದರ ಮಾವಂದಿರ ಮನೆಗಳಲ್ಲಿ ಅಥವಾ ನಿಮ್ಮ ಮಾತೃ ಸೋದರಿಯರ ಮನೆಗಳಲ್ಲಿ ಅಥವಾ ಬೀಗದ ಕೈಗಳು ನಿಮ್ಮ ವಶದಲ್ಲಿರುವ ಮನೆಗಳಲ್ಲಿ ಅಥವಾ ನಿಮ್ಮ ಮಿತ್ರರ ಮನೆಗಳಲ್ಲಿ ಉಣ್ಣುವುದರಲ್ಲಿ ಆಕ್ಷೇಪವಿಲ್ಲ. ಕುರುಡನ ಮೇಲೆ ತಪ್ಪಿಲ್ಲ. ಕುಂಟನ ಮೇಲೆ ತಪ್ಪಿಲ್ಲ. ರೋಗಿಯ ಮೇಲೆ ತಪ್ಪಿಲ್ಲ. ಸ್ವತಃ ನಿಮ್ಮ ಮೇಲೂ ತಪ್ಪಿಲ್ಲ. ನೀವು ಒಟ್ಟಿಗೆ ಕೂತು ಉಣ್ಣುವು ದಕ್ಕೂ ಬೇರೆ ಬೇರೆ ಕೂತು ಉಣ್ಣುವುದಕ್ಕೂ ವಿರೋಧವಿಲ್ಲ. ಆದರೆ ಮನೆಗಳೊಳಗೆ ಪ್ರವೇಶಿ ಸುವಾಗ ಅಲ್ಲಾಹನ ವತಿಯಿಂದ ಇರುವ ಅನು ಗ್ರಹೀತವೂ ಪರಿಪಾವನವೂ ಆದ ಅಭಿವಾದ್ಯ ವೆಂಬ ನೆಲೆಯಲ್ಲಿ ನಿಮ್ಮ ಸ್ವಂತದ ಮೇಲೆ ಸಲಾಮ್ ಹೇಳಿರಿ . ಹೀಗೆ ಅಲ್ಲಾಹನು, ನಿಮಗೆ ನಿದರ್ಶನಗಳನ್ನು ವಿವರಿಸುತ್ತಾನೆ. ನೀವು ಯೋಚಿಸಿ ಗ್ರಹಿಸಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(24,62,'24:62','ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ದೂತರನ್ನು ನಂಬು ವವರೇ ಸತ್ಯವಿಶ್ವಾಸಿಗಳು, ಯಾವುದೇ ಸಾಮೂ ಹಿಕ ಕಾರ್ಯದಲ್ಲಿ ಅವರು ದೇವದೂತರ ಜೊತೆಗೆ ಭಾಗವಹಿಸುತ್ತಿರುವಾಗ ಅವರಿಂದ ಅನುಮತಿ ಪಡೆಯದೆ ಹೊರ ಹೋಗಬಾರದು. (ಪೈಗಂಬರರೇ,) ನಿಮ್ಮೊಡನೆ ಅನುಮತಿ ಕೇಳುವವರೇ ಅಲ್ಲಾಹು ಮತ್ತು ದೂತರನ್ನು ನಂಬುವವರು. ಆದುದರಿಂದ ಅವರು ತಮ್ಮ ಯಾವುದೇ ಕಾರ್ಯ ಕ್ಕಾಗಿ ಹೊರ ಹೋಗಲು ಅನುಮತಿ ಕೇಳಿದರೆ ನೀವಿಚ್ಚಿಸುವವರಿಗೆ ಅನುಮತಿಕೊಡಿರಿ. ಮತ್ತು ಅವರ ಪಾಪ ಮುಕ್ತಿಗಾಗಿ ಅಲ್ಲಾಹನೊಡನೆ ಪ್ರಾರ್ಥಿಸಿರಿ . ಅಲ್ಲಾಹನು ಕ್ಷಮಾಶೀಲನೂ, ಕರುಣಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(24,63,'24:63','ನಿಮ್ಮ ನಡುವೆ ನೀವು ಪರಸ್ಪರ ಒಬ್ಬರನ್ನೊಬ್ಬರು ಕರೆಯುವಂತೆ ದೂತರನ್ನು ನೀವು ಕರೆಯ ಬೇಡಿರಿ . ನಿಮ್ಮ ಪೈಕಿ ಜನರ ಮರೆಯಲ್ಲಿ ಮೆಲ್ಲನೆ ಕೊಸರಿಕೊಳ್ಳುವ ಮಂದಿಯನ್ನು ಅಲ್ಲಾಹು ಚೆನ್ನಾಗಿ ಅರಿಯುತ್ತಾನೆ. ದೂತರ ಆಜ್ಞೆಗೆ ವಿರುದ್ಧ ವರ್ತಿಸುವವರು ಯಾವುದೇ ಪರೀಕ್ಷೆ ಬಂದೆರಗುವ ಬಗ್ಗೆ ಅಥವಾ ತಮ್ಮ ಮೇಲೆ ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆ ಬಂದೆರಗುವ ಬಗ್ಗೆ ಭಯಪಟ್ಟುಕೊಳ್ಳಲಿ.');
INSERT INTO "kannada" VALUES(24,64,'24:64','ತಿಳಿಯಿರಿ, ಭೂಮಿ-ಆಕಾಶಗಳಲ್ಲಿರುವುದು ಅಲ್ಲಾಹನದು. ನೀವು ಯಾವ ಅವಸ್ಥೆಯಲ್ಲಿ ಇದ್ದೀರಿ ಎಂಬುದನ್ನು ಮತ್ತು ಅವರು (ಕಪಟಿ ಗಳು) ಆತನ ಕಡೆಗೆ ಮರಳಿಸಲ್ಪಡುವ ದಿನವನ್ನು ಅಲ್ಲಾಹು ಖಂಡಿತ ಅರಿಯುತ್ತಾನೆ. ಆಗ ಅವರು ಮಾಡಿದ ಕರ್ಮಗಳ ಪ್ರತಿಫಲವನ್ನು ಅಲ್ಲಿ ಅವನು ಅವರಿಗೆ ಕೊಡುವನು. ಅಲ್ಲಾಹು ಸಕಲ ವಿಷಯಗಳ ಜ್ಞಾನವುಳ್ಳವನು .');
INSERT INTO "kannada" VALUES(25,1,'25:1','ಸಮಸ್ತ ಜಗತ್ತಿನವರಿಗೆ ಎಚ್ಚರಿಕೆಗಾರನಾಗಲಿಕ್ಕಾಗಿ ತನ್ನ ದಾಸನ ಮೇಲೆ ಸತ್ಯಾಸತ್ಯವನ್ನು ಬೇರ್ಪಡಿಸಿ ತೋರಿಸುವ ವೇದಗ್ರಂಥವನ್ನು ಅವತೀರ್ಣಗೊಳಿಸಿದವನು ಪರಮಸುಮಂಗಲನು.');
INSERT INTO "kannada" VALUES(25,2,'25:2','ಅವನಿಗೆ ಭೂಮಿ-ಆಕಾಶಗಳ ಪ್ರಭುತ್ವವಿದೆ. ಅವನು ಪುತ್ರನನ್ನು ಉಂಟುಮಾಡಿಲ್ಲ. ಪ್ರಭುತ್ವದಲ್ಲಿ ಅವನಿಗೆ ಯಾರೂ ಸಹಭಾಗಿ ಇಲ್ಲ. ಅವನು ಪ್ರತಿಯೊಂದು ವಸ್ತುವನ್ನೂ ಸೃಷ್ಟಿಸಿದನು. ನಂತರ ಅದಕ್ಕೊಂದು ವಿಧಿ ನಿಯಮವನ್ನು ನಿಶ್ಚಯಿಸಿದನು .');
INSERT INTO "kannada" VALUES(25,3,'25:3','ಅವರು ಅವನನ್ನು ಬಿಟ್ಟು, ಇತರರನ್ನು ಆರಾಧ್ಯರನ್ನಾಗಿ ಮಾಡಿದರು. ಅವರು ಯಾವ ವಸ್ತುವನ್ನೂ ಸೃಷ್ಟಿಸಲಿಲ್ಲ. ಪರಂತು ಅವರೇ ಸೃಷ್ಟಿಸಲ್ಪಟ್ಟವರು. ಅವರು ಸ್ವತಃ ತಮಗೂ ಯಾವುದೇ ಲಾಭ ಅಥವಾ ಹಾನಿಯನ್ನುಂಟು ಮಾಡುವ ಒಡೆತನ ಹೊಂದಿಲ್ಲ. ಸಾವನ್ನಾಗಲಿ, ಬದುಕನ್ನಾಗಲಿ ಪುನರ್ಜೀವನವನ್ನಾಗಲಿ ಅವರು ಅಧೀನಗೊಳಿಸಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(25,4,'25:4','ಸತ್ಯನಿಷೇಧಿಗಳು “ಇದು (ಖುರ್‍ಆನ್) ಇವನೇ ಸ್ವಂತವಾಗಿ ನಿರ್ಮಿಸಿದ ಸುಳ್ಳು ಸೃಷ್ಟಿಯಾಗಿದ್ದು, ಬೇರೆ ಕೆಲವರು ಈ ಕಾರ್ಯದಲ್ಲಿ ಇವನಿಗೆ ಸಹಾ ಯ ಮಾಡಿದ್ದಾರೆ” ಎಂದರು. ಇವರು ದೊಡ್ಡ ಅಕ್ರಮ ಹಾಗೂ ಹಸಿ ಸುಳ್ಳನ್ನೇ ತಂದಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(25,5,'25:5','“ಇವು ಹಳೆಯ ಕಾಲದವರ ಕಟ್ಟು ಕಥೆಗಳು. ಅದನ್ನು ಇವನು ಬರೆಯಿಸಿಕೊಳ್ಳುತ್ತಿದ್ದು ಬೆಳಗು ಬೈಗುಗಳಲ್ಲಿ ಅವನಿಗೆ ಅದನ್ನು ಓದಿ ಕೇಳಿಸಲಾ ಗುತ್ತದೆ” ಎಂದೂ ಅವರು ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(25,6,'25:6','(ಪೈಗಂಬರರೇ) ಹೇಳಿರಿ, “ಭೂಮಿ-ಆಕಾಶಗಳ ರಹಸ್ಯವನ್ನು ಅರಿಯುವವನೇ ಇದನ್ನು (ಖುರ್ ಆನನ್ನು) ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದಾನೆ” ಖಂಡಿತ ಅವನು ಅತ್ಯಂತ ಕ್ಷಮಾಶೀಲನೂ ಕರುಣಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(25,7,'25:7','ಅವರು ಹೇಳಿದರು, “ಆಹಾರ ಸೇವಿಸುವ ಮತ್ತು ಪೇಟೆಗಳಲ್ಲಿ ಸಂಚರಿಸುವ ಈ ಪ್ರವಾದಿಗೆ ಏನಾಗಿದೆ? ಇವನ ಜೊತೆಗೆ ಒಬ್ಬ ತಾಕೀತುಗಾರನಾಗ ಬಲ್ಲ ಒಬ್ಬ ದೇವಚರನನ್ನೇಕೆ ಇವನ ಬಳಿಗೆ ಕಳುಹಿಸಲಾಗಿಲ್ಲ?');
INSERT INTO "kannada" VALUES(25,8,'25:8','ಅಥವಾ ಈತನ ಬಳಿಗೆ ಒಂದು ನಿಧಿಯನ್ನೇಕೆ ಇಡಲಾಗಿಲ್ಲ. ತನಗೆ ತಿನ್ನಲಿಕ್ಕಾಗಿ ಒಂದು ತೋಟವಾದರೂ ಏಕಿಲ್ಲ? “ಈ ಅಕ್ರಮಿಗಳು “ನೀವು ಬುದ್ಧಿಗೆ ಮಂಕು ಬಡಿದ ಕೇವಲ ಒಬ್ಬ ವ್ಯಕ್ತಿಯನ್ನು ಹಿಂಬಾಲಿಸುತ್ತಿರುವಿರಿ” ಎಂದೂ ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(25,9,'25:9','ನೋಡಿರಿ, ಇವರು ಎಂತಹ ಉಪಮೆಗಳನ್ನು ನಿಮ್ಮ ಮುಂದಿಟ್ಟರು? ಹೀಗೆ ಅವರು ಸತ್ಯ ದಾರಿಯಿಂದ ತಪ್ಪಿ ನಡೆದರು. ಸರಿಯಾದ ದಾರಿಯನ್ನು ಸೇರಲು ಅವರಿಗೆ ಸಾಧ್ಯವಾಗುತ್ತಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(25,10,'25:10','ಅವನು ಅನುಗ್ರಹಪೂರ್ಣನು. ಅವನಿಚ್ಚಿಸಿದರೆ, ಇವರು ಹೇಳುವುದಕ್ಕಿಂತ ಉತ್ತಮವಾದುದನ್ನೂ ತಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆಗಳು ಹರಿಯುತ್ತಿರುವ ಉದ್ಯಾನಗಳನ್ನೂ ನಿಮಗೆ ಮಾಡಿಕೊಡುವನು. ದೊಡ್ಡ ದೊಡ್ಡ ಅರಮನೆಗಳನ್ನೂ ದಯಪಾಲಿಸುವನು.');
INSERT INTO "kannada" VALUES(25,11,'25:11','ಆದರೆ ಇವರು, `ಅಂತಿಮ ಘಳಿಗೆಯನ್ನು ನಿಷೇಧಿಸಿದ್ದಾರೆ. ಅಂತ್ಯದಿನವನ್ನು ನಿಷೇಧಿಸಿ ತಳ್ಳಿ ಹಾಕಿದವನಿಗಾಗಿ ಹೊತ್ತಿ ಉರಿಯುವ ಬೆಂಕಿಯನ್ನು ನಾವು ಸಿದ್ಧಗೊಳಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(25,12,'25:12','ಅದು ಇವರನ್ನು ದೂರ ಸ್ಥಳದಿಂದ ಕಂಡಾಗಲೇ ಇವರು ಅದರ ಕೋಪದ ಕಿಡಿಯನ್ನು ಮತ್ತು ಕಿರುಚುವ ಚೀತ್ಕಾರವನ್ನು ಕೇಳುವರು.');
INSERT INTO "kannada" VALUES(25,13,'25:13','ಇವರು ಸಂಕೋಲೆಗಳಲ್ಲಿ ಬಿಗಿಯಲ್ಪಟ್ಟ ಸ್ಥಿತಿಯಲ್ಲಿ ಅದರ ಒಂದು ಇಕ್ಕಟ್ಟಾದ ಜಾಗಕ್ಕೆ ಎಸೆಯಲ್ಪಟ್ಟರೆ ಅಲ್ಲಿ ಅವರು, ಅಯ್ಯೋ, ನಮ್ಮ ನಾಶವೆಂದು ಕೂಗಾಡುವರು.');
INSERT INTO "kannada" VALUES(25,14,'25:14','“ಇಂದು ಒಂದು ನಾಶವನ್ನಲ್ಲ, ಅನೇಕ ನಾಶಗಳಿಗಾಗಿ ಕೂಗಿರಿ” ಎನ್ನಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(25,15,'25:15','ಹೇಳಿರಿ, “ಅದು ಉತ್ತಮವೋ? ಅಥವಾ ಧರ್ಮ ನಿಷ್ಟರಿಗೆ ವಾಗ್ದಾನ ಮಾಡಲಾಗಿರುವ, ಅವರ ಪ್ರತಿಫಲವೂ ನಿರ್ಗಮನ ಸ್ಥಾನವೂ ಆಗಿರುವ ಶಾಶ್ವತ ಸ್ವರ್ಗ ಉತ್ತಮವೋ?”');
INSERT INTO "kannada" VALUES(25,16,'25:16','ಸ್ವರ್ಗದಲ್ಲಿ ಸದಾಕಾಲ ವಾಸಿಸುವ ಅವರು ತಾವೇನು ಇಚ್ಛಿಸುವರೋ ಅವೆಲ್ಲ ಅವರಿಗಲ್ಲಿ ಸಿಗುವುದು. ನಿಮ್ಮ ಪ್ರಭುವಿನ ಮೇಲೆ ಅಪೇಕ್ಷಿ ಸಲ್ಪಡುವ ಒಂದು ವಾಗ್ದಾನವಾಗಿತ್ತದು .');
INSERT INTO "kannada" VALUES(25,17,'25:17','ಇವರನ್ನೂ ಇವರು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಪೂಜಿ ಸುತ್ತಿರುವ ವಸ್ತುಗಳನ್ನೂ ಅವನು ಒಟ್ಟು ಸೇರಿ ಸುವ ದಿನವನ್ನು ನೆನಪಿಸಿರಿ. ಆಗ ಅವನು ಕೇಳುವನು. “ನಮ್ಮ ಈ ದಾಸರನ್ನು ದಾರಿತಪ್ಪಿಸಿದ್ದು ನೀವೋ ಅಥವಾ ಇವರು ಸ್ವತಃ ದಾರಿ ತಪ್ಪಿದರೋ?”');
INSERT INTO "kannada" VALUES(25,18,'25:18','ಆಗ ಅವರು ಹೇಳುವರು, “ನೀನು ಪರಮ ಪಾವ ನನು, ನಿನ್ನ ಹೊರತು ಇತರರನ್ನು ಆರಾಧ್ಯರ ನ್ನಾಗಿ ಮಾಡುವುದು ನಮಗೆ ಭೂಷಣವಾಗಿರಲಿಲ್ಲ. ಆದರೆ ನೀನು ಇವರಿಗೂ ಇವರ ಪೂರ್ವಿಕರಿಗೂ ಸಾಕಷ್ಟು ಸುಖ-ಭೋಗಗಳನ್ನು ನೀಡಿದೆ. ಕೊನೆಗೆ ಇವರು ನಿನ್ನ ಉಪದೇಶವನ್ನು ಮರೆತರು. ನಾಶವಾದ ಜನಾಂಗವಾದರು”.');
INSERT INTO "kannada" VALUES(25,19,'25:19','(ಆಗ ಅಲ್ಲಾಹನು ಬಹುದೇವಾರಾಧಕರಲ್ಲಿ ಹೇಳುವನು) “ನೀವು ಹೇಳುತ್ತಿರುವ ಮಾತು ಗಳನ್ನು ಇವರು ಸುಳ್ಳಾಗಿಸಿದ್ದಾರೆ. (ಇವರು ಆರಾಧ್ಯರು ಎಂಬ ವಾದವನ್ನು ನಿಷೇಧಿಸಿದ್ದಾರೆ.) ಇನ್ನು ನೀವು ಶಿಕ್ಷೆಯನ್ನು ನಿವಾರಿಸಲಿಕ್ಕಾಗಲಿ ಸಹಾಯವನ್ನು ಪಡೆಯಲಿಕ್ಕಾಗಿ ಶಕ್ತರಲ್ಲ. ನಿಮ್ಮಲ್ಲಿ ಯಾರೇ ಅಕ್ರಮವೆಸಗಿದರೂ ಅವನಿಗೆ ನಾವು ಮಹಾ ಶಿಕ್ಷೆಯನ್ನು ಉಣಿಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(25,20,'25:20','(ಪೈಗಂಬರರೇ,) ಸಂದೇಶವಾಹಕರ ಪೈಕಿ ಆಹಾರ ಸೇವಿಸುವ ಮತ್ತು ಪೇಟೆಗಳಲ್ಲಿ ಸಂಚರಿಸುವವರನ್ನೇ ಹೊರತು ನಿಮಗೆ ಮುನ್ನ ನಾವು ನಿಯೋಗಿಸಿಲ್ಲ. ನಾವು ನಿಮ್ಮನ್ನು ಪರಸ್ಪರರ ಪಾಲಿಗೆ ಪರೀಕ್ಷಾ ಸಾಧನವನ್ನಾಗಿ ಮಾಡಿರುತ್ತೇವೆ. ನೀವು ತಾಳ್ಮೆ ವಹಿಸುವಿರಾ? ನಿಮ್ಮ ಪ್ರಭು ಸರ್ವ ವೀಕ್ಷಕನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(25,21,'25:21','ನಮ್ಮನ್ನು ಭೇಟಿಯಾಗಲಿಕ್ಕಿದೆಯೆಂಬ ಆಗ್ರಹ ವನ್ನಿರಿಸಿಕೊಳ್ಳದವರು, “ನಮ್ಮ ಮೇಲೆ ದೇವಚ ರರನ್ನೇಕೆ ಇಳಿಸಲಾಗಿಲ್ಲ? ಅಥವಾ ನಾವು ನಮ್ಮ ಪ್ರಭುವನ್ನು ಏಕೆ ಕಣ್ಣಾರೆ ಕಾಣುತ್ತಿಲ್ಲ?” ಎನ್ನುತ್ತಾರೆ. ಇವರು ತಮ್ಮ ಮನಸ್ಸಿನಲ್ಲಿ ದೊಡ್ಡ ಗರ್ವ ತೋರಿದ್ದಾರೆ ಮತ್ತು ಅತಿ ದೊಡ್ಡ ದಿಕ್ಕಾರ ವನ್ನು ತೋರ್ಪಡಿಸಿದ್ದಾರೆ .');
INSERT INTO "kannada" VALUES(25,22,'25:22','ಇವರು ದೇವಚರರನ್ನು ಕಾಣುವ ದಿನವನ್ನು ನೆನೆಸಿರಿ. ಅಪರಾಧಿಗಳ ಪಾಲಿಗೆ ಅಂದು ಯಾವುದೇ ಸಂತಸದ ವಾರ್ತೆ ಇರಲಾರದು. `ಬಲವಾದ ತಡೆ’ ಎಂದು ಮಲಕ್‍ಗಳು ಹೇಳುವರು .');
INSERT INTO "kannada" VALUES(25,23,'25:23','ಅವರು ಮಾಡಿಟ್ಟಿರುವ ಎಲ್ಲಾ ಕರ್ಮಗಳತ್ತ ನಾವು ಗಮನಹರಿಸುವೆವು. ನಂತರ ಅದನ್ನು ನಾವು ಧೂಳಿನಂತೆ ಹಾರಿಸಿಬಿಡುವೆವು .');
INSERT INTO "kannada" VALUES(25,24,'25:24','ಸ್ವರ್ಗದವರು ಆ ದಿನ ಒಳ್ಳೆಯ ಜಾಗದಲ್ಲಿ ತಂಗುವರು ಮತ್ತು ಉತ್ತಮ ವಿಶ್ರಾಂತಿಯ ಸ್ಥಳವನ್ನು ಪಡೆಯುವರು .');
INSERT INTO "kannada" VALUES(25,25,'25:25','ಮೋಡದೊಂದಿಗೆ ಆಕಾಶವು ಸೀಳುವ ಮತ್ತು ದೇವಚರರು ತಂಡ ತಂಡವಾಗಿ ಇಳಿಸಲ್ಪಡುವ ದಿನ.');
INSERT INTO "kannada" VALUES(25,26,'25:26','ಅಂದು ನಿಜವಾದ ಸಾರ್ವಭೌಮತೆಯು ದಯಾನಿಧಿಯಾದ ಅಲ್ಲಾಹನಿಗಾಗಿರುವುದು. ಅದು ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಬಹಳ ಕಠಿಣ ದಿನವಾಗಿರುವುದು.');
INSERT INTO "kannada" VALUES(25,27,'25:27','ಅಕ್ರಮಿಯು ತನ್ನ ಕೈಗಳನ್ನು ಕಚ್ಚಿಕೊಳ್ಳುವ ದಿನ . ಅವನು ಹೇಳುವನು, “ಅಯ್ಯೋ, ನಾನು ಪ್ರವಾದಿಯೊಂದಿಗೆ ಸನ್ಮಾರ್ಗವನ್ನು ಸ್ವೀಕರಿಸುತ್ತಿದ್ದರೆ ಎಷ್ಟು ಚೆನ್ನಾಗಿತ್ತು!');
INSERT INTO "kannada" VALUES(25,28,'25:28','ಅಯ್ಯೋ ನನ್ನ ಕಷ್ಟವೇ, ನಾನು ಆ ವ್ಯಕ್ತಿಯನ್ನು ಮಿತ್ರನಾಗಿ ಮಾಡಿಕೊಳ್ಳದಿರುತ್ತಿದ್ದರೆ ಎಷ್ಟು ಚೆನ್ನಾಗಿತ್ತು!');
INSERT INTO "kannada" VALUES(25,29,'25:29','ನನ್ನ ಬಳಿಗೆ (ಖುರ್‍ಆನಿನ) ಸದ್ಭೋಧನೆ ಬಂದ ಬಳಿಕ ಅವನು ಅದರಿಂದ ನನ್ನನ್ನು ದಾರಿ ತಪ್ಪಿಸಿದನು. ಶೈತಾನನು ಮನುಷ್ಯನಿಗೆ ಮಹಾ ವಂಚಕನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(25,30,'25:30','ಸಂದೇಶವಾಹಕರು, “ನನ್ನ ಪ್ರಭೂ, ನನ್ನ ಜನಾಂಗದವರು ಈ ಖುರ್‍ಆನನ್ನು ತ್ಯಾಜ ವಸ್ತುವನ್ನಾಗಿ ಮಾಡಿದರು” ಎನ್ನುವರು .');
INSERT INTO "kannada" VALUES(25,31,'25:31','(ಪೈಗಂಬರರೇ,) ಇದೇ ರೀತಿಯಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬ ಪ್ರವಾದಿಗೆ ಶತ್ರುವನ್ನು ನಾವು ಮಾಡಿರುತ್ತೇವೆ. ಮಾರ್ಗದರ್ಶಕನೂ, ಸಹಾಯಕನೂ ಆಗಿ ನಿಮ್ಮ ಪ್ರಭುವೇ ಸಾಕು.');
INSERT INTO "kannada" VALUES(25,32,'25:32','ಸತ್ಯನಿಷೇಧಿಗಳು, “ಇವನ ಮೇಲೆ ಇಡೀ ಖುರ್ ಅನ್ ಒಮ್ಮೆಲೆ ಒಟ್ಟಾಗಿ ಏಕೆ ಅವತೀರ್ಣ ಗೊಂಡಿಲ್ಲ?” ಎಂದು ಕೇಳುತ್ತಾರೆ. ತಮ್ಮ ಹೃದಯ ವನ್ನು ಇದರ ಮೂಲಕ ಬಲಪಡಿಸುವುದಕ್ಕಾಗಿ ಹೀಗೆ ಮಾಡಲಾಗಿದೆ. ಆದ್ದರಿಂದ ಸಾವಧಾನ ಕ್ರಮದಲ್ಲಿ ಅದನ್ನು ನಾವು ಓದಿ ಕೊಡುತ್ತಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(25,33,'25:33','ಅವರು ನಿಮ್ಮ ಮುಂದೆ ಸತ್ಯವನ್ನು ವಿರೋಧಿಸಲು ಯಾವ ಮಾತನ್ನು ಬಳಸಿದರೂ ನಾವು ಅದಕ್ಕೆ ಸಮರ್ಪಕವಾದ ಉತ್ತರವನ್ನೂ ಅತ್ಯುತ್ತಮ ವಿವರಣೆಯನ್ನೂ ಕೊಡದೆ ಇರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(25,34,'25:34','ಅಧೋಮುಖರಾಗಿ ನರಕದ ಕಡೆಗೆ ಒಯ್ಯಲ್ಪಡು ವವರು ಬಹಳ ಕೆಟ್ಟ ನೆಲೆಯವರು ಮತ್ತು ತೀರಾ ತಪ್ಪು ದಾರಿ ತುಳಿದವರು.');
INSERT INTO "kannada" VALUES(25,35,'25:35','ಮೂಸಾರಿಗೆ ನಾವು ವೇದ ಗ್ರಂಥ ನೀಡಿದೆವು. ಅವರ ಜೊತೆಗೆ ಅವರ ಸಹೋದರ ಹಾರೂನರನ್ನು ಸಹಾಯಕನಾಗಿ ನೇಮಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(25,36,'25:36','ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದ ಆ ಜನಾಂಗದ ಕಡೆಗೆ ನೀವಿಬ್ಬರೂ ಹೋಗಿರೆಂದು ಅವರಿಗೆ ಹೇಳಿದೆವು. (ಆ ಜನತೆ ಅವರಿಬ್ಬರನ್ನೂ ನಿಷೇಧಿಸಿತು). ಕೊನೆಗೆ ನಾವು ಅವರನ್ನು ಸಮೂಲ ನಾಶಗೊಳಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(25,37,'25:37','ನೂಹರ ಜನಾಂಗವು ಪ್ರವಾದಿಗಳನ್ನು ಸುಳ್ಳಾಗಿ ಸಿದಾಗ ನಾವು ಅವರನ್ನು ನೀರಿನಲ್ಲಿ ಮುಳುಗಿ ಸಿನಾಶ ಮಾಡಿದೆವು. ಅದನ್ನು ಜನರಿಗೆ ಒಂದು ದೃಷ್ಟಾಂತವಾಗಿ ಮಾಡಿದೆವು. ಅಕ್ರಮಿಗಳಿಗೆ ನಾವು ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆಯನ್ನು (ಪರ ಲೋಕದಲ್ಲಿ) ಸಿದ್ಧಗೊಳಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(25,38,'25:38','ಇದೇ ರೀತಿ ಆದ್, ಸಮೂದ್ ಮತ್ತು ರಸ್ಸ್‍ನವರ ಹಾಗೂ ಅವರ ಮಧ್ಯದ ಶತಮಾನಗಳ ಅನೇಕರನ್ನು ನೆನಪಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(25,39,'25:39','ಎಲ್ಲರಿಗೂ ನಾವು ಉದಾಹರಣೆಗಳನ್ನು ವಿವರಿಸಿ ಕೊಟ್ಟೆವು. ಕೊನೆಗೆ ಎಲ್ಲರನ್ನೂ ಸರ್ವ ನಾಶಗೊಳಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(25,40,'25:40','ಕೆಟ್ಟ ಮಳೆಗೆರೆಯಲಾಗಿದ್ದ ಆ ನಾಡನ್ನು ಅವರು ಹಾದು ಹೋಗಿದ್ದಾರೆ. ಆಗ ಅವರದನ್ನು ಕಾಣಲಿಲ್ಲವೇ? ಆದರೆ ಅವರು ಪುನರುತ್ಥಾನದ ನಿರೀಕ್ಷೆಯನ್ನೇ ಇಟ್ಟುಕೊಂಡಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(25,41,'25:41','(ಪೈಗಂಬರರೇ,) “ಅವರು ನಿಮ್ಮನ್ನು ಕಂಡಾಗಲೆಲ್ಲಾ ನಿಮ್ಮನ್ನು ಗೇಲಿಯ ವಸ್ತುವನ್ನಾಗಿ ಮಾಡುತ್ತಾರೆ. `ಅಲ್ಲಾಹನು ಸಂದೇಶವಾಹಕನಾಗಿ ಮಾಡಿ ಕಳುಹಿಸಿದ್ದು ಇವನನ್ನೇ?.');
INSERT INTO "kannada" VALUES(25,42,'25:42','ನಮ್ಮ ಆರಾಧ್ಯರ ಪೂಜೆಯಲ್ಲಿ ನಾವು ಅಚಲ ರಾಗದೆ ನಿಂತಿದ್ದರೆ ಇವನು ನಮ್ಮನ್ನು ದಾರಿ ತಪ್ಪಿಸುತ್ತಿದ್ದನು” ಎಂದು. ಶಿಕ್ಷೆಯನ್ನು ಕಾಣುವ ವೇಳೆ ತೀರಾ ತಪ್ಪು ದಾರಿ ತುಳಿದವನು ಯಾರೆಂದು ಸ್ವತಃ ಅವರಿಗೇ ತಿಳಿದು ಬರುವುದು.');
INSERT INTO "kannada" VALUES(25,43,'25:43','ತನ್ನ ದೇಹೇಚ್ಚೆಯನ್ನು ತನ್ನ ದೇವನಾಗಿ ಮಾಡಿದವನನ್ನು ತಾವು ಕಂಡಿರಾ? ನೀವು ಈತನ ಮೇಲೆ ಹೊಣೆ ವಹಿಸಿಕೊಳ್ಳುವಿರಾ?');
INSERT INTO "kannada" VALUES(25,44,'25:44','ಅದಲ್ಲ, ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಆಲಿಸುತ್ತಾರೆ, ಇಲ್ಲವೇ ಯೋಚಿಸುತ್ತಾರೆ ಎಂದು ನೀವು ಭಾವಿ ಸುತ್ತೀರಾ? ಇವರು ಪ್ರಾಣಿಗಳಂತಿದ್ದಾರೆ. ಅಲ್ಲ, ಅವಕ್ಕಿಂತಲೂ ಪಥಭ್ರಷ್ಟರು .');
INSERT INTO "kannada" VALUES(25,45,'25:45','ನಿಮ್ಮ ಪ್ರಭು ನೆರಳನ್ನು ಹೇಗೆ ಹರಡಿಸುತ್ತಾ ನೆಂಬುದನ್ನು ನೀವು ಕಂಡಿಲ್ಲವೇ? ಅವನು ಇಚ್ಛಿಸಿದ್ದರೆ ಅದನ್ನು ನಿಶ್ಚಲಗೊಳಿಸುತ್ತಿದ್ದನು. ನಂತರ ನಾವು ಸೂರ್ಯನನ್ನು ಆ ನೆರಳಿಗೆ ಸಂಕೇತವನ್ನಾಗಿ ಮಾಡಿದ್ದೇವೆ .');
INSERT INTO "kannada" VALUES(25,46,'25:46','ಅನಂತರ ನಾವು ಆ ನೆರಳನ್ನು ಕ್ರಮೇಣ ನಮ್ಮ ಕಡೆಗೆ ಕುಗ್ಗಿಸುತ್ತ ಹೋಗುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(25,47,'25:47','ರಾತ್ರಿಯನ್ನು ನಿಮಗೆ ಉಡುಪಾಗಿಯೂ ನಿದ್ದೆಯನ್ನು ವಿಶ್ರಾಂತಿಯಾಗಿಯೂ ಹಗಲನ್ನು ಎಚ್ಚೆತ್ತುಕೊಂಡು ಸಂಚರಿಸುವ ಸಮಯವಾಗಿಯೂ ಮಾಡಿದವನು ಅಲ್ಲಾಹನೇ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(25,48,'25:48','ಅವನು ತನ್ನ ಕೃಪೆಗೆ (ಮಳೆಗೆ) ಮುಂಚಿತವಾಗಿ ಮಾರುತಗಳನ್ನು ಸುವಾರ್ತೆಯಾಗಿ ಕಳುಹಿಸು ವವನು. ಅನಂತರ ನಾವು ಆಕಾಶದಿಂದ ಶುದ್ಧ ತಿಳಿನೀರನ್ನು ಇಳಿಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(25,49,'25:49','ಒಂದು ನಿರ್ಜೀವ ಪ್ರದೇಶಕ್ಕೆ ಇದರಿಂದ ಜೀವ ದಾನ ಮಾಡಲಿಕ್ಕಾಗಿ ಮತ್ತು ತನ್ನ ಸೃಷ್ಟಿಯ ಪೈಕಿ ಅನೇಕ ಜಾನುವಾರುಗಳಿಗೂ ಮನುಷ್ಯರಿಗೂ ಅದನ್ನು ಕುಡಿಸಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(25,50,'25:50','ಅವರು ಯೋಚಿಸಲಿಕ್ಕಾಗಿ ನಾವು ಅದನ್ನು ನಿರೂಪಿಸಿದ್ದೇವೆ . ಆದರೆ ಹೆಚ್ಚಿನವರು ಕೃತಘ್ನತೆಯ ಹೊರತು ಬೇರೇನನ್ನೂ ಅಂಗೀಕರಿಸುತ್ತಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(25,51,'25:51','ನಾವಿಚ್ಛಿಸುತ್ತಿದ್ದರೆ ಪ್ರತಿಯೊಂದು ನಾಡಿಗೂ ಎಚ್ಚರಿಕೆದಾರನನ್ನು ನಿಯೋಗಿಸುತ್ತಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(25,52,'25:52','ಆದುದರಿಂದ (ಪೈಗಂಬರರೇ,) ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ಅನುಸರಿಸಬೇಡಿರಿ. ಈ ಖುರ್‍ಆನಿನ ಮೂಲಕ ಅವರ ವಿರುದ್ಧ ಉಗ್ರ ಹೋರಾಟ ನಡೆಸಿರಿ .');
INSERT INTO "kannada" VALUES(25,53,'25:53','ಅವನು ಎರಡು ಸಮುದ್ರಗಳನ್ನು ಸೇರಿಸಿಟ್ಟಿರು ವವನು. ಒಂದು ರುಚಿಕರವಾದ ಸಿಹಿ ನೀರು. ಇನ್ನೊಂದು ಕಹಿಯಾದ ಉಪ್ಪು ನೀರು. ಇವೆರಡರ ಮಧ್ಯೆ ಒಂದು ಬಲವಾದ ತೆರೆಯನ್ನು ನಿರ್ಮಿಸಿರುವನು. (ಅವು ಪರಸ್ಪರ ಬೆರೆಯದಂತೆ ಮಾಡುವ) ಒಂದು ಗಟ್ಟಿಯಾದ ತಡೆಯನ್ನೂ ನಿರ್ಮಿಸಿರುವನು .');
INSERT INTO "kannada" VALUES(25,54,'25:54','ಅವನೇ ನೀರಿನಿಂದ ಮಾನವನನ್ನು ಸೃಷ್ಟಿಸಿದವನು. ತರುವಾಯ ಅವನು ಮನುಷ್ಯನಿಂದ ವಂಶೀಯ ಮತ್ತು ವೈವಾಹಿಕ ಸಂಬಂಧವನ್ನು ಉಂಟು ಮಾಡಿದನು. ನಿನ್ನ ಪ್ರಭು ಸರ್ವ ಸಮರ್ಥನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(25,55,'25:55','ಅವರು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಅವರಿಗೆ ಲಾಭವಾಗಲಿ ನಷ್ಟವಾಗಲಿ ಉಂಟು ಮಾಡಲಾರದ ಕೆಲವು ವಸ್ತುಗಳನ್ನು ಆರಾಧಿಸುತ್ತಾರೆ. ಸತ್ಯ ನಿಷೇಧಿಯು ತನ್ನ ಪ್ರಭುವಿನ ವಿರುದ್ಧ (ಪಿಶಾಚಿಯ ಮಾರ್ಗದ) ಸಹಾಯಕನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(25,56,'25:56','(ಪೈಗಂಬರರೇ,) ನಾವು ನಿಮ್ಮನ್ನು ಓರ್ವ ಶುಭ ವಾರ್ತೆ ಹಾಗೂ ಎಚ್ಚರಿಕೆ ನೀಡುವವರಾಗಿ ಮಾತ್ರ ಕಳುಹಿಸಿರುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(25,57,'25:57','ಇವರಿಗೆ “ನಾನು ಈ ದೌತ್ಯ ನಿರ್ವಹಣೆಗಾಗಿ ನಿಮ್ಮಿಂದ ಯಾವುದೇ ಪ್ರತಿಫಲ ಕೇಳುವುದಿಲ್ಲ. ಆದರೆ ಯಾರಾದರೂ ತನ್ನ ಪ್ರಭುವಿನೆಡೆಗೆ ಉತ್ತಮ ಮಾರ್ಗವನ್ನು ಸ್ಥಾಪಿಸಲು (ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ದಾನ ಮಾಡಲು) ಉದ್ದೇಶಿಸಿದರೆ ಹಾಗೆ ಮಾಡಲಿ” ಎಂದು ಹೇಳಿರಿ.');
INSERT INTO "kannada" VALUES(25,58,'25:58','(ಪೈಗಂಬರರೇ,) ಎಂದೂ ಮರಣ ಹೊಂದದ, ಅಮರನಾಗಿರುವ ಅಲ್ಲಾಹನ ಮೇಲೆ ಭರವಸೆ ಯಿಡಿರಿ. ಅವನ ಸ್ತುತಿಯೊಂದಿಗೆ ಅವನ ಪರಿಶುದ್ಧ ತೆಯನ್ನು ಕೊಂಡಾಡಿರಿ. ತನ್ನ ದಾಸರ ಪಾಪಗಳ ಬಗ್ಗೆ ಸೂಕ್ಷ್ಮಜ್ಞಾನಿಯಾಗಿ ಅವನು ಮಾತ್ರ ಸಾಕು.');
INSERT INTO "kannada" VALUES(25,59,'25:59','ಅವನು ಭೂಮಿ-ಆಕಾಶಗಳನ್ನೂ ಅವುಗಳ ನಡುವೆ ಇರುವುದನ್ನೂ ಆರು ದಿನಗಳಲ್ಲಿ ಸೃಷ್ಟಿಸಿ ದನು. ತರುವಾಯ ಅವುಗಳ ತರುವಾಯ ಅವನಿ ಗೆ ಸಂಗತವಾದ ರೀತಿಯಲ್ಲಿ ಅರ್ಶ್‍ನ ಮೇಲೆ ಇಸ್ತಿವಾ ಹೊಂದಿದನು ಂ. ಪರಮ ದಯಾಮಯ, ಅವನ ಘನತೆಯನ್ನು ಸೂಕ್ಷ್ಮವಾಗಿ ಬಲ್ಲವರೊಡನೆ ಕೇಳಿರಿ.');
INSERT INTO "kannada" VALUES(25,60,'25:60','‘ರಹ್ಮಾನನಿಗೆ ಸಾಷ್ಟಾಂಗವೆರಗಿರಿ’ ಎಂದು ಅವರಿಗೆ ಹೇಳಿದರೆ, “ರಹ್ಮಾನ್ ಎಂದರೇನು? ನೀನು ಆಜ್ಞಾಪಿಸುವ ವಸ್ತುವಿಗೆ ನಾವು ಸಾಷ್ಟಾಂ ಗವೆರಗಬೇಕೆ?” ಎಂದು ಕೇಳುತ್ತಾರೆ. ಈ ಉಪದೇಶವು ಅವರ ತಿರಸ್ಕಾರವನ್ನು ಇನ್ನಷ್ಟು ಹೆಚ್ಚಿಸುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(25,61,'25:61','ಆಕಾಶದಲ್ಲಿ ನಕ್ಷತ್ರ ರಾಶಿಗಳನ್ನು ಮಾಡಿದವನು ಮತ್ತು ಅದರಲ್ಲಿ ಒಂದು ಸೂರ್ಯ ಮತ್ತು ಹೊಳೆಯುವ ಚಂದ್ರನನ್ನು ಬೆಳಗಿದವನು ಪರಮ ಮಂಗಳನು.');
INSERT INTO "kannada" VALUES(25,62,'25:62','ಯೋಚಿಸಲು ಅಥವಾ ಕೃತಜ್ಞನಾಗಲು ಬಯಸುವ ಪ್ರತಿಯೊಬ್ಬನಿಗಾಗಿ ರಾತ್ರಿ ಹಗಲುಗಳನ್ನು ಪದೇ ಪದೇ ಮರಳಿ ಬರುವ ವಸ್ತುವನ್ನಾಗಿ ಮಾಡಿದವನು ಅವನೇ .');
INSERT INTO "kannada" VALUES(25,63,'25:63','ಪರಮ ದಯಾಳುವಾದ ಅಲ್ಲಾಹನ ನಿಜವಾದ ದಾಸರು ಭೂಮಿಯ ಮೇಲೆ ವಿನಯದಿಂದ ನಡೆಯುತ್ತಾರೆ. ತಿಳಿಗೇಡಿಗಳು ಎದುರುಗೊಂಡು ಮಾತನಾಡಿದರೆ, ರಕ್ಷಣೆಯ ಮಾತನ್ನು ಹೇಳುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(25,64,'25:64','ಅವರು ತಮ್ಮ ಪ್ರಭುವಿಗೆ ಸಾಷ್ಟಾಂಗವೆರಗುತ್ತಲೂ ನಿಂತು ಪ್ರಾರ್ಥಿಸುತ್ತಲೂ ರಾತ್ರಿಗಳನ್ನು ಕಳೆಯುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(25,65,'25:65','ಅವರು ಹೀಗೆ ಪ್ರಾರ್ಥಿಸುತ್ತಾರೆ, “ನಮ್ಮ ಪ್ರಭೂ, ನಮ್ಮನ್ನು ನರಕದ ಶಿಕ್ಷೆಯಿಂದ ರಕ್ಷಿಸು. ನಿಜಕ್ಕೂ ಅದರ ಶಿಕ್ಷೆಯು ನಿತ್ಯ ದುರಂತವಾಗಿದೆ .');
INSERT INTO "kannada" VALUES(25,66,'25:66','ಅದು ಅತ್ಯಂತ ನಿಕೃಷ್ಟ ನೆಲೆ ಮತ್ತು ಸ್ಥಾನವಾಗಿರುತ್ತದೆ’’.');
INSERT INTO "kannada" VALUES(25,67,'25:67','ಅವರು ಖರ್ಚು ಮಾಡುವಾಗ ದುಂದುವೆಚ್ಚ ವನ್ನೂ ಮಾಡುವುದಿಲ್ಲ. ಜಿಪುಣತೆಯನ್ನೂ ತೋರುವುದಿಲ್ಲ. ಅವರ ಖರ್ಚು ಇವೆರಡರ ಮಧ್ಯೆ ನೆಲೆಯಲ್ಲಾಗಿರುವುದು.');
INSERT INTO "kannada" VALUES(25,68,'25:68','ಅವರು ಅಲ್ಲಾಹನ ಹೊರತು ಬೇರೆ ಯಾವ ಆರಾಧ್ಯನನ್ನೂ ಆರಾಧಿಸುವುದಿಲ್ಲ. ಅಲ್ಲಾಹನು ವಿರೋಧಿಸಿದ ಯಾವ ಜೀವವನ್ನೂ ಅನ್ಯಾಯವಾಗಿ ಕೊಲೆ ಮಾಡುವುದಿಲ್ಲ. ವ್ಯಭಿಚಾರವೆಸಗುವುದಿಲ್ಲ. ಈ ಕಾರ್ಯಗಳನ್ನು ಯಾರಾದರೂ ಮಾಡಿದರೆ ಅದರ ಶಿಕ್ಷೆಯನ್ನು ಪಡೆಯುವನು.');
INSERT INTO "kannada" VALUES(25,69,'25:69','ಅಂತ್ಯ ದಿನದಂದು ಅವನಿಗೆ ಇಮ್ಮಡಿ ಶಿಕ್ಷೆ ನೀಡಲಾಗುವುದು. ಅವನು ಅದರಲ್ಲೇ ನಿಂದ್ಯನಾಗಿ ಸದಾ ಬಿದ್ದುಕೊಂಡಿರುವನು.');
INSERT INTO "kannada" VALUES(25,70,'25:70','ಆದರೆ ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು, ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿ, ಸತ್ಕರ್ಮಗಳನ್ನು ಮಾಡಿದರೆ ಅಲ್ಲಾಹನು ಅವರ ಪಾಪಗಳನ್ನು ಪುಣ್ಯಗಳಾಗಿ ಪರಿವರ್ತಿಸುವನು, ಅಲ್ಲಾಹು ಅತ್ಯಂತ ಕ್ಷಮಾಶೀಲನೂ ದಯಾ ನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(25,71,'25:71','ಯಾರು ಪಾಪದಿಂದ ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು ಸತ್ಕರ್ಮಗಳನ್ನು ಮಾಡುತ್ತಾರೆ, ಅವರು ಅಲ್ಲಾಹನ ಕಡೆಗೆ ತೃಪ್ತಿಕರವಾದ ರೀತಿಯಲ್ಲಿ ಮರಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(25,72,'25:72','ಅವರು ಸುಳ್ಳಿಗೆ ಸಾಕ್ಷಿಗಳಾಗುವುದಿಲ್ಲ. ಅವರು ವ್ಯರ್ಥ ವಿಷಯಗಳ ಸಮೀಪದಿಂದ ಹಾದು ಹೋಗುವುದಾದರೆ, ಮಾನ್ಯರಾಗಿ ಹಾದು ಹೋಗುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(25,73,'25:73','ತಮ್ಮ ಪ್ರಭುವಿನ (ಖುರ್‍ಆನ್) ವಚನಗಳನ್ನು ಹೇಳಿ ಉಪದೇಶ ಮಾಡಿದರೆ, ಅವರು ಅದರ ಬಗ್ಗೆ ಕುರುಡರೂ ಕಿವುಡರೂ ಆಗಿ ಬಿದ್ದುಕೊಂಡಿ ರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(25,74,'25:74','ಅವರು, `ನಮ್ಮ ಪ್ರಭೂ, ನಮ್ಮ ಪತ್ನಿಯರಿಂದಲೂ ಮಕ್ಕಳಿಂದಲೂ ನಮ್ಮ ಕಣ್ಣುಗಳನ್ನು ತಣಿಸು ವವರನ್ನು ನಮಗೆ ದಯಪಾಲಿಸು. ನಮ್ಮನ್ನು ಧರ್ಮನಿಷ್ಟರ ಪಾಲಿಗೆ ಆದರ್ಶ ನಾಯಕರಾಗಿ ಮಾಡು’ ಎಂದು ಪ್ರಾರ್ಥಿಸುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(25,75,'25:75','ತಮ್ಮ ಸಹನೆಯ ಕಾರಣದಿಂದ ಅವರಿಗೆ (ಪರಲೋಕದಲ್ಲಿ) ಉನ್ನತ ಅಂತಸ್ತಿನ ಸ್ವರ್ಗವನ್ನು ಪ್ರತಿಫಲ ನೀಡಲಾಗುವುದು. ಅವರನ್ನು ಗೌರವ ಪೂರ್ಣ ಅಭಿನಂದನೆ ಹಾಗೂ ಶಾಂತಿ ಸಂದೇಶಗಳೊಂದಿಗೆ ಸ್ವಾಗತಿಸಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(25,76,'25:76','ಅವರು ಅದರಲ್ಲಿ ಶಾಶ್ವತವಾಗಿ ನೆಲೆಸುವರು. ಆ ನೆಲೆ ಮತ್ತು ಆ ಸ್ಥಾನ ಎಷ್ಟು ಉತ್ತಮವಾದುದು! ');
INSERT INTO "kannada" VALUES(25,77,'25:77','(ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ. ನೀವು ನನ್ನ ಪ್ರಭು ವನ್ನು ಆರಾಧಿಸದಿದ್ದರೆ ಅವನು ನಿಮ್ಮನ್ನು ಲೆಕ್ಕಿಸ ಲಾರನು. ಆದರೆ ನೀವು ಸತ್ಯವನ್ನು ಸುಳ್ಳಾಗಿಸಿದ್ದೀರಿ, ಆದ್ದರಿಂದ ನಂತರ ಅದರ ಶಿಕ್ಷೆ ಅನಿವಾರ್ಯ ವಾಗುವುದು .');
INSERT INTO "kannada" VALUES(26,1,'26:1','ತ್ವಾ ಸೀನ್ ಮೀಮ್.');
INSERT INTO "kannada" VALUES(26,2,'26:2','ಮಹತ್ತರವಾದ ಈ ಅಧ್ಯಾಯದ ವಾಕ್ಯಗಳು ಸುಸ್ಪಷ್ಟವಾದ ಖುರ್‍ಆನ್ ಗ್ರಂಥದ ಸೂಕ್ತಗ ಳಾಗಿವೆ.');
INSERT INTO "kannada" VALUES(26,3,'26:3','(ಸಂದೇಶವಾಹಕರೇ,) ಇವರು ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಸ್ವೀಕರಿಸುವುದಿಲ್ಲವೆಂಬ ಕಾರಣಕ್ಕೆ ನೀವು ನಿಮ್ಮ ಜೀವವನ್ನೇ ಅಪಾಯಕ್ಕೆ ಎಳೆಯಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(26,4,'26:4','ನಾವಿಚ್ಛಿಸಿದರೆ ಇವರ ಮೇಲೆ ಆಕಾಶದಿಂದ ದೃಷ್ಟಾಂತವನ್ನು ಇಳಿಸಿ, ಅದಕ್ಕೆ ಇವರ ಕೊರಳು ಗಳು ಮಣಿಯುವಂತೆ ಮಾಡುತ್ತಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(26,5,'26:5','ಇವರ ಬಳಿಗೆ ದಯಾನಿಧಿಯಾದ ಅಲ್ಲಾಹನ ಕಡೆ ಯಿಂದ ಹೊಸತಾಗಿ ಯಾವ ಉಪದೇಶ ಬಂದರೂ ಇವರು ಅದರಿಂದ ವಿಮುಖರಾಗುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(26,6,'26:6','ಕೊನೆಗೆ ಇವರು ಸುಳ್ಳಾಗಿಸಿ ಬಿಟ್ಟರು. ಆದ್ದರಿಂದ ಇವರು ಯಾವುದನ್ನು ಪರಿಹಾಸ್ಯ ಮಾಡುತ್ತಿರು ವರೋ ಅದರ ವೃತ್ತಾಂತವು ಇವರಿಗೆ ಸದ್ಯವೇ ತಿಳಿದು ಬರಲಿದೆ.');
INSERT INTO "kannada" VALUES(26,7,'26:7','ಎಲ್ಲ ವಿಧದ ಅತ್ಯುತ್ತಮ ಸಸ್ಯಗಳನ್ನು ನಾವು ಇಲ್ಲಿ ಎಷ್ಟೆಲ್ಲ ಬೆಳೆಸಿದ್ದೇವೆ ಎಂದು ಇವರು ಭೂಮಿಯ ಕಡೆಗೆ ನೋಡುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(26,8,'26:8','ಇದರಲ್ಲಿ ಖಂಡಿತ ನಿದರ್ಶನವಿದೆ. ಆದರೆ ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ವಿಶ್ವಾಸವಿರಿಸುವವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(26,9,'26:9','ಖಂಡಿತ ನಿಮ್ಮ ಪ್ರಭು ಪ್ರಬಲನೂ ಕರುಣಾನಿಧಿ ಯೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(26,10,'26:10','ನಿನ್ನ ಪ್ರಭು ಮೂಸಾರನ್ನು ಕರೆದು “ನೀವು ಅಕ್ರಮಿ ಜನಾಂಗದ ಅಂದರೆ ಫಿರ್‍ಔನನ ಜನಾಂಗದ ಕಡೆಗೆ ಹೋಗಿರಿ” ಹೇಳಿದ ಸಂದರ್ಭವನ್ನು ನೆನಪಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(26,11,'26:11','ಅಂದರೆ ಫಿರ್‍ಔನನ ಜನಾಂಗದ ಕಡೆಗೆ. ‘ಅವರು ಅಲ್ಲಾಹನನ್ನು ಭಯ ಪಡುವುದಿಲ್ಲವೇ?’ ಎಂದು ಕೇಳಿರಿ”.');
INSERT INTO "kannada" VALUES(26,12,'26:12','ಆಗ ಅವರು ಹೇಳಿದರು, `ನನ್ನ ಪ್ರಭೂ, ಅವರು ನನ್ನನ್ನು ಸುಳ್ಳಾಗಿಸುವರೆಂಬ ಭಯ ನನಗಿದೆ.');
INSERT INTO "kannada" VALUES(26,13,'26:13','ನನ್ನ ಎದೆ ಮುದುಡುತ್ತಿದೆ. ನನ್ನ ನಾಲಿಗೆ ಚಲಿಸು ವುದಿಲ್ಲ. ಆದ್ದರಿಂದ ಹಾರೂನರಿಗೂ ಸಂದೇಶ ಕಳುಹಿಸು.');
INSERT INTO "kannada" VALUES(26,14,'26:14','ಮಾತ್ರವಲ್ಲ ಅವರಿಗೆ ನನ್ನ ಮೇಲೆ ಒಂದು ಅಪ ರಾಧದ ಆರೋಪವಿದೆ. ಆದುದರಿಂದ ಅವರು ನನ್ನನ್ನು ಕೊಂದು ಬಿಡುವರೆಂದು ನಾನು ಹೆದರುತ್ತೇನೆ’’.');
INSERT INTO "kannada" VALUES(26,15,'26:15','ಆಗ ಅಲ್ಲಾಹನು ಹೇಳಿದನು; `ಖಂಡಿತ ಅದೇನೂ ನಡೆಯದು . ನೀವಿಬ್ಬರೂ ನಮ್ಮ ನಿದರ್ಶನಗಳನ್ನು ತೆಗೆದುಕೊಂಡು ಹೋಗಿರಿ. ನಾವು ನಿಮ್ಮೊಂದಿಗೇ ಇದ್ದೇವೆ. ಎಲ್ಲವನ್ನೂ ಆಲಿಸುತ್ತಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(26,16,'26:16','ಫಿರ್‍ಔನನ ಬಳಿಗೆ ಹೋಗಿರಿ. ಆಮೇಲೆ ಹೀಗೆ ಹೇಳಿರಿ; ಸರ್ವ ಜಗತ್ತಿನ ಪ್ರಭು ಅಲ್ಲಾಹನ ದೂತರು ನಾವು.');
INSERT INTO "kannada" VALUES(26,17,'26:17','ನೀನು ಇಸ್ರಾಈಲ ಜನಾಂಗವನ್ನು ನಮ್ಮ ಜೊತೆ ಕಳುಹಿಸಿಕೊಡು.');
INSERT INTO "kannada" VALUES(26,18,'26:18','ಫಿರ್‍ಔನನು ಹೇಳಿದನು; “ನೀನು ಮಗುವಾಗಿ ದ್ದಾಗ ನಮ್ಮ ಜೊತೆಗೆ ನೆಲೆಗೊಟ್ಟು ನಾವು ನಿನ್ನನ್ನು ಸಾಕಿರಲಿಲ್ಲವೇ? ನಿನ್ನ ಜೀವಮಾನದ ಅನೇಕ ವರ್ಷಗಳನ್ನು ನೀನು ನಮ್ಮಲ್ಲಿ ಕಳೆದಿರುವೆ.');
INSERT INTO "kannada" VALUES(26,19,'26:19','ಅನಂತರ ನೀನು ಮಾಡುವುದೆಲ್ಲವನ್ನೂ ಮಾಡಿದೆ. ನೀನು ಮಹಾಕೃತಘ್ನನಾಗಿರುವೆ’’.');
INSERT INTO "kannada" VALUES(26,20,'26:20','ಆಗ ಮೂಸಾ ಹೇಳಿದರು; “ನಾನು ಆಗ ಆ ಕೃತ್ಯವನ್ನು ಮಾಡಿದ್ದು ನಿಜ. ಆದರೆ ಅಂದು ನಾನು ಅಜ್ಞರಲ್ಲಿ ಸೇರಿದ್ದೆ.');
INSERT INTO "kannada" VALUES(26,21,'26:21','ತರುವಾಯ ನಾನು ನಿಮಗೆ ಹೆದರಿ ಓಡಿಹೋದೆ. ಆ ಬಳಿಕ ನನ್ನ ಪ್ರಭು ನನಗೆ ವಿಜ್ಞಾನವನ್ನು ದಯಪಾಲಿಸಿದನು. ನನ್ನನ್ನು ಸಂದೇಶವಾಹಕ ರಲ್ಲಿ ಸೇರಿಸಿದನು.');
INSERT INTO "kannada" VALUES(26,22,'26:22','ನೀನು ಇಸ್ರಾಈಲ ಜನಾಂಗವನ್ನು ಗುಲಾಮರನ್ನಾಗಿ ಮಾಡಿಕೊಂಡದ್ದೇ ನೀನು ನನಗೆ ಮಾಡಿದ ಉಪಕಾರ” .');
INSERT INTO "kannada" VALUES(26,23,'26:23','ಫಿರ್‍ಔನನು ‘ಸರ್ವ ಜಗತ್ತಿನ ಪ್ರಭು ಎಂದ ರೇನು?’ ಎಂದು ಕೇಳಿದನು.');
INSERT INTO "kannada" VALUES(26,24,'26:24','ಮೂಸಾ `ಭೂಮಿ-ಆಕಾಶಗಳ ಪ್ರಭು ಮತ್ತು ಅವುಗಳ ನಡುವೆ ಇರುವ ಸಕಲದರ ಪ್ರಭು. ನೀವು ದೃಢವಿಶ್ವಾಸವಿರಿಸುವವರಾಗಿದ್ದರೆ’, ಎಂದು ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(26,25,'26:25','ಫಿರ್‍ಔನನು ತನ್ನ ಸುತ್ತಮುತ್ತಲಿದ್ದ ಜನರೊಡನೆ `ಇವನು ಹೇಳುತ್ತಿರುವುದನ್ನು ನೀವು ಸರಿಯಾಗಿ ಕೇಳಿದಿರಾ?’ ಎಂದನು.');
INSERT INTO "kannada" VALUES(26,26,'26:26','ಆಗ ಮೂಸಾ `ನಿಮ್ಮ ಪ್ರಭುವೂ ಗತಿಸಿ ಹೋಗಿರುವ ನಿಮ್ಮ ಪೂರ್ವಿಕರ ಪ್ರಭುವೂ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(26,27,'26:27','ಫಿರ್‍ಔನನು ``ನಿಮ್ಮ ಕಡೆಗೆ ಕಳುಹಿಸಲ್ಪಟ್ಟಿರುವ ನಿಮ್ಮ ದೂತರು ಖಂಡಿತ ಹುಚ್ಚರು’’ ಎಂದನು.');
INSERT INTO "kannada" VALUES(26,28,'26:28','ಆಗ ಮೂಸಾ `ಪೂರ್ವ, ಪಶ್ಚಿಮ ಮತ್ತು ಅವೆರಡರ ನಡುವೆ ಇರುವುದರ ಪ್ರಭು. ನೀವು ಯೋಚಿಸ ಬಲ್ಲವರಾಗಿದ್ದರೆ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(26,29,'26:29','ಆಗ ಫಿರ್‍ಔನನು, “ನನ್ನ ಹೊರತು ಬೇರೆ ದೇವನನ್ನು ಂ ನೀನು ಸ್ವೀಕರಿಸಿದರೆ ಬಂಧಿಸಲ್ಪಟ್ಟ ಕೈದಿಗಳೊಂದಿಗೆ ನಿನ್ನನ್ನು ಜೈಲಿಗೆ ತಳ್ಳುವೆನು’’ ಎಂದು ಹೇಳಿದನು.');
INSERT INTO "kannada" VALUES(26,30,'26:30','ಆಗ ಮೂಸಾ ‘ನಾನು ಒಂದು ಸುಸ್ಪಷ್ಟವಾದ ದೃಷ್ಟಾಂತವನ್ನು ನಿನಗೆ ತಂದರೂ ನೀನು ಒಪ್ಪುವುದಿಲ್ಲವೇ?’ ಎಂದು ಕೇಳಿದರು.');
INSERT INTO "kannada" VALUES(26,31,'26:31','ಆಗ ಫಿರ್‍ಔನನು, `ನೀನು ಸತ್ಯವಾದಿಯಾಗಿದ್ದರೆ ಅದನ್ನು ತಂದು ತೋರಿಸು’ ಎಂದನು.');
INSERT INTO "kannada" VALUES(26,32,'26:32','ಮೂಸಾ ತಮ್ಮ ದಂಡವನ್ನು ನೆಲಕ್ಕೆಸೆದರು. ಅದು ಹಠಾತ್ತನೆ ಒಂದು ಪ್ರತ್ಯಕ್ಷ ಹೆಬ್ಬಾವಾಗಿ ಮಾರ್ಪಟ್ಟಿತು.');
INSERT INTO "kannada" VALUES(26,33,'26:33','ತರುವಾಯ ಅವರು ತಮ್ಮ ಹಸ್ತವನ್ನು ಬಗಲಿನಿಂದ ಹೊರಕ್ಕೆಳೆದರು. ಆಗ ಅದು ಪ್ರೇಕ್ಷಕರ ಮುಂದೆ ಬೆಳ್ಳಗಾಗಿ ತೋರುತ್ತಿತ್ತು.');
INSERT INTO "kannada" VALUES(26,34,'26:34','ಫಿರ್‍ಔನನು ತನ್ನ ಸುತ್ತಲಿನ ಸರದಾರರೊಡನೆ ಹೇಳಿದನು; ‘ಈತನು ಒಬ್ಬ ಪರಿಣತ ಮಾಂತ್ರಿ ಕನೇ ಹೌದು!');
INSERT INTO "kannada" VALUES(26,35,'26:35','ತನ್ನ ಮಂತ್ರ ವಶೀಕರಣದಿಂದ ನಿಮ್ಮನ್ನು ನಿಮ್ಮ ಭೂಮಿಯಿಂದ ಹೊರಗಟ್ಟ ಬಯಸುತ್ತಾನೆ. ಆದ್ದರಿಂದ ಅವನ ಕಾರ್ಯದಲ್ಲಿ ನೀವೇನು ಅಪ್ಪಣೆ ಕೊಡುವಿರಿ?’ ');
INSERT INTO "kannada" VALUES(26,36,'26:36','ಆಗ ಅವರು ಹೇಳಿದರು; ‘ಇವನಿಗೂ ಇವನ ಸಹೋದರನಿಗೂ ಕಾಲಾವಕಾಶ ನೀಡಿರಿ. ಜನರನ್ನು ಒಟ್ಟು ಸೇರಿಸಲಿಕ್ಕಾಗಿ ತಾವು ನಗರಗಳಿಗೆ ದೂತರನ್ನು ಹೇಳಿ ಕಳುಹಿಸಿ.');
INSERT INTO "kannada" VALUES(26,37,'26:37','ಸಕಲ ನುರಿತ ಮಾಂತ್ರಿಕರನ್ನು ಅವರು ತಮ್ಮ ಬಳಿಗೆ ಕರೆತರಲಿ.');
INSERT INTO "kannada" VALUES(26,38,'26:38','ಒಂದು ದಿನದ ನಿಶ್ಚಿತ ಸಮಯಕ್ಕೆ ಜಾದುಗಾ ರರನ್ನು ಒಟ್ಟುಗೂಡಿಸಲಾಯಿತು.');
INSERT INTO "kannada" VALUES(26,39,'26:39','ಜನರೊಡನೆ ಕೇಳಲಾಯಿತು; ‘ನೀವು ಒಟ್ಟು ಸೇರುತ್ತೀರಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(26,40,'26:40','ಮಾಂತ್ರಿಕರು ವಿಜಯಿಗಳಾದರೆ ನಮಗೆ ಅವರ ನ್ನೇ ಹಿಂಬಾಲಿಸಬಹುದಲ್ಲ?’');
INSERT INTO "kannada" VALUES(26,41,'26:41','ಮಾಂತ್ರಿಕರು ಬಂದಾಗ ಅವರು ಫಿರ್‍ಔನನೊಡನೆ, ‘ನಾವು ವಿಜಯಿಗಳಾದರೆ ನಮಗೆ ಪ್ರತಿಫಲ ವಿದೆಯೇ?’ ಎಂದು ಕೇಳಿದರು.');
INSERT INTO "kannada" VALUES(26,42,'26:42','ಅದಕ್ಕೆ ಅವನು, ‘ಹೌದು, ಮಾತ್ರವಲ್ಲ, ಆಗ ನೀವು ನಮ್ಮ ಆಪ್ತರಲ್ಲಿ ಸೇರಿ ಬಿಡುವಿರಿ’ ಎಂದನು.');
INSERT INTO "kannada" VALUES(26,43,'26:43','ಆಗ ಮೂಸಾ ಅವರೊಡನೆ, ‘ನೀವು ಎಸೆಯ ಬೇಕಾದುದನ್ನು ಎಸೆಯಿರಿ’ ಎಂದರು');
INSERT INTO "kannada" VALUES(26,44,'26:44','ಕೂಡಲೇ ಅವರು ತಮ್ಮ ಹಗ್ಗಗಳನ್ನೂ, ದಂಡ ಗಳನ್ನೂ ಕೆಳಗೆ ಎಸೆದರು ಮತ್ತು ‘ಫಿರ್‍ಔನನ ಪ್ರತಾಪದ ಆಣೆ, ನಾವೇ ವಿಜೇತರು’ ಎಂದು ಘೋಷಿಸಿದರು.');
INSERT INTO "kannada" VALUES(26,45,'26:45','ಆಮೇಲೆ ಮೂಸಾ ತಮ್ಮ ದಂಡವನ್ನು ಎಸೆದರು. ತಕ್ಷಣ ಅದು ಅವರು ಕಣ್ಣು ಕಟ್ಟಿ ಕಾಣಿಸಿದ್ದನ್ನು ನುಂಗುತ್ತಾ ಸಾಗಿತು.');
INSERT INTO "kannada" VALUES(26,46,'26:46','ಕೂಡಲೇ ಎಲ್ಲ ಮಾಂತ್ರಿಕರು ತಾವಾಗಿಯೇ ಸಾಷ್ಟಾಂಗವೆರಗುತ್ತ ನೆಲಕ್ಕೆ ಬಿದ್ದರು.');
INSERT INTO "kannada" VALUES(26,47,'26:47','ಮತ್ತು ಹೀಗೆ ಹೇಳಿದರು ‘ಸರ್ವಲೋಕಗಳ ಪಾಲಕ ಪ್ರಭುವಿನಲ್ಲಿ- ನಾವಿದೋ ವಿಶ್ವಾಸವಿರಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(26,48,'26:48','ಮೂಸಾ ಮತ್ತು ಹಾರೂನರ ಪ್ರಭುವಿನಲ್ಲಿ’ .');
INSERT INTO "kannada" VALUES(26,49,'26:49','ಆಗ ಫಿರ್‍ಔನನು, ‘ನಾನು ನಿಮಗೆ ಅನುಮತಿ ಕೊಡುವುದಕ್ಕೆ ಮುಂಚೆ ನೀವು ಮೂಸಾನನ್ನು ನಂಬಿದಿರಾ? ಖಂಡಿತ ಇವನು ನಿಮಗೆ ವಾಮಾ ಚಾರವನ್ನು ಕಲಿಸಿದ ನಿಮ್ಮ ಗುರುವಾಗಿದ್ದಾನೆ. ಆದ್ದರಿಂದ ನಿಮಗೆ ಇದರ ಫಲ ಈಗಲೇ ತಿಳಿಯಲಿದೆ. ನಾನು ನಿಮ್ಮ ಕೈಕಾಲುಗಳನ್ನು ವಿರುದ್ಧ ದಿಕ್ಕುಗಳಿಂದ ಕಡಿಸುವೆನು ಮತ್ತು ನಿಮ್ಮೆಲ್ಲರನ್ನೂ ಶಿಲುಬೆ ಗೇರಿಸುವೆನು’ ಎಂದನು.');
INSERT INTO "kannada" VALUES(26,50,'26:50','ಅವರು ಹೇಳಿದರು ‘ಪರವಾಗಿಲ್ಲ, ನಾವು ನಮ್ಮ ಪ್ರಭುವಿನ ಸನ್ನಿಧಿಗೆ ಮರಳಿ ಹೋಗುವ ವರಾಗಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(26,51,'26:51','ನಾವೀಗ ಪ್ರಥಮ ವಿಶ್ವಾಸಿಗಳಾಗುವ ಮೂಲಕ ನಮ್ಮ ಪ್ರಭು ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸಿ ಬಿಡುವ ನೆಂದು ನಾವು ಆಶಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(26,52,'26:52','ನನ್ನ ದಾಸರನ್ನು ರಾತೋರಾತ್ರಿ ಕರಕೊಂಡು ಹೋಗಿರಿ. ನಿಮ್ಮನ್ನು (ಫಿರ್‍ಔನ್ ಹಾಗೂ ಸಂಘವು) ಬೆನ್ನಟ್ಟಲಿದ್ದಾರೆ ಎಂದು ನಾವು ಮೂಸಾ ರಿಗೆ ಸಂದೇಶ ಕಳುಹಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(26,53,'26:53','ಆಗ ಅವರು ಹೋದ ವಿವರವನ್ನು ತಿಳಿದು ಫಿರ್ ಔನನು ಸೇನೆಗಳನ್ನು ಸಜ್ಜುಗೊಳಿಸುವ ರಾಜ ಘೋಷಕರನ್ನು ನಗರಗಳಿಗೆ ಹೀಗೆ ಹೇಳಿ ಕಳುಹಿಸಿದನು.');
INSERT INTO "kannada" VALUES(26,54,'26:54','`ಇವರು (ಇಸ್ರಾಈಲರು) ಕೇವಲ ಬೆರಳೆಣಿಕೆಯ ಜನರು.');
INSERT INTO "kannada" VALUES(26,55,'26:55','ಮತ್ತು ನಮ್ಮನ್ನು ಬಹಳ ರೇಗಿಸಿದವರು.');
INSERT INTO "kannada" VALUES(26,56,'26:56','ನಾವು ಸುಸಜ್ಜಿತ ಸಮೂಹವಾಗಿದ್ದೇವೆ’ .');
INSERT INTO "kannada" VALUES(26,57,'26:57','ಹೀಗೆ ನಾವು ಅವರನ್ನು ಅವರ ಉದ್ಯಾನಗಳಿಂ ದಲೂ ಚಿಲುಮೆಗಳಿಂದಲೂ');
INSERT INTO "kannada" VALUES(26,58,'26:58','ಖಜಾನೆಗಳಿಂದಲೂ ಗೌರವದ ನಿವಾಸಗಳಿಂ ದಲೂ ಹೊರ ಹಾಕಿದೆವು.');
INSERT INTO "kannada" VALUES(26,59,'26:59','ಹಾಗಿದೆ ನಮ್ಮ ಪ್ರವೃತಿ. ಆಮೇಲೆ ಇಸ್‍ರಾಈಲ ಜನಾಂಗಕ್ಕೆ ಅವುಗಳನ್ನು ನಾವು ಉತ್ತರಾಧಿಕಾರ ಹಕ್ಕನ್ನಾಗಿ ಮಾಡಿದೆವು.');
INSERT INTO "kannada" VALUES(26,60,'26:60','ಫಿರ್‍ಔನ್ ಹಾಗೂ ಸೇನೆಯು ಸೂರ್ಯೋದಯ ಸಮಯದಲ್ಲಿ ಇಸ್ರಾಈಲರ ಬಳಿಗೆ ತಲುಪಿದರು.');
INSERT INTO "kannada" VALUES(26,61,'26:61','ಹಾಗೆ ಎರಡು ಗುಂಪು ಪರಸ್ಪರ ಕಂಡಾಗ ಮೂಸಾರ ಸಂಗಾತಿಗಳು `ನಾವು ಹಿಡಿಯಲ್ಪಡುತ್ತೇವೆ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(26,62,'26:62','ಆಗ ಮೂಸಾ, ‘ಖಂಡಿತ ಇಲ್ಲ. ನನ್ನ ಸಂಗಡ ನನ್ನ ಪ್ರಭು ಇದ್ದಾನೆ. ಅವನು ನನಗೆ ದಾರಿ ತೋರುವನು’ ಎಂದರು .');
INSERT INTO "kannada" VALUES(26,63,'26:63','ನಾವು ಮೂಸಾರಿಗೆ ಹೀಗೆ ಸಂದೇಶವಿತ್ತೆವು; `ನಿಮ್ಮ ದಂಡವನ್ನು ಸಮುದ್ರಕ್ಕೆ ಹೊಡೆಯಿರಿ’ (ಅವರು ಹೊಡೆದರು). ತಕ್ಷಣ ಸಮುದ್ರ ಸೀಳಿತು. ಅದರ ಪ್ರತಿಯೊಂದು ಸೀಳು ದೊಡ್ಡ ಬೆಟ್ಟದಂತೆ ಆಯಿತು .');
INSERT INTO "kannada" VALUES(26,64,'26:64','ಇತರರನ್ನು ನಾವು ಅಲ್ಲಿಗೆ ಹತ್ತಿರಗೊಳಿಸಿದೆವು .');
INSERT INTO "kannada" VALUES(26,65,'26:65','ಮೂಸಾ ಮತ್ತು ಅವರ ಜೊತೆಗಿದ್ದವರೆಲ್ಲರನ್ನೂ ನಾವು ರಕ್ಷಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(26,66,'26:66','ನಂತರ ಇತರರನ್ನು ನಾವು ನೀರಿನಲ್ಲಿ ಮುಳುಗಿಸಿ ನಾಶ ಮಾಡಿದೆವು.');
INSERT INTO "kannada" VALUES(26,67,'26:67','ಈ ಘಟನೆಯಲ್ಲಿ ದೊಡ್ಡ ನಿದರ್ಶನವಿದೆ. ಆದರೆ ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ನಂಬುವವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(26,68,'26:68','ನಿಮ್ಮ ಪ್ರಭು ಖಂಡಿತ ಮಹಾಪ್ರಬಲನೂ ದಯಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(26,69,'26:69','ಇಬ್‍ರಾಹೀಮರ ವೃತ್ತಾಂತವನ್ನು ಇವರಿಗೆ ತಾವು ಓದಿ ಕೇಳಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(26,70,'26:70','ಅವರು ತಮ್ಮ ತಂದೆ ಮತ್ತು ಜನಾಂಗದೊಡನೆ ‘ನೀವು ಏನನ್ನು ಪೂಜಿಸುತ್ತಿರುವಿರಿ’ ಎಂದು ಕೇಳಿದಾಗ');
INSERT INTO "kannada" VALUES(26,71,'26:71','ಅವರು, ‘ನಾವು ಕೆಲವು ವಿಗ್ರಹಗಳನ್ನು ಪೂಜಿಸುತ್ತೇವೆ ಮತ್ತು ಇವುಗಳ ಮುಂದೆ ಸದಾ ಜಪ ಕೂರುತ್ತೇವೆ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(26,72,'26:72','ಆಗ ಇವರು ಕೇಳಿದರು `ನೀವು ಕರೆಯುವಾಗ ಇವು ನಿಮ್ಮನ್ನಾಲಿಸಿ ಕೇಳುತ್ತವೆಯೇನು?');
INSERT INTO "kannada" VALUES(26,73,'26:73','ಅಥವಾ ಇವು ನಿಮಗೆ ಉಪಕಾರ ಮಾಡುವುದೇ? ಅಥವಾ ಉಪದ್ರವಿಸುವುದೇ?');
INSERT INTO "kannada" VALUES(26,74,'26:74','ಅದಕ್ಕೆ ಅವರು, “ಇಲ್ಲ, ಆದರೆ ನಮ್ಮ ಪಿತಾಮ ಹರು ಹೀಗೆಯೇ ಮಾಡುತ್ತಿರುವುದನ್ನು ನಾವು ಕಂಡಿದ್ದೇವೆ ಅಷ್ಟೆ. (ಅವರನ್ನು ನಾವು ಅನುಸರಿಸಿದ್ದೇವೆ)” ಎಂದರು.');
INSERT INTO "kannada" VALUES(26,75,'26:75','ಆಗ ಇಬ್ರಾಹೀಮರು ಹೇಳಿದರು, `ನೀವು ಏನನ್ನು ಆರಾಧಿಸುತ್ತಿದ್ದರೆಂದು ನೀವು ಯೋಚಿಸಿದ್ದೀರಾ? ');
INSERT INTO "kannada" VALUES(26,76,'26:76','ನೀವು ಮತ್ತು ನಿಮ್ಮ ಪೂರ್ವ ಪಿತಾಮಹರು.');
INSERT INTO "kannada" VALUES(26,77,'26:77','ಅವರು ನನ್ನ ಶತ್ರುಗಳು, ಸರ್ವಲೋಕಗಳ ಪಾಲಕ ಪ್ರಭುವನ್ನೇ ಹೊರತು. (ನಾನು ಆರಾಧಿಸಲಾರೆ).');
INSERT INTO "kannada" VALUES(26,78,'26:78','ನನ್ನನ್ನು ಸೃಷ್ಟಿಸಿದವನು. ಅವನೇ ನನ್ನನ್ನು ಸತ್ಪಥಕ್ಕೆ ಸೇರಿಸುವನು.');
INSERT INTO "kannada" VALUES(26,79,'26:79','ನನಗೆ ಉಣಿಸುವವನೂ ಕುಡಿಸುವವನೂ ಅವನೇ.');
INSERT INTO "kannada" VALUES(26,80,'26:80','ನಾನು ಕಾಯಿಲೆ ಬಿದ್ದಾಗ ವಾಸಿ ಮಾಡುವವನೂ ಅವನೇ.');
INSERT INTO "kannada" VALUES(26,81,'26:81','ನನ್ನನ್ನು ಸಾಯಿಸುವವನೂ, ಬದುಕಿಸುವವನೂ ಅವನೇ.');
INSERT INTO "kannada" VALUES(26,82,'26:82','ಪ್ರತಿಫಲ ನೀಡಲಾಗುವ ದಿನ ಅವನು ನನ್ನ ಪಾಪ ಗಳನ್ನು ಕ್ಷಮಿಸುವನೆಂದೂ ನಾನು ಆಶಿಸುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(26,83,'26:83','ಓ ನನ್ನ ಪ್ರಭೂ, ನನಗೆ ಸುಜ್ಞಾನ ನೀಡು ಮತ್ತು ನನ್ನನ್ನು ಸಜ್ಜನರೊಂದಿಗೆ ಸೇರಿಸು.');
INSERT INTO "kannada" VALUES(26,84,'26:84','ಮುಂದಿನ ಪೀಳಿಗೆಗಳಲ್ಲಿ ನನಗೆ ಸತ್ಕೀರ್ತಿಯನ್ನು ದಯಪಾಲಿಸು .');
INSERT INTO "kannada" VALUES(26,85,'26:85','ಅನುಗ್ರಹೀತ ಸ್ವರ್ಗದ ಉತ್ತರಾಧಿಕಾರಿಗಳಲ್ಲಿ ನನ್ನನ್ನು ಸೇರಿಸು.');
INSERT INTO "kannada" VALUES(26,86,'26:86','ನನ್ನ ತಂದೆಗೆ ಕ್ಷಮೆ ನೀಡು, ನಿಜಕ್ಕೂ ಅವರು ಪಥಭ್ರಷ್ಟರಲ್ಲಿ ಸೇರಿ ಹೋಗಿದ್ದಾರೆ .');
INSERT INTO "kannada" VALUES(26,87,'26:87','ಜನರನ್ನು ಜೀವಂತಗೊಳಿಸಿ ಎಬ್ಬಿಸಲ್ಪಡುವ ದಿನ ನನ್ನನ್ನು ಅವಮಾನಿಸದಿರು.');
INSERT INTO "kannada" VALUES(26,88,'26:88','ಸೊತ್ತಾಗಲಿ ಸಂತತಿಗಳಾಗಲಿ ಯಾವುದೇ ಪ್ರಯೋಜನಕ್ಕೆ ಬಾರದ ದಿನ.');
INSERT INTO "kannada" VALUES(26,89,'26:89','ಸುರಕ್ಷಿತ ಹೃದಯದೊಂದಿಗೆ ಅಲ್ಲಾಹನ ಸನ್ನಿಧಿಗೆ ಬಂದವನ ಹೊರತು.');
INSERT INTO "kannada" VALUES(26,90,'26:90','ಸ್ವರ್ಗವನ್ನು ಧರ್ಮನಿಷ್ಟರಿಗೆ ನಿಕಟ ಗೊಳಿಸಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(26,91,'26:91','ನರಕವನ್ನು ಪಥಭ್ರಷ್ಟರಿಗೆ ಪ್ರದರ್ಶಿಸಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(26,92,'26:92','ನೀವು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಪೂಜಿಸುತ್ತಿದ್ದ ವಸ್ತುಗಳೆಲ್ಲಿ? ಎಂದು ಅವರನ್ನು ಕೇಳಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(26,93,'26:93','ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಅವರು ನಿಮಗೆ ಸಹಾಯ ಮಾಡುವರೇ? ಅಥವಾ ಅವರೇ ಸಹಾಯವನ್ನು ಬೇಡುತ್ತಿರುವರೇ?');
INSERT INTO "kannada" VALUES(26,94,'26:94','ನಂತರ ಆ ಆರಾಧ್ಯ ವಸ್ತುಗಳೂ ಅವುಗಳನ್ನು ಆರಾಧಿಸಿದ ಪಥಭ್ರಷ್ಟ ಜನರನ್ನೂ ನರಕದಲ್ಲಿ ಅಧೋ ಮುಖರಾಗಿ ತಳ್ಳಲ್ಪಡಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(26,95,'26:95','ಇಬ್‍ಲೀಸನ ಸೇನೆಯೂ ಎಲ್ಲರೂ.');
INSERT INTO "kannada" VALUES(26,96,'26:96','ಅಲ್ಲಿ ಪರಸ್ಪರ ಜಗಳಾಡುತ್ತ ಅವರು ತಮ್ಮ ಆರಾಧ್ಯರೊಡನೆ ಹೀಗೆ ಹೇಳುವರು.');
INSERT INTO "kannada" VALUES(26,97,'26:97','“ಅಲ್ಲಾಹನಾಣೆ! ನಾವು ಪ್ರತ್ಯಕ್ಷವಾದ ದುರ್ಮಾರ್ಗದಲ್ಲಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(26,98,'26:98','ಸರ್ವಲೋಕಗಳ ಪಾಲಕ ಪ್ರಭುವಿಗೆ ನಿಮ್ಮನ್ನು ನಾವು ಸಮಾನಗೊಳಿಸಿದಾಗ.');
INSERT INTO "kannada" VALUES(26,99,'26:99','ಅಪರಾಧಿಗಳೇ ಹೊರತು ನಮ್ಮನ್ನು ದಾರಿ ತಪ್ಪಿಸಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(26,100,'26:100','ಹೀಗಾಗಿ ನಮಗೆ ಯಾರೂ ಶಿಫಾರಸ್ಸುದಾರರಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(26,101,'26:101','ಅನುಕಂಪವಿರುವ ಆಪ್ತ ಸ್ನೇಹಿತನೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(26,102,'26:102','ಇನ್ನು (ಭೂಲೋಕಕ್ಕೆ) ಮರಳುವ ಒಂದವಕಾಶ ನಮಗಿದ್ದರೆ ನಾವು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಲ್ಲಿ ಸೇರುತ್ತಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(26,103,'26:103','ಇದರಲ್ಲಿ ಖಂಡಿತ ದೃಷ್ಟಾಂತವಿದೆ. ಆದರೆ ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ವಿಶ್ವಾಸವಿಡುವವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(26,104,'26:104','ನಿಜಕ್ಕೂ ನಿಮ್ಮ ಪ್ರಭು ಮಹಾ ಪ್ರಬಲನೂ ದಯಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(26,105,'26:105','ನೂಹರ ಜನಾಂಗವು ದೇವದೂತರನ್ನು ಸುಳ್ಳಾಗಿಸಿತು.');
INSERT INTO "kannada" VALUES(26,106,'26:106','ಅವರ ಸಹೋದರ ನೂಹರು, ಅವರಿಗೆ ಹೇಳಿದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ. ನೀವು ಅಲ್ಲಾಹನನ್ನು ಭಯ ಪಡುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(26,107,'26:107','ನಾನು ನಿಮ್ಮ ಪಾಲಿಗೆ ಒಬ್ಬ ಪ್ರಾಮಾಣಿಕ ದೇವ ದೂತನಾಗಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(26,108,'26:108','ಆದುದರಿಂದ ನೀವು ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ ಮತ್ತು ನನ್ನನ್ನು ಅನುಸರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(26,109,'26:109','ಇದಕ್ಕಾಗಿ ನಾನು ಯಾವ ಪ್ರತಿಫಲವನ್ನೂ ನಿಮ್ಮಲ್ಲಿ ಕೇಳುವುದಿಲ್ಲ. ನನ್ನ ಪ್ರತಿಫಲವು ಕೇವಲ ಸರ್ವಲೋಕಗಳ ಪಾಲಕ ಪ್ರಭುವಿನ ಮೇಲೆ ಇದೆ.');
INSERT INTO "kannada" VALUES(26,110,'26:110','ಆದುದರಿಂದ ನೀವು ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ ಮತ್ತು ನನ್ನನ್ನು ಅನುಸರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(26,111,'26:111','ಅವರು ಹೇಳಿದರು, ‘ನಿನ್ನನ್ನು ಅನುಸರಿಸಿದ್ದು ಅತ್ಯಂತ ಕೀಳು ಜನರಾಗಿರುವಾಗ ನಾವು ನಿನ್ನನ್ನು ನಂಬುವುದೇ?’ ');
INSERT INTO "kannada" VALUES(26,112,'26:112','ನೂಹರು ಹೇಳಿದರು, `ಅವರೆಸಗುತ್ತಿದ್ದ ಕರ್ಮಗಳ ಬಗ್ಗೆ ನನ್ನ ಅರಿವೇನು?');
INSERT INTO "kannada" VALUES(26,113,'26:113','ಅವರ ವಿಚಾರಣೆ ಕೇವಲ ನನ್ನ ಪ್ರಭುವಿನ ಮೇಲಿದೆ. ನೀವು ಕಾರ್ಯ ಪ್ರಜ್ಞೆಯುಳ್ಳವರಾಗಿದ್ದರೆ ! ');
INSERT INTO "kannada" VALUES(26,114,'26:114','ಸತ್ಯವಿಶ್ವಾಸ ಸ್ವೀಕರಿಸಿದವರನ್ನು ನಾನು ಅಟ್ಟಿ ಓಡಿಸುವವನಲ್ಲ.');
INSERT INTO "kannada" VALUES(26,115,'26:115','ನಾನೊಬ್ಬ ಸುಸ್ಪಷ್ಟ ಎಚ್ಚರಿಕೆ ಕೊಡುವವನು ಮಾತ್ರ’');
INSERT INTO "kannada" VALUES(26,116,'26:116','ಆಗ ಅವರು, ‘ನೀನು ಇದನ್ನು ತೊರೆಯದಿದ್ದರೆ ಓ ನೂಹ್, ಖಂಡಿತ ಕಲ್ಲೆಸೆದು ಕೊಲ್ಲಲ್ಪಡುವವರಲ್ಲಿ ನೀನು ಸೇರುವೆ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(26,117,'26:117','ನೂಹರು ಪ್ರಾರ್ಥಿಸಿದರು `ನನ್ನ ಪ್ರಭೂ, ನನ್ನ ಜನಾಂಗವು ನನ್ನನ್ನು ಸುಳ್ಳಾಗಿಸಿದೆ.');
INSERT INTO "kannada" VALUES(26,118,'26:118','ಇನ್ನು ನನ್ನ ಮತ್ತು ಅವರ ನಡುವೆ ಒಂದು ಪರಿಹಾರವನ್ನು ನೀಡು. ನನ್ನನ್ನೂ ನನ್ನೊಂದಿಗಿರುವ ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನೂ ರಕ್ಷಿಸು’.');
INSERT INTO "kannada" VALUES(26,119,'26:119','ಕೊನೆಗೆ ನಾವು ಅವರನ್ನೂ ಅವರ ಜೊತೆಗಾರರನ್ನೂ ಒಂದು ತುಂಬಿದ ನಾವೆಯಲ್ಲಿ ರಕ್ಷಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(26,120,'26:120','ಅವರನ್ನು ರಕ್ಷಿಸಿದ ನಂತರ ಉಳಿದವರನ್ನು ನೀರಿನಲ್ಲಿ ಮುಳುಗಿಸಿ ನಾಶ ಮಾಡಿದೆವು.');
INSERT INTO "kannada" VALUES(26,121,'26:121','ಇದರಲ್ಲಿ ಖಂಡಿತ ದೊಡ್ಡ ನಿದರ್ಶನವಿದೆ. ಆದರೆ ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ನಂಬುವವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(26,122,'26:122','ಖಂಡಿತ ನಿಮ್ಮ ಪ್ರಭು ಮಹಾಪ್ರಬಲನೂ ವಿಶಿಷ್ಟ ಕೃಪಾ ನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(26,123,'26:123','ಆದ್ ಜನಾಂಗವು ದೇವದೂತರುಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿತು.');
INSERT INTO "kannada" VALUES(26,124,'26:124','ಅವರ ಸಹೋದರ ಹೂದರು ಅವರಿಗೆ ಹೇಳಿದ್ದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ: ‘ಅಲ್ಲಾಹನನ್ನು ನೀವು ಭಯ ಪಡುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(26,125,'26:125','ಖಂಡಿತ ನಾನು ನಿಮ್ಮ ಪಾಲಿಗೆ ಓರ್ವ ಪ್ರಾಮಾ ಣಿಕ ದೇವದೂತನಾಗಿರುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(26,126,'26:126','ಆದುದರಿಂದ ನೀವು ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ ಮತ್ತು ನನ್ನನ್ನು ಅನುಸರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(26,127,'26:127','ಇದಕ್ಕಾಗಿ ನಾನು ಯಾವ ಪ್ರತಿಫಲವನ್ನೂ ನಿಮ್ಮಲ್ಲಿ ಕೇಳುವುದಿಲ್ಲ. ನನ್ನ ಪ್ರತಿಫಲವು ಕೇವಲ ಸರ್ವಲೋಕಗಳ ಪಾಲಕ ಪ್ರಭುವಿನ ಮೇಲೆ ಇದೆ.');
INSERT INTO "kannada" VALUES(26,128,'26:128','ನೀವು ಸುಮ್ಮಸುಮ್ಮನೆ ಪ್ರತಿಯೊಂದು ಏರು ಜಾಗಗಳಲ್ಲಿ ಸ್ಮಾರಕ ಕಟ್ಟಡವನ್ನು ಕಟ್ಟುತ್ತೀರಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(26,129,'26:129','ನೀವು ಶಾಶ್ವತರಾಗಿ ಬದುಕುಳಿಯುತ್ತೀರೇನೋ ಎಂಬಂತೆ ಕೋಟೆ ಕೊತ್ತಳಗಳನ್ನು ನಿರ್ಮಿಸುತ್ತೀರಿ .');
INSERT INTO "kannada" VALUES(26,130,'26:130','ನೀವು ಯಾರನ್ನಾದರೂ ಶಿಕ್ಷಿಸುವಾಗ ನಿಷ್ಟುರವಾಗಿ ಶಿಕ್ಷಿಸುತ್ತೀರಿ .');
INSERT INTO "kannada" VALUES(26,131,'26:131','ನೀವು ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ ಮತ್ತು ನನ್ನನ್ನು ಅನುಸರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(26,132,'26:132','ನಿಮಗೆ ತಿಳಿದಿರುವ ಅನುಗ್ರಹಗಳ ಮೂಲಕ ನಿಮಗೆ ಸಹಾಯ ಮಾಡಿದ ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ.');
INSERT INTO "kannada" VALUES(26,133,'26:133','ಅವನು ನಿಮಗೆ ಪ್ರಾಣಿಗಳನ್ನೂ, ಸಂತತಿಯನ್ನೂ ಕೊಟ್ಟು ಸಹಾಯ ಮಾಡಿದನು.');
INSERT INTO "kannada" VALUES(26,134,'26:134','ತೋಟಗಳನ್ನೂ ಚಿಲುಮೆಗಳನ್ನೂ ಕೊಟ್ಟೂ.');
INSERT INTO "kannada" VALUES(26,135,'26:135','ನಿಮ್ಮ ಮೇಲೆ ಒಂದು ಮಹಾದಿನದ ಯಾತನೆಯನ್ನು ನಾನು ಭಯಪಡುತ್ತೇನೆ .');
INSERT INTO "kannada" VALUES(26,136,'26:136','ಆಗ ಅವರು ಹೇಳಿದರು; “ನೀನು ಉಪದೇಶ ಮಾಡಿದರೂ ಮಾಡದಿದ್ದರೂ ನಮ್ಮ ಮಟ್ಟಿಗೆ ಸಮಾನ.');
INSERT INTO "kannada" VALUES(26,137,'26:137','ಇದು ಕೇವಲ ಹಿಂದಿನವರ ರೂಢಿ ಸಂಪ್ರದಾಯ ಮಾತ್ರ.');
INSERT INTO "kannada" VALUES(26,138,'26:138','ನಾವು (ಮರಣಾನಂತರ) ಶಿಕ್ಷೆಗೊಳಗಾಗು ವವರಲ್ಲ”.');
INSERT INTO "kannada" VALUES(26,139,'26:139','ಹೀಗೆ ಅವರು ಹೂದರನ್ನು ಸುಳ್ಳಾಗಿಸಿದರು. ಆಗ ನಾವು ಅವರನ್ನು ನಾಶಗೊಳಿಸಿದೆವು . ಖಂಡಿತ ಇದರಲ್ಲಿ ದೊಡ್ಡ ನಿದರ್ಶನವಿದೆ. ಆದರೆ, ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ನಂಬುವವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(26,140,'26:140','ಖಂಡಿತ, ನಿಮ್ಮ ಪ್ರಭು ಮಹಾ ಪ್ರಬಲನೂ ದಯಾಮಯನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(26,141,'26:141','ಸಮೂದ್ ಜನಾಂಗವು ದೇವದೂತರುಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿತು.');
INSERT INTO "kannada" VALUES(26,142,'26:142','ಅವರ ಸಹೋದರ ಸಾಲಿಹರು ಅವರೊಡನೆ ಹೇಳಿದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ; “ನೀವು ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(26,143,'26:143','ಖಂಡಿತ ನಾನು ನಿಮ್ಮ ಪಾಲಿಗೆ ಒಬ್ಬ ಪ್ರಾಮಾಣಿಕ ದೇವದೂತನಾಗಿರುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(26,144,'26:144','ಆದುದರಿಂದ ನೀವು ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ ಮತ್ತು ನನ್ನನ್ನು ಅನುಸರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(26,145,'26:145','ಇದಕ್ಕಾಗಿ ನಾನು ನಿಮ್ಮಲ್ಲಿ ಯಾವುದೇ ಪ್ರತಿಫಲವನ್ನು ಕೇಳುವುದಿಲ್ಲ. ನನ್ನ ಪ್ರತಿಫಲವು ಕೇವಲ ಸರ್ವ ಲೋಕಗಳ ಪಾಲಕ ಪ್ರಭುವಾದ ಅಲ್ಲಾಹನ ಮೇಲೆ ಇದೆ.');
INSERT INTO "kannada" VALUES(26,146,'26:146','ನೀವು ಇಲ್ಲಿರುವ ಭೌತಿಕ ಸುಖಗಳಲ್ಲಿ ನಿಶ್ಚಿಂತರಾಗಿ ಮೈಮರೆತ ಸ್ಥಿತಿಯಲ್ಲಿ ನಿಮ್ಮನ್ನು ತೊರೆ ದು ಬಿಡಲಾಗುವುದೇ?');
INSERT INTO "kannada" VALUES(26,147,'26:147','ಅಂದರೆ ಉದ್ಯಾನಗಳಲ್ಲೂ ಕಾಲುವೆಗಳಲ್ಲೂ.');
INSERT INTO "kannada" VALUES(26,148,'26:148','ಹೊಲಗಳಲ್ಲೂ ಹಣ್ಣುಗಳ ಭಾರದಿಂದ ಗೊನೆ ಇಳಿದು ನಿಂತ ಖರ್ಜೂರದ ತೋಟಗಳಲ್ಲೂ ?');
INSERT INTO "kannada" VALUES(26,149,'26:149','ನೀವು ಅಮಿತ ಆಹ್ಲಾದ ಭರಿತರಾಗಿ ಪರ್ವತಗಳನ್ನು ಕೊರೆದು ಮನೆಗಳನ್ನು ನಿರ್ಮಿಸುತ್ತೀರಿ.');
INSERT INTO "kannada" VALUES(26,150,'26:150','ಆದ್ದರಿಂದ ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ ಮತ್ತು ನನ್ನನ್ನು ಅನುಸರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(26,151,'26:151','ಅತಿಕ್ರಮಿಗಳ ಆದೇಶವನ್ನು ನೀವು ಪಾಲಿಸಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(26,152,'26:152','ಭೂಮಿಯಲ್ಲಿ ಗೊಂದಲವನ್ನುಂಟು ಮಾಡುವ ಮತ್ತು ಯಾವ ಒಳಿತಿನ ಕಾರ್ಯವನ್ನೂ ಮಾಡದ.');
INSERT INTO "kannada" VALUES(26,153,'26:153','ಆಗ ಅವರು ಹೇಳಿದರು, ನೀನು ಕೇವಲ ಒಬ್ಬ ಮಾಟ ಬಾಧಿತ ವ್ಯಕ್ತಿ.');
INSERT INTO "kannada" VALUES(26,154,'26:154','ನೀನು ನಮ್ಮಂಥ ಒಬ್ಬ ಮನುಷ್ಯನಲ್ಲದೆ ಅಲ್ಲ. ನೀನು ಸತ್ಯವಾದಿಯಾಗಿದ್ದರೆ ಒಂದು ದೃಷ್ಟಾಂತವನ್ನು ತೆಗೆದುಕೊಂಡು ಬಾ.');
INSERT INTO "kannada" VALUES(26,155,'26:155','ಆಗ ಸಾಲಿಹ್ ಹೇಳಿದರು “ಇದೊಂದು ಒಂಟೆ. ಒಂದು ನಿಗದಿತ ದಿನ ನೀರು ಕುಡಿಯುವ ಹಕ್ಕು ಇದಕ್ಕೂ ಇನ್ನೊಂದು ನಿಗದಿತ ದಿನ ನೀರು ಪಡೆಯುವ ಹಕ್ಕು ನಿಮಗೂ ಇದೆ.');
INSERT INTO "kannada" VALUES(26,156,'26:156','ಯಾವುದೇ ರೀತಿಯ ಕೆಡುಕಿನಿಂದ ನೀವದನ್ನು ಮುಟ್ಟಬೇಡಿರಿ. ಇಲ್ಲವೇ ಒಂದು ಮಹಾ ದಿನದ ಶಿಕ್ಷೆಯು ನಿಮ್ಮನ್ನು ಬಾಧಿಸುವುದು”.');
INSERT INTO "kannada" VALUES(26,157,'26:157','ಆದರೆ ಅವರು ಅದನ್ನು ಕೊಯ್ದು ಬಿಟ್ಟರು. ಕೊನೆಗೆ ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟರು .');
INSERT INTO "kannada" VALUES(26,158,'26:158','ಆಗ ಶಿಕ್ಷೆಯು ಅವರನ್ನು ಬಾಧಿಸಿತು. ಖಂಡಿತ ಇದರಲ್ಲಿ ನಿದರ್ಶನವಿದೆ. ಆದರೆ ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ನಂಬುವವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(26,159,'26:159','ಖಂಡಿತ ನಿಮ್ಮ ಪ್ರಭು ಪ್ರತಾಪಶಾಲಿಯೂ, ಕೃಪಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(26,160,'26:160','ಲೂಥರ ಜನಾಂಗವು ದೇವದೂತರುಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿತು.');
INSERT INTO "kannada" VALUES(26,161,'26:161','ಅವರ ಸಹೋದರ ಲೂಥರು ಅವರೊಡನೆ ಹೇಳಿದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ. ನೀವು ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(26,162,'26:162','ನಾನು ನಿಮ್ಮ ಪಾಲಿಗೆ ಓರ್ವ ಪ್ರಾಮಾಣಿಕ ದೇವದೂತನಾಗಿರುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(26,163,'26:163','ಆದುದರಿಂದ ನೀವು ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ ಮತ್ತು ನನ್ನನ್ನು ಅನುಸರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(26,164,'26:164','ಇದಕ್ಕಾಗಿ ನಾನು ಯಾವುದೇ ಪ್ರತಿಫಲವನ್ನೂ ಕೇಳುವುದಿಲ್ಲ. ನನ್ನ ಪ್ರತಿಫಲವು ಕೇವಲ ಸರ್ವ ಲೋಕಗಳ ಪಾಲಕ ಪ್ರಭುವಿನ ಮೇಲೆ ಇದೆ.');
INSERT INTO "kannada" VALUES(26,165,'26:165','ನೀವು ಜಗತ್ತಿನವರ ಪೈಕಿ (ಕಾಮ ತೀರಿಸಲು) ಪುರುಷರ ಬಳಿಗೆ ಹೋಗುತ್ತೀರಾ?');
INSERT INTO "kannada" VALUES(26,166,'26:166','ನಿಮ್ಮ ಪ್ರಭು ನಿಮಗಾಗಿ ಸೃಷ್ಟಿಸಿರುವ ಪತ್ನಿ ಯರನ್ನು ತೊರೆದು ಬಿಡುತ್ತೀರಾ? ನಿಜಕ್ಕೂ ನೀವು ಮಿತಿಮೀರಿದ ಜನಾಂಗವಾಗಿದ್ದೀರಿ .');
INSERT INTO "kannada" VALUES(26,167,'26:167','ಆಗ ಅವರು, “ಲೂಥ್, ನೀನು ಇದರಿಂದ ವಿರಮಿಸದಿದ್ದರೆ ಹೊರಕ್ಕೆ ಅಟ್ಟಲ್ಪಟ್ಟವರ ಸಾಲಿಗೆ ನೀನೂ ಸೇರುವೆ” ಎಂದರು.');
INSERT INTO "kannada" VALUES(26,168,'26:168','ಆಗ ಅವರು ಹೇಳಿದರು, ‘ನಿಮ್ಮ ದುಷ್ಕøತ್ಯಕ್ಕೆ ಮರುಗುವವರಲ್ಲಿ ನಾನು ಸೇರಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(26,169,'26:169','ಪ್ರಭೂ, ನನ್ನನ್ನೂ ನನ್ನ ಕುಟುಂಬವನ್ನೂ ಇವರು ಮಾಡುತ್ತಿರುವ ದುಷ್ಕøತ್ಯಗಳಿಂದ ರಕ್ಷಿಸು’.');
INSERT INTO "kannada" VALUES(26,170,'26:170','ಆಗ ಅವರನ್ನೂ ಅವರ ಎಲ್ಲ ಕುಟುಂಬದವರನ್ನೂ ನಾವು ರಕ್ಷಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(26,171,'26:171','ಕೊನೆಗೆ ಹಿಂದೆ ಉಳಿದವರಲ್ಲಾಗಿದ್ದ ಓರ್ವ ವೃದ್ಧೆಯ ಹೊರತು .');
INSERT INTO "kannada" VALUES(26,172,'26:172','ಅನಂತರ ಉಳಿದವರನ್ನು ಸಮೂಲ ನಾಶ ಮಾಡಿದೆವು.');
INSERT INTO "kannada" VALUES(26,173,'26:173','ಅವರ ಮೇಲೆ ಒಂದು ವಿಶೇಷ ಮಳೆಯನ್ನು ಮಳೆಗೆರೆದೆವು. ಎಚ್ಚರಿಕೆ ಕೊಡಲ್ಪಟ್ಟವರ ಅತ್ಯಂತ ಕೆಟ್ಟ ಮಳೆ.');
INSERT INTO "kannada" VALUES(26,174,'26:174','ಇದರಲ್ಲಿ ಖಂಡಿತ ನಿದರ್ಶನವಿದೆ . ಆದರೆ ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ನಂಬುವವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(26,175,'26:175','ನಿಮ್ಮ ಪ್ರಭು ಮಹಾಪ್ರತಾಪಶಾಲಿಯೂ, ದಯಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(26,176,'26:176','ಐಕಃದವರು ದೇವದೂತರುಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದರು.');
INSERT INTO "kannada" VALUES(26,177,'26:177','ಶುಐಬರು ಅವರೊಡನೆ ಹೇಳಿದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ, “ನೀವು ಅಲ್ಲಾಹನನ್ನು ಭಯ ಪಡುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(26,178,'26:178','ನಾನು ನಿಮ್ಮ ಪಾಲಿಗೆ ಓರ್ವ ಪ್ರಾಮಾಣಿಕ ದೇವದೂತನಾಗಿರುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(26,179,'26:179','ಆದುದರಿಂದ ನೀವು ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ ಮತ್ತು ನನ್ನನ್ನು ಅನುಸರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(26,180,'26:180','ಇದಕ್ಕಾಗಿ ನಾನು ಯಾವುದೇ ಪ್ರತಿಫಲವನ್ನು ಕೇಳುವುದಿಲ್ಲ. ನನ್ನ ಪ್ರತಿಫಲವು ಕೇವಲ ಸರ್ವ ಲೋಕಗಳ ಪಾಲಕ ಪ್ರಭುವಿನ ಮೇಲೆ ಮಾತ್ರವಿದೆ.');
INSERT INTO "kannada" VALUES(26,181,'26:181','ಅಳತೆ ಪಾತ್ರೆಯನ್ನು ಪೂರ್ತಿಯಾಗಿ ತುಂಬಿರಿ ಮತ್ತು ಕಡಿತ ಮಾಡಿಕೊಡುವವರಲ್ಲಿ ನೀವಾ ಗದಿರಿ.');
INSERT INTO "kannada" VALUES(26,182,'26:182','ಸರಿಯಾದ ತಕ್ಕಡಿಯಿಂದ ತೂಗಿರಿ.');
INSERT INTO "kannada" VALUES(26,183,'26:183','ಜನರಿಗೆ ಅವರ ವಸ್ತುಗಳನ್ನು ನಷ್ಟಗೊಳಿಸಬೇಡಿರಿ ಮತ್ತು ಭೂಮಿಯಲ್ಲಿ ನಾಶವನ್ನು ಹರಡಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(26,184,'26:184','ನಿಮ್ಮನ್ನೂ ಗತಪೀಳಿಗೆಗಳನ್ನೂ ಸೃಷ್ಟಿಸಿದ ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ”.');
INSERT INTO "kannada" VALUES(26,185,'26:185','ಆಗ ಅವರು ಹೇಳಿದರು `ನೀನೊಬ್ಬ ಮಾಟ ಬಾಧಿತನಾಗಿರುವೆ.');
INSERT INTO "kannada" VALUES(26,186,'26:186','ನೀನು ಕೇವಲ ನಮ್ಮಂಥ ಓರ್ವ ಮಾನವ. ನಿನ್ನನ್ನು ಬರಿಯ ಸುಳ್ಳುಗಾರನೆಂದು ನಾವು ಭಾವಿಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(26,187,'26:187','ಆದ್ದರಿಂದ ನೀನು ಸತ್ಯವಾದಿಯಾಗಿದ್ದರೆ ನಮ್ಮ ಮೇಲೆ ಆಕಾಶದ ಒಂದು ತುಂಡನ್ನು ಉದುರಿಸು .');
INSERT INTO "kannada" VALUES(26,188,'26:188','ಶುಐಬರು “ನೀವು ಮಾಡುತ್ತಿರುವುದನ್ನು ನನ್ನ ಪ್ರಭು ಚೆನ್ನಾಗಿ ತಿಳಿದಿರುವನು. (ಅವನು ಯುಕ್ತವಾದುದನ್ನು ಮಾಡುವನು). ಎಂದರು.');
INSERT INTO "kannada" VALUES(26,189,'26:189','ಅವರು, ಶುಐಬರನ್ನು ಸುಳ್ಳಾಗಿಸಿದರು, ಕೊನೆಗೆ ಮೋಡದ ನೆರಳಿನ ದಿನದ ಶಿಕ್ಷೆ ಅವರ ನ್ನು ಹಿಡಿಯಿತು. ಅದು ನಿಜಕ್ಕೂ ಅತಿ ಭಯಾನಕ ದಿನದ ಶಿಕ್ಷೆಯಾಗಿತ್ತು.');
INSERT INTO "kannada" VALUES(26,190,'26:190','ಇದರಲ್ಲಿ ಖಂಡಿತ ನಿದರ್ಶನವಿದೆ. ಆದರೆ ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ನಂಬುವವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(26,191,'26:191','ನಿಮ್ಮ ಪ್ರಭು ಮಹಾ ಪ್ರತಾಪಶಾಲಿಯೂ, ದಯಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(26,192,'26:192','ಇದು ಸರ್ವಲೋಕಗಳ ಪಾಲಕನ ಅವತೀರ್ಣವೇ ಹೌದು.');
INSERT INTO "kannada" VALUES(26,193,'26:193','ಪ್ರಾಮಾಣಿಕ ಆತ್ಮವು (ದೇವಚರ ಜಿಬ್ರೀಲರು) ಇದರ ಜೊತೆ ಇಳಿದಿದೆ.');
INSERT INTO "kannada" VALUES(26,194,'26:194','ನಿಮ್ಮ ಹೃದಯಕ್ಕೆ ಇಳಿದಿದೆ. ಇದು ನೀವು ಎಚ್ಚರಿಕೆ ನೀಡುವವರಲ್ಲಿ ಸೇರಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(26,195,'26:195','ಸುಸ್ಪಷ್ಟವಾದ ಅರಬೀ ಭಾಷೆಯಲ್ಲಿ .');
INSERT INTO "kannada" VALUES(26,196,'26:196','ಅದು ಗತಜನಾಂಗಗಳ ಗ್ರಂಥಗಳಲ್ಲೂ ಇದೆ.');
INSERT INTO "kannada" VALUES(26,197,'26:197','ಇಸ್‍ರಾಈಲ ಸಂತತಿಯ ವಿದ್ವಾಂಸರು ಅದನ್ನು ತಿಳಿದಿರುವುದು ಇವರಿಗೆ (ಸತ್ಯನಿಷೇಧಿ ಗಳಿಗೆ) ಒಂದು ನಿದರ್ಶನವಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(26,198,'26:198','ನಾವು ಇದನ್ನು ಓರ್ವ ಅರಬೇತರನ ಮೇಲೆ ಅವತೀರ್ಣಗೊಳಿಸುತ್ತಿದ್ದರೆ,');
INSERT INTO "kannada" VALUES(26,199,'26:199','ಆಮೇಲೆ ಅವನು ಇದನ್ನು ಅವರಿಗೆ ಓದಿ ಹೇಳುತ್ತಿದ್ದರೆ ಇವರು ಅದನ್ನು ನಂಬುತ್ತಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(26,200,'26:200','ಹಾಗೆಯೇ ಅದನ್ನು (ಸತ್ಯನಿಷೇಧವನ್ನು) ಅಪ ರಾಧಿಗಳ ಹೃದಯಗಳಲ್ಲಿ ನಾವು ಹಾಯಿಸಿ ದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(26,201,'26:201','ವೇದನಾತ್ಮಕ ಶಿಕ್ಷೆಯನ್ನು ಕಂಡುಕೊಳ್ಳುವವ ರೆಗೂ ಅವರು ಇದರಲ್ಲಿ (ಖುರ್‍ಆನಿನಲ್ಲಿ) ವಿಶ್ವಾ ಸವಿರಿಸಲಾರರು.');
INSERT INTO "kannada" VALUES(26,202,'26:202','ಅವರಿಗೆ ಯಾವುದೇ ಪರಿವೆಯಿಲ್ಲದಂತೆ ಹಠಾ ತ್ತನೆ ಅದು ಅವರ ಮೇಲೆ ಬಂದೆರಗುವುದು.');
INSERT INTO "kannada" VALUES(26,203,'26:203','‘ನಮಗೆ (ಸ್ವಲ್ಪ) ಕಾಲಾವಕಾಶ ಸಿಗಬಹುದೇ?’ ಎಂದು ಆಗ ಅವರು ಕೇಳುವರು.');
INSERT INTO "kannada" VALUES(26,204,'26:204','ಅಂದರೆ ನಮ್ಮ ಶಿಕ್ಷೆಗಾಗಿ ಅವರು ತವಕಪಡು ತ್ತಿರುವರೇ?');
INSERT INTO "kannada" VALUES(26,205,'26:205','ತಾವು ಆಲೋಚಿಸಿ ನೋಡಿದ್ದೀರಾ? ಇವರಿಗೆ ನಾವು ವರ್ಷಗಟ್ಟಲೆ ಸುಖ ಸೌಕರ್ಯವನ್ನು ಕೊಟ್ಟರೆ,');
INSERT INTO "kannada" VALUES(26,206,'26:206','ಅನಂತರ ಇವರಿಗೆ ಎಚ್ಚರಿಕೆ ಕೊಡಲಾಗುತ್ತಿ ರುವ ಶಿಕ್ಷೆ ಇವರಿಗೆ ಬಂದು ಬಿಟ್ಟರೆ,');
INSERT INTO "kannada" VALUES(26,207,'26:207','ಇವರಿಗೆ ನೀಡಲಾದ ಆ ಸುಖ ಸವಲತ್ತುಗಳು ಅವರಿಗೆ ಯಾವುದೇ ಪ್ರಯೋಜನ ಕೊಡು ತ್ತಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(26,208,'26:208','ಯಾವ ನಾಡನ್ನೂ ನಾವು ನಾಶ ಮಾಡಿಲ್ಲ; ಅದಕ್ಕೆ ತಾಕೀತುಗಾರರಿಲ್ಲದೆ.');
INSERT INTO "kannada" VALUES(26,209,'26:209','ನೆನಪಿಸಲಿಕ್ಕಾಗಿ ಅದು. ನಾವು ಅಕ್ರಮಿಗಳಾಗಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(26,210,'26:210','ಇದನ್ನು (ಖುರ್‍ಆನ್ ಗ್ರಂಥವನ್ನು) ಶೈತಾನರು ತಂದಿಳಿಸಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(26,211,'26:211','ಅದು ಅವರಿಗೆ ಉಚಿತವೂ ಅಲ್ಲ. ಅದಕ್ಕೆ ಅವರು ಸಮರ್ಥರೂ ಅಲ್ಲ.');
INSERT INTO "kannada" VALUES(26,212,'26:212','ಖಂಡಿತ ಅವರು (ದಿವ್ಯ ಸಂದೇಶವನ್ನು) ಆಲಿಸುವುದರಿಂದ ದೂರವಿರಿಸಲ್ಪಟ್ಟವರು .');
INSERT INTO "kannada" VALUES(26,213,'26:213','ಆದುದರಿಂದ (ಸಂದೇಶವಾಹಕರೇ,) ಅಲ್ಲಾಹನೊಂದಿಗೆ ಬೇರೆ ಆರಾಧ್ಯನನ್ನು ಆರಾಧಿಸ ಬೇಡಿರಿ. ಅನ್ಯಥಾ ನೀವು ಶಿಕ್ಷಿಸಲ್ಪಡುವವರ ಸಾಲಿಗೆ ಸೇರುವಿರಿ .');
INSERT INTO "kannada" VALUES(26,214,'26:214','ನಿಮ್ಮ ಅತ್ಯಂತ ನಿಕಟ ಸಂಬಂಧಿಕರಿಗೆ ಎಚ್ಚರಿಕೆ ನೀಡಿರಿ.');
INSERT INTO "kannada" VALUES(26,215,'26:215','ನಿಮ್ಮನ್ನು ಅನುಸರಿಸುವ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ನಿಮ್ಮ (ವಿನಯದ) ರೆಕ್ಕೆಯನ್ನು ತಗ್ಗಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(26,216,'26:216','ಇನ್ನು ಅವರು ನಿಮಗೆ ಎದುರಾದರೆ, ನೀವು ಮಾಡುವ ಕಾರ್ಯಗಳಿಂದ ನಾನು ಮುಕ್ತನು ಎಂದು ಹೇಳಿರಿ.');
INSERT INTO "kannada" VALUES(26,217,'26:217','ಪ್ರತಾಪಿಯೂ, ಕರುಣಾನಿಧಿಯೂ ಆಗಿರುವ ಅಲ್ಲಾಹನ ಮೇಲೆ ಭರವಸೆ ತಾಳಿರಿ.');
INSERT INTO "kannada" VALUES(26,218,'26:218','ನೀವು (ನಮಾಝ್‍ಗೆ) ನಿಂತಾಗ ನಿಮ್ಮನ್ನು ನೋಡಬಲ್ಲವನು ಅವನು.');
INSERT INTO "kannada" VALUES(26,219,'26:219','ಸಾಷ್ಟಾಂಗವೆರಗುವವರ ಮಧ್ಯೆ ನಿಮ್ಮ ಚಲನ ವಲನವನ್ನು (ನೋಡಬಲ್ಲವನು) .');
INSERT INTO "kannada" VALUES(26,220,'26:220','ನಿಜಕ್ಕೂ ಅವನು ಸರ್ವಶ್ರುತನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(26,221,'26:221','ಜನರೇ, ಶೈತಾನರು ಯಾರ ಮೇಲೆ ಇಳಿಯು ತ್ತಾರೆಂದು ನಾನು ನಿಮಗೆ ತಿಳಿಸಿಕೊಡಲೇ?');
INSERT INTO "kannada" VALUES(26,222,'26:222','ಧಾರಾಳ ಸುಳ್ಳು ಹೇಳುವ ಹಾಗೂ ಸಾಕಷ್ಟು ಪಾಪ ಮಾಡುತ್ತಿರುವ ಎಲ್ಲ ಜನರ ಮೇಲೆ ಅವರು (ಶೈತಾನರು) ಇಳಿಯುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(26,223,'26:223','ಅವರು ಕಿವಿ ಕೊಟ್ಟು ಕೇಳುತ್ತಾರೆ. ಅವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಸುಳ್ಳುಗಾರರು.');
INSERT INTO "kannada" VALUES(26,224,'26:224','ಕವಿಗಳನ್ನು ದಾರಿಗೆಟ್ಟವರು ಹಿಂಬಾಲಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(26,225,'26:225','ಅವರು ಎಲ್ಲ ತಪ್ಪಲಲ್ಲೂ ಅಲೆದಾಡುವುದನ್ನು ನೀವು ಕಾಣುತ್ತಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(26,226,'26:226','ಅವರು ತಾವು ಮಾಡದ್ದನ್ನು ಆಡುತ್ತಾರೆ ಎಂಬುದನ್ನೂ ,');
INSERT INTO "kannada" VALUES(26,227,'26:227','ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಸ್ವೀಕರಿಸಿದ, ಸತ್ಕರ್ಮಗಳನ್ನು ಮಾಡಿದ, ಅಲ್ಲಾಹನನ್ನು ಅತ್ಯಧಿಕವಾಗಿ ಸ್ಮರಿಸಿದ ಮತ್ತು ತಾವು ಅಕ್ರಮಕ್ಕೊಳಗಾದ ನಂತರ ಆತ್ಮ ರಕ್ಷಣೆಗೆ ಕ್ರಮ ಕೈಗೊಳ್ಳುವವರು ಇದಕ್ಕೆ ಹೊರತಾಗಿದ್ದಾರೆ. ಅಕ್ರಮವೆಸಗಿದವರು, ಅವರು ಯಾವ ಕಡೆಗೆ ತಿರುಗಿ ಹೋಗಲಿದ್ದಾ ರೆಂದು ಸದ್ಯದಲ್ಲೇ ತಿಳಿಯುವರು.');
INSERT INTO "kannada" VALUES(27,1,'27:1','ತ್ವಾ ಸೀನ್. ಇವು ಖುರ್‍ಆನ್ ಮತ್ತು ಸುವ್ಯಕ್ತ ಗ್ರಂಥದ ಸೂಕ್ತಗಳು .');
INSERT INTO "kannada" VALUES(27,2,'27:2','');
INSERT INTO "kannada" VALUES(27,3,'27:3','ಇದು ನಮಾಝನ್ನು ಸಂಸ್ಥಾಪಿಸುವ ಹಾಗೂ ಝಕಾತ್ ಕೊಡುವ ಮತ್ತು ಪರಲೋಕದಲ್ಲಿ ದೃಢವಾಗಿ ವಿಶ್ವಾಸವನ್ನಿರಿಸಿದ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಮಾರ್ಗದರ್ಶನ ಹಾಗೂ ಸುವಾರ್ತೆಯಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(27,4,'27:4','ಪರಲೋಕದ ಮೇಲೆ ವಿಶ್ವಾಸವಿಲ್ಲದವರಿಗೆ ಅವರ ಕರ್ಮಗಳನ್ನು ನಾವು ಮನೋಹರಗೊಳಿಸಿದ್ದೇವೆ . ಆದುದರಿಂದ ಅವರು ದಾರಿ ತಪ್ಪಿ ಅಲೆದಾಡುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(27,5,'27:5','ಅವರೇ ಕಠಿಣ ಶಿಕ್ಷೆಯುಳ್ಳವರು. ಮತ್ತು ಅವರೇ ಪರಲೋಕದಲ್ಲಿ ಅತ್ಯಧಿಕ ನಷ್ಟಕ್ಕೆ ಒಳಗಾಗುವರು.');
INSERT INTO "kannada" VALUES(27,6,'27:6','(ಪೈಗಂಬರರೇ) ನಿಶ್ಚಯವಾಗಿಯೂ ಓರ್ವ ಮಹಾ ತಂತ್ರಶಾಲಿಯೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುವ ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ನಿಮಗೆ ಖುರ್‍ಆನನ್ನು ನೀಡಲಾ ಗುತ್ತಿದೆ.');
INSERT INTO "kannada" VALUES(27,7,'27:7','ಮೂಸಾರು ತಮ್ಮ ಕುಟುಂಬದೊಡನೆ ಹೀಗೆ ಹೇಳಿದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ. ‘ನಾನು ಒಂದು ಬೆಂಕಿಯನ್ನು ಕಂಡಿದ್ದೇನೆ . ನಾನು ಅಲ್ಲಿಂದೇನಾದರೂ ಸುದ್ದಿ ತರುತ್ತೇನೆ. ಇಲ್ಲವೇ ಅದರಿಂದ ಒಂದು ಬೆಂಕಿಯ ಕೆಂಡವನ್ನಾದರೂ ಹೆಕ್ಕಿ ತರುತ್ತೇನೆ. ನಿಮಗೆ ಚಳಿ ಕಾಯಿಸಬಹುದು’.');
INSERT INTO "kannada" VALUES(27,8,'27:8','ಅವರು ಅಲ್ಲಿ ತಲಪಿದಾಗ ಹೀಗೆ ಕರೆ ಕೇಳಿಸಿತು. ಬೆಂಕಿಯ ಸ್ಥಾನದಲ್ಲಿರುವವರು ಮತ್ತು ಇದರ ಪರಿಸರದಲ್ಲಿರುವವರು ಅನುಗ್ರಹೀತರು. ಸರ್ವಲೋಕಗಳ ಪಾಲಕ ಪ್ರಭುವಾಗಿರುವ ಅಲ್ಲಾಹನು ಪರಮ ಪಾವನನು.');
INSERT INTO "kannada" VALUES(27,9,'27:9','ಓ ಮೂಸಾ, ಪರಮಯುಕ್ತಿವಂತನೂ ಪ್ರತಾಪಿಯೂ ಆಗಿರುವ ಅಲ್ಲಾಹನೇ ನಾನು.');
INSERT INTO "kannada" VALUES(27,10,'27:10','ನಿಮ್ಮ ದಂಡವನ್ನು ಕೆಳಗೆ ಹಾಕಿರಿ. ಆಗ ದಂಡವು ಸಣ್ಣ ಹಾವಿನಂತೆ ತೆವಳುತ್ತಿರುವುದನ್ನು ಕಂಡಾಗ ಮೂಸಾ ಬೆನ್ನು ತಿರುಗಿಸಿ ಓಟಕಿತ್ತರು. ತಿರುಗಿ ಬರಲೂ ಇಲ್ಲ. ಆಗ ಅಲ್ಲಾಹು ಹೇಳಿದನು, “ಮೂಸಾ, ಹೆದರಬೇಡ! ದೇವದೂತರು ನನ್ನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಹೆದರಬೇಕಿಲ್ಲ’’.');
INSERT INTO "kannada" VALUES(27,11,'27:11','ಆದರೆ ಯಾರಾದರೂ ಅಕ್ರಮವೆಸಗಿ, ನಂತರ ಅವನು ಕೆಡುಕಿನ ಬಳಿಕ ಒಳಿತನ್ನು ಬದಲಿಯಾಗಿ ತಂದರೆ ನಾನು ಮಹಾ ಕ್ಷಮಾಶೀಲನೂ ಪರಮದಯಾಳುವೂ ಆಗಿರುವೆನು.');
INSERT INTO "kannada" VALUES(27,12,'27:12','ನಿಮ್ಮ ಕೈಯನ್ನು ನಿಮ್ಮ ಜೇಬಿನೊಳಕ್ಕೆ ಹಾಕಿರಿ. ಅದು ಬೆಳ್ಳಗೆ ಬೆಳಗುತ್ತ ಹೊರಬರುವುದು. ರೋಗದ ಬಿಳುಪಲ್ಲ. ಫಿರ್‍ಔನ್ ಮತ್ತು ಅವನ ಜನಾಂಗದ ಕಡೆಗಿರುವ ಒಂಭತ್ತು ನಿದರ್ಶನಗಳಲ್ಲಿ ಇವು ಸೇರಿವೆ. ಅವರು ಮಹಾ ದುಷ್ಕರ್ಮಿಗಳಾದ ಜನಾಂಗವಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(27,13,'27:13','ಹಾಗೆ ಆ ದುಷ್ಕರ್ಮಿಗಳ ಕಡೆಗೆ ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳು ಅತ್ಯಂತ ಪ್ರತ್ಯಕ್ಷವಾದ ನೆಲೆಯಲ್ಲಿ ಬಂದಾಗ ಅವರು ‘ಇದು ಸ್ಪಷ್ಟವಾದ ಜಾಲವಿದ್ಯೆ’ ಎಂದು ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(27,14,'27:14','ಅವುಗಳ ಬಗ್ಗೆ ಅವರ ಮನಸ್ಸುಗಳಿಗೆ ದೃಢವಾದ ಪ್ರಜ್ಞೆ ಬಂದಿದ್ದರೂ ಅಕ್ರಮ ಹಾಗೂ ಅಹಂಕಾರದಿಂದ ಅವರದನ್ನು ನಿಷೇಧಿಸಿದರು. ಆಗ ಆ ಗೊಂದಲಕಾರಿಗಳ ಪರಿಣಾಮ ಹೇಗಿತ್ತೆಂದು ನೋಡಿರಿ .');
INSERT INTO "kannada" VALUES(27,15,'27:15','ದಾವೂದ್ ಮತ್ತು ಸುಲೈಮಾನರಿಗೆ ನಾವು ಸುಜ್ಞಾನವನ್ನು ದಯಪಾಲಿಸಿದೆವು. ‘ತನ್ನ ಅನೇಕ ಸತ್ಯವಿಶ್ವಾಸಿ ದಾಸರಿಗಿಂತ ನಮಗೆ ಶ್ರೇಷ್ಟತೆಯನ್ನು ಕರುಣಿಸಿದ ಅಲ್ಲಾಹನಿಗೆ ಸರ್ವಸ್ತುತಿ’ ಎಂದು ಅವರಿಬ್ಬರೂ ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(27,16,'27:16','ಸುಲೈಮಾನರು ದಾವೂದರ ಉತ್ತರಾಧಿಕಾರಿ ಯಾದರು. ಅವರು, ‘ಜನರೇ! ಪಕ್ಷಿಗಳ ಭಾಷೆಯನ್ನು ನಮಗೆ ಕಲಿಸಿಕೊಡಲಾಗಿದೆ . (ಅಗತ್ಯ ವಿರುವ) ಎಲ್ಲ ವಸ್ತುಗಳಿಂದ (ಬೇಕಾದುದನ್ನು) ನಮಗೆ ಕೊಡಲಾಗಿದೆ . ನಿಶ್ಚಯವಾಗಿಯೂ ಇದುವೇ ಪ್ರತ್ಯಕ್ಷವಾದ ಅನುಗ್ರಹ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(27,17,'27:17','ಸುಲೈಮಾನರಿಗಾಗಿ ಜಿನ್ನ್, ಮಾನವ ಮತ್ತು ಪಕ್ಷಿಗಳ ಸೇನೆಗಳನ್ನು ಜಮಾಯಿಸಲಾಗಿತ್ತು. ಅವುಗಳನ್ನು ಸಂಪೂರ್ಣ ನಿಯಂತ್ರಣದಲ್ಲಿ ಇಡಲಾಗಿತ್ತು.');
INSERT INTO "kannada" VALUES(27,18,'27:18','ಹಾಗೆ ಅವರು ಇರುವೆಗಳ ಕಣಿವೆಗೆ ತಲುಪಿದಾಗ ಒಂದು ಇರುವೆಯು, ‘ಓ, ಇರುವೆಗಳೇ, ನೀವು ನಿಮ್ಮ ಬಿಲಗಳೊಳಗೆ ನುಗ್ಗಿಕೊಳ್ಳಿರಿ. ಸುಲೈಮಾನರೂ ಅವರ ಸೇನೆಗಳೂ ಅರಿಯದೇ ನಿಮ್ಮನ್ನು ತುಳಿದು ಬಿಡದಿರಲಿ’ ಎಂದಿತು .');
INSERT INTO "kannada" VALUES(27,19,'27:19','ಸುಲೈಮಾನರು ಅದರ ಈ ಮಾತಿಗೆ ಮಂದಹಾ ಸದ ನಗು ಬೀರಿದರು, ನಂತರ ಹೀಗೆ ಹೇಳಿದರು; “ನನ್ನ ಪ್ರಭೂ, ನನ್ನ ಮೇಲೂ ನನ್ನ ಮಾತಾಪಿತರ ಮೇಲೂ ನೀನು ಮಾಡಿದಂತಹ ನಿನ್ನ ಅನುಗ್ರಹಕ್ಕೆ ನಾನು ಕೃತಜ್ಞತೆ ತೋರಲಿಕ್ಕೂ ನೀನು ಮೆಚ್ಚು ವಂತಹ ಸತ್ಕರ್ಮವೆಸಗಲಿಕ್ಕೂ ನನಗೆ ಸ್ಪೂರ್ತಿ ನೀಡು, ನಿನ್ನ ಸಜ್ಜನದಾಸರಲ್ಲಿ ನಿನ್ನ ದಯೆಯಿಂದ ನನ್ನನ್ನು ಸೇರಿಸು .');
INSERT INTO "kannada" VALUES(27,20,'27:20','ಒಮ್ಮೆ ಸುಲೈಮಾನರು ಪಕ್ಷಿಗಳ ಪರಿಶೀಲನೆ ನಡೆಸಿದರು . ಆಮೇಲೆ ಹೀಗೆ ಹೇಳಿದರು, ಏನಾಯಿತು? ಮರಕುಟಿಕವನ್ನು ಕಾಣುವುದಿಲ್ಲ ವೇಕೆ? ಅಥವಾ ಅದು ಕಣ್ಮರೆಯಾದವರಲ್ಲಿ ಸೇರಿದೆಯೇ?');
INSERT INTO "kannada" VALUES(27,21,'27:21','ನಾನು ಅದಕ್ಕೆ ಕಠಿಣ ಶಿಕ್ಷೆ ಕೊಡುವೆನು ಇಲ್ಲವೇ ಅದನ್ನು ಕೊಯ್ದು ಬಿಡುವೆನು. ಅನ್ಯಥಾ ಅದು ನನಗೆ ಸ್ಪಷ್ಟವಾದ ಆಧಾರವನ್ನು ಕೊಡಬೇಕಾದೀತು.');
INSERT INTO "kannada" VALUES(27,22,'27:22','ಹಾಗೆ ಅದು ಅಲ್ಲಿಗೆ ತಲುಪಲು ಹೆಚ್ಚು ಸಮಯ ಹಿಡಿಯಲಿಲ್ಲ. ಆಮೇಲೆ ಅದು ಹೇಳಿತು, ‘ತಾವು ಸೂಕ್ಷ್ಮವಾಗಿ ತಿಳಿಯದಿರುವಂತಹ ಒಂದು ವಿಷಯವನ್ನು ನಾನು ಸೂಕ್ಷ್ಮವಾಗಿ ತಿಳಿದುಕೊಂಡಿದ್ದೇನೆ. ನಾನು ಖಾತರಿಯಾದ ಸುದ್ದಿಯೊಂದನ್ನು ‘ಸಬಅ’ದಿಂದ ನಿಮ್ಮಲ್ಲಿಗೆ ತಂದಿರುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(27,23,'27:23','ಓರ್ವ ಸ್ತ್ರೀ ಅವರನ್ನು ಆಳುತ್ತಿರುವುದನ್ನು ನಾನು ಕಂಡೆನು. ಅವಳಿಗೆ ಎಲ್ಲ ವಸ್ತುಗಳಿಂದ (ಅಗತ್ಯವಾದುದನ್ನು) ನೀಡಲಾಗಿದೆ. ಆಕೆಗೆ ಒಂದು ದೊಡ್ಡ ಸಿಂಹಾಸನವು ಇದೆ .');
INSERT INTO "kannada" VALUES(27,24,'27:24','ಅವಳೂ ಅವಳ ಜನಾಂಗವೂ ಅಲ್ಲಾಹನ ಬದಲು ಸೂರ್ಯನಿಗೆ ಸಾಷ್ಟಾಂಗವೆರಗುವುದನ್ನು ನಾನು ಕಂಡೆನು. ಶೈತಾನನು ಅವರಿಗೆ ಅವರ ಕರ್ಮ ಗಳನ್ನು ಸೊಗಸಾಗಿ ತೋರಿಸಿದನು. ಹಾಗೆ ಅವರನ್ನು ನೇರ ಮಾರ್ಗದಿಂದ ತಡೆದನು. ಆದುದರಿಂದ ಅವರು ನೇರ ಮಾರ್ಗಕ್ಕೆ ಬರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(27,25,'27:25','ಆಕಾಶಗಳಲ್ಲೂ ಭೂಮಿಯಲ್ಲೂ ಅಡಗಿರುವ ವಸ್ತುಗಳನ್ನು ಹೊರತರುವ ಮತ್ತು ನೀವು ಬಚ್ಚಿಡುವುದನ್ನೂ ಬಹಿರಂಗಪಡಿಸುವುದನ್ನೂ ಅರಿಯುವ ಅಲ್ಲಾಹನಿಗೆ ಅವರು ಸಾಷ್ಟಾಂಗ ವೆರಗುವ ಸತ್ಯದ ದಾರಿಗೆ ಅವರು ಬಾರದಿರಲೆಂದು. (ಶೈತಾನನು ಅವರ ಕುಕೃತ್ಯಗಳನ್ನು ಚಂದಗಾಣಿಸಿ ಕೊಟ್ಟಿದ್ದಾನೆ)');
INSERT INTO "kannada" VALUES(27,26,'27:26','ಅಲ್ಲಾಹು! ಅವನ ಹೊರತು ಅನ್ಯ ಆರಾಧ್ಯನಿಲ್ಲ, ಘನವೆತ್ತ ಅರ್ಶ್‍ನ ಒಡೆಯನು.');
INSERT INTO "kannada" VALUES(27,27,'27:27','ಆಗ ಸುಲೈಮಾನರು ಹೀಗೆ ಹೇಳಿದರು; ‘ನೀನು ನಿಜ ಹೇಳಿದೆಯೋ ಅಥವಾ ಸುಳ್ಳಾಡುವವರಲ್ಲಿ ಸೇರಿರುವೆಯೋ ಎಂದು ನಾವು ನೋಡಲಿದ್ದೇವೆ!');
INSERT INTO "kannada" VALUES(27,28,'27:28','ನನ್ನ ಈ ಪತ್ರವನ್ನು ಕೊಂಡು ಹೋಗು, ಆಮೇಲೆ ಅವರ ಮುಂದೆ ಹಾಕಿ ಬಿಡು. ಅನಂತರ ಅವರಿಂದ ಸರಿದು ನಿಂತು ಅವರು ಅದಕ್ಕೆ ಹೇಗೆ ಪ್ರತಿಕ್ರಯಿಸುತ್ತಾರೆಂದು ನೋಡು’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(27,29,'27:29','ರಾಣಿಯು ಹೀಗೆ ಹೇಳಿದಳು - ‘ಓ, ಆಸ್ಥಾನದ ವರೇ, ನನ್ನ ಕಡೆಗೆ ಒಂದು ಮಹತ್ವಪೂರ್ಣ ಪತ್ರವನ್ನು ಹಾಕಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(27,30,'27:30','ಅದು ಸುಲೈಮಾನರ ಕಡೆಯಿಂದ ಬಂದಿದೆ. ಅದು ಹೀಗಿದೆ;’ ಪರಮದಯಾನಿಧಿಯೂ ಕಾರು ಣ್ಯವಂತನೂ ಆದ ಅಲ್ಲಾಹನ ನಾಮದಿಂದ.');
INSERT INTO "kannada" VALUES(27,31,'27:31','ನನ್ನಲ್ಲಿ ನೀವು ಅಹಂಕಾರ ತೋರಬೇಡಿ, ಮತ್ತು ಶರಣಾಗತರಾಗಿ ನನ್ನ ಬಳಿ ಬನ್ನಿರಿ”.');
INSERT INTO "kannada" VALUES(27,32,'27:32','ರಾಣಿಯು, ‘ಪ್ರಮುಖರೇ, ನನ್ನ ಈ ವಿಷಯದ ಬಗ್ಗೆ (ನಾನು ಏನು ಮಾಡಬೇಕೆಂದು) ನನಗೆ ಸಲಹೆ ಕೊಡಿರಿ. ನೀವು ನನ್ನ ಬಳಿ ಹಾಜರಾಗಿ ಸಮಾಲೋಚಿಸದೆ ಯಾವುದೇ ಕಾರ್ಯದಲ್ಲಿ ನಾನು ದೃಢ ನಿರ್ಧಾರ ಕೈಗೊಳ್ಳುವವಳಲ್ಲ ಎಂದು ಹೇಳಿದಳು.');
INSERT INTO "kannada" VALUES(27,33,'27:33','ಆಗ ಅವರು, ಹೇಳಿದರು; “ನಾವು ಬಲಶಾಲಿ ಗಳೂ ರಣಶೂರರೂ ಆಗಿರುತ್ತೇವೆ. ಆಜ್ಞಾಧಿಕಾರ ನಿಮ್ಮ ಕಡೆಯಲ್ಲಿದೆ. ಏನಪ್ಪಣೆ ಕೊಡಬೇಕೆಂದು ನೀವೇ ಯೋಚಿಸಿ ನೋಡಿರಿ’’.');
INSERT INTO "kannada" VALUES(27,34,'27:34','ಆಗ ರಾಣಿ ಹೇಳಿದಳು; ‘ಅರಸರು ಒಂದು ನಾಡಿ ನೊಳಗೆ ಪ್ರವೇಶಿಸಿದರೆ ಅದನ್ನು ಅವರು ಹಾಳು ಗೆಡಹುತ್ತಾರೆ ಮತ್ತು ಅದರಲ್ಲಿರುವ ಮರ್ಯಾ ದಸ್ಥರನ್ನು ಅವಮಾನಗೊಳಿಸುತ್ತಾರೆ. ಇವರು ಕೂಡಾ ಹೀಗೆಯೇ ಮಾಡುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(27,35,'27:35','ಆದ್ದರಿಂದ ನಾನು ಅವರಿಗೆ ಒಂದು ಉಡುಗೊರೆಯನ್ನು ಕಳುಹಿಸುತ್ತೇನೆ. ತರುವಾಯ ನನ್ನ ರಾಯಭಾರಿಗಳು ಯಾವ ಉತ್ತರದೊಂದಿಗೆ ಮರಳುತ್ತಾರೆಂದು ನೋಡುತ್ತೇನೆ’.');
INSERT INTO "kannada" VALUES(27,36,'27:36','ರಾಣಿಯ ದೂತನು (ಉಡುಗೊರೆಯೊಂದಿಗೆ) ಸುಲೈಮಾನರ ಬಳಿಗೆ ಬಂದಾಗ ಅವರು ಹೇಳಿದರು; ‘ನೀವು ನನಗೆ ಧನದ ಮೂಲಕ ಸಹಾಯ ಮಾಡುವಿರಾ? ಆದರೆ ನನಗೆ ಅಲ್ಲಾಹು ನೀಡಿರುವುದು ನಿಮಗೆ ಕೊಟ್ಟಿರುವುದಕ್ಕಿಂತ ಉತ್ತಮವಾಗಿದೆ. ನಿಮ್ಮ ಉಡುಗೊರೆಯಿಂದ ನೀವು ಹರ್ಷರಾಗಿದ್ದೀರಿ.');
INSERT INTO "kannada" VALUES(27,37,'27:37','(ರಾಯಭಾರಿಯೇ, ನೀನು ತಂದ ಉಡುಗೊರೆ ಯೊಂದಿಗೆ) ಅವರ ಕಡೆಗೇ ಮರಳಿ ಹೋಗು. ಅವರಿಗೆ ಎದುರಿಸಲಾಗದ ಸೇನೆಯನ್ನು ನಾವು ಅವರ ಬಳಿಗೆ ತರುವೆವು ಮತ್ತು ಅವರು ನಿಂದ್ಯರೂ ಅಪಮಾನಿತರೂ ಆದ ಸ್ಥಿತಿಯಲ್ಲಿ ಅವರನ್ನು ನಾವು ಅಲ್ಲಿಂದ ಹೊರಕ್ಕಟ್ಟುವೆವು’ .');
INSERT INTO "kannada" VALUES(27,38,'27:38','ಅವರು (ಸುಲೈಮಾನರು) ‘ಸರದಾರರೇ, ಅವರು ಶರಣಾಗತರಾಗಿ (ಮುಸ್ಲಿಮರಾಗಿ) ನನ್ನ ಮುಂದೆ ಹಾಜರಾಗುವುದಕ್ಕಿಂತ ಮುಂಚೆಯೇ ಅವಳ ಸಿಂಹಾಸನವನ್ನು ನನ್ನ ಬಳಿಗೆ ತಂದು ಕೊಡಬಲ್ಲವರು ನಿಮ್ಮಲ್ಲಿ ಯಾರಿದ್ದಾರೆ ?’ ಎಂದು ಕೇಳಿದರು .');
INSERT INTO "kannada" VALUES(27,39,'27:39','ಜಿನ್ನ್‍ಗಳ ಪೈಕಿ ಓರ್ವ ಮಲ್ಲನು, ‘ತಾವು ತಮ್ಮ (ತೀರ್ಪು ವಿಧಿಸುವ) ಈ ಸ್ಥಾನದಿಂದ ಏಳುವುದಕ್ಕೆ ಮುಂಚೆಯೇ ನಿಮಗೆ ನಾನು ಅದನ್ನು ತಂದು ಕೊಡುವೆನು. ನಾನು ಅದಕ್ಕೆ ಶಕ್ತನೂ ಪ್ರಾಮಾಣಿಕನೂ ಆಗಿದ್ದೇನೆ’ ಎಂದನು.');
INSERT INTO "kannada" VALUES(27,40,'27:40','ಗ್ರಂಥದಿಂದ ಜ್ಞಾನ ಪಡೆದ ವ್ಯಕ್ತಿಯೊಬ್ಬರು, ‘ತಾವು ರೆಪ್ಪೆ ಬಡಿಯುವುದಕ್ಕೆ ಮುಂಚೆ ನಾನು ಅದನ್ನು ತರುತ್ತೇನೆ’ ಎಂದರು. ಆಗಲೇ ಆ ಸಿಂಹಾಸನವು ತನ್ನ ಮುಂದೆ ಇಡಲ್ಪಟ್ಟಿರುವುದನ್ನು ಕಂಡಾಗ, ಸುಲೈಮಾನರು ಹೀಗೆ ಹೇಳಿದರು, “ನಾನು ಕೃತಜ್ಞತೆ ತೋರುವೆನೋ, ಅಥವಾ ಕೃತಘ್ನತೆ ತೋರುವೆನೋ ಎಂದು ನನ್ನನ್ನು ಪರೀಕ್ಷಿಸಲಿಕ್ಕಾಗಿ ಇದು ನನ್ನ ಪ್ರಭುವಿನ ಅನು ಗ್ರಹವಾಗಿರುತ್ತದೆ. ಯಾರು ಕೃತಜ್ಞತೆ ತೋರುತ್ತಾನೆ, ಆತನು ತನಗಾಗಿಯೇ ಕೃತಜ್ಞತೆ ತೋರುತ್ತಾನೆ. ಯಾರು ಕೃತಘ್ನತೆ ತೋರುತ್ತಾರೆ, ನನ್ನ ಪ್ರಭು ನಿರಪೇಕ್ಷನೂ ಉತ್ಕøಷ್ಟನೂ ಆಗಿರುತ್ತಾನೆ”.');
INSERT INTO "kannada" VALUES(27,41,'27:41','ಅವರು (ಸುಲೈಮಾನರು) ‘ನೀವು ಅವಳಿಗೆ ಅವಳ ಸಿಂಹಾಸನವನ್ನು ಗುರುತು ಸಿಗದಂತೆ ರೂಪ ವ್ಯತ್ಯಾಸ ಮಾಡಿರಿ. ಅವಳು ಗುರುತು ಹಿಡಿಯುತ್ತಾಳೋ ಅಥವಾ ಗುರುತು ಹಿಡಿಯದವರ ಸಾಲಿಗೆ ಸೇರುತ್ತಾಳೋ ಎಂದು ನಾವು ನೋಡೋಣ’ ಎಂದರು .');
INSERT INTO "kannada" VALUES(27,42,'27:42','ರಾಣಿಯು ಹಾಜರಾದಾಗ (ಅವಳೊಡನೆ) ‘ನಿನ್ನ ಸಿಂಹಾಸನ ಹೀಗೆಯೇ ಇದೆಯೇ?’ ಎಂದು ಕೇಳಲಾಯಿತು. ಆಗ ಅವಳು ಹೇಳಿದಳು; ಇದು ಅದುವೇ ಎಂಬಂತಿದೆ. (ಆಗ ಸುಲೈಮಾನರು ಹೇಳಿದರು) ‘ಅವಳಿಗೆ ಮೊದಲೇ ನಮಗೆ ಅರಿವು ಸಿಕ್ಕಿದೆ. ಮತ್ತು ನಾವು ಮುಸ್ಲಿಮರಾಗಿದ್ದೇವೆ .');
INSERT INTO "kannada" VALUES(27,43,'27:43','ಅವಳು ಆರಾಧಿಸುತ್ತಿದ್ದ ಅಲ್ಲಾಹೇತರ ಆರಾಧ್ಯ ವಸ್ತುವು ಅವಳನ್ನು ತಡೆದಿರಿಸಿತ್ತು. ನಿಜಕ್ಕೂ ಅವಳು ಸತ್ಯನಿಷೇಧಿ ಜನಾಂಗದವಳಾಗಿದ್ದಳು.');
INSERT INTO "kannada" VALUES(27,44,'27:44','ಅರಮನೆಯೊಳಗೆ ಪ್ರವೇಶಿಸು’ ಎಂದು ಅವ ಳೊಡನೆ ಹೇಳಲಾಯಿತು. ಆಗ ಅವಳು ಅದನ್ನು ಕಂಡಾಗ ಅದೊಂದು ಜಲಾಶಯವೆಂದು ಭಾವಿಸಿ ಇಳಿಯಲಿಕ್ಕಾಗಿ ಕಾಲುಗಳಿಂದ ಇಜಾರನ್ನು ಮೇಲೇರಿಸಿ ಕೊಂಡಳು. ಆಗ ಸುಲೈಮಾನರು ‘ಇದು ಸ್ಪಟಿಕ ನಿರ್ಮಿತ ನುಣುಪಾದ ಅರಮನೆ’ ಎಂದರು. ಆಗ ಅವಳು, ‘ಓ ನನ್ನ ಪ್ರಭೂ, ನಾನು ನನ್ನ ಮೇಲೆಯೇ ಅಕ್ರಮವೆಸಗಿದೆ. ನಾನು ಸುಲೈಮಾನರೊಂದಿಗೆ, ಸರ್ವ ಲೋಕಗಳ ಪಾಲಕ ಪ್ರಭುವಾದ ಅಲ್ಲಾಹನಿಗೆ ಶರಣಾಗಿದ್ದೇನೆ’ ಎಂದಳು .');
INSERT INTO "kannada" VALUES(27,45,'27:45','‘ನೀವು ಅಲ್ಲಾಹನನ್ನು ಆರಾಧಿಸಿರಿ’ ಎಂಬ ಸಂದೇಶದೊಂದಿಗೆ ಸಮೂದ್ ಗೋತ್ರದೆಡೆಗೆ ಅವರ ಸಹೋದರ ಸಾಲಿಹರನ್ನು ನಾವು ಕಳುಹಿಸಿದೆವು. ಆಗ ಅವರು ಪರಸ್ಪರ ಜಗಳಾಡುವ ಎರಡು ತಂಡಗಳಾಗಿ ಬಿಟ್ಟರು.');
INSERT INTO "kannada" VALUES(27,46,'27:46','ಅವರು (ಸಾಲಿಹರು) ಹೇಳಿದರು, ‘ನನ್ನ ಜನಾಂಗದವರೇ, ನೀವೇಕೆ ಒಳಿತಿಗಿಂತ ಮೊದಲು ಕೆಡುಕಿಗಾಗಿ ತ್ವರೆ ಮಾಡುವಿರಿ? ನಿಮ್ಮ ಮೇಲೆ ಕೃಪೆ ತೋರುವಂತಾಗಲು ನೀವು ಅಲ್ಲಾಹನೊಡನೆ ಕ್ಷಮಾಯಾಚನೆ ಮಾಡಬಾರದೇ?’');
INSERT INTO "kannada" VALUES(27,47,'27:47','ಆಗ ಅವರು, ‘ನಾವು ನಿನ್ನ ಹಾಗೂ ನಿನ್ನ ಜೊತೆ ಗಾರರಿಂದ ಅಪಶಕುನದಲ್ಲಿ ಬಿದ್ದಿದ್ದೇವೆ’ ಎಂದ ರು. ಅದಕ್ಕೆ ಸಾಲಿಹರು ‘ನಿಮ್ಮ ಶಕುನವು ಅಲ್ಲಾ ಹನ ಬಳಿ ಇದೆ. ಆದರೆ ನೀವು ಪರೀಕ್ಷಿಸಲ್ಪಡುತ್ತಿರುವ ಒಂದು ಜನಾಂಗವಾಗಿದ್ದೀರಿ’ ಎಂದು ಉತ್ತರಿಸಿದರು.');
INSERT INTO "kannada" VALUES(27,48,'27:48','ಆ ನಗರದಲ್ಲಿ ಒಂಭತ್ತು ಮಂದಿಯ ಗುಂಪೊಂದಿತ್ತು. ಅವರು ಭೂಮಿಯಲ್ಲಿ ಕೇಡು ಹರಡುತ್ತಿ ದ್ದರು. ಸುಧಾರಣೆ ಮಾಡುತ್ತಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(27,49,'27:49','ಅವರು ಪರಸ್ಪರ ಹೀಗೆಂದರು; ನಾವು ಸಾಲಿಹ್ ಮತ್ತು ಅವನ ಜನರನ್ನು ರಾತ್ರಿ ಸಮಯದಲ್ಲಿ ಕೊಂದು ಹಾಕೋಣ, ಅನಂತರ ಅವನ ಹಕ್ಕುದಾರನೊಡನೆ ‘ನಿಮ್ಮ ಜನರ ನಾಶಕ್ಕೆ ನಾವು ಹಾಜರಾಗಿಲ್ಲ. ನಾವು ನಿಜವನ್ನೇ ಹೇಳುತ್ತೇವೆ’ ಎಂದು ಹೇಳೋಣ ಎಂದು ಅಲ್ಲಾಹನ ಹೆಸರಲ್ಲಿ ಆಣೆ ಹಾಕಿರಿ”.');
INSERT INTO "kannada" VALUES(27,50,'27:50','ಅವರು ಒಂದು ತಂತ್ರವನ್ನು ಹೂಡಿದರು. ಅವರು ತಿಳಿಯದಂತೆ ಒಂದು ಪ್ರತಿತಂತ್ರವನ್ನು ನಾವೂ ಹೂಡಿದೆವು.');
INSERT INTO "kannada" VALUES(27,51,'27:51','ನೋಡಿರಿ! ಅವರ ಕುತಂತ್ರದ ಪರಿಣಾಮ ಏನಾ ಯಿತು? ನಾವು ಅವರನ್ನೂ ಅವರ ಇಡೀ ಜನಾಂಗವನ್ನೂ ನಾಶಗೊಳಿಸಿದೆವು!');
INSERT INTO "kannada" VALUES(27,52,'27:52','ಹೀಗೆ ಅವರು ಮಾಡುತ್ತಿದ್ದ ಅಕ್ರಮದ ಫಲವಾಗಿ ಅವರ ಮನೆಗಳು ಪಾಳುಬಿದ್ದಿವೆ. ತಿಳಿಯುವ ಜನಾಂಗಕ್ಕೆ ಖಂಡಿತ ಇದರಲ್ಲಿ ನಿದರ್ಶನವಿದೆ.');
INSERT INTO "kannada" VALUES(27,53,'27:53','ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿಕೊಂಡ ಹಾಗೂ ಜಾಗೃತೆ ಪಾಲಿಸಿದ ಜನರನ್ನು ನಾವು ರಕ್ಷಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(27,54,'27:54','ಲೂಥರನ್ನು (ಸ್ಮರಿಸಿರಿ). ಅವರು ತಮ್ಮ ಜನಾಂಗಕ್ಕೆ ಹೇಳಿದ ಸಂದರ್ಭ. ‘ನೀವು ಕಣ್ಣಾರೆ ಕಾಣುತ್ತ ಈ ನೀಚ ಕೆಲಸವನ್ನು ಮಾಡುತ್ತೀರಾ? ');
INSERT INTO "kannada" VALUES(27,55,'27:55','ನೀವು ಕಾಮ ತೀರಿಸಲಿಕ್ಕಾಗಿ ಸ್ತ್ರೀಯರನ್ನು ಬಿಟ್ಟು ಪುರುಷರ ಬಳಿಗೆ ಹೋಗುವುದೇ? ಮಾತ್ರವಲ್ಲ ನೀವು (ಇದರ ದುಷ್ಪರಿಣಾಮದ ಬಗ್ಗೆ) ಅರಿವಿಲ್ಲದ ಜನಾಂಗವಾಗಿದ್ದೀರಿ’.');
INSERT INTO "kannada" VALUES(27,56,'27:56','ಆಗ `ಲೂಥರ ಪರಿವಾರವನ್ನು ನಿಮ್ಮ ನಾಡಿನಿಂದ ಹೊರ ಹಾಕಿರಿ. ಮಹಾಶುದ್ಧ ಜನರಿವರು’ ಎನ್ನುವುದೇ ಹೊರತು ಅವರ ಜನಾಂಗಕ್ಕೆ ಬೇರೆ ಉತ್ತರವಿರಲಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(27,57,'27:57','ಕೊನೆಗೆ ನಾವು ಅವರನ್ನೂ ಅವರ ಜನರನ್ನೂ ರಕ್ಷಿಸಿದೆವು. ಆದರೆ ಅವರ ಪತ್ನಿಯನ್ನು ನಾವು ರಕ್ಷಿಸಲಿಲ್ಲ. ಶಿಕ್ಷೆಯಲ್ಲಿ ಉಳಿದವರ ಸಾಲಿಗೆ ಅವಳನ್ನು ನಾವು ಸೇರಿಸಿ ಲೆಕ್ಕ ಮಾಡಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(27,58,'27:58','ಅವರ ಮೇಲೆ ನಾವು ಒಂದು ಮಳೆ ಸುರಿಸಿದೆವು. ಎಚ್ಚರಿಕೆ ನೀಡಲ್ಪಟ್ಟವರ ಪಾಲಿಗೆ ಅದು ಅತ್ಯಂತ ಕೆಟ್ಟ ಮಳೆಯಾಗಿತ್ತು .');
INSERT INTO "kannada" VALUES(27,59,'27:59','(ಪೈಗಂಬರರೇ) ಹೇಳಿರಿ; ಅಲ್ಲಾಹನಿಗೆ ಸರ್ವ ಸ್ತುತಿ. ಅವನು ಆಯ್ದು ಪುನೀತಗೊಳಿಸಿದ ಅವನ ದಾಸರ ಮೇಲೆ ಶಾಂತಿ ಇರಲಿ. ಅಲ್ಲಾಹು ಉತ್ತಮ ನೋ ಅಥವಾ ಇವರು ಅವನ ಸಹಭಾಗಿಗಳಾಗಿ ಮಾಡುವ ವಸ್ತುಗಳು ಉತ್ತಮವೋ?');
INSERT INTO "kannada" VALUES(27,60,'27:60','ಅಥವಾ ಆಕಾಶಗಳನ್ನೂ ಭೂಮಿಯನ್ನೂ ಸೃಷ್ಟಿಸಿದ ಹಾಗೂ ನಿಮಗಾಗಿ ಆಕಾಶದಿಂದ ನೀರನ್ನು ಸುರಿಸಿದ ಮತ್ತು ತನ್ಮೂಲಕ ನಿಮ್ಮಿಂದ ಬೆಳೆಸಲು ಸಾಧ್ಯವಿಲ್ಲದ ಮರಗಳಿರುವ ಸುಂದರ ತೋಟಗಳನ್ನು ಬೆಳೆಸಿದವನು ಉತ್ತಮನೋ, ಇತರರೋ? ಅಲ್ಲಾಹನೊಂದಿಗೆ ಇತರ ದೇವರೂ ಇರುವರೇ? ಇಲ್ಲ! ವಾಸ್ತವದಲ್ಲಿ ಇವರು (ಅಲ್ಲಾಹನೊಂದಿಗೆ ಇತರ ವಸ್ತುಗಳನ್ನು) ಸಮಾನತೆ ಕಲ್ಪಿಸುವ ಒಂದು ಜನಾಂಗವಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(27,61,'27:61','ಭೂಮಿಯನ್ನು ವಾಸ ಯೋಗ್ಯಗೊಳಿಸಿದ, ಅದರ ನಡುವೆ ನದಿಗಳನ್ನು ಹರಿಸಿದ, ಭೂಮಿಗೆ ಬಲ ಕೊಡುವ ಪರ್ವತಗಳನ್ನು ನಾಟಿದ ಮತ್ತು ಎರಡು ರೀತಿಯ ಜಲಾಶಯಗಳ ಮಧ್ಯೆ ತಡೆ ಯನ್ನಿರಿಸಿದವನೋ ಉತ್ತಮನು (ಅಥವಾ ಇತರರೋ?) ಅಲ್ಲಾಹನೊಂದಿಗೆ ಇತರ ದೇವರೂ ಇರುವರೇ? ಇಲ್ಲ; ಆದರೆ ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ತಿಳಿಯುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(27,62,'27:62','ಅಥವಾ ಕಷ್ಟಕ್ಕೊಳಗಾದವನು ಪ್ರಾರ್ಥಿಸಿದಾಗ ಅವನಿಗೆ ಉತ್ತರಿಸುವವನೂ ಸಂಕಷ್ಟವನ್ನು ನಿವಾರಿಸುವವನೂ ನಿಮ್ಮನ್ನು ಭೂಮಿಯಲ್ಲಿ ವಂಶ ಪರಂಪರೆಯಾಗಿ ನೆಲೆಗೊಳಿಸುವವನೂ ಯಾರು? ಅಲ್ಲಾಹನೊಂದಿಗೆ ಇತರ ದೇವರೂ ಇರುವರೇ? ನೀವು ಸ್ವಲ್ಪ ಮಾತ್ರವೇ ಯೋಚಿಸಿ ಅರ್ಥ ಮಾಡಿಕೊಳ್ಳುತ್ತೀರಿ.');
INSERT INTO "kannada" VALUES(27,63,'27:63','ಅಥವಾ ನೆಲ ಹಾಗೂ ಸಾಗರದ ಅಂಧಕಾರಗಳಲ್ಲಿ ನಿಮಗೆ ದಾರಿ ತೋರಿಸುವವನೂ ತನ್ನ ಕೃಪೆಗಿಂತ ಮುಂಚಿತವಾಗಿ ಮಾರುತಗಳನ್ನು ಸುಮಾರ್ತೆಯಾಗಿ ಕಳುಹಿಸುವವನೂ ಯಾರು? ಅಲ್ಲಾಹನೊಂದಿಗೆ ಇತರ ದೇವರೂ ಇರುವರೇ? ಅಲ್ಲಾಹನು ಇವರು ಮಾಡುತ್ತಿರುವ ದೇವಸಹ ಭಾಗಿತ್ವದಿಂದ ಮುಕ್ತ ಮಹೋನ್ನತನು .');
INSERT INTO "kannada" VALUES(27,64,'27:64','ಅಥವಾ ಸೃಷ್ಟಿಯನ್ನಾರಂಭಿಸಿ ಆ ಬಳಿಕ ಅದರ ಪುನಃ ಸೃಷ್ಟಿ ಮಾಡುವವನೂ ಯಾರು? ಆಕಾ ಶದಿಂದಲೂ, ಭೂಮಿಯಿಂದಲೂ ನಿಮಗೆ - ಜೀವನಾಧಾರ ಕೊಡುವವನಾರು? ಅಲ್ಲಾಹನ ಜೊತೆ ಇತರ ದೇವರೂ ಇರುವರೇ? ಹೇಳಿರಿ ನೀವು ಸತ್ಯವಾದಿಗಳಾಗಿದ್ದರೆ ಪುರಾವೆಗಳನ್ನು ತನ್ನಿರಿ.');
INSERT INTO "kannada" VALUES(27,65,'27:65','ಹೇಳಿರಿ; ಭೂಮಿ-ಆಕಾಶಗಳಲ್ಲಿ, ಇರುವ ಯಾರೂ ಪರೋಕ್ಷವನ್ನು ತಿಳಿಯುವುದಿಲ್ಲ, ಅಲ್ಲಾಹನ ಹೊರತು. ಯಾವಾಗ ಪುನರ್ಜೀವ ತಾಳಿ ಎಬ್ಬಿಸಲ್ಪಡುವರೆಂಬುದು ಈ ಸತ್ಯ ನಿಷೇಧಿಗಳಿಗೆ ಪ್ರಜ್ಞೆ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(27,66,'27:66','ಪರಲೋಕದ ಬಗ್ಗೆ ಇವರಿಗೆ ಅರಿವಾದರೂ ಇದೆ ಯೇ? ಇಲ್ಲ! ಇವರು ಅದರ ಬಗ್ಗೆ ಸಂದೇಹದಲ್ಲಿದ್ದಾರೆ. ಅಲ್ಲ, ಅವರು ಅದರಿಂದ ಕುರುಡರಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(27,67,'27:67','ಸತ್ಯನಿಷೇಧಿಗಳು ಹೇಳುತ್ತಾರೆ. ‘ನಾವೂ ನಮ್ಮ ಪೂರ್ವಿಕರೂ ಮಣ್ಣಾಗಿ ಹೋದರೆ ನಿಜಕ್ಕೂ ನಮ್ಮನ್ನು (ಸಮಾಧಿಗಳಿಂದ) ಹೊರತರಲಾಗು ವುದೇ?');
INSERT INTO "kannada" VALUES(27,68,'27:68','ಇಂತಹ ವಾಗ್ದಾನಗಳು ನಮಗೆ ಮಾತ್ರವಲ್ಲ, ಹಿಂದೆ ನಮ್ಮ ಪೂರ್ವಿಕರಿಗೂ ಕೊಡಲಾಗಿತ್ತು. ಆದರೆ ಇವೆಲ್ಲ ಪೂರ್ವಿಕರ ಬರಿಯ ಕಟ್ಟುಕತೆಗಳೇ ಹೊರತು ಇನ್ನೇನಲ್ಲ’.');
INSERT INTO "kannada" VALUES(27,69,'27:69','(ಓ ಪೈಗಂಬರರೇ, ಇವರಿಗೆ ಹೀಗೆ) ಹೇಳಿರಿ, ಭೂಮಿಯ ಮೇಲೆ ಸಂಚಾರ ಮಾಡಿ. ಅಪರಾಧಿಗಳ ಪರಿಣಾಮ ಏನಾಗಿತ್ತೆಂದು ನೋಡಿರಿ.');
INSERT INTO "kannada" VALUES(27,70,'27:70','ಪೈಗಂಬರರೇ, ಇವರ ಮೇಲೆ ದುಃಖಿಸಬೇಡಿರಿ, ಇವರು ಹೂಡುತ್ತಿರುವ ಸಂಚುಗಳ ಬಗ್ಗೆ ವ್ಯಾಕುಲಗೊಳ್ಳಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(27,71,'27:71','ಅವರು (ಅವಿಶ್ವಾಸಿಗಳು ಪ್ರವಾದಿಯರಲ್ಲಿ) ಹೇಳಿದರು; ನೀವು ಸತ್ಯವಾದಿಗಳಾಗಿದ್ದರೆ ನಿಮ್ಮ ಎಚ್ಚರಿಕೆ ಈಡೇರುವುದು ಯಾವಾಗ?');
INSERT INTO "kannada" VALUES(27,72,'27:72','ಅವರಿಗೆ ಹೀಗೆ ಹೇಳಿರಿ. ನೀವು ತವಕ ಪಡುತ್ತಿ ರುವ ಕಾರ್ಯಗಳಲ್ಲಿ ಕೆಲವು ಬಹುಶಃ ನಿಮ್ಮ ಹತ್ತಿರ ವೇ ಬಂದು ಬಿಟ್ಟಿದೆ.');
INSERT INTO "kannada" VALUES(27,73,'27:73','ಖಂಡಿತ ನಿಮ್ಮ ಪ್ರಭು ಜನರ ಮೇಲೆ ಔದಾರ್ಯ ವುಳ್ಳವನು. ಆದರೆ ಹೆಚ್ಚಿನವರು ಕೃತಜ್ಞತೆ ತೋರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(27,74,'27:74','ಅವರ ಹೃದಯಗಳು ಬಚ್ಚಿಟ್ಟುಕೊಂಡಿರುವುದನ್ನೂ ಅವರು ಪ್ರಕಟಗೊಳಿಸುತ್ತಿರುವುದನ್ನೂ ನಿಮ್ಮ ಪ್ರಭು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(27,75,'27:75','ಆಕಾಶ ಭೂಮಿಯಲ್ಲಿ ಯಾವುದೇ ಗೌಪ್ಯ ವಸ್ತು ಒಂದು ಸುವ್ಯಕ್ತ ಗ್ರಂಥದಲ್ಲಿ ಬರೆಯಲ್ಪಡದೆ ಉಳಿದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(27,76,'27:76','ಇಸ್ರಾಈಲ ಸಂತತಿಗಳು ಭಿನ್ನಾಭಿಪ್ರಾಯವಿರಿಸಿ ಕೊಂಡಿರುವ ಹೆಚ್ಚಿನ ವಿಷಯಗಳನ್ನು ಈ ಖುರ್‍ಆನ್ ಅವರಿಗೆ ವಿವರಿಸಿ ಕೊಡುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(27,77,'27:77','ಖಂಡಿತ ಇದು ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಪಾಲಿಗೆ ಮಾರ್ಗ ದರ್ಶನವೂ ಕೃಪೆಯೂ ಆಗಿದೆ.');
INSERT INTO "kannada" VALUES(27,78,'27:78','ನಿಶ್ಚಯವಾಗಿಯೂ ನಿಮ್ಮ ಪ್ರಭು ತನ್ನ ಅಪ್ಪಣೆ ಮೂಲಕ ಇವರ ನಡುವೆ ತೀರ್ಮಾನ ಮಾಡು ವನು . ಅವನು ಮಹಾ ಪ್ರತಾಪಿಯೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(27,79,'27:79','ಆದುದರಿಂದ (ಪೈಗಂಬರರೇ,) ಅಲ್ಲಾಹನ ಮೇಲೆ ಭರವಸೆ ಇಡಿರಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ನೀವು ಸುಸ್ಪಷ್ಟವಾದ ಸತ್ಯ ಮಾರ್ಗದಲ್ಲಿದ್ದೀರಿ.');
INSERT INTO "kannada" VALUES(27,80,'27:80','ನೀವು ಮೃತರಿಗೆ ಖಂಡಿತ ಕೇಳಿಸಲಾರಿರಿ ಕಿವುಡರು ಬೆನ್ನು ತಿರುಗಿಸಿ ಓಡಿ ಹೋದರೆ ಅವರಿಗೂ ನಿಮ್ಮ ಕೂಗನ್ನು ಮುಟ್ಟಿಸಲಾರಿರಿ.');
INSERT INTO "kannada" VALUES(27,81,'27:81','ಕುರುಡರನ್ನು ಅವರ ದುರ್ಮಾಗದಿಂದ ಸರಿದಾರಿಗೆ ಕೊಂಡೊಯ್ಯಲಾರಿರಿ. ನಮ್ಮ ದೃಷ್ಟಾಂತ ಗಳಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸಿ ಆಮೇಲೆ ಶರಣಾಗತರಾದವರಿಗೆ ಮಾತ್ರ ನಿಮ್ಮ ಮಾತನ್ನು ಕೇಳಿಸಬಲ್ಲಿರಿ .');
INSERT INTO "kannada" VALUES(27,82,'27:82','ಅವರ ಮೇಲೆ ವಚನವು ಪೂರ್ಣಗೊಳ್ಳುವ ಕಾಲ ಬಂದಾಗ ನಾವು ಅವರಿಗಾಗಿ ಒಂದು ಪ್ರಾಣಿ ಯನ್ನು ಭೂಮಿಯಿಂದ ಹೊರತರುವೆವು, ಜನರು ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳಲ್ಲಿ ದೃಢವಿಶ್ವಾಸವಿಲ್ಲದವರಾಗಿದ್ದಾರೆ ಎಂದು ಅದು ಅವರೊಡನೆ ಮಾತಾಡುವುದು .');
INSERT INTO "kannada" VALUES(27,83,'27:83','ಪ್ರತಿಯೊಂದು ಸಮುದಾಯದಿಂದ ನಮ್ಮ ಪುರಾವೆಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುತ್ತಿದ್ದ ಒಂದೊಂದು ತಂಡವನ್ನು ನಾವು ಒಟ್ಟು ಸೇರಿಸುವ ದಿನವನ್ನು ಊಹಿಸಿರಿ. ತರುವಾಯ ಅವರನ್ನು ತಡೆದು ನಿಯಂತ್ರಿಸಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(27,84,'27:84','ಹೀಗೆ ಅವರೆಲ್ಲರೂ (ವಿಚಾರಣಾ ಸ್ಥಳಕ್ಕೆ) ಬಂದಾಗ (ಅಲ್ಲಾಹು) ಅವರನ್ನು ಕೇಳುವನು. ‘ನನ್ನ ದೃಷ್ಟಾಂತಗಳ ಬಗ್ಗೆ ಕೂಲಂಕಶವಾಗಿ ತಿಳಿಯದೆ ನೀವು ಅವುಗಳನ್ನು ನಿಷೇಧಿಸಿಬಿಟ್ಟಿರಾ? ಅದಲ್ಲ ವಾದರೆ ನೀವು ಇನ್ನೇನು ಮಾಡುತ್ತಿದ್ದಿರಿ?’ ಎಂದು ಕೇಳುವನು.');
INSERT INTO "kannada" VALUES(27,85,'27:85','ಅವರು ಮಾಡಿದ ಅಕ್ರಮದ ಕಾರಣದಿಂದ ಅವರ ಮೇಲೆ (ಯಾತನೆಯ) ವಚನವು ನಿಜಗೊಳ್ಳುವುದು. ಆಗ ಅವರು (ಏನನ್ನೂ) ಮಾತಾಡಲಾರರು.');
INSERT INTO "kannada" VALUES(27,86,'27:86','ಅವರು ವಿಶ್ರಾಂತಿ ಪಡೆಯುವುದಕ್ಕಾಗಿ ರಾತ್ರಿ ಯನ್ನೂ ಕಣ್ಣು ಕಾಣಿಸುವ ನೆಲೆಯಲ್ಲಿ ಹಗಲನ್ನೂ ಅವರಿಗೆ ಸೃಷ್ಟಿಸಿದ್ದು ಅವರು ನೋಡುವುದಿಲ್ಲವೇ? ಸತ್ಯವಿಶ್ವಾಸವಿಟ್ಟ ಜನರಿಗೆ ಇದರಲ್ಲಿ ಅನೇಕ ನಿದರ್ಶನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(27,87,'27:87','ಸೂರ್ (ಕಹಳೆ) ಊದಲ್ಪಡುವ ದಿನ. ಆಗ ಅಲ್ಲಾ ಹನು ಇಚ್ಛಿಸಿದವರ ಹೊರತು ಆಕಾಶಗಳಲ್ಲೂ ಭೂಮಿಯಲ್ಲೂ ಇರುವವರೆಲ್ಲರೂ ದಿಗಿಲುಗೊಳ್ಳುವರು. ಎಲ್ಲರೂ ದೈನ್ಯರಾಗಿ ಅವನ ಸನ್ನಿ ಧಿಯಲ್ಲಿ ಹಾಜರಾಗುವರು .');
INSERT INTO "kannada" VALUES(27,88,'27:88','ನೀವು ಪರ್ವತಗಳನ್ನು ಕಾಣುವಾಗ ಅವು ಚೆನ್ನಾಗಿ ನಾಟಿಕೊಂಡಿವೆಯೆಂದು ನೀವು ಭಾವಿಸುವಿರಿ. ಆದರೆ, ಅವು ಮೋಡಗಳು ಚಲಿಸುವಂತೆ ಚಲಿಸುವುವು. ಇದು ಸಕಲವನ್ನೂ ಸೂತ್ರಬದ್ಧ ವಾಗಿ ವ್ಯವಸ್ಥೆಗೊಳಿಸಿರುವ ಅಲ್ಲಾಹನ ಕಾರ್ಯವಾಗಿರುವುದು. ನೀವು ಮಾಡುತ್ತಿರುವುದರ ಬಗ್ಗೆ ಅವನು ಸೂಕ್ಷ್ಮವಾಗಿ ಅರಿತಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(27,89,'27:89','ಯಾರು, ಒಂದು ಒಳಿತನ್ನು ತರುತ್ತಾನೆ, ಆತನಿಗೆ ಅದರ ಪ್ರತಿಫಲ ಸಿಗುವುದು. ಅವರು ಆ ದಿನದ ಭಯವಿಹ್ವಲತೆಯಿಂದ ನಿರ್ಭಯರಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(27,90,'27:90','ಯಾರು ಕೆಡುಕನ್ನು ತರುತ್ತಾನೆ, ಅವರನ್ನು ಅಧೋಮುಖವಾಗಿ ನರಕಕ್ಕೆ ಎಸೆಯಲಾಗುವುದು. ನೀವೆಸಗುತ್ತಿದ್ದ ಕಾರ್ಯಕ್ಕಲ್ಲದೆ ನಿಮಗೆ ಪ್ರತಿಫಲ ಸಿಗುವುದೇ? ');
INSERT INTO "kannada" VALUES(27,91,'27:91','');
INSERT INTO "kannada" VALUES(27,92,'27:92','(ಪೈಗಂಬರರೇ ಹೇಳಿರಿ,) ಈ ನಾಡನ್ನು ಆದರಣೀಯಗೊಳಿಸಿದ ಇದರ ಪ್ರಭುವಿನ ಆರಾಧನೆ ಮಾಡಬೇಕೆಂದು ನನಗೆ ಅಪ್ಪಣೆ ಕೊಡಲಾಗಿದೆ35. ಸಕಲ ವಸ್ತುವೂ ಅವನದ್ದು. ನಾನು ಮುಸ್ಲಿಮರ ಕೂಟಕ್ಕೆ ಸೇರಬೇಕೆಂದೂ ಖುರ್‍ಆನನ್ನು ಓದಿ ಹೇಳಬೇಕೆಂದೂ ಆಜ್ಞಾಪಿಸಲ್ಪಟ್ಟಿದ್ದೇನೆ. ಆದ್ದರಿಂದ ಯಾರು ಸನ್ಮಾರ್ಗವನ್ನು ಸ್ವೀಕರಿಸುತ್ತಾನೆ, ಅವನು ತನ್ನ ಹಿತಕ್ಕಾಗಿಯೇ ಸನ್ಮಾರ್ಗವನ್ನು ಸ್ವೀಕರಿಸುವನು. ಯಾರು ಪಥಭ್ರಷ್ಟನಾಗುವನೋ ಅವನೊಡನೆ, ‘ನಾನು ಎಚ್ಚರಿಕೆ ನೀಡುವವನು ಮಾತ್ರ’ ಎಂದು ಹೇಳಿರಿ.');
INSERT INTO "kannada" VALUES(27,93,'27:93','ಹೇಳಿರಿ; ಸರ್ವಸ್ತುತಿ ಅಲ್ಲಾಹನಿಗೆ, ತನ್ನ ನಿದರ್ಶನಗಳನ್ನು ಅವನು ನಿಮಗೆ ತೋರಿಸಿ ಕೊಡುವನು. ಆಗ ನೀವು ಅವುಗಳನ್ನು ತಿಳಿದು ಕೊಳ್ಳುವಿರಿ. ನೀವು ಮಾಡುತ್ತಿರುವ ಕರ್ಮಗಳ ಬಗ್ಗೆ ನಿಮ್ಮ ಪ್ರಭು ಅಶೃದ್ಧನಲ್ಲ.');
INSERT INTO "kannada" VALUES(28,1,'28:1','ತ್ವಾ ಸೀನ್ ಮೀಮ್.');
INSERT INTO "kannada" VALUES(28,2,'28:2','ಇವು ಸುವ್ಯಕ್ತವಾದ ವೇದಗ್ರಂಥದ ಸೂಕ್ತಗಳು.');
INSERT INTO "kannada" VALUES(28,3,'28:3','ವಿಶ್ವಾಸವಿರಿಸುವ ಜನರಿಗಾಗಿ ನಾವು ನಿಮಗೆ ಮೂಸಾ ಮತ್ತು ಫಿರ್‍ಔನರ ಕೆಲವು ವೃತ್ತಾಂತ ಗಳನ್ನು ಯಥಾರ್ಥವಾಗಿ ಓದಿ ತಿಳಿಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(28,4,'28:4','ಫಿರ್‍ಔನನು ಭೂಮಿಯಲ್ಲಿ ದರ್ಪ ತೋರಿದನು. ಮತ್ತು ಅದರ ನಿವಾಸಿಗಳನ್ನು ಪಂಗಡಗಳಾಗಿ ವಿಂಗಡಿಸಿದನು. ಆ ಪೈಕಿ ಒಂದು ಪಂಗಡವನ್ನು ಅವರ ಗಂಡು ಮಕ್ಕಳನ್ನು ವಧಿಸುತ್ತ ಮತ್ತು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಬದುಕಿರಲು ಬಿಡುತ್ತ ಅವನು ಬಲಹೀನಗೊಳಿಸಿದನು. ನಿಜಕ್ಕೂ ಅವನು ನಾಶಕೋರರಲ್ಲಿ ಸೇರಿ ಹೋಗಿದ್ದನು.');
INSERT INTO "kannada" VALUES(28,5,'28:5','ಭೂಮಿಯಲ್ಲಿ ತುಳಿಯಲ್ಪಟ್ಟವರ ಮೇಲೆ ಕೃಪೆ ತೋರಬೇಕೆಂದೂ ಅವರನ್ನು ನಾಯಕರನ್ನಾಗಿಯೂ ಉತ್ತರಾಧಿಕಾರಿಗಳನ್ನಾಗಿಯೂ ಮಾಡಬೇಕೆಂದೂ ನಾವು ಬಯಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(28,6,'28:6','ಭೂಮಿಯಲ್ಲಿ ಅವರಿಗೆ ಅನುಕೂಲ ನೀಡಬೇಕೆಂದೂ ಫಿರ್‍ಔನ್, ಹಾಮಾನ್ ಮತ್ತು ಅವರ ಸೇನೆಗಳಿಗೆ (ಆ ತುಳಿತಕ್ಕೊಳಗಾದ ಜನಾಂಗದಿಂದ) ಅವರು ಏನನ್ನು ಭಯಪಟ್ಟಿರುವರೋ ಅದನ್ನು ಅವರಿಗೆ (ಅನುಭವದಲ್ಲಿ) ತೋರಿಸಿ ಕೊಡ ಬೇಕೆಂದೂ ನಾವು ಬಯಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(28,7,'28:7','ನಾವು ಮೂಸಾರ ತಾಯಿಗೆ ಹೀಗೆ ಸೂಚನೆ ನೀಡಿದೆವು; ‘ಮಗುವಿಗೆ ಮೊಲೆ ಹಾಲುಣಿಸು. ಇನ್ನು ನಿನಗೆ ಈತನ ಬಗೆಗೆ ಭಯವುಂಟಾದರೆ ಇವನನ್ನು (ಒಂದು ಪೆಟ್ಟಿಗೆಯಲ್ಲಿರಿಸಿ) ನದಿಯಲ್ಲಿ ಹಾಕಿ ಬಿಡು. ಹೆದರಬೇಡ, ದುಃಖಿಸಲೂ ಬೇಡ. ನಾವು ಇವನನ್ನು ನಿನ್ನ ಬಳಿಗೇ ಮರಳಿ ತರಲಿದ್ದು ಇವನನ್ನು ಸಂದೇಶವಾಹಕರಲ್ಲಿ ಸೇರಿಸಲಿದ್ದೇವೆ’ .');
INSERT INTO "kannada" VALUES(28,8,'28:8','ಕೊನೆಗೆ ಫಿರ್‍ಔನನ ಜನರು ಮಗುವನ್ನು ನದಿಯಿಂದ ಎತ್ತಿಕೊಂಡರು. ಕೊನೆಗೆ ಅದು ಅವರಿಗೆ ವೈರಿಯೂ ದುಃಖದಾಯಕವೂ ಆಗಿ ಪರಿಣಮಿಸಿತು. ಫಿರ್‍ಔನನೂ ಹಾಮಾನನೂ ಅವರಿಬ್ಬರ ಸೇನೆಗಳೂ ನಿಜಕ್ಕೂ ಪಾಪಿಗಳಾಗಿದ್ದರು .');
INSERT INTO "kannada" VALUES(28,9,'28:9','ಫಿರ್‍ಔನನ ಪತ್ನಿಯು ಅವನೊಡನೆ ಹೀಗೆ ಹೇಳಿದಳು ‘ಈ ಮಗು ನನಗೂ, ತಮಗೂ ಕಣ್ಣಿಗೆ ತಂಪಾಗಿದೆ. ಇವನನ್ನು ನೀವು ಕೊಲ್ಲಬೇಡಿ. ಇವನು ನಮ್ಮ ಪ್ರಯೋಜನಕ್ಕೆ ಬರಬಹುದು. ಅಥವಾ ನಾವು ಇವನನ್ನು ಮಗನಾಗಿ ಮಾಡಿ ಕೊಳ್ಳಬಹುದು ಅವರು (ನಿಜಸ್ಥಿತಿಯನ್ನು) ತಿಳಿಯಲಾರರು');
INSERT INTO "kannada" VALUES(28,10,'28:10','ಮೂಸಾರ ತಾಯಿಯ ಹೃದಯ ನಿರಾಳವಾಯಿತು . ಆಕೆಯ ಹೃದಯದ ಮೇಲೆ ನಾವು ಹತೋಟಿಯನ್ನು ತರದಿರುತ್ತಿದ್ದರೆ ಅವಳು ಗುಟ್ಟನ್ನು ರಟ್ಟು ಮಾಡುತ್ತಿದ್ದಳು. ಅವಳು ವಿಶ್ವಾಸಿಗರಲ್ಲಿ ಸೇರಬೇಕೆಂದು ನಾವು ಹೀಗೆ ಮಾಡಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(28,11,'28:11','ಅವಳು ಮಗುವಿನ ಸಹೋದರಿಯೊಡನೆ ‘ನೀನು ಆತನ ಹಿಂದೆಯೇ ಹೋಗಿ ಅನ್ವೇಷಣೆ ಮಾಡು’ ಎಂದಳು. ಆ ಪ್ರಕಾರ ಅವಳು ಮಗುವನ್ನು ದೂರದಿಂದ ನೋಡಿ ತಿಳಿದಳು. ಶತ್ರುಗಳು ಇದನ್ನು ಗಮನಿಸಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(28,12,'28:12','ಅದಕ್ಕೂ ಮುನ್ನ ನಾವು ಆ ಮಗುವಿನ ಪಾಲಿಗೆ ಹಾಲುಣಿಸುವ ಸ್ತ್ರೀಯರನ್ನು ನಿಷಿದ್ಧಗೊಳಿಸಿದ್ದೆವು. ಆಗ ಆ ಸ್ತ್ರೀಯು ಅವರೊಡನೆ, ‘ನಿಮಗಾಗಿ ಇದರ ಪೋಷಣೆಯ ಹೊಣೆ ವಹಿಸಬಲ್ಲ, ಮಗುವಿನ ಹಿತ ನೋಡಿಕೊಳ್ಳಬಲ್ಲ ಒಂದು ಮನೆಯವರನ್ನು ನಾನು ನಿಮಗೆ ತೋರಿಸಿ ಕೊಡಲೇ?’ ಎಂದು ಕೇಳಿದಳು.');
INSERT INTO "kannada" VALUES(28,13,'28:13','ಈ ರೀತಿ ನಾವು ಆಕೆಯ ಕಣ್ಣುಗಳು ತಂಪಾಗಲಿ, ಆಕೆ ದುಃಖಿಸದಿರಲಿ ಮತ್ತು ಅಲ್ಲಾಹನ ವಾಗ್ದಾನವು ಸತ್ಯವೆಂದು ಆಕೆ ತಿಳಿಯಲಿ ಎಂದು ಮೂಸಾರನ್ನು ಅವರ ಮಾತೆಯ ಬಳಿಗೆ ಮರಳಿಸಿದ್ದೇವೆ. ಆದರೆ ಹೆಚ್ಚಿನವರು ಇದನ್ನು ತಿಳಿಯುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(28,14,'28:14','ಮೂಸಾ ತಮ್ಮ ತುಂಬು ಯೌವ್ವನಕ್ಕೆ ತಲುಪಿ ದಾಗ ಹಾಗೂ ಅವರ ಬುದ್ಧಿ ಪಕ್ವಗೊಂಡಾಗ ನಾವು ಅವರಿಗೆ ತತ್ವಜ್ಞಾನವನ್ನೂ ಸುಜ್ಞಾನವನ್ನೂ ನೀಡಿದೆವು. ನಾವು ಸಜ್ಜನರಿಗೆ ಇದೇ ರೀತಿ ಸತ್ಫಲವನ್ನು ದಯಪಾಲಿಸುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(28,15,'28:15','ಒಂದು ದಿನ ನಗರವಾಸಿಗಳು ಅಲಕ್ಷ್ಯರಾಗಿದ್ದಾಗ ಅವರು ನಗರದೊಳಗೆ ಪ್ರವೇಶಿಸಿದರು. ಅಲ್ಲಿ ಅವರು ಇಬ್ಬರು ವ್ಯಕ್ತಿಗಳು ಜಗಳಾಡುತ್ತಿರುವುದನ್ನು ಕಂಡರು. ಒಬ್ಬನು ಅವರ ಸ್ವಂತ ಜನಾಂಗದವ ನಾಗಿದ್ದು ಇನ್ನೊಬ್ಬನು ಅವರ ಶತ್ರು ಜನಾಂಗಕ್ಕೆ ಸೇರಿದವನಾಗಿದ್ದನು. ಆಗ ಅವರ ಜನಾಂಗದವನು ಶತ್ರು ಜನಾಂಗದವನ ವಿರುದ್ಧ ಅವರಲ್ಲಿ ಸಹಾಯ ಬೇಡಿದನು. ಆಗ ಮೂಸಾ ಅವನಿಗೆ ಮುಷ್ಟಿಯಿಂದ ಒಂದೇಟು ಕೊಟ್ಟರು. ಹಾಗೆ ಅದು ಅವನ ಕಥೆ ಮುಗಿಸಿತು . ಮೂಸಾ ಹೇಳಿದರು, ‘ಇದು ಶೈತಾನನ ಕೆಲಸ. ಶೈತಾನನು (ಮನುಷ್ಯನನ್ನು) ದಾರಿಗೆಡಿಸುವ ಬಹಿರಂಗ ಶತ್ರುವಾಗಿರುವನು’.');
INSERT INTO "kannada" VALUES(28,16,'28:16','ಅವರು, ‘ನನ್ನ ಪ್ರಭೂ! ನನ್ನ ಮೇಲೆ ನಾನೇ ಅಕ್ರಮವೆಸಗಿದೆ. ಆದ್ದರಿಂದ ನನ್ನನ್ನು ಕ್ಷಮಿಸು’ ಎಂದರು. ಆಗ ಅಲ್ಲಾಹನು ಅವರನ್ನು ಕ್ಷಮಿಸಿದನು. ನಿಜಕ್ಕೂ ಅವನು ಮಹಾಕ್ಷಮಾಶೀಲನೂ ಕರುಣಾಮಯಿಯೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(28,17,'28:17','ಮೂಸಾ ಹೀಗೆ ಹೇಳಿದರು. ‘ನನ್ನ ಪ್ರಭೂ! ನೀನು ನನ್ನ ಮೇಲೆ ಅನುಗ್ರಹಿಸಿರುವ ಮೂಲಕ ನನ್ನನು ಕಾಪಾಡು ಆದರೆ, ನಾನೆಂದೂ ಅಪರಾಧಿಗಳ ಸಹಾಯಕನಾಗಲಾರೆ’.');
INSERT INTO "kannada" VALUES(28,18,'28:18','ಮರುದಿನ ಬೆಳಗಾಗುತ್ತಲೇ (ಕೊಲೆಯ ಪರಿಣಾ ಮಕ್ಕೆ) ಹೆದರುತ್ತಾ, ನಿರೀಕ್ಷಿಸುತ್ತ ಅವರು ನಗರಕ್ಕೆ ಬಂದಾಗ, ನಿನ್ನೆ ಅವರಲ್ಲಿ ಸಹಾಯ ಕೇಳಿದ ವ್ಯಕ್ತಿಯೇ ಇಂದು ಪುನಃ ಸಹಾಯಕ್ಕಾಗಿ ಮೊರೆಯಿಡುತ್ತಿರುವುದನ್ನು ಕಂಡರು. ಮೂಸಾ ಅವನೊ ಡನೆ, ‘ನೀನು ಸ್ಪಷ್ಟವಾದ ದಾರಿಗೆಟ್ಟವನೇ ಸರಿ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(28,19,'28:19','ಅನಂತರ ಮೂಸಾ ಅವರಿಬ್ಬರ ಶತ್ರುವಾದ ವ್ಯಕ್ತಿಯನ್ನು ಹಿಡಿಯಲಿಚ್ಛಿಸಿದಾಗ ಅವನು- ‘ಮೂಸಾ, ನೀನು ನಿನ್ನೆ ಒಬ್ಬನನ್ನು ಕೊಲೆ ಮಾಡಿದಂತೆಯೇ ಇಂದು ನನ್ನನ್ನೂ ಕೊಲೆ ಮಾಡಲು ಹೊರಟಿ ರುವೆಯಾ? ನೀನು ಈ ಭೂಮಿಯಲ್ಲಿ ಒಬ್ಬ ಸ್ವೇಚ್ಛಾಧಿಕಾರಿಯಾಗ ಬಯಸುವವನೇ ಹೊರತು ಒಳಿತು ಮಾಡುವವರ ಜೊತೆ ಸೇರ ಬಯಸುವುದಿಲ್ಲ’ ಎಂದು ಕೂಗಿದನು.');
INSERT INTO "kannada" VALUES(28,20,'28:20','ಅನಂತರ ಒಬ್ಬನು ನಗರದ ತುತ್ತ ತುದಿಯಿಂದ ಓಡುತ್ತಾ ಬಂದು ‘ಓ ಮೂಸಾ, ಸರದಾರರ ನಡುವೆ ನಿನ್ನನ್ನು ಕೊಲೆ ಮಾಡುವ ಸಮಾಲೋ ಚನೆ ನಡೆಯುತ್ತಿದೆ. ಆದ್ದರಿಂದ ಕೂಡಲೇ ಇಲ್ಲಿಂದ ಹೊರಟು ಹೋಗು. ನಾನು ನಿನ್ನ ಹಿತಚಿಂತಕ ನಾಗಿದ್ದೇನೆ’ ಎಂದು ಹೇಳಿದನು.');
INSERT INTO "kannada" VALUES(28,21,'28:21','ಆಗ ಮೂಸಾ ಭಯಭೀತರಾಗಿ (ಇದರ ಪರಿಣಾ ಮದ ಬಗ್ಗೆ) ಜಾಗೃತೆ ವಹಿಸುತ್ತಾ ಅಲ್ಲಿಂದ ಹೊರಟರು. ‘ನನ್ನ ಪ್ರಭೂ, ನನ್ನನ್ನು ಅಕ್ರಮಿ ಜನಾಂಗದಿಂದ ರಕ್ಷಿಸು’ ಎಂದು ಪ್ರಾರ್ಥಿಸಿದರು.');
INSERT INTO "kannada" VALUES(28,22,'28:22','ಮೂಸಾ ಮದ್‍ಯನಿನ ಕಡೆಗೆ ಮುಖ ಮಾಡಿ ದಾಗ ಅವರು, ‘ನನ್ನ ಪ್ರಭು ನನ್ನನ್ನು ಸರಿಯಾದ ಮಾರ್ಗಕ್ಕೆ ಮುನ್ನಡೆಸುವನು’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(28,23,'28:23','ಅವರು ಮದ್‍ಯನಿನ ನೀರಿನ ಬಳಿಗೆ ತಲುಪಿದಾಗ ಅದರ ಹತ್ತಿರ ಜನರ ಗುಂಪೊಂದು ತಮ್ಮ ಆಡುಗಳಿಗೆ ನೀರು ಕುಡಿಸುತ್ತಿರುವುದನ್ನೂ ಅವರಿಂದ ದೂರ ಒಂದೆಡೆ ಇಬ್ಬರು ಮಹಿಳೆಯರು ತಮ್ಮ ಆಡುಗಳನ್ನು ತಡೆದು ನಿಲ್ಲಿಸುತ್ತಿರುವುದ ನ್ನೂ ಕಂಡರು. ಮೂಸಾ, ಆ ಮಹಿಳೆಯರೊಡನೆ, ‘ನಿಮ್ಮ ಸಮಾಚಾರವೇನು?’ ಎಂದು ಕೇಳಿದರು. ಆಗ ಅವರು ‘ಈ ಕುರುಬರು ತಮ್ಮ ಆಡುಗಳಿಗೆ ನೀರು ಕುಡಿಸಿ ಮರಳಿ ಹೋಗುವವರೆಗೂ ನಾವು ನಮ್ಮ ಆಡುಗಳಿಗೆ ನೀರು ಕುಡಿಸುವಂತಿಲ್ಲ. ನಮ್ಮ ತಂದೆ ಓರ್ವ ಅತಿ ವೃದ್ಧರಾಗಿದ್ದಾರೆ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(28,24,'28:24','ಆಗ ಮೂಸಾ, ಆ ಇಬ್ಬರು ಸ್ತ್ರೀಯರಿಗಾಗಿ ಅವರ ಆಡುಗಳಿಗೆ ನೀರು ಕುಡಿಸಿದರು. ತರುವಾಯ ಒಂದು (ಮರದ) ನೆರಳಿರುವ ಜಾಗಕ್ಕೆ ಹೋಗಿ ಕುಳಿತು ಹೀಗೆ ಹೇಳಿದರು. ‘ಪ್ರಭೂ, ನನಗೆ ನೀನು ಇಳಿಸಿ ಕೊಡುವ ಯಾವುದೇ ಒಳಿತಿಗೂ ನಾನು ಅಪೇಕ್ಷಕನಾಗಿದ್ದೇನೆ’.');
INSERT INTO "kannada" VALUES(28,25,'28:25','ಹಾಗೆ ಆ ಇಬ್ಬರು ಮಹಿಳೆಯರಲ್ಲಿ ಒಬ್ಬಳು ನಡೆಯುತ್ತಾ ಲಜ್ಜೆಯೊಂದಿಗೆ ಅವರ ಬಳಿಗೆ ಬಂದು `ನೀವು ನಮಗಾಗಿ ಆಡುಗಳಿಗೆ ನೀರು ಕುಡಿಸಿದುದರ ಪ್ರತಿಫಲವನ್ನು ನಿಮಗೆ ಕೊಡಲಿ ಕ್ಕಾಗಿ ನನ್ನ ತಂದೆ ನಿಮ್ಮನ್ನು ಕರೆಯುತ್ತಿದ್ದಾರೆ’ ಎಂದಳು. ಹಾಗೆ ಮೂಸಾ ಅವರ ಬಳಿಗೆ ತಲುಪಿ ತಮ್ಮ ಸಂಪೂರ್ಣ ವೃತ್ತಾಂತವನ್ನು ಅವರಿಗೆ ವಿವರಿಸಿ ಹೇಳಿದಾಗ ಅವರು, ‘ನೀವು ಹೆದರ ಬೇಡಿರಿ. ನೀವು ಅಕ್ರಮಿಗಳಿಂದ ಪಾರಾಗಿದ್ದೀರಿ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(28,26,'28:26','ಆ ಇಬ್ಬರು ಮಹಿಳೆಯರಲ್ಲಿ ಒಬ್ಬಳು ‘ಅಪ್ಪಾ! ಇವರನ್ನು ನೌಕರನಾಗಿ ಇರಿಸಿಕೊಳ್ಳಿರಿ. ನೀವು ನೌಕರನಾಗಿ ಇರಿಸಿಕೊಳ್ಳುವವರ ಪೈಕಿ ಬಲಿಷ್ಠನೂ ಪ್ರಾಮಾಣಿಕನೂ ಆದವನೇ ಉತ್ತಮನು’ ಎಂದಳು .');
INSERT INTO "kannada" VALUES(28,27,'28:27','ಅವಳ ತಂದೆಯು ನೀವು ಎಂಟು ವರ್ಷಗಳವರೆಗೆ ನನ್ನಲ್ಲಿ ನೌಕರಿ ಮಾಡಬೇಕೆಂಬ ಶರತ್ತಿನ ಮೇಲೆ ನಾನು ನನ್ನ ಈ ಇಬ್ಬರು ಪುತ್ರಿಯರಲ್ಲಿ ಒಬ್ಬಳನ್ನು ನಿಮಗೆ ವಿವಾಹ ಮಾಡಲಿಚ್ಛಿಸುತ್ತೇನೆ. ಇನ್ನು ನೀವು ಹತ್ತು ವರ್ಷ ಪೂರ್ತಿಗೊಳಿಸಿದರೆ ಅದು ನಿಮ್ಮ ಇಚ್ಚೆ. ನಾನು ನಿಮಗೆ ಕಷ್ಟ ಕೊಡಲಿಚ್ಛಿಸು ವುದಿಲ್ಲ. ಅಲ್ಲಾಹನಿಚ್ಚಿಸಿದರೆ ನೀವು ನನ್ನನ್ನು ಸಜ್ಜನರಲ್ಲಿ ಒಬ್ಬನಾಗಿ ಕಾಣುವಿರಿ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(28,28,'28:28','ಆಗ ಮೂಸಾ, ‘ಇದು ನನ್ನ ಮತ್ತು ನಿಮ್ಮ ನಡುವೆ ಇರುವ ತೀರ್ಮಾನವಾಗಿದ್ದು ನಾನು ಈ ಎರಡು ಅವಧಿಗಳಲ್ಲಿ ಯಾವುದನ್ನೇ ಪೂರ್ತಿ ಗೊಳಿಸಿದರೂ ನಂತರ ನನ್ನ ಮೇಲೆ ಹೆಚ್ಚಿನ ಹೊರೆ ಹೊರಿಸಬಾರದು. ನಾವು ಹೇಳುತ್ತಿರುವ ವಿಷಯದ ಮೇಲೆ ಅಲ್ಲಾಹನು ಸಾಕ್ಷಿಯಾಗಿದ್ದಾನೆ’ ಎಂದು ಉತ್ತರಿಸಿದರು.');
INSERT INTO "kannada" VALUES(28,29,'28:29','ಮೂಸಾ, ನಿರ್ದಿಷ್ಟ ಅವಧಿ ಪೂರ್ತಿಗೊಳಿಸಿದ ನಂತರ ತನ್ನ ಕುಟುಂಬದೊಂದಿಗೆ (ಸ್ವದೇಶಕ್ಕೆ) ಹೊರಟಾಗ ಥೂರ್ ಪರ್ವತದ ಕಡೆಯಿಂದ ಅವರಿ ಗೊಂದು ಬೆಂಕಿ ಕಂಡಿತು. ಅವರು ಕುಟುಂಬದೊಂದಿಗೆ ‘ನೀವಿಲ್ಲಿ ನಿಲ್ಲಿರಿ, ನಾನೊಂದು ಬೆಂಕಿಯನ್ನು ಕಂಡಿದ್ದೇನೆ. ಪ್ರಾಯಶಃ ನಾನು ಅಲ್ಲಿಂದೇನಾದರೂ (ದಾರಿಯ ಬಗ್ಗೆ) ಸುದ್ದಿ ಯನ್ನಾಗಲಿ ನಿಮಗೆ ಉಪಯೋಗಕ್ಕೆ ಆ ಬೆಂಕಿಯಿಂದ ಒಂದು ಕೊಳ್ಳಿಯನ್ನಾದರೂ ತರುತ್ತೇನೆ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(28,30,'28:30','ಹಾಗೆ ಅವರು ಅಲ್ಲಿಗೆ ತಲಪಿದಾಗ ಅನುಗ್ರಹಿತ ಕಣಿವೆಯಲ್ಲಿ ಬಲದಿಕ್ಕಿನ ತಪ್ಪಲಿನ ಒಂದು ಮರದಿಂದ ಹೀಗೆ ಕೂಗಿ ಹೇಳಲಾಯಿತು. “ಓ ಮೂಸಾ! ನಾನು ಸರ್ವ ಲೋಕಗಳ ಪ್ರಭುವಾದ ಅಲ್ಲಾಹನಾಗಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(28,31,'28:31','‘ನಿಮ್ಮ ದಂಡವನ್ನು ಕೆಳಗೆ ಹಾಕಿರಿ”. ಆಗ ದಂಡವು (ಚುರುಕಿನ) ಹಾವಿನಂತೆ ತೆವಳುತ್ತಿರುವುದನ್ನು ಕಂಡಾಗ ಮೂಸಾ ಬೆನ್ನು ತಿರುಗಿಸಿ ಓಟಕ್ಕಿತ್ತರು. ಅವರು ತಿರುಗಿ ಬರಲಿಲ್ಲ (ಆಗ ಕರೆ ಕೇಳಿಸಿತು.) ‘ಓ ಮೂಸಾ, ಮುಂದೆ ಬನ್ನಿರಿ, ಹೆದರಬೇಡಿರಿ, ನೀವು ನಿರ್ಭೀತರಲ್ಲಿ ಒಬ್ಬರಾಗಿದ್ದೀರಿ.');
INSERT INTO "kannada" VALUES(28,32,'28:32','ನಿಮ್ಮ ಕೈಯನ್ನು ನಿಮ್ಮ ಜೇಬಿನೊಳಗೆ ಹಾಕಿರಿ, ಆಗ ಯಾವುದೇ ಕೇಡೂ ಇಲ್ಲದೆ ಬೆಳ್ಳಗೆ ಹೊಳೆಯುತ್ತ ಅದು ಹೊರಬರುವುದು. ಭೀತಿಯಿಂದ ಪಾರಾಗಲು ನಿಮ್ಮ ಕೈಯನ್ನು ದೇಹಕ್ಕೆ ಅಪ್ಪಿಕೊಳ್ಳಿರಿ . ಫಿರ್‍ಔನ್ ಮತ್ತು ಅವನ ಪರಿವಾರದ ಕಡೆಗೆ ಇವೆರಡು (ದಂಡ ಹಾಗೂ ಹಸ್ತ) ನಿಮ್ಮ ಪ್ರಭುವಿನ ಪ್ರತ್ಯಕ್ಷ ನಿದರ್ಶನ ಗಳಾಗಿವೆ. ಅವರು ಕರ್ಮಭ್ರಷ್ಟ ಜನಾಂಗವಾಗಿರುವರು’.');
INSERT INTO "kannada" VALUES(28,33,'28:33','ಆಗ ಮೂಸಾ ಹೇಳಿದರು, “ನನ್ನ ಪ್ರಭೂ, ನಾನು ಅವರಲ್ಲಿ ಒಬ್ಬ ವ್ಯಕ್ತಿಯನ್ನು ಕೊಲೆ ಮಾಡಿದ್ದೇನೆ. ಆದ್ದರಿಂದ ಅವರು ನನ್ನನ್ನು ಕೊಂದು ಹಾಕುವರೆಂದು ಭಯಪಡುತ್ತಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(28,34,'28:34','ನನ್ನ ಸಹೋದರ ಹಾರೂನರು ನನಗಿಂತ ಉತ್ತಮ ಮಾತುಗಾರ. ಆದ್ದರಿಂದ ನನ್ನ ನಿಜ ಸ್ಥಿತಿಯನ್ನು ಸಮರ್ಥಿಸುವ ಒಬ್ಬ ಸಹಾಯಕನಾಗಿ ಅವರನ್ನು ನಿಯಮಿಸಿ ನನ್ನ ಜೊತೆ ಕಳುಹಿಸು, ಆ ಜನರು ನನ್ನನ್ನು ಸುಳ್ಳಾಗಿಸುವರೆಂಬ ಆಶಂಕೆ ನನಗಿದೆ’.');
INSERT INTO "kannada" VALUES(28,35,'28:35','ಆಗ ಅಲ್ಲಾಹು ಹೇಳಿದನು. `ನಿಮ್ಮ ಸಹೋದರನ ಮುಖಾಂತರ ನಾವು ನಿಮ್ಮ ತೋಳಿಗೆ ಬಲ ನೀಡಲಿದ್ದೇವೆ. ಅವರು ನಿಮ್ಮ ಬಳಿಗೇನೂ ತಲುಪಲಾಗದ ವಿಜಯ ಶಕ್ತಿಯನ್ನು ನಿಮಗಿಬ್ಬರಿಗೂ ದಯಪಾಲಿಸುತ್ತೇವೆ. ನಮ್ಮ ನಿದರ್ಶನಗಳ ಮುಖೇನ ನೀವಿಬ್ಬರೂ ನಿಮ್ಮನ್ನು ಹಿಂಬಾಲಿಸಿದ ನಿಮ್ಮ ಅನುಯಾಯಿಗಳೂ ಖಂಡಿತ ಮೇಲುಗೈ ಸಾಧಿಸುವಿರಿ’ .');
INSERT INTO "kannada" VALUES(28,36,'28:36','ತರುವಾಯ ಮೂಸಾ ನಮ್ಮ ಸುವ್ಯಕ್ತ ನಿದರ್ಶನ ಗಳನ್ನು ಅವರ ಬಳಿಗೆ ತಂದರು. ಆಗ ಅವರು, ‘ಇದು ಕೃತಕ ಜಾದುವೇ ಹೊರತು ಬೇರೇನಲ್ಲ, ನಾವು ಇದನ್ನು ನಮ್ಮ ಪೂರ್ವಜರಲ್ಲಿ ಎಂದೂ ಕೇಳಿರಲಿಲ್ಲ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(28,37,'28:37','ಮೂಸಾ ಹೀಗೆಂದರು, ‘ನನ್ನ ಪ್ರಭು, ತನ್ನ ವತಿಯಿಂದ ಮಾರ್ಗದರ್ಶನ ಪಡೆದು ಬಂದವರಾರೆಂದೂ ಈ ಭವನ (ಇಹಜೀವನ)ದ ಶುಭ ಪರ್ಯವಸಾನವಿರುವುದು ಯಾರಿಗೆಂದೂ ಚೆನ್ನಾಗಿ ತಿಳಿದಿರುವನು . ಅಕ್ರಮಿಗಳು ಖಂಡಿತ ಯಶಸ್ವಿಯಾಗುವುದಿಲ್ಲ’ .');
INSERT INTO "kannada" VALUES(28,38,'28:38','ಫಿರ್‍ಔನನು, “ಆಸ್ಥಾನಿಕರೇ, ನನ್ನ ಹೊರತು ಇನ್ನಾವ ದೇವನೂ ನಿಮಗಿರುವ ಬಗ್ಗೆ ನಾನರಿಯೆನು, ಆದ್ದರಿಂದ ಓ ಹಾಮಾನ್! ನನಗಾಗಿ ಮಣ್ಣಿನ (ಇಟ್ಟಿಗೆ ಮಾಡಿ) ಮೇಲೆ ಬೆಂಕಿ ಉರಿಸು, ನನಗಾಗಿ ಒಂದು ಎತ್ತರವಾದ ಕಟ್ಟಡವನ್ನು ನಿರ್ಮಿಸು. ಮೂಸಾನ ದೇವನನ್ನು ನಾನೊಮ್ಮೆ ಪ್ರತ್ಯಕ್ಷ ನೋಡುತ್ತೇನೆ. (ಬೇರೆ ಆರಾಧ್ಯನಿದ್ದಾನೆ ಎಂಬ ವಾದದಲ್ಲಿ) ಇವನನ್ನು ನಾನು ಸುಳ್ಳುಗಾರನೆಂದು ಭಾವಿಸುತ್ತೇನೆ” ಎಂದನು.');
INSERT INTO "kannada" VALUES(28,39,'28:39','ಅವನೂ ಅವನ ಸೇನೆಗಳೂ ಭೂಮಿಯಲ್ಲಿ ಯಾವ ನ್ಯಾಯವೂ ಇಲ್ಲದೆ ಅಹಂಭಾವ ತೋರಿದರು. ಅವರಿಗೆಂದೂ ನಮ್ಮ ಕಡೆಗೆ ಮರಳಿ ಬರಲಿಕ್ಕಿಲ್ಲವೆಂದೇ ಅವರು ಭಾವಿಸಿಕೊಂಡರು.');
INSERT INTO "kannada" VALUES(28,40,'28:40','ಆದ್ದರಿಂದ ನಾವು ಅವನನ್ನೂ ಅವನ ಸೇನೆಗಳನ್ನೂ ಹಿಡಿದು ಸಮುದ್ರಕ್ಕೆ ಎಸೆದೆವು. ಆಗ ಅಕ್ರಮಿಗಳ ಗತಿಯೇನಾಯಿತೆಂದು ನೋಡಿರಿ.');
INSERT INTO "kannada" VALUES(28,41,'28:41','ಅವರನ್ನು ನರಕದ ಕಡೆಗೆ ಆಹ್ವಾನಿಸುವ ಮುಂದಾಳುಗಳನ್ನಾಗಿ ನಾವು ಮಾಡಿದೆವು. ಅವರು ಪುನರುತ್ಥಾನ ದಿನ ಯಾವ ಸಹಾಯವನ್ನೂ ಪಡೆಯಲಾರರು.');
INSERT INTO "kannada" VALUES(28,42,'28:42','ಈ ಲೋಕದಲ್ಲಿ ನಾವು ಅವರ ಹಿಂದೆ ಶಾಪವನ್ನು ಅನುಕ್ರಮಿಸಿದೆವು. ಪುನರುತ್ಥಾನ ದಿನ ಅವರು (ನಮ್ಮ ಕಾರುಣ್ಯದಿಂದ) ದೂರ ಅಟ್ಟಲಾದವರ ಗುಂಪಿಗೆ ಸೇರುವರು.');
INSERT INTO "kannada" VALUES(28,43,'28:43','ನಾವು ಹಿಂದಿನ ಪೀಳಿಗೆಗಳನ್ನು ನಾಶಗೊಳಿಸಿದ ಬಳಿಕ ಜನರಿಗೆ ಒಳದೃಷ್ಟಿ, ಮಾರ್ಗದರ್ಶನ ಮತ್ತು ಅನುಗ್ರಹವಾಗಿ ಮೂಸಾರಿಗೆ ವೇದ ಗ್ರಂಥವನ್ನು ದಯಪಾಲಿಸಿದೆವು. ಅವರು ಯೋಚಿ ಸಲಿಕ್ಕಾಗಿ,');
INSERT INTO "kannada" VALUES(28,44,'28:44','ಪ್ರವಾದಿಯರೇ, ನಾವು ಮೂಸಾರಿಗೆ ಈ ಧರ್ಮಾ ದೇಶವನ್ನು ನೀಡಿದಾಗ ನೀವು (ಥೂರ್ ಪರ್ವತದ) ಪಶ್ಚಿಮ ಭಾಗದಲ್ಲಿರಲಿಲ್ಲ. ನೀವು ಅದರ ಸಾಕ್ಷಿದಾರರಲ್ಲೂ ಸೇರಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(28,45,'28:45','ಆದರೆ ಆ ಬಳಿಕ ನಾವು ಅನೇಕ ಪೀಳಿಗೆಗಳನ್ನು ಹೊಸತಾಗಿ ನಿರ್ಮಿಸಿದೆವು. ಅವರ ಮೇಲೆ ದೀರ್ಘಕಾಲ ಕಳೆದಿವೆ. ಮದ್‍ಯನರಿಗೆ ನಮ್ಮ ಸೂಕ್ತಗಳನ್ನು ಓದಿ ಕೇಳಿಸುವಾಗ ನೀವು ಅವರ ಮಧ್ಯೆ ತಂಗಿರಲಿಲ್ಲ. ಆದರೆ ನಾವು ನಿಮಗೆ ವಾರ್ತೆಗಳನ್ನು ಕಳುಹಿಸಿ ಕೊಡುವವರಾಗಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(28,46,'28:46','ನಾವು ಮೂಸಾರನ್ನು ಕರೆದಾಗ ನೀವು ಥೂರ್ ಪರ್ವತದ ಮಗ್ಗುಲಲ್ಲೂ ಇದ್ದಿರಲಿಲ್ಲ. ಆದರೆ ತಮ್ಮ ಪ್ರಭುವಿನ ಮಹತ್ತರ ಅನುಗ್ರಹದ ಮೂಲಕ ಈ ಅರಿವನ್ನು ನಿಮಗೆ ನೀಡಲಾಗುತ್ತಿದೆ. ನಿಮಗಿಂತ ಮುಂಚೆ ಎಚ್ಚರಿಕೆ ನೀಡುವವರಾರೂ ಬಾರದಿದ್ದ ಜನರಿಗೆ ನೀವು ಎಚ್ಚರಿಕೆ ನೀಡುವ ಸಲುವಾಗಿ. ಅವರು ಯೋಚಿಸಲೆಂದು.');
INSERT INTO "kannada" VALUES(28,47,'28:47','ಅವರ ಕೈಗಳು ಮಾಡಿಹೋದ ಸ್ವಂತ ದುಷ್ಕøತ್ಯ ಗಳ ಫಲವಾಗಿ ಅವರಿಗೇನಾದರೂ ವಿಪತ್ತು ತಟ್ಟಿದರೆ ಅವರು, “ಪ್ರಭೂ, ನಾವು ನಿನ್ನ ಸೂಕ್ತಗಳ ಅನುಸರಣೆ ಮಾಡಲು ಹಾಗೂ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಲ್ಲಿ ಸೇರಲು ನೀನು ನಮ್ಮೆಡೆಗೆ ಸಂದೇಶವಾಹ ಕನನ್ನೇಕೆ ಕಳುಹಿಸಲಿಲ್ಲ?” ಎಂದು ಹೇಳಬಾರದೆಂದೇ ನಾವು ತಮ್ಮನ್ನು ಅವರ ಕಡೆಗೆ ಕಳುಹಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(28,48,'28:48','ಆದರೆ ನಮ್ಮ ಬಳಿಯಿಂದ ಅವರಿಗೆ ಸತ್ಯವು ಬಂದಾಗ ಅವರು ಹೇಳಿದರು, `ಮೂಸಾರಿಗೆ ಕೊಡಲಾಗಿದ್ದುದನ್ನು (ದೃಷ್ಟಾಂತವನ್ನು) ಇವರಿಗೇಕೆ ಕೊಡಲಾಗಿಲ್ಲ?’ ಇದಕ್ಕಿಂತ ಮುಂಚೆ ಮೂಸಾರಿಗೆ ಕೊಡಲಾಗಿದ್ದುದನ್ನು ಅವರು ನಿರಾಕರಿಸಲಿಲ್ಲವೇ? ಅವರು ಹೇಳಿದರು, ‘ಇವೆರಡೂ (ತೌರಾತ್ ಹಾಗೂ ಖುರ್‍ಆನ್) ಪರಸ್ಪರ ಸಮರ್ಥಿಸುವ ಜಾದು ಆಗಿವೆ’. ‘ವಾಸ್ತವದಲ್ಲಿ ನಾವು ಎಲ್ಲವನ್ನು ನಿಷೇಧಿಸುತ್ತೇವೆ’ ಎಂದರವರು! ');
INSERT INTO "kannada" VALUES(28,49,'28:49','(ಪ್ರವಾದಿಯರೇ) ಹೇಳಿರಿ, “ನೀವು ಸತ್ಯವಾದಿಗಳಾಗಿದ್ದರೆ ಇವೆರಡಕ್ಕಿಂತಲೂ ಹೆಚ್ಚು ಸನ್ಮಾರ್ಗದರ್ಶನ ನೀಡುವ ಒಂದು ಗ್ರಂಥವನ್ನು ಅಲ್ಲಾಹನ ಬಳಿಯಿಂದ ತನ್ನಿರಿ. ಅದನ್ನು ನಾನು ಅನುಸರಿಸುತ್ತೇನೆ” .');
INSERT INTO "kannada" VALUES(28,50,'28:50','ಇನ್ನು ಅವರು ನಿಮಗೆ ಉತ್ತರಿಸದಿದ್ದರೆ ಖಂಡಿತ ಅವರು ತಮ್ಮ ಸ್ವೇಚ್ಚೆಗಳ ಹಿಂಬಾಲಕರೆಂದು ತಿಳಿದುಕೊಳ್ಳಿರಿ. ಅಲ್ಲಾಹನಿಂದ ಯಾವುದೇ ಮಾರ್ಗದರ್ಶನವಿಲ್ಲದೆ ಕೇವಲ ತನ್ನ ಸ್ವೇಚ್ಛೆಯನ್ನು ಅನುಸರಿಸುವವನಿಗಿಂತ ಹೆಚ್ಚು ದಾರಿಗೆಟ್ಟವನು ಯಾರು? ವಾಸ್ತವದಲ್ಲಿ ಅಲ್ಲಾಹನು ಅಕ್ರಮಿ ಜನಾಂಗವನ್ನು ಸನ್ಮಾರ್ಗಕ್ಕೆ ಸೇರಿಸಲಾರನು.');
INSERT INTO "kannada" VALUES(28,51,'28:51','ಅವರು ಚೆನ್ನಾಗಿ ಯೋಚಿಸುವಂತಾಗಲಿ ಎಂದು ನಾವು ಅವರಿಗೆ (ಮಕ್ಕಾದವರಿಗೆ) ಸತತವಾಗಿ ಈ ವಚನವನ್ನು (ಖುರ್‍ಆನನ್ನು) ತಲುಪಿಸಿ ದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(28,52,'28:52','ಇದಕ್ಕೆ ಮುಂಚೆ ಯಾರಿಗೆ ನಾವು ಗ್ರಂಥ ನೀಡಿದ್ದೇವೋ ಅವರು ಈ ಖುರ್‍ಆನನ್ನು ನಂಬುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(28,53,'28:53','ಅವರ ಮೇಲೆ ಇದನ್ನು ಓದಿ ಹೇಳಿದಾಗ ಅವರು, ‘ನಾವು ಇದರಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸಿದ್ದೇವೆ. ಇದು ನಮ್ಮ ಪ್ರಭುವಿನಿಂದ ಬಂದ ಸತ್ಯವಾಗಿದ್ದು, ನಾವು ಇದಕ್ಕೆ ಮೊದಲೇ ಮುಸ್ಲಿಮರಾಗಿ (ಅಲ್ಲಾ ಹನಿಗೆ ವಿಧೇಯರಾಗಿ) ಬಿಟ್ಟಿದ್ದೇವೆ” ಎನ್ನುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(28,54,'28:54','ಸಹನೆ ತೋರಿದ ಕಾರಣಕ್ಕೆ ಅವರಿಗೆ ಅವರ ಸತ್ಫಲವನ್ನು ಎರಡು ಬಾರಿ ಕೊಡಲಾಗುವುದು. ಅವರು ಕೆಡುಕುಗಳನ್ನು ಒಳಿತಿನಿಂದ ತಡೆಯುತ್ತಾರೆ . ಅವರಿಗೆ ನಾವು ನೀಡಿರುವುದರಿಂದ ಖರ್ಚು ಮಾಡುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(28,55,'28:55','ಅವರು ಅನಗತ್ಯ ಮಾತನ್ನು ಕೇಳಿದರೆ, ಅದರಿಂದ ವಿಮುಖರಾಗಿ ಹೀಗೆ ಹೇಳುವರು. ‘ನಮ್ಮ ಕರ್ಮ ನಮಗೆ, ನಿಮ್ಮ ಕರ್ಮ ನಿಮಗೆ, ನಿಮಗೆ ಸಲಾಮ್, ಮೂಢರ ಜೊತೆ ನಾವಿಲ್ಲ’.');
INSERT INTO "kannada" VALUES(28,56,'28:56','(ಪ್ರವಾದಿಯರೇ,) ನೀವಿಷ್ಟಪಟ್ಟವರನ್ನು ಸನ್ಮಾರ್ಗಕ್ಕೆ ಸೇರಿಸಲಾರಿರಿ. ಆದರೆ ಅಲ್ಲಾಹನು ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ಸನ್ಮಾರ್ಗಕ್ಕೆ ಸೇರಿಸುತ್ತಾನೆ . ಸನ್ಮಾರ್ಗ ಪ್ರಾಪ್ತರನ್ನು ಅವನು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(28,57,'28:57','“ನಾವು ನಿಮ್ಮೊಂದಿಗೆ ಈ ಸನ್ಮಾರ್ಗವನ್ನು ಅನು ಸರಿಸಿದರೆ, ನಾವು ನಮ್ಮ ನಾಡಿನಿಂದ ಅಪಹೃತರಾ ಗುತ್ತೇವೆ” ಎಂದು ಅವರು ಹೇಳುತ್ತಾರೆ. (ಅಲ್ಲಾ ಹು ಕೇಳುತ್ತಾನೆ;) ಎಲ್ಲ ತರದ ಹಣ್ಣು ಹಂಪಲುಗಳು ನಮ್ಮ ಕಡೆಯಿಂದ ಜೀವನಾಧಾರವಾಗಿ ಹರಿದು ಬರುವಂತಹ ಒಂದು ನಿರ್ಭಯದ ಹರಮನ್ನು (ಪವಿತ್ರ ಸ್ಥಳವನ್ನು) ನಾವು ಅವರಿಗೆ ಅನುಕೂಲ ಮಾಡಿಕೊಟ್ಟಿಲ್ಲವೇ? ಆದರೆ ಅವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ತಿಳಿಯುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(28,58,'28:58','ತಮಗೆ ಲಭಿಸಿದ ಜೀವನ ವೈಭವಗಳ ಬಗೆಗೆ ಅಹಂಭಾವ ಹೊಂದಿದ್ದ ಎಷ್ಟೋ ನಾಡುಗಳನ್ನು ನಾವು ನಾಶಗೊಳಿಸಿದ್ದೇವೆ. ಅವರ ಆ ನಿವಾಸಗಳು ಪಾಳು ಬಿದ್ದಿರುವುದನ್ನು ನೋಡಿರಿ. ಅವರ ನಂತರ ಅಪರೂಪದಲ್ಲೇ ಹೊರತು ಅಲ್ಲಿ ಜನವಾಸವಿರಲಿಲ್ಲ. ಕೊನೆಗೆ ನಾವೇ ಅದರ ವಾರೀಸುದಾರರಾದೆವು .');
INSERT INTO "kannada" VALUES(28,59,'28:59','ತಮ್ಮ ಪ್ರಭು, ನಾಡುಗಳ ಕೇಂದ್ರದಲ್ಲಿ, ಅವರಿಗೆ ನಮ್ಮ ಸೂಕ್ತಗಳನ್ನು ಓದಿ ಹೇಳುವ ಓರ್ವ ದೂತರನ್ನು ನಿಯೋಗಿಸುವವರೆಗೂ ಆ ನಾಡುಗಳನ್ನು ನಾಶಗೊಳಿಸುವವನಾಗಲಿಲ್ಲ. ಊರಿನವರು ಅಕ್ರಮಿಗಳಾಗಿ ಬಿಡುವಾಗಲೇ ಹೊರತು ನಾವು ಅವರನ್ನು ನಾಶಗೊಳಿಸುವವರಾಗಲಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(28,60,'28:60','ಸುಖ ಸವಲತ್ತುಗಳಾಗಿ ನಿಮಗೆ ಏನಾದರೂ ಕೊಡಲಾಗಿದ್ದರೆ ಅದು ಕೇವಲ ಲೌಕಿಕ ಜೀವನದ ಭೋಗ ಸಾಧನ ಹಾಗೂ ಅದರ ಅಲಂಕಾರವಾ ಗಿದೆ. ಆದರೆ ಅಲ್ಲಾಹನ ಬಳಿಯಲ್ಲಿರುವುದೇ ಇದಕ್ಕಿಂತ ಉತ್ತಮ ಹಾಗೂ ಅನಶ್ವರ. ನೀವು ಚಿಂತಿಸುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(28,61,'28:61','ಯಾರಿಗೆ ನಾವು ಉತ್ತಮ ವಾಗ್ದಾನ ಮಾಡಿದ್ದು, ಆಮೇಲೆ ಅದನ್ನವನು ಪಡೆದನೋ, ಅವನು, ನಾವು ಕೇವಲ ಲೌಕಿಕ ಜೀವನದ ಸುಖಭೋಗಗಳ ಅನುಭವ ನೀಡಿದ್ದು ಆ ಬಳಿಕ ಪರಲೋಕದಲ್ಲಿ ನರಕ ಶಿಕ್ಷೆಗಾಗಿ ಹಾಜರುಗೊಳಿಸಲ್ಪಡುವ ವ್ಯಕ್ತಿಯಂತೆ ಆಗಬಲ್ಲನೇ?');
INSERT INTO "kannada" VALUES(28,62,'28:62','ಅಲ್ಲಾಹು ಅವರನ್ನು ಕೂಗಿ ಕರೆದು, “ನೀವು ಕುತರ್ಕ ಮಾಡುತ್ತಿದ್ದ ನನ್ನ ಸಹಭಾಗಿಗಳು ಎಲ್ಲಿದ್ದಾರೆ?” ಎಂದು ಕೇಳುವ ಆ ದಿನವನ್ನು ನೆನಪಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(28,63,'28:63','ಅಂದು ಯಾರ ಮೇಲೆ (ಶಿಕ್ಷೆಯ) ಮಾತು ನಿಜವಾಗುವುದೋ ಅವರು, “ನಮ್ಮ ಪ್ರಭೂ, ಇವರು ನಾವು ದಾರಿಗೆಡಿಸಿದ ಜನರು. ನಾವು ಸ್ವತಃ ದಾರಿಗೆಟ್ಟಂತೆಯೇ ಇವರನ್ನು ನಾವು ದಾರಿಗೆಡಿಸಿದ್ದು ನಿಜ. ಆದರೆ ನಾವು ನಿನ್ನ ಮುಂದೆ ಅವರಿಗೆ ಸಂಬಂಧಿಸಿದ ಜವಾಬ್ದಾರಿಯನ್ನು ಬಿಟ್ಟು ಕೊಡುತ್ತಿದ್ದೇವೆ. ಇವರು ನಮ್ಮನ್ನು ಆರಾಧಿಸುತ್ತಿರಲಿಲ್ಲ”ಎಂದು ಹೇಳುವರು.');
INSERT INTO "kannada" VALUES(28,64,'28:64','ಇವರೊಡನೆ ‘ನಿಮ್ಮ ಸಹಭಾಗಿಗಳನ್ನು ಕರೆಯಿರಿ’ ಎನ್ನಲಾಗುವುದು. ಆಗ ಇವರು ಅವರನ್ನು ಕರೆದು ನೋಡುವರು. ಆಗ ಅವರು ಇವರಿಗೆ ಉತ್ತರ ಕೊಡಲಾರರು . ಪರಂತು ಇವರು ಶಿಕ್ಷೆಯನ್ನು ಕಣ್ಣಾರೆ ಕಾಣುವರು. ಇವರು ಸನ್ಮಾರ್ಗ ಪ್ರಾಪ್ತರಾಗಿದ್ದರೆ ಎಷ್ಟು ಚೆನ್ನಾಗಿತ್ತು!');
INSERT INTO "kannada" VALUES(28,65,'28:65','ಅಲ್ಲಾಹು ಇವರನ್ನು ಕರೆದು, ಆಮೇಲೆ ‘ಸಂದೇಶ ವಾಹಕರಿಗೆ ನೀವೇನು ಉತ್ತರ ಕೊಟ್ಟಿದ್ದೀರಿ?’ ಎಂದು ಕೇಳುವ ದಿನವನ್ನು ನೆನೆಯಿರಿ.');
INSERT INTO "kannada" VALUES(28,66,'28:66','ಅಂದು (ಉತ್ತರಿಸಿ ಪಾರಾಗುವ) ಸಮಾಚಾರಗಳು ಇವರಿಗೆ ಕುರುಡಾಗಿ ಬಿಡುವವು. ತನ್ನಿಮಿತ್ತ ಇವರು ಪರಸ್ಪರ (ಆ ಬಗ್ಗೆ) ಕೇಳಲಾರರು.');
INSERT INTO "kannada" VALUES(28,67,'28:67','ಆದರೆ ಯಾರು ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು ಮರಳಿ ಸತ್ಯವಿಶ್ವಾಸ ಸ್ವೀಕರಿಸಿ ಸತ್ಕರ್ಮವೆಸಗುತ್ತಾರೋ ಅವನು ವಿಜಯಿಗಳಲ್ಲಾಗಬಹುದು.');
INSERT INTO "kannada" VALUES(28,68,'28:68','ನಿಮ್ಮ ಪ್ರಭು ತಾನಿಚ್ಛಿಸಿದ್ದನ್ನು ಸೃಷ್ಟಿಸುತ್ತಾನೆ, ತಾನಿಚ್ಛಿಸಿದ್ದನ್ನು ಆರಿಸಿಕೊಳ್ಳುತ್ತಾನೆ. ಆಯ್ಕೆ ಮಾಡುವ ಅಧಿಕಾರ ಇವರಿಗಿಲ್ಲ . ಅಲ್ಲಾಹು ಪರಮ ಪಾವನನು. ಇವರು ಪಾಲುಗೊಳಿಸುತ್ತಿರುವ ದೇವ ಸಹಭಾಗಿತ್ವದಿಂದ ಮುಕ್ತನಾದ ಪರಮೋನ್ನತನು.');
INSERT INTO "kannada" VALUES(28,69,'28:69','ತಮ್ಮ ಪ್ರಭು, ಇವರು ತಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ಬಚ್ಚಿಟ್ಟಿರುವುದನ್ನೂ ಪ್ರಕಟಗೊಳಿಸುವುದನ್ನೂ ತಿಳಿಯುವನು.');
INSERT INTO "kannada" VALUES(28,70,'28:70','ಅವನೇ ಅಲ್ಲಾಹು, ಅವನ ಹೊರತು ಆರಾಧ್ಯನಿಲ್ಲ. ಇಹದಲ್ಲೂ - ಪರದಲ್ಲೂ ಸಕಲ ಸ್ತುತಿಯು ಅವನಿಗೇ ಮೀಸಲು. ಆಜ್ಞಾಧಿಕಾರವೂ ಅವನದ್ದು. ನೀವು ಅವನ ಕಡೆಗೇ ಮರಳಿ ಸಲ್ಪಡುವಿರಿ.');
INSERT INTO "kannada" VALUES(28,71,'28:71','(ಪ್ರವಾದಿವರ್ಯರೇ,) ಹೇಳಿರಿ, ಅಲ್ಲಾಹನು ಅಂತ್ಯ ದಿನದವರೆಗೂ ನಿಮ್ಮ ಮೇಲೆ ಶಾಶ್ವತವಾಗಿ ರಾತ್ರಿಯನ್ನೇ ನೆಲೆನಿಲ್ಲಿಸುತ್ತಿದ್ದರೆ ನಿಮಗೆ ಪ್ರಕಾಶವನ್ನು ತಂದು ಕೊಡಲು ಅಲ್ಲಾಹನ ಹೊರತು ಬೇರಾವ ದೇವನು ಇದ್ದಾನೆಂದು ನೀವು ಹೇಳಿಕೊಡುವಿರಾ ? ಹೀಗಿದ್ದು ನೀವಿದನ್ನು ಕೇಳಿ ಅರ್ಥ ಮಾಡುವುದಿಲ್ಲವೇ? ');
INSERT INTO "kannada" VALUES(28,72,'28:72','ಹೇಳಿರಿ, ಅಲ್ಲಾಹನು ನಿಮ್ಮ ಮೇಲೆ ಅಂತ್ಯ ದಿನದವರೆಗೆ ಶಾಶ್ವತವಾಗಿ ಹಗಲನ್ನೇ ನೆಲೆನಿಲ್ಲಿಸುತ್ತಿದ್ದರೆ ನೀವು ಶಾಂತರಾಗುವ ರಾತ್ರಿಯನ್ನು ನಿಮಗೆ ತಂದು ಕೊಡಲು ಅಲ್ಲಾಹನ ಹೊರತು ಬೇರಾವ ದೇವನು ಇದ್ದಾನೆಂದು ನೀವೊಮ್ಮೆ ಹೇಳಿಕೊಡುವಿರಾ? ಹೀಗಿದ್ದು ನೀವಿದನ್ನು ಕಂಡರಿಯುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(28,73,'28:73','ಅಲ್ಲಾಹನ ಕಾರುಣ್ಯದಿಂದ ಅವನು ನಿಮಗೆ ರಾತ್ರಿಯನ್ನೂ, ಹಗಲನ್ನೂ ಉಂಟುಮಾಡಿರುವನು. ನೀವು ರಾತ್ರಿ ಕಾಲದಲ್ಲಿ ವಿಶ್ರಾಂತಿ ಪಡೆಯಲಿಕ್ಕೂ, ಹಗಲು ಹೊತ್ತಿನಲ್ಲಿ ಅವನ ಅನುಗ್ರಹವನ್ನು ಅರಸಲಿಕ್ಕೂ, ನೀವು ಕೃತಜ್ಞರಾಗಲಿಕ್ಕೂ .');
INSERT INTO "kannada" VALUES(28,74,'28:74','ಅಲ್ಲಾಹು ಅವರನ್ನು ಕರೆದು, ‘ನೀವು ದುರ್ವಾದ ಹೂಡುತ್ತಿದ್ದ ನನ್ನ ಸಹಭಾಗಿಗಳು ಎಲ್ಲಿದ್ದಾರೆ? ಎಂದು ಕೇಳುವ ಆ ದಿನವನ್ನು ನೆನಪಿಡಿರಿ.');
INSERT INTO "kannada" VALUES(28,75,'28:75','ನಾವು ಪ್ರತಿಯೊಂದು ಸಮುದಾಯದಿಂದ ಒಬ್ಬ ಸಾಕ್ಷಿಯನ್ನು ಹೊರತರುತ್ತೇವೆ. ಆಮೇಲೆ ನಿಮ್ಮ ಪುರಾವೆ ತನ್ನಿರಿ ಎಂದು (ಸತ್ಯ ನಿಷೇಧಿಗಳಲ್ಲಿ) ಹೇಳುತ್ತೇವೆ. ಆಗ (ದೇವತ್ವದ) ಹಕ್ಕು ಅಲ್ಲಾಹನಿಗೆಂದು ಅವರಿಗೆ ತಿಳಿದು ಬರುವುದು. ಅವರು ಸೃಷ್ಟಿಸಿಕೊಂಡಿರುವ ಎಲ್ಲ ಮಿಥ್ಯಗಳೂ ಅವರನ್ನು ಬಿಟ್ಟು ಮಾಯವಾಗುವುದು.');
INSERT INTO "kannada" VALUES(28,76,'28:76','ಖಾರೂನನು, ಮೂಸಾರ ಜನಾಂಗಕ್ಕೆ ಸೇರಿದ ಒಬ್ಬ ವ್ಯಕ್ತಿಯಾಗಿದ್ದನು. ನಂತರ ಅವನು ತನ್ನ ಜನಾಂಗದ ಮೇಲೆ ದಿಕ್ಕಾರ ತೋರಿದನು. ನಾವು ಅವನಿಗೆ ದಾರಾಳ ಧನ ಭಂಡಾರಗಳನ್ನು ನೀಡಿದೆವು. ಅದರ ಬೀಗದ ಕೈಗಳೇ ಬಲಶಾಲಿಗಳ ಒಂದು ತಂಡಕ್ಕೆ ಭಾರವಾಗುವಷ್ಟಿದ್ದುವು. ಒಮ್ಮೆ ಅವನ ಜನಾಂಗದವರು ಅವನೊಡನೆ ಹೇಳಿದರು, ‘ಹಿರಿಹಿಗ್ಗದಿರು. ಹಿರಿಹಿಗ್ಗುವವರನ್ನು ಅಲ್ಲಾಹು ಮೆಚ್ಚುವುದಿಲ್ಲ’.');
INSERT INTO "kannada" VALUES(28,77,'28:77','ಅಲ್ಲಾಹನು ನಿನಗೆ ನೀಡಿರುವ ಸಂಪತ್ತಿನಿಂದ ನೀನು ಪರಲೋಕ ಗುಣವನ್ನು ಹುಡುಕು. ಇಹ ಲೋಕದಿಂದಿರುವ ನಿನ್ನ ಪಾಲನ್ನು ಮರೆಯದಿರು . ಅಲ್ಲಾಹು ನಿನಗೆ ಒಳಿತು ಮಾಡಿದಂತೆ ನೀನೂ (ಇತರರಿಗೆ) ಒಳಿತು ಮಾಡು. ಭೂಮಿಯಲ್ಲಿ ಕ್ಷೋಭೆ ಸೃಷ್ಟಿಸಲು ಹೆಣಗಬೇಡ. ಅಲ್ಲಾಹು ಕ್ಷೋಭೆ ಸೃಷ್ಟಿಸುವವರನ್ನು ಮೆಚ್ಚುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(28,78,'28:78','ಅದಕ್ಕೆ ಅವನು, ‘ಇದು ನನ್ನ ಬಳಿಯಿರುವ ಜ್ಞಾನ ದಿಂದಾಗಿ ನನಗೆ ಒದಗಿದೆ’ ಎಂದನು. ಇದಕ್ಕಿಂತ ಮುಂಚೆ ಅವನಿಗಿಂತಲೂ ಹೆಚ್ಚು ಶಕ್ತಿ ಹಾಗೂ ಜನಬಲವನ್ನು ಹೊಂದಿದ್ದ ತಲೆಮಾರುಗಳನ್ನು ಅಲ್ಲಾಹನು ನಾಶಗೊಳಿಸಿದ್ದನ್ನು ಅವನು ತಿಳಿದಿಲ್ಲವೇ? ಅಪರಾಧಿಗಳನ್ನು (ಶಿಕ್ಷಿಸಲು) ಅವರ ಪಾಪಗಳ ಕುರಿತು ಕೇಳಿ ತಿಳಿಯಬೇಕಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(28,79,'28:79','ಹಾಗೆ ಅವನು ತನ್ನ ಜನಾಂಗದ ಮುಂದೆ ತನ್ನ ಸೊಬಗಿನೊಂದಿಗೆ ಹೊರಟನು. ಲೌಕಿಕ ಜೀವನ ವನ್ನು ಬಯಸುವವರು `ಹಾ, ಖಾರೂನನಿಗೆ ದೊರೆತದ್ದು ನಮಗೂ ಸಿಗುತ್ತಿದ್ದರೆ ಎಷ್ಟು ಚೆನ್ನಾಗಿತ್ತು! ಇವನು ಮಹಾ ಭಾಗ್ಯಶಾಲಿ ಎಂದರು.');
INSERT INTO "kannada" VALUES(28,80,'28:80','ಆದರೆ ಜ್ಞಾನವಿದ್ದವರು, “(ಹಾಗೆ ಹೇಳದಿರಿ). ಅಯ್ಯೋ ನಿಮ್ಮ ನಾಶವೇ! ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಸ್ವೀಕರಿಸಿ, ಸತ್ಕರ್ಮವೆಸಗಿದವರಿಗೆ ಸಿಗುವ ಅಲ್ಲಾಹನ ಪ್ರತಿಫಲವೇ ಉತ್ತಮ. ಕ್ಷಮಾ ಶೀಲರಿಗಲ್ಲದೆ ಬೇರಾರಿಗೂ ಅದನ್ನು ಕೊಡಲಾಗುವುದಿಲ್ಲ” ಎಂದು ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(28,81,'28:81','ಕೊನೆಗೆ ನಾವು ಅವನನ್ನೂ ಅವನ ಮನೆಯನ್ನೂ ಭೂಮಿಯ ಆಳಕ್ಕೆ ಇಳಿಸಿದೆವು. ಆಗ ಅಲ್ಲಾಹ ನನ್ನು ಬಿಟ್ಟು ಅವನ ನೆರವಿಗೆ ಬರಲು ಅವನಿಗೆ ಯಾವ ತಂಡವೂ ಇರಲಿಲ್ಲ. ಸ್ವಯಂ ರಕ್ಷಣೆ ಪಡೆಯಲಿಕ್ಕೂ ಅವನಿಂದಾಗಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(28,82,'28:82','ನಿನ್ನೆಯವರೆಗೆ ಅವನ ಸ್ಥಾನಮಾನಕ್ಕೆ ಆಶೆಪಡು ತ್ತಿದ್ದವರು ಇಂದು ಬೆಳಿಗ್ಗೆ, “ಅಯ್ಯೋ! ಅಲ್ಲಾಹು ತನ್ನ ದಾಸರ ಪೈಕಿ ತಾನಿಚ್ಚಿಸಿದವರಿಗೆ ಆಹಾರ ವನ್ನು ಸಮೃದ್ಧವಾಗಿ ನೀಡುತ್ತಾನೆ. ಅದನ್ನು ಪರಿ ಮಿತವಾಗಿಯೂ ಕೊಡುತ್ತಾನೆ. ಅಲ್ಲಾಹು ನಮ್ಮ ಮೇಲೆ ದಯೆ ತೋರದಿರುತ್ತಿದ್ದರೆ ನಮ್ಮನ್ನೂ ಪಾತಾಳಕ್ಕೆ ಇಳಿಸಿಬಿಡುತ್ತಿದ್ದನು. ಅಯ್ಯೋ, ಸತ್ಯನಿಷೇಧಿಗಳು ಜಯಿಸುವುದಿಲ್ಲ” ಎಂದರು.');
INSERT INTO "kannada" VALUES(28,83,'28:83','ಪರಲೋಕದ ಭವನವನ್ನು ಭೂಮಿಯಲ್ಲಿ ಮೇಲ್ಮೆಯನ್ನಾಗಲಿ, ಗೊಂದಲವನ್ನಾಗಲಿ ಬಯಸದವರಿಗಾಗಿ ನಾವು ಕಾದಿರಿಸಿದ್ದೇವೆ. ಅಂತಿಮ ವಿಜಯವು ಧರ್ಮನಿಷ್ಠರದ್ದು.');
INSERT INTO "kannada" VALUES(28,84,'28:84','ಯಾರು ಒಳಿತಿನೊಂದಿಗೆ ಬರುತ್ತಾನೋ ಅವನಿಗೆ ಅವನು ಮಾಡಿದುದಕ್ಕಿಂತಲೂ ಉತ್ತಮ ಪ್ರತಿಫಲವಿದೆ. ಇನ್ನು ಯಾರು ಕೆಡುಕಿನೊಂದಿಗೆ ಬರುತ್ತಾನೋ ಕೆಡುಕುಗಳನ್ನು ಮಾಡಿದವರಿಗೆ, ಅವರು ಎಸಗುತ್ತಿದ್ದುದರ ಪ್ರತಿಫಲವನ್ನಲ್ಲದೆ ಹೆಚ್ಚಿಗೆ ಕೊಡಲಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(28,85,'28:85','(ಪೈಗಂಬರರೇ,) ತಮ್ಮ ಮೇಲೆ ಈ ಖುರ್‍ಆ ನನ್ನು ಕಡ್ಡಾಯಗೊಳಿಸಿದವನು ತಮ್ಮನ್ನು ಖಂಡಿತ ಒಂದು ನಿರ್ಗಮನ ಸ್ಥಾನಕ್ಕೆ ಮರಳಿಸಲಿರುವನು. ತಾವು ಹೇಳಿರಿ, ‘ಸನ್ಮಾರ್ಗದೊಂದಿಗೆ ಬಂದವನು ಯಾರೆಂದೂ ಸುವ್ಯಕ್ತ ದುರ್ಮಾರ್ಗದಲ್ಲಿ ಇರುವವನು ಯಾರೆಂದೂ ನನ್ನ ಪ್ರಭು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು’.');
INSERT INTO "kannada" VALUES(28,86,'28:86','(ಪೈಗಂಬರರೇ) ನಿಮಗೆ ವೇದಗ್ರಂಥವನ್ನು ಕೊಡಲಾಗುವುದೆಂದು ನೀವು ಖಂಡಿತ ಆಗ್ರಹಿಸಿರಲಿಲ್ಲ. ಆದರೆ ಕೇವಲ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕೃಪೆಯಿಂದ ಇದು ನಿಮಗೆ ಬಂದಿದೆ. ಆದುದರಿಂದ ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಖಂಡಿತ ಸಹಾಯಕ ರಾಗದಿರಿ.');
INSERT INTO "kannada" VALUES(28,87,'28:87','ಅಲ್ಲಾಹನ ಸೂಕ್ತಗಳು ನಿಮ್ಮ ಮೇಲೆ ಅವತೀರ್ಣಗೊಂಡ ನಂತರ ಸತ್ಯನಿಷೇಧಿಗಳು ನಿಮ್ಮನ್ನು ಅವುಗಳಿಂದ ತಡೆಯದಿರಲಿ. ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಗೆ ಜನರನ್ನು ಅಹ್ವಾನಿಸಿರಿ . ಬಹುದೇವವಿಶ್ವಾಸಿಗಳಲ್ಲಿ ನೀವು ಸೇರಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(28,88,'28:88','ಅಲ್ಲಾಹನೊಂದಿಗೆ ಬೇರೆ ಯಾವುದೇ ದೇವರನ್ನು ಆರಾಧಿಸಬೇಡಿರಿ. ಅವನ ಹೊರತು ಬೇರಾವ ಆರಾಧ್ಯನೂ ಇಲ್ಲ. ಅವನ ಸತ್ತೆಯ ಹೊರತು ಎಲ್ಲ ವಸ್ತುಗಳೂ ನಾಶ ಹೊಂದಲಿವೆ. ಆಜ್ಞಾಧಿಕಾರ ಅವನಿಗೇ ಮೀಸಲು. ನೀವೆಲ್ಲರೂ ಅವನ ಕಡೆಗೇ ಮರಳಲಿರುವಿರಿ .');
INSERT INTO "kannada" VALUES(29,1,'29:1','ಅಲಿಫ್ ಲಾಮ್ ಮೀಮ್.');
INSERT INTO "kannada" VALUES(29,2,'29:2','`ನಾವು ವಿಶ್ವಾಸವಿರಿಸಿದ್ದೇವೆ’ ಎಂದ ಮಾತ್ರಕ್ಕೆ ಅವರನ್ನು ಪರೀಕ್ಷೆಗೊಳಪಡಿಸದೆ ಬಿಟ್ಟು ಬಿಡಲಾಗುವುದೆಂದು ಜನರು ಭಾವಿಸಿದ್ದಾರೆಯೇ?');
INSERT INTO "kannada" VALUES(29,3,'29:3','ಸತ್ಯವಂತರು ಯಾರು ಎಂದು ಅಲ್ಲಾಹನು ಕಂಡರಿತೇ ತೀರುವಂತೆಯೂ ಸುಳ್ಳುಗಾರರು ಯಾರು ಎಂದು ಕಂಡರಿತೇ ತೀರುವಂತೆಯೂ ಇವರಿಗೆ ಮುಂಚಿನವರನ್ನು ನಾವು ನಿಜವಾಗಿಯೂ ಪರೀಕ್ಷೆಗೆ ಒಡ್ಡಿದ್ದೇವೆ. ');
INSERT INTO "kannada" VALUES(29,4,'29:4','ಅಥವಾ ತಪ್ಪು ಮಾಡುವವರು ನಮ್ಮ ಹಿಡಿತಕ್ಕೆ ಸಿಗದೆ ಮೀರಿ ಮುಂದೆ ಹೋಗುವರೆಂದು ಭಾವಿಸಿದ್ದಾರೆಯೇ? ಅವರು ಕೈಗೊಂಡ ತೀರ್ಮಾನವು ಅತ್ಯಂತ ಕೆಟ್ಟದ್ದು.');
INSERT INTO "kannada" VALUES(29,5,'29:5','ಯಾರು ಅಲ್ಲಾಹನ ಮುಖಾಮುಖಿಯನ್ನು ನಿರೀಕ್ಷಿಸುತ್ತಾರೆ, (ಅವನು ಅದರ ಸಿದ್ಧತೆಯನ್ನು ಮಾಡಿಕೊಳ್ಳಲಿ). ಅಲ್ಲಾಹು ನಿಗದಿಗೊಳಿಸಿದ ಸಮಯವು ಖಂಡಿತ ಬರಲಿದೆ. ಅವನು ಸರ್ವಶ್ರುತನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(29,6,'29:6','ಯಾರು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಹೋರಾಟ ನಡೆಸುತ್ತಾನೆ, ಅವನು ತನ್ನ ಹಿತಕ್ಕಾಗಿಯೇ ಹೋರಾಡುವನು . ನಿಶ್ಚಯ, ಅಲ್ಲಾಹು ಸಕಲ ಲೋಕದವರಿಂದ ನಿರಪೇಕ್ಷನು.');
INSERT INTO "kannada" VALUES(29,7,'29:7','ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿ ಸತ್ಕರ್ಮವೆಸಗುವವರ ಪಾಪಗಳನ್ನು ನಾವು ಅವರಿಂದ ತೆರೆವುಗೊಳಿಸುತ್ತೇವೆ. ಮತ್ತು ಅವರು ಮಾಡುತ್ತಿದ್ದ ಒಳಿತಿಗೆ ತಕ್ಕುದಾದ ಸತ್ಫಲವನ್ನು ನೀಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(29,8,'29:8','ತನ್ನ ಮಾತಾಪಿತರಿಗೆ ಒಳಿತು ಮಾಡುವಂತೆ ನಾವು ಮಾನವನಿಗೆ ಆದೇಶಿಸಿದ್ದೇವೆ. ಆದರೆ ನೀನು ಅರಿಯದಿರುವ ಒಂದು ವಸ್ತುವನ್ನು ನನಗೆ ಪಾಲು ಗೊಳಿಸಬೇಕೆಂದು ಅವರು ನಿನ್ನ ಮೇಲೆ ಒತ್ತಡ ಹೇರಿದರೆ, ಅವರನ್ನು ಅನುಸರಿಸಬೇಡ . ನಿಮ್ಮ ಮರಳುವಿಕೆ ನನ್ನ ಕಡೆಗೇ ಇದೆ. ಆಗ ನೀವು ಎಸಗಿದ ಕರ್ಮಕ್ಕೆ ನಾನು (ಅಲ್ಲಿ) ಪ್ರತಿಫಲ ನೀಡುವೆನು.');
INSERT INTO "kannada" VALUES(29,9,'29:9','ಸತ್ಯವಿಶ್ವಾಸ ತಾಳಿ ಸತ್ಕರ್ಮವೆಸಗಿದವರನ್ನು ನಾವು ಖಂಡಿತ ಸಜ್ಜನರ ಸಾಲಿಗೆ ಸೇರಿಸುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(29,10,'29:10','ನಾವು ಅಲ್ಲಾಹನಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸಿದ್ದೇವೆ ಎಂದು ಹೇಳುವ ಕೆಲವರು ಜನರಲ್ಲಿದ್ದಾರೆ. ಆದರೆ ಅವರು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಪೀಡಿಸಲ್ಪಟ್ಟಾಗ ಜನರ ಪೀಡೆಯನ್ನು ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆಯಂತೆ ಗಣಿಸುತ್ತಾರೆ. ತಮ್ಮ ಪ್ರಭುವಿನಿಂದ ಏನಾದರೂ ಸಹಾಯ ಬಂದರೆ, ‘ಖಂಡಿತ ನಾವು ನಿಮ್ಮ ಜೊತೆಗಿದ್ದೆವು’ ಎಂದು ಹೇಳುತ್ತಾರೆ. ಲೋಕವಾಸಿಗಳ ಹೃದಯಗಳಲ್ಲಿರುವುದನ್ನು ಅಲ್ಲಾಹು ಚೆನ್ನಾಗಿ ತಿಳಿದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(29,11,'29:11','ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಯಾರೆಂದು ಅಲ್ಲಾಹನು ಖಂಡಿತ ತಿಳಿಯುವನು. ಕಪಟವಿಶ್ವಾಸಿಗಳು ಯಾರು ಎಂದೂ ಖಂಡಿತ ತಿಳಿಯುವನು.');
INSERT INTO "kannada" VALUES(29,12,'29:12','ಸತ್ಯನಿಷೇಧಿಗಳು ಸತ್ಯವಿಶ್ವಾಸಿಗಳೊಡನೆ, ‘ನೀವು ನಮ್ಮ ಮಾರ್ಗವನ್ನು ಅನುಸರಿಸಿರಿ. ನಿಮ್ಮ ಪಾಪಗಳನ್ನು ನಾವು ಹೊತ್ತುಕೊಳ್ಳುತ್ತೇವೆ’ ಎನ್ನುತ್ತಾರೆ. ವಾಸ್ತವದಲ್ಲಿ ಇವರ ಪಾಪಗಳ ಪೈಕಿ ಏನನ್ನೂ ಅವರು ಹೊತ್ತುಕೊಳ್ಳುವವರಲ್ಲ. ಅವರು ಖಂಡಿತ ಸುಳ್ಳು ಹೇಳುತ್ತಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(29,13,'29:13','ಆದರೆ ಅವರು ತಮ್ಮ ಪಾಪ ಹೊರೆಯನ್ನು ಹೊರುವ ಜೊತೆಗೆ ಇತರ ಅನೇಕ ಪಾಪ ಹೊರೆಗಳನ್ನೂ ಖಂಡಿತ ಹೊರಬೇಕಾಗುವುದು . ಅವರು ಕಥೆಕಟ್ಟಿ ಹೇಳುತ್ತಿದ್ದ ಸುಳ್ಳುಗಳ ಬಗ್ಗೆ ಅಂತ್ಯದಿನ ವಿಚಾರಣೆ ನಡೆಸಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(29,14,'29:14','ನೂಹರನ್ನು ಅವರ ಜನಾಂಗದ ಕಡೆಗೆ ನಾವು ಕಳುಹಿಸಿದೆವು. ಅವರು ಅವರ ನಡುವೆ ಐವತ್ತು ಕಡಿಮೆ ಒಂದು ಸಾವಿರ ವರ್ಷಗಳ ಕಾಲ ನೆಲೆಸಿದರು. (ಆದರೂ ಅವರು ನೇರ ದಾರಿಗೆ ಬರಲಿಲ್ಲ). ಕೊನೆಗೆ ಆ ಜನರು ಅಕ್ರಮಿಗಳಾಗಿದ್ದ ಸ್ಥಿತಿಯಲ್ಲಿ ಅವರನ್ನು ಜಲಪ್ರಳಯ ಹಿಡಿದುಬಿಟ್ಟಿತು.');
INSERT INTO "kannada" VALUES(29,15,'29:15','ಆಗ ನೂಹರನ್ನೂ ಅವರ ಜೊತೆಗೆ ನಾವೆಯಲ್ಲಿದ್ದವರನ್ನೂ ನಾವು ಕಾಪಾಡಿದೆವು ಮತ್ತು ಲೋಕದ ಜನತೆಗೆ ಅದನ್ನೊಂದು ದೃಷ್ಟಾಂತವನ್ನಾಗಿ ಮಾಡಿದೆವು.');
INSERT INTO "kannada" VALUES(29,16,'29:16','ಇಬ್‍ರಾಹೀಮರನ್ನು ನೆನಪಿಸಿರಿ. ಒಮ್ಮೆ ಅವರು ತಮ್ಮ ಜನಾಂಗದೊಡನೆ ಹೀಗೆ ಹೇಳಿದ್ದರು. ‘ನೀವು ಅಲ್ಲಾಹನಿಗೆ ಆರಾಧನೆ ಮಾಡಿರಿ ಮತ್ತು ಅವನನ್ನು ಭಯಪಡಿರಿ. ನೀವು ಸತ್ಯವನ್ನು ಅರಿಯುವವರಾಗಿದ್ದರೆ ಇದು ನಿಮಗೆ ಉತ್ತಮ ವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(29,17,'29:17','ನೀವು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಕೇವಲ ವಿಗ್ರಹಗಳನ್ನು ಪೂಜಿಸುತ್ತೀರಿ. (ಅವು ಅಲ್ಲಾಹನ ಪಾಲುದಾರರೆಂದು)ಒಂದು ಸುಳ್ಳನ್ನೂ ಸೃಷ್ಟಿಸುತ್ತಿರುವಿರಿ. ನಿಜದಲ್ಲಿ ಅಲ್ಲಾಹನ ಹೊರತು ನೀವು ಪೂಜಿ ಸುತ್ತಿರುವವರು ನಿಮಗೆ ಯಾವುದೇ ಜೀವನಾಧಾರವನ್ನು ಒಡೆತನಗೊಳಿಸಿಲ್ಲ. ಆದ್ದರಿಂದ ಅಲ್ಲಾಹನ ಬಳಿ ಉಪಜೀವನವನ್ನು ಯಾಚಿಸಿರಿ. ಅವನನ್ನೇ ಆರಾಧಿಸಿರಿ. ಅವನಿಗೆ ಕೃತಜ್ಞತೆ ಸಲ್ಲಿಸಿರಿ. ಅವನ ಕಡೆಗೇ ನೀವು ಮರಳಿಸಲ್ಪಡುವಿರಿ .');
INSERT INTO "kannada" VALUES(29,18,'29:18','ನೀವು ಸುಳ್ಳಾಗಿಸುವಿರಾದರೆ ಅದರಲ್ಲಿ ಅಚ್ಚರಿಯಿಲ್ಲ. ನಿಮಗಿಂತ ಮುಂಚೆ ಅನೇಕ ಜನಾಂಗಗಳು ಸುಳ್ಳಾಗಿಸಿವೆ. ಸ್ಪಷ್ಟವಾಗಿ ಸಂದೇಶವನ್ನು ತಲಪಿಸಿಕೊಡುವುದು ಮಾತ್ರವೇ ಸಂದೇಶವಾಹಕರ ಹೊಣೆ.');
INSERT INTO "kannada" VALUES(29,19,'29:19','ಅಲ್ಲಾಹನು ಸೃಷ್ಟಿಯನ್ನು ಆರಂಭಿಸುವುದು, ಅನಂತರ ಅದರ ಪುನರಾವರ್ತನೆ ಮಾಡುವುದು ಹೇಗೆಂದು ಇವರು ನೋಡಲಿಲ್ಲವೇ? ನಿಶ್ಚಯ ಇದು ಅಲ್ಲಾಹನ ಮೇಲೆ ಅತಿ ಸುಲಭವಾಗಿದೆ .');
INSERT INTO "kannada" VALUES(29,20,'29:20','ಹೇಳಿರಿ, ನೀವು ಭೂಮಿಯಲ್ಲಿ ಸಂಚರಿಸಿ ಅವನು ಸೃಷ್ಟಿಯ ಆರಂಭ ಹೇಗೆ ಮಾಡಿರುತ್ತಾನೆಂದು ನೋಡಿ ತಿಳಿದುಕೊಳ್ಳಿರಿ. ನಂತರ ಅಲ್ಲಾಹನು ಎರಡನೇ ಬಾರಿಯೂ ಸೃಷ್ಟಿಸುವನು. ನಿಜವಾಗಿ ಅಲ್ಲಾಹು ಸಕಲ ವಸ್ತುವಿನ ಮೇಲೆ ಸಾಮಥ್ರ್ಯವುಳ್ಳವನು.');
INSERT INTO "kannada" VALUES(29,21,'29:21','ಅವನು ತಾನಿಚ್ಛಿಸುವವರಿಗೆ ಶಿಕ್ಷೆ ನೀಡುತ್ತಾನೆ. ತಾನಿಚ್ಛಿಸುವವರಿಗೆ ಕರುಣೆ ತೋರುತ್ತಾನೆ. ನೀವು ಅವನ ಕಡೆಗೆ ಮರಳಿಸಲ್ಪಡುವಿರಿ.');
INSERT INTO "kannada" VALUES(29,22,'29:22','ನೀವು ಭೂಮಿಯಲ್ಲಾಗಲಿ ಆಕಾಶದಲ್ಲಾಗಲಿ ಅವನನ್ನು ಸೋಲಿಸಲಾರಿರಿ. ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಯಾವ ರಕ್ಷಕಮಿತ್ರನೂ ಸಹಾಯಕನೂ ನಿಮಗಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(29,23,'29:23','ಅಲ್ಲಾಹನ ದೃಷ್ಟಾಂತಗಳನ್ನೂ ಅವನ ಭೇಟಿ ಯನ್ನೂ ನಿಷೇಧಿಸಿದವರು ನನ್ನ ಕೃಪೆಯ ಬಗ್ಗೆ ನಿರಾಶರಾಗುವರು . ಅವರಿಗೆ ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆ ಇದೆ.');
INSERT INTO "kannada" VALUES(29,24,'29:24','‘ಇವನನ್ನು ಕೊಂದು ಹಾಕಿರಿ ಇಲ್ಲವೇ ಇವನನ್ನು ಸುಟ್ಟು ಹಾಕಿರಿ’ ಎನ್ನುವ ಹೊರತು ತಮ್ಮ ಜನಾಂಗಕ್ಕೆ ಬೇರೇನೂ ಉತ್ತರವಿರಲಿಲ್ಲ. ಕೊನೆಗೆ ಅಲ್ಲಾಹನು ಅವರನ್ನು ಅಗ್ನಿಯಿಂದ ರಕ್ಷಿಸಿದನು. ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸುವ ಜನರಿಗೆ ಇದರಲ್ಲಿ ಖಂಡಿತ ಅನೇಕ ನಿದರ್ಶನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(29,25,'29:25','ಅವರು ಹೀಗೆಂದರು; ನೀವು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ವಿಗ್ರಹಗಳನ್ನು ಲೌಕಿಕ ಜೀವನದಲ್ಲಿ ಪರಸ್ಪರ ಪ್ರೀತಿ ಬಾಂಧವ್ಯಕ್ಕಾಗಿ ಆರಾಧ್ಯರನ್ನಾಗಿ ಮಾಡಿಕೊಂಡಿರುವಿರಿ . ಆದರೆ ಅಂತ್ಯದಿನ ನೀವು ಪರಸ್ಪರರನ್ನು ನಿರಾಕರಿಸುವಿರಿ ಮತ್ತು ಪರಸ್ಪರರನ್ನು ಶಪಿಸುವಿರಿ. ನೀವೆಲ್ಲರೂ ಮರಳಿ ಹೋಗುವ ನಿವಾಸ ನರಕವಾಗಿರುವುದು. ನಿಮಗೆ ಸಹಾಯಕರಾಗಿ ಯಾರೂ ಇರಲಾರರು.');
INSERT INTO "kannada" VALUES(29,26,'29:26','ಆಗ ಲೂಥರು ಅವರಲ್ಲಿ ವಿಶ್ವಾಸವಿಟ್ಟರು. ಇಬ್ ರಾಹೀಮರು ಹೇಳಿದರು; ‘ನಾನು ನನ್ನ ಪ್ರಭುವಿ ನತ್ತ (ಅವನ್ನು ಆಜ್ಞಾಪಿಸಿದ ಸ್ಥಳಕ್ಕೆ) ಹಿಜ್ರತ್ ಹೋಗುತ್ತೇನೆ. ಅವನು ಮಹಾ ಪ್ರತಾಪಿಯೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುತ್ತಾನೆ’.');
INSERT INTO "kannada" VALUES(29,27,'29:27','ನಾವು ಅವರಿಗೆ ಇಸ್‍ಹಾಖ್ ಮತ್ತು ಯಅïಖೂಬರನ್ನು ದಯಪಾಲಿಸಿದೆವು. ಅವರ ಸಂತತಿಯಲ್ಲಿ ಪ್ರವಾದಿತ್ವವನ್ನೂ ವೇದ ಗ್ರಂಥವನ್ನೂ ಇರಿಸಿದೆವು . ಅವರಿಗೆ ಈ ಲೋಕದಲ್ಲಿ ಅದರ ಪ್ರತಿಫಲವನ್ನು ನೀಡಿದೆವು. ಪರಲೋಕದಲ್ಲಿ ಅವರು ಖಂಡಿತ ಸಜ್ಜನರಲ್ಲಿ ಒಬ್ಬರಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(29,28,'29:28','ಲೂಥರನ್ನು ಸ್ಮರಿಸಿರಿ. ಅವರು ತಮ್ಮ ಜನಾಂಗಕ್ಕೆ ಹೀಗೆ ಹೇಳಿದರು, ‘ನಿಮಗಿಂತ ಮುಂಚೆ ಲೋಕವಾಸಿಗಳಲ್ಲಿ ಯಾರೂ ಮಾಡಿರದ ನೀಚ ಕಾರ್ಯವನ್ನು ನೀವು ಮಾಡುತ್ತಿದ್ದೀರಿ .');
INSERT INTO "kannada" VALUES(29,29,'29:29','ನೀವು (ಕಾಮ ತೀರಿಸಲು) ಪುರುಷರ ಬಳಿಗೆ ಹೋಗುವುದೇ? ದಾರಿಹೋಕರನ್ನು ದರೋಡೆ ಮಾಡುವುದೇ? ನಿಮ್ಮ ಸಭೆ ಸಮಾರಂಭಗಳಲ್ಲಿ ನಿಷಿದ್ಧ ಕಾರ್ಯಕ್ರಮಗಳನ್ನು ಏರ್ಪಡಿಸುವುದೇ? ಆಗ ಅವರ ಜನಾಂಗದ ಉತ್ತರವು ‘ನೀನು ಸತ್ಯವಾದಿಯಾಗಿದ್ದರೆ ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆಯನ್ನು ನಮಗೆ ತಂದು ಕೊಡು’ ಎನ್ನುವ ಹೊರತು ಬೇರೇನೂ ಆಗಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(29,30,'29:30','ಲೂಥರು, ‘ನನ್ನ ಪ್ರಭೂ, ಈ ದುಷ್ಟ ಜನಾಂಗದ ವಿರುದ್ಧ ನನಗೆ ಸಹಾಯ ನೀಡು’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(29,31,'29:31','ನಮ್ಮ ದೂತರು (ಮಲಕ್‍ಗಳು) ಇಬ್‍ರಾಹೀಮರ ಬಳಿ ಸುವಾರ್ತೆಯನ್ನು ತಂದಾಗ ಆ ದೂತರು ‘ನಾವು ಈ ನಾಡಿನ ಜನರನ್ನು ನಾಶಗೊಳಿಸಲು ಬಂದವರು, ಇದರ ನಿವಾಸಿಗಳು ಮಹಾ ಅಕ್ರಮಿಗಳಾಗಿದ್ದಾರೆ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(29,32,'29:32','ಆಗ ಇಬ್‍ರಾಹೀಮರು, ಅಲ್ಲಿ ಲೂಥರಿದ್ದಾರಲ್ಲ! ಎಂದಾಗ ಅವರು, ‘ಅಲ್ಲಿ ಇರುವವರ ಬಗ್ಗೆ ನಾವು ಚೆನ್ನಾಗಿ ಬಲ್ಲೆವು. ನಾವು ಅವರನ್ನೂ ಅವರ ಪತ್ನಿಯ ಹೊರತು ಅವರ ಮನೆಯವರೆಲ್ಲರನ್ನೂ ರಕ್ಷಿಸುವೆವು. ಅವರ ಪತ್ನಿ ನಾಶಕ್ಕೆ ಬಲಿಯಾಗುವವರಲ್ಲಿ ಸೇರಿರುವಳು’ ಎಂದು ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(29,33,'29:33','ನಮ್ಮ ದೂತರು ಲೂಥರ ಬಳಿಗೆ ತಲುಪಿದಾಗ ಅವರ ಬಗ್ಗೆ ಲೂಥರು ಕಳವಳಗೊಂಡರು. ಅವರ ಹೃದಯ ಇಕ್ಕಟ್ಟಾಯಿತು. ಆಗ ಅವರು ಹೇಳಿದರು; ‘ಹೆದರಬೇಡಿರಿ, ವ್ಯಥೆ ಪಡಬೇಡಿರಿ. ನಾಶಕ್ಕೆ ಬಲಿಯಾಗುವವರಲ್ಲಿ ಸೇರಿರುವ ನಿಮ್ಮ ಪತ್ನಿಯ ಹೊರತು ನಿಮ್ಮನ್ನೂ ನಿಮ್ಮ ಮನೆಯ ವರನ್ನೂ ನಾವು ರಕ್ಷಿಸಲಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(29,34,'29:34','ಇವರು ಮಾಡುವ ನೀಚ ಪ್ರವೃತ್ತಿಯ ಕಾರಣ ನಾವು ಈ ನಾಡಿನವರ ಮೇಲೆ ಆಕಾಶದಿಂದ ಇಂದು ಶಿಕ್ಷೆಯನ್ನು ಇಳಿಸುವವರಿದ್ದೇವೆ’');
INSERT INTO "kannada" VALUES(29,35,'29:35','ಯೋಚಿಸುವ ಜನರಿಗಾಗಿ ಒಂದು ಬಹಿರಂಗ ನಿದರ್ಶನವನ್ನು ಅಲ್ಲಿ ನಾವು ಉಳಿಸಿ ಬಿಟ್ಟಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(29,36,'29:36','ಮದ್‍ಯನ್‍ನ ಕಡೆಗೆ ಅವರ ಸಹೋದರ ಶುಐಬರನ್ನು ನಾವು ನಿಯೋಗಿಸಿದೆವು. ಅವರು, ‘ನನ್ನ ಜನಾಂಗದವರೇ, ನೀವು ಅಲ್ಲಾಹನನ್ನು ಆರಾಧಿಸಿರಿ. ಅಂತಿಮ ದಿನವನ್ನು ಹಂಬಲಿಸಿರಿ. ಭೂಮಿಯಲ್ಲಿ ದುಷ್ಟರಾಗಿ ಗೊಂದಲ ನಿರ್ಮಿಸ ಬೇಡಿರಿ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(29,37,'29:37','ಆದರೆ ಅವರು ಇವರನ್ನು ಸುಳ್ಳಾಗಿಸಿದರು. ಕೊನೆಗೆ ಒಂದು ಪ್ರಚಂಡ ಭೂಕಂಪವು ಅವರಿಗೆ ಬಾಧಿಸಿತು. ಆಗ ಅವರು ತಮ್ಮ ಮನೆಗಳಲ್ಲಿ ಮಂಡಿಯೂರಿ ಸತ್ತು ಬಿದ್ದರು.');
INSERT INTO "kannada" VALUES(29,38,'29:38','ಆದ್ ಮತ್ತು ಸಮೂದರನ್ನು ನಾವು ನಾಶ ಮಾಡಿದ್ದೇವೆ. ಅವರ ವಾಸ ಸ್ಥಳಗಳಿಂದಲೇ ನಿಮಗದು ಸ್ಪಷ್ಟವಾಗಿದೆ. ಅವರ ಕರ್ಮಗಳನ್ನು ಶೈತಾನನು ಅವರಿಗೆ ಚಂದಗಾಣಿಸಿ ಕೊಟ್ಟನು. ಹಾಗೆ ಅವರನ್ನು ನೇರ ಮಾರ್ಗದಿಂದ ತಡೆದನು. ನಿಜದಲ್ಲಿ ಅವರು ಯೋಚಿಸಿ ಅರ್ಥ ಮಾಡಿ ಕೊಳ್ಳಲು ಸಮರ್ಥರಾಗಿದ್ದರು. (ಆದರೆ ಹಾಗೆ ಮಾಡಲಿಲ್ಲ).');
INSERT INTO "kannada" VALUES(29,39,'29:39','ಖಾರೂನ್, ಫಿರ್‍ಔನ್ ಹಾಗೂ ಹಾಮಾನರನ್ನೂ ನಾವು ನಾಶಗೊಳಿಸಿದ್ದೇವೆ. ಅವರ ಬಳಿಗೆ ಪ್ರತ್ಯಕ್ಷ ಪ್ರಮಾಣಗಳೊಂದಿಗೆ ಮೂಸಾ ಬಂದಿದ್ದರು. ಆದರೆ ಅವರು ಭೂಮಿಯಲ್ಲಿ ದುರಹಂಕಾರವನ್ನು ಮೆರೆದರು. (ಆದರೆ ನಮ್ಮ ಶಿಕ್ಷೆಯಿಂದ) ಅವರು ಓಡಿ ತಪ್ಪಿಸುವವರಾಗಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(29,40,'29:40','ಆದ್ದರಿಂದ ನಾವು ಅವರೆಲ್ಲರನ್ನೂ ಅವರ ಪಾಪ ಗಳಿಗಾಗಿ ಹಿಡಿದು ಶಿಕ್ಷಿಸಿದೆವು. ನಾವು ಅವರಲ್ಲಿ ಕೆಲವರ ಮೇಲೆ ಸುಂಟರಗಾಳಿಯನ್ನು ಬೀಸಿ ದೆವು. ಕೆಲವರ ಮೇಲೆ ಘೋರ ಅಟ್ಟಹಾಸವು ಬಂದೆರಗಿತು. ಮತ್ತೆ ಕೆಲವರನ್ನು ನಾವು ಭೂಮಿ ಯಲ್ಲಿ ಹುದುಗಿಸಿದೆವು. ಬೇರೆ ಕೆಲವರನ್ನು ನೀರಿನಲ್ಲಿ ಮುಳುಗಿಸಿ ಕೊಂದೆವು. ಅಲ್ಲಾಹು ಅವರ ಮೇಲೆ ಅಕ್ರಮವೆಸಗಿಲ್ಲ. ಆದರೆ ಅವರು ತಮಗೆ ತಾವೇ ಅಕ್ರಮವೆಸಗುವವರಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(29,41,'29:41','ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಬೇರೆ ವಸ್ತುಗಳನ್ನು ಆರಾಧ್ಯರನ್ನಾಗಿ ಮಾಡಿಕೊಂಡವರ ಉದಾಹರಣೆಯು ಬಲೆಯಿಂದ ಮನೆ ಕಟ್ಟುವ ಜೇಡರ ಹುಳುವಿನಂತಿದೆ. ಮನೆಗಳ ಪೈಕಿ ಜೇಡರ ಹುಳುವಿನ ಮನೆಯು ಅತ್ಯಂತ ದುರ್ಬಲ. ಇವರು ಅರಿತವರಾಗಿದ್ದರೆ ಅಲ್ಲಾಹನ ಹೊರತು ಬೇರೆಯವರನ್ನು ದೇವರನ್ನಾಗಿ ಮಾಡುತ್ತಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(29,42,'29:42','ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಅವರು ಆರಾಧಿಸುವ ಯಾವುದೇ ವಸ್ತುವಿರಲಿ, ಅಲ್ಲಾಹು ಅದನ್ನು ಚೆನ್ನಾಗಿ ತಿಳಿದಿರುತ್ತಾನೆ. ಅವನೇ ಪ್ರತಾಪನೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(29,43,'29:43','ಈ ಉಪಮೆಗಳನ್ನು ನಾವು ಜನರಿಗಾಗಿ ನಿರೂಪಿ ಸುತ್ತಿದ್ದೇವೆ. ಆದರೆ, ಬಲ್ಲಿದರಲ್ಲದೆ ಅದನ್ನು ಯೋಚಿಸಿ ಗ್ರಹಿಸಲಾರರು.');
INSERT INTO "kannada" VALUES(29,44,'29:44','ಅಲ್ಲಾಹನು ಭೂಮಿ-ಆಕಾಶಗಳನ್ನು ಸತ್ಯ ಪೂರ್ಣವಾಗಿ ಸೃಷ್ಟಿಸಿದನು. ನಿಜಕ್ಕೂ ಇದರಲ್ಲಿ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ದೊಡ್ಡ ನಿದರ್ಶನವಿದೆ.');
INSERT INTO "kannada" VALUES(29,45,'29:45','(ಪೈಗಂಬರರೇ) ನಿಮ್ಮ ಕಡೆಗೆ ಸಂದೇಶ ನೀಡ ಲಾದ ಖುರ್‍ಆನ್ ಗ್ರಂಥವನ್ನು ಪಠಿಸಿರಿ, ನಮಾಝನ್ನು ಸಂಸ್ಥಾಪಿಸಿರಿ. ನಿಜಕ್ಕೂ ನಮಾಝ್ ದುಷ್ಕøತ್ಯ ಹಾಗೂ ನಿಷಿದ್ಧ ಕರ್ಮಗಳಿಂದ ತಡೆಯುತ್ತದೆ. ಅಲ್ಲಾಹನ ಸ್ಮರಣೆ ಎಲ್ಲಕ್ಕಿಂತಲೂ ದೊಡ್ಡದು . ನೀವು ಮಾಡುತ್ತಿರುವುದನ್ನು ಅಲ್ಲಾಹನು ಅರಿಯುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(29,46,'29:46','ನೀವು ಗ್ರಂಥದವರೊಡನೆ ಅತ್ಯಂತ ಉತ್ತಮ ರೀತಿಯಿಂದಲೇ ಹೊರತು ಸಂವಾದ ನಡೆಸಬೇಡಿರಿ. ಆದರೆ ಅವರ ಪೈಕಿ ಅಕ್ರಮಿಗಳೊಡನೆ ಹೊರತು . ನೀವು ಹೇಳಿರಿ: ‘ನಮ್ಮ ಕಡೆಗೆ ಇಳಿಸಲಾಗಿರುವುದರ ಮೇಲೂ ನಿಮ್ಮ ಕಡೆಗೆ ಇಳಿಸಲಾಗಿದ್ದುದರ ಮೇಲೂ ನಾವು ವಿಶ್ವಾಸವಿರಿಸಿದ್ದೇವೆ. ನಮ್ಮ ಆರಾಧ್ಯನೂ ನಿಮ್ಮ ಆರಾಧ್ಯನೂ ಒಬ್ಬನೇ ಆಗಿದ್ದು ನಾವು ಅವನಿಗೆ ವಿಧೇಯರೂ (ಮುಸ್ಲಿಮರೂ) ಆಗಿರುತ್ತೇವೆ’.');
INSERT INTO "kannada" VALUES(29,47,'29:47','ಇದೇ ರೀತಿಯಲ್ಲಿ ನಿಮ್ಮ ಕಡೆಗೆ ನಾವು ಈ ಗ್ರಂಥವನ್ನು (ಖುರ್‍ಆನ್) ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದೇವೆ. ನಾವು ಯಾರಿಗೆ ಗ್ರಂಥ ನೀಡಿದ್ದೇವೋ ಅವರು (ಅರ್ಥಾತ್ ಗ್ರಂಥವನ್ನು ನಿಜವೆಂದು ಅಂಗೀಕರಿಸಿದವರು) ಇದರ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸುತ್ತಾರೆ. ಇವರಲ್ಲೂ (ಅರಬರಲ್ಲೂ) ಅನೇಕರು ಇದರ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸುತ್ತಾರೆ. ಕೇವಲ ಸತ್ಯನಿಷೇಧಿಗಳು ಮಾತ್ರ ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳನ್ನು ಅಂಧವಾಗಿ ನಿಷೇಧಿಸುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(29,48,'29:48','ಖುರ್‍ಆನ್ ಲಭಿಸುವ ಮುಂಚೆ ನೀವು ಯಾವುದೇ ಗ್ರಂಥ ಪಠಿಸುತ್ತಿರಲಿಲ್ಲ. ನಿಮ್ಮ ಕೈಯಿಂದ ಬರೆಯುತ್ತಲೂ ಇರಲಿಲ್ಲ. ಹಾಗಾಗಿರುತ್ತಿದ್ದರೆ ಮಿಥ್ಯವಾದಿಗಳು ಸಂಶಯಪಡುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(29,49,'29:49','ಆದರೆ ಯಾರಿಗೆ ಜ್ಞಾನ ನೀಡಲ್ಪಟ್ಟಿರುವುದೋ ಅವರ ಹೃದಯಗಳಲ್ಲಿ ಈ ಖುರ್‍ಆನ್ ಪ್ರತ್ಯಕ್ಷ ವಾಗಿರುವ ಪುರಾವೆಗಳಾಗಿವೆ . ಅಕ್ರಮಿಗಳ ಹೊರತು ಇನ್ಯಾರೂ ನಮ್ಮ ಪುರಾವೆಗಳನ್ನು ನಿಷೇಧಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(29,50,'29:50','ಸತ್ಯನಿಷೇಧಿಗಳು ಕೇಳಿದರು, ‘ಇವನ ಪ್ರಭುವಿ ನಿಂದ ಇವನ ಮೇಲೆ ನಿದರ್ಶನಗಳೇಕೆ ಇಳಿಸಲಾಗಿಲ್ಲ?’ ಹೇಳಿರಿ, ‘ನಿದರ್ಶನಗಳು ಕೇವಲ ಅಲ್ಲಾಹನ ಬಳಿ ಇವೆ. ನಾನು ಕೇವಲ ಸ್ಪಷ್ಟವಾದ ಎಚ್ಚರಿಕೆ ನೀಡುವವನು ಮಾತ್ರ’.');
INSERT INTO "kannada" VALUES(29,51,'29:51','ಇವರಿಗೆ ಓದಿ ಹೇಳಲಾಗುತ್ತಿರುವ ಈ ಗ್ರಂಥ ವನ್ನು ನಿಮ್ಮ ಮೇಲೆ ನಾವು ಅವತೀರ್ಣಗೊಳಿಸಿ ಕೊಟ್ಟಿರುವುದು ಇವರಿಗೆ ನಿದರ್ಶನಕ್ಕೆ ಸಾಲದೇ? ನಿಶ್ಚಯವಾಗಿ ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸುವವರಿಗೆ ಇದರಲ್ಲಿ ಕಾರುಣ್ಯ ಹಾಗೂ ಉಪದೇಶವಿದೆ.');
INSERT INTO "kannada" VALUES(29,52,'29:52','ತಾವು ಹೇಳಿರಿ, ‘ನನ್ನ ಮತ್ತು ನಿಮ್ಮ ನಡುವೆ ಸಾಕ್ಷಿಯಾಗಿ ಅಲ್ಲಾಹನೇ ಸಾಕು. ಅವನು ಭೂಮಿ-ಆಕಾಶಗಳಲ್ಲಿರುವ ಎಲ್ಲವನ್ನೂ ಬಲ್ಲವನು. ಮಿಥ್ಯವನ್ನು ನಂಬುವ ಮತ್ತು ಅಲ್ಲಾಹ ನನ್ನು ನಿಷೇಧಿಸುವ ಜನರೇ ನಷ್ಟವಂತರು’.');
INSERT INTO "kannada" VALUES(29,53,'29:53','ಇವರು ತಮ್ಮಲ್ಲಿ ಶಿಕ್ಷೆಗಾಗಿ ದುಡುಕುತ್ತಾರೆ. ಒಂದು ನಿರ್ಧಿಷ್ಟ ಅವಧಿ ಇಲ್ಲದಿರುತ್ತಿದ್ದರೆ ಇವರಿಗೆ ಶಿಕ್ಷೆ ಕೂಡಲೇ ಬಂದು ಬಿಡುತ್ತಿತ್ತು. ಅವರಿಗೆ ಸುಳಿವೇ ಇಲ್ಲದ ರೂಪದಲ್ಲಿ ಆ ಶಿಕ್ಷೆ ಹಠಾತ್ತಾಗಿ ಬಂದೇ ತೀರುವುದು.');
INSERT INTO "kannada" VALUES(29,54,'29:54','ಇವರು ತಮ್ಮಲ್ಲಿ ಶಿಕ್ಷೆಗಾಗಿ ದುಡುಕುತ್ತಾರೆ. ಖಂಡಿತ, ನರಕವು ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ಸುತ್ತು ವರಿದಿದೆ.');
INSERT INTO "kannada" VALUES(29,55,'29:55','ಇವರ ಮೇಲ್ಭಾಗದಿಂದಲೂ ಕಾಲಿನಡಿಯಿಂದಲೂ ಶಿಕ್ಷೆ ಅವರನ್ನು ಆವರಿಸಿಕೊಳ್ಳುವ ದಿನ (ಅದು ಸಂಭವಿಸುವುದು). ‘ನೀವು ಮಾಡುತ್ತಿದ್ದ ಕರ್ಮಗಳ ಫಲವುಣ್ಣಿರಿ’ ಎಂದು ಅಲ್ಲಾಹು ಹೇಳುವನು.');
INSERT INTO "kannada" VALUES(29,56,'29:56','ಸತ್ಯವಿಶ್ವಾಸಿಗಳಾದ ಓ ನನ್ನ ದಾಸರೇ, ಖಂಡಿತ ನನ್ನ ಭೂಮಿ ವಿಶಾಲವಾಗಿದೆ. ಆದುದರಿಂದ (ಎತ್ತ ಹೋದರೂ) ನೀವು ನನ್ನನ್ನೇ ಆರಾಧಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(29,57,'29:57','ಪ್ರತಿಯೊಂದು ಜೀವಿಯೂ ಮರಣದ ರುಚಿಯುಣ್ಣಲಿದೆ. ತರುವಾಯ ನಿಮ್ಮನ್ನು ನಮ್ಮ ಕಡೆಗೆ ಮರಳಿಸಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(29,58,'29:58','ಸತ್ಯವಿಶ್ವಾಸ ಸ್ವೀಕಾರ ಮಾಡಿದ ಹಾಗೂ ಸತ್ಕರ್ಮ ವೆಸಗಿದ ಜನರನ್ನು ಸ್ವರ್ಗದ ಕೊಠಡಿಗಳಲ್ಲಿ ನಾವು ವಾಸ್ತವ್ಯಗೊಳಿಸುವೆವು. ಅದರ ತಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆಗಳು ಹರಿಯುತ್ತಿರುತ್ತವೆ. ಅಲ್ಲಿ ಅವರು ಸದಾಕಾಲ ನೆಲೆಸುವರು. ಸತ್ಕರ್ಮವೆಸಗುವವರಿಗೆ ಲಭಿಸುವ ಸತ್ಪಲ ಅದೆಷ್ಟು ಉತ್ಕøಷ್ಟ !');
INSERT INTO "kannada" VALUES(29,59,'29:59','ಅವರು ಕಷ್ಟಗಳನ್ನು ಸಹಿಸಿದವರು. ತಮ್ಮ ಪ್ರಭುವಿನ ಮೇಲೆ ಎಲ್ಲವನ್ನು ಅರ್ಪಿಸುವರು .');
INSERT INTO "kannada" VALUES(29,60,'29:60','ತಮ್ಮ ಆಹಾರವನ್ನು ತೆಗೆದಿರಿಸಲಾಗದ ಎಷ್ಟೋ ಪ್ರಾಣಿಗಳಿವೆ. ಅಲ್ಲಾಹನು ಅವುಗಳಿಗೂ ನಿಮಗೂ ಆಹಾರ ಕೊಡುತ್ತಿದ್ದಾನೆ . ಅವನು ಸರ್ವಶ್ರುತನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(29,61,'29:61','ಭೂಮಿ-ಆಕಾಶಗಳನ್ನು ಸೃಷ್ಟಿಸಿದವನು ಮತ್ತು ಸೂರ್ಯ-ಚಂದ್ರರನ್ನು ನಿಯಂತ್ರಿಸುವವನು ಯಾರೆಂದು ತಾವು ಇವರಲ್ಲಿ (ಬಹು ದೇವಾರಾ ಧಕರಲ್ಲಿ) ಕೇಳಿದರೆ `ಅಲ್ಲಾಹನು’ ಎಂದು ಇವರು ಖಂಡಿತ ಹೇಳುವರು’ ಹಾಗಾದರೆ ಇವರನ್ನು ಹೇಗೆ (ತೌಹೀದ್ ನಿಂದ) ತಿರುಗಿಸಲಾಗುತ್ತಿದೆ?');
INSERT INTO "kannada" VALUES(29,62,'29:62','ಅಲ್ಲಾಹನು ತನ್ನ ದಾಸರ ಪೈಕಿ, ತಾನಿಚ್ಛಿಸಿ ದವರಿಗೆ ಆಹಾರವನ್ನು ವಿಸ್ತøತಗೊಳಿಸುತ್ತಾನೆ. ತಾನಿಚ್ಚಿಸಿದವರಿಗೆ ಕಡಿತಗೊಳಿಸುತ್ತಾನೆ. ನಿಶ್ಚಯವಾಗಿ ಅಲ್ಲಾಹು ಸಕಲ ವಸ್ತುಗಳನ್ನು ಬಲ್ಲವನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(29,63,'29:63','ಆಕಾಶದಿಂದ ನೀರನ್ನು ಸುರಿಸಿ, ಅದರ ಮೂಲಕ, ನಿರ್ಜೀವಗೊಂಡು ಬಿದ್ದಿರುವ ಭೂಮಿಯನ್ನು ಸಜೀವಗೊಳಿಸಿದವನು ಯಾರೆಂದು ತಾವು ಇವರಲ್ಲಿ ಕೇಳಿದರೆ, ಇವರು `ಅಲ್ಲಾಹು’ ಎಂದು ಖಂಡಿತ ಹೇಳುವರು. ತಾವು ಹೇಳಿರಿ; ಸರ್ವಸ್ತುತಿಯು ಅಲ್ಲಾಹನಿಗೇ ಮೀಸಲು. ಆದರೆ ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಬುದ್ಧಿ ಕೊಟ್ಟು ಚಿಂತಿಸುವುದಿಲ್ಲ. ');
INSERT INTO "kannada" VALUES(29,64,'29:64','ಈ ಲೌಕಿಕ ಜೀವನವು ಒಂದು ಆಟ ಮತ್ತು ವಿನೋದವಲ್ಲದೆ ಇನ್ನೇನೂ ಅಲ್ಲ . ಪರಲೋಕ ಜೀವನವೇ ನಿಜವಾದ ಜೀವನ . ಇವರು ಅರಿಯುತ್ತಿದ್ದರೆ (ಈ ಲೌಕಿಕ ಜೀವನಕ್ಕೆ ಮಹತ್ವ ಕೊಡುತ್ತಿರಲಿಲ್ಲ).');
INSERT INTO "kannada" VALUES(29,65,'29:65','ಇವರು (ಬಹುದೇವ ವಿಶ್ವಾಸಿಗಳು) ಹಡಗಿನಲ್ಲಿ ಪ್ರಯಾಣಿಸುವಾಗ (ಯಾವುದೇ ದುರಂತವುಂಟಾದರೆ) ತಮ್ಮ ಧರ್ಮವನ್ನು ಅಲ್ಲಾಹನಿಗೆ ಮಾತ್ರ ಮೀಸಲಾಗಿಟ್ಟು ಅವನನ್ನು ಪ್ರಾರ್ಥಿಸುತ್ತಾರೆ. ಇನ್ನು ಅವನು ಇವರನ್ನು ಸಂರಕ್ಷಿಸಿ ದಡಕ್ಕೆ ತಂದರೆ ಅವರು ಅವನಿಗೆ ಸಹಭಾಗಿತ್ವವನ್ನು ಕಲ್ಪಿಸುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(29,66,'29:66','ಈ ಪ್ರವೃತ್ತಿಯು ನಾವು ಅವರಿಗೆ ನೀಡಿದ ಅನು ಗ್ರಹಕ್ಕೆ ಕೃತಘ್ನತೆಯಾಗಿಯೂ ಮತ್ತು ಲೌಕಿಕ ಜೀವನದ ಸವಿಯನ್ನು ಅನುಭವಿಸುವುದಾಗಿಯೂ ಪರಿಣಮಿಸುತ್ತದೆ. ಸದ್ಯದಲ್ಲೇ ಅದರ ಫಲ ಇವರಿಗೆ ತಿಳಿದು ಬರುವುದು.');
INSERT INTO "kannada" VALUES(29,67,'29:67','(ಮಕ್ಕಾ ದೇಶವನ್ನು) ಅವರಿಗೆ ನಾವು ನಿರ್ಭಯವಾದ ಶಾಂತಿಯ ನೆಲೆವೀಡನ್ನಾಡಿ ಮಾಡಿರುವುದು ಇವರಿಗೆ ಗೊತ್ತಿಲ್ಲವೆ? ಇವರ ಆಸುಪಾಸಿನಿಂದ ಜನರು ಅಪಹರಿಸಲ್ಪಡುತ್ತಿದ್ದಾರೆ. ಹೀಗಿದ್ದೂ ಇವರು ಮಿಥ್ಯವನ್ನೇ ನಂಬುವುದೇ? ಅಲ್ಲಾಹನ ಅನುಗ್ರಹಕ್ಕೇ ಕೃತಘ್ನರಾಗುವುದೇ? ');
INSERT INTO "kannada" VALUES(29,68,'29:68','ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಳ್ಳಾರೋಪ ಹೊರಿಸು ವವನಿಗಿಂತ ಅಥವಾ ಸತ್ಯವು ತನ್ನ ಬಳಿ ಬಂದಾಗ ಅದನ್ನು ಸುಳ್ಳಾಗಿಸುವವನಿಗಿಂತ ದೊಡ್ಡ ಅಕ್ರಮಿ ಯಾರು? ನರಕದಲ್ಲಲ್ಲವೇ ಸತ್ಯ ನಿಷೇಧಿಗಳ ವಾಸಸ್ಥಳ?');
INSERT INTO "kannada" VALUES(29,69,'29:69','ನಮ್ಮ ಕಾರ್ಯದಲ್ಲಿ ಹೋರಾಡುವವರನ್ನು ನಾವು ನಮ್ಮ ಮಾರ್ಗಗಳಿಗೆ ಮುನ್ನಡೆಸುತ್ತೇವೆ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹು ಇರುವುದು ಸಚ್ಚರಿತರ ಜೊತೆಯಲ್ಲಿ. ');
INSERT INTO "kannada" VALUES(30,1,'30:1','ಅಲಿಫ್ ಲಾಮ್ ಮೀಮ್.');
INSERT INTO "kannada" VALUES(30,2,'30:2','ರೋಮನರು (ಅರೇಬಿಯಾದ) ಅತ್ಯಂತ ಸಮೀಪದ ಭೂಪ್ರದೇಶದಲ್ಲಿ ಪರಾಜಿತರಾದರು.');
INSERT INTO "kannada" VALUES(30,3,'30:3','ತಮ್ಮ ಪರಾ ಜಯದ ನಂತರದ ಕೆಲವೇ ವರ್ಷಗಳಲ್ಲಿ ಅವರು ಜಯಿಸಲಿರುವರು.');
INSERT INTO "kannada" VALUES(30,4,'30:4','ಅದರ ಹಿಂದೆಯೂ ನಂತರವೂ ಪ್ರಭುತ್ವ ಅಲ್ಲಾಹನದ್ದು. ಅಂದು ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಅಲ್ಲಾಹನ ಸಹಾಯದಿಂದ ಸಂತೋಷಗೊಳ್ಳುವರು .');
INSERT INTO "kannada" VALUES(30,5,'30:5','ಅಲ್ಲಾಹು ತಾನಿಚ್ಚಿಸಿದವರಿಗೆ ಸಹಾಯ ಮಾಡುತ್ತಾನೆ. ಅವನು ಮಹಾಪ್ರತಾಪನೂ ಕರುಣಾಮಯನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(30,6,'30:6','ಇದು ಅಲ್ಲಾಹನ ವಾಗ್ದಾನ. ಅಲ್ಲಾಹು ತನ್ನ ವಾಗ್ದಾನವನ್ನು ಉಲ್ಲಂಘಿಸಲಾರ. ಆದರೆ ಹೆಚ್ಚಿನವರು ಅದನ್ನು ತಿಳಿಯುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(30,7,'30:7','ಲೌಕಿಕ ಜೀವನದ ಬಾಹ್ಯ ಮುಖವನ್ನು ಮಾತ್ರ ಅವರು ತಿಳಿಯುತ್ತಾರೆ. ಆದರೆ ಪರಲೋಕದ ಕುರಿತು ಅವರು ಅಶೃದ್ಧರಾಗಿದ್ದಾರೆ .');
INSERT INTO "kannada" VALUES(30,8,'30:8','ತಮ್ಮ ಕುರಿತಾಗಿಯೇ ಅವರು ಯೋಚಿಸಲಿ ಲ್ಲವೇ? ಅವರು ಸ್ವಯಂ ಆಲೋಚಿಸುತ್ತಿರಲಿ ಲ್ಲವೇ? ಅಲ್ಲಾಹನು ಆಕಾಶಗಳನ್ನೂ ಭೂಮಿ ಯನ್ನೂ ಅವುಗಳ ನಡುವೆ ಇರುವುದನ್ನೂ ಸತ್ಯ ಪೂರ್ಣವಾಗಿ ಮತ್ತು ಒಂದು ನಿರ್ದಿಷ್ಟ ಅವಧಿ ನಿರ್ಣಯದೊಂದಿಗೇ ಸೃಷ್ಟಿಸಿದ್ದಾನೆ . ಆದರೆ ಜನರಲ್ಲಿ ಅನೇಕರು ತಮ್ಮ ಪ್ರಭುವಿನ ಮುಖಾ ಮುಖಿಯನ್ನು ನಿರಾಕರಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(30,9,'30:9','ಅವರು ಭೂಮಿಯಲ್ಲಿ ಸಂಚರಿಸಿ ತಮಗಿಂತ ಮುಂಚೆ ಗತಿಸಿ ಹೋದವರ ಅಂತಿಮ ಪರಿಣಾಮ ಏನಾಯಿತೆಂದು ನೋಡುವುದಿಲ್ಲವೇ? ಅವರು ಇವರಿಗಿಂತಲೂ ಹೆಚ್ಚು ಬಲಶಾಲಿಗಳಾಗಿದ್ದರು. ಅವರು ನೆಲವನ್ನು ಚೆನ್ನಾಗಿ ಅಗೆದು ಕೃಷಿ ಭೂಮಿ ಯಲ್ಲಿ ಬಿತ್ತನೆಯನ್ನು ನಡೆಸಿದರು. ಇವರು ನಡೆ ಸಿದ ಇಮಾರತುಗಳಿಗಿಂತ ಹೆಚ್ಚು ಇಮಾರತು ಗಳನ್ನು ಅವರು ನಡೆಸಿದ್ದರು. ಅವರಿಗೆ ಅವರ ಸಂದೇಶವಾಹಕರು ಸುವ್ಯಕ್ತ ನಿದರ್ಶನಗಳನ್ನು ತಂದರು. (ಪ್ರವಾದಿಗಳನ್ನು ತಳ್ಳಿ ಹಾಕಿದ ಕಾರಣ ಅವರು ಶಿಕ್ಷೆಗೆ ಬಲಿಯಾದರು). ಅಲ್ಲಾಹು ಅವ ರಿಗೆ ಅಕ್ರಮವೆಸಗುವವನಾಗಿರಲಿಲ್ಲ. ಆದರೆ ಅವರು ತಾವೇ ತಮ್ಮ ಮೇಲೆ ಅಕ್ರಮ ವೆಸಗು ವವರಾಗಿದ್ದರು .');
INSERT INTO "kannada" VALUES(30,10,'30:10','ತರುವಾಯ ಪಾಪಕರ್ಮಗಳನ್ನು ಎಸಗಿದ್ದವರ ಪರಿಣಾಮವು ಬಹಳ ಕೆಟ್ಟದಾಯಿತು. ಏಕೆಂದರೆ ಅವರು ಅಲ್ಲಾಹನ ದೃಷ್ಟಾಂತಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದ್ದರು ಮತ್ತು ಅವರು ಅವುಗಳನ್ನು ಅಪಹಾಸ್ಯ ಮಾಡುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(30,11,'30:11','ಅಲ್ಲಾಹನು ಸೃಷ್ಟಿಯನ್ನು ಆರಂಭಿಸುವನು. ನಂತರ ಅದನ್ನು ಮರಳಿ ಸೃಷ್ಟಿಸುವನು. ಮುಂದೆ ಅವನ ಕಡೆಗೇ ನೀವು ಮರಳಿಸಲ್ಪಡುವಿರಿ.');
INSERT INTO "kannada" VALUES(30,12,'30:12','ಲೋಕಾವಸಾನದ ಘಳಿಗೆ ನೆಲೆಗೊಳ್ಳುವ ದಿನ ಅಪರಾಧಿಗಳು ನಿರುತ್ತರರಾಗುವರು .');
INSERT INTO "kannada" VALUES(30,13,'30:13','ಅಂದು ಅವರ ಸಹಭಾಗಿಗಳ ಪೈಕಿ ಅವರಿಗೆ ಶಿಫಾರಸು ಮಾಡುವವರಿರುವುದಿಲ್ಲ. ಅವರು ತಮ್ಮ ಸಹಭಾಗಿಗಳನ್ನು ನಿಷೇಧಿಸುವರು.');
INSERT INTO "kannada" VALUES(30,14,'30:14','ಅಂತಿಮ ಘಳಿಗೆಯು ನೆಲೆಗೊಳ್ಳುವ ದಿನ ಅವರು (ವಿಶ್ವಾಸಿಗಳು ಮತ್ತು ಅವಿಶ್ವಾಸಿಗಳು) ಹರಿ ಹಂಚಾಗಿ ಒಡೆದು ಹೋಗುವರು.');
INSERT INTO "kannada" VALUES(30,15,'30:15','ಆದರೆ ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿದ ಮತ್ತು ಸತ್ಕರ್ಮ ವೆಸಗಿದವರು ಸ್ವರ್ಗೋದ್ಯಾನದಲ್ಲಿ ಹರ್ಷೋಲ್ಲಾಸದಿಂದ ನಲಿದಾಡುವರು.');
INSERT INTO "kannada" VALUES(30,16,'30:16','ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸಿದ ಮತ್ತು ನಮ್ಮ ನಿದರ್ಶನ ಗಳನ್ನೂ ಪರಲೋಕದ ಮುಖಾಮುಖಿ ಯನ್ನೂ ಸುಳ್ಳಾಗಿಸಿದ ಜನರು ಶಿಕ್ಷೆಯಲ್ಲಿ ಹಾಜರಾಗುವರು.');
INSERT INTO "kannada" VALUES(30,17,'30:17','ಆದುದರಿಂದ ನೀವು ಸಂಧ್ಯಾಕಾಲದಲ್ಲೂ, ಪ್ರಾತಃಕಾಲದಲ್ಲೂ ಅಲ್ಲಾಹನ ಪಾವಿತ್ರ್ಯವನ್ನು ಕೊಂಡಾಡಿರಿ.');
INSERT INTO "kannada" VALUES(30,18,'30:18','ಆಕಾಶಗಳಲ್ಲಿ ಮತ್ತು ಭೂಮಿಯಲ್ಲಿ ಸರ್ವಸ್ತುತಿ ಅವನಿಗೇ ಮೀಸಲು. ಸಾಯಾಹ್ನ ವೇಳೆಯಲ್ಲೂ ನಿಮಗೆ ಮಧ್ಯಾಹ್ನ ಸಮಯ ಬಂದಾಗಲೂ ಅವನನ್ನು ಪ್ರಕೀರ್ತಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(30,19,'30:19','ಅವನು ಸಜೀವಿಯನ್ನು ನಿರ್ಜೀವಿಯಿಂದ ಹೊರತರುತ್ತಾನೆ. ನಿರ್ಜೀವಿಯನ್ನು ಸಜೀವಿಯಿಂದ ಹೊರತರುತ್ತಾನೆ. ಭೂಮಿಗೆ ಅದರ ಸಾವಿನ ಬಳಿಕ ಮತ್ತೆ ಜೀವದಾನ ಮಾಡುತ್ತಾನೆ. ಇದೇ ರೀತಿಯಲ್ಲಿ ನೀವು (ಮರಣಾನಂತರ ಸ್ಥಿತಿಯಿಂದ) ಹೊರ ತೆಗೆಯಲ್ಪಡುವಿರಿ.');
INSERT INTO "kannada" VALUES(30,20,'30:20','ಅವನು ನಿಮ್ಮನ್ನು ಮಣ್ಣಿನಿಂದ ಸೃಷ್ಟಿಸಿದ್ದು ತರುವಾಯ ನೀವು ಭೂಮಿಯಲ್ಲಿ ಹರಡುತ್ತಿರುವ ಮನುಷ್ಯರಾಗಿರುವುದು ಅವನ ನಿದರ್ಶನಗಳಲ್ಲೊಂದು.');
INSERT INTO "kannada" VALUES(30,21,'30:21','ಅವನು ನಿಮಗಾಗಿ ನಿಮ್ಮ ವರ್ಗದಿಂದಲೇ ಜೋಡಿಗಳನ್ನು ಸೃಷ್ಟಿಸಿ, ನೀವು ಅವರಿಂದ ಶಾಂತಿ ಹೊಂದುವಂತೆ ಮಾಡಿದ್ದು ಮತ್ತು ನಿಮ್ಮ ನಡುವೆ ಪ್ರೀತಿ ಮತ್ತು ವಾತ್ಸಲ್ಯವನ್ನುಂಟು ಮಾಡಿದ್ದು ಅವನ ನಿದರ್ಶನಗಳಲ್ಲೊಂದು . ವಿವೇಚಿಸುವ ಜನರಿಗೆ ಇದರಲ್ಲಿ ಅನೇಕ ನಿದರ್ಶನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(30,22,'30:22','ಆಕಾಶಗಳು ಮತ್ತು ಭೂಮಿಯ ಸೃಷ್ಟಿಯೂ ನಿಮ್ಮ ಭಾಷೆಗಳು ಹಾಗೂ ವರ್ಣಗಳ ವ್ಯತ್ಯಾ ಸವೂ ಅವನ ನಿದರ್ಶನಗಳಲ್ಲಿ ಸೇರಿವೆ. ಜ್ಞಾನವಂತರಿಗೆ ಇದರಲ್ಲಿ ಅನೇಕ ನಿದರ್ಶನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(30,23,'30:23','ಹಗಲು-ರಾತ್ರಿಯಲ್ಲಿ ನಿಮ್ಮ ನಿದ್ರೆಯೂ ನೀವು ಅವನ ಅನುಗ್ರಹದಿಂದ ಉಪಜೀವನ ಮಾರ್ಗ ವನ್ನು ಹುಡುಕುವುದೂ ಅವನ ನಿದರ್ಶನ ಗಳಲ್ಲೊಂದು. ಆಲಿಸುವ ಜನರಿಗೆ ಇದರಲ್ಲಿ ಅನೇಕ ನಿದರ್ಶನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(30,24,'30:24','ಅವನು ನಿಮಗೆ ಭಯ ಹಾಗೂ ಆಶೆ ಹುಟ್ಟಿಸಿ ಮಿಂಚಿನ ಹೊಳಪನ್ನು ತೋರಿಸುವುದು ಹಾಗೂ ಆಕಾಶದಿಂದ ಮಳೆ ನೀರನ್ನು ಸುರಿಸುವುದು ಅನಂತರ ಅದರ ಮೂಲಕ ಭೂಮಿಗೆ ಅದರ ಮರಣದ ಬಳಿಕ ಮತ್ತೆ ಜೀವ ನೀಡುವುದು ಅವನ ನಿದರ್ಶನಗಳಲ್ಲಿ ಸೇರಿವೆ. ಯೋಚಿಸುವ ಜನರಿಗೆ ಇದರಲ್ಲಿ ಅನೇಕ ನಿದರ್ಶನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(30,25,'30:25','ಆಕಾಶ, ಭೂಮಿಯು ಆತನ ಆಜ್ಞೆ ಪ್ರಕಾರ ನೆಲೆ ನಿಂತಿರುವುದು ಆತನ ನಿದರ್ಶನ. ನಂತರ ಭೂಮಿಯಿಂದ ನಿಮ್ಮನ್ನು ಅವನು ಒಂದು ಕರೆ ಕರೆದರೆ ತಕ್ಷಣ ನೀವು (ಭೂಮಿಯಿಂದ ಜೀವಂತರಾಗಿ) ಹೊರಬರುವಿರಿ.');
INSERT INTO "kannada" VALUES(30,26,'30:26','ಭೂಮ್ಯಾಕಾಶಗಳಲ್ಲಿ ಇರುವವರು ಅವನಿಗೆ ಸೇರಿದ್ದು. ಅವರೆಲ್ಲರೂ ಅವನಿಗೆ ಶರಣರು.');
INSERT INTO "kannada" VALUES(30,27,'30:27','ಸೃಷ್ಟಿಯ ಆರಂಭ ಮಾಡುವವನೂ ಅದರ ಪುನ ರಾವರ್ತನೆ ಮಾಡುವವನೂ ಅವನೇ. ಇದು ಅವನಿಗೆ ಬಲು ಸುಲಭ. ಭೂಮಿ-ಆಕಾಶಗಳಲ್ಲಿ ಅವನಿಗೆ ಸರ್ವಶ್ರೇಷ್ಟಗುಣವಿದ್ದು ಅವನು ಪ್ರಬಲನೂ ಅಗಾಧ ತಂತ್ರಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(30,28,'30:28','ನಿಮ್ಮಿಂದಲೇ ಒಂದು ಉಪಮೆಯನ್ನು ನಿಮಗೆ ಅವನು ವಿವರಿಸಿ ಕೊಡುತ್ತಾನೆ. ನಾವು ನಿಮಗೆ ನೀಡಿರುವ ಸಂಪತ್ತಿನಲ್ಲಿ ಸಮಾನ ಹಕ್ಕುದಾರರಾ ಗಿದ್ದು, ನಿಮ್ಮ ಸ್ವಂತ ಜನರನ್ನು ಹೆದರುವಂತೆ ನೀವು ಹೆದರುವ ಪಾಲುದಾರರು ನಿಮ್ಮ ಒಡೆತನ ದಲ್ಲಿರುವ ನಿಮ್ಮ ಗುಲಾಮರ ಪೈಕಿ ಯಾರಾದರೂ ಇರುವರೇ? ಈ ರೀತಿಯಲ್ಲಿ ನಾವು ಯೋಚಿ ಸುವ ಜನರಿಗೆ ನಮ್ಮ ನಿದರ್ಶನಗಳನ್ನು ವಿವರ ವಾಗಿ ನಿರೂಪಿಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(30,29,'30:29','ಆದರೆ ಅಕ್ರಮಿಗಳು ಏನೂ ಅರಿವಿಲ್ಲದೆ ತಮ್ಮ ತನ್ನಿಚ್ಚೆಗಳನ್ನು ಅನುಸರಿಸುತ್ತಿದ್ದಾರೆ . ಅಲ್ಲಾಹನು ದಾರಿಗೆಡಿಸಿ ಬಿಟ್ಟವನಿಗೆ ನೇರ ದಾರಿ ತೋರು ವವನು ಯಾರು? ಇಂತಹವರಿಗೆ ಸಹಾ ಯಕರಾಗಿ ಯಾರೂ ಇರಲಾರರು.');
INSERT INTO "kannada" VALUES(30,30,'30:30','ಆದುದರಿಂದ (ಸಂದೇಶವಾಹಕರೇ) ಶುದ್ಧ ಮನಸ್ಕರಾಗಿ ನಿಮ್ಮ ಮುಖವನ್ನು ಧರ್ಮದ ನೇರ ನಿಲ್ಲಿಸಿರಿ. ಅಲ್ಲಾಹನು ಮಾನವನನ್ನು ಯಾವ ಪ್ರಕೃತಿಯಲ್ಲಿ ಸೃಷ್ಟಿಸಿರುವನೋ, ಅದೇ ಪ್ರಕೃತಿಯಲ್ಲೇ ಸ್ಥಿರವಾಗಿರಿ. ಅಲ್ಲಾಹನ ಸೃಷ್ಟಿಗೆ ತಿದ್ದುಪಡಿಯಿಲ್ಲ . ಇದುವೇ ವಕ್ರತೆಯಿಲ್ಲದೆ ನೆಲೆ ನಿಲ್ಲುವ ಸರಿಯಾದ ಧರ್ಮ. ಆದರೆ ಹೆಚ್ಚಿನವರು ಅರಿಯುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(30,31,'30:31','ಅಲ್ಲಾಹನೆಡೆಗೆ ಮರಳುತ್ತ ಇದರಲ್ಲೇ ಸ್ಥಿರವಾಗಿರಿ. ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ, ನಮಾಝನ್ನು ಸಂಸ್ಥಾಪಿಸಿರಿ. ನೀವು ಬಹುದೇವ ವಿಶ್ವಾಸಿಗಳಲ್ಲಿ ಸೇರಿ ಹೋಗಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(30,32,'30:32','ಅಂದರೆ ತಮ್ಮ ಧರ್ಮವನ್ನು ಭಿನ್ನ ಭಿನ್ನಗೊಳಿಸಿ ಪಂಗಡಗಳಾಗಿರುವವರ ಕೂಟದಲ್ಲಿ! ಪ್ರತಿ ಯೊಂದು ಪಂಗಡವೂ ತನ್ನ ಬಳಿ ಏನಿದೆಯೋ ಅದರಲ್ಲೇ ಹರ್ಷಗೊಂಡಿದೆ .');
INSERT INTO "kannada" VALUES(30,33,'30:33','');
INSERT INTO "kannada" VALUES(30,34,'30:34','ಯಾವುದೇ ವಿಪತ್ತು ಜನರಿಗೆ ತಟ್ಟಿದರೆ ಅವರು ತಮ್ಮ ಪ್ರಭುವಿನೆಡೆಗೆ ಮರಳಿ ಅವನಲ್ಲಿ ಪ್ರಾರ್ಥಿಸುತ್ತಾರೆ. ನಂತರ ಅವನು ಅವರಿಗೆ ತನ್ನ ಕರುಣೆಯ ರುಚಿಯನ್ನು ಮುಟ್ಟಿಸಿದರೆ ಹಠಾತ್ತನೆ ಅವರಲ್ಲಿ ಒಂದು ವಿಭಾಗವು ನಾವು ಮಾಡಿದ ಉಪಕಾರಕ್ಕೆ ಕೃತಘ್ನತೆ ತೋರಿಸಲಿಕ್ಕಾಗಿ ತಮ್ಮ ಪ್ರಭುವಿನೊಂದಿಗೆ ಇತರ ವಸ್ತುಗಳನ್ನು ಪಾಲು ಸೇರಿಸುತ್ತಾರೆ12. ಆದ್ದರಿಂದ ನೀವು ಸುಖಪಡಿರಿ. ನಂತರ ಅದರ ಪರಿಣಾಮವನ್ನು ತಿಳಿಯಲಿರುವಿರಿ.');
INSERT INTO "kannada" VALUES(30,35,'30:35','ಇವರು ಅಲ್ಲಾಹನಿಗೆ ಪಾಲು ಸೇರಿಸುವ ಕುರಿತಂತೆ ಮಾತನಾಡುವ ಯಾವುದೇ ಪುರಾವೆಯನ್ನು ನಾವು ಇವರ ಮೇಲೆ ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದುಂಟೇ?');
INSERT INTO "kannada" VALUES(30,36,'30:36','ನಾವು ಮನುಷ್ಯರಿಗೆ ಯಾವುದೇ ಕೃಪೆಯ ಸವಿ ಯುಣಿಸಿದಾಗ ಅವರು ಅದರಿಂದ ಹಿರಿಹಿರಿ ಹಿಗ್ಗುತ್ತಾರೆ. ಆದರೆ ಅವರು ಮೊದಲು ಮಾಡಿದ ದುಷ್ಕøತ್ಯಗಳಿಂದಾಗಿ ಅವರಿಗೆ ಯಾವುದೇ ವಿಪತ್ತು ತಟ್ಟಿದರೆ ಕೂಡಲೇ ಅವರು ಹತಾಶರಾಗುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(30,37,'30:37','ಅಲ್ಲಾಹನು ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಆಹಾರವನ್ನು ವಿಸ್ತøತಗೊಳಿಸುವುದನ್ನೂ, ಕಡಿತಗೊಳಿಸುವುದನ್ನೂ ಇವರು ನೋಡುತ್ತಿಲ್ಲವೇ? ಇದರಲ್ಲಿ ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳಿಗೆ ಅನೇಕ ದೃಷ್ಟಾಂತಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(30,38,'30:38','ಆದುದರಿಂದ ಹತ್ತಿರದ ಸಂಬಂಧಿಕನಿಗೂ, ಬಡವನಿಗೂ ಯಾತ್ರಿಕನಿಗೂ ಅವನವನ ಹಕ್ಕನ್ನು ಕೊಡಿರಿ. ಅಲ್ಲಾಹನ ಸಂತೃಪ್ತಿಯನ್ನು ಬಯಸುವವರಿಗೆ ಇದು ಉತ್ತಮವಾಗಿದ್ದು ಅವರೇ ಯಶಸ್ವಿಯಾಗುವವರು.');
INSERT INTO "kannada" VALUES(30,39,'30:39','ಜನರ ಸೊತ್ತಿನಲ್ಲಿ ಸೇರಿ ವೃದ್ಧಿಯಾಗಬೇಕೆಂದು ನೀವು ಕೊಡುವ ಬಡ್ಡಿಯು ಅಲ್ಲಾಹನ ಬಳಿ ವೃದ್ಧಿಸುವುದಿಲ್ಲ . ನೀವು ಅಲ್ಲಾಹನ ಸಂತೃಪ್ತಿ ಬಯಸಿ ಏನಾದರೂ ಝಕಾತ್ ಕೊಟ್ಟರೆ, ಅಂಥವರೇ ವಾಸ್ತವದಲ್ಲಿ ತಮ್ಮ ಪ್ರತಿಫಲವನ್ನು ವೃದ್ಧಿಸಿಕೊಳ್ಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(30,40,'30:40','ನಿಮ್ಮನ್ನು ಸೃಷ್ಟಿಸಿದವನೂ, ನಂತರ ನಿಮಗೆ ಜೀವನಾಧಾರ ನೀಡಿದವನೂ, ಅನಂತರ ನಿಮಗೆ ಮರಣ ಕೊಡುವವನೂ ಆಮೇಲೆ ನಿಮ್ಮನ್ನು ಜೀವಂತಗೊಳಿಸುವವನೂ ಅಲ್ಲಾಹನಾಗಿರುವನು. ಆದರೆ ಈ ಕಾರ್ಯಗಳ ಪೈಕಿ ಯಾವುದ ನ್ನಾದರೂ ಮಾಡುವವರು ನಿಮ್ಮ ಭಾಗೀದಾರರಲ್ಲಿ ಯಾರಾದರೂ ಇರುವರೇ? ಇವರು ಪಾಲು ಸೇರಿಸುತ್ತಿರುವ ವಸ್ತುಗಳಿಂದ ಅವನು ಪರಮ ಪಾವನನೂ ಅತ್ಯುನ್ನತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(30,41,'30:41','ಜನರ ಸ್ವಂತ ಕೈಗಳು ಮಾಡಿದ ಪಾಪದ ಫಲವಾಗಿ ನೆಲ-ಜಲಗಳಲ್ಲಿ ಕ್ಷೋಭೆ ಪ್ರತ್ಯಕ್ಷವಾಗಿದೆ. ಇದು ಅವರ ಕೆಲವು ಕರ್ಮಗಳ ಫಲವನ್ನು ಅವರಿಗೆ ಉಣಿಸಲಿಕ್ಕಾಗಿ. ಅವರು ಮರಳ ಬಹುದೆಂದು.');
INSERT INTO "kannada" VALUES(30,42,'30:42','(ಸಂದೇಶವಾಹಕರೇ,) ತಾವು ಹೇಳಿರಿ, ನೀವು ಭೂಮಿಯಲ್ಲಿ ಸಂಚರಿಸಿ, ಆಮೇಲೆ ಹಿಂದೆ ಗತಿಸಿ ಹೋದವರ ಅಂತಿಮ ಪರಿಣಾಮ ಹೇಗಿತ್ತೆಂದು ನೋಡಿರಿ. ಅವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಅಲ್ಲಾಹನಿಗೆ ಪಾಲು ಸೇರಿಸುವ ಬಹುದೇವಾರಾಧಕರಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(30,43,'30:43','ಆದುದರಿಂದ ಯಾವ ವಿಧದಲ್ಲೂ ತಡೆಯಲು ಸಾಧ್ಯವಿಲ್ಲದ ಒಂದು ದಿನವು ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ಬರುವುದಕ್ಕೆ ಮುನ್ನ ನಿಮ್ಮ ಮುಖವನ್ನು ಸತ್ಯ ಧರ್ಮಕ್ಕೆ ಸ್ಥಿರಗೊಳಿಸಿರಿ . ಅಂದು ಜನರು ಪರಸ್ಪರ ಬೇರ್ಪಡುವರು.');
INSERT INTO "kannada" VALUES(30,44,'30:44','ಯಾರು ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸುತ್ತಾನೆ, ಅವನ ನಿಷೇಧದ ಫಲಾನುಭವ ಅವನ ಮೇಲೆಯೇ ಇದೆ. ಯಾರು ಸತ್ಕರ್ಮವೆಸಗುತ್ತಾರೆ, ಅವರು ತಮಗಾಗಿಯೇ ಸ್ವರ್ಗದ ಪದವಿಗಳನ್ನು ಸಿದ್ಧಗೊಳಿಸುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(30,45,'30:45','(ಹೀಗೆ ಸಿಡಿದು ಬೇರ್ಪಡುವುದು) ಸತ್ಯ ವಿಶ್ವಾಸ ವಿರಿಸಿದ ಮತ್ತು ಸತ್ಕರ್ಮವೆಸಗಿದವರಿಗೆ ಅಲ್ಲಾ ಹನು ತನ್ನ ಅನುಗ್ರಹದಿಂದ ಸತ್ಫಲ ನೀಡಲಿ ಕ್ಕಾಗಿದೆ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅವನು ಸತ್ಯ ನಿಷೇಧಿಗಳನ್ನು ಮೆಚ್ಚುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(30,46,'30:46','ಸುವಾರ್ತೆ ನೀಡುತ್ತ ಮಾರುತಗಳನ್ನು ಕಳುಹಿಸುವುದು ಅಲ್ಲಾಹನ ನಿದರ್ಶನಗಳಲ್ಲಿ ಸೇರಿದೆ. ನಿಮಗೆ ತನ್ನ ಕೃಪೆಯಿಂದ ಆಸ್ವಾಧನೆ ನೀಡಲು ಹಾಗೂ ನಾವೆಗಳು ಅವನ ಆಜ್ಞೆಯಿಂದ ಚಲಿಸುತ್ತಿರಲು, ಆ ಮೂಲಕ ನೀವು ಅವನ ಅನುಗ್ರಹದಿಂದ ಉಪಜೀವನವನ್ನು ಅರಸಲು ಮತ್ತು ಅವನಿಗೆ ಕೃತಜ್ಞರಾಗಿರಲು.');
INSERT INTO "kannada" VALUES(30,47,'30:47','ನಿಮಗಿಂತ ಮುಂಚೆ ಅನೇಕ ಸಂದೇಶವಾಹಕರನ್ನು ನಾವು ಅವರ ಜನಾಂಗದ ಕಡೆಗೆ ಖಂಡಿತ ರವಾನಿಸಿದ್ದೇವೆ. ಹಾಗೆ ಅವರು ಅವರ ಮುಂದೆ ಸುವ್ಯಕ್ತವಾದ ಅನೇಕ ನಿದರ್ಶನಗಳನ್ನು ತಂದರು. (ಆಗ ಅವರು ಅದನ್ನು ನಿಷೇಧಿಸಿದರು). ತನ್ನಿಮಿತ್ತ ಅಪರಾಧವೆಸಗಿದವರ ವಿರುದ್ಧ ನಾವು ಪ್ರತೀಕಾರ ಕ್ರಮ ಕೈಗೊಂಡಿದ್ದೇವೆ. ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಸಹಾಯ ಮಾಡುವುದು ನಮ್ಮ ಹೊಣೆಯಾಗಿತ್ತು. (ಅದನ್ನು ನಾವು ಮಾಡಿದೆವು) .');
INSERT INTO "kannada" VALUES(30,48,'30:48','ಅಲ್ಲಾಹನು ಮಾರುತಗಳನ್ನು ಕಳುಹಿಸುತ್ತಾನೆ. ಹಾಗೆ ಅವು ಮೋಡಗಳನ್ನು ಚದುರಿಸುತ್ತವೆ. ಅನಂತರ ಅವನು ತಾನಿಚ್ಛಿಸುವಂತೆ ಮೋಡಗಳನ್ನು ಆಕಾಶದಲ್ಲಿ ಹರಡುತ್ತಾನೆ. ಅವುಗಳನ್ನು ವಿಭಿನ್ನ ತುಂಡುಗಳನ್ನಾಗಿ ಮಾಡುತ್ತಾನೆ. ಆ ಬಳಿಕ ಮಳೆ ಹನಿಗಳು ಅದರೆಡೆಯಿಂದ ಹೊರಬರುವುದನ್ನು ತಾವು ನೋಡುತ್ತೀರಿ. ಅವನು ತನ್ನ ದಾಸರಲ್ಲಿ ತನಗಿಷ್ಟ ಬಂದವರ ಮೇಲೆ ಮಳೆಯನ್ನು ಸುರಿಸಿದಾಗ ಅವರು ಸಂತೋಷಪಡುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(30,49,'30:49','ಅವರು ತಮ್ಮ ಮೇಲೆ ಮಳೆ ಬೀಳುವ ಮುನ್ನ ನಿಜವಾಗಿಯೂ ನಿರಾಶರಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(30,50,'30:50','ಆದ್ದರಿಂದ ಅಲ್ಲಾಹನ ಅನುಗ್ರಹದ ಪರಿಣಾಮ ವನ್ನು ನೋಡಿರಿ. ಸತ್ತು ಬಿದ್ದ ನೆಲವನ್ನು ಅವನು ಹೇಗೆ ಜೀವಂತಗೊಳಿಸುತ್ತಾನೆ? ಹಾಗೆ ಸಜೀವಗೊಳಿಸುವವನೇ ಖಂಡಿತ ಮೃತರನ್ನು ಜೀವಂತಗೊಳಿಸುವವನು. ಸಕಲ ವಿಷಯಗಳ ಮೇಲೆ ಸಾಮಥ್ರ್ಯವುಳ್ಳವನು.');
INSERT INTO "kannada" VALUES(30,51,'30:51','ನಾವೊಂದು ಸುಳಿಗಾಳಿಯನ್ನು ಕಳುಹಿಸಿದರೆ ಆಗ ಅವರು ಆ ಹೊಲವನ್ನು ಹಳದಿಯಾಗಿ ಕಂಡ ನಂತರ (ಹಿಂದಿನ) ಅನುಗ್ರಹವನ್ನು ತಳ್ಳಿ ಹಾಕುವರು.');
INSERT INTO "kannada" VALUES(30,52,'30:52','(ಪೈಗಂಬರರೇ) ನೀವು ಮೃತರಿಗೆ ಕೇಳಿಸಲಾರಿರಿ , ಬೆನ್ನು ತಿರುಗಿಸಿ ಓಡುತ್ತಿರುವ ಕಿವುಡರಿಗೂ ನೀವು ಕರೆ ಕೇಳಿಸಲಾರಿರಿ.');
INSERT INTO "kannada" VALUES(30,53,'30:53','ನೀವು ಕುರುಡರನ್ನು ಅವರ ದುರ್ಮಾರ್ಗದಿಂದ ರಕ್ಷಿಸಿ ಅವರಿಗೆ ಸನ್ಮಾರ್ಗದರ್ಶನ ಮಾಡಲಾರಿರಿ. ನಮ್ಮ ನಿದರ್ಶನಗಳ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸುವ ಹಾಗೂ ವಿಧೇಯರಾಗುವವರಿಗೆ (ಮುಸ್ಲಿಮ ರಾಗುವವರಿಗೆ) ಮಾತ್ರ ನೀವು ಕೇಳಿಸಬಲ್ಲಿರಿ.');
INSERT INTO "kannada" VALUES(30,54,'30:54','ನಿಮ್ಮನ್ನು ದುರ್ಬಲಾವಸ್ಥೆಯಿಂದ ಸೃಷ್ಟಿಸಿದವನು ಅಲ್ಲಾಹನು. ಮತ್ತೆ ದೌರ್ಬಲ್ಯದ ನಂತರ ಅವನು ಶಕ್ತಿಯನ್ನು ನೀಡಿದನು. ಈ ಶಕ್ತಿಯ ನಂತರ ನಿಮ್ಮನ್ನು ಬಲಹೀನತೆಗೆ ತಂದನು. ನೆರೆತವನ್ನೂ ಉಂಟು ಮಾಡಿದನು. ಅವನು ತಾನಿಚ್ಚಿಸಿದುದನ್ನು ಸೃಷ್ಟಿಸುತ್ತಾನೆ. ಅವನು ಸರ್ವಜ್ಞನೂ ಸರ್ವಶಕ್ತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(30,55,'30:55','ಅಂತ್ಯ ಘಳಿಗೆ ನೆಲೆಗೊಂಡರೆ ಅಪರಾಧಿಗಳು ನಾವು ಒಂದು ಘಳಿಗೆಗಿಂತ ಹೆಚ್ಚುಕಾಲ (ಇಹ ಲೋಕದಲ್ಲಿ) ತಂಗಿರಲಿಲ್ಲವೆಂದು ಆಣೆ ಹಾಕಿ ಹೇಳುವರು . ಅವರು ಐಹಿಕ ಜೀವನದಲ್ಲಿ ಹೀಗೆಯೇ ಸತ್ಯದಿಂದ ವಿಮುಖರಾದರು.');
INSERT INTO "kannada" VALUES(30,56,'30:56','ಆದರೆ ಜ್ಞಾನ ಹಾಗೂ ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಹೊಂದಿ ದವರು ಹೀಗೆ ಹೇಳುವರು; ‘ಅಲ್ಲಾಹನ ಗ್ರಂಥದಲ್ಲಿ ಉಲ್ಲೇಖಿಸಿದ ಪ್ರಕಾರ ಪುನರುತ್ಥಾನ ದಿನದವರೆಗೂ ನೀವು ನೆಲೆಸಿದ್ದೀರಿ. ಇದು ಅದೇ ಪುನರುತ್ಥಾನ ದಿನ. ಆದರೆ ನೀವು ಇದನ್ನು ಅರಿತಿರಲಿಲ್ಲ’.');
INSERT INTO "kannada" VALUES(30,57,'30:57','ಆ ದಿನದಂದು ಅಕ್ರಮಿಗಳಿಗೆ ಅವರ ನೆಪವೇನೂ ಫಲ ಕೊಡದು. ಅಲ್ಲದೆ, ಪರಿಹಾರ ಮಾಡಿಕೊಳ್ಳಲಿಕ್ಕೂ ಅವರನ್ನು ಕೇಳಿಕೊಳ್ಳಲಾಗದು .');
INSERT INTO "kannada" VALUES(30,58,'30:58','ಈ ಖುರ್‍ಆನ್‍ನಲ್ಲಿ ಜನರಿಗಾಗಿ ಎಲ್ಲ ತರದ ಉಪಮೆಯನ್ನು ನಾವು ನೀಡಿದ್ದೇವೆ. ನೀವು ಯಾವುದೇ ನಿದರ್ಶನವನ್ನು ಅವರಿಗೆ ತಂದರೂ ಸತ್ಯನಿಷೇಧಿಗಳು `ನೀವು ಮಿಥ್ಯವಾದಿಗಳು’ ಎಂದೇ ಖಂಡಿತ ಹೇಳುವರು.');
INSERT INTO "kannada" VALUES(30,59,'30:59','ಹೀಗೆ ಸತ್ಯವನ್ನು ತಿಳಿಯದವರ ಹೃದಯಗಳ ಮೇಲೆ ಅಲ್ಲಾಹನು ಮುದ್ರೆಯೊತ್ತುವನು.');
INSERT INTO "kannada" VALUES(30,60,'30:60','ಆದುದರಿಂದ (ಪೈಗಂಬರರೇ) ತಾಳ್ಮೆ ವಹಿಸಿರಿ. ಅಲ್ಲಾಹನ ವಾಗ್ದಾನ ಖಂಡಿತ ಸತ್ಯ. ಸತ್ಯದಲ್ಲಿ ಅಚಲ ನಂಬಿಕೆ ಇರಿಸದ ಜನರು ತಮ್ಮ ಸ್ಥೈರ್ಯವನ್ನು ಚಂಚಲಗೊಳಿಸದಿರಲಿ.');
INSERT INTO "kannada" VALUES(31,1,'31:1','ಅಲಿಫ್ ಲಾಮ್ ಮೀಮ್');
INSERT INTO "kannada" VALUES(31,2,'31:2','ಇವು ಯುಕ್ತಿಪೂರ್ಣ ಗ್ರಂಥದ ಸೂಕ್ತಗಳು.');
INSERT INTO "kannada" VALUES(31,3,'31:3','ಸಚ್ಚರಿತರಿಗೆ ಮಾರ್ಗದರ್ಶನವೂ ಅನುಗ್ರಹವೂ ಆಗಿ ಇದು ನೆಲೆಗೊಂಡಿದೆ.');
INSERT INTO "kannada" VALUES(31,4,'31:4','ಅಂದರೆ ನಮಾಝನ್ನು ಸಂಸ್ಥಾಪಿಸುವ, ಝಕಾತ್ ಕೊಡುವ ಮತ್ತು ಪರಲೋಕದ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸುವ ಸಚ್ಚರಿತರು.');
INSERT INTO "kannada" VALUES(31,5,'31:5','ಅವರು ತಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದಿರುವ ಸನ್ಮಾರ್ಗದಲ್ಲಿದ್ದಾರೆ. ಅವರೇ ಯಶಸ್ವಿಯಾದವರು.');
INSERT INTO "kannada" VALUES(31,6,'31:6','ಕೆಲವು ಜನರು ವಿನೋದ ವಸ್ತುಗಳನ್ನು ಖರೀದಿಸುತ್ತಾರೆ. ಯಾವುದೇ ಜ್ಞಾನವಿಲ್ಲದೆ ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ ಜನರನ್ನು ದಾರಿಗೆಡಿಸಲಿಕ್ಕಾಗಿ ಮತ್ತು ಆ ಮಾರ್ಗವನ್ನು ಪರಿಹಾಸಕ್ಕೀಡು ಮಾಡಲಿಕ್ಕಾಗಿ. ಇಂಥವರಿಗೆ ಬಹಳ ನಿಂದ್ಯವಾದ ಶಿಕ್ಷೆ ಇದೆ .');
INSERT INTO "kannada" VALUES(31,7,'31:7','ಅವನಿಗೆ ನಮ್ಮ ಸೂಕ್ತಗಳನ್ನು ಓದಿ ಕೇಳಿಸ ಲಾದರೆ ಅವುಗಳನ್ನು ತಾನು ಕೇಳಲೇ ಇಲ್ಲವೆಂ ಬಂತೆ, ಅಥವಾ ತನ್ನ ಎರಡು ಕಿವಿಗಳಲ್ಲಿ ಕಿವುಡು ಬಾಧಿಸಿದಂತೆ ದರ್ಪದಿಂದ ಮುಖ ತಿರುಗಿಸುತ್ತಾನೆ. ಆದ್ದರಿಂದ ಅವನಿಗೆ ಒಂದು ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆಯ ಸುವಾರ್ತೆ ಕೊಡಿರಿ .');
INSERT INTO "kannada" VALUES(31,8,'31:8','ಆದರೆ, ಸತ್ಯವಿಶ್ವಾಸ ಸ್ವೀಕರಿಸುವ ಮತ್ತು ಸತ್ಕರ್ಮವೆಸಗುವ ಜನರಿಗೆ ಅನುಗ್ರಹಗಳು ತುಂ ಬಿದ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಿವೆ .');
INSERT INTO "kannada" VALUES(31,9,'31:9','ಅವುಗಳಲ್ಲಿ ಅವರು ಸದಾಕಾಲ ವಾಸಿಸುವರು. ಇದು ಅಲ್ಲಾಹನ ಸತ್ಯವಾದ ವಾಗ್ದಾನ. ಅವನು ಪ್ರಬಲನೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(31,10,'31:10','ನಿಮಗೆ ಕಾಣುವಂಥ ಸ್ಥಂಭಗಳಿಲ್ಲದೆ ಅವನು ಆಕಾಶಗಳನ್ನು ಸೃಷ್ಟಿಸಿದನು. ಭೂಮಿಯು ನಿಮ್ಮೊಂದಿಗೆ ವಾಲಿ ಬಿಡದಿರಲು ಅವನು ಅದರಲ್ಲಿ ಪರ್ವತಗಳನ್ನು ನಾಟಿದನು. ಅವನು ಎಲ್ಲ ತರದ ಚರಜೀವಿಗಳನ್ನು ಭೂಮಿಯಲ್ಲಿ ಹಬ್ಬಿಸಿದನು. ಆಕಾಶದಿಂದ ನಾವು (ಅಲ್ಲಾಹು) ನೀರನ್ನು ಸುರಿಸಿ ಭೂಮಿಯಲ್ಲಿ ಎಲ್ಲ ತರದ ಉತ್ತಮ ಜೋಡಿಗಳನ್ನು ಬೆಳೆಸಿದ್ದೇವೆ .');
INSERT INTO "kannada" VALUES(31,11,'31:11','ಇದು ಅಲ್ಲಾಹನ ಸೃಷ್ಟಿ. ಇನ್ನು ಅವನ ಹೊರತಾ ದವರು ಏನನ್ನು ಸೃಷ್ಟಿಸಿದ್ದಾರೆಂದು ನನಗೆ ತೋರಿಸಿರಿ. ಹಾಗಲ್ಲ; ಅಕ್ರಮಿಗಳು ಸುವ್ಯಕ್ತ ದುರ್ಮಾರ್ಗದಲ್ಲಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(31,12,'31:12','ಲುಖ್ಮಾನರಿಗೆ ನಾವು ತತ್ವಜ್ಞಾನವನ್ನು ದಯ ಪಾಲಿಸಿದೆವು . ‘ಅಲ್ಲಾಹನಿಗೆ ಕೃತಜ್ಞನಾಗು’ ಎಂದೆವು. ಯಾವನು ಅಲ್ಲಾಹನಿಗೆ ಕೃತಜ್ಞನಾಗುತ್ತಾನೋ ಅವನು ತನಗಾಗಿಯೇ ಕೃತಜ್ಞನಾದನು. ಯಾವನು ಕೃತಘ್ನನಾಗುತ್ತಾನೋ ವಾಸ್ತವದಲ್ಲಿ ಅಲ್ಲಾಹು ನಿರಪೇಕ್ಷನೂ ಸ್ತುತ್ಯರ್ಹನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(31,13,'31:13','ಲುಖ್ಮಾನರು ತಮ್ಮ ಪುತ್ರನಿಗೆ ಉಪದೇಶಿಸುತ್ತಾ ಹೇಳಿದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ. “ನನ್ನ ಪ್ರೀತಿಯ ಮಗನೇ, ಅಲ್ಲಾಹನಿಗೆ ಯಾರನ್ನೂ ಸಹಭಾಗಿ ಮಾಡಬೇಡ, ನಿಜವಾಗಿಯೂ ಬಹುದೇವತ್ವವು ಮಹಾ ಅಕ್ರಮವಾಗಿದೆ”.');
INSERT INTO "kannada" VALUES(31,14,'31:14','ಮಾನವನಿಗೆ ತನ್ನ ಮಾತಾಪಿತರ ಬಗ್ಗೆ ನಾವು ವಸಿಯ್ಯತ್ ಮಾಡಿರುತ್ತೇವೆ. ಅವನ ತಾಯಿಯು ನಿತ್ರಾಣದ ಮೇಲೆ ನಿತ್ರಾಣವನ್ನು ಸಹಿಸಿ ಅವನನ್ನು ಗರ್ಭ ಹೊತ್ತಳು. ಅವನ ಸ್ತನಪಾನದ ಮುಕ್ತಾಯವು ಎರಡು ವರ್ಷಗಳಲ್ಲಾಗಿದೆ. (ಆದ್ದರಿಂದ ಓ ಮಾನವ,) ನನಗೂ ನಿನ್ನ ಮಾತಾಪಿತರಿಗೂ ಕೃತಜ್ಞತೆ ಸಲ್ಲಿಸು. ನೀನು ನನ್ನ ಕಡೆಗೇ ಮರಳಬೇಕಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(31,15,'31:15','ಆದರೆ ನಿನಗೆ ಯಥಾಥ್ರ್ಯವೇನೆಂದು ತಿಳಿಯದ ಯಾವುದೇ ವಸ್ತುವನ್ನು ನನಗೆ ಪಾಲು ಸೇರಿಸುವಂತೆ ಅವರು ನಿನ್ನ ಮೇಲೆ ಒತ್ತಡ ಹಾಕಿದರೆ ನೀನು ಅವರನ್ನು ಅನುಸರಿಸಬೇಡ . ಈ ಲೋಕ ದಲ್ಲಿ ಅವರೊಂದಿಗೆ ಸದ್ವರ್ತನೆ ಮಾಡು . ನನ್ನ ಕಡೆಗೆ ಮರಳಿ ಬಂದವರ ಮಾರ್ಗವನ್ನು ಅನುಗಮಿಸು. ಮುಂದೆ ನಿಮಗೆಲ್ಲರಿಗೂ ನನ್ನ ಕಡೆಗೇ ಮರಳಲಿಕ್ಕಿದೆ. ನೀವು ಮಾಡುತ್ತಿದ್ದ ಕರ್ಮಗಳ ಬಗ್ಗೆ ಆಗ ನಾನು ನಿಮಗೆ ಮಾಹಿತಿ ಕೊಡಲಿರುವೆನು,');
INSERT INTO "kannada" VALUES(31,16,'31:16','ನನ್ನ ಮುದ್ದು ಮಗನೇ! ನೀನು ಎಸಗಿದ ಕಾರ್ಯವು ಸಾಸಿವೆ ಕಾಳಿನಷ್ಟಿರುವ ಒಂದು ವಸ್ತುವೇ ಇರಲಿ, ಅದು ಒಂದು ಬಂಡೆಯಲ್ಲಿ ಅಥವಾ ಆಕಾ ಶಗಳಲ್ಲಿ ಅಥವಾ ಭೂಮಿಯೊಳಗೆ ಎಲ್ಲೇ ಇರಲಿ, ಅಲ್ಲಾಹನು ಅದನ್ನು (ನಿರ್ಣಾಯಕ ದಿನದಲ್ಲಿ) ಹೊರತರುವನು. ಅವನು ಸೂಕ್ಷ್ಮದರ್ಶಿ ಮತ್ತು ವಿವರಪೂರ್ಣನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(31,17,'31:17','ನನ್ನ ಮುದ್ದು ಮಗನೇ, ನಮಾಝನ್ನು ವಿಧಿವತ್ತಾಗಿ ನಿರ್ವಹಿಸು, ಒಳಿತನ್ನು ಬೋಧಿಸು, ಕೆಡುಕನ್ನು ತಡೆ, ನಿನಗೆ ತಟ್ಟಿದ ಕಷ್ಟಕ್ಕೆ ಸಹನೆ ತಾಳು. ಇವು ಬಿಡಲೊಲ್ಲದ ಅಚಲ ವಿಷಯಗಳು .');
INSERT INTO "kannada" VALUES(31,18,'31:18','ಜನರಿಂದ ನೀನು ಮುಖ ತಿರುಗಿಸಬೇಡ. ಭೂಮಿಯ ಮೇಲೆ ದರ್ಪದಿಂದ ನಡೆಯಬೇಡ, ದರ್ಪತೋರುವ ಹಾಗೂ ಜಂಭ ಕೊಚ್ಚುವ ಯಾರನ್ನೂ ಅಲ್ಲಾಹನು ಮೆಚ್ಚುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(31,19,'31:19','ನಿನ್ನ ನಡೆಯಲ್ಲಿ ಮಿತಿ ಪಾಲಿಸು. ನಿನ್ನ ಸ್ವರವನ್ನು ತಗ್ಗಿಸು. ಎಲ್ಲ ಸ್ವರಗಳಿಗಿಂತ ಕೆಟ್ಟ ಸ್ವರವು ಕತ್ತೆಯ ಸ್ವರವಾಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(31,20,'31:20','ಅಲ್ಲಾಹನು ಆಕಾಶಗಳಲ್ಲೂ ಭೂಮಿಯಲ್ಲೂ ಇರುವ ಸಕಲ ವಸ್ತುಗಳನ್ನು ನಿಮಗೆ ಅಧೀನ ಗೊಳಿಸಿದ್ದನ್ನೂ ತನ್ನ ಪ್ರತ್ಯಕ್ಷ ಹಾಗೂ ಆಂತರಿಕ ಅನುಗ್ರಹಗಳನ್ನು ನಿಮಗೆ ಪೂರ್ತಿಗೊಳಿಸಿ ಕೊಟ್ಟಿದ್ದನ್ನೂ ನೀವು ನೋಡುತ್ತಿಲ್ಲವೇ? ಹೀಗಿದ್ದೂ ಮಾನವರಲ್ಲಿ ಕೆಲವರು ತಮ್ಮ ಬಳಿ ಯಾವುದೇ ಜ್ಞಾನವಾಗಲಿ ಸನ್ಮಾರ್ಗದರ್ಶನವಾಗಲಿ ಬೆಳಕು ತೋರಿಸುವ ಯಾವುದೇ ಗ್ರಂಥವಾಗಲಿ ಇಲ್ಲದೆ ಅಲ್ಲಾಹನ (ತೌಹೀದ್‍ನ) ವಿಚಾರದಲ್ಲಿ ಜಗಳಾಡುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(31,21,'31:21','ಅಲ್ಲಾಹನು ಅವತೀರ್ಣಗೊಳಿಸಿರುವುದನ್ನು ಅನುಸರಿಸಿರೆಂದು ಅವರಿಗೆ ಹೇಳಲಾದರೆ ಅವರು “ಅಲ್ಲ, ನಮ್ಮ ಪೂರ್ವಜರು ನಡೆದು ಕಂಡ ಮಾರ್ಗವನ್ನೇ ನಾವು ಅನುಸರಿಸುತ್ತೇವೆ” ಎನ್ನುತ್ತಾರೆ. ಶೈತಾನನು ಅವರನ್ನು ನರಕ ಶಿಕ್ಷೆಯ ಕಡೆಗೇ ಕರೆಯುತ್ತಿದ್ದರೂ ಇವರು ಅವರನ್ನೇ ಅನು ಸರಿಸುವರೇ?');
INSERT INTO "kannada" VALUES(31,22,'31:22','ಯಾರು ಸತ್ಕರ್ಮಿಯಾಗಿದ್ದುಕೊಂಡು ತನ್ನ ದೇಹವನ್ನು ಅಲ್ಲಾಹನಿಗೆ ಅರ್ಪಿಸಿಕೊಳ್ಳುತ್ತಾನೋ, ಅವನು ಅತ್ಯಂತ ಬಿಗಿಯಾದ ಪಾಶವನ್ನು ಬಿಗಿ ಹಿಡಿದನು. ಸಕಲ ವಿಷಯಗಳ ಅಂತ್ಯವು ಅಲ್ಲಾಹನ ಕಡೆಗೇ ಇದೆ .');
INSERT INTO "kannada" VALUES(31,23,'31:23','ಯಾರು ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸುತ್ತಾನೆ, ಆತನ ಸತ್ಯ ನಿಷೇಧವು (ಪ್ರವಾದಿಯರೇ) ನಿಮ್ಮನ್ನು ವ್ಯಥೆಗೀಡು ಮಾಡದಿರಲಿ. ಅವರ ಮರಳಿಕೆ ನಮ್ಮ ಕಡೆಗೇ ಆಗಿದೆ. ಅವರು ಎಸಗಿದ ಕರ್ಮಗಳ ಬಗ್ಗೆ ಆವಾಗ ನಾವು ಅವರಿಗೆ ತೋರಿಸಿ ಕೊಡುತ್ತೇವೆ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹು ಎದೆಗಳೊಳಗೆ ಅವಿತಿರುವ ರಹಸ್ಯಗಳನ್ನು ಚೆನ್ನಾಗಿ ತಿಳಿದಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(31,24,'31:24','ನಾವು ಅವರಿಗೆ ಸ್ವಲ್ಪ ಕಾಲ (ಭೂಮಿಯಲ್ಲಿ) ಸುಖ ನೀಡುತ್ತಿದ್ದೇವೆ. ಆ ಬಳಿಕ ಅವರನ್ನು ಒಂದು ಕಠಿಣ ಶಿಕ್ಷೆಯ ಕಡೆಗೆ ಬಲವಂತವಾಗಿ ಎಳೆದೊಯ್ಯುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(31,25,'31:25','ಆಕಾಶಗಳನ್ನೂ ಭೂಮಿಯನ್ನೂ ಸೃಷ್ಟಿಸಿದವರು ಯಾರು ಎಂದು ತಾವು ಇವರನ್ನು ಕೇಳಿದರೆ, ಇವರು ಅಲ್ಲಾಹನೆಂದೇ ಉತ್ತರಿಸುವರು. ತಾವು ಹೇಳಿರಿ, ‘ಅಲ್ ಹಮ್ದುಲಿಲ್ಲಾಹ್’ (ಅಲ್ಲಾಹನಿಗೆ ಸ್ತುತಿ). ಆದರೆ ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ತಿಳಿಯುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(31,26,'31:26','ಆಕಾಶಗಳಲ್ಲೂ ಭೂಮಿಯಲ್ಲೂ ಇರುವುದೆಲ್ಲವೂ ಅಲ್ಲಾಹನದು. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹು ನಿರಪೇಕ್ಷನೂ ಸ್ತುತ್ಯರ್ಹನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(31,27,'31:27','ಭೂಮಿಯಲ್ಲಿರುವ ಮರಗಳೆಲ್ಲ ಲೇಖನಿಗಳಾಗಿ ಮಾರ್ಪಟ್ಟು (ಈಗಿರುವ) ಸಾಗರವು ಮಸಿಯಾಗಿ- ಅದಕ್ಕೆ ಇನ್ನೂ ಸಪ್ತಸಾಗರಗಳು ಮಸಿಯನ್ನು ಒದಗಿಸಿದರೂ - ಅಲ್ಲಾಹನ ವಚನಗಳು ಬರೆದು ಮುಗಿಯಲಾರವು. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹು ಪ್ರತಾಪಶಾಲಿಯೂ ಅಗಾದಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(31,28,'31:28','ನಿಮ್ಮೆಲ್ಲರನ್ನು ಸೃಷ್ಟಿಸುವುದೂ ಪುನಃ ಜೀವಂತಗೊಳಿಸಿ ಎಬ್ಬಿಸುವುದೂ ಅವನ ಮಟ್ಟಿಗೆ ಕೇವಲ ಒಂದು ಜೀವಿಯನ್ನು ಸೃಷ್ಟಿಸುವ ಮತ್ತು ಪುನಃ ಜೀವಂತಗೊಳಿಸುವಂತೆ ಮಾತ್ರವಿದೆ . ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹು ಸರ್ವಶ್ರುತನೂ ಸರ್ವ ವೀಕ್ಷಕನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(31,29,'31:29','ಅಲ್ಲಾಹು ಇರುಳನ್ನು ಹಗಲಿನೊಳಗೂ ಹಗಲನ್ನು ಇರುಳಿನೊಳಗೂ ತೂರಿಸುತ್ತಿರುವುದನ್ನೂ ಸೂರ್ಯಚಂದ್ರರನ್ನು ನಿಯಂತ್ರಿಸಿಟ್ಟಿರುವುದನ್ನೂ ನೀನು ನೋಡುತ್ತಿಲ್ಲವೇ? ಎಲ್ಲವೂ ಒಂದು ನಿಶ್ಚಿತ ಕಾಲದವರೆಗೆ ಚಲಿಸುತ್ತಿವೆ. ನೀವು ಮಾಡುತ್ತಿರುವುದರ ಬಗ್ಗೆ ಅಲ್ಲಾಹು ಸೂಕ್ಷ್ಮಜ್ಞನಾಗಿದ್ದಾನೆ .');
INSERT INTO "kannada" VALUES(31,30,'31:30','ಇದು ಯಾಕೆಂದರೆ, ಅಲ್ಲಾಹನೇ ಚಿರಂತನ ಸತ್ಯವಾಗಿದ್ದಾನೆ. ಅವನನ್ನು ಬಿಟ್ಟು ಇವರು ಆರಾಧಿಸುತ್ತಿರುವ ಇತರ ಎಲ್ಲವೂ ಮಿಥ್ಯವಾಗಿವೆ. ಅಲ್ಲಾಹನೇ ಉನ್ನತನೂ ಮಹಾನನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(31,31,'31:31','ಅಲ್ಲಾಹನ ಅನುಗ್ರಹದಿಂದ ನಾವೆಯು ಸಮುದ್ರದಲ್ಲಿ ಚಲಿಸುತ್ತಿರುವುದನ್ನು ನೀನು ನೋಡು ವುದಿಲ್ಲವೇ? ಇದು ಅವನ ಕೆಲವು ನಿದರ್ಶನ ಗಳನ್ನು ನಿಮಗೆ ತೋರಿಸಿ ಕೊಡಲಿಕ್ಕಾಗಿ ವಾಸ್ತವದಲ್ಲಿ ಕೃತಜ್ಞನಾಗಿರುವ ಎಲ್ಲ ಕ್ಷಮಾಶೀಲರಿಗೂ ಇದರಲ್ಲಿ ಅನೇಕ ದೃಷ್ಟಾಂತಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(31,32,'31:32','ಸಮುದ್ರದಲ್ಲಿ ಬೆಟ್ಟಗಳಂಥ ತೆರೆಮಾಲೆಗಳು ಇವರ ಮೇಲೆ ಆವರಿಸಿದಾಗ ಇವರು ತಮ್ಮ ವಿಧೇಯತೆಯನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ಅಲ್ಲಾಹನಿಗೇ ಮೀಸಲಾಗಿಸಿ ಅವನನ್ನು ಪ್ರಾರ್ಥಿಸುತ್ತಾರೆ. ತರುವಾಯ ಅವನು ಇವರನ್ನು ರಕ್ಷಿಸಿ ದಡಕ್ಕೆ ತಂದರೆ ಅವರಲ್ಲಿ ಕೆಲವರು ಎಡತಾಕ ನೀತಿಯನ್ನನುಸರಿಸುತ್ತಾರೆ . ಕೃತಘ್ನರಾದ ವಿಶ್ವಾಸಘಾತಕರಲ್ಲದೆ ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳನ್ನು ನಿಷೇಧಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(31,33,'31:33','ಜನರೇ, ನಿಮ್ಮ ಪ್ರಭುವಿನ ಬಗ್ಗೆ ಜಾಗ್ರತೆ ಪಾಲಿಸಿರಿ. ಒಂದು ದಿನವನ್ನು ಭಯಪಡಿರಿ. ಅಂದು ಯಾವ ತಂದೆಯೂ ತನ್ನ ಮಗನ ಪರವಾಗಿ ಪರಿಹಾರಕೊಡಲಾರ. ಯಾವ ಮಗನೂ ತನ್ನ ತಂದೆಯ ಪರವಾಗಿ ಯಾವುದೇ ಪರಿಹಾರ ಕೊಡುವವನಲ್ಲ. ಅಲ್ಲಾಹನ ವಾಗ್ದಾನವು ಸತ್ಯವಾಗಿದೆ . ಆದುದರಿಂದ ಈ ಲೌಕಿಕ ಜೀವನವು ನಿಮ್ಮನ್ನು ಮೋಸಗೊಳಿಸದಿರಲಿ. ಮಹಾವಂಚಕ (ಶೈತಾನನು) ನಿಮ್ಮನ್ನು ಅಲ್ಲಾಹನ ವಿಷಯದಲ್ಲಿ ವಂಚಿಸದಿರಲಿ.');
INSERT INTO "kannada" VALUES(31,34,'31:34','ಅಂತಿಮ ಘಳಿಗೆಯ ಜ್ಞಾನ ಅಲ್ಲಾಹನ ಬಳಿಯಲ್ಲೇ ಇದೆ. ಅವನೇ ಮಳೆ ಸುರಿಸುತ್ತಾನೆ. ತಾಯಂದಿರ ಗರ್ಭಗಳಲ್ಲಿರುವುದು ಏನೆಂದು ಅವನು ತಿಳಿಯುತ್ತಾನೆ. ಯಾವ ಜೀವಿಯೂ ತಾನು ನಾಳೆ ಏನನ್ನು ಮಾಡುವೆ ಎಂಬುದನ್ನು ತಿಳಿದಿಲ್ಲ. ತಾನು ಯಾವ ಭೂಭಾಗದಲ್ಲಿ ಮರಣ ಹೊಂದುವೆನು ಎಂಬ ಅರಿವೂ ಯಾರಿಗೂ ಇಲ್ಲ. ನಿಜದಲ್ಲಿ ಅಲ್ಲಾಹನೇ ಸರ್ವಜ್ಞನೂ ಸೂಕ್ಷ್ಮಜ್ಞಾನಿಯೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(32,1,'32:1','ಅಲಿಫ್ ಲಾಮ್ ಮೀಮ್.');
INSERT INTO "kannada" VALUES(32,2,'32:2','ಈ ವೇದ ಗ್ರಂಥದ ಅವತೀರ್ಣದಲ್ಲಿ ಸಂದೇಹಕ್ಕೆ ಆಸ್ಪದವೇ ಇಲ್ಲ. ಇದು ವಿಶ್ವಗಳ ಒಡೆಯನಿಂದ ಅವತೀರ್ಣಗೊಂಡಿದೆ.');
INSERT INTO "kannada" VALUES(32,3,'32:3','ಇದನ್ನು ಈ ವ್ಯಕ್ತಿ ಸ್ವತಃ ಸೃಷ್ಟಿ ಮಾಡಿದನೆಂದು ಸತ್ಯನಿಷೇಧಿಗಳು ಹೇಳುತ್ತಾರೆಯೇ? ಅಲ್ಲ; ಇದು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಬಂದ ಸತ್ಯವಾಗಿದೆ. ಇದು ನಿಮಗಿಂತ ಮುಂಚೆ ಎಚ್ಚರಿಕೆ ನೀಡುವವರು ಬಾರದ ಒಂದು ಜನಾಂಗಕ್ಕೆ ನೀವು ಎಚ್ಚರಿಕೆ ನೀಡಲಿಕ್ಕಾಗಿದೆ. ತನ್ಮೂಲಕ ಅವರು ಸನ್ಮಾರ್ಗ ಪಡೆಯಲೆಂದು .');
INSERT INTO "kannada" VALUES(32,4,'32:4','ಅಲ್ಲಾಹು, ಆಕಾಶಗಳನ್ನೂ ಭೂಮಿಯನ್ನೂ ಅವುಗಳ ನಡುವೆ ಇರುವ ವಸ್ತುಗಳನ್ನೂ ಆರು ದಿನಗಳಲ್ಲಿ ಸೃಷ್ಟಿಸಿದವನು. ಅನಂತರ ಅವನಿಗೆ ಸಂಗತವಾದ ವಿಧದಲ್ಲಿ ಅರ್ಶ್‍ನ ಮೇಲೆ ಇಸ್ತಿವಾ ಹೊಂದಿದನು. ಅವನ ಹೊರತು ನಿಮಗೆ ಯಾರೂ ಸಹಾಯಕನಿಲ್ಲ. ಶಿಫಾರಸು ಮಾಡುವವನೂ ಇಲ್ಲ. ಹೀಗಿರುತ್ತ ನೀವೇಕೆ (ಈ ಯಥಾಥ್ರ್ಯವನ್ನು) ಯೋಚಿಸುವುದಿಲ್ಲ?');
INSERT INTO "kannada" VALUES(32,5,'32:5','ಅವನು ಆಕಾಶದಿಂದ ಭೂಮಿಯವರೆಗೆ ಎಲ್ಲ ಕಾರ್ಯಗಳನ್ನು ಸೂತ್ರಬದ್ಧವಾಗಿ ನಿಯಂತ್ರಿಸುತ್ತಿರುವನು. ಅನಂತರ ಒಂದು ದಿನದಲ್ಲಿ ಅದು ಅವನ ಕಡೆಗೆ ಏರಿ ಹೋಗುವುದು. ಆ ದಿನದ ಅಳತೆಯು (ಗಾತ್ರ) ನೀವು ಲೆಕ್ಕ ಹಾಕುವ ಸಾವಿರ ವರ್ಷವಾಗಿರುವುದು .');
INSERT INTO "kannada" VALUES(32,6,'32:6','ಅವನು ಸಕಲ ಪ್ರತ್ಯಕ್ಷಾಪ್ರತ್ಯಕ್ಷಗಳ ಪರಿಜ್ಞಾನಿ. ಅವನು ಪ್ರತಾಪಶಾಲಿ, ಕರುಣಾನಿಧಿ.');
INSERT INTO "kannada" VALUES(32,7,'32:7','ತಾನು ಸೃಷ್ಟಿಸಿದ ಎಲ್ಲ ವಸ್ತುಗಳನ್ನು ಅವನು ಚೆನ್ನಾಗಿಯೇ ಮಾಡಿರುತ್ತಾನೆ. ಅವನು ಮಾನವ ಸೃಷ್ಟಿಯನ್ನು ಕೊಜೆ ಮಣ್ಣಿನಿಂದ ಆರಂಭಿಸಿದನು.');
INSERT INTO "kannada" VALUES(32,8,'32:8','ನಂತರ ಅವನ ಸಂತತಿಯನ್ನು ದುರ್ಬಲ ಜಲದಿಂದ ಉತ್ಪತ್ತಿಯಾದ ರಕ್ತ ಗಡ್ಡೆಯಿಂದ ಉಂಟು ಮಾಡಿದನು.');
INSERT INTO "kannada" VALUES(32,9,'32:9','ತರುವಾಯ ಅದನ್ನು ಬೇಕಾದಂತೆ ಸ್ವರೂಪಗೊಳಿಸಿದನು ಮತ್ತು ಅದರೊಳಗೆ ತನ್ನ ಆತ್ಮ ವನ್ನು ಊದಿದನು. ನಿಮಗೆ ಕಿವಿಗಳನ್ನೂ ಕಣ್ಣುಗಳನ್ನೂ ಹೃದಯಗಳನ್ನೂ ಕೊಟ್ಟನು. (ಹೀಗಿದ್ದೂ) ನೀವು ಅಲ್ಪವೇ ಕೃತಜ್ಞರಾಗುತ್ತೀರಿ.');
INSERT INTO "kannada" VALUES(32,10,'32:10','“ನಾವು ಮಣ್ಣು ಪಾಲಾಗಿ ಹೋದ ಬಳಿಕ ಪುನಃ ಹೊಸತಾಗಿ ಸೃಷ್ಟಿಸಲ್ಪಡುತ್ತೇವೆಯೇ?” ಎಂದು ಸತ್ಯನಿಷೇಧಿಗಳು ಕೇಳುತ್ತಾರೆ. ಅಷ್ಟೇ ಅಲ್ಲ, ಅವರು ತಮ್ಮ ಪ್ರಭುವನ್ನು ಮುಖಾಮುಖಿ ಎದುರುಗೊಳ್ಳುವುದನ್ನು ನಿಷೇಧಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(32,11,'32:11','ತಾವು ಹೇಳಿರಿ, “ನಿಮ್ಮ ಮೇಲೆ ನಿಯಮಿತವಾದ ಮರಣದ ಮಲಕ್ ನಿಮ್ಮನ್ನು ಮೃತ್ಯುಗೊಳಿಸುವರು . ನಂತರ ನಿಮ್ಮನ್ನು ನಿಮ್ಮ ಪ್ರಭುವಿನೆಡೆಗೆ ಮರಳಿಸಲಾಗುವುದು”.');
INSERT INTO "kannada" VALUES(32,12,'32:12','ಅಪರಾಧಿಗಳು ತಮ್ಮ ಪ್ರಭುವಿನ ಮುಂದೆ ತಲೆ ತಗ್ಗಿಸಿ ನಿಂತಿರುವ ಸಂದರ್ಭವನ್ನು ನೀವು ನೋಡುತ್ತಿದ್ದರೆ, (ಆಗ ಅವರು ಹೇಳುವರು), “ನಮ್ಮ ಪ್ರಭೂ, (ನಾವು ನಿಷೇಧಿಸಿದ್ದನ್ನು) ನಾವು ಕಣ್ಣಾರೆ ಕಂಡೆವು ಮತ್ತು ಕೇಳಿದೆವು. ಆದ್ದರಿಂದ ನಮ್ಮನ್ನು (ಇಹಲೋಕಕ್ಕೆ) ಸತ್ಕರ್ಮವೆಸಗಲಿಕ್ಕಾಗಿ ಮರಳಿ ಕಳುಹಿಸಿಕೊಡು. ಈಗ ನಾವು ದೃಢವಾಗಿ ನಂಬಿದ್ದೇವೆ’’.');
INSERT INTO "kannada" VALUES(32,13,'32:13','ನಾವಿಚ್ಛಿಸುತ್ತಿದ್ದರೆ ಪ್ರತಿಯೊಂದು ಜೀವಕ್ಕೂ ಅದರ ಮಾರ್ಗದರ್ಶನ ನೀಡುತ್ತಿದ್ದೆವು. ಆದರೆ ‘ಜಿನ್ನ್ ಹಾಗೂ ಮಾನವರಿಂದ ನಾನು ನರಕವನ್ನು ತುಂಬಿ ಬಿಡುವೆನು’ ಎಂಬ ಮಾತು ನನ್ನಿಂದ ನಿಜಗೊಂಡಿದೆ.');
INSERT INTO "kannada" VALUES(32,14,'32:14','ಆದುದರಿಂದ ನೀವು ಇಂದಿನ ಭೇಟಿಯನ್ನು ಮರೆತು ಬಿಟ್ಟ ಕಾರಣ, ಅದರ ಶಿಕ್ಷೆಯನ್ನು ಸವಿಯಿರಿ. ನಾವು ಈಗ ನಿಮ್ಮನ್ನು ತೊರೆದು ಬಿಟ್ಟಿ ದ್ದೇವೆ (ಶಿಕ್ಷೆಗೆ ತಳ್ಳಿ ಬಿಟ್ಟಿದ್ದೇವೆ). ನಿಮ್ಮ ದುಷ್ಕøತ್ಯಗಳ ಫಲವಾಗಿ ಶಾಶ್ವತ ಶಿಕ್ಷೆಯ ರುಚಿಯನ್ನು ಸವಿಯಿರಿ.');
INSERT INTO "kannada" VALUES(32,15,'32:15','ನಮ್ಮ ವಚನಗಳನ್ನು ಓದಿ ಹೇಳಿ ಉಪದೇಶಿಸಿ ದಾಗ ಸಾಷ್ಟಾಂಗ ಬೀಳುವ ಮತ್ತು ತಮ್ಮ ಪ್ರಭುವಿನ ಸ್ತುತಿಯೊಂದಿಗೆ ಅವನ ಪರಿಶುದ್ಧತೆಯನ್ನು ಪ್ರಕೀರ್ತನೆ ಮಾಡುವವರು ಮಾತ್ರವೇ ನಮ್ಮ ವಚನಗಳನ್ನು ನಂಬಬಲ್ಲರು. ಅವರು ಜಂಭ ತೋರಲಾರರು.');
INSERT INTO "kannada" VALUES(32,16,'32:16','(ಶಿಕ್ಷೆಯ) ಭಯ ಹಾಗೂ (ಕರುಣೆಯ) ನಿರೀಕ್ಷೆ ಯೊಂದಿಗೆ ತಮ್ಮ ಪ್ರಭುವಿನಲ್ಲಿ ಪ್ರಾರ್ಥಿಸಲು ಚಾಪೆಗಳಿಂದ (ಎದ್ದೇಳುವ ಮೂಲಕ) ಅವರ ಪಾಶ್ರ್ವಗಳು ಅಗಲುತ್ತವೆ. ನಾವು ಅವರಿಗೆ ನೀಡಿದವುಗಳಿಂದ (ಧರ್ಮಮಾರ್ಗದಲ್ಲಿ) ವೆಚ್ಚವನ್ನು ಮಾಡುವರು.');
INSERT INTO "kannada" VALUES(32,17,'32:17','ಅವರ ಕರ್ಮಗಳಿಗೆ ಪ್ರತಿಫಲವಾಗಿ ಅವರ ಕಣ್ಣುಗಳಿಗೆ ತಂಪೆರೆಯುವ ಯಾವ ಸತ್ಫಲಗಳನ್ನು ಅವರಿಗಾಗಿ ಗುಪ್ತವಾಗಿಡಲಾಗಿದೆಯೆಂದು ಯಾವನೂ ಅರಿಯನು.');
INSERT INTO "kannada" VALUES(32,18,'32:18','ಅಂದ ಮೇಲೆ ಸತ್ಯವಿಶ್ವಾಸಿಯಾದವನು ಧಿಕ್ಕಾರಿಯಂತೆ ಆಗಬಲ್ಲನೇ? ಅವರು ಸಮಾನರಲ್ಲ.');
INSERT INTO "kannada" VALUES(32,19,'32:19','ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿದ ಮತ್ತು ಸತ್ಕರ್ಮವೆಸಗಿದ ಜನರಿಗೆ ಅವರ ಸುಕೃತಗಳ ಪ್ರತಿಫಲವಾಗಿ ಕಾದಿರಿಸಿದ ಆತಿಥ್ಯವಾಗಿ ವಾಸಯೋಗ್ಯ ಉದ್ಯಾನಗಳಿವೆ ಂ.');
INSERT INTO "kannada" VALUES(32,20,'32:20','ಧರ್ಮಭ್ರಷ್ಟ ಪ್ರವೃತ್ತಿ ಕೈಗೊಂಡವರ ವಾಸಸ್ಥಾನವು ನರಕಾಗ್ನಿಯಾಗಿದೆ. ಅವರು ಅಲ್ಲಿಂದ ಹೊರ ಹೋಗಲಿಚ್ಛಿಸಿದಾಗಲೆಲ್ಲ ಅದರೊಳಕ್ಕೆ ಪುನಃ ತಳ್ಳಲ್ಪಡುವರು . ‘ನೀವು ಸುಳ್ಳಾಗಿಸುತ್ತಿದ್ದ ನರಕಾಗ್ನಿಯ ರುಚಿಯನ್ನು ಸವಿಯಿರಿ’ ಎಂದು ಅವರಿಗೆ ಹೇಳಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(32,21,'32:21','ಮಹಾ ಶಿಕ್ಷೆಗೆ ಮುಂಚೆ ಈ ಲೋಕದಲ್ಲೇ ನಾವು ಸಣ್ಣ ಶಿಕ್ಷೆಯ ರುಚಿಯನ್ನು ಇವರಿಗೆ ಉಣಿಸುತ್ತೇವೆ. ಅವರು (ದಿಕ್ಕಾರದಿಂದ) ಮರಳಲಿಕ್ಕಾಗಿ .');
INSERT INTO "kannada" VALUES(32,22,'32:22','ತನ್ನ ಪ್ರಭುವಿನ ನಿದರ್ಶನಗಳ ಮೂಲಕ ಉಪದೇಶ ನೀಡಲಾದ ಬಳಿಕವೂ ಅವುಗಳಿಂದ ವಿಮುಖನಾಗುವವನಿಗಿಂತ ದೊಡ್ಡ ಅಕ್ರಮಿ ಯಾರಿದ್ದಾನೆ? ಅಪರಾಧಿಗಳನ್ನು ನಾವು ಖಂಡಿತ ಶಿಕ್ಷಿಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(32,23,'32:23','ನಾವು ಮೂಸಾರಿಗೆ ಗ್ರಂಥ ನೀಡಿದ್ದೇವೆ. ಅವರನ್ನು ಭೇಟಿಯಾಗುವ ಬಗ್ಗೆ ತಾವು ಸಂಶಯದಲ್ಲಿ ಬೀಳದಿರಿ . ಅದನ್ನು (ಆ ಗ್ರಂಥವನ್ನು) ನಾವು ಇಸ್‍ರಾ ಈಲ ಜನಾಂಗಕ್ಕೆ ಸನ್ಮಾರ್ಗದರ್ಶಕವಾಗಿ ಮಾಡಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(32,24,'32:24','ನಮ್ಮ ಅಣತಿ ಪ್ರಕಾರ ಜನರಿಗೆ ಮಾರ್ಗದರ್ಶನ ನೀಡುವ ಮುಂದಾಳುಗಳನ್ನು ಅವರಿಂದ ನಾವು ರೂಪಿಸಿದೆವು. ಅವರು ಪಾಲಿಸಿದ ಸಹನೆಗಾಗಿ, ಅವರು ನಮ್ಮ ನಿದರ್ಶನಗಳಲ್ಲಿ ದೃಢ ವಿಶ್ವಾಸಿಗಳಾಗಿದ್ದರು .');
INSERT INTO "kannada" VALUES(32,25,'32:25','ಅವರು ಪರಸ್ಪರ ಭಿನ್ನಾಭಿಪ್ರಾಯ ಹೊಂದಿರುವ ವಿಷಯದಲ್ಲಿ ನಿಮ್ಮ ಪ್ರಭು ಪುನರುತ್ಥಾನ ದಿನ ಅವರ ನಡುವೆ ತೀರ್ಮಾನವನ್ನು ಮಾಡುವನು.');
INSERT INTO "kannada" VALUES(32,26,'32:26','ಇವರಿಗೆ ಮುಂಚೆ ಎಷ್ಟು ಜನಾಂಗಗಳನ್ನು ನಾವು ನಾಶಗೊಳಿಸಿದ್ದೇವೆ ಎಂಬ ವಿಚಾರವು ಇವರಿಗೆ ದಾರಿತೋರಿಲ್ಲವೇ? ಅವರು ವಾಸಿಸುತ್ತಿದ್ದ ಸ್ಥಳಗಳಲ್ಲಿ ಈಗ ಇವರು ಸಂಚರಿಸುತ್ತಾರೆ . ಇದರಲ್ಲಿ ದೊಡ್ಡ ನಿದರ್ಶನಗಳಿವೆ. ಹೀಗಿದ್ದೂ ಇವರೇಕೆ ಕಿವಿಗೊಡುವುದಿಲ್ಲ?');
INSERT INTO "kannada" VALUES(32,27,'32:27','ಬರಡು ನೆಲಕ್ಕೆ ನಾವು ನೀರು ಕುಡಿಸುವುದನ್ನು ಇವರು ಕಂಡಿಲ್ಲವೇ? ಆ ಮೂಲಕ ಇವರೂ ಇವರ ಜಾನುವಾರುಗಳೂ ತಿನ್ನುವ ಬೆಳೆಯನ್ನು ನಾವು ಬೆಳೆಸುತ್ತೇವೆ. ಹೀಗಿದ್ದೂ ಇವರು ನೋಡಿ ತಿಳಿಯುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(32,28,'32:28','‘ನೀವು ಸತ್ಯವಾದಿಯಾಗಿದ್ದರೆ, ಈ ಗೆಲುವು ಯಾವಾಗ?’ ಎಂದು ಇವರು ಕೇಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(32,29,'32:29','(ಪ್ರವಾದಿಯರೇ) ಹೇಳಿರಿ, ಗೆಲುವಿನ ದಿನ ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ತಮ್ಮ ವಿಶ್ವಾಸ ಪ್ರಯೋಜನವಾಗದು. ಅವರಿಗೆ (ಶಿಕ್ಷೆಯನ್ನು) ಸ್ವಲ್ಪವೂ ಮುಂದೂಡಲಾಗುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(32,30,'32:30','ಆದ್ದರಿಂದ ತಾವು ಅವರನ್ನು ನಿರ್ಲಕ್ಷಿಸಿರಿ ಮತ್ತು ಕಾಯುತ್ತಲಿರಿ. ಇವರೂ ಕಾಯುವವರೇ ಸರಿ. ');
INSERT INTO "kannada" VALUES(33,1,'33:1','ಓ ಪ್ರವಾದಿವರ್ಯರೇ, ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ . ಸತ್ಯನಿಷೇಧಿಗಳು ಹಾಗೂ ಕಪಟ ವಿಶ್ವಾಸಿಗಳನ್ನು ತಾವು ಅನುಸರಿಸಬೇಡಿರಿ . ಅಲ್ಲಾಹನು ಸರ್ವಜ್ಞನೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(33,2,'33:2','ನಿಮ್ಮ ಪ್ರಭುವಿನಿಂದ ನಿಮಗೆ ನೀಡಲಾಗುತ್ತಿರುವ ಬೋಧನೆಯನ್ನು ಅನುಸರಿಸಿರಿ. ಅಲ್ಲಾಹನು, ನೀವು ಮಾಡುತ್ತಿರುವ ಪ್ರವೃತ್ತಿಯ ಬಗ್ಗೆ ಚೆನ್ನಾಗಿ ಅರಿವುಳ್ಳವನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(33,3,'33:3','ತಾವು ಅಲ್ಲಾಹನ ಮೇಲೆ ಭರವಸೆ ಇಡಿರಿ. ಕಾರ್ಯ ನಿರ್ವಾಹಕನಾಗಲು ಅಲ್ಲಾಹನೇ ಸಾಕು.');
INSERT INTO "kannada" VALUES(33,4,'33:4','ಅಲ್ಲಾಹು ಯಾವ ಮನುಷ್ಯನ ದೇಹದೊಳಗೂ ಎರಡು ಹೃದಯಗಳನ್ನಿರಿಸಿಲ್ಲ. ನೀವು ಳಿಹಾರ್ ಮಾಡುವ ನಿಮ್ಮ ಪತ್ನಿಯರನ್ನು ಅವನು ನಿಮ್ಮ ತಾಯಂದಿರನ್ನಾಗಿ ಮಾಡಿಲ್ಲ. ಅವನು ನಿಮ್ಮ ದತ್ತು ಪುತ್ರರನ್ನು ನಿಮ್ಮ ಸ್ವಂತ ಪುತ್ರರನ್ನಾಗಿ ಮಾಡಿಲ್ಲ. ಇವೆಲ್ಲ ನೀವು ನಿಮ್ಮ ಬಾಯಿಯಿಂದ ಹೇಳುವ ನಿಮ್ಮ ಮಾತುಗಳು. ಆದರೆ ಅಲ್ಲಾಹನು ಕೇವಲ ನಿಜವನ್ನು ಹೇಳುತ್ತಾನೆ. ಅವನೇ ಸನ್ಮಾರ್ಗಕ್ಕೆ ನಡೆಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(33,5,'33:5','ದತ್ತು ಮಕ್ಕಳನ್ನು ಅವರ ಪಿತರಿಗೆ ಸೇರಿಸಿ ಕರೆಯಿರಿ. ಇದು ಅಲ್ಲಾಹನ ಬಳಿ ಹೆಚ್ಚು ನ್ಯಾಯೋಚಿತ. ಇನ್ನು ನಿಮಗೆ ಅವರ ಪಿತರು ಯಾರೆಂದು ತಿಳಿದಿರದಿದ್ದರೆ ಅವರು ಧರ್ಮದಲ್ಲಿ ನಿಮ್ಮ ಸಹೋದರರು ಮತ್ತು ಮಿತ್ರರು. ನೀವು ತಪ್ಪಿ ಹೇಳಿಬಿಟ್ಟ ಮಾತಿನಲ್ಲಿ ನಿಮ್ಮ ಮೇಲೆ ದೋಷ ವಿಲ್ಲ. ಆದರೆ ನೀವು ಮನಸ್ಸಾರೆ ಹೇಳಿದ ಮಾತಿನ ಬಗ್ಗೆ ದೋಷವಿದೆ. ಅಲ್ಲಾಹು ಕ್ಷಮಾಶೀಲನೂ ಕರುಣಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(33,6,'33:6','ಪ್ರವಾದಿಯು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಅವರ ಸ್ವಂತ ದೇಹಗಳಿಗಿಂತಲೂ ಮುಖ್ಯರು. ಪ್ರವಾದಿಯ ಪತ್ನಿಯರು ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಮಾತೆಯರು . ಆದರೆ ಅಲ್ಲಾಹನ ನಿಯಮದಲ್ಲಿ ಇತರ ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಹಾಗೂ ಮುಹಾಜಿರರಿಗಿಂತಲೂ ರಕ್ತ-ಸಂಬಂಧಿಕರು ಪರಸ್ಪರರ ಪಾಲಿಗೆ ಹೆಚ್ಚು ಹಕ್ಕುದಾರರು. ಆದರೆ ನಿಮ್ಮ ಮಿತ್ರರಿಗೆ ನೀವು ಉಪಕಾರ ಮಾಡಿದರೆ ಅಡ್ಡಿಯಿಲ್ಲ. ಇದೆಲ್ಲ ದೇವಗ್ರಂಥದಲ್ಲಿ ದಾಖಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(33,7,'33:7','(ಪೈಗಂಬರರೇ,) ಪ್ರವಾದಿಗಳಿಂದಲೂ ನಿಮ್ಮಿಂದಲೂ, ನೂಹ್, ಇಬ್‍ರಾಹೀಮ್, ಮೂಸಾ ಮತ್ತು ಮರ್ಯಮರ ಪುತ್ರ ಈಸಾರಿಂದಲೂ ನಾವು ಅವರ ಕರಾರನ್ನು ಪಡೆದ ಸಂದರ್ಭವನ್ನು ನೆನಪಿಡಿರಿ. ಅವರಿಂದ ನಾವು ಬಲವಾದ ಕರಾರನ್ನು ಪಡೆದಿದ್ದೇವೆ .');
INSERT INTO "kannada" VALUES(33,8,'33:8','ಇದು, ಸತ್ಯಸಂಧರಿಂದ ಅವರ ಸತ್ಯಸಂಧತೆಯ ಕುರಿತು ಅಲ್ಲಾಹನು ಪ್ರಶ್ನಿಸಲಿಕ್ಕಾಗಿ ಇದೆ . ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಅವನು ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆಯನ್ನು ಸಿದ್ಧಪಡಿಸಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(33,9,'33:9','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಸೈನ್ಯಗಳು ನಿಮಗೆ ಬಂದಾಗ ನಾವು ಅವರ ಮೇಲೆ ಬಿರುಗಾಳಿಯನ್ನೂ ನಿಮಗೆ ಕಾಣಿಸದಿರುವ ಸೈನ್ಯಗಳನ್ನೂ ಕಳುಹಿಸಿದ ಸಂದರ್ಭದಲ್ಲಿ ಅಲ್ಲಾಹನು ನಿಮಗೆ ಮಾಡಿದ ಅನುಗ್ರಹವನ್ನು ಸ್ಮರಿಸಿರಿ . ನೀವು ಮಾಡುತ್ತಿದ್ದುದನ್ನು ಅಲ್ಲಾಹು ಚೆನ್ನಾಗಿ ನೋಡುತ್ತಿದ್ದನು.');
INSERT INTO "kannada" VALUES(33,10,'33:10','ಅಂದರೆ ಮೇಲಿಂದಲೂ ಕೆಳಗಿಂದಲೂ ಆ ಸೈನ್ಯಗಳು ನಿಮಗೆ ಏರಿಬಂದಾಗ, ಭಯದಿಂದ ಕಣ್ಣುಗಳು ನಿಶ್ಚಲಗೊಂಡಾಗ, ಹೃದಯಗಳು ಗಂಟಲಿಗೆ ಬಂದಾಗ ಮತ್ತು ನೀವು ಅಲ್ಲಾಹನ ವಿಷಯದಲ್ಲಿ ಕೆಲವು ಗುಮಾನಿಗಳನ್ನಿರಿಸಿದಾಗ,');
INSERT INTO "kannada" VALUES(33,11,'33:11','ಅಲ್ಲಿ ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಚೆನ್ನಾಗಿ ಪರೀಕ್ಷಿಸಲ್ಪಟ್ಟರು ಮತ್ತು ತೀವ್ರವಾಗಿ ನಡುಗಿಸಲ್ಪಟ್ಟರು.');
INSERT INTO "kannada" VALUES(33,12,'33:12','ಕಪಟವಿಶ್ವಾಸಿಗಳೂ ಹೃದಯಗಳಲ್ಲಿ ರೋಗ ವಿದ್ದವರೂ ‘ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ಪ್ರವಾದಿ ನಮಗೆ ವಂಚನೆಯನ್ನಲ್ಲದೆ ಇನ್ನೇನನ್ನೂ ವಾಗ್ದಾನ ಮಾಡಿಲ್ಲ’ ಎಂದು ಹೇಳುತ್ತಿದ್ದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(33,13,'33:13','ಅವರಲ್ಲೊಂದು ತಂಡವು, ‘ಯಸ್ರಿಬ್‍ನವರೇ, ನಿಮಗಿನ್ನು ಯಾವುದೇ ನೆಲೆ ಇಲ್ಲ. ಆದ್ದರಿಂದ ಮರಳಿ ಹೋಗಿರಿ’ ಎಂದು ಹೇಳಿದ ಸಂದರ್ಭವೂ ಸ್ಮರಣೀಯ. ಅವರ ಒಂದು ಕೂಟವು, ನಮ್ಮ ಮನೆಗಳು ಅಪಾಯ ಕ್ಕೊಳಗಾಗಿವೆ ಎನ್ನುತ್ತ ತಮ್ಮ ಪ್ರವಾದಿಯಿಂದ (ರಣರಂಗದಿಂದ ಪರಾರಿಯಾಗಲು) ಅನುಮತಿ ಕೇಳಿದರು. ಅವು ನಿಜವಾಗಿ ಅಪಾಯದಲ್ಲಿರಲಿಲ್ಲ. ಆದರೆ ಅವರು ರಣರಂಗದಿಂದ ಪರಾರಿಯಾಗಲು ಬಯಸಿದ್ದರು.');
INSERT INTO "kannada" VALUES(33,14,'33:14','ಮದೀನಾದ ನಾನಾ ಭಾಗಗಳಿಂದ ಶತ್ರುಗಳು ನುಗ್ಗಿ ಬರುತ್ತಿದ್ದರೆ ಹಾಗೂ ನಂತರ (ಮುಸ್ಲಿಮರ ವಿರುದ್ಧ) ಫಿತ್ನಾ ಮಾಡಲು ಇವರಲ್ಲಿ ಕೇಳಿಕೊಂಡಿದ್ದರೆ ಇವರು ಖಂಡಿತ ಹಾಗೆ ಮಾಡುತ್ತಿದ್ದರು. ಇದಕ್ಕೆ ಸ್ವಲ್ಪ ಸಮಯವನ್ನು ತೆಗೆದು ಕೊಳ್ಳುತ್ತಿದ್ದರೇ ವಿನಾ ಹೆಚ್ಚು ತಡ ಮಾಡುತ್ತಿರಲಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(33,15,'33:15','ತಾವು (ರಣರಂಗದಿಂದ) ಬೆನ್ನು ತಿರುಗಿಸಿ ಹೋಗುವುದಿಲ್ಲವೆಂದು ಇದಕ್ಕೆ ಮುಂಚೆ ಇವರು ಅಲ್ಲಾಹನಲ್ಲಿ ಕರಾರು ಮಾಡಿದ್ದರು. ಅಲ್ಲಾಹನೊಂದಿಗೆ ಮಾಡಿದ ಕರಾರಿನ ಬಗ್ಗೆ ವಿಚಾರಣೆ ಮಾಡಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(33,16,'33:16','(ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ; “ನೀವು ಮರಣ ಅಥವಾ ಕೊಲೆಯನ್ನು ಹೆದರಿ ಓಡುವುದಿದ್ದರೆ ನಿಮ್ಮ ಈ ಪಲಾಯನವು ನಿಮಗೇನೂ ಫಲಕಾರಿಯಾಗದು. ನೀವು ಓಡಿಹೋದರೂ ಅಲ್ಪಕಾಲದ ಹೊರತು ನಿಮಗೆ ಜೀವನದ ಸುಖವನ್ನು ಅನುಭವಿಸಲು ಸಾಧ್ಯವಿಲ್ಲ”.');
INSERT INTO "kannada" VALUES(33,17,'33:17','ಹೇಳಿರಿ. “ಅಲ್ಲಾಹು ನಿಮಗೆ ಕೇಡು ಮಾಡಲಿಚ್ಛಿಸಿದರೆ ಅಥವಾ ಅವನು ನಿಮ್ಮ ಮೇಲೆ ಕರುಣೆ ತೋರಲಿಚ್ಛಿಸಿದರೆ, ಅವನಿಂದ ನಿಮ್ಮನ್ನು ತಡೆ ಯುವವ ನಾರು? ಅಲ್ಲಾಹನ ಹೊರತು ಇವರು ಯಾವ ರಕ್ಷಕನನ್ನೂ ಸಹಾಯಕನನ್ನೂ ಪಡೆಯಲಾರರು.');
INSERT INTO "kannada" VALUES(33,18,'33:18','ನಿಮ್ಮ ಪೈಕಿ ಉದಾಸೀನ ಮಾಡಿಸುವವರನ್ನೂ `ನಮ್ಮ ಕಡೆಗೆ ಬನ್ನಿರಿ’ ಎಂದು ತಮ್ಮ ಸಹೋದರ ರಿಗೆ ಹೇಳುವವರನ್ನೂ ಅಲ್ಲಾಹು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು. ಅವರು ಬಹಳ ಅಪೂರ್ವವಾಗಿಯೇ ಯುದ್ಧದಲ್ಲಿ ಭಾಗವಹಿಸುವರು.');
INSERT INTO "kannada" VALUES(33,19,'33:19','ಅವರು ನಿಮ್ಮ ವಿರುದ್ಧ ದುರಾಗ್ರಹ ಹೊಂದಿದವ ರಾಗಿ, ಭೀತಿ ಎದುರಾದರೆ ಅವರು ಪ್ರಾಣ ಸಂಕಟ ಬಂದವರಂತೆ ಕಣ್ಣು ತಿರುಗಿಸುತ್ತಾ ನಿಮ್ಮ ಕಡೆಗೆ ನೋಡುವುದನ್ನು ನೀವು ಕಾಣಬಹುದು. ಆದರೆ ಭೀತಿ ನೀಗಿದ ಬಳಿಕ ಅದೇ ಜನರು ಯುದ್ಧಾಸ್ತಿಯ ಬಗ್ಗೆ ನಿಮ್ಮ ವಿರುದ್ಧ ದುರಾಶೆ ಯಿಟ್ಟುಕೊಂಡು ಹರಿತವಾದ ನಾಲಗೆಯಿಂದ ನಿಮ್ಮ ಸ್ಥೈರ್ಯ ಕೆಡಿಸುತ್ತಾರೆ. ಅವರು ಸತ್ಯ ವಿಶ್ವಾಸವನ್ನು ಸ್ವೀಕರಿಸಲಿಲ್ಲ. ಹೀಗಾಗಿ ಅಲ್ಲಾಹನು ಅವರ ಕರ್ಮಗಳನ್ನು ಹರಿದೊಗೆದನು. ಇದು ಅಲ್ಲಾಹನಿಗೆ ಬಹಳ ಸುಲಭದ ಕಾರ್ಯ.');
INSERT INTO "kannada" VALUES(33,20,'33:20','ಸಂಘ ಸೇನೆಗಳು ಇನ್ನೂ ಹೋಗಿಲ್ಲವೆಂದು ಇವರು (ಕಪಟಿಗಳು) ಭಾವಿಸುತ್ತಾರೆ. ಇನ್ನು ಸಂಘ ಸೇನೆಗಳು ಪುನಃ ದಂಡೆತ್ತಿ ಬಂದರೆ ಹಳ್ಳಿಗರ ನಡುವೆ ನೆಲೆಸಿ ನಿಮ್ಮ ಸ್ಥಿತಿಗತಿಗಳನ್ನು ವಿಚಾರಿಸಿ ಕೊಂಡು ಇರಬಹುದೆಂದು ಅವರಿಗೆ ಅನಿಸುತ್ತದೆ. ಒಂದು ವೇಳೆ ಅವರು ನಿಮ್ಮ ಜೊತೆಗಿದ್ದರೂ ಅಲ್ಪ ಮಾತ್ರವಲ್ಲದೆ ಯುದ್ಧ ಮಾಡಲಾರರು.');
INSERT INTO "kannada" VALUES(33,21,'33:21','ಅಲ್ಲಾಹನ ಸಂದೇಶವಾಹಕರಲ್ಲಿ ಖಂಡಿತ ನಿಮಗೆ ಅತ್ಯುತ್ತಮ ಮಾದರಿ ಇದೆ . ಇದು ಅಲ್ಲಾಹು ಹಾಗೂ ಅಂತಿಮ ದಿನವನ್ನು ನಿರೀಕ್ಷಿಸುವವನಿಗೆ (ಭಯಪಡುವವನಿಗೆ) ಮತ್ತು ಅಲ್ಲಾಹನನ್ನು ಬಹಳವಾಗಿ ಸ್ಮರಿಸುವವನಿಗೆ.');
INSERT INTO "kannada" VALUES(33,22,'33:22','ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಸಂಘ ಸೇನೆಗಳನ್ನು ಕಂಡಾಗ ``ಇದುವೇ ಅಲ್ಲಾಹು ಮತ್ತು ಪೈಗಂಬರರು ನಮಗೆ ವಾಗ್ದಾನ ಮಾಡಿದ್ದುದು. ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ಸಂದೇಶವಾಹಕರ ಮಾತು ನಿಜವಾಗಿತ್ತು” ಎಂದು ಹೇಳಿದರು. ಈ ವಾಗ್ದಾನವು ಅವರ ಸತ್ಯವಿಶ್ವಾಸವನ್ನೂ ಅವರ ಸಮರ್ಪಣೆಯನ್ನೂ ಇನ್ನಷ್ಟು ಹೆಚ್ಚಿಸಿತು .');
INSERT INTO "kannada" VALUES(33,23,'33:23','ಅಲ್ಲಾಹನೊಡನೆ ಮಾಡಿದ ಕರಾರನ್ನು ನಿಜ ವೆಂದು ಸಾಧಿಸಿ ತೋರಿಸಿದ ಕೆಲವು ಪುರುಷರು ಸತ್ಯವಿಶ್ವಾಸಿ ಗಳಲ್ಲಿದ್ದಾರೆ. ಅವರಲ್ಲಿ ಕೆಲವರು (ಹುತಾತ್ಮರಾಗುವೆವು ಎಂಬ) ತಮ್ಮ ಹರಕೆಯನ್ನು ಸಂದಾಯ ಮಾಡಿದರು. ಇನ್ನು ಕೆಲವರು ಸಮಯ ಕಾಯುವವರು. ಅವರು ತಮ್ಮ ಕರಾರಿನಲ್ಲಿ ಏನೂ ಬದಲಾವಣೆ ಮಾಡಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(33,24,'33:24','ಅಲ್ಲಾಹು ಸತ್ಯಸಂಧರಿಗೆ ಅವರ ಸತ್ಯಸಂಧತೆಯ ಸತ್ಫಲ ನೀಡಲಿಕ್ಕೂ ತಾನಿಚ್ಛಿಸಿದರೆ ಕಪಟವಿಶ್ವಾಸಿ ಗಳನ್ನು ಶಿಕ್ಷಿಸಲಿಕ್ಕೂ ಅಥವಾ ಅವರ ಪಶ್ಚಾತ್ತಾಪ ವನ್ನು ಸ್ವೀಕರಿಸಲಿಕ್ಕೂ (ಇಂಥ ಪರೀಕ್ಷಾ ಘಟ್ಟಗ ಳನ್ನು ಅಲ್ಲಾಹು ಏರ್ಪಡಿಸಿದನು). ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹು ಕ್ಷಮಾಶೀಲನೂ ಕೃಪಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(33,25,'33:25','ಅಲ್ಲಾಹನು ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ಅವರ ಕೋಪ ದೊಂದಿಗೆ (ಯುದ್ಧರಂಗದಿಂದ ಪ್ರಯೋಜನ ಪಡೆಯದೆ) ಮರಳಿ ಕಳುಹಿಸಿದನು. ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಅಲ್ಲಾಹನು ಯುದ್ಧವನ್ನು ಸಾಕು ಮಾಡಿದನು. ಅಲ್ಲಾಹು ಪ್ರಬಲನೂ ಅಜೇಯನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(33,26,'33:26','ಗ್ರಂಥದವರಲ್ಲಿ ಅವಿಶ್ವಾಸಿಗಳಿಗೆ ನೆರವಾದವರನ್ನು ಅಲ್ಲಾಹ್ ಅವರ ಕೋಟೆಗಳಿಂದ ಇಳಿಸಿ ತಂದನು ಮತ್ತು ಅವರ ಹೃದಯಗಳಲ್ಲಿ ಭೀತಿಯನ್ನಿರಿಸಿದನು. ಅವರ ಒಂದು ಗುಂಪನ್ನು ನೀವು ವಧಿಸಿದಿರಿ ಮತ್ತು ಇನ್ನೊಂದು ಗುಂಪನ್ನು ಸೆರೆಹಿಡಿದಿರಿ.');
INSERT INTO "kannada" VALUES(33,27,'33:27','ಅವರ ಜಮೀನುಗಳನ್ನು, ಅವರ ಮನೆಗಳನ್ನು ಮತ್ತು ಅವರ ಸಂಪತ್ತುಗಳನ್ನು ಹಾಗೂ ನೀವು (ಈ ತನಕ) ಕಾಲಿಟ್ಟಿರದ ಭೂಭಾಗವನ್ನು ಅವನು ನಿಮಗೆ ಉತ್ತರಾಧಿಕಾರ ಹಕ್ಕನ್ನಾಗಿ ಮಾಡಿ ಕೊಟ್ಟನು. ಅಲ್ಲಾಹನು ಸಕಲ ವಿಷಯಗಳ ಸಾಮಥ್ರ್ಯವುಳ್ಳವನು.');
INSERT INTO "kannada" VALUES(33,28,'33:28','ಪೈಗಂಬರರೇ ನಿಮ್ಮ ಪತ್ನಿಯರಿಗೆ ಹೇಳಿರಿ; ನೀವು ಈ ಲೋಕ ಮತ್ತು ಇದರ ಅಲಂಕಾರವನ್ನು ಇಚ್ಛಿಸುತ್ತೀರಾದರೆ, ಬನ್ನಿರಿ. ನಾನು ನಿಮಗೆ `ಮುತ್‍ಅತ್’ (ಮುಕ್ತಿದ್ರವ್ಯ) ಕೊಡಿಸಿ ನಿಮ್ಮನ್ನು ಉತ್ತಮ ರೀತಿಯಲ್ಲಿ ಬೀಳ್ಕೊಡುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(33,29,'33:29','ನೀವು ಅಲ್ಲಾಹು, ಅವನ ಸಂದೇಶವಾಹಕರು ಮತ್ತು ಪರಲೋಕವನ್ನು ಇಚ್ಚಿಸುತ್ತೀರಾದರೆ, ನಿಮ್ಮಲ್ಲಿ ಗುಣವಂತರಿಗೆ ಅಲ್ಲಾಹು ಘನ ಸತ್ಫಲ ವನ್ನು ಸಿದ್ಧಗೊಳಿಸಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(33,30,'33:30','ಪೈಗಂಬರರ ಪತ್ನಿಯರೇ, ನಿಮ್ಮಲ್ಲಿ ಯಾರಾದರೂ ವ್ಯಕ್ತವಾದ ಅಶ್ಲೀಲ ಕಾರ್ಯವೆಸಗಿದರೆ ಅವಳಿಗೆ ಇಮ್ಮಡಿ ಯಾತನೆ ಕೊಡಲಾಗುವುದು. ಇದು ಅಲ್ಲಾಹನಿಗೆ ಅತಿ ಸುಲಭವಾದ ಕೆಲಸ .');
INSERT INTO "kannada" VALUES(33,31,'33:31','ನಿಮ್ಮಲ್ಲಿ ಯಾರು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ಸಂದೇ ಶವಾಹಕರ ಅನುಸರಣೆ ಮಾಡುತ್ತಾರೆ, ಮತ್ತು ಸತ್ಕರ್ಮವೆಸಗುತ್ತಾರೆ, ಆಕೆಗೆ ನಾವು ಇಮ್ಮಡಿ ಸತ್ಫಲ ಕೊಡುತ್ತೇವೆ. ನಾವು ಅವಳಿಗೆ ಗೌರವ ಪೂರ್ಣ ಜೀವನಾಧಾರವನ್ನು ಸಿದ್ಧಗೊಳಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(33,32,'33:32','ಪ್ರವಾದಿಯ ಪತ್ನಿಯರೇ, ನೀವು ಬೇರೆ ಸ್ತ್ರೀಯರಂತಲ್ಲ. ನೀವು ಭಯಭಕ್ತಿ (ಸೂಕ್ಷ್ಮತೆ) ಹೊಂದಿದ್ದಲ್ಲಿ. ಆದ್ದರಿಂದ (ಪುರುಷರೊಡನೆ) ಮೃದು ಮಾತು ಆಡದಿರಿ. ಮನದಲ್ಲಿ ರೋಗ ವಿದ್ದವನು ಮೋಹಿಸಿಬಿಟ್ಟಾನು . ನೀವು ಹಸನಾ ದ ನುಡಿಯನ್ನಾಡಿರಿ.');
INSERT INTO "kannada" VALUES(33,33,'33:33','ನಿಮ್ಮ ಮನೆಗಳಲ್ಲೇ ಅಡಗಿದ್ದು ಕೊಳ್ಳಿರಿ . ಗತ ಅಜ್ಞಾನ ಕಾಲದ ಸೌಂದರ್ಯ ಪ್ರದರ್ಶನದಂತೆ ನೀವು ಮೈಮಾಟವನ್ನು ಪ್ರದರ್ಶಿಸಬೇಡಿರಿ. ನಮಾಝನ್ನು ಸಂಸ್ಥಾಪಿಸಿರಿ, ಝಕಾತ್ ಕೊಡಿರಿ, ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ರಸೂಲರನ್ನು ಅನುಸರಿಸಿರಿ. ರಸೂಲರ ಮನೆಯವರೇ, ನಿಮ್ಮಿಂದ ಮಾಲಿನ್ಯವನ್ನು ದೂರೀಕರಿಸಲು ಮತ್ತು ನಿಮ್ಮನ್ನು ಚೆನ್ನಾಗಿ ಶುದ್ಧಿಗೊಳಿಸಲು ಅಲ್ಲಾಹು ಇಚ್ಚಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(33,34,'33:34','ನಿಮ್ಮ ಮನೆಗಳಲ್ಲಿ ಓದಿ ಹೇಳಲಾಗುತ್ತಿರುವ ಅಲ್ಲಾಹನ ಸೂಕ್ತಗಳನ್ನೂ ಯುಕ್ತಿಪೂರ್ಣ ಜ್ಞಾನಗಳನ್ನೂ ಚೆನ್ನಾಗಿ ನೆನಪಿಡಿರಿ. ಅಲ್ಲಾಹು ಸೂಕ್ಷ್ಮಜ್ಞನೂ ವಿವರಪೂರ್ಣನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(33,35,'33:35','ನಿಶ್ಚಯವಾಗಿಯೂ ಅನುಸರಣಶೀಲರು, ಅನು ಸರಣಶೀಲೆಯರು, ಸತ್ಯವಿಶ್ವಾಸಿಗಳು, ಸತ್ಯ ವಿಶ್ವಾಸಿನಿಯರು, ಭಕ್ತರು, ಭಕ್ತೆಯರು, ಸತ್ಯವಂತರು, ಸತ್ಯವಂತೆಯರು, ಸಹನಶೀಲರು, ಸಹನ ಶೀಲೆಯರು, ವಿನಮ್ರರು, ವಿನಮ್ರೆಯರು, ದಾನಶೀಲರು, ದಾನಶೀಲೆಯರು, ವ್ರತಧಾರಿಗಳು, ವೃತ ಧಾರಿಣಿಗಳು, ತಮ್ಮ ಗುಪ್ತಾಂಗಗಳ ಚಾರಿತ್ರ್ಯ ಸಂರಕ್ಷಕರು, ಸಂರಕ್ಷಕಿಯರು, ಅಲ್ಲಾಹನನ್ನು ಅತ್ಯಧಿಕ ಸ್ಮರಿಸುವ ಪುರುಷರು ಮತ್ತು ಸ್ತ್ರೀಯರಿಗೆ ಅಲ್ಲಾಹನು ಕ್ಷಮಾಧಾನ ಮತ್ತು ಅತಿ ದೊಡ್ಡ ಸತ್ಫಲವನ್ನು ಸಿದ್ಧಗೊಳಿಸಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(33,36,'33:36','ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ಸಂದೇಶವಾಹಕರು ಒಂದು ವಿಷಯದ ತೀರ್ಮಾನ ಮಾಡಿದರೆ ಆ ವಿಷಯದಲ್ಲಿ ಸ್ವತಃ ತೀರ್ಮಾನ ಕೈಗೊಳ್ಳುವ ಹಕ್ಕು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಾದ ಯಾವ ಸ್ತ್ರೀ ಪುರುಷರಿಗೂ ಇಲ್ಲ. ಯಾರಾದರೂ ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ಸಂದೇಶವಾಹಕರ ಆಜ್ಞೋಲ್ಲಂಘನೆ ಮಾಡಿದರೆ, ಅವನು ಸುವ್ಯಕ್ತ ಪಥಭ್ರಷ್ಟತೆಗೆ ಒಳಗಾದನು .');
INSERT INTO "kannada" VALUES(33,37,'33:37','(ಪೈಗಂಬರರೇ,) ಅಲ್ಲಾಹನೂ ನೀವೂ ಅನುಗ್ರಹ ನೀಡಿದ ವ್ಯಕ್ತಿಯನ್ನುದ್ದೇಶಿಸಿ ನೀವು, ‘ನಿನ್ನ ಪತ್ನಿಯನ್ನು (ತಲಾಖ್ ನೀಡದೆ) ತಡೆದಿರಿಸು ಮತ್ತು ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡು’ ಎಂದು ಹೇಳಿದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ. ಅಲ್ಲಾಹು ಬಹಿರಂಗಗೊಳಿಸಲು ಇಚ್ಚಿಸಿದ್ದ ವಿಷಯವನ್ನು ನೀವು ನಿಮ್ಮ ಹೃದಯದೊಳಗೆ ಬಚ್ಚಿಟ್ಟಿದ್ದಿರಿ. ಜನರಿಗೆ ಹೆದರಿದಿರಿ. ನಿಜವಾಗಿ ತಮಗೆ ಭಯ ಪಡಲು ಹೆಚ್ಚು ಅರ್ಹನು ಅಲ್ಲಾಹು ಆಗಿರುವನು. ತದನಂತರ ಝೈದ್ ಅವಳಿಂದ ತನ್ನ ಅಪೇಕ್ಷೆಯನ್ನು ಪೂರ್ತಿಗೊಳಿಸಿಕೊಂಡ ಬಳಿಕ ಅವಳ ವಿವಾಹವನ್ನು ನಾವು ನಿಮ್ಮೊಂದಿಗೆ ಮಾಡಿದೆವು. ಇದು ದತ್ತು ಪುತ್ರರ ಪತ್ನಿಯರನ್ನು ವಿವಾಹವಾಗುವುದರಲ್ಲಿ - ಅವರು ತಮ್ಮ ಪತ್ನಿಯರೊಂದಿಗೆ ತಮ್ಮ ಅಪೇಕ್ಷೆ ಪೂರ್ತಿಗೊಳಿಸಿಕೊಂಡ ಬಳಿಕ - ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಯಾವುದೇ ಅಡಚಣೆ ಇಲ್ಲದಂತಾಗಲಿಕ್ಕಾಗಿ ಆಗಿದೆ. ಅಲ್ಲಾಹನ ಆಜ್ಞೆ ಜಾರಿಗೆ ಬಂದೇ ತೀರುವುದು .');
INSERT INTO "kannada" VALUES(33,38,'33:38','ತನಗೆ ಅಲ್ಲಾಹನು ನಿಶ್ಚಯಿಸಿದ (ಅನುಮತಿಸಿದ) ಕಾರ್ಯವನ್ನು ಮಾಡುವುದರಲ್ಲಿ ಪ್ರವಾದಿಗೆ ಯಾವ ತಪ್ಪೂ ಇಲ್ಲ. ಹಿಂದೆ ಗತಿಸಿಹೋಗಿದ್ದ ಪ್ರವಾದಿಗಳ ವಿಷಯದಲ್ಲೂ ಅಲ್ಲಾಹು ಜಾರಿ ಮಾಡಿದ ಆತನ ರೂಢಿ ಇದೇ ಆಗಿತ್ತು. ಅಲ್ಲಾಹನ ಆಜ್ಞೆಯು ನಿರ್ಧರಿಸಲಾದ ಖಚಿತ ತೀರ್ಪಾಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(33,39,'33:39','ಅಂದರೆ ಅಲ್ಲಾಹನ ಸಂದೇಶಗಳನ್ನು ತಲುಪಿಸುವ, ಅವನನ್ನೇ ಭಯಪಡುವ ಮತ್ತು ಅಲ್ಲಾ ಹನ ಹೊರತು ಯಾರನ್ನೂ ಭಯಪಡದವರಿಗೆ (ಇರುವ ಅಲ್ಲಾಹನ ನಿಯಮವಿದು) ನಿಗಾ ಇಟ್ಟುಕೊಳ್ಳಲು ಅಲ್ಲಾಹನೇ ಸಾಕು.');
INSERT INTO "kannada" VALUES(33,40,'33:40','ಮುಹಮ್ಮದರು ನಿಮ್ಮ ಪುರುಷರ ಪೈಕಿ ಯಾರದೇ ತಂದೆಯಲ್ಲ. ಆದರೆ ಅವರು ಅಲ್ಲಾಹನ ಸಂದೇಶವಾ ಹಕರು ಮತ್ತು ಪ್ರವಾದಿಗಳಲ್ಲಿ ಕೊನೆಯವರು . ಅಲ್ಲಾಹು ಸಕಲ ವಸ್ತುಗಳ ಪರಿಜ್ಞಾನವುಳ್ಳವನು.');
INSERT INTO "kannada" VALUES(33,41,'33:41','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಅಲ್ಲಾಹನನ್ನು ಬಹಳವಾಗಿ ಸ್ಮರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(33,42,'33:42','ಬೆಳಗು ಬೈಗುಗಳಲ್ಲಿ ಅವನ ಪರಿಶುದ್ಧತೆಯನ್ನು ಪ್ರಕೀರ್ತಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(33,43,'33:43','ಅವನು ನಿಮಗೆ ಅನುಗ್ರಹ ನೀಡುತ್ತಿದ್ದಾನೆ. ಅವನ ದೇವಚರರು ನಿಮಗಾಗಿ ದಯಾ ಪ್ರಾರ್ಥನೆ ಮಾಡುತ್ತಿದ್ದಾರೆ. ನಿಮ್ಮನ್ನು ಕತ್ತಲೆಗಳಿಂದ ಬೆಳಕಿನೆಡೆಗೆ ಸಾಗಿಸುವ ಸಲುವಾಗಿ! ಅವನು ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಮೇಲೆ ತುಂಬಾ ಕರುಣೆ ಯುಳ್ಳವನು.');
INSERT INTO "kannada" VALUES(33,44,'33:44','ಅವನನ್ನು ಅವರು ಬೇಟಿಯಾಗುವ ದಿನ ಅವನಿಂದ ಅವರಿಗೆ ದೊರೆಯುವ ಅಭಿವಾದ್ಯವು `ಸಲಾಮ್’ ಆಗಿರುವುದು. ಅಲ್ಲಾಹನು ಅವರಿಗೆ ಅತ್ಯಂತ ಗೌರವಪೂರ್ಣ ಸತ್ಫಲವನ್ನು ಸಿದ್ಧಗೊಳಿಸಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(33,45,'33:45','ಓ ಪ್ರವಾದಿವರ್ಯರೇ, ನಾವು ನಿಮ್ಮನ್ನು ಸಾಕ್ಷಿದಾರರಾಗಿಯೂ ಸುವಾರ್ತೆಗಾರನಾಗಿಯೂ ತಾಕೀತು ಗಾರನಾಗಿಯೂ ಮಾಡಿ ಕಳುಹಿಸಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(33,46,'33:46','ಅಲ್ಲಾಹನ ಅನುಮತಿಯಿಂದ ಅವನ ಕಡೆಗೆ ಕರೆ ನೀಡುವವರಾಗಿಯೂ ಪ್ರಕಾಶಮಯ ಜ್ಯೋತಿಯಾಗಿಯೂ (ಮಾಡಿ ಕಳುಹಿಸಿರುತ್ತೇವೆ.)');
INSERT INTO "kannada" VALUES(33,47,'33:47','ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ಅವರಿಗಾಗಿ ಮಹಾ ಔದಾರ್ಯವಿದೆ ಎಂಬ ಸುವಾರ್ತೆಯನ್ನು ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳಿಗೆ ನೀಡಿರಿ.');
INSERT INTO "kannada" VALUES(33,48,'33:48','ಸತ್ಯನಿಷೇಧಿಗಳನ್ನೂ ಕಪಟವಿಶ್ವಾಸಿಗಳನ್ನೂ ಅನುಸರಿಸಬೇಡಿರಿ. ಅವರ ಕಿರುಕುಳಗಳನ್ನು ಕಡೆಗಣಿಸಿರಿ. ಅಲ್ಲಾಹನ ಮೇಲೆ ಭರವಸೆ ಇಡಿರಿ. ಹೊಣೆ ಒಪ್ಪಿಸಲು ಅಲ್ಲಾಹನೇ ಸಾಕು .');
INSERT INTO "kannada" VALUES(33,49,'33:49','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ಸತ್ಯವಿಶ್ವಾಸಿನಿಯರನ್ನು ವರಿಸಿ, ಅವರನ್ನು ಸ್ಪರ್ಶಿಸುವ ಮುನ್ನ ವಿವಾಹ ವಿಚ್ಚೇದನೆ ನೀಡಿದರೆ ಅವರ ಮೇಲೆ ಎಣಿಸಿ ಲೆಕ್ಕ ಮಾಡಬೇಕಾದ ಯಾವುದೇ ಇದ್ದತ್ ಆಚರಿಸುವ ಭಾದ್ಯತೆ ಅವರಿಗೆ ಇರುವುದಿಲ್ಲ. ಆದುದರಿಂದ ಅವರಿಗೆ ಮುಕ್ತಿ ದ್ರವ್ಯ ನೀಡಿರಿ ಮತ್ತು ಉತ್ತಮ ರೀತಿಯಿಂದ ಸಂಬಂಧ ವಿಚ್ಚೇದನೆ ಮಾಡಿ ಬೀಳ್ಕೊಡಿರಿ.');
INSERT INTO "kannada" VALUES(33,50,'33:50','ಓ ನಬಿವರ್ಯರೇ, ತಾವು ಮಹ್ರ್ ಕೊಟ್ಟಿರುವ ನಿಮ್ಮ ಪತ್ನಿಯರನ್ನೂ ಅಲ್ಲಾಹು ಯುದ್ಧದಲ್ಲಿ ವಶಪಡಿಸಿ ಕೊಟ್ಟವರ (ದಾಸಿಯರ) ಪೈಕಿ ನಿಮ್ಮ ಸ್ವಾಧೀನಕ್ಕೆ ಬಂದಿರುವವರನ್ನೂ ನಿಮ್ಮ ಜೊತೆ (ಮದೀನಕ್ಕೆ) ಹಿಜ್ರತ್ ಮಾಡಿರುವ ನಿಮ್ಮ ಪಿತೃವ್ಯ ಪುತ್ರಿಯ ರನ್ನೂ ನಿಮ್ಮ ಸೋದರತ್ತೆಯ ಪುತ್ರಿಯರನ್ನೂ ನಿಮ್ಮ ಸೋದರ ಮಾವನ ಪುತ್ರಿಯರನ್ನೂ ಮತ್ತು ನಿಮ್ಮ ಮಾತೃ ಸೋದರಿಯರ ಪುತ್ರಿಯರನ್ನು ನಾವು ನಿಮಗೆ ಧರ್ಮಸಮ್ಮತಗೊಳಿಸಿದ್ದೇವೆ. ಸತ್ಯವಿಶ್ವಾಸಿ ಮಹಿಳೆ ಯೊಬ್ಬಳು ತನ್ನ ದೇಹವನ್ನು ಪ್ರವಾದಿಗೆ ದಾನವಾಗಿ ನೀಡಿದರೆ, ಪ್ರವಾದಿಯು ಅವಳನ್ನು ವಿವಾಹವಾಗಲು ಇಚ್ಚಿಸಿದರೆ ಬೇರೆ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೇನೂ ಅನ್ವಯವಾಗದ, ತಮಗಾಗಿ ಮಾತ್ರವಿರುವ ನೆಲೆಯಲ್ಲಿ ಅವಳನ್ನೂ ನಾವು ನಿಮಗಾಗಿ ಧರ್ಮ ಸಮ್ಮತಗೊಳಿಸಿರು ತ್ತೇವೆ. ನಾವು ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಮೇಲೆ ಅವರ ಪತ್ನಿಯರ ಹಾಗೂ ಅವರ ಸ್ವಾಧೀನವಿರುವ ದಾಸಿ ಯರ ವಿಷಯದಲ್ಲಿ ಯಾವ ನಿಯಮಗಳನ್ನು ಹೇರಿರುತ್ತೇವೆ ಎಂಬುದು ನಮಗೆ ಗೊತ್ತಿದೆ. ತಮ್ಮ ಮೇಲೆ ಯಾವ ತೊಡಕೂ ಇಲ್ಲದಿರಲು ಇದನ್ನು ಅನುಮತಿಸಲಾಗಿದೆ. ಅಲ್ಲಾಹು ಕ್ಷಮಾ ಶೀಲನೂ ದಯಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(33,51,'33:51','ನಿಮ್ಮ ಪತ್ನಿಯರ ಪೈಕಿ ನಿಮಗೆ ಇಷ್ಟವಿದ್ದವರನ್ನು ನಿಮಗೆ ಹಿಂದೆ ನಿಲ್ಲಿಸಲಿಕ್ಕೂ ನಿಮಗೆ ಇಷ್ಟ ಬಂದವರನ್ನು ಹತ್ತಿರಗೊಳಿಸಲಿಕ್ಕೂ ಬೇರೆ ನಿಲ್ಲಿಸಿದ ಬಳಿಕ ನಿಮಗೆ ಇಷ್ಟವಿದ್ದವರನ್ನು ನಿಮ್ಮ ಬಳಿಗೆ ವಾಪಾಸು ಕರೆಸಿಕೊಳ್ಳಲಿಕ್ಕೂ ನಿಮಗೆ ಅಧಿಕಾರವಿದೆ . ನಿಮ್ಮ ಮೇಲೆ ಯಾವುದೇ ತಪ್ಪಿಲ್ಲ. ಅವರ ಕಣ್ಣುಗಳು ತಣಿದಿರಲು, ಅವರು ದುಃಖಿಸದಿರಲು ಮತ್ತು ನೀವು ಅವರಿಗೆ ಕೊಟ್ಟು ದರಲ್ಲಿ ಅವರೆಲ್ಲರೂ ತೃಪ್ತಿಪಡಲು ಅತ್ಯಂತ ಸೂಕ್ತ ದಾರಿಯಿದು. ನಿಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ಇರುವುದನ್ನು ಅಲ್ಲಾಹು ಅರಿಯುತ್ತಾನೆ. ಅಲ್ಲಾಹು ಸರ್ವಜ್ಞನೂ ಸಹನಶೀಲನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(33,52,'33:52','ಅವರ ನಂತರ ನಿಮಗೆ ಇತರ ಸ್ತ್ರೀಯರು ಧರ್ಮ ಸಮ್ಮತವಲ್ಲ. ಇವರಿಗೆ ಬದಲು ಇತರ ಪತ್ನಿಯರನ್ನು ಸ್ವೀಕರಿಸಿಕೊಳ್ಳುವುದು - ಅವರ ಸೌಂದರ್ಯವು ತಮ್ಮನ್ನು ಎಷ್ಟೇ ವಿಸ್ಮಯಗೊಳಿಸಿದ್ದರೂ - ಸಮ್ಮತವಲ್ಲ. ಆದರೆ ತಾವು ಅಧೀನಗೊಳಿಸಿದವರ (ದಾಸಿಯರ) ಹೊರತು. ಅಲ್ಲಾಹು ಸಕಲ ವಸ್ತುಗಳ ಮೇಲ್ವಿಚಾರಕನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(33,53,'33:53','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನಿಮಗೆ ಊಟಕ್ಕೆ ಅನುಮತಿ ಸಿಕ್ಕಿದರೆ ಹೊರತು, ನೀವು ಪ್ರವಾದಿಯರ ಮನೆಗಳಿಗೆ ಪ್ರವೇಶಿಸದಿರಿ. ಅನುಮತಿ ಸಿಕ್ಕರೂ ಅನ್ನ ಬೇಯುವುದನ್ನು ನಿರೀಕ್ಷಿಸದಿರಿ. ಆದರೆ ನಿಮಗೆ ಅನುಮತಿ ಸಿಕ್ಕಿದರೆ ಪ್ರವೇಶಿಸಿರಿ. ಆಹಾರ ವುಂಡ ಬಳಿಕ ಕೂಡಲೆ ಚದುರಿ ಬಿಡಿರಿ, ಮಾತುಕತೆಯಲ್ಲಿ ಹೊತ್ತು ಕಳೆಯದಿರಿ. ನಿಮ್ಮ ಈ ವರ್ತನೆಗಳು ಪ್ರವಾದಿಯರಿಗೆ ತೊಂದರೆ ಕೊಡುತ್ತಿವೆ. ಆದರೆ ಅವರು ನಿಮ್ಮಿಂದ ಸಂಕೋಚಪಡುತ್ತಾರೆ. (ತೊಂದರೆ ಕೊಟ್ಟರೂ ಏನೂ ಹೇಳಲಾರರು.) ಆದರೆ ಅಲ್ಲಾಹು ಸತ್ಯಸಂಗತಿ ಯನ್ನು ಹೇಳಲು ನಾಚುವುದಿಲ್ಲ. ಪ್ರವಾದಿಯವರ ಸಹ ಧರ್ಮಿಣಿಯರಿಂದ ನಿಮಗೇನಾದರೂ ವಸ್ತು ವನ್ನು ಕೇಳಬೇಕಾಗಿದ್ದರೆ ತೆರೆಯ ಹಿಂದಿನಿಂದ ಪಡೆಯಿರಿ. ಇದು ನಿಮ್ಮ ಹಾಗೂ ಅವರ ಹೃದಯಗಳ ಶುದ್ಧಿಗೆ ಹೆಚ್ಚು ಸೂಕ್ತವಾದ ಕ್ರಮ ವಾಗಿದೆ. ಅಲ್ಲಾಹನ ಸಂದೇಶವಾಹಕರಿಗೆ ತೊಂದರೆ ಕೊಡುವುದು ನಿಮಗೆ ಎಷ್ಟು ಮಾತ್ರಕ್ಕೂ ಸೂಕ್ತವಲ್ಲ. ಅವರ ಕಾಲಾನಂತರ ಅವರ ಪತ್ನಿಯರನ್ನು ವಿವಾಹ ಮಾಡಿಕೊಳ್ಳುವುದು ಎಂದೆಂದಿಗೂ ನಿಮ ಗೆ ಸಮ್ಮತವಾದುದಲ್ಲ. ಇದು ಅಲ್ಲಾಹನ ಬಳಿ ಬಹಳ ಗಂಭೀರ ಕಾರ್ಯ.');
INSERT INTO "kannada" VALUES(33,54,'33:54','ನೀವು ಯಾವುದನ್ನಾದರೂ ಬಯಲುಗೊಳಿಸಿ ದರೂ ಬಚ್ಚಿಟ್ಟರೂ (ಅದಕ್ಕೆ ಅಲ್ಲಾಹನ ಬಳಿ ಪ್ರತಿ ಫಲವಿದೆ). ಅಲ್ಲಾಹು ಪ್ರತಿಯೊಂದು ವಿಷಯದ ಬಗ್ಗೆ ಸಂಪೂರ್ಣ ಜ್ಞಾನಿಯಾಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(33,55,'33:55','ಪ್ರವಾದಿಯವರ ಪತ್ನಿಯರ ಮೇಲೆ ಅವರ ತಂದೆಯಂದಿರಾಗಲಿ, ಪುತ್ರರಾಗಲಿ, ಸಹೋದರರಾಗಲಿ, ಸೋದರ ಪುತ್ರರಾಗಲಿ, ಸೋದರಿ ಪುತ್ರರಾಗಲಿ, ಅವರಂತಹ ಸ್ತ್ರೀಯರಾಗಲಿ ಅವರ ಅಧೀನ ವಿರುವ ಗುಲಾಮರಾಗಲಿ ಪರಸ್ಪರ ಕಾಣುವುದರಲ್ಲಿ ದೋಷವಿಲ್ಲ . (ಪ್ರವಾದಿ ಪತ್ನಿಯರೇ,) ನೀವು ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ. ಅಲ್ಲಾಹು ಸಕಲ ವಸ್ತುಗಳ ಬಗೆಗೆ ಸಾಕ್ಷಿಯಾಗಿದ್ದಾನೆ .');
INSERT INTO "kannada" VALUES(33,56,'33:56','ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ಮಲಕ್‍ಗಳು ಪ್ರವಾದಿಯವರಿಗೆ ಸ್ವಲಾತ್ ನಿರ್ವಹಿಸುತ್ತಾರೆ. ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವೂ ಅವರ ಮೇಲೆ ಸ್ವಲಾತ್ ಮತ್ತು ಸಲಾಮ್ ಸಲ್ಲಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(33,57,'33:57','ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ಸಂದೇಶವಾಹಕರಿಗೆ ತೊಂದರೆ ಕೊಡುವವರನ್ನು ಅಲ್ಲಾಹು ಇಹ ಲೋಕದಲ್ಲೂ ಪರಲೋಕದಲ್ಲೂ ಶಪಿಸಿರುತ್ತಾನೆ. ಅವರಿಗಾಗಿ ಅಪಮಾನಕಾರಕ ಶಿಕ್ಷೆಯನ್ನು ಸಿದ್ಧಗೊಳಿಸಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(33,58,'33:58','ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೂ ಸತ್ಯವಿಶ್ವಾಸಿನಿಯರಿಗೂ ಅವರು ಮಾಡದ ಕರ್ಮಕ್ಕಾಗಿ ತೊಂದರೆ ಕೊಡುವವರು ಗಂಭೀರ ಸುಳ್ಳಾರೋಪ ಹಾಗೂ ಸುವ್ಯಕ್ತ ಪಾಪದ ಹೊರೆಯನ್ನು ಹೊತ್ತುಕೊಂಡರು .');
INSERT INTO "kannada" VALUES(33,59,'33:59','ಓ ನಬಿವರ್ಯರೇ, ತಮ್ಮ ಪತ್ನಿಯರಿಗೆ, ಪುತ್ರಿ ಯರಿಗೆ, ಸತ್ಯವಿಶ್ವಾಸಿನಿಯರಿಗೆ ಹೇಳಿರಿ, ಅವರು ತಮ್ಮ ಮೇಲೆ ತಮ್ಮ ಚಾದರಗಳ ಸೆರಗನ್ನು ಇಳಿಸಿಕೊಳ್ಳಲಿ. ಅವರು ಗುರುತಿಸಲ್ಪಡುವ ಮೂಲಕ ಸತಾಯಿಸಲ್ಪಡದಿರಲು ಇದು ಹೆಚ್ಚು ಸೂಕ್ತವಾದ ವಿಧಾನ. ಅಲ್ಲಾಹು ಅತ್ಯಂತ ಕ್ಷಮಾಶೀಲನೂ ಕರುಣಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(33,60,'33:60','ಕಪಟವಿಶ್ವಾಸಿಗಳೂ ಹೃದಯದಲ್ಲಿ (ಅನೈತಿ ಕತೆಯ) ರೋಗವುಳ್ಳವರೂ ಮದೀನದಲ್ಲಿ ಭೀತಿ ಹುಟ್ಟಿಸುತ್ತಿರುವವರೂ ತಮ್ಮ ದುಷ್ಕøತ್ಯಗಳನ್ನು ಬಿಡದಿದ್ದರೆ ನಾವು ಅವರ ವಿರುದ್ಧ ಕಾರ್ಯಾಚರಣೆ ಗಾಗಿ ನಿಮ್ಮನ್ನು ಹುರಿದುಂಬಿಸುವೆವು. ಅನಂತರ ಅಲ್ಪ ಮಾತ್ರವೇ ಅವರು ಅಲ್ಲಿ ತಮ್ಮೊಡನೆ ಒಡ ನಾಡುವರು.');
INSERT INTO "kannada" VALUES(33,61,'33:61','(ಅದೂ) ಅವರು ಶಪಿಸಲ್ಪಟ್ಟ ನೆಲೆಯಲ್ಲಿ ಅವರು ಎಲ್ಲಿ ಕಂಡರೂ ಸೆರೆಹಿಡಿಯಲ್ಪಡುವರು ಮತ್ತು ನಿರ್ಧಾಕ್ಷಿಣ್ಯವಾಗಿ ಕೊಲ್ಲಲ್ಪಡುವರು.');
INSERT INTO "kannada" VALUES(33,62,'33:62','ಇದು ಹಿಂದಿನವರಲ್ಲಿ ಅಲ್ಲಾಹು ಸ್ವೀಕರಿಸಿರುವ ಸಂಪ್ರದಾಯವಾಗಿದೆ. ನೀವು ಅಲ್ಲಾಹನ ನಡಾವಳಿಯಲ್ಲಿ ಯಾವ ಬದಲಾವಣೆಯನ್ನೂ ಕಾಣಲಾರಿರಿ.');
INSERT INTO "kannada" VALUES(33,63,'33:63','ಅಂತ್ಯಕಾಲದ ಬಗ್ಗೆ ಜನರು ನಿಮ್ಮೊಡನೆ ಕೇಳುತ್ತಾರೆ. ಅದರ ಜ್ಞಾನವಿರುವುದು ಅಲ್ಲಾಹನಿಗೆ ಮಾತ್ರ ಎಂದು ಹೇಳಿರಿ. (ಪ್ರವಾದಿಯರೇ) ತಮಗೆ ಅದರ ಬಗ್ಗೆ ಅರಿವೇನಿದೆ ? ಅಂತ್ಯ ಸಮಯವು ಪ್ರಾಯಶಃ ಹತ್ತಿರದಲ್ಲೇ ಉಂಟಾಗುವುದು.');
INSERT INTO "kannada" VALUES(33,64,'33:64','ಖಂಡಿತ ಅಲ್ಲಾಹನು ಸತ್ಯನಿಷೇಧಕರನ್ನು ಶಪಿಸಿದ್ದಾನೆ. ಅವರಿಗಾಗಿ ಜ್ವಲಿಸುವ ನರಕಾಗ್ನಿಯನ್ನು ಸಿದ್ಧಗೊಳಿಸಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(33,65,'33:65','ಅವರು ಅದರಲ್ಲಿ ಶಾಶ್ವತವಾಗಿ ಉಳಿಯುವರು. ಅವರು ಯಾವ ರಕ್ಷಕನನ್ನೂ ಸಹಾಯಕನನ್ನೂ ಪಡೆಯಲಾರರು.');
INSERT INTO "kannada" VALUES(33,66,'33:66','ಅವರ ಮುಖಗಳು ನರಕಾಗ್ನಿಯಲ್ಲಿ ಹೊರಳಾಡಿಸಲ್ಪಡುವ ದಿನ ಅವರು, ‘ಅಯ್ಯೋ! ನಾವು ಅಲ್ಲಾಹು ಮತ್ತು ರಸೂಲರ ಅನುಸರಣೆ ಮಾಡುತ್ತಿದ್ದರೆ ಎಷ್ಟು ಚೆನ್ನಾಗಿತ್ತು!’ ಎನ್ನುವರು.');
INSERT INTO "kannada" VALUES(33,67,'33:67','ಅವರು (ಮತ್ತೂ) ಹೇಳುವರು, ‘ನಮ್ಮ ಪ್ರಭೂ, ನಾವು ನಮ್ಮ ಸರದಾರರನ್ನು ಹಾಗೂ ಹಿರಿಯರನ್ನು ಅನುಸರಿಸಿದೆವು. ಹಾಗೆ ಅವರು ನಮ್ಮನ್ನು ದಾರಿಗೆಡಿಸಿದರು.');
INSERT INTO "kannada" VALUES(33,68,'33:68','ನಮ್ಮ ಪ್ರಭೂ! ಅವರಿಗೆ ಇಮ್ಮಡಿ ಶಿಕ್ಷೆ ಕೊಡು ಮತ್ತು ಅವರನ್ನು ಅತ್ಯುಗ್ರವಾಗಿ ಶಪಿಸು .');
INSERT INTO "kannada" VALUES(33,69,'33:69','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ಮೂಸಾರಿಗೆ ತೊಂದರೆ ಕೊಟ್ಟವರಂತೆ ಆಗಬೇಡಿರಿ. ಬಳಿಕ ಅಲ್ಲಾಹನು ಆ ಜನರು ಆರೋಪಿಸಿದ ಮಾತುಗಳಿಂದ ಅವರನ್ನು (ಮೂಸಾರನ್ನು) ಮುಕ್ತಗೊಳಿಸಿ ನಿರಪರಾಧಿತ್ವವನ್ನು ಸಾಬೀತುಪಡಿಸಿದನು. ಅವರು ಅಲ್ಲಾಹನ ಬಳಿ ಗೌರವಾನ್ವಿತರಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(33,70,'33:70','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ. ಸರಿಯಾದ ಮಾತನ್ನೇ ಆಡಿರಿ.');
INSERT INTO "kannada" VALUES(33,71,'33:71','ಅಲ್ಲಾಹು ನಿಮ್ಮ ಕರ್ಮಗಳನ್ನು ಸರಿಪಡಿಸು ವನು. ನಿಮ್ಮ ಅಪರಾಧಗಳನ್ನು ಮನ್ನಿಸುವನು. ಯಾರು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ಸಂದೇಶವಾಹಕರ ಅನುಸರಣೆ ಮಾಡುತ್ತಾನೆ, ಅವನು ಮಹಾ ಯಶಸ್ಸನ್ನು ಪಡೆದನು.');
INSERT INTO "kannada" VALUES(33,72,'33:72','ಅಮಾನತ್ತನ್ನು ಆಕಾಶಗಳು, ಭೂಮಿ ಮತ್ತು ಪರ್ವತಗಳ ಮೇಲೆ ನಾವು ಎತ್ತಿ ತೋರಿಸಿದೆವು. ಆಗ ಅವು ಅದನ್ನು ವಹಿಸಿಕೊಳ್ಳಲು ನಿರಾಕರಿಸಿದವು. ಅವು ಅಂಜಿ ತತ್ತರಿಸಿದವು. ಆದರೆ, ಮಾನವನು ಅದನ್ನು ವಹಿಸಿಕೊಂಡನು. ನಿಶ್ಚಯವಾಗಿಯೂ ಅವನು ದೊಡ್ಡ ಅಕ್ರಮಿಯೂ ತಿಳಿಗೇಡಿಯೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(33,73,'33:73','ಇದು ಅಲ್ಲಾಹನು ಕಪಟವಿಶ್ವಾಸಿಗಳನ್ನೂ ಕಪಟ ವಿಶ್ವಾಸಿನಿಯರನ್ನೂ ಬಹುದೇವಾರಾಧಕರಾದ ಪುರುಷರನ್ನೂ, ಸ್ತ್ರೀಯರನ್ನೂ ಶಿಕ್ಷಿಸಲಿಕ್ಕೂ ಮತ್ತು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಾದ ಪುರುಷರು ಮತ್ತು ಸ್ತ್ರೀಯರ ಪಶ್ಚಾತ್ತಾಪವನ್ನು ಅಲ್ಲಾಹನು ಸ್ವೀಕರಿಸಲಿಕ್ಕೂ ಆಗಿದೆ. ಅಲ್ಲಾಹು ಅತ್ಯಂತ ಕ್ಷಮಾಶೀಲನೂ ಕರುಣಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(34,1,'34:1','ಸರ್ವ ಸ್ತುತಿಯು ಭೂಮಿ - ಆಕಾಶಗಳಲ್ಲಿರುವ ಸರ್ವದರ ಒಡೆಯನಾಗಿರುವ ಅಲ್ಲಾಹನಿಗೆ ಮೀಸಲು. ಪರಲೋಕದಲ್ಲೂ ಸಕಲ ಸ್ತುತಿಗಳು ಅವನಿಗೇ ಸಲ್ಲುತ್ತವೆ. ಅವನು ವಿವೇಕಪೂರ್ಣನೂ ಸೂಕ್ಷ್ಮಜ್ಞಾನಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(34,2,'34:2','ಭೂಮಿಯ ಒಳ ನುಗ್ಗುವ ಹಾಗೂ ಅದರಿಂದ ಹೊರಬರುವ ಮತ್ತು ಆಕಾಶದಿಂದ ಇಳಿಯುವ ಹಾಗೂ ಅದರ ಮೇಲೇರುವ ಪ್ರತಿಯೊಂದನ್ನೂ ಅವನು ಬಲ್ಲನು. ಅವನು ಕರುಣಾನಿಧಿಯೂ ಕ್ಷಮಾಶೀಲನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(34,3,'34:3','ಪುನರುತ್ಥಾನದ ಘಳಿಗೆ ನಮಗೆ ಬರುವುದಿಲ್ಲ ಎಂದು ಸತ್ಯನಿಷೇಧಿಗಳು ಹೇಳಿದರು. (ಪ್ರವಾದಿ ಯರೇ) ಹೇಳಿರಿ - ‘ಪರೋಕ್ಷ ಜ್ಞಾನಿಯಾದ ನನ್ನ ಪ್ರಭುವಿನಾಣೆಗೂ ಅದು ನಿಮಗೆ ಬಂದೇ ತೀರುವುದು. ಆಕಾಶಗಳಲ್ಲಾಗಲಿ ಭೂಮಿಯಲ್ಲಾಗಲಿ, ಅಣು ತೂಕದಷ್ಟು ವಸ್ತುವೂ ಅಣುವಿಗಿಂತ ದೊಡ್ಡದಿರಲಿ, ಅದಕ್ಕಿಂತ ಸಣ್ಣದಿರಲಿ, ಒಂದು ಸುಸ್ಪಷ್ಟ ಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಿಲ್ಲದೆ ಅವನಿಂದ ಮರೆಯಾಗಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(34,4,'34:4','ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಸ್ವೀಕರಿಸಿದ ಹಾಗೂ ಸತ್ಕರ್ಮ ಕೈಗೊಂಡವರಿಗೆ, ಅವನು ಪ್ರತಿಫಲ ನೀಡಲಿಕ್ಕಾಗಿ (ಆ ದಿನ ಬರುವುದು). ಅವರಿಗೆ ಪಾಪ ಮುಕ್ತಿಯೂ ಸನ್ಮಾನ್ಯ ಜೀವನಾಧಾರವೂ ಇವೆ.');
INSERT INTO "kannada" VALUES(34,5,'34:5','ನಮ್ಮನ್ನು ಸೋಲಿಸಬಲ್ಲವೆಂದುನಮ್ಮ ಆಯತ್ತು ಗಳಲ್ಲಿ (ಗೊಂದಲವೆಬ್ಬಿಸಲು) ಓಡಾಡಿ ಕೊಂಡಿರುವವರಿಗೆ ಅತಿ ನಿಕೃಷ್ಟ ತರದ ವೇದನಾತ್ಮಕ ಶಿಕ್ಷೆ ಇದೆ.');
INSERT INTO "kannada" VALUES(34,6,'34:6','ನಿಮ್ಮ ಪ್ರಭುವಿನಿಂದ ನಿಮಗೆ ಅವತೀರ್ಣಗೊಳಿಸಲ್ಪಟ್ಟ ಸಂದೇಶ ಪರಮಸತ್ಯವೆಂದೂ ಪ್ರಬಲನೂ ಸ್ತುತ್ತ್ಯರ್ಹನೂ ಆದ ಅಲ್ಲಾಹನ ಮಾರ್ಗಕ್ಕೆ ಅದು ದಾರಿ ತೋರಿಸುತ್ತದೆಂದೂ ಜ್ಞಾನ ನೀಡಲ್ಪಟ್ಟವರು ತಿಳಿಯುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(34,7,'34:7','ಸತ್ಯನಿಷೇಧಿಗಳು ಹೇಳಿದರು, ‘ನೀವು ಸರ್ವ ರೀತಿಯಲ್ಲೂ ಛಿದ್ರಛಿದ್ರವಾಗಿ ನಾಶವಾದ ಬಳಿಕ ನೀವು ಪುನಃ ಹೊಸತಾಗಿ ಸೃಷ್ಟಿಸಲ್ಪಡುವಿರೆಂಬ ಸುದ್ದಿಯನ್ನು ತಿಳಿಸುವಂಥ ಒಬ್ಬ ವ್ಯಕ್ತಿಯನ್ನು ನಾವು ನಿಮಗೆ ತೋರಿಸಿಕೊಡಲೇ?');
INSERT INTO "kannada" VALUES(34,8,'34:8','ಆ ವ್ಯಕ್ತಿ ಅಲ್ಲಾಹನ ಹೆಸರಿನಲ್ಲಿ ಸುಳ್ಳನ್ನು ಸೃಷ್ಟಿಸುವನೋ ಅಥವಾ ಅವನಿಗೆ ಹುಚ್ಚು ಹಿಡಿದಿದೆಯೋ?’ ಹಾಗಲ್ಲ, ವಾಸ್ತವದಲ್ಲಿ ಪರಲೋಕವನ್ನು ನಂಬದವರು (ಅಲ್ಲಿ) ಯಾತನೆಯಲ್ಲೂ (ಇಹ ಲೋಕದಲ್ಲಿ) ವಿದೂರವಾದ ಪಥಭ್ರಷ್ಟತೆಯಲ್ಲೂ ಇರುವರು.');
INSERT INTO "kannada" VALUES(34,9,'34:9','ಆದರೆ ಇವರನ್ನು ಮುಂದಿನಿಂದಲೂ ಹಿಂದಿನಿಂದಲೂ ಸುತ್ತುವರಿದುಕೊಂಡಿರುವ ಭೂಮಿ- ಆಕಾಶಗಳನ್ನು ಇವರು ನೋಡುವುದಿಲ್ಲವೇ? ನಾವಿಚ್ಚಿಸಿದರೆ ಇವರನ್ನು ಭೂಮಿಯಲ್ಲಿ ಹುಗಿದು ಬಿಡಲೂ ಅಥವಾ ಆಕಾಶದ ಕೆಲವು ತುಂಡುಗಳನ್ನು ಇವರ ಮೇಲೆ ಬೀಳಿಸಲೂ ನಾವು ಶಕ್ತರು. ವಾಸ್ತವದಲ್ಲಿ ತನ್ನ ಪ್ರಭುವಿನ ಕಡೆಗೆ ಮರಳುವಂತಹ ಪ್ರತಿಯೊಬ್ಬ ದಾಸನಿಗೂ ಇದರಲ್ಲಿ ಬಲು ದೊಡ್ಡ ನಿದರ್ಶನವಿದೆ.');
INSERT INTO "kannada" VALUES(34,10,'34:10','ನಾವು ದಾವೂದರಿಗೆ ನಮ್ಮಿಂದ ಔದಾರ್ಯವನ್ನು ಕೊಟ್ಟಿದ್ದೇವೆ. ಪರ್ವತಗಳೇ, ಪಕ್ಷಿಗಳೇ, ದಾವೂದರೊಡನೆ ನೀವು ಕೀರ್ತನೆಯನ್ನು ಪುನರಾವರ್ತಿಸಿರಿ (ಎಂದು ನಾವು ಅಪ್ಪಣೆ ಕೊಟ್ಟೆವು). ಅವರಿಗೆ ನಾವು ಕಬ್ಬಿಣವನ್ನು ಮೃದುಗೊಳಿಸಿದೆವು .');
INSERT INTO "kannada" VALUES(34,11,'34:11','ಎದೆ ಕವಚಗಳನ್ನು ತಯಾರಿಸಿರಿ. ಅದರ ಸರಪಳಿಗಳನ್ನು ಸರಿಯಾದ ಅಳತೆಯಲ್ಲಿ ಮಾಡಿರಿ. ನೀವೆಲ್ಲರೂ ಸತ್ಕರ್ಮವೆಸಗಿರಿ, ನೀವು ಮಾಡುವುದನ್ನು ನಾನು ಚೆನ್ನಾಗಿ ಕಾಣುತ್ತಿರುವವನಾಗಿರುವೆ. (ಎಂದೂ ಅಪ್ಪಣೆ ಕೊಟ್ಟೆವು)');
INSERT INTO "kannada" VALUES(34,12,'34:12','ಸುಲೈಮಾನರಿಗೆ ನಾವು ಗಾಳಿಯನ್ನು ನಿಯಂತ್ರಿಸಿ ಕೊಟ್ಟೆವು. ಅದರ ಬೆಳಗಿನ ಸಂಚಾರವು ಒಂದು ತಿಂಗಳ ದೂರವೂ ಸಂಜೆಯ ಸಂಚಾರವು ಒಂದು ತಿಂಗಳ ದೂರವೂ ಆಗಿತ್ತು. ನಾವು ಅವರಿಗಾಗಿ ತಾಮ್ರದ ಲಾವಾರಸದ ಚಿಲುಮೆಯನ್ನು ಹರಿಸಿದೆವು. ತಮ್ಮ ಪ್ರಭುವಿನ ಅಪ್ಪಣೆ ಪ್ರಕಾರ ಅವರ ಮುಂದೆ ಕೆಲಸ ಮಾಡುತ್ತಿದ್ದ ಕೆಲವು ಜಿನ್ನ್‍ಗಳನ್ನು ಅವರ ಅಧೀನಕ್ಕೆ ಕೊಟ್ಟೆವು. ಅವರ ಪೈಕಿ ಯಾವನಾದರೂ ನಮ್ಮ ಆಜ್ಞೆಯಿಂದ ತಪ್ಪಿದರೆ ಆತನಿಗೆ ನಾವು ಉರಿದೇಳುವ ಅಗ್ನಿಯ ಶಿಕ್ಷೆಯಿಂದ ಸವಿಯನ್ನುಣಿಸುವೆವು.');
INSERT INTO "kannada" VALUES(34,13,'34:13','ಅವರು ಸುಲೈಮಾನರಿಗಾಗಿ ಕಟ್ಟಡಗಳು, ಪ್ರತಿಮೆಗಳು, ಕೊಳಗಳಂತಹ ತೊಟ್ಟಿ ಪಾತ್ರೆಗಳು ಮತ್ತು ಸ್ಥಿರಗೊಳಿಸಿ ಊರಲಾದ ಬೃಹತ್ ಅಡುಗೆಯ ಕಡಾಯಗಳು. ಹೀಗೆ ಅವರು ಇಚ್ಚಿ ಸುವುದನ್ನೆಲ್ಲ ಅವರು ಮಾಡಿಕೊಡುತ್ತಿದ್ದರು. ದಾವೂದರ ಸಂತತಿಯೇ, ಕೃತಜ್ಞತೆಯಾಗಿ ನೀವು ಕರ್ಮವೆಸಗಿರಿ. ನನ್ನ ದಾಸರಲ್ಲಿ ಕೃತಜ್ಞರು ಬಹಳ ಕಡಿಮೆ.');
INSERT INTO "kannada" VALUES(34,14,'34:14','ಅನಂತರ ನಾವು ಸುಲೈಮಾನರಿಗೆ ಮರಣವನ್ನು ವಿಧಿಸಿದಾಗ ಜಿನ್ನ್‍ಗಳಿಗೆ ಅವರ ಮರಣದ ಬಗ್ಗೆ ತಿಳಿಸಿದ್ದು ಅವರ ಊರುಗೋಲನ್ನು ತಿನ್ನುತ್ತಿದ್ದ ಗೆದ್ದಲಿನ ಹೊರತು ಬೇರೇನೂ ಅಲ್ಲ. ಹೀಗೆ ಸುಲೈ ಮಾನರು (ಮರಣದ ನಂತರ) ನೆಲಕ್ಕೆ ಬಿದ್ದಾಗ, ತಮಗೆ ಪರೋಕ್ಷ ಜ್ಞಾನವಿರುತ್ತಿದ್ದರೆ ತಾವು ಇಂತಹ ಅಪಮಾನಕರ ಶಿಕ್ಷೆಯಲ್ಲಿ ಬೀಳುತ್ತಿರಲಿಲ್ಲ ವೆಂದು ಜಿನ್ನ್‍ಗಳಿಗೆ ಸ್ಪಷ್ಟವಾಗಿ ತಿಳಿಯಿತು .');
INSERT INTO "kannada" VALUES(34,15,'34:15','`ಸಬಅï’ ಗೋತ್ರದವರಿಗೆ ಅವರ ವಾಸಸ್ಥಾನದ ಹತ್ತಿರದಲ್ಲೇ ಒಂದು ದೊಡ್ಡ ನಿದರ್ಶನವಿತ್ತು. ಅಂದರೆ ಬಲಭಾಗದಲ್ಲೂ ಎಡಭಾಗದಲ್ಲೂ ಇರುವ ಎರಡು ತೋಟಗಳು. (ನಾವು ಅವರಲ್ಲಿ ಹೇಳಿದೆವು) ‘ನಿಮ್ಮ ಪ್ರಭು ಕೊಟ್ಟ ಆಹಾರ ಉಣ್ಣಿರಿ. ಅವನಿಗೆ ಕೃತಜ್ಞತೆ ಸಲ್ಲಿಸಿರಿ’. ಪರಿಶುದ್ಧವಾದ ನಾಡು! ಅತ್ಯಂತ ಕ್ಷಮಾಶೀಲನಾದ ಪ್ರಭುವೂ!');
INSERT INTO "kannada" VALUES(34,16,'34:16','ಆಮೇಲೆ ಅವರು ವಿಮುಖರಾದರು. ತನ್ನಿಮಿತ್ತ ನಾವು ಅವರ ಮೇಲೆ ಅಣೆಕಟ್ಟು ಮುರಿದ ಜಲ ಪ್ರವಾಹವನ್ನು ಕಳುಹಿಸಿದೆವು . ಅವರ ಹಿಂದಿನ ಎರಡು ತೋಟಗಳ ಸ್ಥಾನದಲ್ಲಿ ಕಹಿ ಫಲಗಳು, ಮುಳ್ಳು ಗಿಡಗಂಟಿಗಳು ಮತ್ತು ಕೆಲವು ಬೋರೆ ಮರಗಳಿಂದ ಕೂಡಿದ ಬೇರೆ ಎರಡು ತೋಟಗ ಳನ್ನು ಅವರಿಗೆ ಬದಲಿ ಕೊಟ್ಟೆವು.');
INSERT INTO "kannada" VALUES(34,17,'34:17','ಇದು, ನಾವು ಅವರ ಸತ್ಯನಿಷೇಧಕ್ಕೆ ನೀಡಿದ ಪ್ರತಿಫಲವಾಗಿತ್ತು. ಇಂತಹ ಪ್ರತಿಫಲವನ್ನು ಸತ್ಯನಿಷೇಧಿಗಳ ಹೊರತು ಇನ್ನಾರಿಗೂ ನಾವು ಕೊಡುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(34,18,'34:18','ಅವರ (ಸಬಅï ಗೋತ್ರದ) ಮತ್ತು ನಾವು ಸಮೃ ದ್ಧಗೊಳಿಸಿದ ನಾಡುಗಳ ನಡುವೆ ಪ್ರತ್ಯಕ್ಷವಾದ ಹಲವು ನಾಡುಗಳನ್ನು ನಾವು ಸ್ಥಾಪಿಸಿದೆವು. ಅವು ಗಳಲ್ಲಿ ಸಂಚಾರವನ್ನೂ ನಾವು ಸುಗಮಗೊಳಿ ಸಿದೆವು. (ನಾವು ಹೇಳಿದೆವು) ‘ರಾತ್ರಿ- ಹಗಲು ಗಳಲ್ಲಿ ಸಂಪೂರ್ಣ ನಿರ್ಭಯರಾಗಿ ಈ ದಾರಿ ಗಳಲ್ಲಿ ಸಂಚರಿಸಿರಿ’.');
INSERT INTO "kannada" VALUES(34,19,'34:19','ಆದರೆ ಅವರು, ‘ನಮ್ಮ ಪ್ರಭೂ! ನಮ್ಮ ಪ್ರಯಾಣದ ದೂರವನ್ನು ಹೆಚ್ಚಿಸು’ ಎಂದರು. ಅವರು ತಮಗೆ ತಾವೇ ಅಕ್ರಮವೆಸಗಿದರು. ತನ್ನಿಮಿತ್ತ ನಾವು ಅವರನ್ನು ದಂತಕಥೆಗಳನ್ನಾಗಿ ಮಾಡಿ ಸಂಪೂರ್ಣ ಭಿನ್ನಭಿನ್ನಗೊಳಿಸಿದೆವು. ನಿಶ್ಚಯವಾಗಿಯೂ ಹೆಚ್ಚು ಸಹನಶೀಲ ಹಾಗೂ ಕೃತಜ್ಞನಾಗಿ ರುವ ಪ್ರತಿಯೊಬ್ಬನಿಗೂ ಇದರಲ್ಲಿ ನಿದರ್ಶನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(34,20,'34:20','ಅವರ ವಿಷಯದಲ್ಲಿ ಇಬ್‍ಲೀಸನು ತನ್ನ ಗುಮಾನಿಯನ್ನು ನಿಜಗೊಳಿಸಿದನು. ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳಾಗಿದ್ದ ಒಂದು ಗುಂಪಿನ ಹೊರತು ಅವರೆಲ್ಲರೂ ಅವನನ್ನೇ ಅನುಸರಿಸಿದರು .');
INSERT INTO "kannada" VALUES(34,21,'34:21','ಇಬ್‍ಲೀಸನಿಗೆ ಅವರ ಮೇಲೇನೂ ಅಧಿಕಾರ ವಿರಲಿಲ್ಲ . ಪರಲೋಕದ ಬಗ್ಗೆ ಸಂಶಯದಲ್ಲಿ ಬಿದ್ದವರ ಕೂಟದಿಂದ ಅದರಲ್ಲಿ ನಂಬುವವರನ್ನು ನಾವು ಪ್ರತ್ಯಕ್ಷವಾಗಿ ಬೇರ್ಪಡಿಸಿ ತಿಳಿಯಲಿಕ್ಕಾಗಿ ಮಾತ್ರವಿದು. ನಿನ್ನ ಪ್ರಭು ಎಲ್ಲ ವಸ್ತುವಿನ ಮೇಲೆ ಮೇಲ್ನೋಟವುಳ್ಳವನು.');
INSERT INTO "kannada" VALUES(34,22,'34:22','(ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ, ‘ನೀವು ಅಲ್ಲಾಹನ ಹೊರತಾಗಿ ನಿಮ್ಮ ಆರಾಧ್ಯರೆಂದು ಭಾವಿಸಿ ಕೊಂಡವರನ್ನು ಕರೆಯಿರಿ’ . ಆಕಾಶಗಳಲ್ಲಾಗಲಿ ಭೂಮಿಯಲ್ಲಾಗಲಿ ಅವರು ಅಣು ತೂಕದ ವಸ್ತುವಿಗೂ ಒಡೆಯರಲ್ಲ. ಭೂಮಿ-ಆಕಾಶಗಳಲ್ಲಿ ಅವರಿಗೆ ಯಾವುದೇ ಪಾಲು ಇಲ್ಲ. ಅವರ ಪೈಕಿ ಅಲ್ಲಾಹನಿಗೆ ಯಾವ ಸಹಾಯಕನೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(34,23,'34:23','ಅಲ್ಲಾಹನು ಅನುಮತಿ ನೀಡಿದ ವ್ಯಕ್ತಿಯ ಹೊರತು ಅಲ್ಲಾಹನ ಸನ್ನಿಧಿಯಲ್ಲಿ ಯಾವ ಶಿಫಾ ರಸೂ ಫಲಕಾರಿಯಾಗದು. ಹಾಗೆ ಜನರ ಹೃದ ಯದಿಂದ ಭೀತಿಯು ದೂರವಾದರೆ ಅವರು ಕೇಳುವರು, ‘ನಿಮ್ಮ ಪ್ರಭು ಹೇಳಿದ್ದೇನು?’ ಆಗ ಅವರು, ‘ಅವನು ಹೇಳಿದ್ದು ಸತ್ಯವೇ, ಅವನು ಮಹೋನ್ನತನೂ ಮಹಾನನೂ ಆಗಿರುತ್ತಾನೆ’ ಎಂದು ಹೇಳುವರು .');
INSERT INTO "kannada" VALUES(34,24,'34:24','(ಪೈಗಂಬರರೇ) ಕೇಳಿರಿ, ‘ನಿಮಗೆ ಭೂಮಿ- ಆಕಾಶಗಳಿಂದ ಜೀವನಾಧಾರ ಕೊಡುವವನು ಯಾರು?’ ಹೇಳಿರಿ; “ಅಲ್ಲಾಹು, ನಿಶ್ಚಯವಾಗಿ ಯೂ ನಾವು ಇಲ್ಲವೇ ನೀವು ಇರುವುದು ಸನ್ಮಾರ್ಗ ದಲ್ಲಿ ಅಥವಾ ಸುವ್ಯಕ್ತ ಪಥಭ್ರಷ್ಟತೆಯಲ್ಲಿ” .');
INSERT INTO "kannada" VALUES(34,25,'34:25','ತಾವು ಹೇಳಿರಿ, “ನಾವು ಮಾಡಿದ ಅಪರಾಧದ ಕುರಿತು ನಿಮ್ಮನ್ನು ಪ್ರಶ್ನಿಸಲಾಗದು. ನೀವು ಮಾಡುತ್ತಿರುವುದರ ಕುರಿತು ನಮ್ಮನ್ನು ಪ್ರಶ್ನಿಸಲಾಗದು” .');
INSERT INTO "kannada" VALUES(34,26,'34:26','ತಾವು ಹೇಳಿರಿ, “ನಮ್ಮ ಪ್ರಭು ನಮ್ಮನ್ನು ಒಟ್ಟು ಗೂಡಿಸುವನು. ನಂತರ ನಮ್ಮ ನಡುವೆ ನ್ಯಾಯ ಪ್ರಕಾರ ತೀರ್ಮಾನ ಮಾಡುವನು. ಅವನು ಸರ್ವ ಜ್ಞನೂ ಪ್ರಬಲ ನ್ಯಾಯಾಧೀಶನೂ ಆಗಿರುವನು”.');
INSERT INTO "kannada" VALUES(34,27,'34:27','ತಾವು ಹೇಳಿರಿ; “ನೀವು ಅವನೊಂದಿಗೆ ಸಹ ಭಾಗಿಗಳಾಗಿ ಸೇರಿಸಿಕೊಂಡಿರುವವರನ್ನು ನನಗೆ ತೋರಿಸಿಕೊಡಿರಿ . ಎಷ್ಟು ಮಾತ್ರಕ್ಕೂ (ಅವನಿಗೆ ಸಹಭಾಗಿಗಳು) ಇಲ್ಲ, ಆದರೆ ಅಲ್ಲಾಹನು ಅತ್ಯಂತ ಪ್ರಬಲನೂ ಧೀಮಂತನೂ ಆಗಿರು ವನು”.');
INSERT INTO "kannada" VALUES(34,28,'34:28','(ಪೈಗಂಬರರೇ) ನಾವು ನಿಮ್ಮನ್ನು ಸಮಗ್ರ ಮಾನವರಿಗಾಗಿ ಸುವಾರ್ತೆ ಕೊಡುವವರಾಗಿಯೂ ಎಚ್ಚರಿಕೆ ನೀಡುವವರಾಗಿಯೂ ಕಳುಹಿಸಿರುತ್ತೇವೆ . ಆದರೆ ಹೆಚ್ಚಿನವರು (ಈ ವಾಸ್ತವಿಕತೆಯನ್ನು) ಅರಿಯುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(34,29,'34:29','‘ನೀವು ಸತ್ಯವಂತರಾಗಿದ್ದರೆ, ಈ (ಪ್ರಳಯ ಕಾಲದ) ವಾಗ್ದಾನ ಯಾವಾಗ ನೆರವೇರುವುದು?’ ಎಂದು ಸತ್ಯನಿಷೇಧಿಗಳು ಕೇಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(34,30,'34:30','ಹೇಳಿರಿ; “ನಿಮಗೆ ಒಂದು ನಿಶ್ಚಿತ ದಿನದ ವಾಗ್ದಾನವಿದೆ. (ಅದು ಬರುವಾಗ) ಒಂದು ಘಳಿಗೆಯೂ ಅದನ್ನು ಬಿಟ್ಟು ನೀವು ಹಿಂದೆ ಹೋಗಲಾರಿರಿ. ಮುಂದೆಯೂ ಹೋಗಲಾರಿರಿ”.');
INSERT INTO "kannada" VALUES(34,31,'34:31','ಈ ಖುರ್‍ಆನನ್ನೂ ಇದಕ್ಕಿಂತ ಮುಂಚೆ ಬಂದಿ ರುವ ಯಾವ ಗ್ರಂಥವನ್ನೂ ನಾವು ನಂಬುವುದಿ ಲ್ಲವೆಂದು ಸತ್ಯನಿಷೇಧಿಗಳು ಹೇಳುತ್ತಾರೆ. ಈ ಅಕ್ರಮಿಗಳು ಪರಸ್ಪರ ಆರೋಪ ಹೊರಿಸುತ್ತ ತಮ್ಮ ಪ್ರಭುವಿನ ಮುಂದೆ ನಿಲ್ಲುವ ಸಂದರ್ಭ, ಇವರನ್ನು ನೀವು ಕಾಣುತ್ತಿದ್ದರೆ! (ಅದು ಗಂಭೀರ ದೃಶ್ಯವಾಗುತ್ತಿತ್ತು). ಅಂದರೆ ಈ ಲೋಕದಲ್ಲಿ ದುರ್ಬಲರಾಗಿದ್ದವರು ಅಹಂಕಾರಿಗಳೊಡನೆ, “ನೀವಿಲ್ಲದಿರುತ್ತಿದ್ದರೆ ನಾವು ಸತ್ಯ ವಿಶ್ವಾಸಿಗ ಳಾಗುತ್ತಿದ್ದೆವು.” ಎನ್ನುವರು.');
INSERT INTO "kannada" VALUES(34,32,'34:32','ಗಣ್ಯರೆನಿಸಿಕೊಂಡವರು ದುರ್ಬಲರೊಡನೆ, “ನಿಮ್ಮ ಬಳಿಗೆ ಸನ್ಮಾರ್ಗ ಬಂದ ನಂತರ ಅದರಿಂದ ನಿಮ್ಮನ್ನು ನಾವು ತಡೆದಿದ್ದೇವೆಯೇ? ಇಲ್ಲ, ಪರಂತು ಸ್ವತಃ ನೀವೇ ಅಪರಾಧಿಗಳಾಗಿದ್ದಿರಿ” ಎಂದು ಉತ್ತರಿಸುವರು .');
INSERT INTO "kannada" VALUES(34,33,'34:33','ಬಲಹೀನರು ಅಹಂಕಾರಿಗಳೊಡನೆ, “ಆದರೆ (ಅದು ನಿಮ್ಮದೇ) ಹಗಲು ರಾತ್ರಿಯ ಕುತಂತ್ರ ವಾಗಿತ್ತು. ನಾವು ಅಲ್ಲಾಹನನ್ನು ನಿಷೇಧಿಸಬೇ ಕೆಂದೂ ಅವನಿಗೆ ಸರಿಸಮಾನರನ್ನು ರೂಪಿಸ ಬೇಕೆಂದೂ ನೀವು ನಮಗೆ ಆದೇಶಿಸುತ್ತಿದ್ದಿರಿ” ಎಂದು ಹೇಳುವರು. ಶಿಕ್ಷೆ ಯನ್ನು ಕಾಣುವಾಗ (ಉಭಯ ವಿಭಾಗದವರ) ಒಳಗೇ ಖೇದ ಅಡಗಿ ರುವುದು. ನಾವು ಸತ್ಯನಿಷೇಧಿಗಳ ಕೊರಳುಗಳಿಗೆ ಸಂಕೋಲೆಗಳನ್ನು ಬಿಗಿದಿದ್ದೇವೆ. ಜನರಿಗೆ ಅವರ ಕರ್ಮಗಳಿಗೆ ತಕ್ಕ ಪ್ರತಿಫಲವನ್ನೇ ಹೊರತು ಬೇರೆ ಪ್ರತಿಫಲವನ್ನು ಕೊಡಲಾಗುವುದೇ?');
INSERT INTO "kannada" VALUES(34,34,'34:34','ನಾವು ಯಾವುದೇ ನಾಡಿಗೆ ಎಚ್ಚರಿಕೆ ನೀಡುವ ವನನ್ನು (ಪ್ರವಾದಿಯನ್ನು) ಕಳುಹಿಸಿದಾಗಲೆಲ್ಲಾ ಆ ನಾಡಿನ ಸುಖಲೋಲುಪರು - “ನೀವು ತಂದಿರುವ ಸಂದೇಶವನ್ನು ಖಂಡಿತ ನಾವು ನಿಷೇಧಿಸುತ್ತೇವೆ” ಎಂದು ಹೇಳದೆ ಇರಲಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(34,35,'34:35','‘ನಾವು ಹೆಚ್ಚು ಸಂಪತ್ತನ್ನೂ ಸಂತತಿಯನ್ನೂ ಹೊಂದಿದ್ದು ನಾವು ಎಂದಿಗೂ ಶಿಕ್ಷೆಗೊಳಗಾಗುವರಲ್ಲ’ ಎಂದೂ ಅವರು ಹೇಳಿದ್ದರು .');
INSERT INTO "kannada" VALUES(34,36,'34:36','(ಪೈಗಂಬರರೇ,) ತಾವು ಹೀಗೆ ಹೇಳಿರಿ; “ನನ್ನ ಪ್ರಭು ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಜೀವನಾಧಾರವನ್ನು ವಿಸ್ತøತಗೊಳಿಸುತ್ತಾನೆ. ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಕಡಿತ ಗೊಳಿಸುತ್ತಾನೆ. ಆದರೆ ಹೆಚ್ಚಿನ ಜನರು ಇದರ ನಿಜಸ್ಥಿತಿಯನ್ನು ತಿಳಿಯುವುದಿಲ್ಲ”.');
INSERT INTO "kannada" VALUES(34,37,'34:37','ನಿಮ್ಮ ಸಂಪತ್ತಾಗಲೀ ನಿಮ್ಮ ಸಂತಾನವಾಗಲೀ ನಿಮಗೆ ನಮ್ಮ ಬಳಿ ಸಾಮಿಪ್ಯವನ್ನು ದೊರಕಿಸಿ ಕೊಡಲಾರದು. ಆದರೆ ಸತ್ಯವಿಶ್ವಾಸ ಸ್ವೀಕರಿಸಿ ಸತ್ಕರ್ಮವೆಸಗುವವರು ಇದಕ್ಕೆ ಹೊರತಾಗಿದ್ದು ಅವರಿಗೆ ಅವರ ಕರ್ಮದ ಇಮ್ಮಡಿ ಪ್ರತಿಫಲವಿದೆ ಮತ್ತು ಅವರು ಅತ್ಯುನ್ನತ ಭವನಗಳಲ್ಲಿ ನಿರ್ಭಯರಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(34,38,'34:38','ಇನ್ನು (ನನ್ನ) ಹಿಡಿತದಿಂದ ಪಾರಾಗಬಲ್ಲೆವೆಂದು ಭಾವಿಸಿ ನಮ್ಮ ಆಯತ್ತುಗಳಲ್ಲಿ ಗೊಂದಲ ನಿರ್ಮಿಸಲು ಓಡಿ ನಡೆಯುವವರು ಯಾತನೆಗೆ ಹಾಜ ರಾಗುವರು .');
INSERT INTO "kannada" VALUES(34,39,'34:39','ಹೇಳಿರಿ, “ನನ್ನ ಪ್ರಭು ತನ್ನ ದಾಸರಲ್ಲಿ ತಾನಿಚ್ಛಿಸಿ ದವರಿಗೆ ಜೀವನಾಧಾರವನ್ನು ವಿಶಾಲಗೊಳಿಸುತ್ತಾನೆ. ತಾನಿಚ್ಚಿಸಿದವರಿಗೆ ಪರಿಮಿತಗೊಳಿಸುತ್ತಾನೆ. ನೀವು (ದೇವ ಮಾರ್ಗದಲ್ಲಿ) ಏನನ್ನು ಖರ್ಚು ಮಾಡಿದರೂ ಅದಕ್ಕೆ ಬದಲಾಗಿ ಅವನು ನಿಮಗೆ ಇನ್ನಷ್ಟು ಕೊಡುತ್ತಾನೆ. ಅವನು ಜೀವನಾಧಾರ ನೀಡುವವರಲ್ಲೇ ಅತ್ಯುತ್ತಮನು” .');
INSERT INTO "kannada" VALUES(34,40,'34:40','ಅವನು ಜನರನ್ನು ಒಟ್ಟುಗೂಡಿಸುವ ದಿನ ದೇವ ಚರರೊಡನೆ - “ಇವರು ನಿಮ್ಮನ್ನು ಆರಾಧಿಸುತ್ತಿದ್ದರೆ?” ಎಂದು ಕೇಳುವನು.');
INSERT INTO "kannada" VALUES(34,41,'34:41','ಆಗ ಅವರು, “ನೀನು ಪರಮಪಾವನನು, ನಮ್ಮ ರಕ್ಷಕ ನೀನು, ಅವರಲ್ಲ. ಆದರೆ ಇವರು ಜಿನ್ನ್‍ಗಳನ್ನು ಆರಾಧಿಸುತ್ತಿದ್ದರು. ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಅವರ ಮೇಲೆಯೇ ವಿಶ್ವಾಸವಿರಿಸಿ ದರು” ಎಂದು ಉತ್ತರಿಸುವರು .');
INSERT INTO "kannada" VALUES(34,42,'34:42','ಈ ದಿನ ನೀವು ಪರಸ್ಪರ ಯಾರಿಗೂ ಲಾಭವನ್ನಾಗಲಿ, ನಷ್ಟವನ್ನಾಗಲಿ ಒಡೆತನ ಗೊಳಿಸುವುದಿಲ್ಲ. ಅಕ್ರಮಿಗಳಿಗೆ, ‘ನೀವು ಸುಳ್ಳಾಗಿಸುತ್ತಿದ್ದ ನರಕ ಶಿಕ್ಷೆಯ ರುಚಿಯನ್ನು ಸವಿಯಿರಿ’ ಎಂದು ಹೇಳುವೆವು .');
INSERT INTO "kannada" VALUES(34,43,'34:43','ನಮ್ಮ ಸುವ್ಯಕ್ತ ಸೂಕ್ತಗಳನ್ನು ಇವರಿಗೆ ಓದಿ ಹೇಳಿದಾಗ ಇವರು, ‘ಇವರು ಕೇವಲ ನಿಮ್ಮ ಪೂರ್ವಜರು ಪೂಜಿಸುತ್ತಾ ಬಂದಿರುವÀ ಆರಾಧ್ಯರಿಂದ ನಿಮ್ಮನ್ನು ತಡೆಯಲು ಉದ್ದೇಶಿಸುವ ವ್ಯಕ್ತಿ’ ಎನ್ನುತ್ತಾರೆ. ‘ಇದು ಖುರ್‍ಆನ್ ಕೇವಲ ಒಂದು ಕಲ್ಪಿತ ಸುಳ್ಳು’ ಎಂದೂ ಹೇಳುತ್ತಾರೆ. ಆ ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಸತ್ಯವು ಬಂದೊದಗಿದಾಗ ಇವರು, “ಇದು ಸ್ಪಷ್ಟವಾದ ಜಾಲ ವಿದ್ಯೆಯೇ ಹೊರತು ಇನ್ನೇನೂ ಅಲ್ಲ” ಎಂದು ಹೇಳಿದರು .');
INSERT INTO "kannada" VALUES(34,44,'34:44','ನಾವು ಈ ಮುಂಚೆ ಇವರಿಗೆ ಓದಿ ಕಲಿಯಬಲ್ಲ ಯಾವುದೇ ಗ್ರಂಥವನ್ನೂ ಕೊಡಲಿಲ್ಲ. ನಿಮಗಿಂತ ಮುಂಚೆ ಇವರ ಕಡೆಗೆ ಯಾವ ಎಚ್ಚರಿಕೆದಾರನನ್ನು ಕಳುಹಿಸಿದ್ದೂ ಇಲ್ಲ .');
INSERT INTO "kannada" VALUES(34,45,'34:45','ಇವರ ಮುಂಚೆ ಗತಿಸಿದವರು ಸುಳ್ಳಾಗಿಸಿದ್ದರು. ನಾವು ಅವರಿಗೆ ನೀಡಿದುದರ ಹತ್ತರಲ್ಲಿ ಒಂದಂಶಕ್ಕೂ ಇವರು ತಲುಪಿಲ್ಲ . ಆದರೆ ಅವರು ನನ್ನ ಸಂದೇಶವಾಹಕರನ್ನು ಸುಳ್ಳಾಗಿಸಿದರು. ಆಗ ನನ್ನ ಶಿಕ್ಷೆ ಎಷ್ಟು ಕಠಿಣವಾಗಿ ನಾಟಿತೆಂದು ನೋಡಿರಿ.');
INSERT INTO "kannada" VALUES(34,46,'34:46','(ಪೈಗಂಬರರೇ,) ಹೇಳಿರಿ; “ನಾನು ನಿಮಗೆ ಕೇವಲ ಒಂದು ಉಪದೇಶ ನೀಡುತ್ತೇನೆ. ನೀವು ಅಲ್ಲಾಹನಿಗಾಗಿ ತಲಾ ಇಬ್ಬರಂತೆಯೂ ಒಬ್ಬರಂತೆಯೂ ಸಿದ್ಧರಾಗಿರಿ. ಆಮೇಲೆ ಆಲೋ ಚಿಸಿರಿ” . ನಿಮ್ಮ ಸಂಗಾತಿಯಲ್ಲಿ ಯಾವುದೇ ಹುಚ್ಚು ಭಾದಿಸಿಲ್ಲ. ಅವರು ಒಂದು ಘೋರ ಯಾತನೆಯ ಮುಂದೆ ನಿಮಗೆ ಎಚ್ಚರಿಕೆ ನೀಡುವವರು ಮಾತ್ರ.');
INSERT INTO "kannada" VALUES(34,47,'34:47','ಹೇಳಿರಿ; “ನಾನು ನಿಮ್ಮಲ್ಲಿ ಯಾವುದೇ ಪ್ರತಿಫಲ ಕೇಳಿದ್ದರೆ ಅದು ನಿಮಗೇ ಇರಲಿ. ನನ್ನ ಪ್ರತಿಫಲ ಕೇವಲ ಅಲ್ಲಾಹನ ಮೇಲಿದೆ. ಅವನು ಎಲ್ಲ ವಸ್ತುವಿನ ಮೇಲೆ ಸಾಕ್ಷಿಯಾಗಿರುತ್ತಾನೆ” .');
INSERT INTO "kannada" VALUES(34,48,'34:48','ಹೇಳಿರಿ; “ನನ್ನ ಪ್ರಭು ಸತ್ಯವನ್ನು ಇಳಿಸಿ ಕೊಡುತ್ತಾನೆ. ಅವನು ಸಕಲ ಪರೋಕ್ಷಗಳನ್ನು ಬಲ್ಲವನು” .');
INSERT INTO "kannada" VALUES(34,49,'34:49','ಹೇಳಿರಿ; ‘ಸತ್ಯ ಬಂತು. ಮಿಥ್ಯ ಆರಂಭವಾಗದು. ಮರಳಿಯೂ ಬಾರದು .');
INSERT INTO "kannada" VALUES(34,50,'34:50','ಹೇಳಿರಿ; “ನಾನು ದಾರಿ ತಪ್ಪಿದರೆ ನನ್ನ ಮೇಲೆ ಯೇ ನಾನು ದಾರಿ ತಪ್ಪಿದೆ. ನಾನು ನೇರ ದಾರಿ ಹಿಡಿದರೆ, ಅದು ನನ್ನ ಪ್ರಭು ನನ್ನ ಮೇಲೆ ಅವತೀರ್ಣಗೊಳಿಸುವ ದಿವ್ಯವಾಣಿಯ ಪ್ರಕಾರವಿದೆ . ಅವನು ಸರ್ವಶ್ರುತನೂ ಅತ್ಯಂತ ನಿಕಟನೂ ಆಗಿರುತ್ತಾನೆ” .');
INSERT INTO "kannada" VALUES(34,51,'34:51','(ಪೈಗಂಬರರೇ) ಅವರು ಭಯದಿಂದ ತಲ್ಲಣಿಸುವ ಸಂದರ್ಭವನ್ನು ತಾವು ಕಾಣುತ್ತಿದ್ದರೆ, (ಅದೆಷ್ಟು ಭಯಂಕರ!). ಆಗ ಅವರಿಗೆ (ಅಲ್ಲಾಹನ ಹಿಡಿತದಿಂದ) ತಪ್ಪಿಸಿಕೊಳ್ಳಲಾಗದು. ಮಾತ್ರವಲ್ಲ ಹತ್ತಿರದಿಂದಲೇ ಅವರನ್ನು ಸೆರೆಹಿಡಿಯಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(34,52,'34:52','ಆಗ ಅವರು ‘ಅದನ್ನು ನಾವು ನಂಬಿದ್ದೇವೆ’ ಎನ್ನುವರು. ಆದರೆ ದೂರ ಸ್ಥಳದಿಂದ ಅದು ಅವರ ಕೈಗೆಟಕುವುದು ಹೇಗೆ? ');
INSERT INTO "kannada" VALUES(34,53,'34:53','ಇದಕ್ಕೆ ಮುಂಚೆ ಅವರು ಇದನ್ನು ನಿರಾಕರಿಸಿದ್ದರು. (ಗುರಿಯಿಂದ) ದೂರವಾದ ಸ್ಥಳದಿಂದ ಊಹಾಸ್ತ್ರವನ್ನು ಎಸೆಯತ್ತಿದ್ದರು .');
INSERT INTO "kannada" VALUES(34,54,'34:54','ಅವರ ಹಾಗೂ ಅವರು ಇಚ್ಚಿಸುವುದರ ನಡುವೆ ತಡೆಯನ್ನು ಇಡಲಾಗುವುದು. ಅವರ ಮುಂಚೆ ಇದ್ದ ಪಂಗಡಗಳ ಮೂಲಕ ಮಾಡಲಾದಂತೆ. ನಿಜವಾಗಿಯೂ ಅವರು ಭಾರೀ ಸಂದೇಹದ ಸುಳಿಯಲ್ಲಿ ಶಂಕಿತರಾಗಿದ್ದರು,');
INSERT INTO "kannada" VALUES(35,1,'35:1','ಸರ್ವಸ್ತುತಿಯು ಅಲ್ಲಾಹನಿಗೆ ಮೀಸಲು. ಅವನು ಆಕಾಶಗಳ ಮತ್ತು ಭೂಮಿಯ ಸೃಷ್ಟಿಕರ್ತನು. ಎರಡೆರಡು, ಮೂರು ಮೂರು ಹಾಗೂ ನಾಲ್ಕು ನಾಲ್ಕು ರೆಕ್ಕೆಗಳಿರುವ ದೇವಚರರನ್ನು ದೂತರನ್ನಾಗಿ ನೇಮಿಸಿದವನು. ಅವನು ತನ್ನ ಸೃಷ್ಟಿಯಲ್ಲಿ ತಾನಿಚ್ಚಿಸಿದ್ದನ್ನು ಹೆಚ್ಚಿಸುತ್ತಾನೆ . ಅಲ್ಲಾಹನು ಎಲ್ಲ ವಸ್ತುವಿನ ಮೇಲೆ ಸಾಮಥ್ರ್ಯವುಳ್ಳವನು.');
INSERT INTO "kannada" VALUES(35,2,'35:2','ಅಲ್ಲಾಹನು ಜನರಿಗೆ ಯಾವ ಅನುಗ್ರಹವನ್ನು ತೆರೆದರೂ ಅದನ್ನು ತಡೆಯುವವನಿಲ್ಲ. ಅವನು ಯಾವುದನ್ನು ತಡೆಹಿಡಿದನೋ ಅದರ ನಂತರ ಅದನ್ನು ಬಿಟ್ಟು ಕೊಡುವವನಿಲ್ಲ. ಅವನು ಅತ್ಯಂತ ಪ್ರಬಲನೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(35,3,'35:3','ಜನರೇ, ನಿಮ್ಮ ಮೇಲೆ ಮಾಡಿಕೊಟ್ಟ ಅಲ್ಲಾಹನ ಅನುಗ್ರಹಗಳನ್ನು ಸ್ಮರಿಸಿರಿ . ಅಲ್ಲಾಹನ ಹೊರತು ಆಕಾಶ ಮತ್ತು ಭೂಮಿಯಿಂದ ನಿಮಗೆ ಆಹಾರ ನೀಡುವ ಬೇರೆ ಸೃಷ್ಟಿಕರ್ತನಿರುವನೇ? ಅವನ ಹೊರತು ಬೇರೆ ಆರಾಧ್ಯನಿಲ್ಲ. ಹೀಗಿದ್ದೂ ನೀವು (ಸತ್ಯದಿಂದ) ತಪ್ಪಿ ಬಿಡುವುದು ಹೇಗೆ?');
INSERT INTO "kannada" VALUES(35,4,'35:4','(ಪೈಗಂಬರರೇ) ಇವರು ನಿಮ್ಮನ್ನು ಸುಳ್ಳಾಗಿಸುತ್ತಿದ್ದರೆ, ನಿಮಗಿಂತ ಮುಂಚೆಯೂ ಅನೇಕ ಸಂದೇಶವಾಹಕರು ಸುಳ್ಳಾಗಿಸಲ್ಪಟ್ಟಿದ್ದಾರೆ . (ಆದ್ದರಿಂದ ಅವರು ಕ್ಷಮಿಸಿದಂತೆ ನೀವು ಕ್ಷಮಿಸಿರಿ) ಕಾರ್ಯಗಳು ಅಲ್ಲಾಹನೆಡೆಗೇ ಮರಳಲಿವೆ.');
INSERT INTO "kannada" VALUES(35,5,'35:5','ಜನರೇ, ಅಲ್ಲಾಹನ ವಾಗ್ದಾನವು ಖಂಡಿತ ಸತ್ಯ. ಆದುದರಿಂದ ಲೌಕಿಕ ಜೀವನವು ನಿಮ್ಮನ್ನು ವಂಚಿ ಸದಿರಲಿ . ಆ ಮಹಾಧೂರ್ತನು (ಶೈತಾನನು) ಅಲ್ಲಾಹನ ಬಗ್ಗೆ ನಿಮ್ಮನ್ನು ವಂಚಿಸದಿರಲಿ.');
INSERT INTO "kannada" VALUES(35,6,'35:6','ಖಂಡಿತ ಶೈತಾನನು ನಿಮ್ಮ ಶತ್ರುವಾಗಿದ್ದಾನೆ. ಆದುದರಿಂದ ನೀವು ಅವನನ್ನು ಶತ್ರುವನ್ನಾಗಿ ಮಾಡಿರಿ. ಅವನು ತನ್ನ ಪಕ್ಷದವರನ್ನು ಅವರು ನರಕವಾಸಿಗಳಲ್ಲಿ ಸೇರಲೆಂದೇ ಆಹ್ವಾನಿಸುತ್ತಿದ್ದಾನೆ .');
INSERT INTO "kannada" VALUES(35,7,'35:7','ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸುವವರಿಗೆ ಘೋರ ಯಾತನೆ ಕಾದಿದೆ. ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಸ್ವೀಕರಿಸಿ ಸತ್ಕರ್ಮ ವೆಸಗಿದವರಿಗೆ ಪಾಪ ಮುಕ್ತಿಯೂ ಮಹಾ ಸತ್ಫಲವೂ ಇದೆ.');
INSERT INTO "kannada" VALUES(35,8,'35:8','ಯಾರಿಗೆ ಅವನ ದುಷ್ಕರ್ಮವು ಚಂದಗೊಂಡು ಕೊನೆಗೆ ಅದು ಉತ್ತಮವಾಗಿ ಕಂಡಿತೋ ಅವನು ಸನ್ಮಾರ್ಗ ಪ್ರಾಪ್ತನಂತೆ ಆಗುವನೇ? ಅಲ್ಲಾಹು ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ದಾರಿ ತಪ್ಪಿಸುತ್ತಾನೆ. ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ನೇರ ದಾರಿಗೆ ತರುತ್ತಾನೆ. ಆದುದರಿಂದ ಇವರ ಮೇಲಿನ ವ್ಯಥೆಯಿಂದ ನಿಮ್ಮ ಪ್ರಾಣ ಹೋಗದಿರಲಿ. ಅಲ್ಲಾಹನು ಇವರು ಮಾಡುತ್ತಿರುವುದನ್ನು ಚೆನ್ನಾಗಿ ಅರಿಯುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(35,9,'35:9','ಅಲ್ಲಾಹನು ಮಾರುತಗಳನ್ನು ಕಳುಹಿಸುತ್ತಾನೆ. ಅನಂತರ ಅವುಗಳು ಮೋಡಗಳನ್ನು ಎಬ್ಬಿಸುತ್ತವೆ. ತರುವಾಯ ನಾವು ಅದನ್ನು ಒಂದು ನಿರ್ಜೀವ ಪ್ರದೇಶದ ಕಡೆಗೆ ಒಯ್ಯುತ್ತೇವೆ. ಆಮೇಲೆ ಅದರ ಮೂಲಕ ಆ ಭೂಮಿಯನ್ನು ಅದರ ಸಾವಿನ ಬಳಿಕ ಜೀವಂತಗೊಳಿಸುತ್ತೇವೆ. ಪುನರುಜ್ಜೀವನವೂ ಹೀಗೆಯೇ ಆಗುವುದು.');
INSERT INTO "kannada" VALUES(35,10,'35:10','ಯಾರು ಪ್ರತಾಪವನ್ನು ಇಚ್ಚಿಸುತ್ತಾನೆ (ಅವನು ಅಲ್ಲಾಹನ ಅನುಸರಣೆ ಮಾಡಲಿ. ಏಕೆಂದರೆ) ಪ್ರತಾಪವು ಸರ್ವ ಸಂಪೂರ್ಣವಾಗಿ ಅಲ್ಲಾಹನದ್ದು. ಪರಿಶುದ್ಧ ವಚನವು ಏರಿ ಹೋಗುವುದು ಅವನ ಕಡೆಗಾಗಿದೆ. ಸತ್ಕರ್ಮವು ಅವನ ಕಡೆಗೇ ಏರಿ ಹೋಗುತ್ತದೆ. ಕೆಟ್ಟ ತಂತ್ರಗಳನ್ನು ಹೂಡುವ ವರಿಗೆ ಕಠಿಣ ಶಿಕ್ಷೆ ಇದೆ. ಅವರ ಕುತಂತ್ರವು ನಾಶವಾಗಿ ಹೋಗಲಿದೆ .');
INSERT INTO "kannada" VALUES(35,11,'35:11','ಅಲ್ಲಾಹನು ನಿಮ್ಮನ್ನು ಮಣ್ಣಿನಿಂದ ಸೃಷ್ಟಿಸಿದನು. ತರುವಾಯ ವೀರ್ಯದಿಂದ. ಅನಂತರ ನಿಮ್ಮ ನ್ನು (ಗಂಡು-ಹೆಣ್ಣು) ಜೋಡಿಗಳಾಗಿ ಮಾಡಿದನು . ಅಲ್ಲಾಹನಿಗೆ ಅರಿವಿಲ್ಲದೆ ಯಾವ ಸ್ತ್ರೀಯೂ ಗರ್ಭ ಧರಿಸುವುದಿಲ್ಲ. ಹೆರುವುದೂ ಇಲ್ಲ. ಯಾವನೇ ಆಯುಷಿಗೆ ಆಯುಷ್ಯ ಕೊಡುವುದಾಗಲಿ, ಆಯುಷ್ಯದಿಂದ ಕಡಿತ ಮಾಡುವುದಾಗಲೀ ಒಂದು ಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಾಗಿರದೆ ಸಂಭವಿಸುವುದಿಲ್ಲ. ಇದು ಅಲ್ಲಾಹನಿಗೆ ಬಲು ಸುಲಭ.');
INSERT INTO "kannada" VALUES(35,12,'35:12','ಎರಡು ಸಮುದ್ರಗಳು ಸಮಾನವಲ್ಲ, ಇದು ಕುಡಿಯಲು ಹಿತವಾದ, ಸ್ವಾದಿಷ್ಠ ಸಿಹಿನೀರು. ಇನ್ನೊಂದು ತೀವ್ರ ಉಪ್ಪು! ಎರಡು ನೀರಿಂದಲೂ ನೀವು ತಾಜಾ ಮಾಂಸವನ್ನು ತಿನ್ನುತ್ತೀರಿ. ನೀವು ಧರಿಸುವ ಆಭರಣಗಳನ್ನು ಹೊರ ತೆಗೆಯುತ್ತೀರಿ. ಸಮುದ್ರದಲ್ಲಿ ಹಡಗುಗಳು ನೀರನ್ನು ಸೀಳುತ್ತಾ ಸಾಗುತ್ತಿರುವುದನ್ನು ನೀವು ನೋಡುತ್ತೀರಿ. ಇದು ಅಲ್ಲಾಹನ ಅನುಗ್ರಹದಿಂದ ನೀವು ಹುಡುಕಿ ತೆಗೆಯಲಿಕ್ಕೂ ಅವನಿಗೆ ಕೃತಜ್ಞರಾಗಲಿಕ್ಕೂ.');
INSERT INTO "kannada" VALUES(35,13,'35:13','ಅವನು ರಾತ್ರಿಯನ್ನು ಹಗಲಿಗೂ ಹಗಲನ್ನು ರಾತ್ರಿಗೂ ದಾಟಿಸುತ್ತಾನೆ. ಸೂರ್ಯ ಚಂದ್ರರನ್ನು ಅವನು ನಿಯಂತ್ರಿಸಿದನು. ಎಲ್ಲ ಒಂದು ನಿಶ್ಚಿತ ಅವಧಿಗೆ ನಡೆಯುತ್ತ ಸಾಗುತ್ತಿದೆ . ಈ ಅಲ್ಲಾಹ ನೇ ನಿಮ್ಮ ಪ್ರಭು. ಪ್ರಭುತ್ವವು ಅವನದ್ದು. ಅವ ನನ್ನು ಬಿಟ್ಟು ನೀವು ಆರಾಧಿಸುವವರು ಒಂದು ಈತ ಬೀಜದ ಸಿಪ್ಪೆಯ ಒಡೆಯರೂ ಅಲ್ಲ.');
INSERT INTO "kannada" VALUES(35,14,'35:14','ನೀವು ಅವರನ್ನು ಕರೆದರೆ ಅವರು ನಿಮ್ಮ ಕರೆಯನ್ನು ಕೇಳಲಾರರು. ಕೇಳಿದರೆಂದು ಸಂಕಲ್ಪಿಸಿದರೂ ಅವರು ಉತ್ತರವನ್ನು ನಿಮಗೆ ಕೊಡಲಾರರು . ಪುನರುತ್ಥಾನ ದಿನ ನಿಮ್ಮ ಪಾಲುಗಾರಿಕೆಯನ್ನು ಅವರು ನಿರಾಕರಿಸುವರು . (ಓ ಮಾನವ) ಸೂಕ್ಷ್ಮವಾಗಿ ತಿಳಿಯುವ ಅಲ್ಲಾಹ ನಂತೆ ಬೇರೊಬ್ಬನು ನಿಮಗೆ ತಿಳಿಸಿಕೊಡಲಿಕ್ಕಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(35,15,'35:15','ಓ ಜನರೇ, ನೀವು ಅಲ್ಲಾಹನೆಡೆಗೆ ಅವಲಂಬಿತರು. ಅಲ್ಲಾಹು ನಿರಪೇಕ್ಷನೂ ಸ್ತುತ್ಯರ್ಹನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(35,16,'35:16','ಅವನಿಚ್ಛಿಸಿದರೆ ನಿಮ್ಮನ್ನು ನಾಶ ಮಾಡಿ ಹೊಸ ಸೃಷ್ಟಿಯನ್ನು ತರಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(35,17,'35:17','ಅಲ್ಲಾಹನಿಗೆ ಅದೇನೂ ಕಷ್ಟವಲ್ಲ.');
INSERT INTO "kannada" VALUES(35,18,'35:18','ತಪ್ಪಿನ ಹೊರೆ ಹೊರುವ ಯಾವನೂ ಇನ್ನೊಬ್ಬನ ಹೊರೆಯನ್ನು ಹೊರಲಾರನು. (ಅಪರಾಧದಿಂದ) ಹೊರೆ ಹೊತ್ತ ವ್ಯಕ್ತಿಯು ತನ್ನ ಹೊರೆಯನ್ನು ಹೊರಲು ಇನ್ನೊಬ್ಬನನ್ನು ಕೂಗಿದರೆ, ಅವನ ಆಪ್ತ ಬಂಧುವೇ ಆಗಿದ್ದರೂ ಅವನ ಹೊರೆಯಿಂದ ಒಂದಿಷ್ಟನ್ನೂ ಹೊರಲಾರನು . ಪರೋಕ್ಷವಾಗಿ ತಮ್ಮ ಪ್ರಭುವನ್ನು ಭಯಪಡುವವರಿಗೆ ಮತ್ತು ನಮಾಝ್ ಸಂಸ್ಥಾಪಿಸುವವರಿಗೆ ಮಾತ್ರ ನೀವು ಎಚ್ಚರಿಕೆ ಕೊಡಬಲ್ಲಿರಿ. ಯಾವನು ಶುದ್ಧಿಯಾಗುತ್ತಾನೆ, ಅವನು ತನಗಾಗಿಯೇ ಶುದ್ಧಿಯಾಗು ತ್ತಾನೆ. ನಿರ್ಗಮನ ಅಲ್ಲಾಹನ ಕಡೆಗೇ ಇದೆ.');
INSERT INTO "kannada" VALUES(35,19,'35:19','ಕುರುಡನೂ ದೃಷ್ಟಿಯುಳ್ಳವನೂ ಸಮಾನರಲ್ಲ.');
INSERT INTO "kannada" VALUES(35,20,'35:20','ಇರುಳುಗಳೂ, ಪ್ರಕಾಶವೂ ಅಲ್ಲ.');
INSERT INTO "kannada" VALUES(35,21,'35:21','ನೆರಳೂ ಬಿಸಿಲೂ ಸಮಾನವಲ್ಲ.');
INSERT INTO "kannada" VALUES(35,22,'35:22','ಜೀವಂತರೂ ಮೃತರೂ ಸಮಾನರಲ್ಲ .ಅಲ್ಲಾಹು ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಕೇಳಿಸುತ್ತಾನೆ. ಆದರೆ ಸಮಾಧಿ ಗಳಲ್ಲಿರುವವರಿಗೆ ನೀವು ಕೇಳಿಸಲಾರಿರಿ .');
INSERT INTO "kannada" VALUES(35,23,'35:23','ನೀವು ಕೇವಲ ಓರ್ವ ಎಚ್ಚರಿಕೆ ಕೊಡುವವರು ಮಾತ್ರ.');
INSERT INTO "kannada" VALUES(35,24,'35:24','ಸುವಾರ್ತೆ ಮತ್ತು ಎಚ್ಚರಿಕೆ ನೀಡುವವರಾಗಿ ನಿಮ್ಮನ್ನು ನಾವು ಸತ್ಯಸಹಿತ ಕಳುಹಿಸಿರುತ್ತೇವೆ. ಎಚ್ಚರಿಕೆ ಕೊಡುವ ವ್ಯಕ್ತಿಯೊಬ್ಬರು ಬಂದಿರದೆ ಯಾವುದೇ ಸಮುದಾಯ ಗತಿಸಿ ಹೋಗಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(35,25,'35:25','ಇವರು ನಿಮ್ಮನ್ನು ಸುಳ್ಳಾಗಿಸುತ್ತಾರಾದರೆ ಇವರಿಗಿಂತ ಮುಂಚೆ ಗತಿಸಿದವರೂ ಸುಳ್ಳಾಗಿಸಿದ್ದರು. ಅವರ ಸಂದೇಶವಾಹಕರು ಅವರ ಬಳಿಗೆ ಸುಸ್ಪಷ್ಟ ದೃಷ್ಟಾಂತಗಳನ್ನೂ ಕಿರು ಹೊತ್ತಗೆಗಳನ್ನೂ ಹಾಗೂ ಬೆಳಕು ನೀಡುವ ವೇದಗ್ರಂಥವನ್ನೂ ತಂದಿದ್ದರು.');
INSERT INTO "kannada" VALUES(35,26,'35:26','ಅನಂತರ ಸತ್ಯನಿಷೇಧಕರನ್ನು ನಾನು ಹಿಡಿದು ಶಿಕ್ಷಿಸಿದೆನು. ಆಗ ನನ್ನ ಶಿಕ್ಷೆ ಎಷ್ಟು ಕಠಿಣವಾಗಿತ್ತು!');
INSERT INTO "kannada" VALUES(35,27,'35:27','ಅಲ್ಲಾಹನು ಆಕಾಶದಿಂದ ನೀರನ್ನು ಸುರಿಸುವುದನ್ನೂ ಆ ಬಳಿಕ ಅದರ ಮೂಲಕ ನಾವು (ಅಲ್ಲಾಹು) ವಿಭಿನ್ನ ಬಣ್ಣಗಳಿರುವ ತರತರದ ಫಲಗಳನ್ನು ಹೊರ ತರುವುದನ್ನೂ ನೀವು ನೋಡುವುದಿಲ್ಲವೇ? ಪರ್ವತಗಳಲ್ಲೂ ಬಿಳಿ, ಕೆಂಪು (ಕಡು ಹಾಗೂ ನಸು) ವೈವಿಧ್ಯಮಯ ಬಣ್ಣಗಳ ಮಾರ್ಗರೇಖೆಗಳಿವೆ.ಮತ್ತು ಕಡು ಕಪ್ಪು ಬಂಡೆಗಳೂ ಇವೆ.');
INSERT INTO "kannada" VALUES(35,28,'35:28','ಇದೇ ರೀತಿಯಲ್ಲಿ ಮನುಷ್ಯರಲ್ಲೂ, ಪ್ರಾಣಿಗಳಲ್ಲೂ ಜಾನುವಾರುಗಳಲ್ಲೂ ವಿಭಿನ್ನ ಬಣ್ಣಗಳುಂಟು . ಅಲ್ಲಾಹನ ದಾಸರಲ್ಲಿ ಜ್ಞಾನವುಳ್ಳವರು ಮಾತ್ರ ಅವನನ್ನು ಭಯಪಡುತ್ತಾರೆ . ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಪ್ರತಾಪಶಾಲಿಯೂ ಕ್ಷಮಾ ಶೀಲನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(35,29,'35:29','ಅಲ್ಲಾಹನ ಗ್ರಂಥವನ್ನು ಪಠಿಸುವ, ನಮಾಝ ನ್ನು ನೆಲೆಗೊಳಿಸುವ ಮತ್ತು ನಾವು ಅವರಿಗೆ ಕೊಟ್ಟಿರುವ ಸಂಪತ್ತಿನಿಂದ ರಹಸ್ಯವಾಗಿಯೂ ಪರಸ್ಯವಾಗಿಯೂ ಖರ್ಚು ಮಾಡುವ ಜನರು ನಾಶವಿಲ್ಲದ ಒಂದು ವ್ಯಾಪಾರದ ಆಗ್ರಹದಲ್ಲಿರುವರು.');
INSERT INTO "kannada" VALUES(35,30,'35:30','ಇದರ ಪರಿಣಾಮವು ಅಲ್ಲಾಹು ಅವರ ಸತ್ಫಲವನ್ನು ಸಂಪೂರ್ಣವಾಗಿ ನೀಡುವುದು ಮತ್ತು ಅವನ ಔದಾರ್ಯದಿಂದ ಅವರಿಗೆ ಇನ್ನಷ್ಟು ಹೆಚ್ಚಿಸಿ ಕೊಡುವುದೂ ಆಗಿದೆ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹು ಮಹಾಕ್ಷಮಾಶೀಲನೂ ಉದಾರನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(35,31,'35:31','ನಾವು ನಿಮ್ಮ ಕಡೆಗೆ ಸಂದೇಶ ಕಳುಹಿಸಿದ ಗ್ರಂಥವೇ ಸತ್ಯವಾಗಿದ್ದು ಅದು ಈ ಮುಂಚಿನ ಗ್ರಂಥಗಳನ್ನು ದೃಢೀಕರಿಸುತ್ತದೆ. ಅಲ್ಲಾಹು ತನ್ನ ದಾಸರ ಬಗ್ಗೆ ಸೂಕ್ಷ್ಮ ಜ್ಞಾನಿಯೂ, ಸೂಕ್ಷ್ಮದರ್ಶಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(35,32,'35:32','ಅನಂತರ ನಮ್ಮ ದಾಸರಲ್ಲಿ (ಶ್ರೇಷ್ಟರಾಗಿ) ಆಯ್ದುಕೊಂಡವರಿಗೆ ಈ ಗ್ರಂಥವನ್ನು ನಾವು ಉತ್ತರಾಧಿಕಾರವಾಗಿ ಕೊಟ್ಟೆವು. ಆದರೆ ಅವರಲ್ಲಿ ಕೆಲವರು ತನ್ನ ಮೇಲೆ ತಾನೇ ಅಕ್ರಮವೆಸಗಿದವರು. ಕೆಲವರು ಮಿತತ್ವವನ್ನು ಪಾಲಿಸುವವರು. ಇನ್ನು ಕೆಲವರು ಅಲ್ಲಾಹನ ಇಚ್ಛೆಯಂತೆ ಪುಣ್ಯ ಕಾರ್ಯಗಳಲ್ಲಿ ಮುಂದೆ ದಾಟಿ ಹೋದವರು. ಇದುವೇ ಮಹಾ ಅನುಗ್ರಹ .');
INSERT INTO "kannada" VALUES(35,33,'35:33','ಅವರು ಶಾಶ್ವತವಾದ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ ಪ್ರವೇಶಿಸುವರು. ಅಲ್ಲಿ ಅವರನ್ನು ಬಂಗಾರದ ಕಂಕಣಗಳಿಂದಲೂ ಮುತ್ತುಗಳಿಂದಲೂ ಅಲಂಕರಿಸಲಾಗುವುದು. ಅಲ್ಲಿ ಅವರ ಉಡುಪು ರೇಶ್ಮೆಯ ದ್ದಾಗಿರುವುದು.');
INSERT INTO "kannada" VALUES(35,34,'35:34','ಅವರು, “ನಮ್ಮಿಂದ ದುಃಖವನ್ನು ದೂರೀಕರಿಸಿದ ಅಲ್ಲಾಹನಿಗೆ ಸರ್ವಸ್ತುತಿ. ಖಂಡಿತ ನಮ್ಮ ಪ್ರಭು ಮಹಾಕ್ಷಮಾದಾನಿಯೂ ಕೃತಜ್ಞತೆಯನ್ನು ಸ್ವೀಕರಿಸುವವನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(35,35,'35:35','ಅಂದರೆ ಅವನು ತನ್ನ ಅನುಗ್ರಹದಿಂದ ನಮ್ಮನ್ನು ಶಾಶ್ವತ ನಿವಾಸದಲ್ಲಿ ತಂದಿರಿಸಿದನು. ಇಲ್ಲಿ ನಮಗೆ ಯಾವುದೇ ಕಷ್ಟ ಉಂಟಾಗದು. ಒಂದಿಷ್ಟು ಆಯಾಸವೂ ಬಾಧಿಸದು” ಎನ್ನುವರು.');
INSERT INTO "kannada" VALUES(35,36,'35:36','ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ನರಕಾಗ್ನಿ ಇದೆ. ಅವರಿಗೆ ಮರಣವನ್ನು ವಿಧಿಸಲಾಗುವುದಿಲ್ಲ. ಹಾಗಿದ್ದರೆ ಸತ್ತು ಹೋಗುತ್ತಿದ್ದರು. ನರಕ ಶಿಕ್ಷೆಯಿಂದ ಅವರಿಗೆ ಕಡಿತ ಮಾಡಲಾಗುವುದೂ ಇಲ್ಲ. ನಾವು ಎಲ್ಲ ಸತ್ಯನಿಷೇಧಿಗೂ ಇದೇ ರೀತಿ ಪ್ರತಿಫಲ ಕೊಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(35,37,'35:37','ಅಲ್ಲಿ ಅವರು ಚೀರಾಡುವರು. “ನಮ್ಮ ಪ್ರಭೂ, ನೀನು ನಮ್ಮನ್ನು ಒಮ್ಮೆ ಹೊರಹಾಕು. ಆಗ ನಾವು ಹಿಂದೆ ಮಾಡುತ್ತಿದ್ದಂತಹ ಕರ್ಮಗಳಿಗಿಂತ ಭಿನ್ನವಾದ ಸತ್ಕರ್ಮವನ್ನು ಮಾಡುತ್ತೇವೆ”. “ಯೋಚಿಸುವವರಿಗೆ ಅರ್ಥಮಾಡಿಕೊಳ್ಳಲು ಸಾಕಾಗುವಷ್ಟು ಆಯುಷ್ಯವನ್ನು ನಾವು ನಿಮಗೆ ಕೊಟ್ಟಿರಲಿಲ್ಲವೇ? ನಿಮ್ಮ ಬಳಿಗೆ ಎಚ್ಚರಿಕೆ ಕೊಡು ವವರೂ ಬಂದಿದ್ದರಲ್ಲವೇ? ಆದ್ದರಿಂದ ಈಗ ಸವಿಯಿರಿ, ಇನ್ನು ಅಕ್ರಮಿಗಳಿಗೆ ಯಾರೂ ಸಹಾಯ ಕನಿಲ್ಲ” (ಎಂದು ಅವರಿಗೆ ಉತ್ತರಿಸಲಾಗುವುದು.) ');
INSERT INTO "kannada" VALUES(35,38,'35:38','ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹು ಆಕಾಶಗಳ ಮತ್ತು ಭೂಮಿಯ ಅದೃಶ್ಯ ಜ್ಞಾನಿ. ಅವನು ಹೃದಯಂತರಾಳವನ್ನು ಬಲ್ಲ ಸೂಕ್ಷ್ಮಜ್ಞಾನಿ .');
INSERT INTO "kannada" VALUES(35,39,'35:39','ಅವನು ನಿಮ್ಮನ್ನು ಭೂಮಿಯಲ್ಲಿ (ಗತಜನಾಂ ಗದ) ಪ್ರತಿನಿಧಿಗಳಾಗಿ ಮಾಡಿದವನು. ಆದ್ದರಿಂದ ಯಾರು ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸುತ್ತಾರೆ, ಅವನ ನಿಷೇಧದ ಫಲ ಅವನ ಮೇಲೆಯೇ ಇದೆ. ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಅವರ ಸತ್ಯನಿಷೇಧವು ಅವರ ಪ್ರಭುವಿನ ಬಳಿ ಉಗ್ರ ಕ್ರೋಧವನ್ನಲ್ಲದೆ ಹೆಚ್ಚಿಸುವುದಿಲ್ಲ. ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಅವರ ಸತ್ಯನಿಷೇ ಧವು ಭಾರೀ ನಷ್ಟವನ್ನೇ ಹೊರತು ಬೇರೇನನ್ನೂ ಹೆಚ್ಚಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(35,40,'35:40','(ಪೈಗಂಬರರೇ) ಹೇಳಿರಿ : ನೀವು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಆರಾಧಿಸುತ್ತಿರುವ ನಿಮ್ಮ ಸಹಭಾಗಿಗ ಳನ್ನು ಕಂಡಿದ್ದೀರಾ? (ಇವರ ಬಗ್ಗೆ ನೀವೇನೆನ್ನುವಿರಿ?) ಅವರು ಭೂಮಿಯಲ್ಲಿ ಏನನ್ನು ಸೃಷ್ಟಿಸಿದ್ದಾರೆ? ಅಥವಾ ಆಕಾಶಗಳಲ್ಲಿ ಅವರ ಪಾಲು ಏನೆಂದು ನನಗೆ ತೋರಿಸಿರಿ. ಇಲ್ಲವೇ ಇವರಿಗೆ ನಾವು ಯಾವುದೇ ವೇದಗ್ರಂಥವನ್ನು (ಪ್ರತ್ಯೇಕವಾಗಿ) ಕೊಟ್ಟಿದ್ದೇವೆಯೇ? ಆಮೇಲೆ ಅದರಿಂದ ಯಾವುದೇ ಆಧಾರದ ಮೇಲೆ ಅವರು ನೆಲೆ ಗೊಂಡಿದ್ದಾರೆಯೇ? ಇಲ್ಲ. ಅಕ್ರಮಿಗಳು ಪರಸ್ಪರ ಮೋಸದ ಭರವಸೆಗಳನ್ನು ಮಾತ್ರ ನೀಡಬಲ್ಲರು.');
INSERT INTO "kannada" VALUES(35,41,'35:41','ನಿಶ್ಚಯವಾಗಿಯೂ ಭೂಮಿ-ಆಕಾಶಗಳು (ಅವುಗಳ ಸ್ಥಾನದಿಂದ) ಸರಿದು ಹೋಗದಂತೆ ಅಲ್ಲಾಹನು ಹಿಡಿದಿಟ್ಟಿರುವನು. ಅವು ಸರಿದು ಹೋದರೆ, ಅಲ್ಲಾಹನೇ ಹೊರತು ಅವುಗಳನ್ನು ನಿಯಂತ್ರಿಸಿ ನಿಲ್ಲಿಸಲು ಬೇರಾರಿಗೂ ಸಾಧ್ಯವಿಲ್ಲ. ಅಲ್ಲಾಹು ಅತ್ಯಂತ ಸಹನಶೀಲನೂ ಕ್ಷಮಾಶೀಲನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(35,42,'35:42','ನಿಶ್ಚಯವಾಗಿಯೂ ಭೂಮಿ-ಆಕಾಶಗಳು (ಅವುಗಳ ಸ್ಥಾನದಿಂದ) ಸರಿದು ಹೋಗದಂತೆ ಅಲ್ಲಾಹನು ಹಿಡಿದಿಟ್ಟಿರುವನು. ಅವು ಸರಿದು ಹೋದರೆ, ಅಲ್ಲಾಹನೇ ಹೊರತು ಅವುಗಳನ್ನು ನಿಯಂತ್ರಿಸಿ ನಿಲ್ಲಿಸಲು ಬೇರಾರಿಗೂ ಸಾಧ್ಯವಿಲ್ಲ. ಅಲ್ಲಾಹು ಅತ್ಯಂತ ಸಹನಶೀಲನೂ ಕ್ಷಮಾಶೀಲನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(35,43,'35:43','ಇವರು ಭೂಮಿಯಲ್ಲಿ ಗರ್ವ ತೋರಿದುದಕ್ಕಾಗಿ ಮತ್ತು ಕೀಳುಮಟ್ಟದ ಕುತಂತ್ರಗಳನ್ನು ಹೂಡಿದ್ದಕ್ಕಾಗಿ. ನಿಜದಲ್ಲಿ ಕೆಟ್ಟಕುತಂತ್ರಗಳ ಫಲ ಅದರ ಜನರನ್ನೇ ಅಧೋಗತಿಗೆ ಇಳಿಸಿ ಬಿಡುತ್ತವೆ. ಆಗ ಗತ ಜನಾಂಗದ ಮೇಲೆ ಅಲ್ಲಾಹನು ಕೈಗೊಂಡ ಕ್ರಮವನ್ನೇ ಹೊರತು ಬೇರೇನನ್ನು ಇವರು ನಿರೀಕ್ಷಿಸುತ್ತಿರುವರು? ಹಾಗಿದ್ದರೆ ನೀವು ಅಲ್ಲಾಹನ ಕಾರ್ಯಾ ಚರಣೆಗೆ ಯಾವುದೇ ಬದಲಾವಣೆಯನ್ನು ಕಾಣಲಾರಿರಿ. ಅಲ್ಲಾಹನ ಕಾರ್ಯಾಚರಣೆಗೆ ಯಾವುದೇ ಸ್ಥಾನಪಲ್ಲಟವನ್ನು ನೀವು ಕಾಣಲಾರಿರಿ.');
INSERT INTO "kannada" VALUES(35,44,'35:44','ಇವರು ಭೂಮಿಯಲ್ಲಿ ಸಂಚರಿಸಿ ತಮಗಿಂತ ಮುಂಚೆ ಗತಿಸಿದವರ ಪರಿಣಾಮವೇನಾಯಿ ತೆಂದು ನೋಡಲಿಲ್ಲವೇ? ಅವರು ಇವರಿಗಿಂತ ಅಧಿಕ ಬಲಶಾಲಿಗಳಾಗಿದ್ದರು. ಆಕಾಶಗಳಲ್ಲಾಗಲಿ ಭೂಮಿಯಲ್ಲಾಗಲಿ ಅಲ್ಲಾಹನನ್ನು ಸೋಲಿಸಬಲ್ಲ ಯಾವ ಶಕ್ತಿಯೂ ಇಲ್ಲ . ಅವನು ಸರ್ವಜ್ಞನು ಮತ್ತು ಸರ್ವಶಕ್ತನು.');
INSERT INTO "kannada" VALUES(35,45,'35:45','ಜನರು ಮಾಡಿದ ದುಷ್ಕರ್ಮಗಳಿಗಾಗಿ ಅಲ್ಲಾ ಹನು ಅವರನ್ನು (ತ್ವರಿತವಾಗಿ) ಹಿಡಿದು ಶಿಕ್ಷಿಸುತ್ತಿದ್ದರೆ, ಭೂಮಿಯ ಮೇಲೆ ಒಂದು ಜೀವಿಯನ್ನೂ ಜೀವಂತ ಬಿಡುತ್ತಿರಲಿಲ್ಲ. ಆದರೆ, ಅವನು ಅವರಿಗೆ ಒಂದು ನಿಶ್ಚಿತ ಸಮಯದವರೆಗೆ ಕಾಲಾವಕಾಶ ಕೊಡುವನು. ಅನಂತರ ಅವರ ನಿಶ್ಚಿತ ಕಾಲ ಬಂದಾಗ ಅಲ್ಲಾಹು ತನ್ನ ದಾಸರನ್ನು ನೋಡಿ ಕೊಳ್ಳುವನು. (ತಕ್ಕ ಪ್ರತಿಫಲ ಕೊಡುವನು) .');
INSERT INTO "kannada" VALUES(36,1,'36:1','ಯಾಸೀನ್');
INSERT INTO "kannada" VALUES(36,2,'36:2','ಸತ್ವಪೂರ್ಣವಾದ ಖುರ್‍ಆನಿನಾಣೆ.');
INSERT INTO "kannada" VALUES(36,3,'36:3','ಖಂಡಿತ ತಾವು ಸಂದೇಶವಾಹಕರಲ್ಲಿ ಸೇರಿದವರು.');
INSERT INTO "kannada" VALUES(36,4,'36:4','ತಾವು ನೇರ ಮಾರ್ಗದಲ್ಲೇ ಇರುವಿರಿ.');
INSERT INTO "kannada" VALUES(36,5,'36:5','(ಈ ಖುರ್‍ಆನ್) ಅತ್ಯಂತ ಪ್ರತಾಪಶಾಲಿಯೂ ಪರಮದಯಾಳುವೂ ಆದ ಅಲ್ಲಾಹನಿಂದ ಅವತೀ ರ್ಣಗೊಂಡಿದೆ.');
INSERT INTO "kannada" VALUES(36,6,'36:6','ಇದು ಒಂದು ಜನಾಂಗಕ್ಕೆ ನೀವು ಎಚ್ಚರಿಕೆ ಕೊಡುವಂತಾಗಲು. ಅವರ ಪೂರ್ವಜರಿಗೆ ಮುನ್ನೆಚ್ಚರಿಕೆ ನೀಡಲಾಗಿಲ್ಲ. ಆದ್ದರಿಂದ ಅವರು ನಿರ್ಲಕ್ಷರಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(36,7,'36:7','ಅವರಲ್ಲಿ ಹೆಚ್ಚಿನವರ ಮೇಲೂ ವಚನವು ನಿಜಗೊಂಡಿದೆ. ಆದುದರಿಂದ ಅವರು ವಿಶ್ವಾಸವಿರಿಸಲಾರರು .');
INSERT INTO "kannada" VALUES(36,8,'36:8','ನಿಜವಾಗಿಯೂ ನಾವು ಅವರ ಕೊರಳುಗಳಿಗೆ ಬೇಡಿ ತೊಡಿಸಿದ್ದೇವೆ. ಅದು ಗಡ್ಡಮೂಳೆಗೆ ಬಿಗಿಯಲ್ಪಟ್ಟಿದೆ. ಅವರು ತಲೆಯನ್ನು ಮೇಲೆತ್ತಿರುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(36,9,'36:9','ನಾವು ಒಂದು ತಡೆಯನ್ನು ಅವರ ಹಿಂದೆಯೂ ಇನ್ನೊಂದು ತಡೆಯನ್ನು ಅವರ ಮುಂದೆಯೂ ನಿಲ್ಲಿಸಿದ್ದೇವೆ. ಹೀಗೆ ನಾವು ಅವರನ್ನು ಮುಚ್ಚಿ ಬಿಟ್ಟಿದ್ದೇವೆ. ಅವರಿಗೆ ಈಗ ಏನೂ ಕಾಣುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(36,10,'36:10','ಅವರಿಗೆ ತಾವು ಎಚ್ಚರಿಕೆ ಕೊಟ್ಟರೂ ಕೊಡದಿದ್ದರೂ ಸರಿಯೇ. ಅವರು ವಿಶ್ವಾಸ ಸ್ವೀಕರಿಸುವವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(36,11,'36:11','ಖುರ್‍ಆನನ್ನು ಅನುಸರಿಸುವವರಿಗೆ ಮತ್ತು ದಯಾಮಯನಾದ ಅಲ್ಲಾಹನನ್ನು ಪ್ರತ್ಯಕ್ಷ ನೋಡದೆಯೇ ಭಯಪಡುವವರಿಗೆ ಮಾತ್ರ ನೀವು ಎಚ್ಚರಿಕೆ ನೀಡಬಲ್ಲಿರಿ. ಅಂಥವರಿಗೆ ಕ್ಷಮಾದಾನ ಮತ್ತು ಸನ್ಮಾನ್ಯ ಪ್ರತಿಫಲದ ಶುಭವಾರ್ತೆ ನೀಡಿರಿ.');
INSERT INTO "kannada" VALUES(36,12,'36:12','ಮೃತರನ್ನು ನಾವು ಖಂಡಿತ ಜೀವಂತ ಗೊಳಿಸಲಿದ್ದೇವೆ. ಅವರು ಮಾಡಿದ ಕರ್ಮಗಳನ್ನೂ ಅವರನ್ನು ಅನುಕರಿಸಿದ ಅವರ ಹಿಂಗಾಮಿಗಳ ಕರ್ಮಗಳನ್ನೂ ನಾವು ಬರೆಯುತ್ತಿದ್ದೇವೆ. ಎಲ್ಲವನ್ನು ನಾವು ಒಂದು ಸ್ಪಷ್ಟವಾದ ಗ್ರಂಥದಲ್ಲಿ ಕ್ಲಪ್ತ ಗೊಳಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(36,13,'36:13','ಇವರಿಗೆ ಒಂದು ಗ್ರಾಮದವರ ಉದಾಹರಣೆಯನ್ನು ಕೊಡಿರಿ. ಆ ಗ್ರಾಮದವರಿಗೆ ದೂತರು ಬಂದಾಗ.');
INSERT INTO "kannada" VALUES(36,14,'36:14','ನಾವು ಅವರ ಕಡೆಗೆ ಇಬ್ಬರು ಸಂದೇಶವಾಹ ಕರನ್ನು ಕಳುಹಿಸಿದೆವು. ಆಗ ಅವರು ಇಬ್ಬರನ್ನೂ ಸುಳ್ಳಾಗಿಸಿದರು. ಅನಂತರ ನಾವು ಮೂರನೆಯವನನ್ನು ಕಳುಹಿಸಿ ಅವರಿಬ್ಬರನ್ನು ಬಲಪಡಿಸಿದೆವು. ಅವರೆಲ್ಲರೂ `ನಾವು ನಿಮ್ಮ ಕಡೆಗೆ ಸಂದೇಶ ವಾಹಕರಾಗಿ ಕಳುಹಿಸಲ್ಪಟ್ಟವರು’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(36,15,'36:15','ನಾಡಿನವರು `ನೀವು ನಮ್ಮಂತಹ ಮನುಷ್ಯರಲ್ಲದೆ ಇನ್ನೇನೂ ಅಲ್ಲ. ದಯಾಮಯನಾದ ಅಲ್ಲಾಹನು ಏನನ್ನೂ ಇಳಿಸಿಲ್ಲ. ನೀವು ಬರೇ ಸುಳ್ಳರು’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(36,16,'36:16','ಸಂದೇಶವಾಹಕರು ಹೇಳಿದರು, ‘ನಾವು ಖಂಡಿತ ವಾಗಿಯೂ ನಿಮ್ಮ ಕಡೆಗೆ ಕಳುಹಿಸಲಾದ ಸಂದೇಶ ವಾಹಕರೆಂದು ನಮ್ಮ ಪ್ರಭು ತಿಳಿದಿದ್ದಾನೆ. ');
INSERT INTO "kannada" VALUES(36,17,'36:17','ಪ್ರಮಾಣಬದ್ಧ ಸಂದೇಶವನ್ನು ತಲುಪಿಸುವ ಹೊರತು ಇನ್ನಾವ ಹೊಣೆಯೂ ನಮ್ಮ ಮೇಲಿಲ್ಲ’.');
INSERT INTO "kannada" VALUES(36,18,'36:18','ಅವರು ಹೇಳಿದರು; ‘ನಿಮ್ಮ ದೆಸೆಯಿಂದ ನಮಗೆ ಅಶುಭವುಂಟಾಗಿದೆ. ನೀವು ಈ ಕಾರ್ಯವನ್ನು ಕೈ ಬಿಡದಿದ್ದರೆ, ನಾವು ನಿಮಗೆ ಕಲ್ಲೆಸೆಯುತ್ತೇವೆ. ಮತ್ತು ನಿಮಗೆ ನಮ್ಮಿಂದ ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆ ತಟ್ಟಲಿದೆ!’');
INSERT INTO "kannada" VALUES(36,19,'36:19','ಅದಕ್ಕೆ ಸಂದೇಶವಾಹಕರು “ನಿಮ್ಮ ಅಪಶಕು ನವು ನಿಮ್ಮಲ್ಲೇ ಇದೆ. ನಿಮಗೆ ಉಪದೇಶ ನೀಡಿದ ನಂತರವೂ! (ನೀವು ಹೀಗೆ ಹೇಳುತ್ತಿರುವಿರಾ?) ನಿಜಕ್ಕೂ ನೀವು ಮಿತಿಮೀರಿದವರಾಗಿರುತ್ತೀರಿ” ಎಂದು ಉತ್ತರಿಸಿದರು.');
INSERT INTO "kannada" VALUES(36,20,'36:20','ನಗರದ ಕೊನೆಯ ಅಂಚಿನಿಂದ ಒಬ್ಬನು ಓಡುತ್ತಾ ಬಂದು ಹೀಗೆಂದನು, “ಓ ನನ್ನ ಜನಾಂಗದವರೇ, ನೀವು ಈ ಸಂದೇಶವಾಹಕರನ್ನು ಅನುಸರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(36,21,'36:21','ನಿಮ್ಮಿಂದ ಯಾವ ಪ್ರತಿಫಲವನ್ನೂ ಕೇಳದ ಹಾಗೂ ಸನ್ಮಾರ್ಗ ಪ್ರಾಪ್ತರಾದ ಇವರನ್ನು ಅನುಸರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(36,22,'36:22','ಯಾವನು ನನ್ನನ್ನು ಸೃಷ್ಟಿಸಿರುವನೋ ಮತ್ತು ಯಾವನ ಕಡೆಗೆ ನೀವೆಲ್ಲರೂ ಮರಳಿ ಹೋಗಲಿರುವಿರೋ ಅವನನ್ನು ಆರಾಧಿಸದಿರಲು ನನಗೇನು ತಡೆಯಿದೆ?');
INSERT INTO "kannada" VALUES(36,23,'36:23','ನಾನೇನು ಅವನನ್ನು ಬಿಟ್ಟು ಇತರ ಆರಾಧ್ಯರನ್ನು ಸ್ವೀಕರಿಸಿಕೊಳ್ಳಬೇಕೆ? ದಯಾಮಯನಾದ ಅಲ್ಲಾಹನು ನನಗೇನಾದರೂ ಕೇಡನ್ನು ಬಯಸಿದರೆ ಈ ಆರಾಧ್ಯರ ಶಿಫಾರಸು ನನಗೇನೂ ಫಲಕಾರಿಯಾಗದು. ಅವರು ನನ್ನನ್ನು ಪಾರುಗೊಳಿಸಲಾರರು.');
INSERT INTO "kannada" VALUES(36,24,'36:24','ಹಾಗೆ ಮಾಡಿದರೆ ನಾನು ಖಂಡಿತವಾಗಿಯೂ ಸ್ಪಷ್ಟವಾದ ದುರ್ಮಾಗದಲ್ಲೇ ಉಳಿಯುವೆನು.');
INSERT INTO "kannada" VALUES(36,25,'36:25','ನಾನು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಮೇಲೆ ಖಂಡಿತ ವಿಶ್ವಾಸವಿರಿಸಿದ್ದೇನೆ. ಆದ್ದರಿಂದ ನೀವು ನನ್ನ ಮಾತನ್ನು ಕೇಳಿರಿ”.');
INSERT INTO "kannada" VALUES(36,26,'36:26','');
INSERT INTO "kannada" VALUES(36,27,'36:27','. ‘ಸ್ವರ್ಗದಲ್ಲಿ ಪ್ರವೇಶಿಸು’ ಎಂದು ಆ ವ್ಯಕ್ತಿಗೆ ಹೇಳಲಾಯಿತು. ಅವನು, “ಹಾ! ನನ್ನ ಪ್ರಭುವು ನನ್ನನ್ನು ಕ್ಷಮಿಸಿದ್ದನ್ನೂ ನನ್ನನ್ನು ಸನ್ಮಾನ್ಯ ಜನರಲ್ಲಿ ಸೇರಿಸಿದ್ದನ್ನೂ ನನ್ನ ಜನಾಂಗ ತಿಳಿದಿರುತ್ತಿದ್ದರೆ ಚೆನ್ನಾಗಿತ್ತು !” ಎಂದನು 6.');
INSERT INTO "kannada" VALUES(36,28,'36:28','ಅವನ ನಂತರ ನಾವು ಅವನ ಜನಾಂಗದ ಮೇಲೆ ಆಕಾಶದಿಂದ ಸೈನ್ಯವನ್ನೇನೂ ಇಳಿಸಲಿಲ್ಲ. ನಾವು ಸೇನೆಯನ್ನು ಕಳುಹಿಸುವುದೂ ಇಲ್ಲ ! ');
INSERT INTO "kannada" VALUES(36,29,'36:29','ಕೇವಲ ಒಂದು ಅಟ್ಟಹಾಸದ ಹೊರತು ಬೇರೇನೂ ಇರಲಿಲ್ಲ. ಆಗಲೇ ಅವರೆಲ್ಲರೂ ನಂದಿಹೋದರು .');
INSERT INTO "kannada" VALUES(36,30,'36:30','ಅಯ್ಯೋ ಈ ದಾಸರ ವ್ಯಥೆಯೇ! ಅವರ ಬಳಿಗೆ ಯಾವ ಸಂದೇಶವಾಹಕನು ಬಂದರೂ ಅವರು ಅವನನ್ನು ಗೇಲಿ ಮಾಡದೇ ಬಿಡಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(36,31,'36:31','ನಾವು ಅವರಿಗಿಂತ ಮುಂಚೆ ಎಷ್ಟೋ ಜನಾಂಗಗಳನ್ನು ಅವರೆಂದೂ ಮುಂದೆ ಇವರ ಕಡೆಗೆ ಮರಳಿ ಬಾರದಂತೆ ನಾಶಗೊಳಿಸಿದ್ದನ್ನು ಅವರು ಕಂಡಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(36,32,'36:32','ಅವರೆಲ್ಲರೂ ಒಂದು ದಿನ ನಮ್ಮ ಮುಂದೆ ಒಟ್ಟಾಗಿ ಹಾಜರಾಗದೆ ಇರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(36,33,'36:33','ನಿರ್ಜೀವ ಭೂಮಿಯು ಇವರಿಗೊಂದು ನಿದರ್ಶನವಾಗಿದೆ. ನಾವು ಅದನ್ನು ಸಜೀವಗೊಳಿಸಿದ್ದೇವೆ. ಅದರಿಂದ ಧಾನ್ಯಗಳನ್ನು ಹೊರತಂದಿದ್ದೇವೆ. ಅದರಿಂದಲೇ ಇವರು ಉಣ್ಣುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(36,34,'36:34','ನಾವು ಅದರಲ್ಲಿ ಖರ್ಜೂರ ಮತ್ತು ದ್ರಾಕ್ಷಿಗಳ ತೋಟಗಳನ್ನು ಉಂಟು ಮಾಡಿದ್ದೇವೆ ಮತ್ತು ಅದರೊಳಗಿಂದ ಚಿಲುಮೆಗಳನ್ನು ಹೊಮ್ಮಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(36,35,'36:35','ಅದರ ಫಲಗಳಿಂದ ಇವರು ತಿನ್ನಲಿಕ್ಕಾಗಿ. ಇದನ್ನು ಇವರ ಕೈಗಳು ನಿರ್ಮಿಸಿಲ್ಲ. ಹಾಗಿದ್ದೂ ಇವರು ಕೃತಜ್ಞರಾಗುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(36,36,'36:36','ಭೂಮಿ ಉತ್ಪಾದಿಸುವ ವಸ್ತುವಿನಿಂದಲೂ ಇವರ ವರ್ಗಗಳಿಂದಲೂ ಮತ್ತು ಇವರಿಗೆ ತಿಳಿಯದಿರುವ ವಸ್ತುವಿನಿಂದಲೂ ಜೋಡಿಗಳನ್ನು ಸೃಷ್ಟಿಸಿದವನು ಪರಮ ಪಾವನನು.');
INSERT INTO "kannada" VALUES(36,37,'36:37','ರಾತ್ರಿಯು ಇವರಿಗೊಂದು ನಿದರ್ಶನವಾಗಿದ್ದು ನಾವು ಅದರಿಂದ ಹಗಲನ್ನು ಅನಾವರಣಗೊಳಿಸುತ್ತೇವೆ. ಆಗ ಇವರ ಮೇಲೆ ಕತ್ತಲು ಕವಿಯುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(36,38,'36:38','ಸೂರ್ಯನು ತನ್ನ ನೆಲೆಗೆ ಚಲಿಸುತ್ತಿದ್ದಾನೆ . ಪ್ರತಾಪನೂ ಸರ್ವಜ್ಞನೂ ಆದವನ ನಿರ್ಣಯವಿದು.');
INSERT INTO "kannada" VALUES(36,39,'36:39','ಚಂದ್ರನಿಗೆ ನಾವು ಸ್ಥಾನಗಳನ್ನು ನಿಶ್ಚಯಿಸಿಕೊಟ್ಟಿದ್ದು ಕೊನೆಗೆ ಅದು ಖರ್ಜೂರದ ಗೊನೆಯ ಹಳೆಯ ದಿಂಡಿನ ಸ್ಥಿತಿಗೆ ಮರಳುತ್ತದೆ .');
INSERT INTO "kannada" VALUES(36,40,'36:40','ಸೂರ್ಯನಿಗೆ ಚಂದ್ರನನ್ನು ತಲುಪಲಾಗದು. ರಾತ್ರಿಯು ಹಗಲನ್ನು ಮೀರಿ ಹೋಗದು. ಎಲ್ಲವೂ ಅವುಗಳ ಭ್ರಮಣ ಪಥದಲ್ಲಿ ತೇಲುತ್ತಿವೆ .');
INSERT INTO "kannada" VALUES(36,41,'36:41','ತುಂಬಿದ ನಾವೆಯಲ್ಲಿ ಇವರ ಸಂತತಿಯನ್ನು ನಾವು ಹೊತ್ತು ತೇಲುವಂತೆ ಮಾಡಿದ್ದು ಇವರಿಗೊಂದು ನಿದರ್ಶನವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(36,42,'36:42','ಇವರು ಸವಾರಿ ಮಾಡುವ, ಅದೇ ತರದ ವಾಹನಗಳನ್ನು ನಾವು ಇವರಿಗಾಗಿ ಸೃಷ್ಟಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(36,43,'36:43','ನಾವಿಚ್ಛಿಸಿದರೆ, ಇವರನ್ನು ಮುಳುಗಿಸಬಲ್ಲೆವು. ಆಗ ಇವರಿಗೆ ಸಹಾಯಕರಾರೂ ಇರುತ್ತಿರಲಿಲ್ಲ. ಇವರು ಪಾರಾಗುತ್ತಲೂ ಇರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(36,44,'36:44','ಕೇವಲ ನಮ್ಮ ಕಡೆಯಿಂದಿರುವ ಅನುಗ್ರಹ ಹಾಗೂ ನಿರ್ದಿಷ್ಟ ಅವಧಿಯವರೆಗಿನ ಸುಖ ಭೋಗವಲ್ಲದೆ!');
INSERT INTO "kannada" VALUES(36,45,'36:45','ನಿಮ್ಮ ಎದುರಿಗೇ ಇರುವ (ಇಹಲೋಕದ ಶಿಕ್ಷೆ) ಹಾಗೂ ನಿಮ್ಮ ಬೆನ್ನ ಹಿಂದೆ ಇರುವ (ಪರಲೋಕದ ಶಿಕ್ಷೆಯ) ಬಗ್ಗೆ ಜಾಗೃತರಾಗಿರಿ. ನೀವು ದೇವಾ ನುಗ್ರಹಕ್ಕೆ ಪಾತ್ರರಾಗುವಿರಿ ಎಂದು ಇವರಿಗೆ ಹೇಳಲಾದರೆ! (ಇವರು ಗಮನಹರಿಸುವುದಿಲ್ಲ).');
INSERT INTO "kannada" VALUES(36,46,'36:46','ಇವರ ಮುಂದೆ ಇವರ ಪ್ರಭುವಿನ ನಿದರ್ಶನಗಳ ಪೈಕಿ ಯಾವ ನಿದರ್ಶನ ಬಂದರೂ ಇವರು ಅದರಿಂದ ವಿಮುಖರಾಗುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(36,47,'36:47','ಅಲ್ಲಾಹನು ನಿಮಗೆ ನೀಡಿರುವ ಜೀವನಾ ಧಾರದಿಂದ ಒಂದಿಷ್ಟನ್ನು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಖರ್ಚು ಮಾಡಿರಿ ಎಂದು ಹೇಳಲಾದರೆ, ಈ ಸತ್ಯ ನಿಷೇಧಿಗಳು ಸತ್ಯವಿಶ್ವಾಸಿಗಳೊಡನೆ - “ಅಲ್ಲಾಹು ಇಚ್ಛಿಸುತ್ತಿದ್ದರೆ ಸ್ವತಃ ಅವನೇ ಆಹಾರ ಕೊಡುತ್ತಿರುವಾಗ ನಾವು ಯಾಕೆ ಆಹಾರ ಕೊಡ ಬೇಕು? ನೀವು ಸ್ಪಷ್ಟವಾಗಿ ದಾರಿಗೆಟ್ಟು ಹೋಗಿದ್ದೀರಿ” ಎಂದು ಹೇಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(36,48,'36:48','“(ಪ್ರಳಯ ದಿನದ) ಈ ತಾಕೀತು ಯಾವಾಗ ಬರುವುದು? ನೀವು ಸತ್ಯವಂತರಾಗಿದ್ದರೆ ತಿಳಿಸಿರಿ” ಎಂದು ಇವರು ಹೇಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(36,49,'36:49','ಒಂದು ಘೋರ ಅಟ್ಟಹಾಸದ ಹೊರತು ಇವರು ಇನ್ನೇನೂ ನಿರೀಕ್ಷಿಸುತ್ತಿಲ್ಲ. ಇವರು ತರ್ಕ ಮಾಡುತ್ತಿರುವಂತೆಯೇ ಅದು ಇವರನ್ನು ಹಿಡಿಯುವುದು.');
INSERT INTO "kannada" VALUES(36,50,'36:50','ಆಗ ಇವರಿಗೆ ಮರಣಪೂರ್ವ ಹೇಳಿಕೆಗೂ ಸಾಧ್ಯವಾಗದು. ತಮ್ಮ ಮನೆಯವರೆಡೆಗೆ ಮರಳಲಿಕ್ಕೂ ಆಗದು .');
INSERT INTO "kannada" VALUES(36,51,'36:51','ಕಹಳೆಯನ್ನು ಊದಲಾಗುವುದು. ಆಗ ಇವರು ತಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಗೆ ತಮ್ಮ ಸಮಾಧಿಗಳಿಂದ ಧಾವಿಸಿ ಬರುವರು.');
INSERT INTO "kannada" VALUES(36,52,'36:52','ಇವರು ಕೇಳುವರು; ಅಯ್ಯೋ, ನಮ್ಮ ನಾಶವೇ, ನಮ್ಮನ್ನು ನಮ್ಮ ಶಯನ ಗೃಹದಿಂದ ಎಬ್ಬಿಸಿದವರಾರು?’ ಇದು ದಯಾಮಯನಾದ ಅಲ್ಲಾಹನು ಮಾಡಿದ ವಾಗ್ದಾನವಾಗಿದ್ದು ಸಂದೇಶವಾಹಕರು ಸತ್ಯವನ್ನೇ ಹೇಳಿದ್ದರು .');
INSERT INTO "kannada" VALUES(36,53,'36:53','ಅದು ಒಂದೇ ಒಂದು ಅಟ್ಟಹಾಸವಲ್ಲದೆ ಮತ್ತೇ ನಲ್ಲ. ಆಗ ಅವರೆಲ್ಲರೂ ನಮ್ಮ ಮುಂದೆ ಹಾಜರು ಗೊಳಿಸಲ್ಪಡುವರು.');
INSERT INTO "kannada" VALUES(36,54,'36:54','ಇಂದು ಯಾರ ಮೇಲೂ ಸ್ವಲ್ಪವೂ ಅನ್ಯಾಯವಾಗದು. ನೀವು ಮಾಡುತ್ತಿದ್ದ ಕರ್ಮಗಳಿಗಲ್ಲದೆ ನಿಮಗೆ ಪ್ರತಿಫಲ ನೀಡಲಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(36,55,'36:55','ಅಂದು ಸ್ವರ್ಗವಾಸಿಗಳು ಕಾರ್ಯನಿರತರು. ಸುಖಾನುಭವಿಗಳು.');
INSERT INTO "kannada" VALUES(36,56,'36:56','ಅವರೂ ಅವರ ಪತ್ನಿಯರೂ ದಟ್ಟ ನೆರಳುಗಳಲ್ಲಿ ಸುಖಾಸನಗಳ ಮೇಲೆ ಒರಗಿಕೊಂಡಿರುವರು.');
INSERT INTO "kannada" VALUES(36,57,'36:57','ಅವರಿಗಲ್ಲಿ ಹಣ್ಣುಹಂಪಲುಗಳಿವೆ. ಅವರೇನು ಬಯಸುತ್ತಾರೋ ಅದೆಲ್ಲವೂ ಅವರಿಗಾಗಿ ಸಿದ್ಧವಿದೆ.');
INSERT INTO "kannada" VALUES(36,58,'36:58','ದಯಾನಿಧಿ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಅವರಿಗೆ ವಾಚ್ಯ ಸಲಾಮ್!');
INSERT INTO "kannada" VALUES(36,59,'36:59','ಓ ಅಪರಾಧಿಗಳೇ, ಇಂದು ನೀವು ಬೇರ್ಪಟ್ಟು ನಿಲ್ಲಿರಿ.');
INSERT INTO "kannada" VALUES(36,60,'36:60','ಓ ಆದಮರ ಮಕ್ಕಳೇ, ನಿಮಗೆ ನಾನು ಆದೇ ಶಿಸಲಿಲ್ಲವೇ? ‘ನೀವು ಶೈತಾನನಿಗೆ ಆರಾಧಿಸ ಬೇಡಿರಿ, ಅವನು ನಿಮ್ಮ ಪ್ರತ್ಯಕ್ಷ ಶತ್ರುವಾಗಿ ರುವನು.');
INSERT INTO "kannada" VALUES(36,61,'36:61','ನನ್ನನ್ನು ಆರಾಧಿಸಿರಿ, ಇದು ನೇರ ಮಾರ್ಗವಾಗಿರುತ್ತದೆ’ ಎಂದು.');
INSERT INTO "kannada" VALUES(36,62,'36:62','ಆದರೆ, ಅವನು ನಿಮ್ಮ ಪೈಕಿ ಅಧಿಕ ಜನರನ್ನು ದಾರಿ ತಪ್ಪಿಸಿದನು. ನಿಮಗೆ ಯೋಚಿಸುವವ ರಾಗಬಹುದಿತ್ತಲ್ಲ?');
INSERT INTO "kannada" VALUES(36,63,'36:63','ಇದು ನಿಮಗೆ ಎಚ್ಚರಿಕೆ ನೀಡಲಾಗುತ್ತಿದ್ದ ನರಕವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(36,64,'36:64','ನೀವು (ಭೂಲೋಕದಲ್ಲಿ) ಮಾಡುತ್ತಿದ್ದ ಸತ್ಯನಿಷೇಧದ ಫಲವಾಗಿ ಈಗ ಈ ನರಕದಲ್ಲಿ ಪ್ರವೇಶವಾಗಿರಿ.');
INSERT INTO "kannada" VALUES(36,65,'36:65','ಅಂದು ನಾವು ಇವರ ಬಾಯಿಗಳಿಗೆ ಮುದ್ರೆ ಹಾಕುತ್ತೇವೆ. ಇವರು (ಭೂಲೋಕದಲ್ಲಿ) ಏನು ಸಂಪಾದಿಸುತ್ತಿದ್ದರೆಂದು ಇವರ ಕೈಗಳು ನಮ್ಮೊಡನೆ ಮಾತಾಡಲಿವೆ. ಮತ್ತು ಇವರ ಕಾಲುಗಳು ಸಾಕ್ಷ್ಯ ಹೇಳಲಿವೆ.');
INSERT INTO "kannada" VALUES(36,66,'36:66','ನಾವಿಚ್ಛಿಸಿದರೆ, ಇವರ ಕಣ್ಣುಗಳನ್ನು ಉಜ್ಜಿ ಬಿಡುತ್ತಿದ್ದೆವು. ಆಗ ಇವರು ದಾರಿಯೆಡೆಗೆ ಧಾವಿಸುತ್ತಿದ್ದರು. ಆದರೆ ಇವರು ಹೇಗೆ ಕಾಣುವರು?');
INSERT INTO "kannada" VALUES(36,67,'36:67','ನಾವಿಚ್ಛಿಸಿದರೆ, ಇವರಿದ್ದ ಸ್ಥಳದಲ್ಲೇ ರೂಪಾಂತರಗೊಳಿಸಬಹುದಿತ್ತು. ಆಗ ಇವರಿಗೆ ಮುಂದೆ ನಡೆಯಲಿಕ್ಕೂ ಹಿಂದಕ್ಕೆ ಮರಳಲಿಕ್ಕೂ ಸಾಧ್ಯವಾಗುತ್ತಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(36,68,'36:68','ನಾವು ಯಾರಿಗೆ ದೀರ್ಘಾಯುಷ್ಯ ನೀಡುತ್ತೇವೋ ಅವನ ಪ್ರಕೃತಿಯನ್ನು ಪೂರ್ವಸ್ಥಿತಿಗೆ ಮರಳಿಸುತ್ತೇವೆ . ಅವರೇಕೆ ಚಿಂತಿಸುವುದಿಲ್ಲ?');
INSERT INTO "kannada" VALUES(36,69,'36:69','ನಾವು ಈ ಪ್ರವಾದಿಗೆ ಕವಿತೆ ಕಲಿಸಿಲ್ಲ. ಕವಿತೆಯು ಅವರಿಗೆ ಭೂಷಣವೂ ಅಲ್ಲ . ಇದೊಂದು ಉಪ ದೇಶ ಹಾಗೂ ಸ್ಪಷ್ಟವಾಗಿ ಓದಲ್ಪಡುವ (ಖುರ್ ಆನ್) ಗ್ರಂಥವಲ್ಲದೆ ಬೇರೇನೂ ಅಲ್ಲ.');
INSERT INTO "kannada" VALUES(36,70,'36:70','ಇದು ಜೀವಂತವಿರುವ ಪ್ರತಿಯೊಬ್ಬನಿಗೆ ಎಚ್ಚ ರಿಕೆ ನೀಡಲಿಕ್ಕಾಗಿ ಮತ್ತು ಸತ್ಯನಿಷೇಧಿಗಳ ಮೇಲೆ ನಿರ್ದಿಷ್ಟ ವಚನವು ನಿಜಗೊಳ್ಳಲಿಕ್ಕಾಗಿ ಬಂದಿದೆ.');
INSERT INTO "kannada" VALUES(36,71,'36:71','ನಾವು ನಮ್ಮ ಶಕ್ತಿಯಿಂದ ಇವರಿಗಾಗಿ ಜಾನುವಾರುಗಳನ್ನು ಸೃಷ್ಟಿಸಿರುವುದು ಗೊತ್ತಿಲ್ಲವೆ? ಈಗ ಇವರು ಅವುಗಳ ಮಾಲಿಕರು.');
INSERT INTO "kannada" VALUES(36,72,'36:72','ನಾವು ಅವುಗಳನ್ನು ಇವರ ಹತೋಟಿಯಲ್ಲಿರಿಸಿದ್ದೇವೆ. ಅವುಗಳಲ್ಲಿ ಕೆಲವುದರ ಮೇಲೆ ಇವರು ಸವಾರಿ ಮಾಡುತ್ತಾರೆ. ಇನ್ನು ಕೆಲವುದರ ಮಾಂಸ ಇವರು ತಿನ್ನುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(36,73,'36:73','ಅವುಗಳಲ್ಲಿ ಇವರಿಗಾಗಿ ತರತರದ ಪ್ರಯೋಜನಗಳೂ ಪೇಯಗಳೂ ಇವೆ. ಆದರೂ ಇವರು ಕೃತಜ್ಞರಾಗುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(36,74,'36:74','ಇವರು ತಮಗೆ ಸಹಾಯ ಲಭಿಸಬಹುದೆಂದು ಅಲ್ಲಾಹನ ಹೊರತು ಇತರರನ್ನು ದೇವರುಗಳಾಗಿ ಮಾಡಿಕೊಂಡರು.');
INSERT INTO "kannada" VALUES(36,75,'36:75','ಆ ದೇವರುಗಳು ಇವರಿಗೆ ನೆರವು ನೀಡಲು ಶಕ್ತರಾಗುವುದಿಲ್ಲ. ಅವರು ಇವರ ಜೊತೆಗೆ (ನರಕ ದಲ್ಲಿ) ಹಾಜರುಗೊಳಿಸಲ್ಪಡುವ ಸೇನೆಯಾಗಿರುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(36,76,'36:76','ಆದ್ದರಿಂದ ಇವರ ಮಾತುಗಳು ನಿಮ್ಮನ್ನು ದುಃಖ ಕ್ಕೀಡು ಮಾಡದಿರಲಿ. ಇವರು ಅಡಗಿಸುವ ಮತ್ತು ತೆರೆದಿಡುವ ಎಲ್ಲ ವಿಷಯಗಳನ್ನು ನಾವು ಬಲ್ಲೆವು.');
INSERT INTO "kannada" VALUES(36,77,'36:77','ಮನುಷ್ಯನಿಗೆ ಗೊತ್ತಿಲ್ಲವೆ? ಅವನನ್ನು ನಾವು ವೀರ್ಯದಿಂದ ಸೃಷ್ಟಿಸಿದ್ದೇವೆ ಎಂಬುದನ್ನು. ಇದೀಗ ಅವನು ಸ್ಪಷ್ಟ ಜಗಳಗಂಟನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(36,78,'36:78','ಈಗ ಅವನು ನಮಗೆ ಹೋಲಿಕೆ ಕೊಡುತ್ತಾನೆ ಮತ್ತು ತಾನು ಸೃಷ್ಠಿಯಾದುದನ್ನು ಮರೆತು ಬಿಡುತ್ತಾನೆ. ‘ಈ ಎಲುಬುಗಳು ಶಿಥಿಲವಾಗಿ ಹೋದ ಬಳಿಕ ಇವುಗಳನ್ನು ಜೀವಂತಗೊಳಿಸುವವನಾರು?’ ಎಂದು ಕೇಳುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(36,79,'36:79','ಹೇಳಿರಿ, ಇವುಗಳನ್ನು ಪ್ರಥಮ ಬಾರಿ ಸೃಷ್ಟಿಸಿದವನೇ ಪುನಃ ಜೀವಂತಗೊಳಿಸುವನು . ಅವನು ಪ್ರತಿಯೊಂದು ಸೃಷ್ಟಿಯ ಬಗ್ಗೆಯೂ ಚೆನ್ನಾಗಿ ಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(36,80,'36:80','ನಿಮಗಾಗಿ ಹಚ್ಚಹಸಿರಾದ ಮರದಿಂದ ಬೆಂಕಿ ಯನ್ನುಂಟು ಮಾಡಿದವನು ಅವನೇ. ನೀವು ಅದರಿಂದ ಬೆಂಕಿ ಉರಿಸುತ್ತಿರುವಿರಿ .');
INSERT INTO "kannada" VALUES(36,81,'36:81','ಆಕಾಶಗಳನ್ನು ಮತ್ತು ಭೂಮಿಯನ್ನು ಸೃಷ್ಟಿಸಿದವನು ಇವರಂಥವರನ್ನು ಸೃಷ್ಟಿಸಲು ಶಕ್ತನಲ್ಲವೇ? ಶಕ್ತನಾಗಿರುವನು. ಅವನು ಮಹಾ ಸೃಷ್ಟಿ ಕರ್ತನೂ ಸರ್ವಜ್ಞಾನಿಯೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(36,82,'36:82','ಅವನು ಯಾವುದಾದರೊಂದು ವಿಷಯದ ಸಂಕಲ್ಪ ಮಾಡಿದರೆ, ಅದಕ್ಕೆ `ಆಗು’ ಎನ್ನುವನು. ಆಗಲೇ ಅದು ಆಗಿ ಬಿಡುವುದು.');
INSERT INTO "kannada" VALUES(36,83,'36:83','ಸಕಲ ವಸ್ತುಗಳ ಪ್ರಭುತ್ವವು ಯಾವನ ಅಧೀನ ದಲ್ಲಿದೆಯೋ ಅವನೇ ಪರಮಪಾವನನು. ನೀವು ಅವನ ಕಡೆಗೇ ಮರಳಿಸಲ್ಪಡುವಿರಿ.');
INSERT INTO "kannada" VALUES(37,1,'37:1','ಅಣಿಅಣಿಯಾಗಿ ಕಿಕ್ಕಿರಿದು ನಿಂತಿರುವ ಮಲಕುಗಳಾಣೆ.');
INSERT INTO "kannada" VALUES(37,2,'37:2','ಮೋಡವನ್ನು ಎಳೆದೊಯ್ಯುವ ಮಲಕುಗಳಾಣೆ.');
INSERT INTO "kannada" VALUES(37,3,'37:3','ಖುರ್‍ಆನನ್ನು ಉಪದೇಶವನ್ನಾಗಿ ಓದುವವರಾಣೆ .');
INSERT INTO "kannada" VALUES(37,4,'37:4','ಖಂಡಿವಾಗಿಯೂ ನಿಮ್ಮ ಆರಾಧ್ಯನು ಏಕೈಕನು.');
INSERT INTO "kannada" VALUES(37,5,'37:5','ಅವನು ಭೂಮಿ-ಆಕಾಶಗಳ ಹಾಗೂ ಅವುಗಳ ಎಡೆಯಲ್ಲಿರುವ ಸಕಲ ವಸ್ತುಗಳ ಪರಿಪಾಲಕನು ಮತ್ತು ಉದಯಸ್ಥಾನಗಳ ಒಡೆಯನು .');
INSERT INTO "kannada" VALUES(37,6,'37:6','ನಾವು ಇಹದಾಕಾಶವನ್ನು ನಕ್ಷತ್ರಗಳ ಚೆಲುವಿ ನಿಂದ ಅಲಂಕೃತಗೊಳಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(37,7,'37:7','ಅದನ್ನು ಪ್ರತಿಯೊಬ್ಬ ಧಿಕ್ಕಾರಿ ಶೈತಾನನಿಂದ ಸಂರಕ್ಷಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(37,8,'37:8','');
INSERT INTO "kannada" VALUES(37,9,'37:9','ಶೈತಾನರು (ಮಲಕ್‍ಗಳ) ಉನ್ನತ ಲೋಕದಿಂದ ಆಲಿಸಲಾರರು, ಎಲ್ಲ ದಿಕ್ಕುಗಳಿಂದಲೂ ಅವರನ್ನು ಹೊಡೆದು ಅಟ್ಟಲಾಗುತ್ತದೆ ಮತ್ತು ಅವರಿಗೆ ನಿರಂತರ ಶಿಕ್ಷೆ ಇದೆ.');
INSERT INTO "kannada" VALUES(37,10,'37:10','ಆದರೂ ಅವರ ಪೈಕಿ ಯಾರಾದರೂ ಕದ್ದಾಲಿಸಿದರೆ ತೂರಿ ಬರುವ ತೀಕ್ಷ್ಣ ಜ್ವಾಲೆಯು ಅವರನ್ನು ಬೆನ್ನಟ್ಟುತ್ತದೆ .');
INSERT INTO "kannada" VALUES(37,11,'37:11','ಅವರಲ್ಲಿ ಕೇಳಿ ನೋಡಿರಿ, ಅವರ ಸೃಷ್ಟಿಯು ಹೆಚ್ಚು ಕಷ್ಟವೋ ಅಥವಾ ನಾವು ಸೃಷ್ಟಿಸಿಟ್ಟಿರುವ ಇತರ ವಸ್ತುಗಳ ಸೃಷ್ಟಿಯೋ? ಅಂಟಾದ ರಾಡಿ ಮಣ್ಣಿನಿಂದ ಅವರನ್ನು ನಾವು ಸೃಷ್ಟಿಸಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(37,12,'37:12','ಆದರೆ ನೀವು ಅಚ್ಚರಿ ಪಡುತ್ತೀರಿ. ಅವರು ಅದನ್ನು ಪರಿಹಾಸ್ಯ ಮಾಡುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(37,13,'37:13','ಉಪದೇಶ ಕೊಟ್ಟರೆ ಅವರು ನೆನಪಿಟ್ಟು ಗ್ರಹಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(37,14,'37:14','ಯಾವುದೇ ದೃಷ್ಟಾಂತವನ್ನು ಕಂಡಾಗ ಪರಿಹಾಸ್ಯ ಮಾಡುತ್ತಾರೆ');
INSERT INTO "kannada" VALUES(37,15,'37:15','ಅವರು ಹೇಳುತ್ತಾರೆ; “ಇದು ಸ್ಪಷ್ಟವಾದ ಜಾದುವೇ ಹೊರತು ಇನ್ನೇನಲ್ಲ.');
INSERT INTO "kannada" VALUES(37,16,'37:16','ನಾವು ಸತ್ತು ಮಣ್ಣಾಗಿ ಅಸ್ಥಿಪಂಜರವಾಗಿ ಹೋದ ಬಳಿಕ ಪುನಃ ಜೀವಂತಗೊಳಿಸಿ ಎಬ್ಬಿಸಲ್ಪಡುವುದು ಖಚಿತವೇ?');
INSERT INTO "kannada" VALUES(37,17,'37:17','ಗತಕಾಲದ ನಮ್ಮ ಪೂರ್ವಜರೂ ಪುನರ್ಜೀವ ತಾಳಿ ಎಬ್ಬಿಸಲ್ಪಡುವರೇ?”');
INSERT INTO "kannada" VALUES(37,18,'37:18','ಉತ್ತರಿಸಿರಿ, “ಹೌದು! ಆಗ ನೀವು ನಿಂದ್ಯರಾಗಿರುತ್ತೀರಿ!”');
INSERT INTO "kannada" VALUES(37,19,'37:19','ಅದು ಕೇವಲ ಒಂದು ಘರ್ಜನೆಯಾಗಿರುವುದು. ಆಗ ಹಠಾತ್ತನೆ ಅವರು ಕಣ್ಣುಬಿಟ್ಟು ನೋಡುವರು.');
INSERT INTO "kannada" VALUES(37,20,'37:20','ಆಗ ಅವರು, “ಅಯ್ಯೋ ನಮ್ಮ ನಾಶವೇ! ಎನ್ನುವರು. (ಆಗ ಹೇಳಲಾಗುವುದು;) ಇದು ಪ್ರತಿಫಲದ ದಿನ.');
INSERT INTO "kannada" VALUES(37,21,'37:21','ಇದು ನೀವು ಸುಳ್ಳಾಗಿಸುತ್ತಿದ್ದ ವಿಧಿ ನಿರ್ಣಾಯಕ ದಿನವೇ ಆಗಿದೆ .');
INSERT INTO "kannada" VALUES(37,22,'37:22','');
INSERT INTO "kannada" VALUES(37,23,'37:23','(ಓ ಮಲಕ್‍ಗಳೇ)” ಅಕ್ರಮಿಗಳನ್ನೂ ಅವರ ಜೊತೆಗಾರರನ್ನೂ ಅವರು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಆರಾಧಿಸುತ್ತಿದ್ದ ಆರಾಧ್ಯರನ್ನೂ ಒಟ್ಟುಗೂಡಿಸಿರಿ. ಅನಂತರ ಅವರನ್ನು ಹೊತ್ತಿ ಉರಿಯುವ ನರಕದ ದಾರಿಗೆ ಒಯ್ಯಿರಿ.');
INSERT INTO "kannada" VALUES(37,24,'37:24','ಇವರನ್ನು ತಡೆದು ನಿಲ್ಲಿಸಿರಿ. ಇವರನ್ನು ಪ್ರಶ್ನಿ ಸಲಿಕ್ಕಿದೆ”.');
INSERT INTO "kannada" VALUES(37,25,'37:25','“ನಿಮಗೇನಾಗಿದೆ? ಈಗ ಪರಸ್ಪರ ಸಹಾಯ ಮಾಡುವುದಿಲ್ಲವೇಕೆ?”');
INSERT INTO "kannada" VALUES(37,26,'37:26','“ಹಾಗಲ್ಲ! ಇವರಿಂದು ಸ್ವತಃ ಶರಣಾಗತರು!”');
INSERT INTO "kannada" VALUES(37,27,'37:27','ಆ ಬಳಿಕ ಪರಸ್ಪರ ಪ್ರಶ್ನಿಸುತ್ತಾ ಅವರು ಎದುರಾಗುವರು.');
INSERT INTO "kannada" VALUES(37,28,'37:28','(ಅನುಯಾಯಿಗಳು) “ನೀವು ನಮ್ಮ ಬಳಿಗೆ ಆಣೆ ಹಾಕುತ್ತ ಬರುತ್ತಿದ್ದೀರಲ್ಲವೆ?” ಎಂದು ಕೇಳುವರು .');
INSERT INTO "kannada" VALUES(37,29,'37:29','ಆಗ ಅವರು ಹೇಳುವರು, “ಅಲ್ಲ, ನೀವು ಸತ್ಯವಿಶ್ವಾಸ ಸ್ವೀಕರಿಸುವವರೇ ಆಗಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(37,30,'37:30','ನಿಮ್ಮ ಮೇಲೆ ನಮ್ಮ ಪ್ರಭುತ್ವವೇನೂ ಇರಲಿಲ್ಲ. ನೀವೂ (ನಮ್ಮಂತೆಯೇ) ದಾರಿ ಕೆಟ್ಟವರಾಗಿದ್ದಿರಿ.');
INSERT INTO "kannada" VALUES(37,31,'37:31','ಅಂತೂ ನಮ್ಮ ಪ್ರಭುವಿನ ವಚನವು ನಮ್ಮ ಮೇಲೆ ನಿಜವಾಯಿತು. ನಾವೆಲ್ಲರೂ ಶಿಕ್ಷೆಯ ರುಚಿ ಅನುಭವಿಸಲೇಬೇಕು.');
INSERT INTO "kannada" VALUES(37,32,'37:32','ಹೀಗೆ ನಿಮ್ಮನ್ನು ನಾವು ದಾರಿಗೆಡಿಸಿದೆವು, ನಾವು ಸ್ವತಃ ದಾರಿಗೆಟ್ಟಿದ್ದೆವು.”');
INSERT INTO "kannada" VALUES(37,33,'37:33','ಹೀಗೆ, ಅಂದು ಅವರು ಶಿಕ್ಷೆಯಲ್ಲಿ ಸಹಭಾಗಿಗಳಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(37,34,'37:34','ನಾವು ಅಪರಾಧಿಗಳೊಂದಿಗೆ ಹೀಗೆಯೇ ವರ್ತಿಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(37,35,'37:35','ಅಲ್ಲಾಹನ ಹೊರತು ನೈಜ ಆರಾಧ್ಯನಿಲ್ಲ ಎಂದು ಅವರಿಗೆ ಹೇಳಿದಾಗ ಇವರು ದರ್ಪ ತೋರುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(37,36,'37:36','“ನಾವು ಒಬ್ಬ ಹುಚ್ಚ ಕವಿಗಾಗಿ ನಮ್ಮ ಆರಾಧ್ಯರನ್ನು ಬಿಟ್ಟುಬಿಡಬೇಕೆ?” ಎನ್ನುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(37,37,'37:37','ಹಾಗಲ್ಲ, ಆ ವ್ಯಕ್ತಿ ಸತ್ಯವನ್ನು ತಂದಿದ್ದರು ಮತ್ತು ಗತ ಸಂದೇಶವಾಹಕರನ್ನು ದೃಢೀಕರಿಸಿದ್ದರು.');
INSERT INTO "kannada" VALUES(37,38,'37:38','(ಆಗ ಅವರಿಗೆ ಹೇಳಲಾಗುವುದು) “ನೀವು ವೇದನಾತ್ಮಕ ಶಿಕ್ಷೆಯನ್ನು ಖಂಡಿತ ಸವಿಯಲಿರುವಿರಿ.');
INSERT INTO "kannada" VALUES(37,39,'37:39','ನೀವು ಮಾಡುತ್ತಿದ್ದ ಕರ್ಮಗಳ ಪ್ರತಿಫಲವನ್ನೇ ನಿಮಗೆ ಕೊಡಲಾಗುತ್ತಿದೆ”.');
INSERT INTO "kannada" VALUES(37,40,'37:40','ಆದರೆ ಅಲ್ಲಾಹನ ನಿಷ್ಕಳಂಕ ದಾಸರು ಇದರಿಂದ ಹೊರತಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(37,41,'37:41','ಅವರಿಗೆ ಚಿರಪರಿಚಿತ ಆಹಾರವಿದೆ.');
INSERT INTO "kannada" VALUES(37,42,'37:42','ಅಂದರೆ ಮೋಜಿನ ಆಹಾರಗಳು. ಅವರು ಗೌರವಾನ್ವಿತರು');
INSERT INTO "kannada" VALUES(37,43,'37:43','ಅನುಗ್ರಹೀತ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ.');
INSERT INTO "kannada" VALUES(37,44,'37:44','ಸುಖಾಸನಗಳಲ್ಲಿ ಎದುರು ಬದುರಾಗಿ ಕುಳಿತು ಕೊಳ್ಳುವರು.');
INSERT INTO "kannada" VALUES(37,45,'37:45','ಮದಿರೆಯ ಚಿಲುಮೆಯಿಂದ ತುಂಬಿಸಿಕೊಂಡ ಪಾನಪಾತ್ರೆಯನ್ನು ಅವರ ಸುತ್ತಲೂ ತರಲಾ ಗುವುದು.');
INSERT INTO "kannada" VALUES(37,46,'37:46','ಬಿಳಿಯಾದ, ಕುಡಿಯುವವರಿಗೆ ಅತ್ಯಂತ ರುಚಿಕರವಾದ ಮದ್ಯ.');
INSERT INTO "kannada" VALUES(37,47,'37:47','ಆ ಸುರೆಯಲ್ಲಿ ಮತಿವಿಕಲತೆಯಿಲ್ಲ, ಅದರ ಪಾನದಿಂದ ಅವರಿಗೆ ನಶೆಯೇರುವುದೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(37,48,'37:48','ದೃಷ್ಟಿಯನ್ನು ಕಾಪಾಡಿಕೊಳ್ಳುವ ಬಟ್ಟಲು ಕಂಗಳ ತರುಣಿಯರು ಅವರ ಪಕ್ಕದಲ್ಲಿರುವರು.');
INSERT INTO "kannada" VALUES(37,49,'37:49','ಅವರು ಬಕಪಕ್ಷಿಯ ಮುಚ್ಚಿಟ್ಟ ಮೊಟ್ಟೆಯಂತೆ (ತಿಳಿ ಕೆಂಬಣ್ಣದ) ಸುಂದರಿಯರು.');
INSERT INTO "kannada" VALUES(37,50,'37:50','ಅನಂತರ ಅವರು ಪರಸ್ಪರ ಅಭಿಮುಖವಾಗಿ ಸ್ಥಿತಿಗತಿಗಳನ್ನು ವಿಚಾರಿಸುವರು.');
INSERT INTO "kannada" VALUES(37,51,'37:51','ಅವರಲ್ಲೊಬ್ಬನು ಹೀಗೆನ್ನುವನು, ‘ನನಗೊಬ್ಬ ಸಂಗಾತಿಯಿದ್ದನು.');
INSERT INTO "kannada" VALUES(37,52,'37:52','ಅವನು ನನ್ನೊಡನೆ ಹೀಗೆ ಕೇಳುತ್ತಿದ್ದನು, ‘ಪುನರುತ್ಥಾನ ಸತ್ಯವೆಂದು ನಂಬುವವರ ಪೈಕಿ ನೀನು ಸೇರಿದ್ದೀಯಾ?');
INSERT INTO "kannada" VALUES(37,53,'37:53','ನಿಜಕ್ಕೂ ನಾವು ಸತ್ತು ಮಣ್ಣಾಗಿ, ಅಸ್ಥಿಪಂಜರ ವಾದ ಬಳಿಕ ನಮಗೆ ಪ್ರತಿಫಲ ನೀಡಲಾಗುವುದೇ?’');
INSERT INTO "kannada" VALUES(37,54,'37:54','‘ನೀವೊಮ್ಮೆ ಆತನನ್ನು ನೋಡುವಿರಾ?’ ಎಂದು ಅವನು ಕೇಳುವನು.');
INSERT INTO "kannada" VALUES(37,55,'37:55','ಸರಿಯಾಗಿ ನೋಡಿದಾಗ ನರಕದ ಆಳದಲ್ಲಿ ಅವನನ್ನು ಕಾಣುವನು.');
INSERT INTO "kannada" VALUES(37,56,'37:56','ಅವನನ್ನು ಉದ್ದೇಶಿಸಿ ಹೀಗೆನ್ನುವನು, - “ಅಲ್ಲಾ ಹನಾಣೆ, ನೀನು ನನ್ನನ್ನು ವಿನಾಶಕ್ಕೆ ತಳ್ಳುತ್ತಿದ್ದೆ.');
INSERT INTO "kannada" VALUES(37,57,'37:57','ನನ್ನ ಪ್ರಭುವಿನ ಅನುಗ್ರಹ ಇಲ್ಲದಿರುತ್ತಿದ್ದರೆ ಇಂದು ನಾನು ಕೂಡಾ ನಿನ್ನ ಜೊತೆ ಹಾಜರಾಗ ಬೇಕಿತ್ತು .');
INSERT INTO "kannada" VALUES(37,58,'37:58','ಆದರೆ ನಾವು ಸಾಯುವವರಲ್ಲ ತಾನೆ?');
INSERT INTO "kannada" VALUES(37,59,'37:59','ನಮ್ಮ ಮೊದಲನೆಯ ಮರಣವನ್ನು ಹೊರತು ಪಡಿಸಿ. ಇನ್ನು ನಾವು ಶಿಕ್ಷಿಸಲ್ಪಡುವವರಲ್ಲ”.');
INSERT INTO "kannada" VALUES(37,60,'37:60','ನಿಶ್ಚಯವಾಗಿಯೂ ಇದುವೇ ಮಹತ್ತರ ವಿಜಯವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(37,61,'37:61','ಕರ್ಮವೆಸಗುವವರು ಇಂತಹ ಕಾರ್ಯಕ್ಕಾಗಿ ಕರ್ಮವೆಸಗಲಿ.');
INSERT INTO "kannada" VALUES(37,62,'37:62','ಹೇಳಿರಿ, ಕಾದಿರಿಸಲಾದ ಈ ಆತಿಥ್ಯ ಉತ್ತಮವೋ ಅಥವಾ ಝಖ್ಖೂಮ್ ವೃಕ್ಷ ಉತ್ತಮವೋ? ');
INSERT INTO "kannada" VALUES(37,63,'37:63','ಅಕ್ರಮಿಗಳ ಪಾಲಿಗೆ ನಾವು ಆ ವೃಕ್ಷವನ್ನು ಪರೀಕ್ಷೆಯ ಸಾಧನವಾಗಿ ಮಾಡಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(37,64,'37:64','ಅದು ನರಕದ ತಳಭಾಗದಿಂದ ಹೊರಡುವ ಒಂದು ವೃಕ್ಷವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(37,65,'37:65','ಅದರ ಗೊನೆಗಳು ಶೈತಾನರ ತಲೆಗಳೋ ಎಂಬಂತಿವೆ .');
INSERT INTO "kannada" VALUES(37,66,'37:66','ನರಕವಾಸಿಗಳು ಅದನ್ನು ತಿನ್ನುವರು. ಅದರಿಂದಲೇ ಹೊಟ್ಟೆ ತುಂಬುವರು.');
INSERT INTO "kannada" VALUES(37,67,'37:67','ಆ ಮೇಲೆ ಅವರಿಗೆ ಅದರ ಮೇಲೆ ಬೆರೆಸಲು ಪರಮ ತಾಪದ ದುರ್ಜಲದ ಮಿಶ್ರಣವೂ ಇದೆ!');
INSERT INTO "kannada" VALUES(37,68,'37:68','ನಂತರ ಅವರ ಮರಳುವಿಕೆಯ ಸ್ಥಾನ ನರಕವೇ ಆಗಿದೆ.');
INSERT INTO "kannada" VALUES(37,69,'37:69','ಅವರು ತಮ್ಮ ಪೂರ್ವಿಕರನ್ನು ಪಥಭೃಷ್ಟರಾಗಿ ಕಂಡಿದ್ದರು.');
INSERT INTO "kannada" VALUES(37,70,'37:70','ಅದಾಗ್ಯೂ ಅವರನ್ನಿವರು ತ್ವರೆಪಟ್ಟು ಅನುಸರಿಸಿದರು!');
INSERT INTO "kannada" VALUES(37,71,'37:71','ನಿಜವಾಗಿಯೂ ಅವರಿಗಿಂತ ಮುಂಚೆ ಪೂರ್ವಿಕರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಪಥಭ್ರಷ್ಟರಾಗಿದ್ದರು');
INSERT INTO "kannada" VALUES(37,72,'37:72','ಎಚ್ಚರಿಕೆ ನೀಡುವ ಸಂದೇಶವಾಹಕರನ್ನು ಅವರಿಗೆ ನಾವು ಕಳುಹಿಸಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(37,73,'37:73','ನೋಡಿರಿ, ಎಚ್ಚರಿಕೆ ನೀಡಲಾಗಿದ್ದ ಆ ಜನರ ಗತಿಯೇನಾಯಿತು?');
INSERT INTO "kannada" VALUES(37,74,'37:74','ಅಲ್ಲಾಹನ ನಿಷ್ಕಳಂಕ ದಾಸರು ಮಾತ್ರ ಇದರಿಂದ ಹೊರತಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(37,75,'37:75','ನೂಹರು ನಮ್ಮನ್ನು ಕೂಗಿ ಕರೆದಿದ್ದರು. ಉತ್ತರಿಸುವವರಲ್ಲಿ ನಾವು ಎಷ್ಟು ಉತ್ತಮರು!');
INSERT INTO "kannada" VALUES(37,76,'37:76','ನಾವು ಅವರನ್ನೂ ಅವರ ಜನರನ್ನೂ ಮಹಾ ದುರಂತದಿಂದ ರಕ್ಷಿಸಿದೆವು');
INSERT INTO "kannada" VALUES(37,77,'37:77','ಮತ್ತು ಅವರ ಸಂತತಿಯನ್ನು ಉಳಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(37,78,'37:78','ಅನಂತರದ ಪೀಳಿಗೆಗಳಲ್ಲಿ ಅವರ ಸತ್ಕೀರ್ತಿಯನ್ನು ಉಳಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(37,79,'37:79','ಸಕಲ ಲೋಕವಾಸಿಗಳಲ್ಲಿ ನೂಹರಿಗೆ ‘ಸಲಾಮ್’ ಇರುವುದು.');
INSERT INTO "kannada" VALUES(37,80,'37:80','ಸುಕೃತರಿಗೆ ಇಂತಹ ಸತ್ಫಲವನ್ನೇ ನೀಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(37,81,'37:81','ಖಂಡಿತ ಅವರು ನಮ್ಮ ಸತ್ಯವಿಶ್ವಾಸಿ ದಾಸರಲ್ಲೊಬ್ಬರು.');
INSERT INTO "kannada" VALUES(37,82,'37:82','ನಂತರ ಉಳಿದವರನ್ನು ನಾವು ಮುಳುಗಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(37,83,'37:83','ನಿಜವಾಗಿಯೂ ಇಬ್‍ರಾಹೀಮರು ನೂಹರ ಧರ್ಮದವರೇ ಆಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(37,84,'37:84','ಅವರು ತಮ್ಮ ಪ್ರಭುವಿನ ಸನ್ನಿಧಿಗೆ ನಿರ್ಮಲ ಮನಸ್ಸಿನೊಂದಿಗೆ ಬಂದಾಗ,');
INSERT INTO "kannada" VALUES(37,85,'37:85','ಅಂದರೆ ಅವರು ತಮ್ಮ ತಂದೆ ಹಾಗೂ ಜನಾಂಗದೊಡನೆ ಕೇಳಿದರು, “ನೀವು ಏನನ್ನು ಪೂಜಿಸುತ್ತಿರುವಿರಿ?');
INSERT INTO "kannada" VALUES(37,86,'37:86','ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಮಿಥ್ಯ ಆರಾಧ್ಯರನ್ನು ಬಯಸುತ್ತೀರಾ?');
INSERT INTO "kannada" VALUES(37,87,'37:87','ಆದರೆ ಸರ್ವಲೋಕಗಳ ಪಾಲಕ ಪ್ರಭುವಿನ ಕುರಿತು ನಿಮ್ಮ ಕಲ್ಪನೆಯೇನು?”');
INSERT INTO "kannada" VALUES(37,88,'37:88','ತರುವಾಯ ಅವರು ನಕ್ಷತ್ರಗಳ ಕಡೆಗೆ ದೃಷ್ಟಿ ಬೀರಿದರು .');
INSERT INTO "kannada" VALUES(37,89,'37:89','ಆಮೇಲೆ ಹೇಳಿದರು, ನಾನು ರೋಗಿಯಾಗಿದ್ದೇನೆ .');
INSERT INTO "kannada" VALUES(37,90,'37:90','ಆಗ ಜನರು ಅವರನ್ನು ಬಿಟ್ಟು ಹೋದರು.');
INSERT INTO "kannada" VALUES(37,91,'37:91','ನಂತರ ಇವರು ಮೆಲ್ಲನೆ ಅವರ ಆರಾಧ್ಯರ ಮಂದಿರದೊಳಗೆ ನುಗ್ಗಿದರು. ಆಮೇಲೆ ಕೇಳಿ ದರು, “ನೀವು ತಿನ್ನುವುದಿಲ್ಲವೇಕೆ?');
INSERT INTO "kannada" VALUES(37,92,'37:92','ನಿಮಗೇನಾಗಿದೆ? ನೀವೇಕೆ ಮಾತನಾಡುವುದಿಲ್ಲ?”');
INSERT INTO "kannada" VALUES(37,93,'37:93','ಅನಂತರ ಅವರು ಬಲವಾಗಿ ಹೊಡೆಯುತ್ತ ಅವುಗಳತ್ತ ತಿರುಗಿದರು.');
INSERT INTO "kannada" VALUES(37,94,'37:94','ಆಗ ಅವರು ಇವರ ಬಳಿಗೆ ಓಡೋಡಿ ಬಂದರು.');
INSERT INTO "kannada" VALUES(37,95,'37:95','ಆಗ ಇವರು, ಅವರಲ್ಲಿ ಕೇಳಿದರು, “ನೀವೇ ಕೆತ್ತಿದ ವಸ್ತುಗಳನ್ನು ನೀವು ಪೂಜಿಸುತ್ತಿದ್ದೀರಾ?');
INSERT INTO "kannada" VALUES(37,96,'37:96','ನಿಮ್ಮನ್ನೂ ನೀವು ಮಾಡುವ ಕರ್ಮಗಳನ್ನೂ ಸೃಷ್ಟಿಸಿದವನು ಅಲ್ಲಾಹನೇ ಆಗಿರುವನು”.');
INSERT INTO "kannada" VALUES(37,97,'37:97','ಅವರು ಪರಸ್ಪರ ಹೀಗೆಂದರು. “ಇವನಿಗಾಗಿ ಒಂದು ಅಗ್ನಿಕುಂಡವನ್ನು ತಯಾರಿಸಿರಿ. ಆಮೇಲೆ ಹೊತ್ತಿ ಉರಿಯುವ ಬೆಂಕಿಯ ರಾಶಿಗೆ ಇವನನ್ನು ಎಸೆದು ಬಿಡಿರಿ” .');
INSERT INTO "kannada" VALUES(37,98,'37:98','ಅವರು ಇವರನ್ನು ಮೋಸಕ್ಕೆ ಕೆಡಹಲು ಬಯಸಿದ್ದರು. ಆದರೆ, ನಾವು ಅವರನ್ನೇ ಅತ್ಯಂತ ಕೀಳಾಗಿಸಿಬಿಟ್ಟೆವು.');
INSERT INTO "kannada" VALUES(37,99,'37:99','ಇಬ್‍ರಾಹೀಮರು ಹೇಳಿದರು, ‘ನಾನು ನನ್ನ ಪ್ರಭುವಿನ ಕಡೆಗೆ ಹೋಗುತ್ತೇನೆ . ಅವನು ನನಗೆ ಮಾರ್ಗದರ್ಶನ ಮಾಡಲಿರುವನು.');
INSERT INTO "kannada" VALUES(37,100,'37:100','ನನ್ನ ಪ್ರಭು! ನನಗೆ ಸಜ್ಜನರ ಸಾಲಿಗೆ ಸೇರಿದ ಒಬ್ಬ ಮಗನನ್ನು ದಯಪಾಲಿಸು’.');
INSERT INTO "kannada" VALUES(37,101,'37:101','ಆಗ ನಾವು ಅವರಿಗೆ ಒಬ್ಬ ಸಹನಶೀಲ ಪುತ್ರನ ಸುವಾರ್ತೆ ನೀಡಿದೆವು.');
INSERT INTO "kannada" VALUES(37,102,'37:102','ಆ ಬಾಲಕನು ಅವರ ಜೊತೆ ದುಡಿಯುವ ಪ್ರಾಯಕ್ಕೆ ತಲುಪಿದಾಗ ಇಬ್‍ರಾಹೀಮರು “ಪ್ರಿಯ ಪುತ್ರ, ಕನಸಿನಲ್ಲಿ ನಾನು ನಿನ್ನ ಕೊರಳು ಕೊಯ್ಯುತ್ತಿರುವುದನ್ನು ಕಂಡಿದ್ದೇನೆ. ಆದ್ದರಿಂದ ನಿನ್ನ ಅಭಿಪ್ರಾಯವೇನೆಂದು ಯೋಚಿಸು” ಎಂದರು. ಆಗ ಅವನು “ಪ್ರೀತಿಯ ಅಪ್ಪಾ, ತಮಗೆ ಅಜ್ಞಾಪಿಸಲಾಗಿರುವುದನ್ನು ಮಾಡಿರಿ. ಅಲ್ಲಾಹನಿಚ್ಛಿಸಿದರೆ ತಾವು ನನ್ನನ್ನು ಸಹನ ಶೀಲರ ಸಾಲಿನಲ್ಲಿ ಗುರುತಿಸುವಿರಿ” ಎಂದನು.');
INSERT INTO "kannada" VALUES(37,103,'37:103','ಕೊನೆಗೆ ಇಬ್ಬರೂ ಆಜ್ಞೆಗೆ ವಿಧೇಯರಾದಾಗ, ಇಬ್‍ರಾಹೀಮರು ಮಗನನ್ನು ಕೆನ್ನೆಯೊರಗಿಸಿ ಮಲಗಿಸಿದಾಗ.');
INSERT INTO "kannada" VALUES(37,104,'37:104','ನಾವು ಹೀಗೆ ಕೂಗಿ ಹೇಳಿದೆವು, “ಓ ಇಬ್‍ರಾ ಹೀಮ್!');
INSERT INTO "kannada" VALUES(37,105,'37:105','ನೀವು ಸ್ವಪ್ನವನ್ನು ಖಂಡಿತ ನಿಜಗೊಳಿಸಿದ್ದೀರಿ” . ನಾವು ಸುಕೃತರಿಗೆ ಹೀಗೆಯೇ ಸತ್ಫಲ ನೀಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(37,106,'37:106','ನಿಶ್ಚಯವಾಗಿಯೂ ಇದೊಂದು ಪ್ರತ್ಯಕ್ಷ ಪರೀಕ್ಷೆಯಾಗಿತ್ತು.');
INSERT INTO "kannada" VALUES(37,107,'37:107','ಆ ಬಾಲಕನಿಗೆ ಬದಲಾಗಿ ನಾವು ಒಂದು ಮಹತ್ತರ ಬಲಿದಾನವನ್ನು ಪರಿಹಾರವಾಗಿ ಅವರಿಗೆ ಕೊಟ್ಟೆವು.');
INSERT INTO "kannada" VALUES(37,108,'37:108','ಅವನ ಸತ್ಕೀರ್ತಿಯನ್ನು ಶಾಶ್ವತವಾಗಿ ಮುಂದಿನ ತಲೆಮಾರುಗಳಲ್ಲಿ ಉಳಿಸಿಬಿಟ್ಟೆವು.');
INSERT INTO "kannada" VALUES(37,109,'37:109','ಇಬ್‍ರಾಹೀಮರ ಮೇಲೆ ಶ್ರೀ ರಕ್ಷೆ ಇದೆ.');
INSERT INTO "kannada" VALUES(37,110,'37:110','ಇದೇ ಪ್ರಕಾರ ನಾವು ಸುಕೃತರಿಗೆ ಸತ್ಫಲ ನೀಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(37,111,'37:111','ನಿಶ್ಚಯವಾಗಿಯೂ ಅವರು ನಮ್ಮ ಸತ್ಯವಿಶ್ವಾಸಿ ದಾಸರಲ್ಲಿ ಸೇರಿದ್ದರು.');
INSERT INTO "kannada" VALUES(37,112,'37:112','ನಾವು ಅವರಿಗೆ ಸಜ್ಜನರ ಪೈಕಿ ಒಬ್ಬ ಪ್ರವಾದಿ ಯಾಗಿ ಬರಲಿರುವ ಇಸ್‍ಹಾಖರ ಸುವಾರ್ತೆ ನೀಡಿದೆವು .');
INSERT INTO "kannada" VALUES(37,113,'37:113','ಅವರಿಗೂ ಇಸ್‍ಹಾಖರಿಗೂ ನಾವು ಸಮೃದ್ಧಿ ದಯಪಾಲಿಸಿದೆವು. ಅವರಿಬ್ಬರ ಸಂತತಿಯಲ್ಲಿ ಸದ್ಭಕ್ತರಿದ್ದಾರೆ. ತಮ್ಮ ಮೇಲೆ ತಾವೇ ಬಹಿರಂಗವಾದ ಅಕ್ರಮವೆಸಗುವವರೂ ಇದ್ದಾರೆ .');
INSERT INTO "kannada" VALUES(37,114,'37:114','ಮೂಸಾ ಮತ್ತು ಹಾರೂನರ ಮೇಲೆ ನಾವು ಅನುಗ್ರಹ ತೋರಿದೆವು.');
INSERT INTO "kannada" VALUES(37,115,'37:115','ಅವರನ್ನೂ ಅವರ ಜನಾಂಗವನ್ನೂ ಘೋರ ಕಂಟಕದಿಂದ ಪಾರುಗೊಳಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(37,116,'37:116','ಅವರಿಗೆ ಸಹಾಯ ಮಾಡಿದೆವು. ಇದರಿಂದಾಗಿ ಅವರೇ ಗೆಲುವು ಸಾಧಿಸಿದರು.');
INSERT INTO "kannada" VALUES(37,117,'37:117','ಅವರಿಗೆ ಅತ್ಯಂತ ಸ್ಪಷ್ಟವಾದ ಮಾಹಿತಿಯುಳ್ಳ ಗ್ರಂಥ ನೀಡಿದೆವು.');
INSERT INTO "kannada" VALUES(37,118,'37:118','ಅವರಿಬ್ಬರನ್ನೂ ಸತ್ಪಥಕ್ಕೆ ಸೇರಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(37,119,'37:119','ನಂತರದ ತಲೆಮಾರುಗಳಲ್ಲಿ ಅವರ ಪ್ರಶಂಸೆಯನ್ನು ಉಳಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(37,120,'37:120','ಮೂಸಾ ಮತ್ತು ಹಾರೂನರ ಮೇಲೆ ಶ್ರೀರಕ್ಷೆ ಇದೆ.');
INSERT INTO "kannada" VALUES(37,121,'37:121','ನಿಶ್ಚಯವಾಗಿಯೂ ನಾವು ಸುಕೃತರಿಗೆ ಇಂತಹುದೇ ಸತ್ಫಲ ನೀಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(37,122,'37:122','ನಿಶ್ಚಯವಾಗಿಯೂ ಅವರಿಬ್ಬರೂ ನಮ್ಮ ಸತ್ಯ ವಿಶ್ವಾಸಿ ದಾಸರಲಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(37,123,'37:123','ಇಲ್ಯಾಸರೂ ನಿಜವಾಗಿಯೂ ಸಂದೇಶ ವಾಹಕರಲ್ಲೊಬ್ಬರು.');
INSERT INTO "kannada" VALUES(37,124,'37:124','ಅವರು ತಮ್ಮ ಜನಾಂಗಕ್ಕೆ ಹೀಗೆ ಹೇಳಿದ್ದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ, ನೀವು ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(37,125,'37:125','ನೀವು `ಬಅïಲ’ ವಿಗ್ರಹವನ್ನು ಪೂಜಿಸು ತ್ತೀರಾ ? ಅತ್ಯುತ್ತಮ ಸೃಷ್ಟಿಕರ್ತನನ್ನು ಬಿಟ್ಟು ಬಿಡುತ್ತೀರಾ?');
INSERT INTO "kannada" VALUES(37,126,'37:126','ಅಂದರೆ ನಿಮ್ಮ ಮತ್ತು ನಿಮ್ಮ ಪೂರ್ವಜರ ಪ್ರಭುವಾಗಿರುವ ಅಲ್ಲಾಹನನ್ನು.');
INSERT INTO "kannada" VALUES(37,127,'37:127','ಆದರೆ, ಅವರು ಇಲ್ಯಾಸರನ್ನು ಸುಳ್ಳಾಗಿಸಿದರು. ಆದ್ದರಿಂದ ಖಂಡಿತ ಅವರು ನರಕದಲ್ಲಿ ಹಾಜರುಗೊಳಿಸಲ್ಪಡುವರು.');
INSERT INTO "kannada" VALUES(37,128,'37:128','ಅಲ್ಲಾಹನ ನಿಷ್ಕಳಂಕ ದಾಸರು ಇದಕ್ಕೆ ಹೊರತಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(37,129,'37:129','ಇಲ್ಯಾಸರ ಪ್ರಶಂಸೆಯನ್ನು ಅನಂತರದ ತಲೆಮಾರುಗಳಲ್ಲಿ ಉಳಿಸಿಬಿಟ್ಟೆವು.');
INSERT INTO "kannada" VALUES(37,130,'37:130','ಇಲ್ಯಾಸರ ಮೇಲೆ ಶ್ರೀರಕ್ಷೆ ಇದೆ.');
INSERT INTO "kannada" VALUES(37,131,'37:131','ನಾವು ಸುಕೃತರಿಗೆ ಇಂತಹದೇ ಪ್ರತಿಫಲವನ್ನು ಕೊಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(37,132,'37:132','ನಿಶ್ಚಯವಾಗಿಯೂ ಅವರು ನಮ್ಮ ಸತ್ಯವಿಶ್ವಾಸಿ ದಾಸರಲ್ಲಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(37,133,'37:133','ಲೂಥರೂ ನಿಜವಾಗಿಯೂ ಸಂದೇಶ ವಾಹಕರಲ್ಲೊಬ್ಬರು.');
INSERT INTO "kannada" VALUES(37,134,'37:134','ಅವರನ್ನೂ ಅವರ ಕುಟುಂಬದವರನ್ನೂ ನಾವು ರಕ್ಷಿಸಿದ್ದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(37,135,'37:135','ಶಿಕ್ಷೆಗೆ ಗುರಿಯಾದವರ ಪೈಕಿ ಓರ್ವ ವೃದ್ದೆಯ ಹೊರತು,');
INSERT INTO "kannada" VALUES(37,136,'37:136','ನಂತರ ಉಳಿದವರನ್ನು ನಾವು ನಾಶಗೊಳಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(37,137,'37:137','ನೀವು ಹಗಲಲ್ಲಿ ಅವರ ಅವಶೇಷಗಳ ಮೂಲಕ ಹಾದು ಹೋಗುತ್ತೀರಿ.');
INSERT INTO "kannada" VALUES(37,138,'37:138','ರಾತ್ರಿಯಲ್ಲೂ ! ಅದಾಗ್ಯೂ ನೀವು ಯೋಚಿಸುವು ದಿಲ್ಲವೆ ?');
INSERT INTO "kannada" VALUES(37,139,'37:139','ನಿಶ್ಚಯವಾಗಿ ಯೂನುಸರೂ ಸಂದೇಶವಾಹಕರಲ್ಲೊಬ್ಬರು.');
INSERT INTO "kannada" VALUES(37,140,'37:140','ಅವರು ಭಾರ ತುಂಬಿದ ನಾವೆಯ ಕಡೆಗೆ ಓಡಿದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(37,141,'37:141','ಹಾಗೆ ನಾವೆಯರು ಅದೃಷ್ಟಚೀಟಿ ಎತ್ತಿದರು. ಹಾಗೆ ಅವರು ಪರಾಜಿತರಲ್ಲಿ ಸೇರಿದರು.');
INSERT INTO "kannada" VALUES(37,142,'37:142','ಆಗ ಅವರನ್ನು ಮೀನು ನುಂಗಿ ಹಾಕಿತು. ಅವರು ಆಕ್ಷೇಪಾರ್ಹರಾಗಿ! ');
INSERT INTO "kannada" VALUES(37,143,'37:143','ಅವರು ತಸ್‍ಬೀಹ್ ಹೇಳುವವರಲ್ಲಿ ಒಬ್ಬರಾಗದಿರುತ್ತಿದ್ದಲ್ಲಿ,');
INSERT INTO "kannada" VALUES(37,144,'37:144','ಪುನರುತ್ಥಾನ ದಿನದವರೆಗೂ ಆ ಮೀನಿನ ಹೊಟ್ಟೆಯೊಳಗೆ ಖಂಡಿತ ಇರುತ್ತಿದ್ದರು .');
INSERT INTO "kannada" VALUES(37,145,'37:145','ಕೊನೆಗೆ ಅವರನ್ನು ನಾವು ಒಂದು ಕಡಲು ತೀರಕ್ಕೆ ತಂದು ಎಸೆದೆವು. ಅವರು ತೀವ್ರ ಬಳಲಿದ್ದರು.');
INSERT INTO "kannada" VALUES(37,146,'37:146','ಅವರ ಮೇಲೆ ಸಿಹಿ ಕುಂಬಳಕಾಯಿ ವರ್ಗದ ಗಿಡವೊಂದನ್ನು ಬೆಳೆಸಿದೆವು.');
INSERT INTO "kannada" VALUES(37,147,'37:147','ನಾವು ಅವರನ್ನು ಒಂದು ಲಕ್ಷ ಅಥವಾ ಅದ ಕ್ಕಿಂತಲೂ ಅಧಿಕ ಸಂಖ್ಯೆಯ ಜನರ ಕಡೆಗೆ ಕಳುಹಿಸಿದೆವು .');
INSERT INTO "kannada" VALUES(37,148,'37:148','ಆಗ ಅವರು ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಸ್ವೀಕರಿಸಿದರು. ಹಾಗೆ ಒಂದು ನಿಶ್ಚಿತ ಕಾಲದವರೆಗೆ ಅವರಿಗೆ ನಾವು ಸುಖಜೀವನವನ್ನು ಕೊಟ್ಟೆವು.');
INSERT INTO "kannada" VALUES(37,149,'37:149','ಇವರನ್ನು ಹೀಗೆ ಕೇಳಿರಿ; “ನಿಮ್ಮ ಪ್ರಭುವಿಗೆ ಪುತ್ರಿಯರೂ ಇವರಿಗೆ ಪುತ್ರರೂ ಇರುವುದೇ?');
INSERT INTO "kannada" VALUES(37,150,'37:150','ಅಥವಾ ಅವರು ದೃಕ್‍ಸಾಕ್ಷಿಗಳಾದ ಸ್ಥಿತಿಯಲ್ಲಿ ನಾವು ದೇವಚರರನ್ನು ಸ್ತ್ರೀಯರಾಗಿ ಸೃಷ್ಟಿ ಸಿದ್ದೇವೆಯೇ?”');
INSERT INTO "kannada" VALUES(37,151,'37:151','');
INSERT INTO "kannada" VALUES(37,152,'37:152','ಚೆನ್ನಾಗಿ ಗಮನವಿಟ್ಟು ಕೇಳಿರಿ, ಅಲ್ಲಾಹನು ಸಂತಾನವನ್ನು ಹುಟ್ಟಿಸಿದ್ದಾನೆ ಎಂದು ಇವರು ಸ್ವಯಂ ಸೃಷ್ಟಿಸಿಕೊಂಡು ಹೇಳುತ್ತಿದ್ದಾರೆ. ಇವರು ಖಂಡಿತ ಸುಳ್ಳುಗಾರರು.');
INSERT INTO "kannada" VALUES(37,153,'37:153','ಅಲ್ಲಾಹನು ತನಗಾಗಿ ಪುತ್ರರ ಬದಲು ಪುತ್ರಿಯ ರನ್ನು ಆಯ್ಕೆ ಮಾಡಿದನೇ?');
INSERT INTO "kannada" VALUES(37,154,'37:154','ನಿಮಗೆ ಏನಾಗಿದೆ? ಎಂತಹ ತೀರ್ಮಾನ ಕೈಗೊಳ್ಳುತ್ತಿರುವಿರಿ.');
INSERT INTO "kannada" VALUES(37,155,'37:155','ನೀವು ಯೋಚಿಸುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(37,156,'37:156','ಅಥವಾ ನಿಮ್ಮ ಬಳಿ ಏನಾದರೂ ಸ್ಪಷ್ಟವಾದ ಆಧಾರವಿದೆಯೇ?');
INSERT INTO "kannada" VALUES(37,157,'37:157','ನೀವು ಸತ್ಯವಾದಿಗಳಾಗಿದ್ದರೆ ನಿಮ್ಮ ಆಧಾರ ಗ್ರಂಥವನ್ನು ತನ್ನಿರಿ.');
INSERT INTO "kannada" VALUES(37,158,'37:158','');
INSERT INTO "kannada" VALUES(37,159,'37:159','ಅಲ್ಲಾಹು ಮತ್ತು ಜಿನ್ನ್‍ಗಳ ನಡುವೆ ಇವರು ವಂಶ ಸಂಬಂಧವನ್ನು ಸ್ಥಾಪಿಸಿದರು. ಖಂಡಿತ ಇವರು (ಶಿಕ್ಷೆಗಾಗಿ) ಹಾಜರುಗೊಳಿಸಲ್ಪಡು ವರು ಎಂದು ಜಿನ್ನ್‍ಗಳಿಗೆ ಚೆನ್ನಾಗಿ ತಿಳಿದಿದೆ.');
INSERT INTO "kannada" VALUES(37,160,'37:160','ಇವರು ಹಚ್ಚುತ್ತಿರುವ ದುರಾರೋಪದಿಂದ ಅಲ್ಲಾಹನು ಮುಕ್ತಶುದ್ಧನು. ಅಲ್ಲಾಹನ ನಿಷ್ಕಳಂಕ ದಾಸರ ಹೊರತು.');
INSERT INTO "kannada" VALUES(37,161,'37:161','ಆದುದರಿಂದ ನೀವು ಮತ್ತು ನಿಮ್ಮ ಆರಾಧ್ಯರು,');
INSERT INTO "kannada" VALUES(37,162,'37:162','ಅಲ್ಲಾಹನ ವಿರುದ್ಧ ಯಾರನ್ನೂ ದಂಗೆಯೆಬ್ಬಿ ಸಲು ಪ್ರಾಪ್ತರಲ್ಲ.');
INSERT INTO "kannada" VALUES(37,163,'37:163','ನರಕಾಗ್ನಿಯಲ್ಲಿ ಬಿದ್ದು ಸುಡುವಾತನ ಹೊರತು.');
INSERT INTO "kannada" VALUES(37,164,'37:164','ನಮಗೆಲ್ಲರಿಗೂ ನಿಶ್ಚಿತವಾದ ಒಂದು ಸ್ಥಾನ ಇದ್ದೇ ಇದೆ.');
INSERT INTO "kannada" VALUES(37,165,'37:165','ನಾವು ಪಂಕ್ತಿಬದ್ಧರು.');
INSERT INTO "kannada" VALUES(37,166,'37:166','ಮತ್ತು ಕೀರ್ತನೆ ಮಾಡುವವರು.');
INSERT INTO "kannada" VALUES(37,167,'37:167','ಇವರು (ಬಹುದೇವಾರಾಧಕರು) ಹೀಗೆ ಹೇಳುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(37,168,'37:168','“ನಮ್ಮ ಬಳಿ ಹಿಂದಿನವರಿಂದ ಪ್ರಮಾಣವೇನಾದರೂ ಇರುತ್ತಿದ್ದರೆ,');
INSERT INTO "kannada" VALUES(37,169,'37:169','ಖಂಡಿತವಾಗಿಯೂ ನಾವು ಅಲ್ಲಾಹನ ನಿಷ್ಠ ದಾಸರಾಗುತ್ತಿದ್ದೆವು”.');
INSERT INTO "kannada" VALUES(37,170,'37:170','ಆದರೆ, ಇವರು ಅದನ್ನು ನಿಷೇಧಿಸಿದರು. ಇನ್ನು ಸದ್ಯವೇ ಇವರಿಗೆ ತಿಳಿದು ಬರುವುದು.');
INSERT INTO "kannada" VALUES(37,171,'37:171','ನಾವು ನಮ್ಮ ಸಂದೇಶವಾಹಕ ದಾಸರ ಬಗ್ಗೆ ನಮ್ಮ ವಚನವು ಈ ಮೊದಲೇ ಆಗಿದೆ.');
INSERT INTO "kannada" VALUES(37,172,'37:172','ಖಂಡಿತವಾಗಿಯೂ ಅವರಿಗೆ ಸಹಾಯ ಸಿಗುವುದು ಎಂದು.');
INSERT INTO "kannada" VALUES(37,173,'37:173','ಮತ್ತು ನಮ್ಮ ಸೇನೆಯೇ ಗೆಲ್ಲುವುದು ಎಂದು.');
INSERT INTO "kannada" VALUES(37,174,'37:174','ಆದುದರಿಂದ (ಪೈಗಂಬರರೇ,) ನಿರ್ದಿಷ್ಟ ಕಾಲದ ವರೆಗೆ ಇವರಿಂದ ತಾವು ಬಿಟ್ಟು ನಿಲ್ಲಿರಿ.');
INSERT INTO "kannada" VALUES(37,175,'37:175','ಮತ್ತು ನೋಡುತ್ತಲಿರಿ. ಸದ್ಯವೇ ಇವರು ಸ್ವತಃ ಕಾಣುವರು.');
INSERT INTO "kannada" VALUES(37,176,'37:176','ಇವರು ನಮ್ಮ ಶಿಕ್ಷೆಗಾಗಿ ದುಡುಕುತ್ತಿರು ವರೇನು?');
INSERT INTO "kannada" VALUES(37,177,'37:177','ಆದರೆ ಆ ಶಿಕ್ಷೆ ಅವರ ಅಂಗಳದಲ್ಲೇ ಬಂದಿಳಿ ದಾಗ ಆ ಎಚ್ಚರಿಕೆ ನೀಡಲ್ಪಟ್ಟವರ ಪ್ರಭಾತವು ಅತ್ಯಂತ ಕೆಟ್ಟದಾಗಿರುವುದು.');
INSERT INTO "kannada" VALUES(37,178,'37:178','ಆದುದರಿಂದ ನಿರ್ದಿಷ್ಟಕಾಲ ಇವರಿಂದ ಬಿಟ್ಟು ನಿಲ್ಲಿರಿ.');
INSERT INTO "kannada" VALUES(37,179,'37:179','ಮತ್ತು ನೋಡುತ್ತಲಿರಿ. ಸದ್ಯವೇ ಇವರು ಸ್ವತಃ ಕಾಣುವರು.');
INSERT INTO "kannada" VALUES(37,180,'37:180','ನಿಮ್ಮ ಪ್ರಭು ಪ್ರತಾಪದ ಒಡೆಯನು. ಇವರು ವರ್ಣಿಸುತ್ತಿರುವ ಆರೋಪಗಳಿಂದ ಮುಕ್ತನಾದ ಪರಮಪಾವನನು.');
INSERT INTO "kannada" VALUES(37,181,'37:181','ಸಂದೇಶವಾಹಕರುಗಳ ಮೇಲೆ ‘ಸಲಾಮ್’ ಇದೆ.');
INSERT INTO "kannada" VALUES(37,182,'37:182','ಸಕಲ ಸ್ತುತಿಗಳೂ ಸರ್ವಲೋಕಗಳ ಪಾಲಕ ಪ್ರಭುವಾದ ಅಲ್ಲಾಹನಿಗೇ ಮೀಸಲು.');
INSERT INTO "kannada" VALUES(38,1,'38:1','ಸ್ವಾದ್, ಉಪದೇಶವನ್ನೊಳಗೊಂಡ ಖುರ್‍ಆನಿ ನಾಣೆ .');
INSERT INTO "kannada" VALUES(38,2,'38:2','ನಿಜವಾಗಿ, ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸಿದ ಜನರು ತೀವ್ರ ದುರಭಿಮಾನ ಮತ್ತು ಭಿನ್ನತೆಯಲ್ಲಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(38,3,'38:3','ಇವರಿಗಿಂತ ಮುಂಚೆ ಅದೆಷ್ಟು ಜನಾಂಗಗಳನ್ನು ನಾವು ನಾಶಗೊಳಿಸಿದ್ದೇವೆ! ಆಗ (ರಕ್ಷಣೆಗಾಗಿ) ಅವರು ಗೋಳಿಟ್ಟಿದ್ದಾರೆ. ಆದರೆ ಅದು ಓಡಿ ಪಾರಾಗುವ ಸಮಯವಾಗಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(38,4,'38:4','ಇವರಿಂದಲೇ ಓರ್ವ ಎಚ್ಚರಿಕೆ ನೀಡುವಾತನು ಇವರಿಗೆ ಬಂದಾಗ ಇವರು ಅಚ್ಚರಿಗೊಂಡಿದ್ದಾರೆ. ಸತ್ಯನಿಷೇಧಿಗಳು ಹೇಳಿದರು, `ಇವನೊಬ್ಬ ಜಾದುಗಾರ, ಮಹಾ ಸುಳ್ಳುಗಾರ.');
INSERT INTO "kannada" VALUES(38,5,'38:5','ಇವನು ದೇವರುಗಳನ್ನು ಏಕ ದೇವನಾಗಿ ಮಾಡಿ ಬಿಟ್ಟನೇ? ನಿಜಕ್ಕೂ ಇದೊಂದು ಬಹಳ ವಿಚಿತ್ರ ಸಂಗತಿ’ .');
INSERT INTO "kannada" VALUES(38,6,'38:6','ಜನಾಂಗದ ಸರದಾರರು ಹೀಗೆ ಹೇಳುತ್ತಾ ಹೊರ ನಡೆದರು. “ಹೊರಡಿರಿ. ನಿಮ್ಮ ಆರಾಧ್ಯರ ಮೇಲೆ ಸಹನೆಯಿಂದ ಸ್ಥಿರವಾಗಿ ನಿಲ್ಲಿರಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ಇದು ನಮ್ಮ ಮೇಲೆ ಉದ್ದೇಶವಿದ್ದ ಸಂಗತಿಯಾಗಿದೆ .');
INSERT INTO "kannada" VALUES(38,7,'38:7','ಇದನ್ನು ಕೊನೆಯ ಧರ್ಮದಲ್ಲೂ ನಾವು ಕೇಳಿಲ್ಲ. ಇದು ಕೇವಲ ಸ್ವಯಂ ಸೃಷ್ಟಿ.');
INSERT INTO "kannada" VALUES(38,8,'38:8','ನಮ್ಮ ನಡುವೆ ಕೇವಲ ಈತನಿಗೆ ಈ ಬೋಧನೆ ಅವತೀರ್ಣಗೊಂಡಿತೆ?” ಆದರೆ ನನ್ನ ಬೋಧನೆಯ ಬಗ್ಗೆ ಇವರು ಸಂಶಯದಲ್ಲೇ ಇರುವರು. ವಾಸ್ತವದಲ್ಲಿ ಇವರು ಈ ತನಕ ನನ್ನ ಯಾತನೆಯ ರುಚಿಯನ್ನು ಸವಿದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(38,9,'38:9','ಅಥವಾ ಮಹಾ ಪ್ರತಾಪಿಯೂ ಅತ್ಯುದಾರನೂ ಆದ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕೃಪೆಯ ಭಂಡಾರಗಳು ಇವರ ಕೈಯಲ್ಲಿವೆಯೇ?');
INSERT INTO "kannada" VALUES(38,10,'38:10','ಅಥವಾ ಭೂಮಿ-ಆಕಾಶಗಳ ಹಾಗೂ ಅವುಗಳ ನಡುವೆ ಇರುವ ವಸ್ತುಗಳ ಆಧಿಪತ್ಯ ಇವರಿಗಿದೆಯೇನು? ಹಾಗಿದ್ದರೆ ಇವರು ಅದರ ಮಾರ್ಗಗಳಿಗೆ ಹತ್ತಿ ನೋಡಲಿ.');
INSERT INTO "kannada" VALUES(38,11,'38:11','ಈ ನಿಷೇಧಿಗಳದ್ದು ಕೂಟಗಳ ಪೈಕಿ ಅಲ್ಲಿ ಸೋಲಿಸಲ್ಪಡುವ ಯಕಶ್ಚಿತ್ ಸಂಘ .');
INSERT INTO "kannada" VALUES(38,12,'38:12','');
INSERT INTO "kannada" VALUES(38,13,'38:13','ಇವರಿಗಿಂತ ಮುಂಚೆ ನೂಹರ ಜನಾಂಗ, ಆದ್, ಮೊಳೆಗಾರ ಫಿರ್‍ಔನ್, ಸಮೂದ್, ಲೂಥ್ ಜನಾಂಗ ಮತ್ತು ಐಕದವರು ಸುಳ್ಳಾಗಿಸಿದ್ದಾರೆ. ಅವರು ಸಂಘ ಪರಿವಾರದವರು.');
INSERT INTO "kannada" VALUES(38,14,'38:14','ಅವರ ಪೈಕಿ ಪ್ರತಿಯೊಂದು ಕೂಟವೂ ಸಂದೇಶ ವಾಹಕರನ್ನು ಸುಳ್ಳಾಗಿಸಿತು. ಇದರಿಂದ ನನ್ನ ಶಿಕ್ಷೆಯು ಅವರ ಮೇಲೆ ನಿಜವಾಯಿತು.');
INSERT INTO "kannada" VALUES(38,15,'38:15','ಒಂದು ಘೋರ ಘರ್ಜನೆಯನ್ನಲ್ಲದೆ ಅವರು ನಿರೀಕ್ಷಿಸುತ್ತಿಲ್ಲ. ಅದಕ್ಕೆ ಇನ್ನು ಹೆಚ್ಚು ಕಾಲ ತಡವಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(38,16,'38:16','ಇವರು, “ಓ ನಮ್ಮ ಪ್ರಭೂ, ಲೆಕ್ಕಾಚಾರದ ದಿನ ಬರುವ ಮೊದಲೇ ನಮ್ಮ ಪಾಲು ನಮಗೆ ಬೇಗನೆ ಕೊಟ್ಟುಬಿಡು” ಎನ್ನುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(38,17,'38:17','(ಸಂದೇಶವಾಹಕರೇ,) ಇವರು ಹೇಳುವ ಮಾತುಗಳನ್ನು ಸಹಿಸಿಕೊಳ್ಳಿರಿ. ಇವರಿಗೆ (ದೇವೋಪಾ ಸನೆಯಲ್ಲಿ) ಬಲಿಷ್ಠರಾಗಿದ್ದ ನಮ್ಮ ದಾಸ ದಾವೂದರ ವೃತ್ತಾಂತವನ್ನು ವಿವರಿಸಿರಿ. ಅವರು ಅಲ್ಲಾಹನ ಕಡೆಗೆ ಅತೀ ಹೆಚ್ಚು ಮರಳುವವರಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(38,18,'38:18','ಪರ್ವತಗಳು ಬೆಳಗು ಬೈಗುಗಳಲ್ಲಿ ಅವರೊಂದಿಗೆ ದೇವ ಕೀರ್ತನೆ ಮಾಡುವ ಸ್ಥಿತಿಯಲ್ಲಿ ಅವುಗಳ ನ್ನು ನಾವು ಅವರಿಗೆ ನಿಯಂತ್ರಿಸಿಕೊಟ್ಟಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(38,19,'38:19','ಒಟ್ಟಾಗಿ ಬಂದು ಸೇರುತ್ತಿದ್ದ ಪಕ್ಷಿಗಳನ್ನೂ, ಅವೆಲ್ಲವೂ ಅವರಿಗೆ ಸಂಪೂರ್ಣ ವಿಧೇಯವಾಗಿದ್ದವು.');
INSERT INTO "kannada" VALUES(38,20,'38:20','ನಾವು ಅವರ ಸಾಮ್ರಾಜ್ಯವನ್ನು ಬಲಿಷ್ಟಗೊಳಿಸಿದ್ದೆವು. ಅವರಿಗೆ ನಾವು ಯುಕ್ತಿಜ್ಞಾನವನ್ನೂ ನಿರ್ಣಾಯಕವಾಗಿ ಮಾತನಾಡುವ ಸಾಮಥ್ರ್ಯವನ್ನೂ ನೀಡಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(38,21,'38:21','ದಾವೂದರ ಆರಾಧನಾಲಯದ ಗೋಡೆಯ ಮೇಲೇರಿ ಇಳಿದು ಬಂದಿದ್ದ ಕಕ್ಷಿದಾರರ ಸುದ್ದಿ ನಿಮಗೆ ತಲುಪಿದೆಯೇ?');
INSERT INTO "kannada" VALUES(38,22,'38:22','ಅಂದರೆ ಅವರು ದಾವೂದರ ಬಳಿಗೆ ಬಂದಾಗ ದಾವೂದರು ಅವರನ್ನು ಕಂಡು ಬೆಚ್ಚಿದರು. ಆಗ ಅವರು `ಹೆದರಬೇಡಿರಿ, ನಾವು ಎರಡು ಕಕ್ಷಿದಾರರಾಗಿದ್ದು, ನಮ್ಮ ಪೈಕಿ ಒಬ್ಬನು ಇನ್ನೊಬ್ಬನ ಮೇಲೆ ಅನ್ಯಾಯವೆಸಗಿರುವನು. ತಾವು ನಮ್ಮ ನಡುವೆ ನ್ಯಾಯ ಪೂರ್ಣ ತೀರ್ಮಾನ ಮಾಡಿರಿ, ಅತಿ ಕ್ರಮಿಸಬೇಡಿರಿ. ಸರಿಯಾದ ದಾರಿಗೆ ನಮ್ಮನ್ನು ಮುನ್ನಡೆಸಿರಿ.');
INSERT INTO "kannada" VALUES(38,23,'38:23','ಇವನು ನನ್ನ ಸಹೋದರ, ಇವನಿಗೆ ತೊಂಬ ತ್ತೊಂಬತ್ತು ಹೆಣ್ಣಾಡುಗಳಿವೆ. ನನಗೆ ಒಂದು ಆಡು ಮಾತ್ರ ಇದೆ. ಇವನು ಆ ಒಂದು ಆಡನ್ನೂ ನನಗೆ ಬಿಟ್ಟು ಕೊಡಬೇಕೆಂದು ನನ್ನೊಡನೆ ಹೇಳಿದನು ಮತ್ತು ಮಾತಿನಲ್ಲಿ ನನ್ನನ್ನು ಗೆದ್ದನು.');
INSERT INTO "kannada" VALUES(38,24,'38:24','ಆಗ ದಾವೂದರು- ‘ಇವನು ತನ್ನ ಹೆಣ್ಣು ಆಡುಗಳೊಂದಿಗೆ ನಿನ್ನ ಆಡನ್ನು ಸೇರಿಸಿಕೊಳ್ಳಲು ಕೇಳುವ ಮೂಲಕ ನಿಜಕ್ಕೂ ನಿನ್ನ ಮೇಲೆ ಅಕ್ರಮವೆಸಗಿರುವನು. ಸಹಬಾಳುವೆ ನಡೆಸುವವರಲ್ಲಿ ಹೆಚ್ಚಿನವರೂ ಪರಸ್ಪರ ಅತಿರೇಕವೆಸಗುತ್ತಾರೆ. ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿ ಸತ್ಕರ್ಮವೆಸಗುವವರು ಮಾತ್ರ ಇದರಿಂದ ದೂರವಿರುತ್ತಾರೆ. ಇಂತಹವರು ಬಹಳ ವಿರಳ’ ಎಂದರು. ದಾವೂದರು ಭಾವಿಸಿದರು, ‘ನಾವು ಅವರನ್ನು ಪರೀಕ್ಷಿಸುತ್ತಿದ್ದೇವೆ’ ಎಂದು. ನಂತರ ಅವರು ತಮ್ಮ ಪ್ರಭುವಿನೊಡನೆ ಕ್ಷಮಾಯಾಚನೆ ನಡೆಸಿದರು. ಸಾಷ್ಟಾಂ ಗವೆರಗಿದರು. ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು ಮರಳಿದರು .');
INSERT INTO "kannada" VALUES(38,25,'38:25','ಆಗ ನಾವು ಅವರಿಗೆ ಅದನ್ನು ಕ್ಷಮಿಸಿದೆವು. ನಿಶ್ಚಯ ನಮ್ಮ ಬಳಿ ಅವರಿಗಾಗಿ ನಿಕಟ ಸ್ಥಾನ ಹಾಗೂ ಉತ್ತಮವಾದ ಅಂತಿಮ ನೆಲೆ ಇದೆ .');
INSERT INTO "kannada" VALUES(38,26,'38:26','ಓ, ದಾವೂದ್! ನಾವು ನಿಮ್ಮನ್ನು ಭೂಮಿಯಲ್ಲಿ ಪ್ರತಿನಿಧಿಯಾಗಿ ಮಾಡಿದ್ದೇವೆ. ಆದುದರಿಂದ ನೀವು ಜನರ ನಡುವೆ ನ್ಯಾಯ ಪ್ರಕಾರ ವಿಧಿಸಿರಿ. ಸ್ವೇಚ್ಛೆಯನ್ನು ಅನುಸರಿಸಬೇಡಿರಿ. ಅದು ನಿಮ್ಮ ನ್ನು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ ತಪ್ಪಿಸಿಬಿಡುವುದು. ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ ತಪ್ಪಿ ಹೋಗುವವರಿಗೆ, ಅವರು ಲೆಕ್ಕಾಚಾರದ ದಿನವನ್ನು ಮರೆತುಬಿಟ್ಟ ಕಾರಣಕ್ಕೆ ಖಂಡಿತ ಕಠಿಣ ಶಿಕ್ಷೆ ಇದೆ.');
INSERT INTO "kannada" VALUES(38,27,'38:27','ಆಕಾಶವನ್ನೂ ಭೂಮಿಯನ್ನೂ ಅವುಗಳ ನಡುವೆ ಇರುವುದನ್ನೂ ನಾವು ವೃಥಾ ಸೃಷ್ಟಿಸಲಿಲ್ಲ. ಅದು ಸತ್ಯನಿಷೇಧಿಗಳ ಭಾವನೆಯಾಗಿದೆ. ಹೀಗಾಗಿ ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ನರಕಾಗ್ನಿಯ ಮೂಲಕ ಘೋರ ವಿನಾಶವಿದೆ.');
INSERT INTO "kannada" VALUES(38,28,'38:28','ಅಥವಾ ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿ ಸತ್ಕರ್ಮ ವೆಸಗು ವವರನ್ನು ಭೂಮಿಯಲ್ಲಿ ಕ್ಷೋಭೆ ಸೃಷ್ಟಿಸುವವ ರಂತೆ ನಾವೇಕೆ ಮಾಡಬೇಕು? ಅಥವಾ ಧರ್ಮ ನಿಷ್ಠರನ್ನು ನಾವು ಧರ್ಮಭ್ರಷ್ಟರಂತೆ ಮಾಡುವುದು ಏಕೆ?');
INSERT INTO "kannada" VALUES(38,29,'38:29','(ಪೈಗಂಬರರೇ,) ಇದೊಂದು ಅನುಗ್ರಹೀತ ಗ್ರಂಥವಾಗಿದ್ದು ಇವರು ಇದರ ಸೂಕ್ತಗಳ ಕುರಿತು ಚಿಂತನೆ ನಡೆಸಲೆಂದು ಮತ್ತು ಬುದ್ಧಿಜೀವಿಗಳು ಇದರಿಂದ ಉಪದೇಶ ಪಡೆಯಲೆಂದು ನಾವಿದ ನ್ನು ನಿಮಗೆ ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(38,30,'38:30','ದಾವೂದರಿಗೆ ನಾವು ಸುಲೈಮಾನರನ್ನು (ಪುತ್ರ ನಾಗಿ) ದಯಪಾಲಿಸಿದೆವು. ಅವರು ಅತ್ಯು ತ್ತಮ ದಾಸರು. ತಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಗೆ ಅತಿ ಹೆಚ್ಚಾಗಿ ಮರಳುವವರು !');
INSERT INTO "kannada" VALUES(38,31,'38:31','ಅತಿ ವೇಗದ, ಸನ್ನದ್ಧ ಯುದ್ಧಾಶ್ವಗಳನ್ನು ಅವರ ಮುಂದೆ ಸಂಜೆಯ ವೇಳೆ ಪ್ರದರ್ಶಿಸಲಾದ ಸಂದರ್ಭವು ಸ್ಮರಣೀಯ.');
INSERT INTO "kannada" VALUES(38,32,'38:32','ಆಗ ಅವರು ಹೇಳಿದರು, ನನ್ನ ಪ್ರಭುವಿನ ಸ್ಮರಣೆಯನ್ನು ಬಿಟ್ಟು ಸಂಪತ್ತಿನ ಪ್ರೀತಿಗೆ ಒಲಿದು ಬಿಟ್ಟೆ. ಸೂರ್ಯನು ಮರೆಯಾಗುವವರೆಗೂ!');
INSERT INTO "kannada" VALUES(38,33,'38:33','ಆದ್ದರಿಂದ ಅವುಗಳನ್ನು ನನ್ನ ಬಳಿಗೆ ಮರಳಿ ತನ್ನಿರಿ. ಆಮೇಲೆ ಅವರು ಆ ಕುದುರೆಗಳನ್ನು ಬಲಿದಾನ ನೀಡಿದರು. ಅವುಗಳ ಮೊಣಕಾಲನ್ನು ಕಡಿದರು .');
INSERT INTO "kannada" VALUES(38,34,'38:34','ಸುಲೈಮಾನರನ್ನು ನಾವು ಪರೀಕ್ಷೆಗೊಳಪಡಿಸಿದೆವು. ಅವರ ಸಿಂಹಾಸನದ ಮೇಲೆ ಒಂದು ಜಡಶರೀರವನ್ನು ತಂದು ಹಾಕಿದೆವು . ಅನಂತರ ಅವರು ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು ಮರಳಿದರು.');
INSERT INTO "kannada" VALUES(38,35,'38:35','ಅವರು ಪ್ರಾರ್ಥಿಸಿದರು, “ನನ್ನ ಪ್ರಭೂ, ನನ್ನನ್ನು ಕ್ಷಮಿಸು, ನನ್ನ ಬಳಿಕ ಇನ್ನಾರಿಗೂ ಸಲ್ಲದ ರಾಜ ಪದವಿಯನ್ನು ನನಗೆ ದಯಪಾಲಿಸು. ನಿಜಕ್ಕೂ ನೀನೇ ಅತೀ ಉದಾರನಾಗಿರುವೆ”.');
INSERT INTO "kannada" VALUES(38,36,'38:36','ಆಗ ಅವರಿಗೆ ನಾವು ಗಾಳಿಯನ್ನು ನಿಯಂತ್ರಿಸಿ ಕೊಟ್ಟೆವು. ಅದು ಅವರ ಅಪ್ಪಣೆಯಂತೆ ಅವರು ಇಚ್ಚಿಸಿದ ದಿಕ್ಕಿಗೆ ಮಂದವಾಗಿ ಬೀಸುತ್ತಿತ್ತು.');
INSERT INTO "kannada" VALUES(38,37,'38:37','ಎಲ್ಲ ವಿಧದ ಕಟ್ಟಡ ನಿರ್ಮಾಣಗಾರರೂ ಮುಳುಗು ಪ್ರವೀಣರೂ ಆದ ಶೈತಾನರನ್ನೂ');
INSERT INTO "kannada" VALUES(38,38,'38:38','ಸಂಕೋಲೆಗಳಲ್ಲಿ ಬಿಗಿಯಲ್ಪಟ್ಟ ಇತರ ಶೈತಾನರನ್ನೂ ನಾವು ಅವರಿಗೆ ಅಧೀನಗೊಳಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(38,39,'38:39','“ಇದು ನಮ್ಮ ಕೊಡುಗೆ, ಆದ್ದರಿಂದ ಲೆಕ್ಕ ನೋಡದೆ ಕೊಡಿರಿ ಇಲ್ಲವೇ ತಡೆದಿರಿಸಿರಿ” (ಎಂದು ಸುಲೈಮಾನರಿಗೆ ನಾವು ಹೇಳಿದೆವು).');
INSERT INTO "kannada" VALUES(38,40,'38:40','ನಿಶ್ಚಯವಾಗಿಯೂ ಅವರಿಗೆ ನಮ್ಮ ಬಳಿ ನಿಕಟ ಸ್ಥಾನವೂ ಉತ್ಕøಷ್ಟವಾದ ಅಂತಿಮ ನೆಲೆಯೂ ಇದೆ.');
INSERT INTO "kannada" VALUES(38,41,'38:41','ನಮ್ಮ ದಾಸ ಅಯ್ಯೂಬರನ್ನು ಸ್ಮರಿಸಿರಿ. ಸಂಕಟ ಹಾಗೂ ಪೀಡನೆಯ ಮೂಲಕ ನನ್ನನ್ನು ಶೈತಾನನು ಸ್ಪರ್ಶಿಸಿದ್ದಾನೆಂದು ಅವರು ತಮ್ಮ ಪ್ರಭುವನ್ನು ಪ್ರಾರ್ಥಿಸಿದ ಸಂದರ್ಭ .');
INSERT INTO "kannada" VALUES(38,42,'38:42','(ನಾವು ಆಜ್ಞಾಪಿಸಿದೆವು) “ನಿಮ್ಮ ಕಾಲನ್ನು ನೆಲಕ್ಕೆ ಬಡಿಯಿರಿ. ಇದೋ ಸ್ನಾನದ ತಣ್ಣೀರು, ಮತ್ತು ಕುಡಿಯುವ ನೀರು”.');
INSERT INTO "kannada" VALUES(38,43,'38:43','ಅವರ ಮನೆಯವರನ್ನೂ ಅವರೊಂದಿಗೆ ಪುನಃ ಅಷ್ಟನ್ನೂ ಅವರಿಗೆ ನಾವು ಕೊಟ್ಟೆವು. ಇದು ನಮ್ಮ ವತಿಯಿಂದ ಕರುಣೆಯಾಗಿಯೂ ಬುದ್ದಿಜೀವಿಗ ಳಿಗೆ ನೀತಿ ಪಾಠವಾಗಿಯೂ ನೀಡಿದುದಾಗಿತ್ತು.');
INSERT INTO "kannada" VALUES(38,44,'38:44','`ತಾವು ಒಂದು ಹಿಡಿ ಹುಲ್ಲನ್ನು ತೆಗೆದುಕೊಳ್ಳಿರಿ, ಅದರಿಂದ (ಪತ್ನಿಗೆ) ಹೊಡೆಯಿರಿ. ನಿಮ್ಮ ಶಪಥವನ್ನು ಮುರಿಯಬೇಡಿರಿ . ‘ನಾವು ಅವರನ್ನು ತಾಳ್ಮೆಗಾರರಾಗಿ ಕಂಡೆವು. ಅವರು ಅತ್ಯುತ್ತಮ ದಾಸರು, ತಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಗೆ ಬಹಳಷ್ಟು ಮರಳುವವರು.');
INSERT INTO "kannada" VALUES(38,45,'38:45','ಕರ್ಮ ಶಕ್ತಿ ಹಾಗೂ ಒಳದೃಷ್ಟಿಯುಳ್ಳ ನಮ್ಮ ದಾಸರಾದ ಇಬ್‍ರಾಹೀಮ್, ಇಸ್‍ಹಾಖ್ ಮತ್ತು ಯಅïಖೂಬರನ್ನು ಸ್ಮರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(38,46,'38:46','ಒಂದು ವಿಶಿಷ್ಟ ಗುಣದಿಂದಾಗಿ ಅವರನ್ನು ನಾವು ಪುನೀತರಾಗಿ ಮಾಡಿದೆವು. ಅದು ಪರಲೋಕದ ಸ್ಮರಣೆಯಾಗಿತ್ತು.');
INSERT INTO "kannada" VALUES(38,47,'38:47','ನಿಶ್ಚಯವಾಗಿಯೂ ಅವರು ನಮ್ಮ ಬಳಿ ಆಯ್ದ ಶ್ರೇಷ್ಟರ ಸಾಲಿಗೆ ಸೇರಿದವರು.');
INSERT INTO "kannada" VALUES(38,48,'38:48','ಇಸ್ಮಾಈಲ್, ಅಲ್‍ಯಸಅï ಮತ್ತು ದುಲ್ ಕಿಫ್ಲರನ್ನು ಸ್ಮರಿಸಿರಿ. ಇವರೆಲ್ಲರೂ ಶ್ರೇಷ್ಟರ ಲ್ಲಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(38,49,'38:49','ಇದೊಂದು ಬೋಧನೆಯಾಗಿದ್ದು ಧರ್ಮನಿಷ್ಟರಿಗೆ ಖಂಡಿತ ಉತ್ಕøಷ್ಟವಾದ ಅಂತಿಮ ನೆಲೆಯಿದೆ.');
INSERT INTO "kannada" VALUES(38,50,'38:50','ಅಂದರೆ ಶಾಶ್ವತವಾದ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಿದ್ದು ಅವುಗಳ ಕದಗಳು ಅವರ ಪಾಲಿಗೆ ತೆರೆದಿಡಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(38,51,'38:51','ಅವರು ಅದರಲ್ಲಿ ದಿಂಬುಗಳಿಗೆ ಒರಗಿ ಕುಳಿತು ಕೊಂಡು, ಹೇರಳ ಫಲಗಳನ್ನೂ ಪಾನೀಯಗಳನ್ನೂ ತರಿಸುತ್ತಿರುವರು.');
INSERT INTO "kannada" VALUES(38,52,'38:52','ಅವರ ಬಳಿ ದೃಷ್ಟಿಯನ್ನು ನಿಯಂತ್ರಿಸುವ ಸಮವಯಸ್ಕ ಸುಂದರಿಯರಿರುವರು');
INSERT INTO "kannada" VALUES(38,53,'38:53','ಇವೆಲ್ಲ, ವಿಚಾರಣೆಯ ದಿನಕ್ಕಾಗಿ ನಿಮಗೆ ವಾಗ್ದಾನ ಮಾಡಲಾಗುತ್ತಿರುವ ವಸ್ತುಗಳು.');
INSERT INTO "kannada" VALUES(38,54,'38:54','ಇದು ನಮ್ಮ ಅನುಗ್ರಹವಾಗಿದ್ದು, ಇದೆಂದಿಗೂ ಬತ್ತಿ ಹೋಗಲಾರದು.');
INSERT INTO "kannada" VALUES(38,55,'38:55','ಇದು ಧರ್ಮನಿಷ್ಠರ ಸ್ಥಿತಿ. ಪಾಪಿಗಳಿಗೆ ಅತ್ಯಂತ ನಿಕೃಷ್ಟ ನಿವಾಸವಿದೆ.');
INSERT INTO "kannada" VALUES(38,56,'38:56','ನರಕ, ಅದರಲ್ಲಿ ಅವರು ಉರಿದು ಹೋಗುವರು. ಅದೆಷ್ಟು ಕೆಟ್ಟ ಶಯ್ಯೆಯಾಗಿದೆ!');
INSERT INTO "kannada" VALUES(38,57,'38:57','ಇದು ಸ್ಥಿತಿ. ಆದುದರಿಂದ ಅವರು ಸವಿದು ಕೊಳ್ಳಲಿ, ಕುದಿಯುತ್ತಿರುವ ನೀರನ್ನು, ದುರ್ಗಂಧಪೂರಿತ ಕೀವನ್ನು');
INSERT INTO "kannada" VALUES(38,58,'38:58','ಇಂತಹದೇ ಬೇರೊಂದು ಜಲವನ್ನೂ, ಇನ್ನಿತರ ವಿಧಗಳ ಶಿಕ್ಷೆಯನ್ನೂ.');
INSERT INTO "kannada" VALUES(38,59,'38:59','ಈ ತುಕಡಿಯು ನಿಮ್ಮ ಜೊತೆಗೇ ತಳ್ಳಿ ಬರುತ್ತಿದೆ. ಅವರಿಗೆ ಯಾವ ಕ್ಷೇಮವೂ ಇಲ್ಲ. ನಿಜವಾಗಿಯೂ ಅವರು ನರಕಕ್ಕೆ ಪ್ರವೇಶಿಸಿಯೇ ಬಿಟ್ಟರು .');
INSERT INTO "kannada" VALUES(38,60,'38:60','ಇವರು ಹೇಳುತ್ತಾರೆ; ``ಅಷ್ಟೇ ಅಲ್ಲ, ನಿಮಗೂ ಕ್ಷೇಮವಿಲ್ಲ. ಇದನ್ನು ನೀವು ನಮ್ಮ ಮುಂದಿರಿಸಿ ಕೊಟ್ಟವರು, ಈ ತಾಣವು ಬಹಳ ನಿಕೃಷ್ಟ”.');
INSERT INTO "kannada" VALUES(38,61,'38:61','ಇವರು ಹೇಳುತ್ತಾರೆ; ``ನಮ್ಮ ಪ್ರಭೂ! ಇದನ್ನು ಯಾರು ನಮಗೆ ಮುಂದಿರಿಸಿಕೊಟ್ಟರೋ ಅವರಿಗೆ ಈ ಶಿಕ್ಷೆಯನ್ನು ನರಕದಲ್ಲಿ ಇಮ್ಮಡಿಯಾಗಿ ಹೆಚ್ಚಿಸಿ ಕೊಡು!');
INSERT INTO "kannada" VALUES(38,62,'38:62','ಅವರು ಹೀಗೆ ಹೇಳುವರು; ``ನಮಗೇನಾ ಯಿತು? ನಾವು ಕೆಟ್ಟವರೆಂದು ಭಾವಿಸುತ್ತಿದ್ದವರು ಇಲ್ಲೆಲ್ಲೂ ನಮಗೆ ಕಾಣುತ್ತಿಲ್ಲವಲ್ಲ?');
INSERT INTO "kannada" VALUES(38,63,'38:63','ನಾವು ಅವರನ್ನು ವೃಥಾ ಪರಿಹಾಸ್ಯ ಮಾಡುತ್ತಿ ದ್ದೆವೋ ಅಥವಾ ಅವರನ್ನು ಬಿಟ್ಟು ಕಣ್ಣುಗಳೂ ಕದಲಿತೇ?');
INSERT INTO "kannada" VALUES(38,64,'38:64','ನರಕವಾಸಿಗಳಲ್ಲಿ ಈ ತರದ ವಾಗ್ದಾನ ನಡೆಯಲಿರುವುದು. ಖಂಡಿತವಾಗಿಯೂ ಸತ್ಯ.');
INSERT INTO "kannada" VALUES(38,65,'38:65','(ಪೈಗಂಬರರೇ) ಹೇಳಿರಿ, ನಾನು ಕೇವಲ ಎಚ್ಚರಿಕೆ ಕೊಡುವವನು, ಏಕನೂ ಸರ್ವಾಧಿಪತಿಯೂ ಆಗಿರುವ ಅಲ್ಲಾಹನ ಹೊರತು ಯಾವುದೇ ಆರಾಧ್ಯರಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(38,66,'38:66','ಅವನು ಭೂಮಿ-ಆಕಾಶಗಳ ಹಾಗೂ ಅವುಗಳ ನಡುವೆ ಇರುವುದರ ಒಡೆಯನೂ ಪ್ರಬಲನೂ ಅತ್ಯಂತ ಕ್ಷಮಾಶೀಲನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(38,67,'38:67','ಹೇಳಿರಿ- “ಇದೊಂದು ಗಂಭೀರ ವಾರ್ತೆ!');
INSERT INTO "kannada" VALUES(38,68,'38:68','ನೀವು ಅದನ್ನು ಅವಗಣಿಸುತ್ತೀರಿ,');
INSERT INTO "kannada" VALUES(38,69,'38:69','ಮಲಅï ಅಅïಲಾ (ಸರ್ವೋನ್ನತ ದರಬಾರ) ದಲ್ಲಿ ಮಾತಿನ ಚಕಮಕಿ ನಡೆದಿದ್ದ ಸುದ್ದಿ ನನಗೆ ತಿಳಿದಿರಲೇ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(38,70,'38:70','ನಾನು ಸುಸ್ಪಷ್ಟ ಎಚ್ಚರಿಕೆ ಕೊಡುವವನಾಗಿರುವುದರಿಂದ ಮಾತ್ರ ನನಗೆ ಸಂದೇಶ ಕೊಡಲಾಗುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(38,71,'38:71','ನಿಮ್ಮ ಪ್ರಭು ದೇವಚರರೊಡನೆ ಹೇಳಿದ ಸಂದರ್ಭವನ್ನು ಸ್ಮರಿಸಿರಿ; “ನಾನು ಆವೆಮಣ್ಣಿನಿಂದ ಒಬ್ಬ ಮಾನವನನ್ನು ಸೃಷ್ಟಿಸುವವನಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(38,72,'38:72','ಹಾಗೆ ನಾನು ಅವನನ್ನು ಪೂರ್ಣವಾಗಿ ನಿರ್ಮಿಸಿದಾಗ ಮತ್ತು ಅವನೊಳಗೆ ನನ್ನ ಆತ್ಮದಿಂದ ಊದಿ ದಾಗ, ನೀವು ಅವನ ಮುಂದೆ ಸಾಷ್ಟಾಂಗ ವೆರಗಿರಿ”.');
INSERT INTO "kannada" VALUES(38,73,'38:73','ಆಗ ದೇವಚರರೆಲ್ಲರೂ ಸಾಷ್ಟಾಂಗವೆರಗಿದರು .');
INSERT INTO "kannada" VALUES(38,74,'38:74','ಇಬ್‍ಲೀಸನ ಹೊರತು. ಅವನು ಅಹಂಭಾವ ತೋರಿದನು. ಅವನು ಸತ್ಯನಿಷೇಧಿಗಳ ಕೂಟಕ್ಕೆ ಸೇರಿದ್ದನು .');
INSERT INTO "kannada" VALUES(38,75,'38:75','ಆಗ ಅಲ್ಲಾಹನು ಹೇಳಿದನು; “ಇಬ್‍ಲೀಸ್! ನಾನು ನನ್ನೆರಡು ಕೈಗಳಿಂದ ನಿರ್ಮಿಸಿದ್ದಕ್ಕೆ ಸಾಷ್ಟಾಂಗವೆರಗುವುದರಿಂದ ನಿನ್ನನ್ನು ತಡೆದುದೇನು? ನೀನು ಅಹಂಭಾವ ತೋರಿಸುವೆಯಾ? ಅಥವಾ ನೀನೇ ಉನ್ನತ ಮಟ್ಟದವರಲ್ಲಾಗಿರುವೆಯಾ?”');
INSERT INTO "kannada" VALUES(38,76,'38:76','ಅವನು ಹೀಗೆಂದನು; “ನಾನು ಇವನಿಗಿಂತ ಶ್ರೇಷ್ಟನು, ನೀನು ನನ್ನನ್ನು ಅಗ್ನಿಯಿಂದ ಸೃಷ್ಟಿಸಿದೆ. ಮತ್ತು ಅವನನ್ನು ಆವೆಮಣ್ಣಿನಿಂದ ಸೃಷ್ಟಿಸಿದೆ”.');
INSERT INTO "kannada" VALUES(38,77,'38:77','ಆಗ ಅಲ್ಲಾಹನು ಹೇಳಿದನು; ‘ಹಾಗಿದ್ದರೆ ಇಲ್ಲಿಂದ ತೊಲಗು. ನೀನು ಬಹಿಷ್ಕøತನು.');
INSERT INTO "kannada" VALUES(38,78,'38:78','ನ್ಯಾಯ ವಿಧಿಯ ದಿನದವರೆಗೂ ನಿನ್ನ ಮೇಲೆ ನನ್ನ ಶಾಪವಿದೆ”.');
INSERT INTO "kannada" VALUES(38,79,'38:79','ಆಗ ಅವನು, (ಇಬ್ಲೀಸ್) ಹೇಳಿದನು; “ಪ್ರಭೂ, ಹಾಗಿದ್ದರೆ ಅವರು ಪುನಃ ಎಬ್ಬಿಸಲ್ಪಡುವ ದಿನದ ವರೆಗೆ ನನಗೆ ಕಾಲಾವಕಾಶ ಕೊಟ್ಟು ಬಿಡು”.');
INSERT INTO "kannada" VALUES(38,80,'38:80','ಆಗ ಅಲ್ಲಾಹನು, ಹೇಳಿದನು; ನೀನು ಸಮಯಾ ವಕಾಶವನ್ನು ನೀಡಲ್ಪಟ್ಟವರ ಗುಂಪಿಗೆ ಸೇರಿರುವಿ.');
INSERT INTO "kannada" VALUES(38,81,'38:81','ನಿಶ್ಚಿತವಾದ ಆ ಸಮಯ ಬರುವ ದಿನದ ವರೆಗೆ');
INSERT INTO "kannada" VALUES(38,82,'38:82','ಆಗ ಅವನು (ಇಬ್ಲೀಸ್) ಹೇಳಿದನು “ನಿನ್ನ ಪ್ರತಿಷ್ಟೆಯಾಣೆ, ಅವರೆಲ್ಲರನ್ನೂ ನಾನು ಖಂಡಿತ ದಾರಿಗೆಡಿಸಿ ಬಿಡುವೆನು');
INSERT INTO "kannada" VALUES(38,83,'38:83','ಅವರ ಪೈಕಿ ನಿನ್ನ ನಿಷ್ಕಳಂಕ ದಾಸರ ಹೊರತು');
INSERT INTO "kannada" VALUES(38,84,'38:84','ಆಗ ಅಲ್ಲಾಹನು ಹೇಳಿದನು, ಇದು ಸತ್ಯವಾಗಿದೆ ಮತ್ತು ನಾನು ಸತ್ಯವನ್ನೇ ಹೇಳುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(38,85,'38:85','ನಾನು ನಿನ್ನನ್ನೂ ನಿನ್ನನ್ನು ಅನುಸರಿಸುವ ಸಕಲರನ್ನೂ ನರಕಕ್ಕೆ ಖಂಡಿತ ತುಂಬಿಸಿ ಬಿಡುವೆನು.');
INSERT INTO "kannada" VALUES(38,86,'38:86','(ಸಂದೇಶವಾಹಕರೇ) ಹೇಳಿರಿ; ನಾನು ಇದರ ಹೆಸರಲ್ಲಿ ನಿಮ್ಮಿಂದ ಯಾವ ಪ್ರತಿಫಲವನ್ನೂ ಕೇಳುವುದಿಲ್ಲ. ನಾನು ನಿರ್ಮಿಸಿ ಹೇಳುವವರ ಸಾಲಿಗೆ ಸೇರಿದವನೂ ಅಲ್ಲ.');
INSERT INTO "kannada" VALUES(38,87,'38:87','ಇದು ಲೋಕದವರಿಗಾಗಿ ಒಂದು ಉದ್ಭೋದನೆ ಮಾತ್ರವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(38,88,'38:88','ಒಂದು ಕಾಲಾವಧಿಯ ನಂತರ ಇದರ ವೃತ್ತಾಂತ ನಿಮಗೆ ಖಂಡಿತ ತಿಳಿಯುವುದು .');
INSERT INTO "kannada" VALUES(39,1,'39:1','ಈ ಗ್ರಂಥದ ಅವತೀರ್ಣವು ಅಜೇಯನೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆದ ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(39,2,'39:2','ಈ ಗ್ರಂಥವನ್ನು ನಾವು ನಿಮ್ಮ ಕಡೆಗೆ ಪರಮ ಸತ್ಯವಾಗಿ ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದೇವೆ. ಆದುದರಿಂದ ನೀವು ವಿಧೇಯತೆಯನ್ನು ಅಲ್ಲಾಹನಿಗಾಗಿಯೇ ಮೀಸಲಿಟ್ಟು ಅಲ್ಲಾಹನಿಗೆ ಆರಾಧಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(39,3,'39:3','ತಿಳಿಯಿರಿ, ನಿಷ್ಕಳಂಕವಾದ ವಿಧೇಯತ್ವವು ಕೇವಲ ಅಲ್ಲಾಹನ ಹಕ್ಕಾಗಿದೆ. ಅವನ ಹೊರತು ಇತರರನ್ನು ಆರಾಧ್ಯರನ್ನಾಗಿ ಮಾಡಿಕೊಂಡವರು “ಅವರು ನಮ್ಮನ್ನು ಅಲ್ಲಾಹನಿಗೆ ಹತ್ತಿರಗೊಳಿಸಲಿಕ್ಕಾಗಿ ಮಾತ್ರ ನಾವು ಅವರನ್ನು ಆರಾಧಿಸುತ್ತಿದ್ದೇವೆ” ಎಂದು ಹೇಳುತ್ತಾರೆ . ಅವರು ಭಿನ್ನತೆ ತೋರಿದ ವಿಷಯದಲ್ಲಿ ಅಲ್ಲಾಹು ಅವರ ನಡುವೆ ತೀರ್ಮಾನ ಮಾಡುವನು. ಸುಳ್ಳುಗಾರನೂ, ಕೃತಘ್ನನೂ ಆಗಿರುವಾತನಿಗೆ ಅಲ್ಲಾಹನು ಖಂಡಿತ ಸತ್ಪಥ ನೀಡುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(39,4,'39:4','ಅಲ್ಲಾಹನು ಯಾರನ್ನಾದರೂ ಪುತ್ರನಾಗಿ ಮಾಡಿ ಕೊಳ್ಳಲು ಇಚ್ಚಿಸುತ್ತಿದ್ದರೆ, ತನ್ನ ಸೃಷ್ಟಿಯಲ್ಲಿ ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ಆಯ್ಕೆ ಮಾಡಿಕೊಳ್ಳುತ್ತಿದ್ದನು. ಅವನು ಪರಿಶುದ್ಧನು. ಅವನು ಅಲ್ಲಾಹನು - ಏಕೈಕನು ಮತ್ತು ಸರ್ವಾಧಿಪತಿಯು.');
INSERT INTO "kannada" VALUES(39,5,'39:5','ಭೂಮಿ-ಆಕಾಶಗಳನ್ನು ಸತ್ಯಬದ್ಧವಾಗಿ ಅವನು ಸೃಷ್ಟಿಸಿರುವನು. ಹಗಲಿನ ಮೇಲೆ ರಾತ್ರಿಯನ್ನೂ, ರಾತ್ರಿಯ ಮೇಲೆ ಹಗಲನ್ನೂ ಅವನು ಸುತ್ತಿಸುತ್ತಿರು ತ್ತಾನೆ. ಸೂರ್ಯ ಮತ್ತು ಚಂದ್ರನನ್ನು ಅವನು ನಿಯಂ ತ್ರಿಸುತ್ತಾನೆ. ಎಲ್ಲವೂ ಒಂದು ನಿಗದಿತ ಕಾಲದವ ರೆಗೆ ಚಲಿಸುತ್ತಿರುವುದು. ತಿಳಿಯಿರಿ, ಅವನು ಅಜೇ ಯನೂ ಪರಮಕ್ಷಮಾಶೀಲನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(39,6,'39:6','ನಿಮ್ಮನ್ನು ಒಂದೇ ಒಂದು ದೇಹದಿಂದ ಅವನು ಸೃಷ್ಟಿಸಿದನು. ತರುವಾಯ ಆ ದೇಹದಿಂದ ಅದರ ಸಂಗಾತಿಯನ್ನು ಅವನು ಸೃಷ್ಟಿಸಿದನು. ಜಾನು ವಾರುಗಳಿಂದ ಎಂಟು (ಗಂಡು-ಹೆಣ್ಣು) ಜೋಡಿಗಳನ್ನು ಅವನು ನಿಮಗಾಗಿ ಒದಗಿಸಿದನು. ನಿಮ್ಮ ಮಾತೆಯರ ಉದರಗಳಲ್ಲಿ ಸೃಷ್ಟಿಯ ನಂತರ ಮೂರು ರೀತಿಯ ಕತ್ತಲುಗಳೊಳಗೆ ನಿಮ್ಮನ್ನು ಅವನು ಸೃಷ್ಟಿಸುತ್ತಾನೆ. ಅಂಥವನೇ ನಿಮ್ಮ ಪಾಲಕ ಪ್ರಭುವಾದ ಅಲ್ಲಾಹು. ಪ್ರಭುತ್ವವು ಅವನಿಗೇ ಸೇರಿದೆ. ಅವನ ಹೊರತು ಬೇರೆ ಆರಾಧ್ಯನಿಲ್ಲ. ಹೀಗಿರುವಾಗ ನಿಮ್ಮನ್ನು ಬೇರೊಬ್ಬನ ಆರಾಧನೆಗೆ ಹೇಗೆ ತಿರುಗಿಸಲಾಗುತ್ತಿದೆ ?');
INSERT INTO "kannada" VALUES(39,7,'39:7','ನೀವು ಕೃತಘ್ನತೆ ತೋರಿದರೆ ಅಲ್ಲಾಹು ನಿಮ್ಮಿಂದ ನಿರಪೇಕ್ಷನಾಗಿದ್ದಾನೆ. ಅವನು ತನ್ನ ದಾಸರ ಪಾಲಿ ಗೆ ಕೃತಘ್ನತೆಯನ್ನು ಮೆಚ್ಚುವುದಿಲ್ಲ. ನೀವು ಕೃತಜ್ಞ ರಾದರೆ ಅದನ್ನು ಅವನು ನಿಮ್ಮ ಪಾಲಿಗೆ ಮೆಚ್ಚು ತ್ತಾನೆ. ಪಾಪ ಹೊರೆ ಹೊರುವವನಾರೂ ಇನ್ನೊಬ್ಬನ ಹೊರೆಯನ್ನು ಹೊರಲಾರನು ಂ. ಕೊನೆಗೆ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಗೇ ನಿಮ್ಮ ನಿರ್ಗಮನ. ನೀವು ಮಾಡು ತ್ತಿರುವುದರ ಬಗ್ಗೆ ಆಗ ಅವನು ನಿಮಗೆ ವಿವರವನ್ನು ಕೊಡುವನು. ಖಂಡಿತ ಅವನು ಮನದೊಳಗಿನ ಸಂಗತಿಯನ್ನು ಬಲ್ಲವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(39,8,'39:8','ಮನುಷ್ಯನಿಗೆ ಯಾವುದೇ ವಿಪತ್ತು ಬಾಧಿಸಿದರೆ ಅವನು ದೈನ್ಯನಾಗಿ ತನ್ನ ಪ್ರಭುವಿನ ಕಡೆಗೆ ಮರಳಿ ಅವನನ್ನು ಪ್ರಾರ್ಥಿಸುತ್ತಾನೆ. ನಂತರ ಅವನ ಪ್ರಭು ತನ್ನ ಕಡೆಯಿಂದ ಯಾವುದೇ ಅನುಗ್ರಹವನ್ನು ಅವನಿಗೆ ದಾನ ನೀಡಿದರೆ ಅವ ನು, ಹಿಂದೆ ತಾನು ಯಾವುದರ ಬಗ್ಗೆ ಪ್ರಾರ್ಥಿಸಿದ್ದನೋ ಅದನ್ನು ಮರೆತು ಬಿಡುತ್ತಾನೆ. ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ ತಪ್ಪಿಸಲಿಕ್ಕಾಗಿ ಅವನಿಗೆ ಸಮಾನ ರನ್ನು ಸ್ಥಾಪಿಸುತ್ತಾನೆ. (ಸಂದೇಶವಾಹಕರೇ) ಹೇಳಿರಿ, “ಸ್ವಲ್ಪ ಕಾಲ ನಿನ್ನ ಸತ್ಯನಿಷೇಧದ ಸುಖ ಭೋಗವನ್ನು ಆಸ್ವಾದಿಸುತ್ತಿರು, ಖಂಡಿತವಾಗಿಯೂ ನೀನು ನರಕ ವಾಸಿಗಳಲ್ಲಿ ಸೇರಿರುವೆ”.');
INSERT INTO "kannada" VALUES(39,9,'39:9','ಇಂತಹ ವ್ಯಕ್ತಿ ತನ್ನ ಪ್ರಭುವಿನ ಅನುಗ್ರಹವನ್ನು ಆಗ್ರಹಿಸಿ ಪರಲೋಕವನ್ನು ಭಯಪಟ್ಟು ರಾತ್ರಿಯ ಜಾವಗಳಲ್ಲಿ ಸಾಷ್ಟಾಂಗ ಪ್ರಣಾಮ ಮಾಡುತ್ತ, ನಿಂತು ಪ್ರಾರ್ಥಿಸುತ್ತ ಇರುವ ಉಪಾಸಕನಂತಾಗುವನೇ? ಕೇಳಿರಿ, ತಿಳಿದವರೂ ತಿಳಿಯದವರೂ ಸಮಾನರೇ? ಬುದ್ಧಿವಂತರು ಮಾತ್ರ ಉಪದೇಶ ಸ್ವೀಕರಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(39,10,'39:10','ಹೇಳಿರಿ; “ಸತ್ಯವಿಶ್ವಾಸಿಗಳಾದ ನನ್ನ ದಾಸರೇ! ನಿಮ್ಮ ಪ್ರಭುವನ್ನು ಭಯಪಡಿರಿ. ಇಹಲೋಕದಲ್ಲಿ ಸದಾಚಾರ ಕೈಗೊಂಡವರಿಗೆ ಪುಣ್ಯವಿದೆ. ಅಲ್ಲಾಹನ ಭೂಮಿ ವಿಶಾಲವಾಗಿದೆ . ಸಹನಶೀಲ ರಿಗೆ ಅವರ ಪ್ರತಿಫಲವನ್ನು ಲೆಕ್ಕವಿಲ್ಲದೆ ಪೂರ್ತಿ ಮಾಡಿ ಕೊಡಲಾಗುವುದು”.');
INSERT INTO "kannada" VALUES(39,11,'39:11','ಹೇಳಿರಿ; “ಧರ್ಮವನ್ನು ಅಲ್ಲಾಹನಿಗೆ ಮಾತ್ರ ನಿಷ್ಕಳಂಕಗೊಳಿಸಿ ಅವನನ್ನು ಆರಾಧಿಸಬೇಕೆಂದು ನನಗೆ ಅಪ್ಪಣೆ ಕೊಡಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(39,12,'39:12','ನಾನು ಮುಸ್ಲಿಮರಲ್ಲಿ ಮೊದಲಿಗನಾಗಬೇಕೆಂದೂ ನನಗೆ ಆಜ್ಞಾಪಿಸಲಾಗಿದೆ”.');
INSERT INTO "kannada" VALUES(39,13,'39:13','ಹೇಳಿರಿ; “ನಾನು ನನ್ನ ಪ್ರಭುವಿಗೆ ದಿಕ್ಕಾರ ತೋರಿದರೆ, ಒಂದು ಮಹಾದಿನದ ಶಿಕ್ಷೆಯನ್ನು ಖಂಡಿತ ನಾನು ಭಯಪಡುವೆನು”.');
INSERT INTO "kannada" VALUES(39,14,'39:14','ಹೇಳಿರಿ, “ನನ್ನ ಧರ್ಮವನ್ನು ಅಲ್ಲಾಹನಿಗೆ ಮಾತ್ರ ಮೀಸಲಾಗಿಟ್ಟು ಅವನನ್ನೇ ಆರಾಧಿಸುವೆನು.');
INSERT INTO "kannada" VALUES(39,15,'39:15','ನೀವು ಅವನ ಹೊರತು ನೀವಿಚ್ಚಿಸುವುದನ್ನು ಆರಾಧಿಸಿರಿ” . ಹೇಳಿರಿ; “ಪುನರುತ್ಥಾನ ದಿನ ತಮ್ಮನ್ನೂ ತಮ್ಮ ಕುಟುಂಬದವರನ್ನೂ ನಷ್ಟ ಕ್ಕೀಡು ಮಾಡಿದವರೇ ನಿಜವಾದ ನಷ್ಟವಂತರು”. ತಿಳಿಯಿರಿ. ಇದೇ ಸ್ಪಷ್ಟವಾದ ನಷ್ಟ.');
INSERT INTO "kannada" VALUES(39,16,'39:16','ಅವರ ಮೇಲ್ಭಾಗದಲ್ಲಿ ಅವರಿಗೆ ಅಗ್ನಿಯ ಮುಚ್ಚ ಳಗಳಿವೆ. ಅವರ ಕೆಳಭಾಗದಲ್ಲಿ ಹಾಸುಗಳಿವೆ . ಅಲ್ಲಾಹನು ತನ್ನ ದಾಸರನ್ನು ಎಚ್ಚರಿಸುತ್ತಿರುವನು. ನನ್ನ ದಾಸರೇ, ನನ್ನನ್ನು ನೀವು ಭಯಪಡಿರಿ ಎಂದು.');
INSERT INTO "kannada" VALUES(39,17,'39:17','');
INSERT INTO "kannada" VALUES(39,18,'39:18','ಅಲ್ಲಾಹೇತರ ಆರಾಧ್ಯರ ಆರಾಧನೆಯನ್ನು ತೊರೆದು ಅಲ್ಲಾಹನ ಕಡೆಗೆ ವಿನಯಾನ್ವಿತರಾಗಿ ಮರಳುವವರಿಗೆ ಶುಭವಾರ್ತೆಯಿದೆ. ಆದು ದರಿಂದ ವಚನವನ್ನು ಚೆನ್ನಾಗಿ ಆಲಿಸುವ ಹಾಗೂ ಅದರಲ್ಲಿ ಅತ್ಯುತ್ತಮವಾದುದನ್ನು ಅನುಸರಿಸುವ ನನ್ನ ದಾಸರಿಗೆ ಶುಭವಾರ್ತೆ ನೀಡಿರಿ. ಅಲ್ಲಾಹು ಸನ್ಮಾರ್ಗ ನೀಡಿದ ಜನರವರು. ಅವರೇ ಬುದ್ದಿವಂತರು.');
INSERT INTO "kannada" VALUES(39,19,'39:19','ಯಾರ ಮೇಲೆ ಶಿಕ್ಷೆಯ ವಚನವು ನಿಜ ಗೊಂಡಿದೆಯೋ ಅಂಥವನನ್ನು ನರಕಾಗ್ನಿಯಿಂದ ನೀವು ರಕ್ಷಿಸಬಲ್ಲಿರಾ?');
INSERT INTO "kannada" VALUES(39,20,'39:20','ಆದರೆ ತಮ್ಮ ಪ್ರಭುವನ್ನು ಭಯಪಟ್ಟು ನಡೆದ ವರಿಗೆ ಅಂತಸ್ತಿನ ಮೇಲೆ ಅಂತಸ್ತಿರುವ ಉನ್ನತ ಭವನಗಳಿವೆ. ಅವುಗಳ ತಳಭಾಗದಲ್ಲಿ ಕಾಲು ವೆಗಳು ಹರಿಯುತ್ತಿವೆ. ಅಲ್ಲಾಹನ ವಾಗ್ದಾನವಿದು. ಅಲ್ಲಾಹನು ತನ್ನ ವಾಗ್ದಾನವನ್ನು ಉಲ್ಲಂಘಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(39,21,'39:21','ನೀವು ನೋಡುತ್ತಿಲ್ಲವೇ? ಅಲ್ಲಾಹನು ಆಕಾಶ ದಿಂದ ನೀರು ಇಳಿಸಿದನು. ಅನಂತರ ಅದನ್ನು ಭೂಮಿಯ ಒರತೆಗಳಿಗೆ ಹರಿಸಿದನು. ತರುವಾಯ ಆ ನೀರಿನ ಮೂಲಕ ಅವನು ವಿವಿಧ ಬಣ್ಣದ ಬೆಳೆಯನ್ನು ಹೊರತೆಗೆಯುತ್ತಾನೆ. ಅನಂತರ ಆ ಬೆಳೆ ಹಣ್ಣಾಗಿ ಒಣಗುತ್ತವೆ. ಆಗ ಅದು ಹಳದಿ ಬಣ್ಣ ತಾಳುವುದನ್ನು ನೀವು ನೋಡುತ್ತಿರಿ. ಕೊನೆಗೆ ಅಲ್ಲಾಹನು ಅದನ್ನು ಹೊಟ್ಟಾಗಿ ಮಾಡುತ್ತಾನೆ. ವಾಸ್ತವದಲ್ಲಿ ಇದರಲ್ಲಿ ಬುದ್ಧಿಜೀವಿಗಳಿಗೆ ಗುಣ ಪಾಠವಿದೆ.');
INSERT INTO "kannada" VALUES(39,22,'39:22','ಅಲ್ಲಾಹನು ಯಾರ ಹೃದಯವನ್ನು ಇಸ್ಲಾಮಿಗಾಗಿ ವಿಶಾಲಗೊಳಿಸುತ್ತಾನೋ ಅವನು ತನ್ನ ಪ್ರಭು ವಿನ ಕಡೆಯಿಂದ ಒಂದು ಪ್ರಕಾಶದ ಮೇಲಿರುವನು. ಆದರೆ ಅಲ್ಲಾಹನ ಸ್ಮರಣೆಯಿಂದ ಹೃದಯಗಳು ದೂರವಾದವರು - ಅವರಿಗೆ ವಿನಾಶವಿದೆ. ಅವರು ಸುವ್ಯಕ್ತ ವಾದ ದುರ್ಮಾ ರ್ಗದಲ್ಲೇ ಇರುವರು.');
INSERT INTO "kannada" VALUES(39,23,'39:23','ಅಲ್ಲಾಹನು ಅತ್ಯುತ್ತಮ ವೃತ್ತಾಂತವನ್ನು - ಗ್ರಂಥ ವನ್ನು ಅವತೀರ್ಣಗೊಳಿಸಿರುವನು. ಪರಸ್ಪರ ಸಾಮ್ಯವಿರುವ ಹಾಗೂ ಪುನರಾವರ್ತಿಸಲ್ಪಡುವ ವಚನಗಳನ್ನೊಳಗೊಂಡಿರುವ ಗ್ರಂಥವಿದು. ಇದರಿಂದ ತಮ್ಮ ಪ್ರಭುವನ್ನು ಭಯಪಡುವವರ ಚರ್ಮಗಳು ಕಂಪಿಸುತ್ತವೆ. ಕೊನೆಗೆ ಅವರ ಚರ್ಮ ಹಾಗೂ ಹೃದಯಗಳು ಅಲ್ಲಾಹನ ಸ್ಮರಣೆಯಿಂದ ಶಾಂತವಾಗುವುದು. ಇದು ಅಲ್ಲಾ ಹನ ಮಾರ್ಗದರ್ಶನ. ಆತನು ಇದರ ಮೂಲಕ ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ಸತ್ಪಥಕ್ಕೆ ತರುವನು. ಅಲ್ಲಾಹನು ದಾರಿ ತಪ್ಪಿಸಿದರೆ ಅಂಥವನಿಗೆ ಬೇರಾವ ಮಾರ್ಗದರ್ಶಕನೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(39,24,'39:24','ಪುನರುತ್ಥಾನದಿನ ಕಠೋರವಾದ ಶಿಕ್ಷೆಗೆ ತನ್ನ ಮುಖವನ್ನು ಒಡ್ಡುವವನು (ಅದರಿಂದ ಪಾರಾ ದವನಿಗೆ) ಸಮಾನನೇ? ‘ನೀವು ಸಂಪಾದಿಸುತ್ತಿದ್ದ ಗಳಿಕೆಯ ರುಚಿಯನ್ನು ಸವಿಯಿರಿ’ ಎಂದು ಅಕ್ರಮಿಗಳಿಗೆ ಹೇಳಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(39,25,'39:25','ಇವರಿಗಿಂತ ಮುಂಚಿನವರೂ ಸುಳ್ಳಾಗಿಸಿದ್ದಾರೆ. ಕೊನೆಗೆ ಅವರು ಗ್ರಹಿಸಿಯೇ ಇರದ ಕಡೆಯಿಂದ ಅವರಿಗೆ ಶಿಕ್ಷೆ ಬಂತು.');
INSERT INTO "kannada" VALUES(39,26,'39:26','ತರುವಾಯ ಅಲ್ಲಾಹನು ಅವರಿಗೆ ಲೌಕಿಕ ಜೀವನದಲ್ಲೇ ಅಪಮಾನದ ರುಚಿ ತೋರಿಸಿದನು. ಪರಲೋಕದ ಯಾತನೆಯು ಇದಕ್ಕಿಂತಲೂ ಕಠೋರ. ಇವರು ಅರಿಯುತ್ತಿದ್ದರೆ!');
INSERT INTO "kannada" VALUES(39,27,'39:27','ನಾವು ಖುರ್‍ಆನ್‍ನಲ್ಲಿ ಜನರಿಗೆ ಎಲ್ಲ ತರದ ಉದಾಹರಣೆಗಳನ್ನು ನೀಡಿದ್ದೇವೆ. ಅವರು ಜಾಗೃತರಾಗಲೆಂದು.');
INSERT INTO "kannada" VALUES(39,28,'39:28','ಹೌದು, ಯಾವುದೇ ಅಂಕುಡೊಂಕಿಲ್ಲದ, ಅರಬೀ ಭಾಷೆಯಲ್ಲಿರುವ ಖುರ್‍ಆನ್. ಇವರು ಧರ್ಮ ನಿಷ್ಠೆ ಪಾಲಿಸಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(39,29,'39:29','ಅಲ್ಲಾಹು ಓರ್ವ ವ್ಯಕ್ತಿಯನ್ನು ಉದಾಹರಣೆಯಾಗಿ ತೋರಿಸಿರುವನು. ಪರಸ್ಪರ ಕಚ್ಚಾಡುವ ಕೆಲವು ಪಾಲುದಾರರೇ ಆತನ ಯಜಮಾನರು. ಇನ್ನೊಬ್ಬನು ಕೇವಲ ಒಬ್ಬ ಯಜಮಾನನಿಗೆ ಮಾತ್ರ ಶರಣಾದವನು. ಇವರಿಬ್ಬರೂ ಸಮಾನರೇ? ಸರ್ವಸ್ತುತಿಗಳೂ ಅಲ್ಲಾಹನಿಗೆ ಮೀಸಲು. ಆದರೆ ಹೆಚ್ಚಿನವರು ತಿಳಿಯುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(39,30,'39:30','(ಸಂದೇಶವಾಹಕರೇ) ನೀವೂ ಮೃತರಾಗುವವರು. ಅವರೂ ಮೃತರಾಗುವವರು.');
INSERT INTO "kannada" VALUES(39,31,'39:31','ತರುವಾಯ ಪುನರುತ್ಥಾನ ದಿನ ನೀವೆಲ್ಲರೂ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಸನ್ನಿಧಿಯಲ್ಲಿ ತರ್ಕಹೂಡಿ ಸೇಡು ತೀರಿಸಿಕೊಳ್ಳುವಿರಿ.');
INSERT INTO "kannada" VALUES(39,32,'39:32','ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಳ್ಳಾರೋಪ ಹೊರಿಸಿದ ಮತ್ತು ಸತ್ಯವು ತನ್ನ ಮುಂದೆ ಬಂದಾಗ ಅದನ್ನು ಸುಳ್ಳಾಗಿಸಿದವನಿಗಿಂತ ದೊಡ್ಡ ಅಕ್ರಮಿ ಯಾರಿದ್ದಾರೆ? ಸತ್ಯನಿಷೇಧಿಗಳ ನೆಲೆ ನರಕದಲ್ಲೇ ಅಲ್ಲವೇ!');
INSERT INTO "kannada" VALUES(39,33,'39:33','ಸತ್ಯವನ್ನು ತಂದು, ಅದನ್ನು ಸತ್ಯವೆಂದು ನಂಬಿದವರು ಯಾರು, ಅವರೇ ಧರ್ಮನಿಷ್ಠರು.');
INSERT INTO "kannada" VALUES(39,34,'39:34','ಅವರು ಬಯಸಿದ್ದೆಲ್ಲವೂ ಅವರ ಪ್ರಭುವಿನ ಬಳಿ ಅವರಿಗೆ ಸಿಗುವುದು. ಇದು ಸದಾಚಾರಿಗಳ ಸತ್ಫಲ.');
INSERT INTO "kannada" VALUES(39,35,'39:35','ಅವರು ಮಾಡಿದ್ದ ನಿಕೃಷ್ಟ ಕರ್ಮಗಳನ್ನು ಅಲ್ಲಾಹನು ಅವರಿಂದ ಅಳಿಸಿ ಹಾಕಲಿಕ್ಕಾಗಿ. ಮತ್ತು ಅವರಿಗೆ ಅವರು ಮಾಡುತ್ತಿದ್ದ ಉತ್ತಮ ಕರ್ಮಗಳಿಗನುಸಾರ ಪ್ರತಿಫಲ ನೀಡಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(39,36,'39:36','ಅಲ್ಲಾಹನು ತನ್ನ ದಾಸನಿಗೆ (ಪ್ರವಾದಿಯವರಿಗೆ) ಸಾಕಾಗಲಾರನೇ? ಇವರು ಅಲ್ಲಾಹನ ಹೊರತಾಗಿರುವ ಆರಾಧ್ಯರನ್ನು ತೋರಿಸಿ ನಿಮ್ಮನ್ನು ಹೆದರಿಸುತ್ತಾರೆ. ಅಲ್ಲಾಹನು ದಾರಿ ತಪ್ಪಿಸಿದರೆ ಅವನಿಗೆ ಯಾವ ಮಾರ್ಗದರ್ಶಕನೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(39,37,'39:37','ಯಾರಿಗೆ ಅಲ್ಲಾಹನು ನೇರ ದಾರಿ ತೋರಿದ ನೋ, ಅವನನ್ನು ದಾರಿಗೆಡಿಸುವವನೂ ಯಾರೂ ಇಲ್ಲ. ಅಲ್ಲಾಹು ದುಷ್ಟ ಸಂಹಾರಕನಾದ ಅಜೇ ಯನಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(39,38,'39:38','ಭೂಮಿ-ಆಕಾಶಗಳನ್ನು ಸೃಷ್ಟಿಸಿದವನಾರೆಂದು ನೀವು ಇವರೊಡನೆ ಕೇಳಿದರೆ, ಅಲ್ಲಾಹನೆಂದು ಇವರು ಖಂಡಿತ ಹೇಳುವರು. ಹೇಳಿರಿ, ‘ಅಲ್ಲಾಹನ ಹೊರತು ನೀವು ಆರಾಧಿಸುವುದನ್ನು ಕೇಳಿ ತಿಳಿದು ನೋಡಿರುವಿರಾ? ಅಲ್ಲಾಹನು ನನಗೇನಾದರೂ ಹಾನಿಯನ್ನು ಉಂಟು ಮಾಡಲಿಚ್ಛಿಸಿದರೆ ಅವನ ಹಾನಿಯಿಂದ ಅವು ನನ್ನನ್ನು ರಕ್ಷಿಸಬಲ್ಲವೇ ಅಥವಾ ಅಲ್ಲಾಹು ನನ್ನ ಮೇಲೆ ಕೃಪೆ ತೋರಲಿಚ್ಛಿಸಿದರೆ ಅವು ಅವನ ಕೃಪೆಯನ್ನು ತಡೆಯಬಲ್ಲವೇ?’ ಹೇಳಿರಿ, ನನಗೆ ಅಲ್ಲಾಹನೇ ಸಾಕು. ಭರವಸೆ ಇಡುವವರು ಅವನ ಮೇಲೆ ಯೇ ಭರವಸೆಯನ್ನಿಡುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(39,39,'39:39','ಹೇಳಿರಿ. ‘ನನ್ನ ಜನಾಂಗದವರೇ, ನೀವು ನಿಮ್ಮ ಅವಸ್ಥೆ ಪ್ರಕಾರ ಕಾರ್ಯಾಚರಿಸಿರಿ. ನಾನೂ ಕಾರ್ಯ ಮಾಡುತ್ತಲಿರುವೆನು. ಸದ್ಯದಲ್ಲೇ ನೀವು ತಿಳಿಯುವಿರಿ.');
INSERT INTO "kannada" VALUES(39,40,'39:40','ಯಾರಿಗೆ ಅಪಮಾನಕರ ಶಿಕ್ಷೆ ಬರುತ್ತದೆ ಮತ್ತು ಶಾಶ್ವತವಾದ ಶಿಕ್ಷೆ ಯಾರಿಗೆ ಎರಗುತ್ತದೆ’ ಎಂದು.');
INSERT INTO "kannada" VALUES(39,41,'39:41','(ಸಂದೇಶವಾಹಕರೇ) ನಿಶ್ಚಯವಾಗಿಯೂ ನಾವು ಜನರಿಗಾಗಿ ಈ ಪರಮ ಸತ್ಯದಿಂದ ಕೂಡಿರುವ ಈ ಗ್ರಂಥವನ್ನು ನಿಮಗೆ ಅವತೀರ್ಣಗೊಳಿಸಿ ದ್ದೇವೆ. ಆಗ ಯಾರು ನೇರ ದಾರಿ ಹಿಡಿಯು ತ್ತಾರೋ ಅದು ಆತನ ಹಿತಕ್ಕಾಗಿಯೇ ಇದೆ. ಯಾರು ತಪ್ಪು ದಾರಿ ತುಳಿಯುತ್ತಾರೋ ಅವನು ತನ್ನ ಮೇಲೆಯೇ ತಪ್ಪು ದಾರಿ ತುಳಿಯುತ್ತಾನೆ. ನೀವು ಅವರ ಮೇಲೆ ಹೊಣೆಗಾರರಲ್ಲ.');
INSERT INTO "kannada" VALUES(39,42,'39:42','ಅಲ್ಲಾಹನು ಆತ್ಮಗಳನ್ನು ಅವುಗಳ ಮರಣದ ವೇಳೆ ವಶಪಡಿಸಿಕೊಳ್ಳುವನು . ಮರಣ ಹೊಂದದ ಆತ್ಮವನ್ನು ನಿದ್ರಾವಸ್ಥೆಯಲ್ಲಿ ವಶಪಡಿಸಿ ಕೊಳ್ಳುವನು. ತರುವಾಯ ಅವನು ಯಾವುದರ ಮೇಲೆ ಮರಣ ವಿಧಿಸಿರುವನೋ ಅವುಗಳನ್ನು ತಡೆಹಿಡಿಯುತ್ತಾನೆ. ಇತರ ಆತ್ಮಗಳನ್ನು ಒಂದು ನಿರ್ದಿಷ್ಟ ಕಾಲದವರೆಗೆ ಬಿಟ್ಟುಬಿಡುತ್ತಾನೆ. ವಿಚಾರ ಶೀಲರಿಗೆ ಇದರಲ್ಲಿ ಖಂಡಿತ ನಿದರ್ಶನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(39,43,'39:43','ಅದಲ್ಲ, ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಇತರರನ್ನು ಶಿಫಾರಸ್ಸು ದಾರರಾಗಿ ಅವರು ಸ್ವೀಕರಿಸಿಕೊಂಡರೇ? ಹೇಳಿರಿ; ‘ಅವರು ಏನನ್ನೂ ಒಡೆತನಗೊಳಿಸದಿದ್ದರೂ ಅವರು ಯೋಚಿಸಿ ಅರ್ಥಮಾಡಿಕೊಳ್ಳ ದಿದ್ದರೂ (ಅವರು ಹಾಗೆ ಮಾಡುವರೇ?’) ');
INSERT INTO "kannada" VALUES(39,44,'39:44','ಹೇಳಿರಿ; ಶಿಫಾರಸ್ಸು ಸಂಪೂರ್ಣವಾಗಿ ಅಲ್ಲಾಹನ ಅಧಿಕಾರದಲ್ಲಿದೆ. ಭೂಮಿ ಆಕಾಶಗಳ ಆಧಿಪತ್ಯ ಆತನಿಗೇ ಸೇರಿದ್ದು. ಅನಂತರ ನೀವು ಅವನ ಕಡೆಗೇ ಮರಳಿಸಲ್ಪಡುವಿರಿ.');
INSERT INTO "kannada" VALUES(39,45,'39:45','ಅಲ್ಲಾಹನ ಬಗ್ಗೆ ಮಾತ್ರ ಪ್ರತಿಪಾದಿಸಲಾದರೆ ಪರಲೋಕದ ಮೇಲೆ ವಿಶ್ವಾಸವಿಲ್ಲದವರ ಹೃದಯಗಳು ಅಸ್ವಸ್ಥಗೊಳ್ಳುತ್ತವೆ. ಅವನ ಹೊರತು ಇತರರ ಪ್ರಸ್ತಾಪವೆತ್ತಿದರೆ ಅವರು ತಕ್ಷಣ ಸಂತುಷ್ಟಗೊಳ್ಳುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(39,46,'39:46','ಹೇಳಿರಿ; “ಓ ಅಲ್ಲಾಹ್! ಭೂಮಿ-ಆಕಾಶಗಳ ಸೃಷ್ಟಿಕರ್ತನೇ! ಪ್ರತ್ಯಕ್ಷ ಪರೋಕ್ಷಗಳ ಜ್ಞಾನಿಯೇ, ನಿನ್ನ ದಾಸರು ಪರಸ್ಪರ ಭಿನ್ನಾಭಿಪ್ರಾಯ ತಾಳಿದ ವಿಷಯದಲ್ಲಿ ಅವರ ನಡುವೆ ನೀನೇ ತೀರ್ಮಾನ ಮಾಡಲಿರುವೆ”.');
INSERT INTO "kannada" VALUES(39,47,'39:47','ಭೂಮಿಯಲ್ಲಿರುವ ಸಕಲ ಸಂಪತ್ತು ಮಾತ್ರವಲ್ಲ, ಅಷ್ಟೇ ಬೇರೆಯೂ ಅಕ್ರಮಿಗಳ ವಶದಲ್ಲಿದ್ದರೂ ಕೂಡ ಅವರು ಇದೆಲ್ಲವನ್ನು ಪನರುತ್ಥಾನ ದಿನದ ಉಗ್ರ ಯಾತನೆಯಿಂದ ಪಾರಾಗಲಿಕ್ಕಾಗಿ ದಂಡ ತರಲು ಸಿದ್ಧರಾಗುವರು. ಅವರು ಊಹಿಸಿಯೂ ಇಲ್ಲದ ಹಲವು ಕಾರ್ಯಗಳು ಅಲ್ಲಾಹನ ಕಡೆ ಯಿಂದ ಅವರಿಗೆ ಬಯಲಾಗಿ ಬಿಟ್ಟಿತು.');
INSERT INTO "kannada" VALUES(39,48,'39:48','ಅವರ ಗಳಿಕೆಯ ದುಷ್ಪರಿಣಾಮಗಳೆಲ್ಲವೂ ಅವರಿಗೆ ಬಯಲಾಗಿ ಬಿಟ್ಟಿತು. ಯಾವುದನ್ನು ಅವರು ಪರಿಹಾಸ್ಯ ಮಾಡುತ್ತಿದ್ದರೋ ಅದೇ ಅವರನ್ನು ಸುತ್ತುವರಿಯಿತು.');
INSERT INTO "kannada" VALUES(39,49,'39:49','ಮಾನವನಿಗೆ ಏನಾದರೂ ಸಂಕಷ್ಟವು ಬಂದರೆ ಅವನು ನಮ್ಮನ್ನು ಪ್ರಾರ್ಥಿಸುತ್ತಾನೆ. ಅನಂತರ ನಾವು ಅವನಿಗೆ ನಮ್ಮ ಕಡೆಯಿಂದ ಯಾವುದೇ ಅನುಗ್ರಹವನ್ನು ಕೊಟ್ಟರೆ ಇದನ್ನು ನನಗೆ ಜ್ಞಾನದ ಆಧಾರದಲ್ಲಿ ಕೊಡಲಾಗಿದೆ ಎನ್ನುತ್ತಾನೆ. ಅಲ್ಲ, ವಾಸ್ತವದಲ್ಲಿ ಇದು ಪರೀಕ್ಷೆಯಾಗಿದೆ. ಆದರೆ ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಅರಿಯುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(39,50,'39:50','ಇವರಿಗಿಂತ ಮುಂಚೆ ಗತಿಸಿದವರೂ ಹೀಗೆ ಹೇಳಿದ್ದಾರೆ. ಆದರೆ ಅವರು ಗಳಿಸಿಟ್ಟದ್ದೇನೂ ಅವರಿಗೆ ಫಲಕಾರಿಯಾಗಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(39,51,'39:51','ಹಾಗೆ ಅವರು ತಮ್ಮ ಗಳಿಕೆಯ ಕೆಟ್ಟ ಫಲವನ್ನು ಅನುಭವಿಸಿದರು. ಅವರ ಪೈಕಿ ಅಕ್ರಮವೆಸಗಿದ ವರು ಸದ್ಯದಲ್ಲೇ ತಮ್ಮ ಗಳಿಕೆಯ ದುಷ್ಫಲವನ್ನು ಅನುಭವಿಸುವರು. ಅವರು ನಮ್ಮನ್ನು ಸೋಲಿಸಲಾರರು.');
INSERT INTO "kannada" VALUES(39,52,'39:52','ಅಲ್ಲಾಹನು ತಾನಿಚ್ಚಿಸಿದವರಿಗೆ ಉಪಜೀವನ ವನ್ನು ವಿಶಾಲಗೊಳಿಸುತ್ತಾನೆಂದೂ ತಾನಿಚ್ಚಿಸಿದವರಿಗೆ ಅದನ್ನು ಪರಿಮಿತಗೊಳಿಸುತ್ತಾನೆಂದೂ ಇವರಿಗೆ ಗೊತ್ತಿಲ್ಲವೇ? ವಿಶ್ವಾಸವಿರಿಸುವ ಜನರಿಗೆ ಇದರಲ್ಲಿ ನಿದರ್ಶನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(39,53,'39:53','ಹೇಳಿರಿ; ತಮ್ಮ ಮೇಲೆಯೇ ಅತಿರೇಕವೆಸಗಿ ಕೊಂಡ ಓ ನನ್ನ ದಾಸರೇ, ಅಲ್ಲಾಹನ ಕರುಣೆಯ ಬಗ್ಗೆ ನಿರಾಶರಾಗದಿರಿ. ಅಲ್ಲಾಹು ಸಕಲ ಪಾಪ ಗಳನ್ನು ಖಂಡಿತ ಕ್ಷಮಿಸುತ್ತಾನೆ. ಅವನು ಕ್ಷಮಾ ಶೀಲನೂ ಕರುಣಾಮಯಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(39,54,'39:54','ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಗೆ ಮರಳಿರಿ. ಆತನಿಗೆ ಶರಣಾಗಿರಿ. ನಿಮ್ಮ ಮೇಲೆ ಶಿಕ್ಷೆ ಬರುವ ಮುನ್ನ . ನಂತರ ನೀವು ಸಹಾಯ ಪಡೆಯಲಾರಿರಿ.');
INSERT INTO "kannada" VALUES(39,55,'39:55','ನೀವು ತಿಳಿಯದಂತೆ ಹಠಾತ್ತನೆ ನಿಮಗೆ ಶಿಕ್ಷೆ ಬರುವ ಮುನ್ನ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ನಿಮಗೆ ಕಳುಹಿಸಲಾಗಿರುವ ಪೈಕಿ ಅತ್ಯುತ್ತಮವಾದುದನ್ನು ಅನುಸರಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(39,56,'39:56','‘ಓ ನನ್ನ ಖೇದವೇ, ಅಲ್ಲಾಹನ ಭಾಗದಲ್ಲಿ ಮಾಡ ಬೇಕಾದ ಕರ್ಮದಲ್ಲಿ ನಾನು ಲೋಪ ಮಾಡಿದೆ, ನಾನು ಗೇಲಿ ಮಾಡುವವರ ಸಾಲಿಗೆ ಸೇರಿದ್ದೆನು’ ಎಂದು ಹೇಳಲಿಕ್ಕಾಗಿ,');
INSERT INTO "kannada" VALUES(39,57,'39:57','‘ಅಥವಾ ಅಲ್ಲಾಹನು ನನಗೆ ಮಾರ್ಗದರ್ಶನ ನೀಡಿರುತ್ತಿದ್ದರೆ ನಾನೂ ದೇವನಿಷ್ಟರಲ್ಲಾಗುತ್ತಿದ್ದೆ’ ಎಂದು ಹೇಳುವುದಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(39,58,'39:58','‘ಅಥವಾ ಶಿಕ್ಷೆಯನ್ನು ಕಣ್ಣಾರೆ ಕಾಣುವಾಗ, ನನಗೆ ಮರಳಿ ಹೋಗಲು ಸಾಧ್ಯವಿರುತ್ತಿದ್ದರೆ ನಾನೂ ಸದ್ಭಕ್ತರಲ್ಲಿ ಸೇರಿ ಬಿಡುತ್ತಿದ್ದೆ’ ಎಂದು ಹೇಳುವುದಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(39,59,'39:59','(ಆಗ ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ಹೀಗೆ ಹೇಳಲಾ ಗುವುದು) ಹೌದು, ನನ್ನ ದೃಷ್ಟಾಂತಗಳು ನಿನ್ನ ಬಳಿಗೆ ಬಂದಿವೆ. ಆಗ ನೀನು ಅವುಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದೆ ಮತ್ತು ದುರಹಂಕಾರ ತೋರಿದೆ, ನೀನು ಸತ್ಯನಿಷೇಧಿಗಳಲ್ಲಿ ಸೇರಿ ಹೋದೆ.');
INSERT INTO "kannada" VALUES(39,60,'39:60','ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಳ್ಳಾರೋಪ ಹೊರಿಸಿದವರ ಮುಖಗಳು ಪುನರುತ್ಥಾನ ದಿನ ಕಪ್ಪಾಗಿ ಬಿಟ್ಟಿರು ವುದನ್ನು ನೀವು ಕಾಣುವಿರಿ. ದುರಹಂಕಾರಿಗಳ ವಾಸಸ್ಥಳ ನರಕಾಗ್ನಿಯಲ್ಲೇ ಅಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(39,61,'39:61','ದೇವಭಯವುಳ್ಳವರನ್ನು ತಮ್ಮ ಸುರಕ್ಷಿತ ತಾಣದಲ್ಲಿ (ತಂದಿರಿಸುವ ಮೂಲಕ) ಅಲ್ಲಾಹನು ಸಂರಕ್ಷಿಸುವನು. ಯಾವ ಹಾನಿಯೂ ಅವರಿಗೆ ತಟ್ಟದು. ಅವರು ದುಃಖಿಸುವವರೂ ಅಲ್ಲ.');
INSERT INTO "kannada" VALUES(39,62,'39:62','ಅಲ್ಲಾಹು ಸಕಲ ವಸ್ತುಗಳ ಸೃಷ್ಟಿಕರ್ತನು. ಅವನು ಸಕಲ ವಸ್ತುಗಳ ಮೇಲ್ವಿಚಾರಕನು.');
INSERT INTO "kannada" VALUES(39,63,'39:63','ಭೂಮಿ-ಆಕಾಶಗಳ ಕೀಲಿ ಕೈಗಳು ಅವನ ವಶದಲ್ಲಿವೆ. ಅಲ್ಲಾಹನ ನಿದರ್ಶನಗಳನ್ನು ನಿಷೇಧಿಸುವವರೇ ನಷ್ಟ ಹೊಂದಿದವರು.');
INSERT INTO "kannada" VALUES(39,64,'39:64','(ಸಂದೇಶವಾಹಕರೇ) ಹೇಳಿರಿ: ತಿಳಿಗೇಡಿಗಳೇ, ಅಲ್ಲಾಹನ ಹೊರತು ಇತರರನ್ನು ನಾನು ಆರಾ ಧಿಸುವಂತೆ ನನಗೆ ನೀವು ಆದೇಶ ಕೊಡುತ್ತಿ ರುವಿರಾ?');
INSERT INTO "kannada" VALUES(39,65,'39:65','ನೀವು ದೇವಸಹಭಾಗಿತ್ವವೆಸಗಿದರೆ ನಿಮ್ಮ ಕರ್ಮವು ನಿರರ್ಥಕಗೊಳ್ಳುವುದು ಮತ್ತು ನೀವು ನಷ್ಟ ಹೊಂದಿದವರಲ್ಲಾಗುವಿರೆಂದು ನಿಮಗೂ ನಿಮಗಿಂತ ಮುಂಚಿನವರಿಗೂ ದಿವ್ಯವಾಣಿ ಕಳುಹಿಸಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(39,66,'39:66','ಆದುದರಿಂದ (ಸಂದೇಶವಾಹಕರೇ) ಅಲ್ಲಾಹ ನನ್ನೇ ನೀವು ಆರಾಧಿಸಿರಿ. ಕೃತಜ್ಞರಲ್ಲಿ ಸೇರಿರಿ.');
INSERT INTO "kannada" VALUES(39,67,'39:67','ಇವರು ಅಲ್ಲಾಹನನ್ನು ಗೌರವಿಸಬೇಕಾದ ರೀತಿಯಲ್ಲಿ ಗೌರವಿಸಲಿಲ್ಲ. ಪುನರುತ್ಥಾನ ದಿನ ಇಡೀ ಭೂಮಿಯು ಅವನ ಹಿಡಿತದಲ್ಲಿರುವುದು. ಆಕಾಶಗಳು ಅವನ ಶಕ್ತಿಯಲ್ಲಿ ಸುತ್ತಿಕೊಂಡಿರುವವು . ಇವರು ಎಸಗುತ್ತಿರುವ ಬಹುದೇವಾರಾಧನೆಯಿಂದ ಅವನು ಪಾವನನೂ ಪರಮೋ ನ್ನತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(39,68,'39:68','ಕಹಳೆಯಲ್ಲಿ ಊದಲಾಗುವುದು. ಆಗ ಭೂಮಿ- ಆಕಾಶಗಳಲ್ಲಿರುವವರೆಲ್ಲರೂ ಸತ್ತು ಬೀಳುವರು. ಅಲ್ಲಾಹು ಇಚ್ಚಿಸುವವರ ಹೊರತು . ತರುವಾಯ ಇನ್ನೊಂದು ಕಹಳೆ ಊದಲಾಗುವುದು. ಆಗ ಎಲ್ಲರೂ ಎದ್ದು ನಿಂತು ನಿರೀಕ್ಷಿಸುವರು.');
INSERT INTO "kannada" VALUES(39,69,'39:69','ಭೂಮಿಯು ತನ್ನ ಪ್ರಭುವಿನ ಪ್ರಕಾಶದಿಂದ ಬೆಳಗುವುದು. ಕರ್ಮಗ್ರಂಥವನ್ನು ತಂದಿಡ ಲಾಗುವುದು. ಪ್ರವಾದಿಗಳು ಮತ್ತು ಸಾಕ್ಷಿಗಳನ್ನು ತರಲಾಗುವುದು. ಜನರ ನಡುವೆ ಸತ್ಯ-ನ್ಯಾಯ ಪ್ರಕಾರ ತೀರ್ಮಾನ ಮಾಡಲಾಗುವುದು. ಅವರಿಗೆ ಅನ್ಯಾಯವೆಸಗಲಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(39,70,'39:70','ಪ್ರತಿಯೊಬ್ಬ ವ್ಯಕ್ತಿಗೂ ತಾನು ಮಾಡಿದ್ದ ಕರ್ಮಗಳ ಸರ್ವಸಂಪೂರ್ಣ ಪ್ರತಿಫಲ ನೀಡಲಾಗುವುದು. ಅವರು ಮಾಡುತ್ತಿರುವುದನ್ನು ಅಲ್ಲಾಹು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(39,71,'39:71','ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ನರಕಕ್ಕೆ ವಿವಿಧ ಗುಂಪು ಗಳಾಗಿ ಅಟ್ಟಲಾಗುವುದು. ಅವರು ಅಲ್ಲಿಗೆ ತಲುಪುವಾಗ ಅದರ ಪ್ರವೇಶ ದ್ವಾರಗಳನ್ನು ತೆರೆಯಲಾಗುವುದು. ಅದರ ಪಾಳೆಯಗಾರರು ಕೇಳುವರು, “ಏನು, ನಿಮ್ಮ ಪ್ರಭುವಿನ ವಚನಗಳನ್ನು ಓದಿ ಹೇಳುವ ಹಾಗೂ ನಿಮ್ಮ ಈ ದಿನವನ್ನು ಕಾಣಬೇಕಾಗುವ ಬಗ್ಗೆ ನಿಮ್ಮನ್ನು ಎಚ್ಚರಿಸುವ ದೇವದೂತರು ನಿಮ್ಮಿಂದಲೇ ನಿಮ್ಮ ಬಳಿ ಬಂದಿರಲಿಲ್ಲವೇ?”. ಅವರು ಹೀಗೆ ಉತ್ತರ ಕೊಡು ವರು; ಹೌದು ಬಂದಿದ್ದರು, ಆದರೆ ಸತ್ಯ ನಿಷೇಧಿ ಗಳ ಮೇಲೆ ಶಿಕ್ಷೆಯ ವಚನವು ನಿಜಗೊಂಡಿದೆ.');
INSERT INTO "kannada" VALUES(39,72,'39:72','ಆಗ ಅವರೊಡನೆ `ನರಕದ ದ್ವಾರಗಳ ಮೂಲಕ ಒಳಗೆ ನುಗ್ಗಿರಿ. ನೀವು ಇದರಲ್ಲಿ ಶಾಶ್ವತವಾಗಿ ನೆಲೆಗೊಳ್ಳುವಿರಿ. ದುರಹಂಕಾರಿಗಳ ತಾಣವು ಎಷ್ಟು ನಿಕೃಷ್ಟ’ ಎನ್ನಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(39,73,'39:73','ತಮ್ಮ ಒಡೆಯನ ಮೇಲೆ ಭಕ್ತಿಯಿರಿಸಿಕೊಂಡ ಧರ್ಮ ನಿಷ್ಠರನ್ನು ಸ್ವರ್ಗಕ್ಕೆ ವಿವಿಧ ಗುಂಪುಗ ಳಾಗಿ ಕರೆದೊಯ್ಯಲಾಗುವುದು. ಅದರ ಬಾಗಿಲುಗಳನ್ನು ತೆರೆದಿಡಲಾದ ಸ್ಥಿತಿಯಲ್ಲಿ ಅವರು ಅಲ್ಲಿಗೆ ತಲುಪುವಾಗ ಅದರ ಕಾವಲುಗಾರರು ಅವರೊಡನೆ, ‘ನಿಮಗೆ ಶುಭವಾಗಲಿ. ನೀವು ಪುನೀ ತರಾಗಿದ್ದಿರಿ. ಆದ್ದರಿಂದ ಸದಾಕಾಲ ನೆಲೆಸಲಿ ಕ್ಕಾಗಿ ಒಳಗೆ ಚಿತ್ತೈಸಿರಿ’ ಎನ್ನುವರು.');
INSERT INTO "kannada" VALUES(39,74,'39:74','ಆಗ ಇವರು ಹೇಳುವರು; ನಮ್ಮೊಂದಿಗೆ ತನ್ನ ವಾಗ್ದಾನವನ್ನು ಸತ್ಯಗೊಳಿಸಿದ ಹಾಗೂ ಸ್ವರ್ಗ ದಲ್ಲಿ ನಮಗಿಷ್ಟ ಬಂದ ಕಡೆ ನೆಲೆಸುವ ರೀತಿ ಯಲ್ಲಿ ನಮ್ಮನ್ನು ಈ ಭೂಮಿಯ ವಾರೀಸು ದಾರರಾಗಿ ಮಾಡಿದ ಅಲ್ಲಾಹನಿಗೆ ಕೃತಜ್ಞತೆಗಳು. ಅಂದ ಮೇಲೆ ಕರ್ಮಶಾಲಿಗಳ ಪ್ರತಿಫಲ ಎಷ್ಟು ವಿಶಿಷ್ಟವಾದುದು.');
INSERT INTO "kannada" VALUES(39,75,'39:75','ದೇವಚರರು ತಮ್ಮ ಪ್ರಭುವಿಗೆ ಸ್ತುತಿ ಕೀರ್ತ ನೆಗಳನ್ನು ಅರ್ಪಿಸುತ್ತ ಅರ್ಶ್‍ನ ಸುತ್ತಲೂ ಪ್ರದ ಕ್ಷಿಣೆ ಬರುತ್ತಿರುವುದನ್ನು ನೀವು ನೋಡುವಿರಿ. ಸೃಷ್ಟಿಗಳ ನಡುವೆ ನ್ಯಾಯಪೂರ್ಣ ತೀರ್ಮಾನ ಮಾಡಲಾಗುವುದು. ಸರ್ವಲೋಕ ಪಾಲಕನಾದ ಅಲ್ಲಾಹನಿಗೇ ಸಕಲ ಸ್ತೋತ್ರಗಳೂ ಮೀಸಲೆಂದು ಹೇಳಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(40,1,'40:1','ಹಾಮೀಮ್.');
INSERT INTO "kannada" VALUES(40,2,'40:2','ಈ ಗ್ರಂಥವು ಪರಮ ಪ್ರತಾಪಿಯೂ, ಸರ್ವಜ್ಞನೂ ಆದ ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ಅವತೀರ್ಣಗೊಂಡಿದೆ.');
INSERT INTO "kannada" VALUES(40,3,'40:3','ಅವನು ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸುವವನೂ ಪಶ್ಚಾತ್ತಾಪ ಸ್ವೀಕರಿಸುವವನೂ ಕಠಿಣ ಶಿಕ್ಷೆ ಕೊಡುವವನೂ, ಅನುಗ್ರಹದಾತನೂ ಆಗಿರುತ್ತಾನೆ. ಅವನ ಹೊರತು ಬೇರೆ ಆರಾಧ್ಯರಿಲ್ಲ, ಸರ್ವರಿಗೂ ಅವನ ಕಡೆಗೇ ಮರಳಬೇಕಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(40,4,'40:4','ಅಲ್ಲಾಹನ ವಚನಗಳ ಬಗ್ಗೆ ಸತ್ಯನಿಷೇಧಿಗಳು ಮಾತ್ರವೇ ಜಗಳಾಡಬಲ್ಲರು. ಆದುದರಿಂದ ಜಗತ್ತಿನ ನಾಡುಗಳಲ್ಲಿ ಅವರು ಸ್ಥೈರ್ಯವಾಗಿ ವಿಹರಿಸುವುದನ್ನು ಕಂಡು ಮೋಸ ಹೋಗಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(40,5,'40:5','ಇವರಿಗಿಂತ ಮುಂಚೆ ನೂಹರ ಜನಾಂಗವೂ ಆ ಬಳಿಕ ಇತರ ಅನೇಕ ಸಂಘ ಪರಿವಾರಗಳೂ ಸತ್ಯ ವನ್ನು ನಿಷೇಧಿಸಿವೆ. ಪ್ರತಿಯೊಂದು ಜನಾಂಗವೂ ತನ್ನ ಸಂದೇಶವಾಹಕನನ್ನು ಸೆರೆ ಹಿಡಿಯಲಿಕ್ಕೂ ಮಿಥ್ಯದ ಅಸ್ತ್ರಗಳಿಂದ ಸತ್ಯವನ್ನು ನಾಶ ಮಾಡಲಿ ಕ್ಕೂ ಯತ್ನಿಸಿತು. ಆಗ ನಾನು ಅವರನ್ನು ಹಿಡಿದು ಬಿಟ್ಟೆನು. ನನ್ನ ಪ್ರತೀಕಾರವು ಎಷ್ಟು ಕಠಿಣವಾಗಿ ತ್ತೆಂದು ಬಲ್ಲಿರಾ?');
INSERT INTO "kannada" VALUES(40,6,'40:6','ಸತ್ಯನಿಷೇಧಕರು ನರಕವಾಸಿಗಳಾಗಲಿರು ವರೆಂಬ ನಿಮ್ಮ ಪ್ರಭುವಿನ ವಚನವು ಹಾಗೆ ನಿಜವಾಯಿತು.');
INSERT INTO "kannada" VALUES(40,7,'40:7','ಅರ್ಶನ್ನು ಹೊತ್ತ ದೇವಚರರು ಮತ್ತು ಅದರ ಸುತ್ತಲೂ ನೆರೆದವರು ತಮ್ಮ ಪ್ರಭುವಿನ ಪ್ರಕೀ ರ್ತನೆ ಮಾಡುತ್ತ ಅವನನ್ನು ಸ್ತುತಿಸುತ್ತ ಅವನ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸುತ್ತಾರೆ. ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಪಾಪ ವಿಮೋಚನೆಗಾಗಿ ಅವರು ಹೀಗೆ ಪ್ರಾರ್ಥಿಸುತ್ತಾರೆ. “ನಮ್ಮ ಪ್ರಭೂ! ನಿನ್ನ ಕೃಪೆಯೂ ಜ್ಞಾನವೂ ಸಕಲ ವಸ್ತುಗಳನ್ನು ಆವರಿಸಿಕೊಂಡಿದೆ. ಆದುದರಿಂದ ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು ಮರಳಿದ ಹಾಗೂ ನಿನ್ನ ಮಾರ್ಗವನ್ನು ಸ್ವೀಕರಿಸಿಕೊಂಡವರಿಗೆ ಕ್ಷಮಾದಾನ ನೀಡು ಮತ್ತು ನರಕಯಾತನೆಯಿಂದ ಅವರನ್ನು ರಕ್ಷಿಸು.');
INSERT INTO "kannada" VALUES(40,8,'40:8','ನಮ್ಮ ಪ್ರಭೂ, ಅವರನ್ನೂ ಸಜ್ಜನರಾದ ಅವರ ಪಿತರನ್ನೂ, ಪತ್ನಿಯರನ್ನೂ, ಮಕ್ಕಳನ್ನೂ ಅವರಿಗಾಗಿ ನೀನು ವಾಗ್ದಾನ ಮಾಡಿರುವ ಶಾಶ್ವ ತವಾದ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ ಪ್ರವೇಶ ಗೊಳಿಸು. ನಿಶ್ಚಯವಾಗಿ ನೀನು ಪ್ರಬಲನೂ ಯುಕ್ತಿವಂತನೂ ಆಗಿರುವೆ.');
INSERT INTO "kannada" VALUES(40,9,'40:9','ಅವರನ್ನು ಕೇಡುಗಳಿಂದ ರಕ್ಷಿಸು. ನೀನು ಆ ದಿನ ಯಾರನ್ನು ಕೇಡುಗಳಿಂದ ರಕ್ಷಿಸಿದೆಯೋ ಅವನ ಮೇಲೆ ನೀನು ಕರುಣೆ ತೋರಿದೆ. ಇದುವೇ ಮಹಾ ವಿಜಯ.');
INSERT INTO "kannada" VALUES(40,10,'40:10','ಸತ್ಯನಿಷೇಧಿಗಳೊಡನೆ ಹೀಗೆ ಕೂಗಿ ಹೇಳಲಾ ಗುವುದು - “ನಿಮ್ಮನ್ನು ಸತ್ಯವಿಶ್ವಾಸದ ಕಡೆಗೆ ಕರೆಯಲಾಗುತ್ತಿದ್ದ ವೇಳೆ ನೀವು ಅವಿಶ್ವಾಸ ತಾಳಿದ ಸಂದರ್ಭದಲ್ಲಿ ಅಲ್ಲಾಹನಿಗೆ ನಿಮ್ಮ ಮೇಲೆ ಬಂದ ಕೋಪವು ಇದೀಗ ನಿಮಗೆ ನಿಮ್ಮಲ್ಲೇ ಇರುವ ಕೋಪಕ್ಕಿಂತ ಮಿಗಿಲಾಗಿದೆ”.');
INSERT INTO "kannada" VALUES(40,11,'40:11','ಅವರು ಹೇಳುವರು; “ನಮ್ಮ ಪ್ರಭೂ, ನಿಜಕ್ಕೂ ನೀನು ನಮಗೆ ಎರಡು ಸಲ ಮರಣವನ್ನೂ ಎರಡು ಸಲ ಜೀವನವನ್ನೂ ನೀಡಿರುವೆ . ಈಗ ನಾವು ನಮ್ಮ ಅಪರಾಧಗಳನ್ನು ಒಪ್ಪಿಕೊಳ್ಳುತ್ತೇವೆ. ಆದ್ದರಿಂದ ಈಗ ಇಲ್ಲಿಂದ ಹೊರ ಹೋಗಲು ಯಾವುದಾದರೂ ದಾರಿ ಇದೆಯೇ?”');
INSERT INTO "kannada" VALUES(40,12,'40:12','ಇದು ಏಕೆ ಸಂಭವಿಸಿತೆಂದರೆ ನಿಮ್ಮನ್ನು ಏಕಮಾತ್ರ ಅಲ್ಲಾಹನ ಕಡೆಗೆ ಕರೆಯುತ್ತಿದ್ದಾಗ ನೀವು ನಿರಾಕರಿಸಿದ್ದೀರಿ. ಅವನ ಜೊತೆ ಇತರ ರನ್ನು ಪಾಲು ಸೇರಿಸಿದಾಗ ನೀವು ವಿಶ್ವಾಸವಿ ರಿಸುತ್ತಿದ್ದಿರಿ. ಈಗ ತೀರ್ಮಾನದ ಅಧಿಕಾರವು ಅತ್ಯುನ್ನತನೂ ಮಹಾನನೂ ಆದ ಅಲ್ಲಾಹ ನಿಗಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(40,13,'40:13','ಅವನು ನಿಮಗೆ ತನ್ನ ನಿದರ್ಶನಗಳನ್ನು ತೋರಿಸುತ್ತಾನೆ. ಆಕಾಶದಿಂದ ನಿಮಗಾಗಿ (ಮಳೆಯ ಮೂಲಕ) ಆಹಾರವನ್ನು ಇಳಿಸು ತ್ತಾನೆ. ಆದರೆ ಅಲ್ಲಾಹನ ಕಡೆಗೆ ಮರಳುವವನು ಮಾತ್ರ ಯೋಚಿಸಿ ಗ್ರಹಿಸಿಕೊಳ್ಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(40,14,'40:14','ಆದುದರಿಂದ ನಿಮ್ಮ ಧರ್ಮವನ್ನು ಅಲ್ಲಾಹನಿಗೆ ಏಕನಿಷ್ಠಗೊಳಿಸಿದವರಾಗಿರುತ್ತ ನೀವು ಅವನಿಗೆ ಆರಾಧಿಸಿರಿ. ಇದು ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಎಷ್ಟು ಅಪ್ರಿಯವಾಗಿದ್ದರೂ ಸರಿಯೇ.');
INSERT INTO "kannada" VALUES(40,15,'40:15','ಅವನು ಅತ್ಯುನ್ನತ ಪದವಿಯುಳ್ಳವನೂ ಅರ್ಶ್‍ನ ಒಡೆಯನೂ ಆಗಿರುತ್ತಾನೆ. ಪರಸ್ಪರ ಭೇಟಿಯಾಗುವ ದಿನದ ಬಗೆಗೆ ಎಚ್ಚರಿಕೆ ಕೊಡಲಿ ಕ್ಕಾಗಿ ಅವನು ತನ್ನ ದಾಸರ ಪೈಕಿ ತಾನಿಚ್ಛಿ ಸಿದವರ ಮೇಲೆ ತನ್ನ ವಚನದ ಮೂಲಕ ದಿವ್ಯಸಂದೇಶವನ್ನು ಹಾಕುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(40,16,'40:16','ಅವರು ಹೊರಬರುವ ದಿನ ಅವರ ಯಾವ ವಿಷಯವೂ ಅಲ್ಲಾಹನಿಂದ ಅಡಗಿರಲಾರದು. ‘ಇಂದು ರಾಜಪ್ರಭುತ್ವ ಯಾರದು?’ ‘ಏಕೈಕನೂ ಸರ್ವಾಧಿಪತಿಯೂ ಆದ ಅಲ್ಲಾಹನದು’.');
INSERT INTO "kannada" VALUES(40,17,'40:17','ಆಗ ಪ್ರತಿಯೊಬ್ಬ ಜೀವಿಗೆ ಅವನು ಸಂಪಾದಿ ಸಿದುದರ ಪ್ರತಿಫಲ ನೀಡಲಾಗುವುದು. ಅಂದು ಯಾರ ಮೇಲೂ ಅನ್ಯಾಯವಿಲ್ಲ. ಅಲ್ಲಾಹನು ಲೆಕ್ಕ ಪರಿಶೋದನೆಯಲ್ಲಿ ಖಂಡಿತ ಅತಿ ಶೀಘ್ರನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(40,18,'40:18','(ಸಂದೇಶವಾಹಕರೇ,) ಸನ್ನಿಹಿತವಾಗಲಿರುವ ಅಂತ್ಯ ದಿನದ ಬಗ್ಗೆ ಜನರಿಗೆ ಎಚ್ಚರಿಕೆ ನೀಡಿರಿ. ಹೃದಯಗಳು ಗಂಟಲಿಗೇರಿ ಗಲಿಬಿಲಿಗೊಳ್ಳುವ ಆ ದಿನ ಅಕ್ರಮಿಗಳಿಗೆ ಆಪ್ತಮಿತ್ರನಾಗಲಿ ಅನುಸ ರಿಸಲ್ಪಡುವ ಶಿಫಾರಸ್ಸುದಾರನಾಗಲಿ ಇರಲಾರನು.');
INSERT INTO "kannada" VALUES(40,19,'40:19','ಅಲ್ಲಾಹನು ದೃಷ್ಟಿಯ ಕದ್ದು ನೋಟವನ್ನೂ ಹೃದಯದೊಳಗೆ ಬಚ್ಚಿಟ್ಟಿರುವ ರಹಸ್ಯಗಳನ್ನೂ ತಿಳಿಯುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(40,20,'40:20','ಅಲ್ಲಾಹನು ನ್ಯಾಯಪೂರ್ಣ ತೀರ್ಮಾನವೆಸಗು ವನು. ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಅವರು ಯಾರನ್ನು ಪೂಜಿಸುತ್ತಾರೋ ಅವರು ಯಾವ ವಿಷಯವನ್ನೂ ತೀರ್ಮಾನಿಸುವವರಲ್ಲ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಎಲ್ಲವನ್ನು ಕೇಳುವವನೂ ಕಾಣು ವವನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(40,21,'40:21','ಇವರು ಭೂಮಿಯಲ್ಲಿ ಸಂಚರಿಸಿಲ್ಲವೇ? ಆಗ ಇವರಿಗೆ ಮುಂಚೆ ಗತಿಸಿದವರ ಅಂತಿಮ ಪರಿ ಣಾಮವೇನಾಯಿತೆಂದು ನೋಡಬಹುದಲ್ಲವೇ? ಅವರು ಇವರಿಗಿಂತ ಹೆಚ್ಚು ಬಲಶಾಲಿಗಳಾಗಿದ್ದರು ಮತ್ತು ಇವರಿಗಿಂತ ಭದ್ರಸ್ಮಾರಕಗಳನ್ನು ಭೂಮಿಯಲ್ಲಿ ಬಿಟ್ಟುಹೋಗಿರುವರು. ಆದರೆ ಅಲ್ಲಾಹನು ಅವರನ್ನು ಅವರ ಪಾಪಗಳಿಗಾಗಿ ಹಿಡಿದು ಬಿಟ್ಟನು. ಅವರನ್ನು ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆ ಯಿಂದ ರಕ್ಷಿಸುವವನಾರೂ ಇರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(40,22,'40:22','ಇದು ಏಕೆಂದರೆ, ಅವರ ಸಂದೇಶವಾಹಕರು ಸುಸ್ಪಷ್ಟ ನಿದರ್ಶನಗಳನ್ನು ಅವರ ಬಳಿಗೆ ತಂದಿ ದ್ದರು. ಆಗ ಅವರು ನಿಷೇಧಿಸಿಬಿಟ್ಟರು. ಕೊನೆಗೆ ಅಲ್ಲಾಹನು ಅವರನ್ನು ಹಿಡಿದುಬಿಟ್ಟನು. ನಿಶ್ಚ ಯವಾಗಿಯೂ ಅವನು ಪ್ರಬಲನೂ ಕಠಿಣವಾಗಿ ಶಿಕ್ಷಿಸುವವನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(40,23,'40:23','');
INSERT INTO "kannada" VALUES(40,24,'40:24','ನಾವು ಮೂಸಾರನ್ನು ಫಿರ್‍ಔನ್, ಹಾಮಾನ್ ಮತ್ತು ಖಾರೂನ್‍ರ ಕಡೆಗೆ ನಮ್ಮ ನಿದರ್ಶನಗಳು ಹಾಗೂ ಸುವ್ಯಕ್ತವಾದ ಪ್ರಮಾಣ ದೊಂದಿಗೆ ರವಾನಿಸಿದೆವು. ಆಗ ಅವರು, ಇವನು ಮಾಟಗಾರ, ಸುಳ್ಳುಗಾರ ಎಂದರು.');
INSERT INTO "kannada" VALUES(40,25,'40:25','ತರುವಾಯ ಅವರು ನನ್ನ ಕಡೆಯಿಂದ ಸತ್ಯ ವನ್ನು ಅವರ ಮುಂದೆ ತಂದಾಗ ಅವರು, ‘ಇವನ ಜೊತೆ ನಂಬುವವರ ಗಂಡು ಮಕ್ಕಳನ್ನು ವಧಿಸಿರಿ ಮತ್ತು ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಜೀವಂತ ಬಿಡಿರಿ’ ಎಂದರು. ಆದರೆ ಸತ್ಯನಿಷೇಧಿಗಳ ಕುತಂತ್ರವು ಪರಾಭವಗೊಳ್ಳದೆ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(40,26,'40:26','ಫಿರ್‍ಔನನು “ನನ್ನನ್ನು ಬಿಡಿರಿ, ನಾನು ಈ ಮೂಸಾನನ್ನು ಕೊಂದು ಬಿಡುತ್ತೇನೆ. ಇವನು ತನ್ನ ಪ್ರಭುವನ್ನು ರಕ್ಷಣೆಗೆ ಕರೆಯಲಿ, ಇವನು ನಿಮ್ಮ ಧರ್ಮವನ್ನು ಬದಲಾಯಿಸುವನು ಅಥವಾ ದೇಶದಲ್ಲಿ ನಾಶವನ್ನು ಹರಡುವನು ಎಂಬ ಭಯ ನನಗೆ ಕಾಡಿದೆ” ಎಂದನು.');
INSERT INTO "kannada" VALUES(40,27,'40:27','ಆಗ ಮೂಸಾ, “ವಿಚಾರಣೆಯ ದಿನದ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸದ ಪ್ರತಿಯೊಬ್ಬ ಅಹಂಕಾರಿಯ ವಿರುದ್ಧ ನಾನು ನನ್ನ ಹಾಗೂ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಅಭಯ ಪಡೆದಿರುತ್ತೇನೆ” ಎಂದರು.');
INSERT INTO "kannada" VALUES(40,28,'40:28','ಆಗ ತನ್ನ ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಬಚ್ಚಿಟ್ಟುಕೊಂಡಿದ್ದ ಫಿರ್‍ಔನನ ವಂಶದ ಓರ್ವ ಸತ್ಯವಿಶ್ವಾಸಿಯು ಹೀಗೆ ಹೇಳಿದನು, “ನನ್ನ ಪ್ರಭು ಅಲ್ಲಾಹನಾಗಿರು ತ್ತಾನೆಂದು ಹೇಳಿದುದಕ್ಕಾಗಿ ಮಾತ್ರ ನೀವು ಒಬ್ಬನನ್ನು ಕೊಂದು ಬಿಡುವಿರಾ? ವಸ್ತುತಃ ಅವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ನಿಮ್ಮ ಬಳಿಗೆ ಸುವ್ಯಕ್ತ ಪ್ರಮಾಣಗಳನ್ನು ತಂದಿದ್ದಾರೆ. ಇನ್ನು ಅವರು ಸುಳ್ಳುಗಾರರಾಗಿದ್ದರೆ ಅವರ ಸುಳ್ಳಿನ ಕೇಡು ಅವರ ಮೇಲೆಯೇ ಇರುವುದು. ಅವರು ಸತ್ಯವಾದಿಯಾಗಿದ್ದರೆ ಅವರು ನಿಮ್ಮನ್ನು ಎಚ್ಚರಿಸುತ್ತಿರುವ ಕೆಲವು ಶಿಕ್ಷೆಗಳು ನಿಮ್ಮನ್ನು ಖಂಡಿತ ಬಾಧಿಸಬಲ್ಲವು. ಅತಿಕ್ರಮಿ ಮತ್ತು ಸುಳ್ಳುಗಾರನಿಗೆ ಅಲ್ಲಾಹನು ಸನ್ಮಾರ್ಗದರ್ಶನ ನೀಡುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(40,29,'40:29','ನನ್ನ ಜನಾಂಗವೇ, ಭೂಮಿಯಲ್ಲಿ ಮೇಲುಗೈ ಸಾಧಿಸಿದವರು ಎಂಬ ನೆಲೆಯಲ್ಲಿ ಇಂದು ನಿಮಗೆ ಅಧಿಕಾರ ಪ್ರಾಪ್ತವಿದೆ. ಆದರೆ, ಅಲ್ಲಾಹನ ಯಾತನೆ ನಮ್ಮ ಮೇಲೆ ಬಂದು ಬಿಟ್ಟರೆ ಅದರಿಂದ ನಮ್ಮ ನ್ನು ರಕ್ಷಿಸಿ ನೆರವಾಗಬಲ್ಲವರು ಯಾರು?” ಆಗ ಫಿರ್‍ಔನನು, ‘ನಿಮಗೆ ನಾನು ನನಗೆ ಯುಕ್ತ ಕಂಡ ಮಾರ್ಗವಲ್ಲದೆ ತೋರಿಸಿಕೊಡುತ್ತಿಲ್ಲ. ಸತ್ಪಥಕ್ಕಲ್ಲದೆ ನಾನು ನಿಮಗೆ ಮಾರ್ಗದರ್ಶನ ಮಾಡುತ್ತಿಲ್ಲ’ ಎಂದನು.');
INSERT INTO "kannada" VALUES(40,30,'40:30','ಸತ್ಯವಿಶ್ವಾಸಿ ವ್ಯಕ್ತಿ ಹೇಳಿದನು, ‘ನನ್ನ ಜನಾಂ ಗದವರೇ, ಸಂಘಪರಿವಾರದ ದಿನದಂಥ ದಿನ ನಿಮ್ಮ ಮೇಲೂ ಬಂದು ಬಿಡುವುದೋ ಎಂದು ನಾನು ಭಯಪಡುತ್ತಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(40,31,'40:31','ಅಥವಾ ನೂಹರ ಜನಾಂಗ, ಆದ್, ಸಮೂದ್ ಮತ್ತು ಅವರ ನಂತರದ ಜನಾಂಗದ ಅನುಭವಕ್ಕೆ ಸಮಾನವಾದದ್ದು. ತನ್ನ ದಾಸರ ಮೇಲೆ ಅಕ್ರಮವೆಸಗುವ ಉದ್ದೇಶ ಅಲ್ಲಾಹನಿಗೆ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(40,32,'40:32','ನನ್ನ ಜನಾಂಗವೇ, ಗೋಳು ಹೊಯ್ಯುವ ದಿನ ವನ್ನು ನಿಮ್ಮ ಮೇಲೆ ನಾನು ಭಯ ಪಡುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(40,33,'40:33','ಅಥವಾ ನೀವು ಬೆನ್ನು ತಿರುಗಿಸಿ ಓಡುವ ದಿನ. ಅಲ್ಲಾಹನಿಂದ ರಕ್ಷಿಸುವವರು ಯಾರೂ ನಿಮಗೆ ಇರಲಾರರು. ಯಾರನ್ನು ಅಲ್ಲಾಹು ದಾರಿಗೆಡಿಸಿರುವನೋ ಅವನಿಗೆ ಬೇರಾರೂ ದಾರಿತೋರಿಸುವವರಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(40,34,'40:34','ಇದಕ್ಕೆ ಮುಂಚೆ ಯೂಸುಫರು ನಿಮ್ಮ ಬಳಿಗೆ ಸುವ್ಯಕ್ತ ನಿದರ್ಶನಗಳನ್ನು ತಂದಿದ್ದರು. ಆಗ ಅವರು ತಂದ ದೃಷ್ಟಾಂತದ ಬಗೆಗೆ ನೀವು ಸಂಶಯಪಡುತ್ತಲೇ ಇದ್ದಿರಿ. ತರುವಾಯ ಅವರು ನಿಧನರಾದಾಗ ನೀವು, ‘ಇನ್ನು ಅವರ ಬಳಿಕ ಅಲ್ಲಾಹನು ಸಂದೇಶವಾಹಕರನ್ನು ನಿಯೋಗಿಸ ಲಿಕ್ಕಿಲ್ಲ’ ಎಂದಿರಿ. ಇದೇ ರೀತಿಯಲ್ಲಿ ಅಲ್ಲಾಹನು, ಅತಿಕ್ರಮಿಗಳು ಹಾಗೂ ಸಂಶಯಗ್ರಸ್ತರನ್ನು ದಾರಿ ತಪ್ಪಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(40,35,'40:35','ಅಥವಾ ತಮ್ಮ ಬಳಿಗೆ ಬಂದಂತಹ ಯಾವುದೇ ಪುರಾವೆಗಳಿಲ್ಲದೆಯೇ ಅಲ್ಲಾಹನ ನಿದರ್ಶನ ಗಳಲ್ಲಿ ಜಗಳ ಮಾಡುವವರು ಯಾರೋ ಅವರ ಈ ಜಗಳವು ಅಲ್ಲಾಹು ಮತ್ತು ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಬಳಿ ಅತ್ಯಂತ ಕ್ರೋಧಕ್ಕೀಡಾದ ಸಂಗತಿಯಾಗಿದೆ. ಇದೇ ರೀತಿಯಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬ ಅಹಂಕಾರಿ ಹಾಗೂ ಗರ್ವಿಷ್ಟನ ಹೃದಯಕ್ಕೆ ಅಲ್ಲಾಹು ಮುದ್ರೆ ಹಾಕುವನು.');
INSERT INTO "kannada" VALUES(40,36,'40:36','');
INSERT INTO "kannada" VALUES(40,37,'40:37','ಫಿರ್‍ಔನ್ ಹೇಳಿದನು; ‘ಓ ಹಾಮಾನ್, ನಾನು ಆ ಮಾರ್ಗಗಳನ್ನು ಅಥವಾ ಆಕಾಶ ಮಾರ್ಗಗಳನ್ನು ತಲುಪಿ ಮೂಸಾನ ದೇವನನ್ನು ಇಣುಕಿ ನೋಡುವಂತಾಗಲು ನನಗಾಗಿ ಒಂದು ಎತ್ತರದ ಕಟ್ಟಡವನ್ನು ನಿರ್ಮಿಸು, ನಾನಂತೂ ಈ ಮೂಸಾ ಸುಳ್ಳುಗಾರನೆಂದೇ ಭಾವಿಸಿದ್ದೇನೆ’. ಹೀಗೆ ಫಿರ್‍ಔನನಿಗೆ ಅವನ ದುಷ್ಕರ್ಮವನ್ನು ಅಲಂಕೃತಗೊಳಿಸಲಾಯಿತು. ಮತ್ತು ಅವನನ್ನು ಸನ್ಮಾರ್ಗದಿಂದ ತಡೆಯಲಾಯಿತು. ಫಿರ್‍ಔನನ ಕುತಂತ್ರವು ನಷ್ಟದಲ್ಲೇ ಬಿದ್ದು ಹೋಯಿತು.');
INSERT INTO "kannada" VALUES(40,38,'40:38','ಆ ಸತ್ಯವಿಶ್ವಾಸಿ ವ್ಯಕ್ತಿ ಹೇಳಿದನು, ‘ನನ್ನ ಜನಾಂ ಗದವರೇ, ನನ್ನನ್ನು ಅನುಸರಿಸಿರಿ. ನಾನು ನಿಮಗೆ ಸತ್ಪಥವನ್ನು ತೋರಿಸುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(40,39,'40:39','ಜನಾಂಗವೇ, ಈ ಲೌಕಿಕ ಜೀವನವು ಕ್ಷಣಿಕ ಸಾಧನ. ಶಾಶ್ವತ ನಿವಾಸವು ಪರಲೋಕವೇ ಆಗಿದೆ.');
INSERT INTO "kannada" VALUES(40,40,'40:40','ಯಾರು ಪಾಪ ಮಾಡುತ್ತಾನೆ, ಅವನಿಗೆ ಅದರಷ್ಟೇ ಪ್ರತಿಫಲ ಸಿಗುವುದು. ಪುರುಷನಿರಲಿ ಸ್ತ್ರೀ ಇರಲಿ ಯಾರು ಸತ್ಯವಿಶ್ವಾಸಿಯಾಗಿದ್ದು ಸತ್ಕರ್ಮ ವೆಸಗುತ್ತಾರೆ, ಅಂಥವರು ಸ್ವರ್ಗವನ್ನು ಪ್ರವೇಶಿಸು ವರು, ಅಲ್ಲಿ ಅವರಿಗೆ ಲೆಕ್ಕವಿಲ್ಲದಷ್ಟು ಜೀವನಾ ಧಾರ ನೀಡಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(40,41,'40:41','ಜನಾಂಗವೇ! ಇದೇನು, ನಾನು ನಿಮ್ಮನ್ನು ರಕ್ಷೆಯ ಕಡೆಗೆ ಕರೆಯುತ್ತಿದ್ದೇನೆ; ಆದರೆ ನೀವು ನನ್ನನ್ನು ನರಕಾಗ್ನಿಯತ್ತ ಕರೆಯುತ್ತಿರುವಿರಿ!');
INSERT INTO "kannada" VALUES(40,42,'40:42','ನಾನು ಅಲ್ಲಾಹನಲ್ಲಿ ಅವಿಶ್ವಾಸ ತಾಳಬೇಕೆಂದೂ ನಾನರಿಯದ್ದನ್ನು ಅವನಿಗೆ ದಿವ್ಯತ್ವದಲ್ಲಿ ಪಾಲು ಕಲ್ಪಿಸಬೇಕೆಂದೂ ನೀವು ನನಗೆ ಕರೆ ಕೊಡುತ್ತಿದ್ದೀರಿ. ಆದರೆ ನಾನು ನಿಮ್ಮನ್ನು ಅಜೇಯನೂ ಮಹಾ ಕ್ಷಮಾದಾನಿಯೂ ಆಗಿರುವ ಅಲ್ಲಾಹನ ಕಡೆಗೆ ಕರೆಯುತ್ತಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(40,43,'40:43','ನೀವು ನನ್ನನ್ನು ಯಾವುದರ ಕಡೆಗೆ ಕರೆಯುತ್ತಿರು ವಿರೋ ಅದಕ್ಕೆ ಇಹಲೋಕದಲ್ಲಾಗಲಿ, ಪರಲೋಕದಲ್ಲಾಗಲಿ ಯಾವುದೇ ಪ್ರಾರ್ಥನೆ ಇಲ್ಲ . ಅಲ್ಲಾಹ ನ ಕಡೆಗೇ ನಮಗೆ ಮರಳಿ ಹೋಗಬೇಕಾಗಿದೆ ಮತ್ತು ಅತಿಕ್ರಮಿಗಳೇ ನರಕವಾಸಿಗಳು ಎನ್ನುವುದರಲ್ಲಿ ಯಾವ ಸಂಶಯವೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(40,44,'40:44','ಆದರೆ ನಾನು ನಿಮ್ಮಲ್ಲಿ ಹೇಳುತ್ತಿರುವ ಮಾತನ್ನು ಸದ್ಯದಲ್ಲೇ ನೀವು ಜ್ಞಾಪಿಸಿಕೊಳ್ಳುವಿರಿ. ನನ್ನ ಕಾರ್ಯವನ್ನು ಅಲ್ಲಾಹನಿಗೆ ನಾನು ಒಪ್ಪಿಸಿ ಬಿಡುತ್ತೇನೆ. ಅಲ್ಲಾಹನು ತನ್ನ ದಾಸರ ವೀಕ್ಷಕ ನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(40,45,'40:45','ಕೊನೆಗೆ ಅವರು ನಡೆಸಿದ್ದ ಎಲ್ಲ ಕುತಂತ್ರಗಳ ದುಷ್ಫಲಗಳಿಂದ ಅಲ್ಲಾಹನು ಆ ಸತ್ಯವಿಶ್ವಾಸಿ ಯನ್ನು ಕಾಪಾಡಿದನು. ಫಿರ್‍ಔನನ ಜನರನ್ನು ಅತ್ಯಂತ ಕೆಟ್ಟ ಯಾತನೆಯು ಸುತ್ತುವರಿಯಿತು.');
INSERT INTO "kannada" VALUES(40,46,'40:46','ಬೆಳಗು ಬೈಗುಗಳಲ್ಲಿ ಅವರನ್ನು ನರಕಾಗ್ನಿಯ ಮುಂದೆ ಪ್ರದರ್ಶಿಸಲಾಗುವುದು. ಅಂತಿಮ ಘಳಿಗೆ ಜಾರಿಯಾಗುವ ದಿನ ಫಿರ್‍ಔನನ ಜನರನ್ನು ಅತ್ಯುಗ್ರ ಶಿಕ್ಷೆಯಲ್ಲಿ ಪ್ರವೇಶಗೊಳಿಸಿರೆಂದು ಅಪ್ಪಣೆಯಾಗುವುದು .');
INSERT INTO "kannada" VALUES(40,47,'40:47','ನರಕಾಗ್ನಿಯಲ್ಲಿ ಅವರು ಪರಸ್ಪರ ವಾದ ಹೂಡುವ ಸನ್ನಿವೇಶವನ್ನು ಊಹಿಸಿರಿ. ಆಗ ದುರ್ಬಲರು ಅಹಂಕಾರಿಗಳಲ್ಲಿ ಹೀಗೆ ಹೇಳುವರು, ‘ನಾವು ನಿಮಗೆ ವಿಧೇಯರಾಗಿದ್ದೆವು. ಆದ್ದರಿಂದ ನರಕಾ ಗ್ನಿಯಿಂದ ಯಾವುದೆ ಅಂಶವನ್ನು ನಮ್ಮಿಂದ ನಿವಾರಿಸಿ ರಕ್ಷಿಸುವಿರಾ?’');
INSERT INTO "kannada" VALUES(40,48,'40:48','ಅಹಂಕಾರಿಗಳು ಹೀಗೆ ಉತ್ತರ ಕೊಡುವರು; ‘ನಾವೆಲ್ಲರೂ ಈಗ ನರಕದಲ್ಲಿದ್ದೇವೆ. ಅಲ್ಲಾಹನು ದಾಸರ ನಡುವೆ ತೀರ್ಮಾನ ಮಾಡಿ ಮುಗಿ ಯಿತು’.');
INSERT INTO "kannada" VALUES(40,49,'40:49','ನರಕದಲ್ಲಿರುವವರು ನರಕಪಾಲಕರೊಡನೆ, ‘ನೀವು ನಿಮ್ಮ ಪ್ರಭುವನ್ನು ಪ್ರಾರ್ಥಿಸಿ, ಒಂದು ದಿನದ ಶಿಕ್ಷೆಯನ್ನಾದರೂ ಅವನು ಹಗುರ ಮಾಡಲಿ’ ಎನ್ನುವರು.');
INSERT INTO "kannada" VALUES(40,50,'40:50','ಆಗ “ನಿಮ್ಮ ಸಂದೇಶವಾಹಕರು ಸುವ್ಯಕ್ತ ನಿದರ್ಶನಗಳನ್ನು ತರುತ್ತಿರಲಿಲ್ಲವೇ?” ಎಂದು ನರಕಪಾಲಕರು ಕೇಳುವರು. ಆಗ ಅವರು ‘ಹೌದು’ ಎನ್ನುವರು. ಆಗ ನರಕಪಾಲಕರು, “ಹಾಗಾದರೆ, ಅಲ್ಲಾಹನಲ್ಲಿ ನೀವೇ ಪ್ರಾರ್ಥಿಸಿರಿ” ಎನ್ನುವರು. ಸತ್ಯನಿಷೇಧಿಗಳ ಪ್ರಾರ್ಥನೆ ವ್ಯರ್ಥವೇ ಆಗಿದೆ.');
INSERT INTO "kannada" VALUES(40,51,'40:51','ನಾವು ನಮ್ಮ ಸಂದೇಶವಾಹಕರಿಗೆ ಹಾಗೂ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಐಹಿಕ ಜೀವನದಲ್ಲಿಯೂ ಸಾಕ್ಷಿದಾರರು ಸಾಕ್ಷಿ ನಿಲ್ಲುವ ದಿನದಂದೂ ಸಹಾಯ ಮಾಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(40,52,'40:52','ಅಥವಾ ಅಕ್ರಮಿಗಳಿಗೆ ತನ್ನ ನೆಪಗಳು ಯಾವುದೇ ಪ್ರಯೋಜನವಾಗದ ದಿನ. ಅವರಿಗೆ ಶಾಪವಿದೆ. ಕೆಟ್ಟ ಭವನವೂ ಅವರಿಗುಂಟು.');
INSERT INTO "kannada" VALUES(40,53,'40:53','ಮೂಸಾರಿಗೆ ನಾವು ಮಾರ್ಗದರ್ಶನವನ್ನು ಕೊಟ್ಟೆವು. ಇಸ್ರಾಈಲರನ್ನು ನಾವು ವೇದಗ್ರಂಥದ ಹಕ್ಕುದಾರರಾಗಿ ಮಾಡಿದೆವು.');
INSERT INTO "kannada" VALUES(40,54,'40:54','ಅದು ಬುದ್ಧಿಜೀವಿಗಳಿಗೆ ಸನ್ಮಾರ್ಗದರ್ಶನವೂ ಬೋಧನೆಯೂ ಆಗಿತ್ತು.');
INSERT INTO "kannada" VALUES(40,55,'40:55','ಆದುದರಿಂದ (ಸಂದೇಶವಾಹಕರೇ,) ತಾಳ್ಮೆ ವಹಿಸಿರಿ. ಅಲ್ಲಾಹನ ವಾಗ್ದಾನವು ಪರಮ ಸತ್ಯ ವಾಗಿದೆ. ನಿಮ್ಮ ತಪ್ಪಿನ ಬಗ್ಗೆ ಕ್ಷಮೆಯಾಚಿಸಿರಿ . ಬೆಳಗು ಬೈಗುಗಳಲ್ಲಿ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಸ್ತುತಿಯೊಂದಿಗೆ ಅವನಿಗೆ ನಮಾಝ್ ನಿರ್ವಹಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(40,56,'40:56','ಅವರ ಬಳಿ ಬಂದಿರುವ ಅಲ್ಲಾಹನ ನಿದರ್ಶನಗಳ ಬಗ್ಗೆ ಯಾವುದೇ ಆಧಾರವಿಲ್ಲದೆ ಜಗಳ ಹೂಡುವವರ ಹೃದಯಗಳಲ್ಲಿ ಅಹಂಕಾರವಲ್ಲದೆ ಬೇರೇನಿಲ್ಲ. ಆದರೆ ಅವರು ಗುರಿ ತಲುಪುವ ವರಲ್ಲ. ಆದ್ದರಿಂದ ಅಲ್ಲಾಹನಲ್ಲಿ ಅಭಯವನ್ನು ಯಾಚಿಸಿರಿ. ಅವನು ಸರ್ವ ಶ್ರೋತೃನೂ, ಸರ್ವ ದರ್ಶಕನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(40,57,'40:57','ಖಂಡಿತವಾಗಿ ಆಕಾಶಗಳನ್ನು ಮತ್ತು ಭೂಮಿ ಯನ್ನು ಸೃಷ್ಟಿಸುವುದು ಮಾನವನ ಮರು ಸೃಷ್ಟಿಗಿಂತ ದೊಡ್ಡದಾದ ಕಾರ್ಯವಾಗಿದೆ. ಆದರೆ ಹೆಚ್ಚಿನ ಜನರು ಅರಿಯುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(40,58,'40:58','ಕುರುಡನೂ ಕಣ್ಣಿದ್ದವನೂ ಸಮಾನರಲ್ಲ. ಸತ್ಯ ವಿಶ್ವಾಸವಿರಿಸಿ ಸದಾಚಾರ ಕೈಗೊಂಡವರೂ ದುಷ್ಕರ್ಮಿಗಳೂ ಸಮಾನರಲ್ಲ. ಆದರೆ ಅವರು ಸ್ವಲ್ಪ ಮಾತ್ರವೇ ಯೋಚಿಸಿ ಅರ್ಥ ಮಾಡಿ ಕೊಳ್ಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(40,59,'40:59','ಪುನರುತ್ಥಾನದ ಘಳಿಗೆ ಖಂಡಿತ ಬರಲಿದೆ. ಅದರಲ್ಲಿ ಏನೂ ಸಂದೇಹವಿಲ್ಲ. ಆದರೆ ಹೆಚ್ಚಿನ ಜನರು ನಂಬುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(40,60,'40:60','ನಿಮ್ಮ ಪ್ರಭು ಹೀಗೆ ಹೇಳುತ್ತಾನೆ; ನನ್ನನ್ನು ಪ್ರಾರ್ಥಿಸಿರಿ. ನಾನು ನಿಮಗೆ ಉತ್ತರಿಸುವೆನು . ಅಹಂಕಾರದಿಂದ ನನ್ನ ಆರಾಧನೆಯನ್ನು ತೊರೆಯುವವರು ಯಾರೋ ಖಂಡಿತವಾಗಿಯೂ ಅವರು ಅಪಮಾನಿತರಾಗಿ ನರಕದಲ್ಲಿ ಪ್ರವೇಶಿಸಲಿರುವರು.');
INSERT INTO "kannada" VALUES(40,61,'40:61','ಅಲ್ಲಾಹನು, ನೀವು ವಿಶ್ರಾಂತಿ ಪಡೆಯಲೆಂದು ನಿಮಗೆ ರಾತ್ರಿಯನ್ನು ಒದಗಿಸಿದನು. ಹಗಲನ್ನು ಪ್ರಕಾಶಿತಗೊಳಿಸಿದನು. ಅಲ್ಲಾಹನು ಜನರ ಮೇಲೆ ಮಹಾ ಅನುಗ್ರಹದಾತನು. ಆದರೆ ಹೆಚ್ಚಿನ ಜನ ಕೃತಜ್ಞತೆ ಸಲ್ಲಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(40,62,'40:62','ಅವನೇ ನಿಮ್ಮ ಪ್ರಭು, ಸಕಲ ವಸ್ತುಗಳ ಕರ್ತೃನಾದ ಅಲ್ಲಾಹು. ಅವನ ಹೊರತು ಬೇರೆ ಆರಾಧ್ಯರಿಲ್ಲ. ಹೀಗಿರಲು ಹೇಗೆ ನೀವು ದಾರಿಗೆಡಿಸಲ್ಪಡುತ್ತಿರುವಿರಿ?');
INSERT INTO "kannada" VALUES(40,63,'40:63','ಅಲ್ಲಾಹನ ನಿದರ್ಶನಗಳನ್ನು ನಿಷೇಧಿಸುತ್ತಿದ್ದವರು ದಾರಿ ಕೆಟ್ಟಂತೆ ಇವರೂ ದಾರಿಗೆಡುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(40,64,'40:64','ಅಲ್ಲಾಹನು ಭೂಮಿಯನ್ನು ನಿಮಗೆ ನೆಲೆಯನ್ನಾಗಿಯೂ ಆಕಾಶವನ್ನು ಛಾವಣಿಯನ್ನಾಗಿ ಯೂ ಮಾಡಿದವನು. ಅವನು ನಿಮಗೆ ರೂಪ ಕೊಟ್ಟನು. ನಿಮ್ಮ ರೂಪವನ್ನು ಸುಂದರಗೊಳಿಸಿದನು. ಅವನು ನಿಮಗೆ ಶುದ್ಧ ವಸ್ತುಗಳಿಂದ ಆಹಾರ ನೀಡಿದನು. ಅವನೇ ನಿಮ್ಮ ಪ್ರಭುವಾದ ಅಲ್ಲಾಹು. ಸಕಲ ಲೋಕಗಳ ಪ್ರಭುವಾದ ಅಲ್ಲಾಹನು ಪರಮ ಸಮೃದ್ಧನು.');
INSERT INTO "kannada" VALUES(40,65,'40:65','ಅವನು ಚಿರಂಜೀವಿಯು, ಅವನ ಹೊರತು ಅನ್ಯ ಆರಾಧ್ಯರಿಲ್ಲ. ಆದ್ದರಿಂದ ನಿಮ್ಮ ಧರ್ಮವನ್ನು ಅವನಿಗೆ ಮಾತ್ರ ಮೀಸಲಾಗಿಟ್ಟು ಅವನನ್ನು ಆರಾಧಿಸಿರಿ. ಸರ್ವಸ್ತುತಿಯು ಸಕಲ ಲೋಕಗಳ ಪಾಲಕ ಪ್ರಭುವಾದ ಅಲ್ಲಾಹನಿಗೆ ಮೀಸಲು.');
INSERT INTO "kannada" VALUES(40,66,'40:66','ಸಂದೇಶವಾಹಕರೇ, ಹೇಳಿರಿ; “ನನ್ನ ಪ್ರಭುವಿನ ವತಿಯಿಂದ ನನಗೆ ಪುರಾವೆಗಳು ಬಂದಿರುವಾಗ, ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ನೀವು ಆರಾಧಿಸುತ್ತಿರುವವುಗಳಿಗೆ ನಾನು ಆರಾಧಿಸದಂತೆ ನನಗೆ ನಿಷೇಧ ವಿಧಿಸಲಾಗಿದೆ ಹಾಗೂ ಸಕಲ ಲೋಕ ಪಾಲಕನಿಗೆ ವಿಧೇಯನಾಗಬೇಕೆಂದು ನನಗೆ ಅಪ್ಪಣೆ ಕೊಡಲಾಗಿದೆ”.');
INSERT INTO "kannada" VALUES(40,67,'40:67','ಮಣ್ಣಿನಿಂದ, ತರುವಾಯ ವೀರ್ಯದಿಂದ, ಅನಂ ತರ ರಕ್ತಪಿಂಡದಿಂದ ನಿಮ್ಮನ್ನು ಸೃಷ್ಟಿಸಿದವನು ಅವನೇ. ಆಮೇಲೆ ಅವನು ನಿಮ್ಮನ್ನು ಶಿಶುವಿನ ರೂಪದಲ್ಲಿ ಹೊರತರುತ್ತಾನೆ. ಆ ಬಳಿಕ ನೀವು ನಿಮ್ಮ ಸಂಪೂರ್ಣ ಶಕ್ತಿಯನ್ನು ತಲುಪಲಿಕ್ಕಾಗಿ, ಅನಂತರ ನೀವು ವೃದ್ಧರಾಗಲಿಕ್ಕಾಗಿ ನಿಮ್ಮನ್ನು ಉಳಿಸುತ್ತಾನೆ. ನಿಮ್ಮಲ್ಲಿ ಕೆಲವರು ಇದಕ್ಕೆ ಮೊದ ಲೇ ಮರಣ ಹೊಂದುತ್ತಾರೆ. ನಿಶ್ಚಿತ ಕಾಲಾವಧಿ ಯನ್ನು ನೀವು ತಲುಪಲಿಕ್ಕಾಗಿ ಹಾಗೂ ನೀವು ಯೋಚಿಸಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(40,68,'40:68','ಅವನೇ ಜೀವಂತಗೊಳಿಸುವವನು ಮತ್ತು ಮರಣ ಕೊಡುವವನು. ಅವನು ಒಂದು ವಿಷಯ ವನ್ನು ತೀರ್ಮಾನ ಮಾಡಿದರೆ ‘ಆಗು’ ಎಂದು ಅದಕ್ಕೆ ಹೇಳುವನು. ಆಗಲೇ ಅದು ಆಗಿ ಬಿಡುವುದು.');
INSERT INTO "kannada" VALUES(40,69,'40:69','ಅಲ್ಲಾಹನ ನಿದರ್ಶನಗಳ ಬಗ್ಗೆ ತರ್ಕ ಹೂಡುವವರನ್ನು ನೀವು ನೋಡಿಲ್ಲವೇ? ಅವರನ್ನು ಸತ್ಯದಿಂದ ಹೇಗೆ ತಿರುಗಿಸಲಾಗುತ್ತಿದೆ!');
INSERT INTO "kannada" VALUES(40,70,'40:70','ವೇದ ಗ್ರಂಥವನ್ನೂ ನಮ್ಮ ಸಂದೇಶವಾಹಕರಿಗೆ ನಾವು ಯಾವುದನ್ನು ಕೊಟ್ಟು ಕಳುಹಿಸಿದ್ದೆವೋ. ಅದನ್ನೂ ಸುಳ್ಳಾಗಿಸಿದವರು ಅವರು. ಸದ್ಯವೇ ಇವರಿಗೆ ಗೊತ್ತಾಗಲಿದೆ.');
INSERT INTO "kannada" VALUES(40,71,'40:71','');
INSERT INTO "kannada" VALUES(40,72,'40:72','ಆಗ ಅವರ ಕೊರಳುಗಳಲ್ಲಿ ಬೇಡಿಗಳು ಮತ್ತು ಸಂಕೋಲೆಗಳಿದ್ದು ಅವರನ್ನು ಕುದಿಯುತ್ತಿರುವ ನರಕಾಗ್ನಿಯಲ್ಲಿಗೆ ಎಳೆದೊಯ್ಯುವ ಸಂದರ್ಭ. ತರುವಾಯ ಅವರನ್ನು ನರಕಾಗ್ನಿಯಲ್ಲಿ ಹೊತ್ತಿ ಸಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(40,73,'40:73','');
INSERT INTO "kannada" VALUES(40,74,'40:74','ಅನಂತರ ಅವರೊಡನೆ ಕೇಳಲಾಗುವುದು, ‘ನೀವು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ದೇವ ಸಹಭಾಗಿಗಳನ್ನಾಗಿ ಮಾಡಿದವರು ಎಲ್ಲಿದ್ದಾರೆ?’ ಆಗ ‘ಅವರು ಹೇಳುವರು, ಅವರು ನಮ್ಮಿಂದ ಕಳೆದು ಹೋದರು. ಅಲ್ಲ, ಇದಕ್ಕೆ ಮುಂಚೆ ಯಾವುದನ್ನೂ ನಾವು ಆರಾಧಿಸುತ್ತಿರಲಿಲ್ಲ’. ಹೀಗೆ ಸತ್ಯನಿಷೇಧಿ ಗಳನ್ನು ಅಲ್ಲಾಹನು ಮಾರ್ಗ ಭ್ರಂಶಗೊಳಿಸುವನು.');
INSERT INTO "kannada" VALUES(40,75,'40:75','(ಆಗ ಹೇಳಲಾಗುವುದು) “ನೀವು ಭೂಮಿಯಲ್ಲಿ ಅನ್ಯಾಯವಾಗಿ ಆಹ್ಲಾದಿಸುತ್ತಿದ್ದ ಹಾಗೂ ವಿಲಾಸಿಗಳಾಗಿ ಬದುಕಿದುದರ ಫಲವಿದು.');
INSERT INTO "kannada" VALUES(40,76,'40:76','ನರಕ ದ್ವಾರಗಳಲ್ಲಿ ಪ್ರವೇಶಿಸಿರಿ. ಅದರಲ್ಲಿ ನಿತ್ಯವಾಸಿ ಗಳಾಗಿ. ಅಹಂಕಾರಿಗಳ ವಾಸಸ್ಥಾನ ಎಷ್ಟು ಕೆಟ್ಟದು”.');
INSERT INTO "kannada" VALUES(40,77,'40:77','ಆದುದರಿಂದ (ಸಂದೇಶವಾಹಕರೇ,) ತಾಳ್ಮೆ ವಹಿಸಿರಿ, ಅಲ್ಲಾಹನ ವಾಗ್ದಾನವು ಪರಮ ಸತ್ಯವಾಗಿದೆ. ಇನ್ನು ನಾವು ಇವರಿಗೆ ಎಚ್ಚರಿಕೆ ನೀಡುತ್ತಿರುವ ಶಿಕ್ಷೆಗಳಲ್ಲಿ ಕೆಲವನ್ನು ಇವರು ಅನುಭವಿಸುತ್ತಿರುವುದನ್ನು ತಮಗೆ ನಾವು ತೋರಿಸಿದರೂ ಅಥವಾ (ಅದಕ್ಕೂ ಮುನ್ನ) ನಿಮ್ಮನ್ನು ನಾವು ಮರಣಗೊಳಿಸಿದರೂ ಇವರ ನಿರ್ಗಮನ ನಮ್ಮ ಕಡೆಗೇ ಇದೆ.');
INSERT INTO "kannada" VALUES(40,78,'40:78','(ಸಂದೇಶವಾಹಕರೇ,) ನಾವು ನಿಮಗಿಂತ ಮುಂಚೆ ಅನೇಕ ಸಂದೇಶವಾಹಕರನ್ನು ಕಳುಹಿಸಿದ್ದೇವೆ. ಅವರಲ್ಲಿ ಕೆಲವರ ವೃತ್ತಾಂತ ಗಳನ್ನು ನಾವು ನಿಮಗೆ ಬಿತ್ತರಿಸಿದ್ದೇವೆ. ಕೆಲವರ ವೃತ್ತಾಂತಗಳನ್ನು ಬಿತ್ತರಿಸಿಲ್ಲ. ಅಲ್ಲಾಹನ ಅನುಮತಿ ಇಲ್ಲದೆ ಯಾವುದೇ ನಿದರ್ಶನವನ್ನು ತರಲು ಯಾವ ಸಂದೇಶವಾಹಕರಿಗೂ ಸಾಧ್ಯ ವಿರಲಿಲ್ಲ. ಅಲ್ಲಾಹನ ಆಜ್ಞೆ ಬಂದರೆ ಸತ್ಯಕ್ಕನು ಸಾರ ತೀರ್ಮಾನ ಮಾಡಲಾಗುವುದು. ಆಗ ಅಸತ್ಯವಾದಿಗಳು ನಷ್ಟಕ್ಕೆ ಬೀಳುವರು.');
INSERT INTO "kannada" VALUES(40,79,'40:79','ಅಲ್ಲಾಹನು ನಿಮಗಾಗಿ ಜಾನುವಾರುಗಳನ್ನು ಉಂಟು ಮಾಡಿದನು. ಅವುಗಳ ಪೈಕಿ ಕೆಲವನ್ನು, ನೀವು ಸವಾರಿ ಮಾಡಲಿಕ್ಕಾಗಿ, ಅವುಗಳಿಂದಲೇ ನೀವು ಭಕ್ಷಿಸುವ ಸಲುವಾಗಿಯೂ.');
INSERT INTO "kannada" VALUES(40,80,'40:80','ಅವುಗಳಲ್ಲಿ ನಿಮಗೆ ಅನೇಕ ಪ್ರಯೋಜನಗಳಿವೆ. ನಿಮ್ಮ ಮನಗಳಲ್ಲಿರುವ ಅಗತ್ಯಕ್ಕೆ ಅವುಗಳ ಮೂಲಕ ನೀವು ತಲುಪಬಲ್ಲಿರಿ. ಅವುಗಳ ಬೆನ್ನ ಮೇಲೂ, ನಾವೆಗಳ ಮೇಲೂ ನಿಮ್ಮನ್ನು ಒಯ್ಯಲಾಗುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(40,81,'40:81','ಅವನು ತನ್ನ ಈ ನಿದರ್ಶನಗಳನ್ನು ನಿಮಗೆ ತೋರಿಸಿ ಕೊಡುವನು. ಆಗ ನೀವು ಅವನ ಯಾವ ನಿದರ್ಶನಗಳನ್ನು ನಿರಾಕರಿಸಬಲ್ಲಿರಿ?');
INSERT INTO "kannada" VALUES(40,82,'40:82','ಇವರು ಭೂಮಿಯಲ್ಲಿ ಸಂಚರಿಸಿಕೊಂಡು ಇವರಿಗಿಂತ ಮುಂಚೆ ಗತಿಸಿದವರ ಗತಿಯೇ ನಾಯಿ ತೆಂದು ನೋಡಲಿಲ್ಲವೇ? ಅವರು ಇವರಿಗಿಂತ ಹೆಚ್ಚಿನ ಸಂಖ್ಯೆಯಲ್ಲಿದ್ದರು, ಶಕ್ತಿ- ಸಾಮಥ್ರ್ಯ ಹಾಗೂ ಭೂಮಿಯಲ್ಲಿ ಬಿಟ್ಟು ಹೋದ ಕುರುಹುಗಳಲ್ಲಿ ಇವರಿಗಿಂತ ಹೆಚ್ಚು ಗಟ್ಟಿಗರಾ ಗಿದ್ದರು. ಅದಾಗ್ಯೂ ಅವರು ಸಂಪಾದಿಸಿದ್ದು ಅವರ ಪ್ರಯೋಜನಕ್ಕೆ ಬರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(40,83,'40:83','ಅವರ ಸಂದೇಶವಾಹಕರು ಅವರ ಬಳಿಗೆ ಪ್ರತ್ಯಕ್ಷ ಪ್ರಮಾಣಗಳನ್ನು ತಂದಾಗ ಅವರು ತಮ್ಮಲ್ಲಿದ್ದ ಜ್ಞಾನದಲ್ಲೇ ಸಂತುಷ್ಟರಾದರು. ಯಾವುದನ್ನು ಅವರು ಗೇಲಿ ಮಾಡುತ್ತಿದ್ದರೋ ಅದು ಅವರ ಮೇಲೆ ಎರಗಿತು.');
INSERT INTO "kannada" VALUES(40,84,'40:84','ಅವರು ನಮ್ಮ ಕಠಿಣ ಶಿಕ್ಷೆಯನ್ನು ಕಂಡಾಗ ಅಲ್ಲಾಹನ ಏಕತ್ವದ ಸ್ವೀಕೃತಿಯೊಂದಿಗೆ “ನಾವು ಅವನಲ್ಲಿ ವಿಶ್ವಾಸ ತಾಳಿದೆವು, ಅವನಿಗೆ ದಿವ್ಯತ್ವದಲ್ಲಿ ನಾವು ಪಾಲು ಸೇರಿಸಿದ ಸಹ ಆರಾಧ್ಯರುಗಳನ್ನು ತಿರಸ್ಕರಿಸಿದೆವು” ಎಂದರು.');
INSERT INTO "kannada" VALUES(40,85,'40:85','ಆದರೆ ನಮ್ಮ ಕಠಿಣ ಸಜೆಯನ್ನು ಕಂಡಾಗ ಅವರು ತೋರಿದ ಈ ವಿಶ್ವಾಸವು ಅವರಿಗೆ ಏನೂ ಫಲಕಾರಿಯಾಗಲಿಲ್ಲ . ತನ್ನ ದಾಸರ ಕಾರ್ಯದಲ್ಲಿ ಮೊದಲೇ ಜಾರಿಯಲ್ಲಿದ್ದ ಅಲ್ಲಾಹನ ಸಂಪ್ರದಾಯವಾಗಿತ್ತದು. ಆಗ ಸತ್ಯನಿಷೇಧಿಗಳ ನಿಶ್ಚಿತ ನಷ್ಟ ಸಾಕಾರಗೊಂಡಿತು.');
INSERT INTO "kannada" VALUES(41,1,'41:1','ಹಾ ಮೀಮ್.');
INSERT INTO "kannada" VALUES(41,2,'41:2','ಇದು ಪರಮ ದಯಾಳುವೂ, ಪರಮ ವತ್ಸಲನೂ ಆದವನ ಕಡೆಯಿಂದ ಅವತೀರ್ಣಗೊಂಡಿದೆ.');
INSERT INTO "kannada" VALUES(41,3,'41:3','ಗ್ರಹಿಸಿಕೊಳ್ಳುವ ಜನರಿಗೆ ವಚನಗಳನ್ನು ವಿಶದೀಕರಿಸಲ್ಪಟ್ಟ ಅರಬಿ ಭಾಷೆಯಲ್ಲಿರುವ ಖುರ್‍ಆನ್ ಗ್ರಂಥವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(41,4,'41:4','ಸುವಾರ್ತೆ ಹಾಗೂ ಮುನ್ನೆಚ್ಚರಿಕೆ ನೀಡುವ ಗ್ರಂಥ. ಆಗ ಇವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಇದರಿಂದ ವಿಮುಖರಾದರು. ಅವರು ಕಿವಿಗೊಡುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(41,5,'41:5','ಅವರು ಹೇಳುತ್ತಾರೆ ``ನೀವು ಯಾವುದರ ಕಡೆಗೆ ನಮ್ಮನ್ನು ಕರೆಯುತ್ತಿರುವಿರೋ ಅದರ ಬಗ್ಗೆ ನಮ್ಮ ಹೃದಯಗಳು ಮುಚ್ಚಿಹೋಗಿವೆ. ನಮ್ಮ ಕಿವಿಗಳು ಕಿವುಡಾಗಿವೆ. ನಮ್ಮ - ನಿಮ್ಮ ನಡುವೆ ಒಂದು ತೆರೆ ಬಿದ್ದಿದೆ. ಆದ್ದರಿಂದ ನೀನು ನಿನ್ನ ಧರ್ಮದಲ್ಲಿ ವರ್ತಿಸು. ನಾವು ನಮ್ಮ ಧರ್ಮದಲ್ಲಿ ವರ್ತಿಸುತ್ತೇವೆ”.');
INSERT INTO "kannada" VALUES(41,6,'41:6','(ಸಂದೇಶವಾಹಕರೇ,) ಹೇಳಿರಿ, ನಾನು ನಿಮ್ಮಂ ತೆಯೇ ಇರುವ ಒಬ್ಬ ಮನುಷ್ಯ. ನಿಮ್ಮ ಆರಾಧ್ಯ ನು ಏಕಮಾತ್ರ ಆರಾಧ್ಯನೆಂದು ನನಗೆ ದಿವ್ಯ ಸಂದೇಶ ನೀಡಲಾಗಿದೆ. ಆದುದರಿಂದ ನೀವು ಅವನ ಕಡೆಗಿರುವ ಋಜುವಾದ ದಿಕ್ಕಿನಲ್ಲೇ ನೆಲೆಯೂರಿರಿ. ಅವನಲ್ಲಿ ಕ್ಷಮೆಯಾಚಿಸಿರಿ. ಬಹುದೇವ ವಿಶ್ವಾಸಿಗಳಿಗೆ ವಿನಾಶವಿದೆ.');
INSERT INTO "kannada" VALUES(41,7,'41:7','ಅವರು ಝಕಾತ್ ಕೊಡುವುದಿಲ್ಲ. ಪರಲೋಕ ವನ್ನು ನಿಷೇಧಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(41,8,'41:8','ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಸ್ವೀಕರಿಸಿ ಸತ್ಕರ್ಮವೆಸಗುವವರು ಯಾರೋ, ಅವರಿಗೆ ಎಂದೂ ಮುರಿಯದ ಸತ್ಫಲವಿದೆ.');
INSERT INTO "kannada" VALUES(41,9,'41:9','(ಸಂದೇಶವಾಹಕರೇ) ಹೇಳಿರಿ;“ಭೂಮಿಯನ್ನು ಎರಡು ದಿನಗಳಲ್ಲಿ ಸೃಷ್ಟಿಸಿದ ದೇವನನ್ನು ನೀವು ನಿರಾಕರಿಸುತ್ತೀರಾ? ಅವನಿಗೆ ಸಮಾನರನ್ನು ಕಲ್ಪಿಸುತ್ತೀರಾ? ಅವನೇ ಸರ್ವ ಲೋಕಗಳ ಪಾಲಕ ಪ್ರಭು.');
INSERT INTO "kannada" VALUES(41,10,'41:10','ಅವನು ಭೂಮಿಯ ಮೇಲೆ ಪರ್ವತಗಳನ್ನು ನಾಟಿದನು. ಅದರಲ್ಲಿ ಅನುಗ್ರಹ ಬೀರಿದನು ಮತ್ತು ಭೂಮಿಯ ಸೃಷ್ಟಿಯ ಬಗ್ಗೆ ಅನ್ವೇಷಕರಿಗೆ ಕ್ಲಪ್ತವಾಗಿ ಉತ್ತರಿಸಬಹುದಾದ ಕ್ರಮದಲ್ಲಿ ನಾಲ್ಕು ದಿವಸಗಳೊಳಗೆ ಭೂಮಿಯಲ್ಲಿ ಅವನು ಜೀವಿಗಳಿಗೆ ಆಹಾರಗಳನ್ನು ನಿರ್ಣಯಿಸಿದನು.');
INSERT INTO "kannada" VALUES(41,11,'41:11','ಅನಂತರ ಅವನು ಆಕಾಶದ ಕಡೆಗೆ ಗಮನ ಹರಿಸಿದನು . ಅದೊಂದು ದಟ್ಟ ಧೂಮಪಟಲ ವಾಗಿತ್ತು. ಆಗ ಅವನು ಆಕಾಶ-ಭೂಮಿಗಳೊಡನೆ, ‘ನೀವು ವಿಧೇಯರಾಗಿ ಇಲ್ಲವೇ ಬಲಾತ್ಕಾರವಾಗಿ ಬನ್ನಿರಿ’ ಎಂದನು. ಅವೆರಡೂ, ‘ನಾವು ವಿಧೇಯರಾಗಿ ಬಂದಿದ್ದೇವೆ’ ಎಂದವು.');
INSERT INTO "kannada" VALUES(41,12,'41:12','ಆಗ ಅವನು ಎರಡು ದಿನಗಳಲ್ಲಿ ಸಪ್ತಗಗನ ಗಳನ್ನು ರೂಪಿಸಿದನು. ಎಲ್ಲ ಆಕಾಶಗಳಲ್ಲೂ ಅದರ ಕಾರ್ಯವನ್ನು ಬೋಧಿಸಿದನು. ಭೂಮಿಗೆ ಹತ್ತಿರದ ಆಕಾಶವನ್ನು ನಾವು ದೀಪಗಳಿಂದ ಅಲಂಕರಿಸಿದೆವು ಮತ್ತು ಅದನ್ನು ಸಂರಕ್ಷಣೆಯನ್ನಾಗಿ ಮಾಡಿದೆವು. ಅಜೇಯನೂ, ಸರ್ವಜ್ಞನೂ ಆದ ಅಲ್ಲಾಹನ ನಿರ್ಣಯವಿದು.');
INSERT INTO "kannada" VALUES(41,13,'41:13','ಇನ್ನು ಅವರು ವಿಮುಖರಾದರೆ ಹೇಳಿರಿ; ನಾನು ನಿಮ್ಮನ್ನು ಆದ್ ಮತ್ತು ಸಮೂದರಿಗೆ ಬಾಧಿಸಿದ ಉಗ್ರ ಶಿಕ್ಷೆಯಂಥ ಒಂದು ಶಿಕ್ಷೆಯ ಬಗ್ಗೆ ಎಚ್ಚರಿಸುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(41,14,'41:14','ಪ್ರವಾದಿಗಳು ಅವರ ಸಮಕಾಲೀನರಾಗಿಯೂ ಅವರ ಪೂರ್ವಕಾಲದಲ್ಲೂ ಅವರ ಬಳಿ ಬಂದು, ‘ನೀವು ಅಲ್ಲಾಹನ ಹೊರತು ಇನ್ನಾರಿಗೂ ಆರಾಧಿಸಬೇಡಿರಿ’ ಎಂದು ಹೇಳಿದಾಗ ಅವರು, ‘ನಮ್ಮ ಪ್ರಭು ಇಚ್ಛಿಸುತ್ತಿದ್ದರೆ ದೇವಚರರನ್ನು ಕಳುಹಿಸುತ್ತಿದ್ದನು. ಆದುದರಿಂದ ನಿಮ್ಮನ್ನು ಯಾವ ಸಂದೇಶದೊಂದಿಗೆ ಕಳುಹಿಸಲಾಗಿ ದೆಯೋ ಅದನ್ನು ನಾವು ನಿರಾಕರಿಸುತ್ತೇವೆ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(41,15,'41:15','ಆದ್ ಜನಾಂಗ ಅನ್ಯಾಯವಾಗಿ ಭೂಮಿಯಲ್ಲಿ ಅಹಂಕಾರ ಪಡುತ್ತಿದ್ದರು. ‘ಶಕ್ತಿಯಲ್ಲಿ ನಮ್ಮನ್ನು ಮೀರಿಸುವವರು ಯಾರಿದ್ದಾರೆ?’ ಎಂದರು. ಅವರನ್ನು ಸೃಷ್ಟಿಸಿದ ಅಲ್ಲಾಹನು ಅವರಿಗಿಂತ ಎಷ್ಟೋ ಅಧಿಕ ಶಕ್ತಿಶಾಲಿಯೆಂದು ಅವರಿಗೆ ಕಾಣ ಲಿಲ್ಲವೇ? ಅವರು ನಮ್ಮ ನಿದರ್ಶನಗಳನ್ನು ನಿರಾಕರಿಸುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(41,16,'41:16','ಕೊನೆಗೆ ಕೆಲವು ಅಶುಭ ದಿನಗಳಲ್ಲಿ ಅವರ ಮೇಲೆ ಪ್ರಚಂಡ ಶೀತ ಗಾಳಿಯನ್ನು ನಾವು ಕಳುಹಿಸಿದೆವು. ಲೌಕಿಕ ಜೀವನದಲ್ಲೇ ಅವರಿಗೆ ನಾವು ಅಪಮಾನಕರ ಶಿಕ್ಷೆಯನ್ನು ಉಣಿಸಲಿಕ್ಕಾಗಿ. ಪರಲೋಕದ ಯಾತನೆಯು ಇದಕ್ಕಿಂತಲೂ ಹೆಚ್ಚು ಕಠಿಣಕರ. ಅಲ್ಲಿ ಅವರಿಗೆ ಯಾವ ಸಹಾಯವೂ ಸಿಗದು.');
INSERT INTO "kannada" VALUES(41,17,'41:17','ಸಮೂದ್ ಗೋತ್ರಕ್ಕೆ ನಾವು ನೇರ ಮಾರ್ಗವನ್ನು ತೋರಿಸಿಕೊಟ್ಟೆವು. ಆದರೆ ಅವರು ಸನ್ಮಾರ್ಗಕ್ಕಿಂತ ಅಂಧತೆಯನ್ನು ಮೆಚ್ಚಿಕೊಂಡರು. ಕೊನೆಗೆ ಅವರ ದುಷ್ಕರ್ಮಗಳ ಫಲವಾಗಿ ಅವರಿಗೆ ಅಪಮಾನಕರವಾದ ಘೋರ ಶಿಕ್ಷೆಯೊಂದು ಬಾಧಿಸಿತು.');
INSERT INTO "kannada" VALUES(41,18,'41:18','ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಸ್ವೀಕರಿಸಿ ದೇವನಿಷ್ಟೆ ತೋರುತ್ತಿದ್ದವರನ್ನು ನಾವು ರಕ್ಷಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(41,19,'41:19','ಅಲ್ಲಾಹನ ಶತ್ರುಗಳನ್ನು ನರಕಾಗ್ನಿಯ ಕಡೆಗೆ ಒಟ್ಟುಗೂಡಿಸುವ ಮತ್ತು ಅಟ್ಟಿಸಿಕೊಂಡು ಹೋಗುವ ದಿನದ ಬಗ್ಗೆ ಯೋಚಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(41,20,'41:20','ಅವರೆಲ್ಲರೂ ಅಲ್ಲಿಗೆ ತಲಪಿದಾಗ ಅವರ ಕಿವಿಗಳೂ ಕಣ್ಣುಗಳೂ ಅವರ ಚರ್ಮಗಳೂ ಅವರು ಮಾಡುತ್ತಿದ್ದುದರ ಕುರಿತು ಅವರ ವಿರುದ್ಧ ಸಾಕ್ಷಿ ಹೇಳುವುವು.');
INSERT INTO "kannada" VALUES(41,21,'41:21','ಅವರು ತಮ್ಮ ಚರ್ಮಗಳೊಡನೆ ಹೀಗೆ ಕೇಳು ವರು,-‘ನೀವು ನಮ್ಮ ವಿರುದ್ಧ ಸಾಕ್ಷಿ ಹೇಳಿದ್ದೇಕೆ?’ ಆಗ ಅವು `ಸಕಲ ವಸ್ತುವಿಗೂ ಮಾತನಾಡುವ ಶಕ್ತಿಯನ್ನು ದಯಪಾಲಿಸಿದ ಅಲ್ಲಾಹನೇ ನಮಗೂ ಮಾತಾಡುವ ಶಕ್ತಿಯನ್ನು ನೀಡಿರುವನು. ಅವನೇ ನಿಮ್ಮನ್ನು ಪ್ರಥಮ ಬಾರಿ ಸೃಷ್ಟಿಸಿದ್ದನು. ಅವನ ಕಡೆಗೇ ನಿಮ್ಮನ್ನು ಮರಳಿಸಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(41,22,'41:22','ನಿಮ್ಮ ಕಿವಿಗಳು, ಕಣ್ಣುಗಳು ಮತ್ತು ಚರ್ಮಗಳು ನಿಮ್ಮ ವಿರುದ್ಧ ಸಾಕ್ಷಿ ಹೇಳಲಿರುವುದನ್ನು ತಪ್ಪಿಸಿ ನೀವೇನೂ (ಪಾಪಕೃತ್ಯದಿಂದ) ದೂರವಾಗಿರಲಿಲ್ಲ . ಅಲ್ಲದೆ ನೀವು ನಿಮ್ಮ ಮಿಕ್ಕ ಕರ್ಮಗಳ ಬಗ್ಗೆ ಅಲ್ಲಾಹು ಅರಿತಿಲ್ಲ ಎಂದು ಭಾವಿಸಿಕೊಂಡಿದ್ದೀರಿ.');
INSERT INTO "kannada" VALUES(41,23,'41:23','ನಿಮ್ಮ ಪ್ರಭುವಿನ ಬಗೆಗೆ ನೀವಿರಿಸಿಕೊಂಡಿದ್ದ ಭಾವನೆಯಿದು. ಇದೇ ನಿಮ್ಮನ್ನು ಕೆಡಿಸಿತು. ಇದರಿಂದ ನೀವು ಪರಾಜಿತರ ಕೂಟಕ್ಕೆ ಸೇರಿದಿರಿ’.');
INSERT INTO "kannada" VALUES(41,24,'41:24','ಇನ್ನು ಅವರು ತಾಳ್ಮೆವಹಿಸಿದರೂ ನರಕಾಗ್ನಿಯೇ ಅವರ ವಾಸಸ್ಥಳ. ಅವರು ತೃಪ್ತಿಯನ್ನು ಬಯಸಿದರೂ ಅವರಿಗೆ ತೃಪ್ತಿ ದೊರೆಯಲಾರದು.');
INSERT INTO "kannada" VALUES(41,25,'41:25','ಅವರಿಗೆ ನಾವು ಕೆಲವು ಸಂಗಾತಿಗಳನ್ನು ನಿಶ್ಚಯಿಸಿದೆವು . ಆ ಸಂಗಡಿಗರು ಅವರ ಮುಂದೆ ಮತ್ತು ಹಿಂದೆ ಇರುವ ವಿಷಯಗಳನ್ನು ಅವರಿಗೆ ಮೋಡಿ ಮಾಡಿ ತೋರಿಸಿದರು. ಅವರಿಗೆ ಮುಂಚೆ ವಿನಾಶಕ್ಕೊಳಗಾದ ಜಿನ್ನ್ ಮತ್ತು ಮನುಷ್ಯ ಸಮುದಾಯಗಳ ಗುಂಪಿನಲ್ಲಿ ನಮ್ಮ ಶಿಕ್ಷೆಯ ತೀರ್ಮಾನ ಇವರಿಗೂ ನಿಜಗೊಂಡಿತು. ಅವರು ನಷ್ಟ ಹೊಂದಿದವರಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(41,26,'41:26','‘ಈ ಖುರ್‍ಆನನ್ನು ಆಲಿಸಬೇಡಿರಿ. ಇದನ್ನು ಕೇಳಿಸಲಾದರೆ ನೀವು ಗುಲ್ಲೆಬ್ಬಿಸಿರಿ. ನೀವು ಗೆಲ್ಲುವಿರಂತೆ’ ಎಂದರು ಸತ್ಯನಿಷೇಧಿಗಳು.');
INSERT INTO "kannada" VALUES(41,27,'41:27','ಆದರೆ ನಾವು ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಘೋರ ಶಿಕ್ಷೆಯನ್ನು ಉಣಿಸಲಿದ್ದೇವೆ. ಇವರು ಮಾಡುತ್ತಿದ್ದ ಈ ದುಷ್ಕರ್ಮಗಳ ಪ್ರತಿಫಲವನ್ನು ಅತ್ಯಂತ ಅಮಾನುಷ ರೀತಿಯಲ್ಲಿ ನೀಡಲಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(41,28,'41:28','ಅಲ್ಲಾಹನ ಶತ್ರುಗಳಿಗೆ ಪ್ರತಿಫಲವಾಗಿ ಸಿಗಲಿರುವ ಕಠಿಣ ಸಜೆಯ ನರಕವದು. ಇವರ ಶಾಶ್ವತ ನೆಲೆಯು ಅದರಲ್ಲೇ ಇರುವುದು. ಅವರು ನಮ್ಮ ನಿದರ್ಶನಗಳನ್ನು ತಿರಸ್ಕರಿಸುತ್ತಿದ್ದ ಅಪರಾಧಕ್ಕೆ ಶಿಕ್ಷೆಯಿದು.');
INSERT INTO "kannada" VALUES(41,29,'41:29','ಸತ್ಯನಿಷೇಧಿಗಳು ಹೇಳುವರು, ‘ನಮ್ಮ ಪ್ರಭೂ, ಜಿನ್ನ್ ಮತ್ತು ಮಾನವರ ಪೈಕಿ ನಮ್ಮನ್ನು ದಾರಿ ತಪ್ಪಿಸಿದ ಎರಡು ವಿಭಾಗವನ್ನು ನಮಗೊಮ್ಮೆ ತೋರಿಸಿಕೊಡು. ಅವರು ತೀವ್ರ ಅಪಮಾನಿತರಾಗುವಂತೆ ನಮ್ಮ ಕಾಲಡಿಗೆ ಹಾಕಿ ನಾವು ಅವರನ್ನು ತುಳಿದು ಬಿಡುತ್ತೇವೆ’.');
INSERT INTO "kannada" VALUES(41,30,'41:30','‘ನಮ್ಮ ಪ್ರಭು ಅಲ್ಲಾಹನು’ ಎಂದು ಸಾರಿ ಆ ಬಳಿಕ ಕರ್ತವ್ಯ ಬದ್ಧರಾದವರ ಬಳಿಗೆ ದೇವಚರರು ಇಳಿದು ಬಂದು ಹೇಳುತ್ತಾರೆ. “ಹೆದರಬೇಡಿರಿ, ದುಃಖಿಸಬೇಡಿರಿ. ನಿಮಗೆ ವಾಗ್ದಾನ ಮಾಡಲಾಗಿರುವ ಸ್ವರ್ಗದ ಸುವಾರ್ತೆಯಿಂದ ಸಂತಸಗೊಳ್ಳಿರಿ.');
INSERT INTO "kannada" VALUES(41,31,'41:31','ನಾವು ಇಹ ಜೀವನದಲ್ಲೂ, ಪರಲೋಕದಲ್ಲೂ ನಿಮ್ಮ ಸಹಾಯಕರಾಗಿದ್ದೇವೆ . ಅಲ್ಲಿ ನಿಮ್ಮ ಮನಸ್ಸು ಬಯಸಿದ್ದೆಲ್ಲವೂ ನಿಮಗೆ ಸಿಗುವುದು. ನೀವು ಕೇಳುವ ಪ್ರತಿಯೊಂದು ವಸ್ತುವೂ ನಿಮಗಲ್ಲಿ ಸಿಗುವುದು.');
INSERT INTO "kannada" VALUES(41,32,'41:32','ಪರಮ ಕ್ಷಮಾಶೀಲನೂ, ಕರುಣಾವಾರಿಧಿಯೂ ಆದ ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ನಿಮಗಿರುವ ಆತಿಥ್ಯವಿದು”.');
INSERT INTO "kannada" VALUES(41,33,'41:33','ಅಲ್ಲಾಹನ ಕಡೆಗೆ ಅಹ್ವಾನಿಸಿ, ಸತ್ಕರ್ಮವೆಸಗಿ ಮತ್ತು ‘ಖಂಡಿತ ನಾನೊಬ್ಬ ಮುಸ್ಲಿಮ್’ ಎಂದು ಹೇಳುವ ವ್ಯಕ್ತಿಗಿಂತ ಶ್ರೇಷ್ಠ ಮಾತನ್ನು ಹೇಳಿದವನು ಬೇರೆ ಯಾರಿದ್ದಾನೆ? ');
INSERT INTO "kannada" VALUES(41,34,'41:34','ಒಳಿತು ಮತ್ತು ಕೆಡುಕು ಸರಿಸಮಾನವಲ್ಲ. ನೀನು ಕೆಡುಕನ್ನು ಅತ್ಯುತ್ತಮ ಒಳಿತಿನ ಮೂಲಕ ತಟ್ಟಿ ಹಾಕು. ಹಾಗಾದಾಗ ನಿನ್ನೊಂದಿಗೆ ಹಗೆತನ ಕಟ್ಟಿಕೊಂಡವನು ನಿನ್ನ ಆಪ್ತಮಿತ್ರನಾಗಿ ಮಾರ್ಪಡುವನು.');
INSERT INTO "kannada" VALUES(41,35,'41:35','ಸಹನಶೀಲರಿಗಲ್ಲದೆ ಅದು ಲಭಿಸದು. ಮಹಾ ಭಾಗ್ಯಶಾಲಿಗಳ ಹೊರತು ಇನ್ನಾರಿಗೂ ಅದು ಒಲಿಯದು.');
INSERT INTO "kannada" VALUES(41,36,'41:36','ಶೈತಾನನ ಕಡೆಯಿಂದೇನಾದರೂ ದುಷ್ಟ ಪ್ರೇರಣೆ ನಿಮ್ಮನ್ನು ದಾರಿ ತಪ್ಪಿಸಿದರೆ ನೀವು ಅಲ್ಲಾ ಹನ ಅಭಯವನ್ನು ಯಾಚಿಸಿರಿ . ಅವನು ಸರ್ವಶ್ರುತನೂ, ಸರ್ವಜ್ಞನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(41,37,'41:37','ರಾತ್ರಿ ಹಗಲೂ ಸೂರ್ಯಚಂದ್ರರೂ ಅಲ್ಲಾಹನ ನಿದರ್ಶನಗಳಲ್ಲಿ ಸೇರಿವೆ. ಸೂರ್ಯನಿಗಾಗಲಿ, ಚಂದ್ರನಿಗಾಗಲೀ ಸಾಷ್ಟಾಂಗವೆರಗಬೇಡಿರಿ. ಪರಂತು ಅವುಗಳನ್ನು ಸೃಷ್ಟಿಸಿದ ಅಲ್ಲಾಹನಿಗೇ ಸಾಷ್ಟಾಂಗವೆರಗಿರಿ. ನೀವು ನಿಜಕ್ಕೂ ಅವನನ್ನೇ ಆರಾಧಿಸುವವರಾಗಿದ್ದರೆ !');
INSERT INTO "kannada" VALUES(41,38,'41:38','ಇನ್ನು ಇವರು ಅಹಂಕರಿಸುವುದಾದರೆ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಬಳಿ ಇರುವವರು (ದೇವಚರರು) ಆಹೋರಾತ್ರಿ ಅವನನ್ನು ಸ್ತುತಿಸುವರು. ಅವರೆಂದಿಗೂ ಬಳಲುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(41,39,'41:39','ಇದು ಅಲ್ಲಾಹನ ನಿದರ್ಶನಗಳಲ್ಲೊಂದು, ಭೂಮಿಯು ಒಣಗಿ ಬಿದ್ದಿರುವುದನ್ನು ನೀವು ಕಾಣುತ್ತೀರಿ, ಆ ಬಳಿಕ ನಾವು ಅದರ ಮೇಲೆ ನೀರನ್ನು ಸುರಿದರೆ, ತಕ್ಷಣ ಅದು ಅರಳುತ್ತದೆ ಹಾಗೂ ಬೆಳೆಯುತ್ತದೆ. ಖಂಡಿತವಾಗಿಯೂ ಭೂಮಿಯನ್ನು ಹೀಗೆ ಜೀವಂತಗೊಳಿಸುವವನೇ ಮೃತರ ಪುನರುಜ್ಜೀವಕನು. ನಿಶ್ಚಯವಾಗಿಯೂ ಅವನು ಎಲ್ಲ ವಸ್ತುಗಳ ಮೇಲೆ ಸಾಮಥ್ರ್ಯವುಳ್ಳವನು.');
INSERT INTO "kannada" VALUES(41,40,'41:40','ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳ ವಿರುದ್ಧ ಅಪಾರ್ಥ ಕಟ್ಟು ವವರು ನಮ್ಮ ದೃಷ್ಟಿಯಿಂದ ಮರೆಯಾಗಿಲ್ಲ. ನರ ಕಾಗ್ನಿಗೆ ಎಸೆಯಲ್ಪಡುವವನು ಉತ್ತಮನೋ ಅಥವಾ ಪುನರುತ್ಥಾನ ದಿನ ನಿರ್ಬಿಡೆಯಿಂದ ಹಾಜರಾಗುವವನು ಉತ್ತಮನೋ? ನೀವಿಚ್ಛಿಸು ವುದನ್ನು ಮಾಡಿರಿ. ನಿಮ್ಮ ಎಲ್ಲ ಕರ್ಮಗಳನ್ನು ಅಲ್ಲಾಹು ವೀಕ್ಷಿಸುತ್ತಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(41,41,'41:41','ಬೋಧನೆಯು ತಮ್ಮ ಮುಂದೆ ಬಂದಾಗ ಅದನ್ನು ನಿಷೇಧಿಸಿದವರು ಇವರು. ಇದೊಂದು ಪ್ರತಾಪವುಳ್ಳ ಗ್ರಂಥವೇ ಸರಿ.');
INSERT INTO "kannada" VALUES(41,42,'41:42','ಇದರ ಮುಂದಿನಿಂದಾಗಲಿ ಹಿಂದಿನಿಂದಾಗಲಿ ಮಿಥ್ಯ ನುಸುಳದು. ಇದು ಯುಕ್ತಿಪೂರ್ಣನೂ ಸ್ತುತ್ಯರ್ಹನೂ ಆದ ಅಲ್ಲಾಹನಿಂದ ಅವತೀರ್ಣಗೊಂಡ ಗ್ರಂಥ.');
INSERT INTO "kannada" VALUES(41,43,'41:43','(ಸಂದೇಶವಾಹಕರೇ,) ನಿಮಗಿಂತ ಮುಂಚೆ ಗತಿ ಸಿದ ಸಂದೇಶವಾಹಕರಿಗೆ ಹೇಳದ ವಿಷಯವ ನ್ನೇನೂ ನಿಮಗೆ ಹೇಳಲಾಗುತ್ತಿಲ್ಲ. ನಿಸ್ಸಂದೇಹ ವಾಗಿಯೂ ನಿಮ್ಮ ಪ್ರಭು ಮಹಾ ಕ್ಷಮಾಶೀಲ ನೂ, ವೇದನಾಜನಕ ಶಿಕ್ಷೆಕೊಡುವವನೂ ಆಗಿ ದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(41,44,'41:44','ನಾವು ಇದನ್ನು ಅನರಬಿ ಖುರ್‍ಆನನ್ನಾಗಿ ಮಾಡಿ ಕಳುಹಿಸುತ್ತಿದ್ದರೆ ಇವರು ಹೀಗೆ ಹೇಳುತ್ತಿದ್ದರು: “ಇದರ ಸೂಕ್ತಗಳನ್ನು ಸುಸ್ಪಷ್ಟವಾಗಿ ವಿವರಿ ಸಲಾಗಿಲ್ಲವೇಕೆ? ಗ್ರಂಥ ಅನರಬಿಯೂ ದೂತರು ಅರಬರೂ ಆಗಿದ್ದೇಕೆ? ಹೇಳಿರಿ, ಸತ್ಯವಿಶ್ವಾಸ ಸ್ವೀಕರಿಸುವವರ ಪಾಲಿಗೆ ಅದು (ಖುರ್‍ಆನ್) ಮಾರ್ಗದರ್ಶನವೂ ದಿವ್ಯಔಷಧವೂ ಆಗಿದೆ. ಆದರೆ ಸತ್ಯನಿಷೇಧಿಗಳ ಕಿವಿಗಳಲ್ಲಿ ಗಂಟು ಬಿದ್ದಿದೆ. ಅದು ಅವರ ಮೇಲೆ ಕುರುಡಾಗಿದೆ. ಅವರ ಅವಸ್ಥೆಯು ಅವರನ್ನು ದೂರದಿಂದ ಕರೆಯಲಾಗುತ್ತಿದೆಯೋ ಎಂಬಂತಿದೆ.');
INSERT INTO "kannada" VALUES(41,45,'41:45','ನಾವು ಮೂಸಾರಿಗೆ ವೇದಗ್ರಂಥ ನೀಡಿದ್ದೆವು. ಆಮೇಲೆ ಅದರಲ್ಲಿ ಭಿನ್ನಮತ ತಲೆದೋರಿತು. ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ವಚನವು ಪೂರ್ವ ನಿರ್ಧರಿತವಾಗದೆ ಇರುತ್ತಿದ್ದರೆ ಅವರ ನಡುವೆ (ಈಗಲೇ) ತೀರ್ಮಾನ ಮಾಡಲಾಗುತ್ತಿತ್ತು. ನಿಜಕ್ಕೂ ಇವರು ಅದರ ಕುರಿತು ತೀವ್ರ ಸಂಶ ಯಕ್ಕೆ ಈಡಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(41,46,'41:46','ಯಾವನು ಪುಣ್ಯಕಾರ್ಯಗಳನ್ನು ಮಾಡುತ್ತಾನೋ, ಅವನು ತನ್ನ ಹಿತಕ್ಕಾಗಿಯೇ ಮಾಡುತ್ತಾನೆ. ಯಾವನು ಪಾಪ ಮಾಡುತ್ತಾನೋ ಅದು ಅವನ ಮೇಲೆಯೇ ಇರುವುದು. ನಿಮ್ಮ ಪ್ರಭು ತನ್ನ ದಾಸರ ಪಾಲಿಗೆ ಅಕ್ರಮಿಯಲ್ಲ.');
INSERT INTO "kannada" VALUES(41,47,'41:47','ಅಂತ್ಯ ಘಳಿಗೆಯ ಜ್ಞಾನವು ಅಲ್ಲಾಹನ ಕಡೆಗೇ ಮರಳುತ್ತದೆ. ಅವನ ಅರಿವಿಲ್ಲದೆ ಹಣ್ಣು ತಮ್ಮ ಮೊಗ್ಗುಗಳಿಂದ ಹೊರಬರುವುದಾಗಲಿ, ಯಾವಳೇ ಹೆಣ್ಣು ಗರ್ಭಧರಿಸುವುದಾಗಲಿ, ಹೆರುವುದಾಗಲಿ ಮಾಡುತ್ತಿಲ್ಲ. ‘ನನ್ನ ಸಹಭಾಗಿಗಳು ಎಲ್ಲಿದ್ದಾರೆ?’ ಎಂದು ಅವನು ಇವರನ್ನು ಕಡಿದು ಕೇಳುವ ದಿನ ಇವರು, `ಅದಕ್ಕೆ ಸಾಕ್ಷಿಯಾಗಿ ನಮ್ಮಲ್ಲಾರೂ ಇಲ್ಲ’ ಎಂದು ನಿನಗೆ ತಿಳಿಸುತ್ತಿದ್ದೇವೆ ಎನ್ನುವರು.');
INSERT INTO "kannada" VALUES(41,48,'41:48','ಆಗ ಈ ಹಿಂದೆ ಇವರು ಆರಾಧಿಸುತ್ತಿದ್ದ ಎಲ್ಲ ಆರಾಧ್ಯರೂ ಇವರಿಂದ ತಪ್ಪಿ ಹೋಗುವರು. ತಮಗೆ ಯಾವುದೇ ಅಭಯ ಸ್ಥಾನ ಇಲ್ಲವೆಂದು ಅವರಿಗೆ ಮನದಟ್ಟಾಗುವುದು.');
INSERT INTO "kannada" VALUES(41,49,'41:49','ಮಾನವನು ಒಳಿತಿಗಾಗಿ ಪ್ರಾರ್ಥಿಸಿ ದಣಿ ಯುವುದಿಲ್ಲ. ತನಗೇನಾದರೂ ವಿಪತ್ತು ಬಾಧಿಸಿದರೆ, ಆಗ ಅವನು ನಿರಾಶನೂ ಹತಾಶ ನೂ ಆಗಿ ಬಿಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(41,50,'41:50','ಆದರೆ ಅವನಿಗೆ ಕಷ್ಟ ಬಾಧಿಸಿದ ನಂತರ ನಾವು ಇವನಿಗೆ ನಮ್ಮ ಕೃಪೆಯ ಸವಿಯನ್ನುಣಿಸಿದಾಗ ಇವನು, “ಇದು ನನ್ನ ಹಕ್ಕು. ಪುನರುತ್ಥಾನವು ನೆಲೆಗೊಂಡೀತೆಂದು ನಾನು ಭಾವಿಸುವುದಿಲ್ಲ. ಇನ್ನು ಒಂದು ವೇಳೆ ನಾನು ನನ್ನ ಪ್ರಭುವಿನ ಕಡೆಗೆ ಮರಳಿಸಲ್ಪಟ್ಟರೂ ಅಲ್ಲೂ ನನಗೆ ಸುಖವೇ ಇರುವುದು” ಎನ್ನುತ್ತಾನೆ. ವಾಸ್ತವದಲ್ಲಿ ನಾವು ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ, ಅವರೆಸಗಿದ ಕರ್ಮದ ಬಗ್ಗೆ ಖಂಡಿತ ತಿಳಿಸುತ್ತೇವೆ. ಅಲ್ಲದೆ ನಾವು ಅವರಿಗೆ ಕರಾಳವಾದ ಶಿಕ್ಷೆಯ ಸವಿಯುಣಿಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(41,51,'41:51','ನಾವು ಮಾನವನಿಗೆ ಅನುಗ್ರಹವನ್ನು ನೀಡಿದರೆ ಅವನು ವಿಮುಖನಾಗುತ್ತಾನೆ. ಅವನ ಪಾಡಿಗೆ ಹೋಗುತ್ತಾನೆ. ಅವನಿಗೇನಾದರೂ ವಿಪತ್ತು ತಟ್ಟಿದರೆ ಬಹಳ ಹೆಚ್ಚು ಪ್ರಾರ್ಥಿಸುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(41,52,'41:52','ಹೇಳಿರಿ, “ಇದು (ಖುರ್‍ಆನ್) ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದಲೇ ಬಂದಿದ್ದರೆ ಮತ್ತೆ ನೀವು ಇದನ್ನು ನಿಷೇಧಿಸಿದರೆ, ಸತ್ಯದಿಂದ ದೂರವಾದ ವಿರೋಧದಲ್ಲಿ ಬಿದ್ದವನಿಗಿಂತ ದೊಡ್ಡ ಪಥಭ್ರಷ್ಟನು ಯಾರೆಂದು ನೀವು ಆಲೋಚಿಸಿದಿರಾ?”');
INSERT INTO "kannada" VALUES(41,53,'41:53','ಈ ಖುರ್‍ಆನ್ ನಿಜವಾಗಿಯೂ ಸತ್ಯವಾಗಿದೆಯೆಂಬುದು ಇವರಿಗೆ ವ್ಯಕ್ತವಾಗುವ ತನಕ, ದಿಗಂತಗಳಲ್ಲೂ, ಅವರೊಳಗೂ ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳನ್ನು ಅವರಿಗೆ ನಾವು ತೋರಿಸಿ ಕೊಡುವೆವು. ನಿಮ್ಮ ಪ್ರಭು ಸಕಲ ವಿಷಯಗಳಿಗೂ ಸಾಕ್ಷಿ ಎಂಬುದು ನಿಮಗೆ ಸಾಲದೇ?');
INSERT INTO "kannada" VALUES(41,54,'41:54','ತಿಳಿಯಿರಿ. ಇವರು ತಮ್ಮ ಪ್ರಭುವನ್ನು ಅಭಿ ಮುಖ ಗೊಳ್ಳಲಿರುವ ಕುರಿತು ಸಂದೇಹದಲ್ಲಿದ್ದಾರೆ. ತಿಳಿಯಿರಿ. ಅವನು ಸಕಲ ವಸ್ತುಗಳನ್ನು ಆವರಿಸಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(42,1,'42:1','ಹಾಮೀಮ್.');
INSERT INTO "kannada" VALUES(42,2,'42:2','ಐನ್ ಸೀನ್ ಖಾಫ್.');
INSERT INTO "kannada" VALUES(42,3,'42:3','(ಓ ಪೈಗಂಬರರೇ) ಪ್ರತಾಪಶಾಲಿಯೂ ಯುಕ್ತಿ ಪೂರ್ಣನೂ ಆಗಿರುವ ಅಲ್ಲಾಹು ನಿಮಗೂ ನಿಮಗಿಂತ ಮುಂಚಿನವರಿಗೂ ಇದೇ ರೀತಿಯಲ್ಲಿ ವಹ್ಯ್ (ಸಂದೇಶ) ಕಳುಹಿಸುತ್ತಲಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(42,4,'42:4','ಆಕಾಶಗಳಲ್ಲಿರುವ ಮತ್ತು ಭೂಮಿಯಲ್ಲಿರುವ ಪ್ರತಿಯೊಂದು ವಸ್ತುವೂ ಅವನದೇ. ಅವನು ಪರಮೋನ್ನತನೂ ಮಹಾನನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(42,5,'42:5','ಆಕಾಶಗಳು ಅವುಗಳ ಮೇಲ್ಗಡೆಯಿಂದ ಸಿಡಿದು ಬೀಳುವುದರಲ್ಲಿದೆ . ದೇವಚರರು (ಮಲಕ್‍ಗಳು) ತಮ್ಮ ಪ್ರಭುವಿನ ಸ್ತುತಿಯೊಂದಿಗೆ ಅವನ ಪರಿಶು ದ್ಧತೆಯನ್ನು ಪ್ರಕೀರ್ತಿಸುತ್ತಿದ್ದಾರೆ ಮತ್ತು ಭೂವಾಸಿಗಳಿಗಾಗಿ ಪಾಪ ಮುಕ್ತಿಯನ್ನು ಬೇಡು ತ್ತಿರುತ್ತಾರೆ. ತಿಳಿಯಿರಿ, ನಿಜಕ್ಕೂ ಅಲ್ಲಾಹನು ಮಹಾ ಕ್ಷಮಾಶೀಲನೂ ಕೃಪಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(42,6,'42:6','ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಇತರರನ್ನು ತಮ್ಮ ರಕ್ಷಕರಾಗಿ ಮಾಡಿಕೊಂಡಿರುವವರ ಮೇಲ್ವಿಚಾರಕನು ಅಲ್ಲಾಹನೇ ಆಗಿರುತ್ತಾನೆ. ನೀವು ಅವರ ಮೇಲೆ ಹೊಣೆಗಾರರಲ್ಲ.');
INSERT INTO "kannada" VALUES(42,7,'42:7','(ಪ್ರವಾದಿಯರೇ,) ಇದೇ ರೀತಿಯಲ್ಲಿ ಖುರ್‍ಆ ನನ್ನು ನಾವು ನಿಮ್ಮ ಕಡೆಗೆ ಅರಬಿ ಭಾಷೆಯಲ್ಲಿ ವಹ್ಯ್ ಮಾಡಿರುತ್ತೇವೆ. ನಾಡುಗಳ ಕೇಂದ್ರ ಮಕ್ಕಾ ಹಾಗೂ ಅದರ ಸುತ್ತಮುತ್ತ ಇರುವವರನ್ನು ನೀವು ಎಚ್ಚರಿಸುವಂತಾಗಲು ಮತ್ತು ಸರ್ವರನ್ನು ಒಗ್ಗೂಡಿಸುವ ಯಾವುದೇ ಸಂಶಯವಿಲ್ಲದ ಒಂದು ದಿನದ ಬಗ್ಗೆ ಎಚ್ಚರಿಕೆ ನೀಡುವಂತಾಗಲು. ಅಂದು ಒಂದು ಕೂಟವು ಸ್ವರ್ಗಕ್ಕೆ ಇನ್ನೊಂದು ಕೂಟವು ನರಕಕ್ಕೆ ಹೋಗಲಿಕ್ಕಿದೆ.');
INSERT INTO "kannada" VALUES(42,8,'42:8','ಅಲ್ಲಾಹನು ಬಯಸುತ್ತಿದ್ದರೆ ಇವರೆಲ್ಲರನ್ನೂ ಒಂದೇ ಸಮುದಾಯವನ್ನಾಗಿ ಮಾಡುತ್ತಿದ್ದನು. ಆದರೆ ಅವನು ತನಗೆ ಇಷ್ಟವಿದ್ದವರನ್ನು ತನ್ನ ಕೃಪೆಯೊಳಗೆ ಸೇರಿಸಿಕೊಳ್ಳುತ್ತಾನೆ ಮತ್ತು ಅಕ್ರಮಿಗಳಿಗೆ ಯಾವುದೇ ರಕ್ಷಕ ಮಿತ್ರನಾಗಲಿ ಸಹಾಯಕನಾಗಲಿ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(42,9,'42:9','ಇವರು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಇತರರನ್ನು ರಕ್ಷಕ (ಆರಾಧ್ಯ)ರನ್ನಾಗಿ ಸ್ವೀಕರಿಸಿಕೊಳ್ಳುವರೇ? ಅಲ್ಲಾಹನೇ ರಕ್ಷಕ ಪ್ರಭು. ಅವನೇ ಮೃತರನ್ನು ಜೀವಂತಗೊಳಿಸುವವನು. ಮತ್ತು ಸಕಲ ವಿಷಯಗಳಿಗೂ ಸಾಮಥ್ರ್ಯವುಳ್ಳವನು.');
INSERT INTO "kannada" VALUES(42,10,'42:10','ನಿಮ್ಮೊಳಗೆ ಯಾವ ವಿಷಯದಲ್ಲೇ ಭಿನ್ನಮತವುಂ ಟಾದರೂ ಅದರ ತೀರ್ಮಾನ ಅಲ್ಲಾಹನಿಂದಲೇ ನಡೆಯುತ್ತದೆ . ಅವನೇ ನನ್ನ ಪ್ರಭುವಾದ ಅಲ್ಲಾಹು. ನಾನು ಅವನ ಮೇಲೆಯೇ ಭರವಸೆಯನ್ನಿರಿಸಿದ್ದೇನೆ ಮತ್ತು ಅವನ ಕಡೆಗೇ ನಾನು ಮರಳುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(42,11,'42:11','ಅವನು ಆಕಾಶಗಳ ಹಾಗೂ ಭೂಮಿಯ ಸೃಷ್ಟಿ ಕರ್ತ. ಅವನು ನಿಮ್ಮ ವರ್ಗದಿಂದಲೇ ನಿಮಗಾಗಿ ಜೋಡಿಗಳನ್ನು ಸೃಷ್ಟಿಸಿದನು, ಹಾಗೆಯೇ ಪ್ರಾಣಿ ಗಳಲ್ಲೂ ಜೋಡಿಗಳನ್ನು ಉಂಟು ಮಾಡಿದನು. ಈ ಮೂಲಕ ಅವನು ನಿಮ್ಮನ್ನು ಹೆಚ್ಚಿಸುತ್ತಾನೆ . ಯಾವ ವಸ್ತುವೂ ಅವನಂತೆ ಇಲ್ಲ. ಅವನು ಎಲ್ಲ ವನ್ನು ಆಲಿಸುವವನೂ ವೀಕ್ಷಿಸುವವನೂ ಆಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(42,12,'42:12','ಭೂಮಿ-ಆಕಾಶಗಳ (ಭಂಡಾರಗಳ) ಬೀಗದ ಕೈಗಳು ಅವನ ಅಧೀನದಲ್ಲಿವೆ. ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಅವನು ಜೀವನಾಧಾರವನ್ನು ವಿಶಾಲಗೊಳಿಸುತ್ತಾನೆ. ಮತ್ತು ಬೇರೆ ಕೆಲವರಿಗೆ ಪರಿಮಿತಗೊಳಿಸುತ್ತಾನೆ. ಖಂಡಿತಾ ಅವನು ಸಕಲ ವಿಷಯಗಳ ಜ್ಞಾನಿ.');
INSERT INTO "kannada" VALUES(42,13,'42:13','ನೂಹ್‍ರಿಗೆ ಅಜ್ಞಾಪಿಸಿದ್ದ ಈಗ ನಾವು ನಿಮಗೆ ದಿವ್ಯ ಸಂದೇಶ ಕಳುಹಿಸಿದ, ಇಬ್‍ರಾಹೀಂ ಮೂಸಾ ಮತ್ತು ಈಸಾರಿಗೂ ನಾವು ಬೋಧಿಸಿದ ಅಂದರೆ, ಈ ಧರ್ಮವನ್ನು ಸಂಸ್ಥಾಪಿಸಿರಿ ಮತ್ತು ಇದರಲ್ಲಿ ವಿಭಿನ್ನರಾಗದಿರಿ ಎಂಬ ಆದೇಶವನ್ನು ಅವನು ನಿಮಗೆ ಧರ್ಮಶಾಸ್ತ್ರವಾಗಿ ನಿಶ್ಚಯಿಸಿದ್ದಾನೆ . ಬಹುದೇವವಿಶ್ವಾಸಿಗಳನ್ನು ತಾವು ಯಾವೊಂದಕ್ಕೆ ಆಹ್ವಾನಿಸುತ್ತೀರೋ ಅದು ಅವರಿಗೆ ದೊಡ್ಡ ಹೊರೆಯಾಗಿದೆ. ಅಲ್ಲಾಹನು ತಾನಿಚ್ಛಿಸುವವರನ್ನು ತನ್ನ ಕಡೆಗೆ ಆರಿಸಿಕೊಳ್ಳುತ್ತಾನೆ ಮತ್ತು ತನ್ನತ್ತ ಮರಳುವವರನ್ನು ಸರಿಯಾದ ಮಾರ್ಗಕ್ಕೆ ಕೊಂಡೊಯ್ಯುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(42,14,'42:14','ಅವರು (ಪೂರ್ವ ವೈದಿಕರು) ಭಿನ್ನಾಭಿಪ್ರಾಯ ಕ್ಕೊಳಗಾಗಿರುವುದು ಅವರ ಬಳಿಗೆ ಜ್ಞಾನ ಬಂದ ಬಳಿಕವಾಗಿತ್ತು. ಅವರ ನಡುವಣ ವೈರಾಗ್ಯದಿಂದ. ಒಂದು ನಿಶ್ಚಿತ ಸಮಯದವರೆಗೆ ವಿಧಿಸಲಾದ ಒಂದು ವಚನವನ್ನು ನಿಮ್ಮ ಪ್ರಭು ಮೊದಲೇ ಸಾದರಗೊಳಿಸದಿರುತ್ತಿದ್ದರೆ (ತಕ್ಷಣ) ಅವರಿಗೆ ಶಿಕ್ಷೆಯ ತೀರ್ಮಾನ ಮಾಡಲಾಗುತ್ತಿತ್ತು. ಅವರ ಬಳಿಕ ವೇದಗ್ರಂಥ ವಾರೀಸಾಗಿ ಲಭಿಸಲ್ಪಟ್ಟವರು ಅವುಗಳ ಕುರಿತು ಅತೀವ ಅನುಮಾನದಲ್ಲಿ ಬಿದ್ದಿರುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(42,15,'42:15','(ಓ ಪೈಗಂಬರರೇ,) ಹೀಗಾಗಿ ನೀವು (ಅದೇ ಧರ್ಮದ ಕಡೆಗೆ) ಕರೆ ನೀಡಿರಿ ಮತ್ತು ನಿಮಗೆ ಆಜ್ಞಾಪಿಸಲಾಗಿರುವ ಪ್ರಕಾರ ಅದರಲ್ಲೇ ಸ್ಥಿರ ವಾಗಿರಿ. ಅವರ ಸ್ವೇಚ್ಛೆಗಳನ್ನು ಅನುಸರಿಸ ಬೇಡಿರಿ. ಮತ್ತು (ಅವರೊಡನೆ) ಹೇಳಿರಿ; ಅಲ್ಲಾ ಹನು ಅವತೀರ್ಣಗೊಳಿಸಿದ ಪ್ರತಿಯೊಂದು ಗ್ರಂಥದ ಮೇಲೆ ನಾನು ವಿಶ್ವಾಸವಿರಿಸಿದೆನು. ನಿಮ್ಮ ನಡುವೆ ನ್ಯಾಯಪಾಲಿಸಬೇಕೆಂದು ನನಗೆ ಆದೇ ಶಿಸಲಾಗಿದೆ. ಅಲ್ಲಾಹನು ನಮ್ಮ ಮತ್ತು ನಿಮ್ಮ ಪ್ರಭುವೂ ಆಗಿರುತ್ತಾನೆ. ನಮ್ಮ ಕರ್ಮ (ಫಲ) ಗಳು ನಮಗೆ, ನಿಮ್ಮ ಕರ್ಮ(ಫಲ)ಗಳು ನಿಮಗೆ, ನಮ್ಮ ನಿಮ್ಮೊಳಗೆ ಯಾವ ವಿವಾದವೂ ಇಲ್ಲ . ಅಲ್ಲಾಹನು ನಮ್ಮೆಲ್ಲರನ್ನು ಒಟ್ಟುಗೂಡಿಸುವನು ಮತ್ತು ಎಲ್ಲರ ಮರಳಿಕೆ ಅವನ ಕಡೆಗೇ ಇದೆ.');
INSERT INTO "kannada" VALUES(42,16,'42:16','ಅಲ್ಲಾಹನ ಕರೆಗೆ (ಧರ್ಮಕ್ಕೆ) ಓಗೊಟ್ಟ ಬಳಿಕ ಆ ಕರೆಯ ಕುರಿತು ತರ್ಕಿಸುವವರ ಪುರಾವೆಗಳು ಅವರ ಪ್ರಭುವಿನ ಬಳಿ ನಿರರ್ಥಕವಾಗಿದೆ. ಅವರ ಮೇಲೆ ಅವನ ಪ್ರಕೋಪವೂ ಅವರಿಗೆ ಕಠಿಣ ಶಿಕ್ಷೆಯೂ ಇದೆ.');
INSERT INTO "kannada" VALUES(42,17,'42:17','ಸತ್ಯ ಸಮೇತ ಗ್ರಂಥವನ್ನೂ ತಕ್ಕಡಿ (ನೀತಿ) ಯನ್ನೂ ಇಳಿಸಿದವನು ಅಲ್ಲಾಹನೇ. ತಮಗೇನು ಗೊತ್ತು? (ಅಂತ್ಯ) ಘಳಿಗೆಯು ಹತ್ತಿರ ಬಂದಿರಲೂಬಹುದು.');
INSERT INTO "kannada" VALUES(42,18,'42:18','ಅದರಲ್ಲಿ (ಅಂತ್ಯ ಸಮಯದಲ್ಲಿ) ನಂಬಿಕೆ ಇಲ್ಲದವರು ಅದಕ್ಕಾಗಿ ದುಡುಕುತ್ತಿದ್ದಾರೆ. ಅದರಲ್ಲಿ ನಂಬಿಕೆಯುಳ್ಳವರು ಅದನ್ನು ಭಯಪಡು ತ್ತಾರೆ ಮತ್ತು ಅದು ಸತ್ಯ ಎಂಬುದನ್ನು ಅರಿತಿದ್ದಾರೆ. ತಿಳಿಯಿರಿ; ಅಂತ್ಯ ದಿನದ ಕುರಿತು ತರ್ಕಿಸುವವರು ಬಹುದೂರವಾದ ಪಥಭ್ರಷ್ಟತೆಯಲ್ಲಿ ಬಿದ್ದಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(42,19,'42:19','ಅಲ್ಲಾಹನು ತನ್ನ ದಾಸರಲ್ಲಿ ಅತ್ಯಂತ ಕರುಣೆಯುಳ್ಳವನಾಗಿದ್ದಾನೆ. ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಅವನು ಆಹಾರ ನೀಡುತ್ತಾನೆ. ಅವನು ಅತಿ ಪ್ರಬಲನೂ, ಪ್ರತಾಪಶಾಲಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(42,20,'42:20','ಯಾವನು (ತನ್ನ ಕರ್ಮದಿಂದ) ಪರಲೋಕದ ಬೆಳೆಯನ್ನು ಬಯಸುತ್ತಾನೋ ಅವನ ಬೆಳೆಯಲ್ಲಿ ನಾವು ವರ್ಧನೆ ಉಂಟುಮಾಡುತ್ತೇವೆ. ಯಾವನು ಇಹಲೋಕದ ಬೆಳೆಯನ್ನು ಬಯಸುತ್ತಾನೋ ಅವನಿಗೆ ನಾವು ಅದರಿಂದಲೇ ಕೊಟ್ಟುಬಿಡುತ್ತೇವೆ. ಆದರೆ ಪರಲೋಕದಲ್ಲಿ ಅವನಿಗೆ ಯಾವ ಪಾಲೂ ಇರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(42,21,'42:21','ಅವರಿಗೆ ಅಲ್ಲಾಹು ಅನುಮತಿಸದ ಕಾರ್ಯವನ್ನು ಧರ್ಮವಾಗಿ ನಿರ್ಣಯಿಸಿಕೊಟ್ಟ ಸಹಭಾಗಿಗಳು ಅವರಿಗೆ ಇದ್ದಾರೆಯೇ? ನಿರ್ಣಾಯಕ ವಿಧಿಯ ಕುರಿತು ಆಜ್ಞೆಯು ಅಸ್ತಿತ್ವದಲ್ಲಿರದಿರುತ್ತಿದ್ದರೆ ಇವರ ಮಧ್ಯೆ ತಕ್ಷಣ ತೀರ್ಮಾನ ಮಾಡಿ ಬಿಡಲಾಗುತ್ತಿತ್ತು. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಕ್ರಮಿಗಳಿಗೆ ವೇದ ನಾಜನಕ ಶಿಕ್ಷೆಯಿದೆ.');
INSERT INTO "kannada" VALUES(42,22,'42:22','(ಪರಲೋಕದಲ್ಲಿ) ಈ ಅಕ್ರಮಿಗಳು ತಮ್ಮ ಸಂಪಾದನೆಯ ಪರಿಣಾಮದಿಂದ ಭಯಪಡುವುದನ್ನು ತಾವು ಕಾಣುವಿರಿ. ಆ ಶಿಕ್ಷೆ ಅವರ ಮೇಲೆ ಸಂಭವಿಸಿಯೇ ತೀರುವುದು. ಸತ್ಯವಿಶ್ವಾಸ ಸ್ವೀಕರಿಸಿ ಸತ್ಕರ್ಮಗಳನ್ನು ಅನುಷ್ಠಿಸಿದವರು ಸ್ವರ್ಗೋದ್ಯಾನದಲ್ಲಿರುವರು. ತಮ್ಮ ಪ್ರಭುವಿನಿಂದ ಅವರು ಬಯಸಿದುದನ್ನೆಲ್ಲಾ ಪಡೆಯು ವರು. ಅದುವೇ ಮಹಾ ಅನುಗ್ರಹವಾಗಿದೆ .');
INSERT INTO "kannada" VALUES(42,23,'42:23','ಇದು ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಸ್ವೀಕರಿಸಿ ಸತ್ಕರ್ಮ ನಿರ್ವಹಿಸಿದಂತಹ ತನ್ನ ದಾಸರಿಗೆ ಅಲ್ಲಾಹು ನೀಡುತ್ತಿರುವ ಸುವಾರ್ತೆಯಾಗಿದೆ. (ಪ್ರವಾದಿ ಯರೇ!) ತಾವು ಹೇಳಿರಿ; ನಾನು ಈ ಕಾರ್ಯ ಕ್ಕಾಗಿ ನಿಮ್ಮಿಂದ ಯಾವುದೇ ಪ್ರತಿಫಲವನ್ನು ಅಪೇಕ್ಷಿಸುವುದಿಲ್ಲ. (ಬದಲಾಗಿ) ಸಾಮಿಪ್ಯ ಬಾಂ ಧವ್ಯದ ಪ್ರೀತಿಯನ್ನು ಖಂಡಿತ ಅಪೇಕ್ಷಿಸುತ್ತೇನೆ . ಯಾವನಾದರೂ ಒಂದು ಒಳಿತನ್ನು ಗಳಿಸಿದರೆ ಅದರಲ್ಲಿ ನಾವು ಅವನಿಗೆ ಗುಣವನ್ನು ವರ್ಧಿಸಿಕೊಡುವೆವು. ಖಂಡಿತ, ಅಲ್ಲಾಹು ಕ್ಷಮಾಶೀಲನೂ ಹಿರಿಯ ಕೃತಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(42,24,'42:24','ಈ ವ್ಯಕ್ತಿ ಅಲ್ಲಾಹನ ಹೆಸರಲ್ಲಿ ಸುಳ್ಳಾರೋಪ ಹೊರಿಸುತ್ತಾನೆಂದು ಅವರು ಹೇಳುತ್ತಾರೆಯೆ? ಅಲ್ಲಾಹು ಇಚ್ಛಿಸಿದರೆ ನಿಮ್ಮ ಹೃದಯಕ್ಕೆ ಮುದ್ರೆಯೊತ್ತ ಬಹುದಿತ್ತು. ಅವನು ಮಿಥ್ಯವನ್ನು ಅಳಿಸಿ ಬಿಡುತ್ತಾನೆ. ಸತ್ಯವನ್ನು ತನ್ನ ವಚನಗಳ ಮೂಲಕ ಸ್ಥಿರಪಡಿಸಿ ತೋರಿಸುತ್ತಾನೆ. ಖಂಡಿತ, ಹೃದಯಗಳಲ್ಲಿ ಅವಿತಿರುವುದನ್ನು ಅವನು ಸ್ಪಷ್ಟವಾಗಿ ಅರಿಯುವವನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(42,25,'42:25','ತನ್ನ ದಾಸರಿಂದ ಪಶ್ಚಾತ್ತಾಪ ಸ್ವೀಕರಿಸುವವನು ಮತ್ತು ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸುವವನು ಅವನೇ. ವಸ್ತುತಃ ನಿಮ್ಮ ಕರ್ಮಗಳ ಅರಿವು ಅವನಿಗಿದೆ.');
INSERT INTO "kannada" VALUES(42,26,'42:26','ಅವನು ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಹಾಗೂ ಸತ್ಕರ್ಮಿಗಳ ಪ್ರಾರ್ಥನೆ ಸ್ವೀಕರಿಸುತ್ತಾನೆ ಮತ್ತು ಅವರಿಗೆ ತನ್ನ ಅನುಗ್ರಹದಿಂದ ಇನ್ನಷ್ಟು ಹೆಚ್ಚು ನೀಡುತ್ತಾನೆ. ಇನ್ನು ಸತ್ಯನಿಷೇಧಿಗಳ ವಿಷಯ. ಅವರಿಗಂತೂ ಘೋರ ಯಾತನೆ ಕಾದಿದೆ.');
INSERT INTO "kannada" VALUES(42,27,'42:27','ಅಲ್ಲಾಹನು ತನ್ನ ಎಲ್ಲ ದಾಸರಿಗೂ ವಿಶಾಲ ಜೀವ ನಾಧಾರ ಕೊಟ್ಟು ಬಿಡುತ್ತಿದ್ದರೆ, ಅವರು ಭೂಮಿಯಲ್ಲಿ ಅನ್ಯಾಯವೆಸಗುತ್ತಿದ್ದರು. ಆದರೆ ಅವನು ಒಂದು ಪ್ರಮಾಣಕ್ಕನುಸಾರವಾಗಿ ತಾನಿಚ್ಛಿಸಿದಷ್ಟು ಇಳಿಸುತ್ತಾನೆ. ಖಂಡಿತ ವಾಗಿಯೂ ಅವನು ತನ್ನ ದಾಸರ ಬಗ್ಗೆ ತಿಳಿದಿರು ವವನೂ ಅವರನ್ನು ನೋಡುತ್ತಿರುವವನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(42,28,'42:28','ಜನರು ನಿರಾಶರಾಗಿ ಬಿಟ್ಟ ಬಳಿಕ ಮಳೆಗರೆಯು ವವನೂ ತನ್ನ ಕೃಪೆಯನ್ನು ಹರಡುವವನೂ ಅವನೇ. ಪ್ರಶಂಸಾರ್ಹನಾದ ರಕ್ಷಕ ಮಿತ್ರನೂ ಅವನೇ.');
INSERT INTO "kannada" VALUES(42,29,'42:29','ಆಕಾಶಗಳು ಮತ್ತು ಭೂಮಿಯ ಸೃಷ್ಟಿಯೂ ಅವನು ಇವೆರಡರಲ್ಲಿ ಹರಡಿಸಿರುವ ಈ ಜೀವಿಗಳೂ ಅವನ ನಿದರ್ಶನಗಳಿಗೆ ಸೇರಿದ್ದು. ತಾನಿಚ್ಛಿಸಿದಾಗ ಅವನು ಅವುಗಳನ್ನು ಒಟ್ಟು ಗೂಡಿಸಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(42,30,'42:30','ನಿಮಗೆ ಒದಗಿರುವ ಯಾವುದೇ ಸಂಕಷ್ಟವು ನಿಮ್ಮ ಕೈಗಳ ಗಳಿಕೆಯಿಂದಲೇ ಆಗಿದೆ. ಅನೇಕ ಪಾಪಗಳನ್ನು ಅವನು ಹಾಗೆಯೇ ಕ್ಷಮಿಸಿ ಬಿಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(42,31,'42:31','ನೀವು ಭೂಮಿಯಲ್ಲಿ ಅವನನ್ನು ಮಣಿಸಲಾರಿರಿ. ಮತ್ತು ನಿಮಗೆ ಅಲ್ಲಾಹನ ವಿರುದ್ಧ ಯಾವುದೇ ರಕ್ಷಕನಾಗಲಿ ಸಹಾಯಕನಾಗಲಿ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(42,32,'42:32','ಸಮುದ್ರದಲ್ಲಿ ಚಲಿಸುವ ಬೆಟ್ಟಗಳಂಥ ಹಡಗುಗಳು ಅವನ ನಿದರ್ಶನಕ್ಕೆ ಸೇರಿದೆ.');
INSERT INTO "kannada" VALUES(42,33,'42:33','ಅಲ್ಲಾಹನು ತಾನಿಚ್ಛಿಸಿದಾಗ ಗಾಳಿಯನ್ನು ಸ್ತಬ್ದಗೊಳಿಸಬಲ್ಲನು. ಆಗ ಗಾಳಿಯು ಸಮುದ್ರದ ಮೇಲೆ ನಿಂತಲ್ಲೇ ನಿಂತು ಬಿಡುವುದು. ತೀವ್ರ ಸಹನಶೀಲ ಹಾಗೂ ತೀವ್ರ ಕೃತಜ್ಞನಿಗೆ ಇದರಲ್ಲಿ ದೊಡ್ಡ ನಿದರ್ಶನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(42,34,'42:34','ಅಥವಾ ಅವರ ಕೆಲವೊಂದು ದುಷ್ಕøತ್ಯಗಳ ಫಲವಾಗಿ ಅವರನ್ನು ಮುಳುಗಿಸಿ ಬಿಡಲೂಬ ಹುದು. ಅವರ ಪೈಕಿ ಹೆಚ್ಚಿನವರಿಗೆ ಕ್ಷಮಿಸಲೂಬಹುದು.');
INSERT INTO "kannada" VALUES(42,35,'42:35','ಆಗ ನಮ್ಮ ನಿದರ್ಶನಗಳ ಕುರಿತು ಜಗಳಾಡುವವರಿಗೆ, ತಮಗೆಲ್ಲೂ ಅಭಯ ಸ್ಥಾನವಿಲ್ಲವೆಂದು ತಿಳಿದು ಬರುವುದು.');
INSERT INTO "kannada" VALUES(42,36,'42:36','ನಿಮಗೆ ನೀಡಲಾಗಿರುವ ಪ್ರತಿಯೊಂದು ವಸ್ತುವು ಕೇವಲ ಇಹಲೋಕದ ನಶ್ವರ ಜೀವನ ಸಾಧನಗಳಾಗಿವೆ. ಅಲ್ಲಾಹನ ಬಳಿಯಲ್ಲಿರುವುದೆಲ್ಲ ಉತ್ತಮವೂ ಶಾಶ್ವತವೂ ಆಗಿರುತ್ತದೆ. ಅದು ಇರುವುದು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗಾಗಿ ಮತ್ತು ತಮ್ಮ ಪ್ರಭುವಿನ ಮೇಲೆ ಭರವಸೆ ಇಡುವವರಿಗಾಗಿ.');
INSERT INTO "kannada" VALUES(42,37,'42:37','ಅವರು ದೊಡ್ಡ ದೊಡ್ಡ ಪಾಪಗಳಿಂದ ಮತ್ತು ಅಶ್ಲೀಲ ಕಾರ್ಯಗಳಿಂದ ದೂರವಿರುವವರೂ ಸಿಟ್ಟು ಬಂದಾಗ ಕ್ಷಮಿಸಿ ಬಿಡುವವರೂ ಆಗಿರುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(42,38,'42:38','ಅವರು ತಮ್ಮ ಪ್ರಭುವಿನ ಆಜ್ಞೆಗಳನ್ನು ಪಾಲಿ ಸುತ್ತಾರೆ, ನಮಾಝನ್ನು ಸಂಸ್ಥಾಪಿಸುತ್ತಾರೆ, ತಮ್ಮ ವ್ಯವಹಾರಗಳನ್ನು ಪರಸ್ಪರ ಸಮಾ ಲೋಚನೆಯಿಂದ ನಡೆಸುತ್ತಾರೆ, ನಾವು ಅವರಿಗೆ ಕೊಟ್ಟಿರುವ ಜೀವನಾಧಾರಗಳಿಂದ ಖರ್ಚು ಮಾಡುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(42,39,'42:39','ಮತ್ತು ಅವರಿಗೇನಾದರೂ ಅತಿರೇಕ ಸಂಭವಿಸಿದರೆ ಅದನ್ನು ಪ್ರತಿರೋಧಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(42,40,'42:40','ಕೆಡುಕಿಗೆ ಅದಕ್ಕೆ ಸಮಾನವಾದ ಕೆಡುಕು ಪ್ರತಿಕ್ರಮವಾಗಿರುತ್ತದೆ. ಇನ್ನು ಯಾರಾದರೂ ಕ್ಷಮಿಸಿಬಿಟ್ಟರೆ ಹಾಗೂ ಸುಧಾರಿಸಿಕೊಂಡರೆ ಅವನ ಪ್ರತಿಫಲದ ಹೊಣೆ ಅಲ್ಲಾಹನ ಮೇಲಿದೆ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅವನು ಅಕ್ರಮಿಗಳನ್ನು ಮೆಚ್ಚುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(42,41,'42:41','ಯಾರು ತಾನು ಅಕ್ರಮಕ್ಕೊಳಗಾದ ಬಳಿಕ ಪ್ರತೀಕಾರ ಕ್ರಮ ಕೈಗೊಂಡರೆ ಅವರ ವಿರುದ್ಧ ಕ್ರಮ ಕೈಗೊಳ್ಳಲು ಯಾವ ದಾರಿಯೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(42,42,'42:42','ವಾಸ್ತವದಲ್ಲಿ ಇತರರ ಮೇಲೆ ನ್ಯಾಯವಿಲ್ಲದೆ ಅಕ್ರಮವೆಸಗುವವರು ಮತ್ತು ಭೂಮಿಯಲ್ಲಿ ಅತಿರೇಕವೆಸಗುವವರ ಮೇಲೆ ಮಾತ್ರ ಪ್ರತಿಕ್ರಮಕ್ಕೆ ದಾರಿಯಿದೆ. ಅಂತಹವರಿಗೆ ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆ ಇದೆ.');
INSERT INTO "kannada" VALUES(42,43,'42:43','ಆದರೆ ಯಾರಾದರೂ ಸಹನೆಯಿಂದ ವರ್ತಿಸಿದರೆ ಹಾಗೂ ಕ್ಷಮಿಸಿದರೆ ಇದೊಂದು ಮನೋದಾಡ್ರ್ಯತೆಯ ಕಾರ್ಯಗಳಲ್ಲಾಗುವುದು .');
INSERT INTO "kannada" VALUES(42,44,'42:44','ಯಾರನ್ನು ಅಲ್ಲಾಹನು ಪಥಭ್ರಷ್ಟಗೊಳಿಸಿ ಬಿಡುವನೋ ಆ ಬಳಿಕ ಅವನನ್ನು ರಕ್ಷಿಸಬಲ್ಲವರು ಯಾರೂ ಇಲ್ಲ. ಅಕ್ರಮಿಗಳು ಶಿಕ್ಷೆಯನ್ನು ಕಂಡಾಗ, ‘ಇನ್ನು (ಇಹಲೋಕಕ್ಕೆ) ಮರಳಿ ಹೋಗಲು ದಾರಿಯೇನಾದರೂ ಇದೆಯೇ?’ ಎಂದು ಹೇಳುವುದನ್ನು ನೀವು ಕಾಣುವಿರಿ.');
INSERT INTO "kannada" VALUES(42,45,'42:45','ಅವಮಾನಿತರಾಗಿ ತಲೆ ತಗ್ಗಿಸುತ್ತ ನರಕಾಗ್ನಿಯ ಮುಂದೆ ಅವರನ್ನು ಪ್ರದರ್ಶಿಸಲ್ಪಡುವುದನ್ನೂ ಅದನ್ನು ಕುಡಿನೋಟದೊಂದಿಗೆ ಕದ್ದು ನೋಡು ವುದನ್ನೂ ನೀವು ಕಾಣುವಿರಿ. ಪುನರುತ್ಥಾನದ ಈ ದಿನ ತಮ್ಮನ್ನೂ ತಮ್ಮ ಸಂಬಂಧಿಕರನ್ನೂ ನಷ್ಟಕ್ಕೊಳಪಡಿಸಿಕೊಂಡವರೇ ನಿಜವಾದ ನಷ್ಟಕ್ಕೊಳಗಾದವರೆಂದು ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಆಗ ಹೇಳುವರು. ತಿಳಿಯಿರಿ, ಅಕ್ರಮಿಗಳು ಶಾಶ್ವತ ವಾದ ಶಿಕ್ಷೆಯಲ್ಲಿರುವರು.');
INSERT INTO "kannada" VALUES(42,46,'42:46','ಅಲ್ಲಾಹನ ಹೊರತಾಗಿ ಅವರ ಸಹಾಯಕ್ಕೆ ಬರಬಲ್ಲ ಯಾವ ರಕ್ಷಕರೂ ಅವರಿಗಿರಲಾರರು. ಅಲ್ಲಾಹನು ಯಾರನ್ನು ಪಥಭ್ರಷ್ಟಗೊಳಿಸಿರು ವನೋ ಅವನ ಪಾಲಿಗೆ ರಕ್ಷಣೆಯ ಯಾವ ಮಾರ್ಗವೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(42,47,'42:47','ಯಾವ ಖಿಯಾಮತ್ ದಿನದ ರದ್ದತಿಗೆ ಅಲ್ಲಾಹನು ಯಾವುದೇ ಅವಕಾಶವನ್ನಿಟ್ಟಿಲ್ಲವೋ ಆ ದಿನವು ಬರುವುದಕ್ಕೆ ಮುಂಚೆ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕರೆಗೆ ಓಗೊಡಿರಿ. ಅಂದು ನಿಮಗಾಗಿ ಯಾವುದೇ ಅಭಯ ಸ್ಥಾನ ಇರಲಾರದು. ನಿಮ್ಮ ಅಪರಾಧಗಳನ್ನು ನಿರಾಕರಿಸಲೂ ನಿಮಗಾಗದು.');
INSERT INTO "kannada" VALUES(42,48,'42:48','ಹೀಗಿದ್ದೂ ಇವರು ವಿಮುಖರಾದರೆ (ಸಂದೇಶ ವಾಹಕರೇ,) ನಾವು ನಿಮ್ಮನ್ನು ಇವರ ಕಾವಲುಗಾರ ನಾಗಿ ಕಳುಹಿಸಿಲ್ಲ. ದೌತ್ಯವನ್ನು ತಲುಪಿಸಿ ಬಿಡುವ ಹೊಣೆಗಾರಿಕೆ ಮಾತ್ರ ನಿಮ್ಮ ಮೇಲಿದೆ. ನಾವು ಮನುಷ್ಯನಿಗೆ ನಮ್ಮ ಕಡೆಯಿಂದ ಕೃಪೆಯ ಸವಿಯನ್ನು ಉಣಿಸಿದಾಗ ಅವನು ಅದರಿಂದ ಹರ್ಷಗೊಳ್ಳುತ್ತಾನೆ. ಆದರೆ ಅವರ ಕರಗಳು ಸ್ವತಃ ಮಾಡಿದ ಕರ್ಮಗಳ ಫಲವಾಗಿ ಯಾವುದೇ ವಿಪತ್ತು ಎರಗಿ ಬಿಟ್ಟಾಗ ಅವರು ಮಹಾ ಕೃತಘ್ನರಾಗಿ ಬಿಡುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(42,49,'42:49','ಆಕಾಶಗಳು ಮತ್ತು ಭೂಮಿಯ ಒಡೆತನ ಅಲ್ಲಾ ಹನದ್ದು. ತಾನಿಚ್ಛಿಸಿದ್ದನ್ನು ಅವನು ಸೃಷ್ಟಿಸುತ್ತಾನೆ. ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಹೆಣ್ಣು ಮಕ್ಕಳನ್ನು ಕೊಡು ತ್ತಾನೆ. ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಗಂಡು ಮಕ್ಕಳನ್ನು ಕೊಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(42,50,'42:50','ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಗಂಡು-ಹೆಣ್ಣುಗಳೆರಡನ್ನೂ ಸೇರಿಸಿಕೊಡುತ್ತಾನೆ, ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ಬಂಜೆಯಾಗಿ ಮಾಡುತ್ತಾನೆ. ಅವನು ಸರ್ವಜ್ಞನೂ ಸರ್ವ ಸಮರ್ಥನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(42,51,'42:51','ಮನುಷ್ಯನೊಂದಿಗೆ ಅಲ್ಲಾಹನು ಖುದ್ದು ಮಾತಾ ಡುವ ಸಂಭವವಿಲ್ಲ. ಬೋಧನೆಯ ರೂಪದಲ್ಲಿ ಅಥವಾ ತೆರೆಯಮರೆಯಿಂದ ಅಥವಾ ಓರ್ವ ದೂತನನ್ನು ಕಳುಹಿಸಿ ಅಲ್ಲಾಹನ ಅನುಮತಿಯಂತೆ ಅವನಿಚ್ಚಿಸಿದ್ದನ್ನು ಆ ದೂತರು ಬೋಧನೆ ಕೊಡುವ ರೂಪದಲ್ಲಿ ಹೊರತು . ಅವನು ಮಹೋನ್ನತನೂ ಮಹಾಯುಕ್ತಿವಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(42,52,'42:52','ಇದೇ ರೀತಿಯಲ್ಲಿ ನಿಮಗೆ ನಾವು ನಮ್ಮ ಅಪ್ಪಣೆ ಯಿಂದ ಒಂದು ಆತ್ಮ ಚೈತನ್ಯದಾಯಕ ದಿವ್ಯ ಸಂದೇಶವನ್ನು ನೀಡಿರುತ್ತೇವೆ . ವೇದ ಗ್ರಂಥವಾಗಲಿ, ಸತ್ಯವಿಶ್ವಾಸವಾಗಲಿ ಏನೆಂದು ನಿಮಗೆ ಗೊತ್ತಿರಲಿಲ್ಲ . ಆದರೆ ಅದನ್ನು ನಾವು ಒಂದು ಪ್ರಕಾಶವಾಗಿ ಮಾಡಿದೆವು. ಆ ಮೂಲಕ ನಾವು ನಮ್ಮ ದಾಸರ ಪೈಕಿ ನಾವಿಚ್ಛಿಸುವವರಿಗೆ ದಾರಿ ತೋರಿಸುತ್ತೇವೆ. ನಿಸ್ಸಂದೇಹವಾಗಿಯೂ ನೀವು ನೇರಮಾರ್ಗದ ಕಡೆಗೆ ಮಾರ್ಗದರ್ಶನ ಮಾಡಿಸುತ್ತಿರುವಿರಿ.');
INSERT INTO "kannada" VALUES(42,53,'42:53','ಆಗಸಗಳು ಮತ್ತು ಭೂಮಿಯಲ್ಲಿರುವ ಸರ್ವದರ ಮಾಲಿಕನಾದ ಅಲ್ಲಾಹನ ಮಾರ್ಗದೆಡೆಗೆ. ತಿಳಿಯಿರಿ; ಎಲ್ಲ ಕಾರ್ಯಗಳೂ ಅಲ್ಲಾಹನೆಡೆಗೇ ಮರಳುತ್ತವೆ.');
INSERT INTO "kannada" VALUES(43,1,'43:1','ಹಾಮೀಮ್.');
INSERT INTO "kannada" VALUES(43,2,'43:2','ಸುವ್ಯಕ್ತವಾದ ಈ ಗ್ರಂಥದಾಣೆ.');
INSERT INTO "kannada" VALUES(43,3,'43:3','ನೀವು ಗ್ರಹಿಸಿಕೊಳ್ಳಲಿಕ್ಕಾಗಿ ನಾವು ಇದನ್ನು ಅರಬಿ ಭಾಷೆಯ ಖುರ್‍ಆನ್ (ಗ್ರಂಥ) ಆಗಿ ಮಾಡಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(43,4,'43:4','ವಾಸ್ತವದಲ್ಲಿ ಅದು ಮೂಲಗ್ರಂಥದಲ್ಲಿ ಲಿಖಿತವಿದ್ದು ನಮ್ಮ ಬಳಿ ಅತ್ಯುನ್ನತ ಸ್ಥಾನ ಹೊಂದಿರುವ ತತ್ವ ಪೂರ್ಣ ಗ್ರಂಥ.');
INSERT INTO "kannada" VALUES(43,5,'43:5','ಹೀಗಿರುವಾಗ ಇದನ್ನು ನಿಮಗೆ ರವಾನಿಸದೆ ನಾವು ತಡೆದಿರಿಸಬೇಕೆ? ನೀವು ಹದ್ದುಮೀರಿದ ಜನತೆ ಎಂಬ ಕಾರಣಕ್ಕೆ?');
INSERT INTO "kannada" VALUES(43,6,'43:6','ಪೂರ್ವಿಕರಲ್ಲೂ ನಾವು ಎಷ್ಟೆಷ್ಟೋ ಪ್ರವಾದಿಗಳನ್ನು ಕಳುಹಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(43,7,'43:7','ಅವರ ಬಳಿಗೆ ಬಂದ ಯಾವೊಬ್ಬ ಪ್ರವಾದಿಯನ್ನೂ ಅವರು ಪರಿಹಾಸ ಮಾಡದೇ ಬಿಟ್ಟಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(43,8,'43:8','ಆಗ ಇವರಿಗಿಂತ ಅದೆಷ್ಟೋ ಸಮರ್ಥ ಬಲಶಾಲಿಗಳಾಗಿದ್ದವರನ್ನು ನಾವು ನಾಶ ಗೊಳಿಸಿದ್ದೇವೆ. ಪೂರ್ವಿಕರ ಉದಾಹರಣೆ (ಖುರ್‍ಆನಿನಲ್ಲಿ) ಪ್ರಸ್ತಾಪಿಸಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(43,9,'43:9','ಆಕಾಶಗಳನ್ನೂ, ಭೂಮಿಯನ್ನೂ ಸೃಷ್ಟಿಸಿದ್ದು ಯಾರೆಂದು ತಾವು ಅವರೊಂದಿಗೆ ಕೇಳಿದರೆ, ಅವುಗಳನ್ನು ಪ್ರಬಲನೂ ಅಭಿಜ್ಞನೂ ಆದವನು (ಅಲ್ಲಾಹು) ಸೃಷ್ಟಿಸಿದನೆಂದು ಅವರು ಹೇಳುವರು .');
INSERT INTO "kannada" VALUES(43,10,'43:10','(ಹೌದು) ನಿಮಗಾಗಿ ಈ ಭೂಮಿಯನ್ನು ಒಂದು ಹಾಸನ್ನಾಗಿಯೂ ಗುರಿ ತಲುಪುವಂತಾಗಲು ಅದರಲ್ಲಿ ನಿಮಗೆ ದಾರಿಗಳನ್ನೂ ಮಾಡಿಕೊಟ್ಟವನು ಅವನೇ.');
INSERT INTO "kannada" VALUES(43,11,'43:11','ಅವನು ಆಕಾಶದಿಂದ ಒಂದು ನಿಶ್ಚಿತ ಪ್ರಮಾಣದಲ್ಲಿ ನೀರನ್ನು ಸುರಿಸಿಕೊಟ್ಟನು. ಹಾಗೆ ನಾವು ಅದರ ಮೂಲಕ ನಿರ್ಜೀವಗೊಂಡಿದ್ದ ಭೂಪ್ರದೇ ಶವನ್ನು ಜೀವಂತಗೊಳಿಸಿದೆವು. ಅಂತೆಯೇ ನೀವು (ಒಂದು ದಿನ ಸಮಾಧಿಯಿಂದ) ಹೊರ ತರಲ್ಪಡುವಿರಿ.');
INSERT INTO "kannada" VALUES(43,12,'43:12','ಎಲ್ಲಾ ಜೋಡಿಗಳನ್ನು ಸೃಷ್ಟಿಸಿದವನು ಅವನೇ. ಅವನು ನಿಮಗೆ ಯಾತ್ರೆಗಿರುವ ನೌಕೆಗಳನ್ನು ಮತ್ತು ಪ್ರಾಣಿಗಳನ್ನು (ವಾಹನಗಳಾಗಿ) ಮಾಡಿ ಕೊಟ್ಟಿರುವನು.');
INSERT INTO "kannada" VALUES(43,13,'43:13','ನೀವು ಅವುಗಳ ಬೆನ್ನೇರಿ ಕುಳಿತುಕೊಳ್ಳಲು ಆಮೇಲೆ ನೀವು ಅವುಗಳ ಮೇಲೆ ಕುಳಿತಾಗ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಅನುಗ್ರಹವನ್ನು ನೀವು ಸ್ಮರಿಸಿಕೊಳ್ಳಲು ಹಾಗೂ ನೀವು ಹೀಗೆ ಹೇಳಲಿಕ್ಕಾಗಿ: ``ನಮಗೆ ಇದನ್ನು ಅಧೀನಪಡಿಸಿಕೊಟ್ಟವನು ಪರಮ ಪರಿಶುದ್ಧನು. ನಾವು ಸ್ವಂತವಾಗಿ ಇದನ್ನು ಅಧೀನಪಡಿಸಲು ಶಕ್ತರಾಗಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(43,14,'43:14','ಖಂಡಿತ. ನಾವು ನಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಗೇ ಮರಳಿ ಹೋಗಲಿಕ್ಕಿದೆ”.');
INSERT INTO "kannada" VALUES(43,15,'43:15','ಅವರು ಅವನ (ಅಲ್ಲಾಹನ) ದಾಸರುಗಳಲ್ಲಿ ಕೆಲವರನ್ನು ಅವನ ಅಂಶವನ್ನಾಗಿ (ಸಂತಾನವಾಗಿ) ಮಾಡಿಬಿಟ್ಟರು. ಖಂಡಿತ, ಮನುಷ್ಯನು ಪ್ರತ್ಯಕ್ಷ ಕೃತಘ್ನನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(43,16,'43:16','ಅದಲ್ಲ, ತಾನು ಸೃಷ್ಟಿಸುವುದರಲ್ಲಿ ಕೆಲವರನ್ನು ಅವನು ಪುತ್ರಿಯರಾಗಿ ಮಾಡಿ ಪುತ್ರರನ್ನು ನಿಮಗೆ ಪ್ರತ್ಯೇಕಗೊಳಿಸಿಕೊಟ್ಟಿರುವನೇ?');
INSERT INTO "kannada" VALUES(43,17,'43:17','ಅವರಲ್ಲಿ ಒಬ್ಬನಿಗೆ- ಅವನು ಕರುಣಾನಿಧಿಯಾದ ಅಲ್ಲಾಹನಿಗೆ ಉಪಮೆಗೊಳಿಸಿದ್ದನ್ನು (ಹೆಣ್ಮಕ್ಕಳನ್ನು)- ಸುವಾರ್ತೆಯಾಗಿ ನೀಡಲ್ಪಟ್ಟರೆ ಅವನು ಮಹಾ ದುಃಖಿತನಾಗಿ, ಅವನ ಮುಖ ಕಪ್ಪಾಗಿ ಬಿಡುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(43,18,'43:18','ಆಭರಣಾಲಂಕಾರಗಳಿಂದ ಬೆಳೆಸಲ್ಪಡುವವರ ನ್ನೋ (ನೀವು ಅಲ್ಲಾಹನಿಗೆ ಸಂತಾನಗಳಾಗಿ ಮಾಡಿ ಬಿಡುವುದು?) ವಾಸ್ತವದಲ್ಲಿ ಅವರು ವಾಗ್ವಾದದಲ್ಲಿ ತಮ್ಮ ಮನದಿಂಗಿತವನ್ನು ಕೂಡ ವ್ಯಕ್ತಪಡಿಸಲು ಸಾಧ್ಯವಾಗದವರಾಗಿದ್ದಾರೆ .');
INSERT INTO "kannada" VALUES(43,19,'43:19','ಮಹಾ ಕಾರುಣಿಕನಾದ ಅಲ್ಲಾಹನ ದಾಸರಾಗಿ ರುವ ದೇವಚರರನ್ನು ಅವರು ಸ್ತ್ರೀಯರೆಂದು ವಾದಿಸಿಕೊಂಡರು. ಅವರನ್ನು ಸೃಷ್ಟಿಸುವಾಗ ಇವರು ಅಲ್ಲಿ ಸನ್ನಿಹಿತರಾಗಿದ್ದರೇ? ಅವರ ಸಾಕ್ಷಿ ಯನ್ನು ಬರೆದಿರಿಸಿಕೊಳ್ಳಲಾಗುವುದು ಮತ್ತು ಅವರು ಅದರ ಕುರಿತು ವಿಚಾರಿಸಲ್ಪಡುವರು.');
INSERT INTO "kannada" VALUES(43,20,'43:20','``ಕಾರುಣ್ಯವಂತನಾದ ಅಲ್ಲಾಹು ಇಚ್ಛಿಸುತ್ತಿದ್ದರೆ ನಾವು ದೇವಚರರನ್ನು ಆರಾಧಿಸುತ್ತಿರಲಿಲ್ಲ’’ ಎಂದು ಅವರು ಹೇಳುತ್ತಾರೆ . ಅದರ ಕುರಿತು ಅವರಿಗೆ ಯಾವುದೇ ಅರಿವಿಲ್ಲ. ಅವರು ಕೇವಲ ಊಹೆಯಿಂದ ಹೇಳುತ್ತಿದ್ದಾರಷ್ಟೆ.');
INSERT INTO "kannada" VALUES(43,21,'43:21','ಅದಲ್ಲ, (ಈ ವಿಷಯದಲ್ಲಿ) ಇದಕ್ಕೆ ಮೊದಲು ನಾವು ಯಾವುದಾದರೂ ವೇದಗ್ರಂಥವನ್ನು ಅವರಿಗೆ ನೀಡಿದ್ದು, ಅದನ್ನವರು ಬಲವಾಗಿ ಹಿಡಿದಿದ್ದರೇನು? (ಇಲ್ಲ)');
INSERT INTO "kannada" VALUES(43,22,'43:22','ಆದರೆ, ಅವರು ಹೇಳುತ್ತಾರೆ; ``ನಮ್ಮ ಪೂರ್ವಜರನ್ನು ಒಂದು ಮಾರ್ಗದಲ್ಲಿದ್ದುದಾಗಿ ನಾವು ಕಂಡಿದ್ದೇವೆ. ನಾವು ಅವರ ಹೆಜ್ಜೆ ಗುರುತುಗಳ ಮೇಲೆ ಸನ್ಮಾರ್ಗ ಗಳಿಸಿಕೊಂಡವರಾಗಿದ್ದೇವೆ.’’');
INSERT INTO "kannada" VALUES(43,23,'43:23','(ಪ್ರವಾದಿಯರೇ) ಇದೇ ರೀತಿಯಲ್ಲಿ ತಮಗಿಂತ ಮುಂಚೆ ನಾವು ಪ್ರತಿಯೊಂದು ನಾಡಲ್ಲೂ ಒಬ್ಬ ಮುನ್ನೆಚ್ಚರಿಕೆಗಾರನನ್ನು ಕಳುಹಿಸಿದಾಗ ಅಲ್ಲಿ ಸುಖಲೋಲುಪರು (ಈ ರೀತಿ) ಹೇಳದೆ ಬಿಟ್ಟಿಲ್ಲ; ``ನಮ್ಮ ಪೂರ್ವಜರನ್ನು ಒಂದು ಮಾರ್ಗದಲ್ಲಿ ನಾವು ಕಂಡರಿತೆವು. ನಾವು ಕೂಡಾ ಅವರ ಹೆಜ್ಜೆ ಗುರುತುಗಳನ್ನು ಅನುಕರಿಸುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(43,24,'43:24','ಅವರು (ಪ್ರವಾದಿ) ಕೇಳಿದರು; ನೀವು ನಿಮ್ಮ ಪೂರ್ವಿಕರನ್ನು ಕಂಡರಿತ ಮಾರ್ಗಕ್ಕಿಂತ ಸಂಶುದ್ಧ ಮಾರ್ಗವನ್ನು ನಾನು ನಿಮಗೆ ತಂದು ಕೊಟ್ಟರೆ? (ನೀವು ಅದೇ ಮಾರ್ಗದಲ್ಲಿ ಚಲಿ ಸುವಿರಾ?) ಅವರು (ಆ ಜನತೆ) ಹೇಳಿದರು; ``ನೀವು ಯಾವ ಮಾರ್ಗ ದೆಡೆಗೆ ಅಹ್ವಾನಿಸಲು ನಿಯೋಗಿಸಲ್ಪಟ್ಟಿರುವಿರೋ ಅದನ್ನು ನಾವು ನಿಷೇಧಿಸುವವರಾಗಿದ್ದೇವೆ”.');
INSERT INTO "kannada" VALUES(43,25,'43:25','(ಕೊನೆಗೆ) ಅವರನ್ನು ನಾವು ಶಿಕ್ಷಿಸಿದೆವು. ಆ ಸತ್ಯ ನಿಷೇಧಿಗಳ ಅಂತಿಮ ಸ್ಥಿತಿ ಏನಾಯಿತೆಂಬು ದನ್ನು ಕಂಡುಕೊಳ್ಳಿರಿ.');
INSERT INTO "kannada" VALUES(43,26,'43:26','(ಪ್ರವಾದಿ) ಇಬ್‍ರಾಹೀಮರು ತನ್ನ ತಂದೆ ಯೊಂದಿಗೆ ಮತ್ತು ಜನತೆಯೊಂದಿಗೆ ಹೀಗೆ ಹೇಳಿದ ಸಂದರ್ಭ ವನ್ನು ಸ್ಮರಿಸಿರಿ. `ನೀವು ಯಾವುದನ್ನು ಆರಾಧಿಸುತ್ತೀರೋ ಅದರಿಂದ ನಾನು ಮುಕ್ತನಾಗಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(43,27,'43:27','ತನ್ನನ್ನು ಸೃಷ್ಟಿಸಿದ (ಅಲ್ಲಾಹನ) ಹೊರತು. ಅವನೇ ನನಗೆ ಸನ್ಮಾರ್ಗದರ್ಶನ ಮಾಡುವನು.');
INSERT INTO "kannada" VALUES(43,28,'43:28','ಅವರು (ಇಬ್‍ರಾಹೀಮ್) ಅದನ್ನು (ಆ ಘೋಷಣೆಯನ್ನು) ತನ್ನ ಸಂತತಿಗಳಲ್ಲಿ ಬಿಟ್ಟು ಹೋದ ಒಂದು ವಚನವನ್ನಾಗಿ ಮಾಡಿದರು . ಅವರು (ಅನುಯಾಯಿಗಳು ಸತ್ಯದ ಕಡೆಗೆ) ಮರಳು ವಂತಾಗಲು.');
INSERT INTO "kannada" VALUES(43,29,'43:29','ಇಷ್ಟಾಗಿಯೂ ಇವರಿಗೆ ಮತ್ತು ಇವರ ಪೂರ್ವ ಜರಿಗೆ ಸತ್ಯ (ಖುರ್‍ಆನ್) ಮತ್ತು ಸುವ್ಯಕ್ತ ವಿವರಣೆ ನೀಡುವ ಪ್ರವಾದಿ ಆಗಮಿಸುವ ತನಕ ನಾನು ಜೀವನ ಸಾಧನ ಕೊಡುತ್ತಲೇ ಇದ್ದೆನು.');
INSERT INTO "kannada" VALUES(43,30,'43:30','ಹಾಗೆ, ಅವರ ಬಳಿಗೆ ಸತ್ಯವು ಬಂದು ತಲುಪಿದಾಗ ``ಇದು ಮಾರಣ (ಸಿಹ್‍ರ್) ಆಗಿರುತ್ತದೆ. ನಾವಿದನ್ನು ನಿಷೇಧಿಸುತ್ತೇವೆ” ಎಂದವರು ಹೇಳಿ ಬಿಟ್ಟರು.');
INSERT INTO "kannada" VALUES(43,31,'43:31','ಅವರು ಕೇಳಿದರು; ``ಈ ಖುರ್‍ಆನನ್ನು ಈ ಎರಡು ನಾಡುಗಳಲ್ಲಿರುವ ಮಹಾನ್ ವ್ಯಕ್ತಿಯೊ ಬ್ಬರ ಮೇಲೆ ಅವತೀರ್ಣಗೊಳಿಸಲ್ಪಟ್ಟಿಲ್ಲವೇಕೆ?” ');
INSERT INTO "kannada" VALUES(43,32,'43:32','(ಪ್ರವಾದಿಯರೇ) ತಮ್ಮ ಪ್ರಭುವಿನ ಕೃಪೆಯನ್ನು ಹಂಚಿಕೊಡುವವರು ಅವರಾಗಿರುವರೇ? ಐಹಿಕ ಜೀವನದಲ್ಲಿ ಅವರ ಉಪಜೀವನ ಸಾಧನಗಳನ್ನು ಅವರ ಮಧ್ಯೆ ನಾವು ಭಾಗಿಸಿಕೊಟ್ಟಿದ್ದೇವೆ. ಅವರಲ್ಲಿ ಕೆಲವರು ಇನ್ನು ಕೆಲವರನ್ನು ಅಧೀನದಲ್ಲಿಟ್ಟು ಕೊಳ್ಳಲಿಕ್ಕಾಗಿ ಅವರ ಪೈಕಿ ಕೆಲವರನ್ನು ಇನ್ನು ಕೆಲವರಿಗಿಂತ ನಾವು ಮೇಲೇರಿಸಿರುವೆವು. ತಮ್ಮ ಪ್ರಭುವಿನ ಕೃಪೆಯು ಅವರು ಸಂಗ್ರಹಿಸುತ್ತಿರುವ ಸಂಪತ್ತಿಗಿಂತ ಅದೆಷ್ಟೋ ಉತ್ತಮವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(43,33,'43:33','ಮನುಷ್ಯರು ಸತ್ಯನಿಷೇಧದಲ್ಲಿ ಏಕ ಸಮುದಾ ಯವಾಗುವ ಸಂಭವ ಇಲ್ಲದಿರುತ್ತಿದ್ದರೆ ಕಾರುಣ್ಯ ವಂತನಾದ ಅಲ್ಲಾಹನನ್ನು ನಿಷೇಧಿಸುವವರ ಮನೆಗಳಿಗೆ ಬೆಳ್ಳಿಯ ಛಾವಣಿಗಳನ್ನೂ ಅವರು ತಮ್ಮ ಮಾಳಿಗೆಗೇರಲು (ಬೆಳ್ಳಿಯ) ಮೆಟ್ಟಲು ಗಳನ್ನೂ');
INSERT INTO "kannada" VALUES(43,34,'43:34','');
INSERT INTO "kannada" VALUES(43,35,'43:35','ಅವರ ಮನೆಗಳಿಗೆ (ಬೆಳ್ಳಿಯ) ಬಾಗಿಲು ಗಳನ್ನೂ ಅವರಿಗೆ ಒರಗಿ ಕುಳಿತುಕೊಳ್ಳಲು (ಬೆಳ್ಳಿಯ) ಮಂಚಗಳನ್ನೂ ಚಿನ್ನದ ಅಲಂಕಾರ ವನ್ನೂ ನಾವು ಮಾಡಿಕೊಡುತ್ತಿದ್ದೆವು. (ವಾಸ್ತವ ದಲ್ಲಿ) ಅವೆಲ್ಲವೂ ಐಹಿಕ ಜೀವನದ ಸರಕು (ದೊಡ್ಡಸ್ತಿಕೆ)ಗಳಾಗಿವೆ . ಪರಲೋಕವು ತಮ್ಮ ಪ್ರಭುವಿನ ಬಳಿ ಭಯ-ಭಕ್ತಿಯುಳ್ಳವರಿಗೆ ಮಾತ್ರ ಇರುವುದಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(43,36,'43:36','ಕಾರುಣ್ಯವಂತನಾದ ಅಲ್ಲಾಹನ ಸ್ಮರಣೆಯಿಂದ ಯಾವನಾದರೂ ಹಿಂದೆ ಸರಿದರೆ ನಾವು ಅವನಿಗೆ ಒಂದು ಪಿಶಾಚಿಯನ್ನು ಏರ್ಪಡಿಸಿ ಕೊಡುವೆವು. ಆಗ ಆ ಪಿಶಾಚಿ ಅವನ ಆಪ್ತ ಸಂಗಾತಿಯಾಗಿ ಬಿಡುವನು.');
INSERT INTO "kannada" VALUES(43,37,'43:37','ಆ ಪಿಶಾಚಿಗಳು ಅವರನ್ನು ಸನ್ಮಾರ್ಗದಿಂದ ತಡೆಯುತ್ತಾರೆ. ಅವರಾದರೆ, ನಾವು ಸನ್ಮಾರ್ಗ ಪ್ರಾಪ್ತಿಸಿಕೊಂಡಿದ್ದೇವೆಂದು ತಮ್ಮಷ್ಟಕ್ಕೆ ತಿಳಿದು ಕೊಂಡಿರುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(43,38,'43:38','ಕೊನೆಗೆ, ನಮ್ಮ ಬಳಿ ಬಂದಾಗ ಅವರು (ತಮ್ಮ ಸಂಗಾತಿಯೊಂದಿಗೆ) ಹೇಳುತ್ತಾರೆ; ``ಅಯ್ಯೋ, ನನ್ನ ಮತ್ತು ನಿನ್ನ ಮಧ್ಯೆ ಪೂರ್ವ ಪಶ್ಚಿಮಗಳ ಅಂತರವಿರುತ್ತಿದ್ದರೆ ಬಹಳ ಉತ್ತಮವಿತ್ತು’’. ಹಾಗಾದರೆ ಆ ಸಂಗಾತಿ ಎಷ್ಟು ನಿಕೃಷ್ಟ !');
INSERT INTO "kannada" VALUES(43,39,'43:39','ಇಂದು ಅದು (ನಿಮ್ಮ ಆಗ್ರಹ ಪ್ರಕಟನೆ) ನೀವೆಸಗಿದ ಅಕ್ರಮವು ಬಹಿರಂಗಗೊಂಡ ಸಂದರ್ಭದಲ್ಲಿ ನಿಮಗೆ ಫಲಕಾರಿಯಾಗದು. ನೀವು ಶಿಕ್ಷೆಯಲ್ಲಿ ಎರಡು ವಿಭಾಗದವರೂ ಪಾಲುದಾರರೇ ಆಗಿರುವಿರಿ.');
INSERT INTO "kannada" VALUES(43,40,'43:40','(ಪ್ರವಾದಿಯರೇ) ತಾವು ಕಿವುಡರಿಗೆ ಕೇಳಿಸ ಬಲ್ಲಿರಾ? ಅಥವಾ ಅಂಧರನ್ನು ಮತ್ತು ಸುವ್ಯಕ್ತ ಪಥಭೃಷ್ಟತೆಯಲ್ಲಿ ಬಿದ್ದಿರುವವರನ್ನು ಸನ್ಮಾರ್ಗದಲ್ಲಿ ಮಾಡುವಿರಾ? ');
INSERT INTO "kannada" VALUES(43,41,'43:41','ಇನ್ನು (ಇಲ್ಲಿಂದ ವಫಾತ್ ಮುಖಾಂತರ) ತಮ್ಮ ನ್ನು ನಾವು ಎತ್ತಿಕೊಂಡು ಹೋದರೂ ಅವರನ್ನು ನಾವು ಶಿಕ್ಷಿಸಿಯೇ ಬಿಡುವೆವು.');
INSERT INTO "kannada" VALUES(43,42,'43:42','ಅಲ್ಲದಿದ್ದಲ್ಲಿ, ಅವರಿಗೆ ನಾವು ವಾಗ್ದಾನ ನೀಡಿರುವ ಶಿಕ್ಷೆಯನ್ನು ತಮಗೆ ಕಾಣಿಸಿಕೊಡಲಾಗುವುದು . ಅವರ ಮೇಲೆ ನಾವು ಸಂಪೂರ್ಣ ನಿಯಂತ್ರಣವುಳ್ಳವ (ಶಕ್ತ)ರೇ ಆಗಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(43,43,'43:43','ಹಾಗಾಗಿ, ತಮಗೆ ನೀಡಲ್ಪಟ್ಟ ಸಂದೇಶವನ್ನು ತಾವು ಬಲವಾಗಿ ಹಿಡಿದುಕೊಳ್ಳಿರಿ. ಖಂಡಿತವಾಗಿಯೂ ತಾವು ನೇರ ಮಾರ್ಗದಲ್ಲಿದ್ದೀರಿ.');
INSERT INTO "kannada" VALUES(43,44,'43:44','ಇದು (ಖುರ್‍ಆನ್) ತಮಗೂ ತಮ್ಮ ಜನತೆಗೂ ಒಂದು ಉದ್ಬೋಧನೆಯಾಗಿದೆ. ಕೊನೆಯಲ್ಲಿ ನೀವೆಲ್ಲರೂ ಪ್ರಶ್ನಿಸಲ್ಪಡುವಿರಿ.');
INSERT INTO "kannada" VALUES(43,45,'43:45','ನಾವು ತಮಗಿಂತ ಮುಂಚೆ ಕಳುಹಿಸಿದ್ದ ನಮ್ಮ ಸಂದೇಶವಾಹಕರುಗಳೊಂದಿಗೆ ತಾವು ಕೇಳಿ ನೋಡಿರಿ . “ಕಾರುಣ್ಯವಂತನಾದ ಅಲ್ಲಾ ಹನ ಹೊರತು ಆರಾಧಿಸಲ್ಪಡುವ ಯಾವುದೇ ದೇವರುಗಳನ್ನು ನಾವು ನಿಶ್ಚಯಿಸಿದ್ದೇವೆಯೇ?”');
INSERT INTO "kannada" VALUES(43,46,'43:46','ನಾವು ಮೂಸಾರನ್ನು ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳೊಂದಿಗೆ ಫಿರ್‍ಔನ್ ಮತ್ತು ಅವನ ಆಸ್ಥಾನಿಕರ ಬಳಿಗೆ ಕಳುಹಿಸಿದೆವು. ಅವರು (ಹೋಗಿ) ``ನಾನು ಸರ್ವಲೋಕ ಪರಿಪಾಲಕನಾದ ಪ್ರಭು ವಿನ ಸಂದೇಶವಾಹಕನಾಗಿದ್ದೇನೆ’’ ಎಂದು ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(43,47,'43:47','ಹಾಗೆಯೇ ಅವರು ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳೊಂದಿಗೆ ಅವರ ಬಳಿ ಹೋದಾಗ ಅವರು ಅದರ ಕುರಿತು (ಪರಿಹಾಸದಿಂದ) ನಕ್ಕುಬಿಟ್ಟರು.');
INSERT INTO "kannada" VALUES(43,48,'43:48','ಅವರಿಗೆ ನಾವು ಕಾಣಿಸಿಕೊಡುತ್ತಿದ್ದ ಪ್ರತಿಯೊಂದು ದೃಷ್ಠಾಂತವು ಒಂದು ಮತ್ತೊಂದಕ್ಕಿಂತ ಮಿಗಿಲಾದುದಾಗಿತ್ತು. (ಹಲವು ತರದ) ಶಿಕ್ಷೆಗಳಿಂದ ಅವರನ್ನು ನಾವು ಬಂಧಿಸಿದೆವು. ಅವರು (ಸತ್ಯದ ಕಡೆ) ಹಿಂತಿರುಗುವಂತಾಗಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(43,49,'43:49','ಅವರು ಹೇಳಿದರು; “ಓ ಮಾಟಗಾರ! ನಿನ್ನ ಪ್ರಭು ನಿನಗೆ ತಿಳಿಸಿಕೊಟ್ಟ ಆಧಾರದಲ್ಲಿ ನಿನ್ನ ಪ್ರಭುವಿನಲ್ಲಿ ನಮಗಾಗಿ ಪ್ರಾರ್ಥಿಸು. ನಾವು ಖಂಡಿತ ಸನ್ಮಾರ್ಗಕ್ಕೆ ಬರುತ್ತೇವೆ”.');
INSERT INTO "kannada" VALUES(43,50,'43:50','ಹಾಗೆಯೇ ನಾವು ಅವರ ಮೇಲಿನ ಶಿಕ್ಷೆಯನ್ನು ತೊಲಗಿಸಿದಾಗ ಅವರು ಕರಾರು ಮುರಿದು ಬಿಡುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(43,51,'43:51','ಫಿರ್‍ಔನ್ (ಒಮ್ಮೆ) ತನ್ನ ಜನತೆಯನ್ನು ಕರೆದು ಕೇಳಿದನು; ``ನನ್ನ ಜನರೇ, ಈಜಿಪ್ತಿನ ಆಧಿಪತ್ಯ ನನ್ನದಲ್ಲವೇ? ಈ ನದಿಗಳು ಹರಿಯುವುದು ನನ್ನ ಅಧೀನದಲ್ಲಲ್ಲವೇ? ನೀವು ಇವುಗಳನ್ನು ಕಂಡರಿತಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(43,52,'43:52','ಅಥವಾ ಅಧಮನೂ ಸ್ಪಷ್ಟವಾಗಿ ಮಾತನಾಡಲಾಗದವನೂ ಆದ ಇವನಿಗಿಂತ (ಮೂಸಾ ಗಿಂತ) ಉತ್ತಮನು ನಾನೇ ಅಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(43,53,'43:53','ಇನ್ನು (ಇವನು ಹೇಳುವುದು ಸತ್ಯವೆಂದಾದರೆ) ಇವನಿಗೆ ಚಿನ್ನದ ಕಂಕಣಗಳೇಕೆ ಧರಿಸಲ್ಪಟ್ಟಿಲ್ಲ? ಅಥವಾ ಇವನೊಂದಿಗೆ ದೇವಚರರ ತುಕಡಿ ಗಳೇಕೆ ಬಂದಿಲ್ಲ?”');
INSERT INTO "kannada" VALUES(43,54,'43:54','ಹಾಗೆ, ಅವನು ತನ್ನ ಜನತೆಯನ್ನು ಮೂಢರ ನ್ನಾಗಿ ಮಾಡಿದನು ಮತ್ತು ಅವರು ಅವನನ್ನು ಅನುಸರಿಸಿದರು. ನಿಶ್ಚಯವಾಗಿಯೂ ಅವರು ಧಿಕ್ಕಾರಿಗಳೇ ಆಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(43,55,'43:55','(ಈ ರೀತಿ) ಅವರು ನಮ್ಮನ್ನು ರೇಗಿಸಿದಾಗ ನಾವು ಅವರನ್ನು ಶಿಕ್ಷಿಸಿದೆವು ಮತ್ತು ಅವರೆಲ್ಲರನ್ನೂ ನಾವು ಮುಳುಗಿಸಿ ಕೊಂದೆವು.');
INSERT INTO "kannada" VALUES(43,56,'43:56','ಹಾಗೆಯೇ, ಅವರನ್ನು ನಾವು ಅನಂತರದವರಿಗೆ ಒಂದು ಮಾದರಿಯೂ, ಉಪಮೆಯೂ ಆಗಿ ಮಾಡಿದೆವು.');
INSERT INTO "kannada" VALUES(43,57,'43:57','(ಪ್ರವಾದಿಯರೇ) ಮರ್ಯಮರ ಪುತ್ರ ಈಸಾರನ್ನು ಒಂದು ಉಪಮೆಯಾಗಿ ಮಾಡಲ್ಪ(ಕಾಣಿ ಸಲ್ಪ)ಟ್ಟಾಗ ತಮ್ಮ ಜನತೆ ಅದರ ಕುರಿತು ಗೋಳಿಟ್ಟರು.');
INSERT INTO "kannada" VALUES(43,58,'43:58','‘ನಮ್ಮ ದೇವರುಗಳೋ ಉತ್ತಮ? ಅಥವಾ ಅವರೋ?’ ಎಂದು ಅವರು ಕೇಳುವರು. ಒಂದು ತರ್ಕವಾಗಿ ಮಾತ್ರವೇ ಅವರದನ್ನು ತಮ್ಮ ಮುಂದಿಟ್ಟಿರುವುದು. ಮಾತ್ರವಲ್ಲ ಅವರು ಕಚ್ಚಾಡುವ ಜನತೆಯೇ ಆಗಿರುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(43,59,'43:59','ಅವರು (ಮರ್ಯಮರ ಪುತ್ರ) ನಾವು ಅನುಗ್ರಹಿಸಿದ ಓರ್ವ ದಾಸನೇ ಹೊರತು ಬೇರೇನೂ ಆಗಿರಲಿಲ್ಲ. ಇಸ್ರಾಈಲ್ ಸಂತತಿಗಳಿಗೆ ಅವರನ್ನು ನಾವು (ಅದ್ಭುತ ದೃಷ್ಟಾಂತಕ್ಕೆ) ಒಂದು ಉಪ ಮೆಯಾಗಿ ಮಾಡಿಬಿಟ್ಟೆವು.');
INSERT INTO "kannada" VALUES(43,60,'43:60','ನಾವು ಇಚ್ಛಿಸುತ್ತಿದ್ದರೆ ಭೂಮಿಯಲ್ಲಿ ನಿಮ್ಮ ಉತ್ತರಾಧಿಕಾರಿಯಾಗಲು ನಿಮ್ಮಿಂದಲೇ ನಾವು ದೇವಚರರನ್ನು ಸೃಷ್ಟಿಸಿ ಬಿಡುತ್ತಿದ್ದೆವು .');
INSERT INTO "kannada" VALUES(43,61,'43:61','ಅವರು (ಮರ್ಯಮರ ಪುತ್ರ) ಲೋಕಾಂತ್ಯದ ಒಂದು ಅರಿವು (ಕುರುಹು) ಆಗಿದ್ದಾರೆ . ಆದು ದರಿಂದ ನೀವು ಅದರ ಕುರಿತು ಸ್ವಲ್ಪವೂ ಸಂಶಯ ಪಡಬೇಡಿರಿ. ನನ್ನನ್ನು (ನನ್ನ ಮಾರ್ಗವನ್ನು) ಅನುಸರಿಸಿರಿ. ಇದು ನೇರ ಮಾರ್ಗವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(43,62,'43:62','ಪಿಶಾಚಿ ನಿಮ್ಮನ್ನು (ಈ ಮಾರ್ಗದಿಂದ) ತಡೆಯದಿರಲಿ. ಖಂಡಿತ, ಅವನು ನಿಮಗೆ ಪ್ರತ್ಯಕ್ಷ ಶತ್ರುವಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(43,63,'43:63','ಸುವ್ಯಕ್ತ ದೃಷ್ಟಾಂತಗಳೊಂದಿಗೆ ಈಸಾ (ನಬಿ) ಬಂದಾಗ ಹೇಳಿದ್ದರು; ``ನಾನು ನಿಮಗೆ ಜ್ಞಾನ ವನ್ನು (ಹಿಕ್ಮತ್) ತಂದಿರುವೆನು. ಮತ್ತು ನೀವು ಪರಸ್ಪರ ಭಿನ್ನಾಭಿಪ್ರಾಯದಲ್ಲಿರುವ ಕೆಲವು ವಿಚಾರಗಳನ್ನು ನಿಮಗೆ ವಿವರಿಸಿಕೊಡಲು ಬಂದಿರುವೆನು. ಆದುದರಿಂದ ನೀವು ಅಲ್ಲಾಹ ನನ್ನು ಭಯಪಟ್ಟು ನನ್ನನ್ನು ಅನುಸರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(43,64,'43:64','ನನ್ನ ಮತ್ತು ನಿಮ್ಮ ಪ್ರಭು ಅಲ್ಲಾಹುವಾಗಿದ್ದಾನೆ. ಆದುದರಿಂದ ನೀವು ಅವನನ್ನು ಮಾತ್ರ ಆರಾಧಿಸಿರಿ, ಅದುವೇ ನೇರ ಮಾರ್ಗ” .');
INSERT INTO "kannada" VALUES(43,65,'43:65','ಆದರೆ ಅವರ ಮಧ್ಯೆ ಪಂಗಡಗಳು ಪರಸ್ಪರ ಭಿನ್ನ ಭೇದಗೊಂಡವು . ಆದುದರಿಂದ (ಆ) ಅಕ್ರಮಿಗಳಿಗೆ ಒಂದು ದಿನದ ವೇದನಾಜನಕವಾದ ಶಿಕ್ಷೆ ಮೂಲಕ ಮಹಾ ನಾಶವಿದೆ.');
INSERT INTO "kannada" VALUES(43,66,'43:66','ಸುದ್ದಿಯೇ ಇಲ್ಲದಂತೆ ಅನಿರೀಕ್ಷಿತವಾಗಿ ಇವರ ಮೇಲೆ ಆ ಪುನರುತ್ಥಾನ ದಿನವು ಬಂದೆರಗುವುದರ ಹೊರತು ಬೇರೇನನ್ನಾದರೂ ಅವರು ಕಾಯುತ್ತಿರುವರೇ?');
INSERT INTO "kannada" VALUES(43,67,'43:67','ಅಂದು ಭಯಭಕ್ತಿಯುಳ್ಳ ಧರ್ಮನಿಷ್ಠರ ಹೊರತು ಇತರ ಮಿತ್ರರೆಲ್ಲರೂ ಪರಸ್ಪರ ಶತ್ರುಗಳಾಗಿ ಬಿಡುವರು.');
INSERT INTO "kannada" VALUES(43,68,'43:68','');
INSERT INTO "kannada" VALUES(43,69,'43:69','(ಅಂದು) ನಮ್ಮ ವಚನಗಳಲ್ಲಿ ವಿಶ್ವಾಸ ಹೊಂದಿದ ಮತ್ತು ಆಜ್ಞಾಪಾಲಕರಾದ ಮುಸ್ಲಿಮರೊಡನೆ ``ಓ ನನ್ನ ದಾಸರುಗಳೇ, ಇಂದು ನಿಮಗೇನೂ ಭಯವಿಲ್ಲ. ನಿಮಗೆ ದುಃಖವೂ ಉಂಟಾಗದು’’ (ಎಂದು ಹೇಳಲಾಗುವುದು)');
INSERT INTO "kannada" VALUES(43,70,'43:70','ನೀವೂ ನಿಮ್ಮ ಪತ್ನಿಯರೂ ಸಂತೋಷಪೂರ್ವಕ ಸ್ವರ್ಗದೊಳಗೆ ಪ್ರವೇಶಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(43,71,'43:71','ಸ್ವರ್ಣದ ತಟ್ಟೆಗಳು ಮತ್ತು ಲೋಟೆಗಳೊಂದಿಗೆ ಅವರ ಸುತ್ತ ನಡೆಯಲ್ಪಡುವುದು. ಅದರಲ್ಲಿ ದೇಹಗಳು ಇಚ್ಛಿಸುವ ಮತ್ತು ಕಣ್ಣುಗಳು ರಸಾನುಭವಿಸುವ ಎಲ್ಲವೂ ಸಿದ್ಧವಿರುವುವು. ಇದರಲ್ಲಿ ನೀವು ಸ್ಥಿರವಾಸಿಗಳಾಗಿರುವಿರಿ.');
INSERT INTO "kannada" VALUES(43,72,'43:72','ನೀವು (ಭೂಲೋಕದಲ್ಲಿ) ನಿರ್ವಹಿಸುತ್ತಿದ್ದ ಸತ್ಕರ್ಮಗಳ ನಿಮಿತ್ತ ನಿಮಗೆ ವಾರೀಸು ಹಕ್ಕಾಗಿ ಲಭಿಸಲ್ಪಟ್ಟ ಸ್ವರ್ಗವಿದು.');
INSERT INTO "kannada" VALUES(43,73,'43:73','ನಿಮಗಿಲ್ಲಿ ಧಾರಾಳ ಹಣ್ಣು ಹಂಪಲುಗಳಿವೆ. ನೀವು ಅವುಗಳಿಂದ ತಿಣ್ಣುವಿರಿ.');
INSERT INTO "kannada" VALUES(43,74,'43:74','ಅಪರಾಧಿಗಳು ನರಕ ಶಿಕ್ಷೆಯಲ್ಲಿ ಖಂಡಿತ ಸ್ಥಿರವಾಸಿಗಳಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(43,75,'43:75','ಆ ಶಿಕ್ಷೆಯಲ್ಲಿ ಅವರಿಗೆ ಕಡಿತ ಲಭಿಸದು. ಅವರದರಲ್ಲಿ ನಿರಾಶರಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(43,76,'43:76','ನಾವು ಅವರನ್ನು ಅಕ್ರಮಿಸಿಲ್ಲ. ಬದಲಾಗಿ ಸ್ವತಃ ಅವರೇ ಅಕ್ರಮಿಗಳಾಗಿಬಿಟ್ಟರು.');
INSERT INTO "kannada" VALUES(43,77,'43:77','ಅವರು ಕೂಗಿ ಹೇಳುತ್ತಾರೆ; ``ಓ ಮಾಲಿಕ್, ತಮ್ಮ ಪ್ರಭು ನಮ್ಮನ್ನು ಮುಗಿಸಿ ಬಿಡುವಂತಾಗಲಿ! ಆಗ ಅವರು (ಮಾಲಿಕ್) ಹೇಳುತ್ತಾರೆ; ``ನೀವು (ಶಿಕ್ಷೆಯಲ್ಲೇ) ವಾಸಿಸುವವರಾಗಿರುವಿರಿ.');
INSERT INTO "kannada" VALUES(43,78,'43:78','ನಿಮಗೆ ನಾವು ಸತ್ಯವನ್ನು ತಂದು ಕೊಟ್ಟೆವು. ಆದರೆ, ನಿಮ್ಮ ಪೈಕಿ ಹೆಚ್ಚಿನವರು ಸತ್ಯವನ್ನು ತಿರಸ್ಕರಿಸುವವರಾದರು”.');
INSERT INTO "kannada" VALUES(43,79,'43:79','ಅವರೇನಾದರೂ ತೀರ್ಮಾನಿಸಿಕೊಂಡಿರುವರೋ? ಹಾಗಾದರೆ ನಾವೂ ಕೆಲವೊಂದನ್ನು ತೀರ್ಮಾನಿಸಿ ಕೊಳ್ಳುವವರಾಗಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(43,80,'43:80','ಅವರ ರಹಸ್ಯಗಳನ್ನೂ ಗೂಢಾಲೋಚನೆಯನ್ನೂ ನಾವು ಆಲಿಸುವುದಿಲ್ಲವೆಂದು ಅವರು ಗ್ರಹಿಸಿ ಕೊಂಡಿರುವರೇ? ವಾಸ್ತವದಲ್ಲಿ (ನಾವು ಆಲಿಸುತ್ತಿದ್ದೇವೆ). ನಮ್ಮ ದೂತರು (ಮಲಕುಗಳು) ಅವರ ಬಳಿಯಲ್ಲೇ ಬರೆಯುತ್ತಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(43,81,'43:81','(ಪ್ರವಾದಿಯರೇ) ತಾವು ಹೇಳಿರಿ; ``ಕಾರುಣ್ಯವಂತನಾದ ಅಲ್ಲಾಹನಿಗೆ ಒಂದು ಸಂತಾನವಿರುತ್ತಿದ್ದರೆ ಅದನ್ನು ಆರಾಧಿಸುವವರಲ್ಲಿ ನಾನು ಮೊದಲಿಗ ನಾಗುತ್ತಿದ್ದೆನು’’. ಂ.');
INSERT INTO "kannada" VALUES(43,82,'43:82','ಆಕಾಶಗಳ ಮತ್ತು ಭೂಮಿಯ ಪ್ರಭು ಹಾಗೂ ಅರ್ಶ್‍ನ ಒಡೆಯನಾದವನು, ಅವರು ವರ್ಣಿಸಿ ಹೇಳುವ ಎಲ್ಲಾ ದೋಷಗಳಿಂದ ಪರಿಶುದ್ಧನಾಗಿ ರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(43,83,'43:83','ಆದುದರಿಂದ ಅವರನ್ನು ಬಿಟ್ಟು ಬಿಡಿರಿ. ಅವರು ತಾಕೀತು ನೀಡಲಾಗುತ್ತಿರುವ ದಿನವನ್ನು ಕಂಡು ತಲುಪುವವರೆಗೂ ಅವರು (ದುರ್ಮಾರ್ಗದಲ್ಲಿ) ವಿಹರಿಸುತ್ತಾ ವಿನೋದಪಡುತ್ತಾ ಇರಲಿ!');
INSERT INTO "kannada" VALUES(43,84,'43:84','ಅವನು (ಅಲ್ಲಾಹು) ಆಕಾಶದಲ್ಲೂ ಅರಾಧ್ಯನು, ಭೂಮಿಯಲ್ಲೂ ಆರಾಧ್ಯನು. ಅವನು ತಂತ್ರಜ್ಞನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(43,85,'43:85','ಆಕಾಶಗಳು ಮತ್ತು ಭೂಮಿ ಹಾಗೂ ಅವುಗಳ ಮಧ್ಯೆ ಇರುವ ಪ್ರತಿಯೊಂದು ವಸ್ತುಗಳ ಆಧಿಪತ್ಯ ಯಾವನಿಗಿರುವುದೋ ಅವನು ಮಹೋನ್ನತನು. ಅಂತ್ಯ ಘಳಿಗೆಯ ಕುರಿತುಳ್ಳ ಅರಿವು ಅವನಿಗಿದೆ. ನೀವೆಲ್ಲರೂ ಅವನ ಬಳಿಗೇ ಮರಳಿಸಲ್ಪಡುವಿರಿ.');
INSERT INTO "kannada" VALUES(43,86,'43:86','ಅವನನ್ನು ಬಿಟ್ಟು ಅವರು ಯಾರನ್ನು ಆರಾಧಿಸುತ್ತಿರುವರೋ ಅವರು ಶಿಫಾರಸ್ಸಿನ ಅಧಿಕಾರವನ್ನು ಹೊಂದಿಲ್ಲ. ಅರಿವು ಉಳ್ಳವರಾಗಿ ಸತ್ಯದಲ್ಲಿ ಸಾಕ್ಷ್ಯವಹಿಸಿದವರ ಹೊರತು .');
INSERT INTO "kannada" VALUES(43,87,'43:87','ಅವನನ್ನು ಸೃಷ್ಟಿಸಿದವನು ಯಾರೆಂದು ತಾವು ಅವರಲ್ಲಿ ಕೇಳಿದರೆ ಅಲ್ಲಾಹನೆಂದು ಅವರು ಹೇಳುವರು. ಮತ್ತೆ ಅವರು (ಸತ್ಯದಿಂದ) ಹೇಗೆ ತಪ್ಪಿ ಹೋಗುತ್ತಿದ್ದಾರೆ? ');
INSERT INTO "kannada" VALUES(43,88,'43:88','``ನನ್ನ ಪ್ರಭೂ, ಈ ಕೂಟದವರು ವಿಶ್ವಾಸ ಹೊಂದದ ಜನತೆಯಾಗಿರುವರು’’ ಎಂಬ ಅವರ (ನಬಿಯರ) ಮಾತಿನ ಕುರಿತೂ (ಅಲ್ಲಾಹನಿಗೆ ಅರಿವಿದೆ)');
INSERT INTO "kannada" VALUES(43,89,'43:89','(ಪ್ರವಾದಿಯರೇ) ತಾವು ಅವರಿಂದ ದೂರ ಸರಿಯಿರಿ ಮತ್ತು ಸಲಾಮ್ ಎಂದು ಹೇಳಿರಿ. ಅವರಿಗೆ ಸದ್ಯದಲ್ಲೆ (ವಾಸ್ತವಿಕತೆ) ತಿಳಿದು ಬರುವುದು.');
INSERT INTO "kannada" VALUES(44,1,'44:1','ಹಾ ಮೀಮ್.');
INSERT INTO "kannada" VALUES(44,2,'44:2','ಸುವ್ಯಕ್ತವಾದ ಈ ಗ್ರಂಥದಾಣೆ!');
INSERT INTO "kannada" VALUES(44,3,'44:3','ನಿಶ್ಚಯವಾಗಿಯೂ ನಾವು ಇದನ್ನು ಒಂದು ಅನುಗ್ರಹೀತ ರಾತ್ರಿಯಲ್ಲಿ ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದೇವೆ . ನಾವು ಜನರಿಗೆ ಮುನ್ನೆಚ್ಚರಿಕೆ ನೀಡುವವರೇ ಆಗಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(44,4,'44:4','');
INSERT INTO "kannada" VALUES(44,5,'44:5','ಆ ರಾತ್ರಿಯಲ್ಲಿ ನಮ್ಮ ಅಪ್ಪಣೆಯಿಂದ ಸುಶಕ್ತ ವಾದ ಸಕಲ ವಿಷಯಗಳನ್ನು ಬೇರ್ಪಡಿಸಿ ವಿವರಿಸಲಾಗುತ್ತದೆ . ನಾವು ಸಂದೇಶವಾಹಕರನ್ನು ಕಳುಹಿಸುತ್ತಿರುವವರಾಗಿರುವೆವು.');
INSERT INTO "kannada" VALUES(44,6,'44:6','ನಿನ್ನ ಪ್ರಭುವಿನ ಕಾರುಣ್ಯವಾಗಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅವನೇ ಸರ್ವವನ್ನೂ ಆಲಿಸುವವನು ಮತ್ತು ಸರ್ವವನ್ನೂ ಅರಿಯುವವನು.');
INSERT INTO "kannada" VALUES(44,7,'44:7','ಆಕಾಶಗಳ ಹಾಗೂ ಭೂಮಿಯ ಮತ್ತು ಅವುಗಳ ನಡುವೆ ಇರುವ ಪ್ರತಿಯೊಂದು ವಸ್ತುವಿನ ಪ್ರಭು(ವಿನ ಕಾರುಣ್ಯವಾಗಿ). ನೀವು ದೃಢನಂಬಿಕೆಯುಳ್ಳವರಾಗಿದ್ದರೆ.');
INSERT INTO "kannada" VALUES(44,8,'44:8','ಅವನ ಹೊರತು ಬೇರೆ ಆರಾಧ್ಯರಿಲ್ಲ. ಅವನು ಜೀವಂತಗೊಳಿಸುತ್ತಾನೆ ಮತ್ತು ಮರಣ ಗೊಳಿಸುತ್ತಾನೆ. ಅವನು ನಿಮ್ಮ ಮತ್ತು ನಿಮಗಿಂತ ಮುಂಚೆ ಗತಿಸಿದ ಪೂರ್ವಜರ ಪ್ರಭು.');
INSERT INTO "kannada" VALUES(44,9,'44:9','ಆದರೆ ಅವರು ಸಂಶಯದಲ್ಲಿ ವಿನೋದವಾಡುತ್ತಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(44,10,'44:10','ಹೀಗಾಗಿ ಆಕಾಶವು ಸ್ಪಷ್ಟವಾದ ಹೊಗೆಯೊಂದಿಗೆ ಬರುವ ದಿನವನ್ನು ನಿರೀಕ್ಷಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(44,11,'44:11','ಅದು ಜನರನ್ನು ಆವರಿಸಿಕೊಳ್ಳುತ್ತದೆ. ಇದು ಒಂದು ಯಾತನಾಮಯ ಶಿಕ್ಷೆಯಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(44,12,'44:12','(ಅವರು ಹೇಳುವರು) “ನಮ್ಮ ಪ್ರಭು! ನಮ್ಮಿಂದ ಈ ಶಿಕ್ಷೆಯನ್ನು ತೊಲಗಿಸು. ನಾವು ಖಂಡಿತ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಾಗುತ್ತೇವೆ”.');
INSERT INTO "kannada" VALUES(44,13,'44:13','ಅವರಿಗೆ ಹೇಗೆ ಬೋಧೋದಯ ಸಂಭವಿಸಿತು? ಅವರ ಬಳಿಗೆ (ಕಾರ್ಯಗಳನ್ನು) ವ್ಯಕ್ತಗೊಳಿ ಸುವ ಒಬ್ಬ ದೂತರು ಬಂದಿದ್ದರು.');
INSERT INTO "kannada" VALUES(44,14,'44:14','ಆದರೂ ಇವರು ಅವರಿಂದ ವಿಮುಖರಾದರು. `ಇವನೊಬ್ಬ ಕಲಿಸಲ್ಪಟ್ಟವನೂ ಹುಚ್ಚನೂ ಆಗಿರುವನು’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(44,15,'44:15','ನಾವು ಶಿಕ್ಷೆಯನ್ನು ಸ್ವಲ್ಪ ಕಾಲ ತೊಲಗಿಸಿ ಬಿಡುತ್ತೇವೆ. ಆದರೆ (ಸತ್ಯನಿಷೇಧಿಗಳೇ!) ನೀವು (ಹಳೆಯ ಅವಸ್ಥೆಗೆ) ಮರಳಿ ಹೋಗುವಿರಿ.');
INSERT INTO "kannada" VALUES(44,16,'44:16','ನಾವು ಭಾರೀ ಹಿಡಿತವನ್ನು ಹಿಡಿಯುವ ದಿನವೇ ನಾವು ನಿಮ್ಮ ಮೇಲೆ ಸೇಡು ತೀರಿಸಲಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(44,17,'44:17','ಇವರಿಗಿಂತ ಮುಂಚೆ ಫಿರ್‍ಔನನ ಜನಾಂಗವನ್ನೂ ನಾವು ಪರೀಕ್ಷಿಸಿದ್ದೆವು. ಅವರ ಬಳಿಗೆ ಓರ್ವ ಸನ್ಮಾನ್ಯ ದೂತರು ಬಂದಿದ್ದರು.');
INSERT INTO "kannada" VALUES(44,18,'44:18','(ದೂತರು ಹೇಳಿದರು) `ಅಲ್ಲಾಹನ ದಾಸರನ್ನು ನನ್ನ ವಶಕ್ಕೆ ಒಪ್ಪಿಸಿ ಬಿಡಿರಿ. ನಾನು ನಿಮ್ಮ ಕಡೆಗಿರುವ ಓರ್ವ ಪ್ರಾಮಾಣಿಕ ದೂತನಾಗಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(44,19,'44:19','ಅಲ್ಲಾಹನ ವಿರುದ್ಧ ನೀವು ದರ್ಪ ತೋರದಿರಿ. ನಾನು ಸುಸ್ಪಷ್ಟ ಪುರಾವೆಯೊಂದಿಗೆ ನಿಮ್ಮನ್ನು ಸಮೀಪಿಸಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(44,20,'44:20','ನೀವು ನನ್ನನ್ನು ಕಲ್ಲೆಸೆಯದಿರಲು ನಾನು ನನ್ನ ಮತ್ತು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಅಭಯ ಪಡೆದಿರುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(44,21,'44:21','ನೀವು ನನ್ನಲ್ಲಿ ವಿಶ್ವಾಸ ಹೊಂದದಿದ್ದಲ್ಲಿ ನನ್ನನ್ನು ಬಿಟ್ಟು ನೀವು ದೂರ ಸರಿಯಿರಿ.');
INSERT INTO "kannada" VALUES(44,22,'44:22','ಕೊನೆಗೆ ಅವರು ತಮ್ಮ ಪ್ರಭುವಿನಲ್ಲಿ ಪ್ರಾರ್ಥಿಸಿದರು; `ಇವರೊಂದು ಅಪರಾಧಿ ಜನಾಂಗದ ವರಾಗಿದ್ದಾರೆ’ (ಅವರ ಮೇಲೆ ಆವಶ್ಯ ಕ್ರಮ ಕೈಗೊಳ್ಳು).');
INSERT INTO "kannada" VALUES(44,23,'44:23','(ಆಗ ಅಲ್ಲಾಹು ಆದೇಶಿಸಿದನು) `ಹಾಗಿದ್ದರೆ ನನ್ನ ದಾಸರನ್ನು (ಇಸ್ರಾಈಲ್‍ರನ್ನು) ಕರೆದುಕೊಂಡು ನೀವು ರಾತ್ರಿಯಲ್ಲಿ ಪ್ರಯಾಣ ಬೆಳೆಸಿರಿ. ನಿಮ್ಮನ್ನು (ಶತ್ರುಗಳಿಂದ) ಬೆನ್ನಟ್ಟಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(44,24,'44:24','ಸಮುದ್ರವನ್ನು ಶಾಂತವಾಗಿ ಇರಲು ಬಿಡಿರಿ. ಅವರು ಮುಳುಗಿ ನಾಶವಾಗುವ ಒಂದು ಸೈನ್ಯವೇ ಆಗಿದ್ದಾರೆ .');
INSERT INTO "kannada" VALUES(44,25,'44:25','');
INSERT INTO "kannada" VALUES(44,26,'44:26','ಅದೆಷ್ಟೋ ಉದ್ಯಾನಗಳನ್ನೂ ಚಿಲುಮೆ ಗಳನ್ನೂ ಹೊಲಗಳನ್ನೂ ಭವ್ಯ ನಿವಾಸಗಳನ್ನೂ ಅವರು ಬಿಟ್ಟು ಹೋದರು.');
INSERT INTO "kannada" VALUES(44,27,'44:27','ಅವರು ಆಹ್ಲಾದಪೂರ್ವಕ ಸುಖಿಸುತ್ತಿದ್ದ ಎಷ್ಟೋ ಭೋಗ ಸಾಮಗ್ರಿಗಳು!');
INSERT INTO "kannada" VALUES(44,28,'44:28','(ಅವರ ಅಂತ್ಯ) ಹಾಗೆ ಸಂಭವಿಸಿತು. ನಾವು ಬೇರೊಂದು ಜನತೆಯನ್ನು ಈ ವಸ್ತುಗಳ ವಾರಿಸುದಾರರಾಗಿ ಮಾಡಿದೆವು .');
INSERT INTO "kannada" VALUES(44,29,'44:29','ಆಗ ಆಕಾಶವಾಗಲಿ-ಭೂಮಿಯಾಗಲಿ ಅವರಿಗಾಗಿ ರೋದಿಸಲಿಲ್ಲ. ಒಂದಿಷ್ಟು ಕಾಲಾವಕಾಶವೂ ಅವರಿಗೆ ಲಭಿಸಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(44,30,'44:30','');
INSERT INTO "kannada" VALUES(44,31,'44:31','ಇಸ್ರಾಈಲ್ ಸಂತತಿಗಳನ್ನು ನಿಂದನೀಯ ಶಿಕ್ಷೆಯಿಂದ ಅಂದರೆ ಫಿರ್‍ಔನ್‍ನಿಂದ ನಾವು ರಕ್ಷಿಸಿದೆವು. ಅವನು ನಿಜಕ್ಕೂ ಅತಿಕ್ರಮಿಗಳಲ್ಲಿ ಒಳಪಟ್ಟ ಅಹಂಕಾರಿಯಾಗಿದ್ದನು.');
INSERT INTO "kannada" VALUES(44,32,'44:32','(ಇಸ್ರಾಈಲ್ ಜನಾಂಗದ ಅವಸ್ಥೆಯನ್ನು) ತಿಳಿದುಕೊಂಡೇ ನಾವು ಅವರನ್ನು ಜಗತ್ತಿನ ಇತರ ಜನತೆಗಿಂತ ಉತ್ಕøಷ್ಟರಾಗಿ ಆಯ್ಕೆ ಮಾಡಿದೆವು.');
INSERT INTO "kannada" VALUES(44,33,'44:33','ಸುಸ್ಪಷ್ಟ ಪರೀಕ್ಷೆಯಿದ್ದ ಕೆಲವು ನಿದರ್ಶನಗಳನ್ನೂ ನಾವು ಅವರಿಗೆ (ತೋರಿಸಿ)ಕೊಟ್ಟೆವು.');
INSERT INTO "kannada" VALUES(44,34,'44:34','');
INSERT INTO "kannada" VALUES(44,35,'44:35','ಅವರು ಹೇಳುತ್ತಾರೆ; ‘ನಮ್ಮ ಪ್ರಥಮ ಬಾರಿ ಯ ಮೃತ್ಯುವಿನ ಹೊರತು ಬೇರೇನೂ ಇಲ್ಲ. ನಾವು ಪುನಃ ಎಬ್ಬಿಸಲ್ಪಡುವವರೂ ಅಲ್ಲ’.');
INSERT INTO "kannada" VALUES(44,36,'44:36','ನೀವು ಸತ್ಯವಾದಿಯಾಗಿದ್ದರೆ ನಮ್ಮ ಪೂರ್ವಜರನ್ನು ಎಬ್ಬಿಸಿ ತನ್ನಿರಿ’.');
INSERT INTO "kannada" VALUES(44,37,'44:37','ಇವರೋ ಉತ್ತಮರು? ಅಥವಾ ತುಬ್ಬಅï ಜನಾಂಗ ಮತ್ತು ಅದಕ್ಕಿಂತ ಹಿಂದಿನವರು ಉತ್ತಮರೋ? ಏಕೆಂದರೆ ನಾವು ಅವರನ್ನು ನಾಶಗೊಳಿಸಿದೆವು. ಅವರು ಅಪರಾಧಿಗಳಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(44,38,'44:38','ಆಕಾಶಗಳನ್ನೂ-ಭೂಮಿಯನ್ನೂ ಅವುಗಳ ನಡುವೆ ಇರುವ ವಸ್ತುಗಳನ್ನೂ ನಾವು ವಿನೋದವಾಗಿ ಸೃಷ್ಟಿಸಿದ್ದಲ್ಲ.');
INSERT INTO "kannada" VALUES(44,39,'44:39','ನಾವು ಅವೆರಡನ್ನು ಸತ್ಯಪೂರ್ಣವಾಗಿ ಸೃಷ್ಟಿಸಿದ್ದೇವೆ. ಆದರೆ ಅವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಅರಿಯುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(44,40,'44:40','ಅವರೆಲ್ಲರ ನಿಶ್ಚಿತ ಸಮಯವು ಆ ನಿರ್ಣಾಯಕ ತೀರ್ಪಿನ ದಿನವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(44,41,'44:41','ಯಾವನೇ ಬಂಧು ತನ್ನ ಇನ್ನೊಬ್ಬ ಬಂಧುವಿಗೆ ಸ್ವಲ್ಪವೂ ಪ್ರಯೋಜನ ಕೊಡದ ಮತ್ತು ಅವರಿಗೆ ಯಾವ ಸಹಾಯವೂ ಲಭಿಸದ ದಿನ.');
INSERT INTO "kannada" VALUES(44,42,'44:42','ಅಲ್ಲಾಹನು ಕೃಪೆ ತೋರಿದವರ ಹೊರತು . ಅವನು ಮಹಾ ಪ್ರತಾಪಿಯೂ, ದಯಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(44,43,'44:43','');
INSERT INTO "kannada" VALUES(44,44,'44:44','ಖಂಡಿತ ಝಖ್ಖೂಮ್ ವೃಕ್ಷವು (ನರಕದಲ್ಲಿ) ಪಾಪಿಗಳ ಆಹಾರ.');
INSERT INTO "kannada" VALUES(44,45,'44:45','ಅದು ಉರಿಲೋಹದಂತೆ. ಅದು ಅವರ ಹೊಟ್ಟೆಯೊಳಗೆ ಕುದಿಯುತ್ತಿರುವುದು ಬಿಸಿ ನೀರು ಕುದಿಯುತ್ತಿರುವಂತೆ.');
INSERT INTO "kannada" VALUES(44,46,'44:46','');
INSERT INTO "kannada" VALUES(44,47,'44:47','ಅವನನ್ನು ಹಿಡಿಯಿರಿ, ಆಮೇಲೆ ನರಕದ ಮಧ್ಯಕ್ಕೆ ಎಳೆದೊಯ್ಯಿರಿ.');
INSERT INTO "kannada" VALUES(44,48,'44:48','ಅನಂತರ ಕುದಿಯುವ ಬಿಸಿ ನೀರಿನ ಶಿಕ್ಷೆಯನ್ನು ಅವನ ತಲೆಯ ಮೇಲೆ ಸುರಿದು ಬಿಡಿರಿ.');
INSERT INTO "kannada" VALUES(44,49,'44:49','“ನೀನು ಇದರ ರುಚಿಯನ್ನು ಸವಿದುಕೋ, ಖಂಡಿತಾ ನೀನೊಬ್ಬ ಪ್ರತಾಪಿಯೂ, ಮರ್ಯಾದಸ್ಥನೂ ಆಗಿದ್ದೀಯಲ್ಲ?”');
INSERT INTO "kannada" VALUES(44,50,'44:50','ನೀವೆಲ್ಲ ಸಂದೇಹಪಡುತ್ತಿದ್ದ ಶಿಕ್ಷೆ ಖಂಡಿತಾ ಇದುವೇ ಆಗಿದೆ.');
INSERT INTO "kannada" VALUES(44,51,'44:51','ನಿಜವಾಗಿಯೂ ಭಕ್ತರು ಸುರಕ್ಷಿತ ತಾಣದಲ್ಲಿರುವರು.');
INSERT INTO "kannada" VALUES(44,52,'44:52','ಕೆಲವು ಉದ್ಯಾನಗಳಲ್ಲೂ ಚಿಲುಮೆಗಳಲ್ಲೂ.');
INSERT INTO "kannada" VALUES(44,53,'44:53','ತೆಳು ರೇಶ್ಮೆ ಹಾಗೂ ದೊರಗಿನ ರೇಶ್ಮೆ ಬಟ್ಟೆಗಳನ್ನು ಧರಿಸಿಕೊಂಡು ಎದುರು ಬದುರಾಗಿ (ಕುಳಿತಿರುವರು).');
INSERT INTO "kannada" VALUES(44,54,'44:54','ಅವರ ಅವಸ್ಥೆ ಹಾಗಿರುವುದು. ಬಿಳಿ ಬಣ್ಣದ, ವಿಶಾಲ ನಯನಗಳುಳ್ಳ ತರುಣಿಯರನ್ನು ನಾವು ಅವರಿಗೆ ವಿವಾಹ ಮಾಡಿಸುವೆವು.');
INSERT INTO "kannada" VALUES(44,55,'44:55','ಅಲ್ಲಿ ಅವರು ನಿಶ್ಚಿಂತರಾಗಿ ಎಲ್ಲ ತರದ ರುಚಿ ಕರವಾದ ಫಲ ವಸ್ತುಗಳನ್ನು ಅವಶ್ಯಪಡುವರು.');
INSERT INTO "kannada" VALUES(44,56,'44:56','(ಭೂಲೋಕದಲ್ಲಿ) ಮೊದಲು ಬಂದ ಮರಣದ ಹೊರತು ಅಲ್ಲಿ ಮರಣದ ರುಚಿಯನ್ನು ಅವರು ಸವಿಯಲಾರರು. ಅಲ್ಲಾಹನು ಅವರನ್ನು ನರಕ ಶಿಕ್ಷೆಯಿಂದ ರಕ್ಷಿಸುವನು.');
INSERT INTO "kannada" VALUES(44,57,'44:57','ತಮ್ಮ ಪ್ರಭುವಿನ ಮಹಾ ಔದಾರ್ಯದಿಂದ (ಇವೆಲ್ಲವೂ ಲಭಿಸುವುದು). ಅದುವೇ ಅತೀ ದೊಡ್ಡ ಯಶಸ್ಸು.');
INSERT INTO "kannada" VALUES(44,58,'44:58','(ದೂತರೇ,) ಅವರು ಯೋಚಿಸಿ ಗ್ರಹಿಸಲೆಂದು ನಾವು ಇದನ್ನು (ಖುರ್‍ಆನನ್ನು) ತಮ್ಮ ಭಾಷೆಯಲ್ಲಿ ಸರಳಗೊಳಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(44,59,'44:59','ಇನ್ನು ತಾವೂ ಕಾಯಿರಿ, ಅವರೂ ಕಾಯುತ್ತಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(45,1,'45:1','ಹಾಮೀಮ್.');
INSERT INTO "kannada" VALUES(45,2,'45:2','ಈ ಗ್ರಂಥದ ಅವತೀರ್ಣವು ಪ್ರಬಲನೂ ಯುಕ್ತಜ್ಞನೂ ಆದ ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ಬಂದಿದೆ.');
INSERT INTO "kannada" VALUES(45,3,'45:3','ನಿಶ್ಚಯವಾಗಿಯೂ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಆಕಾಶಗಳಲ್ಲೂ ಭೂಮಿಯಲ್ಲೂ ಅನೇಕ ದೃಷ್ಟಾಂತಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(45,4,'45:4','ದೃಢನಂಬಿಕೆಯುಳ್ಳ ಜನರಿಗೆ ನಿಮ್ಮ ಸೃಷ್ಟಿಯಲ್ಲೂ (ಭೂಮಿಯ ಮೇಲೆ) ಅಲ್ಲಾಹನು ಹಬ್ಬಿಸುತ್ತಿರುವ, ಪ್ರಾಣಿಗಳಲ್ಲೂ ಹಲವು ದೃಷ್ಟಾಂತಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(45,5,'45:5','ರಾತ್ರೆ ಹಗಲುಗಳ ಆವರ್ತನೆಯಲ್ಲೂ ಅಲ್ಲಾಹನು ಆಕಾಶದಿಂದ ಜೀವನಾಧಾರವನ್ನಿಳಿಸಿ ಆ ಮೂಲಕ ಭೂಮಿಯನ್ನು ಅದರ ನಿರ್ಜೀವಾವಸ್ಥೆಯ ಬಳಿಕ ಜೀವಂತಗೊಳಿಸುವುದರಲ್ಲೂ ಮತ್ತು ಮಾರುತಗಳ ಗತಿ ನಿಯಂತ್ರಣದಲ್ಲೂ ಯೋಚಿಸುವ ಜನರಿಗೆ ಅನೇಕ ನಿದರ್ಶನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(45,6,'45:6','ಅವು ಅಲ್ಲಾಹನ ನಿದರ್ಶನಗಳಾಗಿದ್ದು ಅವುಗಳನ್ನು ನಾವು ಸತ್ಯ ಪ್ರಕಾರ ತಮ್ಮ ಮೇಲೆ ಓದಿ ಕೇಳಿಸುತ್ತೇವೆ. ಹೀಗಿರುತ್ತಾ ಅಲ್ಲಾಹನ ಹಾಗೂ ಅವನ ಪುರಾವೆಗಳ ಬಳಿಕ ಇನ್ನಾವ ವಾರ್ತೆಯ ಮೇಲೆ ಅವರು (ಜನರು) ವಿಶ್ವಾಸವಿರಿಸುವರು?');
INSERT INTO "kannada" VALUES(45,7,'45:7','ಸುಳ್ಳ ಹಾಗೂ ಮಹಾಪಾಪಿಯಾದ ಪ್ರತಿ ಯೊಬ್ಬನಿಗೆ ವಿನಾಶವಿದೆ. ');
INSERT INTO "kannada" VALUES(45,8,'45:8','ಅಲ್ಲಾಹನ ಸೂಕ್ತಗಳನ್ನು ಆತನ ಮೇಲೆ ಓದಿ ಹೇಳಲಾಗುತ್ತದೆ. ಅವನು ಅದನ್ನು ಆಲಿಸುತ್ತಾನೆ. ಅನಂತರ ಅವನು ಕೇಳಲೇ ಇಲ್ಲವೆಂಬಂತೆ ಅಹಂಕಾರಿಯಾಗಿರುತ್ತ ನೆಲೆಗೊಳ್ಳುತ್ತಾನೆ. ಹೀಗಾಗಿ ಅವನಿಗೆ ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆಯ ಸುವಾರ್ತೆಯನ್ನು ತಿಳಿಸಿ ಬಿಡಿರಿ.');
INSERT INTO "kannada" VALUES(45,9,'45:9','ನಮ್ಮ ಸೂಕ್ತಗಳ ಪೈಕಿ ಏನಾದರೊಂದು ವಿಷಯ ಅವನಿಗೆ ತಿಳಿದಾಗ ಅವನು ಅದನ್ನು ಗೇಲಿಯ ವಿಷಯವನ್ನಾಗಿ ಮಾಡುತ್ತಾನೆ. ಅಂತಹವರಿಗೆ ನಿಂದನೀಯ ಶಿಕ್ಷೆಯಿದೆ.');
INSERT INTO "kannada" VALUES(45,10,'45:10','ಅವರ ಹಿಂದೆ ನರಕವಿದೆ. ಅವರು ಸಂಪಾದಿಸಿ ಟ್ಟದ್ದಾಗಲಿ, ಅವರು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ತಮ್ಮ ರಕ್ಷಕ ಮಿತ್ರರಾಗಿ ಮಾಡಿ ಕೊಂಡಿರುವವರಾಗಲಿ ಅವರಿಗೆ (ನರಕಯಾತನೆ ಯಿಂದ ರಕ್ಷಿಸಲು) ಯಾವ ಪ್ರಯೋಜನವನ್ನೂ ಮಾಡಲಾರರು. ಅವರಿಗೆ ಘೋರ ಶಿಕ್ಷೆಯಿದೆ.');
INSERT INTO "kannada" VALUES(45,11,'45:11','ಇದು (ಖುರ್‍ಆನ್) ಮಾರ್ಗದರ್ಶನವಾಗಿದೆ. ತಮ್ಮ ಪ್ರಭುವಿನ ಸೂಕ್ತಗಳನ್ನು ನಿಷೇಧಿಸಿದವರಿಗೆ ತೀವ್ರ ವೇದನಾಯುಕ್ತವಾದ ಶಿಕ್ಷೆಯಿದೆ.');
INSERT INTO "kannada" VALUES(45,12,'45:12','ನಿಮಗೆ ಸಮುದ್ರವನ್ನು ಅಧೀನಪಡಿಸಿಕೊಟ್ಟವನು ಅಲ್ಲಾಹನೇ. ಅವನ ಅಪ್ಪಣೆಯಂತೆ ಅದರಲ್ಲಿ ನಾವೆಗಳು ಚಲಿಸುವಂತೆಯೂ ಅವನ ಅನುಗ್ರಹ ದಿಂದ ನೀವು ಅರಸುವಂತೆಯೂ ಕೃತಜ್ಞರಾಗು ವಂತೆಯೂ ಆಗಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(45,13,'45:13','ಆಕಾಶಗಳಲ್ಲಿರುವ ಮತ್ತು ಭೂಮಿಯಲ್ಲಿರುವ ಸಕಲ ವಸ್ತುಗಳನ್ನು ತನ್ನ ವತಿಯಿಂದ ಅವನು ನಿಮಗಾಗಿ ಅಧೀನಪಡಿಸಿಕೊಟ್ಟನು. ಚಿಂತಿಸುವ ಜನರಿಗೆ ಇದರಲ್ಲಿ ಧಾರಾಳ ನಿದರ್ಶನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(45,14,'45:14','(ನಬಿಯರೇ) ಅಲ್ಲಾಹನ (ಶಿಕ್ಷೆಯ) ದಿನಗಳ (ಸಂಭವ್ಯತೆಯ)ನ್ನು ಭಯದಿಂದ ನಿರೀಕ್ಷಿಸದ (ಸತ್ಯನಿಷೇಧಿ) ಜನರನ್ನು ಕ್ಷಮಿಸುವಂತೆ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಹೇಳಿರಿ. (ಏಕೆಂದರೆ) ಒಂದು ಜನಾಂಗಕ್ಕೆ ಅವರೆಸಗಿದ ಕ್ಷಮೆಯ ಫಲವನ್ನು ಅಲ್ಲಾಹನು ನೀಡುವಂತಾಗಲು.');
INSERT INTO "kannada" VALUES(45,15,'45:15','ಯಾರಾದರೂ ಸತ್ಕರ್ಮ ಮಾಡಿದರೆ ಅದು ಅವನ ಹಿತಕ್ಕೆ. ದುಷ್ಕರ್ಮವೆಸಗಿದರೆ ಅದರ ಪಾಪ ಅವನಿಗೆ. ಅನಂತರ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಗೇ ನೀವು ಮರಳಲ್ಪಡುವಿರಿ.');
INSERT INTO "kannada" VALUES(45,16,'45:16','ಇಸ್ರಾಈಲ್ ಸಂತತಿಗಳಿಗೆ ವೇದಗ್ರಂಥ, (ತೌರಾತ್) ವಿಜ್ಞಾನ ಮತ್ತು ಪ್ರವಾದಿತ್ವವನ್ನು ನೀಡಿದ್ದೆವು. ಉತ್ತಮ ವಸ್ತುಗಳಿಂದ ನಾವು ಅವರಿಗೆ ಆಹಾರ ಕರುಣಿಸಿದೆವು. ಜಗತ್ತಿನವರಿಗಿಂತ ಅವರಿಗೆ ನಾವು ಶ್ರೇಷ್ಠತೆ ದಯಪಾಲಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(45,17,'45:17','ಅವರಿಗೆ ನಾವು ಧರ್ಮದ ವಿಷಯದಲ್ಲಿ ಸುವ್ಯಕ್ತ ನಿದರ್ಶನಗಳನ್ನು ಕೊಟ್ಟೆವು. ಆದರೆ ಅವರು ಭಿನ್ನಾಭಿಪ್ರಾಯ ತಾಳಿದ್ದು ಜ್ಞಾನ ಬಂದ ಬಳಿಕವಾಗಿತ್ತು. ಅದು ಪರಸ್ಪರರ ಜಿದ್ದಿನ ನಿಮಿತ್ತವಾಗಿತ್ತು. ಅವರು ಭಿನ್ನಾಭಿಪ್ರಾಯ ತಾಳುತ್ತಾ ಬಂದಿರುವ ವಿಷಯಗಳಲ್ಲಿ ಪುನರುತ್ಥಾನದ ದಿನ ಅವರ ನಡುವೆ ತಮ್ಮ ಪ್ರಭು ವಿಧಿ ನೀಡುವನು.');
INSERT INTO "kannada" VALUES(45,18,'45:18','(ಪೈಗಂಬರರೇ,) ಅನಂತರ ತಮ್ಮನ್ನು ನಾವು ಧರ್ಮದ ವಿಷಯದಲ್ಲಿ ಒಂದು ಸುಸ್ಪಷ್ಟ ಮಾರ್ಗದಲ್ಲಿ (ಧರ್ಮಶಾಸ್ತ್ರದಲ್ಲಿ) ಇರಿಸಿದ್ದೇವೆ. ಆದುದರಿಂದ ತಾವು ಅದನ್ನು ಅನುಸರಿಸಿ. ಅಜ್ಞಾನಿಗಳ ತನ್ನಿಷ್ಟಗಳನ್ನು ಅನುಸರಿಸಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(45,19,'45:19','ಅಲ್ಲಾಹನಿಂದ ಯಾವ ಕಾರ್ಯಕ್ಕೂ ಅವರು ತಮಗೆ ಎಳ್ಳಷ್ಟೂ ಪ್ರಯೋಜನವಾಗಲಾರರು. ಅಕ್ರಮಿಗಳು ಪರಸ್ಪರ ಮಿತ್ರರು. ಅಲ್ಲಾಹನು ಧರ್ಮನಿಷ್ಟರ ಮಿತ್ರನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(45,20,'45:20','ಇದು ಮನುಷ್ಯರ ಕಣ್ತೆರೆಸುವ ದಿವ್ಯಜ್ಞಾನ ಮತ್ತು ದೃಢವಿಶ್ವಾಸವುಳ್ಳವರಿಗೆ ಸನ್ಮಾರ್ಗ ದರ್ಶನವೂ ಕೃಪೆಯೂ ಆಗಿದೆ.');
INSERT INTO "kannada" VALUES(45,21,'45:21','ಅದಲ್ಲ, ದುಷ್ಕರ್ಮಗಳನ್ನೆಸಗಿದವರು ಭಾವಿಸಿರುವರೇ? ನಾವು ಅವರನ್ನು ಸತ್ಯವಿಶ್ವಾಸವನ್ನಿರಿಸಿ ಸತ್ಕರ್ಮವೆಸಗಿದವರಿಗೆ ಸಮಾನರಾಗಿ ಅಂದರೆ ಅವರಿಬ್ಬರ ಜೀವನ ಹಾಗೂ ಮರಣಗಳು ಸಮಾನ ರೂಪದಲ್ಲಿ ಮಾಡುತ್ತೇವೆಂದು . ಅವರು ಮಾಡುವ ತೀರ್ಮಾನ ಬಹಳ ಕೆಟ್ಟದ್ದೇ ಸರಿ.');
INSERT INTO "kannada" VALUES(45,22,'45:22','ಆಕಾಶಗಳನ್ನೂ ಭೂಮಿಯನ್ನೂ ಅಲ್ಲಾಹನು ಸತ್ಯಪೂರ್ಣವಾಗಿ ಸೃಷ್ಟಿಸಿರುತ್ತಾನೆ. ಅದು ಪ್ರತಿ ಯೊಬ್ಬ ವ್ಯಕ್ತಿಗೆ ತನ್ನ ದುಡಿಮೆಯ ಪ್ರತಿಫಲ ನೀಡಲಿಕ್ಕಾಗಿ, ಖಂಡಿತ ಅವರು ದ್ರೋಹಿಸಲ್ಪಡಲಾರರು.');
INSERT INTO "kannada" VALUES(45,23,'45:23','ತನ್ನ ಸ್ವೇಚ್ಚೆಯನ್ನು ತನ್ನ ದೇವರಾಗಿ ಮಾಡಿ ಕೊಂಡವನನ್ನು ನೀವು ಕಂಡಿರಾ? ಅಲ್ಲಾಹನು ಅರಿತುಕೊಂಡೇ ಅವನನ್ನು ದಾರಿ ತಪ್ಪಿಸಿದನು. ಅವನ ಹೃದಯ ಹಾಗೂ ಕಿವಿಗಳಿಗೆ ಮುದ್ರೆಯನ್ನು ಒತ್ತಿ ಬಿಟ್ಟನು. ಮತ್ತು ಅವನ ದೃಷ್ಟಿಗಳ ಮೇಲೆ ತೆರೆಯೆಳೆದುಬಿಟ್ಟನು. ಅಲ್ಲಾಹನ ಬಳಿಕ ಅವನಿಗೆ ಸನ್ಮಾರ್ಗ ನೀಡಲು ಇನ್ನಾರಿದ್ದಾರೆ? ಹೀಗಿರಲು ನೀವು ಯೋಚಿಸಿ ಗ್ರಹಿಸುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(45,24,'45:24','ಅವರು ಹೇಳಿದರು; ``ಜೀವನವೆಂದರೆ ನಮ್ಮ ಈ ಲೋಕದ ಜೀವನ ಮಾತ್ರ. ನಾವು ಸಾಯುತ್ತೇವೆ ಮತ್ತು ಬದುಕುತ್ತೇವೆ. ಕಾಲದ ಹೊರತು ನಮ್ಮನ್ನು ನಾಶಗೊಳಿಸುವುದಿಲ್ಲ’’. (ವಾಸ್ತವದಲ್ಲಿ) ಅವರಿಗೆ ಅದರ ಕುರಿತು ಯಾವ ಜ್ಞಾನವೂ ಇಲ್ಲ. ಅವರು ಕೇವಲ ಊಹಿಸುತ್ತಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(45,25,'45:25','ನಮ್ಮ ಸೂಕ್ತಗಳನ್ನು ವ್ಯಕ್ತವಾಗಿ ಅವರಿಗೆ ಓದಿ ಕೇಳಿಸಲಾದರೆ ಅವರ ನ್ಯಾಯವಾದವು `ನೀವು ಸತ್ಯವಂತರಾಗಿದ್ದರೆ ನಮ್ಮ ಪೂರ್ವಜರನ್ನು ಎಬ್ಬಿಸಿ ತನ್ನಿರಿ’ ಎಂದು ಹೇಳುವುದು ಮಾತ್ರ ವಾಗಿತ್ತು.');
INSERT INTO "kannada" VALUES(45,26,'45:26','ತಾವು ಹೇಳಿರಿ;-ಅಲ್ಲಾಹನು ನಿಮ್ಮನ್ನು ಬದು ಕಿಸುತ್ತಾನೆ. ಅನಂತರ ಅವನು ನಿಮಗೆ ಮರಣ ಕೊಡುತ್ತಾನೆ. ಆ ಬಳಿಕ ಪುನರುತ್ಥಾನದ ದಿನ ಅವನು ನಿಮ್ಮನ್ನು (ಜೀವಂತಗೊಳಿಸಿ) ಒಟ್ಟುಗೂಡಿಸುವನು. ಅದರಲ್ಲಿ ಯಾವ ಸಂದೇಹವೂ ಇಲ್ಲ. ಆದರೆ ಹೆಚ್ಚಿನವರು ತಿಳಿಯುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(45,27,'45:27','ಆಕಾಶಗಳ ಮತ್ತು ಭೂಮಿಯ ಅಧಿಪತ್ಯ ಅಲ್ಲಾಹನಿಗೇ ಆಗಿದೆ. ಪುನರುತ್ಥಾನದ ಘಳಿಗೆ ಬರುವ ದಿನದಂದು ಅಸತ್ಯವಾದಿಗಳು ನಷ್ಟಕ್ಕೊಳಗಾಗುವ ದಿನ.');
INSERT INTO "kannada" VALUES(45,28,'45:28','(ಅಂದು) ಪ್ರತಿಯೊಂದು ಸಮುದಾಯವನ್ನು ಮೊಣಕಾಲೂರಿ ಬಿದ್ದ ಸ್ಥಿತಿಯಲ್ಲಿ ತಾವು ಕಾಣುವಿರಿ. ಪ್ರತಿಯೊಂದು ಸಮುದಾಯವು ತಮ್ಮ ಕರ್ಮ ಪತ್ರವನ್ನು ನೋಡಲಿಕ್ಕಾಗಿ ಕರೆಯಲ್ಪಡುವುದು. (ಅವರೊಡನೆ ಹೇಳಲಾಗುವುದು.) ಇಂದು ನಿಮಗೆ ನೀವು ಮಾಡುತ್ತಿದ್ದ ಕರ್ಮಗಳ ಪ್ರತಿಫಲವನ್ನು ನೀಡಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(45,29,'45:29','ಇದು ನಾವು ದಾಖಲಿಸಿದ ಕರ್ಮಪತ್ರವಾಗಿದ್ದು ಇದು ನಿಮ್ಮೆದುರು ಸತ್ಯಸಂಧವಾಗಿ ಮಾತನಾಡುತ್ತದೆ. ನೀವು ಮಾಡುತ್ತಿದ್ದ ಎಲ್ಲವನ್ನು ನಾವು ಬರೆಸುತ್ತಲೇ ಇದ್ದೆವು.');
INSERT INTO "kannada" VALUES(45,30,'45:30','ಅಂದ ಮೇಲೆ ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿ ಸತ್ಕರ್ಮ ಮಾಡುತ್ತಿದ್ದವರನ್ನು ಅವರ ಪ್ರಭು ತನ್ನ ಕೃಪೆಯೊಳಗೆ ಪ್ರವೇಶಗೊಳಿಸುವನು. ಇದುವೇ ಸುವ್ಯಕ್ತ ವಿಜಯ.');
INSERT INTO "kannada" VALUES(45,31,'45:31','ಆದರೆ ಸತ್ಯನಿಷೇಧಿಗಳೊಡನೆ `ನನ್ನ ಸೂಕ್ತ ಗಳನ್ನು ನಿಮಗೆ ಓದಿ ಹೇಳಲಾಗುತ್ತಿರಲಿಲ್ಲವೇ? ಆದರೆ, ನೀವು ಅಹಂಕಾರಪಟ್ಟಿರಿ ಮತ್ತು ಅಪರಾಧಿಗಳಾಗಿ ಬಾಳಿದಿರಿ’ (ಎನ್ನಲಾಗುವುದು.)');
INSERT INTO "kannada" VALUES(45,32,'45:32','‘ಅಲ್ಲಾಹನ ವಾಗ್ದಾನ ಸತ್ಯಪೂರ್ಣವಾಗಿದೆ. ಅಂತಿಮ ಘಳಿಗೆಯಲ್ಲಿ ಯಾವ ಸಂದೇಹವೂ ಇಲ್ಲ’, ಎಂದು ಹೇಳಲಾದಾಗ ನೀವು, ‘ಅಂತಿಮ ಘಳಿಗೆಯೇನೆಂದು ನಮಗೆ ತಿಳಿಯದು. ನಮಗೊಂದು ಊಹೆ ಮಾತ್ರ ಇದೆ, ನಾವು ದೃಢವಾಗಿ ನಂಬಿದವರಲ್ಲ’ ಎಂದು ಹೇಳುತ್ತಿದ್ದಿರಿ .');
INSERT INTO "kannada" VALUES(45,33,'45:33','(ಆಗ) ಅವರ ಕರ್ಮಗಳ ದೋಷಗಳು ಅವರಿಗೆ ವ್ಯಕ್ತವಾಗುವುವು. ಅವರು ಯಾವುದನ್ನು ಗೇಲಿ ಮಾಡುತ್ತಿದ್ದರೋ ಅದು (ಶಿಕ್ಷೆ) ಅವರನ್ನು ಸುತ್ತುವರಿಯಿತು.');
INSERT INTO "kannada" VALUES(45,34,'45:34','(ಅವರೊಡನೆ ಹೀಗೆ) ಹೇಳಲಾಗುವುದು, ನಿಮ್ಮ ಈ ದಿನದ ಭೇಟಿಯನ್ನು ನೀವು ಮರೆತು ಬಿಟ್ಟಂತೆಯೇ ಇಂದು ನಾವು ನಿಮ್ಮನ್ನು ಮರೆತು ಬಿಡುತ್ತೇವೆ. ನರಕಾಗ್ನಿಯೇ ನಿಮ್ಮ ನೆಲೆಯಾಗಿದೆ. ನಿಮಗೆ ಯಾರೂ ಸಹಾಯಕರಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(45,35,'45:35','ಅಲ್ಲಾಹನ ಸೂಕ್ತಗಳನ್ನು ನೀವು ಪರಿಹಾಸ್ಯ ಮಾಡಿದ್ದುದರಿಂದಲೂ ಭೂಲೋಕದ ಜೀವನವು ನಿಮ್ಮನ್ನು ವಂಚಿಸಿ ಬಿಟ್ಟಿದ್ದರಿಂದಲೂ ಹೀಗೆ ಸಂಭವಿಸಿತು. ಆದುದರಿಂದ ಇಂದು ಇವರನ್ನು ನರಕದಿಂದ ಹೊರಹಾಕಲಾಗುವುದಿಲ್ಲ. ಇವರೊಡನೆ ಪ್ರಾಯಶ್ಚಿತ್ತವನ್ನು ಕೇಳಲಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(45,36,'45:36','ಆದುದರಿಂದ ಆಕಾಶಗಳ ಪ್ರಭು, ಭೂಮಿಯ ಪ್ರಭು ಮತ್ತು ಸಕಲ ಲೋಕವಾಸಿಗಳ ಪ್ರಭು ವಾಗಿರುವ ಅಲ್ಲಾಹನಿಗೇ ಸರ್ವ ಸ್ತುತಿ.');
INSERT INTO "kannada" VALUES(45,37,'45:37','ಆಕಾಶಗಳಲ್ಲೂ ಭೂಮಿಯಲ್ಲೂ ಆತನಿಗೆ ಘನತೆಯಿದೆ. ಅವನೇ ಪ್ರಬಲನೂ ಯುಕ್ತಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(46,1,'46:1','ಹಾಮೀಮ್.');
INSERT INTO "kannada" VALUES(46,2,'46:2','ಈ ಗ್ರಂಥದ ಅವತೀರ್ಣವು ಪ್ರಬಲನೂ ಯುಕ್ತ ಜ್ಞನೂ ಆದ ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದಾಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(46,3,'46:3','ಆಕಾಶಗಳನ್ನೂ, ಭೂಮಿಯನ್ನೂ ಅವುಗಳ ನಡುವೆ ಇರುವುದೆಲ್ಲವನ್ನೂ ಪರಮ ಸತ್ಯವಾಗಿ ಹಾಗೂ ಒಂದು ನಿಶ್ಚಿತ ಅವಧಿಯನ್ನು ನಿರ್ಧರಿಸಿ ನಾವು ಸೃಷ್ಟಿಸಿದ್ದೇವೆ. ಆದರೆ ಸತ್ಯನಿಷೇಧಿಗಳು ತಮಗೆ ನೀಡಲಾದ ಎಚ್ಚರಿಕೆಯನ್ನು ಗಮನಿಸದೆ ವಿಮುಖರಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(46,4,'46:4','(ನಬಿಯರೇ,) ಹೇಳಿರಿ; ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ನೀವು ಆರಾಧಿಸುತ್ತಿರುವ ವಸ್ತುಗಳ ಬಗ್ಗೆ ನೀವು ಯೋಚಿಸಿದ್ದೀರಾ? ಭೂಮಿಯಲ್ಲಿ ಅವರು ಏನನ್ನು ಸೃಷ್ಟಿಸಿದ್ದಾರೆಂಬುದನ್ನು ನೀವು ನನಗೆ ತೋರಿಸು ವಿರಾ? ಅಥವಾ ಆಕಾಶಗಳ ಸೃಷ್ಟಿಯಲ್ಲಿ ಅವರಿಗೇ ನಾದರೂ ಪಾಲಿದೆಯೇ? ನೀವು ಸತ್ಯವಾದಿಗಳಾಗಿದ್ದರೆ ಇದಕ್ಕಿಂತ ಮುಂಚೆ ಬಂದ ಯಾವುದೇ ಗ್ರಂಥ ಅಥವಾ ಜ್ಞಾನದ ಯಾವುದೇ ಅವಶೇಷವನ್ನು ನನಗೆ ನೀವು ತಂದು ಕೊಡುವಿರಾ?');
INSERT INTO "kannada" VALUES(46,5,'46:5','ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು, ಪುನರುತ್ಥಾನ ದಿನದವರೆಗೂ ಉತ್ತರ ಕೊಡಲಾಗದಂತಹವರನ್ನು ಆರಾಧಿಸುವುದಕ್ಕಿಂತಲೂ ಹೆಚ್ಚು ದಾರಿಗೆಟ್ಟವನು ಇನ್ನಾರು? ಅವರಂತೂ ಇವರ ಆರಾಧನೆಯಿಂದ ಅಶೃದ್ಧರಾಗಿರುವರು .');
INSERT INTO "kannada" VALUES(46,6,'46:6','ಜನರನ್ನು ಒಟ್ಟುಗೂಡಿಸಲ್ಪಡುವಾಗ ಅವರು ಇವರ ಶತ್ರುಗಳಾಗಿ ಬಿಡುವರು. ಇವರು ತಮ್ಮ ಆರಾಧನೆಯನ್ನೇ ನಿರಾಕರಿಸಿ ಬಿಡುವರು .');
INSERT INTO "kannada" VALUES(46,7,'46:7','ಸುಸ್ಪಷ್ಟವಾಗಿ ನಮ್ಮ ಸೂಕ್ತಗಳನ್ನು ಓದಿ ಹೇಳು ವಾಗ ಹಾಗೂ ಸತ್ಯವು ಇವರ ಮುಂದೆ ಬಂದು ಬಿಡುವಾಗ ಅದರ ಬಗ್ಗೆ ಈ ಸತ್ಯ ನಿಷೇಧಿಗಳು ‘ಇದು ಸ್ಪಷ್ಟವಾದ ಮಾರಣವಾಗಿರುತ್ತದೆ’ ಎಂದು ಹೇಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(46,8,'46:8','ಅದಲ್ಲ, ಅವರು (ದೂತರು) ಇದನ್ನು ಸ್ವತಃ ಸೃಷ್ಟಿಸಿದ್ದೆಂದು ಅವರು ಹೇಳುತ್ತಿರುವರೇ? (ತಾವು) ಹೇಳಿರಿ; “ಇದನ್ನು ಸ್ವತಃ ನಾನೇ ರಚಿಸಿ ದ್ದೆಂದಾದರೆ, ನೀವು ನನ್ನನ್ನು ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆಯಿಂದ ಸ್ವಲ್ಪವೂ ರಕ್ಷಿಸಲಾರಿರಿ. ನೀವು ಖುರ್ ಆನಿನ ಕುರಿತು ಕಟ್ಟುತ್ತಿರುವ ಮಾತುಗಳನ್ನು ಅಲ್ಲಾಹನು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು. ನನ್ನ ಮತ್ತು ನಿಮ್ಮ ನಡುವೆ ಸಾಕ್ಷಿಯಾಗಿ ಅವನೇ ಸಾಕು. ಅವನು ಮಹಾ ಕ್ಷಮಾಶೀಲನೂ ದಯಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(46,9,'46:9','ತಾವು ಹೇಳಿರಿ; “ನಾನು ದೇವದೂತರಲ್ಲಿ ಹೊಸ ಬನಲ್ಲ. ನನ್ನನ್ನಾಗಲಿ, ನಿಮ್ಮನ್ನಾಗಲಿ ಏನು ಮಾಡಲಾಗುತ್ತದೆ ಎಂಬುದು ನನಗರಿಯದು. ನನ್ನ ಬಳಿಗೆ ಭೋದಿಸಲ್ಪಡುವುದನ್ನು ಮಾತ್ರ ನಾನು ಅನುಸರಿಸುತ್ತೇನೆ. ನಾನು ಸುಸ್ಪಷ್ಟ ಎಚ್ಚರಿಕೆ ನೀಡುವವನು ಮಾತ್ರ.');
INSERT INTO "kannada" VALUES(46,10,'46:10','(ಪ್ರವಾದಿಯವರೇ,) ಹೇಳಿರಿ! ನಿಮ್ಮ ಅವಸ್ಥೆಯೇನೆಂದು ತಿಳಿಸುವಿರಾ? ಈ ಖುರ್‍ಆನ್ ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದಾಗಿದ್ದು ಅದನ್ನು ನೀವು ಧಿಕ್ಕರಿಸಿದ ಸ್ಥಿತಿಯಲ್ಲೇ ಇಸ್ರಾಈಲ್ ವರ್ಗದ ಓರ್ವ ಸಾಕ್ಷಿದಾರನು . ಅದು ದೈವಿಕವಾಣಿ ಎಂಬುದಕ್ಕೆ ಸಾಕ್ಷಿ ನಿಂತಿರುವುದಲ್ಲದೆ ಖುರ್‍ಆನಿಗೆ ವಿಶ್ವಾಸ ನಿಷ್ಠೆಯನ್ನು ತೋರಿರುತ್ತಾರೆ. ನೀವು ಅಹಂಭಾವ ತೋರಿದ್ದೀರಿ. (ನೀವು ಅಕ್ರಮಿ ಗಳಲ್ಲವೇ?) ಅಕ್ರಮಿ ಜನತೆಗೆ ಅಲ್ಲಾಹನು ಖಂಡಿತವಾಗಿಯೂ ಸತ್ಪಥ ತೋರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(46,11,'46:11','ಇದು (ಖುರ್‍ಆನ್) ಒಳಿತಾಗಿರುತ್ತಿದ್ದರೆ ಇದರ ಸ್ವೀಕೃತಿಯಲ್ಲಿ ಇವರು ನಮಗಿಂತ ಮೊದಲಿಗರಾಗುತ್ತಿರಲಿಲ್ಲ ಎಂದು ವಿಶ್ವಾಸಿಗಳನ್ನುದ್ದೇಶಿಸಿ ನಿಷೇಧಿಗಳು ಹೇಳಿದರು . ವಾಸ್ತವದಲ್ಲಿ ಇವರು ಖುರ್‍ಆನ್‍ನಿಂದ ಸತ್ಯದರ್ಶನ ಹೊಂದದಿರುವುದರಿಂದ ‘ಇದು ಹಳೇ ಸುಳ್ಳು’ ಎನ್ನಲಿರುವರು.');
INSERT INTO "kannada" VALUES(46,12,'46:12','ಇದಕ್ಕಿಂತ ಮುಂಚೆ ಮೂಸಾರ ಗ್ರಂಥವು (ಜನ ರಿಗೆ) ನಾಯಕನಾಗಿ ಮತ್ತು ಕೃಪೆಯಾಗಿ ಬಂದಿತ್ತು. ಅದನ್ನು ಸಮರ್ಥಿಸುವ ಈ ಗ್ರಂಥವು ಅರಬೀ ಭಾಷೆಯಲ್ಲಿ ಬಂದಿದೆ. ಅಕ್ರಮಿಗಳಿಗೆ ಎಚ್ಚರಿಕೆ ಕೊಡಲಿಕ್ಕಾಗಿ ಹಾಗೂ ಸಜ್ಜನರಿಗೆ ಸುವಾರ್ತೆ ನೀಡಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(46,13,'46:13','ನಿಜದಲ್ಲಿ ಅಲ್ಲಾಹನೇ ನಮ್ಮ ಪ್ರಭು ಎಂದು ಹೇಳಿ ದೇವಾರಾಧನೆಯಲ್ಲಿ ನೆಲೆಗೊಂಡವರಿಗೆ ಯಾವ ಭಯವೂ ಇಲ್ಲ. ಅವರು ದುಃಖತಪ್ತರೂ ಆಗಲಿ ಕ್ಕಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(46,14,'46:14','ಅವರೇ ಸ್ವರ್ಗಕ್ಕೆ ಅರ್ಹರಾದವರು. ಅದರಲ್ಲವರು ನಿತ್ಯ ನಿವಾಸಿಗಳು. ಅವರು ಮಾಡುತ್ತಿದ್ದ ಸತ್ಕರ್ಮ ಗಳ ಪ್ರತಿಫಲವಾಗಿ (ಈ ಫಲ ಪಡೆಯುವರು).');
INSERT INTO "kannada" VALUES(46,15,'46:15','ತನ್ನ ಮಾತಾಪಿತರಿಗೆ ಒಳಿತು ಮಾಡಬೇಕೆಂದು ನಾವು ಮಾನವನಿಗೆ ಆದೇಶಿಸಿದೆವು. ಅವನ ತಾಯಿಯು ಬಹಳ ಕಷ್ಟಪಟ್ಟು ಅವನನ್ನು ಗರ್ಭದಲ್ಲಿರಿಸಿ, ಕಷ್ಟದಿಂದ ಅವನನ್ನು ಹೆತ್ತಳು. ಅವನನ್ನು ಗರ್ಭ ಧರಿಸಿದ ಕಾಲ ಮತ್ತು ಮೊಲೆ ಹಾಲು ಬಿಡಿಸುವ ಕಾಲವು (ಒಟ್ಟು) ಮೂವತ್ತು ತಿಂಗಳುಗಳು. ಅವನು ಪೂರ್ಣ ಪ್ರಾಬಲ್ಯಕ್ಕೆ ಬಂದು ಆಮೇಲೆ ನಲ್ವತ್ತು ವರ್ಷದವನಾದಾಗ, “ನನ್ನ ಪ್ರಭೂ, ನೀನು ನನಗೂ ನನ್ನ ಮಾತಾಪಿ ತರಿಗೂ ಕರುಣಿಸಿರುವ ಅನುಗ್ರಹಗಳಿಗೆ ಕೃತಜ್ಞತೆಸಲ್ಲಿಸುವ ಹಾಗೂ ನೀನು ಸಂತೃಪ್ತನಾಗುವಂತಹ ಸತ್ಕರ್ಮಗಳನ್ನು ಮಾಡುವ ಸೌಭಾಗ್ಯವನ್ನು ನನಗೆ ನೀಡು. ಮತ್ತು ನನ್ನ ಸಂತಾನಗಳಲ್ಲಿ ನನಗೆ ನೀನು ಒಳಿತನ್ನು ದಯಪಾಲಿಸು. ನಾನು ನಿನ್ನ ಕಡೆಗೆ ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು ಮರಳಿದ್ದೇನೆ ಮತ್ತು ಖಂಡಿತ ನಾನು ಶರಣಾಗತ ದಾಸರಲ್ಲಾಗಿರುತ್ತೇನೆ ಎಂದನು.');
INSERT INTO "kannada" VALUES(46,16,'46:16','ನಾವು ಇಂತಹವರ ಉತ್ತಮ ಕರ್ಮಗಳನ್ನು ಸ್ವೀಕರಿಸುತ್ತೇವೆ ಮತ್ತು ಅವರ ಪಾಪಗಳನ್ನು ನಾವು ಮನ್ನಿಸಿ ಕೊಡುತ್ತೇವೆ. ಅವರು ಸ್ವರ್ಗೀಯರಲ್ಲಿ ಒಳಪಟ್ಟವರಾಗಿರುತ್ತಾರೆ. ಇದು ಅವರಿಗೆ ನೀಡಲಾಗುತ್ತಿದ್ದ ವಾಗ್ದಾನದ ಸಾಕ್ಷಾತ್ಕಾರ ವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(46,17,'46:17','ಓರ್ವನು, ತನ್ನ ಮಾತಾಪಿತರೊಡನೆ ‘ಛೀ, ನೀವಿ ಬ್ಬರ ಕಾರ್ಯ ಕಷ್ಟ. ನನ್ನನ್ನು (ಮರಣಾನಂತರ ಗೋರಿಯಿಂದ) ಹೊರತರಲಾಗುವುದು ಎಂದು ನೀವು ನನ್ನನ್ನು ಹೆದರಿಸುತ್ತಿರುವಿರಾ? ವಸ್ತುತಃ ನನಗಿಂತ ಮುಂಚೆ ಅನೇಕ ವಂಶಗಳು ಗತಿಸಿವೆ. (ಅವರಾರೂ ಈ ತನಕ ಹೊರಬಂದಿಲ್ಲ)’ ಎಂದನು. ಅವನ ಮಾತಾಪಿತರು ಅಲ್ಲಾಹನಲ್ಲಿ ಸಹಾಯ ಬೇಡುತ್ತ, ‘ನಿನಗೆ ನಾಶ! ನೀನು ನಂಬಿಕೋ, ಅಲ್ಲಾಹನ ವಾಗ್ದಾನವು ಸತ್ಯವಾ ದುದು’ ಎನ್ನುತ್ತಾರೆ. ಆದರೆ ಅವನು, ‘ಇವೆಲ್ಲ ಪ್ರಾಚೀನರ ಕಟ್ಟುಕತೆಗಳಾಗಿವೆ’ ಎನ್ನುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(46,18,'46:18','ನಮ್ಮ (ಶಿಕ್ಷೆಯ) ತೀರ್ಮಾನವು ಇಂತಹ ಜನರ ಮೇಲೆ ಸ್ಥಿರಗೊಂಡಿರುತ್ತದೆ. ಇವರಿಗಿಂತ ಮೊದಲು ಗತಿಸಿರುವ ಜಿನ್ನ್ ಮತ್ತು ಮಾನವರ ಗುಂಪಿಗೆ ಇವರೂ ಸೇರಿಕೊಳ್ಳುವರು. ಖಂಡಿತ ಇವರು ನಷ್ಟಕ್ಕೊಳಗಾದವರೇ ಆಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(46,19,'46:19','ಎರಡು ತಂಡಗಳ ಪೈಕಿ ಪ್ರತಿಯೊಂದು ತಂಡದವರಿಗೂ ಅವರವರ ಕರ್ಮಕ್ಕನುಸಾರವಾದ ಸ್ಥಾನ ವಿದೆ. ಅಲ್ಲಾಹನು ಅವರಿಗೆ ಅವರ ಕರ್ಮಗಳ ಪರಿಪೂರ್ಣ ಪ್ರತಿಫಲ ನೀಡುವಂತಾಗಲಿಕ್ಕಾಗಿ. ಅವರು ಖಂಡಿತ ಅನ್ಯಾಯಕ್ಕೊಳಗಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(46,20,'46:20','ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ನರಕದ ಮುಂದೆ ಪ್ರದರ್ಶಿ ಸಲ್ಪಡುವ ದಿನ (ಅವರೊಡನೆ,) “ನೀವು ನಿಮ್ಮ ಲೌಕಿಕ ಜೀವನದಲ್ಲೇ ನಿಮ್ಮ ಉತ್ತಮ ವಸ್ತುಗಳನ್ನು (ಸುಖಿಸಿ) ಕಳೆದು ಕೊಂಡಿರಿ. ಮತ್ತು ಅವುಗಳ ಸವಿಯನ್ನು ಆಸ್ವಾದಿಸಿಕೊಂಡಿರಿ. ನೀವು ಭೂಮಿಯಲ್ಲಿ ಯಾವುದೇ ಹಕ್ಕಿಲ್ಲದೆ ತೋರುತ್ತಿದ್ದ ಅಹಂಕಾರದ ಹಾಗೂ ನೀವು ಮಾಡಿದ್ದ ಪಾಪದ ಫಲವಾಗಿ ಇಂದು ನಿಮಗೆ ಅವಮಾನಕಾರಿ ಶಿಕ್ಷೆ ಕೊಡಲಾಗುವುದು” ಎಂದು ಹೇಳಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(46,21,'46:21','`ಆದ್’ರ ಸಹೋದರನನ್ನು (ಹೂದ್‍ರನ್ನು) ಸ್ಮರಿಸಿರಿ. ಅವರು `ಅಹ್‍ಖಾಫ್’ನಲ್ಲಿರುವ ತಮ್ಮ ಜನಾಂಗಕ್ಕೆ; ‘ಅಲ್ಲಾಹನ ಹೊರತು ಯಾರನ್ನೂ ಆರಾಧಿಸಬೇಡಿರಿ, ನನಗೆ ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ಒಂದು ಭಯಾನಕ ದಿನದ ಯಾತನೆಯ ಭಯ ವಿದೆ’ ಎಂಬ ಎಚ್ಚರಿಕೆ ಕೊಟ್ಟ ಸಂದರ್ಭ. ಇಂತಹ ಎಚ್ಚರಿಕೆ ನೀಡುವವರು ಅವರಿಗಿಂತ ಮುಂಚೆ ಯೂ ಅವರ ನಂತರವೂ ಆಗಿ ಹೋಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(46,22,'46:22','ಅವರು ಹೇಳಿದರು; ‘ನೀನು ನಮ್ಮನ್ನು ಮೋಸ ಗೊಳಿಸಿ ನಮ್ಮ ಆರಾಧ್ಯರಿಂದ ಬೇರ್ಪಡಿಸಲಿಕ್ಕಾಗಿ ಬಂದಿರುವೆಯಾ? ನೀನು ನಿಜಕ್ಕೂ ಸತ್ಯ ವಾದಿಯಾಗಿದ್ದರೆ ನಮ್ಮನ್ನು ಬೆದರಿಸುತ್ತಿರುವಂತಹ ನಿನ್ನ ಆ ಶಿಕ್ಷೆಯನ್ನು ತಂದು ಬಿಡು’.');
INSERT INTO "kannada" VALUES(46,23,'46:23','ಆಗ ಅವರು, `ಅದರ ಜ್ಞಾನವಿರುವುದು ಅಲ್ಲಾಹನಿಗೆ ಮಾತ್ರವಾಗಿದೆ. ನಾನು ನನಗೆ ಕೊಟ್ಟು ಕಳು ಹಿಸಲಾಗಿರುವಂತಹ ಸಂದೇಶವನ್ನು ಮಾತ್ರ ನಿಮಗೆ ತಲುಪಿಸುತ್ತಿದ್ದೇನೆ. ಆದರೆ, ನಿಮ್ಮನ್ನು ನಾನು (ಶಿಕ್ಷೆಗಾಗಿ ದುಡುಕುತ್ತಿರುವ) ಮೌಢ್ಯ ಜನಾಂಗವನ್ನಾಗಿ ಕಾಣುತ್ತಿದ್ದೇನೆ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(46,24,'46:24','ತರುವಾಯ ಅವರು ಆ ಶಿಕ್ಷೆಯು ತಮ್ಮ ಕಣಿವೆ ಗಳತ್ತ ಒಂದು ಮೇಘವಾಗಿ ಬರುತ್ತಿದ್ದುದನ್ನು ಕಂಡು, “ಇದು ನಮ್ಮ ಮೇಲೆ ಮಳೆ ಸುರಿಸಲಿ ಕ್ಕಾಗಿ ಬರುತ್ತಿರುವ ಮೋಡ” ಎಂದರು. ಅಲ್ಲ, ವಾಸ್ತವದಲ್ಲಿ ಇದು ನೀವು ಅವಸರಪಡಿಸಿದ್ದೇ ಆಗಿದೆ. ಹೌದು, ವೇದನಾಜನಕ ಶಿಕ್ಷೆ ಅಡಗಿದ ಒಂದು ಬಿರುಗಾಳಿ.');
INSERT INTO "kannada" VALUES(46,25,'46:25','ಇದು ತನ್ನ ಪ್ರಭುವಿನ ಅಪ್ಪಣೆಯಂತೆ ಸಕಲ ವಸ್ತುಗಳನ್ನೂ ನಾಶಗೊಳಿಸಿಬಿಡುವುದು. ಕೊನೆಗೆ ಅವರ ವಾಸಸ್ಥಾನಗಳ ಹೊರತು ಅಲ್ಲಿ ಬೇರೇನೂ ಕಂಡುಬಾರದ ಸ್ಥಿತಿ ಉಂಟಾಯಿತು. ಈ ರೀತಿಯಲ್ಲಿ ನಾವು ಅಪರಾಧಿಗಳಾದ ಜನತೆಗೆ ಪ್ರತಿಫಲ ಕೊಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(46,26,'46:26','ನಾವು ನಿಮಗೆ ಪ್ರಭಾವ ಕೊಡದ ರಂಗಗಳಲ್ಲಿ ಅವರಿಗೆ (ಆದ್ ಸಮುದಾಯಕ್ಕೆ) ನಾವು ಪ್ರಭಾವ ಕೊಟ್ಟಿದ್ದೆವು. ನಾವು ಅವರಿಗೆ ಕಿವಿ, ಕಣ್ಣು ಮತ್ತು ಮನಸ್ಸುಗಳನ್ನೂ ಕೊಟ್ಟಿದ್ದೆವು. ಆದರೆ ಅವರ ಕಿವಿಗಳಾಗಲಿ, ಕಣ್ಣುಗಳಾಗಲಿ, ಮನಸ್ಸುಗಳಾಗಲಿ, ಅವರಿಗೆ ಯಾವ ಪ್ರಯೋಜನವನ್ನೂ ನೀಡಿಲ್ಲ. (ಏಕೆಂದರೆ) ಅವರು ಅಲ್ಲಾಹನ ದೃಷ್ಠಾಂತಗಳನ್ನು ನಿರಾಕರಿಸುತ್ತಿದ್ದರು. ಅವರು ಪರಿಹಾಸ್ಯ ಮಾಡುತ್ತಿದ್ದುದು (ಶಿಕ್ಷೆ) ಅವರಿಗೆ ಬಂದೇ ಬಿಟ್ಟಿತು.');
INSERT INTO "kannada" VALUES(46,27,'46:27','ನಾವು ನಿಮ್ಮ ಪರಿಸರ ಪ್ರದೇಶಗಳಲ್ಲಿದ್ದ ಅನೇಕ ನಾಡುಗಳನ್ನು ನಾಶಗೊಳಿಸಿಬಿಟ್ಟಿದ್ದೇವೆ . ಅವರು ಸತ್ಯದ ಕಡೆಗೆ ಮರಳುವಂತಾಗಲು ನಾವು ತರತರದ ನಿದರ್ಶನಗಳನ್ನು ವಿವರಿಸಿ ಕೊಟ್ಟಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(46,28,'46:28','ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು, ದೇವ ಸಾಮೀಪ್ಯದ ಮಾಧ್ಯಮಗಳಾಗಿ ಮಾಡಿಕೊಂಡಿದ್ದ ಆರಾಧ್ಯ ವಸ್ತುಗಳು ಆ ಸಂದರ್ಭದಲ್ಲಿ ಅವರಿಗೇಕೆ ನೆರವಾಗಲಿಲ್ಲ? ಮಾತ್ರವಲ್ಲ, ಆ ಇಲಾಹ್‍ಗಳು ಅವರಿಂದ ಅಪ್ರತ್ಯಕ್ಷರಾಗಿ ಬಿಟ್ಟರು. ಅದು (ವಿಗ್ರಹಾ ರಾಧನೆ) ಅವರ ಕಡೆಯ ವ್ಯಾಜ ಹಾಗೂ ಅವರು ಕಟ್ಟಿಕೊಂಡಿದ್ದ ಕೃತಕ ವಿಶ್ವಾಸವಾಗಿತ್ತು.');
INSERT INTO "kannada" VALUES(46,29,'46:29','ಖುರ್‍ಆನನ್ನು ಆಲಿಸಲಿಕ್ಕಾಗಿ ಜಿನ್ನ್‍ಗಳ ಒಂದು ತಂಡವನ್ನು ನಾವು ನಿಮ್ಮ ಕಡೆಗೆ ತಿರುಗಿಸಿ ಬಿಟ್ಟ ಸಂದರ್ಭ. ಅವರು (ನೀವು ಖುರ್‍ಆನ್ ಪಠಿಸುತ್ತಿದ್ದ) ಆ ಸ್ಥಳಕ್ಕೆ ಹಾಜರಾದಾಗ `ಮೌನವಾಗಿ ಆಲಿಸಿರಿ’ ಎಂದು (ಪರಸ್ಪರ) ಹೇಳಿದರು. ಓದಿ ಮುಗಿದಾಗ ಅವರು ತಮ್ಮ ಜನಾಂಗದ ಕಡೆಗೆ ಮರಳಿದರು, ತಮ್ಮವರಿಗೆ ಮುನ್ನೆಚ್ಚರಿಕೆ ನೀಡುವವರಾಗಿ.');
INSERT INTO "kannada" VALUES(46,30,'46:30','ಅವರು (ಹೋಗಿ ತಮ್ಮ ಜನತೆಯೊಂದಿಗೆ) ಹೇಳಿದರು; `ನಮ್ಮ ಜನಾಂಗದವರೇ, ನಾವು ಒಂದು ಗ್ರಂಥವನ್ನು ಆಲಿಸಿದೆವು. ಅದು ಮೂಸಾರ ಬಳಿಕ ಅವತೀರ್ಣಗೊಳಿಸಲ್ಪಟ್ಟಿದೆ. ಅದು ತನಗಿಂತ ಮುಂಚೆ ಬಂದ ಗ್ರಂಥಗಳನ್ನು ಸಮರ್ಥಿಸುತ್ತದೆ ಮತ್ತು ಸತ್ಯ ಹಾಗೂ ಸನ್ಮಾರ್ಗದೆಡೆಗೆ ಮಾರ್ಗ ದರ್ಶನ ಮಾಡುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(46,31,'46:31','ನಮ್ಮ ಜನಾಂಗದವರೇ, ಅಲ್ಲಾಹನತ್ತ ಕರೆಯುವ ವ್ಯಕ್ತಿಯ ಕರೆಗೆ ಉತ್ತರಿಸಿರಿ. ಮತ್ತು ಅದರಲ್ಲಿ ವಿಶ್ವಾಸವಿಡಿರಿ. ಅಲ್ಲಾಹನು ನಿಮ್ಮ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸುವನು ಮತ್ತು ವೇದನಾಜನಕ ಶಿಕ್ಷೆ ಯಿಂದ ನಿಮ್ಮನ್ನು ರಕ್ಷಿಸುವನು.');
INSERT INTO "kannada" VALUES(46,32,'46:32','ಅಲ್ಲಾಹನ ಕಡೆಗೆ ಕರೆ ನೀಡುವವನ ಕರೆಗೆ ಓಗೊಡದವನಿಗೆ ಭೂಮಿಯಲ್ಲಿ ಅಲ್ಲಾಹನನ್ನು ಜೈಸಲು ಸಾಧ್ಯವಿಲ್ಲ. ಅಲ್ಲಾಹನ ಹೊರತಾಗಿ ಯಾವುದೇ ರಕ್ಷಕ ಮಿತ್ರರೂ ಅವನಿಗೆ ಇಲ್ಲ. ಇಂತ ಹವರು ಸ್ಪಷ್ಟ ಪಥಭ್ರಷ್ಟತೆಯಲ್ಲಿ ಬಿದ್ದಿರುವರು.');
INSERT INTO "kannada" VALUES(46,33,'46:33','ಅಲ್ಲಾಹನ ಕಡೆಗೆ ಕರೆ ನೀಡುವವನ ಕರೆಗೆ ಓಗೊಡದವನಿಗೆ ಭೂಮಿಯಲ್ಲಿ ಅಲ್ಲಾಹನನ್ನು ಜೈಸಲು ಸಾಧ್ಯವಿಲ್ಲ. ಅಲ್ಲಾಹನ ಹೊರತಾಗಿ ಯಾವುದೇ ರಕ್ಷಕ ಮಿತ್ರರೂ ಅವನಿಗೆ ಇಲ್ಲ. ಇಂತ ಹವರು ಸ್ಪಷ್ಟ ಪಥಭ್ರಷ್ಟತೆಯಲ್ಲಿ ಬಿದ್ದಿರುವರು.');
INSERT INTO "kannada" VALUES(46,34,'46:34','ಈ ಸತ್ಯನಿಷೇಧಿಗಳು ನರಕಾಗ್ನಿಯ ಮೇಲೆ ಕಾಣಿಸಲ್ಪಡುವ ದಿವಸ (ಅವರೊಡನೆ,) ‘ಇದು ಸತ್ಯವಲ್ಲವೇ?’ ಎಂದು ಕೇಳಲಾಗುವುದು. ಆಗ ಅವರು `ಹೌದು! ನಮ್ಮ ಪ್ರಭುವಿನಾಣೆ ಸತ್ಯ’ ಎನ್ನುವರು. ಆಗ ಅಲ್ಲಾಹನು - ‘ಹಾಗಿದ್ದರೆ ನೀವು ತಾಳಿದ ಅವಿಶ್ವಾಸದ ಫಲವಾಗಿ ಈಗ ನೀವು ಶಿಕ್ಷೆಯ ಸವಿಯನ್ನು ಅನುಭವಿಸಿರಿ’ ಎಂದು ಹೇಳುವನು.');
INSERT INTO "kannada" VALUES(46,35,'46:35','ಆದುದರಿಂದ (ಪ್ರವಾದಿಯರೇ,) ದೃಢಚಿತ್ತರಾಗಿದ್ದ ಪ್ರವಾದಿಗಳು ತಾಳ್ಮೆ ವಹಿಸಿದಂತೆ ನೀವೂ ತಾಳ್ಮೆ ವಹಿಸಿರಿ. ಇವರ (ಸತ್ಯನಿಷೇಧಿಗಳ) ವಿಷಯದಲ್ಲಿ ತಾವು ಅವಸರ ಪಡಬೇಡಿರಿ. ಇವರಿಗೆ ಎಚ್ಚರಿಕೆ ನೀಡಲಾಗುತ್ತಿದ್ದುದನ್ನು (ಶಿಕ್ಷೆ) ಇವರು ಕಣ್ಣಾರೆ ಕಾಣುವ ದಿನದಂದು ನಾವು (ಭೂಲೋಕದಲ್ಲಿ) ಒಂದು ಹಗಲಿನ ಒಂದು ತಾಸಿಗಿಂತ ಹೆಚ್ಚು ಕಾಲ ತಂಗಲಿಲ್ಲವೋ ಎಂಬಂತೆ ಇವರಿಗೆ ಭಾಸ ವಾಗುವುದು. ಇದೊಂದು ಉದ್ಭೋದನೆಯಾಗಿದೆ. ದುರ್ಮಾರ್ಗಿಗಳಾದ ಜನತೆಯಲ್ಲದೆ ಇನ್ನಾರಾದರೂ ನಾಶ ಹೊಂದುವರೇ?');
INSERT INTO "kannada" VALUES(47,1,'47:1','ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸಿ, ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ (ಜನರನ್ನು) ತಡೆದವರು ಯಾರೋ ಅವರ ಕರ್ಮ ಗಳನ್ನು ಅಲ್ಲಾಹನು ವ್ಯರ್ಥಗೊಳಿಸಿಬಿಟ್ಟನು .');
INSERT INTO "kannada" VALUES(47,2,'47:2','ಸತ್ಯವಿಶ್ವಾಸ ಸ್ವೀಕರಿಸಿದ ಹಾಗೂ ಸತ್ಕರ್ಮ ಮಾಡಿದ, ಮುಹಮ್ಮದರ ಮೇಲೆ ಅವತೀರ್ಣ ಗೊಂಡದ್ದನ್ನು ಅವರ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಪರಮ ಸತ್ಯವಾದುದೆಂದು ನಂಬಿದವರ ಪಾಪಗಳನ್ನು ಅಲ್ಲಾಹು ಅವರಿಂದ ದೂರೀಕರಿಸಿ, ಅವರ ಸ್ಥಿತಿಯನ್ನು ಸರಿಪಡಿಸಿದನು.');
INSERT INTO "kannada" VALUES(47,3,'47:3','ಅದೇಕೆಂದರೆ, ಸತ್ಯನಿಷೇಧಿಗಳು ಮಿಥ್ಯವನ್ನು ಅನುಸರಿಸಿದರು ಮತ್ತು ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಅವರ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಬಂದಿರುವ ಸತ್ಯವನ್ನು ಅನುಸರಿಸಿದರು. ಈ ರೀತಿಯಲ್ಲಿ ಅಲ್ಲಾಹನು ಜನರಿಗೆ ಅವರ ಉಪಮೆಗಳನ್ನು ವಿವರಿಸಿ ಕೊಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(47,4,'47:4','ಆದುದರಿಂದ ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು (ಯುದ್ಧದಲ್ಲಿ) ನೀವು ಎದುರುಗೊಂಡರೆ, ನೀವು ಅವರ ಕತ್ತು ಕಡಿಯಿರಿ. ನೀವು ಅವರನ್ನು ಸದೆಬಡಿದ ಬಳಿಕ ಕೈದಿಗಳನ್ನು ಬಿಗಿಯಾಗಿ ಕಟ್ಟಿರಿ. ಆ ಬಳಿಕ ನೀವು ಅವರ ಮೇಲೆ ಸೌಜನ್ಯ ತೋರಿ ಬಿಡಬಹುದು ಅಥವಾ ಮೋಕ್ಷದ್ರವ್ಯ ಪಡೆದು ಬಿಟ್ಟು ಬಿಡಬಹುದು. ಇದು ಯುದ್ಧವು ಅದರ (ಆಯುಧ) ಹೊರೆಗಳನ್ನು ಕೆಳಗಿಡುವ ತನಕ. ಇದು ಯುದ್ಧದ ವಿಧಾನ. ಅಲ್ಲಾಹನಿಚ್ಛಿಸುತ್ತಿದ್ದರೆ (ಯುದ್ಧ ರಹಿತವಾಗಿ) ಸ್ವತಃ ಅವನೇ ಅವರನ್ನು ಶಿಕ್ಷಿಸುತ್ತಿದ್ದನು. ಆದರೆ, ನಿಮ್ಮನ್ನು ಪರಸ್ಪರರ ಮೂಲಕ ಪರೀಕ್ಷಿಸಲಿಕ್ಕಾಗಿ (ಅವನು ನಿಮ್ಮೊಂದಿಗೆ ಯುದ್ಧಕ್ಕೆ ಆದೇಶಿಸಿದನು) . ಅಲ್ಲಾಹನು ತನ್ನ ಮಾರ್ಗದಲ್ಲಿ ಹತರಾಗುವವರ ಕರ್ಮಗಳನ್ನು ಖಂಡಿತ ವ್ಯರ್ಥಗೊಳಿಸಲಾರನು.');
INSERT INTO "kannada" VALUES(47,5,'47:5','ಅವನು ಅವರಿಗೆ (ಗೆಲುವಿಗೆ) ಮಾರ್ಗದರ್ಶನ ಮಾಡುವನು, ಅವರ ಸ್ಥಿತಿಯನ್ನು ಉತ್ತಮ ಗೊಳಿಸುವನು.');
INSERT INTO "kannada" VALUES(47,6,'47:6','ಅವನು ಅವರಿಗೆ (ಮೊದಲೇ) ಪರಿಚಯಿಸಿ ಕೊಟ್ಟಿರುವಂತಹ ಸ್ವರ್ಗದಲ್ಲಿ ಅವರನ್ನು ಪ್ರವೇಶಗೊಳಿಸುವನು.');
INSERT INTO "kannada" VALUES(47,7,'47:7','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ಅಲ್ಲಾಹನಿಗೆ ಸಹಾಯ ಮಾಡಿದರೆ, ಅವನು ನಿಮಗೆ ಸಹಾಯ ಮಾಡುವನು ಮತ್ತು ನಿಮ್ಮ ಪಾದಗಳನ್ನು ಸ್ಥಿರಗೊಳಿಸುವನು.');
INSERT INTO "kannada" VALUES(47,8,'47:8','ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ವಿನಾಶ ಕಾದಿದೆ ಮತ್ತು ಅಲ್ಲಾಹನು ಅವರ ಕರ್ಮಗಳನ್ನು ವ್ಯರ್ಥಗೊಳಿಸಿಬಿಟ್ಟಿರುವನು.');
INSERT INTO "kannada" VALUES(47,9,'47:9','ಏಕೆಂದರೆ ಅವರು ಅಲ್ಲಾಹನು ಅವತೀರ್ಣಗೊಳಿಸಿದುದನ್ನು ಅನಿಷ್ಟಪಟ್ಟರು. ಆದುದರಿಂದ ಅಲ್ಲಾಹನು ಅವರ ಕರ್ಮಗಳನ್ನು ನಿಷ್ಪಲಗೊಳಿಸಿಬಿಟ್ಟನು.');
INSERT INTO "kannada" VALUES(47,10,'47:10','ಅವರಿಗಿಂತ ಮುಂಚೆ ಗತಿಸಿದವರ ಗತಿಯೇ ನಾಯಿತೆಂಬುದನ್ನು ಕಂಡುಕೊಳ್ಳಲು ಅವರು ಭೂಮಿಯಲ್ಲಿ ಸಂಚರಿಸಲಿಲ್ಲವೇ? ಅಲ್ಲಾಹನು ಅವರನ್ನು ಧ್ವಂಸಗೊಳಿಸಿದನು. ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಇಂತಹದೇ ಆದ ಅನುಭವಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(47,11,'47:11','ಇದೇಕೆಂದರೆ, ಅಲ್ಲಾಹನು ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಸಹಾಯಕನಾಗಿದ್ದಾನೆ ಮತ್ತು ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಯಾರೂ ಸಹಾಯಕರಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(47,12,'47:12','ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಸ್ವೀಕರಿಸಿ ಸದಾಚಾರ ಕೈ ಗೊಂಡವರನ್ನು ಅಲ್ಲಾಹನು ತಳಭಾಗದಲ್ಲಿ ನದಿಗಳು ಹರಿಯುವಂತಹ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ ಪ್ರವೇಶಗೊಳಿಸುವನು. ಸತ್ಯನಿಷೇಧಿಗಳು (ಇಹಲೋಕದಲ್ಲಿ) ಸುಖಿಸುತ್ತಾ ಪಶುಗಳಂತೆ ತಿನ್ನುತ್ತಿದ್ದಾರೆ. ನರಕವೇ ಅವರ ಅಂತಿಮ ನೆಲೆಯಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(47,13,'47:13','(ಸಂದೇಶವಾಹಕರೇ,) ತಮ್ಮನ್ನು ಹೊರಹಾಕಿದ ತಮ್ಮ ನಾಡಿಗಿಂತ ಬಹಳಷ್ಟು ಬಲಿಷ್ಠವಾಗಿದ್ದ ಅದೆ ಷ್ಟೋ ನಾಡುಗಳಿವೆ . ನಾವು ಅವರನ್ನು ನಾಶಗೊಳಿಸಿದ್ದೇವೆ. ಆಗ ಅವರನ್ನು ರಕ್ಷಿಸುವ ಯಾವ ಸಹಾಯಕನೂ ಇರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(47,14,'47:14','ತನ್ನ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಒಂದು ಸುಸ್ಪಷ್ಟ ಆಧಾರದಡಿಯಲ್ಲಿ ನೆಲೆಗೊಳ್ಳುವ ಒಬ್ಬನು ಸ್ವಯಂ ದುಷ್ಕøತ್ಯ ಚಂದಗಾಣಿಸಲ್ಪಟ್ಟು ತಮ್ಮ ಸ್ವೇಚ್ಛೆಗಳ ಬೆನ್ನು ಹತ್ತಿರುವ ಒಬ್ಬನಂತೆ ಆಗಬಲ್ಲನೇ? (ಒಮ್ಮೆಯೂ ಇಲ್ಲ).');
INSERT INTO "kannada" VALUES(47,15,'47:15','ಧರ್ಮನಿಷ್ಟರಿಗೆ ವಾಗ್ಧಾನ ಮಾಡಲಾಗಿರುವ ಸ್ವರ್ಗದ ಉಪಮೆಯೇನೆಂದರೆ ಅದರಲ್ಲಿ ಕಳಂಕ ವಿಲ್ಲದ ತಿಳಿನೀರಿನ ಕಾಲುವೆಗಳಿವೆ. ಸ್ವಲ್ಪವೂ ರುಚಿ ಬದಲಾಗಿರದಂತಹ ಹಾಲಿನ ಕಾಲುವೆ ಗಳೂ ಕುಡಿಯುವವರಿಗೆ ಅತ್ಯಂತ ರುಚಿಕರವಾಗಿರುವ ಮದಿರೆಯ ಕಾಲುವೆಗಳೂ, ಸಂಸ್ಕರಿತ ಶುದ್ಧ ಜೇನಿನ ಕಾಲುವೆಗಳೂ ಇವೆ. ಅದರಲ್ಲಿ ಅವರಿಗಾಗಿ ಎಲ್ಲ ಬಗೆಯ ಫಲ ಮೂಲಗಳಿವೆ ಮತ್ತು ತಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಅವರಿಗೆ ಕ್ಷಮೆಯೂ ಇದೆ. (ಇವರು) ಸದಾಕಾಲ ನರಕದಲ್ಲೇ ವಾಸಿಸಲಿರುವ ಜನರಿಗೆ ಸಮಾನರಾಗ ಬಲ್ಲರೇ? ಅವರಿಗೆ ಕರುಳುಗಳನ್ನು ಕತ್ತರಿಸಿ ಬಿಡುವಂತಹ ಕುದಿಯುವ ನೀರನ್ನು ಕುಡಿಸಲಾ ಗುವುದು .');
INSERT INTO "kannada" VALUES(47,16,'47:16','ಅವರ ಪೈಕಿ ಕೆಲವರು ತಮ್ಮ ಮಾತುಗಳನ್ನು ಕಿವಿಗೊಟ್ಟು ಕೇಳುತ್ತಾರೆ. ಆ ಬಳಿಕ ನಿಮ್ಮಲ್ಲಿಂದ ಹೊರಟು ಹೋದಾಗ, ವೇದಜ್ಞಾನದ ಅನುಗ್ರಹ ನೀಡಲ್ಪಟ್ಟವರೊಡನೆ, ಅವರು (ಪರಿಹಾಸಪೂರ್ವ) ಈಗ ಅವರು ಹೇಳಿದ್ದೇನು?’ ಎಂದು ಕೇಳುತ್ತಾರೆ. ಅಂಥವರ ಹೃದಯಗಳ ಮೇಲೆ ಅಲ್ಲಾಹನು ಮುದ್ರೆಯೊತ್ತಿರುವನು. ಮತ್ತು ಅವರು ತಮ್ಮ ಸ್ವೇಚ್ಛೆಗಳ ಬೆನ್ನು ಹತ್ತಿರುವರು .');
INSERT INTO "kannada" VALUES(47,17,'47:17','ಸನ್ಮಾರ್ಗ ಹೊಂದಿದವರಿಗೆ ಅಲ್ಲಾಹನು ಇನ್ನಷ್ಟು ಸನ್ಮಾರ್ಗ ಬೋಧವನ್ನು ವರ್ಧಿಸಿ ಕೊಡುತ್ತಾನೆ. ಮತ್ತು ಅವರಿಗೆ ಭಕ್ತಿಯ ಬೋಧವನ್ನೂ ದಯಪಾಲಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(47,18,'47:18','ಇನ್ನು ಲೋಕಾಂತ್ಯವು ಹಠಾತ್ತನೆ ಅವರಿಗೆ ಬಂದೆರಗುವುದನ್ನೇ ಹೊರತು ಬೇರೇನನ್ನಾದರೂ ಅವರು ಕಾಯುತ್ತಿರುವರೇ? ಆದರೆ ಅದರ ಕುರುಹುಗಳು ಬಂದೇ ಬಿಟ್ಟಿವೆ . ಇನ್ನು ಅದುವೇ ಅವರಿಗೆ ಬಂದುಬಿಟ್ಟಾಗ, ಅವರ ನೆನಪು ಅವರಿಗೆ ಪ್ರಯೋಜನವಾಗುವುದೆಂತು?');
INSERT INTO "kannada" VALUES(47,19,'47:19','(ಪ್ರವಾದಿಯರೇ,) ನಿಮಗೆ ತಿಳಿದಿರಲಿ! ಅಲ್ಲಾಹನ ಹೊರತು ಖಂಡಿತವಾಗಿಯೂ ಬೇರೆ ಆರಾಧ್ಯನಿಲ್ಲ ಎಂಬುದು. ನಿಮ್ಮ ಪಾಪಮೋಚನೆಗಾಗಿಯೂ ಸತ್ಯವಿಶ್ವಾಸಿಗಳಾದ ಪುರುಷರಿಗಾಗಿಯೂ, ಸ್ತ್ರೀಯರಿಗಾಗಿಯೂ ಪ್ರಾರ್ಥಿಸಿರಿ. ಅಲ್ಲಾಹನು ನಿಮ್ಮ ಹಗಲಿನ ಚಲನವನ್ನೂ ನಿಮ್ಮ ರಾತ್ರಿಯ ವಿಶ್ರಮ ತಾಣವನ್ನೂ ಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(47,20,'47:20','ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಹೇಳುವರು; `(ಯುದ್ಧದ ಕುರಿ ತು) ಒಂದು ಅಧ್ಯಾಯ ಅವತೀರ್ಣಗೊಳಿಸಲಾಗಿಲ್ಲವೇಕೆ?’. ಆದರೆ ನಿಯಮ ವ್ಯಕ್ತವಾಗಿ ವಿವರಿ ಸಲ್ಪಟ್ಟ ಅಧ್ಯಾಯವೊಂದನ್ನು ಅವತೀರ್ಣಗೊಳಿಸಿ, ಅದರಲ್ಲಿ ಯುದ್ಧದ ಕುರಿತು ಹೇಳಲ್ಪಟ್ಟಾಗ, ಹೃದಯದೊಳಗೆ ರೋಗವಿದ್ದವರು, ಮೃತ್ಯು ಆವರಿಸಿ ಪ್ರಜ್ಞಾಹೀನನಾದ ವ್ಯಕ್ತಿ ನೋಡುವಂತೆ ತಮ್ಮ ಕಡೆ ನೋಡುವುದನ್ನು ಕಾಣುವಿರಿ. ಆಗ ಅವರಿಗೆ ಅತ್ಯಂತ ಸೂಕ್ತವಾದುದು.');
INSERT INTO "kannada" VALUES(47,21,'47:21','ಅನುಸರಣೆ ಮತ್ತು ಹಿತದ ಮಾತು. ಕಾರ್ಯವು ನಿರ್ಧಾರವಾದಾಗ (ಯುದ್ಧ ಕಡ್ಡಾಯವಾದಾಗ) ಅವರು ಅಲ್ಲಾಹನೊಂದಿಗೆ (ಮಾಡಿಕೊಂಡ ಕರಾರಿನಲ್ಲಿ) ಸತ್ಯಸಂಧತೆಯನ್ನು ತೋರುತ್ತಿದ್ದರೆ ಅದು ಅವರಿಗೇ ಹಿತಕರವಾಗಿರುತ್ತಿತ್ತು.');
INSERT INTO "kannada" VALUES(47,22,'47:22','ನೀವು ಈ ಧರ್ಮದಿಂದ ವಿಮುಖರಾದರೆ ಭೂಮಿಯಲ್ಲಿ ಕ್ಷೋಭೆಯನ್ನುಂಟು ಮಾಡಿ, ನಿಮ್ಮ ಕುಟುಂಬ ಸಂಬಂಧಗಳನ್ನು ಮುರಿದು ಹಾಕು ತ್ತೀರೇನೋ?');
INSERT INTO "kannada" VALUES(47,23,'47:23','ಅಂಥವರನ್ನು ಅಲ್ಲಾಹನು ಶಪಿಸಿರುತ್ತಾನೆ. ಹೀಗಾಗಿ ಅವರನ್ನು ಕಿವುಡರಾಗಿಯೂ, ಕುರುಡರಾಗಿಯೂ ಮಾಡಿಬಿಟ್ಟಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(47,24,'47:24','ಅವರು ಖುರ್‍ಆನ್‍ನ ಬಗ್ಗೆ ಮನಸ್ಸಿಟ್ಟು ಚಿಂತನೆ ನಡೆಸಲಿಲ್ಲವೇ? ಅಥವಾ ಹೃದಯಗಳ ಮೇಲೆ ಅವುಗಳ ಬೀಗಮುದ್ರೆ ಬಿದ್ದಿದೆಯೇ?');
INSERT INTO "kannada" VALUES(47,25,'47:25','ಸನ್ಮಾರ್ಗವು ಸ್ಪಷ್ಟಗೊಂಡ ಬಳಿಕ ಅದರಿಂದ ಹಿಂದಕ್ಕೆ ಮರಳಿ ಬಿಟ್ಟವರಿಗೆ ಶೈತಾನನು (ಅವರ ಕರ್ಮವನ್ನು) ಚಂದಗಾಣಿಸಿರುವನು ಹಾಗೂ ಅವರಿಗೆ ವ್ಯಾಮೋಹಗಳನ್ನು ದೀರ್ಘ ಮಾಡಿ ಕೊಟ್ಟಿರುವನು.');
INSERT INTO "kannada" VALUES(47,26,'47:26','ಅವರಿಗೆ ಈ ದುರ್ಮಾರ್ಗವು ಏಕೆ ಒದಗಿತೆಂದರೆ ಅಲ್ಲಾಹನು ಅವತೀರ್ಣಗೊಳಿಸಿದ ಧರ್ಮವನ್ನು ಮೆಚ್ಚದವರೊಡನೆ, “ಕೆಲವು ವಿಷಯಗಳಲ್ಲಿ ನಾವು ನಿಮ್ಮನ್ನು ಅನುಸರಿಸುವೆವು” ಎಂದು ಅವರು ಹೇಳಿಬಿಟ್ಟ ಕಾರಣಕ್ಕಾಗಿದೆ. ಅವರು ರಹಸ್ಯಗೊಳಿಸುವುದನ್ನು ಅಲ್ಲಾಹನು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು .');
INSERT INTO "kannada" VALUES(47,27,'47:27','ದೇವಚರರು ಅವರ ಮುಖಗಳಿಗೂ ಬೆನ್ನುಗಳಿಗೂ ಹೊಡೆಯುತ್ತಾ ಅವರನ್ನು ಮರಣಗೊಳಿಸುವಾಗ ಅವರ ಸ್ಥಿತಿ ಏನಾದೀತು?');
INSERT INTO "kannada" VALUES(47,28,'47:28','ಅದು ಏಕೆಂದರೆ, ಅಲ್ಲಾಹನನ್ನು ಕುಪಿತಗೊಳಿಸುವಂತಹ ಕಾರ್ಯವನ್ನು ಅವರು ಅನುಸರಿಸಿದರು ಮತ್ತು ಅವನ ಮೆಚ್ಚುಗೆಯನ್ನು ಅನಿಷ್ಟಪಟ್ಟರು. ಆದುದರಿಂದಲೇ ಅವರ ಕರ್ಮಗಳನ್ನು ಅಲ್ಲಾಹು ನಿರರ್ಥಕಗೊಳಿಸಿಬಿಟ್ಟನು.');
INSERT INTO "kannada" VALUES(47,29,'47:29','ಅದಲ್ಲ, ಹೃದಯಗಳಲ್ಲಿ ರೋಗವುಳ್ಳವರು, ಅವರ ಹೃದಯಗಳೊಲಗಿರುವ ಹಗೆಯನ್ನು ಅಲ್ಲಾಹು ಹೊರತರಲಾರನೆಂದು ಭಾವಿಸಿಕೊಂಡಿರುವರೇ?');
INSERT INTO "kannada" VALUES(47,30,'47:30','(ಪ್ರವಾದಿಯರೇ,) ನಾವಿಚ್ಛಿಸಿದರೆ ಅವರನ್ನು ನಿಮಗೆ ತೋರಿಸಿಕೊಡುತ್ತಿದ್ದೆವು. ಆಗ ನೀವು ಅವರನ್ನು ಅವರ ಲಕ್ಷಣಗಳಿಂದಲೇ ಗುರುತಿಸ ಬಲ್ಲಿರಿ. ಅವರ ಮಾತಿನ ಶೈಲಿಯಿಂದಲೂ ನೀವು ಅವರನ್ನು ಗುರುತಿಸಿಯೇ ತೀರುವಿರಿ. ಅಲ್ಲಾಹು ನಿಮ್ಮ ಕರ್ಮಗಳನ್ನು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(47,31,'47:31','ನಿಮ್ಮಲ್ಲಿ ಧರ್ಮಯೋಧರೂ ಕ್ಷಮಾಶೀಲರೂ ಯಾರೆಂದು ತಿಳಿಯಲಿಕ್ಕಾಗಿ ಹಾಗೂ ನಿಮ್ಮ ಸ್ಥಿತಿಗತಿಗಳನ್ನು ಬೆಳಕಿಗೆ ತರುವ ತನಕ ನಾವು ಖಂಡಿತವಾಗಿಯೂ ನಿಮ್ಮನ್ನು ಪರೀಕ್ಷೆಗೆ ಒಳಪಡಿಸುವೆವು.');
INSERT INTO "kannada" VALUES(47,32,'47:32','ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸಿದವರು, ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ (ಜನರನ್ನು) ತಡೆದವರು ಮತ್ತು ತಮಗೆ ಸನ್ಮಾರ್ಗ ಸ್ಪಷ್ಟವಾಗಿ ತಿಳಿದ ಬಳಿಕ ದೇವ ದೂತರೊಡನೆ ಜಗಳವಾಡಿದವರು ಯಾರೋ ವಾಸ್ತವದಲ್ಲಿ ಅವರು ಅಲ್ಲಾಹನಿಗೆ ಯಾವ ಹಾನಿಯನ್ನೂ ಮಾಡಲಾರರು, ಕೊನೆಗೆ ಅವರ ಕರ್ಮಗಳನ್ನು ಅಲ್ಲಾಹನೇ ನಿಷ್ಪಲಗೊಳಿಸುವನು.');
INSERT INTO "kannada" VALUES(47,33,'47:33','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ಅಲ್ಲಾಹನನ್ನು ಅನುಸರಿಸಿರಿ. ದೇವದೂತರನ್ನೂ ಅನುಸರಿಸಿರಿ, ಮತ್ತು ನಿಮ್ಮ ಸ್ವಂತ ಕರ್ಮಗಳನ್ನು ನಿರರ್ಥಕ ಗೊಳಿಸಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(47,34,'47:34','ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸಿ, ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ (ಜನರನ್ನು) ತಡೆದು, ಆ ನಂತರ ಸತ್ಯನಿಷೇಧಿಗಳಾಗಿಯೇ ಸತ್ತವರನ್ನು ಅಲ್ಲಾಹನು ಖಂಡಿತ ಕ್ಷಮಿಸಲಾರನು.');
INSERT INTO "kannada" VALUES(47,35,'47:35','ಆದುದರಿಂದ (ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ,) ನೀವು ಬಲಹೀನರಾಗದಿರಿ. ನೀವು ಸಂಧಾನಕ್ಕಾಗಿ ನಿವೇದಿಸಬೇಡಿರಿ. ನೀವು ಉನ್ನತರು. ಅಲ್ಲಾಹು ನಿಮ್ಮ ಜೊತೆಗಿದ್ದಾನೆ ಮತ್ತು ಅವನು ನಿಮ್ಮ ಕರ್ಮಗಳನ್ನು (ಅದರ ಪ್ರತಿಫಲವನ್ನು) ಖಂಡಿತ ಕಡಿತಗೊಳಿಸಲಾರನು.');
INSERT INTO "kannada" VALUES(47,36,'47:36','ಇಹಲೋಕದ ಜೀವನವು ಕೇವಲ ಆಟ ಮತ್ತು ವಿನೋದವಾಗಿದೆ. ನೀವು ವಿಶ್ವಾಸವಿರಿಸಿದರೆ ಮತ್ತು (ಧರ್ಮನಿಷ್ಟರಾಗಿ ಅಲ್ಲಾಹನನ್ನು) ಭಯ ಪಟ್ಟರೆ ಅಲ್ಲಾಹನು ನಿಮಗೆ ನಿಮ್ಮ ಪ್ರತಿಫಲವನ್ನು ನೀಡುವನು. ಅವನು ನಿಮ್ಮಿಂದ ನಿಮ್ಮ ಸೊತ್ತುಗಳನ್ನು ಕೇಳಲಾರನು .');
INSERT INTO "kannada" VALUES(47,37,'47:37','ಇನ್ನು ಅವನು ನಿಮ್ಮಿಂದ ನಿಮ್ಮ ಸಂಪತ್ತನ್ನು ಕೇಳಿ ನಿಮ್ಮನ್ನು ತೊಂದರೆಗೊಳಿಸುತ್ತಿದ್ದರೆ, ನೀವು ಜಿಪುಣತೆ ತೋರುವಿರಿ. ಅದು ನಿಮಗೆ ಧರ್ಮದ ಮೇಲೆ ಜಿಗುಪ್ಸೆ ಮೂಡಿಸುತ್ತಿತ್ತು.');
INSERT INTO "kannada" VALUES(47,38,'47:38','ತಿಳಿಯಿರಿ, ಓ ಸಮೂಹವೇ, ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಸಂಪತ್ತನ್ನು ಖರ್ಚು ಮಾಡಬೇಕೆಂದು ನಿಮಗೆ ಕರೆ ನೀಡಲಾಗುತ್ತಿದೆ. ಆಗ ನಿಮ್ಮಲ್ಲಿ ಕೆಲವರು ಜಿಪುಣತೆ ತೋರುತ್ತಿದ್ದಾರೆ. ಯಾವನು ಜಿಪುಣತೆ ತೋರುವನೋ ವಾಸ್ತವದಲ್ಲಿ ಅವನು ತನ್ನ ಮೇಲೆ ತಾನೇ ಜಿಪುಣತೆ ತೋರುತ್ತಿದ್ದಾನೆ. ಅಲ್ಲಾಹನು ಶ್ರೀಮಂತನು. ನೀವು ಬಡವರು. ನೀವು ವಿಮುಖರಾದರೆ ಅಲ್ಲಾಹನು ನೀವಲ್ಲದ ಬೇರೊಂದು ಜನಾಂಗವನ್ನು ಬದಲಿಗೆ ತರುವನು. ಅವರು ನಿಮ್ಮಂತೆ ಆಗಲಾರರು.');
INSERT INTO "kannada" VALUES(48,1,'48:1','(ಪ್ರವಾದಿವರ್ಯರೇ,) ನಾವು ತಮಗೆ ಸುಸ್ಪಷ್ಟ ವಿಜಯವೊಂದನ್ನು ಪ್ರದಾನ ಮಾಡಿದೆವು.');
INSERT INTO "kannada" VALUES(48,2,'48:2','');
INSERT INTO "kannada" VALUES(48,3,'48:3','ಅಲ್ಲಾಹನು ತಮ್ಮ ಹಿಂದಿನ ಹಾಗೂ ಮುಂದಿನ ಎಲ್ಲ ಎಡವುಗಳನ್ನು ತಮಗೆ ಕ್ಷಮಿಸಲಿಕ್ಕಾಗಿ, ತಮ್ಮ ಮೇಲೆ ತನ್ನ ಅನುಗ್ರಹವನ್ನು ಪರಿಪೂರ್ಣಗೊಳಿಸಲಿಕ್ಕಾಗಿ, ತಮಗೆ ಸನ್ಮಾರ್ಗವನ್ನು ನೆಲೆಗೊಳಿಸಲಿಕ್ಕಾಗಿ ಮತ್ತು ತಮಗೆ ಘನವೆತ್ತ ಸಹಾಯವನ್ನು ನೀಡಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(48,4,'48:4','ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಹೃದಯಗಳಲ್ಲಿ ಪ್ರಶಾಂತತೆಯನ್ನು ಇಳಿಸಿದವನು ಅವನೇ. ಅವರು ತಮ್ಮ ವಿಶ್ವಾಸದ ಮೇಲೆ ವಿಶ್ವಾಸವನ್ನು ವರ್ಧಿಸಿಕೊಳ್ಳುವಂತಾಗಲಿಕ್ಕಾಗಿ, ಅಲ್ಲಾಹನಿಗೆ ಆಕಾಶಗಳಲ್ಲೂ, ಭೂಮಿಯಲ್ಲೂ ಸೇನೆಗಳಿವೆ. ಅವನು ಸರ್ವಜ್ಞನೂ, ಯುಕ್ತಿವಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(48,5,'48:5','ಸತ್ಯವಿಶ್ವಾಸಿ ಪುರುಷರನ್ನು ಮತ್ತು ಸ್ತೀಯರನ್ನು ತಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆಗಳು ಹರಿಯುತ್ತಿರುವಂ ತಹ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ ಸ್ಥಿರವಾಸಿಗಳಾಗಿ ಪ್ರವೇಶಗೊಳಿಸಲಿಕ್ಕಾಗಿ ಮತ್ತು ಅವರ ದೋಷಗಳನ್ನು ಕ್ಷಮಿಸಿ ಕೊಡಲಿಕ್ಕಾಗಿ. ಅಲ್ಲಾಹನ ಬಳಿ ಅದು ಮಹಾ ವಿಜಯವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(48,6,'48:6','ಅಲ್ಲಾಹನ ಕುರಿತು ಕೆಟ್ಟ ಭಾವನೆಗಳನ್ನಿಟ್ಟುಕೊಂಡಿದ್ದ ಕಪಟ ವಿಶ್ವಾಸಿಗಳಾದ ಸ್ತ್ರೀ-ಪುರುಷರನ್ನೂ ಬಹುದೇವಾರಾಧಕರಾದ ಸ್ತ್ರೀ-ಪುರುಷರನ್ನೂ ಶಿಕ್ಷಿಸಲಿಕ್ಕಾಗಿ. ಅವರಿಗೆ ನಾಶವು ಸುತ್ತುವರಿದಿದೆ. ಅವರ ಮೇಲೆ ಅಲ್ಲಾಹನು ಕ್ರೋಧಗೊಂಡನು ಮತ್ತು ಅವರನ್ನು ಶಪಿಸಿದನು. ಅವರಿಗಾಗಿ ಅತ್ಯಂತ ಕೆಟ್ಟ ವಾಸಸ್ಥಾನವಾಗಿರುವ ನರಕವನ್ನು ಕಾದಿರಿಸಿದನು.');
INSERT INTO "kannada" VALUES(48,7,'48:7','ಅಲ್ಲಾಹನಿಗೆ ಆಕಾಶ-ಭೂಮಿಗಳಲ್ಲಿ ಸೇನೆಗಳಿವೆ. ಅವನು ಪ್ರಬಲನೂ ಯುಕ್ತಿಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(48,8,'48:8','(ಸಂದೇಶವಾಹಕರೇ,) ನಾವು ತಮ್ಮನ್ನು ಸಾಕ್ಷಿ ಯಾಗಿಯೂ, (ಸ್ವರ್ಗದ) ಸುವಾರ್ತೆ ನೀಡುವವ ರಾಗಿಯೂ ಮತ್ತು (ನರಕದ) ಎಚ್ಚರಿಕೆ ನೀಡುವವರಾಗಿಯೂ ನಿಯೋಗಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(48,9,'48:9','(ಜನರೇ,) ನೀವು ಅಲ್ಲಾಹನಲ್ಲೂ ಅವನ ದೂತರಲ್ಲೂ ವಿಶ್ವಾಸವಿರಿಸಲು, ಅವನಿಗೆ (ಅಲ್ಲಾಹನಿಗೆ) ನೆರವಾಗಲು, ಅವನನ್ನು ಗೌರವಿಸಲು ಮತ್ತು ಸಂಜೆ- ಮುಂಜಾನೆ ಅವನ ಪರಿಶುದ್ಧಿಯನ್ನು ಪ್ರಕೀರ್ತಿಸಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(48,10,'48:10','(ಸಂದೇಶವಾಹಕರೇ,) ತಮ್ಮೊಂದಿಗೆ (ಹುದೈಬಿ ಯಾದಲ್ಲಿ) ಒಪ್ಪಂದ ಮಾಡಿಕೊಂಡವರು ವಾಸ್ತವದಲ್ಲಿ ಅಲ್ಲಾಹನೊಂದಿಗೆ ಒಪ್ಪಂದ ಮಾಡಿಕೊಂಡರು. ಅವರ ಹಸ್ತಗಳ ಮೇಲೆ ಅಲ್ಲಾಹನ ಜ್ಞಾನ ಹಸ್ತವಿದೆ . ಹೀಗಾಗಿ ಯಾರು ಈ ಒಪ್ಪಂದವನ್ನು ಉಲ್ಲಂಘಿಸುತ್ತಾರೆ ಅವನು ತನ್ನ ವಿರುದ್ಧವೇ ಆ ಉಲ್ಲಂಘನೆ ನಡೆಸುತ್ತಾನೆ. ಯಾರು ಅಲ್ಲಾಹನೊಂದಿಗೆ ಮಾಡಿದ ಕರಾರನ್ನು ಪಾಲಿಸುವನೋ ಅವನಿಗೆ ಅಲ್ಲಾಹನು ಶೀಘ್ರವೇ ಘನವೆತ್ತ ಪ್ರತಿಫಲವನ್ನು ದಯಪಾಲಿಸುವನು.');
INSERT INTO "kannada" VALUES(48,11,'48:11','(ಸಂದೇಶವಾಹಕರೇ,) ನಿಮ್ಮ ಜತೆ ಬಾರದೆ ಉಳಿದ ಗುಡ್ಡಗಾಡು ಜನಾಂಗದವರು ಖಂಡಿತ ನಿಮ್ಮ ಬಳಿ ಬಂದು; “ನಮ್ಮ ಸೊತ್ತು ಮತ್ತು ಮನೆ ಮಂದಿಯ ಚಿಂತೆಯು ನಮಗೆ ತಡೆಯಾದುವು. ನಮ್ಮ ಕ್ಷಮೆಗಾಗಿ ತಾವು ಪ್ರಾರ್ಥಿಸಿರಿ” ಎಂದು ಹೇಳುವರು. ಇವರು ತಮ್ಮ ಹೃದಯಗಳಲ್ಲಿಲ್ಲದ ಮಾತುಗಳನ್ನು ನಾಲಗೆಗಳಿಂದ ಆಡುತ್ತಾರೆ. ಹೇಳಿರಿ; “ಹಾಗಾದರೆ ಅಲ್ಲಾಹನು ನಿಮಗೆ ಯಾವುದೇ ದೋಷವನ್ನು ಮಾಡಲು ಇಚ್ಛಿಸಿದರೆ, ಅಥವಾ ನಿಮಗೆ ಯಾವುದೇ ಗುಣ ವನ್ನು ಅವನು ಇಚ್ಛಿಸಿದರೆ ಅಲ್ಲಾಹನಿಂದ ಯಾವುದೇ ವಸ್ತುವನ್ನು ಅಧೀನಗೊಳಿಸಬಲ್ಲವರು ಯಾರಿದ್ದಾರೆ? ಆದರೆ ನೀವು ಮಾಡುವ ಕರ್ಮದ ಬಗ್ಗೆ ಅಲ್ಲಾಹನು ಸೂಕ್ಷ್ಮಜ್ಞಾನಿಯಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(48,12,'48:12','ವಾಸ್ತವದಲ್ಲಿ ದೂತರು ಮತ್ತು ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ತಮ್ಮ ಕುಟುಂಬಗಳಿಗೆ ಎಂದಿಗೂ ಮರಳಲಾರರು ಎಂದು ನೀವು ಭಾವಿಸಿಕೊಂಡಿದ್ದಿರಿ. ಅದು ನಿಮ್ಮ ಮನಸ್ಸುಗಳಲ್ಲಿ ಮೆರುಗು ಮೂಡಿಸಿತು. ನೀವು ಬಹಳ ಕೆಟ್ಟ ಗುಮಾನಿಗಳನ್ನು ಮಾಡಿದಿರಿ. ನೀವು ನಾಶ ಹೊಂದಿದ ಜನಾಂಗವಾದಿರಿ”.');
INSERT INTO "kannada" VALUES(48,13,'48:13','ಯಾರು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ದೂತರಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸಲಿಲ್ಲವೋ, (ಅವನು ಪರಾಜಿತನಾ ದನು. ಏಕೆಂದರೆ,) ಸತ್ಯನಿಷೇಧಿಗಳಿಗಾಗಿ ನಾವು ಕಠಿಣವಾದ ನರಕಾಗ್ನಿಯನ್ನು ಸಿದ್ಧ ಗೊಳಿಸಿಟ್ಟಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(48,14,'48:14','ಆಕಾಶಗಳು ಮತ್ತು ಭೂಮಿಯ ಆಧಿಪತ್ಯ ಅಲ್ಲಾಹನಿಗೆ. ಅವನು ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ಕ್ಷಮಿ ಸುತ್ತಾನೆ, ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ಶಿಕ್ಷಿಸುತ್ತಾನೆ. ಅವನು ಕ್ಷಮಾಶೀಲನೂ ದಯಾಳುವೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(48,15,'48:15','ನೀವು ಸಮರಾರ್ಜಿತ ಸೊತ್ತನ್ನು ಪಡೆಯಲು ಹೊರಟಾಗ ಹಿಂದುಳಿದ ಈ ಜನರು-ನಮ್ಮನ್ನೂ ನೀವು (ತಡೆಯದೆ) ಬಿಟ್ಟು ಬಿಡಿರಿ. ನಿಮ್ಮ ಜೊತೆ ನಾವು ಹಿಂಬಾಲಿಸುತ್ತೇವೆ ಎಂದು ನಿಮ್ಮೊಡನೆ ಖಂಡಿತ ಹೇಳುವರು. ಇವರು ಅಲ್ಲಾಹನ ಮಾತನ್ನು ಬದಲಿಸಿ ಬಿಡಬೇಕೆಂದು ಇಚ್ಛಿಸುತ್ತಾರೆ . ಹೇಳಿರಿ; ‘ಖಂಡಿತ ನೀವು ನಮ್ಮ ಜೊತೆ ಬರುವಂತಿಲ್ಲ. ಅಲ್ಲಾಹನು ಈ ಮೊದಲೇ ಇದನ್ನು ಹೇಳಿರುತ್ತಾನೆ’. ಆಗ ಇವರು - ‘ಇಲ್ಲ, ನೀವು ನಮ್ಮ ಬಗ್ಗೆ ಅಸೂಯೆಪಡುತ್ತಿರುವಿರಿ’ ಎಂದು ಹೇಳುವರು. ಆದರೆ ಗ್ರಹಿಸಿಕೊಳ್ಳುವವರು ಇವರಲ್ಲಿ ಅಲ್ಪ ಜನ ಮಾತ್ರವೇ ಹೊರತು ಇನ್ನಾರೂ ಯೋಚಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(48,16,'48:16','ಹಿಂಜರಿದಂತಹ ಈ ಗ್ರಾಮೀಣ ಅರಬರೊಂದಿಗೆ ಹೇಳಿರಿ: “ಶೀಘ್ರವೇ ನಿಮ್ಮನ್ನು ಬಹಳ ಬಲಿಷ್ಟರಾಗಿರುವ ಜನಾಂಗದೊಂದಿಗೆ ಯುದ್ಧ ಮಾಡಲಿಕ್ಕಾಗಿ ಕರೆಯಲಾಗುವುದು. ನೀವು ಅವರೊಂದಿಗೆ ಯುದ್ದ ಮಾಡಬೇಕಾದೀತು. ಅಥವಾ ಅವರು ಶರಣಾಗುವರು. ಆಗ ನೀವು ಆಜ್ಞೆಯನ್ನು ಪಾಲಿಸಿದರೆ, ಅಲ್ಲಾಹು ನಿಮಗೆ ಅತ್ಯುತ್ತಮ ಪ್ರತಿಫಲ ನೀಡುವನು. ನೀವು ಈ ಹಿಂದೆ ವಿಮುಖರಾದಂತೆಯೇ ಪುನಃ ವಿಮುಖರಾದರೆ ಅಲ್ಲಾಹು ನಿಮಗೆ ವೇದನಾಯುಕ್ತ ಯಾತನೆ ಕೊಡುವನು.');
INSERT INTO "kannada" VALUES(48,17,'48:17','ಕುರುಡನ ಮೇಲೆ ತಪ್ಪಿಲ್ಲ. ಕುಂಟನ ಮೇಲೂ ತಪ್ಪಿಲ್ಲ. ಮತ್ತು ರೋಗಿಯ ಮೇಲೂ ತಪ್ಪಿಲ್ಲ. ಯಾರು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ದೂತರ ಅನು ಸರಣೆ ಮಾಡುತ್ತಾರೋ, ತಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆಗಳು ಹರಿಯುತ್ತಿರುವ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಿಗೆ ಅಲ್ಲಾಹನು ಪ್ರವೇಶ ಕೊಡುವನು. ಯಾರು ವಿಮುಖನಾಗುವನೋ ಅವನಿಗೆ ಅವನು ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆಯನ್ನು ಕೊಡುವನು.');
INSERT INTO "kannada" VALUES(48,18,'48:18','ಆ ವೃಕ್ಷದಡಿಯಲ್ಲಿ ನಿಮ್ಮೊಂದಿಗೆ ಸತ್ಯವಿಶ್ವಾಸಿ ಗಳು ಆಜ್ಞಾನುಸರಣೆಯ ಒಪ್ಪಂದ ಮಾಡುತ್ತಿದ್ದಾಗ ಅಲ್ಲಾಹನು ಅವರಿಂದ ಸಂತುಷ್ಟನಾದನು . ಅವನು ಅವರ ಮನಸ್ಸಿನಲ್ಲಿರುವುದನ್ನು ತಿಳಿದಿದ್ದನು. ಆದುದರಿಂದ ಅವನು ಅವರ ಮೇಲೆ ಪ್ರಶಾಂತತೆಯನ್ನು ಇಳಿಸಿದನು. ಅವರಿಗೆ ಶೀಘ್ರ ವಿಜಯವನ್ನು ಪ್ರತಿಫಲವನ್ನಾಗಿ ನೀಡಿದನು.');
INSERT INTO "kannada" VALUES(48,19,'48:19','ಮತ್ತು ಅವರು ಪಡೆದುಕೊಳ್ಳುವಂತಹ ಧಾರಾಳ ಸಮರಾರ್ಜಿತ ಸೊತ್ತನ್ನು ನೀಡಲಿರುವನು. ಅಲ್ಲಾಹು ಪ್ರಬಲನೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(48,20,'48:20','ನೀವು ಪಡೆಯುವಂತಹ ಧಾರಾಳ ಸಮರಾರ್ಜಿತ ಸೊತ್ತಿನ ವಾಗ್ದಾನವನ್ನು ಅಲ್ಲಾಹನು ನೀಡಿರುವನು. ಇದನ್ನು (ಖೈಬರಿನ ಸಮರಾರ್ಜಿತ ಸ್ವತ್ತನ್ನು) ಅವನು ನಿಮಗೆ ಧೃತಿಯಲ್ಲಿ ಕೊಟ್ಟನು ಮತ್ತು ಜನರ ಕೈಗಳು ನಿಮ್ಮ ವಿರುದ್ಧ ಏಳದಂತೆ ತಡೆದನು. ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಪಾಲಿಗೆ ಇದು ಒಂದು ನಿದರ್ಶನವಾಗಲೆಂದು ಮತ್ತು ಅಲ್ಲಾ ಹನು ನಿಮ್ಮನ್ನು ಸನ್ಮಾರ್ಗದೆಡೆಗೆ ಸಾಗಿಸಲೆಂದು.');
INSERT INTO "kannada" VALUES(48,21,'48:21','ನಿಮಗಿನ್ನೂ ಗಳಿಸಲು ಸಾಧ್ಯವಾಗದ ಮತ್ತಷ್ಟು ಸಮರಾರ್ಜಿತ ಸೊತ್ತನ್ನೂ ಅವನು ನಿಮಗೆ ವಾಗ್ದಾನ ಮಾಡಿರುವನು. ಅಲ್ಲಾಹನು ಅವುಗಳನ್ನು ಸುತ್ತುವರಿದಿದ್ದಾನೆ . ಅಲ್ಲಾಹನು ಸಕಲ ವಿಷಯಗಳಿಗೆ ಸಾಮಥ್ರ್ಯವುಳ್ಳವನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(48,22,'48:22','ಆ ಸತ್ಯನಿಷೇಧಿಗಳು ನಿಮ್ಮೊಂದಿಗೆ ಯುದ್ಧ ಮಾಡುತ್ತಿದ್ದರೆ ಖಂಡಿತ ಪಲಾಯನ ಮಾಡುತ್ತಿದ್ದರು. ನಂತರ ಅವರಿಗೆ ರಕ್ಷಕ ಮಿತ್ರರಾಗಲಿ, ಸಹಾಯಕರಾಗಲಿ ಯಾರೂ ಸಿಗುತ್ತಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(48,23,'48:23','ಇದು ಹಿಂದಿನಿಂದಲೇ ನಡೆದು ಬರುತ್ತಿರುವ ಅಲ್ಲಾಹನ ಕಾರ್ಯವಿಧಾನವಾಗಿದೆ. ಅಲ್ಲಾಹನ ಕಾರ್ಯವಿಧಾನದಲ್ಲಿ ಯಾವ ಬದಲಾವಣೆಯನ್ನೂ ನೀವು ಕಾಣಲಾರಿರಿ.');
INSERT INTO "kannada" VALUES(48,24,'48:24','ಅವರನ್ನು ನೀವು ಗೆದ್ದ ನಂತರ ಮಕ್ಕಾ ಕಣಿವೆಯಲ್ಲಿ (ಹುದೈಬಿಯಾದಲ್ಲಿ) ಅವರ ಕೈಗಳನ್ನು ನಿಮ್ಮಿಂದಲೂ ನಿಮ್ಮ ಕೈಗಳನ್ನು ಅವರಿಂದಲೂ ತಡೆದವನು ಅವನೇ, ನೀವು ಮಾಡುತ್ತಿದ್ದುದನ್ನೆಲ್ಲ ಅಲ್ಲಾಹನು ವೀಕ್ಷಿಸುತ್ತಿದ್ದನು.');
INSERT INTO "kannada" VALUES(48,25,'48:25','ಇವರೇ ಅವಿಶ್ವಾಸ ತಾಳಿದವರು. ಮಸ್ಜಿದುಲ್ ಹರಾಮ್‍ನಿಂದ ನಿಮ್ಮನ್ನು ತಡೆದವರು ಮತ್ತು ಬಲಿ ಪ್ರಾಣಿಗಳನ್ನು ಅವುಗಳ ನೆಲೆಗೆ ತಲುಪಲು ಬಿಡದವರು. ನಿಮಗೆ ತಿಳಿಯದ ಸತ್ಯವಿಶ್ವಾಸಿ ಪುರುಷರು ಮತ್ತು ಸತ್ಯವಿಶ್ವಾಸಿನಿ ಸ್ತ್ರೀಯರು (ಮಕ್ಕಾದಲ್ಲಿ ಇವರ ಜೊತೆ) ಇಲ್ಲದಿರುತ್ತಿದ್ದರೆ, ಅಂದರೆ (ನಿಮಗೆ ಯುದ್ಧಾನುಮತಿ ಸಿಕ್ಕಿದರೆ) ನೀವು ಅವರ ಬಗ್ಗೆ ತಿಳುವಳಿಕೆಯಿಲ್ಲದೆ (ಅವಿಶ್ವಾಸಿಗಳೊಂದಿಗೆ) ವಧಿಸಿ ಬಿಡುವ ಹಾಗೂ ಮತ್ತು ಅದರಿಂದ ನಿಮಗೆ ದೋಷ ಬರುವ ಅಪಾಯವಿಲ್ಲದಿರುತ್ತಿದ್ದರೆ, (ಮಕ್ಕಾದ ಮೇಲೆ ದಾಳಿ ಮಾಡಲು ನಿಮಗೆ ಅನುಮತಿ ನೀಡಲಾಗುತ್ತಿತ್ತು). ಇದು ಅಲ್ಲಾಹನು ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ತನ್ನ ಕೃಪೆಯಲ್ಲಿ ಸೇರಿಸಿಕೊಳ್ಳುವಂತಾಗಲು. ಆ ಸತ್ಯವಿಶ್ವಾಸಿಗಳು (ಅವಿಶ್ವಾಸಿಗಳಿಂದ) ಬೇರ್ಪಟ್ಟಿರುತ್ತಿದ್ದರೆ ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ನಾವು ಖಂಡಿತವಾಗಿಯೂ ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆ ಕೊಡುತ್ತಿದ್ದೆವು .');
INSERT INTO "kannada" VALUES(48,26,'48:26','ಆ ಸತ್ಯನಿಷೇಧಿಗಳು ತಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ಅಜ್ಞಾನ ಜನ್ಯ ದುರಭಿಮಾನವನ್ನಿರಿಸಿಕೊಂಡಾಗ ಅಲ್ಲಾಹನು ತನ್ನ ದೂತರ ಮತ್ತು ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಮೇಲೆ ಪ್ರಶಾಂತತೆಯನ್ನು ಇಳಿಸಿದನು. ಮತ್ತು ಅವರನ್ನು ದೇವಭಕ್ತಿಯ ನುಡಿಗೆ ಬದ್ಧರಾಗಿರುವಂತೆ ಮಾಡಿದನು. ಏಕೆಂದರೆ ಇದಕ್ಕೆ ಅವರೇ ಹೆಚ್ಚು ಹಕ್ಕುದಾರರೂ ಅರ್ಹರೂ ಆಗಿದ್ದರು. ಅಲ್ಲಾಹನು ಸಕಲ ವಿಷಯಗಳ ಜ್ಞಾನವುಳ್ಳವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(48,27,'48:27','ನಿಜಕ್ಕೂ ಅಲ್ಲಾಹನು ತನ್ನ ದೂತರಿಗೆ ಸ್ವಪ್ನವನ್ನು ಸಂಪೂರ್ಣ ಸತ್ಯವಾಗಿ ನಿಜಗೊಳಿಸಿದನು. ಅಂದರೆ ಅಲ್ಲಾಹನಿಚ್ಛಿಸಿದರೆ, ನೀವು ಸಮಾಧಾನ ಚಿತ್ತರಾಗಿ ಕೇಶ ಮುಂಡನ ಮಾಡಿದವರೂ, ಕೂದಲು ಕತ್ತರಿಸಿಕೊಂಡವರೂ ಆಗಿ ಯಾವುದೇ ಭಯವಿಲ್ಲದೆ ಮಸ್ಜಿದುಲ್ ಹರಾಮನ್ನು ಖಂಡಿತ ಪ್ರವೇಶಿಸುವಿರಿ ಎಂಬ ಸ್ವಪ್ನವನ್ನು. ಆದರೆ ನೀವು ಅರಿಯದಿದ್ದುದನ್ನು ಅವನು ಅರಿತಿದ್ದನು . ಆದುದರಿಂದ ಅದಕ್ಕೆ (ಮಕ್ಕಾ ಪ್ರವೇಶಕ್ಕೆ) ಹೊರತಾಗಿ ಅವನು ಸನ್ನಿಹಿತವಾದ ಒಂದು ವಿಜಯವನ್ನು (ಖೈಬರ್ ವಿಜಯವನ್ನು) ನಿಮಗೆ ದಯಪಾಲಿಸಿದನು.');
INSERT INTO "kannada" VALUES(48,28,'48:28','ಅವನೇ ತನ್ನ ಸಂದೇಶವಾಹಕನನ್ನು ಸನ್ಮಾರ್ಗ ಹಾಗೂ ಸತ್ಯಧರ್ಮದೊಂದಿಗೆ ಕಳುಹಿಸಿದವನು. ಅದನ್ನು ಸಕಲ ಧರ್ಮಗಳ ಮೇಲೆ ಸಮರ್ಥಿಸಿ ತೋರಿಸಲಿಕ್ಕಾಗಿ. ಸಾಕ್ಷಿಯಾಗಿ ಅಲ್ಲಾಹನೇ ಸಾಕು .');
INSERT INTO "kannada" VALUES(48,29,'48:29','ಮುಹಮ್ಮದರು ಅಲ್ಲಾಹನ ಸಂದೇಶವಾಹಕರು. ಅವರ ಜೊತೆಗಿರುವವರು ಸತ್ಯನಿಷೇಧಿಗಳ ಪಾಲಿಗೆ ಕಠಿಣರೂ ಪರಸ್ಪರ ಕರುಣಾಳುಗಳೂ ಆಗಿರುತ್ತಾರೆ. ಅಲ್ಲಾಹನ ಅನುಗ್ರಹ ಮತ್ತು ಸಂಪ್ರೀತಿಯನ್ನು ಅರಸುತ್ತ ಅವರು ರುಕೂಅï ಮತ್ತು ಸುಜೂದ್ ಮಾಡಿ ನಮಾಝ್ ಮಾಡು ವುದನ್ನು ನೀವು ಕಾಣುವಿರಿ. ಸುಜೂದ್‍ನ ಫಲ ವಾಗಿ ಅವರ ಗುರುತುಗಳು ಅವರ ಮುಖಗಳಲ್ಲಿವೆ . ಇದು ತೌರಾತ್‍ನಲ್ಲಿರುವ, ಅವರ ಗುಣ ವಿಶೇಷವಾಗಿದೆ. ಇನ್ಜೀಲ್‍ನಲ್ಲಿ ಅವರ ಉದಾಹರಣೆಯನ್ನು ಹೀಗೆ ಕೊಡಲಾಗಿದೆ . ಒಂದು ಪೈರು, ಅದು ಟಿಸಿಲೊಡೆಯಿತು. ತರುವಾಯ ಅದನ್ನು ಬಲಪಡಿಸಿಕೊಂಡಿತು. ಅನಂತರ ಅದು ಬಿರುಸುಗೊಂಡಿತು. ನಂತರ ಅದು ಕೃಷಿಕನನ್ನು ಮುದಗೊಳಿಸುತ್ತಾ ತನ್ನ ಕಾಂಡದ ಮೇಲೆ ನೆಲೆಯೂರಿತು. (ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಈ ಉಪಮೆ ನೀಡಿದ್ದು) ಇವರ ಮುಖಾಂತರ ಸತ್ಯನಿಷೇಧಿಗ ಳನ್ನು ಕೆರಳಿಸುವ ಸಲುವಾಗಿ. ಇವರ ಕೂಟದಲ್ಲಿ ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿಕೊಂಡು ಸತ್ಕರ್ಮವೆಸಗಿದವರಿಗೆ ಅಲ್ಲಾಹನು ಕ್ಷಮೆ ಮತ್ತು ಘನವೆತ್ತ ಪ್ರತಿಫಲ ವಾಗ್ದಾನವಿತ್ತಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(49,1,'49:1','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ದೂತರ ಮುಂದೆ ಮೀರಿ ನಡೆಯಬೇಡಿರಿ . ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ. ಅಲ್ಲಾಹನು ಸರ್ವಶೃತನೂ ಸರ್ವಜ್ಞನೂ ಆಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(49,2,'49:2','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನಿಮ್ಮ ಧ್ವನಿಗಳನ್ನು ಪ್ರವಾದಿವರ್ಯರ ಧ್ವನಿಗಿಂತ ಎತ್ತರಿಸಬೇಡಿರಿ! ನೀವು ಪರಸ್ಪರ ಸ್ವರವೆತ್ತಿ ಮಾತನಾಡುವಂತೆ ಪ್ರವಾದಿವರ್ಯರೊಡನೆ ಉಚ್ಚ ಸ್ವರದಲ್ಲಿ ಮಾತನಾಡ ಬೇಡಿರಿ. ನಿಮಗೆ ಅರಿವೇ ಇಲ್ಲದಂತೆ ನಿಮ್ಮ ಸುಕೃತಗಳು ನಿಷ್ಪಲವಾಗುವ ಅಪಾಯವಿರುವುದರಿಂದ (ಈ ಆದೇಶ ನೀಡಲಾಗಿದೆ) .');
INSERT INTO "kannada" VALUES(49,3,'49:3','ಅಲ್ಲಾಹನ ದೂತರ ಸನ್ನಿಧಿಯಲ್ಲಿ ತಮ್ಮ ಧ್ವನಿಯನ್ನು ತಗ್ಗಿಸಿಕೊಳ್ಳುವವರ್ಯಾರೋ ಅವರ ಹೃದಯಗಳನ್ನು ಅಲ್ಲಾಹನು ತಕ್ವಾಕ್ಕೆ (ಭಯ ಭಕ್ತಿಗೆ) ಪರೀಕ್ಷಿಸಿ ತೆಗೆದಿರುವನು. ಅವರಿಗೆ ಪಾಪ ಮುಕ್ತಿ ಯೂ ಘನವೆತ್ತ ಪ್ರತಿಫಲವೂ ಇದೆ .');
INSERT INTO "kannada" VALUES(49,4,'49:4','(ಸಂದೇಶವಾಹಕರೇ,) ತಮ್ಮನ್ನು ಕೊಠಡಿಗಳ ಹೊರಗಿನಿಂದ ಕೂಗಿ ಕರೆಯುವವರ ಪೈಕಿ ಹೆಚ್ಚಿನವರು (ತಮ್ಮ ಮಹತ್ವವನ್ನು) ಗ್ರಹಿಸಿಕೊಂಡವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(49,5,'49:5','ತಾವು ಅವರ ಬಳಿಗೆ ಹೊರಟು ಬರುವ ತನಕ ಅವರು ತಾಳ್ಮೆ ವಹಿಸುತ್ತಿದ್ದರೆ ಅದು ಅವರಿಗೆ ಬಹಳ ಒಳ್ಳೆಯದಿತ್ತು. ಅಲ್ಲಾಹನು ಅತಿ ಕ್ಷಮಾಶೀಲನೂ ಕರುಣಾವಾರಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(49,6,'49:6','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಅಶ್ರದ್ಧನೊಬ್ಬನು ನಿಮ್ಮ ಬಳಿ ಗೇನಾದರೂ ಸುದ್ದಿ ತಂದರೆ ಅದರ ಬಗ್ಗೆ ಸ್ಪಷ್ಟವಾಗಿ (ಸತ್ಯಾಸತ್ಯತೆಯನ್ನು) ತಿಳಿದುಕೊಳ್ಳಿರಿ. ನೀವು ತಿಳಿಯದೆ ಯಾವುದೇ ಒಂದು ಸಮೂಹಕ್ಕೆ ಹಾನಿ ಮಾಡಿ, ಆ ಬಳಿಕ ನಿಮ್ಮ ಕೃತ್ಯದ ಬಗ್ಗೆ ಪಶ್ಚಾ ತ್ತಾ ಪಪಡುವಂತಾಗದಿರಲು (ಈ ಆದೇಶ) ');
INSERT INTO "kannada" VALUES(49,7,'49:7','ನಿಮ್ಮಲ್ಲಿ ಅಲ್ಲಾಹನ ಸಂದೇಶವಾಹಕರಿದ್ದಾರೆ ಎಂಬುದನ್ನು ನೀವು ಚೆನ್ನಾಗಿ ತಿಳಿದುಕೊಳ್ಳಿರಿ. ಹೆಚ್ಚಿನ ವಿಷಯಗಳಲ್ಲಿ ಅವರು ನಿಮ್ಮ ಮಾತನ್ನು ಅನುಸರಿಸಿದರೆ, ನೀವು ಸಂಕಷ್ಟಕ್ಕೊಳಗಾಗುವಿರಿ. ಆದರೆ, ಅಲ್ಲಾಹನು ನಿಮಗೆ ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಪ್ರಿಯಗೊಳಿಸಿರುವನು ಹಾಗೂ ಅದನ್ನು ನಿಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ಚಂದಗಾಣಿಸಿರುವನು. ಅವಿಶ್ವಾಸ, ಕರ್ಮಭ್ರಷ್ಟತೆ, ಆಜ್ಞೋಲ್ಲಂಘನೆಯನ್ನು ನಿಮಗೆ ಅನಿಷ್ಟಗೊಳಿಸಿದನು. ಅವರೇ ಸನ್ಮಾರ್ಗದರ್ಶಿಗಳು.');
INSERT INTO "kannada" VALUES(49,8,'49:8','(ಈ ವಿಜಯವು) ಅಲ್ಲಾಹನ ಔದಾರ್ಯ ಮತ್ತು ಕೃಪೆಯಿಂದಲೇ ಆಗಿದೆ. ಅಲ್ಲಾಹು ಸರ್ವಜ್ಞನೂ ಯುಕ್ತಿವಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(49,9,'49:9','ಸತ್ಯವಿಶ್ವಾಸಿಗಳಲ್ಲಿ ಎರಡು ಗುಂಪುಗಳು ಪರಸ್ಪರ ಕಾದಾಡಿದರೆ, ಅವರ ನಡುವೆ ನೀವು ಸಂಧಾನ ಮಾಡಿಸಿರಿ. ಅವರಲ್ಲಿ ಒಂದು ಗುಂಪು ಇನ್ನೊಂದು ಗುಂಪಿನ ಮೇಲೆ ಅತಿರೇಕವೆಸಗಿದರೆ, ಅತಿರೇಕವೆಸಗಿದವರು ಅಲ್ಲಾಹನ ಆದೇಶದೆಡೆಗೆ ಮರಳಿ ಬರುವವರೆಗೂ ನೀವು ಅವರ ವಿರುದ್ಧ ಹೋರಾಡಿರಿ. ಆಮೇಲೆ ಅವರು ಮರಳಿ ಬಂದರೆ, ಅವರ ಮಧ್ಯೆ ನ್ಯಾಯಬದ್ಧ ಸಂಧಾನ ಮಾಡಿಸಿರಿ ಮತ್ತು ನ್ಯಾಯವನ್ನು ಪಾಲಿಸಿರಿ. ನ್ಯಾಯ ಪರಿಪಾಲಕರನ್ನು ಅಲ್ಲಾಹು ಖಂಡಿತಾ ಪ್ರೀತಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(49,10,'49:10','ನಿಶ್ಚಯವಾಗಿಯೂ ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಪರಸ್ಪರ ಸಹೋದರರು. ಆದುದರಿಂದ ನಿಮ್ಮ ಇಬ್ಬರು ಸಹೋದರರ ನಡುವೆ ಸಂಧಾನ ನಡೆಸಿರಿ ಮತ್ತು ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ. ನೀವು ಅನುಗ್ರಹೀತರಾಗಬಹುದು .');
INSERT INTO "kannada" VALUES(49,11,'49:11','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಒಂದು ವಿಭಾಗ ಇನ್ನೊಂದು ವಿಭಾಗವನ್ನು ಪರಿಹಾಸ್ಯ ಮಾಡದಿರಲಿ. ಅವರು (ಪರಿಹಾಸ್ಯಕ್ಕೊಳಗಾದವರು) ಇವರಿಗಿಂತ ಉತ್ತಮರಿರಲೂಬಹುದು. ಸ್ತ್ರೀಯರು ಇತರ ಸ್ತ್ರೀಯರನ್ನೂ ಪರಿಹಾಸ್ಯ ಮಾಡದಿರಲಿ. ಅವರು (ಪರಿಹಾಸ್ಯ ಕ್ಕೊಳಗಾದ ಸ್ತ್ರೀಯರು) ಇವರಿಗಿಂತ ಉತ್ತಮರಿರಲೂಬಹುದು. ನೀವು ನಿಮ್ಮನ್ನೇ ಹಳಿದುಕೊಳ್ಳಬೇಡಿರಿ. ಮತ್ತು ಪರಸ್ಪರ ಅಡ್ಡ ಹೆಸರಿನಿಂದ ಕರೆದುಕೊಳ್ಳದಿರಿ. ಸತ್ಯ ವಿಶ್ವಾಸ ಸ್ವೀಕರಿಸಿದ ಬಳಿಕ ಅಧಾರ್ಮಿಕ ಹೆಸರು ಎಷ್ಟೊಂದು ನಿಕೃಷ್ಟ. ಯಾರು ಇದರಿಂದ ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು ಮರಳುವುದಿಲ್ಲವೋ ಅವರೇ ಅಕ್ರಮಿಗಳು .');
INSERT INTO "kannada" VALUES(49,12,'49:12','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಹೆಚ್ಚಿನ ಗುಮಾನಿಗಳಿಂದ ದೂರವಿರಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ಕೆಲವು ಗುಮಾನಿ ಗಳು ಪಾಪಕರವಾಗಿವೆ. ನೀವು ಗೂಡಚಾರಿಕೆ ನಡೆಸದಿರಿ. ನೀವು ಪರಸ್ಪರ ಪರದೂಷಣೆ ಮಾಡದಿರಿ. ನೀವು ಪ್ರತಿಯೊಬ್ಬರು ತನ್ನ ಸಹೋದರ ಮೃತನಾಗಿರುವಾಗ ಅವನ ಕಳೇಬರದ ಮಾಂಸ ತಿನ್ನಲು ಇಷ್ಟಪಡುವಿರಾ? ನೀವದನ್ನು ಅನಿಷ್ಟಪಡುವಿರಿ. ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ, ಅಲ್ಲಾಹು ಯಥೇಚ್ಚಪಶ್ಚಾತ್ತಾಪ ಸ್ವೀಕರಿಸುವವನೂ ಪರಮ ದಯಾಳುವೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(49,13,'49:13','ಓ ಜನರೇ, ನಾವು ನಿಮ್ಮನ್ನು ಒಬ್ಬ ಪುರುಷ ಮತ್ತು ಒಬ್ಬ ಸ್ತ್ರೀಯಿಂದ ಸೃಷ್ಟಿಸಿದೆವು. ನೀವು ಪರಸ್ಪರ ಗುರುತಿಸಿಕೊಳ್ಳುವ ಸಲುವಾಗಿ ನಾವು ನಿಮ್ಮನ್ನು ವಿವಿಧ ಜನಾಂಗಗಳಾಗಿಯೂ, ಗೋತ್ರ ಗಳಾಗಿಯೂ ಮಾಡಿದೆವು. ನಿಮ್ಮ ಪೈಕಿ ಅತ್ಯಧಿಕ ಧರ್ಮನಿಷ್ಟನೇ ವಾಸ್ತವದಲ್ಲಿ ಅಲ್ಲಾಹನ ಬಳಿ ಅತಿ ಹೆಚ್ಚು ಗೌರವಕ್ಕೆ ಪಾತ್ರನು. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹು ಸರ್ವಜ್ಞನೂ ಸೂಕ್ಷ್ಮಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(49,14,'49:14','ಗ್ರಾಮೀಣ ಅರಬರು `ನಾವು ವಿಶ್ವಾಸವಿರಿಸಿದ್ದೇವೆ’ ಎನ್ನುತ್ತಾರೆ. (ಪ್ರವಾದಿಯರೇ,) ಹೇಳಿರಿ: `ನೀವು ವಿಶ್ವಾಸವಿರಿಸಲಿಲ್ಲ. (ಬಾಹ್ಯವಾಗಿ ಶರಣಾಗಿರು ವುದು ಮಾತ್ರ). ಆದರೆ `ನಾವು ವಿಧೇಯರಾಗಿದ್ದೇವೆ’ ಎಂದು ನೀವು ಹೇಳಿಕೊಳ್ಳಿರಿ. ಸತ್ಯ ವಿಶ್ವಾಸವು ನಿಮ್ಮ ಹೃದಯಗಳಲ್ಲಿ ಪ್ರವೇಶಿಸಿಲ್ಲ. ನೀವು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ದೂತರನ್ನು ಅನುಸರಣೆ ಮಾಡಿದರೆ, ಅವನು ನಿಮಗೆ ನಿಮ್ಮ ಸುಕೃತಗಳ ಪ್ರತಿಫಲದಿಂದ ಒಂದಿಷ್ಟು ಕಡಿತ ಗೊಳಿಸಲಾರನು. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹು ಮಹಾ ಕ್ಷಮಾಶೀಲನೂ ತೀವ್ರ ದಯಾಳುವೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(49,15,'49:15','ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ದೂತರಲ್ಲಿ ವಿಶ್ವಾಸವಿಟ್ಟು, ಆ ಬಳಿಕ ಸ್ವಲ್ಪವೂ ಸಂಶಯಗ್ರಸ್ತರಾಗದವರು ಮತ್ತು ತಮ್ಮ ತನು-ಧನಗಳಿಂದ ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಹೋರಾಡಿದವರು ಯಾರೋ ಅವರೇ ವಾಸ್ತವದಲ್ಲಿ ಸತ್ಯವಿಶ್ವಾಸಿಗಳು. ಅವರೇ ಸತ್ಯಸಂಧರು.');
INSERT INTO "kannada" VALUES(49,16,'49:16','(ಸಂದೇಶವಾಹಕರೇ,) ಕೇಳಿರಿ, ನಿಮ್ಮ ಧರ್ಮ ನಿಷ್ಠೆಯ ಕುರಿತು ನೀವು ಅಲ್ಲಾಹನಿಗೆ ಕಲಿಸುತ್ತಿ ರುವಿರಾ? ಭೂಮ್ಯಾಕಾಶಗಳಲ್ಲಿರುವುದನ್ನೆಲ್ಲ ಅಲ್ಲಾಹನು ಬಲ್ಲನು. ಅಲ್ಲಾಹು ಸಕಲ ವಸ್ತುಗಳ ಜ್ಞಾನವುಳ್ಳವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(49,17,'49:17','ಅವರು ಇಸ್ಲಾಮ್ ಸ್ವೀಕರಿಸಿಕೊಂಡಿರುವುದನ್ನು ತಮಗೆ ಮಾಡಿದ ಒಂದು ಔದಾರ್ಯವನ್ನಾಗಿ ಎತ್ತಿ ತೋರಿಸುತ್ತಾರೆ. ನೀವು ಹೇಳಿರಿ. ‘ನೀವು ಇಸ್ಲಾಮ್ ಸ್ವೀಕರಿಸಿರುವುದನ್ನು ನನ್ನ ಮೇಲಿನ ಒಂದು ಔದಾರ್ಯವನ್ನಾಗಿ ಪ್ರದರ್ಶಿಸಬೇಡಿರಿ. ವಾಸ್ತವದಲ್ಲಿ ನೀವು ಸತ್ಯವಂತರಾಗಿದ್ದರೆ ನಿಮಗೆ ಸತ್ಯವಿಶ್ವಾಸದ ದಾರಿ ತೋರುವ ಮೂಲಕ ಅಲ್ಲಾಹನು ನಿಮ್ಮ ಮೇಲೆ ಔದಾರ್ಯ ತೋರಿದ್ದಾನೆ’.');
INSERT INTO "kannada" VALUES(49,18,'49:18','ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಆಕಾಶಗಳ ಮತ್ತು ಭೂಮಿಯ ಅದೃಶ್ಯವನ್ನು ಬಲ್ಲವನು ಮತ್ತು ನಿಮ್ಮ ಕಾರ್ಯ ಪ್ರವರ್ತಿಗಳನ್ನು ಕಂಡರಿಯುವವನು.');
INSERT INTO "kannada" VALUES(50,1,'50:1','ಕ್ವಾಫ್, ಘನವೆತ್ತ ಕುರ್‍ಆನಿನಾಣೆ');
INSERT INTO "kannada" VALUES(50,2,'50:2','ಆದರೆ ಅವರಿಂದಲೇ ಇರುವ ಓರ್ವ ಮುನ್ನೆಚ್ಚರಿಕೆ ನೀಡುವವನು ಅವರ ಬಳಿಗೆ ಬಂದುದು ಇವರಿಗೆ ಅದ್ಭುತವೆನಿಸಿತು. ತರುವಾಯ ಸತ್ಯನಿಷೇಧಿಗಳು ಹೇಳಿದರು; `ಇದು ಸೋಜಿಗದ ವಿಷಯ’.');
INSERT INTO "kannada" VALUES(50,3,'50:3','ನಾವು ಸತ್ತು ಮಣ್ಣಾಗಿ ಹೋದ ಬಳಿಕ (ಒಂದು ಪುನರ್ಜನ್ಮವೇ?) ಅದು ರೂಢಿಗೆ ವಿದೂರವಾದ ವಾಪಾಸಾತಿಯೇ ಸರಿ.');
INSERT INTO "kannada" VALUES(50,4,'50:4','ಭೂಮಿಯು ಅವರ ಶರೀರದಿಂದ ಕುಂದಿಸಿ ಬಿಡುವುದನ್ನು ಖಂಡಿತ ನಾವು ಬಲ್ಲೆವು. ನಮ್ಮ ಬಳಿಯಲ್ಲೊಂದು ಸುರಕ್ಷಿತ ಗ್ರಂಥವಿದೆ.');
INSERT INTO "kannada" VALUES(50,5,'50:5','ಆದರೆ ಸತ್ಯವು ಅವರ ಬಳಿಗೆ ಬಂದು ತಲುಪಿದಾಗ ಅವರು ಅದನ್ನು ಸ್ಪಷ್ಟವಾಗಿ ನಿಷೇಧಿಸಿಬಿಟ್ಟರು. ಆದ್ದರಿಂದಲೇ ಅವರು ಚಂಚಲದಲ್ಲಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(50,6,'50:6','ಅವರು ತಮ್ಮ ಮೇಲಿರುವ ಆಕಾಶದ ಕಡೆಗೆ ನೋಡಿಲ್ಲವೇ? ನಾವು ಅದನ್ನು ಹೇಗೆ ನಿರ್ಮಿಸಿದೆವು ಮತ್ತು ಅಲಂಕರಿದೆವು. ಅದರಲ್ಲಿ ಎಲ್ಲೂ ಬಿರುಕಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(50,7,'50:7','ಭೂಮಿಯನ್ನು ನಾವು ಹರಡಿದೆವು, ಅದರಲ್ಲಿ ಊರಿನಿಂತ ಪರ್ವತಗಳನ್ನು ನಾಟಿದೆವು ಮತ್ತು ಅದರಲ್ಲಿ ಸೊಗಸಾದ ಸಸ್ಯ ಜೋಡಿಗಳನ್ನು ಬೆಳೆಸಿದೆವು.');
INSERT INTO "kannada" VALUES(50,8,'50:8','(ಅಲ್ಲಾಹನ ಬಳಿಗೆ) ವಿಧೇಯತ್ವವಿರುವ ಪ್ರತಿಯೊಬ್ಬ ದಾಸನು ಕಂಡು ತಿಳಿಯಲು ಮತ್ತು ಅವನಿಗೆ ಪಾಠ ಲಭಿಸಲು (ಹೀಗೆ ಮಾಡಿರುವುದು).');
INSERT INTO "kannada" VALUES(50,9,'50:9','ನಾವು ಆಕಾಶದಿಂದ ಅನುಗ್ರಹೀತ ನೀರನ್ನಿಳಿಸಿದೆವು, ತರುವಾಯ ಅದರಿಂದ ತೋಟಗಳನ್ನೂ ಕೊಯ್ದು ತೆಗೆಯುವ ಧಾನ್ಯಗಳನ್ನೂ ನಾವು ಉತ್ಪಾದಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(50,10,'50:10','ದಟ್ಟ ಗೊಂಚಲುಗಳುಳ್ಳ ಎತ್ತರದ ಖರ್ಜೂರದ ಮರಗಳನ್ನೂ ಸೃಷ್ಟಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(50,11,'50:11','(ಇದು, ನಮ್ಮ) ದಾಸರಿಗೆ ಉಪಜೀವನವಾಗಿ. ಆ ನೀರಿನ ಮೂಲಕ ನಿರ್ಜೀವ ನಾಡನ್ನು ನಾವು ಸಜೀವಗೊಳಿಸುತ್ತೇವೆ. (ಸಮಾಧಿಯಿಂದ) ಹೊರ ಬರುವುದೂ ಹೀಗೆಯೇ.');
INSERT INTO "kannada" VALUES(50,12,'50:12','ಇವರಿಗಿಂತ ಮುಂಚೆ ನೂಹರ ಜನಾಂಗವೂ ರಸ್ಸ್‍ನವರೂ ಸಮೂದ್ ಗೋತ್ರದವರೂ ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸಿದ್ದರು.');
INSERT INTO "kannada" VALUES(50,13,'50:13','');
INSERT INTO "kannada" VALUES(50,14,'50:14','ಆದ್ ಸಮುದಾಯವೂ ಫಿರ್‍ಔನನೂ `ಲೂಥ’ ರ ಸಹೋದರರೂ, ಐಕದವರೂ, ತುಬ್ಬಅï ಜನಾಂಗದವರೂ ಕೂಡಾ. ಅವರೆಲ್ಲರೂ ದೇವ ದೂತರನ್ನು ಸುಳ್ಳಾಗಿದರು. ಕೊನೆಗೆ ನನ್ನ ಎಚ್ಚರಿಕೆಯು ಅವರಿಗೆ ನಿಜವಾಗಿ ಪರಿಣಮಿಸಿತು.');
INSERT INTO "kannada" VALUES(50,15,'50:15','ಪ್ರಥಮ ಬಾರಿಯ ಸೃಷ್ಟಿಯಿಂದ ನಾವೇನು ಬಳಲಿದ್ದೇವೆಯೇ? (ಇಲ್ಲ) ಆದರೂ ಅವರು ಹೊಸ ಸೃಷ್ಟಿಯ ಬಗ್ಗೆ ಸಂಶಯಗ್ರಸ್ಥರಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(50,16,'50:16','ನಿಶ್ಚಯ ನಾವು ಮಾನವನನ್ನು ಸೃಷ್ಟಿಸಿದೆವು. ಅವನ ಮನಸ್ಸನ್ನು ಚಂಚಲಗೊಳಿಸುತ್ತಿರುವ ದುರ್ಭಾವನೆಗಳನ್ನೂ ನಾವು ಬಲ್ಲೆವು. ನಾವು ಅವನಿಗೆ ಅವನ ಕಂಠನಾಡಿಗಿಂತಲೂ ಹೆಚ್ಚು ಸಮೀಪವಿದ್ದೇವೆ .');
INSERT INTO "kannada" VALUES(50,17,'50:17','ಬಲ ಹಾಗೂ ಎಡಭಾಗಗಳಲ್ಲಿ ಕುಳಿತು ಸ್ವೀಕರಿಸುವ (ದಾಖಲಿಸುವ) ಇಬ್ಬರು (ದೇವಚರರು) ಸ್ವೀಕರಿಸುವ ಸಂದರ್ಭ.');
INSERT INTO "kannada" VALUES(50,18,'50:18','');
INSERT INTO "kannada" VALUES(50,19,'50:19','ನಿರೀಕ್ಷಕ, ಉಪಸ್ಥಿತರ (ರಖೀಬ್ - ಅತೀದ್ ಎಂಬ ದೇವಚರರ) ನಿಗಾಕ್ಕೊಳಪಡದೆ ಮನುಷ್ಯ ಒಂದು ಮಾತನ್ನೂ ಉಚ್ಛರಿಸಲಾರ. ಮರಣದ ಕಠಿಣ ಸಂಕಟವು ಸತ್ಯವನ್ನು ತಂದಿದೆ. ನೀನು ಯಾವುದರಿಂದ ತಪ್ಪಿಸಿಕೊಂಡು ಓಡುತ್ತಿದ್ದೀಯೋ ಅದುವೇ ಇದು. (ಎಂದು ಹೇಳಲಾಗುವುದು)');
INSERT INTO "kannada" VALUES(50,20,'50:20','ಸೂರ್ (ಕಹಳೆಯಿಂದ) ಊದಲ್ಪಟ್ಟಿತು. ಅದುವೇ ತಾಕೀತು ನೀಡಲ್ಪಟ್ಟ ದಿನ.');
INSERT INTO "kannada" VALUES(50,21,'50:21','ಪ್ರತಿಯೊಂದು ಶರೀರವು (ಅಂದು - ಮಹ್‍ಶರ ದಲ್ಲಿ) ಬರುವುದು. ಅಟ್ಟಿಕೊಂಡು ಬರುವ ಒಬ್ಬನೂ ಓರ್ವ ಸಾಕ್ಷಿಯೂ ಅವನ ಜೊತೆಗಿರುವರು.');
INSERT INTO "kannada" VALUES(50,22,'50:22','ನೀನು ಇದರ ಬಗ್ಗೆ ಅಲಕ್ಷ್ಯನಾಗಿದ್ದೆ. ಇದೀಗ ನಾವು ನಿನ್ನ ಮುಂದಿದ್ದ ತೆರೆಯನ್ನು ಸರಿಸಿ ಬಿಟ್ಟಿದ್ದೇವೆ. ಆದ್ದರಿಂದ ಇಂದು ನಿನ್ನ ದೃಷ್ಟಿಯು ಬಹಳ ತೀಕ್ಷ್ಣವಿದೆ (ಎಂದು ಸತ್ಯನಿಷೇಧಿಗೆ ಹೇಳಲಾಗುವುದು).');
INSERT INTO "kannada" VALUES(50,23,'50:23','ಇದು ನನ್ನ ಬಳಿ ಸಿದ್ಧಗೊಂಡ ದಾಖಲೆ ಎಂದು ಅವನ ಜೊತೆಗಾರ (ಮಲಕ್) ಹೇಳುವನು.');
INSERT INTO "kannada" VALUES(50,24,'50:24','');
INSERT INTO "kannada" VALUES(50,25,'50:25','ಪ್ರತಿಸ್ಪರ್ಧಿ, ಒಳಿತಿಗೆ ವಿಘ್ನಕಾರಿ, ದುಷ್ಕರ್ಮಿ ಹಾಗೂ ಸಂಶಯಗ್ರಸ್ತನಾದ ಎಲ್ಲ ಸತ್ಯನಿಷೇಧಿ ಯನ್ನೂ ನರಕಕ್ಕೆ ತಳ್ಳಿ ಬಿಡು, (ಎಂದು ನರಕ ಪಾಲಕ ರಾದ ಮಲಕ್ - ಮಾಲಿಕರಿಗೆ ಆಜ್ಞೆ ಬರುವುದು)');
INSERT INTO "kannada" VALUES(50,26,'50:26','ಅವನು ಅಲ್ಲಾಹನೊಂದಿಗೆ ಇತರರನ್ನು ದೇವ ರಾಗಿ ಮಾಡಿಕೊಂಡಿದ್ದನು. ಆದ್ದರಿಂದ ಅವನನ್ನು ಘೋರ ಶಿಕ್ಷೆಗೆ ಹಾಕಿಬಿಡು.');
INSERT INTO "kannada" VALUES(50,27,'50:27','ಅವನ ಜೊತೆಗಾರನು (ಶೈತಾನ) ಹೇಳುವನು; ನಮ್ಮ ಪ್ರಭೂ, ನಾನು ಇವನನ್ನು ದಾರಿತಪ್ಪಿಸಲಿಲ್ಲ. ಅವನೇ ಅತ್ಯಂತ ಅಸಹಜವಾದ ಪಥಭೃಷ್ಟತೆಯಲ್ಲಿದ್ದನು.');
INSERT INTO "kannada" VALUES(50,28,'50:28','ಅವನು (ಅಲ್ಲಾಹು) ಹೇಳಿದನು; ನೀವು ನನ್ನ ಸಮ್ಮುಖದಲ್ಲಿ ತರ್ಕಿಸಬೇಡಿರಿ. ನಾನು ನಿಮಗೆ (ಶಿಕ್ಷೆಯ ಬಗ್ಗೆ) ಮೊದಲೇ ಎಚ್ಚರಿಸಿದ್ದೆ.');
INSERT INTO "kannada" VALUES(50,29,'50:29','ನನ್ನಲ್ಲಿ ಮಾತು ಬದಲಾಯಿಸಲಾಗುವುದಿಲ್ಲ. ಮತ್ತು ನಾನು ನನ್ನ ದಾಸರ ಮೇಲೆ ಅಕ್ರಮವೆ ಸಗುವವನಲ್ಲ.');
INSERT INTO "kannada" VALUES(50,30,'50:30','ಅಂದು ನಾವು ನರಕದೊಡನೆ - ನೀನು ತುಂಬಿ ಕೊಂಡೆಯಾ? ಎಂದು ಕೇಳುವೆವು, ಆಗ ಅದು, ಇನ್ನಷ್ಟು ಇದೆಯೇ? ಎಂದು ಕೇಳುವುದು.');
INSERT INTO "kannada" VALUES(50,31,'50:31','ಧರ್ಮ ಭಕ್ತರಿಗೆ ಸ್ವರ್ಗವನ್ನು ದೂರವಾಗದ ಸ್ಥಿತಿಯಲ್ಲಿ ಹತ್ತಿರಗೊಳಿಸಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(50,32,'50:32','ಈ ಕಾಣುತ್ತಿರುವುದು ನಿಮಗೆ ಸುವಾರ್ತೆ ನೀಡಲ್ಪಡುತ್ತಿದ್ದುದೇ ಆಗಿದೆ. ಬಹಳಷ್ಟು ಪಶ್ಚಾ ತ್ತಾಪಿಸಿ ಮರಳುತ್ತಿದ್ದ ಮತ್ತು (ಅಲ್ಲಾಹನ ವಿಧಿ- ಮೇರೆಗಳನ್ನು) ಪಾಲಿಸುತ್ತಿದ್ದ ಪ್ರತಿಯೊಬ್ಬರಿಗೂ.');
INSERT INTO "kannada" VALUES(50,33,'50:33','(ಅಂದರೆ) ಪರಮ ದಯಾಮಯನಾದ ಅಲ್ಲಾಹ ನನ್ನು ಕಣ್ಣಾರೆ ಕಾಣದೆಯೇ ಭಯಪಡುತ್ತಿದ್ದ ಮತ್ತು ವಿನಯಾನ್ವಿತ ಹೃದಯದೊಂದಿಗೆ ಬಂದಿರುವವನಿಗಾಗಿ.');
INSERT INTO "kannada" VALUES(50,34,'50:34','ಶಾಂತಿಯೊಂದಿಗೆ ನೀವು ಇದಕ್ಕೆ (ಸ್ವರ್ಗಕ್ಕೆ) ಪ್ರವೇಶಿಸಿರಿ. ಇದು ಶಾಶ್ವತ ಜೀವನದ ದಿನವಾಗಿರುವುದು.');
INSERT INTO "kannada" VALUES(50,35,'50:35','ಅಲ್ಲಿ ಅವರಿಗಾಗಿ ಅವರು ಬಯಸಿದ್ದೆಲ್ಲವೂ ಲಭಿಸುವುದು ಮತ್ತು (ಅವರಿಗಾಗಿ) ಅದಕ್ಕಿಂತಲೂ ಬಹಳಷ್ಟು ನಮ್ಮಲ್ಲಿ ಕೊಡಲಿಕ್ಕಿದೆ.');
INSERT INTO "kannada" VALUES(50,36,'50:36','ಇವರಿಗಿಂತ ಮುಂಚೆ ಅನೇಕ ಜನಾಂಗಗಳನ್ನು ನಾವು ನಾಶಗೊಳಿಸಿದ್ದೇವೆ. ಅವರು ಇವರಿಗಿಂತ ತುಂಬಾ ಬಲಶಾಲಿಗಳಾಗಿದ್ದರು. ಮತ್ತು ಅವರು ನಾಡುಗಳಲ್ಲಿ ಜಾಲಾಡಿದ್ದರು. ಆದರೆ (ನಮ್ಮ ಶಿಕ್ಷೆ ಬಂದೆರಗಿದಾಗ) ಅವರು ಯಾವುದಾದರೂ ಅಭಯ ಸ್ಥಾನವನ್ನು ಪಡೆದರೆ?');
INSERT INTO "kannada" VALUES(50,37,'50:37','(ಇಲ್ಲ) ಹೃದಯವುಳ್ಳ ಅಥವಾ ಹೃದಯ ಸಾನಿ ಧ್ಯದಿಂದ ಲಕ್ಷ್ಯಕೊಟ್ಟು ಕೇಳುವ ಪ್ರತಿಯೊಬ್ಬನಿಗೆ ಇದರಲ್ಲಿ ಬೋಧಪ್ರದ ಪಾಠವಿದೆ.');
INSERT INTO "kannada" VALUES(50,38,'50:38','ನಾವು ಆಕಾಶಗಳನ್ನೂ, ಭೂಮಿಯನ್ನೂ ಅವುಗಳ ನಡುವೆ ಇರುವುದನ್ನೂ ಆರು ದಿನಗಳಲ್ಲಿ (ಆರು ಹಂತಗಳಲ್ಲಿ) ಸೃಷ್ಟಿಸಿದೆವು. ಆದರೆ ನಮಗೇನೂ ದಣಿವಾಗಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(50,39,'50:39','ಆದುದರಿಂದ (ಸಂದೇಶವಾಹಕರೇ,) ಅವರಾಡುವ ಮಾತುಗಳ ಬಗ್ಗೆ ತಾಳ್ಮೆವಹಿಸಿರಿ ಮತ್ತು ಸೂರ್ಯೋದಯಕ್ಕೆ ಮುಂಚೆಯೂ ಸೂರ್ಯಾಸ್ತ ಮಾನಕ್ಕಿಂತ ಮುಂಚೆಯೂ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಸ್ತುತಿ ಮಾಡುತ್ತಾ ಅವನ ಪರಿಶುದ್ಧತೆಯನ್ನು ಕೊಂಡಾಡಿರಿ .');
INSERT INTO "kannada" VALUES(50,40,'50:40','ಪುನಃ ರಾತ್ರಿ ವೇಳೆಯೂ ಸಾಷ್ಟಾಂಗವೆರಗಿದ ಬಳಿಕವೂ ಅವನನ್ನು ಜಪಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(50,41,'50:41','(ಓ ಮನುಷ್ಯಾ) ಕರೆಯುವವನು (ಪ್ರತಿಯೊಬ್ಬನ) ಸಮೀಪದಿಂದಲೇ ಕರೆಯುವ ದಿನವನ್ನು ಗಮನವಿಟ್ಟಿರು.');
INSERT INTO "kannada" VALUES(50,42,'50:42','ಅಂದರೆ, ಆ ಘೋರ ಶಬ್ಧವನ್ನು ಅವರು ನಿಜವಾಗಿಯೂ ಕೇಳಿಸಿಕೊಳ್ಳುವ ದಿನ. ಅದು (ಸಮಾಧಿಗಳಿಂದ) ಹೊರಬರುವ ದಿನವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(50,43,'50:43','ನಾವೇ ಜೀವದಾನ ಮಾಡುತ್ತೇವೆ ಮತ್ತು ನಾವೇ ಮರಣ ಕೊಡುತ್ತೇವೆ. ನಮ್ಮ ಕಡೆಗೇ ಮರಳ ಬೇಕಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(50,44,'50:44','ಅಂದರೆ, ಭೂಮಿಯು ಅವರೊಂದಿಗೆ ಬಿರಿದು ಅವರು ಅತಿ ಬೇಗನೆ ಹೊರಡುವ ದಿನ. ಅದು ನಮಗೆ ಅತಿ ಸುಲಭದ ಒಟ್ಟುಗೂಡಿಸುವಿಕೆಯಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(50,45,'50:45','(ಸಂದೇಶವಾಹಕರೇ,) ಅವರು ಹೇಳುವುದರ ಕುರಿತು ನಾವು ಚೆನ್ನಾಗಿ ಬಲ್ಲೆವು. ತಮಗೆ ಅವರ ನ್ನು ಬಲಾತ್ಕರಿಸುವ ಅಧಿಕಾರವಿಲ್ಲ. ಆದುದರಿಂದ ನನ್ನ ಎಚ್ಚರಿಕೆಯ ಭಯವಿರುವವರಿಗೆ ಈ ಖುರ್‍ಆನಿನ ಮೂಲಕ ನೀವು ಉಪದೇಶ ನೀಡಿರಿ.');
INSERT INTO "kannada" VALUES(51,1,'51:1','ಧೂಳೆಬ್ಬಿಸುವ ಗಾಳಿಗಳ ಆಣೆ.');
INSERT INTO "kannada" VALUES(51,2,'51:2','ಭಾರವೇರಿದ ಘನೀಕೃತ ಮೋಡಗಳ ಆಣೆ.');
INSERT INTO "kannada" VALUES(51,3,'51:3','ಅನಾಯಾಸವಾಗಿ ಚಲಿಸುತ್ತಿರುವ ಹಡಗುಗಳ ಆಣೆ.');
INSERT INTO "kannada" VALUES(51,4,'51:4','ಯೋಜನೆಗಳನ್ನು ವಿತರಿಸುವ ದೇವಚರರುಗಳಾಣೆ.');
INSERT INTO "kannada" VALUES(51,5,'51:5','ಖಂಡಿತವಾಗಿಯೂ ನಿಮಗೆ ತಾಕೀತು ನೀಡಲಾದ ವಿಷಯಗಳು ಪರಮಸತ್ಯ.');
INSERT INTO "kannada" VALUES(51,6,'51:6','(ಅಂತ್ಯ ದಿನದ) ವಿಧಿಯ ತೀರ್ಪು ಖಂಡಿತವಾಗಿಯೂ ಸಂಭವನೀಯವಾಗಿದೆ .');
INSERT INTO "kannada" VALUES(51,7,'51:7','ವಿವಿಧ ಪಥಗಳಿರುವ ಗಗನದ ಆಣೆ.');
INSERT INTO "kannada" VALUES(51,8,'51:8','ನಿಜವಾಗಿಯೂ ನೀವು ವಿಭಿನ್ನ ಅಭಿಪ್ರಾಯಗಳಲ್ಲೇ ಇದ್ದೀರಿ .');
INSERT INTO "kannada" VALUES(51,9,'51:9','ವಿಮುಖನಾಗಲ್ಪಟ್ಟವನು ನಬಿಯವರಿಂದ ವಿಮುಖನಾಗಲ್ಪಡುವನು.');
INSERT INTO "kannada" VALUES(51,10,'51:10','ಸುಳ್ಳುಗಾರರು ಶಪಿಸಲ್ಪಟ್ಟಿರುವರು .');
INSERT INTO "kannada" VALUES(51,11,'51:11','ಅವರು ಅಜ್ಞಾನದ ಮಡುವಿನಲ್ಲಿ ಮುಳುಗಿ ನಿರ್ಲಕ್ಷ್ಯರಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(51,12,'51:12','ಪ್ರತಿಫಲದ ದಿನ ಯಾವಾಗ ಬರಲಿದೆ? ಎಂದು ಅವರು ಕುಚೋದ್ಯವಾಡುತ್ತಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(51,13,'51:13','ಅದು, ಅವರು ಅಗ್ನಿಯಲ್ಲಿ ಶಿಕ್ಷಿಸಲ್ಪಡುವಂದು (ಬರಲಿದೆ.)');
INSERT INTO "kannada" VALUES(51,14,'51:14','ಈಗ ನೀವು ನಿಮ್ಮ ವಿನಾಶದ ರುಚಿಯನ್ನು ಸವಿಯಿರಿ. ನೀವು ಯಾವುದಕ್ಕಾಗಿ ಇಹದಲ್ಲಿ ತ್ವರೆ ಮಾಡುತ್ತಿದ್ದಿರೋ ಅದುವೇ ಇದು. (ಎಂದು ಅವರೊಡನೆ ಹೇಳಲಾಗುವುದು.)');
INSERT INTO "kannada" VALUES(51,15,'51:15','ಖಂಡಿತವಾಗಿಯೂ ಧರ್ಮನಿಷ್ಟರು ಅಂದು ಉದ್ಯಾನಗಳಲ್ಲೂ ಚಿಲುಮೆಗಳಲ್ಲೂ ಇರುವರು.');
INSERT INTO "kannada" VALUES(51,16,'51:16','ಅವರ ಪ್ರಭು ಅವರಿಗೆ ದಯಪಾಲಿಸಿದ್ದನ್ನು ಸಂತೋಷದಿಂದ ಸ್ವೀಕರಿಸುತ್ತಿರುವರು. ಅವರು ಅದಕ್ಕೂ ಮುನ್ನ ಸಚ್ಚರಿತರಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(51,17,'51:17','ರಾತ್ರೆಯಲ್ಲಿ ಅವರು ಸ್ವಲ್ಪ ಸಮಯವೇ ನಿದ್ರಿಸುತ್ತಿದ್ದರು');
INSERT INTO "kannada" VALUES(51,18,'51:18','ಅವರು ರಾತ್ರಿಯ ಕೊನೆಯ ಜಾವಗಳಲ್ಲಿ ಕ್ಷಮಾಯಾಚನೆ ಮಾಡುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(51,19,'51:19','ಅವರ ಸೊತ್ತುಗಳಲ್ಲಿ ಬೇಡುವವನಿಗೂ ಬೇಡಲು ತಟಸ್ಥವಿದ್ದವನಿಗೂ ಹಕ್ಕಿರುವುದು .');
INSERT INTO "kannada" VALUES(51,20,'51:20','ದೃಢ ವಿಶ್ವಾಸಿಗಳಿಗೆ ಭೂಮಿಯಲ್ಲಿ ಅಸಂಖ್ಯಾತ ನಿದರ್ಶನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(51,21,'51:21','ಸ್ವತಃ ನಿಮ್ಮಲ್ಲೂ (ನಿದರ್ಶನಗಳು) ಇವೆ. ನೀವು ಕಂಡರಿಯುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(51,22,'51:22','ಆಕಾಶದಲ್ಲಿ ನಿಮ್ಮ ಜೀವನಾಧಾರವೂ ನಿಮಗೆ ವಾಗ್ದಾನ ಮಾಡಲಾಗುತ್ತಿರುವವುಗಳೂ ಇವೆ .');
INSERT INTO "kannada" VALUES(51,23,'51:23','ಆದುದರಿಂದ ಆಕಾಶ ಮತ್ತು ಭೂಮಿಯ ಪ್ರಭುವಿನಾಣೆ, ಇದು ಸತ್ಯವಾಗಿದೆ. ನೀವು ಆ ಕುರಿತು ಮಾತನಾಡಿಕೊಳ್ಳುವಷ್ಟೇ ಅದು ಖಚಿತವಾಗಿದೆ .');
INSERT INTO "kannada" VALUES(51,24,'51:24','(ಸಂದೇಶವಾಹಕರೇ,) ಇಬ್‍ರಾಹೀಮರ ಮಾನ್ಯ ಅತಿಥಿಗಳ ವೃತ್ತಾಂತವು ನಿಮಗೆ ತಲುಪಿದೆಯೇ?');
INSERT INTO "kannada" VALUES(51,25,'51:25','ಅವರು, ಅವರ (ಇಬ್ರಾಹೀಮರ) ಬಳಿಗೆ ಬಂದು ಸಲಾಮ್ ಹೇಳಿದ ಸಂದರ್ಭ, ಅವರು (ಇಬ್‍ರಾ ಹೀಮ್) ಸಲಾಮ್, ನೀವು ಅಪರಿಚಿತರು ಎಂದರು.');
INSERT INTO "kannada" VALUES(51,26,'51:26','ಅನಂತರ ಅವರು ದೃತಿಯಿಂದ ತಮ್ಮ ಮನೆ ಯವರ ಬಳಿಗೆ ಹೋದರು. ಆಮೇಲೆ ಒಂದು ಕೊಬ್ಬಿದ ಕರುವನ್ನು (ಬೇಯಿಸಿ) ತಂದರು.');
INSERT INTO "kannada" VALUES(51,27,'51:27','ತರುವಾಯ ಅದನ್ನು ಅವರ ಹತ್ತಿರ ಇಟ್ಟರು. ನೀವು ತಿನ್ನುವುದಿಲ್ಲವೇ? ಎಂದು ಕೇಳಿದರು.');
INSERT INTO "kannada" VALUES(51,28,'51:28','(ಅವರು ತಿನ್ನಲಿಲ್ಲ) ಆಗ ಇಬ್‍ರಾಹೀಮರು ಅವರ ಬಗೆಗೆ ಅಂಜಿದರು. ಆಗ ಅತಿಥಿಗಳು; `ತಾವು ಹೆದರಬೇಡಿ’ ಎಂದರು ಮತ್ತು ಅವರಿಗೆ ಓರ್ವ ಸುಜ್ಞಾನಿ ಪುತ್ರನು ಜನಿಸುವನೆಂಬ ಸುವಾರ್ತೆ ನೀಡಿದರು .');
INSERT INTO "kannada" VALUES(51,29,'51:29','ಆಗ ಅವರ ಪತ್ನಿಯು ಚೀರುತ್ತ ಮುಂದೆ ಬಂದರು ಮತ್ತು ತನ್ನ ಮುಖಕ್ಕೆ ಬಡಿದುಕೊಳ್ಳುತ್ತ, ಬಂಜೆಯಾದ ಓರ್ವ ವೃದ್ಧೆ! (ಹೆರುವುದು ಹೇಗೆ?) ಎಂದು ಕೇಳಿದರು.');
INSERT INTO "kannada" VALUES(51,30,'51:30','ಆಗ ಅತಿಥಿಗಳು; “ನಿನ್ನ ಪ್ರಭು ಹೀಗೆಯೇ ಹೇಳಿರುತ್ತಾನೆ. ಅವನು ಮಹಾ ಯುಕ್ತಿವಂತನೂ, ಸರ್ವಜ್ಞನೂ ಆಗಿರುತ್ತಾನೆ”ಎಂದರು.');
INSERT INTO "kannada" VALUES(51,31,'51:31','ಇಬ್ರಾಹೀಮರು ಕೇಳಿದರು, ದೇವದೂತರೇ, ನಿಮ್ಮ ಉದ್ದೇಶವೇನು?');
INSERT INTO "kannada" VALUES(51,32,'51:32','ಅವರು ಹೇಳಿದರು, ನಾವು ಒಂದು ಅಪರಾಧಿ ಜನಾಂಗದೆಡೆಗೆ ಕಳುಹಿಸಲ್ಪಟ್ಟಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(51,33,'51:33','ಅವರ ಮೇಲೆ ಸುಡುಮಣ್ಣಿನ ಕಲ್ಲುಗಳ ಮಳೆಗರೆಯಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(51,34,'51:34','ಆ ಕಲ್ಲುಗಳನ್ನು ಅತಿಕ್ರಮಿಗಳಿಗಾಗಿ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಬಳಿ ಗುರುತು ಹಾಕಿಡಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(51,35,'51:35','ಆ ನಾಡಿನಲ್ಲಿದ್ದ ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನೆಲ್ಲ ನಾವು ಅಲ್ಲಿಂದ ಹೊರಕಳುಹಿಸಿ ರಕ್ಷಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(51,36,'51:36','ಅಲ್ಲಿ ಮುಸ್ಲಿಮರ ಒಂದು ಮನೆಯ ಹೊರತು ಬೇರಾವ ಮನೆಯನ್ನೂ ನಾವು ಕಾಣಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(51,37,'51:37','ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆಯನ್ನು ಭಯಪಡುವ ಜನರಿಗಾಗಿ ಒಂದು ನಿದರ್ಶನವನ್ನು ಅಲ್ಲಿ ನಾವು ಉಳಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(51,38,'51:38','ಮೂಸಾರ ವೃತ್ತಾಂತದಲ್ಲಿ (ದೃಷ್ಟಾಂತಗಳಿವೆ). ಸುಸ್ಪಷ್ಟ ಆಧಾರ ಪ್ರಮಾಣದೊಂದಿಗೆ ಫಿರ್‍ಔನನ ಬಳಿಗೆ ನಾವು ಅವರನ್ನು ಕಳುಹಿಸಿದ ಸಂದರ್ಭ.');
INSERT INTO "kannada" VALUES(51,39,'51:39','ಅವನು ತನ್ನ ಸೈನಿಕ ಶಕ್ತಿಯ ಧೈರ್ಯದಿಂದ ಸತ್ಯಕ್ಕೆ ವಿಮುಖನಾದನು. ಮತ್ತು ಇವನು ಮಾಟಗಾರನೋ ಹುಚ್ಚನೋ ಆಗಿದ್ದಾನೆ ಎಂದನು.');
INSERT INTO "kannada" VALUES(51,40,'51:40','ಕೊನೆಗೆ ನಾವು ಅವನನ್ನೂ ಅವನ ಸೇನೆಗಳನ್ನೂ ಹಿಡಿದೆವು. ಅನಂತರ ಅವರನ್ನು ಸಮುದ್ರಕ್ಕೆ ಎಸೆದು ಬಿಟ್ಟೆವು ಮತ್ತು ಅವನು ನಿಂದ್ಯನಾಗಿ ಬಿಟ್ಟನು.');
INSERT INTO "kannada" VALUES(51,41,'51:41','`ಆದ್’ ಜನಾಂಗದಲ್ಲಿ (ದೃಷ್ಟಾಂತವಿದೆ). ನಾವು ಅವರ ಮೇಲೆ ಒಂದು ವಿನಾಶಕರ ಗಾಳಿಯನ್ನು ಕಳುಹಿಸಿದ ಸಂದರ್ಭ.');
INSERT INTO "kannada" VALUES(51,42,'51:42','ಅದು ಹಾದು ಹೋದ ಯಾವುದೇ ಒಂದು ವಸ್ತುವನ್ನೂ ಧೂಳೀಪಟದಂತೆ ಶಿಥಿಲಗೊಳಿಸದೆ ಬಿಟ್ಟಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(51,43,'51:43','`ಸಮೂದ್’ ಜನಾಂಗದಲ್ಲೂ (ನಿದರ್ಶನವಿದೆ). ಒಂದು ನಿಶ್ಚಿತ ಕಾಲದವರೆಗೆ ನೀವು ಸುಖ ಭೋಗಗಳನ್ನು ಅನುಭವಿಸಿಕೊಳ್ಳಿರೆಂದು ಅವರೊಡನೆ ಹೇಳಲ್ಪಟ್ಟ ಸಂದರ್ಭ.');
INSERT INTO "kannada" VALUES(51,44,'51:44','ಆದರೆ, ಅವರು ತಮ್ಮ ಪ್ರಭುವಿನ ಆಜ್ಞೆಯ ವಿರುದ್ಧ ಧಿಕ್ಕಾರ ತೋರಿದರು. ಕೊನೆಗೆ ಅವರು ನೋಡುತ್ತಿದ್ದಂತೆಯೇ ಹಠಾತ್ತನೆ ಘೋರ ಶಬ್ದ ಅವರ ಮೇಲೆರಗಿತು.');
INSERT INTO "kannada" VALUES(51,45,'51:45','ಆಗ ಅವರಿಗೆ ಎದ್ದು ನಿಲ್ಲುವ ಶಕ್ತಿಯಾಗಲಿ ತಮ್ಮನ್ನು ರಕ್ಷಿಸಿಕೊಳ್ಳುವ ಸಾಮಥ್ರ್ಯವಾಗಲಿ ಉಳಿಯಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(51,46,'51:46','ಅದಕ್ಕೂ ಮುಂಚೆ `ನೂಹ’ರ ಜನಾಂಗವನ್ನು (ನಾವು ನಾಶಗೊಳಿಸಿದೆವು.) ಅವರು ಕೂಡಾ ಕರ್ಮಭ್ರಪ್ಟರಾದ ಒಂದು ಜನಾಂಗವಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(51,47,'51:47','ನಾವು ಆಕಾಶವನ್ನು ನಮ್ಮ ಶಕ್ತಿಯಿಂದಲೇ ನಿರ್ಮಿಸಿರುತ್ತೇವೆ ಮತ್ತು ನಾವು ಅದನ್ನು ವಿಶಾಲಗೊಳಿಸುವವರಾಗಿದ್ದೇವೆ .');
INSERT INTO "kannada" VALUES(51,48,'51:48','ಭೂಮಿಯನ್ನು ನಾವು ಹಾಸಿರುತ್ತೇವೆ, ಅದನ್ನು ಜನ ವಾಸಯೋಗ್ಯಗೊಳಿಸಿದವನು ಎಷ್ಟು ಉತ್ತಮನು!');
INSERT INTO "kannada" VALUES(51,49,'51:49','ನಾವು ಪ್ರತಿಯೊಂದು ವಸ್ತುವಿಗೂ ಜೋಡಿಗಳನ್ನು ಸೃಷ್ಟಿಸಿರುತ್ತೇವೆ. ನೀವು ಚಿಂತಿಸುವಂತಾಗಲು.');
INSERT INTO "kannada" VALUES(51,50,'51:50','ಆದುದರಿಂದ (ತಾವು ಹೇಳಿರಿ;) ನೀವು ಅಲ್ಲಾಹನ ಕಡೆಗೆ ಧಾವಿಸಿರಿ. ನಾನು ನಿಮಗೆ ಅವನ ಕಡೆ ಯಿಂದ ಸುವ್ಯಕ್ತ ಎಚ್ಚರಿಕೆ ನೀಡುವವನಾಗಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(51,51,'51:51','ಅಲ್ಲಾಹನೊಂದಿಗೆ ಬೇರೆ ಆರಾಧ್ಯರನ್ನು ಮಾಡಿ ಕೊಳ್ಳಬೇಡಿರಿ. ನಾನು ನಿಮಗೆ ಅವನ ಕಡೆ ಯಿಂದ ಸುವ್ಯಕ್ತ ಎಚ್ಚರಿಕೆ ನೀಡುವವನೇ ಆಗಿ ದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(51,52,'51:52','ಇದೇ ರೀತಿ ಇವರಿಗಿಂತ ಮುಂಚಿನ ಜನಾಂಗ ಗಳ ಬಳಿಗೂ ಯಾವುದೇ ದೂತರು ಬಂದಾಗ ಅವರನ್ನು ಇವರು ಮಾಟಗಾರ ಅಥವಾ ಹುಚ್ಚ ಎಂದು ಹೇಳದೆ ಬಿಡಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(51,53,'51:53','ಅದಕ್ಕೆ (ಹಾಗೆ ಹೇಳುವುದಕ್ಕೆ) ಅವರೇನು ಪರಸ್ಪರ ಒಪ್ಪಂದ ಮಾಡಿಕೊಂಡಿರುವರೇ? ಇಲ್ಲ. ವಾಸ್ತವ ದಲ್ಲಿ ಅವರು ಅತಿಕ್ರಮಿ ಜನತೆಯಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(51,54,'51:54','ಆದುದರಿಂದ (ಸಂದೇಶವಾಹಕರೇ,) ಅವರ ಬಗ್ಗೆ ನಿರ್ಲಕ್ಷ್ಯ ತಾಳಿರಿ. (ಅವರ ವಿಮುಖತೆಗೆ) ತಾವು ಆಕ್ಷೇಪಾರ್ಹರಲ್ಲ.');
INSERT INTO "kannada" VALUES(51,55,'51:55','ತಾವು ಉಪದೇಶ ಮಾಡಿರಿ. ಏಕೆಂದರೆ ನಿಜವಾಗಿಯೂ ಉಪದೇಶವು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಫಲ ನೀಡುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(51,56,'51:56','ನಾನು ಜಿನ್ನ್‍ಗಳನ್ನೂ ಮನುಷ್ಯರನ್ನೂ ನನ್ನ ಆರಾಧನೆಗಾಗಿಯೇ ಹೊರತು ಸೃಷ್ಟಿಸಲಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(51,57,'51:57','ನಾನು ಅವರಿಂದ ಜೀವನಾಧಾರವನ್ನೇನೂ ಬಯಸುವುದಿಲ್ಲ ಮತ್ತು ಅವರು ನನಗೆ ಉಣಿಸಬೇಕೆಂದೂ ಆಗ್ರಹಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(51,58,'51:58','ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಮಹಾ ಅನ್ನದಾತನು , ಮಹಾಶಕ್ತಿಶಾಲಿ ಮತ್ತು ಪ್ರಚಂಡನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(51,59,'51:59','ಆದುದರಿಂದ (ಇಂದು) ಅಕ್ರಮವೆಸಗಿದವರಿಗೆ (ಪೂರ್ವಿಕರಾದ) ತಮ್ಮ ಸಂಗಡಿಗರಿಗೆ ಸಿಕ್ಕಿದಂತಹ ಯಾತನೆಯ ಪಾಲು ಸಿದ್ಧವಿದೆ. ಆದ್ದರಿಂದ ಶಿಕ್ಷೆಗಾಗಿ ಅವರು ನನ್ನೊಡನೆ ಆತುರ ತೋರುವುದು ಬೇಡ.');
INSERT INTO "kannada" VALUES(51,60,'51:60','ಯಾವ ದಿನದ ಬಗ್ಗೆ ಎಚ್ಚರಿಸಲಾಗುತ್ತಿದೆಯೋ ಆ ದಿನ ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಕಠಿಣ ಶಿಕ್ಷೆಯಿದೆ.');
INSERT INTO "kannada" VALUES(52,1,'52:1','');
INSERT INTO "kannada" VALUES(52,2,'52:2','');
INSERT INTO "kannada" VALUES(52,3,'52:3','');
INSERT INTO "kannada" VALUES(52,4,'52:4','');
INSERT INTO "kannada" VALUES(52,5,'52:5','');
INSERT INTO "kannada" VALUES(52,6,'52:6','ತ್ವೂರ್ ಪರ್ವತದ ಆಣೆ.ಹರಡಿದ ಹಾಳೆ ಯಲ್ಲಿ ಬರೆದ ತೆರೆದಿಟ್ಟ ಗ್ರಂಥದ ಆಣೆ.`ಬೈತುಲ್ ಮಅï ಮೂರ್’ನ ಆಣೆ.`ಬೈತುಲ್ ಮಅï ಮೂರ್’ನ ಆಣೆ.ಎತ್ತರಿಸಲ್ಪಟ್ಟ ಛಾವಣಿಯ ಆಣೆ.');
INSERT INTO "kannada" VALUES(52,7,'52:7','ನಿನ್ನ ಪ್ರಭುವಿನ ಕಡೆಯ ಯಾತನೆ ಖಂಡಿತ ಸಂಭವಿಸಲಿದೆ.');
INSERT INTO "kannada" VALUES(52,8,'52:8','ಅದನ್ನು ತಡೆಯುವವರು ಯಾರೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(52,9,'52:9','ಆಕಾಶವು ತೀವ್ರವಾಗಿ ನಡುಗುವ ದಿನ');
INSERT INTO "kannada" VALUES(52,10,'52:10','ಹಾಗೂ ಪರ್ವತಗಳು ವೇಗವಾಗಿ ಹಾರಾಡುವ ದಿನ (ಅದು ಸಂಭವಿಸುವುದು.)');
INSERT INTO "kannada" VALUES(52,11,'52:11','ಅಂದು ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ವಿನಾಶವಿದೆ.');
INSERT INTO "kannada" VALUES(52,12,'52:12','ಅವರು ಯಾರೆಂದರೆ ಅನಾವಶ್ಯ ಕಾರ್ಯಗಳಲ್ಲಿ ಮಗ್ನರಾಗಿ ಆಟವಾಡುತ್ತಿದ್ದವರು.');
INSERT INTO "kannada" VALUES(52,13,'52:13','ಅವರನ್ನು ನರಕಾಗ್ನಿಗೆ ಬಲವಾಗಿ ತಳ್ಳಿ ಹಾಕಲ್ಪಡುವ ದಿನ.');
INSERT INTO "kannada" VALUES(52,14,'52:14','``ನೀವು ನಿಷೇಧಿಸುತ್ತಲಿದ್ದ ನರಕ ಇದುವೇ ಆಗಿದೆ’’ (ಎಂದು ಅವರೊಂದಿಗೆ ಹೇಳಲ್ಪಡುವುದು)');
INSERT INTO "kannada" VALUES(52,15,'52:15','(ಈಗ ಹೇಳಿರಿ)-`ಇದು ಮಾಟವೇ? ಅಥವಾ ನಿಮಗೆ ಕಾಣುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(52,16,'52:16','ಇದರೊಳಗೆ ನೀವು ಪ್ರವೇಶಿಸಿರಿ. ನೀವು ಸಹಿಸಿದರೂ ಸಹಿಸದಿದ್ದರೂ ನಿಮ್ಮ ಮಟ್ಟಿಗೆ ಸಮಾನವಾಗಿದೆ. ನಿಜವಾಗಿಯೂ ನೀವು ಎಸಗಿದ್ದುದರ ಪ್ರತಿಪಲವನ್ನೇ ನಿಮಗೆ ನೀಡಲಾಗುತ್ತಿದೆ. (ಎಂದು ಹೇಳಲಾಗುವುದು.)');
INSERT INTO "kannada" VALUES(52,17,'52:17','ಖಂಡಿತ ಧರ್ಮನಿಷ್ಠರು ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿಯೂ ಸುಖ ಸೌಭಾಗ್ಯಗಳಲ್ಲಿಯೂ ಇರುವರು.');
INSERT INTO "kannada" VALUES(52,18,'52:18','ಅವರ ಪ್ರಭು ಅವರಿಗೆ ದಯಪಾಲಿಸಲಿರುವ ವಸ್ತುಗಳಿಂದ ಸುಖಾನುಭವ ಹೊಂದುವರು. ಮತ್ತು ಅವರ ಪ್ರಭು ಅವರನ್ನು ನರಕ ಶಿಕ್ಷೆಯಿಂದ ರಕ್ಷಿಸಿರುವನು.');
INSERT INTO "kannada" VALUES(52,19,'52:19','ನೀವು ಮಾಡುತ್ತಲಿದ್ದ ಕರ್ಮಗಳ ಪ್ರತಿಫಲವಾಗಿ ಸಂತೋಷದಿಂದ ಭುಜಿಸಿರಿ, ಕುಡಿಯಿರಿ (ಎಂದು ಅವರೊಂದಿಗೆ ಹೇಳಲ್ಪಡುವುದು)');
INSERT INTO "kannada" VALUES(52,20,'52:20','ಅವರು ಸಾಲು ಸಾಲಾಗಿ ಇರಿಸಲಾಗಿರುವ ಪೀಠಗಳಲ್ಲಿ ಒರಗಿ ಕುಳಿತಿರುವರು ಮತ್ತು ವಿಶಾಲಾಕ್ಷಿಗಳಾದ ಸುಂದರಿಯರನ್ನು ಅವರಿಗೆ ನಾವು ವಿವಾಹ ಮಾಡಿಕೊಡುವೆವು.');
INSERT INTO "kannada" VALUES(52,21,'52:21','ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನೂ ಅವರನ್ನು ಸತ್ಯವಿಶ್ವಾಸ ದಲ್ಲಿ ಅನುಸರಿಸಿದ ಅವರ ಸಂತಾನಗಳನ್ನೂ ನಾವು ಅವರೊಂದಿಗೆ ಸ್ವರ್ಗದಲ್ಲಿ ಸೇರಿಸುವೆವು ಮತ್ತು ನಾವು ಅವರ ಕರ್ಮದಲ್ಲೇನೂ ಕಡಿತಗೊಳಿಸಲಾರೆವು. ಪ್ರತಿಯೊಬ್ಬನೂ ತನ್ನ ಸಂಪಾದನೆಗೆ ಅಡವಿಡಲ್ಪಟ್ಟವನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(52,22,'52:22','ಅವರು ಇಚ್ಛಿಸುವ ಹಣ್ಣುಗಳನ್ನೂ, ಮಾಂಸವನ್ನೂ ನಾವು ಅವರಿಗೆ ಯಥೇಚ್ಛವಾಗಿ ಒದಗಿಸುವೆವು.');
INSERT INTO "kannada" VALUES(52,23,'52:23','ಅವರು ಪರಸ್ಪರ ಪಾನ ಪಾತ್ರೆಗಳನ್ನು ಬದಲಾಯಿಸಿಕೊಳ್ಳುವರು. ಯಾವುದೇ ಅಸಂಬದ್ಧ ಮಾತಾಗಲಿ, ದುರ್ನಡೆಯಾಗಲಿ ಅಲ್ಲಿರಲಾರದು .');
INSERT INTO "kannada" VALUES(52,24,'52:24','ಅವರಿಗಾಗಿಯೇ ಮೀಸಲಿರುವ ಬಾಲಕರು (ಅವರ ಸೇವೆಗಾಗಿ) ಅವರನ್ನು ಸುತ್ತುತ್ತಲಿರುವರು. ಅವರು ಚಿಪ್ಪಿಯೊಳಗಣ ಮುತ್ತಿನಂತೆ ಸುಂದರರಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(52,25,'52:25','ಅವರು ಪರಸ್ಪರ ಪ್ರಶ್ನಿಸುತ್ತಾ ಅನ್ಯೋನ್ಯತೆ ಯಿಂದ ಅಭಿಮುಖರಾಗುವರು.');
INSERT INTO "kannada" VALUES(52,26,'52:26','ಅವರು ಹೇಳುವರು; ನಾವು ಇಹದಲ್ಲಿ ನಮ್ಮ ಮನೆಯವರೊಡನಿದ್ದಾಗ (ಅಲ್ಲಾಹನನ್ನು) ಹೆದರುವವರಾಗಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(52,27,'52:27','ಅದರಿಂದಾಗಿ ಅಲ್ಲಾಹನು ನಮ್ಮ ಮೇಲೆ ಅನು ಗ್ರಹ ತೋರಿದನು ಮತ್ತು ರೋಮಕೂಪದಲ್ಲೂ ತೂರಿಕೊಳ್ಳುವ ಸುಡು ಬೆಂಕಿಯ ಶಿಕ್ಷೆಯಿಂದ ನಮ್ಮನ್ನು ರಕ್ಷಿಸಿಬಿಟ್ಟನು.');
INSERT INTO "kannada" VALUES(52,28,'52:28','ನಾವು ಗತಜೀವನದಲ್ಲಿ ಅವನಿಗೆ ಮಾತ್ರ ಆರಾಧಿಸುತ್ತಿದ್ದೆವು. ಅವನು ನಿಜಕ್ಕೂ ಪರಮೋಪಕಾರಿಯೂ, ಪರಮೋದಾರಿಯೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(52,29,'52:29','ಆದುದರಿಂದ (ಸಂದೇಶವಾಹಕರೇ,) ತಾವು ಉಪದೇಶ ಮಾಡುತ್ತಲಿರಿ. ತಮ್ಮ ಪ್ರಭುವಿನ ಅನುಗ್ರಹದಿಂದ, ತಾವು ಜೋತಿಷಿಯೋ ಹುಚ್ಚನೋ ಅಲ್ಲ.');
INSERT INTO "kannada" VALUES(52,30,'52:30','ಅವನು ಕವಿ, ಅವನಿಗೆ ಏನಾದರೂ ಕಾಲ ವಿಪತ್ತು ಬರುವುದನ್ನು ನಾವು ಕಾಯುತ್ತಿದ್ದೇವೆ ಎಂದಾಗಿದೆಯೇ ಅವರು ಹೇಳುತ್ತಿರುವುದು?');
INSERT INTO "kannada" VALUES(52,31,'52:31','ತಾವು ಹೇಳಿರಿ. `ನೀವು ಕಾಯುತ್ತಲೇ ಇರಿ, ನಿಮ್ಮ ಜೊತೆ ನಾನೂ ಕಾಯುವವರಲ್ಲಿ ಒಳ ಪಟ್ಟವನಾಗಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(52,32,'52:32','ಅವರ ಬುದ್ದಿಯು ಅವರಿಗೆ ಇಂತಹುದೇ ಮಾತು ಗಳನ್ನಾಡಲು ಹೇಳುತ್ತಿದೆಯೇ? ಅಥವಾ ಅವರು ಧಿಕ್ಕಾರದಲ್ಲಿ ಮಿತಿಮೀರಿರುವ ಜನರಾಗಿರುವರೇ? ');
INSERT INTO "kannada" VALUES(52,33,'52:33','ಅಥವಾ ಇವನು (ಪ್ರವಾದಿ) ಖುರ್‍ಆನನ್ನು ಸ್ವತಃ ಸೃಷ್ಟಿಸಿಕೊಂಡಿರುವನೆಂದು ಅವರು ಹೇಳು ತ್ತಾರೆಯೇ? ವಾಸ್ತವದಲ್ಲಿ ಅವರು ಸತ್ಯವಿಶ್ವಾಸ ವನ್ನು ಸ್ವೀಕರಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(52,34,'52:34','ಅವರು ಸತ್ಯ ಹೇಳುವವರಾಗಿದ್ದರೆ ಇದೇ ಮಟ್ಟದ ಒಂದು ಸಂದೇಶವನ್ನು ಅವರು ರಚಿಸಿ ತರಲಿ.');
INSERT INTO "kannada" VALUES(52,35,'52:35','ಅಥವಾ ಅವರು ಒಬ್ಬ ಸೃಷ್ಟಿಕರ್ತನಿಲ್ಲದೆ ಸ್ವಯಂ ಸೃಷ್ಟಿಸಲ್ಪಟ್ಟರೇ? ಅಥವಾ ಅವರು ಸೃಷ್ಟಿಕರ್ತರೇ?');
INSERT INTO "kannada" VALUES(52,36,'52:36','ಅಥವಾ ಆಕಾಶಗಳನ್ನು ಮತ್ತು ಭೂಮಿಯನ್ನು ಅವರು ಸೃಷ್ಟಿಸಿದ್ದಾರೆಯೇ? ವಾಸ್ತವದಲ್ಲಿ ಅವರು ಏನನ್ನೂ ದೃಢವಾಗಿ ನಂಬುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(52,37,'52:37','ಅಥವಾ ತಮ್ಮ ಪ್ರಭುವಿನ ಭಂಡಾರಗಳು ಅವರ ವಶದಲ್ಲಿವೆಯೇ? ಅಥವಾ ಅವರಾಗಿದ್ದಾರೆಯೇ ಪರಮಾಧಿಕಾರಿಗಳು?');
INSERT INTO "kannada" VALUES(52,38,'52:38','ಅಥವಾ ಏರಿ ಹೋಗಿ (ಆಕಾಶದ) ರಹಸ್ಯಗಳನ್ನು ಕೇಳಲು ಸಾಧ್ಯವಾಗುವಂತಹ ಏಣಿಯೇನಾ ದರೂ ಅವರಲ್ಲಿ ಇದೆಯೇ? (ಹಾಗಿದ್ದಲ್ಲಿ) ಅವರ ಪೈಕಿ ರಹಸ್ಯವನ್ನು (ಕದ್ದು) ಕೇಳಿದವನು ಯಾವುದಾದರೂ ವ್ಯಕ್ತ ಪುರಾವೆಯನ್ನು ತರಲಿ.');
INSERT INTO "kannada" VALUES(52,39,'52:39','ಅಥವಾ ಅಲ್ಲಾಹನಿಗೆ ಪುತ್ರಿಯರು ಮತ್ತು ನಿಮಗೆ ಪುತ್ರರೇನು?');
INSERT INTO "kannada" VALUES(52,40,'52:40','ಅಥವಾ ಅವರು ಋಣಭಾರದಲ್ಲಿ ಮುಳುಗುವಂತಾಗಲು, ತಾವು ಅವರಿಂದೇನಾದರೂ ಪ್ರತಿಫಲ ಕೇಳುತ್ತೀರಾ?');
INSERT INTO "kannada" VALUES(52,41,'52:41','ಅಥವಾ ಅವರ ಬಳಿ ಅಗೋಚರ ಜ್ಞಾನವಿದ್ದು ಅದನ್ನು ಅವರು ಬರೆಯುತ್ತಿರುವರೇ? ');
INSERT INTO "kannada" VALUES(52,42,'52:42','ಅಥವಾ ಅವರೇನಾದರೂ ಕುತಂತ್ರವನ್ನು ಹೂಡ ಬಯಸುತ್ತಾರೆಯೇ? ಹಾಗಾದರೆ ಸತ್ಯನಿಷೇಧಿಗಳ ಕುತಂತ್ರವು ಅವರ ಮೇಲೆಯೇ ತಿರುಗಿ ಬೀಳಲಿದೆ.');
INSERT INTO "kannada" VALUES(52,43,'52:43','ಅಥವಾ ಅವರಿಗೆ ಅಲ್ಲಾಹನ ಹೊರತು ಬೇರೆ ದೇವನಿದ್ದಾನೆಯೇ? ಅವರು ಮಾಡುತ್ತಿರುವ ಸಹಭಾಗಿತ್ವದಿಂದ ಅಲ್ಲಾಹನು ಪರಿಶುದ್ಧನು.');
INSERT INTO "kannada" VALUES(52,44,'52:44','ಆಕಾಶದಿಂದ ಒಂದು ತುಂಡು ಉದುರಿ ಬೀಳುತ್ತಿರುವುದನ್ನು ಕಂಡರೂ, ಅದು ದಟ್ಟೈಸಿದ ಮೋಡಗಳು ಎಂದು ಅವರು ಹೇಳುವರು.');
INSERT INTO "kannada" VALUES(52,45,'52:45','ಆದುದರಿಂದ (ಸಂದೇಶವಾಹಕರೇ,) ಅವರು ಸ್ಮøತಿ ತಪ್ಪಿ ಬೀಳುವ ದಿನವನ್ನು ತಲಪುವವರೆಗೂ (ಮರಣ ದವರೆಗೂ) ಅವರನ್ನು ಅವರ ಪಾಡಿಗೆ ಬಿಟ್ಟು ಬಿಡಿರಿ.');
INSERT INTO "kannada" VALUES(52,46,'52:46','ಅದು ಅವರ ಯಾವ ಕುತಂತ್ರವೂ ಅವರಿಗೆ ಫಲಕಾರಿಯಾಗದ, ಮತ್ತು ಅವರಿಗೆ ನೆರವು ಸಿಗದ ದಿನ.');
INSERT INTO "kannada" VALUES(52,47,'52:47','ಅಕ್ರಮಿಗಳಿಗೆ ಇದಲ್ಲದೆ (ಬೇರೆ) ಶಿಕ್ಷೆಯೂ ಇದೆ. ಆದರೆ ಅವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ತಿಳಿಯುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(52,48,'52:48','(ಸಂದೇಶವಾಹಕರೇ,) ತಾವು ತಮ್ಮ ಪ್ರಭುವಿನ ತೀರ್ಮಾನ ಬರುವವರೆಗೆ ತಾಳ್ಮೆ ವಹಿಸಿರಿ. ತಾವು ನಮ್ಮ ಕಣ್ಗಾವಲಿನಲ್ಲೇ ಇದ್ದೀರಿ. ತಾವು ಎದ್ದೇಳುವ ಸಂದರ್ಭದಲ್ಲಿ ತಮ್ಮ ಪ್ರಭುವನ್ನು ಪ್ರಶಂಸೆಯೊಂದಿಗೆ ಕೊಂಡಾಡಿರಿ.');
INSERT INTO "kannada" VALUES(52,49,'52:49','ರಾತ್ರೆಯಲ್ಲಿ ಸ್ವಲ್ಪ ಸಮಯವೂ ಮತ್ತು ನಕ್ಷತ್ರಗಳು ಅಸ್ತಮಿಸುವಾಗಲೂ ಅವನ (ಅಲ್ಲಾಹನ) ಪರಿಶು ದ್ಧತೆಯನ್ನು ಪ್ರಕೀರ್ತಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(53,1,'53:1','ನಕ್ಷತ್ರದಾಣೆ - ಅದು ಅಸ್ತಮಿಸಿದಾಗ,');
INSERT INTO "kannada" VALUES(53,2,'53:2','ನಿಮ್ಮ ಸಂಗಡಿಗ (ಪ್ರವಾದಿ) ದಾರಿಗೆಡಲೂ ಇಲ್ಲ. ದುರ್ಮಾರ್ಗಿಯೂ ಅಲ್ಲ.');
INSERT INTO "kannada" VALUES(53,3,'53:3','ಅವರು ತನ್ನಿಚ್ಛೆಯಂತೆ ಮಾತಾಡುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(53,4,'53:4','ಅವರ ನುಡಿ ಅವರಿಗೆ ಅವತೀರ್ಣಗೊಳ್ಳುವ ಸಂದೇಶ ಮಾತ್ರವಾಗಿರುವುದು.');
INSERT INTO "kannada" VALUES(53,5,'53:5','ಅವರಿಗೆ ಅದನ್ನು ಮಹಾಶಕ್ತಿಶಾಲಿಯಾದ ಒಬ್ಬನು (ಜಿಬ್‍ರೀಲ್) ಕಲಿಸಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(53,6,'53:6','(ಅವನು) ಬಹಳ ಸಮರ್ಥನು . ಅವನು ಸರಿಯಾಗಿ ಪ್ರತ್ಯಕ್ಷನಾದನು,');
INSERT INTO "kannada" VALUES(53,7,'53:7','ಅವನು ಸಮುನ್ನತ ಕ್ಷಿತಿಜದಲ್ಲಿರುವ ಸ್ಥಿತಿಯಲ್ಲಿ .');
INSERT INTO "kannada" VALUES(53,8,'53:8','ಅನಂತರ ಅವನು ಹತ್ತಿರವಾದನು ಹಾಗೂ ಮತ್ತೂ ನಿಕಟವಾದನು.');
INSERT INTO "kannada" VALUES(53,9,'53:9','ಎರಡು ಧನುಸ್ಸುಗಳಷ್ಟು ಅಥವಾ ಅದಕ್ಕಿಂತಲೂ ಹೆಚ್ಚು ಹತ್ತಿರವಾದನು .');
INSERT INTO "kannada" VALUES(53,10,'53:10','ಆಗ, ಅಲ್ಲಾಹನು ತನ್ನ ದಾಸನಿಗೆ ತಾನು ಬೋಧಿಸಬೇಕಾದುದನ್ನು ಬೋಧಿಸಿದನು.');
INSERT INTO "kannada" VALUES(53,11,'53:11','ಅವರು ಕಂಡದ್ದನ್ನು ಅವರ ಮನಸ್ಸು ಸುಳ್ಳಾಗಿಸಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(53,12,'53:12','ಹೀಗಿರಲು, ಅವರು ಕಣ್ಣಾರೆ ಕಂಡುದರ ಬಗ್ಗೆ ನೀವು ಅವರೊಡನೆ ತರ್ಕಿಸುತ್ತಿದ್ದೀರಾ?');
INSERT INTO "kannada" VALUES(53,13,'53:13','ಮತ್ತೊಮ್ಮೆ ಅವರು (ಪ್ರವಾದಿ) ಅವನನ್ನು (ಜಿಬ್ರೀಲ್‍ರನ್ನು) ಕಂಡಿದ್ದರು .');
INSERT INTO "kannada" VALUES(53,14,'53:14','`ಸಿದ್ರತುಲ್ ಮುಂತಹಾ’ದ ಬಳಿ .');
INSERT INTO "kannada" VALUES(53,15,'53:15','`ಜನ್ನತುಲ್ ಮಅïವಾ’ ಅದರ ಹತ್ತಿರದಲ್ಲೇ ಇದೆ.');
INSERT INTO "kannada" VALUES(53,16,'53:16','ಸಿದ್‍ರತ್ ವೃಕ್ಷವನ್ನು ಕವಿದುಕೊಂಡಿದ್ದುದು ಕವಿದುಕೊಂಡಾಗ. (ಅವರು ಜಿಬ್‍ರೀಲರನ್ನು ಕಂಡಿದ್ದರು)');
INSERT INTO "kannada" VALUES(53,17,'53:17','(ಆದರೆ ಪ್ರವಾದಿಯ) ದೃಷ್ಟಿ ತಪ್ಪಲೂ ಇಲ್ಲ, ಮೇರೆ ಮೀರಲೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(53,18,'53:18','ಅವರು (ಪ್ರವಾದಿ) ತಮ್ಮ ಪ್ರಭುವಿನ ಅತಿ ಮಹತ್ತ ರವಾದ ಕೆಲವು ನಿದರ್ಶನಗಳನ್ನು ಕಂಡರು.');
INSERT INTO "kannada" VALUES(53,19,'53:19','');
INSERT INTO "kannada" VALUES(53,20,'53:20','(ಹೇಳಿರಿ) ನೀವು `ಲಾತ್’, `ಉಝ್ಝಾ’ ಮತ್ತು ಮೂರನೆಯ `ಮನಾತ್’ನ ಕುರಿತು ವಿವೇ ಚಿಸಿ ನೋಡಿದ್ದೀರಾ? (ಅವುಗಳು ಆರಾಧಿಸಲ್ಪಡಲು ಅರ್ಹವೇ?) ');
INSERT INTO "kannada" VALUES(53,21,'53:21','ಏನು! ನಿಮಗೆ ಗಂಡು ಮತ್ತು ಅಲ್ಲಾಹನಿಗೆ ಹೆಣ್ಣೇ? ');
INSERT INTO "kannada" VALUES(53,22,'53:22','ಇದಂತೂ ಬರೀ ನೀತಿ ತಪ್ಪಿದ ಹಂಚಿಕೆಯಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(53,23,'53:23','ನಿಜವಾಗಿ ಇವೆಲ್ಲ ನೀವು ಮತ್ತು ನಿಮ್ಮ ಪೂರ್ವ ಜರು ನಾಮಕರಣ ಮಾಡಿದ ಕೆಲವು ಹೆಸರುಗಳೇ ಹೊರತು ಇನ್ನೇನೂ ಅಲ್ಲ. ಅಲ್ಲಾಹನು ಇವುಗಳ ಬಗ್ಗೆ ಯಾವ ಆಧಾರವನ್ನೂ ಇಳಿಸಿಲ್ಲ. ವಾಸ್ತವ ದಲ್ಲಿ ಅವರು ಕೇವಲ ಊಹೆಯನ್ನು ಮತ್ತು ದೇಹೇಚ್ಛೆಯನ್ನು ಮಾತ್ರವೇ ಅನುಸರಿಸುತ್ತಿದ್ದಾರೆ. ವಾಸ್ತವದಲ್ಲಿ ಅವರ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಅವರಿಗೆ ಮಾರ್ಗದರ್ಶನ ಬಂದಿದೆ. (ಹೀಗಿದ್ದೂ ಅವರು ಅದನ್ನು ಬಿಟ್ಟು ಬಿಡಲಿಲ್ಲ).');
INSERT INTO "kannada" VALUES(53,24,'53:24','ಮನುಷ್ಯನಿಗೆ ತಾನಿಚ್ಛಿಸಿದ್ದು ದಕ್ಕುವುದೇ?');
INSERT INTO "kannada" VALUES(53,25,'53:25','ಆದರೆ ಪರಲೋಕ ಮತ್ತು ಇಹಲೋಕ ಅಲ್ಲಾಹ ನಿಗಿರುವುದಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(53,26,'53:26','ಆಕಾಶಗಳಲ್ಲಿ ಎಷ್ಟೋ ದೇವಚರರಿದ್ದಾರೆ. ಅಲ್ಲಾಹು ಉದ್ದೇಶಿಸುವ ಮತ್ತು ತೃಪ್ತಿಪಡುವ ವರಿಗೆ (ಶಿಫಾರಸು ಮಾಡಲು) ಅವನು ಅನು ಮತಿ ನೀಡಿದ ಬಳಿಕವಲ್ಲದೆ ಆ ದೇವಚರರ ಶಿಫಾರಸು ಸ್ವಲ್ಪವೂ ಉಪಯುಕ್ತವಾಗಲಾರದು.');
INSERT INTO "kannada" VALUES(53,27,'53:27','ಆದರೆ ಪರಲೋಕವನ್ನು ನಂಬದವರು ದೇವಚರರಿಗೆ ಸ್ತ್ರೀ ನಾಮಗಳನ್ನು ಹಾಕುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(53,28,'53:28','ಅದರ ಕುರಿತಾದ ಯಾವ ಜ್ಞಾನವೂ ಅವರಿಗೆ ಇಲ್ಲ. ಅವರು ಕೇವಲ ಊಹೆಯನ್ನು ಮಾತ್ರವೇ ಅನುಸರಿಸುತ್ತಾರೆ. ಊಹೆಯು ಸತ್ಯದ ಸ್ಥಾನದಲ್ಲಿ ಯಾವ ಪ್ರಯೋಜನಕ್ಕೂ ಬಾರದು.');
INSERT INTO "kannada" VALUES(53,29,'53:29','ಆದುದರಿಂದ ನಮ್ಮ ಸ್ಮರಣೆಯಿಂದ ವಿಮುಖನಾಗುವ ಹಾಗೂ ಲೌಕಿಕ ಜೀವನದ ಹೊರತು ಇನ್ನೇನನ್ನೂ ಬಯಸದವರನ್ನು ತಾವು ಅವಗಣಿ ಸಿಬಿಡಿರಿ.');
INSERT INTO "kannada" VALUES(53,30,'53:30','ಅದು ಅವರ ಅರಿವಿನ ಪರಾಕಾಷ್ಠೆ ಅಷ್ಟೇ . ಅವನ ಮಾರ್ಗದಿಂದ ಯಾರು ತಪ್ಪಿದ್ದಾರೆ ಮತ್ತು ಯಾರು ಸನ್ಮಾರ್ಗದಲ್ಲಿದ್ದಾರೆ ಎಂಬುದನ್ನು ತಮ್ಮ ಪ್ರಭು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(53,31,'53:31','ಆಕಾಶಗಳಲ್ಲಿ ಮತ್ತು ಭೂಮಿಯಲ್ಲಿ ಇರುವು ದೆಲ್ಲವೂ ಅಲ್ಲಾಹನದ್ದಾಗಿದೆ. ದುಷ್ಕರ್ಮಿಗಳಿಗೆ ಅಲ್ಲಾಹನು ಅವರ ಕರ್ಮಗಳ ಪ್ರತಿಫಲ ನೀಡಲಿಕ್ಕಾಗಿ ಮತ್ತು ಸತ್ಕರ್ಮ ನಿರ್ವಹಿಸಿದವರಿಗೆ ಅತಿ ಉತ್ತಮ ಪ್ರತಿಫಲ ನೀಡಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(53,32,'53:32','ಆ ಸತ್ಕರ್ಮಿಗಳು ಯಾರೆಂದರೆ ದೊಡ್ಡ ಪಾಪ ಕೃತ್ಯಗಳನ್ನು ಮತ್ತು ಅನೈತಿಕ ಕೃತ್ಯಗಳನ್ನು ವರ್ಜಿಸುವವರು. ಸಣ್ಣ ತಪ್ಪುಗಳ ಹೊರತು . ನಿಸ್ಸಂದೇಹವಾಗಿಯೂ ತಮ್ಮ ಪ್ರಭುವಿನ ಕ್ಷಮಾ ಶೀಲತೆಯು ಬಹಳ ವಿಶಾಲವಾಗಿದೆ. ಅವನು ನಿಮ್ಮನ್ನು ಭೂಮಿಯಿಂದ ಸೃಷ್ಟಿಸಿದ ಸಂದರ್ಭದಲ್ಲೂ ಹಾಗೂ ನೀವು ನಿಮ್ಮ ಮಾತೆಯರ ಉದರಗಳಲ್ಲಿ ಭ್ರೂಣಾವಸ್ಥೆಯಲ್ಲಿದ್ದಾಗಲೂ ಅವನು ನಿಮ್ಮನ್ನು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು. ಆದುದರಿಂದ ನೀವು ನಿಮ್ಮ ಆತ್ಮ ಪ್ರಶಂಸೆ ಮಾಡಿಕೊಳ್ಳದಿರಿ. ನಿಜವಾದ ಧರ್ಮನಿಷ್ಟನು ಯಾರೆಂಬುದನ್ನು ಅವನೇ ಚೆನ್ನಾಗಿ ಬಲ್ಲವನು.');
INSERT INTO "kannada" VALUES(53,33,'53:33','(ಸಂದೇಶವಾಹಕರೇ,) ದೇವಮಾರ್ಗದಿಂದ ವಿಮುಖನಾದವನನ್ನು ಕಂಡಿರಾ? ');
INSERT INTO "kannada" VALUES(53,34,'53:34','ಸ್ವಲ್ಪ ಮಾತ್ರ ಕೊಟ್ಟು ಬಳಿಕ ತಡೆಹಿಡಿದವನು.');
INSERT INTO "kannada" VALUES(53,35,'53:35','ಅವನ ಬಳಿ ಅದೃಶ್ಯ ಜ್ಞಾನವೇನಾದರೂ ಇದ್ದು ಆ ಮೂಲಕ ಸತ್ಯವನ್ನು ಕಂಡರಿಯುತ್ತಿರುವನೇ?');
INSERT INTO "kannada" VALUES(53,36,'53:36','');
INSERT INTO "kannada" VALUES(53,37,'53:37','ಅಥವಾ ಮೂಸಾರ ಹಾಗೂ ಆದೇಶಗಳನ್ನು ಪರಿಪೂರ್ಣ ಪಾಲಿಸಿದ ಇಬ್ರಾಹೀಮ್‍ರ ಗ್ರಂಥ ಗಳಲ್ಲಿ ತಿಳಿಸಲಾಗಿರುವ ವಿಷಯಗಳ ಬಗ್ಗೆ ಅವನಿಗೆ ತಿಳಿಸಿಕೊಡಲಾಗಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(53,38,'53:38','ಯಾವನೇ (ಪಾಪದ ಹೊರೆ) ಹೊರುವಾತನು ಇನ್ನೊಬ್ಬನ (ಪಾಪದ) ಹೊರೆ ಹೊರಲಾರನು .');
INSERT INTO "kannada" VALUES(53,39,'53:39','ಮನುಷ್ಯನಿಗೆ ತಾನೇ ಪರಿಶ್ರಮಿಸಿದುದರ ಹೊರತು ಬೇರೇನೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(53,40,'53:40','ಅವನ ಪರಿಶ್ರಮವನ್ನು ಸದ್ಯವೇ ತೋರಿಸಿ ಕೊಡಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(53,41,'53:41','ಅನಂತರ ಅವನ ಸಂಪೂರ್ಣ ಪ್ರತಿಫಲವನ್ನು ಕೊಡಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(53,42,'53:42','ಅಂತಿಮ ಗುರಿ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಗೇ ಇದೆ.');
INSERT INTO "kannada" VALUES(53,43,'53:43','ಅವನೇ ನಗಿಸಿದನು ಮತ್ತು ಅಳುವಂತೆ ಮಾಡಿದನು.');
INSERT INTO "kannada" VALUES(53,44,'53:44','ಅವನೇ ಮರಣ ಕೊಟ್ಟನು ಮತ್ತು ಅವನೇ ಜೀವಂತಗೊಳಿಸಿದನು.');
INSERT INTO "kannada" VALUES(53,45,'53:45','ಅವನೇ ಗಂಡು ಮತ್ತು ಹೆಣ್ಣುಗಳ ಜೊತೆಯನ್ನು ಸೃಷ್ಟಿ ಮಾಡಿದನು.');
INSERT INTO "kannada" VALUES(53,46,'53:46','ಒಂದು ಶುಕ್ಲ ಬಿಂದುವಿನಿಂದ, (ಗರ್ಭಾಶಯ ದಲ್ಲಿ) ಅದು ಸುರಿಸಲ್ಪಟ್ಟಾಗ');
INSERT INTO "kannada" VALUES(53,47,'53:47','ಇನ್ನೊಂದು ಜೀವನ (ಪುನರುಜ್ಜೀವನ) ಕೊಡುವುದೂ ಅವನ ವಚನಬದ್ಧತೆಯಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(53,48,'53:48','ಅವನೇ ಶ್ರೀಮಂತಗೊಳಿಸಿದನು ಮತ್ತು (ಧನದ) ನಿಕ್ಷೇಪಗಾರನಾಗಿ ಮಾಡಿದನು.');
INSERT INTO "kannada" VALUES(53,49,'53:49','ಅವನೇ ಶಿಅïರಾ ನಕ್ಷತ್ರದ (ತ್ರಿಶಂಕು ನಕ್ಷತ್ರದ) ಪ್ರಭು .');
INSERT INTO "kannada" VALUES(53,50,'53:50','ಅವನೇ `ಆದ್’ ಎಂಬ ಆದ್ಯ ಜನಾಂಗವನ್ನು ನಾಶಗೊಳಿಸಿದವನು.');
INSERT INTO "kannada" VALUES(53,51,'53:51','ಮತ್ತು ಸಮೂದ್ ಜನಾಂಗವನ್ನು ಅವರಲ್ಲಿ ಯಾರೂ ಉಳಿಯದಂತೆ ಅಳಿಸಿಬಿಟ್ಟನು.');
INSERT INTO "kannada" VALUES(53,52,'53:52','ಅವರಿಗಿಂತ ಮುಂಚೆ ನೂಹರ ಜನಾಂಗವನ್ನೂ ನಾಶಗೊಳಿಸಿದನು. ಏಕೆಂದರೆ, ಅವರು ಮಹಾ ಅಕ್ರಮಿಗಳೂ ಮತ್ತು ಧಿಕ್ಕಾರಿಗಳೂ ಆಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(53,53,'53:53','ಅಧೋಮುಖವಾಗಿ ಮಗುಚಿಬಿದ್ದ ಆ ನಾಡನ್ನು ಅವನು ಧ್ವಂಸಗೊಳಿಸಿದನು.');
INSERT INTO "kannada" VALUES(53,54,'53:54','ತರುವಾಯ ಅದನ್ನು ಆವರಿಸಿದುದೆಲ್ಲವೂ ಆವರಿಸಿ ಬಿಟ್ಟಿತು.');
INSERT INTO "kannada" VALUES(53,55,'53:55','ಆದುದರಿಂದ (ಎಲೈ ಮಾನವಾ,) ನಿನ್ನ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳ ಬಗ್ಗೆ ನೀನು ತರ್ಕಿಸುತ್ತಿರುವೆ?');
INSERT INTO "kannada" VALUES(53,56,'53:56','ಇವರು (ಪ್ರವಾದಿ ಮುಹಮ್ಮದ್) ಈ ಹಿಂದೆ ಬಂದಿದ್ದ ಎಚ್ಚರಿಕೆಗಾರರ ಪೈಕಿ ಒಬ್ಬ ಎಚ್ಚರಿಕೆಗಾರರಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(53,57,'53:57','ಬರಲಿರುವ ಘಳಿಗೆಯು (ಸಂಭವ) ಸನ್ನಿಹಿತವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(53,58,'53:58','ಅಲ್ಲಾಹನ ಹೊರತು ಯಾರೂ ಅದನ್ನು ತಡೆದು ಸರಿಸುವವರಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(53,59,'53:59','ನೀವೀಗ ಈ ವಾರ್ತೆಯ (ಖುರ್‍ಆನಿನ) ಬಗ್ಗೆ ಅಚ್ಚರಿಪಡುತ್ತಿರುವುದೇ?');
INSERT INTO "kannada" VALUES(53,60,'53:60','ನೀವು ನಗುತ್ತೀರಾ? ಅಳುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(53,61,'53:61','ನೀವು ಹೊಣೆಗಾರಿಕೆಯಿಂದ ನಿರ್ಲಕ್ಷ್ಯರಾಗಿರುವಿರಿ.');
INSERT INTO "kannada" VALUES(53,62,'53:62','ಆದುದರಿಂದ ನೀವು ಅಲ್ಲಾಹನ ಮುಂದೆ ಸಾಷ್ಟಾಂಗವೆರಗಿರಿ ಮತ್ತು ಅವನನ್ನು ಆರಾಧಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(54,1,'54:1','ಅಂತ್ಯ ಸಮಯ ಸನ್ನಿಹಿತವಾಯಿತು ಮತ್ತು ಚಂದ್ರನು ಹೋಳಾದನು .');
INSERT INTO "kannada" VALUES(54,2,'54:2','ಆದರೆ ಯಾವ ದೃಷ್ಟಾಂತವನ್ನು ಕಂಡರೂ ಅವರು ವಿಮುಖರಾಗುತ್ತಾರೆ ಮತ್ತು ಇದೊಂದು ಸ್ಥಾಯೀ ಮಾಟಗಾರಿಕೆ ಎನ್ನುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(54,3,'54:3','ಅವರು (ಪ್ರವಾದಿಯವರನ್ನು) ಸುಳ್ಳಾಗಿಸಿದರು ಮತ್ತು ತಮ್ಮ ಸ್ವೇಚ್ಛೆಗಳನ್ನೇ ಅನುಸರಿಸಿದರು. ಎಲ್ಲ ಕಾರ್ಯಗಳು ಸ್ಥಿರ ನಿಶ್ಚಿತವೇ ಆಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(54,4,'54:4','(ದೇವ ನಿಷೇಧದಿಂದ) ತಡೆಯಬಲ್ಲಷ್ಟು ಪಾಠವುಳ್ಳ ವಾರ್ತೆಗಳು ಅವರಿಗೆ ಬಂದು ತಲುಪಿವೆ.');
INSERT INTO "kannada" VALUES(54,5,'54:5','ಸಂಪೂರ್ಣವಾದ ತತ್ವ. (ಅವರ ಮುಂದೆ ಬಂದಿವೆ.) ಆದರೆ ಯಾವ ಎಚ್ಚರಿಕೆಯೂ ಅವರ ಮೇಲೆ ಪರಿಣಾಮ ಬೀರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(54,6,'54:6','ಆದುದರಿಂದ (ಸಂದೇಶವಾಹಕರೇ,) ಅವರಿಂದ ವಿಮುಖರಾಗಿರಿ. ಕೂಗುವಾತನು ಒಂದು ಅತ್ಯಂತ ಅನಿಷ್ಟಕರ ವಿಚಾರದ ಕಡೆಗೆ ಕೂಗುವಂದು,');
INSERT INTO "kannada" VALUES(54,7,'54:7','ಅವರು ತಲ್ಲಣಗೊಂಡ ದೃಷ್ಟಿಯೊಂದಿಗೆ ಕೂಟ ಚದುರಿದ ಕತ್ತರಿಗಿಳಿಗಳಂತೆ ತಮ್ಮ ಸಮಾಧಿಗಳಿಂದ ಹೊರಬರುವರು.');
INSERT INTO "kannada" VALUES(54,8,'54:8','ಕೂಗುವವನ ಕಡೆಗೆ ಧಾವಿಸುತ್ತಿರುವರು. (ಅದೇ) ಸತ್ಯನಿಷೇಧಿಗಳು ಅಂದು, ಇದು ಕಠಿಣವಾದ ದಿನ ಎಂದು ಹೇಳುವರು.');
INSERT INTO "kannada" VALUES(54,9,'54:9','ಇವರಿಗಿಂತ ಮುಂಚೆ ನೂಹರ ಜನಾಂಗವು (ಸತ್ಯವನ್ನು) ಸುಳ್ಳಾಗಿಸಿದೆ. ಅವರು ನಮ್ಮ ದಾಸನನ್ನು (ನೂಹ್‍ರನ್ನು) ಸುಳ್ಳುಗಾರನೆಂದರು. ಇವನು ಹುಚ್ಚನೆಂದರು. ಮತ್ತು ನೂಹರು ತೀವ್ರವಾಗಿ ಅಟ್ಟಲ್ಪಟ್ಟರು.');
INSERT INTO "kannada" VALUES(54,10,'54:10','ಕೊನೆಗೆ ಅವರು ತನ್ನ ಪ್ರಭುವಿನೊಂದಿಗೆ ನಾನು ಸೋತೆ, ಇನ್ನು ನೀನು ರಕ್ಷಾಕ್ರಮ ಕೈಗೊಳ್ಳು ಎಂದು ಪ್ರಾರ್ಥಿಸಿದರು.');
INSERT INTO "kannada" VALUES(54,11,'54:11','ಆಗ ಧಾರಾಕಾರವಾಗಿ ಸುರಿಯುವ ಮಳೆಯೊಂದಿಗೆ ಆಕಾಶದ ದ್ವಾರಗಳನ್ನು ನಾವು ತೆರೆದು ಬಿಟ್ಟೆವು.');
INSERT INTO "kannada" VALUES(54,12,'54:12','ಭೂಮಿಯನ್ನು ಸೀಳಿ ಚಿಲುಮೆಗಳಾಗಿ ಮಾರ್ಪಡಿಸಿ ಬಿಟ್ಟೆವು ಮತ್ತು ವಿಧಿಸಲ್ಪಟ್ಟಿದ್ದ ಒಂದು ಕಾರ್ಯಕ್ಕಾಗಿ ನೀರು ಸಂಧಿಸಿತು.');
INSERT INTO "kannada" VALUES(54,13,'54:13','ಹಲಗೆ ಹಾಗೂ ಮೊಳೆಗಳಿದ್ದ ಒಂದು ವಸ್ತುವಿನ (ಹಡಗಿನ) ಮೇಲೆ ಅವರನ್ನು (ನೂಹ್‍ರನ್ನು) ನಾವು ಸವಾರಿ ಮಾಡಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(54,14,'54:14','ಅದು (ಹಡಗು) ನಮ್ಮ ಮೇಲ್ನೋಟದಲ್ಲಿ ಚಲಿಸುತ್ತಿತ್ತು. ಅದು ನಿಷೇಧಿಸಿ ತಳ್ಳಲಾದ ವ್ಯಕ್ತಿಯ (ನೂಹ್‍ರ) ಪರವಾಗಿ ಪ್ರತೀಕಾರವಾಗಿತ್ತು .');
INSERT INTO "kannada" VALUES(54,15,'54:15','ಆ ಹಡಗನ್ನು ನಾವು ಒಂದು ದೃಷ್ಟಾಂತವಾಗಿ ಉಳಿಸಿದೆವು. ಯಾರಿದ್ದಾರೆ ಇದರಿಂದ (ಚಿಂತಿಸಿ) ಉಪದೇಶ ಪಡೆಯುವವರು ?');
INSERT INTO "kannada" VALUES(54,16,'54:16','ಹೇಗಿತ್ತು ನನ್ನ ಶಿಕ್ಷೆ ಮತ್ತು ಎಚ್ಚರಿಕೆಗಳು!');
INSERT INTO "kannada" VALUES(54,17,'54:17','ಖುರ್‍ಆನನ್ನು ಚಿಂತಿಸಲಿಕ್ಕಾಗಿ ನಾವು ಸರಳಗೊಳಿಸಿದ್ದೇವೆ. ಯಾರಿದ್ದಾರೆ ಯೋಚಿಸುವವರು?');
INSERT INTO "kannada" VALUES(54,18,'54:18','ಆದ್ ಜನಾಂಗದವರು (ಸತ್ಯವನ್ನು) ಧಿಕ್ಕರಿಸಿದರು. ಆಗ ನನ್ನ ಶಿಕ್ಷೆ ಮತ್ತು ನನ್ನ ಎಚ್ಚರಿಕೆಗಳು ಹೇಗಾಯಿತೆಂದು ನೋಡಿರಿ.');
INSERT INTO "kannada" VALUES(54,19,'54:19','ಒಂದು ಸ್ಥಿರವಾದ ಅಶುಭ ದಿನದಂದು ಅವರ ವಿರುದ್ಧ ತೀವ್ರ ಝೇಂಕಾರದ ಬಿರುಗಾಳಿಯನ್ನು ನಾವು ಕಳುಹಿಸಿದೆವು. ');
INSERT INTO "kannada" VALUES(54,20,'54:20','ಆ ಗಾಳಿಯು ಖರ್ಜೂರದ ಮರಗಳನ್ನು ಬುಡ ಕಳಚಿ ಬೀಳಿಸುವಂತೆ ಜನರನ್ನು ಎತ್ತಿ ಅಪ್ಪಳಿಸಿ ಹಾಕಿತು.');
INSERT INTO "kannada" VALUES(54,21,'54:21','ಹೇಗಿತ್ತು ನನ್ನ ಶಿಕ್ಷೆ ಮತ್ತು ನನ್ನ ಎಚ್ಚರಿಕೆಗಳು!');
INSERT INTO "kannada" VALUES(54,22,'54:22','ಚಿಂತಿಸಲಿಕ್ಕಾಗಿ ಖುರ್‍ಆನನ್ನು ನಾವು ಸರಳ ಗೊಳಿಸಿರುವೆವು. ಯಾರಿದ್ದಾರೆ ಚಿಂತಿಸುವರು?');
INSERT INTO "kannada" VALUES(54,23,'54:23','ಸಮೂದ್ ಜನಾಂಗದವರು ಎಚ್ಚರಿಕೆಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದರು.');
INSERT INTO "kannada" VALUES(54,24,'54:24','ನಮ್ಮವನೇ ಆದ ವ್ಯಕ್ತಿಯೊಬ್ಬನನ್ನು ನಾವು ಅನುಸರಿಸುವುದೆ? ಹಾಗಾದರೆ ನಾವು ದಾರಿಗೆಟ್ಟವರೂ ಮರುಳರೂ ಆಗಿಬಿಡುವೆವು.');
INSERT INTO "kannada" VALUES(54,25,'54:25','ನಮ್ಮ ನಡುವೆ ಸಂದೇಶ (`ದಿಕ್ರ್’) ಇಳಿಸಲ್ಪಡಲು ಅವನೊಬ್ಬನೇ ಇರುವುದೇ? (ಅಲ್ಲ) ಅವನು ಮಹಾ ಸುಳ್ಳುಗಾರನೂ, ದುರಹಂಕಾರಿಯೂ ಆಗಿದ್ದಾನೆ ಎಂದು ಅವರು ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(54,26,'54:26','ಮಹಾ ಸುಳ್ಳುಗಾರನೂ ದುರಹಂಕಾರಿಯೂ ಯಾರೆಂದು ನಾಳೆ ಅವರಿಗೆ ತಿಳಿಯಲಿದೆ.');
INSERT INTO "kannada" VALUES(54,27,'54:27','ಒಂದು ಒಂಟೆಯನ್ನು ಅವರಿಗೆ ಪರೀಕ್ಷೆಯಾಗಿ ಕಳುಹಿಸುತ್ತಿದ್ದೇವೆ. ಆದ್ದರಿಂದ ಅವರನ್ನು ನಿರೀಕ್ಷಿಸುತ್ತಿರಿ, ಕ್ಷಮೆಗೊಳ್ಳಿರಿ.');
INSERT INTO "kannada" VALUES(54,28,'54:28','ನೀರನ್ನು ಅವರ ನಡುವೆ ಹಂಚಲ್ಪಟ್ಟದ್ದೆಂದೂ ಪ್ರತಿಯೊಬ್ಬರೂ ತನ್ನ ಜಲಪಾನದ ಪಾಳಿಯಂತೆ ಹಾಜರಾಗುವಂತೆಯೂ ಅವರಿಗೆ ತಿಳಿಸಿ. (ಎಂದು ಸಾಲಿಹ್ ನಬಿಗೆ ನಾವು ಹೇಳಿದೆವು). ');
INSERT INTO "kannada" VALUES(54,29,'54:29','ಕೊನೆಗೆ ಅವರು ತಮ್ಮ ಸ್ನೇಹಿತನನ್ನು ಕೂಗಿ ಕರೆದರು. ಅವನು ಈ ಕಾರ್ಯದ ಹೊಣೆಹೊತ್ತನು ಮತ್ತು ಅದನ್ನು ಕೊಂದು ಹಾಕಿದನು.');
INSERT INTO "kannada" VALUES(54,30,'54:30','ಅನಂತರ ನೋಡಿರಿ, ಹೇಗಿತ್ತು ನನ್ನ ಶಿಕ್ಷೆ ಮತ್ತು ನನ್ನ ಎಚ್ಚರಿಕೆಗಳು?');
INSERT INTO "kannada" VALUES(54,31,'54:31','ನಾವು ಅವರ ಮೇಲೆ ಒಂದೇ ಒಂದು ಘೋರ ವಾದ ಶಬ್ಧವನ್ನು ಕಳುಹಿಸಿದೆವು. ಆಗ ಅವರು ಹಟ್ಟಿ ಕಟ್ಟುವವನ ತ್ಯಾಜ್ಯದಂತಾದರು .');
INSERT INTO "kannada" VALUES(54,32,'54:32','ಚಿಂತಿಸುವವರಿಗೆ ಈ ಖುರ್‍ಆನನ್ನು ನಾವು ಸುಲಭಗೊಳಿಸಿದ್ದೇವೆ. ಯಾರಿದ್ದಾರೆ ಚಿಂತಿಸುವವರು?');
INSERT INTO "kannada" VALUES(54,33,'54:33','ಲೂಥರ ಜನಾಂಗವು ಎಚ್ಚರಿಕೆಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿತು.');
INSERT INTO "kannada" VALUES(54,34,'54:34','ನಾವು ಅವರ ಮೇಲೆ ಕಲ್ಲಿನ ಸುಳಿಗಾಳಿಯನ್ನು ಕಳುಹಿಸಿದೆವು. ಲೂಥರ ಕುಟುಂಬವನ್ನು ಅದರಿಂದ ಹೊರತುಪಡಿಸಲಾಗಿತ್ತು. ರಾತ್ರೆಯ ಅಂತಿಮ ಜಾವದಲ್ಲಿ ನಾವು ಅವರನ್ನು ಸಂರಕ್ಷಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(54,35,'54:35','ನಮ್ಮ ಕಡೆಯಿಂದಿರುವ ಒಂದು ಅನುಗ್ರಹವಾಗಿ. ಕೃತಜ್ಞರಾಗಿರುವವರಿಗೆ ಈ ರೀತಿ ನಾವು ಪ್ರತಿಫಲ ನೀಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(54,36,'54:36','ಲೂಥರು ತಮ್ಮ ಜನಾಂಗದವರಿಗೆ ನಮ್ಮ ಶಿಕ್ಷೆಯ ಕುರಿತು ಎಚ್ಚರಿಕೆ ನೀಡಿದ್ದರು. ಆದರೆ ಅವರು ಎಲ್ಲ ಎಚ್ಚರಿಕೆಗಳ ಬಗೆಗೆ ಕುತರ್ಕ ಹೂಡಿ ಸುಳ್ಳಾಗಿಸಿದರು.');
INSERT INTO "kannada" VALUES(54,37,'54:37','ತಮ್ಮ (ಲೂಥರ) ಅತಿಥಿಗಳನ್ನು (ದುರ್ಬಳಕೆಗೆ) ಬಿಟ್ಟು ಕೊಡುವಂತೆ ಅವರು ಲೂಥರಲ್ಲಿ ಅಪೇಕ್ಷಿಸಿದರು. ಆಗ ಅವರ ಕಣ್ಣುಗಳನ್ನು ಸವರಿ ಸಮತಟ್ಟು ಮಾಡಿಬಿಟ್ಟೆವು. ನನ್ನ ಶಿಕ್ಷೆ ಹಾಗೂ ಮುನ್ನೆಚ್ಚರಿಕೆಗಳ ಫಲವನ್ನು ಅನುಭವಿಸಿರಿ (ಎಂದು ಹೇಳಿದೆವು).');
INSERT INTO "kannada" VALUES(54,38,'54:38','ಒಂದು ಪ್ರಾತಃಕಾಲದಲ್ಲಿ ಸ್ಥಿರ ಶಿಕ್ಷೆಯು ಅವರನ್ನು ಆವರಿಸಿತು.');
INSERT INTO "kannada" VALUES(54,39,'54:39','ನನ್ನ ಶಿಕ್ಷೆಯ ಮತ್ತು ನನ್ನ ಎಚ್ಚರಿಕೆಗಳ ರುಚಿಯನ್ನು ಸವಿಯಿರಿ (ಎಂದು ಅವರಲ್ಲಿ ಹೇಳ ಲಾಯಿತು).');
INSERT INTO "kannada" VALUES(54,40,'54:40','ನಾವು ಈ ಖುರ್‍ಆನನ್ನು ಚಿಂತಿಸಲಿಕ್ಕಾಗಿ ಸರಳಗೊಳಿಸಿರುವೆವು. ಚಿಂತಿಸುವವರು ಯಾರಾದರೂ ಇದ್ದಾರೆಯೇ?');
INSERT INTO "kannada" VALUES(54,41,'54:41','ಫಿರ್‍ಔನ್‍ನ ಪರಿವಾರಕ್ಕೂ ಎಚ್ಚರಿಕೆಗಳು ಬಂದಿದ್ದುವು.');
INSERT INTO "kannada" VALUES(54,42,'54:42','ಅವರು ನಮ್ಮ ಎಲ್ಲ ದೃಷ್ಟಾಂತಗಳನ್ನು ನಿಷೇಧಿಸಿ ತಳ್ಳಿಹಾಕಿದರು. ಕೊನೆಗೆ ಪ್ರಚಂಡ ಶಕ್ತಿಶಾಲಿಯೊಬ್ಬನು ಹಿಡಿಯುವಂತೆ ಅವರನ್ನು ನಾವು ಹಿಡಿದು ಶಿಕ್ಷಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(54,43,'54:43','(ಜನರೇ) ನಿಮ್ಮ ಸತ್ಯನಿಷೇಧಿಗಳು ಅವರಿಗಿಂತ (ಆ ಶಿಕ್ಷೆಗೊಳಗಾದವರಿಗಿಂತ) ಉತ್ತಮರೇ? ಅಥವಾ ದಿವ್ಯಗ್ರಂಥಗಳಲ್ಲಿ ನಿಮಗಾಗಿ ಏನಾದರೂ ಶಿಕ್ಷೆಯಿಂದ ವಿನಾಯಿತಿ ಲಿಖಿತವಾಗಿ ದೆಯೇ?');
INSERT INTO "kannada" VALUES(54,44,'54:44','ಅಥವಾ `ನಾವು ಸಹಾಯ ಪ್ರಾಪ್ತರಾದ ಸಂಘ’ ಎಂದವರು ಹೇಳಿಕೊಳ್ಳುತ್ತಿದ್ದಾರೆಯೆ?');
INSERT INTO "kannada" VALUES(54,45,'54:45','ಆದರೆ ಸದ್ಯದಲ್ಲೇ ಈ ಸಂಘವು ಸೋತು ಹೋಗುವುದು ಮತ್ತು ಅವರು ಬೆನ್ನು ತಿರುಗಿಸಿ ಓಡಿ ಹೋಗುವರು.');
INSERT INTO "kannada" VALUES(54,46,'54:46','ಮಾತ್ರವಲ್ಲ, ಅವರಿಗೆ ಶಿಕ್ಷೆಯ ವಾಗ್ದಾನ ಸಮಯವು ಅಂತಿಮ ದಿನವಾಗಿರುತ್ತದೆ. ಆ ದಿನದ ಶಿಕ್ಷೆಯು ಅತ್ಯಂತ ಘೋರವೂ ಅತ್ಯಂತ ಕಹಿಯೂ ಆಗಿರುವುದು.');
INSERT INTO "kannada" VALUES(54,47,'54:47','ನಿಜವಾಗಿಯೂ ಈ ಅಪರಾಧಿಗಳು ವಿನಾಶ ಹಾಗೂ ಘೋರ ಅಗ್ನಿಯಲ್ಲಿರುವರು.');
INSERT INTO "kannada" VALUES(54,48,'54:48','ಅವರು ಅಧೋಮುಖಿಗಳಾಗಿ ನರಕಾಗ್ನಿಯಲ್ಲಿ ಎಳೆಯಲ್ಪಡುವ ದಿನ, ಅವರೊಡನೆ - ಈಗ ನರಕ ಸ್ಪರ್ಶವನ್ನು ಸವಿಯಿರಿ ಎನ್ನಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(54,49,'54:49','ನಾವು ಪ್ರತಿಯೊಂದು ವಸ್ತುವನ್ನು ಒಂದು ನಿರ್ಣಯ ಪ್ರಕಾರ ಸೃಷ್ಟಿಸಿರುವೆವು .');
INSERT INTO "kannada" VALUES(54,50,'54:50','ನಮ್ಮ ಆಜ್ಞೆಯು ಕೇವಲ ಒಂದೇ ಒಂದು ಆಜ್ಞೆಯಾಗಿರುತ್ತದೆ. ಕಣ್ಣಿನ ರೆಪ್ಪೆ ಬಡಿಯುವಷ್ಟು (ವೇಗದಲ್ಲಿ ಉದ್ದೇಶಿತ ಕಾರ್ಯ ಜರುಗುವುದು).');
INSERT INTO "kannada" VALUES(54,51,'54:51','(ಸತ್ಯನಿಷೇಧಿಗಳೇ!) ನಿಮ್ಮಂತಹ ಅನೇಕ ಪಂಗಡಗಳನ್ನು ನಾವು ನಾಶಗೊಳಿಸಿರುತ್ತೇವೆ. ಹೀಗಿರುತ್ತ, ಚಿಂತಿಸಲು ಸಿದ್ಧರುಳ್ಳ ಯಾರಾದರೂ ಇದ್ದಾರೆಯೇ?');
INSERT INTO "kannada" VALUES(54,52,'54:52','ಅವರು ಮಾಡಿರುವುದೆಲ್ಲವೂ ದಾಖಲೆಗಳಲ್ಲಿ ಲಿಖಿತವಿದೆ.');
INSERT INTO "kannada" VALUES(54,53,'54:53','ಪ್ರತಿಯೊಂದು ಚಿಕ್ಕ ಮತ್ತು ದೊಡ್ಡ ವಿಷಯವನ್ನೂ ಬರೆದಿಡಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(54,54,'54:54','ಖಂಡಿತವಾಗಿಯೂ ಭಕ್ತರು ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ ಮತ್ತು ಕಾಲುವೆಗಳಲ್ಲಿ ಇರುವರು.');
INSERT INTO "kannada" VALUES(54,55,'54:55','ನೈಜತೆಯ ಸೋಪಾನದಲ್ಲಿ ಪರಮ ಶಕ್ತ ಸಾಮ್ರಾಟನ ಸನ್ನಿಧಿಯಲ್ಲಿ.');
INSERT INTO "kannada" VALUES(55,1,'55:1','ಪರಮ ಕಾರುಣಿಕನು.');
INSERT INTO "kannada" VALUES(55,2,'55:2','ಅವನು ಖುರ್‍ಆನನ್ನು ಕಲಿಸಿದನು.');
INSERT INTO "kannada" VALUES(55,3,'55:3','ಅವನು ಮಾನವನನ್ನು ಸೃಷ್ಟಿಸಿದನು.');
INSERT INTO "kannada" VALUES(55,4,'55:4','ಅವನಿಗೆ ಮಾತಾಡಲು ಕಲಿಸಿದನು.');
INSERT INTO "kannada" VALUES(55,5,'55:5','ಸೂರ್ಯ ಮತ್ತು ಚಂದ್ರ ಒಂದು ನಿಶ್ಚಿತ ಗಣನೆ ಪ್ರಕಾರ ಚಲಿಸುತ್ತವೆ.');
INSERT INTO "kannada" VALUES(55,6,'55:6','ಮರ-ಬಳ್ಳಿಗಳು ಅವನಿಗೆ ಸಾಷ್ಟಾಂಗವೆರಗುತ್ತವೆ .');
INSERT INTO "kannada" VALUES(55,7,'55:7','ಆಕಾಶವನ್ನು ಅವನು ಮೇಲೆತ್ತಿದನು ಮತ್ತು ನೀತಿ ಸ್ಥಾಪಿಸಿದನು .');
INSERT INTO "kannada" VALUES(55,8,'55:8','ತೂಕದಲ್ಲಿ ನೀವು ಅನೀತಿ ಕಾಣಿಸದಿರಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(55,9,'55:9','ಅಂದರೆ ನೀವು ನೀತಿಪೂರ್ವಕ ತೂಕ ಮಾಡಿರಿ, ತೂಕದಲ್ಲಿ ಕಡಿತ ಮಾಡಬೇಡಿರಿ ಎಂಬುದಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(55,10,'55:10','ಅವನು ಭೂಮಿಯನ್ನು ಸಕಲ ಸೃಷ್ಟಿಗಳಿಗಾಗಿ ಇಟ್ಟಿರುವನು.');
INSERT INTO "kannada" VALUES(55,11,'55:11','ಅದರಲ್ಲಿ ಹಣ್ಣುಗಳೂ, ಕೋಶಗಳಿರುವ ಖರ್ಜೂರ ಮರಗಳೂ ಇವೆ.');
INSERT INTO "kannada" VALUES(55,12,'55:12','ಹುಲ್ಲುಗಳಿಂದ ಕೂಡಿದ ಧಾನ್ಯಗಳಿವೆ. ಸುಗಂಧ ಸಸ್ಯಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(55,13,'55:13','ಮತ್ತೆ ನೀವೆರಡು ವಿಭಾಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಉಪಕಾರವನ್ನು ಸುಳ್ಳಾಗಿಸುತ್ತಿರುವಿರಿ? ');
INSERT INTO "kannada" VALUES(55,14,'55:14','ಸಪ್ಪಳವೆಬ್ಬಿಸುವ ಸುಟ್ಟ ಆವೆಮಣ್ಣಿನಂತಹ ಶುಷ್ಕ ಮಣ್ಣಿನಿಂದ ಅವನು ಮನುಷ್ಯನನ್ನು ಸೃಷ್ಟಿಸಿದನು.');
INSERT INTO "kannada" VALUES(55,15,'55:15','ಅವನು ಜಿನ್ನ್‍ಗಳನ್ನು ಅಗ್ನಿಯ ಹೊಗೆರಹಿತ ಜ್ವಾಲೆಯಿಂದ ಸೃಷ್ಟಿಸಿದನು.');
INSERT INTO "kannada" VALUES(55,16,'55:16','ಮತ್ತೆ ನೀವೆರಡು ವಿಭಾಗದವರು ನಿಮ್ಮ ಪ್ರಭು ವಿನ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುತ್ತಿರುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,17,'55:17','ಅವನು ಎರಡು ಪೂರ್ವಗಳ ಮತ್ತು ಎರಡು ಪಶ್ಚಿಮಗಳ ಪ್ರಭು .');
INSERT INTO "kannada" VALUES(55,18,'55:18','ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,19,'55:19','ಅವನು ಎರಡು ಸಮುದ್ರಗಳನ್ನು (ಶುದ್ಧ ನೀರು ಮತ್ತು ಉಪ್ಪು ನೀರು) ಪರಸ್ಪರ ಸಂಧಿಸುವಂತೆ ಮಾಡುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(55,20,'55:20','ಆದರೂ ಅವುಗಳ ನಡುವೆ ಒಂದು ಪರದೆ ಅಡ್ಡವಾಗಿದ್ದು ಅವು ಪರಸ್ಪರ ಆಕ್ರಮಿಸಿಕೊಳ್ಳುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(55,21,'55:21','ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,22,'55:22','ಅವೆರಡು ಸಮುದ್ರಗಳಿಂದ ಮುತ್ತು ಮತ್ತು ಹವಳಗಳು ಹೊರಬರುತ್ತವೆ.');
INSERT INTO "kannada" VALUES(55,23,'55:23','ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ನೇಮತ್ತುಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,24,'55:24','ಸಮುದ್ರದಲ್ಲಿ ಸಂಚರಿಸುವ ಪರ್ವತ ಗಾತ್ರದ ಹಡಗುಗಳು ಅವನದೇ ಆಗಿವೆ.');
INSERT INTO "kannada" VALUES(55,25,'55:25','ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,26,'55:26','ಈ ಭೂಮಿಯಲ್ಲಿರುವ ಪ್ರತಿಯೊಂದು ವಸ್ತುವೂ ನಾಶವಾಗಲಿದೆ.');
INSERT INTO "kannada" VALUES(55,27,'55:27','ತಮ್ಮ ಪ್ರಭುವಿನ ಅತ್ಯಮೋಘ, ಪರಮ ಮಾನ್ಯ ಸತ್ತೆಯು ಮಾತ್ರ ಬಾಕಿ ಉಳಿಯಲಿದೆ.');
INSERT INTO "kannada" VALUES(55,28,'55:28','(ಜಿನ್ನ್‍ಗಳೇ ಮತ್ತು ಮಾನವರೇ), ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,29,'55:29','ಆಕಾಶ-ಭೂಮಿಗಳಲ್ಲಿರುವವರೆಲ್ಲರೂ ಅವನಲ್ಲಿ ಬೇಡುತ್ತಾರೆ. ಪ್ರತಿದಿನವೂ ಅಲ್ಲಾಹನು ಕಾರ್ಯ ನಿರ್ವಹಣೆಯಲ್ಲಿರುವನು.');
INSERT INTO "kannada" VALUES(55,30,'55:30','(ಜಿನ್ನ್‍ಗಳೇ ಮತ್ತು ಮಾನವರೇ), ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,31,'55:31','ಓ ಭಾರಪೂರ್ಣವಾದ ಎರಡು ವರ್ಗಗಳೇ! ನಾವು ನಿಮ್ಮ ವಿಚಾರಣೆಗಾಗಿ ಸದ್ಯದಲ್ಲೇ ಸಮಯಾವಕಾಶ ಹೊಂದಲಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(55,32,'55:32','ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,33,'55:33','ಓ ಜಿನ್ನ್ ಮತ್ತು ಮಾನವ ವರ್ಗದವರೇ, ನೀವು ಆಕಾಶ-ಭೂಮಿಗಳ ಸರಹದ್ದುಗಳಿಂದ ಮೀರಿ ಹೋಗಬಲ್ಲಿರಾದರೆ ದಾಟಿ ಹೋಗಿರಿ, ಪ್ರಚಂಡ ಶಕ್ತಿ ಇಲ್ಲದೆ ನೀವು ಹೊರಗೆ ಹೋಗಲಾರಿರಿ .');
INSERT INTO "kannada" VALUES(55,34,'55:34','ಆದುದರಿಂದ ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,35,'55:35','ಎರಡು ವರ್ಗದವರಾದ ನಿಮ್ಮ ನೇರಕ್ಕೆ ಬೆಂಕಿಯ ಜ್ವಾಲೆಯನ್ನೂ ಹೊಗೆಯನ್ನೂ ಕಳುಹಿಸಿ ಬಿಡಲಾಗುವುದು. ಆಗ ಅದನ್ನು ತಡೆದು ನಿಲ್ಲಲು ನಿಮ್ಮಿಂದ ಸಾಧ್ಯವಾಗದು.');
INSERT INTO "kannada" VALUES(55,36,'55:36','ನೀವು (ಎರಡು ವರ್ಗದವರು) ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿ ಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,37,'55:37','ಆಕಾಶವು ಬಿರುಕು ಬಿದ್ದು ಕಾದು ಕೆಂಪೇರಿದ ತೊಗಲಿನಂತಾದಾಗ (ಅವಸ್ಥೆ ಏನಾದೀತು?)');
INSERT INTO "kannada" VALUES(55,38,'55:38','ನೀವೆರಡು ವರ್ಗದವರು (ಆಗ) ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,39,'55:39','ಯಾವನೇ ಮಾನವನೊಂದಿಗೂ ಜಿನ್ನ್‍ನೊಂದಿ ಗೂ ಅವನ ಪಾಪಗಳ ಕುರಿತು ಆ ದಿನ ಪ್ರಶ್ನಿಸಲಾಗುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(55,40,'55:40','ಆಗ, ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿ ಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,41,'55:41','ಅಪರಾಧಿಗಳು ಅಲ್ಲಿ ತಮ್ಮ ಲಕ್ಷಣಗಳಿಂದಲೇ ಗುರುತಿಸಲ್ಪಡುವರು. ಆಗ ಅವರ ಜುಟ್ಟನ್ನೂ ಕಾಲುಗಳನ್ನೂ ಹಿಡಿಯಲ್ಪಡುವುದು.');
INSERT INTO "kannada" VALUES(55,42,'55:42','ಮತ್ತೆ ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,43,'55:43','ಅಪರಾಧಿಗಳು ಸುಳ್ಳಾಗಿಸಿದಂತಹ ನರಕವಿದು. (ಎಂದು ಅವರೊಂದಿಗೆ ಆಗ ಹೇಳಲಾಗುವುದು)');
INSERT INTO "kannada" VALUES(55,44,'55:44','ಆ ನರಕ ಮತ್ತು ಕುದಿಯುತ್ತಿರುವ ನೀರಿನ ನಡುವೆ ಅವರು ಸುತ್ತುತ್ತಿರುವರು.');
INSERT INTO "kannada" VALUES(55,45,'55:45','ಈಗ ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,46,'55:46','ತನ್ನ ಪ್ರಭುವಿನ ಸನ್ನಿಧಿಯನ್ನು ಭಯಪಡುವವನಿಗೆ ಎರಡು ಸ್ವರ್ಗೋದ್ಯಾನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(55,47,'55:47','ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ನೀವು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,48,'55:48','ಗೊನೆ ತುಂಬಿದ ರೆಂಬೆಗಳಿಂದ ದಟ್ಟವಾದ ಸ್ವರ್ಗೋದ್ಯಾನ.');
INSERT INTO "kannada" VALUES(55,49,'55:49','ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ನೀವು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,50,'55:50','ಎರಡು ಉದ್ಯಾನಗಳಲ್ಲೂ ಹರಿಯುತ್ತಿರುವ ಎರಡು ಚಿಲುಮೆಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(55,51,'55:51','ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಕೊಡುಗೆಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,52,'55:52','ಅವೆರಡರಲ್ಲಿ ಎಲ್ಲಾ ತರದ ಫಲವರ್ಗಗಳ ಜೋಡಿಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(55,53,'55:53','ಈಗ ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,54,'55:54','ಅವರು ಹಾಸಿಗೆಗಳಲ್ಲಿ ಒರಗಿಕೊಂಡಿರುವರು. ಆ ಹಾಸಿಗೆಗಳ ಒಳ ಭಾಗವು ದಪ್ಪ ರೇಶ್ಮೆಯಿಂದ ತುಂಬಿಸಲ್ಪಟ್ಟಿರುತ್ತವೆ. ಮತ್ತು ಎರಡು ಉದ್ಯಾನಗಳ ಫಲಗಳು (ಕೈಗೆಟಕುವಂತೆ) ಬಾಗುತ್ತಿರುವುವು.');
INSERT INTO "kannada" VALUES(55,55,'55:55','ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,56,'55:56','ಅದರಲ್ಲಿ ನಯನಗಳನ್ನು ನಿಯಂತ್ರಿಸುವ ತರುಣಿ ಯರಿರುವರು . ಇವರಿಗಿಂತ ಮುಂಚೆ ಅವರ ನ್ನು ಮಾನವನಾಗಲಿ ಜಿನ್ನ್ ಆಗಲಿ ಎಂದೂ ಸ್ಪರ್ಶಿಸಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(55,57,'55:57','ಈಗ ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,58,'55:58','ಅವರು ಮಾಣಿಕ್ಯ ಮತ್ತು ಮುತ್ತುಗಳಂತೆ ಇರುವರು.');
INSERT INTO "kannada" VALUES(55,59,'55:59','ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,60,'55:60','ಒಳಿತಿನ ಪ್ರತಿಫಲ ಒಳಿತಲ್ಲದೆ ಇನ್ನೇನು?');
INSERT INTO "kannada" VALUES(55,61,'55:61','ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,62,'55:62','ಆ ಎರಡು ಉದ್ಯಾನಗಳಲ್ಲದೆ ಬೇರೆರಡು ಸ್ವರ್ಗೋದ್ಯಾನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(55,63,'55:63','ಈಗ ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿ ಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,64,'55:64','ಹಸಿರಿನ ಗಾಢತೆಯಿಂದ ಕಪ್ಪಾಗಿರುವ ಎರಡು ಉದ್ಯಾನಗಳು.');
INSERT INTO "kannada" VALUES(55,65,'55:65','ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,66,'55:66','ಅವೆರಡೂ ಉದ್ಯಾನಗಳಲ್ಲಿ ಚಿಮ್ಮುತ್ತಿರುವ ಎರಡು ನೀರ್ಝರಿಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(55,67,'55:67','ಈಗ ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,68,'55:68','ಅವೆರಡರಲ್ಲೂ ಧಾರಾಳ ಫಲಗಳು, ಖರ್ಜೂರ, ದಾಳಿಂಬೆ ಇರುವುವು.');
INSERT INTO "kannada" VALUES(55,69,'55:69','ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,70,'55:70','ಅವುಗಳಲ್ಲಿ ಸುಗುಣೆಯರಾದ ರೂಪಸಿಗಳಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(55,71,'55:71','ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,72,'55:72','ಮುತ್ತಿನ ಗುಡಾರಗಳಲ್ಲಿ ಕಾದಿರಿಸಲ್ಪಟ್ಟ ಅಭಿ ನೇತ್ರಿಯರು.');
INSERT INTO "kannada" VALUES(55,73,'55:73','ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,74,'55:74','ಇವರಿಗೆ ಮುಂಚೆ ಯಾವನೇ ಮಾನವನಾಗಲಿ, ಜಿನ್ನ್ ಆಗಲಿ ಅವರನ್ನು ಎಂದೂ ಸ್ಪರ್ಶಿಸಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(55,75,'55:75','ಆದುದರಿಂದ ನೀವೆರಡು ವರ್ಗದವರು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನುಗ್ರಹಗಳನ್ನು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,76,'55:76','(ಆ ರೂಪಸಿಯರು) ಹಸಿರು ರತ್ನಗಂಬಳಿಗಳ ಹಾಗೂ ನುಣುಪಾದ ಶ್ರೇಷ್ಠ ಹಾಸಿಗೆಗಳಲ್ಲಿ ಸುಖಾಸೀನರಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(55,77,'55:77','ಈಗ ನಿಮ್ಮ ಪ್ರಭುವಿನ ಯಾವ ಯಾವ ಅನು ಗ್ರಹಗಳನ್ನು ನೀವೆರಡು ವರ್ಗದವರು ಸುಳ್ಳಾಗಿಸುವಿರಿ?');
INSERT INTO "kannada" VALUES(55,78,'55:78','ಪರಮ ಗಣ್ಯನೂ, ಔದಾರ್ಯಪೂರ್ಣನೂ ಆಗಿರುವ ನಿನ್ನ ಪ್ರಭುವಿನ ನಾಮವು ಅತಿ ಅನುಗ್ರಹೀತವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(56,1,'56:1','ಆ ಘಟನೆ (ಅಂತ್ಯ ದಿನ) ಸಂಭವಿಸಿದಾಗ.');
INSERT INTO "kannada" VALUES(56,2,'56:2','ಅದರ ಸಂಭಾವ್ಯತೆಯನ್ನು ಸುಳ್ಳಾಗಿಸುವ ಯಾವನೂ ಇರಲಾರನು.');
INSERT INTO "kannada" VALUES(56,3,'56:3','ಪತನ, ಉಚ್ಛ್ರಾಯದ ಘಟನೆ .');
INSERT INTO "kannada" VALUES(56,4,'56:4','ಅಂದರೆ ಭೂಮಿ ಬಲವಾಗಿ ನಡುಗಿಸಲ್ಪಟ್ಟಾಗ.');
INSERT INTO "kannada" VALUES(56,5,'56:5','ಪರ್ವತಗಳು ನುಚ್ಚುನೂರು ಮಾಡಲ್ಪಟ್ಟಾಗ.');
INSERT INTO "kannada" VALUES(56,6,'56:6','ಹಾಗೆ ಅವು ಚದುರಿ ಹರಡಿದ ಧೂಳೀಪಟ ಗಳಾಗುವುವು.');
INSERT INTO "kannada" VALUES(56,7,'56:7','ಆಗ ನೀವು ಮೂರು ಗುಂಪುಗಳಾಗಿ ವಿಂಗಡಿಸಲ್ಪಡುವಿರಿ.');
INSERT INTO "kannada" VALUES(56,8,'56:8','ಆಗ ಒಂದು ವಿಭಾಗ ಬಲ ವಿಭಾಗದವರು! ಬಲ ವಿಭಾಗದವರದ್ದು ಏನವಸ್ಥೆ !');
INSERT INTO "kannada" VALUES(56,9,'56:9','ಎಡ ವಿಭಾಗದವರು! ಎಡ ವಿಭಾಗದವರ ಪರಿಸ್ಥಿತಿಯೇನು?');
INSERT INTO "kannada" VALUES(56,10,'56:10','(ಸತ್ಯವಿಶ್ವಾಸ ಹಾಗೂ ಸತ್ಕರ್ಮದಲ್ಲಿ) ಮುನ್ನಡೆ ಸಾಧಿಸಿದವರು (ಪರಲೋಕದಲ್ಲೂ) ಮುನ್ನಡೆದವರು.');
INSERT INTO "kannada" VALUES(56,11,'56:11','ಅವರು ಅಲ್ಲಾಹನ ಸಾಮೀಪ್ಯ ಪಡೆದವರಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(56,12,'56:12','ಸಂಪತ್ಸಮೃದ್ಧ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ (ಅವರು ವಾಸಿಸುವರು.)');
INSERT INTO "kannada" VALUES(56,13,'56:13','ಪೂರ್ವಿಕರಲ್ಲಿ ಒಂದು ಕೂಟ.');
INSERT INTO "kannada" VALUES(56,14,'56:14','ಮುಂದಿನವರಲ್ಲಿ ಸ್ವಲ್ಪ ಜನರು.');
INSERT INTO "kannada" VALUES(56,15,'56:15','ರತ್ನಗಳ ದಪ್ಪ ನಾರುಗಳಿಂದ ಹೊಸೆಯಲಾದ ಮಂಚಗಳಲ್ಲಿರುವರು.');
INSERT INTO "kannada" VALUES(56,16,'56:16','ಅವರದರಲ್ಲಿ ಎದುರುಬದುರಾಗಿ ಸುಖಾಸೀನರಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(56,17,'56:17','ಚಿರ ಬಾಲಕರು (ಸೇವೆಗಾಗಿ) ಅವರ ನಡುವೆ ಸುತ್ತುತ್ತಿರುವರು.');
INSERT INTO "kannada" VALUES(56,18,'56:18','ಉದಕದಿಂದ ಸುರೆ ತುಂಬಿಸಿಕೊಂಡ ಪಾತ್ರೆಗಳನ್ನು, ಹೂಜಿಗಳನ್ನು ಮತ್ತು ಪಾನ ಲೋಟಗಳನ್ನು ಹಿಡಿದುಕೊಂಡು (ಓಡಾಡುತ್ತಿರುವರು.)');
INSERT INTO "kannada" VALUES(56,19,'56:19','ಅದನ್ನು ಕುಡಿದಾಗ ಅವರ ತಲೆ ತಿರುಗಲಾರದು ಮತ್ತು ಅವರಿಗೆ ಅಮಲೇರಲಾರದು.');
INSERT INTO "kannada" VALUES(56,20,'56:20','ಅವರು ಇಷ್ಟಪಟ್ಟು ಆಯ್ಕೆ ಮಾಡಿಕೊಳ್ಳುವಂತಹ ತರತರದ ಹಣ್ಣುಹಂಪಲುಗಳನ್ನು (ಆ ಬಾಲ ಕರು) ತಂದಿರಿಸುವರು.');
INSERT INTO "kannada" VALUES(56,21,'56:21','ಅವರು ಆಶೆಪಡುವ ಪಕ್ಷಿಗಳ ಮಾಂಸದೊಂದಿಗೆ (ಆ ಬಾಲಕರು ಓಡಾಡುವರು)');
INSERT INTO "kannada" VALUES(56,22,'56:22','ಅವರಿಗಾಗಿ ಸುರಸುಂದರ ಬೊಗಸೆ ಕಂಗಳ ಅಪ್ಸರೆಯರಿರುವರು.');
INSERT INTO "kannada" VALUES(56,23,'56:23','ಅವರು ಚಿಪ್ಪಿಯೊಳಗೆ ಬಚ್ಚಿಡಲಾದ ಮುತ್ತುಗಳಂತಿರುವರು.');
INSERT INTO "kannada" VALUES(56,24,'56:24','ಇವೆಲ್ಲ ಅವರು ಭೂಲೋಕದಲ್ಲಿ ಮಾಡುತ್ತಿದ್ದ ಸತ್ಕರ್ಮಗಳ ಪ್ರತಿಫಲಗಳು.');
INSERT INTO "kannada" VALUES(56,25,'56:25','ಅಲ್ಲಿ ಅವರು ಯಾವುದೇ ಅಸಭ್ಯ ಅಥವಾ ಪಾಪಕರ ಮಾತನ್ನು ಕೇಳಲಾರರು.');
INSERT INTO "kannada" VALUES(56,26,'56:26','ಸಲಾಂ-ಸಲಾಂ ಎಂಬ ಮಾತಿನ ಹೊರತು.');
INSERT INTO "kannada" VALUES(56,27,'56:27','ಇನ್ನು ಬಲಗಡೆಯವರು, ಬಲಗಡೆಯವರದ್ದು ಏನವಸ್ಥೆ!');
INSERT INTO "kannada" VALUES(56,28,'56:28','ಅವರು ಮುಳ್ಳಿಲ್ಲದ ಬೊಗರಿ ವೃಕ್ಷಗಳು,');
INSERT INTO "kannada" VALUES(56,29,'56:29','ಅಡಿಯಿಂದ ಮುಡಿ ತನಕ ಹಣ್ಣುಗಳು ದಟ್ಟೈಸಿದ ಬಾಳೆ ಗಿಡಗಳು, ');
INSERT INTO "kannada" VALUES(56,30,'56:30','ಬಹು ದೂರ ಹಬ್ಬಿರುವ ನೆರಳು,');
INSERT INTO "kannada" VALUES(56,31,'56:31','ಸದಾ ಹರಿಯುತ್ತಲೇ ಇರುವ ಜಲ,');
INSERT INTO "kannada" VALUES(56,32,'56:32','');
INSERT INTO "kannada" VALUES(56,33,'56:33','ಮತ್ತು ಎಂದೂ ಮುಗಿಯದ ಹಾಗೂ ಅಡೆತಡೆಯಿಲ್ಲದೆ ಧಾರಾಳವಾಗಿ ಸಿಗುತ್ತಲಿರುವ ಹಣ್ಣು ಹಂಪಲುಗಳು');
INSERT INTO "kannada" VALUES(56,34,'56:34','ಹಾಗೂ ಎತ್ತರದ ಮೆತ್ತೆಗಳಲ್ಲಿ ಸುಖಾನು ಭವದಲ್ಲಿರುವರು.');
INSERT INTO "kannada" VALUES(56,35,'56:35','ನಾವು ಆ ತರುಣಿಯರನ್ನು ವಿಶಿಷ್ಟ ರೀತಿಯಲ್ಲಿ ಉತ್ಪಾದಿಸಿರುವೆವು.');
INSERT INTO "kannada" VALUES(56,36,'56:36','ನಾವು ಅವರನ್ನು ಚಿರ ಕನ್ನಿಕೆಯರನ್ನಾಗಿ ಮಾಡಿರುವೆವು.');
INSERT INTO "kannada" VALUES(56,37,'56:37','ಅವರು ಪ್ರಿಯತಮೆಯರು ಮತ್ತು ಸಮ ಪ್ರಾಯದವರು.');
INSERT INTO "kannada" VALUES(56,38,'56:38','ಇವೆಲ್ಲ ಬಲಗಡೆಯವರಿಗಾಗಿ ಇರುವುದಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(56,39,'56:39','ಅವರು ಪೂರ್ವಿಕರ ಪೈಕಿ ಒಂದು ವಿಭಾಗವೂ');
INSERT INTO "kannada" VALUES(56,40,'56:40','ಮುಂದಿನವರ ಪೈಕಿ ಒಂದು ವಿಭಾಗವೂ ಆಗಿರುವರು.');
INSERT INTO "kannada" VALUES(56,41,'56:41','ಇನ್ನು ಎಡಗಡೆಯವರು. ಎಡಗಡೆಯವರ ಅವಸ್ಥೆಯೇನು!');
INSERT INTO "kannada" VALUES(56,42,'56:42','');
INSERT INTO "kannada" VALUES(56,43,'56:43','');
INSERT INTO "kannada" VALUES(56,44,'56:44','ಅವರು ಸುಡುಗಾಳಿಯ ಉರಿಯಲ್ಲೂ ಕುದಿಯುತ್ತಿರುವ ನೀರಲ್ಲೂ ತಂಪಾಗಲಿ, ರಮ್ಯತೆ ಯಾಗಲಿ ಇಲ್ಲದ ಕರಿಹೊಗೆಯ ನೆರಳಲ್ಲೂ ನೆಲೆಸುವರು.');
INSERT INTO "kannada" VALUES(56,45,'56:45','ಅವರು ಅದಕ್ಕೆ ಮುನ್ನ (ಇಹಲೋಕದಲ್ಲಿ) ಸುಖಲೋಲುಪರಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(56,46,'56:46','ಮತ್ತು ಗಂಭೀರ ಪಾಪದಲ್ಲಿ ನೆಲೆಗೊಂಡಿದ್ದರು.');
INSERT INTO "kannada" VALUES(56,47,'56:47','');
INSERT INTO "kannada" VALUES(56,48,'56:48','“ನಾವು ಸತ್ತು ಮಣ್ಣಾಗಿ ಮೂಳೆಗಳಾದ ನಂತರ ಮರು ಜೀವಗೊಳಿಸಲ್ಪಡುವುದುಂಟೆ? ನಮ್ಮ ಪೂರ್ವಿಕರೂ ಮರಳಿ ಜೀವ ತಾಳಲಿಕ್ಕಿದೆಯೇ?” ಎಂದು ಅವರು ಕುಚೋದ್ಯವಾಡುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(56,49,'56:49','');
INSERT INTO "kannada" VALUES(56,50,'56:50','(ಸಂದೇಶವಾಹಕರೇ,) ಹೇಳಿರಿ - ನಿಶ್ಚ ಯವಾಗಿಯೂ ಹಿಂದಿನವರೂ ಮುಂದಿನವರೂ ಎಲ್ಲರೂ ಒಂದು ನಿಶ್ಚಿತ ದಿನದ ಕ್ಲಪ್ತ ಸಮಯಕ್ಕೆ ಖಂಡಿತ ಒಟ್ಟುಗೂಡಿಸಲ್ಪಡುವರು.');
INSERT INTO "kannada" VALUES(56,51,'56:51','ಓ ಸುಳ್ಳುಗಾರರಾದ ಪಥಭ್ರಷ್ಟರೇ, ನಂತರ ಖಂಡಿತಾ ನೀವು');
INSERT INTO "kannada" VALUES(56,52,'56:52','`ಝಖ್ಖೂಮ್’ ಎಂಬ ವೃಕ್ಷದ ಆಹಾರವನ್ನು ತಿನ್ನಲಿರುವಿರಿ.');
INSERT INTO "kannada" VALUES(56,53,'56:53','ಅದರಿಂದಲೇ ನೀವು ಹೊಟ್ಟೆ ತುಂಬುವಿರಿ.');
INSERT INTO "kannada" VALUES(56,54,'56:54','ಮತ್ತು ಅದರ ಮೇಲೆ ಕುದಿಯುವ ನೀರನ್ನು ಕುಡಿಯುವಿರಿ.');
INSERT INTO "kannada" VALUES(56,55,'56:55','ಬಾಯಾರಿದ ಒಂಟೆ ಕುಡಿಯುವಂತೆ ನೀವು ಧಾವಂತದಿಂದ ಕುಡಿಯುವಿರಿ.');
INSERT INTO "kannada" VALUES(56,56,'56:56','ಇದುವೇ ಪ್ರತಿಫಲದ ದಿನ ಅವರಿಗೆ ಕಾದಿರಿಸಲಾದ ಆತಿಥ್ಯ.');
INSERT INTO "kannada" VALUES(56,57,'56:57','ನಿಮ್ಮನ್ನು ಸೃಷ್ಟಿಸಿದ್ದು ನಾವು. ಆದರೂ ನೀವು (ಮರುಸೃಷ್ಟಿಯ ನನ್ನ ಸಾಮಥ್ರ್ಯವನ್ನು) ಅಂಗೀಕರಿಸಿಲ್ಲವೇಕೆ?');
INSERT INTO "kannada" VALUES(56,58,'56:58','(ಸ್ತ್ರೀಯರ ಗರ್ಭಾಶಯಕ್ಕೆ) ನೀವು ಸುರಿಸುವ ವೀರ್ಯದ ಕುರಿತು ವಿವೇಚಿಸಿರುವಿರಾ?');
INSERT INTO "kannada" VALUES(56,59,'56:59','ಅದನ್ನು ಸೃಷ್ಟಿ ಮಾಡುವವರು ನೀವೋ ಅಥವಾ ಅದನ್ನು ಸೃಷ್ಟಿ ಮಾಡಿದವರು ನಾವೋ?');
INSERT INTO "kannada" VALUES(56,60,'56:60','ನಿಮ್ಮೊಳಗೆ ಮರಣವನ್ನು ನಿಶ್ಚಯಿಸಿದ್ದು ನಾವೇ ಆಗಿದ್ದೇವೆ. ನಿಮಗೆ ಬದಲು ನಿಮ್ಮಂತಹ ವರನ್ನು ತರುವಲ್ಲಿ ಮತ್ತು ನಿಮಗೆ ತಿಳಿದಿರದ ಯಾವುದಾದರೂ ರೂಪದಲ್ಲಿ ನಿಮ್ಮನ್ನು ಪುನಃ ಸೃಷ್ಟಿಸುವಲ್ಲಿ ನಾವೆಂದೂ ಪರಾಜಿತರಲ್ಲ.');
INSERT INTO "kannada" VALUES(56,61,'56:61','');
INSERT INTO "kannada" VALUES(56,62,'56:62','ಮೊದಲ ಉತ್ಪತ್ತಿಯ ಕುರಿತಂತೆ ನೀವು ತಿಳಿದಿರುವಿರಿ. ಮತ್ತೇಕೆ ನೀವು ಚಿಂತಿಸಿ ಪಾಠ ಕಲಿಯುವುದಿಲ್ಲ?');
INSERT INTO "kannada" VALUES(56,63,'56:63','ನೀವು ಬಿತ್ತನೆ ಮಾಡುವ ಕುರಿತು ನೀವೆಂದಾದರೂ ವಿವೇಚಿಸಿ ನೋಡಿರುವಿರಾ?');
INSERT INTO "kannada" VALUES(56,64,'56:64','ಅದನ್ನು ಬೆಳೆಸುವವರು ನೀವೋ ಅಥವಾ ಅದನ್ನು ಬೆಳೆಸುವವರು ನಾವೋ?');
INSERT INTO "kannada" VALUES(56,65,'56:65','');
INSERT INTO "kannada" VALUES(56,66,'56:66','');
INSERT INTO "kannada" VALUES(56,67,'56:67','ನಾವಿಚ್ಛಿಸಿದರೆ ಅದನ್ನು (ಕೃಷಿಗಳನ್ನು) ಹೊಟ್ಟನ್ನಾಗಿ ಮಾಡಿಬಿಡಬಲ್ಲೆವು. ಆಗ ನೀವು ಸಖೇದಾಶ್ಚರ್ಯದೊಂದಿಗೆ; ``ನಾವು ನಷ್ಟ ಹೊಂದಿ ದೆವು, ಮಾತ್ರವಲ್ಲ, ನಮಗೆ ಜೀವನಾಧಾರವೇ ತಡೆಯಲ್ಪಟ್ಟಿತು’’ ಎನ್ನುವಿರಿ.');
INSERT INTO "kannada" VALUES(56,68,'56:68','ನೀವು ಕುಡಿಯುವ ನೀರಿನ ಬಗ್ಗೆ ಯೋಚಿಸಿದ್ದೀರಾ?');
INSERT INTO "kannada" VALUES(56,69,'56:69','ಅದನ್ನು ಮೋಡಗಳಿಂದ ಸುರಿಸಿದ್ದು ನೀವೋ ಅಥವಾ ಅದನ್ನು ಸುರಿಸಿದ್ದು ನಾವೋ?');
INSERT INTO "kannada" VALUES(56,70,'56:70','ನಾವಿಚ್ಛಿಸಿದರೆ ಅದನ್ನು ಕಡು ಉಪ್ಪನ್ನಾಗಿ ಮಾಡಿ ಬಿಡಬಲ್ಲೆವು. ಹೀಗಿರುತ್ತ ನೀವೇಕೆ ಕೃತಜ್ಞರಾಗುವುದಿಲ್ಲ ?');
INSERT INTO "kannada" VALUES(56,71,'56:71','ನೀವು ಉರಿಸುತ್ತಿರುವ ಬೆಂಕಿಯ ಕುರಿತು ನೀವು ಚಿಂತನೆ ನಡೆಸಿದ್ದುಂಟೇ?');
INSERT INTO "kannada" VALUES(56,72,'56:72','ಅದರ ಮರವನ್ನು ಉಂಟುಮಾಡಿದ್ದು ನೀವೋ ಅಥವಾ ಅದನ್ನುಂಟುಮಾಡಿದ್ದು ನಾವೋ?');
INSERT INTO "kannada" VALUES(56,73,'56:73','ಅದನ್ನು ನಾವು ಒಂದು ಚಿಂತನಾ ವಿಷಯವಾಗಿಯೂ ಮರುಭೂಮಿಯ ಸಂಚಾರಿಗಳಿಗೆ ಒಂದು ಅನುಕೂಲವಾಗಿಯೂ ಮಾಡಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(56,74,'56:74','ಆದುದರಿಂದ (ಸಂದೇಶವಾಹಕರೇ), ಅತ್ಯಂತ ಮಹಾನನಾದ ತಮ್ಮ ಪ್ರಭುವಿನ ನಾಮವನ್ನು ಪ್ರಕೀರ್ತಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(56,75,'56:75','ಖಂಡಿತ ನಾನು ನಕ್ಷತ್ರಗಳ ಅಸ್ತಮಯ ಸ್ಥಾನಗಳ ಆಣೆಯಿಡುತ್ತೇನೆ .');
INSERT INTO "kannada" VALUES(56,76,'56:76','ನೀವು ಅರಿಯುವುದಾದರೆ, ಅದೊಂದು ಉನ್ನತ ಮಟ್ಟದ ಆಣೆಯಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(56,77,'56:77','ಇದು ಆದರಣೀಯವಾದ ಖುರ್‍ಆನ್ ಆಗಿದೆ.');
INSERT INTO "kannada" VALUES(56,78,'56:78','ಭದ್ರವಾಗಿ ಕಾಯ್ದಿಡಲಾದ ಒಂದು ಗ್ರಂಥದಲ್ಲಿ ಮುದ್ರಿತವಿದೆ.');
INSERT INTO "kannada" VALUES(56,79,'56:79','ಪರಿಶುದ್ಧರಲ್ಲದೆ ಬೇರಾರೂ ಇದನ್ನು ಸ್ಪರ್ಶಿಸಲಾರರು .');
INSERT INTO "kannada" VALUES(56,80,'56:80','ಇದು ಲೋಕಪಾಲಕನಿಂದ ಅವತೀರ್ಣಗೊಂಡಿದೆ.');
INSERT INTO "kannada" VALUES(56,81,'56:81','ಹೀಗಿರುತ್ತ ನೀವು ಈ ವೃತ್ತಾಂತದ ಕುರಿತು ನಿರ್ಲಕ್ಷ್ಯ ತೋರುವಿರಾ?');
INSERT INTO "kannada" VALUES(56,82,'56:82','ಮತ್ತು ಸತ್ಯನಿಷೇಧವನ್ನೇ ನಿಮ್ಮ ಉಪಜೀವನ ಮಾರ್ಗವನ್ನಾಗಿ ಮಾಡಿಕೊಂಡಿರುವಿರಾ?');
INSERT INTO "kannada" VALUES(56,83,'56:83','ಹಾಗಾದರೆ, ಅದು (ಆತ್ಮ) ಗಂಟಲಿಗೆ ತಲುಪಿದಾಗ (ಅದನ್ನು ಯಾಕೆ ನಿಮಗೆ ತಡೆಯಲಾಗುವುದಿಲ್ಲ?)');
INSERT INTO "kannada" VALUES(56,84,'56:84','ನೀವಾದರೋ ಆ ಸಂದರ್ಭದಲ್ಲಿ (ಮರಣವನ್ನು) ಕಣ್ಣಾರೆ ನೋಡಿ ನಿಂತಿರುತ್ತೀರಿ.');
INSERT INTO "kannada" VALUES(56,85,'56:85','ನಾವು ಅವನೊಂದಿಗೆ (ಆ ಮರಣಾಸನ್ನನೊಂದಿಗೆ) ನಿಮಗಿಂತ ನಿಕಟವಾಗಿರುವೆವು. ಆದರೆ ಅದನ್ನು ನೀವು ಕಾಣುತ್ತಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(56,86,'56:86','');
INSERT INTO "kannada" VALUES(56,87,'56:87','ನೀವು ಪರಾಧೀನರಲ್ಲದಿರುತ್ತಿದ್ದರೆ ಮತ್ತು ನೀವು ಸತ್ಯವಾದಿಗಳಾಗಿರುತ್ತಿದ್ದರೆ (ಪ್ರಾಣವನ್ನು ರಕ್ಷಿಸಿಕೊಳ್ಳಲು ಯಾಕಾಗುತ್ತಿಲ್ಲ?)');
INSERT INTO "kannada" VALUES(56,88,'56:88','ಇನ್ನು ಆ ಮರಣ ಹೊಂದುವವನು ಅಲ್ಲಾಹನ ನಿಕಟವರ್ತಿಗಳಲ್ಲಿ ಒಬ್ಬನಾಗಿದ್ದರೆ');
INSERT INTO "kannada" VALUES(56,89,'56:89','ಅವನಿಗೆ ಸುಖ ಸಂತೋಷ, ಸುಗಂಧಪೂರಿತ ಜೀವನಾಧಾರ ಮತ್ತು ಅನುಗ್ರಹ ತುಂಬಿದ ಸ್ವರ್ಗೋದ್ಯಾನವಿದೆ.');
INSERT INTO "kannada" VALUES(56,90,'56:90','ಇನ್ನು ಅವನು ಬಲಗಡೆಯವನಾಗಿದ್ದರೆ');
INSERT INTO "kannada" VALUES(56,91,'56:91','ನೀನು ಬಲಗಡೆಯವನಾದುದರಿಂದ ನಿನಗೆ ಶಾಂತಿ ಇರಲಿ ಎಂದು ಅವನಲ್ಲಿ ಹೇಳಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(56,92,'56:92','ಇನ್ನು ಅವನು ಸುಳ್ಳಾಗಿಸುವ ಪಥಭ್ರಷ್ಟ ರಲ್ಲೊಬ್ಬನಾಗಿದ್ದರೆ');
INSERT INTO "kannada" VALUES(56,93,'56:93','ಅವನಿಗೆ ಕುದಿಯುವ ನೀರಿನ ಸತ್ಕಾರವೂ,');
INSERT INTO "kannada" VALUES(56,94,'56:94','ಜ್ವಲಿಸುವ ನರಕ ಪ್ರವೇಶದ ಶಿಕ್ಷೆಯೂ (ಲಭಿಸಲಿದೆ)');
INSERT INTO "kannada" VALUES(56,95,'56:95','ಇದುವೇ ಖಚಿತವಾದ ಸತ್ಯ.');
INSERT INTO "kannada" VALUES(56,96,'56:96','ಆದುದರಿಂದ (ಸಂದೇಶವಾಹಕರೇ), ತಮ್ಮ ಮಹೋನ್ನತ ಪ್ರಭುವಿನ ನಾಮವನ್ನು ಪ್ರಕೀರ್ತಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(57,1,'57:1','ಆಕಾಶ-ಭೂಮಿಗಳಲ್ಲಿರುವ ಪ್ರತಿಯೊಂದು ವಸ್ತುವೂ ಅಲ್ಲಾಹನ ಪರಿಶುದ್ಧತೆಯನ್ನು ಕೊಂಡಾ ಡಿವೆ. ಅವನು ಪರಮ ಪ್ರತಾಪಿಯೂ, ಪರಮ ಯುಕ್ತಿವಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(57,2,'57:2','ಭೂಮಿ-ಆಕಾಶಗಳ ಆಧಿಪತ್ಯವು ಆತನಿಗೇ ಇದೆ. ಜೀವಂತಗೊಳಿಸುವವನೂ ಮರಣಗೊಳಿಸುವ ವನೂ ಅವನೇ. ಅವನು ಎಲ್ಲದರ ಮೇಲೆ ಸಾಮಥ್ರ್ಯವುಳ್ಳವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(57,3,'57:3','ಆದ್ಯನೂ ಅಂತ್ಯನೂ ಪ್ರತ್ಯಕ್ಷನೂ ಪರೋಕ್ಷನೂ ಅವನೇ . ಅವನು ಸಕಲ ವಿಷಯಗಳ ಕುರಿತು ಜ್ಞಾನವುಳ್ಳವನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(57,4,'57:4','ಅವನೇ ಭೂಮಿ-ಆಕಾಶಗಳನ್ನು ಆರು ದಿನಗಳಲ್ಲಿ ಸೃಷ್ಟಿಸಿದವನು. ಅನಂತರ ಅವನು ಅರ್ಶ್‍ನ ಮೇಲೆ ಇಸ್ತಿವಾ ಹೊಂದಿದನು. ಭೂಮಿಯೊಳಗೆ ಪ್ರವೇಶಿಸುವ ಹಾಗೂ ಅದರಿಂದ ಹೊರಬರುವ ಮತ್ತು ಆಕಾಶದಿಂದ ಇಳಿಯುವ ಮತ್ತು ಅದಕ್ಕೆ ಏರಿಹೋಗುವ ಎಲ್ಲವನ್ನೂ ಅವನು ತಿಳಿಯುವನು. ನೀವೆಲ್ಲಿದ್ದರೂ ಅವನು ನಿಮ್ಮ ಜೊತೆಗಿದ್ದಾನೆ . ನೀವು ಮಾಡುವುದೆಲ್ಲವನ್ನೂ ಅಲ್ಲಾಹು ನೋಡುವವನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(57,5,'57:5','ಆಕಾಶ-ಭೂಮಿಗಳ ಅಧಿಪತ್ಯ ಅವನಿಗೆ ಇರುವುದಾಗಿದೆ ಮತ್ತು ಕಾರ್ಯಗಳು ಅವನ ಕಡೆಗೆ ಮರಳಿಸಲ್ಪಡುತ್ತವೆ.');
INSERT INTO "kannada" VALUES(57,6,'57:6','ಅವನು ರಾತ್ರೆಯನ್ನು ಹಗಲಿನೊಳಗೂ ಹಗಲನ್ನು ರಾತ್ರೆಯೊಳಗೂ ಪ್ರವೇಶಗೊಳಿಸುತ್ತಾನೆ. ಹೃದಯಗಳ ರಹಸ್ಯಗಳನ್ನೂ ಅವನು ಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(57,7,'57:7','(ಮನುಷ್ಯರೇ) ನೀವು ಅಲ್ಲಾಹ್ ಮತ್ತು ಅವನ ದೂತರ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸಿರಿ. ಅವನು ಯಾವ ಸೊತ್ತುಗಳ ಮೇಲೆ ನಿಮ್ಮನ್ನು ವಾರೀಸುದಾರ ರಾಗಿ ಮಾಡಿರುವನೋ ಅವುಗಳಿಂದ ನೀವು ಖರ್ಚು ಮಾಡಿರಿ. ನಿಮ್ಮ ಪೈಕಿ ಸತ್ಯವಿಶ್ವಾಸವಿರಿ ಸುವ ಮತ್ತು ಸಂಪತ್ತನ್ನು ಖರ್ಚು ಮಾಡುವ ಜನರಿಗೆ ಮಹಾ ಪ್ರತಿಫಲವಿದೆ.');
INSERT INTO "kannada" VALUES(57,8,'57:8','ಈ ದೂತರು ನಿಮಗೆ ನಿಮ್ಮ ಒಡೆಯನ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸಲು ಕರೆ ನೀಡುತ್ತಿದ್ದರೂ ನೀವು ಅಲ್ಲಾಹನ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸದಿರಲು ನಿಮಗೇ ನು ನ್ಯಾಯವಿದೆ? ನೀವು ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಬಯಸುವಿರಾದರೆ ಅವನು ಖಂಡಿತ ನಿಮ್ಮಿಂದ ಕರಾರು ಪಡೆದಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(57,9,'57:9','ನಿಮ್ಮನ್ನು ಅಂಧಕಾರಗಳಿಂದ ಪ್ರಕಾಶದೆಡೆಗೆ ತರಲಿಕ್ಕಾಗಿ ತನ್ನ ದಾಸನ ಮೇಲೆ ಸುಸ್ಪಷ್ಟ ದೃಷ್ಟಾಂ ತಗಳನ್ನು ಅವತೀರ್ಣಗೊಳಿಸುತ್ತಿರುವವನು ಅವನೇ. ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹನು ನಿಮ್ಮೊಂ ದಿಗೆ ಬಹಳ ಕೃಪೆ ತೋರುವವನೂ ಕರುಣಾಮಯಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(57,10,'57:10','ಆಕಾಶಗಳ ಹಾಗೂ ಭೂಮಿಯ ಉತ್ತರಾಧಿಕಾರವು ಅಲ್ಲಾಹನದ್ದಾಗಿರುವಾಗ ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಖರ್ಚು ಮಾಡದಿರಲು ನಿಮಗೇನು ನ್ಯಾಯವಿದೆ? ನಿಮ್ಮ ಪೈಕಿ (ಮಕ್ಕಾ) ವಿಜಯಪ್ರಾಪ್ತಿಯ ಮೊದಲಿನಿಂದ ಖರ್ಚು ಮಾಡಿದ ಮತ್ತು ಹೋರಾಟ ನಡೆಸಿದ ಜನರು (ಹಾಗೆ ಮಾಡದವರಿಗೆ) ಸಮಾನರಲ್ಲ. ಅವರು ಅನಂತರ ಖರ್ಚು ಮಾಡಿದ ಹಾಗೂ ಹೋರಾಟ ನಡೆಸಿದವರಿಗಿಂತ ಮಹತ್ತಾದ ಪದವಿ ಉಳ್ಳವರಾಗಿದ್ದಾರೆ. ಅದಾಗ್ಯೂ ಅಲ್ಲಾಹನು ಉಭಯತ್ರರಿಗೂ ಅತ್ಯಂತ ಉತ್ತಮ ಪ್ರತಿಫಲದ ವಾಗ್ದಾನ ಮಾಡಿರುವನು. ನೀವು ಮಾಡುವುದೆಲ್ಲವನ್ನೂ ಅಲ್ಲಾಹನು ಚೆನ್ನಾಗಿ ಬಲ್ಲವನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(57,11,'57:11','ಅಲ್ಲಾಹನಿಗೆ ಶ್ರೇಷ್ಠ ಸಾಲ ಕೊಡುವವನಾರಿದ್ದಾನೆ? ಅಲ್ಲಾಹು ಅವನಿಗೆ ಅದನ್ನು ಇಮ್ಮಡಿಗೊಳಿಸಿ ಹೆಚ್ಚಿಸಿ ಕೊಡುವನು ಮತ್ತು ಅವನಿಗೆ ಅತ್ಯುತ್ತಮ ಪ್ರತಿಫಲವಿದೆ .');
INSERT INTO "kannada" VALUES(57,12,'57:12','ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನೂ ಸತ್ಯವಿಶ್ವಾಸಿನಿಯರನ್ನೂ ಅವರ ಪ್ರಕಾಶವು ಅವರ ಮುಂದೆಯೂ ಅವರ ಬಲ ಭಾಗದಲ್ಲೂ ಸಾಗುತ್ತಿರುವುದನ್ನು ತಾವು ದರ್ಶಿಸುವ ದಿನ! (ಅಂದು ಅವರಲ್ಲಿ ಹೇಳಲಾಗುವುದು;) ಇಂದು ನಿಮಗೆ ಶುಭವಾರ್ತೆ ಎಂದರೆ ತಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆಗಳು ಹರಿಯುತ್ತಿರುವ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಿದ್ದು ಅವುಗಳಲ್ಲಿ ನೀವು ಸ್ಥಿರವಾಸಿಗಳಾಗಿರುವಿರಿ. ಇದುವೇ ಮಹಾ ಯಶಸ್ಸು.');
INSERT INTO "kannada" VALUES(57,13,'57:13','ಕಪಟವಿಶ್ವಾಸಿ ಪುರುಷರು ಮತ್ತು ಕಪಟ ವಿಶ್ವಾಸಿನಿ ಸ್ತ್ರೀಯರು ಸತ್ಯವಿಶ್ವಾಸಿಗಳೊಡನೆ ಈ ರೀತಿ ಹೇಳುವ ದಿನ; ನಮ್ಮನ್ನು ನೋಡಿರಿ. ನಾವು ನಿಮ್ಮ ಪ್ರಕಾಶದಿಂದ ಒಂದಿಷ್ಟು ಹಿಡಿದು ತೆಗೆಯಲೆ?’ (ಆಗ) ಅವರೊಂದಿಗೆ ಹೇಳಲ್ಪಡುವುದು; ನೀವು ನಿಮ್ಮ ಹಿಂಭಾಗಕ್ಕೆ ಮರಳಿ ಪ್ರಕಾ ಶವನ್ನು ಹುಡುಕಿ ಪಡೆದುಕೊಳ್ಳಿರಿ! ಆಗ ಆ ಎರಡು ವಿಭಾಗದವರ ನಡುವೆ ಒಂದು ಅಡ್ಡ ಗೋಡೆಯನ್ನು ನಿರ್ಮಿಸಲಾಗುವುದು. ಅದರಲ್ಲಿ ಒಂದು ಬಾಗಿಲಿರುವುದು. ಆ ಬಾಗಿಲಿನ ಒಳಗೆ ಕರುಣೆಯೂ (ಸ್ವರ್ಗ) ಹೊರಗೆ ಅವನ ಕಡೆಯಿಂದ ಯಾತನೆಯೂ (ನರಕ) ಇರುವುದು.');
INSERT INTO "kannada" VALUES(57,14,'57:14','ಅವರು (ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನು) ಕೂಗಿ ಕರೆಯುತ್ತ, ನಾವು (ಇಹಲೋಕದಲ್ಲಿ) ನಿಮ್ಮ ಜೊತೆಗಿರಲಿ ಲ್ಲವೇ? ಎಂದು ಕೇಳುವರು. ಆಗ ಸತ್ಯವಿಶ್ವಾಸಿ ಗಳು ಹೌದು, ಆದರೆ ಸ್ವತಃ ನೀವೇ ನಿಮ್ಮನ್ನು ಕ್ಷೋಭೆಗೆ ಒಡ್ಡಿದಿರಿ. (ಮುಸ್ಲಿಮರಿಗೆ) ಕೆಟ್ಟ ಸಂದ ರ್ಭಗಳನ್ನು ನಿರೀಕ್ಷಿಸಿದಿರಿ. (ಧರ್ಮದಲ್ಲಿ) ಸಂಶ ಯಗ್ರಸ್ತರಾದಿರಿ ಮತ್ತು ಅಲ್ಲಾಹನ ಆದೇಶ (ಮರಣ) ಬಂದು ತಲುಪುವ ತನಕ ವ್ಯಾಮೋಹಗಳು ನಿಮ್ಮನ್ನು ಮೋಸಗೊಳಿಸಿದವು, ಮತ್ತು ಅಲ್ಲಾಹನ ಕುರಿತು ಮಹಾವಂಚಕ ಪಿಶಾಚಿ ನಿಮ್ಮನ್ನು ವಂಚಿಸಿ ಬಿಟ್ಟನು.');
INSERT INTO "kannada" VALUES(57,15,'57:15','ಆದುದರಿಂದ ಇಂದು ನಿಮ್ಮಿಂದಾಗಲಿ, ಸತ್ಯ ನಿಷೇಧಿಗಳಿಂದಾಗಲಿ ಯಾವ ಪ್ರಾಯಶ್ಚಿತ್ತವನ್ನೂ ಸ್ವೀಕರಿಸಲಾಗದು. ನರಕವು ನಿಮ್ಮ ನಿವಾಸವಾಗಿದೆ. ಅದೇ ನಿಮಗೆ ಅತ್ಯಂತ ಯೋಗ್ಯವಾದ ಅಭಯ ಸ್ಥಾನ ಮತ್ತು ಅತ್ಯಂತ ಕೆಟ್ಟ ಸಂಕೇತ.');
INSERT INTO "kannada" VALUES(57,16,'57:16','ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ, ಅವರ ಹೃದಯವು ಅಲ್ಲಾಹನ ಸ್ಮರಣೆಯ ಕಡೆಗೆ ಹಾಗೂ ಅವನಿಂದ ಅವತೀ ರ್ಣಗೊಂಡ ಸತ್ಯದ (ಖುರ್‍ಆನಿನ) ಕಡೆಗೆ ಮಣಿಯಲು ಮತ್ತು ಅವರು ಈ ಹಿಂದೆ ಗ್ರಂಥ ನೀಡಲ್ಪಟ್ಟವರಂತೆ ಆಗದಿರಲು ಕಾಲ ವಿನ್ನೂ ಕೂಡಿ ಬಂದಿಲ್ಲವೇ? ಕೊನೆಗೆ ಆ ವೇದದವರಿಗೆ ಒಂದು ದೀರ್ಘಕಾಲ ಕಳೆದು ಹೋಯಿತು. ಹೀಗಾಗಿ ಅವರ ಹೃದಯಗಳು ಕಠಿಣವಾದುವು ಮತ್ತು (ಇಂದು) ಅವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ಧಿಕ್ಕಾರಿ ಗಳಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(57,17,'57:17','ಚೆನ್ನಾಗಿ ತಿಳಿದುಕೊಳ್ಳಿರಿ. ಅಲ್ಲಾಹನು ಭೂಮಿಯನ್ನು, ಅದು ಸತ್ತ ಬಳಿಕ ಜೀವಂತಗೊಳಿಸುತ್ತಾನೆ . ನೀವು ವಿವೇಚಿಸುವಂತಾಗಲು ನಾವು ನಿಮಗೆ ನಿದರ್ಶನಗಳನ್ನು ವಿವರಿಸಿ ಕೊಟ್ಟಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(57,18,'57:18','ದಾನಿಗಳಾದ ಪುರುಷರಿಗೂ, ದಾನಿಗಳಾದ ಸ್ತ್ರೀ ಯರಿಗೂ ಮತ್ತು ಅಲ್ಲಾಹನಿಗೆ ಶ್ರೇಷ್ಠ ಸಾಲವನ್ನು ನೀಡಿದವರಿಗೆ ಖಂಡಿತವಾಗಿಯೂ (ಪ್ರತಿಫಲ ವನ್ನು) ಇಮ್ಮಡಿಯಾಗಿ ಕೊಡಲಾಗುವುದು. ಅವರಿಗೆ ಮಾನ್ಯವಾದ ಪ್ರತಿಫಲವಿದೆ.');
INSERT INTO "kannada" VALUES(57,19,'57:19','ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ದೂತರ ಮೇಲೆ ವಿಶ್ವಾಸ ವಿರಿಸಿದವರ್ಯಾರೋ ಅವರೇ ತಮ್ಮ ಪ್ರಭುವಿನ ಬಳಿ ಸಿದ್ದೀಖ್‍ಗಳು (ಸತ್ಯಸಂಧರು) ಮತ್ತು ಶಹೀದ್ (ಸತ್ಯಸಾಕ್ಷಿ)ಗಳಾಗಿರುತ್ತಾರೆ . ಅವರಿಗೆ ಅವರ ಪ್ರತಿಫಲವೂ ಅವರ ಪ್ರಕಾಶವೂ ಇದೆ. ಸತ್ಯನಿಷೇಧಿಗಳು ಮತ್ತು ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದವರು ನರಕದವರಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(57,20,'57:20','ತಿಳಿಯಿರಿ! ಐಹಿಕ ಜೀವನವು ಕೇವಲ ಒಂದು ಆಟ, ವಿನೋದ, ಅಲಂಕಾರ ಮತ್ತು ನೀವು ಪರಸ್ಪರ ಹೆಗ್ಗಳಿಕೆ ತೋರಿಸಿಕೊಳ್ಳುವ ಪ್ರಯತ್ನ ಮತ್ತು ಸಂಪತ್ತು ಹಾಗೂ ಸಂತಾನಗಳಲ್ಲಿ ಪರಸ್ಪರ ಪೈಪೋಟಿ ಮಾತ್ರವಲ್ಲದೆ ಇನ್ನೇನೂ ಅಲ್ಲ. ಅದರ ಉಪಮೆ; ಒಂದು ಮಳೆಯಂತೆ. ಅದರಿಂದಾಗಿ ಹುಟ್ಟುವ ಸಸ್ಯಗಳು ಕೃಷಿಕರನ್ನು ಅದ್ಭುತಗೊಳಿ ಸಿತು. ತರುವಾಯ ಅದು (ಬೆಳೆಯು) ಬಲಿತಾಗ ಅದು ಹಳದಿಯಾಗಿ ಬಿಟ್ಟದ್ದನ್ನು ನೀವು ಕಾಣುತ್ತೀರಿ. ಅನಂತರ ಅದು ದ್ರವಿಸಿ ಹೊಟ್ಟಾಗಿ ಬಿಡುತ್ತದೆ. ಪರಲೋಕದಲ್ಲಿ (ಇಹಕ್ಕೆ ಪರಕ್ಕಿಂತ ಆದ್ಯತೆ ಕೊಟ್ಟವರಿಗೆ) ಕಠಿಣ ಶಿಕ್ಷೆ ಇದೆ. (ಪರಕ್ಕೆ ಇಹಕ್ಕಿಂತ ಆದ್ಯತೆ ನೀಡಿದವರಿಗೆ) ಅಲ್ಲಾಹನ ಕ್ಷಮೆ ಹಾಗೂ ಸಂಪ್ರೀತಿ ಇದೆ. ಲೌಕಿಕ ಜೀವನವು ಒಂದು ವಂಚನೆಯ ಸರಕಲ್ಲದೆ ಮತ್ತೇನೂ ಅಲ್ಲ.');
INSERT INTO "kannada" VALUES(57,21,'57:21','ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕ್ಷಮೆ ಹಾಗೂ ಭೂಮಿ-ಆಕಾಶ ಗಳಷ್ಟು ವಿಶಾಲವಾಗಿರುವ ಸ್ವರ್ಗದ ಕಡೆಗೆ ನೀವು ಧಾವಿಸಿ ಬನ್ನಿರಿ. ಅದನ್ನು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ದೂತರುಗಳ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸಿದವರಿಗಾಗಿ ಸಿದ್ಧಗೊಳಿಸಲಾಗಿದೆ. ಅದು ಅಲ್ಲಾಹನ ಔದಾ ರ್ಯವಾಗಿದೆ. ಅವನದನ್ನು ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ನೀಡುತ್ತಾನೆ. ಅಲ್ಲಾಹು ಮಹಾ ಔದಾರ್ಯಶಾಲಿ ಆಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(57,22,'57:22','ಭೂಮಿಯಲ್ಲಿ ಮತ್ತು ನಿಮ್ಮ ದೇಹಗಳಲ್ಲಿ ತಟ್ಟುವ ಯಾವ ಸಂಕಷ್ಟವೂ ನಾವು ಅದನ್ನು ಸೃಷ್ಟಿಸುವುದಕ್ಕೆ ಮುಂಚೆ ಒಂದು ಗ್ರಂಥದಲ್ಲಿ (ಲೌಹುಲ್ ಮಹ್‍ಫೂಳ್‍ನಲ್ಲಿ) ಬರೆದಿಟ್ಟುಕೊಳ್ಳದೆ ಇಲ್ಲ. ಖಂಡಿತವಾಗಿಯೂ ಹೀಗೆ ಮಾಡುವುದು ಅಲ್ಲಾಹನಿಗೆ ಅತ್ಯಂತ ಸುಲಭ ಕಾರ್ಯವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(57,23,'57:23','(ನಿಮಗಿದನ್ನು ನೆನಪಿಸಿದ್ದು) ನಿಮಗುಂಟಾಗುವ ಯಾವುದೇ ನಷ್ಟಕ್ಕಾಗಿ ನೀವು ದುಃಖಿಸದಿರಲು ಮತ್ತು ಅಲ್ಲಾಹನು ನಿಮಗೆ ಏನನ್ನು ದಯಪಾಲಿಸಿರುವನೋ ಅದರ ಬಗ್ಗೆ (ನೀವು ಮೈಮರೆತು) ಹಿಗ್ಗದಿರಲು. ಅಹಂಕಾರಿಯೂ ದುರಭಿಮಾನಿಯೂ ಆದ ಯಾವನನ್ನೂ ಅಲ್ಲಾಹು ಮೆಚ್ಚಲಾರನು.');
INSERT INTO "kannada" VALUES(57,24,'57:24','ಅಂದರೆ ಸ್ವತಃ ಜಿಪುಣತೆ ತೋರುವವರನ್ನು ಮತ್ತು ಜನರಲ್ಲಿ ಜಿಪುಣತೆಗೆ ಆದೇಶಿಸುವವರನ್ನು (ಅಲ್ಲಾಹು ಮೆಚ್ಚುವುದಿಲ್ಲ). ಇನ್ನು ಯಾರಾದರೂ (ಇದನ್ನು ತಿರಸ್ಕರಿಸಿ) ವಿಮುಖರಾದರೆ, ಅಲ್ಲಾಹನು ನಿರಪೇಕ್ಷನೂ ಸ್ತುತ್ಯರ್ಹನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(57,25,'57:25','ನಾವು ನಮ್ಮ ದೂತರುಗಳನ್ನು ಸುವ್ಯಕ್ತ ನಿದರ್ಶ ನಗಳೊಂದಿಗೆ ಕಳುಹಿಸಿದೆವು. ಜನರು ನ್ಯಾಯ ದಲ್ಲಿ ಸ್ಥಿರವಾಗಿ ನಿಲ್ಲಲೆಂದು ಅವರ ಜೊತೆ ವೇದ ಗ್ರಂಥ ಮತ್ತು ನೀತಿಯನ್ನು ಅವತೀರ್ಣಗೊಳಿಸಿದೆವು ಮತ್ತು ನಾವು ಕಬ್ಬಿಣವನ್ನು ಇಳಿಸಿದೆವು. ಅದರಲ್ಲಿ ಭಾರೀ ಆಯೋಧನ ಶಕ್ತಿ ಇದೆ ಮತ್ತು ಜನರಿಗೆ ಹಲವು ಪ್ರಯೋಜನಗಳಿವೆ. ಯಾರು ಅಲ್ಲಾಹನನ್ನು ಕಾಣದೆಯೇ ಅವನಿಗೆ ಮತ್ತು ಅವನ ದೂತರುಗಳಿಗೆ ನೆರವಾಗುತ್ತಾರೆ ಎಂಬುದನ್ನು ಅಲ್ಲಾಹನು (ಪ್ರತ್ಯಕ್ಷ) ತಿಳಿಯುವಂತಾಗಲು ಹೀಗೆ ಮಾಡಲಾಗಿದೆ. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಅತ್ಯಂತ ಬಲಿಷ್ಟನೂ ಅಜೇಯನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(57,26,'57:26','ನಾವು ನೂಹರನ್ನೂ ಇಬ್ರಾಹೀಮರನ್ನೂ ದೂತರಾಗಿ ಕಳುಹಿಸಿದೆವು. ಅವರಿಬ್ಬರ ಸಂತತಿಯಲ್ಲಿ ಪ್ರವಾದಿತ್ವವನ್ನೂ ಗ್ರಂಥವನ್ನೂ ಇರಿಸಿದೆವು. ಅವರಲ್ಲಿ ಸನ್ಮಾರ್ಗ ಸ್ವೀಕರಿಸಿದವರಿದ್ದಾರೆ. ಅವರ ಪೈಕಿ ಹೆಚ್ಚಿನವರು ದುರ್ಮಾರ್ಗಿಗಳಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(57,27,'57:27','ಅವರ ಬಳಿಕ ನಾವು ಒಬ್ಬರ ನಂತರ ಒಬ್ಬರಂತೆ ಸಂದೇಶವಾಹಕರನ್ನು ಕಳುಹಿಸಿದೆವು ಮತ್ತು ಬಳಿಕ ಮರ್ಯಮರ ಪುತ್ರ ಈಸಾರನ್ನು ಕಳುಹಿಸಿದೆವು. ಅವರಿಗೆ ಇಂಜೀಲನ್ನು ದಯಪಾಲಿಸಿ ದೆವು. ಅವರ ಅನುಯಾಯಿಗಳ ಹೃದಯಗಳಲ್ಲಿ ನಾವು ಅನುಕಂಪ ಮತ್ತು ಕರುಣೆಯನ್ನಿರಿಸಿದೆವು. ಸನ್ಯಾಸವನ್ನು ಅವರು ತಾವೇ ಸೃಷ್ಟಿಸಿಕೊಂಡರು . ಅಲ್ಲಾಹನ ಸಂಪ್ರೀತಿಯ ಅಭಿಲಾಷೆಯಿಂದ ಅವರೇ ಈ ಆಚಾರವನ್ನು ಆರಂಭಿಸಿಕೊಂಡರೇ ಹೊರತು ನಾವು ಅದನ್ನವರಿಗೆ ಕಡ್ಡಾಯಗೊಳಿಸಿರಲಿಲ್ಲ. ಆದರೆ ಅವರು ಅದನ್ನು ಪಾಲಿಸಬೇಕಾದ ರೀತಿಯಲ್ಲಿ ಪಾಲಿಸಲಿಲ್ಲ. ಅವರ ಪೈಕಿ ಸತ್ಯವನ್ನು ಸ್ವೀಕರಿಸಿದವರಿಗೆ ಅವರ ಪ್ರತಿಫಲವನ್ನು ನಾವು ದಯಪಾಲಿಸಿದೆವು.ಆದರೆ ಅವರಲ್ಲಿ ಹೆಚ್ಚಿನವರು ದುರ್ಮಾರ್ಗಿಗಳಾಗಿರುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(57,28,'57:28','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ಅಲ್ಲಾಹನನ್ನು ಭಯ ಪಡಿರಿ ಮತ್ತು ಅವನ ದೂತರ (ಮುಹಮ್ಮದ್ ನಬಿಯರ) ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸಿರಿ. ಅಲ್ಲಾಹನು ನಿಮಗೆ ತನ್ನ ಕೃಪೆಯಿಂದ ದುಪ್ಪಟ್ಟು ನೀಡುವನು ಮತ್ತು ನಿಮಗೆ ಪ್ರಕಾಶವನ್ನು ಪ್ರದಾನ ಮಾಡು ವನು, ನೀವು ಅದರ ಬೆಳಕಲ್ಲಿ ನಡೆಯುವಿರಿ ಮತ್ತು ಅವನು ನಿಮ್ಮ ತಪ್ಪುಗಳನ್ನು ಕ್ಷಮಿಸುವನು. ಅಲ್ಲಾಹನು ಮಹಾಕ್ಷಮಾಶೀಲನೂ ಕರುಣಾಮಯಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(57,29,'57:29','ತಮಗೆ ಅಲ್ಲಾಹನ ಅನುಗ್ರಹದಿಂದ ಯಾವುದೇ ಒಂದನ್ನು ಗಳಿಸಲು ಸಾಧ್ಯವಿಲ್ಲವೆಂದೂ ಮತ್ತು ಅಲ್ಲಾಹನ ಅನುಗ್ರಹವು ಅವನ ಅಧೀನದಲ್ಲೇ ಇದ್ದು ಅದನ್ನು ಅವನು ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ನೀಡುತ್ತಾನೆಂದೂ ಗ್ರಂಥದವರಿಗೆ ತಿಳಿಯುವಂತಾಗಲು (ಈ ವಿಷಯ ವ್ಯಕ್ತ ಗೊಳಿಸುವುದು). ಅಲ್ಲಾಹನು ಮಹಾ ಔದಾರ್ಯದ ಒಡೆಯನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(58,1,'58:1','(ಪ್ರವಾದಿವರ್ಯರೇ) ತನ್ನ ಪತಿಯ ವಿಷಯದಲ್ಲಿ ತಮ್ಮಲ್ಲಿ ವಾದಿಸುತ್ತಿರುವ ಹಾಗೂ ಅಲ್ಲಾಹನೊಡನೆ ಮೊರೆಯಿಡುತ್ತಿರುವ ಸ್ತ್ರೀಯ ಮಾತನ್ನು ಅಲ್ಲಾಹನು ಖಂಡಿತ ಆಲಿಸಿದನು. ಅಲ್ಲಾಹನು ನಿಮ್ಮಿಬ್ಬರ ಸಂಭಾಷಣೆಯನ್ನು ಕೇಳುತ್ತಿದ್ದಾನೆ. ಅಲ್ಲಾಹನು ಎಲ್ಲವನ್ನೂ ಕೇಳುವವನು ಮತ್ತು ನೋಡುವವನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(58,2,'58:2','ನಿಮ್ಮ ಪೈಕಿ ತಮ್ಮ ಪತ್ನಿಯರನ್ನು (ಳಿಹಾರ್) ತಾಯಿಗೆ ಸಮಾನರಂತೆ ಸಾರಿ ಬಿಡುವವರು (ತಪ್ಪು ಮಾಡಿರುತ್ತಾರೆ). ಪತ್ನಿಯರು ಅವರ ತಾಯಂದಿರಲ್ಲ. ಅವರನ್ನು ಹೆತ್ತವರೇ ಅವರ ತಾಯಂದಿರು. ಅವರು ನಿಷಿದ್ಧ ಹಾಗೂ ಸುಳ್ಳಾದ ಮಾತನ್ನು ಹೇಳುತ್ತಾರೆ. ವಾಸ್ತವದಲ್ಲಿ ಅಲ್ಲಾಹನು ಬಹಳ ಮನ್ನಿಸುವವನೂ ಮಹಾ ಕ್ಷಮಾಶೀಲನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(58,3,'58:3','ನಿಮ್ಮ ಪೈಕಿ ತಮ್ಮ ಪತ್ನಿಯರನ್ನು ತಾಯಿಗೆ ಸಮಾನವಾಗಿ (ಳಿಹಾರ್) ಮಾಡಿದವರು, ಅನಂತರ ತಾವು ಹೇಳಿದ ಮಾತಿನಿಂದ ಮರಳಿದರೆ ಅವರು ಪರಸ್ಪರ ಸ್ಪರ್ಶಿಸುವುದಕ್ಕೆ ಮುಂಚೆ ಒಬ್ಬ ಗುಲಾಮನನ್ನು ವಿಮೋಚಿಸಬೇಕಾಗುವುದು. ಇದು ನಿಮಗೆ ನೀಡಲಾಗುವ ಉಪದೇಶ. ನೀವು ಮಾಡುವುದರ ಬಗ್ಗೆ ಅಲ್ಲಾಹನು ಸೂಕ್ಷ್ಮಜ್ಞಾನಿಯಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(58,4,'58:4','ಇನ್ನು ಯಾರಿಗಾದರೂ ಗುಲಾಮನು ಸಿಗದಿದ್ದರೆ ಅವರಿಬ್ಬರು ಪರಸ್ಪರ ಸ್ಪರ್ಶಿಸುವುದಕ್ಕೆ ಮುಂಚೆ ಅವನು ನಿರಂತರವಾಗಿ ಎರಡು ತಿಂಗಳ ಉಪವಾಸ ವ್ರತವನ್ನು ಆಚರಿಸಬೇಕು. ಇದಕ್ಕೂ ಅಶಕ್ತನಾಗಿರುವವನು ಅರುವತ್ತು ಮಂದಿ ಮಿಸ್ಕೀನರಿಗೆ (ಬಡವರಿಗೆ) ಊಟ ಕೊಡಬೇಕು. ಇದು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ದೂತರ ಮೇಲೆ ನೀವು ವಿಶ್ವಾಸವಿರಿಸುವಂತಾಗಲಿಕ್ಕಾಗಿ ಇದೆ. ಇವು ಅಲ್ಲಾಹನ ನಿಯಮ ಪರಿಧಿಗಳಾಗಿವೆ. ಸತ್ಯ ನಿಷೇಧಿಗಳಿಗೆ ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆಯಿದೆ.');
INSERT INTO "kannada" VALUES(58,5,'58:5','ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ದೂತರನ್ನು ವಿರೋಧಿಸು ವವರು ತಮಗಿಂತ ಹಿಂದಿನವರು ಅಪಮಾನಿಸ ಲ್ಪಟ್ಟಂತೆ ಅಪಮಾನಿಸಲ್ಪಡುವರು. ಸುಸ್ಪಷ್ಟ ವಚನಗಳನ್ನು ನಾವು ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದೇವೆ. ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಅಪಮಾನಕರವಾದ ಶಿಕ್ಷೆ ಇದೆ.');
INSERT INTO "kannada" VALUES(58,6,'58:6','ಅಲ್ಲಾಹನು ಅವರೆಲ್ಲರನ್ನೂ ಮಗದೊಮ್ಮೆ ಜೀವಂತ ಎಬ್ಬಿಸುವ ದಿನ. ಅವರು ಮಾಡಿರುವ ಕರ್ಮಗಳ ಬಗ್ಗೆ ಅವರಿಗೆ ತಿಳಿಸಲಾಗುವುದು. ಅಲ್ಲಾಹನು ಅವರು ಮಾಡಿದುದನ್ನು ಎಣಿಸಿ ಕಾಯ್ದಿರಿಸಿದ್ದಾನೆ. ಆದರೆ ಅವರು ಮರೆತುಬಿಟ್ಟಿದ್ದಾರೆ. ಅಲ್ಲಾಹನು ಪ್ರತಿಯೊಂದು ವಸ್ತುವಿನ ಮೇಲೆ ಸಾಕ್ಷಿಯಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(58,7,'58:7','ಆಕಾಶಗಳಲ್ಲಿರುವುದನ್ನೂ-ಭೂಮಿಯಲ್ಲಿರುವುದ ನ್ನೂ ಅಲ್ಲಾಹನು ಅರಿಯುತ್ತಾನೆಂದು ನಿಮಗೆ ತಿಳಿ ದಿಲ್ಲವೇ? ಮೂರು ಮಂದಿಗಳ ನಡುವೆ ಗುಪ್ತ ಸಮಾಲೋಚನೆ ನಡೆಸುತ್ತಿರುವಾಗ ಅವರೊಂದಿಗೆ ನಾಲ್ಕನೆಯವನಾಗಿ ಅಲ್ಲಾಹನು ಖಂಡಿತ ಇರು ವನು. ಐದು ಮಂದಿಗಳು ಗೂಢಾಲೋಚನೆ ನಡೆ ಸುವಾಗ ಆರನೆಯವನಾಗಿ ಅಲ್ಲಾಹು ಅಲ್ಲಿ ಇದ್ದೇ ಇರುತ್ತಾನೆ. ಅವರು (ಮಾತು ಕತೆ ನಡೆಸುವವರು) ಇದಕ್ಕಿಂತ ಕಡಿಮೆ ಇರಲಿ ಅಥವಾ ಹೆಚ್ಚಿರಲಿ, ಅವರು ಎಲ್ಲೇ ಇರಲಿ, ಅಲ್ಲಾಹನು ಅವರ ಜೊತೆಗಿರುತ್ತಾನೆ. ಅವರು ಮಾಡಿದ್ದನ್ನು ಪುನರುತ್ಥಾನದಂದು ಅವರಿಗವನು ತಿಳಿಸುವನು. ಅಲ್ಲಾಹನು ಸಕಲ ವಸ್ತುಗಳ ಕುರಿತು ಸರಿಯಾದ ಜ್ಞಾನವುಳ್ಳವನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(58,8,'58:8','ಗೂಢಾಲೋಚನೆ ನಡೆಸುವುದನ್ನು ವಿರೋಧಿಸಲ್ಪಟ್ಟ ಒಂದು ವಿಭಾಗ ಜನರನ್ನು ತಾವು ನೋಡಲಿಲ್ಲವೇ? ಅವರು ಮತ್ತೆ ಅದೇ ವಿರೋಧಿಸಲ್ಪಟ್ಟ ಕೃತ್ಯದ ಕಡೆಗೆ ಮರಳುತ್ತಾರೆ. ಅವರು ಪಾಪ, ಅತಿರೇಕ ಹಾಗೂ ದೂತರೊಂದಿಗಿರುವ ಧಿಕ್ಕಾರದಿಂದ ಪರಸ್ಪರ ಗೂಢಾಲೋಚನೆ ನಡೆಸುತ್ತಾರೆ. ಅವರು ತಮ್ಮ ಬಳಿಗೆ ಬಂದಾಗ ಅಲ್ಲಾಹನು ತಮಗೆ ಸಲಾಮ್ ಹೇಳಿರದ ರೀತಿಯಲ್ಲಿ ತಮಗೆ ಸಲಾಮ್ ಹೇಳುತ್ತಾರೆ ಮತ್ತು ನಮ್ಮ ಈ ಮಾತುಗಳಿಗಾಗಿ ಅಲ್ಲಾಹನು ನಮಗೆ ಶಿಕ್ಷೆ ನೀಡುವುದಿಲ್ಲವೇಕೆ ಎಂದು ಅವರು ಪರಸ್ಪರ ಕೇಳಿಕೊಳ್ಳುತ್ತಾರೆ . ಅವರಿಗೆ ನರಕವೇ ಸಾಕು. ಅವರು ಅದರಲ್ಲಿ ಕರಟುವರು. ಅವರ ಪರ್ಯವಸಾನ ಬಹಳ ನಿಕೃಷ್ಟ.');
INSERT INTO "kannada" VALUES(58,9,'58:9','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ಪರಸ್ಪರ ರಹಸ್ಯ ಸಂಭಾಷಣೆ ನಡೆಸುವಾಗ ಪಾಪ, ಅತಿರೇಕ ಮತ್ತು ದೂತರನ್ನು ಧಿಕ್ಕರಿಸಿಕೊಂಡು ರಹಸ್ಯ ಮಾತುಕತೆ ನಡೆಸಬೇಡಿ, ಪುಣ್ಯ ಹಾಗೂ ಧರ್ಮನಿಷ್ಠೆಯಲ್ಲಿ ನೀವು ರಹಸ್ಯ ಉಪದೇಶ ನೀಡಿರಿ. ಮತ್ತು ನೀವು ಯಾರ ಕಡೆಗೆ ಹೋಗಿ ಒಟ್ಟು ಸೇರಲಿಕ್ಕಿದೆಯೋ ಆ ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ.');
INSERT INTO "kannada" VALUES(58,10,'58:10','ಆ ಗೂಢಾಲೋಚನೆಯು ಪೈಶಾಚಿಕ ಕೃತ್ಯ ವಾಗಿದೆ. ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಅದರಿಂದ ಬೇಸರ ಗೊಳ್ಳಲೆಂದು ಹಾಗೆ ಮಾಡಲಾಗುತ್ತದೆ. ಆದರೆ ಅಲ್ಲಾಹನ ಅನುಮತಿಯಿಲ್ಲದೆ ಅದು ಅವರಿಗೆ ಯಾವ ಹಾನಿಯನ್ನೂ ಮಾಡಲಾರದು. ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳು ಅಲ್ಲಾಹನ ಮೇಲೆಯೇ ಭರವಸೆ ಇಡಲಿ.');
INSERT INTO "kannada" VALUES(58,11,'58:11','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನಿಮ್ಮ ಸಭೆಗಳಲ್ಲಿ ಸ್ಥಳವಕಾಶ ಮಾಡಿಕೊಡಿರೆಂದು ನಿಮ್ಮೊಡನೆ ಹೇಳಲ್ಪಟ್ಟಾಗ ನೀವು ಸ್ಥಳವಕಾಶ ಮಾಡಿಕೊಡಿರಿ. ಅಲ್ಲಾಹನು ನಿಮಗೆ ವಿಶಾಲತೆಯನ್ನು ದಯಪಾಲಿಸುವನು, ನಿಮ್ಮೊಡನೆ ಎದ್ದು ಹೋಗಿರಿ ಎಂದು ಹೇಳಲ್ಪಟ್ಟಾಗ ನೀವು ಎದ್ದು ಹೋಗಿರಿ. ನಿಮ್ಮ ಪೈಕಿ ಸತ್ಯವಿಶ್ವಾಸವುಳ್ಳವರಿಗೆ ಮತ್ತು ಜ್ಞಾನವು ನೀಡಲ್ಪಟ್ಟಿರುವವರಿಗೆ ಅಲ್ಲಾಹನು ಉನ್ನತ ಪದವಿಗಳನ್ನು ದಯಪಾಲಿಸುವನು . ನೀವು ಮಾಡುತ್ತಿರುವುದರ ಬಗ್ಗೆ ಅಲ್ಲಾಹನು ಸೂಕ್ಷ್ಮ ಜ್ಞಾನಿಯು.');
INSERT INTO "kannada" VALUES(58,12,'58:12','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ದೂತರೊಡನೆ ಭೇಟಿ ನಡೆಸುವುದಾದರೆ ನಿಮ್ಮ ಭೇಟಿಗೆ ಮೊದಲು ಏನಾ ದರೊಂದು ದಾನವನ್ನು ಮುಂದಿಡಿರಿ . ಅದು ನಿಮ್ಮ ಪಾಲಿಗೆ ಹೆಚ್ಚು ಉತ್ತಮವೂ ಪರಿಶುದ್ಧವೂ ಆಗಿದೆ. ಇನ್ನು ನಿಮ್ಮ ಬಳಿ (ದಾನಧರ್ಮ ಮಾಡಲು) ಏನೂ ಇಲ್ಲದಿದ್ದರೆ, ಅಲ್ಲಾಹನು ಮಹಾಕ್ಷಮಾಶೀಲ ನೂ ಕೃಪಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(58,13,'58:13','ಭೇಟಿ ನಡೆಸುವುದಕ್ಕೆ ಮುಂಚೆ ಕಾಣಿಕೆ ನೀಡು ವುದರಿಂದ (ದಾರಿದ್ರ್ಯವನ್ನು) ಹೆದರಿಬಿಟ್ಟಿರಾ? ಸರಿ, ನೀವು ಹಾಗೆ ಮಾಡದಿದ್ದರೆ- ಅಲ್ಲಾಹನು ನಿಮ್ಮನ್ನು ಕ್ಷಮಿಸಿಬಿಟ್ಟನು. ನೀವು ನಮಾಝನ್ನು ಸಂಸ್ಥಾಪಿಸಿರಿ, ಝಕಾತ್ ಕೊಡಿರಿ ಮತ್ತು ಅಲ್ಲಾ ಹು ಮತ್ತು ಅವನ ದೂತರನ್ನು ಅನುಸರಿಸಿರಿ. ನೀವು ಮಾಡುತ್ತಿರುವುದೆಲ್ಲವನ್ನೂ ಅಲ್ಲಾಹನು ಸೂಕ್ಷ್ಮವಾಗಿ ಬಲ್ಲವನು.');
INSERT INTO "kannada" VALUES(58,14,'58:14','ಅಲ್ಲಾಹನ ಕ್ರೋಧಕ್ಕೊಳಗಾಗಿರುವಂತಹ ಒಂದು ಕೂಟದವರೊಂದಿಗೆ (ಯಹೂದರು) ಮೈತ್ರಿ ಮಾಡಿ ಕೊಂಡವರನ್ನು (ಕಪಟವಿಶ್ವಾಸಿಗಳನ್ನು) ತಾವು ಕಂಡಿರಾ? ಅವರು ನಿಮ್ಮವರೂ ಅಲ್ಲ, ಅವರ (ಯಹೂದರ) ಕಡೆಯವರೂ ಅಲ್ಲ, ಅವರು ತಿಳಿದುಕೊಂಡೇ ಅಸತ್ಯದ ಹೆಸರಲ್ಲಿ ಸುಳ್ಳು ಆಣೆ ಹಾಕುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(58,15,'58:15','ಅಲ್ಲಾಹನು ಅವರಿಗಾಗಿ ಕಠಿಣ ಶಿಕ್ಷೆಯನ್ನು ಸಿದ್ಧ ಪಡಿಸಿಟ್ಟಿರುವನು. ಅವರು ಮಾಡುತ್ತಿರುವ ಕೃತ್ಯ ಗಳು ಅತ್ಯಂತ ಕೆಟ್ಟವು.');
INSERT INTO "kannada" VALUES(58,16,'58:16','ಅವರು ತಮ್ಮ ಆಣೆಗಳನ್ನು ಒಂದು ಗುರಾಣಿಯಾಗಿ ಮಾಡಿಕೊಂಡಿದ್ದಾರೆ. ಅವರು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ ಜನರನ್ನು ತಡೆಯುತ್ತಾರೆ. ಅದಕ್ಕಾಗಿ ಅವರಿಗೆ ಅಪಮಾನಕರ ಶಿಕ್ಷೆಯಿದೆ.');
INSERT INTO "kannada" VALUES(58,17,'58:17','ಅವರ ಸಂಪತ್ತಾಗಲಿ, ಸಂತಾನಗಳಾಗಲಿ ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆಯಿಂದ ರಕ್ಷಿಸಲು ಎಳ್ಳಷ್ಟೂ ಉಪಯುಕ್ತವಾಗಲಾರವು. ಅವರು ನರಕದವರು. ಅವರು ಅದರಲ್ಲಿ ಸ್ಥಿರವಾಸಿಗಳು.');
INSERT INTO "kannada" VALUES(58,18,'58:18','ಅಲ್ಲಾಹನು ಅವರೆಲ್ಲರನ್ನೂ ಎಬ್ಬಿಸುವ ದಿನ. ಅವರು ನಿಮ್ಮ ಮುಂದೆ ಆಣೆ ಹಾಕಿದಂತೆಯೇ ಅವನ (ಅಲ್ಲಾಹನ) ಮುಂದೆಯೂ ಆಣೆ ಹಾಕುವರು ಮತ್ತು ನಾವು (ಈ ಸುಳ್ಳು ಆಣೆಯಿಂದ) ಏನೋ ಒಂದು ಉತ್ತಮ ಕಾರ್ಯದಲ್ಲಿರು ವೆವೆಂದು ಅವರು ಭಾವಿಸುವರು. ತಿಳಿದುಕೊಳ್ಳಿರಿ. ಅವರೇ ಸುಳ್ಳುಗಾರರು.');
INSERT INTO "kannada" VALUES(58,19,'58:19','ಶೈತಾನನು ಅವರನ್ನು ಸ್ವಾಧೀನಿಸಿಕೊಂಡಿದ್ದಾನೆ ಮತ್ತು ಅವನು ಅಲ್ಲಾಹನ ಸ್ಮರಣೆಯನ್ನು ಅವರಿಗೆ ಮರೆಸಿ ಬಿಟ್ಟಿದ್ದಾನೆ. ಅವರು ಶೈತಾನನ ಪಕ್ಷದವರು. ತಿಳಿಯಿರಿ, ಶೈತಾನನ ಪಕ್ಷದವರೇ ನಷ್ಟಕ್ಕೊಳಗಾದವರು.');
INSERT INTO "kannada" VALUES(58,20,'58:20','ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ದೂತರೊಂದಿಗೆ ಸ್ಪರ್ಧೆಗೆ ನಿಲ್ಲುವವರು ಖಂಡಿತವಾಗಿಯೂ ಅತ್ಯಂತ ನಿಕೃಷ್ಟ ಕೂಟದಲ್ಲಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(58,21,'58:21','ನಾನು ಮತ್ತು ನನ್ನ ದೂತರುಗಳು ವಿಜಯಿಗಳಾಗುವೆವೆಂದು ಅಲ್ಲಾಹನು ವಿಧಿ ಬರೆದಿರುವನು. ಖಂಡಿತ ಅಲ್ಲಾಹನು ಅತ್ಯಂತ ಬಲಿಷ್ಠನೂ ಅಜೇಯನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(58,22,'58:22','ಅಲ್ಲಾಹು ಮತ್ತು ಅಂತ್ಯದಿನದಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿ ಸುವ ಒಂದು ಜನತೆ ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ಸಂದೇಶವಾಹಕರ ವಿರುದ್ಧ ಸ್ಪರ್ಧಿಸುವವರೊಂದಿಗೆ ಮೈತ್ರಿ ಬೆಳೆಸುವುದನ್ನು ತಾವೆಂದೂ ಕಾಣಲಾರಿರಿ . ಅಂಥವರು ಅವರ ತಂದೆಯೋ, ಪುತ್ರ ರೋ ಸಹೋದರರೋ ಮತ್ತು ಆಪ್ತಬಂಧುಗಳೋ ಆಗಿದ್ದರೂ ಸರಿಯೇ. ಅವರ ಹೃದಯಗಳಲ್ಲಿ ಅಲ್ಲಾಹನು ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ದಾಖಲು ಮಾಡಿರುತ್ತಾನೆ. ಮತ್ತು ತನ್ನ ಕಡೆಯಿಂದ ಒಂದು ಆತ್ಮ ಚೈತನ್ಯದ ಮೂಲಕ ಅವರಿಗೆ ರಟ್ಟೆಬಲ ನೀಡಿರುತ್ತಾನೆ. ಅವನು ಅವರನ್ನು, ತಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆಗಳು ಹರಿಯುತ್ತಿರುವ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ ಪ್ರವೇಶಗೊಳಿಸುವನು. ಅವುಗಳಲ್ಲಿ ಅವರು ಸದಾಕಾಲ ವಾಸಿಸುವರು, ಅಲ್ಲಾಹು ಅವರನ್ನು ತೃಪ್ತಿಪಟ್ಟಿರುವನು ಮತ್ತು ಅವರು ಅಲ್ಲಾಹನನ್ನೂ ತೃಪ್ತಿಪಟ್ಟಿರುವರು. ಅವರು ಅಲ್ಲಾಹನ ಪಕ್ಷದವರು, ತಿಳಿಯಿರಿ, ಅಲ್ಲಾಹನ ಪಕ್ಷದವರೇ ವಿಜಯಿಗಳು.');
INSERT INTO "kannada" VALUES(59,1,'59:1','ಭೂಮಿ - ಆಕಾಶಗಳಲ್ಲಿರುವ ಪ್ರತಿಯೊಂದು ವಸ್ತುa ÀÇ ಅಲ್ಲಾಹನನ್ನು ಪ್ರಕೀರ್ತನೆ ಮಾಡಿವೆ. ಅವನು ಪ್ರಬಲನೂ ಯುಕ್ತಿವಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(59,2,'59:2','ಗ್ರಂಥದವರಾದ ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ಪ್ರಥಮ ಗಡೀಪಾರಿನಲ್ಲಿ ಅವರ ಮನೆಗಳಿಂದ ಹೊರ ಹಾಕಿದವನು ಅವನೇ. ಅವರು ಹೊರಟು ಬರುವರೆಂದು ನೀವು ಭಾವಿಸಿರಲಿಲ್ಲ. ತಮ್ಮ ಕೋಟೆಗಳು ತಮ್ಮನ್ನು ಅಲ್ಲಾಹನಿಂದ ಪ್ರತಿರೋಧಿಸಬಹುದೆಂದು ಅವರು ಭಾವಿಸಿದ್ದರು. ಆದರೆ ಅವರು ಊಹಿಸದ ಕಡೆಯಿಂದ ಅಲ್ಲಾಹ್ ಅವರ ಹತ್ತಿರ ಬಂದನು . ಅವನು ಅವರ ಹೃದಯದಲ್ಲಿ ಭೀತಿಯನ್ನು ಹಾಕಿದನು. ಅವರು ಸ್ವತಃ ಕೈಗಳಿಂದಲೂ ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳ ಕೈಗಳಿಂದಲೂ ತಮ್ಮ ಮನೆಗಳನ್ನು ಹಾಳುಗೆಡಹಿದ್ದರು. ಆದುದರಿಂದ ಕಣ್ಣುಳ್ಳವರೇ ನೀವು ಗುಣ ಪಾಠ ಕಲಿಯಿರಿ.');
INSERT INTO "kannada" VALUES(59,3,'59:3','ಅಲ್ಲಾಹನು ಅವರ ಮೇಲೆ ದೇಶ ಬಿಟ್ಟು ಹೋಗುವು ದನ್ನು ವಿಧಿಸದಿರುತ್ತಿದ್ದರೆ ಇಹಲೋಕದಲ್ಲೇ ಅವರನ್ನು ಶಿಕ್ಷಿಸುತ್ತಿದ್ದನು. ಪರಲೋಕದಲ್ಲಿ ಅವರಿಗೆ ನರಕ ಶಿಕ್ಷೆಯೂ ಇದೆ.');
INSERT INTO "kannada" VALUES(59,4,'59:4','ಅದು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ರಸೂಲರೊಂದಿಗೆ ಅವರು ಸ್ಪರ್ಧಿಸಿ ನಿಂತುದರ ಫಲ. ಯಾರಾದರೂ ಅಲ್ಲಾಹನ ವಿರುದ್ಧ ಸ್ಪರ್ಧಿಸಿದರೆ, ಖಂಡಿತ ಅಲ್ಲಾಹನು ಶಿಕ್ಷೆ ಕೊಡುವುದರಲ್ಲಿ ಕಠೋರನು.');
INSERT INTO "kannada" VALUES(59,5,'59:5','(ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ,) ನೀವು ಖರ್ಜೂರದ ಮರಗಳನ್ನು ಕಡಿದರೆ ಅಥವಾ ಅವುಗಳ ಕಾಂಡಗಳಲ್ಲಿ ನಿಲ್ಲಲು ಬಿಟ್ಟರೆ ಅದು, ಅಲ್ಲಾಹನ ಅನುಮತಿ ಪ್ರಕಾರವಾಗಿದೆ. ಮತ್ತು ಅಧರ್ಮಿಗಳನ್ನು ಅಪಮಾನಿಸಲಿಕ್ಕಾಗಿಯೂ ಆಗಿದೆ .');
INSERT INTO "kannada" VALUES(59,6,'59:6','ಅಲ್ಲಾಹನು ಅವರಿಂದ (ಯಹೂದರಿಂದ) ಪಡೆದು ತನ್ನ ಸಂದೇಶವಾಹಕರ ಕಡೆಗೆ ಮರಳಿಸಿದ ಸೊತ್ತಿಗಾಗಿ ನೀವು ಕುದುರೆಗಳನ್ನು ಮತ್ತು ಒಂಟೆಗಳನ್ನು ಓಡಿಸಿಲ್ಲ. ಆದರೆ ಅಲ್ಲಾಹನು ತನ್ನ ದೂತರುಗಳಿಗೆ ತಾನಿಚ್ಛಿಸಿದವರ ಮೇಲೆ ಅಧಿಕಾರವನ್ನು ನೀಡಿ ಬಿಡುತ್ತಾನೆ. ಅಲ್ಲಾಹನು ಸಕಲ ವಸ್ತುವಿನ ಮೇಲೆ ಸಾಮಥ್ರ್ಯವುಳ್ಳವನು.');
INSERT INTO "kannada" VALUES(59,7,'59:7','ಅಲ್ಲಾಹನು ಅವನ ರಸೂಲರಿಗೆ ವಿವಿಧ ನಾಡುಗಳ ಜನರಿಂದ ಗಳಿಸಿಕೊಟ್ಟದ್ದೆಲ್ಲವೂ ಅಲ್ಲಾಹು ಮತ್ತು ರಸೂಲರಿಗೂ ಹಾಗೂ ರಸೂಲರ ಹತ್ತಿರದ ಸಂಬಂಧಿಕರು, ಅನಾಥರು, ಬಡವರು ಮತ್ತು ಯಾತ್ರಿಕರಿಗಾಗಿದೆ. ಆ ಸೊತ್ತು ನಿಮ್ಮ ಶ್ರೀಮಂತರ ನಡುವೆ ಮಾತ್ರ ಹಸ್ತಾಂತರಗೊಳ್ಳದಿರಲಿಕ್ಕಾಗಿ ಈ ನಿಯಮ. ರಸೂಲರು ನಿಮಗೆ ಏನು ಕೊಡುವರೋ ಅದನ್ನು ಸ್ವೀಕರಿಸಿಕೊಳ್ಳಿರಿ. ಮತ್ತು ಯಾವುದರಿಂದ ಅವರು ನಿಮ್ಮನ್ನು ತಡೆಯುವರೋ ಅದರಿಂದ ದೂರವಿರಿ . ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ. ಅಲ್ಲಾಹನು ಕಠಿಣವಾಗಿ ಶಿಕ್ಷಿಸುವವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(59,8,'59:8','ತಮ್ಮ ಮನೆಗಳಿಂದಲೂ ಆಸ್ತಿಪಾಸ್ತಿಗಳಿಂದಲೂ ಹೊರಹಾಕಲ್ಪಟ್ಟಿರುವ ಬಡ ಮುಹಾಜಿರರ ತ್ಯಾಗ ಆಶ್ಚರ್ಯಕರ! ಇವರು ಅಲ್ಲಾಹನಿಂದ ಅನುಗ್ರಹವನ್ನೂ ಸಂಪ್ರೀತಿಯನ್ನೂ ಬಯಸುತ್ತಾರೆ ಮತ್ತು ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ರಸೂಲರಿಗೆ ಸಹಾಯ ಮಾಡುತ್ತಾರೆ. ಅವರೇ ಸತ್ಯಸಂಧರು.');
INSERT INTO "kannada" VALUES(59,9,'59:9','ಅವರ (ಮುಹಾಜಿರರ) ಆಗಮನಕ್ಕೆ ಮುಂಚೆಯೇ ವಾಸಸ್ಥಳವನ್ನೂ, ಸತ್ಯವಿಶ್ವಾಸವನ್ನೂ ಸ್ವೀಕಾರ ಮಾಡಿದ್ದ (ಅನ್ಸಾರ್‍ಗಳ) ಸೇವೆಯು ಪ್ರಸ್ತಾಪಾರ್ಹ . ತಮ್ಮ ಬಳಿಗೆ ದೇಶತ್ಯಾಗ ಮಾಡಿ ಬಂದವರನ್ನು ಅವರು ಪ್ರೀತಿಸುತ್ತಾರೆ. ಅವರಿಗೆ (ಮುಹಾಜಿರರಿಗೆ) ನೀಡಲ್ಪಟ್ಟ ಧನದ ಕುರಿತು ಇವರು ಯಾವುದೇ ದುರಾಗ್ರಹವನ್ನು ತಮ್ಮ ಮನಸ್ಸಿನಲ್ಲಿ ಕಾಣುವುದಿಲ್ಲ. ಸ್ವತಃ ತಮಗೆ ದಾರಿದ್ರ್ಯವಿದ್ದರೂ ಇತರರಿಗೆ ಸ್ವದೇ ಹಕ್ಕಿಂತ ಪ್ರಾಶಸ್ತ್ಯನೀಡುತ್ತಾರೆ. ಯಾರು ತಮ್ಮ ಮನಸ್ಸಿನ ಲೋಭತನದಿಂದ ರಕ್ಷಿಸಲ್ಪಟ್ಟರೋ ಅವರೇ ವಿಜಯಿಗಳು.');
INSERT INTO "kannada" VALUES(59,10,'59:10','ಅವರ ನಂತರ ಬಂದವರ ಅವಸ್ಥೆಯು ಪ್ರಶಂಸಾರ್ಹ. ಅವರು ಹೀಗೆ ಪ್ರಾರ್ಥಿಸುತ್ತಾರೆ. ನಮ್ಮ ಪ್ರಭೂ, ನಮ್ಮನ್ನೂ ನಮಗಿಂತ ಮುಂಚೆ ಸತ್ಯವಿಶ್ವಾಸ ಸ್ವೀಕಾರ ಮಾಡಿದವರನ್ನೂ-ನಿಮ್ಮ ನಮ್ಮೆಲ್ಲ ಸಹೋದರರನ್ನೂ ಕ್ಷಮಿಸಿ ಬಿಡು ಮತ್ತು ನಮ್ಮ ಮನಸ್ಸುಗಳಲ್ಲಿ ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಬಗ್ಗೆ ಯಾವುದೇ ಹಗೆತನವನ್ನಿರಿಸದಿರು. ನಮ್ಮ ಪ್ರಭೂ, ನೀನು ಮಹಾ ವಾತ್ಸಲ್ಯಪೂರ್ಣನೂ ಕೃಪಾನಿಧಿಯೂ ಆಗಿರುವೆ .');
INSERT INTO "kannada" VALUES(59,11,'59:11','ಆ ಕಾಪಟ್ಯ ತೋರಿದವರನ್ನು ನೀವು ನೋಡಿಲ್ಲವೇ? ಇವರು ಗ್ರಂಥದವರಾದ ತಮ್ಮ ಸತ್ಯ ನಿಷೇಧಿ ಸಹೋದರರೊಡನೆ, ನಿಮ್ಮನ್ನು ಹೊರ ಹಾಕಲಾದರೆ ನಿಮ್ಮ ಜೊತೆ ನಾವೂ ಹೊರಡುವೆವು ಮತ್ತು ನಿಮ್ಮ ವಿಷಯದಲ್ಲಿ ನಾವು ಖಂಡಿತ ಯಾರನ್ನೂ ಅನುಸರಿಸಲಾರೆವು ಮತ್ತು ನಿಮ್ಮ ವಿರುದ್ಧ ಯುದ್ದ ಮಾಡಲಾದರೆ ನಾವು ನಿಮಗೆ ಸಹಾಯ ಮಾಡುವೆವು ಎನ್ನುತ್ತಾರೆ. ಆದರೆ ಇವರು ಸುಳ್ಳುಗಾರರೆಂಬುದಕ್ಕೆ ಅಲ್ಲಾಹನು ಸಾಕ್ಷ್ಯವಹಿಸುವನು .');
INSERT INTO "kannada" VALUES(59,12,'59:12','ಅವರು (ಯಹೂದ್ಯರು) ಹೊರಹಾಕಲ್ಪಟ್ಟರೆ ಇವರು (ಕಪಟ ವಿಶ್ವಾಸಿಗಳು) ಖಂಡಿತ ಅವರ ಜೊತೆ ಹೊರಡಲಾರರು. ಅವರ ವಿರುದ್ಧ ಯುದ್ಧ ಮಾಡ ಲಾದರೆ ಇವರು ಖಂಡಿತ ಅವರಿಗೆ ಸಹಾಯ ಮಾಡಲಾರರು. ಇನ್ನು ಇವರು ಅವರಿಗೆ ಸಹಾಯಕ್ಕೆ ಮುಂದೆ ಬಂದರೂ ಬೆನ್ನು ತಿರುಗಿಸಿ ಓಡುವರು. ಅನಂತರ ಯಹೂದಿಗಳು ಯಾವ ಸಹಾಯವನ್ನೂ ಪಡೆಯಲಾರರು.');
INSERT INTO "kannada" VALUES(59,13,'59:13','ಅವರ ಹೃದಯಗಳಲ್ಲಿ ಅಲ್ಲಾಹನಿಗಿಂತ ಅಧಿಕವಾಗಿ ನಿಮ್ಮ ಬಗ್ಗೆ ಭಯವಿದೆ. ಏಕೆಂದರೆ ಇವರು ಕಾರ್ಯವನ್ನು ಗ್ರಹಿಸದ ಜನರಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(59,14,'59:14','ಕೋಟೆಗಳಿಂದಾವೃತವಾದ ಪಟ್ಟಣಗಳಿಂದ ಅಥವಾ ಗೋಡೆಗಳ ಮರೆಯಲ್ಲಿ ಅವಿತುಕೊಂಡು ಮಾತ್ರವಲ್ಲದೆ ಇವರು ಒಟ್ಟಾಗಿ ನಿಮ್ಮ ವಿರುದ್ಧ ಯುದ್ಧ ಮಾಡಲಾರರು. ಇವರು ತಮ್ಮ ಒಳಜಗ ಳಗಳಲ್ಲಿ ಬಹಳ ಕಠೋರರಾಗಿದ್ದಾರೆ. ನೀವು ಇವರನ್ನು ಸಂಘಟಿತರೆಂದು ಭಾವಿಸುತ್ತೀರಿ. ಆದರೆ, ಇವರ ಮನಸ್ಸುಗಳು ಛಿದ್ರವಾಗಿವೆ. ಇದು ಇವರು ಯೋಚಿಸಿ ಗ್ರಹಿಸಲಾರದ ಒಂದು ಜನತೆ ಯಾದ ಕಾರಣಕ್ಕೆ.');
INSERT INTO "kannada" VALUES(59,15,'59:15','ಇವರ ಸ್ಥಿತಿಯು, ಇವರಿಗಿಂತ ಸ್ವಲ್ಪವೇ ಕಾಲ ಹಿಂದೆ ಗತಿಸಿಹೋದ ಜನರಂತಿದೆ. ಅವರ ಕೃತ್ಯಗಳ ದುಷ್ಪರಿಣಾಮವನ್ನು ಅವರು ಸವಿದುಬಿಟ್ಟರು . ಇವರಿಗೆ ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆ ಕಾದಿದೆ.');
INSERT INTO "kannada" VALUES(59,16,'59:16','ಇವರ ಉದಾಹರಣೆಯು ಶೈತಾನನಂತಿದೆ. ಅವನು ಮನುಷ್ಯನೊಡನೆ ಸತ್ಯವನ್ನು ನಿರಾಕರಿಸು ಎಂದು ಹೇಳಿದ ಸಂದರ್ಭ. ಕೊನೆಗೆ ಮನುಷ್ಯನು ಅವಿಶ್ವಾಸ ತಾಳಿದಾಗ ಶೈತಾನನು ‘ನಾನು ನಿನ್ನಿಂದ ಮುಕ್ತನಾಗಿದ್ದೇನೆ. ಸರ್ವಲೋಕಗಳ ಪಾಲಕ ಪ್ರಭುವಾದ ಅಲ್ಲಾಹನನ್ನು ನಾನು ಭಯಪಡುತ್ತೇನೆ’ ಎಂದನು.');
INSERT INTO "kannada" VALUES(59,17,'59:17','ಕೊನೆಗೆ ಇವರೀರ್ವರ ಅಂತಿಮ ಗತಿ ಶಾಶ್ವತರಾಗಿ ನರಕದಲ್ಲಿ ಕಳೆಯುವುದೇ ಆಯಿತು. ಇದೇ ಅಕ್ರಮಿಗಳಿಗಿರುವ ಪ್ರತಿಫಲ.');
INSERT INTO "kannada" VALUES(59,18,'59:18','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ. ಪ್ರತಿಯೊಬ್ಬನೂ ತಾನು ನಾಳೆಗಾಗಿ ಏನನ್ನು ಸಿದ್ಧ ಪಡಿಸಿಟ್ಟಿರುವೆನೆಂಬುದನ್ನು ನೋಡಿಕೊಳ್ಳಲಿ . ನೀವು ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ. ನಿಶ್ಚಯವಾಗಿ ಯೂ ನೀವು ಮಾಡುತ್ತಿರುವ ಸಕಲ ಕರ್ಮಗಳ ಕುರಿತು ಅಲ್ಲಾಹನು ಸೂಕ್ಷ್ಮ ಜ್ಞಾನಿಯಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(59,19,'59:19','ಅಲ್ಲಾಹನನ್ನು ಮರೆತು ಬಿಟ್ಟ ಜನರಂತೆ ನೀವಾಗ ಬಾರದು. ತನ್ಮೂಲಕ ಅಲ್ಲಾಹನು ಅವರಿಗೆ ಅವರ ಕುರಿತೇ ಮರೆಯುವಂತೆ ಮಾಡಿದನು. ಅವರೇ ದುರ್ಮಾರ್ಗಿಗಳು.');
INSERT INTO "kannada" VALUES(59,20,'59:20','ನರಕದ ಜನರೂ ಸ್ವರ್ಗದ ಜನರೂ ಎಂದೂ ಸಮಾನರಾಗಲಾರರು. ಸ್ವರ್ಗಕ್ಕೆ ಹೋಗುವವರೇ ನಿಜವಾದ ವಿಜಯಿಗಳು.');
INSERT INTO "kannada" VALUES(59,21,'59:21','ಈ ಖುರ್‍ಆನನ್ನು ನಾವು ಒಂದು ಪರ್ವತದ ಮೇಲೆ ಇಳಿಸಿ ಬಿಡುತ್ತಿದ್ದರೆ, ಅದು (ಪರ್ವತ) ಅಲ್ಲಾಹನ ಭಯದಿಂದ ವಿನೀತಗೊಳ್ಳುವುದನ್ನು , ಅಂಜಿ ತತ್ತರಿಸಿ ನುಚ್ಚುನೂರಾಗುವುದನ್ನು ನೀವು ಕಾಣುತ್ತಿದ್ದಿರಿ . ಜನರು ಚಿಂತಿಸಲೆಂದು ನಾವು ಅವರ ಮುಂದೆ ಈ ಉಪಮೆಗಳನ್ನು ವಿವರಿಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(59,22,'59:22','ಅವನೇ ಅಲ್ಲಾಹನು. ಅವನ ಹೊರತು ಅನ್ಯ ಆರಾಧ್ಯನಿಲ್ಲ. ದೃಶ್ಯಾ- ದೃಶ್ಯಗಳ ಜ್ಞಾನಿ. ಅವನು ಪರಮ ದಯಾಮಯನೂ ಕೃಪಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(59,23,'59:23','ಅವನೇ ಅಲ್ಲಾಹನು. ಅವನ ಹೊರತು ಬೇರೆ ಆರಾಧ್ಯನಿಲ್ಲ. ಅವನು ಸಾಮ್ರಾಟನು, ಪರಮ ಪಾವನನು, ಶಾಂತಿದಾತನು, ಸಂರಕ್ಷಕನು, ಮೇಲ್ನೋಟಗಾರನು, ಪ್ರಬಲನು, ಪರಮಾಧಿಕಾರಿ. ಸರ್ವೋನ್ನತನು, ಜನರು ಮಾಡುತ್ತಿರುವ ಸಹಭಾಗಿತ್ವದಿಂದ ಅಲ್ಲಾಹನು ಪರಿಶುದ್ಧನು.');
INSERT INTO "kannada" VALUES(59,24,'59:24','ಅವನೇ ಅಲ್ಲಾಹು. ಸೃಷ್ಟಿಕರ್ತನು, ಶೂನ್ಯದಿಂದ ಅಸ್ತಿತ್ವಕ್ಕೆ ತರುವವನು. ರೂಪುದಾಯಕನು . ಅವನಿಗೆ ಉತ್ತಮೋತ್ತಮ ನಾಮಗಳಿವೆ . ಭೂಮಿ-ಆಕಾಶಗಳಲ್ಲಿರುವ ಪ್ರತಿಯೊಂದು ವಸ್ತುವೂ ಅವನ ಮಹತ್ವವನ್ನು ಕೊಂಡಾಡುತ್ತಿವೆ. ಅವನು ಪ್ರಬಲನೂ ಯುಕ್ತಿವಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(60,1,'60:1','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನನ್ನ ಮತ್ತು ನಿಮ್ಮ ಶತ್ರುಗಳೊಂದಿಗೆ ಸ್ನೇಹಬಂಧ ಬೆಳೆಸಿಕೊಂಡು ನೀವು ಅವರನ್ನು ಮಿತ್ರರಾಗಿ ಮಾಡಿಕೊಳ್ಳಬೇಡಿರಿ. ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದಿರುವ ಸತ್ಯವನ್ನು ಅವರು ತಿರಸ್ಕರಿಸಿಯೇ ಬಿಟ್ಟಿರುವರು. ನೀವು ನಿಮ್ಮ ಪ್ರಭುವಾಗಿರುವ ಅಲ್ಲಾಹನ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸಿದ್ದೀರೆಂಬ ಕಾರಣಕ್ಕಾಗಿ ಮಾತ್ರ ಸಂದೇಶವಾಹಕರನ್ನೂ ನಿಮ್ಮನ್ನೂ ಅವರು ನಾಡಿನಿಂದ ಹೊರಹಾಕುತ್ತಾರೆ. ನನ್ನ ಮಾರ್ಗದಲ್ಲಿ ಹೋರಾಡಲಿಕ್ಕಾಗಿ ಮತ್ತು ನನ್ನ ಒಲವಿನಗಳಿಕೆಗಾಗಿ ನೀವು ಹೊರಟಿರುವಿರಾದರೆ, (ನೀವು ಆ ರೀತಿ ಮೈತ್ತಿಯನ್ನು ಸ್ಥಾಪಿಸಬೇಡಿರಿ). ನೀವು ಅವರೊಂದಿಗೆ ಗುಪ್ತವಾಗಿ ಸ್ನೇಹ ಸಂಪರ್ಕ ಬೆಳೆಸುತ್ತೀರಿ! ನೀವು ಗುಪ್ತವಾಗಿಸುವ ಮತ್ತು ಬಹಿರಂಗಗೊಳಿಸುವ ಸಕಲವನ್ನೂ ನಾನು ಚೆನ್ನಾಗಿ ಬಲ್ಲೆನು. ನಿಮ್ಮ ಪೈಕಿ ಹೀಗೆ ಯಾರೇ ಮಾಡಿದರೂ ಅವನು ಖಂಡಿತ ಸನ್ಮಾರ್ಗದಿಂದ ದೂರಸರಿದನು .');
INSERT INTO "kannada" VALUES(60,2,'60:2','ಅವರು ನಿಮ್ಮ ಮೇಲೆ ಜಯ ಸಾಧಿಸಿದರೆ (ಅಧೀನ ಗೊಳಿಸಿದರೆ) ಅವರು ನಿಮ್ಮ ಶತ್ರುಗಳಾಗುವರು. ನಿಮ್ಮ ವಿರುದ್ಧ ಕೇಡಿನಿಂದ ಅವರು ಕೈಗಳನ್ನೂ, ನಾಲಗೆಯನ್ನೂ ಚಾಚುವರು. ನೀವು ಸತ್ಯ ನಿಷೇಧಿಗಳಾಗಿರುತ್ತಿದ್ದರೆ ಎಂದು ಅವರು ಆಗ್ರಹಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(60,3,'60:3','ಪುನರುತ್ಥಾನ ದಿನ ನಿಮ್ಮ ರಕ್ತ ಬಾಂಧವ್ಯಗಳಾಗಲಿ, ನಿಮ್ಮ ಸಂತತಿಗಳಾಗಲಿ ನಿಮಗೆ ಉಪಕರಿಸಲಾರವು. ಅಂದು ಅಲ್ಲಾಹನು ನಿಮ್ಮ ನಡುವೆ ಬೇರ್ಪಡಿಸುವನು ಮತ್ತು ನಿಮ್ಮ ಕರ್ಮಗಳನ್ನು ಅಲ್ಲಾಹು ನೋಡುವವನಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(60,4,'60:4','ಇಬ್‍ರಾಹೀಮ್ ಮತ್ತು ಅವರ ಸಂಗಡಿಗರಲ್ಲಿ ನಿಮಗೆ ಉತ್ತಮ ಆದರ್ಶವಿದೆ. ಅವರು ತಮ್ಮ ಜನಾಂಗದೊಡನೆ ಹೇಳಿದ ಸಂದರ್ಭ - ``ನಾವು ನಿಮ್ಮಿಂದ ಹಾಗೂ ನೀವು ಅಲ್ಲಾಹನನ್ನು ಬಿಟ್ಟು ಪೂಜಿಸುತ್ತಿರುವ ಈ ಆರಾಧ್ಯ ವಸ್ತುಗಳಿಂದ ಸಂಪೂರ್ಣ ದೂರಸರಿದವರಾಗಿದ್ದೇವೆ. ನಾವು ನಿಮ್ಮಲ್ಲಿ ಅವಿಶ್ವಾಸ ತಾಳಿದೆವು. ನೀವು ಏಕೈಕನಾದ ಅಲ್ಲಾಹನ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸುವವರೆಗೆ ನಮ್ಮ ನಿಮ್ಮೊಳಗೆ ಶಾಶ್ವತವಾಗಿ ದ್ವೇಷ ಮತ್ತು ವೈರತ್ವ ಪ್ರಕಟವಾಯಿತು . ಆದರೆ ಇಬ್‍ರಾಹೀಮರು ತಮ್ಮ ತಂದೆಯೊಡನೆ ‘ನಾನು ಖಂಡಿತ ನಿಮ್ಮ ಕ್ಷಮೆಗಾಗಿ ಪ್ರಾರ್ಥಿಸುವೆನು ಮತ್ತು ಅಲ್ಲಾಹನಿಂದ ನಿಮಗಾಗಿ ನಾನು ಏನನ್ನೂ ಒಡೆತನಗೊಳಿಸುವುದಿಲ್ಲ’ ಎಂದು ಹೇಳಿದ ಮಾತಿನ ಹೊರತು. (ಆ ಮಾತಿನಲ್ಲಿ ನಿಮಗೆ ಮಾದರಿ ಇರುವುದಿಲ್ಲ.) “ನಮ್ಮ ಪ್ರಭೂ, ನಾವು ನಿನ್ನ ಮೇಲೆಯೇ ಭರವಸೆಯನ್ನಿರಿಸಿದ್ದೇವೆ. ನಿನ್ನ ಕಡೆಗೇ ನಾವು ವಿಧೇಯರಾಗಿ ಮರಳಿದ್ದೇವೆ ಮತ್ತು ನಿನ್ನ ಸನ್ನಿಧಿಗೇ ಎಲ್ಲರ ಮರಳಿಕೆ” (ಎಂದು ಇಬ್ರಾಹೀಮರು ಪ್ರಾರ್ಥಿಸಿದರು).');
INSERT INTO "kannada" VALUES(60,5,'60:5','ನಮ್ಮ ಪ್ರಭೂ, ನಮ್ಮನ್ನು ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಪರೀಕ್ಷೆಯನ್ನಾಗಿ ಮಾಡದಿರು . ನಮ್ಮ ಪ್ರಭೂ, ನಮ್ಮ ತಪ್ಪುಗಳನ್ನು ಕ್ಷಮಿಸು, ನಿಸ್ಸಂದೇಹವಾಗಿಯೂ ನೀನೇ ಪ್ರಬಲನೂ ಯುಕ್ತಿವಂತನೂ ಆಗಿದ್ದಿ.');
INSERT INTO "kannada" VALUES(60,6,'60:6','ನಿಮಗೆ - ಅಲ್ಲಾಹು ಹಾಗೂ ಪುನರುತ್ಥಾನ ದಿನವನ್ನು ನಿರೀಕ್ಷೆ (ಭಯ) ಮಾಡಿರುವ ಪ್ರತಿಯೊಬ್ಬನಿಗೆ - ಅವರ ಚರ್ಯೆಯಲ್ಲಿ ಖಂಡಿತ ಉತ್ತಮ ಆದರ್ಶವಿದೆ. ಯಾರಾದರೂ ವಿಮುಖರಾದರೆ, ಅಲ್ಲಾಹನು ನಿರಪೇಕ್ಷನೂ, ಸ್ತುತ್ಯರ್ಹನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(60,7,'60:7','ನೀವು ಅವರ ಪೈಕಿ ಯಾರೊಂದಿಗೆ ವೈರ ಬೆಳೆಸಿ ಕೊಂಡಿರುವಿರೋ ಅವರ ಮತ್ತು ನಿಮ್ಮ ನಡುವೆ ಅಲ್ಲಾಹನು ಸ್ನೇಹ ಸಂಬಂಧವನ್ನು ಸ್ಥಾಪಿಸಿ ಬಿಡಲೂಬಹುದು . ಅಲ್ಲಾಹನು ಸರ್ವ ಸಮರ್ಥ ನಾಗಿದ್ದಾನೆ. ಮತ್ತು ಅಲ್ಲಾಹನು ಅತ್ಯಂತ ಕ್ಷಮಾ ಶೀಲನೂ, ಕರುಣಾಮಯಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(60,8,'60:8','ಧರ್ಮದ ವಿಷಯದಲ್ಲಿ ನಿಮ್ಮೊಡನೆ ಯುದ್ಧ ಮಾಡದ ಹಾಗೂ ನಿಮ್ಮನ್ನು ನಿಮ್ಮ ಮನೆಗಳಿಂದ ಹೊರ ಹಾಕದ ಜನರಿಗೆ ನೀವು ಉಪಕಾರ ಮಾಡುವುದನ್ನೂ ನೀವು ಅವರಿಗೆ ನ್ಯಾಯ ತೋರು ವುದನ್ನೂ ಅಲ್ಲಾಹನು ನಿಮ್ಮನ್ನು ತಡೆಯುವುದಿಲ್ಲ. ನೀತಿವಂತರನ್ನು ಅಲ್ಲಾಹನು ಖಂಡಿತ ಪ್ರೀತಿಸು ತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(60,9,'60:9','ನಿಜವಾಗಿಯೂ ಅಲ್ಲಾಹನು ನಿಮಗೆ ನಿಷೇಧ ಮಾಡಿರುವುದು ಧರ್ಮದ ವಿಷಯದಲ್ಲಿ ನಿಮ್ಮ ವಿರುದ್ಧ ಯುದ್ಧ ಮಾಡಿರುವ ಹಾಗೂ ನಿಮ್ಮನ್ನು ನಿಮ್ಮ ಮನೆಗಳಿಂದ ಹೊರಹಾಕಿರುವ ಮತ್ತು ನಿಮ್ಮನ್ನು ಹೊರಹಾಕುವ ವಿಷಯದಲ್ಲಿ ಪರಸ್ಪರ ಸಹಕರಿಸಿರುವ ಜನರೊಂದಿಗೆ ಸ್ನೇಹ ಬೆಳೆಸುವು ದನ್ನಾಗಿದೆ. ಯಾರಾದರೂ ಅವರೊಂದಿಗೆ ಸ್ನೇಹ ಬೆಳೆಸಿದರೆ, ಅವರೇ ಅಕ್ರಮಿಗಳು.');
INSERT INTO "kannada" VALUES(60,10,'60:10','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಸತ್ಯವಿಶ್ವಾಸಿನಿಯರು ಸ್ವದೇಶ ತೊರೆದು ಅಭಯಾರ್ಥಿನಿಗಳಾಗಿ ನಿಮ್ಮ ಬಳಿಗೆ ಬಂದರೆ ಅವರನ್ನು ಪರೀಕ್ಷಿಸಿ ನೋಡಿ. ಅವರ ಸತ್ಯವಿಶ್ವಾಸದ ಕುರಿತು ಅಲ್ಲಾಹನು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು. ಇನ್ನು ಅವರು ಸತ್ಯವಿಶ್ವಾಸಿ ನಿಯರೆಂದು ನಿಮಗೆ ತಿಳಿದು ಬಂದರೆ, ಅವರ ನ್ನು ಸತ್ಯನಿಷೇಧಿಗಳ ಬಳಿಗೆ ಹಿಂದಿರುಗಿಸದಿರಿ . ಆ ಸ್ತ್ರೀಯರು ಅವರಿಗೆ (ಸತ್ಯನಿಷೇಧಿಗಳಾದ ಪುರುಷರಿಗೆ) ಅನುವದನೀಯರಲ್ಲ. ಅವರು ಆ ಸ್ತ್ರೀಯರಿಗೂ ಅನುವದನೀಯರಲ್ಲ. ಅವರ (ಸತ್ಯನಿಷೇಧಿ) ಗಂಡಂದಿರು ಅವರಿಗೆ ಖರ್ಚು ಭರಿಸಿದ್ದನ್ನು ಅವರಿಗೆ ಮರಳಿಸಿರಿ. ನೀವು ಆ ಸ್ತ್ರೀಯರಿಗೆ ಅವರ ಪ್ರತಿಫಲವನ್ನು ಪಾವತಿ ಮಾಡಿದರೆ ನಿಮಗೆ ಅವರನ್ನು ಮದುವೆ ಮಾಡಿ ಕೊಳ್ಳುವುದಕ್ಕೆ ವಿರೋಧವಿಲ್ಲ . ನೀವು ಸತ್ಯ ನಿಷೇಧಿ ಸ್ತ್ರೀಯರ ವಿವಾಹ ಬಂಧನದಲ್ಲಿ ಹಿಡಿದು ನಿಲ್ಲದಿರಿ. ಖರ್ಚು ಭರಿಸಿದ್ದನ್ನು ನೀವು ಕೇಳಿಪಡೆದುಕೊಳ್ಳಿರಿ. ಅವರು ಖರ್ಚು ಭರಿಸಿದ್ದನ್ನು ಅವರೂ ಕೇಳಿಪಡಕೊಳ್ಳಲಿ. ಇದು ಅಲ್ಲಾಹನ ನಿಯಮವಾಗಿದೆ. ಅವನು ನಿಮ್ಮ ನಡುವೆ ತೀರ್ಮಾನ ಮಾಡುತ್ತಾನೆ. ಅಲ್ಲಾಹನು ಸರ್ವ ಜ್ಞನೂ ಯುಕ್ತಿವಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(60,11,'60:11','ನಿಮ್ಮ ಪತ್ನಿಯರ ಪೈಕಿ ಯಾರಾದರೂ ಸತ್ಯ ನಿಷೇಧಿಗಳ ಬಳಿಗೆ ಹೋಗಿ ನಿಮಗೆ ನಷ್ಟವಾದರೆ, ಅನಂತರ ನೀವು ಕ್ರಮ ಕೈಗೊಂಡರೆ, ಯಾರ ಪತ್ನಿಯರು ಕಳೆದು ಹೋಗಿರುವರೋ ಅವರಿಗೆ ಅವರು ಖರ್ಚು ಭರಿಸಿದ್ದಕ್ಕೆ ಸರಿದೂಗುವ ಮೊತ್ತ ವನ್ನು (ಮಹ್ರ್) ಕೊಟ್ಟುಬಿಡಿರಿ ಮತ್ತು ನೀವು ವಿಶ್ವಾಸವಿರಿಸಿರುವ ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ.');
INSERT INTO "kannada" VALUES(60,12,'60:12','ಓ ಪ್ರವಾದಿಯರೇ, ಸತ್ಯವಿಶ್ವಾಸಿನಿಯರು, ತಮ್ಮ ಬಳಿಗೆ ಬಂದು `ಅಲ್ಲಾಹನೊಂದಿಗೆ ಯಾರನ್ನೂ ಸಹಭಾಗಿಗಳಾಗಿ ಮಾಡಲಾರೆವು, ಕಳ್ಳತನ ಮಾಡಲಾರೆವು, ವ್ಯಭಿಚಾರವೆಸಗಲಾರೆವು, ತಮ್ಮ ಮಕ್ಕಳನ್ನು ವಧಿಸಲಾರೆವು, ತಮ್ಮ ಕೈಕಾಲುಗಳ ಮಧ್ಯೆ ಯಾವುದೇ ಸುಳ್ಳಾರೋಪವನ್ನು ಸೃಷ್ಟಿಸಿ ತರಲಾರೆವು ಮತ್ತು ಯಾವುದೇ ಸತ್ಕಾರ್ಯದ ವಿಷಯದಲ್ಲಿ ತಮಗೆ ಅವಿಧೇಯತೆ ತೋರಲಾರೆವು’ ಎಂದು ಪ್ರತಿಜ್ಞೆ ಮಾಡಿದರೆ, ಅವರಿಂದ ಪ್ರತಿಜ್ಞೆಯನ್ನು ಸ್ವೀಕರಿಸಿಕೊಳ್ಳಿರಿ ಮತ್ತು ಅವರಿಗಾಗಿ ಅಲ್ಲಾಹನಲ್ಲಿ ಕ್ಷಮೆ ಯಾಚಿಸಿರಿ. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಕ್ಷಮಾಶೀಲನೂ ಕರುಣಾಮಯಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(60,13,'60:13','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಅಲ್ಲಾಹನ ಕ್ರೋಧಕ್ಕೊಳಗಾದ ಒಂದು ಜನತೆಯೊಂದಿಗೆ ನೀವು ಮೈತ್ರಿ ಬೆಳೆಸ ಬೇಡಿರಿ. ಸಮಾಧಿಗಳೊಳಗೆ ಇರುವವರ ಕುರಿತು ಸತ್ಯನಿಷೇಧಿಗಳು ನಿರಾಶರಾಗಿ ಬಿಟ್ಟಂತೆಯೇ ಅವರು ಪರಲೋಕದ ಬಗೆಗೆ ನಿರಾಶರಾಗಿ ಬಿಟ್ಟಿರುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(61,1,'61:1','ಆಕಾಶ-ಭೂಮಿಗಳಲ್ಲಿರುವ ಪ್ರತಿಯೊಂದು ವಸ್ತುವೂ ಅಲ್ಲಾಹನ ಪರಿಶುದ್ಧತೆಯನ್ನು ಕೊಂಡಾ ಡಿವೆ. ಅವನು ಅಜೇಯನೂ ಯುಕ್ತಿವಂತನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(61,2,'61:2','ಓ ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ಮಾಡದ್ದನ್ನು ಆಡುತ್ತೀರೇಕೆ ?');
INSERT INTO "kannada" VALUES(61,3,'61:3','ನೀವು ಮಾಡದ್ದನ್ನು ಆಡುವುದು ಅಲ್ಲಾಹನ ಬಳಿ ಬಹಳ ಕ್ರೋಧದ ವಿಚಾರವಾಗಿದೆ .');
INSERT INTO "kannada" VALUES(61,4,'61:4','(ಕಲ್ಲುಗಳನ್ನು) ಪರಸ್ಪರ ಬಂಧಿಸಿ ನಿರ್ಮಿಸಲ್ಪಟ್ಟ ಒಂದು ಕಟ್ಟಡದಂತೆ ಪಂಕ್ತಿಬದ್ಧರಾಗಿ ತನ್ನ ಮಾರ್ಗದಲ್ಲಿ ಹೋರಾಡುವವರನ್ನು ಅಲ್ಲಾಹನು ಖಂಡಿತ ಮೆಚ್ಚುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(61,5,'61:5','ಮೂಸಾ, ತಮ್ಮ ಜನಾಂಗದೊಡನೆ ಹೇಳಿದ ಸಂದರ್ಭ (ಸ್ಮರಿಸಿರಿ) “ನನ್ನ ಜನಾಂಗದವರೇ, ನಾನು ನಿಮ್ಮ ಕಡೆಗೆ ಕಳುಹಿಸಲ್ಪಟ್ಟ ಅಲ್ಲಾಹನ ದೂತನೆಂದು ನಿಮಗೆ ಚೆನ್ನಾಗಿ ತಿಳಿದಿದ್ದರೂ ನೀವು ನನ್ನನ್ನೇಕೆ ಹಿಂಸಿಸುತ್ತೀರಿ?” ತರುವಾಯ ಅವರು ಸತ್ಯದಿಂದ ತಪ್ಪಿ ಹೋದಾಗ ಅಲ್ಲಾಹನು ಅವರ ಹೃದಯಗಳನ್ನು ತಪ್ಪಿಸಿ ಬಿಟ್ಟನು. ಅಲ್ಲಾಹನು ಧಿಕ್ಕಾರಿಗಳಾದ ಜನತೆಗೆ ಮಾರ್ಗದರ್ಶನ ನೀಡಲಾರನು.');
INSERT INTO "kannada" VALUES(61,6,'61:6','ಮರ್ಯಮರ ಪುತ್ರ ಈಸಾ ಹೇಳಿದ ಸಂದರ್ಭ (ಸ್ಮರಿಸಿರಿ) “ಇಸ್ರಾಈಲ್ ಸಂತತಿಗಳೇ, ನಾನು, ನನಗಿಂತ ಮೊದಲು ಬಂದಿರುವ ತೌರಾತನ್ನು ದೃಢೀಕರಿಸುವವನಾಗಿಯೂ ನನ್ನ ಬಳಿಕ ಬರಲಿರುವ ಅಹ್ಮದ್ ಎಂಬ ಹೆಸರಿನ ಓರ್ವ ದೂತರ ಕುರಿತು ಸುವಾರ್ತೆ ನೀಡುವವನಾಗಿಯೂ ನಿಮ್ಮ ಕಡೆಗೆ ಅಲ್ಲಾಹನಿಂದ ಕಳುಹಿಸಲಾದ ದೂತನಾಗಿರುತ್ತೇನೆ”. ಅಂತೆಯೇ, ಅವರು ಸುಸ್ಪಷ್ಟ ನಿದರ್ಶನದೊಂದಿಗೆ ಅವರ ಬಳಿಗೆ ಹೋದಾಗ ‘ಇದು ಸ್ಪಷ್ಟ ಜಾಲ ವಿದ್ಯೆ’ ಎಂದು ಅವರು ಹೇಳಿ ಬಿಟ್ಟರು .');
INSERT INTO "kannada" VALUES(61,7,'61:7','ಒಬ್ಬನನ್ನು ಇಸ್ಲಾಮಿನೆಡೆಗೆ ಕರೆಯಲಾದಾಗ ಅವನು ಅಲ್ಲಾಹನ ಮೇಲೆ ಸುಳ್ಳಾರೋಪ ಹೊರಿಸಿದರೆ ಅವನಿಗಿಂತ ದೊಡ್ಡ ಅಕ್ರಮಿ ಯಾರಿದ್ದಾನೆ? ಅಕ್ರಮಿಗಳಾದ ಜನತೆಗೆ ಅಲ್ಲಾಹನು ಮಾರ್ಗದರ್ಶನ ನೀಡಲಾರನು.');
INSERT INTO "kannada" VALUES(61,8,'61:8','ಅವರು ಅಲ್ಲಾಹನ ಪ್ರಕಾಶವನ್ನು ತಮ್ಮ ಬಾಯಿಂದ ಊದಿ ನಂದಿಸಲಿಚ್ಚಿಸುತ್ತಾರೆ. ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಅಪ್ರಿಯವಾಗಿದ್ದರೂ ಅಲ್ಲಾಹನು ತನ್ನ ಪ್ರಕಾಶವನ್ನು ಪೂರ್ತಿಮಾಡುವನು .');
INSERT INTO "kannada" VALUES(61,9,'61:9','ಸನ್ಮಾರ್ಗ ಮತ್ತು ಸತ್ಯಧರ್ಮದೊಂದಿಗೆ ಉಳಿದ ಧರ್ಮಗಳಿಗಿಂತ ಅದನ್ನು ಮೇಲಾಗಿ ಬೆಳಕಿಗೆತರುವ ಸಲುವಾಗಿ ತನ್ನ ದೂತನನ್ನು ಕಳುಹಿಸಿರುವವನು ಅವನೇ. ಬಹುದೇವ ವಿಶ್ವಾಸಿಗಳಿಗೆ ಇದು ಎಷ್ಟೇ ಅಪ್ರಿಯವಾದರೂ ಸರಿಯೇ.');
INSERT INTO "kannada" VALUES(61,10,'61:10','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನಿಮ್ಮನ್ನು ವೇದನಾಜನಕ ಶಿಕ್ಷೆಯಿಂದ ರಕ್ಷಿಸುವಂತಹ ಒಂದು ವ್ಯಾಪಾರದ ಬಗ್ಗೆ ನಿಮಗೆ ತಿಳಿಸಿಕೊಡಲೇ? ');
INSERT INTO "kannada" VALUES(61,11,'61:11','(ಅದೇನೆಂದರೆ) ಅಲ್ಲಾಹು ಮತ್ತು ಅವನ ಸಂದೇಶ ವಾಹಕರಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸುವುದು, ನಿಮ್ಮ ಸಂಪತ್ತುಗಳಿಂದಲೂ ಶರೀರಗಳಿಂದಲೂ ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಹೋರಾಡುವುದು. ನಿಮ್ಮ ಪಾಲಿಗೆ ಅದು ಅತಿ ಉತ್ತಮವಾಗಿದೆ. ನೀವು ತಿಳುವಳಿಕೆಯುಳ್ಳವರಾಗಿದ್ದರೆ.');
INSERT INTO "kannada" VALUES(61,12,'61:12','(ಹಾಗಾದರೆ) ಅಲ್ಲಾಹನು ನಿಮ್ಮ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸುವನು ಮತ್ತು ತಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆಗಳು ಹರಿಯುತ್ತಿರುವ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ ಮತ್ತು ಸ್ಥಿರವಾಸಕ್ಕಿರುವ ಸ್ವರ್ಗಧಾಮಗಳಲ್ಲಿಯ ವಿಶಿಷ್ಟ ವಸತಿಗಳಲ್ಲಿ ಅವನು ನಿಮ್ಮನ್ನು ಪ್ರವೇಶಗೊಳಿಸುವನು. ಅದುವೇ ದಿಗ್ವಿಜಯ.');
INSERT INTO "kannada" VALUES(61,13,'61:13','ನೀವು ಇಷ್ಟಪಡುವ ಇನ್ನೊಂದು ವಸ್ತುವನ್ನೂ (ನಿಮಗೆ ಕೊಡುವನು.) ಅಂದರೆ (ಅದು) ಅಲ್ಲಾ ಹನ ಕಡೆಯಿಂದ ಸಹಾಯ ಮತ್ತು ಆಸನ್ನವಾದ ವಿಜಯ. (ನಬಿಯರೇ,) ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ (ಇದರ) ಸುವಾರ್ತೆ ನೀಡಿರಿ.');
INSERT INTO "kannada" VALUES(61,14,'61:14','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ಅಲ್ಲಾಹನ ಸಹಾಯಕರಾಗಿರಿ, ಮರ್ಯಮರ ಪುತ್ರ ಈಸಾ ಹವಾರಿಗಳೊಡನೆ (ಶಿಷ್ಯರೊಂದಿಗೆ) ‘ಅಲ್ಲಾಹನ ಕಡೆಗೆ ಕರೆ ನೀಡುವಲ್ಲಿ ನನ್ನ ಸಹಾಯಕರು ಯಾರಿದ್ದಾರೆ?’ ಎಂದು ಹೇಳಿದ್ದಂತೆ. ಹವಾರಿಗಳು ಹೇಳಿದರು; ನಾವು ಅಲ್ಲಾಹನ ಸಹಾಯಕರು. ಆಗ ಇಸ್ರಾಈಲ ಸಂತತಿಗಳ ಪೈಕಿ ಒಂದು ವಿಭಾಗ ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಸ್ವೀಕರಿಸಿತು ಮತ್ತು ಇನ್ನೊಂದು ವಿಭಾಗವು ನಿರಾಕರಿಸಿತು. ತರುವಾಯ ನಾವು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಅವರ ಶತ್ರುಗಳ ವಿರುದ್ಧ ಬಲ ನೀಡಿದೆವು. ಹಾಗೆ ಅವರು ವಿಜಯಿಗಳಾಗಿ ಬಿಟ್ಟರು .');
INSERT INTO "kannada" VALUES(62,1,'62:1','ಭೂಮಿ-ಆಕಾಶಗಳಲ್ಲಿರುವ ಪ್ರತಿಯೊಂದು ವಸ್ತುವೂ ಅಲ್ಲಾಹನ ಪರಿಶುದ್ಧತೆಯನ್ನು ಪ್ರಕೀ ರ್ತಿಸುತ್ತಿದೆ. (ಅವನು) ರಾಜಾಧಿಪತಿಯೂ ಅತ್ಯಂತ ಪರಿಶುದ್ಧನೂ ಅಜೇಯನೂ ಯುಕ್ತಿಪೂರ್ಣನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(62,2,'62:2','ಅವನೇ ನಿರಕ್ಷರಿಗಳ ನಡುವೆ ಅವರಿಂದಲೇ ಒಬ್ಬ ಸಂದೇಶವಾಹಕರನ್ನು ನಿಯೋಗಿಸಿದವನು. ಅವರು ಅವರಿಗೆ ಅವನ ಸೂಕ್ತಗಳನ್ನು ಓದಿ ಹೇಳು ತ್ತಾರೆ, ಅವರನ್ನು ಸಂಸ್ಕರಿಸುತ್ತಾರೆ ಮತ್ತು ಅವರಿಗೆ ಗ್ರಂಥ ಹಾಗೂ ತತ್ವಜ್ಞಾನವನ್ನು ಕಲಿಸುತ್ತಾರೆ. ಅದಕ್ಕಿಂತ ಮುಂಚೆ ಅವರು ಸ್ಪಷ್ಟ ಪಥಭ್ರಷ್ಟತೆಗೆ ಒಳಗಾಗಿದ್ದರು .');
INSERT INTO "kannada" VALUES(62,3,'62:3','ಅವರಲ್ಲಿ ಒಳಪಟ್ಟವರೂ ಇನ್ನೂ ಅವರ ಬಳಿ ಬಂದು ತಲುಪದಿರುವವರೂ ಆದ ಬೇರೆ ಕೆಲವರ ಬಳಿಗೂ (ಮುಹಮ್ಮದ್ ನಬಿಯನ್ನು ನಿಯೋಗಿಸಲಾಗಿದೆ). ಅಲ್ಲಾಹನು ಪ್ರಬಲನೂ ಯುಕ್ತಿವಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(62,4,'62:4','ಅದು ಅಲ್ಲಾಹನ ಅನುಗ್ರಹ. ಅದನ್ನು ತಾನಿಚ್ಚಿಸಿ ದವರಿಗೆ ಅವನು ನೀಡುತ್ತಾನೆ. ಅಲ್ಲಾಹನು ಮಹಾ ಅನುಗ್ರಹದಾತನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(62,5,'62:5','ತೌರಾತಿನ (ಅನುಸರಣೆಯ) ಹೊಣೆ ಹೊರಿಸಲ್ಪಟ್ಟು, ತದನಂತರ ಅದರ ಹೊಣೆಗಾರಿಕೆಯನ್ನು ಹೊರದೆ ಇರುವವರ ಉದಾಹರಣೆಯು ಗ್ರಂಥಗಳನ್ನು ಹೊತ್ತುಕೊಂಡ ಕತ್ತೆಯಂತಿದೆ . ಅಲ್ಲಾಹನ ಸೂಕ್ತಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದ ಜನತೆಯ ಉಪಮೆ ಬಹಳ ನಿಕೃಷ್ಟ. (ಇಂತಹ) ಅಕ್ರಮಿಗಳಾದ ಜನತೆಗೆ ಅಲ್ಲಾಹನು ಮಾರ್ಗದರ್ಶನ ಮಾಡುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(62,6,'62:6','ಹೇಳಿರಿ; ಯಹೂದಿಗಳೇ, ಇತರ ಮನುಷ್ಯರನ್ನು ಹೊರತು ನೀವು ಮಾತ್ರ ಅಲ್ಲಾಹನಿಗೆ ನೆಚ್ಚಿನವರೆಂಬವಾದ ನಿಮ್ಮದಾಗಿದ್ದರೆ ಮತ್ತು ಅದರಲ್ಲಿ ನೀವು ಸತ್ಯವಂತರಾಗಿದ್ದರೆ ಮರಣವನ್ನು ನೀವು ಆಶಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(62,7,'62:7','ಆದರೆ ಅವರು ತಮ್ಮ ಕೈಗಳಿಂದ ಮಾಡಿರುವಂತಹ ದುಷ್ಕøತ್ಯಗಳ ಕಾರಣದಿಂದಾಗಿ ಯಾವತ್ತೂ ಮರಣವನ್ನು ಅವರು ಆಶಿಸಲಾರರು. ಅಲ್ಲಾಹನು ಅಕ್ರಮಿಗಳನ್ನು ಚೆನ್ನಾಗಿ ಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(62,8,'62:8','ತಾವು ಹೇಳಿರಿ - ನೀವು ಯಾವ ಮರಣದಿಂದ ಓಡುತ್ತಿರುವಿರೋ ಅದು ನಿಮಗೆ ಬಂದೇ ತೀರು ವುದು. ಅನಂತರ ನಿಮ್ಮನ್ನು ದೃಶ್ಯಾದೃಶ್ಯಗಳ ಜ್ಞಾನಿಯಾದ ಅಲ್ಲಾಹನೆಡೆಗೆ ಮರಳಿಸಲಾಗುವುದು. ನೀವು ಮಾಡುತ್ತಿದ್ದ ಕರ್ಮಗಳ ಬಗ್ಗೆ ಆಗ ಅವನು ನಿಮಗೆ ತಿಳಿಸುವನು.');
INSERT INTO "kannada" VALUES(62,9,'62:9','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ಶುಕ್ರವಾರ ದಿನದ ನಮಾಝಿ ಗಾಗಿ ನಿಮ್ಮನ್ನು ಕರೆಯಲಾದರೆ ನೀವು ಅಲ್ಲಾ ಹನ ದಿಕ್ರ್‍ನ ಕಡೆಗೆ ಧಾವಿಸಿರಿ ಮತ್ತು ವ್ಯಾಪಾರ ವನ್ನು ತ್ಯಜಿಸಿರಿ. ನೀವು ಅರಿತವರಾಗಿದ್ದರೆ ಅದು ನಿಮಗೆ ಅತ್ಯುತ್ತಮವಾಗಿದೆ .');
INSERT INTO "kannada" VALUES(62,10,'62:10','ನಮಾಝ್ ನಿರ್ವಹಿಸಲಾದರೆ, ನೀವು ಭೂಮಿ ಯಲ್ಲಿ ಚದುರಿಬಿಡಿರಿ. ಅಲ್ಲಾಹನ ಅನುಗ್ರಹದಿಂದ ಅರಸಿರಿ. ಅಲ್ಲಾಹನನ್ನು ನೀವು ಧಾರಾಳ ಸ್ಮರಿಸಿರಿ. ನೀವು ಯಶಸ್ಸುಗೊಂಡವರಾಗಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(62,11,'62:11','ಅವರು ಒಂದು ವ್ಯಾಪಾರವನ್ನೋ, ಮನರಂಜನೆ ಯನ್ನೋ ಕಂಡಾಗ ಅದರೆಡೆಗೆ ಧಾವಿಸಿ ಹೋ ದರು ಮತ್ತು ನಿಮ್ಮನ್ನು ನಿಂತಲ್ಲೇ ಉಪೇಕ್ಷಿಸಿ ಬಿಟ್ಟರು. ತಾವು ಹೇಳಿರಿ, ಅಲ್ಲಾಹನ ಬಳಿ ಇರು ವುದು ವ್ಯಾಪಾರಕ್ಕಿಂತಲೂ ಮನರಂಜನೆಗಿಂ ತಲೂ ಉತ್ತಮವಾಗಿದೆ. ಅಲ್ಲಾಹನು ಅತ್ಯುತ್ತಮ ಅನ್ನದಾತನಾಗಿದ್ದಾನೆ .');
INSERT INTO "kannada" VALUES(63,1,'63:1','ಕಪಟವಿಶ್ವಾಸಿಗಳು ತಮ್ಮ ಬಳಿಗೆ ಬಂದಾಗ ``ಖಂಡಿತ ನೀವು ಅಲ್ಲಾಹನ ದೂತರೆಂದು ನಾವು ಸಾಕ್ಷ್ಯವಹಿಸುತ್ತೇವೆ’’ ಎಂದು ಹೇಳುತ್ತಾರೆ. ನೀವು ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನ ದೂತರೆಂದು ಅಲ್ಲಾಹನಿಗೆ ತಿಳಿದಿದೆ. ಆದರೆ ಕಪಟವಿಶ್ವಾಸಿಗಳು ಖಂಡಿತ ಸುಳ್ಳುಗಾರರೆಂದು ಅಲ್ಲಾಹನು ಸಾಕ್ಷ್ಯವಹಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(63,2,'63:2','ಅವರು ತಮ್ಮ ಪ್ರತಿಜ್ಞೆಗಳನ್ನು ಒಂದು ಗುರಾಣಿಯಾಗಿ ಮಾಡಿಕೊಂಡಿದ್ದಾರೆ. ಈ ರೀತಿಯಲ್ಲಿ ಅವರು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಿಂದ (ಜನರನ್ನು) ತಡೆದು ಬಿಡುತ್ತಾರೆ. ಅವರು ಮಾಡುತ್ತಿರುವ ಕೃತ್ಯಗಳು ಎಷ್ಟು ಕೆಟ್ಟವು!');
INSERT INTO "kannada" VALUES(63,3,'63:3','ಅವರು ಸತ್ಯದ ಮೇಲೆ (ನಾಲಿಗೆಯಿಂದ) ವಿಶ್ವಾಸವಿರಿಸಿ ಆ ಬಳಿಕ (ಹೃದಯದಿಂದ) ನಿರಾಕರಿಸಿದ್ದರಿಂದ ಹೀಗೆಲ್ಲ ಆಯಿತು. ಆದುದರಿಂದ ಅವರ ಹೃದಯಗಳಿಗೆ ಮುದ್ರೆ ಹಾಕಿಬಿಡಲಾಯಿತು. ಇನ್ನು ಅವರು ಸತ್ಯವನ್ನು ಗ್ರಹಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(63,4,'63:4','ಅವರನ್ನು ತಾವು ನೋಡಿದರೆ ಅವರ ಶರೀರಗಳು ತಮ್ಮನ್ನು ಅದ್ಭುತಗೊಳಿಸಬಹುದು. ಅವರಿನ್ನು ಮಾತನಾಡಿದರೆ ತಾವು ಅವರ ಮಾತನ್ನು ಕೇಳುತ್ತಲೇ ಇದ್ದು ಬಿಡುವಿರಿ. ಆದರೆ ವಾಸ್ತವದಲ್ಲಿ ಅವರು ಒರಗಿಸಿಡಲ್ಪಟ್ಟಿರುವ ಮರದ ಕೊರಡುಗಳಂತಿದ್ದಾರೆ. ಪ್ರತಿಯೊಂದು ಉಚ್ಛ ಸ್ವರವನ್ನು ತಮ್ಮ ವಿರುದ್ಧವೆಂದು ಅವರು ಭಾವಿಸುತ್ತಾರೆ . ಅವರು ಅಪ್ಪಟ ಶತ್ರುಗಳು. ಅವರ ಬಗ್ಗೆ ಎಚ್ಚರವಾಗಿರಿ. ಅವರನ್ನು ಅಲ್ಲಾಹನು ಶಪಿಸಿರುವನು. ಅವರು ಅದ್ಹೇಗೆ ಸತ್ಯದಿಂದ ವಿಮುಖರಾಗುತ್ತಿರುವರೋ!');
INSERT INTO "kannada" VALUES(63,5,'63:5','‘ಬನ್ನಿರಿ! ಅಲ್ಲಾಹನ ಸಂದೇಶವಾಹಕರು ನಿಮ ಗಾಗಿ ಕ್ಷಮಾಯಾಚನೆ ಮಾಡುತ್ತಾರೆ’ ಎಂದು ಅವರೊಡನೆ ಹೇಳಲಾದಾಗ ಅವರು ತಮ್ಮ ತಲೆಗಳನ್ನು ತಿರುವಿ ಬಿಡುತ್ತಾರೆ. ಅವರು ಬಹಳ ದರ್ಪತೊರುತ್ತಾ ವಿಮುಖರಾಗುತ್ತಿರುವು ದನ್ನು ತಾವು ಕಾಣುವಿರಿ .');
INSERT INTO "kannada" VALUES(63,6,'63:6','(ಸಂದೇಶವಾಹಕರೇ,) ತಾವು ಅವರಿಗಾಗಿ ಕ್ಷಮಾಯಾಚನೆ ಮಾಡಿದರೂ ಮಾಡದಿದ್ದರೂ ಅವರ ಮಟ್ಟಿಗೆ ಸಮಾನವಾಗಿದೆ. ಅಲ್ಲಾಹನು ಅವರನ್ನು ಎಂದಿಗೂ ಕ್ಷಮಿಸಲಾರನು. ಅಲ್ಲಾಹನು ಫಾಸಿಕರಿಗೆ (ಧಿಕ್ಕಾರಿಗಳಿಗೆ) ಖಂಡಿತ ಸನ್ಮಾರ್ಗ ನೀಡುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(63,7,'63:7','‘ಸಂದೇಶವಾಹಕರ ಸಂಗಾತಿಗಳಿಗಾಗಿ ನೀವು ಖರ್ಚು ಮಾಡದಿರಿ. ಅವರು ಚದರಿ ಹೋಗುವವರೆಗೆ ಎಂದು ಹೇಳುವವರು ಅವರೇ. ವಸ್ತುತಃ ಆಕಾಶ-ಭೂಮಿಗಳ ಭಂಡಾರದ ಒಡೆತನ ಅಲ್ಲಾಹನ ಕೈಯಲ್ಲಿದೆ. ಆದರೆ ಕಪಟವಿಶ್ವಾಸಿಗಳು ಅದನ್ನು ಗ್ರಹಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(63,8,'63:8','ನಾವು ಮದೀನಕ್ಕೆ ಹಿಂದಿರುಗಿದಾಗ ಹೆಚ್ಚು ಗೌರವಾನ್ವಿತರು, ನಿಂದ್ಯರನ್ನು (ಅಲ್ಲಿಂದ) ಹೊರ ಹಾಕುವರು ಎಂದು ಅವರು ಹೇಳುತ್ತಾರೆ. ನಿಜ ದಲ್ಲಿ ಗೌರವವು ಅಲ್ಲಾಹ್, ಅವನ ಸಂದೇಶವಾಹಕರು ಮತ್ತು ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಮಾತ್ರವೇ ಇರುವುದು. ಆದರೆ ಕಪಟವಿಶ್ವಾಸಿಗಳು ಅದನ್ನು ತಿಳಿ ಯುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(63,9,'63:9','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನಿಮ್ಮ ಸಂಪತ್ತು ಮತ್ತು ನಿಮ್ಮ ಸಂತಾನಗಳು ಅಲ್ಲಾಹನ ಸ್ಮರಣೆಯಿಂದ ನಿಮ್ಮನ್ನು ಅಲಕ್ಷ್ಯಗೊಳಿಸದಿರಲಿ. ಯಾರಾದರೂ ಹಾಗೆ ಮಾಡಿದರೆ ಅವರೇ ನಷ್ಟ ಅನುಭವಿಸುವವರು.');
INSERT INTO "kannada" VALUES(63,10,'63:10','ನಾವು ನಿಮಗೆ ನೀಡಿರುವುದರಿಂದ ನೀವು (ಧರ್ಮಕ್ಕಾಗಿ) ಖರ್ಚು ಮಾಡಿರಿ. ನಿಮ್ಮ ಪೈಕಿ ಪ್ರತಿಯೊಬ್ಬನಿಗೂ ಮರಣ ಆಸನ್ನವಾಗುವ ಮೊದಲು. ಮರಣ ಆಸನ್ನವಾದಾಗ ಅವನು ಹೇಳುವನು, ನನ್ನ ಪ್ರಭೂ, ನನಗೆ ಇನ್ನೂ ಸ್ವಲ್ಪ ಕಾಲಾವಧಿ ನೀಡ ಬಾರದೇಕೆ? ಹಾಗಾದಲ್ಲಿ ನಾನು ದಾನಧರ್ಮ ನೀಡುತ್ತಿದ್ದೆ ಹಾಗೂ ಸಜ್ಜನರಲ್ಲಿ ಒಳಪಡುತ್ತಿದ್ದೆ’.');
INSERT INTO "kannada" VALUES(63,11,'63:11','ಒಬ್ಬನ ಮರಣದ ಅವಧಿಯು ಬಂದು ಬಿಟ್ಟರೆ ಅಲ್ಲಾಹನು ಯಾರಿಗೂ ಕಾಲಾವಧಿಯನ್ನು ಮುಂದೂಡುವುದಿಲ್ಲ. ನೀವು ಮಾಡುತ್ತಿರುವುದೆಲ್ಲವನ್ನು ಅಲ್ಲಾಹನು ಸೂಕ್ಷ್ಮವಾಗಿ ತಿಳಿಯುವವನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(64,1,'64:1','ಭೂಮಿ-ಆಕಾಶಗಳಲ್ಲಿರುವ ಪ್ರತಿಯೊಂದು ವಸ್ತುವೂ ಅಲ್ಲಾಹನ ಪರಿಶುದ್ಧತೆಯನ್ನು ಪ್ರಕೀರ್ತಿಸುತ್ತಿವೆ. ಪ್ರಭುತ್ವವು ಅವನದ್ದು. ಸ್ತುತಿಯು ಅವನಿಗೇ ಮೀಸಲು. ಅವನು ಎಲ್ಲ ಕಾರ್ಯಗಳಿಗೂ ಪರಮ ಸಮರ್ಥನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(64,2,'64:2','ಅವನೇ ನಿಮ್ಮನ್ನು ಸೃಷ್ಟಿಸಿದನು. ಹಾಗಿದ್ದೂ ನಿಮ್ಮ ಪೈಕಿ ಸತ್ಯನಿಷೇಧಿ ಇದ್ದಾನೆ ಮತ್ತು ಸತ್ಯವಿಶ್ವಾಸಿ ಇದ್ದಾನೆ. ನೀವು ಮಾಡುತ್ತಿರುವುದನ್ನು ಅಲ್ಲಾಹನು ವೀಕ್ಷಿಸುತ್ತಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(64,3,'64:3','ಅವನು ಆಕಾಶಗಳನ್ನೂ ಭೂಮಿಯನ್ನೂ, ಸತ್ಯ ಪೂರ್ಣವಾಗಿ ಸೃಷ್ಟಿಸಿರುವನು ಮತ್ತು ನಿಮಗೆ ಒಂದು ರೂಪಕೊಟ್ಟನು ಹಾಗೂ ನಿಮ್ಮ ರೂಪವನ್ನು ಅತಿ ಶ್ರೇಷ್ಠಗೊಳಿಸಿದನು. ಕೊನೆಗೆ ಅವನ ಕಡೆಗೇ ನಿಮ್ಮ ನಿರ್ಗಮನ.');
INSERT INTO "kannada" VALUES(64,4,'64:4','ಭೂಮಿ ಮತ್ತು ಆಕಾಶಗಳಲ್ಲಿರುವ ಪ್ರತಿಯೊಂದು ವಸ್ತುವನ್ನೂ ಅವನು ಬಲ್ಲನು. ನೀವು ಗುಟ್ಟಾ ಗಿಸಿಡುವುದನ್ನೂ, ಪ್ರಕಟಗೊಳಿಸುವುದನ್ನೂ ಅವನು ಬಲ್ಲನು. ಅಲ್ಲಾಹನು ಹೃದಯಗಳ ಅಂತರಾಳಗಳಲ್ಲಿ ಇರುವುದನ್ನು ಅರಿಯುವ ಪರಮಜ್ಞಾನಿ ಯು.');
INSERT INTO "kannada" VALUES(64,5,'64:5','ನಿಮಗಿಂತ ಮುಂಚೆ ಸತ್ಯವನ್ನು ನಿಷೇಧಿಸಿದ ಹಾಗೂ ಕೊನೆಗೆ ತಮ್ಮ ದುಷ್ಕøತ್ಯಗಳ ಫಲವನ್ನು ಸವಿದವರ ವೃತ್ತಾಂತವು ನಿಮಗೆ ತಲಪಿಲ್ಲವೇ? (ಪರಲೋಕದಲ್ಲಿ) ಅವರಿಗೆ ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆ ಇದೆ.');
INSERT INTO "kannada" VALUES(64,6,'64:6','ಅವರ ಇಂತಹ ದುರ್ಗತಿಗೆ ಕಾರಣ (ಇದಾಗಿದೆ), ಅವರ ಬಳಿಗೆ ದೂತರು ಸುಸ್ಪಷ್ಟ ಆಧಾರ ಪ್ರಮಾಣ ಗಳೊಂದಿಗೆ ಬರುತ್ತಿದ್ದರು. ಆಗ ಅವರು, ಒಬ್ಬ ಮನುಷ್ಯ ನಮಗೆ ಸನ್ಮಾರ್ಗದರ್ಶನ ನೀಡುವುದೇ? ಎಂದು ಕೇಳಿದರು. ಹೀಗೆ ಅವರು ಸತ್ಯ ವನ್ನು ನಿಷೇಧಿಸಿದರು ಮತ್ತು ವಿಮುಖರಾದರು. ಆಗ ಅಲ್ಲಾಹನೂ ಅವರ ಬಗ್ಗೆ ನಿರ್ಲಕ್ಷ್ಯ ತಾಳಿದನು. ಅಲ್ಲಾಹನು ನಿರಪೇಕ್ಷನೂ ಸ್ವಯಂಸ್ತುತ್ಯರ್ಹನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(64,7,'64:7','ನಮ್ಮನ್ನು ಪುನರುಜ್ಜೀವನಗೊಳಿಸಲಾಗದೆಂದು ಸತ್ಯನಿಷೇಧಿಗಳು ವಾದಿಸಿದರು. (ಪ್ರವಾದಿಯರೇ,) ಹೇಳಿರಿ - ಹಾಗಲ್ಲ, ನನ್ನ ಪ್ರಭುವಿನಾಣೆ, ನೀವು ಖಂಡಿತ ಪುನರುಜ್ಜೀವನಗೊಳಿಸಿ ಎಬ್ಬಿಸಲ್ಪಡುವಿರಿ. ತರುವಾಯ ನೀವು ಮಾಡಿದ ಕರ್ಮದ ಬಗ್ಗೆ ನಿಮಗೆ ಖಂಡಿತ ವಿವರಿಸಿ ಕೊಡಲಾಗುವುದು. ಹೀಗೆ ಮಾಡುವುದು ಅಲ್ಲಾಹನ ಮಟ್ಟಿಗೆ ಬಹಳ ಸುಲಭವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(64,8,'64:8','ಆದುದರಿಂದ ಅಲ್ಲಾಹನ ಮೇಲೂ ಅವನ ದೂತರ ಮೇಲೂ ನಾವು ಅವತೀರ್ಣಗೊಳಿಸಿರುವ ಪ್ರಕಾಶದ (ಖುರ್‍ಆನಿನ) ಮೇಲೂ ವಿಶ್ವಾಸವಿರಿಸಿರಿ . ನೀವು ಮಾಡುತ್ತಿರುವುದನ್ನು ಅಲ್ಲಾಹನು ಸೂಕ್ಷ್ಮವಾಗಿ ಅರಿತಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(64,9,'64:9','ಆ ಸಮ್ಮೇಳನದ ದಿನಕ್ಕೆ ಅವನು ನಿಮ್ಮೆಲ್ಲರನ್ನೂ ಒಟ್ಟುಗೂಡಿಸುವ ದಿನ (ಸ್ಮರಣೀಯವಾಗಿದೆ). ಅದು ನಷ್ಟವು ಸ್ಪಷ್ಟವಾಗುವ ದಿನವಾಗಿರುವುದು . ಯಾರು ಅಲ್ಲಾಹನ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸಿ ಸತ್ಕ ರ್ಮವೆಸಗುತ್ತಾನೋ ಅವನ ಪಾಪಗಳನ್ನು ಅಲ್ಲಾಹು ಅಳಿಸಿ ಬಿಡುವನು ಮತ್ತು ಅವನನ್ನು ತಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆಗಳು ಹರಿಯುತ್ತಿರುವಂತಹ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ ಪ್ರವೇಶಗೊಳಿಸುವನು. ಅವರು ಅವುಗಳಲ್ಲಿ ಸದಾಕಾಲ ವಾಸಿಸುವರು. ಅದುವೇ ಮಹಾ ಯಶಸ್ಸು.');
INSERT INTO "kannada" VALUES(64,10,'64:10','ಅವಿಶ್ವಾಸ ತಾಳಿದ ಮತ್ತು ನಮ್ಮ ನಿದರ್ಶನಗಳನ್ನು ಸುಳ್ಳಾಗಿಸಿದವರು ನರಕವಾಸಿಗಳಾಗಿರುವರು. ಅದರಲ್ಲಿ ಅವರು ಸದಾಕಾಲ ವಾಸಿಸುವರು ಅದು ಅತ್ಯಂತ ನಿಕೃಷ್ಟ ವಾಸಸ್ಥಾನವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(64,11,'64:11','ಯಾವ ದುರಂತವೂ ಅಲ್ಲಾಹನ ಅಪ್ಪಣೆಯಿಲ್ಲದೆ ಸಂಭವಿಸುವುದಿಲ್ಲ. ಯಾವನಾದರೂ ಅಲ್ಲಾಹನ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸಿದರೆ ಅವನ ಹೃದಯಕ್ಕೆ ಅಲ್ಲಾಹನು ಮಾರ್ಗದರ್ಶನ ನೀಡುತ್ತಾನೆ. ಅಲ್ಲಾಹನು ಸಕಲ ವಸ್ತುವಿನ ಬಗ್ಗೆ ಪರಮಜ್ಞಾನಿ.');
INSERT INTO "kannada" VALUES(64,12,'64:12','ನೀವು ಅಲ್ಲಾಹನನ್ನು ಅನುಸರಿಸಿ ಮತ್ತು ದೇವ ದೂತರನ್ನೂ ಅನುಸರಿಸಿರಿ. ಇನ್ನು ನೀವು ಅನು ಸರಣೆಯಿಂದ ವಿಮುಖರಾದರೆ, ನಮ್ಮ ದೂತರ ಮೇಲೆ ಸುಸ್ಪಷ್ಟವಾಗಿ ಸತ್ಯವನ್ನು ತಲುಪಿಸುವ ಹೊಣೆ ಮಾತ್ರವೇ ಇರುವುದು.');
INSERT INTO "kannada" VALUES(64,13,'64:13','ಅಲ್ಲಾಹು - ಅವನ ಹೊರತು ಬೇರೆ ಆರಾಧ್ಯನಿಲ್ಲ, ಆದುದರಿಂದ ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಅಲ್ಲಾಹನ ಮೇಲೆಯೇ ಭರವಸೆಯನ್ನಿಟ್ಟುಕೊಳ್ಳಲಿ.');
INSERT INTO "kannada" VALUES(64,14,'64:14','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನಿಮ್ಮ ಪತ್ನಿಯರಲ್ಲಿ ಮತ್ತು ನಿಮ್ಮ ಮಕ್ಕಳಲ್ಲಿ ನಿಮಗೆ ಶತ್ರುಗಳಿದ್ದಾರೆ. ಆದುದರಿಂದ ಅವರ ಬಗ್ಗೆ ಜಾಗರೂಕರಾಗಿರಿ . ನೀವು ಮಾಪಿ ಮಾಡಿದರೆ, ರಿಯಾಯಿತಿ ತೋರಿದರೆ ಹಾಗೂ ಕ್ಷಮಿಸಿ ಬಿಡುತ್ತೀರೆಂದಾದರೆ ಅಲ್ಲಾಹನು ಮಹಾ ಕ್ಷಮಾಶೀಲನೂ, ದಯಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(64,15,'64:15','ನಿಮ್ಮ ಸಂಪತ್ತು ಮತ್ತು ಸಂತತಿಗಳು ಒಂದು ಪೀಡೆಯಾಗಿದೆ. ಘನವೆತ್ತ ಪ್ರತಿಫಲವು ಅಲ್ಲಾಹನ ಬಳಿಯಲ್ಲೇ ಇದೆ.');
INSERT INTO "kannada" VALUES(64,16,'64:16','ಆದುದರಿಂದ ನಿಮ್ಮಿಂದ ಸಾಧ್ಯವಿರುವಷ್ಟು ನೀವು ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ. ನೀವು ಆಲಿಸಿರಿ, ಅನುಸರಿಸಿರಿ ಮತ್ತು ನಿಮಗೆ ಗುಣಕರವಾಗುವ ರೀತಿಯಲ್ಲಿ ಸಂಪತ್ತನ್ನು ಖರ್ಚು ಮಾಡಿರಿ. ಯಾರು ತಮ್ಮ ಸ್ವಂತ ಲೋಭದಿಂದ ಮುಕ್ತರಾದರೋ ಅವರು ಮಾತ್ರವೇ ವಿಜಯಿಗಳು.');
INSERT INTO "kannada" VALUES(64,17,'64:17','ನೀವು ಅಲ್ಲಾಹನಿಗೆ ಉತ್ತಮ ಸಾಲ ನೀಡಿದರೆ ಅವನು ನಿಮಗೆ ಅನೇಕ ಪಟ್ಟು ಹೆಚ್ಚಿಸಿಕೊಡುವನು ಮತ್ತು ನಿಮಗೆ ಕ್ಷಮಿಸುವನು. ಅಲ್ಲಾಹನು ಮಹಾ ಕೃತಜ್ಞನೂ ಸಹನಶೀಲನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(64,18,'64:18','ದೃಶ್ಯ - ಅದೃಶ್ಯಗಳ ಜ್ಞಾನಿ. ಪ್ರಬಲನೂ ಯುಕ್ತಿವಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(65,1,'65:1','ಓ ಪ್ರವಾದಿವರ್ಯರೇ, ನೀವು (ವಿಶ್ವಾಸಿಗಳು) ಸ್ತ್ರೀಯರನ್ನು (ಪತ್ನಿಯರನ್ನು) ವಿಚ್ಛೇದಿಸುವುದಾದರೆ ಅವರಿಗೆ ಅವರ ಇದ್ದಃ ಕಾಲದಲ್ಲಿ ತಲಾಖ್ ಕೊಡಿರಿ. ಇದ್ದಃದ ಕಾಲಾವಧಿಯನ್ನು ಸರಿಯಾಗಿ ಎಣಿಸಿರಿ . ನಿಮ್ಮ ಪ್ರಭು ವಾದ ಅಲ್ಲಾಹನನ್ನು ನೀವು ಭಯಪಡಿರಿ. ಅವರು ಸ್ಪಷ್ಟವಾದ ನೀಚ ವೃತ್ತಿಯನ್ನು (ವ್ಯಭಿಚಾರವನ್ನು) ಮಾಡದ ಹೊರತು ನೀವು ಅವರನ್ನು ಅವರ ಮನೆಗಳಿಂದ ಹೊರಹಾಕ ಬೇಡಿರಿ ಮತ್ತು (ಇದ್ದಃ ಮುಗಿಯುವವರೆಗೆ) ಅವರು ಸ್ವತಃ ಹೊರಟು ಹೋಗದಿರಲಿ . ಇವು ಅಲ್ಲಾಹನ ಮೇರೆಗಳು. ಯಾರಾದರೂ ಅಲ್ಲಾಹನ ಮೇರೆಗಳನ್ನು ಮೀರಿದರೆ ಅವನು ತನ್ನನ್ನೇ ಅಕ್ರಮಿಸಿಕೊಂಡನು. ಪ್ರಾಯಶಃ ಇದರ (ತಲಾಖಿನ) ಬಳಿಕ ಅಲ್ಲಾಹನು ಯಾವುದಾದರೂ ಹೊಸ ಕಾರ್ಯವನ್ನು ಉಂಟು ಮಾಡಲೂಬಹುದೆಂದು ತಮಗರಿಯದು.');
INSERT INTO "kannada" VALUES(65,2,'65:2','ಇನ್ನು ಅವರು ತಮ್ಮ ಕಾಲಾವಧಿಯನ್ನು ತಲು ಪಿದರೆ, (ಇದ್ದಃ ಮುಗಿದರೆ) ಅವರನ್ನು ಉತ್ತಮ ರೀತಿಯಲ್ಲಿ ಇರಿಸಿಕೊಳ್ಳಿರಿ ಅಥವಾ ಉತ್ತಮ ರೀತಿಯಲ್ಲಿ ಬೇರ್ಪಡಿಸಿರಿ. ನಿಮ್ಮ ಪೈಕಿ ನ್ಯಾಯ ಶೀಲರಾದ ಇಬ್ಬರನ್ನು ಸಾಕ್ಷಿ ನಿಲ್ಲಿಸಿರಿ. ಸಾಕ್ಷ್ಯ ವನ್ನು ಅಲ್ಲಾಹನಿಗಾಗಿಯೇ ನೆಲೆಗೊಳಿಸಿರಿ. ಇದು ಅಲ್ಲಾಹನ ಮತ್ತು ಪರಲೋಕದ ಮೇಲೆ ವಿಶ್ವಾಸ ವಿರಿಸುವ ಪ್ರತಿಯೊಬ್ಬನಿಗೆ ಉಪದೇಶಿಸಲಾಗುವ ವಿಷಯ. ಯಾವನು ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡುತ್ತ ಧರ್ಮನಿಷ್ಠರಾಗಿ ಬಾಳುತ್ತಾರೋ ಆತನಿಗೆ ಅಲ್ಲಾಹನು (ಸಂಕಷ್ಟಗಳಿಂದ ಪಾರಾಗುವ) ದಾರಿಯನ್ನು ತೆರೆದುಕೊಡುವನು .');
INSERT INTO "kannada" VALUES(65,3,'65:3','ಮತ್ತು ಅವನು ಊಹಿಸದ ಕಡೆಯಿಂದ ಅವನಿಗೆ ಜೀವನಾಧಾರ ನೀಡುವನು. ಯಾವನು, ಅಲ್ಲಾ ಹನ ಮೇಲೆ ಭಾರವನ್ನು ಅರ್ಪಿಸುತ್ತಾನೋ ಅವನಿಗೆ ಅಲ್ಲಾಹನೇ ಸಾಕು. ಅಲ್ಲಾಹನು ತನ್ನ ಕಾರ್ಯವನ್ನು ಗುರಿ ಮುಟ್ಟಿಸಬಲ್ಲವನು. ಅಲ್ಲಾಹನು ಎಲ್ಲ ವಸ್ತುವಿಗೂ ವಿಧಿಯನ್ನು ನಿರ್ಣಯಿಸಿರುವನು.');
INSERT INTO "kannada" VALUES(65,4,'65:4','ನಿಮ್ಮ ಸ್ತ್ರೀಯರ ಪೈಕಿ ಋತುಸ್ರಾವದ ಬಗ್ಗೆ ನಿರಾಶರಾದವರ (ಅವರ ಇದ್ದತ್‍ನ) ಕುರಿತು ನಿಮಗೇನಾದರೂ ಸಂಶಯ ಬಂದರೆ, ಅವರ ಇದ್ದತಿನ ಅವಧಿಯು ಮೂರು ತಿಂಗಳಾಗಿದೆ. ಇನ್ನೂ ಋತುಮತಿಯರಾಗದವರಿಗೂ ಇದೇ ನಿಯಮವಿದೆ. ಮತ್ತು ಗರ್ಭಿಣಿಯರ (ಇದ್ದತಿನ) ಅವಧಿಯು ಅವರ ಹೆರಿಗೆಯ ತನಕವಿದೆ . ಅಲ್ಲಾಹನ ಆದೇಶಗಳ ಬಗ್ಗೆ ಕಟ್ಟೆಚ್ಚರ ಪಾಲಿಸುವವರಿಗೆ ಅಲ್ಲಾಹನು ಸರಳತೆಯನ್ನು ದಯಪಾಲಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(65,5,'65:5','ಇದು ಅಲ್ಲಾಹನು ನಿಮ್ಮ ಕಡೆಗೆ ಅವತೀರ್ಣ ಗೊಳಿಸಿರುವ ಅವನ ಅಪ್ಪಣೆಯಾಗಿದೆ. ತನ್ನನ್ನು ಭಯಪಟ್ಟು ಜೀವಿಸುವವನ ದೋಷಗಳನ್ನು ಅಲ್ಲಾಹನು ಅವನಿಂದ ಹೋಗಲಾಡಿಸುವನು ಮತ್ತು ಅವನ ಪ್ರತಿಫಲವನ್ನು ಗಣನೀಯಗೊಳಿಸುವನು.');
INSERT INTO "kannada" VALUES(65,6,'65:6','ಅವರನ್ನು, (ಇದ್ದತ್‍ನ ಸಮಯದಲ್ಲಿ) ನಿಮ್ಮ ಅನು ಕೂಲಕ್ಕೆ ತಕ್ಕುದಾದ ನೀವಿರುವ ನಿವಾಸದಲ್ಲೇ ವಾಸಗೊಳಿಸಿರಿ ಮತ್ತು ಅವರನ್ನು ಇಕ್ಕಟ್ಟಿನಲ್ಲಿ ಸಿಲುಕಿಸುವ ಸಲುವಾಗಿ ಅವರಿಗೆ ತೊಂದರೆ ಕೊಡದಿರಿ. ಅವರು ಗರ್ಭಿಣಿಯರಾಗಿದ್ದರೆ, ಅವರ ಹೆರಿಗೆಯಾಗುವವರೆಗೂ ಅವರಿಗೆ ಖರ್ಚು ಕೊಡಿರಿ. ಅನಂತರ ಅವರು ನಿಮಗೋಸ್ಕರ ಮಗುವಿಗೆ ಸ್ತನಪಾನ ಮಾಡಿಸಿದರೆ, ಅವರಿಗೆ ಅವರ ಪ್ರತಿಫಲ ವನ್ನು ಕೊಡಿರಿ ಮತ್ತು ನೀವು ಪರಸ್ಪರ ಶಿಷ್ಟಾಚಾರ ಪ್ರಕಾರ ಮಾತುಕತೆ ನಡೆಸಿ (ಸಮಸ್ಯೆಯನ್ನು ಬಗೆಹರಿಸಿ)ರಿ. ಆದರೆ ನಿಮಗೆ ಕಷ್ಟವೆನಿಸುವುದಾದರೆ ಅವನಿಗಾಗಿ (ಶಿಶುವಿಗೆ) ಬೇರೊಬ್ಬ ಸ್ತ್ರೀ ಮೊಲೆಯುಣಿಸಲಿ.');
INSERT INTO "kannada" VALUES(65,7,'65:7','ಸ್ಥಿತಿವಂತನು ತನ್ನ ಸ್ಥಿತಿಗನುಸಾರ ಖರ್ಚಿಗೆ ಕೊಡಲಿ. ಪರಿಮಿತಿ ಜೀವನಾಧಾರ ನೀಡಲ್ಪಟ್ಟಿರುವವನು ಅಲ್ಲಾಹನು ಆತನಿಗೆ ನೀಡಿರುವುದರಿಂದ ಖರ್ಚು ಮಾಡಲಿ. ಅಲ್ಲಾಹನು ಯಾರಿಗೂ ತಾನು ಒದಗಿಸಿದುದಕ್ಕಿಂತ ಹೆಚ್ಚಿನ ಬಾದ್ಯತೆಯನ್ನು ಹೇರುವುದಿಲ್ಲ. ಪ್ರಾರಬ್ಧ ನೀಡಿದ ನಂತರ ಅಲ್ಲಾಹನು ಅನುಕೂಲವನ್ನು ಒದಗಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(65,8,'65:8','ಅದೆಷ್ಟೋ ನಾಡಿನವರು ತಮ್ಮ ಪ್ರಭು ಮತ್ತು ಅವನ ದೂತರುಗಳ ಆಜ್ಞೋಲ್ಲಂಘನೆ ಮಾಡಿದರು? ಆಗ ನಾವು ಅವರನ್ನು ಉಗ್ರ ವಿಚಾರಣೆಗೆ ಒಳಪಡಿಸಿದೆವು ಮತ್ತು ಅವರನ್ನು ನಾವು ಹೀನಾಯವಾಗಿ ಶಿಕ್ಷಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(65,9,'65:9','ಹಾಗೆ ಅವರು ತಮ್ಮ ಕರ್ಮಫಲದ ಸವಿಯನ್ನುಂಡರು. ಅದರ ಅಂತಿಮ ಗತಿಯು ನಷ್ಟವೇ ಆಗಿತ್ತು.');
INSERT INTO "kannada" VALUES(65,10,'65:10','ಅಲ್ಲಾಹನು ಅವರಿಗೆ ಘೋರ ಶಿಕ್ಷೆಯನ್ನು ಸಿದ್ಧಪಡಿಸಿಟ್ಟಿದ್ದಾನೆ. ಆದುದರಿಂದ ಸತ್ಯ ವಿಶ್ವಾಸಿ ಗಳಾಗಿರುವ ಬುದ್ದಿಜೀವಿಗಳೇ, ನೀವು ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡಿರಿ. ನಿಶ್ಚಯವಾಗಿಯೂ ಅಲ್ಲಾಹನು ನಿಮ್ಮ ಕಡೆಗೆ ಒಂದು ಉದ್ಭೋಧವನ್ನು ಅವತೀರ್ಣಗೊಳಿಸಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(65,11,'65:11','ಅಥವಾ ಅಲ್ಲಾಹನ ಸುಸ್ಪಷ್ಟ ದೃಷ್ಟಾಂತಗಳನ್ನು ನಿಮಗೆ ಓದಿ ಕೇಳಿಸುವ ಒಬ್ಬ ಸಂದೇಶವಾಹಕನನ್ನು ನಿಮ್ಮ ಕಡೆಗೆ (ಅವನು ನಿಯೋಗಿಸಿರುವನು). ವಿಶ್ವಾಸವಿರಿಸಿದ ಮತ್ತು ಸತ್ಕರ್ಮವೆಸಗಿದ ಜನರನ್ನು ಅಂಧಕಾರಗಳಿಂದ ಪ್ರಕಾಶದೆಡೆಗೆ ಒಯ್ಯಲಿಕ್ಕಾಗಿ. ಯಾವನು ಅಲ್ಲಾಹನ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸುತ್ತಾನೋ ಹಾಗೂ ಸತ್ಕರ್ಮ ಮಾಡುತ್ತಾನೋ ಆ ವ್ಯಕ್ತಿಯನ್ನು ತಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆಗಳು ಹರಿಯುತ್ತಿರುವ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ ಅಲ್ಲಾಹನು ಪ್ರವೇಶಗೊಳಿಸುವನು. ಅವರು ಅದರಲ್ಲಿ ಸದಾಕಾಲ ವಾಸಿಸುವರು. ಇಂತಹವನಿಗೆ ಅಲ್ಲಾಹನು ಅತ್ಯುತ್ತಮ');
INSERT INTO "kannada" VALUES(65,12,'65:12','ಅಲ್ಲಾಹನೇ ಸಪ್ತಗಗನಗಳನ್ನು ಮತ್ತು ಭೂಮಿಯಿಂದ ಅವುಗಳಿಗೆ ಸಮಾನವಾದುದನ್ನು ಸೃಷ್ಟಿಸಿದವನು . ಅವುಗಳ ನಡುವೆ ಅವನ ಆದೇ ಶಗಳು ಇಳಿಯುತ್ತವೆ. ಅಲ್ಲಾಹನು ಎಲ್ಲದಕ್ಕೂ ಸಾಮಥ್ರ್ಯವುಳ್ಳವನು. ಅಲ್ಲಾಹನ ಜ್ಞಾನವು ಎಲ್ಲ ವಿಷಯಗಳಿಗೂ ವ್ಯಾಪಿಸಿಕೊಂಡಿದೆ ಎಂಬುದನ್ನು ನೀವು ತಿಳಿದುಕೊಳ್ಳುವಂತಾಗಲು.');
INSERT INTO "kannada" VALUES(66,1,'66:1','ಓ ಪ್ರವಾದಿವರ್ಯರೇ, ತಾವು ತಮ್ಮ ಪತ್ನಿಯರ ಮೆಚ್ಚುಗೆಯನ್ನು ಬಯಸಿಕೊಂಡು ಅಲ್ಲಾಹನು ತಮಗೆ ಹಲಾಲ್ (ಧರ್ಮಸಮ್ಮತ)ಗೊಳಿಸಿರುವ ವಸ್ತುವನ್ನು ತಾವೇಕೆ ಹರಾಮ್ (ನಿಷಿದ್ಧ) ಗೊಳಿಸುತ್ತೀರಿ? ಅಲ್ಲಾಹನು ಕ್ಷಮಾದಾನಿಯೂ ಕೃಪಾನಿಧಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(66,2,'66:2','ಅಲ್ಲಾಹನು ನಿಮ್ಮ ಶಪಥಗಳ ಪರಿಹಾರ ನಿಯಮವನ್ನು ನಿಶ್ಚಯಿಸಿರುತ್ತಾನೆ. ಅಲ್ಲಾಹನೇ ತಮ್ಮ ಯಜಮಾನನು. ಅವನು ಸರ್ವಜ್ಞನೂ ಯುಕ್ತಿವಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(66,3,'66:3','ಪ್ರವಾದಿಯು ತಮ್ಮ ಓರ್ವ ಪತ್ನಿಯೊಡನೆ ಒಂದು ಸಮಾಚಾರವನ್ನು ರಹಸ್ಯವಾಗಿ ಹೇಳಿದ ಸಂದರ್ಭ (ವನ್ನು ಸ್ಮರಿಸಿರಿ). ತರುವಾಯ ಆ ಪತ್ನಿಯು ಆ ರಹಸ್ಯವನ್ನು (ಬೇರೊಬ್ಬರಲ್ಲಿ) ಬಯಲುಗೊಳಿಸಿ ದಾಗ ಮತ್ತು ಅಲ್ಲಾಹನು ಪ್ರವಾದಿಗೆ ಈ ಸುದ್ದಿಯನ್ನು ತಿಳಿಸಿದಾಗ, ಅವರು ಅದರ ಸ್ಪಲ್ಪಾಂಶವನ್ನು ತಿಳಿಸಿದರು ಮತ್ತು ಸ್ವಲ್ಪಾಂಶವನ್ನು ಕಡೆಗಣಿಸಿದರು. ಹಾಗೆ ಪ್ರವಾದಿಯವರು ಆ ಪತ್ನಿಗೆ ಆ ಸುದ್ದಿಯನ್ನು ತಿಳಿಸಿದಾಗ ಆ ಪತ್ನಿ; ``ತಮಗೆ ಈ ಸುದ್ದಿ ತಿಳಿಸಿದವರು ಯಾರು?’’ ಎಂದು ಕೇಳಿದರು. ಪ್ರವಾದಿಯವರು, ``ನನಗೆ ಸರ್ವಜ್ಞನೂ ಸೂಕ್ಷ್ಮಜ್ಞಾನಿಯೂ ಆಗಿರುವ ಅಲ್ಲಾಹನು ತಿಳಿಸಿ ಕೊಟ್ಟನು’ ಎಂದರು .');
INSERT INTO "kannada" VALUES(66,4,'66:4','ನೀವಿಬ್ಬರೂ ಅಲ್ಲಾಹನೆಡೆಗೆ ಪಶ್ಚಾತ್ತಾಪಪಟ್ಟು ಮರಳುತ್ತೀರಾದರೆ (ಉತ್ತಮ). ಏಕೆಂದರೆ, ನಿಮ್ಮಿ ಬ್ಬರ ಹೃದಯಗಳು (ಕೆಡುಕಿಗೆ) ವಾಲಿಬಿಟ್ಟಿವೆ. ಇನ್ನು ನೀವಿಬ್ಬರೂ ಪ್ರವಾದಿಯ ವಿರುದ್ಧ ಪರಸ್ಪರ ಸಹಕರಿಸಿದರೆ, (ನೆನಪಿರಲಿ) ಅಲ್ಲಾಹನು ಅವರ ಸಂರಕ್ಷನಾಗಿದ್ದಾನೆ ಮತ್ತು ಜಿಬ್‍ರೀಲರು, ಎಲ್ಲ ಸಜ್ಜನ ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಮತ್ತು ತದನಂತರ ಎಲ್ಲ ದೇವಚರರೂ (ಪ್ರವಾದಿಯ) ಸಹಾಯಿಗಳಾಗಿರುವರು .');
INSERT INTO "kannada" VALUES(66,5,'66:5','(ಪ್ರವಾದಿ ಪತ್ನಿಯರೇ!) ಪ್ರವಾದಿಯು ನಿಮ್ಮನ್ನು ತಲಾಖ್ ಹೇಳಿದರೆ ಅಲ್ಲಾಹನು ಅವರಿಗೆ ನಿಮ್ಮ ಬದಲಿಗೆ ನಿಮಗಿಂತ ಉತ್ತಮರೂ ನೈಜ ಮುಸ್ಲಿಮರೂ ಸತ್ಯವಿಶ್ವಾಸವುಳ್ಳವರೂ, ಭಯಭಕ್ತಿಯುಳ್ಳವರೂ, ಪಶ್ಚಾತ್ತಾಪಪಡುವವರೂ ಆರಾಧನಾಶೀಲೆಯರೂ ಉಪವಾಸವೃತ ಆಚರಿಸುವವರೂ ಆದ - ವಿಧವೆಯರೂ, ಕನ್ಯೆಯರೂ ಆದ ಪತ್ನಿಯರನ್ನು ದಯಪಾಲಿಸಬಹುದು.');
INSERT INTO "kannada" VALUES(66,6,'66:6','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ಸ್ವಶರೀರಗಳನ್ನೂ, ನಿಮ್ಮ ಕುಟುಂಬದವರನ್ನೂ ನರಕಾಗ್ನಿಯಿಂದ ರಕ್ಷಿಸಿಕೊಳ್ಳಿರಿ . ಮನುಷ್ಯರು ಮತ್ತು ಕಲ್ಲುಗಳು ಅದರ ಇಂಧನವಾಗಿರುವುದು . ಅದರ ಮೇಲೆ ಅತ್ಯಂತ ಕಠೋರ ಮತ್ತು ಬಲಾಡ್ಯರಾದ ದೇವಚರರು ನಿಯುಕ್ತರಾಗಿರುವರು. ಅವರೆಂದೂ ಅಲ್ಲಾಹನ ಅಜ್ಞೋಲ್ಲಂಘನೆ ಮಾಡುವುದಿಲ್ಲ. ಅವರಿಗೆ ನೀಡಲಾಗುವ ಪ್ರತಿಯೊಂದು ಅಪ್ಪಣೆಯನ್ನೂ ಅವರು ಪಾಲಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(66,7,'66:7','ಸತ್ಯನಿಷೇಧಿಗಳೇ, ನೀವಿಂದು ನೆಪಗಳನ್ನು ಹೇಳ ಬೇಡಿರಿ. ನೀವು ಮಾಡುತ್ತಿದ್ದ ಕರ್ಮಗಳಿಗೆ ತಕ್ಕುದಾದ ಪ್ರತಿಫಲವನ್ನೇ ನಿಮಗೆ ನೀಡಲಾಗುತ್ತಿದೆ. (ಎಂದು ಸತ್ಯನಿಷೇಧಿಗಳೊಂದಿಗೆ ಹೇಳಲಾಗುವುದು.)');
INSERT INTO "kannada" VALUES(66,8,'66:8','ಸತ್ಯವಿಶ್ವಾಸಿಗಳೇ, ನೀವು ಅಲ್ಲಾಹನೆಡೆಗೆ ನಿಷ್ಕಳಂಕವಾದ ಪಶ್ಚಾತ್ತಾಪ ಕೈಗೊಂಡು ಮರಳಿರಿ. ನಿಮ್ಮ ಪ್ರಭು ನಿಮ್ಮ ದೋಷಗಳನ್ನು ಅಳಿಸಬಹುದು ಮತ್ತು ತಳಭಾಗದಲ್ಲಿ ಕಾಲುವೆಗಳು ಹರಿಯುತ್ತಿರುವ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ ನಿಮ್ಮನ್ನು ಪ್ರವೇಶಗೊಳಿಸಲೂಬಹುದು. ಅದು ಅಲ್ಲಾಹನು ತನ್ನ ಪ್ರವಾದಿಯನ್ನಾಗಲಿ, ಅವರೊಂದಿಗೆ ವಿಶ್ವಾಸವಿರಿಸಿದ ಜನರನ್ನಾಗಲಿ ಅವಮಾನಗೊಳಿಸದಂತಹ ದಿನವಾಗಿರುವುದು. ಅವರ ಪ್ರಕಾಶವು ಅವರ ಮುಂದಿನಿಂದಲೂ ಅವರ ಬಲಬದಿಯಲ್ಲೂ ಚಲಿಸುತ್ತಿರುವುದು. ಅವರು, ನಮ್ಮ ಪ್ರಭು, ನಮ್ಮ ಪ್ರಕಾಶವನ್ನು ನಮಗೆ ನೀನು ಪರಿಪೂರ್ಣಗೊಳಿಸಿಕೊಡು ಮತ್ತು ನಮ್ಮನ್ನು ಕ್ಷಮಿಸು, ನೀನು ಸಕಲ ವಸ್ತುವಿನ ಮೇಲೆ ಸರ್ವ ಸಮರ್ಥನು ಎಂದು ಹೇಳುತ್ತಿರುವರು.');
INSERT INTO "kannada" VALUES(66,9,'66:9','ಓ ಪ್ರವಾದಿಯರೇ, ಸತ್ಯನಿಷೇಧಿಗಳೊಂದಿಗೂ ಕಪಟವಿಶ್ವಾಸಿಗಳೊಂದಿಗೂ ಸಮರ ಹೂಡಿರಿ. ಅವರೊಡನೆ ಕಠಿಣವಾಗಿ ವರ್ತಿಸಿರಿ. ನರಕವೇ ಅವರ ನಿವಾಸವಾಗಿದೆ. ಅದು ಅತ್ಯಂತ ಕೆಟ್ಟ ನಿರ್ಗಮನ ಕೇಂದ್ರವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(66,10,'66:10','ಅಲ್ಲಾಹನು ನೂಹರ ಪತ್ನಿಯನ್ನು ಮತ್ತು ಲೂಥರ ಪತ್ನಿಯನ್ನು ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಉದಾ ಹರಣೆಯಾಗಿ ಮುಂದಿಡುತ್ತಾನೆ. ಆ ಇಬ್ಬರು ಸ್ತ್ರೀಯರು ನಮ್ಮ ದಾಸರ ಪೈಕಿ ಇಬ್ಬರು ಸಜ್ಜನ ದಾಸರ ದಾಂಪತ್ಯ ಬಂಧನದಲ್ಲಿದ್ದರು. ಅವರೀರ್ವರೂ ತಮ್ಮ ಪತಿಯರನ್ನು ವಂಚಿಸಿದರ . ಆ ಇಬ್ಬರು ಸ್ತ್ರೀಯರನ್ನೂ ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆಯಿಂದ ಪಾರು ಮಾಡಲು ಆ ಪ್ರವಾದಿಗಳಿಗೆ ಸಾಧ್ಯವಾಗಲಿಲ್ಲ. ‘ಅಗ್ನಿಯಲ್ಲಿ ಬೀಳುವವರ ಜೊತೆಗೆ ನೀವಿಬ್ಬರೂ ಬೀಳಿರಿ’ ಎಂದು ಹೇಳಲಾಯಿತು.');
INSERT INTO "kannada" VALUES(66,11,'66:11','ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಉದಾಹರಣೆಯಾಗಿ ಫಿರ್ ಔನನ ಪತ್ನಿಯನ್ನು ಅಲ್ಲಾಹನು ಮುಂದಿಡು ತ್ತಾನೆ. ಆಕೆ ಹೇಳಿದ ಸಂದರ್ಭ; ನನ್ನ ಪ್ರಭೂ, ನನಗಾಗಿ ನಿನ್ನ ಬಳಿ ಸ್ವರ್ಗದಲ್ಲೊಂದು ಭವನವನ್ನು ನಿರ್ಮಿಸಿಕೊಡು ಮತ್ತು ನನ್ನನ್ನು ಫಿರ್ ಔನನಿಂದಲೂ ಅವನ ಕರ್ಮಗಳಿಂದಲೂ ರಕ್ಷಿಸು ಮತ್ತು ಅಕ್ರಮಿ ಜನಾಂಗದಿಂದ ನನ್ನನ್ನು ಪಾರುಗೊಳಿಸು’.');
INSERT INTO "kannada" VALUES(66,12,'66:12','ಅವನು ಇಮ್ರಾನರ ಪುತ್ರಿ ಮರ್ಯಮರನ್ನೂ (ಸತ್ಯವಿಶ್ವಾಸಿಗಳಿಗೆ ಉದಾಹರಣೆಯಾಗಿ) ಮುಂದಿಡುತ್ತಾನೆ. ಅವರು ತಮ್ಮ ಶೀಲವನ್ನು ಕಾಪಾಡಿಕೊಂಡಿದ್ದರು. ಆಗ ನಾವು ಅವರೊಳಗೆ ನಮ್ಮ ಆತ್ಮದಿಂದ ಊದಿದೆವು. ಆಕೆ ತಮ್ಮ ಪ್ರಭುವಿನ ವಚನಗಳಲ್ಲೂ, ಅವನ ಗ್ರಂಥಗಳಲ್ಲೂ ವಿಶ್ವಾಸವಿರಿಸಿದ್ದರು. ಮತ್ತು ಆಕೆ ಭಕ್ತಿಯುಳ್ಳವರಲ್ಲಿ ಒಳಪಟ್ಟವರಾದರು .');
INSERT INTO "kannada" VALUES(67,1,'67:1','ಆಧಿಪತ್ಯ ಯಾವನ ಕೈವಶದಲ್ಲಿದೆಯೋ ಅವನು ಪರಮ ಪರಿಶುದ್ಧನು. ಅವನು ಸಕಲ ವಸ್ತುವಿನ ಮೇಲೆ ಸರ್ವ ಸಮರ್ಥನು .');
INSERT INTO "kannada" VALUES(67,2,'67:2','ನಿಮ್ಮ ಪೈಕಿ ಯಾರು ಹೆಚ್ಚು ಚೆನ್ನಾಗಿ ಕರ್ಮಾಚರಣೆ ಮಾಡುತ್ತಾನೆ ಎಂದು ಪರೀಕ್ಷಿಸಿ ನೋಡಲಿಕ್ಕಾಗಿ ಅವನು ಮರಣವನ್ನೂ ಜೀವನವನ್ನೂ ಸೃಷ್ಟಿಸಿದವನಾಗಿದ್ದಾನೆ. ಅವನು ಅಜೇಯನೂ ಕ್ಷಮಾಶೀಲನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(67,3,'67:3','ಅಂತಸ್ತುಗಳಾಗಿ ಸಪ್ತ ಗಗನಗಳನ್ನು ಉಂಟು ಮಾಡಿದವನು ಅವನೇ. ಕರುಣಾಮಯಿಯ ಸೃಷ್ಟಿಯಲ್ಲಿ ನೀನು ಯಾವುದೇ ಅಸಂಗತವನ್ನು ಕಾಣಲಾರೆ. (ಬೇಕಿದ್ದರೆ) ಇನ್ನೊಮ್ಮೆ ನೋಡು, ನಿನಗೆಲ್ಲಾದರೂ ಬಿರುಕು ಕಾಣಿಸುತ್ತಿದೆಯೇ ?');
INSERT INTO "kannada" VALUES(67,4,'67:4','ಮತ್ತೆ ಎರಡು ಬಾರಿ ಆವರ್ತಿಸಿ ನೋಡು. ದೃಷ್ಟಿಯು ಸೋತು ಹತಾಶವಾಗಿ ನಿನ್ನಲ್ಲಿಗೆ ಮರಳಿ ಬರುವುದು.');
INSERT INTO "kannada" VALUES(67,5,'67:5','ಅತಿ ಸಮೀಪದ ಆಕಾಶವನ್ನು ನಾವು ದೀಪಗಳಿಂದ (ನಕ್ಷತ್ರಗಳಿಂದ) ಅಲಂಕರಿಸಿದ್ದೇವೆ ಮತ್ತು ಅವುಗಳನ್ನು ಶೈತಾನರನ್ನು ಹೊಡೆದಟ್ಟುವ ಸಾಧನವಾಗಿ ಮಾಡಿದ್ದೇವೆ . ನಾವು ಆ ಶೈತಾನರಿಗಾಗಿ ಧಗಧಗಿಸುವ ನರಕ ಶಿಕ್ಷೆಯನ್ನು ಸಿದ್ಧಪಡಿಸಿಟ್ಟಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(67,6,'67:6','ತಮ್ಮ ಪ್ರಭುವನ್ನು ನಿಷೇಧಿಸುವವರಿಗೆ ನರಕ ಶಿಕ್ಷೆ ಇದೆ ಮತ್ತು ಅದು ಅತ್ಯಂತ ಕೆಟ್ಟ ಸಂಕೇತವೇ ಆಗಿದೆ.');
INSERT INTO "kannada" VALUES(67,7,'67:7','ಅವರು ಆ ನರಕಕ್ಕೆ ಎಸೆಯಲ್ಪಡುವಾಗ ಅದರಿಂದ ಒಂದು ಭೋರ್ಗರೆತವನ್ನು ಕೇಳುವರು. ಅಲ್ಲದೆ ಅದು ಕುದಿಯುತ್ತಲಿರುವುದು.');
INSERT INTO "kannada" VALUES(67,8,'67:8','ಸಿಟ್ಟಿನಿಂದ ಅದು ಸಿಡಿಯುವಂತಿರುವುದು. ಪ್ರತಿ ಬಾರಿಯೂ ಒಂದೊಂದು ಕೂಟ ಜನರನ್ನು ಅದರೊಳಗೆ ಹಾಕಲ್ಪಡುವಾಗಲೆಲ್ಲಾ ಅದರ ಕಾವಲುಗಾರರು ಅವರೊಡನೆ, ‘ನಿಮ್ಮ ಬಳಿಗೆ ಎಚ್ಚರಿಕೆ ಕೊಡುವವನಾರೂ ಬಂದಿರಲಿಲ್ಲವೇ ?’ ಎಂದು ಕೇಳುವರು.');
INSERT INTO "kannada" VALUES(67,9,'67:9','ಆಗ ಅವರು ಹೇಳುವರು; ಹೌದು, ಎಚ್ಚರಿಕೆ ಕೊಡುವವರು ಬಂದಿದ್ದರು. ಆದರೆ ನಾವು ಅವರನ್ನು ಸುಳ್ಳಾಗಿಸಿದೆವು ಮತ್ತು ಅಲ್ಲಾಹನು ಏನನ್ನೂ ಅವತೀರ್ಣಗೊಳಿಸಿಲ್ಲ, ನೀವು ದೊಡ್ಡ ದಾರಿಗೇಡಿನಲ್ಲಿ ಬಿದ್ದಿರುವಿರಿ’ ಎಂದು ನಾವು (ಅವರಲ್ಲಿ) ಹೇಳಿದೆವು.');
INSERT INTO "kannada" VALUES(67,10,'67:10','ಮತ್ತು ಅವರು ಹೀಗೂ ಹೇಳುವರು; ‘ನಾವು (ದೂತರ ಮಾತು) ಕೇಳಿರುತ್ತಿದ್ದರೆ ಮತ್ತು ಗ್ರಹಿಸಿರುತ್ತಿದ್ದರೆ ನಾವು ಧಗಧಗಿಸುವ ಜನರಲ್ಲಿ ಸೇರುತ್ತಿರಲಿಲ್ಲ’.');
INSERT INTO "kannada" VALUES(67,11,'67:11','ಹಾಗೆ ಅವರು ತಮ್ಮ ಅಪರಾಧವನ್ನು ತಾವೇ ಒಪ್ಪಿಕೊಳ್ಳುವರು. ನರಕವಾಸಿಗಳು (ಅಲ್ಲಾಹನ ಅನುಗ್ರಹದಿಂದ) ಎಷ್ಟು ವಿದೂರ !');
INSERT INTO "kannada" VALUES(67,12,'67:12','ತಮ್ಮ ಪ್ರಭುವನ್ನು ಅದೃಶ್ಯವಾಗಿ ಭಯಪಡುವವರಿಗೆ ಖಂಡಿತವಾಗಿಯೂ ಕ್ಷಮಾದಾನ ಮತ್ತು ದೊಡ್ಡ ಪ್ರತಿಫಲವಿದೆ.');
INSERT INTO "kannada" VALUES(67,13,'67:13','ನೀವು ರಹಸ್ಯವಾಗಿ ಮಾತನಾಡಿರಿ, ಬಹಿರಂಗವಾಗಿ ಮಾತನಾಡಿರಿ, (ಹೇಗಿದ್ದರೂ) ಅವನು ಹೃದ್ಗತಗಳನ್ನು ಬಲ್ಲ ಸರ್ವಜ್ಞಾನಿಯಾಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(67,14,'67:14','ಸೃಷ್ಟಿಸಿದವನು ಅರಿಯದಿರುವನೇ ? ಅವನು ಅತ್ಯಂತ ಸೂಕ್ಷ್ಮಜ್ಞನೂ ವಿವರಪೂರ್ಣನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(67,15,'67:15','ನಿಮಗೆ ಭೂಮಿಯನ್ನು ಅಧೀನಗೊಳಿಸಿ ಕೊಟ್ಟವನು ಅವನೇ. ಆದುದರಿಂದ ನೀವು ಅದರ ಪಾಶ್ರ್ವಗಳಲ್ಲಿ ನಡೆದಾಡಿರಿ ಮತ್ತು ಅಲ್ಲಾಹನ ಆಹಾರದಿಂದ ನೀವು ಭುಜಿಸಿರಿ. ಪುನಃ (ಜೀವಂತರಾಗಿ) ಮರಳುವುದು ಅವನ ಬಳಿಗೇ ಆಗಿದೆ.');
INSERT INTO "kannada" VALUES(67,16,'67:16','ಆಕಾಶದಲ್ಲಿ (ಆಧಿಪತ್ಯ) ಇರುವವನು ಭೂಮಿಯಲ್ಲಿ ನಿಮ್ಮನ್ನು ಹುಗಿದು ಬಿಡುವ ಕುರಿತು ನೀವು ನಿರ್ಭೀತರಾಗಿರುವಿರಾ? ಆಗ ಅದು (ಭೂಮಿ) ನಡುಗುತ್ತಲಿರುತ್ತವೆ.');
INSERT INTO "kannada" VALUES(67,17,'67:17','ಅಥವಾ ಆಕಾಶದಲ್ಲಿ (ಆಧಿಪತ್ಯ) ಹೊಂದಿರುವನು ನಿಮ್ಮ ಮೇಲೆ ಒಂದು ಕಲ್ಲು ಮಳೆಯನ್ನು ಕಳುಹಿಸಿ ಬಿಡುವ ಕುರಿತು ನೀವು ನಿರ್ಭಿತರಾಗಿರುವಿರಾ? ನನ್ನ ಮುನ್ನೆಚ್ಚರಿಕೆ ಹೇಗಿರುತ್ತದೆಂಬುದು ಆಗ ನಿಮಗೆ ತಿಳಿದು ಬರುವುದು.');
INSERT INTO "kannada" VALUES(67,18,'67:18','ಇವರಿಗಿಂತ ಮುಂಚೆ ಗತಿಸಿ ಹೋದವರು ಸತ್ಯವನ್ನು ಸುಳ್ಳಾಗಿಸಿದ್ದರು. ಆಗ ನನ್ನ ಪ್ರತಿಷೇಧವು ಹೇಗಿತ್ತು ?');
INSERT INTO "kannada" VALUES(67,19,'67:19','ತಮ್ಮ ಮೇಲೆ ಪಕ್ಷಿಗಳು ರೆಕ್ಕೆ ಬಿಚ್ಚಿಕೊಂಡೂ ಮಡಚಿಕೊಂಡೂ ಹಾರಾಡುತ್ತಿರುವುದನ್ನು ಅವರು ನೋಡುತ್ತಿಲ್ಲವೇ ? ಪರಮದಯಾಮಯನ ಹೊರತು ಅವುಗಳನ್ನು ಹಿಡಿದಿರಿಸಿಕೊಳ್ಳುವವರು ಬೇರಾರೂ ಇಲ್ಲ. ಅವನು ಪ್ರತಿಯೊಂದು ವಸ್ತುವನ್ನೂ ಕಾಣುವವನೇ ಆಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(67,20,'67:20','ಅದಲ್ಲ, ಪರಮ ದಯಾಮಯನ ಹೊರತು ನಿಮಗೆ ಸಹಾಯ ಮಾಡಬಲ್ಲಂತಹ ಒಂದು ಸೈನ್ಯವಾಗಿ ನಿಮಗೆ ಯಾರಿದ್ದಾರೆ? ಸತ್ಯನಿಷೇಧಿಗಳು ಖಂಡಿತ ಮೋಸಕ್ಕೊಳಗಾಗದೇ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(67,21,'67:21','ಅಥವಾ ಪರಮ ದಯಾಮಯನು ತನ್ನ ಜೀವನಾಧಾರಗಳನ್ನು ತಡೆ ಹಿಡಿದರೆ, ನಿಮಗೆ ಜೀವನಾಧಾರ ನೀಡಬಲ್ಲವನು ಯಾರು? ಆದರೆ ಅವರು ದರ್ಪದಲ್ಲೂ ಸತ್ಯದಿಂದ ದೂರ ಚದುರುವುದರಲ್ಲೂ ನಿರತ ರಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(67,22,'67:22','ತಲೆಕೆಳಗಾಗಿ ನಡೆಯುತ್ತಿರುವವನೋ ಅಥವಾ ನೇರವಾಗಿ ಸಮತಟ್ಟಾದ ಮಾರ್ಗದಲ್ಲಿ ನಡೆಯುತ್ತಿರುವವನೋ? ಯಾವನು ಸನ್ಮಾರ್ಗ ಪ್ರಾಪ್ತನು? ');
INSERT INTO "kannada" VALUES(67,23,'67:23','(ಸಂದೇಶವಾಹಕರೇ), ಹೇಳಿರಿ; ನಿಮ್ಮನ್ನು ಸೃಷ್ಟಿಸಿದವನೂ ನಿಮಗೆ ಶ್ರವಣ ಮತ್ತು ದೃಷ್ಟಿಗಳನ್ನು ಹಾಗೂ ಹೃದಯಗಳನ್ನು ನೀಡಿದವನೂ ಅವನೇ ಆಗಿರುವನು. ಆದರೆ, ನೀವು ಸ್ವಲ್ಪವೇ ಕೃತಜ್ಞತೆ ಸಲ್ಲಿಸುತ್ತೀರಿ’ .');
INSERT INTO "kannada" VALUES(67,24,'67:24','ಹೇಳಿರಿ; ‘ನಿಮನ್ನು ಭೂಮಿಯ ಮೇಲೆ ಸೃಷ್ಟಿಸಿ ಬೆಳೆಸಿದವನು ಅವನೇ (ಅಲ್ಲಾಹನೇ). ಅವನ ಕಡೆಗೇ ನೀವು ಒಟ್ಟುಗೂಡಿಸಲ್ಪಡುವಿರಿ’ .');
INSERT INTO "kannada" VALUES(67,25,'67:25','ಈ ವಾಗ್ದಾನವು ಪೂರ್ತಿ ಗೊಳ್ಳುವುದು ಯಾವಾಗ, ನೀವು ನಿಜ ಹೇಳುವುದಾಗಿದ್ದರೆ ಎಂದು ಅವರು ಕೇಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(67,26,'67:26','ಹೇಳಿರಿ; (ಪ್ರವಾದಿಯವರೇ,) ಅದರ ಜ್ಞಾನ ಅಲ್ಲಾಹನಿಗೆ ಮಾತ್ರವಿದೆ. ನಾನು ಸ್ಪಷ್ಟವಾದ ಎಚ್ಚರಿಕೆ ನೀಡುವವನು ಮಾತ್ರ.');
INSERT INTO "kannada" VALUES(67,27,'67:27','ಮುಂದೆ ಶಿಕ್ಷೆಯು ಸಮೀಪವಾಗಿರುವುದನ್ನು ಅವರು ಕಂಡಾಗ, ಸತ್ಯನಿಷೇಧಿಗಳ ಮುಖಗಳಿಗೆ ಮ್ಲಾನತೆ ಬಾಧಿಸುವುದು. ನೀವು (ಇಲ್ಲವೆಂದು) ವಾದಿಸುತ್ತಿದ್ದ ವಿಚಾರವು ಇದುವೇ ಆಗಿದೆ’ ಎಂದು (ಅವರಿಗೆ) ಹೇಳಲಾಗುವುದು .');
INSERT INTO "kannada" VALUES(67,28,'67:28','ಹೇಳಿರಿ; (ಪ್ರವಾದಿಯವರೇ,) ನೀವು ಯೋಚಿಸಿದ್ದೀರಾ? ನನ್ನನ್ನು ಮತ್ತು ನನ್ನ ಜೊತೆಗಿರುವವರನ್ನು ಅಲ್ಲಾಹನು ಶಿಕ್ಷಿಸಿದರೆ ಅಥವಾ ನಮ್ಮಲ್ಲಿ ಕರುಣೆ ತೋರಿಸಿದರೆ (ಈ ಎಡರಡರಲ್ಲೂ ನಿಮಗೇನು ಲಾಭ?) ವೇದನಾಜನಕ ಶಿಕ್ಷೆಯಿಂದ ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ಯಾರು ರಕ್ಷಿಸುವರು?’');
INSERT INTO "kannada" VALUES(67,29,'67:29','ಹೇಳಿರಿ; ಅವನು ಪರಮ ಕಾರುಣಿಕನು. ನಾವು ಅವನಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸಿದ್ದೇವೆ ಮತ್ತು ಅವನ ಮೇಲೆಯೇ ಭರವಸೆಯನ್ನಿರಿಸಿದ್ದೇವೆ. ಆದರೆ ಸ್ಪಷ್ಟ- ಪಥಭ್ರಷ್ಟತೆಯಲ್ಲಿರುವವನು ಯಾರೆಂದು ಸದ್ಯದಲ್ಲೇ ನಿಮಗೆ ತಿಳಿದುಬರುವುದು’.');
INSERT INTO "kannada" VALUES(67,30,'67:30','(ಪ್ರವಾದಿಯವರೇ,) ಹೇಳಿರಿ; ನಿಮ್ಮ ನೀರು ನೆಲದೊಳಕ್ಕೆ ಇಂಗಿ ಹೋದರೆ, ಹರಿಯುವ ಚಿಲುಮೆಗಳನ್ನು ನಿಮಗೆ ತಂದು ಕೊಡುವವನು ಯಾರೆಂದು ನೀವು ಹೇಳಬಲ್ಲಿರಾ?’.');
INSERT INTO "kannada" VALUES(68,1,'68:1','ನೂನ್-ಲೇಖನಿಯ ಆಣೆ ಹಾಗೂ ಅವರು ಬರೆದಿಡುತ್ತಿರುವುದರಾಣೆ.');
INSERT INTO "kannada" VALUES(68,2,'68:2','ತಮ್ಮ ಪ್ರಭುವಿನ ಅನುಗ್ರಹದಿಂದ ತಾವು ಹುಚ್ಚರಲ್ಲ.');
INSERT INTO "kannada" VALUES(68,3,'68:3','ಖಂಡಿತವಾಗಿಯೂ ತಮಗೆ ಎಂದಿಗೂ ಮುಗಿದು ಹೋಗದಂತಹ ಸತ್ಫಲವಿದೆ.');
INSERT INTO "kannada" VALUES(68,4,'68:4','ನಿಶ್ಚಯವಾಗಿಯೂ ತಾವು ಸತ್‍ಸ್ವಭಾವದ ಅತ್ಯುನ್ನತ ಮಟ್ಟದಲ್ಲಿದ್ದೀರಿ.');
INSERT INTO "kannada" VALUES(68,5,'68:5','ಸದ್ಯದಲ್ಲೇ ತಾವೂ ನೋಡಿ ತಿಳಿಯುವಿರಿ ಮತ್ತು ಅವರೂ ಕಂಡರಿಯುವರು.');
INSERT INTO "kannada" VALUES(68,6,'68:6','ನಿಮ್ಮ ಪೈಕಿ ಯಾರು ಪೀಡೆಗೊಳಗಾದವರು?’ ಎಂಬುದನ್ನು.');
INSERT INTO "kannada" VALUES(68,7,'68:7','ತಮ್ಮ ಪ್ರಭುವು ಅವನ ಮಾರ್ಗದಿಂದ ತಪ್ಪಿ ಹೋದವರ ಬಗ್ಗೆ ಚೆನ್ನಾಗಿ ಅರಿತಿರುತ್ತಾನೆ ಮತ್ತು ಸನ್ಮಾರ್ಗ ಪ್ರಾಪ್ತರ ಬಗೆಗೂ ಅವನು ಚೆನ್ನಾಗಿ ಬಲ್ಲವನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(68,8,'68:8','ಆದುದರಿಂದ ಸುಳ್ಳಾಗಿಸುವವರನ್ನು ತಾವು ಅನುಸರಿಸಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(68,9,'68:9','ತಾವು ಸಡಿಲು ತೋರಿದರೆ ಒಳ್ಳೆಯದಿತ್ತೆಂದು ಅವರು ಆಗ್ರಹಿಸುತ್ತಾರೆ. ಆಗ ಅವರು ಕೂಡಾ ಸಡಿಲು ತೋರುವರು.');
INSERT INTO "kannada" VALUES(68,10,'68:10','');
INSERT INTO "kannada" VALUES(68,11,'68:11','');
INSERT INTO "kannada" VALUES(68,12,'68:12','');
INSERT INTO "kannada" VALUES(68,13,'68:13','ಅತ್ಯಧಿಕ ಆಣೆ ಹಾಕುವವನೂ, ನೀಚನೂ ಚುಚ್ಚು ಮಾತು ಹೇಳುವವನೂ ಚಾಡಿ ಹೇಳುತ್ತಾ ತಿರುಗುವವನೂ ಸತ್ಕಾರ್ಯಗಳನ್ನು ಮಾಡದಂತೆ ತಡೆಯುವವನೂ ದುಷ್ಕರ್ಮಿಯೂ ಮಹಾಪಾಪಿಯೂ ಕ್ರೂರನೂ ಮತ್ತು ಇದೆಲ್ಲದರ ಜೊತೆಗೆ ಕುಲಗೆಟ್ಟವನೂ ಆಗಿರುವ ಯಾವನನ್ನೂ ತಾವು ಅನುಸರಿಸಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(68,14,'68:14','ಅವನು ಬಹಳಷ್ಟು ಸಂಪತ್ತು ಮತ್ತು ಸಂತಾನವು ಳ್ಳವನೆಂಬ ನೆಲೆಯಲ್ಲಿ ಹೀಗಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(68,15,'68:15','ಅವನಿಗೆ ನಮ್ಮ ಸೂಕ್ತಗಳನ್ನು ಓದಿ ಕೇಳಿಸಿದಾಗ ಇವೆಲ್ಲ ಪೂರ್ವಿಕರ ಕಟ್ಟುಕತೆಗಳು ಎಂದು ಹೇಳುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(68,16,'68:16','ಶೀಘ್ರವೇ ನಾವು ಅವನ ಸೊಂಡಿಲಿಗೆ (ನೀಳ ಮೂಗಿಗೆ) ಬರೆ ಗುರುತು ಎಳೆಯಲಿದ್ದೇವೆ .');
INSERT INTO "kannada" VALUES(68,17,'68:17','ಒಂದು ತೋಟದವರನ್ನು ನಾವು ಪರೀಕ್ಷೆಗೆ ಒಳಪಡಿಸಿದ್ದಂತೆಯೇ ಇವರನ್ನು ಪರೀಕ್ಷೆ ಗೊಳಪಡಿಸಿರುತ್ತೇವೆ. ಆ ತೋಟದ ಫಲಗಳನ್ನು ನಸುಕಿನ ವೇಳೆ ಕೊಯ್ಯುವುದಾಗಿ ಅವರು ಶಪಥ ಹಾಕಿದ ಸಂದರ್ಭ.');
INSERT INTO "kannada" VALUES(68,18,'68:18','ಅವರು ಏನನ್ನೂ ಹೊರತುಪಡಿಸಿ ಹೇಳಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(68,19,'68:19','ಆಮೇಲೆ ಇರುಳಲ್ಲಿ ಅವರು ನಿದ್ರಿಸುತ್ತಿದ್ದಾಗ ತಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಆ ತೋಟಕ್ಕೆ ವಿಪತ್ತೊಂದು ಎರಗಿತು.');
INSERT INTO "kannada" VALUES(68,20,'68:20','ಆಮೇಲೆ ಇರುಳಲ್ಲಿ ಅವರು ನಿದ್ರಿಸುತ್ತಿದ್ದಾಗ ತಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಆ ತೋಟಕ್ಕೆ ವಿಪತ್ತೊಂದು ಎರಗಿತು.');
INSERT INTO "kannada" VALUES(68,21,'68:21','ಅನಂತರ ಪ್ರಭಾತದಲ್ಲಿ ಅವರು - ಪರಸ್ಪರ ಕೂಗಿ ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(68,22,'68:22','``ನೀವು ಫಲಗಳನ್ನು ಕೊಯ್ಯಬೇಕೆಂದು ಉದ್ದೇಶಿಸುತ್ತೀರೆಂದಾದರೆ ನಸುಕಿನಲ್ಲೇ ನಿಮ್ಮ ಹೊಲಗಳ ಕಡೆಗೆ ಹೊರಡಿರಿ’’.');
INSERT INTO "kannada" VALUES(68,23,'68:23','');
INSERT INTO "kannada" VALUES(68,24,'68:24','‘ಇಂದು ಬಡವನಾರೂ ನಿಮ್ಮ ಹತ್ತಿರ ತೋಟಕ್ಕೆ ಬಾರದಂತೆ ನೋಡಿಕೊಳ್ಳಬೇಕು’ ಎಂದು ಅವರು ಪರಸ್ಪರ ಪಿಸುಗುಟ್ಟುತ್ತ ತೆರಳಿದರು.');
INSERT INTO "kannada" VALUES(68,25,'68:25','(ಬಡವರನ್ನು) ತಡೆಯಲು ನಾವು ಶಕ್ತರು ಅಂದು ಕೊಳ್ಳುತ್ತಾ ನಸುಕಿನಲ್ಲೇ ಅಲ್ಲಿಗೆ ಹೋದರು.');
INSERT INTO "kannada" VALUES(68,26,'68:26','ಆದರೆ ತೋಟವನ್ನು ಕಂಡಾಗ ಅವರು ಹೇಳಿ ದರು, ‘ನಾವು ದಾರಿ ತಪ್ಪಿದವರೇ ಆಗಿದ್ದೇವೆ.’');
INSERT INTO "kannada" VALUES(68,27,'68:27','ಅಲ್ಲ, ನಿಜವಾಗಿ ನಾವು (ಉಪಜೀವನಮಾರ್ಗ) ತಡೆಯಲ್ಪಟ್ಟವರಾಗಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(68,28,'68:28','ಅವರ ಪೈಕಿ ಮಧ್ಯಮ ಧೋರಣೆಯವನು; ‘ನೀವೇಕೆ ಅಲ್ಲಾಹನ ಮಹತ್ವವನ್ನು ಒಪ್ಪದೇ ಹೋದಿರಿ? ಎಂದು ನಾನು ನಿಮ್ಮೊಡನೆ ಹೇಳಿರಲಿ ಲ್ಲವೇ?’ ಎಂದನು.');
INSERT INTO "kannada" VALUES(68,29,'68:29','`ಆಗ ಅವರು, ‘ನಮ್ಮ ಪ್ರಭು ಪರಮ ಪಾವನನು,ನಿಜಕ್ಕೂ ನಾವು ಅಕ್ರಮಿಗಳಾಗಿದ್ದೆವು’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(68,30,'68:30','ತರುವಾಯ ಅವರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬರೂ ಪರಸ್ಪರ ತೆಗಳುತ್ತ ಎದುರುಗೊಂಡರು.');
INSERT INTO "kannada" VALUES(68,31,'68:31','ಕೊನೆಗೆ ಅವರು, ‘ಅಯ್ಯೋ ನಮ್ಮ ನಾಶವೇ! ನಿಜಕ್ಕೂ ನಾವು ಅತಿಕ್ರಮಿಗಳಾಗಿಬಿಟ್ಟಿದ್ದೆವು’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(68,32,'68:32','ನಮ್ಮ ಪ್ರಭು ಇದಕ್ಕೆ ಬದಲಾಗಿ ಇದಕ್ಕಿಂತಲೂ ಉತ್ತಮ ತೋಟ ದಯಪಾಲಿಸಲೂಬಹುದು. ನಾವು ನಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಗೆ ಆಗ್ರಹ ಸಮರ್ಪಿ ಸುವವರಾಗಿದ್ದೇವೆ’ ಎಂದರು.');
INSERT INTO "kannada" VALUES(68,33,'68:33','ಶಿಕ್ಷೆಯು ಹೀಗಿರುತ್ತದೆ. ಮತ್ತು ಪರಲೋಕದ ಶಿಕ್ಷೆಯು ಇದಕ್ಕಿಂತಲೂ ದೊಡ್ಡದಾಗಿದೆ. ಇದನ್ನು ಅವರು ತಿಳಿದಿರುತ್ತಿದ್ದರೆ!');
INSERT INTO "kannada" VALUES(68,34,'68:34','ಖಂಡಿತವಾಗಿಯೂ, ದೇವಭಯವುಳ್ಳವರಿಗೆ ಅವರ ಪ್ರಭುವಿನ ಬಳಿ ಅನುಗ್ರಹೀತ ಸ್ವರ್ಗೋ ದ್ಯಾನಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(68,35,'68:35','ಮುಸ್ಲಿಮರನ್ನು ನಾವು ಅಪರಾಧಿಗಳಂತೆ ಮಾಡುವೆವೇನು?');
INSERT INTO "kannada" VALUES(68,36,'68:36','ನಿಮಗೇನಾಗಿದೆ? ನೀವು ಹೇಗೆ ತೀರ್ಮಾನ ಮಾಡುತ್ತಿರುವಿರಿ?');
INSERT INTO "kannada" VALUES(68,37,'68:37','ಅದಲ್ಲ, ನಿಮ್ಮ ಬಳಿ ಗ್ರಂಥವಿದ್ದು, ನೀವು ಅದರಲ್ಲಿ ಅಧ್ಯಯನ ನಡೆಸುತ್ತಿರುವಿರಾ?');
INSERT INTO "kannada" VALUES(68,38,'68:38','ನೀವು (ಯಥೇಷ್ಟ) ಆಯ್ಕೆ ಮಾಡುವ ವಿಷಯಗಳು ನಿಮಗೆ ಆ ಗ್ರಂಥದಲ್ಲಿ ಬಂದಿದೆಯೇನು?');
INSERT INTO "kannada" VALUES(68,39,'68:39','ಅಥವಾ ಪುನರುತ್ಥಾನದ ದಿನದ ವರೆಗೆ ದೀರ್ಘಾ ವಧಿಯುಳ್ಳ ವಾಗ್ದಾನವನ್ನು ನಮ್ಮಿಂದ ಪಡೆದಿ ರುವಿರೇನು? ನೀವು ತೀರ್ಮಾನಿಸುವುದೆಲ್ಲವೂ ನಿಮಗಿದೆ ಎಂಬ ಬಗ್ಗೆ!');
INSERT INTO "kannada" VALUES(68,40,'68:40','ಅವರ ಪೈಕಿ ಅದರ ಕುರಿತು ಜವಾಬ್ದಾರಿ ವಹಿಸಿ ಕೊಳ್ಳುವವರು ಯಾರು? ಎಂದು ಅವರೊಡನೆ ಕೇಳಿ ನೋಡಿರಿ.');
INSERT INTO "kannada" VALUES(68,41,'68:41','ಅಥವಾ ಅವರಿಗೆ ಸಹಭಾಗಿಗಳು ಯಾರಾದರೂ ಇದ್ದಾರೆಯೇ? ಅವರು ಸತ್ಯವಂತರಾಗಿದ್ದರೆ ತಮ್ಮ ಸಹಭಾಗಿಗಳನ್ನು ತರಲಿ.');
INSERT INTO "kannada" VALUES(68,42,'68:42','ಕಣೆಗಾಲು ತೆರೆಯಲ್ಪಡುವ (ಭಯಂಕರವಾದ) ಒಂದು ದಿನವನ್ನು (ತಾವು ನೆನೆಯಿರಿ). ಅಂದು ಸಾಷ್ಟಾಂಗವೆರಗಲಿಕ್ಕಾಗಿ ಅವರನ್ನು ಕರೆಯು ವಾಗ ಅವರಿಗೆ ಅದು ಸಾಧ್ಯವಾಗದು .');
INSERT INTO "kannada" VALUES(68,43,'68:43','ಅವರ ದೃಷ್ಟಿಗಳು ದೈನ್ಯತೆಯಿಂದ ಕೂಡಿದ್ದು ಅಪಮಾನವು ಅವರನ್ನು ಆವರಿಸಿಕೊಂಡಿ ರುವುದು. ಅವರು ಸುರಕ್ಷಿತರಾಗಿದ್ದಾಗ ಇವರನ್ನು ಸಾಷ್ಟಾಂಗವೆರಗಲಿಕ್ಕಾಗಿ ಕರೆಯಲಾಗುತ್ತಿತ್ತು. (ಆದರೆ ಅವರಂದು ನಿರ್ವಹಿಸಲಿಲ್ಲ)');
INSERT INTO "kannada" VALUES(68,44,'68:44','ಆದುದರಿಂದ ಸಂದೇಶವಾಹಕರೇ, ತಾವು ನನ್ನನ್ನು ಮತ್ತು ಈ ವೃತ್ತಾಂತವನ್ನು ತಿರಸ್ಕರಿಸು ವವರನ್ನು ಬಿಟ್ಟುಬಿಡಿರಿ. ಅವರು ಅರಿಯದ ಭಾಗದಿಂದ ನಾವು ಅವರನ್ನು ಹಂತಹಂತವಾಗಿ ಹಿಡಿದು ಕೊಳ್ಳುವೆವು.');
INSERT INTO "kannada" VALUES(68,45,'68:45','ನಾನು ಅವರಿಗೆ ಸಾವಕಾಶ ನೀಡುತ್ತೇನೆ. ನನ್ನ ತಂತ್ರವು ಅತ್ಯಂತ ಪ್ರಚಂಡವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(68,46,'68:46','ತಾವು ಅವರಿಂದ ಏನಾದರೂ ಪ್ರತಿಫಲ ಕೇಳು ತ್ತಿರುವಿರಾ? ಅದರ ಭಾರ ಅವರಿಗೆ ಹೊರೆಯಾ ಗುವ ಸ್ಥಿತಿಯಲ್ಲಿ?');
INSERT INTO "kannada" VALUES(68,47,'68:47','ಅಥವಾ ಪರೋಕ್ಷವು ಅವರ ಬಳಿ ಇದೆಯೆ? ಅದ ರಿಂದ ಅವರು ಬರೆದುಕೊಳ್ಳುತ್ತಿರುವರೇನು?');
INSERT INTO "kannada" VALUES(68,48,'68:48','ಸರಿ, ತಾವು ತಮ್ಮ ಪ್ರಭುವಿನ (ಅವರ ವಿಷಯ ದಲ್ಲಿ) ತೀರ್ಪಿಗಾಗಿ ತಾಳ್ಮೆ ವಹಿಸಿರಿ ಮತ್ತು ಮತ್ಸ್ಯ ದ ವ್ಯಕ್ತಿಯಂತೆ (ಯೂನುಸ್ ನಬಿಯರಂತೆ) ಆಗಬೇಡಿರಿ. ಅವರು ವ್ಯಸನ ಚಿತ್ತರಾಗಿ ಪ್ರಾರ್ಥಿ ಸಿದ ಸಂದರ್ಭ!');
INSERT INTO "kannada" VALUES(68,49,'68:49','ಅವರ ಪ್ರಭುವಿನ ಒಂದು ಅನುಗ್ರಹವು ಅವರಿಗೆ ಸಿಗದೆ ಇರುತ್ತಿದ್ದರೆ, ಅವರು ಆಕ್ಷೇಪಾರ್ಹರಾಗಿ ಬಟ್ಟ ಮರುಭೂಮಿಯಲ್ಲಿ ಹೊರ ಎಸೆಯಲ್ಪಡು ತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(68,50,'68:50','ಕೊನೆಗೆ ಅವರ ಪ್ರಭು ಅವರನ್ನು ಆರಿಸಿಕೊಂ ಡನು ಮತ್ತು ಅವರನ್ನು ಸಜ್ಜನರ ಸಾಲಿಗೆ ಸೇರಿ ಸಿಕೊಂಡನು.');
INSERT INTO "kannada" VALUES(68,51,'68:51','ಸತ್ಯನಿಷೇಧಿಗಳು, ಈ ಉಪದೇಶವನ್ನು ಕೇಳಿ ದಾಗ ಅವರ ಕಣ್ಣುಗಳಿಂದ ದುರುಗುಟ್ಟಿ ನೋ ಡುತ್ತ ತಮ್ಮನ್ನು ಜಾರಿ ಬೀಳಿಸಲು ಹೆಣಗುತ್ತಾರೆ. ಮತ್ತು ಇವನು ಖಂಡಿತ ಹುಚ್ಚ ಎಂದು ಹೇಳು ತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(68,52,'68:52','ಇದು ಲೋಕದ ಜನತೆಗಿರುವ ಒಂದು ಉದ್ಬೋಧನೆಯಲ್ಲದೆ ಇನ್ನೇನೂ ಅಲ್ಲ.');
INSERT INTO "kannada" VALUES(69,1,'69:1','ಖಚಿತವಾದ ಆ ಮಹಾ ಘಟನೆ!');
INSERT INTO "kannada" VALUES(69,2,'69:2','ಆ ಮಹಾ ಘಟನೆ ಏನದು?');
INSERT INTO "kannada" VALUES(69,3,'69:3','ಆ ಖಚಿತವಾದ ಮಹಾ ಸಂಭವ ಏನೆಂಬುದು ತಮಗೆ ಗೊತ್ತೆ?');
INSERT INTO "kannada" VALUES(69,4,'69:4','ಸಮೂದ್ ಮತ್ತು ಆದ್ ಸಮುದಾಯವು ಆ ಭಯಾನಕ ಘಟನೆಯನ್ನು ನಿರಾಕರಿಸಿಬಿಟ್ಟವು!');
INSERT INTO "kannada" VALUES(69,5,'69:5','ಸಮೂದ್ ಸಮುದಾಯ ಒಂದು ಭೀಕರ ಶಿಕ್ಷೆಯ ಮೂಲಕ ನಾಶಗೊಳಿಸಲ್ಪಟ್ಟರು.');
INSERT INTO "kannada" VALUES(69,6,'69:6','ಆದ್ ಜನಾಂಗದವರು ರುದ್ರನಾದದ ಒಂದು ಪ್ರಚಂಡ ಚಂಡಮಾರುತದಿಂದ ನಾಶವಾದರು.');
INSERT INTO "kannada" VALUES(69,7,'69:7','ಅವನು (ಅಲ್ಲಾಹನು) ಅದನ್ನು ನಿರಂತರ ಏಳು ರಾತ್ರೆ ಮತ್ತು ಎಂಟು ಹಗಲುಗಳ ಕಾಲ ಅವರ ಮೇಲೆ ಛೂ ಬಿಟ್ಟನು. ಅದರಿಂದಾಗಿ ಆ ಜನಾಂಗ ಖರ್ಜೂರದ ವೃಕ್ಷಗಳು ಬುಡಕಳಚಿ ಬಿದ್ದಂತೆ ಬಿದ್ದು ಕೊಂಡಿರುವುದನ್ನು ನಿನಗೆ ಕಾಣಬಹುದಾಗಿತ್ತು!');
INSERT INTO "kannada" VALUES(69,8,'69:8','ಇನ್ನು ಅವರಿಗೆ ಏನಾದರೂ ಉಳಿದಿರುವುದು ತಮಗೆ ಕಾಣಿಸುತ್ತಿದೆಯೇ?');
INSERT INTO "kannada" VALUES(69,9,'69:9','ಫಿರ್‍ಔನ್ ಮತ್ತು ಅವನಿಗಿಂತ ಹಿಂದಿನವರು ಮತ್ತು ಬುಡಮೇಲಾದ ನಾಡುಗಳ ಜನರು ಮಹಾಪರಾಧವನ್ನು ಎಸಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(69,10,'69:10','ಅವರೆಲ್ಲರೂ ತಮ್ಮ ಪ್ರಭುವಿನ ದೂತರನ್ನು ಧಿಕ್ಕರಿಸಿದರು. ಆಗ ಅವನು ಅವರನ್ನು ಅತ್ಯು ಗ್ರವಾಗಿ ಹಿಡಿದನು.');
INSERT INTO "kannada" VALUES(69,11,'69:11','ಜಲವು ಮಿತಿಮೀರಿದಾಗ ನಾವು ನಿಮ್ಮನ್ನು ಹಡಗಿಗೆ ಹತ್ತಿಸಿ ರಕ್ಷಿಸಿದ್ದೆವು .');
INSERT INTO "kannada" VALUES(69,12,'69:12','ಅದನ್ನು ನಿಮ್ಮ ಪಾಲಿಗೆ ಒಂದು ಪಾಠ ಬೋಧಕ ಸ್ಮರಣೆಯಾಗಿ ಮಾಡಲು ಮತ್ತು ಕಿವಿಗೊಟ್ಟು ಆಲಿಸುವ ಕಿವಿಗಳು ಇದರ ನೆನಪನ್ನು ಅಚ್ಚಳಿಯದೆ ಉಳಿಸಲು.');
INSERT INTO "kannada" VALUES(69,13,'69:13','ಮುಂದೆ ಒಂದೇ ಒಂದು ಬಾರಿ ಕಹಳೆಯನ್ನೂದಿದಾಗ!');
INSERT INTO "kannada" VALUES(69,14,'69:14','ಹಾಗೂ ಭೂಮಿ ಮತ್ತು ಪರ್ವತಗಳನ್ನೆತ್ತಿ ಒಂದೇ ಏಟಿಗೆ ನುಚ್ಚುನೂರು ಮಾಡಲಾದಾಗ.');
INSERT INTO "kannada" VALUES(69,15,'69:15','ಅಂದು, ಆ ಘಟನೆಯು ಸಂಭವಿಸಿ ಬಿಡುವುದು.');
INSERT INTO "kannada" VALUES(69,16,'69:16','ಆಕಾಶವು ಬಿರಿಯುವುದು. ಅಂದು ಅದು ದುರ್ಬಲವಾಗಿರುವುದು.');
INSERT INTO "kannada" VALUES(69,17,'69:17','ದೇವಚರರು ಅದರ ನಾನಾ ಅಂಚುಗಳಲ್ಲಿರುವರು. ಮತ್ತು ಅವರ ಮೇಲಕ್ಕೆ ತಮ್ಮ ಪ್ರಭುವಿನ ಅರ್ಶ್‍ನ್ನು ಎಂಟು ಮಲಕ್‍ಗಳು ಹೊತ್ತಿರುವರು .');
INSERT INTO "kannada" VALUES(69,18,'69:18','ಅದು ನಿಮ್ಮನ್ನು (ಅಲ್ಲಾಹನ ಸನ್ನಿಧಿಯಲ್ಲಿ) ಪ್ರದರ್ಶಿಸಲ್ಪಡುವ ದಿನವಾಗಿರುವುದು. ಯಾವ ರಹಸ್ಯವೂ ನಿಮ್ಮಿಂದ ಅಡಗಿರಲಾರದು.');
INSERT INTO "kannada" VALUES(69,19,'69:19','ಅಂದು ಯಾವನ ಕರ್ಮಗ್ರಂಥವನ್ನು ಅವನ ಬಲಗೈಯಲ್ಲಿ ಕೊಡಲಾಗುವುದೋ ಅವನು ಹೇಳುವನು; `ಇದೋ ನನ್ನ ಕರ್ಮಗ್ರಂಥ. ಒಮ್ಮೆ ಹಿಡಿದುಕೊಂಡು ಓದಿ ನೋಡಿರಿ.');
INSERT INTO "kannada" VALUES(69,20,'69:20','ನನಗೆ ವಿಚಾರಣೆಯನ್ನು ಎದುರಿಸಲಿಕ್ಕಿದೆ ಎಂದು ನಾನು ಮೊದಲೇ ನಂಬಿಕೊಂಡಿದ್ದೆ.');
INSERT INTO "kannada" VALUES(69,21,'69:21','ಆಗ ಅವನು ಸಂತೃಪ್ತಿಯ ಜೀವನದಲ್ಲಿರುವನು.');
INSERT INTO "kannada" VALUES(69,22,'69:22','ಅಂದರೆ, ಅತ್ಯುನ್ನತ ಸ್ವರ್ಗೋದ್ಯಾನದಲ್ಲಿ.');
INSERT INTO "kannada" VALUES(69,23,'69:23','ಅದರ ಹಣ್ಣುಗಳ ಗೊಂಚಲುಗಳು (ಕೊಯ್ದು ತೆಗೆಯಲು ಸುಲಭವಾಗುವಂತೆ) ಅವರಿಗೆ ನಿಕಟವಾಗಿರುವುವು.');
INSERT INTO "kannada" VALUES(69,24,'69:24','ಕಳೆದು ಹೋದ ದಿನಗಳಲ್ಲಿ ನೀವು ಮಾಡಿರುವ ಸತ್ಕರ್ಮಗಳ ಪ್ರತಿಫಲವಾಗಿ ಸಂತೋಷದಿಂದ ತಿನ್ನಿರಿ ಮತ್ತು ಕುಡಿಯಿರಿ. (ಎಂದು ಅವರೊಂದಿಗೆ ಹೇಳಲಾಗುವುದು.)');
INSERT INTO "kannada" VALUES(69,25,'69:25','ಯಾವನ ಕರ್ಮಗ್ರಂಥವನ್ನು ಅವನ ಎಡಗೈಯಲ್ಲಿ ಕೊಡಲಾಗುವುದೋ ಅವನು ಹೇಳುವನು, ‘ಅಯ್ಯೋ! ನನ್ನ ಕರ್ಮಗ್ರಂಥವನ್ನು ನನಗೆ ಕೊಡದೆ ಇರುತ್ತಿದ್ದರೆ !');
INSERT INTO "kannada" VALUES(69,26,'69:26','ಮತ್ತು ನನ್ನ ವಿಚಾರಣಾ ಫಲವೇನೆಂದು ನನಗೆ ತಿಳಿಯದೆ ಇರುತ್ತಿದ್ದರೆ ಉತ್ತಮವಿತ್ತು.');
INSERT INTO "kannada" VALUES(69,27,'69:27','ಅಯ್ಯೋ ನನ್ನ ಮರಣವೇ ಅಂತಿಮವಾಗಿರುತ್ತಿದ್ದರೆ (ಉತ್ತಮವಿತ್ತು.)');
INSERT INTO "kannada" VALUES(69,28,'69:28','ನನ್ನ ಸಂಪತ್ತು (ಇಂದು) ನನಗೇನೂ ಪ್ರಯೋಜನ ಕೊಡಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(69,29,'69:29','ನನ್ನ ಅಧಿಕಾರವೆಲ್ಲವೂ ನನಗೆ ನಷ್ಟಗೊಂಡಿತು.');
INSERT INTO "kannada" VALUES(69,30,'69:30','(ದೇವಚರರೊಂದಿಗೆ ಅಲ್ಲಾಹು ಹೇಳುವನು) ಅವನನ್ನು ಹಿಡಿಯಿರಿ ಮತ್ತು ಅವನಿಗೆ (ಕೈ, ಕೊರಳಿಗೆ) ಬೇಡಿ ತೊಡಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(69,31,'69:31','ಅನಂತರ ಅವನನ್ನು ನರಕದೊಳಕ್ಕೆ ಪೂರ್ಣವಾಗಿ ಮುಳುಗಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(69,32,'69:32','ಆ ಬಳಿಕ ಅವನನ್ನು ಎಪ್ಪತ್ತು ಮೊಳ ಉದ್ದದ ಸಂಕೋಲೆಯೊಳಕ್ಕೆ ಬಿಗಿಯಿರಿ.');
INSERT INTO "kannada" VALUES(69,33,'69:33','ಅವನು ಮಹಾನನಾದ ಅಲ್ಲಾಹನ ಮೇಲೆ ವಿಶ್ವಾ ಸವಿರಿಸುತ್ತಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(69,34,'69:34','ಮತ್ತು ಬಡವರಿಗೆ (ಮಿಸ್ಕೀನರಿಗೆ) ಆಹಾರ ಕೊಡಲು ಪ್ರೇರೇಪಿಸುತ್ತಲೂ ಇರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(69,35,'69:35','ಆದುದರಿಂದ ಇಂದು ಅವನಿಗೆ ಇಲ್ಲಿ ಬಂಧುಗಳು ಯಾರೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(69,36,'69:36','ಗಿಸ್ಲೀನ್ (ನರಕದಲ್ಲಿ ಮಡುಗಟ್ಟುವ ರಕ್ತ, ಕೀವು, ಕೊಳಚೆ ನೀರುಗಳ ಸಂಗ್ರಹ) ಹೊರತು ಅವನಿಗೆ ಬೇರೆ ಯಾವ ಆಹಾರವೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(69,37,'69:37','ಅದನ್ನು ಪಾಪಿಗಳ ಹೊರತು ಇನ್ನಾರೂ ತಿನ್ನುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(69,38,'69:38','');
INSERT INTO "kannada" VALUES(69,39,'69:39','ನೀವು ಕಾಣುವ ವಸ್ತುಗಳ ಮತ್ತು ಕಾಣದ ವಸ್ತುಗಳ ಆಣೆ ಹಾಕುತ್ತೇನೆ!');
INSERT INTO "kannada" VALUES(69,40,'69:40','ಇದು (ಖುರ್‍ಆನ್) ಓರ್ವ ಗೌರವಾನ್ವಿತ ದೂತರ ಮಾತಾಗಿದೆ .');
INSERT INTO "kannada" VALUES(69,41,'69:41','(ಇದು) ಯಾವುದೇ ಒಂದು ಕವಿಯ ಮಾತಲ್ಲ. ನೀವು ಸ್ವಲ್ಪ ಮಾತ್ರವೇ ವಿಶ್ವಾಸ ಹೊಂದಿರುತ್ತೀರಿ.');
INSERT INTO "kannada" VALUES(69,42,'69:42','ಇದೊಂದು ಜ್ಯೋತಿಷಿಯ ಮಾತೂ ಅಲ್ಲ. ನೀವು ಅತ್ಯಲ್ಪವೇ ಯೋಚಿಸಿ ಅರ್ಥ ಮಾಡಿಕೊಳ್ಳುತ್ತೀರಿ.');
INSERT INTO "kannada" VALUES(69,43,'69:43','ಇದು ಸರ್ವಲೋಕ ಪಾಲಕ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ ಅವತೀರ್ಣಗೊಂಡಿದೆ.');
INSERT INTO "kannada" VALUES(69,44,'69:44','ಪ್ರವಾದಿಯು ನಮ್ಮ ಮೇಲೆ (ನಾವು ಹೇಳದ) ಯಾವುದೇ ಮಾತುಗಳನ್ನು ಸ್ವಂತದ್ದಾಗಿ ರಚಿಸಿ ಹೇಳಿರುತ್ತಿದ್ದರೆ,');
INSERT INTO "kannada" VALUES(69,45,'69:45','ಖಂಡಿತ ನಾವು ನಮ್ಮ ಬಲದಿಂದ ಅವರಿಗೆ ಶಿಕ್ಷೆ ಯನ್ನು ಕೊಡುತ್ತಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(69,46,'69:46','ಮತ್ತು ಅವರ ಹೃದಯದ ಜೀವನಾಡಿಯನ್ನು ಕತ್ತರಿಸಿ ಹಾಕುತ್ತಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(69,47,'69:47','ಆಗ, ನಿಮ್ಮ ಪೈಕಿ ಯಾರಿಗೂ ಅವರಿಂದ ನನ್ನ ಶಿಕ್ಷೆಯನ್ನು ತಡೆಯಲು ಸಾಧ್ಯವಿರುತ್ತಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(69,48,'69:48','ಇದು (ಖುರ್‍ಆನ್) ದೇವಭಯವುಳ್ಳವರಿ ಗೊಂದು ಉಪದೇಶವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(69,49,'69:49','ನಿಮ್ಮ ಪೈಕಿ ಇದನ್ನು (ಖುರ್‍ಆನನ್ನು) ನಿಷೇಧಿಸಿ ತಳ್ಳುವವರಿದ್ದಾರೆಂಬುದನ್ನು ನಾವು ಬಲ್ಲೆವು.');
INSERT INTO "kannada" VALUES(69,50,'69:50','ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಖಂಡಿತವಾಗಿಯೂ ಇದು ಖೇದವೇ ಆಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(69,51,'69:51','ಖಂಡಿತ ಇದು ಅತ್ಯಂತ ಖಚಿತವಾದ ಸತ್ಯವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(69,52,'69:52','ಆದುದರಿಂದ (ಸಂದೇಶವಾಹಕರೇ,) ತಾವು ತಮ್ಮ ಮಹೋನ್ನತ ಪ್ರಭುವಿನ ನಾಮವನ್ನು ಕೊಂಡಾಡಿರಿ.');
INSERT INTO "kannada" VALUES(70,1,'70:1','');
INSERT INTO "kannada" VALUES(70,2,'70:2','ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಸಂಭವಿಸಲಿಕ್ಕಿರುವ ಒಂದು ಶಿಕ್ಷೆಯ ಕುರಿತು ಪ್ರಶ್ನಿಸುವವನೊಬ್ಬನು ಪ್ರಶ್ನಿಸಿದ್ದಾನೆ. (ಸಂಭವಿಸಲಿರುವ) ಅದನ್ನು ತಡೆಯ ಬಲ್ಲವರು ಯಾರೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(70,3,'70:3','ಆರೋಹಣ ಪಥಗಳ ಒಡೆಯನಾದ ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ. (ಆ ಶಿಕ್ಷೆ ಸಂಭವಿಸುವುದು)');
INSERT INTO "kannada" VALUES(70,4,'70:4','ದೇವಚರರು ಮತ್ತು ರೂಹ್ ಅವನ ಸಾನಿಧ್ಯಕ್ಕೆ ಏರಿ ಹೋಗುತ್ತಾರೆ . ಐವತ್ತು ಸಾವಿರ ವರುಷಗಳ ಅವಧಿಯುಳ್ಳ ಒಂದು ದಿನದಲ್ಲಿ. (ಆ ಶಿಕ್ಷೆ ಸಂಭವಿಸಲಿದೆ) ');
INSERT INTO "kannada" VALUES(70,5,'70:5','ಆದುದರಿಂದ (ಸಂದೇಶವಾಹಕರೇ,) ತಾವು ತಾಳ್ಮೆ ವಹಿಸಿರಿ, ಸುಂದರವಾದ ತಾಳ್ಮೆ.');
INSERT INTO "kannada" VALUES(70,6,'70:6','ಅವರು ಅದನ್ನು ವಿದೂರವಾಗಿ ಕಾಣುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(70,7,'70:7','ನಾವು ಅದನ್ನು ಸಮೀಪದಲ್ಲಿ ಕಾಣುತ್ತಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(70,8,'70:8','ಆ ದಿನ ಆಕಾಶವು ಕರಗಿದ ಲೋಹದಂತಾಗುವುದು.');
INSERT INTO "kannada" VALUES(70,9,'70:9','ಮತ್ತು ಪರ್ವತಗಳು ಹಿಂಜಿದ ಉಣ್ಣೆರೋಮದಂತಾಗುವುವು.');
INSERT INTO "kannada" VALUES(70,10,'70:10','ಅಂದು ಯಾವ ಆಪ್ತ ಬಂಧುವೂ ತನ್ನ ಆಪ್ತ ಬಂಧುವಿನಲ್ಲಿ ಏನನ್ನೂ ಕೇಳಲಾರನು.');
INSERT INTO "kannada" VALUES(70,11,'70:11','');
INSERT INTO "kannada" VALUES(70,12,'70:12','');
INSERT INTO "kannada" VALUES(70,13,'70:13','');
INSERT INTO "kannada" VALUES(70,14,'70:14','ಅವರು ಪರಸ್ಪರರಿಗೆ ತೋರಿಸಲ್ಪಡುವರು. (ಆದರೂ ಅವರು ಪರಸ್ಪರ ಕೇಳಲಾರರು) ಅಪರಾಧಿಯು ತನ್ನ ಮಕ್ಕಳನ್ನು, ತನ್ನ ಪತ್ನಿ ಯನ್ನು, ತನ್ನ ಸಹೋದರರನ್ನು, ತನಗೆ ಆಶ್ರಯ ನೀಡುತ್ತಿದ್ದ ತನ್ನ ಅತ್ಯಂತ ಆಪ್ತ ಕುಟುಂಬವನ್ನು ಮತ್ತು ಭೂಮುಖದಲ್ಲಿರುವ ಎಲ್ಲ ಜನರನ್ನೂ ಪ್ರಾಯಶ್ಚಿತ್ತವಾಗಿ ಕೊಟ್ಟು ಆ ದಿನದ ಶಿಕ್ಷೆಯಿಂದ ಪಾರಾಗಬಯಸುವನು.');
INSERT INTO "kannada" VALUES(70,15,'70:15','ಅದು (ಆ ಮೋಹ) ಬೇಡ. ಅದು ಧಗಧಗಿಸುವ ನರಕವಾಗಿರುವುದು.');
INSERT INTO "kannada" VALUES(70,16,'70:16','ತಲೆಯ ಚರ್ಮವನ್ನು ಸುಲಿಯುವ ಅಗ್ನಿ!');
INSERT INTO "kannada" VALUES(70,17,'70:17','');
INSERT INTO "kannada" VALUES(70,18,'70:18','ಸತ್ಯದಿಂದ ವಿಮುಖನಾದ, ಬೆನ್ನು ತಿರುಗಿಸಿದ, ಸಂಪತ್ತನ್ನು ಕಲೆ ಹಾಕಿ (ಅದನ್ನು ದಾನ ನೀಡದೆ) ಜೋಪಾನವಾಗಿ ಸಂಗ್ರಹಿಸಿಟ್ಟ ಪ್ರತಿಯೊಬ್ಬನನ್ನೂ ಅದು ತನ್ನ ಕಡೆಗೆ ಕೂಗಿ ಕರೆಯುವುದು.');
INSERT INTO "kannada" VALUES(70,19,'70:19','ಮಾನವನು ಬಹಳ ಅಕ್ಷಮನಾಗಿಯೂ ಅತ್ಯಾ ಗ್ರಹಿಯಾಗಿಯೂ ಸೃಷ್ಟಿಸಲ್ಪಟ್ಟಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(70,20,'70:20','ಅವನಿಗೆ ಸಂಕಷ್ಟ ಬಂದಾಗ ಕಳವಳಗೊಳ್ಳುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(70,21,'70:21','ಮತ್ತು ಸುಖ ಬಂದಾಗ (ದಾನ ನೀಡದೆ) ಲೋಭ ತೋರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(70,22,'70:22','ಆದರೆ ನಮಾಝ್ ಮಾಡುವವರ ಹೊರತು.');
INSERT INTO "kannada" VALUES(70,23,'70:23','ಅವರು ತಮ್ಮ ನಮಾಝನ್ನು ತಪ್ಪದೆ ನಿರ್ವಹಿಸುವರು.');
INSERT INTO "kannada" VALUES(70,24,'70:24','ಅವರ ಸಂಪತ್ತುಗಳಲ್ಲಿ ಒಂದು ನಿಶ್ಚಿತ ಹಕ್ಕಿದೆ.');
INSERT INTO "kannada" VALUES(70,25,'70:25','ಬೇಡುವವನಿಗೆ ಮತ್ತು (ಬೇಡದ ಕಾರಣದಿಂದ) ತಡೆಯಲ್ಪಟ್ಟವನಿಗೆ.');
INSERT INTO "kannada" VALUES(70,26,'70:26','ಅವರು ಪ್ರತಿಫಲ ದಿನವನ್ನು ಸತ್ಯವೆಂದು ಒಪ್ಪಿಕೊಳ್ಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(70,27,'70:27','ಅವರು ತಮ್ಮ ಪ್ರಭುವಿನ ಶಿಕ್ಷೆಯನ್ನು ಭಯಪಡುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(70,28,'70:28','ಅವರ ಪ್ರಭುವಿನ ಶಿಕ್ಷೆಯು (ಸಂಭವಿಸದೆಂದು) ನಿರ್ಭೀತಿಯಿಂದಿರುವಂತಹದ್ದಲ್ಲ.');
INSERT INTO "kannada" VALUES(70,29,'70:29','ಅವರು ತಮ್ಮ ಗುಪ್ತಾಂಗಗಳನ್ನು ರಕ್ಷಿಸಿಕೊಳ್ಳುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(70,30,'70:30','ತಮ್ಮ ಪತ್ನಿಯರ ಮತ್ತು ತಮ್ಮ ದಾಸಿಯರ ಹೊರತು. ಅದರಿಂದ ಅವರು ಆಕ್ಷೇಪಾರ್ಹರಲ್ಲ.');
INSERT INTO "kannada" VALUES(70,31,'70:31','ಆದರೆ, ಅದಕ್ಕಿಂತ ಆಚೆ ಯಾರಾದರೂ ಬಯಸುತ್ತಾರಾದರೆ ಅವರು ಮೇರೆ ಮೀರಿದವರು.');
INSERT INTO "kannada" VALUES(70,32,'70:32','ಅವರು ತಮ್ಮಲ್ಲಿರಿಸಿದ ಅಮಾನತುಗಳನ್ನು ಮತ್ತು ತಮ್ಮ ವಾಗ್ದಾನಗಳನ್ನು ಪಾಲಿಸುವವರೂ ಆಗಿರುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(70,33,'70:33','ಅವರು ತಮ್ಮ ಸಾಕ್ಷ್ಯಗಳನ್ನು ಸರಿಯಾಗಿ ನಿರ್ವಹಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(70,34,'70:34','ಮತ್ತು ತಮ್ಮ ನಮಾಝ್‍ಗಳಲ್ಲಿ ಅವರು ಸೂಕ್ಷ್ಮತೆ ಪಾಲಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(70,35,'70:35','ಇವರು (ಇಂತಹವರು) ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ ಸನ್ಮಾನಿಸಲ್ಪಡುವರು.');
INSERT INTO "kannada" VALUES(70,36,'70:36','');
INSERT INTO "kannada" VALUES(70,37,'70:37','ಆದುದರಿಂದ (ಸಂದೇಶವಾಹಕರೇ,) ಈ ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಏನಾಗಿದೆ? ಅವರು ನಿಮ್ಮತ್ತ ದೀರ್ಘ ನೋಟ ಬೀರುತ್ತಾರೆ. ತಮ್ಮ ಎಡಬಲಗಳಿಂದ ಗುಂಪುಗುಂಪಾಗಿ ಚದುರುತ್ತಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(70,38,'70:38','ಅವರಲ್ಲಿ ಪ್ರತಿಯೊಬ್ಬನೂ ತಾನು ಸುಖಾನು ಭೂತಿಯ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿ ಪ್ರವೇಶ ಗೊಳಿಸಲ್ಪಡುವೆನೆಂದು ಮೋಹಿಸುತ್ತಿರುವನೇ?');
INSERT INTO "kannada" VALUES(70,39,'70:39','ಖಂಡಿತ ಅದು ಬೇಡ. ಅವರು ಬಲ್ಲ ವಸ್ತುವಿನಿಂದಲೇ ನಾವು ಅವರನ್ನು ಸೃಷ್ಟಿಸಿರುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(70,40,'70:40','');
INSERT INTO "kannada" VALUES(70,41,'70:41','ಉದಯ ಸ್ಥಾನಗಳ ಮತ್ತು ಅಸ್ತಮ ಸ್ಥಾನಗಳ ಒಡೆಯನ ಆಣೆ ಹಾಕುತ್ತೇನೆ. ಖಂಡಿತ ಅವರ ಸ್ಥಾನದಲ್ಲಿ ಅವರಿಗಿಂತ ಉತ್ತಮರನ್ನು ತರಲು ನಮಗೆ ಸಾಮಥ್ರ್ಯವಿದೆ. ನಾವು ಖಂಡಿತ ಪರಾಜಯಗೊಳ್ಳಲಾರೆವು.');
INSERT INTO "kannada" VALUES(70,42,'70:42','ಆದುದರಿಂದ (ಪ್ರವಾದಿಯವರೇ) ಅವರಿಗೆ ತಾಕೀತು ನೀಡಲಾದ ಅವರ ಆ ದಿನಕ್ಕೆ ಹೋಗಿ ಮುಟ್ಟುವವರೆಗೂ ಅವರ ಪಾಪದಲ್ಲಿ ಮುಳುಗಲು ಮತ್ತು ಆಟವಾಡಿಕೊಂಡಿರಲು ಅವರನ್ನು ಬಿಟ್ಟು ಬಿಡಿರಿ.');
INSERT INTO "kannada" VALUES(70,43,'70:43','ಅವರು ಗೋರಿಗಳಿಂದ ಹೊರಬಂದು ಧಾವಿಸುವ ಆ ದಿನ. ಅವರು ಒಂದು ನಾಟೆ ಗುರಿಯತ್ತ ಓಡುತ್ತಿರುವಂತಿರುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(70,44,'70:44','ಅವರ ಕಣ್ಣುಗಳು ಭಯವಿಹ್ವಲವಾಗಿರುವುವು. ನಿಂದ್ಯತೆಯು ಅವರನ್ನು ಆವರಿಸಿಕೊಂಡಿರುವುದು. ಅದುವೇ ಅವರಿಗೆ ಮುನ್ನೆಚ್ಚರಿಕೆ ನೀಡಲಾಗುತ್ತಿದ್ದ ದಿನ.');
INSERT INTO "kannada" VALUES(71,1,'71:1','ನೂಹರನ್ನು ಅವರ ಜನಾಂಗದ ಕಡೆಗೆ ನಾವು ಕಳುಹಿಸಿದೆವು. ``ನಿನ್ನ ಜನಾಂಗದವರಿಗೆ ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆ ಬಂದೆರಗುವುದಕ್ಕೆ ಮುಂಚೆಯೇ ಅವರಿಗೆ ಮುನ್ನೆಚ್ಚರಿಕೆ ನೀಡು’’ ಎಂಬ ಆದೇಶದೊಂದಿಗೆ.');
INSERT INTO "kannada" VALUES(71,2,'71:2','ಅವರು ಹೇಳಿದರು; `ನನ್ನ ಜನಾಂಗದವರೇ, ನಾನು ನಿಮಗೆ ಸ್ಪಷ್ಟ ಎಚ್ಚರಿಕೆ ಕೊಡುವವನಾಗಿದ್ದೇನೆ.');
INSERT INTO "kannada" VALUES(71,3,'71:3','ನೀವು ಅಲ್ಲಾಹನನ್ನು ಆರಾಧಿಸಿರಿ, ಅವನನ್ನು ಭಯಪಡಿರಿ ಮತ್ತು ನನ್ನನ್ನು ಅನುಸರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(71,4,'71:4','ಹಾಗಾದರೆ ಅಲ್ಲಾಹನು ನಿಮ್ಮ ಪಾಪಗಳನ್ನು ಕ್ಷಮಿಸುವನು ಮತ್ತು ನಿಮ್ಮನ್ನು ಒಂದು ನಿಶ್ಚಿತ ಕಾಲದವರೆಗೆ ಮುಂದೂಡುವನು. ಅಲ್ಲಾಹನ ಅವಧಿ ಬಂದು ಬಿಟ್ಟರೆ ಮತ್ತೆ ಅದನ್ನು ಮುಂದೂಡಲಾಗುವುದಿಲ್ಲ. ನಿಮಗಿದು ತಿಳಿದಿರುತ್ತಿದ್ದರೆ!');
INSERT INTO "kannada" VALUES(71,5,'71:5','ಅವರು (ನೂಹ್) ಹೇಳಿದರು; `ನನ್ನ ಪ್ರಭೂ, ನಾನು ನನ್ನ ಜನಾಂಗದವರನ್ನು ರಾತ್ರಿಯೂ ಹಗಲೂ (ನಿನ್ನ ಮಾರ್ಗಕ್ಕೆ) ಕರೆದೆನು.');
INSERT INTO "kannada" VALUES(71,6,'71:6','ಆದರೆ ನನ್ನ ಕರೆಯು ಅವರ ಪಲಾಯನವನ್ನೇ ಹೆಚ್ಚಿಸಿತು.');
INSERT INTO "kannada" VALUES(71,7,'71:7','ನೀನು ಅವರನ್ನು ಕ್ಷಮಿಸಲೆಂದು ನಾನು ಅವರನ್ನು ಕರೆದಾಗಲೆಲ್ಲ ಅವರು ತಮ್ಮ ಕಿವಿಗಳಲ್ಲಿ ಬೆರಳುಗಳನ್ನು ತುರುಕಿಸಿಕೊಂಡರು, ತಮ್ಮ ವಸ್ತ್ರಗಳಿಂದ (ಮುಖ) ಮುಚ್ಚಿಕೊಂಡರು, (ಅಸತ್ಯದಲ್ಲಿ) ಊರಿ ನಿಂತರು. ಮತ್ತು ಭಾರೀ ಅಹಂಭಾವ ತೋರಿದರು.');
INSERT INTO "kannada" VALUES(71,8,'71:8','ತರುವಾಯ ನಾನು ಅವರಿಗೆ ಜೋರಾಗಿ ಕರೆಕೊಟ್ಟೆನು.');
INSERT INTO "kannada" VALUES(71,9,'71:9','ಅನಂತರ ನಾನು ಅವರಿಗಾಗಿ ಬಹಿರಂಗ ಆಹ್ವಾನ ನೀಡಿದೆನು ಮತ್ತು ಬಹಳ ರಹಸ್ಯವಾಗಿಯೂ ಬೋಧಿಸಿದೆನು.');
INSERT INTO "kannada" VALUES(71,10,'71:10','ನಾನು ಹೇಳಿದೆನು; ನಿಮ್ಮ ಪ್ರಭುವಿನಲ್ಲಿ ನೀವು ಕ್ಷಮೆಯನ್ನು ಯಾಚಿಸಿರಿ. ಖಂಡಿತವಾಗಿಯೂ ಅವನು ಮಹಾಕ್ಷಮಾಶೀಲನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(71,11,'71:11','ಹಾಗಾದಲ್ಲಿ ನಿಮಗೆ ಅವನು ಆಕಾಶದಿಂದ ಸಮೃದ್ಧವಾಗಿ ಮಳೆ ಸುರಿಸಿ ಕೊಡುವನು.');
INSERT INTO "kannada" VALUES(71,12,'71:12','ನಿಮಗೆ ಸೊತ್ತುಗಳನ್ನೂ ಸಂತಾನಗಳನ್ನೂ ಕೊಟ್ಟು ಪೋಷಿಸುವನು, ನಿಮಗಾಗಿ ತೋಟ ಗಳನ್ನೂ ನದಿಗಳನ್ನೂ ನಿರ್ಮಿಸಿ ಕೊಡುವನು.');
INSERT INTO "kannada" VALUES(71,13,'71:13','ಅಲ್ಲಾಹು ನಿಮ್ಮನ್ನು ಗೌರವಿಸುವುದನ್ನು ಯಾಕೆ ನೀವು ಆಗ್ರಹಪಡುವುದಿಲ್ಲ?');
INSERT INTO "kannada" VALUES(71,14,'71:14','ಅವನು ನಿಮ್ಮನ್ನು ವಿವಿಧ ಹಂತಗಳಲ್ಲಿ ಸೃಷ್ಟಿಸಿರುವನು .');
INSERT INTO "kannada" VALUES(71,15,'71:15','ನೀವು ನೋಡುತ್ತಿಲ್ಲವೇ? ಅಲ್ಲಾಹನು ಸಪ್ತಗಗನಗಳನ್ನು (ಒಂದರ ಮೇಲೆ ಒಂದರಂತೆ) ಅಂತಸ್ತುಗಳಾಗಿ ಹೇಗೆ ಸೃಷ್ಟಿಸಿದನು?');
INSERT INTO "kannada" VALUES(71,16,'71:16','ಮತ್ತು ಅವುಗಳಲ್ಲಿ ಚಂದ್ರನನ್ನು ಒಂದು ಪ್ರಕಾಶವಾಗಿಯೂ ಸೂರ್ಯನನ್ನು ಒಂದು ಬೆಳಕಾಗಿಯೂ ಮಾಡಿರುವನು.');
INSERT INTO "kannada" VALUES(71,17,'71:17','ಅಲ್ಲಾಹನು ನಿಮ್ಮನ್ನು ಭೂಮಿಯಿಂದ ಬೆಳೆ ಬೆಳೆಸಿದನು .');
INSERT INTO "kannada" VALUES(71,18,'71:18','ಮುಂದೆ ಅವನು ನಿಮ್ಮನ್ನು ಅದಕ್ಕೆ ಮರಳಿ ಸುವನು ಮತ್ತು ಅದರಿಂದ ಒಮ್ಮೆಲೇ ನಿಮ್ಮನ್ನು ಹೊರತೆಗೆಯುವನು.');
INSERT INTO "kannada" VALUES(71,19,'71:19','ಅಲ್ಲಾಹನು ನಿಮಗಾಗಿ ಭೂಮಿಯನ್ನು ಒಂದು ಹಾಸನ್ನಾಗಿ ಮಾಡಿದನು.');
INSERT INTO "kannada" VALUES(71,20,'71:20','ನೀವು ಅದರಲ್ಲಿ ವಿಶಾಲವಾದ ಮಾರ್ಗಗಳಲ್ಲಿ ಪ್ರವೇಶಿಸುವಂತಾಗಲು.');
INSERT INTO "kannada" VALUES(71,21,'71:21','ನೂಹ್ ಹೇಳಿದರು; `ನನ್ನ ಪ್ರಭೂ, ಅವರು ನನ್ನನ್ನು ಧಿಕ್ಕರಿಸಿದರು. ಸಂಪತ್ತು ಹಾಗೂ ಸಂತಾನಗಳನ್ನು ಪಡೆದು ನಷ್ಟವನ್ನು ಇನ್ನಷ್ಟು ಹೆಚ್ಚಿಸಿಕೊಂಡಿರುವ ಕೆಲವರನ್ನು ಅವರು ಅನುಸರಿಸಿದರು.');
INSERT INTO "kannada" VALUES(71,22,'71:22','ಅವರು ಬಹುದೊಡ್ಡ ಕುತಂತ್ರವನ್ನು ಹೂಡಿದರು.');
INSERT INTO "kannada" VALUES(71,23,'71:23','ಅವರು, `(ಜನರೇ) ನೀವು ಖಂಡಿತ ನಿಮ್ಮ ದೈವಗಳನ್ನು ತ್ಯಜಿಸಬೇಡಿರಿ. ವದ್ದ್, ಸುವಾಅï, ಯಗೂಸ್, ಯಊಖ್ ಮತ್ತು ನಸ್ರ್‍ಗಳನ್ನು ನೀವು ಖಂಡಿತ ಉಪೇಕ್ಷಿಸದಿರಿ’ ಎಂದು ಹೇಳಿದರು.');
INSERT INTO "kannada" VALUES(71,24,'71:24','ಅವರು ಅನೇಕ ಮಂದಿಯನ್ನು ದಾರಿಗೆಡಿಸಿದ್ದಾರೆ. (ಆದುದರಿಂದ ಪ್ರಭೂ!) ನೀನು ಈ ಅಕ್ರಮಿಗಳಿಗೆ ಪಥಭ್ರಷ್ಟತೆಯಲ್ಲದೆ ಬೇರೇನನ್ನೂ ವರ್ಧಿಸಿ ಕೊಡಬೇಡ.');
INSERT INTO "kannada" VALUES(71,25,'71:25','ತಮ್ಮ ಪಾಪಗಳ ಕಾರಣದಿಂದಾಗಿಯೇ ಅವರು ನೀರಲ್ಲಿ ಮುಳುಗಿಸಲ್ಪಟ್ಟರು ಮತ್ತು ನರಕಾಗ್ನಿಗೆ ಸೇರಿಸಲ್ಪಟ್ಟರು. ಅನಂತರ ಅವರಿಗೆ, ಅಲ್ಲಾಹನ ಹೊರತು ಸಹಾಯಕನಾರೂ ಲಭಿಸಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(71,26,'71:26','ನೂಹ್ ಹೇಳಿದರು; `ನನ್ನ ಪ್ರಭೂ, ಸತ್ಯನಿಷೇಧಿಗಳ ಪೈಕಿ ಯಾವನೇ ಗೃಹಸ್ಥನನ್ನು (ಯಾರನ್ನೂ) ಈ ಭೂಮಿಯ ಮೇಲೆ ನೆಲೆಸಲು ಬಿಡಬೇಡ.');
INSERT INTO "kannada" VALUES(71,27,'71:27','ನೀನು ಅವರನ್ನು ನಾಶಪಡಿಸದೆ ಹಾಗೆ ಬಿಟ್ಟರೆ, ಅವರು ನಿನ್ನ ದಾಸರನ್ನು ದಾರಿಗೆಡಿಸುವರು. ಮತ್ತು ಸತ್ಯನಿಷೇಧಿಗಳಾದ ದುರಾಚಾರಿ ಸಂತತಿಗಳನ್ನಲ್ಲದೆ ಅವರು ಹುಟ್ಟುಹಾಕಲಾರರು.');
INSERT INTO "kannada" VALUES(71,28,'71:28','ನನ್ನ ಪ್ರಭೂ, ನನಗೂ, ನನ್ನ ಮಾತಾಪಿತರಿಗೂ ಸತ್ಯವಿಶ್ವಾಸಿಯಾಗಿ ನನ್ನ ಮನೆಗೆ ಪ್ರವೇಶಿಸಿದ ಪ್ರತಿಯೊಬ್ಬರಿಗೂ ಮತ್ತು ಎಲ್ಲ ಸತ್ಯವಿಶ್ವಾಸಿ ಸ್ತ್ರೀ - ಪುರುಷರಿಗೂ ನೀನು ಕ್ಷಮೆ ನೀಡು. ಅಕ್ರಮಿಗಳಿಗೆ ವಿನಾಶದ ಹೊರತು ಇನ್ನೇನನ್ನೂ ವರ್ಧಿಸ ಬೇಡ.');
INSERT INTO "kannada" VALUES(72,1,'72:1','(ಸಂದೇಶವಾಹಕರೇ,) ಹೇಳಿರಿ, ನನಗೆ ಹೀಗೆ ದಿವ್ಯ ಸಂದೇಶ ದೊರೆತಿದೆ- ಜಿನ್ನ್‍ಗಳ ಒಂದು ತಂಡವು ಖುರ್‍ಆನನ್ನು ಶ್ರದ್ಧಾಪೂರ್ವಕ ಆಲಿಸಿತು ತರುವಾಯ (ಸ್ವಂತ ಸಮೂಹದೊಡನೆ) ಹೀಗೆ ಹೇಳಿತು `ನಾವು ಒಂದು ಅತ್ಯದ್ಭುತ ಖುರ್‍ಆನನ್ನು ಆಲಿಸಿದ್ದೇವೆ.’');
INSERT INTO "kannada" VALUES(72,2,'72:2','ಅದು (ಖುರ್‍ಆನ್) ಸನ್ಮಾರ್ಗದ ಕಡೆಗೆ ಮಾರ್ಗದರ್ಶನ ನೀಡುತ್ತದೆ. ಆದುದರಿಂದ ನಾವು ಅದರ ಮೇಲೆ ವಿಶ್ವಾಸವಿರಿಸಿದೆವು. ಇನ್ನು ಮೇಲೆ ನಮ್ಮ ಪ್ರಭುವಿನೊಂದಿಗೆ ಯಾರನ್ನೂ ನಾವು ಸಹಭಾಗಿಯಾಗಿ ಮಾಡಲಾರೆವು.');
INSERT INTO "kannada" VALUES(72,3,'72:3','ನಮ್ಮ ಪ್ರಭುವಿನ ಮಹತ್ವವು ಅತ್ಯುನ್ನತವಾಗಿದೆ. ಅವನು ಯಾರನ್ನೂ ತನ್ನ ಸಂಗಾತಿ ಅಥವಾ ಪುತ್ರನಾಗಿ ಮಾಡಿಕೊಂಡಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(72,4,'72:4','ಅಲ್ಲದೆ, ನಮ್ಮ ಕೂಟದಲ್ಲಿರುವ ತಿಳಿಗೇಡಿಗಳು ಅಲ್ಲಾಹನ ಕುರಿತು ಘೋರ ಅತಿರೇಕವನ್ನು ಹೇಳುತ್ತಿದ್ದರು .');
INSERT INTO "kannada" VALUES(72,5,'72:5','ಜಿನ್ನ್‍ಗಳು ಮತ್ತು ಮಾನವರು ಅಲ್ಲಾಹನ ಹೆಸರಲ್ಲಿ ಎಂದೂ ಸುಳ್ಳು ಹೇಳಲಾರರೆಂದು ನಾವು ಭಾವಿಸಿಕೊಂಡಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(72,6,'72:6','ಮಾನವರ ಪೈಕಿ ಕೆಲವು ವ್ಯಕ್ತಿಗಳು ಜಿನ್ನ್‍ಗಳ ಪೈಕಿ ಕೆಲವು ವ್ಯಕ್ತಿಗಳಲ್ಲಿ ಅಭಯ ಬೇಡುತ್ತಿದ್ದರು. ಹೀಗೆ ಅವರು ಜಿನ್ನ್‍ಗಳ ಅಹಂಕಾರವನ್ನು ಇನ್ನಷ್ಟು ಹೆಚ್ಚಿಸಿದರು.');
INSERT INTO "kannada" VALUES(72,7,'72:7','ಅವರು (ಮಾನವರು) ಕೂಡಾ ನೀವು ಭಾವಿಸಿದಂತೆಯೇ ಅಲ್ಲಾಹನು ಯಾರನ್ನೂ ಪುನರುಜ್ಜೀವಗೊಳಿಸಲಾರನೆಂದು ಭಾವಿಸಿ ಕೊಂಡಿದ್ದರು.');
INSERT INTO "kannada" VALUES(72,8,'72:8','ನಾವು ಆಕಾಶವನ್ನು ಸ್ಪರ್ಶಿಸಿ ನೋಡಿದಾಗ ಅದು ಸಶಕ್ತ ಕಾವಲುಗಾರರಿಂದ ಮತ್ತು ಅಗ್ನಿಜ್ವಾಲೆಗಳಿಂದ ತುಂಬಿರುವುದನ್ನು ಕಂಡೆವು.');
INSERT INTO "kannada" VALUES(72,9,'72:9','ಹಿಂದೆ ನಾವು ಕದ್ದಾಲಿಸಲಿಕ್ಕಾಗಿ ಆಕಾಶದ ಕೆಲವು ಕಡೆಗಳಲ್ಲಿ ಕುಳಿತುಕೊಳ್ಳುತ್ತಿದ್ದೆವು. ಆದರೆ ಈಗ ಯಾವನಾದರೂ ಕದ್ದಾಲಿಸಲು ಪ್ರಯತ್ನಿಸುವು ದಾದರೆ ಅವನು ತನಗಾಗಿ ಹೊಂಚು ಹಾಕುತ್ತಿರುವ ಒಂದು ಅಗ್ನಿಜ್ವಾಲೆ (ಉಲ್ಕೆ)ಯನ್ನು ಕಾಣುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(72,10,'72:10','ಭೂಮಿಯ ನಿವಾಸಿಗಳಿಗೆ ಕೆಡುಕನ್ನು ಉದ್ದೇಶಿ ಸಲಾಗಿದೆಯೋ? ಅಥವಾ ಅವರ ಪ್ರಭುವು ಅವರಿಗೆ ಸನ್ಮಾರ್ಗವನ್ನು ತೋರಿಸ ಬಯಸಿರುವನೋ? ಎಂದೇನೂ ನಮಗೆ ತಿಳಿಯದು.');
INSERT INTO "kannada" VALUES(72,11,'72:11','ನಮ್ಮ ಕೂಟದಲ್ಲಿ ಸಜ್ಜನರಿದ್ದಾರೆ ಮತ್ತು ಅದರ ಕೆಳಗಿನವರೂ ನಮ್ಮಲ್ಲಿದ್ದಾರೆ. ನಾವು ನಾನಾ ದಾರಿಗಳಲ್ಲಿ ಹರಿಹಂಚಿ ಹೋಗಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(72,12,'72:12','ನಮಗೆ ಭೂಮಿಯಲ್ಲಿ ಅಲ್ಲಾಹನನ್ನು ಸೋಲಿಸಲಾಗದು ಮತ್ತು ನಾವು ತಪ್ಪಿಸಿ ಓಡಿ ಹೋಗಿಯೂ ಅವನನ್ನು ಸೋಲಿಸಲಾರೆವು ಎಂದು ನಾವು ಖಚಿತಪಡಿಸಿಕೊಂಡಿರುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(72,13,'72:13','ನಾವು ಸನ್ಮಾರ್ಗ (ಖುರ್‍ಆನ್)ವನ್ನು ಕೇಳಿದಾಗ ಅದರಲ್ಲಿ ವಿಶ್ವಾಸವಿರಿಸಿದೆವು. ತನ್ನ ಪ್ರಭುವಿನ ಮೇಲೆ ಯಾವನು ವಿಶ್ವಾಸವಿರಿಸುವನೋ ಅವನು ಅಲ್ಲಾಹನಿಂದ ಯಾವುದೇ ನಷ್ಟವನ್ನೂ, ಅನೀತಿಯನ್ನೂ ಭಯಪಡಬೇಕಾಗಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(72,14,'72:14','ನಮ್ಮ ಕೂಟದಲ್ಲಿ ವಿಧೇಯರು (ಮುಸ್ಲಿಮರು) ಇದ್ದಾರೆ. ಅನ್ಯಾಯಗಾರರೂ ನಮ್ಮಲ್ಲಿ ಇದ್ದಾರೆ. ಯಾರು ವಿಧೇಯರಾಗುತ್ತಾರೋ (ಇಸ್ಲಾಮನ್ನು ಸ್ವೀಕರಿಸಿಕೊಳ್ಳುತ್ತಾರೋ) ಅವರು ಸತ್ಪಥವನ್ನು ಕಂಡುಕೊಂಡರು.');
INSERT INTO "kannada" VALUES(72,15,'72:15','ಯಾರು ಅನ್ಯಾಯಗಾರರೋ ಅವರು ನರಕದ ಇಂಧನವಾಗುವರು.');
INSERT INTO "kannada" VALUES(72,16,'72:16','(ಅಲ್ಲಾಹು ಹೇಳುತ್ತಾನೆ) ಅವರು ಸನ್ಮಾರ್ಗದಲ್ಲಿ ಸ್ಥಿರಚಿತ್ತರಾಗಿ ನಿಲ್ಲುತ್ತಿದ್ದರೆ, ನಾವು ಅವರಿಗೆ ಕುಡಿಯಲು ಧಾರಾಳ ನೀರನ್ನು ಕೊಡುತ್ತಿದ್ದೆವು. ');
INSERT INTO "kannada" VALUES(72,17,'72:17','ಅದರಲ್ಲಿ (ಈ ಅನುಗ್ರಹದ ಮೂಲಕ) ಅವರನ್ನು ಪರೀಕ್ಷಿಸಲಿಕ್ಕಾಗಿ. ಯಾರು ತನ್ನ ಪ್ರಭುವಿನ ದಿಕ್‍ರ್‍ನಿಂದ (ಖುರ್‍ಆನ್‍ನಿಂದ) ವಿಮುಖ ನಾಗುವನೋ, ಅವನನ್ನು ಅವನ ಪ್ರಭುವು ಕಠಿಣ ಸಜೆಗೆ ಗುರಿಪಡಿಸುವನು.');
INSERT INTO "kannada" VALUES(72,18,'72:18','ಮಸೀದಿಗಳು ಅಲ್ಲಾಹನಿಗಿರುವುದಾಗಿವೆ. ಆದುದರಿಂದ ಅವುಗಳಲ್ಲಿ ಅಲ್ಲಾಹನೊಂದಿಗೆ ಇತರ ಯಾರನ್ನೂ ಆರಾಧಿಸಬೇಡಿರಿ .');
INSERT INTO "kannada" VALUES(72,19,'72:19','ಅಲ್ಲಾಹನ ದಾಸನು (ಪ್ರವಾದಿ) ಅವನನ್ನು ಆರಾಧಿಸುತ್ತಾ ನಿಂತಾಗ, ಅವರು (ಜಿನ್ನ್‍ಗಳು) ಆ ದಾಸನ ಸುತ್ತ ಕೂಟ-ಕೂಟವಾಗಿ ಕಿಕ್ಕಿರಿದು ತುಂಬ ಹತ್ತಿದರು.');
INSERT INTO "kannada" VALUES(72,20,'72:20','(ಸಂದೇಶವಾಹಕರೇ,) ತಾವು ಹೇಳಿರಿ; ನಾನು ನನ್ನ ಪ್ರಭುವನ್ನು ಮಾತ್ರ ಆರಾಧಿಸುತ್ತೇನೆ. ನಾನು ಅವನೊಂದಿಗೆ ಯಾರನ್ನೂ ಸಹಭಾಗಿಯಾಗಿ ಮಾಡುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(72,21,'72:21','ಹೇಳಿರಿ; ನಿಮಗೆ ಯಾವುದೇ ಹಾನಿ ಮಾಡಲಿಕ್ಕಾಗಲಿ, ಸನ್ಮಾರ್ಗ ನೀಡಲಿಕ್ಕಾಗಲಿ ನಾನು ಒಡೆತನ (ಅಧಿಕಾರ) ಹೊಂದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(72,22,'72:22','ಹೇಳಿರಿ; ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆಯಿಂದ ಯಾರೂ ನನ್ನನ್ನು ರಕ್ಷಿಸಲಾರರು. ಮತ್ತು ಅವನ ಹೊರತು ಯಾವುದೇ ಅಭಯ ಸ್ಥಾನವನ್ನು ನಾನೆಂದೂ ಕಂಡುಕೊಳ್ಳಲಾರೆನು.');
INSERT INTO "kannada" VALUES(72,23,'72:23','ಅಲ್ಲಾಹನ ಸಂದೇಶಗಳನ್ನು ತಲುಪಿಸುವ ಮತ್ತು ಅವನ ದೌತ್ಯವನ್ನು ನಿರ್ವಹಿಸುವ ಹೊರತು (ಬೇರೇನೂ ನನ್ನ ಅಧೀನತೆಯಲ್ಲಿಲ್ಲ.) ಯಾರು ಅಲ್ಲಾಹನ ಮತ್ತು ಅವನ ದೂತರನ್ನು ದಿಕ್ಕರಿಸುತ್ತಾರೋ ಅವನಿಗೆ ನರಕಾಗ್ನಿ ಇದೆ. ಅದರಲ್ಲಿ ಅವರು ಚಿರವಾಸಿಗಳಾಗಿರುವರು ಎಂದೆಂದಿಗೂ.');
INSERT INTO "kannada" VALUES(72,24,'72:24','ಅವರಿಗೆ ತಾಕೀತು ನೀಡಲಾಗುತ್ತಿರುವುದನ್ನು (ಶಿಕ್ಷೆ) ಕಣ್ಣಾರೆ ಕಂಡುಕೊಳ್ಳುವಾಗ ಯಾರ ಸಹಾಯಕರು ಅತ್ಯಂತ ದುರ್ಬಲರು ಮತ್ತು ಸಂಖ್ಯೆಯಲ್ಲಿ ಅತ್ಯಲ್ಪರು ಎಂಬುದು ಅವರಿಗೆ ತಿಳಿದು ಬಿಡುವುದು.');
INSERT INTO "kannada" VALUES(72,25,'72:25','ತಾವು ಹೇಳಿರಿ; ನಿಮಗೆ ಏನನ್ನು ತಾಕೀತು ನೀqಲಾಗುತ್ತಿದೆಯೋ ಅದು (ಶಿಕ್ಷೆ) ಸದ್ಯವೇ ಸಂಭವಿಸುವುದೋ ಅಥವಾ ನನ್ನ ಪ್ರಭು ಅದಕ್ಕೆ (ದೀರ್ಘವಾದ) ಅವಧಿ ನಿಶ್ಚಯಿಸುವನೋ ಎಂಬುದು ನನಗೆ ತಿಳಿದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(72,26,'72:26','ಅವನು ಅದೃಶ್ಯ ಜ್ಞಾನಿಯಾಗಿದ್ದಾನೆ. ಅವನು ತನ್ನ ಅದೃಶ್ಯ ಜ್ಞಾನವನ್ನು ಯಾರಿಗೂ ಬಹಿ ರಂಗಪಡಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(72,27,'72:27','ತಾನು ಮೆಚ್ಚಿಕೊಂಡ ಯಾವುದೇ ದೂತರಿಗೆ ಹೊರತು . ಆಗ ಆ ದೂತರ ಮುಂದೆಯೂ ಹಿಂದೆ ಯೂ ಅವನು ಕಾವಲುಗಾರರನ್ನು ಖಂಡಿತ ಏರ್ಪಡಿಸುವನು.');
INSERT INTO "kannada" VALUES(72,28,'72:28','ಅದು ಅವರು ತಮ್ಮ ಪ್ರಭುವಿನ ಸಂದೇಶಗಳನ್ನು ತಲುಪಿಸಿರುವರೆಂಬುದನ್ನು ಅವನು ಪ್ರತ್ಯಕ್ಷವಾಗಿ ತಿಳಿಯಲಿಕ್ಕಾಗಿ, ಅವರ ಬಳಿ ಇರುವುದನ್ನು ಅವನು ಸಂಪೂರ್ಣವಾಗಿ ಅರಿತಿರುತ್ತಾನೆ ಮತ್ತು ಪ್ರತಿಯೊಂದು ವಸ್ತುವನ್ನೂ ಅವನು ಲೆಕ್ಕ ಮಾಡಿಟ್ಟಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(73,1,'73:1','ಓ, ಹೊದ್ದು ಕೊಂಡವರೇ! (ಪ್ರವಾದಿಯರೇ!) ');
INSERT INTO "kannada" VALUES(73,2,'73:2','ರಾತ್ರಿ ಎದ್ದು ನಿಂತು ನಮಾಝ್ ನಿರ್ವಹಿಸಿರಿ, ಸ್ವಲ್ಪ ಹೊತ್ತು ಹೊರತುಪಡಿಸಿ.');
INSERT INTO "kannada" VALUES(73,3,'73:3','ರಾತ್ರಿಯ ಅರ್ಧಭಾಗ ಅಥವಾ ಅದಕ್ಕಿಂತ ಸ್ವಲ್ಪ (ಸಮಯ) ಕಡಿತಗೊಳಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(73,4,'73:4','ಅಥವಾ ಅದಕ್ಕಿಂತ ತುಸು (ಸಮಯ) ವರ್ಧಿಸಿಕೊಳ್ಳಿರಿ ಮತ್ತು ಖುರ್‍ಆನನ್ನು ಸಾವಕಾಶವಾಗಿ ಪಠಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(73,5,'73:5','ಖಂಡಿತ ನಾವು ತಮ್ಮ ಮೇಲೆ ಒಂದು ಘನವಾದ ಮಾತನ್ನು ಅವತೀರ್ಣಗೊಳಿಸಲಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(73,6,'73:6','ರಾತ್ರಿ ಎದ್ದು ನಿರ್ವಹಿಸುವ ನಮಾಝ್‍ನಿಂದ ಹೃದಯ ಸಾನಿಧ್ಯಕ್ಕೆ ಬಲವೂ ಮಾತುಗಳಿಗೆ ಸ್ಪಷ್ಟತೆಯೂ ಪ್ರಾಪ್ತವಾಗುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(73,7,'73:7','ಹಗಲು ಹೊತ್ತಿನಲ್ಲಿ ತಮಗೆ ದೀರ್ಘವಾದ ಕೆಲಸ ಕಾರ್ಯಗಳಿರುತ್ತವೆ.');
INSERT INTO "kannada" VALUES(73,8,'73:8','ತಮ್ಮ ಪ್ರಭುವಿನ ನಾಮವನ್ನು ಜಪಿಸಿರಿ ಮತ್ತು ಸರ್ವಾತ್ಮನಾ ಅವನಲ್ಲಿಗೆ ಮನ ಸಮರ್ಪಿಸಿರಿ. (ನಿಷ್ಕಳಂಕವಾಗಿ ಆರಾಧಿಸಿರಿ) ');
INSERT INTO "kannada" VALUES(73,9,'73:9','ಪೂರ್ವ-ಪಶ್ಚಿಮದ ಒಡೆಯನವನು. ಅವನ ಹೊರತು ಬೇರೆ ಆರಾಧ್ಯನಿಲ್ಲ. ಆದುದರಿಂದ ತಾವು ಅವನನ್ನೇ ಕಾರ್ಯಾಸಾಧಕನಾಗಿ ಸ್ವೀಕರಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(73,10,'73:10','ಅವರು (ಸತ್ಯನಿಷೇಧಿಗಳು) ಆಡುತ್ತಿರುವ ಮಾತುಗಳ ಮೇಲೆ ತಾಳ್ಮೆವಹಿಸಿರಿ ಮತ್ತು ಸುಂದರ ರೀತಿಯಲ್ಲಿ ಅವರಿಂದ ಬೇರ್ಪಟ್ಟು ನಿಲ್ಲಿರಿ .');
INSERT INTO "kannada" VALUES(73,11,'73:11','ಸುಖಲೋಲುಪರಾದ ಸತ್ಯನಿಷೇಧಿಗಳ ವಿರುದ್ಧ ಕ್ರಮ ಕೈಗೊಳ್ಳಲು ನನ್ನನ್ನು ಬಿಟ್ಟು ಬಿಡಿರಿ. ಅವರಿಗೆ ಸ್ವಲ್ಪ ಸಾವಕಾಶವನ್ನು ನೀಡಿರಿ.');
INSERT INTO "kannada" VALUES(73,12,'73:12','ನಮ್ಮ ಬಳಿ ಭಾರವಾದ ಬೇಡಿಗಳಿವೆ ಮತ್ತು ಹೊತ್ತಿ ಉರಿಯುವ ಅಗ್ನಿಯಿದೆ.');
INSERT INTO "kannada" VALUES(73,13,'73:13','ಗಂಟಲಲ್ಲಿ ಸಿಕ್ಕಿಕೊಳ್ಳುವ ಭೋಜನ ಮತ್ತು ವೇದನಾಜನಕ ಶಿಕ್ಷೆಯೂ ಇದೆ.');
INSERT INTO "kannada" VALUES(73,14,'73:14','ಭೂಮಿ ಮತ್ತು ಪರ್ವತಗಳು ನಡುಗುವ ಹಾಗೂ ಪರ್ವತಗಳು ಕೊಚ್ಚಿ ಹೋದ ಮರಳ ರಾಶಿಯಂತೆ ಆಗಿ ಬಿಡುವ ದಿನ. (ಆ ಶಿಕ್ಷೆ ಸಂಭವಿಸುವುದು.)');
INSERT INTO "kannada" VALUES(73,15,'73:15','(ಜನರೇ) ನಿಜವಾಗಿಯೂ ನಿಮ್ಮ ಕಡೆಗೆ, ನಿಮ್ಮ ವಿಷಯಗಳಲ್ಲಿ ಸಾಕ್ಷಿಯಾಗಿರುವ ಓರ್ವ ದೂತ ನನ್ನು ನಾವು ಕಳುಹಿಸಿರುತ್ತೇವೆ. ಫಿರ್‍ಔನನ ಕಡೆಗೆ ನಾವು ದೂತನನ್ನು ಕಳುಹಿಸಿದ್ದಂತೆ.');
INSERT INTO "kannada" VALUES(73,16,'73:16','(ಅಂದು) ಫಿರ್‍ಔನನು ಆ ಸಂದೇಶವಾಹಕರನ್ನು ಧಿಕ್ಕರಿಸಿದನು. ಆಗ ನಾವು ಅವನನ್ನು ಅತ್ಯಂತ ಉಗ್ರವಾಗಿ ಹಿಡಿದು ಬಿಟ್ಟೆವು.');
INSERT INTO "kannada" VALUES(73,17,'73:17','ನೀವು ಅವಿಶ್ವಾಸವನ್ನು ತಾಳಿದರೆ, ಮಕ್ಕಳನ್ನು (ನೆರೆ ಕೂದಲಿನ) ವೃದ್ಧರಾಗಿ ಮಾಡಿ ಬಿಡುವ ಒಂದು ಭಯಂಕರ ದಿನ ನೀವು ಹೇಗೆ ರಕ್ಷಣೆ ಹೊಂದುವಿರಿ?');
INSERT INTO "kannada" VALUES(73,18,'73:18','ಅಂದು ಆಕಾಶವು ಸಿಡಿದು ನುಚ್ಚುನೂರಾಗುವುದು. ಅಲ್ಲಾಹನ ವಾಗ್ದಾನವು ಕಾರ್ಯಗತ ಗೊಂಡೇ ತೀರುವುದು.');
INSERT INTO "kannada" VALUES(73,19,'73:19','ಇದೊಂದು ಉದ್ಭೋಧನೆಯಾಗಿದೆ. ಆದುದರಿಂದ ಇಚ್ಛೆಯುಳ್ಳವನು ತನ್ನ ಪ್ರಭುವಿನ ಕಡೆಗೆ ಒಂದು ದಾರಿಯನ್ನು ಮಾಡಿಕೊಳ್ಳಲಿ.');
INSERT INTO "kannada" VALUES(73,20,'73:20','(ಸಂದೇಶವಾಹಕರೇ,) ತಾವು ಸರಿಸುಮಾರು ರಾತ್ರೆಯ ಮೂರರಲ್ಲಿ ಎರಡು ಭಾಗ (ಇನ್ನು ಕೆಲವೊಮ್ಮೆ) ಅರ್ಧಭಾಗ ಮತ್ತು (ಕೆಲವೊಮ್ಮೆ) ಮೂರನೆಯ ಒಂದುಭಾಗ ನಮಾಝ್ ಮಾಡು ತ್ತೀರೆಂದು ತಮ್ಮ ಪ್ರಭು ಖಂಡಿತ ತಿಳಿದಿರುತ್ತಾನೆ. ತಮ್ಮ ಸಂಗಡ ಒಂದು ಕೂಟದವರೂ. (ಹಾಗೇ ಮಾಡುತ್ತಿದ್ದಾರೆ) ರಾತ್ರಿ ಮತ್ತು ಹಗಲನ್ನು ಅಲ್ಲಾಹನು ಲೆಕ್ಕವಿಡುವನು. ನಿಮಗೆ ಕಾಲಗಣನೆ ಯನ್ನು ಸರಿಯಾಗಿ ಮಾಡಲಾಗದೆಂದು ಅವನಿಗೆ ತಿಳಿದಿದೆ. ಆದುದರಿಂದ ಅವನು ನಿಮ್ಮ ಮೇಲೆ ವಿನಾಯಿತಿ ತೋರಿದನು. ಹೀಗಾಗಿ ನೀವು ಖುರ್‍ಆನ್‍ನಿಂದ, ಸೌಕರ್ಯವಾದುದನ್ನು ಪಠಿಸಿ (ನಮಾಝ್ ಮಾಡಿ)ಕೊಳ್ಳಿರಿ. ನಿಮ್ಮ ಪೈಕಿ ರೋಗಿಗಳಿರಬಹುದೆಂದೂ, ಇನ್ನು ಕೆಲವರು ಅಲ್ಲಾಹನ ಅನುಗ್ರಹವನ್ನರಸಿ ಕೊಂಡು ಭೂಮಿಯಲ್ಲಿ ಸಂಚರಿಸಲಿರುವರೆಂದೂ ಮತ್ತೆ ಕೆಲವರು ಅಲ್ಲಾಹನ ಮಾರ್ಗದಲ್ಲಿ ಯುದ್ಧಕ್ಕೆ ಹೋಗಲಿರುವರೆಂದೂ ಅವನಿಗೆ (ಅಲ್ಲಾಹನಿಗೆ ) ತಿಳಿದಿದೆ. ಆದುದರಿಂದ ನೀವು ಖುರ್‍ಆನ್‍ನಿಂದ ಸೌಕರ್ಯವಾದುದನ್ನು ಪಠಿಸಿರಿ, ನಮಾಝ್ ಕ್ರಮಪ್ರಕಾರ ನಿರ್ವಹಿಸಿರಿ. ಝಕಾತನ್ನು ಪಾವತಿಸಿರಿ ಮತ್ತು ಅಲ್ಲಾಹನಿಗೆ ಉತ್ತಮ ಸಾಲವನ್ನು ಕೊಡಿರಿ. ನೀವು ಸ್ವಂತಕ್ಕಾಗಿ ಯಾವುದೇ ಸತ್ಕರ್ಮವನ್ನು ಮುಂದಾಗಿ ನಿರ್ವಹಿಸುತ್ತೀರೆಂದಾದರೆ, ಅಲ್ಲಾಹನ ಬಳಿ ಅದು ಅತ್ಯಧಿಕ ಗುಣಕರವಾಗಿಯೂ, ಮಹತ್ತರ ಪ್ರತಿಫಲವುಳ್ಳದ್ದಾಗಿಯೂ ನೀವು ಕಾಣುವಿರಿ. ನೀವು ಅಲ್ಲಾಹನೊಡನೆ ಪಾಪ ಮುಕ್ತಿಗೆ ಬೇಡಿರಿ. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಮಹಾಕ್ಷಮಾಶೀಲನೂ ಕರುಣಾಮಯಿಯೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(74,1,'74:1','ಓ ಹೊದ್ದು ಕೊಂಡವರೇ ! (ಪ್ರವಾದಿಯರೇ!)');
INSERT INTO "kannada" VALUES(74,2,'74:2','ಎದ್ದೇಳಿರಿ ಮತ್ತು (ಜನರನ್ನು) ಎಚ್ಚರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(74,3,'74:3','ತಮ್ಮ ಪ್ರಭುವಿನ ಹಿರಿಮೆಯನ್ನು ಪ್ರಕೀರ್ತಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(74,4,'74:4','ತಮ್ಮ ವಸ್ತ್ರಗಳನ್ನು ಶುಚಿಗೊಳಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(74,5,'74:5','ಮತ್ತು ಪಾಪ ಮಾಲಿನ್ಯವನ್ನು ವರ್ಜಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(74,6,'74:6','ಅಧಿಕ ಲಭಿಸುವ ಆಗ್ರಹದಿಂದ ಔದಾರ್ಯ ತೋರಬೇಡಿರಿ .');
INSERT INTO "kannada" VALUES(74,7,'74:7','ಮತ್ತು ತಮ್ಮ ಪ್ರಭುವಿಗಾಗಿ ತಾಳ್ಮೆವಹಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(74,8,'74:8','ಕಹಳೆಯಲ್ಲಿ ಮೊಳಗಿಸಲ್ಪಟ್ಟಾಗ,');
INSERT INTO "kannada" VALUES(74,9,'74:9','ಆ ದಿನವು ಅತ್ಯಂತ ಪ್ರಯಾಸಕರ ದಿನವಾಗಿರುವುದು.');
INSERT INTO "kannada" VALUES(74,10,'74:10','ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ಅದು ಅನಾಯಾಸವಲ್ಲದ ದಿನವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(74,11,'74:11','ನನ್ನನ್ನು ಮತ್ತು ಏಕನಾದ ಸ್ಥಿತಿಯಲ್ಲಿ ನನ್ನಿಂದ ಸೃಷ್ಠಿಯಾಗಿರುವ ಒಬ್ಬನನ್ನು ನಮ್ಮಿಬ್ಬರ ಪಾಡಿಗೆ ಬಿಡು .');
INSERT INTO "kannada" VALUES(74,12,'74:12','ಅವನಿಗೆ ನಾನು ಹೇರಳ ಸಂಪತ್ತನ್ನು ಕೊಟ್ಟೆನು.');
INSERT INTO "kannada" VALUES(74,13,'74:13','ಉಪಸ್ಥಿತರಾದ ಪುತ್ರರನ್ನೂ ಕೊಟ್ಟೆನು .');
INSERT INTO "kannada" VALUES(74,14,'74:14','ಮತ್ತು ಅವನ ಪಾಲಿಗೆ ಬೇಕಾದ ಸೌಕರ್ಯಗಳನ್ನು ನಾನು ಏರ್ಪಡಿಸಿಕೊಟ್ಟೆನು.');
INSERT INTO "kannada" VALUES(74,15,'74:15','ಇಷ್ಟಿದ್ದೂ ನಾನು ಅವನಿಗೆ ಇನ್ನಷ್ಟು ಕೊಡ ಬೇಕೆಂದು ಅವನು ಹಂಬಲಿಸುತ್ತಿರುವನು.');
INSERT INTO "kannada" VALUES(74,16,'74:16','ಖಂಡಿತ ಇಲ್ಲ. ಅವನು ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳಿಗೆ ಪ್ರತಿಸ್ಪರ್ಧಿಯಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(74,17,'74:17','ಅವನನ್ನು ನಾನು ಒಂದು ಕಠಿಣವಾದ ಯಾತನೆಯನ್ನು ಅನುಭವಿಸಲು ಶೀಘ್ರವೇ ನಿರ್ಬಂಧಿಸುವೆನು.');
INSERT INTO "kannada" VALUES(74,18,'74:18','ಅವನು ಯೋಚಿಸಿದನು ಮತ್ತು (ಒಂದು ನಿರ್ಧಾರ) ನಿರ್ಣಯಿಸಿಕೊಂಡನು.');
INSERT INTO "kannada" VALUES(74,19,'74:19','ಅದರಿಂದಾಗಿ ಅವನು ನಾಶವಾಗಲಿ. ಅವನು ಹೇಗೆ ನಿರ್ಣಯಿಸಿಕೊಂಡನು?');
INSERT INTO "kannada" VALUES(74,20,'74:20','ಪುನಃ ಅವನು ನಾಶವಾಗಲಿ! ಅವನು ಹೇಗೆ ನಿರ್ಣಯಿಸಿಕೊಂಡನು?');
INSERT INTO "kannada" VALUES(74,21,'74:21','ಪುನಃ ಅವನು (ಸಭಿಕರ ಮುಖಕ್ಕೆ) ನೋಡಿದನು.');
INSERT INTO "kannada" VALUES(74,22,'74:22','ತರುವಾಯ ಮುಖ ಸಿಂಡರಿಸಿದನು ಮತ್ತು ಮುಖ ಮುರಿದುಕೊಂಡನು.');
INSERT INTO "kannada" VALUES(74,23,'74:23','ಅನಂತರ (ಸತ್ಯದಿಂದ) ಹಿಂದಕ್ಕೆ ಮರಳಿದನು ಮತ್ತು ಅಹಂಭಾವ ತೋರಿದನು.');
INSERT INTO "kannada" VALUES(74,24,'74:24','ಕೊನೆಗೆ ಹೀಗೆಂದನು, ಇದು (ಖುರ್‍ಆನ್) (ಯಾರಿಂದಲೋ) ಬಿತ್ತರಿಸಲ್ಪಡುವ ಮಾಂತ್ರಿಕತೆಯಲ್ಲದೆ ಇನ್ನೇನೂ ಅಲ್ಲ.');
INSERT INTO "kannada" VALUES(74,25,'74:25','ಇದೊಂದು ಮನುಷ್ಯನ ಮಾತಲ್ಲದೆ ಬೇರೇನೂ ಅಲ್ಲ.');
INSERT INTO "kannada" VALUES(74,26,'74:26','ನಾನು ಸದ್ಯವೇ ಅವನನ್ನು ಸಖರಿಗೆ (ನರಕಕ್ಕೆ) ತಳ್ಳಿಬಿಡುವೆನು.');
INSERT INTO "kannada" VALUES(74,27,'74:27','``ಸಖರ್’’ನ ಕುರಿತು ತಮಗೆ ಗೊತ್ತಾ?');
INSERT INTO "kannada" VALUES(74,28,'74:28','. ಅದು ಯಾವುದನ್ನೂ ಬಾಕಿ ಬಿಡುವುದೋ ಉಳಿಸುವುದೋ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(74,29,'74:29','ಅದು ಚರ್ಮವನ್ನು ಸುಟ್ಟು ಕರಕಲು ಮಾಡುವುದು.');
INSERT INTO "kannada" VALUES(74,30,'74:30','ಆ ನರಕದ ಮೇಲೆ ಹತ್ತೊಂಬತ್ತು ಕಿಂಕರರಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(74,31,'74:31','ನಾವು ನರಕದ ಕಾರ್ಯಕರ್ತರನ್ನಾಗಿ ನೇಮಿಸಿರುವುದು ದೇವಚರರನ್ನು ಮಾತ್ರವಾಗಿದೆ . ಅವರ ಸಂಖ್ಯೆಯನ್ನು ಸತ್ಯನಿಷೇಧಿಗಳ ಪಾಲಿಗೆ ಒಂದು ಪರೀಕ್ಷಾ ಸಾಧನವಾಗಿ ಮಾಡಿದ್ದೇವೆ. ಗ್ರಂಥ ನೀಡಲ್ಪಟ್ಟವರಿಗೆ ದೃಢನಂಬಿಕೆ ಉಂಟಾಗಲಿಕ್ಕಾಗಿ, ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ವಿಶ್ವಾಸವು ವರ್ಧಿಸಲಿಕ್ಕಾಗಿ, ಗ್ರಂಥ ನೀಡಲ್ಪಟ್ಟವರು ಮತ್ತು ವಿಶ್ವಾಸಿಗಳು ಸಂಶಯದಲ್ಲಿ ಬೀಳದಿರಲಿಕ್ಕಾಗಿ ಮತ್ತು ಮನಸ್ಸಿನಲ್ಲಿ ರೋಗವಿರುವವರು ಹಾಗೂ ಸತ್ಯ ನಿಷೇಧಿಗಳು ಈ ಉದಾಹರಣೆಯ ಮೂಲಕ ಅಲ್ಲಾಹನು ಉದ್ದೇಶಿಸುವುದಾದರೂ ಏನನ್ನು? ಎಂದು ಹೇಳುವಂತಾಗಲು. ಈ ರೀತಿ ಅಲ್ಲಾಹನು ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ದಾರಿ ತಪ್ಪಿಸುತ್ತಾನೆ ಮತ್ತು ತಾನಿಚ್ಛಿಸಿದವರಿಗೆ ನೇರ ದಾರಿ ತೋರುತ್ತಾನೆ. ತಮ್ಮ ಪ್ರಭುವಿನ ಸೇನೆಗಳನ್ನು ಸ್ವತಃ ಅವನ ಹೊರತು ಇನ್ನಾರೂ ಅರಿತಿಲ್ಲ. ಇದು ಮನುಷ್ಯರಿಗೆ ಒಂದು ಉದ್ಭೋಧನೆಯಲ್ಲದೆ ಇನ್ನೇನೂ ಅಲ್ಲ.');
INSERT INTO "kannada" VALUES(74,32,'74:32','ನಿಸ್ಸಂದೇಹ! ಚಂದ್ರನಾಣೆ!');
INSERT INTO "kannada" VALUES(74,33,'74:33','ಮತ್ತು ರಾತ್ರಿಯಾಣೆ. ಅದು ಮರಳುವಾಗ,');
INSERT INTO "kannada" VALUES(74,34,'74:34','ಮತ್ತು ಮುಂಜಾವಿನಾಣೆ ! ಅದು ಬೆಳಗುವಾಗ,');
INSERT INTO "kannada" VALUES(74,35,'74:35','ಖಂಡಿತವಾಗಿಯೂ ಸಕರ್ ನರಕವು ಗಂಭೀರ ವಿನಾಶಗಳ ಪೈಕಿ ಒಂದಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(74,36,'74:36','ಮಾನವರ ಪಾಲಿಗೆ ಒಂದು ಎಚ್ಚರಿಕೆ ಎಂಬ ನೆಲೆಯಲ್ಲಿ.');
INSERT INTO "kannada" VALUES(74,37,'74:37','ಅಂದರೆ ನಿಮ್ಮ ಪೈಕಿ ಮುಂದುವರಿಯಲು ಅಥವಾ ಹಿಂದುಳಿಯಲು ಇಚ್ಛಿಸುವವರಿಗೆ (ಅದೊಂದು ಎಚ್ಚರಿಕೆಯಾಗಿದೆ).');
INSERT INTO "kannada" VALUES(74,38,'74:38','ಪ್ರತಿಯೊಂದು ಶರೀರವೂ ಅದರ ಪ್ರವೃತ್ತಿ ಯೊಂದಿಗೆ ಅಡವಿಡಲ್ಪಟ್ಟಿದೆ .');
INSERT INTO "kannada" VALUES(74,39,'74:39','ಬಲಗಡೆಯವರ ಹೊರತು.');
INSERT INTO "kannada" VALUES(74,40,'74:40','(ಅವರು)ಸ್ವರ್ಗೋದ್ಯಾನಗಳಲ್ಲಿರುವರು.');
INSERT INTO "kannada" VALUES(74,41,'74:41','ಅವರು ಅಪರಾಧಿಗಳೊಡನೆ ಕೇಳುವರು;');
INSERT INTO "kannada" VALUES(74,42,'74:42','``ನಿಮ್ಮನ್ನು ನರಕಕ್ಕೆ ಒಯ್ದುದು ಯಾವುದು?’’');
INSERT INTO "kannada" VALUES(74,43,'74:43','ಅವರು ಹೇಳುವರು; ``ನಾವು ನಮಾಝ್ ಮಾಡುವವರಲ್ಲಿ ಸೇರಿದವರಾಗಿರಲಿಲ್ಲ’’.');
INSERT INTO "kannada" VALUES(74,44,'74:44','ನಾವು ಬಡವರಿಗೆ ಆಹಾರ ಉಣಿಸುತ್ತಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(74,45,'74:45','ದುರ್ವೃತ್ತಿಗಳಲ್ಲಿ ನಿರತರಾದವರೊಂದಿಗೆ ಸೇರಿ ನಾವು ಅದರಲ್ಲಿ ನಿರತರಾಗಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(74,46,'74:46','ಮತ್ತು ನಾವು ಪ್ರತಿಫಲದ ದಿನವನ್ನು ಸುಳ್ಳಾಗಿಸುತ್ತಿದ್ದೆವು.');
INSERT INTO "kannada" VALUES(74,47,'74:47','ಕೊನೆಗೆ ಖಚಿತವಾದುದು (ಮರಣ) ನಮಗೆ ಬಂದೇ ಬಿಟ್ಟಿತು.');
INSERT INTO "kannada" VALUES(74,48,'74:48','(ಅಂದು) ಶಿಫಾರಸು ಮಾಡುವವರ ಶಿಫಾರಸು ಅವರಿಗೆ ಯಾವುದೇ ಪ್ರಯೋಜನ ನೀಡದು .');
INSERT INTO "kannada" VALUES(74,49,'74:49','ಅವರು ಉಪದೇಶದಿಂದ ವಿಮುಖರಾಗಲು ಅವರಿಗೆ ಸಂಭವಿಸಿರುವುದಾದರೂ ಏನು?');
INSERT INTO "kannada" VALUES(74,50,'74:50','');
INSERT INTO "kannada" VALUES(74,51,'74:51','.ಅವರ ಅವಸ್ಥೆಯು ಸಿಂಹದಿಂದ ಓಡಿ ಪಾರಾಗುವ ಅಂಜಿ ತತ್ತರಿಸಿದ ಕಾಡು ಕತ್ತೆಗಳಂತೆ ಆಗಿದೆ.');
INSERT INTO "kannada" VALUES(74,52,'74:52','ಅಲ್ಲ, ಅವರ ಪೈಕಿ ಪ್ರತಿಯೊಬ್ಬನೂ, ತನಗೆ ಅಲ್ಲಾಹನಿಂದ ತೆರೆದಿಡಲ್ಪಟ್ಟ ಗ್ರಂಥಗಳನ್ನು ಕೊಡಲ್ಪ ಡಬೇಕೆಂದು ಬಯಸುವನು.');
INSERT INTO "kannada" VALUES(74,53,'74:53','ಖಂಡಿತ ಇಲ್ಲ. ಆದರೆ ಅವರು ಪರಲೋಕವನ್ನು ಭಯಪಡುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(74,54,'74:54','ಖಂಡಿತವಾಗಿಯೂ ಇದು (ಖುರ್‍ಆನ್) ಒಂದು ನೀತಿಪಾಠವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(74,55,'74:55','ಆದುದರಿಂದ ಇಷ್ಟವುಳ್ಳವರು ಇದರಿಂದ ಓದಿ ಉಪದೇಶ ಪಡೆದುಕೊಳ್ಳಲಿ.');
INSERT INTO "kannada" VALUES(74,56,'74:56','ಅಲ್ಲಾಹನು ಇಚ್ಛಿಸದೇ ಅವರು ಯಾರೂ ಪಠಿಸಿ ಓದಿ ಉಪದೇಶ ಪಡಕೊಳ್ಳಲಾರರು. ಅವನು ಭಯಭಕ್ತಿಗೆ ಅರ್ಹನೂ ಪಾಪ ಮುಕ್ತಿಗೆ ಅರ್ಹನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(75,1,'75:1','ನಾನು ಪುನರುತ್ಥಾನ ದಿನದ ಆಣೆ ಹಾಕಿ ಹೇಳುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(75,2,'75:2','ಸ್ವದೂಷಣೆ ಮಾಡಿಕೊಳ್ಳುತ್ತಿರುವ ಮನಸ್ಸಿನ ಆಣೆ ಹಾಕುತ್ತಿದ್ದೇನೆ .');
INSERT INTO "kannada" VALUES(75,3,'75:3','ನಾವು ಅವನ ಎಲುಬುಗಳನ್ನು (ಅವು ದ್ರವಿಸಿದ ಬಳಿಕ) ಒಟ್ಟುಗೂಡಿಸಲಾರೆವೆಂದು ಮಾನವನು ಭಾವಿಸುತ್ತಿರುವನೇ?');
INSERT INTO "kannada" VALUES(75,4,'75:4','ಹೌದು, ನಾವು ಅವನ ಬೆರಳುಗಳ ತುದಿಗಳನ್ನು ಕೂಡಾ ಸರಿಯಾಗಿ ರಚಿಸಲು ಶಕ್ತರಾಗಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(75,5,'75:5','ಆದರೆ ಮಾನವನು ತನ್ನ ಭವಿಷ್ಯವನ್ನು (ಪುನರುತ್ಥಾನವನ್ನು) ಸುಳ್ಳಾಗಿಸಲು ಬಯಸುತ್ತಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(75,6,'75:6','ಅವನು, ಪುನರುತ್ಥಾನ ದಿನ ಯಾವಾಗ? ಎಂದು ಕೇಳುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(75,7,'75:7','ಆದರೆ, ದೃಷ್ಟಿಗಳು ಸ್ಥಂಭೀಭೂತವಾದಾಗ!');
INSERT INTO "kannada" VALUES(75,8,'75:8','ಚಂದ್ರನು ಕಳೆಗುಂದಿದಾಗ;');
INSERT INTO "kannada" VALUES(75,9,'75:9','ಮತ್ತು ಸೂರ್ಯ ಚಂದ್ರರು ಒಟ್ಟು ಸೇರಿಸಲ್ಪಟ್ಟಾಗ.');
INSERT INTO "kannada" VALUES(75,10,'75:10','ಆ ದಿನ ಮನುಷ್ಯನು ``ಎಲ್ಲಿಗೆ ಓಡಿ ಹೋಗಿ ರಕ್ಷೆ ಪಡೆಯಲಿ?’’ ಎಂದು ಹೇಳುವನು.');
INSERT INTO "kannada" VALUES(75,11,'75:11','ಖಂಡಿತ ಇಲ್ಲ, ಯಾವ ಅಭಯ ಸ್ಥಾನವೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(75,12,'75:12','ಅಂದು ತನ್ನ ಪ್ರಭುವಿನ ಮುಂದೆಯೇ (ಎಲ್ಲರೂ) ಹೋಗಿ ತಲುಪಬೇಕಾಗುವುದು.');
INSERT INTO "kannada" VALUES(75,13,'75:13','ಅಂದು ಮಾನವನಿಗೆ ಅವನ ಹಿಂದಿನ ಮತ್ತು ಮುಂದಿನ ಎಲ್ಲಾ ಕರ್ಮಗಳನ್ನೂ ತೋರಿಸಿ ಕೊಡಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(75,14,'75:14','ಅಷ್ಟೇ ಅಲ್ಲ, ಮಾನವನು ತನಗೆ ವಿರುದ್ಧ ತಾನೇ ಒಂದು ರುಜುವಾಗಿದ್ದಾನೆ. (ಪುರಾವೆಯಾಗಿದ್ದಾನೆ)');
INSERT INTO "kannada" VALUES(75,15,'75:15','ಅವನು ತನಗಾಗಿ ಎಷ್ಟೇ ನೆಪಗಳನ್ನೊಡ್ಡಿದ್ದರೂ ಸರಿ.');
INSERT INTO "kannada" VALUES(75,16,'75:16','(ಸಂದೇಶವಾಹಕರೇ,) ತಾವು ಖುರ್‍ಆನನ್ನು ದೃತಿಯಿಂದ ಕಂಠಪಾಠ ಮಾಡಿಕೊಳ್ಳಲಿಕ್ಕಾಗಿ ತಮ್ಮ ನಾಲಗೆಯನ್ನು ಚಲಿಸಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(75,17,'75:17','ಅದನ್ನು ಒಗ್ಗೂಡಿಸಿಕೊಡುವ ಮತ್ತು ಓದಿಸಿ ಕೊಡುವ ಹೊಣೆ ನಮ್ಮದೇ ಆಗಿರುತ್ತದೆ .');
INSERT INTO "kannada" VALUES(75,18,'75:18','ಆದುದರಿಂದ ನಾವು ಅದನ್ನು ಓದಿ ಕೊಟ್ಟರೆ, ತಾವು ಅದರ ಪಠಣವನ್ನು ಅನುಗಮಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(75,19,'75:19','ಬಳಿಕ ಅದನ್ನು ವಿವರಿಸುವ ಹೊಣೆಯೂ ನಮ್ಮ ಮೇಲಿದೆ .');
INSERT INTO "kannada" VALUES(75,20,'75:20','ಖಂಡಿತ ಬೇಡ. ನೀವು ಕ್ಷಣಿಕವಾದ ಈ (ಐಹಿಕ) ಬದುಕನ್ನು ಪ್ರೀತಿಸುತ್ತೀರಿ.');
INSERT INTO "kannada" VALUES(75,21,'75:21','ಪರಲೋಕವನ್ನು ನೀವು ಬಿಟ್ಟು ಬಿಡುತ್ತೀರಿ.');
INSERT INTO "kannada" VALUES(75,22,'75:22','ಅಂದು ಕೆಲವು ಮುಖಗಳು ಪ್ರಸನ್ನಗೊಂಡಿವೆ.');
INSERT INTO "kannada" VALUES(75,23,'75:23','ತಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಗೆ ದೃಷ್ಟಿನೆಟ್ಟಿವೆ.');
INSERT INTO "kannada" VALUES(75,24,'75:24','ಅಂದು ಕೆಲವು ಮುಖಗಳು ಖಿನ್ನವಾಗಿವೆ.');
INSERT INTO "kannada" VALUES(75,25,'75:25','ಮತ್ತು ತಮ್ಮೊಂದಿಗೆ ಅಮಿತಾಪತ್ತಿನಿಂದ ವರ್ತಿಸಲಾಗುವುದೆಂದು (ಯಾವುದೋ ದುರಂತ ತಮ್ಮನ್ನು ಬಾಧಿಸಲಿವೆ ಎಂದು) ಭಾವಿಸುತ್ತಿರುವುವು.');
INSERT INTO "kannada" VALUES(75,26,'75:26','ತಿಳಿಯಿರಿ, ಪ್ರಾಣವು ಗಂಟಲಿಗೆ ತಲಪಿದಾಗ,');
INSERT INTO "kannada" VALUES(75,27,'75:27','``ಯಾರಾದರೂ ಮಂತ್ರಿಸುವವನು ಇದ್ದಾನೆಯೇ?’’ ಎಂದು ಕೇಳಲಾದಾಗ,');
INSERT INTO "kannada" VALUES(75,28,'75:28','ಇದು ತನ್ನ ಅಗಲುವ ಸಮಯವೆಂದು ಅವನು ಖಚಿತಪಡಿಸಿಕೊಂಡಾಗ,');
INSERT INTO "kannada" VALUES(75,29,'75:29','ಮತ್ತು ಮೊಣಕಾಲು ಮೊಣಕಾಲಿನೊಂದಿಗೆ ಸೇರಿಕೊಂಡಾಗ,');
INSERT INTO "kannada" VALUES(75,30,'75:30','ಅಂದು ತಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಗೆ (ಅವನನ್ನು) ಕೊಂಡುಹೋಗಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(75,31,'75:31','ಆದರೆ, ಅವನಾಗ ಸತ್ಯವನ್ನು ಅಂಗೀಕರಿಸಲೂ ಇಲ್ಲ, ನಮಾಝ್ ನಿರ್ವಹಿಸಲೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(75,32,'75:32','ವಾಸ್ತವದಲ್ಲಿ, ಅವನು (ಸತ್ಯವನ್ನು) ಸುಳ್ಳಾಗಿಸಿ ದನು ಮತ್ತು ವಿಮುಖನಾದನು.');
INSERT INTO "kannada" VALUES(75,33,'75:33','ಮಾತ್ರವಲ್ಲ, ಅವನು ದುರಭಿಮಾನ ತೋರಿಕೊಂಡು ತನ್ನ ಕುಟುಂಬದ ಕಡೆಗೆ ನಡೆದುಬಿಟ್ಟನು.');
INSERT INTO "kannada" VALUES(75,34,'75:34','(ಓ ಮಾನವ ನೀನು ಅನಿಷ್ಟಪಡುವಂತಹುದು) ನಿನಗೆ ಒದಗಿ ಬಂದಿದೆ. ಅದು ನಿನಗೆ ತಕ್ಕುದಾಗಿಯೇ ಇದೆ.');
INSERT INTO "kannada" VALUES(75,35,'75:35','ಪುನಃ ಅದು ನಿನಗೆ ಒದಗಿ ಬಂದಿದೆ. ಅದು ನಿನಗೆ ತಕ್ಕುದಾಗಿಯೇ ಇದೆ.');
INSERT INTO "kannada" VALUES(75,36,'75:36','ತನ್ನನ್ನು ಸುಮ್ಮನೆ ಬಿಟ್ಟು ಬಿಡಬಹುದೆಂದು ಮಾನವನು ಭಾವಿಸಿಕೊಂಡಿರುವನೇ?');
INSERT INTO "kannada" VALUES(75,37,'75:37','ಅವನು (ಮಾತೆಯ ಗರ್ಭಾಶಯದೊಳಗೆ) ಸುರಿಯಲ್ಪಡುವ ಒಂದು ಶುಕ್ಲ ಬಿಂದು ಮಾತ್ರವಾಗಿರಲಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(75,38,'75:38','ನಂತರ ಅವನು ಒಂದು ರಕ್ತ ಪಿಂಡವಾದನು. (ಭ್ರೂಣವಾದನು) ಅನಂತರ ಅಲ್ಲಾಹನು ಅವನನ್ನು (ಒಂದು ಮನುಷ್ಯನಾಗಿ) ಸೃಷ್ಟಿಸಿ ರೂಪುಗೊಳಿಸಿದನು.');
INSERT INTO "kannada" VALUES(75,39,'75:39','ಆ ರೀತಿಯಲ್ಲಿ ಅವನು ಅದರಿಂದ ಪುರುಷ ಮತ್ತು ಸ್ತ್ರೀ ಎಂಬೆರಡು ವರ್ಗವನ್ನು ಮಾಡಿದನು.');
INSERT INTO "kannada" VALUES(75,40,'75:40','(ಅಂತಹ) ಅಲ್ಲಾಹನು ಮೃತರನ್ನು ಪುನಃ ಜೀವಂತಗೊಳಿಸಲು ಶಕ್ತನಲ್ಲವೇ? ');
INSERT INTO "kannada" VALUES(76,1,'76:1','ಪ್ರಸ್ತಾಪಿಸತಕ್ಕ ಒಂದು ವಸ್ತುವೇ ಆಗಿರದಂತಹ ಕಾಲವೊಂದು ಮಾನವನ ಮೇಲೆ ಕಳೆದಿಲ್ಲವೇ? ');
INSERT INTO "kannada" VALUES(76,2,'76:2','ನಾವು ಮಾನವನನ್ನು ಒಂದು ಸಮ್ಮಿಶ್ರ ಶುಕ್ಲ ಬಿಂದುವಿನಿಂದ ಸೃಷ್ಟಿಸಿದೆವು . ಅವನನ್ನು ನಾವು ಪರೀಕ್ಷಿಸಲಿದ್ದೇವೆ. (ಈ ಉದ್ದೇಶದಿಂದ) ನಾವು ಅವನನ್ನು ಆಲಿಸುವವನಾಗಿಯೂ, ಕಾಣುವವನಾಗಿಯೂ ಮಾಡಿದೆವು.');
INSERT INTO "kannada" VALUES(76,3,'76:3','ನಾವು ಅವನಿಗೆ ಮಾರ್ಗದರ್ಶನ ಮಾಡಿದೆವು. ಒಂದೋ ಅವನು ಕೃತಜ್ಞತೆ ಸಲ್ಲಿಸುವವನಾಗಿರ ಬಹುದು ಅಥವಾ ಕೃತಘ್ನನಾಗಬಹುದು.');
INSERT INTO "kannada" VALUES(76,4,'76:4','ಸತ್ಯನಿಷೇಧಿಗಳಿಗೆ ನಾವು ಕೆಲವು ಸಂಕೋಲೆಗಳನ್ನೂ ಕೋಳಗಳನ್ನೂ ಹೊತ್ತಿ ಉರಿಯುವ ನರಕವನ್ನೂ ಸಿದ್ಧಗೊಳಿಸಿಟ್ಟಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(76,5,'76:5','ಪುಣ್ಯವಂತರು (ಸುರೆ ತುಂಬಿದ) ಪಾನಪಾತ್ರೆಯಿಂದ ಕುಡಿಯುತ್ತಿರುವರು. ಅದರ ಮಿಶ್ರಿತವು ಕರ್ಪೂರವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(76,6,'76:6','ಅಲ್ಲಾಹನ ದಾಸರು ಪಾನ ಮಾಡುವ ಒಂದು ಚಿಲುಮೆಯ ನೀರು ಅದು. ಮತ್ತು (ತಾವಿಚ್ಛಿಸಿ ದೆಡೆಗೆ) ಅವರು ಅದನ್ನು ಚಿಮ್ಮಿಸಿ ಹರಿಸಿ ಬಿಡುವರು.');
INSERT INTO "kannada" VALUES(76,7,'76:7','ಅವರು ಹರಕೆಯನ್ನು ಪೂರ್ತಿಗೊಳಿಸುತ್ತಾರೆ. ಎಲ್ಲೆಲ್ಲೂ ಸಂಕಷ್ಟಗಳೇ ವ್ಯಾಪಕವಾಗಿರುವ ಒಂದು ದಿನವನ್ನು ಅವರು ಭಯಪಡುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(76,8,'76:8','ಆಹಾರ ತಮಗೆ ಅಗತ್ಯವಿದ್ದೂ ಕೂಡಾ ಅವರು ಅಲ್ಲಾಹನಲ್ಲಿರುವ ಒಲವಿನಿಂದ ಬಡವರಿಗೂ ಅನಾಥರಿಗೂ ಸೆರೆಯಾಳುಗಳಿಗೂ ಉಣ ಬಡಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(76,9,'76:9','ನಾವು ನಿಮಗೆ ಕೇವಲ ಅಲ್ಲಾಹನ ಪ್ರೀತಿಗಾಗಿ (ಅವನ ತೃಪ್ತಿಯನ್ನು ಬಯಸಿ) ಉಣಿಸುತ್ತಿದ್ದೇವೆ. ನಾವು ನಿಮ್ಮಿಂದ ಯಾವುದೇ ಪ್ರತಿಫಲವನ್ನೋ, ಕೃತಜ್ಞತೆಯನ್ನೋ ಬಯಸುವುದಿಲ್ಲ (ಎಂದವರು ಹೇಳುವರು).');
INSERT INTO "kannada" VALUES(76,10,'76:10','(ಕಳೆಗುಂದುವ) ಮುಖ ಸುರುಟಿ ಹೋಗುವ, ಅತ್ಯಂತ ಕಾಠಿಣ್ಯದ ಸುದೀರ್ಘ ದಿನವನ್ನು ನಮ್ಮ ಪ್ರಭುವಿನಿಂದ (ಖಂಡಿತ ನಾವು) ಭಯಪಡುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(76,11,'76:11','ಆದುದರಿಂದ ಅಲ್ಲಾಹನು ಅವರನ್ನು ಆ ದಿನದ ಕೇಡಿನಿಂದ ರಕ್ಷಿಸುವನು ಮತ್ತು ಅವರಿಗೆ ಪ್ರಸನ್ನತೆಯನ್ನೂ ಸಂತೋಷವನ್ನೂ ನೀಡುವನು.');
INSERT INTO "kannada" VALUES(76,12,'76:12','ಅವರು ಸಹಿಸಿದ ಸಹನೆಯ ಪ್ರತಿಫಲವಾಗಿ ಅವರಿಗೆ ಸ್ವರ್ಗೋದ್ಯಾನವನ್ನೂ ರೇಶ್ಮೆಯ ಉಡುಪನ್ನೂ ದಯಪಾಲಿಸುವನು.');
INSERT INTO "kannada" VALUES(76,13,'76:13','ಅಲ್ಲಿ ಅವರು ಆರಾಮ ಮಂಚಗಳ ಮೇಲೆ ಒರಗಿ ಸುಖಿಸುವರು. ಅಲ್ಲಿ ಅವರಿಗೆ ಬಿಸಿಲಿನ ಧಗೆಯಾಗಲಿ, ಚಳಿಯ ತೀವ್ರತೆಯಾಗಲಿ ಬಾಧಿಸಲಾರದು.');
INSERT INTO "kannada" VALUES(76,14,'76:14','ಅದರ (ಸ್ವರ್ಗದ ಮರಗಳ) ನೆರಳು ಅವರಿಗೆ ನಿಕಟವಿರುವುದು. ಮತ್ತು ಅದರ ಹಣ್ಣು ಗೊನೆಗಳು (ಯಾವಾಗ ಕೊಯ್ಯಲೂ) ಅವರಿಗೆ ಅನುಕೂಲ ಗೊಳಿಸಲ್ಪಟ್ಟಿರುವುವು.');
INSERT INTO "kannada" VALUES(76,15,'76:15','ಬೆಳ್ಳಿಯ ಪಾತ್ರೆಗಳನ್ನೂ ನುಣುಪಿನಿಂದ ಸ್ಫಟಿಕ ದಂತಾದ ಬೆಳ್ಳಿಯ ಹೂಜಿಗಳನ್ನೂ ಅವರ ಸುತ್ತ ತರಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(76,16,'76:16','ಅವು ಬೆಳ್ಳಿಯಿಂದಲೇ ನಿರ್ಮಿತವಾದ ಸ್ಫಟಿಕ ಪಾತ್ರೆಗಳು. ಅವರು (ಸೇವಕರು) ಅವುಗಳನ್ನು ಸರಿಯಾದ ಪ್ರಮಾಣದಲ್ಲಿ ನಿರ್ಣಯಿಸಿರುವರು.');
INSERT INTO "kannada" VALUES(76,17,'76:17','ಅಲ್ಲಿ ಅವರಿಗೆ ಸುರೆ ತುಂಬಿದ ಪಾತ್ರೆಗಳಿಂದ ಕುಡಿಸಲಾಗುವುದು. ಅದು ಶುಂಠಿ ಬೆರೆತಿರುವಂತಹ ಮದಿರೆಯಾಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(76,18,'76:18','ಅದು ಸಲ್‍ಸಬೀಲ್ ಎಂಬ ಹೆಸರಿನ ಒಂದು ಚಿಲುಮೆಯಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(76,19,'76:19','ಅವರ (ಸ್ವರ್ಗವಾಸಿಗಳ) ಮಧ್ಯೆ ಚಿರಕುಮಾರ ರಾದ ಕೆಲವು ಬಾಲಕರು ಸುತ್ತಾಡುತ್ತಿರುವರು. ತಾವು ಅವರನ್ನು ಕಂಡರೆ ಹರಡಿದ ಮುತ್ತುಗಳೆಂದೇ ಭಾವಿಸುವಿರಿ .');
INSERT INTO "kannada" VALUES(76,20,'76:20','ಅಲ್ಲಿ ತಾವು ಎತ್ತ ನೋಡಿದರೂ ಅನುಗ್ರಹಗಳನ್ನೂ ಒಂದು ಬೃಹತ್ ಸಾಮ್ರಾಜ್ಯವನ್ನೂ ಕಾಣುವಿರಿ.');
INSERT INTO "kannada" VALUES(76,21,'76:21','ಹಸಿರು ಬಣ್ಣದ ತೆಳು ರೇಶ್ಮೆ ಹಾಗೂ ದಪ್ಪ ರೇಷ್ಮೆಯ ಉಡುಪುಗಳು ಅವರ ಮೇಲಿರುವುದು. ಅವರಿಗೆ ಬೆಳ್ಳಿಯ ಬಳೆಗಳನ್ನು ತೊಡಿಸಲಾಗುವುದು ಮತ್ತು ಅವರ ಪ್ರಭು ಅವರಿಗೆ ನಿರ್ಮಲವಾದ ಪಾನೀಯವನ್ನು ಕುಡಿಸುವನು.');
INSERT INTO "kannada" VALUES(76,22,'76:22','(ಓ ಪುಣ್ಯವಂತರೇ!) ನಿಮಗಿರುವ ಪ್ರತಿ ಫಲವಿದು, ನಿಮ್ಮ ಪರಿಶ್ರಮವನ್ನು ಕೃತಜ್ಞತಾ ಪೂರ್ವಕ ಸ್ವೀಕರಿಸಲಾಗಿತ್ತು. (ಎಂದು ಅವರೊಂದಿಗೆ ಹೇಳಲಾಗುವುದು.)');
INSERT INTO "kannada" VALUES(76,23,'76:23','(ಸಂದೇಶವಾಹಕರೇ,) ನಾವೇ ಈ ಖುರ್‍ಆನನ್ನು ತಮ್ಮ ಮೇಲೆ ಸ್ವಲ್ಪ ಸ್ವಲ್ಪವಾಗಿ ಅವತೀರ್ಣ ಗೊಳಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(76,24,'76:24','ಆದುದರಿಂದ ತಾವು ತಮ್ಮ ಪ್ರಭುವಿನ ತೀರ್ಪಿಗಾಗಿ ತಾಳ್ಮೆವಹಿಸಿರಿ. ಅವರ ಪೈಕಿ ಯಾವುದೇ ಪಾಪಿಯನ್ನಾಗಲಿ, ಕೃತಘ್ನನನ್ನಾಗಲಿ ತಾವು ಅನುಸರಿಸಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(76,25,'76:25','ತಾವು ಸಂಜೆ - ಮುಂಜಾನೆಗಳಲ್ಲಿ ತಮ್ಮ ಪ್ರಭುವಿನ ನಾಮವನ್ನು ಸ್ಮರಿಸಿರಿ.');
INSERT INTO "kannada" VALUES(76,26,'76:26','ರಾತ್ರಿಯಲ್ಲಿ (ಸ್ವಲ್ಪ ಸಮಯ) ಅವನಿಗೆ ಸಾಷ್ಟಾಂಗವೆರಗಿರಿ. ರಾತ್ರೆಯ ದೀರ್ಘ ಸಮಯ ಅವನನ್ನು ಪ್ರಕೀರ್ತಿಸಿರಿ .');
INSERT INTO "kannada" VALUES(76,27,'76:27','ಅವರು ಕ್ಷಣಿಕವಾದ (ಶೀಘ್ರ ಪ್ರಾಪ್ತವಾಗುವ) ಐಹಿಕ ಜೀವನವನ್ನು ಪ್ರೀತಿಸುತ್ತಾರೆ ಮತ್ತು ಭಾರವೇರಿದ ಒಂದು ದಿನವನ್ನು ಅವರು ಉಪೇ ಕ್ಷಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(76,28,'76:28','ನಾವೇ ಅವರನ್ನು ಸೃಷ್ಟಿಸಿರುತ್ತೇವೆ ಮತ್ತು ಅವರ ಶರೀರ ಘಟನೆಯನ್ನು ಬಲಾಢ್ಯಗೊಳಿಸಿದ್ದೇವೆ. ನಾವಿಚ್ಛಿಸಿದರೆ ಅವರಂಥವರನ್ನು ಖಂಡಿತ ಬದಲಿಸಿ ತರಬಲ್ಲೆವು.');
INSERT INTO "kannada" VALUES(76,29,'76:29','ಇದೊಂದು ಉದ್ಭೋಧನೆಯಾಗಿದೆ. ಆದುದರಿಂದ ಇಚ್ಛೆಯುಳ್ಳವನು ತನ್ನ ಪ್ರಭುವಿನ ಕಡೆಗೆ ಮಾರ್ಗ ವನ್ನು ಮಾಡಿಕೊಳ್ಳಲಿ.');
INSERT INTO "kannada" VALUES(76,30,'76:30','ಅಲ್ಲಾಹನು ಇಚ್ಛಿಸದೆ ನೀವು ಇಚ್ಛಿಸಲಾರಿರಿ. ಖಂಡಿತವಾಗಿಯೂ ಅಲ್ಲಾಹನು ಸರ್ವಜ್ಞನೂ, ಯುಕ್ತಿವಂತನೂ ಆಗಿರುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(76,31,'76:31','ಅವನು ತಾನಿಚ್ಛಿಸಿದವರನ್ನು ತನ್ನ ಕಾರುಣ್ಯದಲ್ಲಿ ಪ್ರವೇಶಗೊಳಿಸುತ್ತಾನೆ. ಅಕ್ರಮಿಗಳಿಗಾಗಿ ವೇದ ನಾಜನಕವಾದ ಶಿಕ್ಷೆಯನ್ನು ಅವನು ಸಜ್ಜು ಗೊಳಿಸಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(77,1,'77:1','');
INSERT INTO "kannada" VALUES(77,2,'77:2','');
INSERT INTO "kannada" VALUES(77,3,'77:3','');
INSERT INTO "kannada" VALUES(77,4,'77:4','');
INSERT INTO "kannada" VALUES(77,5,'77:5','');
INSERT INTO "kannada" VALUES(77,6,'77:6','ನಿರಂತರ ರವಾನಿಸಲ್ಪಡುವ, ಅನಂತರ ರಭಸದಿಂದ ಬೀಸಿ ಬರುವ, ತದನಂತರ (ಮಳೆಯನ್ನು) ಹರಡುವ ವಾಯುವಿನ ಆಣೆ!. ಬೇರ್ಪಡಿಸಿ ವಿವೇಚಿಸುವ ಹಾಗೂ ನೆಪ ಗಳ ನಿವಾರಣೆಯಾಗಿಯೋ, ಮುನ್ನೆಚ್ಚರಿಕೆಯಾಗಿಯೋ ಸಂದೇಶಗಳನ್ನು ಹಾಕಿಕೊಡುವ (ಮಲಕ್‍ಗಳ) ಆಣೆ! .');
INSERT INTO "kannada" VALUES(77,7,'77:7','ನಿಮಗೆ ತಾಕೀತು ನೀಡಲಾದ ಘಟನೆಯು ಖಂಡಿತ ಸಂಭವಿಸಿಯೇ ತೀರುವುದು.');
INSERT INTO "kannada" VALUES(77,8,'77:8','ನಕ್ಷತ್ರಗಳು (ಅವುಗಳ ಪ್ರಕಾಶವನ್ನು) ಮಾಸಲ್ಪಟ್ಟಾಗ,');
INSERT INTO "kannada" VALUES(77,9,'77:9','ಆಕಾಶವು ಸೀಳಲ್ಪಡುವಾಗ,');
INSERT INTO "kannada" VALUES(77,10,'77:10','ಪರ್ವತಗಳು ಧೂಳೀಪಟಗೊಳಿಸಲ್ಪಟ್ಟಾಗ,');
INSERT INTO "kannada" VALUES(77,11,'77:11','ಮತ್ತು ಪ್ರವಾದಿಗಳಿಗೆ (ಒಟ್ಟು ಸೇರಲ್ಪಡುವ) ಸಮಯವನ್ನು ನಿಗದಿಗೊಳಿಸಲಾದಾಗ,');
INSERT INTO "kannada" VALUES(77,12,'77:12','ಯಾವ ದಿನಕ್ಕಾಗಿ ಅವರಿಗೆ ಅವಧಿ ನಿಶ್ಚಯಿಸಲಾಗಿದೆ?');
INSERT INTO "kannada" VALUES(77,13,'77:13','ತೀರ್ಮಾನದ ದಿನಕ್ಕಾಗಿಯೇ !');
INSERT INTO "kannada" VALUES(77,14,'77:14','ಆ ತೀರ್ಮಾನದ ದಿನದ ಕುರಿತು ತಮಗೇನು ಗೊತ್ತು?');
INSERT INTO "kannada" VALUES(77,15,'77:15','ಅಂದು, ಸುಳ್ಳಾಗಿಸುವವರಿಗೆ ಭೀಕರ ನಾಶವಿದೆ.');
INSERT INTO "kannada" VALUES(77,16,'77:16','ನಾವು ಪೂರ್ವಿಕರನ್ನು ನಾಶಗೊಳಿಸಲಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(77,17,'77:17','ಬಳಿಕ ಅನಂತರದವರನ್ನೂ ನಾವು ಅವರ ಹಿಂದೆಯೇ ಕಳುಹಿಸುವೆವು.');
INSERT INTO "kannada" VALUES(77,18,'77:18','ಅಪರಾಧಿಗಳೊಂದಿಗೆ ನಾವು ಹಾಗೆಯೇ ಮಾಡುವೆವು.');
INSERT INTO "kannada" VALUES(77,19,'77:19','ಸುಳ್ಳಾಗಿಸಿದವರಿಗೆ ಅಂದು ಭಯಂಕರ ನಾಶವಿದೆ!');
INSERT INTO "kannada" VALUES(77,20,'77:20','ನಾವು ನಿಮ್ಮನ್ನು ಒಂದು ದುರ್ಬಲ ದ್ರವದಿಂದ ಸೃಷ್ಟಿಸಲಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(77,21,'77:21','ಆಮೇಲೆ ಅದನ್ನು ನಾವು ಒಂದು ಸುರಕ್ಷಿತ ಸ್ಥಳದಲ್ಲಿ ನಿಕ್ಷೇಪಿಸಲಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(77,22,'77:22','ಒಂದು ನಿಶ್ಚಿತ ಕಾಲಾವಧಿಯವರೆಗೆ.');
INSERT INTO "kannada" VALUES(77,23,'77:23','');
INSERT INTO "kannada" VALUES(77,24,'77:24','ನಾವು ಹಾಗೆ (ಎಲ್ಲಾ) ನಿರ್ಣಯಿಸಿದೆವು. (ವ್ಯವಸ್ಥೆ ಗೊಳಿಸಿದೆವು) ಆದುದರಿಂದ ನಾವು ಎಷ್ಟೊಂದು ಒಳ್ಳೆಯ ವ್ಯವಸ್ಥಾಪಕರು. ಸುಳ್ಳಾಗಿಸಿದವರಿಗೆ ಅಂದು ವಿನಾಶ ಕಾದಿದೆ!');
INSERT INTO "kannada" VALUES(77,25,'77:25','ನಾವು ಭೂಮಿಯನ್ನು ಒಳಗೊಳ್ಳುವಂತಾಗಿ ಮಾಡಲಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(77,26,'77:26','ಜೀವಂತವಿರುವವರನ್ನೂ ಮೃತರನ್ನೂ.');
INSERT INTO "kannada" VALUES(77,27,'77:27','ಅದರಲ್ಲಿ ತಳ ಭದ್ರವಾಗಿ ತಲೆಯೆತ್ತಿ ನಿಂತಿರುವ ಪರ್ವತಗಳನ್ನು ನಾವು ಸ್ಥಾಪಿಸಿದೆವು. ಮತ್ತು ನಿಮಗೆ ನಾವು ಸ್ವಚ್ಛವಾದ ನೀರನ್ನು ಕುಡಿಯಲು ಕೊಟ್ಟೆವು.');
INSERT INTO "kannada" VALUES(77,28,'77:28','ಸುಳ್ಳಾಗಿಸಿದವರಿಗೆ ಅಂದು ನಾಶವಿದೆ!');
INSERT INTO "kannada" VALUES(77,29,'77:29','(ಓ ಸತ್ಯನಿಷೇಧಿಗಳೇ,) ನೀವು ಯಾವೊಂದನ್ನು ಸುಳ್ಳೆಂದು ಹೇಳುತ್ತಿದ್ದಿರೋ ನೀವೀಗ ಅದರೆಡೆಗೇ ನಡೆಯಿರಿ (ಎಂದು ಅವರೊಂದಿಗೆ ಹೇಳಲಾಗು ವುದು)');
INSERT INTO "kannada" VALUES(77,30,'77:30','ಅಂದರೆ, ಮೂರು ಕವಲುಗಳುಳ್ಳ ಕರಿ ಧೂಮದ ಇರುಳಿನ ಕಡೆಗೆ ನೀವು ಹೋಗಿ ಬಿಡಿರಿ!');
INSERT INTO "kannada" VALUES(77,31,'77:31','ಅದು (ಬಿಸಿಲಿಗೆ) ನೆರಳು ಕೊಡದು. ಅಗ್ನಿ ಜ್ವಾಲೆಯಿಂದ ರಕ್ಷಿಸದು.');
INSERT INTO "kannada" VALUES(77,32,'77:32','ಅದು (ನರಕ) ಕಟ್ಟಡದಂತಹ ದೊಡ್ಡ ದೊಡ್ಡ ಕಿಡಿಗಳನ್ನು ತೂರಿ ಬಿಡುತ್ತಿರುವುದು! ');
INSERT INTO "kannada" VALUES(77,33,'77:33','ಅವು (ಅಗ್ನಿ ಕಿಡಿಗಳು) ಹಳದಿ ಬಣ್ಣದ ಒಂಟೆಗಳಂತೆ ಇರುವುವು.');
INSERT INTO "kannada" VALUES(77,34,'77:34','ಸುಳ್ಳಾಗಿಸಿದವರಿಗೆ ಅಂದು ಮಹಾನಾಶವಿದೆ!');
INSERT INTO "kannada" VALUES(77,35,'77:35','ಇದು, ಅವರು ಮಾತನಾಡದ ದಿನವಾಗಿರುವುದು .');
INSERT INTO "kannada" VALUES(77,36,'77:36','ಅಂದು ಅವರಿಗೆ ಅನುಮತಿಯನ್ನು ನೀಡಲಾಗುವುದಿಲ್ಲ. ಹಾಗಿದ್ದರೆ ಅವರು ನೆಪ - ನೆವನಗಳನ್ನು ಹೇಳುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(77,37,'77:37','ಸುಳ್ಳಾಗಿಸಿದವರಿಗೆ ಅಂದು ವಿಪರೀತ ನಾಶವಿದೆ !');
INSERT INTO "kannada" VALUES(77,38,'77:38','ಇಂದು ತೀರ್ಪಿನ ದಿನ, ನಾವು ನಿಮ್ಮನ್ನೂ ನಿಮ್ಮ ಪೂರ್ವಜರನ್ನೂ (ಇಲ್ಲಿ) ಒಟ್ಟುಗೂಡಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(77,39,'77:39','ಈಗ ನಿಮಗೆ ಏನಾದರೂ ಉಪಾಯ ಸಾಧ್ಯವಿದ್ದರೆ ಅದನ್ನು ನನ್ನ ವಿರುದ್ಧ ಪ್ರಯೋಗಿಸಿ ಕೊಳ್ಳಿರಿ.');
INSERT INTO "kannada" VALUES(77,40,'77:40','ಸುಳ್ಳಾಗಿಸಿದವರಿಗೆ ಅಂದು ಬಹು ದೊಡ್ಡ ನಾಶವಿದೆ.!');
INSERT INTO "kannada" VALUES(77,41,'77:41','(ಅಂದು) ಧರ್ಮನಿಷ್ಠರು ದಟ್ಟ ವೃಕ್ಷಗಳ ನೆರಳುಗಳಲ್ಲೂ ಚಿಲುಮೆಗಳಲ್ಲೂ ಇರುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(77,42,'77:42','ಅವರು ಇಷ್ಟಪಡುವ ಹಣ್ಣುಹಂಪಲುಗಳೊಂದಿಗೆ.');
INSERT INTO "kannada" VALUES(77,43,'77:43','ನೀವು ನಿರ್ವಹಿಸುತ್ತಿದ್ದ ಕರ್ಮಗಳ ಪ್ರತಿಫಲವಾಗಿ (ಇಂದು) ಸಂತೋಷದಿಂದ ತಿನ್ನಿರಿ ಮತ್ತು ಕುಡಿ ಯಿರಿ. (ಎಂದು ಅವರೊಂದಿಗೆ ಹೇಳಲಾಗುವುದು)');
INSERT INTO "kannada" VALUES(77,44,'77:44','ನಾವು ಸದ್ವøತ್ತರಿಗೆ ಹೀಗೆಯೇ ಸತ್ಫಲವನ್ನು ಕೊಡುವುದು.');
INSERT INTO "kannada" VALUES(77,45,'77:45','ಸುಳ್ಳಾಗಿಸಿದವರಿಗೆ ಅಂದು ಭಯಾನಕ ನಾಶವಿದೆ!');
INSERT INTO "kannada" VALUES(77,46,'77:46','ಸ್ವಲ್ಪ ಕಾಲ ನೀವು ತಿನ್ನಿರಿ ಮತ್ತು ಸುಖಪಡಿರಿ - ವಾಸ್ತವದಲ್ಲಿ ನೀವು ಅಪರಾಧಿಗಳಾಗಿದ್ದೀರಿ.');
INSERT INTO "kannada" VALUES(77,47,'77:47','ಸುಳ್ಳಾಗಿಸಿದವರಿಗೆ ಅಂದು ವಿನಾಶವಿದೆ.');
INSERT INTO "kannada" VALUES(77,48,'77:48','ಇವರೊಡನೆ ತಲೆಬಾಗಿರಿ ಎಂದಾಗ ಇವರು ತಲೆ ಬಾಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(77,49,'77:49','ಸುಳ್ಳಾಗಿಸಿದವರಿಗೆ ಅಂದು ವಿನಾಶವಿದೆ.');
INSERT INTO "kannada" VALUES(77,50,'77:50','ಇನ್ನು ಇದರ (ಖುರ್‍ಆನ್) ನಂತರ ಇನ್ನಾವ ವೃತ್ತಾಂತದ ಮೇಲೆ ತಾನೇ ಇವರು ವಿಶ್ವಾಸವಿರಿ ಸುವುದು? ');
INSERT INTO "kannada" VALUES(78,1,'78:1','ಅವರು ಯಾವುದರ ಕುರಿತು ಪರಸ್ಪರ ವಿಚಾರಿಸುತ್ತಿದ್ದಾರೆ? ');
INSERT INTO "kannada" VALUES(78,2,'78:2','');
INSERT INTO "kannada" VALUES(78,3,'78:3','ಯಾವ ಘಟನೆಯ ಕುರಿತು ಅವರು ಭಿನ್ನಮತ ಹೊಂದಿದ್ದಾರೋ ಅಂತಹ ಗಂಭೀರ ಘಟನೆಯ ಕುರಿತು .');
INSERT INTO "kannada" VALUES(78,4,'78:4','ಖಂಡಿತ ಬೇಡ, ಮುಂದೆ (ಪುನರುತ್ಥಾನದ ದಿನದಲ್ಲಿ) ಅವರು ತಿಳಿಯಲಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(78,5,'78:5','ಖಡಾ ಖಂಡಿತಾ ಬೇಡ, ಅವರು ತಿಳಿಯಲಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(78,6,'78:6','ಭೂಮಿಯನ್ನು ನಾವು ಹಾಸನ್ನಾಗಿ ಮಾಡಲಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(78,7,'78:7','ಪರ್ವತಗಳನ್ನು ಮೊಳೆಗಳನ್ನಾಗಿ ನಾಟಿ ಬಿಟ್ಟಿಲ್ಲವೇ');
INSERT INTO "kannada" VALUES(78,8,'78:8','ನಿಮ್ಮನ್ನು (ಗಂಡು-ಹೆಣ್ಣು) ಜೋಡಿಗಳನ್ನಾಗಿ ನಾವು ಸೃಷ್ಟಿಸಲಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(78,9,'78:9','ನಿಮ್ಮ ನಿದ್ದೆಯನ್ನು ವಿಶ್ರಾಂತಿಯನ್ನಾಗಿ ನಾವು ಮಾಡಿಲ್ಲವೆ?');
INSERT INTO "kannada" VALUES(78,10,'78:10','ರಾತ್ರೆಯನ್ನು ಮುಚ್ಚುವ ವಸ್ತ್ರವನ್ನಾಗಿ ನಾವು ಮಾಡಿಲ್ಲವೆ?');
INSERT INTO "kannada" VALUES(78,11,'78:11','ಹಗಲನ್ನು ಉಪಜೀವನ ಅರಸುವ ಸಮಯವ ನ್ನಾಗಿ ನಾವು ಮಾಡಿಲ್ಲವೆ?');
INSERT INTO "kannada" VALUES(78,12,'78:12','ನಿಮ್ಮ ಮೇಲೆ ಅತ್ಯಂತ ಬಲಿಷ್ಟವಾದ ಏಳು ಗಗನಗಳನ್ನು ನಾವು ನಿರ್ಮಿಸಿಲ್ಲವೆ?');
INSERT INTO "kannada" VALUES(78,13,'78:13','ಜ್ವಾಜಲ್ಯಮಾನ ದೀಪವನ್ನು ನಾವು ಸ್ಥಾಪಿಸಿಲ್ಲವೆ? ');
INSERT INTO "kannada" VALUES(78,14,'78:14','ಘನೀಕೃತ ಮೇಘಗಳಿಂದ ಧಾರಾಕಾರ ಮಳೆಯನ್ನು ನಾವು ಸುರಿಸುತ್ತಿಲ್ಲವೆ?');
INSERT INTO "kannada" VALUES(78,15,'78:15','ಅದರ ಮೂಲಕ ಧಾನ್ಯವನ್ನೂ ಸಸ್ಯಗಳನ್ನೂ ನಾವು ಹೊರಡಿಸಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(78,16,'78:16','ದಟ್ಟ ತೋಟಗಳನ್ನು ಸೃಷ್ಟಿಸಲಿಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(78,17,'78:17','ಖಂಡಿತವಾಗಿಯೂ (ಸಜ್ಜನರು ಮತ್ತು ದುರ್ಜನರನ್ನು) ಬೇರ್ಪಡಿಸುವ ದಿನವು ಸಮಯ ನಿಗದಿತವಾಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(78,18,'78:18','ಅಂದರೆ ಕಹಳೆಯಲ್ಲಿ ಊದಲಾಗುವ ದಿನ. ಆಗ ನೀವು ತಂಡೋಪತಂಡವಾಗಿ ಬರುವಿರಿ.');
INSERT INTO "kannada" VALUES(78,19,'78:19','ಆಕಾಶವು ಬಿರುಕು ಬೀಳುವುದು. ಆಗ ಅದು ಬಾಗಿಲುಗಳದ್ದಾಗುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(78,20,'78:20','ಪರ್ವತಗಳನ್ನು ಚಲಾಯಿಸಲಾಗುವುದು. ಆಗ ಅವು ಮರೀಚಿಕೆಯಾಗುತ್ತವೆ. (ಅನಾಯಾಸ ಚಲನೆಯ ವಿಷಯದಲ್ಲಿ)');
INSERT INTO "kannada" VALUES(78,21,'78:21','ಖಂಡಿತವಾಗಿಯೂ ನರಕವು ಕಟ್ಟೆಚ್ಚರದ ನಿರೀಕ್ಷೆಯಲ್ಲಿರುತ್ತದೆ .');
INSERT INTO "kannada" VALUES(78,22,'78:22','ಅಕ್ರಮಿಗಳ ಆವಾಸ ಸ್ಥಾನವಾಗಿ.');
INSERT INTO "kannada" VALUES(78,23,'78:23','ಅವರು ಆ ನರಕದಲ್ಲಿ ಅನಂತಕಾಲ ಬಿದ್ದು ಕೊಂಡಿರುವರು .');
INSERT INTO "kannada" VALUES(78,24,'78:24','ಅಲ್ಲಿ ಅವರು ತಣಿವನ್ನಾಗಲಿ ಯಾವುದೇ ಪಾನೀಯವನ್ನಾಗಲಿ ಸವಿಯಲಾರರು.');
INSERT INTO "kannada" VALUES(78,25,'78:25','ಕುದಿಯುವ ಬಿಸಿ ನೀರು ಮತ್ತು ಅತೀ ದುರ್ಗಂಧಮಯ (ನರಕದವರ ದೇಹಗಳಿಂದ ಹೊರ ಬರುವ) ಕೀವಿನ ಹೊರತು.');
INSERT INTO "kannada" VALUES(78,26,'78:26','(ಅವರು ಭೂಮಿಯಲ್ಲಿ ಮಾಡಿದ ದುಷ್ಕರ್ಮ ಗಳಿಗೆ) ಇದು ತಕ್ಕುದಾದ ಪ್ರತಿಫಲವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(78,27,'78:27','ಏಕೆಂದರೆ ಅವರು ವಿಚಾರಣಾ ದಿನವನ್ನು ಭಯಪಟ್ಟಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(78,28,'78:28','ನಮ್ಮ ಎಲ್ಲಾ ದೃಷ್ಟಾಂತಗಳನ್ನು ಅವರು ಸುಳ್ಳಾಗಿಸಿದ್ದರು.');
INSERT INTO "kannada" VALUES(78,29,'78:29','ಅವರ ಸಕಲ ಪ್ರವೃತ್ತಿಗಳನ್ನು ದಾಖಲೆ ಸಹಿತ ನಾವು ತಿಳಿದಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(78,30,'78:30','ಇಂದು ನೀವು ನರಕ ಶಿಕ್ಷೆಯ ರುಚಿಯನ್ನು ಸವಿಯಿರಿ. ನಿಮಗೆ ಯಾತನೆಯ ಹೊರತು ಬೇರೇನನ್ನೂ ನಾವು ಹೆಚ್ಚಿಸಲಾರೆವು.');
INSERT INTO "kannada" VALUES(78,31,'78:31','ಖಂಡಿತವಾಗಿಯೂ ಧರ್ಮನಿಷ್ಠರಿಗೆ ರಕ್ಷಣೆಯ ತಾಣವಿದೆ.');
INSERT INTO "kannada" VALUES(78,32,'78:32','ತೋಟಗಳಿವೆ. ದ್ರಾಕ್ಷೆಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(78,33,'78:33','ಕುಂಭಕುಚದ, ಸಮವಯಸ್ಕ ತರುಣಿಯರಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(78,34,'78:34','ತುಂಬಿ ತುಳುಕುವ ಪಾನಪಾತ್ರೆಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(78,35,'78:35','ಅಲ್ಲಿ ಅವರು ಅನಗತ್ಯ ಹಾಗೂ ಅಸತ್ಯ ಮಾತು ಗಳನ್ನು ಕೇಳಲಾರರು.');
INSERT INTO "kannada" VALUES(78,36,'78:36','ಇದು ನಿನ್ನ ಪ್ರಭುವಿನ ವತಿಯಿಂದ ಯಥೇಚ್ಛ ಹಾಗೂ ಉದಾರ ಪ್ರತಿಫಲ .');
INSERT INTO "kannada" VALUES(78,37,'78:37','ಇದು ಭೂಮ್ಯಾಕಾಶಗಳ ಮತ್ತು ಅವುಗಳ ನಡುವೆ ಇರುವ ಸಕಲ ವಸ್ತುಗಳ ರಕ್ಷಕನಾಗಿರುವ ಪರಮ ಕೃಪಾಳುವಾದ ಪ್ರಭುವಿನ ಕಡೆಯಿಂದ. ಅಂದು ಯಾರಿಗೂ ಅವನಲ್ಲಿ ಸಂಭಾಷಣೆಗೆ ಸಾಧ್ಯವಿರದು .');
INSERT INTO "kannada" VALUES(78,38,'78:38','ಆತ್ಮ ಮತ್ತು ಮಲಕ್‍ಗಳು ಸಾಲುಸಾಲಾಗಿ ನಿಲ್ಲುವ ಆ ದಿನ! ಕೃಪಾಳುವಾದ ಅಲ್ಲಾಹನು ಸಮ್ಮತಿ ನೀಡಿದ ಹಾಗೂ ಸತ್ಯವನ್ನೇ ನುಡಿದವರಲ್ಲದೆ ಮತ್ತಾರೂ ಅಂದು ಮಾತಾಡಲಾರರು .');
INSERT INTO "kannada" VALUES(78,39,'78:39','ಆ ದಿನ ಬರುವುದಂತೂ ನಿಜ. ಆದ್ದರಿಂದ ಇಚ್ಛೆಯುಳ್ಳವನು ತನ್ನ ಪ್ರಭುವಿನ ಕಡೆಗೆ ವಿಧೇಯತೆಯಿಂದ ಮರಳಲಿ.');
INSERT INTO "kannada" VALUES(78,40,'78:40','ನಿಮಗೆ ನಾವು ಖಂಡಿತ ಎಚ್ಚರಿಕೆ ನೀಡಿದ್ದೇವೆ. ಸನ್ನಿಹಿತ ಶಿಕ್ಷೆಯ ಬಗ್ಗೆ! ಪ್ರತಿಯೊಬ್ಬನೂ ತಾನೆ ಸಗಿದ ಕರ್ಮ ಫಲವನ್ನು ಕಾಣುವ ಹಾಗೂ ಸತ್ಯ ನಿಷೇಧಿಯು; “ನನ್ನ ನಾಶವೇ, ನಾನು ಮಣ್ಣಾಗಿದ್ದರೆ ಎಷ್ಟು ಚೆನ್ನಾಗಿತ್ತು” ಎಂದು ಪ್ರಲಾಪಿಸುವ ದಿನ! ');
INSERT INTO "kannada" VALUES(79,1,'79:1','ಆಳಕ್ಕಿಳಿದು ನಿಷ್ಠುರವಾಗಿ ಎಳೆದೊಯ್ಯುವವರಾಣೆ!');
INSERT INTO "kannada" VALUES(79,2,'79:2','ಮೃದುವಾಗಿ ಹೊರತೆಗೆಯುವವರಾಣೆ!');
INSERT INTO "kannada" VALUES(79,3,'79:3','ವೇಗವಾಗಿ ಈಜಿ ಬರುತ್ತಿರುವವರಾಣೆ!');
INSERT INTO "kannada" VALUES(79,4,'79:4','ಧಾವಿಸಿ ಮುನ್ನೇರುತ್ತಿರುವವರಾಣೆ!');
INSERT INTO "kannada" VALUES(79,5,'79:5','(ಪ್ರಪಂಚದ) ಕಾರ್ಯಗಳನ್ನು ನಿಯಂತ್ರಿಸುವವರಾಣೆ! ');
INSERT INTO "kannada" VALUES(79,6,'79:6','ಕಂಪಿಸುವುದೆಲ್ಲಾ ಕಂಪಿಸುವ ದಿನ .');
INSERT INTO "kannada" VALUES(79,7,'79:7','ನಂತರದ ಕಾರ್ಯವು ಆದರ ಹಿಂದೆಯೇ ಬರುವುದು .');
INSERT INTO "kannada" VALUES(79,8,'79:8','ಹೃದಯಗಳು ಅಂದು ಭಯದಿಂದ ತತ್ತರಿಸಿವೆ.');
INSERT INTO "kannada" VALUES(79,9,'79:9','ಅಂದು ಅವರ ಕಣ್ಣುಗಳು ಭಯಗ್ರಸ್ತವಾಗಿವೆ.');
INSERT INTO "kannada" VALUES(79,10,'79:10','ಅವರು ಈಗ ಕೇಳುವರು, (ಸತ್ತ ನಂತರ) ನಮ್ಮನ್ನು ಪೂರ್ವಸ್ಥಿತಿಗೆ ಮರಳಿಸಲಾಗುವುದೇ?!');
INSERT INTO "kannada" VALUES(79,11,'79:11','ನಾವು ಜೀರ್ಣಿಸಿದ ಎಲುಬುಗಳಾಗಿ ಬಿಟ್ಟರೂ,');
INSERT INTO "kannada" VALUES(79,12,'79:12','ಹಾಗಾದರೆ ಅದೊಂದು ನಷ್ಟದ ಮರಳುವಿಕೆಯೇ ಸರಿ ಎನ್ನುತ್ತಾರವರು.');
INSERT INTO "kannada" VALUES(79,13,'79:13','ಅದು ಒಂದೇ ಒಂದು ಭೀಕರ ಘರ್ಜನೆ ಮಾತ್ರ ವಾಗಿರುವುದು.');
INSERT INTO "kannada" VALUES(79,14,'79:14','ಅಷ್ಟರಲ್ಲೇ ಅವರು (ಸತ್ತವರು) ಭೂಮಿಯ ಮೇಲೆ ಬರುವರು.');
INSERT INTO "kannada" VALUES(79,15,'79:15','ತಮಗೆ ಮೂಸಾರ ವೃತ್ತಾಂತವು ತಲುಪಿದೆಯೇ?');
INSERT INTO "kannada" VALUES(79,16,'79:16','ಅಂದರೆ ಪವಿತ್ರ ‘ಥುವಾ’ ಕಣಿವೆಯಲ್ಲಿ ಅವರ ಪ್ರಭುವು ಅವರನ್ನು ಕರೆದಾಗ!');
INSERT INTO "kannada" VALUES(79,17,'79:17','ಫಿರ್‍ಔನನ ಬಳಿಗೆ ಹೋಗು. ಖಂಡಿತ, ಅವನು ಮಹಾ ಧಿಕ್ಕಾರಿಯಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(79,18,'79:18','ಶುದ್ಧನಾಗಲು ನೀನು ಬಯಸುತ್ತೀಯಾ? ಎಂದು ಕೇಳು.');
INSERT INTO "kannada" VALUES(79,19,'79:19','ನಿನಗೆ ನಾನು ನಿನ್ನ ಪ್ರಭುವಿನ ದಾರಿ ತೋರಿಸುವೆನು. ಆಗ ನೀನು ಅವನನ್ನು ಭಯಪಟ್ಟು ಜೀವಿಸುವೆ.');
INSERT INTO "kannada" VALUES(79,20,'79:20','ತರುವಾಯ ಮೂಸಾ ಅವನಿಗೆ ದೊಡ್ಡ ದೃಷ್ಟಾಂತವನ್ನು ತೋರಿಸಿದರು .');
INSERT INTO "kannada" VALUES(79,21,'79:21','ಅವನು ನಿಷೇಧಿಸಿದನು ಹಾಗೂ ಧಿಕ್ಕರಿಸಿದನು.');
INSERT INTO "kannada" VALUES(79,22,'79:22','ನಂತರ ಬಹಿರ್ಮುಖನಾದನು. ಕ್ಷೋಭೆಯಲ್ಲಿ ತೀವ್ರೋನ್ಮುಖನಾಗಿ.');
INSERT INTO "kannada" VALUES(79,23,'79:23','(ತನ್ನವರನ್ನು) ಒಗ್ಗೂಡಿಸಿದನು. ಕೂಗಿ ಕರೆದನು.');
INSERT INTO "kannada" VALUES(79,24,'79:24','ನಾನೇ ನಿಮ್ಮ ಪರಮೋಚ್ಚ ಪ್ರಭು ಎಂದು ಘೋಷಿಸಿದನು.');
INSERT INTO "kannada" VALUES(79,25,'79:25','ಕೊನೆಗೆ ಅಲ್ಲಾಹನು ಆತನನ್ನು ಪಾರತ್ರಿಕ ಮತ್ತು ಐಹಿಕ ಶಿಕ್ಷೆಗಳಿಗೆ ಗುರಿಪಡಿಸಿದನು.');
INSERT INTO "kannada" VALUES(79,26,'79:26','ಈ ಹೇಳಿದುದರಲ್ಲಿ ಅಲ್ಲಾಹನನ್ನು ಭಯ ಪಡುವವರಿಗೆ ಖಂಡಿತ ಪಾಠವಿದೆ.');
INSERT INTO "kannada" VALUES(79,27,'79:27','ನಿಮ್ಮನ್ನು ಸೃಷ್ಟಿಸುವುದೋ ಅಥವಾ ಆಕಾಶವನ್ನು ಸೃಷ್ಟಿಸುವುದೋ ಹೆಚ್ಚು ಪ್ರಯಾಸಕರ? ಅವನು ಆಕಾಶವನ್ನು ಸೃಷ್ಟಿಸಿದನು .');
INSERT INTO "kannada" VALUES(79,28,'79:28','ಅದರ ಛಾವಣಿಯನ್ನು ಎತ್ತರಿಸಿದನು ಹಾಗೂ ಅದನ್ನು ಸಮರ್ಪಕಗೊಳಿಸಿದನು.');
INSERT INTO "kannada" VALUES(79,29,'79:29','ಅವನು ಅದರ ರಾತ್ರಿಗೆ ಕತ್ತಲು ಕವಿಸಿದನು ಹಾಗೂ ಅದರ ಬೆಳಕನ್ನು ಪ್ರಕಟಗೊಳಿಸಿದನು .');
INSERT INTO "kannada" VALUES(79,30,'79:30','ಅದರ ನಂತರ ಭೂಮಿಯನ್ನು ಹಾಸಿದನು .');
INSERT INTO "kannada" VALUES(79,31,'79:31','ಅದರಿಂದ ಅದರ ನೀರನ್ನೂ ಮೇವನ್ನೂ ಹೊರಡಿಸಿದನು.');
INSERT INTO "kannada" VALUES(79,32,'79:32','ಅದರಲ್ಲಿ ಪರ್ವತಗಳನ್ನು ನಾಟಿದನು.');
INSERT INTO "kannada" VALUES(79,33,'79:33','ನಿಮ್ಮ ಹಾಗೂ ನಿಮ್ಮ ಜಾನುವಾರುಗಳ ಪ್ರಯೋಜನಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(79,34,'79:34','ಮುಂದೆ ಅತೀ ಭಯಂಕರವಾದ ವಿಪತ್ತು ಬಂದಾಗ, ');
INSERT INTO "kannada" VALUES(79,35,'79:35','ಮನುಷ್ಯನು ತಾನು ಮಾಡಿದ್ದನ್ನು ಸ್ಮರಿಸುವ ದಿನವದು!');
INSERT INTO "kannada" VALUES(79,36,'79:36','ಜ್ವಲಿಸುವ ನರಕವು ನೋಡುಗರಿಗೆ ಪ್ರದರ್ಶಿಸಲ್ಪಡುವ ದಿನ.');
INSERT INTO "kannada" VALUES(79,37,'79:37','ಯಾವನು ದುರ್ಮಾರ್ಗಿಯಾಗಿರುವನೋ,');
INSERT INTO "kannada" VALUES(79,38,'79:38','ಹಾಗೂ ಕೇವಲ ಭೌತಿಕ ಜೀವನವನ್ನು ಆಯ್ದು ಕೊಂಡಿರುವನೋ,');
INSERT INTO "kannada" VALUES(79,39,'79:39','ಖಂಡಿತವಾಗಿಯೂ ನರಕವೇ ಅವನ ಅಂತಿಮ ನೆಲೆಯಾಗಿರುವುದು.');
INSERT INTO "kannada" VALUES(79,40,'79:40','ಯಾವನು ತನ್ನ ಪ್ರಭುವಿನ ಸನ್ನಿಧಾನವನ್ನು ಭಯಪಟ್ಟಿರುವನೋ ಹಾಗೂ ದೇಹವನ್ನು ಕಾಮನೆಗಳಿಂದ ದೂರವಿರಿಸಿಕೊಳ್ಳುವನೋ,');
INSERT INTO "kannada" VALUES(79,41,'79:41','ಖಂಡಿತ, ಸ್ವರ್ಗವೇ ಅವನ ಅಂತಿಮ ನೆಲೆಯಾಗಿರುವುದು.');
INSERT INTO "kannada" VALUES(79,42,'79:42','ಅವರು ಅಂತ್ಯದಿನ ಬರುವುದು ಯಾವಾಗ ಎಂದು ತಮ್ಮಲ್ಲಿ ಪ್ರಶ್ನಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(79,43,'79:43','ಅದನ್ನು ಅವರಿಗೆ ತಿಳಿಸಿಕೊಡುವ ಅರಿವು ತಮಗೆಲ್ಲಿದೆ?');
INSERT INTO "kannada" VALUES(79,44,'79:44','ಅದರ ಅರಿವಿನ ಪಾರಮ್ಯವು ನಿಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಗಿದೆ.');
INSERT INTO "kannada" VALUES(79,45,'79:45','ಅಂತ್ಯದಿನವನ್ನು ಭಯಪಡುವವರಿಗೆ ತಾವು ಎಚ್ಚರಿಕೆಯನ್ನು ನೀಡುವವರು ಮಾತ್ರವಾಗಿರುವಿರಿ .');
INSERT INTO "kannada" VALUES(79,46,'79:46','ಅಂತ್ಯದಿನವನ್ನು ಅವರು ಕಣ್ಣಾರೆ ಕಾಣುವ ದಿನ ನಾವು ಭೂಮಿಯಲ್ಲಿ ತಂಗಿದ್ದು ಕೇವಲ ಸಂಜೆ ಅಥವಾ ಮುಂಜಾನೆ ಮಾತ್ರವಾಗಿದೆಯೆಂದು ಅವರು ಭಾವಿಸುವರು.');
INSERT INTO "kannada" VALUES(80,1,'80:1','ಅವರು (ಪ್ರವಾದಿ) ನಿರ್ಲಕ್ಷ್ಯಮುಖಿಯಾದರು ಹಾಗೂ ವಿಮುಖರಾದರು.');
INSERT INTO "kannada" VALUES(80,2,'80:2','ಅಂಧ ವ್ಯಕ್ತಿಯು (ಇಬ್‍ನು ಉಮ್ಮಿ ಮಕ್ತೂಮ್) ಅವರ ಬಳಿ ಬಂದ ಕಾರಣಕ್ಕಾಗಿ .');
INSERT INTO "kannada" VALUES(80,3,'80:3','ತಮಗೆ ತಿಳಿಸಿಕೊಟ್ಟ ವಸ್ತು ಯಾವುದು? ಆ ವ್ಯಕ್ತಿ ಬಹುಶಃ ಪರಿಶುದ್ಧಿ ಪ್ರಾಪ್ತನಾಗುವ ಬಗ್ಗೆ!');
INSERT INTO "kannada" VALUES(80,4,'80:4','ಅಥವಾ ತಮ್ಮ ಉಪದೇಶವನ್ನು ಆಲಿಸುವ ಬಗ್ಗೆ! ಆ ಉಪದೇಶ ಆತನಿಗೆ ಫಲಕೊಟ್ಟೀತು.');
INSERT INTO "kannada" VALUES(80,5,'80:5','ಆದರೆ (ಸಿರಿವಂತಿಕೆಯಿಂದ) ಸ್ವಾವಲಂಬಿಯಾ ದವನು.');
INSERT INTO "kannada" VALUES(80,6,'80:6','ಅವನ ಕಡೆಗೆ ತಾವು ಗಮನಹರಿಸಿದ್ದೀರಿ.');
INSERT INTO "kannada" VALUES(80,7,'80:7','ಅವನು ಶುದ್ಧನಾಗದಿದ್ದರೆ ತಮ್ಮ ಮೇಲೆ ಯಾವುದೇ ದೋಷವಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(80,8,'80:8','ಆದರೆ ಒಳಿತನ್ನು ಅರಸಿ ತಮ್ಮ ಬಳಿ ಧಾವಿಸುತ್ತಿರುವ,');
INSERT INTO "kannada" VALUES(80,9,'80:9','ಮತ್ತು ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡುವ,');
INSERT INTO "kannada" VALUES(80,10,'80:10','ವ್ಯಕ್ತಿಯ ಬಗ್ಗೆ ನೀವು ನಿರ್ಲಕ್ಷ್ಯ ವಹಿಸಿದಿರಿ.');
INSERT INTO "kannada" VALUES(80,11,'80:11','ಖಂಡಿತ ಹಾಗೆ ಮಾಡಬೇಡಿರಿ. ಇದೊಂದು ಉಪದೇಶವಾಗಿರುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(80,12,'80:12','ಇಚ್ಚಿಸುವವರು ಇದನ್ನು ಸ್ಮರಿಸಿ ಪಾಠ ಕಲಿಯಲಿ .');
INSERT INTO "kannada" VALUES(80,13,'80:13','ಇದು ಪವಿತ್ರವಾದ ಗ್ರಂಥಗಳಲ್ಲಿ ದಾಖಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(80,14,'80:14','ಅದು ಪರಿಶುದ್ಧವಾದ, ಮೇರು ಮಟ್ಟದ ಗ್ರಂಥ .');
INSERT INTO "kannada" VALUES(80,15,'80:15','');
INSERT INTO "kannada" VALUES(80,16,'80:16','ಮಾನ್ಯರೂ ಸಚ್ಚರಿತರೂ ಆದ ದೇವಚರರ ಕರಲಿಪಿಗಳಿಂದ ಲಿಖಿತವಾಗಿದೆ .');
INSERT INTO "kannada" VALUES(80,17,'80:17','ಸತ್ಯನಿಷೇಧಿಯು ಶಪಿತನಾದನು. ಅವನನ್ನು ಇಷ್ಟೊಂದು ಧಿಕ್ಕಾರಿಯನ್ನಾಗಿ ಮಾಡಿದ್ದಾದರೂ ಏನು? ');
INSERT INTO "kannada" VALUES(80,18,'80:18','ಅಲ್ಲಾಹನು ಅವನನ್ನು ಯಾವ ವಸ್ತುವಿನಿಂದ ಸೃಷ್ಟಿಸಿದ್ದಾನೆ?');
INSERT INTO "kannada" VALUES(80,19,'80:19','ಅವನನ್ನು ವೀರ್ಯ ಬಿಂದುವಿನಿಂದ ಸೃಷ್ಟಿಸಿರುವನು ಹಾಗೂ ಹಲವು ಹಂತಗಳಾಗಿ ನಿರ್ಣಯಿಸಿರುವನು.');
INSERT INTO "kannada" VALUES(80,20,'80:20','ತರುವಾಯ ಹೊರ ಬರುವ ಹಾದಿಯನ್ನು ಅವನಿಗೆ ಸುಗಮಗೊಳಿಸಿದನು.');
INSERT INTO "kannada" VALUES(80,21,'80:21','ಆಮೇಲೆ ಅವನನ್ನು ಮರಣಗೊಳಿಸುವನು. ಬಳಿಕ ಅವನನ್ನು ಸಮಾಧಿಯೊಳಗೆ ತಲುಪಿಸುವನು .');
INSERT INTO "kannada" VALUES(80,22,'80:22','ನಂತರ ತಾನು ಉದ್ದೇಶಿಸಿದಾಗ ಅವನಿಗೆ ಪುನರ್ಜೀವ ನೀಡಿ ಹೊರತರುವನು.');
INSERT INTO "kannada" VALUES(80,23,'80:23','ಖಂಡಿತ! ಅಲ್ಲಾಹು ಆದೇಶಿಸಿದ್ದನ್ನು ಅವನು ಪಾಲಿಸಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(80,24,'80:24','ಮನುಷ್ಯನು ತನ್ನ ಆಹಾರ ಪದಾರ್ಥಗಳನ್ನು ನೋಡಲಿ.');
INSERT INTO "kannada" VALUES(80,25,'80:25','ಖಂಡಿತವಾಗಿಯೂ ನಾವು ಧಾರಾಕಾರ ಜಲಧಾರೆ ಗೈದಿರುವೆವು.');
INSERT INTO "kannada" VALUES(80,26,'80:26','ನಂತರ ನಾವು ಭೂಮಿಯನ್ನು ಶಕ್ತವಾಗಿ ಸೀಳಿದೆವು.');
INSERT INTO "kannada" VALUES(80,27,'80:27','ಅನಂತರ ನಾವು ಧಾನ್ಯಗಳನ್ನು ಮೊಳಕೆ ಯೊಡೆಯಿಸಿದೆವು.');
INSERT INTO "kannada" VALUES(80,28,'80:28','ದ್ರಾಕ್ಷೆಯನ್ನೂ ಹುಲ್ಲುಗಳನ್ನೂ. (ಉತ್ಪಾದಿಸಿದೆವು)');
INSERT INTO "kannada" VALUES(80,29,'80:29','ಆಲಿವ್ ಹಾಗೂ ಖರ್ಜೂರದ ವೃಕ್ಷಗಳನ್ನೂ.');
INSERT INTO "kannada" VALUES(80,30,'80:30','ದಟ್ಟವಾದ ತೋಟಗಳನ್ನೂ.');
INSERT INTO "kannada" VALUES(80,31,'80:31','ಹಣ್ಣು ಹಂಪಲುಗಳನ್ನೂ ಮೇವನ್ನೂ.');
INSERT INTO "kannada" VALUES(80,32,'80:32','ನಿಮ್ಮ ಹಾಗೂ ನಿಮ್ಮ ಜಾನುವಾರುಗಳ ಅನುಕೂಲಕ್ಕಾಗಿ.');
INSERT INTO "kannada" VALUES(80,33,'80:33','ಭೀಕರ ಶಬ್ದ ಮೊಳಗಿದರೆ, ');
INSERT INTO "kannada" VALUES(80,34,'80:34','ಆ ದಿನ ಮನುಷ್ಯನು ತನ್ನ ಸಹೋದರನಿಂದ ಓಡಿ ಹೋಗುವನು,');
INSERT INTO "kannada" VALUES(80,35,'80:35','ತನ್ನ ತಾಯಿಯಿಂದಲೂ ತಂದೆಯಿಂದಲೂ,');
INSERT INTO "kannada" VALUES(80,36,'80:36','ತನ್ನ ಪತ್ನಿಯಿಂದಲೂ ಮಕ್ಕಳಿಂದಲೂ. (ಓಡಿ ಹೋಗುವನು) ');
INSERT INTO "kannada" VALUES(80,37,'80:37','ಅಂದು ಪ್ರತಿಯೊಬ್ಬರಿಗೂ ಇತರರ ಚಿಂತೆಯಿಂದ ಮುಕ್ತವಾದ ಕಾರ್ಯವಿರುವುದು.');
INSERT INTO "kannada" VALUES(80,38,'80:38','ಅಂದು ಕೆಲವು ಮುಖಗಳು ಬೆಳಗಿರುತ್ತವೆ.');
INSERT INTO "kannada" VALUES(80,39,'80:39','ನಗುತ್ತಿರುತ್ತವೆ, ಆನಂದ ತುಂದಿಲವಾಗಿರುತ್ತವೆ.');
INSERT INTO "kannada" VALUES(80,40,'80:40','ಅಂದು ಕೆಲವು ಮುಖಗಳು ಧೂಳುಮಯವಾಗಿರುತ್ತವೆ.');
INSERT INTO "kannada" VALUES(80,41,'80:41','ಅವುಗಳ ಮೇಲೆ ಇರುಳು, ಕರಿ ಆವರಿಸಿರುತ್ತವೆ.');
INSERT INTO "kannada" VALUES(80,42,'80:42','ಅವರೇ ದುಷ್ಕರ್ಮಿಗಳಾದ ಅವಿಶ್ವಾಸಿಗಳು.');
INSERT INTO "kannada" VALUES(81,1,'81:1','ಸೂರ್ಯನು ಸುರುಟಿ ನಂದಿಸಲ್ಪಟ್ಟರೆ, ');
INSERT INTO "kannada" VALUES(81,2,'81:2','ನಕ್ಷತ್ರಗಳು ಉದುರಿ ಬಿದ್ದರೆ,');
INSERT INTO "kannada" VALUES(81,3,'81:3','ಪರ್ವತಗಳು ಧೂಳೀಪಟವಾಗಿ ತೇಲಾಡಿದರೆ,');
INSERT INTO "kannada" VALUES(81,4,'81:4','ತುಂಬು ಗರ್ಭಿಣಿ ಒಂಟೆಗಳು (ನೋಡುವವನಿ ಲ್ಲದೆ) ನಿರ್ಲಕ್ಷ್ಯಕ್ಕೀಡಾದರೆ, ');
INSERT INTO "kannada" VALUES(81,5,'81:5','ವನ್ಯ ಮೃಗಗಳನ್ನು ಒಂದೆಡೆ ಸೇರಿಸಲಾದರೆ,');
INSERT INTO "kannada" VALUES(81,6,'81:6','ಸಮುದ್ರಗಳು ಹೊತ್ತಿ ಉರಿಯಲ್ಪಟ್ಟರೆ,');
INSERT INTO "kannada" VALUES(81,7,'81:7','ಆತ್ಮಗಳು ದೇಹಕ್ಕೆ ಬೆಸೆಯಲ್ಪಟ್ಟರೆ, ');
INSERT INTO "kannada" VALUES(81,8,'81:8','ಜೀವಂತ ಹೂಳಲ್ಪಟ್ಟ ಹೆಣ್ಣು ಹಸುಳೆಯೊಂದಿಗೆ ಕೇಳಲ್ಪಟ್ಟರೆ,');
INSERT INTO "kannada" VALUES(81,9,'81:9','ಯಾವ ತಪ್ಪಿಗಾಗಿ ನಿನ್ನನ್ನು ಕೊಲ್ಲಲಾಯಿತೆಂದು.');
INSERT INTO "kannada" VALUES(81,10,'81:10','ದಾಖಲು ಗ್ರಂಥಗಳು ತೆರೆಯಲ್ಪಟ್ಟರೆ,');
INSERT INTO "kannada" VALUES(81,11,'81:11','ಆಕಾಶವು ಕಿತ್ತು ಹಾಕಲ್ಪಟ್ಟರೆ,');
INSERT INTO "kannada" VALUES(81,12,'81:12','ನರಕವು ಭುಗಿಲೆದ್ದು ಉರಿದರೆ,');
INSERT INTO "kannada" VALUES(81,13,'81:13','ಸ್ವರ್ಗವು ನಿಕಟಗೊಳಿಸಲ್ಪಟ್ಟರೆ,');
INSERT INTO "kannada" VALUES(81,14,'81:14','ತಾನು ಏನನ್ನು ತಂದಿರುವೆನೆಂದು ಪ್ರತಿಯೊಬ್ಬನೂ ತಿಳಿಯುವನು.');
INSERT INTO "kannada" VALUES(81,15,'81:15','ಹಗಲು ಸಮಯದಲ್ಲಿ ಮರೆಯಾಗುವ ಮತ್ತು ರಾತ್ರಿಯಲ್ಲಿ ಮಿನುಗುವ ನಕ್ಷತ್ರಗಳ ಮೇಲೆ ನಾನು ಆಣೆ ಹಾಕುತ್ತೇನೆ! ');
INSERT INTO "kannada" VALUES(81,16,'81:16','ಚಲಿಸುವ ಹಾಗೂ ಅಸ್ತಮಾನ ವೇಳೆ ಮರೆಯಾಗುವ ನಕ್ಷತ್ರಗಳು.');
INSERT INTO "kannada" VALUES(81,17,'81:17','ರಾತ್ರಿಯ ಮೇಲೆ ನಾನು ಆಣೆ ಹಾಕುತ್ತೇನೆ, ಅದು ಕತ್ತಲನ್ನು ತಂದರೆ,');
INSERT INTO "kannada" VALUES(81,18,'81:18','ಪ್ರಭಾತದ ಮೇಲೆ ನಾನು ಆಣೆ ಹಾಕುತ್ತೇನೆ, ಅದು ಬೆಳಕನ್ನು ಹರಡಿದರೆ,');
INSERT INTO "kannada" VALUES(81,19,'81:19','ಖಂಡಿತವಾಗಿಯೂ ಇದು (ಖುರ್‍ಆನ್) ಸನ್ಮಾನ್ಯ ದೂತರು ಬಿತ್ತರಿಸಿದ ಮಾತಾಗಿದೆ5.');
INSERT INTO "kannada" VALUES(81,20,'81:20','ಆ ದೂತರು, ಪ್ರಚಂಡ ಶಕ್ತಿಯನ್ನು ಪಡೆದವರು, ಅರ್ಶ್‍ನ ಅಧಿಪತಿಯಾದ ಅಲ್ಲಾಹನ ಬಳಿ ಉನ್ನತ ಸ್ಥಾನೀಯರು ಆಗಿರುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(81,21,'81:21','ಬಾನ ಲೋಕದಲ್ಲಿ ಅನುಸರಿಸಲ್ಪಡುವವರು, ಪ್ರಾಮಾಣಿಕರು .');
INSERT INTO "kannada" VALUES(81,22,'81:22','ನಿಮ್ಮ ಒಡನಾಡಿ ಒಬ್ಬ ಹುಚ್ಚನಲ್ಲ.');
INSERT INTO "kannada" VALUES(81,23,'81:23','ಅವರು ಆ ದೂತರನ್ನು (ಜಿಬ್‍ರೀಲರನ್ನು) ಸುವ್ಯಕ್ತ ದಿಗಂತದಲ್ಲಿ ಕಂಡಿರುತ್ತಾರೆ .');
INSERT INTO "kannada" VALUES(81,24,'81:24','ಅವರು (ಅಲ್ಲಾಹನಿಂದ ಲಭಿಸುವ) ಪರೋಕ್ಷ ಜ್ಞಾನವನ್ನು ತಲುಪಿಸುವುದರಲ್ಲಿ ಜಿಪುಣರಲ್ಲ.');
INSERT INTO "kannada" VALUES(81,25,'81:25','ಇದು ದೇವಕೃಪೆಯಿಂದ ಬಹಿಷ್ಕøತನಾದ ಪಿಶಾಚಿಯ ನುಡಿಯಲ್ಲ.');
INSERT INTO "kannada" VALUES(81,26,'81:26','ಇದು ದೇವಕೃಪೆಯಿಂದ ಬಹಿಷ್ಕøತನಾದ ಪಿಶಾಚಿಯ ನುಡಿಯಲ್ಲ.');
INSERT INTO "kannada" VALUES(81,27,'81:27','ಇದು ಸಮಸ್ತ ಜಗತ್ತಿಗೂ ಅನ್ವಯವಾಗುವ ಉಪದೇಶವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(81,28,'81:28','ಅಂದರೆ ನಿಮ್ಮ ಪೈಕಿ ಧರ್ಮ ಬದ್ಧತೆಯನ್ನು ಇಚ್ಚಿಸುವವರಿಗೆ.');
INSERT INTO "kannada" VALUES(81,29,'81:29','ಜಗದೊಡೆಯನಾದ ಅಲ್ಲಾಹನು ಇಚ್ಚಿಸದ ಹೊರತು ನೀವು ಇಚ್ಛಿಸಲಾರಿರಿ.');
INSERT INTO "kannada" VALUES(82,1,'82:1','ಆಕಾಶವು ಒಡೆದು ಛಿದ್ರಗೊಂಡರೆ,');
INSERT INTO "kannada" VALUES(82,2,'82:2','ನಕ್ಷತ್ರಗಳು ಉದುರಿ ಬಿದ್ದರೆ,');
INSERT INTO "kannada" VALUES(82,3,'82:3','ಸಮುದ್ರಗಳು ಅಖಂಡಗೊಳಿಸಲ್ಪಟ್ಟರೆ,');
INSERT INTO "kannada" VALUES(82,4,'82:4','ಸಮಾಧಿಗಳು ಅಗೆಯಲ್ಪಟ್ಟರೆ, ');
INSERT INTO "kannada" VALUES(82,5,'82:5','ಪ್ರತಿಯೊಬ್ಬ ವ್ಯಕ್ತಿಯು ತಾನು ಮಾಡಿಟ್ಟ ಕರ್ಮಗಳನ್ನೂ, ಮಾಡದೆ ಬಿಟ್ಟು ಬಿಟ್ಟ ಕಾರ್ಯಗಳನ್ನೂ ತಿಳಿಯುವನು.');
INSERT INTO "kannada" VALUES(82,6,'82:6','ಓ ಮಾನವಾ, ನಿನ್ನ ಸನ್ಮಾನ್ಯ ಪ್ರಭುವಿನ ಮೇಲೆ ಹೇಗೆ ಧೈರ್ಯ ತಾಳಿದ್ದೀಯಾ?');
INSERT INTO "kannada" VALUES(82,7,'82:7','ನಿನ್ನನ್ನು ಅವನು ಸೃಷ್ಟಿಸಿದನು. ನಿನ್ನನ್ನು ಸಮುಚಿ ತಗೊಳಿಸಿದನು. ಹಾಗೂ ನಿನ್ನ ದೇಹವನ್ನು ಸಮರೂಪಗೊಳಿಸಿದನು.');
INSERT INTO "kannada" VALUES(82,8,'82:8','ಅವನು ನಿನಗೆ ಯಾವ ರೂಪು ಕೊಡಲು ಉದ್ದೇಶಿಸಿದನೋ ಆ ರೀತಿ ರೂಪುಗೊಳಿಸಿದನು.');
INSERT INTO "kannada" VALUES(82,9,'82:9','ಖಂಡಿತ ಭಂಡ ಧೈರ್ಯ ಬೇಡ, ಆದರೆ ನೀವು ಕರ್ಮಫಲವುಣ್ಣಲಿರುವುದನ್ನು ಸುಳ್ಳಾಗಿಸುತ್ತೀರಿ.');
INSERT INTO "kannada" VALUES(82,10,'82:10','ಖಂಡಿತ ನಿಮ್ಮ ಮೇಲೆ ನಿಗಾ ಇಡುವವರಿದ್ದಾರೆ!');
INSERT INTO "kannada" VALUES(82,11,'82:11','ಅವರು ಗೌರವಾನ್ವಿತರಾದ ಬರಹಗಾರರು.');
INSERT INTO "kannada" VALUES(82,12,'82:12','ನಿಮ್ಮ ಕೃತ್ಯಗಳನ್ನು ಅವರು ತಿಳಿಯುವರು.');
INSERT INTO "kannada" VALUES(82,13,'82:13','ಸಜ್ಜನರು ಖಂಡಿತವಾಗಿಯೂ ಸ್ವರ್ಗ ಸುಖದಲ್ಲಿರುವರು.');
INSERT INTO "kannada" VALUES(82,14,'82:14','ದುಷ್ಕರ್ಮಿಗಳು ಖಂಡಿತವಾಗಿಯೂ ಹೊತ್ತಿ ಉರಿಯುವ ನರಕದಲ್ಲಾಗಿರುವರು.');
INSERT INTO "kannada" VALUES(82,15,'82:15','ಪ್ರತಿಫಲ ನೀಡುವ ದಿನ ಅವರದರಲ್ಲಿ ಪ್ರವೇಶಿಸಲಿರುವರು.');
INSERT INTO "kannada" VALUES(82,16,'82:16','ಅದರಿಂದ ಅವರು ಹೊರ ಬರುವವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(82,17,'82:17','ಪ್ರತಿಫಲದ ದಿನವೇನೆಂದು ನಿನಗೆ ಗೊತ್ತೆ?');
INSERT INTO "kannada" VALUES(82,18,'82:18','ಆದಾಗ್ಯೂ ಗೊತ್ತೆ ನಿನಗೆ ಪ್ರತಿಫಲದ ದಿನ ವೇನೆಂದು?');
INSERT INTO "kannada" VALUES(82,19,'82:19','ಅದು ಯಾವುದೇ ದೇಹವು ಇನ್ನೊಂದು ದೇಹಕ್ಕೆ ಒಂದಿಷ್ಟೂ ಉಪಕಾರ ಮಾಡಲಾಗದ ದಿನ. ಅಂದು ಎಲ್ಲ ಕಾರ್ಯಗಳ ಒಡೆತನವು ಅಲ್ಲಾಹನಿಗೆ ಮೀಸಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(83,1,'83:1','ತೂಕ ಮತ್ತು ಅಳತೆಯಲ್ಲಿ ಕಡಿತಗೊಳಿಸುವವರಿಗೆ ಕಠಿಣವಾದ ಶಿಕ್ಷೆಯಿದೆ.');
INSERT INTO "kannada" VALUES(83,2,'83:2','ಅವರು ಜನರಿಂದ ಕೊಳ್ಳಲು ಅಳೆಯುವಾಗ ಪೂರ್ತಿಯಾಗಿ ಪಡೆಯುವರು.');
INSERT INTO "kannada" VALUES(83,3,'83:3','ಜನರಿಗೆ ಅಳತೆ ಅಥವಾ ತೂಕ ಮಾಡಿಕೊಡುವಾಗ ಕಡಿಮೆಗೊಳಿಸುವರು.');
INSERT INTO "kannada" VALUES(83,4,'83:4','ಅವರನ್ನು ಪುನರ್ಜೀವಗೊಳಿಸಲಾಗುತ್ತದೆ ಎಂಬು ದನ್ನು ಅವರು ತಿಳಿದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(83,5,'83:5','ಭಯಾನಕವಾದ ದಿನದಲ್ಲಿ');
INSERT INTO "kannada" VALUES(83,6,'83:6','ವಿಶ್ವ ಪ್ರಭು ಅಲ್ಲಾಹನ ಮುಂದೆ ವಿಚಾರಣೆಗಾಗಿ ಜನರು ನಿಲ್ಲುವ ದಿನವದು.');
INSERT INTO "kannada" VALUES(83,7,'83:7','ನಿಸ್ಸಂಶಯ! ಖಂಡಿತವಾಗಿಯೂ ದುಷ್ಕರ್ಮಿಗಳ ದಾಖಲೆ ಗ್ರಂಥವು ಸಿಜ್ಜೀನ್‍ನಲ್ಲಿದೆ .');
INSERT INTO "kannada" VALUES(83,8,'83:8','ಸಿಜ್ಜೀನ್ ಏನೆಂದು ತಮಗೆ ಗೊತ್ತಾ?');
INSERT INTO "kannada" VALUES(83,9,'83:9','ಮುದ್ರೆ ಹಾಕಲಾದ ಗ್ರಂಥವದು .');
INSERT INTO "kannada" VALUES(83,10,'83:10','ಸುಳ್ಳಾಗಿಸಿದವರಿಗೆ ಅಂದು ಸರ್ವನಾಶವಿದೆ.');
INSERT INTO "kannada" VALUES(83,11,'83:11','ಅಂದರೆ ಅವರು ಪ್ರತಿಫಲ ದಿನವನ್ನು ಸುಳ್ಳಾಗಿಸಿದವರು.');
INSERT INTO "kannada" VALUES(83,12,'83:12','ಹದ್ದು ಮೀರಿದ ಮಹಾ ದುಷ್ಕರ್ಮಿಯ ಹೊರತು ಆ ದಿನವನ್ನು ಇನ್ನಾರೂ ಸುಳ್ಳಾಗಿಸಲಾರರು.');
INSERT INTO "kannada" VALUES(83,13,'83:13','ನಮ್ಮ ಶ್ಲೋಕಗಳನ್ನು ಅವನಿಗೆ ಓದಿ ಕೇಳಿಸಿದರೆ ಅದು ಪೂರ್ವಿಕರ ಕಟ್ಟುಕಥೆಗಳೆಂದು ಅವನು ಹೇಳುವನು.');
INSERT INTO "kannada" VALUES(83,14,'83:14','ಖಂಡಿತ ಹಾಗಲ್ಲ, ನಿಜದಲ್ಲಿ ಅವರು ಮಾಡುವ ದುಷ್ಕøತ್ಯಗಳು ಅವರ ಹೃದಯಗಳಿಗೆ ತಡೆ ಕವಚ ಹಾಕಿವೆ .');
INSERT INTO "kannada" VALUES(83,15,'83:15','ಖಂಡಿತ ಹಾಗಲ್ಲ. ಅಂತ್ಯ ದಿನದಲ್ಲಿ ಅವರಿಗೆ ತಮ್ಮ ಪ್ರಭುವಿನ ದರ್ಶನವನ್ನು ತಡೆಯಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(83,16,'83:16','ನಂತರ ಅವರು ಖಂಡಿತ ನರಕಾಗ್ನಿಗೆ ಹೋಗಿ ಸೇರುವರು.');
INSERT INTO "kannada" VALUES(83,17,'83:17','ನಂತರ ಅವರಲ್ಲಿ ಹೀಗೆ ಹೇಳಲಾಗುತ್ತದೆ, ಇದು ನೀವು ಸುಳ್ಳಾಗಿಸಿದ ಕಾರ್ಯವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(83,18,'83:18','ಖಂಡಿತ ಹಾಗಲ್ಲ! ಸಜ್ಜನರ ಕರ್ಮಗ್ರಂಥವು ನಿಜವಾಗಿಯೂ ಇಲ್ಲಿಯ್ಯೀನ್‍ನಲ್ಲಿದೆ .');
INSERT INTO "kannada" VALUES(83,19,'83:19','ಇಲ್ಲಿಯ್ಯೀನ್ ಎಂದರೆ ಏನೆಂಬುದು ತಮಗೆ ಗೊತ್ತಾ?');
INSERT INTO "kannada" VALUES(83,20,'83:20','ಮುದ್ರೆ ಹಾಕಲಾದ ಗ್ರಂಥ.');
INSERT INTO "kannada" VALUES(83,21,'83:21','ನಿಕಟರಾದ ದೇವಚರರು ಅದಕ್ಕೆ ಸಾಕ್ಷಿ ನಿಲ್ಲುವರು.');
INSERT INTO "kannada" VALUES(83,22,'83:22','ಖಂಡಿತವಾಗಿಯೂ ಸಜ್ಜನರು ಸ್ವರ್ಗ ಸುಖದಲ್ಲಿರುವರು.');
INSERT INTO "kannada" VALUES(83,23,'83:23','ಸುಖಾಸನಗಳಲ್ಲಿ ಕೂತು ನೋಡುತ್ತಿರುವರು .');
INSERT INTO "kannada" VALUES(83,24,'83:24','ಅವರ ಮುಖದಲ್ಲಿ ಅನುಗ್ರಹದ ಕಾಂತಿಯನ್ನು ಕಾಣುವಿರಿ.');
INSERT INTO "kannada" VALUES(83,25,'83:25','ಮುದ್ರೆ ಹಾಕಲಾದ ಸ್ವಚ್ಛ ಮದಿರೆಯಿಂದ ಅವರಿಗೆ ಕುಡಿಸಲಾಗುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(83,26,'83:26','ಪಾನದ ಕೊನೆಯಲ್ಲಿ ಕಸ್ತೂರಿಯ ಪರಿಮಳವು ಹೊರಸೂಸುವುದು. ಸ್ಪರ್ಧಾಳುಗಳು ಇದಕ್ಕಾಗಿ ಸ್ಪರ್ಧಿಸಲಿ.');
INSERT INTO "kannada" VALUES(83,27,'83:27','ಅದರಲ್ಲಿ ‘ತಸ್‍ನೀಮ್’ ಅನ್ನು ಬೆರೆಸಲಾಗುತ್ತದೆ .');
INSERT INTO "kannada" VALUES(83,28,'83:28','ಅಂದರೆ ಚಿಲುಮೆ! ಅದರಿಂದ ಅಲ್ಲಾಹನ ಸಮೀಪಸ್ಥರು ಕುಡಿಯುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(83,29,'83:29','ಖಂಡಿತ ದುಷ್ಕರ್ಮಿಗಳು ಸತ್ಯವಿಶ್ವಾಸಿಗಳನ್ನು ಕಾಣುವಾಗ ಗೇಲಿ ಮಾಡಿ ನಗುವವರಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(83,30,'83:30','ವಿಶ್ವಾಸಿಗಳು ಅವರ ಬಳಿ ಹಾದು ಹೋಗುವಾಗ ಕಣ್ಸನ್ನೆ ಮಾಡುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(83,31,'83:31','ಅವರು ತಮ್ಮ ಕುಟುಂಬಸ್ಥರಲ್ಲಿಗೆ ಮರಳುವಾಗ ಇದೇ ಹೆಮ್ಮೆಯೊಂದಿಗೆ ಮರಳುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(83,32,'83:32','ವಿಶ್ವಾಸಿಗಳನ್ನು ಕಂಡರೆ ಇವರು ದಾರಿ ತಪ್ಪಿದವರೆಂದು ಹೇಳುತ್ತಿದ್ದರು.');
INSERT INTO "kannada" VALUES(83,33,'83:33','ವಾಸ್ತವದಲ್ಲಿ ಅವರನ್ನು ಇವರ ಮೇಲೆ ವೀಕ್ಷಕ ರಾಗಿ ನೇಮಿಸಲ್ಪಟ್ಟಿರಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(83,34,'83:34','ಇಂದು ಸತ್ಯವಿಶ್ವಾಸಿಗಳು ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ಅಣಕಿಸಿ ನಗುವರು.');
INSERT INTO "kannada" VALUES(83,35,'83:35','ಸೊಗ ಮಂಟಪಗಳಲ್ಲಿ ಸುಖಾಸೀನರಾಗಿ ಸತ್ಯನಿಷೇಧಿಗಳನ್ನು ನೋಡುವರು.');
INSERT INTO "kannada" VALUES(83,36,'83:36','“ಸತ್ಯನಿಷೇಧಿಗಳು ತಾವು ಮಾಡಿದ ತಪ್ಪಿಗೆ ಸೂಕ್ತ ಪ್ರತಿಫಲ ಪಡೆದರೇ” ಎಂದು.');
INSERT INTO "kannada" VALUES(84,1,'84:1','ಆಕಾಶವು ಸಿಡಿದು ಸೀಳಾದಾಗ.');
INSERT INTO "kannada" VALUES(84,2,'84:2','ಅದು ತನ್ನ ಪ್ರಭುವಿನ ಆಜ್ಞೆಗೆ ವಿಧೇಯವಾದಾಗ. ವಿಧೇಯತೆ ಅದರ ಭಾಧ್ಯತೆ ಕೂಡಾ.');
INSERT INTO "kannada" VALUES(84,3,'84:3','ಭೂಮಿಯನ್ನು ಹರಡಿ ಸಮತಲಗೊಳಿಸಲ್ಪಟ್ಟಾಗ .');
INSERT INTO "kannada" VALUES(84,4,'84:4','ಭೂಮಿಯು ತನ್ನೊಳಗಿರುವ ಸರ್ವವನ್ನೂ ಹೊರಗೆಸೆದು ಬರಿದಾದಾಗ. ');
INSERT INTO "kannada" VALUES(84,5,'84:5','ಅದು ತನ್ನ ಪ್ರಭುವಿನ ಆಜ್ಞೆಗೆ ವಿಧೇಯವಾದಾಗ, ವಿಧೇಯತೆ ಅದರ ಬಾಧ್ಯತೆ ಕೂಡಾ. (ಮನುಷ್ಯ ತನ್ನ ಕರ್ಮಫಲವನ್ನು ಉಣ್ಣುತ್ತಾನೆ.)');
INSERT INTO "kannada" VALUES(84,6,'84:6','ಓ ಮನುಜ! ನಿನ್ನ ಪರಿಶ್ರಮವು ನಿನ್ನ ಪ್ರಭುವಿನ ಭೇಟಿಯತ್ತ ಮುಟ್ಟಿಸುತ್ತದೆ. ಆಗ ಪರಿಶ್ರಮದ ಫಲವನ್ನು ಕಾಣುವೆ!');
INSERT INTO "kannada" VALUES(84,7,'84:7','ಆಗ ಯಾರಿಗೆ ತನ್ನ ಬಲಗೈಯಲ್ಲಿ ತನ್ನ ದಾಖಲೆ ಗ್ರಂಥವನ್ನು ನೀಡಲಾಗುವುದೋ.');
INSERT INTO "kannada" VALUES(84,8,'84:8','ಅವನನ್ನು ಲಘುವಾಗಿ ವಿಚಾರಣೆ ಮಾಡಲಾಗುವುದು .');
INSERT INTO "kannada" VALUES(84,9,'84:9','ಅವನು ತನ್ನವರ ಕಡೆಗೆ ಸಂತೋಷಭರಿತನಾಗಿ ಮರಳುವನು .');
INSERT INTO "kannada" VALUES(84,10,'84:10','ಯಾರಿಗೆ ತನ್ನ ದಾಖಲೆ ಗ್ರಂಥವನ್ನು ಬೆನ್ನ ಹಿಂದೆ ನೀಡಲಾಗುವುದೋ ');
INSERT INTO "kannada" VALUES(84,11,'84:11','ಅವನು ‘ನನ್ನ ನಾಶವೇ’ ಎಂದು ಅರಚುವನು.');
INSERT INTO "kannada" VALUES(84,12,'84:12','ಆತನು ಧಗಧಗಿಸುವ ನರಕಾಗ್ನಿಗೆ ಪ್ರವೇಶಿಸುವನು.');
INSERT INTO "kannada" VALUES(84,13,'84:13','ಅವನು ತನ್ನ ಕುಟುಂಬಸ್ಥರೊಂದಿಗೆ ವಿಘ್ನ ಸಂತೋಷದಲ್ಲಿದ್ದನು.');
INSERT INTO "kannada" VALUES(84,14,'84:14','ಅವನು ತನ್ನ ರಕ್ಷಕನ ಬಳಿಗೆ ಮರಳಲಿಕ್ಕಿಲ್ಲವೆಂದು ಭಾವಿಸಿದ್ದನು.');
INSERT INTO "kannada" VALUES(84,15,'84:15','ಆದರೆ ವಸ್ತು ಸ್ಥಿತಿ ಹಾಗಲ್ಲ. ಖಂಡಿತವಾಗಿಯೂ ಅವನ ಪ್ರಭು ಅವನನ್ನು ವೀಕ್ಷಿಸುವವನಾಗಿದ್ದನು.');
INSERT INTO "kannada" VALUES(84,16,'84:16','ನಾನು ಸಂಧ್ಯಾರಾಗದ ಮೇಲೆ ಆಣೆ ಹಾಕುತ್ತಿದ್ದೇನೆ!');
INSERT INTO "kannada" VALUES(84,17,'84:17','ನಿಶೆ ಮತ್ತು ಅದು ಒಳಗೊಂಡಿರುವುದರ ಆಣೆ!');
INSERT INTO "kannada" VALUES(84,18,'84:18','ಚಂದಿರನಾಣೆ ! ಅದು ಪೂರ್ಣದೆಸೆಗೆ ತಲುಪಿದಾಗ');
INSERT INTO "kannada" VALUES(84,19,'84:19','ನೀವು ಹಂತದಿಂದ ಹಂತಕ್ಕೆ ಖಂಡಿತ ದಾಟಲಿರುವಿರಿ .');
INSERT INTO "kannada" VALUES(84,20,'84:20','ಹೀಗಿರುವಾಗ ಆ ಪುನರುತ್ಥಾನವನ್ನು ನಂಬದಿರಲು ಅವರಿಗೆ ಏನು ಕಾರಣವಿದೆ?');
INSERT INTO "kannada" VALUES(84,21,'84:21','ಅವರಿಗೆ ಖುರ್‍ಆನನ್ನು ಓದಿ ಕೇಳಿಸಿದರೆ ಅವರು ಸಾಷ್ಟಾಂಗವೆರಗುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(84,22,'84:22','ಆದರೆ ಸತ್ಯನಿಷೇಧಿಗಳು ಸುಳ್ಳಾಗಿಸುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(84,23,'84:23','ಅವರು ಜಮೆ ಮಾಡುತ್ತಿರುವುದನ್ನು ಬಲ್ಲವನಾಗಿರುವನು ಅಲ್ಲಾಹು .');
INSERT INTO "kannada" VALUES(84,24,'84:24','ಆದ್ದರಿಂದ (ಪ್ರವಾದಿಯವರೇ) ಅವರಿಗೆ ವೇದನಾಯುಕ್ತ ಶಿಕ್ಷೆಯ ಸುವಾರ್ತೆಯನ್ನು ಅರುಹಿರಿ.');
INSERT INTO "kannada" VALUES(84,25,'84:25','ಸತ್ಯವಿಶ್ವಾಸವನ್ನು ಸ್ವೀಕರಿಸಿ ಸತ್ಕರ್ಮಗಳನ್ನು ಮಾಡಿದವರ ಹೊರತು. ಅವರಿಗೆ ಎಂದೆಂದಿಗೂ ಅಬಾಧಿತವಾದ ಪ್ರತಿಫಲವಿದೆ.');
INSERT INTO "kannada" VALUES(85,1,'85:1','ತಾರಾಪಥಗಳಿರುವ ಗಗನದಾಣೆ');
INSERT INTO "kannada" VALUES(85,2,'85:2','ಘೋಷಿತ ದಿನದಾಣೆ');
INSERT INTO "kannada" VALUES(85,3,'85:3','ಸಾಕ್ಷಿಯಾಗುವ ಮತ್ತು ಸಾಕ್ಷಿಗೊಳಗಾಗುವುದರ ಆಣೆ .');
INSERT INTO "kannada" VALUES(85,4,'85:4','ಅಗ್ನಿ ಗುಂಡಿಗಳ ಜನರು ನಾಶವಾದರು .');
INSERT INTO "kannada" VALUES(85,5,'85:5','ಇಂಧನಗಳಿಂದ ಹೊತ್ತಿ ಉರಿಯುತ್ತಿದ್ದ ಅಗ್ನಿ ಕುಂಡಗಳು!');
INSERT INTO "kannada" VALUES(85,6,'85:6','ಅವರು ಆ ಅಗ್ನಿ ಕುಂಡಗಳ ಸುತ್ತಲೂ ಕುಳಿತಿದ್ದಾಗ.');
INSERT INTO "kannada" VALUES(85,7,'85:7','ಸತ್ಯವಿಶ್ವಾಸಿಗಳ ಮೇಲಿನ ಅಕ್ರಮಕ್ಕೆ ಅವರು ಪ್ರತ್ಯ ಕ್ಷದರ್ಶಿಗಳಾದಾಗ.');
INSERT INTO "kannada" VALUES(85,8,'85:8','');
INSERT INTO "kannada" VALUES(85,9,'85:9','ಪರಮ ಪ್ರತಾಪಿಯೂ ಸರ್ವಸ್ತುತಿಗಳ ಪರಮಗಮ್ಯನೂ ಭೂಮ್ಯಾಕಾಶಗಳ ಸಾಮ್ರಾಜ್ಯದ ಅಧಿಪತಿಯೂ ಆದ ಅಲ್ಲಾಹನನ್ನು ನಂಬಿರುವರು ಎಂಬ ಏಕ ಕಾರಣದ ಹೊರತು ಅವರು ಸತ್ಯ ವಿಶ್ವಾಸಿಗಳನ್ನು ಪೀಡಿಸಿಲ್ಲ. ಅಲ್ಲಾಹು ಎಲ್ಲವನ್ನೂ ವೀಕ್ಷಿಸುವವನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(85,10,'85:10','ಸತ್ಯವಿಶ್ವಾಸಿಗಳಾದ ಸ್ತ್ರೀ ಪುರುಷರನ್ನು ಉಪದ್ರವಿಸಿದವರು ನಂತರ ಪಶ್ವಾತ್ತಾಪಪಟ್ಟು ಮರಳದಿದ್ದಲ್ಲಿ, ಅವರಿಗೆ ಜಹನ್ನಮ ಎಂಬ ನರಕದ ಶಿಕ್ಷೆಯಿದೆ. ಸುಟ್ಟು ಹಾಕುವ ಘೋರ ಶಿಕ್ಷೆಯೂ ಇದೆ.');
INSERT INTO "kannada" VALUES(85,11,'85:11','ಸತ್ಯ ವಿಶ್ವಾಸವನ್ನು ತಾಳಿ ಸತ್ಕರ್ಮವೆಸಗಿದವರಿಗೆ ತಳಭಾಗದಲ್ಲಿ ನದಿಗಳು ಹರಿಯುವ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಿವೆ. ಅದು ಪ್ರಚಂಡ ವಿಜಯವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(85,12,'85:12','ನಿಜಕ್ಕೂ ನಿನ್ನ ಪ್ರಭುವಿನ ಶಿಕ್ಷೆಯು ಅತೀ ಕಠಿಣ ವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(85,13,'85:13','ಅವನು ಪ್ರಥಮ ಬಾರಿ ಸೃಷ್ಟಿಸುವವನೂ ನಂತರ ಮರಳಿ ಸೃಷಿಸುವವನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(85,14,'85:14','ಅವನು ಪಾಪ ವಿನಾಶಕನೂ ಅತ್ಯಂತ ಪ್ರೀತಿಸುವವನೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(85,15,'85:15','‘ಅರ್ಶ್’ನ ಒಡೆಯನು, ಪರಮೋನ್ನತನು.');
INSERT INTO "kannada" VALUES(85,16,'85:16','ಅವನು ತಾನಿಚ್ಛಿಸಿದ್ದನ್ನು ಮಾಡುವವನು.');
INSERT INTO "kannada" VALUES(85,17,'85:17','ಸೇನೆಗಳ ವೃತ್ತಾಂತವು ತಮಗೆ ಬಂದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(85,18,'85:18','ಅಂದರೆ ಫಿರ್‍ಔನ್ ಹಾಗೂ ಸಮೂದ್ ಗೋತ್ರದವರ ಸೇನೆಗಳ ವೃತ್ತಾಂತ,');
INSERT INTO "kannada" VALUES(85,19,'85:19','ಆದರೆ ಸತ್ಯನಿಷೇಧಿಗಳು ತಮ್ಮನ್ನು ಸುಳ್ಳಾಗಿಸು ವುದರಲ್ಲೇ ತಲ್ಲೀನರಾಗಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(85,20,'85:20','ಅಲ್ಲಾಹನು ಅವರ ಬೆನ್ನ ಹಿಂದೆಯೇ ನಿಗಾದಲ್ಲಿರುವನು.');
INSERT INTO "kannada" VALUES(85,21,'85:21','ಅದು ಉನ್ನತವಾದ ಖುರ್‍ಆನ್ ಆಗಿದೆ.');
INSERT INTO "kannada" VALUES(85,22,'85:22','ಅದು ಸುರಕ್ಷಿತ ಫಲಕದಲ್ಲಿ ಸುಭದ್ರವಾಗಿದೆ .');
INSERT INTO "kannada" VALUES(86,1,'86:1','ಆಕಾಶದಾಣೆ ಮತ್ತು ಬಾನಲ್ಲಿ ರಾತ್ರಿ ಪ್ರತ್ಯಕ್ಷ ಗೊಳ್ಳುವ ವಸ್ತುವಿನಾಣೆ!');
INSERT INTO "kannada" VALUES(86,2,'86:2','ಆ ವಸ್ತುವೇನೆಂದು ತಮಗೆ ತಿಳಿಸಿಕೊಟ್ಟದ್ದೇನು?');
INSERT INTO "kannada" VALUES(86,3,'86:3','ಅದು ಪ್ರಖರ ಪ್ರಕಾಶಮಾನ ನಕ್ಷತ್ರ.');
INSERT INTO "kannada" VALUES(86,4,'86:4','ಪ್ರತಿಯೊಂದು ಜೀವಕ್ಕೂ ಕಾವಲುಗಾರನನ್ನು ನಿಯುಕ್ತಿಗೊಳಿಸದೆ ಬಿಡಲಾಗಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(86,5,'86:5','ತನ್ನನ್ನು ಯಾವುದರಿಂದ ಸೃಷ್ಟಿಸಲಾಗಿದೆ ಎಂಬುದನ್ನು ಮನುಷ್ಯನು ನೋಡಲಿ.');
INSERT INTO "kannada" VALUES(86,6,'86:6','ಅವನನ್ನು ಪುಟಿಯುವ ದ್ರವದಿಂದ ಸೃಷ್ಟಿಸಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(86,7,'86:7','ಅದು, ಬೆನ್ನೆಲುಬು ಹಾಗೂ ಎದೆಯ ಮೂಳೆಗಳ ನಡುವಿನಿಂದ ಹೊರಡುವುದು .');
INSERT INTO "kannada" VALUES(86,8,'86:8','ಮನುಷ್ಯನನ್ನು ಮರುಜೀವಗೊಳಿಸುವುದರಲ್ಲಿ ಅವನು ಖಂಡಿತ ಸಮರ್ಥನಾಗಿರುವನು');
INSERT INTO "kannada" VALUES(86,9,'86:9','ಎಲ್ಲ ರಹಸ್ಯಗಳು ಬಹಿರಂಗವಾಗುವ ದಿನ .');
INSERT INTO "kannada" VALUES(86,10,'86:10','ಅಲ್ಲಾಹನ ಶಿಕ್ಷೆಯಿಂದ ತಡೆಯುವ ಶಕ್ತಿಯಾಗಲಿ, ಅದರಿಂದ ಪಾರುಮಾಡುವ ಸಹಾಯಕನಾಗಲಿ ಅವನಿಗೆ ಇರಲಾರದು.');
INSERT INTO "kannada" VALUES(86,11,'86:11','ಮಳೆ ಸುರಿಸುವ ಆಕಾಶದಾಣೆ!');
INSERT INTO "kannada" VALUES(86,12,'86:12','ಸೀಳುಗಳಿರುವ ಭೂಮಿಯಾಣೆ! .');
INSERT INTO "kannada" VALUES(86,13,'86:13','ಖಂಡಿತವಾಗಿಯೂ ಖುರ್‍ಆನ್ (ಸತ್ಯಾ ಸತ್ಯಗಳನ್ನು ಬೇರ್ಪಡಿಸುವ) ನಿರ್ಣಾಯಕ ಮಾತು.');
INSERT INTO "kannada" VALUES(86,14,'86:14','ಇದು ತಮಾಷೆಯಲ್ಲ .');
INSERT INTO "kannada" VALUES(86,15,'86:15','ಖಂಡಿತವಾಗಿಯೂ ಅವರು ಹಲವು ರೀತಿಯ ಕುತಂತ್ರಗಳನ್ನು ಹೂಡುತ್ತಿದ್ದಾರೆ.');
INSERT INTO "kannada" VALUES(86,16,'86:16','ನಾನು ಪ್ರತಿತಂತ್ರ ಹೂಡುವೆನು.');
INSERT INTO "kannada" VALUES(86,17,'86:17','ಆದ್ದರಿಂದ ಸ್ವಲ್ಪ ಕಾಲ ಅವರನ್ನು ಬಿಟ್ಟು ಬಿಡಿರಿ.');
INSERT INTO "kannada" VALUES(87,1,'87:1','ನಿನ್ನ ಸರ್ವೋನ್ನತ ಪ್ರಭುವಿನ ಪರಿಶುದ್ಧತೆಯನ್ನು ಕೊಂಡಾಡು.');
INSERT INTO "kannada" VALUES(87,2,'87:2','ಅವನು ಸೃಷ್ಟಿಸಿದನು. ಸಮುಚಿತಗೊಳಿಸಿದನು .');
INSERT INTO "kannada" VALUES(87,3,'87:3','ನಿರ್ಣಯಿಸಿದನು. ತರುವಾಯ ಮಾರ್ಗದರ್ಶನ ನೀಡಿದನು .');
INSERT INTO "kannada" VALUES(87,4,'87:4','ಹುಲ್ಲು-ಸಸ್ಯಗಳನ್ನು ಹೊರಡಿಸಿದನು.');
INSERT INTO "kannada" VALUES(87,5,'87:5','ನಂತರ ಅದನ್ನು ಒಣಗಲು ಕರಿಯನ್ನಾಗಿ ಮಾಡುವನು.');
INSERT INTO "kannada" VALUES(87,6,'87:6','ತಮಗೆ ನಾವು ಖುರ್‍ಆನನ್ನು ಓದಿ ಕೊಡುತ್ತೇವೆ. ನಂತರ ತಾವು ಅದನ್ನು ಒಮ್ಮೆಯೂ ಮರೆಯಲಾರಿರಿ .');
INSERT INTO "kannada" VALUES(87,7,'87:7','ಅಲ್ಲಾಹು ಇಚ್ಚಿಸಿದ್ದನ್ನು ಹೊರತುಪಡಿಸಿ , ಖಂಡಿತವಾಗಿಯೂ ಅವನು ಗೋಚರ ಮತ್ತು ಅಗೋಚರವನ್ನೂ ಬಲ್ಲನು.');
INSERT INTO "kannada" VALUES(87,8,'87:8','ತಮಗೆ ನಾವು ಸುಲಭ ಮಾರ್ಗವನ್ನು ಒದಗಿಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(87,9,'87:9','ಉಪದೇಶವು ಫಲಪ್ರದವಾಗುವುದಾದರೆ ತಾವು ಉಪದೇಶ ನೀಡಿರಿ .');
INSERT INTO "kannada" VALUES(87,10,'87:10','ಭಯಭಕ್ತಿಯುಳ್ಳವನು ಉಪದೇಶವನ್ನು ಸ್ವೀಕರಿಸುವನು.');
INSERT INTO "kannada" VALUES(87,11,'87:11','ಹತಭಾಗ್ಯನು ಅದರಿಂದ ದೂರ ಉಳಿಯುವನು.');
INSERT INTO "kannada" VALUES(87,12,'87:12','ಅವನು ಭೀಕರವಾದ ನರಕಾಗ್ನಿಗೆ ಹೋಗಿ ಸೇರುವನು.');
INSERT INTO "kannada" VALUES(87,13,'87:13','ನಂತರ ಅವನು ಅದರಲ್ಲಿ ಸಾಯುವುದಿಲ,್ಲ ಬದುಕುವುದೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(87,14,'87:14','(ಸತ್ಯವಿಶ್ವಾಸದಿಂದ) ಪರಿಶುದ್ಧನಾದವನು ಖಂಡಿತ ಜಯಶಾಲಿಯಾದನು.');
INSERT INTO "kannada" VALUES(87,15,'87:15','ತನ್ನ ಪ್ರಭುವಿನ ನಾಮವನ್ನು ಉಚ್ಚರಿಸಿ ನಮಾಝ್ ಮಾಡಿದವನು.');
INSERT INTO "kannada" VALUES(87,16,'87:16','ಆದರೆ ನೀವು ಭೌತಿಕ ಜೀವನವನ್ನು ಆಯ್ಕೆ ಮಾಡುತ್ತೀರಿ.');
INSERT INTO "kannada" VALUES(87,17,'87:17','ಪಾರತ್ರಿಕ ಜೀವನವು ಅತ್ಯಂತ ಉತ್ತಮವೂ, ಶಾಶ್ವತವೂ ಆಗಿದೆ.');
INSERT INTO "kannada" VALUES(87,18,'87:18','ಈ ವಿಚಾರಗಳನ್ನು ಪೂರ್ವ ಗ್ರಂಥಗಳಲ್ಲಿ');
INSERT INTO "kannada" VALUES(87,19,'87:19','(ಅರ್ಥಾತ್) ಇಬ್‍ರಾಹೀಮ್ ಹಾಗೂ ಮೂಸಾರ ಗ್ರಂಥಗಳಲ್ಲಿ ಪ್ರಸ್ತಾಪಿಸಲಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(88,1,'88:1','ಮುಕ್ಕುರಿಸುವ (ದುರಂತದ) ಸುದ್ಧಿಯು ತಮಗೆ ತಲುಪಿದೆಯೇ?');
INSERT INTO "kannada" VALUES(88,2,'88:2','ಅಂದು ಕೆಲವು ಮುಖಗಳು ನಿಂದ್ಯವಾಗಿವೆ.');
INSERT INTO "kannada" VALUES(88,3,'88:3','ದುಡಿದಿರುತ್ತವೆ. ಬಸವಳಿದಿರುತ್ತವೆ .');
INSERT INTO "kannada" VALUES(88,4,'88:4','ಉರಿಯುವ ಘೋರ ಅಗ್ನಿಯಲ್ಲಿ ಬೀಳುತ್ತಿವೆ.');
INSERT INTO "kannada" VALUES(88,5,'88:5','ಕಠಿಣವಾಗಿ ಕುದಿಯುವ ಮಡುವಿನಿಂದ ಅವರಿಗೆ ಕುಡಿಸಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(88,6,'88:6','ಭೀಕರ ಮುಳ್ಳೇ ಅವರ ಆಹಾರವಾಗಿರುವುದು.');
INSERT INTO "kannada" VALUES(88,7,'88:7','ಅದು ಪೋಷಕವೂ ಅಲ್ಲ. ಹಸಿವನ್ನು ನೀಗಿಸುವುದೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(88,8,'88:8','ಅಂದು ಕೆಲವು ಮುಖಗಳು ಅನುಗ್ರಹೀತ.');
INSERT INTO "kannada" VALUES(88,9,'88:9','ತಮ್ಮ ಪುಣ್ಯಶ್ರಮದ ಫಲವಾಗಿ ತೃಪ್ತವಾಗಿವೆ.');
INSERT INTO "kannada" VALUES(88,10,'88:10','ಎತ್ತರವಾದ ಸ್ವರ್ಗೋದ್ಯಾನದಲ್ಲಿ.');
INSERT INTO "kannada" VALUES(88,11,'88:11','ಅಲ್ಲಿ ಅಸಹ್ಯ ಮಾತುಗಳನ್ನು ಕೇಳುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(88,12,'88:12','ಅಲ್ಲಿ ಹರಿಯುವ ಚಿಲುಮೆಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(88,13,'88:13','ಅಲ್ಲಿ ಎತ್ತರದ ಮಂಚಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(88,14,'88:14','ಅಳವಡಿಸಲಾದ ಪಾನಪಾತ್ರೆಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(88,15,'88:15','ಜೋಡಿಸಿಡಲಾದ ದಿಂಬುಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(88,16,'88:16','ಹಾಸಲಾದ ರತ್ನಗಂಬಳಿಗಳಿವೆ.');
INSERT INTO "kannada" VALUES(88,17,'88:17','ಒಂಟೆಯನ್ನು ಹೇಗೆ ಸೃಷ್ಟಿಸಲಾಗಿದೆಯೆಂದು ಅವರು ಚಿಂತಿಸುವುದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(88,18,'88:18','ಆಕಾಶವನ್ನು ಹೇಗೆ ಎತ್ತರಿಸಲಾಗಿದೆಯೆಂದೂ !');
INSERT INTO "kannada" VALUES(88,19,'88:19','ಪರ್ವತಗಳನ್ನು ಹೇಗೆ ನಾಟಲಾಗಿದೆಯೆಂದೂ !');
INSERT INTO "kannada" VALUES(88,20,'88:20','ಭೂಮಿಯನ್ನು ಹೇಗೆ ಹಾಸಲಾಗಿದೆಯೆಂದೂ ! ');
INSERT INTO "kannada" VALUES(88,21,'88:21','(ಪ್ರವಾದಿವರ್ಯರೇ) ನೀವು ಉಪದೇಶ ನೀಡಿರಿ, ತಾವು ಓರ್ವ ಉಪದೇಶಕರೇ ಹೌದು.');
INSERT INTO "kannada" VALUES(88,22,'88:22','ತಾವು ಅವರ ಮೇಲೆ ಕ್ರಮ ಜರಗಿಸುವವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(88,23,'88:23','ಆದರೆ ಯಾರು ವಿಮುಖನಾಗುತ್ತಾನೆ ಹಾಗೂ ನಿಷೇಧಿಸುತ್ತಾನೆ,');
INSERT INTO "kannada" VALUES(88,24,'88:24','ಅವನನ್ನು ಅತ್ಯಂತ ಕಠಿಣವಾದ ಶಿಕ್ಷೆಗೆ ಅಲ್ಲಾಹು ಗುರಿಪಡಿಸುವನು.');
INSERT INTO "kannada" VALUES(88,25,'88:25','ನನ್ನ ಬಳಿಗಾಗಿದೆ ಖಂಡಿತವಾಗಿಯೂ ಅವರ ನಿರ್ಗಮನ.');
INSERT INTO "kannada" VALUES(88,26,'88:26','ನಂತರ ಅವರ ವಿಚಾರಣೆಯೂ ನಮ್ಮ ಬಾಧ್ಯತೆಯಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(89,1,'89:1','ಪ್ರಭಾತದಾಣೆ,');
INSERT INTO "kannada" VALUES(89,2,'89:2','ಹತ್ತು ರಾತ್ರಿಗಳಾಣೆ,');
INSERT INTO "kannada" VALUES(89,3,'89:3','ಸಮ ಹಾಗೂ ಬೆಸಗಳ ಆಣೆ,');
INSERT INTO "kannada" VALUES(89,4,'89:4','ಸಂಚಲಿತ ರಾತ್ರಿಯಾಣೆ,');
INSERT INTO "kannada" VALUES(89,5,'89:5','ಇದರಲ್ಲಿ ಆಣೆಗೆ ಯೋಗ್ಯವಾದ ದೃಷ್ಟಾಂತಗಳು ಇದೆಯೆಂದು ವಿವೇಕಮತಿಗಳಿಗೆ ತಿಳಿಯುವುದಿಲ್ಲವೇ? ');
INSERT INTO "kannada" VALUES(89,6,'89:6','ಆದ್ ಸಮೂಹದೊಂದಿಗೆ ನಿಮ್ಮ ಪ್ರಭು ಹೇಗೆ ವರ್ತಿಸಿದನೆಂಬುದನ್ನು ನೀವು ತಿಳಿದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(89,7,'89:7','ಅಂದರೆ ಸ್ಥಂಭಗಳ ಒಡೆಯರಾದ ಇರಮ್ ಜನಾಂಗದೊಂದಿಗೆ,');
INSERT INTO "kannada" VALUES(89,8,'89:8','ಅಂತಹ ಜನಾಂಗವನ್ನು ಯಾವುದೇ ನಾಡುಗಳಲ್ಲಿ ಸೃಷ್ಟಿಸಲಾಗಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(89,9,'89:9','ಕಣಿವೆಯಲ್ಲಿ ಬಂಡೆ ಕಲ್ಲುಗಳನ್ನು ಕೊರೆದು ಮನೆ ನಿರ್ಮಿಸುತ್ತಿದ್ದ ಸಮೂದ್ ಜನಾಂಗದೊಂದಿಗೆ,');
INSERT INTO "kannada" VALUES(89,10,'89:10','ಮೊಳೆಗಾರನಾದ ಫಿರ್‍ಔನನೊಂದಿಗೆ,');
INSERT INTO "kannada" VALUES(89,11,'89:11','ಅವರು ನಾಡಿನಲ್ಲಿ ಅಕ್ರಮಗಳನ್ನು ಎಸಗಿದವರಾಗಿದ್ದರು.');
INSERT INTO "kannada" VALUES(89,12,'89:12','ಅವರು ನಾಡಿನಲ್ಲಿ ಹೇರಳ ಕ್ಷೋಭೆ ನಡೆಸಿದ್ದರು.');
INSERT INTO "kannada" VALUES(89,13,'89:13','ಅವರ ಮೇಲೆ ತಮ್ಮ ಪಾಲಕನು ಶಿಕ್ಷೆಯ ದಂಡವನ್ನು ಸುರಿಸಿದನು.');
INSERT INTO "kannada" VALUES(89,14,'89:14','ನಿಜವಾಗಿಯೂ ತಮ್ಮ ಪ್ರಭುವು ನಿಗಾ ಇರಿಸಿ ಕೊಂಡು ವೀಕ್ಷಿಸುವವನು .');
INSERT INTO "kannada" VALUES(89,15,'89:15','ಆದರೆ ಮನುಷ್ಯನು, ಅವನ ಪ್ರಭುವು ಅವನನ್ನು ಪರೀಕ್ಷೆಗೊಳಪಡಿಸಿ ಅವನಿಗೆ ಗೌರವ ಹಾಗೂ ಅನುಕೂಲ ನೀಡಿದರೆ “ನನ್ನ ರಕ್ಷಕನು ನನ್ನನ್ನು ಗೌರವಿಸಿದನು” ಎಂದು ಹೇಳುವನು.');
INSERT INTO "kannada" VALUES(89,16,'89:16','ಅವನನ್ನು ಪರೀಕ್ಷೆಗೊಳಪಡಿಸಿ ಅವನ ಜೀವನಾ ಧಾರವನ್ನು ಇಕ್ಕಟ್ಟುಗೊಳಿಸಿದರೆ ನನ್ನ ರಕ್ಷಕನು ನನ್ನನ್ನು ಅಪಮಾನಿಸಿದನು ಎಂದು ಹೇಳುವನು ');
INSERT INTO "kannada" VALUES(89,17,'89:17','ವಸ್ತು ಸ್ಥಿತಿ ಹಾಗಲ್ಲ. ಆದರೆ ನೀವು ಅನಾಥರನ್ನು ಪರಿಗಣಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(89,18,'89:18','ನಿರ್ಗತಿಕರಿಗೆ ಆಹಾರ ನೀಡುವುದರ ಮೇಲೆ ನೀವು ಪರಸ್ಪರ ಪ್ರೋತ್ಸಾಹಿಸುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(89,19,'89:19','ನೀವು (ಪರರ) ವಾರೀಸುಸೊತ್ತನ್ನು ಹೇರಳವಾಗಿ ತಿನ್ನುತ್ತೀರಿ.');
INSERT INTO "kannada" VALUES(89,20,'89:20','ನೀವು ಸಂಪತ್ತನ್ನು ಅತಿಯಾಗಿ ಪ್ರೀತಿಸುತ್ತೀರಿ.');
INSERT INTO "kannada" VALUES(89,21,'89:21','ಇದು ಸಲ್ಲದು, ಭೂಮಿ ನುಚ್ಚುನೂರು ಮಾಡಲ್ಪಟ್ಟಾಗ !');
INSERT INTO "kannada" VALUES(89,22,'89:22','ತಮ್ಮ ಪ್ರಭುವಿನ ಆದೇಶ ಬಂದು ಮಲಕುಗಳು ಸಾಲುಸಾಲಾಗಿ ನಿಂತಾಗ. ');
INSERT INTO "kannada" VALUES(89,23,'89:23','ಅಂದು ನರಕವನ್ನು ತರಲಾಗುವುದು, ಅಂದು ಮನುಷ್ಯನು ಚಿಂತನೆ ನಡೆಸುವನು. ಆದರೆ ಅಂದು ಚಿಂತನೆ ನಡೆಸಿ ಏನು ಫಲವಿದೆ?');
INSERT INTO "kannada" VALUES(89,24,'89:24','‘ನನ್ನ ನಾಶವೇ! ನನ್ನ ಈ ಜೀವನಕ್ಕಾಗಿ ನಾನು ಸಿದ್ಧಪಡಿಸಿಟ್ಟಿದ್ದರೆ ! ಎನ್ನುವನು.');
INSERT INTO "kannada" VALUES(89,25,'89:25','ಅಂದು ಅಲ್ಲಾಹನು ನೀಡುವಂತಹ ಶಿಕ್ಷೆಯನ್ನು ನೀಡಬಲ್ಲವನು ಯಾವೊಬ್ಬನೂ ಇರುವುದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(89,26,'89:26','ಅವನು ಬಂಧಿಸುವಂತೆ ಬೇರಾರೂ ಬಂಧಿಸುವವರಿಲ್ಲ');
INSERT INTO "kannada" VALUES(89,27,'89:27','ಓ ಪ್ರಶಾಂತ ಆತ್ಮವೇ,');
INSERT INTO "kannada" VALUES(89,28,'89:28','ನೀನು ನಿನ್ನ ರಕ್ಷಕನೆಡೆಗೆ ಸ್ವತೃಪ್ತನೂ ದೇವಕೃಪಾ ಪಾತ್ರನೂ ಆಗಿ ಮರಳು');
INSERT INTO "kannada" VALUES(89,29,'89:29','ನನ್ನ ಸಜ್ಜನ ದಾಸರಲ್ಲಿ ನೀನು ಸೇರು.');
INSERT INTO "kannada" VALUES(89,30,'89:30','ನನ್ನ ಸ್ವರ್ಗಕ್ಕೆ ಪ್ರವೇಶಿಸು .');
INSERT INTO "kannada" VALUES(90,1,'90:1','ಈ ನಾಡಿನ ಮೇಲೆ ನಾನು ಆಣೆ ಹಾಕುತ್ತಿದ್ದೇನೆ .');
INSERT INTO "kannada" VALUES(90,2,'90:2','ತಾವು ಈ ಊರಲ್ಲಿ ಮುಕ್ತರಾಗಿದ್ದೀರಿ .');
INSERT INTO "kannada" VALUES(90,3,'90:3','ಜನಕ ಹಾಗೂ ಜಾತರ ಆಣೆ,');
INSERT INTO "kannada" VALUES(90,4,'90:4','ಮನುಷ್ಯರನ್ನು ನಾವು ಕಷ್ಟ ಅನುಭವಿಸುವ ರೀತಿಯಲ್ಲಿ ಸೃಷ್ಟಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(90,5,'90:5','ಅವನನ್ನು ಪರಾಭವಗೊಳಿಸಲು ಯಾರಿಂದಲೂ ಸಾಧ್ಯವಿಲ್ಲವೆಂದು ಅವನು ಭಾವಿಸುತ್ತಾನೆಯೇ?');
INSERT INTO "kannada" VALUES(90,6,'90:6','ನಾನು ಅಡಿಗಡಿಗೆ ಧಾರಾಳ ಖರ್ಚು ಮಾಡಿದ್ದೇನೆ ಎನ್ನುತ್ತಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(90,7,'90:7','ಅವನನ್ನು ಯಾರೂ ಕಾಣುತ್ತಿಲ್ಲವೆಂದು ಅವನು ಭಾವಿಸುತ್ತಾನೆಯೇ?');
INSERT INTO "kannada" VALUES(90,8,'90:8','ಅವನಿಗೆ ನಾವು ಎರಡು ಕಣ್ಣುಗಳನ್ನು ನೀಡಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(90,9,'90:9','ಅವನಿಗೆ ನಾವು ಒಂದು ನಾಲಗೆಯನ್ನೂ ಎರಡು ತುಟಿಗಳನ್ನೂ ನೀಡಿಲ್ಲವೇ? ');
INSERT INTO "kannada" VALUES(90,10,'90:10','ಅವನಿಗೆ ನಾವು ಎರಡು ಮಾರ್ಗಗಳನ್ನು ತೋರಿಸಿ ಕೊಡಲಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(90,11,'90:11','ಅವನಿಗೆ ಬೆಟ್ಟದ ಕಡಿದಾದ ದಾರಿಯಲ್ಲಿ ಹಾದು ಹೋಗಬಾರದಿತ್ತೆ?');
INSERT INTO "kannada" VALUES(90,12,'90:12','ಬೆಟ್ಟದ ಕಡಿದಾದ ದಾರಿ ಯಾವುದೆಂದು ನಿಮಗೆ ಗೊತ್ತೆ ?');
INSERT INTO "kannada" VALUES(90,13,'90:13','ದಾಸ್ಯ ಮುಕ್ತಿ.');
INSERT INTO "kannada" VALUES(90,14,'90:14','ಅಥವಾ ಹಸಿದ ದಿನದಲ್ಲಿ ಉಣಬಡಿಸುವುದು.');
INSERT INTO "kannada" VALUES(90,15,'90:15','ಸಂಬಂಧಿಕರಾದ ಅನಾಥ ಮಕ್ಕಳಿಗೆ.');
INSERT INTO "kannada" VALUES(90,16,'90:16','ಅಥವಾ ಕೈಬರಿದಾದ ಕಡು ಬಡವನಿಗೆ.');
INSERT INTO "kannada" VALUES(90,17,'90:17','ಅಲ್ಲದೆ, ಅವನು ಸತ್ಯ ವಿಶ್ವಾಸವಿರಿಸಿದ ಹಾಗೂ ಸಹನೆಗೆ ಪರಸ್ಪರ ಉಪದೇಶ ಮತ್ತು ಕರುಣೆಗೆ ಪರಸ್ಪರ ಉಪದೇಶ ನೀಡುವವರ ಸಾಲಿಗೆ ಸೇರಿದವನಾಗುವುದು.');
INSERT INTO "kannada" VALUES(90,18,'90:18','ಅವರೇ ಬಲಗಡೆಯವರು .');
INSERT INTO "kannada" VALUES(90,19,'90:19','ನಮ್ಮ ದೃಷ್ಟಾಂತಗಳನ್ನು ನಿಷೇಧಿಸಿದವರು ಯಾರೋ ಅವರೇ ಎಡಗಡೆಯವರು .');
INSERT INTO "kannada" VALUES(90,20,'90:20','ಅವರ ಮೇಲೆ ಆವರಿಸಿಕೊಂಡ ನರಕಾಗ್ನಿಯಿದೆ.');
INSERT INTO "kannada" VALUES(91,1,'91:1','ಸೂರ್ಯ ಮತ್ತು ಅದರ (ಪೂರ್ವಾಹ್ನ) ಪ್ರಕಾಶದಾಣೆ !');
INSERT INTO "kannada" VALUES(91,2,'91:2','ಚಂದ್ರನಾಣೆ; ಅದು ಸೂರ್ಯನನ್ನು ಹಿಂಬಾಲಿಸಿ ದಾಗ,');
INSERT INTO "kannada" VALUES(91,3,'91:3','ಹಗಲಿನಾಣೆ, ಅದು ಸೂರ್ಯನನ್ನು ಪ್ರದರ್ಶಿಸಿದಾಗ,');
INSERT INTO "kannada" VALUES(91,4,'91:4','ರಾತ್ರಿಯಾಣೆ, ಅದು ಸೂರ್ಯನನ್ನು ಆವರಿಸಿದಾಗ,');
INSERT INTO "kannada" VALUES(91,5,'91:5','ಆಕಾಶದಾಣೆ ಹಾಗೂ ಅದನ್ನು ನಿರ್ಮಿಸಿದವನಾಣೆ !');
INSERT INTO "kannada" VALUES(91,6,'91:6','ಭೂಮಿಯಾಣೆ ಹಾಗೂ ಅದನ್ನು ಹಾಸಿದವನಾಣೆ !');
INSERT INTO "kannada" VALUES(91,7,'91:7','ಆತ್ಮದಾಣೆ ಹಾಗೂ ಅದನ್ನು (ಜೋಡಿಸಿ) ಸಮುಚಿತಗೊಳಿಸಿದವನಾಣೆ !');
INSERT INTO "kannada" VALUES(91,8,'91:8','ಬಳಿಕ ಅದರ ದುಷ್ಟತೆಯನ್ನೂ ಅದರ ಶಿಷ್ಟತೆ ಯನ್ನೂ ಅಲ್ಲಾಹು ಅದಕ್ಕೆ ತಿಳಿಯಪಡಿಸಿದನು .');
INSERT INTO "kannada" VALUES(91,9,'91:9','ಆತ್ಮವನ್ನು ಪರಿಶುದ್ಧಗೊಳಿಸಿಕೊಂಡವನು ಖಂಡಿತ ಜಯ ಹೊಂದಿರುವನು.');
INSERT INTO "kannada" VALUES(91,10,'91:10','ಅದನ್ನು ಕುಲಗೆಡಿಸಿಕೊಂಡವನು ಖಂಡಿತ ಸೋಲುಂಡನು .');
INSERT INTO "kannada" VALUES(91,11,'91:11','ತಮ್ಮ ದುಷ್ಕøತ್ಯಗಳ ಮೂಲಕ ಸಮೂದ್ ಜನಾಂಗವು ಸತ್ಯವನ್ನು ಸುಳ್ಳಾಗಿಸಿತು.');
INSERT INTO "kannada" VALUES(91,12,'91:12','ಅವರಲ್ಲಿ ಅತ್ಯಂತ ನತದೃಷ್ಟನು ಸೆಟೆದೆದ್ದು ನಿಂತಾಗ.');
INSERT INTO "kannada" VALUES(91,13,'91:13','ಅಲ್ಲಾಹನ ಪ್ರವಾದಿಯು ಅವರೊಂದಿಗೆ ಹೇಳಿದರು, ಅಲ್ಲಾಹನ ಒಂಟೆಯನ್ನು ಮತ್ತು ಅದರ ಜಲಪಾನವನ್ನು ಅದರ ಪಾಡಿಗೆ ಬಿಟ್ಟು ಬಿಡಿ.');
INSERT INTO "kannada" VALUES(91,14,'91:14','ಆದರೆ ಅವರು ಅಲ್ಲಾಹನ ಪ್ರವಾದಿಯನ್ನು ಸುಳ್ಳಾಗಿಸಿದರು ಹಾಗೂ ಒಂಟೆಯನ್ನು ಕೊಯ್ದು ಕೊಂದರು . ಅವರ ಈ ಪಾಪದ ಫಲವಾಗಿ ಅವರ ಪ್ರಭುವು ಅವರ ಮೇಲೆ ನಾಶವನ್ನು ತಟ್ಟಿದನು. ಆಮೇಲೆ (ಯಾವನೂ ಬಾಕಿಯಾಗದಂತೆ) ಸಮಗೊಳಿಸಿದನು.');
INSERT INTO "kannada" VALUES(91,15,'91:15','ದುರಂತದ ಮುಂದಿನ ಫಲಿತಾಂಶದ ಬಗ್ಗೆ ಅಲ್ಲಾಹನಿಗೆ ಭಯವಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(92,1,'92:1','ರಾತ್ರಿಯಾಣೆ, ಅದು ಇರುಳಾವರಿಸಿದಾಗ,');
INSERT INTO "kannada" VALUES(92,2,'92:2','ಹಗಲಿನಾಣೆ, ಅದು ಬೆಳಗಿದಾಗ,');
INSERT INTO "kannada" VALUES(92,3,'92:3','ಗಂಡು-ಹೆಣ್ಣುಗಳನ್ನು ಸೃಷ್ಟಿಸಿದವನಾಣೆ!');
INSERT INTO "kannada" VALUES(92,4,'92:4','ಖಂಡಿತ ನಿಮ್ಮ ಪರಿಶ್ರಮವು ವಿಭಿನ್ನವಾಗಿರುವುದು .');
INSERT INTO "kannada" VALUES(92,5,'92:5','ಆದ್ದರಿಂದ ಯಾರು ದಾನ ನೀಡುತ್ತಾನೆ ಹಾಗೂ ಅಲ್ಲಾಹನನ್ನು ಭಯಪಡುತ್ತಾನೆ,');
INSERT INTO "kannada" VALUES(92,6,'92:6','ಹಾಗೂ ಅತ್ಯುತ್ತಮವಾದುದನ್ನು ಸತ್ಯವೆಂದು ಅಂಗೀಕರಿಸುತ್ತಾನೆ,');
INSERT INTO "kannada" VALUES(92,7,'92:7','ಅವನಿಗೆ ನಾವು (ಸ್ವರ್ಗಕ್ಕಿರುವ) ಸರಳ ಪಥವನ್ನು ಸುಗಮಗೊಳಿಸುವೆವು .');
INSERT INTO "kannada" VALUES(92,8,'92:8','ಯಾರು ಜಿಪುಣತೆ ತೋರುತ್ತಾನೆ ಮತ್ತು ಅಲ್ಲಾಹನಿಂದ ನಿರಪೇಕ್ಷನಾಗುತ್ತಾನೆ,');
INSERT INTO "kannada" VALUES(92,9,'92:9','ಹಾಗೂ ಅತ್ಯುತ್ತಮವಾದುದನ್ನು ಸುಳ್ಳಾಗಿಸುತ್ತಾನೆ,');
INSERT INTO "kannada" VALUES(92,10,'92:10','ಅವನಿಗೆ ನಾವು ಕಷ್ಟಕರವಾದ (ನರಕದ) ಹಾದಿಯನ್ನು ಸುಗಮಗೊಳಿಸುವೆವು.');
INSERT INTO "kannada" VALUES(92,11,'92:11','ಅವನು ನರಕದಲ್ಲಿ ಬೀಳುವಾಗ ಅವನ ಸಂಪತ್ತು ಅವನನ್ನು ಪಾರುಮಾಡಲಾರದು.');
INSERT INTO "kannada" VALUES(92,12,'92:12','ದಾರಿತೋರಿಸುವ ಹೊಣೆ ನಮ್ಮ ಮೇಲಿದೆ.');
INSERT INTO "kannada" VALUES(92,13,'92:13','ಖಂಡಿತ ಪರಲೋಕ ಮತ್ತು ಇಹಲೋಕವು ನಮ್ಮ ಒಡೆತನಕ್ಕೆ ಸೇರಿದೆ.');
INSERT INTO "kannada" VALUES(92,14,'92:14','ಧಗಧಗಿಸುವ ನರಕಾಗ್ನಿಯ ಕುರಿತು ನಿಮಗೆ ನಾನು ಮುನ್ನೆಚ್ಚರಿಕೆ ನೀಡಿರುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(92,15,'92:15','');
INSERT INTO "kannada" VALUES(92,16,'92:16','ಸುಳ್ಳಾಗಿಸಿದ ಹಾಗೂ ವಿಮುಖನಾದ ಹತ ಭಾಗ್ಯನ ಹೊರತು ಇನ್ನಾರೂ ಅದರಲ್ಲಿ ಪ್ರವೇಶಿಸಲಾರರು.');
INSERT INTO "kannada" VALUES(92,17,'92:17','ಸದ್ಭಕ್ತನು ಅದರಿಂದ ದೂರವಾಗಲಿರುವನು.');
INSERT INTO "kannada" VALUES(92,18,'92:18','ಅವನು ಅಲ್ಲಾಹನ ಬಳಿ ಪುನೀತನೆನಿಸಿಕೊಳ್ಳಲು ತನ್ನ ಸಂಪತ್ತನ್ನು ದಾನ ನೀಡುವನು.');
INSERT INTO "kannada" VALUES(92,19,'92:19','ಅವನ ಮೇಲೆ ಪ್ರತ್ಯುಪಕಾರ ಮಾಡಬೇಕಾದಂತಹ ಯಾವುದೇ ಉಪಕಾರ ಭಾರ ಯಾರಿಗೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(92,20,'92:20','ಅವನ ಸರ್ವೋನ್ನತ ಪಾಲಕ ಪ್ರಭುವಾದ ಅಲ್ಲಾಹನ ಪ್ರಸನ್ನತೆಯ ಉದ್ದೇಶದ ಹೊರತು.');
INSERT INTO "kannada" VALUES(92,21,'92:21','ಅವನು ಖಂಡಿತವಾಗಿಯೂ ಸಂತೃಪ್ತ ಗೊಳ್ಳಲಿರುವನು.');
INSERT INTO "kannada" VALUES(93,1,'93:1','ಬಿಸಿಲೇರುವ ಹೊತ್ತಿನಾಣೆ!');
INSERT INTO "kannada" VALUES(93,2,'93:2','ರಾತ್ರಿಯಾಣೆ. ಅದು ಕತ್ತಲಾವರಿಸಿದರೆ,');
INSERT INTO "kannada" VALUES(93,3,'93:3','ತಮ್ಮ ಪಾಲಕನಾದ ಅಲ್ಲಾಹು ತಮ್ಮನ್ನು ಕೈಬಿಡಲಿಲ್ಲ ಹಾಗೂ ತಮ್ಮ ಮೇಲೆ ಕೋಪಿಸಲಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(93,4,'93:4','ಐಹಿಕ ಲೋಕಕ್ಕಿಂತ ಪರಲೋಕವು ತಮಗೆ ಉತ್ತಮವಾಗಿದೆ.');
INSERT INTO "kannada" VALUES(93,5,'93:5','ತಮ್ಮ ಪರಿಪಾಲಕನಾದ ಅಲ್ಲಾಹು ಮುಂದೆ ತಮಗೆ ನೀಡುವನು, ಆಗ ತಾವು ಸಂತೃಪ್ತರಾಗುವಿರಿ.');
INSERT INTO "kannada" VALUES(93,6,'93:6','ಅವನು ತಮ್ಮನ್ನು ಅನಾಥರನ್ನಾಗಿ ಕಂಡಾಗ ತಮ್ಮನ್ನು ಸಂರಕ್ಷಿಸಲಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(93,7,'93:7','ತಮ್ಮನ್ನು ದಾರಿ ತಿಳಿಯದವರಾಗಿ ಕಂಡಾಗ ಸರಿಯಾದ ದಾರಿಯನ್ನು ತೋರಲಿಲ್ಲವೇ? ');
INSERT INTO "kannada" VALUES(93,8,'93:8','ತಮ್ಮನ್ನು ಬಡವನಾಗಿ ಕಂಡಾಗ ಸ್ಥಿತಿವಂತನನ್ನಾಗಿ ಮಾಡಲಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(93,9,'93:9','ಆದುದರಿಂದ ತಾವು ಅನಾಥರನ್ನು ದಮನಿಸ ಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(93,10,'93:10','ಭಿಕ್ಷುಕನನ್ನು ಜರೆಯಬೇಡಿರಿ.');
INSERT INTO "kannada" VALUES(93,11,'93:11','ತಮ್ಮ ಪರಿಪಾಲಕನಾದ ಅಲ್ಲಾಹು ತಮಗೆ ನೀಡಿದ ಅನುಗ್ರಹವನ್ನು ಎತ್ತಿ ಹೇಳಿರಿ .');
INSERT INTO "kannada" VALUES(94,1,'94:1','ನಾವು ತಮಗಾಗಿ ತಮ್ಮ ಹೃದಯವನ್ನು ವಿಸ್ತಾರ ಗೊಳಿಸಲಿಲ್ಲವೇ? ');
INSERT INTO "kannada" VALUES(94,2,'94:2','ತಮ್ಮ ಕರ್ತವ್ಯ ಭಾರವನ್ನು ನಾವು ಇಳಿಸಲಿಲ್ಲವೇ? ');
INSERT INTO "kannada" VALUES(94,3,'94:3','ಆ ಕರ್ತವ್ಯ ಭಾರವು ತಮ್ಮ ಬೆನ್ನು ಮುರಿಯುವಂತಿತ್ತು .');
INSERT INTO "kannada" VALUES(94,4,'94:4','ತಮ್ಮ ಮಹಿಮೆಯನ್ನು ತಮಗೆ ನಾವು ಎತ್ತರಕ್ಕೇರಿಸಲಿಲ್ಲವೇ? ');
INSERT INTO "kannada" VALUES(94,5,'94:5','ನಿಜವಾಗಿಯೂ ಕಷ್ಟದ ಜೊತೆ ಸುಲಭವಿದೆ.');
INSERT INTO "kannada" VALUES(94,6,'94:6','ಕಷ್ಟದ ಜೊತೆ ಖಂಡಿತ ಸುಲಭವಿದೆ .');
INSERT INTO "kannada" VALUES(94,7,'94:7','ತಾವು ವಿರಮಿಸಿದರೆ ಆರಾಧನೆಯಲ್ಲಿ ತೊಡಗಿರಿ ,');
INSERT INTO "kannada" VALUES(94,8,'94:8','ತಮ್ಮ ಪ್ರಭುವಿನ ಕಡೆಗೆ ಮೊರೆ ಹೋಗಿರಿ.');
INSERT INTO "kannada" VALUES(95,1,'95:1','ಅಂಜೂರ ಮತ್ತು ಆಲಿವ್ ಹಣ್ಣಿನಾಣೆ .');
INSERT INTO "kannada" VALUES(95,2,'95:2','ಶುಭ ಸುಂದರ ‘ತ್ವೂರ್’ ಪರ್ವತದಾಣೆ.');
INSERT INTO "kannada" VALUES(95,3,'95:3','ನಿರ್ಭೀತಿಯ ತಾಣವಾಗಿರುವ ಈ ಊರಿನಾಣೆ.');
INSERT INTO "kannada" VALUES(95,4,'95:4','ಮನುಷ್ಯನನ್ನು ನಾವು ಅತ್ಯುತ್ತಮ ರೂಪದಲ್ಲಿ ಸೃಷ್ಟಿಸಿದ್ದೇವೆ.');
INSERT INTO "kannada" VALUES(95,5,'95:5','ನಂತರ ಅವನನ್ನು ಅತ್ಯಂತ ದುರ್ಬಲಾವಸ್ಥೆಗೆ ಮರಳಿಸುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(95,6,'95:6','ಆದರೆ ಸತ್ಯ ವಿಶ್ವಾಸವನ್ನು ಅಂಗೀಕರಿಸಿ ಸತ್ಕರ್ಮ ವೆಸಗುವವರಿಗೆ ಒಮ್ಮೆಯೂ ನಿಂತು ಹೋಗದ ಪ್ರತಿಫಲವಿದೆ.');
INSERT INTO "kannada" VALUES(95,7,'95:7','ಇದರ ಬಳಿಕವೂ ಪ್ರತಿಫಲ ದಿನವನ್ನು ನೀನೇಕೆ ಸುಳ್ಳಾಗಿಸುವೆ?');
INSERT INTO "kannada" VALUES(95,8,'95:8','ಅಲ್ಲಾಹು ಎಲ್ಲಾ ನ್ಯಾಯಾಧೀಶರಿಗಿಂತಲೂ ಶ್ರೇಷ್ಟ ನ್ಯಾಯಾಧೀಶನಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(96,1,'96:1','ಸೃಷ್ಟಿಸಿದ ನಿಮ್ಮ ಪ್ರಭುವಿನ ನಾಮದೊಂದಿಗೆ ಓದಿರಿ.');
INSERT INTO "kannada" VALUES(96,2,'96:2','ಮನುಷ್ಯನನ್ನು ಅವನು ರಕ್ತ ಪಿಂಡದಿಂದ ಸೃಷ್ಟಿಸಿರುವನು.');
INSERT INTO "kannada" VALUES(96,3,'96:3','ಓದಿರಿ, ತಮ್ಮ ಪಾಲಕ ಪ್ರಭು ಅತ್ಯಂತ ಉದಾರಿಯಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(96,4,'96:4','ಲೇಖನಿಯ ಮೂಲಕ ಕಲಿಸಿಕೊಟ್ಟವನು.');
INSERT INTO "kannada" VALUES(96,5,'96:5','ಮನುಷ್ಯನಿಗೆ ತಿಳಿದಿರದ ಕಾರ್ಯಗಳನ್ನು ಅವನು ಕಲಿಸಿಕೊಟ್ಟಿರುವನು .');
INSERT INTO "kannada" VALUES(96,6,'96:6','ಖಂಡಿತ ಮನುಷ್ಯನು ದ್ರೋಹವೆಸಗುತ್ತಾನೆ .');
INSERT INTO "kannada" VALUES(96,7,'96:7','ಅವನು ತನ್ನನ್ನು ಆರ್ಥಿಕ ಸ್ವಾವಲಂಬಿ ಎಂದು ತಿಳಿದುಕೊಂಡಿರುವುದರಿಂದ !');
INSERT INTO "kannada" VALUES(96,8,'96:8','ನಿನ್ನ ಪ್ರಭುವಿನ ಬಳಿಗಾಗಿದೆ ನಿನ್ನ ಮರಳುವಿಕೆ.');
INSERT INTO "kannada" VALUES(96,9,'96:9','ತಡೆಯುವ ಒಬ್ಬ ವ್ಯಕ್ತಿಯನ್ನು ಕಂಡೆಯಾ?');
INSERT INTO "kannada" VALUES(96,10,'96:10','ಒಬ್ಬ ದಾಸನನ್ನು, ಅವನು ನಮಾಝ್ ಮಾಡುವಾಗ! ');
INSERT INTO "kannada" VALUES(96,11,'96:11','ಕಂಡೆಯಾ! ಆ ದಾಸನು ಸನ್ಮಾರ್ಗದಲ್ಲಾಗಿದ್ದರೆ,');
INSERT INTO "kannada" VALUES(96,12,'96:12','ಅಥವಾ ಅವನು ದೇವಭಕ್ತಿಯ ಆದೇಶ ನೀಡುತ್ತಿದ್ದರೆ, (ತಡೆದವನ ಗತಿಯೇನು?)');
INSERT INTO "kannada" VALUES(96,13,'96:13','ಕಂಡೆಯಾ? ಆತ (ಆ ದಾಸನನ್ನು) ಸುಳ್ಳಾಗಿಸಿದರೆ ಮತ್ತು ಸತ್ಯವಿಶ್ವಾಸದಿಂದ ವಿಮುಖನಾದರೆ,');
INSERT INTO "kannada" VALUES(96,14,'96:14','ಅಲ್ಲಾಹು ಅವನನ್ನು ಕಾಣುತ್ತಿದ್ದಾನೆ ಎಂಬುದನ್ನು ಅವನು ಅರಿತಿಲ್ಲವೆ?');
INSERT INTO "kannada" VALUES(96,15,'96:15','ಖಂಡಿತ ಬೇಡ, ಆತ (ಅಬೂಜಹಲ್) ತನ್ನ ಈ ದುಷ್ಕøತ್ಯವನ್ನು ಕೊನೆಗೊಳಿಸದಿದ್ದರೆ ನಾವು ಆತನ ಜುಟ್ಟನ್ನು ಹಿಡಿದು ನರಕಕ್ಕೆ ತಳ್ಳುತ್ತೇವೆ.');
INSERT INTO "kannada" VALUES(96,16,'96:16','ಆ ಜುಟ್ಟುಗಾರನು ಸುಳ್ಳುಗಾರನೂ ಅಪರಾಧಿಯೂ ಆಗಿರುವನು.');
INSERT INTO "kannada" VALUES(96,17,'96:17','ಅವನು ತನ್ನ ಬಳಗದವರನ್ನು ಕರೆದುಕೊಳ್ಳಲಿ.');
INSERT INTO "kannada" VALUES(96,18,'96:18','(ಅದಕ್ಕೆ ಪ್ರತಿಯಾಗಿ) ನಾವು ಕಠಿಣ ದಂಡಕ ದೇವಚರರನ್ನು ಕರೆದುಕೊಳ್ಳುವೆವು .');
INSERT INTO "kannada" VALUES(96,19,'96:19','ಬೇಡ! ತಾವು ಆ ಜುಟ್ಟುಗಾರನನ್ನು ಅನುಸರಿಸ ಬೇಡಿರಿ. ಬದಲು (ಅಲ್ಲಾಹನಿಗೆ) ಸಾಷ್ಟಾಂಗವೆರಗಿರಿ ಹಾಗೂ ಸಾಮಿಪ್ಯವನ್ನು ಪಡೆಯಿರಿ.');
INSERT INTO "kannada" VALUES(97,1,'97:1','ಇದನ್ನು (ಖುರ್‍ಆನನ್ನು) ನಾವು ಖದ್‍ರಿನ ರಾತ್ರಿಯಲ್ಲಿ ಅವತೀರ್ಣಗೊಳಿಸಿದ್ದೇವೆ .');
INSERT INTO "kannada" VALUES(97,2,'97:2','ಖದ್‍ರಿನ ರಾತ್ರಿ ಏನೆಂದು ಗೊತ್ತೆ?');
INSERT INTO "kannada" VALUES(97,3,'97:3','ಖದ್‍ರಿನ ರಾತ್ರಿಯು ಸಾವಿರ ಮಾಸಕ್ಕಿಂತಲೂ ಶ್ರೇಷ್ಟವಾಗಿದೆ .');
INSERT INTO "kannada" VALUES(97,4,'97:4','ಅಂದು ಅಲ್ಲಾಹನ ಸಮ್ಮತಿ ಮೇರೆಗೆ ಎಲ್ಲ ಕಾರ್ಯಗಳಿಗಾಗಿ ದೇವಚರರು ಹಾಗೂ ‘ರೂಹ್’ ಇಳಿದು ಬರುವರು .');
INSERT INTO "kannada" VALUES(97,5,'97:5','ಆ ರಾತ್ರಿಯು ಪ್ರಭಾತದವರೆಗೆ ಸುರಕ್ಷೆಯಾಗಿದೆ. ');
INSERT INTO "kannada" VALUES(98,1,'98:1','ಗ್ರಂಥದವರು ಹಾಗೂ ಬಹುದೇವ ವಿಶ್ವಾಸಿಗಳಾದ ಸತ್ಯನಿಷೇಧಿಗಳು ಅವರಿಗೆ ಸುವ್ಯಕ್ತ ಪುರಾವೆ ಬರುವವರೆಗೆ ಆದರ್ಶವನ್ನು ಬಿಡಿಸಿಕೊಂಡವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(98,2,'98:2','(ಸುವ್ಯಕ್ತ ಪುರಾವೆ ಏನೆಂದರೆ) ಅಲ್ಲಾಹನ ಕಡೆಯಿಂದ ಪರಿಶುದ್ಧವಾದ ಹೊತ್ತಗೆಗಳನ್ನು ಓದಿ ಕೊಡುವ ಓರ್ವ ದೂತರು .');
INSERT INTO "kannada" VALUES(98,3,'98:3','ಅದರಲ್ಲಿ ನೇರ, ಸರಳ ಲಿಖಿತ ನಿಯಮಗಳಿವೆ . (ಆಗ ಕೆಲವರು ವಿಶ್ವಾಸ ತಾಳಿದರು. ಕೆಲವರು ನಿಷೇಧಿಸಿದರು).');
INSERT INTO "kannada" VALUES(98,4,'98:4','(ಆದರೆ) ವೇದಗ್ರಂಥ ನೀಡಲ್ಪಟ್ಟವರು ಭಿನ್ನಾಭಿಪ್ರಾಯ ತಾಳಿದ್ದು ಅವರಿಗೆ ಸುವ್ಯಕ್ತ ಪುರಾವೆ ಬಂದ ಬಳಿಕ ಮಾತ್ರವೇ ಆಗಿತ್ತು .');
INSERT INTO "kannada" VALUES(98,5,'98:5','ತಮ್ಮ ಧರ್ಮವನ್ನು ಅಲ್ಲಾಹನಿಗೆ ಮಾತ್ರ ನಿಷ್ಕಳಂಕವಾಗಿ ಮೀಸಲಿಟ್ಟು ಎಲ್ಲ ಮಿಥ್ಯ ಧರ್ಮಗಳಿಂದ ದೂರವಾಗಿ ಅಲ್ಲಾಹನನ್ನು ಮಾತ್ರ ಆರಾಧಿಸಲೂ, ನಮಾಝ್ ಸಂಸ್ಥಾಪಿಸಲೂ, ಕಡ್ಡಾಯ ದಾನ ನೀಡಲೂ ಮಾತ್ರವೇ ಅವರಲ್ಲಿ (ಅವರ ವೇದಗಳಲ್ಲಿ) ಆದೇಶಿಸಲಾಗಿತ್ತು. ಅದುವೇ ಸರಿಯಾದ ಧರ್ಮ.');
INSERT INTO "kannada" VALUES(98,6,'98:6','ಗ್ರಂಥದವರೂ, ಬಹುದೇವಾರಾಧಕರೂ ಆದ ಸತ್ಯ ನಿಷೇಧಿಗಳು ಖಂಡಿತವಾಗಿಯೂ ಜಹನ್ನಮ್ ಎಂಬ ನರಕದ ಬೆಂಕಿಯಲ್ಲಿ ಸದಾಕಾಲ ಬಿದ್ದು ಕೊಂಡಿರುವರು, ಅವರೇ ಸೃಷ್ಟಿಗಳ ಪೈಕಿ ಅತ್ಯಂತ ನಿಕೃಷ್ಟರು!');
INSERT INTO "kannada" VALUES(98,7,'98:7','Àತ್ಯವಿಶ್ವಾಸವನ್ನು ತಾಳಿ ಸತ್ಕರ್ಮ ಮಾಡಿದವರು, ಖಂಡಿತ ಅವರೇ ಸೃಷ್ಟಿಗಳ ಪೈಕಿ ಅತ್ಯುತ್ತಮರು.');
INSERT INTO "kannada" VALUES(98,8,'98:8','ಅವರ ಪಾಲಕನ ಬಳಿ ಅವರಿಗಿರುವ ಪ್ರತಿಫಲವು ತಳಭಾಗದಲ್ಲಿ ನದಿಗಳು ಹರಿಯುವವಾಸ ಯೋಗ್ಯವಾದ ಸ್ವರ್ಗೋದ್ಯಾನಗಳಾಗಿವೆ. ಅದರಲ್ಲಿ ಅವರು ಶಾಶ್ವತವಾಗಿ ನೆಲೆಸುವರು. ಅಲ್ಲಾಹು ಅವರ ಬಗ್ಗೆ ಪ್ರಸನ್ನನಾದನು, ಅವರೂ ಅಲ್ಲಾಹನ ಕುರಿತು ಪ್ರಸನ್ನರಾದರು. ಈ ಪ್ರತಿಫಲವು ತನ್ನ ಪ್ರಭುವನ್ನು ಭಯಪಟ್ಟವನಿಗೆ.');
INSERT INTO "kannada" VALUES(99,1,'99:1','ಭೂಮಿಯು ತನ್ನ ಉಗ್ರ ಕಂಪನದೊಂದಿಗೆ ಕಂಪಿಸಲ್ಪಟ್ಟರೆ,');
INSERT INTO "kannada" VALUES(99,2,'99:2','(ಆಗ) ಭೂಮಿ ತನ್ನ ಒಡಲಲ್ಲಿರುವ ಹೊರೆಗಳನ್ನು ಹೊರ ಹಾಕಿದರೆ,');
INSERT INTO "kannada" VALUES(99,3,'99:3','(ಆಗ) ಮನುಷ್ಯನು ಹೇಳುವನು ‘ಭೂಮಿಗೆ ಏನು ಸಂಭವಿಸಿದೆ?');
INSERT INTO "kannada" VALUES(99,4,'99:4','ಅಂದು ಭೂಮಿಯು ತನ್ನ ವೃತ್ತಾಂತವನ್ನು ವಿವರಿಸುವುದು.');
INSERT INTO "kannada" VALUES(99,5,'99:5','ಏಕೆಂದರೆ ತಮ್ಮ ಪಾಲಕನಾದ ಅಲ್ಲಾಹು ಅದಕ್ಕೆ ಹಾಗೆ ಆದೇಶ ಕೊಟ್ಟಿರುವನು.');
INSERT INTO "kannada" VALUES(99,6,'99:6','ಅಂದು ಅವರವರ ಕರ್ಮಗಳ ಪ್ರತಿಫಲವನ್ನು ಕಾಣಲಿಕ್ಕಾಗಿ ಜನರು ಬೇರೆ ಬೇರೆಯಾಗಿ ತೆರಳುವರು.');
INSERT INTO "kannada" VALUES(99,7,'99:7','ಯಾರು ಅಣುಗಾತ್ರದಷ್ಟು ಒಳಿತನ್ನು ಮಾಡುತ್ತಾನೋ, ಅವನು ಅದರ ಫಲವನ್ನು ಕಾಣುವನು.');
INSERT INTO "kannada" VALUES(99,8,'99:8','ಯಾರು ಅಣುಗಾತ್ರದಷ್ಟು ಕೆಡುಕನ್ನು ಮಾಡುತ್ತಾನೋ, ಅವನು ಅದರ ಫಲವನ್ನು ಕಾಣುವನು.');
INSERT INTO "kannada" VALUES(100,1,'100:1','ಹೇಂಕರಿಸುತ್ತ ರಭಸದಿಂದ ಓಡುವ ಕುದುರೆಗಳಾಣೆ!');
INSERT INTO "kannada" VALUES(100,2,'100:2','ಗೊರಸುಗಳಿಂದ ಕಿಡಿ ಹಾರಿಸುವ ಕುದುರೆಗಳಾಣೆ!');
INSERT INTO "kannada" VALUES(100,3,'100:3','ಪ್ರಭಾತದ ವೇಳೆ ಶತ್ರುಗಳ ಮೇಲೆ ಮುಗಿ ಬೀಳುವ ಕುದುರೆಗಳಾಣೆ ! ');
INSERT INTO "kannada" VALUES(100,4,'100:4','ಆಗ ಅವು ಧೂಳೆಬ್ಬಿಸುತ್ತವೆ.');
INSERT INTO "kannada" VALUES(100,5,'100:5','ಆ ಧೂಳಿನೊಂದಿಗೆ ಶತ್ರು ಸೇನೆಯ ಮಧ್ಯಕ್ಕೆ ಲಗ್ಗೆಯಿಡುತ್ತವೆ.');
INSERT INTO "kannada" VALUES(100,6,'100:6','ಖಂಡಿತವಾಗಿಯೂ ಮನುಷ್ಯನು ತನ್ನ ಪರಿಪಾ ಲಕನಾದ ಅಲ್ಲಾಹನಿಗೆ ಬಹಳ ಕೃತಘ್ನನಾಗಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(100,7,'100:7','ಅವನ ಕೃತಘ್ನತೆಗೆ ಅವನೇ ಸಾಕ್ಷಿಯಾಗಿದ್ದಾನೆ .');
INSERT INTO "kannada" VALUES(100,8,'100:8','ಮನುಷ್ಯನು ಸಂಪತ್ತುಗಳ ಮೇಲೆ ತೀವ್ರ ಮೋಹವುಳ್ಳವನು.');
INSERT INTO "kannada" VALUES(100,9,'100:9','ಅವನು ತಿಳಿದಿಲ್ಲವೇ? ಸಮಾಧಿಗಳಿಂದ (ಮೃತರನ್ನು) ಹೊರಡಿಸಲಾದರೆ?');
INSERT INTO "kannada" VALUES(100,10,'100:10','ಹೃದಯಗಳಲ್ಲಿರುವುದನ್ನು ಬಹಿರಂಗಗೊಳಿಸಲಾದರೆ, ');
INSERT INTO "kannada" VALUES(100,11,'100:11','ಆ ದಿನ ಅವರ ಪರಿಪಾಲಕನಾದ ಅಲ್ಲಾಹನು ಅವರನ್ನು ಸೂಕ್ಷ್ಮವಾಗಿ ತಿಳಿಯುವವನಾಗಿದ್ದಾನೆ .');
INSERT INTO "kannada" VALUES(101,1,'101:1','ಭಯಂಕರ ಸದ್ದಿನ ಆ ಮಹಾ ದುರಂತ!');
INSERT INTO "kannada" VALUES(101,2,'101:2','ಅದೆಂತಹ ಭೀಕರ ಘಟನೆ!');
INSERT INTO "kannada" VALUES(101,3,'101:3','ಆ ಭೀಕರ ಘಟನೆ ಏನೆಂದು ತಮಗೆ ಗೊತ್ತೆ?');
INSERT INTO "kannada" VALUES(101,4,'101:4','ಮನುಷ್ಯರೆಲ್ಲರೂ ಚದುರಿದ ಹಾತೆಗಳಂತೆ ಚೆಲ್ಲಾಪಿಲ್ಲಿಯಾಗುವ ದಿನ.');
INSERT INTO "kannada" VALUES(101,5,'101:5','ಅಂದು ಪರ್ವತಗಳು ಹಿಂಜಿದ ಅರಳೆಯಂತಾಗುವುವು !');
INSERT INTO "kannada" VALUES(101,6,'101:6','(ಆಗ) ಯಾರ ಸತ್ಕರ್ಮಗಳ ತಟ್ಟೆಯು ಭಾರವಾಗಿರುವುದೋ,');
INSERT INTO "kannada" VALUES(101,7,'101:7','ಅವನು ತೃಪ್ತಿಕರವಾದ ಜೀವನದಲ್ಲಾಗುವನು.');
INSERT INTO "kannada" VALUES(101,8,'101:8','ಆಗ ಯಾರ ಸತ್ಕರ್ಮಗಳ ತಟ್ಟೆಯು ಹಗುರ ವಾಗಿರುವುದೋ,');
INSERT INTO "kannada" VALUES(101,9,'101:9','ಅವನ ವಾಸಸ್ಥಳ ಪ್ರಪಾತವಾಗಿರುವುದು.');
INSERT INTO "kannada" VALUES(101,10,'101:10','ಆ ಪ್ರಪಾತ ಏನೆಂದು ನಿಮಗೆ ಗೊತ್ತೆ ?');
INSERT INTO "kannada" VALUES(101,11,'101:11','ಘೋರತಾಪದ ನರಕವದು.');
INSERT INTO "kannada" VALUES(102,1,'102:1','ಹೆಚ್ಚುಗಾರಿಕೆಯ ಪೈಪೋಟಿಯು ನಿಮ್ಮನ್ನು (ದೇವ ಶ್ರದ್ಧೆಯಿಂದ) ಮೊಟಕುಗೊಳಿಸಿದೆ.');
INSERT INTO "kannada" VALUES(102,2,'102:2','ನೀವು ಸಮಾಧಿಗಳನ್ನು ಸಂದರ್ಶಿಸುವವರೆಗೆ .');
INSERT INTO "kannada" VALUES(102,3,'102:3','ಜೋಕೆ! (ಮರಣದ ವೇಳೆ) ನೀವು ತಿಳಿಯಲಿದ್ದೀರಿ!');
INSERT INTO "kannada" VALUES(102,4,'102:4','ಎಚ್ಚರವಿರಲಿ! ನಂತರ (ಸಮಾಧಿಯೊಳಗೆ) ನೀವು ತಿಳಿಯಲಿದ್ದೀರಿ .');
INSERT INTO "kannada" VALUES(102,5,'102:5','ಖಂಡಿತ ! (ಪೈಪೋಟಿಯ ಪರ್ಯವಸಾನವನ್ನು) ನೀವು ಖಚಿತವಾಗಿ ತಿಳಿದಿದ್ದರೆ...! (ನೀವು ಪೈಪೋಟಿ ನಡೆಸುತ್ತಿರಲಿಲ್ಲ.)');
INSERT INTO "kannada" VALUES(102,6,'102:6','ನೀವು ನರಕವನ್ನು ಕಂಡೇ ತೀರುವಿರಿ.');
INSERT INTO "kannada" VALUES(102,7,'102:7','ಪುನಃ ನೀವದನ್ನು ಖಚಿತ ದೃಷ್ಟಿಯಲ್ಲಿ ಕಾಣಲಿದ್ದೀರಿ.');
INSERT INTO "kannada" VALUES(102,8,'102:8','ಆಮೇಲೆ ನಿಮಗೆ ದೊರೆತಿದ್ದ ಅನುಗ್ರಹಗಳ ಕುರಿತು ಅಂದು ನಿಮ್ಮಲ್ಲಿ ವಿಚಾರಿಸಲಾಗುವುದು.');
INSERT INTO "kannada" VALUES(103,1,'103:1','ಕಾಲದ ಆಣೆ ! ');
INSERT INTO "kannada" VALUES(103,2,'103:2','ಮನುಷ್ಯನು ಘೋರ ನಷ್ಟದಲ್ಲಿದ್ದಾನೆ.');
INSERT INTO "kannada" VALUES(103,3,'103:3','ಸತ್ಯವಿಶ್ವಾಸವಿರಿಸಿದ, ಸತ್ಕರ್ಮವೆಸಗಿದ ಮತ್ತು ಪರಸ್ಪರ ಸತ್ಯಕ್ಕೆ ಉಪದೇಶವನ್ನೂ ಪರಸ್ಪರ ಸಹನೆಯ ಹಿತೋಪದೇಶವನ್ನೂ ಮಾಡಿಕೊಂಡವರ ಹೊರತು.');
INSERT INTO "kannada" VALUES(104,1,'104:1','ಮಾನ ಹರಿಯುವ ಹಾಗೂ ಚುಚ್ಚು ನುಡಿಯುವ ಸರ್ವರಿಗೂ ಕಾದಿದೆ ವಿನಾಶ .');
INSERT INTO "kannada" VALUES(104,2,'104:2','ಅವನು ಸಂಪತ್ತನ್ನು ಸಂಗ್ರಹಿಸಿದನು ಮತ್ತು ಅದನ್ನು ಕೂಡಿಟ್ಟನು.');
INSERT INTO "kannada" VALUES(104,3,'104:3','ಅವನು ತನ್ನ ಸಂಪತ್ತು ತನ್ನನ್ನಿಲ್ಲಿ ಶಾಶ್ವತಗೊಳಿಸುತ್ತದೆ ಎಂದು ಭಾವಿಸುತ್ತಾನೆ.');
INSERT INTO "kannada" VALUES(104,4,'104:4','ಹಾಗೆ ಭಾವಿಸ ಬೇಡ, ಖಂಡಿತ ಅವನನ್ನು ಹುತ್ವಮಃಕ್ಕೆ ಎಸೆಯಲ್ಪಡುವುದು.');
INSERT INTO "kannada" VALUES(104,5,'104:5','ಹುತ್ವಮಃ ಎಂದರೆ ಏನೆಂದು ನಿಮಗೆ ಗೊತ್ತಾ?');
INSERT INTO "kannada" VALUES(104,6,'104:6','ಅಲ್ಲಾಹನ ಹೊತ್ತಿಜ್ವಲಿಸುವ ಅಗ್ನಿಯದು!');
INSERT INTO "kannada" VALUES(104,7,'104:7','ಅದರ ಅಗ್ನಿಯು ಹೃದಯದಾಳಕ್ಕೆ ತಿವಿಯುತ್ತದೆ.');
INSERT INTO "kannada" VALUES(104,8,'104:8','ಖಂಡಿತವಾಗಿಯೂ ಅದು ಅವರ ಮೇಲೆ ಮುಗಿ ಬಿದ್ದಿರುವುದು.');
INSERT INTO "kannada" VALUES(104,9,'104:9','ಎತ್ತರವಾದ ಸ್ಥಂಭಗಳಲ್ಲಿ .');
INSERT INTO "kannada" VALUES(105,1,'105:1','ಆನೆಯವರೊಂದಿಗೆ ತಮ್ಮ ಪ್ರಭು ಹೇಗೆ ವರ್ತಿಸಿ ದನೆಂದು ತಮಗೆ ತಿಳಿದಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(105,2,'105:2','ಅವರ ದುರಾಲೋಚನೆಯನ್ನು ನಾಶಕ್ಕೀಡು ಮಾಡಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(105,3,'105:3','ಅವರ ಮೇಲೆ ಗುಂಪು ಗುಂಪಾದ ಪಕ್ಷಿಗಳನ್ನು ರವಾನಿಸಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(105,4,'105:4','ಅವು ಮಣ್ಣಿನ ಸುಡುಗಲ್ಲನ್ನು ಅವರಿಗೆ ಎಸೆಯುತ್ತಿದ್ದವು.');
INSERT INTO "kannada" VALUES(105,5,'105:5','ಹಾಗೆ ಅವರನ್ನು ತಿಂದುಳಿದ ಬೈಹುಲ್ಲಿನಂತೆ ಮಾಡಿದನು .');
INSERT INTO "kannada" VALUES(106,1,'106:1','ಕುರೈಶರಿಗೆ ಸಂಯೋಜನೆ ನೀಡಿದ್ದಕ್ಕಾಗಿ,');
INSERT INTO "kannada" VALUES(106,2,'106:2','ಅಂದರೆ ಚಳಿಗಾಲ ಹಾಗೂ ಬೇಸಿಗೆ ಕಾಲದ ಯಾತ್ರೆಯಲ್ಲಿ ಸಂಯೋಜನೆ ನೀಡಿದ್ದಕ್ಕಾಗಿ .');
INSERT INTO "kannada" VALUES(106,3,'106:3','ಆದ್ದರಿಂದ ಈ ಭವನದ ಒಡೆಯನಾದ ಅಲ್ಲಾಹ ನನ್ನು ಅವರು ಆರಾಧಿಸಲಿ .');
INSERT INTO "kannada" VALUES(106,4,'106:4','ಅವರಿಗೆ ಅವನು ಹಸಿವಿಗೆ ಅನ್ನ ನೀಡಿದನು ಹಾಗೂ ಭಯ ನಿವಾರಿಸಿ ಶಾಂತಿ ಕೊಟ್ಟನು .');
INSERT INTO "kannada" VALUES(107,1,'107:1','ಪ್ರತಿಫಲ ದಿನವನ್ನು ಸುಳ್ಳಾಗಿಸುವವನನ್ನು ತಾವು ಕಂಡಿಲ್ಲವೇ?');
INSERT INTO "kannada" VALUES(107,2,'107:2','ಅವನು ಯಾರೆಂದರೆ, ತಬ್ಬಲಿಯನ್ನು ಹೊರ ದಬ್ಬುವವನು.');
INSERT INTO "kannada" VALUES(107,3,'107:3','ಅವನು ಬಡವರಿಗೆ ಆಹಾರ ನೀಡುವುದಕ್ಕೆ ಪ್ರೋತ್ಸಾಹ ನೀಡುವುದಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(107,4,'107:4','ನಮಾಜು ಮಾಡುವವರಿಗೆ ವಿನಾಶ ಕಾದಿದೆ.');
INSERT INTO "kannada" VALUES(107,5,'107:5','ಅವರು ಯಾರೆಂದರೆ ತಮ್ಮ ನಮಾಝಿನ ಕುರಿತು ಅನಾಸ್ಥೆ ತಾಳುವವರು .');
INSERT INTO "kannada" VALUES(107,6,'107:6','ಅವರು ತೋರಿಕೆಗಾಗಿ ನಮಾಝ್ ಮಾಡುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(107,7,'107:7','ಮತ್ತು ಪುಟ್ಟ ಉಪಯೋಗ ವಸ್ತುಗಳನ್ನು ತಡೆಯುತ್ತಾರೆ.');
INSERT INTO "kannada" VALUES(108,1,'108:1','ತಮಗೆ ನಾವು ಖಂಡಿತ ‘ಅಲ್ ಕೌಸರ್’ ಅನ್ನು ನೀಡಿರುತ್ತೇವೆ .');
INSERT INTO "kannada" VALUES(108,2,'108:2','ಆದ್ದರಿಂದ ತಾವು ತಮ್ಮ ಪಾಲಕನಾದ ಅಲ್ಲಾಹನಿಗೆ ನಮಾಝ್ ಮಾಡಿರಿ ಮತ್ತು ಬಲಿದಾನ ನೀಡಿರಿ.');
INSERT INTO "kannada" VALUES(108,3,'108:3','ತಮ್ಮೊಂದಿಗೆ ದ್ವೇಷ ಸಾಧಿಸುವವನೇ ನಿಜದಲ್ಲಿ ಸಂತಾನ ಕಡಿಯಲ್ಪಟ್ಟವನು .');
INSERT INTO "kannada" VALUES(109,1,'109:1','(ಪ್ರವಾದಿಯವರೇ) ಹೇಳಿರಿ, ಓ ಸತ್ಯನಿಷೇಧಿಗಳೇ, ');
INSERT INTO "kannada" VALUES(109,2,'109:2','ನೀವು ಆರಾಧಿಸುತ್ತಿರುವುದನ್ನು ನಾನು ಆರಾಧಿಸುತ್ತಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(109,3,'109:3','ನಾನು ಆರಾಧಿಸುತ್ತಿರುವುದನ್ನು ನೀವೂ ಆರಾಧಿಸುತ್ತಿಲ್ಲ .');
INSERT INTO "kannada" VALUES(109,4,'109:4','ನೀವು ಆರಾಧಿಸುತ್ತಿರುವುದನ್ನು ನಾನು ಆರಾಧಿಸಲಾರೆ .');
INSERT INTO "kannada" VALUES(109,5,'109:5','ನಾನು ಆರಾಧಿಸುತ್ತಿರುವುದನ್ನು ನೀವು (ಮುಂದೆಯೂ) ಆರಾಧಿಸುವವರಲ್ಲ.');
INSERT INTO "kannada" VALUES(109,6,'109:6','ನಿಮ್ಮ ಧರ್ಮ ನಿಮಗೆ. ನನ್ನ ಧರ್ಮ ನನಗೆ .');
INSERT INTO "kannada" VALUES(110,1,'110:1','ಅಲ್ಲಾಹನ ಸಹಾಯ ಮತ್ತು ವಿಜಯವು ಬಂದರೆ,');
INSERT INTO "kannada" VALUES(110,2,'110:2','ಜನರು ಅಲ್ಲಾಹನ ಧರ್ಮಕ್ಕೆ ತಂಡೋಪತಂಡವಾಗಿ ಪ್ರವೇಶಿಸುವುದನ್ನು ತಾವು ಕಂಡರೆ,');
INSERT INTO "kannada" VALUES(110,3,'110:3','ತಮ್ಮ ಪಾಲಕ ಪ್ರಭು ಅಲ್ಲಾಹನ ಸ್ತುತಿಕೀರ್ತನೆಯೊಂದಿಗೆ ಅವನ ಪರಿಶುದ್ಧತೆಯನ್ನು ಕೊಂಡಾಡಿರಿ ಹಾಗೂ ಅವನೊಂದಿಗೆ ಕ್ಷಮೆ ಯಾಚಿಸಿರಿ . ಖಂಡಿತವಾಗಿಯೂ ಅವನು ಪಶ್ಚಾತ್ತಾಪವನ್ನು ಯಥೇಷ್ಠ ಸ್ವೀಕರಿಸುವವನಾಗಿರುವನು.');
INSERT INTO "kannada" VALUES(111,1,'111:1','ಅಬೂಲಹಬನ ಎರಡು ಕೈಗಳು ನಾಶವಾಗಲಿ ಹಾಗೂ ಅವನು ನಾಶವಾದನು.');
INSERT INTO "kannada" VALUES(111,2,'111:2','ಅವನ ಧನವೂ, ಅವನ ಸಂಪಾದನೆಯೂ ಅವನನ್ನು ಪಾರು ಮಾಡಲಾರದು.');
INSERT INTO "kannada" VALUES(111,3,'111:3','ಅಗ್ನಿ ಜ್ವಾಲೆಗಳಿಂದ ಹೊತ್ತಿ ಉರಿಯುವ ನರಕಕ್ಕೆ ಅವನು ಪ್ರವೇಶಿಸುವನು.');
INSERT INTO "kannada" VALUES(111,4,'111:4','(ಅವನೊಂದಿಗೆ) ಕಟ್ಟಿಗೆ ಹೊರುವ ಅವನ ಪತ್ನಿಯೂ ಕೂಡ .');
INSERT INTO "kannada" VALUES(111,5,'111:5','ಅವಳ ಕೊರಳಲ್ಲಿ ಹೊಸೆದ ಹಗ್ಗವಿರುವ ಸ್ಥಿತಿಯಲ್ಲಿ.');
INSERT INTO "kannada" VALUES(112,1,'112:1','(ಪ್ರವಾದಿಯವರೇ) ಹೇಳಿರಿ, ಅವನು ಅಲ್ಲಾಹು . ಏಕಮಾತ್ರನು .');
INSERT INTO "kannada" VALUES(112,2,'112:2','ಅಲ್ಲಾಹು ನಿರಪೇಕ್ಷನು, ಎಲ್ಲರೂ ಆತನ ಅವಲಂಬಿತರು.');
INSERT INTO "kannada" VALUES(112,3,'112:3','ಅವನು ಜನ್ಮ ಕೊಟ್ಟಿಲ್ಲ ಹಾಗೂ ಜನ್ಮ ಪಡೆದಿಲ್ಲ.');
INSERT INTO "kannada" VALUES(112,4,'112:4','ಅವನಿಗೆ ಸರಿಸಮಾನರು ಯಾರೂ ಇಲ್ಲ.');
INSERT INTO "kannada" VALUES(113,1,'113:1','(ಪ್ರವಾದಿಯವರೇ) ಹೇಳಿರಿ, ಪ್ರಭಾತದ ಪಾಲಕ ಪ್ರಭುವಿನೊಂದಿಗೆ ನಾನು ಅಭಯ ಯಾಚಿಸುತ್ತೇನೆ .');
INSERT INTO "kannada" VALUES(113,2,'113:2','ಅವನು ಸೃಷ್ಟಿಸಿದ ವಸ್ತುಗಳ ಕೇಡಿನಿಂದ.');
INSERT INTO "kannada" VALUES(113,3,'113:3','ರಾತ್ರಿಯ ಕೇಡಿನಿಂದ, ದಟ್ಟ ಕತ್ತಲು ಕವಿದಾಗ .');
INSERT INTO "kannada" VALUES(113,4,'113:4','ಗಂಟುಗಳಲ್ಲಿ ಊದುವವರ ಕೇಡಿನಿಂದ .');
INSERT INTO "kannada" VALUES(113,5,'113:5','ಅಸೂಯೆಗಾರನ ಕೇಡಿನಿಂದಲೂ - ಅವನು ಅಸೂಯೆಪಟ್ಟಾಗ.');
INSERT INTO "kannada" VALUES(114,1,'114:1','(ಪ್ರವಾದಿವರ್ಯರೇ) ಹೇಳಿರಿ, ಮನುಷ್ಯರ ಪಾಲಕ ಪ್ರಭುವಿನೊಂದಿಗೆ ನಾನು ಅಭಯ ಯಾಚಿಸುತ್ತೇನೆ.');
INSERT INTO "kannada" VALUES(114,2,'114:2','ಮನುಷ್ಯರ ಅಧಿಪತಿಯಾದ (ಅಲ್ಲಾಹನೊಂದಿಗೆ)');
INSERT INTO "kannada" VALUES(114,3,'114:3','ಮನುಷ್ಯರ ಆರಾಧ್ಯನಾದ (ಅಲ್ಲಾಹನೊಂದಿಗೆ.)');
INSERT INTO "kannada" VALUES(114,4,'114:4','ಹೊಂಚುಗಾರನಾದ ಶೈತಾನನ ಕೇಡಿನಿಂದ. ');
INSERT INTO "kannada" VALUES(114,5,'114:5','ಮನುಷ್ಯರ ಹೃದಯಗಳಲ್ಲಿ ಚಂಚಲ ಮೂಡಿಸುವ');
INSERT INTO "kannada" VALUES(114,6,'114:6','ಜಿನ್ನ್ ಮತ್ತು ಮನುಷ್ಯರ ಪೈಕಿ ');
