INSERT INTO "king_fahad_thai" VALUES(1,1,'1:1','ด้วยพระนามของอัลลอฮฺ ผู้ทรงกรุณาปราณี ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(1,2,'1:2','การสรรเสริญทั้งหลายนั้น เป็นสิทธิของอัลลอฮฺผู้เป็นพระเจ้าแห่งสากลโลก');
INSERT INTO "king_fahad_thai" VALUES(1,3,'1:3','ผู้ทรงกรุณาปราณี ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(1,4,'1:4','ผู้ทรงอภิสิทธิ์แห่งวันตอบแทน');
INSERT INTO "king_fahad_thai" VALUES(1,5,'1:5','เฉพาะพระองค์เท่านั้นที่พวกข้าพระองค์เคารพอิบาดะฮฺ และเฉพาะพระองค์เท่านั้นที่พวกข้าพระองค์ขอความช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(1,6,'1:6','ขอพระองค์ทรงแนะนําพวกข้าพระองค์ซึ่งทางอันเที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(1,7,'1:7','(คือ) ทางของบรรดาผู้ที่พระองค์ได้ทรงโปรดปราณแก่พวกเขา มิใช่ในทางของพวกที่ถูกกริ้ว และมิใช่ทางของพวกที่หลงผิด');
INSERT INTO "king_fahad_thai" VALUES(2,1,'2:1','อะลิฟ ลาม มีม');
INSERT INTO "king_fahad_thai" VALUES(2,2,'2:2','คัมภีร์นี้ ไม่มีความสงสัยใด ๆ ในนั้น เป็นคําแนะนําสําหรับบรรดาผู้ยําเกรงเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(2,3,'2:3','คือบรรดาผู้ศรัทธาต่อสิ่งเร้นลับ และดํารงไว้ซึ่งการละหมาด และส่วนหนึ่งจากสิ่งที่เราได้ให้เป็นปัจจัยยังชีพแก่พวกเขานั้น พวกเขาก็บริจาค');
INSERT INTO "king_fahad_thai" VALUES(2,4,'2:4','และบรรดาผู้ที่ศรัทธาต่อสิ่งที่ถูกประทานลงมาแก่เจ้า และสิ่งที่ถูกประทานลงมาก่อนเจ้า และต่อวันปรโลกนั้นพวกเขาเชื่อมั่น');
INSERT INTO "king_fahad_thai" VALUES(2,5,'2:5','ชนเหล่านี้ คือ ผู้ที่(ตั้ง)อยู่บนคําแนะนํา ที่มาจากพระผู้เป็นเจ้าของพวกเขา และชนเหล่านี้คือผู้ที่บรรลุผล');
INSERT INTO "king_fahad_thai" VALUES(2,6,'2:6','แท้จริงบรรดาผู้ที่ปฏิเสธการศรัทธานั้นย่อมมีผลเท่ากันแก่พวกเขา เจ้าจะตักเตือนพวกเขาแล้วหรือยังมิได้ตักเตือนพวกเขาก็หาได้ศรัทธาไม่');
INSERT INTO "king_fahad_thai" VALUES(2,7,'2:7','อัลลอฮฺได้ทรงประทับตราบนหัวใจของพวกเขา และบนหูของพวกเขาแล้ว และบนตาของพวกเขาก็มีสิ่งบดบังอยู่ และเขาเหล่านั้น จะได้รับการลงโทษอันมหันต์');
INSERT INTO "king_fahad_thai" VALUES(2,8,'2:8','และจากหมู่ชนนั้น มีผู้กล่าว่า เราได้ศรัทธาต่ออัลลอฮฺ และวันปรโลกแล้ว ทั้ง ๆ ที่พวกเขาหาใช่เป็นผู้ศรัทธาไม่');
INSERT INTO "king_fahad_thai" VALUES(2,9,'2:9','เขาเหล่านั้นต่างหลอกลวงอัลลอฮฺ และบรรดาผู้ที่ศรัทธา และพวกเขาหาได้หลอกลวงใครไม่ นอกจากตัวของพวกเขาเองเท่านั้น แต่พวกเขาไม่รู้สึก');
INSERT INTO "king_fahad_thai" VALUES(2,10,'2:10','ในหัวใจของพวกเขามีโรคอย่างหนึ่ง แล้วอัลลอฮฺได้ทรงเพิ่มโรคอีกอย่างหนึ่ง ให้แก่พวกเขา และพวกเขาจะได้รับการนลงโทษอันเจ็บแสบเนื่องจากการที่พวกเขากล่าวเท็จ');
INSERT INTO "king_fahad_thai" VALUES(2,11,'2:11','และเมื่อได้ถูกกล่าวแก่พวกเขาว่า พวกท่านจงอย่าก่อความเสียหาแก่แผ่นดิน ซิ พวกเขาก็กล่าวว่า ที่จริงนั้น เราเป็นผู้ปรับปรุงให้ดีต่างหาก');
INSERT INTO "king_fahad_thai" VALUES(2,12,'2:12','พึงรู้เถอะว่าแท้จริงพวกเขานั่นแหละ เป็นผู้ที่ก่อความเสียหาย แต่ทว่าพวกเขาไม่รู้สึก');
INSERT INTO "king_fahad_thai" VALUES(2,13,'2:13','และเมื่อได้ถูกล่าวแก่พวกเขาว่า พวกท่านจงศรัทธาเยี่ยงที่ประชาชน เขาศรัทธากันซิ พวกเขาก็กล่าวว่า จะให้เราศรัทธาเยี่ยงผู้โฉดเขลาเหล่านั้นศรัทธากัน กระนั่นหรือ? พึงรู้เถิดว่าพวกเขาเองนั่นแหละเป็นผู้ที่โฉดเขลาแต่พวกเขาหารู้ไม่');
INSERT INTO "king_fahad_thai" VALUES(2,14,'2:14','และเมื่อพวกเขาพบบรรดาผู้ศรัทธาพวกเขาก็กล่าวว่า เราศรัทธาแล้ว และเมื่อพวกเขาได้ร่วมอยู่กับบรรดาหัวใจพวกเขาแต่ลําพัง พวกเขาก็กล่าวว่า แท้จริงเรายังอยู่กับพวกท่าน ที่จริงเราเป็นแต่เพียงผู้เย้ยหยันเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(2,15,'2:15','อัลลอฮฺจะทรงเย้ยหยันพวกเขา และจะทรงยืดเวลาให้พวกเขาระเหเร่ร่อนอยู่ในการละเมิดของพวกเขาต่อไป');
INSERT INTO "king_fahad_thai" VALUES(2,16,'2:16','ชนเหล่านี้คือผู้ที่ซื้อทางหลงผิด ด้วยทางที่ถูก ดังนั้น การค้าของพวกเขาจึงไม่ได้กําไร และทั้งพวกเขาก็ไม่เคยเป็นผู้รับเอาทางที่ถูกต้อง');
INSERT INTO "king_fahad_thai" VALUES(2,17,'2:17','อุปมาพวกเขานั้น ดังผู้ที่จุดไฟขึ้น ครั้งเมื่อไฟได้ให้แสงสว่างแก่สิ่งที่อยู่รอบ ๆ ตัวเขา อัลลอฮฺก็ทรงนําเอาแสงสว่างของพวกเขาไป และปล่อยพวกเขาไว้ในบรรดาความมืด ซึ่งพวกเขาไม่สามารถจะมองเห็นได้');
INSERT INTO "king_fahad_thai" VALUES(2,18,'2:18','เขาเหล่านั้นเป็นคนหูหนวก เป็นใบ้ และตาบอด ดังนั้นพวกเขาจึงไม่สามารถจะกลับมาได้');
INSERT INTO "king_fahad_thai" VALUES(2,19,'2:19','หรือดังฝนที่หลั่งลงมาจากฟากฟ้า โดยที่ในฝนนั้นมีทั้งบรรดาความมืด ฟ้าคํารน และฟ้าแลบ พวกเขาจึงเอานิ้วมือของพวกเขาอุดหูไว้ เนื่องจากฟ้าผ่า ทั้งนี้เพราะกลัวความตาย และอัลลอฮฺนั้นทรงล้อม พวกปฏิเสธการศรัทธาเหล่านั้นไว้แล้ว');
INSERT INTO "king_fahad_thai" VALUES(2,20,'2:20','สายฟ้าแลบแทบจะเฉี่ยวสายตาของพวกเขาไป คราใดที่มันให้แสงสว่างแก่พวกเขา พวกเขาก็เดินไปในแสงสว่างนั้น และเมื่อมันมืดลงแก่พวกเขา พวกเขาก็หยุดยืน แลหากอัลลอฮฺทรงประสงค์แล้ว แท่นอนก็ทรงนําเอาหูและตาของพวกเขาไปแล้ว แท้จริงอัลลอฮฺนั้นทรงอานุภาพเหนือทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(2,21,'2:21','มนุษย์เอ๋ย! จงเคารพอิบาดะฮฺ พระผู้เป็นเจ้าของพวกเจ้าที่ทรงบังเกิดพวกเจ้า และบรรดาผู้ที่มาก่อนพวกเจ้าเถิด เพื่อว่าพวกเจ้าจะทรงยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(2,22,'2:22','คือผู้ทรงให้แผ่นดินเป็นที่นอน และฟ้าเป็นอาคาร แก่พวกเจ้า และทรงให้น้ําหลั่งลงมาจากฟากฟ้า แล้วได้ทรงให้บรรดาผลไม้ออกมา เนื่องด้วยน้ํานั้น ทั้งนี้เพื่อเป็นปัจจัยยังชีพแก่พวกเจ้า ดังนั้นพวกเจ้าจงอย่าให้มีผู้เท่าเทียมใด ๆ ขึ้น สําหรับอัลลอฮฺ โดยที่พวกเจ้าก็รู้กันอยู่');
INSERT INTO "king_fahad_thai" VALUES(2,23,'2:23','และหากปรากฏว่าพวกเจ้าอยู่ในความแคลงใจใด ๆ จากสิ่ง ที่เราได้ลงมาแก่บ่าวของเราแล้ว ก็จงนํามาสักซูเราะฮฺหนึ่งเยี่ยงสิ่งนั้น และจงเชิญชวนผู้ที่อยู่ในหมู่พวกเจ้าอื่นนอกจากอัลลอฮฺหากพวกเจ้าเป็นผู้พูดจริง');
INSERT INTO "king_fahad_thai" VALUES(2,24,'2:24','แต่พวกเจ้าก็ยังมิได้ทํา และจะไม่กระทําตลอดไปแล้ว ก็จงระวังไฟนรก ซึ่งเชื้อเพลิงของมันนั้นคือ มนุษย์ และหิน โดยที่มันได้ถูกเตรียมไว้ สําหรับบรรดาผู้)ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(2,25,'2:25','และ(มุฮัมมัด) จงแจ้งข่าวดีแก่บรรดาผู้ศรัทธา และประกอบสิ่งที่ดีทั้งหลายว่า แน่นอนพวกเขาจะได้รับบรรดาสวนสวรรค์ ซึ่งมีแม่น้ําหลายสาย ไหลอยู่ภายใต้สวนสวรรค์เหล่านั้น คราใดที่พวกเขาได้รับผลไม้จากสวนสวรรค์นั้นเป็นเครื่องยังชีพ พวกเขาก็กล่าวว่า นี่คือสิ่งที่เราได้รับเป็นปัจจัยยังชีพมาก่อนแล้ว และสิ่งที่พวกเขาได้รับนั้น มีลักษณะคล้ายคลึงกัน และในสวรรค์นั้น พวกเขาจะได้รับคู่ครองที่บริสุทธิ์ และพวกเขาจะพํานักอยู่ในสวรรค์นั้นตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(2,26,'2:26','แท้จริงอัลลอฮฺไม่ทรงละอาย ในการที่ระองค์จะทรงยกอุทาหรณ์ใด ๆ ขึ้นเปรียบเทียบไม่ว่าจะเป็นริ้นสักตัวหนึ่งแล้วก็สิ่งที่ยิ่งไปกว่านั้นก็ตาม ส่วนบรรดาผู้ที่ศรัทธานั้นพวกเขาย่อมรู้ว่าแท้จริงมัน คือ ความจริงที่มาจากพระเจ้าของพวกเขา และส่วนบรรดาผู้ที่ปฏิเสธการศรัทธานั้นพวกเขาจะพูดว่า อัลลอฮฺทรงประสงค์สิ่งใดในการยกอุทาหรณ์ด้วยสิ่งนี้ ? พระองค์ทรงให้คนมากมายหลงผิดด้วยอุทาหรณ์นั้น และทรงแนะนําทางที่ถูกต้องแก่คนมากมายด้วยอุทาหรณ์นั้น และพระองค์จะไม่ทรงให้ใครหลงผิดด้วยอุทาหรณ์นั้นนอกจากผู้ที่ฝ่าฝืนเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(2,27,'2:27','คือบรรดาผู้ที่ทําลายสัญญาของอัลลอฮฺหลังจากที่ได้มีสัญญาไว้แก่พระองค์ และตัดสิ่งที่อัลลอฮฺทรงใช้ให้ต่อ และบ่อนทําลายในผืนแผ่นดิน ชนเหล่านี้แหละคือพวกที่ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(2,28,'2:28','พวกเจ้าจะปฏิเสธการศรัทธาต่ออัลลอฮฺได้อย่างไร? ทั้ง ๆ ที่พวกจ้านั้นเคยปราศจากชีวิตมาก่อน แล้วพระองค์ก็ทรงให้เจ้ามีชีวิตขึ้น ภายหลังก็จะทรงให้พวกเจ้าตาย แล้วก็จะทรงให้พวกเจ้ามีชีวิตขึ้นอีก และพวกเจ้าก็จะถูกนํากลับไปสู่พระองค์');
INSERT INTO "king_fahad_thai" VALUES(2,29,'2:29','พระองค์คือผู้ได้ทรงสร้างสิ่งทั้งมวลในโลกไว้สําหรับพวกเจ้า ภายหลังได้ทรงมุ่งสู่ฟากฟ้า และได้ทําให้มันสมบูรณ์ขึ้นเป็นเจ็ดชั้นฟ้า และพระองค์นั้นได้ทรงรอบรู้ในทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(2,30,'2:30','และจงรําลึกถึงขณะที่พระเจ้าของเจ้าได้ตรัสแก่มะลาอิกะฮฺว่า แท้จริงข้าจะให้มีผู้แทนคนหนึ่ง ในพิภพ มะลาอิกะฮฺได้ทูลขึ้นว่า พระองค์จะทรงให้มีขึ้นในพิภพซึ่งผู้ที่บ่อนทําลาย และก่อการนองเลือด ในพิภพกระนั้นหรือ ? ทั้ง ๆ ที่พวกข้าพระองค์ให้ความบริสุทธิ์ พร้อมด้วยการสรรเสริญพระองค์ และเทิดทูนความบริสุทธิ์ในพระองค์ พระองค์ตรัสว่า แท้จริงข้ารู้ยิ่งในสิ่งที่พวกเจ้าไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(2,31,'2:31','และพระองค์ได้ทรงสอนบรรดานามของทั้งปวงให้แก่อาดัม ภายหลังได้ทรงแสดงสิ่งเหล่านั้นแก่มะลาอิกะฮฺ แล้วตรัสว่า จงบอกบรรดาชื่อของสิ่งเหล่านั้นแก่ข้า หากพวกเจ้าเป็นผู้พูดจริง');
INSERT INTO "king_fahad_thai" VALUES(2,32,'2:32','พวกเขา(บรรดามะลาอิกะฮฺ)ทูลว่า มหาบริสุทธิ์พระองค์ท่านไม่มีความรู้ใด ๆ แก่พวกข้าพระองค์นอกจากสิ่งที่พระองค์ได้ทรงสอนพวกข้าพระองค์เท่านั้น แท้จริงพระองค์คือผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(2,33,'2:33','พระองค์ตรัสว่า โอ้อาดัม! จงบอกบรรดาชื่อของสิ่งเหล่านั้นแก่พวกเขาที (บรดามะลาอิกะฮฺ) ครั้นเมื่ออาดัมได้บอกชื่อของสิ่งเหล่านั้นแก่พวกเขาแล้ว พระองค์จึงตรัสว่า ข้ามิได้บอกแก่พวกเจ้าดอกหรือว่า แท้จริงข้าเป็นผู้รู้ยิ่งซึ่งความเร้นลับแห่งชั้นฟ้าทั้งหลายและแผ่นดินและเป็นผู้รู้ยิ่งในสิ่งที่พวกเจ้าเปิดเผยและสิ่งที่พวกเจ้าปกปิด');
INSERT INTO "king_fahad_thai" VALUES(2,34,'2:34','และจงรําลึกถึง ขณะที่เราได้กล่าวแก่มะลาอิกะฮฺว่า พวกเจ้าจงสุยูด แก่อาดัมเถิด แล้วพวกเขาก็สุยูดกัน นอกจากอิบลีส โดยที่มันไม่ยอมสุยูด และแสดงโอหัง และมันจึงได้กลายเป็นผู้สิ้นสภาพแห่งการศรัทธา (กาฟิรฺ)');
INSERT INTO "king_fahad_thai" VALUES(2,35,'2:35','และเราได้กล่าว่า โอ้ อาดัม ! เจ้าและคู่ครองของเจ้าจงพํานักอยู่ในสวนสวรรค์นั้นเถิดและเจ้าทั้งสองจงบริโภคจากสวนนั้นอย่างกล้างขวาง ณ ที่ที่เจ้าทั้งสองปรารถนา และอย่าเข้าใกล้ต้นไม้ต้นนี้ (มิเช่นนั้นแล้ว) เจ้าทั้งสองจะกลายเป็นผู้อธรรมแก่ตัวเอง');
INSERT INTO "king_fahad_thai" VALUES(2,36,'2:36','ภายหลังจากชัยฎอนได้ทําให้ทั้งสองนั้นพลั้งพลาดไป เนื่องจากต้นไม้ต้นนั้น แล้วได้ทําให้ทั้งสองออกจากที่ที่เคยพํานักอยู่ และเราได้กล่าวว่า พวกเจ้าจงออกไป โดยที่บางส่วนของพวกเจ้าต่างเป็นศัตรูต่อกัน และ(สําหรับพวกเจ้าในผืนแผ่นดินนั้น) มีที่พํานัก และมีสิ่งอํานวยประโยชน์จนถึงระยะเวลาหนึ่ง');
INSERT INTO "king_fahad_thai" VALUES(2,37,'2:37','ภายหลังดาอัมได้เรียนรู้คําวิงวอนจากพระเจ้าของเขา แล้วพระองค์อภัยโทษแก่เขา แท้จริงพระองค์คือผู้ทรงอภัยโทษ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(2,38,'2:38','เราได้กล่าว่า พวกเจ้าจงออกไปทั้งหมด จากสวนนั้น แล้วหากมีคําแนะนําจากข้ามายังพวกเจ้าแล้ว ผู้ใดที่ปฏิบัติตามคําแนะนําของข้า ก็ไม่มีความกลัวใด ๆ แก่พวกเขา และทั้งพวกเขาก็จะไม่เสียใจ');
INSERT INTO "king_fahad_thai" VALUES(2,39,'2:39','และบรรดาผู้ที่ปฏิเสธศรัทธา และไม่เชื่อบรรดาโองการของเรานั้น ชนเหล่านี้คือชาวนรก โดยที่พวกเขาจะอยู่ในนรกนั้นตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(2,40,'2:40','วงศ์วานอิสรออีลเอ๋ย ! จงรําลึกถึงความโปรดปรานของข้า ที่ข้าได้โปรดปานแก่พวกเจ้า และจงรักษาข้อสัญญาของข้าให้ครบถ้วน ข้าก็จะรักษาสัญญาของพวกเจ้าให้ครบถ้วน และเฉพาะข้าเท่านั้น พวกเจ้าจงเกรงกลัว');
INSERT INTO "king_fahad_thai" VALUES(2,41,'2:41','และพวกเจ้าจงศรัทธาต่อสิ่งที่ข้าได้ให้ลงมาเพื่อยืนยันสิ่งทีมีอยู่กับพวกเจ้า และพวกเจ้าจงอย่าเป็นผู้ปฏิเสธศรัทธาต่อสิ่งนั้นเป็นคนแรก และจงอย่าได้นําโองการของข้าไปแลกเปลี่ยนกับราคาอันเล็กน้อย และเฉาะข้าเท่านั้น พวกเจ้าจงยําเกรงฯ');
INSERT INTO "king_fahad_thai" VALUES(2,42,'2:42','และพวกเจ้าจงอย่าปะปน สิ่งจริงด้วยสิ่งเท็จ และจงอย่าปกปิดสิ่งที่เป็นจริง ทั้ง ๆ ที่พวกเจ้ารู้กันอยู่');
INSERT INTO "king_fahad_thai" VALUES(2,43,'2:43','และพวกเจ้าจงดํารงไว้ซึ่งการละหมาดและจงชําระซะกาต และจงรุกัวะร่วมกับผู้รุกัวะทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(2,44,'2:44','พวกเจ้าใช้ให้ผู้คนกระทําความดี โดยที่พวกเจ้าลืมตัวของพวกเจ้าเองกระนั้นหรือ ? และทั้ง ๆ ที่พวกเจ้าอ่านคัมภีร์กันอยู่ แล้วพวกเจ้าไม่ใช้ปัญญากระนั้นหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(2,45,'2:45','และพวกเจ้าจงอาศัยความอดทน และการละหมาดเถิด และแท้จริงการละหมาดนั้นเป็นสิ่งใหญ่โต นอกจากบรรดาผู้ที่นอบน้อมถ่อมตนเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(2,46,'2:46','คือ บรรดาผู้ที่คาดคิดว่า แน่นอนพวกเขาจะพบกับพระเจ้าของพวกเขา และแน่นอนพวกเขาจะเป็นผู้กลับไปสู่พระองค์');
INSERT INTO "king_fahad_thai" VALUES(2,47,'2:47','วงศ์วานอิสรออีลเอ๋ย ! จงรําลึกถึงความโปรดปรานของข้า ที่ข้าได้โปรดปรานแก่พวกเจ้า และแท้จริงนั้น ข้าได้เทิดพวกเจ้าไว้เหนือประชาชาติทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(2,48,'2:48','และจงกลัวเกรงวันหนึ่ง ซึ่งไม่มีชีวิตใดจะตอบแทนสิ่งใดแทนอีกชีวิตได้ และการขอให้มีความช่วยเหลือใด ๆก็จะไม่ถูกรับจากชีวิตนั้น และค่าไถ่ถอนใด ๆ ก็จะไม่ถูกรับเอาจากชีวิตนั้น ด้วย และทั้งพวกเขาก็จะไม่ได้รับความช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(2,49,'2:49','และจงรําลึกถึง ขณะที่เราได้ช่วยพวกเจ้าให้พ้นจากพวกพ้องของฟิรอาวน์ โดยที่พวกเขาบังคับขู่เข็ญพวกเจ้า ซึ่งการทรมานอันร้ายแรง พวกเขาเชือดบรรดาลูกผู้ชายของพวกเจ้า และไว้ชีวิตบรรดาลูกผู้ชายของพวกเจ้า และไว้ชีวิตบรรดาลูกผู้หญิงของพวกเจ้า และในเรื่องนั้น คือการทดสอบอันสําคัญจากพระเจ้าของพวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(2,50,'2:50','และจงรําลึกถึงขณะที่เราได้แยกทะเลออก เพราะพวกเจ้า แล้วเราได้ช่วยพวกเจ้าให้รอดพ้น และได้รให้พวกฟิรอาวน์จมน้ําตาย ขณะที่พวกเจ้ามองดูอยู่');
INSERT INTO "king_fahad_thai" VALUES(2,51,'2:51','และจงรําลึกถึงขณะที่เราได้สัญญาแก่มูซาไว้สี่สิบคืน แล้วพวกเจ้าได้ยึดถือลูกวัวตัวนั้นหลักจากเขา และพวกเจ้านั้นคือผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(2,52,'2:52','แล้วเราก็ได้ให้อภัยแก่พวกเจ้า หลังจากนั้น เพื่อว่าพวกเจ้าจักขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(2,53,'2:53','และจงรําลึกถึงขณะที่เราได้ให้คัมภีร์และ อัล-ฟุรฺกอน แก่ มูซา หวังว่าพวกเจ้าจะได้รับคําแนะนําที่ถูกต้อง');
INSERT INTO "king_fahad_thai" VALUES(2,54,'2:54','และจงรําลึกถึงขณะที่มูซาได้กล่าวแก่กลุ่มชนของเขาว่า โอ้กลุ่มชนของฉัน ! แท้จริงพวกท่านได้ อยุติธรรมแก่ตัวของพวกท่านเอง โดยที่พวกท่านได้ยึดถือลูกวัวตัวนั้น (เป็นที่เคารพสักการะ) ดังนั้นจงกลับสู่พระผู้บังเกิดพวกท่านเถิด แล้วจงฆ่าตัวของพวกท่านเอง นั่นเป็นสิ่งดีแก่พวกท่าน ณ พระผู้บังเกิดพวกท่าน ภายหลังพระองค์ก็ได้ทรงอภัยโทษแก่พวกท่าน แท้จริงพระองค์คือผู้ทรงอภัยโทษ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(2,55,'2:55','และจงรําลึกถึง ขณะที่พวกเจ้ากล่าวว่า โฮ้มูซา ! เราจะไม่ศรัทธาต่อท่านเป็นอันขาด จนกว่าเราจะได้เห็นอัลลอฮฺโดยเปิดเผย แล้วสายฟ้าฝ่าก็ได้คร่าพวกเจ้า ขณะที่พวกเจ้ามองดูกันอยู่');
INSERT INTO "king_fahad_thai" VALUES(2,56,'2:56','ภายหลังเราได้ให้พวกเจ้าคืนชีพ หลังจากที่พวกเจ้าได้ตายไปแล้ว เพื่อว่าพวกเจ้าจักขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(2,57,'2:57','และเราได้ให้เมฆบดบังพวกเจ้า และได้ให้อัล-มันนะ และอัส-ซัลวา แก่พวกเจ้า พวกเจ้าจงบริโภคสิ่งที่เราได้ให้เป็นปัจจัยยังชีพแก่พวกเจ้าจากสิ่งดี ๆ เถิด และพวกเขาหาได้อธรรมแก่เราไม่ แต่ทว่าพวกเขาอธรรมแก่ตัวของพวกเขาเองต่างหาก');
INSERT INTO "king_fahad_thai" VALUES(2,58,'2:58','และจงรําลึกถึงขณะที่เราได้กล่าวว่า พวกเจ้าจงเข้าไปในเมืองนี้ แล้วจงบริโภคจากเมืองนั้นอย่างกล้างขวาง ณ ที่ที่พวกเจ้าปรารถนา และจงเข้าประตูนั้น ไปในสภาพผู้โน้มศรีษะลงด้วยความนอบน้อม และจงกล่าวว่า “อิฏเฏาะฮฺ” เราก็จะอภัยโทษให้แก่พวกเจ้าซึ่งบรรดาความผิดของพวกเจ้า และเราจะเพิ่มพูนแก่บรรดาผู้กระทําความดี');
INSERT INTO "king_fahad_thai" VALUES(2,59,'2:59','แล้วบรรดาผู้อธรรมเหล่านั้น ได้เปลี่ยนเอาคําพูดหนึ่งซึ่งมิใช่คําพูดที่ถูกกล่าวแก่พวกเขา เราจึงได้ให้การลงโทษจากฟากฟ้าลงมาแก่บรรดาผู้อธรรมเหล่านั้น เนื่องจากการที่พวกเขาละเมิด');
INSERT INTO "king_fahad_thai" VALUES(2,60,'2:60','และจงรําลึกถึงขณะที่มูซาได้ขอน้ําให้แก่กลุ่มชนของพวกเขา แล้วเราได้กล่าวว่า เจ้าจงตีหินด้วยไม่เท้าของเจ้าแล้วตาน้ําสิบสองตา ก็พุ่งออกจากหินนั้น แน่นอนกลุ่มชนแต่ละกลุ่ม ย่อมรู้แหล่งน้ําดื่มของตน พวกเจ้าจงกินและจงดื่มจากปัจจัยยังชีพของอัลลอฮ์ และจงอย่าก่อกวนในผืนแผ่นดิน ในฐานะผู้บ่อนทําลาย');
INSERT INTO "king_fahad_thai" VALUES(2,61,'2:61','และจงรําลึกถึงขณะที่พวกเจ้ากล่าวว่า โอ้มูซา ! เราไม่สามารถจะอดทนต่ออาหารชนิดเดียว อีกต่อไปได้ ดังนั้นจงวิงวอนต่อพระเจ้าของท่านให้แก่เราเถิด พระองค์จะทรงให้ออกมาแก่เราจากสิ่งที่แผ่นดินให้งอกเงยขึ้น อันได้แก่พืชผัก แตงกวา กระเทียม ถั่ว และหัวหอม มูซาได้กล่าวว่าพวกท่านจะขอเปลี่ยนเอาสิ่งที่มันเลวกว่า ดัวยสิ่งที่มันดีกว่ากระนั้นหรือ? พวกท่านจงลงไปอยู่ในเมืองเถิด แล้วสิ่งที่พวกท่านขอก็จะเป็นของพวกท่าน และความอัปยศ และความขัดสนก็ถูกกระหน่ําลงบนพวกเขา และพวกเขาได้นําเอาความกริ้วโกรธจากอัลลอฮ์กลับไป นั่นก็เพราะว่าพวกเขาเคยปฏิเสธสัญญาณต่างๆ ของอัลลอฮ์ และยังฆ่าบรรดานะบี โดยปราศจากความเป็นธรรม นั่นก็เนื่องจากความดื้อดันของพวกเขา และพวกเขาจึงได้กลายเป็นผู้ละเมิดขอบเขต');
INSERT INTO "king_fahad_thai" VALUES(2,62,'2:62','แท้จริงบรรดาผู้ศรัทธา และบรรดาผู้ที่เป็นยิว และบรรดาผู้ที่เป็นคริสเตียน และอัศ-ซอบิอีน ผู้ใดก็ตามที่ศรัทธาต่ออัลลอฮฺและวันปรโลก และประกอบสิ่งที่ดีแล้ว พวกเขาก็จะได้รับรางวัลของพวกเขา ณ พระผู้เป็นเจ้าของพวกเขา และไม่มีความหวาดกลัวใด ๆ แก่พวกเขา และทั้งพวกเขาก็จะไม่เสียใจ');
INSERT INTO "king_fahad_thai" VALUES(2,63,'2:63','และจงรําลึกถึงขณะที่เราได้เอาคํามั่นสัญญาจากพวกเจ้า และเราได้ยกภูเขาขึ้นเหนือพวกเจ้า จงยึดถือสิ่งที่เราได้ให้แก่พวกเจ้าด้วยความเข้มแข็ง และจงรําลึกถึงสิ่งที่มีอยู่ในนั้น หวังว่าพวกเจ้าจะเกรงกลัว');
INSERT INTO "king_fahad_thai" VALUES(2,64,'2:64','แล้วหลังจากนั้น พวกเจ้าก็ผินหลังให้ หากอัลลอฮ์ไม่ทรงโปรดปราน และกรุณาเมตตาแก่พวกเจ้าแล้ว แน่นอนพวกเจ้าย่อมกลายเป็นพวกที่ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(2,65,'2:65','และแน่นอนพวกเจ้ารู้กันแล้วถึงบรรดาผู้ที่อยู่ในพวกของเจ้าที่ได้ละเมิดในวันสับบาโต แล้วเราได้กล่าวแก่พวกเขาว่า พวกเจ้าจงเป็นลิงที่ถูกขับไล่ให้ห่างไกล');
INSERT INTO "king_fahad_thai" VALUES(2,66,'2:66','แล้วเราได้ให้การลงโทษนั้นเป็นเยี่ยงอย่างแก่ประชาชาติที่อยู่เบื้องหน้ามัน และประชาชาติที่อยู่เบื้องหลังมัน และให้เป็นข้อเตือนสติแก่ผู้เกรงกลัวทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(2,67,'2:67','และจงรําลึกถึงขณะที่มูซาได้กล่าวแก่กลุ่มชนของเขาว่า แท้จริงอัลลอฮฺทรงบัญชาแก่พวกท่านให้เชือดวัวตัวเมียตัวหนึ่ง พวกเขากล่าวว่า ท่านจะถือเอาพวกเราเป็นที่ล้อเล่นกระนั้นหรือ? มูซากล่าวว่า ฉันขอความคุ้มครองต่ออัลลอฮฺให้พ้นจากการที่ฉันจะเป็นพวกโง่เขลาเบาปัญญา');
INSERT INTO "king_fahad_thai" VALUES(2,68,'2:68','และพวกเขากล่าวว่า โปรดวิงวอนต่อพระเจ้าของท่านให้แก่พวกเราด้วยเถิด พระองค์ก็จะทรงแจ้งแก่พวกเราว่า วัวนั้นเป็นอย่างไร? มูซากล่าวว่า แท้จริงพระองค์ตรัสว่า มันเป็นวัวตัวเมียที่ไม่แก่และไม่สาว แต่มีอายุกึ่งกลางระหว่างนั้น พวกท่านจงปฏิบัติตามสิ่งที่พวกท่านถูกใช้เถิด');
INSERT INTO "king_fahad_thai" VALUES(2,69,'2:69','พวกเขากล่าวว่า โ)รดวิงวอนต่อพรเจ้าของท่านให้แก่พวกเราเถิด พระองค์ก็จะทรงแจ้งแก่พวกเราว่า วัวนั้นสีอะไร? มูซากล่าวว่า แท้จริงพรองค์ตรัสว่า มันเป็นวัวสีเหลือง สีของมันเข้มซึ่งทําให้เกิดความปิติยินดีแก่บรรดาผู้ที่มองดู');
INSERT INTO "king_fahad_thai" VALUES(2,70,'2:70','พวกเขากล่าวว่า โปรดวิงวอนต่อพระเจ้าของท่านให้แก่เราเถิด พระองค์ก็จะทรงแจ้งแก่พวกเราว่า วัวนั้นเป็นอย่างไร ? แท้จริงวัวนั้นมันคล้าย ๆ กันแก่พวกเรา และหากอัลลอฮ์ทรงประสงค์แน่นอนพวกเราก็เป็นผู้ที่ได้รับคําแนะนํา');
INSERT INTO "king_fahad_thai" VALUES(2,71,'2:71','มูซากล่าวว่า แท้จริงพระองค์ตรัสว่า มันเป็นวัวที่ไม่สยบง่าย ๆ ที่จะไถดินและที่จะทดน้ําเข้านา เป็นวัวบริสุทธิ์ปราศจากสีอื่นใดแซมในตัวมัน พวกเขากล่าวว่า บัดนี้ท่านได้นําความจริงมาให้แล้ว แล้วพวกเขาก็เชือดมัน และพวกเขาเกือบจะไม่ทํามันอยู่แล้ว');
INSERT INTO "king_fahad_thai" VALUES(2,72,'2:72','และจงรําลึกถึงขณะที่พวกเจ้าฆ่าคนคนหนึ่ง แล้วพวกเจ้าต่สงปกป้องตัวเองในเรื่องนั้น และอัลลอฮ์นั้น จะเป็นผู้ทรงเปิดเผยสิ่งที่พวกเจ้าปกปิดไว้');
INSERT INTO "king_fahad_thai" VALUES(2,73,'2:73','แล้วเราได้กล่าวว่า พวกเจ้าจงตีเขาด้วยบางส่วนของวัวตัวนั้น ในทํานองนั้นแหละ อัลลอฮ์จะทรงให้ผู้ตาย มีชีวิตขึ้นมา และจะทรงให้พวกเจ้าเห็นสัญญาณต่าง ๆ เพื่อว่าพวกเจ้าจะได้เข้าใจ');
INSERT INTO "king_fahad_thai" VALUES(2,74,'2:74','แล้วหลังจากนั้น หัวใจของพวกเจ้าก็แข็งกระด้าง มันประดุจหิน หรือแข็งกระด้างยิ่งกว่าและแท้จริงจากบรรดาหินนั้น มีส่วนที่บรรดาธารน้ําพวกพุ่งออกจากมัน และแท้จริงจากบรรดาหินนั้นมีส่วนที่แตกแยกออก แล้วมีน้ําออกจากมัน และแท้จริงจากบรรดาหินนั้นมีส่วนที่ทลายลง เนื่องจากความเกรงกลัวอัลลอฮ์ และอัลลอฮ์นั้นจะไม่ทรงเผลต่อสิ่งที่พวกเจ้ากระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(2,75,'2:75','พวกเจ้ายังโลภที่จะให้พวกเขา ศรัทธาต่อพวกเจ้าอีกกระนั้นหรือ ? ทั้ง ๆ ที่กลุ่มหนึ่งในพวกเขาเคยสดับฟังดํารัสอัลลอฮ์แล้วพวกเขาก็บิดเบือนมันเสีย หลังจากที่พวกเขาเข้าใจแล้ว ทั้ง ๆ ที่พวกเขาก็ตระหนักดีอยู่');
INSERT INTO "king_fahad_thai" VALUES(2,76,'2:76','และเมื่อพวกเขาได้พบแบบรรดาผุ้ที่ศรัทธาพวกเขาก็กล่าวว่า เราศรัทธากันแล้ว และเมื่อบางคนในพวกเขาอยู่ตามลําพังกับอีกบางคน พวกเขาก็กล่าวว่า พวกท่านจะพูดให้พวกเขาฟังซึ่งสิ่งที่อัลลอฮ์ได้ทรงเปิดเผยแก่พวกท่าน เพื่อพวกเขาจะได้นําสิ่งนั้นไปเป็นหลักฐานยืนยันแก่พวกท่าน ณ ที่พระเจ้าของพวกท่านกระนั้นหรือ ? พวกท่านไม่ใช้ปัญญาดอกหรือ?');
INSERT INTO "king_fahad_thai" VALUES(2,77,'2:77','และเขาเหล่านั้นไม่รู้หรือว่า แท้จริงอัลลอฮ์นั้นทรงรอบรู้ในสิ่งที่พวกเขาปกปิด และสิ่งที่พวกเขาเปิดเผย');
INSERT INTO "king_fahad_thai" VALUES(2,78,'2:78','และในหมู่พวกเขานั้นมีบรรดาผู้ที่เขียนอ่านไม่เป็น ซึ่งพวกเขาไม่รู้คัมภีร์ นอกจากความเพ้อฝันเท่านั้น และพวกเขาหาได้มีอะไรไม่ นอกจากจะนึกคิดเอาเองเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(2,79,'2:79','ดังนั้นความวิบัติจะได้แก่บรรดาผุ้ที่เขียนคัมภัร์ขึ้นด้วยมือของคตนเอง แล้วกล่าวว่า นี่แหละมาจากอัลลอฮ์ เพื่อพวกเขาจะได้นํามันไปแลกเปลี่ยนกับราคาอันเล็กน้อย ดังนั้นความวิบัติจะได้แก่พวกเขา เนื่องจากสิ่งที่มือของพวกเขาได้เขียนขึ้นและความวิบัตินั้นจะได้แก่พวกเขา เนื่องจากสิ่งที่พวกเขาแสวงหาไว้');
INSERT INTO "king_fahad_thai" VALUES(2,80,'2:80','และเขาเหล่านั้นกล่าวว่า ไฟนรกนั้นจะไม่แตะต้องพวกเราเลย นอกจากบรรดาวันที่ถูกนับได้ จงกล่าวเถิด (มุฮัมมัด) ว่าท่านทั้งหลายได้ยึดถือคํามั่นสัญญา ณ ที่อัลลอฮ์จะไม่ทรงผิดสัญญาของพระองค์เลยกระนั้นหรือ ? หรือว่าพวกท่านอุปโลกน์ความเท็จขึ้นให้แก่อัลลอฮ์ในสิ่งที่พวกท่านไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(2,81,'2:81','หาใช่เช่นนั้นไม่ ผู้ใดที่แสดวงหาความชั่วและความผิดของเขาได้ล้อมเขาไว้นั้น ชนเหล่านี้คือชาวรกโดยที่พวกเขาจะอยู่ในนรกนั้นตลอกกาล');
INSERT INTO "king_fahad_thai" VALUES(2,82,'2:82','และบรรดาผู้ที่ศรัทธา และประกอบสิ่งที่ดีงามทั้งหลายนั้น ชนเหล่านี้แหละคือชาวสวรรค์โดยที่พวกเขาจะพํานักอยู่ในสวรรค์โดยที่พวกเขาจะพํานักอยู่ในสวรรค์ตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(2,83,'2:83','และจงรําลึกถึงขณะที่เราได้เอาคํามั่นสัญญาจากวงศ์วานอิสรออีลว่า พวกเจ้าจะต้องไม่เคารพสักการะนอกจากอัลลอฮ์เท่านั้น และจงทําดีต่อบิดามารดา ญาติที่ใกล้ชิด เด็กกําพร้า และขัดสน และจงพูดจาแก่เพื่อนมนุษย์อย่างดี และจงดํารงไว้ซึ่งการละหมาด และจงชําระซะกาต แต่แล้วพวกเจ้าก็ผินหลังให้ นอกจากเพียงเล็กน้อยในหมู่พวกเจ้าเท่านั้น และพวกเจ้าก็กําลังผินหลังให้อยู่');
INSERT INTO "king_fahad_thai" VALUES(2,84,'2:84','และจงรําลึกถึงขณะที่เราได้อาคํามั่นสัญญาแก่พวกเจ้าว่า พวกเจ้าจะต้องไม่หลั่งเลือดของพวกเจ้า และจะต้องไม่ขับไล่ตัวของพวกเจ้าเอง ออกจากหมู่บ้านของพวกเจ้า แล้วพวกเจ้าก็ได้ให้ในการรับรอง และทั้งพวกเจ้าก็ยังยืนอยู่');
INSERT INTO "king_fahad_thai" VALUES(2,85,'2:85','ภายหลังพวกเจ้านี้แหละฆ่าตัวของพวกเจ้าเอง และขับไล่กลุ่มหนึ่งในหมู่พวกเจ้าออกจากหมู่บ้านของพวกเขา โดยที่พวกเจ้าต่างร่วมมือกันเอาชนะพวกเขา ด้วยการกระทําบาป และการเป็นศัตรูกัน และถ้าพวกเขา มายังพวกเจ้าในฐานะเชลย พวกเจ้าก็ไถ่ตัวพวกเขา ทั้ง ๆ ที่การขังไล่พวกเขาออกไปนั้น เป็นที่ต้องห้ามแก่พวกเจ้า พวกเจ้าจะศรัทธาแต่เพียงบางส่วนของคัมภีร์และปฏิเสธและปฏิเสธอีกบางส่วนกระนั้นหรือ ? ดังนั้น สิ่งตอบแทนแก่ผุ้กระทําเช่นนั้นจากพวกเจ้าจึงมิใช่อะไรอื่นนอกจากความอัปยศอดสูในชีวิตความเป็นอยู่แห่งโลกนี้เท่านั้น และในวันกิยามะฮ์ พวกเขาจะถูกนํากลับไปสู่การลงโทษอันฉกรรจ์ยิ่ง และอัลลอฮ์นั้นจะไม่ทรงเผลอในสิ่งที่พวกเจ้ากระทํากันอยู่');
INSERT INTO "king_fahad_thai" VALUES(2,86,'2:86','ชนเหล่านี้ คือ ผู้ที่ซื้อเอาชีวิตความเป็นอยู่แห่งโลกนี้ไว้ด้วยชีวิตความเป็นอยู่แห่งปรโลก ดังนั้น การลงโทษจึงไม่ถูกลดหย่อนแก่พวกเขาและทั้งพวกเขาก็จะไม่ได้รับความช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(2,87,'2:87','และแท้จริงนั้น เราได้ให้คัมภีร์มูซาและหลังจากเขา เราได้ให้บรรดาร่อซูล ติดตามมาและราได้ให้หลัฐานต่าง ๆ อันชัดเจน แก่ อีซา บุตรของมัรยัม และเราได้สนับสนุนเขาด้วยวิญญาณอันปริสุทธิ์ แล้วคราใดที่ได้มีร่อซูลนําสิ่งที่ไม่สบอารมณ์ของพวกเจ้ามายังพวกเจ้า พวกเจ้าก็ยะโสแล้วกลุ่มหนึ่งพวกเจ้าก็ปฏิเสธ และอีกกลุ่มหนึ่งพวกเจ้าก็ฆ่าเสียกระนั้หรือ ?');
INSERT INTO "king_fahad_thai" VALUES(2,88,'2:88','และพวกเขากล่าวว่า หัวใจของพวกเรามีเปลือกหุ้มอยู่ มิใช่เช่นนั้นดอก อัลลอฮ์ทรงขับไล่พวกเขาให้ออกจากความเมตตาของพระองค์ต่างหาก เนื่องจากการปฏิเสธศรัทธาของพวกเขาช่างน้อยเหลือเกินที่พวกเขาศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(2,89,'2:89','และเมื่อได้มีคัมภีร์ฉบับหนึ่งจากที่อัลลอฮ์มายังพวกเขา ซึ่งยืนยันในสิ่งที่มีอยู่กับพวกเขา ทั้ง ๆ ที่พวกเขาเคยขอให้มีชัยชนะเหนือบรรดาผุ้ที่ปฏิเสธศรัทธามาก่อน ครั้นเมื่อสิ่งที่พวกเขารู้จักดี ได้มายังพวกเขาแล้ว พวกเขากลับปฏิเสธสิ่งนั้นเสีย ดังนั้นความห่างไกลจากเราะฮ์มัต ของอัลลอฮ์จึงตกอยู่แก่บรรดาผู้ปฏิเสธเหล่านั้น');
INSERT INTO "king_fahad_thai" VALUES(2,90,'2:90','ชั่วช้าจริง ๆ สิ่งที่พวกเขาขายตัวของพวกเขาด้วยสิ่งนั้น คือการที่พวกเขาปฏิเสธสิ่งที่อัลลอฮ์ ได้ทรงประทานลงมา ทั้งนี้เพราะความอิจฉาริษยาในการที่อัลลอฮ์ทรงประทานส่วนหนึ่งจากความโปรดปรานของพระองค์แก่ผู้ที่พระองค์ทรงประสงค์ในหมู่ปวงบ่าวของพระองค์ ดังนั้นพวกเขาจึงนําความกริ้วโกรธซ้อนความกริ้วโกรธกลับไป (ยังพระองค์) และสําหรับผู้ปฏิเสธการศรัทธานั้นคือการลงโทษอันต่ําช้า');
INSERT INTO "king_fahad_thai" VALUES(2,91,'2:91','และเมื่อได้ถูกกล่าวแก่เขาเหล่านั้นว่า จงศรัทธาต่อสิ่งที่อัลลอฮ์ได้ทรงประทานลงมาเถิด พวกเขาก็กล่าวว่า เรากําลังศรัทธาต่อสิ่งที่ได้ถูกประทานลงมาแก่เราอยู่แล้ว ทั้ง ๆ ที่สิ่งนั้นคือ ความจริงโดยยืนยันสิ่งที่มีอยู่กับพวกเขา จงกล่าวเถิด (มุฮัมมัด) ว่า เพราะเหตุใด เมื่อก่อนโน้นพวกท่านจึงฆ่านะบีของอัลลอฮ์ ถ้าหากพวกท่านเป็นผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(2,92,'2:92','และแท้จริงนั้น มูซาได้นําบรรดาหลักฐานอันชัดเจนมายังพวกเจ้าแล้ว แต่พวกเจ้าได้ยึดถือลูกวัว (เป็นที่เคารพสักการะ) หลังจากเขา และพวกเจ้านี้คือ พวกอธรรม');
INSERT INTO "king_fahad_thai" VALUES(2,93,'2:93','และจงรําลึกถึงขณะที่เราได้เอาคํามั่นสัญญาจากพวกเจ้า และเราได้ยกภูเขาฎูร์ขึ้นเหนือพวกเจ้า พวกเจ้าจงยึดถือสิ่งที่เราได้ให้ลงมาแก่พวกเจ้าด้วยความเข้มแข็ง และจงสดับฟัง พวกเขากล่าวว่า พวกข้าพระองค์ฟังกันแล้ว และก็ได้ฝ่าฝืนกันไปแล้ว และพวกเขาได้ถูกให้ดื่มลูกวัวเข้าไปในหัวใจของพวกเขา เนื่องจากพวกเขาปฏิเสธศรัทธา จงกล่าวเถิด(มุฮัมมัด)ว่าช่างชั่วช้าจริง ๆ สิ่งที่การศรัทธาพวกท่านใช้พวกท่านให้กระทําสิ่งนั้น ถ้าหากว่าพวกท่านเป็นผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(2,94,'2:94','จงกล่าวเถิด (มุฮัมมัด) ว่า หากว่าสถานที่พํานักแห่งปรโลก ณ ที่อัลลอฮ์เป็นของพวกท่านโดยเฉพาะ มิใช่ของบุคคลอื่นแล้วไซร้ก็จงปรารถนาความตายเสียเถิด ถ้าหากพวกเจ้าเป็นผู้พูดจริง');
INSERT INTO "king_fahad_thai" VALUES(2,95,'2:95','และเขาเหล่านั้น จะไม่ปรารถนาความตายเลยตลอดกาล เนื่องด้วยสิ่งที่มือของพวกเขาได้ประกอบล่วงหน้าไว้ และอัลลอฮ์นั้นเป็นผู้ทรงรู้ดีต่อบรรดาผู้อธรรมเหล่านั้น');
INSERT INTO "king_fahad_thai" VALUES(2,96,'2:96','และแน่นอนเหลือเกินเจ้า จะพบว่าพวกเขาเป็นมนุษย์ที่ห่วงใยยิ่งต่อชีวิตความเป็นอยู่ และยิ่งกว่าบรรดาผู้ที่ให้มีภาคีขึ้น(แก่อัลลอฮ์) เสียอีก คนหนึ่งคนใดในพวกเขานั้นชอบ หากว่าเขาจะถูกให้มีอายุถึงพันปี และมันจะไม่ทําให้เขาห่างไกลจากการลงโทษไปได้ ในการที่เขาจะถูกให้มีอายุยืนนาน และอัลลอฮ์นั้นเป็นผู้ทรงเห็นในสิ่งที่เขาเหล่านั้นกระทํากันอยู่');
INSERT INTO "king_fahad_thai" VALUES(2,97,'2:97','จงกล่าวเถิด (มุฮัมมัด) ว่า ใครที่เคยเป็นศัตรูต่อญิบรีลบ้าง ? แท้จริงนั้น เขาได้นําอัล-กรุ์อาน ทยอยลงมายังหัวใจจของเจ้าด้วยอนุมัติของอัลลอฮ์ ทั้งนี้เพื่อยืนยัน สิ่งที่อยู่หน้าอัล-กรุอาน และเพื่อเป็นข้อแนะนํา และข่าวดีแก่ผู้ศรัทธาทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(2,98,'2:98','ใครที่เป็นศัตรูต่ออัลลอฮ์ และมะลาอิกะฮ์ของพระองค์ และบรรดาร่อซูลของพระองค์และเป็นศัตรูต่อญิบรีล และมีกาอีลนั้น แท้จริงอัลลอฮ์ ทรงเป็นศัตรูแก่ผู้ปฏิเสธศรัทธาทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(2,99,'2:99','และแท้จริงเราได้ให้สัญญาณต่าง ๆ อันชัดแจ้งแก่เจ้าแล้ว และย่อมจะไม่มีใครปฏิเสธสัญญาณเหล่านั้น นอกจากบรรดาผุ้ที่ฝ่าฝืนเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(2,100,'2:100','และคราใดที่พวกเขา ได้ให้คํามั่นสัญญาใด ๆ ไว้ กลุ่มหนึ่งในพวกเขาก็เหวี่ยงสัญญานั้นทิ้งเสียกระนั้นหรือ ? หามิได้ส่วนมากของพวกเขาไม่ศรัทธาต่างหาก');
INSERT INTO "king_fahad_thai" VALUES(2,101,'2:101','และเมื่อได้มีร่อซูลคนใด ณ ที่อัลลอฮฺ มายังพวกเขา ซึ่งจะเป็นผู้ยืนยันสิ่งที่มีอยู่กับพวกเขา กลุ่มหนึ่งจากบรรดาผู้ที่ได้รับคัมภีร์ ก็เหวี่ยงคัมภีร์ของอัลลอฮฺไว้เบื้องหลังของพวกเขาเสีย เสมือนหนึ่งว่าพวกเขาไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(2,102,'2:102','และพวกเขาได้ปฏิบัติตามสิ่งที่บรรดาชัยฏอน ในสมัยสุลัยมานอ่านให้ฟัง และสุลัยมานหาได้ปฏิเสธการศรัทธาไม่ แต่ทว่าชัยฏอนเหล่านั้นต่างหากที่ปฏิเสธการศรัทธา โดยสอนประชาชนซึ่งวิชาไสยศาสตร์และสิ่งที่ถูกประทานลงมา แก่มะลาอิกะฮ์ทั้งสอง คือ ฮารูต และมารูต ณ เมืองบาบิล และเขาทั้งสองจะไม่สอนให้แก่ผู้ใดจนกว่าจะกล่าวว่า แท้จริงเราเพียงเป็นผู้ทดสอบเท่านั้น ท่านจงอย่าปฏิเสธการศรัทธาเลย แล้วเขาเหล่านั้นก็ศึกษาจากเขาทั้งสอง สิ่งที่พวกเขาจะใช้มันยังความแตกแยกระหว่างบุคคลกับภรรยาของเขา และพวกเขาไม่อาจทําให้สิ่งนั้นเป็นอันตรายแก่ผู้ใดได้ นอกจากด้วยการอนุมัติของอัลลอฮฺเท่านั้น และพวกเขาก็เรียนสิ่งที่เป็นโทษแก่พวกเขา และมิใช่เป็นคุณแก่พวกเขา และแท้จริงนั้นพวกเขารู้แล้วว่าแน่นอนผู้ที่ซื้อมันไว้นั้น ในปรโลกก็ย่อมไม่มีส่วนได้ใด ๆ และแน่นอนเป็นสิ่งที่ชั่วช้าจริง ๆ ที่พวกเขาขายตัวของพวกเขาด้วยสิ่งนั้น หากพวกเขารู้');
INSERT INTO "king_fahad_thai" VALUES(2,103,'2:103','และหากว่า เขาเหล่านั้นศรัทธา และเกรงกลัวแล้ว แน่นอน ผลานิสงส์ ณ ที่อัลลอฮ์นั้นย่อมดีกว่า หากพวกเขารู้');
INSERT INTO "king_fahad_thai" VALUES(2,104,'2:104','โอ้ ศรัทธาทั้งหลาย ! จงอย่าพูดว่ารออินา และจงพูดว่า อุนซุรนา และจงฟัง และสําหรับผู้ปฏิเสธการศรัทธานั้น คืดการลงโทษอันเจ็บแสบ');
INSERT INTO "king_fahad_thai" VALUES(2,105,'2:105','บรรดาผู้ปฏิเสธการศรัทธาทั้งจากอะฮ์ลุลกิตาป และจากบรรดามุชริกนั้น ต่างไม่ชอบที่จะให้มีความดีใด ๆ จากพระเจ้าของพวกเจ้าถูกประทานลงมาแก่พวกเจ้าและอัลลอฮ์านั้นจะทรงเจาะจงความกรุณาของพระองค์แก่ผู้ที่พระองค์ทรงประสงค์และอัลลอฮ์นั้นเป็นผู้มีบุญคุณอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(2,106,'2:106','โองการใดที่เรายกเลิก หรือเราทําให้มันลืมเลือนไปนั้น เราจะนําสิ่งที่ดีกว่าโองการนั้นมา หรือสิ่งที่เท่าเทียมกับโองการนั้น เจ้ามิได้รู้ดอกหรือว่า แท้จริงอัลลอฮ์นั้นทรงเดชานุภาพ เหนือทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(2,107,'2:107','เจ้า (มุฮัมมัด) มิได้รู้ดอกหรือว่า แท้จริงอัลลอฮ์นั้นทรงมีอํานาจแห่งชั้นฟ้าทั้งหลาย และแผ่นดิน อื่นจากอัลลอฮ์แล้ว พวกเจ้าย่อมไม่มีผู้คุ้มครองใด ๆ และผู้ช่วยเหลือใด ๆ');
INSERT INTO "king_fahad_thai" VALUES(2,108,'2:108','หรือพวกเจ้าจะขอร้องต่อร่อซูลของพวกเจ้า เช่นเดียวกับที่มูซาเคยถูกขอร้องมาก่อนแล้ว และผุ้ใดเปลี่ยนเอาการปฏิเสธไว้แทนการศรัทธาแล้วไซร้ แน่นอนเขาได้หลงทางอันเที่ยงตรงเสียแล้ว');
INSERT INTO "king_fahad_thai" VALUES(2,109,'2:109','อะฮ์ลุลกิตาบมากมาย ชอบ หากพวกเขาจะสามารถทําให้พวกเจ้ากลับเป็นผู้ปฏิเสธการศรัทธาอีก ทั้งนี้เพราะความอิจฉาริษยาที่มาจากตัวของพวกเขาเอง หลังจากความจริงได้ประจักษ์แก่พวกเขา ดังนั้น พวกเจ้าจงให้อภัย และเบือนหน้าเสีย จนกว่าอัลลอฮ์จะประทานคําสั่งของพระองค์มา แท้จริงอัลลอฮ์นั้นทรงเดชานุภาพเหนือทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(2,110,'2:110','และพวกเจ้าจงดํารงไว้ซึ่งการละหมาดเถิด และจงชําระซะกาตเสีย และความดีใด ๆ ที่พวกเจ้าได้ประกอบล่วงหน้าไว้สําหรับตัวของพวกเจ้าเอง พวกเจ้าก็จะพบมัน ณ ที่อัลลอฮ์ แท้จริงอัลลอฮ์นั้นทรงเห็นในสิ่งที่พวกเจ้าปฏิบัติกันอยู่');
INSERT INTO "king_fahad_thai" VALUES(2,111,'2:111','และพวกเขากล่าวว่า จะไม่มีใครเข้าสวรรค์เลย นอกจากผู้ที่เป็นยิวหรือเป็นคริสเตียนเท่านั้น นั่นคือความเพ้อฝันของพวกเขา จงกล่าวเถิด (มูฮัมมัด) ว่า พวกท่านจงนําหลักฐานของพวกท่านมา ถ้าพวกท่านเป็นผู้พูดจริง');
INSERT INTO "king_fahad_thai" VALUES(2,112,'2:112','หาใช่เช่นนั้นไม่ ผู้ใดที่มอบใบหน้าของเขา ให้แก่อัลลอฮ์ และขณะเดียวกัน เขาก็เป็นผู้กระทําความดีแล้วไซร้ เขาจะได้รับรางวัลของเขา ณ ที่พระเจ้าของเขา และไม่มีความกลัวใด ๆ แก่พวกเขา และทั้งพวกเขาก็จะไม่เสียใจ');
INSERT INTO "king_fahad_thai" VALUES(2,113,'2:113','และชาวยิวกล่าวว่า ชาวคริสต์นั้นมิได้ตั้งอยู่บนสิ่งใด และชาวคริสต์ก็กล่าว่า ชาวยิวก็มิได้ตั้งอยู่บนสิ่งใด ทั้ง ๆ ที่เขาเหล่านั้นอ่านคัมภีร์กันอยู่ ในทํานองเดียวกัน บรรดาผู้ที่ไม่รู้ก็ได้กล่าวเช่นเดียวกับคํากล่าวของพวกเขา ดังนั้นในวันกิยามะฮ์ อัลลอฮ์จะทรงตัดสินระหว่างพวกเขาในสิ่งที่พวกเขาขัดแย้งกัน');
INSERT INTO "king_fahad_thai" VALUES(2,114,'2:114','และใครเล่าจะเป็นผุ้อธรรมยิ่งไปกว่าผู้ที่หวงห้ามบรรดามัสยิดของออัลลอฮ์ ในการที่พระนามของพระองค์จะถูกกล่าวในมัสยิดเหล่านั้น และพยายามในการทําลายมัสยิดเหล่านั้นด้วย ชนเหล่านี้แหละไม่บังควรแก่พวกเขาที่จะเข้าไปในมัสยิดเหล่านั้น นอกจากในฐานะผู้เกรงกลัวเท่านั้น และเขาเหล่านั้นจะได้รับความอัปยศในโลกนี้ และในปรโลกนั้น พวกเขาจะได้รับการลงโทษอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(2,115,'2:115','และทิศตะวันออกและทิศตะวันตกนั้นเป็นสิทธิของอัลลอฮ์ ดังนั้นพวกเจ้าจะผินไปทางไหน ที่นั่นแหละคือพระพักตร์ของอัลลอฮ์ แม้จริงอัลลอฮ์คือผู้ทรงกว้างขวาง ผู้ทรงรอาบรู้');
INSERT INTO "king_fahad_thai" VALUES(2,116,'2:116','และพวกเขากล่าวว่า อัลลอฮ์ได้ทรงยึดเอาพระบุตรองค์หนึ่ง มหาบริสุทธิ์พระองค์ท่านหาใช่เช่นนั้นไม่ สิ่งที่อยู่ในบรรดาชั้นฟ้าและแผ่นดินนั้น เป็นของพระองค์ทั้งสิ้น โดยที่ทั้งหมดนั้นเป็นผู้ภักดี และนอบน้อมต่อพระองค์');
INSERT INTO "king_fahad_thai" VALUES(2,117,'2:117','พระองค์ผู้ทรงประดิษฐ์ชั้นฟ้า และแผ่นดิน และเมื่อพระองค์ทรงกําหนดสิ่งใดแล้วพระองค์ก็เพียงแต่ประกาศิตแก่สิ่งนั้นว่า จงเป็นแล้วสิ่งนั้นก็จะเป็นขึ้น');
INSERT INTO "king_fahad_thai" VALUES(2,118,'2:118','และบรรดาผู้ที่ไม่รู้กล่าวว่า ไฉนอัลลอฮ์จึงไม่ตรัสแก่พวกเรา หรือไม่มีสัญญาณหนึ่งมายังพวกเรา ในทํานองเดียวกัน บรรดาชนรุ่นก่อนพวกเขา ก็กล่าวเช่นเดียวกับคําพูดของพวกเขา โดยที่หัวใจของพวกเขาคล้ายคลึงกัน แท้จริงนั้น เราได้แจกแจงสัญญาณต่าง ๆ ไว้อย่างชัดเจนแล้ว แก่พวกที่ศรัทธามั่น');
INSERT INTO "king_fahad_thai" VALUES(2,119,'2:119','และแท้จริงเราได้ส่งเจ้ามาพร้อมด้วยความจริง ในฐานะผู้แจ้งข่าวดี และผุ้ตักเตือน และเจ้าจะไม่ถูกไต่สวนเกี่ยวกับชาวเปลงเพลิงที่ลุกโชติช่วง');
INSERT INTO "king_fahad_thai" VALUES(2,120,'2:120','และชาวยิวและชาวคริสต์นั่น จะไม่ยินดีแก่เจ้า (มุฮัมมัด) เป็นอันขาด จนกว่าเจ้าจะปฏิบัติตามศาสนาของพวกเขา จงกล่าวเถิด แท้จริงคําแนะนําของอัลลอฮ์เท่านั้น คือ คําแนะนํา แน่นอนถ้าเจ้าปฏิบัติตามความใคร่ของพวกเขา หลังจากที่มีความรู้มายังแล้ว ก็ย่อมไม่มีผู้คุ้มครองและผู้ช่วยเหลือใด ๆ สําหรับเจ้าให้พ้นจากการลงโทษของอัลลอฮ์ได้');
INSERT INTO "king_fahad_thai" VALUES(2,121,'2:121','บรรดาผุ้ที่เราได้ให้คัมภีร์แก่พวกเขาโดยที่พวกเขาอ่านคัมภีร์นั้นอย่างจริง ๆ ชนเหล่านี้แหหละคือ ผู้ที่ศรัทธาต่อคัมภีร์นั้นและผู้ใดปฏิเสธศรัทธาต่อคัมภีร์นั้นไซร้ แน่นอนชนเหล่านี้คือผู้ที่ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(2,122,'2:122','วงศ์วานอิสรออีลเอ๋ย ! จงรําลึกถึงความกรุณาของข้า ที่ข้าได้กรุณาต่อพวกเจ้า และแท้จริงข้าได้เทิดพวกเจ้าเหนือประชาชนทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(2,123,'2:123','และพวกเจ้าจงหวั่นเกรงวันหนึ่งซึ่งไม่มีชีวิตใดจะชดเชยสิ่งใดแทนอีกชีวิตหนึ่งได้ และค่าไถ่ถอนใด ๆ ก็หาได้รับประโยชน์แก่ชีวิตนั้นไม่ ตลอดจนเขาเหล่านั้นก็จะไม่ได้รับความช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(2,124,'2:124','และจงรําลึกถึง ขณะที่พระเจ้าของอิบรอฮีมได้ทดสอบเขา ด้วยพระบัญชาบางประการ แล้วเขาก็ได้สนองตามพระบัญชานั้นโดยครบถ้วน พระองค์ตรัสว่า แท้จริงข้าจะให้เจ้าเป็นผู้นํามนุษย์ชาติ เขากล่าวว่า และจากลูกหลานของข้าพระองค์ด้วย พระองค์ตรัสว่า สัญญาของข้านั้นจะไม่ได้แก่บรรดาผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(2,125,'2:125','และจงรําลึกถึงขณะที่เราได้ให้บ้านหลังนั้นเป็นที่กลับมาสําหรับมนุษย์และเป็นที่ปลอดภัยและพวกเจ้าจงยึดเอาเป็นที่ยืนของอิบรอฮีม เป็นที่ละหมาดเถิด และเราได้สั่งเสียแก่อิบรอฮีม และอิสมาอีลว่า เจ้าทั้งสองจงทําความสะอาดบ้านของข้าเพื่อบรรดาผู้ทําการเฎาะวาฟ และบรรดาผู้ทําการเอียะติกาฟ และบรรดาผู้ที่ทํารุกัวะและสุยูด');
INSERT INTO "king_fahad_thai" VALUES(2,126,'2:126','และจงรําลึกถึงขณะที่อิบรอฮีมได้วิงวอนว่า ข้าแต่พระเจ้าของข้าพระองค์โปรดทรงให้ที่นี่เป็นเมืองที่ปลอดภัย และโปรดประทานบรรดาผลไม้ให้เป็นปัจจัยยังชีพแก่ชาวเมืองนั้นด้วย คือผู้ที่ศรัทธาต่ออัลลอฮ์ และวันปรโลกจากพวกเขา พระองค์ตรัสว่า ผู้ใดที่ปฏิเสธการศรัทธา ข้าจะให้เขาได้รับความสําราญชั่วเวลาเพียงเล็กน้อยเท่านั้น ภายหลังข้าจะบีบบังคับให้เขาไปสู่การทรมานแห่งขุมนรก และเป็นจุดหมายปลายทางอันชั่วช้ายิ่ง');
INSERT INTO "king_fahad_thai" VALUES(2,127,'2:127','และจงรําลึกถึงขณะที่อิบรอฮีมและอิสมาอีล ได้ก่อฐานของบ้านหลังนั้น ให้สูงขึ้น (ทั้งสองได้กล่าววิงวอนว่า) ข้าแต่พระผู้เป็นเจ้าของพวกข้าพระองค์โปรดรับ(งาน) จากพวกข้าพระองค์ด้วยเถิด แท้จริงพระองค์นั้นทรงได้ยินและทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(2,128,'2:128','ข้าพระผู้เป็นเจ้าของข้าพระองค์ โปรดให้พระองค์ทั้งสองเป็นผู้นอบน้อมต่อพระองค์ และโปรดให้มีขึ้นจากลูกหลานของพวกพระองค์ ซึ่งประชาชนที่นอบน้อมต่อพระองค์ และโปรดแสดงแก่ข้าพระองค์ ซึ่งพิธีการทําฮัจญ์ของพวกข้าพระองค์ และโปรดอภัยโทษแก่พวกข้าพระองค์ด้วย แท้จริงพระองค์ทรงเป็นผู้อภัยโทษ ทรงเอ็นดูเมตตา');
INSERT INTO "king_fahad_thai" VALUES(2,129,'2:129','ข้าแต่พระผู้เป็นเจ้าของพวกข้าพระองค์โปรดส่งร่อซูลคนหนึ่งคนใดจากพวกเขาเองไปในหมู่พวกเขา ซึ่งเขาจะได้อ่านบรรดาโองการของพระองค์ให้พวกเขาฟัง และจะได้สอนคัมภีร์ และความมุ่งหมายแห่งบัญญัติให้พวกเขาทราบ และซักฟอกพวกเขาให้สะอาด แท้จริงพระองค์ทรงไว้ซึ่งเดชานุภาพและปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(2,130,'2:130','และใครเล่าที่จะไม่พึงปรารถนาในแนวทางของอิบรอฮีม นอกจากผู้ที่ทําให้ตัวเองโฉดเขลาเท่านั้น และแท้จริงนั้น เราได้คัดเลือกเขา(ให้เป็นนะบี และร่อซูล) ในโลกนี้ และแท้จริงในปรโลกนั้น เขาจะอยู่ในหมู่คนดีๆ อย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(2,131,'2:131','จงรําลึกถึงขณะที่พระเจ้าของเขาได้กล่าวแก่เขาว่า เจ้าจงสวามิภักดิ์เถิดเขากล่าวว่า ข้าพระองค์ได้สวามิภักดิ์แด่พระเจ้าแห่งสากลโลกแล้ว');
INSERT INTO "king_fahad_thai" VALUES(2,132,'2:132','และอิบรอฮีมได้สั่งเสียแก่ลูก ของเขาให้ปฏิบัติตามแนวทางนั้น และยะกูบก็สั่งเสียด้วยว่า โอ้ลูก ๆ ของฉัน แท้จริงอัลลอฮ์ได้ทรงเลือกศาสนาให้แก่พวกเจ้าแล้ว ดังนั้น พวกเจ้าจงอย่ายอมตามเป็นอันขาด นอกจากในขณะที่พวกเจ้าเป็นผู้สวามิภักดิ์(ต่ออัลลฮ์) เท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(2,133,'2:133','หรือว่าพวกเจ้าอยู่ด้วย เมื่อความตายได้เยี่ยมกรายยะอ์กูบ ขณะที่เขากล่าวแก่ลูก ๆ ของเขาว่า พวกเจ้าจะเคารพสักการะอะไร หลังจากฉัน? พวกเขากล่าวว่า พวกเราจะเคารพสักการะพระเจ้าของท่าน และพระเจ้าแห่งบรรดาบิดาของท่าน คือ อิบรอฮีม อิสมาอีล และอิสฮาก แต่เพียงองค์เดียวและพวกเราจะเป็นผู้สวามิภักดิ์ต่อพระองค์เท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(2,134,'2:134','นั่นคือ หมู่ชนที่ล่วงลับไปแล้ว สิ่งที่พวกเขาขวนขวายไว้ ก็ย่อมได้แก่พวกเขา และสิ่งที่พวกเจ้าขวนขวายไว้ก็ย่อมได้แก่พวกเจ้า และพวกเจ้าจะไม่ถูกไต่สวนถึงสิ่งที่พวกเขากระทํา');
INSERT INTO "king_fahad_thai" VALUES(2,135,'2:135','และพวกเขากล่าวว่า พวกท่านจงเป็นยิวเถิด หรือเป็นคริสต์เถิด พวกท่านก็จะได้รับคําแนะนําอันถูกต้อง จงกล่าวเถิด(มุฮัมมัด) หาใช่เช่นนั้นไม่ แนวทางของอิบรอฮีมผู้ใฝ่หาความจริงต่างหาก');
INSERT INTO "king_fahad_thai" VALUES(2,136,'2:136','พวกท่านจงกล่าวเถิด เราได้ศรัทธาต่ออัลลอฮ์ และสิ่งที่ถูกประทานลงมาแก่เรา และสิ่งที่ถูกประทานลงมาแก่อิบรอฮีม และอิสมาอีล และอิสฮาก และยะอ์กูบ และบรรดาวงศ์วานเหล่านั้น และสิ่งที่มูซา และอีซาได้รับ และสิ่งที่บรรดานะบีได้รับจากพระเจ้าของพวกเขา พวกเรามิได้แบ่งแยกระหว่างท่านหนึ่งท่านใดจากเขาเหล่านั้น และพวกเราจะเป็นผู้สวามิภักดิ์ต่อพระองค์เท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(2,137,'2:137','แล้วหากพวกเขาศรัทธาอย่างที่พวกเจ้าศรัทธาแล้ว แน่นอนพวกเขาก็ย่อมได้รับข้อแนะนําที่ถูกต้อง และหากพวกเขาผินหลังให้ แน่นอนพวกเขาย่อมอยู่ในความแตกแยกกัน แล้วอัลลอฮ์ก็จะทรงให้เจ้าพอเพียงแก่พวกเขา และพระองค์นั้นเป็นผู้ทรงไว้ซึ่งการได้ยิน ทรงไว้ซึ่งความรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(2,138,'2:138','การย้อมของอัลลอฮฺ และใครเล่าจะย้อมดียิ่งไปกว่าอัลลอฮฺ และพวกเราจะเป็นผู้เคารพอิบาดะฮฺต่อพระองค์');
INSERT INTO "king_fahad_thai" VALUES(2,139,'2:139','จงกล่าวเถิด(มุฮัมมัด) ว่าพวกท่านจะโต้แย้งกับเราในเรื่องของอัลลอฮ์กระนั้นหรือ? ทั้ง ๆ ที่พระองค์ทรงเป็นพระเจ้าของเราและพระเจ้าพวกท่าน และบรรดาการงานของเราก็ย่อมเป็นของเรา และบรรดาการงานของพวกท่านก็เป็นของพวกท่าน และพวกเรานั้น จะเป็นผู้มอบการอิบาดะฮ์ทั้งหลายให้แก่พระองค์เท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(2,140,'2:140','หรือว่าพวกท่านจะกล่าวว่า แท้จริงอิบรอฮีม และอิสมาอีล และอิสฮาก และยะกูบและบรรดาวงศ์วานเหล่านั้น เป็นพวกยิวหรือเป็นคริสต์จงกล่าวเถิด(มุฮัมมัด) ว่าพวกท่านรู้ดียิ่งกว่าอัลลอฮ์กระนั้นหรือ หรืออัลลอฮ์? แล้วผู้ใดจะอธรรมยิ่งไปกว่าผู้ที่ปิดบังหลักฐานจากอัลลอฮ์ ซึ่งมีอยู่ที่เขา และอัลลอฮ์นั้นจะไม่ทรงเผลอในสิ่งที่พวกเจ้ากระทํากันอยู่');
INSERT INTO "king_fahad_thai" VALUES(2,141,'2:141','นั่นคือ กลุ่มชนที่ล่วงลับไปแล้ว สิ่งที่พวกเขาได้ขวนขวายไว้ ก็ย่อมเป็นของพวกเขา และสิ่งที่พวกเจ้าขวนขวายไว้ก็ย่อมเป็นของพวกเจ้าและพวกเจ้าจะไม่ถูกไต่สวน ถึงสิ่งที่เขาเหล่านั้นปฏิบัติกัน');
INSERT INTO "king_fahad_thai" VALUES(2,142,'2:142','บรรดาผู้โฉดเขลา ในหมู่มนุษย์นั้นจะกล่าวว่า อะไรเล่าที่ทําให้พวกเขา หันออกไปจากกิบลัตของพวกเขา ที่พวกเขาเคยผินไป จขงกล่าวเถิด(มุอัมมัด) ว่าทิศตะวันออกและทิศตะวันตกนั้นเป็นสิทธิของอัลลอฮ์เท่านั้น พระองค์จะทรงแนะนําผู้ที่พระองค์ทรงประสงค์ไปสู่ทางอันเที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(2,143,'2:143','และในทํานองเดียวกัน เราได้ให้พวกเจ้าเป็นประชาชาติที่เป็นกลาง เพื่อพวกเจ้าจะได้เป็นสักขีพยานแก่มนุษย์ทั้งหลาย และร่อซูล ก็จะเป็นสักขีพยานแด่พวกเจ้า และเรามิได้ให้มีขึ้นซึ่งกิบลัตที่เจ้าเคยผินไป นอกจากเพื่อเราจะได้รู้ว่าใครบ้างที่จะปฏิบัติตามร่อซูล จากผู้ที่กําลังหันสันเท้าทั้งสองของเขากลับ และแท้จริงการเปลี่ยนแปลงกิบลัตนั้น เป็นเรื่องใหญ่ นอกจากแก่บรรดาผู้ที่อัลลอฮ์ได้ทรงแนะนําเท่านั้น และใช่ว่าอัลลอฮ์นั้นจะทําให้การศรัทธาของพวกเจ้าสูญไปก็หาไม่แท้จริงอัลลอฮ์เป็นผู้ทรงกรุณาปราณี ผู้ทรงเมตตาแก่มนุษย์เสมอ');
INSERT INTO "king_fahad_thai" VALUES(2,144,'2:144','แท้จริงเราเห็นใบหน้าของเจ้าแหงนไปในฟากฟ้าบ่อยครั้ง แน่นอนเราให้เจ้าผินไปยังทิศ ที่เจ้าพึงใจ ดังนั้นเจ้าจงผินใบหน้าของเจ้าไปทางมัสยิดิลฮะรอมเถิด และที่ใดก็ตามที่พวกเจ้าปรากฏอยู่ ก็จงผินใบหน้าของพวกเจ้าไปทางทิศนั้น และแท้จริงบรรดาผู้ที่ได้รับคัมภีร์ นั้นย่อมรู้ดีว่ามัน คือความจริงที่มาจากพระเจ้าของพวกเขา และอัลลอฮ์นั้นไม่ทรงเผลอในสิ่งที่พวกเขากระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(2,145,'2:145','และแน่นอน ถ้าหากเจ้าได้นําหลักฐานทุกอย่างมาแสดงแก่บรรดาผู้ได้รับคัมภีร์พวกเขาก็ไม่ตามกิบลัตของเจ้า และเจ้าก็มิใช่จะเป็นผู้ตามกิบลัตของพวกเขา และบางกลุ่มในพวกเขาเองก็มิใช่จะเป็นผู้ตามกิบลัตของอีกบางกลุ่ม และถ้าหากเจ้าไปปฏิบัติตามความใคร่ของพวกเขา หลังจากที่มีความรู้มายังเจ้าแล้ว แน่นอนทันใดนั้น เจ้าก็อยู่ในหมู่ผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(2,146,'2:146','บรรดาผู้ที่เราได้ให้ คัมภีร์แก่พวกเขานั้น พวกเขาย่อมรู้จักเขา ดีเหมือนกับที่พวกเขารู้จักลูก ๆ ของเขาเอง และแท้จริงกลุ่มหนึ่งจากพวกเขานั้นปิดบังความจริง ไว้ทั้ง ๆ ที่พวกเขารู้กันอยู่');
INSERT INTO "king_fahad_thai" VALUES(2,147,'2:147','ความจริงนั้นมาจากพระผู้เป็นเจ้าของเจ้า ดังนั้นเจ้าอย่าได้อยู่ในหมู่ผู้สงสัยเป็นอันขาด');
INSERT INTO "king_fahad_thai" VALUES(2,148,'2:148','และสําหรับแต่ละประชาชาตินั้น ต่างก็มีทิศทางหนึ่ง ซึ่งประชาชาตินั้นผินไปสู่ ดังนั้นพวกเจ้าจงแข่งขัยในความดีทั้งหลายเถิด ที่ใดก็ตามที่พวกเจ้าปรากฏอยู่อัลลอฮ์ก็จะทรงนําพวกเจ้ามาทั้งหมด แท้จริงอัลลอฮ์ทรงเดชานุภาพเหนือทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(2,149,'2:149','และจากที่ใดก็ตามที่เจ้าได้ออกไป ก็จงผินหน้าของเจ้าไปทางอัล-มัสยิดิลฮะรอม และแท้จริงนั้น มัน คือความจริงที่มาจากพระเจ้าของเจ้า และอัลลอฮ์นั้นไม่เป็นผู้ทรงเผลอในสิ่งที่พวกเจ้ากระทํากั้นอยู่');
INSERT INTO "king_fahad_thai" VALUES(2,150,'2:150','และจากที่ใดก็ตามที่เจ้าออกไป ก็จงผินหน้าของเจ้าไปทางอัล-มัสยิดิลฮะรอม และที่ใดก็ตามที่พวกเจ้าปรากฏอยู่ ก็จงผินหน้าของพวกเจ้าไปทางนั้น เพื่อว่าจะได้ไม่เป็นข้ออ้างใด ๆแก่หมู่ชนที่แย้งพวกเจ้าได้ นอกจากบรรดาผู้อธรรมในหมู่ของพวกเขาเท่านั้น ดังนั้นพวกเจ้าจงอย่ากลัวพวกเขา แต่จงกลัวข้าเถิด และเพื่อที่ข้าจะได้ให้ความกรุณาของข้าครบถ้วน แก่พวกเจ้า และเพื่อว่าพวกเจ้าจะได้รับแนวทางอันถูกต้อง');
INSERT INTO "king_fahad_thai" VALUES(2,151,'2:151','ดังที่เราได้ส่งร่อซูลผู้หนึ่ง จากพวกเจ้าเองมาในหมู่พวกเจ้า ซึ่งเขาจะอ่านบรรดาโองการของเราให้พวกเจ้าฟัง และจะทําให้พวกเจ้าสอาดบริสุทธิ์ และจะสอนคัมภีร์ และความรู้เกี่ยวกับข้อปฏิบัติให้แก่พวกเจ้า และจะสอนพวกเจ้าในสิ่งที่พวกเจ้าไม่เคยรู้มาก่อน');
INSERT INTO "king_fahad_thai" VALUES(2,152,'2:152','ดังนั้นพวกเจ้าจงรําลึกถึงข้าเถิด ข้าก็จะรําลึกถึงพวกเจ้า และจงขอบคุณข้าเถิด และจงอย่าเนรคุณต่อข้าเลย');
INSERT INTO "king_fahad_thai" VALUES(2,153,'2:153','บรรดาผู้ศรัทธาทั้งหลาย! จงอาศัยความอดทน และการละหมาดเถิด แท้จริงอัลลออ์นั้นทรงอยู่ร่วมกับผู้อดทนทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(2,154,'2:154','และพวกเจ้าอย่ากล่าวแก่ผู้ที่ถูกฆ่าในทางของอัลลอฮ์ว่า พวกเขาตาย มิได้พวกเขายังมีชีวิตอยู่ แต่ทว่าพวกเจ้าไม่รู้สึก');
INSERT INTO "king_fahad_thai" VALUES(2,155,'2:155','และแน่นอน เราจะทดลองพวกเจ้าด้วยสิ่งใดสิ่งหนึ่งจากความกลัว และความหิวและด้วยความสูญเสีย(อย่างใดอย่างหนึ่ง)จากทรัพย์สมบัติ ชีวิต และพืชผล และเจ้าจงแจ้งข่าวดีแก่บรรดาผู้อดทนเถิด');
INSERT INTO "king_fahad_thai" VALUES(2,156,'2:156','คือบรรดาผู้ที่เมื่อมีเคราะห์ร้ายมาประสบแก่พวกเขา พวกเขาก็กล่าวว่า แท้จริงพวกเราเป็นกรรมสิทธิ์ของอัลลอฮ์ และแท้จริงพวกเราจะกลับไปยังพระองค์');
INSERT INTO "king_fahad_thai" VALUES(2,157,'2:157','ชนเหล่านี้แหละพวกเขาจะได้รับคําชมเชย และการเอ็นดูเมตตาจากพระเจ้าของพวกเขาและชนเหล่านี้แหละคือผู้ที่ได้รับข้อแนะนําอันถูกต้อง');
INSERT INTO "king_fahad_thai" VALUES(2,158,'2:158','แท้จริงภูเขาเศะฟา และภูเขามัรวะฮ์นั้น เป็นส่วนหนึ่งจากบรรดาเครื่องหมายของอัลลอฮ์ ดังนั้นผู้ใดประกอบพิธีอัจญ์หรือ อุมเราะฮ์ ณ บัยตุลลอฮ์ก็ไม่มีบาปใด ๆ แก่เขาที่จะเดินวนเวียนไปมา ณ ภูเขาทั้งสองนั้น และผู้ใดประกอบความดีโดยสมัครใจแล้ว แน่นอนอัลลอฮ์นั้นคือผู้ทรงขอบใจ และผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(2,159,'2:159','แท้จริงบรรดาผู้ที่ปิดบังหลักฐานอันชัดเจน และข้อแนะนําอันถูกต้องที่เราได้ให้ลงมาหลังจากที่เราได้ชีแจงมันไว้แล้วในคัมภีร์สําหรับมนุษย์นั้น ชนเหล่านี้แหละอัลลอฮ์จะทรงขับไล่พวกเขาให้พ้นจากความเมตตาของพระองค์ และผุ้สาปแช่งทั้งหลายก็จะสาปแช่งพวกเขาด้วย');
INSERT INTO "king_fahad_thai" VALUES(2,160,'2:160','นอกจากผู้ที่สําหนึกผิดกลับเนื้อกลับตัว และปรับปรุงแก้ไข และชี้แจงสิ่งที่ปกปิดไว้ ชนเหล่านี้ข้าจะอภัยโทษให้แก่พวกเขา และข้าคือผู้อภัยโทษ และเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(2,161,'2:161','แท้จริงบรรดาผู้ปฏิเสธศรัทธาและได้สิ้นชีพลง ขณะที่พวกเขาเป็นผู้ปฏิเสธศรัทธาอยู่นั้น ชนเหล่านี้จะได้รับการขับไล่ให้พ้นจากความเมตตาของอัลลอฮฺ และจะได้รับการสาปแช่งจากมะลาอิกะฮ์ และมนุษย์ทั้งมวล');
INSERT INTO "king_fahad_thai" VALUES(2,162,'2:162','พวกเขาจะอยู่ในการขับไล่ให้พ้นจากความเมตตาของอัลลอฮฺ และการสาปแช่งจากมะลาอิกะฮ์ และมนุษย์ตลอดกาล โดยที่การลงโทษนั้นจะไม่ถูกผ่อนปรนแก่พวกเขา และทั้งพวกเขาก็จะไม่ถูกรั้งรอในการลงโทษ');
INSERT INTO "king_fahad_thai" VALUES(2,163,'2:163','และผู้ที่ควรแก่การเคารพ สักการะของพวกเจ้านั้น มีเพียงองค์เดียว ไม่มีผู้ควรแก่การเคารพสักการะใด ๆ นอกจากพระองค์ ผู้ทรงกรุณาปรานีผู้ทรงเมตตาเสมอเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(2,164,'2:164','แท้จริงในการสร้างบรรดาชั้นฟ้าและแผ่นดิน และสับเปลี่ยนกลางคืนและกลางวัน และเรือที่วิ่งอยู่ในทะเล พร้อมด้วยสิ่งที่อํานวยประโยชน์แก่มนุษย์ และน้ํา ที่อัลลอฮ์ได้ทรงให้หลั่งลงมาจากฟากฟ้า แล้วทรงให้แผ่นดินมีชีวิตชีวาขึ้น ด้วยน้ํานั้นหลังจากที่มันตายไปแล้ว และได้ทรงให้สัตว์แต่ละชนิด แพร่สะพัดไปในแผ่นดิน และในการให้ลมเปลี่ยนทิศทาง และให้เมฆซึ่งถูกกําหนดให้บริการ(แก่โลก) ผันแปรไประหว่างฟากฟ้าและแผ่นดินนั้น แน่นอนล้วนเป็นสัญญาณนานาประการแก่กลุ่มชนที่ใช้ปัญญา');
INSERT INTO "king_fahad_thai" VALUES(2,165,'2:165','และในหมู่มนุษย์นั้น มีผู้ที่ยึดถือบรรดาภาคี อื่นจากอัลลอฮ์ ซึ่งพวกเขารักภาคีเหล่านั้นเช่นเดียวกับรักอัลลอฮ์ แต่บรรดาผู้ศรัทธานั้นเป็นผู้ที่รักอัลลอฮ์มากยิ่งกว่า และหากบรรดาผู้อธรรมจะได้เห็น ขณะที่พวกเขาเห็นการลงโทษอยู่นั้น (แน่นอนพวกเขาจะต้องตระหนักดีว่า) แท้จริงอัลลอฮ์นั้นเป็นผู้ทรงลงโทษที่รุนแรง');
INSERT INTO "king_fahad_thai" VALUES(2,166,'2:166','(และ) ขณะที่บรรดาผู้ถูกตาม ได้ปลีกตัวออกจากบรรดาผู้ตาม และขณะที่พวกเขาเห็นการลงโทษ และขณะที่บรรดาสัมพันธภาพที่มีต่อกันได้ขาดสบั้นลง');
INSERT INTO "king_fahad_thai" VALUES(2,167,'2:167','และบรรดาผู้ที่ตามได้กล่าวว่า หากว่าเรามีโอกาสกลับไปอีกครั้งหนึ่ง เราก็จะปลีกตัวออกจากพวกเขาบ้าง เช่นเดียวกับที่พวกเขาได้ปลีกตัวออกจากพวกเรา ในทํานองเดียวนั้นแหละ อัลลอฮ์จะทรงให้พวกเขาเห็นงานต่างๆ ของพวกเขาเป็นที่น่าเสียใจแก่พวกเขา และทั้งพวกเขาจะไม่ได้ออกจากไฟนรกด้วย');
INSERT INTO "king_fahad_thai" VALUES(2,168,'2:168','มนุษย์เอ๋ย! จงบริโภคสิ่งอนุมัติที่ดี ๆ จากสิ่งที่อยู่ในแผ่นดิน และจงอย่าตามบรรดาก้าวเดินของชัยฏอน แท้จริงมันคือศัตรูที่ชัดแจ้งของพวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(2,169,'2:169','ที่จริงมันเพียงแต่จะใช้พวกเจ้าให้ประกอบสิ่งชั่ว และสิ่งลามกเท่านั้น และจะใช้พวกเจ้ากล่าวความเท็จให้แก่อัลลอฮ์ในสิ่งที่พวกเจ้าไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(2,170,'2:170','และเมื่อได้ถูกกล่าวแก่พวกเขาว่าจงปฏิบัติตามสิ่งที่อัลลอฮ์ได้ทรงประทานลงมาเถิดพวกเขาก็กล่าวว่า มิได้ เราจะแฏิบัติสิ่งที่เราได้พบบรรดาบรรพบุรุษของเราเคยปฏิบัติมาเท่านั้นและแม้ได้ปรากฏว่า บรรพบุรุษของพวกเขาไม่เข้าใจสิ่งใด และทั้งไม่ได้รับแนวทางอันถูกต้องก็ตามกระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(2,171,'2:171','และอุปมาบรรดาผู้ที่ปฏิเสธศรัทธานั้นดังผู้ที่ส่งเสียงตวาดสิ่ง ที่มันฟังไม่รู้เรื่อง นอกจากเสียงเรียกและเสียงตะโกนเท่านั้นพวกเขาคือคนหูหนวก เป็นใบ้ ตาบอด ดังนั้น พวกเขาจึงไม่เข้าใจ');
INSERT INTO "king_fahad_thai" VALUES(2,172,'2:172','บรรดาผู้ศรัทธาทั้งหลาย จงบริโภคสิ่งที่เราได้ให้เป็นปัจจัยยังชีพแก่พวกเจ้า จากสิ่งดี ๆ ทั้งหลาย และจงขอบคุณอัลลอฮ์เถิด หากเฉพาะพระองค์เท่านั้น ที่พวกเจ้าจักเป็นผู้เคารพสักการะ');
INSERT INTO "king_fahad_thai" VALUES(2,173,'2:173','ที่จริงที่พระองค์ทรงห้ามพวกเจ้านั้นเพียงแต่สัตว์ที่ตายเอง และเลือด และเนื้อสุกร และสัตว์ที่ถูกเปล่งเสียงที่มันเพื่ออื่นจากอัลลอฮ์ แล้วผู้ใดได้รับความคับขัน โดยมิใช่ผู้เสาะแสวงหา และมิใช่เป็นผู้ละเมิดขอบเขตแล้วไซร้ ก็ไม่มีบาปใด ๆ แก่เขา แท้จริงอัลลอฮ์เป็นผู้ทรงอภัยโทษผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(2,174,'2:174','แท้จริงบรรดาผู้ที่ปิดบังสิ่งซึ่งเป็นส่วนหนึ่งของคัมภีร์ ที่อัลลอฮ์ได้ทรงประทานลงมาและนําสิ่งนั้นไปแลกเปลี่ยนกับราคาอันเล็กน้อย ชนเหล่านั้นมิได้กินอะไรเข้าไปในท้องของพวกเขานอกจากไฟเท่านั้น และในวันกิยามะฮ์ อัลลอฮ์จะไม่ทรงพูดแก่พวกเขา และจะไม่ทรงทําให้พวกเขาบริสุทธิ์ และพวกเขาจะได้รับการลงโทษอันเจ็บแสบ');
INSERT INTO "king_fahad_thai" VALUES(2,175,'2:175','ชนเหล่านี้คือผู้ที่นําเอาแนวทางที่ถูกต้องไปแลกเปลี่ยนกับแนวทางที่หลงผิดและเอาการอภัยโทษไปแลกเปลี่ยนกับการลงโทษ พวกเขาช่างทนต่อไฟนรกเสียนี่กระไร.');
INSERT INTO "king_fahad_thai" VALUES(2,176,'2:176','นั่นก็เพราะว่า อัลลอฮ์ได้ทรงประทานคัมภีร์ลงมาพร้อมด้วยสัจธรรม และแท้จริงบรรดาผู้ที่ขัดแย้งกันในคัมภีร์นั้น ย่อมอยู่ในการแตกแยกที่ห่างไกล');
INSERT INTO "king_fahad_thai" VALUES(2,177,'2:177','หาใช่คุณธรรมไม่ การที่พวกเจ้าผินหน้าของพวกเจ้าไปทางทิศตะวันออกและทิศตะวันตกแต่ทว่าคุณธรรมนั้นคือผู้ที่ศรัทธาต่ออัลลอฮ์ และวันปรโลก และศรัทธาต่อมลาอิกะฮ์ ต่อบรรดาคัมภีร์และนะบีทั้งหลาย และบริจาคทรัพย์ทั้งๆ ที่มีความรักในทรัพย์นั้น แก่บรรดาญาติที่สนิทและบรรดาเด็กกําพร้า และแก่บรรดาผู้ยากจนและผู้ที่อยู่ในการเดินทาง และบรรดาผู้ที่มาขอและบริจาคในการไถ่ทาส และเขาได้ดํารงไว้ซึ่งการละหมาด และชําระซะกาต และ(คุณธรรมนั้น) คือบรรดาผู้ที่รักษาสัญญาของพวกเขาโดยครบถ้วน เมื่อพวกเขาได้สัญญาไว้ และบรรดาผู้ที่อดทนไนความทุกข์ยาก และในความเดือดร้อน แลละขณะต่อสู่ในสมรภูมิ ชนเหล่านี้แหละคือผู้ที่พูดจริง และชนเหล่านี้แหละคือผู้ที่มีความยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(2,178,'2:178','ผู้ศรัทธาทั้งหลาย! การประหารฆาตกรให้ตายตามในกรณีที่มีผู้ถูกฆ่าตายนั้น ได้ถูกกําหนดแก่พวกเจ้าแล้ว คือชายอิสระต่อชายอิสระ และทาสต่อทาส และหญิงต่อหญิง แล้วผู้ใดที่สิ่งหนึ่งจากพี่น้องของเขาถูกอภัยให้แก่เขาแล้ว ก็ให้ปฏิบัติไปตามนั้นโดยชอบ และให้ชําระแก่เขาโดยดี นั่นคือการผ่อนปรนจากพระเจ้าของพวกเจ้า และคือการเอ็นดูเมตตาด้วย แล้วผู้ใดละเมิดหลังจากนั้นเขาก็จะได้รับการลงโทษอันเจ็บแสบ');
INSERT INTO "king_fahad_thai" VALUES(2,179,'2:179','และในการประหารฆาตกรให้ตายตามนั้น คือการธํารงไว้ซึ่งชีวิตสําหรับพวกเจ้า โอ้ผู้มีสติปัญญาทั้งหลาย! เพื่อว่าพวกเจ้าจะได้ยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(2,180,'2:180','การทําพินัยกรรมให้แก่ผู้บังเกิดเกล้าทั้งสอง และบรรดาญาติที่ใกล้ชิดโดยชอบธรรมนั้นได้ถูกําหนดขึ้นแก่พวกเจ้าแล้ว เมื่อความตายได้มายังคนหนึ่งคนใดในพวกเจ้า หากเขาได้ทิ้งทรัพย์สมบัติไว้ ทั้งนี้เป็นหน้าที่แก่ผู้ยําเกรงทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(2,181,'2:181','แล้วผู้ใดเปลี่ยนแปลงพินัยกรรม หลังจากที่เขาได้ยินมันแล้ว โทษแห่งการเปลี่ยนแปลงพินัยกรรมนั้นก็ตกอยู่แก่บรรดาผู้เปลี่ยนแปลงพินัยกรรมนั้นเท่านั้น แท้จริงอัลลอฮ์ทรงได้ยิน ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(2,182,'2:182','แล้วผู้ใดเกรงว่า ผู้ทําพินัยกรรมมีความไม่เป็นธรรม (โดยไม่รู้) หรือกระทําความผิด(โดยเจตนา) แล้ว แล้วเขาได้ประนีประนอมในระหว่างพวกเขา ก็ไม่มีโทษใด ๆ แก่เขา แท้จริงอัลลอฮ์เป็นผู้ทรงอภัยผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(2,183,'2:183','บรรดาผู้ศรัทธาทั้งหลาย! การถือศีลอดนั้นได้ถูกําหนดแก่พวกเจ้าแล้ว เช่นเดียวกับที่ได้ถูกกําหนดแก่บรรดาผู้ก่อนหน้าพวกเจ้ามาแล้วเพื่อว่าพวกเจ้าจะได้ยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(2,184,'2:184','(คือถูกกําหนดให้ถือ) ในบรรดาวันที่ถูกนับไว้ แล้วผู้ใดในพวกเจ้าป่วยหรืออยู่ในการเดินทางก็ให้ถือใช้ในวันอื่น และหน้าที่ของบรรดาผู้ที่ถือศีลอดด้วยความลําบากยิ่ง (โดยที่เขาได้งดเว้นการถือ) นั้น คือการชดเชยอันได้แก่การให้อาหาร(มื้อหนึ่ง)แก่คนมิสกีนคนหนึ่ง (ต่อการงดเว้นจาการถือหนึ่งวัน) แต่ผู้กระทําความดีโดยสมัครใจ มันก็เป็นความดีแก่เขา และการที่พวกเจ้าจะถือศีลอดนั้น ย่อมเป็นสิ่งที่ดียิ่งกว่าพวกเจ้า หากพวกเจ้ารู้');
INSERT INTO "king_fahad_thai" VALUES(2,185,'2:185','เดือนรอมฏอนนั้น เป็นเดือนที่อัลกรุ-อานได้ถูกประทานลงมาในฐานะเป็นข้อแนะนําสําหรับมนุษย์ และเป็นหลักฐานอันชัดเจนเกี่ยวกับข้อแนะนํานั้น และเกี่ยวกับสิ่งที่จําแนกระหว่างความจริงกับความเท็จ ดังนั้นผู้ใดในหมูพวกเจ้าเข้าอยู่ในเดือนนั้นแล้ว ก็จงถือศีลอดในเดือนนั้น และผู้ใดป่วย หรืออยู่ในการเดินทาง ก็จงถือใช้ในวันอื่นแทน อัลลอฮ์ทรงประสงค์ให้มีความสะดวกแก่พวกเจ้า และไม่ทรงให้มีความลําบากแก่พวกเจ้าและเพื่อที่พวกเจ้าจะได้ให้ครบถ้วน ซึ่งจํานวนวัน(ของเดือนรอมฏอน) และเพื่อพวกเจ้าจะได้ให้ความเกรียงไกรแด่อัลลอฮ์ในสิ่งที่พระองค์ทรงแนะนําแก่พวกเจ้า และเพื่อพวกเจ้าจะขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(2,186,'2:186','และเมื่อบ่าวของข้าถามเจ้าถึงข้าแล้วก็ (จงตอบเถิดว่า) แท้จริงนั้นอยู่ใกล้ ข้าจะตอบรับคําวิงวอนของผู้ที่วิงวอน เมื่อเขาวิงวอนต่อข้าดังนั้น พวกเขาจงตอบรับข้าเถิด และศรัทธาต่อข้า เพื่อว่าพวกเขาจะได้อยู่ในทางที่ถูกต้อง');
INSERT INTO "king_fahad_thai" VALUES(2,187,'2:187','ได้เป็นที่อนุมัติแก่พวกเจ้าแล้ว ซึ่งการสมสู่กับบรรดาภรรยาของพวกเจ้าในค่ําคืนของการถือศีลอด นางทั้งหลายนั้นคือเครื่องนุ่งห่มของพวกเจ้า และพวกเจ้าก็คือเครื่องนุ่งห่มของพวกนาง อัลลอฮ์ทรงรู้ว่า พวกเจ้านั้นเคยทุจริตต่อตัวเอง แล้วพระองค์ก็ทรงยกโทษให้แก่พวกเจ้า และอภัยให้แก่พวกเจ้าแล้ว บัดนี้พวกเจ้าสมสู่กับพวกนางได้ และแสวงหาสิ่งที่อัลลอฮ์ได้ทรงกําหนดให้แก่พวกเจ้าเถิด และจงกิน และดื่ม จนกระทั่งเส้นขาว จะประจักษ์แก่พวกเจ้า จากเส้นดํา เนื่องจากแสงรุ่งอรุณ แล้วพวกเจ้าจงให้การถือศีลอดครบเต็มจนถึงพลบค่ํา และพวกเจ้าจงอย่าสมสู่กับพวกนางขณะที่พวกเจ้าเอียะติก๊าฟ อยู่ในมัสยิด นั่นคือบรรดาขอบเขตของอัลลอฮ์ดังนั้นพวกเจ้าจงอย่าเข้าใกล้ขอบเขตนั้น ในทํานองนั้นแหละอัลลอฮ์จะทรงแจกแจงบรรดาโองการของพระองค์แก่มนุษย์ เพื่อว่าพวกเขาจะได้ยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(2,188,'2:188','และพวกเจ้าจงอย่ากินทรัพย์ สมบัติของพวกเจ้า ระหว่างพวกเจ้าโดยมิชอบ และจงอย่าจ่ายมัน ให้แก่ผู้พิพากษา เพื่อที่พวกเจ้าจะได้กินส่วนหนึ่งจากทรัพย์สินสมบัติของผู้อื่น ด้วยการกระทําสิ่งที่เป็นบาป ทั้งๆ ที่พวกเจ้ารู้กันอยู่');
INSERT INTO "king_fahad_thai" VALUES(2,189,'2:189','เขาเหล่านั้นจะถามเจ้า เกี่ยวกับเดือนแรกขึ้น จงกล่าวเถิด มันคือกําหนดเวลาต่างๆ สําหรับมนุษย์ และสําหรับประกอบพิธีฮัจญ์และหาใช่เป็นคุณธรรมไม่ ในการที่พวกเจ้าเข้าบ้านทางหลังบ้าน แต่ทว่าคุณธรรมนั้นคือผู้ที่ยําเกรงต่างหาก และพวกเจ้าจงเข้าบ้านทางประตูบ้าน และพวกเจ้าจงยําเกรงอัลลอฮ์เถิด เพื่อว่าพวกเจ้าจะได้รับความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(2,190,'2:190','และพวกเจ้าจงต่อสู้ในทางของอัลลอฮ์ต่อบรรดาผู้ที่ทําร้ายพวกเจ้า และวงอย่ารุกรานแท้จริง อัลลอฮ์ไม่ทรงชอบบรรดาผู้รุกราน');
INSERT INTO "king_fahad_thai" VALUES(2,191,'2:191','และจงประหัตประหารพวกเขา ณ ที่ใดก็ตามที่พวกเจ้าพบพวกเขา และจงขับไล่พวกเขาออกจากที่ที่พวกเขาเคยขับไล่พวกเจ้าออก และการก่อความวุ่นวาย นั้น ร้ายแรงยิงกว่าการประหัตประหารเสียอีก และจงอย่าสู้รบกับพวกเขา ณ อัล-มัสยิดิลฮะรอม จนกว่าพวกเขาจะทําร้าย พวกเจ้าในที่นั้น หากพวกเขาทําร้ายพวกเจ้าแล้ว ก็จงประหัตประหารพวกเขาเสีย เช่นนั้นแหละคือการตอบแทนแก่ผู้ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(2,192,'2:192','แล้วถ้าหากพวกเขายุติ แน่นอ อัลลอฮ์ นั้นเป็นผู้ทรงอภัยโทษ ผุ้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(2,193,'2:193','และจงสู้รบกับพวกเขา จนกว่าการก่อความวุ่นวาย จะไม่ปรากฏขึ้น และจนกว่าการอิบาดะฮ์ ทั้งหลายจะเป็นสิทธิของอัลลอฮ์เท่านั้น แต่ถ้าพวกเขายุติ ก็ย่อมไม่มีการเป็นปฏิปักษ์ใด ๆ นอกจากแก่บรรดาผู้อธรรมเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(2,194,'2:194','เดือนที่ต้องห้ามนั้น ก็ด้วยเดือนที่ต้องห้าม และบรรดาสิ่งจําเป็นต้องเคารพนั้น ก็ย่อมมีการตอบโต้เยี่ยงเดียวกัน ดังนั้นผู้ใดละเมิดต่อพวกเจ้า ก็จงละเมิดต่อเขา เยี่ยงที่เขาละเมิดต่อพวกเข้า และพึงยําเกรงอัลลอฮ์เถิด และจงรู้ไว้ด้วยว่าแท้จริงอัลลอฮ์นั้นทรงอยู่กับบรรดาผู้ยําเกรงทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(2,195,'2:195','และพวกเจ้าจงบริจาคในทางของอัลลอฮ์และจงอย่าโยนตัวของพวกเจ้าสู่ความพินาศ และจงทําดีเถิด แท้จริงอัลลอฮ์นั้นทรงชอบผู้กระทําดีทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(2,196,'2:196','และพวกเจ้าจงให้สมบูรณ์ ซึ่งการทําฮัจญ์ และการทําอุมเราะฮ์เพื่ออัลลอฮ์เถิด แล้วถ้าพวกเจ้าถูกสกัดกั้น ก็ให้เชือดสัตว์พลีที่หาได้ง่าย และจงอย่าโกนศีรษะของพวกเจ้า จนกว่าสัตว์พลีนั้นจะถึงที่ของมัน แล้วผู้ใดในหมู่พวกเจ้าป่วยลง หรือที่เขามีสิ่งก่อความเดือดร้อนจากศรีษะของเขา ก็ให้มีการชดเชย อันได้แก่การถือศีลอด หรือการทําทาน หรือการเชือดสัตว์ ครั้นเมื่อพวกเจ้าปลอดภัยแล้ว ผู้ใดที่แสวงหาประโยชน์จนกระทั่งถึงฮัจญ์ด้วยการทําอุมเราะฮ์แล้ว ก็ให้เชือดสัตว์พลีที่หาได้ง่าย ผู้ใดที่หาไม่ได้ ก็ให้ถือศีลอดสามวันในระหว่างการทําฮัจญ์ และอีกเจ็ดวันเมื่อพวกเจ้ากลับบ้านนั่นคือครบสิบวัน ดังกล่าวนั้น สําหรับที่ครอบครัวของเขามิได้ประจําอยู่ที่อัล-มัสยิดิลฮะรอม และพวกเจ้าจงยําเกรงอัลลอฮ์เถิด และพึงรู้ด้วยว่า แท้จริงอัลลอฮ์นั้นเป็นผู้ทรงลงโทษที่รุนแรง');
INSERT INTO "king_fahad_thai" VALUES(2,197,'2:197','(เวลา) การทําฮัจญ์นั้นมีหลายเดือนอันเป็นที่ทราบกันอยู่แล้ว ดังนั้นผู้ใดที่ได้ให้การทําฮัจญ์จําเป็นแก่เขาในเดือนเหล่านั้น แล้ว ก็ต้องไม่มีการสมสู่ และไม่มีการละเมิด และไม่มีการวิวาทใด ๆ ใน (เวลา) การทําฮัจญ์ และความดีใด ๆ ที่พวกเจ้ากระทํานั้น อัลลอฮ์ทรงรู้ดี และพวกเจ้าจงเตรียมเสบียงเถิด แท้จริงเสบียงที่ดีที่สุดนั้นคือความยําเกรง และพวกเจ้าจงยําเกรงข้าเถิด โอ้ ผู้มีปัญญาทั้งหลาย !');
INSERT INTO "king_fahad_thai" VALUES(2,198,'2:198','ไม่มีโทษใด ๆ แก่พวกเจ้า การที่พวกเจ้าจะแสดวงหาความกรุณาอย่างหนึ่งอย่างใดจากพระเจ้าของพวกเจ้า ครั้นเมื่อพวกเจ้าได้หลั่งไหล กันออกจากอะเราะฟาดแล้ว ก็จงกล่าวรําลึกถึงอัลลอฮ์ ณ อัล-มัชระริลฮะรอม และจงกล่าวรําลึกถึงพระองค์ ดังที่พระองค์ได้ทรงแนะนําพวกเจ้าไว้ และแท้จริงก่อนหน้านั้น พวกเจ้าอยู่ในหมู่ผู้ที่หลงทาง');
INSERT INTO "king_fahad_thai" VALUES(2,199,'2:199','แล้วพวกเจ้าจงหลั่งไหลกันออกไปจากที่ที่ผุ้คนได้หลั่งไหลกันออกไป และจงขออภัยต่ออัลลอฮ์เถิด แท้จริงอัลลอฮ์นั้นเป็นผู้ทรงอภัยโทษ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(2,200,'2:200','ครั้นเมื่อพวกเจ้าประกอบพิธีฮัจญ์ของพวกเจ้าเสร็จแล้ว ก็จงกล่าวรําลึกถึงอัลลอฮ์ ดังที่พวกเจ้ากล่าวรําลึกถึงบรรพบุรุษของพวกเจ้า หรือกล่าวรําลึกให้มากยิ่งกว่า ในหมู่มนุษย์นั้นมีผู้กล่าวว่า โอ้พระเจ้าของเรา โปรดประทานให้แก่พวกเราในโลกนี้เถิด และเขาจะไม่ได้รับส่วนดีใด ๆ ในปรโลก');
INSERT INTO "king_fahad_thai" VALUES(2,201,'2:201','และในหมู่พวกเขานั้น มีผู้ที่กล่าว่า โอ้พระเจ้าของเรา โปรดประทานให้แก่พวกเรา ซึ่งดีงามในโลกนี้ และสิ่งดีงามในปรโลกและโปรดคุ้มครองพวกเราให้พ้นจากลงโทษแห่งไฟนรด้วยเถิด');
INSERT INTO "king_fahad_thai" VALUES(2,202,'2:202','ชนเหล่านี้แหละ พวกเขาจะได้รับส่วนดี จากสิ่งที่พวกเขาได้แสวงหาไว้ และอัลลอฮ์นั้นเป็นผู้ทรงรวดเร็วในการชําระสอบสวน');
INSERT INTO "king_fahad_thai" VALUES(2,203,'2:203','และพวกเจ้าจงกล่าวรําลึกถึงอัลลอฮ์ ในบรรดาวันที่ถูกนับไว้ แล้วผู้ใดรีบกลับในสองวัน ก็ไม่มีโทษใด ๆ แก่เขาและผู้ใดรั้งรอไปอีก ก็ไม่มีโทษใด ๆ แก่เขา (ทั้งนี้) สําหรับผุ้ที่มีความยําเกรง และจงยําเกรงอัลลอฮ์เถิด และพึงรู้ด้วยว่า พวกเจ้านั้นจะถูกนําไปชุมนุมยังพระองค์');
INSERT INTO "king_fahad_thai" VALUES(2,204,'2:204','และในหมู่มนุษย์นั้น มีผู้ที่คําพูดของเขา ทําให้เจ้าพึงพอใจในชีวิตความเป็นอยู่แห่งโลกนี้และจะอ้างอัลลอฮ์เป็นพยานซึ่งสิ่งที่อยู่ในหัวใจของเขา และขณะเดียวกันก็เป็นผู้โต้เถียงที่ฉกาจฉกรรจ์ยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(2,205,'2:205','และเมื่อเขาให้กลังไปแล้ว เขาก็เพียรพยายามในแผ่นดิน เพื่อก่อความเสียหายในนั้นและทําลายพืชผล และเผ่าพันธุ์ และอัลลอฮ์นั้นไม่ทรงชอบการก่อความเสียหาย');
INSERT INTO "king_fahad_thai" VALUES(2,206,'2:206','และเมื่อถูกกล่าวแก่เขาว่า จงยําเกรงอัลลอฮ์เถิด ความหยิ่งในเกียรติก็ยึดเขาไว้ให้กระทําบาปต่อไป สิ่งที่พอเพียงแก่เขานั้นก็คือ ญะฮันนัมและแน่นอนเป็นสิ่งที่หลับนอนอันเลวร้ายยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(2,207,'2:207','และในหมู่มนุษย์นั้นมีผู้ที่ขายตัวของเขา ทั้งนี้เพื่อแสวงหาความพอพระทัยของอัลลอฮ์และอัลลอฮ์นั้นเป็นผู้ทรงปรานีแก่ปวงบ่าวทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(2,208,'2:208','บรรดาผู้ศรัทธาทั้งหลาย! จงเข้าอยู่ในความสันติ โดยทั่วทั้งหมด และจงอย่าทําตามบรรดาก้าวเดินของชัยฏอน แท้จริงมันคือศัตรูที่ชัดแจ้งของพวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(2,209,'2:209','แต่ถ้าพวกเจ้าหันเหออกไป หลังจากที่ได้มีบรรดาหลักฐานอันชัดเจนมายังพวกเจ้าแล้วก็พึงรู้เถิดว่า แท้จริงอัลลอฮ์นั้นทรงเดชานุภาพทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(2,210,'2:210','และพวกเขามิได้คอยอะไร นอกจากการที่อัลลอฮ์และมลาอิกะอ์ของพระองค์จะมายังพวกเขา ในร่มเงาจากเมฆ และเรื่องนั้นได้ถูกชี้ขาดไว้แล้ว และยังอัลลอฮ์นั้นเรื่องราวทั้งหลายจะถูกนํากลับไป');
INSERT INTO "king_fahad_thai" VALUES(2,211,'2:211','เจ้าจงถามวงศ์วานอิสรออีลดูเถิดว่าสัญญาณอันชัดเจนกี่มากน้อยแล้ว ที่เราได้นํามายังพวกเขา และผู้ใดเปลี่ยนแปลงความกรุณาของอัลลอฮ์ หลังจากที่มันได้มายังเขาแล้ว แน่นอนอัลลอฮ์นั้นเป็นผู้ทรงลงโทษที่รุนแรง');
INSERT INTO "king_fahad_thai" VALUES(2,212,'2:212','ชีวิตความเป็นอยู่แห่งโลกนี้นั้นได้ถูกประดับให้สวยงามแก่ผู้ปฏิเสธศรัทธาทั้งหลายและพวกเขายังเย้ยหยันบรรดาผู้ที่ศรัทะาด้วย แต่บรรดาผู้ยําเกรงนั้น เหนือกว่าพวกเขาในวันกิยามะฮ์และอัลลอฮ์จะทรงประทานปัจจัยยังชีพแก่ผู้ที่พระองค์ทรงประสงค์ โดยปราศจากการคํานวณนับ');
INSERT INTO "king_fahad_thai" VALUES(2,213,'2:213','มนุษย์นั้นเคยเป็นประชาชาติเดียวกัน ภายหลังอัลลอฮ์ได้ส่งบรรดานะบีมาในฐานะผู้แจ้งข่าวดี และผู้ตักเตือน และได้ทรงประทานคัมภีร์อันกอปรไปด้วยความจริงลงมากับพวกเขาด้วยเพื่อว่าคัมภีร์นั้นจะได้ตัดสินระหว่างมนุษย์ในสิ่งที่พวกเขาขัดแย้งกัน และไม่มีใครที่ขัดแย้งในคัมภีร์นั้น นอกจากบรรดาผู้ที่ได้รับคัมภีร์นั้นมา หลังจากที่บรรดาหลักฐานอันชัดเแจ้งได้มายังพวกเขาเหล่านั้น ทั้งนี้เพราะความอิจฉาริษยาในระหว่างพวกเขา แล้วอัลลอฮ์ก็ทรงแนะนําแก่บรรดาผู้ศรัทธา ซึ่งความจริงที่พวกเขาขัดแย้งกันด้วยอนุมัติของพระองค์ และอัลลอฮ์นั้นทรงแนะนําผู้ที่พระองค์ทรงประสงค์ไปสู่ทางอันเที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(2,214,'2:214','หรือพวกเจ้าคิดว่า พวกเจ้าจะได้เข้าสวรรค์ โดยเยี่ยงอย่างของผู้ล่วงลับไปก่อนพวกเจ้า ยังมิได้มายังพวกเจ้าเลย ซึ่งบรรดาความลําบากและความเดือดร้อนได้ประสบแก่พวกเขา และพวกเขาได้รับความหวั่นไหว จนกระทั่งร่อซูลและบรรดาผู้ศรัทธา ซึ่งอยู่กับเขา กล่าวขึ้นว่า เมื่อไรเล่าการช่วยเหลือของอัลลอฮ์? พึงรู้เภิดว่าแท้จริงการช่วยเหลือของอัลลอฮ์ใกล้อยู่แล้ว');
INSERT INTO "king_fahad_thai" VALUES(2,215,'2:215','พวกเขาจะถามเจ้า (มุฮัมมัด) ว่า พวกเขาจะบริจาคสิ่งใดบ้าง? จงกล่าวเถิดว่า คือทรัพย์สินใด ๆ ก็ตามที่พวกท่านบริจาคไปก็จงให้แก่ผู้บังเกิดเกล้าทั้งสอง และบรรดาญาติที่ใกล้ชิด และแก่บรรดาเด็กกําพร้า และบรรดาคนยากจน และผู้ที่อยู่ในการเดินทาง และก็ความดีใด ๆ ที่พวกท่านกระทําอยู่นั้น แท้จริงอัลลอฮ์ทรงรู้ดี');
INSERT INTO "king_fahad_thai" VALUES(2,216,'2:216','การสู้รบนั้นได้ถูกกําหนดแก่พวกเจ้าแล้ว ทั้งๆ ที่มันเป็นที่รังเกียจแก่พวกเจ้า และอาจเป็นไปได้ว่า การที่พวกเจ้าเกลียดสิ่งหนึ่งทั้งๆ ที่สิ่งนั้นเป็นสิ่งดีแก่พวกเจ้าและก็อาจเป็นไปได้ว่าการที่พวกเจ้าชอบสิ่งหนึ่ง ทั้ง ๆ ที่สิ่งนั้นเป็นสิ่งเลวร้ายแก่พวกเจ้าและอัลลอฮ์นั้นทรงรู้ดี แต่พวกเจ้าไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(2,217,'2:217','พวกเขาจะถามเจ้าเกี่ยวกับเดือนต้องห้าม ซึ่งการสู้รบในเดือนนั้น จงกล่าวเถิดว่า การสู้รบในเดือนนั้นเป็นสิ่งใหญ่โตและการขัดขวางให้ออกจากทางของอัลลอฮ์ และการปฏิเสธการศรัทธาต่อพระองค์ และการกีดกัน อัล-มัสยิดิลฮะรอมตลอดจนการขับไล่ชาวอัล-มัสยิดิลฮะรอมออกไปนั้นเป็นสิ่งใหญ่โตยิ่งกว่า ณ ที่อัลลอฮ์ และการฟิตนะฮ์ นั้นใหญ่โตยิ่งกว่าการฆ่า และพวกเขาจะยังคงต่อสู้พวกเจ้าต่อไป จนกว่าพวกเขาจะทําให้พวกเจ้ากลับออกไปจากศาสนาของพวกเจ้า หากพวกเขาสามารถ และผู้ใดในหมู่พวกเจ้ากลับออกไปจากศาสนาของเขา แล้วเขาตายลง ขณะที่เขาเป็นผู้ปฏิเสธศรัทธาแล้วไซร้ ชนเหล่านี้แหละบรรดาการงานของพสกเขาไร้ผล ทั้งในโลกนี้และปรโลก และชนเหล่านี้แหละคือชาวนรก ซึ่งพวกเขาจะอยู่ในนรกนั้นตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(2,218,'2:218','แท้จริงบรรดาผู้ศรัทธา และบรรดาผู้ที่อพยพ และได้เสียสละต่อสู้ในทางของอัลลอฮ์นั้น ชนเหล่านี้แหละที่หวังในความเมตตาของอัลลอฮ์ และอัลลอฮ์นั้นเป็นผู้ทรงอภัยโทษ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(2,219,'2:219','พวกเขาจะถามเจ้าเกี่ยวกับน้ําเมา และการพนัน จงกล่าวเถิดว่า ในทั้งสองนั้นมีโทษมากและมีคุณหลายอย่างแก่มนุษย์ แต่โทษของมันทั้งสองนั้นมากกว่าคุณของมัน และพวกเขาจะถามเจ้าว่า พวกเขาจะบริจาคสิ่งใด ? จงกล่าวเถิดว่า สิ่งที่เหลือจากการใช้จ่าย ในทํานองนั้นแหละ อัลลอฮ์จะทรงแจกแจงโองการทั้งหลายแก่พวกเจ้าหวังว่าพวกเจ้าจะได้ใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(2,220,'2:220','ทั้งในโลกนี้และปรโลก และพวกเขาจะถามเจ้าเกี่ยวกับบรรดาเด็กกําพร้า จงกล่าวเถิดว่า การแก้ไขปรับปรุงใด ๆ ให้แก่พวกเขานั้น เป็นสิ่งที่ดียิ่ง และถ้าหากพวกเจ้าจะร่วมอยู่กับพวกเขาก็คือพี่น้องของพวกเจ้า และอัลลอฮ์นั้นทรงรู้ดีถึงผู้ที่ก่อความเสียหาย จากผู้ที่ปรับปรุงแก้ไข และหากอัลลอฮ์ทรงประสงค์แล้ว แน่นอนก็ทรงให้พวกเจ้าลําบากไปแล้ว แท้จริงอัลลอฮ์เป็นผู้ทรงเดชานุภาพ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(2,221,'2:221','และพวกเจ้าจงอย่าแต่งงานกับหญิงมุชริก จนกว่านางจะศรัทธา และทาสหญิงที่เป็นผู้ศรัทธานั้นดียิ่งกว่าหญิงที่เป็นมุชริก แม้ว่านางได้ทําให้พวกเจ้าพึงใจก็ตาม และพวกเจ้าจงอย่าให้แต่งงานกับบรรดาชายมุชริก จนกว่าพวกเขาจะศรัทธา และทาสชายที่เป็นผู้ศรัทธานั้นดีกว่าชายมุชริก และแม้ว่าเขาได้ทําให้พวกเจ้าพึงใจก็ตาม ชนเหล่านี้แหละจะชักชวนไปสู่ไฟนรกและอัลลอฮ์นั้นทรงเชิญชวนไปสู่สวรรค์ และไปสู่การอภัยโทษ ด้วยอนุมัติของพระองค์ และพระองค์จะทรงแจกแจงบรรดาโองการของพระองค์แก่มนุษย์ เพื่อว่าพวกเขาจะได้รําลึกกันได้');
INSERT INTO "king_fahad_thai" VALUES(2,222,'2:222','และพวกเขาจะถามเจ้าเกี่ยวกับประจําเดือน จงกล่าวเถิดว่า มันเป็นสิ่งที่ให้โทษ ดังนั้นพวกเจ้าจงห่างไกลหญิง มนขณะมีประจําเดือน และจงอย่าเข้าใกล้นาง จนกว่านางจะสะอาด ครั้นเมื่อนางได้ชําระร่างกายสะอาดแล้ว ก็จงมาหานางตามที่อัลลอฮ์ทรงใช้พวกท่าน แท้จริงอัลลอฮ์ทรงชอบบรรดาผู้สํานึกผิด กลับเนื้อกลับตัว และทรงชอบบรรดาผู้ที่ทําตนให้สะอาด');
INSERT INTO "king_fahad_thai" VALUES(2,223,'2:223','บรรดาผู้หญิงของพวกเจ้านั้น คือแหล่งเพาะปลูกของพวกเจ้า ดังนั้นพวกเจ้าจงมายังแหล่งเพาะปลูกของพวกเจ้า ตามแต่พวกเจ้าประสงค์และจงประกอบล่วงหน้าไว้สําหรับตัวของพวกเจ้า และพึงยําเกรงอัลลอฮ์เถิด และพึงรู้ด้วยว่าแท้จริงพวกเจ้านั้นจะเป็นผู้พบกับพระองค์ และเจ้า จงแจ้งข่าวดีแก่บรรดาผู้ศรัทธาทั้งหลายเถิด');
INSERT INTO "king_fahad_thai" VALUES(2,224,'2:224','และพวกเจ้าจงอย่าให้อัลลอฮ์เป็นอุปสรรคขัดขวาง เนื่องจากการสาบานของพวกเจ้าในการที่พวกเจ้าจะกระทําความดีและที่จะมีความยําเกรง และในการที่พวกเจ้าจะประนีประนอมระหว่างผู้คนแบละอัลลอฮ์นั้นเป็นผู้ทรงได้ยิน ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(2,225,'2:225','อัลลอฮ์จะไม่ทรงเอาโทษแก่พวกเจ้าด้วยคําพูดพล่อยๆ ในการสาบานของพวกเจ้า แต่ทว่าพระองค์จะทรงเอาโทษแก่พวกเจ้า ด้วยการสาบานที่หัวใจของพวกเจ้ามุ่งหมายด้วย และอัลลอฮ์นั้นเป็นผู้ทรงอภัยโทษผู้ทรงหนักแน่น');
INSERT INTO "king_fahad_thai" VALUES(2,226,'2:226','สําหรับบรรดาผู้ที่สาบานว่า จะไม่สมสู่ภรรยาของเขานั้น ให้มีการรอคอยไว้สี่เดือน แล้วถ้าหากเขากลับคืนดี แน่นอนอัลลอฮ์นั้น เป็นผู้ทรงอภัยโทษ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(2,227,'2:227','และถ้าพวกเขาปลงใจ ซึ่งการหย่าแล้วไซร้ แน่นอนอัลลอฮ์ผู้ทรงได้ยิน ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(2,228,'2:228','และบรรดาหญิงที่ถูกหย่าร้าง พวกนางจะต้องรอคอยตัวของตนเองสามกุรุอ์ และไม่อนุมัติให้แก่พวกนาง ในการที่พวกนางจะปกปิดสิ่งที่อัลลอฮ์ได้ทรงให้บังเกิดขึ้นในมดลูกของพวกนาง หากพวกนางศรัทธาต่ออัลลอฮ์และวันปรโลกและบรรดาสามีของพวกนางนั้นเป็นผู้มีสิทธิกว่าในการให้พวกนางกลับมาในกรณีดังกล่าว หากพวกเขาปรารถนาประนีประนอมและพวกนางนั้นจะได้นรับเช่นเดียวกับสิ่งที่เป็นหน้าที่ของพวกนางจะต้องปฏิบัติโดยชอบธรรม และสําหรับบรรดาชายนั้นมีฐานะเหนือพวกนางขั้นหนึ่งและอัลลอฮ์นั้นเป็นผู้ทรงเดชานุภาพ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(2,229,'2:229','การหย่านั้นมีสองครั้ง แล้วให้มีการยับยั้งไว้โดยชอบธรรม หรือไม่ก็ปล่อยไปพร้อมด้วยการทําความดี และไม่อนุญาตแก่พวกเจ้าในการที่พวกเจ้าจะเอาสิ่งหนึ่งสิ่งใดจากสิ่งที่พวกเจ้าได้ให้แก่พวกนาง (มะฮัร) นอกจากทั้งทั้งสองเกรงว่าจะไม่สามารถดํารงไว้ซึ่งขอบเขตของอัลลอฮ์ได้เท่านั้น ถ้าหากพวกเจ้าเกรงว่า เขาทั้งสองจะไม่ดํารงไว้ซึ่งขอบเขตของอัลลอฮ์แล้วไซร้ ก็ไม่มีบาปใด ๆ แก่เขาทั้งสองในสิ่งที่นางใช้มันไถ่ตัวนาง เหล่านั้นแหละคือขอบเขตของอัลลอฮ์ พวกเจ้าจงอย่าละเมิดมัน และผู้ใดละเมิดขอบเขตของอัลลอฮ์แล้ว ชนเหล่านั้นแหละคือผู้ที่อธรรมแก่ตัวเอง');
INSERT INTO "king_fahad_thai" VALUES(2,230,'2:230','ถ้าหากเขาได้หย่านางอีก นางก็ไม่เป็นที่อนุมัติแก่เขาหลังจากนั้น จนกว่าจะแต่งงานกับสามีอื่นจากเขา แล้วหากสามีนั้นหย่านาง ก็ไม่มีบาปใด ๆ แก่ทั้งสอง ที่จะคืนดีกันใหม่ หากเขาทั้งสองคิดว่า จะดํารงไว้ซึ่งขอบเขตของอัลลอฮ์ได้และนั่นแหละคือขอบเขตของอัลลอฮ์ ซึ่งพระองค์ทรงแจกแจงมันอย่างแจ่มแจ้งแก่กลุ่มชนที่รู้ดี');
INSERT INTO "king_fahad_thai" VALUES(2,231,'2:231','และเมื่อพวกเจ้าหย่าบรรดาหญิง แล้วพวกนางถึงกําหนดเวลา ของพวกนางแล้ว ก็จงยับยั้งนางไว้โดยชอบธรรม หรือไม่ก็จงปล่อยนางไปโดยชอบธรรม และพวกเจ้าจงอย่ายับยั้งพวกนางไว้โดยมุ่งก่อความเดือดร้อน เพื่อพวกเจ้าจะได้ข่มเหงรังแก และผู้ใดกระทําเช่นนั้น แน่นอนเขาก็ข่มเหงตนเอง และจงอย่าถือเอาโองการของอัลลอฮ์เป็นที่เย้ยหยัน และพึงรําลึกถึงความเมตตาของอัลลอฮ์ที่มีแก่พวกเจ้า และสิ่งที่พระองค์ได้ทรงประทานลงมาแก่พวกเจ้าอันได้แก่คัมภีร์ และบทบัญญัติ(ที่มีอยู่ในคัมภีร์นั้น) ซึ่งพระองค์จะทรงใช้คัมภีร์นั้นแนะนําตักเตือนพวกเจ้า และพวกเจ้าพึงยําเกรงอัลลอฮ์เถิด และจงรู้ด้วยเถิดว่า แท้จริงอัลลอฮ์นั้นทรงรอบรู้ในทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(2,232,'2:232','และเมื่อพวกเจ้าหย่าบรรดาหญิง แล้วนางเหล่านั้นได้ถึงกําหนดเวลาของพวกนางแล้วก็จงอย่าขัดขวางพวกนาง ในการที่พวกนางจะแต่งกับบรรดาคู่ครองของพวกนาง เมื่อพวกเขาต่างพอใจกันระหว่างพวกเขาโดยชอบธรรม นั่นแหละคือ สิ่งที่จะถูกนํามาแนะนําตักเตือนแก่ผู้ที่ศรัทธาต่ออัลลอฮ์ และวันปรโลก นั่นแหละคือสิ่งที่บริสุทธิ์กว่า และสะอาดกว่า สําหรับพวกเจ้าและอัลลอฮ์นั้นทรงรู้ แต่พวกเจ้าไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(2,233,'2:233','และมารดาทั้งหลายนั้น จะให้นมแก่ลูก ๆ ของนางภายในสองปีเต็ม สําหรับผู้ที่ต้องการ จะให้ครบถ้วนในการให้นม และหน้าที่ของพ่อเด็กนั้นคือปัจจัยยังชีพของพวกนางและเครื่องนุ่งห่มของพวกนางโดยชอบธรรม ไม่มีชีวิตใดจะถูกบังคับนอกจากเท่าที่ชีวิตนั้นมีกําลังความสามารถเท่านั้น มารดาก็จงอย่าได้ก่อความเดือดร้อน(ให้แก่สามี) เนื่องด้วยลูกของนาง และพ่อเด็กก็จงอย่าได้ก่อความเดือดร้อน(ให้แก่ภรรยา) เนื่องด้วยลูกของเขา และหน้าที่ของทายาทผู้รับมรดกก็เช่นเดียวกัน แต่ถ้าทั้งสองต้องการหย่านม อันเกิดจากความพอใจ และการปรึกษาหารือกันจากทั้งสองคนแล้ว ก็ไม่มีบาปใดๆ แก่เขาทั้งสอง และหากพวกเจ้าประสงค์ที่จะให้มีแม่นมขึ้นแก่ลูก ๆ ของพวกเจ้าแล้วก็ย่อมไม่มีบาปใด ๆ แก่พวกเจ้า เมื่อพวกเจ้าได้มอบสิ่งที่พวกเจ้าให้(แก่นางเป็นค่าตอบแทน) โดยชอบธรรม และจงยําเกรงอัลลอฮ์เถิด และพึงรู้ด้วยว่า แท้จริงอัลลอฮ์นั้น ทรงเห็นในสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(2,234,'2:234','และบรรดาผู้ที่ถึงแก่ชีวิตลงในหมู่พวกเจ้า และทิ้งคู่ครองไว้นั้น พวกนางจะต้องรอคอยตัวของพวกนางเอง สี่เดือนกับสิบวัน ครั้นเมื่อพวกนางครบกําหนดเวลาของพวกนางแล้ว ก็ไม่มีบาปใด ๆ แก่พวกเจ้า ในสิ่งที่พวกนางได้กระทําไปในส่วนตัวของพวกนางโดยชอบธรรม และอัลลอฮ์นั้นทรงรอบรู้อย่างละเอียด ในสิ่งที่พวกเจ้ากระทํานั้น');
INSERT INTO "king_fahad_thai" VALUES(2,235,'2:235','และไม่มีบาปใดๆ แก่พวกเจ้า ในสิ่งที่พวกเจ้ากล่าวเป็นนัยในการขอหญิง และสิ่งที่พวกเจ้าเก็บงําไว้ในใจของพวกเจ้า อัลลอฮ์ทรงรู้ว่าพวกเจ้าจะบอกกล่าวแก่นางให้ทราบแต่ทว่าพวกเจ้าอย่าได้สัญญาแก่นางเป็นการลับ นอกจากพวกเจ้าจะกล่าวถ้อยคําอันดีเท่านั้น และจงอย่าปลงใจซึ่งการทําพิธีแต่งงาน จนกว่าเวลาที่ถูกกําหนดไว้จะบรรลุถึงความสิ้นสุดของมัน และพึงรู้เถิดว่าแท้จริงอัลลอฮ์ทรงรู้สิ่งที่อยู่ในจิตใจของพวกเจ้า พวกเจ้าจงสังวรณ์พระองค์ไว้เถิด และพึงรู้ไว้เถิดว่า อัลลอฮ์นั้นเป็นผู้ทรงอภัยโทษผู้ทรงหนักแน่น');
INSERT INTO "king_fahad_thai" VALUES(2,236,'2:236','ไม่มีบาปใด ๆ แก่พวกเจ้า ถ้าหากพวกเจ้าหย่าหญิง โดยที่พวกเจ้ายังมิได้แตะต้องพวกนาง หรือยังมิได้กําหนดมะฮัรใดๆ แก่พวกนาง และจงให้นางได้รับสิ่งที่อํานวยประโยชน์แก่พวกนาง โดยที่หน้าที่ของผู้มั่งมีนั้นคือตามกําลังความสามารถของเขา และหน้าที่ของผู้ยากจนนั้นคือตามกําลังความสามารถของเขา เป็นการให้ประโยชน์โดยชอบธรรม เป็นสิทธิเหนือผู้กระทําดีทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(2,237,'2:237','และถ้าหากพวกเจ้าหย่าพวกนางก่อนที่พวกเจ้าจะแตะต้องพวกนาง โดยที่พวกเจ้าได้กําหนดมะฮัรแก่นางแล้ว ก็จงให้แก่นางครึ่งหนึ่งของสิ่งที่พวกเจ้ากําหนดไว้ นอกจากว่าพวกนางจะยกให้หรือผู้ที่การตกลงแต่งงานอยู่ในมือของเขา จะยกให้และการที่พวกเจ้าจะยกให้นั้น เป็นสิ่งที่ใกล้แก่ความยําเกรงมากกว่า และพวกเจ้าอย่าลืมการทําคุณในระหว่างพวกเจ้าแท้จริงอัลลอฮ์เป็นผู้ทรงเห็นในสิ่งที่พวกเจ้ากระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(2,238,'2:238','พวกเจ้าจงรักษาบรรดาละหมาดไว้ และละหมาดที่อยู่กึ่งกลาง และจงยืนละหมาดเพื่ออัลลอฮ์โดยนอบน้อม');
INSERT INTO "king_fahad_thai" VALUES(2,239,'2:239','ถ้าพวกเจ้ากลัว ก็จงละหมาดพลางเดินหรือขี่ ครั้นเมื่อพวกเจ้าปลอดภัยแล้ว ก็จงกล่าวรําลึกถึงอัลลอฮ์ ดังที่พระองค์ได้ทรงสอนพวกเจ้าซึ่งสิ่งที่พวกเจ้ามิเคยรู้มาก่อน');
INSERT INTO "king_fahad_thai" VALUES(2,240,'2:240','และบรรดาผู้ที่จะถึงแก่ชีวิตลงในหมู่พวกเจ้า และจะทิ้งคู่ครองไว้นั้น จงให้มีพินัยกรรมไว้แก่คู่ครองของพวกนาง ซึ่งสิ่งอํานวยประโยชน์(แก่นาง) ถึงหนึ่งปี โดยไม่มีการขับไล่ใด ๆ แก่พวกเจ้าในสิ่งที่พวกนางได้กระทําในส่วนตัวของพวกนางจากสิ่งที่ชอบธรรม และอัลลอฮ์นั้นเป็นผู้ทรงเดชานุภาพ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(2,241,'2:241','และสําหรับบรรดาหญิงที่ถูกหย่านั้นจะได้รับสิ่งอํานวยประโยชน์โดยชอบธรรม เป็นสิทธิเหนือผู้ยําเกรงทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(2,242,'2:242','ในทํานองนั้นแหละ อัลลอฮ์จะทรงแจกแจงบรรดาโองการของพระองค์ให้พวกเจ้าทราบเพื่อว่าพวกเจ้าจะได้เข้าใจ');
INSERT INTO "king_fahad_thai" VALUES(2,243,'2:243','เจ้ามิได้มองดู บรรดาผู้ที่ออกจากบ้านของพวกเขาดอกหรือ ทั้งๆ ที่พวกเขาเป็นพัน ๆ คน ทั้งนี้เพราะกลัวความตาย และอัลลอฮ์ก็ได้ประกาศิตแก่พวกเขาว่า พวกเจ้าจงตายเสียเถิด ภายหลังพระองค์ทรงให้พวกเขามีชีวิตขึ้นใหม่ แท้จริงอัลลอฮ์นั้นเป็นผู้มีบุญคุณแก่มนุษย์ แต่ทว่ามนุษย์ส่วนมากไม่ขอบพระคุณ');
INSERT INTO "king_fahad_thai" VALUES(2,244,'2:244','และพวกเจ้าจงต่อสู้ในทางของอัลลอฮ์เถิด และพึงรู้ด้วยว่า แท้จริงอัลลอฮ์นั้น เป็นผู้ทรงได้ยิน ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(2,245,'2:245','มีใครบ้างไหมที่จะให้อัลลอฮ์ทรงยืมหนี้ที่ดี แล้วพระองค์จะทรงเพิ่มพูนหนี้ นั้น ให้แก่เขามากมายหลายเท่าและอัลลอฮ์นั้นทรงกําไว้และทรงแบออก และยังพระองค์เท่านั้นพวกเจ้าจะถูกนํากลับไป');
INSERT INTO "king_fahad_thai" VALUES(2,246,'2:246','เจ้า (มุฮัมมัด) มิได้มองดู พวกหัวหน้าในหมู่วงศ์วานอิสรออีล หลังจากมูซาดอกหรือ ? ขณะที่พวกเขาได้กล่าวแก่นะบีของพวกเขาคนหนึ่ง ว่า โปรดส่งกษัตริย์องค์หนึ่งมาให้แก่พวกเราเถิด พวกเราจะได้ต่อสู้ในทางของอัลลอฮ์ เขากล่าวว่า อาจเป็นไปได้ไหมว่า พวกท่านนั้น ถ้าการสู้รบได้ถูกกําหนดแก่พวกท่านแล้ว พวกท่านจะไม่ต่อสู้พวกเขากล่าว่า และได้มีสิ่งใดเกิดขึ้นแก่พวกเรากระนั้นหรือ ที่พวกเราจะไม่ต่อสู้ในทางของอัลลอฮ์ทั้ง ๆที่พวกเรา และลูก ๆ ของของพวกเราถูกขับไล่ออกจากหมู่บ้านของเรา ครั้นเมื่อการสู้รบได้ถูกกําหนดขึ้นแก่พวกเขาแล้ว พวกเขาก็ผินหลังให้ นอกจากส่วนน้อยในหมู่พวกเขาเท่านั้น และอัลลอฮ์เป็นผู้ทรงรู้ดีต่อบรรดาผู้อธรรมเหล่านั้น');
INSERT INTO "king_fahad_thai" VALUES(2,247,'2:247','และนะบีของพวกเขาก็กล่าวแก่พวกเขาว่า แท้จริงอัลลอฮ์ได้ทรงส่งฎอลูตมาเป็นกษัตริย์แก่พวกท่านแล้ว พวกเขากล่าวว่าเขาจะมีอํานาจเหนือพวกเราได้อย่างไร ? ทั้ง ๆ ที่พวกเราเป็นผู้สมควรต่ออํานาจนั้นยิ่งกว่าเขา และทั้งเขาก็มิได้รับทรัพย์สมบัติอันกว้างขวางเขา (นะบีของเขา)กล่าวว่า ฮัลลอฮ์ได้ทรงคัดเลือกเขาให้มีอํานาจเหนือพวกท่านแล้ว และได้ทรงเพิ่มให้แก่เขาอีก ซึ่งความกว้างขวางในความรู้ และความสูงใหญ่ในร่างกายและอัลลอฮ์นั้นจะทรงประท่านอํานาจของพระองค์ให้แก่ผุ้ที่พระองค์ทรงประสงค์ และอัลลอฮ์เป็นผู้ทรงกว้างขวาง ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(2,248,'2:248','และนะบีของพวกเขาได้กล่าวแก่พวกเขาว่า แท้จริงสัญญาณแห่งอํานาจของเขานั้น คือการที่หีบใบนั้น จะมายังพวกท่าน ในหีบนั้นมีความสงบ จากพระเจ้าของพวกท่าน และมีส่วนที่เหลือจากสิ่งที่วงศ์วานของมูซา และวงศ์วานของฮารูนได้ละทิ้งไว้ โดยที่มะลาอิกะฮ์จะแบกมันมา แท้จริงในเรื่องนั้นมีสัญญาณหนึ่งแน่นอนสําหรับพวกท่านหากพวกท่านเป็นผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(2,249,'2:249','ครั้นเมื่อฎอลูตได้นํากําลังทหารออกไป เขาได้กล่าวว่า แท้จริงอัลลอฮ์จะเป็นผุ้ทดสอบพวกท่าน ด้วยแม่น้ําสายหนึ่ง ผู้ใดดื่มน้ําจากแม่น้ํานั้น เขาก็จะไม่ใช่พวกของฉัน และผู้ใดไม่ชิมมัน แท้จริงเขาเป็นพวกของฉัน นอกจากผู้วักน้ําด้วยมือของเขาอุ้งมือหนึ่งเท่านั้น แต่แล้วพวกเขาก็ดื่มน้ํากันจากแม่น้ํานั้น นอกจากส่วนน้อยในหมู่พวกเขาเท่านั้น ครั้นเมื่อฎอลูตและบรรดาผู้ศรัทธาที่ร่วมอยู่กับเขาได้ข้ามแม่น้ํานั้นไป พวกเขาก็กล่าวว่า วันนี้พวกเราไม่มีกําลังใด ๆ จะต่อสู้กับญาลูต และไพร่พลของเขาได้ บรรดาผู้ที่เชื่อแน่ว่าพวกเขาจะพบกับอัลลอฮ์ได้กล่าวว่า กี่มากน้อยแล้ว พวกน้อยเอาชนะพวกมากได้ ด้วยอนุมัติของอัลลอฮ์ และอัลลอฮ์นั้นทรงอยู่กับผู้อดทนทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(2,250,'2:250','และเมื่อพวกเขาได้ออกไปประจัญหน้ากับญาลูต และไร่พลของเขาแล้ว พวกเขาก็กล่าวว่า โอ้พระเจ้าของพวกข้าพระองค์โปรดทรงประทานความอดทนให้แก่พวกข้าพระองค์ด้วยเถิด และโปรดทรงให้มั่นคงซึ่งเท้าของข้าพระองค์ และโปรดทรงช่วยพวกข้าพระองค์ให้ชนะเหนือพวกปฏิเสธศรัทธาทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(2,251,'2:251','แล้วพวกเขาก็ยังความปราชัยให้แก่พวกนั้น ด้วยอนุมัติของอัลลอฮ์ และดาวูดได้ฆ่าญาลูตและอัลลอฮ์ได้ทรงประทานอํานาจ และความรู้แก่เขา และทรงสอนเขาจากสิ่งที่พระองค์ทรงประสงค์ และหากว่าอัลลอฮ์ไม่ทรงป้องกันมนุษย์ ซึ่งบางส่วนของพวกเขาด้วยอีกบางส่วนแล้วไซร้ แน่นอนแผ่นดินก็ย่อมเสื่อมเสียไปแล้ว แต่ทว่าอัลลอฮ์นั้น ทรงเป็นผู้มีพระคุณแก่โลกทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(2,252,'2:252','นั้นแหละคือบรรดาโองการของอัลลอฮ์ซึ่งเราอ่านโองการเหล่านั้นให้เจ้าฟังด้วยความจริง และแท้จริงเจ้านั้นเป็นผู้หนึ่งในบรรดาร่อซูลทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(2,253,'2:253','บรรดาร่อซูลเหล่านั้น เราได้ให้บางคนในหมู่พวกเขาดีเด่นกว่าอีกบางคน ในหมู่พวกเขานั้น มีผู้ทีอัลลอฮ์ตรัสด้วย และได้ทรงยกบางคนในหมู่พวกเขาขึ้นหลายขั้น และเราได้ให้บรรดาหลักฐานอันชัดแจ้งแก่อีซาบุตรของมัรยัม และเราได้สนับสนุนเขาด้วยวิญญาณแห่งความบริสุทธิ์ และหากอัลลอฮ์ทรงประสงค์แล้ว บรรดาชนหลังจากพวกเขา ก็คงไม่ฆ่าฟันกัน หลังจากได้มีบรรดาหลักฐานอันชัดเจนมายังพวกเขา แต่ทว่าพวกเขาขัดแย้งกัน แล้วในหมู่พวกเขานั้นมีผู้ที่ศรัทธา และในหมู่พวกเขานั้น มีผู้ที่ปฏิเสธศรัทธา และหากว่าอัลลอฮ์ทรงประสงค์แล้ว พวกเขาก็คงไม่ฆ่าฟันกัน แต่ทว่าอัลลอฮ์นั้นทรงกระทําตามที่พระรองค์ทรงประสงค์');
INSERT INTO "king_fahad_thai" VALUES(2,254,'2:254','บรรดาผู้ศรัทธาทั้งหลาย จงบริจาคส่วนหนึ่ง จากสิ่งที่เราได้ให้เป็นปัจจัยยังชีพแก่พวกเจ้าก่อนจากที่วันหนึ่งจะมา ซึ่งในวันนั้นไม่มีการซื้อขาย และไม่มีการเป็นมิตร และไม่มีชะฟาอะฮ์ และบรรดาผู้ปฏิเสธศรัทธานั้น คือ พวกที่อธรรม(แก่ตัวเอง)');
INSERT INTO "king_fahad_thai" VALUES(2,255,'2:255','อัลลอฮ์นั้น คือ ไม่มีผู้ที่เป็นที่เคารพสักการะใด ๆ นอกจากพระองค์เท่านั้น ผู้ทรงมีชีวิต ผุ้ทรงบริหารกิจการทั้งหลาย โดยที่การง่วงนอน และการนอนหลับใด ๆ จะไม่เอาพระองค์ สิ่งที่อยู่ในบรรดาชั้นฟ้าและสิ่งที่อยู่ในแผ่นดินนั้นเป็นของพระองค์ ใครเล่าคือผู้ที่จะขอความช่วยเหลือให้แก่ผู้อื่น ณ ที่พระองค์ได้ นอกจากด้วยอนุมัติของพระองค์เท่านั้น พระองค์ทรงรู้สิ่งที่อยู่เบื้องหน้าของพวกเขา และสิ่งที่อยู่เบื้องหลังของพวกเขา และพวกเขาจะไม่ล้อมสิ่งใด จากความรู้ของพระองค์ไว้ได้ นอกจากสิ่งที่พระองค์ประสงค์เท่านั้น เก้าอี้พระองค์นั้นกว้างขวางทั่วชั้นฟ้าทั้งหลายและแผ่นดิน และการรักษามันทั้งสองก็ไม่เป็นภาระหนักแก่พระองค์ และพระองค์นั้นคือผู้ทรงสูงส่ง ผู้ทรงยิงใหญ่');
INSERT INTO "king_fahad_thai" VALUES(2,256,'2:256','ไม่มีการบังคับใด (ให้นับถือ) ในศาสนา อิสลาม แน่นอน ความถูกต้องนั้นได้เป็นที่กระจ่างแจ้งแล้วจากความผิด ดังนั้นผู้ใดปฏิเสธศรัทธาต่อ อัฎ-ฎอฆูต และศรัทธาต่ออัลลอฮ์แล้ว แน่นอนเขาได้ยึดห่วงอันมั่นคงไว้แล้ว โดยไม่มีการขาดใด ๆ เกิดขึ้นแก่มัน และอัลลอฮ์นั้นเป็นผู้ทรงได้ยิน ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(2,257,'2:257','และอัลลอฮ์นั้นคือผู้ทรงช่วยเหลือบรรดาผู้ที่ศรัทธา โดยทรงนําพวกเขาออกจากบรรดาความมืดสู่แสงสว่าง และบรรดาผู้ปฏิเสธศรัทธานั้น บรรดาผู้ช่วยเหลือของพวกเขาก็คือ อัฎ-ฎอฆูต โดยที่พวกมันจะนําพวกเขาออกจากแสงสว่างไปสู่ความมืด ชนเหล่านี้แหละคือชาวนรก โดยที่พวกเขาจะอยู่ในนรกนั้นตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(2,258,'2:258','เจ้า (มุฮัมมัด) มิได้มองดูผู้ที่โต้แย้ง อิบรอฮีมในเรื่องพระเจ้าของเขาดอกหรือ ? เนื่องจากอัลลอฮ์ได้ทรงประทานอํานาจแก่เขา ขณะที่อิบรอฮีมได้กล่าวว่า พระเจ้าของฉันนั้น คือ ผู้ที่ทรงให้เป็นและทรงให้ตายได้ เขากล่าวว่า ข้าก็ให้เป็นและให้ตายได้ อิบรอฮีมกล่าวว่า แท้จริงอัลลอฮ์นั้นทรงนําดวงอาทิตย์มาจากทิศตะวันออก ท่านจงนํามันมาจากทิศตะวันตกเถิด แล้วผู้ที่ปฏิเสธศรัทธานั้น ก็ได้รับความงงงวย และอัลลอฮ์นั้นจะไม่ทรงประทานแนวทางอันถูกต้องแก่ผู้อธรรมทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(2,259,'2:259','หรือเช่นผู้ที่ได้ผ่านเมืองหนึ่ง (บัยตุลมักดิส) โดยที่มันพังทับลงบนหลังคาของมัน เขาได้กล่าวว่า อัลลอฮ์จะทรงให้เมืองนี้มีชีวิตขึ้นได้อย่างไร หลังจากที่มันได้ตายพินาศไปแล้ว และอัลลอฮ์ก็ทรงให้เขาตายเป็นเวลาร้อยปี ภายหลังพระองค์ได้ทรงให้เขาฟื้นคืนชีพ พระองค์รงกล่าวว่า เจ้าพักอยู่นานเท่าใด? เขากล่าวว่า ข้าพระองค์พักอยู่วันหนึ่งหรือบางส่วนของวันเท่านั้น พระองค์ทรงกล่าวว่ามิได้ เจ้าพักอยู่นานถึงร้อยปี เจ้าจงมองดูอาหารของเจ้า และเครื่องดื่มของเจ้า มันยังไม่บูดเลย และจงมองดูลาของเจ้าซิ และเพื่อเราจะให้เจ้าเป็นสัญญาณหนึ่งสําหรับมนุษย์ และจงมองบรรดากระดูก เหล่านั้น ดูว่าเรากําลังยกมันไว้ ณ ที่ของมัน และประกอบมันขึ้น แล้วให้มีเนื้อหุ้มห่อมันไว้อย่างไร? ครั้นเมื่อสิ่งเหล่านั้นได้ประจักษ์แก่เขา เขาก็กล่าวว่า ข้าพระองค์รู้ว่า แท้จริงอัลลอฮ์นั้นทรงเดชานุภาพเหนือทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(2,260,'2:260','และจงรําลึกถึงขณะที่ที่อิบรอฮีม กล่าวว่า โอ้พระผู้เป็นเจ้าจองข้าพระองค์ โปรดได้ทรงให้ข้าพระองค์เห็นด้วยเถิดว่า พระองค์จะทรงให้บรรดาผู้ที่ตายมีชีวิตขึ้นอย่างไร ? พระองค์ตรัสว่า เจ้ามิได้เชื่อดอกหรือ ? อิบรอฮีมกล่าวว่า หามิได้ แต่ทว่าเพื่อหัวใจของข้าพระองค์จะได้สงบ พระองค์ตรัสว่าเจ้าจงเอานกมาสี่ตัว แล้วจงเลี้ยงมันให้ค้นแก่เจ้า และตัดมันออกเป็นท่อน ๆ ภายหลังเจ้าจงวางไว้บนภูเขาทุกลูก ซึ่งส่วนหนึ่งจากนกเหล่านั้น แล้วจงเรียกมัน มันจะมายังเจ้าโดยรีบเร่ง และพึงรุ้ไว้เถิดว่า แท้จริงอัลลอฮ์นั้น เป็นผู้ทรงเดชานุภาพผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(2,261,'2:261','อุปมาบรรดาผู้ที่บริจาคทรัพย์ของพวกเขาในทางของอัลลอฮ์นั้น ดังอุปมัยเมล็ดพืชเมล็ดหนึ่งที่งอกขึ้นเป็นเจ็ดรวง ซึ่งในแต่ละรวงนั้นมีร้องเมล็ด และอัลลอฮ์นั้นจะทรงเพิ่มพูนแก่ผู้ที่พระองค์ทรงประสงค์อีก และอัลลอฮ์เป็นผู้ทรงกว้างขวาง ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(2,262,'2:262','บรรดาผู้บริจาคทรัพย์ของพวกเขาในทางของอัลลอฮ์ แล้วพวกเขามิให้ติดตามสิ่งที่พวกเขาบริจาคไป ซึ่งการลําเลิกและการก่อความเดือด ร้อนใด ๆ นั้น พวกเขาจะได้รับรางวัลของพวกเขา ณ พระผู้เป็นเจ้าของพวกเขา และไม่มีความกลัวใด ๆ แก่พวกเขา และทั้งพวกเขาก็จะไม่เสียใจ');
INSERT INTO "king_fahad_thai" VALUES(2,263,'2:263','คําพูดที่ดี และการให้อภัยนั้น ดียิ่งกว่าทานที่มีการก่อความเดือดร้อนติดตามทานนั้น และอัลลอฮ์นั้นเป็นผู้ทรงมั่งมี ผู้ทรงหนักแน่นเสมอ');
INSERT INTO "king_fahad_thai" VALUES(2,264,'2:264','บรรดาผู้ศรัทธาทั้งหลาย ! จงอย่าให้บรรดาทานของพวกเจ้าไร้ผล ด้วยการลําเลิก และการก่อความเดือดร้อน เช่นผู้ที่บริจาคทรัพย์ของเขา เพื่ออวดอ้างผู้คน และทั้งเขาก็ไม่ศรัทธาต่ออัลลอฮ์ และวันปรโลก ดังนั้นอุปมาเขาผู้นั้น ดังอุปมัยหินเกลี้ยงที่มีฝุ่นจับอยู่บนมัน แล้วมีฝนหนัก ประสบแก่มัน แล้วได้ทิ้งมันไว้ในสภาพเกลี้ยง พวกเขาไม่สามารถที่จะได้สิ่งหนึ่งสิ่งใดจากสิ่งที่ขวนขวายไว้ และอัลลอฮ์นั้จะไม่ทรงแนะนําแก่กลุ่มชนที่ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(2,265,'2:265','และอุปมาบรรดาผู้ที่บริจาคทรัพย์ของพวกเขา เพื่อแสวงหาความพึงใจของอัลลอฮ์ และเพื่อให้เกิดความมั่นคงแก่ตัวของพวกเขาเอง นั้นดังอุปมัยสวนแห่งหนึ่ง ณ ที่เนินสูง ซึ่งมีฝนหนัก ประสบแก่มัน แล้วมันก็นํามาซึ่งผลของมันสองเท่า แต่ถ้ามิได้มีฝนหนักประสบแก่มัน ก็มีฝนปรอยๆ และอัลลอฮ์นั้นทรงเห็นในสิ่งที่พวกเจ้ากระทํากันอยู่');
INSERT INTO "king_fahad_thai" VALUES(2,266,'2:266','มีคนใดในพวกเจ้าชอบบ้างไหมที่เขาจะมีสวน อินทผาลัม และองุ่น ซึ่งเบื้องล่างของสวนนั้นมีลําน้ําหลายสายไหลผ่าน ผลไม่ทั้งหมดในสวนนั้นเป็นของเขา และความชราได้ประสบแก่เขา และเขาก็มีลูก ๆ ที่ยังอ่อนแออยู่ แต่แล้วได้มีลมพายุประสบแก่สวนนั้น ซึ่งในลมพายุนั้นมีไฟด้วย แล้วมันเผามอดไหม้ไป ในทํานองนั้นแหละ อัลลอฮ์จึงทรงแจกแจงโองการทั้งหลายให้พวกเจ้าทราบ เพื่อว่าพวกเจ้าจะได้ใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(2,267,'2:267','บรรดาผู้ศรัทธาทั้งหลาย ! จงบริจาคส่วนหนึ่งจากบรรดาสิ่งดี ๆ ของสิ่งที่พวกเจ้าได้แสวงหาไว้ และจากสิ่งที่เราได้ให้ออกมาจากดิน สําหรับพวกเจ้า และพวกเจ้าอย่ามุ่งเอาสิ่งที่เลวจากมันมาบริจาค ทั้ง ๆ ที่พวกเจ้าเองก็มิใช่จะเป็นผุ้รับมันไว้ นอกจากว่าพวกเจ้าจะหลับตาในการรับมันเท่านั้น และพึงรู้เถิดว่า แท้จริงอัลลอฮ์นั้น เป็นผู้ทรงมั่งมี ผู้ทรงได้รับการสรรเสริญ');
INSERT INTO "king_fahad_thai" VALUES(2,268,'2:268','ชับฎอนนั้น มันจะขู่พวกเจ้าให้กลัวความยากจน และจะใช้พวกเจ้าให้กระทําความชั่ว และอัลลอฮ์นั้น ทรงสัญญาแก่พวกเจ้าไว้ ซึ่งการอภัยโทษ และความกรุณาจากพระองค์ และอัลลอฮ์ นั้นเป็นผู้ทรงกว้างขวาง ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(2,269,'2:269','พระองค์จะทรงประทานความรู้ให้แก่ผู้ที่พรองค์ทรงประสงค์ และผู้ใดที่ได้รับความรู้ แน่นอนเขาก็ได้รับความความดีอันมากมาย และไม่มีใครจะรําลึก นอกจากบรรดาผุ้ที่มีสติปัญญาเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(2,270,'2:270','และสิ่งที่บริจาคใดก็ดี ที่พวกเจ้าบริจาคไป หรือสิ่งบนบานใดก็ดี ที่พวกเจ้าได้บนบานไว้นั้น แท้จริงอัลลอฮ์ทรงรู้มันดีและสําหรับผู้อธรรมทั้งหลายนั้น ย่อมไม่มีผู้ช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(2,271,'2:271','หากพวกเจ้าเปิดเผยสิ่งที่ให้เป็นทาน มันก็เป็นสิ่งที่ดีอยู่ และถ้าหากพวกเจ้าปกปิดมัน และให้มันแก่บรรดาผุ้ยากจนแล้วมันก็เป็นสิ่งที่ดีแก่พวกเจ้ายิ่งกว่า และพระองค์จะทรงลบล้างออกจากพวกเจ้า ซึ่งบางส่วนจากาบรรดาความผิดของพวกเจ้า และอัลลอฮ์นั้นทรงรอบรู้อย่างถี่ถ้วนในสิ่งที่พวกเจ้ากระทํากันอยู่');
INSERT INTO "king_fahad_thai" VALUES(2,272,'2:272','หาใช่เป็นหน้าที่ของเจ้าไม่ ซึ่งการแนะนําพวกเขา (ให้เกิดความศรัทธา) แต่ทว่าอัลลอฮ์ต่างหากที่จะแนะนําใครก็ได้ที่พระองค์ทรงประสงค์(ให้เขาศรัทธา) สิ่งดีใด ๆ ที่พวกเจ้าบริจาคไปก็ย่อมแก่ตัวของพวกเจ้าเอง และพวกเจ้าจะไม่บริจาคสิ่งใด นอกจากเพื่อแสวงหาความโปรดปรานของอัลลอฮ์เท่านั้น และสิ่งดีใด ๆ ที่พวกเจ้าบริจาคไป มันก็จะถูกตอบแทนโดยครบถ้วนแก่พวกเจ้าและพวกเจ้าจะไม่ถูกอยุติธรรม');
INSERT INTO "king_fahad_thai" VALUES(2,273,'2:273','(คือให้บริจาคทาน) แก่บรรดาผู้ที่ยากจนที่ถูกจํากัดตัวให้อยู่ในทางของอัลลอฮ์ โดยที่พวกเขาสามารถจะเดินทางไปในดินแดนอื่นๆ ได้(เพื่อประกอบอาชีพ) ผู้ที่ไม่รู้คิดว่าพวกเขาเป็นผู้มั่งมี อันเนื่องจากความสงบเสงี่ยมเจียมตัว โดยที่เจ้าจะรู้จักเขาได้ด้วยเครื่องหมายของพวกเขา พวกเขาจะไม่ขอจากผู้คนในสภาพเซ้าซี้ และสิ่งดีใด ๆ ที่พวกเจ้าบริจาคไปนั้น แท้จริงอัลลอฮ์ทรงรู้ดีในสิ่งนั้น');
INSERT INTO "king_fahad_thai" VALUES(2,274,'2:274','บรรดาผู้ที่บริจาคทรัพย์ของพวกเขาทั้งในเวลากลางคืน และกลางวัน ทั้งโดยปกปิด และเปิดเผยนั้น พวกเขาจะได้รับรางวัลของพวกเขา ณ พระผู้เป็นเจ้าของพวกเขา และไม่มีความกลัวอย่างหนึ่งอย่างใดเกิดขึ้นแก่พวกเขา และทั้งพวกเขาก็จะไม่เสียใจ');
INSERT INTO "king_fahad_thai" VALUES(2,275,'2:275','บรรดาผู้กินดอกเบี้ยนั้น พวกเขาจะไม่ทรงตัว นอกจากจะเป็นเช่นเดียวกับผู้ที่ชัยฏอนทําร้ายเขาทรงตัว พวกเขากล่าวว่า ที่จริงการค้าขายนั้นก็เหมือนการเอาดอกเบี้ยนั้นเอง และอัลลอฮ์นั้นทรงอนุมัติการขาย และทรงห้ามการเอาดอกเบี้ย ดังนั้นผู้ใดที่การตักเตือนจากพระเจ้าของเขาได้มายังเขา แล้วเขาก็เลิก สิ่งที่ล่วงแล้วมาก็เป็นสิทธิของเขา และเรื่องของเขานั้นย่อมกลับไปสู่อัลลอฮ์ และผู้มดกลับ(กระทํา) อีก ชนเหล่านี้แหละคือชาวนรกโดยที่พวกเขาจะอยู่ในนรกนั้นตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(2,276,'2:276','อัลลอฮ์จะทรงให้ดอกเบี้ยลดน้อยลงและหมดความจําเริญ และจะทรงให้บรรดาที่เป็นทานเพิ่มพูนขึ้น และอัลลอฮ์นั้นไม่ทรงชอบผู้เนรคุณ ผู้กระทําบาปทุกคน');
INSERT INTO "king_fahad_thai" VALUES(2,277,'2:277','แท้จริงบรรดาผู้ที่ศรัทธา และประกอบสิ่งที่ดีงามทั้งหลาย และดํารงค์ไว้ซึ่งการละหมาดและจ่ายซะกาตนั้น พวกเขาจะได้รับรางวัลของพวกเขา ณ พระเจ้าของพวกเขา และไม่มีความกลัวอย่างหนึ่ง อย่างใดเกิดขึ้นแก่พวกเขา และทั้งพวกเขาก็จะไม่เสียใจ');
INSERT INTO "king_fahad_thai" VALUES(2,278,'2:278','บรรดาผู้ศรัทธาทั้งหลาย! พึงยําเกรงอัลลอฮ์เถิด และจงละเว้นดอกเบี้ยที่ยังเหลืออยู่เสีย หากพวกเจ้าเป็นผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(2,279,'2:279','และถ้าพวกเจ้ามิได้ปฏิบัติตาม ก็พึงรับรู้ไว้ด้วยว่า ซึ่งสงครามจากอัลลอฮ์ และร่อซูลของพระองค์ และหากพวกเจ้าสํานึกผิดกลับเนื้อกลับตัวแล้ว สําหรับพวกเจ้าก็คือต้นทุนแห่งทรัพย์ของพวกเจ้า โดยที่พวกเจ้านะได้ไม่อธรรม และไม่ถูกอธรรม');
INSERT INTO "king_fahad_thai" VALUES(2,280,'2:280','และหากเขา (ลูกหนี้) เป็นผู้ยากไร้ก็จงให้มีการรอคอยจนกว่าจะถึงคราวสะดวก และการที่พวกเจ้าจะให้เป็นทานนั้น ย่อมเป็นการดีแก่พวกเจ้า หากพวกเจ้ารู้');
INSERT INTO "king_fahad_thai" VALUES(2,281,'2:281','และพวกเจ้าจงยําเกรงวันหนึ่ง ซึ่งพวกเจ้าจะถูกนํากลับไปยังอัลลอฮ์ในวันนั้น แล้วแต่ละชีวิตจะถูกตอบแทนโดยครบถ้วนตามที่ชีวิตนั้นได้แสวงหาไว้ และพวกเขาจะไดไม่ถูกอธรรม');
INSERT INTO "king_fahad_thai" VALUES(2,282,'2:282','บรรดาผู้ศรัทธาทั้งหลาย! เมื่อพวกเจ้าต่างมีหนี้สินกันจะด้วยหนี้สินใด ๆก็ตาม จนกว่าจะถึงกําหนดเวลา(ใช้หนี้) ที่ถูกระบุไว้แล้ว ก็จงบันทึกหนี้สินนั้นเสีย และผู้เขียนก็จงบันทึกระหว่างพวกเจ้าด้วยความเที่ยงธรรม และผู้เขียนคนหนึ่งคนใดก็จงอย่าปฏิเสธที่จะบันทึก ดังที่อัลลอฮ์ได้ทรงสอนเขา ดังนั้นเขาจงบันทึกเถิด และจงให้ผู้ที่มีสิทะเหนือเขา(ลูกหนี้) บอกให้บันทึกและเขาจงยําเกรงอัลลอฮ์ผู้เป็นพระเจ้าของเขา และจงอย่าให้บกพร่องแต่อย่างใดจากสิทธินั้น และถ้าผู้มีสิทธิเหนือเขา(ลูกหนี้) เป็นคนโง่ หรือเป็นผู้อ่อนแอหรือไม่สามารถจะบอกให้บันทึกได้ ก็จงให้ผู้ปกครองของเขาบอกด้วยความเที่ยงธรรม และพวกเจ้าจงให้มีพยานขึ้นสองนายจากบรรดาผู้ชายในหมู่พวกเจ้า แต่ถ้ามิปรากฏว่า พยานทั้งสองนั้นเป็นชายก็ให้มีผู้ชายหนึ่งกับผู้หญิงสองคน จากผู้ที่พวกเจ้าพึงใจในหมู่พยานทั้งหลาย เพื่อว่าหญิงใดในสองคนนั้นหลงไป คนหนึ่งในสองคนนั้นก็จะได้เตือนอีกคนหนึ่ง และบรรดาพยานนั้นก็จงอย่าได้ปฏิเสธ เมื่อพวกเขาถูกเรียกร้อง และพวกเจ้าจงอย่าเบื่อหน่ายที่จะบันทึกหนี้สินนั้นไม่ว่าน้อยหรือมากก็ตาม จนกว่าจะถึงกําหนดเวลาของมัน นั่นแหละคือสิ่งที่ยุติธรรมยิ่งกว่า ณ ที่อัลลอฮ์ และเที่ยงตรงยิ่งกว่าสําหรับเป็นหลบักฐานยืนยัน และเป็นสิ่งใกล้ยิ่งกว่าที่พวกเจ้าจะไม่สงสัย นอกจากว่ามันเป็นสินค้าที่ปรากฏอยู่ต่อหน้า ซึ่งพวกเจ้าหมุนเวียนมัน (ซื้อขายแลกเปลี่ยน) ระหว่างพวกเจ้าก็ไม่มีโทษอันใดแก่พวกเจ้าที่พวกเจ้าจะไม่บันทึกมัน และพวกเจ้าจงให้มีพยานขึ้น เมื่อพวกเจ้าต่างซื้อขายกัน และผู้เขียนก็จงอย่าก่อให้เกิดความเดือดร้อนขึ้น และผู้เป็นพยานด้วย และหากว่าพวกเจ้ากระทํา แน่นอนมันก็เป็นการฝ่าฝืนเนื่องด้วยพวกเจ้า และพวกเจ้าจงพึงยําเกรงอัลลอฮ์เถิด และอัลลอฮ์นั้นทรงให้ความรู้แก่พวกเจ้าอยู่ และอัลลอฮ์นั้นทรงรอบรู้ในทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(2,283,'2:283','และถ้าพวกเจ้าอยู่ในระหว่างเดินทางและไม่พบผู้เขียนคนใด ก็ให้มีสิ่งค้ําประกันยึดถือไว้ แต่ถ้าบางคนในพวกเจ้าไว้ใจอีกบางคน(ลูกหนี้) ผู้ที่ได้รับความไว้ใจ (ลูกหนี้) ก็จงคืนสิ่งที่ถูกไว้ใจ(หนี้) ของเขาเสีย และเขาจงกลัวเกรงอัลลอฮ์ผู้เป็นพระเจ้าของเขาเถิด และพวกเจ้าจงอย่าปกปิดพยานหลักฐาน และผู้ใดปกปิดมันไว้ แน่นอนหัวใจของเขาก็มีบาป และอัลลอฮ์นั้นเป็นผู้ทรงรอบรู้ในสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(2,284,'2:284','สิ่งที่อยู่ในบรรดาชั้นฟ้า และสิ่งที่อยู่ในแผ่นดินนั้น เป็นสิทธิของอัลลอฮ์ และถ้าหากพวกเจ้าเปิดเผยสิ่งที่อยู่ในใจของพวกเจ้าหรือปกปิดมันไว้ก็ตาม อัลลอฮ์จะทรงนําสิ่งนั้นมาชําระสอบสวนแก่พวกเจ้า แล้วพระองค์จะทรงอภัยโทษแก่ผู้ที่พระองค์ทรงประสงค์ และอัลลอฮ์นั้นเป็นผู้ทรงเดชานุภาพเหนือทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(2,285,'2:285','ร่อซูลนั้น(นะบีมุฮัมมัด) ได้ศรัทธาต่อสิ่งที่ได้ถูกประทานลงมาแก่เขา จากพระเจ้าของเขา และมุมินทั้งหลายก็ศรัทธาด้วยทุกคนศรัทธาต่ออัลลอฮ์และมลาอิกะฮ์ของพระองค์ และบรรดาคัมภีร์ของพระองค์ และบรรดาร่อซูลของพระองค์(พวกเขากล่าวว่า) เราจะไม่แยกระหว่างท่านหนึ่งท่านใดจากบรรดาร่อซูลของพระองค์ และพวกเขาได้กล่าวว่า เราได้ยินแล้ว และได้ปฏิบัติตามแล้วการอภัยโทษจากพระองค์เท่านั้นที่พวกเราปรารถนา โอ้พระเจ้าของพวกเรา! และยังพระองค์นั้น คือ การกลับไป');
INSERT INTO "king_fahad_thai" VALUES(2,286,'2:286','อัลลอฮ์จะไม่ทรงบังคับชีวิตหนึ่งชีวิตใดนอกจากตามความสามารถของชีวิตนั้นเท่านั้น ชีวิตนั้นจะได้รับการตอบแทนดีในสิ่งที่เขาได้แสวงหาไว้ และชีวิตนั้นจะได้รับการลงโทษในสิ่งชั่วที่เขาได้แสวงหาไว้ โอ้พระเจ้าของพวกเรา! โปรดอย่าเอาโทษแก่เราเลย หากพวกเราลืม หรือผิดพลาดไป โอ้พระเจ้าของพวกเรา! โปรดอย่าได้บรรทุกภาระหนักใด ๆแก่พวกเรา เช่นเดียวกับที่พระองค์ได้ทรงบรรทุกมัน แก่บรรดาผู้ที่อยู่ก่อนหน้าพวกเรามาแล้ว โอ้พระเจ้าของพวกเรา! โปรดอย่าให้พวกเราแบกมันได้ และโปรดได้ทรงอภัยแก่พวกเราและยกโทษให้แก่พวกเรา และเมตตาแก่พวกเราด้วยเถิด พระองค์นั้น คือผุ้ปกครองของพวกเราดังนั้นโปรดได้ทรงช่วยเหลือพวกเราให้ไดเรับชัยชนะเหนือกลุ่มชนที่ปฏิเสธศรัทธาด้วยเถิด');
INSERT INTO "king_fahad_thai" VALUES(3,1,'3:1','อะลิฟ ลาม มีม');
INSERT INTO "king_fahad_thai" VALUES(3,2,'3:2','อัลลอฮ์นั้นคือ ไม่มีผู้ที่เป็นที่เคารพสักการะใด ๆ นอกจากพระองค์เท่านั้น ผู้ทรงมีชีวิตอยู่เสมอ ผู้ทรงบริหารกิจการทั้งหลายเป็นเนืองนิจ (ในสิ่งที่พรองค์ทรงสร้างและทรงบังเกิด)');
INSERT INTO "king_fahad_thai" VALUES(3,3,'3:3','พระองค์ได้ทรงประทานคัมภีร์นั้น ลงมาแก่เจ้าเป็นครั้งคราว พร้อมด้วยความจริง เพื่อยืนยันคัมภีร์ที่อยู่เบื้องหน้า คัมภีร์นั้น และได้ทรงประทานอัตเตารอต และอัล-อินญีล');
INSERT INTO "king_fahad_thai" VALUES(3,4,'3:4','(ให้มี) มาก่อน ในฐานะเป็นข้อแนะนําสําหรับมนุษย์ และได้ประทานอัล-ฟุรกอนมาด้วย แท้จริงบรรดาผู้ปฏิเสธศรัทธาต่อบรรดาโองการของอัลลอฮ์นั้น พวกเขาจะได้รับโทษอันรุนแรง และอัลลอฮ์นั้นเป็นผู้ทรงเดชานุภาพ ผู้ทรงทําการลงโทษ');
INSERT INTO "king_fahad_thai" VALUES(3,5,'3:5','แท้จริงอัลลอฮ์นั้น ไม่มีสิ่งใดในแผ่นดินจะซ่อนเร้นพระองค์ไปได้ และทั้งไม่มีฟากฟ้าด้วย');
INSERT INTO "king_fahad_thai" VALUES(3,6,'3:6','พระองค์คือผู้ทรงทําให้พวกเจ้ามีรูปร่างขึ้นในมดลูก ตามที่พระองค์ทรงประสงค์ ไม่มีสิ่งที่ควรได้รับการเคารพสักการะใด ๆนอกจากพระองค์เท่านั้น ผู้ทรงเดชานุภาพ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(3,7,'3:7','พระองค์คือผู้ทรงประทานคัมภีร์ลงมาแก่เจ้า โดยที่ส่วนหนึ่งจากคัมภีร์นั้นมีบรรดาโฮงการที่มีข้อความรัดกุมชัดเจน ซึ่งโองการเหล่านั้น คือรากฐานของคัมภีร์ และมีโองการอื่น ไ อีกที่มีข้อความเป็นนัย ส่วนบรรดาผู้ที่ในหัวใจของพวกเขามีการเอนเอียงออกจากความจริงนั้น เขาจะติดตามโองการที่มีข้อความเป็นนัยจากคัมภีร์ ทั้งนี้ เพื่อแสวงหาความวุ่นวาย และเพื่อแสวงหาการตีความในโองการนั้น แลไม่มีใครรู้ในการตีความโองการนั้นได้นอกจากอัลลอฮ์ และบรรดาผู้ที่มั่นคงในความรู้เท่านั้น โดยที่พวกเขาจะกล่าวว่า พวกเราศรัทธาต่อโองการนั้น ทั้งหมดนั้นมาจากที่ที่พระผู้เป็นเจ้าของเราทั้งสิ้น และไม่มีใครที่จะรับคําตักเตือนนอกจากบรรดาผู้ที่มีสติปัญญาเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(3,8,'3:8','โอ้พระผู้เป็นเจ้าของพวกเรา ! โปรดอย่าให้หัวใจของพวกเราเอนเอียงออกจากความจริงเลย หลังจากที่พระองค์ได้ทรงแนะนําแก่พวกเราแล้ว และโปรดได้ประทานความเอ็นดูเมตตา จากที่ที่พระองค์ให้แก่พวกเราด้วยเถิด แท้จริงพระองค์นั้นคือผู้ทรงประทานให้อย่างมากมาย');
INSERT INTO "king_fahad_thai" VALUES(3,9,'3:9','โอ้พระผู้เป็นเจ้าของพวกเรา ! แท้จริงพระองค์นั้น เป็นผู้ชุมนุมมนุษย์ทั้งหลายในวันหนึ่งซึ่งไม่มีการสงสัยใด ๆ ในวันนั้น แท้จริงอัลลอฮ์นั้นจะไม่ทรงผิดสัญญา');
INSERT INTO "king_fahad_thai" VALUES(3,10,'3:10','แท้จริงบรรดาผู้ปฏิเสธศรัทธานั้น ทรัพย์สมบัติของพวกเขา และลูก ๆ ของพวกเขานั้น จะไม่อํานวยประโยชน์แก่พวกเขาให้พ้นจากการลงโทษของอัลลอฮ์ได้แต่อย่างใดเลย และชนเหล่านี้แหละคือเชื้อเพลิงแห่งไฟนรก');
INSERT INTO "king_fahad_thai" VALUES(3,11,'3:11','เช่นเดียวกับสภาพความเคยชินของวงศ์วานอิสรออีล และบรรดาผู้ก่อนหน้าพวกเขา ซึ่งพวกเขาได้ปฏิเสธบรรดาโองการของเรา แล้วอัลลอฮ์ก็ได้ทรงลงโทษพวกเขา เพราะบาปกรรมของพวกเขาเอง และอัลลอฮ์นั้นเป็นผู้ทรงลงโทษอันรุนแรง');
INSERT INTO "king_fahad_thai" VALUES(3,12,'3:12','จงกล่าวเถิด (มุฮัมมัด) แก่บรรดาผู้ปฏิเสธศรัทธาว่า พวกท่านจะได้รับความปราชัย และ (ในวันปรโลก) พวกท่านจะถูกต้อนไปสู่ญะฮันนัม และเป็นที่นอนอันเลวร้านยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(3,13,'3:13','แน่นอนได้มีสัญญาณหนึ่งปรากฏแก่พวกเจ้าแล้ว ซึ่งอยู่ในสองฝ่ายที่เผชิญหน้ากัน ฝ่ายหนึ่งต่อสู้ในทางของอัลลอฮ์ และอีกฝ่ายหนึ่งเป็นผู้ปฏิเสธศรัทธา ซึ่งเห็นเขาเหล่านั้น ด้วยตาตนเองเป็นสองเท่าของพวกเขา และอัลลอฮ์นั้นจะทรงสนับสนุนผุ้ที่พระองค์ทรงประสงค์ด้วยการช่วยเหลือของพระองค์ แท้จริงในสิ่งที่กล่าวมานั้นย่อมเป็นข้อเตือนสติแก่ผู้มีดวงตา ทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,14,'3:14','ได้ถูกทําให้สวยงาม (ลุ่มหลง) แก่มนุษย์ซึ่งความรักในบรรดาสิ่งที่เป็นเสน่ห์อันได้แก่ผู้หญงและลูกชาย,ทองและเงินอันมากมาย และม้าดีและปศุสัตว์ และไร่นา นั่นเป็นสิ่งอํานวยประโยชน์ชั่วคราวในชีวิตความเป็นอยู่แห่งโลกนี้เท่านั้น และอัลลอฮ์นั้นณ พระองค์ คือที่กลับอันสวยงาม');
INSERT INTO "king_fahad_thai" VALUES(3,15,'3:15','จงกล่าวเถิด (มุฮัมมัด) ว่าจะให้ฉันบอกแก่พวกท่านถึงสิ่งที่ดียิ่งกว่านั้น ไหม? คือบรรดาผู้ที่ยําเกรงนั้น ณ พระผู้เป็นเจ้าของพวกเขา พวกเขาจะได้รับบรรดาสวนสวรรค์ ซึ่งมีแม่น้ําหลายสายไหลผ่านอยู่เบื้องล่าง โดยที่พวกเขาจะพํานักอยู่ในสวนสวรรค์เหล่านั้นตลอดกาลและจะได้รับบรรดาคู่ครองที่บริสุทธิ์ และความพึงใจจากอัลลอฮ์ด้วย และอัลลอฮ์นั้นทรงเห็นบรรดาบ่าวทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,16,'3:16','คือบรรดาผู้ที่กล่าวว่า โอ้พระเจ้าแห่งพวกข้าพระองค์ แท้จริงพวกข้าพระองค์ศรัทธากันแล้ว โปรดทรงอภัยโทษให้แก่พวกข้าพระองค์ด้วยเถิด ซึ่งบรรดาความผิดของพวกข้าพระองค์และโปรดได้ทรงป้องกันพวกข้าพระองค์ให้พ้นจากการลงโทษแห่งไฟรกด้วย');
INSERT INTO "king_fahad_thai" VALUES(3,17,'3:17','บรรดาผู้ที่อดทน และบรรดาผู้ที่พูดจริง และบรรดาผู้ที่ภักดี และบรรดาผู้ที่บริจาคและบรรดาผู้ที่ขออภัยโทษในยามใกล้รุ่ง');
INSERT INTO "king_fahad_thai" VALUES(3,18,'3:18','อัลลอฮ์ทรงยืนยันว่า แท้จริงไม่มีผู้ที่ควรได้รับการเคารพสักการะใด ๆ นอกจากพระองค์เท่านั้น และมะลาอิกะฮ์ และผู้มีความรู้ในฐานะดํารงไว้ซึ่งความยุติธรรมนั้น ก็ยืนยันด้วยว่าไม่มีผู้ที่ควรได้รับการเคารพสักการะใด ๆ นอกจากพระองค์ผู้ทรงเดชานุภาพ ผู้ทรงปรีชาญาณเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(3,19,'3:19','แท้จริงศาสนา ณ อัลลอฮ์นั้นคือ อัลอิสลาม และบรรดาผู้ที่ได้รับคัมภีร์ มิได้ขัดแย้งกันนอกจากหลังจากที่ได้รับความรู้ มายังพวกเขาเท่านั้น ทั้งนี้เนื่องจากความอิจฉาริษยาระหว่างพวกเขา เอง และผู้ใดปฏิเสธศรัทธาต่อบรรดาโองการของอัลลอฮ์แล้วไซร้แน่นอนอัลลอฮ์นั้นเป็นผู้ทรงรวดเร็วในการชําระ');
INSERT INTO "king_fahad_thai" VALUES(3,20,'3:20','แล้วหากพวกเขาโต้แย้งเจ้า ก็จงกล่าวเถิดว่าฉันได้มอบใบหน้า (ร่างกาย) ของฉัน แด่อัลลอฮ์แล้ว และผู้ที่ปฏิบัติตามฉัน(ก็มอบ) ด้วยและเจ้าจงกล่าวแก่บรรดาผู้ที่ได้รับคัมภีร์ และบรรดาผู้ที่เขียนอ่านไม่เป็น ว่า พวกท่านมอบ (ใบหน้าแด่อัลลอฮ์) แล้วหรือ? ถ้าหากพวกเขาได้มอบแล้วแน่นอนพวกเขาก็ได้รับแล้ว ซึ่งแนวทางอันถูกต้อง และถ้าหากพวกเขาผินหลังให้ แท้จริงหน้าที่ของเจ้านั้นเพียงการประกาศให้ทราบเท่านั้น และอัลลอฮ์นั้นเป็นผู้ทรงเห็นปวงบ่าวทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,21,'3:21','แท้จริงบรรดาผู้ปฏิเสธศรัทธา ต่อโองการทั้งหลายของอัลลอฮ์ และฆ่าบรรดานะบีโดยปราศจากความเป็นธรรม และฆ่าบรรดาผู้ที่ใช้ให้มีความยุติธรรม จากหมู่ประชาชนนั้น เจ้า (มุฮัมมัด) จงแจ้งข่าวดี แก่พวกเขาเถิด ด้วยการลงโทษอันเจ็บแสบ');
INSERT INTO "king_fahad_thai" VALUES(3,22,'3:22','ชนเหล่านี้แหละคือผู้ที่บรรดาการงานของพวกเขาไร้ผลทั้งในโลกนี้ และปรโลก และจะไม่มีบรรดาผู้ช่วยเหลือสําหรับพวกเขาเลย');
INSERT INTO "king_fahad_thai" VALUES(3,23,'3:23','เจ้า (มุฮัมมัด) มิได้มองดูบรรดาผู้ที่ได้รับส่วนหนึ่งจากคัมภีร์ ดอกหรือ? โดยที่พวกเขาถูกเชิญไปสู่คัมภีร์ของอัลลอฮ์ เพื่อคัมภีร์นั้นจะได้ตัดสินระหว่างพวกเขา (พวกผิดประเวณี) แล้วกลุ่มหนึ่งในหมู่พวกเขาก็ผินหลังให้และพวกเขาก็กําลังผินหลังให้อยู่');
INSERT INTO "king_fahad_thai" VALUES(3,24,'3:24','นั่นก็เพราะพวกเขากล่าวว่า ไฟนรกนั้นจะไม่แตะต้องพวกเราเลย นอกจากบรรดาวันที่ถูกนับไว้ และสิ่งที่พวกเขากุขึ้นในศาสนาของพวกเขานั้น ได้หลอกลวงพวกเขาให้หลงเชื่อ');
INSERT INTO "king_fahad_thai" VALUES(3,25,'3:25','แล้วจะเป็นอย่างไรเล่า เมื่อเราได้ชุมนุมพวกเขาไว้สําหรับวันหนึ่ง ซึ่งในวันนั้นไม่มีการสงสัยใด ๆ และแต่ละชีวิตจะถูกตอบแทนอย่างครบถ้วนในสิ่งที่มีชีวิตนั้นได้แสวงหาไว้ โดยที่พวกเขาจะไม่ถูกอธรรม');
INSERT INTO "king_fahad_thai" VALUES(3,26,'3:26','จงกล่าวเถิด (มุฮัมมัด) ว่าข้าแต่อัลลอฮ์ผู้ทรงอภิสิทธิ์แห่งอํานาจทั้งปวง ! พระองค์นั้นจะทรงประทานอํานาจแก่ผู้ที่พระองค์ทรงประสงค์ และจะทรงถอดถอนอํานาจจากผู้ที่พระองค์ทรงประสงค์ และจะทรงให้เกียรติแก่ผู้ที่พระองค์ทรงประสงค์ และจะทรงยังความต่ําต้อยแก่ผู้ที่พระองค์ทรงประสงค์ ความดีทั้งหลายนั้นอยู่ที่พระหัตถ์ ของพระองค์ แท้จริงพระองค์นั้นเป็นผู้ทรงอานุภาพเหนือทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(3,27,'3:27','พระองค์ทรงให้กลางคืนเข้าไปในกลางวัน และทรงให้กลางวันเข้าไปในกลางคืน และทรงให้สิ่งที่มีชีวิต ออกจากสิ่งที่ไม่มีชีวิต และทรงให้สิ่งที่ไม่มีชีวิตออกจากสิ่งที่มีชีวิต และทรงให้ปัจจัยยังชีพแก่ผู้ที่พะองค์ทรงประสงค์โดยปราศจากการคํานวณ');
INSERT INTO "king_fahad_thai" VALUES(3,28,'3:28','ผู้ศรัทธาทั้งหลายนั้น จงอย่าได้ยึดเอาบรรดาผู้ปฏิเสธศรัทธาเป็นมิตรอื่นจากบรรดามุมิน และผู้ใดกระทําเช่นนั้น เขาย่อมไม่อยู่ในสิ่งใดที่มาจากอัลลอฮ์ นอกจากพวกเจ้าจะป้องกัน (ให้พ้นอันตราย) จากพวกเขาจริง ๆ เท่านั้น และอัลลอฮ์ทรงเตือนพวกเจ้าให้ยําเกรงพระองค์ และยังอัลลอฮ์นั้นคือการกลับไป (ของพวกเจ้า)');
INSERT INTO "king_fahad_thai" VALUES(3,29,'3:29','จงกล่าวเถิด(มุฮัมมัด) ว่าหากพวกท่านปกปิดสิ่งที่อยู่ในอกของพวกท่าน หรือเปิดเผยมันก็ตาม อัลลอฮ์ก็ย่อมรู้สิ่งนั้นดีและทรงรู้สิ่งที่อยู่ในบรรดาชั้นฟ้า และสิ่งที่อยู่ในแผ่นดิน และอัลลอฮ์นั้นทรงอานุภาพเหนือทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(3,30,'3:30','วันที่แต่ละชีวิตพบความดีที่ตนได้ประกอบไว้ถูกนํามาอยู่ต่อหน้า และความชั่วที่ตนได้ประกอบไว้ด้วย แต่ละชีวิตนั้นชอบ หากว่าระหว่างตนกับความชั่วนั้นจะมีระยะทางที่ห่างไกล และอัลลอฮ์ทรงเตือนพวกเจ้าให้ยําเกรงพระองค์ และอัลลอฮ์นั้นเป็นผู้ทรงกรุณาปราณีต่อปวงบ่าวทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,31,'3:31','จงกล่าวเถิด(มุฮัมมัด) ว่า หากพวกท่านรักอัลลอฮ์ ก็จงปฏิบัติตามฉัน อัลลอฮ์ก็จะทรงรักพวกท่าน และจะทรงอภัยให้แก่พวกท่านซึ่งโทษทั้งหลายของพวกท่าน และอัลลอฮ์นั้นเป็นผู้ทรงอภัยโทษ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(3,32,'3:32','จงกล่าวเถิด(มุฮัมมัด) ว่า พวกท่านจงเชื่อฟังอัลลอฮ์และร่อซูลเถิด แต่ถ้าพวกเขาผินหลังให้ แท้จริงอัลลอฮ์นั้นไม่ทรงชอบผู้ปฏิเสธศรัทธาทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,33,'3:33','แท้จริงอัลลอฮ์ได้ทรงคัดเลือก อาดัมและนูห์ และวงศ์วานของอิบรอฮีม และวงศ์วานของอิมรอนให้เหนือกว่าประชาชาติทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,34,'3:34','เป็นเผ่าพันธุ์ ซึ่งสืบเชื้อสายมาจากกันและกัน และอัลลอฮ์นั้นเป็นผู้ทรงได้ยิน ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(3,35,'3:35','จงรําลึกถึงขณะที่ภรรยาของอิมรอน กล่าวว่า โอ้พระผู้เป็นเจ้าของข้าพระองค์! แท้จริงข้าพระองค์ได้บนไว้ว่าให้สิ่ง(บุตร)ที่อยู่ในครรภ์ของข้าพระองค์ ถูกเจาะจงอยู่ในฐานะผู้เคารพอิบาดะฮ์ต่อพระองค์และรับใช้พระองค์เท่านั้น ดังนั้นขอพระองค์ได้โปรดรับจากข้าพระองค์ด้วยเถิด แท้จริงพระองค์ท่านเป็นผู้ทรงได้ยิน ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(3,36,'3:36','ครั้นเมื่อนางได้คลอดบุตร นางก็กล่าวว่า โอ้พระเจ้าของข้าพระองค์! แท้จริงข้าพระองค์ได้คลอดบุตรเป็นหญิง และอัลลอฮ์นั้นทรงรู้ดียิ่งกว่าถึงบุตรที่นางได้คลอดมา และใช่ว่าเพศชายนั้นจะเหมือนกันเพศหญิงก็หาไม่ และข้าพระองค์ได้ตั้งชื่อเขาว่า “มัรยัม” แล้วข้าพระองค์ขอต่อพระองค์ให้ทรงคุ้มครองนาง และลูกของนางให้พ้นจากชัยฏอนที่ถูกขับไล่');
INSERT INTO "king_fahad_thai" VALUES(3,37,'3:37','แล้วพระเจ้าของนางก็ทรงรับมัรยัมไว้อย่างดี และทรงให้นางเจริญวัยอย่างดีด้วยและได้ทรงให้ซะกะรียาอุปการะนาง คราใดที่ซะกะรียาเข้าไปหานางที่อัลมิห์รอบ เขาก็พบปัจจัยยังชีพ อยู่ที่นาง เขากล่าวว่า มัรยัมเอ๋ย! เธอได้สิ่งนี้มาอย่างไร?นางกล่าวว่า มันมาจากที่อัลลอฮฺ แท้จริงอัลลอฮฺนั้นจะทรงประทานปัจจัยยังชีพแก่ผู้ที่พระองค์ทรงประสงค์โดยปราศจากการคิดคํานวณ');
INSERT INTO "king_fahad_thai" VALUES(3,38,'3:38','ที่โน่น แหละ ซะกะรียาได้วิงวอนต่อพระผู้เป็นเจ้าของเขาโดยกล่าวว่า ข้าแต่พระเจ้าของข้าพระองค์ โปรดได้ทรงประทานแก่ข้าพระองค์ซึ่งบุตรที่ดีคนหนึ่งจากที่พระองค์ แท้จริงพระองค์เป็นผู้ทรงได้ยินคําวิงวอน');
INSERT INTO "king_fahad_thai" VALUES(3,39,'3:39','และมะลาอิกะฮ์ได้เรียกเขา ขณะที่เขากําลังยืนละหมาด อยู่ในอัลมิห์รอบ ว่า แท้จริงอัลลอฮ์ทรงแจ้งข่าวดีแก่ท่านด้วยยะห์ยา โดยที่จะเป็นผู้ยืนยันพจมานหนึ่ง จากอัลลอฮ์ และจะเป็นผู้นําและผู้รักษาไว้ซึ่งความบริสุทธิ์ และเป็นนะบีคนหนึ่งจากหมู่ชนที่เป็นคนดี');
INSERT INTO "king_fahad_thai" VALUES(3,40,'3:40','เขา กล่าวว่า ข้าแต่พระเจ้าของข้าพระองค์ ข้าพระองค์จะมีบุตรได้อย่างไร โดยที่ความชราภาพได้มาถึงข้าพระองค์แล้วและภรรยาของข้าพระองค์ก็เป็นหมันด้วย พระองค์ตรัสว่ากระนั้นก็ตามอัลลอฮ์จะทรงกระทําตามที่พระองค์ทรงประสงค์');
INSERT INTO "king_fahad_thai" VALUES(3,41,'3:41','เขากล่าวว่า ข้าแต่พระเจ้าของข้าพระองค์! โปรดได้ทรงให้มีสัญญาณหนึ่งแก่ข้าพระองค์ด้วยเถิด พระองค์ตรัสว่าสัญญาณของเจ้านั้นคือ เจ้าจะไม่สามารถพูดแก่ผู้คนเป็นเวลาสามวัน นอกจากด้วยการแสดงท่าทางเท่านั้น และจงรําลึกถึงพระเจ้าของเจ้ามาก ๆ และจงกล่าวสดุดีในความบริสุทธิ์ของพระองค์ ทั้งในเวลายืนและเวลาเช้า');
INSERT INTO "king_fahad_thai" VALUES(3,42,'3:42','และจงรําลึกขณะที่มะลาอิกะฮ์กล่าวว่ามัรยัมเอ๋ย! แท้จริงอัลลอฮ์ได้ทรงเลือกเธอและทรงทําให้เธอบริสุทธิ์ และได้ทรงเลือกเธอให้เหนือบรรดาหญิงแห่งประชาชาติทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,43,'3:43','มัรยัมเอ๋ย! จงภักดีต่อพระเจ้าของเจ้าเถิด และจงสุยูดและรุกูอ์ ร่วมกับรรดาผู้รุกูอ์ทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,44,'3:44','นั่นคือส่วนหนึ่งจากบรรดาข่าวของสิ่งเร้นลับ ซึ่งเราชี้แจงให้เจ้า ทราบ และเจ้ามิได้อยู่ ณ ที่พวกเขา ขณะที่พวกเขาโยเครื่องเสี่ยงทายของพวกเขา(เพื่อทราบว่า) ใครในหมู่พวกเขาจะได้อุปการะมัรยัม และเจ้ามิได้อยู่ ณ ที่พวกเขา ขณะพวกเขาโต้เถียงกัน');
INSERT INTO "king_fahad_thai" VALUES(3,45,'3:45','จงรําลึกถึงขณะที่มะลาอิกะฮ์กล่าวว่า มัรยัมเอ๋ย ! แท้จริงอัลลอฮ์ทรงแจ้งข่าวดีแก่เธอซึ่งพจมานหนึ่ง จากพระองค์ ชื่อของเขาคือ อัลมะซีห์ อีซาบุตรของมัรยัม โดยที่เขาจะเป็นผู้มีเกียรติในโลกนี้ และปรโลก และจะอยู่ในหมู่ผู้ใกล้ชิด');
INSERT INTO "king_fahad_thai" VALUES(3,46,'3:46','และเขา จะพูดแก่ผู้คนขณะอยู่ในเปล และในวัยกลางคน และจะอยู่ในหมู่คนดี');
INSERT INTO "king_fahad_thai" VALUES(3,47,'3:47','นางกล่าวว่า ข้าแต่พระเจ้าของข้าพระองค์ ข้าพระองค์จะมีบุตรได้อย่างไร ทั้ง ๆ ที่มิได้มีบุรุษใดแตะต้องข้าพระองค์ พระองค์ตรัสว่า กระนั้นก็ตาม อัลลอฮ์จะทรงบังเกิดสิ่งที่พระองค์ประสงค์ เมื่อพระองค์ทรงชี้ขาดงานใดแล้ว? พระองค์ก็เพียงประกาศิตแก่สิ่งนั้นว่า จงเป็นขึ้นเถิด แล้วมันก็จะเป็นขึ้น');
INSERT INTO "king_fahad_thai" VALUES(3,48,'3:48','และพระองค์ก็จะทรงสอนเขา ซึ่งการเขียน และความรู้อันถูกต้อง และสอนอัตเตารอต และอัลอินญีล');
INSERT INTO "king_fahad_thai" VALUES(3,49,'3:49','และเป็นฑูต (นะบีอีซา) ไปยังวงศ์วานอิสรออีล (โดยที่เขาจะกล่าวว่า) แท้จริงนั้นได้นําสัญญาณหนึ่งจากพระเจ้าของพวกท่านมายังพวกท่านแล้ว โดยที่ฉันจะจําลองขึ้นจากดินให้แก่พวกท่าน ดั่งรูปนก แล้วฉันจะเป่าเข้าไปในมัน แล้วมันก็จะกลายเป็นนก ด้วยอนุมัติของอัลลอฮ์ และฉันจะรักษาคนตาบอดแต่กําเนิด และคนเป็นโรคเรื้อน และฉันจะให้ผุ้ที่ตายแล้วมีชีวิตขึ้น ด้วยอนุมัติของอัลลอฮ์ และฉันจะบอกพวกท่านถึงสิ่งที่พวกท่านจะบริโภคกันและสิ่งที่พวกท่านสะสมไว้ในบ้านของพวกท่าน แท้จริงในนั้น มีสัญญาณหนึ่งสําหรับพวกท่าน หากพวกท่านเป็นผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(3,50,'3:50','และฉันจะเป็นผู้มายืนยันสิ่งที่อยู่เบื้องหน้าของฉัน อันได้แก่ อัตเตารอต และเพื่อที่ฉันจะได้อนุมัติแก่พวกท่าน ซึ่งบางสิ่งที่ถูกห้ามแก่พวกท่าน และฉันได้นําสัญญาณหนึ่งจากพระเจ้าของท่านมายังพวกท่านแล้ว ดังนั้นพึงยําเกรงอัลลอฮ์เถิด และจงเชื่อฟังฉัน');
INSERT INTO "king_fahad_thai" VALUES(3,51,'3:51','แท้จริงอัลลอฮ์นั้นคือ พระเจ้าของฉัน และพระเจ้าของพวกท่าน ดังนั้น จงอิบาดะฮ์ ต่อพระองค์เถิด นี้แหละคือทางอันเที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(3,52,'3:52','ครั้นเมื่ออีซารู้สึกว่ามีการปฏิเสธศรัทธาเกิดขึ้นในหมู่พวกเขา จึงได้กล่าวว่า ใครบ้างจะเป็นผู้ช่วยเหลือฉันไปสู่อัลลอฮ์ บรรดาสาวกผู้บริสุทธิ์ใจกล่าวว่า พวกเราคือผู้ช่วยเหลืออัลลอฮ์ พวกเราศรัทธาต่ออัลลอฮ์แล้ว และท่านจงเป็นพยานด้วยว่า แท้จริงพวกเรานั้น คือผู้น้อมตาม');
INSERT INTO "king_fahad_thai" VALUES(3,53,'3:53','ข้าแต่พระเจ้าของพวกข้าพระองค์พวก ข้าพระองค์ศรัทธาแล้วต่อสิ่งที่พระองค์ได้ประทานลงมา และพวกข้าพระองค์ก็ได้ปฏิบัติตาม ร่อซูลแล้ว โปรดทรงบันทึกพวกข้าพระองค์ร่วมกับบรรดาผู้ที่กล่าวปฏิญาณยืนยันทั้งหลายด้วยเถิด');
INSERT INTO "king_fahad_thai" VALUES(3,54,'3:54','และพวกเขาได้วางแผน และอัลลอฮ์ก็ทรงวางแผนด้วย และอัลลอฮ์นั้นเป็นผุ้ทรงวางแผนที่ดีเยี่ยม');
INSERT INTO "king_fahad_thai" VALUES(3,55,'3:55','จงรําลึกถึงขณะที่อัลลอฮ์ตรัสว่า โอัอีซา ! ข้าจะเป็นผู้รับเจ้าไปพร้อมด้วยชีวิตและร่างกายของเจ้า และจะเป็นผู้ยกเจ้าขึ้นไปยังข้าและจะเป็นผู้ทําให้เจ้าบริสุทธิ์ พ้นจากบรรดาผู้ที่ปฏิเสธศรัทธา และจะเป็นผู้ให้บรรดาที่ปฏิบัติตามเจ้าเหนือผู้ปฏิเสธศรัทธาทั้งหลาย จนกระทั่งถึงวันกิยามะฮ์ แล้วยังข้านั้นคือการกลับไปของพวกเจ้า แล้วข้าจะตัดสินระหว่างพวกเจ้า ในสิ่งที่พวกเจ้าขัดแย้งกัน');
INSERT INTO "king_fahad_thai" VALUES(3,56,'3:56','ส่วนบรรดาผู้ที่ปฏิเสธศรัทธานั้น ข้าจะลงโทษพวกเขาอย่างรุนแรงทั้งในโลกนี้และปรโลก และจะไม่มีบรรดาผู้ช่วยเหลือใดสําหรับพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(3,57,'3:57','และส่วนบรรดาผู้ศรัทธา และประกอบสิ่งที่ดีทั้งหลายนั้น พระองค์จะทรงตอบแทนแก่พวกเขาโดยครบถ้วน ซึ่งรางวัลของพวกเขาและอัลลอฮ์นั้นไม่ทรงชอบบรรดาผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(3,58,'3:58','ดังกล่าวนั้นแหละ เราอ่านมันให้เจ้าฟัง อันได้แก่โองการต่าง ๆ และคําเตือนรําลึกที่รัดกุมชัดเจน');
INSERT INTO "king_fahad_thai" VALUES(3,59,'3:59','แท้จริงอุปมาของอีซานั้น ดั่งอุปมัยของอาดัม พระองค์ทรงบังเกิดเขาจากดิน และได้ทรงประกาศิตแก่เขาว่าจงเป็นขึ้นเถิด แล้วเขาก็เป็นขึ้น');
INSERT INTO "king_fahad_thai" VALUES(3,60,'3:60','ความจริง นั้นมาจากพระเจ้าของเจ้า (มุฮัมมัด) ดังนั้นเจ้าจงอย่าเป็นคนหนึ่งในหมู่ผู้สงสัยเป็นอันขาด');
INSERT INTO "king_fahad_thai" VALUES(3,61,'3:61','ดังนั้นผู้ใดที่โต้เถียงเจ้าในเรื่องของเขา (อีซา) หลังจากที่ได้มีความรู้มายังเจ้าแล้ว ก็จงกล่าวเถิดว่า ท่านทั้งหลายจงมาเถิด เราก็จะเรียกลูก ๆ ของเรา และลูกของพวกท่านและเรียกบรรดาผู้หญิงของเรา และบรรดาผุ้หญิงของพวกท่านและตัวของพวกเรา และตัวของพวกท่านและเราก็จะวิงวอนกัน (ต่ออัลลอฮ์) ด้วยความนอบน้อม โดยที่เราจะขอให้ละฮ์นัด ของอัลลอฮ์พึงประสบ แก่บรรพาผุ้ที่พูดโกหก');
INSERT INTO "king_fahad_thai" VALUES(3,62,'3:62','แท้จริงเรื่องนี้ เป็นเรื่องจริง และไม่มีผู้ที่ควรได้รับการเคารพสักการะใด ๆ นอกจากอัลลอฮ์เท่านั้น และแท้จริงอัลลอฮ์ คือผู้ทรงเดชานุภาพ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(3,63,'3:63','แล้วหากพวกเขาผินหลังให้ แน่นอนอัลลอฮ์นั้นเป็นผุ้ทรงรู้ดี ต่อผู้บ่อนทําลายทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,64,'3:64','จงกล่าวเถิด (มุฮัมมัด) ว่า โอ้บรรดาผู้ได้รับคัมภีร์ ! จงมายังถ้อยคําหนึ่งซึ่งเท่าเทียมกัน ระหว่างเราและพวกท่าน คือว่าเราจะไม่เคาระสักการะนอกจากพระองค์เท่านั้น และเราจะไม่ให้สิ่งหนึ่งสิ่งใดเป็นภาคีกับพระองค์ และพวกเราบางคนก็จะไม่ยึดถืออีกบางคนเป็นพระเจ้าอื่นจากอัลลอฮ์ แล้วหากพวกเขาผินหลังให้ ก็จงกล่าวเถิดว่า พวกท่านจงเป็นพยานด้วยว่า แท้จริงพวกเราเป็นผู้น้อมตาม');
INSERT INTO "king_fahad_thai" VALUES(3,65,'3:65','โอ้บรรดาผู้ที่ได้รับคัมภีร์ ! เพราะเหตุใดเล่าพวกเจ้าจึงโต้เถียงกันในตัวของอิบรอฮีม และอัตเตารอต และอัลอินญีลนั้นมิได้ถูกประทานลงมา นอกจากหลังจากเขา แล้วพวกเจ้าไม่ใช้ปัญญาดอกหรือ');
INSERT INTO "king_fahad_thai" VALUES(3,66,'3:66','พวกเจ้านี้แหละ ได้โต้เถียงกันในสิ่งที่พวกเจ้ามีความรู้ในสิ่งนั้นแล้ว แล้วก็เหตุไฉนเล่าพวกเจ้าจึงโต้เถียงกันในสิ่งที่พวกเจ้าไม่มีความรู้ และอัลลอฮ์นั้นทรงรู้ แต่พวกเจ้าไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(3,67,'3:67','อิบรอฮีมไม่เคยเป็นยิวและไม่เคยเป็นคริสต์ แต่ทว่าเขาเป็นผู้หันออกจากความเท็จสู่ความจริง เป็นผู้น้อมตาม และเขาก็ไม่เคยอยู่ในหมู่ผู้ให้มีภาคีขึ้น (แก่อัลลอฮ์)');
INSERT INTO "king_fahad_thai" VALUES(3,68,'3:68','แท้จริงผู้คนที่สมควรยิ่งต่ออิบรอฮีมนั้น ย่อมได้แก่บรรดาผู้ปฏิบัติตามเขา และปฏิบัติตามนะบีนี้ และบรรดาผู้ที่ศรัทธาด้วย และอัลลอฮ์นั้นทรงคุ้มครองผู้ศรัทธาทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,69,'3:69','กลุ่มหนึ่งจากผู้ได้รับคัมภีร์นั้นชอบหากพวกเขาจะทําให้พวกเจ้าหลงผิด และพวกเขาจะไม่ทําให้ใครหลงผิด นอกจากตัวของพวกเขาเองแต่พวกเขาไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(3,70,'3:70','โอ้ผู้ที่ได้รับคัมภีร์ทั้งหลาย ! เพราะเหตุใดพวกเจ้าจึงปฏิเสธบรรดาโองการของอัลลอฮ์ ทั้ง ๆ ที่พวกเจ้าก็เป็นพยานยืนยันอยู่');
INSERT INTO "king_fahad_thai" VALUES(3,71,'3:71','โอ้บรรดาผู้ได้รับคัมภีร์ทั้งหลาย ! เพราะเหตุใดพวกเจ้าจึงสวมความจริงไว้ด้วยความเท็จ และปกปิดความจริงไว้ ทั้งๆ ที่พวกเจ้าก็รู้ดีอยู่');
INSERT INTO "king_fahad_thai" VALUES(3,72,'3:72','และกลุ่มหนึ่งจากหมู่ผู้ได้รับคัมภีร์กล่าวว่า ท่านทั้งหลายจงศรัทธาต่อสิ่งที่ถูกให้ลงมาแก่บรรดาผู้ที่ศรัทธา ในตอนเริ่มแรกของกลางวัน(เช้า) และจงปฏิเสธศรัทธาในตอนสุท้ายของมัน (เย็น) เพื่อว่าพวกเขาจะได้กลับใจ');
INSERT INTO "king_fahad_thai" VALUES(3,73,'3:73','และพวกท่านจงอย่าเชื่อ นอกจากแก่ผู้ที่ปฏิบัติตามศาสนาของพวกท่านเท่านั้น จงกล่าวเถิด (มุฮัมมัด) ว่า คําแนะนํานั้นคือคําแนะนําของอัลลอฮ์เท่านั้น (คือจงอย่าเชื่อว่า) จะมีผู้ใดได้รับ เยี่ยงที่พวกท่านจะได้รับ หรือ (อย่าเชื่อว่า) เขาเหล่านั้น จะโต้แย้งพวกท่าน ณ พระเจ้าของพวกทานเลย จงกล่าวเถิด (มุฮัมมัด) ว่า แท้จริงความโปรดปรานนั้นอยู่ ณพระหัตถ์ของอัลลอฮ์ซึ่งพระองค์ก็จะทรงประทานมันให้แก่ผู้ที่พระองค์ทรงประสงค์ และอัลลอฮ์นั้นเป็นผู้ทรงไพศาลผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(3,74,'3:74','พระองค์จะทรงเจาะจงความเอ็นดูเมตตาของพระองค์แก่ผู้ที่พระองค์ทรงประสงค์และอัลลอฮ์นั้นเป็นผู้ทรงโปรดปรานที่ยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(3,75,'3:75','และจากหมู่ผู้ที่ได้รับคัมภีร์นั้น มีผู้ที่หากเจ้าฝากเขาไว้ซึ่งทรัพย์สมบัติอันมากมาย เขาก็จะคืนมันแก่เขจ้า และจากหมู่พวกเขานั้นมีผู้ที่หากเจ้าฝากเขาไว้สักเหรียญทองหนึ่ง เขาก็จะไม่คืนมันแก่เจ้า นอกจากเจ้าจะยืนเผ้าทวงเขาอยู่เท่านั้น นั่นก็เพราะว่าพวกเขากล่าวว่า ในหมู่ผู้ที่อ่านเขียนไม่เป็นนั้น ไม่มีทางใดที่เป็นโทษแก่เราได้ และพวกเขากล่าวความเท็จให้แก่อัลลอฮ์ทั้ง ๆ ที่พวกเขารู้กันดีอยู่');
INSERT INTO "king_fahad_thai" VALUES(3,76,'3:76','มิใช่นั้นดอก ผู้ใดที่รักษาสัญญาของเขาโดยครบถ้วน และยําเกรง(อัลลอฮ์) แล้วแน่นอนอัลลอฮ์ทรงชอบผู้ที่ยําเกรงทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,77,'3:77','แท้จริงบรรดาผุ้ที่นําสัญญาของอัลลอฮ์และการสาบานของพวกเขาไปขายด้วยราคาอันเล็กน้อยนั้น ชนเหล่านี้แหละไม่มีส่วนได้ใด ๆ แก่พวกเขาในปรโลก และอัลลอฮ์จะไม่ทรงพูดแก่พวกเขา และจะไม่ทรงมองดูพวกเขาในวันกิยามะฮ์ และทั้งจะไม่ทําให้พวกเขาสะอาดด้วย และพวกเขาจะได้รับโทษอันเจ็บแสบ');
INSERT INTO "king_fahad_thai" VALUES(3,78,'3:78','และแท้จริงจากหมู่พวกเขานั้น มีกลุ่มหนึ่งบิดลิ้น ของพวกเขา ในการอ่านคัมภีร์ ทั้งๆ ที่มันมิได้มาจากคัมภีร์ และพวกเขากล่าวว่า มันมาจากที่อัลลอฮ์ทั้งๆ ที่มันมิใช่มาจากอัลลอฮ์ และพวกเขากล่าวความเท็จให้แก่อัลลอฮ์ ทั้งๆ ที่พวกเขาก็รู้กันดีอยู่');
INSERT INTO "king_fahad_thai" VALUES(3,79,'3:79','ไม่เคยปรากฏแก่บุคคลใดที่อัลลอฮ์ทรงประทานคัมภีร์และข้อตัดสิน และการเป็นนะบีแก่เขา แล้วเขากล่าวแก่ผู้คนว่า ท่านทั้งหลายจงเป็นบ่าวของฉัน อื่นจากอัลลอฮ์ หากแต่(ขาจะกล่าวว่า) ท่านทั้งหลายจงเป็นผู้ที่ผูกพันธ์กับพระเจ้าเถิด เนื่องจากการที่พวกท่านเคยสอนคัมภีร์ และเคยศึกษาคัมภีร์มา');
INSERT INTO "king_fahad_thai" VALUES(3,80,'3:80','และเขาจะไม่ใช้พวกเจ้าให้ยึดเอามะลาอิกะฮ์และบรรดานะบีเป็นพระเจ้า เขาจะใช้พวกเจ้าให้ปฏิเสธศรัทธา หลังจากที่พวกเจ้าเป็นผู้นอบน้อม แล้วกระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(3,81,'3:81','และจงรําลึกถึงขณะที่อัลลอฮ์ได้ทรงเอาข้อสัญญาแก่นะบีทั้งหลายว่า สิ่งที่ข้าได้ให้แก่พวกเจ้านั้นไม่ว่าจะเป็นคัมภีร์ก็ดี และความรู้เกี่ยวกับข้อปฏิบัติในบัญญัติศาสนาก็ดี ภายหลังได้มีร่อซูลคนใดมายังพวกเจ้าซึ่งเป็นผู้นยืนยันในสิ่งที่มีอยู่กับพวกเจ้าแล้ว แน่นอนพวกเจ้าจะต้องศรัทธาต่อเขา และช่วยเหลือเขา พระองค์ตรัสว่า พวกเจ้ายอมรับและเอาข้อสัญญาของข้าดังกล่าวนั้นแล้วใช่ไหม? พวกเขากล่าวว่า พวกข้าพระองค์ยอมรับแล้ว พระองค์ตรัสว่า พวกเจ้าจงเป้นพยานเถิด และข้าก็อยู่ในหมู่เป็นพยานร่วมกับพวกเจ้าด้วย');
INSERT INTO "king_fahad_thai" VALUES(3,82,'3:82','แล้วผู้ใดที่ผินหลังให้หลังจากนั้น ชนเหล่านั้นและพวกเขาคือผู้ละเมิด');
INSERT INTO "king_fahad_thai" VALUES(3,83,'3:83','อื่นจากศาสนาของอัลลอฮ์กระนั้นหรือที่พวกเขาแสวงหา? และแด่พระองค์นั้น ผู้ที่อยู่ในชั้นฟ้าทั้งหลาย และแผ่นดินได้นอบน้อมกัน ทั้งด้วยการสมัครใจ และฝืนใจ และยังพระองค์นั้นพวกเขาจะถูกนํากลับไป');
INSERT INTO "king_fahad_thai" VALUES(3,84,'3:84','จงกล่าวเถิด (มุฮัมมัด) ว่า เราได้ศรัทธาต่ออัลลอฮ์แล้วและได้ศรัทธาต่อสิ่งที่ถูกประทานแก่เรา และสิ่งที่ถูกประทานแก่อิบรอฮีมและอิสมาอีล และอิสฮาก และยะกูบและบรรดาผู้สืบเชื้อสาย(จากยะอ์กูบ) และศรัทธาต่อสิ่งที่มูซาและอีซา และนะบีทั้งหลายได้รับจากพระเจ้าของพวกเขา โดยที่เราจะไม่แยกระหว่างคนหนึ่งคนใดในหมู่พวกเขา และพวกเรานั้นเป็นผู้ที่นอบน้อมต่อพระองค์.');
INSERT INTO "king_fahad_thai" VALUES(3,85,'3:85','และผู้ใดแสวงหาศาสนาหนึ่งศาสนาใดอื่นจากอิสลามแล้ว ศาสนานั้นก็จะไม่ถูกรับจากเขาเป็นอันขาด และในปรโลกเขาจะอยู่ในหมู่ผู้ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(3,86,'3:86','อย่างไรเล่าที่อัลลอฮ์จะทรงแนะนําพวกใดพวกหนึ่งที่ปฏิเสธศรัทธาหลังจากที่พวกเขาศรัทธาแล้ว และทั้งยังได้ยืนยันด้วยว่าแท้จริงร่อซูล นั้นเป้นความจริง และได้มีหลักฐานต่างๆ อันชัดแจ้งมายังพวกเขาด้วย และอัลลอฮ์นั้นจะไม่ทรงแนะนําพวกที่อธรรม');
INSERT INTO "king_fahad_thai" VALUES(3,87,'3:87','ชนเหล่านี้แหละการตอบแทนแก่พวกเขาก็คือ การละอ์นัต จากอัลลอฮ์ จากมะลาอิกะฮ์ และจากมนุษย์ทั้งมวลนั้นจะตกอยู่แก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(3,88,'3:88','โดยที่พวกเขาจะอยู่ในการละอ์นัตนั้นตลอดกาล ซึ่งการลงโทษนั้นจะไม่ถูกผ่อนเบาแก่พวกเขา และทั้งพวกเขาจะไม่ถูกประวิง อีกด้วย');
INSERT INTO "king_fahad_thai" VALUES(3,89,'3:89','นอกจากบรรดาผู้ที่สํานึกผิดกลับเนื้อกลับตัวหลังจากนั้น และได้ปรับปรุงแก้ไข แท้จริงอัลลอฮ์นั้นเป็นผู้ทรงอภัยโทษ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(3,90,'3:90','แท้จริงบรรดาผู้ที่ปฏิเสธศรัทธาหลังจากที่พวกเขาได้ศรัทธากัน แล้วยังได้ทวีการปฏิเสธศรัทธาขึ้นอีกนั้น การสํานึกผิดกลับเนื้อกลับตัวของพวกเขาจะไม่ถูกรับเป็นอันขาด และชนเหล่านี้แหละคือผู้ที่หลงทาง');
INSERT INTO "king_fahad_thai" VALUES(3,91,'3:91','แท้จริงบรรดาผู้ที่ปฏิเสธศรัทธาและพวกเขาได้ตายไปในขณะที่พวกเขาเป็นผู้ปฏิเสธศรัทธานั้น ทองเต็มแผ่นดินก็จะไม่ถูกรับจากคนใดในพวกเขาเป็นอันขาด และแม้ว่าเขาจะใช้ทองนั้นไถ่ตัวเขาก็ตาม ชนเหล่านี้แหละสําหรับพวกเขานั้น คือการลงโทษอันเจ็บแสบและทั้งไม่มีบรรดาผู้ช่วยเหลือใด ๆ สําหรับพวกเขาด้วย');
INSERT INTO "king_fahad_thai" VALUES(3,92,'3:92','พวกเจ้าจะไม่ได้คุณธรรมเลยจนกว่าพวกเจ้าจะบริจาคจากสิ่งที่พวกเจ้าชอบ และสิ่งใดที่พวกเจ้าบริจาคไป แท้จริงอัลลอฮฺทรงรู้ในสิ่งนั้นดี');
INSERT INTO "king_fahad_thai" VALUES(3,93,'3:93','อาหารทุกชนิดนั้นเคยเป็นที่อนุมัติแก่วงศ์วานอิสรออีลมาแล้ว นอกจากที่อิสรออีลได้ให้เป็นที่ต้องห้ามแก่ตัวเขาเอง ก่อนจากที่อัตเตารอตจะถูกประทานลงมาเท่านั้น จงกล่าวเถิด(มุฮัมมัด) ว่าพวกเจ้าจงนําเอาอัตเตารอตมาแล้วจงอ่านมันดู หากพวกเจ้าเป็นผู้พูดจริง');
INSERT INTO "king_fahad_thai" VALUES(3,94,'3:94','แล้วผู้ใดที่อุปโลกน์ความเท็จให้อัลลอฮ์หลังจากนั้นชนเหล่านี้แหละ พวกเขาคือผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(3,95,'3:95','จงกล่าวเถิด(มุฮัมมัด) ว่าอัลลอฮ์นั้นตรัสจริงแล้ว ดังนั้นพวกเจ้าจงปฏิบัติตามแนวทางของอิบรอฮีมผู้หันออกจากความเท็จสู่ความจริงเถิด และเขาไม่เคยอยู่ในหมู่ผู้ให้มีภาค(แก่อัลลอฮ์)เลย');
INSERT INTO "king_fahad_thai" VALUES(3,96,'3:96','แท้จริงบ้านหลักแรกที่ถูกตั้งขึ้นสําหรับมนุษย์(เพื่อการอิบาดะฮ์) นั้นคือบ้านที่มักกะฮ์ โดยเป็นที่ที่ถูกให้มีความจําเริญ และเป็นที่แนะนําแก่ประชาชาติทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,97,'3:97','ในบ้านนั้น มีหลายสัญญาณที่ชัดแจ้ง(ส่วนหนึ่งนั้น)คือมะกอมอิบรอฮีม และผู้ใดได้เข้าไปในบ้านนั้น เขาก็เป็นผู้ปลอดภัยและสิทธิของอัลลอฮ์ที่มีแก่มนุษย์นั้น คือการมุ่งสู่บ้านหลังนั้น อันได้แก่ผู้ที่สามารถหาทางไปยังบ้านหลังนั้นได้และผู้ใดปฏิเสธ แท้จริงอัลลอฮ์นั้นไม่ทรงพึ่งประชาชาติทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,98,'3:98','จงกล่าวเถิด(มุฮัมมัด) ว่า โอ้ผู้ที่ได้รับคัมภีร์ทั้งหลาย! เพราะเหตุใดพวกท่านจึงปฏิเสธศรัทธาต่อบรรดาโองการของอัลลอฮ์และอัลลอฮ์นั้นจะทรงเป็นพยานยืนยันในสิ่งที่พวกท่านกระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(3,99,'3:99','จงกล่าวเถิด(มุฮัมมัด) ว่าโอ้ผู้ที่ได้กรับคัมภีร์ทั้งหลาย! เพราะเหตุใดท่านจึงขัดขวางผ็ศรัทธาซึ่งทางของอัลลอฮ์โดยที่พวกท่านปรารถนาจะให้ทางของอัลลอฮ์คด ทั้ง ๆ ที่พวกท่านก็เป็นพยานยืนยันอยู่ และอัลลอฮ์นั้นมิใช่เป็นผู้ทรงเผลอในสิ่งที่พวกท่านกระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(3,100,'3:100','โอ้ผู้ศรัทธาทั้งหลาย! หากพวกเจ้าเชื่อฟังกลุ่มหนึ่งกลุ่มใด ในหมู่ผู้ที่ได้รับคัมภีร์แล้ว พวกเขาก็จะให้พวกเจ้ากลับเป็นผู้ปฏิเสธศรัทธาอีก หลังจากที่พวกเจ้าศรัทธาแล้ว');
INSERT INTO "king_fahad_thai" VALUES(3,101,'3:101','และอย่างไรเล่าที่พวกเจ้าจะปฏิเสธศรัทธากัน ทั้งๆ ที่พวกเจ้านั้น มีบรรดาโองการของอัลลอฮ์ถูกอ่านแก่พวกเจ้าอยู่ และยังมีร่อซูลของพระองค์อยู่ในหมู่พวกเจ้าด้วย และผู้ใดยืดมั่นต่ออัลลอฮ์ แน่นอนเขาก็ได้รับคําแนะนําไปสู่ทางอันเที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(3,102,'3:102','โอ้ผู้ศรัทธาทั้งหลาย! จงยําเกรงอัลลอฮ์อย่างแท้จริงเถิด และพวกเจ้าจงอย่าตาย เป็นอันขาดนอกจากในฐานะที่พวกเจ้าเป็นผู้นอบน้อมเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(3,103,'3:103','และพวกเจ้าจงยึดสายเชือก ของอัลลอฮ์โดยพร้อมกันทั้งหมดและจงอย่าแตกแยกกัน และจํารําลึกถึงความเมตตาของอัลลอฮ์ที่มีแต่พวกเจ้า ขณะที่พวกเจ้าเป็นศัตรูกัน แล้วพระองค์ได้ทรงให้สนิทสนมกันระหว่างหัวใจของพวกเจ้า แล้วพวกเจ้าก็กลายเป็นพี่น้องกันด้วย ความเมตตาของพระองค์ และพวกเจ้าเคยปรากฏอยู่บนปากหลุมแห่งไฟนรก แล้วพระองค์ก็ทรงช่วยพวกเจ้าให้พ้นจากปากหลุมแห่งนรกนั้น ในทํานองนั้นแหละ อัลลอฮ์จะทรงแจกแจงแก่พวกเจ้าซึ่งบรรดาโองการของพระอง๕เพื่อว่าเพวกเจ้าจะได้รับแนวทางอันถูกต้อง');
INSERT INTO "king_fahad_thai" VALUES(3,104,'3:104','และจงให้มีขึ้นจากพวกเจ้า ซึ่งคณะหนึ่งที่จะเชิญชวนไปสู่ความดีและใช้ให้กระทําสิ่งที่ชอบ และห้ามมิให้กระทําสิ่งที่มิชอบและชนเหล่านี้แหละพวกเขาคือผู้ได้รับความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(3,105,'3:105','และพวกเจ้าจงอย่าเป็นเช่นบรรดาผู้ที่แตกแยกกัน และขัดแย้งกันหลังจากที่บรรดาหลักฐานอันชัดแจ้งได้มายังพวกเขาแล้วและชนเหล่านี้แหละสําหรับพวกเขา คือการลงโทษอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(3,106,'3:106','วันซึ่งบรรดาใบหน้าจะขาวผ่อง และบรรดาใบหน้าจะดําคล้ํา ส่วนผู้ที่ใบหน้าของพวกเขาดําคล้ํานั้น (พวกเขาจะถูกถามว่า) พวกเจ้าได้ปฏิเสธศรัทธา หลังจากที่พวกเจ้าศรัทธาแล้วกระนั้นหรือ? พวกเจ้าจงชิมการลงโทษเถิด เนื่องจากการที่พวกเจ้าปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(3,107,'3:107','และส่วนบรรดาผู้ที่ใบหน้าของพวกเขาขาวผ่องนั้น เขาจะอยู่ในความเมตตา ของอัลลอฮ์ โดยที่พวกเขาจะอยู่ในความเมตตานั้นตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(3,108,'3:108','นั่นคือบรรดาโองการของอัลลอฮ์โดยที่เรา อ่านโองการเหล่านั้นแก่เจ้าด้วยความจริง และอัลลอฮ์นั้นไม่ทรงประสงค์ซึ่งการอธรรมใด ๆ แก่ประชาชาติทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,109,'3:109','และสิ่งที่อยู่ในบรรดาชั้นฟ้า และสิ่งที่อยู่ในแผ่นดินนั้นเป็นสิทธิของอัลลอฮ์เท่านั้น และยังอัลลอฮ์นั้นกิจการทั้งหลายจะถูกนํากลับไป');
INSERT INTO "king_fahad_thai" VALUES(3,110,'3:110','พวกเจ้านั้น เป็นประชาชาติที่ดียิ่งซึ่งถูกให้อุบัติขึ้นสําหรับมนุษย์ชาติ โดยที่พวกเจ้าใช้ให้ปฏิบัติสิ่งที่ชอบ และห้ามมิให้ปฏิบัติสิ่งที่มิชอบ และศรัทธาต่ออัลลอฮ์ และถ้าหากว่าบรรดาผู้ที่ได้รับคัมภีร์ ศรัทธากันแล้ว แน่นอนมันก็เป็นการดีแก่พวกเขา จากพวกเขานั้นมีบรรดาผู้ที่ศรัทธา และส่วนมากของพวกเขานั้นเป็นผู้ละเมิด');
INSERT INTO "king_fahad_thai" VALUES(3,111,'3:111','พวกเขา จะไม่ทําอันตรายแก่พวกเจ้าได้เลย นอกจากการก่อความเดือดร้อนเล็ก ๆ น้อยๆ เท่านั้น และหากพวกเขาต่อสู้พวกเจ้า พวกเขาก็จะหันหลังหนีพวกเจ้า แล้วพวกเขาก็จะไม่ได้กรับความช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(3,112,'3:112','ความต่ําช้าได้ถูกฟาดลงบนพวกเขา ณ ที่ใดก็ตามที่พวกเขาถูกพบ นอกจากด้วยสายเชือกจากอัลลอฮ์ และสายเชือกจากมนุษย์ และพวกเขาจะนําความกริ้วโกรธจากอัลลอฮ์กลับไป และความขัดสนก็จะถูกฟาดลงบนพวกเขา นั่นก็เพราะว่าพวกเขาเคยปฏิเสธบรรดาโองการของอัลลอ์ และฆ่าบรรดานะบีโดยปราศจากความเป็นธรรม นั่นก็เนื่องจากการที่พวกเขาดื้อดึง และเคยทําการละเมิด');
INSERT INTO "king_fahad_thai" VALUES(3,113,'3:113','พวกเขาหาใช่เหมือนกันไม่ จากบรรดาผู้ที่ได้รับคัมภีร์นั้นมีกลุ่มชนหนึ่งที่เที่ยงธรรม ซึ่งพวกเขาอ่านบรรดาโองการของอัลลอฮ์ในยามค่ําคืน และพร้อมกันนั้น พวกเขาก็สุยูดกัน');
INSERT INTO "king_fahad_thai" VALUES(3,114,'3:114','พวกเขาศรัทธาต่ออัลลอฮ์ และวันปรโลก และใช้ให้ปฏิบัติสิ่งที่ชอบ และห้ามมิให้ปฏิบัติสิ่งที่ไม่ชอบ และต่างรีบเร่งกันในบรรดาสิ่งดีงาม และชนเหล่านี้และอยู่ในหมู่ที่ประพฤติดี');
INSERT INTO "king_fahad_thai" VALUES(3,115,'3:115','และความดีใด ๆ ที่พวกเขากระทําพวกเขาจะไม่ถูกปฏิเสธในความดีนั้น เป็นอันขาดและอัลลอฮ์ทรงรู้ดีต่อบรรดาผู้ที่ยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(3,116,'3:116','แท้จริงบรรดาผู้ปฏิเสธศรัทธานั้นทรัพย์สินของพวกเขา และลูก ๆ ของพสกเขาจะไม่อํานวยประฌยชน์ให้พวกเขาพ้นจากการลงโทษของอัลลอฮ์ได้อย่างใดเลย และชนเหล่านี้แหละคือชาวนรก โดยที่พวกเขาจะอยู่ในนรกนั้นตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(3,117,'3:117','อุปมาสิ่งที่พวกเขาบริจาคไปในชีวิตความเป้นอยู่แห่งโลกนี้นั้นอุปมัยลมซึ่งมีความเย็นจัด ได้ประสบแก่พืชผลของพวกกหนึ่งที่อธรรมแก่ตัวเองแล้วได้ทําลายพืชผลนั้น อัลลอฮ์นั้นมิได้ทรงอธรรมแก่พวกเขา แต่ทว่าพวกเขาอธรรมแก่ตัวของพวกเขาเอง');
INSERT INTO "king_fahad_thai" VALUES(3,118,'3:118','โอ้ผู้ศรัทธาทั้งหลาย! จงอย่าได้ยึดเอาเพื่อสนิทที่รู้เห็นกิจการภายใน อื่นจากพวกของเจ้าเอง ซึ่งเขาเหล่านั้นจะไม่ลดลบะแก่พวกเจ้าในการก่อความเสียหายให้เกิดขึ้นพวกเขาชอบกสรที่พวกเจ้าลําบาก แท้จริงความเกลียดชังต่างๆ ได้เผยออกมาแล้วจากปากของพวกเขา และสิ่งที่หัวอกของพสกเขาซ่อนไว้นั้นใหญ่ยิ่งกว่า แน่นอนเราได้แจกแจงบรรดาโองการไว้แก่พวกเจ้าแล้ว หากพวกเจ้าใช้ปัญญากัน');
INSERT INTO "king_fahad_thai" VALUES(3,119,'3:119','ถึงรู้เถิดว่า พวกเจ้านี้แหละรักใคร่พวกเขาทั้งๆ ที่พวกเขาไม่รักใคร่พวกเจ้า และพวกเจ้าศรัทธาต่อคัมภีร์ทุกเล่ม และเมื่อพวกเขาพบพวกเจ้าพวกเขาก็กล่าวว่า พวกเราศรัทธากันแล้ว และเมื่อพวกเขาอยู่แต่ลําพัง พวกเขาก็กัดนิ้วมือ เนื่องจากความเคียดแค้นแก่พวกเจ้าจงกล่าเถิด(มุฮัมมัด) ว่า พวกเจ้าจงตายด้วยความเคียดแค้นของพสกเจ้าเถิด แท้จริงอัลลอฮ์เป็นผู้ทรงรอบรู้สิ่งที่อยู่ในหัวอกทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,120,'3:120','หากมีความดีใดๆ ประสบแก่พวกเจ้า ก็ทําให้พวกเขาเศร้าใจและถ้าหากความชั่วใด ๆ ประสบแก่พวกเจ้า พวกเขาก็ดีใจเนื่องด้วยความชั่วนั้น และถ้าพวกเจ้าอดทน และยําเกรงแล้วไซร้ อุบายของพวกเขาก็ย่อมไม่เป็นอันตรายแก่พวกเจ้าแต่อย่างใดแท้จริงอัลลอฮ์นั้นทรงล้อม ซึ่งสิ่งที่พวกเขากระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(3,121,'3:121','และจงรําลึกถึงขณะที่เจ้าจากครอบครัวของเจ้าไปแต่เช้าตรู่ โดยที่เจ้าจะได้จัดให้บรรดามุมินประจําที่มั่นต่างๆ เพื่อการสู้รบ และอัลลอฮ์นั้นเป็นผู้ทรงได้ยิน ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(3,122,'3:122','จงรําลึกขณะที่สองกลุ่ม ในหมู่พวกเจ้ารู้สึกอ่อนแอและขลาด และอัลลอฮ์เป็รนผู้ทรงคุ้มครองทั้งสองกลุ่มนั้นไว้ และแด่อัลลอฮ์นั้นมุมินทั้งหลายจงมอบหมายเถิด');
INSERT INTO "king_fahad_thai" VALUES(3,123,'3:123','และแน่นอน อัลลอฮ์ได้ทรงช่วยเหลือพวกเจ้าที่บะดัร มาแล้วทั้งๆ ที่พวกเข้าเป็นพวกด้อยกว่า ดังนั้นพวกเจ้าพึงยําเกรงอัลลอ์เถิด หวังว่าพวกเจ้าจักขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(3,124,'3:124','จงรําลึกถึงขณะที่เจ้า (มุฮัมมัด)กล่าวแก่บรรดามุมินว่า ไม่เพียงกอแก่พวกเจ้าเลยหรือ การที่พระเจ้าของพวกท่านจะหนุนกําลังแก่พวกท่าน ด้วยมะลาอิกะฮ์จํานวนสามพันโดยถูกส่งลงมา');
INSERT INTO "king_fahad_thai" VALUES(3,125,'3:125','เพียงพอแน่นอน หากพวกเจ้าอดทนและยําเกรง และพวกเขาจะ มายังพวกเจ้าทันทีทันใดขณะนี้ แล้วพระเจ้าของพวกเจ้าก็จะหนุนกําลังแก่พวกเจ้าอีก ด้วยจํานวนมะลาอิกะฮ์ห้าพัน โดยมีเครื่องหมาย');
INSERT INTO "king_fahad_thai" VALUES(3,126,'3:126','และอัลลอฮ์มิได้ทรงให้กําลังหนุนนั้นมีขึ้น นอกจากเพื่อเป็นข่าวดีแก่พวกเจ้า และเพื่อที่หัวใจของพวกเจ้าจะได้สงบด้วยกําลังหนุนนั้นและความช่วยเหลือทั้งหลายนั้นไม่มี(จากที่อื่นใด) นอกจากที่อัลลอฮ์ ผู้ทรงเดชานุภาพ ผู้ทรงปรีชาญาณเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(3,127,'3:127','เพื่อพระองค์จะทรงบั่นทองส่วนหนึ่ง ออกจากบรรดาผู้ปฏิเสธศรัทธาหรือทรงให้พวกเขาได้รับความอัปยศ แล้วพวกเขาก็จะถอยกลับไปในฐานะผู้ผิดหวัง');
INSERT INTO "king_fahad_thai" VALUES(3,128,'3:128','ไม่มีสิ่งใดเป็นสิทธิของเจ้า(มุฮัมมัด) จากกิจการเหล่านั้น หรือไม่ก็พระองค์จะทรงอภัยโทษให้แก่พวกเขา หรือลงโทษพวกเขาเพราะพวกเขานั้นคือผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(3,129,'3:129','และสิ่งที่อยู่ในบรรดาชั้ยนฟ้าและสิ่งที่อยู่ในแผ่นดินนั้นเป็นสิทธิของอัลลอฮ์เท่านั้นพระองค์จะทรงอภัยดทษให้แก่พผู้ที่พระองค์ทรงประสงค์ และจะทรงลงโทษแก่ผู้ที่พระองค์ทรงประสงค์ และอัลลอฮ์นั้นคือผู้ทรงอภัยโทษ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(3,130,'3:130','โอ้ ผู้ศรัทธาทั้งหลาย! จงอย่ากินดอกเบี้ยหลายเท่าที่ถูกทบทวีและพวกเขาพึงยําเกรงอัลลอฮ์เถิด เพื่อว่าพวกเจ้าจะได้รับความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(3,131,'3:131','และพวกเจ้าจงเกรงกลัวไฟนรกที่ถูกเตรียมไว้สําหรับบรรดาผู้ปฏิเสธศรัทธาเถิด');
INSERT INTO "king_fahad_thai" VALUES(3,132,'3:132','และพวกเจ้าจงเชื่อฟังอัลลอฮ์และร่อซูลของพระองค์ เพื่อว่าพวกเจ้าจะได้รับความเมตตา');
INSERT INTO "king_fahad_thai" VALUES(3,133,'3:133','และพวกเจ้าจงรีบเร่งกันไปสู่การอภัยโทษจากพระเจ้าของพวกเจ้า และไปสู่สวรรค์ซึ่งความกว้างของมันนั้น คือบรรดาชั้นฟ้าและแผ่นดิน โดยที่มันถูกเตรียมไว้สําหรับบรรดาผู้ที่ยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(3,134,'3:134','คือบรรดาผู้ที่บริจาคทั้งในยามสุขสบาย และในยามเดือดร้อน และบรรดาผู้ข่มโทษและบรรดาผู้ให้อภัยแก่เพื่อนมนุษย์และอัลลอฮ์นั้นทรงรักผู้กระทําดีทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,135,'3:135','บรรดาผู้ที่เมื่อพวกเขากระทําสิ่งชั่วใด ๆ หรือ อยุติธรรมแก่ตัวเองแล้ว พวกเขาก็รําลึกถึงอัลลอฮ์ แล้วขออภัยโทษในบรรดาความผิดของพวกเขา และใครเล่าที่จะอภัยโทษบรรดาความผิดทั้งหลายให้ได้ นอกจากอัลลอฮ์แล้ว และพวกเขามิได้ดื้อรั้นปฏิบัติในสิ่ง ที่เขาเคยปฏิบัติมาโดยที่พวกเขารู้กันอยู่');
INSERT INTO "king_fahad_thai" VALUES(3,136,'3:136','ชนเหล่านี้แหละการตอบแทนแก่พวกเขาคือการอภัยโทษจากพระเจ้าของพวกเขาและบรรดาสวนสวรรค์ ซึ่งมีแม่น้ําหลายสายไหลอยู่ภายในสวนเหล่านั้น โดยที่พวกเขาจะพํานักอยู่ในสวนเหล่านั้นตลอดกาล และรางวัลของผู้ทํางาน นั้นช่างเลิศจริงๆ');
INSERT INTO "king_fahad_thai" VALUES(3,137,'3:137','แน่นอนได้ผ่านพ้นมาแล้วก่อนพวกเจ้า ซึ่งแนวทางต่างๆ ดังนั้นพวกเจ้าจงท่องเที่ยวไปในแผ่นดิน แล้วจงดูว่าบั้นปลายของบรรดาผู้ปฏิเสธเป็นอย่างไร?');
INSERT INTO "king_fahad_thai" VALUES(3,138,'3:138','นี่คือข้อชี้แจงอันชัดเจสําหรับมนุษย์และเป็นคําแนะนําที่ถูกต้อง และเป็นคําตักเตือนสําหรับผู้ยําเกรงทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,139,'3:139','และพวกเจ้าจงอย่าท้อแท้ และจงอย่าเสียใจ แลบพวกเจ้านั้นคือผู้ที่สูงส่งยิ่ง หากพวกเจ้าเป็นผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(3,140,'3:140','หากประสบแก่พวกเจ้า ซึ่งบาดแผลหนึ่งบาดแผลใด แน่นอนก็ย่อมประสบแก่พวกนั้น ซึ่งบาดแผลเยี่ยงเดียวกัน และบรรดาวันเหล่านั้นเราได้ให้มันหมุนเวียนไประหว่างมนุษย์ และเพื่ออัลลอฮ์จะได้ทรงรับรู้บรรดาผู้ที่ศรัทธา แลเพื่อเอาบรรดาผู้เสียชีวิตในสงคราม จากพวกเจ้าและอัลลอฮ์นั้นไม่ทรงรักใคร่ผู้อธรรมทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,141,'3:141','เพื่อที่อัลลอฮ์จะทรงขัดเกลาบรรดาผู้ศรัทธาให้บริสุทธิ์ และทรงขจัดบรรดาผู้ปฏิเสธศรัทธาให้หมดไป');
INSERT INTO "king_fahad_thai" VALUES(3,142,'3:142','หรือว่าพวกเจ้าคิดว่า พวกเจ้าจะได้เข้าสวนสวรรค์ ทั้งๆ ที่อัลลอฮ์ยังมิได้ทรงรู้ บรรดาผู้ที่ต่อสู้(ญิฮาด) ในหมู่พวกเจ้าพร้อมกันนั้น พระองค์ก็จะทรงรู้บรรดาผู้ที่อดทนด้วย');
INSERT INTO "king_fahad_thai" VALUES(3,143,'3:143','และแน่นอนพวกเจ้า เคยปรารถนาความตาย ก่อนจากที่พวกเจ้าจะได้พบมัน แล้วแน่นอนพวกเจ้าก็ได้เห็นมันแล้ว ขณะที่พวกเจ้ามองดูกันอยู่');
INSERT INTO "king_fahad_thai" VALUES(3,144,'3:144','และมุฮัมมัดนั้นหาใช่อื่นมดไม่นอกจากเป็นร่อซูลผู้หนึ่งเท่านั้น ซึ่งบรรดาร่อซูลก่อนจากเขาก็ได้ล่วงลับไปแล้ว แล้วหากเขาตายไปหรือเขาถูกฆ่าก็ตาม พวกเจ้าก็หันสันเท้าของพวกเจ้ากลับกระนั้นหรือ? และผู้ใดที่หันสันเท้าทั้งสองของเขากลับแล้วไซร้ มันก็จะไม่ก่อให้เกิดอันตรายแก่อัลลอฮ์แต่อย่างใดเลย และอัลลอฮ์นั้นจะทรงตอบแทนแก่ผู้กตัญญูทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,145,'3:145','และมิเคยปรากฏแก่ชีวิตใดที่จะตายนอกจากด้วยอนุมัติของอัลลอฮ์เท่านั้น ทั้งนี้เป็นลิขิตที่ถูกกําหนดไว้ และผู้ใดต้องการผลตอบแทนในโลกนี้ เราก็จะให้แก่เขาจากโลกนี้ และผู้ใดต้องการผลตอบแทนในปรโลก เราก็จะให้แก่เขาจากปรโลกและจะตอบแทนแก่ผู้กตัญญูทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,146,'3:146','และนะบีกี่มากน้อยแล้ว ที่กลุ่มชนอันมากมายได้ต่อสู้ร่วมกับเขา แล้วพวกเขาหาได้ท้อแท้ไม่ต่อสิ่งที่ได้ประสบแก่พวกเขาในทางของอัลลอฮ์ และพวกเขาหาได้อ่อนกําลังลง และหาได้สยบไม่ และอัลลอฮ์นั้นทรงรักผู้ที่อดทนทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,147,'3:147','และคําพูดของพวกเขามิปรากฏเป็นอื่นใด นอกจากพวกเขากล่าวว่า โอ้พระเจ้า แห่งพวกข้าพระองค์ โปรดได้ทรงอภัยโทษให้แก่พวกข้าพระองค์ด้วยเถิด ซึ่งบรรดาความผิดของพวกข้าพระองค์ และการที่พวกข้าพระองค์กระทําเกินขอบเขตในกิจการของพวกข้าพระองค์ และโปรดทรงให้เท้าของพวกข้าพระองค์มั่นอยู่ และโปรดทรงช่วยเหลือพวกข้าพระองค์ให้ชนะเหนือกลุ่มชนผู้ปฏิเสธศรัทธาด้วย');
INSERT INTO "king_fahad_thai" VALUES(3,148,'3:148','แล้วอัลลอฮ์ก็ทรงประทานให้แก่พวกเขาซึ่งผลตอบแทนแห่งโลกนี้ และผลตอบแทนที่ดีแห่งปรโลก และอัลลอฮ์นั้นทรงรักผู้กระทําดีทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,149,'3:149','โอ้ผู้ศรัทธาทั้งหลาย ! หากพวกเจ้าเชื่อฟังบรรดาผู้ที่ปฏิเสธศรัทธาแล้ว พวกเขาก็จะให้พวกเจ้ากลับส้นเท้าของพวกเจ้าเสีย แล้วพวกเจ้าก็จะกลับเป็นผู้ที่ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(3,150,'3:150','แต่ทว่าอัลลอฮ์ต่างหาก คือผู้ช่วยเหลือพวกเจ้า และพระองค์ทรงเป็นผู้ที่ดีเยี่ยมในบรรดาผู้ช่วยเหลือทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,151,'3:151','เราจะโยนความกลัวเข้าไปในหัวใจของบรรดาผู้ที่ปฏิเสธศรัทธาเหล่านั้น เนื่องจากการที่พวกเขาให้มีภาคีแก่อัลลอฮ์ ซึ่งสิ่งที่พระองค์มิได้ทรงประทานหลักฐานใด ๆ มายืนยันในสิ่งนั้น และที่อยู่ของพวกเขา คือขุมนรก ช่างเลวร้ายจริง ๆ ซึ่งที่อยู่ของบรรดาผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(3,152,'3:152','และแน่นอน อัลลอฮ์ได้ทรงให้สัญญาของพระองค์สมจริงแก่พวกเจ้าแล้ว ขณะที่พวกเจ้าเข่นฆ่าพวกเขา ด้วยอนุมัติของอัลลอฮ์ จนกระทั้งพวกเจ้าขลาดที่จะต่อสู้ และขัดแย้งกันในคําสั่ง และพวกเจ้าได้ฝ่าฝืน หลังจากที่พระองค์ได้ทรงให้พวกเจ้าเห็นสิ่งที่พวกเจ้าชอบแล้ว จากนั้นมีผู้ที่ต้องการโลกนี้ และจากพวกเจ้านั้นมีผู้ที่ต้องการปรโลก(แล้วพระองค์ก็ทรงให้พวกเจ้าหันกลับขากพวกเขาเสีย เพื่อที่จะทรงทดสอบพวกเจ้า และแน่นอนพระองค์ได้ทรงอภัยโทษให้แก่พวกเจ้าแล้ว และอัลลอฮ์นั้นเป็นผู้ทรงมีพระคุณแก่ผู้ศรัทธาทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,153,'3:153','จงรําลึกถึงขณะที่พวกเจ้าหนีเอาตัวรอด และไม่เหลียวมองคนหนึ่งคนใด ทั้ง ๆ ที่ร่อซูลกําลังเรียกพวกเจ้าอยู่ทางเบื้องหลังของพวกเจ้า แล้วพระองค์ก็ได้ทรงตอบแทนพวกเจ้าซึ่งความเศร้าโศกอย่างหนึ่ง พร้อมด้วยความเศร้าโศกอีกอย่างหนึ่ง เพื่อว่าพวกเจ้าจะได้ไม่เสียในสิ่งที่หลุดมือพวกเจ้าไป และไม่เสียใจต่อสิ่งที่ประสบแก่พวกเจ้า และอัลลอฮ์นั้นทรงรอบรู้อย่างละเอียดต่อสิ่งที่พวกเจ้ากระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(3,154,'3:154','แล้วพระองค์ก็ทรงประทานแก่พวกเจ้า ซึ่งความปลอดภัย หลังจากความเศร้าโศกนั้น คือให้มีการงีบหลับครอบคลุมกลุ่มหนึ่งในหมู่พวกเจ้า และอีกกลุ่มหนึ่งนั้น ตัวของพวกเขาเองทําให้พวกเขากกระวนกระวายใจ พวกเขากล่าวหาอัลลอฮ์ โดยปราศจากความเป็นธรรมอย่างพวกสมัยงมงาย (อัลญาฮิลียะฮ์) พวกเขากล่าวว่า มีสิ่งหนึ่งสิ่งใดจากกิจการนั้นเป็นสิทธิของเราบ้างไหม? จงกล่าวเถิด (มุฮัมมัด) ว่า แท้จริง กิจการนั้นทั้งหมดเป็นสิทธิของอัลลอฮ์เท่านั้น พวกเขาปกปิดไว้ในใจของพวกเขา สิ่งซึ่งพวกเขาจะไม่เปิดเผยแก่เจ้า พวกเขากล่าวว่าหากปรากฏว่ามีสิ่งหนึ่งสิ่งใดจากกิจการนั้น เป็นสิทธิของเราแล้วไซร้ พวกเราก็ไม่ถูกฆ่าตายที่นี่ จงกล่าวเถิด (มุฮัมมัด) ว่า แม้ปรากฏว่า พวกท่านอยู่ในบ้านของพวกท่านก็ตาม แน่นอนบรรดาผู้ที่การฆ่าได้ถูกกําหนดแก่พวกเขา ก็จะออกไปสู่ที่นอนตายของพวกเขา และเพื่อที่อัลลอฮ์จะทรงทดสอบสิ่งที่อยู่ในหัวอกของพวกเจ้า และอัลลอฮ์นั้นเป็นผู้ทรงรอบรู้สิ่งที่อยู่หัวอกทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,155,'3:155','แท้จริงบรรดาผู้ที่อยู่ในหมู่พวกเจ้าที่หันหลังหนีในวันที่สองกลุ่ม เผชิญหน้ากันนั้น แท้จริงชัยฎอนต่างหากที่ทําให้พลั้งพลาดไป เนื่องจากบางสิ่งที่พวกเขาได้ประกอบไว้เท่านั้น และแน่นอนอัลลอฮ์ก็ทรงอภัยให้แก่พวกเขาแล้ว แท้จริงอัลลอฮ์นั้นเป็นทรงอภัยโทษ ผู้ทรงหนักแน่น');
INSERT INTO "king_fahad_thai" VALUES(3,156,'3:156','โอ้ผู้ที่ศรัทธาทั้งหลาย! จงอย่าเป็นเช่นบรรดาผู้ที่ปฏิเสธศรัทธา และกล่าวแก่พวกพ้องของพวกเขา ขณะที่เขาเหล่านั้นเดินทางไปในผืนแผ่นดิน หรือขณะที่เขาเหล่านั้นเป็นนักรบว่า หากพวกเขาอยู่ที่เราแล้วพวกเขาก็ย่อมไม่ตาย และไม่ถูกฆ่า เพื่อว่าอัลลอฮ์จะทรงให้เรื่องนั้นเป็นที่เศร้าโศกในหัวใจของพวกเขา และอัลลอฮ์นั้นทรงให้เป็นและให้ตาย และอัลลอฮ์เป็นผู้ทรงเห็นในสิ่งที่พวกเจ้ากระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(3,157,'3:157','และแน่นอน ถ้าหากพวกเจ้าถูกฆ่าในทางของอัลลอฮ์ หรือพวกเจ้าตาย (ในทางของพระองค์) แล้วแน่นอนการอภัยโทษและการเอ็นดุเมตตาจากอัลลอฮ์นั้นดียิ่งกว่าสิ่งที่พวกเขาสะสมกันอยู่');
INSERT INTO "king_fahad_thai" VALUES(3,158,'3:158','และแน่นอน ถ้าหาพวกเจ้าตายไปหรือถูกฆ่า แล้ว แน่นอนยังอัลลอฮ์เท่านั้นที่พวกเจ้าจะถูกนําไปชุมนุม');
INSERT INTO "king_fahad_thai" VALUES(3,159,'3:159','เนื่องด้วยความเมตตาจากอัลลอฮ์นั่นเอง เจ้า(มุฮัมมัด) จึงได้สุภาพอ่อนโยนแก่พวกเขา และถ้าหากเจ้าเป็นผู้ประพฤติหยาบช้า และมีใจแข็งกระด้างแล้วไซร้ แน่นอนพวกเขาก็ย่อมแยกตัวออกไปจากรอบ ๆ เจ้ากันแล้ว ดังนั้นจงอภัยให้แก่พวกเขาเถิด และจงขออภัยให้แก่พวกเขาด้วย และจงปรึกษาหารือกับพวกเขาในกิจการทั้งหลาย ครั้นเมื่อเจ้าได้ตัดสินใจแล้ว ก็จงมอบหมายแด่อัลลอฮ์เถิด แท้จริงอัลลอฮ์ทรงรักใคร่ผู้มอบหมายทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,160,'3:160','หากว่าอัลลอฮ์ทรงช่วยเหลือพวกเจ้าก็ไม่มีผู้ใดชนะพวกเจ้าได้ และหากพระองค์ทรงทอดทิ้งพวกเจ้าแล้ว ก็ผู้ใดเล่าจะช่วยเหลือพวกเจ้าได้หลังจากพระองค์ และแด่อัลลอฮ์นั้น ผู้ศรัทธาทั้งหลายจงมอบหมายเถิด');
INSERT INTO "king_fahad_thai" VALUES(3,161,'3:161','และไม่ปรากฏแก่นะบีคนใดที่จะยักยอก(ทรัพย์เชลย) และผู้ใดยักยอกแล้ว เขาก็จะนําสิ่งที่เขายักยอกนั้นมาในวันกิยามะฮ์แล้วแต่ละคนจะได้รับการตอบแทนอย่างครบถ้วน ตามที่เขาได้แสวงหาไว้ โดยที่พวกเขาจะไม่ได้รับความอยุติธรรม');
INSERT INTO "king_fahad_thai" VALUES(3,162,'3:162','ผู้ที่ปฏิบัติตาม ความปิติยินดีของอัลลอฮ์นั้น จะเหมือนกับผู้ที่ได้นําความกริ้วโกรธจากอัลลอฮ์กลับไปกระนั้นหรือ ? และที่อยู่ของเขานั้นคือ ญะฮันนัม และเป็นที่กลับอันเลวร้ายยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(3,163,'3:163','พวกเขาเหล่านั้นมีหลายระดังขั้น ณ อัลลอฮ์ และอัลลอฮ์นั้นเป็นผู้ทรงเห็นในสิ่งที่พวกเขากระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(3,164,'3:164','แน่นอนยิ่ง อัลลอฮ์นั้นทรงมีพระคุณแก่ผู้ศรัทธาทั้งหลาย โดยที่พระองค์ได้ทรงส่งร่อซูลคนหนึ่งจากพวกเขาเองมาในหมู่พวกเขาโดดยที่เขาจะได้อ่านบรรดาโองการของพระองค์ให้พวกเขาฟัง และจะทําให้พวกเขาสะอาดและจะสอนคัมภีร์ และความรู้เกี่ยวกับข้อปฏิบัติในบัญญัติศาสนาแก่พวกเขาด้วย และแท้จริงเมื่อก่อนนั้นพวกเขาเคยอยู่ในความหลงผิดอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(3,165,'3:165','และเมื่อมีภยันตรายหนึ่ง ประสบแก่พวกเจ้า ทั้ง ๆ ที่พวกเจ้าได้ให้ประสบแก่พวกเขามาแล้วถึงสองเท่าแห่งภยันตรายนั้น พวกเจ้าก็ยังกล่าวว่าสิ่งนี้มาจากไหนกระนั้นหรือ ? จงกล่าวเถิด(มุฮัมมัด) ว่า มันมาจากที่ตัวของพวกท่านเอง แท้จริงอัลลอฮ์เป็นผู้ทรงเดชานุภาพเหนือทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(3,166,'3:166','และสิ่งที่ประสบแก่พวกเจ้า ในวันที่สองกลุ่มเผชิญกันนั้นก็โดยอนุมัติของอัลลอฮ์ และเพื่อที่พระองค์จะทรงรู้ ผู้ศรัทธาทั้งหลายนั้นเอง');
INSERT INTO "king_fahad_thai" VALUES(3,167,'3:167','และเพื่อพระองค์จะทรงรู้บรรดาผู้ที่กลับกลอก ด้วย และได้ถูกกล่าวแก่พวกเขาว่าจงมากันเถิด จงต่อสู้ในทางของอัลลอฮ์กัน หรือไม่ก็จงป้องกัน พวกเขากล่าวว่า หากเรารู้ว่ามีการสู้รบกันแล้ว แน่นอนเราก็ตามพวกท่านไปแล้ว ในวันนั้น พวกเขาใกล้แก่การปฏิเสธศรัทธายิ่งกว่าพวกเขามีศรัทธา พวกเขาจะกล่าวด้วยปากของพวกเขา สิ่งที่ไม่ใช่อยู่ในหัวใจของพวกเขา และอัลลอฮ์นั้นทรงรู้ยิ่งใยสิ่งที่พวกเขาปกปิดกัน');
INSERT INTO "king_fahad_thai" VALUES(3,168,'3:168','บรรดาผู้ที่พูดเกี่ยวแก่พี่น้องของพวกเขา โดยที่พวกเขานั่งเฉยอยู่ ว่าถ้าหากพวกเขาเชื่อฟังเรา พวกเขาก็ไม่ถูกฆ่า จงกล่าวเถิด(มุอัมมัด) ว่า พวกท่านจงป้องกันความตายให้พ้นจากตัวของพวกท่านเถิด หากพวกท่านพูดจริง');
INSERT INTO "king_fahad_thai" VALUES(3,169,'3:169','และเจ้าจงอย่าได้คิดเป็นอันขาดว่า บรรดาผู้ที่ถูกฆ่าในทางของอัลลอฮ์นั้นตาย มิได้ พวกเขายังมีชีวิตอยู่ ณ พระเจ้าของพวกเขาในสภาพที่ได้รับปัจจัยยังชีพ');
INSERT INTO "king_fahad_thai" VALUES(3,170,'3:170','ปลาบปลื้มต่อสิ่ง ที่อัลลอฮ์ทรงประทานให้แก่พวกเขา จากความกรุณาของพระองค์ และปิติยินดีต่อบรรดาผู้ที่ยังมาไม่ทันพวกเขาซึ่งอยู่เบื้องหลังพวกเขา ว่า ไม่มีความกลัวใด ๆ แก่พวกเขาและทั้งพวกเขาจะไม่เสียใจ');
INSERT INTO "king_fahad_thai" VALUES(3,171,'3:171','พวกเขาปิติยินดีต่อสิ่งอํานวยความสุขจากอัลลอฮ์ และความกรุณา(จากพระองค์)ด้วย และแท้จริงอัลลอฮ์นั้นจะไม่ทรงให้สูญหายซึ่งรางวัลของผู้ศรัทธาทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,172,'3:172','คือบรรดาผู้ที่ตอบรับอัลลอฮ์ และร่อซูลหลังจากที่บาดแผลได้ประสบแก่พวกเขา สําหรับบรรดาผู้กระทําดีในหมู่พวกเขาและมีความยําเกรงนั้น คือรางวัลอันยิ่งใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(3,173,'3:173','บรรดาที่ ผู้คน ได้กล่าวแก่พวกเขาว่า แท้จริงมีผู้คน ได้ชุมนุมสําหรับพวกท่าน ดังนั้นพวกท่านจงกลัวพวกเขาเถิดแล้วมัน ได้เพิ่มการอีมานแก่พวกเขา และพวกเขากล่าวว่าอัลลอฮ์นั้นเป็นผู้ที่พอเพียงแก่เราแล้ว และเป็นผู้รับมอบหมายที่ดีเยี่ยม');
INSERT INTO "king_fahad_thai" VALUES(3,174,'3:174','แล้วพวกเขาได้กลับมา พร้อมด้วยความกรุณาจากอัลลอฮ์ และความโปรดปราน(จากพระองค์) โดยมิได้มีอันตรายใด ๆ ประสบแก่พวกเขา และพวกเขาได้ปฏิบัติตามความพอพระทัยของอัลลอฮ์ และอัลลอฮ์คือผู้ทรงโปรดปรานที่ยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(3,175,'3:175','แท้จริงชัยฏอนนั้น เพียงขู่ได้เฉพาะบรรดาผู้ที่ปฏิบัติตามมันเท่านั้น ดังนั้นพวกเจ้าจงอย่ากลัวพวกเขา และจงกลัวข้าเถิด หากพวกเจ้าเป็นผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(3,176,'3:176','และจงอย่าให้บรรดาผู้รีบเร่งกันในการปฏิเสธศรัทธา เป็นที่เสียใจแก่เจ้า แท้จริงพวกเขาจะไม่ก่อ่ให้เกิดอันตรายแก่อัลลอฮ์ ได้แต่อย่างใดเลย อัลลอฮ์นั้นทรงต้องการที่จะไม่ให้มีส่วนได้ใด ๆ แก่พวกเขาในปรโลก และสําหรับพวกเขานั้นคือการลงโทษอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(3,177,'3:177','แท้จริงบรรดาผู้ที่ซื้อการกุฟุร์ ด้วยการอีมาน นั้น พวกเขาจะไม่ก่อให้เกิดอันตรายแก่อัลลอฮ์ได้แต่อย่างใดเลย และสําหรับพวกเขานั้นคือการลงโทษอันเจ็บแสบ');
INSERT INTO "king_fahad_thai" VALUES(3,178,'3:178','และบรรดาผู้ที่ปฏิเสธศรัทธานั้น จงอย่าได้คิดเป็นอันขาดว่า ที่เราประวิง ให้แก่พวกเขานั้น เป็นการดีแก่ตัวของพวกเขาแท้จริงที่เราประวิงให้แก่พวกเขานั้น เพื่อพวกเขาจะได้เพิ่มพูนซึ่งบาปกรรม เท่านั้น และสําหรับพวกเขานั้น คือการลงโทษอันต่ําช้า');
INSERT INTO "king_fahad_thai" VALUES(3,179,'3:179','ใช่ว่าอัลลอฮ์จะทรงทอดทิ้งบรรดาผู้ศรัทธาไว้ในสภาพที่พวกเจ้ากําลังเป็นอยู่ก็หาไม่ จนกว่าพระองค์จะทรงจําแนกผู้ที่เลวออกจากผู้ที่ดีเท่านั้น และใช่ว่าอัลลอฮ์จะทรงให้พวกเจ้ามองเห็นสิ่งเร้นลับก็หาไม่ แต่ทว่าอัลลอฮ์นั้นจะทรงคัดเลือกจากบรรดาร่อซูลของพระองค์ผู้ที่พระองค์ทรงประสงค์ ดังนั้นพวกเจ้าจงศรัทธาต่ออัลออฮ์ และบรรดาร่อซูลของพระองค์เถิด และหากพวกเจ้าศรัทธาและยําเกรงแล้ว สําหรับพวกเจ้าก็คือ รางวัลอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(3,180,'3:180','และบรรดาผู้ที่ตระหนี่ในสิ่งที่อัลลอฮ์ได้ทรงประทานให้แก่พวกเขาจากความกรุณาของพระองค์นั้น จงอย่าได้คิดเป็นอันขาดว่ามันเป็นการดีแก่พวกเขา หากแต่มันเป็นความชั่ว แก่พวกเขา ซึ่งพวกเขาจะถูกคล้องสิ่งที่พวกเขาได้ตระหนี่มันไว้ในวันกิยามะฮ์ และสําหรับอัลลอฮ์นั้น คือมรดกแห่งบรรดาชั้นฟ้า และแผ่นดิน และอัลลอฮ์นั้นเป็นผู้ทรงรอบรู้อย่างละเอียดในสิ่งที่พวกเจ้ากระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(3,181,'3:181','แน่นอนยิ่ง อัลลอฮ์ทรงได้ยินคําพูดของบรรดาผู้ที่ กล่าวว่า แท้จริงอัลลอฮ์นั้นเป็นผู้ยากจน และพวกเรานั้นเป็นผู้มั่งมี เรา จะจารึกสิ่งที่พวกเขาได้กล่าวไว และการที่พวกเขาฆ่าบรรดานะบี โดยปราศจากความเป็นธรรมด้วยและเราจะกล่าวว่าพวกเจ้าจงลิ้มการลงโทษแห่งเปลว เพลิงเถิด');
INSERT INTO "king_fahad_thai" VALUES(3,182,'3:182','นั่น ก็เพราะสิ่งที่มือของพวกเจ้าได้ประกอบไว้ก่อน และแท้จริงอัลลอฮ์นั้นมิใช่ผู้อธรรมแก่ปวงบ่วงทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,183,'3:183','บรรดาผู้ที่กล่าว ว่า แท้จริงอัลลอฮ์นั้นได้ทรงสั่งเสียแก่เราว่า เราจะไม่ศรัทธาแก่ร่อซูลคนใด จนกว่าเขาจะนํามาแก่เรา ซึ่งสิ่งพลีแด่อัลลอฮ์อย่างใดอย่างหนึ่งซึ่งจะมีไฟกินมัน จงกล่าวเถิด (มุฮัมมัด) ว่า แท้จริงได้มีบรรดาร่อซูลก่อนจากฉันได้นําบรรดาหลักฐานอันชัดเจนมายังพวกท่านแล้ว และได้นําสิ่งที่พวกท่านได้กล่าวไว้ด้วย แล้วไฉนเล่า พวกท่านจึงได้ฆ่าพวกเขา หากพวกท่านพูดจริง');
INSERT INTO "king_fahad_thai" VALUES(3,184,'3:184','แล้วหากพวกเขาปฏิเสธเจ้า ก็แท้จริงนั้น บรรดาร่อซูลก่อนหน้าเจ้าก็ได้ถูกปฏิเสธมาแล้วซึ่งเขาเหล่านั้นได้นําบรรดาหลักฐานอันชัดเจน บรรดาคัมภีร์ซะบูร์ และคัมภีร์ที่ให้แสงสว่าง มาด้วย');
INSERT INTO "king_fahad_thai" VALUES(3,185,'3:185','แต่ละชีวิตนั้น จะได้ลิ้มรสแห่งความตาย และแท้จริงที่พวกเจ้าจะได้รับรางวัลของพวกเจ้าโดยครบถ้วนนั้น คือวันปรโลก แล้วผู้ใดที่ถูกให้ห่างไกลจากไฟนรก และถูกให้เข้าสวรรค์แล้วไซร้ แน่นอน เขาก็ชนะแล้ว และชีวิตความเป็นอยู่แห่งโลกนี้นั้น มิใช่อะไรอื่นนอกจากสิ่งอํานวยประโยชน์แห่งการหลอกลวง เท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(3,186,'3:186','แน่นอนยิ่งพวกเจ้าจะถูกทดสอบ ในทรัพย์สมบัติของพวกเจ้าและตัวของพวกเจ้าและแน่นอนยิ่งพวกเจ้าจะได้ยินจากบรรดาผู้ที่ได้รับคัมภีร์ก่อนหน้าพวกเจ้า และบรรดาผู้ที่ให้มีภาคีขึ้น (แก่อัลลอฮ์) ซึ่งการก่อความเดือดร้อนอันมากมาย และหากพวกเจ้าอดทนและยําเกรงแล้ว แท้จริงนั่นคือส่วนหนึ่งจากกิจการที่เด็ดเดี่ยว');
INSERT INTO "king_fahad_thai" VALUES(3,187,'3:187','และจงรําลึกถึงขณะที่อัลลอฮฺทรงเอาคํามั่นสัญญาจากบรรดาผู้ที่ได้รับคัมภีร์ว่า แน่นอนยิ่งพวกเจ้าจะต้องแจกแจงคัมภีร์ นั้นให้แจ่มแจ้งแก่ประชาชนทั้งหลาย และพวกเจ้าจะต้องไม่ปิดบัง มัน แล้วพวกเขาก็เหวี่ยงมันไว้เบื้องหลังพวกเขา และได้แลกเปลี่ยนกับราคาอันเล็กน้อย ช่างเลวร้ายแท้ ๆ สิ่งที่พวกเขาแลกเปลี่ยนมา');
INSERT INTO "king_fahad_thai" VALUES(3,188,'3:188','เจ้าจงอย่าคิดเป็นอันขาดว่า บรรดาผู้ที่ปิติยินต่อสิ่งที่พวกเขากระทํา และชอบที่จะได้รับการาชมเชยในสิ่งที่พวกเขามิได้กระทํานั้น (จะรอดพ้นการลงโทษไปได้) ดังนั้นเจ้าจงอย่าคิดเป็นอันขาดว่า พวกเขาจะมีทางรอดพ้นจากการลงโทษไปได้และสําหรับพวกเขานั้นคือ การลงโทษอันเจ็บแสบ');
INSERT INTO "king_fahad_thai" VALUES(3,189,'3:189','และอํานาจแห่งบรรดาชั้นฟ้า และแผ่นดินนั้นเป็นสิทธิของอัลลอฮฺ และอัลลอฮฺนั้นทรงเดชานุภาพ เหนือทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(3,190,'3:190','แท้จริงในการสร้างบรรดาชั้นฟ้าและแผ่นดิน และการที่กลางวันและกลางคืนตามหลังกันนั้น แน่นอนมีหลายสัญญาณสําหรับผู้มีปัญญา');
INSERT INTO "king_fahad_thai" VALUES(3,191,'3:191','คือบรรดาผู้ที่รําลึกถึงอัลลอฮ์ ทั้งในสภาพยืน และนั่ง และในสภาพที่นอนตะแคง และพวกเขาพินิจพิจารณากันในการสร้างบรรดาชั้นฟ้า และแผ่นดิน (โดยกล่าวว่า) โอ้พระเจ้าของพวกเข้าพระองค์ พระองค์มิได้ทรงสร้างสิ่งนี้มาโดยไร้สาระ มหาบริสุทธิ์พระองค์ท่าน โปรดทรงคุ้มครองพวกข้าพระองค์ให้พ้นจากการลงโทษแห่งไฟนรกด้วยเถิด');
INSERT INTO "king_fahad_thai" VALUES(3,192,'3:192','โอ้พระเจ้าของพวกข้าพระองค์แท้จริงผู้ใดที่พระองค์ทรงให้เข้าไฟนรก แน่นอนพระองค์ก็ยังความอัปยศแก่เขาแล้ว และสําหรับบรรดาผู้อธรรมนั้น ย่อมไม่มีผู้ช่วยเหลือใด ๆ');
INSERT INTO "king_fahad_thai" VALUES(3,193,'3:193','โอ้พระเจ้าของพวกข้าพระองค์! แท้จริงพวกข้าพระองค์ได้ยินผู้ประกาศเชิญชวนผู้หนึ่ง กําลังประกาศเชิญชวนให้มีการศรัทธาว่า ท่านทั้งหลายจงศรัทธาต่อพระเจ้าของพวกเจ้าเถิด และพวกข้าพระองค์ก็ศรัทธากัน โอ้พระเจ้าของพวกข้าพระองค์! โปรดทรงอภัยแก่พวกข้าพระองค์ด้วย ซึ่งบรรดาโทษของพวกข้าพระองค์และโปรดลบล้างให้พ้นจากพวกข้าพระองค์ ซึ่งบรรดาความผิดของพวกข้าพระองค์ และโปรดทรงให้พวกข้าพระองค์สิ้นชีวิตโดยร่วมอยู่กับบรรดาผู้ที่เป็นคนดีด้วยเถิด');
INSERT INTO "king_fahad_thai" VALUES(3,194,'3:194','โอ้พระผู้เป็นเจ้าของพวกข้าพระองค์! และได้โปรดประทานแก่พวกข้าพระองค์สิ่งที่พระองค์ได้สัญญาไว้แก่พวกข้าพระองค์ โดยผ่านบรรดาร่อซูลของพระองค์ และโปรดอย่าได้ทรงยังความอัปยศแก่พวกข้าพระองค์ในวันปรโลกเลย แท้จริงพระองค์นั้นไม่ทรงผิดสัญญา');
INSERT INTO "king_fahad_thai" VALUES(3,195,'3:195','แล้วพระเจ้าของพวกเขาก็ตอบรับพวกเขาว่า แท้จขริงข้าจะไม่ให้สูญเสียซึ่งงานของผู้ทํางานคนหนึ่งคนใดในหมู่พวกเจ้าไม่ว่าจะเป็นชายหรือหญิงก็ตาม โดยที่บางส่วนของพวกเจ้านั้นมาจากอีกบางส่วน บรรดาผู้ทีอพยพ และที่ถูกขับไล่ให้ออกจากหมู่บ้านของพวกเขา และได้รับความเดือดร้อนในทางของข้า และได้ต่อสู้และถูกฆ่าตายนั้น แน่นอนข้าจะลบล้างให้พ้นจากพวกเขา ซึ่งบรรดาความผิดของพวกเขา และแน่นอนข้าจะให้พวกเขาเข้าบรรดาสวนสวรรค์ซึ่งมีบรรดาแม่น้ําไหลอยู่เบื้องล่างของสวนสวรรค์เหล่านั้น ทั้งนี้เป็นรางวัลตอบแทนจากอัลลอฮ์ และอัลลอฮ์นั้น ณ พระองค์มีการตอบแทนอันดีงาม');
INSERT INTO "king_fahad_thai" VALUES(3,196,'3:196','อย่าให้การเคลื่อนไหว ของบรรดาผู้ปฏิเสธศรัทธาในเมืองหลวง เจ้าได้เป็นเอันขาด');
INSERT INTO "king_fahad_thai" VALUES(3,197,'3:197','มันเป็นสิ่งอํานวยประโยชน์เล็กน้อยเท่านั้น แล้วที่อยู่ของพวกเขานั้น คือ ญะฮันนัม และช่างเป็นที่พักนอนที่เลวร้ายจริง ๆ');
INSERT INTO "king_fahad_thai" VALUES(3,198,'3:198','แต่บรรดาผู้ที่ยําเกรงพระเจ้าของพวกเขานั้น สําหรับพวกเขาคือบรรดาสวนสวรรค์ซึ่งมีแม่น้ําหลายสายไหลอยู่เบื้องล่างของสวนสวรรค์เล่านั้น โดยที่พวกเขาจะพํานักอยู่ในสวนสวรรค์เหล่านั้นตลอดกาล ทั้งนี้เป็นสถานที่รับรองที่มาจากอัลลอฮ์ และสิ่งที่มีอยู่ ณ อัลลอฮ์นั้น คือสิ่งที่ดียิ่งสําหรับผู้ที่เป็นคนดีทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(3,199,'3:199','และแท้จริงในหมู่ผู้ได้รับคัมภีร์นั้นมีผู้ที่ศรัทธาต่ออัลลอฮ์ และสิ่งที่ถูกประทานลงมาแก่พวกเจ้า และสิ่งที่ถูกประทานลงมาแก่พวกเขา ในฐานะผู้นอบน้อมต่ออัลลอฮ์ โดยที่พวกเขาจะไม่แลกเปลี่ยนโองการของอัลลอฮ์กับราคาอันเล็กน้อย ชนเหล่านี้แหละพวกเขาจะได้รับรางวัลของพวกเขา ณ พระผู้เป็นเจ้าของพวกเขาแท้จริงอัลลอฮ์นั้น เป็นผู้ทรงรวดเร็วในการชําระสอบสวน');
INSERT INTO "king_fahad_thai" VALUES(3,200,'3:200','โอ้บรรดาผู้ศรัทธาทั้งหลาย ! จงมีความอดทน และจงต่างอดทนซึ่งกันและกันเถิด และจงประจําอยู่ชายแดน และพึงกลัวเกรงอัลลอฮ์เถิด เพื่อว่าพวกเจ้าจะได้รับความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(4,1,'4:1','มนุษยชาติทั้งหลาย ! จงยําเกรงพระเจ้าของพวกเจ้าที่ได้บังเกิดพวกเจ้ามาจากชีวิตหนึ่ง และได้ทรงบังเกิดจากชีวิตนั้นซึ่งคู่ครองของเขา และได้ทรงให้แพร่สะพัดไปจากทั้งสองนั้น ซึ่งบรรดาชายและบรรดาหญิงอันมากมาย และจงยําเกรงอัลลอฮฺที่พวกเจ้าต่างขอกัน ด้วยพระองค์ และพึงรักษาเครือญาติ แท้จริงอัลลอฮฺทรงสอดส่องดูพวกเจ้าอยู่เสมอ');
INSERT INTO "king_fahad_thai" VALUES(4,2,'4:2','และจงให้แก่บรรดาเด็กกําพร้า ซึ่งทรัพย์สมบัติของพวกเขา และจงอย่าเปลี่ยนเอาของเลว ด้วยของดี และจงอย่ากินทรัพย์ของพวกเขาร่วมกับทรัพย์ของพวกเจ้า แท้จริงมันเป็นบาปอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(4,3,'4:3','และหากพวกเจ้าเกรงว่าจะไม่สามารถให้ความยุติธรรมในบรรดาเด็กกําพร้าได้ ก็จงแต่งงานกับผู้ที่ดีแก่พวกเจ้า ในหมู่สตรี สองคน หรือสามคน หรือสี่คน แต่ถ้าพวกเจ้าเกรงว่าพวกเจ้าจะให้ความยุติธรรมไม่ได้ ก็จงมีแต่หญิงเดียว หรือไม่ก็หญิงที่มือขวาของพวกเจ้าครอบครองอยู่ นั้นเป็นสิ่งที่ใกล้ยิ่งกว่าในการที่พวกเจ้าจะไม่ลําเอียง');
INSERT INTO "king_fahad_thai" VALUES(4,4,'4:4','และจงให้แก่บรรดาหญิงซึ่งมะฮัรของนาง ด้วยความเต็มใจ แต่ถ้านางเห็นชอบที่จะให้สิ่งหนึ่งแก่พวกเจ้าจากมะฮัรนั้นแล้ว ก็จงบริโภคสิ่งนั้นด้วยความเอร็ดอร่อยและโอชา');
INSERT INTO "king_fahad_thai" VALUES(4,5,'4:5','และจงอย่าให้แก่บรรดาผู้ที่โง่เขลา ซึ่งทรัพย์ของพวกเจ้า ที่อัลลอฮฺได้ทรงให้เป็นสิ่งค้ําจุนแก่พวกเจ้า และจงให้ปัจจัยยังชีพและเครื่องนุ่งห่มแก่พวกเขาในทรัพย์นั้น และจงกล่าววาจาแก่พวกเขาอย่างดี');
INSERT INTO "king_fahad_thai" VALUES(4,6,'4:6','และจงทดสอบบรรดาเด็กกําพร้าดู จนกระทั่งพวกเขาบรรลุวัยสมรส ถ้าพวกเจ้าเห็นว่าในหมู่พวกเขานั้นมีไหวพริบรู้ผิดรู้ถูกแล้ว ก็จงมอบทรัพย์ของพวกเขาให้แก่พวกเขาไป และจงอย่ากินทรัพย์นั้นโดยฟุ่มเฟือย และรีบเร่ง ก่อนที่พวกเขาจะเติบโต และผู้ใดเป็นผู้มั่งมีก็จงงดเว้นเสีย และผู้ใดเป็นผู้ยากจนก็จงกินโดยชอบธรรม ครั้นเมื่อพวกเจ้าได้มอบทรัพย์ของพวกเขาให้แก่พวกเขาไปแล้ว ก็จงให้มีพยานยืนยันแก่พวกเขา และเพียงพอแล้วที่อัลลอฮฺเป็นผู้ทรงสอบสวน');
INSERT INTO "king_fahad_thai" VALUES(4,7,'4:7','สําหรับบรรดาชายนั้น มีส่วนได้รับจากสิ่งที่ ผู้บังเกิดเกล้าทั้งสอง และบรรดาญาติที่ใกล้ชิดได้ทิ้งไว้ และสําหรับบรรดาหญิงนั้นก็มีส่วนได้รับจากสิ่งที่ผู้บังเกิดเกล้าทั้งสองและบรรดาญาติที่ใกล้ชิดได้ทิ้งไว้ ซึ่งสิ่งนั้นจะน้อยหรือมากก็ตาม เป็นส่วนได้รับที่ถูกําหนดอัตราส่วนไว้');
INSERT INTO "king_fahad_thai" VALUES(4,8,'4:8','และบรรดาญาติที่ใกล้ชิด และบรรดาเด็กกําพร้า และบรรดาผู้ที่ขัดสนมาร่วมอยู่ด้วยในการแบ่งมรดก ก็จงปันส่วนหนึ่งจากสิ่งนั้น ให้เป็นปัจจัยยังชีพแก่พวกเขา และจงกล่าวแก่พวกเขาอย่างดี');
INSERT INTO "king_fahad_thai" VALUES(4,9,'4:9','และพึงวิตกเถิด บรรดาผู้ที่หากพวกเขาละทิ้งลูกๆ ที่ยังอ่อนแออยู่ไว้เบื้องหลังของพวกเขา ซึ่งพวกเขากลัวว่าจะมีอันตรายเกิดขึ้นแก่ลูก ๆ ของพวกเขานั้น พวกเขาจงเกรงกลัวอัลลอฮฺเถิด และจงกล่าววาจาอย่างเที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(4,10,'4:10','แท้จริงบรรดาผู้ที่กินทรัพย์ของบรรดาเด็กกําพร้าด้วยความอธรรมนั้น แท้จริงพวกเขากินไฟเข้าไปในท้องของพวกเขาต่างหากและพวกเขาก็จะเข้าไปสู่เปลวเพลิง');
INSERT INTO "king_fahad_thai" VALUES(4,11,'4:11','อัลลอฮฺได้ทรงสั่งพวกเจ้าไว้ในลูก ๆของพวกเจ้าว่า สําหรับเพศชายนั้นจะได้รับ เท่ากับส่วนได้ของเพศหญิงสองคน แต่ถ้าลูกๆ เป็นหญิงเกินกว่าสองคน พวกนางก็จะได้สองในสามของสิ่งที่เขา ได้ทิ้งไว้ และถ้าลูกเป็นหญิงคนเดียว นางก็จะได้ครึ่งหนึ่ง และสําหรับบิดาและมารดาของเขานั้น แต่ละคนในทั้งสองนั้นจะได้หนึ่งในหกจากสิ่งที่เขา ได้ทิ้งไว้หากเขามีบุตร แต่ถ้าเขาไม่มีบุตรและมีบิดามารดาของเขาเท่านั้นที่รับมรดกของเขาแล้ว มารดาของเขาก็ได้รับหนึ่งในสาม ถ้าเขามีพี่น้องหลายคน มารดาของเขาก็ได้รับหนึ่งในหกทั้งนี้หลังจากพินัยกรรมที่เขาได้สั่งเสียมันไว้หรือหลังจากหนี้สิน บรรดาบิดาของพวกเจ้าและลูก ๆ ของพวกเจ้านั้น พวกเจ้าไม่รู้ดอกว่าฝ่ายไหนในพวกเขานั้นเป็นผู้ที่มีคุณประโยชน์แก่พวกเจ้าใกล้กว่ากัน ทั้งนี้เป็นบัญญัติที่มาจากอัลลอฮฺ แท้จริงอัลลอฮฺเป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(4,12,'4:12','และสําหรับพวกเจ้านั้นจะได้รับครึ่งหนึ่งของสิ่งที่บรรดาภรรยาของพวกเจ้าได้ทิ้งไว้ หากมิได้ปรากฏว่าพวกนางมีบุตร แต้ถ้าพวกนางมีบุตรพวกเจ้าก็จะได้รับหนึ่งในสี่จากสิ่งที่พวกนางได้สั่งเสียมันไว้ หรือหลังจากหนี้สิน และสําหรับพวกนางนั้นจะได้รับหนึ่งในสี่จากสิ่งที่พวกเจ้าได้ทิ้งไว้ หากมิปรากฏว่าพวกเจ้ามีบุตร พวกนางก็จะได้รับหนึ่งในแปดจากสิ่งที่พวกเจ้าทิ้งไว้ ทั้งนี้หลังจากพินัยกรรมที่พวกเจ้าสั่งเสียมันไว้ หรือหลังจากหนี้สิน และถ้ามีชายคนหนึ่งหรือหญิงคนหนึ่งถูกรับมรดก ในฐานะเป็นผู้ที่ไม่มีบิดาและบุตร แต่เขามีพี่ชายหรือน้องชายคนหนึ่ง หรือมีพี่สาวหรือน้องสาวคนหนึ่งแล้ว แต่ละคนจากสองคนนั้น จะได้รับหนึ่งในหก แต่ถ้าพี่น้องของเขามีมากกว่านั้น พวกเขาก็เป็นผู้รับร่วมกันในหนึ่งในสาม ทั้งนี้หลังจากพินัยกรรมที่ถูกสั่งเสียไว้หรือหลังจากหนี้สินโดยมิใช่สิ่งที่นํามาซึ่งผลร้ายใด ๆ เป็นคําสั่งที่มาจากอัลลอฮฺ และอัลลอฮฺเป็นผู้ทรงรอบรู้ผู้ทรงหนักแน่น');
INSERT INTO "king_fahad_thai" VALUES(4,13,'4:13','เหล่านั้นแหละคือขอบเขต ของอัลลอฮฺ และผู้ใดที่เชื่อฟังอัลลอฮฺ และร่อซูลของพระองค์แล้ว พระองค์ก็จะทรงให้เขาเข้าบรรดาสวนสวรรค์ซึ่งมีแม่น้ําหลายสายไหลอยู่เบื้องล่างของมัน โดยที่พวกเขาจะพํานักอยู่ในสวนสวรรค์เหล่านั้นตลอดกาลและนั่นคือชัยชนะอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(4,14,'4:14','และผู้ใดฝ่าฝืนอัลลอฮฺ และร่อซูลของพระองค์ และละเมิดขอบเขตของพระองค์แล้วไซร้ พระองค์ก็จะทรงให้เขาเข้านรก โดยที่เขาจะอยู่ในนรกนั้นตลอดกาล และเขาจะได้รับการลงโทษที่ยังความอัปยศให้(แก่เขา)');
INSERT INTO "king_fahad_thai" VALUES(4,15,'4:15','และบรรดาผู้ที่กระทําสิ่งลามก จากในหมู่สตรีของพวกเจ้านั้น จงให้มีพยานสี่คนของพวกเจ้ายืนยันนางเหล่านั้น ถ้าพวกเขายืนยันแล้ว ก็จงกักขังนางเหล่านั้นไว้ในบ้าน จนกว่าความตายจะพรากพวกนาง หรือไม่ก็จะทรงให้มีทางหนึ่งสําหรับพวกนาง');
INSERT INTO "king_fahad_thai" VALUES(4,16,'4:16','และชายสองคนในหมู่ของพวกเจ้าที่กระทําการลามก นั้น พวกเจ้าจงลงโทษเขาเสียทั้งสองคน หากทั้งสองสํานึกผิดกลับเนื้อกลับตัวและปรับปรุงแก้ไขแล้ว ก็จงระงับการลงโทษเขาทั้งสองเสีย แท้จริงอัลลอฮฺนั้นเป็นผู้ทรงอภัยโทษ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(4,17,'4:17','แท้จริงการสํานึกผิดกลับเนื้อกลับตัวที่อัลลอฮฺจะทรงรับนั้นคือสําหรับบรรดาผู้ที่กระทําความชั่วโดยรู้เท่าไม่ถึงการณ์เท่านั้นแล้วพวกเขาสํานึกผิดกลับเนื้อกลับตัวในเวลาอันใกล้ ชนเหล่านี้และอัลลอฮฺจะทรงอภัยโทษให้แก่พวกเขา และอัลลอฮฺเป็นผู้ทรงรอบรู้ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(4,18,'4:18','การสํานึกผิดกลับเนื้อกลับตัว(ที่อัลลอฮฺจะทรงรับ) นั้นมิใช่สําหรับบรรดาผู้ที่กระทําความชั่วต่างๆ จนกระทั่งเมื่อความตายได้มายังคนหนึ่งคนใดในพวกเขาแล้วเขาก็กล่าวว่า บัดนี้แหละข้าพระองค์ขอสํานึกผิดกลับเนื้อกลับตัว และก็มิใช่สําหรับบรรดาผู้ที่ตาย ในขณะที่พวกเขาเป็นผู้ปฏิเสธศรัทธาด้วย ชนเหล่านี้เราได้เตรียมไว้แล้วสําหรับพวกเขาซึ่งการลงโทษอันเจ็บแสบ');
INSERT INTO "king_fahad_thai" VALUES(4,19,'4:19','ผู้ศรัทธาทั้งหลาย! ไม่อนุมัติแก่พวกเจ้าการที่พวกเจ้าจะเอาบรรดาหญิงเป็นมรดกด้วยการบังคับ และไม่อนุมัติเช่นเดียวกันการที่พวกเจ้าจะขัดขวางบรรดานางเพื่อพวกเจ้าจะเอาบางส่วนของสิ่งที่พวกเจ้าได้ให้แก่พวกนาง นอกจากว่าพวกนางจะกระทําสิ่งลามก อันชัดแจ้งเท่านั้น และจงอยู่ร่วมกับพวกนางด้วยดี หากพวกเจ้าเกลียดพวกนาง ก็อาจเป็นไปได้ว่า การที่พวกเจ้าเกลียดสิ่งหนึ่งขณะเดียวกันอัลลอฮฺก็ทรงให้มีในสิ่งนั้น ซึ่งความดีอันมากมาย');
INSERT INTO "king_fahad_thai" VALUES(4,20,'4:20','และหากพวกเจ้าต้องการเปลี่ยนคู่ครองคนหนึ่งแทนที่ของคู่ครองอีกคนหนึ่ง และพวกเจ้าได้ให้แก่นางหนึ่งในหมู่นางเหล่านั้น ซึ่งทรัพย์อันมากมายก็ตาม ก็จงอย่าได้เอาสิ่งใดจากทรัพย์นั้นคืน พวกเจ้าจะเอามันคืนด้วยการอุปโลกน์ความเท็จและการกระทําบาปอันชัดเจนกระนั้นหรือ');
INSERT INTO "king_fahad_thai" VALUES(4,21,'4:21','และพวกเจ้าจะเอามันคืนได้อย่างไรทั้งๆ ที่บางคนของพวกเจ้าได้แนบกายกับอีกบางคนแล้ว และพวกนางก็ได้เอาคํามั่นสัญญาอันหนักแน่นจากพวกเจ้าแล้วด้วย');
INSERT INTO "king_fahad_thai" VALUES(4,22,'4:22','และจงอย่าแต่งงานกับบรรดาหญิงที่บิดาของพวกเจ้าได้แต่งงานมาแล้ว นอกจากที่ได้ผ่านพ้นมาเท่านั้น แท้จริงมันเป็นสิ่งลามกและน่าเกลียดยิ่ง และเป็นวิถีทางที่ชั่ว');
INSERT INTO "king_fahad_thai" VALUES(4,23,'4:23','ที่ได้ถูกห้ามแก่พวกเจ้านั้นคือมารดา ของพวกเจ้า ลูกหญิงของพวกเจ้า พี่น้องหญิงของพวกเจ้า พี่น้องหญิงแห่งบิดาของพวกเจ้า และพี่น้องหญิงแห่งมารดาของพวกเจ้า บุตรหญิงของพี่หรือน้องชายของพวกเจ้า บุตรหญิงของพี่หรือน้องหญิงของพวกเจ้า และมารดาของพวกเจ้าที่ให้นมแก่พวกเจ้าและพี่น้องหญิงของพวกเจ้าเนื่องจากการดื่มนม และมารดาภรรยาของพวกเจ้าแลลูกเลี้ยงของพวกเจ้าที่อยู่ในตักของพวกเจ้า จากภรรยาของพวกเจ้าที่พวกเจ้ามิได้สมสู่นาง แต่ถ้าพวกเจ้ามิได้สมสู่นางแล้ว ก็ไม่มีบาปใด ๆ แก่พวกเจ้าและภรรยาของบุตรพวกเจ้าที่มาจากเชื้อสายของพวกเจ้า และการที่พวกเจ้ารวมระหว่างหญิงสองพี่น้องไว้ด้วยกัน นอกจากที่ได้ผ่านพ้นไปแล้วเท่านั้น แท้จริงอัลลอฮฺเป็นผู้ทรงอภัยผู้เมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(4,24,'4:24','และบรรดาหญิงที่อยู่ในปกครองของสามี นอกจากที่มือขวาของพวกเจ้าครอบครอง เป็นบัญญัติของอัลลอฮฺที่มีแก่พวกเจ้า และได้ถูกอนุมัติให้แก่พวกเจ้าที่นอกเหนือจากนั้น ในการที่พวกเจ้าจะแสวงหามาด้วยทรัพย์ของพสกเจ้า ในฐานะเป็นผู้แต่งงาน มิใช่ในฐานะผู้ล่วงประเวณี ดังนั้นหญิงใดที่พวกเจ้าเสพสุขด้วยนางจากบรรดาหญิงเหล่านั้น ก็จงให้แก่พวกนาง ซึ่งสินตอบแทนแก่พวกนาง ตามที่มีกําหนดไว้และไม่เป็นบาปใด ๆแก่พวกเจ้าในสิ่งที่พวกเจ้าต่างยินยอมกันในสิ่งนั้น หลังจากที่มีกําหนดนั้นขึ้นแท้จริงอัลลอฮฺเป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(4,25,'4:25','และผู้ใดในหมู่พวกเต้าไม่สามารถมีกําลัง ที่จะแต่งงานกับบรรดาหญิงอิสระที่มีศรัทธาได้ก็จงแต่งงานกับเด็กสาว ของพวกเจ้าที่เป็นผู้ศรัทธาในหมู่ที่มือขวาของพวกเจ้าครอบครอง และอัลลอฮฺเป็นผู้ทรงรอบรู้ยิ่งที่ในการศรัทธาของพวกเจ้าบางคนในหมู่พวกเจ้านั้นมาจากอีกบางคน ดังนั้นจงแต่งงานกับพวกนางด้วยการอนุมัติจากผู้เป็นนายของพวกนาง และจงให้แก่พวกนางซึ่งสินตอบแทนของพวกนางโดยชอบธรรม ในฐานะที่พวกนางเป้นหญิงที่ได้รับการแต่งงานมิใช่เป็นหญิงที่ค้าประเวณี และไม่ใช่หญิงที่ยึดเอาชายเป็นเพื่อนสนิท เมื่อพวกนางได้รับการแต่งงานแล้ว หากพวกนางกระทําความชั่ว พวกนางก็จะได้รับโทษครึ่งหนึ่งของโทษที่บรรดาหญิงอิสระได้รับ นั่นสําหรับผู้ในหมู่พวกเจ้าที่กลัวการทําชั่ว และการที่พวกเจ้าอดกลั้นไว้ได้นั้น เป็นการดีกว่าสําหรับพวกเจ้า และอัลลอฮฺเป็นผู้ทรงอภัยโทษ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(4,26,'4:26','อัลลอฮฺ ทรงปรารถนาที่จะแจกแจงแก่พวกเจ้า และแนะนําพวกเจ้าซึ่งแนวทางต่างๆ ของบรรดาผู้ที่มาก่อนพวกเจ้า และจะทรงอภัยโทษให้แก่พวกเจ้า และอัลลอฮฺเป็นผู้ทรงรอบรู้และเป็นผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(4,27,'4:27','และอัลลอฮฺ ทรงปรารถนาที่จะอภัยโทษให้แก่พวกเจ้า และบรรดาผู้ที่ปิบัติตามความใคร่ใฝ่ต่ํานั้นปรารถนาที่จะให้พวกเจ้าเอนเอียงออกไปอย่างมากมาย');
INSERT INTO "king_fahad_thai" VALUES(4,28,'4:28','อัลลอฮฺ ทรงปรารถนาที่จะผ่อนผันให้แก่พวกเจ้า และมนุษย์นั้นถูกบังเกิดขึ้นในสภาพที่อ่อนแอ');
INSERT INTO "king_fahad_thai" VALUES(4,29,'4:29','ผู้ศรัทธาทั้งหลาย! จงอย่ากินทรัพย์ของพวกเจ้า ในระหว่างพวกเจ้าโดยมิชอบ นอกจากมันจะเป็นการค้าขายที่เกิดจากความพอใจในหมู่พวกเจ้า และจงอย่าฆ่าตัวของพวกเจ้าเอง แท้จริงอัลลอฮฺเป็นผู้ทรงเมตตาต่อพวกเจ้าเสมอ');
INSERT INTO "king_fahad_thai" VALUES(4,30,'4:30','และผู้ใดที่กระทําเช่นนั้น โดยเจตนาละเมิดและข่มเหงแล้ว เราก็จะให้เขาเข้าไฟนรกและนั่นเป็นสิ่งที่ง่ายดายแก่อัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(4,31,'4:31','หากพวกเจ้าปลีกตัวออกจากบรรดาบาปใหญ่ ๆ ของสิ่งที่พวกเจ้าถูกห้ามให้ละเว้นมันแล้ว เราก็จะลบล้างบรรดาความผิดเล็ก ๆ น้อย ๆ ของพวกเจ้า ออกจากพวกเจ้า และเราจะให้พวกเจ้าเข้าอยู่ในสถานที่อันมีเกียรติ');
INSERT INTO "king_fahad_thai" VALUES(4,32,'4:32','แลจงอย่าปรารถนาในสิ่งที่อัลลอฮฺได้ทรงให้แก่บางคนในหมู่พวกเจ้าเหนือกว่าอีกบางคน สําหรับผู้ชายนั้นมีส่วนได้รับจากสิ่งที่พวกเขาได้ขวนขวายไว้ และสําหรับหญิงนั้นก็มีส่วนได้รับจากสิ่งที่พวกนางได้ขวนขวายไว้ และพวกเจ้าจงขอต่ออัลลอฮฺเถิด จากความกรุณาของพระองค์แท้จริงอัลลอฮฺทรงรอบรู้ในทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(4,33,'4:33','และสําหรับแต่ละคนนั้น เราได้ให้มีผู้รับมรดก จากสิ่งที่ผู้บังเกิดเกล้าทั้งสอง และญาติที่ใกล้ชิดได้ทิ้งไว้ และบรรดาผู้ที่มือขวาของพวกเจ้าได้ตกลงไว้นั้น ก็จงให้แก่พวกเขาซึ่งส่วนได้ของพวกเขา แท้จริงอัลลอฮฺทรงเป็นพยานในทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(4,34,'4:34','บรรดาชายนั้น คือผู้ที่ทําหน้าที่ปกครองเลี้ยงดูบรรดาหญิง เนื่องด้วยการที่อัลลอฮฺ ได้ทรงให้บางคนของพวกเขาเหนือกว่าอีกบางคน และด้วยการที่พวกเขาได้จ่ายไปจากทรัพย์ของพวกเขา บรรดากุลสตรีนั้นคือผู้จงรักภักดี ผู้รักษาในทุกสิ่งทุกอย่างที่อยู่ลับหลบังสามี เนื่องด้วยสิ่งที่อัลลอฮฺทรงรักษาไว้ และบรรดาหญิงที่พวกเจ้าหวั่นเกรงในความดื้อดึงของนางนั้น ก็จงกล่าวตักเตือนนางและทอดทิ้งนางไว้แต่ลําพังในที่นอน และจงเฆี่ยนนาง แต่ถ้านางเชื่อฟังพวกเจ้าแล้ว ก็จงอย่าหาทางเอาเรื่องแก่นาง แท้จริงอัลลอฮฺเป็นผู้ทรงสูงส่งผู้ทรงเกรียงไกร');
INSERT INTO "king_fahad_thai" VALUES(4,35,'4:35','และหากพวกเจ้าหวั่นเกรงการแตกแยกระหว่างเขาทั้งสอง ก็จงส่งผู้ตัดสินคนหนึ่งจากครอบครัวของฝ่ายชาย และผู้ตัดสินอีกคนหนึ่งจากครอบครัวฝ่ายหญิง หากทั้งสอง ปรารถนาให้มีการประนีประนอมกันแล้ว อัลลอฮฺก็จะทรงให้ความสําเร็จในระหว่างทั้งสอง แม้จริงอัลลอฮฺเป็นผู้ทรงรอบรู้ ผู้ทรงสัพพัญญู');
INSERT INTO "king_fahad_thai" VALUES(4,36,'4:36','และจงเคารพสักการะอัลลอฮฺเถิด และอย่าให้มีสิ่งหนึ่งสิ่งใดเป็นภาคีกับพระองค์ และจงทําดีต่อผู้บังเกิดเกล้าทั้งสองและต่อผู้เป็นญาติที่ใกล้ชิด และเด็กกําพร้าและผู้ขัดสน และเพื่อนบ้านใกล้เคียงและเพื่อนที่ห่างไกล และเพื่อนเคียงข้าง และผู้เดินทาง และผู้ที่มือขวาของพวกเจ้าครอบครอง แท้จริงอัลลอฮฺ ไม่ทรงชอบผู้ยะโส ผู้โอ้อวด');
INSERT INTO "king_fahad_thai" VALUES(4,37,'4:37','บรรดาผู้ที่ตระหนี่ และใช้ผู้คนให้ตระหนี่ และปกปิดสิ่งที่อัลลอฮฺได้ทรงประทานให้แก่พวกเขา จากความกรุณาของพระองค์นั้น (แน่นอนพวกเขาจะอยู่ในนรกตลอดกาล) และเราได้เตรียมไว้แล้ว ซึ่งการลงโทษที่ยังความอัปยศ แก่ผู้ปฏิเสธศรัทธาทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(4,38,'4:38','และบรรดาผู้ที่บริจาคทรัพย์ของพวกเขา เพื่อโอ้อวดผู้คน และทั้งพวกเขาก็ไม่ศรัทธาต่ออัลลอฮฺ และไม่ศรัทธาต่อวันปรโลกนั้น(แน่นอนพวกเขาจะอยู่ในนรกตลอดกาล) และผู้ใดที่มีชัยฏอนเป็นเพื่อนของเขาแล้ว มันก็เป็นเพื่อนที่เลว');
INSERT INTO "king_fahad_thai" VALUES(4,39,'4:39','และอะไรจะเกิดขึ้นแก่พวกเขา หากพวกเขาศรัทธาต่ออัลลอฮฺ และวันปรโลก และได้บริจาคส่วนหนึ่งจากสิ่งที่อัลลอฮฺ ได้ทรงให้เป็นปัจจัยยังชีพแก่พวกเขา และอัลลอฮฺ ทรงรอบรู้ในพวกเขาดี');
INSERT INTO "king_fahad_thai" VALUES(4,40,'4:40','แท้จริงอัลลอฮฺจะไม่ทรงอธรรมแม้เพียงน้ําหนักเท่าผงธุลี และถ้ามันเป็นสิ่งที่ดีอย่างหนึ่งอย่างใด พระองค์ก็จะทรงเพิ่มพูนความดีนั้นเป็นทวีคูณ และทรงประทานให้จากที่พระองค์ ซึ่งรางวัลอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(4,41,'4:41','แล้วอย่างไรเล่า เมื่อเรานําพยานคนหนึ่งจากแต่ละประชาชาติมา และเราได้นําเจ้ามาเป็นพยานต่อชนเหล่านี้');
INSERT INTO "king_fahad_thai" VALUES(4,42,'4:42','ในวันนั้น บรรดาผู้ที่ปฏิเสธศรัทธาและดื้อดึงต่อร่อซูลชอบ หากว่าแผ่นดินถูกให้ทับถมพวกเขาจนราบเรียบไป และพวกเขาไม่สามารถจะปิดบังคําพูดใด ๆ แก่อัลลอฮฺได้');
INSERT INTO "king_fahad_thai" VALUES(4,43,'4:43','ผู้ศรัทธาทั้งหลาย! จงอย่าเข้าใกล้การละหมาด ขณะที่พวกเจ้ากําลังมันเมาอยู่ จนกว่าพวกเจ้าจะรู้ สิ่งที่พวกเจ้าพูด และก็จงอย่าเข้าใกล้การละหมาด ขณะที่เป็นผู้มีญะนาบะฮ์ นอกจากผู้ที่ผ่านทางไปเท่านั้น จนกว่าพวกเจ้าจะอาบน้ํา และหากพวกเจ้าป่วยหรืออยู่ในการเดินทาง หรือคนหนึ่งคนใดในพวกเจ้ามาจากที่ถ่ายทุกข์ หรือพวกเจ้าสัมผัสผู้หญิง แล้วพวกเจ้าไม่พบน้ํา ก็จงมุ่งสู่ดินที่ดี แล้วจงลูบใบหน้าของพวกเจ้าและมือของพวกเจ้า แท้จริงอัลลอฮฺเป็นผู้ทรงอภัย ผู้ทรงยกโทษเสมอ');
INSERT INTO "king_fahad_thai" VALUES(4,44,'4:44','เจ้ามิได้มองดูบรรดาผู้ที่ได้รับส่วนหนึ่งจากคัมภีร์ดอกหรือ? โดยที่เขาเหล่านั้นกําลังซื้อความหลงผิดไว้ และต้องการที่จะให้พวกเจ้าหลงทาง');
INSERT INTO "king_fahad_thai" VALUES(4,45,'4:45','และอัลลอฮฺ ทรงรู้ดียิ่งต่อบรรดาศัตรูของพวกเจ้า และพอเพียงแล้วที่อัลลอฮฺ ทรงเป็นผู้คุ้มครอง และพอเพียงแล้วที่อัลลอฮฺ ทรงเป็นผู้ช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(4,46,'4:46','จากบางคนในหมู่ผู้เป็นยิวนั้น พวกเขาบิดเบือนบรรดาถ้อยคําให้เหออกจากที่ของมัน และพวกเขากล่าวว่า เราได้ยินกันแล้วและเราก็ได้ฝ่าฝืนกันแล้ว และท่านจงฟังโดยที่มิใช่เป็นผู้ได้ยิน และจงสดับฟังเราโดยบิดลิ้นของพวกเขา และใส่ร้ายในศาสนา และหากว่าพวกเขากล่าวว่า เราได้ยินกันแล้ว และได้เชื่อฟังกันแล้ว และท่านจงฟัง และมองดูเราเถิด ก็จะเป็นสิ่งดีกว่าแก่พวกเขา และเที่ยงตรงกว่า แต่ทว่าอัลลอฮฺได้ทรงละอนัต พวกเขาเสียแล้ว เนื่องด้วยการปฏิเสธศรัทธาของพวกเขา ดังนั้นพวกเขาจึงไม่ศรัทธากัน นอกจากเพียงเล็กน้อยเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(4,47,'4:47','บรรดาผู้ที่ได้รับคัมภีร์ทั้งหลาย ! จงศรัทธาต่อสิ่งที่เราได้ให้ลงมา เพื่อยืนยันสิ่งที่มีอยู่กับพวกเจ้า เถิด ก่อนจากที่เราจะลบใบหน้าของพวกเขา แล้วให้มันกลับไปอยู่ข้างหลังของมัน หรือไม่ก็จะสาปพวกเขาเช่นเดียวกับที่เราได้สาปบรรดาผู้ที่ทําการละเมิดในวันสับบะโต และคําสั่งของอัลลอฮฺนั้นย่อมถูกปฏิบัติตามเสมอ');
INSERT INTO "king_fahad_thai" VALUES(4,48,'4:48','แท้จริงอัลลอฮฺ จะไม่ทรงอภัยโทษให้แก่การที่สิ่งหนึ่งจะถูกให้มีภาคี ขึ้นแก่พระองค์ และพระองค์ทรงอภัยให้แก่สิ่งอื่นจากนั้น สําหรับผู้ที่พระองค์ทรงประสงค์ และผู้ใดให้มีภาคีขึ้นแก่อัลลอฮฺแล้ว แน่นอนเขาก็ได้อุปโลกน์บาปกรรมอันใหญ่หลวงขึ้น');
INSERT INTO "king_fahad_thai" VALUES(4,49,'4:49','เจ้ามิได้มองดูบรรดาผู้ที่ให้ตัวเองเป็นผู้บริสุทธิ์ดอกหรือ ? มิได้อัลลอฮฺต่างหากที่จะให้บริสุทธิ์ซึ่งผู้ที่พระองค์ทรงประสงค์ และพวกเขาจะไม่ถูกอธรรมแม้เพียงขนาดร่องเมล็ดอินทผาลัม');
INSERT INTO "king_fahad_thai" VALUES(4,50,'4:50','จงดูเถิดว่า อย่างไรเล่า ที่พวกเขาอุปโลกน์ความเท็จให้แก่อัลลอฮฺ และพอเพียงแล้วที่ความเท็จนั้นเป็นบาปอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(4,51,'4:51','เจ้ามิได้มองดูบรรดาผู้ที่ได้รับส่วนหนึ่งจากคัมภีร์ดอกหรือ โดยที่พวกเขาศรัทธาต่ออัลญิบติ และอัฏ-ฏอฆูต และกล่าวแก่บรรดาผู้ปฏิเสธศรัทธา ว่า พวกเขาเหล่านี้แหละเป็นผู้อยู่ในทางที่เที่ยงตรงกว่าบรรดาผู้ศรัทธาทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(4,52,'4:52','ชนเหล่านี้คือผู้ที่อัลลอฮฺได้ทรงละอนัต แก่พวกเขา และผู้ใดที่อัลลอฮฺทรงละอนัตแก่พวกเขาแล้ว เจ้าจะไม่พบว่ามีผู้ช่วยเหลือใด ๆ สําหรับเขาเลย');
INSERT INTO "king_fahad_thai" VALUES(4,53,'4:53','หรือว่าพวกเขามีส่วนได้ใด ๆ จากอํานาจ ถ้าเช่นนั้นแล้ว พวกเขาก็จะไม่ให้แก่คนอื่น แม้เพียงจุดบนเมล็ดอินทผาลัม');
INSERT INTO "king_fahad_thai" VALUES(4,54,'4:54','หรือว่าพวกเขาอิจฉาคนอื่น ในสิ่งที่อัลลอฮฺทรงประทานให้แก่พวกเขา จากความกรุณาของพระองค์ แท้จริงนั้นพระองค์ได้ประทานให้แก่วงศ์วานของอิบรอฮีมมาแล้วซึ่งคัมภีร์และความรู้เกี่ยวกับศาสนา และได้ทรงให้แก่พวกเขาซึ่งอํานาจอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(4,55,'4:55','แล้วในหมู่พวกเขามีผู้ศรัทธาต่อเขาและในหมู่พวกเขามีผู้ที่ขัดขวางเขา และเพียงพอแล้วที่ญะฮันนัมเป็นเปลวเพลิงอันโชติช่วง');
INSERT INTO "king_fahad_thai" VALUES(4,56,'4:56','แท้จริงบรรดาผู้ปฏิเสธศรัทธาต่อโองการทั้งหลายของเรานั้น เราจะให้พวกเขาเข้าไปในไฟนรก คราใดที่ผิวหนังของพวกเขาสุกเราก็เปลี่ยนผิวหนังให้แก่พวกเขาใหม่ซึ่งมิใช่ผิวหนังเดิม เพื่อพวกเขาจะได้ลิ้มรสการลงโทษ แท้จริงอัลลอฮฺ เป็นผู้ทรงเดชานุภาพ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(4,57,'4:57','และบรรดาผู้ที่ศรัทธา และประกอบสิ่งดีงามทั้งหลายนั้น เราจะให้พวกเขาเข้าในบรรดาสวนสวรรค์ ซึ่งมีแม่น้ําหลายสายไหลอยู่ภายใต้สวนสวรรค์เหล่านั้น โดยที่พวกเขาจะอยู่ในนั้นตลอดกาล ซึ่งในนั้นพวกเขาจะได้รับคู่ครองที่บริสุทธิ์และเราจะให้เขาเข้าอยู่ในเงาร่มอันร่มเย็น');
INSERT INTO "king_fahad_thai" VALUES(4,58,'4:58','แท้จริงอัลลอฮฺทรงใช้พวกเจ้าให้มอบคืนบรรดาของฝากแก่เจ้าของของมัน และเมื่อพวกเจ้าตัดสินระหว่างผู้คน พวกเจ้าก็จะต้องตัดสินด้วยความยุติธรรม แท้จริงอัลลอฮฺทรงแนะนําพวกเจ้าด้วยสิ่งซึ่งดีจริง ๆ แท้จริงอัลลอฮฺเป็นผู้ทรงได้ยินและได้เห็น');
INSERT INTO "king_fahad_thai" VALUES(4,59,'4:59','ผู้ศรัทธาทั้งหลาย ! จงเชื่อฟังอัลลอฮฺ และเชื่อฟังร่อซูลเถิด และผู้ปกครองในหมู่พวกเจ้าด้วย แต่ถ้าพวกเจ้าขัดแย้งกันในสิ่งใด ก็จงนําสิ่งนั่นกลับไปยังอัลลอฮฺ และร่อซูล หากพวกเจ้าศรัทธาต่ออัลลอฮฺและวันปรโลก นั่นแหละเป็นสิ่งที่ดียิ่งและเป็นการกลับไป ที่สวยยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(4,60,'4:60','เจ้ามิได้มองดูบรรดาผู้ที่อ้างตนว่าพวกเขาศรัทธาต่อสิ่งที่ถูกประทานลงมาแก่เจ้าและสิ่งที่ถูกประทานลงมาก่อนเจ้าดอกหรือ ? โดยที่เขาเหล่านั้นต้องการที่จะให้การแก่อัฏฏอฆูต ทั้ง ๆ ที่พวกเขาถูกใช้ให้ปฏิเสธมันและชัยฏอนนั้นต้องการที่จะให้พวกเขาหลงทางที่ห่างไกล');
INSERT INTO "king_fahad_thai" VALUES(4,61,'4:61','และเมื่อถูกล่าวแก่พวกเขาว่า ท่านทั้งหลายจงมายังสิ่งที่อัลลอฮฺได้ประทานลงมา และยังร่อซูลเถิด เจ้าก็ได้เห็นพวกมุนาฟิก เหล่านั้นผินหลังให้แก่เจ้าจริง ๆ');
INSERT INTO "king_fahad_thai" VALUES(4,62,'4:62','แล้วจะเป็นอย่างไรเล่า เมื่อมีเหตุร้ายใด ๆ ประสบแก่พวกเขา เนื่องจากสิ่งที่มือของพวกเขาได้ประกอบขึ้น แล้วพวกเขาก็มาหาเจ้าโดยสาบานต่ออัลลอฮฺว่า พวกเรามิได้ต้องการนอกจากการทําดีและให้มีการปรองดองกันเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(4,63,'4:63','ชนเหล่านี้แหละคือผู้ที่อัลลอฮฺทรงรู้สิ่งที่อยู่ในหัวใจของพวกเขา ดังนั้นเจ้าจงผินหลังให้แก่พวกเขา และจงตักเตือนพวกเขาและจงกล่าวแก่พวกเขาในเรื่องที่เกี่ยวกับตัวของพวกเขาอย่างสาแก่ใจ');
INSERT INTO "king_fahad_thai" VALUES(4,64,'4:64','และเรามิได้ส่งร่อซูลคนใดมานอกจากเพื่อให้เขาได้รับการเชื่อฟัง ด้วยอนุมัติของอัลลอฮฺเท่านั้น และแม้ว่าพวกเขานั้นขณะที่พวกเขาอธรรมแก่ตัวเอง ได้มาหาเจ้า แล้วขออภัยโทษต่ออัลออฮ์และร่อซูลก็ได้ขออภัยโทษให้แก่พวกเขาด้วยแล้วแน่นอน พวกเขาก็ย่อมพบว่าอัลลอฮฺนั้นคือผู้ทรงอภัยโทษ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(4,65,'4:65','มิใช่เช่นนั้นดอก ข้าขอสาบานด้วยพระเจ้าของเจ้าว่า เขาเหล่านั้นจะยังไม่ศรัทธาจนกว่าพวกเขาจะให้เจ้าตัดสินในสิ่งที่ขัดแย้งกันระหว่างพวกเขาแล้วพวกเขาไม่พบความ คับใจใด ๆ ในจิตใจของพวกเขาจากสิ่งที่เจ้าได้ตัดสินใจ และพวกเขายอมจํานนด้วยดี');
INSERT INTO "king_fahad_thai" VALUES(4,66,'4:66','และแม้นว่าเราได้กําหนดแก่พวกเขาว่า พวกเจ้าจงฆ่าตัวของพวกเจ้าเองหรือจงออกไปจากหมู่บ้านของพวกเจ้า พวกเขาก็ไม่กระทําตามกําหนดนั้น นอกจากเพียงเล็กน้อยในหมู่พวกเขาเท่านั้น และแม้ว่าพวกเขาได้กระทําตามสิ่งที่พวกเขาได้รับคําแนะนําแล้ว แน่นอนก็เป็นสิ่งดียิ่งแก่พวกเขา และเป็นสี่งที่ทําให้มั่นคงยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(4,67,'4:67','และถ้าเช่นนั้นแล้ว แน่นอนเราก็จะให้แก่พวกเขา ซึ่งรางวัลอันใหญ่หลวงจากที่เรานี้เอง');
INSERT INTO "king_fahad_thai" VALUES(4,68,'4:68','และแน่นอน เราก็จะแนะนําแก่พวกเขาซึ่งทางอันเที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(4,69,'4:69','และผู้ใดที่เชื่อฟังอัลลอฮฺ และร่อซูลแล้วชนเหล่านี้จะอยู่ร่วมกับบรรดาผู้ที่อัลลอฮฺทรงกรุณาเมตตาแก่พวกเขา อันได้แก่บรรดานะบี บรรดาผู้ที่เชื่อโดยดุษฏี บรรดาผู้ที่เสียชีวิตในสงคราม และบรรดาผู้ที่ประพฤติดี และชนเหล่านี้แหละเป็นเพื่อนที่ดี');
INSERT INTO "king_fahad_thai" VALUES(4,70,'4:70','ความกรุณาดังกล่าวนั้นมาจากอัลลอฮฺและพอเพียงแล้วที่อัลลอฮฺเป็นผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(4,71,'4:71','ผู้ศรัทธาทั้งหลาย ! จงยึดถือไว้ซึ่งความระมัดระวังของพวกเจ้า แล้วจงออกไปเป็นกลุ่ม ๆ หรืออกไปโดยรวมเป็นกลุ่มเดียวกัน');
INSERT INTO "king_fahad_thai" VALUES(4,72,'4:72','และแท้จริงในหมู่พวกเจ้านั้นมีผู้ที่ทําชักช้า อยู่ หากมีเหตุร้ายใด ประสบแก่พวกเจ้า เขาก็กล่าวว่า แท้จริงอัลลอฮฺได้ทรงกรุณาแก่ฉัน ที่ฉันมิได้ร่วมอยู่กับพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(4,73,'4:73','และถ้าหากว่ามีความกรุณาใด ๆ จากอัลลอฮฺได้ประสบกับพวกเจ้าแล้ว แน่นอนเขาก็จะกล่าวประหนึ่งไม่เคยมีความชอบพอใดๆ เกิดขึ้นระหว่างพวกเจ้ากับเขาว่า โอ้หวังว่าฉันได้ร่วมอยู่กับพวกเขา แล้วฉันก็จะได้ชัยชนะอย่างใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(4,74,'4:74','จงสู้รบในทางของอัลลอฮฺเถิด บรรดาผู้ขายชีวิตความเป็นอยู่แห่งโลกนี้ด้วยปรโลก และผู้ใดสู้รบในทางของอัลลอฮฺ และเขาถูกฆ่าหรือได้รับชัยชนะเราจะให้รางวัลอันใหญ่หลวงแก่เรา');
INSERT INTO "king_fahad_thai" VALUES(4,75,'4:75','มีเหตุใดเกิดขึ้นแก่พวกเจ้ากระนั้นหรือ? ที่พวกเจ้าไม่สู้รบในทางของอัลลอฮฺ ทั้ง ๆที่บรรดาผู้อ่อนแอ ไม่ว่าชายและหญิง และเด็ก ๆ ต่างกล่าวกันว่า โอ้พระเจ้าของเรา ! โปรดนําพวกเราออกไปจากเมืองนี้ ซึ่งชาวเมืองเป็นผู้ข่มเหงรังแก และโปรดให้มีขึ้นแก่พวกเราซึ่งผู้คุ้มครองคนหนึ่งจากที่พระองค์และโปรดให้มีขึ้นแก่พวกเราซึ่งผู้ช่วยเหลือคนหนึ่งจากที่พระองค์');
INSERT INTO "king_fahad_thai" VALUES(4,76,'4:76','บรรดาผู้ที่ศรัทธานั้น พวกเขาจะต่อสู้ในทางของอัลลอฮฺ และบรรดาผู้ปฏิเสธศรัทธานั้น พวกเขาจะต่อสู้ในทางของอัฎ-ฎอฆูต ดังนั้นพวกเจ้าจงต่อสู้บรรดาสมุนของชัยฏอนเถิด แท้จริงอุบายของชัยฏอนนั้นเป็นสิ่งที่อ่อนแอ');
INSERT INTO "king_fahad_thai" VALUES(4,77,'4:77','เจ้ามิได้มองดูบรรดาผู้ที่ถูกกล่าวแก่พวกเขา ว่า จงระงับมือของพวกเจ้าก่อนเถิด และจงละหมาด และจงชําระซะกาต ครั้นเมื่อการสู้รบได้ถูกกําหนดขึ้นแก่พวกเขา ทันใดนั้นกลุ่มหนึ่งในหมู่พวกเขาก็กลัวมนุษย์เช่นเดียวกับกลัวอัลลอฮฺ หรือกลัวยิ่งกว่า และพวกเขากล่าวว่า โอ้พระเจ้าของเรา ! เพราะเหตุใดพระองค์จึงได้กําหนดการสู้รบขึ้นแก่พวกเรา พระองค์จะทรงเลื่อนให้แก่พวกเรายังกําหนดเวลาอันใกล้ไม่ได้หรือ ? จงกล่าวเถิด (มุฮัมมัด) สิ่งอํานวยประโยชน์แห่งโลกนี้นั้นเล็กน้อยเท่านั้น และปรโลกนั้นดียิ่งกว่าสําหรับผู้ที่ยําเกรงและพวกเจ้าจะไม่ถูกอธรรมแม้เท่าขนาดร่องเมล็ดอินทผาลัม');
INSERT INTO "king_fahad_thai" VALUES(4,78,'4:78','ณ ที่ใดก็ตามที่พวกเจ้าปรากฏอยู่ ความตายก็ย่อมมาถึงพวกเจ้า และแม้ว่าพวกเจ้าจะอยู่ในป้อมปราการอันสูงตระหง่านก็ตาม และหากมีความดีใด ๆ ประสบแก่พวกเขา พวกเขาก็จะกล่าวว่าสิ่งนี้มาจากอัลลอฮฺและหากมีความชั่วใด ๆ ประสบแก่พวกเขา พวกเขาก็จะกล่าวว่า สิ่งนี้มาจากเจ้า จงกล่าวเถิด (มุฮัมมัด) ทุกอย่างนั้นมาจากอัลลอฮฺทั้งสิ้น มีเหตุใดเกิดขึ้นแก่กลุ่มชนเหล่านี้ กระนั้นหรือ ที่พวกเขาห่างไกลที่จะเข้าใจคําพูด');
INSERT INTO "king_fahad_thai" VALUES(4,79,'4:79','ความดีใด ๆ ที่ประสบแก่เจ้านั้นมาจากอัลลอฮฺ และความชั่วใด ๆ ที่ประสบแก่เจ้านั้นมาจากตัวของเจ้าเอง และเราได้ส่งเจ้าไปเป็นร่อซูลแก่มนุษย์ และเพียงพอแล้วที่อัลลอฮฺทรงเป็นพยาน');
INSERT INTO "king_fahad_thai" VALUES(4,80,'4:80','ผู้ใดเชื่อฟังร่อซูล แน่นอนเขาก็เชื่อฟังอัลลอฮฺแล้ว และผู้ใดผินหลังให้ เราก็หาได้ส่งเจ้าไปในฐานะเป็นผุ้ควบคุมพวกเขาไม่');
INSERT INTO "king_fahad_thai" VALUES(4,81,'4:81','และพวกเขากล่าวว่า เชื่อฟัง แต่เมื่อพวกเขาออกไปจากเจ้าแล้ว กลุ่มหนึ่งในพวกเขาก็ได้วางแผนในเวลากลางคืน อื่นจากสิ่งที่เจ้ากล่าว และอัลลอฮฺจะทรงบันทึกสิ่งที่พวกเขาวางแผนกันในเวลากลางคืน ดังนั้นจงเพิกเฉยต่อพวกเขาเสีย และจงมอบหมายแก่อัลลอฮฺเถิด และพอเพียงแล้วที่อัลลอฮฺเป็นผู้ที่ได้รับมอบหมายให้คุ้มครองรักษา');
INSERT INTO "king_fahad_thai" VALUES(4,82,'4:82','พวกเขาไม่พิจารณาดูอัล-กุรอานบ้างหรือ ? และหากว่า อัล-กุรอานมาจากผู้ที่ไม่ใช่อัลลอฮฺแล้วแน่นอนพวกเขาก็จะพบว่าในนั้นมีความขัดแย้งกันมากมาย');
INSERT INTO "king_fahad_thai" VALUES(4,83,'4:83','และเมื่อมีเรื่องหนึ่งเรื่องใดมายังพวกเขาจะเป็นความปลอดภัยก็ดีหรือความกลัวก็ดี พวกเขาก็จะแพร่มันออกไป และหากว่าพวกเขาให้มันกลับไปยังร่อซูล และยังผู้ปกครองการงานในหมู่พวกเขาแล้ว แน่นอนบรรดาผู้ที่วินิจฉัยมันในหมู่พวกเขาก็ย่อมรู้มันได้ และหากมิใช่ความเมตตาของอัลลอฮฺที่มีต่อพวกเจ้าแล้ว แน่นอน พวกเจ้าก็คงปฏิบัติตามชัยฎอนไปแล้ว นอกจากเพียงเล็กน้อยเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(4,84,'4:84','เจ้า จงสู้รบในทางของอัลลอฮฺเถิด โดยที่เจ้ามิได้ถูกบังคับ (ให้เกณฑ์ผู้ใด) นอกจากตัวของเจ้าเอง และจงปลุกใจบรรดาผู้ศรัทธาด้วย เป็นไปได้ว่าอัลลอฮฺจะทรงยับยั้งกําลังรบของบรรดาผู้ปฏิเสธศรัทธาเหล่านั้น และอัลลอฮฺเป็นผู้ทรงมีกําลังรบที่เข้มแข็งกว่า และเป็นผู้ทรงมีการลงโทษที่รุนแรงกว่า');
INSERT INTO "king_fahad_thai" VALUES(4,85,'4:85','ผู้ใดที่ให้ความช่วยเหลือย่างดีก็จะเป็นของเขา ซึ่งส่วนหนึ่งจากความดีนั้น และผู้ใดให้ความช่วยเหลือย่างชั่ว ก็จะเป็นของเขา ซึ่งส่วนหนึ่งจากความชั่วนั้น และปรากฏว่าอัลลอฮฺนั้นทรงเดชานุภาพเหนือทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(4,86,'4:86','และเมื่อพวกเจ้าได้รับคําอวยพรจะด้วยคําอวยพรใด ๆ ก็ตาม ก็จงกล่าวคําอวยพรตอบที่ดีกว่านั้น หรือไม่ก็กล่าวคําอวยพรนั้นตอบกลับไป แท้จริงอัลลอฮฺทรงคํานวณนับในทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(4,87,'4:87','อัลลอฮฺนั้นคือไม่มีผู้ที่ควรได้รับเคารพสักการะใด ๆ นอกจากพระองค์เท่านั้น แน่นอน พระองค์จะทรงรวบรวมพวกเจ้าทั้งหลายสู่วันกิยามะฮ์ ซึ่งไม่มีการสงสัยใด ๆ ในวัน และใครเล่าที่จะมีคําพูดจริงยิ่งกว่าอัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(4,88,'4:88','มีอะไรเกิดขึ้นแก่พวกเจ้ากระนั้นหรือ ที่พวกเจ้าได้กลายเป็นสองพวก ในกรณีบรรดามุนาฟิกเหล่านั้น และอัลลอฮฺได้ทรงให้พวกเขากลับสู่สภาพเดิมแล้ว เนื่องด้วยสิ่งที่พวกเขาได้ขวนขวายไว้ พวกเจ้าต้องการที่จะแนะนําผู้ที่อัลลอฮฺได้ทรงให้หลงผิดไปแล้วกระนั้นหรือ? และผู้ใดที่อัลลอฮฺทรงให้หลงผิดไปแล้ว เจ้าก็จะไม่พบทางใด ๆ สําหรับเขาเป็นอันขาด');
INSERT INTO "king_fahad_thai" VALUES(4,89,'4:89','พวกเขาชอบหากว่า พวกเจ้าจะปฏิเสธศรัทธา ดังที่พวกเขาได้ปฏิเสธ พวกเจ้าจะได้กลายเป็นผู้ที่เท่าเทียมกัน ดังนั้นจงอย่าได้ยึดเอาใครในหมู่พวกเขาเป็นมิตร จนกว่าพวกเขาจะอพยพไปในทางของอัลลอฮฺ แต่ถ้าพวกเขาผินหลังให้ก็จงเอาพวกเขาไว้ และจงฆ่าพวกเขา ณ ที่ที่พวกเจ้าพบพวกเขา และจงอย่าเอาใครในหมู่พวกเขาเป็นมิตรและเป็นผู้ช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(4,90,'4:90','นอกจากบรรดาผู้ที่ติดต่ออยู่ กับพวกหนึ่งซึ่งระหว่างพวกเจ้ากับพวกนั้นมีพันธะสัญญาอยู่ หรือบรรดาผู้ที่มาหาพวกเจ้าโดยที่หัวอกของพวกเขาอัดอั้น ต่อการที่พวกเขาจะสู้รบกับพวกเจ้าหรือสู้รบกับหมู่ชนของพวกเขาเอง และหากว่าอัลลอฮฺทรงประสงค์แล้ว แน่นอนก็ทรงให้พวกเขามีอํานาจเหนือพวกเจ้าแล้ว แล้วแน่นอนพวกเขาก็สู้รบกับพวกเจ้าแล้วด้วยแต่ถ้าพวกเขาออกห่างพวกเจ้า โดยที่มิได้ทําการสู้รบกับพวกเจ้า และได้เจรจาแก่พวกเจ้าซึ่งการประนีประนอมแล้วไซร้ อัลลอฮฺก็ไม่ทรงให้มีทางใดแก่พวกเจ้าที่จะขจัดพวกเขาได้');
INSERT INTO "king_fahad_thai" VALUES(4,91,'4:91','พวกเจ้าจะพบพวกอื่นอีก โดยพวกเขาปรารถนาที่จะปลอดภัยจากพวกเจ้า และปลอดภัยจากพวกเขาเอง คราใดที่พวกเขาถูกให้กลับไปสู่การฟิตนะฮ์ พวกเขาก็ถูกให้กลับไปอยู่ในนั้นตามเดิม ดังนั้นถ้าพวกเขามิได้ออกห่างจากพวกเจ้าไป และมิได้เจรจาแก่พวกเจ้าซึ่งการประนีประนอม และมิได้ระงับมือของพวกขาแล้ว ก็จงเอาพวกเขาไว้ และจงฆ่าพวกเขา ณ ที่ที่พวกเจ้าพบพวกเขา และชนเหล่านี้แหละเราได้ให้มีอํานาจอันชัดเจนแก่พวกเจ้าที่จะขจัดพวกเขาได้');
INSERT INTO "king_fahad_thai" VALUES(4,92,'4:92','และมิใช่วิสัยของผู้ศรัทธาที่จะฆ่าผู้ศรัทธาคนหนึ่งคนใด นอกจากด้วยความผิดพลาดเท่านั้น และผู้ใดที่ฆ่าผู้ศรัทธาด้วยความผิดพลาดแล้ว ก็ให้มีการปล่อยทาสหญิงที่ศรัทธาคนหนึ่งให้เป็นไท และให้มีค่าทําขวัญ ซึ่งถูกมอบให้แก่ครอบครัวของเขา นอกจากว่าครอบครัวของพวกเขาจะทําทานให้เท่านั้น แต่ถ้าหากเขาอยู่ในหมู่ชนที่เป็นศัตรูของพวกเจ้า โดยที่เขาเป็นผู้ศรัทธาก็ให้มีการปล่อยทาศหญิงที่ศรัทธาคนหนึ่งให้เป็นไท และถ้าเขาอยู่ในหมู่ชนที่มีพันธะสัญญา ระหว่างพวกเจ้ากับพวกเขาแล้ว ก็ให้มีการทําขวัญ ซึ่งถูกมอบให้แก่ครอบครัวของเขา และให้มีการปล่อยทาสหญิงที่ศรัทธาคนหนึ่ง ผู้ใดที่ไม่พบ ก็ให้มีการถือศีลอดสองเดือนต่อเนื่องกัน เป็นการอภัยโทษจากอัลลอฮฺ และปรากฏว่าอัลลอฮฺ นั้นเป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาน');
INSERT INTO "king_fahad_thai" VALUES(4,93,'4:93','และผู้ใดฆ่าผู้ศรัทธาโดยจงใจ การตอบแทนแก่เขาก็คือ นรกญะฮันนัม โดยที่เขาจะอยู่ในนั้นตลอดกาล และอัลลอฮฺก็ทรงกริ้วโกรธเขา และทรงละนัตเขา และได้ทรงเตรียมไว้สําหรับเขาซึ่งการลงโทษอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(4,94,'4:94','ผู้ที่ศรัทธาทั้งหลาย ! เมื่อพวกเจ้าเดินทางไปในทางของอัลลอฮฺ ก็จงให้ประจักษ์ชัดเสียก่อน และจงอย่ากล่าวแก่ผู้ที่กล่าวสลามแก่พวกเจ้าว่า “ท่านมิใช่เป็นผู้ศรัทธา” โดยแสวงหาสิ่งอํานวยประโยชน์ชั่วคราวแห่งชีวิตความเป็นอยู่ในโลกนี้ แต่ณ ที่อัลลอฮฺนั้นมีปัจจัยยังชีพอันมากมาย ในทํานองเดียวกันนั้นพวกเจ้าก็เคยเป็นมาก่อน แล้วอัลลอฮฺได้ทรงโปรดปรานแก่พวกเจ้า ดังนั้นพวกเจ้าจงให้ประจักษ์เสียก่อน แท้จริงอัลลอฮฺนั้นทรงรอบรู้อย่างถี่ถ้วนในสิ่งที่พวกเจ้ากระทํากันอยู่');
INSERT INTO "king_fahad_thai" VALUES(4,95,'4:95','บรรดาผู้ที่นั่งอยู่จากหมู่มุมินที่มิใช่ผู้มีความเดือดร้อน และบรรดาผุ้ต่อสู้และเสียสละในทางของอัลลอฮฺ ทั้งด้วยทรัพย์สมบัติของพวกเขาและชีวิตของพวกเขานั่น หาได้เท่าเทียมกันไม่ อัลลอฮฺทรงให้บรรดาผู้ที่ต่อสู้และเสียสละด้วยทรัพย์สมบัติของพวกเขา และชีวิตของพวกเขา เหนือกว่าบรรดาผู้ที่นั่งอยู่ ขั้นหนึ่ง และทั้งหมดนั้นอัลลอฮฺได้ทรงสัญญาไว้ให้ซึ่งสิ่งที่ดีเยี่ยม แต่อัลลอฮฺทรงให้บรรดาผู้ที่ต่อสู้และเสียสละเหนือกว่าบรรดาผู้ที่นั่อยู่ด้วยรางวัลอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(4,96,'4:96','(คือพวกเขาจะได้รับ)หลายขั้น จากพระองค์ และ(จะได้รับ)การอภัยโทษ และการเอ็นดูเมตตาด้วย และปรากฏว่าอัลลอฮฺนั้นเป็นผู้ทรงอภัยโทษ ผู้ทรงเอ็นดูเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(4,97,'4:97','แท้จริงบรรดาผู้ที่มะลาอิกะฮ์ได้เอาชีวิตของพวกเขาไป โดยที่พวกเขาเป็นผู้อธรรมแก่ตัวของพวกเขาเองนั้น มลาอิกะฮ์ได้กล่าวว่า พวกเจ้าปรากฏอยู่ในสิ่งใด พวกเขากล่าวว่าพวกเราเป็นผู้ที่ถูกนับว่าอ่อนแอ ในแผ่นดิน มลาอิกะฮ์กล่าวว่า แผ่นดินของอัลลอฮฺมิได้กว้างขวางดอกหรือที่พวกเจ้าจะอพยพไปอยู่ในส่วนนั้น ชนเหล่านี้แหละที่อยู่ของพวกเขาคือนรกญะฮันนัม และเป็นที่กลับไปอันชั่วร้าย');
INSERT INTO "king_fahad_thai" VALUES(4,98,'4:98','นอกจากบรรดาผู้ที่ถูกนับว่าอ่อนแอ ไม่ว่าจะเป็นชายและหญิง และเด็ก ก็ตามที่ไม่สามารถมีอุบายใด ๆ ได้ และทั้งไม่ได้รับการแนะนําทางหนึ่งทางใดด้วย');
INSERT INTO "king_fahad_thai" VALUES(4,99,'4:99','ชนเหล่านี้แหละ อัลลอฮฺ อาจจะทรงยกโทษให้แก่พวกเขา และอัลลอฮฺเป็นทรงยกโทษเสมอ');
INSERT INTO "king_fahad_thai" VALUES(4,100,'4:100','และผู้ใดที่อพยพไปในทางของอัลลอฮฺเขาก็จะพบในผืนแผ่นดิน ซึ่งสถานที่อพยพไปอันมากมาย และความมั่งคั่งด้วย และผู้ที่ออกจากบ้านของเขาไป ในฐานะผู้อพยพไปยังอัลลอฮฺ และรอ่ซูลของพระองค์ แล้วความตายก็มาถึงเขา แน่นอนรางวัลของเขานั้นย่อมปรากฏอยู่แล้ว ณ อัลลอฮฺ และอัลลอฮฺนั้นเป็นทรงอภัยโทษผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(4,101,'4:101','และเมื่อพวกเจ้าเดินทางไปในผืนแผ่นดินก็ไม่มีบาปใด ๆ แก่พวกเจ้าในการที่พวกเจ้าจะลดลงจากการละหมาด หากพวกเจ้ากลัวว่าบรรดาผู้ปฏิเสธศรัทธาจะข่มเหงรังแกพวกเจ้า แท้จริงบรรดาผู้ปฏิเสธศรัทธานั้น เป็นศัตรูอันชัดเจนแก่พวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(4,102,'4:102','และเมื่อเจ้า อยู่ในหมู่พวกเขา แล้วเจ้าได้ให้มีการปฏิบัติละหมาดขึ้นแก่พวกเขา ดังนั้น กลุ่มหนึ่งจากพวกเจาก็จงยืนละหมาดร่วมกับเจ้า และก็จงเอาอาวุธของพวกเขาถือไว้ด้วย ครั้นเมื่อพวกเขาสุยูดแล้ว พวกเขาก็จงอยู่เบื้องหลังของพวกเจ้า และอีกกลุ่มหนึ่งที่ยังมิได้ละหมาดก็จงมา และจงละหมาดร่วมกับเจ่า และจงยึดถือไว้ซึ่งการระมัดระวัง ของพวกเขา และอาวุธของพวกเขา บรรดาผู้ที่ปฏิเสธศรัทธานั้น หากว่าพวกเจ้าละเลยอาวุธของพวกเจ้า และสัมภาระของพวกเจ้าแล้ว พวกเขาก็จะจู่โจมพวกเจ้าอย่างรวดเร็ว และไม่มีบาปใด ๆ แก่พวกเจ้า-หากว่าที่พวกเจ้ามีความเดือดร้อน เนื่องจากฝนตกหรือพวกเจ้าป่วย-ในการที่พวกเจ้าจะวางอาวุธ ของพวกเจ้า และพวกเจ้าจงยึดถือไว้ ซึ่งการระมัดระวังของพวกเจ้า แท้จริงอัลลอฮฺทรงเตรียมไว้แล้ว ซึ่งการลงโทษที่ยังความอัปยศแก่ผู้ปฏิเสธศรัทธาทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(4,103,'4:103','ครั้นเมื่อพวกเจ้าเสร็จจากการละหมาดแล้ว ก็จงกล่าวรําลึกถึงอัลลอฮฺ ทั้งในสภาพยืนและนั่งและในสภาพนอนเอกเขนกของพวกเจ้า ครั้นเมื่อพวกเจ้าปลอดภัยแล้ว ก็จงดํารงไว้ซึ่งการละหมาด แท้จริงการละหมาดนั้นเป็นบัญญัติที่ถูกกําหนดเวลา ไว้แก่ผู้ศรัทธาทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(4,104,'4:104','และพวกเจ้าจงอย่าท้อแท้ในการแสวงหากลุ่มชนพวกนั้น หากพวกเจ้าเจ็บ พวกเขาก็เจ็บเช่นเดียวกับพวกเจ้า แต่พวกเจ้าหวังจากอัลลอฮฺสิ่งที่พวกเขาไม่หวัง และอัลลอฮฺนั้น เป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(4,105,'4:105','แท้จริง เราได้ให้คัมภีร์ลงมาแก่เจ้าเป็นความจริง เพื่อเจ้าจะได้ตัดสินระหว่างผู้คน ด้วยสิ่งที่อัลลอฮฺได้ทรงให้เจ้ารู้เห็น และเจ้าจงอย่าเป็นผู้เถียงแก้ให้แก่ผู้บิดพริ้วทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(4,106,'4:106','และเจ้า จงขอภัยโทษต่ออัลลอฮฺเถิด แท้จริงอัลลอฮฺนั้นเป็นผู้ทรงอภัยโทษ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(4,107,'4:107','และเจ้าจงอย่าโต้เถียงแทนบรรดาผู้ที่บิดพริ้วต่อตัวของพวกเขาเอง เลย แท้จริงอัลลอฮฺไม่ทรงชอบผู้ที่เคยบิดพริ้ว ที่เคยทําบาป');
INSERT INTO "king_fahad_thai" VALUES(4,108,'4:108','พวกเขาจะปกปิดให้พ้นจากมนุษย์ได้แต่พวกเขาจะปกปิดให้พ้นจากอัลลอฮฺนั้นไม่ได้ โดยที่พระองค์ร่วมอยู่ด้วย กับพวกเขาขณะที่พวกเขาวางแผนกันเวลากลางคืน ซึ่งคําพูดที่พระองค์ไม่ทรงพอพระทัย และอัลลอฮฺนั้นทรงล้อม ไว้เสมอซึ่งสิ่งที่พวกเขากระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(4,109,'4:109','พึงรู้เถิดว่า พวกเจ้านี้แหละ ได้เถียงแทนพวกเขา กันในชีวิตความเป็นอยู่แห่งโลกนี้ แล้วใครเล่าที่จะเถียงกับอัลลอฮฺแทนพวกเขาในวันกิยามะฮ์ หรือว่าใครเล่าจะเป็นผู้รับมอบหมายให้คุ้มครองรักษา');
INSERT INTO "king_fahad_thai" VALUES(4,110,'4:110','และผู้ใดที่กระทําความชั่วหรืออธรรมแก่ตัวเอง แล้วเขาขออภัยโทษต่ออัลลอฮฺ เขาก็จะพบว่าอัลลอฮฺ เป็นผู้ทรงอภัยโทษเป็นผู้ทรงเมตตา');
INSERT INTO "king_fahad_thai" VALUES(4,111,'4:111','ผู้ใดที่แสวงหาบาปกรรมไว้ แท้จริงแล้วเขาแสวงหามันไว้ให้เป็นภัยแก่ตัวของเขาเองเท่านั้น และอัลลอฮฺนั้นเป็นผู้ทรงรอบรู้ ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(4,112,'4:112','และผู้ใดที่แสวงหาความผิดหรือบาปกรรมไว้ แล้วก็โยนบาปกรรมนั้นให้แก่ผู้บริสุทธิ์แน่นอนเขาได้แบกความเท็จและบาปกรรมอันชัดเจนไว้');
INSERT INTO "king_fahad_thai" VALUES(4,113,'4:113','และหากไม่มีความกรุณาของอัลลอฮฺและความเมตตาของพระองค์แก่เจ้า แล้ว แน่นอนกลุ่มหนึ่งจากพวกเขาก็มุ่งแล้วที่จะให้เจ้าหลงผิดไป แต่พวกเขาจะไม่ทําให้ใครหลงผิดไปได้ นอกจากตัวของพวกเขาเองเท่านั้น และพวกเขาก็จะไม่ทําอันตรายแก่เจ้าได้แต่อย่างใด และอัลลอฮฺได้ทรงประทานคัมภีร์ลงมาแก่เจ้า และความเข้าใจในบทบัญญัติแห่งคัมภีร์นั้นด้วย และได้ทรงสอนเจ้าในสิ่งที่เจ้าไม่เคยรู้มาก่อน และความกรุณาของอัลลอฮฺที่มีแก่เจ้านั้นใหญ่หลวงนัก');
INSERT INTO "king_fahad_thai" VALUES(4,114,'4:114','ไม่มีความดีใด ๆ ในการพูดซุบซิบอันมากมายของพวกเขา นอกจากผู้ที่ใช้ให้ทําทานหรือให้ทําสิ่งที่ดีงาม นอกจากผู้ที่ใช้ให้ทํางานหรือให้ทําสิ่งที่ดีงาม หรือให้ประนีประนอมระหว่างผู้คนเท่านั้น และผู้ใดกระทําดังกล่าวเพื่อแสวงหาความโปรดปรานจากอัลลอฮฺแล้ว เราจะให้แก่เขาซึ่งรางวัลอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(4,115,'4:115','และผู้ใดที่ฝ่าฝืนร่อซูล หลังจากที่คําแนะนําอันถูกต้องได้ประจักษ์แก่เขาแล้ว และเขายังปฏิบัติตามที่มิใช่ทางของบรรดาผู้ศรัทธา นั้น เราก็จะให้เขาหันไปตามที่เขาได้หันไป และเราจะให้เขาเข้านรกญะฮันนัม และมันเป็นกลับอันชั่วร้าย');
INSERT INTO "king_fahad_thai" VALUES(4,116,'4:116','แท้จริงอัลลอฮฺจะไม่ทรงอภัยโทษให้แก่การที่สิ่งหนึ่งจะถูกให้เป็นภาคีกับพระองค์ แต่พระองค์จะทรงอภัยโทษให้ซึ่งสิ่งอื่นจากนั้น สําหรับผู้ที่พระองค์ทรงประสงค์ และผู้ใดให้มีภาคี ขึ้นแก่อัลลอฮฺแล้ว แน่นอน เขาก็ได้หลงทางไปแล้วอย่างไกล');
INSERT INTO "king_fahad_thai" VALUES(4,117,'4:117','พวกเขาจะไม่วิงวอนขออื่นจากพระองค์นอกจากเจว็ดหญิง และพวกเขาจะไม่วิงวอนนอกจากชัยฏอนที่ดื้อดันเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(4,118,'4:118','อัลลอฮฺได้ทรงละอ์นัตมันแล้ว และมันได้กล่าวว่า แน่นอนยิ่งข้าพระองค์จะเอาจากปวงบ่าวขององค์ให้ได้ ซึ่งส่วนที่ถูกกําหนดไว้');
INSERT INTO "king_fahad_thai" VALUES(4,119,'4:119','และแน่นอนยิ่งข้าพระองค์จะทําให้พวกเขาหลงผิด และแน่นอนยิ่งข้าพระองค์จะทําให้พวกเขาเพ้อฝัน และแน่นอนยิ่งข้าพระองค์จะใช้พวกเขา แล้วแน่นอนพวกเขาก็จะผ่าหูปศุสัตว์ และแน่นอนยิ่งข้าพระองค์จะใช้พวกเขา แล้วแน่นอนพวกเขาก็จะเปลี่ยนแปลงสิ่งที่อัลลอฮฺทรงสร้าง และผู้ใดที่ยึดเอาชัยฏอนเป็นผู้ช่วยเหลือแล้ว แน่นอนเขาก็ขาดทุนอย่างชัดเจน');
INSERT INTO "king_fahad_thai" VALUES(4,120,'4:120','มันจะสัญญาแก่พวกเขา และจะทําให้พวกเขาเพ้อฝัน และชัยฏอนมันจะไม่สัญญานอกจากการหลอกลวงเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(4,121,'4:121','ชนเหล่านี้แหละที่อยู่ของพวกเขาก็คือนรกญะฮันนัม และพวกเขาจะไม่พบทางหนีใด ๆ ให้พ้นจากมันไปได้');
INSERT INTO "king_fahad_thai" VALUES(4,122,'4:122','และบรรดาผู้ที่ศรัทธาและประกอบสิ่งดีงามทั้งหลายนั้น เราจะให้พวกเขาเข้าบรรดาสวนสวรรค์ซึ่งมีแม่น้ําหลายสายไหลอยู่ภายใต้สวนสวรรค์เหล่านั้น โดยที่พวกเขาจะพํานักอยู่ในสวนสวรรค์เหล่านั้นตลอดกาล เป็นสัญญาอันแท้จริงของอัลลอฮฺ และใครเล่าที่มีคําพูดจริงยิ่งไปกว่าอัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(4,123,'4:123','มิใช่ความเพ้อฝันของพวกเจ้า และมิใช่ความเพ้อฝันของผู้ที่ได้รับคัมภีร์ ผู้ใดที่กระทําชั่วเขาก็ถูกตอบแทนด้วยความชั่วนั้นและเขาจะไม่พบผู้คุ้มครอง และผู้ช่วยเหลือใด ๆ สําหรับเขาอื่นจากอัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(4,124,'4:124','และผู้ใดกระทําในส่วนที่เป็นสิ่งดีงามทั้งหลาย ไม่ว่าจะเป็นเพศชายหรือเพศหญิงก็ตาม ในฐานะที่เขาเป็นผู้ศรัทธาแล้วไซร้ ชนเหล่านี้จะได้เข้าสวรรค์ และพวกเขาจะไม่ถูกอธรรมแม้เท่ารูเล็ก ๆ ที่อยู่บนหลังเมล็ดอินทผาลัม');
INSERT INTO "king_fahad_thai" VALUES(4,125,'4:125','และผู้ใดเล่าจะมีศาสนา ดียิ่งไปกว่าผู้ที่มอบใบหน้าของเขาให้แก่อัลลอฮฺ และขณะเดียวกันเขาก็เป็นผู้กระทําดี และปฏิบัติตามแนวทางของอิบรอฮ๊ม ผู้ใฝ่หาความจริง และอัลลอฮฺ ได้ถือเอาอิบรอฮีมเป็นสหาย');
INSERT INTO "king_fahad_thai" VALUES(4,126,'4:126','และสิ่งที่อยู่ในบรรดาชั้นฟ้าและสิ่งที่อยู่ในแผ่นดินนั้นเป็นสิทธิของอัลลอฮฺ และอัลลอฮฺนั้นทรงล้อมทุกสิ่งทุกอย่างไว้');
INSERT INTO "king_fahad_thai" VALUES(4,127,'4:127','และพวกเขาจะขอให้เจ้าชี้ขาดในเรื่องของบรรดาหญิง จงกล่าวเถิดว่าอัลลอฮฺจะทรงชี้ขาดให้แก่พวกท่านในเรื่องของนางเหล่านั้น และสิ่งที่ถูกอ่านให้พวกท่านฟังซึ่งอยู่ในคัมภีร์นั้น (และ) ในเรื่องของหญิงกําพร้าที่พวกท่านไม่ได้ให้แก่พวกนางซึ่งสิ่งที่ถูกกําหนดขึ้นแก่พวกนาง และพวกท่านปรารถนาจะแต่งงานกับพวกนาง และในเรื่องของบรรดาผู้อ่อนแอในหมู่เด็ก ๆ และในการที่พวกท่านจะดํารงไว้ซึ่งความยุติธรรมแก่บรรดาเด็กกําพร้า และความดีใด ๆ ที่พวกเจ้ากระทําไปนั้น แท้จริงอัลลอฮฺทรงรู้ในความดีนั้น');
INSERT INTO "king_fahad_thai" VALUES(4,128,'4:128','และหากหญิงใด เกรงว่าจะมีการปึ่งชา หรือมีการผินหลังให้ จากสามีของนางแล้วก็ไม่มีบาปใด ๆ แก่ทั้งสองที่จะตกลงประนีประนอมกันอย่างใดอย่างหนึ่ง และการประนีประนอมนั้นเป็นสิ่งดีกว่า และจิตใจคนนั้นถูกให้มีความตระหนี่มาด้วย และหากพวกเจ้ากระทําดี และมีความยําเกรงแล้ว แท้จริงอัลลอฮฺนั้นทรงรู้อย่างถี่ถ้วนในสิ่งที่พวกเจ้ากระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(4,129,'4:129','และพวกเจ้าไม่สามารถที่จะให้ความยุติธรรมในระหว่างบรรดาหญิง ได้เลย และแม้ว่าพวกเจ้าจะมีความปรารถนาอันแรงกล้าก็ตาม ดังนั้นพวกเจ้าจงอย่าเอียงไปหมด แล้วพวกเจ้าก็จะปล่อยให้บรรดานาง (ที่ถูกทอดทิ้ง) นั้นประหนึ่งผุ้ที่ถูกแขวนไว้ และหากพวกเจ้าประนีประนอมกัน และมีความยําเกรงแล้ว แท้จริงอัลลอฮฺนั้นเป็นผู้ทรงอภัยโทษ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(4,130,'4:130','และหากทั้งสองจะแยกกัน อัลลอฮฺก็จะทรงให้ความพอเพียงแก่เขาทั้งหมด จากความมั่งมีของพระองค์ และอัลลอฮฺนั้นเป็นผู้ทรงกว้างขวาง ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(4,131,'4:131','และสิ่งที่อยู่ในบรรดาชั้นฟ้าและสิ่งที่อยู่ในแผ่นดินนั้น เป็นสิทธิของอัลลอฮฺ และแท้จริงเราได้สั่งเสียไว้แก่บรรดาผู้ที่ได้รับคัมภีร์ก่อนจากพวกเจ้า และพวกเจ้าด้วย ว่าจงยําเกรง อัลลอฮฺเถิด และหากว่าพวกเจ้าปฏิเสธศรัทธา ก็แท้จริงสิ่งที่อยู่ในบรรดาชั้นฟ้า และสิ่งที่อยู่ในแผ่นดินนั้นเป็นสิทธิของอัลลอฮฺ และอัลลอฮฺเป็นผู้ทรงมั่งมีผู้ทรงได้รับการสรรเสริญ');
INSERT INTO "king_fahad_thai" VALUES(4,132,'4:132','และสิ่งที่อยู่ในบรรดาชั้นฟ้า และสิ่งที่อยู่ในแผ่นดินนั้นเป็นสิทธิของอัลลอฮฺ และพอเพียงแล้วที่อัลลอฮฺได้รับมอบหมายได้คุ้มครองรักษา');
INSERT INTO "king_fahad_thai" VALUES(4,133,'4:133','หากพระองค์ทรงประสงค์ก็จะทรงให้พวกเจ้าหมดไป มนุษย์เอ๋ย ! –และจงทรงนําพวกอื่นมา และอัลลอฮฺทรงเดชานุภาพเหนือสิ่งนั้น');
INSERT INTO "king_fahad_thai" VALUES(4,134,'4:134','ผู้ใดที่ต้องการสิ่งตอบแทนในโลกนี้ก็ที่อัลลอฮฺนั้นมีทั้งสิ่งตอบแทนในโลกนี้และปรโลก และอัลลอฮฺนั้น เป็นผู้ทรงได้ยินผู้ทรงเห็น');
INSERT INTO "king_fahad_thai" VALUES(4,135,'4:135','ผู้ศรัทธาทั้งหลาย ! จงเป็นผู้ที่ดํารงไว้ซึ่งความยุติธรรม จงเป็นพยานเพื่ออัลลอฮฺ และแม้ว่าจะเป็นอันตรายแก่ตัวของพวกเจ้าเอง หรือผู้บังเกิดเกล้าทั้งสองและญาติที่ใกล้ชิดก็ตาม หากเขาจะเป็นคนมั่งมีหรือคนยากจน อัลลอฮฺก็สมควรยิ่งกว่าเขาทั้งสอง ดังนั้นจงอย่าปฏิบัติตามความใคร่ใฝ่ต่ําในการที่พวกเจ้าจะมีความยุติธรรม และหากพวกเจ้าบิดเบือนหรือผินหลังให้แท้จริงอัลลอฮฺทรงรอบรู้อย่างถี่ถ้วนในสิ่งที่พวกเจ้ากระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(4,136,'4:136','ผู้ศรัทธาทั้งหลาย ! จงศรัทธาต่ออัลลอฮฺและร่อซูลของพระองค์เถิด และคัมภีร์ที่พระองค์ได้ทรงประทานลงมาแก่ร่อซูลของพระองค์ และคัมภีร์ที่พระองค์ได้ทรงประทานลงมาก่อนนั้น และผู้ใดปฏิเสธศรัทธาต่ออัลลอฮฺ และมลาอิกะฮ์ของพระองค์และบรรดาคัมภีร์ของพระองค์และบรรดาร่อซูลของพระองค์ และวันปรโลกแล้วไซร้ แน่นอนเขาก็ได้หลงทางไปแล้วอย่างไกล');
INSERT INTO "king_fahad_thai" VALUES(4,137,'4:137','แท้จริงบรรดาผู้ที่ศรัทธาแล้วปฏิเสธศรัทธาแล้วศรัทธา แล้วปฏิเสธศรัทธา แล้วเพิ่มการปฏิเสธศรัทธายิ่งขั้นนั้น ใช่ว่าอัลลอฮฺจะทรงอภัยโทษให้แก่พวกเขาก็หาไม่ และใช่ว่าพระองค์จะทรงแนะนําทางใดให้แก่พวกเขาก็หาไม่');
INSERT INTO "king_fahad_thai" VALUES(4,138,'4:138','จงแจ้งข่าวดีแก่พวกมุนาฟิก เถิดว่า แท้จริงพวกเขาจะได้รับการลงโทษอันเจ็บแสบ');
INSERT INTO "king_fahad_thai" VALUES(4,139,'4:139','บรรดาผู้ที่ยึดเอาบรรดาผู้ปฏิเสธศรัทธาเป็นมิตรอื่นจากผู้ศรัทธาทั้งหลายนั้น พวกเขาจะแสวงหากําลังอํานาจที่พวกเขากระนั้นหรือ แท้จริงกําลังอํานาจนั้นเป็นสิทธิของอัลลอฮฺทั้งหมด');
INSERT INTO "king_fahad_thai" VALUES(4,140,'4:140','และแน่นอน อัลลอฮฺได้ทรงประทานลงมาแก่พวกเจ้าแล้วในคัมภีร์ นั้นว่า เมื่อพวกเจ้าได้ยินบรรดาโองการของอัลลอฮฺโองการเหล่านั้นก็ถูกปฏิเสธศรัทธา และถูกเย้ยหยัน อังนั้นพวกเจ้าจงอย่านั่งร่วมกับพวกเขา จนกว่าพวกเขาจะพูดคุยกันในเรื่องอื่นจากนั้น แท้จริงพวกเจ้านั้น-ถ้าเช่นนั้นแล้ว ก็เหมือนพวกเขา แท้จริงอัลลอฮฺจะทรงรวบรวมบรรดามุนาฟิก และบรรดาผู้ปฏิเสธศรัทธาไว้ในนรกญะฮันนัมทั้งหมด');
INSERT INTO "king_fahad_thai" VALUES(4,141,'4:141','บรรดาผู้ที่คอยดู พวกเจ้าอยู่นั้นถ้าหากพวกเจ้าได้รับชัยชนะจากอัลลอฮฺ พวกเขาก็กล่าวว่าเรามิได้ร่วมกับพวกท่านดอกหรือ? และหากว่ามีส่วนได้ใดๆ แก่บรรดาผู้ปฏิเสธศรัทธาพวกเขาก็กล่าวว่าเรามิได้มีอํานาจเหนือพวกท่านดอกหรือ?และเรามิได้ป้องกันพวกท่านให้พ้นจากบรรดาผู้ศรัทธากระนั้นหรือ?อัลลอฮจะทรงตัดสินระหว่างพวกเจ้าในวันกิยามะฮ์และอัลลอฮจะไม่ทรงให้บรรดาผู้ปฏิเสธศรัทธามีท่างใดเหนือบรรดาผู้ศรัทธาเป็นอันขาด');
INSERT INTO "king_fahad_thai" VALUES(4,142,'4:142','แท้จริงบรรดามุนาฟิกนั้นกําลังหลอกลวงอัลลอฮฺ อยู่ ขณะเดียวกันพระองค์ก็ทรงหลอกลวงพวกเขา และเมื่อพวกเขาลุกขึ้นไปละหมาด พวกเขาก็ลุกขึ้นในสภาพเกียจคร้านโดยให้ผู้คนเห็นเท่านั้น และพวกเขาจะไม่กล่าวรําลึกพึงอัลลอฮฺ นอกจากเล็กน้อยเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(4,143,'4:143','โดยที่พวกเขาลังเลใจในระหว่างนั้น จะไปในทางพวกนี้ก็ไม่ไป จะไปทางพวกนี้ก็ไม่ไป และผู้ใดที่อัลลอฮฺให้หลงทางไปแล้ว เจ้าก็จะไม่พบทางใด ๆ สําหรับเขาเป็นอันขาด');
INSERT INTO "king_fahad_thai" VALUES(4,144,'4:144','ผู้ศรัทธาทั้งหลาย ! จงอย่าได้ยึดเอาบรรดาผู้ปฏิเสธศรัทธาเป็นมิตรอื่นจากผู้ศรัทธาทั้งหลาย พวกเจ้าต้องการที่จะให้อัลลอฮฺมีหลักฐานอันชัดเจนจัดการแก่พวกเจ้ากระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(4,145,'4:145','แท้จริงบรรดามุนาฟิกนั้นอยู่ในชั้นต่ําสุดจากนรก และเจ้าจะไม่พบผู้ช่วยเหลือใด ๆ สําหรับพวกเขาเป็นอันขาด');
INSERT INTO "king_fahad_thai" VALUES(4,146,'4:146','นอกจากบรรดาผู้ที่สํานึกผิดกลับตัว และปรับปรุงแก้ไข และยึดมั่นต่ออัลลอฮฺ และได้มอบการอิบาดะฮ์ของพวกเขาให้แก่อัลลอฮฺโดยสิ้นเชิง ชนพวกนี้แหละจะร่วมอยู่กับบรรดาผู้ศรัทธาและอัลลอฮฺจะทรงประทานแก่ผู้ศรัทธาทั้งหลายซึ่งรางวัลอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(4,147,'4:147','อัลลอฮฺทําการลงโทษพวกเจ้าทําไมหากพวกเจ้ากตัญญู และศรัทธา และอัลลอฮฺนั้นเป็น ผู้ทรงขอบใจ ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(4,148,'4:148','อัลลอฮฺไม่ทรงชอบการใช้เสียงดังในถ้อยคําที่เลวร้าย นอกจากผู้ที่ถูกข่มเหง และอัลลอฮฺนั้นเป็นผู้ทรงได้ยิน ทรงรอบรู้เลย');
INSERT INTO "king_fahad_thai" VALUES(4,149,'4:149','หากพวกเจ้าเปิดเผยความดี หรือปกปิดมันไว้ หรือให้อภัยในความเลวร้ายใด ๆ แล้ว แท้จริงอัลลอฮฺนั้นเป็นผู้ทรงอภัยโทษผู้ทรงอานุภาพเสมอ');
INSERT INTO "king_fahad_thai" VALUES(4,150,'4:150','แท้จริงบรรดาผู้ปฏิเสธการศรัทธาต่ออัลลอฮฺ และบรรดาร่อซูลของพระองค์และต้องการที่จะแยกระหว่างอัลลอฮฺ และบรรดาร่อซูลของพระองค์ และกล่าวว่า เราศรัทธาในบางคนและปฏิเสธศรัทธาในบางคน และพวกเขาต้องการที่จะยึดเอาในระหว่างนั้น ซึ่งทางใดทางหนึ่งนั้น');
INSERT INTO "king_fahad_thai" VALUES(4,151,'4:151','ชนเหล่านี้แหละคือผู้ปฏิเสธศรัทธาโดยแท้จริง และเราได้เตรียมไว้แล้ว ซึ่งการลงโทษที่ยังความอัปยศแก่ผู้ปฏิเสธศรัทธาทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(4,152,'4:152','และบรรดาผู้ที่ศรัทธาต่ออัลลอฮฺ และบรรดาร่อซูลของพระองค์ และมิได้แยกระหว่างคนหนึ่งคนใดในพวกเขา นั้น ชนเหล่านี้แหละพระองค์จะทรงประทานแก่พวกเขาซึ่งรางวัลของพวกเขา และอัลลอฮฺนั้นเป็นผู้ทรงอภัยโทษ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(4,153,'4:153','บรรดาผู้ได้รับคัมภีร์ จะขอร้องเจ้าให้เจ้านําคัมภีร์ฉบับหนึ่งจากฟากฟ้าลงมาแก่พวกเขา แท้จริงนั้นพวกเขาได้ขอร้องมูซาซึ่งสิ่งที่ใหญ่กว่านั้นมาแล้ว โดยที่พวกเขากล่าวว่า จงให้พวกเราเห็นอัลลอฮฺโดยชัดแจ้งเถิด แล้วฟ้าฝ่าก็ได้คร่าพวกเขา เนื่องด้วยความอธรรมของพวกเขา ภายหลังพวกเขาก็ได้ยึดถือลูกวัวหลังจากที่บรรดาหลักฐานอันชัดเจนได้มายังพวกเขา แล้วเราก็อภัยให้ในเรื่องนั้นและเราได้ให้แก่มูซาซึ่งอํานาจอันชัดเจน');
INSERT INTO "king_fahad_thai" VALUES(4,154,'4:154','และเราได้ยกภูเขาอัฏฏูร์ขึ้นเหนือพวกเขาเพื่อให้ได้มาซึ่งสัญญาของพวกเขา และเราได้กล่าวแก่พวกเขาว่า จงเข้าประตูนั้นไป โดยโน้มศีรษะลง และเราได้กล่าวแก่พวกเขาว่า จงอย่าได้ละเมิดในวันสับบะโต และเราได้เอาจากพวกเขาซึ่งสัญญาอันหนักแน่น');
INSERT INTO "king_fahad_thai" VALUES(4,155,'4:155','แล้วเราจึงได้กริ้วพวกเขา และละอ์นัตพวกเขา เนื่องด้วยการที่พวกเขาทําลายสัญญาของพวกเขา และปฏิเสธบรรดาโองการของอัลลอฮฺและฆ่าบรรดานะบี โดยปราศจากความเป็นธรรมและการที่พวกเขากล่าวว่า หัวใจของเรามีเปลือกหุ้มอยู่ หามิได้ อัลลอฮฺได้ทรงประทับตราบนหัวใจของพวกเขาต่างหาก เนื่องจากการปฏิเสธศรัทธาของพวกเขา ดังนั้นพวกเขาจึงไม่ศรัทธากัน นอกจากเพียงเล็กน้อยเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(4,156,'4:156','และเนื่องจากการที่พวกเขาปฏิเสธศรัทธา และกล่าวให้ร้ายแก่มัรยัม ซึ่งความเท็จอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(4,157,'4:157','และการที่พวกเขากล่าวว่า แท้จริงพวกเราได้ฆ่า อัล-มะซีห์ อีซา บุตรของมัรยัม ร่อซูลของอัลลอฮฺ และพวกเขาหาได้ฆ่าอีซาและหาได้ตรึงเขาบนไม้กางเขนไม่ แต่ทว่าเขาถูกให้เหมือนแก่พวกเขา และแท้จริงบรรดาผู้ที่ขัดแย้งในตัวเขา นั้นแน่นอนย่อมอยู่ในความสงสัย เกี่ยวกับเขาพวกเขาหามีความรู้ใด ๆ ต่อเขาไม่ นอกจากคล้อยตามความนึกคิดเท่านั้น และพวกเขามิได้ฆ่าเขาด้วยความแน่ใจ (อีซา)');
INSERT INTO "king_fahad_thai" VALUES(4,158,'4:158','หามิได้ อัลลอฮฺได้ทรงยกเขา (อีซา) ขึ้นไปยังพระองค์ต่างหาก และอัลลอฮฺเป็นผู้ทรงเดชานุภาพ ผู้ทรงปรีชาญาณเสมอ');
INSERT INTO "king_fahad_thai" VALUES(4,159,'4:159','และไม่มีอะฮ์ลิลกิตาบคนใด นอกจากแน่นอนเขาจะต้องศรัทธา ต่อท่านนะบีอีซา ก่อนที่เขาจะตาย และวันกิยามะฮ์ เขา(อีซา) จะเป็นพยานยืนยันพวกเขาเหล่านั้น');
INSERT INTO "king_fahad_thai" VALUES(4,160,'4:160','แล้วก็เนื่องด้วยความอธรรมจากบรรดาผู้ที่เป็นยิว เราจึงได้ให้เป็นที่ต้องห้ามแก่พวกเขาซึ่งบรรดาสิ่งดี ๆ ที่ได้ถูกอนุมัติแก่พวกเขามาแล้วและเนื่องด้วยการที่พวกเขาขัดขวางทางของอัลลอฮฺอย่างมากมาย ด้วย');
INSERT INTO "king_fahad_thai" VALUES(4,161,'4:161','และเนื่องด้วยการที่พวกเขาเอาดอกเบี้ยทั้ง ๆ ที่พวกเขาถูกห้ามในเรื่องนั้น และเนื่องด้วยการที่พวกเขากินทรัพย์ของผู้คนโดยไม่ชอบ และเราได้เตรียมไว้แล้ว สําหรับผู้ปฏิเสธศรัทธาทั้งหลาย ซึ่งการลงโทษอันเจ็บแสบ');
INSERT INTO "king_fahad_thai" VALUES(4,162,'4:162','แต่ทว่าบรรดาผู้มั่นในความรู้ในหมู่พวกเขา และบรรดาผุ้ที่ศรัทธานั้น พวกเขาย่อมศรัทธาต่อสิ่งที่ถูกประทานลงมาแก่เจ้า และสิ่งที่ถูกประทานลงมาก่อนเจ้า และบรรดาผู้ที่ดํารงไว้ซึ่งการละหมาด และบรรดาผู้ชําระซะกาต และบรรดาผู้ศรัทธาต่ออัลลอฮฺ และวันปรโลกชนเหล่านี้แหละเราจะให้แก่พวกเขาซึ่งรางวัลอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(4,163,'4:163','แท้จริงเราได้มีโองการแก่เจ้า เช่นเดียวกับที่เราได้มีโองการแก่นูฮ์ และบรรดานะบีหลังจากเขา และเราได้มีโองการแก่อิบรอฮีมและอิสมาอีล และอิสฮาก และยะอ์กูบ และอัล-อัสบาฏ และอีซา และอัยยูบ และยูนุส และฮารูน และ สุลัยมาน และเราได้ให้ ซุบูร์ แก่ดาวูด');
INSERT INTO "king_fahad_thai" VALUES(4,164,'4:164','และมีบรรดาร่อซูล ซึ่งเราได้เล่าถึงพวกเขาแก่เจ้ามาก่อนแล้ว และมีบรรดาร่อซูลซึ่งเรามิได้เล่าแก่เจ้าเกี่ยวกับพวกเขาและอัลลอฮฺได้ตรัสแก่มูซาจริงๆ');
INSERT INTO "king_fahad_thai" VALUES(4,165,'4:165','คือบรรดาร่อซูลในฐานะผู้แจ้งข่าวดี และในฐานะผู้ตักเตือน เพื่อว่ามนุษย์จะได้ไม่มีหลักฐานใด ๆ อ้างแก้ตัวแก่อัลลอฮฺได้ หลังจากบรรดาร่อซูลเหล่านั้น และอัลลอฮฺเป็นผู้ทรงเดชานุภาพ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(4,166,'4:166','แต่ทว่าอัลลอฮฺนั้นทรงยืนยันในสิ่ง ที่พระองค์ได้ทรงประทานลงมาแก่เจ้า ว่า พระองค์ได้ทรงประทานสิ่งนั้นมาด้วยความรู้ของพระองค์และมะลาอิกะฮ์ก็ยืนยันด้วย และพอเพียงแล้วที่อัลลอฮฺเป็นผู้ทรงยืนยัน');
INSERT INTO "king_fahad_thai" VALUES(4,167,'4:167','แท้จริงบรรดาผู้ที่ปฏิเสธศรัทธา และขัดขวางทางของอัลลอฮฺนั้น แน่นอนพวกเขาได้หลงทางไปแล้วอย่างไกล');
INSERT INTO "king_fahad_thai" VALUES(4,168,'4:168','แท้จริงบรรดาผู้ที่ปฏิเสธศรัทธา และอธรรมแก่ตัวเองนั้น ใช่ว่าอัลลอฮฺจะทรงอภัยโทษให้แก่พวกเขาก็หาไม่ และก็ใช่ว่าพระองค์จะทรงแนะนําแก่พวกเขา ซึ่งทางหนีทางใดก็หาไม่');
INSERT INTO "king_fahad_thai" VALUES(4,169,'4:169','นอกจากทางแห่งนรกญะฮันนัม โดยที่พวกเขาจะอยู่ในนั้นตลอดกาล และนั่นเป็นสิ่งง่ายดายแก่อัลลอฮฺเป็นสิทธิของอัลลอฮฺทั้งสิ้น และอัลลอฮฺนั้นเป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(4,170,'4:170','มนุษย์ชาติทั้งหลาย ! แท้จริงร่อซูลนั้น ได้นําความจริงจากพระเจ้าของพวกเจ้ามายังพวกเจ้าแล้ว จงศรัทธากันเถิด มันเป็นสิ่งดียิ่งแก่พวกเจ้า และหากพวกเจ้าปฏิเสธศรัทธา แล้ว แท้จริงสิ่งที่อยู่ในบรรดาชั้นฟ้า และในแผ่นดินนั้นเป็นสิทธิ์ของอัลลอฮฺทั้งสิ้น และอัลลอฮฺนั้นเป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(4,171,'4:171','อะฮ์ลุลกิตาบทั้งหลาย จงอย่าปฏิบัติให้เกินขอบเขต ในศาสนาของพวกเจ้า และจงอย่ากล่าวเกี่ยวกับอัลลอฮฺ นอกจากสิ่งที่เป็นจริงเท่านั้น แท้จริง อัล-มะซีฮ์ อีซาบุตรของมัรยัมนั้น เป็นเพียงร่อซูลของอัลลอฮฺ และเป็นเพียงดํารัสของพระองค์ที่ได้ทรงกล่าวมันแก่มัรยัม และเป็นเพียงวิญญาณหนึ่งจากพระองค์ เท่านั้น ดังนั้นจงศรัทธาต่ออัลลอฮฺ และบรรดาร่อซูลของพระองค์เถิด และจงอย่ากล่าวว่าสามองค์เลย จงหยุดยั้งเสียเถิด มันเป็นสิ่งดียิ่งแก่พวกเจ้า แท้จริงอัลลอฮฺคือผู้ควรได้รับการเคารพสักการะแต่เพียงองค์เดียวเท่านั้น พระองค์ทรงบริสุทธิ์จากการที่จะทรงมีพระบุตร สิ่งที่อยู่ในบรรดาชั้นฟ้า และสิ่งที่อยู่ในแผ่นดินนั้นเป็นสิทธิของพระองค์ทั้งสิ้น และเพียงพอแล้วที่อัลลอฮฺเป็นผู้ได้รับมอบหมายให้คุ้มครองรักษา');
INSERT INTO "king_fahad_thai" VALUES(4,172,'4:172','อัล-มะซีห์นั้นจะไม่หยิ่งเป็นอันขาดที่จะเป็นบ่าวของอัลลอฮฺ และมลาอิกะฮ์ผู้ใกล้ชิด (พระองค์) ก็ไม่หยิ่งด้วย และผู้ใดหยิ่งต่อการที่อิบาดะฮ์ ต่อพระองค์ และยะโสแล้ว พระองค์ก็จะทรงชุมนุมพวกเขาไว้ยังพระองค์ทั้งหมด');
INSERT INTO "king_fahad_thai" VALUES(4,173,'4:173','ส่วนบรรดาผู้ที่ศรัทธา และประกอบสิ่งที่ดีงามทั้งหลายนั้น พระองค์จะทรงตอบแทนพวกเขาโดยครบถ้วน ซึ่งรางวัลของพวกเขา และจะทรงเพิ่มให้แก่พวกเขาด้วย จากความกรุณาของพระองค์ และส่วนบรรดาผู้ที่หยิ่งยะโสนั้น พระองค์จะทรงลงโทษพวกเขา ซึ่งการลงโทษอันเจ็บแสบ และพวกเขาจะไม่พบผู้คุ้มครอง และผู้ช่วยเหลือใด สําหรับพวกเขาอื่นจากอัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(4,174,'4:174','มนุษยชาติทั้งหลาย! แน่นอนได้มีหลักฐาน จากพระเจ้าของพวกเจ้ามรยังพวกเจ้าแล้ว และเราได้ให้แสงสว่าง อันแจ่มแจ้งลงมาแก่พวกเจ้าด้วย');
INSERT INTO "king_fahad_thai" VALUES(4,175,'4:175','ส่วนบรรดาผู้ที่ศรัทธาต่ออัลลอฮฺ และยึดมั่นในพระองค์นั้น พระองค์จะทรงให้พวกเขาเข้าอยู่ในความเอ็นดูเมตตา และความโปรดปรานจากพระองค์ และจะทรงแนะนําพวกเขาซึ่งทางอันเที่ยงตรงไปสู่พระองค์');
INSERT INTO "king_fahad_thai" VALUES(4,176,'4:176','เขาเหล่านั้นจะขอให้เจ้าชี้ขาดปัญหา จงกล่าวเถิดว่า อัลลอฮฺ จะทรงชี้ขาดให้แก่พวกเจ้าในเรื่องของผู้เสียชีวิตที่มีมีบิดาและบุตร คือถ้าชายคนหนึ่งตาย โดยที่เขาไม่มีบุตรแต่มีพี่สาวหรือน้องสาวคนหนึ่งแล้ว นางจะได้รับครึ่งหนึ่งของมรดกที่เขาได้ทิ้งไว้ และขณะเดียวกันเขาก็จะได้รับมรดาของนาง หากนางไม่มีบุตร แต่ถ้าปรากฏว่าพี่สาวหรือน้องสาวของเขามีด้วยกันสองคน ทั้งสองนั้นจะได้รับสองในสามจากมรดกที่เขาได้ทั้งไว้ แต่ถ้าพวกเขาเป็นพี่น้องหลายคนทั้งชายและหญิง สําหรับชายจะได้รับเท่ากับส่วนได้ของหญิงสองคน ที่อัลลอฮฺทรงแจกแจงแก่พวกเจ้านั้น เนื่องจากการที่พวกเจ้าหลงฟิด และอัลลอฮฺนั้นทรงรอบรู้ในทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(5,1,'5:1','บรรดาผู้ศรัทธาทั้งหลาย! จงรักษาบรรดาสัญญา ให้ครบถ้วนเถิด สัตว์ประเภทปศุสัตว์ นั้นได้ถูกอนุมัติแก่พวกเจ้าแล้ว นอกจากที่จุถูกอ่านให้พวกเจ้าฟัง โดยที่พวกเจ้ามิใช่ผู้ที่ให้สัตว์ที่จะถูกล่านั้น เป็นที่อนุมัติขณะที่พวกเจ้าอยู่ในอิหรอม แท้จริงอัลลอฮ์นั้นทรงชี้ขาดตามที่พระองค์ทรงประสงค์');
INSERT INTO "king_fahad_thai" VALUES(5,2,'5:2','ผู้ศรัทธาทั้งหลาย! จงอย่าให้เป็นที่อนุมัติ ซึ่งบรรดาเครื่องหมายแห่งศาสนาของอัลลอฮ์ และเดือนที่ต้องห้าม และสัตว์พลี และสัตว์ที่ถูกสามเครื่องหมายไว้ที่คอเพื่อเป็นสัตว์พลี และบรรดาผุ้ที่มุ่งสู่บ้านอันเป็นที่ต้องห้าม โดยแสวงหาความโปรดปราน และความพอพระทัยจากพระเจ้าของพวกเขา แต่เมื่อพวกเจ้าเปลื้องอิห์รอมแล้ว ก็จงล่าสัตว์ได้ และจงอย่าให้การเกลียดชังแก่พวกหนึ่งพวกใด ที่ขัดขวางพวกเจ้ามิให้เข้ามัศยิดฮะรอม ทําให้พวกเจ้ากระทําการละเมิด และพวกจงช่วยเหลือกันในสิ่งที่เป็นคุณธรรม และความยําเกรง และจงอย่าช่วยกันในสิ่งที่เป็นบาป และเป็นศัตรูกันและพึงกลัวเกรงอัลลอฮ์เถิด แท้จริงอัลลอฮ์นั้นเป็นผู้ทรงรุนแรงในการลงโทษ');
INSERT INTO "king_fahad_thai" VALUES(5,3,'5:3','ได้ถูกห้ามแก่พวกเจ้าแล้ว ซึ่งสัตว์ที่ตายเอง และเลือด และเนื้อสุกร และสัตว์ที่ถูกเปล่งนามอื่นจากอัลลอฮ์ ที่มัน (ขณะเชือด) และสัตว์ที่ถูกรัดคอตาย และสัตว์ที่ถูกตีตาย และสัตว์ที่ตกเหวตาย และสัตว์ที่ถูกขวิดตาย และสัตว์ที่สัตว์ร้ายกัดกิน นอกจากที่พวกเจ้าเชือดกัน และสัตว์ที่ถูกเชือดบนแท่นหินบูชา และการที่พวกเจ้าเสี่ยงทายด้วยไม้ติ้ว เหล่านั้นเป็นการละเมิด วันนี้ บรรดาผู้ปฏิเสธการศรัทธา หมดหวังในศาสนาของพวกเจ้าแล้ว ดังนั้นพวกเจ้าจงอย่ากลัวพวกเขา และจงกลัวข้าเถิด วันนี้ข้าได้ให้สมบูรณ์แก่พวกเจ้าแล้ว ซึ่งศาสนาของพวกเจ้าและข้าได้ให้ครบถ้วนแก่พวกเจ้าแล้ว ซึ่งความกรุณาเมตตาของข้า และข้าได้เลือกอิสลามให้เป็นศาสนาแก่พวกเจ้าแล้ว ผู้ใดได้รับความคับขันในความหิวโหย โดยมิใช่เป็นผู้จงใจกระทําบาปแล้วไซร้ แน่นอนอัลลอฮ์นั้นเป็นผู้ทรงอภัยโทษผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(5,4,'5:4','เขาเหล่านั้นจะถามเจ้าว่า มีอะไรบ้างที่ถูกอนุมัติแก่พวกเขา จงกล่าวเถิด ที่ถูกอนุมัติแพวกเจ้านั้นคือสิ่งดี ๆ ทั้งหลาย และบรรดาสัตว์สําหรับล่าเนื้อที่พวกเจ้าฝึกสอนมัน พวกเจ้าจงบริโภคจากสิ่งที่มันจับมาให้แก่พวกเจ้า และจงกล่าวพระนามของอัลลอฮ์บนมันเสียก่อน และจงกลัวเกรงอัลลอฮ์เถิด แท้จริงอัลลอฮ์นั้นเป็นผู้ทรงรวดเร็วในการชําระสอบสวน');
INSERT INTO "king_fahad_thai" VALUES(5,5,'5:5','วันนี้สิ่งดี ๆ ทั้งหลายได้ถูกอนุมัติแก่พวกเจ้าแล้ว และอาหารของบรรดาผู้ที่ได้รับคัมภีร์นั้น เป็นที่อนุมัติแก่พวกเจ้าแล้ว และอาหารของพวกเจ้าก็เป็นที่อนุมัติแก่พวกเขาและบรรดาหญิงบริสุทธิ์ ในหมู่ผู้ศรัทธาหญิงและบรรดาหญิงบริสุทธิ์ในหมู่ผู้ที่ได้รับคัมภีร์ก่อน จากพวกเจ้าก็เป็นอนุมัติแก่พวกเจ้าด้วย เมื่อพวกเจ้าได้มอบให้แก่พวกนางซึ่งมะหัร์ของพวกนางในฐานะเป็นผู้แต่งงานมิใช่เป็นผู้กระทําการซินาโดยเปิดเผย และมิใช่ยึดเอานางเป็นเพื่อน โดยกระทําซินาลับ ๆ และผู้ใดปฏิเสธการศรัทธา แน่นอนงานของเขาก็ไร้ผล ขณะเดียวกันในวันปรโลกพวกเขาจะอยู่ในหมู่ผู้ที่ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(5,6,'5:6','ผู้ศรัทธาทั้งหลาย! เมื่อพวกเจ้ายืนขึ้นจะไปละหมาด ก็จงล้างหน้าของพวกเจ้า และมือของพวกเจ้าถึงข้อศอก และจงลูบศีรษะของพวกเจ้า และล้างเท้าของพวกเจ้าถึงตาตุ่มทั้งสอง และหากพวกเจ้ามีญะนาบะฮฺ ก็จงชําระร่างกายให้สะอาด และหากพวกเจ้าป่วย หรืออยู่ในการเดินทาง หรือคนใดในหมู่พวกท่านมาจากการถ่ายทุกข์ หรือได้สัมผัสหญิงมา แล้วพวกเจ้าไม่พบน้ําก็จงมุ่งสู่ดินที่ดี แล้วลูบใบหน้าของพวกเจ้า และมือของพวกเจ้า จากดินนั้น อัลลอฮฺนั้นไม่ทรงประสงค์เพื่อจะให้มีความลําบากใด ๆ แก่พวกเจ้า แต่ทว่าทรงประสงค์ที่จะให้พวกเจ้าสะอาด และเพื่อให้ความกรุณาเมตตาของพระองค์ครบถ้วนแก่พวกเจ้า เพื่อว่าพวกเจ้าจักขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(5,7,'5:7','และจงรําลึกถึงความกรุณาเมตตาของอัลลอฮ์ที่มีต่อพวกเจ้า และสัญญาของพระองค์ที่ได้ทรงกระทํามันไว้แก่พวกเจ้า ขณะที่พวกเจ้ากล่าวว่า พวกเราได้ยินแล้ว และพวกเราเชื่อฟังแล้ว และพึงยําเกรงอัลลอฮ์เถิด แท้จริงอัลลอฮ์นั้นทรงรอบรู้ถึงสิ่งที่อยู่ในทรวงอก');
INSERT INTO "king_fahad_thai" VALUES(5,8,'5:8','ผู้ศรัทธาทั้งหลาย ! จงเป็นผู้ปฏิบัติหน้าที่ด้วยดีเพื่ออัลลอฮ์ เป็นพยานด้วยความเที่ยงธรรมและจงอย่าให้การเกลียดชังพวกหนึ่งพวกใด ทําให้พวกเจ้าไม่ยุติธรรม จงยุติธรรมเถิด มันเป็นสิ่งที่ใกล้กับความยําเกรงยิ่งกว่า และพึงยําเกรง อัลลอฮ์เถิด แท้จริงอัลลอฮ์นั้น เป็นผู้ทรงรอบรู้อย่างละเอียดในสิ่งที่พวกเจ้ากระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(5,9,'5:9','และอัลลอฮ์ได้ทรงสัญญาแก่บรรดาผู้ที่ศรัทธา และประกอบสิ่งที่ดีงามทั้งหลายว่าสําหรับพวกเขานั้นคือ การอภัยโทษ และรางวัลอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(5,10,'5:10','และบรรดาผู้ที่ปฏิเสธศรัทธา และปฏิเสธบรรดาโองการของเรานั้น ชนเหล่านี้แหละคือชาวนรก');
INSERT INTO "king_fahad_thai" VALUES(5,11,'5:11','ผู้ศรัทธาทั้งหลาย ! จงรําลึกถึงความกรุณาของอัลลอฮ์ที่มีต่อพวกเจ้า ขณะที่พกวหนึ่งปลงใจที่จะยื่นมือของพวกเขามาทําร้ายพวกเจ้าแล้วพระองค์ก็ทรงยับยั้งและหันเหมือนพวกเขาออกจากพวกเจ้าเสีย และพึงยําเกรงอัลลอฮ์เถิด และแต่อัลลอฮ์เท่านั้น ผู้ศรัทธาทั้งหลายจงมอบหมาย');
INSERT INTO "king_fahad_thai" VALUES(5,12,'5:12','และแท้จริงอัลลอฮฺได้ทรงเอาสัญญาแก่วงศ์วานอิสรออีล และเราได้แต่งตั้งผู้ดูแลจากหมู่พวกเขาขึ้นสิบสองคน และอัลลอฮฺได้ทรงกล่าวว่า แท้จริงข้านั้นร่วมอยู่ด้วยกับพวกเจ้า ถ้าหากพวกเจ้าดํารงไว้ซึ่งการละหมาด และชําระซะกาต และศรัทธาต่อบรรดาร่อซูลของข้า และสนับสนุนพวกเขา และให้อัลลอฮฺยืมหนี้ที่ดี แล้วแน่นอนข้าจะลบล้างให้พ้นจากพวกเจ้า ซึ่งความชั่วทั้งหลายของพวกเจ้า และแน่นอนข้าจะให้พวกเจ้าเข้าบรรดาสวนสวรรค์ ซึ่งมีแม่น้ําหลายสายไหลอยู่เบื้องล่างของสวนสวรรค์เหล่านั้น แล้วผู้ใดในหมู่พวกเจ้าปฏิเสธ หลังจากนั้นแล้ว แน่นอนเขาก็หลงทางอันเที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(5,13,'5:13','แต่เนื่องจากการที่พวกเขาทําลายสัญญาของพวกเขา เราจึงได้ให้พวกเขาห่างไกลจากความกรุณาเมตตาของเราและให้หัวใจของพวกเขาแข็งกระด้าง พวกเขากระทําการบิดเบือน บรรดาถ้อยคําให้เฉออกจากตําแหน่งของมัน และลืมส่วนหนึ่งจากสิ่งที่พวกเขาถูกเตือนไว้ และเจ้า ก็ยังคงมองเห็นอยู่ในการคดโกงจากพวกเขานอกจากเพียงเล็กน้อยในหมู่พวกเขาเท่านั้น จงอภัยให้แก่พวกเขาเถิด และเมินหน้าเสีย แท้จริงอัลลอฮ์นั้นทรงชอบผู้ทําดีทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(5,14,'5:14','และจากบรรดาผู้ที่กล่าวว่า พวกเราเป็นคริสต์นั้น เราได้เอาสัญญาจากพวกเขา แต่แล้วพวกเขาก็ลืมส่วนหนึ่งจากสิ่งที่พวกเขาถูกเตือนไว้ เราจึงได้ให้เกิดขึ้นระหว่างพวกเขาซึ่งการเป็นศัตรูและการเกลียดชังกันจนกระทั่งวันกิยามะฮ์ และอัลลอฮ์จะทรงบอกเขาเหล่านั้นถึงสิ่งที่เขาเหล่านั้นได้กระทํามาก่อน');
INSERT INTO "king_fahad_thai" VALUES(5,15,'5:15','บรรดาผู้ได้รับคัมภีร์ทั้งหลาย ! แท้จริงร่อซูลของเรา ได้มายังพวกเจ้าแล้ว โดยที่เขาจะแจกแจงแก่พวกเจ้า ซึ่งมากมายจากสิ่งที่พวกเจ้าปกปิดไว้จากคัมภีร์ และเขาจะระงับไว้มากมาย แท้จริงแสงสว่างจากอัลลอฮ์ และคัมภีร์อันชัดแจ้งนั้นได้มายังพวกเจ้าแล้ว');
INSERT INTO "king_fahad_thai" VALUES(5,16,'5:16','ด้วยคัมภีร์นั้นแหละ อัลลอฮ์จะทรงแนะนําผู้ที่ปฏิบัติตามความพึงพระทัยของพระองค์ ซึ่งบรรดาทางแห่งความปลอดภัย และจะทรงให้พวกเขาออกจากความมืดไปสู่แสงสว่างด้วยอนุมัติของอัลลอฮ์ และจะทรงแนะนําพวกเขาสู่ทางอันเที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(5,17,'5:17','แน่นอนได้ปฏิเสธศรัทธาแล้วบรรดาผู้ที่กล่าวว่า แท้จริงอัลลอฮ์นั้นคืออัลมะซีห์ บุตรของมัรยัม จงกล่าวเถิด(มุฮัมมัด) ก็ใครเล่าที่จะมีอํานาจครอบครองสิ่งของ จากอัลลอฮ์ได้ หากพระองค์ทรงประสงค์ที่จะทําลายอัล-มะซีห์ บุตรของมัรยัม และมารดาของเขา และผู้ที่อยู่ในแผ่นดินทั้งหมด และอํานาจแห่งบรรดาชั้นฟ้า และแผ่นดินและสิ่งที่อยู่ในระหว่างทั้งสองนั้นเป็นสิทธิของอัลลอฮ์เท่านั้น และอัลลอฮ์นั้นทรงเดชานุภาพเหนือทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(5,18,'5:18','และบรรดาชาวยิว และชาวคริสต์ได้กล่าวว่า พวกเราคือบุตรของอัลลอฮ์ และเป็นที่รักใคร่ของพระองค์ จงกล่าวเถิด (มุฮัดมัด) แล้วไฉนเล่าพระองค์จึงทรงลงโทษพวกท่าน เนื่องด้วยความผิดทั้งหลายของพวกท่าน มิใช่เช่นนั้นดอกพวกท่านเป็นสามัญชนในหมู่ผู้ที่พระองคืทรงบังเกิดมาต่างหาก ซึ่งพระองค์จะทรงอภัยโทษแก่ผู้ที่พระงอค์ทรงประสงค์ และอํานาจแห่งบรรดาชั้นฟ้า และแผ่นดิน และสิ่งที่อยู่ในระหว่างทั้งสองนั้น เป็นสิทธิของอัลลอฮ์เท่านั้น และยังพระองค์นั้นคือการกลับไป');
INSERT INTO "king_fahad_thai" VALUES(5,19,'5:19','บรรดาผู้ได้รับคัมภีร์ทั้งหลาย ! แท้จริงร่อซูล ของเราได้มายังพวกเจ้าแล้ว โดยที่เขาจะได้ชี้แจงแก่พวกเจ้า ตามวาระสมัยที่ได้ว่างเว้นบรรดาร่อซูลมา ทั้งนี้เนื่องจากการที่พวกเจ้าจะกล่าวว่า มิได้มีผู้แจ้งข่าวดีคนใด และผู้ตักเตือนคนใดมายังพวกเรา แท้จริงได้มีผู้แจ้งข่าวดีและผู้ตักเตือนมายังพวกเจ้าแล้ว และอัลลอฮ์นั้นทรงเดชานุภาพเหนือทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(5,20,'5:20','และจงรําลึกถึงขณะที่มูซาได้กล่าวแก่ประชาชาติของเขาว่า โอ้ประชาชาติของฉัน ! พึงรําลึกถึงความกรุณาเมตตาของอัลลอฮ์ที่มีแด่พวกท่านเถิด เพราะว่าพระองค์ได้ทรงให้มีบรรดานะบีขึ้นในหมู่พวกท่าน และได้ทรงให้พวกท่านเป็นกษัตริย์ และได้ทรงประทานแก่พวกท่าน สิ่งที่มิได้ทรงประทานให้แก่ผู้ใดในหมู่ประชาชาติทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(5,21,'5:21','โอ้ประชาชาติของฉัน ! จงเข้าไปในแผ่นดินอันบริสุทธิ์ ที่อัลลอฮ์ได้ทรงกําหนดให้แก่พวกท่านเถิด และจงอย่าหันหลังของพวกท่านกลับ เพราะจะทําให้พวกท่านกลับกลายเป็นผู้ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(5,22,'5:22','พวกเขากล่าวว่า โอ้มูซา แท้จริงในแผ่นดินอันบริสุทธิ์นั้นมีพวกที่เหี้ยมโหด และพวกเราจะไม่เข้าไปในแผ่นดินนั้นเป็นอันขาด จนกว่าพวกเขาจะออกไปจากที่นั้น แต่ถ้าพวกเขาออกไปจากที่นั้นแล้ว พวกเราจึงจะเป็นผู้เข้าไป');
INSERT INTO "king_fahad_thai" VALUES(5,23,'5:23','มีชายสองคนในหมู่ผู้ยําเกรงที่อัลลอฮ์ได้ทรงกรุณาเมตตาแก่เขามทั้งสองได้กล่าวว่าพวกท่านจงเข้าประตูนั้น ไปเผชิญหน้ากับพวกเขาเถิดครั้นเมื่อพวกท่านเข้าประตูนั้นไปแล้ว แน่นอนพวกท่านจะเป็นผู้ชนะ และแด่อัลลอฮ์นั้นพวกเจ้าจงมอบหมายเถิด หากพวกท่านเป็นผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(5,24,'5:24','พวกเขากล่าวว่า โอ้มูซา ! แท้จริงพวกเราจะไม่เข้าไปที่นั้นโดยเด็ดขาด ตราบใดที่พวกเขา ยังคงอยู่ที่นั้น ดังนั้นท่านและพระเจ้าของท่านจงไปเถิด แล้วจงต่อสู้ พวกเราจะนั่งอยู่ที่นี่');
INSERT INTO "king_fahad_thai" VALUES(5,25,'5:25','เขา กล่าวว่า โอ้พระเจ้าแห่งข้าพระองค์แท้จริงข้าพระองค์ไม่มีอํานาจ นอกจากตัวของข้าพระองค์เองและพี่ชายของข้าพระองค์ เท่านั้น ดังนั้นโปรดได้แยกระหว่างเรา กับประชาชาติผู้ละเมิดด้วยเถิด');
INSERT INTO "king_fahad_thai" VALUES(5,26,'5:26','พระองค์ตรัสว่า แท้จริงแผ่นดินนั้น เป็นที่ต้องห้ามแก่พวกเขา สี่สิบปี ซึ่งพวกเขาจะระเหเร่ร่อนไปในผืนแผ่นดิน ดังนั้นเจ้าจงอย่าเสียใจให้แก่ประชาชาติผู้ละเมิดเหล่านั้นเลย');
INSERT INTO "king_fahad_thai" VALUES(5,27,'5:27','และเจ้าจงอ่านให้พวกเขา ฟัง ซึ่งข่าวคราวเกี่ยวกับบุตรชายสองคน ของอาดัมตามความเป็นจริง ขณะที่ทั้งสองได้กระทําการพลีซึ่งสิ่งพลี อยู่นั้น แล้วสิ่งพลีนั้นก็ถูกรับจากคนหนึ่งในสองคน และมันมิไสด้ถูกรับจากอีกคนหนึ่ง เขา จึงได้กล่าวว่า แน่นอนข้าจะฆ่าเจ้า ให้ได้เขา กล่าวว่า แท้จริงอัลลอฮ์จะทรงรับจากหมู่ผู้มีความยําเกรงเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(5,28,'5:28','หากท่าน ยื่นมือของท่านมายังฉัน เพื่อจะฆ่าฉัน ฉันก็จะไม่ยื่นมือของฉันไปยังท่าน เพื่อจะฆ่าท่าน แท้จริงฉันกลัวอัลลอฮ์ผู้เป็นพระเจ้าแห่งสากลโลก');
INSERT INTO "king_fahad_thai" VALUES(5,29,'5:29','แท้จริงฉันต้องการที่จะให้ท่านนําบาปของฉันและบาปของท่านกลับไป แล้วท่านก็จะกลายเป็นคนหนึ่งในหมู่ชาวนรก และนั่นแหละคือการตอบแทนแก่บรรดาผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(5,30,'5:30','แล้วจิตใจของเขา ก็คล้อยตามเขาในการที่จะฆ่าน้องชายของเขา แล้วเขาก็ฆ่าน้องชายของเขา ดังนั้นเขาจึงได้กลายเป็นคนหนึ่งในหมู่ผู้ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(5,31,'5:31','แล้วอัลลอฮ์ก็ได้ส่งกาตัวหนึ่งมาคุ้ยหาในดิน เพื่อที่จะให้เขาเห็นว่าเขาจะกลบศพน้องชายของเขาอย่างไรเขากล่าวว่า โอ้ความพินาศของฉัน ฉันไม่สามารถที่จะเป็นเช่นกาตัวนี้แล้วกลบศพน้องชายของฉันเชียวหรือนี่? แล้วเขาก็กลายเป็นคนหนึ่งในหมู่ผู้ตรอมใจ');
INSERT INTO "king_fahad_thai" VALUES(5,32,'5:32','เนื่องจากเหตุนั้นแหละ เราจึงได้บัญญัติแก่วงศ์วาน อิสรออีลว่า แท้จริงผู้ใดฆ่าชีวิตหนึ่งโดยมิใช่เป็นการชดเชยอีกชีวิตหนึ่ง หรือมิใช่เนื่องจากกการบ่อนทําลายในแผ่นดินแล้วก็ประหนึ่ง่าเขาได้ฆ่ามนุษย์ทั้งมวล และแท้จริงนั้นบรรดาร่อซูลของเราได้นําหลักฐานต่าง ๆ อันชัดแจ้งมายังพวกเขาแล้ว แล้วได้มีจํานวนมากมายในหมู่พวกเขาเป็นผู้ฟุ่มเฟือยในแผ่นดิน');
INSERT INTO "king_fahad_thai" VALUES(5,33,'5:33','แท้จริงการตอบแทนแก่บรรดาผู้ที่ทําสงครามต่ออัลลอฮ์ และร่อซูลของพระองค์ และพยายามบ่อนทําลายในแผ่นดิน นั้นก็คือการที่พวกเขาจะถูกฆ่า หรือถูกตรึงบนไม่กางเขน หรือมือของพวกเขาและเท้าของพวกเขาจะถูกตัดสลับข้าง หรือถูกเนรเทศออกไปจากแผ่นดิน นั้นก็คือพวกเขาจะได้รับความอัปยศในโลกนี้ และจะได้รับการลงโทษอันใหญ่หลวงในปรโลก');
INSERT INTO "king_fahad_thai" VALUES(5,34,'5:34','นอกจากบรรดาผู้ที่สํานึกผิดกลับเนื้อกลับตัว ก่อนจากที่พวกเจ้าจะสามารถลงโทษพวกเขา พึงรู้เถิดว่า แท้จริงอัลลอฮ์นั้นเป็นผู้ทรงอภัยโทษ ผู้ทรงเอ็นดูเมตตา');
INSERT INTO "king_fahad_thai" VALUES(5,35,'5:35','ผู้ศรัทธาทั้งหลาย ! พึงยําเกรงอัลลอฮ์เถิด และจงแสวงหาสื่อ ไปสู่พระองค์ และจงต่อสู้และเสียสละในทางของอัลลอฮ์เถิด เพื่อว่าพวกเจ้าจะได้รับความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(5,36,'5:36','แท้จริงบรรดาผู้ปฏิเสธศรัทธานั้น หากพวกเขามีสิ่งที่อยู่ในแผ่นดินทั้งหมด และมีเยี่ยงนั้นอีกรวมกัน เพื่อจะใช้มันไถ่ตัวให้พ้นจากการลงโทษในวันกิยามะฮ์แล้ว มันก็จะไม่ถูกรับจากพวกเขา และสําหรับพวกเขานั้นคือการลงโทษอันเจ็บแสบ');
INSERT INTO "king_fahad_thai" VALUES(5,37,'5:37','เขาเหล่านั้นปรารถนาที่จะออกจากไฟนรก แต่พวกเขาก็หาได้ออกจากมันไปได้ไม่ และสําหรับพวกเขานั้นคือการลงโทษที่คงอยู่ตลอดไป');
INSERT INTO "king_fahad_thai" VALUES(5,38,'5:38','และขโมยชายและขโมยหญิงนั้นจงตัดมือของเขา ทั้งสองคน ทั้งนี้เพื่อเป็นการตอบแทนในสิ่งที่ทั้งสองนั้นได้แสวงหาไว้ (และ) เพื่อเป็นเยี่ยงอย่างการลงโทษ จากอัลลอฮ์ และอัลลอฮ์นั้นทรงเดชานุภาพ ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(5,39,'5:39','แล้วผู้ใดสํานึกผิดกลับเนื้อกลับตัวหลังจากการอธรรมของเขา และแก้ไขปรับปรุงแล้ว แท้จริงอัลลอฮฺจะทรงอภัยโทษให้แก่พวกเขา แท้จริงอัลลอฮฺนั้นเป็นผู้ทรงอภัยโทษ ผู้ทรงเอ็นดูเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(5,40,'5:40','เจ้ามิได้รู้ดอกหรือว่า แท้จริงอัลลอฮ์นั้นทรงมีอํานาจในบรรดาชั้นฟ้าและแผ่นดิน โดยที่พระองค์จะทรงลงโทษใครก็ได้ที่พระองค์ทรงประสงค์ และจะทรงอภัยโทษแก่ใครก็ได้ที่พระองค์ทรงประสงค์ และอัลลอฮ์นั้น ทรงเดชานุภาพเหนือทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(5,41,'5:41','รอซูลเอ๋ย ! จงอย่าให้เป็นที่เสียใจแก่เจ้าซึ่งบรรดาผู้ที่รีบเร่งกันในการปฏิเสธศรัทธาจากหมู่ผู้ที่กล่าวด้วยปากของพวกเขาว่า พวกเราศรัทธาแล้วโดยที่หัวใจของพวกเขามิได้ศรัทธา และจากหมู่ผู้ที่เป็นยิวด้วย โดยที่พวกเขาชอบฟังคํามุสา พวกเขาชอบฟังเพื่อพวกอื่นที่มิได้มุ่งหาเจ้า พวกเขาบิดเบือนบรรดาถ้อยคําหลังจาก (ที่มันถูกวางใน) ที่ของมัน พวกเขากล่าวว่า หากพวกท่านได้รับสิ่งนี้ก็จงเอามันไว้ และถ้าหากพวกท่านมิได้รับมันก็จงระวัง และผู้ใดที่อัลลอฮ์ทรงประสงค์ซึ่งการทดสอบเขาแล้ว เจ้าก็ไม่มีสิทธิแต่อย่างใดจากอัลลอฮ์ที่จะช่วยเหลือเขาได้ ชนเหล่านี้แหละคือผู้ที่อัลลอฮ์มิทรงประสงค์จะให้หัวใจของพวกเขาสะอาด โดยที่พวกเขาจะได้รับความอัปยศในโลกนี้ และจะได้รับการลงโทษอันมหันต์ในปรโลก');
INSERT INTO "king_fahad_thai" VALUES(5,42,'5:42','พวกเขาชอบฟังคํามุสา ชอบกินสิ่งต้องห้าม ถ้าหากพวกเขามาหาเจ้า ก็จงตัดสินระหว่างพวกเขา หรือไม่ก็หลีกเลี่ยงพวกเขาเสีย และถ้าหากเจ้าหลีกเลี่ยงพวกเขา พวกเขาก็จะไม่ให้โทษแก่เจ้าได้แต่อย่างใดเลย และหากเจ้าตัดสินใจ ก็จงตัดสินใจระหว่างพวกเขา ด้วยความยุติธรรม แท้จริงอัลลอฮ์นั้นทรงรักบรรดาผู้ที่ยุติธรรม');
INSERT INTO "king_fahad_thai" VALUES(5,43,'5:43','และอย่างไรเล่าที่พวกเขาจะให้เจ้าตัดสินทั้ง ๆ ที่พวกเขามี อัต-เตารอตอยู่ ซึ่งในนั้นมีข้อตัดสินของอัลลอฮ์อยู่แล้วแล้วพวกเขาก็ผินหลังให้ หลังจากนั้น ชนเหล่านี้หาใช่เป็นผู้ศรัทธาไม่');
INSERT INTO "king_fahad_thai" VALUES(5,44,'5:44','แท้จริงเราได้ให้อัต-เตารอตลงมา โดยที่ในนั้นมีข้อแนะนําและแสงสว่าง ซึ่งบรรดานบีที่สวามิภักดิ์ได้ใช้อัต-เตารอตตัดสินบรรดาผู้ที่เป็นยิว และบรรดาผู้ที่รู้แล้วในอัลลอฮ์ และนักปราชญ์ทั้งหลายก็ได้ใช้อัต-เตารอต ตัดสิน ด้วย เนื่องด้วยสิ่งที่พวกเขาได้รับมอบหมายให้รักษาไว้ (นั่นคือ) คัมภีร์ของอัลลอฮ์ และพวกเขาก็เป็นพยานยืนยันในคัมภีร์นั้นด้วย ดังนั้นพวกเจ้า จงอย่ากลัวมนุษย์แต่จงกลัวข้าเถิด และจงอย่าแลกเปลี่ยนบรรดาโองการของข้ากับราคาอันเล็กน้อย และผู้ใดที่มิได้ตัดสินด้วยสิ่งที่อัลลอฮ์ได้ทรงประทานลงมาแล้ว ชนเหล่านี้แหละคือผู้ปฏิเสธการศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(5,45,'5:45','และเราได้บัญญัติแก่พวกเขาไว้ในคัมภีร์นั้นว่า ชีวิตด้วยชีวิต และตาด้วยตา และจมูกด้วยจมูก และหูด้วยหู และฟันด้วยฟัน และบรรดาบาดแผลก็ให้มีการชดเชยเยี่ยงเดียวกัน และผู้ใดให้การชดเชยนั้นเป็นทาน มันก็เป็นสิ่งลบล้างบาปของเขา และผู้ใดมิได้ตัดสินด้วยสิ่งที่อัลลอฮ์ได้ทรงประทานลงมาแล้ว ชนเหล่านี้แหละคือผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(5,46,'5:46','และเราได้ให้อีซาบุตรของมัรยัมตามหลังพวกเขามา ในฐานะผู้ยืนยันสิ่งที่อยู่เบื้องหน้าเขาคือ อัต-เตารอต และเราได้ให้อัล-อินญีลแก่เขา ซึ่งในนั้นมีคําแนะนําและแสงสว่าง และเป็นที่ยืนยันสิ่งที่อยู่เบื้องหน้ามัน คืออัต-เตารอต และเป็นคําแนะนํา และคําตักเตือนแก่ผู้ยําเกรงทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(5,47,'5:47','และบรรดาผู้ที่ได้รับอัล-อินญีลก็จงตัดสินด้วยสิ่งที่อัลลอฮ์ได้ทรงประทานลงมาในนั้น และผู้ใดที่มิได้ตัดสินด้วยสิ่งที่อัลลอฮ์ได้ทรงประทานลงมาแล้ว ชนเหล่านี้คือผู้ที่ละเมิด');
INSERT INTO "king_fahad_thai" VALUES(5,48,'5:48','และเราได้ให้คัมภีร์ลงมาแก่เจ้า ด้วยความจริง ในฐานะเป็นที่ยืนยันคัมภีร์ที่อยู่เบื้องหน้ามันและเป็นที่ควบคุมคัมภีร์(เบื้องหน้า) นั้น ดังนั้นเจ้าจงตัดสินสินระหว่างพวกเขา ด้วยสิ่งที่อัลลอฮ์ทรงประทานลงมาเถิด และจงอย่าปฏิบัติตามความใคร่ใฝ่ต่ําของพวกเขา โดยเขวออกจากความจริงที่ได้มายังเจ้า สําหรับแต่ละประชาชาติในหมู่พวกเจ้านั้น เราได้ให้มีบทบัญญัติและแนวทางไว้ และหากอัลลอฮ์ทรงประสงค์แล้วแน่นอนก็ทรงให้พวกเจ้าเป็นประชาชาติเดียวกันแล้ว แต่ทว่าเพื่อที่จะทรงทดสอบพวกเจ้าในสิ่งที่พระองค์ได้ประทานแก่พวกเจ้า ดังนั้นพวกเจ้าจงแข่งขันกันในความดีทั้งหลายเถิด ยังอัลลอฮ์นั้นคือ การกลับไปของพวกเจ้าทั้งหมด แล้วพระองค์จะทรงแจ้งให้พวกเจ้าทราบในสิ่งที่พวกเจ้ากําลังขัดแย้งกันในสิ่งนั้น');
INSERT INTO "king_fahad_thai" VALUES(5,49,'5:49','และเจ้า จงตัดสินระหว่างพวกเขา ด้วยสิ่งที่อัลลอฮ์ได้ทรงประทานลงมาเถิด และจงอย่าปฏิบัติตามความใคร่ใฝ่ต่ําของพวกเขา และจงระวังพวกเขา ในการที่พวกเขาจะจูงใจเจ้าให้เขวออกจากบางสิ่ง ที่อัลลอฮ์ได้ทรงประทานลงมาแก่เจ้า แล้วถ้าหากพวกเจ้าผินหลังให้ ก็พึงรู้เถิดว่า แท้จริงอัลลอฮ์นั้นเพียงประสงค์จะให้ประสบแก่พวกเขาซึ่งบางส่วนแห่งโทษของพวกเขา เท่านั้น และแท้จริง จํานวนมากมายในหมู่มนุษย์นั้นเป็นผู้ละเมิด');
INSERT INTO "king_fahad_thai" VALUES(5,50,'5:50','ข้อตัดสินสมัยญาฮิลีญะฮ์ กระนั้นหรือ ที่พวกเขาปรารถนา และใครเล่าที่จะมีข้อตัดสินดียิ่งกว่าอัลลอฮ์สําหรับกลุ่มชนที่เชื่อมั่น');
INSERT INTO "king_fahad_thai" VALUES(5,51,'5:51','ผู้ศรัทธาทั้งหลาย ! จงอย่าได้ยึดเอาชาวยิวและชาวคริสต์เป็นมิตร บางส่วนของพวกเขาคือมิตรของอีกบางส่วน และผู้ใดในหมู่พวกเจ้าเอาพวกเขามาเป็นมิตรแล้วไซร้ แน่นอนผู้นั้นก็เป็นคนหนึ่งในพวกเขา แท้จริงอัลลอฮ์นั้นไม่ทรงแนะนํากลุ่มชนที่อธรรม');
INSERT INTO "king_fahad_thai" VALUES(5,52,'5:52','แล้วเจ้าจะได้เห็นบรรดาผู้ที่ในหัวใจของพวกเขามีโรค ต่างรีบเร่งกันไปอยู่ในหมู่พวกเขา โดยกล่าวว่า พวกเรากลัวภัยพิบัติ จะเวียนมาประสบแก่พวกเรา อาจเป็นไปได้ว่าอัลลอฮ์นั้นจะทรงนํามาซึ่งชัยชนะ หรือไม่ก็นําพระบัญชาอย่างหนึ่งอย่างใดมาจากที่พระองค์ แล้วพวกเขาก็กลายเป็นผู้เสียใจต่อสิ่งที่พวกเขาปกปิดไว้ในใจของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(5,53,'5:53','และบรรดาผู้ที่ศรัทธากล่าวว่า ชนเหล่านี้หรือ คือผู้ที่สามบานต่ออัลลอฮ์อย่างเข้มแข็งว่า แท้จริงพวกเขานั้นจะรวมอยู่กับพวกเจ้า การงานของพวกเขานั้นไร้ผล แล้วพวกเขาก็กลายเป็นผู้ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(5,54,'5:54','บรรดาผู้ศรัทธาทั้งหลาย ! ผู้ใดในหมู่พวกเจ้ากลับออกจากศาสนาของพวกเขาไปอัลลอฮ์ ก็จะทรงนํามาซึ่งพวกหนึ่ง ที่พระองค์ทรงรักพวกเขาและพวกเขาก็รักพระองค์ เป็นผู้นอบน้อมถ่อมตนต่อบรรดามุมิน ไว้เกียรติแก่บรรดาผู้ปฏิเสธศรัทธาพวกเขาจะเสียสละและต่อสู้ในทางของอัลลอฮ์ และไม่กลัวการตําหนิของผู้ตําหนิคนใดนั่นคือความโปรดปรานของอัลลอฮ์ซึ่งพระองค์จะทรงประทานมันแก่ผุ้ที่พระองค์ทรงประสงค์ และอัลลอฮ์นั้นเป็นผู้ทรงกว้างขวาง ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(5,55,'5:55','แท้จริงผู้ที่เป็นมิตรของพวกเจ้านั้น คืออัลลอฮ์ และร่อซูลของพระองค์ และบรรดาผู้ศรัทธาที่ดํารงไว้ซึ่งการละหมาด และชําระซะกาตและขณะเดียวกันพวกเขาก็เป็นผู้นอบน้อม');
INSERT INTO "king_fahad_thai" VALUES(5,56,'5:56','และผู้ใดให้อัลลอฮฺและร่อซูลของพระองค์ และบรรดาผู้ที่ศรัทธาเป็นมิตรแล้วไซร้ แท้จริงพรรคของอัลลอฮฺนั้น คือพวกที่ชนะ');
INSERT INTO "king_fahad_thai" VALUES(5,57,'5:57','ผู้ศรัทธาทั้งหลาย ! จงอย่าได้ยึดเอามาเป็นมิตรผู้ซึ่งถือเอาศรัทธาของพวกเจ้าเป็นการเย้ยหยัน และเป็นการล้อเล่น จากบรรดาผู้ที่ได้รับคัมภีร์ ก่อนพวกเจ้า และบรรดาผู้ปฏิเสธศรัทธาทั้งหลาย และพึงยําเกรงอัลลอฮ์เถิด หากพวกเจ้าเป็นผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(5,58,'5:58','และเมื่อพวกเจ้าได้เรียกร้องไปสู่การละหมาด พวกเขาก็ถือเอาการละหมาดเป็นการเย้ยหยันเป็นการล้อเล่นนั่นก็เพราะพวกเขาเป็นพวกที่ไม่ใช้ปัญญา');
INSERT INTO "king_fahad_thai" VALUES(5,59,'5:59','จงกล่าวเถิด (มุฮัมมัด) ว่า โอ้บรรดาผู้ได้รับคัมภีร์ทั้งหลาย ! พวกท่านมิได้ตําหนิติเตียนและปฏิเสธพวกเรา(เพราะอื่นใด) นอกจากว่าพวกเราศรัทธาต่ออัลลอฮ์ และสิ่งที่ถูกประทานลงมาแก่เรา และสิ่งที่ถูกประทานลงมาก่อนแล้วเท่านั้น และแท้จริงส่วนมากของพวกท่านนั้นเป็นผู้ละเมิด');
INSERT INTO "king_fahad_thai" VALUES(5,60,'5:60','จงกล่าวเถิด (มุฮัมมัด) ว่าจะให้ฉันบอกแก่พวกท่านไหม ถึงการตอบแทนที่เลวร้ายยิ่งกว่านั้น ณ ที่อัลลอฮ์ คือผู้ที่อัลลอฮ์ได้ทรงละอ์นัต เขาและกริ้วโกรธเขา และให้ส่วนหนึ่งในพวกเขาเป็นลิง และเป็นสุกร และเป็นผู้สักการะชัยฎอน ชนเหล่านี้แหละคือผู้ที่มีตําแหน่งอันชั่วร้ายและเป็นผู้ที่หลงไปจากทางอันเที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(5,61,'5:61','และเมื่อเขาเหล่านั้น มาหาพวกเจ้า พวกเขาก็กล่าวว่า เราศรัทธาแล้ว ทั้ง ๆ ที่โดยแท้จริงนั้น พวกเขาเข้ามาในสภาพผู้ปฏิเสธศรัทธา และขณะที่พวกเขาออกไปก็ในสภาพนั้น และอัลลอฮ์เป็นผู้ทรงรู้ยิ่งในสิ่งที่พวกเขาปกปิด');
INSERT INTO "king_fahad_thai" VALUES(5,62,'5:62','และเจ้าจะได้เห็นมากมายในหมู่พวกเขา ต่างรีบเร่งกันในการทําบาป และการเป็นศัตรูกันและการที่พวกเขากินสิ่งที่เป็นที่ต้องห้าม ช่างเลวจริง ๆ สิ่งที่พวกเขากระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(5,63,'5:63','ไฉนเล่าผู้ที่รู้แจ้งในอัลลอฮ์และนักปราชญ์เหล่านั้นจึงไม่ห้ามพวกเขา ในการที่พวกเขาพูดสิ่งที่เป็ฯบาป และในการที่พวกเขากินสิ่งที่ต้องห้ามช่างเลวจริง ๆ สิ่งที่พวกเขาทํา');
INSERT INTO "king_fahad_thai" VALUES(5,64,'5:64','และชาวยิวนั้นได้กล่าวว่า พระหัตถ์ของอัลลอฮ์นั้นถูกล่ามตรวน มือของพวกเขาต่างหากที่ถูกล่ามตรวน และพวกเขาได้รับละอ์นัต เนื่องจากสิ่งที่พวกเขาพูดมิได้ พระหัตถ์ทั้งสองของพระองค์ถูกแบออกต่างหาก ซึ่งพระองค์จะทรงแจกจ่ายอย่างไรก็ได้ที่พระองค์ทรงประสงค์ และแน่นอนสิ่งที่ถูกประทานลงมาแก่เจ้าจากพระเจ้าของเจ้านั้นจะเพิ่มการละเมิด และการปฏิเสธศรัทธาแก่จํานวนมากมายในหมู่พวกเขา และเราได้ก่อให้เกิดการเป็นศัตรูกันและการเกลียดชังกันในระหว่างพวกเขา จนถึงวันกิยามะฮ์ ทุกครั้งที่พวกเขาจุดไฟขึ้น เพื่อทําสงคราม อัลลอฮ์ก็ทรงดับไฟนั้นเสีย และพวกเขาเพียรพยายามบ่อนทําลายในผืนแผ่นดิน และอัลลอฮ์นั้นไม่ทรงชอบผู้บ่อนทําลายทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(5,65,'5:65','และหากอะฮ์ลุลกิตาบศรัทธา และยําเกรงแล้ว แน่นอนเราก็จะลบล้างบรรดาความชั่วของพวกเขาให้พ้นจากพวกเขา และแน่นอนเราจะให้พวกเขาเข้าบรรดาสวนสวรรค์แห่งความสุขสําราญ');
INSERT INTO "king_fahad_thai" VALUES(5,66,'5:66','และหากว่าเขาเหล่านั้น ได้ตํารงไว้ซึ่งอัต-เตารอต และอัล-อินญีล และสิ่งที่ถูกปผระทานลงมา แก่พวกเขาจากพระเจ้าของพวกเขาแล้ว แน่นอนพวกเขาก็ได้บริโภคไปแล้วที่มาจากเบื้องบน ของพวกเขา และที่มาจาภายใต้เท้า ของพวกเขาในหมู่พวกเขานั้นมีกลุ่มหนึ่งที่มีความยุติธรรม และมากมายในหมู่พวกเขานั้น ช่างเลวร้ายจริง ๆ สิ่งที่พวกเขากระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(5,67,'5:67','ร่อซูลเอ๋ย ! จงประกาศสิ่งที่ถูกประทานลงมาแก่เจ้าจากพระเจ้าของเข้า และถ้าเจ้ามิได้ปฏิบัติ เจ้าก็มิได้ประกาศสารของพระองค์ และอัลลอฮ์นั้นจะทรงคุ้มกันเจ้าให้พ้นจากมนุษย์ แท้จริงอัลลอฮ์จะไม่ทรงแนะนําพวกที่ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(5,68,'5:68','จงกล่าวเถิด (มุฮัมมัด) ว่า บรรดาผู้ได้รับคัมภีร์ทั้งหลาย ! พวกท่านมิได้ตั้งอยู่บนสิ่งใด จนกว่าพวกท่านจะดํารงไว้ซึ่งอัต-เตารอต และอัล-อินญีล และสิ่งที่ถูกประทานลงมาแก่พวกท่านจากพระเจ้าของพวกท่าน และแน่นอนสิ่งที่ถูกประทานลงมาแก่เจ้า จากพระเจ้าของเจ้านั้นจะเพิ่มการละเมิด และการปฏิเสธศรัทธาแก่จํานวนมากในหมู่พวกเขา ดังนั้นเจ้าจงอย่าเศร้าใจแก่พวกที่ปฏิเสธศรัทธาเหล่านั้นเลย');
INSERT INTO "king_fahad_thai" VALUES(5,69,'5:69','แท้จริงบรรดาผู้ที่ศรัทธา และบรรดาผู้ที่เป็นยิว และพวกซอบิอูน และบรรดาผู้ที่เป็นคริสต์นั้น ผู้ใดที่ศรัทธาต่ออัลลอฮ์ และวันปรโลกและประกอบสิ่งที่ดีงามแล้ว ก็ไม่มีความกลัวใด ๆ แก่พวกเขา และทั้งพวกเขาก็จะไม่สียใจ');
INSERT INTO "king_fahad_thai" VALUES(5,70,'5:70','แท้จริงนั้นเราได้เอาสัญญาแก่วงศ์วานอิสราอีล และเราได้ส่งบรรดาร่อซูลมายังพวกเขา ทุกครั้งที่ร่อซูลคนใดนําสิ่งที่จิตใจของพวกเขาไม่ชอบมายังพวกเขาแล้ว กลุ่มหนึ่ง พวกเขาก็ปฏิเสธ และอีกกลุ่มหนึ่งพวกเขาก็ฆ่าเสีย');
INSERT INTO "king_fahad_thai" VALUES(5,71,'5:71','และพวกเขาคิดว่าจะไม่มีการทอสอบใด ๆ เกิดขึ้น แล้วพวกเขาจึงได้ตาบอด และหูหนวก แล้วอัลลอฮ์ก็ทรงอภัยโทษให้แก่พวกเขา แล้วพกวกเขาก็ตาบอดและหูหนวกอีก คือจํานวนมากในหมู่พวกเขา และอัลลอฮ์นั้นทรงเห็นสิ่งที่พวกเขากระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(5,72,'5:72','แท้จริงบรรดาผุ้ที่กล่าวว่า อัลลอฮ์คือ อัล-มะซีห์บุตรของมัรยัมนั้นได้ตกเป็นผู้ปฏิเสธศรัทธาแล้ว และอัล-มะซีห์ได้กล่าวว่า วงศ์วานอิสรออีลเอ๋ย! จงเคารพอิบาดะฮ์ต่ออัลลอฮ์ผุ้เป็นพระเจ้าของฉัน และเป็นพระเจ้าของพวกท่านเถิด แท้จริงผู้ใดให้มีภาแก่อัลลอฮ์ แน่นอนอัลลอฮ์จะทรงให้สวรรค์เป็นที่ต้องห้ามแก่เขา และที่พํานักของเขานั้นคือนรก และสําหรับบรรดาผู้อธรรมนั้นย่อมไม่มีผู้ช่วยเหลือใด ๆ');
INSERT INTO "king_fahad_thai" VALUES(5,73,'5:73','แท้จริงบรรดาผู้ที่กล่าวว่าอัลลอฮ์เป็นผู้ที่สามของสามองค์ นั้นได้ตกเป็นผู้ปฏิเสธศรัทธาแล้ว ไม่มีสิ่งใดที่ควรได้รับการเคารพสักการะนอกจากผู้ที่ควรเคารพสักการะองค์เดียวเท่านั้น และหากพวกเขามิหยุดยั้งจากสิ่งที่พวกเขากล่าวแน่นอนบรรดาผู้ที่ปฏิเสธการศรัทธาในหมู่พวกเขานั้นจะต้องประสบการลงโทษอันเจ็บแสบ');
INSERT INTO "king_fahad_thai" VALUES(5,74,'5:74','พวกเขาจะไม่สํานึกผิดกลับเนื้อกลับตัวต่อัลลอฮ์ และขออภัยโทษต่อพระองค์กระนั้นหรือ? และอัลลอฮ์นั้นเป็นผู้ทรงอภัยโทษผู้ทรงเอ็นดูเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(5,75,'5:75','อัล-มะซีห์บุตรของมัรยัม นั้นมิใช่ใครอื่นนอกจากเป็นร่อซูลคนหนึ่งเท่านั้น ซึ่งบรรดาร่อซูลก่อนเขาก็ได้ล่วงลับไปแล้ว และมารดาของเขานั้นคือหญิงที่มีสัจจะวาจา ซึ่งทั้งสองนั้นรับประทานอาหาร จงดูเถิด(มุฮัมมัด) ว่าอย่างไรเล่าที่เราได้แจกแจงโองการต่าง ๆ แก่พวกเขา? และจงดูเถิดว่าอย่างไรเล่าพวกเขาจึงถูกหันเหไปได้');
INSERT INTO "king_fahad_thai" VALUES(5,76,'5:76','จงกล่าวเถิด (มุฮัมมัด) ว่า พวกท่านจะเคารพสักการะอื่นจากอัลลอฮ์ สิ่งซึ่งไม่มีอํานาจครอบครองอันตรายใด ๆ และประโยชน์ใด ๆ ไว้สําหรับพวกท่านกระนั้นหรือ? และอัลลอฮ์นั้นคือผู้ทรงได้ยิน ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(5,77,'5:77','จงกล่าวเถิด(มุฮัมมัด) ว่าบรรดาผู้ได้รับคัมภีร์ทั้งหลาย! จงอย่าปฏิบัติให้เกินขอบเขตในศาสนาของพวกท่าน โดยปราศจากความเป็นจริง และจงอย่าปฏิบัติตามความใคร่ใฝ่ต่ําของพวกหนึ่งพวกใดที่พวกเขาได้หลงผิดมาก่อนแล้ว และได้ทําให้ผู้คนมากมายหลงผิดด้วย และพวกเขาก็ได้หลงผิดไปจากทางอันเที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(5,78,'5:78','บรรดาผู้ที่ปฏิเสธศรัทธาในหมู่วงศ์วานอิสรออีลนั้นได้ถูกสาปโดยถ้อยคําของดาวูด และอีซาบุตรของมัรยัม นั่นก็เนื่องจากการที่พวกเขาฝ่าฝืน และที่พวกเขาเคยละเมิดกัน');
INSERT INTO "king_fahad_thai" VALUES(5,79,'5:79','ปรากฏว่าพวกเขาต่างไม่ห้ามปรามกันในสิ่งไม่ชอบที่พวกเขาได้กระทํามันขึ้น ช่างเลวร้ายจริง ๆ สิ่งที่พวกเขากระทํา');
INSERT INTO "king_fahad_thai" VALUES(5,80,'5:80','เจ้า(มุฮัมมัด) ก็จะเห็นมากมายในหมู่พวกเขา เป็นมิตรกับบรรดาผู้ที่ปฏิเสธศรัทธา ช่างเลวร้ายจริงๆสิ่งที่ตัวของพวกเขาเองได้ประกอบล่วงหน้าไว้สําหรับพวกเขา อันเป็นเหตุให้อัลลอฮ์ทรงกริ้วพวกเขาและพวกเขาจะคงอยู่ในการลงโทษตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(5,81,'5:81','และหากพวกเขาศรัทธาต่ออัลลอฮ์ และนะบีและสิ่งที่ถูกประทานลงมาแก่เขา แล้วพวกเขาก็จะไม่ยึดเอาเขาเหล่านั้น เป็นมิตร แต่ทว่ามากมายในหมู่พวกเขานั้นเป็นผู้ที่ละเมิด');
INSERT INTO "king_fahad_thai" VALUES(5,82,'5:82','แน่นอนเจ้าจะพบว่า หมู่ชนที่เป็นศัตรูอันรุนแรงแก่บรรดาผู้ที่ศรัทธานั้นคือชาวยิว และบรรดาผู้ที่ให้มีภาคีแก่อัลลอฮ์ และแน่นอนเจ้าจะพบว่า บรรดาผู้ที่มีความรักใคร่แก่บรรดาผู้ที่ศรัทธาใกล้กว่า พวกเขา นั้นคือ บรรดาผู้ที่กล่าวว่าแท้จริงพวกเราเป็นคริสต์ นั่นก็เพราะว่า ในหมู่พวกเขานั้นมีบรรดานักปราชญ์ และบาดหลวงและก็เพราะว่าพวกเขาไม่เย่อหยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(5,83,'5:83','และเมื่อพวกเขา ได้ยินสิ่งที่ถูกประทานลงมาแก่ร่อซูลแล้ว เจ้า ก็จะเห็นตาของพวกเขาหลั่งออกมาซึ่งน้ําตา เนื่องจากความจริงที่พวกเขารู้ โดยที่พวกเขาจะกล่าวว่า โอ้พระเจ้าของพวกข้าพระองค์โปรดได้ทรงจารึกพวกข้าพระองค์ไว้ร่วมกับบรรดาผู้กล่าวปฏิญาณยืนยันด้วยเถิด');
INSERT INTO "king_fahad_thai" VALUES(5,84,'5:84','และไม่มีเหตุผลใด ๆ แก่เราที่เราจะไม่ศรัทธาต่ออัลลอฮ์ และความจริงที่มายังเรา และเราปรารถนาอย่างแรงกล้าที่พระเจ้าของเราจะทรงให้เราเข้าร่วมอยู่กับพวกที่ดี ๆ ทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(5,85,'5:85','แล้วอัลลอฮ์ก็ได้ทรงตอบแทนแก่พวกเขาเนื่องด้วยสิ่งที่พวกเขากล่าว ซึ่งบรรดาสวนสวรรค์ที่มีแม่น้ําหลายสายไหลอยู่ภายใต้สวนเหล่านั้น โดยที่พวกเขาจะพํานักอยู่ในนั้นตลอดกาล และนั่นแหละคือ การตอบแทนแก่บรรดาผู้กระทําดี');
INSERT INTO "king_fahad_thai" VALUES(5,86,'5:86','และบรรดาผู้ที่ปฏิเสธศรัทธา และปฏิเสธบรรดาโองการของเรานั้น ชนเหล่านี้แหละคือ ชาวนรกที่มีเปลวไฟอันโชติช่วง (อัล-ญะฮีม)');
INSERT INTO "king_fahad_thai" VALUES(5,87,'5:87','ผู้ที่ศรัทธาทั้งหลาย! จงอย่าได้ให้เป็นที่ต้องห้าม ซึ่งบรรดาสิ่งดี ๆ ในสิ่งที่อัลลอฮ์ได้ทรงอนุมัติแก่พวกเจ้า และพวกเจ้าจงอย่าละเมิด แท้จริงอัลลอฮ์นั้นไม่ทรงชอบบรรดาผู้ละเมิด');
INSERT INTO "king_fahad_thai" VALUES(5,88,'5:88','และพวกเจ้าจงบริโภคสิ่งอนุมัติที่ดี ๆ จากสิ่งที่อัลลอฮ์ได้ทรงให้เป็นปัจจัยชีพแก่พวกเจ้า และพึงยําเกรงอัลลอฮ์ผู้ซึ่งพวกเจ้าศรัทธาต่อพระองค์เถิด');
INSERT INTO "king_fahad_thai" VALUES(5,89,'5:89','อัลลอฮ์จะไม่ทรงเอาโทษแก่พวกเจ้าด้วยถ้อยคําที่ไร้สาระในการสาบานของพวกเจ้า แต่ทว่าพระองค์จะทรงเอาโทษแก่พวกเจ้าด้วยถ้อยคําที่พวกเจ้าปลงใจสาบาน แล้วสิ่งไถ่โทษมัน นั้นคือการให้อาหารแก่มิสกีนสิบคนจากอาหารปานกลางของสิ่งที่พวกเจ้าให้เป็นอาหารแก่ครอบครัวของพวกเจ้า หรือไม่ก็ให้เครื่องนุ่งห่มแก่พวกเขา หรือไถ่ทาสคนหนึ่งให้เป็นอิสระ ผู้ใดไม่พบ ก็ให้มีการถือบวชสามวัน นั่นแหละคือสิ่งไถ่โทษในการสาบานของพวกเจ้าเมื่อพวกเจ้าได้สาบานไว้ และจงรักษาการสาบานของพวกเจ้าเถิด ในทํานองนั้นแหละอัลลอฮ์จะทรงแจกแจงบรรดาโองการของพระองค์แก่พวกเจ้า เพื่อว่าพวกเจ้าจักขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(5,90,'5:90','ผู้ศรัทธาทั้งหลาย! ที่จริงสุราและการพนันและแท่นหินสําหรับเชือดสัตว์บูชายัญ และการเสี่ยงติ้ว นั้นเป็นสิ่งโสมมอันเกิดจากการกระทําของชัยฏอน ดังนั้นพวกเจ้าจงห่างไกลจากมันเสียเพื่อว่าพวกเจ้าจะได้รับความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(5,91,'5:91','ที่จริงชัยฏอนนั้นเพียงต้องการที่จะให้เกิดการเป็นศัตรูกันและการเกลียดชังกันระหว่างพวกเจ้าในสุราและการพนัน เท่านั้น และมันจะหันเหพวกเจ้าออกจากการรําลึกถึงอัลลอฮ์ และการละหมาด แล้วพวกเจ้าจะยุติใหม่');
INSERT INTO "king_fahad_thai" VALUES(5,92,'5:92','และพวกเจ้าจงเชื่อฟังอัลลอฮ์ และจงเชื่อฟังร่อซูลเถิด และพึงระมัดระวัง ไว้ด้วย แต่ถ้าพวกเจ้าผินหลังได้ ก็พึงรู้เถิดว่าที่จริงหน้าที่ของร่อซูลของเรานั้น คือ การประกาศอันชัดเจนเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(5,93,'5:93','ไม่มีบาปใด ๆ แก่บรรดาผู้ที่ศรัทธาและปฏิบัติสิ่งที่ดีงามทั้งหลาย ในสิ่งที่พวกเขาได้บริโภค เมื่อพวกเขามีความยําเกรงและศรัทธา และปฏิบัติสิ่งที่ดีงามทั้งหลาย แล้วก็มีความยําเกรงและศรัทธาแล้วก็มีความยําเกรง และกระทําดี และอัลลอฮ์นั้นทรงรักผู้กระทําดีทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(5,94,'5:94','ผู้ศรัทธาทั้งหลาย! แน่นอนอัลลอฮ์จะทรงทดสอบพวกเจ้าด้วยสิ่งหนึ่ง อันได้แก่สัตว์ล่า ที่มือของพวกเจ้าได้มันมา และหอกของพวกเจ้าด้วย เพื่ออัลลอฮ์จะทรงรู้ว่าใครที่ยําเกรงพระองค์ในสภาพที่เขาไม่เห็นพระองค์ แล้วผู้ใดละเมิดหลังจากนั้น เขาก็จะได้รับโทษอันเจ็บแสบ');
INSERT INTO "king_fahad_thai" VALUES(5,95,'5:95','ผู้ศรัทธาทั้งหลาย! จงอย่าฆ่าสัตว์ล่าในขณะที่พวกเจ้ากําลังครองอิห์รอมอยู่ และผู้ใดในหมู่พวกเจ้าได้ฆ่ามันโดยเจตนาแล้วไซร้ การชดเชยก็คือ ชนิดเดียวกับที่ถูกฆ่า (จากปศุสัตว์) โดยผู้ที่ยุติธรรมสองคนในหมู่พวกเจ้าจะกระทําการชี้ขาดมัน ในฐานะเป็นสัตว์พลีที่ไปถึงอัล-กะฮ์บะฮ์ หรือไม่ ก็ให้มีการลงไถ่โทษ คือให้อาหารแก่บรรดามีสกีน หรือสิ่งที่เท่าเทียมสิ่งนั้น ด้วยการถือศีลอด เพื่อที่เขาจะได้ลิ้มรสผลภัยแห่งกิจกรรมของเขา อัลลอฮ์ได้ทรงอภัยให้จากสิ่งที่ได้ล่วงเลยมาแล้ว และผู้ใดกลับกระทําอีก อัลลอฮ์ก็จะทรงลงโทษเขาและอัลลอฮ์ คือผู้ทรงเดชานุภาพ ผู้ทรงลงโทษ');
INSERT INTO "king_fahad_thai" VALUES(5,96,'5:96','ได้ถูกอนุมัติแก่พวกเจ้า ซึ่งสัตว์ล่าในทะเล และอาหารจากทะเล ทั้งนี้เพื่อเป็นสิ่งอํานวยประโยชน์แก่พวกเจ้า และแก่บรรดาผู้เดินทาง และได้ถูกห้ามแก่พวกเจ้า ซึ่งสัตว์ล่าบนบกตราบใดที่พวกเจ้าครองอิห์รอมอยู่และจงยําเกรงอัลลอฮ์เภิดผู้ที่พวกเจ้าจะถูกรวบรวมนําไปสู่พระองค์');
INSERT INTO "king_fahad_thai" VALUES(5,97,'5:97','อัลลอฮ์ได้ทรงให้อัล-กะอ์บะฮ์ อันเป็นบ้าที่ต้องห้ามนั้นเป็นที่ดํารงอยู่สําหรับมนุษย์ และเดือนที่ต้องห้าม และสัตว์พลีและสัตว์ที่ถูกสวมเครื่องหมายไว้ที่คอ เพื่อเป็นสัตว์พลีด้วย นั่นก็เพื่อพวกเจ้าจะได้รู้ว่า แท้จริงอัลลอฮ์ทรงรู้สิ่งที่อยู่ในบรรดาชั้นฟ้า และสิ่งที่อยู่ในแผ่นดิน และแท้จริงอัลลอฮ์ทรงรอบรู้ในทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(5,98,'5:98','พวกเจ้าพึงรู้เถิดว่า แท้จริงอัลลอฮ์เป็นผู้รุนแรงในการลงโทษ และแท้จริงอัลลอฮ์เป็นผู้ทรงอภัยโทษทรงเอ็นดูเมตตา');
INSERT INTO "king_fahad_thai" VALUES(5,99,'5:99','หน้าที่ของร่อซูลนั้นมิใช่อะไรอื่น นอกจากการประกาศให้ทราบ เท่านั้น และอัลบลอฮ์ทรงรู้สิ่งที่พวกเจ้าเปิดเผย และสิ่งที่พวกเจ้าปกปิด');
INSERT INTO "king_fahad_thai" VALUES(5,100,'5:100','จงกล่าวเถิด(มุฮัมมัด)ว่าสิ่งเลวกับสิ่งดีนั้นย่อมไม่เท่าเทียมกัน และแม้ว่าความมากมายของสิ่งชั่วนั้น ได้ทําให้ท่านพึงใจก็ตาม จงยําเกรงอัลลอฮ์เถิด ผู้มีสติบัญญัติทั้งหลาย! เพื่อว่าพวกเจ้าจะได้รับความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(5,101,'5:101','ผู้ศรัทธาทั้งหลาย! จงอย่าถามถึงสิ่งต่างๆ หากสิ่งเหล่านี้ถูกเปิดเผยขึ้น แล้วมันก็จะก่อให้เกิดความเลวร้าย แก่พวกเจ้า และถ้าพวกเจ้าถามถึงสิ่งเหล่านั้น ขณะที่อัล-กรุอานถูกประทานลงมา มันก็จะถูกเปิดเผยขึ้น แก้พวกเจ้า อัลลอฮ์ได้ทรงอภัยสิ่งเหล่านั้นแล้ว และอัลลอฮ์เป็นผู้ทรงอภัยผู้ทรงหนักแน่น');
INSERT INTO "king_fahad_thai" VALUES(5,102,'5:102','แท้จริงได้มีพวกหนึ่งก่อนพวกเจ้าได้ถามถึงสิ่งต่างๆ เหล่านั้นมาแล้ว แล้วพวกเขาก็กลายเป็นผู้ปฏิเสธสิ่งต่างๆ เหล่านั้น');
INSERT INTO "king_fahad_thai" VALUES(5,103,'5:103','อัลลอฮ์มิได้ทรงให้มีขึ้น ซึ่งบะฮีเราะฮ์ และซาอิบะฮ์ และวะซีละฮ์ และฮาม แต่ทว่าบรรดาผู้ปฏิเสธศรัทธาต่างหากที่อุปโลกน์ความเท็จแก่อัลลอฮ์ และส่วนมากของพวกเขาไม่ใช่ปัญญา');
INSERT INTO "king_fahad_thai" VALUES(5,104,'5:104','และเมื่อได้ถูกกล่าวแก่พวกเขาว่า ท่านทั้งหลายจงมาสู้สิ่งที่อัลลอฮ์ ได้ทรงประทานลงมาเถิด และมาสู่ร่อซูลด้วย พวกเขาก็กล่าวว่า เป็นการพอเพียงแก่เราแล้ว สิ่งที่เราได้พบบรรพบุรุษของเราเคยกระทํามันมา ถึงแม้ได้ปรากฏว่าบรรพบุรุษของพวกเขาไม่เข้าใจสิ่งใด และทั้งไม่ได้รับคําแนะนําอีกด้วยกระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(5,105,'5:105','ผู้ศรัทธาทั้งหลาย! จําเป็นแก่พวกเจ้าในการป้องกันตัวของพวกเจ้า ผู้ที่หลงผิดไปนั้นจะไม่เป็นอันตรายแก่พวกเจ้า ได้ เมื่อพวกเจ้ารับคําแนะนําไว้ ยังอัลลอฮ์นั้นคือการกลับไปของพวกเจ้าทั้งหมด แล้วพระองค์ก็จะทรงบอกแก่พวกเจ้าทั้งหลาย ในสิ่งที่พวกเจ้ากระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(5,106,'5:106','ผู้ศรัทธาทั้งหลาย! การเป็นพยานระหว่างพวกเจ้า-เมื่อความตายได้มายังคนหนึ่งคนใดในพวกเจ้า ขณะมีการทําพินัยกรรมนั้น-คือสองคนที่เป็นผู้เที่ยงธรรมในหมู่พวกเจ้า หรือคนอื่นสองคนที่มิใช่ในหมู่พวกเจ้า หากพวกเจ้าได้เดินทางไปในผืนแผ่นดินแล้วได้มีเหตุภัยแห่งความตายประสบกับพวกเจ้าโดยที่พวกเจ้าจะต้องกักตัวเขาทั้งสองไว้หลังจากละหมาด แล้วทั้งสองนั้นก็จะสาบานต่ออัลลอฮ์-หากพวกเจ้าสงสัย -ว่าเราจะไม่นําการสาบานนั้นไปแลกเปลี่ยนกับราคาใด ๆ และแม้ว่าเขา จะเป็นญาติใกล้ชิดก็ตาม และเราจะไม่ปกปิดหลักฐานของอัลลอฮ์ (ถ้ามิเช่นนั้น)แน่นอนทันใดนั้นเองเราก็จะอยู่ในหมู่ผู้ที่กระทําบาป');
INSERT INTO "king_fahad_thai" VALUES(5,107,'5:107','แล้วหากได้รับรู้ว่าพยานทั้งสองคนนั้นสมควรได้รับโทษ ก็ให้คนอื่นสองคนทําหน้าที่ในตําแหน่งพยานทั้งสองนั้นแทน จากบรรดาผู้ที่มีคนสองคนในหมู่พวกเขาเป็นผู้สมควร แล้วทั้งสองนั้นก็จะสาบานต่ออัลลอฮ์ว่า แน่นอนการเป็นพยานของเรานั้นสมควรยิ่งกว่าการเป็นพยานของเขาทั้งสอง และเรามิได้ละเมิด(ถ้ามิเช่นนั้น)แน่นอนทันใดนั้นเอง เราก็จะอยู่ในหมู่ผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(5,108,'5:108','นั้นแหละคือสิ่งที่ใกล้ยิ่งกว่า ในการที่พวกเขาจะนํามาซึ่งการเป็นพยานตามความเป็นจริงของมัน หรือในการที่พวกเขากลัวว่า คําสาบานจะถูกปฏิเสธ หลังจากที่พวกเขาสาบาน จงยําเกรงอัลลอฮ์และจงสดับฟังเถิด แท้จริงอัลลอฮ์นั้นจะไม่ทรงแนะนําพวกที่เป็นผู้ละเมิด');
INSERT INTO "king_fahad_thai" VALUES(5,109,'5:109','วันที่อัลลอฮ์จะทรงชุมนุมบรรดาร่อซูล แล้วตรัสว่าสิ่งใดบ้างที่พวกเจ้าได้รับการตอบสนอง พวกเขากล่าวว่าไม่มีความรู้ใด ๆ แก่พวกข้าพระองค์ แท้จริงพระองค์เป็นผู้ทรงรอบรู้ความเร้นลับทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(5,110,'5:110','จงรําลึกถึงขณะที่อัลลอฮ์ ตรัสแก่อีซาบุตรของมัรยัมว่า จงรําถึงความโปรดปรานของข้าที่มีต่อเจ้า และมารดาของเจ้า ขณะที่ข้าได้สนับสนุนเจ้า ด้วยวิญญาณอันบริสุทธิ์ โดยที่เจ้าพูดกับประชาชน ขณะที่อยู่ในเปล แลบะขณะที่อยู่ในวัยกลางคน และขณะที่ข้าได้สอนเจ้า ซึ่งคัมภีร์และความมุ่งหมายแห่งบัญญัติศาสนาและอัต-เตารอตและอัล-อิน-ญีล และขณะที่เจ้าสร้างขึ้นจากดินดั่งรูปนกด้วยอนุมัติของข้า แล้วเจ้าเป่าเข้าไปในรูปนกนั้น มันก็กลายเป็นนก ด้วยอนุมัติของข้า และที่เจ้าทําให้คนตาบอดแต่กําเนิด และคนเป็นโรคผิวหนังหาย ด้วยอนุมัติของข้า และขณะที่เจ้าทําให้บรรดาคนตายออกมา ด้วยอนุมัติของข้า และขณะที่ข้าได้ยับยั้งและหันเหวงศ์วานอิสรออีลออกจากเจ้า เมื่อเจ้านําบรรดาหลักฐานอันชัดแจ้งมายังพวกเขาแล้วบรรดาผู้ฝ่าฝืนในหมู่พวกเขาก็กล่าวว่า สิ่งนี้ มิใช่อื่นใด นอกจากมายากลอันชัดแจ้งเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(5,111,'5:111','และจงรําลึกถึงขณะที่ข้าได้ดลใจแก่อัลฮะวารียิน ว่าจงศรัทธาต่อข้าและต่อร่อซูลของข้า เถิด พวกเขากล่าวว่า พวกข้าพระองค์ศรัทธากันแล้ว และโปรดได้ทรงเป็นพยานด้วยว่า แท้จริงพวกข้าพระองค์นั้นเป็นผู้สวามิภักดิ์(ต่อพระองค์)');
INSERT INTO "king_fahad_thai" VALUES(5,112,'5:112','ขณะที่อัล-ฮะวารียูนกล่าวว่า โอ้อีซาบุตรของมัรยัม! พระเจ้าของท่านสามารถที่จะให้สํารับอาหารจากฟากฟ้าลงมาแก่พวกเราไหม? เขากล่าวว่า พวกเจ้าจงยําเกรงอัลลอฮ์ หากพวกเจ้าเป็นผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(5,113,'5:113','พวกเขากล่าวว่า พวกเราต้องการที่จะบริโภคจากมัน และที่จะให้หัวใจของพวกเราสงบ และที่พวกเราจะได้รู้ว่า ท่านได้พูดจริงแก่พวกเรา และที่พวกเราจะได้เป็นพยานยืนยันในเรื่องนั้นด้วย');
INSERT INTO "king_fahad_thai" VALUES(5,114,'5:114','อีซาบุตรของมัรยัม ได้กล่าวว่า ข้าแต่อัลลอฮ์ ผู้เป้นพระเจ้าของข้าพระองค์! โปรดได้ทรงประทานลงมาแก่พวกข้าพระองค์ ซึ่งสํารับอาหารจากฟากฟ้าด้วยเถิด จะได้เป็นวันรื่นเริงแก่พวกข้าพระองค์ ทั้งแก่คนแรกของพวกข้าพระองค์และแก่คนสุดท้ายของพวกข้าพระองค์ และจะได้เป็นสัญญาณหนึ่ง จากพระองค์ และโปรดได้ทรงประทานปัจจัยยังชีพแก่พวกข้าพระองค์ด้วยเถิด และพระองค์นั้น คือผู้ที่ดีเยี่ยมในหมู่ผู้ประทานปัจจัยยังชีพทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(5,115,'5:115','อัลลอฮ์ตรัสว่า แท้จริงข้าจะให้มันลงมาแก่พวกเจ้า แล้วผู้ใดในหมู่พวกเจ้าปฏิเสธศรัทธาหลังจากนั้น แน่นอนข้าจะลงโทษเขา ซึ่งโทษที่ข้าจะไม่ลงโทษนั้นแก่ผู้ใดในหมู่ประชาชาติทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(5,116,'5:116','และจงรําลึกถึงขณะที่อัลลอฮ์ ตรัสว่า อีซาบุตรของมัรยัม เอ๋ย! เจ้าพูดแก่ผู้คนกระนั้นหรือว่า จงยึดถือฉันและมารดาของฉันเป็นที่เคารพสักการะทั้งสองอื่นจากอัลลอฮ์ เขากล่าวว่า มหาบริสุทธิ์พระองค์ท่าน! ไม่เคยแก่ข้าพระองค์ที่จะกล่าวสิ่งที่มิใช่สิทธิของข้าพระองค์ หากข้าพระองค์เคยกล่าวสิ่งนั้น แน่นอนพระองค์ย่อมรู้ดี โดยที่พระองค์ทรงรู้สิ่งที่อยู่ในใจของข้าพระองค์ และข้าพระองค์ไม่รู้สิ่งที่อยู่ในใจของพระองค์ แท้จริงพระองค์นั้นคือผู้ทรงรอบรู้ในสิ่งเร้นลับทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(5,117,'5:117','ข้าพระองค์มิได้กล่าวแก่พวกเขา นอกจากสิ่งที่พระองค์ใช้ข้าพระองค์เท่านั้น ที่ว่าท่านทั้งหลายจงเคารพสักการะต่ออัลลอฮ์ ผู้เป็นเจ้าของฉัน และเป็นพระเจ้าของพวกท่านด้วย และข้าพระองค์ย่อมเป็นพยานยืนยันต่อพวกเขาในระยะเวลาที่ข้าพระองค์อยู่ในหมู่พวกเขา ครั้นเมื่อพระองค์ได้ทรงรับข้าพระองค์ไปแล้ว พระองค์ท่านก็เป็นผู้ดูและพวกเขา และพระองค์ทรงเป็นสักขีพยานในทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(5,118,'5:118','หากพระองค์จะทรงลงโทษพวกเขาแท้จริงพวกเขาก็คือบ่าวของพระองค์ และหากพระองค์จะทรงอภัยโทษให้แก่พวกเขา แท้จริงพระองค์ท่านคือ ผู้ทรงเดชานุภาพ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(5,119,'5:119','อัลลอฮ์ตรัสว่า นี่แหละคือวัน ที่การพูดความจริงของพวกเขาจะอํานวยประโยชน์แก่บรรดาผู้ที่พูดจริง พวกเขาจะได้รับบรรดาสวนสวรรค์เหล่านั้น โดยที่พวกเขาจะพํานักอยู่ในนั้นตลอดกาล ในสภาพที่อัลลอฮ์ทรงพอพระทัยในพวกเขา และพวกเขาก็พึงพอใจในพระองค์นั่นแหละคือชัยชนะอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(5,120,'5:120','อํานาจแห่งบรรดาชั้นฟ้าและแผ่นดิน และสิ่งที่อยู่ในบรรดาชั้นฟ้าและแผ่นดินนั้น เป็นสิทธิของอัลลอฮ์ทั้งสิ้น และพระองค์ทรงเดชานุภาพเหนือทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(6,1,'6:1','การสรรเสริญทั้งหลายนั้นเป็นสิทธิของอัลลอฮ์ ผู้ทรงสร้างบรรดาชั้นฟ้าและแผ่นดินได้ทรงให้มีบรรดาความมืดและแสงสว่าง แต่แล้วบรรดาผู้ที่ปฏิเสธศรัทธานั้น ก็ยังให้(สิ่งอื่น) เท่าเทียมกับพระเจ้าของเขาอยู่');
INSERT INTO "king_fahad_thai" VALUES(6,2,'6:2','พระองค์คือ ผู้ที่ทรงบังเกิดพวกเจ้าจากดิน แล้วได้ทรงกําหนดเวลาแห่งความตายไว้ และกําหนดที่ถูกระบุไว้อีกกําหนดหนึ่งนั้น อยู่ที่พระองค์แต่แล้วพวกเจ้าก็ยังสงสัยกันอยู่');
INSERT INTO "king_fahad_thai" VALUES(6,3,'6:3','และพระองค์นั้นคือ อัลลอฮ์ ทั้งในบรรดาชั้นฟ้าและในแผ่นดิน ทรงรู้สึกเร้นลับของพวกเจ้า และสิ่งเปิดเผยของพวกเจ้า และทรงรู้สิ่งที่พวกเจ้าขวนขวายกันอยู่');
INSERT INTO "king_fahad_thai" VALUES(6,4,'6:4','และไม่มีโองการใด จากบรรดาโองการแห่งพระเจ้าของพวกเขามายังพวกเขา นอกจากได้ปรากฏว่าพวกเขาผินหลังให้แก่โองการนั้น');
INSERT INTO "king_fahad_thai" VALUES(6,5,'6:5','แน่นอนพวกเขาได้ปฏิเสธความจริง เมื่อความจริงนั้นได้มายังพวกเขา แล้วข่าวคราว ของสิ่งที่พวกเขาเคยเย้ยหยันไว้นั้นก็จะมายังพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(6,6,'6:6','พวกเขามิได้เห็นดอกหรือว่า กี่ประชาชาติมาแล้วที่เราได้ทําลายมาก่อนหน้าพวกเขา ซึ่งเราได้พวกเขามีอํานาจและความสามารถในแผ่นดิน ซึ่งสิ่งที่เรามิได้ให้มีแก่พวกเจ้า และเราได้ส่งฝนมายังพวกเขาอย่างมากมาย และเราได้ให้มีแม่น้ําหลายสายไหลอยู่เบื้องล่างของพวกเขา แล้วเราก็ทําลายพวกเขาเสีย เนื่องด้วยบรรดาความผิดของพวกเขา และเราได้ให้มีขึ้นหลังจากพวกเขาซึ่งประชาชาติอื่น');
INSERT INTO "king_fahad_thai" VALUES(6,7,'6:7','และหากเราได้ให้ลงมาแก่เจ้า ซึ่งคัมภีร์ฉบับหนึ่ง (ที่ถูกจารึกไว้) ในกระดาษ แล้วพวกเขาก็ได้สัมผัส คัมภีร์นั้นด้วยมือของพวกเขาเองแน่นอนบรรดาผู้ที่ปฏิเสธศรัทธาย่อมกล่าวว่า สิ่งนี้มิใช่อื่นใด นอกจากมายากลอันชัดแจ้งเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(6,8,'6:8','และพวกเขาได้กล่าวว่าไฉนเล่ามะลัก จึงมิได้ถูกให้ลงมาแก่เขา และหากว่าเราได้ให้มะลักลงมาแล้ว แน่นอนกิจการทั้งหลายก็ย่อมถูกชี้ขาด แล้วพวกเขาก็จะไม่ถูกรอคอย');
INSERT INTO "king_fahad_thai" VALUES(6,9,'6:9','และหาพวกว่าเราได้ให้เขา เป็นมะลักแน่นอนเราก็ย่อมให้เขาเป็นคนผู้ชาย และแน่นอนเราก็ย่อมให้สิ่งที่พวกเขาคลุมเครือกัน อยู่เป็นที่คลุมเครือแก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(6,10,'6:10','และแน่นอนบรรดาร่อซูล ก่อนเจ้านั้นได้ถูกเย้ยหยันมาแล้ว ดังนั้นจึงได้ล้อมบรรดาผู้ที่เย้ยหยันร่อซูลเหล่านั้นไว้ ซึ่งสิ่งที่พวกเขาเคยเย้ยหยันกัน');
INSERT INTO "king_fahad_thai" VALUES(6,11,'6:11','จงกล่าวเถิด (มุฮัมมัด)ว่า พวกท่านจงเดิน ไปในแผ่นดินเถิด แล้วจงดูว่า ผลสุดท้ายของบรรดาผู้ปฏิเสธนั้นเป็นอย่างไรบ้าง ?');
INSERT INTO "king_fahad_thai" VALUES(6,12,'6:12','จงกล่าวเถิด(มุฮัมมัด)ว่า สิ่งที่อยู่ในบรรดาชั้นฟ้าและแผ่นดินนั้นเป็นของใคร? จงกล่าวเถิด(มุฮัมมัด) ว่าเป็นของอัลลอฮ์ พระองค์ได้ทรงกําหนดการเอ็นดูเมตตาไว้บนตัวของพระองค์ แน่นอนพระองค์จะทรงรวบรวมพวกเจ้าไปสู่วันกิยามะฮ์ โดยที่ไม่มีการสงสัยใด ๆ ในวันนั้นบรรดาผู้ที่ทําให้ตัวของพวกเขาขาดทุนนั้น พวกเขาก็จะไม่ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(6,13,'6:13','และสิ่งที่สงบเงียบอยู่ ในเวลากลางคืนและกลางวันนั้นเป็นสิทธิของพระองค์ และพระองค์คือผู้ทรงได้ยิน ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(6,14,'6:14','จงกล่าวเถิด ฉันจะยึดถือ “ผู้คุ้มครองอื่นจากอัลลอฮฺกระนั้นหรือ” ซึ่งพระองค์เป็นผู้ประดิษฐ์บรรดาชั้นฟ้าและแผ่นดิน และพระองค์เป็นผู้ทรงให้อาหาร และไม่ถูกให้อาหาร จงกล่าวเถิด(มุฮัมมัด) แท้จริงฉันถูกใช้ให้เป็นคนแรกในหมู่ที่สวามิภักดิ์ และพวกท่านจงอย่าอยู่ในหมู่ผู้ให้มีภาคีเป็นอันขาด');
INSERT INTO "king_fahad_thai" VALUES(6,15,'6:15','จงกล่าวเถิด(มุฮัมมัด) ว่า แท้จริงฉันกลัวการลงโทษในวันอันยิ่งใหญ่ หากฉันฝ่าฝืนพระเจ้าของฉัน');
INSERT INTO "king_fahad_thai" VALUES(6,16,'6:16','ผู้ใดที่การลงโทษถูกหันเหออกจากเขาในวันนั้น แน่นอนพระองค์ทรงเอ็นดูเมตตาเขา และนั่นคือชัยชนะอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(6,17,'6:17','และหากว่าอัลลอฮ์ ทรงให้ความเดือดร้อนอย่างหนึ่งอย่างใดประสบแก่เจ้า แล้วก็ไม่มีผู้ใดจะเปลื้องมันได้ นอกจากพระองค์เท่านั้น และหากพระองค์ทรงให้ความดีอย่างหนึ่งอย่างใดประสบแก่เจ้า แท้จริงพระองค์นั้นทรงเดชานุภาพเหนือทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(6,18,'6:18','และพระองค์คือผู้ทรงชนะเหนือปวงบ่าวของพระองค์ และพระองค์คือผู้ทรงปรีชาญาณผู้ทรงรอบรู้อย่างละเอียดถี่ถ้วน');
INSERT INTO "king_fahad_thai" VALUES(6,19,'6:19','จงกล่าวเถิด(มุฮัมมัด) ว่า สิ่งใดใหญ่ยิ่งกว่าในการเป็นพยาน จงกล่าวเถิดว่าอัลลอฮ์นั้นคือผู้เป็นพยานระหว่างฉันกับพวกท่าน และอัล-กรุอานนี้ก็ได้ถูกประทานลงมาแก่ฉัน เพื่อที่ฉันจะได้ใช้อัลกรุอาน นี้ตักเตือนพวกท่าน และผู้ที่อัลกรุอานนี้ไปถึง พวกท่านจะยืนยันโดยแน่นอนกระนั้นหรือว่า มีบรรดาที่เคารพสักการะอื่นร่วมกับอัลลอฮ์? จงกล่าวเถิด(มุฮัมมัด) ว่าฉันจะไม่ยืนยัน จงกล่าวเถิด แท้จริงพระองค์นั้นคือผู้ที่ควรแก่การเคารพสักการะแต่เพียงองค์เดียวเท่านั้น และแท้จริงฉันขอปลีกตัวอกจากสิ่งที่พวกท่านให้มีภาคี(แก่อัลลอฮ์)');
INSERT INTO "king_fahad_thai" VALUES(6,20,'6:20','บรรดาผู้ที่เราได้ให้คัมภีร์แก่พวกเขา นั่น พวกเขารู้จักเขา เช่นเดียวกับที่พวกเขารู้จักลูก ๆ ของพวกเขาเอง บรรดาผู้ที่ทําให้ตัวของพวกเขาขาดทุนนั้น พวกเขาจะไม่ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(6,21,'6:21','และผู้ใดเล่า คือผู้อธรรมยิ่งกว่าผู้ที่อุปโลกน์ความเท็จให้แก่อัลลอฮ์ หรือปฏิเสธบรรดาโองการของพระองค์? แท้จริงบรรดาผู้อธรรมนั้นจะไม่ได้รับความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(6,22,'6:22','และวันที่เราจะชุมชุมพวกเขาทั้งมวลแล้วเรากล่าวแก่บรรดาผู้ที่ให้มีภาคีขึ้นว่าไหนเล่า บรรดาภาคีของพวกเจ้าที่พวกเจ้าอ้างกัน');
INSERT INTO "king_fahad_thai" VALUES(6,23,'6:23','แล้ว(ผลแห่ง) การทดสอบพวกเขาก็มิได้เป็นอย่างอื่น นอกจากพวกเขากล่าวว่า พวกข้าพระองค์ขอสาบานต่ออัลลอฮ์ ผู้เป็นพระเจ้าของพวกข้าพระองค์ว่า พวกข้าพระองค์ไม่เคยเป็นผู้ให้มีภาคีขึ้น');
INSERT INTO "king_fahad_thai" VALUES(6,24,'6:24','จงดูเถิด(มุอัมมัด)ว่า พวกเขาได้โกหกแก่ตัวของพวกเขาเองอย่างไร ? และสิ่งที่พวกเขาเคยอุปโลกน์ขึ้นก็ได้หายไป จากพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(6,25,'6:25','และในหมู่พวกเขานั้นมีผู้ที่สดับฟังเจ้าอยู่บ้าง แต่เราได้ให้มีสิ่งปิดกั้นอยู่บนหัวใจของพวกเขา ในการที่พวกเขาจะเข้าใจอัลกุรอาน และได้ให้ในหูของพวกเขามีความหนวกอยู่ด้วย และหากพวกเขาเห็นสัญญาณทุกอย่าง พวกเขาก็จะไม่ศรัทธาจนกระทั่งพวกเขาได้มาหาเจ้าก็ยังโต้เถียงกับเจ้า บรรดาผู้ที่ปฏิเสธศรัทธานั้นจะกล่าวว่า นี่ มิใช่อะไรอื่น นอกจากบรรดาสิ่งขีดเขียนอันไร้สาระของคนก่อน ๆ เท่านั้น (นิยายโบราณ)');
INSERT INTO "king_fahad_thai" VALUES(6,26,'6:26','และพวกเขาห้าม เกี่ยวข้องกับอัลกุรอาน และพวกเขาก็ปลีกตัวออกห่างจากอัลกรุอานด้วย และพวกเขาจะไม่ทําให้ใครพินาศนอกจากตัวของพวกเขาเองเท่านั้น แต่พวกเขาไม่รู้สึก');
INSERT INTO "king_fahad_thai" VALUES(6,27,'6:27','และหากเจ้าจะได้เห็น ขณะที่พวกเขาถูกให้หยุดยืนอยู่เบื้องหน้าไฟนรก แล้วพวกเขาได้กล่าวว่า โอ้! หวังว่าเราจะถูกนํากลับไป และเราก็จะไม่ปฏิเสธบรรดาโองการแห่งพระเจ้าของเราอีกและเราก็จะได้กลายเป็นผู้ที่อยู่ในหมู่ผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(6,28,'6:28','แต่ทว่าได้ประจักษ์แก่พวกเขาแล้ว สิ่งที่พวกเขาปกปิดไว้ แต่กาลก่อน และแม้ว่าพวกเขาถูกให้กลับไป แน่นอนพวกเขาก็กลับกระทําอีกในสิ่งที่พวกเขาถูกห้ามไว้ และแท้จริงพวกเขาคือผู้ที่กล่าวเท็จ');
INSERT INTO "king_fahad_thai" VALUES(6,29,'6:29','และพวกเขากล่าวว่า มันมิใช่อะไรอื่นนอกจากชีวิตความเป็นอยู่ของเราในโลกนี้เท่านั้น และเรานั้นใช่ว่าจะเป็นผู้ถูกให้ฟื้นคือชีพก็หาไม่');
INSERT INTO "king_fahad_thai" VALUES(6,30,'6:30','และหากกล่าวเจ้าจะได้เห็น ขณะที่พวกเขาถูกให้ยืนอยู่เบื้องหน้าพระเจ้าของพวกเขา โดยที่พระองค์ได้ทรงกล่าวว่า นี่ มิใช่ความจริงดอกหรือ? พวกเขาตอบว่า ใช่ขอรับ พวกข้าพระองค์ขอสาบานด้วยพระเจ้าของพวกข้าพระองค์ พระองค์ตรัสว่า พวกจ้าจงลิ้มรสการลงโทษกันเถิด เนื่องจากการที่พวกเจ้าปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(6,31,'6:31','แน่นอนได้ขาดทุนไปแล้ว บรรดาผู้ที่ปฏิเสธต่อการพบอัลลอฮ์ จนกระทั่งเมื่อวันกิยามะฮ์ได้มายังพวกเขาโดยกระทันหัน แล้วพวกเขาก็กล่าวว่า โอ้ความเสียใจของเรา ในสิ่งที่เราได้ทําให้บกพร่องในโลก โดยที่พวกเขาแบกบรรดาบาปของพวกเขาไว้บนหลังของพวกเขาด้วย พึงรู้เถิดว่า ช่างเลวร้ายจริง ๆ สิ่งที่พวกเขากําลังแบกอยู่');
INSERT INTO "king_fahad_thai" VALUES(6,32,'6:32','และชีวิตความเป็นอยู่แห่งโลกนี้นั้นมิใช่อะไรอื่น นอกจากการเล่น และการเพลิดเพลินเท่านั้น และแน่นอนสําหรับบ้านแห่งอาคีเราะห์ นั้นดียิ่งกว่า สําหรับบรรดาผู้ที่ยําเกรง พวกเจ้าไม่ใช้ปัญญาดอกหรือ?');
INSERT INTO "king_fahad_thai" VALUES(6,33,'6:33','เรารู้ดีว่า สิ่งที่พวกเขากล่าวกัน นั้นทําให้เจ้าเสียใจ แท้จริงพวกเขาหาได้ปฏิเสธเจ้าไม่ แต่ทว่าบรรดาผู้อธรรมนั้นปฏิเสธโองการต่าง ๆ ของอัลลอฮ์ ต่างหาก');
INSERT INTO "king_fahad_thai" VALUES(6,34,'6:34','และแน่นอนบรรดา ร่อซูลก่อนเจ้านั้นได้ถูกปฏิเสธมาแล้ว แล้วพวกเขาอดทนต่อสิ่งที่พวกเขาถูกปฏิเสธมา และถูกทําร้ายจนกระทั่ง ความช่วยเหลือของเราได้มายังพวกเขา และไม่มีใครเปลี่ยนแปลงพจนารถของอัลลอฮ์ได้ และแท้จริงนั้นได้มายังเจ้าแล้วจากข่าวคราวของบรรดาผู้ที่ถูกส่งมา');
INSERT INTO "king_fahad_thai" VALUES(6,35,'6:35','และหากว่าการผินหลังให้ของพวกเขานั้นมันใหญ่โต แก่เจ้าแล้ว หากเจ้าสามารถที่จะแสวงหาช่องใด ๆ ลงในแผ่นดิน หรือบันไดสู่ฟากฟ้า แล้วทําสัญญาณหนึ่งมายังพวกเขา และหากว่าอัลลอฮ์ทรงประสงค์แล้ว แน่นอนพระองค์ก็ทรงรวบรวมพวกเขาให้อยู่บนคําแนะนําแล้ว ดังนั้นเจ้าจงอย่าเป็นผู้หนึ่งในหมู่ผู้งมงายเลย');
INSERT INTO "king_fahad_thai" VALUES(6,36,'6:36','แท้จริง ที่ตอบรับ นั้น เพียงบรรดาผู้ที่ฟัง เท่านั้น และบรรดาผู้ที่ตาย นั้น อัลลอฮ์จะทรงให้พวกเขาฟื้นคืนชีพ และพวกเขาก็จะถูกนํากลับไปยังพระองค์');
INSERT INTO "king_fahad_thai" VALUES(6,37,'6:37','และพวกเขากล่าวว่า ไฉนเล่าจึงไม่มีสัญญาณหนึ่งจากพระเจ้าของเขาถูกให้ลงมาแก่เขา จงกล่าวเถิด (มุฮัมมัด) แท้จริงอัลลอฮ์นั้นทรงสามารถที่จะให้สัญญาณหนึ่งลงมา แต่ทว่าส่วนมากพวกเขานั้นไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(6,38,'6:38','และไม่มีสัตว์ใด ๆ ในแผ่นดิน และไม่มีสัตว์ปีกใด ๆ ที่ยินด้วยสองปีกของมัน นอกจากประหนึ่งเป็นประชาชาติ เยี่ยงพวกเจ้านั้นเอง เรามิได้ให้บกพร่องแต่อย่างใดในคัมภีร์ แล้วยังพระเจ้าของพวกเขานั้น พวกเขาจะถูกนําไปชุมนุม');
INSERT INTO "king_fahad_thai" VALUES(6,39,'6:39','และบรรดาผู้ที่ปฏิเสธโองการทั้งหลายของเรานั้น คือผู้ที่หูหนวก และเป็นใบ้ ซึ่งอยู่ในบรรดาความมืด ผู้ใดที่อัลลอฮ์ ทรงประสงค์ พระองค์ก็จะทรงให้เขาหลงทางไป และผู้ใดที่พระองค์ประสงค์ ก็จะทรงให้เขาอยู่บนทางอันเที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(6,40,'6:40','จงกล่าวเถิด (มุฮัมมัด) ท่านได้เห็นพวกท่านแล้วมิใช่หรือ? หากการลงโทษของอัลลอฮ์มายังพวกท่าน หรือวันกิยามะฮ์ ได้มายังพวกท่านอื่นจากอัลลอฮ์ กระนั้นหรือ ที่พวกท่านจะวิงวอนหากพวกเจ้าเป็นผู้พูดจริง');
INSERT INTO "king_fahad_thai" VALUES(6,41,'6:41','มิได้ เฉพาะพระองค์เท่านั้นที่พวกท่านจะวิงวอนขอ แล้วพระองค์ก็จะทรงปลดเปลื้องสิ่งที่พวกท่านวิงวอนให้ช่วยเหลือ หากพระองค์ทรงประสงค์ และพวกเจ้าก็จะลืมสิ่งที่พวกเจ้าให้มีภาคีขึ้น');
INSERT INTO "king_fahad_thai" VALUES(6,42,'6:42','และแท้จริงเราได้ส่งไปยังประชาชาติก่อนหน้าเจ้า แล้วเราก็ได้ลงโทษพวกเขา ด้วยความแร้นแค้น และการเจ็บป่วยเพื่อว่าพวกเขาจะได้นอบน้อม');
INSERT INTO "king_fahad_thai" VALUES(6,43,'6:43','แล้วไฉนเล่า พวกเขาจึงไม่นอบน้อม เมื่อการลงโทษของเราได้มายังพวกเขา แต่ทว่าหัวใจของพวกเขาแข็งกระด้าง และชัยฎอนก็ได้ให้เป็นที่สวยงามแก่พวกเขาด้วย ในสิ่งที่พวกเขากระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(6,44,'6:44','ครั้นเมื่อพวกเขาลืมสิ่งที่พวกเขาถูกเตือน ให้รําลึกในสิ่งนั้น เราก็เปิดให้แก่พวกเขาซึ่งบรรดาประตูของสิ่ง จนกระทั่งเมื่อพวกเขาระเริงต่อสิ่งที่พวกเขาได้รับ เราก็ลงโทษพวกเขาโดยกระทันหัน แล้วทันใดนั้นพวกเขาก็หมดหวัง');
INSERT INTO "king_fahad_thai" VALUES(6,45,'6:45','แล้วได้ถูกตัดขาด จนคนสุดท้ายของกลุ่มชนที่อธรรม และการสรรเสริญทั้งหลายนั้นเป็นสิทธิของอัลลอฮ์ ผู้เป็นพระเจ้าแห่งสากลโลก');
INSERT INTO "king_fahad_thai" VALUES(6,46,'6:46','จงกล่าวเถิด (มุฮัมมัด) พวกท่านเห็นแล้วมิใช่หรือ? หากอัลลอฮ์ทรงเอาหูของพวกท่าน และตาของพวกท่านไป และได้ทรงประทับตราบนหัวใจของพวกท่านด้วยแล้ว ใครเล่าคือผุ้ซึ่งได้รับการเคารพสักการะอื่นจากอัลลอฮ์ที่จะนํามันมาให้แก่พวกท่านได้ จงดูเถิดว่า อย่างไรเล่าที่เราแจกแจงโองการทั้งหลาย แล้วพวกเขาก็ยังหันเหไปได้');
INSERT INTO "king_fahad_thai" VALUES(6,47,'6:47','จงกล่าวเถิด (มุฮัมมัด) พวกท่านเห็นแล้วมิใช่หรือว่า หากการลงโทษของอัลลอฮ์ มายังพวกท่านโดยกระทันหันก็ดี หรือโดยเปิดเผย ก็ดีนั้น จะไม่มีใครถูกทําลาย นอกจากกลุ่มชนผู้อธรรมเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(6,48,'6:48','และเราจะไม่ส่งบรรดาร่อซูลมา นอกจากในฐานะผู้แจ้งข่าวดี และผู้ตักเตือนเท่านั้น ดังนั้นผู้ใดที่ศรัทธาและปรับปรุงแก้ไขแล้ว ก็ไม่มีความกลัวใด ๆ แก่พวกเขา และทั้งพวกเขาก็จะไม่เสียใจ');
INSERT INTO "king_fahad_thai" VALUES(6,49,'6:49','และบรรดาผู้ที่ปฏิเสธบรรดาโองการของเรานั้น การลงโทษจะประสบแก่พวกเขา เนื่องจากการที่พวกเขาละเมิด');
INSERT INTO "king_fahad_thai" VALUES(6,50,'6:50','จงกล่าวเถิด (มุฮัมมัด)ว่า ฉันจะไม่กล่าวแก่พวกท่านว่า ที่ฉันมีบรรดาคลังสมบัติของอัลลอฮ์และทั้งฉันก็ไม่รู้สิ่งเร้นลับ และฉันก็จะไม่กล่าวแก่พวกท่านว่า ฉันคือมะลัก ฉันจะไม่ปฏิบัติตาม นอกจากสิ่งที่ถูกให้เป็นโองการแก่ฉันเท่านั้นจงกล่าวเถิด คนตาบอดกับคนตาดีนั้นจะเท่าเทียมกันหรือ? พวกท่านไม่ใคร่ครวญดอกหรือ?');
INSERT INTO "king_fahad_thai" VALUES(6,51,'6:51','และเจ้าจงตักเตือนด้วย อัลกุรอานแก่บรรดาผู้ที่เกรงกลัวว่าพวกเขาจะถูกนําไปชุมชุมยังพระเจ้าของพวกเขา โดยที่อื่นจากพระองค์แล้วไม่มีผู้ช่วยเหลือคนใด และไม่มีผู้ทําการซะฟาอะฮ์ คนใด สําหรับพวกเขา เพื่อว่าพวกเขาจะได้ยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(6,52,'6:52','เจ้าจงอย่าขับไล่บรรดาผู้ที่วิงวอนต่อพระเจ้าของพวกเขา ทั้งในเวลาเช้าและเวลาเย็น โดยปรารถนาความโปรดปรานจากพระองค์ ไม่เป็นภัยแก่เจ้าแต่อย่างใด ในการชําระพวกเขาและก็ไม่เป็นภัยแก่พวกเขาแต่อย่างใด จากการชําระเจ้าแล้วเหตุใดเจ้าจึงจะขับไล่พวกเขา? (ถ้าเจ้าทําเช่นนั้นแล้ว) เจ้าก็จะกลายเป็นผู้หนึ่งในหมู่ผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(6,53,'6:53','และในทํานองนั้นเราได้ทดสอบบางคนของพวกเขาด้วยอีกบางคน เพื่อพวกเขาจะได้กล่าวว่า ชนเหล่านี้กระนั้นหรือ ที่อัลลอฮ์ทรงกรุณาแก่พวกเขา ในระหว่างพวกเรา อัลลอฮ์นั้นมิใช่เป็นผู้ทรงรู้ยิ่งต่อบรรดาผู้ที่กตัญญูดอกหรือ?');
INSERT INTO "king_fahad_thai" VALUES(6,54,'6:54','และเมื่อบรรดาผู้ศรัทธาต่อบรรดาโองการของเราได้มาหาเจ้า(มุฮัมมัด) ก็จงกล่าวเถิดว่าขอความปลอดภัยจงมีแด่พวกท่านเถิดพระเจ้าของพวกเจ้าได้กําหนดการเอ็นดูเมตตาไว้บนตัวของพระองค์ว่า ผู้ใดในหมู่พวกเจ้ากระทําความชั่วโดยไม่รู้แล้วเขาสํานึกผิดกลับเนื้อกลับตัวหลังจากนั้นและปรับปรุงแก้ไขแล้ว แท้จริงพระองค์คือผู้ทรงอภัยโทษผู้ทรงเอ็นดูเมตตา');
INSERT INTO "king_fahad_thai" VALUES(6,55,'6:55','และในทํานองนั้นเราจะแจกแจงโองการทั้งหลาย และเพื่อที่วิถีทางของผู้กระทําผิดจะได้ประจักษ์ชัด');
INSERT INTO "king_fahad_thai" VALUES(6,56,'6:56','จงกล่าวเถิด(มุฮัมมัด)ว่าแท้จริงฉันถูกห้ามมิให้เคารพสักการะ บรรดาผู้ที่พวกท่านวิงวอนกันอยู่ อื่นจากอัลลอฮ์ จวงกล่าวเถิดฉันจะไม่ปฏิบัติตามความใคร่ใฝ่ต่ําของพวกเจ้า ถ้าเช่นนั้นแน่นอน ฉันก็ย่อมหลงผิดไปด้วย และฉันก็จะไม่ใช่เป็นคนหนึ่งในหมู่ผู้ได้รับคําแนะนํา');
INSERT INTO "king_fahad_thai" VALUES(6,57,'6:57','จงกล่าวเถิด(มุฮัมมัด) แท้จริงฉันอยู่บนหลักฐานอันชัดเจน จากพระเจ้าของฉัน ในขณะเดียวกันพวกเจ้าก็ปฏิเสธหลักฐานนั้น ที่ฉันนั้นไม่มีสิ่งที่พวกเจ้าเร่งรีบดอก แท้จริงการชี้ขาดนั้นมิใช่สิทธิของใครอื่น นอกจากเป็นสิทธิของอัลลอฮ์เท่านั้น โดยที่พระองค์จะทรงแจ้งความจริง และพรองค์เป็นผู้ที่เยี่ยมที่สุดในบรรดาผู้ชี้ขาด');
INSERT INTO "king_fahad_thai" VALUES(6,58,'6:58','จงกล่าวเถิด(มุฮัมมัด) ว่า หากที่ฉันมีสิ่ง(อํานาจ) ที่พวกเจ้าเร่งรีบกันแล้ว แน่นอนกิจการทั้งหลายก็ถูกชี้ขาดระหว่างฉันกับพวกท่านแล้วและอัลลอฮ์เป็นผู้ทรงรู้ยิ่งต่อผู้อธรรมทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(6,59,'6:59','ปละที่พระองค์นั้นมีบรรดากุญแจแห่งความเร้นลับ โดยที่ไม่มีใครรู้กุญแจเหล่านั้น นอกจากพระองค์เท่านั้น และพระองค์ทรงรู้สิ่งที่อยู่ในแผ่นดิน และในทะเล และไม่มีใบไม้ใด ร่วงหล่นลงนอกจากพระองค์จะทรงรู้มัน และไม่มีเมล็ดพืชใด ซึ่งอยู่ในบรรดาความมืดของแผ่นดิน และไม่มีสิ่งที่อ่อนนุ่มใด และสิ่งที่แห้งใด นอกจากจะอยู่ในบันทึกอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(6,60,'6:60','และพระองค์คือผู้ที่ทรงให้พวกเจ้าตาย ในเวลากลางคืน และทรงรู้สิ่งที่พวกเจ้าได้กระทําขึ้นในเวลากลางวัน แล้วก็ทรงให้พวกเจ้าฟื้นคืนชีพ ในเวลานั้น เพื่อว่าเวลาแห่งอายุที่ถูกกําหนดไว้นั้นจะได้ถูกใช้ให้หมดไป แล้วยังพระองค์นั้นคือการกลับไปของพวกเจ้า แล้วพระองค์จะทรงบอกแก่พวกเจ้าในสิ่งที่พวกเจ้ากระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(6,61,'6:61','และพระองค์คือผู้ทรงชนะเหนือปวงบ่าวของพระองค์ และทรงส่งบรรดาผู้บันทึกความดีและความชั่ว มายังพวกเจ้าด้วย จนกระทั่งเมื่อความตายได้มายังคนใดในพวกเจ้าแล้ว บรรดาทูตของเรา ก็จะรับชีวิตของพวกเขาไป โดยที่พวกเขาจะไม่ทําให้บกพร่อง');
INSERT INTO "king_fahad_thai" VALUES(6,62,'6:62','แล้วพวกเขาก็ถูกนํากลับไปยังอัลลอฮ์ ผู้เป็นนายอันแท้จริงของพวกเรา พึงรู้เถิดว่า การชี้ขาด นั้นเป็นสิทธิของพระองค์เท่านั้น และพระองค์เป็นผู้รวดเร็วยิ่งในหมู่ผู้ชําระทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(6,63,'6:63','ขงกล่าวเถิด(มุอัมมัด)ว่า ใครเล่าจะช่วยพวกเจ้าให้พ้นจากบรรดาความมืดของทางบก และทางทะเล โดยที่พวกเจ้าวิงวอนขอต่อเขาด้วยความนอบน้อม และแผ่วเบาว่า ถ้าหากพระองค์ทรงช่วยเราให้รอดพ้นจากสิ่งนี้แล้ว แน่นอนพวกข้าพระองค์ก็จะเป็นผู้ที่อยู่ในหมู่ผู้กตัญญูรู้คุณ');
INSERT INTO "king_fahad_thai" VALUES(6,64,'6:64','จงกล่าวเถิด(มุฮัมมัด) ว่าอัลลอฮ์จะช่วยพวกท่านให้รอดพ้นจากมัน และจากความทุกข์ยากทุกอย่างด้วย แต่แล้วพสกท่านก็ให้มีภาคีขึ้นอีก(แก่พระองค์)');
INSERT INTO "king_fahad_thai" VALUES(6,65,'6:65','จงกล่าวเถิด(มุฮัมมัด) พระองค์คือผู้ทรงสามารถที่จะส่งการลงโทษมายังพวกท่าน จากเบื้องบน ของพวกท่านหรือจากใต้เท้า ของพวกท่านหรือ ให้พวกท่านปนเปกันโดยมีหลายพวก และให้บางส่วนของพวกท่านลิ้มรส ซึ่งการรุกรานของอีกบางส่วน จงดูเถิด(มุฮัมมัด) ว่า เรากําลังแจกแจงโองการทั้งหลายอยู่อย่างไร? เพื่อว่าพวกเขาจะได้เข้าใจ');
INSERT INTO "king_fahad_thai" VALUES(6,66,'6:66','และกลุ่มชนของเจ้านั้นได้ปฏิเสธอัลกุรอาน ทั้ง ๆ ที่อัลกุรอานนั้นเป็นสัจธรรม จงกล่าวเถิด (มุฮัมมัด) ว่า ฉันมิใช่ผู้พิทักษ์พวกท่านดอก');
INSERT INTO "king_fahad_thai" VALUES(6,67,'6:67','สําหรับแต่ละข่าวคราวนั้น ย่อมมีเวลาที่เกิดขึ้น และพวกเจ้าจะได้รู้');
INSERT INTO "king_fahad_thai" VALUES(6,68,'6:68','และเมื่อเจ้าเห็นบรรดาผู้ซึ่งกําลังวิพากษ์วิจารณ์กันอยู่ในบรรดาโองการของเรา แล้ว ก็จงออกห่างจากพวกเขาเสีย จนกว่าพวกเขาจะวิพากษ์วิจารณ์ในเรื่องอื่นจากนั้น และถ้าชัยฏอนทําให้เจ้าลืมแล้ว ก็จงอย่างนั่งรวมกับพวกที่อธรรมเหล่านั้นต่อไป หลังจากที่มีการนึกขึ้นได้');
INSERT INTO "king_fahad_thai" VALUES(6,69,'6:69','และไม่เป็นภัยแก่บรรดาผู้ที่ยําเกรงแต่อย่างใดจากการชําระพวกเขา แต่ทว่าเป็นการตักเตือน (แก่พวกเขา) เพื่อว่าพวกเขาจะได้ยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(6,70,'6:70','และเจ้าจงปล่อยเสีย ซึ่งบรรดาผู้ที่ ยึดเอาศาสนา ของพวกเขาเป็นของเล่น และสิ่งให้ความเพลิดเพลิน และชีวิตความเป็นอยู่ในโลกนี้ได้หลอกลวงพวกเขา และเจ้าจงเตือนด้วยอัล-กุรอาน การที่ชีวิตหนึ่งชีวิตใด จะถูกสังกัดอยู่ กับสิ่งที่ชีวิตได้ขวนขวายไว้ โดยที่อื่นจากอัลลอฮ์ แล้วจะไม่มีผู้ช่วยเหลือคนใด และไม่มีผู้ทําการชะฟาอะฮ์คนใดสําหรับชีวิตนั้นและถ้าชีวิตนั้นจะไถ่ถอนด้วยสิ่งไถ่ถอนทุกอย่าง มันก็จะไม่ถูกรับจากชีวิตนั้น ชนเหล่านี่คือบรรดาผู้ที่ได้ถูกให้สังกัดอยู่กับสิ่งที่พวกเขาได้แสวงหาไว้ ซึ่งพวกเขาจะได้รับเครื่องดื่มจากน้ําที่ร้อนจัด และจะได้รับการลงโทษอันเจ็บแสบเนื่องจากที่พวกเขาปฏิเสธการศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(6,71,'6:71','จงกล่าวเถิด (มุฮัมมัด) ว่า เราจะวิงวอนขอต่อสิ่งที่ไม่ให้คุณแก่เราได้ และไม่ให้โทษแก่เราได้อื่นจากอัลลอฮ์กระนั้นหรือ? และเราก็จะถูกให้หันส้นเท้าของเรากลับ หลังจากที่อัลลอฮ์ได้ทรงแนะนําเราแล้ว ดั่งผู้ที่พวกชัยฏอนได้ทําให้เขาหลงไปในแผ่นดินในสภาพที่งงงวย ซึ่งเขามีเพื่อน ๆ เรียกร้องเขาให้ไปสู่คําแนะนําที่ถูกต้อง ว่า จงหาพวกเราเถิด จงกล่าวเถิด(มุฮัมมัด)ว่าแท้จริงคําแนะนําของอัลลอฮ์เท่านั้นคือคําแนะนํา และพวกเราได้รับบัญชาให้เราสวามิภักดิ์แด่พระผู้เป็นเจ้าแห่งสากลโลกเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(6,72,'6:72','และ(พวกเราได้รับบัญชา) ว่าจงดํารงไว้ซึ่งการละหมาด และจงยําเกรงพระองค์เถิด และพระองค์คือผู้ที่พวกเจ้าจะถูกนกกลับไปชุมนุมยังพระองค์');
INSERT INTO "king_fahad_thai" VALUES(6,73,'6:73','และพระองค์คือ ผู้ที่ทรงสร้างบรรดาชั้นฟ้า และแผ่นดินด้วยความจริง และวันที่ พระองค์ตรัสว่า เจ้าจงเป็นขึ้น แล้วมันก็จะเป็นขึ้น พระดํารัสของพระองค์คือความจริง และอํานาจทั้งหลายนั้นเป็นของพระองค์ ในวันที่จะถูกเป่าเข้าไปในแตร พระผู้ทรงรอบรู้ในสิ่งเร้นลับ และในสิ่งเปิดเผย และพระองค์คือผู้ทรงปรีชาญาณ ผู้ทรงรอบรู้อย่างละเอียดถี่ถ้วน');
INSERT INTO "king_fahad_thai" VALUES(6,74,'6:74','และจงรําลึกขณะที่อิบรอฮีมได้กล่าวแก่บิดาของเขา คืออาซัรว่า ท่านจะยึดถือเอาบรรดาเจว็ดเป็นที่เคารพสักการะกระนั้นหรือ?แท้จริงฉันเห็นว่าท่านและกลุ่มชนของท่านนั้นอยู่ในความหลงผิดอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(6,75,'6:75','และในทํานองนั้นแหละ เราจะให้อิบรอฮีมเห็นอํานาจอันยิ่งใหญ่ ในบรรดาชั้นฟ้า และแผ่นดินและเพื่อเขาจะได้เป็นผู้หนึ่งในหมู่ผู้เชื่อมั่นทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(6,76,'6:76','ครั้นเมื่อกลางคืนปกคลุมเขา เขาได้เห็นดาวดวงหนึ่ง เขากล่าวว่า นี้คือพระเจ้าของฉัน แต่เมื่อมันลับไป เขาก็กล่าวว่า ฉันไม่ชอบบรรดาสิ่งที่ลับไป');
INSERT INTO "king_fahad_thai" VALUES(6,77,'6:77','ครั้นเมื่อเขาเห็นดวงจันทร์กําลังขึ้นเขาก็กล่าวว่านี้คือพระเจ้าของฉัน แต่เมื่อมันลับไป เขาก็กล่าวว่านี้คือพระเจ้าของฉัน แต่เมื่อมันลับไป เขาก็กล่าวว่า ถ้าพระเจ้าของฉันมิได้ทรงแนะนําฉันแล้ว แน่นอนฉันก็จะกลายเป็นคนหนึ่งในกลุ่มชนที่หลงผิด');
INSERT INTO "king_fahad_thai" VALUES(6,78,'6:78','ครั้นเมื่อเขาเห็นดวงอาทิตย์กําลังขึ้นเขาก็กล่าวว่า นี้แหละคือพระเจ้าของฉัน นี้แหละใหญ่กว่า แต่เมื่อมันได้ลับไป เขาก็กล่าวว่า โอ้กลุ่มชนของฉัน! แท้จริงฉันขอปลีกตัวออก จากสิ่งที่พวกท่านให้มีภาคีขึ้น(แก่อัลลอฮ์)');
INSERT INTO "king_fahad_thai" VALUES(6,79,'6:79','แท้จริงข้าพระองค์ขอผินหน้าของข้าพระองค์แด่ผู้ที่สร้างบรรดาชั้นฟ้า และแผ่นดินในฐานะผู้ใฝ่หาความจริง ผู้สวามิภักดิ์และข้าพระองค์มิใช่คนหนึ่งในหมู่ผู้ให้มีภาคีขึ้น');
INSERT INTO "king_fahad_thai" VALUES(6,80,'6:80','และกลุ่มชนของเขาได้โต้เถียงเขา เขาได้กล่าวว่า พวกท่านจะโต้เถียงฉันในเรื่องอัลลอฮ์ กระนั้นหรือ? และแท้จริงพระองค์ได้ทรงแนะนําฉันแล้ว และฉันจะไม่กลัวสิ่งที่พวกท่านให้สิ่งนั้นเป็นภาคีขึ้น นอกจากพระเจ้าของข้าพระองค์จะทรงประสงค์สิ่งหนึ่งสิ่งใดเท่านั้น พระเจ้าของฉันนั้นมีความรู้กว้างขวางทั่วทุกสิ่ง แล้วพวกเจ้าไม่รําลึกลึกหรือ?');
INSERT INTO "king_fahad_thai" VALUES(6,81,'6:81','และอย่างไรเล่าที่ฉันจะกลัวสิ่งที่พวกท่านให้มีภาคีขึ้น โดยที่พวกท่านไม่กลัวที่พวกท่านได้ให้มีภาคีแก่อัลลอฮ์ซึ่งสิ่งที่พระองค์มิได้ทรงให้มีหลักฐานใด ๆ ลงมาแก่พวกเจ้าในสิ่งนั้น แล้วฝ่ายใดเล่าในสองฝ่ายนั้น เป็นฝ่ายที่สมควรต่อความปลอดภัย ยิ่งกว่าหากพวกท่านรู้');
INSERT INTO "king_fahad_thai" VALUES(6,82,'6:82','บรรดาผู้ที่ศรัทธา โดยที่มิได้ให้การศรัทธาของพวกเขาปะปนกับการอธรรม นั้น ชนเหล่านี้แหละพวกเขาจะได้รับความปลอดภัย และพวกเขาคือผู้ที่รับเอาคําแนะนําไว้');
INSERT INTO "king_fahad_thai" VALUES(6,83,'6:83','และนั่นคือ หลักฐานขอวงเราที่ได้ให้มันแก่อิบรอฮีม โดยมีฐานะเหนือกลุ่มชนของเขา เราจะยกขึ้นหลายขั้น ผู้ที่เราประสงค์ แท้จริงพระเจ้าของเจ้านั้น เป็นผู้ทรงปรีชาญาณผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(6,84,'6:84','และเราได้ให้เขา ซึ่งอิสฮาก และยะอ์กูบ ทั้งหมดนั้นเราได้แนะนําแล้ว และนูฮเราก็ได้แนะนําแล้วแต่ก่อนโน้น และจากลูกหลานของเขานั้น คือดาวูด และสุลัยมาน และอัยยูบและยูซุฟและมูซา และฮารูน และในทํานองนั้นแหละ เราจะตอบแทนแก่ผู้กระทําดีทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(6,85,'6:85','และซะกะรียา และยะฮ์ยา และอีซา และอิลยาส ทุกคนนั้นอยู่ในหมู่คนดี');
INSERT INTO "king_fahad_thai" VALUES(6,86,'6:86','และอิสรออีล และอัล-ยะสะอ์ และยูนุสและลูฏ แต่ละคนนั้นเราได้ให้ดีเด่นเหนือกว่าประชาชาติทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(6,87,'6:87','และ (เราได้ให้ดีเด่นอีก) ซึ่งส่วนหนึ่งจากบรรดาบิดาของพวกเขา และเราได้เลือกพวกเขา และได้แนะนําพวกเขาไปสู่ทางอันเที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(6,88,'6:88','นั่นแหละคือ คําแนะนําของอัลลอฮ์ โดยที่พระองค์จะทรงแนะนําผู้ที่พระองค์ทรงประสงค์ในหมู่ปวงบ่าวของพระองค์ ด้วยคําแนะนํานั้น และหากเขาได้ให้มีภาคีขึ้นแล้ว แน่นอนสิ่งที่พวกเขาเคยกระทํากันมา ก็สูญสิ้นไปจากพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(6,89,'6:89','ชนเหล่านี้คือ ผู้ที่เราได้ให้คัมภีร์แก่พวกเขาและให้คําตัดสิน และให้การเป็นนะบีด้วย แต่ถ้าชนเหล่านี้ ปฏิเสธศรัทธาต่อมัน แน่นอนเราได้มอบมันไว้แล้วแก่กลุ่มชนหนึ่งที่พวกเขามิใช่เป็นผู้ปฏิเสธศรัทธาต่อมัน');
INSERT INTO "king_fahad_thai" VALUES(6,90,'6:90','ชนเหล่านี้ คือผู้ที่อัลลอฮ์ได้ทรงแนะนําของพวกเขา เจ้า จงเจริญรอยตามเถิด จงกล่าวเถิด (มุฮัมมัด) ว่าฉันจะไม่ขอต่อพวกท่านซึ่งค่าจ้างใด ๆ ในการให้ ศรัทธาต่ออัลกุรอาน อัลกุรอานนั้น มิใช่อะไรอื่นนอกจากคําตักเตือนสําหรับประชาชาติทั้งหลายเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(6,91,'6:91','และพวกเขา มิได้ให้ความยิ่งใหญ่แก่อัลลอฮ์ตามควรแก่ความยิ่งใหญ่ของพระองค์ จงรําลึกขณะที่พวกเขากล่าวว่า อัลลอฮ์มิได้ทรงประทานสิ่งใดแก่ปุถุชนคนใด จงกล่าวเถิด(มุฮัมมัด) ว่าผู้ใดเล่าที่ได้ทรงประทานลงมา ซึ่งคัมภีร์ที่มูซานํามาเป็นแสงสว่าง และคําแนะนําแก่มนุษย์ซึ่งพวกท่านได้บันทึกไว้ในกระดาษ โดยที่จะได้เปิดเผยมันและก็ปกปิดมันไว้มากมาย และพวกเจ้า ถูกสอนในสิ่งที่ทั้งพวกเจ้า และบรรพบุรุษของพวกเจ้ามิได้รู้มาก่อน จงกล่าวเถิด(มุฮัมมัด) ว่า (ผู้ทรงประทาน) คืออัลลอฮ์นั่นเอง แล้วจงปล่อยพวกเขาสนุกสนามกันในการวิพากษ์วิจารณ์ของพวกเขาต่อไป');
INSERT INTO "king_fahad_thai" VALUES(6,92,'6:92','นี้คือ คัมภีร์ ที่เราได้ให้ลงมาอันเป็นคัมภีร์ที่มีความจําเริญ ที่ยืนยันสิ่ง ซึ่งอยู่เยื้องหน้าคัมภีร์นี้และเพื่อที่เจ้าจะได้ตักเตือนแม่แห่งเมืองทั้งหลาย และผู้ที่อยู่รอบ ๆ แม่เมืองนั้น และบรรดาผู้ที่ศรัทธาต่อปรโลกนั้น พวกเขาย่อมศรัทธาต่อคัมภีร์นี้ และขณะเดียวกันพวกเขาก็จะรักษาการละหมาดของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(6,93,'6:93','และใครเล่าคือ ผู้อธรรมยิ่งกว่าผู้ที่อุปโลกน์ความเท็จให้แก่อัลลอฮ์ หรือกล่าวว่าได้ถูกประทานโองการแก่ฉัน ทั้ง ๆ ที่มิได้มีสิ่งใดถูกประทานให้เป็นโองการแก่เขา และผู้ที่กล่าวว่า ฉันจะให้ลงมาเช่นเดียวกับสิ่งที่อัลลอฮ์ให้ลงมา และหากเจ้าจะได้เห็นขณะที่บรรดาผู้เอธรรมอยู่ในภาวะคับขันแห่งความตาย และมลาอิกะฮ์ กําลังแบมือของพวกเขา(โดยกล่าวว่า)จงให้ชีวิตของพวกท่านออกมา วันนี้พสกท่านจะได้รับการตอบแทน ซึ่งโทษแห่งการต่ําต้อย เนื่องจากที่พวกท่านกล่าวให้ร้ายแก่อัลลอฮ?โดยปราศจากความจริง และเนื่องจากการที่พวกท่านแสดงยะโสต่อบรรดาโองการของพระองค์');
INSERT INTO "king_fahad_thai" VALUES(6,94,'6:94','และแน่นอนพวกเจ้าได้มายังเราโดยลําพัง เยี่ยงที่เราได้บังเกิดพวกเจ้ามาในครั้งแรก และพวกเจ้าได้ละทิ้งสิ่งที่เราไดให้แก่พวกเจ้าไว้เบื้องของพวกเจ้า และเราไม่เห็นอยู่กับพวกเจ้าบรรดาผู้ที่จะช่วยเหลือพวกเจ้าได้อ้างไว้ว่า พวกเขาเป็นผู้มีหุ้นส่วนในพวกเจ้า แน่นอนได้ขาดเป็นเสี่ยง ๆ แล้วในระหว่างพวกเจ้า และได้หายจากพวกเจ้าสิ่งที่พวกเจ้าได้อ้างไว้');
INSERT INTO "king_fahad_thai" VALUES(6,95,'6:95','แท้จริงอัลลอฮ์ เป็นผู้ทรงให้เมล็ดพืชและเมล็ดอินทผาลัมปริออก ทรงให้สิ่งที่มีชีวิตออกจากสิ่งที่ไม่มีชีวิต และทรงให้สิ่งที่ไม่มีชีวิตออกจากสิ่งที่มีชีวิต นั่นแหละคืออัลลอฮ์ แล้วอย่างไรเล่าที่พวกเจ้าถูกหันเหไปได้');
INSERT INTO "king_fahad_thai" VALUES(6,96,'6:96','ผู้ทรงเผยอรุโณทัย และทรงให้กลางคืนเป็นเวลาพักผ่อน และทรงให้ดวงอาทิตย์และดวงจันทร์เป็นการคํานวณ นั่นคือการกําหนดให้มีขึ้นของผู้ทรงเดชานุภาพ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(6,97,'6:97','และพระองค์คือ ผู้ที่ทรงให้มีแก่พวกเจ้า ซึ่งดวงดาวทั้งหลาย เพื่อพวกเจ้าจะได้รับการชี้นําด้วยดวงดาวเหล่านั้น ทั้งในความมืดแห่งทางบกและทางทะเล แน่นอนเราได้แจกแจงโองการทั้งหลายไว้แล้ว สําหรับกลุ่มชนที่รู้');
INSERT INTO "king_fahad_thai" VALUES(6,98,'6:98','และพระองค์คือ ผู้ที่ทรงให้พวกเจ้าเกิดขึ้นจากชีวิตหนึ่ง โดยให้มีที่พัก และให้มีที่ฝาก แน่นอนเราได้แจกแจงโองการทั้งหลายไว้แล้วสําหรับกลุ่มชนที่เข้าใจ');
INSERT INTO "king_fahad_thai" VALUES(6,99,'6:99','และพระองค์นั้นคือผู้ที่ทรงให้น้ําลงมาจากฟากฟ้าแล้วทรงให้ออกมาด้วยน้ํานั้น ซึ่งพันธุ์พืชของทุกสิ่งและเราได้ให้ออกจากพันธุ์พืชนั้นซึ่งสิ่งที่มีสีเขียว จากสิ่งที่มีสีเขียวนั้นเราได้ให้ออกมาซึ่งเมล็ดที่ซ้อนตัวกันอยู่ และจากต้นอินทผาลัมนั้น จั่นของมันเป็นทลายต่ํา และทรงให้ออกมาด้วยน้ํานั้นอีก ซึ่งสวนองุ่นและซัยตูน และทับทิม โดยมีสภาพคล้ายกันและไม่คล้ายกัน “พวกเจ้าจงมองดู ผลของมัน” “เมื่อมันเริ่มออกผลและเมื่อมันแก่สุก แท้จริงในสิ่งเหล่านั้นแน่นอน มีสัญญาณมากมาย สําหรับหมู่ชนผู้ศรัทธา”');
INSERT INTO "king_fahad_thai" VALUES(6,100,'6:100','และพวกเขาได้ให้มีขึ้นแก่อัลลอฮ์ ซึ่งบรรดาภาคีแห่งญิน ทั้งๆ ที่พระองค์ทรงบังเกิดพวกเขา แต่พวกเขา ได้อุปโลกษ์ให้แก่พระองค์ซึ่งบรรดาบุตรชาย และบรรดาบุตรหญิง โดยปราศจากความรู้ พระองค์ทรงบริสุทธิ์และทรงสูงส่งเกินกว่าที่พวกเขาจะกล่าวให้ลักษณะกัน');
INSERT INTO "king_fahad_thai" VALUES(6,101,'6:101','พระผู้ทรงประดิษฐ์ บรรดาชั้นฟ้าและแผ่นดิน อย่างไรเล่าที่พระองค์จะทรงมีพระบุตรโดยที่พระองค์มิได้ทรงมีคู่ครอง? และพระองค์นั้นทรงบังเกิดทุกสิ่งทุกอย่าง และพระองค์ก็ทรงรู้ในทุกสิ่งทุกอย่างด้วย');
INSERT INTO "king_fahad_thai" VALUES(6,102,'6:102','นั่นแหละคืออัลลอฮ์ ผู้เป็นพระเจ้าของพวกเจ้า ไม่มีผู้ควรได้รับการเคารพสักการะ นอกจากพระองค์ ผู้ทรงบังเกิดทุกสิ่งทุกอย่างเท่านั้นพวกเจ้าจงเคารพสักการะพระองค์เถิด และพระองค์ทรงเป็นผู้รับมอบหมายให้คุ้มครองรักษาในทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(6,103,'6:103','สายตาทั้งหลายย่อมไม่ถึงพระองค์ แต่พระองค์ทรงถึงสายตาเหล่านั้น และพระองค์ก็คือผู้ทีรงปรานี ผู้ทรงรอบรู้อย่างถี่ถ้วน');
INSERT INTO "king_fahad_thai" VALUES(6,104,'6:104','“แท้จริงบรรดาหลักฐานจากพระเจ้าของพวกเจ้านั้นได้มายังพวกเจ้าแล้ว ดังนั้น ผู้ใดมองเห็น ก็ย่อมได้แก่ตัวของเขา และผู้ใดมองไม่เห็น ก็ย่อมเป็นภัยแก่ตัวของเขา และฉันมิใช่เป็นผู้พิทักษ์รักษาพวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(6,105,'6:105','และในทํานองเดียวกัน เราจะแจกแจงโองการทั้งหลายไว้ และเพื่อพวกเขาจะได้กล่าวว่า เจ้า(มุฮัมมัด) ได้ศึกษามา และเพื่อเราจะได้ให้แจ่มแจ้งแก่กลุ่มชนที่รู้');
INSERT INTO "king_fahad_thai" VALUES(6,106,'6:106','จงปฏิบัติสิ่งที่ถูกประทานลงมาแก่เจ้าจากพระเจ้าของเจ้าเถิด ไม่มีผู้ใดที่ควรได้รับการเคารพสักการะใด ๆ นอกจากพระองค์เท่านั้น และเจ้าจงผินหลังให้แก่บรรดาผู้ให้มีภาคี เถิด');
INSERT INTO "king_fahad_thai" VALUES(6,107,'6:107','และหากว่าอัลลอฮ์ทรงประสงค์แล้ว พวกเขาก็ย่อมมิให้มีภาคีขึ้น และเราก็มิได้ให้เจ้าเป็นผู้พิทักษ์รักษาพวกเขา และเจ้าก็มิใช่เป็นผู้รับมอบหมาย ให้คุ้มครองรักษาพวกเขาด้วย”');
INSERT INTO "king_fahad_thai" VALUES(6,108,'6:108','และพวกเจ้าจงอย่าด่าว่า บรรดาที่พวกเขาวิงวอนขอ อื่นจากอัลลอฮ์ แล้วพวกเขาก็จะด่าว่าอัลลอฮ์เป็นการละเมิด โดยปราศจากความรู้ ในทํานองนั้นแหละ เราได้ให้สวยงามแก่ทุกชาติ ซึ่งการงานของพวกเขา และยังพระเจ้าของพวกเขานั้น คือการกลับไปของพวกเขา แล้วพระองค์ก็จะทรงบอกแก่พวกเขาในสิ่งที่พวกเขากระทํากัน”');
INSERT INTO "king_fahad_thai" VALUES(6,109,'6:109','และพวกเขาได้สาบานต่ออัลลอฮ์หนักแน่นอย่างยิ่งว่า ถ้าหากมีสัญญาหนึ่ง มายังพวกเขา แน่นอนพวกเขาจะศรัทธา เนื่องด้วยสัญญาณนั้น จงกล่าวเถิด(มุอัมมัด) ว่า แท้จริงสัญญาณทั้งหลายนั้นอยู่ที่อัลลอฮ์เท่านั้น ซึ่งการงานของพวกเขาและยังพระเจ้าของพวกเขานั้น คือการกลับไปของพวกเขา แล้วพระองค์ก็จะทรงบอกแก่พวกเขาในสิ่งที่พวกเขาจะทํากัน”');
INSERT INTO "king_fahad_thai" VALUES(6,110,'6:110','และเราจะพลิกหัวใจของพวกเขา และตาชองพวกเขา เช่นเดียวกับที่พวกเขามิได้ศรัทธาต่อสิ่งนั้น ในครั้งแรก และเราจะปล่อยพวกเขาให้ระเหเร่ร่อนอยู่ในความละเมิดของพวกเขาต่อไป”');
INSERT INTO "king_fahad_thai" VALUES(6,111,'6:111','และแม้ว่าเราได้ให้มลาอิกะฮ์ลงมายังพวกเขา และบรรดาคนตายได้พูดกับพวกเขา และเราได้รวบรวมทุกสิ่งไว้เบื้องหน้าพวกเขา ก็ใช่ว่าพวกเขาจะศรัทธากัน นอกจากอัลลอฮ์จะทรงประสงค์เท่านั้น แต่ทว่าส่วนมากในหมู่พวกเขานั้นไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(6,112,'6:112','และในทํานองนั้นแหละเราได้ให้มีศัตรูขึ้นแก่นะบีทุกคน คือ บรรดาชัยฏอนมนุษย์ และญินโดยที่บางส่วนของพวกเขาจะกระซิบกระซาบแก่อีกบางส่วน ซึ่งคําพูดที่ตกแต่งเป็นการหลอกลวง และหากว่าพระเจ้าของเจ้าทรงประสงค์ แล้วพวกเขาก็มิกระทํามันขึ้นได้ เจ้าจงปล่อยพวกเขาและสิ่งที่พวกเขาอุปโลกน์ ขึ้นเถิด');
INSERT INTO "king_fahad_thai" VALUES(6,113,'6:113','และเพื่อที่หัวใจของบรรดาผู้ที่ไม่ศรัทธาต่อปรโลกจะได้โน้มเอียงไปสู่คําพูดที่ตกแต่งนั้นและเพื่อที่พวกเขาจะได้พึงพอใจในคําพูด นั้น และเพื่อที่พวกเขาจะได้กระทําในสิ่งที่พวกเขาเป็นผู้กระทํากันอยู่');
INSERT INTO "king_fahad_thai" VALUES(6,114,'6:114','อื่นจากอัลลอฮ์กระนั้นหรือ ที่ฉันจะแสวงหาผู้ชี้ขาด ทั้ง ๆ ที่พระองค์เป็นผู้ทรงประทานคัมภีร์ลงมาแก่พวกท่านในสภาพที่ถูกแจกแจงไว้อย่างละเอียด ? และบรรดาผุ้ที่เรา ได้ให้คัมภีร์แก่พวกเขา นั้น พวกเขารู้ดีว่า แท้จริงอัลลอฮ์นั้นถูกประทานลงมาจากพระเจ้าของเจ้า ด้วยความเป็นจริง เจ้าอย่าได้อยู่ในหมู่ผู้สงสัยเป็นอันขาด');
INSERT INTO "king_fahad_thai" VALUES(6,115,'6:115','และถ้อยคําแห่งพระเจ้าของฉันนั้นครบถ้วนแล้ว ซึ่งความสัจจะและความยุติธรรมไม่มีผู้ใดเปลี่ยนแปลงบรรดาถ้อยคําของพระองค์ได้และพระองค์นั้นคือผู้ทรงได้ยิน ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(6,116,'6:116','และหากเจ้าเชื่อฟังสั่นมากของผู้คนในแผ่นดินแล้ว พวกเขาก็จะทําให้เจ้าหลงทางจากทางของอัลลอฮ์ไป พวกเขาจะไม่ปฏิบัติตามนอกจากการนึกคิดเอา เอง และพวกเขามิได้ตั้งอยู่บนสิ่งใดนอกจากพวกเขาจะคาดคะเนเอาเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(6,117,'6:117','แท้จริงพระเจ้าของเจ้านั้นคือผู้ที่ทรงรู้ยิ่งต่อผู้ที่กําลังหลงไปจากท่ายของพระองค์ และเป็นผู้รู้ยิ่งต่อบรรดาผู้ที่รับเอาคําแนะนํา”');
INSERT INTO "king_fahad_thai" VALUES(6,118,'6:118','“ดังนั้นพวกเจ้าจงบริโภคจากสิ่ง ที่พระนามของอัลลอฮ์ถูกกล่าวบนมัน เถิด หากพวกเจ้าเป็นผู้ศรัทธาต่อบรรดาโองการของพระองค์”');
INSERT INTO "king_fahad_thai" VALUES(6,119,'6:119','“แลมีอะไรเกิดขึ้นแก่พวกเจ้ากระนั้นหรือ? ที่พวกเข้าไม่บริโภคจากสิ่งที่พระนามของอัลลอฮ์ถูกกล่าวบนมัน ทั้งๆ ที่พระองค์ทรงแจกแจงแก่พวกเจ้าแล้ว ซึ่งสิ่ง ที่พระองค์ได้ทรงห้ามแก่พวกเจ้า นอกจากสิ่งที่พวกเจ้าได้รับความคับขันให้ต้องการ มันเท่านั้น และแท้จริงมีผู้คนมากมายทําให้ผู้อื่นหลงผิดไป ด้วยความใคร่ใฝ่ต่ําของพวกเขาโดยปราศจากความรู้แท้จริง พระเจ้าของเจ้านั้นคือผู้ที่ทรงรอบรู้ยิ่งต่อผู้ละเมิดทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(6,120,'6:120','“และพวกเจ้าจงสละซึ่งบาปที่เปิดเผยและบาปที่ปกปิด แท้จริงบรรดาผู้ที่ขวนขวายกระทําสิ่งที่เป็นบาปกันอยู่นั้น พวกเขาจะได้รับการตอบแทน ตามที่พวกเขากระทํากัน”');
INSERT INTO "king_fahad_thai" VALUES(6,121,'6:121','และพวกเจ้าจงอย่าบริโภคจากสิ่งที่พระนามของอัลลอฮ์มิได้ถูกกล่าวบน มัน และแท้จริงมัน เป็นการละเมิดแน่ๆ และแท้จริงบรรดาชัยฏอนนั้นจะกระซิบกระซาบแก่บรรดาสหาย ของมัน เพื่อพวกเขา จะได้โต้เถียงกับพวกเจ้า และถ้าหากพวกเจ้าเชื่อฟังพวกเขา แน่นอนพวกเจ้าก็เป็นผู้ให้มีภาคีขึ้น');
INSERT INTO "king_fahad_thai" VALUES(6,122,'6:122','และผู้ที่ตายแล้ว แล้วเราได้ให้เขามีชีวิตขึ้น และเราได้ให้แสงสว่าง แก่เขา ซึ่งเขาใช้แสงสว่างนั้นเดินไปในหมู่มนุษย์นั้น จะเหมือนกับผู้ที่อุปมาของเขาซึ่งอยู่ในบรรดาความมืดโดยที่มิใช่เป็นผู้ที่จะออกมาจากบรรดาความมืดเหล่านั้นได้กระนั้นหรือ ? ในทํานองนั้นแหละได้ถูกประดับให้สวยงาม แก่ผู้ปฏิเสธศรัทธาทั้งหลาย ซึ่งสิ่งที่พวกเขากระทํากันอยู่');
INSERT INTO "king_fahad_thai" VALUES(6,123,'6:123','และในทํานองนั้นแหละ เราได้ให้มีขึ้นในแต่ละเมือง ซึ่งบรรดาบุคคลสําคัญ ๆ เป็นผู้กระทําความผิดแห่งเมืองนั้นๆ เพื่อที่พวกเขาจะได้วางอุบายหลอกลวงในเมืองนั้น และพวกเขาจะไม่วางอุบายหลอกลวง นอกจากตัวของพวกเขาเองเท่านั้น แต่พวกเขาหารู้สึกไม่');
INSERT INTO "king_fahad_thai" VALUES(6,124,'6:124','และเมื่อได้มีโองการใดมายังพวกเขาพวกเขาก็กล่าวว่า เราจะไม่ศรัทธาเป็นอันขาด จนกว่าเราจะได้รับ เยี่ยงสิ่งที่บรรดาร่อซูลของอัลลอฮ์ได้รับมาแล้ว อัลลอฮ์เป็นผู้ทรงรอบรู้ยิ่ง ณ ที่ ที่พระองค์จะทรงให้มีสารของพระองค์ขึ้น บรรดาความต่ําต้อยและการลงโทษอันรุนแรงจากอัลลอฮ์นั้น จะประสบแก่บรรดาผู้ที่กระทําความผิด เนื่องจากการที่พวกเขาวางอุบายหลอกลวงกัน');
INSERT INTO "king_fahad_thai" VALUES(6,125,'6:125','ผู้ใดที่อัลลอฮ์ทรงต้องการจะแนะนําเขาก็จะทรงให้หัวอกของเขาเบิกบาน เพื่ออิสลาม และผู้ใดที่พระองค์ทรงต้องการจะปล่อยให้เขาหลงทาง ก็จะทรงให้ทรวงอกของพวกเขาแคบ อึดอัด ประหนึ่งว่าเขากําลังขึ้นไปยังฟากฟ้า ในทํานองนั้นแหละอัลลอฮ์จะทรงให้มีความโสมม แก่บรรดาผู้ที่ไม่ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(6,126,'6:126','และนี่แหละคือทางแห่งพระเจ้าของเจ้าโดยมีสภาพอันเที่ยงตรง แท้จริงเราได้แจกแจงบรรดาโองการทั้งหลายไว้ แล้ว สําหรับกลุ่มชนที่รําลึก');
INSERT INTO "king_fahad_thai" VALUES(6,127,'6:127','สําหรับพวกเขา นั้น คือนิวาสแห่งความปลอดภัย ในพระผู้เป็นเจ้าจองพวกเขา แลบะขณะเดียวกันพระองค์ก็ทรงเป็นผู้คึ้มครองพวกเขาด้วยเนื่องจากสิ่งที่พวกเขากระทํา');
INSERT INTO "king_fahad_thai" VALUES(6,128,'6:128','และวันที่พระองค์ตจะทรงชุมชนพวกเขาไว้ทั้งหมด(โดยตรัสขึ้นว่า) หมู่ญิณทั้งหลาย ! แท้จริงพวกเจ้าได้กระทําแก่พวกมนุษย์มากมาย และบรรดาสหายของพวกเขาจนหมู่มนุษย์ได้กล่วว่าข้าแด่พระผู้เป็นเจ้าแห่งพวกข้าพระองค์บางส่สนของพวกข้าพระองค์นั้นได้รับประโยชน์ด้วยอีกบางส่วน และพวกข้าพระองค์ก็ได้ถึงแล้วซึ่งกําหนดเวลา ของพวกข้าพระองค์ที่พระองค์ได้ทรงกําหนดไว้แก่พวกข้าพระองค์ พระองค์ตรัสว่านรกนั้นคอที่อยู่ของพวกเจ้า โดยที่จะเป็นผู้อยู่ในนั้นตลอดกาลนอกจากสิ่ง ที่อัลลอฮ์ทรงประสงค์เท่านั้น แท้จริงพระเจ้าของเจ้านั้นเป็นผู้ทรงปรีชาญาณผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(6,129,'6:129','ในทํานองนั้นแหละเราจะให้บางส่วนของผู้อธรรมทั้งหลายเป็นสหายกับอีกบางส่วน เนื่องจากสิ่งที่พวกเขาขวนขวายกัน');
INSERT INTO "king_fahad_thai" VALUES(6,130,'6:130','หมู่ญินและมนุษย์ทั้งหลาย! บรรดาร่อซูลจากพวกเจ้ามิได้มายังพวกเจ้าดอกหรือ? โดยที่พวกเขาจะบอกเล่าแก่พวกเจ้า ซึ่งบรรดาโองการของข้า และเตือนพวกเจ้า ซึ่งบรรดาโองการของข้า และเพื่อนพวกเจ้า ซึ่งการพบกับวัน ของพวกเจ้านี้ พวกเขากล่าวว่า พวกข้าพระองค์ขอยืนยันแก่ตัวของพวกเข้าพระองค์เอง และชีวิตความเป็นอยู่แห่งโลกนี้ได้หลอกลวงพวกเขา และพวกเขาก็ได้ยืนยันแก่ตัวของพวกเขาเองว่า แท้จริงพวกเขานั้นเป็นผู้ปฏิเสธการศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(6,131,'6:131','นั่นก็เพราะว่า พระเจ้าของเจ้านั้นมิเคยเป็นผู้ทําลายเมืองทั้งหลายด้วยความอธรรม โดยที่ชาวเมืองเหล่านั้นไม่รู้อะไร');
INSERT INTO "king_fahad_thai" VALUES(6,132,'6:132','และสําหรับแต่ละคนนั้นมีหลายระดังชั้น เนื่องจากสิ่งที่พวกเขาได้ประกอบไว้และพระเจ้าของเจ้านั้นมิใช่เผลอไผลในสื่งที่พวกเขากระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(6,133,'6:133','และพระเจ้าของเจ้านั้นคือผู้ทรงมั่งมี ผู้ทรงเอ็นดูเมตตา หากพระองค์ทรงประสงค์พระองค์ก็จะทรงให้พวกเจ้าหมดสิ้นไป และจะทรงให้สืบ แทนจากพวกเจ้า ตามที่พระองค์ทรงประสงค์ดังที่ได้ทรงบังเกิดพวกเจ้ามาจากลูหลานของกลุ่มชนอื่น');
INSERT INTO "king_fahad_thai" VALUES(6,134,'6:134','แท้จริงสิ่งที่พวกเจ้าถูกสัญญาไว้ นั้นจะมาแน่นอน และพวกเจ้านั้นไม่สามารถที่จะรอดพ้นไปได้');
INSERT INTO "king_fahad_thai" VALUES(6,135,'6:135','จงกล่าวเถิด(มุฮัมมัด)ว่า ประชาชาติของฉันทั้งหลาย! จงปฏิบัติตามสภาพ ของพวกท่านเถิด แท้จริงฉันก็จะเป็นผู้ปฏิบัติด้วย และพวกท่านจะได้รู้ว่าใครกัน บั้นปลาย แห่งปรโลกจะเป็นของเขา แท้จริงบรรดาผู้อธรรมนั้นจะไม่ได้รับความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(6,136,'6:136','และพวกเขาได้ให้มีส่วนหนึ่งสําหรับอัลลอฮ์ ซึ่งสิ่งที่พระองค์ได้บังเกิดขึ้นอันได้แก่พืชและปศุสัตว์ โดยที่พวกเขากล่าวว่า นี้สําหรับอัลลอฮ์ตามการอ้างของพวกเขา และนี้สําหรับบรรดาภาคีของพวกเรา แล้วส่วนที่เป็นของบรรดาภาคีแห่งพวกเขานั้นก็จะไม่ถึงอัลลอฮ์ แต่ส่วนที่เป็นของอัลลอฮ์นั้นจะถึงบรรดาภาคีของพวกเขา ช่างชั่วช้าแท้ ๆ สิ่งที่พวกเขาตัดสินกัน');
INSERT INTO "king_fahad_thai" VALUES(6,137,'6:137','และในทํานองนั้นแหละ บรรดาภาคีของพวกเขา นั้น ได้ทําให้สวยงามแก่จําวนมากมายในหมู่มุชริกีน ซึ่งการฆ่าลุก ๆ ของพวกเขาเพื่อที่จะทําลายพวกเขา แลเพื่อที่จะให้สับสนแก่พวกเขา ซึ่งศาสนา ของพวกเขา และแม้ว่าอัลลอฮ์ประสงค์ แล้วพวกเขาย่อมไม่กระทํามันเจ้า จงปล่อยพวกเขา และสิ่งที่พวกเขาอุปโลกน์ความเท็จกันเถิด');
INSERT INTO "king_fahad_thai" VALUES(6,138,'6:138','และพวกเขากล่าว่า นี้คือปศุสัตว์ปละพืชผลที่หวงห้ามไว้ ซึ่งไม่มีใครจะบริโภคมันได้นอกจากผู้ที่เราประสงค์ เท่านั้น ด้วยการอ้างของพวกเขา และปศุสัตว์ที่หลังของมันถูกห้าม และปศุสัตว์ ที่พวกเขาจะไม่กล่าวพระนามอัลลอฮ์บนมัน ทั้งนี้เป็นการอุปโลกน์ ความเท็จแก่พระองค์ ซึ่งพระองค์จะทรงตอบแทนลบงโทษพวกเขาใฝนสิ่งที่พวกเขาอุปโลกน์ความเท็จขึ้น');
INSERT INTO "king_fahad_thai" VALUES(6,139,'6:139','และพวกเขากล่าวว่า สิ่งที่อยู่ในท้องของปศุสัตว์ เหล่านั้น เฉพาะบรรดาผู้ชายของเราเท่านั้น และเป็นสิ่งที่ต้องห้ามแก่บรรดาภรรยาของเรา และหากว่ามัน ตาย พวกเขา พวกเขา ก็เป็นผู้มีหุ้นส่วนในมัน และพระองค์จะทรงลงโทษพวกเขา ในการที่เขาได้กําหนดลักษณะไว้ แท้จริงพระองค์เป็นผู้ทรงปรีชาญาณ ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(6,140,'6:140','แท้จริงได้ขาดทุนแล้ว บรรดาผู้ที่ฆ่าลูก ๆ ของพวกเขา เพราะความโง่เขลาโดยปราศจากความรู้ และให้เป็นที่ต้องห้ามในสิ่งที่อัลลอฮ์ให้เป็นปัจจัยบยังชีพแก่พวกเขา ทั้งนี้เป็นการอุปโลกน์ความเท็จให้แก่อัลลอฮ์ แท้จริงนั้นพวกเขาหลงผิดไปแลพวกเขาไม่เคยได้รับคําแนะนํา');
INSERT INTO "king_fahad_thai" VALUES(6,141,'6:141','และพระองค์นั้นคือผู้ที่ทรงให้มีขึ้น ซึ่งสวนทั้งหลายทั้งที่ถูกให้มีร้านขึ้น และไม่ถูกให้มีร้านขึ้น และต้นอินทผาลัมและพืช โดยที่ผลของมันต่างๆ กัน และต้นซัยตูน และต้นทับทิม โดยที่มีความละม้ายคล้ายกัน และไม่ละม้ายคล้ายกัน จงบริโภคจากผลของมันเถิดเมื่อออกผล และจงจ่ายส่วนอันเป็นสิทธิ ในมันด้วย ในวันแห่งการเก็บเกี่ยวมัน และจงอย่าฟุ่มเฟือยทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(6,142,'6:142','และหลังจากหมู่ปศุสัตว์นั้น (ได้ทรงให้มี)ที่ใช้บรรทุก และเชือด จงบริโภคจากสิ่งที่อัลลอฮ์ได้ทรงให้เป็นปัจจัยยังชีพแก่พวกเจ้เาถิดและจงอย่าตามก้าวเดิน ของชัยฏอน แท้จริงมันคือศัตรูอันชัดแจ้งของพวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(6,143,'6:143','และ(ได้ทรงให้มี) สัตว์แปดตัวเป็นคู่ ๆ คือจากแกะสองตัว และจากแพะสองตัว จงกล่าวเถิด(มุฮัมมัด) ว่า ตัวผู้สองตัว นั้นหรือที่พระองค์ทรงห้าม หรือว่าตัวเมียสองตัวนั้น หรือว่าที่มดลูกของตัวเมียทั้งสองนั้นได้คุ้มครองรักษาไว้ พวกท่านจงแจ้งให้ฉันทราบด้วยความรู้อันใดอันหนึ่ง หากพวกท่านพูดจริง');
INSERT INTO "king_fahad_thai" VALUES(6,144,'6:144','และจากอูฐสองตัว และจากวัวสองตัวจงกล่าวเถิด(มุฮัมมัด) ว่า ตัวผู้สองตัวนั้น กระนั้นหรือที่พระองค์ทรงห้ามหรือว่าตัวเมียทั้งสอง นั้นหรือที่มดลูกของตัวเมียทั้งสองนั้นได้คุ้มครองรักษาไว้ หรือว่าพวกท่านร่วมอยู่ ขณะที่อัลลอฮ์ได้ทรงรับสั่งแก่พวกท่านด้วยสิ่งนี้ ก็ใครเล่าคือผู้ที่อธรรมยิ่งกว่าผู้ที่ได้อุปโลกน์ความเท็จให้แก่อัลลอฮ์ เพื่อจะทําให้มนุษย์หลงผิด โดยไม่มีความรู้ แท้จริงอัลลอฮ์นั้นจะไม่ทรงแนะนํากลุ่มชนที่อธรรม');
INSERT INTO "king_fahad_thai" VALUES(6,145,'6:145','จงกล่าวเถิด (มุฮัมมัด) ว่า ฉันไม่พบว่าในสิ่งที่ถูให้เป็นโองการแก่ฉันนั้น มีสิ่งต้องห้ามแก่ผู้บริโภคที่จะบริโภคมัน นอกจากสิ่งนั้นเป็นสัตว์ที่ตายเอง หรือเลือดที่ไหลออก หรือเนื้อสุกร แท้จริงมันเป็นสิ่งโสมม หรือเป็นสิ่งละเมิด ซึ่งถูกเปล่งนามอื่นจากอัลลอฮ์ที่มัน ถ้าผู้ใดได้รับความคับขัน โดยมิใช่เป็นผู้แสวงหา และมิใช่ผู้ละเมิด แล้วไซร้ แท้จริงพระเจ้าของเจ้านั้น เป็นผู้ทรงอภัยโทษ เป็นผู้ทรงเอ็นดูเมตตา');
INSERT INTO "king_fahad_thai" VALUES(6,146,'6:146','และแก่บรรดาผู้เป็นยิวนั้น เราได้ห้ามสัตว์ทุกชนิดที่นิ้วตีนไม่แยกจากกัน และจากวัวและแกะนั้น เราได้ห้ามแก่พวกเขา ซึ่งไขมันของมัน นอกจากไขมันที่หลังของมัน หรือลําไส้ได้อุ้มไว้ หรือที่ปะปนอยู่ที่กระดูกนั่นแหละ เราได้ลงโทษพวกเขา เนื่องด้วยความอธรรมของพวกเขา และแท้จริงเรานั้นเป็นผู้พูดจริง');
INSERT INTO "king_fahad_thai" VALUES(6,147,'6:147','หากพวกเขาปฏิเสธเจ้า ก็จงกล่าวเถิดว่าพระเจ้าของพวกเจ้านั้นเป็นผู้ทรงเอ็นดูเมตตาอันกว้างขวาง และการลงโทษของพระองค์นั้นจะไม่ถูกโต้กลับให้พ้นจากกลุ่มชนที่กระทําความผิด');
INSERT INTO "king_fahad_thai" VALUES(6,148,'6:148','บรรดาผู้ที่ให้มีภาคีขึ้นนั้นจะกล่าวว่าหากว่าอัลลอฮ์ทรงประสงค์ แล้วไซร้ พวกเราก็ย่อมไม่ให้มีภาคีขึ้น และทั้งบรรพบุรุษของพวกเราอีกด้วย และพวกเราก็ย่อมไม่ให้สิ่งใดเป็นที่ต้องห้าม ในทํานองนั้นแหละบรรดาผู้ก่อนหน้าพวกเขาก็ได้มุสาแล้ว จนกระทั่งพวกเขาได้ลิ้มรสการลงโทษของเรา จงกล่าวเถิด(มุฮัมมัด)ว่า ที่พวกท่านนั้นมีความรู้อันใดกระนั้นหรือ ฉะนั้นพวกเจ้าจงจะต้องนํามันออกมาให้แก่เรา พวกท่านจะไม่ปฏิบัติตามสิ่งใด นอกจากการคาดคิดเอาเท่านั้น และพวกท่านไม่มีอื่นใด นอกจากจะกล่าวเท็จเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(6,149,'6:149','จงกล่าวเถิด (มุฮัมมัด) ว่าอัลลอฮ์นั้นทรงมีหลักฐานอันทั่วถึง หากว่าพระองค์ทรงประสงค์แล้ว แน่นอนพระองค์ก็ย่อมแนะนําพวกท่านแล้วทั้งหมด');
INSERT INTO "king_fahad_thai" VALUES(6,150,'6:150','จงกล่าวเถิด (มุฮัมมัด) ว่า พวกท่านจงนํามาซึ่งบรรดาพยานของพวกท่านที่จะยืนว่า แท้จริงฮัลลอฮ์ได้ทรงห้ามสิ่งนี้ แล้วถ้าพวกเขา(เป็นพยาน) ยืนยัน เจ้าก็อย่ายืนยันกับพวกเขาด้วยและอย่าตามความใคร่ใฝ่ต่ําของบรรดาผู้ที่ปฏิเสธโองการทั้งหลายของเรา และบรรดาผู้ที่ไม่ศรัทธาต่อปกโลก และขณะเดียวกันพวกเขาก็ให้สิ่งอื่นเท่าเทียมกับพระเจ้าของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(6,151,'6:151','จงกล่าวเถิด(มุฮัมมัด)ว่าท่านทั้งหลายจงมากันเถิด ฉันจะอ่านให้ฟังสิ่งที่พระเจ้าของพวกท่านได้ห้ามไว้แก่พวกท่านคือ พวกเจ้าอย่าให้สิ่งหนึ่งสิ่งใดเป็นภาคีกับพระองค์ และจงทําดีต่อผู้บังเกิดเกล้าทั้งสองจริง ๆ และอย่าฆ่าลูกของพวกเจ้า เนื่องจากความจนเราเป็นผู้ให้ปัจจัยยังชีพแก่พวกเจ้า และแก่พวกเขา และจงอย่าเข้าใกล้บรรดาสิ่งชั่วช้า ทั้งที่เปิดเผยและที่ปกปิด และอย่าฆ่าชีวิต ที่อัลลอฮ์ทรงห้ามไว้ นอกจากด้วยสิทธิอันชอบธรรมเท่านั้น นั่นแหละที่พระองค์ได้ทรงสั่งเสียมันไว้แก่พวกเจ้า เพื่อว่าพวกเจ้าจะใช้ปัญญา');
INSERT INTO "king_fahad_thai" VALUES(6,152,'6:152','และจงอย่าเข้าใกล้ทรัพย์สมบัติของเด็กกําพร้า นอกจากด้วยวิถีทางที่ดียิ่ง จนกว่าเขาจะบรรลุวัยฉกรรจ์ และจงให้ครบเต็มซึ่งเครื่องตวงและเครื่องชั่งด้วยความเที่ยงตรง เราจะไม่บังคับชีวิตนั้นมีความสามารถเท่านั้นและเมื่อพวกเจ้าพูด ก็จงยุติธรรม และแม้ว่าเขา จะเป็นญาติที่ใกล้ชิดก็ตาม และต่อสัญญาของอัลลอฮ์นั้นก็จงปฏิบัติตามให้ครบถ้วย นั่นแหละที่พระองค์ได้ทรงสั่งเสียมันไว้แก่พวกเจ้า เพื่อว่าพวกเจ้าจะได้รําลึก');
INSERT INTO "king_fahad_thai" VALUES(6,153,'6:153','และแท้จริงนี้คือทางของข้าอันเที่ยงตรงพวกเจ้าจงปฏิบัติตามมันเถิด และอย่าปฏิบัติตามหลาย ๆ ทาง เพราะมันจะทําให้พวกเจ้าแยกออกไปจากทางของพระองค์ นั่นแหละที่พระองค์ได้สั่งเสียมันไว้แก่พวกเจ้า เพื่อว่าพวกเจ้าจะยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(6,154,'6:154','แล้วเราได้ให้คัมภีร์แก่มูซาทั้งนี้เป็นการครบถ้วน แก่ผู้ที่กระทําดี และเป็นการแจกแจงในทุกสิ่งทุกอย่าง และเพื่อเป็นการแนะนํา และเป็นการเอ็นดูเมตตา เพื่อว่าพวกเขาจะได้ศรัทธาต่อการพบกับพระเจ้าของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(6,155,'6:155','และนี้แหละคือคัมภีร์ ที่มีความจําเริญซึ่งเราได้ให้คัมภีร์ลงมายังเจ้า จงปฏิบัติตามคัมภีร์นั้นเถิด และจงยําเกรง เพื่อว่าพวกเจ้าจะได้รับความกรุณาเมตตา');
INSERT INTO "king_fahad_thai" VALUES(6,156,'6:156','(มิเช่นนั้น) พวกเจ้าจะกล่าวว่า แท้จริงคัมภีร์ได้ถูกประทานลงมาให้แก่สองพวก เท่านั้น ก่อนหน้าพวกข้าพระองค์และแท้จริงพวกข้าพระองค์ไม่รู้เรื่องในการอ่านของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(6,157,'6:157','หรือไม่ก็พวกเจ้าจะกล่าวว่า แท้จริงพวกข้าพระองค์นั้น หากได้มีคัมภีร์ถูกประทานลงมาแก่พวกข้าพระองค์แล้วไซร้ แน่นอนพวกข้าพระองค์ก็เป็นผู้ที่อยู่ในคําแนะนําดียิ่งกว่าพวกเขา แท้จริงนั้นได้มายังพวกเจ้าแล้วจากพระเจ้าของพวกเจ้า ซึ่งหลักฐานอันชัดแจ้ง และคําแนะนําและการเอ็นดูเมตตา ดังนั้นใครเล่าคือผู้อธรรมยิ่งไปกว่าผู้ที่ปฏิบัติบรรดาโองการของอัลลอฮ์และผินหลังให้แก่โองการเหล่านั้น เราจะตอบแทนแก่บรรดาผู้ที่ผินหลังให้แก่โองการทั้งหลายของเรา ซึ่งการลงโทษอันชั่วช้า เนื่องจากการที่พวกเขาผินหลังให้');
INSERT INTO "king_fahad_thai" VALUES(6,158,'6:158','พวกเขามิได้รอคอยอะไร นอกจากการที่มะลาอิกะฮ์จะมายังพวกเขา หรือการที่พระเจ้าของเจ้าจะมา หรือการที่สัญญาณบางอย่างแห่งพระเจ้าของเจ้าจะมา วันที่สัญญาณบางอย่างแห่งพระเจ้าของเจ้ามานั้น จะไม่อํานวยประโยชน์แก่ชีวิตหนึ่งชีวิตใด ซึ่งการศรัทธาของเขาโดยที่เขามิได้ศรัทธามาก่อน หรือมิได้แสวงหาความดีใด ๆ ไว้ในการศรัทธาของเขา จงกล่าวเถิด(มุฮัมมัด) ว่า พวกท่านจงรอกันเถิด แท้จริงพวกเราก็เป็นผู้รอคอย');
INSERT INTO "king_fahad_thai" VALUES(6,159,'6:159','แท้จริงบรรดาผู้ที่แบ่งแยกศาสนาของพวกเขา และพวกเขาได้กลายเป็นนิกายต่าง ๆ นั้นเจ้า (มุฮัมมัด) หาใช่อยู่ในพวกเขาแต่อย่างใดไม่แท้จริงเรื่องราวของพวกเขานั้น ย่อมไปสู่อัลลอฮ์แล้วพระองค์จะทรงแจ้งแก่พวกเขาในสิ่งที่พวกเขากระทํากัน');
INSERT INTO "king_fahad_thai" VALUES(6,160,'6:160','ผู้ใดที่นําความดีมา เขาก็จะได้รับสิบเท่าของความดีนั้น และผู้ใดนําความชั่วมาเขาจะไม่ถูกตอบแทน นอกจากเท่าความชั่วนั้นเท่านั้น และพวกเขาจะไม่ถูกอธรรม');
INSERT INTO "king_fahad_thai" VALUES(6,161,'6:161','จงกล่าวเถิด (มุฮัมมัด) ว่า แท้จริงฉันนั้น พระเจ้าของฉันได้แนะนําฉันไปสู่ทางอันเที่ยงตรง คือศาสนที่เที่ยงแท้อันเป็นแนวทางของอิบรอฮีมผู้ใฝ่หาความจริง และเขา(อิบรอฮีม) ไม่เป็นผู้อยู่ในหมู่ผู้ให้มีภาคีขึ้น');
INSERT INTO "king_fahad_thai" VALUES(6,162,'6:162','จงกล่าวเถิด(มุฮัมมัด)ว่า แท้จริงการละหมาดของฉัน และการอิบาดะฮ์ ของฉัน และการมีชีวิตของฉัน และการตายของฉันนั้นเพื่ออัลลอฮ์ผู้เป็นพระเจ้าแห่งสากลโลกเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(6,163,'6:163','ไม่มีภาคีใด ๆ แก่พระองค์ และด้วยสิ่งนั้นแหละข้าพระองค์ถูกใช้ และข้าพระองค์คือคนแรกในหมู่ผู้สวามิภักดิ์ทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(6,164,'6:164','จงกล่าวเถิด(มุฮัมมัด)ว่า อื่นจากอัลลอฮ์กระนั้นหรือ ที่ฉันจะแสวงหาพระเจ้า? ทั้ง ๆ ที่พระองค์นั้นเป็นพระเจ้าของทุกสิ่ง และแต่ละชีวิตนั้นจะไม่แสวงหาสิ่งใด นอกจากจะเป็นภาระแก่ชีวิตนั้นเองเท่านั้น และไม่มีผู้แบกภาระคนใดจะแบกภาระของผู้อื่นได้แล้วยังพระเจ้าของพวกเจ้านั้น คือการกลับไปของพวกเจ้า แล้วพระองค์จะทรงแจ้งแก่พวกเจ้าในสิ่งที่พวกเจ้าขัดแย้งกัน');
INSERT INTO "king_fahad_thai" VALUES(6,165,'6:165','และพระองค์นั้นคือผู้ที่ทรงให้พวกเจ้าเป็นผู้สืบแทนในแผ่นดิน และได้ทรงเทิดบางคนของพวกเจ้าเหนือกว่าอีกบางคนหลายขั้น เพื่อที่พระองค์จะทรงทดสอบพวกเจ้าในสิ่งที่พระองค์ได้ทรงประทานแก่พวกเจ้า แท้จริงพระเจ้าของเจ้านั้น เป็นผู้รวดเร็วในการลงโทษและแท้จริงพระองค์นั้นเป็นผู้ทรงอภัยโทษผู้ทรงเอ็นดูเมตตา');
INSERT INTO "king_fahad_thai" VALUES(7,1,'7:1','อะลิฟ ลาม มีม ศอด');
INSERT INTO "king_fahad_thai" VALUES(7,2,'7:2','มีคัมภีร์ฉบับหนึ่ง ซึ่งถูกประทานลงมาแก่เจ้า ดังนั้นจงอย่าให้ความอึดอัดเนื่องจากคัมภีร์นั้นมีอยู่ในหัวอกของเจ้า ทั้งนี้ เพื่อเจ้าจะได้ใช้คัมภีร์นั้นตักเตือน(ผู้คน) และเพื่อเป็นข้อเตือนใจ แก่ผู้ศรัทธาทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(7,3,'7:3','พวกเจ้าจงปฏิบัติตามสิ่งที่ถูกประทานลงมาแก่พวกเจ้าจากพระเจ้าของพวกเจ้าเถิด และอย่าปฏิบัติตามบรรดาผู้คุ้มครองใดๆ อื่นจากพระองค์ ส่วนน้อยจากพวกเจ้าเท่านั้นแหละที่จะรําลึก');
INSERT INTO "king_fahad_thai" VALUES(7,4,'7:4','และกี่เมืองแล้วที่เราได้ทําลายมัน โดยที่การลงโทษของเราได้มายังเมืองนั้น ในยามค่ําคืนหรือในขณะที่พวกเขานอนพักผ่อนในเวลาบ่าย');
INSERT INTO "king_fahad_thai" VALUES(7,5,'7:5','มิปรากฏว่า พวกเขาวิงวอนขออื่นใดขณะที่การลงโทษของเราได้มายังพวกเขา นอกจากการที่พวกเขากล่าว(สารภาพ)ว่า แท้จริงพวกข้าพระองค์เป็นผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(7,6,'7:6','แน่นอนเราจะถามบรรดาผู้ที่ถูกร่อซูลไปยังพวกเขา และแน่นอนเราจะถามบรรดาร่อซูลทั้งหลายด้วย');
INSERT INTO "king_fahad_thai" VALUES(7,7,'7:7','แน่นอนเราจะนําความรู้มาเล่าให้พวกเขาฟัง และเราไม่เคยหายไปไหน');
INSERT INTO "king_fahad_thai" VALUES(7,8,'7:8','และการชั่ง เป็นความจริงผู้ใดที่ตราชูของเขาหนัก ชนเหล่านี้แหละคือผู้ที่ได้รับความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(7,9,'7:9','และผู้ใดที่ตราชูของเขาเบา ชนเหล่านี้แหละคือผู้ที่ก่อความขาดทุนให้แก่ตัวของพวกเขาเอง เนื่องจากการที่พวกเขามิได้ให้ความเป็นธรรมแก่บรรดาโองการของเรา');
INSERT INTO "king_fahad_thai" VALUES(7,10,'7:10','และแท้จริงนั้น เราได้ให้พวกเจ้ามีที่พํานักอยู่ในแผ่นดิน และเราได้ให้มีขึ้นแก่พวกเจ้า ซึ่งบรรดาเครื่องยังชีพในผืนแผ่นดินนั้น ส่วนน้อยของพวกเจ้าเท่านั้นที่ขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(7,11,'7:11','และแท้จริงเราได้บังเกิดพวกเจ้า แล้วเราได้ให้พวกเจ้าเป็นรูปร่าง แล้วเราได้กล่าวแก่มลาอิกะฮ์ว่า จงสุยูด แก่อาดัมเถิด แล้วพวกเขาก็สุยูดกัน นอกจากอิบลิส เท่านั้น มิปรากฏว่ามันอยู่ในหมู่ผู้สุยูด');
INSERT INTO "king_fahad_thai" VALUES(7,12,'7:12','พระองค์ตรัสว่า อะไรที่ขัดขวางเจ้ามิให้เจ้าสุยูด ขณะที่ข้าได้ใช้เจ้า มันกล่าวว่า ข้าพระองค์ดีกว่าเขา โดยที่พระองค์ทรงบังเกิดข้าพระองค์จาไฟ และได้บังเกิดเขาจากดิน');
INSERT INTO "king_fahad_thai" VALUES(7,13,'7:13','พระองค์ตรัสว่า จงลงจากสวน นั้นไปเสีย ไม่สมควรแก่เจ้าที่จะทําโอหังในนั้น จงออกไปให้พ้นแท้จริงเจ้านั้นอยู่ในหมู่ผู้ต่ําต้อย');
INSERT INTO "king_fahad_thai" VALUES(7,14,'7:14','มันกล่าวว่า โปรดผ่อนผันข้าพระองค์จนถึงวันที่พวกเขา ถูกให้ฟื้นคืนชีพด้วยเถิด');
INSERT INTO "king_fahad_thai" VALUES(7,15,'7:15','พระองค์ตรัสว่า แท้จริงเจ้าอยู่ในหมู่ผู้ที่ได้รับการผ่อนผัน');
INSERT INTO "king_fahad_thai" VALUES(7,16,'7:16','มันกล่าวว่า ด้วยเหตุที่พระองค์ได้ทรงให้ข้าพระองค์ตกอยู่ในความหลงผิด แน่นอนข้าพระองค์จะนั่งขวางกั้นพวกเขา ซึ่งทางอันเที่ยงตรงของพระองค์');
INSERT INTO "king_fahad_thai" VALUES(7,17,'7:17','แล้วข้าพระองค์จะมายังพวกเขา จากเบื้องหน้าของพวกเขา และจากเบื้องหลังของพวกเขาและจากเบื้องขวาของพวกเขา และจากเบื้องซ้ายของพวก เขา และพระองค์จะไม่พบว่าส่วนมากของพวกเขานั้น เป็นผู้ขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(7,18,'7:18','พระองค์ตรัสว่า จงออกจากสวนนั้น ไปในฐานะผู้ถูกติเตียน และถูกขับไล่ ข้าสาบานว่า ผู้ใดในหมู่พวกเขาที่ปฏิบัติตามเจ้า ข้าจะบรรจุให้เต็มญะฮันนัมทั้งจากพวกเจ้าด้วยทั้งหมด');
INSERT INTO "king_fahad_thai" VALUES(7,19,'7:19','และพระองค์ตรัสว่า อาดัมเอ๋ย ! ทั้งเจ้าและคู่ครองเจ้าจงอยู่ในสวนสวรรค์นั้นเถิด แล้วจงบริโภค ณ ที่ใดก็ได้ที่เจ้าทั้งสองประสงค์และเจ้าทั้งสองอย่าเข้าใกล้ต้นไม้ต้นนี้(มิเช่นนั้นแล้ว) เจ้าทั้งสองจะกลายเป็นผู้อยู่ในหมู่ผู้ที่อธรรม');
INSERT INTO "king_fahad_thai" VALUES(7,20,'7:20','แล้วชัยฏอนก็ได้กระซิบกระซาบแก่ทั้งสองนั้น เพื่อที่จะเผย แก่เขาทั้งสองซึ่งสิ่งที่ถูกปิดบังแก่เขาทั้งสองไว้ อันได้แก่สิ่งอันถึงละอาย ของเขาทั้งสอง และมันได้กล่าวว่า พระเจ้าของท่านทั้งสองมิได้ทรงหวงห้ามท่านทั้งสอง ซึ่งต้นไม้ต้นนี้(เพราะอื่นใด) นอกจากการที่ท่านทั้งสองจะกลายเป็นมะลาอิกะฮ์ หรือไม่ก็กลายเป็นผู้อยู่ในหมู่ผู้ที่ยั่งยืนอยู่ตลอดกาลเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(7,21,'7:21','และมันได้สาบานแก่ทั้งสองนั้นว่าแท้จริง)นอยู่ในพวกที่แนะนําท่านทั้งสอง');
INSERT INTO "king_fahad_thai" VALUES(7,22,'7:22','แล้วเราก็ทําให้ทั้งสองนั้นตกอยู่ในสิ่งที่มันต้องการ อันเนื่องจากการหลอกลวง ครั้นเมื่อทั้งสองได้ลิ้มรสต้นไม้ต้นนั้นแล้ว สิ่งอันพึงละอายของเขาทั้งสอง ก็เผยให้ประจักษ์แก่เขาทั้งสอง และเขาทั้งสองก็เริ่มปกปิดบน(ส่วนที่น่าละอาย)ของเขาทั้งสองจากใบไม้แห่งสวนสวรรค์ นั้น และพระเจ้าของเขาทั้งสองจึงได้เรียกเขาทั้งสอง (โดยกล่าวว่า)ข้ามิได้ห้ามเจ้าทั้งสองเกี่ยวกับต้นไม้นั้นดอกหรือ? และข้ามิได้กล่าวแก่เจ้าทั้งสองดอกหรือว่า แท้จริงชัยฏอน นั้นคือศัตรูที่ชัดแจ้งแก่เจ้าทั้งสอง');
INSERT INTO "king_fahad_thai" VALUES(7,23,'7:23','เขาทั้งสองได้กล่าวว่า โอ้พระเจ้าของพวกเข้าพระองค์ พวกข้าพระองค์ได้อธรรมแก่ตัวของพวกข้าพระองค์เอง แลถ้าพระองค์ไม่ทรงอภัยโทษแก่พวกข้าพระองค์และเอ็นดูเมตตาแก่ข้าพระองค์แล้ว แน่นอนพวกข้าพระองค์ก็ต้องกลายเป็นพวกที่ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(7,24,'7:24','พระองค์ตรัสว่า พวกเจ้าจงลง กันไปโดยที่บางส่วนของพวกเจ้า คือ ศัตรูกับอีกบางส่วนและในแผ่นดินนั้นมีที่พํานัก และสิ่งอํานวยประโยชน์สําหรับพวกเจ้าจนถึงระยะเวลาหนึ่ง');
INSERT INTO "king_fahad_thai" VALUES(7,25,'7:25','พระองค์ตรัสว่า ในแผ่นดินนั้นแหละพวกเจ้าจะมีชีวิตอยู่ และในแผ่นดินนั้นแหละพวกเจ้าจะตาย และจากแผ่นดินนั้นและพวกเจ้าจะถูกออกมาอีก');
INSERT INTO "king_fahad_thai" VALUES(7,26,'7:26','ลูกหลานอาดัมเอ๋ย! แท้จริงเราได้ให้ลงมาแก่พวกเจ้าแล้ว ซึ่งเครื่องนุ่งห่ม ทีปกปิดสิ่งที่อันน่าละอายของพวกเจ้าและเครื่องนุ่งห่มที่ให้ความสวยงาม และเครื่องนุ่งห่มแห่งความยําเกรง นั่นคือสิ่งที่ดียิ่ง นั่นแหละคือส่วนหนึ่งจากบรรดาโองการของอัลลอฮ์ เพื่อที่ว่าเขาเหล่านั้นจะได้รําลึก');
INSERT INTO "king_fahad_thai" VALUES(7,27,'7:27','ลูกหลานของอาดัมเอ๋ย! จงอย่าให้ชัยฏอนหลอกลวงพวกเจ้า เช่นเดียวกับที่มันได้ให้พ่อแม่ของพวกเจ้า ออกจากสวนสวรรค์มาแล้ว โดยที่มันได้ถอดเครื่องนุ่งห่ม ของเขาทั้งสองออกเพื่อที่จะให้เขาทั้งสองเห็นสิ่งที่น่าละอาย ของเขาทั้งสองแท้จริงทั้งมัน และเผ่าพันธุ์ของมันมองเห็นพวกเจ้าโดยที่พวกเจ้าไม่เห็นพวกมัน แท้จริงเราได้ให้บรรดาชัยฏอนเป็นเพื่อนกับบรรดาผู้ที่ไม่ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(7,28,'7:28','และเมื่อพวกเขากระทําสิ่งชั่วช้าน่ารังเกียจ พวกเขาก็กล่าวว่า พวกเราได้พบเห็นบรรดาบรรพบุรุษของพวกเราเคยกระทํามา และอัลลอฮฺก็ทรงใช้พวกเราให้กระทํามันด้วย จงกล่าวเถิด(มุฮัมมัด) ว่าแท้จริงอัลลอฮฺนั้นไม่ทรงใช้ให้กระทําสิ่งชั่วช้าน่ารังเกียจดอก พวกท่านจะกล่าวให้ร้ายแก่อัลลอฮฺในสิ่งที่พวกเจ้าไม่รู้กระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(7,29,'7:29','จงกล่าวเถิด(มุฮัมมัด) ว่า พระเจ้าของฉันได้ทรงสั่งให้มีความยุติธรรม และพวกเจ้าจงผินให้ตรงซึ่งใบหน้า ของพวกเจ้า ณ ทุก ๆมัสยิด และจงวินวอนต่อพระองค์ในฐานะผู้มอบอิบาดะฮ์ทั้งหลายแด่พระองค์โดยบริสุทธิ์ใจ เช่นเดียวกับที่พระองค์ได้ทรงบังเกิดพวกเจ้าแต่แรกนั้น พวกเจ้าก็จะกลับไป');
INSERT INTO "king_fahad_thai" VALUES(7,30,'7:30','พวกหนึ่งพระองค์ทรงแนะนําให้ และอีกพวกหนึ่งสมควรแก่พวกเขาแล้วซึ่งการหลงผิด แท้จริงพวกเขาได้ยึดเอาบรรดาชัยฏอนเป็นผู้คุ้มครอง อื่นจากอัลลอฮ์ และพวกเขาคิดว่าพวกเขาคือผู้ที่ได้รับคําแนะนํา');
INSERT INTO "king_fahad_thai" VALUES(7,31,'7:31','ลูหลานของอาดัมเอ๋ย ! จงเอา เครื่องประดับกาย ของพวกเจ้า ณ ทุกมัสยิดและจงกินและจงดื่ม และจงอย่าฟุ่มเฟือย แท้จริงพระองค์ไม่ชอบบรรดาผู้ที่ฟุ่มเฟือย');
INSERT INTO "king_fahad_thai" VALUES(7,32,'7:32','จงกล่าวเถิด(มุฮัมมัด) ว่าผู้ใดเล่าที่ให้เป็นที่ต้องห้าม ซึ่งเครื่องประดับร่างกาย จากอัลลอฮ์ ที่พระองค์ได้ทรงให้ออกมาสําหรับปวงบ่าวของพระองค์ และบรรดาสิ่งดี ๆ จากปัจจัยยังชีพ จงกล่าวเถิด(มุฮัมัด) ว่าสิ่งเหล่านั้นสําหรับบรรดาผู้ที่ศรัทธา ในชีวิตความเป็นอยู่แห่งโลกนี้ (และสําหรับพวกเขา) โดยเฉพาะ ในวันกิยามะฮ์ ในทํานองนั้นแหละเราจะแจกแจงโองการทั้งหลายแก่ผู้ที่รู้');
INSERT INTO "king_fahad_thai" VALUES(7,33,'7:33','จงกล่าวเถิด (มุฮัมมัด) ว่า แท้จริงสิ่งที่พระเจ้าของฉันทรงห้ามนั้น คือบรรดาสิ่งที่ชั่วช้าน่ารังเกียจ ทั้งเป็นสิ่งที่เปิดเผยจากมันและสิ่งที่ไม่เปิดเผย และสิ่งที่เป็นบาป และการข่มเหงรังแกโดยไม่เป็นธรรม และการที่พวกเจ้าให้เป็นภาคแก่อัลลอฮ์ซึ่งสิ่งที่พระองค์มิได้ทรงประทานหลักฐานใด ๆ ลงมาแก่สิ่งนั้น และการที่พวกเจ้ากล่าวให้ภัย แก่อัลลอฮ์ในสิ่งที่พวกเจ้าไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(7,34,'7:34','และสําหรับแต่ละประชาชาตินั้นมีกําหนดเวลาหนึ่ง ครั้นเมื่อกําหนดเวลาของพวกเขามาแล้ว พวกเขาจะขอให้ล่าช้าไปสักชั่วโมงหนึ่งก็ไม่ได้ และจะขอให้เร็วไป (สักชั่วโมงหนึ่ง) ก็ไม่ได้');
INSERT INTO "king_fahad_thai" VALUES(7,35,'7:35','ลูกหลานอาดัมเอ๋ย ! ถ้ามีบรรดาร่อซูลในหมู่พวกเจ้ามายังพวกเจ้าโดยบอกเล่าโองการของข้าแก่พวกเจ้าแล้วผู้ใดที่ยําเกรงและปรับปรุงแก้ไขแล้วก็ไม่มีความหวาดกลัวใดๆแก่พวกเขาและทั้งพวกเขาก็จะไม่เสียใจ');
INSERT INTO "king_fahad_thai" VALUES(7,36,'7:36','และบรรดาผู้ที่ปฏิเสธโองการทั้งหลายของเราและแสดงโอหังเหล่านั้นชนเหล่านี้แหละคือชาวนรกโดยที่พวกเขาจะอยู่ในนรกนั้นตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(7,37,'7:37','แล้วผู้ใดเล่าคือผู้ที่อธรรมยิ่งกว่าผู้ที่อุปโหลกความเท็จให้แก่อัลลอฮ์ หรือปฏิเสธบรรดาโองการของพระองค์ ชนเหล่านี้แหละส่วนของพวกเขาที่ถูกกําหนดไว้นั้น ก็จะได้แก่พวกเขา จนกว่าบรรดาฑูตของเราที่จะเอาชีวิตของพวกเขาได้มายังพวกเขาโดยกล่าวว่า ไหนเล่าสิ่ง ที่พวกท่านวิงวอนขออื่นจากอัลลอฮ์? พวกเขาก็กล่าวว่า เขาเหล่านั้นได้หายหน้าไปจากเราเสียแล้ว และพวกเขาได้ยืนยันแก่ตัวพวกเขาเองว่า พวกเขานั้นเป็นผู้ปฏิเสธการศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(7,38,'7:38','พระองค์ตรัสว่า พวกเจ้าจงเข้าไปในหมู่ประชาชาติที่ได้ล่วงลับมาก่อนพวกเจ้าทั้งที่เป็นญิน (บังเกิดมาจากไฟ) และมนุษย์ ซึ่งอยู่ในไฟนรกนั้นเถิด ทุกครั้งที่มีกลุ่มชนหนึ่งเข้าไป พวกเขาก็สาปแช่งพี่น้องของพวกเขา จนกระทั่งเมื่อพวกเขาได้ไปทันกันในไฟนรกนั้นทั้งหมด แล้ว กลุ่มหลังสุดของพวก เขาก็กล่าวแก่กลุ่มแรกของพวกเขาว่า โอ้พระเจ้าของข้าพระองค์ ชนเหล่านี้แหละได้ทําให้พวกข้าพระองค์หลงผิด ดังนั้นโปรดได้ทรงนํามาแก่พวกเขา ซึ่งการลงโทษสองเท่าจากไฟนรกด้วยเถิด พระองค์ตรัสว่า แต่ละกลุ่มนั้นจะได้รับสองเท่า แต่ทว่าพวกเจ้าไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(7,39,'7:39','และกลุ่มแรกของพวกเขา ได้กล่าวแก่กลุ่มหลังว่า พวกท่านไม่มีความที่เด่นใด ๆ เหนือพวกเรา ดังนั้นพวกท่านจงชิมการลงโทษ เนื่องด้วยสิ่งที่พวกเจ้าแสวงหากันไว้เถิด');
INSERT INTO "king_fahad_thai" VALUES(7,40,'7:40','แท้จริงบรรดาผู้ปฏิเสธโองการต่าง ๆของเรา และได้แสดงโอหังต่อโองการเหล่านั้น บรรดาประตูแห่งฟากฟ้าจะไม่ถูกเปิดให้แก่พวกเขา และพวกเขาจะไม่ได้เข้าสวรรค์ จนกว่าอูฐจะลอดเข้าไปในรูเข็มได้ และในทํานองนั้นแหละ เราจะตอบแทนลงโทษแก่ผู้กระทําความผิด');
INSERT INTO "king_fahad_thai" VALUES(7,41,'7:41','สําหรับพวกเขานั้น คือ ที่นอนจากนรกญะฮันนัม และจากเบื้องบนของพวกเขานั้น มีสิ่งคลุมครอบอยู่ และในทํานองนั้นแหละเราจะตอบแทนลงโทษแก่บรรดาผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(7,42,'7:42','และบรรดาผู้ที่ศรัทธาและประกอบสิ่งที่ดีทั้งหลายนั้นเราไม่บังคับชีวิตใด นอกจากที่ชีวิตนั้นมีความสามารถเท่านั้น ชนเกล่านี้แหละคือชาวสวรรค์ โดยที่พวกเขาจะพํานักอยู่ในสวรรค์นั้นชั่วนิรันดร์');
INSERT INTO "king_fahad_thai" VALUES(7,43,'7:43','และเราได้ถอนออก ซึ่งการผูกใจเจ็บที่อยู่ในหัวอกของพวกเขา (คือชาวสวรรค์) โดยมีบรรดาแม่น้ําไหลอยู่ภายใต้ของพวกเขา และพวกเขาได้กล่าวว่า การสรรเสริญทั้งหลายนั้นเป็นสิทธิของอัลลอฮ์ผู้ทรงแนะนําพวกเราให้ได้รับสิ่งนี้ และใช่ว่าพวกเราจะได้รับคําแนะนําก็หาไม่ หากว่าอัลลอฮ์ไม่ทรงแนะนําแก่พวกเรา แน่นอนบรรดาร่อซูลแห่งพระเจ้าของเรานั้นได้นําความจริงมา และพวกเราได้ถูกป่าวร้องว่า นั้นแหละคือสวนสวรรค์โดยที่พวกท่านได้รับมันไว้เป็นมรดก เนื่องด้วยสิ่งที่พวกเจ้าเคยกระทํากันไว้');
INSERT INTO "king_fahad_thai" VALUES(7,44,'7:44','และชาวสวรรค์ได้ร้องเรียกชาวนรก ว่าแท้จริงพวกเราได้พบแล้วว่าสิ่งที่พระเจ้าของเราได้สัญญาแก่เราไว้นั้นเป็นความจริง และพวกท่านได้พบสิ่งที่พระเจ้าของพวกท่านได้ทรงสัญญาไว้เป็นความจริงไหม? พวกเขากล่าวว่า เป็นความจริงแล้วมีผู้ประกาศคนหนึ่งได้ประกาศขึ้นในระหว่างพวกเขาว่า ขอละอ์นัต ของอัลลอฮ์จงมีแต่ผู้อธรรมทั้งหลายเถิด');
INSERT INTO "king_fahad_thai" VALUES(7,45,'7:45','คือบรรดาผู้ที่ขัดขวางทางของอัลลอฮฺ และปรารถนาให้ทางนั้นคดเคี้ยว และต่อวันปรโลกนั้นพวกเขาปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(7,46,'7:46','และระหว่างพวกเขานั้นมีกําแพงกั้น และบนส่วนสูงของกําแหงนั้นมีบรรดาชายกลุ่มหนึ่ง ซึ่งพวกเขารู้จัก (พวกนั้น) ทั้งหมด ด้วยเครื่องหมาย ของพวกเขา (ชาวสวรรค์) และพวกเขาได้เรียกชาวสวรรค์ (โดยกล่าวว่า) ขอความปลอดภัยจงมีแด่พวกท่านเถิดโดยที่พวกเขา ยังมิได้เข้าสวนสวรรค์ ทั้ง ๆ ที่พวกเขาก็ปรารถนาอย่างแรงกล้า');
INSERT INTO "king_fahad_thai" VALUES(7,47,'7:47','และเมื่อบรรดาสายตาของพวกเขา ถูกหันไปทางชาวนรก พวกเขาก็กล่าวว่า โอ้พระเจ้าของพวกข้าพระองค์ โปรดอย่าได้ทรงให้พวกข้าพระองค์ร่วมอยู่กับกลุ่มผู้อธรรมเหล่านั้นเลย');
INSERT INTO "king_fahad_thai" VALUES(7,48,'7:48','และบรรดาผู้ที่อยู่บนส่วนสูงของกําแพงนั้นได้ร้องเรียกชายกลุ่มหนึ่งซึ่งพวกเขารู้จักพวกนั้นได้ด้วยเครื่องหมาย ของพวกเขา (ชาวนรก) โดยกล่าวว่ามันย่อมไม่อํานวยประโยชน์แก่พวกท่านเลยซึ่งการสะสม (ทรัพย์) ของพวกท่านและการที่พวกท่านหยิ่งยะโส');
INSERT INTO "king_fahad_thai" VALUES(7,49,'7:49','ชนเหล่านี้ใช่ไหม คือผู้ที่พวกเจ้าได้สาบานไว้ว่า อัลลอฮ์จะไม่ทรงให้ได้แก่พวกเขาซึ่งความเอ็นดูเมตตาใด ๆ พวกเจ้าจงเข้าสวรรค์กันเถิด โดยปราศจากความกลัวใด ๆ แก่พวกเจ้า และทั้งพวกเจ้าก็จะไม่เสียใจ');
INSERT INTO "king_fahad_thai" VALUES(7,50,'7:50','และชาวนรกได้ร้องเรียกชาวสวรรค์ว่า จงเทน้ํามาให้แก่พวกเราด้วยเถิด หรือไม่ก็สิ่งที่อัลลอฮ์ได้ทรงให้เป็นปัจจัยยังชีพแก่พวกท่าน ด้วย เขาเหล่านั้นกล่าวว่า แท้จริงอัลลอฮ์ได้ทรงให้สิ่งทั้งสองนั้นเป็นที่ต้องห้ามแก่ผู้ปฏิเสธศรัทธาทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(7,51,'7:51','คือบรรดาผู้ที่ยึดเอาศาสนาของพวกเขาเป็นสิ่งให้ความเพลิดเพลิน และเป็นของเล่น เลแชีวิตความเป็นอยู่แห่งโลกนี้ก็ได้หลอกลวงพวกเขา ด้วย ดังนั้นวันนี้เราจะลืมพวกเขาบ้าง ดังที่พวกเขาได้ลืมการพบกับวันของพวกเขานี้ และการที่พวกเขาปฏิเสธบรรดาโองการของเขา');
INSERT INTO "king_fahad_thai" VALUES(7,52,'7:52','และแท้จริงนั้นเราได้นําคัมภีร์ฉบับหนึ่ง มาให้แก่พวกเขาแล้ว ซึ่งเราได้แจกแจงคัมภีร์นั้นด้วยความรอบรู้ ทั้งนี้เพื่อเป็นข้อแนะนํา และเป็นการเอ็นดูเมตตาแก่กลุ่มชนที่ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(7,53,'7:53','เขาเหล่านั้นมิได้คอยอะไร นอกจากผลสุดท้ายแห่งคัมภีร์นั้นเท่านั้น วันที่ผลสุดท้ายแห่งคัมภีร์จะมานั้น บรรดาผู้ที่ได้ลืมคัมภีร์มาก่อน จะกล่าวว่า แท้จริงบรรดาร่อซูลแห่งพระเจ้าของเราได้นําความจริงมาแล้ว มีบรรดาผู้ที่จะขอความช่วยเหลือให้แก่พวกเราบ้างไหม ซึ่งพวกเขาจะได้ขอความช่วยเหลือให้แก่พวกเรา หรือไม่ก็ให้พวกเราถูกนํากลับไปใหม่ แล้วพวกเราก็จะได้ปฏิบัติอื่นจาก ที่พวกเราเคยปฏิบัติมา แน่นอนพวกเขาได้ยังความขาดทุนให้แก่ตัวของพวกเขาเอง และสิ่งที่พวกเขาอุปโลกน์ขึ้นนั้น ได้หายหน้าจากพวกเขาไป');
INSERT INTO "king_fahad_thai" VALUES(7,54,'7:54','แท้จริงพระเจ้าของพวกเจ้านั้น คือ อัลลออฮืผู้ทรงสร้างบรรดาชั้นฟ้า และแผ่นดินภายในหกวัน แล้วสถิตย์อยู่บนลังลังก์พระองค์ทรงให้กลางคืนครองคลุมกลางวันในสภาพที่กลางคืนไล่ตามกลางวันโดยรวดเร็ว และทรงสร้างดวงอาทิตย์ และดวงจันทร์ และบรรดาดวงดาวขึ้นโดยถูกกําหนดให้กําหนดทําหน้าที่บริการ ตามพระบัญชาของพระองค์ พึงรู้เถิดว่า การสร้างและกิจการทั้งหลายนั้นเป็นสิทธิของพระองค์เท่านั้น มหาบริสุทธิ์อัลลอฮ์ผู้เป็นพระเจ้าแห่งสากลโลก');
INSERT INTO "king_fahad_thai" VALUES(7,55,'7:55','พวกเจ้าจงวิงวอนต่อพระเตจ้าของพวกเจ้าในสภาพถ่อมตนและปกปิด แท้จริงพระองค์ไม่ทรงชอบบรรดาผู้ที่ละเมิด');
INSERT INTO "king_fahad_thai" VALUES(7,56,'7:56','และพวกเจ้าอย่าก่อความเสียหายไว้ในแผ่นดิน หลังจากได้มีการปรับปรุงแกไขมันแล้ว และจงวิงวอนขอต่อพระองค์ด้วยความยําเกรง และความปรารถนาอันแรงกล้า แท้จริงความเอ็นดูเมตตาของอัลลอฮ์นั้นใกล้แก่ผู้กระทําดีทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(7,57,'7:57','“และพระองค์นั้นคือผู้ที่ทรงส่งลงมาเป็นข่าวดีเบื้องหน้าความเอ็นดูเมตตา ของพระองค์จนกระทั่งเมื่อมันได้แบกเมฆ อันหนักอึ้งไว้ เราก็นํามันไปสู่ เมืองที่แห้งแล้ง แล้วเราก็ให้น้ําหลั่งลงที่เมืองนั้น แล้วเราได้ให้ผลไม้ทุกชนิดออกมาด้วยน้ํานั้น ในทํานองนั้นแหละเราจะให้บรรดาผู้ที่ตายแล้วออกมา หวังว่าพวกเจ้าจะได้รําลึก”');
INSERT INTO "king_fahad_thai" VALUES(7,58,'7:58','“และเมืองที่ดี นั้นพืชของมันจะงอกออกมา ด้วยอนุมัติแห่งพระเจ้าของมัน และเมืองที่ไม่ดีนั้นพืชของมันจะไม่ออกนอกจากในสภาพแกร็น ในทํานองนั้นแหละ เราจะแจกแจงบรรดาโองการทั้งหลายแก่กลุ่มชนที่ขอบคุณ”');
INSERT INTO "king_fahad_thai" VALUES(7,59,'7:59','“และแท้จริงเราได้ส่งนูฮ์ไปยังประชาชาติของเขา แล้วเขาได้กล่าวว่า โอ้ประชาชาติของฉันจงเคารพสักการะอัลลอฮ์เถิดไม่มีผู้ได้รับการเคารพสักการะใด ๆ สําหรับพวกท่านอีกแล้วอื่นจากพระองค์ แท้จริงฉันกลัวการลงโทษในวันอันยิ่งใหญ่จะประสบแก่พวกท่าน”');
INSERT INTO "king_fahad_thai" VALUES(7,60,'7:60','“บรรดาชนชั้นนําในหมู่ประชาชนของเขา ได้กล่าวว่า แท้จริงเขาเห็นท่านอยู่ในความหลงผิดอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(7,61,'7:61','“เขากล่าวว่า โอ้ประชาชาติของฉัน! ไม่มีความหลงผิดใด ๆ อยู่ที่ฉัน แต่ทว่าฉัน คือฑูตคนหนึ่ง ซึ่งมาจากพระเจ้าแห่งสากลโลก”');
INSERT INTO "king_fahad_thai" VALUES(7,62,'7:62','โดยที่ฉันจะประกาศแก่พวกท่าน ซึ่งบรรดาสารแห่งพระเจ้าของฉัน และฉันจะชี้แจงและนําให้แก่พวกท่าน และฉัน รู้จากอัลลอฮ์สิ่งที่พวกท่านไม่รู้”');
INSERT INTO "king_fahad_thai" VALUES(7,63,'7:63','“และพวกท่านแปลกใจกระนั้นหรือ? การที่ได้มีข้อตักเตือนจากพระเจ้าของพวกท่านมายังพวกท่านโดยผ่านชายคนหนึ่งในหมู่พวกท่านเพื่อเขาจะได้ตักเตือนพวกท่าน และเพื่อที่พวกท่านจะได้ยําเกรง และเพื่อว่าพวกท่านจะได้รับการเอ็นดูเมตตา”');
INSERT INTO "king_fahad_thai" VALUES(7,64,'7:64','“แล้วพวกเขาได้ปฏิเสธนูฮฺ ภายหลังเรา ได้ช่วยเขา และบรรดาผู้ที่อยู่กับเขาในเรือให้รอดนั้น และเราได้ให้บรรดาผู้ที่ปฏิเสธโองการทั้งหลายของเราจมน้ํา แท้จริงพวกเขานั้นเป็นกลุ่มชนที่มืดบอด');
INSERT INTO "king_fahad_thai" VALUES(7,65,'7:65','“และยังประชาชาติอ๊าดนั้น เราได้ส่งฮูด ซึ่งเป็นพี่น้องของพวกเขาไปเขากล่าวว่า โอ้ประชาชาติของฉัน! จงเคารพสักการะอัลลอฮ์เถิด ไม่มีผู้ที่ควรได้รับการเคารพสักการะใด ๆ สําหรับพวกท่านอีกแล้วอื่นจากพระองค์ พวกท่านไม่ยําเกรงดอกหรือ?”');
INSERT INTO "king_fahad_thai" VALUES(7,66,'7:66','“บรรดาชนชั้นนําที่ปฏิเสธการศรัทธาในหมู่ประชาชาติของเขาได้กล่าวว่า แท้จริงเราเห็นท่านอยู่ในความโฉดเขลา และแท้จริงพวกเราแน่ใจว่าท่านนั้นเป็นคนหนึ่งในหมู่ผู้มุสา”');
INSERT INTO "king_fahad_thai" VALUES(7,67,'7:67','“เขากล่าวว่า โอ้ประชาชาติของฉัน! ไม่มีความโฉดเขลาใด ๆ อยู่ที่ฉัน แต่ทว่าฉันคือร่อซูลคนหนึ่ง ซึ่งมาจากพระเจ้าแห่งสากลโลก”');
INSERT INTO "king_fahad_thai" VALUES(7,68,'7:68','“โดยที่ฉันจะประกาศแก่พวกท่าน ซึ่งบรรดาสารแห่งพระเจ้าของฉัน และฉันนั้นเป็นผู้แนะนําที่ซื่อตรงแก่พวกท่าน”');
INSERT INTO "king_fahad_thai" VALUES(7,69,'7:69','“และพวกท่านแปลกใจกระนั้นหรือ? การที่ได้มีข้อตักเตือนจากพระเจ้าของพวกเจ้ามายังพวกเจ้าโดยผ่านชายคนหนึ่งในหมู่พวกท่าน เพื่อเขาจะได้ตักเตือนพวกท่าน และพวกท่านจงรําลึกเถิด ขณะที่พระองค์ได้ทรงให้พวกท่านเป็นผุ้สืบช่วงแทน มาหลังจากประชาชาติของนูฮ์ และได้ทรงเพิ่มพละกําลังแก่พวกท่านในการบังเกิด ดังนั้นพวกท่านถึงรําลึกถึงความกรุณาของอัลลอฮ์เถิดเพื่อว่าพวกเจ้าจะได้รับความสําเร็จ”');
INSERT INTO "king_fahad_thai" VALUES(7,70,'7:70','“พวกเขากล่าวว่า ที่ท่านมหาพวกเรานั้น เพื่อว่าเราจะได้เคารพสักการะอัลลอฮ์แต่เพียงองค์เดียว และละทิ้งสิ่งที่บรรดาบรรพบุรุษของพวกเราะคยเคารพสักการะมากระนั้นหรือ? จงนําสิ่ง ที่ท่านได้สัญญาแก่พวกเรามายังพวกเราเถิดหากท่านอยู่ในหมู่ผู้พูดจริง”');
INSERT INTO "king_fahad_thai" VALUES(7,71,'7:71','“เขากล่าวว่า แน่นอนได้เกิดขึ้นแล้วแก่พวกท่าน ซึ่งการลงโทษ และความกริ้วโกรธจากระเจ้าของพวกท่าน พวกท่านจะโต้เถียงฉันในบรรดาชื่อ ที่พวกท่านและบรรพบุรุษของพวกท่านได้ตั้งมันขึ้นมาเอง โดยที่อัลลอฮ์มิได้ทรงประทานหลักฐานใด ๆ มาสําหรับชื่อเหล่านั้น กระนั้นหรือ? ดังนั้นพวกท่านจงรอคอยเถิดแท้จริงฉันร่วมกับพวกท่านด้วยในหมู่ผู้รอคอย”');
INSERT INTO "king_fahad_thai" VALUES(7,72,'7:72','“แล้วเราได้ช่วยเขา และบรรดาผู้ที่ร่วมอยู่กับเขาให้รอดพ้น ด้วยความเอ็นดูเมตตาจากเรา แลเราได้ตัดขาด ซึ่งคนสุดท้ายของบรรดาผู้ที่ปฏิเสธโองการทั้งหลายของเรา และมิเคยปรากฏว่าพวกเขาเป็นผู้ศรัทธา”');
INSERT INTO "king_fahad_thai" VALUES(7,73,'7:73','“และยังประชาชาติซะมูตนั้น เราได้ส่งซอและฮ์ซึ่งเป็นพี่น้องของพวกเขาไป เขากล่าวว่าโอ้ประชาชาติของฉัน! จงเคารพสักการะอัลลอฮ์เถิด ไม่มีผู้ที่ควรได้รับการเคารพสักการะใด ๆ สําหรับพวกท่านอีกแล้วอื่นจากพระองค์ แน่นอนได้มีหลักฐานอันชัดเจนจากพระเจ้าของพวกท่านมายังพวกท่านแล้วนี้คืออูฐตัวเมัย ของอัลลอฮ์ในฐานะเป็นสัญญาณหนึ่งสําหรับพวกท่าน ดังนั้นพวกท่านจงปล่อยมันกินในแผ่นดินของอัลลอฮ์เถิด และจงอย่าแตะต้องมันด้วยการทําร้ายใด ๆเลยจะเป็นเหตุให้การลงโทษอันเจ็บแสบคร่าพวกท่านเสีย”');
INSERT INTO "king_fahad_thai" VALUES(7,74,'7:74','“และพวกท่านจงรําลึกขณะที่พระองค์ได้ทรงให้พวกท่านเป็นผู้สืบช่วงแทนมา หลังจากชาวอ๊าด และได้ทรงให้พวกท่านตั้งหลักแหล่งอยู่ในแผ่นดินส่วนนั้น โดยยึดเอาจากที่ราบอขงมันเป็นวัง และสกัดภูเขาเป็นเป็น พวกท่านพึงรําลึกถึงความกรุณาของอัลลอฮ์เถิด และจงอย่าก่อกวนในแผ่นดินในฐานะผู้บ่อนทําลาย”');
INSERT INTO "king_fahad_thai" VALUES(7,75,'7:75','“บรรดาชั้นชั้นนําที่แสดงโอหังจากประชาชาติของเขาได้กล่าวแก่บรรดาผู้ที่ถูกนับว่าอ่อนแอ(กล่าวคือ) แก่บรรดาผู้ศรัทธาในหมู่พวกเขากล่าว่าพวกท่านรู้กระนั้นหรือว่า แท้จริงซอและฮ์นั้นเป็นผู้ถูกส่งมาจากพระเจ้าของเขา พวกเขากล่าวว่าแท้จริงพวกเราเป็นผู้ศรัทธาต่อสิ่งที่เขาถูกส่งให้นําสิ่งนั้นมา”');
INSERT INTO "king_fahad_thai" VALUES(7,76,'7:76','“บรรดาผู้ที่แสดงโอหังกล่าวว่า แท้จริงเราเป็นผู้ปฏิเสธศรัทธา ต่อสิ่งที่พวกท่านได้ศรัทธากัน”');
INSERT INTO "king_fahad_thai" VALUES(7,77,'7:77','“และพวกเขาก็ตัดขาดอูฐตัวเมียตัวนั้นและได้ละเมิดคําสั่ง แห่งพระเจ้าของพวกเจ้า และได้กล่าวว่าโอ้ซอและฮ์ ! จงนําสิ่ง ที่ท่านได้สัญญาแก่พวกเราไว้มาให้แก่พวกเราเถิด ถ้าหากท่านอยู่ในหมู่ผู้ที่ถูกส่งมาเป็นร่อซูล"');
INSERT INTO "king_fahad_thai" VALUES(7,78,'7:78','“และความไวอย่างแรงของแผ่นดิน ก็ได้เคร่าพวกเขา แล้วพวกเขาก็กลายเป็นผู้นั่งคุกเข่าตายในบ้านของพวกเขา”');
INSERT INTO "king_fahad_thai" VALUES(7,79,'7:79','“แล้วเขา ก็หันออกไปจากพวกนั้น และกล่าวว่า โอ้ประชาชาติของฉัน แท้จริงฉันได้ประกาศแก่พวกท่านแล้ว ซึ่งสารแห่งพระเจ้าของฉัน และฉันก็ได้ชี้แจงแนะนําแก่พวกท่านด้วย แต่ทว่าพวกท่านไม่ชอบบรรดาผู้ชี้แจงแนะนํา”');
INSERT INTO "king_fahad_thai" VALUES(7,80,'7:80','“และจงรําลึกถึงลูฏ ขณะที่เขาได้กล่าวแก่ประชาชาติของเขาว่า ท่านทั้งหลายจะประกอบสิ่งชั่วช้าน่ารังเกียจ ซึ่งๆม่มีคนใดในหมู่ประชาชาติทั้งหลายได้ประกอบมันมาก่อนพวกท่านกระนั้นหรือ?”');
INSERT INTO "king_fahad_thai" VALUES(7,81,'7:81','“แท้จริงพวกท่านจะสมสู่เพศชายด้วยตัณหาราคะอื่นจากเพศหญิง ยิ่งกว่าพวกท่านยังเป็นพวกที่ละเมิดขอบเขตด้วย');
INSERT INTO "king_fahad_thai" VALUES(7,82,'7:82','“และคําตอบแห่งประชาชาติของเขานั้นมิปรากฏเป็นอื่นใด นอกจากการที่พวกเขากล่าวว่า ท่านทั้งหลายจงขับไล่พวกเขา ออกไปจากเมืองของพวกท่านเสีย แท้จริงพวกเขาเป็นพวกที่บริสุทธิ์”');
INSERT INTO "king_fahad_thai" VALUES(7,83,'7:83','“และเรา ได้ช่วยเขาและครอบครัวของเขาให้รอดพ้น นอกจากภรรยาของเขาเท่านั้น ซึ่งนางปรากฏอยู่ในหมู่ที่คงอยู่ (เพื่อรับการลงโทษ)');
INSERT INTO "king_fahad_thai" VALUES(7,84,'7:84','“และเราได้ให้ฝน ตกลงมาบนพวกเขาแล้วเจ้า จงดูเถิดว่า ผลสุดท้ายของบรรดาผู้กระทําผิดนั้นเป็นอย่างไร?”');
INSERT INTO "king_fahad_thai" VALUES(7,85,'7:85','“และยังประชาชาติมัดยันนั้น เราได้ส่งชุอัยบ์ ซึ่งเป็นพี่น้องของพวกเขาไป เขากล่าวว่าโอ้ประชาชาติของฉัน! จงเคารพสักการะอัลลอฮ์เถิด ไม่มีสิ่งใดที่ควรได้รับการเคารพสักการะสําหรับพวกท่านอีแล้วอื่นจากพระองค์ แท้จริงหลักฐานอันชัดเจนจากพระเจ้าของพวกท่านนั้นได้มายังพวกท่านแล้ว ดังนั้นจงให้ครบเต็มซึ่งเครื่องตวงและเครื่องชั่งเถิด และจงอย่าให้ขาดแก่เพื่อมนุษย์ซึ่งบรรดาสิ่งของของพวกเขา หลังจากที่มีการแก้ไขมันแล้ว นั่นแหละเป็นสิ่งที่ดียิ่งแก่พวกท่านหากพวกท่านเป็นผู้ศรัทธา”');
INSERT INTO "king_fahad_thai" VALUES(7,86,'7:86','“และพวกท่านอย่านั่งในทุกหนทาง โดยทําการขู่และสกัดกั้นให้ออกจากทางของอัลลอฮ์ผู้ซึ่งศรัทธาต่อพระองค์ และพวกท่านยังปรารถนาให้ทางของอัลลอฮ์คต และจงรําลึกถึงขณะที่พวกท่านมีจํานวนน้อย แล้วพระองค์ได้ทรงให้พวกท่านมีจํานวนมากขึ้น และพวกท่านจงดูเถิดว่าผลสุดท้ายของบรรดาผู้ก่อความเสียหายนั้นเป็นอย่างไร?”');
INSERT INTO "king_fahad_thai" VALUES(7,87,'7:87','“และถ้าหากว่ามีกลุ่มหนึ่งในหมู่พวกท่านศรัทธาต่อสิ่งที่ฉันถูกส่งให้นําสิ่งนั้นมา และอีกกลุ่มหนึ่งมิได้ศรัทธาแล้วก็จงอดทนไปเถิดจนกว่าอัลลอฮ์จะทรงชี้ขาดระหว่างเรา และพระองค์นั้นคือผู้ที่ดีเยี่ยมในหมู่ผู้ชี้ขาดทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(7,88,'7:88','“บรรดาชนชั้นนําที่แสดงโอหังจากประชาชาติของเขา ได้กล่าวว่า แน่นอนเราจะขับไล่ท่านออกไปโอ้ชุอัย! และบรรดาผู้ที่ศรัทธากับท่านด้วยจากเมืองของเรา หรือไม่ก็แน่นอนท่านจะต้องกลับมาในลัทธิของเรา เขา กล่าวว่า แม้ว่าพวกเราจะเกลียด ก็ตามกระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(7,89,'7:89','“แน่นอนพวกเราก็ได้อุปโลกน์ความเท็จให้แก่อัลลอฮ์ หากพวกเรากลับไปในลัทธิของพวกท่านหลังจากที่อัลลอฮ์ได้ทรงช่วยพวกเราให้พ้นจากลัทธินั้นมาแล้ว และไม่บังควรแก่พวกเราที่จะกลับไปในลัทธินั้นอีก นอกจากอัลลอฮ์ผู้เป็นพระเจ้าของพวกเราจะทรงประสงค์เท่านั้น พระเจ้าของพวกเรานั้นทรงมีความรู้กว้างขวางทั่วทุกสิ่งทุกอย่าง แด่อัลลอฮ์เท่านั้นที่พวกเราได้มอบหมายโอ้พระเจ้าของเราโปรดชี้ขาดระหว่งพวกเราและประชาชาติของเราด้วยความจริงเถิด และพระองค์นั้นคือผู้ที่ดีเยี่ยมในหมู่ผู้ชี้ขาดทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(7,90,'7:90','“และบรรดาบุคคลชั้นนําที่ปฏิเสธศรัทธา จากหมู่ประชาชาติของเขา ได้กล่าวว่า แน่นอนถ้าหากพวกเจ้าปฏิบัติตามชุอัยบ์แล้ว แน่นอนพวกท่านก็เป็นผู้ขาดทุนในทันที”');
INSERT INTO "king_fahad_thai" VALUES(7,91,'7:91','“แล้วความไหวอย่างแรงของแผ่นดินก็ได้คร่าพวกเขา แล้วพวกเขาก็กลายเป็นผู้นั่งคุกเข่าตายในบ้านของพวกเขา”');
INSERT INTO "king_fahad_thai" VALUES(7,92,'7:92','“บรรดาผู้ที่ปฏิเสธชุอัยบ์ประหนึ่งว่าพวกเขาไม่เคยอยู่ในหมู่บ้านนั้น บรรดาผู้ที่ปฏิเสธชุอัยบ์นั้น พวกเขาเป็นผู้ขาดทุน”');
INSERT INTO "king_fahad_thai" VALUES(7,93,'7:93','“แล้วเขา ก็หันออกไปจากพวกเขา และกล่าวว่า โอ้ประชาชาติของฉัน แท้จริงฉันได้ประกาศแก่พวกท่านแล้ว ซึ่งบรรดาสารแห่งพระเจ้าของฉัน และฉันก็ได้ชี้แจงแนะนําแก่พวกท่านแล้วแล้วฉันจะเสียใจต่อกลุ่มชนที่ปฏิเสธศรัทธาอย่างไร?”');
INSERT INTO "king_fahad_thai" VALUES(7,94,'7:94','“และเรามิได้ส่งนบีคนใดไปในเมืองหนึ่งเมืองใด นอกจากเราได้ลงโทษชาวเมืองนั้น ด้วยความแร้นแค้น และการเจ็บป่วย เพื่อว่าพวกเขาจะได้นอบน้อม”');
INSERT INTO "king_fahad_thai" VALUES(7,95,'7:95','“ภายหลังเราได้เปลี่ยนความดีแทนที่ความชั่วจนกระทั่งพวกเขามีมาก และพวกเขากล่าวว่า แท้จริงได้ประสบบรรพบุรุษของเรามาแล้วซึ่งความเดือดร้อน และความสุขสบาย แล้วเราจึงได้ลงโทษพวกเขาโดยกระทันหันขณะที่พวกเขาไม่รู้ตัว”');
INSERT INTO "king_fahad_thai" VALUES(7,96,'7:96','“และหากว่าชาวเมืองนั้นได้ศรัทธากันและมีความยําเกรงแล้วไซร้ แน่นอนเราก็เปิดให้แก่พวกเขาแล้ว ซึ่งบรรดาความเพิ่มพูนจากฟากฟ้า และแผ่นดินแต่ทว่าพวกเขาปฏิเสธ ดังนั้นเราจึงได้ลงโทษพวกเขา เนื่องด้วยสิ่งที่พวกเขาขวนขวายไว้”');
INSERT INTO "king_fahad_thai" VALUES(7,97,'7:97','“แล้วชาวเมืองนั้นปลอดภัยกระนั้นหรือ? ในการที่การลงโทษของเราจะมายังพวกเขาในเวลากลางคืน ขณะที่พวกเขานอนหลับอยู่”');
INSERT INTO "king_fahad_thai" VALUES(7,98,'7:98','“และชาวเมืองนั้นปลอดภัยกระนั้นหรือ? ในการที่การลงโทษของเราจะมายังพวกเขาในเวลาสายขณะที่พวกเขากําลังเล่นสนุกสนานกัน อยู่”');
INSERT INTO "king_fahad_thai" VALUES(7,99,'7:99','“แล้วพวกเขาปลอดภัยจากอุบายของอัลลอฮ์กระนั้นหรือ ไม่มีใครมั่นใจว่าจะปลอดภัยจากอุบายของอัลลอฮ์ นอกจากกลุ่มชนที่ขาดทุนเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(7,100,'7:100','“และก็ยังมิได้ประจักษ์แก่บรรดาผู้ที่ได้รับแผ่นดิน สืบทอดหลังจากเจ้าของมันดอกหรือว่าหากเราประสงค์แล้ว เราก็ให้ภัยพิบัติประสบแก่พวกเขาแล้ว เนื่องด้วยบรรดาบาปกรรมของพวกเขาและเราจะประทับตราบนหัวใจของพวกเขา แล้วพวกเขาก็จะไม่ได้ยิน');
INSERT INTO "king_fahad_thai" VALUES(7,101,'7:101','“บรรดาเมืองเหล่านั้นหละ เรากําลังเล่าให้เจ้า ทราบถึงข่าวคราวของมัน และแท้จริงนั้นบรรดาร่อซูลของพวกเขาได้นําบรรดาหลักฐานอันชัดแจ้งมายังพวกเขาแล้ว แต่แล้วใช่ว่าพวกเขาจะศรัทธาต่อสิ่งที่พวกเขาปฏิเสธมาก่อนก็หาไม่ ในทํานองนั้นแหละ อัลลอฮ์จะทรงประทับตราบนหัวใจของผู้ปฏิเสธศรัทธาทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(7,102,'7:102','“และเราไม่พบว่ามีสัญญาใดๆ สําหรับส่วนมากของพวกเขา และแน่นอนเราได้พบว่าส่วนมากของพวกเขานั้นเป็นผู้ละเมิด');
INSERT INTO "king_fahad_thai" VALUES(7,103,'7:103','“แล้วหลังจากพวกเขา เราได้ส่งมูซาพร้อมด้วยสัญญาณต่าง ๆ ของเราไปยังฟิรอาวน์และบรรดาบุคคลชั้นนําของเขา แต่พวกเขาได้ปฏิเสธศรัทธาต่อสัญญาณเหล่านั้น ดังนั้นเจ้าจงมองดูเถิดว่าบั้นปลายของบรรดาผู้ก่อความเสียหายนั้นเป็นอย่างไร ?');
INSERT INTO "king_fahad_thai" VALUES(7,104,'7:104','“และมูซาได้กล่าวว่า โอ้ฟิรอาวน์! แท้จริงฉันคือทูตที่มาจากพระเจ้าแห่งสากลโลก');
INSERT INTO "king_fahad_thai" VALUES(7,105,'7:105','“เป็นสิ่งสมควรในการ ที่ฉันจะไม่กล่าวเกี่ยวกับอัลลอฮ์ นอกจากความจริงเท่านั้น แท้จริงฉันได้นําหลักฐานจากพระเจ้าของพวกเจ้า มายังพวกเจ้าแล้ว ดังนั้นจงส่งวงศ์วานอิสรออีลไปกับฉัน เถิด”');
INSERT INTO "king_fahad_thai" VALUES(7,106,'7:106','“เขา กล่าวว่า หากท่านได้นําหลักฐานใด ๆ มาก็จงนํามันมาเถิด หากท่านอยู่ในหมู่ผู้พูดจริง”');
INSERT INTO "king_fahad_thai" VALUES(7,107,'7:107','“แล้วเขาได้ชักมือของเขาออก แล้วทันใดมันก็คืองูชัด ๆ');
INSERT INTO "king_fahad_thai" VALUES(7,108,'7:108','“และเขาได้ชักมือของเขาออก แล้วทันมันก็ขาว แก่บรรดาผู้ที่มองดูกัน”');
INSERT INTO "king_fahad_thai" VALUES(7,109,'7:109','“บรรดาบุคคลชั้นนําจากประชาชาติของฟิรเอาวน์ได้กล่าวว่า แท้จริงผู้นี้คือมายากลที่รอบ รู้”');
INSERT INTO "king_fahad_thai" VALUES(7,110,'7:110','“เขาต้องการที่จะขับไล่พวกท่านออกจากแผ่นดินของพวกท่าน ดังนั้นพกวท่านจะใช้ให้ทําสิ่งใด ”');
INSERT INTO "king_fahad_thai" VALUES(7,111,'7:111','“พวกเขากล่าวว่า จงประวิงเขาและพี่ชายของเขาไว้ก่อน และจงส่งคนไปรวบรวม ในเมืองต่างๆ');
INSERT INTO "king_fahad_thai" VALUES(7,112,'7:112','“พวกเขา ก็จะนํามายังท่าน ซึ่งนักมายากลทุกคนที่รอบรู้”');
INSERT INTO "king_fahad_thai" VALUES(7,113,'7:113','“และบรรดานักมายากลก็ได้มายังฟิรอาวน์โดยกล่าวว่า แน่นอนพวกเราจะต้องได้รางวัลถ้าพวกเราเป็นผู้ชนะ”');
INSERT INTO "king_fahad_thai" VALUES(7,114,'7:114','“เขา กล่าวว่า ใช่แล้ว และแท้จริงพวกท่านนั้นจะได้อยู่ในหมู่ผู้ใกล้ชิด”');
INSERT INTO "king_fahad_thai" VALUES(7,115,'7:115','“พวกเขา กล่าวว่า โอ้มูซา! ท่านจะโยนก่อนหรือว่าพวกเราจะเป็นผู้โยนก่อน”');
INSERT INTO "king_fahad_thai" VALUES(7,116,'7:116','“เขา กล่าวว่า พวกท่านจงโยนก่อนเถิดครั้นเมื่อพวกเขาได้โยน ออกไป พวกเขาก็ลวงตาประชาชน และทําให้พวกเขากลัว และพวกเขานั้นได้นํามาซึ่งมายากลอันใหญ่หลวง”');
INSERT INTO "king_fahad_thai" VALUES(7,117,'7:117','“แลเราได้มีโองการแก่มูซาว่า จงโยนไม้เท้าของเจ้า แล้วทันใด มันก็กลืนสิ่งที่พวกเขาลวงตาไว้');
INSERT INTO "king_fahad_thai" VALUES(7,118,'7:118','“และความจริง ก็ได้เกิดขึ้น และสิ่งที่พวกเขากระทํา กันขึ้นก็ตกไป”');
INSERT INTO "king_fahad_thai" VALUES(7,119,'7:119','“แล้วที่โน่นแหละ พวกเขา ก็ได้รับความพ่ายแพ้ และกลายเป็นผู้ต่ําต้อย”');
INSERT INTO "king_fahad_thai" VALUES(7,120,'7:120','“และบรรดานักมายากลก็ถูกทําให้ล้มตัวลงกราบ ” (โดยความจริง)');
INSERT INTO "king_fahad_thai" VALUES(7,121,'7:121','“โดยกล่าวว่า พวกเราได้ศรัทธาแล้วต่อพระเจ้าแห่งสากลโลก”');
INSERT INTO "king_fahad_thai" VALUES(7,122,'7:122','“คือพระเจ้าของมูซา และฮารูน”');
INSERT INTO "king_fahad_thai" VALUES(7,123,'7:123','“ฟิรเอาวน์กล่าวว่า พวกท่านศรัทธาต่อเขาก่อนที่ข้าจะอนุมัติแก่พวกท่านกระนั้นหรือ ? แท้จริงนี้คืออุบายหนึ่งที่พวกท่าน ได้วางแผนมันไว้ในเมือง เพื่อที่จะขับไล่ชาวเมือง ให้ออกไปจากเมืองเสีย แล้วพวกท่านจะได้รู้ ”');
INSERT INTO "king_fahad_thai" VALUES(7,124,'7:124','“ข้าสาบานว่าข้าจะตัดมือของท่านและท้าของพวกท่านโดยสลับข้างกัน แล้วข้าจะตรึงพวกท่านทั้งหมดไว้ (ที่ลําต้อนอินทผาลัม)');
INSERT INTO "king_fahad_thai" VALUES(7,125,'7:125','“พวกเขา กล่าวว่า) แท้จริงพวกเราจะเป็นผู้กลับไปยังพระเจ้าของเรา ”');
INSERT INTO "king_fahad_thai" VALUES(7,126,'7:126','“และท่าน จะไม่แก้แค้นเรา นอกจากว่าเราศรัทธาต่อบรรดาสัญญาณ แห่งพระเจ้าของเราเท่านั้น เมื่อมันได้มายังเรา โอ้พระเจ้าของเราโปรดเทความอดทน ลงมาบนพวกเราด้วยเถิด และโปรดทรงให้พวกเราตายในฐานะผู้สวามิภักดิ์ด้วย”');
INSERT INTO "king_fahad_thai" VALUES(7,127,'7:127','“และบรรดาบุคคลชั้นนําจากประชาชาติของฟิรอาวน์ได้กล่าวว่า ท่านจะปล่อยมูซาและพวกพ้อง ของเขาไว้เพื่อก่อความเสียหายในแผ่นดิน และละเลยท่าน และบรรดาที่เคารพสักการะของท่านกระนั้นหรือ ? เขา กล่าวว่า เราจะฆ่าบรรดาลูกชายของพวกเขา และไว้ชีวิตบรรดาหญิงของพวกเขาและแท้จริงเราเป็นผู้มีกําลังอํานาจเหนือพวกเขา”');
INSERT INTO "king_fahad_thai" VALUES(7,128,'7:128','“มูซาได้กล่าวแก่พวกพ้องของเขาว่า จงขอความช่วยเหลือต่ออัลลอฮ์เถิด และจงอดทนด้วย แท้จริงแผ่นดินนั้นเป็นสิทธิของอัลลอฮ์ ซึ่งพระองค์จะทรงให้มันสืบทอดแก่ผู้ที่พระองค์ทรงประสงค์ จากปวงบ่าวของพระองค์ และบั้นปลายนั้นย่อมเป็นของผู้ยําเกรงทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(7,129,'7:129','“พวกเขา กล่าวว่า พวกเราได้รับการทารุณ ทั้งก่อนจากที่ท่าจะมายังพวกเรา และหลังจากที่ท่าได้มายังเราเขา กล่าวว่าหวังว่าพระเจ้าของพวกท่านจะทรงทําลายศัตรูของพวกท่าและจะทรงให้พวกท่าสืบช่วง แทนในแผ่นดินแล้วพระองค์จะทรงดูว่าพวกท่านจะทําอย่างไร?');
INSERT INTO "king_fahad_thai" VALUES(7,130,'7:130','“และแน่นอนเราได้ลงโทษวงศ์วานของฟิรอาวน์ด้วยความแห้งแล้ง และขาดแคลนผลไม้ต่างๆ เพื่อว่าพวกเขาจะได้รําลึก”');
INSERT INTO "king_fahad_thai" VALUES(7,131,'7:131','“ครั้นเมื่อความดี ได้มายังพวกเขา พวกเขาก็กล่าวว่า นี้คือสิทะของเรา และหากความชั่วใด ๆ ประสบแก่พวกเขาพวกเขาก็ถือเอานบีมูซาเป็นลางร้าย และผู้ที่ร่วมอยู่กับเขาด้วย พึงรู้เถิดว่าที่จริงลางร้ายของพวกเขานั้นอยู่ที่อัลลอฮ์ต่างหาก แต่ทว่าส่วนมากของพวกเขาไม่รู้”');
INSERT INTO "king_fahad_thai" VALUES(7,132,'7:132','“และพวกเขากล่าวว่า ท่านจะนําสัญญาณหนึ่งสัญญาณใดมายังพวกเราอย่างไร ก็ตามเพื่อที่จะลวงเราให้หลงเชื่อต่อสัญญาณนั้น เราก็จะไม่เป็นผู้ศรัทธาต่อท่าน ”');
INSERT INTO "king_fahad_thai" VALUES(7,133,'7:133','“แล้วเราได้ส่ง น้ําท่วม และตั๊กแตนและเหา และกบ และเลือดมาเป็นสัญญาณ อันชัดเจนแก่พวกเขา แต่แล้วพวกเขาก็แสดงโอหังและได้กลายเป็นกลุ่มชนที่กระทําความผิด”');
INSERT INTO "king_fahad_thai" VALUES(7,134,'7:134','“และเมื่อมีการลงโทษ เกิดขึ้นแก่พวกเขา พวกเขาก็กล่าวว่า โอ้มูซา! จงขอต่อพระเจ้าของท่านให้แก่เราตามที่พระองค์ได้สัญญาไว้ที่ท่านเถิด ถ้าหากท่านได้ปลดเปลื้องการลงโทษนั้นให้พ้นจากเราแล้ว แน่นอนเราจะศรัทธาต่อท่านและแน่นอนเราจะส่งวงศ์วานอิศรออีลไปกับท่าน”');
INSERT INTO "king_fahad_thai" VALUES(7,135,'7:135','“ครั้นเมื่อเราได้ปลดเปลื้องการลงโทษนั้นให้พ้นจากพวกเขาไปยังกําหนดหนึ่ง ซึ่งพวกเขาถึงกําหนดไปแล้ว ทันใดพวกเขาก็ผิดสัญญา ”');
INSERT INTO "king_fahad_thai" VALUES(7,136,'7:136','“แล้วเราก็ได้ลงโทษพวกเขา โดยให้พวกเขาจมในทะเล เนื่องด้วยพวกเขาได้ปฏิเสธสัญญาณ ต่างๆ ของเรา และพวกเขาจึงได้กลายเป็นที่ไม่ใส่ใจต่อสัญญาณต่างๆ เหล่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(7,137,'7:137','“และเราได้ให้เป็นมรดก แก่กลุ่มชนที่ถูกนับว่าอ่อนแอ ซึ่งบรรดาทิศตะวันออกของแผ่นดิน และบรรดาทิศตะวันตกของมัน อันเป็นแผ่นดินที่เราได้ให้มีความจําเริญในนั้น และถ้อยคําแห่งพระเจ้าของเจ้าอันสวยงามยิ่งนั้นครบถ้วนแล้ว แก่วงศ์วานอิสรออีล เนื่องจากการที่พวกเขามีความอดทน และเราได้ทําลายสิ่งที่ฟิรอาวน์ และพวกพ้องของเขาได้ทําไว้ และสิ่งที่พวกเขาได้ก่อสร้างไว้”');
INSERT INTO "king_fahad_thai" VALUES(7,138,'7:138','“และเราได้ให้วงศ์วานอิสรออีลข้ามทะเลไปได้ แล้วพวกเขาก็มายังกลุ่มชนหนึ่ง ซึ่งกําลังประจําอยู่ที่บรรดาเจว็ดของพวกเขา พวกเขาได้กล่าวขึ้นว่า โอ้มูซา!จงให้มีขึ้นแก่พวกเราด้วยเถิดสิ่งซึ่งเป็นที่เคารพสักการะสักองค์หนึ่ง เช่นเดียวกับที่พวกเขามีสิ่งที่เป็นที่เคารพสักการะหลายองค์เขากล่าวว่า แท้จริงพวกท่านเป็นพวกที่โฉดเขลา”');
INSERT INTO "king_fahad_thai" VALUES(7,139,'7:139','“แท้จริงชนเหล่านี้แหละ สิ่งที่พวกเขาเคารพสักการะกันอยู่นั้นจะถูกทําลาย และสิ่งที่พวกเขาเคยกระทํากันมาก็ไร้ผล ”');
INSERT INTO "king_fahad_thai" VALUES(7,140,'7:140','“เขากล่าวว่า อื่นจากอัลลอฮ์กระนั้นหรือที่ฉันจะแสวงหาสิ่งที่เคารพสักการะให้แก่พวกท่าน ทั้งๆ ที่พระองค์ได้ทรงเทิดพวกท่านเหนือประชาชาติทั้งหลาย ”');
INSERT INTO "king_fahad_thai" VALUES(7,141,'7:141','“และจะรําลึกขณะที่เราได้ช่วยพวกเจ้าให้พ้นจากพวกพ้องของฟิรอาวน์ดดยที่พวกเขาบังคับขู่เข็ญพวกเจ้า ซึ่งการทรมานอันร้ายแรง พวกเขาฆ่าบรรดาลูกผู้ชายของพวกเจ้า และไว้ชีวิตบรรดาลูกผู้หญิงของพวกเจ้า และในเรื่องนั้น คือการทดสอบอันสําคัญจากระเจ้าของพวกเจ้า”');
INSERT INTO "king_fahad_thai" VALUES(7,142,'7:142','“และเราได้สัญญาแก่มูซาสามสิบคืนแลเราได้ให้มันครบอีกสิบ ดังนั้นกําหนดเวลาแห่งพระเจ้าของเราจึงครบสี่สิบคืน และมูซาได้กล่าวแก่ฮารูนพี่ชายของเขาว่า จงทําหน้าที่แทนฉันในหมู่ชนของฉัน และจงปรับปรุงแก้ไข และจงอย่าปฏิบัติตามทางของผู้ก่อความเสียหาย ”');
INSERT INTO "king_fahad_thai" VALUES(7,143,'7:143','“และเมื่อมูซาได้มาตามกําหนดเวลาของเรา และพระเจ้าของเขาได้ตรัสแก่เขา เขา ได้กล่าวขึ้นว่า โอ้พระเจ้าของข้าพระองค์ โปรดให้ข้าพระองค์เห็นด้วยเถิด โดยที่ข้าพระองค์จะได้มองดูพระองค์ พระองค์ตรัสว่า เจ้าจะเห็นข้าไม่ได้เป็นอันขาด แต่ทว่าเจ้าจงมองดูภูเขา นั้นเถิด ถ้าหากมันมั่นอยู่ ณ ที่ของมัน เจ้าก็จะเห็นข้า ครั้นเมื่อพระเจ้าของเขาได้ประจักษ์ที่ภูเขานั้น ก็ทําให้มันทลายตัวลงอย่างราบเรียบ และมูซาก็ล้มลงในสภาพหมดสติ ครั้นเมื่อเขาฟื้นขึ้น เขาก็กล่าวว่ามหาบริสุทธิ์พระองค์ท่าน ข้าพระองค์ขอลุแก่โทษต่อพระองค์ และข้าพระองค์นั้นคือคนแรกในหมู่ผู้ศรัทธาทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(7,144,'7:144','“พระองค์ตรัสว่า โอ้มูซา! แท้จริงข้าได้เลือกเจ้าให้เหนือกว่ามนุษย์ทั้งหลาย เนื่องด้วยบรรดาสารของข้า และด้วยถ้อยคําของเขา ดังนั้นจงยึดถือสิ่งที่ข้าได้ให้แก่เจ้า และจงอยู่ในหมู่ผู้ขอบคุณ”');
INSERT INTO "king_fahad_thai" VALUES(7,145,'7:145','“และเราได้บันทึกคําตักเตือนจากทุกสิ่ง และการแจกแจงในทุกอย่าง ไว้ให้แก่เขาในบรรดาแผ่นจารึก ดังนั้นเจ้า จงยึดถือมันไว้ด้วยความเข้มแข็ง และจงใช้พวกพ้องของเจ้าเถิด พวกเขาก็จะยึดถือสิ่งที่ดีที่สุดของมัน ข้าจะให้พวกเจ้าได้เห็นที่อยู่ของผู้ละเมิด ทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(7,146,'7:146','“ข้าจะหันเห ออกจากบรรดาโองการของข้า ซึ่งบรรดาผู้ที่ยะโสในแผ่นดินโดยไม่บังควร และแม้ว่าพวกเขาจะได้เห็นสัญญาณทุกอย่างพวกเขาก็จะไม่ศรัทธาต่อสัญญาณนั้น และหากพวกเขาเห็นทางแห่งความถูกต้อง พวกเขาก็จะไม่ถือมันเป็นทาง และหากพวกเขาเห็นทางแห่งความผิด พวกเขาก็ยึดถือมันเป็นทาง นั่นก็เพราะว่าพวกเขาปฏิเสธบรรดาโองการของเราและพวกเขาจึงได้เป็นผู้ละเลยโองการเหล่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(7,147,'7:147','“และบรรดาผู้ที่ปฏิเสธบรรดาโองการของเรา และการพบกับปรโลกนั้น บรรดาการงานของพวกเขาย่อมไร้ผล พวกเขาจะไม่ถูกตอบแทนนอกจากสิ่งที่พวกเขากระทําเท่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(7,148,'7:148','“และพวกพ้องของมูซาได้ยึดถือลูกวัวที่เป็นรูปร่าง ซึ่งทํามาจากเครื่องประดับ ของพวกเขา หลังจากเขา ซึ่งลุกวัวนั้นมีเสียงร้อง พวกเขามิได้เห็นดอกหรือว่าแท้จริงมันพูดกับพวกเขาไม่ได้ และมันก็แนะนําทางใดทางหนึ่งให้แก่พวกเขาไม่ได้ด้วย ? พวกเขาได้ยึดถือลูกวัวนั้น และพวกเขาจึงได้กลายเป็นผู้อธรรม ”');
INSERT INTO "king_fahad_thai" VALUES(7,149,'7:149','“และเมื่อได้ถูกตกลงในเมืองของพวกเขา และพวกเขาได้เห็นว่าพวกเขาได้หลงผิดไปแล้ว พวกเขาจึงกล่าวว่า แน่นอนถ้าหากพระเจ้าของเรามิได้เอ็นดูเมตตาแก่เรา และมิได้อภัยโทษให้แก่เราแล้ว แน่นอนพวกเราก็จะต้องอยู่ในหมู่ผู้ขาดทุน”');
INSERT INTO "king_fahad_thai" VALUES(7,150,'7:150','“และเมื่อมูซาได้กลับมายังพวกพ้องของเขาด้วยความโกรธ และเสียใจเขาได้กล่าวว่า ช่างเลวร้ายจริง ๆ ที่พวกท่านทําหน้าที่แทน ฉัน หลังจากฉัน พวกท่านด้วนกระทําก่อน คําสั่งของพระเจ้าของพวกท่าน กระนั้นหรือ?และเขาได้โยนบรรดาแผ่นจารึกลง และจับศรีษะพี่ชายของเขา โดยดึงมัน มายังเขา เขา กล่าวว่า โอ้ลูกแม่ แท้จริงพวกพ้องเหล่านั้นเห็นว่าฉันเป็นผู้อ่อนแอ และพวกเขาเกือบจะฆ่าฉันแล้ว ดังนั้นจงอย่าให้ศัตรูทั้งหลายดีใจ ต่อสิ่งที่ประสบกับฉันเลยและจงอย่าให้ฉันร่วมอยู่ ในกลุ่มชนที่อธรรมเหล่านั้นเลย”');
INSERT INTO "king_fahad_thai" VALUES(7,151,'7:151','“เขากล่าวว่า โอ้พระเจ้าแห่งข้าพระองค์โปรดอภัยโทษแก่ข้าพระองค์ และแก่พี่ชายของข้าพระองค์ด้วย และโปรดได้ทรงให้พวกข้าพระองคืเข้าอยู่ในความเอ็นดูเมตตาของพระองค์เถิดและพระองค์นั้นคือผู้ที่ทรงเอ็นดูเมตตายิ่งกว่าผู้เอ็นดูเมตตาทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(7,152,'7:152','“แท้จริงบรรดาผู้ที่ยึดลูกวัวนั้นจะได้แก่พวกเขา ซึ่งความกริ้วโกรธจากระเจ้าของพวกเขา และความต่ําช้าในชีวิตความเป็นอยู่แห่งโลกนี้ และในทํานองเดียวกัน เราจะตอบแทนแก่บรรดาผู้อุปโลกน์ความเท็จขึ้น”');
INSERT INTO "king_fahad_thai" VALUES(7,153,'7:153','“และบรรดาผู้ที่กระทําสิ่งที่ชั่ว แล้วสํานึกผิดกลับตัวหลังจากนั้น และศรัทธาแล้วไซร้แท้จริงพระเจ้าของเจ้านั้น หลังจากนั้น แล้ว แน่นอนย่อมเป็นผู้ทรงอภัยโทษทรงเอ็นดูเมตตา”');
INSERT INTO "king_fahad_thai" VALUES(7,154,'7:154','“และเมื่อความกริ้วโกรธได้สงบลงจากมูซา เขาก็เอาบรรดาแผ่นจารึกนั้นไปและในสิ่งที่ถูกจารึกไว้ในมันนั้นมีคําแนะนําและความเอ็นดูเมตตาแก่บรรดาผู้ที่เกรงกลัวพระเจ้าของพวกเขา”');
INSERT INTO "king_fahad_thai" VALUES(7,155,'7:155','“และมูซาได้เลือก จากพวกพ้องของเขาซึ่งชายเจ็ดสิบคน สําหรับกําหนดเวลาของ เราครั้นเมื่อความไหวอันรุนแรง ได้คร่าพวกเขา เขา กล่าวว่า โอ้พระเจ้าแห่งข้าพระองค์ หากพระองค์ทรงประสงค์แล้ว พระองค์ก็ทรงทําลายพวกเขาไปก่อนแล้ว และข้าพระองค์ด้วย พระองค์จะทรงทําลายพวกข้าพระองค์ เนื่องด้วยสิ่งที่บรรดาผู้โฉดเขลาในหมู่ผู้ข้าพระองค์ได้กระทําขึ้น กระนั้นหรือ? มัน มิใช่อื่นใดดอก นอกจากการทดสอบของพระองค์เท่านั้นพระองค์จะทรงให้ผู้ที่พระองค์ทรงประสงค์หลงผิด ไปเนื่องด้วยการทดสอบนั้นและจะทรงแนะนําผู้ที่พระองค์ทรงประสงค์ พระองค์นั้นคือผู้ทรงคุ้มครองพวกข้าพระองค์ ดังนั้นโปรดได้ทรงอภัยให้แก่พวกข้าพระองค์ และเอ็นดูเมตตาพวกข้าพระองค์ด้วยเถิด และพระองค์นั้นคือผู้ทรงเยี่ยมกว่าในหมู่ผู้ให้อภัยทั้งหลาย ”');
INSERT INTO "king_fahad_thai" VALUES(7,156,'7:156','“และโปรดได้ทรงกําหนด ความดีให้แก่พวกข้าพระองค์ในโลกนี้ และในปรโลกด้วยแท้จริงพวกข้าพระองค์สํานึกผิดและกลับมายังพระองค์แล้ว พระองค์ตรัสว่า การลงโทษของข้านั้น ข้าจะให้มันประสบแก่ผู้ที่ข้าประสงค์ และการเอ็นดูเมตตาของข้านั้น กว้างขวางทั่วทุกสิ่งซึ่งข้าจะกําหนดมันให้แก่บรรดาผู้ที่ยําเกรง และชําระซะกาต และแก่บรรดาผู้ที่พวกเขาศรัทธาต่อบรรดาโองการของเรา ”');
INSERT INTO "king_fahad_thai" VALUES(7,157,'7:157','“คือบรรดาผู้ปฏิบัติตามร่อซูล ผู้เป็นนบีที่เขียนอ่านไม่เป็นที่พวกเขา พบเขาถูกจารึกไว้ ณ ที่พวกเขา ทั้งในอัต-เตารอต และในอัล-อินญีลโดยที่เขา จะใช้พวกเขาให้กระทําในสิ่งที่ชอบและห้ามพวกเขามืให้กระทําในสิ่งที่ไม่ชอบและจะอนุมัติให้แก่พวกเขาซึ่งสิ่งดี ๆ ทั้งหลาย และจะให้เป็นที่ต้องห้ามแก่พวกเขา ซึ่งสิ่งที่เลวทั้งหลาย และจะปลดเปลื้องออกจากพวกเขา ซึ่งภาระหนักของพวกเขาและห่วงคอ ที่ปรากฏอยู่บนพวกเขา ดังนั้นบรรดาผู้ที่ศรัทธาต่อเขา และให้ความสําคัญแก่เขาและช่วยเหลือเขา และปฏิบัติตามแสงสว่าง ที่ถูกประทานลงมาแก่เขาแล้วไซร้ ชนเหล่านี้แหละคือบรรดาผู้ที่สําเร็จ”');
INSERT INTO "king_fahad_thai" VALUES(7,158,'7:158','“จงกล่าวเถิด (มุฮัมมัด) ว่า โอ้มนุษย์ทั้งหลาย! แท้จริงฉันคือร่อซูลของอัลลอฮ์มายังพวกท่านทั้งมวล ซึ่งพระองค์นั้นอํานาจแห่งบรรดาชั้นฟ้าและแผ่นดินเป็นของพระองค์ ไม่มีผู้ใดควรได้รับการเคารพสักการะ นอกจากพระองค์เท่านั้น ผู้ทรงให้เป็นและทรงให้ตาย ดังนั้นพวกท่านจงศรัทธาต่ออัลลอฮ์และร่อซูลของพระองค์ ผู้เป็นนะบีที่เขียนอ่านไม่เป็น ซึ่งเขาศรัทธาต่ออัลลอฮ์ และดํารัสทั้งหลายของพระองค์ และพวกเจ้าจงปฏิบัติตามเขา เถิด เพื่อว่าพวกเจ้าจะได้รับคําแนะนํา”');
INSERT INTO "king_fahad_thai" VALUES(7,159,'7:159','“และจากพวกพ้องของมูซานั้นมีกลุ่มหนึ่งที่แนะนําชี้แจงด้วยความจริง และด้วยความจริงนั้นพวกเขาใก้ความเที่ยงธรรม”');
INSERT INTO "king_fahad_thai" VALUES(7,160,'7:160','“และเราได้แบ่งพวกเขาออกเป็นสิบสองเหล่า คือสิบสองกลุ่ม และเราได้มีโองการแก่มูซาขณะที่พวกพ้องของเขาได้ขอน้ําจากเขาว่า จงตีหินก้อนนั้นด้วยไม้เท้าของเจ้า แล้วตาน้ําสิบสองตาก็พวยพุ่งขึ้นจากก้อนหิน นั้น แท้จริงกลุ่มชนแต่ละเหล่า ย่อมรู้แหล่งน้ําดื่มของตน และเราได้ให้เมฆบดบังพวกเขา และเราได้ให้ลงมาแก่พวกเขาซึ่งของหวาน และนกคุ่ม พวกเจ้าจงบริโภคสิ่งที่เราได้ให้เป็นปัจจัยยังชีพแก่พวกเจ้าจากสิ่งที่ดี ๆ เถิดและพวกเขาหาได้อธรรมแก่เราไม่ แต่ทว่าพวกเขาอธรรมแก่ตัวของพวกเขาเองต่างหาก”');
INSERT INTO "king_fahad_thai" VALUES(7,161,'7:161','“และเมื่อถูกกล่าวแก่พวกเขาว่า จงอยู่ในเมือง นี้เถิด และจงบริโภคจากเมืองนั้น ณ ที่ใดก็ได้ที่พวกเจ้าประสงค์ และจงกล่าวว่า ฮิฏเฏาะฮ์ และจงเข้าประตู นั้นไปในสภาพผู้โน้มศรีษะลงด้วยความนอบน้อม เราก็จะอภัยโทษให้แก่พวกเจ้าซึ่งบรรดาความผิดของพวกเจ้า และเราจะเพิ่มพูนแก่บรรดาผู้กระทําความดี”');
INSERT INTO "king_fahad_thai" VALUES(7,162,'7:162','“แล้วบรรดาผู้อธรรมเหล่านั้นได้เปลี่ยนเอาคําพูดหนึ่ง ซึ่งมิใช่คําพูดที่ถูฏกล่าวแก่พวกเขาดังนั้นเราจึงได้ส่งการลงโทษจากฟากฟ้ามายังพวกเขาเนื่องจากที่พวกเขาละเมิด”');
INSERT INTO "king_fahad_thai" VALUES(7,163,'7:163','“และเจ้าจงถามพวกเขา ถึงเมืองที่เคยอยู่ใกล้ทะเล ขณที่พวกเขา ละเมิดในวันสับบะโต ทั้งนี้ขณะที่บรรดาปลาของพวกเขา มายังพวกเขาในวันสับบะโตของพวกเขาในสภาพลอยตัวให้เห็นบนผิวน้ํา และวันที่พวกเขาไม่ถือว่าเป็นวันสับบะโตนั้น ปลาเหล่านั้นหาได้มายังพวกเขาไม่ ในทํานองนั้นแหละเราจะทดสอบพวกเขา เนื่องด้วยการที่พวกเขาละเมิด');
INSERT INTO "king_fahad_thai" VALUES(7,164,'7:164','“และจงรําลึกขณะที่กลุ่มหนึ่ง ในพวกเขากล่าวว่า เพราะเหตุใดเล่าพวกท่านจึงตักเตือนกลุ่มชน ที่อัลลอฮ์จะทรงเป็นผู้ทําลายพวกเขาหรือเป็นผู้ลงโทษพวกเขาอย่างรุนแรง? พวกเขากล่าวว่า (การที่เราตักเตือนนั้น) เพื่อเป็นข้ออ้าง ต่อพระเจ้าของพวกเจ้า และเพื่อว่าพวกเขาจะได้ยําเกรง”');
INSERT INTO "king_fahad_thai" VALUES(7,165,'7:165','“ครั้นเมื่อพวกเขาลืม สิ่งที่พวกเขาถูกเตือนในสิ่งนั้น เราก็ช่วยเหลือบรรดาผู้ที่ห้ามปรามการทําชั่วให้รอดพ้น และได้จัดการแก่บรรดาผู้ที่อธรรมเหล่านั้น ด้วยการลงโทษอันรุนแรงเนื่องด้วยการที่พวกเขาละเมิด');
INSERT INTO "king_fahad_thai" VALUES(7,166,'7:166','“ครั้นเมื่อพวกเขาละเมิดสิ่งที่พวกเขาถูกห้ามในสิ่งนั้นแล้ว เราก็ประกาศิตแก่พวกเขาว่า พวกเจ้าจงเป็นสิ่งที่ถูกขับไล่ให้ห่างไกล');
INSERT INTO "king_fahad_thai" VALUES(7,167,'7:167','“และจงรําลึกขณะที่พระเจ้าของเจ้าได้แจ้งให้ทราบว่า แน่นอนพระองค์จะส่งมาให้มีอํานาจเหนือพวกเขาจนถึงวันกิยามะฮ์ ซึ่งผู่ที่จะบังคับขู่เข็ญพวกเขา ด้วยการทรมานอันร้ายแรงแท้จริงพระเจ้าของเจ้านั้น ทรงเป็นผู้รวดเร็วในการลงโทษและแท้จริงพระองค์นั้นคือผู้ทรงอภัยโทษ ผู้ทรงเอ็นดูเมตตา”');
INSERT INTO "king_fahad_thai" VALUES(7,168,'7:168','“และเราได้แยกพวกเขาออกเป็นกลุ่มๆ ในแผ่นดิน จากพวกเขานั้นมีคนดี และจากพวกเขานั้นมีอื่นจากนั้น และเราได้ทดสอบพวกเขาด้วยบรรดาสิ่งที่ดี และบรรดาสิ่งที่ชั่ว เพื่อว่าพวกเขาจะกลับมา ”');
INSERT INTO "king_fahad_thai" VALUES(7,169,'7:169','“แล้วได้มีกลุ่มชั่วกลุ่มหนึ่งสืบแทนหลังจากพวกเขา ซึ่งได้รับช่วงคัมภีร์ไว้ โดยที่พวกเขารับเอาสิ่งเล็ก ๆ น้อย ๆ แห่งโลกนี้ และกล่าวว่ามันจะถูกอภัยให้แก่เรา และหากมีสิ่งเล็ก ๆ น้อย ๆ เยี่ยงเดียวกันนั้นมายังพวกเขา พวกเขาก็รับเอามันอีก มิได้ถูกเอาแก่พวกเขาดอกหรือ ซึ่งข้อสัญญาแห่งคัมภีร์ว่า พวกเขาจะไม่กล่าวพาดพิงเกี่ยวกับอัลลอฮ์ นอกจากความจริงเท่านั้น และพวกเขาก็ได้ศึกษาสิ่งที่อยู่ในคัมภีร์นั้น แล้ว และที่พํานักแห่งปรโลกนั้นคือสิ่งที่ดียิ่งสําหรับบรรดาผู้ที่ยําเกรงพวกเจ้าไม่ใช้ปัญญาดอกหรือ?”');
INSERT INTO "king_fahad_thai" VALUES(7,170,'7:170','“และบรรดาผู้ที่ยึดถือคัมภีร์ และดํารงไว้ซึ่งการละหมาดนั้น แท้จริงเราจะไม่ให้สูญไปซึ่งรางวัลของผู้ปรับปรุงแก้ไขทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(7,171,'7:171','“และจงรําลึกขณะที่เราได้ให้ภูเขาลูกนั้น ไหวตัว และถอนตัวขึ้นเหนือพวกเขา ประหนึ่งมันเป็นสิ่งที่ให้เงาร่ม กระนั้น และพวกเขาคิดว่ามันจะตกลงทับพวกเขา พวกเจ้าจงยึดเอาสิ่ง ที่เราได้ให้ไว้แก่พวกเจ้าด้วยความเข้มแข็ง และจงรําลึกถึงสิ่ง ที่มีอยู่ในนั้น หวังว่าพวกเจ้าจะเกรงกลัว”');
INSERT INTO "king_fahad_thai" VALUES(7,172,'7:172','“และจงรําลึกขณะที่พระเจ้าของเจ้าได้เอาจากลูกหลานของอาดัม ซึ่งลูก ๆ ของพวกเขาจากหลังของพวกเขา และให้พวกเขา ยืนยันแก่ตัวของเขาเอง(โดยตอบคําถามที่ว่า) ข้ามิใช่พระเจ้าของพวกเจ้าดอกหรือ? พวกเขากล่าวว่าใช่ขอรับ พวกข้าพระองค์ขอยืนยัน (มิฉันนั้น) พวกเจ้าจะกล่าวในวันกิยามะฮ์ว่า แท้จริงพวกข้าพระองค์ไม่รู้เรื่องเกี่ยวกับเรื่องนี้”');
INSERT INTO "king_fahad_thai" VALUES(7,173,'7:173','“หรือไม่ก็พวกเจ้าจะกล่าวว่า ที่จริงนั้นบรรพบุรุษของพวกข้าพระองค์ได้ให้ภาคีขึ้นมาก่อนและพวกเรเาป็นลูกหลานที่มาหลังจากพวกเขา แล้วพระองค์จะทรงทําลายพวกเรา เนื่องด้วยการกระทําของบรรดาผู้ที่ทําให้เสียกระนั้นหรือ ?”');
INSERT INTO "king_fahad_thai" VALUES(7,174,'7:174','“และในทํานองนั้นแหละเราจะแจกแจงโองการทั้งหลาย เพื่อว่าพวกเขาจะกลับมา ”');
INSERT INTO "king_fahad_thai" VALUES(7,175,'7:175','“และจงอ่านให้พวกเขา ฟัง ซึ่งข่าวของผู้ที่เราได้ให้บรรดาโองการของเราแก่เขา แล้วเขาได้ถอนตัวออกจากโองการเหล่านั้น แล้วชัยฏอนก็ติดตามเขา ดังนั้นเขาจึงอยู่ในหมู่ผู้หลงผิด”');
INSERT INTO "king_fahad_thai" VALUES(7,176,'7:176','“และหากเราประสงค์แล้ว แน่นอนเราก็ยกเขาขึ้นและ ด้วยบรรดาโองการเหล่านั้น แต่ทว่าเขาคงมั่นอยู่กับดิน และปฏิบัติตามความใคร่ใฝ่ต่ําของเขา ดังนั้นอุปมาของผู้นั้น จึงดั่งอุปไมยของสุนัขหากเจ้าขับไล่มัน มันก็จะหอบแลบลิ้นห้อยลง นั่นแหละคือ อุปมากลุ่มชนที่ปฏิเสธบรรดาโองการของเรา ดังนั้นเจ้าจงเล่าเรื่องราวเหล่านั้นเถิด เพื่อว่าพวกเขาจะไดใคร่ครวญ”');
INSERT INTO "king_fahad_thai" VALUES(7,177,'7:177','“เป็นตัวอย่างที่ชั่วช้าจริงๆ กลุ่มชนที่ปฏิเสธบรรดาโองการของเรา และก็ตัวของพวกเขานั้นเองพวกเขาอธรรมกันอยู่”');
INSERT INTO "king_fahad_thai" VALUES(7,178,'7:178','“ผู้ที่อัลลอฮ์ทรงแนะนํานั้น เขาก็เป็นผู้รับคําแนะนํา และผู้ที่พระองค์ทรงปล่อยให้หลงผิด นั้นชนเหล่านี้แหละพวกเขาคือผู้ที่ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(7,179,'7:179','”และแน่นอนเราได้บังเกิดสําหรับญฮันนัม ซึ่งมากมายจากญิน และมนุษย์ โดยที่พวกเขามีหัวใจซึ่งพวกเขาไม่ใช้มันทําความเข้าใจและพวกเขามีตา ซึ่งพวกเขาไม่ใช่มันมอง และพวกเขามีหู ซึ่งพวกเขาไม่ใช้มันฟังชนเหล่านี้แหละประหนึ่งปศุสัตว์ ใช่แต่เท่านั้น พวกเขาเป็นผู้หลงผิดยิ่งกว่า ชนเหล่านี้แหละ พวกเขาคือผู้ทีเผลอเรอ');
INSERT INTO "king_fahad_thai" VALUES(7,180,'7:180','“และอัลลอฮ์นั้นมีบรรดาพระนามอันสวยงาม ดังนั้นพวกเจ้าจงเรียกหาก พระองค์ด้วยพระนามเหล่านั้นเถิด และจงปล่อยบรรดาผู้ที่ทําให้เฉ ในบรรดาพระนามของพระองค์เถิด พวกเขานั้นจะถูกตอบแทนในสิ่งที่พวกเขากระทํา');
INSERT INTO "king_fahad_thai" VALUES(7,181,'7:181','“และส่วนหนึ่งจากผู้ที่เราได้บังเกิดนั้นคือ คณะหนึ่ง ซึ่งพวกเขาแนะนํา ด้วยความจริง และด้วยความจริงนั้น พวกเขาปฏิบัติ โดยเที่ยงธรรม”');
INSERT INTO "king_fahad_thai" VALUES(7,182,'7:182','“และบรรดาผู้ปฏิเสธบรรดาโองการของเรานั้น เราจะจัดการแก่พวกเขาเป็นขั้นตอน โดยที่พวกเขาไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(7,183,'7:183','“และข้าจะประวิงเวลา ให้แก่พวกเขาแท้จริงอุบาย ของข้านั้นแข็งแรงนัก');
INSERT INTO "king_fahad_thai" VALUES(7,184,'7:184','“และพวกเขามิได้ใคร่ครวญดอกหรือว่าที่สหายของพวกเขานั้นหาได้มีความบ้าใด ๆ ไม่ เขามิใช่ใครอื่นนอกจากผู้ตักเตือนที่ชัดแจ้งคนหนึ่งเท่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(7,185,'7:185','“และพวกเขามิได้มองดูในอํานาจทั้งหลายแห่งบรรดาชั้นฟ้า และแผ่นดิน และสิ่งใดสิ่งหนึ่งที่อัลลอฮ์ได้ทรงบังเกิดขึ้นดอกหรือ ? และแท้จริงอาจเป็นไปได้ว่า กําหนดเวลาแห่งความตายของพวกเขานั้นได้ใกล้มาแล้ว แล้วก็ถ้อยคําใดเล่าที่พวกเขาจะศรัทธากันหลังจากอัล-กุรอาน ”');
INSERT INTO "king_fahad_thai" VALUES(7,186,'7:186','“ผู้ใดที่อัลลอฮ์ทรงปล่อยให้หลงไปแล้ว ก็ไม่มีผู้แนะนําใด ๆ สําหรับเขา พระองค์จะทรงปล่อยพวกเขาให้ระเหเร่ร่อนอยู่ในการละเมิดของพวกเขา ”');
INSERT INTO "king_fahad_thai" VALUES(7,187,'7:187','“พวกเขาจะถามเจ้าถึงยามอวสาน (วันกิยามะฮ์) นั้นว่า เมื่อใดเล่ามันจะเกิดขึ้น ? จงกล่าวเถิดว่าแท้จริง ความรู้ในเรื่องนั้นอยู่ที่พระเจ้าของฉันเท่านั้นไม่มีใครจะเผยมันให้ทราบสําหรับเวลาของมัน ได้ นอกจากพระองค์เท่านั้น มันหนักอึ้ง อยู่ในบรรดาชั้นฟ้า และแผ่นดิน มันจะไม่มายังพวกเจ้า นอกจากโดยกระทันหัน พวกเขาถามเจ้ากันประหนึ่งว่าเจ้านั้นเป็นผู้ที่รู้ในเรื่องนั้น ดี จงกล่าวเถิดแท้จริงความรู้ในเรื่องนั้นอยู่ที่อัลลอฮ์เท่านั้น แต่ทว่ามนุษย์ส่วนมากไม่รู้”');
INSERT INTO "king_fahad_thai" VALUES(7,188,'7:188','“จงกล่าวเถิดว่า (มุอัมมัด) ว่าฉันไม่มีอํานาจที่จะครอบครองประโยชน์ใด ๆ และโทษใด ๆ ไว้เป็นกรรมสิทธิ์แก่ตัวของฉันได้ นอกจากสิ่งที่อัลลอฮ์ทรงประสงค์เท่านั้น และหากฉันเป็นผู้ที่รู้สิ่งเร้นลับแล้ว แน่นอนฉันก็ย่อมกอบโกยสิ่งที่ดีไว้ มากมายแล้ว และความชั่วร้ายก็ย่อมไม่ต้องฉันได้ ฉันมิใช้ใครอื่น นอกจากผู้ตักเตือนและผู้ประกาศข่าวดีแก่กลุ่มชนที่ศรัทธาเท่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(7,189,'7:189','“พระองค์นั้นคือผู้ที่ได้ทรงบังเกิดพวกเจ้าจากชีวิตเดียว และได้ทรงให้มีขึ้นจากชีวิตนั้น ซึ่งคู่ครอง ของชีวิตนั้นเพื่อชีวิตนั้นจะได้มีความสงบสุขกับนาง ครั้นเมื่อชีวิตนั้นได้สมสู่นาง นางก็อุ้มครรภ์อย่างเบา ๆ แล้วนางก็ผ่านมันไป ครั้นเมื่อนางอุ้มครรภ์หนัก เขาทั้งสอง ก็วิงวอนต่ออัลลอฮ์ผู้เป็นพระเจ้าของเขาทั้งสองว่า ถ้าหากพระองค์ทรงประทานบุตรที่สมบูรณ์ให้ข้าพระองค์แล้ว แน่นอนข้าพระองค์ก็อยู่ในหมู่ผู้ขอบคุณ”');
INSERT INTO "king_fahad_thai" VALUES(7,190,'7:190','“ครั้นเมื่อพระองค์ได้ทรงประทานให้เขาทั้งสองซึ่งบุตรที่สมบูรณ์ เขาทั้งสอง ก็ให้มีบรรดาภาคีขึ้นแก่พระองค์ ในสิ่งที่พระองค์ทรงประทานให้แก่เขาทั้งสอง อัลลอฮ์นั้นทรงสูงเกินกว่าที่พวกเขาให้มีภาคีขึ้น”');
INSERT INTO "king_fahad_thai" VALUES(7,191,'7:191','“พวกเขา จะให้สิ่งที่บังเกิดอันใดมีหุ้นส่วน (กับพระองค์) ทั้ง ๆ ที่พวกมันถูกบังเกิดขึ้น กระนั้นหรือ ?”');
INSERT INTO "king_fahad_thai" VALUES(7,192,'7:192','และพวกมัน ไม่สามารถให้ความช่วยเหลือใด ๆ แก่พวกเขา และทั้งไม่สามารถช่วยเหลือตัวของพวกมัน เองด้วย');
INSERT INTO "king_fahad_thai" VALUES(7,193,'7:193','“และหากพวกเจ้าเชิญชวนพวกเขา ไปสู่คําแนะนําที่ถูกต้อง พวกเขาก็จะไม่ปฏิบัติตาม พวกเจ้า ย่อมมีผลเท่ากันแก่พวกเจ้า พวกเจ้าจะเชิญชวนพวกเขา หรือพวกเจ้าจะนิ่งเฉยอยู่ก็ตาม”');
INSERT INTO "king_fahad_thai" VALUES(7,194,'7:194','“แท้จริงบรรดาผู้ที่พวกเจ้าวิงวอนขอ อื่นจากอัลลอฮ์นั้นคือ ผู้ที่เป็นบ่าว เยี่ยงพวกเจ้านั้นเอง จงวิงวอนขอต่อพวกเขาเถิด แล้วจงให้พวกเขาตอบรับพวกเจ้าด้วย หากพวกเจ้าเป็นผู้พูดจริง ”');
INSERT INTO "king_fahad_thai" VALUES(7,195,'7:195','“พวกมัน มีเท้าที่ใช้มันเดินกระนั้นหรือ ? หรือว่าพวกมันมีมีที่ใช้มันจัดการอย่างรุน แรง หรือว่าพวกมันมีตาที่ใช้มอง หรือว่าพวกมันมีหูที่ใช้มันฟัง จงกล่าวเถิด (มุฮัมมัด) ว่า พวกท่านจงวิงวอนขอต่อบรรดาภาคี ของพวกเจ้าเถิดแล้วจงวางอุบาย แก่ฉันด้วย จงอย่าได้ประวิงเวลาให้แก่ฉันเลย”');
INSERT INTO "king_fahad_thai" VALUES(7,196,'7:196','“แท้จริงผู้คุ้มครองฉันนั้นคือ อัลลอฮ์ผู้ทรงประทานคัมภีร์ลงมา และในขณะเดียวกันพระองค์ก็ทรงคุ้ครองบรรดาผู้ประพฤติดีทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(7,197,'7:197','“และบรรดาผู้ที่พวกเจ้าวิงวอนขอ อื่นจากพระองค์นั้น พวกมันไม่สามารถจะช่วยเหลือพวกเจ้าได้ และไม่สามารถช่วยเหลือตัวของพวกมันเองด้วย”');
INSERT INTO "king_fahad_thai" VALUES(7,198,'7:198','“และหากพวกเจ้าวิงวอนพวกมันให้ช่วยนําไปสู่คําแนะนําที่ถูกต้อง พวกมันก็ไม่ได้ยินและเจ้าจะเห็นพวกมันมองมายังเจ้า ทั้ง ๆ ที่พวกมันมองไม่เห็น”');
INSERT INTO "king_fahad_thai" VALUES(7,199,'7:199','“เจ้า (มุฮัมมัด) จงยึดถือไว้ซึ่งการอภัย และจงใช้ให้กระทําสิ่งที่ชอบ และจงผินหลัง ให้แก่ผู้โฉดเขลาทั้งหลายเถิด”');
INSERT INTO "king_fahad_thai" VALUES(7,200,'7:200','“และหากมีการยั่วยุใด ๆ จากชัยฏอนกําลังยั่วยุ เจ้าอยู่ ก็จงขอความคุ้มครองต่ออัลลอฮ์เถิด แท้จริงพระองค์เป็นผู้ทรงได้ยินผู้ทรงรอบรู้”');
INSERT INTO "king_fahad_thai" VALUES(7,201,'7:201','“แท้จริงบรรดาผู้ที่ยําเกรงนั้น เมื่อมีคําชี้นํา ใด ๆ จากชัยฏอนประสบแก่พวกเขา พวกเขาก็รําลึก ได้แล้วทันใดพวกเขาก็มองเห็น');
INSERT INTO "king_fahad_thai" VALUES(7,202,'7:202','“และพี่น้อง ของพวกมันนั้นจะช่วยเหลือพวกมันในการหลงผิด แล้วพวกเขาก็จะไม่ลดละ');
INSERT INTO "king_fahad_thai" VALUES(7,203,'7:203','“และเมื่อมิได้มีอายะฮ์ ได้มายังพวกเขา พวกเขาก็กล่าวว่า ไฉนเล่าท่านจึงไม่อุปโลกน์มันขึ้นเอง จงกล่าวเถิด (มุฮัมมัด) ว่า แท้จริงฉันจจะปฏิบัติตามเฉพาะสิ่งที่ถูกให้เป็นโองการแก่ฉันจากพระเจ้าของฉันเท่านั้น นี่ คือบรรดาหลักฐาน จากพระเจ้าของพวกเจ้า และ (นี้คือ) ข้อแนะนําและการเอ็นดูเมตตาแก่กลุ่มชนที่ศรัทธา”');
INSERT INTO "king_fahad_thai" VALUES(7,204,'7:204','“และเมื่ออัล-กุรอานถูกอ่านขึ้น ก็จงสดับฟังอัล-กุรอานนั้นเถิด และจงนิ่งเงียบ เพื่อว่าพวกเจ้าจะได้รับการเอ็นดูเมตตา”');
INSERT INTO "king_fahad_thai" VALUES(7,205,'7:205','“และเจ้า (มุฮัมมัด) จงรําลึกถึงพระเจ้าของเจ้าในใจของเจ้าด้วยความนอบน้อมและยําเกรงและโดยไม่ออกเสียงดัง ทั้งในเวลาเช้าและเย็นและจงอย่าอยู่ในหมู่ผู้ที่เผลเรอ');
INSERT INTO "king_fahad_thai" VALUES(7,206,'7:206','“แท้จริงบรรดา ผู้ที่อยู่ที่พระเจ้าของเจ้านั้น พวกเขาจะไม่หยิ่งต่อการเคารพสักการะพระองค์ และกล่าวให้ความบริสุทธิ์แก่พระองค์และแด่พระองค์เท่านั้น พวกเขากราบกรานกัน”');
INSERT INTO "king_fahad_thai" VALUES(8,1,'8:1','พวกเขาจะถามเจ้า เกี่ยวกับบรรดาทรัพย์สินเชลย "จงกล่าวเถิด (มุฮัมมัด) ว่า บรรดาทรัพย์สินเชลยนั้นเป็นสิทธิ์ของอัลลอฮฺและของร่อซูล ดังนั้นพวกท่านจงยําเกรงอัลลอฮฺเถิด และจงปรับปรุงความสัมพันธ์ ระหว่างพวกท่านเถิดหากพวกท่านเป็นผู้ศรัทธา”');
INSERT INTO "king_fahad_thai" VALUES(8,2,'8:2','“แท้จริงบรรดาผู้ที่ศรัทธานั้น คือ ผู้ที่เมื่ออัลลอฮฺถูกกล่าวขึ้นแล้ว หัวใจของพวกเขาก็หวั่นเกรง และเมื่อบรรดาโองการของพระองค์ถูกอ่านแก่พวกเขา โองการเหล่านั้นก็เพิ่มพูนความศรัทธาแก่พวกเขา และแด่พระเจ้าของพวกเขานั้น พวกเขามอบหมายกัน”');
INSERT INTO "king_fahad_thai" VALUES(8,3,'8:3','“คือบรรดาผู้ที่ดํารงไว้ซึ่งการละหมาดและส่วนหนึ่งจากสิ่งที่เราได้ให้เป็นปัจจัยยังชีพแก่พวกเขา พวกเขาก็บริจาค”');
INSERT INTO "king_fahad_thai" VALUES(8,4,'8:4','“ชนเหล่านี้แหละพวกเขาคือ ผู้ศรัทธาอย่างแท้จริง โดยที่พวกเขาจะได้รับหลายชั้น ณ พระเจ้าของพวกเขา และจะได้รับการอภัยโทษและปัจจัยยังชีพอันมากมาย ”');
INSERT INTO "king_fahad_thai" VALUES(8,5,'8:5','“เช่นเดียวกับที่พระเจ้าของเจ้าให้เจ้าออกไปจากบ้านของเจ้า เนื่องด้วยความจริง และแท้จริงกลุ่มหนึ่งจากบรรดาผู้ศรัทธานั้นรังเกียจ');
INSERT INTO "king_fahad_thai" VALUES(8,6,'8:6','“พวกเขาโต้เถียงกับเจ้าในความจริง หลังจากที่มันได้ประจักษ์ขึ้น ประหนึ่งว่าพวกเขาดูถูกต้อนไปสู่ความตายโดยที่พวกเขากําลังมองดูกันอยู่ ”');
INSERT INTO "king_fahad_thai" VALUES(8,7,'8:7','“และจงรําลึกขณะที่อัลลอฮฺได้ทรงสัญญาไว้แก่พวกเจ้า ซึ่งหนึ่งในสองกลุ่ม ว่า มันเป็นของพวกเจ้า และพวกเจ้าชอบที่จะให้กลุ่มที่ไม่มีกําลังอาวุธนั้นเป็นของพวกเจ้า แต่อัลลอฮฺทรงต้องการให้ความจริง ประจักษ์เป็นจริงขึ้นด้วยพจนารถของพระองค์ และจะทรงตัดขาดซึ่งคนสุดท้าย ของผู้ปฏิเสธทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(8,8,'8:8','เพื่อพระองค์จะทรงให้สิ่งที่เป็นจริง ได้ประจักษ์เป็นความจริง และให้สิ่งเท็จ ได้ประจักษ์เป็นสิ่งเท็จ และแม้ว่าบรรดาผู้กระทําความผิด ไม่พอใจก็ตาม”');
INSERT INTO "king_fahad_thai" VALUES(8,9,'8:9','“จงรําลึกขณะที่พวกเจ้าขอความช่วยเหลือยามคับขนต่อพระเจ้าของเจ้า แล้วพระองค์ก็ได้ทรงรับสนองแก่พวกเจ้าว่า แท้จริงข้าจะช่วยพวกเจ้าด้วยมลาอิกะฮ์หนึ่งพันตน โดยทยอยกันลงมา”');
INSERT INTO "king_fahad_thai" VALUES(8,10,'8:10','“และอัลลอฮฺนั้นมิได้ทรงให้มัน มีขึ้นนอกจากเป็นข่าวดี เท่านั้น และเพื่อว่าหัวใจของพวกเจ้าจะได้สงบขึ้นด้วยสิ่งนั้น และไม่มีการช่วยเหลือ นอกจากที่มาจากที่อัลลอฮฺเท่านั้น แท้จริงอัลลอฮฺเป็นผู้ทรงเดชานุภาพ ผู้ทรงปรีชาญาณ”');
INSERT INTO "king_fahad_thai" VALUES(8,11,'8:11','“จงรําลึกขณะที่พระองค์ทรงให้มีการงีบหลับครอบงําพวกเจ้า ด้วยความปลอดภัยจากพระองค์ และทรงให้น้ําลงมาแก่พวกเจ้า จากฟากฟ้าเพื่อทรงชําระพวกเจ้า ด้วยน้ํานั้น และทรงให้หมดไปจากพวกเจ้าซึ่งความโสมม ของชัยฏอน และเพื่อที่จะทรงผูกหัวใจ ของพวกเจ้า และทรงให้เท้ามั่นคง ด้วยน้ํานั้น”');
INSERT INTO "king_fahad_thai" VALUES(8,12,'8:12','“จงรําลึกขณะที่พระเจ้าของเจ้าประทานโองการแก่มลาอิกะฮ์ ว่า แท้จริงข้านั้นร่วมอยู่กับพวกเจ้าด้วย ดังนั้นพวกเจ้าจงทําให้บรรดาผู้ศรัทธามั่นคง เถิด ข้าจะโยน ความกลัวเข้าไปในหัวใจของบรรดาผู้ที่ปฏิเสธศรัทธา แล้วพวกเจ้าจงฟันลงบนก้านคอ และจงฟันทุก ๆ ส่วนปลายของนิ้วมือ จากพวกเขา”');
INSERT INTO "king_fahad_thai" VALUES(8,13,'8:13','“นั่นก็เพราะว่าพวกเขาฝ่าฝืนและต่อต้านอัลลอฮฺ และร่อซูลของพระองค์ และผู้ใดฝ่าฝืนและต่อต้านอัลลอฮฺ และร่อซูลของพระองค์แล้ว แท้จริงอัลลอฮฺนั้นเป็นผู้ทรงรุนแรงในการลงโทษ”');
INSERT INTO "king_fahad_thai" VALUES(8,14,'8:14','“นั่นแหละ พวกเจ้าจงลิ้มรสมันเถิด และแท้จริงสําหรับบรรดาผู้ปฏิเสธศรัทธานั้น คือ การลงโทษแห่งไฟนรก”');
INSERT INTO "king_fahad_thai" VALUES(8,15,'8:15','“บรรดาผู้ศรัทธาทั้งหลาย ! เมื่อพวกเจ้าพบบรรดาผู้ที่ปฏิเสธศรัทธาเคลื่อน มา พวกเจ้าจงอย่าหันหลังหนีพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(8,16,'8:16','“และใครที่หัสหลังของเขาหนีพวกเขา ในวันนั้น ยกเว้นผู้ที่เปลี่ยนที่เพื่อทําการสู้รบหรือผู้ที่ไปร่วมกับอีกกลุ่มหนึ่ง เท่านั้นแน่นอน เขาย่อมนําความกริ้วโกรธจากอัลลอฮฺกลับไป และที่อยู่ของเขานั้นคือญะฮันนัม และเป็นที่กลับไปที่เลวร้าย”');
INSERT INTO "king_fahad_thai" VALUES(8,17,'8:17','“พวกเจ้ามิได้ฆ่า พวกเขา แต่ทว่า อัลลอฮฺต่างหากที่ทรงฆ่าพวกเขา และเจ้ามิได้ขว้างดอก ขณะที่เจ้าขว้าง แต่ทว่าอัลลอฮฺต่างหากที่ข้างและเพื่อว่าพระองค์จะทรงทดสอบบรรดาผู้ศรัทธาอย่างดีงามจากพระองค์ แท้จริงอัลลอฮฺนั้นทรงได้ยินทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(8,18,'8:18','นั่นแหละ และแท้จริงอัลลอฮฺนั้น คือผู้ทําให้อ่อนแอ ซึ่งอุบายของผู้ปฏิเสธศรัทธาทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(8,19,'8:19','“หากพวกเจ้าขอให้มีการชี้ขาด แน่นอนการชี้ขาดนั้นก็ได้มายังพวกเจ้าแล้ว และถ้าหากพวกเจ้าหยุดยั้ง มันก็เป็นการดีแก่พวกเจ้า และหากพวกเจ้ากลับ (ทําการรุกรานอีก) เราก็จะกลับ (ช่วยเหลือให้พวกเจ้าแพ้อีก) พรรคพวกของเจ้านั้นไม่สามารถที่จะอํานวยประโยชน์ย่างใดให้แก่พวกเจ้า ได้เลย และแม้ว่าพวกเขาจะมากมายก็ตามและแท้จริงอัลลอฮฺนั้น อยู่ร่วม กับผู้ศรัทธาทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(8,20,'8:20','“บรรดาผู้ศรัทธาทั้งหลาย ! จงเชื่อฟังอัลลอฮฺ และร่อซูลของพระองค์เถิด และจงอย่าได้ผินหลังให้แก่เขา ขณะที่พวกเจ้าฟังกันอยู่”');
INSERT INTO "king_fahad_thai" VALUES(8,21,'8:21','“และพวกเจ้าจงอย่าเป็นเช่นบรรดาผู้ที่กล่าวว่า พวกเราได้ยิน แล้ว ในขณะเดียวกับพวกเขาหาได้ยินไม่”');
INSERT INTO "king_fahad_thai" VALUES(8,22,'8:22','“แท้จริงสัตว์ ที่ชั่วร้ายยิ่ง ณ อัลลอฮฺนั้น คือ ที่หูหนวก ที่เป็นใบ้ ซึ่งเป็นผู้ที่ไม่ใช้ปัญญา');
INSERT INTO "king_fahad_thai" VALUES(8,23,'8:23','“และหากอัลลอฮฺทรงรู้ว่าในตัวพวกเขานั้นมีความดี แน่นอนก็จะทรงให้พวกเขาได้ยิน และหากพระองค์ทรงให้พวกเขาได้ยิน แล้ว แน่นอนพวกเขาก็ผินหลังให้ โดยที่พวกเขานั้นเป็นผู้ผินหลังให้อยู่แล้ว”');
INSERT INTO "king_fahad_thai" VALUES(8,24,'8:24','“บรรดาผู้ศรัทธาทั้งหลาย ! จงตอบรับอัลลอฮฺ และร่อซูลเถิด เมื่อเขา ได้เชิญชวนพวกเจ้าสู่สิ่งที่ทําให้พวกเจ้ามีชีวิตชีวา ขึ้น และพึงรู้เถิดว่า แท้จริงอัลลอฮฺนั้นจะทรงกั้น ระหว่างบุคคลกับหัวใจของเขา และแท้จริงยังพระองค์นั้นพวกเจ้าจะถูกนํากลับไปชุมนุม”');
INSERT INTO "king_fahad_thai" VALUES(8,25,'8:25','“และพวกเจ้าจงระวังการลงโทษ ซึ่งมันจะไม่ประสบแก่บรรดาผู้อธรรมในหมู่พวกเจ้าโดยเฉพาะ เท่านั้น และพึงรู้เถิดว่า แท้จริงอัลลอฮฺนั้น เป็นผู้รุนแรงในการลงโทษ');
INSERT INTO "king_fahad_thai" VALUES(8,26,'8:26','“และพวกเจ้าจงรําลึก ขณะที่พวกเจ้ามีจํานวนน้อยซึ่งเป็นผู้อ่อนแอในแผ่นดินโดยที่พวกเจ้ากลัวว่าผู้คนจะโฉบเฉี่ยว พวกเจ้าไปแล้วพระองค์ได้ทรงให้พวกเจ้ามีที่พักพิง และได้ทรงสนับสนุนพวกเจ้าด้วยการช่วยเหลือของพระองค์ และได้ทรงประทานปัจจัยยังชีพแก่พวกเจ้าจากสิ่งที่ดี ๆ ทั้งหลาย เพื่อว่าพวกเจ้าจะได้ขอบคุณ”');
INSERT INTO "king_fahad_thai" VALUES(8,27,'8:27','“บรรดาผู้ศรัทธาทั้งหลาย ! จงอย่าทุจริต ต่ออัลลอฮฺ และร่อซูล และจงอย่าทุจริตต่อบรรดาของฝาก ของพวกเจ้า โดยที่พวกเจ้ารู้กันอยู่”');
INSERT INTO "king_fahad_thai" VALUES(8,28,'8:28','“และพึงรู้เถิดว่า แท้จริงทรัพย์สินของพวกเจ้า และลูก ๆ ของพวกเจ้านั้น เป็นสิ่งทดสอบ ชนิดหนึ่งเท่านั้น และแท้จริงอัลลอฮฺนั้น ณ พระองค์มีรางวัลอันใหญ่หลวง”');
INSERT INTO "king_fahad_thai" VALUES(8,29,'8:29','“บรรดาผู้ศรัทธาทั้งหลาย ! หากพวกเจ้ายําเกรงอัลลอฮฺ พระองค์ก็จะทรงให้มีแก่พวกเจ้าซึ่งสิ่งที่จําแนกระหว่างความจริงและความ เท็จและจะทรงลบล้างบรรดาความผิดของพวกเจ้าออกจากพวกเจ้าและจะทรงอภัยโทษให้แก่พวกเจ้าด้วยและอัลลอฮฺนั้นคือผู้ทรงมีบุญคุณอันใหญ่หลวง”');
INSERT INTO "king_fahad_thai" VALUES(8,30,'8:30','“และจงรําลึกขณะที่บรรดาผู้ปฏิเสธศรัทธาวางอุบายต่อเจ้า เพื่อกักขังเจ้า หรือฆ่าเจ้า หรือขับไล่เจ้าออก ไปและพวกเขาวางอุบายกันและอัลลอฮฺก็ทรงวางอุบาย และอัลลออฮ์นั้นทรงเป็นผู้เยี่ยมกว่าในหมู่ผู้วางอุบาย”');
INSERT INTO "king_fahad_thai" VALUES(8,31,'8:31','“และเมื่อบรรดาโองการของเราถูกอ่านให้แก่พวกเขาฟัง พวกเขาก็กล่าวว่า เราได้ยินแล้วหากเราประสงค์ แน่นอนเราก็พูดเช่นนี้แล้ว สิ่งนี้ใช่อื่นใดไม่ นอกจากถ้อยคําที่ถูกขีดเขียนไว้ของคนก่อน ๆ เท่านั้น ”');
INSERT INTO "king_fahad_thai" VALUES(8,32,'8:32','“และจงรําลึกขณะที่พวกเขากล่าวว่า ข้าแต่อัลลอฮฺหากปรากฏว่าสิ่งนี้ คือความจริงที่มาจากที่พระองค์แล้วไซร้ก็โปรดได้ทรงให้หินจากฟากฟ้าตกลงมาดังฝนแก่พวกเราเถิด หรือไม่ก็โปรดทรงนํามาแก่เรา ซึ่งการลงโทษอันเจ็บแสบ”');
INSERT INTO "king_fahad_thai" VALUES(8,33,'8:33','และพระองค์อัลลอฮฺจะไม่ทรงลงโทษพวกเขาขณะที่เจ้า อยู่ในพวกเขาและอัลลอฮฺจะไม่เป็นผู้ทรงลงโทษพวกเขาทั้งๆที่พวกเขาขออภัยโทษกัน');
INSERT INTO "king_fahad_thai" VALUES(8,34,'8:34','และมีอะไร แก่พวกเขากระนั้นหรือที่อัลลอฮฺจะไม่ทรงลงโทษพวกเขาทั้งๆที่พวกเขาขัดขวาง มิให้เข้ามัศยิดิลฮะรอม และพวกเขาก็มิใช่เป็นผู้ปกครองมัศยิดนั้นด้วยบรรดาผู้ปกครองมัศยิดนั้นใช่ใครอื่นไม่นอกจากบรรดาผู้ยําเกรงเท่านั้นแต่ทว่าส่วนมากของพวกเขาไม่รู้”คือมีสิ่งใด');
INSERT INTO "king_fahad_thai" VALUES(8,35,'8:35','“มิปรากฏว่า การละหมาดของพวกเขา ณ บ้านของอัลลอฮฺ นั้นเป็นอย่างอื่น นอกจากการเป่าเสียงหวัด และการตบมือเท่านั้น ดังนั้นการพวกเจ้า จงลิ้มการลงโทษเถิด เนื่องด้วยการที่พวกเจ้าปฏิเสธศรัทธา”');
INSERT INTO "king_fahad_thai" VALUES(8,36,'8:36','“แท้จริงบรรดาผู้ที่ปฏิเสธศรัทธานั้น พวกเขาจะบริจาคทรัพย์สินของพวกเขา เพื่อขัดขวาง(ผู้คน) ให้ออกจากจากการงานของอัลลอฮฺ แล้วพวกเขาก็จะบริจาคมันต่อไปภายหลังทรัพย์สินนั้นก็จะกลายเป็นความเสียใจ แก่พวกเขา แล้วพวกเขาก็จะได้รับความปราชัยด้วย และบรรดาผู้ที่ปฏิเสธศรัทธานั้นพวกเขาจะถูกต้อนไปสู่นรกญะฮันนัม”');
INSERT INTO "king_fahad_thai" VALUES(8,37,'8:37','“เพื่อที่อัลลอฮฺจะทรงแยกคนเลว ออกจากคนดี และจะทรงให้คนเลว ซึ่งบางส่วนของพวกเขาอยู่บน อีกบางส่วน โดยทรงสุมพวกเขาทั้งหมดไว้เป็นกอง แล้วพระองค์จะทรงให้พวกเขาอยู่ในนรกญะฮันนัม ชนเหล่านี้แหละพวกเขาคือผู้ที่ขาดทุน”');
INSERT INTO "king_fahad_thai" VALUES(8,38,'8:38','“จงกล่าวเถิด(มุฮัมมัด) แก่บรรดาผู้ที่ปฏิเสธศรัทธาว่า หากพวกเขาหยุดยั้ง สิ่งที่แล้วมาก็จะถูกอภัยให้แก่พวกเขาและหากพวกเขากลับ(ต่อต้าน)อีก แท้จริงนั้นแนวทางของคนก่อน ๆ นั้นได้ผ่านมา แล้ว”');
INSERT INTO "king_fahad_thai" VALUES(8,39,'8:39','“และพวกเจ้าจงสู้รบกับพวกเขา จนกว่าจะไม่มีการปฏิเสธศรัทธาใด ๆ ปรากฏขึ้น และการอิบาดะฮ์ ทุกชนิดนั้นจะต้องเป็นสิทธิ์ของอัลลอฮฺเท่านั้น ถ้าหากพวกเขาหยุดยั้ง แน่นอนอัลลอฮฺนั้นทรงเห็นในสิ่งที่พวกเขากระทํา”');
INSERT INTO "king_fahad_thai" VALUES(8,40,'8:40','“และหากพวกเขาผินหลังให้ ก็พึงรู้เถิดว่า แท้จริงอัลลอฮฺนั้น คือผู้ทรงคุ้มครองพวกเจ้า ผู้ทรงคุ้มครองที่ดีเลิศและผู้ทรงช่วยเหลือที่ดีเยี่ยม”');
INSERT INTO "king_fahad_thai" VALUES(8,41,'8:41','“และพึงรู้เถิดว่า แท้จริงสิ่งใดที่พวกเจ้าได้มาจากการทําศึก นั้น แน่นอนหนึ่งในห้าของมันเป็นของอัลลอฮฺ และเป็นขอวงร่อซูล และเป็นของญาติที่ใกล้ชิด และบรรดาเด็กกําพร้า และบรรดาผู้ขัดสน และผู้เดินทาง หากพวกเจ้าศรัทธาต่ออัลลอฮฺและสิ่ง ที่เราได้ลงมาแก่บ่าวของเราในวันหนึ่งแห่งการจําแนกระหว่างการศรัทธา และการปฏิเสธ คือวันที่สองฝ่าย เผชิญกัน และอัลลอฮฺนั้นเป็นผู้ทรงเดชานุภาพเหนือทุกสิ่งทุกอย่าง”');
INSERT INTO "king_fahad_thai" VALUES(8,42,'8:42','“จงรําลึกขณะที่พวกเจ้าอยู่ด้านหุบเขาที่ใกล้กว่า และพวกเขาอยู่ด้านหุบเขาที่ไกลกว่า และกองคาราวานนั้นอยู่ต่ํากว่าพวกเจ้า และถ้าหากพวกเจ้าต่างได้สัญญา กัน แน่นอนพวกเจ้าก็ย่อมขัดแย้งกัน แล้วในสัญญานั้นแต่ทว่าเพื่อที่อัลลอฮฺจะได้ทรงให้งานหนึ่ง เสร็จสิ้นไป ซึ่งงานนั้นได้ถูกกระทําไว้แล้ว เพื่อว่าผู้พินาศ จะได้พินาศลงโดยหลักฐานอันชัดแจ้ง และผู้มีชีวิตอยู่ จะได้มีชีวิตอยู่โดยหลักฐานอันชัดแจ้ง และแท้จริงอัลลอฮฺนั้นเป็นผู้ทรงได้ยินผู้ทรงรอบรู้”');
INSERT INTO "king_fahad_thai" VALUES(8,43,'8:43','“จงรําลึกขณะที่อัลลอฮฺทรงให้เจ้า เห็นพวกเขา มีจํานวนน้อยในความฝันของเจ้า และหากว่าพระองค์ทรงให้เจ้าเห็นพวกเขามีจํานวนมากแล้วไซร้ แน่นอนพวกเจ้า ก็ย่อมย่อท้อกันและขัดแย้งกันในกิจการนั้น แต่ทว่าอัลลอฮฺได้ทรงให้ปลอดภัย แท้จริงพระองค์นั้นคือ ผู้ทรงรอบรู้สิ่ง ที่อยู่ในทรวงอก”');
INSERT INTO "king_fahad_thai" VALUES(8,44,'8:44','“และจงรําลึกขณะที่พระองค์ทรงให้พวกเจ้าเห็นพวกเขามีจํานวนน้อยในสายตาของพวกเจ้าขณะที่พวกเจ้าได้เผชิญหน้ากัน และทรงให้พวกเจ้ามีจํานวนน้อยในสายตาของพวก เขา เพื่อที่อัลลอฮฺจะได้ทรงให้งานหนึ่ง เสร็จสิ้นไปซึ่งงานนั้นได้ถูกกระทําไว้แล้ว และยังอัลลอฮฺนั้นกิจการทั้งหลายจะถูกนํากลับไป”');
INSERT INTO "king_fahad_thai" VALUES(8,45,'8:45','“บรรดาผู้ศรัทธาทั้งหลาย! เมื่อพวกเจ้าพบกลุ่มใดกลุ่มหนึ่ง ก็จงยืนหยัด เถิด และจงรําลึกถึงอัลลอฮฺมาก ๆเพื่อว่าพวกเจ้าจะได้รับความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(8,46,'8:46','“และจงเชื่อฟังอัลลอฮฺ และร่อซูลของพระองค์เถิด และจงอย่าขัดแย้งกัน จําทําให้พวกเจ้าย่อท้อ และทําให้ความเข้มแข็งของพวกเจ้าหมดไป และจงอดทนเถิด แท้จริงอัลลอฮฺนั้นทรงอยู่กับผู้ที่อดทนทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(8,47,'8:47','“และจงอย่าเป็นเช่นบรรดาผู้ที่ออกจากหมู่บ้านของพวกเขา ไป ด้วยความหยิ่งผยอง และโอ้อวดผู้คน และขัดขวาง ให้เขวออกจากทางของอัลลอฮฺ และอัลลอฮฺนั้นทรงล้อม สิ่งที่พวกเขากระทํากันอยู่”');
INSERT INTO "king_fahad_thai" VALUES(8,48,'8:48','“และจงรําลึกขณะที่ชัยฏอน ได้ทําให้สวยงามแก่พวกเขา ซึ่งการงานของพวกเขา และมันได้กล่าวว่า วันนี้ไม่มีผู้ใดในหมู่มนุษย์ชนะพวกท่านได้ และแท้จริงนั้นฉันคือผู้ช่วยเหลือพวกท่านครั้นเมื่อทั้งสองฝ่าย ต่างมองเห็นกันแล้ว มันก็กลับส้นเท้าทั้งสองของมัน และกล่าวว่าแท้จริงฉันไม่เกี่ยวข้องกับพวกท่าน แท้จริงฉันกําลังเห็นสิ่ง ที่พวกท่านไม่เห็น แท้จริงฉันกลัวอัลลอฮฺ และอัลลอฮฺนั้นเป็นผู้ทรงรุนแรงในการลงโทษ”');
INSERT INTO "king_fahad_thai" VALUES(8,49,'8:49','“จงรําลึกขณะที่บรรดามุนาฟิก และบรรดาผู้ที่ในหัวใจของพวกเขามีโรค กล่าวว่า ที่ได้ลวงผู้คนเหล่า นี้นั้น คือศาสนาของพวกเขา และผู้ใดมอบหมายแด่อัลลอฮฺ แท้จริงอัลลอฮฺนั้นคือผู้ทรงเดชานุภาพผู้ทรงปรีชาญาณ”');
INSERT INTO "king_fahad_thai" VALUES(8,50,'8:50','“และหากว่าเจ้าเห็นขณะที่มลาอิกะฮ์เอาวิญญาณของบรรดาผู้ปฏิเสธศรัทธาอยู่นั้นพวกเขา จะตีใบหน้าของพวกเขา และหลังของพวกเขา และ(กล่าวว่า) พวกเจ้าจงลิ้มการลงโทษแห่งการเผาไหม้เถิด');
INSERT INTO "king_fahad_thai" VALUES(8,51,'8:51','“นั่นก็เนื่องจากสิ่งที่มือของพวกท่านได้ประกอบไว้ก่อน และแท้จริงอัลลอฮฺนั้นมิใช่ผู้อธรรมแก่บ่าวทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(8,52,'8:52','“เช่นเดียวกับสภาพแห่งวงศ์วานของฟิรอาวน์ และบรรดาผู้ที่ก่อนหน้าพวกเขา ซึ่งพวกเขาปฏิเสธศรัทธาต่อบรรดาโองการของอัลลอฮฺแล้วอัลลอฮฺก็ได้ทรงลงโทษพวกเขา เนื่องด้วยบรรดาความฟิดของพวกเขา แท้จริงอัลลอฮฺนั้นเป็นผู้ทรงพลังและผู้ทรงรุนแรงในการลงโทษ”');
INSERT INTO "king_fahad_thai" VALUES(8,53,'8:53','นั่นก็เพราะว่า อัลลอฮฺมิได้ทรงเป็นผู้เปลี่ยนแปลงความกรุณาใด ๆ ที่พระองค์ทรงประทานมันแก่กลุ่มชนหนึ่งกลุ่มชนใดจนกว่าพวกเขาจะได้เปลี่ยนแปลงสิ่งที่อยู่ในตัวของพวกเขาเอง และแท้จริงอัลลอฮฺนั้นเป็นผู้ทรงได้ยินผู้ทรงรอบรู้”');
INSERT INTO "king_fahad_thai" VALUES(8,54,'8:54','“เช่นเดียวกับสภาพแห่งวงศ์วานฟิรอาวน์และบรรดาผู้ก่อนหน้าพวกเขา ซึ่งพวกเขาปฏิเสธบรรดาโองการแห่งพระเจ้าของพวกเขา แล้วเราก็ได้ทําลายพวกเขา เนื่องด้วยความผิดของพวกเขา และเราได้ให้วงศ์วานฟิรอาวน์จมน้ําตาย และทั้งหมดนั้นพวกเขาเป็นผู้อธรรม”');
INSERT INTO "king_fahad_thai" VALUES(8,55,'8:55','“แท้จริงสัตว์โลกที่ชั่วร้ายยิ่ง ณ อัลลอฮฺนั้นคือบรรดาผู้ที่เนรคุณ ดังนั้นพวกเขาจึงไม่ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(8,56,'8:56','“คือบรรดาผู้ที่เจ้าได้ทําสัญญาไว้ในหมู่พวกเขา แล้วพวกเขาก็ทําลายสัญญาของพวกเขาในทุกครั้ง โดยที่พวกเขาหาเกรงกลัวไม่”');
INSERT INTO "king_fahad_thai" VALUES(8,57,'8:57','“ถ้าหากเจ้าจับพวกเขา ไว้ได้ในการรบก็จงขับไล่ผู้ที่อยู่ข้างหลัง พวกเขา ด้วยการลงโทษพวกเขา (ให้เป็นเยี่ยงอย่าง) เพื่อว่าพวกเขา จะได้สํานึก”');
INSERT INTO "king_fahad_thai" VALUES(8,58,'8:58','“และถ้าหากเจ้าเกรงว่าจะมีการทุจริต จากพวกหนึ่งพวกใด ก็จงโอน (สัญญา) กลับคืนแก่พวกเขาไปโดยตั้งอยู่บนความเท่าเทียม กัน แท้จริงอัลลอฮฺนั้นไม่ทรงชอบบรรดาผู้ที่ทุจริต”');
INSERT INTO "king_fahad_thai" VALUES(8,59,'8:59','“และบรรดาผู้ปฏิเสธศรัทธานั้นจงอย่าได้คิดเป็นอันขาดว่า พวกเขาได้หนีพ้นไป แล้วแท้จริงพวกเขาไม่ทําให้อัลลอฮฺหมดความสามารถ ได้”');
INSERT INTO "king_fahad_thai" VALUES(8,60,'8:60','“และพวกเจ้าจงเตรียมไว้สําหรับ(ป้องกัน)พวกเขา สิ่งที่พวกเจ้าสามารถ อันได้แก่กําลังอย่างหนึ่งอย่างใด และการผูกม้าไว้ โดยที่พวกเจ้าจะทําให้ศัตรูของอัลลอฮฺ และศัตรูของพวกเจ้าหวั่นแกรงด้วยสิ่ง นั้น และพวกอื่น ๆ อีก อื่นจากพวกเขา ซึ่งพวกเจ่ายังไม่รู้จักพวกเขา อัลลอฮฺทรงรู้จักพวกเขาดี และสิ่งที่พวกเข่าบริจาคในทางของอัลลอฮฺนั้นไม่ว่าจะเป็นสิ่งใดก็ตาม สิ่งนั้นจะถูกตอบแทนแก่พวกเจ้าโดยครบถ้วนโดยที่พวกเจ้าจะไม่ถูกอธรรม”');
INSERT INTO "king_fahad_thai" VALUES(8,61,'8:61','“และหากพวกเขาโอนอ่อนมาเพื่อการประนีประนอมแล้ว เจ้า ก็จงโอนอ่อนตามเพื่อการนั้น ด้วย และจงมอบหมายแต่อัลลอฮฺเถิด แท้จริงนั้นพระองค์คือผู้ทรงได้ยินทรงรอบรู้”');
INSERT INTO "king_fahad_thai" VALUES(8,62,'8:62','“และถ้าหากพวกเขาต้องการที่จะหลอกลวงเจ้า ก็แท้จริงอัลลอฮฺนั้นเป็นที่พอเพียงแก่เจ้าแล้ว พระองค์คือผู้ทีได้ทรงสนับสนุนเจ้าด้วยการช่วยเหลือของพระองค์ และด้วยผู้ศรัทธาทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(8,63,'8:63','“และได้ทรงให้สนิทสนมระหว่างหัวใจของพวกเขา หากเจ้าได้จ่ายสิ่งที่อยู่ในแผ่นดินทั้งหมด เจ้าก็ไม่สามารถให้สนิทสนมระหว่างหัวใจของพวกเขาได้ แต่ทว่าอัลลอฮฺนั้นได้ทรงให้สนิทสนมระหว่างพวกเขา และแท้จริงพระองค์นั้นคือผู้ทรงเดชานุภาพ ผู้ทรงปรีชาญาณ”');
INSERT INTO "king_fahad_thai" VALUES(8,64,'8:64','“โอ้ นะบี! อัลลอฮฺนั้นเป็นที่พอเพียงแก่เจ้า และแก่ผู้ที่ปฏิบัติตามเจ้าด้วย อันได้แก่ผู้ศรัทธาทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(8,65,'8:65','“โอ้ นะบี! จงปลุกใจผู้ศรัทธาทั้งหลาย ในการสู้รบเถิด หากปรากฏว่าในหมู่พวกเจ้ามียี่สิบคนที่อดทน ก็จะชนะสองร้อยคน และหากปรากฏว่าในหมู่พวกเจ้ามีร้อยคน ก็จะชนะพันคนในหมู่ผู้ที่ปฏิเสธศรัทธา เพราะพวกเขาเป็นพวกที่ไม่เข้าใจ');
INSERT INTO "king_fahad_thai" VALUES(8,66,'8:66','“บัดนี้อัลลอฮฺได้ทรงผ่อนผันแก่พวกเจ้าแล้ว และทรงรู้ว่า แท้จริงในหมู่พวกเจ้านั้นมีความอ่อนแอ ดังนั้นหากในหมู่พวกเจ้ามีร้อยคนที่อดทนก็จะชนะสองร้อยคน และหากในหมู่พวกเจ้ามีพันคนก็จะชนะสองพันคน ด้วยอนุมัติของอัลลอฮฺ และอัลลอฮฺนั้นทรงอยู่ร่วมกับผู้อดทนทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(8,67,'8:67','“ไม่บังควรแก่นะบีคนใดที่เขาจะมีเชลยศึกไว้ จนกว่าเขาจะได้ประหัตประหารอย่างมากมายเสียก่อนในแผ่นดิน พวกเจ้าต้องกรสิ่งเล็กน้อย แห่งโลกนี้ แต่อัลลอฮฺทรงต้องการปรโลก และอัลลอฮฺเป็นผู้ทรงเดชานุภาพผู้ทรงปรีชาญาณ”');
INSERT INTO "king_fahad_thai" VALUES(8,68,'8:68','“หากว่าไม่มีพระกําหนด จากอัลลอฮฺล่วงหน้าอยู่ก่อน แน่นอนการลงโทษอันมหันต์ก็ประสบแก่พวกเจ้าแล้ว เนื่องในสิ่งที่พวกเจ้าเอา ”');
INSERT INTO "king_fahad_thai" VALUES(8,69,'8:69','“ดังนั้น พวกเจ้าจงบริโภคสิ่งอนุมัติที่ดีจากสิ่งที่พวกเจ้าได้มาจากการทําศึก และพึงยําเกรงอัลลอฮฺเถิด แท้จริงอัลลอฮฺนั้นคือผู้ทรงอภัยโทษผู้ทรงเอ็นดูเมตตา');
INSERT INTO "king_fahad_thai" VALUES(8,70,'8:70','“โอ้ นะบี! จงกล่าวแก่ผู้ที่อยู่ในมือของพวกเจ้า จากบรรดาผู้เป็นเชลยศึกเถิดว่า หากอัลลอฮฺทรงรู้ว่ามีความดี ใดๆ ในหัวใจของพวกท่านแล้ว พระองค์ก็จะทรงประทานให้แก่พวกท่าน ซึ่งสิ่งที่ดียิ่งกว่าสิ่ง ที่ถูกเอามาจากพวกท่านและจอทรงอภัยโทษแก่พวกท่านด้วย และอัลลอฮฺนั้นเป็นผู้ทรงอภัยโทษเป็นผู้ทรงเอ็นดูเมตตา”');
INSERT INTO "king_fahad_thai" VALUES(8,71,'8:71','“และถ้าหากพวกเขาต้องการจะทุจริตต่อเจ้า ก็แท้จริงนั้นพวกเขาได้ทุจริตต่ออัลลอฮฺมาก่อนแล้ว แล้วพระองค์ก็ทรงให้สมารถ ชนะพวกเขาได้ และอัลลอฮฺเป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ”');
INSERT INTO "king_fahad_thai" VALUES(8,72,'8:72','“แท้จริงบรรดาผู้ศรัทธา และอพยพและต่อสู้ทั้งด้วยทรัพย์สมบัติของพวกเขา และชีวิตของพวกเขาในทางของอัลลอฮฺ และบรรดา ผู้ที่ให้พักอาศัย และช่วยเหลือนั้น ชนเหล่านี้แหละคือบางส่วนของพวกเขาย่อมเป็นผู้ช่วยเหลืออีกบางส่วน และบรรดาผู้ที่ศรัทธา และมิได้อพยพนั้นก็ไม่เป็นหน้าที่แก่พวกเจ้าแต่อย่างใดในการช่วยเลือพวกเขา จนกว่าพวกเขาจะอพยพ และถ้าหากเขาขอให้พวกเจ้าช่วยเหลือในเรื่องศาสนา ก็จําเป็นแก่พวกเจ้าซึ่งการช่วยเหลือนั้น นอกจากในการต่อต้าน พวกที่ระหว่างพวกเจ้ากับพวกเขามีสัญญากันอยู่ และอัลลอฮฺนั้นทรงเห็นในสิ่งที่พวกเจ้ากระทํากัน”');
INSERT INTO "king_fahad_thai" VALUES(8,73,'8:73','“และบรรดาผู้ที่ปฏิเสธศรัทธานั้น บางส่วนของพวกเขาย่อมเป็นผู้ช่วยเหลืออีกบางส่วน หากพวกเจ้าไม่ปฏิบัติในสิ่งนั้น แล้ว ความวุ้นวายและความเสียหายอันใหญ่หลวง ก็จะเกิดขึ้นในแผ่นดิน');
INSERT INTO "king_fahad_thai" VALUES(8,74,'8:74','“และบรรดาผู้ที่ศรัทธา และอพยพ และต่อสู้ในทางของอัลลอฮฺ และบรรดาผู้ที่ให้ที่พักอาศัย และช่วยเหลือนั้น ชนเหล่านี้แหละพวกเขาคือผู้ศรัทธาโดยแท้จริง ซึ่งพวกเขาจะได้รับการอภัยโทษ และเครื่องยังชีพอันมากมาย ”');
INSERT INTO "king_fahad_thai" VALUES(8,75,'8:75','“และบรรดาผู้ที่ได้ศรัทธาที่หลัง และได้อพยพ และต่อสู้ร่วมกับพวกเจ้านั้น ชนเหล่านี้แหละเป็นส่วนหึ่งของพวกเจ้า และบรรดาญาตินั้น บางส่วนของพวกเขาเป็นผู้สมควรต่ออีกบางส่วน ในคัมภีร์ของอัลลอฮฺ แท้จริงอัลลอฮฺนั้นทรงรอบรู้ในทุกสิ่งทุกอย่าง”');
INSERT INTO "king_fahad_thai" VALUES(9,1,'9:1','(นี้คือประกาศ) การพ้นข้อผูกพันธ์ใด ๆ จากอัลลอฮ์ และร่อซูลของพระองค์ แด่บรรดาผู้สักการะเจว็ด (มุชริกีน) ที่พวกเจ้า ได้ทําสัญญาไว้');
INSERT INTO "king_fahad_thai" VALUES(9,2,'9:2','“ดังนั้นพวกท่าน จงท่องเที่ยวไปในแผ่นดินสี่เดือน และพึงรู้เถิดว่า แท้จริงพวกท่านนั้นมิใช่ผู้ที่จะทําให้อัลลอฮ์หมดความสามารถ ก็หาไม่ และแท้จริงอัลลอฮ์จะทรงให้ผู้ปฏิเสธศรัทธาทั้งหลายอัปยศ');
INSERT INTO "king_fahad_thai" VALUES(9,3,'9:3','“และเป็นประกาศจากอัลลอฮ์ และร่อซูลของพระองค์ แด่ประชาชนทั้งหลายในวันฮัจญอันใหญ่ยิ่ง ว่าแท้จริงอัลลอฮ์นั้นทรงพ้นข้อผูกพันธ์ จากมุชริกทั้งหลาย และร่อซูลของพระองค์ก็พ้นข้อผูกพันธ์นั้นด้วย และหากพวกเจ้า สํานึกผิด และกลับตัว มันก็เป็นสิ่งดีแก่พวกเจ้า และหากพวกเจ้าผินหลังให้ก็พึงรู้เถิดว่า แท้จริงพวกเจ้านั้นมิใช่ผู้ที่จะทําให้อัลลอฮ์หมดความสามารถ ได้ และจงแจ้งข่าวดีแก่บรรดาผู้ปฏิเสธศรัทธาเหล่านั้นเถิดด้วยการลงโทษอันเจ็บแสบ”');
INSERT INTO "king_fahad_thai" VALUES(9,4,'9:4','“นอกจากบรรดาผู้สักการะเจว็ด (มุชริกีน) ที่พวกเจ้าได้ทําสัญญาไว้ แล้วพวกเขามิได้ผิดสัญญาแก่พวกเจ้าแต่อย่างใด และมิได้สนับสนุนผู้ใดต่อต้านพวกเจ้า ดังนั้นจะให้ครบถ้วนแก่พวกเขาซึ่งสัญญาของพวกเขาจนถึงกําหนดเวลาของพวกเขาเถิดแท้จริงอัลลอฮ์นั้นทรงชอบผู้ที่ยําเกรงทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(9,5,'9:5','“ครั้นเมื่อบรรดาเดือนต้องห้าม เหล่านั้นได้ผ่านพ้นไปแล้ว ก็จงประหัตประหารมุชริกเหล่านั้น ณ ที่ใดก็ตามที่พวกเจ้าพบพวกเขา และจงจับพวกเขาและจงล้อมพวกเขา และจงนั่งสอดส่องพวกเขาทุกจุดที่สองส่อง แต่ถ้าพวกเขาสําหนึกผิกกลับตัว และดํารงไว้ซึ่งการละหมาด และชําระซะกาต แล้วไซร้ ก็จงปล่อยพวกเขาไป แท้จริงอัลลอฮ์นั้นเป็นผู้ทรอภัยโทษผู้ทรงเอ็นดูเมตตา”');
INSERT INTO "king_fahad_thai" VALUES(9,6,'9:6','“และหากว่ามีคนใดในหมู่มุชริกได้ขอให้เจ้าคุ้มครอง ก็จงคุ้มครองเขาเถิด จนกว่าเขาจะได้ยินดํารัส ของอัลลอฮ์ แล้วจงส่งเขายังที่ปลอดภัย ของเขา นั่นก็เพราะว่าพวกเขาเป็นกลุ่มชนที่ไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(9,7,'9:7','“จะเป็นไปได้อย่างไรแก่มุชริกีนที่จะมีสัญญาใด ๆ ณ ที่อัลลอฮ์ และร่อซูลของพระองค์ได้ นอกจากบรรดาผู้ที่พวกเจ้าได้ทําสัญญาไว้ที่ อัล-มัศยิดดิลฮะรอม เท่านั้น ดังนั้นตราบใดที่พวกเขาเที่ยงธรรมต่อพวกเจ้า ก็จงเที่ยงธรรมต่อพวกเขา แท้จริงอัลลอฮ์นั้นทรงชอบบรรดาผู้ทีความยําเกรง”');
INSERT INTO "king_fahad_thai" VALUES(9,8,'9:8','“(จะมีสัญญาใด) อย่างไรเล่า ? และหากพวกเขามีชัยชนะเหนือพวกเจ้า พวกเขาก็ไม่คํานึงถึงเครือญาติ และพันธะสัญญาใด ๆ ในหมู่พวกเจ้า พวกเขาทําให้พวกเจ้าพึงพอใจด้วยลมปากของพวกเขาเท่านั้น โดยที่หัวใจของพวกเขาปฏิเสธ และส่วนมากของพวกเขานั้นเป็นผู้ละเมิด”');
INSERT INTO "king_fahad_thai" VALUES(9,9,'9:9','“พวกเขาได้เอาบรรดาโองการของอัลลอฮ์แลกเปลี่ยนกับราคาอันเล็กน้อย แล้วก็ขัดขวาง(ผู้คน) ให้ออกจากทางของอัลลอฮ์ แท้จริงพวกเขานั้น สิ่งที่พวกเขาทําอยู่ช่างชั่วช้าจริง ๆ “');
INSERT INTO "king_fahad_thai" VALUES(9,10,'9:10','“พวกเขาจะไม่คํานึงถึงเครือญาติและพันธะสัญญาในผู้ศรัทธาคนหนึ่งคนใด และชนเหล่านี้แหละพวกเขาคือผู้ละเมิด”');
INSERT INTO "king_fahad_thai" VALUES(9,11,'9:11','“แล้วหากพวกเขาสํานึกผิดกลับตัว และดํารงไว้ซึ่งการละหมาด และชําระซะกาตแล้วไซร้ก็เป็นพี่น้องของพวกเจ้าในศาสนา และเราจะแจกแจงบรรดาโองการไว้แก่กลุ่มชนที่รู้ ”');
INSERT INTO "king_fahad_thai" VALUES(9,12,'9:12','“และถ้าหากพวกเขาทําลายคํามั่นสัญญาของพวกเขา หลังจากที่พวกเขาได้ทําสัญญา ไว้ และใส่ร้ายในศาสนา ของพวกเจ้าแล้วไซร้ ก็จงต่อสู้บรรดาผู้นําแห่งการปฏิเสธศรัทธา เถิด แท้จริงพวกเขานั้นหาได้มีคํามั่นสัญญาใด ๆ แก่พวกเขาไม่เพื่อว่าพวกเขาจะหยุดยั้ง”');
INSERT INTO "king_fahad_thai" VALUES(9,13,'9:13','“พวกเจ้าจะไม่ต่อสู้กระนั้นหรือ ซึ่งกลุ่มชนที่ทําลายคํามั่นสัญญาของพวกเขา และมุ่งขับไล่ร่อซูลให้ออกไป ทั้ง ๆ ที่พวกเขาได้เริ่มปฏิบัติแก่พวกเจ้าก่อนเป็นครั้งแรก พวกเจ้ากลัวพวกเขากระนั้นหรือ ? อัลลอฮ์ต่างหากเล่า คือผู้ที่สมควรแก่การที่พวกเจ้าจะกลัวหากพวกเจ้าเป็นผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(9,14,'9:14','“พวกเจ้าจงต่อสู้พวกเขาเถิด อัลลอฮ์ จะได้ทรงลงโทษพวกเขาด้วยมือของพวกเจ้า และจะได้ทรงหยามพวกเขา และจะได้ทรงช่วยเหลือพวกเจ้าให้ได้รับชัยชนะเหนือพวกเขา และจะได้ทรงบําบัด หัวอกของกลุ่มชนที่ศรัทธาทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(9,15,'9:15','“และจะได้ทรงให้หมดไปซึ่งความกริ้วโกรธแห่งหัวใจของพวกเขา และอัลลอฮ์นั้นจะทรงอภัยโทษแก่ผู้ที่พระองค์ทรงประสงค์ และอัลลอฮ์ คือผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ”');
INSERT INTO "king_fahad_thai" VALUES(9,16,'9:16','“หรือพวกเจ้าคิดว้าพวกเจ้าจะถูกปล่อยไว้ โดยที่อัลลอฮ์ยังมิได้ทรงรู้ บรรดาผู้ที่ต่อสู้ในหมู่พวกเจ้า และมิได้ยึดถือเพื่อนสนิท อื่นจากอัลลอฮ์ และร่อซูลของพระองค์ และอื่นจากผู้ศรัทธาทั้งหลาย และอัลลอฮ์นั้นทรงรอบรู้อย่างละเอียดในสิ่งที่พวกเจ้ากระทํา”');
INSERT INTO "king_fahad_thai" VALUES(9,17,'9:17','“ไม่บังควรแก่มุชริกทั้งหลายที่จะบูรณะ บรรดามัศยิดของอัลลอฮ์ ในฐานะที่พวกเขายืนยันแก่ตัวของพวกเขาเองแล้ว ซึ่งการปฏิเสธศรัทธา ชนเหล่านี้แหละบรรดาการงานของพวกเขาไร้ผล และในนรกนั้นพวกเขาจะอยู่ตลอดกาล”');
INSERT INTO "king_fahad_thai" VALUES(9,18,'9:18','“แท้จริงที่จะบูรณะบรรดามัศยิดของอัลลอฮ์นั้นคือผู้ที่ศรัทธาต่ออัลลอฮ์ และวันปรโลก และได้ดํารงไว้ซึ่งการละหมาด และชําระซะกาต และเขามิได้ยําเกรงนอกจากอัลลอฮ์เท่านั้น ดังนั้นจึงหวังได้ว่า ชนเหล่านี้แหละจะเป็นผู้อยู่ในหมู่ผู้รับคําแนะนํา');
INSERT INTO "king_fahad_thai" VALUES(9,19,'9:19','“พวกเจ้าได้ถือเอาการให้น้ําดื่ม แก่ผู้ประกอบพิธีฮัจญ์ และการบูรณะมัศยิดอัล-ฮะรอม ดั่งผู้ที่ศรัทธาต่ออัลลอฮ์ และวันปรโลก และต่อสู้ในทางของอัลลอฮ์กระนั้นหรือ ? เขาเหล่านั้นย่อมไม่เท่าเทียมกัน ณ ที่อัลลอฮ์ และอัลลอฮ์นั้นจะไม่ทรงแนะนํากลุ่มชนที่เป็นผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(9,20,'9:20','“บรรดาผู้ที่ศรัทธา และอพยพและต่อสู้ในทางของอัลลอฮ์ทั้งด้วยทรัพย์สมบัติของพวกเขาและชีวิตของพวกเขานั้นย่อมเป็นผู้มีระดับชั้นยิ่งใหญ่กว่า ณ ที่อัลลอฮ์ และชนเหล่านี้แหละพวกเขาคือผู้มีชัยชนะ”');
INSERT INTO "king_fahad_thai" VALUES(9,21,'9:21','“พระเจ้าของพวกเขาทรงแจ้งข่าวดีแก่พวกเขาด้วยกความกรุณาเมตตาจากพระองค์ และด้วยความปิติยินดี และบรรดาสวนสวรรค์ ด้วยซึ่งในสวนสวรรค์เหล่านั้น พวกเขาจะได้รับสิ่งอํานวยความสุขอันจีรังยั่งยืน');
INSERT INTO "king_fahad_thai" VALUES(9,22,'9:22','“โดยที่พวกเขาจะพํานักอยู่ในสวนสวรรค์เหล่านั้นตลอดกาล แท้จริงอัลลอฮ์นั้น ณ ที่พระองค์มีรางวัลอันยิ่งใหญ่”');
INSERT INTO "king_fahad_thai" VALUES(9,23,'9:23','“บรรดาผู้ศรัทธาทั้งหลาย ! จงอย่าได้ถือเอาบิดาของพวกเจ้า และพี่น้องของพวกเจ้าเป็นมิตร หากพวกเขาชอบการปฏิเสธศรัทธา เหนือการอีมาน และผู้ใดในหมู่พวกเจ้าให้พวกเขาเป็นมิตรแล้ว ชนเหล่านี้แหละพวกเขาคือผู้อธรรม”');
INSERT INTO "king_fahad_thai" VALUES(9,24,'9:24','“จงกล่าวเถิด (มุฮัมมัด) ว่า หากบรรดาบิดาของพวกเจ้า และบรรดาลูก ๆ ของพวกเจ้า และบรรดาพี่น้องของพวกเจ้า และบรรดาคู่ครองของพวกเจ้า และบรรดาญาติของพวกเจ้า และบรรดาทรัพย์สมบัติที่พวกเจ้าแสวงหาไว้ และสินค้าที่พวกเจ้ากลัวว่าจะจําหน่ายมันไม่ออก และบรรดาที่อยู่อาศัยที่พวกเจ้าพึงพอใจมันนั้น เป็นที่รักใคร่แก่พวกเจ้ายิ่งกว่าอัลลอฮฺ และร่อซูลของพระองค์ และการต่อสู้ในทางของพระองค์แล้วไซร้ ก็จงรอคอยกันเถิดจนกว่าอัลลอฮฺจะทรงนํามาซึ่งกําลัง ของพระองค์ และอัลลอฮฺนั้นจะไม่ทรงนําทางแก่กลุ่มชนที่ละเมิด"');
INSERT INTO "king_fahad_thai" VALUES(9,25,'9:25','“แท้จริงอัลลอฮ์ได้ทรงช่วยเหลือพวกเจ้าแล้วในสนามรบอันมากมาย และในวันแห่งสงครามฮุนัยน์ ด้วย ขณะที่การมีจํานวนมากของพวกเจ้าทําให้พวกเจ้าพึงใจ แล้วมันก็มิได้อํานวยประโยชน์แก่พวกเจ้าแต่อย่างใด และแผ่นดินก็แคบแพวกเจ้า ทั้ง ๆ ที่มันกว้างอยู่ แล้วพวกเจ้าก็หันหลังหนี”');
INSERT INTO "king_fahad_thai" VALUES(9,26,'9:26','“และอัลลอฮ์ก็ได้ทรงประทานลงมาซึ่งความสงบใจจากพระองค์แก่ร่อซูลของพระองค์และแก่บรรดาผู้ศรัทธาเหล่านั้น และได้ทรงให้ไพร่พลลง มา ซึ่งพวกเจ้าไม่เห็นพวกเขา และได้ทรงลงโทษบรรดาผู้ปฏิเสธศรัทธาเหล่านั้น และนั่นคือการตอบแทนแก่บรรดาผู้ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(9,27,'9:27','“และพระองค์ก็ทรงอภัยโทษหลังจากนั้นแก่ผู้ที่พระองค์ทรงประสงค์ และอัลลอฮ์นั้นเป็นผู้ทรงอภัยโทษผู้ทรงเอ็นดูเมตตา”');
INSERT INTO "king_fahad_thai" VALUES(9,28,'9:28','“บรรดาผู้ศรัทธาทั้งหลาย ! แท้จริงบรรดามุชริกนั้นโสมม ดังนั้นพวกเขาจงอย่าเข้าใกล้อัล-มัศยิดิลฮะรอม หลังจากปีของพวกเขา นี้ และหากพวกเจ้ากลัวความยากจน อัลลอฮ์ก็จะทรงให้พวกเจ้ามั่งมี จากความกรุณาของพระองค์ หากพระองค์ทรงประสงค์แท้จริงอัลลอฮ์เป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ”');
INSERT INTO "king_fahad_thai" VALUES(9,29,'9:29','“พวกเจ้าจงต่อสู้บรรดาผู้ที่ไม่ศรัทธาต่ออัลลอฮ์และต่อวันปรโลก และไม่งดเว้นสิ่งที่อัลลอฮ์และร่อซูล ห้ามไว้ และไม่ปฏิบัติตามศาสนาแห่งความสัจจะ อันได้แก่บรรดาผู้ที่ได้รับคัมภีร์ จนกว่าพวกเขาจะจ่ายอัล-ญิซยะฮ์ จากมือของพวกเขา เอง ในสภาพที่พวกเขาเป็นผู้ต่ําต้อย”');
INSERT INTO "king_fahad_thai" VALUES(9,30,'9:30','“และชาวยิวได้กล่าวว่า อุซัยร์ เป็นบุตรของอัลลอฮ์ และชาวคริสต์ได้กล่าวว่า อัล-มะซีห์ เป็นบุตรของอัลลอฮ์ นั่นคือถ้อยคําที่พวกเขากล่าวขึ้นด้วยปากของพวกเขาเอง ซึ่งคล้ายกับถ้อยคําของบรรดาผู้ที่ได้ปฏิเสธการศรัทธามาก่อน ขออัลลอฮ์ทรงละอ์นัต พวกเขาด้วยเถิด พวกเขาถูกหันเห ไปได้อย่างไร?');
INSERT INTO "king_fahad_thai" VALUES(9,31,'9:31','“พวกเขาได้ยึดเอาบรรดานักปราชญ์ของพวกเขา และบรรดาบาดหลวงของพวกเขาเป็นพระเจ้า อื่นจากอัลลอฮ์ และยึดเอาอัล-มะซีห์บุตรของมัรยัมเป็นพระเจ้าด้วย ทั้ง ๆที่พวกเขามิได้ถูกใช้นอกจากเพื่อเคารพสักการะผู้ทีสมควรได้รับการเคารพสักการะ แต่เพียงองค์เดียว ซึ่งไม่มีผู้ใดควรได้รับการเคารพสักการะนอกจากพระองค์เท่านั้น พระองค์ทรงบริสุทธิ์จากสิ่งที่พวกเขาให้มีภาคีขึ้น”');
INSERT INTO "king_fahad_thai" VALUES(9,32,'9:32','“พวกเขาต้องการเพื่อจะดับแสงสว่างของอัลลอฮ์ด้วยปากของพวกเขา และอัลลอฮ์นั้นไม่ทรงยินยอม นอกจากจะทรงให้แสงสว่างของพระองค์สมบูรณ์เท่านั้น และแม้ว่าบรรดาผู้ปฏิเสธศรัทธาจะชิงชังก็ตาม');
INSERT INTO "king_fahad_thai" VALUES(9,33,'9:33','“พระองค์นั้นคือผู้ที่ได้ส่งร่อซูลของพระองค์มาพร้อมด้วยคําแนะนํา และศาสนาแห่งสัจจะ เพื่อที่จะทรงให้ศาสนาแห่งสัจจะนั้นประจักษ์ เหนือศาสนาทุกศาสนา และแม้ว่าบรรดามุชริกจะชิงชังก็ตาม”');
INSERT INTO "king_fahad_thai" VALUES(9,34,'9:34','“บรรดาผู้ศรัทธาทั้งหลาย ! แท้จริงจํานวนมากมายจากบรรดานักปราชญ์ และบาดหลวงนั้นกินทรัพย์ของประชาชนโดยไม่ชอบ และขัดขวาง(ผู้คน) ให้ออกจากทางของอัลลอฮ์และบรรดาผู้ที่สะสมทองและเงิน และไม่จ่ายมันในทางของอัลลอฮ์ นั้น จงแจ้งข่าวดีแก่พวกเขาเถิด ด้วยการลงโทษอันเจ็บปวด”');
INSERT INTO "king_fahad_thai" VALUES(9,35,'9:35','“วันที่มัน จะถูกเผาไฟนรกแห่งญะฮันนัม แล้วหน้าผากของพวกเขา และสีข้างของพวกเขา และหลังของพวกเขาจะถูกนาบด้วยมัน นี้แหละ คือสิ่งที่พวกเจ้าได้สะสมไว้ เพื่อ ตัวของพวกเจ้าเอง ดังนั้นจงลิ้มรสสิ่งที่พวกเจ้าสะสมไว้เถิด”');
INSERT INTO "king_fahad_thai" VALUES(9,36,'9:36','“แท้จริงจํานวนเดือน ณ อัลลอฮ์นั้นมีสิบสองเดือน ในคัมภีร์ของอัลลอฮ์ตั้งแต่วันที่พระองค์ทรงสร้างบรรดาชั้นฟ้าและแผ่นดินจากเดือนเหล่านั้นมีสี่เดือน ซึ่งเป็นเดือนที่ต้องห้าม นั่นคือบัญญัติอันเที่ยงตรง ดังนั้นพวกเจ้าจงอย่าอธรรม แก่ตัวของพวกเจ้าเองในเดือนเหล่านั้นและจงต่อสู้บรรดามุชริกทั้งหมด เช่นเดียวกับที่พวกเขากําลังต่อสู้พวกเจ้าทั้งหมด และพึงรู้เถิดว่า แท้จริงอัลลอฮ์นั้นอยู่ร่วมกับบรรดาผู้ที่ยําเกรง”');
INSERT INTO "king_fahad_thai" VALUES(9,37,'9:37','“แท้จริงการเลื่อนเดือนที่ต้องห้ามให้ล่าช้า ไปนั้นเป็นการเพิ่มในการปฏิเสธศรัทธา ยิ่งขึ้นโดยที่ผู้ปฏิเสธศรัทธาเหล่านั้นถูกทําให้หลงผิดไป เนื่องด้วยการเลื่อนเดือนต้องห้ามนั้น พวกเขาได้ให้มันเป็นที่อนุมัติปีหนึ่ง และให้มันเป็นที่ต้องห้ามปีหนี่ง เพื่อพวกเขาจะให้พ้องกับจํานวนเดือนที่อัลลอฮ์ได้ทรงห้ามไว้ (มิเช่นนั้นแล้ว) พวกเขาก็จะทําให้เป็นที่อนุมัติสิ่งที่อัลลอฮ์ไทรงให้เป็นที่ต้องห้ามไป โดยที่ความชั่วแห่งบรรดาการงานของพวกเขาได้ถูกประดับประดาให้สวยงามแก่พวกเขา และอัลลอฮ์นั้นจะไม่ทรงนําทางแก่กลุ่มชนที่ปฏิเสธศรัทธา”');
INSERT INTO "king_fahad_thai" VALUES(9,38,'9:38','“บรรดาผู้ศรัทธาทั้งหลาย มีอะไรเกิดขึ้นแก่พวกเจ้ากระนั้นหรือ ? เมื่อได้ถูกกล่าวแก่พวกเจ้าว่า จงออกไปต่อสู้ในทางของอัลลอฮ์ เถิด พวกเจ้าก็แนบหนักอยู่กับพื้นดิน พวกเจ้าพึงพอใจต่อชีวิตความเป็นอยู่แห่งโลกนี้ แทน ปรโลกกระนั้นหรือ ? สิ่งอํานวยความสุขแห่งชีวิตความเป็นอยู่ในโลกนี้นั้น ในปรโลกแล้ว ไม่มีอะไรนอกจากสิ่งเล็กน้อยเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(9,39,'9:39','“ถ้าหากพวกเจ้าไม่ออกไป พระองค์ก็จะทรงลงโทษพวกเจ้าอย่างเจ็บปวด และจะทรงให้พวกหนึ่งอื่นจากพวกเจ้ามาแทน และพวกเจ้าไม่สามารถจะยังความเดือดร้อนให้แก่พระองค์ได้แต่อย่างใด และอัลลอฮ์นั้นทรงเดชานุภาพเหนือทุกสิ่งทุกอย่าง”');
INSERT INTO "king_fahad_thai" VALUES(9,40,'9:40','“ถ้าหากพวกจ้าไม่ช่วยเขา ก็แท้จริงนั้นอัลลอฮ์ได้ทรงช่วยเขามาแล้ว ขณะที่บรรดาผู้ปฏิเสธศรัทธา ได้ขับไล่เขาออกไปโดยที่เขาเป็นคนที่สองในสองคน ขณะที่ทั้งสอง อยู่ในถ้ํานั้นคือขณะที่เขา ได้กล่าวแก่สหายของเขา ว่า ท่านอย่าเสียใจ แท้จริงอัลลอฮ์ทรงอยู่กับเรา แล้วอัลลอฮ์ก็ทรงประทานลงมาแก่เขา ซึ่งความสงบใจจากพระองค์ และได้ทรงสนับสนุนเขาด้วยบรรดาไพร่พล ซึ่งพวกเจ้าไม่เห็นพวกเขา และได้ทรงให้ถ้อยคํา ของผู้ที่ปฏิเสธศรัทธาอยู่ในระดับต่ําสุด และพจนารถของอัลลอฮ์นั้น คือพจนารถที่สูงสุด และอัลลอฮ์คือ ผู้ทรงเดชานุภาพ ผู้ทรงปรีชาญาณ”');
INSERT INTO "king_fahad_thai" VALUES(9,41,'9:41','“พวกเจ้าจงออกไปกันเถิด ทั้งผู้ที่มีสภาพว่องไว และผู้ที่มีสภาพเชื่องช้า และจงเสียสละทั้งด้วยทรัพย์ของพวกเจ้า แลเชีวิตของพวกเจ้าในทางของอัลลอฮ์ นั่นแหละคือสิ่งที่ดียิ่งสําหรับพวกเจ้า หากพวกเจ้ารู้”');
INSERT INTO "king_fahad_thai" VALUES(9,42,'9:42','“หากมัน เป็นผลได้อันใกล้ และเป็นการเป็นทางที่สะดวกและใกล้แล้วไซร้ แน่นอนพวกเขา ก็ปฏิบัติตามเจ้าแล้ว แต่ทว่าระยะทาง อันลําบากนั้นไกลแก่พวกเขา และพวกเขาจะสาบานต่ออัลลอฮ์ ว่า ถ้าหากพวกเราสามารถแล้ว แน่นอนพวกเราก็ออกไปกับพวกท่านแล้ว พวกเขากําลังทําลายชีวิตของพวกเขาเอง และอัลลอฮ์นั้นทรงรู้ว่าแท้จริงพวกเขานั้นเป็นผู้กล่าวเท็จ”');
INSERT INTO "king_fahad_thai" VALUES(9,43,'9:43','“อัลลอฮ์นั้นได้ทรงอภัยโทษให้แก่เจ้า แล้วเพราะเหตุใดเล่าเจ้าจึงอนุมัติให้แก่พวกเขา จนกว่าจะได้ประจักษ์แก่เจ้าก่อน ซึ่งบรรดาผู้ที่พูดจริง และจนกว่าเจ้าจะได้รู้บรรดาผู้ที่กล่าวเท็จ”');
INSERT INTO "king_fahad_thai" VALUES(9,44,'9:44','“บรรดาผู้ทีศรัทธาต่ออัลลอฮ์ และวันปรโลกนั้นจะไม่ขออนุมัติต่อเจ้าในการที่พวกเขาจะเสียสละทั้งด้วยทรัพย์ของพวกเขา และชีวิตของพวกเขา และอัลลอฮ์นั้นเป็นผู้ทรงรอบรู้ต่อบรรดาผู้ที่ยําเกรง”');
INSERT INTO "king_fahad_thai" VALUES(9,45,'9:45','“แท้จริงที่จะขออนุมัติต่อเจ้านั้นคือบรรดาผู้ที่ไม่ศรัทธาต่ออัลลอฮ์ และวันปรโลกและหัวใจของพวกเขาสงสัยเท่านั้น แล้วในการสงสัยของพวกเขานั้นเอง พวกเขาจึงลังเลใจ”');
INSERT INTO "king_fahad_thai" VALUES(9,46,'9:46','“และหากพวกเขาต้องการออกไป แน่นอนพวกเขาต้องเตรียมสัมภาระ สําหรับการออกไปนั้นแล้ว แต่ทว่าอัลลอฮ์ทรงเกลียดการออกไปของพวกเขาพระองค์จึงได้ทรงกีดขวาง พวกเขาไว้ และได้ถูกกล่าวว่า ท่านทั้งหลายจงนั่งอยู่กับผู้ที่นั่งทั้งหลาย เถิด”');
INSERT INTO "king_fahad_thai" VALUES(9,47,'9:47','“หากว่าพวกเขาออกไปในหมู่พวกเจ้าแล้วก็ไม่มีอะไรเพิ่มแก่พวกเจ้า นอกจากความเสียหาย เท่านั้น และแน่นอนพวกเขาก็ย่อมฉวยโอกาสยุแหย่ระหว่างพวกเจ้าโดยปรารถนาให้เกิดความวุ่นวายแก่พวกเจ้า และในหมู่พวกเจ้านั้นก็มีพวกที่รับฟังพวกเขาอยู่ และอัลลอฮ์นั้นเป็นผู้ทรงรอบรู้ในผู้อธรรมทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(9,48,'9:48','“แท้จริงนั้นพวกเขาได้แสวงหาความวุ่นวาย มาก่อนแล้ว และวางแผนต่าง ๆนานา เพื่อต่อต้าน เจ้า จนกระทั่งความจริง ได้มา และพระบัญชาของอัลลอฮ์ได้ประจักษ์ขึ้น ทั้ง ๆ ที่พวกเขาไม่พอใจ”');
INSERT INTO "king_fahad_thai" VALUES(9,49,'9:49','“และในหมู่พวกเขา นั้นมีผู้ที่กล่าว ว่า จงอนุมัติแก่ฉันเถิด และอย่าให้ฉันตกอยู่ในการทําความชั่ว เลย พึงรู้เถิดว่า พวกเขาได้ตกอยู่ในการทําความชั่ว นั้นแล้ว และแท้จริงนรกญะฮันนัมนั้นล้อมบรรดาผู้ปฏิเสธการศรัทธาอยู่แล้ว”');
INSERT INTO "king_fahad_thai" VALUES(9,50,'9:50','“หากมีความดีใด ๆ ประสบแก่เจ้าก็ทําให้พวกเขาไม่สบายใจ และหากมีอันตรายใด ๆ ประสบแก่เจ้า พวกเขาก็กล่าวว่า แท้จริงพวกเราได้เอากิจการของเราไว้ก่อน แล้ว และพวกเขาก็ผินหลังให้ โดยที่พวกเขาเป็นผู้ปิติยินดี');
INSERT INTO "king_fahad_thai" VALUES(9,51,'9:51','“จงกล่าวเถิด (มุฮัมมัด) ว่า จะไม่ประสบแก่เราเป็นอันขาด นอกจากสิ่งที่อัลลอฮฺได้กําหนดไว้แก่เราเท่านั้น ซึ่งพระองค์เป็นผู้คุ้มครองเรา และแด่อัลลอฮฺนั้น มุอฺมินทั้งหลายจงมอบหมายเถิด”');
INSERT INTO "king_fahad_thai" VALUES(9,52,'9:52','“จงกล่าวเถิด (มุฮัมมัด) ว่าพวกท่านจะไม่คอยดูพวกเรา นอกจากหนึ่งในสองสิ่ง ทีดีเยี่ยมเท่านั้น และเราก็จะคอยดูพวกท่านในการที่อัลลอฮ์จะทรงให้ประสบแก่พวกท่านด้วยการลงโทษที่มาจากที่พระองค์ หรือด้วยมือของพวกเรา ดังนั้นพวกเจ้าจงคอยดูไปเถิด แท้จริงพวกเราก็จะเป็นผู้คอยดูพร้อมกับพวกท่านด้วย”');
INSERT INTO "king_fahad_thai" VALUES(9,53,'9:53','“จงกล่าวเถิด (มุฮัมมัด) ว่าพวกท่านจงบริจาคกันเถิด ทั้งด้วยสมัครใจหรือด้วยฝืนใจก็ตาม มัน จะไม่ถูกรับจากพวกท่านเป็นอันขาดแท้จริงพวกท่านนั้นเป็นพวกที่ละเมิด ”');
INSERT INTO "king_fahad_thai" VALUES(9,54,'9:54','“ไม่มี่สิ่งใดขัดขวางพวกเขา ในการที่บรรดาสิ่งบริจาคของพวกเขาไม่ถูกรับจากพวกเขานอกจากพวกเขาปฏิเสธศรัทธาต่ออัลลอฮ์ และต่อร่อซูลของพระองค์เท่านั้น และพวกเขาจะไม่มาละหมาด นอกจากพวกเขาจะมีสภาพเกียจคร้าน แลพวกเขาจะไม่บริจาค นอกจากพวกเขาจะมีสภาพฝืนใจ ”');
INSERT INTO "king_fahad_thai" VALUES(9,55,'9:55','“ดังนั้นจงอย่าให้ทรัพย์สมบัติพวกเขา และอย่าให้ลูก ๆ ของพวกเขาเป็นที่พึงใจแก่เจ้า แท้จริงอัลลอฮ์ทรงต้องการที่จะลงโทษพวกเขาด้วยสิ่งเหล่านั้นในชีวิตแห่งโลกนี้ และที่จะให้ชีวิตของพวกเขาออกจากร่างไป ขณะที่พวกเขาเป็นผุ้ปฏิเสธศรัทธาเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(9,56,'9:56','“และพวกเขา จะสาบานต่ออัลลอฮ์ว่าแท้จริงพวกเขานั้นเป็นพวกของพวกเจ้า และพวกเขาหาใช่เป็นพวกของพวกเจ้าไม่ แต่ทว่าพวกเขานั้นคือ พวกที่หวาดกลัว ต่างหาก”');
INSERT INTO "king_fahad_thai" VALUES(9,57,'9:57','“หากพวกเขาพบที่พักพิง หรือบรรดาถ้ําหรืออุโมง แน่นอนพวกเขาจะหันไปหามัน โดยที่พวกเขาจะไปอย่างรีบด่วน ”');
INSERT INTO "king_fahad_thai" VALUES(9,58,'9:58','“และในหมู่พวกเขานั้นมีผู้ที่ตําหนิเจ้าในเรื่องสิ่งบริจาค ถ้าหากพวกเขาได้รับจากสิ่งบริจาคนั้นพวกเขาก็ยินดี และหากพวกเขามิได้รับจากสิ่งบริจาคนั้น ทันใดพวกเขาก็โกรธ”');
INSERT INTO "king_fahad_thai" VALUES(9,59,'9:59','“และหากพวกเขายินดีต่อสิ่ง ที่อัลลอฮ์และร่อซูลของพระองค์ได้ให้แก่พวกเขา และกล่าวว่า อัลลอฮ์นั้นทรงพอเพียงแก่เรา แล้วโดยที่อัลลอฮ์จะทรงประทานแก่เราจากความกรุณาของพระองค์และร่อซูลของพระองค์ (ก็จะให้ด้วย) แท้จริงแด่อัลลอฮ์เท่านั้นพวกเราเป็นผู้วิงวอนขอ”');
INSERT INTO "king_fahad_thai" VALUES(9,60,'9:60','“แท้จริงทานทั้งหลาย นั้น สําหรับบรรดาผู้ที่ยากจน และบรรดาผู้ที่ขัดสน และบรรดาเจ้าหน้าที่ในการรวบรวมมัน และบรรดาผู้ที่หัวใจของพวกเขาสนิทสนม และในการไถ่ทาส และบรรดาผู้ที่หนี้สินล้นตัว และในทางของอัลลอฮ์ และผู้ที่อยู่ในระหว่างเดินทาง ทั้งนี้เป็นบัญญัติอันจําเป็นซึ่งมาจากอัลลอฮ์ และอัลลอฮ์นั้นเป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ”');
INSERT INTO "king_fahad_thai" VALUES(9,61,'9:61','“และในหมู่พวกเขานั้นมีบรรดาผู้ที่ก่อความเดือดร้อนแก่นะบี โดยที่พวกเขากล่าวว่า เขาคือหู จงกล่าวเถิด (มุฮัมมัด) ว่า คือหูแห่งความดีสําหรับพวกท่าน โดยที่ศรัทธาต่ออัลลอฮ์ และเชื่อถือต่อผู้ศรัทธาทั้งหลาย และเป็นการเอ็นดูเมตตา แก่บรรดาผู้ศรัทธาในหมู่พวกท่านและบรรดาผู้ที่ก่อความเดือดร้อนแก่ร่อซูลของอัลลอฮ์ นั้นพวกเขาจะได้รับการลงโทษอันเจ็บปวด”');
INSERT INTO "king_fahad_thai" VALUES(9,62,'9:62','“พวกเขาสาบาน ด้วยอัลลอฮ์แก่พวกเจ้า เพื่อที่จะให้พวกเจ้าพอใจ และอัลลอฮ์และร่อซูลของพระองค์นั้นเป็นผู้สมควรยิ่งกว่าที่พวกเขาจะทําให้เขาพึงพอใจ หากพวกเขาเป็นผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(9,63,'9:63','“พวกเขามิได้รู้ดอกหรือว่า แท้จริงผู้ใดที่ฝ่าฝืนอัลลอฮ์และร่อซูลของพระองค์ แน่นอนสําหรับเขานั้นคือไฟนรกญะฮันนัมโดยที่เขาจะอยู่ในนั้นตลอดกาลนั่นแหละคือความอัปยศอันใหญ่หลวง”');
INSERT INTO "king_fahad_thai" VALUES(9,64,'9:64','“บรรดามุนาฟิก นั้นหวั่นเกรงว่า จะมีซูเราะฮ์ หนึ่งถูกประทานลงมาแก่พวกเขา ซึ่งแจ้งให้พวกเขาทราบสิ่งที่อยู่ในใจ ของพวกเขา จงกล่าวเถิด (มุฮัมมัด) ว่า พวกท่านจงเย้ยหยัน กันเถิด แท้จริงอัลลอฮ์นั้นจะทรงให้ออกมาซึ่งสิ่ง ที่พวกท่านหวั่นเกรง');
INSERT INTO "king_fahad_thai" VALUES(9,65,'9:65','“และถ้าหากเจ้าได้ถามพวกเขา แน่นอนพวกเขาจะกล่าวว่า แท้จริงพวกเราเป็นเพียงแต่พูดสนุก, พูดเล่น,เท่านั้นจงกล่าวเถิด (มุฮัมมัด) ว่าต่ออัลลอฮ์ และบรรดาโองการของพระองค์และร่อซูลของพระองค์กระนั้นหรือที่พวกท่านเย้ยหยันกัน ?”');
INSERT INTO "king_fahad_thai" VALUES(9,66,'9:66','“พวกท่านอย่าแก้ตัวเลย แท้จริงพวกท่านได้ปฏิเสธศรัทธา แล้ว หลังจากการมีศรัทธาของพวกท่าน หากเราจะอภัยโทษให้แก่กลุ่มหนึ่งในหมู่พวกเจ้า เราก็จะลงโทษอีกกลุ่มหนึ่ง เพราะว่าพวกเขาเป็นผู้กระทําความผิด”');
INSERT INTO "king_fahad_thai" VALUES(9,67,'9:67','“บรรดามุนาฟิก ชาย และบรรดามุนาฟิกหญิงนั้นบางส่วนของพวกเขาต่างมาจากอีกบางส่วน ซึ่งพวกเขาจะใช้ให้ปฏิบัติในสิ่งที่ไม่ชอบและห้ามปรามในสิ่งที่ชอบ และกํามือ ของพวกเขาไว้ โดยที่พวกเขาลืมอัลลอฮ์ แล้วพระองค์ก็ทรงลืมพวกเขา บ้าง แท้จริงบรรดามุนาฟิกนั้นพวกเขาคือผู้ละเมิด”');
INSERT INTO "king_fahad_thai" VALUES(9,68,'9:68','“อัลลอฮ์ได้ทรงขู่ไว้แก่บรรดามุนาฟิกชาย และบรรดามุนาฟิกหญิง และผู้ปฏิเสธศรัทธาทั้งหลาย ซึ่งไฟนรกญะฮันนัมโดยที่พวกเขาจะอยู่ในนั้นตลอดกาล มันเป็นสิ่งที่พอเพียงแก่พวกเขาแล้ว และอัลลอฮ์ ก็ได้ทรงให้พวกเขาห่างไกลจากความเอ็นดูเมตตาของพระองค์ และสําหรับพวกเขานั้นคือ การลงโทษอันจีรังยั่งยืน”');
INSERT INTO "king_fahad_thai" VALUES(9,69,'9:69','“เช่นเดียวกับบรรดาผู้ที่ก่อนหน้าพวกเจ้า ซึ่งพวกเขาเป็นพวกที่มีกําลังแข็งแรงกว่าพวกเจ้า และมีทรัพย์สมบัติและลูก ๆ มากกว่าพวกเจ้า แล้วพวกเขาก็ได้หาความสําราญในสิ่งที่เป็นส่วนได้ ของพวกเขา พวกเจ้าก็ได้หาความสําราญในสิ่งที่เป็นส่วนได้ของพวกเจ้า เช่นเดียวกับบรรดาผู้ทีก่อนหน้าพวกเจ้าได้หาความสําราญในสิ่งที่เป็นส่วนได้ของพวกเขามาแล้ว และพวกเจ้าพูดกันในสิ่งไร้สาระ เช่นเดียวกับพวกเขาพูดคุยกัน ชนเหล่านี้แหละ บรรดาการงานของพวกเขาไร้ผล ทั้งในโลกนี้และปรโลก และชนเหล่านี้แหละพวกเขาคือผู้ขาดทุน”');
INSERT INTO "king_fahad_thai" VALUES(9,70,'9:70','“มิได้มายังพวกเขาดอกหรือ ซึ่งข่าวคราวของบรรดาผู้ก่อนหน้าพวกเขา คือกลุ่มชนาของนูฮ์ และของอ๊าด และของซะมูด และกลุ่มชนของอิบรอฮีม และชาวมัดยัน และชาวอัล-มุตะฟิกาต โดยที่บรรดาร่อซูลของพวกเขาได้นําหลักฐานต่าง ๆ อันชัดแจ้งมายังพวกเขา ใช่ว่าอัลลอฮ์นั้นจะอธรรมแก่พวกเขาก็หาไม่ แต่ทว่าพวกเขาอธรรมแก่ตัวของพวกเขาเองต่างหาก”');
INSERT INTO "king_fahad_thai" VALUES(9,71,'9:71','“และบรรดามุมิน ชาย และบรรดามุมินหญิงนั้น บางส่วนของพวกเขาต่างเป็นผู้ช่วยเหลืออีกบางส่วน ซึ่งพวกเขาจะใช้ให้ปฏิบัติในสิ่งที่ชอบและห้ามปรามในสิ่งที่ไม่ชอบ และพวกเขาจะดํารงไว้ซึ่งการละหมาดและจ่ายซะกาต และภักดีต่ออัลลอฮ์ และร่อซูลของพระองค์ชนเหล่านี้แหละ อัลลอฮ์จะทรงเอ็นดูเมตตาแก่พวกเขา แท้จริงอัลลอฮ์นั้นเป็นผู้ทรงเดชานุภาพ ผู้ทรงปรีชาญาณ”');
INSERT INTO "king_fahad_thai" VALUES(9,72,'9:72','“อัลลอฮ์ได้ทรงสัญญาแก่บรรดามุมินชายและบรรดามุมินหญิง ซึ่งบรรดาสวนสวรรค์ ซึ่งมีแม่น้ําหลายสายไหลอยู่ภายใต้สวนสวรรค์เหล่านั้น โดยที่พวกเขาจะอยู่ในนั้นตลอดกาล และบรรดาสถานที่พํานัก อันดีซึ่งอยู่ในบรรดาสวนสวรรค์แห่งความวัฒนาสถาพร และความปิติยินดี จากอัลลอฮ์นั้นใหญ่กว่า นั่นคือ ชัยชนะอันใหญ่หลวง”');
INSERT INTO "king_fahad_thai" VALUES(9,73,'9:73','“โอ้นะบี ! จงต่อสู้บรรดาผู้ปฏิเสธศรัทธาและบรรดาผู้กลับกลอกในการศรัทธา (มุนาฟิกีน) และจงเฉียบขาดแก่พวกเขาและที่อยู่ของพวกเขานั้นคือ นรกญะฮันนัม และที่กลับไป นั้น ชั่วช้าจริง ๆ');
INSERT INTO "king_fahad_thai" VALUES(9,74,'9:74','“พวกเขาสาบานต่ออัลลอฮ์ว่า พวกเขามิได้พูดและแท้จริงนั้น พวกเขาได้พูดซึ่งถ้อยคําแห่งการปฏิเสธศรัทธา และพวกเขาได้ปฏิเสธศรัทธาแล้ว หลังจากการเป็นมุสลิมของพวกเขาและพวกเขามุ่งกระทําในสิ่งที่พวกเขามิได้รับผล และพวกเขามิได้ปฏิเสธ และจงเกลียดจงชัง นอกจากว่า อัลลอฮ์ และร่อซูลของพระองค์ได้ทรงให้พวกเขามั่งคั่งขึ้นจากความกรุณาของพระองค์ และหากพวกเขาสํานึกผิดกลับเนื้อกลับตัว ก็เป็นสิ่งดีแก่พวกเขา และหากพวกเขาผินหลังให้อัลลอฮ์ก็จะทรงลงโทษพวกเขาอย่างเจ็บแสบทั้งในโลกนี้และปรโลก และพวกเขาไม่มีผู้คุ้มครองและผู้ช่วยเหลือใด ๆ ในผืนแผ่นดิน”');
INSERT INTO "king_fahad_thai" VALUES(9,75,'9:75','“และในหมู่พวกเขานั้นมีผู้ที่ได้สัญญาแก่อัลลอฮ์ว่า ถ้าหากพระองค์ได้ทรงประทานแก่พวกเรา ซึ่งส่วนหนึ่งจากความกรุณาของพระองค์แล้วไซร้ แน่นอนเหลือเกิน พวกเราจะบริจาคทานและแน่นอนพวกเราจะได้เป็นผู้อยู่ในหมู่คนดี”');
INSERT INTO "king_fahad_thai" VALUES(9,76,'9:76','“ครั้นเมื่อพระองค์ได้ทรงประทานให้แก่พวกเขา ซึ่งส่วนหนึ่งจากความกรุณาของพระองค์พวกเขาก็ตระหนี่ในส่วนนั้นและได้ผินหลังให้ โดยที่พวกเขาเป็นผู้ผินหลังให้อยู่แล้ว”');
INSERT INTO "king_fahad_thai" VALUES(9,77,'9:77','“แล้วพระองค์ก็ทรงให้การกลับกลอกในหัวใจของพวกเขาเป็นผลลัพธ์ แก่พวกเขา จนกระทั่งถึงวันที่พวกเขาจะพบพระองค์ เนื่องจากการที่พวกเขาบิดพริ้วต่ออัลลอฮ์ ในสิ่งที่พวกเขาให้สัญญาไว้แก่พระองค์ และเนื่องจากการที่พวกเขาปฏิเสธ ”');
INSERT INTO "king_fahad_thai" VALUES(9,78,'9:78','“พวกเขามิได้รู้ดอกหรือว่า แท้จริงอัลลอฮ์นั้นทรงรู้ความเร้นลับของพวกเขา และการพูดซุบซิบของพวกเขา และแท้จริงอัลลอฮ์ เป็นผู้ทรงรอบรู้ในซึ่งสิ่งเร้นลับทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(9,79,'9:79','“พวกที่ตําหนิ บรรดาผู้ที่สมัครใจจากหมู่ผู้ศรัทธาในการบริจาคทาน และตําหนิผู้ที่ไม่พบสิ่งใด (จะบริจาค)นอกจากค่าแรงงานอันเล็กน้อยของพวกเขา แล้วเย้ยหยันพวกเขา นั้น อัลลอฮ์ได้ทรงเย้ยหยันพวกเขา แล้วและสําหรับพวกเขานั้น คือการลงโทษอันเจ็บแสบ”');
INSERT INTO "king_fahad_thai" VALUES(9,80,'9:80','“เจ้าจงขออภัยโทษให้แก่พวกเขา หรือไม่ก็จงอย่าขออภัยโทษให้แก่พวกเขา หากเจ้าขออภัยให้แก่พวกเขาเจ็ดสิบครั้ง อัลลอฮ์ก็จะไม่ทรงอภัยให้แก่พวกเขาเป็นอันขาด นั่นก็เพราะว่าพวกเขาได้ปฏิเสธศรัทธาต่ออัลลอฮ์ และร่อซูลของพระองค์ และอัลลอฮ์ จะไม่ทรงแนะนํากลุ่มชนที่ละเมิด”');
INSERT INTO "king_fahad_thai" VALUES(9,81,'9:81','“บรรรดาผู้ที่ถูกปล่อยให้อยู่เบื้องหลัง นั้นดีใจในการที่พวกเขานั่งอยู่เบื้องหลัง ของร่อซูลุลลอฮ์ และพวกเขาเกลียดในการที่พวกเขาจะต่อสู้ด้วยทรัพย์ของพวกเขา และชีวิตของพวกเขาในทางของอัลลอฮ์ และพวกเขากล่าวว่า ท่านทั้งหลายอย่าออกไปในความร้อนเลย จงกล่าวเถิด (มุฮัมมัด) ว่า ไฟนรกญะฮัมนัมนั้นร้อนแรงยิ่งกว่า หากพวกเขาเข้าใจ”');
INSERT INTO "king_fahad_thai" VALUES(9,82,'9:82','“พวกเขาหัวเราะแต่น้อย และจงร้องไห้มาก ๆ เถิด ทั้งนี้เป็นการตอบแทนตามที่พวกเขาขวนขวาย ไว้"');
INSERT INTO "king_fahad_thai" VALUES(9,83,'9:83','“หากอัลลอฮ์ไทรงให้เจ้ากลับไปยังกลุ่มหนึ่งในหมู่พวกเขา แล้วพวกเขาจะขออนุมัติเจ้าเพื่อออกไป ก็จงกล่าวเถิดว่า พวกท่านจะไม่ออกไปกับฉันตลอดกาล และจะไม่ต่อสู้ร่วมกับฉันซึ่งศัตรูใด ๆ เป็นอันขาด แท้จริงพวกท่านพอใจต่อการนั่งอยู่แต่ครั้งแรกแล้ว ดังนั้น จะนั่งอยู่กับบรรดาผู้ที่อยู่เบื้องหลัง ต่อไปเถิด”');
INSERT INTO "king_fahad_thai" VALUES(9,84,'9:84','“และเจ้า จงอย่าละหมาดให้แก่คนใดในหมู่พวกเขาที่ตายไปเป็นอันขาด และจงอย่ายืนที่หลุมศพของเขาด้วย แท้จริงพวกเขานั้นได้ปฏิเสธศรัทธาต่ออัลลอฮ์ และร่อซูลของพระองค์ และพวกเขาได้ตายลง ขณะที่พวกเขาเป็นผู้ละเมิด”');
INSERT INTO "king_fahad_thai" VALUES(9,85,'9:85','“และจงอย่าให้ทรัพย์สมบัติของพวกเขาและลูก ๆของพวกเขา เป็นที่พึงใจแก่เจ้า แท้จริงอัลลอฮ์ ทรงต้องการที่จะลงโทษพวกเขาด้วยสิ่งเหล่านั้นในโลกนี้ และที่จะให้ชีวิตของพวกเขาออกจากร่างไป ขณะที่พวกเขาเป็นผู้ปฏิเสธศรัทธาเท่านั้น” ”');
INSERT INTO "king_fahad_thai" VALUES(9,86,'9:86','“และเมื่อมีซูเราะฮ์หนึ่งซูเราะฮ์ใด ถูกประทานลงมาว่า พวกเข่าจงศรัทธาต่ออัลลอฮ์เถิด และจงต่อสู้ด้วยทรัพย์สมบัติและชีวิต ร่วมกับร่อซุลของพระองค์ ผู้ที่มั่งคั่งในหมู่พวกเขา ก็ขออนุญาติต่อเจ้า และกล่าวว่า จงปล่อยพวกเราไว้เถิด พวกเราจะได้อยู่กับบรรดาผู้ที่นั่งอยู่ กัน”');
INSERT INTO "king_fahad_thai" VALUES(9,87,'9:87','“พวกเขายินดีในการที่พวกเขาจะอยู่กับบรรดาผู้ที่อยู่เบื้องหลัง และได้ถูกประทับตราไว้แล้วบนหัวใจของพวกเขา แล้ว ดังนั้นพวกเขาจึงไม่เข้าใจ”');
INSERT INTO "king_fahad_thai" VALUES(9,88,'9:88','“แต่ทว่าร่อซูล และบรรดาผู้ที่ศรัทธาซึ่งร่วมอยู่กับท่านนั้น ได้ต่อสู้ด้วยทรัพย์สมบัติของพวกเขา และชีวิตของพวกเขา ชนเหล่านี้แหละสําหรับพวกเขานั้นจะได้รับความดีมากมาย และชนเหล่านี้แหละคือผู้ที่ได้รับความสําเร็จ”');
INSERT INTO "king_fahad_thai" VALUES(9,89,'9:89','“อัลลอฮ์ได้ทรงเตรียมไว้แล้ว สําหรับพวกเขา ซึ่งบรรดาสวนสวรรค์ ที่มีแม่น้ําหลายสายไหลอยู่ภายใต้ สวนสวรรค์เหล่านั้นโดยที่พวกเขาจะพํานักอยู่ในนั้นตลอดกาล นั่นแหละคือชัยชนะอันใหญ่หลวง”');
INSERT INTO "king_fahad_thai" VALUES(9,90,'9:90','“และบรรดาผู้ที่แก้ตัว ในหมู่อาหรับชนบทเหล่านั้นได้มา เพื่อจะได้ถูกอนุมัติให้แก่พวกเขา และบรรดาผู้ที่ปฏิเสธ อัลลอฮ์ และร่อซูลของพระองค์นั้นได้นั่งกันอยู่ การลงโทษอันเจ็บแสบจะประทานแก่บรรดาผู้ปฏิเสธศรัทธาในหมู่พวกเขา”');
INSERT INTO "king_fahad_thai" VALUES(9,91,'9:91','“ไม่มีบาปใด ๆ แก่บรรดาผู้ที่อ่อนแอ, และแก่ผู้ที่ป่วยไข้ และแก่บรรดาผู้ที่ไม่พบสิ่งที่จะบริจาค เมื่อพวกเขาได้แนะนําตักเตือนให้จงรักภักดีต่ออัลลอฮ์ และร่อซูลของพระองค์ ไม่มีทางใดที่จะกล่าวโทษแก่บรรดาผู้กระทําดีได้ และอัลลอฮ์นั้นคือผู้ทรงอภัยโทษ ผู้ทรงเอ็นดูเมตตา”');
INSERT INTO "king_fahad_thai" VALUES(9,92,'9:92','“และไม่มีบาปใด ๆ แก่บรรดาผู้ที่เมื่อพวกเขามาหาเจ้าเพื่อให้เจ้าจัดให้พวกเขาขี่ เจ้าได้กล่าวว่า ฉันไม่พบพาหนะที่จะให้พวกท่านขี่บนมันได้ พวกเขาก็ผินหลังกลับโดยที่นัยน์ตาของพวกเขาท่วมท้วนไปด้วยน้ําตา เพราะเสียใจที่พวกเขาไม่พบสิ่งที่พวกเขาจะบริจาค”');
INSERT INTO "king_fahad_thai" VALUES(9,93,'9:93','“แท้จริงทางที่จะกล่าวโทษได้นั้น ก็เพียงแต่บรรดาผู้ที่อนุญาตต่อเจ้า ทั้ง ๆ ที่พวกเขาเป็นผู้มั่งมี ซึ่งยินดีที่จะอยู่ร่วมกับบรรดาผู้ที่อยู่เบื้องหลัง และอัลลอฮ์ได้ทรงประทับตราบนหัวใจของพวกเขาแล้ว ดังนั้นพวกเขาจึงไม่รู้อะไร”');
INSERT INTO "king_fahad_thai" VALUES(9,94,'9:94','พวกเขา(ที่ไม่ออกไปสงครามตะบู๊ก)จะแก้ตัวแก่พวกท่าน เมื่อพวกท่านกลับมายังพวกเขาจงกล่าวเถิด(มุฮัมมัด) ว่าพวกท่านอย่าแก้ตัวเลยเราจะไม่เชื่อพวกท่านดอก แท้จริงอัลลอฮ์ทรงแจ้งข่าวคราวของพวกท่านแก่เราแล้ว และอัลลอฮ์นั้นทรงเห็นการกระทําของพวกท่าน และร่อซูลของพระองค์ก็เห็นด้วย แล้วพวกท่านก็จะถูกนําลับไปยังพระผู้ทรงรอบรู้แห่งสิ่งเร้นลับและสิ่งเปิดเผย แล้วพระองค์ก็จะทรงแจ้งแก่พวกท่านให้รู้ถึงสิ่งที่พวกท่านกระทํานั้น');
INSERT INTO "king_fahad_thai" VALUES(9,95,'9:95','พวกเขาจะสาบานต่ออัลเลอฮ์แก่พวกท่านเมื่อพวกท่านได้กลับมายังพวกเขา เพื่อให้พวกท่านยกโทษให้พวกเขา ดังนั้นพวกท่านจงผินหลังให้พวกเขาเถิด แท้จริงพวกเขานั้นชั่วร้าย และที่พํานักของพวกเขาคือนรก ทั้งนี้เป็นการตอบแทนในสิ่งที่พวกเขาขวนขวายไว้');
INSERT INTO "king_fahad_thai" VALUES(9,96,'9:96','พวกเขาจะสาบานแก่พวกท่าน เพื่อให้พวกท่านพอใจต่อพวกเขา แล้วหากพวกท่านพอใจต่อพวกเขา แท้จริงอัลลอฮ์นั้นจะไม่ทรงพอพระทัยต่อกลุ่มชนที่ละเมิดฝ่าฝืน');
INSERT INTO "king_fahad_thai" VALUES(9,97,'9:97','บรรดาอาหรับชนบทนั้น เป็นพวกปฏิเสธศรัทธาและพวกกลับกลอกที่ร้ายกาจที่สุด และเป็นการสมควรยิ่งแล้ว ที่พวกเขาจะไม่รู้ขอบเขตในสิ่งที่อัลลอฮ์ทรงประทานให้แก่ร่อซูลของพระองค์และอัลลออ์นั้นเป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(9,98,'9:98','และในหมู่อาหรับชนบทนั้น มีผู้ถือเอาสิ่งที่ตนบริจาคไปเป็นค่าปรับ และถือว่าเป็นการขาดทุนและพวกเขารอคอยเหตุร้ายที่จะเกิดแก่พวกท่าน เหตุร้ายเหล่านั้นจงประสบแก่พวกเขาเขาเถิดและอัลลอฮ์นั้นเป็นผู้ทรงได้ยินทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(9,99,'9:99','และในหมู่อาหรับชนบทนั้น มีผู้ศรัทธาต่ออัลลอฮฺและวันอาคิเราะฮฺ และถือเอาสิ่งที่ตนบริจาคไปนั้นเป็นการใกล้ชิดกับอัลลอฮฺ และเป็นการขอพรของร่อซูล พึงรู้เถิดว่า แท้จริงมันเป็นการทําให้ใกล้ชิดแก่พวกเขา อัลลอฮฺจะทรงให้พวกเขาอยู่ในความเอ็นดูเมตตาของพระองค์ แท้จริงอัลลอฮฺนั้นเป็นผู้ทรงอภัยโทษ ทรงเอ็นดูเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(9,100,'9:100','บรรดาบรรพชนรุ่นแรกในหมู่ผู้อพยพ(ชาวมุฮาญิรีนจากมักกะฮ์) และในหมู่ผู้ให้ความช่วยเหลือ(ชาวอันศัอรจากมะดีนะฮ์) และบรรดาผู้ดําเนินตามพวกเขาด้วยการทําดีนั้น อัลลอฮ์ทรงพอพระทัยในพวกเขา และพวกเขาก็พอใจในพระองค์ด้วย และพระองค์ทรงเตรียมไว้ให้พวกเขาแล้ว ซึ่งบรรดาสวนสวรรค์ที่มีแม่น้ําหลายสายไหลผ่านอยู่เบื้องล่างพวกเขาจะพํานักอยู่ในนั้นตลอดกาลนั่นคือชัยชนะอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(9,101,'9:101','และส่วนหนึ่งจากผู้ที่พํานักอยู่รอบๆ พวกท่านที่เป็นอาหรับชนบทนั้น เป็นพวกกลับกลอก และในหมู่ชาวมะดีนะฮ์ก็เช่นเดียวกัน พวกเขาเหล่านั้นดื้อรั้นในการกลับกลอก เจ้า(มุฮัมมัด) ไม่รู้จักธาตุแท้ของพวกเขาดอก เรา(อัลลอฮ์) รู้จักพวกเขาดี เราจะลงโทษพวกเขาสองครั้ง แล้วพวกเขาจะถูกนํากลับไปสู่การลงโทษอันยิ่งใหญ่ต่อไป');
INSERT INTO "king_fahad_thai" VALUES(9,102,'9:102','และมีชนกลุ่มอื่นที่สารภาพความผิดของพวกเขา โดยที่พวกเขาประกอบกรรมดีปะปนไปกับงานที่ชั่ว หวังว่าอัลลอฮ์จะทรงอภัยโทษให้แก่พวกเขา แท้จริงอัลลอฮ์นั้นคือ ผู้ทรงอภัยโทษผู้ทรงเอ็นดูเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(9,103,'9:103','(มุฮัมมัด) เจ้าจงเอาส่วนหนึ่งจากทรัพย์สมบัติของพวกเขาเป้นทาน เพื่อทําให้พวกเขาบริสุทธิ์ และล้างมลทินของพวกเขาด้วยส่วนตัวที่เป็นทานนั้น และเจ้าจงขอพรให้แก่พวกเขาเถิด เพราะแท้จริงการขอพรของพวกเจ้านั้น ทําให้เกิดความสุขใจแก่พวกเขา และอัลลอฮ์นั้นเป็นผู้ทรงได้ยิน ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(9,104,'9:104','พวกเขาไม่รู้ดอกหรือว่า แท้จริงอัลลอฮ์นั้นทรงรับการสํานึกผิดจากปวงบ่าวของพระองค์ และทรงรับบรรดาสิ่งที่เป็นทาน(ศ่อดะเกาะฮ์) และแท้จริงอัลลอฮ์นั้นคือ ผู้ทรงอัยโทษ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(9,105,'9:105','จงกล่าวเถิด(มุฮัมมัด) ว่า “พวกท่านจงทํางานเถิด แล้วอัลลอฮ์จะทรงเห็นการงานของพวกท่าน และร่อซูลของพระองค์และบรรดามุอ์มินก็จะเห็นด้วย และพวกท่านจะถูกนํากลับไปยังพระผู้ทรงรอบร็ในสิ่งเร้นลับและสิ่งเปิดเผยแล้วพระองค์จะทรงแจ้งแกาพวกท่าน ในสิ่งที่พวกท่านทําไว้”');
INSERT INTO "king_fahad_thai" VALUES(9,106,'9:106','และมีชนอีกกลุ่มหนึ่ง ที่ยังรอคําบัญชาของอัลลออ์ พระองค์อาจจะทรงลงโทษพวกเขาและพระองค์ก็อาจจะทรงอภัยโทษให้แก่พวกเขาและอัลลอฮ์นั้นเป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(9,107,'9:107','และบรรดาผู้ที่ยึดเอามัสยิดหลังหนึ่งเพื่อก่อให้เกิดความเดือดร้อนและปฉิเสธศรัทธาและก่อให้เกิดการแตกแยกระหว่างบรรดามุอ์มินด้วยกัน และเป็นแหล่งส้องสุมสําหรับผู้ที่ทําสงครามต่อต้านอัลลอฮ์และร่อซุลของพระองค์มาก่อนและแน่นอนพวกเขาจะสาบานว่า เราไม่มีเจตนาอื่นใดนอกจากที่ดี และอัลลอฮ์นั้นทรงเป็นพยานยืนยันว่า แท้จริงพวกเขานั้นเป็นพวกกล่าวเท็จอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(9,108,'9:108','เจ้าอย่าไปร่วมยืนละหมาดในมัสยิดนั้นเป็นอันขาด แน่นอน มัสยิดที่ถูกวางรากฐานบนความยําเกรงตั้งแต่วันแรกนั้นสมควรอย่างยิ่งที่เจ้าจะเข้าไปยืนละหมาดในนั้น เพราะในมัสยิดนั้นมีคณะบุคคลที่ชอบจะชําระตัวให้บริสุทธิ์ และอัลลอฮ์นั้นทรงรักบรรดาผู้ที่ชําระตัวให้สะอาดบริสุทธิ์อยู่เสมอ');
INSERT INTO "king_fahad_thai" VALUES(9,109,'9:109','ผู้ที่วางรากฐานอาคารของเขาบนความยําเกรงต่ออัลลอฮ์ และบนความโปรดปรานนั้นดีกว่าหรือว่าผู้ที่วางรากฐานอาคารของเขาบนริมขอบเหวที่จะพังทลายลง แล้วมันก็พัง นําเขาลงไปในนรกและอัลลอฮ์นั้นจะไม่ชี้แนะทางแก่กลุ่มชนที่อธรรม');
INSERT INTO "king_fahad_thai" VALUES(9,110,'9:110','อาคารของพวกเขาที่ก่อสร้างขึ้นมานั้นยังคงเป็นที่ระแวงสงสัยอยู่ในจิตใจของพวกเขาจนกระทั่งวหัวใจเหล่านั้นจะแตกออกเป็นเสี่ยง ๆ และอัลลอฮ์นั้นเป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(9,111,'9:111','แท้จริงอัลลอฮ์นั้นได้ทรงซื้อแล้วจากบรรดาผู้ศรัทธา ซึ่งชีวิตของพวกเขาและทรัพย์สมบัติของพวกเขา โดยพวกเขาจะได้รับสวนสวรรค์เป็นการตอบแทน พวกเขาจะต่อสู้ในทางของอัลลอฮ์แล้วพวกเขาก็จะฆ่าและถูกฆ่า เป็นสัญญาของพระองค์เองอย่างแท้จริง ซึ่งมีอยู่ในคัมภีร์เตารอฮ์ อินญีล และกรุอาน และใครเล่าจะรักษาสัญญาของเขาให้ดียิ่งไปกว่าอัลลอฮ์ ดังนั้น พวกท่านจงชื่นชมยินดีในการขายของพวกท่านเถิด ซึ่งพวกท่านได้ขายมันไป และนั่นคือชัยชนะอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(9,112,'9:112','บรรดาผู้กลับเนื้อกลับตัว ผู้กระทําการอิบาดดะฮ์ ผู้กล่าวคําสรรเสริญ ผู้เดินทางเพื่อต่อสู้หรือแสวงหาวิชาความรู้ ผู้รุกัวะอ์ ผู้สุญูด ผู้กําชับให้ทําความดี ผู้ห้ามปรามให้ละเว้นความชั่วและบรรดาผุ้รักษาขอบเขตของอัลลอฮ์และจงแจ้งข่าวดีแก่บรรดาผู้ศรัทธาเถิด');
INSERT INTO "king_fahad_thai" VALUES(9,113,'9:113','ไม่บังควรแก่นะบีและบรรดาผู้ศรัทธาที่จะขออภัยโทษให้แก่พวกตั้งภาคี และแม้ว่าพวกเขาจะเป็นญาติใกล้ชิดกันก็ตาม ทั้งนี้หลังจากเป็นที่ประจักษ์แก่พวกเขาแล้ว แน่นอนพวกเหล่านั้นเป็นชาวนรก');
INSERT INTO "king_fahad_thai" VALUES(9,114,'9:114','และการขออภัยโทษของอิบรอฮีมให้แก่บิดาของเขามิได้ปรากฏขึ้น นอกจากเป็นสัญญาที่เขาได้ให้ไว้แก่บิดาของเขาเท่านั้น แต่เมื่อได้เป็นที่ประจักษ์แก่เขาแล้ว แท้จริงบิดาของเขาเป็นศัตรูของอัลลอฮฺ เขาก็ปลีกตัวออกจากบิดาของเขา แท้จริงอิบรอฮีมนั้นเป็นผู้อ่อนโยน และเป็นผู้มีขันติอดทน');
INSERT INTO "king_fahad_thai" VALUES(9,115,'9:115','และอัลลออ์นั้นจะไม่ทรงให้กลุ่มชนใดหลงผิด หลังจากที่พระองค์ได้ทรงชี้ทางที่ถูกต้องให้แก่พวกเขาแล้ว นอกจากจะป็นที่ประจักษ์แก่พวกเขา ซึ่งสิ่งที่พวกเขาจะยําเกรงเท่านั้น แท้จริงอัลลอฮ์นั้นเป็นผู้ทรงรอบรู้ทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(9,116,'9:116','แท้จริงอัลลอฮ์นั้น อํานาจแห่งบรรดาชั้นฟ้าและแผ่นดินเป็นกรรมสิทธิ์ของพระองค์ พระองค์ทรงให้เป็น ทรงให้ตายและนอกจากอัลลอฮ์แล้วก็ไม่มีผู้คุ้มครองและผู้ช่วยเหลือใด ๆ สําหรับพวกท่าน');
INSERT INTO "king_fahad_thai" VALUES(9,117,'9:117','แท้จริงอัลลอฮ์ทรงอภัยโทษให้แก่ท่านนะบี ชาวมุฮาญิรีน และชาวอันศ้อรแล้ว ซึ่งเขาเหล่านั้นได้ปฏิบัติตามเขา (นะบี)ในยามคับขันหลังจากที่จิตใจของชนกลุ่มหนึ่งในพวกเขา เกือบจะหันเหออกจากความจริง แล้วพระองค์ก็ทรงอภัยโทษให้แก่พวกเขา แท้จริงพระองค์นั้นเป็นผู้ทรงเมตตา ผู้ทรงกรุณาอยู่เสมอ');
INSERT INTO "king_fahad_thai" VALUES(9,118,'9:118','และอัลลอฮ์ทรงอภัยโทษให้แก่ชายสามคน (คือ กะอุบ์ อิบนุมาลิก มุรอเราะฮ์ อิบนุ อัรร่อบีอ์ และฮิลาล อิบนุอุมัยยะฮ์) ที่ไม่ได้ออกไปสงคราม จนกระทั่งแผ่นดินได้คับแคบแก่พวกเขาทั้ง ๆ ที่มันกว้างใหญ่ไพศาล และตัวของพวกเขาก็รู้สึกอึดอัดไปด้วย แล้วพวกเขาก็คาดคิดกันว่าไม่มีที่พึ่งอื่นใดเพื่อให้พ้นจากอัลลอฮ์ไปได้ นอกจากกลับไปหาพระองค์ แล้วพระองค์ก็ทรงอภัยโทษให้พ้นจากอัลลอฮ์ไปได้ นอกจากกลับไปหาพระองค์ แล้วพระองค์ก็ทรงอภัยโทษให้แก่พวกเขา เพื่อพวกเขาจะได้กลับเนื้อกลับตัวสํานักผิดต่อพระองค์ แท้จริงอัลลอฮ์นั้นคือผู้ทรงอภัยโทษ ผู้ทรงเอ็นดูเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(9,119,'9:119','โอ้ศรัทธาชนทั้งหลาย พึงยําเกรงอัลลอฮ์เถิด และจงอยู่อยู่ร่วมกับบรรดาผู้ที่พูดจริง');
INSERT INTO "king_fahad_thai" VALUES(9,120,'9:120','ไม่บังคับควรแก่ชาวมะดีนะฮ์และชาวอําหรับชนบทที่พักอยู่รอบ ๆ จะผินหลังให้กับร่อซูลของอัลลอฮ์ และไม่บังคับควรที่เขาเหล่านั้นจะห่วงชีวิตของพวกเขา มากกว่าชีวิตของท่านร่อซูลทั้งนี้เนื่องจากความกระหายน้ําก็ดี ความทุกข์ยากก็ดีและความหิวโหยก็ดี เพื่อหนทางของอัลลอฮ์นั้นจะไม่สบกับพวกเขา และไม่ว่าพวกเขาจะเหยียบย่างไป ณ ที่ใดที่ ทําให้พวกปฏิเสธศรัทธา กริ้วโกรธก็ตาม และพวกเขาจะไม่ได้รับอันตรายจากศัตรูนอกจากการางที่ดีถูกจากรึกไว้แล้วสําหรับพวกเขาเพราะสิ่งนั้น แท้จริงอัลลอฮ์จะไม่ทรงให้รางวัลของผู้กระทําความดีต้องสูญเสียไปเป็นอันขาด');
INSERT INTO "king_fahad_thai" VALUES(9,121,'9:121','และพวกเขาจะไม่บริจาคในการบริจาคใด ๆ ไม่ว่าจะน้อยหรือมากก็ตาม และพวกเขาไม่เดินผ่านหุบเขาใด ๆ นอกจากมันจะถูกบันทึกไว้แก่พวกเขา เพื่อว่าอัลลอฮฺจะทรงตอบแทนให้แก่พวกเขา ซึ่งสิ่งที่ดียิ่งในสิ่งที่พวกเขาได้ทําไว้');
INSERT INTO "king_fahad_thai" VALUES(9,122,'9:122','ไม่บังควรที่บรรดาผู้ศรัทธาจะออกไปสู้รบกันทั้งหมด ทําไมแต่ละกลุ่มในหมู่พวกเขาจึงไม่ออกไปเพื่อหาความเข้าใจในศาสนา และเพื่อจะได้ตักเตือนหมู่คณะของพวกเขา เมื่อพวกเขาได้กลับมายังหมู่คณะของพวกเขา โดยหวังว่าหมู่คณะของพวกเขาจะได้ระมัดระวัง');
INSERT INTO "king_fahad_thai" VALUES(9,123,'9:123','โอ้ศรัทธาชนทั้งหลาย จนสู้รบกับบรรดาผู้ที่อยู่ใกล้เคียงพวกท่าน ที่เป็นผู้ปฏิเสธศรัทธาเสียก่อน และจงให้พวกเหล่านั้นประสบกับความรุนแรงจากพวกท่าน และพึงรู้เถิดว่า แท้จริงอัลลอฮ์นั้นทรงอยู่ร่วมกับบรรดาผู้ยําเกรงทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(9,124,'9:124','และเมื่อมีบทหนึ่งบทใดของอัลกรุอานถูกประทานลงมา ดังนั้น ในหมู่พวกเขามีผู้กล่าวขึ้นว่า “มีใครบ้างในพวกท่านที่บทนี้ทําให้ศรัทธาเพิ่มขึ้น ? “ สําหรับบรรดาผู้ที่มีความศรัทธานั้น บทนี้ได้ทําให้การศรัทธาเพิ่มขึ้นแก่พวกเขา แล้วพวกเขาก็มีความปิติยินดี');
INSERT INTO "king_fahad_thai" VALUES(9,125,'9:125','และสําหรับบรรดาผู้ที่มีโรคอยู่ในจิตใจของพวกเขา บทนี้ก็ยิ่งจะเพิ่มความสกปรกให้แก่พวกเขามากยิ่งขึ้นไปอีก และพวกเขาจะตายไปในสภาพที่เป็นพวกปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(9,126,'9:126','และพวกเขาไม่เห็นดอกหรือว่า แท้จริงพวกเขาจะถูกทดสอบในทุก ๆปี ครั้งหนึ่งหรือสองครั้ง แล้วพวกเขาก็ไม่ยอมกลับเนื้อกลับตัวและพวกเขาก็ไม่สํานึกผิดอีกด้วย');
INSERT INTO "king_fahad_thai" VALUES(9,127,'9:127','และเมื่อบทหนึ่งบทใดของอัล-กรุอานถูกประทานลงมา บางคนในหมู่พวกเขาต่างก็มองตาซึ่งกันและกัน (แล้วถามขึ้นว่า) “มีใครเห็นพวกท่านบ้างไหม” แล้วพวกเขาก็พากันหันเหออกจากแนวทางที่ถูกต้อง เพราะแท้จริงพวกเขาเป็นกลุ่มชนที่ไม่มีความเข้าใจอะไรเลย');
INSERT INTO "king_fahad_thai" VALUES(9,128,'9:128','แท้จริงมีร่อซูลคนหนึ่งจากพวกท่านเองได้มาหาพวกท่านแล้ว เป็นที่ลําบากใจแก่เขาในสิ่งที่พวกท่านได้รับความทุกข์ยาก เป็นผู้ห่วงใยย่าน เป็นผุ้เมตตา ผู้กรุณาสงสาร ต่อบรรดาผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(9,129,'9:129','พากพวกเขาผินหลังให้ ก็จงกล่าวเถิด(มุฮัมมัด) ว่า “อัลลอฮ์นั้นเป็นที่พอเพียงแก่ฉันแล้ว ไม่มีพระเจ้าอื่นใดนอกจากพระองค์เท่านั้น แด่พระองค์เท่านั้นที่ฉันขอมอบหมาย และพระองค์คือเจ้าของบัลลังก์อันยิ่งใหญ่”');
INSERT INTO "king_fahad_thai" VALUES(10,1,'10:1','อะลิฟ ลาม รอ เหล่านี้คือบรรดาโองการ แห่งคัมภีร์ที่ชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(10,2,'10:2','เป็นการประหลาดแก่มนุษย์หรือ ที่เราได้ให้วะฮีย์แก่ชายคนหนึ่งจากพวกเขา ให้เตือนสําทับมนุษย์ และแจ้งข่าวดีแก่บรรดาผู้ศรัทธาว่าแท้จริงสําหรับพวกเขานั้น จะได้รับตําแหน่งอันสูง ณ ที่พระเจ้าของพวกเขา บรรดาผู้ปฏิเสธศรัทธากล่าวว่า “แท้จริงนี่คือนักมายากลอย่างแน่นอน”');
INSERT INTO "king_fahad_thai" VALUES(10,3,'10:3','แท้จริงพระเจ้าของพวกท่านคืออัลลอฮ์ผู้ทรงสร้างชั้นฟ้าทั้งหลายและแผ่นดินในเวลา 6 วันแล้วพระองค์ทรงประทับบนบัลลังก์ ทรงบริหารกิจการ ไม่มีผู้ให้ความช่วยเหลือคนใด เว้นแต่ต้องได้รับอนุมัติจากพระองค์ นั่นคืออัลลอฮ์พระเจ้าของพวกท่าน พวกท่านจงเคารพภักดีต่อพระองค์เถิด พวกท่านมิได้ใคร่ครวญกันดอกหรือ?');
INSERT INTO "king_fahad_thai" VALUES(10,4,'10:4','ยังพระองค์เท่านั้นคือทางกลับของพวกท่านทั้งหลาย สัญญาของอัลลอฮ์นั้นเป็นจริงเสมอแท้จริงพระองค์นั้นทรงเริ่มการสร้าง แล้วพระองค์ก็ทรงให้มันบังเกิดขึ้นอีกครั้งหนึ่ง เพื่อทรงตอบแทนบรรดาผู้ศรัทธา และผู้ประกอบความดี โดยเที่ยงธรรม ส่วนบรรดาผู้ปฏิเสธศรัทธานั้น พวกเขาจะได้รับเครื่องดื่มที่ร้อนจัด และการลงโทษอันเจ็บแสบ เพราะพวกเขาปฏิเสธ ไม่ยอมศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(10,5,'10:5','พระองค์ทรงทําให้ดวงอาทิตย์มีแสงจ้าและดวงจันทร์มีแสงนวล และทรงกําหนดให้มันมีทางโคจร เพื่อพวกท่านจะได้รู้จํานวนปีและการคํานวณ อัลลอฮฺมิได้ทรงสร้างสิ่งเหล่านั้น เว้นแต่ด้วยความจริง พระองค์ทรงจําแนกสัญญาณต่าง ๆ สําหรับหมู่ชนที่มีความรู้');
INSERT INTO "king_fahad_thai" VALUES(10,6,'10:6','แท้จริงการสับเปลี่ยนของกลางคืนและกลางวัน และที่อัลลอฮ์ทรงสร้างในชั้นฟ้าทั้งหลายและแผ่นดินนั้น แน่นอนเป็นสัญญาณแก่กลุ่มชนที่มีความยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(10,7,'10:7','แท้จริงบรรดาผู้ที่ไม่หวังจะพบเรา และพวกเราพอใจต่อชีวิตในโลกดุนยา และพวกเขาดีใจต่อมัน และบรรดาผู้ละเลยต่อสัญญาณต่าง ๆ ของเรา');
INSERT INTO "king_fahad_thai" VALUES(10,8,'10:8','ชนเหล่านั้น ที่พํานักของพวกเขาคือนรกเนื่องด้วยพวกเขาขวนขวายเอาไว้');
INSERT INTO "king_fahad_thai" VALUES(10,9,'10:9','แท้จริงบรรดาผู้ศรัทธาและผู้ประกอบความดี พระเจ้าของพวกเขาจะทรงชี้แนะทางที่ถูกต้องให้แก่พวกเขา เนื่องด้วยการศรัทธาของพวกเขา ภายใต้พวกเขามีแม่น้ําหลายสายไหลผ่านซี่งอยู่ในสวนสวรรค์อันเกษมสําราญ');
INSERT INTO "king_fahad_thai" VALUES(10,10,'10:10','การขอพรของพวกเขาในสวนสวรรค์คือ “มหาบริสุทธิ์แด่พระองค์ท่าน ข้าแต่พระเจ้าของเรา” และคําทักทายของพวกเขาในนั้นคือ “ความสันติสุข” (อัสละลาม) และสุดท้ายแห่งการขอพรของพวกเขาคือ “การสรรเสริญเป็นของอัลลอฮ์ พระเจ้าแห่งสากลโลก”');
INSERT INTO "king_fahad_thai" VALUES(10,11,'10:11','และหากอัลลอฮ์ทรงเร่งตอบรับความชั่วของมนุษย์ เช่นเดียวกับการเร่งของพวกเขาเพื่อขอความดีแล้ว แน่นอนความตายของพวกเขาก็คงถูกกําหนดแก่พวกเขา แล้วเราจะปล่อยบรรดาผู้ที่ไม่หวังจะพบเราให้อยู่ในความงงงวย เพราะการดื้อดึงของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(10,12,'10:12','และเมื่ออันตรายประสบกับมนุษย์ เขาก็จะวิงวอนขอเราในสภาพนอนตะแคง หรือนั่งหรือยืน ครั้นเมื่อเราปลดเปลื้องอันตรายของเขาให้พ้นจากเขาไปแล้ว เขาก็เมินคล้ายกับว่าเขามิได้วิงวอนขอเราให้พ้นจากอันตรายที่ได้ประสบแก่เขา เช่นนั้นแหละ ถูกทําให้สวยงามแก่บรรดาผู้ละเมิดขอบเขตในสิ่งที่พวกเขากระทํา');
INSERT INTO "king_fahad_thai" VALUES(10,13,'10:13','และโดยแน่นอน เราได้ทําลายประชาชาติจากศตวรรษก่อนพวกท่านไปแล้ว เมื่อผู้เขาเป็นผู้อธรรม และบรรดาร่อซูลของพวกเขาได้มายังพวกเขา พร้อมด้วยหลักฐานอันชัดแจ้ง แล้วพวกเขาก็ไม่ศรัทธา เช่นนั้นแหละ เราได้ตอบแทนแก่หมู่ชนที่เป็นอาชญากร');
INSERT INTO "king_fahad_thai" VALUES(10,14,'10:14','แล้วเราก็ได้แต่งตั้งพวกท่านให้เป็นตัวแทนในแผ่นดิน หลังจากพวกเขาเหล่านั้น เพื่อเราจะดูว่าพวกท่านจะปฏิบัติอย่างไร');
INSERT INTO "king_fahad_thai" VALUES(10,15,'10:15','และเมื่อบรรดาโองการอันชัดแจ้งของเราถูกอ่านแก่พวกเขา บรรดาผู้ไม่หวังที่จะพบเรา ก็กล่าวว่า “ท่านจงนํากรุอานอื่นจากนี้มาให้เราหรือเปลี่ยนแปลงเสีย” จงกล่าวเถิดมุฮัมมัด “ไม่บังควรแก่ฉันที่จะเปลี่ยนแปลงโดยพละการจากตัวฉัน ฉันจะไม่ปฏิบัติตาม เว้นแต่สิ่งที่ถูกประทานมาให้แก่ฉันเท่านั้น แท้จริงฉันกลัวว่า หากฉันฝ่าฝืนพระเจ้าของฉันแล้ว จะได้รับการลงโทษในวันอันยิ่งใหญ่”');
INSERT INTO "king_fahad_thai" VALUES(10,16,'10:16','จงกล่าวเถิด(มุฮัมมัด) “หากอัลลอฮ์ทรงประสงค์ ฉันจะไม่อ่านอัลกุรอานแก่พวกท่าน และพระองค์จะไม่ให้พวกท่านได้รู้อัลกุรอานนั้น แน่นอนฉันได้มีอายุอยู่ในหมู่พวกท่านมาก่อนนั้น พวกท่านไม่ใช้สติปัญญาคิดบ้างหรือ?”');
INSERT INTO "king_fahad_thai" VALUES(10,17,'10:17','ดังนั้น ผู้ใดเล่าจะอธรรมยิ่งกว่าผุ้กล่าวเท็จต่ออัลลอฮ์ หรือผู้ปฏิเสธต่อบรรดาโองการของพระองค์ แท้จริงบรรดาผู้ทําผิดนั้นย่อมไม่บรรลุความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(10,18,'10:18','และพวกเขาจะเคารพภักดีสิ่งอื่นไปจากอัลลอฮ์ ที่มิได้ให้โทษแก่พวกเขา และมิได้ให้ประโยชน์แก่พวกเขา และพวกเขาจะกล่าวว่า “เหล่านี้คือผู้ช่วยเหลือเรา ณ ที่อัลลอฮ์” จงกล่าวเถิด(มุฮัมมัด) “พวกท่านจะแจ้งข่าวแก่อัลลอฮ์ด้วยสิ่งที่พระองค์ไม่ทรงรู้ ในบรรดาชั้นฟ้าและในแผ่นดินกระนั้นหรือ? พระองค์ทรงมหาบริสุทธิ์และทรงสูงส่ง เหนือสิ่งที่พวกเขาตั้งภาคีขึ้น”');
INSERT INTO "king_fahad_thai" VALUES(10,19,'10:19','และมนุษย์นั้นไม่ใช่อื่นใด นอกจากเป็นประชาชาติเดียวกัน และพวกเขาก็แตกแยกกัน และหากมิใช่ลิขิตได้บันทึกไว้ที่พระเจ้าของพวกเจ้าแล้วแน่นอนก็คงถูกตัดสินระหว่างพวกเขา ในเรื่องที่พวกเขาขัดแย้งกัน');
INSERT INTO "king_fahad_thai" VALUES(10,20,'10:20','และพวกเขากล่าวว่า “ทําไมอภินิหารจากพระเจ้าของเขา จึงไม่ถูกประทานมาให้เขา” จงกล่าวเถิด (มุฮัมมัด) “แท้จริงสิ่งเร้นลับนั้นเป็นของอัลลอฮ์ พวกท่านจงคอยดูเถิด แท้จริงฉันนั้นอยู่กับพวกท่านในหมู่ผู้คอยดู”');
INSERT INTO "king_fahad_thai" VALUES(10,21,'10:21','และเมื่อเราให้มนุษย์ลิ้มรสความเมตตาหลังจากภยันตรายได้ประสบแก่พวกเขา ดังนั้นพวกเขาก็มีอุบายต่อโองการต่าง ๆ ของเรา จงกล่าวเถิดมุฮัมมัด “อัลลอฮ์ทรงรวดเร็วยิ่งในการแก้อุบาย” แท้จริงบรรดามะลาอิกะฮ์ของเรา จะบันทึกสิ่งที่พวกท่านกําลังทําอุบายอยู่');
INSERT INTO "king_fahad_thai" VALUES(10,22,'10:22','พระองค์ผู้ทรงให้พวกท่านเดินทางโดยทางบกและทางทะเล จนกระทั่งเมื่อพวกท่านอยู่ในเรือและมันได้นําพวกเขาแล่นไปด้วยลมที่ดี และพวกเขาดีใจกับมัน ทันใดนั้นลงพายุได้พัดกระหน่ําและคลื่นซัดเข้ามายังพวกเขา จากทุกด้าน และพวกเขาคิดว่า แท้จริงพวกเขาถูกล้อมด้วยสิ่งเหล่านี้พวกเขาจึงวิงวอนขอต่ออัลลอฮ์ด้วยความบริสุทธิ์ใจต่อพระองค์ว่า “หากพระองค์ทรงให้พวกเราพ้นจากภยันตรายนี้ โดยแน่นอนยิ่ง พวกเราจะอยู่ในหมู่ผู้กตัญญูทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(10,23,'10:23','ครั้นเมื่อเพื่อพระองค์ทรงให้พวกเขารอดมาแล้วพวกเขาก็ทําความเสียหายในแผ่นดิน โดยปราศจากความเป็นธรรม โอ้มนุษย์เอ๋ย แท้จริงการทําความเสียหายของพวกเจ้านั้น มันเป็นอันตรายต่อตัวของพวกเจ้าเอง เป็นความเพลิดเพลินของชีวิตในโลกนี้เท่านั้น แล้วในที่สุดพวกเจ้าก็จะกลับไปหาเรา แล้วเราจะแจ้งข่าวให้พวกเจ้าทราบถึงสิ่งที่พวกเจ้าได้กระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(10,24,'10:24','แท้จริง อุปมาชีวิตในโลกนี้ ดั่งน้ําฝนที่เราได้หลั่งมันลงมาจากฟากฟ้า พืชของแผ่นดินได้คละเคล้ากับน้ํานั้น บางส่วนของมนุษย์และปศุสัตว์ใช้กินเป็นอาหาร จนกระทั่งเมื่อแผ่นดินได้เริ่มปรากฏความงดงามของมัน และถูกประดับด้วยพืชผลอย่างสวยงาม เจ้าของของมันก็คิดว่าแท้จริงพวกเขามีอํานาจเหนือมัน คําบัญชาของเราได้มายังมันในเวลากลางคืนหรือเวลากลางวัน แล้วเราได้ทําให้มันถูกเก็บเกี่ยว เสมือนกับว่าไม่มีการหว่านมาแต่วันวาน เช่นนั้นแหละ เราได้จําแนกโองการต่าง ๆ แก่หมู่ชนผู้ใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(10,25,'10:25','และอัลลอฮ์ทรงเรียกร้องไปสู่สถานที่แห่งศานติ และทรงชี้แนะนวทางที่ถูกต้องแก่ผุ้ที่พระองค์ทรงประสงค์ไปสู่ทางที่เที่ยงธรรม');
INSERT INTO "king_fahad_thai" VALUES(10,26,'10:26','สําหรับบรรดาผู้กระทําความดี จะได้รับความดี และได้เพิ่มขึ้นอีก ความหมองคล้ําและความต่ําต้อยจะไม่ปกคลุมใบหน้าของพวกเขา ชนเหล่านี้คือชาวสวรรค์ พวกเขาจะอยู่ในนั้นตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(10,27,'10:27','และบรรดาผู้ขวนขวายทําความชั่ว การตอบแทนความชั่วด้วยความชั่วเช่นเดียวกัน ความต่ําต้อยจะปกคลุมพวกเขา ไม่มีผู้คุ้มกันพวกเขาให้พ้นจากอัลลอฮ์ได้ เสมือนว่าใบหน้าของพวกเขาถูกคลุมไว้ด้วยส่วนหนึ่งของกลางคืนอันมืดทึบ ชนเหล่านี้คือชาวนรก พวกเขาจะอยู่ในนั้นตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(10,28,'10:28','และวันที่เราชุมนุมพวกเขาทั้งหมด แล้วเราจะกล่าวแก่บรรดาผู้ตั้งภาคีว่า “จงอยู่ ณ สถานที่ของพวกเจ้า พวกเจ้าและบรรดาภาคีของพวกเจ้า” แล้วเราได้แยกพวกเขาออกจากกัน และบรรดาภาคีของพวกเขากล่าวว่า “ไม่ควรเลยที่พวกท่านจะเคารพสักการะต่อเรา !”');
INSERT INTO "king_fahad_thai" VALUES(10,29,'10:29','”ดังนั้น จึงพอเพียงแล้วที่อัลลอฮ์ทรงเป็นพยาน ระกว่างเรากับพวกท่าน แน่นอนเรา(บรรดาภาคี) ไม่รู้เลยในการเคารพสักการะของพวกท่านต่อเรา”');
INSERT INTO "king_fahad_thai" VALUES(10,30,'10:30','ขณะนั้นทุกชีวิตจะถูกสอบถึงสิ่งที่กระทําไว้ก่อน และพวกเขาจะถูกนํากลับไปยังอัลลอฮ์พระเจ้าที่แท้จริงของพวกเขา และสิ่งที่พวกเขาอุปโลกขึ้นมาจะหนีไปจากพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(10,31,'10:31','จงกล่าวเถิด(มุฮัมมัด) “ใครเป็นผู้ประทานปัจจัยยังชีพที่มาจากฟากฟ้า และแผ่นดินแด่พวกท่าน หรือใครเป็นเจ้าของการได้ยินและการมอง และใครเป็นผู้ให้มีชีวิตหลังจากการตายและเป็นผู้ให้ตายหลังจากมีชีวิตมา และใครเป็นผู้บริหารกิจการ” แล้วพวกเขาจะกล่าวกันว่า “อัลลอฮฺ” ดังนั้นจงกล่าวเถิด(มุฮัมมัด) “พวกท่านไม่ยําเกรงหรือ?”');
INSERT INTO "king_fahad_thai" VALUES(10,32,'10:32','นั่นแหละอัลลอฮ์ พระเจ้าที่แท้จริงของพวกท่าน ฉะนั้นหลังจากความจริงแล้วจะมีอะไรอีกเล่า นอกจากความหลงผิดเท่านั้น แล้วทําไมเล่าพวกท่านจึงถูกให้หันเหออกไปอีก?');
INSERT INTO "king_fahad_thai" VALUES(10,33,'10:33','เช่นนั้นแหละ ลิขิตของพระเจ้าของเจ้าย่อมเป็นจริงแก่บรรดาผู้ที่ฝ่าฝืนว่า แน่แท้พวกเขาจะไม่ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(10,34,'10:34','จงกล่าวเถิด(มุฮัมมัด) “มีใครบ้างในหมู่ภาคีของพวกท่าน ที่เป็นผู้เริ่มแรกในการให้บังเกิดอีก?” จงกล่าวเถิด(มุฮัมมัด)”อัลลอฮ์ทรงเริ่มแรกในการให้บังเกิด แล้วทรงให้มันบังเกิดอีกครั้งหนึ่ง ดังนั้นทําไมพวกท่านจึงหันเหออกจากความจริงไป (สู่ความเท็จ) เล่า?”');
INSERT INTO "king_fahad_thai" VALUES(10,35,'10:35','จงกล่าวเถิด(มุฮัมมัด) “มีใครบ้างในหมู่ภาคีของพวกท่าน เป็นผู้ชี้แนะทางสู่สัจธรรม?” จงกล่าวเถิด(มุฮัมมัด)“อัลลอฮ์ทรงชี้แนะทางสู่สัจธรรม ดังนั้นผู้ที่ชี้แนะทางสู่สัจธรรมสมควรกว่าที่จะได้รับการปฏิบัติตาม(อิบาดะฮ์)หรือว่าผู้ที่ไม่อาจจะชี้แนะผู้อื่นได้ เว้นแต่จะถูกชี้แนะ ทําไมพวกท่านจึงตัดสินใจเช่นนั้น?”');
INSERT INTO "king_fahad_thai" VALUES(10,36,'10:36','และส่วนใหญ่ของพวกเขามิได้ปฏิบัติตามสิ่งใด นอกจากการนึกคิด แท้จริงการนึกคิดนั้นไม่อาจจะแทนความจริงได้แต่อย่างใด แท้จริงอัลลอฮ์นั้นทรงรอบรู้ในสิ่งที่พวกเขากระทํา');
INSERT INTO "king_fahad_thai" VALUES(10,37,'10:37','และอัลกุรอานนี้มิใช่จะถูกปั้นแต่งขึ้นโดยผู้ใดนอนจากอัลลอฮ์ แต่เป็นการยืนยันคัมภีร์ที่มีมาก่อน และเป็นการจําแนก ข้อบัญญัติต่าง ๆ ในนั้น ไม่มีข้อสงสัยในคัมภีร์นั้น ซึ่งมาจากพระเจ้าแห่งสากลโลก');
INSERT INTO "king_fahad_thai" VALUES(10,38,'10:38','หรือพวกเขากล่าว่า “เขา(มุฮัมมัด) เป็นผู้ปั้นแต่งขึ้น” จงกล่าวเถิด(มุฮัมมัด) “พวกท่านจงนํากลับมาสักบทหนึ่งเยี่ยงนั้น และจงเรียกร้องผู้ที่พวกท่านสามารถนํามาได้ นอกจากอัลลอฮ์ หากพวกท่านเป็นผู้สัจจริง”');
INSERT INTO "king_fahad_thai" VALUES(10,39,'10:39','แต่ว่าพวกเขาปฏิเสธสิ่งที่พวกเขายังไม่รู้มาก่อน และสัญญาร้ายยังมิได้มายังพวกเขา เช่นนั้นแหละ บรรดาชนรุ่นก่อนจากพวกเขาได้ปฏิเสธมาแล้ว ดังนั้น เจ้าจงดูเถิดว่า ผลสุดท้ายของพวกอธรรมนั้นเป็นอย่างไร?');
INSERT INTO "king_fahad_thai" VALUES(10,40,'10:40','และในหมู่พวกเขามีศรัทธาในอัลกุรอานและในหมู่พวกเขามีผู้ไม่ศรัทธา และพระเจ้าของเจ้าทรงรู้ดียิ่ง ต่อบรรดาผู้บ่อนทําลายทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(10,41,'10:41','และถ้าพวกเขาปฏิเสธ(ไม่ยอมศรัทธา)จงกล่าวเถิด (มุฮัมมัด) “การงานของฉันก็เป็นของฉัน และการงานของพวกท่านก็เป็นของพวกท่าน พวกท่านจงปลีกตัวออกจากสิ่งที่ฉันกระทําและฉันก็จะปลีกตัวออกจากสิ่งที่พวกท่านกระทํา”');
INSERT INTO "king_fahad_thai" VALUES(10,42,'10:42','และในหมู่พวกเขามีผู้ฟัง (การอ่านอัลกุรอาน)ของเจ้า เจ้าจะให้คนหูหนวกได้ยินกระนั้นหรือ? และถึงแม้พวกเขาหูหนวก แต่ก็ไม่ใช้ปัญญา');
INSERT INTO "king_fahad_thai" VALUES(10,43,'10:43','และในหมู่พวกเขามีผู้มองไปยังเจ้า เจ้าจะชี้แนะทางให้คนตาบอดกระนั้นหรือ? และถึงแม้พวกเขาตาบอดแต่ก็ไม่มีสายตาที่จะมองดู');
INSERT INTO "king_fahad_thai" VALUES(10,44,'10:44','แท้จริงอัลลอฮ์นั้นจะไม่ทรงอธรรมแก่มนุษย์แต่อย่างไร แต่ว่ามนุษย์ต่างหากที่อธรรมต่อตัวของพวกเขาเอง');
INSERT INTO "king_fahad_thai" VALUES(10,45,'10:45','และวันที่พระองค์ทรงชุมนุมพวกเขาประหนึ่งว่าพวกเขามิได้พํานักอยู่นาน(ในโลกนี้)เว้นแต่เพียงชั่วครู่เดียวในเวลากลางวัน พวกเขาทักทายซึ่งกันและกัน แน่นอนบรรดาผู้ปฏิเสธต่อการพบอัลลอฮ์ย่อมขาดทุน และพวกเขามิได้เป็นผู้ที่อยู่ในแนวทางที่ถูกต้อง');
INSERT INTO "king_fahad_thai" VALUES(10,46,'10:46','และบางทีเราจะให้เจ้าได้เห็นบางส่วน(ของการลงโทษ) ซึ่งเราสัญญาแก่พวกเขา หรือเราจะให้เจ้าตายเสียก่อนดังนั้น ทางกลับของพวกเขาย่อมไปหาเรา แล้วอัลลอฮ์ทรงเป็นพยานต่อสิ่งที่พวกเขากระทํา');
INSERT INTO "king_fahad_thai" VALUES(10,47,'10:47','และทุกประชาชาติมีร่อซูลถูกส่งมา ดังนั้นเมื่อร่อซูลของพวกเขาได้มาแล้ว กิจการระหว่างพวกเขาก็ถูกตัดสินโดยเที่ยงธรรม และพวกเขาจะไม่ถูกอธรรม');
INSERT INTO "king_fahad_thai" VALUES(10,48,'10:48','และพวกเขาจะกล่าวว่า “เมื่อใดเล่าสัญญานี้(จะปรากฏ) หากพวกท่านสัจจริง?”');
INSERT INTO "king_fahad_thai" VALUES(10,49,'10:49','จงกล่าวเถิด (มุฮัมมัด) “ฉันไม่มีอํานาจที่จะให้โทษและให้คุณแก่ตัวฉัน เว้นแต่ที่อัลลอฮ์ทรงประสงค์ สําหรับทุกประชาชาติย่อมมีเวลากําหนด เมื่อเวลาของพวกเขามาถึง พวกเขาจะขอผ่อนผันให้ล่าช้าซักระยะหนึ่งไม่ได้ และจะร่นเวลาให้เร็วเข้าก็ไม่ได้"');
INSERT INTO "king_fahad_thai" VALUES(10,50,'10:50','จงกล่าวเถิด(มุฮัมมัด) “พวกท่านเห็นแล้วมิใช่หรือ? หากการลงโทษของพระองค์ประสบแก่พวกท่าน ในเวลากลางคืน หรือเวลากลางวัน ทําไมพวกอาชญากรเหล่านั้นจึงขอร่นเวลา!”');
INSERT INTO "king_fahad_thai" VALUES(10,51,'10:51','ครั้นเมื่อมันเกิดขึ้น พวกท่านก็ศรัทธาต่อพระองค์กระนั้นหรือ? ขณะนี้ (พวกท่านศรัทธา)ก่อนหน้านั้นพวกท่าน(เยาะเย้ย) ขอร่นเวลา');
INSERT INTO "king_fahad_thai" VALUES(10,52,'10:52','แล้วมีเสียงกล่าวแก่พวกอธรรมว่า “พวกท่านจงลิ้มรสการลงโทษอันจีรังเถิด พวกท่านจะไม่ถูกตอบแทน เว้นแต่สิ่งที่พวกท่านขวนขวายไว้เท่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(10,53,'10:53','และพวกเขาจะสอบถามเจ้าว่า “(การลงโทษ) จะเกิดขึ้นจริงหรือ?” จงกล่าวเถิด (มุฮัมมัด) “แน่นอนทีเดียว ขอสาบานต่อพระเจ้าของฉัน แท้จริงมันจะเกิดขึ้นอย่างแน่นอน และพวกท่านไม่สามารถจะรอดไปได้”');
INSERT INTO "king_fahad_thai" VALUES(10,54,'10:54','หากทุกชีวิตที่อธรรม (ครอบครองคลังสมบัติ) ที่อยู่ในแผ่นดินมันก็จะยอมไถ่ตน และพวกเขาก็จะซ่อนความเสียใจเมื่อได้เห็นการลงโทษ และถูกตัดสินระหว่างพวกเขาอย่างเที่ยงธรรม และพวกเขาจะไม่ถูกอธรรมแต่อย่างใด');
INSERT INTO "king_fahad_thai" VALUES(10,55,'10:55','พึงทราบเถิด ! แท้จริงในชั้นฟ้าทั้งหลายและแผ่นดินเป็นกรรมสิทธิ์ของอัลลอฮฺ พึงทราบเถิด ! แท้จริงสัญญาของอัลลอฮฺนั้นจะเกิดขึ้นจริง แต่ส่วนใหญ่ของพวกเขาไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(10,56,'10:56','พระองค์ทรงให้เป็นและให้ตาย และยังพระองค์เท่านั้นพวกท่านจะได้ถูกนํากลับไป');
INSERT INTO "king_fahad_thai" VALUES(10,57,'10:57','โอ้มนุษย์เอ๋ย ! แท้จริงข้อตักเตือน (อัลกุรอาน) จากพระเจ้าของพวกท่าน ได้มายังพวกท่านแล้ว และ(มัน) เป็นการบําบัดสิ่งที่มีอยู่ในทรวงอก และเป็นการชี้แนะทาง และเป็นความเมตตาแก่บรรดาผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(10,58,'10:58','จงกล่าวเถิด (มุฮัมมัด) “ด้วยความโปรดปรานของอัลลอฮ์ และด้วยความเมตตาของพระองค์ ดังกล่าวนั้นพวกเขาจงดีใจเถิด” ซึ่งมันดียิ่งกว่าสิ่งที่พวกเขาสะสมไว้');
INSERT INTO "king_fahad_thai" VALUES(10,59,'10:59','จงกล่าวเถิด (มุฮัมมัด) “พวกท่านเห็นแล้วมิใช่หรือ ซึ่งเครื่องยังชีพที่อัลลอฮ์ทรงประทานให้แก่พวกท่านแล้วพวกท่านก็ทําให้บางส่วนเป็นที่ต้องห้าม (หะรอม) และบางส่วนเป็นที่อนุมัติ(หะลาล)” จงกล่าวเถิด (มุฮัมมัด)“อัลลอฮ์ทรงอนุมัติให้แก่พวกท่าน หรือพวกท่านปั้นแต่งให้แก่อัลลอฮ์”');
INSERT INTO "king_fahad_thai" VALUES(10,60,'10:60','และบรรดาผู้ที่ปั้นแต่งความเท็จให้แก่อัลลอฮ์ จะนึกคิดอย่างไรในวันกิยามะฮ์ แท้จริงอัลลอฮ์นั้นทรงเป็นผู้มีบุญคุณต่อมนุษย์ แต่ว่าส่วนใหญ่ของพวกเขาไม่ขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(10,61,'10:61','และเจ้ามิได้อยู่ในเรื่องหนึ่งเรื่องใด และเจ้ามิได้อ่านบางส่วนจากมันในอัลกุรอาน และพวกท่านมิได้กระทําการใดๆ เว้นแต่เราได้เป็นพยานแก่พวกท่าน ในขณะที่พวกท่านกําลังง่วงอยู่ในเรื่องนั้น และจะไม่รอดพ้นจากพระเจ้าของเจ้า (การกระทําใด ๆ) ที่มีน้ําหนักเท่าธุลี ทั้งในแผ่นดินและในชั้นฟ้า และที่เล็กกว่านั้นและที่ใหญ่กว่านั้นเว้นแต่อยู่ในบันทึกอันชัดแจ้งทั้งสิ้น');
INSERT INTO "king_fahad_thai" VALUES(10,62,'10:62','พึงทราบเถิด ! แท้จริง บรรดาคนที่อัลลอฮ์รักนั้น ไม่มีความหวาดกลัวใด ๆ แก่พวกเขาและพวกเขาจะไม่เศร้าโศกเสียใจ');
INSERT INTO "king_fahad_thai" VALUES(10,63,'10:63','คือบรรดาผู้ศรัทธา และพวกเขามีความยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(10,64,'10:64','สําหรับพวกเขาจะได้รับข่าวดี ในการมีชีวิตอยู่ในโลกนี้และในโลกหน้า ไม่มีการเปลี่ยนแปลงในลิขิตของอัลลอฮ์ นั่นคือชัยชนะอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(10,65,'10:65','และคําพูดของพวกท่าน จะไม่ทําให้เจ้าเสียใจ แท้จริงอํานาจทั้งมวลนั้นเป็นของอัลลอฮ์พระองค์เป็นผู้ทรงได้ยิน ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(10,66,'10:66','พึงทราบเถิด! แท้จริงทุกสิ่งในชั้นฟ้าทั้งหลายและทุกสิ่งในแผ่นดินนั้น เป็นของอัลลอฮ์ และบรรดาผู้วิงวอนขอสิ่งอื่นจากอัลลอฮ์นั้น จะไม่ปฏิบัติตามภาคีเหล่านั้น พวกเขาจะไม่ปฏิบัติตาม เว้นแต่การคิดเท่านั้น และพวกเขามิได้ตั้งอยู่บนสิ่งใด นอกจากจะคาดคะเนขึ้น');
INSERT INTO "king_fahad_thai" VALUES(10,67,'10:67','พระองค์ผู้ทรงบันดาลกลางคืนให้แก่พวกท่าน เพื่อพวกท่านจะได้พักผ่อนในมัน แบะกลางวันเพื่อจะได้มองเห็น แท้จริงในการนั้นแน่นอนย่อมเป็นสัญญาณแก่หมู่ชนที่ได้ยิน เพื่อใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(10,68,'10:68','พวกเขากล่าวว่า “อัลลอฮฺทรงแต่งตั้งพระบุตร” มหาบริสุทธิ์พระองค์ท่าน ทรงพอเพียงจากสิ่งใด ๆ สิ่งที่อยู่ในชั้นฟ้าทั้งหลาย และสิ่งที่อยู่ในแผ่นดินเป็นของพระองค์ พวกท่านไม่มีหลักฐานใด ๆ ในการกล่าวเช่นนี้ พวกท่านจะกล่าวร้ายต่ออัลลอฮฺในสิ่งที่พวกท่านไม่รู้กระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(10,69,'10:69','จงกล่าวเถิด(มุฮัมมัด) “แท้จริงบรรดาผู้กล่าวเท็จต่ออัลลอฮ์นั้น พวกเขาจะไม่ประสบความสําเร็จดอก!”');
INSERT INTO "king_fahad_thai" VALUES(10,70,'10:70','ความเพลิดเพลินในโลกนี้ แล้วพวกเขาก็กลับคืนมาสู่เรา แล้วเราจะให้พวกเขาลิ้มรสการลงโทษอย่างหนัก เพระเหตุที่พวกเขาปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(10,71,'10:71','และเจ้าจงอ่านให้พวกเขาฟังถึงเรื่องราวของนะบีนูห์ เมื่อเขา(นูห์) กล่าวแก่ประชาชาติของเขาว่า “โอ้หมู่ชนของฉัน ! หากว่าการพักอยู่ของฉันและการตัดเตือนของฉัน ด้วยโองการทั้งหลายของอัลลอฮ์ เป็นเรื่องใหญ่แก่พวกท่านแล้ว ดังนั้นฉันขอมอบหมายแด่อัลลอฮ์เท่านั้นพวกท่านจงร่วมกันวางแผนของพวกท่าน พร้อมกับบรรดาภาคีของพวกท่านเถิด แล้วอย่าให้แผนของพวกท่านเป็นที่ปิดบังแก่พวกท่าน แล้วจงดําเนินการต่อฉันทันทีและอย่าได้ลังเลเลย');
INSERT INTO "king_fahad_thai" VALUES(10,72,'10:72','“หากพวกท่านผินหลังให้ ฉันมิได้ขอค่าตอบแทนใดๆ จากพวกท่าน แต่รางวัลของฉันอยู่ที่อัลลอฮฺ และฉันถูกใช้ให้อยู่ในหมู่ผู้นอบน้อม”');
INSERT INTO "king_fahad_thai" VALUES(10,73,'10:73','แล้วพวกเขาก็ปฏิเสธเขา (นูหฺ) เราได้ช่วยให้เขาและผู้อยู่กับเขารอดพ้นไว้ในเรือ และเราได้ให้พวกเขาเป็นตัวแทน (ในเวลาต่อมา) และเราได้ให้บรรดาผู้ปฏิเสธโองการทั้งหลายของเราจมน้ําดังนั้น เจ้าจงดูเถิดว่า ผลสุดท้ายของพวกที่ถูกเตือนนั้นเป็นอย่างไร?');
INSERT INTO "king_fahad_thai" VALUES(10,74,'10:74','หลังจากเขา (นูห์) แล้ว เราได้ส่งบรรดาร่อซูลไปยังประชาชาติของพวกเขา แล้วบรรดาร่อซูลเหล่านั้นได้นําหลักฐานอย่างชัดแจ้งมายังพวกเขา แต่พวกเขามิได้ศรัทธาในสิ่งที่พวกเขาได้เคยปฏิเสธต่อนูหฺมาก่อนแล้ว เช่นนั้นแหละ เราได้ประทับตราบนหัวใจของบรรดาผู้ฝ่าฝืนเหล่านั้น');
INSERT INTO "king_fahad_thai" VALUES(10,75,'10:75','หลังจากพวกเขาเหล่านั้นแล้ว เราได้ส่งมูซาและฮารูนไปยังพิรเอาน์และบรรดาผู้นําของเขา ด้วยสัญญาณทั้งหลายของเรา พวกเขาก็เย่อหยิ่งโอหัง โดยพวกเขาเป็นกลุ่มชนที่มีความผิด');
INSERT INTO "king_fahad_thai" VALUES(10,76,'10:76','ครั้นเมื่อความจริงจากเราได้มายังพวกเขาแล้ว พวกเขากล่าวว่า “แท้จริงนี่คือวิทยากลอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(10,77,'10:77','มูซาได้กล่าวว่า “พวกท่านกล่าวร้ายต่อความจริง เมื่อมันได้มายังพวกท่านเช่นนั้นหรือ? นี่หรือวิทยากล และนักวิทยากล และนักวิทยากลนั้นจะไม่ประสบความสําเร็จดอก!”');
INSERT INTO "king_fahad_thai" VALUES(10,78,'10:78','พวกเขากล่าวว่า “ท่านมาหาเราเพื่อที่จะหันเหเรา ออกจากสิ่งที่เราได้พบเห็น (ศาสนา) ของบรรพบุรุษของเราและเพื่อที่ความยิ่งใหญ่ในแผ่นดินจะได้เป็นของท่านทั้งสองกระนั้นหรือ และเราจะไม่ศรัทธาต่อท่านทั้งสองเป็นแน่!”');
INSERT INTO "king_fahad_thai" VALUES(10,79,'10:79','และฟิรเอาน์ได้กล่าวว่า “พวกท่านจงนํามาให้ฉัน นักวิทยากลผู้เชี่ยวชาญทุกคน”');
INSERT INTO "king_fahad_thai" VALUES(10,80,'10:80','เมื่อนักวิทยากลมาแล้ว มูซาได้กล่าวกับพวกเขาว่า “พวกท่านจงโยนสิ่งที่พวกท่านนํามาเพื่อจะโยนเถิด”');
INSERT INTO "king_fahad_thai" VALUES(10,81,'10:81','เมื่อพวกเขาได้โยนไปแล้ว มูซาได้กล่าวว่า “สิ่งที่พวกท่านนํามานั้นคือวิทยากล แท้จริงอัลลอฮ์จะทรงทําลายมัน แท้จริงอัลลอฮ์จะทรงทําลายมัน แท้จริงอัลลอฮ์จะไม่ทรงทําให้การงานของบรรดาผู้บ่อนทําลายดีขึ้น”');
INSERT INTO "king_fahad_thai" VALUES(10,82,'10:82','“และอัลลอฮ์จะทรงให้สัจธรรมยืนหยัดอยู่ ด้วยคํากล่าวของพระองค์ และแม้ว่าบรรดาคนชั่วจะเกลียดชังก็ตาม');
INSERT INTO "king_fahad_thai" VALUES(10,83,'10:83','ไม่มีใครศรัทธาต่อมูซา นอกจากลูกหลานบางคนจากกลุ่มชนของเขา (บะนีอิสรออีล) เนื่องจากความกลัวต่อฟิรเอาน์ และหัวหน้าของพวกเขา จะทําความวุ่นวายแก่พวกเขา และแท้จริงฟิรเอาน์นั้นเป็นเป็นผู้หยิ่งผยองในแผ่นดิน และแท้จริงเขาอยู่ในหมู่ผู้ละเมิด');
INSERT INTO "king_fahad_thai" VALUES(10,84,'10:84','และมูซากล่าวว่า “โอ้กลุ่มชนของฉัน! หากพวกท่านศรัทธาต่ออัลลอฮ์ พวกท่านก็จงมอบหมายต่อพระองค์ หากพสกท่านเป็นผู้ยอมจํานน');
INSERT INTO "king_fahad_thai" VALUES(10,85,'10:85','พวกเขากล่าวว่า “แด่อัลลอฮ์เราขอมอบหมาย ข้าแต่พระเจ้าของเราได้ทรงโปรดอย่าให้เราเป็นเครื่องทดลองสําหรับหมู่ชนผู้อธรรมเลย”');
INSERT INTO "king_fahad_thai" VALUES(10,86,'10:86','“และได้ทรงโปรดช่วยเราให้พ้นจากหมู่ชนผู้ปฏิเสธศรัทธา ด้วยพระเมตตาของพระองค์ด้วยเถิด”');
INSERT INTO "king_fahad_thai" VALUES(10,87,'10:87','และเราได้วะฮีย์มายังมูซาและพี่ชายของเขา(ฮารูน) ให้จัดสร้างบ้านให้แก่กลุ่มชนของเจ้าทั้งสองในอียิปต์ และจงทําบ้านของพวกท่านเป็นกิบละฮ์ และจงดํารงค์การละหมาด และจงแจ้งข่าวดีแก่บรรดาผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(10,88,'10:88','และมูซาได้กล่าวว่า “ข้าแต่พระเจ้าของเรา แท้จริงพระองค์ทรงประทานความสําราญและทรัพย์สิน แก่ฟิรเอาน์และหัวหน้าของเขา ในการมีชีวิตอยู่ในโลกนี้ ข้าแต่พระเจ้าของเรา โดยพวกเขาจะทําให้(กลุ่มชน) หลงจากแนวทางของพระองค์ ข้าแต่พระเจ้าของเรา ขอพระองค์ทรงทําลายทรัพย์สินของพวกเขา และทรงโปรดทําให้หัวใจของพวกเขาแข็งกระด้าง เพื่อมิให้พวกเขาศรัทธา จนกว่าพวกเขาจะเห็นการลงโทษอย่างเจ็บปวด”');
INSERT INTO "king_fahad_thai" VALUES(10,89,'10:89','พระองค์ตรัสว่า “การวิงวอนของเจ้าทั้งสองถูกรับแล้ว เจ้าทั้งสองจงดําเนินตามแนวทางที่เที่ยงธรรม และอย่าปฏิบัติตามแนวทางของบรรดาผู้ไม่รู้”');
INSERT INTO "king_fahad_thai" VALUES(10,90,'10:90','และเราได้ให้บะนีอิสรออีลข้ามทะเลพ้นไป ดังนั้น ฟิรเอาน์และพลพรรคของเขาได้ติดตามพวกเขา (บะนีอิสรออีล) ไปโดยอธรรมและเป็นศัตรู จนกระทั่งเมื่อการจมน้ํามาถึงเขาแล้ว เขากล่าวว่า “ฉันศรัทธาแล้วว่า แท้จริงไม่มีพระเจ้าอื่นใด นอกจากผู้ซึ่งบะนีอิสรออีลได้ศรัทธาต่อพระองค์ และฉันคือคนหนึ่งในหมู่ผู้นอบน้อม”');
INSERT INTO "king_fahad_thai" VALUES(10,91,'10:91','บัดนี้ และแน่นอนเจ้าเป็นผู้ทรยศก่อนหน้านี้ และเจ้าเป็นคนหนึ่งในหมู่ผู้บ่อนทําลาย');
INSERT INTO "king_fahad_thai" VALUES(10,92,'10:92','ดังนั้น วันนี้เราจะให้ร่างของเจ้าออกจากทะเล เพื่อจักได้เป็นสัญญาณแก่ชนรุ่นหลังจากเจ้า และแท้จริงส่วนใหญ่ของมนุษย์เฉยเมยต่อสัญญาณต่างๆ ของเรา');
INSERT INTO "king_fahad_thai" VALUES(10,93,'10:93','และโดยแน่นอน เราได้ให้บะนีอิสรออีลพํานักอาศัยอยู่ ณ สถานที่อันดี และเราได้ให้ปัจจัยยังชีพที่ดีมากมายแก่พวกเขา ดังนั้น พวกเขามิได้แยกแตกกัน จนกระทั่งคัมภีร์ได้มายังพวกเขา แท้จริงพระเจ้าของเจ้าจะทรงตัดสินระหว่างพวกเขาในวันกิยามะฮ์ ในสิ่งที่พวกเขาขัดแข้งกัน');
INSERT INTO "king_fahad_thai" VALUES(10,94,'10:94','หากเจ้าอยู่ในการสงสัย ในสิ่งที่เราได้ให้แก่เจ้า ก็จงถามบรรดาผู้อ่านคัมภีร์ก่อนเจ้า(เตารอฮ์) โดยแน่นอนสัจธรรมได้มายังเจ้าจากพระเจ้าของเจ้า ดังนั้นเจ้าจงอย่าอยู่ในหมู่ผู้สงสัย');
INSERT INTO "king_fahad_thai" VALUES(10,95,'10:95','และเจ้าอย่าเป็นเช่นบรรดาผู้ปฏิเสธโองการทั้งหลายของอัลลอฮ์ ดังนั้นเจ้าจะอยู่ในหมู่ผู้ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(10,96,'10:96','แท้จริง บรรดาผู้ที่พระดํารัส(การลงโทษ) ของพระเจ้าของเจ้า ได้บัญญัติแก่พวกเขาแล้วพวกเขาจะไม่ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(10,97,'10:97','ปละแม้ว่าทุกสัญญาณได้มายังพวกเขาจนกว่าพวกเขาจะแลเห็นการลงโทษอย่างเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(10,98,'10:98','ทําไมถึงไม่มีหมู่บ้านซักแห่งหนึ่งศรัทธา โดยที่การศรัทธาของพวกเขาจะอํานวยประโยชน์แก่พวกเขา นอกจากกลุ่มชนของยูนุสเมื่อพวกเขาศรัทธา เราได้ปลดเปลื้องการลงโทษอันอัปยศจากพวกเขา ในการมีชีวิตในโลกนี้และเราได้ยึดเวลาระยะหนึ่ง แก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(10,99,'10:99','และหากพระเจ้าของเจ้าจงประสงค์แน่นอนผู้ที่อยู่ในแผ่นดินทั้งมวลจะศรัทธา เจ้าจะบังคับมวลชนจนกว่าพวกเจ้าจะเป็นผู้ศรัทธากระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(10,100,'10:100','และมิเคยปรากฏว่าชีวิตใดจะศรัทธาเว้นแต่ด้วยอนุมัติของอัลลอฮ์ และพระองค์จะทรงลงโทษแก่บรรดาผู้ไม่ใช้สติปัญญา');
INSERT INTO "king_fahad_thai" VALUES(10,101,'10:101','จงกล่าวเถิด (มุฮัมมัด) “พวกท่านจงดูว่ามีอะไรในชั้นฟ้าทั้งหลายและแผ่นดิน” และสัญญาณทั้งหลาย และการตักเตือนทั้งหลาย จะไม่อํานวยผลแก่กลุ่มชนที่ไม่ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(10,102,'10:102','พวกเขาจะไม่คอยดูสิ่งใด ๆ นอกจากการคอยดูเยี่ยงวันทั้งหลายของบรรดาผู้ที่ล่วงลับไปก่อนพวกเขา จงกล่าวเถิด (มุฮัมมัด) “พวกท่านจงคอยดูเถิด แท้จริงฉันจะเป็นคนหนึ่งในหมู่ผู้คอยดู”');
INSERT INTO "king_fahad_thai" VALUES(10,103,'10:103','แล้วเราจะช่วยบรรดาร่อซูลของเราและบรรดาผู้ศรัทธาให้รอดพ้น เช่นนั้นแหละเป็นหน้าที่เรา ที่เราจะช่วยเหลือบรรดามุอ์มินให้รอดพ้น');
INSERT INTO "king_fahad_thai" VALUES(10,104,'10:104','จงกล่าวเถิด(มุฮัมมัด) “โอ้มนุษย์เอ๋ย ! หากพวกท่านสงสัยในศาสนาของฉัน ดังนั้นฉันจะไม่เคารพภักดีต่อบรรดาที่พวกท่านเคารพภักดีอื่นจากอัลลอฮ์ แต่ฉันจะเคารพภักดีอัลลอฮ์ผู้ทรงทําให้พวกท่านตาย และฉันได้รับบัญชาให้เป็นคนหนึ่งในหมู่ผู้ศรัทธา”');
INSERT INTO "king_fahad_thai" VALUES(10,105,'10:105','และว่า จงมุ่งหน้าของเจ้าเพื่อศาสนาอย่างเที่ยงตรง และอย่าอยู่ในหมู่ผู้ตั้งภาคี');
INSERT INTO "king_fahad_thai" VALUES(10,106,'10:106','และเจ้าอย่าวิงวอนสิ่งใดอื่นจากอัลลอฮ์ที่ไม่อํานวยประโยชน์แก่เจ้า และไม่ให้โทษแก่เจ้า หากเจ้ากระทําเช่นนั้น แท้จริงเจ้าจะอยู่ในหมู่ผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(10,107,'10:107','และหากอัลลอฮฺจะทรงให้ทุกข์ภัย ประสบแก่เจ้าแล้ว ก็ไม่มีผู้ใดปลดเปลื้องมันได้ นอกจากพระองค์ และหากพระองค์ทรงปรารถนาความดีแก่เจ้าแล้ว ก็จะไม่มีผู้ใดกีดกันความโปรดปรานของพระองค์ได้ พระองค์จะทรงให้ประสบแก่ผู้ที่พระองค์ทรงประสงค์จากปวงบ่าวของพระองค์ และพระองค์จะเป็นผู้ทรงอภัยโทษ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(10,108,'10:108','จงกล่าวเถิด (มุฮัมมัด) “โอ้มนุษย์เอ๋ย ! แน่นอนสัจธรรม จากพระเจ้าของพวกท่านได้มายังพวกท่านแล้ว ดังนั้นผู้ใดปฏิบัติตามแนวทางถูกต้อง แท้จริงเขาดําเนินตามแนวทางถูกต้องเพื่อตัวของเขา และผุ้ใดหลงทางแท้จริงเขาก็หลงทางเพื่อตัวของเขา และฉันไม่ได้เป็นผู้คุ้มกันพวกท่าน”');
INSERT INTO "king_fahad_thai" VALUES(10,109,'10:109','และเจ้าจงปฏิบัติตามที่ถูกวะฮีย์แก่เจ้าและจงอดทน จนกว่าอัลลอฮ์จะทรงตัดสิน และพระองค์ทรงเป็นผู้ตัดสินที่ดียิ่ง');
INSERT INTO "king_fahad_thai" VALUES(11,1,'11:1','อะลีฟ ลาม รอ คัมภีร์ที่โองการทั้งหลายของมันถูกทําให้รัดกุมมีระเบียบ แล้วถูกจําแนกเรื่องต่างๆ อย่างชัดแจ้ง จากพระผู้ทรงปรีชาญาณ ผู้ทรงรอบรู้เชี่ยวชาญ');
INSERT INTO "king_fahad_thai" VALUES(11,2,'11:2','เพื่อพวกท่านต้องไม่เคารพภักดีผู้ใดนอกจากอัลลอฮ์ แท้จริงฉันได้รับการแต่งตั้งจากพระองค์มายังพวกท่าน เพื่อเป็นผู้ตักเดือนและผู้แจ้งข่าว');
INSERT INTO "king_fahad_thai" VALUES(11,3,'11:3','และพวกท่านจงขอนิรโทษจากพระเจ้าของพวกท่าน แล้วจงกลับเนื้อกลับตัวต่อพระองค์ พระองค์จะทรงหใปัจจัยแก่พวกท่านซึ่งปัจจัยที่ไปจนถึงวาระหนึ่งที่กําหนดไว้ และพระองค์จะทรงประทานแก่ทุก ๆ ผู้ทําความดีซึ่งความดีของเขาและหากพวกท่านผินหลังให้ แท้จริงฉันกลัวแทน พวกท่านซึ่งการลงโทษในวันอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(11,4,'11:4','การกลับของพวกท่านย่อมไปสู่อัลลอฮ์และพระองค์เป็นผู้ทรงอนุภาพเหนือทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(11,5,'11:5','พึงรู้เถิด แท้จริงพวกเขาปกปิดความลับในทรวงอกของพวกเขา เพื่อพวกเขาจะซ่อนความความเป็นศัตรูจากพระองค์ พึงรู้เถิด ขณะที่พวกเขาเอาเสื้อผ้าของพวกเขาปกคลุมตัวนั้น พระองค์ทรงรู้สิ่งที่พวกเขาปกปิด และสิ่งที่พวกเขาเปิดเผยแท้จริงพระองค์เป็นผู้ทรงรอบรู้สิ่งที่อยู่ในทรวงอก');
INSERT INTO "king_fahad_thai" VALUES(11,6,'11:6','และไม่ว่าสัตว์ตัวใดที่เหยียบย่ําอยู่ในแผ่นดิน เว้นแต่เครื่องยังชีพของมันเป็นหน้าที่ของอัลลอฮ์ และพระองค์ทรงรู้ที่พํานักของมันและที่พักชั่วคราวของมัน ทุกสิ่งอยู่ในบันทึกอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(11,7,'11:7','และพระองค์คือผู้สร้างชั้นฟ้าทั้งหลายและแผ่นดินในระยะ 6 วัน และบัลลังก์ของพระองค์อยู่เหนือน้ํา เพื่อพระองค์จะทรงทดสอบพวกท่านว่า ผู้ใดในหมู่พวกท่านมีการงานที่ดีเยี่ยม และหากเจ้า (มุฮัมมัด) กล่าวว่า “แท้จริงพวกท่านจะถูกให้ฟื้นคืนชีพขึ้นมา หลังจากที่ได้ตายไปแล้ว” แน่นอนบรรดาผู้ปฏิเสธศรัทธาจะกล่าวว่า “นี่มิใช่อื่นใดเลย นอกจากเล่ห์กลอย่างชัดแจ้ง”');
INSERT INTO "king_fahad_thai" VALUES(11,8,'11:8','และหากเรายึดเวลาการลงโทษพวกเขาออกไปอีกระยะเวลาหนึ่งที่ได้กําหนดไว้ แน่นอนพวกเขาจะกล่าวว่า “อะไรหรือได้ยับยั้งมันไว้” พึงรู้เถิด ! วันซึ่งการลงโทษจะมายังพวกเขา มันจะไม่ละเว้นไปจากพวกเขา และมันจะห้อมล้อมพวกเขา ตามที่พวกเขาได้เยาะเย้ยมัน');
INSERT INTO "king_fahad_thai" VALUES(11,9,'11:9','และถ้าเราได้ให้มนุษย์ลิ้มรสความเมตตาจากเรา แล้วเราได้ดึงมันกลับมาจากเขา แท้จริงเขานั้นเป็นผู้หมดหวังและสิ้นศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(11,10,'11:10','และถ้าเราได้ให้เขาลิ้มรสความโปรดปรานหลังจากความทุกข์ยากได้ประสบกับเขา แน่นอนเขาจะกล่าวว่า “ความชั่วร้ายต่างๆ ได้ผ่านพ้นจากฉันไปแล้ว” แท้จริง เขานั้นเป็นผู้คึกคะนองหยิ่งยะโส');
INSERT INTO "king_fahad_thai" VALUES(11,11,'11:11','เว้นแต่บรรดาผู้อดทนและบรรดาผู้ปฏิบัติความดีทั้งหลาย ชนเหล่านั้นแหละ สําหรับพวกเขาจะได้รับการอภัยโทษและรางวัลอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(11,12,'11:12','และบางทีเจ้าจะทิ้งบางส่วนที่ถูกวะฮีย์มายังเจ้า และหัวอกของเจ้าจะอึดอัดต่อสิ่งนั้น โดยที่พวกเขากล่าวกันว่า “ทําไมเล่าขุมทรัพย์จึงไม่ถูกส่งลงมา หรือทําไมมะลัก จึงไม่ถูกส่งลงมาพร้อมกับเขา?” แท้จริงเจ้าเป็นเพียงผู้ตักเตือนเท่านั้น และอัลลอฮฺเป็นผู้ทรงคุ้มครองรักษาทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(11,13,'11:13','หรือพวกเขากล่าวว่า “เขา(มุฮัมมัด) ได้ปลอมแปลงอัลกุรอานขึ้นมา” (มุฮัมมัด) จงกล่าวเถิด “ดังนั้น พวกท่านจงนํามาสักสิบซูเราะฮ์ที่ถูกปลอมแปลงขึ้นให้ได้อย่างอัลกรุอาน และพวกท่านจงเรียกผู้ที่มีความสามารถในหมู่พวกท่านอื่นจากอัลลอฮ์(ให้มาช่วย) ถ้าพวกท่านเป็นพวกสัตย์จริง');
INSERT INTO "king_fahad_thai" VALUES(11,14,'11:14','หากพวกเขาไม่ตอบสนองการเรียกร้องของพวกท่าน ก็จงรู้เถิดว่า แท้จริงอัลกรุอานถูกประทานลงมาด้วยวะฮีย์ของอัลลอฮ์ และนั่นคือไม่มีพระเจ้าอื่นใดนอกจากพระองค์ แล้วพวกเจ้า(มุชริกีน) ยังมินอบน้อมอีกหรือ?”');
INSERT INTO "king_fahad_thai" VALUES(11,15,'11:15','ผู้ใดปรารถนาการมีชีวิตอยู่ในโลกนี้และความเพริศแพร้วของมัน เราก็จะตอบแทนให้พวกเขาอย่างครบถ้วน ซึ่งการงานของพวกเขาในโลกนี้เท่านั้น และพวกเขาจะไม่ถูกริดรอนในการงานนั้นแต่อย่างใด');
INSERT INTO "king_fahad_thai" VALUES(11,16,'11:16','ชนเหล่านั้น พวกเขาจะไม่ได้รับการตอบแทนอันใดในโลกอาคิเราะฮ์ นอกจากไฟนรกและสิ่งที่พวกเขาได้ปฏิบัติไว้ในโลกดุนยาก็จะไร้ผลและสิ่งที่พวกเขาได้กระทําไว้ก็จะสูญเสียไป');
INSERT INTO "king_fahad_thai" VALUES(11,17,'11:17','ดังนั้น ผู้ที่อยู่บนหลักฐานอันชัดแจ้งจากพระเจ้าของเขา และผู้เป็นพยานจากพระองค์จะสาธยายมัน และก่อนนั้นมีคัมภีร์ของมูซาเป็นแนวทางและเป็นเมตตา ชนเหล่านั้นศรัทธาต่อมันและผู้ใดจากพรรคต่าง ๆ ปฏิเสธศรัทธาต่อมันไฟนรกคือสัญญาของเขา ดังนั้นเจ้าอย่าได้อยู่ในการสงสัยมันเลย แท้จริงมันเป็นสัจธรรมจากพระเจ้าของเจ้า แต่ทว่ามนุษย์ส่วนใหญ่จะไม่ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(11,18,'11:18','และผู้ใดเล่าที่จะอธรรมยิ่งไปกว่าผู้ที่อุปโลกน์ความเท็จต่ออัลลอฮ์ ชนเหล่านั้นจะถูกนํามาเสนอต่อพระเจ้าขงพวกเขาและบรรดาพยานจะกล่าวว่า “พวกเหล่านั้นคือบรรดาผู้ที่กล่าวเท็จต่อพระเจ้าของพวกเขา พึงรู้เถิด! การสาปแช่งของอัลลอฮ์จะได้แก่บรรดาผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(11,19,'11:19','บรรดาผู้กีดขวางทางอัลลอฮ์ และพวกเขาใคร่ที่จะให้มันคดเคี้ยว และพวกเขาสําหรับโลกอาคิเราะฮ์นั้นพวกเขาเป็นผู้ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(11,20,'11:20','ชนเหล่านี้จะไม่รอดพ้น(จากการลงโทษ)ในแผ่นดินนี้ และสําหรับพวกเขาไม่มีผู้คุ้มครองอื่นจากอัลลอฮ์ การลงโทษแก่พวกเขาจะถูกเพิ่มเป็นทวีคูณ พวกเขาไม่สามารถที่จะฟังได้และจะไม่เห็น');
INSERT INTO "king_fahad_thai" VALUES(11,21,'11:21','ชนเหล่านี้คือบรรดาผู้ที่ทําให้ชีวิตของพวกเขาขาดทุน และสิ่งที่พวกเขาอุปโลกน์ขึ้นนั้นก็ได้เตลิดหนีไปจากพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(11,22,'11:22','โดยแน่นอน แท้จริงพวกเขาเป็นผู้ขาดทุนยิ่งในอาคิเราะฮ์');
INSERT INTO "king_fahad_thai" VALUES(11,23,'11:23','แท้จริงบรรดาผู้ศรัทธาและผู้ปฏิบัติความดีและสํารวมตนต่อพระเจ้าของพวกเขา ชนเหล่านั้นคือชาวสวรรค์ ซึ่งพวกเขาจะอยู่ในนั้นตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(11,24,'11:24','อุปมาของทั้งสองฝ่าย ดังเช่นคนตาบอดและหูหนวก กับคนมองเห็นและได้ยิน ทั้งสองนี้จะเท่าเทียมกันหรือ พวกท่านมิได้ไตร่ตรองหรือ?');
INSERT INTO "king_fahad_thai" VALUES(11,25,'11:25','และโดยแน่นอน เราได้ส่งนุห์ไปยังกลุ่มชนของเขา (โดยกล่าวว่า) “แท้จริงฉันเป็นผู้ตักเตือนอันแน่ชัดแก่พวกท่านแล้ว');
INSERT INTO "king_fahad_thai" VALUES(11,26,'11:26','คือพวกท่านอย่าเคารพอิบาดะฮ์ผู้ใดนอกจากอัลลอฮ์ แท้จริงฉันกลัวแทนพวกท่านถึงการลงโทษในวันอันเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(11,27,'11:27','แล้วบรรดาบุคคลชั้นนําซึ่งปฏิเสธศรัทธาจากกลุ่มชนของเขากล่าวว่า “เรามิเห็นท่านเป็นอื่นใด นอกจากสามัญชนเช่นเรา และเรามิเห็นผู้ใดปฏิบัติตามท่าน นอกจากบรรดาผู้ต่ําช้าของพวกเราที่มีความคิดเห็นตื้น ๆ และเรามิเห็นว่าพวกท่านประเสริฐกว่าพวกเรา แต่เราคิดว่าพวกท่านเป็นพวกโกหก');
INSERT INTO "king_fahad_thai" VALUES(11,28,'11:28','เขา(นุห์) กล่าวว่า “โอ้หมู่ชนของฉันเอ๋ย ! พวกท่านเห็นแล้วใช่ไหมว่า หากฉันมีหลักฐานอันแจ้งชัดจากพระเจ้าของฉัน และพระองค์ทรงประทานแก่ฉันซึ่งความเมตตาจากพระองค์ แล้วได้ถูกทําให้มืดมนแก่พวกท่าน เราจะบังคับพวกท่านให้รับมันทั้ง ๆ ที่พวกท่านเกลียดชังมันกระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(11,29,'11:29','และโอ้หมู่ชนของฉันเอ๋ย ! ฉันมิได้ร้องขอทรัพย์สินใดสําหรับการเยแพร่ แต่รางวัลของฉันอยู่ที่อัลลอฮ์ และฉันจะไม่เป็นผู้ขับไล่บรรดาผู้ศรัทธาดอก แท้จริงพวกเขาจะเป็นผู้พบพระเจ้าของพวกเขา แต่ฉันเห็นว่าพวกท่านเป็นหมู่ชนผู้งมงาย');
INSERT INTO "king_fahad_thai" VALUES(11,30,'11:30','และโอ้หมู่ชนของฉันเอ๋ย ! ผู้ใดจะช่วยฉัน ณ ที่อัลลอฮ์หากฉันขับไล่พวกเขา พวกท่านไม่คิดบ้างดอกหรือ?');
INSERT INTO "king_fahad_thai" VALUES(11,31,'11:31','ฉันมิได้กล่าวแก่พวกท่านว่า ฉันมีขุมคลังของอัลลอฮ์ และฉันไม่รู้ถึงสิ่งพ้นญาณวิสัยและฉันมิได้กล่าวว่าแท้จริงฉันเป็นมลัก และและมิได้กล่าวแก่บรรดาผู้ที่สายตาของพวกท่านเหยียดหยามว่า อัลลอฮ์จะไม่ทรงประทานความดีแก่พวกเขา อัลลอฮ์ทรงรอบรู้ดียิ่งถึงสิ่งที่อยู่ในจิตใจของพวกเขา แท้จริง ถ้าเป็นเช่นนั้น ฉันจะอยู่ในหมู่ผู้อธรรมทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(11,32,'11:32','พวกเขากล่าวว่า “โอ้นุห์เอ๋ย ! แน่นอนท่านได้โต้เถียงของเรามากเรื่องขึ้น ดังนั้น จงนํามาให้เราเถิดสิ่งที่สัญญากับเราไว้ ถ้าท่านอยู่ในหมู่ผู้สัตย์จริง”');
INSERT INTO "king_fahad_thai" VALUES(11,33,'11:33','เขา(นุห์) กล่าวว่า “แท้จริงอัลลอฮฺเท่านั้นที่จะทรงนํามันมายังพวกท่าน หากพระองค์ทรงประสงค์ และพวกท่านจะไม่เป็นผู้รอดไปได้"');
INSERT INTO "king_fahad_thai" VALUES(11,34,'11:34','และคําสั่งสอนของฉันจะไม่เกิดประโยชน์แก่พวกท่าน ตามที่ฉันปรารถนาจะสั่งสอนพวกท่านถ้าอัลลอฮ์ทรงประสงค์จะให้พวกท่านหลงผิด พระองค์คือพระเจ้าของพวกท่าน และพวกท่านจะถูกนํากลับไปยังพระองค์”');
INSERT INTO "king_fahad_thai" VALUES(11,35,'11:35','หรือพวกเขา(กุฟฟารกุเรช) กล่าวว่า “เขา (มุฮัมมัด) ได้อุปโลกน์มันขึ้นมา” (มุฮัมมัด) จงกล่าวเถิดว่า “ถ้าฉันได้อุปโลกน์มันขึ้นมา ความผิดของฉันย่อมอยู่ที่ฉัน และฉันปลีกตัวอกจากสิ่งที่พวกท่านกระทําผิด”');
INSERT INTO "king_fahad_thai" VALUES(11,36,'11:36','และได้มีวะฮีแก่นุห์ว่า “แท้จริงจะไม่มีผู้ใดจากหมู่ชนของเจ้าศรัทธา เว้นแต่ผู้ที่ได้ศรัทธาแล้ว ดังนั้น เจ้าอย่าเศร้าหมองในสิ่งที่พวกเขากระทํา');
INSERT INTO "king_fahad_thai" VALUES(11,37,'11:37','และจ้าจงสร้างเรือต่อหน้าเราและตามคําบัญชาของเรา และอย่ามาดูดกับข้า ถึงบรรดาผู้อธรรม แท้จริงพวกเขาจะถูกจมน้ําตาย');
INSERT INTO "king_fahad_thai" VALUES(11,38,'11:38','และเขาได้สร้างเรือ และคราใดที่บุคคลชั้นนําจากหมู่ชนของผ่านเขา(นุห์) พวกเขาก็เยาะเย้ยเขา เขาก็จะกล่าวว่า “หากพวกท่านเยาะเย้ยพวกเรา แท้จริงเราก็จะเยาะเย้ยพวกท่านเช่นเดียวกับที่พวกท่านเยาะเย้ย');
INSERT INTO "king_fahad_thai" VALUES(11,39,'11:39','แล้วพวกท่านก็จะรู้ว่าผู้ใดที่การลงโทษอันอัปยศจะมายังเขา และการลงโทษอันยั่งยืนจะประสบแก่เขา');
INSERT INTO "king_fahad_thai" VALUES(11,40,'11:40','จนกระทั่งเมื่อคําบัญชาของเราได้มา และบนพื้นแผ่นดินน้ําได้พวยพุ่งขึ้น เรากล่าวว่า ”จงบรรทุกไว้ในเรือจากทุกชนิดเป็นคู่ ๆ และครอบครัวของเจ้าด้วย เว้นแต่ผู้ที่พระดํารัสได้กําหนดแก่เขาไว้ก่อน และผู้ศรัทธา แต่ไม่มีผู้ศรัทธาร่วมกับเขานอกจากจํานวนเล็กน้อย');
INSERT INTO "king_fahad_thai" VALUES(11,41,'11:41','และเขากล่าวว่า “พวกท่านจงลงในเรือด้วยพระนามของอัลลอฮ์ ทั้งในยามแล่นของมันและในยามจอดของมัน แท้จริงพระเจ้าของฉันเป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(11,42,'11:42','และมันแล่พาพวกเขาไปท่ามกลางคลื่นลูกเท่าภูเขา และนุห์ได้ร้องเรียกลูกชายของเขาซึ่งอยู่อย่างโดดเดี่ยว “โอ้ลูกของฉันเอ๋ย ! จงมาโดยสารเรือกับเราเถิด และเจ้าอย่าอยู่ร่วมกับผู้ปฏิเสธศรัทธาเลย”');
INSERT INTO "king_fahad_thai" VALUES(11,43,'11:43','เขา(ลูกชาย) กล่าวว่า “ฉันจะไปอาศัยภูเขาลูกหนึ่ง มันจะคุ้มครองฉันจากน้ํานี้ได้” เขา(นุห์) กล่าวว่า “ไม่มีผู้ใดคุ้มครองในวันนี้จากพระบัญชาของอัลลอฮ์ เว้นแต่ผู้ทีพระองค์ทรงเมตตา” และคลื่นได้ซัดเข้ามาระหว่างเขาทั้งสอง และเขา(ลูกชาย) ได้อยู่ในหมู่ผู้จมน้ํา');
INSERT INTO "king_fahad_thai" VALUES(11,44,'11:44','และได้มีเสียงกล่าวว่า “แผ่นดินเอ๋ย! จงกลืนน้ําของเจ้า และฟ้าเอ๋ย ! จงหยุด” และน้ําได้ลดลงและกิจการได้ถูกตัดสิน และมันได้จอดเทียบอยู่ที่ภูเขาญดีย์ และได้มีเสียงกล่าวว่า “ความหมายนะจงประสบแก่หมู่ชนผู้อธรรมเถิด”');
INSERT INTO "king_fahad_thai" VALUES(11,45,'11:45','และนุห์ได้ร้องเรียนต่อพระเจ้าของเขาโดยกล่าวว่า “ข้าแต่พระผู้เป็นเจ้าของพระองค์แท้จริงลูกชายของข้าพระองค์เป็นคนหนึ่งในครอบครัวของข้าพระองค์ และแท้จริงสัญญาของพระองค์นั้นเป็นความจริง และพระองค์ท่านนั้นทรงตัดสินเที่ยงธรรมยิ่ง ในหมู่ผู้ตัดสินทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(11,46,'11:46','พระองค์ทรงตรัสว่า “โอ้นุห์เอ๋ย ! แท้จริงเขามิได้เป็นคนหนึ่งในครอบครัวของเจ้า แท้จริงการกระทําของเขาไม่ดี ดังนั้นเจ้าอย่าร้องเรียนต่อข้าในสิ่งที่เจ้าไม่มีความรู้ แท้จริงข้าขอเตือนเจ้าที่เจ้าจะอยู่ในหมู่ผู้งมงาย');
INSERT INTO "king_fahad_thai" VALUES(11,47,'11:47','เขากล่าวว่า “ข้าแต่พระผู้เป็นเจ้าของข้าพระองค์ แท้จริงข้าพระองค์ขอความคุ้มครองต่อพระองค์ท่าน ให้พ้นจากการร้องเรียนต่อพระองค์ท่านในสิ่งที่ข้าพระองค์ไม่มีความรู้ในเรื่องนั้น และหากพระองค์ไม่ทรงอภัยแก่ข้าพระองค์ และไม่ทรงเมตตาข้าพระองค์แล้ว ข้าพระองค์ก็จะอยู่ในหมู่ผู้ขาดทุน”');
INSERT INTO "king_fahad_thai" VALUES(11,48,'11:48','ได้มีเสียงกล่าวว่า “โอ้นุห์เอ๋ย ! จงลงไป(จากเรื่อ) ด้วยความศานติจากเรา และความจําเริญแก่เจ้า และแก่กลุ่มชนที่อยู่กับเจ้า และกลุ่มชนอื่นที่เราจะให้พวกเขาหลงระเริง แล้วการลงโทษอย่างเจ็บปวดจากเราก็จะประสบแก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(11,49,'11:49','เหล่านั้นคือส่วนหนึ่งจากเรื่องราวอันเร้นลับที่เราได้วะอีมายังเจ้า(มุฮัมมัด) เจ้าไม่รู้เรื่องนี้และกลุ่มชนของเจ้าก็ไม่รู้มาก่อนเลย ดังนั้นเจ้าจงอดทนแท้จริงบั้นปลายที่ดีนั้นสําหรับบรรดาผู้ยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(11,50,'11:50','และยังอ๊าด (เราได้ส่ง) พี่น้องคนหนึ่งของพวกเขาคือฮูด เขากล่าวว่า “โอ้กลุ่มชนของฉันเอ๋ย! พวกท่านจงเคารพอิบาดะฮ์อัลลอฮ์เถิดพวกท่านไม่มีพระเจ้าอื่นใดนอกจากพระองค์ พวกท่านมิใช่อื่นใดนอกจากเป็นพวกอุปโลกน์เท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(11,51,'11:51','โอ้กลุ่มชนของฉันเอ๋ย! ฉันมิได้ขอร้องต่อพวกท่านซึ่งรางวัลในการนี้เลย รางวัลของฉันนั้นอยู่กับพระผู้ให้บังเกิดฉัน พวกท่านไม่ใช้ปัญญาหรือ?');
INSERT INTO "king_fahad_thai" VALUES(11,52,'11:52','และโอ้กลุ่มชนของฉันเอ๋ย ! จงขออภัยโทษต่อพระเจ้าของพวกท่าน แล้วจงกลับเนื้อกลับตัวต่อพระองค์ พระองค์จะส่งเมฆ(น้ําฝน) มาเหนือพวกท่าน ให้หลั่งน้ําฝนลงมาอย่างหนัก และจะทรงเพิ่มพลังเป็นทวีคุณให้แก่พวกท่าน และพวกท่าน และพวกท่านอย่าผินหลังโดยเป็นผู้กระทําผิด”');
INSERT INTO "king_fahad_thai" VALUES(11,53,'11:53','พวกเขากล่าวว่า “โอ้ฮูดเอ๋ย! ท่านมิได้นําหลักฐานอันชัดแจ้งมาให้แก่เรา และพวกเราก็จะไม่ละทิ้งพระเจ้าทั้งหลายของเราเพราะคํากล่าวของท่าน และพวกเราก็จะไม่ศรัทธาในตัวท่าน');
INSERT INTO "king_fahad_thai" VALUES(11,54,'11:54','เราจะไม่กล่าวอย่างใด เว้นแต่พระเจ้าบางองค์ของเราได้นําความชั่วเข้าไปสิงในตัวท่าน” เขา (ฮูด) กล่าวว่า “แท้จริงฉันให้อัลลอฮ์ทรงเป็นพยาน แล้วพวกท่านจงเป็นพยานด้วยว่าแท้จริงฉันปลีกตัวออกจากสิ่งที่พวกท่านตั้งภาคี');
INSERT INTO "king_fahad_thai" VALUES(11,55,'11:55','อื่นจากพระองค์ ดังนั้นพวกท่านทั้งหมดจงวางแผนทําร้ายฉันเถิด แล้วพวกท่านอย่าได้ให้ฉันต้องรอคอยเลย');
INSERT INTO "king_fahad_thai" VALUES(11,56,'11:56','แท้จริงฉันมอบหมายต่ออัลลอฮ์ พระเจ้าของฉันและพระเจ้าของพวกท่าน ไม่มีสัตว์เลื้อยคลานใด ๆ เว้นแต่พระองค์ทรงกําขมับมัน แท้จริงพระเจ้าของฉันอยู่บนทางที่เที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(11,57,'11:57','หากพวกท่านผินหลังให้แล้วไซร้ แน่นอนฉันได้แจ้งข่าวแก่พวกท่านแล้ว ตามที่ฉันได้ถูกส่งมายังพวกท่านเพื่อมัน และพระเจ้าของฉันจะทรงแต่งตั้งกลุ่มชนอื่นจากพวกท่านเป็นตัวแทน และพวกท่านจะไม่อันตรายต่อพระองค์แต่อย่างใด แท้จริงพระเจ้าของฉันเป็นผู้ทรงพิทักษ์ทุกสิ่ง”');
INSERT INTO "king_fahad_thai" VALUES(11,58,'11:58','และเมื่อบัญชาของเรา ได้มาถึง เราได้ช่วยฮูดและบรรดาผู้ศรัทธาร่วมกับเขาให้รอดพ้น ด้วยความเมตตาจากเรา และเราได้ช่วยให้พวกเขาพ้นจากการลงโทษอันโหดร้าย');
INSERT INTO "king_fahad_thai" VALUES(11,59,'11:59','และนั่นคือกลุ่มชนอ๊าด พวกเขาปฏิเสธโองการทั้งหลายของพระเจ้าของพวกเขา และฝ่าฝืนต่อบรรดาร่อซูลของพระองค์ และปฏิบัติตามคําสั่งของผู้หยิ่งผยองผู้ขัดขืนทุกคน');
INSERT INTO "king_fahad_thai" VALUES(11,60,'11:60','และพวกเขาถูกติดตามด้วยการสาปแช่งในโลกดุนยานี้และวันกิยามะฮฺ พึงทราบเถิด! แท้จริงกลุ่มชนอ๊าดปฏิเสธศรัทธาต่อพระเจ้าของพวกเขา พึงทราบเถิด! จงห่างไกลจากความเมตตาเถิดอ๊าดกลุ่มชนของฮูด');
INSERT INTO "king_fahad_thai" VALUES(11,61,'11:61','และยังษะมูด (เราได้ส่ง) พี่น้องคนหนึ่งของพวกเขาคือซอและฮ์ เขากล่าวว่า “โอ้กลุ่มชนของฉันเอ๋ย! พวกท่านจงเคารพอิบาดะฮ์อัลลอฮ์เถิด พวกท่านไม่มีพระเจ้าอื่นใดนอกจากพระองค์ พระองค์ทรงบังเกิดพวกท่านพํานักอยู่ในนั้น อังนั้น พวกท่านจงขออภัยต่อพระองค์ และจงกลับเนื้อกลับตัวต่อพระองค์ แท้จริงพระเจ้าของฉันนั้นทรงอยู่ใกล้ทรงตอบรับเสมอ”');
INSERT INTO "king_fahad_thai" VALUES(11,62,'11:62','พวกเขากล่าวว่า “โอ้ ศอและฮ์เอ๋ย ! แน่นอนท่านเคยเป็นความหวังในหมู่พวกเรามาก่อน บัดนี้ ท่านจะห้ามมิให้เราเคารพอิบาดะฮ์สิ่งที่บรรพบุรุษของเราเคารพอิบาดะฮ์อยู่กระนั้นหรือ? และแท้จริงพวกเราอยู่ในการสงสัยต่อสิ่งที่ท่านเรียกร้องเชิญชวนเรายังสิ่งนั้น”');
INSERT INTO "king_fahad_thai" VALUES(11,63,'11:63','เขากล่าวว่า “โอ้กลุ่มชนของฉันเอ๋ย ! พวกท่านไม่เห็นดอกหรือ หากฉันมีหลักฐานอันชัดแจ้งจากพระเจ้าของฉัน และพระองค์ทรงประทานความเมตตา จากพระองค์แก่ฉัน ดังนั้นผู้ใดเล่าจะช่วยฉันให้พ้นจากอัลลอฮ์ หากฉันฝ่าฝืนพระองค์ ดังนั้น พวกท่านจะไม่เพิ่มสิ่งใดให้แก่ฉันเลยนอกจากการขาดทุนเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(11,64,'11:64','และโอ้กลุ่มชนของฉันเอ๋ย! นี่คืออูฐตัวเมียของอัลลอฮ์ เป็นสัญญาณหนึ่งแก่พวกท่านดังนั้นพวกท่านจงปล่อยมันให้หากินตามลําพังในแผ่นดินของอัลลอฮ์ และอย่าก่อความทุกข์ยากแก่มัน มิฉะนั้นแล้ว การลงโทษอันรวดเร็วจะประสบแก่พวกท่าน”');
INSERT INTO "king_fahad_thai" VALUES(11,65,'11:65','ต่อมาพวกเขาได้ฆ่า ดังนั้นเขา(ศอและฮ์) กล่าวว่า “พวกท่านจงสุขสําราญในบ้านของพวกท่านสามวัน นั่นคือสัญญาที่ไม่โกหก”');
INSERT INTO "king_fahad_thai" VALUES(11,66,'11:66','ดังนั้น เมื่อพระบัญชาของเราได้มาถึง เราได้ช่วยศอและฮ์และบรรดาผู้ศรัทธาร่วมกับเขาให้รอดพ้น ด้วยความเมตตาจากเรา และจากความอดสูของวันนั้น แท้จริงพระเจ้าของเจ้านั้นเป็นผู้ทรงพลัง ผู้ทรงอํานาจ');
INSERT INTO "king_fahad_thai" VALUES(11,67,'11:67','และเสียงกัมปนาท ได้คร่าบรรดาผู้อธรรม แล้วพวกเขาได้กลายเป็นผู้นอนพังพาบตายในบ้านของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(11,68,'11:68','ประหนึ่งว่า พวกเขามิได้เคยอยู่ในนั้นมาก่อน พึงทราบเถิด! แท้จริงษะมูดนั้นปฏิเสธศรัทธาพระเจ้าของพวกเขา พึงทางเถิด! จงห่างไกลจากความเมตตาเถิดสําหรับษะมูด');
INSERT INTO "king_fahad_thai" VALUES(11,69,'11:69','และแน่นอนบรรดาทูตของเราได้มายังอิบรอฮีมพร้อมทั้งข่าวดี พวกเขากล่าวว่า “ขอความศานติจงมีแด่ท่าน” เขา(อิบรอฮีม) กล่าวว่า ”ขอความศานติจงมีแด่พวกท่าน” ดังนั้นเขามิได้รีรอที่จะนําลูกวัวย่างออกมา');
INSERT INTO "king_fahad_thai" VALUES(11,70,'11:70','ครั้นเมื่อเขาเห็นว่ามือของพวกเขาไม่ถึงมัน เขาไม่พอใจและรู้สึกกลัวพวกเขา พวกเขากล่าวว่า “อย่ากลัวเลย แท้จริงเราถูกส่งมายังกลุ่มชนของลูฏ”');
INSERT INTO "king_fahad_thai" VALUES(11,71,'11:71','และภริยาของเขายืนอยู่แล้ว นางก็หัวเราะเราจึงแจ้งข่าวดีแก่นางด้วย(การได้บุตรชื่อ) อิสฮาก และหลังจากอิสฮากคือยะอ์กูบ');
INSERT INTO "king_fahad_thai" VALUES(11,72,'11:72','นางกล่าวว่า “โอ้ แปลกแท้ ๆ ฉันจะมีบุตรหรือ ขณะที่ฉันแก่แล้ว และนี่สามีของฉันก็แก่หง่อมแล้ว แท้จริงนี่เป็นเรื่องประหลาดแท้”');
INSERT INTO "king_fahad_thai" VALUES(11,73,'11:73','พวกเขากล่าวว่า “เธอแปลกใจต่อพระบัญชาของอัลลอฮ์หรือ? ความเมตตาของอัลลอฮ์และความจําเริญของพระองค์จงประสบแด่พวกท่านโอ้ครอบครัว (ของอิบรฮีม) แท้จริงพระองค์นั้นเป็นผู้ได้รับการสรรเสริญ ผู้ทรงประเสริฐยิ่ง”');
INSERT INTO "king_fahad_thai" VALUES(11,74,'11:74','ครั้นเมื่อความตระหนกได้คลายไปจากอิบรอฮีมแล้ว และข่าวดีได้มายังเขา เขาก็โต้เถียงเราในเรื่องของกลุ่มชนลูฏ');
INSERT INTO "king_fahad_thai" VALUES(11,75,'11:75','แท้จริงอิบรอฮีมนั้นเป็นผู้อดทนขันติ จิตใจอ่อนโยน หันหน้าเข้าหาอัลลอฮ์เสมอ');
INSERT INTO "king_fahad_thai" VALUES(11,76,'11:76','โอ้อิบรอฮีมเอ๋ย! จงผินหลังจากเรื่องนี้เถิด แท้จริงพระบัญชาของพระเจ้าของเจ้าได้มาถึงแล้ว และแท้จริงเพวกเขาเหล่านั้น การลงโทษที่ไม่เปลี่ยนเป็นอย่างอื่นจะมายังพวกเขาอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(11,77,'11:77','และเมื่อบรรดาทูตของเรา(มะลาอิกะฮ์) ได้มายังลูฏ เขาเป็นทุกข์ต่อพวกเขาและหนักใจในพวกเขา และกล่าวว่า “นี่เป็นอันชั่วร้ายที่สุด”');
INSERT INTO "king_fahad_thai" VALUES(11,78,'11:78','และกลุ่มชนของเขาได้มาหาเขา พวกเขารีบร้อนมายังเขา และก่อนหน้านั้นพวกเขาเคยทําความชั่ว เขากล่าวว่า “กลุ่มชนของฉันเอ๋ย! เหล่านี้คือลูกสาวของฉัน พวกนางนั้นบริสุทธิ์สําหรับพวกท่าน ดังนั้น พวกท่านจงยําเกรงอัลลอฮ์เถิดและอย่าทําให้ฉันขายหน้าต่อแขกของฉันเลย ไม่มีคนที่มีสติสัมปชัญญะในหมู่พวกท่านบ้างหรือ?”');
INSERT INTO "king_fahad_thai" VALUES(11,79,'11:79','พวกเขากล่าวว่า “โดยแน่นอน ท่านรู้ดีว่า เราไม่มีสิทธิ์ในลูกสาวของท่าน และแท้จริงท่านรู้ดีถึงสิ่งที่เราปรารถนา”');
INSERT INTO "king_fahad_thai" VALUES(11,80,'11:80','เขากล่าวว่า “หากว่าฉันมีกําลังปราบพวกท่าน หรือฉันหันไปพึ่งที่พักพิงอันแข็งแรง”');
INSERT INTO "king_fahad_thai" VALUES(11,81,'11:81','พวกเขา(มะลาอิกะฮ์) กล่าวว่า “โอ้ลูฏเอ๋ย! พวกเราเป็นทูตของพระเจ้าของท่าน พวกเหล่านั้นจะไม่ถึงท่านได้เลย ดังนั้น ท่านจงเดินทางไปในเวลากลางคืนพร้อมกับครอบครัวของท่านและคนใดในหมู่พวกท่านอย่าได้เหลียวหลังมองเว้นแต่ภริยาของท่าน แท้จริงจะประสบแก่นางเช่นเดียวกับที่ได้ประสบแก่พวกเขา แท้จริงสัญญาของพวกเขาคือเวลาเช้า เวลาเช้านั้นใกล้เข้ามาแล้วมิใช่หรือ?”');
INSERT INTO "king_fahad_thai" VALUES(11,82,'11:82','ดังนั้น เมื่อพระบัญชาของเราได้มาถึง เราได้ทําให้ข้างบนของมันเป็นข้างล่าง และเราได้ ให้ก้อนหินแกร่งหล่นพรูลงมา');
INSERT INTO "king_fahad_thai" VALUES(11,83,'11:83','ถูกตราเครื่องหมายไว้ ณ ที่พระเจ้าของท่าน และมัน ไม่ไกลไปจากบรรดาผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(11,84,'11:84','และยังกลุ่มชนของมัดยัน เราได้ส่งพี่น้องคนหนึ่งของพวกเขาคือชุอัยบ์ เขากล่าวว่า “โอ้กลุ่มชนของฉันเอ๋ย! พวกท่านจงเคารพอิบาดะฮ์อัลลอฮ์เถิด พวกท่านนั้นไม่มีพระเจ้าอื่นใดนอกจากพระองค์ และพวกท่านอย่าให้การตวง และการชั่งบกพร่อง แท้จริงฉันเห็นพวกท่านยังอยู่ในความดี และแท้จริงฉันกลัวแทนพวกท่านต่อการลงโทษในวันที่ถูกห้อมล้อมไว้');
INSERT INTO "king_fahad_thai" VALUES(11,85,'11:85','และโอ้กลุ่มชนของฉันเอ๋ย ! พวกท่านจงให้ครบสมบูรณ์ไว้ซึ่งการตวงและการชั่งโดยเที่ยงธรรม และอย่าให้บกพร่องแก่มนุษย์ซึ่งสิ่งต่าง ๆ ของพวกเขา และอย่าก่อกวนในแผ่นดินโดยเป็นผู้บ่อนทําลาย');
INSERT INTO "king_fahad_thai" VALUES(11,86,'11:86','สิ่งที่เหลืออยู่ของอัลลอฮ์นั้น ดียิ่งสําหรับพวกท่าน หากพวกท่านเป็นผู้ศรัทธา และฉันมิใช่ผู้คุ้มกันพวกท่าน”');
INSERT INTO "king_fahad_thai" VALUES(11,87,'11:87','พวกเขากล่าวว่า “โอ้ชุอัยบ์เอ๋ย ! การละหมาดของท่านสั่งสอนท่านว่า ให้พวกเราละทิ้งสิ่งที่บรรพบุรุษของเราเคารพบูชา หรือว่าให้เรากระทําต่อทรัพย์สินของเราตามที่เราต้องการกระนั้นหรือ? แท้จริงท่านนั้นเป็นผู้อดทนขันติ เป็นผู้มีสติปัญญา”');
INSERT INTO "king_fahad_thai" VALUES(11,88,'11:88','เขากล่าวว่า “โอ้กลุ่มชนชนของฉันเอ๋ย! พวกท่านมิเห็นดอกหรือ หากฉันมีหลักฐานอันชัดแจ้งจากพระเจ้าของฉัน และพระองค์ได้ประทานริซกีแก่ฉัน ซึ่งเป็นริซกีที่ดีจากพระองค์ และฉันมิปรารถนาที่จะขัดแย้งกับพวกท่าน ในสิ่งที่ฉันได้ห้ามพวกท่านให้ละเว้น ฉันมิปรารถนาสิ่งใดนอกจากการปฏิรูปให้ดีขึ้นเท่าที่ฉันสามารถ และความสําเร็จของฉันจะไม่เกิดขึ้น เว้นแต่ด้วยความช่วยเหลือของอัลลอฮ์ แด่พระองค์ฉันขอมอบหมายและยังพระองค์เท่านั้นฉันกลับไปหา');
INSERT INTO "king_fahad_thai" VALUES(11,89,'11:89','และโอ้กลุ่มชนของฉัน อย่าให้การแตกแยกของฉันทําให้พวกท่านกระทําผิด ซึ่งจะประสบแก่พวกท่านเช่นที่ได้ประสบแก่กลุ่มชนของนูห์หรือกลุ่มชนของฮูด หรือกลุ่มชนของศอและฮ์ และกลุ่มชนของลูฏมิได้อยู่ห่างไกลจากพวกท่าน');
INSERT INTO "king_fahad_thai" VALUES(11,90,'11:90','และพวกท่านจงขออภัยโทษต่อพระเจ้าของพวกท่าน แล้วจงกลับเนื้อกลับตัวต่อพระองค์แท้จริงพระเจ้าของฉันนั้นเป็นผู้ทรงเมตตา ผู้ทรงรักใคร่”');
INSERT INTO "king_fahad_thai" VALUES(11,91,'11:91','พวกเขากล่าวว่า “โอ้ ชุอัยบ์เอ๋ย! เราไม่เข้าใจส่วนมากที่ท่านกล่าว และแท้จริงเราเห็นว่าท่านเป็นคนอ่อนแอในหมู่พวกเรา ถ้ามิใช่เพราะครอบครัวของท่านแล้ว เราจะเอาหินขว้างท่าน และท่านก็มิได้เป็นผู้มีเกียรติเหนือพวกเรา”');
INSERT INTO "king_fahad_thai" VALUES(11,92,'11:92','เขากล่าวว่า “โอ้กลุ่มชนของฉันเอ๋ย! ครอบครัวของฉันเป็นที่นับถือแก่พวกท่านมากยิ่งกว่าอัลลอฮ์กระนั้นหรือ? และพวกท่านได้เอาพระองค์ไว้เบื้องหลังพวกท่าน แท้จริงพระเจ้าของฉันทรงรอบรู้สิ่งที่พวกท่านกระทํา');
INSERT INTO "king_fahad_thai" VALUES(11,93,'11:93','และโอ้กลุ่มชนของฉันเอ๋ย! พวกท่านจงกระทําตามแนวทางของพวกท่าน ฉันก็จะกระทํา(ตามแนวทางของฉัน) แล้วพวกท่านก็จะรู้ว่าผู้ใดที่การลงโทษจะประสบแก่เขา จะทําให้เขาอดสูและผู้ใดที่เขาเป็นคนโกหก และพวกท่านจงคอยเฝ้าดูเถิด แท้จริงฉันก็ร่วมกับพวกท่านคอยเฝ้าดูอยู่ด้วย”');
INSERT INTO "king_fahad_thai" VALUES(11,94,'11:94','และเมื่อพระบัญชาของเราได้มาถึง เราได้ช่วยชุอัยบ์และบรรดาผู้ศรัทธาร่วมกับเขาให้รอดพ้น ด้วยความเมตตาจากเรา และเสียงกัมปนาทได้ร่าบรรดาผู้อธรรม แล้วพวกเขาได้กลายเป็นผู้นอนพังพาบตายในบ้านของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(11,95,'11:95','ประหนึ่งว่าพวกเขามิได้เคยอยู่ในนั้นมาก่อน พึงทราบเถิด! จงห่างไกลจากความเมตตาเถิดสําหรับมัดยัน เช่นเดียวกับที่ษะมูดได้ห่างไกลมาแล้ว');
INSERT INTO "king_fahad_thai" VALUES(11,96,'11:96','และโดยแน่นอนเราได้ส่งมูซา พร้อมด้วยสัญญาณต่างๆ ของเราและหลักฐานอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(11,97,'11:97','ยังฟิรเอาน์ และบรรดาบุคคลชั้นนําของเขา พวกเขาได้ปฏิบัติตามคําสั่งของฟิรเอาน์ และคําสั่งของฟิรเอาน์ นั้นไม่เหมาะสม');
INSERT INTO "king_fahad_thai" VALUES(11,98,'11:98','เขาจะนําหน้ากลุ่มชนของเขาในวันกิยามะฮ์ และนําพวกเขาลงในไฟนรก และมันเป็นทางลงที่ชั่วช้าที่พวกเขาได้ลงไป');
INSERT INTO "king_fahad_thai" VALUES(11,99,'11:99','และพวกเขาถูกติดตามด้วยการถูกสาปแช่งในโลกนี้และวันกิยามะฮ์ มันเป็นความช่วยเหลือที่ชั่วช้าที่พวกเขาได้รับความช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(11,100,'11:100','นั่นคือส่วนหนึ่งจากเรื่องราวของเมืองต่างๆ เราได้บอกเล่ามันแกเจ้า ส่วนหนึ่งของมันยังคงอยู่ และส่วนหนึ่งก็เสื่อมโทรมไปแล้ว');
INSERT INTO "king_fahad_thai" VALUES(11,101,'11:101','และเรามิได้อธรรมต่อพวกเขา แต่ว่าพวกเขาอธรรมต่อตัวของพวกเขาเอง และบรรดาพระเจ้าของพวกเขาที่พวกเขาวิงวอนขออื่นจากอัลลอฮ์นั้น จะไม่อํานวยประโยชน์อันใดให้แก่พวกเขาเลย เมื่อพระบัญชาของพระเจ้าของท่านได้มาถึง และพระเจ้าเหล่านั้นมิได้เพิ่มอันใดแก่พวกเขา นอกจากความพินาศ');
INSERT INTO "king_fahad_thai" VALUES(11,102,'11:102','และเช่นนี้แหละคือการลงโทษของพระเจ้า เมื่อพระองค์ทรงลงโทษหมู่บ้านซึ่งเป็นหมู่บ้านที่อธรรม แท้จริงการลงโทษของพระองค์นั้นเจ็บแสบสาหัส');
INSERT INTO "king_fahad_thai" VALUES(11,103,'11:103','แท้จริง ในการนั้นเป็นสัญญาณสําหรับผู้ที่กลัวการลงโทษในวันอาคิเราะฮ์ นั่นคือวันแห่งการรวบรวมปวงมนุษย์สําหรับพระองค์ และนั่นคือวันแห่งการอยู่ร่วมกันอย่างพร้อมพรัก');
INSERT INTO "king_fahad_thai" VALUES(11,104,'11:104','และเรามิได้หน่วงมัน เว้นแต่เพื่อวาระที่ถูกกําหนดไว้');
INSERT INTO "king_fahad_thai" VALUES(11,105,'11:105','วันที่เมื่อมันมาถึงไม่มีชีวิตใดจะพูดได้เว้นแต่โดยอนุมัติของพระองค์ ดังนั้น ในหมู่พวกเขาจะมีผู้เป็นทุกข์และผู้เป็นสุข');
INSERT INTO "king_fahad_thai" VALUES(11,106,'11:106','ดังนั้น สําหรับบรรดาผู้ที่มีทุกข์ก็จะอยู่ในนรก สําหรับพวกเขาที่อยู่ในนั้นคือ การถอนหายใจและการสะอื้น');
INSERT INTO "king_fahad_thai" VALUES(11,107,'11:107','พวกเขาจะพํานักอยู่ในนั้นตลอดกาล ตราบเท่าที่ชั้นฟ้าทั้งหลายและแผ่นดินยืนยง เว้นแต่ที่พระเจ้าของเจ้าทรงประสงค์ แท้จริงพระเจ้าของเจ้าเป็นผู้กระทําโดยเด็ดขาดตามที่พระองค์ทรงประสงค์');
INSERT INTO "king_fahad_thai" VALUES(11,108,'11:108','และสําหรับบรรดาผู้เป็นสุขก็จะอยู่ในสวนสวรรค์ พวกเขาจะพํานักอยู่ในนั้นตลอดกาลตราบเท่าที่ชั้นฟ้าทั้งหลายและแผ่นดินยืนยัง เว้นแต่ที่พระเจ้าของของเจ้า ทรงประสงค์ เป็นการประทานให้โดยปราศจากการตัดทอน');
INSERT INTO "king_fahad_thai" VALUES(11,109,'11:109','ดังนั้น เจ้าอย่าอยู่ในการสงสัย จากการที่เขาเหล่านั้นเคารพบูชาเลย พวกเขามิได้เคารพบูชาสิ่งใด เว้นแต่เช่นเดียวกับที่บรรพบุรุษของพวกเขาได้เคารพบูชามาก่อนแล้ว และแท้จริงเราจะให้สมบูรณ์แก่พวกเขาซึ่งส่วนของพวกเขา โดยปราศจากการบกพร่อง');
INSERT INTO "king_fahad_thai" VALUES(11,110,'11:110','และโดยแน่นอน เราได้ให้คัมภีร์แก่มูซา แล้วได้เกิดการขัดแย้งกันขึ้นในนั้น และหากมิใช่ลิขิตได้บันทึกไว้ที่พระเจ้าของเจ้าแล้วแน่นอนก็คงจะถูกตัดสินระหว่างพวกเขา และแท้จริงพวกเขานั้นเป็นผู้สงสัย ย่อมอยู่ในการสงสัยจากมัน(คัมภีร์)');
INSERT INTO "king_fahad_thai" VALUES(11,111,'11:111','และแท้จริงพวกเขาทั้งหมด พระเจ้าของเจ้าจะทรงตอบแทนแก่พวกเขาอย่างครบถ้วนซึ่งการงานของพวกเขา แท้จริงพระองค์ทรงรู้ทุกสิ่งที่พวกเขากระทํา');
INSERT INTO "king_fahad_thai" VALUES(11,112,'11:112','เจ้าจงอยู่ในความเที่ยงธรรมเช่นที่ถูกบัญชา และผู้ที่ขอลุโทษแก่เจ้า และพวกท่านอย่าได้ละเมิด แท้จริงพระองค์ทรงรู้เห็นสิ่งที่พวกท่านกระทํา');
INSERT INTO "king_fahad_thai" VALUES(11,113,'11:113','และพวกท่านอย่าเห็นชอบไปกับธรรมดาผู้อธรรม ไฟนรกจะสัมผัสพวกท่านได้ และสําหรับพวกท่านไม่มีผู้คุ้มครองใด ๆ นอกจากอัลลอฮ์แล้วพวกท่านจะไม่ช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(11,114,'11:114','และเจ้าจงดํารงไว้ซึ่งการละหมาด ตามปลายช่วงทั้งสองของกลางวัน และยามต้นจากกลางคืน แท้จริงความดีทั้งหลายย่อมลบล้างความชั่วทั้งหลายนั่นคือข้อเตือนสําหรับบรรดาผู้ที่รําลึก');
INSERT INTO "king_fahad_thai" VALUES(11,115,'11:115','และเจ้าจงอดทน เพระแท้จริงอัลลอฮ์จะไม่ทรงทําให้รางวัลของผู้ทําความเสียหาย');
INSERT INTO "king_fahad_thai" VALUES(11,116,'11:116','ทําไมในศตวรรษก่อนจากพวกเจ้าจึงไม่มีปัญญาชนช่วยกันห้ามปรามการบ่อนทําลายในแผ่นดิน เว้นแต่จํานวนน้อยเท่านั้นจากผู้ที่เราได้ช่วยพวกเขาให้รอดพ้น และบรรดาผู้อธรรมได้ปฏิบัติตามสิ่งที่พวกเขาถูกให้อยู่ในความสําราญ พวกเขาจึงเป็นผู้กระทําผิด');
INSERT INTO "king_fahad_thai" VALUES(11,117,'11:117','และพระเจ้าของเจ้าจะไม่ทรงทําลายหมู่บ้านโดยอยุติธรรม โดยที่ประชากรของหมู่บ้านนั้นเป็นผู้ฟื้นฟูทําความดี');
INSERT INTO "king_fahad_thai" VALUES(11,118,'11:118','และหากพระเจ้าของเจ้าทรงประสงค์แน่นอนพระเองค์จะทรงทําให้ปวงมนุษย์เป็นประชาชาติเพียวกัน แต่พวกเขาก็ยังคงแตกแยกกัน');
INSERT INTO "king_fahad_thai" VALUES(11,119,'11:119','เว้นแต่ผุ้ที่พระเจ้าของเจ้ามีเมตตา และเช่นนั้นแหละพระองค์ทรงบังเกิดพวกเขาและลิขิตของพระเจ้า ทรงกําหนดไว้สมบูรณ์แล้ว แน่นอนข้าจะให้นรกนั้นเต็มไปด้วยพวกญินและมนุษย์ทั้งหมด');
INSERT INTO "king_fahad_thai" VALUES(11,120,'11:120','และทั้งหมดนี้เราได้บอกเล่าแก่เจ้า จากเรื่องราวของบรรดาร่อซูล เพื่อทําให้จิตใจของเจ้าหนักแน่น และได้มายังเจ้าแล้วใน(เรื่องราวเหล่า) นี้ซึ่งความจริงและข้อตักเตือน และข้อรําลึกสําหรับผู้ศรัทธาทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(11,121,'11:121','จงกล่าวเถิดมุฮัมมัดแก่บรรดาผู้ไม่ศรัทธาว่า “พวกท่านจงกระทําตามแนวทางพวกท่าน แท้จริงเราก็กระทําเช่นกัน”');
INSERT INTO "king_fahad_thai" VALUES(11,122,'11:122','และพวกท่านจงคอยดูเถิด แท้จริงเราก็เป็นผู้คอยดู”');
INSERT INTO "king_fahad_thai" VALUES(11,123,'11:123','และกรรมสิทธิ์ของอัลลอฮ์คือ สิ่งพันญาณวิสัยแห่งชั้นฟ้าทั้งหลายและแผ่นดิน และยังพระองค์การงานทั้งมวลจะถูกนํากลับไป ดังนั้นเจ้าจงเคารพอิบาดะฮ์พระองค์ และจงมอบหมายต่อพระองค์และพระเจ้าของเจ้าจะไม่เป็นผู้ทรงเผลอในสิ่งที่พวกท่านกระทํา');
INSERT INTO "king_fahad_thai" VALUES(12,1,'12:1','อะลิฟ ลาม รอ เหล่านี้คือโองการทั้งหลายแห่งคัมภีร์ที่ชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(12,2,'12:2','แท้จริงพวกเราได้ให้อัลกุรอานแก่เขาเป็นภาษาอาหรับ เพื่อพวกเจ้าจะใช้ปัญญาคิด');
INSERT INTO "king_fahad_thai" VALUES(12,3,'12:3','เราจะเล่าเรื่องราวที่ดียิ่งแก่เจ้า ตามที่เราได้วะฮีอัลกุรอานนี้แก่เจ้า และหากว่าก่อนหน้านี้เจ้าอยู่ในหมู่ผู้ไม่รู้เรื่องราว');
INSERT INTO "king_fahad_thai" VALUES(12,4,'12:4','จงรําลึกขณะที่ยูซุฟกล่าวแก่พ่อของเขาว่า “โอ้พ่อจ๋า! แท้จริงฉันได้ฝันเห็นดวงดาวสิบเอ็ดดวง และดวงอาทิตย์ และดวงจันทร์ ฉันฝันเห็นพวกมันสุญูดต่อฉัน”');
INSERT INTO "king_fahad_thai" VALUES(12,5,'12:5','เขา(ยะอฺกูบ) กล่าวว่า “โอ้ลูกรักเอ๋ย ! เจ้าอย่าเล่าความฝันของเจ้าแก่พี่น้องของเจ้า เพราะพวกเขาจะวางอุบายแก่เจ้าอย่างมีเล่ห์เหลี่ยม แท้จริงชัยฏอนนั้นเป็นศัตรูที่ชัดแจ้งกับมนุษย์"');
INSERT INTO "king_fahad_thai" VALUES(12,6,'12:6','และเช่นนั้นแหละพระเจ้าของเจ้าทรงเลือกเจ้า และทรงสอนเจ้าให้รู้วิชาทํานายฝัน และทรงให้สมบูรณ์ซึ่งความโปรดปรานของพระองค์แก่เจ้าและวงศ์วานของยะอฺกูบ เช่นเดียวกับที่พระองค์ได้ทรงให้สมบูรณ์ ซึ่งความโปรดปรานแก่ปู่ทั้งสองของเจ้าแต่ก่อน คืออิบรอฮีมและอิสฮาก แท้จริงพระเจ้าของเจ้าเป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(12,7,'12:7','แท้จริงเกี่ยวกับยูซุฟและพี่น้องของเขานั้น มีสัญญาณทั้งหลายสําหรับผู้สอบถาม');
INSERT INTO "king_fahad_thai" VALUES(12,8,'12:8','จงรําลึกขณะที่พวกเขากล่าวว่า “แน่นอนยูซุฟและน้องของเขาเป็นที่รักแก่พ่อของเรายิ่งกว่าพวกเราทั้ง ๆ ที่พวกเรามีจํานวนมากแท้จริงพ่อของเราอยู่ในการหลงผิดจริง ๆ');
INSERT INTO "king_fahad_thai" VALUES(12,9,'12:9','“พวกท่านจงฆ่ายูซุฟ หรือเอาไปทิ้งในที่เปลี่ยวเสีย เพื่อความเอาใจใส่ของพ่อของพวกท่านจะเกิดขึ้นแก่พวกท่าน และพวกท่านจะเป็นกลุ่มชนที่ดีหลังจากเขา”');
INSERT INTO "king_fahad_thai" VALUES(12,10,'12:10','คนหนึ่งในพวกเขากล่าวว่า พวกท่านอย่าฆ่ายูซุฟ แต่จงโยนเขาลงในบ่อลึก เพื่อผู้เดินทางบางคนจะได้เอาเขาออกมา หากพวกท่านจําต้องกระทําเช่นนั้น”');
INSERT INTO "king_fahad_thai" VALUES(12,11,'12:11','พวกเขากล่าวว่า “โอ้คุณพ่อของเรา! ทําไมท่านจึงไม่ไว้ใจเราที่มีต่อยูซุฟ และแท้จริงเรานั้นเป็นผู้บริสุทธิ์ใจต่อเขา”');
INSERT INTO "king_fahad_thai" VALUES(12,12,'12:12','พรุ่งนี้ขอให้ส่งเขาไปกับเรา เพื่อเขาจะกินให้อิ่มและเล่นอย่างสนุก และแท้จริงเรานั้นจะเป็นผู้คุ้มกันเขา”');
INSERT INTO "king_fahad_thai" VALUES(12,13,'12:13','เขากล่าวว่า “แท้จริงมันจะทําให้ฉันเศร้าใจ เมื่อพวกเจ้าจะเอาเขาไป และฉันกลัวว่า สุนัขป่าจะกินเขา ขณะที่พวกเจ้ามิได้เอาใจใส่ต่อเขา”');
INSERT INTO "king_fahad_thai" VALUES(12,14,'12:14','พวกเขากล่าวว่า “หากสุนัขป่ากินเขาทั้ง ๆ ที่พวกเรามีจํานวนมาก ดังนั้นแท้จริงพวกเราเป็นผู้ขาดทุนแน่นอน”');
INSERT INTO "king_fahad_thai" VALUES(12,15,'12:15','เมื่อพวกเขาพาเขาไป พวกเขาตกลงกันว่าจะเอาเขาไปโยนในบ่อลึก และเราได้วะฮีแก่เขาว่า ”แน่นอน เจ้าจะได้เล่าแก่พวกเขาถึงการกระทําของพวกเขาในครั้งนี้ โดยที่พวกเขาไม่รู้สึก”');
INSERT INTO "king_fahad_thai" VALUES(12,16,'12:16','และพวกเขาได้กลับมาหาพ่อของพวกเขาเวลาค่ํา พลางร้องไห้');
INSERT INTO "king_fahad_thai" VALUES(12,17,'12:17','พวกเขากล่าวว่า “โอ้พ่อของเรา ! พวกเราได้ออกไปวิ่งแข่งกัน และเราได้ปล่อยยูซุฟไว้เฝ้าสิ่งของ ๆเรา แล้วสุนัขป่าได้มากินเขาและท่านย่อมไม่เชื่อเราทั้ง ๆ ที่เราเป็นผู้สัตย์จริง”');
INSERT INTO "king_fahad_thai" VALUES(12,18,'12:18','และพวกเขาได้นําเสื้อของเขามา มีเลือดปลอมติดอยู่ “แต่ว่าพวกเจ้าได้แต่งเรื่องขึ้นเพื่อพวกเจ้า ดังนั้น การอดทนเป็นสิ่งที่ดีและอัลลอฮ์ทรงเป็นผู้ให้ความช่วยเหลือในสิ่งที่พวกเจ้ากล่าวอ้าง”');
INSERT INTO "king_fahad_thai" VALUES(12,19,'12:19','และคณะเดินทางได้มาถึง ดังนั้นพวกเขาได้ส่งคนแบกน้ําของพวกเขา(ไปตักน้ําจากบ่อ) เขาได้หย่อนถังของเขาลงไป(ในบ่อ)เขากล่าวว่า “โอ้ข่าวดีจ๊ะ! นี่มันเด็กนี่ และพวกเขาได้ซ่อนเขาไว้เป็นสินค้า และอัลลอฮ์ทรงรอบรู้ในสิ่งที่พวกเขากระทํา');
INSERT INTO "king_fahad_thai" VALUES(12,20,'12:20','และพวกเขาได้ขายเขาด้วยราคาถูก นับได้ไม่กี่ดิรฮัม และพวกเขาเป็นผู้มักน้อย');
INSERT INTO "king_fahad_thai" VALUES(12,21,'12:21','และผู้ที่ซื้อเขามาจากอียิปต์ กล่าวกับภริยาของเขาว่า “จงให้ที่พักแก่เขาอย่างมีเกียรติ บางทีเขาจะทําประโยชน์ให้เราได้บ้างหรือรับเขาเป็นบุตร” และเช่นนั้นแหละเราได้ทําให้ยูซุฟมีอํานาจในแผ่นดิน และเพื่อเราจะได้สอนให้เขารู้วิชาทํานายฝัน และอัลลอฮ์ทรงเป็นผู้พิจิตในกิจการของพระองค์ และแต่ว่าส่วนใหญ่ของมนุษย์ไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(12,22,'12:22','แลเมื่อเขาบรรลุวัยหนุ่มฉกรรจ์ของเขา เราได้ให้ความสุขุมรอบคอบและวิชาการแก่เขาและเช่นนั้นแหละ เราตอบแทนแก่บรรดาผู้กระทําความดี');
INSERT INTO "king_fahad_thai" VALUES(12,23,'12:23','และนางได้ยั่วยวนเขาโดยที่เขาอยู่ในบ้านของนาง และนางได้ปิดประตูอย่างแน่นและกล่าวว่า”มานี่ซิ!“ เขากล่าวว่า “ฉันขอความคุ้มครองต่ออัลลอฮ์ แท้จริงเขาเป็นนายของฉัน ให้ที่พักพิงที่ดียิ่งแก่ฉัน แท้จริงบรรดาผู้อธรรมจะไม่บรรลุความสําเร็จ”');
INSERT INTO "king_fahad_thai" VALUES(12,24,'12:24','และแท้จริง นางได้ตั้งใจมั่นในตัวเขาและเขาก็ตั้งใจในตัวนาง หากเขาไม่เห็นหลักฐานแห่งพระเจ้าของเขา เช่นนั้นแหละเพื่อเราจะให้ความชั่วและการลามห่างไกลจากเขา แท้จริงเขาคือคนหนึ่งในปวงบ่าวของเราที่สุจริต');
INSERT INTO "king_fahad_thai" VALUES(12,25,'12:25','และทั้งสองได้วิ่งไปที่ประตู และนางได้ดึงเสื้อของเขาขาดทางด้านหลัง และทั้งสองได้พบสามีของนางที่ประตู นางกล่าวว่า“อะไรคือการตอบแทนของผู้ประสงค์ร้ายต่อภริยาของท่านนอกจากการจําคุกหรือการลงโทษอย่างเจ็บปวด”');
INSERT INTO "king_fahad_thai" VALUES(12,26,'12:26','เขากล่าวว่า “นางได้ยั่วยวนขืนใจฉัน” และพยานคนหนึ่งในบ้านของนางได้เป็นพยาน ”หากเสื้อของเขาถูกดึงขาดทางด้านหน้า ดังนั้นนางก็พูดจริง และเขาอยู่ในหมู่ผู้กล่าวเท็จ”');
INSERT INTO "king_fahad_thai" VALUES(12,27,'12:27','และหากว่าเสื้อของเขาถูกดึงขาดทางด้านหลัง นางก็กล่าวเท็จ และเขาอยู่ในหมู่ผู้พูดจริง”');
INSERT INTO "king_fahad_thai" VALUES(12,28,'12:28','ดังนั้น เมื่อเขาเห็นเสื้อของเขาถูกดึงขาดทางด้านหลัง เขากล่าวว่า “แท้จริงมันเป็นอุบายของพวกเธอแท้จริงอุบายของพวกเธอนั้นยิ่งใหญ่”');
INSERT INTO "king_fahad_thai" VALUES(12,29,'12:29','ยูซุฟ จงผินหลังให้เรื่องนี้เถิด และเธอจงขออภัยโทษในความผิดของเธอ แท้จริงเธออยู่ในหมู่ผู้กระทําผิด”');
INSERT INTO "king_fahad_thai" VALUES(12,30,'12:30','และพวกผู้หญิงในเมืองกล่าวว่า ภริยาของผู้ว่าฯ ได้ยั่วยวนเด็กรับใช้ของนาง แน่นอนเขาทําให้นางหลงรัก แท้จริงเราเห็นว่านางอยู่ในการหลงผิดอย่างชัดแจ้ง”');
INSERT INTO "king_fahad_thai" VALUES(12,31,'12:31','เมื่อนางได้ยินเสียง(กล่าวหา) โจษจรรย์ของนางเหล่านั้น นางจึงส่งคนไปยังนางเหล่านั้นและนางได้เตรียมที่พักพิงสําหรับนางเหล่านั้นและได้นํามีดมาให้ทุกคนในหมู่นางเหล่านั้น และนางกล่าว (แก่เขาว่า) “จงออกไปหานางเหล่านั้น” เมื่อนางเหล่านั้นเห็นเขาก็ให้การสรรเสริญและเฉือนมือของพวกนาง และเขากล่าวว่า “เป็นไปไม่ได้ นี่ไม่ใช่มนุษย์เป็นแน่ มิใช่อื่นใดนอกจากมะลักผู้มีเกียรติ”');
INSERT INTO "king_fahad_thai" VALUES(12,32,'12:32','นางกล่าวว่า “นั่นคือสิ่งที่พวกเธอประณามฉันเกี่ยวกับเขา และแน่นอนฉันได้ยั่วยวนเขาแต่เขาขัดขวางอย่างแข็งขัน และหากเขาไม่ปฏิบัติตามที่ฉันสั่งเขา แน่นอนเขาจะถูกจําคุกและจะอยู่ในหมู่ผู้ยอมจํานน”');
INSERT INTO "king_fahad_thai" VALUES(12,33,'12:33','เขากล่าวว่า “โอ้ ข้าแต่พระเจ้าของข้าพระองค์ คุกนั้นเป็นที่รักยิ่งแก่ข้าพระองค์กว่าสิ่งที่พวกนางเรียกร้องข้าพระองค์ไปสู่มัน และหากพระองค์มิทรงให้อุบายของพวกนางพ้นไปจากข้าพระองค์แล้ว ข้าพระองค์อาจจะโน้มเอียงไปหาพวกนาง และข้าพระองค์จะเป็นคนหนึ่งในหมู่ผู้โง่เขลา”');
INSERT INTO "king_fahad_thai" VALUES(12,34,'12:34','ดังนั้น พระเจ้าของเขาได้ตอบรับเขาแล้วพระองค์ทรงให้อุบายของพวกนางหันห่างไปจากเขา แท้จริงพระองค์คือผู้ทรงได้ยิน ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(12,35,'12:35','เมื่อเป็นที่ประจักษ์แก่พวกเขาหลังจากที่ได้พบเห็นหลักฐาน (ก็ลงความเห็นว่า) ต้องขังเขาไว้ระยะหนึ่ง');
INSERT INTO "king_fahad_thai" VALUES(12,36,'12:36','และชายหนุ่มสองคน(มหาดเล็ก) ได้เข้าคุกพร้อมกับเขา หนึ่งในสองคนกล่าวว่า “แท้จริงฉันฝันเห็นว่าฉันคั้นเหล้า” และอีกคนหนึ่งกล่าวว่า “แท้จริงฉันฝันเห็นว่าฉันแบกขนมปังไว้บนศรีษะของฉัน แล้วนกได้มากินมัน จงบอกเราด้วยการทํานายฝัน แท้จริงเราเห็นท่านอยู่ในหมู่ผู้ทําความดี”');
INSERT INTO "king_fahad_thai" VALUES(12,37,'12:37','เขากล่าวว่า “อาหารที่ท่านทั้งสองจะได้รับจะยังไม่มาถึงท่านทั้งสอง เว้นแต่ฉันจะบอกกับท่านทั้งสองเป็นการทํานายมัน ก่อนที่มันจะมาถึงท่านทั้งสอง นั่นแหละคือสิ่งที่พระเจ้าของฉันทรงสอนฉัน แท้จริงฉันได้ละทิ้งแนวทางของกลุ่มชนผู้ไม่ศรัทธาต่ออัลลอฮ์ และพวกเขาเป็นพวกที่ปฏิเสธศรัทธาต่อวันปรโลก');
INSERT INTO "king_fahad_thai" VALUES(12,38,'12:38','และฉันได้ดําเนินตามแนวทางของบรรพบุรุษของฉัน คือ อิบรอฮีมและอิสฮากและยะอฺกูบ ไม่เป็นการบังควรแก่เราที่จะตั้งภาคีด้วยสิ่งใดต่ออัลลอฮ์ นั่นคือความโปรดปรานของอัลลอฮ์แก่เราและมนุษยชาติ แต่ส่วนใหญ่ของมนุษย์ไม่ขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(12,39,'12:39','“โอ้ เพื่อนร่วมคุกทั้งสองของฉันเอ๋ย! พระเจ้าหลายองค์ดีกว่า หรือว่าอัลลอฮ์เอกองค์ผู้ทรงอนุภาพ');
INSERT INTO "king_fahad_thai" VALUES(12,40,'12:40','สิ่งที่พวกท่านเคารพอิบาดะฮ์อื่นจากพระองค์ มิใช่อื่นใดนอกจากบรรดาชื่อที่พวกท่านและบรรดาบรรพบุรุษของพวกท่านใช้เรียกมัน อัลลอฮ์มิได้ประทานหลักฐานในเรื่องนี้ลงมา การตัดสินมิได้เป็นสิทธิของใครนอกจากอัลลอฮ์พระองค์ทรงใช้มิให้พวกท่านเคารพอิบาดะฮ์สิ่งใด นอกจากพระองค์เท่านั้น นั่นคือศาสนาที่เที่ยงธรรมแต่ส่วนใหญ่ของมนุษย์ไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(12,41,'12:41','“โอ้เพื่อนร่วมคุกทั้งสองของฉันเอ๋ย ! ส่วนคนหนึ่งของท่านทั้งสองเขาจะรินเหล้าให้นายของเขาและส่วนอีกคนหนึ่งจะถูกแขวนตรึง แล้วนกจะกินศรีษะของเขา เรื่องถูกกําหนดไว้ตามที่ท่านทั้งสองขอความเห็น”');
INSERT INTO "king_fahad_thai" VALUES(12,42,'12:42','และเขากล่าวแก่คนที่เขาคิดว่าจะพ้นโทษในสองคนว่า “จงเล่าเรื่องของฉันแก่นายของท่านด้วย” แล้วชัยฎอนได้ทําให้เขาลืมเรื่องของเขา(ยูซุฟ) ณ ที่นายของเขา เขาจึงอยู่ในคุกอีกหลายปี');
INSERT INTO "king_fahad_thai" VALUES(12,43,'12:43','และกษัตริย์ได้ตรัสว่า “แท้จริงฉันฝันเห็นวัวตัวเมียอ้วนเจ็ดตัวถูกวัวผอมเจ็ดตัวกินพวกมัน และรวงข้าวเขียวเจ็ดรวงถูกรวงข้าวแห้งเจ็ดรวงรัดกินมัน โอ้ขุนนางทั้งหลายเอ๋ย! จงอธิบายแก่ฉันในการฝันของฉันนี้ หากพวกท่านเป็นผู้ทํานายฝันได้”');
INSERT INTO "king_fahad_thai" VALUES(12,44,'12:44','พวกเขากล่าวว่า “เป็นการฝันที่สับสนและพวกเรามิใช่ผู้รู้ในการทํานายฝัน”');
INSERT INTO "king_fahad_thai" VALUES(12,45,'12:45','เขาผู้รอดพ้นคนหนึ่งในสองคนรําลึกขึ้นมาได้หลังจากชั่วเวลาหนึ่ง กล่าวว่า “ฉันจะบอกพวกท่านซึ่งการทํานายฝัน พวกท่านจงส่งฉันไปซิ”');
INSERT INTO "king_fahad_thai" VALUES(12,46,'12:46','“ยูซุฟผู้ซื่อสัตย์เอ๋ย! จงอธิบายแก่เราเรื่องวัวตัวเมียอ้วนเจ็ดตัวถูกวัวตัวผอมเจ็ดตัวกินมัน และรวงข้าวเจ็ดรวงถูกรวงข้าวแห้งเจ็ดรวงรัดกินมัน หวังว่าฉันจะกลับไปหามวลชน เพื่อพวกเขาจะได้รู้เรื่อง”');
INSERT INTO "king_fahad_thai" VALUES(12,47,'12:47','เขากล่าวว่า “พวกท่านจะเพาะปลูก 7 ปีต่อเนื่องกัน สิ่งที่พวกท่านเก็บเกี่ยวได้จงปล่อยมันไว้ในรวงของมัน เว้นแต่ส่วนน้อยที่ท่านจะกินมัน');
INSERT INTO "king_fahad_thai" VALUES(12,48,'12:48','หลังจากนั้น 7 ปีแห่งความแร้นแค้นจะติดตามมา มันจะกินสิ่งที่พวกท่านสะสมไว้สําหรับมัน นอกจากส่วนน้อยที่พวกท่านจะเก็บไว้ทําพันธุ์');
INSERT INTO "king_fahad_thai" VALUES(12,49,'12:49','“หลังจากนั้นปีที่มวลชนจะได้รับฝนติดตามมา และในปีนั้นพวกเขาจะได้คั้นองุ่น”');
INSERT INTO "king_fahad_thai" VALUES(12,50,'12:50','และกษัตริย์ตรัสว่า “จงนําเขามาหาฉันซิ!” เมื่อคนนําข่าวมาหาเขา เขากล่าวว่า “จงกลับไปยังนายของท่าน แล้วถามพระองค์ถึงเรื่องของพวกผู้หญิงที่เฉือนมือของนาง แท้จริงพระเจ้าของฉันทรงรอบรู้ถึงอุบายของนางเหล่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(12,51,'12:51','กษัตริย์ตรัสว่า “เรื่องราวของพวกเธอเป็นเช่นไร เมื่อพวกเธอยั่วยวนยูซุฟ” พวกนางกล่าวว่า “ขออัลลอฮ์ทรงคุ้มครอง เราไม่รู้มาก่อนเลยว่าเขาทําชั่ว” ภริยาของผู้ว่าฯ กล่าวว่า “บัดนี้ความจริงได้ปรากฏขึ้นแล้ว ฉันได้ยั่วยวนเขาและแท้จริงเขาคือผู้หนึ่งในหมู่ผู้สัตย์จริงอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(12,52,'12:52','“ทั้งนี้เพื่อให้เขารู้ว่า แท้จริงฉันมิได้ทรยศต่อเขาโดยลับหลัง และแท้จริงอัลลอฮ์จะไม่ทรงชี้แนะแนวการวางแผนของพวกทรยศ');
INSERT INTO "king_fahad_thai" VALUES(12,53,'12:53','“และฉันไม่อาจชําระจิตใจของฉันให้สะอาดบริสุทธิ์ได้ แท้จริงจิตใจนั้นถูกครอบงําไว้ด้วยความชั่วนอกจากที่พระเจ้าของฉันทรงเมตตา แท้จริงพระเจ้าของฉันเป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ”');
INSERT INTO "king_fahad_thai" VALUES(12,54,'12:54','และกษัตริย์ตรัสว่า “จงนําเขามาหาฉันซิ! ฉันจะแต่งตั้งเขาให้เป็นผู้ใกล้ชิดของฉัน” เมื่อยูซุฟได้สนทนากับพระองค์แล้ว พระองค์ตรัสว่า “แท้จริงท่านอยู่ต่อหน้าเราวันนี้ เป็นผู้มีตําแหน่งสูงเป็นที่ไว้วางใจ”');
INSERT INTO "king_fahad_thai" VALUES(12,55,'12:55','เขากล่าวว่า “ได้โปรดแต่งตั้งฉันให้ควบคุมการคลังของประเทศ แท้จริงฉันเป็นผู้ชื่อสัตย์ผู้รู้”');
INSERT INTO "king_fahad_thai" VALUES(12,56,'12:56','และเช่นนั้นแหละ เราได้ให้ยูซุฟมีอํานาจในแผ่นดิน เขาจะพํานักอยู่ที่ใดได้ตามต้องการ เราให้ความเมตตาของเราแก่ผู้ที่เราประสงค์ และเราจะมิให้รางวัลของบรรดาผู้ทําความดีสูญหาย');
INSERT INTO "king_fahad_thai" VALUES(12,57,'12:57','และแน่นอน รางวัลในปรโลกนั้นดียิ่งสําหรับบรรดาผู้ศรัทธาและพวกเขายําเกรง');
INSERT INTO "king_fahad_thai" VALUES(12,58,'12:58','และพี่น้องของยูซุฟได้มา แล้วเขาไปหาเขา ยูซุฟจําพวกเขาได้ แต่พวกเขาจํายูซุฟไม่ได้');
INSERT INTO "king_fahad_thai" VALUES(12,59,'12:59','และเมื่อเขาได้จัดเตรียมให้แก่พวกเขาซึ่งเสบียงอาหารของพวกเขาแล้ว เขากล่าวว่า “จงนําน้องชายของพวกท่านจากพ่อของพวกท่านมาหาฉันด้วย พวกท่านไม่เห็นหรือว่าแท้จริงฉันได้ตวงให้อย่างครบถ้วน และฉันนั้นดียิ่งในหมู่ผู้ให้การต้อนรับ');
INSERT INTO "king_fahad_thai" VALUES(12,60,'12:60','“หากพวกท่านไม่นําเขามาหาฉัน จะไม่มีการตวงจากฉันให้พวกท่านอีก และพวกท่านอย่าเข้ามาใกล้ฉัน”');
INSERT INTO "king_fahad_thai" VALUES(12,61,'12:61','พวกเขากล่าวว่า “เราจะเกลี้ยกล่อมบิดาของเขาให้เขาออกมา และแท้จริงเราจะทําได้อย่างแน่นอน”');
INSERT INTO "king_fahad_thai" VALUES(12,62,'12:62','และเขากล่าวแก่พวกคนใช้ของเขาว่า จงใส่เงินของพวกเขาไว้ในย่ามของพวกเขา หวังว่าพวกเขาคงจําไม่ได้ เมื่อพวกเขากลับไปหาครอบครัวของพวกเขา เพื่อพวกเขาจะได้กลับมาอีก');
INSERT INTO "king_fahad_thai" VALUES(12,63,'12:63','เมื่อพวกเขากลับไปหาพ่อของพวกเขาพวกเขากล่าวว่า “โอ้พ่อของเรา! การตวงถูกห้ามแก่เรา จงส่งน้องของเราไปกับเราเพื่อเราจะได้ส่วนตวง และแท้จริงเราจะเป็นผู้คุ้มกันเขา”');
INSERT INTO "king_fahad_thai" VALUES(12,64,'12:64','เขา(พ่อ) กล่าวว่า “ฉันจะไม่ไว้ใจพวกเจ้าที่มีต่อเขาอีก นอกจากว่าเช่นกับที่ฉันได้ไว้ใจพวกเจ้าที่มีต่อพี่ของเขาเมื่อก่อนนี้ ดังนั้นอัลลอฮ์ทรงเป็นผู้คุ้มกันที่ดียิ่ง และพระองค์เท่านั้นทรงเมตตายิ่งในหมู่ผู้เมตตาทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(12,65,'12:65','และเมื่อพวกเขาเปิดย่ามสะเบียงของพวกเขา ก็เห็นเงินของพวกเขาถูกคืนกลับมายังพวกเขาด้วยพวกเขาจึงกล่าวว่า”โอ้พ่อของเรา! เราต้องการอะไรอีกเล่า? นี่เงินของเราถูกคืนกลับมายังเรา แล้วเรายังได้นําสะเบียงมายังครอบครัวของเราอีก และเราจะคุ้มกันน้องของเรา และเราจะได้เพิ่มการตวงอีกหนึ่งตัวลา นั่นเป็นการตวงที่ง่าย”');
INSERT INTO "king_fahad_thai" VALUES(12,66,'12:66','เขา(พ่อ) กล่าวว่า “ฉันจะไม่ส่งเขาไปกับพวกเจ้า จนกว่าพวกเจ้าจะนําสัญญาจากอัลลอฮ์ให้แก่ฉันเสียก่อนว่า พวกเจ้าจะนําเขากลับมาอย่างแน่นอน เว้นแต่พวกเจ้าจะถูกปิดล้อม” เมื่อพวกเขาได้ให้สัญญาของพวกเขาแก่เขาแล้ว (พ่อ) กล่าวว่า ”อัลลอฮ์ทรงเป็นพยานต่อสิ่งที่เราสัญญาไว้”');
INSERT INTO "king_fahad_thai" VALUES(12,67,'12:67','และเขากล่าวว่า “โอ้ลูกเอ๋ย! พวกเจ้าอย่าเข้าเมืองทางเดียวกัน แต่พวกเจ้าจงเข้าเมืองต่างทางกัน และฉันไม่อาจให้ความคุ้มกันพวกเจ้าจากอัลลอฮ์ได้ การตัดสินเป็นสิทธิ์ของอัลลอฮ์ฉันขอมอบการไว้ใจแด่พระองค์และขอให้บรรดาผู้มอบการไว้ใจจงไว้ใจแด่พระองค์”');
INSERT INTO "king_fahad_thai" VALUES(12,68,'12:68','และเมื่อได้เข้าเมืองตามที่พ่อของพวกเขาได้สั่งไว้ ไม่มีสิ่งใดที่จะช่วยพวกเขาให้พ้นจากอัลลอฮ์ได้ เว้นแต่ความต้องการในจิตใจของยะอฺกูบซึ่งเขาได้ปฏิบัติไป และแท้จริงเขาเป็นผู้มีความรู้ซึ่งเราได้สอนเขา แต่ส่วนมากของมนุษย์ไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(12,69,'12:69','และเมื่อพวกเขาได้เข้าไปหายูซุฟ เขารับน้องชายของเขาไปอยู่กับเขา เขากล่าวว่า “แท้จริงฉันเป็นพี่ชายของเจ้า ดังนั้นเจ้าอย่าเสียใจในสิ่งที่พวกเขากระทํา”');
INSERT INTO "king_fahad_thai" VALUES(12,70,'12:70','เมื่อเขาได้จัดเตรียมสะเบียงอาหารของพวกเขาให้แก่พวกเขาแล้ว เขาได้ใส่ขันน้ํา ลงในย่ามของน้องชายเขา แล้วผู้ประกาศได้ประกาศว่า ”โอ้คณะเดินทางทั้งหลายเอ๋ย! แท้จริงพวกท่านเป็นพวกขโมย”');
INSERT INTO "king_fahad_thai" VALUES(12,71,'12:71','พวกเขากล่าวพลางหันไปทางพวกเขา(ผู้ประกาศ) ว่า “มีอะไรหายไปจากพวกท่าน?”');
INSERT INTO "king_fahad_thai" VALUES(12,72,'12:72','พวกเขากล่าวว่า “ขันน้ําของกษัตริย์หายไปจากเรา และผู้ใดนํามันมาคืนเขาจะได้รับสะเบียงเป็นรางวัลหนึ่งตัวลา และฉันเป็นผู้รับรอง”');
INSERT INTO "king_fahad_thai" VALUES(12,73,'12:73','พวกเขากล่าวว่า “ขอสาบานต่ออัลลอฮ์โดยแน่นอนพวกท่านทราบดีว่า เรามิได้มาที่นี่เพื่อทําความเสียหายในแผ่นดิน และเราก็มิใช่พวกขโมย”');
INSERT INTO "king_fahad_thai" VALUES(12,74,'12:74','พวกเขากล่าวว่า “ถ้าเช่นนั้นโทษของมันจะเป็นเช่นใด หากพวกท่านเป็นผู้กล่าวเท็จ”');
INSERT INTO "king_fahad_thai" VALUES(12,75,'12:75','พวกเขากล่าวว่า “โทษของมันคือ ผู้ใดถูกค้นพบในย่ามของเขา ดังนั้นเขาก็รับโทษของมัน เช่นนั้นแหละเราลงโทษบรรดาผู้อธรรม”');
INSERT INTO "king_fahad_thai" VALUES(12,76,'12:76','ดังนั้น เขาได้เริ่มค้นในย่ามของพวกเขาก่อนย่ามของน้องชายของเขา แล้วเขาก็ได้เอามันออกมาจากย่ามของน้องชายของเขา เช่นนั้นแหละเราได้ถูกยูซุฟใช้กลอุบาย เขาจะเอาน้องชายของเขาไว้ไม่ได้ในศาสนาของกษัตริย์ นอกจากว่าอัลลอฮ์จะทรงประสงค์ เราจะเชิดชูฐานะหลายชั้นแก่ผู้ที่เราประสงค์ และเหนือทุก ๆ ผู้ที่มีความรู้คือผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(12,77,'12:77','พวกเขากล่าวว่า “หากเขาขโมย แน่นอนพี่ชายของเขาก็ได้ขโมยมาก่อน” แต่ยูซุฟได้ซ่อนความรู้สึกไว้ในใจของเขา และไม่เปิดเผยมันแก่พวกเขา เขากล่าว “พวกท่านมีสถานะที่เลวมาก และอัลลอฮ์ทรงรอบรู้ดียิ่งที่พวกท่านกล่าวหา”');
INSERT INTO "king_fahad_thai" VALUES(12,78,'12:78','พวกเขากล่าวว่า “โอ้ท่านผู้ว่าฯ เขามีพ่อที่แก่ชรามากแล้ว ขอได้โปรดเอาคนหนึ่งในพวกเราไว้แทน แท้จริงเราเห็นว่าท่านนั้นอยู่ในหมู่ผู้ทําความดี”');
INSERT INTO "king_fahad_thai" VALUES(12,79,'12:79','เขากล่าวว่า “เราขอความคุ้มครองด้วยอัลลอฮ์ ที่เราจะเอาคนอื่น นอกจากผู้ที่เราพบทรัพย์สินของเราอยู่ที่เขา ดังนั้น แท้จริงเราก็เป็นผู้อธรรมอย่างแน่นอน”');
INSERT INTO "king_fahad_thai" VALUES(12,80,'12:80','ดังนั้น เมื่อพวกเขาหมดอาลัยจากเขาพวกเขาก็หันหน้าเข้าปรึกษากันตามลําพัง พี่คนโตของพวกเขากล่าวว่า “พวกท่านไม่รู้ดอกหรือว่าพ่อของพวกท่านได้เอาสัญญาอย่างมั่นคงจากอัลลอฮ์แก่พวกท่าน และก่อนนี้พวกท่านก็ได้ทําผิดพลาดในเรื่องของยูซุฟ มาแล้ว ฉันจะไม่ออกจากดินแดนนี้จนกว่าพ่อของฉันจะอนุญาตแก่ฉัน หรืออัลลอฮ์จะทรงตัดสินแก่ฉัน และพระองค์ทรงเป็นผู้ตัดสินที่ดียิ่ง');
INSERT INTO "king_fahad_thai" VALUES(12,81,'12:81','พวกท่านจงกลับไปยังพ่อของพวกท่านแล้วกล่าวว่า โอ้คุณพ่อของเรา! แท้จริงลูกของท่านขโมยและเราไม่เป็นพยานเว้นแต่ในสิ่งที่เรารู้และเรามิใช่ผู้เก็บความลับ');
INSERT INTO "king_fahad_thai" VALUES(12,82,'12:82','และจงถามชาวเมืองซึ่งเราพํานักอยู่ในนั้นและกองคาราวานซึ่งเราเดินทางร่วมมากับมัน และแท้จริงเรานั้นเป็นผู้สัตย์จริงอย่างแน่นอน”');
INSERT INTO "king_fahad_thai" VALUES(12,83,'12:83','เขากล่าวว่า “แต่ว่าจิตใจของพวกเจ้าได้ตกแต่งเรื่องขึ้นเพื่อพวกเจ้า ดังนั้นการอดทนเป็นสิ่งที่ดี หวังว่าอัลลอฮ์จะทรงนําพวกเขาทั้งหมดมาหาฉัน แท้จริงพระองค์เป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ”');
INSERT INTO "king_fahad_thai" VALUES(12,84,'12:84','และเขาผินหลังให้พวกเขา และกล่าว่า “โอ้อนิจจา ยูซุฟเอ๋ย!” และตาทั้งสองข้างของเขามัวเนื่องจากความเศร้าโศก และเขาเป็นผู้อดกลั้น');
INSERT INTO "king_fahad_thai" VALUES(12,85,'12:85','พวกเขากล่าวว่า “ขอสาบานต่ออัลลอฮ์ท่านยังคงรําลึกถึงยูซุฟอยู่ จนกระทั่งท่านเจ็บจวนจะตายหรือท่านจะพินาศไป”');
INSERT INTO "king_fahad_thai" VALUES(12,86,'12:86','เขากล่าวว่า “แท้จริงฉันร้องเรียนความเศร้าสลดของฉันและความทุกข์ระทมของฉันต่ออัลลอฮ์ และฉันรู้ (เรื่องความเมตตา)จากอัลลอฮ์ซึ่งพวกเจ้าไม่รู้"');
INSERT INTO "king_fahad_thai" VALUES(12,87,'12:87','“โอ้ลูกรัก พวกเจ้าจงไปสืบข่าวของยูซุฟ และน้องของเขา และพวกเจ้าอย่าเบื่อหน่ายต่อความเมตตาของอัลลอฮ์ แท้จริงไม่มีผู้ใดเบื่อหน่ายต่อความเมตตาของอัลลอฮ์ นอกจากหมู่ชนผู้ปฏิเสธ”');
INSERT INTO "king_fahad_thai" VALUES(12,88,'12:88','ดังนั้น เมื่อพวกเขาได้เข้ามาหาเขา (ยูซุฟ) พวกเขากล่าวว่า “โอ้ท่านข้าหลวง ความทุกข์ได้ประสบกับเราและครอบครัวของเราและได้นําสินค้าราคาต่ํามา ดังนั้นขอท่านได้โปรดตวงให้เราอย่างครบถ้วน และโปรดบริจาคให้เราด้วย แท้จริงอัลลอฮ์ทรงตอบแทนผู้บริจาคทาน”');
INSERT INTO "king_fahad_thai" VALUES(12,89,'12:89','เขากล่าวว่า “พวกท่านทราบไหม สิ่งที่พวกท่านได้ทํากับยูซุฟและน้องชายของเขา เมื่อพวกท่านเป็นผู้งมงาย”');
INSERT INTO "king_fahad_thai" VALUES(12,90,'12:90','พวกเขากล่าวว่า “แน่นอน ท่านคือยูซุฟใช่ไหม?” เขากล่าวว่า “ฉันคือยูซุฟและนี่คือน้องของฉัน แน่นอนอัลลอฮ์ทรงโปรดปรานเรา แท้จริงผู้ใดที่ยําเกรงและอดทน แน่นอนอัลลอฮ์จะมิทรงให้รางวัลของบรรดาผู้ทําความดีสูญหาย”');
INSERT INTO "king_fahad_thai" VALUES(12,91,'12:91','พวกเขากล่าวว่า “ขอสาบานต่ออัลลอฮ์โดยแน่นอนอัลลอฮ์ทรงให้เกียรติท่านเหนือพวกเรา โดยที่พวกเราเป็นผู้ผิดอย่างแน่นอน”');
INSERT INTO "king_fahad_thai" VALUES(12,92,'12:92','เขากล่าวว่า “วันนี้ไม่มีการประณามพวกท่าน อัลลอฮ์จะทรงอภัยโทษพวกท่าน และพระองค์ทรงเมตตายิ่งในบรรดาผู้เมตตา');
INSERT INTO "king_fahad_thai" VALUES(12,93,'12:93','“พวกท่านจงนําเสื้อของฉันตัวนี้ไปวางไว้ที่ข้างหน้าพ่อของฉัน เขาจะกลับเป็นผู้มองเห็น และจงนําครอบครัวของพวกท่านทั้งหมดมายังฉัน”');
INSERT INTO "king_fahad_thai" VALUES(12,94,'12:94','เมื่อกองคาราวานได้ออกมา (จากอียิปต์) พ่อของพวกเขากล่าวว่า “แท้จริงฉันได้กลิ่นของยูซุฟ หากพวกท่านไม่กล่าวหาฉันว่าเหลวไหล”');
INSERT INTO "king_fahad_thai" VALUES(12,95,'12:95','พวกเขากล่าวว่า “ขอสาบานด้วยอัลลอฮ์แท้จริงท่านนั้นยังอยู่ในการหลงของท่านเช่นเดิม”');
INSERT INTO "king_fahad_thai" VALUES(12,96,'12:96','เมื่อผู้นําข่าวดีมาถึง เขาได้วางเสื้อของยูซุฟไว้ที่ข้างหน้าเขา ดังนั้นเขาจึงกลับเป็นผู้มองเห็นเขากล่าวว่า “ฉันมิได้บอกพวกเจ้าหรือว่า แท้จริงฉันรู้จากอัลลอฮ์ในสิ่งที่พวกเจ้าไม่รู้”');
INSERT INTO "king_fahad_thai" VALUES(12,97,'12:97','พวกเขากล่าวว่า “โอ้พ่อของเรา! โปรดขออภัยโทษความผิดของเราให้แก่เรา แท้จริงเราเป็นผู้ผิด”');
INSERT INTO "king_fahad_thai" VALUES(12,98,'12:98','เขากล่าวว่า “ฉันจะขออภัยโทษต่อพระเจ้าของฉันให้พวกเจ้า แท้จริงพระองค์เท่านั้นเป็นผู้ทรงอภัยโทษ ผู้ทรงเมตตาเสมอ”');
INSERT INTO "king_fahad_thai" VALUES(12,99,'12:99','ดังนั้นเมื่อพวกเขาเข้าไปหายูซุฟ เขาได้สวมกอดพ่อแม่ของเขาและกล่าวว่า “พวกท่านจงเข้ามาในอียิปต์โดยปลอดภัยเถิด อินชาอัลลอฮ์”');
INSERT INTO "king_fahad_thai" VALUES(12,100,'12:100','และเขาได้ยกย่องพ่อแม่ของเขาขึ้นบนบัลลังก์แล้วพวกเขาก็ก้มลงสุญูด(คารวะ) เขา และกล่าวว่า “โอ้พ่อของฉัน นี่คือการทํานายฝันของฉันแต่ครั้งก่อน พระเจ้าของฉันทรงทําให้เป็นจริง และพระองค์ทรงให้เกียรติฉัน โดยพระองค์ทรงให้ฉันออกจากคุก และนําพวกท่านมาจากชนบท หลังจากที่ชัยฏอนได้ยุยงให้เกิดการแตกแยกระหว่างฉันกับพี่น้องของฉัน แท้จริงพระเจ้าของฉันทรงโปรดปรานแก่ผู้ที่พระองค์ทรงประสงค์ แท้จริงพระองค์เท่านั้นเป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(12,101,'12:101','“โอ้พระเจ้าของข้าพระองค์ แท้จริงพระองค์ทรงได้ประทานอํานาจบางส่วนแก่ข้าพระองค์และทรงสอนข้าพระองค์ให้รู้การทํานายฝัน พระผู้ทรงสร้างชั้นฟ้าทั้งหลายและแผ่นดิน พระองค์เป็นผู้คุ้มครองข้าพระองค์ทั้งในโลกดุนยาและอาคิเราะฮ์ ขอพระองค์ทรงให้ข้าพระองค์ตายในสภาพเป็นผู้นอบน้อม และทรงให้ข้าพระองค์รวมอยู่ในหมู่คนดีทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(12,102,'12:102','นั่นคือส่วนหนึ่งจากข่าวเร้นลับที่เราได้วะฮีแก่เจ้า และเจ้ามิได้อยู่กับพวกเขา ขณะที่พวกเขาตกลงกันในเรื่องของพวกเขาและพวกเขาวางแผน');
INSERT INTO "king_fahad_thai" VALUES(12,103,'12:103','และส่วนใหญ่ของมนุษย์จะไม่ศรัทธาต่อเจ้า ถึงแม้เจ้าปรารถนาอย่างยิ่งก็ตาม');
INSERT INTO "king_fahad_thai" VALUES(12,104,'12:104','และเจ้ามิได้ขอรางวัลพวกเขาในเรื่องนี้มัน(อัลกุรอาน) มิใช่อื่นใด นอกจากเป็นการตักเตือนแก่ปวงมนุษย์');
INSERT INTO "king_fahad_thai" VALUES(12,105,'12:105','และกี่มากน้อยแล้ว จากสัญญาณในชั้นฟ้าทั้งหลายและแผ่นดิน ที่พวกเขาผ่านมันไปโดยที่พวกเขาผินหลังให้');
INSERT INTO "king_fahad_thai" VALUES(12,106,'12:106','และส่วนใหญ่ของพวกเขาจะไม่ศรัทธาต่ออัลลอฮ์ เว้นแต่พวกเขาเป็นผู้ตั้งภาคี');
INSERT INTO "king_fahad_thai" VALUES(12,107,'12:107','พวกเขาจะปลอดภัยละหรือ เมื่อการครอบคลุมแห่งการลงโทษของอัลลอฮ์จะมายังพวกเขา หรือเมื่อวันอวสานจะมายังพวกเขาอย่างฉับพลัน โดยที่พวกเขาไม่รู้สึกตัว');
INSERT INTO "king_fahad_thai" VALUES(12,108,'12:108','จงกล่าวเถิดมุฮัมมัด “นี่คือแนวทางของฉัน ฉันเรียกร้องไปสู่อัลลอฮ์อย่างประจักษ์แจ้งทั้งตัวฉันและผู้ปฏิบัติตามฉัน และมหาบริสุทธิ์แห่งอัลลอฮ์ ฉันมิได้อยู่ในหมู่ตั้งภาคี”');
INSERT INTO "king_fahad_thai" VALUES(12,109,'12:109','และเรามิได้ส่งผู้ใดมาก่อนเจ้า นอกจากบรรพบุรุษจากชาวเมืองที่เราให้วะฮีแก่พวกเขา พวกเขามิได้ตระเวนไปในแผ่นดินดอกหรือ เพื่อพวกเขาจะเห็นบั้นปลายของบรรดาผู้ที่มาก่อนพวกเขาว่าเป็นอย่างไร และแน่นอนโลกอาคิเราะฮ์นั้นดียิ่งสําหรับบรรดาผู้ยําเกรง พวกท่านมิได้ใช้สติปัญญาดอกหรือ?');
INSERT INTO "king_fahad_thai" VALUES(12,110,'12:110','จนกระทั่งเมื่อบรรดาร่อซูลหมดหวัง และคิดว่าพวกเขา(มุชริกีน) ปฏิเสธศรัทธา แล้วการช่วยเหลือของเราได้มายังพวกเขา ดังนั้นผู้ที่เราประสงค์(บรรดาร่อซูลและบรรดามุมิน) ก็ถูกช่วยเหลือให้รอด และการลงโทษของเราจะไม่ถูกผลักออกจากหมู่ชนผู้กระทําผิด');
INSERT INTO "king_fahad_thai" VALUES(12,111,'12:111','โดยแน่นอนยิ่ง ในเรื่องราวของพวกเขา เป็นบทเรียนสําหรับบรรดาผู้มีสติปัญญา มิใช่เป็นเรื่องราวที่ถูกปั้นแต่งขึ้น แต่ว่าเป็นการยืนยันความจริงที่อยู่ต่อหน้าเขา และเป็นการแจกแจงทุกสิ่งทุกอย่าง และเป็นการชี้ทางที่ถูกต้อง และเป็นการเมตตาแก่หมู่ชนผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(13,1,'13:1','อะลิฟ ลาม มีม รอ เหล่านี้คือบรรดาโองการแห่งคัมภีร์ และสิ่งที่ได้ถูกประทานลงมาแก่เจ้าจากพระเจ้าของเจ้านั้นเป็นสัจธรรม และแต่ส่วนมากของมนุษย์ไม่ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(13,2,'13:2','อัลลอฮฺคือผู้ทรงยกชั้นฟ้าทั้งหลายไว้โดยปราศจากเสาค้ําจุน ซึ่งพวกเจ้ามองเห็นมัน แล้วทรงสถิตย์อยู่บนบัลลังก์ และทรงให้ดวงอาทิตย์และดวงจันทร์เป็นประโยชน์ (แก่มนุษย์) ทุกสิ่งโคจรไปตามวาระที่ได้กําหนดไว้ ทรงบริหารกิจการทรงจําแนกโองการทั้งหลายให้ชัดแจ้ง เพื่อพวกเจ้าจะได้เชื่อมั่นในการพบพระเจ้าของพวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(13,3,'13:3','และพระองค์คือผู้ทรงแผ่แผ่นดิน และในนั้นทรงทําให้มันมีภูเขามั่นคง และลําน้ํามากหลาย และจากพืชผลทุกชนิดทรงให้มีจํานวนคู่ ทรงให้กลางคืนครอบคลุมกลางวัน แท้จริงในการนั้นแน่นอนย่อมเป็นสัญญาณสําหรับหมู่ชนผู้ใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(13,4,'13:4','และในแผ่นดินมีเขตแดนติดต่อใกล้เคียงกัน และมีสวนพฤกษา เช่น ต้นองุ่น และต้นที่มีเมล็ด และต้นอินทผลัมที่มาจากรากเดียวกัน และมิใช่รากเดียวกัน ได้รับแหล่งน้ําเดียวกัน และเราได้ให้บางชนิดดีเด่นกว่าอีกบางชนิดในรสชาติ แท้จริงในการนั้น แน่นอนเป็นสัญญาณสําหรับหมู่ชนผู้ใช้ปัญญา');
INSERT INTO "king_fahad_thai" VALUES(13,5,'13:5','และหากเจ้า (มุฮัมมัด) ฉงน ดังนั้นคํากล่าวของพวกเขาก็น่าฉงน (ที่ว่า) “เมื่อเรากลายเป็นผุยผงไปแล้ว แท้จริงเราจะเกิดใหม่กระนั้นหรือ?” ชนเหล่านั้นคือบรรดาผู้ปฎิเสธศรัทธาต่อพระเจ้าของพวกเขา และชนเหล่านั้นคือชาวนรก พวกเขาจะอยู่ในนั้นตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(13,6,'13:6','และพวกเขาเร่งเร้าเจ้า ขอความชั่ว (การลงโทษ) ก่อนความดี (ความสุข) และแน่นอนได้มีหลายตัวอย่างเกิดขึ้นก่อนหน้าพวกเขาแล้ว และแท้จริงพระเจ้าของเจ้าเป็นผู้ทรงอภัยโทษแก่มนุษย์ต่อการอธรรมของพวกเขา และแท้จริงพระเจ้าของเจ้าเป็นผู้ทรงเข้มงวดในการลงโทษ');
INSERT INTO "king_fahad_thai" VALUES(13,7,'13:7','และบรรดาผู้ปฏิเสธศรัทธาจะกล่าวว่า “ทําไมจึงไม่มีปาฏิหาริย์จากพระเจ้าของเขาถูกประทานลงมาให้แก่เขา?” แท้จริงเจ้าเป็นเพียงผู้ตักเตือนเท่านั้น และสําหรับทุกๆ หมู่ชนย่อมมีผู้ชี้แนะแนวทาง');
INSERT INTO "king_fahad_thai" VALUES(13,8,'13:8','อัลลอฮฺทรงรอบรู้สิ่งที่ผู้หญิงทุกคนอุ้มครรภ์ และที่บรรดามดลูกคลอดก่อนกําหนดและที่เกินกําหนด และทุกๆสิ่ง ณ ที่พระองค์นั้นมีการกําหนดภาวะไว้');
INSERT INTO "king_fahad_thai" VALUES(13,9,'13:9','ผู้ทรงรอบรู้สิ่งเร้นลับและสิ่งเปิดเผยผู้ทรงเกรียงไกร ผู้ทรงสูงส่งยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(13,10,'13:10','เท่าเทียมกันในหมู่พวกเจ้า ผู้ที่ปกปิดคําพูด และผู้ที่เปิดเผยมัน และผู้ที่ซ่อนการกระทําในเวลากลางคืนและผู้ที่เดินไปอย่างเปิดเผยในเวลากลางวัน');
INSERT INTO "king_fahad_thai" VALUES(13,11,'13:11','สําหรับเขามีมะลาอิกะฮฺผู้เฝ้าติดตามทั้งข้างหน้าและข้างหลังเขา รักษาเขาตามพระบัญชาของอัลลอฮฺ แท้จริงอัลลอฮฺจะมิทรงเปลี่ยนแปลงสภาพของชนกลุ่มใด จนกว่าพวกเขาจะเปลี่ยนแปลงสภาพของพวกเขาเอง และเมื่ออัลลอฮฺทรงปรารถนาความทุกข์แก่ชนกลุ่มใดก็จะไม่มีผู้ตอบโต้พระองค์ และสําหรับพวกเขาไม่มีผู้ช่วยเหลือนอกจากพระองค์');
INSERT INTO "king_fahad_thai" VALUES(13,12,'13:12','พระองค์คือผู้ทรงให้พวกเจ้าเห็นฟ้าแลบ เพื่อความกลัวและความหวัง และทรงให้เกิดเมฆทึบ');
INSERT INTO "king_fahad_thai" VALUES(13,13,'13:13','และฟ้าลั่นจะแซ่ซร้อง สดุดีด้วยการสรรเสริญพระองค์ และมะลาอิกะฮฺจะสดุดีด้วย เพราะความกลัวพระองค์ และพระองค์ทรงให้ฟ้าผ่าแล้วมันจะฟาดไปยังผู้ที่พระองค์ทรงประสงค์ โดยพวกเขาโต้เถียงกันในเรื่องของอัลลอฮฺ และพระองค์คือผู้ทรงอํานาจยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(13,14,'13:14','สําหรับพระองค์นั้นคือการวิงวอนที่แท้จริง และบรรดาผู้วิงวอนอื่นจากพระองค์ มัน(เจว็ด) จะไม่สนองตอบใดๆแก่พวกเขา เว้นแต่เสมือนกับผู้ที่แบมือทั้งสองไปยังน้ําเพื่อให้ไหลสู่ปากของเขา และมันจะไหลถังไม่ได้ และการวิงวอนของพวกปฏิเสธศรัทธานั้นหาใช่อื่นใด นอกจากอยู่ในการหลงผิด');
INSERT INTO "king_fahad_thai" VALUES(13,15,'13:15','และผู้อยู่ในชั้นฟ้าทั้งหลายและแผ่นดินต่างก็สญูดต่ออัลลอฮด้วยความภักดีและด้วยความจํายอม และเงาของมันจะสญูดด้วย ทั้งยามเช้าและยามเย็น');
INSERT INTO "king_fahad_thai" VALUES(13,16,'13:16','จงกล่าวเถิดมุฮัมมัด “ใครคือพระเจ้าแห่งชั้นฟ้าทั้งหลายและแผ่นดิน?” จงกล่าวเถิด ”อัลลอฮฺ” จงกล่าวเถิด “พวกท่านได้ยึดเอาบรรดาผู้คุ้มครองอื่นจากอัลลอฮฺกระนั้นหรือ? ซึ่งพวกเขาไม่มีอํานาจให้คุณและให้โทษแก่ตัวของพวกเขาเอง” จงกล่าวเถิด “คนตาบอดกับคนตาดีจะเหมือนกันหรือ? หรือความมืดจะเหมือนกับแสงสว่างหรือ?” หรือพวกเขาได้ตั้งเหล่าภาคีขึ้นเพื่ออัลลอฮฺนั้น เพื่อให้ได้สร้างเช่นกับการสร้างของพระองค์ แล้วการสร้างนั้นได้คล้ายคลึงแก่พวกเขากระนั้นหรือ? จงกล่าวเถิด “อัลลอฮฺคือผู้ทรงสร้างทุกสิ่ง และพระองค์คือผู้ทรงเอกะ ผู้ทรงพิชิต"');
INSERT INTO "king_fahad_thai" VALUES(13,17,'13:17','พระองค์ทรงประทานน้ําลงมาจากฟากฟ้า แล้วลําน้ําต่าง ๆ ก็ไหลไปตามปริมาณของมัน กระแสน้ําได้พัดพาเอาฟองลอยอยู่เหนือน้ํา และจากสิ่งที่พวกเขาหลอมลงไปในไฟ เพื่อหวังทําเครื่องประดับหรือเครื่องใช้จะมีฟองเช่นกันในทํานองนั้นอัลลอฮทรงยกตัวอย่างความจริงและความเท็จสําหรับฟองนั้นก็จะออกไปเป็นสิ่งเหลือเดน ส่วนที่เป็นประโยชน์แก่มนุษย์ก็คงจะอยู่ในแผ่นดิน ในทํานองนั้น อัลลอฮทรงยกอุทาหรณ์ทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(13,18,'13:18','สําหรับบรรดาผู้ตอบสนองต่อพระเจ้าของพวกเขา คือการได้รับความดี และบรรดาผู้ไม่ตอบสนองต่อพระองค์ แม้ว่าพวกเขาจะมีทั้งหมดที่มีอยู่ในแผ่นดินและมีอีกเยี่ยงนั้น พวกเขาจะยอมเอามาไถ่โทษอย่างแน่นอน ชนเหล่านั้นสําหรับพวกเขาคืนการมีบัญชีที่ชั่ว และที่พํานักของพวกเขาคือนรกญะฮันนัน มันเป็นที่พํานักที่ชั่วช้ายิ่ง');
INSERT INTO "king_fahad_thai" VALUES(13,19,'13:19','ดังนั้น ผู้ที่รู้ว่าแท้จริงสิ่งที่ถูกประทานแก่เจ้าจากพระเจ้าของเจ้านั้นเป็นความจริง จะเหมือนกับผู้ที่ตาบอดกระนั้นหรือ? แท้จริงบรรดาผู้มีสติปัญญาเท่านั้นที่จะใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(13,20,'13:20','บรรดาผู้ให้ครบถ้วนซึ่งสัญญาณของอัลลอฮและไม่บิดพริ้วข้อตกลง');
INSERT INTO "king_fahad_thai" VALUES(13,21,'13:21','และบรรดาผู้เชื่อมสัมพันธ์ที่อัลลอฮทรงบัญชาให้เขาเชื่อมสัมพันธ์ และยําเกรงพระเจ้าของพวกเขา และกลัวการมีบัญชีที่ชั่ว');
INSERT INTO "king_fahad_thai" VALUES(13,22,'13:22','และบรรดาผู้อดทนโดยหวังพระพักตร์ (ความโปรดปราน) ของพระเจ้าของพวกเขา และดํารงการละหมาดและบริจาคสิ่งที่เราได้ให้เป็นปัจจัยยังชีพแก่พวกเขา โดยซ่อนเร้นและเปิดเผย และพวกเขาขจัดความชั่วด้วยความดี ชนเหล่านั้นสําหรับพวกเขาคืนที่พํานักในปั้นปลายที่ดี');
INSERT INTO "king_fahad_thai" VALUES(13,23,'13:23','สวนสวรรค์ทั้งหลายอันสถาพร พวกเขาจะเข้าไปอยู่พร้อมกับผู้ทําดีจากบรรพบุรุษของพวกเขา และคู่ครองของพวกเขา และบรรดาลูกหลานของพวกเขา และมะลาอิกะฮจะเข้ามาหาพวกเขาจากทุกประตู (ของสวนสวรรค์)');
INSERT INTO "king_fahad_thai" VALUES(13,24,'13:24','(พร้อมกับกล่าวว่า) “ความศานติจงมีแต่พวกท่าน เนื่องด้วยพวกท่านได้อดทน มันช่างดีเสียนี่กระไรที่พํานักบั้นปลายนี้”');
INSERT INTO "king_fahad_thai" VALUES(13,25,'13:25','และบรรดาผู้ทําลายพันธะของอัลลอฮฺ หลังจากที่ได้ให้คํามั่นสัญญาแก่พระองค์ และพวกเขาตัดขาดสิ่งที่อัลลอฮฺทรงใช้ให้เขาต่อ และบ่อนทําลายในแผ่นดิน ชนเหล่านี้แหละพวกเขาจะได้รับการสาปแช่ง และจะได้ที่พํานักอันชั่วช้า');
INSERT INTO "king_fahad_thai" VALUES(13,26,'13:26','อัลลอฮทรงให้กว้างขวางและทรงให้คับแคบซึ่งปัจจัยยังชีพ แก่ผู้ที่พระองค์ทรงประสงค์ และพวกเขาดีใจต่อชีวิตในโลกนี้ และชีวิตของโลกนี้เมื่อเทียบกับโลกอาคิเราะฮแล้ว หาใช่อื่นใดไม่ นอกจากความเพลิดเพลินเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(13,27,'13:27','และบรรดาผู้ปฏิเสธศรัทธากล่าวว่า “ทําไมสัญญาณจากพระเจ้าของเขาจึงไม่ถูกประทานให้แก่เขา?” จงกล่าวเถิด “แท้จริงอัลลอฮฺทรงให้หลงทางแก่ผู้ที่พระองค์ทรงประสงค์ และทรงนําทางสู่พระองค์แก่ผู้ที่สํานึกตัว');
INSERT INTO "king_fahad_thai" VALUES(13,28,'13:28','“บรรดาผู้ศรัทธา และจิตใจของพวกเขาสงบด้วยการรําลึกถึงอัลลอฮ พึงทราบเถิด! ด้วยการรําลึกถึงอัลลอฮเท่านั้นทําให้จิตใจสงบ');
INSERT INTO "king_fahad_thai" VALUES(13,29,'13:29','“บรรดาผู้ศรัทธาและกระทําความดี ความผาสุกย่อมได้แก่พวกเขา และเป็นการกลับไปที่ดียิ่ง”');
INSERT INTO "king_fahad_thai" VALUES(13,30,'13:30','ในทํานองนี้เราได้ส่งเจ้ามายังกลุ่มชนหนึ่ง ซึ่งก่อนหน้านั้นมีกลุ่มชนอื่นได้ล่วงลับไปแล้ว เพื่อเจ้าจะได้บอกกล่าวแก่พวกเขาถึงสิ่งที่เราได้วะฮี แก่เจ้า โดยที่พวกเขาปฏิเสธศรัทธาต่อพระผู้ทรงกรุณาปรานี จงกล่าวเถิด “พระองค์คือพระเจ้าของฉัน ไม่มีพระเจ้าอื่นใดนอกจากพระองค์เฉพาะพระองค์เท่านั้นฉันมอบความไว้วางใจ และยังพระองค์คือการกลับไปของฉัน”');
INSERT INTO "king_fahad_thai" VALUES(13,31,'13:31','และมาตรว่าอัลกุรอาน โดยมันนั้นภูเขาถูกทําให้เคลื่อนที่ได้ หรือโดยมันนั้นแผ่นดินถูกทําให้แยกออกจากกันได้ หรือโดยมันนั้นคนตายถูกทําให้พูดได้ แต่ทว่าพระบัญชาทั้งมวลเป็นกรรมสิทธิ์ของอัลลอฮ บรรดาผู้ศรัทธายังมิรู้ดอกหรือว่า มาตรว่าอัลลอฮทรงประสงค์ แน่นอนพระองค์จะทรงชี้แนะทางแก่มนุษย์ทั้งมวลก็ได้ และบรรดาผู้ปฏิเสธศรัทธานั้น ความหายนะคงจะประสบแก่พวกเขา เนื่องด้วยพวกเขาได้กระทําไว้ หรือจะเกิดขึ้นใกล้ที่พํานักของพวกเขา จนกระทั่งสัญญาณของอัลลอฮจะมาถึง แท้จริงอัลลอฮมิทรงผิดสัญญา');
INSERT INTO "king_fahad_thai" VALUES(13,32,'13:32','และโดยแน่นอน บรรดาร่อซูลก่อนหน้าเจ้าได้ถูกเย้ยหยันมาแล้ว ข้าได้ประวิงเวลาแก่บรรดาผู้ปฏิเสธศรัทธาแล้วข้าได้คร่าพวกเขา ดังนั้นการลงโทษของข้าเป็นเช่นใด?');
INSERT INTO "king_fahad_thai" VALUES(13,33,'13:33','ดังนั้น พระองค์ผู้ทรงเฝ้ามองทุกชีวิตที่มันได้ขวนขวายเอาไว้ (จะเหมือนกับเจว็ดทั้งหลาย) กระนั้นหรือ? และพวกเขาได้ตั้งภาคีเทียมอัลลอฮฺ จงกล่าวเถิดมุฮัมมัด “พวกท่านจงกล่าวชื่อพวกมัน หรือพวกท่านจะบอกพระองค์ในสิ่งที่พระองค์ไม่รู้ในแผ่นดิน หรือเป็นเพียงคําพูดที่กล่าวขึ้นมาลอยๆ กระนั้นหรือ? เปล่าเลย! ได้ถูกทําให้เพริศแพร้วแก่บรรดาผู้ปฏิเสธ ซึ่งแผนการณ์ของพวกเขา และถูกปิดกั้นจากแนวทาง (ของอัลลอฮฺ) และผู้ใดที่อัลลอฮฺทรงประสงค์ให้เขาหลงทางสําหรับเขาจะไม่มีผู้ชี้แนะให้เลย');
INSERT INTO "king_fahad_thai" VALUES(13,34,'13:34','สําหรับพวกเขาจะได้รับการลงโทษในชีวิตของโลกนี้ และแน่นอนการลงโทษในปรโลกนั้นร้ายแรงกว่า และสําหรับพวกเขาไม่มีผู้คุ้มกันจาก (การลงโทษของ) อัลลอฮได้');
INSERT INTO "king_fahad_thai" VALUES(13,35,'13:35','อุปมาสวนสวรรค์ซึ่งบรรดาผู้ยําเกรงได้ถูกสัญญาไว้คือ มีลําน้ําหลายสายไหลผ่านภายใต้มัน ผลไม้และเงาร่มมีอยู่ตลอดกาล นั่นคือบั้นปลายของบรรดาผู้ยําเกรง และบั้นปลายของบรรดาผู้ปฏิเสธศรัทธา ก็คือไฟนรก');
INSERT INTO "king_fahad_thai" VALUES(13,36,'13:36','และบรรดาผู้ที่เราได้ให้คัมภีร์แก่พวกเขาต่างก็ดีใจ ต่อสิ่งที่ได้ถูกประทานให้แก่เจ้า (อัลกุรอาน) และส่วนหนึ่งจากกลุ่มชนต่าง ๆ มีผู้ปฏิเสะบางส่วนของมัน (อัลกุรอาน) จงกล่าวเถิดมุฮัมมัด “แท้จริงฉันถูกบัญชาให้เคารพภักดีต่ออัลลอฮ และฉันจะไม่ตั้งภาคีเท่าเทียมพระองค์ และยังพระองค์ฉันจะเชิญชวน และยังพระองค์เท่านั้นคือการกลับไปของฉัน”');
INSERT INTO "king_fahad_thai" VALUES(13,37,'13:37','และในทํานองนั้น เราได้ให้อัลกุรอานแก่เขาไว้เป็นข้อชี้ขาดที่เป็นภาษาอาหรับ และหากเจ้าปฏิบัติตามความใคร่ของพวกเขา หลังจากหลักฐานได้มายังเจ้าแล้ว สําหรับเจ้าจะไม่มีผู้ช่วยเหลือและผู้คุ้มกันจากการลงโทษของอัลลอฮ');
INSERT INTO "king_fahad_thai" VALUES(13,38,'13:38','และโดยแน่นอน เราได้ส่งบรรดาร่อซูลมาก่อนหน้าเจ้า และเราได้ให้พวกเขามีภริยาและลูกหลาน และไม่บังควรแก่ร่อซูลที่จะนํามาซึ่งสัญญาณ (ปาฏิหาริย์) ใดๆ เว้นแต่โดยอนุมัติของอัลลอฮ สําหรับทุกสิ่งอย่างนั้น มีบันทึกไว้แล้ว');
INSERT INTO "king_fahad_thai" VALUES(13,39,'13:39','อัลลอฮฺทรงยกเลิกสิ่งที่พระองค์ทรงประสงค์ และทรงยืนหยัดให้มั่น (สิ่งที่พระองค์ทรงประสงค์) และที่พระองค์คือแม่บทแห่งคัมภีร์ (อัลลูฮุลมะฮฺฟูซ)');
INSERT INTO "king_fahad_thai" VALUES(13,40,'13:40','และหากเราจะให้เจ้าเห็นบางสิ่ง ซึ่งเราสัญญากับพวกเขา (ถึงการลงโทษ) หรือเราจะให้เจ้าตาย แท้จริงหน้าที่ของเจ้าคือการเผยแพร่ และหน้าที่ของเราคือการชําระบัญชี');
INSERT INTO "king_fahad_thai" VALUES(13,41,'13:41','พวกเขาไม่เห็นดอกหรือว่า แท้จริงเขาขยายพื้นที่ แล้วเราให้มันลดน้อยลงจากอาณาเขตของมัน และอัลลอฮทรงตัดสิน ไม่มีผู้ใดเปลี่ยนแปลงการตัดสินของพระองค์ และพระองค์เป็นผู้ทรงฉับพลันในการคิดบัญชี');
INSERT INTO "king_fahad_thai" VALUES(13,42,'13:42','และโดยแน่นอน บรรดาผู้ที่มาก่อนหน้า พวกเขาได้วางแผนมาก่อนแล้ว ดังนั้นแผนการณ์ทั้งหมดเป็นของอัลลอฮ พระองค์ทรงรอบรู้สิ่งที่ทุกชีวิตแสวงหาเอาไว้ และพวกปฏิเสธศรัทธาจะได้รู้ว่าจะได้แก่ผู้ใดบ้างที่พํานักที่ดีในบั้นปลาย');
INSERT INTO "king_fahad_thai" VALUES(13,43,'13:43','และบรรดาผู้ปฏิเสธศรัทธากล่าวว่า “ท่านมิใช่เป็นผู้ได้รับแต่ตั้ง” จงกล่าวเถิด “เพียงพอแล้วที่อัลลอฮทรงเป็นพยานระหว่างฉันกับพวกท่าน และผู้ที่เขามีความรู้ในคัมภีร์ (ก็เป็นพยานด้วย)');
INSERT INTO "king_fahad_thai" VALUES(14,1,'14:1','อะลิฟ ลาม รอ คัมภีร์ที่เราได้ประทานลงมาแก่เจ้า เพื่อให้เจ้านํามนุษย์ออกจากความมืดมนทั้งหลาย สู่ความสว่าง ด้วยอนุมัติของพระเจ้าของพวกเขา สู่ทางของพระผู้เดชานุภาพ ผู้ทรงได้รับการสรรเสริญ');
INSERT INTO "king_fahad_thai" VALUES(14,2,'14:2','(คือ) ทางของอัลลอฮ ซึ่งสิ่งที่อยุ่ในชั้นฟ้าทั้งหลายและสิ่งที่อยู่ในแผ่นดิน เป็นของพระองค์และความหายนะจากการลงโทษอย่างสาหัส จงประสบแก่พวกปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(14,3,'14:3','บรรดาผู้พอใจเลือกเอาชีวิตในโลกนี้เหนือปรโลก และปิดกั้นจากทางของอัลลอฮฺ และต้องการที่จะให้มันคดเคี้ยว ชนเหล่านั้นอยู่ในการหลงทางที่ห่างไกล');
INSERT INTO "king_fahad_thai" VALUES(14,4,'14:4','และเรามิได้ส่งร่อซูลคนใด นอกจากด้วยการพูดภาษาชนชาติของเขา เพื่อจะได้ชี้แจงอย่างชัดแจ้งแก่พวกเขา อัลลอฮฺจะทรงให้ผู้ที่พระองค์ทรงประสงค์หลงทาง และทรงชี้แนะทางแก่ผู้ที่พระองค์ทรงประสงค์ และพระองค์เป็นผู้ทรงเดชานุภาพ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(14,5,'14:5','และโดยแน่นอน เราได้ส่งมูซาพร้อมด้วยสัญญาณต่าง ๆ ของเราว่า “จงนํากลุ่มชนของเจ้าออกจากความมืดมนทั้งหลายสู่ความสว่าง และจงเตือนพวกเขาให้รําลึกถึงวัน (แห่งความโปรดปราน) ของอัลลอฮฺ” แท้จริงในการนั้นย่อมเป็นสัญญาณแก่ผู้อดทนผู้ขอบคุณทุกคน');
INSERT INTO "king_fahad_thai" VALUES(14,6,'14:6','และจงรําลึกขณะที่มูซากล่าวแก่กลุ่มชนของเขาว่า “จงรําลึกถึงความโปรดปรานของอัลลอฮที่มีต่อพวกท่าน เมื่อพระองค์ทรงให้พวกท่านรอดพ้นจากวงศ์วานของฟิรเอาน โดยให้พวกท่านได้ลิ้มรสการทรมานอันชั่วช้า และฆ่าลูกชายของพวกท่าน และไว้ชีวิตผู้หญิงของพวกท่าน และในการนั้นย่อมเป็นการทดสอบอย่างใหญ่หลวง จากพระเจ้าของพวกท่าน”');
INSERT INTO "king_fahad_thai" VALUES(14,7,'14:7','“และจงรําลึกขณะที่พระเจ้าของพวกเจ้าได้ประกาศว่า หากพวกเจ้าขอบคุณ ข้าก็จะเพิ่มพูนให้แก่พวกเจ้า และหากพวกเจ้าเนรคุณ แท้จริงการลงโทษของข้านั้นสาหัสยิ่ง”');
INSERT INTO "king_fahad_thai" VALUES(14,8,'14:8','และมูซาได้กล่าวว่า “หากพวกท่านและผู้ที่อยุ่ในแผ่นดินทั้งหมดปฏิเสธศรัทธา แท้จริงอัลลอฮนั้นทรงพอเพียงและทรงได้รับการสรรเสริญอย่างแน่นอน”');
INSERT INTO "king_fahad_thai" VALUES(14,9,'14:9','เรื่องราวของบรรดาผู้ทีมาก่อนหน้าพวกเจ้า เช่นกลุ่มชนของนูห อ๊าด และษะมูด และบรรดาผู้ที่มาหลังจากพวกเขา มิได้มาถึงพวกเจ้าดอกหรือ? ไม่มีผู้ใดรู้เรื่องของพวกเขา นอกจากอัลลอฮ บรรดาร่อซูลของพวกเขาได้มายังพวกเขาพร้อมด้วยหลักฐานอันชัดแจ้ง พวกเขาได้เอามือปิดปากของพวกเขาเอง และกล่าวว่า “แท้จริงเราได้ปฏิเสธศรัทธาในสิ่งที่พวกท่านถูกส่งมา (คืออัลกุรอาน) และแท้จริงพวกเราอยู่ในการสงสัยอย่างแน่นอนต่อสิ่งที่พวกท่านเรียกร้องเรา”');
INSERT INTO "king_fahad_thai" VALUES(14,10,'14:10','บรรดาร่อซูลของพวกเขาได้กล่าวว่า “มีการสงสัยในอัลลอฮฺ พระผู้สร้างชั้นฟ้าทั้งหลายและแผ่นดินกระนั้นหรือ? พระองค์ทรงเรียกร้องพวกท่านเพื่อทรงยกโทษในความผิดของพวกท่านและทรงผ่อนผันพวกท่าน จนกระทั่งถึงวาระที่ถูกกําหนดไว้” พวกเขากล่าวว่า “พวกท่านมิใช่อื่นใด นอกจากเป็นปุถุชนเยี่ยงเรา พวกท่านประสงค์ที่จะกีดกันพวกเราจากสิ่งที่บรรพบุรุษของพวกเราเคยเคารพบูชา ดังนั้นพวกท่านจงนําหลักฐานอันชัดแจ้งมาให้พวกเราซิ”');
INSERT INTO "king_fahad_thai" VALUES(14,11,'14:11','บรรดาร่อซูลของพวกเขากล่าวแก่พวกเขาว่า “พวกเรามิใช่อื่นใด นอกจากเป็นปุถุชนเยี่ยงพวกท่าน แต่ทว่าอัลลอฮฺทรงโปรดปรานผู้ที่พระองค์ทรงประสงค์ จากปวงบ่าวของพระองค์ ไม่บังควรแก่เราที่จะนําหลักฐานมาแสดงแก่พวกท่าน เว้นแต่โดยอนุมัติของอัลลอฮฺเท่านั้น และแด่อัลลอฮฺเท่านั้น บรรดามุอฺมินพึงมอบความไว้วางใจเถิด"');
INSERT INTO "king_fahad_thai" VALUES(14,12,'14:12','“และทําไมเล่าเราจึงไม่มอบความไว้วางใจแด่อัลลัอฮ และแน่นอนพระองค์ทรงชี้แนะแนวทางทั้งหลายแก่เรา และเราจะอดทนต่อการที่พวกท่านทําร้ายเรา และบรรดาผู้มอบความไว้วางใจพึงไว้วางใจแด่อัลลอฮเท่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(14,13,'14:13','และบรรดาผู้ปฏิเสธศรัทธาได้กล่าวแก่บรรดาร่อซูลของพวกเขาว่า “แน่นอนเราจะขับพวกท่านออกจากแผ่นดินของเรา หรือว่าพวกท่านจะกลับไปยังศาสนาของเรา” ดังนั้นพระเจ้าของพวกเขาทรงวะฮีให้แก่พวกเขา (บรรดาร่อซูล) ว่า “แน่นอน เราจะทําลายพวกอธรรม”');
INSERT INTO "king_fahad_thai" VALUES(14,14,'14:14','“และแน่นอน เราจะให้พวกท่านพํานักในแผ่นดิน หลังจากพวกเขา” นั่นสําหรับผู้ที่กลัวต่อการเผชิญหน้าข้า และกลัวต่อสัญญาการลงโทษของข้า”');
INSERT INTO "king_fahad_thai" VALUES(14,15,'14:15','และพวกเขา(บรรดาร่อซูล) ขอต่อพระองค์ให้ได้รับชัยชนะ และให้ผู้หยิ่งผยองที่ดื้อด้านทุกคนประสบความพินาศ');
INSERT INTO "king_fahad_thai" VALUES(14,16,'14:16','จากเบื้องหลังของเขาคือนรกญะอันนัม และจะได้ดื่มน้ําจากน้ําหนอง');
INSERT INTO "king_fahad_thai" VALUES(14,17,'14:17','เขาจิบมันแต่ไม่อาจจะกลืนมันได้ และความตายมาหาเขาจากทุกทิศทาง โดยที่เขาก็ไม่ตาย และเบื้องหลังของเขาคือการลงโทษที่รุนแรง');
INSERT INTO "king_fahad_thai" VALUES(14,18,'14:18','อุปมาบรรดาผู้ปฏิเสธศรัทธา ต่อพระเจ้าของพวกเขา การงานของพวกเขาแสวงหาไว้แต่อย่างใด นั่นคือการหลงทางที่ไกลลิบ');
INSERT INTO "king_fahad_thai" VALUES(14,19,'14:19','สูเจ้ามิเห็นดอกหรือว่า แท้จริงอัลลอฮทรงสร้างชั้นฟ้าทั้งหลายและแผ่นดินด้วยความจริง หากพระองค์ทรงประสงค์ พระองค์ก็จะทรงให้พวกเจ้าสูญสิ้นไป และจะทรงนํามาซึ่งกลุ่มชนรุ่นใหม่');
INSERT INTO "king_fahad_thai" VALUES(14,20,'14:20','และในการนั้นมีใช่เป็นการยากแก่อัลลอฮ');
INSERT INTO "king_fahad_thai" VALUES(14,21,'14:21','และพวกเขาได้ออกมาพร้อมกันต่อหน้าอัลลอฮ พวกอ่อนแอกล่าวกับพวกหัวหน้าว่า “แท้จริงพวกเราเป็นผู้ตามพวกท่าน พวกท่านจะช่วยพวกเราได้อย่างไร ให้พ้นจากการลงโทษของอัลลอฮ?” พวกเขากล่าวว่า “หากอัลลอฮทรงชี้แนะทางแก่เรา แน่นอนเราก็จะชี้แนะทางแก่พวกท่าน มีผลเท่ากันสําหรับเรา ถึงแม้ว่าเรากระวนกระวายหรือเราอดทน สําหรับพวกเรานั้นไม่มีทางรอดไปได้”');
INSERT INTO "king_fahad_thai" VALUES(14,22,'14:22','และชัยฎอนได้กล่าวเมื่อการงานได้ถูกตัดสินแล้วว่า “แท้จริงอัลลอฮได้ทรงสัญญาพวกท่านซึ่งเป็นสัญญาแห่งความจริง และฉันได้สัญญาพวกท่านแล้วฉันได้บิดพลิ้วพวกท่าน ฉันไม่มีอํานาจใดๆ เหนือพวกท่าน นอกจากฉันได้เรียกร้องพวกท่าน แล้วพวกท่านก็ตอบสนองฉัน ดังนั้นพวกท่านอย่าได้ประณามฉัน แต่ทว่าจงประณามตัวพวกท่านเอง ฉันไม่อาจร้องทุกข์แทนพวกท่านได้ และพวกท่านก็ไม่อาจ ร้องทุกข์แทนฉัน (จากการลงโทษของอัลลอฮ) ได้ แท้จริงฉันได้ปฏิเสธต่อสิ่งที่พวกท่านตั้งฉันให้เป็นภาคี (กับอัลลอฮ) แต่ก่อนนี้ แท้จริงบรรดาผู้อธรรมนั้น สําหรับพวกเขาคือการลงโทษอย่างเจ็บปวด”');
INSERT INTO "king_fahad_thai" VALUES(14,23,'14:23','และบรรดาผู้ศรัทธาและกระทําความดีทั้งหลาย ถูกนําให้เข้าสวนสวรรค์มากหลาย มีลําน้ําหลายสายไหลผ่านเบื้องล่าง โดยพํานักอยู่ในนั้นตลอดกาลด้วยอนุมัติของพระเจ้าของพวกเขา การกล่าวคํานับทักทายของพวกเขาในนั้นคือศานติ');
INSERT INTO "king_fahad_thai" VALUES(14,24,'14:24','เจ้ามิเห็นดอกหรือว่า อัลลอฮทรงยกอุทาหรณ์ไว้ว่า อุปมาคําพูดที่ดีดั่งต้นไม้ที่ดี รากของมันฝังแน่นลึกมั่นคง และกิ่งก้านของมันชูขึ้นไปในท้องฟ้า');
INSERT INTO "king_fahad_thai" VALUES(14,25,'14:25','ผลของมันจะออกมาทุกกาลเวลา โดยอนุมัติของพระเจ้าของมัน และอัลลอฮทรงยกอุทาหรณ์แก่ปวงมนุษย์เพื่อพวกเขาจะได้รําลึก');
INSERT INTO "king_fahad_thai" VALUES(14,26,'14:26','และอุปมาคําพูดที่เลว ดั่งต้นไม้ที่อับเฉาถูกถอนราออกจากพื้นดิน มันไม่มีความมั่นคงเลย');
INSERT INTO "king_fahad_thai" VALUES(14,27,'14:27','อัลลอฮทรงให้บรรดาผู้ศรัทธาหนักแน่นด้วยคํากล่าวที่มั่นคง ในการมีชีวิตอยุ่ทั้งในโลกนี้ และในปรโลก และอัลลอฮทรงให้บรรดาผู้อธรรมหลงทาง และอัลลอฮทรงกระทําสิ่งที่พระองค์ทรงประสงค์');
INSERT INTO "king_fahad_thai" VALUES(14,28,'14:28','เจ้าไม่เห็นดอกหรือ บรรดาผู้เปลี่ยนความโปรดปรานของอัลลอฮ เป็นการปฏิเสธศรัทธาและได้นํากลุ่มชนของพวกเขาลงสู่ที่พํานักอันหายนะ”');
INSERT INTO "king_fahad_thai" VALUES(14,29,'14:29','นรกญะฮันนัมที่มีเปลวไฟร้อนจัดของมัน และมันเป็นที่พํานักอันชั่วช้า');
INSERT INTO "king_fahad_thai" VALUES(14,30,'14:30','และพวกเขาได้ตั้งภาคีคู่เคียงกับอัลลอฮ เพื่อให้พวกเขาหลงทางของพระองค์ จงกล่าวเถิดมุฮัมมัด “พวกท่านจงร่าเริงกันเถิด เพราะแท้จริงทางกลับของพวกท่านย่อมไปสู่ไฟนรก”');
INSERT INTO "king_fahad_thai" VALUES(14,31,'14:31','จงกล่าวแก่ปวงบ่างผู้ศรัทธาของข้าว่าให้พวกเขาดํารงการละหมาด และบริจาคสิ่งที่เราได้ให้เป็นปัจจัยยังชีพแก่พวกเขา ทั้งอย่างลับ ๆ และอย่างเปิดเผย ก่อนที่วันหนึ่งจะมาถึงซึ่งไม่มีการซื้อขาย(ต่อรอง) ในวันนั้นและไม่มีการเป็นมิตร');
INSERT INTO "king_fahad_thai" VALUES(14,32,'14:32','อัลลอฮผู้ทรงสร้างชั้นฟ้าทั้งหลายและแผ่นดิน และทรงให้น้ําลงมาจากชั้นฟ้า และทรงให้พืชผลงอกเงยออกมาด้วยมัน (จากน้ํา) เพื่อเป็นปัจจัยยังชีพแก่พวกเจ้า และทรงให้เรือเดินสมุทรมีความสะดวกแก่พวกท่าน เพื่อใช้แล่นตามแม่น้ําโดยพระบัญชาของพระองค์ และทรงให้ลําน้ําทั้งหลายเป็นประโยชน์แก่พวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(14,33,'14:33','และพระองค์ทรงให้ดวงอาทิตย์และดวงจันทร์ เป็นประโยชน์แก่พวกเจ้า โดยโคจรเป็นปกติ และทรงให้กลางคืนและกลางวันเป็นประโยชน์แก่พวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(14,34,'14:34','และพระองค์ทรงประทานแก่พวกเจ้าทุกสิ่งที่พวกเจ้าขอต่อพระองค์ และหากพวกเจ้าจะนับความโปรดปรานของอัลลลอฮแล้ว พวกเจ้าก็ไม่อาจจะคํานวณมันได้ แท้จริงมนุษย์นั้นอธรรมยิ่ง เนรคุณยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(14,35,'14:35','และจงรําลึกเมื่ออิบรอฮีมกล่าวว่า “โอ้ พระเจ้าของข้าพระองค์ ขอพระองค์ทรงให้เมืองนี้ (มักกะฮ) ปลอดภัยและทรงให้ข้าพระองค์และลูกหลานของข้าพระองค์พ้นจากการบูชาเจว็ด');
INSERT INTO "king_fahad_thai" VALUES(14,36,'14:36','“โอ้พระเจ้าของข้าพระองค์ แท้จริงพวกมันได้ทําให้มนุษย์ส่วนใหญ่หลงทาง ดังนั้นผู้ใดปฏิบัติตามข้าพระองค์ แท้จริงเขาเป็นพวกของข้าพระองค์ และผู้ใดฝ่าฝืนข้าพระองค์ แท้จริงพระองค์เป็นผู้ทรงอภัย ผู้ทรงเมตตา');
INSERT INTO "king_fahad_thai" VALUES(14,37,'14:37','“โอ้พระเจ้าของเรา แท้จริงข้าพระองค์ได้ให้ลูกหลานของข้าพระองค์ พํานักอยู่ ณ ที่ราบลุ่มนี้โดยไม่มีพืชผลใดๆ ซึ่งอยู่ใกล้บ้านอันเป็นเขตหวงห้ามของพระองค์ โอ้พระเจ้าของเรา เพื่อให้พวกเขาดํารงการละหมาด ขอพระองค์ทรงให้จิตใจจากปวงมนุษย์ มุ่งไปยังพวกเขา และทรงประทานปัจจัยยังชีพที่เป็นพืชผลแก่พวกเขาหวังว่าพวกเขาจะขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(14,38,'14:38','“โอ้พระเจ้าของเรา แท้จริงพระองค์ทรงรอบรู้สิ่งที่เราปิดบังและสิ่งที่เราเปิดเผย ไม่มีสิ่งใดจะซ่อนเร้นไปจากอัลลอฮ ทั้งในแผ่นดินและในชั้นฟ้า');
INSERT INTO "king_fahad_thai" VALUES(14,39,'14:39','“การสรรเสริญเป็นของอัลลอฮ ผู้ได้ประทานอิสมาอีลและอิสฮากแก่ข้าพระองค์ ขณะที่ข้าพระองค์อยู่ในวัยชรา แท้จริงพระเจ้าของข้าพระองค์เป็นผู้ทรงได้ยินการวิงวอนอย่างแน่นอน”');
INSERT INTO "king_fahad_thai" VALUES(14,40,'14:40','“โอ้พระเจ้าของข้าพระองค์ ขอพระองค์ทรงให้ข้าพระองค์และจากลูกหลานของข้าพระองค์เป็นผู้ดํารงการละหมาด โอ้พระเจ้าของเรา ขอพระองค์ทรงตอบรับการวิงวอนของข้าพระองค์ด้วยเทอญ');
INSERT INTO "king_fahad_thai" VALUES(14,41,'14:41','“โอ้พระเจ้าของเรา ขอพระองค์ทรงอภัยโทษให้แก่ข้าพระองค์ และแก่บิดามารดาของข้าพระองค์ และแก่บรรดามุอมิน ในวันที่การสอบสวนจะมีขึ้น”');
INSERT INTO "king_fahad_thai" VALUES(14,42,'14:42','และเจ้าอย่าคิดว่าอัลลอฮฺทรงละเลยต่อสิ่งที่พวกอธรรมปฏิบัติ แท้จริงพระองค์ทรงประวิงเวลาให้พวกเขาจนถึงวันที่สายตาเงยจ้องไม่กระพริบ (วันกิยามะฮฺ)');
INSERT INTO "king_fahad_thai" VALUES(14,43,'14:43','พวกเขารีบเร่งเงยศรีษะของพวกเขาขึ้นนัยตาของพวกเขาไม่กระพริบ และจิตใจของพวกเขาลอย');
INSERT INTO "king_fahad_thai" VALUES(14,44,'14:44','และจงเตือนมนุษย์ถึงวันที่การลงโทษจะมาหาพวกเขา บรรดาผู้อธรรมจะกล่าวว่า “โอ้พระเจ้าของเรา โปรดประวิงเวลาแก่เราจนถึงกําหนดอันใกล้นี้ เพื่อเราจะได้สนองตอบการเชิญชวนของพระองค์ และเราจะปฏิบัติตามบรรดาร่อซูล” และพวกเจ้ามิได้สาบานแต่ก่อนนี้ดอกหรือว่า พวกเจ้านั้นจะไม่สูญสลาย');
INSERT INTO "king_fahad_thai" VALUES(14,45,'14:45','และพวกเจ้าได้พํานักอยู่ในสถานที่ของบรรดาผู้อธรรมแก่ตัวของพวกเขาเอง และเป็นที่ประจักษ์แจ้งแก่พวกเจ้าแล้วว่า เราได้กระทําแก่พวกเขาอย่างไร และเราได้ยกอุทาหรณ์แก่พวกเจ้าแล้ว');
INSERT INTO "king_fahad_thai" VALUES(14,46,'14:46','และแน่นอนพวกเขาได้วางแผนของพวกเขา และแผนของพวกเขาอยุ่ที่อัลลอฮฺ ถึงแม้ว่าแผนของพวกเขาจะทําให้ภูเขาเคลื่อนย้ายไปก็ตาม');
INSERT INTO "king_fahad_thai" VALUES(14,47,'14:47','และแท้จริงเจ้าอย่าคิดเลยว่า อัลลอฮฺจะทรงเป็นผู้ผิดสัญญากับบรรดาร่อซูลของพระองค์ แท้จริงอัลลอฮฺนั้นเป็นผู้ทรงเดชานุภาพ ผู้ทรงตอบโต้อย่างเด็ดขาด');
INSERT INTO "king_fahad_thai" VALUES(14,48,'14:48','วันซึ่งแผ่นดินจะถูกเปลี่ยนเป็นอื่นจากแผ่นดินนี้ และชั้นฟ้าทั้งหลาย (ก็เช่นเดียวกัน) พวกเขาจะปรากฎตัวต่อหน้าอัลลอฮ ผู้ทรงเอกะ ผู้ทรงพิชิต');
INSERT INTO "king_fahad_thai" VALUES(14,49,'14:49','และวันนั้นเจ้าจะเห็นบรรดาอาชญากรถูกล่ามตรึงไว้ด้วยโซ่');
INSERT INTO "king_fahad_thai" VALUES(14,50,'14:50','เครื่องนุ่งห่มของพวกเขาทําด้วยวัสดุไหม้ไฟ และไฟจะลุกคลุมใบหน้าของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(14,51,'14:51','เพื่ออัลลอฮจะได้ทรงตอบแทนทุกชีวิตตามที่มันได้แสวงหาไว้ แท้จริงอัลลอฮนั้นทรงฉับพลันในการสอบสวน');
INSERT INTO "king_fahad_thai" VALUES(14,52,'14:52','นี่คือการประกาศ แก่ปวงมนุษย์ เพื่อพวกเขาจะถูกเตือนด้วยมัน และเพื่อพวกเขาจะได้รู้ว่า แท้จริงพระองค์คือพระเจ้าผู้ทรงเอกะ และเพื่อบรรดาผู้มีสติจะได้รําลึก');
INSERT INTO "king_fahad_thai" VALUES(15,1,'15:1','อะลีฟ ลาม รอ เหล่านี้คือโองการทั้งหลายแห่งคัมภีร์ และเป็นกรุอานอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(15,2,'15:2','บรรดาผู้ปฏิเสธศรัทธาหวังกันว่า หากพวกเขาได้เป็นมุสลิม');
INSERT INTO "king_fahad_thai" VALUES(15,3,'15:3','เจ้าจงปล่อยพวกเขาบริโภคและร่าเริงและความหวังจะทําให้พวกเขาลืม แล้วพวกเขาก็จะรู้');
INSERT INTO "king_fahad_thai" VALUES(15,4,'15:4','เรามิได้ทําลายเมืองใด นอกจากว่าได้มีกําหนดเป็นที่รู้กันแก่มันแล้ว');
INSERT INTO "king_fahad_thai" VALUES(15,5,'15:5','ไม่มีประชาชาติใดจะร่าเริงกําหนดการลงโทษได้ และไม่อาจจะทําให้ล่าช้ากว่ากําหนดได้');
INSERT INTO "king_fahad_thai" VALUES(15,6,'15:6','และพวกเขากล่าวว่า “โอ้ผู้ซึ่งข้อตักเตือนถูกประทานแก่เขา แท้จริงท่านเป็นคนบ้าอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(15,7,'15:7','“ทําไมท่านไม่นํามะลาอิกะฮ์มาที่เรา หากท่านอยู่ในหมู่ผู้สัตย์จริง”');
INSERT INTO "king_fahad_thai" VALUES(15,8,'15:8','เราจะไม่ส่งมะลาอิกะฮ์ลงมา เว้นแต่ด้วยความจริง และดังนั้นพวกเขาไม่ต้องคอย');
INSERT INTO "king_fahad_thai" VALUES(15,9,'15:9','แท้จริงเราได้ให้ข้อตักเตือน(อัลกุรอาน)ลงมา และแท้จริงเราเป็นผุ้รักษามันอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(15,10,'15:10','และโดยแน่นอน ก่อนหน้าเจ้านั้นเราได้ส่ง(บรรดาร่อซูล) มาในหมู่คณะต่างๆ เมื่อครั้งสมัยก่อน ๆ');
INSERT INTO "king_fahad_thai" VALUES(15,11,'15:11','และไม่มีร่อซูลคนใดมายังพวกเขา เว้นแต่พวกเขาจะต้องเย้ยหยัน');
INSERT INTO "king_fahad_thai" VALUES(15,12,'15:12','ในทํานองนั้น เราได้ทําให้มัน อยู่ในจิตใจของพวกอาชญากร');
INSERT INTO "king_fahad_thai" VALUES(15,13,'15:13','พวกเขาจะไม่ศรัทธาต่ออัลกุรอาน และแน่นอนแบบอย่างของคนรุ่นก่อน ๆ ได้ล่วงลับไปแล้ว');
INSERT INTO "king_fahad_thai" VALUES(15,14,'15:14','และหาเราเปิดประตูแห่งชั้นฟ้าแก่พวกเขา แล้วพวกเขาจะขึ้นต่อไปเรื่อยๆ');
INSERT INTO "king_fahad_thai" VALUES(15,15,'15:15','แน่นอนพวกเขาจะกล่าวว่า “แท้จริงสายตาของพวกเราถูกปิดกั้น ไม่แต่เพียงเท่านั้นพวกเรายังเป้นกลุ่มชนที่ถูกเวทมนตร์อีกด้วย”');
INSERT INTO "king_fahad_thai" VALUES(15,16,'15:16','และโดยแน่นอน เราให้มีหมู่ดวงดาวในท้องฟ้า และเราได้ประดับมันให้สวยงามแก่บรรดาผู้เฝ้ามอง');
INSERT INTO "king_fahad_thai" VALUES(15,17,'15:17','และเราได้รักษามันให้พ้นจากชัยฏอนทุกตัวที่ถูกสาปแช่ง');
INSERT INTO "king_fahad_thai" VALUES(15,18,'15:18','เว้นแต่ผู้ที่แอบฟัง ดังนั้นดวงดาวที่ลุกโชติช่วงจะตามติดเผามัน');
INSERT INTO "king_fahad_thai" VALUES(15,19,'15:19','และแผ่นดินนั้นเราได้แผ่มันออกไป และเราได้ทําให้มีเทือกเขาเป็นที่ยึดอย่างมั่นคง และเราได้ให้ทุกสิ่งงอกเงยอย่างสมดุล');
INSERT INTO "king_fahad_thai" VALUES(15,20,'15:20','และในแผ่นดินนั้นเราได้ทําให้มีเครื่องยังชีพแก่พวกเจ้า และแก่ผู้ที่พวกเจ้ามิได้เป็นผู้ให้ริซกีแก่เขา');
INSERT INTO "king_fahad_thai" VALUES(15,21,'15:21','และไม่มีสิ่งใด (เครื่องยังชีพ) เว้นแต่ที่เรานั้นมีคลังของมัน และเราจะไม่ให้มันลงมานอกจากตามสภาวะที่ได้ถูกกําหนดไว้แล้ว');
INSERT INTO "king_fahad_thai" VALUES(15,22,'15:22','และเราได้ส่งลมผสมเกสร แล้วเราได้ให้น้ําลงมาจากฟ้า แล้วเราได้ให้พวกเจ้าดื่มมัน และพวกเจ้าก็มิได้เป็นผู้สะสมมันไว้');
INSERT INTO "king_fahad_thai" VALUES(15,23,'15:23','และโดยแน่นอนเราเป็นผู้ให้ชีวิต และเราเป็นผู้ให้ตาย และเราเป็นผู้คงอยู่แต่ผู้เดียว');
INSERT INTO "king_fahad_thai" VALUES(15,24,'15:24','และโดยแน่นอน เรารอบรู้กลุ่มชนก่อนพวกเจ้า และโดยแน่นอนเรารอบรู้กลุ่มชนรุ่นหลัง');
INSERT INTO "king_fahad_thai" VALUES(15,25,'15:25','และแท้จริง พระเจ้าของเจ้าคือผู้ทรงชุมนุมพวกเขา แท้จริงพระองค์เป็นผู้ทรงปรีชาญาณผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(15,26,'15:26','และโดยแน่นอน เราได้สร้างมนุษย์จากดินแห้ง จากดินดําเป็นตม');
INSERT INTO "king_fahad_thai" VALUES(15,27,'15:27','และญินนั้น เราได้สร้างมันมาก่อนจากไฟของลมร้อน');
INSERT INTO "king_fahad_thai" VALUES(15,28,'15:28','และจงรําลึกเมื่อพระเจ้าของเจ้าตรัสแก่มะลาอิกะฮ์แก่ “แท้จริงข้าเป็นผู้สร้างมนุษย์จากดินแห้ง จากดินดําเป็นตม');
INSERT INTO "king_fahad_thai" VALUES(15,29,'15:29','“ดังนั้น เมื่อข้าได้ทําให้เขามีรูปร่างสมส่วนและเป่าวิญญาณจากข้าเข้าไปในตัวเขา ฉะนั้นพวกเจ้าจงก้มลงสุญูดต่อเขา”');
INSERT INTO "king_fahad_thai" VALUES(15,30,'15:30','ดังนั้น มะลาอิกะฮ์ทั้งหมดได้ก้มลงสุญูด');
INSERT INTO "king_fahad_thai" VALUES(15,31,'15:31','เว้นแต่อิบลีส มันปฏิเสธที่จะอยู่ร่วมกับบรรดาผู้สุญูด');
INSERT INTO "king_fahad_thai" VALUES(15,32,'15:32','พระองค์ตรัสว่า “โอ้อิบลีส ทําไมเจ้าจึงไม่อยู่ร่วมกับบรรดาผู้สุญูด ?”');
INSERT INTO "king_fahad_thai" VALUES(15,33,'15:33','มันกล่าวว่า “ข้าพระองค์จะไม่สุญูดต่อมนุษย์ที่พระองค์ทรงสร้างเขาจากดินแห้ง จากดินดําเป็นตม”');
INSERT INTO "king_fahad_thai" VALUES(15,34,'15:34','พระองค์ตรัสว่า “ดังนั้นเจ้าจงออกไปจากที่นี่ แท้จริงเจ้าเป็นผู้ถูกขับไล่');
INSERT INTO "king_fahad_thai" VALUES(15,35,'15:35','และแท้จริง การสาบแช่งจงประสบแก่เจ้า จนกระทั่งวันแห่งการตอบแทน”');
INSERT INTO "king_fahad_thai" VALUES(15,36,'15:36','มันกล่าวว่า “โอ้พระเจ้าของข้าพระองค์ได้โปรดประวิงเวลาให้แก่ข้าพระองค์จนถึงวันฟื้นคืนชีพด้วยเถิด”');
INSERT INTO "king_fahad_thai" VALUES(15,37,'15:37','พระองค์ตรัสว่า “ดังนั้น แท้จริงเจ้าอยู่ในหมู่ผู้ถูกประวิงเวลา”');
INSERT INTO "king_fahad_thai" VALUES(15,38,'15:38','จนกระทั่งถึงวันแห่งเวลาที่ถูกกําหนดไว้แล้ว”');
INSERT INTO "king_fahad_thai" VALUES(15,39,'15:39','มันกล่าวว่า “โอ้พระเจ้าของข้าพระองค์โดยที่พระองค์ทรงให้ข้าพระองค์หลงผิดไปแล้วแน่นอน ข้าพระองค์ก็จะทําให้เป็นที่เพริศแพร้วแก่พวกเขาในแผ่นดินนี้ และแน่นอนข้าพระองค์จะทําให้พวกเขาทั้งหมดหลงผิด');
INSERT INTO "king_fahad_thai" VALUES(15,40,'15:40','“เว้นแต่ปวงบ่าวของพระองค์ ในหมู่พวกเขาที่มีใจบริสุทธิ์เท่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(15,41,'15:41','พระองค์ตรัสว่า “นี่คือทางอันเที่ยงธรรมของข้า”');
INSERT INTO "king_fahad_thai" VALUES(15,42,'15:42','“แท้จริงปวงบ่าวของข้า เจ้าจะไม่มีอํานาจใด ๆ เหนือพวกเขา เว้นแต่ผู้ที่เชื่อฟังเจ้าในหมู่ผู้หลงผิดเท่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(15,43,'15:43','และแท้จริงนรกญะฮันนัม แน่นอนคือสัญญาของพวกเขาทั้งหมด');
INSERT INTO "king_fahad_thai" VALUES(15,44,'15:44','มันมีเจ็ดประตู สําหรับทุกประตูมีส่วนที่ถูกจัดไว้แล้ว');
INSERT INTO "king_fahad_thai" VALUES(15,45,'15:45','แท้จริง บรรดาผู้ยําเกรงอยู่ในสวนสวรรค์มากหลาย และตาน้ําพุ');
INSERT INTO "king_fahad_thai" VALUES(15,46,'15:46','พวกเจ้าจงเข้าไปในนั้นด้วยความศานติและปลอดภัย');
INSERT INTO "king_fahad_thai" VALUES(15,47,'15:47','และเราได้ขจัดความขุ่นแค้นที่มีอยู่ในหัวอกของพวกเขา ให้กลายเป็นพี่น้องกัน โดยพํานักอยู่บนเตียงหันหน้าเข้าหากัน');
INSERT INTO "king_fahad_thai" VALUES(15,48,'15:48','ความเหนื่อยยากจะไม่ประสบแก่พวกเขาในนั้น และพวกเขาจะไม่ถูกนําออกจากที่นั้น');
INSERT INTO "king_fahad_thai" VALUES(15,49,'15:49','จงแจ้งแก่ปวงบ่าวของข้าว่า แท้จริงข้าคือผู้อภัย ผู้เมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(15,50,'15:50','และแท้จริงการลงโทษของข้านั้น เป็นการลงโทษที่เจ็บแสบ');
INSERT INTO "king_fahad_thai" VALUES(15,51,'15:51','และจงแจ้งพวกเขาให้ทราบถึงบรรดาแขกของอิบรอฮีม');
INSERT INTO "king_fahad_thai" VALUES(15,52,'15:52','เมื่อพวกเขาเข้าไปหาเขา (อิบรอฮีม) แล้วกล่าวว่า “ศานติ” เขากล่าวว่า “แท้จริงเรากลัวพวกท่าน”');
INSERT INTO "king_fahad_thai" VALUES(15,53,'15:53','พวกเรากล่าวว่า “ท่านอย่ากลัว แท้จริงเราขอแจ้งข่าวดีแก่ท่าน เกี่ยวกับเด็กคนหนึ่งที่มีความรู้”');
INSERT INTO "king_fahad_thai" VALUES(15,54,'15:54','เขากล่าวว่า “พวกท่านมาแจ้งข่าวดีแก่ฉันเมื่อความชราภาพได้ประสบแก่ฉันแล้วกระนั้นหรือ? แล้วเรื่องอะไรเล่าที่พวกท่านจะแจ้งข่าวดีแก่ฉัน!?”');
INSERT INTO "king_fahad_thai" VALUES(15,55,'15:55','พวกเขากล่าวว่า “เราขอแจ้งข่าวดีแก่ท่านด้วยความจริง ดังนั้นท่านอย่าอยู่ในหมู่ผู้สิ้นหวัง”');
INSERT INTO "king_fahad_thai" VALUES(15,56,'15:56','เขากล่าวว่า “และจะไม่มีผู้ใดที่สิ้นหวังในพระเมตตาของพระเจ้าของเขา นอกจากพวกหลงผิด”');
INSERT INTO "king_fahad_thai" VALUES(15,57,'15:57','เขากล่าวว่า “ดังนั้น ธุระอันใดเล่าของพวกท่าน โอ้ทูตทั้งหลาย !?”');
INSERT INTO "king_fahad_thai" VALUES(15,58,'15:58','พวกเขากล่าวว่า “แท้จริงเราถูกส่งมายังกลุ่มชนผู้ทําผิด”');
INSERT INTO "king_fahad_thai" VALUES(15,59,'15:59','“นอกจากบริวารของลูฏ แท้จริงเราจะเป็นผู้ช่วยพวกเขาให้รอดทั้งหมด”');
INSERT INTO "king_fahad_thai" VALUES(15,60,'15:60','“เว้นแต่ภริยาของเขา เราได้กําหนดไว้แล้วว่า แท้จริงนางจะอยู่ในหมู่ผู้ล่วงไปแล้ว”');
INSERT INTO "king_fahad_thai" VALUES(15,61,'15:61','ครั้นเมื่อพวกทูตได้มายังบริวารของลูฏ');
INSERT INTO "king_fahad_thai" VALUES(15,62,'15:62','เขา (บริวารของลูฏ) กล่าวว่า “แท้จริงพวกท่านเป็นกลุ่มชนที่ไม่คุ้นหน้า”');
INSERT INTO "king_fahad_thai" VALUES(15,63,'15:63','พวกเขากล่าวว่า “แต่ว่าเรามาหาท่าน (ลูฏ) ด้วยเรื่องที่พวกเขาสงสัยกันอยู่');
INSERT INTO "king_fahad_thai" VALUES(15,64,'15:64','“และเรามาหาท่านด้วยเรื่องจริง และแท้จริงเราเป็นผู้ซื่อสัตย์อย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(15,65,'15:65','“ดังนั้น ท่านจงเดินทางไปกับครอบครัวของท่าน ในช่วงเวลากลางคืน และจงตามหลังพวกเขา และอย่าให้ผู้ใดในหมู่พวกท่านเหลียวหลัง และจงเดินต่อไปตามที่พวกท่านถูกบัญชา”');
INSERT INTO "king_fahad_thai" VALUES(15,66,'15:66','และเราได้แจ้งแก่เขาถึงเรื่องนั้นว่า คนสุดท้ายของพวกเหล่านี้จะถูกตัดขาดในยามเช้า');
INSERT INTO "king_fahad_thai" VALUES(15,67,'15:67','และชาวเมือง ได้มาหาอย่างดีใจ');
INSERT INTO "king_fahad_thai" VALUES(15,68,'15:68','เขา (ลูฎ) กล่าวว่า “แท้จริง เขาเหล่านั้นคือแขกของฉัน ดังนั้น พวกท่านอย่าทําให้ฉันอัปยศ');
INSERT INTO "king_fahad_thai" VALUES(15,69,'15:69','“และจงยําเกรงอัลลอฮ์ และอย่าทําให้ฉันได้รับความละอาย”');
INSERT INTO "king_fahad_thai" VALUES(15,70,'15:70','พวกเขากล่าวว่า “และเรามิได้ห้ามท่านเกี่ยวกับการต้อนรับแขกดอกหรือ?”');
INSERT INTO "king_fahad_thai" VALUES(15,71,'15:71','ลูฏกล่าวว่า “เขาเหล่านี้คือลูกสาวของฉันหากพวกท่านจําเป็นต้องกระทํา?”');
INSERT INTO "king_fahad_thai" VALUES(15,72,'15:72','ขอสาบานด้วยชีวิตของเจ้า แน่นอนแท้จริงพวกเขาอยู่ในการมึนเมาหลงทาง');
INSERT INTO "king_fahad_thai" VALUES(15,73,'15:73','ดังนั้น เสียงกัมปนาทได้คร่าพวกเขาเมื่อดวงอาทิตย์ขึ้น');
INSERT INTO "king_fahad_thai" VALUES(15,74,'15:74','แล้วเราได้พลิกกลับส่วนบนของมันเป็นส่วนล่าง และได้ให้หินจากนรกหล่นลงมาทับพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(15,75,'15:75','แท้จริงในการนั้น แน่นอนเป็นสัญญาณแก่บรรดาผู้พินิจพิเคราะห์');
INSERT INTO "king_fahad_thai" VALUES(15,76,'15:76','และแท้จริง มัน(สถานที่นั้น)ยังคงเป็นเส้นทางที่พักอาศัยอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(15,77,'15:77','แท้จริงในการนั้น แน่นอนย่อมเป็นสัญญาณแก่บรรดามุอ์มิน');
INSERT INTO "king_fahad_thai" VALUES(15,78,'15:78','และกระนั้นก็ดี ชาวอัลอัยกะฮ์ เป็นผู้อธรรมอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(15,79,'15:79','ดังนั้นเราได้ลงโทษพวกเขา และแท้จริงทั้งสองพวก อยู่บนเส้นทางที่ชัดแจ้งอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(15,80,'15:80','และโดยแน่นอน ชาวอัลฮิจร์ได้ปฏิเสธบรรดาร่อซูล');
INSERT INTO "king_fahad_thai" VALUES(15,81,'15:81','และเราได้ให้สัญญาณต่างๆ ของเราแก่พวกเขา แล้วพวกเขาก็ผินหลังให้มัน');
INSERT INTO "king_fahad_thai" VALUES(15,82,'15:82','และพวกเขาได้สะกัดภูเขาเป็นบ้านพักอย่างปลอดภัย');
INSERT INTO "king_fahad_thai" VALUES(15,83,'15:83','ดังนั้น เสียงกัมปนาทได้คร่าชีวิตพวกเขาในยามเช้า');
INSERT INTO "king_fahad_thai" VALUES(15,84,'15:84','ฉะนั้น สิ่งที่พวกเขากระทําเอาไว้ ไม่ให้ประโยชน์แก่พวกเขาได้เลย');
INSERT INTO "king_fahad_thai" VALUES(15,85,'15:85','และเรามิได้สร้างชั้นฟ้าทั้งหลายและแผ่นดิน และสิ่งที่อยู่ระหว่างทั้งสองนั้น เพื่ออื่นใดเว้นแต่เป็นความจริง และแท้จริงวันกิยามะฮ์จะมีมาอย่างแน่นอน ดังนั้นเจ้าจงอภัยด้วยการอภัยที่ดี');
INSERT INTO "king_fahad_thai" VALUES(15,86,'15:86','แท้จริงพระเจ้าของเจ้า(มุฮัมมัด) ผู้ทรงสร้าง ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(15,87,'15:87','และโดยแน่นอน เราได้ให้แก่เจ้าเจ็ดอายาตที่ถูกอ่านซ้ํา และอัลกุรอานที่ยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(15,88,'15:88','อย่าทอดสายตาทั้งสองของเจ้าไปยังชนชั้นต่างๆ ของพวกเขา และอย่าเสียใจแทนพวกเขา และจงลดปีของเจ้าให้ต่ําต่อบรรดาผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(15,89,'15:89','และมุฮัมมัดจงกล่าวเถิดว่า “แท้จริงฉันเป็นผู้ตักเตือนอย่างชัดแจ้ง”');
INSERT INTO "king_fahad_thai" VALUES(15,90,'15:90','เช่นเดียวกับที่เราได้ให้แก่พวกแบ่งการศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(15,91,'15:91','(คือ) บรรดาผู้แบ่งแยกอัลกุรอานเป็นส่วน ๆ');
INSERT INTO "king_fahad_thai" VALUES(15,92,'15:92','ดังนั้น ของสาบานด้วยพระเจ้าของเจ้าแน่นอนเราจะถามพวกเขาทั้งหมด');
INSERT INTO "king_fahad_thai" VALUES(15,93,'15:93','ถึงที่พวกเขาได้กระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(15,94,'15:94','ดังนั้น จงประกาศอย่างเปิดเผยในสิ่งที่เจ้าถูกบัญชา และจงผินหลังให้พวกมุชริกีน');
INSERT INTO "king_fahad_thai" VALUES(15,95,'15:95','แท้จริง เราได้ให้ความพอเพียงแก่เจ้าต่อพวกเย้ยหยันแล้ว');
INSERT INTO "king_fahad_thai" VALUES(15,96,'15:96','(คือ) บรรดาผู้ตั้งพระเจ้าอื่นเป็นภาคีกับอัลลอฮ์ ดังนั้นพวกเขาจะรู้');
INSERT INTO "king_fahad_thai" VALUES(15,97,'15:97','และโดยแน่นอน เรารู้ว่าแท้จริงเจ้านั้นหัวอกของเจ้าอึดอัดต่อสิ่งที่พวกเขากล่าว');
INSERT INTO "king_fahad_thai" VALUES(15,98,'15:98','ดังนั้น จงกล่าวสดุดีด้วยการสรรเสริญพระเจ้าของเจ้า และจงร่วมอยู่ในหมู่ผู้สุญูด');
INSERT INTO "king_fahad_thai" VALUES(15,99,'15:99','และจงเคารพภักดีพระเจ้าของเจ้า จนกว่าความแน่นอน(ความตาย) จะมาหาเจ้า');
INSERT INTO "king_fahad_thai" VALUES(16,1,'16:1','พระบัญชา ของอัลลอฮ์ได้มาแล้ว ดังนั้นเจ้าอย่าได้เร่งมัน มหาบริสุทธิ์แด่พระองค์ และพระองค์ทรงสูงส่งเหนือที่พวกเขาตั้งภาคี');
INSERT INTO "king_fahad_thai" VALUES(16,2,'16:2','พระองค์ทรงส่งมะลาอีกะฮ์ลงมาพร้อมด้วยวะฮี ตามพระบัญชาของพระองค์ แก่ผู้ที่พระองค์ทรงประสงค์ จากปวงบ่าวของพระองค์โดยให้พวกเขาตักเตือนว่า ไม่มีพระเจ้าอื่นใดนอกจากข้า ดังนั้นพวกเจ้าจงยําเกรงต่อข้าเถิด');
INSERT INTO "king_fahad_thai" VALUES(16,3,'16:3','พระองค์ทรงสร้างชั้นฟ้าทั้งหลายและแผ่นดินด้วยความจริง พระองค์ทรงสูงส่งเหนือที่พวกเขาตั้งภาคี');
INSERT INTO "king_fahad_thai" VALUES(16,4,'16:4','พระองค์ทรงสร้างมนุษย์จากน้ําอสุจิแล้วเขาก็เป็นปรปักษ์อย่างชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(16,5,'16:5','และปศุสัตว์ พระองค์ทรงสร้างมันในตัวมันมีความอบอุ่นสําหรับพวกเจ้า และประโยชน์มากหลาย และในส่วนหนึ่งจากมันนั้นพวกเจ้าเอามาบริโภคได้');
INSERT INTO "king_fahad_thai" VALUES(16,6,'16:6','และในตัวมันมีความสง่างามสําหรับพวกเจ้า ขณะที่มันกลับจากทุ่งหญ้าและขณะที่นํามันออกไปเลี้ยง');
INSERT INTO "king_fahad_thai" VALUES(16,7,'16:7','และมันแบกสัมภาระหนักของพวกเจ้าไปยังเมืองไกล ๆ โดยที่พวกเจ้าจะไปถึงมันไม่ได้เว้นแต่ด้วยความเหนื่อยยากลําบากใจ แท้จริงพระเจ้าของพวกเจ้านั้นเป็นผู้ทรงเอ็นดู ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(16,8,'16:8','และม้า และล่อ ละลา เพื่อพวกเจ้าจะได้ขี่มันและเป็นเครื่องประดับ และพระองค์ยังทรงสร้างสิ่งอื่นๆ ที่พวกเจ้าไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(16,9,'16:9','และเป็นหน้าที่เหนืออัลลอฮ์คือการชี้แนะทางที่เที่ยงตรง และจากมัน(ทางต่าง ๆ) นั้น ก็มีทางคดเคคยวและหากพระองค์ทรงประสงค์แน่นอนพระองค์ก็จะทรงชี้นําทางแก่พวกเจ้าทั้งหมด');
INSERT INTO "king_fahad_thai" VALUES(16,10,'16:10','พระองค์คือ ผู้ทรงหลั่งน้ําลงมาจากฟากฟ้าสําหรับพวกเจ้า ส่วนหนึ่ง เป็นเครื่องดื่มและอีกส่วนหนึ่ง (ทําให้) พฤกษชาติ(เจริญเติบโต) เพื่อพวกเจ้าใช้เลี้ยงสัตว์');
INSERT INTO "king_fahad_thai" VALUES(16,11,'16:11','ด้วยมัน (น้ํา) พระองค์ทรงให้พืชผลและผลมะกอก และอินทผลัม และองุ่น งอกงามสําหรับพวกเจ้า และจากผลไม้อีกหลายชนิด แท้จริงในการนั้น แน่นอน ย่อมเป็นสัญญาณสําหรับกลุ่มชนที่ตึกตรอง');
INSERT INTO "king_fahad_thai" VALUES(16,12,'16:12','และพระองค์ทรงให้กลางคืนและกลางวันและดวงอาทิตย์และดวงจันทร์เป็นประโยชน์แก่พวกเจ้า และหมู่ดาวถูกใช้หใเป็นประโยชน์โดยพระบัญชาของพระองค์ แท้จริงในการนั้น แน่นอนย่อมเป็นสัญญาณมากหลาย สําหรับกลุ่มชนที่ใช้ปัญญา');
INSERT INTO "king_fahad_thai" VALUES(16,13,'16:13','และสิ่งที่พระองค์ทรงให้มีขึ้นมากมายสําหรับพวกท่านในแผ่นดินนั้น ชนิดของมันแตกต่างกันไป แท้จริงในการนั้น แน่นอนย่อมเป็นสัญญาณสําหรับกลุ่มชนผู้ใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(16,14,'16:14','และพระองค์คือผู้ทรงทําให้ทะเลเป็นประโยชน์ เพื่อพวกเจ้าจะได้กินเนื้อนุ่มสดจากมัน และพวกเจ้าเอาเครื่องประดับออกจากมัน สําหรับใช้ประดับราคา และเจ้าเห็นเรือแล่นฝ่าคลื่นในท้องทะเล และเพื่อพวกเจ้าจะได้แสวงหาความโปรดปรานของพระองค์ และเพื่อพวกเจ้าจะได้ขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(16,15,'16:15','และพระองค์ทรงให้มีเทือกเขามั่นคงในแผ่นดิน เพื่อมิให้มันสั่นสะเทือกนแก่พวกเจ้า และ(ทําให้มี) ลําน้ําและหนทาง เพื่อพวกเจ้าจะได้บรรลุสู่เป้าหมาย');
INSERT INTO "king_fahad_thai" VALUES(16,16,'16:16','เครื่องหมายต่าง ๆ และด้วยดวงดาว พวกเขาใช้นําทาง');
INSERT INTO "king_fahad_thai" VALUES(16,17,'16:17','ดังนั้น ผู้ทรงสร้างย่อมไม่เหมือนกับผู้ที่ถูกสร้าง พวกเจ้าไม่ใคร่ครวญดอกหรือ?');
INSERT INTO "king_fahad_thai" VALUES(16,18,'16:18','และหากพวกเจ้าจะนับความโปรดปรานของอัลลอฮ์ พวกเจ้าก็ไม่สามารถจะคํานวณมันได้ แท้จริงอัลลอฮ์นั้น เป็นผู้ทรงอภัย ผู้ทรงเมตตาอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(16,19,'16:19','และอัลลอฮทรงรอบรู้สิ่งที่พวกท่านปิดบังและสิ่งที่พวกท่านเปิดเผย');
INSERT INTO "king_fahad_thai" VALUES(16,20,'16:20','และบรรดาสิ่งที่พวกเขาวิงวอนอื่นจากอัลลอฮ์นั้น พวกมันไม่ได้สร้างสิ่งใดเลย แต่พวกมันถูกสร้าง');
INSERT INTO "king_fahad_thai" VALUES(16,21,'16:21','พวกมันตาย ปราศจากชีวิต และพวกมันไม่รู้ด้วยว่า เมื่อใดจะถูกให้ฟื้นขึ้นมาอีก');
INSERT INTO "king_fahad_thai" VALUES(16,22,'16:22','พระเจ้าของพวกเจ้านั้นคือพระองค์เดียว ดังนั้น บรรดาผู้ไม่ศรัทธาต่อวันปรโลกหัวใจของพวกเขาปฏิเสธไม่รับรู้ และพวกเขาหยิ่งผยอง');
INSERT INTO "king_fahad_thai" VALUES(16,23,'16:23','โดยแน่นอน แท้จริงอัลลอฮ์นั้นทรงรอบรู้สิ่งที่พวกเขาปิดบัง และสิ่งที่พวกเขาเปิดเผยแท้จริงพระองค์มิทรงรักพวกหยิ่งผยอง');
INSERT INTO "king_fahad_thai" VALUES(16,24,'16:24','และเมื่อได้ถูกกล่าวแก่พวกเขาว่า “พระเจ้าของพวกเจ้าได้ประทานอะไรลงมา? พวกเขากล่าวว่า “นิยายสมัยก่อน”');
INSERT INTO "king_fahad_thai" VALUES(16,25,'16:25','พวกเขาจงแบกความผิดอย่างครบครันในวันกิยามะฮ์เถิด และ(จงแบก) ความผิดของบรรดาผู้ที่พวกเขาทําให้เขาเหล่านั้น หลงผิดโดยไม่รู้ พึงทราบเถิด! สิ่งที่พวกเขาทําผิดนั้นชั่วช้ายิ่ง');
INSERT INTO "king_fahad_thai" VALUES(16,26,'16:26','แน่นอน บรรดาผู้ที่มาก่อนหน้าพวกเขาได้วางแผน ดังนั้น อัลลอฮ์ทําลายอาคารของพวกเขาจากรากฐาน (ของมัน) ฉะนั้นหลังคาที่อยู่เหนือพวกเขาได้หล่นลงมาทับพวกเขา และการลงโทษได้มายังพวกเขา โดยที่พวกเขาไม่รู้ตัว');
INSERT INTO "king_fahad_thai" VALUES(16,27,'16:27','แล้วในวันกิยามะฮ์พระองค์ทรงให้พวกเขาอัปยศ และตรัสว่า “ไหนเล่าภาคีของข้าที่พวกเจ้าโต้เถียงกันในเรื่องของพวกเขา” บรรดาผู้รู้กล่าวว่า “แน่แท้ วันนี้ความอัปยศและความชั่วช้าจะประสบแก่พวกปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(16,28,'16:28','“(คือ) บรรดาผู้ที่มะลาอิกะฮ์เอาชีวิตของพวกเขา โดยที่พวกเขาอธรรมต่อตัวเอง” ดังนั้นพวกเขายอมจํานน (พลางกล่าวว่า)“เรามิได้กระทําความชั่วใด ๆ “ เปล่าเลย! แท้จริงอัลลอฮ์เป็นผู้ทรงรอบรู้สิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(16,29,'16:29','ดังนั้น พวกเจ้าจงเข้าประตูนรกเพื่ออยู่ในนั้นตลอดกาล ฉะนั้น ที่พํานักของพวกหยิ่งผยองมันชั่วช้ายิ่ง');
INSERT INTO "king_fahad_thai" VALUES(16,30,'16:30','และได้ถูกกล่าวแก่บรรดาผู้ยําเกรงว่า “พระเจ้าของพวกเจ้าได้ทรงประทานอะไรลงมา?” พวกเขากล่าวว่า “ความดีทั้งนั้น” สําหรับบรรดาผู้ทําดีในโลกนี้คือความดี และแน่นอนในปรโลกนั้นย่อมดีกว่า และที่พํานักของบรรดาผู้ยําเกรงนั้นช่างดีเลิศ');
INSERT INTO "king_fahad_thai" VALUES(16,31,'16:31','สวนสวรรค์ที่พํานักที่พวกเขาจะเข้าไปในนั้น มีลําน้ําหลายสายไหลผ่าน ณ เบื้องล่างสําหรับพวกเขาที่อยู่ในนั้นจะได้สิ่งที่ต้องการ ในทํานองนั้น อัลลอฮฺทรงตอบแทนบรรดาผู้ยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(16,32,'16:32','บรรดาผู้ที่มะลาอิกะฮ์เอาชีวิตของพวกเขาโดยที่พวกเขาเป็นคนดี พลางกล่าวว่า “ศานติจงมีแด่พวกเจ้า จงเข้าไปในสวนสวรรค์เนื่องจากสิ่งที่พวกเจ้าได้กระทําไว้”');
INSERT INTO "king_fahad_thai" VALUES(16,33,'16:33','พวกเขามิได้คอยสิ่งใด นอกจากจะให้มะลาอิกะฮ์มาหาพวกเขา หรือพระบัญชาของพระเจ้าจะมา ในทํานองนั้นแหละ บรรดาผู้มาก่อนหน้าพวกเขาได้กระทํา และอัลลอฮ์มิได้ทรงอธรรมต่อพวกเขา แต่พวกเขาได้อธรรมต่อตัวของพวกเขาเอง');
INSERT INTO "king_fahad_thai" VALUES(16,34,'16:34','ดังนั้น ความชั่วทั้งหลายที่พวกเขาได้กระทําไว้ ก็จะประสบแก่พวกเขา และสิ่งที่พวกเขาเคยเย้ยหยันไว้ ก็จะห้อมล้อมพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(16,35,'16:35','และบรรดาผู้ตั้งภาคีดังกล่าว”หากอัลลอฮ์ทรงประสงค์ พวกเราจะไม่เคารพบูชาผู้ใดอื่นจากพระองค์ ทั้งพวกเราและบรรพบุรุษบองพวกเราและพวกเราจะไม่ห้ามสิ่งใดอื่นจากที่พระองค์(ทรงห้ามไว้ ในทํานองนั้นแหละบรรดาผู้มาก่อนหน้าพวกเขาได้กระทํา ดังนั้น บรรดาร่อซูลมิได้มีหน้าที่อื่นใด นอกจากการประกาศอันชัดแจ้งเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(16,36,'16:36','และโดยแน่นอน เราได้ส่งร่อซูลมาในทุกประชาชาติ (โดยบัญชาว่า) “พวกท่าจงเคารพภักดีอัลลอฮ์ และจงหลีกหนีให้ห่างจากพวกเจว็ด” ดังนั้น ในหมู่พวกเขามีผู้ที่อัลลอฮ์ทรงชี้แนะทางให้และในหมู่พวกเขามีการหลงผิดคู่ควรแก่เขาฉะนั้น พวกเจ้าจงตระเวนไปในแผ่นดิน แล้วจงดูว่าบั้นปลายของผู้ปฏิเสธนั้นเป็นเช่นใด!');
INSERT INTO "king_fahad_thai" VALUES(16,37,'16:37','หากเจ้าห่วงใยต่อการชี้แนะทางของพวกเขา ดังนั้น แท้จริงอัลลออ์จะไม่ทรงแนะทางแก่ผู้ที่พระองค์จะให้เขาหลง และสําหรับพวกเขาจะไม่มีผู้ช่วยเหลือใด ๆ');
INSERT INTO "king_fahad_thai" VALUES(16,38,'16:38','และพวกเขาได้สาบานต่ออัลลอฮ์ พ้วยการสาบานอย่างแข็งขันว่า อัลลอฮ์จะไม่ทรงให้ผู้ตายฟื้นขึ้นมาอีก มิใช่เช่นนั้น! สัญญานั้นย่อมเป็นจริงแก่พระองค์เสมอ แต่มนุษย์ส่วนมากไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(16,39,'16:39','เพื่อพระองค์จะทรงชี้แจงแก่พวกเขาถึงสิ่งที่พวกเขาขัดแย้งกันในเรื่องนั้น และเพื่อพระองค์จะทรงให้บรรดาผู้ปฏิเสธศรัทธาได้รู้ว่าแท้จริงพวกเขานั้นเป็นผู้โกหก');
INSERT INTO "king_fahad_thai" VALUES(16,40,'16:40','แท้จริงเมื่อเราปรารถนาคําตรัสของเราแก่สิ่งใด เราก็จะกล่าวแก่มันว่า “จงเป็น” แล้วมันก็เป็นขึ้น');
INSERT INTO "king_fahad_thai" VALUES(16,41,'16:41','และบรรดาผู้ที่อพยพในเรื่องของอัลลอฮ์หลังจากที่พวกเขาถูกข่มเหง และแน่นอนเราจะให้ที่พํานักที่ดีแก่เขาในโลกนี้ และแน่นอนรางวัลของวันปรโลกนั้นยิ่งใหญ่กว่า หากพวกเขารู้');
INSERT INTO "king_fahad_thai" VALUES(16,42,'16:42','บรรดาผู้อดทน และพวกเขามอบความไว้วางใจต่อพระเจ้าของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(16,43,'16:43','และเรามิได้ส่งผู้ใดมาก่อนหน้าเจ้า นอกจากเป็นผู้ชายที่เราได้วะฮีแก่พวกเขา ดังนั้น พวกเจ้าจงถามบรรดาผู้รู้ หากพวกเจ้าไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(16,44,'16:44','ด้วยหลักฐานทั้งหลายที่ชัดแจ้ง และคัมภีร์ต่างๆ ที่ศักดิ์สิทธิ์ และเราได้ให้อัลกุรอานแก่เจ้าเพื่อเจ้าจะได้ชี้แจง(ให้กระจ่าง)แก่มนุษย์ซึ่งสิ่งที่ได้ถูกประทานมาแก่พวกเขา และเพื่อพวกเขาจะได้ไตร่ตรอง');
INSERT INTO "king_fahad_thai" VALUES(16,45,'16:45','บรรดาผู้วางแผนชั่วร้ายทั้งหลายจะปลอดภัยกระนั้นหรือ จากการที่อัลลอฮ์จะทรงให้ธรณีสูบพวกเขา หรือการลงโทษที่จะมาหาพวกเขาโดยที่พวกเขาไม่รู้ตัว');
INSERT INTO "king_fahad_thai" VALUES(16,46,'16:46','หรือพระองค์จะทรงคร่าชีวิตพวกเขาขณะที่พวกเขาเดินทาง ดังนั้น พวกเขาจะไม่สามารถหักห้าม(พระองค์)ได้');
INSERT INTO "king_fahad_thai" VALUES(16,47,'16:47','หรือพระองค์พวกเขาให้ตายทีละน้อย ดังนั้น แท้จริงพระเจ้าของพวกเจ้านั้น แน่นอนเป็นผู้ทรงเอ็นดู ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(16,48,'16:48','และพวกเขามิได้มองไปยังสิ่งที่อัลลอฮ์ทรงสร้าง บ้างดอกหรือว่า เงาของมันจะทอดไปทางขวาและทางซ้าย เพื่อสุญูดต่ออัลลอฮ์โดยที่พวกมันนอบน้อม');
INSERT INTO "king_fahad_thai" VALUES(16,49,'16:49','และสิงที่อยู่ในชั้นฟ้าทั้งหลายและสิ่งที่อยู่ในแผ่นดิน ที่เป็นสัตว์โลกทั้งหลายและมะลาอิกะฮ์จะสุญูดต่ออัลลอฮ์ โดยที่พวกมันจะไม่หยิ่งผยอง');
INSERT INTO "king_fahad_thai" VALUES(16,50,'16:50','พวกเขาจะกลัวพระเจ้าของพวกเขา ผู้ทรงอํานาจเหนือพวกเจ้า ปฏิบัติตามสิ่งที่พวกเขาถูกบัญชา');
INSERT INTO "king_fahad_thai" VALUES(16,51,'16:51','และอัลลอฮ์ตรัสว่า “พวกเจ้าอย่ายึดถือพระเจ้าสององค์ แท้จริงพระองค์คือพระผู้เป็นเจ้าองค์เดียวเท่านั้น ดังนั้น เฉพาะข้าเท่านั้นที่พวกเจ้าต้องเกรงกลัว"');
INSERT INTO "king_fahad_thai" VALUES(16,52,'16:52','และเป็นกรรมสิทธิ์ของพระองค์ สิ่งที่อยู่ในชั้นฟ้าทั้งหลายและแผ่นดิน และการภักดีต่อพระองค์เท่านั้นจําเป็นต้องมีประจํา ดังนั้นพวกเจ้าจะยําเกรงผู้ใดอื่นจากอัลลอฮ์กระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(16,53,'16:53','และไม่มีความโปรดปรานใด ๆ ที่พวกเจ้าได้รับ นอกจากมันย่อมมาจากอัลลอฮ์ ดังนั้น เมื่อความทุกข์ร้ายประสบแก่พวกเจ้า พวกเข้าก็จะคร่ําครวญขอพรต่อพระองค์');
INSERT INTO "king_fahad_thai" VALUES(16,54,'16:54','แล้วเมื่อพระองค์ทรงปลดเปลื้องความทุกข์ยากออกจากพวกเจ้า ขณะนั้นกลุ่มหนึ่งจากพวกเจ้าก็จะตั้งภาคีต่อพระเจ้าของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(16,55,'16:55','ก็ให้พวกเขาเนรคุณต่อสิ่งที่เราได้ให้แก่พวกเขา ดังนั้นจงร่าเริงไปเถิด แล้วพวกเจ้าก็จะรู้');
INSERT INTO "king_fahad_thai" VALUES(16,56,'16:56','และพวกเขาตั้งสิ่งที่พวกเขาไม่รู้ (ให้เป็นเจว็ด) อันเป็นส่วนได้จากสิ่งที่เราได้ให้ปัจจัยยังชีพแก่พวกเขา ขอสาบานต่ออัลลอฮ์พวกเจ้าจะถูกสอบสวนอย่างแน่นอน เกี่ยวกับสิ่งที่พวกเจ้ากุขึ้น');
INSERT INTO "king_fahad_thai" VALUES(16,57,'16:57','และพวกเขาตั้งบุตรีให้แก่อัลลอฮ์ มหาบริสุทธิ์แด่พระองค์ และสําหรับพวกเขามีสิ่งที่พวกเขาใคร่จะมี(บุตรชาย');
INSERT INTO "king_fahad_thai" VALUES(16,58,'16:58','และเมื่อผู้ใดในหมู่พวกเขาได้รับข่าวว่าได้ลูกผู้หญิง ใบหน้าของเขากลายเป็นหมองคล้ําและเศร้าสลด');
INSERT INTO "king_fahad_thai" VALUES(16,59,'16:59','เขาจะซ่อนตัวเองจากกลุ่มชน เนื่องจากความอับอายที่ได้ถูกแจ้งแก่เขา เขาจะเก็บเอาไว้ด้วยความอัปยศหรือฝังมันในดิน พึงรู้เถิด ! สิ่งที่พวกเขาตัดสินใจนั้นมันชั่วแท้ ๆ');
INSERT INTO "king_fahad_thai" VALUES(16,60,'16:60','สําหรับบรรดาผู้ไม่ศรัทธาต่อวันปรโลกเป็นตัวอย่างที่ชั่ว และสําหรับอัลลอฮ์มีตัวอย่างอันสูงส่ง และพระองค์คือผู้ทรงเดชานุภาพ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(16,61,'16:61','และหากอัลลอฮ์จะทรงเอาโทษมนุษย์เนื่องจากความอธรรมของพวกเขา พระองค์จะไม่ทรงให้เหลือทิ้งไว้บนมัน (แผ่นดิน) ซึ่งสัตว์โลกใด ๆ แต่พระองค์ทรงประวิงเวลาให้แก่พวกเขาได้มาถึง พวกเขาจะประวิงเวลาให้ช้าสักชั่วโมงหนึ่งก็ไม่ได้ และพวกเขาจะเร่งให้เร็ว(สักชั่วโมงหนึ่ง) ก็ไม่ได้');
INSERT INTO "king_fahad_thai" VALUES(16,62,'16:62','และพวกเขาตั้งสิ่งที่พวกเขาชิงชังให้อัลลอฮ์ และลิ้นของพวกเขากล่าวเท็จขึ้นว่า สําหรับพวกเขานั้นดีเยี่ยม โดยแน่นนอ แท้จริงสําหรับพวกเขาคือไฟนรก และพวกเขาจะถูกส่งล่วงหน้าไปก่อน');
INSERT INTO "king_fahad_thai" VALUES(16,63,'16:63','ด้วยพระนามของอัลลอฮฺ โดยแน่นอนเราได้ส่ง(บรรดาร่อซูล) ไปยังประชาชาติต่าง ๆ ก่อนเจ้า แล้วชัยฏอนได้ทําให้การงานของพวกเขาเพริศแพร้วแก่พวกเขา ดังนั้น มันเป็นผู้คุ้มครองพวกเขาในวันนี้ และสําหรับพวกเขาคือการลงโทษอันเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(16,64,'16:64','และเรามิได้ให้คัมภีร์นี้ลงมาแก่เจ้า เพื่ออื่นใด เว้นแต่เพื่อให้เจ้าชี้แจง ให้แกจ่มแจ้งแก่พวกเขาในสิ่งที่พวกเขาขัดแย้งกัน และเพื่อเป็นการชี้แนวทางและเป็นความเมตตาแก่หมู่ชนผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(16,65,'16:65','และอัลลอฮ์ทรงประทานน้ําลงมาจากฟากฟ้า เพื่อให้แผ่นดินมีชีวิตด้วยมัน หลังจากการแห้งแล้งของมัน แท้จริงในการนี้ แน่นอนย่อมเป็นสัญญาณแก่กลุ่มชนผู้รับฟัง (การตักเตือนเพื่อนํามาใคร่ครวญ)');
INSERT INTO "king_fahad_thai" VALUES(16,66,'16:66','และแท้จริงในปศุสัตว์ ย่อมมีบทเรียนอย่างแน่นอนแก่พวกเจ้า เราให้พวกเจ้าดื่มจากสิ่งที่อยู่ในท้องของมัน จากระหว่างมูลและเลือดเป็นน้ํานมบริสุทธิ์ เป็นที่โอชาแก่ผู้ดื่ม');
INSERT INTO "king_fahad_thai" VALUES(16,67,'16:67','และจากผลของต้นอินทผาลัมและองุ่นพวกเจ้าได้จากมันมาเป็นทั้งของมึนเมา และอาหารที่ดี แท้จริงในการนั้น แน่นอนย่อมเป็นสัญญาณแก่กลุ่มชนผู้ใช้ปัญญา');
INSERT INTO "king_fahad_thai" VALUES(16,68,'16:68','และพระเจ้าของเจ้า ทรงดลใจ แก่ผึ้งว่าจงทํารังตามภูเขาและตามต้นไม้ และตามที่พวกเขาทําร้านขึ้น');
INSERT INTO "king_fahad_thai" VALUES(16,69,'16:69','แล้วเจ้า(ผึ้ง) จงกินจากผลไม้ทั้งหลาย แล้งจงดําเนินตามทางของพระเจ้าของเจ้า โดยสะดวกสบาย มีเครื่องดื่มที่มีสีสรรต่างๆออกมาจากท้องของมัน ในนั้นมีสิ่งบําบัดแก่ปวงมนุษย์แท้จริงในการนั้น แน่นอนย่อมเป็นสัญญาณแก่กลุ่มชนผู้ตรึกตรอง');
INSERT INTO "king_fahad_thai" VALUES(16,70,'16:70','และอัลลอฮ์ทรงบังเกิดพวกเจ้า แล้วทรงให้พวกเจ้าตาย และบางคนในหมู่พวกเจ้ามีผู้ถูกนํากลับไปยังวัยต่ําสุดของชีวิต เพื่อมิให้เขารู้อะไรหลังจากที่เคยมีความรู้ แม้จริงอัลลอฮ์เป็นผู้ทรงรอบรู้ ผู้ทรงอานุภาพ');
INSERT INTO "king_fahad_thai" VALUES(16,71,'16:71','และอัลลอฮ์ทรงให้บางคนในหมู่พวกเจ้า ดีเด่นกว่าอีกบางคนในเรื่องปัจจัยยังชีพ แล้วทําไมบรรดาผู้ที่ได้รับความดีเด่น จึงไม่แบ่งปัจจัยยังชีพของพวกเขา แก่บรรดาผู้ที่มือขวาของพวกเขาครอบครอง เพื่อพวกเขาจะได้เท่าเทียมกัน ในเรื่องนั้น ดังนั้น ต่อความโปรดปรานของอัลลอฮ์พวกเขาอกตัญญูกระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(16,72,'16:72','และอัลลอฮ์ทรงกําหนดคู่ครองแก่พวกเจ้าซึ่งมาจากหมู่พวกเจ้า และทรงทําให้พวกเจ้ามีลูกและหากนจากคู่ครองของพวกเจ้าและทรงประทานริซกีจากสิ่งดี ๆ แก่พวกเจ้า ดังนั้น ต่อสิ่งเท็จพวกเขาศรัทธาและต่อความโปรดปรานของอัลลอฮ์พวกเขาเนรคุณกระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(16,73,'16:73','และพวกเขาเคารพบูชาอื่นจากอัลลอฮ์ตือสิ่งที่ไม่มีอํานาจให้ปัจจัยยังชีพแก่พวกเขาจากชั้นฟ้าทั้งหลายและแผ่นดินแต่อย่างใด และพวกมันก็ไม่สามารถกระทําได้');
INSERT INTO "king_fahad_thai" VALUES(16,74,'16:74','ดังนั้น พวกเจ้าอย่ายกอุทาหรณ์ทั้งหลายกับอัลลอฮ์เลย แท้จริงอัลลอฮ์นั้นทรงรอบรู้ และพวกเจ้าไม่รู้เท่าพระองค์');
INSERT INTO "king_fahad_thai" VALUES(16,75,'16:75','อัลลอฮ์ทรงยกอุทาหรณ์ถึงบ่าวผู้เป็นทาสไม่มีอํานาจในสิ่งใด กับผู้ที่เราได้ให้ปัจจัยยังชีพที่ดีจากเราแก่เขา แล้วก็เขาบริจาคมันโดยทางลับและเปิดเผย พวกเขาจะเท่าเทียมกันละหรือ? บรรดาการสรรเสริญเป็นของอัลลอฮ์ แต่ทว่าส่วนมากของพวกเขาไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(16,76,'16:76','และอัลลอฮ์ทรงยกอุทาหรณ์ถึงชายสองคน หนึ่งในสองคนเป็นใบ้ เขาไม่สามารถในสิ่งใด และเขาเป็นภาระแก่นายของเขาอีกด้วยไม่ว่าแห่งใดที่นายจะส่งเขาไป เขาจะไม่นําความดีใด ๆ มาเลยเขาจะเท่าเทียมกับผู้กําชับในทางที่เที่ยงธรรม และเขาอยู่ในทางที่เที่ยงตรงกระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(16,77,'16:77','และเฉพาะอัลลอฮ์(ทรงรู้) สิ่งพันญาณวิสัยแห่งชั้นฟ้าทั้งหลายและแผ่นดิน และเรื่องที่เกี่ยวกับวันสิ้นโลกนั้นมิใช่อื่นใด นอกจากเพียงชั่วพริบตาเดียวหรือมันใกล้(เร็ว) ยิ่งกว่า แท้จริงอัลลอฮ์ทรงอานุภาพเหนือทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(16,78,'16:78','และอัลลอฮ์ทรงให้พวกเจ้าออกจากครรภ์มารดาของพวกเจ้า โดยพวกเจ้าไม่รู้อะไรเลย และพระองค์ทรงทําให้พวกเจ้าได้ยินและเห็นและมีหัวใจ (สําหรับนึกและคิด) เพื่อพวกเจ้าจะได้ขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(16,79,'16:79','พวกเขาไม่เห็นฝูงนกดอกหรือว่า พวกมันได้รับความสะดวกในห้วงอากาศแห่งชั้นฟ้า ไม่มีผู้ใดดึงพวกมันไว้ได้นอกจากอัลลอฮ์ แท้จริงในการนั้น แน่นอนย่อมเป็นสัญญาณมากหลายสําหรับกลุ่มชนผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(16,80,'16:80','และอัลลอฮ์ทางทําให้มีที่พํานักในบ้านของพวกเจ้า และทรงทําให้พวกเจ้ามีบ้านทําจากหนังปศุสัตว์ ซึ่งทําให้พวกเจ้ารู้สึกเบาในยามเดินทางของพวกเจ้า และในยามพักของพวกเจ้า และจากขนของมันและปุยของมันและผมของมัน ทําเป็นเครื่องใช้และสิ่งมีประโยชน์ชั่วเวลาหนึ่ง');
INSERT INTO "king_fahad_thai" VALUES(16,81,'16:81','และอัลลอฮ์ทรงทําจากสิ่งที่พระองค์ทรงสร้าง ให้เป็นที่ร่มแก่พวกเจ้า และพระองค์ทรงทําที่พักพิงจากภูเขา ให้แก่พวกเจ้าและพระองค์ทรงทําเครื่องนุ่มห่ม ให้พวกเจ้า เพื่อป้องกันพวกเจ้าให้พ้นจากความร้อน และเสื้อเกราะเพื่อป้องกันพวกเจ้าให้พ้นจากอันตรายในยามสงคราม เช่นนั้นแหละ พระองค์ทรงให้ความโปรดปรานของพระองค์อย่างครบถ้วนแก่พวกเจ้า เพื่อพวกเจ้าจะได้นอบน้อม');
INSERT INTO "king_fahad_thai" VALUES(16,82,'16:82','ดังนั้นหากพวกเขาผินหลังกลับ แท้จริงหน้าที่ของเจ้าคือการแจ้งข่าวอย่างชัดแจ้งเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(16,83,'16:83','พวกเขาตระหนักดีในความโปรดปรานของอัลลอฮ์ แต่แล้วพวกเขาปฏิเสธมัน และส่วนมากของพวกเขาเป็นผู้ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(16,84,'16:84','และวันที่เราจะตั้งพยานขึ้นจากทุกประชาชาติ แล้วบรรดาผู้ปฏิเสธศรัทธาจะไม่ถูกอนุญาต และพวกเขาจะไม่ถูกขอร้องให้รําพันถึงความผิด');
INSERT INTO "king_fahad_thai" VALUES(16,85,'16:85','และเมื่อบรรดาผู้อธรรมได้เห็นการลงโทษมันจะไม่ถูกลดหย่อนแก่พวกเขา และพสกเขาจะไม่ถูกพิจารณาให้ผ่อนปรน');
INSERT INTO "king_fahad_thai" VALUES(16,86,'16:86','และเมื่อบรรดาผู้ตั้งภาคีได้มองเห็นเจว็ดของพวกเขา พวกเขากล่าวว่า “โอ้พระเจ้าของเราเหล่านี้คือเจว็ดของพวกเรา ซึ่งพวกเราได้เรียกร้อง(ให้บูชา) อื่นจากพระองค์” ดังนั้น พวกมันได้กล่าวตอบว่า “แท้จริงพวกท่านนั้นเป็นผู้โกหก”');
INSERT INTO "king_fahad_thai" VALUES(16,87,'16:87','และในวันนั้นพวกเขาจะยอมจํานนต่ออัลลออ์ และสิ่งที่พวกเขาเคยกุขึ้นมาก็จะสูญหายไปจากพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(16,88,'16:88','บรรดาผู้ปฏิเสธศรัทธาและปิดกั้นทางของอัลลอฮ์ เราได้เพิ่มการลงโทษแก่พวกเขาให้มากขึ้น เนื่องจากพวกเขาได้ก่อความเสียหาย');
INSERT INTO "king_fahad_thai" VALUES(16,89,'16:89','และวันที่เราจะตั้งพยานขึ้นจากทุกประชาชาติ (เพื่อเป็นพยาน) ต่อพวกเขาจากหมู่พวกเขาเอง และเราก็นําเจ้ามาเป็นพนานต่อเขาเหล่านั้นและเราได้ให้คัมภีร์แก่เจ้าเพื่อชี้แจงแก่ทุกสิ่ง และเพื่อเป็นทางนําและเป็นความเมตตา และเป็นข่าวดีแก่บรรดามุสลิม');
INSERT INTO "king_fahad_thai" VALUES(16,90,'16:90','แท้จริงอัลลอฮ์ทรงใช้ให้รักษาความยุติธรรมและทําดี และการบริจาคแก่ญาติใกล้ชิดและให้ละเว้นจากการทําลามกและการชั่วช้า และการอธรรม พระองค์ทรงตักเตือนพวกเจ้าเพื่อพวกเจ้าจักได้รําลึก');
INSERT INTO "king_fahad_thai" VALUES(16,91,'16:91','และพวกเจ้าจงปฏิบัติให้ครบตามพันธสัญญาของอัลลอฮ์ เมื่อพวกเจ้าได้ให้สัญญาไว้ และพวกเจ้าอย่าได้ทําลายคําสาบานหลังจากได้ยืนยันมัน และแน่นอน พวกเจ้าได้ตั้งอัลลอฮ์เป็นพยานแก่พวกเจ้า แท้จริงอัลลอฮ์ทรงรอบรู้สิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(16,92,'16:92','และพวกเจ้าอย่าเป็นเช่นนางที่คลายเกลียวด้ายของนาง หลังจากที่ได้ปั่นให้มันแน่นแล้ว โดยถือเอาการสาบานของพวกเจ้าเป็นการล่อลวงระหว่างพวกเจ้า เพื่อที่จะให้ชาติหนึ่งเข้มแข็งกว่าอีกชาติหนึ่ง แท้จริง อัลลอฮ์ทรงทดลองพวกเจ้าด้วยการสาบาน และแน่นอน พระองค์ตะทรงชี้แจงแก่พวกเจ้าในวันกิยามะฮ์ ถึงสิ่งที่พวกเจ้าขัดแย้งกันในเรื่องนั้น');
INSERT INTO "king_fahad_thai" VALUES(16,93,'16:93','และหากอัลลอฮ์ทรงประสงค์ แน่นอนพระองค์จะทรงทําให้พวกเจ้าเป็นประชาชาติเดียวกัน แต่พระองค์จะให้ผู้ที่พระองค์ประสงค์หลงทาง และจะทรงชี้แนะทางแก่ผู้ที่พระองค์ทรงประสงค์ และแน่นอน พวกเจ้าจะถูกสอบสวนถึงสิ่งที่พวกเจ้าได้กระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(16,94,'16:94','และพวกเจ้าอย่าถือเอาการสาบานของพวกเจ้า เป็นการล่อลวงระหว่างพวกเจ้า แล้วเท้าก็จะก้าวพลาดได้หลังจากที่มันมั่นคงแล้ว และพวกเจ้าจะได้ลิ่มรสความชั่ว เพระพวกเจ้าได้ปิดกั้นทางของอัลลอฮ์ และสําหรับพวกเจ้าจะได้รับการลงโทษอย่างมหันต์');
INSERT INTO "king_fahad_thai" VALUES(16,95,'16:95','และพวกเจ้าอย่าแลกเปลี่ยนข้อตกลงของอัลลอฮ์กับราคาเพียงเล็กน้อย แท้จริง ณ ที่อัลลอฮ์ ย่อมดีกว่าสําหรับพวกเจ้า หากพวกเจ้ารู้');
INSERT INTO "king_fahad_thai" VALUES(16,96,'16:96','สิ่งที่อยู่กับพวกเจ้าย่อมอันตรธาน และสิ่งที่อยู่กับอัลลอฮ์นั้นย่อมจีรัง และแน่นอน เราจะตอบแทนบรรดาผู้อดทนซึ่งรางวัลของพวกเขาที่ดียิ่งกว่าที่พวกเขาได้เคยกระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(16,97,'16:97','ผู้ใดปฏิบัติความดีไม่ว่าจะเป็นเพศชายหรือเพศหญิงก็ตาม โดยที่เขาเป็นผู้ศรัทธา ดังนั้นเราจะให้เขาดํารงชีวิตที่ดี และแน่นอนเราจะตอบแทนพวกเขาซึ่งรางวัลของพวกเขา ที่ดียิ่งกว่าที่พวกเขาได้เคยกระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(16,98,'16:98','ดังนั้น เมื่อเจ้าอ่านอัลกรุอาน ก็จงขอความคุ้มครองต่ออัลลอฮ์ให้พ้นจากชัยฏอนที่ถูกสาปแช่ง');
INSERT INTO "king_fahad_thai" VALUES(16,99,'16:99','แท้จริงมันไม่มีอํานาจใด ๆ เหนือบรรดาผู้ศรัทธา โดยที่พวกเขาได้มอบหมาย(การงาน) ต่อพระเจ้าของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(16,100,'16:100','แท้จริงอํานาจของมันจะมีเหนือบรรดาผู้เป็นมิตรกับมัน และบรรดาผู้ที่พวกเขาเป็นผู้ตั้งภาคีกับพระองค์');
INSERT INTO "king_fahad_thai" VALUES(16,101,'16:101','และเมื่อเราได้เปลี่ยนโองการหนึ่งแทนอีกโองการหนึ่ง และอัลลอฮ์ทรงรู้ดียิ่งในสิ่งที่พระองค์ได้ทรงประทานลงมา พวกเขากล่าวว่า “แท้จริงท่านเป็นผู้กุขึ้น” เปล่าเลย! ส่วนมากของพวกเขาไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(16,102,'16:102','จงกล่าวเถิดมุฮัมมัด วิญญาณบริสุทธิ์ ได้นํามัน(โองการอัลกรุอาน) ลงมาจากพระเจ้าของเจ้าด้วยความจริง เพื่อให้บรรดาผู้ศรัทธามีความมั่นคง และเป็นทางนํา และข่าวดีแก่บรรดามุสลิม');
INSERT INTO "king_fahad_thai" VALUES(16,103,'16:103','และโดยแน่นอนเรารู้ที่พวกเขากล่าวว่า “แท้จริงสามัญชนคนหนึ่งสอนเขา” ภาษาที่พวกเขาพาดพิงไปถึงนั้นเป็นภาษาต่างถิ่นและนี่เป็นภาษาอาหรับที่ชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(16,104,'16:104','แท้จริง บรรดาผู้ไม่ศรัทธาต่อโองการทั้งหลายของอัลลอฮ์ อัลลอฮ์จะไม่ชี้แนะทางแก่พวกเขา และสําหรับพวกเขาจะได้รับการลงโทษอันเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(16,105,'16:105','แท้จริงบรรดาผู้ไม่ศรัทธาต่อโองการทั้งหลายของอัลลอฮ์นั้น กุความเท็จขึ้น และชนเล่านั้นคือผู้กล่าวเท็จ');
INSERT INTO "king_fahad_thai" VALUES(16,106,'16:106','ผู้ใดปฏิเสธศรัทธาต่ออัลลอฮ์หลังจากที่เขาได้รับศรัทธาแล้ว(เขาจะได้รับความกริ้วจากอัลลอฮ์) เว้นแต่ผู้ที่ถูกบังคับทั้งๆ ที่หัวใจของเขาเปี่ยมไปด้วยศรัทธา แต่ผู้ใดเปิดหัวอกของเขาด้วยการปฏิเสธศรัทธา พวกเขาก็จะได้รับความกริ้วจากอัลลอฮ์และสําหรับพวกเขาจะได้รับการลงโทษอย่างมหันต์');
INSERT INTO "king_fahad_thai" VALUES(16,107,'16:107','ทั้งนี้เพราะว่าพวกเขาพอใจเลือกเอาชีวิตในโลกนี้เหนือปรโลก และแท้จริงอัลลอฮ์จะไม่ทรงชี้แนะทางแก่กลุ่มชนผู้ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(16,108,'16:108','ชนเหล่านั้นคือผู้ที่อัลลอฮ์ทรงประทับตราบนหัวใจของพวกเขา และบนหูของพวกเขาและบนตาของพวกเขา และชนเหล่านั้นคือผู้เผลอเรอ');
INSERT INTO "king_fahad_thai" VALUES(16,109,'16:109','โดยแน่นอน แท้จริงพวกเขาในปรโลกจะเป็นผู้ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(16,110,'16:110','แต่ แท้จริงพระเจ้าของเจ้านั้น สําหรับบรรดาผู้อพยพหลังจากถูกทดสอบแล้ว พวกเขาก็ต่อสู้ดิ้นรนและอดทน แท้จริงพระเจ้าของเจ้าหลังจากนั้น แน่นอนพระองค์เป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(16,111,'16:111','วันที่ทุกชีวิตจะมาโต้แย้งแทนชีวิตของมัน และทุกชีวิตจะถูกตอบแทนจนครบถ้วนตามที่ได้กระทําไว้ และพสกเขาจะไม่ถูกอธรรม');
INSERT INTO "king_fahad_thai" VALUES(16,112,'16:112','และอัลลอฮ์ทรงยกอุทาหรณ์ เมืองหนึ่งซึ่งปลอดภัยและสงบ ปัจจัยยังชีพของมันมีมาอย่างอุดมสมบูรณ์ทุกแห่งหน แล้วมันก็ปฏิเสธต่อความโปรดปรานของอัลลอฮ์ ดังนั้น อัลลอฮ์จึงทรงให้มันลิ้มรสความรู้สึก แห่งความหิวและความกลัว ตามที่พวกเขาได้เคยกระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(16,113,'16:113','และโดยแน่นอน ได้มีร่อซู,ท่านหนึ่งจากหมู่พวกเขามายังพวกเขา แล้วพวกเขาปฏิเสธไม่ยอมเชื่อเขา ดังนั้น การลงโทษได้คร่าพวกเขาโดยที่พวกเขาเป็นผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(16,114,'16:114','พวกเจ้าจงบริโภคในสิ่งที่อัลลอฮ์ทรงประทานปัจจัยยังชีพแก่พวกเจ้า ซึ่งเป็นที่อนุมัติที่ดี และพวกเจ้าจงขอบคุณต่อความโปรดปรานของอัลลอฮ์ หากพวกเจ้าเคารพภักดีเฉพาะพระองค์เท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(16,115,'16:115','แท้จริง พระองค์ทรงห้ามพวกเจ้าเพียงแต่สัตว์ที่ตายเอง และเลือด และเนื้อสุกร และสัตว์ที่ถูกเปล่งเสียงที่มันเพื่ออื่นจากอัลลอฮ์ ดังนั้นผู้ใดที่อยู่ในสภาพคับขัน โดยหลีกเลี่ยงไม่ได้และไม่เป็นผู้ละเมิดแล้ว แท้จริง อัลลอฮ์เป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(16,116,'16:116','และพวกเจ้าอย่ากล่าวตามที่ลิ้นของพวกเจ้ากล่าวเท็จขึ้นว่า “นี่เป็นที่อนุมัติและนี่เป็นที่ต้องห้าม” เพื่อที่พวกเจ้าจะกล่าวเท็จต่ออัลลอฮ์แท้จริงบรรดาผู้กล่าวเท็จต่ออัลลอฮ์นั้น พวกเขาจะไม่ได้รับความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(16,117,'16:117','ผลประโยชน์เพียงเล็กน้อย และสําหรับพวกเขาคือการลงโทษอันเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(16,118,'16:118','และแก่บรรดาชาวยิว เราได้ห้ามสิ่งที่เราได้บอกแก่เจ้ามาแต่ก่อนแล้ว และเรามิได้อยุติธรรมต่อพวกเขา แต่พวกเขาได้อยุติธรรมต่อตัวพวกเขาเอง');
INSERT INTO "king_fahad_thai" VALUES(16,119,'16:119','แต่แท้จริงพระเจ้าของเจ้านั้น สําหรับบรรดาผู้กระทําการชั่วโดยไม่รู้ แล้วพวกเขาก็ลุแก่โทษหลังจากนั้น และฟื้นฟูแก้ไขให้ดี แท้จริงพระเจ้าของเจ้าหลังจากนั้น แน่นอนพระองค์เป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(16,120,'16:120','แท้จริง อิบรอฮีมนั้นเป็นแบบอย่างอันดีเลิศ เป็นผู้ภักดีต่ออัลลอฮ์ เป็นผู้เที่ยงธรรม และเขามิได้อยู่ในหมู่ผู้ตั้งภาคี');
INSERT INTO "king_fahad_thai" VALUES(16,121,'16:121','เป็นผู้กตัญญูกตเวทีต่อความโปรดปรานของพระองค์ พระองค์ทรงเลือกเขา และทรงชี้แนะทางแก้เขาสู่ทางที่เที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(16,122,'16:122','และเราได้ให้ความดี แก่เขาในโลกนี้และแท้จริงในปรโลกนั้น เขาจะอยู่ในหมู่คนดี ๆ อย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(16,123,'16:123','แล้วเราได้วะฮีแก่เจ้าว่า “จงปฏิบัติตามศาสนาของอิบรอฮีมผู้เที่ยงธรรม และเขามิได้อยู่ในมหู่ผู้ตั้งภาคี”');
INSERT INTO "king_fahad_thai" VALUES(16,124,'16:124','แท้จริง วันเสาร์ได้ถูกกําหนดขึ้นแก่บรรดาผู้ขัดแย้งกันในเรื่องนั้น และแท้จริงพระเจ้าของเจ้าจะตัดสินระหว่างพวกเขาอย่างแน่นอนในวันกิยามะฮ์ ในสิ่งที่พวกเขาขัดแย้งกันในเรื่องนั้น');
INSERT INTO "king_fahad_thai" VALUES(16,125,'16:125','จงเรียกร้องสู่แนวทางแห่งพระเจ้าของสูเจ้าโดยสุขุม และการตักเตือนที่ดี และจงโต้แย้งพวกเขาด้วยสิ่งที่ดีกว่า แท้จริงพระเจ้าของพระองค์และพระองค์ทรงรู้ดียิ่งถึงบรรดาผู้ที่อยู่ในทางที่ถูกต้อง');
INSERT INTO "king_fahad_thai" VALUES(16,126,'16:126','และหากพวกเจ้าจะลงโทษ(ฝ่ายปรปักษ์) ก็จงลงโทษเยี่ยงที่พวกเจ้าได้รับโทษ และหากพวกเจ้าอดทน แน่นอน มันเป็นการดียิ่งสําหรับบรรดาผู้อดทน');
INSERT INTO "king_fahad_thai" VALUES(16,127,'16:127','และจงอดทนเถิด และการอดทนของเจ้าจะมีขึ้นไม่ได้ เว้นแต่ด้วย(การเตาฟีกของ) อัลลอฮ์ และอย่าเศร้าโศกต่อพวกเขา และอย่าคับใจในสิ่งที่พวกเขาวางกลอุบาย');
INSERT INTO "king_fahad_thai" VALUES(16,128,'16:128','แท้จริง อัลลอฮฺทรงอยู่ร่วมกับบรรดาผู้ยําเกรง และบรรดาผู้กระทําความดี');
INSERT INTO "king_fahad_thai" VALUES(17,1,'17:1','มหาบริสุทธิ์ผู้ทรงนําบ่าวของพระองค์เดินทางในเวลากลางคืน จากมัสยิดอัลหะรอมไปยังมัสยิดอัลอักซอ ซึ่งบริเวณรอบมันเราได้ให้ความจําเริญ เพื่อเราจะให้เขาเห็นบางอย่างจากสัญญาณต่างๆของเรา แท้จริง พระองค์คือผู้ทรงได้ยิน ผู้ทรงเห็น');
INSERT INTO "king_fahad_thai" VALUES(17,2,'17:2','และเราได้ให้คัมภีร์แก่มูซา และเราได้ทําให้มันเป็นทางนําแก่วงศ์วานของอิสรออีลว่าอย่ายึดถือผู้ใดอื่นจากข้าเป็นผู้คุ้มครองอย่างเด็ดขาด');
INSERT INTO "king_fahad_thai" VALUES(17,3,'17:3','โอ้ เผ่าพันธุ์ของเราได้บรรทุก (ไว้ในเรือ) กับนูหเอ๋ย! แท้จริงเขาเป็นบ่าวผู้กตัญญู');
INSERT INTO "king_fahad_thai" VALUES(17,4,'17:4','และเราได้แจ้งแก่วงศ์วานของอิสรออีลในคัมภีร์ว่า พวกเจ้าจะก่อการเสียหายในแผ่นดินสองครั้ง และแน่นอน พวกเจ้าจะโอหังยโสยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(17,5,'17:5','ดังนั้น เมื่อสัญญาหนึ่งในสองครั้งได้มาถึง เราได้ส่งบรรดาบ่าวของเราผู้มีอํานาจเข้มแข็งเข้าครอบครอง พวกเจ้า แล้วพวกเขาได้บุกเข้าค้นตามบ้านเรือน และมันเป็นสัญญาที่ได้เกิดขึ้นแล้ว');
INSERT INTO "king_fahad_thai" VALUES(17,6,'17:6','และเราได้ให้พวกเจ้ากลับมีอํานาจเหนือพวกเขา และเราได้ให้พวกเจ้ามีทรัพย์สินและบุตรหลาน และเราได้ทําให้พวกเจ้ามีรี้พลมากมาย');
INSERT INTO "king_fahad_thai" VALUES(17,7,'17:7','หากพวกเจ้าทําความดี พวกเจ้าก็ทําเพื่อตัวของเจ้าเอง และหากว่าพวกเจ้าทําความชั่วก็เพื่อตัวเอง ดังนั้น เมื่อสัญญาอีกข้อหนึ่งได้มาถึง เพื่อพวกเขาก่อความอับอายขายหน้าแก่พวกเจ้า และเพื่อเข้าไปในมัสยิดเช่นที่พวกเขาได้เข้าไปแล้วในครั้งแรก และเพื่อทําลายสิ่งที่พวกเขาได้ชัยชนะอย่างหมดสิ้น');
INSERT INTO "king_fahad_thai" VALUES(17,8,'17:8','หวังว่าพระเจ้าของพวกเจ้าจะทรงเมตตาแก่พวกเจ้า และหากพวกเจ้ากลับมา (ก่อกวน)อีกเราก็โต้กลับ และเราได้ให้นรกเป็นที่คุมขังสําหรับผู้ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(17,9,'17:9','แท้จริง อัลกุรอานนี้นําสู่ทางที่เที่ยงตรงยิ่ง และแจ้งข่าวดีแก่บรรดาผู้ศรัทธาที่ประกอบความดีทั้งหลายว่า สําหรับพวกเขานั้นจะได้รับการตอบแทนอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(17,10,'17:10','และแท้จริงบรรดาผู้ไม่ศรัทธาต่อโลกหน้านั้น เราได้เตรียมไว้สําหรับพวกเขาแล้วซึ่งการลงโทษอันเจ็บแสบ');
INSERT INTO "king_fahad_thai" VALUES(17,11,'17:11','และมนุษย์นั้นวิงวอนขอความชั้ว เยี่ยงการวิงวอนขอของเขาเพื่อความดี และมนุษย์นั้นเป็นผู้รีบร้อนเสมอ');
INSERT INTO "king_fahad_thai" VALUES(17,12,'17:12','และเราได้ทําให้กลางคืนและกลางวันเป็นสองสัญญาณ ดังนั้น เราทําให้สัญญาณของกลางคืนมืดมน และเราได้ทําให้สัญญาณของกลางวันมีแสงสว่าง เพื่อพวกเจ้าจะได้แสวงหาความโปรดปรานจากพระเจ้าของพวกเจ้า และเพื่อพวกเจ้าจะได้รู้จํานวนปีทั้งหลายและการคํานวณและทุกๆสิ่งเราได้แจกแจงมันอย่างละเอียดแล้ว');
INSERT INTO "king_fahad_thai" VALUES(17,13,'17:13','และมนุษย์ทุกคน เราได้ให้การงานของเขาแขวนติดไว้ที่ติดไว้ที่คอของเขา และในวันกิยามะอ เราจะเอาบันทึกออกมาให้เขาพบมันในสภาพที่กางแผ่');
INSERT INTO "king_fahad_thai" VALUES(17,14,'17:14','เจ้าจงอ่านบันทึกของเจ้า พอเพียงแก่ตัวเจ้าแล้ววันนี้ที่จะเป็นผู้ชําระของตัวเจ้าเอง');
INSERT INTO "king_fahad_thai" VALUES(17,15,'17:15','ผู้ใดได้พบแนวทางที่ถูกต้อง แท้จริงเขาจะอยู่ในทางนั้นเพื่อตัวเขาเอง และไม่มีผู้แบกภาระใดที่จะแบกภาระของผู้อื่นได้ และเรามิเคยลงโทษผู้ใด จนกว่าเราจะแต่งตั้งร่อซูลมา');
INSERT INTO "king_fahad_thai" VALUES(17,16,'17:16','และเมื่อเราปรารถนาที่จะทําลายหมู่บ้านใด เราได้บัญชาให้พวกฟุ่มเฟือยของมัน แล้วพวกเขาก็ฝ่าฝืน ดังนั้น พระดํารัส(การลงโทษ)สมควรแล้วแก่มัน ฉะนั้นเราจะได้ทําลายมันอย่างพินาศ');
INSERT INTO "king_fahad_thai" VALUES(17,17,'17:17','และกี่ศตวรรษแล้วหลังจากนูหที่เราได้ทําลาย และพอเพียงกับพระเจ้าของเจ้าผู้ทรงรอบรู้ ทรงเห็นความผิดของปวงบ่าวของพระองค์');
INSERT INTO "king_fahad_thai" VALUES(17,18,'17:18','ผู้ใดปรารถนาชีวิตชั่วคราว (ในโลกนี้) เราก็จะเร่งให้เขาได้รับมัน	ตามที่เราประสงค์แก่ผู้ที่เราปรารถนา แล้วเราได้เตรียมนรกไว้สําหรับเขา เขาจะเข้าไปอย่างถูกเหยียดหยามถูกขับไส');
INSERT INTO "king_fahad_thai" VALUES(17,19,'17:19','และผู้ใดปรารถนาปรโลก	และขวนขวายเพื่อมันอย่างจริงจัง โดยที่เขาเป็นผู้ศรัทธาชนเหล่านั้น	การขวนขวายของพวกเขาจะได้รับการชมเชย');
INSERT INTO "king_fahad_thai" VALUES(17,20,'17:20','ทั้งหมด เราช่วยเขาเหล่านี้และเขาเหล่าโน้น จากการประทานให้ของพระเจ้าของเจ้า และการประทานให้ของพระเจ้าของเจ้านั้นมิถูกห้าม (แก่ผู้ใด)');
INSERT INTO "king_fahad_thai" VALUES(17,21,'17:21','จงดูเถิด ! เราได้ทําให้บางคนในหมู่พวกเขาดีเด่นกว่าอีกบางคนอย่างไร? และแน่นอนปรโลกนั้นมีฐานะยิ่งใหญ่กว่าหลายชั้น และยิ่งใหญ่กว่าในทางดีเด่น');
INSERT INTO "king_fahad_thai" VALUES(17,22,'17:22','เจ้าอย่าตั้งพระเจ้าอื่นคู่เคียงกับอัลลอฮมิฉะนั้นเจ้าจะกลายเป็นผู้ถูกเหยียดหยามถูกทอดทิ้ง');
INSERT INTO "king_fahad_thai" VALUES(17,23,'17:23','และพระเจ้าของเจ้าบัญชาว่า พวกเจ้าอย่าเคารพภักดีผู้ใดนอกจากพระองค์เท่านั้นและจงทําดีต่อบิดามารดา เมื่อผู้ใดในทั้งสองหรือทั้งสองบรรลุสู่วัยชราอยู่กับเจ้า ดังนั้นอย่ากล่าวแก่ทั้งสองว่า อุฟ ! และอย่าขู่เข็ญท่านทั้งสอง และจงพูดแก่ท่านทั้งสองด้วยถ้อยคําที่อ่อนโยน');
INSERT INTO "king_fahad_thai" VALUES(17,24,'17:24','และจงนอบน้อมแก่ท่านทั้งสอง ซึ่งการถ่อมตนเนื่องจากความเมตตา และจงกล่าวว่า “ข่าแต่พระเจ้าของฉัน ทรงโปรดเมตตาแก่ท่านทั้งสองเช่นที่ทั้งสองได้เลี้ยงดูฉันเมื่อเยาว์วัย”');
INSERT INTO "king_fahad_thai" VALUES(17,25,'17:25','พระเจ้าของพวกเจ้าทรงรู้ดียิ่งถึงสิ่งที่อยู่ในจิตใจของพวกเจ้า หากพวกเจ้าเป็นคนดี ดังนั้นพระองค์เป็นผู้ทรงอภัยแก่บรรดาผู้กลับเนื้อกลับตัวอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(17,26,'17:26','และจงให้สิทธิแก่ญาติที่ใกล้ชิด และผู้ขัดสน และผู้เดินทาง และอย่าสุรุ่ยสุร่ายอย่างฟุ่มเฟือย27.แท้จริง บรรดาผู้สุรุ่ยสุร่ายนั้นเป็นพวกพ้องของเหล่าชัยฏอน และชัยฏอนนั้นเนรคุณต่อพระเจ้าของมัน');
INSERT INTO "king_fahad_thai" VALUES(17,27,'17:27','แท้จริงบรรดาผู้สุรุ่ยสุร่ายนั้นเป็นพวกพ้องของเหล่าชัยฏอน และชัยฏอนนั้นเนรคุณต่อพระเจ้าของมัน');
INSERT INTO "king_fahad_thai" VALUES(17,28,'17:28','และหากเจ้าผินหลังให้พวกเขา เพื่อแสวงหาความเมตตาจากพระเจ้าของเจ้า โดยหวังมันอยู่ ดังนั้น จงกล่าวแก่พวกเขาด้วยถ้อยคําที่นิ่มนวล');
INSERT INTO "king_fahad_thai" VALUES(17,29,'17:29','และอย่าให้มือของเจ้าถูกตรึงอยู่ที่คอของเจ้า และอย่าแบมันจนหมดสิ้น มิฉะนั้นเจ้าจะกลายเป็นผู้ถูกประนาม เศร้าโศกเสียใจ');
INSERT INTO "king_fahad_thai" VALUES(17,30,'17:30','แท้จริง พระเจ้าของเจ้าทรงเพิ่มพูนปัจจัยยังชีพแก่ผู้ที่พระองค์ทรงประสงค์ และทรงให้คับแคบ แท้จริงพระองค์เป็นผู้ทรงรอบรู้ เป็นผู้ทรงเห็นปวงบ่าวของพระองค์');
INSERT INTO "king_fahad_thai" VALUES(17,31,'17:31','และพวกเจ้าอย่าฆ่าลูกๆ ของพวกเจ้าเพราะกลัวความยากจน เราให้ปัจจัยยังชีพแก่พวกเขาและแก่พวกเจ้าโดยเฉพาะ แท้จริงการฆ่าพวกเขานั้นเป็นความผิดอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(17,32,'17:32','และพวกเจ้าอย่าเข้าใกล้การผิดประเวณี แท้จริงมันเป็นการลามกและทางอันชั่วช้า');
INSERT INTO "king_fahad_thai" VALUES(17,33,'17:33','และพวกเจ้าอย่าฆ่าชีวิตที่อัลลอฮทรงห้ามไว้ เว้นแต่ด้วยความเที่ยงธรรม และผู้ใดถูกฆ่าอย่างอยุติธรรม ดังนั้น เราได้ให้อํานาจแก่ผู้ปกครองของเขา ฉะนั้น อย่าได้ล่วงเกินขอบเขตในเรื่องการฆ่า แท้จริงเขา (ผู้ถูกอธรรม) จะได้รับความช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(17,34,'17:34','และพวกเจ้าอย่าเข้าใกล้ทรัพย์สินของเด็กกําพร้า เว้นแต่โดยวิธีที่ดียิ่ง จนกว่าเขาจะบรรลุนิติภาวะ และจงให้ครบตามสัญญา (เพราะ) แท้จริงสัญญานั้นจะถูกสอบสวน');
INSERT INTO "king_fahad_thai" VALUES(17,35,'17:35','และจงตวงให้เต็ม เมื่อพวกเจ้าตวงและจงชั่งด้วยตาชั่งที่เที่ยงตรง นั่นเป็นการดียิ่งและเป็นกานตัดสินใจที่ดีกว่า');
INSERT INTO "king_fahad_thai" VALUES(17,36,'17:36','และอย่าติดตามสิ่งที่เจ้าไม่มีความรู้ในเรื่องนั้น แท้จริงหู และตา และหัวใจ ทุกสิ่งเหล่านั้นจะถูกสอบสวน');
INSERT INTO "king_fahad_thai" VALUES(17,37,'17:37','และอย่าเดินบนแผ่นดินอย่างเย่อหยิ่งแท้จริงเจ้าจะแยกแผ่นดินไม่ได้เลย และจะไม่บรรลุความสูงของภูเขา');
INSERT INTO "king_fahad_thai" VALUES(17,38,'17:38','ทั้งหมดนั้น ความเลวของมันเป็นที่รังเกียจยิ่ง ณ พระผู้เป็นเจ้าของเจ้า');
INSERT INTO "king_fahad_thai" VALUES(17,39,'17:39','นั่นคือส่วนหนึ่งจากที่พระเจ้าของเจ้าทรงประทานฮิกมะฮแก่เจ้า และเจ้าอย่าตั้งพระเจ้าอื่นใดเคียงคู่กับอัลลอฮ มิฉะนั้นเจ้าจะถูกโยนลงในนรกญะฮันนัม เป็นผู้ถูกครหา ถูกขับไล่');
INSERT INTO "king_fahad_thai" VALUES(17,40,'17:40','พระเจ้าของพวกเจ้าทรงเลือกลูกผู้ชายให้แก่พวกเจ้า และพระองค์ทรงเลือกเอามะลาอิกะฮเป็นลูกผู้หญิงกระนั้นหรือ ? แท้จริงพวกเจ้านั้นกําลังกล่าวคําพูดที่ร้ายกาจอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(17,41,'17:41','และโดยแน่นอน เราได้ชี้แจงในอัลกุรอานนี้ เพื่อพวกเขาจะได้รําลึก แต่มันมิได้เพิ่มสิ่งใดแก่พวกเขา นอกจากการเตลิดหนี');
INSERT INTO "king_fahad_thai" VALUES(17,42,'17:42','จงกล่าวเถิดมุฮัมมัด หากมีพระเจ้ามากหลายคู่เคียงกับพระองค์เช่นที่พวกเขากล่าวเมื่อนั้นแน่นอนพวกมันจะแสวงหาทางไปสู่พระผู้ทรงครองบัลลังก์');
INSERT INTO "king_fahad_thai" VALUES(17,43,'17:43','มหาบริสุทธิ์แห่งพระองค์ และพระองค์ทรงสูงส่งเหนือจากที่พวกเขากล่าว ทรงสูงส่งอย่างใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(17,44,'17:44','ชั้นฟ้าทั้งเจ็ดและแผ่นดินและที่อยู่ในนั้นสดุดีสรรเสริญแด่พระองค์ และไม่มีสิ่งใดเว้นแต่จะสดุดีด้วยการสรรเสริญพระองค์แต่ว่าพวกเจ้าไม่เข้าใจคําสดุดีของพวกเขา แท้จริงพระองค์เป็นผู้ทรงหนักแน่น ผู้ทรงอภัยเสมอ');
INSERT INTO "king_fahad_thai" VALUES(17,45,'17:45','และเมื่อเจ้าอ่านอัลกุรอาน เราได้กางม่านที่ถูกซ่อนไว้ กั้นระหว่างเจ้าและบรรดาผู้ไม่ศรัทธาต่อวันปรโลก');
INSERT INTO "king_fahad_thai" VALUES(17,46,'17:46','และเราได้ทําฝาปิดบนหัวใจของพวกเขาเพื่อมิให้พวกเขาเข้าใจมัน (อัลกุรอาน) และในหูของพวกเขานั้นหนวก และเมื่อเจ้ากล่าวถึงพระเจ้าของเจ้าในอัลกุรอานเพียงองค์เดียว พวกเขาก็ผินหลังของพวกเขาเตลิดหนี');
INSERT INTO "king_fahad_thai" VALUES(17,47,'17:47','เรารู้ดียิ่งถึงสิ่งที่พวกเขาฟังมัน ขณะที่พวกเขาเงี่ยหูฟังเจ้า และขณะที่พวกเขาปรึกษากันลับๆ โดยพวกอธรรมกล่าวว่า“พวกท่านมิได้ตามผู้ใด นอกจากผู้ถูกเวทมนต์เท่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(17,48,'17:48','จงดูเถิด ! พวกเขายกอุทาหรณ์แก่เจ้าอย่างไร ดังนั้นพวกเขาได้หลงแล้ว พวกเขาไม่สามารถหาทางใดๆ ได้');
INSERT INTO "king_fahad_thai" VALUES(17,49,'17:49','และพวกเขากล่าวว่า “เมื่อเราเป็นกระดูกและร่วนยุ่ยแล้ว แท้จริงเราจะถูกให้ฟื้นขึ้นเพื่อกําเนิดใหม่แน่หรือ ?');
INSERT INTO "king_fahad_thai" VALUES(17,50,'17:50','จงกล่าวเถิดมุฮัมมัด “หากพวกท่านเป็นหินหรือเหล็ก”');
INSERT INTO "king_fahad_thai" VALUES(17,51,'17:51','”หรือกําเนิดใดจากที่แข็งยิ่งในหัวอกของพวกท่านก็ตาม” ดังนั้นพวกเขาจะกล่าวว่า “ผู้ใดเล่าจะให้เรากลับขึ้นมาอีก ?”จงกล่าวเถิดมุฮัมมัด“พระองค์ผู้ทรงบังเกิดพวกท่านเป็นครั้งแรก” แล้วพวกเขาก็สั่นศีรษะของพวกเขาแก่เจ้าพลางกล่าวว่า“เมื่อใดเล่า ?” จงกล่าวเถิดมุฮัมหมัด “หวังว่ามันใกล้เข้ามาแล้ว”');
INSERT INTO "king_fahad_thai" VALUES(17,52,'17:52','วันที่พระองค์จะทรงเรียกร้องพวกเจ้าและพวกเจ้าจะตอบสนองด้วยการสรรเสริญพระองค์และพวกเจ้าจะนึกว่ามิได้อยู่ (ในโลกนี้) เว้นแต่เพียงชั่วครู่เท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(17,53,'17:53','และจงกล่าวแก่ปวงบ่าวของข้าที่พวกเขากล่าวแต่คําพูดที่ดียิ่งว่า แท้จริงชัยฏอนนั้นมันยุแหย่ระหว่างพวกเขา แท้จริงชัยฏอนนั้นเป็นศัตรูที่เปิดเผยของมนุษย์');
INSERT INTO "king_fahad_thai" VALUES(17,54,'17:54','พระเจ้าของพวกเจ้าทรงรู้จักพวกเจ้าดียิ่ง หากพระองค์ทรงประสงค์พระองค์ก็จะทรงเมตตาพวกเจ้า หรือหากพระองค์ทรงประสงค์พระองค์ก็จะทรงลงโทษพวกเจ้า และเรามิได้ส่งเจ้ามาเป็นผู้คุ้มครองพวกเขาแต่ประการใด');
INSERT INTO "king_fahad_thai" VALUES(17,55,'17:55','และพระเจ้าของเจ้าทรงรู้ดียิ่งถึงสิ่งที่อยู่ในชั้นฟ้าทั้งหลายและแผ่นดิน และโดยแน่นอนเราได้เลือกนะบีบางคนให้ดีเด่นกว่าอีกบางคน และเราได้ให้ซะบูรแก่ดาวูด');
INSERT INTO "king_fahad_thai" VALUES(17,56,'17:56','จงกล่าวเถิดมุฮัมมัด “พวกท่านจงเรียกร้องบรรดาสิ่งที่พวกท่านกล่าวอ้างอื่นจากพระองค์4 พวกมันไม่มีอํานาจที่จะปลดเปลื้องความทุกข์ยากและเปลี่ยนแปลงมันจากพวกท่านได้”');
INSERT INTO "king_fahad_thai" VALUES(17,57,'17:57','เหล่านั้นที่พวกเขาวิงวอนนั้น พวกมันก็ยังหวังที่จะหาทางเข้าสู่พระเจ้าของพวกมันว่า ผู้ใดในหมู่พวกมันจะเข้าใกล้ที่สุดและพวกมันยังหวังในความเมตตาของพระองค์ และกลัวการลงโทษของพระองค์ แท้จริงการลงโทษของพระเจ้าของเจ้านั้นควรน่าระวัง');
INSERT INTO "king_fahad_thai" VALUES(17,58,'17:58','และไม่มีหมู่บ้านใดเว้นแต่เราเป็นผู้ทําลายมันก่อนถึงวันกิยามะฮ หรือเป็นผู้ลงโทษมันด้วยการลงโทษอย่างสาหัส นั่นมันได้ถูกบันทึกไว้แล้วในบันทึก');
INSERT INTO "king_fahad_thai" VALUES(17,59,'17:59','ไม่มีสิ่งใดยับยั้งเราโดยที่เราจะส่งสัญญาณต่าง ๆ เว้นแต่ว่าพวกสมัยก่อน ๆ ได้ปฏิเสธมัน และเราได้ให้อูฐตัวเมียเป็นที่ประจักษ์แจ้งแก่พวกษะมูด แต่พวกเขาได้ทารุณมัน และเรามิได้ส่งสัญญาณต่างๆ เพื่ออื่นใด เว้นแต่เพื่อเป็นการเตือนสําทับเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(17,60,'17:60','และจงรําลึกเมื่อเรากล่าวแก่เจ้าว่า แท้จริงพระเจ้าของเจ้าทรงรอบรู้ในเรื่องของมนุษย์และมิได้ทําให้การฝันซึ่งเราได้เจ้าเห็นเพื่ออื่นใด เว้นแต่เพื่อเป็นการทดสอบแก่มนุษย์ และต้นไม้ (ซักกูม) ที่ถูกสาปในอัลกุรอาน และเราได้ทําให้พวกเขาหวาดกลัว ดังนั้น มันมิได้เพิ่มสิ่งใดแก่พวกเขา นอกจากการดื้อรั้นมาก');
INSERT INTO "king_fahad_thai" VALUES(17,61,'17:61','และจงรําลึกเมื่อเรากล่าวแก่มะลาอิกะฮว่า “จงสุญูดต่ออาดัม” ดังนั้นพวกเขาได้สุญูดเว้นแต่อิบลีส มันกล่าวว่า “ฉันจะสุญูดต่อผู้ที่พระองค์ทรงสร้างจากดินกระนั้นหรือ ?”');
INSERT INTO "king_fahad_thai" VALUES(17,62,'17:62','มันกล่าวว่า “พระองค์ทรงเห็นแล้วมิใช่หรือ เขาผู้นี้ที่พระองค์ทรงให้เกียรติมากกว่าฉันหากพระองค์ทรงโปรดประวิงเวลาแก่ฉันจนถึงวันกิยามะฮ แน่นอนฉันจะทําลายล้างลูกหลานของเขาให้หมดสิ้น เว้นแต่เพียงเล็กน้อย”');
INSERT INTO "king_fahad_thai" VALUES(17,63,'17:63','พระองค์ตรัสว่า “เจ้าจงไปให้พ้น ! ดังนั้นผู้ใดในหมู่พวกเขาปฏิบัติตามเจ้า แท้จริงนรกคือการตอบแทนของพวกเจ้า.เป็นการตอบแทนที่สมบูรณ์');
INSERT INTO "king_fahad_thai" VALUES(17,64,'17:64','”และเจ้าจงยั่วยวนผู้ที่เจ้าสามารถทําให้เขาหลงในหมู่พวกเขาด้วยเสียงของเจ้า และชักชวนพวกเขาให้เห็นพ้องด้วยด้วยม้าของเจ้าและด้วยเท้าของเจ้า และจงร่วมกับพวกเขาในทรัพย์สินและลูกหลาน และจงสัญญาพวกเขา”และชัยฏอนมิได้ให้สัญญาใดๆ แก่พวกเขา เว้นแต่เป็นการหลอกลวงเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(17,65,'17:65','”แท้จริงปวงบ่าวของข้านั้น เจ้าไม่มีอํานาจใดๆ เหนือพวกเขา และพอเพียงแล้วที่พระเจ้าของเจ้าเป็นผู้คุ้มครอง”');
INSERT INTO "king_fahad_thai" VALUES(17,66,'17:66','พระเจ้าของพวกเจ้าคือผู้ทรงให้เรือแล่นตามท้องทะเล เพื่อพวกเจ้าจะได้แสวงหาความโปรดปรานของพระองค์ แท้จริงพระองค์เป็นผู้ทรงเมตตาแก่พวกเจ้าเสมอ');
INSERT INTO "king_fahad_thai" VALUES(17,67,'17:67','และเมื่อทุกขภัยประสบแก่พวกเจ้าในท้องทะเล ผู้ที่พวกเจ้าวิงวอนขอก็จะสูญหายไปเว้นแต่พระองค์เท่านั้น ต่อมาเมื่อพระองค์ทรงช่วยให้พวกเจ้ารอดพ้นขึ้นบก พวกเจ้าก็หันหลังออกไป และมนุษย์นั้นเป็นผู้เนรคุณเสมอ');
INSERT INTO "king_fahad_thai" VALUES(17,68,'17:68','พวกเจ้าจะปลอดภัยละหรือ หากพระองค์จะทรงให้ริมฝั่งนั้นถล่มลงไปกับพวกเจ้า หรือจะทรงส่งลมหอบกรวดกระหน่ําลงมาใส่พวกเจ้าแล้วพวกเจ้าจะไม่พบผู้ใดเป็นผู้คุ้มครองพวกเจ้าเลย');
INSERT INTO "king_fahad_thai" VALUES(17,69,'17:69','หรือพวกเจ้าจะปลอดภัยละหรือ หากพระองค์จะทรงนําพวกเจ้ากลับไปในนั้นอีกครั้งหนึ่ง แล้วทรงส่งลมพายุร้ายกระหน่ําพวกเจ้า แล้วให้พวกเจ้าจมน้ําตายเพราะพวกเจ้าเนรคุณ หลังจากนั้นพวกเจ้าก็จะไม่พบผู้ใดแก้แค้นแทนเรา');
INSERT INTO "king_fahad_thai" VALUES(17,70,'17:70','และโดยแน่นอน เราได้ให้เกียรติแก่ลูกหลานของอาดัม และเราได้บรรทุกพวกเขาทั้งทางบกและทางทะเล และได้ให้ปัจจัยยังชีพที่ดีทั้งหลายแก่พวกเขา และเราได้ให้พวกเขาดีเด่นอย่างมีเกียรติเหนือกว่าผู้ที่เราได้ให้บังเกิดมาเป็นส่วนใหญ่');
INSERT INTO "king_fahad_thai" VALUES(17,71,'17:71','วันที่เราจะเรียกร้องมหาชนทั้งหลาย พร้อมด้วยบันทึกของพวกเขา ดังนั้นผู้ใดที่บันทึกของเขาถูกยื่นให้ทางขวาของเขา เขาเหล่านั้นก็จะได้อ่านบันทึกของพวกเขา โดยที่พวกเขาจะไม่ถูกอธรรมแม้แต่น้อย');
INSERT INTO "king_fahad_thai" VALUES(17,72,'17:72','และผู้ใดบอดในโลกนี้ ดังนั้นเขาก็จะบอดในปรโลกด้วย และหลงทางอย่างไกลยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(17,73,'17:73','และหากว่าพวกเขาจะทําให้เจ้าหลงไปจากที่เราได้วะฮีแก่เจ้า เพื่อเจ้าจะได้กุสิ่งอื่นขึ้นแก่เรา และเมื่อนั้นแหละพวกเขาก็จะคบเจ้าเป็นเพื่อนสนิท');
INSERT INTO "king_fahad_thai" VALUES(17,74,'17:74','และหากว่าเรามิได้ให้เจ้าตั้งมั่นอยู่บนความจริงแล้ว โดยแน่นอนยิ่ง เจ้าอาจจะโน้มเอียงไปทางพวกเขาบ้างเล็กน้อย');
INSERT INTO "king_fahad_thai" VALUES(17,75,'17:75','ถ้าเช่นนั้น แน่นอนเราก็จะให้เจ้าลิ้มรส (การลงโทษ) สองเท่าในชีวิตนี้ และสาองเท่าเมื่อยามตาย แล้วเจ้าจะไม่พบผู้ช่วยเหลือแก่เจ้าให้พ้นจาก (การลงโทษของ) เรา');
INSERT INTO "king_fahad_thai" VALUES(17,76,'17:76','และหากพวกเขายุแหย่ให้เจ้าออกจากแผ่นดิน เพื่อขับไล่เจ้าออกไป และเมื่อนั้นพวกเขาจะไม่พํานักอยู่นานหลังจากเจ้า(ออกไปแล้ว) เว้นแต่ช่วงเวลาเพียงเล็กน้อยเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(17,77,'17:77','นี่คือแนวทางของผู้ที่เราได้ส่งเขามาก่อนเจ้าจากบรรดาร่อซูลของเรา และเจ้าจะไม่พบการเปลี่ยนแปลงในแนวทางของเราแต่ประการใด');
INSERT INTO "king_fahad_thai" VALUES(17,78,'17:78','จงดํารงการละหมาดไว้ตั้งแต่ตะวันคล้อยจนพลบค่ํา และการอ่านยามรุ่งอรุณ จริงการอ่านยามรุ่งอรุณนั้นเป็นพยานยืนยันเสมอ');
INSERT INTO "king_fahad_thai" VALUES(17,79,'17:79','และจากบางส่วนของกลางคืนเจ้าจงตื่นขึ้นมาละหมาดในเวลาของมัน เป็นการสมัครใจสําหรับเจ้า หวังว่าพระเจ้าของเจ้าจะทรงให้เจ้าได้รับตําแหน่งที่ถูกสรรเสริญ');
INSERT INTO "king_fahad_thai" VALUES(17,80,'17:80','และจงกล่าวว่า “ข้าแต่พระเจ้าของข้าพระองค์ ได้ทรงโปรดนําข้าพระองค์เข้าตามทางเข้าที่ชอบธรรม และได้ทรงโปรดนําข้าพระองค์ออกตามทางออกที่ชอบธรรม และทรงโปรดให้ข้าพระองค์มีอํานาจที่เข้มแข็ง ซึ่งได้รับการช่วยเหลือจากพระองค์”');
INSERT INTO "king_fahad_thai" VALUES(17,81,'17:81','และจงกล่าวเถิด “เมื่อความจริง” ปรากฏขึ้นและความเท็จย่อมมลายไป แท้จริงความเท็จนั้นย่อมมลายไปเสมอ”');
INSERT INTO "king_fahad_thai" VALUES(17,82,'17:82','และเราได้ให้ส่วนหนึ่งจากอัลกุรอานลงมา ซึ่งเป็นการบําบัดและความเมตตาแก่บรรดาผู้ศรัทธา และมันมิได้เพิ่มอันใดแก่พวกอธรรม นอกจากการขาดทุนเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(17,83,'17:83','และเมื่อเราให้ความโปรดปรานแก่มนุษย์เขาเหินห่างและปลีกตัวออกไปข้าง ๆ และเมื่อความชั่วประสบแก่เขาเขาก็เบื่อหน่ายหมดอาลัย');
INSERT INTO "king_fahad_thai" VALUES(17,84,'17:84','จงกล่าวเถิดมุฮัมมัด “ทุกคนจะกระทําตามรูปแบบของเขา ฉะนั้น พระเจ้าของพวกท่านทรงรู้ดียิ่งถึงผู้ที่เขาได้รับแนวทางอย่างถูกต้อง”');
INSERT INTO "king_fahad_thai" VALUES(17,85,'17:85','และพวกเขาจะถามเจ้า เกี่ยวกับวิญญาณจงกล่าวเถิดว่า “เรื่องวิญญาณนั้นเป็นไปตามพระบัญชาของพระเจ้าของฉัน และพวกท่านจะไม่ได้รับความรู้ใดๆ เว้นแต่เพียงเล็กน้อยเท่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(17,86,'17:86','และหากเราประสงค์ แน่นอนเราจะเอาสิ่งซึ่งเราได้วะฮีแก่เจ้าไปเสีย และเจ้าจะไม่พบผู้คุ้มครองคนใดเหนือเราในเรื่องนี้สําหรับเจ้า');
INSERT INTO "king_fahad_thai" VALUES(17,87,'17:87','แต่ว่ามันเป็นพระเมตตาจากพระเจ้าของเจ้า แท้จริงความโปรดปรานของพระองค์ที่มีต่อเจ้านั้นใหญ่หลวงนัก');
INSERT INTO "king_fahad_thai" VALUES(17,88,'17:88','จงกล่าวเถิดมุฮัมมัด แน่นอนหากมนุษย์และญินรวมกันที่จะนํามาเช่นอัลกุรอานนี้ พวกเขาไม่อาจจะนํามาเช่นนั้นได้ และแม้ว่าบางคนในหมู่พวกเขาเป็นผู้ช่วยเหลือแก่อีกบางคนก็ตาม');
INSERT INTO "king_fahad_thai" VALUES(17,89,'17:89','และโดยแน่นอนเราได้อธิบายแก่มนุษย์แล้ว จากทุกอุทาหรณ์ในอัลกุรอานนี้ แต่ส่วนมากของมนุษย์ปฏิเสธไม่ยอมรับนอกจากการไม่ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(17,90,'17:90','และพวกเขากล่าวว่า “เราจะไม่ศรัทธาต่อท่าน จนกว่าท่านจะทําให้แผ่นดินแตกออกเป็นลําธารแก่เรา"');
INSERT INTO "king_fahad_thai" VALUES(17,91,'17:91','”หรือให้ท่านมีส่วนอินทผลัม และองุ่นให้มันแยกเป็นลําน้ําหลายสาย พวยพุ่งออกมาท่านกลางมัน');
INSERT INTO "king_fahad_thai" VALUES(17,92,'17:92','”หรือท่านทําให้ชั้นฟ้าหล่นลงมาบนพวกเราเป็นเสี่ยงๆ ตามที่ท่านอ้าง หรือนําอัลลอฮและมะลาอิกะฮมาให้เราเห็นต่อหน้า');
INSERT INTO "king_fahad_thai" VALUES(17,93,'17:93','”หรือให้ท่านมีบ้านที่ประดับประดาไว้หนึ่งหลัง หรือท่านขึ้นไปบนชั้นฟ้า และเราจะไม่ศรัทธาสําหรับการขึ้นไปของท่านจนกว่าท่านจะนําคัมภีร์เล่มหนึ่งลงมาให้เราได้อ่าน” จงกล่าวเถิด “มหาบริสุทธิ์แห่งพระเจ้าของฉัน ฉันมิได้เป็นอื่นใด นอกจากเป็นมนุษย์ เป็นร่อซูล”');
INSERT INTO "king_fahad_thai" VALUES(17,94,'17:94','และไม่มีสิ่งใดที่จะห้ามมนุษย์ให้พวกเขาศรัทธา เมื่อแนวทางที่ถูกต้องมายังพวกเขาแล้ว นอกจากพวกเขาจะกล่าวว่า “อัลลอฮฺทรงแต่งตั้งมนุษย์ธรรมดาเป็นร่อซูลกระนั้นหรือ?”');
INSERT INTO "king_fahad_thai" VALUES(17,95,'17:95','จงกล่าวเถิดมุฮัมมัด “หากมะลาอิกะฮเดินสัญจรอย่างสงบในแผ่นดิน แน่นอนเราจะส่งมะลักหนึ่งลงมาจากฟากฟ้า เป็นร่อซูลแก่พวกเขา”');
INSERT INTO "king_fahad_thai" VALUES(17,96,'17:96','จงกล่าวเถิดมุฮัมมัด “พอเพียงแล้วที่อัลลอฮทรงเป็นพยาน ระหว่างฉันและพวกท่านแท้จริงพระองค์เป็นผู้ทรงรอบรู้ ทรงมองเห็นปวงบ่าวของพระองค์”');
INSERT INTO "king_fahad_thai" VALUES(17,97,'17:97','และผู้ใดที่อัลลอฮทรงแนะแนวทาง เขาก็จะเป็นผู้อยู่ในแนวทางที่ถูกต้อง และผู้ใดที่อัลลอฮทรงให้หลงทางแล้ว ดังนั้นสูเจ้าจะไม่พบอีกเลยสําหรับพวกเขา ซึ่งบรรดาผู้คุ้มครองอื่นจากพระองค์และเราจะชุมนุมพวกเขาในวันกิยามะฮ ถูกลากคว่ําหน้า โดยมีสภาพเป็นคนตาบอด เป็นใบ้และหูหนวก ที่พํานักของพวกเขาคือนรกญะฮันนัมทุกครั้งที่มันมอดเราได้เพิ่มการเผาไหม้ลุกโชนแก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(17,98,'17:98','นั่นคือการตอบแทนของพวกเขา โดยแน่นอน พวกเขาปฏิเสธศรัทธาต่อโองการทั้งหลายของเรา และพวกเขากล่าวว่า“เมื่อเราเป็นกระดูกและร่วนยุ่ยแล้ว แท้จริงเราจะถูกให้ฟื้นขึ้นเพื่อกําเนิดใหม่ได้อย่างไร ?”');
INSERT INTO "king_fahad_thai" VALUES(17,99,'17:99','พวกเขาไม่เห็นดอกหรือว่า แท้จรองอัลลอฮผู้ทรงสร้างชั้นฟ้าทั้งหลายและแผ่นดิน พระองค์เป็นผู้ทรงอานุภาพที่จะสร้างเยี่ยงพวกเขา และทรงกําหนดเวลาหนึ่งสําหรับพวกเขา ไม่มีการสงสัยใดๆ ในนั้น แต่พวกอธรรมปฏิเสธไม่ยอมรับนอกจากการไม่ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(17,100,'17:100','จงกล่าวเถิดมุฮัมมัด “หากพวกท่านครอบครองขุมแห่งความเมตตาของพระเจ้าของฉันเมื่อนั้นพวกท่านก็จะหน่วงเหนี่ยวมันไว้ เพราะกลัวการบริจาค และมนุษย์นั้นเป็นคนตระหนี่”');
INSERT INTO "king_fahad_thai" VALUES(17,101,'17:101','และโดยแน่นอน เราได้ให้แก่มูซาสัญญาณต่างๆ อันแจ่มชัด 9 ประการ ดังนั้น เจ้าจงถามวงศ์วานของอิสรออีลเมื่อเขา (มูซา) มายังพวกเขาฟิรเอานได้พูดกับเขาว่า “โอ้ มูซาเอ๋ย แท้จริงฉันคิดว่าท่านถูกเวทมนต์อย่างแน่นอน”');
INSERT INTO "king_fahad_thai" VALUES(17,102,'17:102','เขากล่าวว่า “โดยแน่นอนท่านย่อมรู้ดีว่าไม่มีผู้ใดประทานสิ่งเหล่านี้ นอกจากพระเจ้าแห่งชั้นฟ้าทั้งหลายและแผ่นดินเพื่อเป็นพยาน และแท้จริงฉันคิดว่าแน่นอนท่าน โอ้ฟิรเอานเอ๋ย เป็นผู้หายนะแล้ว”');
INSERT INTO "king_fahad_thai" VALUES(17,103,'17:103','ดังนั้น เขา (ฟิรเอาน) ต้องการที่จะย้ายพวกเขาออกไปจากแผ่นดิน ฉะนั้น เราจึงให้เขาจมน้ําตายและผู้ที่อยู่ร่วมกับเขาทั้งหมด');
INSERT INTO "king_fahad_thai" VALUES(17,104,'17:104','และเราได้กล่าวแก่วงศ์วานของอิสรออีลหลังจากเขาว่า “จงพํานักอยู่ในดินแดนนี้” ดังนั้นเมื่อสัญญาแห่งวันอาคิเราะฮได้มาถึง เราจะนําพวกเจ้าทั้งหมดมารวมไว้ด้วยกัน”');
INSERT INTO "king_fahad_thai" VALUES(17,105,'17:105','และด้วยความจริง เราได้ประทานมัน (อัลกุรอาน) ลงมา และด้วยความจริงมันได้ลงมาและเรามิได้ส่งเจ้าเพื่ออื่นใด นอกจากเพื่อเป็นผู้แจ้งข่าวดี และเป็นผู้ตักเตือน');
INSERT INTO "king_fahad_thai" VALUES(17,106,'17:106','และอัลกุรอาน เราได้แยกมันไว้อย่างชัดเจน เพื่อเจ้าจะได้อ่านมันแก่มนุษย์อย่างช้าๆ และเราได้ประทานมันลงมาเป็นขั้นตอน');
INSERT INTO "king_fahad_thai" VALUES(17,107,'17:107','จงกล่าวเถิดมุฮัมมัด “พวกท่านจะศรัทธาในมันหรือไม่ศรัทธาก็ตาม แท้จริง บรรดาผู้ได้รับความรู้ก่อนหน้ามันนั้นเมื่อมันได้ถูกอ่านแก่พวกเขาพวกเขาจะหมอบราบลง ใบหน้าจรดพื้นเพื่อสุญด”');
INSERT INTO "king_fahad_thai" VALUES(17,108,'17:108','และพวกเขาจะกล่าวว่า “มหาบริสุทธิ์แห่งพระเจ้าของเรา สัญญาของพระเจ้าของเรานั้นแน่นอน ย่อมถูกปฏิบัติให้ครบถ้วน”');
INSERT INTO "king_fahad_thai" VALUES(17,109,'17:109','และพวกเขาจะหมอบราบลงใบหน้าจรดพื้นพลางร้องไห้ และมันจะเพิ่มการสํารวมแก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(17,110,'17:110','จงกล่าวเถิดมุฮัมมัด “พวกท่านจงเรียกอัลลอฮหรือจงเรียกอัรเราะหมานเถิด อันใดก็ตามที่เจ้าเรียก สําหรับพระองค์นั้นพระนามสวยงามยิ่ง และอย่ายกเสียงดังในเวลาละหมาดของเจ้า และอย่าลดให้ค่อยเช่นกัน แต่จงแสวงหาทางระหว่างนั้น (ปานกลาง)”');
INSERT INTO "king_fahad_thai" VALUES(17,111,'17:111','และจงกล่าวเถิดมุฮัมมัด “การสรรเสริญทั้งมวลเป็นของอัลลอฮ ซึ่งไม่ทรงตั้งพระบุตรและไม่มีภาคีใดๆ ร่วมกับพระองค์ในอํานาจ และไม่มีผู้ช่วยเหลือใดๆ แก่พระองค์ให้พ้นจากความต่ําต้อยและจงให้ความเกรียงไกรแด่พระองค์อย่างกึกก้อง”');
INSERT INTO "king_fahad_thai" VALUES(18,1,'18:1','บรรดาการสรรเสริญเป็นของอัลลอฮผู้ทรงประทานคัมภีร์แก่บ่าวของพระองค์ และพระองค์มิได้ทรงทําให้มันมีการบิดเบือนแต่อย่างใด');
INSERT INTO "king_fahad_thai" VALUES(18,2,'18:2','เป็นคัมภีร์ที่เที่ยงธรรม เพื่อเตือนสําทับถึงการลงโทษอย่างสาหัสจากพระองค์ และเพื่อแจ้งข่าวดีแก่บรรดาผู้ศรัทธาที่กระทําความดีทั้งหลายว่า สําหรับพวกเขานั้นจะได้รับรางวัลอันดีงาม (คือสวนสวรรค์)');
INSERT INTO "king_fahad_thai" VALUES(18,3,'18:3','เป็นผู้พํานักอยู่ในนั้นตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(18,4,'18:4','และเพื่อเตือนสําทับบรรดาผู้ที่กล่าวว่า “อัลลอฮทรงตั้งพระบุตรขึ้น”');
INSERT INTO "king_fahad_thai" VALUES(18,5,'18:5','พวกเขาไม่มีความรู้ใดๆ ในเรื่องนี้ และบรรพบุรุษของพวกเขาก็เช่นกัน เป็นคํากล่าวที่น่าเกลียดยิ่งที่ออกจากปากของพวกเขา โดยที่พวกเขามิได้กล่าวอันใดนอกจากความเท็จ');
INSERT INTO "king_fahad_thai" VALUES(18,6,'18:6','ดังนั้น บางทีเจ้าอาจเป็นผู้ทําลายชีวิตของเจ้าด้วยความเสียใจ เนื่องจากการผินหลังของพวกเขา หากพวกเขาไม่ศรัทธาต่ออัลกุรอานนี้');
INSERT INTO "king_fahad_thai" VALUES(18,7,'18:7','แท้จริง เราได้ทําให้สิ่งที่อยู่บนแผ่นดินเป็นที่ประดับสําหรับมัน เพื่อเราจะทดสอบพวกเขาว่า ผู้ใดในหมู่พวกเขามีผลงานที่ดีเยี่ยม');
INSERT INTO "king_fahad_thai" VALUES(18,8,'18:8','และแท้จริง แน่นอนเราเป็นผู้ทําให้สิ่งที่อยู่บนพื้นดินเป็นผุยผงแห้งแล้ง');
INSERT INTO "king_fahad_thai" VALUES(18,9,'18:9','เจ้าคิดหรือว่า ชาวถ้ําและแผ่นจารึก เป็นส่วนหนึ่งจากสัญญาณมหัศจรรย์ของเรากระนั้นหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(18,10,'18:10','จงรําลึกขณะที่พวกชายหนุ่มหลบเข้าไปในถ้ําแล้วพวกเขากล่าวว่า “ข้าแต่พระผู้เป็นเจ้าของเรา ขอพระองค์ทรงโปรดประทานความเมตตาจากพระองค์แก่เรา และทรงทําให้การงานของเราอยู่ในแนวทางที่ถูกต้อง”');
INSERT INTO "king_fahad_thai" VALUES(18,11,'18:11','แล้วเราได้อุดหูพวกเขา (ให้นอนหลับ) ในถ้ํา เป็นเวลาหลายปี');
INSERT INTO "king_fahad_thai" VALUES(18,12,'18:12','แล้วเราได้ให้พวกเขาลุกขึ้น เพื่อเราจะได้รู้ว่าผู้ใดในสองพวกนั้น นับเวลาที่พวกเขาพํานักอยู่ได้ถูกต้องกว่า');
INSERT INTO "king_fahad_thai" VALUES(18,13,'18:13','เราจะเล่าเรื่องราวของพวกเขาแก่เจ้าตามความเป็นจริง แท้จริงพวกเขาเป็นชายหนุ่มที่ศรัทธาต่อพระเจ้าของพวกเขา และเราได้เพิ่มแนวทางที่ถูกต้องให้แก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(18,14,'18:14','และเราได้ให้ความเข้มแข็งแก่หัวใจของพวกเขา ขณะที่พวกเขายืนขึ้นประกาศว่า “พระเจ้าของเราคือพระเจ้าแห่งชั้นฟ้าทั้งหลายและแผ่นดินเราจะไม่วิงวอนพระเจ้าอื่น จากพระองค์ มิเช่นนั้นเราก็กล่าวเกินความจริงอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(18,15,'18:15','กลุ่มชนของเราเหล่านั้นได้ยึดเอาพระเจ้าต่างๆ อื่นจากพระองค์ ทําไมพวกเขาจึงไม่นําหลักฐานอันชัดแจ้งมายืนยันเล่า ดังนั้นจะมีผู้ใดอธรรมยิ่งไปกว่าผู้ที่กล่าวเท็จต่ออัลลอฮ');
INSERT INTO "king_fahad_thai" VALUES(18,16,'18:16','และเมื่อพวกเจ้าปลีกตัวออกห่างจากพวกเขา และสิ่งที่พวกเขาเคารพบูชาอื่นจากอัลลอฮแล้ว ดังนั้นพวกเจ้าก็จงหลบเข้าไปในถ้ํา พระผู้เป็นเจ้าของพวกเจ้าจะทรงแผ่ความเมตตาของพระองค์แก่พวกเจ้า และจะทรงทําให้กิจการของพวกเจ้าดําเนินไปอย่างสะดวกสบาย');
INSERT INTO "king_fahad_thai" VALUES(18,17,'18:17','และเจ้าจะเห็นดวงอาทิตย์ เมื่อมันขึ้น มันจะคล้อยจากถ้ําของพวกเขาไปทางขวา และเมื่อมันตก มันจะเบนออกไปทางซ้าย โดยพวกเขาอยู่ในที่โล่งกว้างของมัน นั่นคือส่วนหนึ่งจากสัญญาณทั้งหลายของอัลลอฮฺ ผู้ใดที่อัลลอฮฺทรงแนะทางที่ถูกต้องแก่เขา เขาก็คือผู้ที่อยู่ในแนวทางที่ถูกต้อง และผู้ใดที่พระองค์ทรงให้เขาหลง เขาจะไม่พบ');
INSERT INTO "king_fahad_thai" VALUES(18,18,'18:18','และเจ้าคิดว่าพวกเขาตื่นทั้งๆ ที่พวกเขาหลับ และเราพลิกพวกเขาไปทางขวาและทางซ้ายและสุนัขของพวกเขาเหยียดขาหน้าทั้งสองของมันไปทางปากถ้ํา หากเจ้าจ้องมองพวกเขา แน่นอนเจ้าจะหันหลังเตลิดหนีจากพวกเขา และเจ้าจะเต็มไปด้วยความตกใจเพราะพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(18,19,'18:19','และในทํานองนั้นเราได้ให้พวกเขาลุกขึ้นเพื่อพวกเขาจะถามซึ่งกันและกัน คนหนึ่งในพวกเขากล่าวว่า “พวกท่านพํานักอยู่นานเท่าใด ?” พวกเขากล่าวว่า “เราพักอยู่วันหนึ่งหรือส่วนหนึ่งของวัน” พวกเขากล่าวว่า “พระผู้เป็นเจ้าของพวกท่านทรงทราบดีว่า พวกท่านพํานักอยู่นานเท่าใด ดังนั้นจงส่งคนหนึ่งในหมู่พวกท่านไปในเมือง พร้อมด้วยเหรียญเงินนี้ของพวกท่าน เพื่อเลือกดูอาหารที่ดียิ่ง และให้เขาซื้อมาให้แก่พวกท่าน และให้เขาประพฤติอย่างสุภาพ และอย่าให้ผู้ใดรู้เรื่องของพวกท่าน”');
INSERT INTO "king_fahad_thai" VALUES(18,20,'18:20','แท้จริงพวกเขานั้น หากพวกเขารู้เรื่องของพวกท่าน พวกเขาจะเอาก้อนหินขว้างพวกท่านหรือนําพวกท่านกลับไปนับถือศาสนาของพวกเขา และเมื่อนั้นพวกท่านจะไม่บรรลุความสําเร็จเลย”');
INSERT INTO "king_fahad_thai" VALUES(18,21,'18:21','และในทํานองนั้นเราได้เปิดเผยแก่พวกเขาเพื่อพวกเขาจะได้รู้ว่าสัญญาของอัลลอฮ์นั้นเป็นจริง และแท้จริงวันสิ้นโลกนั้นมีจริง ไม่ต้องสงสัยเลย เมื่อพวกเขาโต้เถียงกันในหมู่พวกเขาถึงเรื่องของพวกเขา(ชาวถ้ํา) แล้ว พวกเขากล่าวว่า “จงสร้างอาคารที่ปากถ้ําให้แก่พวกเขา” พระผู้เป็นเจ้าของพวกเขาทรงรู้ดียิ่งขึ้นในเรื่องของพวกเขา ฝ่ายบรรดาผู้มีเสียงข้างมากในเรื่องของพวกเขากล่าวว่า “แน่นอนเราจะสร้างมัสยิดที่ปากถ้ําให้แก่พวกเขา”');
INSERT INTO "king_fahad_thai" VALUES(18,22,'18:22','พวกเขาจะกล่าวกันว่า ชาวถ้ํานั้นมีสามคน ที่สี่ก็คือสุนัขของพวกเขา และอีกกลุ่มจะกล่าวว่า มีห้าคน ที่หกก็คือสุนัขของพวกเขา ทั้งนี้เป็นการเดาในสิ่งที่ไม่รู้ และอีกกลุ่มหนึ่งจะกล่าวว่ามีเจ็ดคน และที่แปดก็คือสุนัขของพวกเขา จงกล่าวเถิด“พระผู้เป็นเจ้าของฉันทรงรู้ดียิ่งถึงจํานวนของพวกเขา ไม่มีผู้ใดรู้เรื่องของพวกเขาเว้นแต่ส่วนน้อย” ดังนั้น เจ้าอย่าโต้เถียงกันในเรื่องของพวกเขา นอกจากการโต้เถียงที่ประจักษ์แจ้ง และอย่าสอบถามผู้ใดในเรื่องของพวกเขาเลย');
INSERT INTO "king_fahad_thai" VALUES(18,23,'18:23','และเจ้าอย่ากล่าวเกี่ยวกับสิ่งใดว่า “แท้จริงฉันจะเป็นผู้ทําสิ่งนั้นในวันพรุ่งนี้”');
INSERT INTO "king_fahad_thai" VALUES(18,24,'18:24','เว้นแต่อัลลอฮ์ทรงประสงค์ จงรําลึกถึงพระผู้เป็นเจ้าของเจ้าเมื่อลืม และจงกล่าวว่า “บางทีพระผู้เป็นเจ้าของฉันจะทรงชี้แนะทางที่ถูกต้องที่ใกล้กว่านี้แก่ฉัน”');
INSERT INTO "king_fahad_thai" VALUES(18,25,'18:25','และพวกเขาพํานักอยู่ในถ้ําของพวกเขาสามร้อยปี และเพิ่มอีกเก้าปี');
INSERT INTO "king_fahad_thai" VALUES(18,26,'18:26','จงกล่าวเถิดมุฮัมมัด “อัลลอฮ์ทรงรู้ดียิ่งว่าพวกเขาพํานักอยู่นานเท่าใด สําหรับพระองค์นั้นทรงรู้สิ่งพ้นญาณวิสัย ในชั้นฟ้าทั้งหลายและแผ่นดินพระองค์ทรงเห็นชัดและทรงฟังชัดทุกสิ่งทุกอย่าง ไม่มีผู้คุ้มครองใดสําหรับพวกเขาอื่นจากพระองค์พระองค์ไม่ทรงรับรู้ผู้ใด เข้าร่วมภาคีในการปกครองของพระองค์”');
INSERT INTO "king_fahad_thai" VALUES(18,27,'18:27','และจงอ่านสิ่งที่ถูกวะฮีแก่เจ้า จากคัมภีร์ของพระผู้เป็นเจ้าของเจ้า ไม่มีผู้ใดเปลี่ยนแปลงคํากล่าวของพระองค์ และเจ้าจะไม่พบที่พึ่งใด ๆ เลยนอกจากพระองค์');
INSERT INTO "king_fahad_thai" VALUES(18,28,'18:28','และจงอดทนต่อตัวของเจ้า ร่วมกับบรรดาผู้วิงวอนต่อพระผู้เป็นเจ้าของพวกเขา ทั้งยามเช้าและยามเย็น โดยปารถนาความโปรดปรานของพระองค์ และอย่าให้สายตาของเจ้าหันเหออกไปจากพวกเขา ขณะที่เจ้าประสงค์ความสวยงามแห่งชีวิตของโลกนี้ แลเจ้าอย่าเชื่อฟังผู้ที่เราทําให้หัวใจของเขาละเลยจากการรําลึกถึงเรา และปฏิบัติตามอารมณ์ต่ําของเขา และกิจการของเขาพินาศสูญหาย');
INSERT INTO "king_fahad_thai" VALUES(18,29,'18:29','และจงกล่าวเถิดมุฮัมมัด “สัจธรรมนั้นมาจากพระผู้เป็นเจ้าของพวกเจ้า” ดังนั้น ผู้ใดประสงค์ก็จงศรัทธา และผู้ใดประสงค์ก็จงปฏิเสธ แท้จริง เราได้เตรียมไฟนรกไว้สําหรับพวกอธรรมซึ่งกําแพงของมันล้อมรอบพวกเขา และถ้าพวกเขาร้องขอความช่วยเหลือ ก็จะถูกช่วยเหลือด้วยน้ําเสมือนน้ําทองแดงเดือดลวกใบหน้า มันเป็นน้ําดื่มที่ชั่วช้าและเป็นที่พํานักที่เลวร้าย');
INSERT INTO "king_fahad_thai" VALUES(18,30,'18:30','แท้จริงบรรดาผู้ศรัทธาและกระทําความดีทั้งหลาย เราจะไม่ให้การตอบแทนของผู้กระทําความดีสูญหายอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(18,31,'18:31','ชนเหล่านั้นแหละ สําหรับพวกเขาจะได้รับสวนสวรรค์หลากหลายเป็นที่พํานัก มีลําน้ําหลายสายไหลผ่าน ณ เบื้องล่างของพวกเขา ในสวนสวรรค์พวกเขาจะได้ประดับกําไลทอง และสวมอาภรณ์สีเขียวทําด้วยผ้าไหมละเอียดและผ้าไหมหยาบนอนเอกเขนกบนเตียงในสวรรค์ เป็นการตอบแทนที่ดียิ่งและเป็นพํานักที่ดีเยี่ยม');
INSERT INTO "king_fahad_thai" VALUES(18,32,'18:32','และจงเปรียบเทียบอุทาหรณ์หนึ่งแก่พวกเขา คือชายสองคน เราไให้สวนองุ่นสองแห่งแก่คนหนึ่งในสองคน และเราได้ล้อมสวนทั้งสองไว้ด้วยต้นอินทผลัม และเราได้ทําให้มีพืชพันธุ์ระหว่างสวนทั้งสองด้วย');
INSERT INTO "king_fahad_thai" VALUES(18,33,'18:33','แต่ละสวนทั้งสองแห่งนี้ได้ออกผลิตผลของมันอย่างสมบูรณ์ ไม่เคยลดน้อยแต่อย่างใดและเราได้ให้ลําน้ําไหลท่ามกลางสวนทั้งสอง');
INSERT INTO "king_fahad_thai" VALUES(18,34,'18:34','และเขาได้รับผลิตผล ดังนั้นเขาจึงกล่าวแก่เพื่อนของเขา ขณะที่กําลังโต้เถียงกันอยู่ว่า “ฉันมีทรัพย์สินมากกว่าท่าน และมีข้าบริพารมากกว่า”');
INSERT INTO "king_fahad_thai" VALUES(18,35,'18:35','เขาได้เข้าไปในสวนของเขาโดยที่เขาเป็นผู้อธรรมแก่ตัวเขาเอง เขากล่าวว่า “ฉันไม่คิดว่าสวนนี้จะพินาศไปได้เลย”');
INSERT INTO "king_fahad_thai" VALUES(18,36,'18:36','“และฉันไม่คิดว่าวันอวสานของโลกจะมีขึ้น และหากว่าฉันจะถูกกลับไปยังพระผู้เป็นเจ้าของฉัน แน่นอน ฉันจะพบที่กลับที่ดียิ่งขึ้นกว่านี้”');
INSERT INTO "king_fahad_thai" VALUES(18,37,'18:37','เพื่อนของเขากล่าวแก่เขาขณะที่กําลังโต้เถียงกันอยู่ว่า “ท่านเนรคุณต่อพระผุ้สร้างท่านจากดิน แล้วจากเชื้ออสุจิ แล้วพระองค์ทรงทําให้ท่านเป็นคนโดยสมบูรณ์ กระนั้นหรือ?”');
INSERT INTO "king_fahad_thai" VALUES(18,38,'18:38','“แต่ฉันเชื่อว่าพระองค์คืออัลลอฮ์ พระผู้เป็นเจ้าของฉัน และฉันจะไม่ตั้งผู้ใดร่วมเป็นภาคีกับผู้เป้นเจ้าของฉันเลย”');
INSERT INTO "king_fahad_thai" VALUES(18,39,'18:39','“และทําไมเล่าเมื่อท่านเข้าไปในสวนของท่าน ท่านควรกล่าวว่า สิ่งที่อัลลอฮ์ทรงประสงค์(ย่อมเกิดขึ้น) ไม่มีพลังใด ๆ(ที่จะช่วยเราได้) นอกจากที่อัลลอฮ์ หากท่านเห็นว่าฉันด้อยกว่าท่านทางด้านทรัพย์สมบัติและลูกหลาน');
INSERT INTO "king_fahad_thai" VALUES(18,40,'18:40','ดังนั้น บางทีพระผู้เป็นเจ้าของฉันจะทรงประทานให้ฉันดีกว่าสวนของท่าน และจะทรงส่งสายฟ้าฟาดลงที่สวนของท่าน แล้วมันจะกลายเป็นที่ดินโล่งเตียน');
INSERT INTO "king_fahad_thai" VALUES(18,41,'18:41','“หรือน้ําของมันกลายเป็นเหือดแห้งแล้วท่านไม่สามารถจะพบมันได้เลย”');
INSERT INTO "king_fahad_thai" VALUES(18,42,'18:42','และผลิตผลของเขาถูกทําลายหมด แล้วเขาก็ประกบฝ่ามือทั้งสองด้วยความเสียใจต่อสิ่งที่เขาก็ประกบฝ่ามือทั้งสองด้วยความเสียใจต่อสิ่งที่เขาได้จับจ่ายไป และมันพังพาบลงมา และเขากล่าวว่า “โอ้ ! หากฉันไม่เอาผู้ใดมาตั้งภาคีกับพระผู้เป็นเจ้าของฉัน”');
INSERT INTO "king_fahad_thai" VALUES(18,43,'18:43','และเขาไม่มีพรรคพวกจะช่วยเขาได้ นอกจากอัลลอฮ์ และเขาก็มิได้เป็นผู้ช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(18,44,'18:44','ด้วยเหตุนั้น การคุ้มครองช่วยเหลือเป็นของอัลลอฮ์ผู้ทรงสัจจะ และพระองค์ทรงดียิ่งในการตอบแทน และทรงดียิ่งในบั้นปลาย');
INSERT INTO "king_fahad_thai" VALUES(18,45,'18:45','และจงเปรียบอุทาหรณ์การดํารงชีวิตอยู่ในโลกนี้แก่พวกเขา ประหนึ่งน้ําที่เราหลั่งมันลงมาจากฟากฟ้า ดังนั้นพืชผลในแผ่นดินก็จะคลุกเคล้าไปกับน้ํา แล้วมันก็แห้งกรังเป็นเศษเป็นชิ้นซึ่งลมจะพัดมันให้ปลิวว่อน และอัลลอฮ์เป้นผู้ทรงอานุภาพเหนือทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(18,46,'18:46','ทรัพย์สมบัติและลูกหลานคือ เครื่องประดับแห่งการดํารงชีวิตในโลกนี้ และความดีทั้งหลายที่จีรังนั้น เป็นการตอบแทนที่ดียิ่ง ณ ที่พระเจ้าของเจ้า และเป็นความหวังที่ดียิ่ง');
INSERT INTO "king_fahad_thai" VALUES(18,47,'18:47','และ(จงรําลึก) วันที่เราให้เทือกเขาเคลื่อนย้ายไป และเจ้าจะเห็นแผ่นดินราบเรียบและเราจะชุมนุมพวกเขา ดังนั้น เราจะไม่ให้ผู้ใดออกไปจากพวกเขาเลย');
INSERT INTO "king_fahad_thai" VALUES(18,48,'18:48','และพวกเขาจะถูกนํามารวมเป็นแถวต่อหน้าพระผู้เป็นเจ้าของเจ้า โดยแน่นอน พวกเจ้าจะถูกนํามายังเราดั่งที่เราให้บังเกิดพวกเจ้าในครั้งแรก แต่พวกเจ้าอ้างว่าเราไม่ได้กําหนดเวลาสําหรับพวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(18,49,'18:49','และบันทึกจะถูกวางไว้ ดังนั้น เจ้าจะเห็นผู้กระทําผิดทั้งหลายหวั่นกลัวสิ่งที่มีอยู่ในบันทึกและพวกเขาจะกล่าวว่า “โอ้ความวิบัติของเราเอ๋ย! บันทึกอะไรกันนี่ มันมิได้ละเว้นสิ่งเล็กน้อยและสิ่งใหญ่โตเลย เว้นแต่ได้บันทึกไว้ครบถ้วน” และพวกเขาได้พบสิ่งที่พวกเขาได้ปฏิบัติไว้ปรากฏอยู่ต่อหน้าและพระผู้เป็นเจ้าของเจ้ามิทรงอธรรมต่อผู้ใดเลย');
INSERT INTO "king_fahad_thai" VALUES(18,50,'18:50','และเมื่อเราได้กล่าวแก่มะลาอิกะฮ์ว่า “จงสุญูดคารวะต่ออาดัม” พวกเขาก็แสดงคารวะเว้นแต่อิบลิส มันอยู่ในจําพวกญิน ดังนั้น มันจึงฝ่าฝืนคําสั่งของพระผู้เป็นเจ้าของมัน แล้วพวกเจ้าจะยึดเอามันและวงศ์วานของมัน เป็นผู้คุ้มครองอื่นจากข้ากระนั้นหรือหรือ? ทั้งๆ ที่พวกมันเป็นศัตรูกับพวกเจ้า มันช่างชั่วช้าแท้ๆ ในการแลกเปลี่ยนสําหรับพวกอธรรม”');
INSERT INTO "king_fahad_thai" VALUES(18,51,'18:51','ข้ามิได้เอาพวกมันมาเป็นพยาน ในการสร้างชั้นฟ้าทั้งหลายและแผ่นดิน แม้ในการสร้างตัวพวกมันเอง และข้ามิได้เอาพวกที่ทําให้ผู้อื่นหลงผิดมาให้ความช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(18,52,'18:52','และ(จงรําลึก) วันที่พระองค์ตรัสว่าพวกเจ้าจงเรียกคู่ภาคีของข้าที่พวกเจ้ากล่าวอ้างนั้น” แล้วพวกเขาก็ร้องขอให้พวกมันช่วยเหลือ แต่พวกมันจะไม่ตอบรับพวกเขา และเราได้กําหนดให้มีแหล่งพินาศระหว่างพวกมันเอง?”');
INSERT INTO "king_fahad_thai" VALUES(18,53,'18:53','และพวกกระทําผิดมองเห็นไฟนรกพวกเขาก็รู้ว่า แน่นอนพวกตนจะตกลงไปในนั้นและพวกเขาจะไม่พบทางรอดจากมันไปได้เลย');
INSERT INTO "king_fahad_thai" VALUES(18,54,'18:54','และเราได้ชี้แจงแก่มนุษย์ในอัลกรุอานนี้แต่ละตัวอย่าง แต่มนุษย์นั้นชอบโต้เถียงในเรื่องต่างๆ เป็นส่วนใหญ่');
INSERT INTO "king_fahad_thai" VALUES(18,55,'18:55','และไม่มีสิ่งใดที่จะยับยั้งมนุษย์จากการศรัทธา เมื่อแนวทางที่ถูกต้องได้มายังพวกเขา และการขออภัยโทษต่อพระเจ้าของพวกเขา เว้นแต่จะให้แบบอย่างแต่เก่าก่อน(การลงโทษ) มายังพวกเขาหรือจะให้การลงโทษมายังพวกเขาต่อหน้าต่อตา');
INSERT INTO "king_fahad_thai" VALUES(18,56,'18:56','และเรามิได้ส่งบรรดาร่อซูลมาเพื่ออื่นใดเว้นแต่เป็นผู้แจ้งข่าวดีและเป็นผู้ตักเตือน และบรรดาผู้ปฏิเสธศรัทธาจะโต้แย้งด้วยความเท็จ เพื่อทําลายล้างสัจธรรมด้วยมัน (ความเท็จ) และพวกเขายึดเอาโองการทั้งหลายของข้าและสิ่งที่ถูกตักเตือนเป็นการล้อเลียน');
INSERT INTO "king_fahad_thai" VALUES(18,57,'18:57','และผู้ใดจะอธรรมยิ่งไปกว่าผู้ที่ถูกตักเตือนให้รําลึก ด้วยโองการทั้งหลายของพระผู้เป็นเจ้าของเขา แล้วเขาก็หันหลังห่างออกไป แล้วลืมสิ่งที่มือทั้งสองของเขาประกอบไว้ แท้จริงเราได้ทําฝาปิดบนหัวใจของพวกเขา ในการที่พวกเขาจะเข้าใจมัน และในหูของพวกเขานั้นหนวก และถ้าเจ้าเรียกร้องพวกเขาไปสู่แนวทางที่ถูกต้อง พวกเขาจะก็ไม่อยู่ในแนวทางที่ถูกต้องนั้นเลย');
INSERT INTO "king_fahad_thai" VALUES(18,58,'18:58','และพระผุ้เป็นเจ้าของเจ้าคือผู้ทรงอภัยผู้ทรงเมตตาเสมอ หากพระองค์จะทรงเอาโทษพวกเขา ตามที่พวกเขาได้สะสมเอาไว้แน่นอนพระองค์จะทรงเร่งการลงโทษแก่พวกเขา แต่สําหรับพวกเขามีกําหนดเวลา ซึ่งพวกเขาจะไม่พบที่พึ่งอื่นใดนอกจากพระองค์');
INSERT INTO "king_fahad_thai" VALUES(18,59,'18:59','และเมืองเหล่านั้น เราได้ทําลายพวกเขาเมื่อพวกเขาอยุติธรรม และเราได้กําหนดกําหนดเวลาสําหรับความพินาศของพวกเขาไว้แล้ว');
INSERT INTO "king_fahad_thai" VALUES(18,60,'18:60','และจงรําลึกเมื่อมูซาได้กล่าวแก่คนใช้หนุ่ม (ยูะฮ์ อิบน์นูน) ของเขาว่า “ฉันจะยังคงเดินต่อไปจนกว่าจะบรรลุสู่ชุมทางแห่งสองทะเลหรือฉันจะคงเดินต่อไปอีกหลายปี”');
INSERT INTO "king_fahad_thai" VALUES(18,61,'18:61','ดังนั้น เมื่อทั้งสองถึงชุมทางระหว่างสองทะเลแล้ว ทั้งสองลืมปลาของเขา ดังนั้นมันจึงหาวิธีของมันลงทะเลไปตามทาง');
INSERT INTO "king_fahad_thai" VALUES(18,62,'18:62','ครั้นเมื่อทั้งสองเดินเลยต่อไปอีก เขาได้กล่าวแก่คนใช้หนุ่มของเขาว่า “จงนําอาหารกลางวันของเราออกมา โดยแน่นอน เราได้รับความลําบากจากการเดินทางของเรานี้”');
INSERT INTO "king_fahad_thai" VALUES(18,63,'18:63','เขากล่าวว่า “ท่านมิเห็นดอกหรือ เมื่อเราพักอยู่ที่ก้อนหิน แท้จริงฉันลืมที่จะพูดถึงเรื่องปลาและไม่มีผู้ใดที่ทําให้ฉันลืมกล่าวถึงมันนอกจากชัยฏอน และมันก็หาทางลงทะเลไปอย่างน่าประหลาดแท้ ๆ”');
INSERT INTO "king_fahad_thai" VALUES(18,64,'18:64','เขากล่าวว่า “นั่นแหละคือสิ่งที่เราต้องการหา” ดังนั้น ทั้งสองจึงหวลกลับตามร่องรอยไปที่เดิม”');
INSERT INTO "king_fahad_thai" VALUES(18,65,'18:65','แล้วทั้งสองได้พบบ่าวคนหนึ่งจากปวงบ่าวของเรา ที่เราได้ประทานความเมตตาจากเราให้แก่เขา และเราได้สอนความรู้จากเราให้แก่เขา');
INSERT INTO "king_fahad_thai" VALUES(18,66,'18:66','มูซาได้กล่าวแก่เขาว่า “จะให้ฉันติดตามท่านไปได้ไหม? โดยท่านจะต้องสอนฉันจากสิ่งที่ท่านได้เคยเรียนรู้มา ตามแนวทางที่เที่ยงตรง”');
INSERT INTO "king_fahad_thai" VALUES(18,67,'18:67','เขากล่าวว่า”แท้จริง ท่านจะไม่สามารถมีความอดทนร่วมกันฉันได้”');
INSERT INTO "king_fahad_thai" VALUES(18,68,'18:68','“และท่านจะอดทนอย่างได้อย่างไร ในสิ่งที่ท่านไม่มีความรู้อย่างละเอียดลออ?”');
INSERT INTO "king_fahad_thai" VALUES(18,69,'18:69','เขากล่าวว่า “หากอัลลอฮ์ทรงประสงค์ท่านจะพบฉันเป็นผู้อดทน และฉันจะไม่ฝ่าฝืนคําสั่งของท่าน”');
INSERT INTO "king_fahad_thai" VALUES(18,70,'18:70','เขากล่าวว่า “ดังนั้น ถ้าท่านติดตามฉันก็อย่าได้ถามฉันถึงสิ่งใด จนกว่าฉันจะเล่าเรื่องนั้นแก่ท่าน”');
INSERT INTO "king_fahad_thai" VALUES(18,71,'18:71','ดังนั้นทั้งสองจึงออกเดินทาง จนกระทั่งเมื่อทั้งสองลงเรือเขา(เคาะฏิร) จึงเจาะรูมัน เขา(มูซา) กล่าวว่า “ท่านเจาะรูมันเพื่อให้ผู้ที่อยู่ในเรือจมน้ํากรนั้นหรือ? โดยแน่นอนท่านได้นํามาซึ่งสิ่งที่อันตรายยิ่ง”');
INSERT INTO "king_fahad_thai" VALUES(18,72,'18:72','เขากล่าวว่า “ฉันมิได้บอกหรือว่า แท้จริงท่านจะไม่สามารถมีความอดทนร่วมกับฉันได้”');
INSERT INTO "king_fahad_thai" VALUES(18,73,'18:73','เขา(มูซา) กล่าวว่า “โปรดอย่าเอาโทษกับฉันเลยในสิ่งที่ฉันลืม และอย่าบังคับฉันให้ลําบากใจเรื่องของฉันเลย”');
INSERT INTO "king_fahad_thai" VALUES(18,74,'18:74','ดังนั้นเขาทั้งสองจึงออกเดินทางต่อไปจนกระทั่งเมื่อทั้งสองพบเด็กคนหนึ่ง เขา(เคาะฏิร)จึงฆ่าเด็กคนนั้น เขา(มูซา)กล่าวว่า “ท่านฆ่าชีวิตบริสุทธิ์โดยมิได้ทําผิดต่อชีวิตอื่นกระนั้นหรือ? โดยแน่นอน ท่านทําสิ่งที่ร้ายแรงยิ่ง”');
INSERT INTO "king_fahad_thai" VALUES(18,75,'18:75','เขากล่าวว่า “ฉันมิได้บอกหรือว่า แท้จริงท่านจะไม่สามารถมีความอดทนร่วมกับฉันได้”');
INSERT INTO "king_fahad_thai" VALUES(18,76,'18:76','เขา(มูซา) กล่าวว่า “หากฉันถามสิ่งใดจากท่านหลังจากนี้ท่านอย่าคบฉันเป็นเพื่อร่วมทางอีกเลย แน่นอน ท่านมีข้อแก้ตัวจากฉันพอแล้ว”');
INSERT INTO "king_fahad_thai" VALUES(18,77,'18:77','ดังนั้นทั้งสองจึงออกเดินทางต่อไป จนกระทั่งเมื่อทั้งสองพบชาวเมืองหนึ่ง ทั้งสองได้ขออาหารจากชาวเมืองนั้น แต่พวกเขาปฏิเสธที่จะต้อนรับเขาทั้งสองต่อมาเขาทั้งสองได้พบกําแพงแห่งหนึ่งกําลังจะพังลงมาแล้วเขาก็ทําให้มันตรงเขา(มูซา)กล่าวว่า “ถ้าท่านประสงค์ แน่นอนท่านจะเอาค่าแรงตอบแทนสําหรับมันได้”');
INSERT INTO "king_fahad_thai" VALUES(18,78,'18:78','เขากล่าว่า “นี่คือการแยกกันระหว่างฉันกับท่าน ฉันจะบอกท่านถึงความหมายที่ท่านไม่สามารถมีความอดทนในสิ่งนั้น ๆ ได้”');
INSERT INTO "king_fahad_thai" VALUES(18,79,'18:79','“ส่วนหนึ่งของเรือเดินทะเลนั้น มันเป็นของพวกผู้ขัดสนทํางานอยู่ในทะเล ฉันตั้งใจจะทําให้มันมีตําหนิ เพราะเบื้องหลังพวกเขานั้นมีกษัตริย์องค์หนึ่งคอยยึดเรือดี ๆ ทุกลําโดยใช้อํานาจ');
INSERT INTO "king_fahad_thai" VALUES(18,80,'18:80','“และส่วนเรื่องของเด็กนั้นก็คือ พ่อแม่ของเขาเป็นผู้ศรัทธา เรากลัวว่า เขาจะเคี่ยวเข็ญให้ทั้งสองตกอยู่ในการละเมิดและปฏิเสธศรัทธา”');
INSERT INTO "king_fahad_thai" VALUES(18,81,'18:81','“ดังนั้นเราปรารถนา(ฆ่าเขาโดยหวัง) ว่าพระผู้เป็นเจ้าของทั้งสองจะทรงเปลี่ยนลูกที่ดีกว่าให้แก่ทั้งสอง มัความบริสุทธิ์กว่าและใกล้ชิดต่อความเมตตา(แก่ทั้งสอง)');
INSERT INTO "king_fahad_thai" VALUES(18,82,'18:82','“และส่วนเรื่องของกําแพงนั้น มันเป็นของเด็กผู้ชายกําพร้าสองคนที่อยู่ในเมือง และใต้กําแพงนั้นมีขุมทรัพย์ของเขาทั้งสอง และพ่อของเด็กทั้งสองก็เป็นคนดี ดังนั้น พระผู้เป็นเจ้าจองท่านทรงประสงค์ที่จะให้เด็กทั้งสองบรรลุสู่ความเป็นผู้ใหญ่และจะให้เด็กทั้งสองเอาขุมทรัพย์ของทั้งสองออกมาเอง เป็นความเมตตาจากพระผู้เป็นเจ้าของท่าน และฉันมิได้ทําสิ่งนั้นตามความพอใจของฉัน นั่นคือความหมายที่ท่านไม่สามารถมีความอดทนในสิ่งนั้นๆ ได้”');
INSERT INTO "king_fahad_thai" VALUES(18,83,'18:83','และพวกเขาถามเจ้าเกี่ยวกับซุลก็อรนัยน์ จงกล่าวเถิด “ฉันจะเล่าเรื่องของเขาแก่พวกท่าน”');
INSERT INTO "king_fahad_thai" VALUES(18,84,'18:84','แท้จริงเราได้ให้อํานาจแก่เขาในแผ่นดิน และเราให้เขาทุกสิ่งที่เขาต้องการ');
INSERT INTO "king_fahad_thai" VALUES(18,85,'18:85','ดังนั้น เขาจึงมุ่งไปทางหนึ่ง (ทางทิศตะวันตก)');
INSERT INTO "king_fahad_thai" VALUES(18,86,'18:86','จนกระทั่งเมื่อเขาไปถึงดินแดนที่ดวงอาทิตย์ตก เขาพบลงในน้ําขุ่นดํา และพบ ณ ที่นั้นชนหมู่หนึ่ง เรากล่าวว่า(อัลลอฮ์ทรงดลใจเขา) “โอ้ ซุลก็อรนัยน์ เจ้าจะลงโทษพวกเขา หรือทําความดีต่อพวกเขา”');
INSERT INTO "king_fahad_thai" VALUES(18,87,'18:87','เขากล่าวว่า “ส่วนผู้ที่อธรรมนั้นเราจะลงโทษเขา แล้วเขาจะถูกกลับไปยังพระผู้เป็นเจ้าของเขา ดังนั้นพระองค์จะทรงลงโทษเขาซึ่งการลงโทษอย่างรุนแรง');
INSERT INTO "king_fahad_thai" VALUES(18,88,'18:88','“และส่วนผู้ศรัทธาและประกอบความดีนั้น สําหรับเขาคือการตอบแทนที่ดี และเราจะพูดกับเขาในกิจการงานของเราอย่างง่ายๆ “');
INSERT INTO "king_fahad_thai" VALUES(18,89,'18:89','แล้วเขาได้มุ่งไปอีกทางหนึ่ง(ทางตะวันออก)');
INSERT INTO "king_fahad_thai" VALUES(18,90,'18:90','จนกระทั่งเมื่อเขาไปถึงดินแดนที่ตะวันขึ้น เขาพบมันขึ้นเหนือกลุ่มชนหนึ่ง เรามิได้ทําที่กําบังแดดให้แก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(18,91,'18:91','เช่นนั้นแหละ เราหยั่งรู้ข่าวคราวที่เกี่ยวกับเขา');
INSERT INTO "king_fahad_thai" VALUES(18,92,'18:92','แล้วเขาได้มุ่งไปอีกทางหนึ่ง(ไปทางเหนือ)');
INSERT INTO "king_fahad_thai" VALUES(18,93,'18:93','จนกระทั่งเมื่อเขาไปถึงบริเวณหว่างภูผาทั้งสอง เขาได้พบชนกลุ่มหนึ่งที่เชิงภูผาทั้งสองนั้นซึ่งพวกเขาเกือบจะไม่เข้าใจคําพูดกันเลย');
INSERT INTO "king_fahad_thai" VALUES(18,94,'18:94','พวกเขากล่าวว่า “โอ้ซุลก็อรนัย แท้จริงยะอ์ญูจกและมะอ์ญูจนั้นเป็นผู้บ่อนทําลายในแผ่นดินนี้ ดังนั้น เราขอมอบบรรดาณาการแก่ท่าน เพื่อท่านจะได้สร้างกําแพงกั้นระหว่างพวกเรากับพวกเขา”');
INSERT INTO "king_fahad_thai" VALUES(18,95,'18:95','เขากล่าวว่า “สิ่งที่พระผู้เป็นเจ้าของฉันได้ให้อํานาจแก่ฉันดียิ่งกว่า ดังนั้นพวกท่านจงช่วยฉันด้วยกําลัง ฉันจะสร้างกําแพงแน่นหนากั้นระหว่างพวกท่านกับพวกเขา”');
INSERT INTO "king_fahad_thai" VALUES(18,96,'18:96','“พวกท่านจงเอาเหล็กท่อนโต ๆ มาให้ฉัน” จนกระทั่งเมื่อเขาทําให้บริเวณภุผาทั้งสองราบเรียบเขาก็กล่าวว่า “จงเป่ามันด้วยเครื่องเป่าลม” จนกกระทั่งเมื่อเขาทําให้มันร้อนเป็นไฟ เขากล่าวว่า “ปล่อยให้ฉันเททองแดงหลอมลงไปบนมัน”');
INSERT INTO "king_fahad_thai" VALUES(18,97,'18:97','ดังนั้น พวกเขา (ยะอ์ญูจและมะอ์ญูจ)ไม่สามารถจะข้ามมันได้ และไม่สามารถจะขุดโพรงผ่านมาได้');
INSERT INTO "king_fahad_thai" VALUES(18,98,'18:98','เขากล่าวว่า “นี่คือความเมตตาจากพระผู้เป็นเจ้าของฉัน ดังนั้น เมื่อสัญญาของพระผุ้เป็นเจ้าของฉันมาถึง พระองค์จะทรงทําให้มันพังทลาย และสัญญาของพระผู้เป็นเจ้าของฉันนั้นเป็นจริงเสมอ”');
INSERT INTO "king_fahad_thai" VALUES(18,99,'18:99','และวันนั้นเราได้ปล่อยให้บางส่วนของพวกเขาปะทะกับอีกบางส่วน และสังข์จะถูกเป่าขึ้น แล้วเราจะรวมพวกเขาทั้งหมด');
INSERT INTO "king_fahad_thai" VALUES(18,100,'18:100','และวันนั้นเราจะนํานรกญะฮันนัม มาเปิดเผยแก่พวกปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(18,101,'18:101','คือบรรดาผู้ที่ดวงตาของพวกเขาถูกปกปิดจากการรําลึกถึงข้า และพวกเขาไม่สามารถจะได้ยิน');
INSERT INTO "king_fahad_thai" VALUES(18,102,'18:102','บรรดาผู้ปฏิเสธศรัทธาได้คิดแล้วหรือว่าพวกเขาจะยึดเอาปวงบ่าวของข้าอื่นจากข้าเป็นผู้คุ้มครองได้ แท้จริงเราได้เตรียมนรกณะฮันนัมไว้เป็นที่พํานัก สําหรับบรรดาผู้ปฏิเสธศรัทธาแล้ว');
INSERT INTO "king_fahad_thai" VALUES(18,103,'18:103','จงกล่าวเถิดมุฮัมมัด “เราจะแจ้งแก่พวกท่านไหม ถึงบรรดาผู้ที่ขาดทุนยิ่งในการงาน?”');
INSERT INTO "king_fahad_thai" VALUES(18,104,'18:104','“คือบรรดาผู้ที่การขวนขวายของพวกเขาสูญสิ้นไป ในการมีชีวิตในโลกนี้ และพวกเขาคิดว่าแท้จริงพวกเขาปฏิบัติความดีแล้ว”');
INSERT INTO "king_fahad_thai" VALUES(18,105,'18:105','เขาเหล่านั้นคือบรรดาผู้ปฏิเสธศรัทธาต่อโองการทั้งหลายของพระผู้เป็นเจ้าของพวกเขา และการพบปะกับพระองค์ ดังนั้นการงานของพวกเขาจึงไร้ผล และในวันกิยามะฮ์เราจะไม่ให้มันมีค่าแก่พวกเขาเลย');
INSERT INTO "king_fahad_thai" VALUES(18,106,'18:106','นั่นแหละการตอบแทนของพวกเขาคือนรกญะฮันนัม เนื่องจากพวกเขาปฏิเสธศรัทธา และพวกเขายึดเอาโองการทั้งหลายของข้า และบรรดาร่อซูลของข้า เป็นที่ล้อเล่น');
INSERT INTO "king_fahad_thai" VALUES(18,107,'18:107','แท้จริง บรรดาผู้ศรัทธาและปฏิบัติความดีสําหรับพวกเขานั้นคือสวนสวรรค์ชั้นฟิรเดาส เป็นที่พํานัก');
INSERT INTO "king_fahad_thai" VALUES(18,108,'18:108','พวกเขาพํานักอย่างถาวรอยู่ในนั้น พวกเขาไม่ประสงค์จะเปลี่ยนที่จากมัน');
INSERT INTO "king_fahad_thai" VALUES(18,109,'18:109','จงกล่าวเถิดมุฮัมมัด “หากว่าทะเลเป็นน้ําหมึกสําหรับบันทึกพจนารถของพระผู้เป็นเจ้าของฉัน แน่นอน ทะเลจะเหือดแห้งก่อนที่คํากล่าวของพระผู้เป็นเจ้าของฉันหมดสิ้นไป และแม้ว่าเราจะนํามันเยี่ยงนั้นมาเป็นน้ําหมึกอีกก็ตาม');
INSERT INTO "king_fahad_thai" VALUES(18,110,'18:110','จงกล่าวเถิดมุฮัมมัด “แท้จริง ฉันเป็นเพียงสามัญชนคนหนึ่งเยี่ยงพวกท่าน มีวะฮีแก่ฉันว่าแท้จริง พระเจ้าของพวกท่านนั้นคือพระเจ้าองค์เดียว ดังนั้น ผู้ใดหวังที่จะพบพระผู้เป็นเจ้าของเขา ก็ให้เขาประกอบการงานที่ดี และอย่างตั้งผู้ใดเป็นภาคีในการเคารพภักดีต่อพระผู้เป็นเจ้าของเขาเลย”');
INSERT INTO "king_fahad_thai" VALUES(19,1,'19:1','กาฟ ฮา ยา อัยน์ ศอด');
INSERT INTO "king_fahad_thai" VALUES(19,2,'19:2','(นี่คือ) การกล่าวถึงเมตตาธรรมแห่งพระเจ้าของเจ้า ที่มีต่อซะกะรียาบ่าวของพระองค์');
INSERT INTO "king_fahad_thai" VALUES(19,3,'19:3','เมื่อเขาวิงวอนต่อพระเจ้าของเขา ด้วยการวิงวอนอย่างค่อย ๆ');
INSERT INTO "king_fahad_thai" VALUES(19,4,'19:4','เขากล่าวว่า “ข้าแต่พระเจ้าของข้าพระองค์ แท้จริงกระดูกของข้าพระองค์อ่อนแล้ว และศีรษะก็มีประกายหงอกแล้ว และมิเคยปรากฏเลยว่าการวิงวอนของข้าพระองค์ต่อพระองค์นั้นไร้ผล โอ้ข้าแต่พระเจ้าของข้าพระองค์');
INSERT INTO "king_fahad_thai" VALUES(19,5,'19:5','“และแท้จริงข้าพระองค์กลัวลูกหลานของข้าพระองค์ ภายหลัง (การตายของ) ข้าพระองค์ และภริยาของข้าพระองค์ก็เป็นหมันด้วย ดังนั้นขอพระองค์ทรงโปรดประทานทายาที่ดีจากพระองค์แก่ข้าพระองค์ด้วยเถิด”');
INSERT INTO "king_fahad_thai" VALUES(19,6,'19:6','“ผู้ซึ่งจะสืบทายาทแทนข้าพระองค์ และสืบทายาทจากตระกูลของยะอ์กูบ และขอพระองค์ทรงโปรดให้เขาเป็นที่โปรดปรานด้วยเถิด ข้าแต่พระเจ้าของข้าพระองค์”');
INSERT INTO "king_fahad_thai" VALUES(19,7,'19:7','โอ้ ซะกะรียาเอ๋ย ! แท้จริงเราจะแจ้งข่าวดีแก่เจ้าซึ่งลูกคนหนึ่ง ชื่อของเขาคือยะห์ยา เรามิเคยตั้งชื่อผู้ใดมาก่อนเลย');
INSERT INTO "king_fahad_thai" VALUES(19,8,'19:8','เขากล่าวว่า “ข้าแต่พระเจ้าของข้าพระองค์ ข้าพระองค์จะมีลูกได้อย่างไร ในเมื่อภริยาของข้าพระองค์ก็เป็นหมัน และข้าพระองค์ได้บรรลุสู่ความแก่ชราแล้ว !”');
INSERT INTO "king_fahad_thai" VALUES(19,9,'19:9','เขา (มลัก) กล่าวว่า “กระนั้นก็ดี พระเจ้าของเจ้าได้ตรัสว่า มันง่ายสําหรับข้า และแน่นอนข้าได้บังเกิดเจ้ามาก่อน เมื่อเจ้ายังมิได้เป็นสิ่งใด”');
INSERT INTO "king_fahad_thai" VALUES(19,10,'19:10','เขากล่าวว่า “ข้าแต่พระเจ้าของข้าพระองค์ ขอพระองค์ทรงโปรดทําให้มีสัญญาณ แก่ข้าพระองค์ด้วย” พระองค์ตรัสว่าสัญญาณของเจ้าคืออย่าพูดกับผู้คนเป็นเวลาสามคืน ทั้ง ๆ ที่เจ้าอยู่ในสภาพที่สมบูณ์”');
INSERT INTO "king_fahad_thai" VALUES(19,11,'19:11','แล้วเขาได้ออกจากแท่นสวดมายังหมู่ชนของเขา และเขาได้ชี้ใบ้แก่พวกของเขาว่า พวกท่านจงกล่าวสดุดีในยามเช้าและยามเย็น');
INSERT INTO "king_fahad_thai" VALUES(19,12,'19:12','โอ้ ยะห์ยาเอ๋ย ! เจ้าจงยึดมั่นในคัมภีร์ (เตารอฮ์) อย่างมั่นคง และเราได้ประทานความเฉลียวฉลาดให้เขา ตั้งแต่ยังเป็นเด็กอยู่');
INSERT INTO "king_fahad_thai" VALUES(19,13,'19:13','และความน่าสงสารจากเรา และความบริสุทธิ์แก่เขา และเขาเป็นผู้ยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(19,14,'19:14','และเป็นผู้กระทําความดีต่อบิดามารดาของเขา และเขามิได้เป็นผู้หยิ่งยะโส ผู้ฝ่าฝืน');
INSERT INTO "king_fahad_thai" VALUES(19,15,'19:15','และความศานติจงมีแด่เขา วันที่เขาถูกคลอด และวันที่เขาตาย และวันที่เขาถูกฟื้นขึ้นให้มีชีวิตใหม่');
INSERT INTO "king_fahad_thai" VALUES(19,16,'19:16','และจงกล่าวถึง (เรื่องของ) มัรยัมที่อยู่ในคัมภีร์ เมื่อนางได้ปลีกตัวออกจากหมู่ญาติของนาง ไปยังมุมหนึ่งทางตะวันออก(ของบัยตุลมักดิส)');
INSERT INTO "king_fahad_thai" VALUES(19,17,'19:17','แล้วนางได้ใช้ม่านกั้นให้ห่างพ้นจากพวกเขาแล้วเราได้ส่งวิญญาณของเรา (ญิบรีล) ไปยังนางแล้วเขาได้จําแลงตนแก่นาง ให้เป็นชายอย่างสมบูรณ์');
INSERT INTO "king_fahad_thai" VALUES(19,18,'19:18','นางกล่าวว่า “แท้จริงฉันขอความคุ้มครองต่อพระผู้ทรงกรุณาปรานีให้พ้นจากท่าน หากท่านเป็นผู้ยําเกรง”');
INSERT INTO "king_fahad_thai" VALUES(19,19,'19:19','เขา (ญิบรีล) กล่าวว่า “แท้จริงฉันเป็นเพียงฑูตแห่งพระเจ้าของเธอ เพื่อฉันจะให้ลูกชายผู้บริสุทธิ์แก่เธอ”');
INSERT INTO "king_fahad_thai" VALUES(19,20,'19:20','นางกล่าวว่า “ฉันจะมีลูกได้อย่างไร ทั้ง ๆ ที่ไม่มีชายใดมาแตะต้องฉันเลย และฉันก็มิได้เป็นหญิงชั่ว”');
INSERT INTO "king_fahad_thai" VALUES(19,21,'19:21','เขา (ญิบรีล) กล่าวว่า “กระนั้นก็เถิด พระเจ้าของเธอตรัสว่า มันง่ายสําหรับข้า และเพื่อเราจะทําให้เขาเป็นสัญญาณหนึ่งสําหรับมนุษย์และเป็นความเมตตาจากเรา และนั่นเป็นกิจการที่ถูกกําหนดไว้แล้ว');
INSERT INTO "king_fahad_thai" VALUES(19,22,'19:22','แล้วนางได้ตั้งครรภ์ และนางได้ปลีกตัวออกไปพร้อมกับบุตรในครรภ์ ยังสถานที่ไกลแห่งหนึ่ง');
INSERT INTO "king_fahad_thai" VALUES(19,23,'19:23','ความเจ็บปวดใกล้คลอดทําให้นางหลบไปที่โคนตัวต้นอินทผาลัม นางได้กล่าวว่า “โอ้ ! หากฉันได้ตายไปเสียก่อนหน้านี้ และฉันเป็นคนไร้ค่าถูกลืมเสียก็จะดี”');
INSERT INTO "king_fahad_thai" VALUES(19,24,'19:24','ดังนั้น เขา (มะลัก) ได้เรียกนางทางเบื้องล่างต้นอินทผลัมว่า “อย่าได้เศร้าเสียใจ แน่นอน พระเจ้าของเธอทรงจัดลําธารไว้เบื้องล่างเธอแล้ว');
INSERT INTO "king_fahad_thai" VALUES(19,25,'19:25','“และจงเขย่าต้นอินทผลัม ให้มันเอนมาทางตัวเธอ มันจะหล่นลงมาที่ตัวเธอเป็นอินทผลัมที่สุกน่ากิน”');
INSERT INTO "king_fahad_thai" VALUES(19,26,'19:26','“ฉะนั้น จงกิน จงดื่ม และจงทําจิตใจให้เบิกบานเถิด หากเธอเห็นมนุษย์คนใดก็จงกล่าวว่า ฉันได้บนการสงบนิ่งไว้ต่อพระผู้ทรงกรุณาปรานีฉันจะไม่พูดกับผู้ใดเลยวันนี้”');
INSERT INTO "king_fahad_thai" VALUES(19,27,'19:27','แล้วนางใดพาเขามายังหมู่ญาติของนางโดยอุ้มเขามา พวกเขากล่าวว่า “โอ้ มัรยัมเอ๋ย ! แท้จริงเธอได้นําเรื่องประหลาดมาแล้ว”');
INSERT INTO "king_fahad_thai" VALUES(19,28,'19:28','“โอ้ น้องหญิงของฮารูน พ่อของเธอมิได้เป็นชายชั่ว และแม่ของเธอก็มิได้เป็นหญิงไม่บริสุทธิ์”');
INSERT INTO "king_fahad_thai" VALUES(19,29,'19:29','นางชี้ไปทางเขา พวกเขากล่าวว่า “เราจะพูดกับผู้ที่อยู่ในเปลที่เป็นเด็กได้อย่างไร?”');
INSERT INTO "king_fahad_thai" VALUES(19,30,'19:30','เขา (อีซา) กล่าวว่า “แท้จริงฉันเป็นบ่าวของอัลลอฮ์ พระองค์ทรงประทานคัมภีร์แก่ฉันและทรงให้ฉันเป็นนะบี”');
INSERT INTO "king_fahad_thai" VALUES(19,31,'19:31','“และพระองค์ทรงให้ฉันได้รับความจําเริญ ไม่ว่าฉันจะอยู่ ณ ที่ใด และทรงสั่งเสียให้ฉันทําการละหมาดและจ่ายซะกาตตราบที่ฉันมีชีวิตอยู่”');
INSERT INTO "king_fahad_thai" VALUES(19,32,'19:32','“และทรงให้ฉันทําดีต่อมารดาของฉันและจะไม่ทรงทําให้ฉันเป็นผู้หยิ่งยะโส ผู้เลวทรามต่ําช้า”');
INSERT INTO "king_fahad_thai" VALUES(19,33,'19:33','“และความศานติจงมีแด่ฉัน วันที่ฉันถูกคลอด และวันที่ฉันตาย และวันที่ฉันถูกฟื้นขึ้นให้มีชีวิตใหม่”');
INSERT INTO "king_fahad_thai" VALUES(19,34,'19:34','นั่นคืออีซาบุตรของมัรยัม มันเป็นคําบอกเล่าที่จริง ซึ่งพวกเขายังมีความสงสัยกันอยู่');
INSERT INTO "king_fahad_thai" VALUES(19,35,'19:35','ไม่เป็นการบังควรสําหรับอัลลอฮ์ ที่พระองค์จะทรงตั้งผู้ใดเป็นพระบุตร มหาบริสุทธิ์แห่งพระองค์ท่าน ! เมื่อพระองค์ทรงกําหนดกิจการใด พระองค์จะตรัสแก่มันว่า “จงเป็น “ แล้วมันก็จะเป็นขึ้นมา');
INSERT INTO "king_fahad_thai" VALUES(19,36,'19:36','และแท้จริงอัลลอฮ์คือพระเจ้าของฉันและพระเจ้าของพวกท่าน ดังนั้น พวกท่านจงเคารพภักดีพระองค์เถิด นี่คือทางอันเที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(19,37,'19:37','คณะต่าง ๆ ได้ขัดแย้งระหว่างกันเองดังนั้น ความหายนะจงประสบแก่บรรดาผู้ปฏิเสธศรัทธา เมื่อมีการชุมนุมแห่งวันอันยิ่งใหญ่เถิด');
INSERT INTO "king_fahad_thai" VALUES(19,38,'19:38','พวกเขาจะได้ฟังอย่างชัดแจ้งและเห็นอย่างชัดอะไรอย่างนั้น ! วันที่พวกเขาจะมาหาเรา แต่วันนี้บรรดาผู้อธรรมอยู่ในการหลงผิดที่ชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(19,39,'19:39','และเจ้าจงเตือนสําทับพวกเขาถึงวันแห่งความเสียใจ เมื่อกิจการนั้นถูกตัดสิน และพวกเขาอยู่ในหลงลืม และพวกเขาไม่ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(19,40,'19:40','แท้จริง เราเป็นผู้ครอบครองมรดกแผ่นดินและที่อยู่บนแผ่นดิน และพวกเขาจะถูกนํากลับมายังเรา');
INSERT INTO "king_fahad_thai" VALUES(19,41,'19:41','และจงกล่าวถึง (เรื่องของ) อิบรอฮีมที่อยู่ในคัมภีร์ แท้จริงเขาเป็นผู้ซื่อสัตย์ เป็นนะบี');
INSERT INTO "king_fahad_thai" VALUES(19,42,'19:42','และจงรําลึกถึงเมื่อเขากล่าวแก่บิดาของเขาว่า “โอ้พ่อจ๋า ทําไมท่านจึงเคารพบูชาสิ่งที่ไม่ได้ยินและไม่เห็น และไม่ให้ประโยชน์อันใดแก่ท่านเลย ?');
INSERT INTO "king_fahad_thai" VALUES(19,43,'19:43','“โอ้พ่อจ๋า แท้จริงความรู้ได้มีมายังฉันแล้ว ซึ่งมิได้มีมายังท่าน ดังนั้น จงเชื่อฟังปฏิบัติตามฉันเถิด ฉันจะชี้แนะท่านสู่ทางที่ราบรื่น ”');
INSERT INTO "king_fahad_thai" VALUES(19,44,'19:44','“โอ้พ่อจ๋า ! อย่าเคารพบูชาชัยฏอนเป็นอันขาด แท้จริงชัยฏอนนั้นมันดื้อรั้นต่อพระผู้ทรงกรุณาปรานี');
INSERT INTO "king_fahad_thai" VALUES(19,45,'19:45','“โอ้พ่อจ๋า ! แท้จริง ฉันกลัวว่าการลงโทษจากพระผู้ทรงกรุณาปรานีจะประสบแก่ท่านแล้วท่านก็จะเป็นสหายของชัยฎอน”');
INSERT INTO "king_fahad_thai" VALUES(19,46,'19:46','เขา (บิดา) กล่าวว่า “เจ้ารังเกียจพระเจ้าทั้งหลายของฉันกระนั้นหรือ โอ้อิบรอฮีมเอ๋ย ! หากเจ้าไม่หยุดยั้ง (จากการตําหนิ) แน่นอนฉันจะขว้างเจ้า (ด้วยก้อนหิน) และเจ้าจงไปให้พ้นจากฉันตลอดไป”');
INSERT INTO "king_fahad_thai" VALUES(19,47,'19:47','เขา (อิบรอฮีม) กล่าวว่า “ขอความศานติจงมีแด่ท่าน ฉันจะขออภัยโทษจากพระเจ้าของฉันให้แก่ท่าน แท้จริงพระองค์เป็นผู้ทรงเมตตากรุณาแก่ฉันมาก');
INSERT INTO "king_fahad_thai" VALUES(19,48,'19:48','“และฉันจะปลีกตัวออกจากพวกท่านและสิ่งที่พวกท่านวิงวอนขออื่นจากอัลลอฮ์ และฉันจะวิงวอนขอพระเจ้าของฉัน หวังว่าด้วยการวิวอนขอต่อพระเจ้าของฉัน จะไม่ทําให้ฉันได้รับความทุกข์”');
INSERT INTO "king_fahad_thai" VALUES(19,49,'19:49','ครั้นเมื่อเขาปลีกตัวออกไปจากพวกเขาและสิ่งที่พวกเขาเคารพบุชาอื่นจากอัลลอฮ์แล้ว เราได้ให้แก่เขา อิสฮาก และยะอ์กูบ และแต่ละคนเราได้แต่งตั้งให้เป็นนะบี');
INSERT INTO "king_fahad_thai" VALUES(19,50,'19:50','และเราได้ให้ความเมตตาของเราแก่พวกเขา และเราได้ทําให้พวกเขาได้รับการกล่าวขวัญที่ดี (ในหมู่มวลมนุษย์)');
INSERT INTO "king_fahad_thai" VALUES(19,51,'19:51','และจงกล่าวถึงเรื่องมูซาที่อยู่ในคัมภีร์ แท้จริงเขาเป็นผู้ได้รับคัดเลือก และเขาเป็นร่อซูลเป็นนะบี');
INSERT INTO "king_fahad_thai" VALUES(19,52,'19:52','และเราได้ร้องเรียกเขาจากทางด้านขวาของภูเขาฎูร และเราได้ให้เขาเข้ามาใกล้ชิดเพื่อบอกความลับ');
INSERT INTO "king_fahad_thai" VALUES(19,53,'19:53','และเราได้ให้ความเมตตาของเราแก่เขาของภูเขาฎูร และเราได้ให้เขาเข้ามาใกล้ชิดเพื่อบอกความลับ');
INSERT INTO "king_fahad_thai" VALUES(19,54,'19:54','และจงกล่าวถึงเรื่องของอิสมาอีลที่อยู่ในคัมภีร์ แท้จริงเขาเป็นผู้ซื่อสัตย์ต่อสัญญา และเขาเป็นร่อซูลเป็นนะบี');
INSERT INTO "king_fahad_thai" VALUES(19,55,'19:55','และเขาใช้หมู่ญาติของเขาให้ปฏิบัติละหมาดและจ่ายซะกาต และเขาเป็นที่โปรดปราน ณ ที่พระเจ้าของเขา');
INSERT INTO "king_fahad_thai" VALUES(19,56,'19:56','และจงกล่าวถึงเรื่องของอิดรีสที่อยู่ในคัมภีร์ แท้จริงเขาเป็นผู้ซื่อสัตย์ เป็นนะบี');
INSERT INTO "king_fahad_thai" VALUES(19,57,'19:57','และเราได้เทิดเกียรติเขาซึ่งตําแหน่งอันสูงส่ง');
INSERT INTO "king_fahad_thai" VALUES(19,58,'19:58','ชนเหล่านั้นคือบรรดาผู้ที่อัลลอฮ์ทรงโปรดปรานพวกเขาให้เป็นนะบี ที่มีเชื้อสายจากอาดัม และจากเชื้อสายผู้ที่เราบรรทุกไว้ในเรือกับนูห์ และจากเชื้อสายของอิบรอฮีม และอิสรออีล และจากเชื้อสายผู้ที่เราได้ชี้แนะทางและเราได้คัดเลือกไว้ เมื่อบรรดาโองการของพระผู้ทรงกรุณาปรานีถูกอ่านแก่พวกเขา พวกเขาจะก้มลงสุญูดและร้องให้');
INSERT INTO "king_fahad_thai" VALUES(19,59,'19:59','ภายหลังจากพวกเขา ชนรุ่นชั่วก็ได้สืบต่อมา พวกเขาได้ทิ้งละหมาด และปฏิบัติตามความใคร่ ต่อมาพวกเขาก็จะประสบความหายนะ');
INSERT INTO "king_fahad_thai" VALUES(19,60,'19:60','เว้นแต่ผู้ขอลุแก่โทษและศรัทธา และกระทําความดี ชนเหล่านั้นจะได้เข้าสวนสวรรค์และพวกเขาจะไม่ได้รับความอธรรมแต่อย่างใด');
INSERT INTO "king_fahad_thai" VALUES(19,61,'19:61','สวนสวรรค์หลากหลายอันสถาพร ซึ่งพระผู้ทรงกรุณาปรานีทรงสัญญาแก่ปวงบ่าวของพระองค์ด้วยความเร้นลับ แท้จริงสัญญาของพระองค์นั้นจะมีมาอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(19,62,'19:62','พวกเขาจะไม่ได้ยินสิ่งไร้สาระในนั้น นอกจากคําทักทายที่เป็นศานติ และสําหรับพวกเขาจะได้รับเครื่องยังชีพของพวกเขาในนั้น ทั้งในยามเช้าและยามเย็น');
INSERT INTO "king_fahad_thai" VALUES(19,63,'19:63','นั่นคือสวนสวรรค์ซึ่งเราให้เป็นมรดกแก่ปวงบ่าวของเรา ผู้ที่มีความยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(19,64,'19:64','และเรา (ญิบรีล) มิได้ลงมา เว้นแต่ด้วยพระบัญชาของพระเจ้าของท่าน สําหรับพระองค์นั้น สิ่งที่อยู่ระหว่างเบื้องหน้าของเราและสิ่งที่อยู่เบื้องหลังของเรา และสิ่งที่อยู่ระหว่างทั้งสองและพระเจ้าของท่านนั้นมิทรงหลงลืมสิ่งใดเลย');
INSERT INTO "king_fahad_thai" VALUES(19,65,'19:65','พระเจ้าแห่งบรรดาชั้นฟ้าและแผ่นดินและสิ่งที่อยู่ระหว่างทั้งสอง ดังนั้นจงเคารพภักดีต่อพระองค์ และจงอดทนต่อการเคารพภักดีพระองค์สูเจ้ารู้หรือว่ามีผู้ใดเสมอเหมือนพระองค์ ?');
INSERT INTO "king_fahad_thai" VALUES(19,66,'19:66','และมนุษย์ (กาฟิร) กล่าวว่า “เมื่อฉันตายไปแล้ว ฉันจะถูกให้ออกมา ในสภาพมีชีวิตจริงหรือ ?”');
INSERT INTO "king_fahad_thai" VALUES(19,67,'19:67','มนุษย์ไม่คิดบ้างหรือว่า แท้จริงเราได้บังเกิดเขามาแต่กาลก่อน โดยที่เขามิได้เป็นสิ่งใดมาก่อนเลย');
INSERT INTO "king_fahad_thai" VALUES(19,68,'19:68','ดังนั้นด้วยพระนามของพระเจ้าของเจ้าแน่นอนเราจะชุมนุมพวกเขาพร้อมด้วยบรรดาชัยฏอนแล้วเราจะนําพวกเขาให้มาคุกเข่าอยู่รอบ ๆ นรก');
INSERT INTO "king_fahad_thai" VALUES(19,69,'19:69','แล้วแน่นอนที่สุดเราจะดึงออกจากทุก ๆ คณะ ใครในหมู่พวกเขาที่ดื้อรั้นที่สุดต่อพระผู้ทรงกรุณาปรานี');
INSERT INTO "king_fahad_thai" VALUES(19,70,'19:70','แล้วแน่นอนที่สุด เรารู้ดียิ่งถึงบรรดาผู้ที่เหมาะสมยิ่งที่จะเข้าไปอยู่ในนรก');
INSERT INTO "king_fahad_thai" VALUES(19,71,'19:71','และไม่มีผู้ใดในหมู่พวกเจ้า นอกจากจะเป็นผู้ผ่านเข้าไปในมัน มันเป็นสิ่งที่กําหนดไว้แน่นอนแล้วสําหรับพระเจ้าของเจ้า');
INSERT INTO "king_fahad_thai" VALUES(19,72,'19:72','แล้วเราจะให้บรรดาผู้ยําเกรงรอดพ้นแล้วเราจะปล่อยให้บรรดาผู้อธรรมคุกเข่าอยู่ในนั้น');
INSERT INTO "king_fahad_thai" VALUES(19,73,'19:73','และเมื่อโองการทั้งกลายอันแจ่มแจ้งของเรา ถูกอ่นขึ้นแก่พวกเขา บรรดาผู้ปฏิเสธศรัทธากล่าวแก่บรรดาผู้ศรัทธาว่า “ฝ่ายใดในสองฝ่ายนี้จะมีฐานะดีกว่า และมีเกียรติทางสังคมมากกว่า ?”');
INSERT INTO "king_fahad_thai" VALUES(19,74,'19:74','และกี่มากน้อยแล้วประชาชาติก่อนพวกเขาเราได้ทําลายพวกเขา โดยที่พวกเขามีสิ่งของ เครื่องใช้และรูปร่างลักษณะดีกว่า');
INSERT INTO "king_fahad_thai" VALUES(19,75,'19:75','จงกล่าวเถิด ผู้ที่อยู่ในความหลงผิดนั้น พระผู้ทรงกรุณาปรานีจะทรงผ่อนผันให้เขาระยะหนึ่ง จนกระทั่งเมื่อพวกเขาได้เห็นสิ่งที่พวกเขาถูกสัญญาไว้ว่า จะเป็นการลงโทษในโลกนี้หรือจะเป็นการลงโทษในปรโลก แล้วพวกเขาก็จะรู้ว่าใครจะมีฐานะชั่วร้ายกว่าและมีกําลังพลน้อยกว่า');
INSERT INTO "king_fahad_thai" VALUES(19,76,'19:76','และอัลลอฮ์จะทรงเพิ่มแนวทางที่ถูกต้องให้แก่ผู้ที่อยู่ในแนวทางนั้น และการงานที่ดีที่ยั่งยืนนั้นดียิ่ง ณ ที่พระเจ้าของเจ้า ในการตอบแทนรางวัล และดียิ่งในการกลับ (ไปสู่พระองค์)');
INSERT INTO "king_fahad_thai" VALUES(19,77,'19:77','เจ้าเห็นหรือไม่ว่า ผู้ที่ปฏิเสธโองการทั้งหลายของเรา แล้วเขากล่าวอ้างว่า ฉันจะได้รับทรัพย์สมบัติและลูกหลานนั้น');
INSERT INTO "king_fahad_thai" VALUES(19,78,'19:78','เขาล่วงรู้ในสิ่งเร้นลับหรือว่าเขาได้รับคํามั่นสัญญา จากพระผู้ทรงกรุณาปรานี ?');
INSERT INTO "king_fahad_thai" VALUES(19,79,'19:79','เปล่าเลย ! เราจะบันทึกสิ่งที่เขากล่าวและเราจะเพิ่มการลงโทษแก่เขาอีกระยะหนึ่ง');
INSERT INTO "king_fahad_thai" VALUES(19,80,'19:80','และเราจะรับช่วงจากเขาสิ่งที่เขากล่าวไว้ และเขาจะมาหาเราอย่างโดดเดี่ยว');
INSERT INTO "king_fahad_thai" VALUES(19,81,'19:81','และพวกเขาได้ยึดเอารูปปั้นต่าง ๆ เป็นพระเจ้าอื่นจากอัลลอฮ์ เพื่อที่จะเป็นพลังอํานาจแก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(19,82,'19:82','เปล่าเลย ! รูปปั้นเหล่านั้นจะปฏิเสธการเคารพบูชาของพวกเขา และพวกมันจะเป็นปฏิปักษ์ต่อพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(19,83,'19:83','เจ้ามิเห็นดอกหรือว่า แท้จริงเราได้ปล่อยให้ชัยฏอนมีอํานาจเหนือพวกที่ปฏิเสธศรัทธาเพื่อมันจะได้ยุแหย่พวกเขาอย่างจริงจัง');
INSERT INTO "king_fahad_thai" VALUES(19,84,'19:84','ดังนั้น เจ้าอย่าได้เร่งร้อนต่อพวกเขา แท้จริงเราได้นับวันที่เหลือสําหรับพวกเขาไว้แล้วด้วยการนับที่แน่นอน');
INSERT INTO "king_fahad_thai" VALUES(19,85,'19:85','วันที่เราจะรวมบรรดาผู้ยําเกรง ให้มาชุมนุมต่อหน้าพระผู้ทรงกรุณาปรานีเป็นกลุ่ม ๆ');
INSERT INTO "king_fahad_thai" VALUES(19,86,'19:86','และเราจะไล่ต้อนบรรดาผู้มีความผิดไปยังนรก อย่างสัตว์ที่กระหายน้ําเป็นฝูง ๆ');
INSERT INTO "king_fahad_thai" VALUES(19,87,'19:87','พวกเขาไม่มีอํานาจในการชะฟาอะฮ์ นอกจากผู้ที่ได้ทําสัญญาไว้กับพระผู้ทรงกรุณาปรานี');
INSERT INTO "king_fahad_thai" VALUES(19,88,'19:88','และพวกเขากล่าวว่า “พระผู้ทรงกรุณาปรานีทรงตั้งพระบุตรขึ้นเพื่อพระองค์”');
INSERT INTO "king_fahad_thai" VALUES(19,89,'19:89','แน่นอนที่สุด พวกเจ้าได้นํามาซึ่งสิ่งร้ายแรงยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(19,90,'19:90','ชั้นฟ้าทั้งหลายแทบจะพังทลายลงมาและแผ่นดินก็แทบจะถล่มลึกลงไป และขุนเขาทั้งหลายก็แทบจะยุบทลายลงมาเป็นเสี่ยง ๆ');
INSERT INTO "king_fahad_thai" VALUES(19,91,'19:91','ที่พวกเขาอ้างพระบุตรแก่พระผู้ทรงกรุณาปรานี');
INSERT INTO "king_fahad_thai" VALUES(19,92,'19:92','และไม่เป็นการบังควรแก่พระผู้ทรงกรุณาปรานี ที่พระองค์จะทรงตั้งพระบุตรขึ้น');
INSERT INTO "king_fahad_thai" VALUES(19,93,'19:93','ไม่มีผู้ใดในชั้นฟ้าทั้งหลายและแผ่นดินเว้นแต่เขาจะมายังพระผู้ทรงกรุณาปรานีในฐานะบ่าวคนหนึ่ง');
INSERT INTO "king_fahad_thai" VALUES(19,94,'19:94','แน่นอนที่สุด พระองค์ทรงรอบรู้ถึงพวกเขาและทรงนับพวกเขาอย่างถี่ถ้วนไว้แล้ว');
INSERT INTO "king_fahad_thai" VALUES(19,95,'19:95','และทุกคนในพวกเขาจะมายังพระองค์ในวันกิยามะฮ์อย่างโดดเดี่ยว');
INSERT INTO "king_fahad_thai" VALUES(19,96,'19:96','แท้จริง บรรดาผู้ศรัทธาและประกอบคุณงามความดีทั้งหลายนั้น พระผู้ทรงกรุณาปรานีจะทรงโปรดปรานความรักใคร่แก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(19,97,'19:97','แท้จริง เราได้ทําให้อัลกุรอานเป็นภาษาที่ง่ายแก่เจ้า เพื่อว่าเจ้าจะได้นํามันไปแจ้งเป็นข่าวดีแก่บรรดาผู้ยําเกรง และเจ้าจะได้นํามันไปตักเตือนหมู่ชนที่ดื้อรั้น');
INSERT INTO "king_fahad_thai" VALUES(19,98,'19:98','และกี่มากน้อยแล้วจากประชาชาติในอดีต เราได้ทําลายพวกเขา เจ้าได้เห็นผู้ใดในหมู่พวกเขาหรือได้ยินเสียงกระซิบของพวกเขาบ้างไหม ?');
INSERT INTO "king_fahad_thai" VALUES(20,1,'20:1','ฏอฮา');
INSERT INTO "king_fahad_thai" VALUES(20,2,'20:2','เรามิได้ให้อัลกุรอานลงมาแก่เจ้า เพื่อให้เจ้าลําบาก');
INSERT INTO "king_fahad_thai" VALUES(20,3,'20:3','เว้นแต่เป็นการตักเตือนแก่ผู้ที่ยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(20,4,'20:4','เป็นการประทานลงจากพระผู้สร้างแผ่นดินและชั้นฟ้าทั้งหลายอันสูงส่ง');
INSERT INTO "king_fahad_thai" VALUES(20,5,'20:5','ผู้ทรงกรุณาปรานี ทรงสถิตย์อยู่บนบัลลังก์');
INSERT INTO "king_fahad_thai" VALUES(20,6,'20:6','กรรมสิทธิ์ของพระองค์นั้นคือ สิ่งที่อยู่ในชั้นฟ้าทั้งหลายและสิ่งที่อยู่ในแผ่นดิน และสิ่งที่อยู่ในระหว่างทั้งสอง และสิ่งที่อยู่ใต้พื้นดิน');
INSERT INTO "king_fahad_thai" VALUES(20,7,'20:7','และหากว่าเจ้ากล่าวเสียงดัง เพราะแท้จริงอัลลอฮฺทรงรอบรู้สิ่งเร้นลับ และสิ่งซ่อนเร้น');
INSERT INTO "king_fahad_thai" VALUES(20,8,'20:8','อัลลอฮฺ ไม่มีพระเจ้าอื่นใดนอกจากพระองค์สําหรับพระองค์นั้นทรงพระนามอันสวยงาม');
INSERT INTO "king_fahad_thai" VALUES(20,9,'20:9','และเรื่องราวของมูซาได้มีมาถึงเจ้าบ้างไหม ?');
INSERT INTO "king_fahad_thai" VALUES(20,10,'20:10','เมื่อเขาเห็นไฟ เขาจึงกลัวแก่ครอบครัวของเขาว่า “พวกท่านจงหยุดอยู่ที่นี่ เพราะฉันเห็นไฟ บางทีฉันจะนําคบเพลิงจากที่นั่นมาให้พวกท่าน หรือฉันอาจจะพบผู้นําทางที่กองไฟนั้น');
INSERT INTO "king_fahad_thai" VALUES(20,11,'20:11','ครั้นเมื่อเขามาถึงกองไฟนั้น มีเสียงเรียกขึ้นว่า “โอ้ มูซาเอ๋ย !');
INSERT INTO "king_fahad_thai" VALUES(20,12,'20:12','“แท้จริงข้าคือพระเจ้าของเจ้า จงถอดรองเท้าทั้งสองข้างของเจ้าออก แท้จริงเจ้ากําลังอยู่ ณ หุบเขาอันศักดิ์สิทธิ์ (มีชื่อว่า) ฏุว่า');
INSERT INTO "king_fahad_thai" VALUES(20,13,'20:13','“และข้าได้เลือกเจ้า ฉะนั้น จงตั้งใจฟังสิ่งที่ถูกวะฮี”');
INSERT INTO "king_fahad_thai" VALUES(20,14,'20:14','“แท้จริงข้าคืออัลลอฮฺ ไม่มีพระเจ้าอื่นใดนอกจากข้า ดังนั้นเจ้าจงเคารพภักดีต่อข้าและจงดํารงไว้ซึ่งการละหมาด เพื่อรําลึกถึงข้า');
INSERT INTO "king_fahad_thai" VALUES(20,15,'20:15','“แท้จริงวันอวสานของโลกนั้นกําลังมาถึงข้าปกปิดมันไว้เพื่อทุกชีวิตจะถูกตอบแทนตามี่มันได้แสวงหาไว้');
INSERT INTO "king_fahad_thai" VALUES(20,16,'20:16','“ดังนั้น ผู้ที่ไม่ศรัทธาต่อมันจะต้องไม่ทําให้เจ้าเหินห่างจากมัน และปฏิบัติตามอารมณ์ต่ําของเขา แล้วเจ้าจะพินาศ”');
INSERT INTO "king_fahad_thai" VALUES(20,17,'20:17','“และอะไรที่อยู่ในมือขวาขอเจ้าเล่า โอ้มูซา เอ๋ย !');
INSERT INTO "king_fahad_thai" VALUES(20,18,'20:18','เขากล่าวว่า “มันคือไม้เท้าของข้าพระองค์ ข้าพระองค์ใช้มันสําหรับยัน และข้าพระองค์ใช้มันตีบนพุ่มไม้เพื่อเป็นอาหารสําหรับแกะของข้าพระองค์ และข้าพระองค์ใช้มันในประโยชน์อื่น ๆ อีก”');
INSERT INTO "king_fahad_thai" VALUES(20,19,'20:19','พระองค์ตรัสว่า “จงโยนมันไปซิ โอ้มูซาเอ๋ย”');
INSERT INTO "king_fahad_thai" VALUES(20,20,'20:20','เขาจึงโยนมันลงไป แล้วมันก็ได้กลายเป็นงูเลื้อย');
INSERT INTO "king_fahad_thai" VALUES(20,21,'20:21','พระองค์ตรัสว่า”จงจับมันขึ้นมาและอย่ากลัว เราจะให้มันกลับมาเป็นไม้เท้าตามสภาพก่อนของมัน”');
INSERT INTO "king_fahad_thai" VALUES(20,22,'20:22','“และจงเอามือของเข้าซุกเข้าไปใต้รักแร้แล้วเอามันออกมามันจะมีสภาพขาวประกาย ปราศจากอันตรายใด ๆ มันเป็นอีกสัญญาณหนึ่ง');
INSERT INTO "king_fahad_thai" VALUES(20,23,'20:23','“เพื่อเราจะให้เข้าได้เห็นบางส่วนจากสัญญาณทั้งหลายอันยิ่งใหญ่ของเรา”');
INSERT INTO "king_fahad_thai" VALUES(20,24,'20:24','“จงไปหาฟิรเอาน์ เพราะเขาได้ละเมิดฝ่าฝืน”');
INSERT INTO "king_fahad_thai" VALUES(20,25,'20:25','เขากล่าวว่า “ข้าแต่พระเจ้าของข้าพระองค์ ขอพระองค์ทรงโปรดเปิดอกของข้าพระองค์ให้แก่ข้าพระองค์ด้วยเถิด”');
INSERT INTO "king_fahad_thai" VALUES(20,26,'20:26','“และทรงโปรดทําให้การงานของข้าพระองค์ ง่ายดายแก่ข้าพระองค์ด้วย');
INSERT INTO "king_fahad_thai" VALUES(20,27,'20:27','“และทรงโปรดแก้ปม จากลิ้นของข้าพระองค์ด้วย”');
INSERT INTO "king_fahad_thai" VALUES(20,28,'20:28','“เพื่อให้พวกเขาเข้าใจคําพูดของข้าพระองค์”');
INSERT INTO "king_fahad_thai" VALUES(20,29,'20:29','“และทรงโปรดให้คนในครอบครัวของข้าพระองค์ เป็นผู้ช่วยแก่ข้าพระองค์ด้วย”');
INSERT INTO "king_fahad_thai" VALUES(20,30,'20:30','“ฮารูนพี่ชายของข้าพระองค์”');
INSERT INTO "king_fahad_thai" VALUES(20,31,'20:31','“ได้โปรดให้เขาเพิ่มความเข้มแข็งแก่ข้าพระองค์ด้วย”');
INSERT INTO "king_fahad_thai" VALUES(20,32,'20:32','“และให้เขามีส่วนร่วมในกิจการของข้าพระองค์ด้วย”');
INSERT INTO "king_fahad_thai" VALUES(20,33,'20:33','“เพื่อเราจักได้ถวายการแซ่ซร้องสดุดีต่อพระองค์ท่านอย่างมากมาย”');
INSERT INTO "king_fahad_thai" VALUES(20,34,'20:34','“และเราจักได้รําลึกถึงพระองค์ท่านอย่างมากมาย”');
INSERT INTO "king_fahad_thai" VALUES(20,35,'20:35','“แท้จริงพระองค์ท่านเป็นผู้ทรงเห็นเรา”');
INSERT INTO "king_fahad_thai" VALUES(20,36,'20:36','พระองค์ตรัสว่า “แน่นอน เราได้ให้ตามคําขอของเจ้าแล้ว โอ้ มูซาเอ๋ย”');
INSERT INTO "king_fahad_thai" VALUES(20,37,'20:37','และโดยแน่นอน เราได้ให้ความโปรดปรานแก่เจ้ามาครั้งหนึ่งก่อนนี้แล้ว');
INSERT INTO "king_fahad_thai" VALUES(20,38,'20:38','โดยที่เราได้ดลใจให้มารดของเจ้าถึงสิ่งที่ถูกดลใจ');
INSERT INTO "king_fahad_thai" VALUES(20,39,'20:39','โดยให้นางวางเขาลงในหีบ แล้วเอาไปปล่อยในแม่น้ํา (ไนล์) แล้วแม่น้ําก็ซัดเขาไปติดที่ชายฝั่ง (จากนั้น) ศัตรูของข้าและศัตรูของเขาก็จะเก็บเอาเขาไป และข้าก็ได้ให้ความรักจากข้าแก่เจ้า เพื่อเจ้าจะได้รับการเลี้ยงดู ภายใต้การดูแลของข้า');
INSERT INTO "king_fahad_thai" VALUES(20,40,'20:40','เมื่อพี่สาวของเจ้าเดินไป เธอได้พูด(กับพวกนั้น)ว่า ฉันจะชี้แนะผู้ที่เลี้ยงดูเขาแก่พวกท่านไหม? แล้วเราให้เจ้ากับไปหามารดาของเจ้า เพื่อที่จะได้เป็นที่รื่นรมณ์แก่สายตาของนางและไม่เศร้าโศกและเจ้าได้ฆ่าชายคนหนึ่ง แล้วเราได้ช่วยเจ้าให้พ้นจากความหนักใจ และเราได้ทดสอบเจ้าด้วยการทดสอบนานาชนิด แล้วเจ้าได้พํานักอยู่กับชาวมัดยันเป็นเวลาหลายปี ภายหลังเจ้าได้กลับมาตามกําหนด โอ้ มูซาเอ๋ย !');
INSERT INTO "king_fahad_thai" VALUES(20,41,'20:41','และเราได้เลือกเจ้าเพื่อทําหน้าที่ของข้า');
INSERT INTO "king_fahad_thai" VALUES(20,42,'20:42','เจ้าจงไปพร้อมกับพี่ชายของเจ้า พร้อมด้วยสัญญาณทั้งหลายของข้า และเจ้าทั้งสองอย่าเฉื่อยชาในการรําลึกถึงข้า');
INSERT INTO "king_fahad_thai" VALUES(20,43,'20:43','เจ้าทั้งสองจงไปหาฟิรเอาน์ แท้จริงเขายะโสโอหังมาก');
INSERT INTO "king_fahad_thai" VALUES(20,44,'20:44','แล้วเจ้าทั้งสองจงพูดกับเขาด้วยคําพูดที่อ่อนโยน บางทีเขาอาจจะรําลึกขึ้นมา หรือเกิดความยําเกรงขึ้น');
INSERT INTO "king_fahad_thai" VALUES(20,45,'20:45','เขาทั้งสองได้กล่าวว่า “ข้าแต่พระผู้เป็นเจ้าของเรา แท้จริงเรากลัวว่า เขาจะล่วงเกินเรา');
INSERT INTO "king_fahad_thai" VALUES(20,46,'20:46','พระองค์ตรัสว่า “เจ้าทั้งสองอย่ากลัวแท้จริงข้าอยู่กับเจ้าทั้งสอง ข้าได้ยินและได้เห็น (ทุกสิ่งทุกอย่าง)');
INSERT INTO "king_fahad_thai" VALUES(20,47,'20:47','ดังนั้น เจ้าทั้งสองจงไปหาเขาแล้วกล่าวว่า “แท้จริงเราเป็นร่อซูลของพระเจ้าของท่าน ฉะนั้นท่านจงปล่อยวงศ์วานอิสรออีลมากับเราเถิด และอย่าได้ทรมานพวกเขาเลย แน่นอน เราได้นําสัญญาณ จากพระเจ้าของท่านมายังท่านแล้วและความปลอดภัย (จากการลงโทษของอัลลอฮฺ) จงมีแด่ผู้ปฏิบัติตามแนวทางที่ถูกต้อง');
INSERT INTO "king_fahad_thai" VALUES(20,48,'20:48','“แท้จริงได้มีวะฮีมายังเราว่า แท้จริงการลงโทษจะประสบแก่ผู้ที่ปฏิเสธ (บรรดานะบีของอัลลอฮฺ) และหันหลัง (ให้กับการอีมาน)');
INSERT INTO "king_fahad_thai" VALUES(20,49,'20:49','เขากล่าวว่า “ดังนั้น ใครเล่าคือพระเจ้าของท่านทั้งสอง โอ้มูซาเอ๋ย”');
INSERT INTO "king_fahad_thai" VALUES(20,50,'20:50','มูซากล่าวว่า “พระเจ้าของเราคือ ผุ้ทรงประทานทุกอย่างแก่สิ่งที่พระองค์ทรงสร้างแล้วพระองค์ก็ทรงชี้แนะแนวทางให้”');
INSERT INTO "king_fahad_thai" VALUES(20,51,'20:51','เขากล่าวว่า “แล้วสภาพของคนรุ่นก่อน ๆ นั้นเป็นเช่นไร ?”');
INSERT INTO "king_fahad_thai" VALUES(20,52,'20:52','มูซากล่าวว่า “ความรู้ในเรื่องนั้น อยู่ที่พระเจ้าของฉัน ในบันทึกของพระองค์ พระเจ้าของฉันจะไม่ทรงผิดพลาด และไม่ทรงหลงลืม”');
INSERT INTO "king_fahad_thai" VALUES(20,53,'20:53','พระผู้ทรงทําให้แผ่นดินเป็นพื้นราบเรียบ สําหรับพวกท่าน และทรงทําให้เป็นถนนหนทางสําหรับพวกท่าน และทรงหลั่งน้ําฝนมาจากฟากฟ้าและเราได้ให้พืชผลนานาชนิดออกมาเป็นคู่ ๆ');
INSERT INTO "king_fahad_thai" VALUES(20,54,'20:54','พวกเจ้าจงกิน และจงเลี้ยงปศุสัตว์ของพวกเจ้า แท้จริงในการนั้น แน่นนอน ย่อมเป็นสัญญาณมากหลายสําหรับปัญญาชน');
INSERT INTO "king_fahad_thai" VALUES(20,55,'20:55','จากแผ่นดินเราได้บังเกิดพวกเจ้า และ ณ แผ่นดินนั้นเราจะให้พวกเจ้ากลับคืนไป และจากแผ่นดินนั้น เราจะให้พวกเจ้ากลับออกมาอีกครั้งหนึ่ง');
INSERT INTO "king_fahad_thai" VALUES(20,56,'20:56','และแน่นอน เราได้ให้เขาเห็นสัญญาณทั้งหมดของเรา แต่เขาได้ปฏิเสธและดื้อดึง');
INSERT INTO "king_fahad_thai" VALUES(20,57,'20:57','เขากล่าวว่า “เจ้ามาหาเราเพื่อที่จะเอาเราออกจาแผ่นดินของเรา ด้วยเล่ห์กลของเจ้ากระนั้นหรือ โอ้ มูซาเอ๋ย !”');
INSERT INTO "king_fahad_thai" VALUES(20,58,'20:58','“ดังนั้น เราก็จะนํามาซึ่งเล่ห์กลนั้นเจ้าเช่นเดียวกัน ฉะนั้น เจ้าจงกําหนดวันขึ้นระหว่างเรากับท่าน ณ สถานที่ที่แน่นอน โดยที่เราจะไม่ผิดสัญญาและตัวท่านด้วย”');
INSERT INTO "king_fahad_thai" VALUES(20,59,'20:59','มูซากล่าวว่า “กําหนดวันของพวกท่านคือวันอีดวันรื่นเริง โดยให้ประชาชนมาร่วมชุมนุมกันในตอนสาย”');
INSERT INTO "king_fahad_thai" VALUES(20,60,'20:60','ฟิรเอาน์ได้กลับออกไป เพื่อไปร่วมมือกันวางแผนการณ์ของเขา แล้วได้มายังที่นัดหมาย');
INSERT INTO "king_fahad_thai" VALUES(20,61,'20:61','มูซาได้กล่าวแก่พวกเขา “ความหายนะจงประสบแก่พวกท่าน พวกท่านอย่าได้เสกสรรปั้นแต่งการมุสาต่ออัลลอฮฺ มิฉะนั้นพระองค์จะทรงทําลายพวกท่านด้วยการลงโทษ และแน่นอนผู้ปั้นแต่งการมุสานั้น ได้ประสบความผิดหวังมาแล้ว”');
INSERT INTO "king_fahad_thai" VALUES(20,62,'20:62','พวกเขาได้โต้แย้งกันในเรื่องของพวกเขา และได้มีการพูดกันอย่างลับ ๆ');
INSERT INTO "king_fahad_thai" VALUES(20,63,'20:63','พวกเขากล่าวว่า “สองคนนี้มิได้เป็นอื่นใด นอกจากเป็นนักมายากลอย่างแน่นอน ต้องการที่จะเอาพวกท่านออกจาแผ่นดินของพวกท่านด้วยเล่ห์กลของเขาทั้งสอง และต้องการจะลบล้างขนบธรรมเนียมอันดีงามของพวกท่าน');
INSERT INTO "king_fahad_thai" VALUES(20,64,'20:64','“ดังนั้น พวกท่านจงรวบรวมแผนการณ์ของพวกท่าน แล้วเดินออกมาเป็นแถว และวันนี้ผู้ใดเหนือกว่า ก็จะได้รับชัยชนะอย่างแน่นอน”');
INSERT INTO "king_fahad_thai" VALUES(20,65,'20:65','พวกเขากล่าวว่า “โอ้ มูซาเอ๋ย ! ท่านจะเป็นผู้โยนหรือว่าพวกเราจะเป็นผู้โยนก่อน”');
INSERT INTO "king_fahad_thai" VALUES(20,66,'20:66','มูซากล่าวว่า “แต่ว่าพวกท่านจงโยนก่อนเถิด” ณ บัดนั้น เชือกและไม้เท้าของพวกเขาดูประหนึ่งว่ามันเลื้อยคลานไปมาเพราะเล่ห์กลของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(20,67,'20:67','มูซาจึงรู้สึกกลัวขึ้นในตัวของเขา');
INSERT INTO "king_fahad_thai" VALUES(20,68,'20:68','เรากล่าวว่า “เจ้าอย่ากลัว แท้จริง เจ้าอยู่ในสภาพที่เหนือกว่า”');
INSERT INTO "king_fahad_thai" VALUES(20,69,'20:69','“และเจ้าจงโยนสิ่งที่อยู่ในมือขวาของเจ้า มันจะกลืนสิ่งที่พวกเขาทําขึ้น แท้จริงสิ่งที่พวกเขาทําขึ้นนั้นเป็นแผนของนักมายากล และนักมายากลนั้นจะไม่ประสบความสําเร็จ ไม่ว่าเขาจะมาจากทางไหนก็ตาม”');
INSERT INTO "king_fahad_thai" VALUES(20,70,'20:70','ดังนั้น พวกมายากลได้ก้มลงสุญูด โดยกล่าวว่า “เราขอศรัทธาต่อพระเจ้าของฮารูนและมูซา”');
INSERT INTO "king_fahad_thai" VALUES(20,71,'20:71','เขา (ฟิรเอาน์) กล่าวว่า “พวกท่านศรัทธาต่อเขา ก่อนที่ฉันจะขออนุญาตให้แก่พวกท่านกระนั้นหรือ ? แท้จริงเขาต้องเป็นหัวหน้าของพวกท่าน ซึ่งได้สอนวิชามายากลแก่พวกท่าน ฉะนั้น ฉันจะตัดมือและเท้าของพวกท่านสลับข้างกัน และฉันจะเอาพวกท่านไปตรึงไว้ที่ต้นอินทผาลัม และพวกท่านก็จะรู้อย่างแน่ชัดว่า ผู้ใดในหมู่พวกเราที่จะให้การลงโทษที่สาหัสกว่า และยาวนานยิ่งกว่า”');
INSERT INTO "king_fahad_thai" VALUES(20,72,'20:72','พวกเขากล่าวว่า “เราจะไม่ฝักใฝ่ท่านมากกว่าหลักฐานที่ชัดแจ้งที่ได้มายังเรา ขอสาบานต่อพระผู้ให้บังเกิดเรา ท่านจงกระทําตามสิ่งที่ท่านต้องการจะกระทําผิด แท้จริงท่านจะกระทําได้ในชีวิตแห่งโลกนี้เท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(20,73,'20:73','“แท้จริง เราได้ศรัทธาต่อพระเจ้าของเราเพื่อพระองค์จะทรงอภัยความผิดต่าง ๆ ของเราให้แก่เรา และทรงอภัยสิ่งที่ท่านได้บังคับให้เรากระทําเกี่ยวกับเรื่องมายากล และอัลลอฮฺนั้นทรงเป็นผู้ดีเลิศยิ่งและทรงยั่งยืนตลอดไป”');
INSERT INTO "king_fahad_thai" VALUES(20,74,'20:74','ความจริงนั้น ผู้ใดมาหาพระเจ้าของเขาในสภาพของผู้กระทําความผิด แน่นอน เขาจะได้รับนรกเป็นการตอบแทน โดยที่เขาจะไม่ตายและไม่เป็นในนั้น');
INSERT INTO "king_fahad_thai" VALUES(20,75,'20:75','และผู้ใดมาหาพระองค์โดยเป็นผู้ศรัทธาเขาได้กระทําความดีต่าง ๆ ไว้ ชนเหล่านี้แหละสําหรับพวกเขานั้นจะมีสถานะอันสูงส่ง');
INSERT INTO "king_fahad_thai" VALUES(20,76,'20:76','สวนสวรรค์หลากหลายอันสถาพร ณ เบื้องล่างของมันมีลําน้ําหลายสายไหลผ่าน พวกเขาเป็นผู้พํานักอยู่ในนั้นตลอดกาล และนั่นคือการตอบแทนสําหรับผู้ขัดเกลาตนเอง (ให้พ้นจากความชั่ว)');
INSERT INTO "king_fahad_thai" VALUES(20,77,'20:77','และโดยแน่นอน เราได้วะฮีแก่มูซาว่า “จงเดินทางในเวลากลางคืนพร้อมด้วยปวงบ่าวของข้าแล้วฟาดลงในทะเล ให้เป็นทางเดินแห้งแก่พวกเขา เจ้าอย่าได้กลัวว่าจะถูกตามทัน และจ้าอย่าได้กลัวจมน้ํา”');
INSERT INTO "king_fahad_thai" VALUES(20,78,'20:78','ฟิรเอาน์พร้อมด้วยไร่พลของเขาได้ตามมาทันพวกเขา แล้วน้ําจากทะเลได้ท่วม ทําให้พวกเขาจมน้ํา');
INSERT INTO "king_fahad_thai" VALUES(20,79,'20:79','และฟิรเอาน์ได้ทําให้กลุ่มชนของเขาหลงผิด และมิได้แนะทางที่ถูกต้องให้');
INSERT INTO "king_fahad_thai" VALUES(20,80,'20:80','โอ้ วงศ์วานของอิสรออีลเอ๋ย ! แน่นอนเราได้ช่วยพวกเจ้าให้พ้นจากศัตรูของพวกเจ้า และเราได้สัญญาพวกเจ้าทางด้านขวาของภูเขาฏูร และเราได้ให้อาหารหวานและนกคุ่มแก่พวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(20,81,'20:81','พวกเจ้าจงกินจากสิ่งที่ดีทั้งหลาย ที่เราได้ให้เป็นปัจจัยยังชีพแก่พวกเจ้า และพวกเจ้าอย่าได้ฝ่าฝืน มิฉะนั้น ความกริ้วของข้าจะเกิดขึ้นแก่พวกเจ้า และผู้ใดที่ความกริ้วของข้าจะเกิดขึ้นแก่เขาแน่นอนเขาจะประสบความพินาศ');
INSERT INTO "king_fahad_thai" VALUES(20,82,'20:82','และแท้จริง ข้าเป็นผู้อภัยอย่างมากหลายแก่ผู้ลุแก่โทษ และศรัทธา และประกอบความดีแล้วยึดมั่นอยู่ในแนวทางที่ถูกต้อง');
INSERT INTO "king_fahad_thai" VALUES(20,83,'20:83','และอะไรเล่าที่ทําให้เจ้ารีบเร่งออกจากกลุ่มชนของเจ้า โอ้ มูซาเอ๋ย !');
INSERT INTO "king_fahad_thai" VALUES(20,84,'20:84','เขากล่าวว่า ”พวกเขาเหล่านั้นตามหลังข้าพระองค์มาอยู่แล้ว และข้าพระองค์ได้รีบเร่งมายังพระองค์เท่านั้น โอ้พระเจ้าของข้าพระองค์ก็เพื่อให้พระองค์ทรงพอพระทัยเท่านั้น"');
INSERT INTO "king_fahad_thai" VALUES(20,85,'20:85','พระองค์ตรัสว่า “แท้จริงเราได้ทดสอบกลุ่มชนของเจ้า หลังจากที่เจ้าได้จากมา และซามิรีย์ก็ได้ทําให้พวกเขาหลงทาง”');
INSERT INTO "king_fahad_thai" VALUES(20,86,'20:86','มูซาได้กลับมายังกลุ่มชนของเขาด้วยความกริ้วโกรธเสียใจ เขากล่าวว่า “โอ้กลุ่มชนของฉันเอ๋ย พระเจ้าของพวกท่านมิได้ทรงสัญญากับพวกท่านด้วยสัญญาที่ดีดอกหรือ คํามั่นสัญญานั้นนานเกินไปสําหรับพวกท่านกระนั้นหรือ ? หรือว่าพวกท่านประสงค์ที่จะให้ความกริ้วจากพระเจ้าของพวกท่าน เกิดขึ้นแก่พวกท่าน ? พวกท่านจึงได้บิดพริ้วสัญญาของฉัน”');
INSERT INTO "king_fahad_thai" VALUES(20,87,'20:87','พวกเขากล่าวว่า “เรามิได้บิดพริ้วสัญญาของท่าน ตามความสมัครใจของเราดอก แต่ว่าเราต้องแบกน้ําหนักเครื่องประดับของพรรคพวกอย่างมากมาย เราจึงโยนมันลงไป เช่นเดียวกัน ซามิรีย์ก็ได้โยนมันลงไปด้วย”');
INSERT INTO "king_fahad_thai" VALUES(20,88,'20:88','แล้วซามิรีย์ก็ได้ทําลูกวัวออกมาเป็นรูปร่างมีเสียงร้อง พวกเขาจึงกล่าวว่า “นี่คือพระเจ้าของพวกท่าน และพระเจ้าของมูซา แต่เขาลืมเสีย”');
INSERT INTO "king_fahad_thai" VALUES(20,89,'20:89','พวกเขาไม่รู้ดอกหรือว่า มันไม่อาจจะให้คําตอบแพวกเขา และมันไม่สามารถจะให้โทษและให้คุณแก่พวกเขาเลย');
INSERT INTO "king_fahad_thai" VALUES(20,90,'20:90','และโดยแน่นอน ฮารูนกล่าวกับพวกเขาก่อนว่า “โอ้กลุ่มชนของฉันเอ๋ย แท้จริงพวกท่านถูกทดสอบให้หลงเสียแล้ว และแท้จริงพระเจ้าของพวกท่านนั้นคือพระผู้ทรงกรุณาปรานี ดังนั้นพวกท่านจงปฏิบัติตามฉัน และจงเชื่อฟังคําสั่งของฉัน');
INSERT INTO "king_fahad_thai" VALUES(20,91,'20:91','พวกเขากล่าวว่า “เรายังคงจะบูชามันโดยจะจงรักภักดี (ต่อมัน) จนกว่ามูซาจะกลับมาหาพวกเรา”');
INSERT INTO "king_fahad_thai" VALUES(20,92,'20:92','(เมื่อมูซากลับมาแล้ว) เขากล่าวว่า “โอ้ฮารูนเอ๋ย อันใดเล่าที่ยับยั้งท่าน เมื่อท่านเห็นพวกเขาหลงผิด”');
INSERT INTO "king_fahad_thai" VALUES(20,93,'20:93','“ทําไมท่านจึงไม่ปฏิบัติตามฉัน” ท่านฝ่าฝืนคําสั่งของฉันกระนั้นหรือ ?”');
INSERT INTO "king_fahad_thai" VALUES(20,94,'20:94','ฮารูน กล่าวว่า “โอ้ลูกของแม่ฉันเอ๋ย อย่าดึงเคราและศีรษะของฉันซิ แท้จริงฉันกลัวว่า ท่านจะกล่าว(แก่ฉัน) ว่า ท่านได้ก่อการแตกแยกขึ้นในหมู่วงศ์วานอิสรออีล และท่านไม่คอบฟังคําสั่งของฉัน”');
INSERT INTO "king_fahad_thai" VALUES(20,95,'20:95','มูซากล่าวว่า “เจ้าต้องการอะไร โอ้ซามิรีย์เอ๋ย !”');
INSERT INTO "king_fahad_thai" VALUES(20,96,'20:96','เขากล่าวว่า “ฉันเห็นในสิ่งที่พวกเขามองไม่เห็น ดังนั้น ฉันจึงกําเอากอบหนึ่งจากรอยของร่อซูล (หมายถึงญิบรีล) แล้วฉันได้โยนมันลงไปและเช่นนั้นแหละจิตใจของฉันได้เห็นดีเห็นงาม”');
INSERT INTO "king_fahad_thai" VALUES(20,97,'20:97','มูซากล่าวว่า “ท่านจงออกไป แท้จริงสําหรับท่านในชีวิตนี้จะได้รับการลงโทษโดยท่านกล่าวว่า อย่ามาแตะต้องฉัน และแท้จริงสําหรับท่านนั้นมีสัญญาหนึ่ง ท่านจะไม่ถูกทําให้ผิดสัญญาและจงดูพระเจ้าของท่านซึ่งท่านยึดถือบูชามันแน่นอนเราจะเผามัน แล้วเราจะโปรยมันลงในทะเลให้กระจาย”');
INSERT INTO "king_fahad_thai" VALUES(20,98,'20:98','แท้จริง พระเจ้าของพวกท่านนั้นคือ อัลลอฮฺ ซึ่งไม่มีพระเจ้าอื่นใดนอกจากพระองค์ พระองค์ทรงแผ่ความรอบรู้ในยังทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(20,99,'20:99','เช่นนี้แหละ เราได้บอกเล่าข่าวคราวที่ได้เกิดขึ้นแต่กาลก่อนแก่เจ้า และแน่นอน เราได้ให้ข้อเตือนสติ จากเราแก่เจ้า');
INSERT INTO "king_fahad_thai" VALUES(20,100,'20:100','ผู้ใดหันหลังให้อัลกุรอาน แท้จริงเขาจะแบกโทษหนักในวันกิยามะฮ์ (อยุ่ในนรก)');
INSERT INTO "king_fahad_thai" VALUES(20,101,'20:101','พวกเขาจะพํานักอย่างถาวรอยู่ในนั้น และโทษหนักนั้นเป็นความชั่วช้าสําหรับพวกเขาในวันกิยามะฮ์เสียนี่กระไร !');
INSERT INTO "king_fahad_thai" VALUES(20,102,'20:102','วันซึ่งสังข์จะถูกเป่า และในวันนั้นเราจะรวมนักโทษทั้งหลายที่มีตาสีฟ้าไว้ด้วยกัน');
INSERT INTO "king_fahad_thai" VALUES(20,103,'20:103','พวกเขาจะกระซิบกระซาบระหว่างกันว่า “พวกท่านมิได้พักในโลกนี้นอกจากเพียง 10 วัน เท่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(20,104,'20:104','เรารู้ดียิ่งในสิ่งที่พวกเขากล่าวกัน เมื่อผู้มีความคิดที่ดียิ่งกล่าวว่า “พวกท่านมิได้พักอยู่ นอกจากเพียงวันเดียวเท่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(20,105,'20:105','และพวกเขาจะถามเจ้าเกี่ยวกับภูเขา จงกล่าวเถิด (มุฮัมมัด) “พระเจ้าของฉันจะทรงทําให้มันแตกออกเป็นผุยผง ”');
INSERT INTO "king_fahad_thai" VALUES(20,106,'20:106','“แล้วจะทรงปล่อยให้มันเป็นที่ราบโล่งเตียน (ไม่มีต้นไม้และสิ่งก่อสร้าง)”');
INSERT INTO "king_fahad_thai" VALUES(20,107,'20:107','“สูเจ้าจะไม่เห็น ณ ที่นั้น ที่ลุ่มและที่ดอน”');
INSERT INTO "king_fahad_thai" VALUES(20,108,'20:108','วันนั้นพวกเขาจะติดตามผู้ร้องเรียกไปโดยไม่มีการอิดเอื้อนแต่ประการใด เสียงทั้งหลายก็จะลดค่อยลงต่อพระผู้ทรงกรุณาปรานี เจ้าจะไม่ได้ยินเสียงใด นอกจากเสียแผ่วเบา');
INSERT INTO "king_fahad_thai" VALUES(20,109,'20:109','วันนั้น การชะฟาอะฮ์ จะไม่เกิดประโยชน์อันใด นอกจากผู้ที่พระผู้ทรงกรุณาปรานีทรงอนุญาตแก่เขา และพระองค์ทรงพอพระทัยในคําพูดของเขาเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(20,110,'20:110','พระองค์ทรงรอบรู้สิ่งต่าง ๆ ที่อยู่ข้างหน้าพวกเขา และสิ่งต่าง ๆที่อยู่ลับหลังพวกเขาและความรู้ของพวกเขาไม่อาจจะเท่าเทียมความรู้ของพระองค์ได้');
INSERT INTO "king_fahad_thai" VALUES(20,111,'20:111','และใบหน้าทั้งหลาย ได้สยบลงต่อพระผู้ทรงเป็นอยู่เสมอ พระผู้ทรงอมตะ และแน่นอน ผู้ที่แบกเอาความอธรรมไว้ (ชิริก)ต้องประสบกับการขาดทุนอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(20,112,'20:112','และผู้ใดปฏิบัติคุณงามความดีทั้งหลายโดยที่เขาเป็นผู้ศรัทธา เขาจะไม่กลัวความอธรรมและการบั่นทอนใด ๆ');
INSERT INTO "king_fahad_thai" VALUES(20,113,'20:113','และเช่นนั้นแหละ เราได้ให้กุรอานเป็นภาษาอาหรับลงมาแก่เขา และเราได้กล่าวซ้ําในนั้นซึ่งข้อตักเตือน หวังว่าพวกเขาจะมีความยําเกรงหรือเกิดข้อเตือนใจแก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(20,114,'20:114','ดังนั้น อัลลอฮฺคือพระผู้ทรงสูงส่งยิ่ง พระผู้ทรงอํานาจอันแท้จริง และเจ้าอย่ารีบเร่งในการอ่านอัลกุรอาน ก่อนที่วะฮีย์ของพระองค์จะจบลง และจงกล่าวเถิด “ข้าแต่พระเจ้าของข้า พระองค์ขอพระองค์ทรงโปรดเพิ่มพูนความรู้แก่ข้าพระองค์ด้วย”');
INSERT INTO "king_fahad_thai" VALUES(20,115,'20:115','และโดยแน่นอน เราได้ให้คํามั่นสัญญาแก่อาดัมแต่กาลก่อน แต่เขาได้ลืม และเราไม่พบความมั่ใจอดทนในตัวเขา');
INSERT INTO "king_fahad_thai" VALUES(20,116,'20:116','และเมื่อเรากล่าวแก่บรรดามะลาอิกะฮ์ว่า “จงสุญูดคารวะแก่อาดัม” และพวกเขาได้สุญูดนอกจากอิบลีส มันได้ดื้อดึง');
INSERT INTO "king_fahad_thai" VALUES(20,117,'20:117','แล้วเราได้กล่าวว่า “โอ้อาดัมเอ๋ย ! แท้จริงนี่คือศัตรูของเจ้าและของภริยาของเจ้า ดังนั้นอย่าให้มันทําให้เจ้าทั้งสองออกจากสวนสวรรค์ แล้วเจ้าจะได้รับความลําบาก”');
INSERT INTO "king_fahad_thai" VALUES(20,118,'20:118','“แท้จริงในสวนสวรรค์นั้น เจ้าจะไม่หิวและจะไม่ต้องเปลือยกาย”');
INSERT INTO "king_fahad_thai" VALUES(20,119,'20:119','“และแท้จริงในสวนสวรรค์นั้น เจ้าจะไม่กระหายน้ํา และจะไม่ตากแดด”');
INSERT INTO "king_fahad_thai" VALUES(20,120,'20:120','ต่อมาชัยฏอนมารร้ายได้กระซิบกระซาบเขา มันกล่าวว่า “อาดัมเอ๋ย ฉันจะชี้แนะแก่ท่านไปยังต้นไม้ที่อยู่เป็นนิจตลอดกาลและการมีอํานาจที่ไม่สูญสลายเอาไหม?”');
INSERT INTO "king_fahad_thai" VALUES(20,121,'20:121','ดังนั้น เขาทั้งสองจึงกินจากต้นไม้นั้นสิ่งพึงสงวนของทั้งสองจึงถูกเผยแก่เขาทั้งสอง เขาทั้งสองจึงเริ่มเอาใบไม้ของสวนนั้นมาปกปิดบนตัวของเขาทั้งสอง และอาดัมได้ฝ่าฝืนพระเจ้าของเขา เขาจึงหลงผิด');
INSERT INTO "king_fahad_thai" VALUES(20,122,'20:122','ภายหลัง พระเจ้าของเขาทรงคัดเลือกเขาแล้วทรงอภัยโทษให้แก่เขา และทรงแนะทางที่ถูกต้องให้เขา');
INSERT INTO "king_fahad_thai" VALUES(20,123,'20:123','พระองค์ตรัสว่า “เจ้าทั้งสองจงออกไปจากสวนสวรรค์ทั้งหมด โดยบางคน (ลูกหลาน) ในหมู่พวกเจ้าเป็นศัตรูกับอีกบางคน บางทีเมื่อมีคําแนะนํา (ฮิดายะฮ์) จากข้ามายังพวกเจ้า แล้วผู้ใดปฏิบัติตามคําแนะนํา (ฮิดายะฮ์) ของข้า เขาก็จะไม่หลงผิด และจะไม่ได้รับความลําบาก”');
INSERT INTO "king_fahad_thai" VALUES(20,124,'20:124','“และผู้ใดผินหลังจากการรําลึกถึงข้า แท้จริงสําหรับเขาคือ การมีชีวิตอยู่อย่างคับแค้น และเราจะให้เขาฟื้นคืนชีพในวันกิยามะฮ์ในสภาพของคนตาบอด”');
INSERT INTO "king_fahad_thai" VALUES(20,125,'20:125','เขากล่าวว่า “ข้าแต่พระเจ้าของข้าพระองค์ ทําไมพระองค์จึงทรงให้ข้าพระองค์ฟื้นคืนชีพขึ้นมาในสภาพของคนตาบอดเล่า ทั้งๆที่ข้าพระองค์เคยเป็นคนตาดี มองเห็น”');
INSERT INTO "king_fahad_thai" VALUES(20,126,'20:126','พระองค์ตรัสว่า “เช่นนั้นแหละ เมื่อโองการทั้งหลายของเราได้มีมายังเจ้า เจ้าก็ทําเป็นลืมมัน และในทํานองเดียวกัน วันนี้เจ้าก็จะถูกลืม”');
INSERT INTO "king_fahad_thai" VALUES(20,127,'20:127','และเช่นเดียวกัน เราจะตอบแทนผู้ที่ล่วงละเมิดขอบเขต และไม่ศรัทธาต่อโองการทั้งหลายของพระเจ้า และแน่นอน การลงโทษในปรโลกนั้นสาหัสยิ่ง และยาวนานยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(20,128,'20:128','ยังมิเป็นที่ประจักษ์ชัดแก่พวกเขาดอกหรือว่า กี่มากน้อยแล้ว เราได้ทําลายประชาชาติก่อนหน้าพวกเขาหลายชั่วศตวรรษ โดยที่พวกเขา (กุฟารมักกะฮ์) ได้ไปพบเห็นมาในที่พํานักอาศัยของพวกเขา แท้จริง ในการลงโทษเช่นนั้นแหละเป็นนิทัศน์อุทาหารณ์สําหรับบรรดาผู้มีสติปัญญา');
INSERT INTO "king_fahad_thai" VALUES(20,129,'20:129','และหากมิใช่ลิขิตจากพระเจ้าของเจ้า ถูกบันทึกไว้ล่วงหน้าก่อนแล้ว แน่นอน การลงโทษจะเกิดขึ้นทันทีอย่างหลีกเลี่ยงไม่ได้');
INSERT INTO "king_fahad_thai" VALUES(20,130,'20:130','ดังนั้น เจ้าจงอดทนต่อสิ่งที่พวกเขากล่าวร้าย และจงแซ่ซร้องสดุดีด้วยการสรรเสริญพระเจ้าของเจ้า ก่อนตะวันขึ้นและก่อนตะวันลับลงไป และส่วนหนึ่งจากเวลากลางคืน ก็จงแซ่ซร้องสดุดีและปลายช่วงของเวลากลางวัน เพื่อเจ้าจะได้พออกพอใจ');
INSERT INTO "king_fahad_thai" VALUES(20,131,'20:131','และเจ้าจงอย่าทอดสายตาของเจ้าไปยังสิ่งที่เราได้ให้ความเพลิดเพลินแก่บุคคลประเภทต่าง ๆ ของพวกกุฟฟาร ซึ่งความสุขสําราญในโลกดุนยา เพื่อเราจะได้ทดสอบพวกเขาในการนี้ และการตอบแทนของพระเจ้านั้น ดียิ่งกว่าและจีรังยิ่งกว่า');
INSERT INTO "king_fahad_thai" VALUES(20,132,'20:132','และเจ้าจงใช้ครอบครัวของเจ้า ให้ทําละหมาด และจงอดทนในการปฏิบัติ เรามิได้ขอเครื่องยังชีพจากเจ้า เราต่างหากเป็นผู้ให้เครื่องยังชีพแก่เจ้า และบั้นปลายนั้นสําหรับผู้ที่มีความยําแกรง');
INSERT INTO "king_fahad_thai" VALUES(20,133,'20:133','และพวกเขากล่าวว่า “ทําไมเขาจึงไม่นําสัญญาณหนึ่งจากพระเจ้าของเขามาให้เรา” หรือว่าหลักฐานอันชัดแจ้งที่ปรากฏอยู่ในคัมภีร์ต่าง ๆ สมัยก่อนนั้น มิได้มีมายังพวกเขาดอกหรือ?');
INSERT INTO "king_fahad_thai" VALUES(20,134,'20:134','และหากเราทําลายพวกเขาด้วยการลงโทษก่อนการให้อัลกุรอานลงมา แน่นอนพวกเขาก็กล่าวว่า ข้าแต่พระเจ้าของเราทําไมพระองค์ท่านจึงไม่ส่งร่อซูลมายังพวกเรา เพื่อเราจะได้ปฏิบัติตามโองการของพระองค์ท่าน ก่อนที่เราจะได้รับความต่ําต้อยและความอัปยศ');
INSERT INTO "king_fahad_thai" VALUES(20,135,'20:135','จงกล่าวเถิดมุฮัมมัด ทุกคนเป็นผู้คอย ดังนั้น พวกท่านจงคอยเถิด แล้วพวกท่านจะได้รู้ว่าใครคือ พวกที่อยู่ในแนวทางอันเที่ยงตรง และใครคือผู้ที่อยู่ในแนวทางที่ถูกต้อง”');
INSERT INTO "king_fahad_thai" VALUES(21,1,'21:1','เวลาแห่งการคิดบัญชีของมนุษย์ได้ใกล้เขามาแล้ว โดยที่พวกเขาอยู่ในสภาพหลงลืม เป็นผู้ผินหลังให้');
INSERT INTO "king_fahad_thai" VALUES(21,2,'21:2','ไม่มีข้อตักเตือนใหม่ ๆ จากพระเจ้าของเขามายังพวกเขา เว้นแต่ว่าพวกเขารับฟังมันและพวกเขาล้อเล่นไปด้วย');
INSERT INTO "king_fahad_thai" VALUES(21,3,'21:3','จิตใจของพวกเขาเผลอเรอ และบรรดาผู้อธรรมต่างกระซิบกระซาบระหว่างกันว่า เขา (มุฮัมมัด) นี้มิใช่ใครอื่น นอกจากเป็นสามัญชนเยี่ยงพวกท่าน พวกท่านจะยอมรับมายากล ทั้ง ๆ ที่พวกท่านรู้เห็นอยู่ว่ามันเป็นมายากลกระนั้นหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(21,4,'21:4','เขา (มุฮัมมัด) กล่าวว่า “พระเจ้าของฉันทรงรอบรู้ทุกคําพูด ทั้งในชั้นฟ้าและแผ่นดิน และพระองค์เป็นผู้ทรงได้ยิน เป็นผู้ทรงรอบรู้”');
INSERT INTO "king_fahad_thai" VALUES(21,5,'21:5','แต่ทว่าพวกเขากล่าวว่า “มันเป็นความฝันที่สับสน หากแต่ว่าเขาได้เสกสรรปั้นแต่งมันขึ้นหากแต่ว่าเขาเป็นกวี ถ้าเช่นนั้น ให้เขานําหลักฐานหนึ่งมาแสดงแก่เรา เช่นเดียวกับบุคคลในยุคก่อน ๆได้ถูกส่งมา”');
INSERT INTO "king_fahad_thai" VALUES(21,6,'21:6','ไม่มีชาวเมืองใดก่อนหน้าพวกเขา (มุชริกีนมักกะฮ์) ซึ่งเราได้ทําลายมัน (ชาวเมืองนั้น) ได้ศรัทธาแล้วพวกเขา (มุชริกีนมักกะฮ์) จะศรัทธากระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(21,7,'21:7','และพวกเขาปรารถนาที่จะวางแผนร้ายแก่เขา แต่เราได้ทําให้พวกเขาประสบกับความสูญเสียมากยิ่งกว่า');
INSERT INTO "king_fahad_thai" VALUES(21,8,'21:8','และเรามิได้ทําให้พวกเขา(บรรดานะบี) มีร่างกายที่ไม่ต้องการอาหาร (เช่นมะลาอิกะฮ์) และพวกเขามิได้เป็นผู้มีชีวิตยั่งยืน');
INSERT INTO "king_fahad_thai" VALUES(21,9,'21:9','และเรามิได้ทําให้สัญญาเป็นที่ประจักษ์จริงแก่พวกเขา และเราได้ให้พวกเขารอดพ้น และผู้ที่ประสงค์ และเราได้ทําลายผู้ปฏิเสธ ละเมิดฝ่าฝืน');
INSERT INTO "king_fahad_thai" VALUES(21,10,'21:10','เราขอสาบานว่า แท้จริงเราได้ให้คัมภีร์อัลกุรอานมายังพวกเจ้า ในนั้นมีข้อเตือนสติแก่พวกเจ้า พวกเจ้าไม่ใช้สติปัญญาคิดบ้างดอกหรือ');
INSERT INTO "king_fahad_thai" VALUES(21,11,'21:11','และกี่มากน้อยแล้วที่เราได้ทําลายหมู่บ้านที่อธรรม (ปฏิเสธการศรัทธา) และเราได้ให้หมู่ชนอื่นเกิดขึ้นมาแทนที่หลังจากนั้น');
INSERT INTO "king_fahad_thai" VALUES(21,12,'21:12','เมื่อพวกเขารู้สึกว่า การลงโทษของเราเกิดขึ้นแล้ว พวกเขาจึงวิ่งหนีออกไป');
INSERT INTO "king_fahad_thai" VALUES(21,13,'21:13','(มะลาอิกะฮ์พูดกับพวกนั้นว่า) “พวกท่านอย่าวิ่งหนีซิ และจงกลับไปยังสิ่งที่พวกท่านได้รับความสําราญ และยังที่พักของพวกท่าน เพื่อว่าพวกท่านจะได้ถูกสอบถามสิ่งที่เกิดขึ้นแก่ท่าน”');
INSERT INTO "king_fahad_thai" VALUES(21,14,'21:14','พวกเขากล่าวว่า “โอ้ ความหายนะที่เกิดขึ้นแก่เรา แท้จริงเรานั้นเป็นผู้อธรรม”');
INSERT INTO "king_fahad_thai" VALUES(21,15,'21:15','ไม่ทันที่คําพูดของพวกเขาจะสิ้นสุดลง เราก็ได้ทําลายพวกเขา เสมือนพืชที่ถูกเก็บเกี่ยวมอดไหม้ไป');
INSERT INTO "king_fahad_thai" VALUES(21,16,'21:16','และเรามิได้สร้างชั้นฟ้าและแผ่นดิน และสิ่งที่อยู่ในระหว่างทั้งสอง เพื่อการสนุกสนานอย่างไร้ประโยชน์');
INSERT INTO "king_fahad_thai" VALUES(21,17,'21:17','หากเราปรารถนาที่จะเอาเป็นเครื่องเล่นสนุกสนาน เราก็จะเอามันจากที่มีอยู่ที่เรา หากเราปรารถนาจะกระทําเช่นนั้น');
INSERT INTO "king_fahad_thai" VALUES(21,18,'21:18','แต่ว่าเราได้ให้ความจริงทําลายความเท็จแล้วเราก็ให้มันเสียหายไป แล้วมันก็จะมลายสิ้นไป และความหายนะจะประสบแก่พวกเจ้า ในสิ่งที่พวกเจ้า กล่าวเสกสรรปั้นแต่งต่ออัลลอฮ์');
INSERT INTO "king_fahad_thai" VALUES(21,19,'21:19','และเป็นกรรมสิทธิ์ของพระองค์ ผู้ที่อยู่ในชั้นฟ้าทั้งหลายและแผ่นดิน และผู้ที่อยู่ ณ ที่พระองค์ (มะลาอิกะฮ์) พวกเขาจะไม่ลําพองตนในการเคารพภักดีพระองค์ และพวกเขาจะไม่เหนื่อยหน่าย');
INSERT INTO "king_fahad_thai" VALUES(21,20,'21:20','พวกเขาจะแซ่ซร้องสดุดีพระองค์ในเวลากลางคืน และกลางวัน โดยไม่ขาดระยะ');
INSERT INTO "king_fahad_thai" VALUES(21,21,'21:21','แต่ทว่าพวกเขา (มุชริกีน) ได้ยึดถือบรรดาพระเจ้าจากพื้นดิน โดยคิดว่าสิ่งเหล่านั้นสามารถจะให้คนตาย ฟื้นคืนชีพมาได้กระนั้นหรือ');
INSERT INTO "king_fahad_thai" VALUES(21,22,'21:22','หากในชั้นฟ้าและแผ่นดินมีพระเจ้าหลายองค์ นอกจากอัลลอฮ์แล้ว ก็จะก่อให้เกิดความเสียหายอย่างแน่นอน อัลลอฮ์พระเจ้าแห่งบัลลังก์ทรงบริสุทธิ์จากสิ่งที่พวกเขาเสกสรรปั้นแต่งขึ้น');
INSERT INTO "king_fahad_thai" VALUES(21,23,'21:23','พระองค์จะไม่ทรงถูกสอบถามในสิ่งที่พระองค์ทรงปฏิบัติ และพวกเขาต่างหากที่จะถูกสอบถาม');
INSERT INTO "king_fahad_thai" VALUES(21,24,'21:24','พวกเขาได้ยึดถือบรรดาพระเจ้า นอกจากพระองค์กระนั้นหรือ? จงกล่าวเถิดมุฮัมมัด “ขอให้พวกท่านนําหลักฐานของพวกท่านมา นี่คือคัมภีร์ที่อยู่กับฉัน (อัลกุรอาน) และคัมภีร์ที่มีอยู่ก่อนหน้าฉัน (เตารอตและอินญีล)” แต่ว่าส่วนใหญ่ของพวกเขาไม่รู้ความจริง พวกเขาจึงผินหลังเมินห่าง');
INSERT INTO "king_fahad_thai" VALUES(21,25,'21:25','และเรามิได้ส่งร่อซูลคนใดก่อนหน้าเจ้านอกจากเราได้วะฮีแก่เขาว่า “แท้จริงไม่มีพระเจ้าอื่นใดนอกจากข้า ดังนั้นพวกเจ้าจงเคารพภักดีต่อข้า”');
INSERT INTO "king_fahad_thai" VALUES(21,26,'21:26','และพวกเขา (มุริกูน) กล่าวว่า พระผู้ทรงกรุณาปรานีทรงยึดมะลาอิกะฮ์เป็นพระบุตร” มหาบริสุทธิ์แห่งพระองค์ แต่ว่าพวกเขา (มะลาอิกะฮ์) เป็นบ่าวผู้มีเกียรติ');
INSERT INTO "king_fahad_thai" VALUES(21,27,'21:27','พวกเขาจะไม่ชิงกล่าวคําพูดก่อนพระองค์ และพวกเขาปฏิบัติตามพระบัญชาของพระองค์');
INSERT INTO "king_fahad_thai" VALUES(21,28,'21:28','พระองค์ทรงรอบรู้ สิ่งที่อยู่เบื้องหน้าพวกเขา และสิ่งที่อยู่เบื้องหลังพวกเขา และพวกเขาจะไม่ให้ความช่วยเหลือแก่ผู้ใด นอกจากผู้ที่พระองค์ทรงพอพระทัย และเนื่องจากความกลัวพวกเขาจึงเนื้อตัวสั่น');
INSERT INTO "king_fahad_thai" VALUES(21,29,'21:29','และผู้ใดในหมู่พวกเขาที่กล่าวว่า “แท้จริงฉันคือพระเจ้าอื่นจากพระองค์” เขาผู้นั้นเราจะตอบแทนลงโทษด้วยนรก เช่นนั้นแหละเราตอบแทนบรรดาผู้อธรรม”');
INSERT INTO "king_fahad_thai" VALUES(21,30,'21:30','และบรรดาผู้ปฏิเสธศรัทธาเหล่านั้นไม่เห็นดอกหรือว่า แท้จริงชั้นฟ้าทั้งหลายและแผ่นดินนั้นแต่ก่อนนี้รวมติดเป็นอันเดียวกัน แล้วเราได้แยกมันทั้งสองออกจากกัน และเราได้ทําให้ทุกสิ่งมีชีวิตมาจากน้ํา ดังนั้นพวกเขาจะยังไม่ศรัทธาอีกหรือ');
INSERT INTO "king_fahad_thai" VALUES(21,31,'21:31','และเราได้ทําให้เทือกเขามั่นคงในแผ่นดินเพื่อมันจะมิได้หวั่นไหวไปกับพวกเขา และเราได้ทําให้หุบเขาเป็นทางกว้างในแผ่นดินนั้น เพื่อว่าพวกเขาได้ใช้เป็นทางเดินอย่างถูกต้อง');
INSERT INTO "king_fahad_thai" VALUES(21,32,'21:32','และเราได้ทําให้ชั้นฟ้าเป็นหลังคา ถูกรักษาไว้ไม่ให้หล่นลงมา และพวกเขาก็ยังผินหลังให้สัญญาณต่าง ๆ ของมัน');
INSERT INTO "king_fahad_thai" VALUES(21,33,'21:33','และพระองค์ผู้ทรงสร้างกลางคืนและกลางวัน และดวงอาทิตย์และดวงจันทร์ แต่ละหน่วยโคจรตามจักรราศี');
INSERT INTO "king_fahad_thai" VALUES(21,34,'21:34','และเรามิได้ทําให้บุคคลใดก่อนหน้าเจ้าอยู่ยั่งยืนนาน หากเจ้าตายไปพวกเขาจะมีชีวิตอยู่ยงคงต่อไปกระนั้นหรือ');
INSERT INTO "king_fahad_thai" VALUES(21,35,'21:35','ทุกชีวิตย่อมลิ้มรสความตาย และเราจะทดสอบพวกเจ้าด้วยความชั่วและความดี และพวกเจ้าจะต้องกลับไปหาเราอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(21,36,'21:36','และเมื่อบรรดาผู้ปฏิเสธศรัทธา (กุฟฟารมักกะฮ์) พบเห็นเจ้า พวกเขาก็ถือเอาเจ้าเป็นที่ล้อเลียน “คนนี้นะหรือที่กล่าวตําหนิพระเจ้าของพวกท่าน” ทั้ง ๆ ที่พวกเขาก็กล่าวตําหนิพระผู้ทรงกรุณาปรานี โดยพวกเขาเป็นผู้ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(21,37,'21:37','มนุษย์ถูกสร้างขึ้นมาด้วยความรีบร้อน ข้าจะแสดงสัญญาณต่าง ๆ ของข้าให้พวกเจ้าเห็น ฉะนั้นพวกเจ้าอย่าได้เร่งข้าเลย');
INSERT INTO "king_fahad_thai" VALUES(21,38,'21:38','และพวกเขากล่าวว่า “เมื่อใดเล่าสัญญานี้จะเกิดขึ้น หากพวกท่าน เป็นผู้สัตย์จริง”');
INSERT INTO "king_fahad_thai" VALUES(21,39,'21:39','หากบรรดาผู้ปฏิเสธศรัทธารู้ว่า พวกเขาไม่สามารถที่จะป้องกันไฟจากทางด้านหน้าของพวกเขาและทางด้านหลังของพวกเขา และพวกเขาจะไม่ได้รับความช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(21,40,'21:40','แต่ว่ามันจะมาถึงพวกเขาโดยฉับพลันไม่รู้ตัว แล้วจะทําให้พวกเขาตกใจ ดังนั้น พวกเขาจึงไม่สามารถที่จะผลักดันให้พ้นไปได้ และพวกเขาก็ไม่อาจจะประวิงเวลาต่อไปได้');
INSERT INTO "king_fahad_thai" VALUES(21,41,'21:41','และโดยแน่นอน บรรดาร่อซูลก่อนหน้าเจ้าได้ถูกเย้ยหยันมาแล้ว แล้วการลงโทษได้ประสบแก่บรรดาผู้ที่เย้ยหยันต่อบรรดาร่อซูล ในสิ่งที่พวกเขาได้เย้ยหยัน');
INSERT INTO "king_fahad_thai" VALUES(21,42,'21:42','จงกล่าวเถิดมุฮัมมัด “ใครเล่าจะคุ้มกันรักษาพวกท่าน ในเวลากลางคืนและกลางวัน จากพระผู้ทรงกรุณาปรานี” แต่ทว่าพวกเขาเป็นผู้ผินหลังให้การรําลึกถึงพระเจ้าของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(21,43,'21:43','หรือว่าพวกเขามีพระเจ้าหลายองค์ นอกไปจากเรา คอบป้องกันพวกเขา ทั้ง ๆ ที่พระเจ้าเหล่านั้นไม่สามารถที่จะช่วยเหลือตัวเอง และไม่สามารถป้องกันตัวเองให้พ้นจากการลงโทษของเราได้');
INSERT INTO "king_fahad_thai" VALUES(21,44,'21:44','แต่ทว่า เราได้ให้ความสุขสํารายแก่พวกเขาและบรรพบุรุษของพวกเขา จนกระทั่งพวกเขามีอายุยั่งยืน พวกเขาไม่เห็นดอกหรือว่า แท้จริงเราได้มายังแผ่นดินเพื่อทําให้มันคับแคบลงจากขอบเขตของมัน แล้วพวกเขาจะเป็นผู้ชนะอีกหรือ?');
INSERT INTO "king_fahad_thai" VALUES(21,45,'21:45','จงกล่าวเถิดมุฮัมมัด “แท้จริงฉันเพียงตักเตือนพวกท่านด้วยวะฮีเท่านั้น” แต่คนหูหนวกไม่ได้ยินเสียงเรียกร้อง เมื่อพวกเขาถูกตักเตือน');
INSERT INTO "king_fahad_thai" VALUES(21,46,'21:46','และหากการลงโทษเพียงเล็กน้อยจากพระเจ้าของเจ้าประสบกับพวกเขา แน่นอนพวกเขาก็จะกล่าวว่า “โอ้ความหายนะแก่เรา แท้จริงเราเป็นผู้อธรรม”');
INSERT INTO "king_fahad_thai" VALUES(21,47,'21:47','และเราตั้งตราชูที่เที่ยงธรรมสําหรับวันกิยามะฮ์ ดังนั้นจะไม่มีชีวิตใดถูกอธรรมแต่อย่างใดเลย และแม้ว่ามันเป็นเพียงน้ําหนักเท่าเมล็ดพืชเล็ก เราก็จะนํามันมาแสดง และเป็นการพอเพียงแล้วสําหรับเราที่เป็นผู้ชําระสอบสวน');
INSERT INTO "king_fahad_thai" VALUES(21,48,'21:48','และแท้จริง เราได้ให้แก่มูซาและฮารูน (ซึ่งคัมภีร์เตารอฮ์) ที่แยกระหว่างความจริงกับความเท็จและเป็นแสงสว่างแห่งดวงประทีปและข้อตักเตือนสําหรับบรรดาผู้ยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(21,49,'21:49','บรรดาผู้เกรงกลัวพระเจ้าของพวกเขาโดยที่พวกเขาไม่เห็น และพวกเขายังหวั่นกลัวต่อวันอวสานด้วย');
INSERT INTO "king_fahad_thai" VALUES(21,50,'21:50','และนี่คืออัลกุรอาน เป็นการตักเตือนที่จําเริญซึ่งเราได้ให้มันลงมา แล้วพวกเจ้ายังจะปฏิเสธมันอีกหรือ');
INSERT INTO "king_fahad_thai" VALUES(21,51,'21:51','และโดยแน่นอน เราได้ให้ความเฉลียวฉลาดแก่อิบรอฮีม แต่ครั้งก่อน โดยที่เรารู้จักเขาดี');
INSERT INTO "king_fahad_thai" VALUES(21,52,'21:52','ขณะที่เขากล่าวแก่บิดาของเขาและกลุ่มชนของเขาว่า “รูปปั้นอะไรกันนี่ที่พวกท่านเฝ้าบูชากัน ”');
INSERT INTO "king_fahad_thai" VALUES(21,53,'21:53','พวกเขากล่าวว่า “เราได้พบเห็นบรรพบุรุษของเรา เป็นผู้สักการะบูชามันก่อน”');
INSERT INTO "king_fahad_thai" VALUES(21,54,'21:54','เขากล่าวว่า “โดยแน่นอน พวกท่านและบรรพบุรุษของพวกท่าน อยู่ในการหลงผิดอย่างชัดแจ้ง”');
INSERT INTO "king_fahad_thai" VALUES(21,55,'21:55','พวกเขากล่าวว่า “ท่านได้นําความจริงมาเสนอแก่เรา หรือท่านเป็นแต่เพียงคนหนึ่งในพวกล้อเล่น”');
INSERT INTO "king_fahad_thai" VALUES(21,56,'21:56','เขากล่าวว่า “แต่ที่แท้จริงพระเจ้าของพวกท่าน คือพระเจ้าแห่งชั้นฟ้าทั้งหลายและแผ่นดิน ซึ่งพระองค์ทรงเนรมิตมัน และฉันเป็นผู้หนึ่งในหมู่ผู้เป็นพยานต่อการณ์นี้');
INSERT INTO "king_fahad_thai" VALUES(21,57,'21:57','“และขอสาบานด้วยพระนามของอัลลอฮ์ แท้จริง ฉันจะวางแผนต่อต้านรูปปั้นทั้งหลายของพวกท่าน หลังจากที่พวกท่านผินหลังกลับออกไป”');
INSERT INTO "king_fahad_thai" VALUES(21,58,'21:58','ดังนั้น เขาได้ทําให้มันแหลกลาญ เหลือไว้เพียงรูปปั้นตัวใหญ่สําหรับพวกเขา หวังว่าพวกเขาจะได้กลับไปสอบถามมัน');
INSERT INTO "king_fahad_thai" VALUES(21,59,'21:59','พวกเขากล่าวว่า “ใครกระทําเช่นนี้กับพระเจ้าของเรา แท้จริง เขาผู้นั้นอยู่ในหมู่อธรรมอย่างแน่นอน”');
INSERT INTO "king_fahad_thai" VALUES(21,60,'21:60','พวกเขากล่าวว่า “เราได้ยินเด็กหนุ่มคนหนึ่งกล่าวตําหนิรูปปั้นเหล่านี้ เขามีชื่อว่าอิบรอฮีม”');
INSERT INTO "king_fahad_thai" VALUES(21,61,'21:61','พวกเขากล่าวว่า “พวกท่านจงนําเขามาท่ามกลางสายตาของประชาชน หวังว่าเขาทั้งหลายจะได้เป็นพยาน”');
INSERT INTO "king_fahad_thai" VALUES(21,62,'21:62','พวกเขากล่าวว่า “เจ้าเป็นผู้กระทําเช่นนี้ต่อพระเจ้าเหล่านั้นของเรากระนั้นหรือ อิบรอฮีมเอ๋ย!W');
INSERT INTO "king_fahad_thai" VALUES(21,63,'21:63','เขากล่าวว่า “แต่ว่าพระเจ้าตัวใหญ่ของพวกมันนี้ต่างหากเป็นผู้กระทํามัน พวกท่านจงถามพระเจ้าเหล่านั้นซิ พวกมันพูดได้”');
INSERT INTO "king_fahad_thai" VALUES(21,64,'21:64','ดังนั้น พวกเขาก็กลับมาคิดถึงตัวของพวกเขาเอง แล้วกล่าวขึ้นว่า”แท้จริงพวกท่านนั่นแหละเป็นผู้อธรรม”');
INSERT INTO "king_fahad_thai" VALUES(21,65,'21:65','ครั้นแล้วศีรษะของพวกเขาก็ก้มลงมา(อยู่ในสภาพคอตก) แล้วกล่าวว่า “ท่าน ก็รู้ดีอยู่แล้วว่ารูปปั้นเหล่านั้นพูดไม่ได้”');
INSERT INTO "king_fahad_thai" VALUES(21,66,'21:66','เขากล่าวว่า”พวกท่านเคารพภักดีสิ่งอื่นนอกจากอัลลอฮ์ ที่มันไม่ให้คุณแก่พวกท่านและไม่ให้โทษแก่พวกท่านแต่อย่างใดเลย กระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(21,67,'21:67','“เป็นที่น่ารังเกียจแก่พวกท่าน และสิ่งที่พวกท่านเคารพบูชาอื่นจากอัลลอฮ์ พวกท่านไม่มีสติปัญญาหรือ?”');
INSERT INTO "king_fahad_thai" VALUES(21,68,'21:68','พวกเขากล่าวว่า “จงเผาเขาเสีย” และจงช่วยเหลือพระเจ้าทั้งหลายของพวกท่าน หากพวกท่านจะกระทําเช่นนั้น”');
INSERT INTO "king_fahad_thai" VALUES(21,69,'21:69','เรา (อัลลอฮ์) กล่าวว่า “ไฟเอ๋ย ! จงเย็นลง และให้ความปลอดภัยแก่อิบรอฮีมเถิด”');
INSERT INTO "king_fahad_thai" VALUES(21,70,'21:70','และพวกเขาปราถนาที่จะวางแผนร้ายแก่เขา แต่เราได้ทําให้พวกเขาประสบกับความสูญเสียมากยิ่งกว่า');
INSERT INTO "king_fahad_thai" VALUES(21,71,'21:71','และเราได้ให้เขา (อิบรอฮีม) และลูฏ (หลายชาย-ลูกของพี่ชาย) รอดพ้นไปสู่แผ่นดินซึ่งเราได้ให้มีความจําเริญอุดมสมบูรณ์ในแผ่นดินนั้นแก่บรรดาชาติต่าง ๆ');
INSERT INTO "king_fahad_thai" VALUES(21,72,'21:72','และเราได้ให้บุตรชื่ออิสฮากแก่เขา และยะอ์กูบ (หลาน) เป็นการเพิ่มพูน และทั้งหมดนั้นเราได้ให้เป็นคนดีมีคุณธรรม');
INSERT INTO "king_fahad_thai" VALUES(21,73,'21:73','และเราได้แต่งตั้งพวกเขาให้เป็นผู้นําเพื่อชี้แนะแนวทางที่ถูกต้องโดยคําสั่งของเรา และเราได้วะฮีแก่พวกเขาให้ปฏิบัติความดี และธํารงการละหมาด แล้วบริจาคทานซะกาต และพวกเขาก็เป็นผู้เคารพภักดีต่อเราเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(21,74,'21:74','และลุฏนั้นเราได้ให้การเป็นนะบี และวิชาความรู้แก่เขา และเราได้ให้เขารอดพ้นจากหมู่บ้านนั้น ซึ่งชาวบ้านได้กระทําความชั่ว แท้จริงพวกเขาเป็นหมู่ชนที่ชั่วช้าและฝ่าฝืน');
INSERT INTO "king_fahad_thai" VALUES(21,75,'21:75','และเราได้ให้เขาเข้าอยู่ในความเมตตาของเรา แท้จริงเขาเป็นคนหนึ่งในหมู่คนดี');
INSERT INTO "king_fahad_thai" VALUES(21,76,'21:76','และจงรําลึกถึงเรื่องราวของนูห์ เมื่อเขาได้ร้องเรียน (ต่ออัลลอฮ์) ก่อนหน้าหนี้ แล้วเราได้ตอบรับการร้องเรียกแก่เขา และเราได้ช่วยให้เขาและพรรคพวกของเขา รอดพ้นจากความทุกข์ระทมอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(21,77,'21:77','และเราได้ช่วยเหลือเขาให้รอดพ้นจากหมู่ชนที่ปฏิเสธต่อโองการของเรา แท้จริงพวกเขาเป็นหมู่ชนที่ชั่วช้า แล้วเราได้ให้พวกเขาทั้งหมดจมน้ําตาย');
INSERT INTO "king_fahad_thai" VALUES(21,78,'21:78','และจงรําลึกถึงเรื่องราวของดาวูดและสุลัยมาน เมื่อเขาทั้งสองได้ตัดสินใจเรื่องไร่นาเมื่อฝูงแกะของชนหมุ่หนึ่งได้หลบเข้าไปกินพืชในเวลากลางคืน และเราเป็นพยานต่อการตัดสินของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(21,79,'21:79','ดังนั้น เราได้ดลใจให้สุลัยมานเข้าใจการตัดสินนั้น และเราได้ให้ความเฉลียวฉลาดและวิชาความรู้ที่หลักแหลมแก่แต่ละคน และเราได้ทําให้ภูเขาและนกแซ่ซร้องสดุดีร่วมกับดาวูด และเราเป็นผู้กระทําสิ่งเหล่านี้');
INSERT INTO "king_fahad_thai" VALUES(21,80,'21:80','และเราได้สอนเขาให้รู้การทําเสื้อเกราะแก่พวกเจ้า เพื่อป้องกันเจ้าจากการรบพุ่งกัน แล้วพวกเจ้าจะเป็นผู้กตัญญูขอบคุณบ้างไหม ?');
INSERT INTO "king_fahad_thai" VALUES(21,81,'21:81','และสําหรับสุลัยมาน เราได้ทําให้ลมกลายเป็นพายุ ตามคําบัญชาของเขา ไปยังดินแดนซึ่งเราได้ให้ความจําเริญ ณ ที่นั้น และเราเป็นผู้รอบรู้ทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(21,82,'21:82','และเราได้ให้ชัยฎอนบางตัวดําน้ําให้สุลัยมาน และพวกเขาทํางานอื่นจากนั้น และเราเป็นผู้คุ้มกันรักษาพวกเขาเหล่านั้น');
INSERT INTO "king_fahad_thai" VALUES(21,83,'21:83','และจงรําลึกถึงเรื่องราวของอัยยูบ เมื่อเขาได้ร้องเรียนพระเจ้าของเขาว่า “แท้จริงข้าพระองค์นั้น ความทุกข์ยากได้ประสบแก่ข้าพระองค์และพระองค์เท่านั้นเป็นผู้ทรงเมตตายิ่ง ในหมู่ผู้เมตตาทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(21,84,'21:84','ดังนั้น เราได้ตอบรับการร้องเรียนของเขาแล้วเราได้ปลดเปลื้องสิ่งที่เป็นความทุกข์ยากแก่เขา และเราได้ให้ครอบครัวของเขาแก่เขา และเช่นเดียวกับที่เขาได้เคยมีมาก่อน (เช่น บุตรหลานและพวกพ้อง) เป็นความเมตตาจากเรา และเป็นข้อตักเตือนแก่บรรดาผู้ที่เคารพภักดี');
INSERT INTO "king_fahad_thai" VALUES(21,85,'21:85','และจงรําลึกถึงเรื่องราวของอิสมาอีลและอิดรีส และซัลกิฟลิ แต่ละคนอยุ่ในหมู่ผู้อดทนขันติ');
INSERT INTO "king_fahad_thai" VALUES(21,86,'21:86','และเราได้ให้พวกเขาเข้าอยู่ในความเมตตาของเรา แท้จริงพวกเขาอยู่ในหมุ่คนดีมีคุณธรรม');
INSERT INTO "king_fahad_thai" VALUES(21,87,'21:87','และจงรําลึกถึงเรื่องราวของซันนูน (นะบียูนุส) เมื่อเขาจากไปด้วยความโกรธพรรคพวกของเขา แล้วเขาคิดว่าเราจะไม่ทําให้เขาได้รับความลําบาก แล้วเขาก็ร้องเรียนท่านกลางความมืดทึบทะมึนว่า “ไม่มีพระเจ้าอื่นใดนอกจากกพระองค์ท่าน มหาบริสุทธิ์แห่งพระองค์ท่าน แท้จริงข้าพระองค์เป็นผู้หนึ่งในหมู่ผู้อธรรมทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(21,88,'21:88','ดังนั้นเราได้ตอบรับการร้องเรียนของเขาและเราได้ช่วยให้เขารอดพ้นจากความทุกข์ระทมและเช่นเดียวกันนี้ เราช่วยบรรดาผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(21,89,'21:89','และจงรําลึกถึงเรื่องราวของซะกะรียาเมื่อเขาไร้องเรียนพระเจ้าของเขาว่า “ข้าแต่พระเจ้าของข้าพระองค์ของพระองค์ทรงอย่าปล่อยให้ข้าพระองค์อยู่อย่างเดียวดาย และพระองค์ท่านเท่านั้นเป็นผู้สืบมรดกอันดียิ่ง”');
INSERT INTO "king_fahad_thai" VALUES(21,90,'21:90','ดังนั้นเราได้ตอบรับการร้องเรียนแก่เขา และเราได้ประทานบุตรแก่เขาคือยะฮฺยา และเราได้ปรับปรุงแก้ไขภริยาของเขาให้เป็นปกติแก่เขา แท้จริงพวกเขา แข่งขันกันในการทําความดีและพวกเขาวิงวอนเราด้วยความหวังในการลงโทษของเรา และพวกเขาเป็นผู้ถ่อมตัวเกรงกลัวต่อเรา');
INSERT INTO "king_fahad_thai" VALUES(21,91,'21:91','และจงรําลึกถึงสตรีที่รักษาความบริสุทธิ์ของนางเอาไว้ แล้วเราได้เป่าวิญญาณของเราเข้าไปในนาง และเราได้ทําให้นางและบุตรของนางเป็นสัญญาณหนึ่งแก่มวลมนุษย์');
INSERT INTO "king_fahad_thai" VALUES(21,92,'21:92','แท้จริง นี่คือประชาชาติของพวกเจ้า ซึ่งเป็นประชาชาติเดียวกัน และข้าเป็นพระเจ้าของพวกเจ้า ดังนั้นพวกเจ้าจงเคารพภักดีข้าเถิด');
INSERT INTO "king_fahad_thai" VALUES(21,93,'21:93','และพวกเขาได้แตกแยกกันในเรื่องของศาสนาระหว่าเขากันเอง ทั้งหมดนี้พวกเขาจะเป็นผู้กลับไปหาเรา');
INSERT INTO "king_fahad_thai" VALUES(21,94,'21:94','ดังนั้นผู้ใดประกอบกรราดีทั้งหลาย โดยที่เขาเป็นผู้ศรัทธา สําหรับการอุตสาหะวิริยะของเขาจะไม่ถูกปฏิเสธ และแท้จริงเราเป็นผู้บันทึกความดีสําหรับเขา');
INSERT INTO "king_fahad_thai" VALUES(21,95,'21:95','และเป็นที่ห้ามแก่ชาวเมือง ที่เราได้ทําลายเมืองนั้นแล้วว่า แน่นอนพวกเขาจะไม่กลับฟื้นคืนชีพขึ้นมาอีก');
INSERT INTO "king_fahad_thai" VALUES(21,96,'21:96','จนกระทั่งเมื่อยะอ์ญูจญ์ และมะอ์ญูจญ์ถูกปล่อยออกมาจากกําแพง และพวกเขาจะหลั่งไหลกันลงมาจากทุกทิศทาง');
INSERT INTO "king_fahad_thai" VALUES(21,97,'21:97','และเมื่อสัญญาแห่งความจริงได้ใกล้เข้ามา ขณะนั้นเรื่องของบรรดาผู้ปฏิเสธศรัทธาคือสายตาของบรรดาผู้ปฏิเสธจะจ้องเขม็งแล้ว กล่าวว่า “โอ้ความหายนะของเรา ! แน่นอนยิ่งเราอยู่ในความหลงลืม (จากทางหลับที่น่ากลัวนี้) ยิ่งกว่านั้นเรายังเป็นผู้อธรรมอีกด้วย');
INSERT INTO "king_fahad_thai" VALUES(21,98,'21:98','แท้จริงพวกเจ้า (มุชริกีน) และสิ่งที่พวกเจ้าเคารพบูชาอื่นจากอัลลอฮ์นั้น ทั้งหมดนั้นเป็นเชื้อเพลิงของนรก โดยพวกเจ้าจะเข้าไปอยู่ในนั้น');
INSERT INTO "king_fahad_thai" VALUES(21,99,'21:99','หากมันเหล่านั้นเป็นพระเจ้าจริงแล้วมันจะไม่เข้าไปอยู่ในนั้น และทั้งหมดจะเข้าอยู่ในนั้นอย่างถาวร');
INSERT INTO "king_fahad_thai" VALUES(21,100,'21:100','สําหรับพวกเขาในนรกนั้นมีแต่เสียงครวญครางและพวกเขาในนรกนั้นจะไม่ได้ยินมันเลย');
INSERT INTO "king_fahad_thai" VALUES(21,101,'21:101','แท้จริงบรรดาผู้ที่ความดีจากเราได้ประสบแก่พวกเขามาก่อนนั้น ชนเหล่านั้นเป็นผู้ที่อยู่ห่างไกลจากมัน');
INSERT INTO "king_fahad_thai" VALUES(21,102,'21:102','พวกเขาจะไม่ได้ยินแม้แต่เสียงแผ่วเบาของมันและพวกเขาจะอยู่ในสวนสวรรค์อย่างถาวรตามที่จิตใจของพวกเขาปรารถนา');
INSERT INTO "king_fahad_thai" VALUES(21,103,'21:103','ความตื่นตระหนกอันยิ่งใหญ่จะไม่ทําให้พวกเขาเศร้าโศก และมะลาอิกะฮ์จะพบพวกเขา แล้วกล่าวว่า “นี่คือวันของพวกท่านซึ่งพวกท่านได้ถูกสัญญาไว้”');
INSERT INTO "king_fahad_thai" VALUES(21,104,'21:104','วันซึ่งเราจะม้วนชั้นฟ้า ประหนึ่งการม้วนแผ่นกระดาษสําหรับการบันทึก ดังเช่นที่เราได้เริ่มให้มีการบังเกิดครั้งแรก เราจะให้มันกลับเป็นขั้นมาอีก เป็นสัญญาผูกพันกับเรา แท้จริงเราเป็นผู้กระทําอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(21,105,'21:105','และแท้จริงนั้นเราได้บันทึกไว้ในคัมภีร์อัซซะบูร หลังจากที่เราได้บันทึกไว้ในลูห์มะห์ฟูซว่า แผ่นดินนั้นปวงบ่าวของเราที่ดีมีคุณธรรมจะเป็นผู้สืบมรดกมัน');
INSERT INTO "king_fahad_thai" VALUES(21,106,'21:106','แท้จริงในการกล่าวไว้เช่นนี้ เป็นการเพียงพอสําหรับหมู่ชนที่เคารพภักดีต่ออัลลอฮ์');
INSERT INTO "king_fahad_thai" VALUES(21,107,'21:107','และเรามิได้ส่งเจ้ามาเพื่ออื่นใดนอกจากเพื่อเป็นความเมตตาแก่ประชาชาติทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(21,108,'21:108','จงกล่าวเถิดมุฮัมมัด “แท้จริงฉันได้รับวะฮีมา ให้ประกาศว่า แท้จริงพระเจ้าของพวกท่านนั้นคือพระเจ้าองค์เดียว ดังนั้นพวกท่านยังมิยอมนอนน้อมอีกหรือ?”');
INSERT INTO "king_fahad_thai" VALUES(21,109,'21:109','หากพวกเขาผินหลังให้ ก็จงกล่าวเถิดมุฮัมมัดว่า “ฉันได้ประกาศแจ้งให้พวกท่านทราบแล้วโดยถ้วนหน้า และฉันไม่รู้ว่าสิ่งที่พวกท่านถูกสัญญาไว้นั้น จะอยู่ใกล้หรือไกล”');
INSERT INTO "king_fahad_thai" VALUES(21,110,'21:110','“แท้จริงพระองค์ทรงรอบรู้คําพูดที่เปิดเผยและทรงรอบรู้สิ่งที่พวกท่านปิดบังไว้”');
INSERT INTO "king_fahad_thai" VALUES(21,111,'21:111','“และฉันก็ไม่รู้ หวังว่าการประวิงเวลา อาจจะเป็นการทดสอบแก่พวกท่าน และอาจจะเป็นการร่าเริงชั่วขณะหนึ่ง ”');
INSERT INTO "king_fahad_thai" VALUES(21,112,'21:112','เขา (มุฮัมมัด) กล่าวว่า “ข้าแต่พระผู้เป็นเจ้าของข้าพระองค์ ขอพระองค์ทรงชี้ขาดตัดสินแก่เราด้วยความจริง และพระเจ้าของเรา คือ พระผู้ทรงกรุณา ปรานี ผู้ทรงถูกขอความช่วยเหลือต่อสิ่งที่พวกท่านกล่าวหา”');
INSERT INTO "king_fahad_thai" VALUES(22,1,'22:1','โอ้ มหาชนเอ๋ย ! พวกเจ้าจงยําเกรง พระเจ้าของพวกเจ้าเถิด เพราะแท้จริง การสั่นสะเทือนของวันอวสานนั้น เป็นสิ่งที่ร้ายแรงยิ่งนัก');
INSERT INTO "king_fahad_thai" VALUES(22,2,'22:2','วันที่พวกเจ้าจะเห็นมันคือ แม่นมทุกคนจะตกตะลึงลืมสิ่งที่นางกําลังให้นมแก่ลูกอ่อน และหญิงตั้งครรภ์ทุกคนจะคลอดลูกที่อยู่ในครรภ์ของนางออกมา และเจ้าจะเห็นมนุษย์อยู่ในสภาพมึนเมา ทั้ง ๆ ที่พวกเขามิได้เมา และแต่ว่าการลงโทษของอัลลอฮ์นั้นรุนแรงยิ่งนัก');
INSERT INTO "king_fahad_thai" VALUES(22,3,'22:3','และในหมู่มนุษย์บางคนมีผู้โต้เถียงในเรื่องของอัลลอฮ์โดยปราศจากความรู้ และเขาจะปฏิบัติตามชัยฎอนทุกตัวที่ดื้อรั้น');
INSERT INTO "king_fahad_thai" VALUES(22,4,'22:4','ได้มีกําหนดไว้กับมันว่า แท้จริงผู้ใดยึดมันเป็นมิตรสหายแล้ว แน่นอน มันจะทําให้เขาหลงทางและจะนําเขาไปสู่การลงโทษที่มีเปลวลุกโชน');
INSERT INTO "king_fahad_thai" VALUES(22,5,'22:5','โอ้ มนุษย์เอ๋ย ! หากพวกเจ้ายังอยู่ในการสงสัยแคลงใจ เกี่ยวกับการฟื้นคืนชีพแล้วไซร้ แท้จริงเราได้บังเกิดพวกเจ้าจากดิน แล้วจากเชื้ออสุจิ แล้วจากก้อนเนื้อ ทั้งที่เป็นรูปร่างที่สมบูรณ์ และไม่เป็นรูปร่างที่สมบูรณ์ เพื่อเราจะได้ชี้แจงเคล็ดลับแห่งเดชานุภาพ แก่พวกเจ้า และเราให้การตั้งครรภ์เป็นที่แน่นอนอยู่ในมดลูกตามที่ประสงค์ จนถึงเวลาที่กําหนดไว้แล้วเราให้พวกเจ้าคลอดออกมาเป็นทารก แล้วเพื่อพวกเจ้าจะได้บรรลุสู่วัยฉกรรจ์ของพวกเจ้า และในหมู่พวกเจ้ามีผู้เสียชีวิตในวัยหนุ่ม และในหมู่พวกเจ้ามีผู้ถูกนํากลับสู่วัยต่ําต้อย วัยชรา เพื่อเขาจะไม่รู้อะไรเลยหลังจากมีความรู้ และเจ้าจะเห็นแผ่นดินแห้งแล้ง ครั้นเมื่อเราได้หลั่งน้ําฝนลงมาบนมัน มันก็จะเคลื่อนไหวขยายตัวและพองตัวและงอกเงยออกมาเป็นพืช ทุกอย่างเป็นคู่ ๆ ดูสวยงาม');
INSERT INTO "king_fahad_thai" VALUES(22,6,'22:6','นั่นก็เพราะว่า แท้จริงอัลลอฮ์นั้นพระองค์คือผู้ทรงสัจจะ และแท้จริงพระองค์ทรงให้ผู้ตายมีชีวิตขึ้น และแท้จริงพระองค์ทรงเดชานุภาพเหนือทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(22,7,'22:7','และแท้จริงวันอวสานจะมาถึงอย่างแน่นอน ปราศจากข้อสงสัยในมัน และแท้จริงอัลลอฮ์จะทรงให้ผู้ที่อยู่ในสุสานฟื้นคืนชีพขึ้นมา');
INSERT INTO "king_fahad_thai" VALUES(22,8,'22:8','และในหมู่มนุษย์บางคนมีผู้โต้เถียงในเรื่องของอัลลอฮ์ โดยปราศจากความรู้และโดยปราศจากแนวทางที่ถูกต้อง และปราศจากคัมภีร์ที่มีหลักฐานชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(22,9,'22:9','เขาจะเอี้ยวตัวอของเขาไปอย่างหยิ่งยะโสเพื่อให้ผู้คนหลงจากทางอัลลอฮ์ สําหรับเขาจะไดรับความอัปยศในโลกนี้และเราจะให้เขาลิ้มรสการลงโทษที่มีไฟลุกไหม้ในวันกิยามะฮ์');
INSERT INTO "king_fahad_thai" VALUES(22,10,'22:10','นั่นเพราะว่า มือทั้งสองของเจ้าได้ก่อกรรมทําไว้ และแท้จริงอัลลอฮ์นั้นจะไม่ทรงอธรรมต่อปวงบ่าว');
INSERT INTO "king_fahad_thai" VALUES(22,11,'22:11','และในหมู่มนุษย์บางคน มีผู้เคารพภักดีต่ออัลลอฮฺบนขอบทางของศาสนา หากความดีประสบแก่เขา เขาก็พออกพอใจต่อสิ่งนั้น หากความทุกข์ยากประสบแก่เขา เขาก็จะผินหน้าของเขากลับสู่การปฏิเสธ เขาขาดทุนทั้งในโลกนี้และในโลกหน้า นั่นคือการขาดทุนอย่างชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(22,12,'22:12','เขาวิงวอนสิ่งอื่นจากอัลลอฮ์ ซึ่งมันไม่ให้โทษแก่เขาและมันก็ไม่ให้คุณแก่เขา นั่นคือการหลงผิดที่ไกลลิบ');
INSERT INTO "king_fahad_thai" VALUES(22,13,'22:13','เขาวิงวอนต่อผู้ที่โทษของมันจะมีขึ้นเร็วกว่าคุณประโยชน์ของมัน แน่นอนมันเป็นผู้คุ้มครองที่ชั่วช้าแท้ ๆ และมันเป็นสหายที่ชั่วช้าจริง ๆ');
INSERT INTO "king_fahad_thai" VALUES(22,14,'22:14','แท้จริงอัลลอฮ์ทรงให้บรรดาผู้ศรัทธาและผู้กระทําความดีทั้งหลาย เข้าสวนสวรรค์หลากหลาย ณ เบื้องล่างของมันมีลําน้ําหลายสายไหลผ่านแท้จริงอัลลอฮ์ทรงกระทําสิ่งที่พระองค์ทรงประสงค์');
INSERT INTO "king_fahad_thai" VALUES(22,15,'22:15','ผู้ใดคิดว่าอัลลอฮฺจะไม่ทรงช่วยเหลือเขา (มุฮัมมัด) ทั้งในโลกนี้และโลกหน้า ก็จงให้เขาผู้นั้นต่อเชือกขึ้นสู่ท้องฟ้า แล้วให้เขาตัดมันออก (ผูกคอตาย) แล้วให้เขาเฝ้าดูว่าแผนการณ์ของเขา จะทําให้สิ่งที่เขาเคียดแค้นหมดสิ้นไปไหม ?');
INSERT INTO "king_fahad_thai" VALUES(22,16,'22:16','และเช่นนั้นแหละ เราได้ให้อัลกุรอานลงมาเป็นโองการทั้งหลายที่ชัดแจ้ง และอัลลอฮ์นั้นทรงชี้แนะทางให้แก่ผู้ที่พระองค์ทรงประสงค์');
INSERT INTO "king_fahad_thai" VALUES(22,17,'22:17','แท้จริงบรรดาผู้ศรัทธา และบรรดาชาวยิว และพวกศอบิอีน และพวกนะศอรอ และพวกบูชาไฟ และบรรดาผู้ตั้งภาคี แท้จริงอัลลอฮ์จะทรงตัดสินใจในระหว่างพวกเขาในวันกิยามะฮ์ แท้จริงอัลลอฮ์ทรงเป็นพยานต่อทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(22,18,'22:18','เจ้ามิได้เห็นหรือว่า แท้จริงอัลลอฮ์เท่านั้นผู้ที่อยู่ในชั้นฟ้าทั้งหลาย และผู้ที่อยู่ในแผ่นดิน และดวงอาทิตย์ และดวงจันทร์และดวงดาวทั้งหลาย และภูเขาทั้งหลาย และต้นไม้ และสัตว์ ทั้งหลาย และส่วนมากของมนุษย์ ต่างก็สุญูดนอบน้อมต่อพระองค์ แต่ส่วนมากการลงโทษจะเหมาะสมคู่ควรแก่เขา และผู้ใดที่อัลลอฮ์ทรงทําให้อัปยศก็จะไม่มีผู้ใดให้เกียรติเขาแท้จริง อัลลอฮ์นั้นทรงกระทําสิ่งที่พระองค์ทรงสงค์');
INSERT INTO "king_fahad_thai" VALUES(22,19,'22:19','ผู้โต้เถียงทั้งสองฝ่ายนี้ต่างก็โต้เถียงกันเกี่ยวกับพระเจ้าของพวกเขา สําหรับบรรดาผู้ปฏิเสธศรัทธานั้น มีอาภรณ์ที่ทําด้วยไฟถูกตัดไว้สําหรับพวกเขา น้ําร้อนเดือดจะถูกเทราดลงบนศรีษะของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(22,20,'22:20','สิ่งที่อยู่ในท้องของพวกเขาและหนังจะถูกละลายด้วยน้ําร้อนเดือดร้อน');
INSERT INTO "king_fahad_thai" VALUES(22,21,'22:21','และสําหรับพวกเขา จะถูกทุบเขาฆ้อนเหล็ก');
INSERT INTO "king_fahad_thai" VALUES(22,22,'22:22','ทุกครั้งที่พวกเขาต้องการจะออกไปจากนรกเนื่องจากความทุกข์ยาก พวกเขาก็ถูกให้กลับไปในนั้นอีก (มีเสียงกล่าวแก่พวกเขาว่า) “พวกเจ้าจงลิ้มรสการลงโทษที่มีไฟคุไหม้”');
INSERT INTO "king_fahad_thai" VALUES(22,23,'22:23','แท้จริง อัลลอฮ์จะทรงให้บรรดาผู้ศรัทธาและทําความดีทั้งหลาย เข้าสวนสวรรค์ทั้งหลากหลาย ณ เบื้องล่างของมันมีลําน้ําหลายสายไหลผ่านในนั้น พวกเขาจะถูกสวมใส่กําไลมือทีทําจากทองคําและไข่มุก และเสื้อผ้าของพวกเขาที่สวมใส่ในนั้นก็เป็นผ้าไหม');
INSERT INTO "king_fahad_thai" VALUES(22,24,'22:24','และพวกเขาจะถูกนําสู่คําพูดที่ดีมีประโยชน์ และจะถูกนําสู่ทางที่ได้รับการสรรเสริญ คือ สวนสวรรค์');
INSERT INTO "king_fahad_thai" VALUES(22,25,'22:25','แท้จริงบรรดาผู้ปฏิเสธศรัทธา และขัดขวางทางอัลลอฮ์ และมัสยิดอัลหะรอม ซึ่งเราได้ทํามันไว้ สําหรับมนุษย์อย่างเท่าเทียมกันทั้งผู้ที่พํานักอยู่ในนั้นและที่มาจากภายนอก และถ้าผู้ใดปรารถนาที่จะออกนอกทางด้วยความอธรรม เราก็จะให้เขาลิ้มรสการลงโทษอย่างเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(22,26,'22:26','และจงรําลึกเมื่อเราได้ชี้แนะสถานอัลบัยต์ แก่อิบรอฮีมว่า “เจ้าอย่าตั้งภาคีต่อข้าแต่อย่างใด และจงทําบ้านของข้าให้สะอาด สําหรับผู้มาเวียนรอบ ผู้ยืนละหมาด ผู้รุกัวะ และผู้สุญูด');
INSERT INTO "king_fahad_thai" VALUES(22,27,'22:27','“และจงประกาศแก่มนุษย์ทั่วไปเพื่อการทําฮัจญ์ พวกเขาจะมาหาเจ้าโดยทางเท้า และโดยทางอูฐเพรียวทุกตัว จะมาจากทางไกลทุกทิศทาง');
INSERT INTO "king_fahad_thai" VALUES(22,28,'22:28','“เพื่อพวกเขาจะได้มาร่วม เป็นพยานในผลประโยชน์ของพวกเขา และกล่าวพระนามอัลลอฮ์ในวันที่รู้กันอยู่แล้ว คือวันเชือด ตามที่พระองค์ทรงประทานปัจจัยยังชีพแก่พวกเขาจากสัตว์สี่เท้า ดังนั้นพวกเจ้าจงกินเนื้อมัน และจงให้อาหารแก่ผู้ยากจนขัดสน');
INSERT INTO "king_fahad_thai" VALUES(22,29,'22:29','“แล้วให้พวกเขาชําระทําความสะอาด้วยการโกนหรือตัด และให้พวกเขาทําให้ครบถ้วนในเรื่องบนบานทั้งหลายของพวกเขา(เป็นการจงรักภักดีต่ออัลลอฮ์) และจงให้พวกเขาฎอวาฟ (เดินเวียน) รอบบ้านอันเก่าแก่”');
INSERT INTO "king_fahad_thai" VALUES(22,30,'22:30','เช่นนั้นแหละ และผู้ใดให้เกียรติต่อข้อห้ามทั้งหลายของอัลลอฮ์ มันก็เป็นการดีแก่เขา ณ ที่พระเจ้าของเขา และปศุสัตว์ทั้งหลายได้เป็นที่อนุมัติแก่พวกเจ้าเว้นแต่บางสิ่งที่ถูกบอกกล่าวไว้แก่พวกเจ้า ดังนั้น พวกเจ้าจงปลีกตัวให้พ้นจากความโสมม ซึ่งหมายถึงเจว็ดทั้งหลาย และจงออกห่างจากการกล่าวคําเท็จ');
INSERT INTO "king_fahad_thai" VALUES(22,31,'22:31','โดยเป็นผู้ยึดมั่นความเป็นจริง เพื่ออัลลอฮ์ไม่เป็นผู้ตั้งภาคีใด ๆ ต่อพระองค์ และผู้ใดตั้งภาคีต่ออัลลอฮ์ เสมือนว่าเขาร่วงลงมาจากชั้นฟ้า แล้วนกก็บินเฉี่ยวเอาเขาไป หรือลมได้พัดพาเขาไปยังดินแดนอันไกลโพ้น');
INSERT INTO "king_fahad_thai" VALUES(22,32,'22:32','ฉะนั้น ผู้ใดที่ให้เกียรติแก่พระบัญญัติของอัลลอฮ์ แท้จริงมันเป็นส่วนหนึ่งแห่งการยําเกรงของจิตใจ');
INSERT INTO "king_fahad_thai" VALUES(22,33,'22:33','ในปศุสัตว์เหล่านั้นมีคุณประโยชน์มากหลายสําหรับพวกเจ้า จนถึงเวลาที่ถูกกําหนดไว้ และสถานที่เชือดของมันคือบริเวณบ้านอันเก่าแก่');
INSERT INTO "king_fahad_thai" VALUES(22,34,'22:34','และสําหรับทุ ๆ ประชาชาติเราได้กําหนดสถานที่ทําพิธีกรรม เพื่อพวกเขาจักได้กล่าวพระนามของอัลลอฮ์ ต่อสิ่งที่พระองค์ทรงประทานให้เป็นปัจจัยยังชีพแก่พวกเขา คือสัตว์สี่เท้า (เช่น อูฐ วัว แพะ แกะ) ฉะนั้นพระเจ้าของพวกเจ้าคือพระเจ้าองค์เดียว ดังนั้นสําหรับพระองค์เท่านั้น พวกเจ้าจงนอบน้อมและจงแจ้งข่าวดีแก่บรรดาผุ้จงรักภักดีนอบน้อมถ่อมตนเถิด');
INSERT INTO "king_fahad_thai" VALUES(22,35,'22:35','คือบรรดาผู้ที่เมื่อพระนามของอัลลอฮ์ถูกกล่าวขึ้น หัวใจของพวกเขาหวั่นเกรง และบรรดาผู้อดทนต่อสิ่งที่ประสบกับพวกเขาและบรรดาผู้ดํารงการละหมาด และจากสิ่งที่เราได้ให้เป็นเครื่องยังชีพแก่พวกเขา พวกเขาก็บริจาค');
INSERT INTO "king_fahad_thai" VALUES(22,36,'22:36','และอูฐที่อ้วนพีเราได้กําหนดมันให้มีขึ้นสําหรับพวกเจ้า ถือเป็นส่วนหนึ่งจากบรรดาเครื่องหมายของอัลลอฮ์ เพราะในตัวมันมีของดีสําหรับพวกเจ้า ดังนั้นพวกเจ้าจงกล่าวพระนามของอัลลอฮ์ (เมื่อเวลาเชือด) ขณะที่มันยืน ฉะนั้นเมื่อมันล้มลงนอนตะแคงแล้ว พวกท่านก็จงบริโภคมัน และจงแจกจ่ายเป็นอาหารแก่คนที่ไม่เอ่ยขอ และคนที่เอ่ยขอ เช่นนั้นแหละเราได้ทําให้มันยอมจํานน แก่พวกเจ้า เพื่อพวกเจ้าจักได้ขอบคุณอัลลอฮ์');
INSERT INTO "king_fahad_thai" VALUES(22,37,'22:37','เนื้อของมันและเลือดของมันจะไม่ถึงอัลลอฮ์แต่อย่างใด แต่การยําเกรงของพวกเจ้าจะถึงพระองค์ เช่นนั้นแหละเราได้ทําให้มันยอมจํานนต่อพวกเจ้า เพื่อพวกเจ้าจักได้แซ่ซร้องอัลลอฮ์อย่างเกรรียงไกรต่อการที่พระองค์ทรงชี้แนะแก่พวกเจ้า และจงแจ้งข่าวดีแก่บรรดาผู้ทําความดีเถิด');
INSERT INTO "king_fahad_thai" VALUES(22,38,'22:38','แท้จริงอัลลอฮ์ทรงปกป้องบรรดาผู้ศรัทธาให้พ้นจากศัตรู แท้จริงอัลลอฮ์ไม่ททรงโปรดปรานทุกคนที่ทรยศเนรคุณ');
INSERT INTO "king_fahad_thai" VALUES(22,39,'22:39','สําหรับบรรดาผู้ (ที่ถูกโจมตีนั้น) ได้รับอนุญาตให้ต่อสู้ได้ เพราะพวกเขาถูกข่มเหง และแท้จริงอัลลอฮ์ทรงสามารถที่จะช่วยเหลือพวกเขาได้อย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(22,40,'22:40','บรรดาผู้ที่ถูกขับไล่ออกจากบ้านเรือนของพวกเขา โดยปราศจากความยุติธรรม นอกจากพวกเขากล่าวว่า “อัลลอฮ์คือพระเจ้าของเราเท่านั้น” และหากว่าอัลลอฮ์ทรงขัดขวางมิให้มนุษย์ต่อสู้ซึ่งกันและกันแล้ว บรรดาหอสวด และโบสถ์ (ของพวกคริสต์) และสถานที่สวด (ของพวกยิว) และมัสยิดทั้งหลายที่พระนามของอัลลอฮ์ ถูกกล่าวรําลึกอย่างมากมาย ต้องถูกทําลายอย่างแน่นอน และแน่นอนอัลลอฮ์ จะทรงช่วยเหลือผู้ที่สนับสนุนศาสนาของพระองค์ แท้จริงอัลลอฮ์เป็นผู้ทรงพลัง ผู้ทรงเดชานุภาพอย่างแท้จริง');
INSERT INTO "king_fahad_thai" VALUES(22,41,'22:41','บรรดาผู้ที่เราให้พวกเขามีอํานาจในแผ่นดิน คือบรรดาผู้ที่ดํารงการละหมาด และบริจาคซะกาตและใช้กันให้กระทําความดี และห้ามปรามกันให้ละเว้นความชั่ว และบั้นปลายอของกิจการทั้งหลายย่อมกลับไปหาอัลลอฮ์');
INSERT INTO "king_fahad_thai" VALUES(22,42,'22:42','และหากพวกเขา (มุชริกีนมักกะฮ์) ปฏิเสธไม่ยอมเชื่อฟังเจ้า แม้จริงชนชาติของนูห์และอาาดและษะมูด ได้ปฏิเสธไม่เชื่อฟังนะบีของพวกเขามาก่อนหน้าพวกเขาแล้ว');
INSERT INTO "king_fahad_thai" VALUES(22,43,'22:43','และชนชาติของอิบรอฮีม และชนชาติของลูฎ');
INSERT INTO "king_fahad_thai" VALUES(22,44,'22:44','และชาวมัดยัน และมูซา ก็ได้ถูกปฏิเสธไม่ยอมเชื่อฟังเช่นกัน แต่เราได้ประวิงเวลาให้แก่พวกปฏิเสธศรัทธา และเราก็ได้ลงโทษพวกเขา ดังนั้น เป็นเช่นใดเล่าการลงโทษของเรา');
INSERT INTO "king_fahad_thai" VALUES(22,45,'22:45','ฉะนั้นกี่เมืองมาแล้ว เราได้ทําลายมันโดยที่ชาวเมืองนั้นอธรรม และมันได้พังพาบลงมาและกี่บ่อนน้ําที่ถูกทอดทิ้ง และกี่ปราสาทสูงที่มั่นคง');
INSERT INTO "king_fahad_thai" VALUES(22,46,'22:46','พวกเขามิได้ออกเดินทางไปในแผ่นดินดอกหรือ เพื่อหัวใจจะได้พิจารณาเพื่อพวกเขาเอง หรือมีหูเพื่อสดับฟังมัน เพราะแท้จริงการมองของนัยตานั้นมิได้บอกดอก แต่ว่าหัวใจที่อยู่ในทรวงอกต่างหากที่บอด');
INSERT INTO "king_fahad_thai" VALUES(22,47,'22:47','และพวกเขาเร่งเต้าเจ้า ให้มีการลงโทษแต่ว่าอัลลอฮ์ นั้นจะไม่ทรงผิดสัญญาของพระองค์เป็นอันขาด และแท้จริงวันหนึ่ง ณ ที่พระเจ้าของเจ้านั้นเท่ากับหนึ่งพันปี ตามที่พวกเจ้าคํานวณนับ');
INSERT INTO "king_fahad_thai" VALUES(22,48,'22:48','และกี่เมืองมาแล้ว เราได้ประวิงเวลาการลงโทษมัน โดยที่ชาวเมืองนั้นอธรรม แล้วเราได้ลงโทษทําลายมัน และยังเรานั้นคือทางกลับ');
INSERT INTO "king_fahad_thai" VALUES(22,49,'22:49','จงกล่าวเถิดมุฮัมมัด “โอ้มนุษย์เอ๋ย ! แท้จริงฉันนั้นคือผู้ตักเตือนอันชัดแจ้งแก่พวกท่านเท่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(22,50,'22:50','ดังนั้นบรรดาผู้ศรัทธาและทําความดีทั้งหลาย สําหรับพวกเขานั้นจะได้รับการอภัยโทษและปัจจัยยังชีพอันมีเกียรติ คือสวนสวรรค์');
INSERT INTO "king_fahad_thai" VALUES(22,51,'22:51','“และบรรดาผุ้ที่เพียรพยายามอย่างผู้ไร้ความสามารถเพื่อลบล้างโองการทั้งหลายของเรานั้น ชนเหล่านั้นคือชาวนรก');
INSERT INTO "king_fahad_thai" VALUES(22,52,'22:52','และเรามิได้ส่งร่อซูลคนใด และนะบีคนใดก่อนหน้าเจ้า เว้นแต่ว่าเมื่อเขาหวังตั้งใจ ชัยฏอนก็จะเข้ามายุแหย่ให้หันเหออกจากความหวังตั้งใจของเขา แต่อัลลอฮ์ก็ทรงทําลายล้างสิ่งที่ชัยฏอนยุแหย่ แล้วอัลลอฮ์ ก็ทรงทําให้โองการทั้งหลายของพระองค์มั่นคง และอัลลอฮ์ทรงรอบรู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(22,53,'22:53','เพื่อพระองค์จะทรงทําให้สิ่งที่ชัยฏอนยุแหย่นั้น เป็นการทดสอบสําหรับบรรดาผู้ที่ในจิตใจของพวกเขามีโรค และจิตใจของพวกเขาแข็งกระด้าง และแท้จริงบรรดาผู้อธรรมนั้นอยู่ในการแตกแยกที่ห่างไกล');
INSERT INTO "king_fahad_thai" VALUES(22,54,'22:54','และเพื่อบรรดาผู้รู้จะตระหนักว่า แท้จริงอัลกุรอาน นั้นคือสัจธรรมจากพระเจ้าของเจ้า เพื่อพวกเขาจะได้ศรัทธาต่อมัน (อัลกุรอาน) แล้วจิตใจของพวกเขาจะได้นอบน้อมต่อมัน (อัลกุรอาน) และแท้จริงอัลลอฮ์ทรงเเป็นผู้ชี้แนะบรรดาผู้ศรัทธาสู่แนวทางอันเที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(22,55,'22:55','และบรรดาผู้ปฏิเสธศรัทธานั้นยังคงอยู่ในการสงสัยต่อัลกุรอานจนกระทั่งวันอวสานเกิดขึ้นแก่พวกเขาโดยฉับพลัน หรือการลงโทษแห่งวันกิยามะฮ์ จะเกิดขึ้นแก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(22,56,'22:56','อํานาจในวันนั้นเป็นของอัลลอฮ์ พระองค์ทรงตัดสินระหว่างพวกเขา ดังนั้นบรรดาผู้ศรัทธาและกระทําความดีทั้งหลายจะอยู่ในสวนสวรรค์หลากหลายแห่งควงามโปรดปราน');
INSERT INTO "king_fahad_thai" VALUES(22,57,'22:57','ส่วนบรรดาผู้ปฏิเสธศรัทธา และไม่เชื่อฟังโองการทั้งหลายของเรานั้น ชนเหล่านั้นพวกเขาจะได้รับการลงโทษอย่างอัปยศ');
INSERT INTO "king_fahad_thai" VALUES(22,58,'22:58','และบรรดาผู้อพยพในทางของอัลลอฮ์แล้วพวกเขาถูกหรือตาย แน่นอนอัลลอฮ์ จะทรงประทานปัจจัยยังชีพที่ดี และแท้จริงอัลลอฮ์ นั้นพระองค์ทรงเป็นผู้ดีเลิศแห่งบรรดาผู้ประทานปัจจัยยังชีพ');
INSERT INTO "king_fahad_thai" VALUES(22,59,'22:59','พระองค์จะทรงให้พวกเขาเข้าไปในสถานที่ที่พวกเขาพอใจ คือสวนสวรรค์ และแท้จริงอัลลอฮ์เป็นผู้ทรงรอบรู้ ผู้ทรงขันติ');
INSERT INTO "king_fahad_thai" VALUES(22,60,'22:60','เช่นนั้นแหละ และผู้ใดตอบโต้เยี่ยงที่เขาถูกรังแก แล้วเขาก็ยังถูกข่มเหงอีก อัลลอฮ์จะทรงช่วยเหลือเขาอย่างแน่นอนแท้จริงอัลลอฮ์เป็นผู้ทรงอภัยยิ่ง ผู้ทรงยกโทษ');
INSERT INTO "king_fahad_thai" VALUES(22,61,'22:61','เช่นนั้นแหละ เพราะว่าอัลลอฮ์ ทรงให้กลางคืนคาบเกี่ยวเข้าไปในกลางวัน และพระองค์ทรงให้กลางวันคาบเกี่ยวไปในกลางคืน และแท้จริงอัลลอฮ์ เป็นผู้ทรงได้ยิน ผู้ทรงเห็น');
INSERT INTO "king_fahad_thai" VALUES(22,62,'22:62','เช่นนั้นแหละ เพราะว่าอัลลอฮ์ คือผู้ทรงสัจจะ และแท้จริงสิ่งที่พวกเขาวิงวอนขออื่นจากพระองค์เป็นผู้ทรงสูงส่ง ผู้ทรงยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(22,63,'22:63','เจ้ามิได้พิจารณาดอกหรือว่า แท้จริงอัลลอฮ์นั้นทรงให้น้ําหลั่งลงมาจากฟากฟ้า แล้วแผ่นดินก็กลายเป็นเขียวสดชื่น แท้จริงอัลลอฮ์เป็นผู้ทรงเมตตา ผู้ทรงรอบรู้ยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(22,64,'22:64','สิ่งที่อยู่ในชั้นฟ้าทั้งหลาย และแผ่นดินเป็นสิทธิของพระองค์ และแท้จริงอัลลอฮ์นั้นคือผู้ทรงพอเพียงจากสิ่งทั้งหลาย ผู้ทรงได้รับการสรรเสริญ');
INSERT INTO "king_fahad_thai" VALUES(22,65,'22:65','เจ้ามิได้พิจารณาดอกหรือว่า แท้จริงอัลลอฮ์นั้นทรงอํานวยความสะดวกให้แก่พวกเจ้าสิ่งที่มีอยู่ในแผ่นดิน และเรือแล่นไปตามท้องทะเลโดยบัญชาของพระองค์ และทรงยึดชั้นฟ้ามิให้มันตกลงมาบนแผ่นดิน เว้นแต่โดยอนุมัติของพระองค์ แท้จริงอัลลอฮ์นั้นเป็นผู้ทรงอ่อนโยน ผุ้ทรงเมตตาเสมอต่อมนุษย์');
INSERT INTO "king_fahad_thai" VALUES(22,66,'22:66','และพระองค์คือผู้ทรงให้พวกเจ้าเป็น แล้วทรงให้พวกเจ้าตาย หลังจากนั้นก็ทรงให้พวกเจ้าเป็นอีก แท้จริงมนุษย์นั้นช่างเนรคุณยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(22,67,'22:67','สําหรับทุก ๆ ประชาชาติเราได้กําหนดพิธีทางศาสนาขึ้นโดยที่พวกเขาปฏิบัติพิธีนั้น ดังนั้นคนหนึ่งคนใดอย่าได้โต้แย้งเจ้าในกิจการนั้น และจงเรียกร้องเชิญชวนไปสู่พระเจ้าของเจ้า แท้จริงเจ้านั้นอยู่บนแนวทางที่เที่ยงธรรม');
INSERT INTO "king_fahad_thai" VALUES(22,68,'22:68','และหากพวกเขาโต้แย้งเจ้า ก็จงกล่าวเถิดว่า “อัลลอฮ์ทรงรอบรู้ยิ่งในสิ่งที่พวกท่านกระทําอยู่”');
INSERT INTO "king_fahad_thai" VALUES(22,69,'22:69','“อัลลอฮ์จะทรงตัดสินระหว่างพวกท่านในวันกิยามะฮ์ ในสิ่งที่พวกท่านได้ขัดแย้งกันในเรื่องนั้น”');
INSERT INTO "king_fahad_thai" VALUES(22,70,'22:70','เจ้ามิรู้ดอกหรือว่า แท้จริงอัลลอฮ์นั้นทรงรอบรู้สิ่งที่อยู่ในชั้นฟ้าและแผ่นดิน แท้จริงสิ่งนั้นอยู่ในบันทึกแล้ว” แท้จริงในการนั้นเป็นการง่ายดายสําหรับอัลลอฮ์');
INSERT INTO "king_fahad_thai" VALUES(22,71,'22:71','และพวกเขาสักการะบูชาสิ่งอื่นจากอัลลอฮ์ ซึ่งพระองค์มิได้ประทานลงมาให้เป็นหลักฐาน และสําหรับพวกเขาไม่มีความรู้เกี่ยวกับเรื่องนี้ และสําหรับพวกอธรรมนั้นไม่มีผู้ช่วยเหลือใด ๆ');
INSERT INTO "king_fahad_thai" VALUES(22,72,'22:72','และเมื่อโองการทั้งหลายอันชัดแจ้งของเราได้ถูกนํามาอ่านแก่พวกเขา เจ้าจะสังเกตเห็นอาการบอกปัดไม่ยอมรับบนใบหน้าของบรรดาผู้ปฏิเสธศรัทธา พวกเขาเกือบจะเข้าไปทําร้ายบรรดาผู้อ่านโองการทั้งหลายของเราให้พวกเขาฟัง จงกล่าวเถิดมุฮัมมัด “ฉันจะแจ้งให้พวกท่านทราบถึงสิ่งที่เลวร้ายยิ่งกว่านี้ไหม มันคือไฟนรกไงเล่า อัลลอฮ์ทรงสัญญามันไว้แก่บรรดาผู้ปฏิเสธศรัทธา และมันเป็นทางกลับที่ชั่วร้ายยิ่ง”');
INSERT INTO "king_fahad_thai" VALUES(22,73,'22:73','โอ้มนุษย์ เอ๋ย ! อุทาหรณ์หนึ่งถูกยกมากล่าวไว้แล้ว ดังนั้นพวกเจ้าจงฟังมันให้ดี แท้จริงบรรดาที่พวกเจ้าวิงวอนขอความช่วยเหลืออื่นจากอัลลอฮ์นั้น พวกมันไม่สามารถจะให้บังเกิดแม้แต่แมลงวันสักตัวหนึ่ง หากว่าพวกมันจะรวมหัวกันเพื่อการนั้นก็ตาม และถ้าแมลงวันพาสิ่งใดหนีไปจากพวกมัน พวกมันก็ไม่สามารถจะเอามันกลับคืนมาได้จากแมลงวันทั้งผู้ขอและผู้ถูกขอ อ่อนแอแท้ ๆ');
INSERT INTO "king_fahad_thai" VALUES(22,74,'22:74','พวกเขามิได้ให้เกียรติอัลลอฮ์ ตามที่ควรจะให้เกียรติต่อพระองค์ แท้จริงอัลลอฮ์เป็นผู้ทรงพลัง ผู้ทรงเดชานุภาพโดยแท้จริง');
INSERT INTO "king_fahad_thai" VALUES(22,75,'22:75','อัลลอฮ์ทรงคัดเลือกบรรดาฑูตจากหมู่มะลาอิกะฮ์และจากหมู่มนุษย์ แท้จริงอัลลอฮ์เป็นผู้ทรงได้ยิน ผู้ทรงเห็น');
INSERT INTO "king_fahad_thai" VALUES(22,76,'22:76','พระองค์ทรงรอบรู้สิ่งที่เบื้องหน้าพวกเขา และสิ่งที่อยู่เบื้องหลังพวกเขา และกิจการทั้งหลายจะถูกนํากลับไปยังอัลลอฮ์');
INSERT INTO "king_fahad_thai" VALUES(22,77,'22:77','โอ้บรรดาผู้ศรัทธาเอ๋ย! พวกเจ้าจงรูกั๊วะ จงสุญูด และจงเคารพภักดีต่อพระเจ้าของพวกเจ้าเถิด และจงประกอบความดี หวังว่าพวกเจ้าจะได้รับชัยชนะ');
INSERT INTO "king_fahad_thai" VALUES(22,78,'22:78','และจงต่อสู้เพื่ออัลลอฮ์ ซึ่งเป็นการต่อสู้ที่แท้จริงเพื่อพระองค์ พระองค์ทรงคัดเลือกพวกเจ้า และพระองค์มิได้ทรงทําให้เป็นการลําบากแก่พวกเจ้าในเรื่องของศาสนา ศาสนา (ที่ไม่ลําบาก) คือศาสนาของอิบรอฮีม บรรพบุรุษของพวกเจ้า พระองค์ทรงเรียกชื่อพวกเจ้าว่ามุสลิมีน ในคัมภีร์ก่อน ๆ และในอัลกุรอานเพื่อร่อซูลจะได้เป็นพยานต่อพวกเจ้า และพวกเจ้าจะได้เป็นพยานต่อมนุษย์ทั่วไป ดังนั้นพวกเจ้าจงดํารงการละหมาด และบริจาคซะกาต และจงยึดมั่นต่ออัลลอฮ์ พระองค์เป็นผู้คุ้มครองพวกเจ้า เพราะพระองค์คือผู้คุ้มครองที่ดีเลิศ และผู้ทรงช่วยเหลือที่ดีเยี่ยม');
INSERT INTO "king_fahad_thai" VALUES(23,1,'23:1','แน่นอนบรรดาผู้ศรัทธาได้ประสบความสําเร็จแล้ว');
INSERT INTO "king_fahad_thai" VALUES(23,2,'23:2','บรรดาผู้ที่พวกเขาเป็นผู้นอบน้อมถ่อมตนในเวลาละหมาดของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(23,3,'23:3','และบรรดาผู้ที่พวกเขาเป็นผู้ผินหลังให้จากเรื่องไร้สาระต่าง ๆ');
INSERT INTO "king_fahad_thai" VALUES(23,4,'23:4','และบรรดาผู้ที่พวกเขาเป็นผู้บริจาคซะกาต');
INSERT INTO "king_fahad_thai" VALUES(23,5,'23:5','และบรรดาผู้ที่พวกเขาเป็นผู้รักษา (ไว้ซึ่งความบริสุทธิ์ของ) ทวารของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(23,6,'23:6','เว้นแต่แก่บรรดาภรรยาของพวกเขา หรือที่มือขวาของพวกเขาครอบครอง (คือทาสี) ในกรณีเช่นนั้นพวกเขาจะไม่ถูกตําหนิ');
INSERT INTO "king_fahad_thai" VALUES(23,7,'23:7','ฉะนั้นผู้ใดแสวงหาอื่นจากนั้น ชนเหล่านั้นพวกเขาก็เป็นผู้ละเมิด');
INSERT INTO "king_fahad_thai" VALUES(23,8,'23:8','และบรรดาผู้ที่พวกเขาเป็นผู้เอาใจใส่ต่อสิ่งที่ได้รับมอบหมายของพวกเขา และสัญญาของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(23,9,'23:9','และบรรดาผู้ที่พวกเขาเป็นผู้รักษาการละหมาดของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(23,10,'23:10','ชนเหล่านี้แหละพวกเขาเป็นทายาท');
INSERT INTO "king_fahad_thai" VALUES(23,11,'23:11','ซึ่งพวกเขา จะได้รับมรดกสวนสวรรค์ชั้นฟริเดาส์ พวกเขาจะพํานักอยู่ในนั้นตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(23,12,'23:12','และขอสาบานว่า แน่นอนเราได้สร้างมนุษย์มาจากธาตุแท้ของดิน');
INSERT INTO "king_fahad_thai" VALUES(23,13,'23:13','แล้วเราทําให้เขาเป็นเชื้ออสุจิ อยู่ในที่พักอันมั่นคง (คือมดลูก)');
INSERT INTO "king_fahad_thai" VALUES(23,14,'23:14','แล้วเราได้ทําให้เชื้ออสุจิกลายเป็นก้อนเลือดแล้วเราได้ทําให้ก้อนเลือดกลายเป็นก้อนเนื้อแล้วเราได้ทําให้ก้อนเนื้อกลายเป็นกระดูก แล้วเราหุ้มกระดูกนั้นด้วยเนื้อ แล้วเราได้เป่าวิญญาณให้เขากลายเป็นอีกรูปร่างหนึ่ง ดังนั้นอัลลอฮ์ทรงจําเริญยิ่ง ผู้ทรงเลิศแห่งปวงผู้สร้าง');
INSERT INTO "king_fahad_thai" VALUES(23,15,'23:15','หลังจากนั้น แท้จริงพวกเจ้าต้องตายอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(23,16,'23:16','แล้ว แท้จริงพวกเจ้าจะถูกให้ฟื้นคืนชีพขึ้น ในวันกิมามะฮ์');
INSERT INTO "king_fahad_thai" VALUES(23,17,'23:17','และแน่นอนยิ่งเราได้สร้างชั้นฟ้าทั้งเจ็ดไว้เบื้องบนพวกเจ้า และเรามิได้เพิกเฉย ทอดทิ้งระบบการสร้าง');
INSERT INTO "king_fahad_thai" VALUES(23,18,'23:18','และเราได้หลั่งน้ําให้ลงมาจากฟากฟ้าตามปริมาณ แล้วเราได้ให้มันขังอยู่ในแผ่นดิน และแท้จริงเราเป็นผู้สามารถอย่างแน่นอนที่จะให้มันเหือดหายไป');
INSERT INTO "king_fahad_thai" VALUES(23,19,'23:19','และด้วยน้ํานั้นเราทําให้มันเป็นสวนหลากหลายแก่พวกเจ้ามีต้นอินทผลัม และต้นองุ่นสําหรับพวกเจ้าในสวนนั้นมีผลไม้มากมาย และส่วนหนึ่งพวกเจ้าก็บริโภคมัน');
INSERT INTO "king_fahad_thai" VALUES(23,20,'23:20','และเราได้ทําให้มันเป็นต้นไม้ (ไซตูน)ที่ภูเขาซีนาย ซึ่งมันได้ผลิตออกมาเป็นน้ํามันและน้ําแกง สําหรับผู้บริโภค');
INSERT INTO "king_fahad_thai" VALUES(23,21,'23:21','และแท้จริงในเรื่องปศุสัตว์ (อูฐ วัว แพะ แกะ) นั้นเป็นบทเรียนสําหรับพวกเจ้า เราให้พวกเจ้าดื่ม สิ่งที่อยู่ในท้องของมัน (น้ํานม) และในตัวมันมีประโยชน์มากมาย สําหรับพวกเจ้าและบางชนิดพวกเจ้าก็บริโภคมัน');
INSERT INTO "king_fahad_thai" VALUES(23,22,'23:22','และพวกเจ้าได้บรรทุกบนหลังมัน เช่น เดียวกับใช้บรรทุกบนเรือ');
INSERT INTO "king_fahad_thai" VALUES(23,23,'23:23','และเป็นที่แน่นอนยิ่ง เราได้ส่งนูห์ ไปยังหมู่ชนของเขา ดังนั้นเขาได้กล่าวว่า “โอ้หมู่ชนของฉันเอ๋ย พวกท่านจงเคารพภักดี อัลลฮ์เถิดสําหรับพวกท่านนั้นไม่มีพระเจ้าอื่นใดนอกจากพระองค์ ดังนั้นพวกท่านจะไม่ยําเกรง (การลงโทษของพระองค์) หรือ?”');
INSERT INTO "king_fahad_thai" VALUES(23,24,'23:24','แล้วหัวหน้าของบรรดาผู้ปฏิเสธศรัทธาในหมู่ชนของเขาได้กล่าวขึ้นว่า “เขาผู้ที่มิใช่ใครอื่นนอกจากเป็นปุถุชนคนธรรมดาเช่นเดียวกับพวกท่านเพียงแต่เขาต้องการที่จะทําตัวให้ดีเด่นเหนือพวกท่าน และหากอัลลอฮ์ ทรงประสงค์แล้ว แน่นอนพระองค์จะทรงส่งมะลาอิกะฮ์ลงมา เราไม่เคยได้ยินคําพูดเช่นนี้ในสมัยบรรพบุรุษของเราแต่กาลก่อนเลย');
INSERT INTO "king_fahad_thai" VALUES(23,25,'23:25','“เขามิได้เป็นอะไรนอกจากเป็นคนบ้า ดังนั้นพวกท่านจงอดทนคอยเขาสักระยะเวลาหนึ่ง”');
INSERT INTO "king_fahad_thai" VALUES(23,26,'23:26','นูห์ ได้กล่าวว่า “ข้าแต่พระเจ้าของข้าพระองค์ ขอพระองค์ทรงโปรดช่วยเหลือข้าพระองค์ด้วย เพราะพวกเขาปฏิเสธไม่ยอมเชื่อข้าพระองค์”');
INSERT INTO "king_fahad_thai" VALUES(23,27,'23:27','ดังนั้น เราจึงวะฮีแก่เขาให้ต่อเรือภายใต้การคุ้มครองของเราและคําสั่งสอนของเรา และเมื่อคําบัญชาของเราได้มาถึง น้ําในเตาก็จะเดือดพุ่ง เจ้าจงบรรทุกทุกชนิดของสัตว์เป็นคู่ ๆ และครอบครัวของเจ้าด้วย นอกจากผู้ที่คําดํารัสได้บันทึกไว้ก่อนแล้ว (ให้หายนะ) ในหมู่พวกเขา (ที่ไม่ยอมศรัทธา) และเจ้าอย่าได้ขอช่วยเหลือเขา ในบรรดาผู้ที่อธรรม แท้จริงพวกเขาจะถูกให้จมน้ําตาย');
INSERT INTO "king_fahad_thai" VALUES(23,28,'23:28','ครั้นเมื่อเจ้า และผู้ที่อยู่ร่วมกับเจ้าได้ขึ้นไปอยู่บนเรือแล้ว ก็จงกล่าวเถิดว่า “บรรดาการสรรเสริญเป็นของอัลลอฮ์ ผู้ซึ่งทรงให้เรารอดพ้นจากหมู่ชนผู้อธรรม”');
INSERT INTO "king_fahad_thai" VALUES(23,29,'23:29','และจงกล่าวเถิดว่า “ข้าแต่พระเจ้าของข้าพระองค์ ขอพระองค์ทรงให้ข้าพระองค์ลงจากเรือด้วยการลงที่มีความจําเริญ และพระองค์เท่านั้นเป็นผู้เลิศยิ่งแห่งบรรดาผู้ให้ลงจากเรือ”');
INSERT INTO "king_fahad_thai" VALUES(23,30,'23:30','แท้จริงในการนั้นย่อมเป็นสัญญาณข้อเตือนสติมากหลาย และถึงแม้ว่าเราเป็นผู้ทดสอบปวงบ่าวของเรา');
INSERT INTO "king_fahad_thai" VALUES(23,31,'23:31','แล้วหลังจากพวกเขา เราได้บังเกิดชนอีกกลุ่มหนึ่ง (พวกอ๊าด)');
INSERT INTO "king_fahad_thai" VALUES(23,32,'23:32','ดังนั้นเราได้ส่งร่อซูลคนหนึ่ง (ฮูด) ของพวกเขาไปยังพวกเขา โดยกล่าวว่า “พวกท่านจงเคารพภักดีอัลลอฮ์เถิด สําหับพวกท่านนั้นไม่มีพระเจ้าอื่นใดนอกจากพระองค์ ดังนั้นพวกท่านจะไม่ยําเกรง (การลงโทษของ) พระองค์หรือ ?”');
INSERT INTO "king_fahad_thai" VALUES(23,33,'23:33','และหัวหน้าหมู่ชนของเขา คือบรรดาผู้ปฏิเสธศรัทธา และปฏิเสธไม่ยอมเชื่อการมีวันปรโลก และเราได้ให้ความสําราญแก่พวกเขาในโลกนี้ กล่าวว่า “เขาผู้นี้มิใช่ใครอื่น นอกจากเป็นปุถุชนคนธรรมดาเช่นเดียวกับพวกท่านเขากินอาหารเช่นเดียวกับที่พวกท่านกิน และเขาดื่มเช่นเดียวกับพวกท่านดื่ม');
INSERT INTO "king_fahad_thai" VALUES(23,34,'23:34','“และหากพวกท่านเชื่อฟังปฏิบัติตามมนุษย์ธรรมดาเช่นเดียวกับพวกท่าน ดังนั้นแน่นอนพวกท่านเป็นผู้ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(23,35,'23:35','“เขาสัญญากับพวกท่านกระนั้นหรือว่าแท้จริงเมื่อพวกท่านได้ตายไปแล้ว และพวกท่านกลายเป็นดิน และกระดูกแล้ว แน่นอนพวกท่านจะถูกนําให้ออกมาฟื้นขึ้นอีก ?”');
INSERT INTO "king_fahad_thai" VALUES(23,36,'23:36','“ไกลเสียจริง ไกลเสียจริง ในสิ่งที่พวกท่านถูกสัญญาไว้”');
INSERT INTO "king_fahad_thai" VALUES(23,37,'23:37','“ไม่มีชีวิต นอกจากการดํารงชีวิตของเราในโลกนี้ เราจะตายไป และ (บางคนในพวก)เราก็จะมีชีวิตอยู่ และพวกเราจะไม่ถูกให้ฟื้นคืนชีพมาอีก”');
INSERT INTO "king_fahad_thai" VALUES(23,38,'23:38','“เขามิใช่ใครอื่น นอกจากเป็นคนธรรมดาที่กล่าวเท็จต่ออัลลอฮ์ และเราไม่ยอมศรัทธาต่อเขาเป็นอันขาด”');
INSERT INTO "king_fahad_thai" VALUES(23,39,'23:39','เขา (ฮูด) กล่าวว่า “ข้าแต่พระเจ้าของข้าพระองค์ ขอพระองค์ทรงโปรดช่วยเหลือข้าพระองค์ด้วย เพราะพวกเขาปฏิเสธไม่ยอมเชื่อข้าพระองค์”');
INSERT INTO "king_fahad_thai" VALUES(23,40,'23:40','พระองค์ ตรัสว่า “หลังจากชั่วเวลาอีกเล็กน้อยพวกเขาจะกลายเป็นผู้เศร้าโศกเสียใจอย่างแน่นอน”');
INSERT INTO "king_fahad_thai" VALUES(23,41,'23:41','ดังนั้นเสียงงกัมปนาทได้ผลาญชีวิตพวกเขาอย่างยุติธรรม แล้วเราได้ทําให้พวกเขากลายเป็นเศษขยะ ฉะนั้น(ความหายนะ) ความห่างไกล (จากเมตตาของอัลลอฮ์) จึงประสบแก่หมู่ชนผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(23,42,'23:42','แล้วหลังจากพวกเขา เราได้บังเกิดหมู่ชนอีกหลายกลุ่ม');
INSERT INTO "king_fahad_thai" VALUES(23,43,'23:43','ไม่มีประชาชาติใดที่จะได้รับการลงโทษก่อนกําหนดของมัน และก็จะไม่ล่าช้ากว่ากําหนดเช่นกัน');
INSERT INTO "king_fahad_thai" VALUES(23,44,'23:44','แล้วเราได้ส่งบรรดาร่อซูลของเรามาอย่างต่อเนื่องกัน ทุกครั้งที่ร่อซูลของพวกเขาได้มายังชนชาติหนึ่งพวกเขาก็ปฏิเสธไม่ยอมเชื่อถือเขา ดังนั้นเราจึงให้บางกลุ่มของพวกเขาติดตามอีกบางกลุ่ม (ด้วยความหายนะพินาศ) แล้วเราด้ทําให้พวกเขาเป็นเรื่องบอกเล่าต่อกันมา ฉะนั้น (ความหายนะ) ความห่างไกล (จากเมตตาของอัลลอฮ์) จึงประสบแก่บรรดาผู้ไม่ยอมศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(23,45,'23:45','แล้วเราได้ส่งมูซา และพี่ชายของเขาคือฮารูน พร้อมด้วยสัญญาณทั้งหลาย ของเราและหลักฐานอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(23,46,'23:46','ไปยังฟิรเอาน์ และบุคคลชั้นหัวหน้าของเขา แต่พวกเขาก็อวดใหญ่อวดโต และพวกเขาเป็นหมู่ชนที่เย่อหยิ่งจองหอง');
INSERT INTO "king_fahad_thai" VALUES(23,47,'23:47','พวกเขากล่าวว่า “จะให้พวกเราศรัทธาต่อบุคคลทั้งสองที่มีสภาพเช่นเดียวกับเราและทั้ง ๆ ที่พวกพ้องของเขาทั้งสองก็เป็นทาสรับใช้เรา”');
INSERT INTO "king_fahad_thai" VALUES(23,48,'23:48','ฉะนั้นพวกเขาจึงปฏิเสธไม่ยอมเชื่อฟังเขาทั้งสอง พวกเขาจึงอยู่ในหมู่ผู้ถูกทําลายจมน้ําตาย');
INSERT INTO "king_fahad_thai" VALUES(23,49,'23:49','และแท้จริงเราได้ให้คัมภีร์ (เตารอฮ์) แก่มูซา เพื่อพวกเขาจะได้อยู่ในแนวทางที่ถูกต้อง');
INSERT INTO "king_fahad_thai" VALUES(23,50,'23:50','และเราได้ทําให้อีซาบุตรของมัรยัม และแม่ของเขาเป็นสัญญาณหนึ่ง (ปาฏิหาริย์อันยิ่งใหญ่) และเราได้ให้ที่พักพิงแก่เขาทั้งสอง ณ ที่ราบสูงแห่งหนึ่ง (บัยตุลมักดิส) เป็นที่พักอย่างสะดวกสบาย และมีธารน้ําไหล');
INSERT INTO "king_fahad_thai" VALUES(23,51,'23:51','โอ้ บรรดาร่อซูลเอ๋ย ! พวกเจ้าจงบริโภคส่วนที่ดี (ฮะล้าล) และจงกระทําความดีเถิดเพราะแท้จริง ข้ารอบรู้สิ่งพวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(23,52,'23:52','แท้จริงนี่คือประชาชาติของพวกเจ้า เป็นประชาชาติเดียวกัน และข้าคือพระเจ้าของพวกเจ้าฉะนั้นพวกเจ้าจงยําเกรงต่อข้า');
INSERT INTO "king_fahad_thai" VALUES(23,53,'23:53','พวกเขาได้แตกแยกกันในเรื่องของพวกเขา ระหว่างพวกเขากันเอง แต่ละฝ่ายก็พอใจในสิ่งที่ตนเองยึดถือ');
INSERT INTO "king_fahad_thai" VALUES(23,54,'23:54','ดังนั้นเจ้า (มุฮัมมัด) จงปล่อยพวกเขาให้อยู่ในความงมงายของพวกเขา สักระยะเวลาหนึ่ง');
INSERT INTO "king_fahad_thai" VALUES(23,55,'23:55','พวกเขาคิดหรือว่า แท้จริงสิ่งที่เราได้ให้แก่พวกเขา เช่น ทรัพย์สมบัติ และลูกหลานนั้น');
INSERT INTO "king_fahad_thai" VALUES(23,56,'23:56','เราได้รีบเร่งให้ความดีต่าง ๆ แก่พวกเขากระนั้นหรือ ? เปล่าเลย แต่ทว่าพวกเขาไม่มีความรู้สึกหรือ');
INSERT INTO "king_fahad_thai" VALUES(23,57,'23:57','แท้จริงบรรดาผู้ที่พวกเขาเป็นผู้มีจิตใจยําเกรงเนื่องจากความกลัวต่อพระเจ้าของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(23,58,'23:58','และบรรดาผู้ที่พวกเขาศรัทธาต่อสัญญาณต่าง ๆ แห่งพระเจ้าของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(23,59,'23:59','และบรรดาผู้ที่พวกเขาไม่ตั้งภาคีต่อพระเจ้าของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(23,60,'23:60','และบรรดาผู้ที่บริจาคสิ่งที่พวกเขาได้มา โดยที่จิตใจของเขาเปี่ยมได้ด้วยความหวั่นเกรงว่าแท้จริงพวกเขาต้องกลับไปหาพระเจ้าของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(23,61,'23:61','ชนเหล่านั้น พวกเขารีบเร่งในการประกอบความดีทั้งหลาย และพวกเขาเป็นผู้เหมาะสม สมควรเป็นผู้รุดหน้าไปก่อน');
INSERT INTO "king_fahad_thai" VALUES(23,62,'23:62','และเรามิได้บังคับผู้ใด เว้นแต่ความสามารถของเขา และ ณ ที่เรานั้นมีบันทึก ที่บันทึกแต่ความจริง โดยที่พวกเขาจะไม่ถูกอยุติธรรม');
INSERT INTO "king_fahad_thai" VALUES(23,63,'23:63','แต่ว่าจิตใจของพวกเขาอยู่ในปลักแห่งความงมงายจากอัลกุรอาน และสําหรับพวกเขามีการงานอื่นอีกจากนั้น โดยที่พวกเขาต้องปฏิบัติมัน');
INSERT INTO "king_fahad_thai" VALUES(23,64,'23:64','จนกระทั่งเมื่อเราได้คร่าเอาชีวิตพวกที่อยู่ในความสุขสําราญของพวกเขาด้วยการลงโทษ เมื่อนั้นพวกเขาก็ตะโกนร้องขอความช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(23,65,'23:65','พวกเจ้าอย่าได้ตะโกนร้องขอความช่วยเหลือเลยในวันนี้ แท้จริงพวกเจ้าจะไม่ได้รับความช่วยเหลือจากเราดอก');
INSERT INTO "king_fahad_thai" VALUES(23,66,'23:66','แน่นอนโองการทั้งหลายของเราถูกนํามาอ่านแก่พวกเจ้า แล้วพวกเจ้าก็หันสันเท้าของพวกเจ้ากลับ');
INSERT INTO "king_fahad_thai" VALUES(23,67,'23:67','พวกเขาหยิ่งจองหองต่ออัลกุรอาน พวกเจ้าจับกลุ่มสนทนากันในเวลากลางคืน');
INSERT INTO "king_fahad_thai" VALUES(23,68,'23:68','พวกเขามิได้พิจารณา พระดํารัสดอกหรือ ? หรือว่าได้มีมายังพวกเขา สิ่งที่มิได้มีมายังบรรพบุรุษของพวกเขารุ่นก่อน ๆ');
INSERT INTO "king_fahad_thai" VALUES(23,69,'23:69','หรือว่าพวกเขาไม่รู้จักร่อซูลของพวกเขาดังนั้นพวกเขาจึงปฏิเสธต่อต้านเขา');
INSERT INTO "king_fahad_thai" VALUES(23,70,'23:70','หรือพวกเขากล่าวหาเขาว่าเป็นบ้า มิใช่เช่นนั้นดอก เขาได้นําความจริงมาให้พวกเขาแล้ว แต่ส่วนมากของพวกเขาเป็นผู้เกลียดชังความจริง');
INSERT INTO "king_fahad_thai" VALUES(23,71,'23:71','และหากว่าความจริงนั้นสอดคล้องอารมณ์ใฝ่ต่ําของพวกเขาแล้ว ชั้นฟ้าทั้งหลาย และแผ่นดินและสิ่งที่อยู่ในนั้นต้องเสียหายอย่างแน่นอน แต่ทว่าเราได้นําข้อเตือนสติของพวกเขา (คืออัลกุรอาน) มาให้พวกเขาแล้ว แต่พวกเขาเป็นผู้หันหลังให้กับข้อเตือนสติของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(23,72,'23:72','หรือเจ้าขอค่าจ้าง(ในการเผยแพร่ศาสนา)จากพวกเขากระนั้นหรือ? แต่การให้ค่าจ้างของพระเจ้าของเจ้านั้นเลิศยิ่งกว่า และพระองค์เท่านั้นทรงเป็นเลิศยิ่งในหมู่ผู้ประทานปัจจัยยังชีพ');
INSERT INTO "king_fahad_thai" VALUES(23,73,'23:73','และแท้จริงเจ้านั้นเป็นพวกชักชวนพวกเขาอย่างแน่นอนไปสู่แนวทางอันเที่ยงธรรม');
INSERT INTO "king_fahad_thai" VALUES(23,74,'23:74','และแท้จริงบรรดาผู้ไม่ศรัทธาต่อวันปรโลกนั้น พวกเขาเป็นผู้หันห่างจากแนวทางอันเที่ยงธรรม');
INSERT INTO "king_fahad_thai" VALUES(23,75,'23:75','และหากเราเมตตาพวกเขา และเราได้ปลดเปลื้องความทุกข์ยากออกจากพวกเขาแล้ว แน่นอนพวกเขาก็ยังคงมั่วสุมอยู่ในความผยองของพงกเขาเช่นเดียวกับคนตาบอด');
INSERT INTO "king_fahad_thai" VALUES(23,76,'23:76','และโดยแน่แท้เราได้ทดสอบพวกเขาด้วยการลงโทษ แต่พวกเขาก็หาได้นอบน้อมต่อพระเจ้าของพวกเขาไม่ และพวกเขาก็ไม่ยอมถ่อมตน');
INSERT INTO "king_fahad_thai" VALUES(23,77,'23:77','จนกระทั่งเมื่อเราเปิดประตูแห่งการลงโทษอันสาหัสแก่พวกเขา เมื่อนั้นแหละพวกเขาก็เป็นผู้ท้อถอยหมดหวังเสียแล้ว');
INSERT INTO "king_fahad_thai" VALUES(23,78,'23:78','และพระองค์เป็นผู้สร้างสัมผัสการฟังและการเห็น และหัวใจเพื่อเข้าใจแก่พวกเจ้า แต่เเพียงส่วนน้อยเท่านั้นที่พวกเจ้าขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(23,79,'23:79','และพระองค์เป็นผู้ทรงแพร่พันธุ์ของพวกเจ้าในแผ่นดิน และพวกเจ้าจะถูกรวบรวมให้กลับไปหาพระองค์');
INSERT INTO "king_fahad_thai" VALUES(23,80,'23:80','และพระองค์เป็นผู้ทรงให้เป็นและทรงให้ตาย และพระองค์ทรงสิทธิในการสับเปลี่ยนหมุนเวียนกลางคืนกลางวัน พวกเจ้ามิได้ใช้สติปัญญาพิจารณาดอกหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(23,81,'23:81','แต่ทว่าพวกเขาได้กล่าวเช่นเดียวกับชนชาติสมัยก่อน ๆ ได้กล่าวไว้');
INSERT INTO "king_fahad_thai" VALUES(23,82,'23:82','พวกเขาได้กล่าวว่า “เมื่อเราได้ตายไปแล้ว และเราได้กลายเป็นฝุ่นดินและกระดูกป่นเราาาจะถูกให้ฟื้นคืนชีพครั้งหนึ่งหรือ ?”');
INSERT INTO "king_fahad_thai" VALUES(23,83,'23:83','“แท้จริงเราและบรรพบะรุษของเราได้ถูกสัญญามาก่อนแล้วในเรื่องนี้ มันมิใช่อะไรอื่นนอกจากเป็นนิยายเหลวไหล สมัยก่อนเท่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(23,84,'23:84','จงกล่าวเถิดมุฮัมมัด “แผ่นดินนี้ และผู้ที่อยู่ในนั้นเป็นของใคร หากพวกท่านรู้”');
INSERT INTO "king_fahad_thai" VALUES(23,85,'23:85','พวกเขาจะกล่าวว่า “มันเป็นของอัลลอฮ์” จงกล่าวเถิดมุฮัมมัด “ถ้าเช่นนั้น พวกท่านจะไม่พิจารณาใคร่ครวญหรือ ?”');
INSERT INTO "king_fahad_thai" VALUES(23,86,'23:86','จงกล่าวเถิดมุฮัมมัด “ใครเป็นเจ้าของชั้นฟ้าทั้งเจ็ด และเป็นผู้สร้างบัลลังก์อันยิ่งใหญ่ ?”');
INSERT INTO "king_fahad_thai" VALUES(23,87,'23:87','พวกเขาจะกล่าวว่า “มันเป็นของอัลลอฮ์” จงกล่าวเถิดมุฮัมมัด “ถ้าเช่นนั้นพวกท่านจะไม่ยําเกรง (การลงโทษของ)พระองค์หรือ ?”');
INSERT INTO "king_fahad_thai" VALUES(23,88,'23:88','จงกล่าวเถิดมุฮัมมัด “อํานาจอันกว้างใหญ่ไพศาลทุกสิ่งอย่างนี้อยู่ในพระหัตถ์ของผู้ใด ?” และพระองค์เป็นผู้ทรงปกป้องคุ้มครอง และจะไม่มีใครปกป้องคุ้มครองพระองค์ หากพวกท่านรู้”');
INSERT INTO "king_fahad_thai" VALUES(23,89,'23:89','พวกเขาจะกล่าวว่า “มันเป็นของอัลลอฮ์” จงกล่าวเถิดมุฮัมมัด “ดังนั้นพวกท่านถูกหลอกลวงได่อย่างไร ?”');
INSERT INTO "king_fahad_thai" VALUES(23,90,'23:90','แต่ทว่า เราได้นําเอาสัจธรรมมาให้แก่พวกเขาแล้ว และแท้จริงพวกเขาเป็นผู้กล่าวเท็จอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(23,91,'23:91','อัลลอฮ์มิได้ทรงตั้งผู้ใดเป็นพระบุตร และไม่มีพระเจ้าอื่นใดคู่เคียงกับพระองค์ ถ้าเช่นนั้นพระเจ้าแต่ละองค์ก็จะเอาสิ่งที่ตนสร้างไปเสียหมด และแน่นอนพระเจ้าบางพระองค์ในหมู่พวกเขาก็จะมีอํานาจเหนือกว่าอีกบางองค์ มหาบริสุทธิ์ยิ่งแห่งอัลลอฮ์ ให้พ้นจากที่พวกเขากล่าวหา');
INSERT INTO "king_fahad_thai" VALUES(23,92,'23:92','พระผู้ทรงรอบรู้ในสิ่งพ้นญาณวิสัยและสิ่งที่มองเห็นได้ พระองค์ทรงสูงส่งยิ่งจากสิ่งที่พวกเขาตั้งเป็นภาคี');
INSERT INTO "king_fahad_thai" VALUES(23,93,'23:93','จงกล่าวเถิดมุฮัมมัด “ข้าแต่พระเจ้าของข้าพระองค์ หากพระองค์จะทรงให้ข้าพระองค์ได้เห็นสิ่งที่พวกเขาถูกกล่าวเตือนสําทับไว้แล้ว”');
INSERT INTO "king_fahad_thai" VALUES(23,94,'23:94','“ข้าแต่พระเจ้าของข้าพระองค์ ของพระองค์ทรงอย่าให้ข้าพระองค์อยู่ในหมู่พวกอธรรมเลย”');
INSERT INTO "king_fahad_thai" VALUES(23,95,'23:95','และแท้จริงเราเป็นผู้มีอานุภาพอย่างแน่นอนที่จะให้เจ้าได้เห็นสิ่งที่เราได้สัญญาพวกเขาไว้');
INSERT INTO "king_fahad_thai" VALUES(23,96,'23:96','เจ้าจงปราบความชั่ว ด้วยสิ่งที่ดียิ่ง เรารู้ดียิ่งในสิ่งที่พวกเขากล่าวหา');
INSERT INTO "king_fahad_thai" VALUES(23,97,'23:97','และจงกล่าวเถิดมุฮัมมัด “ข้าแต่พระเจ้าของข้าพระองค์ ข้าพระองค์ขอความคุ้มครองต่อพระองค์ ให้พ้นจากเสียงกระซิบกระซาบของชัยฎอน');
INSERT INTO "king_fahad_thai" VALUES(23,98,'23:98','“และข้าพระองค์ขอความคุ้มครองต่อพระองค์ ข้าแต่พระองค์ของข้าพระองค์ ให้พ้นจากการที่พวกมันจะนําความชั่วร้ายมาสู่ข้าพระองค์”');
INSERT INTO "king_fahad_thai" VALUES(23,99,'23:99','จนกระทั่งเมื่อความตายได้มาหาคนใดในพวกเขา เขาก็จะกล่าวขึ้นว่า “ข้าแต่พระเจ้าของข้าพระองค์ ขอพระองค์ทรงให้ข้าพระองค์กลับไปมีชีวิตอีกครั้งหนึ่งเถิด”');
INSERT INTO "king_fahad_thai" VALUES(23,100,'23:100','“เพื่อข้าพระองค์จะได้กระทําความดีในสิ่งที่ข้าพระองค์ปล่อยทิ้งไว้” เปล่าเลย ! มันเป็นเพียงถ้อยคําที่เขากล่าวมันไว้เท่านั้น และเบื้องหน้าของพวกเขานั้นมีโลกบัรซัค จนกระทั่งถึงวันที่พวกเขาจะถูกฟื้นคืนชีพขึ้นมา');
INSERT INTO "king_fahad_thai" VALUES(23,101,'23:101','ดังนั้นเมื่อสังข์ได้ถูกเป่าขึ้น ดังนั้นจะไม่มีความสัมพันธ์ทางเครือญาติระหว่างพวกเขาในวันนั้น และพวกเขาจะไม่ไต่ถามซึ่งกันและกัน');
INSERT INTO "king_fahad_thai" VALUES(23,102,'23:102','ดังนั้นผู้ใดตราชูของเขาหนัก ชนเหล่านั้น พวกเขาเป็นผู้ประสบชัยชนะ');
INSERT INTO "king_fahad_thai" VALUES(23,103,'23:103','และผู้ใดตราชูของเบา ชนเหล่านั้นคือบรรดาผู้ที่ทําให้ตัวของพวกเขาขาดทุน พวกเขาจะพํานักอยู่ในนรกตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(23,104,'23:104','ไฟนรกจะเผาไหม้ใบหน้าของพวกเขา และพวกเขาจะมีใบหน้าที่บูดเบี้ยวในนรกนั้น');
INSERT INTO "king_fahad_thai" VALUES(23,105,'23:105','โองการทั้งหลายของเรามิได้ถูกนํามาอ่านแก่พวกเจ้าดอกหรือ แล้วพวกเจ้าก็ปฏิเสธไม่ยอมเชื่อมัน');
INSERT INTO "king_fahad_thai" VALUES(23,106,'23:106','พวกเขากล่าวว่า “ข้าแต่พระเจ้าของเราความชั่วช้าเลวทรามของพวกเราได้เข้ามาครอบงําพวกเรา และพวกเราเป็นหมู่ชนหลงทาง”');
INSERT INTO "king_fahad_thai" VALUES(23,107,'23:107','“ข้าแต่พระเจ้าของเรา ขอพระองค์ทรงโปรดเอาพวกเราออกจากนรกด้วยเถิด ถ้าหากเรากลับไปทําความชั่วช้าอีก แน่นอนเราก็เป็นพวกอธรรม');
INSERT INTO "king_fahad_thai" VALUES(23,108,'23:108','พระองค์ ตรัสว่า “พวกเจ้าจงตะเพิดไปจมอยู่ในนั้นเถิด และพวกเจ้าอย่าได้มาพูดกับข้าเลย”');
INSERT INTO "king_fahad_thai" VALUES(23,109,'23:109','แท้จริงมีหมู่ชนกลุ่มหนึ่งจากปวงบ่าวของเรา พวกเขากล่าวว่า “ข้าแต่พระเจ้าของเรา พวกเราได้ศรัทธาต่อพระองค์ ขอพระองค์ทรงโปรดอภัยโทษให้แก่เรา และทรงเมตตาต่อเราด้วย และพระองค์ท่านเท่านั้น ทรงเป็นผู้เมตตาที่ดียิ่ง”');
INSERT INTO "king_fahad_thai" VALUES(23,110,'23:110','พวกเจ้าได้ดูถูกเหยียดหยามพวกเขา จนกระทั่ง (การกระทําเช่นนั้นแก่พวกเขา) ทําให้พวกเจ้าลืมนึกถึงข้า และพวกเจ้าก็หัวเราะเยาะเย้ยพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(23,111,'23:111','แท้จริงข้าได้ตอบแทนรางวัลให้แก่พวกเขาแล้วในวันนี้ เพราะพวกเขาอดทน แท้จริงพวกเขาเท่านั้นเป็นผู้ได้รับชัยชนะ');
INSERT INTO "king_fahad_thai" VALUES(23,112,'23:112','พระองค์ตรัสว่า “พวกเจ้าพํานักอยู่ในแผ่นดินเป็นจํานวนกี่ปี ?”');
INSERT INTO "king_fahad_thai" VALUES(23,113,'23:113','พวกเขากล่าวตอบว่า “เราพํานักอยู่วันหนึ่งหรือส่วนหนึ่งของวัน ขอพระองค์โปรดถามนักคํานวณที่เชี่ยวชาญเถิด”');
INSERT INTO "king_fahad_thai" VALUES(23,114,'23:114','พระองค์ตรัสว่า “พวกเจ้ามิได้พํานักอยู่เว้นแต่เพียงเล็กน้อยเท่านั้น หากพวกเจ้ารู้');
INSERT INTO "king_fahad_thai" VALUES(23,115,'23:115','“พวกเจ้าคิดว่า แท้จริงเราได้ให้พวกเจ้าบังเกิดมาโดยไร้ประโยชน์ และแท้จริงพวกเจ้าจะไม่กลับไปหาเรากระนั้นหรือ ?”');
INSERT INTO "king_fahad_thai" VALUES(23,116,'23:116','อัลลอฮ์ผู้ทรงสูงส่ง ผู้ทรงอํานาจ ผู้ทรงสัจจะ ไม่มีพระเจ้าอื่นใดนอกจากพระองค์ พระเจ้าแห่งบัลลังก์อันทรงเกียรติ');
INSERT INTO "king_fahad_thai" VALUES(23,117,'23:117','และผู้ใดวิงวอนขอพระเจ้าอื่นคู่เคียงกับอัลลอฮ์ โดยไม่มีหลักฐานพิสูจน์แก่เขาในการนี้ แท้จริงการคิดบัญชีของเขาอยู่ที่พระเจ้าของเขา แท้จริงบรรดาผู้ปฏิเสธศรัทธาจะไม่ประสบความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(23,118,'23:118','และจงกล่าวเถิดมุฮัมมัดว่า “ข้าแต่พระเจ้าของข้าพระองค์ ขอพระองค์ทรงโปรดอภัยและทรงเมตตา และพระองค์ท่านเท่านั้นทรงเป็นผู้เมตตาที่ดียิ่ง”');
INSERT INTO "king_fahad_thai" VALUES(24,1,'24:1','นี่คือซูเราะฮ์หนึ่งที่เราได้ประทานมันลงมา และเราได้กําหนดเป็นข้อบังคับสิ่งที่มีอยู่ในมัน และเราได้ประทานโองการต่าง ๆ ที่มีอยู่ในนั้นให้เป็นบทบัญญัติอันชัดแจ้ง เพื่อพวกเจ้าจักได้รําลึกใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(24,2,'24:2','หญิงมีชู้และชายมีชู้ พวกเจ้าจงโบยแต่ละคนในสองคนนั้นคนละหนึ่งร้อยที และอย่าให้ความสงสารยับยั้งการกระทําของพวกเจ้าต่อคนทั้งสองนั้น ในบัญญัติของอัลลอฮฺเป็นอันขาด หากพวกเจ้าศรัทธาต่ออัลลอฮฺและวันปรโลก และจงให้กลุ่มหนึ่งของบรรดาผู้ศรัทธาเป็นพยานในการลงโทษเขาทั้งสอง');
INSERT INTO "king_fahad_thai" VALUES(24,3,'24:3','ชายมีชู้จะไม่สมรสกับใคร นอกจากกับหญิงมีชู้หรือหญิงมุชริกะฮ์ และหญิงมีชู้จะไม่มีใครสมรสกับเธอ นอกจากกับชายมีชู้หรือชายมุชริก และ (การมีชู้) เช่นนั้นเป็นที่ต้องห้ามแก่บรรดาผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(24,4,'24:4','และบรรดาผู้กล่าวโทษบรรดาหญิงบริสุทธิ์ แล้วพวกเขามิได้นําพยานสี่คนมา พวกเจ้าจงโบยพวกเขาแปดสิบที และพวกเจ้าอย่ารับการเป็นพยานของพวกเขาเป็นอันขาด ชนเหล่านั้นพวกเขาเป็นผู้ฝ่าฝืน');
INSERT INTO "king_fahad_thai" VALUES(24,5,'24:5','นอกจากบรรดาผู้ลุแก่โทษหลักจากนั้น และพวกเขาปรับปรุงตัวเองให้ดีขึ้น แท้จริงอัลลอฮ์นั้นเป็นผู้ทรงอภัยผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(24,6,'24:6','และบรรดาผู้กล่าวโทษภรรยาของพวกเขา และสําหรับพวกเขาไม่มีพยานนอกจากตัวของพวกเขาเอง ก็ให้การเป็นพยานของคนหนึ่งในพวกเขากล่าวสาบานสี่ครั้ง ด้วยพระนามของอัลลอฮ์แท้จริงเขาเป็นหนึ่งในหมู่ผู้พูดจริง');
INSERT INTO "king_fahad_thai" VALUES(24,7,'24:7','และครั้นที่ห้าให้เขากล่าวว่า แท้จริงการสาปแช่งของอัลลอฮ์จงมีแด่เขา หากเขาเป็นผู้ที่กล่าวเท็จ');
INSERT INTO "king_fahad_thai" VALUES(24,8,'24:8','และพวกเขาจะทําให้นางพ้นจากการลงโทษ ต่อเมื่อนางกล่าวสาบานสี่ครั้งด้วยพระนามอัลลอฮ์ว่าเขาเป็นหนึ่งในหมู่ผู้กล่าวเท็จ');
INSERT INTO "king_fahad_thai" VALUES(24,9,'24:9','และครั้งที่ห้าให้นางกล่าวว่า แท้จริงความกริ้วของอัลลอฮ์จงมีแด่นาง หากเขาเป็นหนึ่งในหมู่ผู้พูดจริง');
INSERT INTO "king_fahad_thai" VALUES(24,10,'24:10','และหากมิใช่ความโปรดปรานของอัลลอฮฺแก่พวกเจ้า และความเมตตาของพระองค์แล้ว และแท้จริงอัลลอฮฺทรงเป็นผู้นิรโทษ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(24,11,'24:11','แท้จริงบรรดาผู้นําข่าวเท็จมานั้น เป็นบุคคลกลุ่มหนึ่งจากพวกเจ้า พวกเจ้าอย่าได้คิดว่ามันเป็นการชั่วแก่พวกเจ้า แต่ว่ามันเป็นการดีแก่พวกเจ้า สําหรับทุกคนในพวกเขานั้น คือสิ่งที่เขาได้ขวนขวายไว้จากการทําบาปส่วนผู้ที่มีบทบาทมากในเรื่องนี้ในหมู่พวกเขานั้น เขาผู้นั้นจะได้รับการลงโทษอย่างมหันต์');
INSERT INTO "king_fahad_thai" VALUES(24,12,'24:12','เมื่อพวกเจ้าได้ยินข่าวเท็จนี้ ทําไมบรรดามุอ์มินและบรรดามุอ์มินะฮ์ จึงไม่คิดเปรียบเทียบกับตัวของพวกเขาเองในทางที่ดี และกล่าวว่า “นี่เป็นเรื่องโกหกอย่างชัดแจ้ง”');
INSERT INTO "king_fahad_thai" VALUES(24,13,'24:13','ทําไมพวกเขาจึงไม่นําพยานสี่คนมาเพื่อมัน หากพวกเขาไม่นําพยานเหล่านั้นมาแล้ว ดังนั้นชนเหล่านั้น ณที่อัลลอฮ์พวกเขาเป็นผู้กล่าวเท็จ');
INSERT INTO "king_fahad_thai" VALUES(24,14,'24:14','และหากมิใช่ความโปรดปรานของอัลลอฮ์แก่พวกเจ้า และความเมตตาของพระองค์ทั้งในโลกนี้และโลกหน้าแล้ว แน่นอนการลงโทษอย่างมหันต์ก็จะประสบแก่พวกเจ้า ในสิ่งที่พวกเจ้ากําลังง่วนกันอยู่');
INSERT INTO "king_fahad_thai" VALUES(24,15,'24:15','ขณะที่พวกเจ้าได้รับข่าวนั้น ด้วยการพูดกันระหว่างพวกเจ้า และพวกเจ้าพูดกันในสิ่งที่พวกเจ้าไม่มีความรู้ และพวกเจ้าคิดว่ามันเป็นเรื่องเล็ก แต่ ณ ที่อัลลอฮ์นั้นมันเป็นเรื่องใหญ่');
INSERT INTO "king_fahad_thai" VALUES(24,16,'24:16','เมื่อพวกเจ้าได้ยินมัน ทําไมพวกเจ้าจึงไม่กล่าวว่า “ไม่บังควรที่เราจะพูดถึงเรื่องนี้ มหาบริสุทธิ์แห่งพระองค์ท่านนี่มันเป็นการกล่าวร้ายอย่ามหันต์ !”');
INSERT INTO "king_fahad_thai" VALUES(24,17,'24:17','อัลลอฮ์ทรงตักเตือนพวกเจ้า เพื่อมิให้กลับไปประพฤติเช่นนี้อีกเป็นอันขาด หากพวกเจ้าเป็นผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(24,18,'24:18','และอัลลอฮ์ทรงชี้แจงโองการทั้งหลายอย่างชัดเจนแก่พวกเจ้า และอัลลอฮ์เป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(24,19,'24:19','แท้จริงบรรผู้ชอบที่จะให้เรื่องบัดสีแพร่หลายไปในหมู่ผู้ศรัทธานั้น พวกเขาจะได้รับการลงโทษอย่างเจ็บปวด ทั้งในโลกนี้และโลกหน้า และอัลลอฮ์ทรงรอบรู้และพวกเจ้าไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(24,20,'24:20','และหากมิใช่ความโปรดปรานของอัลลอฮ์แก่พวกเจ้า และความเมตตาของพระองค์แล้ว และแท้จริงอัลลอฮ์นั้นเป็นผู้ทรงเอ็นดู ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(24,21,'24:21','โอ้ บรรดาผู้ศรัทธาเอ๋ย ! พวกเจ้าอย่าคิดตามทางเดินของชัยฎอน และผู้ใดติดตามทางเดิมของชัยฎอน แท้จริงมันจะใช้ให้ทําการลามกและความชั่ว และหากมิใช่ความโปรดปรานของอัลลอฮ์แก่พวกเจ้า และความเมตตาของพระองค์แล้ว ก็จะไม่มีผู้ใดเลยหมู่พวกเจ้าบริสุทธิ์ แต่อัลลอฮ์ทรงให้ผู้ที่พระองค์ทรงประสงค์บริสุทธิ์ และอัลลอฮ์เป็นผู้ทรงได้ยินผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(24,22,'24:22','และผู้มีเกียรติและผู้มั่งคั่งในหมู่พวกเจ้าอย่าได้สาบานที่จะไม่ให้ (ความช่วยเหลือ) แก่ญาติมิตร และคนยากจน และผู้อพยพในหนทางของอัลลอฮ์ และพวกเขาจงอภัย และยกโทษ (ให้แก่พวกเขาเถิด) พวกเจ้าจะไม่ชอบหรือที่อัลลอฮ์จะทรงอภัยให้แก่พวกเจ้า และอัลลอฮ์นั้นเป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(24,23,'24:23','แท้จริงบรรดาผู้กล่าวโทษบรรดาหญิงบริสุทธิ์ หญิงไม่รู้เรื่องอะไร หญิงผู้ศรัทธา พวกเขาถูกสาปแช่งทั้งในโลกนี้และโลกหน้า และสําหรับพวกเขาจะได้รับการลงโทษอย่างมหันต์');
INSERT INTO "king_fahad_thai" VALUES(24,24,'24:24','วัน ที่ลิ้นของพวกเขา และมือของพวกเขา และเท้าของพวกเขา จะเป็นพยานปรักปรําพวกเขา ตามที่พวกเขาได้กระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(24,25,'24:25','วันนั้นอัลลอฮ์จะทรงตอบแทนแก่พวกเขาอย่างครบถ้วน ตามส่วนแห่งการตอบแทนของพวกเขาอย่างแท้จริง และพวกเขาจะรู้ว่า แท้จริงอัลลอฮ์นั้น พระองค์คือผู้ทรงสัจจะ ผู้ทรงเปิดเผย');
INSERT INTO "king_fahad_thai" VALUES(24,26,'24:26','หญิงชั่วย่อมคู่ควรกับชายชั่ว และชายชั่วย่อมคู่ควรกับหญิงชั่ว และหญิงดีย่อมคู่ควรกับชายดี และชายดีย่อมคู่ควรกับหญิงดี ชนเหล่านั้นเป็นผู้บริสุทธิ์จากสิ่งที่พวกเขากล่าวร้าย สําหรับพวกเขา (ผู้ถูกล่าวร้าย) จะได้รับการอภัยโทษ และเครื่องยังชีพอันมีเกียรติ');
INSERT INTO "king_fahad_thai" VALUES(24,27,'24:27','โอ้บรรดาผู้ศรัทธาเอ๋ย พวกเจ้าอย่าเข้าไปในบ้านใดอื่นจากบ้านของพวกเจ้า จนกว่าจะขออนุญาติและให้สลามแก่เจ้าของบ้านเสียก่อน เช่นนั้นแหละเป็นการดีสําหรับพวกเจ้า หวังว่าพวกเจ้าจะใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(24,28,'24:28','เมื่อพวกเจ้าไม่พบผู้ใดในบ้านนั้นก็อย่าเข้าไป จนกว่าจะได้รับอนุญาตแก่พวกเจ้า และเมื่อมีการกล่าวแก่พวกเจ้าว่า จงกลับไป ก็จงกลับไปมันเป็นการเหมาะสมยิ่งแก่พวกเจ้า และอัลลอฮ์ทรงรอบรู้สิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(24,29,'24:29','ไม่ถือเป็นความผิดแก่พวกเจ้าที่จะเข้าไปในสถานที่ใด ที่มิใช่เป็นที่พักอาศัย ซึ่ง ณ ที่นั้นมีสิ่งที่เป็นประโยชน์แก่พวกเจ้า และอัลลอฮ์ทรงรู้ดีสิ่งที่พวกเจ้าเปิดเผยและสิ่งที่พวกเจ้าปกปิด');
INSERT INTO "king_fahad_thai" VALUES(24,30,'24:30','จงกล่าวเถิดมุฮัมมัดแก่บรรดามุอ์มิน ให้พวกเขาลดสายตาของพวกเขาลงต่ํา และให้พวกเขารักษาทวารของพวกเขา นั่นเป็นการบริสุทธิ์ยิ่งแก่พวกเขา แท้จริงอัลลอฮ์ทรงรอบรู้สิ่งที่พวกเขากระทํา');
INSERT INTO "king_fahad_thai" VALUES(24,31,'24:31','และจงกล่าวเถิดมุฮัมมัดแก่บรรดามุอ์มินะฮ์ให้พวกเธอลดสายตาของพวกเธอลงต่ํา และให้พวกเธอรักษาทวารของพวกเธอ และอย่าเปิดเผยเครื่องประดับของพวกเธอ เว้นแต่สิ่งที่พึงเปิดเผยได้ และให้เธอปิดด้วยผ้าคลุมศรีษะของเธอลงมาถึงหน้าอกของเธอ และอย่าให้เธอเปิดเผยเครื่องประดับของพวกเธอ เว้นแต่แก่สามีของพวกเธอ หรือบิดาของสามีของพวกเธอ หรือลูกชายของพวกเธอ หรือลูกชายสามีของพวกเธอ หรือพี่ชายน้องชายของพวกเธอ หรือลูกชายของพี่ชายน้องชายของพวกเธอหรือลูกชายของพี่สาวน้องสาวของพวกเธอ หรือพวกผู้หญิงของพวกเธอ หรือที่มือขวาของพวกเธอครอบครอง (ทาสและทาสี) หรือคนใช้ผู้ชายที่ไม่มีความรู้สึกทางเพศ หรือเด็กที่ยังไม่รู้เรื่องเพศสงวนของผู้หญิง และอย่าให้เธอกระทืบเท้าของพวกเธอ เพื่อให้ผู้อื่นรู้สิ่งที่พวกเธอควรปกปิดในเครื่องประดับของพวกเธอ และพวกกเจ้าทั้งหลายจงขอลุแก่โทษต่ออัลลอฮ์เถิด โอ้ บรรดาผู้ศรัทธาเอ๋ย เพื่อว่าพวกเจ้าจะได้รับชัยชนะ');
INSERT INTO "king_fahad_thai" VALUES(24,32,'24:32','และจงให้พวกเจ้าแต่งงานกับผู้เป็นโสดในหมู่พวกเจ้า และกับคนดี ๆ จากปวงบ่าวผู้ชายของพวกเจ้า และบ่าวผู้หญิงของพวกเจ้า หากพวกเขายากจน อัลลอฮ์ทรงให้พวกเขาร่ํารวยขึ้นจากความโปรดปรานของพระองค์ และอัลลอฮ์นั้นเป็นผู้ทรงไพบูลย์ ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(24,33,'24:33','และบรรดาผู้ที่ยังไม่มีโอกาสแต่งงานก็จงให้เขาข่มความใคร่ จนกว่าอัลลอฮฺจะทรงให้พวกเขาร่ํารวยขึ้นจากความโปรดปรานของพระองค์ และบรรดาผู้ที่ต้องการจะไถ่ตัวให้เป็นอิสระจากผู้ที่มือขวาของพวกเจ้าครอบครอง พวกเจ้าจงทําสัญญากับพวกเขา หากพวกเจ้ารู้ว่าเป็นการดีกับพวกเขา และจงบริจาคแก่พวกเขาซึ่งทรัพย์สมบัติของอัลลอฮฺ ที่พระองค์ทรงประทานแก่พวกเจ้า และพวกเจ้าอย่าบังคับบรรดาทาสีของพวกเจ้าให้ผิดประเวณี หากนางประสงค์จะอยู่อย่างบริสุทธิ์ แต่พวกเจ้าต้องการผลประโยชน์แห่งการดํารงชีวิตในโลกนี้ และผู้ใดบังคับพวกนางเช่นนั้น ดังนั้นหลังจากการบังคับพวกนาง แท้จริงอัลลอฮฺเป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(24,34,'24:34','และโดยแน่นอน เราได้ประทานโองการต่าง ๆ อันชัดแจ้งแก่พวกเจ้าแล้ว และอุทาหรณ์จากบรรดาผู้ได้ล่วงลับไปก่อนพวกเจ้า และข้อตักเตือนแก่บรรดาผู้ยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(24,35,'24:35','อัลลอฮ์ทรงเป็นดวงประทีปแห่งชั้นฟ้าทั้งหลายและแผ่นดิน อุปมาดวงประทีปของพระองค์เสมือนดังช่องตามผนังที่มีตะเกียง ตะเกียงนั้นอยู่ในโคมแก้ว โคมแก้วเป็นเสมือนดวงดาวที่ประกายแสง ถูกจุดจากน้ํามันของต้นไม้ที่มีความจําเริญคือต้นมะกอก มันมิได้อยู่ทางตะวันออก และมิได้อยู่ทางตะวันออก น้ํามันของมันแทบประกายแสงออกมา แม้นว่าไฟมิได้กระทบมัน ดวงประทีปซ้อนดวงประทีป อัลลอฮ์ทรงนําทางด้วยดวงประทีปของพระองค์แก่ผู้ที่พระองค์ทรงประสงค์ และอัลลอฮ์ทรงยกอุทาหรณ์ทั้งหลายนี้เพื่อมนุษยชาติและอัลลอฮ์เป็นผู้ทรงรอบรู้ทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(24,36,'24:36','ในบรรดาบ้าน (หมายถึงมัสยิด) อัลลอฮฺทรงอนุญาติให้เทิดพระเกียรติ และให้พระนามของพระองค์ถูกรําลึกอยู่เสมอ เพื่อที่จะแซ่ซร้องสดุดีแด่พระองค์ในนั้น ทั้งในยามเช้าและยามพลบค่ํา');
INSERT INTO "king_fahad_thai" VALUES(24,37,'24:37','บรรดาชายผู้ที่การค้าและการขายมิได้ทําให้พวกเขาหันห่างออกจากการรําลึกถึงอัลลอฮ์และการดํารงละหมาด และการจ่ายซะกาต เพราะพวกเขากลัววันที่หัวใจและสายตาจะเหลือกลานในวันนั้น');
INSERT INTO "king_fahad_thai" VALUES(24,38,'24:38','เพื่ออัลลอฮ์จะทรงตอบแทนพวกเขาอย่างดีเยี่ยม ตามที่พวกเขาได้กระทําไว้ และพระองค์จะทรงเพิ่มให้พวกเขาอีกจากความโปรดปรานของพระองค์ และอัลลอฮ์ทรงประทานปัจจัยยังชีพแก่ผู้ที่พระองค์ทรงประสงค์ โดยปราศจากการคํานวณ');
INSERT INTO "king_fahad_thai" VALUES(24,39,'24:39','และบรรดาผู้ปฏิเสธศรัทธานั้น การงานของพวกเขาเปรียบเสมือนภาพลวงตาในที่ราบโล่งเตียน คนกระหายน้ําคิดว่ามันเป็นแอ่งน้ํา เมื่อเขามาถึงมันเขาจะไม่พบสิ่งใดเลย แต่เขาได้พบอัลลอฮ์ทรงอยู่ประจันหน้าเขา แล้วพระองค์ทรงตอบแทนบัญชีการงานของเขาอย่างครบครัน และอัลลอฮ์นั้นทรงเป็นผู้รีบเร่งในการตอบแทน');
INSERT INTO "king_fahad_thai" VALUES(24,40,'24:40','หรือเปรียบเสมือนความมืดมนทั้งหลายในท้องทะเลลึก มีคลื่นซ้อนคลื่นท่วมมิดตัวเขาและเบื้องบนของมันก็มีเมฆหนาทึบซ้อนกันชั้นแล้วชั้นเล่า เมื่อเขาเอามือของเขาออกมาเขาแทบจะมองไม่เห็นมัน และผู้ใดที่อัลลอฮ์ไม่ทรงทําให้เขาได้รับแสงสว่าง เขาก็จะไม่ได้รับแสงสว่างเลย');
INSERT INTO "king_fahad_thai" VALUES(24,41,'24:41','เจ้ามิเห็นดอกหรือว่า แท้จริงอัลลอฮ์นั้นผู้ที่อยู่ในชั้นฟ้าทั้งหลายและแผ่นดิน และนกที่กางปีกอยู่ ต่างก็แซ่ซ้องสดุดีพระองค์ ทั้งหมดนั้นต่างก็รู้การสวดขอพระของเขาและการแซ่ซ้องสดุดีของเขา และอัลลอฮ์ทรงรอบรู้ในสิ่งที่พวกเขากระทํา');
INSERT INTO "king_fahad_thai" VALUES(24,42,'24:42','และอํานาจอันเด็ดขาดแห่งชั้นฟ้าทั้งหลายและแผ่นดินนั้น เป็นสิทธิ์ของอัลลอฮ์ และยังอัลลอฮ์คือจุดหมายปลายทาง');
INSERT INTO "king_fahad_thai" VALUES(24,43,'24:43','เจ้ามิได้เห็นดอกหรือว่า แท้จริงอัลลอฮ์นั้นทรงให้เมฆลอย แล้วทรงทําให้ประสานตัวกันแล้วทรงทําให้รวมกันเป็นกลุ่มก้อน แล้วเจ้าก็จําเห็นฝนโปรยลงมาจากกลุ่มเมฆนั้น และพระองค์ทรงให้มันตกลงมาจากฟากฟ้า มีขนาดเท่าภูเขา ในนั้นมีลูกเห็บ แล้วพระองค์จะทรงให้มันหล่นลงมาโดนผู้ที่พระองค์ทรงประสงค์ และพระองค์จะทรงให้มันผ่านพ้นไปจากผู้ที่พระองค์ทรงประสงค์ แสงประกายของสายฟ้าแลบเกือบจะเฉี่ยวสายตาผู้มอง');
INSERT INTO "king_fahad_thai" VALUES(24,44,'24:44','อัลลอฮ์ทรงให้กลางคืนและกลางวันหมุนเวียนกลับไปกลับมา แท้จริงในลักษณะเช่นนั้นแน่นอนมันเป็นข้อเตือนสติแก่ผู้มีสายตาพิจารณา');
INSERT INTO "king_fahad_thai" VALUES(24,45,'24:45','และอัลลอฮ์ทรงให้บังเกิดสิ่งมีชีวิตทุกชนิดจากน้ํา ดังนั้นในหมู่พวกมัน มันจะเคลื่อนย้ายด้วยท้องของมัน และในหมู่พวกมัน มันจะเดินด้วยแท้าทั้งสี่ และในหมู่พวกมัน มันจะเดินด้วยเท้าทั้งสี่ อัลลอฮ์ทรงบังเกิดสิ่งที่พระองค์ทรงประสงค์ แท้จริง อัลลอฮ์นั้นทรงอานุภาพเหนือทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(24,46,'24:46','โดยแน่นอน เราได้ประทานโองการต่าง ๆ อันชัดแจ้ง และอัลลอฮ์ทรงชี้แนะทางแก่ผู้ที่พระองค์ทรงประสงค์ ยังทางที่เที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(24,47,'24:47','และพวกเขากล่าวกันว่า เราศรัทธาต่ออัลลอฮ์และอัลร่อซูล และเราเชื่อฟังปฏิบัติตามแล้วส่วนหนึ่งจากพวกเขาก็หันหลังกลับไปหลังจากนั้น และพวกเหล่านั้นมิใช่ผู้ศรัทธาอย่างแท้จริง');
INSERT INTO "king_fahad_thai" VALUES(24,48,'24:48','และเมื่อพวกเขาถูกเรียกร้องไปสู่อัลลอฮ์และร่อซูลของพระองค์ เพื่อให้ตัดสินระหว่างพวกเขา เมื่อนั้นฝ่ายหนึ่งจากพวกเขาพากันผินหลังให้');
INSERT INTO "king_fahad_thai" VALUES(24,49,'24:49','และหากว่าความจริงอยู่ข้างพวกเขาแล้ว พวกเขาจะรีบมาหาเขา (มุฮัมมัด) อย่างนอบน้อม');
INSERT INTO "king_fahad_thai" VALUES(24,50,'24:50','ในหัวใจของพวกเขามีโรคกระนั้นหรือ หรือว่าพวกเขาสงสัย หรือว่าพวกเขากลัวว่าอัลลอฮ์และร่อซูลของพระองค์จะลําเอียงออกจากพวกเขา หามิได้ พวกเขาเหล่านี้เป็นผู้อธรรมต่างหาก');
INSERT INTO "king_fahad_thai" VALUES(24,51,'24:51','แท้จริงคํากล่าวของบรรดาผู้ศรัทธา เมื่อพวกเขาถูกเรียกร้องไปสู่อัลลอฮ์และร่อซูลของพระองค์เพื่อให้ตัดสินระหว่างพวกเขา พวกเขาจะกล่าวว่า “เราได้ยินแล้ว และเราเชื่อฟังปฏิบัติตาม” และชนเหล่านี้พวกเขาเป็นผู้ประสบความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(24,52,'24:52','และผู้ใดเชื่อฟังปฏิบัติตามอัลลอฮ์และร่อซูลของพระองค์ และเกรงกลัวอัลลอฮ์และยําเกรงพระองค์แล้ว ดังนั้น ชนเหล่านั้นพวกเขาเป็นผู้ได้รับชัยชนะ');
INSERT INTO "king_fahad_thai" VALUES(24,53,'24:53','และพวกเขาได้สาบานด้วยพระนามของอัลลอฮ์ โดยการสาบานอย่างแข็งขันของพวกเขาว่า หากเจ้ามีคําสั่งแก่พวกเขา แน่นอนพวกเขาก็จะออกไป จงกล่าวเถิดมุฮัมมัด “พวกเจ้าอย่าสาบานเลย การเชื่อฟัง (ของพวกเจ้านั้น) เป็นที่รู้กันดี แท้จริง อัลลอฮ์นั้นทรงรู้ดียิ่งในสิ่งที่พวกเจ้ากระทํา”');
INSERT INTO "king_fahad_thai" VALUES(24,54,'24:54','จงกล่าวเถิดมุฮัมมัด “พวกเจ้าจงเชื่อฟังปฏิบัติตามอัลลอฮฺ และจงเชื่อฟังปฏิบัติตามอัลร่อซูล หากพวกเขาผินหลังให้ แท้จริงหน้าที่ของเขา (ร่อซูล) คือสิ่งที่ได้ถูกมอบหมาย และหน้าที่ของพวกเจ้าคือสิ่งที่ได้ถูกมอบหมายเช่นกัน และหากพวกเจ้าเชื่อฟังปฏิบัติตามเขาแล้ว พวกเจ้าก็จะอยู่ในทางที่ถูกต้อง (ฮิดายะฮฺ) และหน้าที่ของอัลร่อซูลไม่มีอื่นใด นอกจากการเผยแพร่อันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(24,55,'24:55','อัลลอฮ์ทรงสัญญากับบรรดาผู้ศรัทธาในหมู่พวกเจ้า และบรรดาผู้กระทําความดีทั้งหลายว่า แน่นอนพระองค์จะทรงให้พวกเขาเป็นตัวแทนสืบช่วงในแผ่นดิน เสมือนดังที่พระองค์ทรงให้บรรดาชนก่อนพวกเขา เป็นตัวแทนสืบช่วงมาก่อนแล้ว และพระองค์จะทรงทําให้ศาสนาของพวกเขาซึ่งพระองค์ทรงโปรดปราน เป็นที่มั่นคงเป็นเกียรติแก่พวกเขา และแน่นอนพระองค์จะทรงเปลี่ยนแปลงให้พวกเขาได้รับความปลอดภัย หลังจากความกลัวของพวกเขา โดยที่พวกเขาจะต้องเคารพภักดีข้าไม่ตั้งภาคีอื่นใดต่อข้า และผู้ใดปฏิเสธศรัทธาหลังจากนั้น ชนเหล่านั้นพวกเขาคือผู้ฝ่าฝืน');
INSERT INTO "king_fahad_thai" VALUES(24,56,'24:56','และพวกเจ้าจงดํารงละหมาด และจงบริจาคอัซซะกาฮ์ และจงเชื่อฟังปฏิบัติตามอัลร่อซูล เพื่อพวกเจ้าจะได้รับควาเมตตา');
INSERT INTO "king_fahad_thai" VALUES(24,57,'24:57','และเจ้า (มุฮัมมัด) อย่าคิดว่า บรรดาผู้ปฏิเสธศรัทธานั้นพวกเขาจะรอดพ้นไปในแผ่นดินนี้ และที่พํานักของพวกเขาคือไฟนรก และมันเป็นทางกลับที่ชั่วช้า');
INSERT INTO "king_fahad_thai" VALUES(24,58,'24:58','โอ้บรรดาผู้ศรัทธาเอ๋ย ! จงให้บรรดาผู้ที่มือขวาของพวกเจ้าครอบครอง และบรรดาผู้ที่ยังไม่บรรลุศาสนภาวะในหมู่พวกเจ้า ขออนุญาตพวกเจ้าสามเวลาคือ ก่อนเวลาละหมาดฟัจญริ และเวลาพวกเจ้าเปลื้องเสื้อผ้าในเวลากลางวัน และหลังจากเวลาละหมาดอิชาอฺ ทั้งสามนี้เป็นเวลาส่วนตัวสําหรับพวกเจ้า หลังจากนี้แล้วไม่เป็นที่น่าตําหนิแก่พวกเจ้าและแก่พวกเขา เพราะพวกเขาวนเวียนรับใช้บางคนในหมู่พวกเจ้า เช่นนั้นแหละอัลลอฮฺทรงชี้แจงโองการทั้งหลายให้เป็นที่ชัดแจ้งแก่พวกเจ้า และอัลลอฮฺเป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(24,59,'24:59','และเมื่อเด็ก ๆ ในหมู่พวกเจ้าบรรลุศาสนภาวะ ก็จงให้พวกเขาขออนุญาตเช่นเดียวกับบรรดาชนก่อนหน้าพวกเขาได้ขออนุญาต เช่นนั้นแหละอัลลอฮ์ทรงชี้แจงโองการทั้งหลายของพระองค์ให้เป็นที่ชัดแจ้งแก่พวกเจ้า และอัลลอฮ์เป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(24,60,'24:60','และบรรดาหญิงวัยชราซึ่งพวกนางไม่ปรารถนาที่จะสมรสแล้ว ไม่เป็นที่ตําหนิแก่พวกนางที่จะเปลื้องเสื้อผ้าของนางออก โดยไม่เปิดเผยส่วนงดงาม และหากพวกนางงดเว้นเสียก็จะเป็นการดีแก่พวกนาง และอัลลอฮ์นั้นเป็นผู้ทรงได้ยิน ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(24,61,'24:61','ไม่เป็นการลําบากใจอันใด แก่คนตาบอดและไม่เป็นการลําบากใจอันใดแก่คนพิการ และไม่เป็นการลําบากใจอันใดแก่คนป่วย และไม่เช่นกันแก่ตัวของพวกเจ้า ที่จะรับประทานที่บ้านของพวกเจ้า หรือบ้านของพ่อ ๆ ของพวกเจ้า หรือบ้านของแม่ ๆ ของพวกเจ้าหรือบ้านของพี่ชายน้องชายของพวกเจ้า หรือบ้านของพี่สาวน้องสาวของพวกเจ้าหรือบ้านของลุง อา ของพวกเจ้า หรือบ้านของป้า อาสาวของพวกเจ้า หรือบ้านของลุง น้า ของพวกเจ้า หรือบ้านของป้า น้าสาวของพวกเจ้า หรือบ้านที่พวกเจ้าครอบครองกุญแจของมัน หรือบ้านของเพื่อน ๆ ของพวกเจ้าไม่เป็นการลําบากใจอันใดแก่พวกเจ้า ที่จะร่วมรับประทานกันเป็นหมู่หรือแยกกัน เมื่อพวกเจ้าเข้าไปในบ้านก็จงกล่าวสลามให้แก่ตัวของพวกเจ้าเอง เป็นการคํานับอันจําเริญยิ่งจากอัลลอฮ์ เช่นนั้นแหละ อัลลอฮ์ทรงชี้แจงโองการทั้งหลายให้เป็นที่ชัดแจ้งแก่พวกเจ้าเพื่อพวกเจ้าจะได้ใช้สติปัญญาพิจารณา');
INSERT INTO "king_fahad_thai" VALUES(24,62,'24:62','แท้จริงบรรดาผู้ศรัทธา (ที่แท้จริง) นั้น คือบรรดาผู้ศรัทธาต่ออัลลอฮ์และร่อซูลของพระองค์และเมื่อพวกเขามารวมกันอยู่กับเขา (มุฮัมมัด) ในกิจการที่สําคัญ พวกเขาจะไม่ผละออกไป จนกว่าพวกเขาจะขออนุญาตจากเขา(มุฮัมมัด) เสียก่อน แท้จริงบรรดาผู้ที่ขออนุญาตต่อเจ้านั้น เขาเหล่านั้นคือบรรดาผู้ที่ศรัทธาต่ออัลลอฮ์และร่อซูลของพระองค์ ดังนั้น เมื่อพวกเขาขออนุญาตต่อเจ้าเพื่อกิจการบางอย่างของพวกเขาแล้ว ก็จงอนุญาตแก่ผู้ที่เจ้าพึงประสงค์ในหมู่พวกเขาเถิด และจงขออภัยต่ออัลลอฮ์ให้แก่พวกเขา แท้จริงอัลลอฮ์นั้นทรงเป็นผู้อภัย ผู้เมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(24,63,'24:63','พวกเจ้าอย่าทําให้การร้องเรียกของอัลร่อซูลในหมู่พวกเจ้า เป็นเช่นเดียวกับการร้องเรียกในระหว่างพวกเจ้าด้วยกันเอง แน่นอน อัลลอฮ์ทรงรู้บรรดาผู้ที่แอบหลีกออกไปในหมู่พวกเจ้า ดังนั้นบรรดาผู้ที่ฝ่าฝืนคําสั่งของเขา (มุฮัมมัด) จงระวังตัวเถิดว่า เคราะห์กรรมจะเกิดขึ้นแก่พวกเขา หรือว่าการลงโทษอันเจ็บปวดจะเกิดขึ้นแก่พวกเขาเช่นกัน');
INSERT INTO "king_fahad_thai" VALUES(24,64,'24:64','พึงรู้เถิดว่า แท้จริงสิ่งที่อยู่ในชั้นฟ้าทั้งหลายและแผ่นดินนั้น เป็นกรรมสิทธิ์ของอัลลอฮ์ แน่นอน อัลลอฮ์ทรงรอบรู้สิ่งที่พวกเจ้ามีสภาพเป็นอยู่ และวันที่พวกเขาจะถูกนํากลับไปสู่พระองค์ ดังนั้น พระองค์จะทรงแจ้งแก่พวกเขาในสิ่งที่พวกเขาได้กระทําไว้ และอัลลอฮ์เป็นผู้ทรงรอบรู้ทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(25,1,'25:1','ความจําเริญยิ่งแด่พระองค์ ผู้ทรงประทานอัลฟุรกอน แก่บ่าวของพระองค์(มุฮัมมัด) เพื่อเขาจะได้เป็นผู้ตักเตือนแก่ปวงบ่าวทั้งมวล');
INSERT INTO "king_fahad_thai" VALUES(25,2,'25:2','สําหรับพระองค์ เป็นผู้ครอบครองบรรดาชั้นฟ้าและแผ่นดิน และพระองค์จะไม่ตั้งผู้ใดเป็นพระบุตร และสําหรับพระองค์นั้น ไม่มีหุ้นส่วนร่วมกับพระองค์ในการครองอํานาจ และพระองค์ทรงให้บังเกิดทุกสิ่ง แล้วทรงกําหนดมันให้เป็นไปตามกฏสภาวะ');
INSERT INTO "king_fahad_thai" VALUES(25,3,'25:3','พวกเขาได้เคารพบูชาพระเจ้าอื่นๆ จากพระองค์ โดยที่พระเจ้าเหล่านั้นมิได้สร้างสิ่งใดทั้ง ๆ ที่พวกเขาถูกสร้างขึ้นมา และพวกเขาไม่มีอํานาจที่จะให้โทษและให้คุณแก่ตัวเองได้ และพวกเขาไม่มีอํานาจควบคุมความตายและความเป็นและการฟื้นคืนชีพ');
INSERT INTO "king_fahad_thai" VALUES(25,4,'25:4','และบรรดาผู้ปฏิเสธศรัทธากล่าวว่า “แท้จริงอัลกุรอานนี้มิใช่อันใด นอกจากการโกหกที่มุฮัมมัดได้กุขึ้นเอง และหมู่ชนอื่นๆ ได้ช่วยเขาในเรื่องนี้” ดังนั้น แน่นอนพวกเขาได้นํามาซึ่งความอยุติธรรมและการโกหก');
INSERT INTO "king_fahad_thai" VALUES(25,5,'25:5','และพวกเขา กล่าวว่า “อัลกุรอานเป็นนิยายของประชาชาติสมัยก่อน ๆ ที่เขียนกันขึ้นแล้วถูกนํามาอ่านให้ขึ้นใจ ทั้งเวลาเช้าและเวลาเย็น”');
INSERT INTO "king_fahad_thai" VALUES(25,6,'25:6','จงกล่าวเถิดมุฮัมมัด “ผู้ทรงรอบรู้ความลับในชั้นฟ้าทั้งหลายและแผ่นดินเป็นผู้ประทานมันลง แท้จริงพระองค์เป็นผู้ทรงอภัยผู้ทรงเมตตาเสมอ”');
INSERT INTO "king_fahad_thai" VALUES(25,7,'25:7','และพวกเขากล่าวว่า “อะไรกันกับร่อซูลคนนี้ ! เขากินอาหารและเดินในตลาด ทําไมจึงไม่มีมะลักถูกส่งมากับเขา เพื่อจะได้เป็นผู้ตักเตือนร่วมกับเขา');
INSERT INTO "king_fahad_thai" VALUES(25,8,'25:8','“หรือมีคลังสมบัติถูกโยนลงมาให้เขา หรือให้เขามีสวนแห่งหนึ่ง เพื่อเขาจะได้กินสิ่งที่มีอยู่ในนั้นจากมัน” และบรรดาผู้อธรรมกล่าวขึ้นว่า ”พวกท่านมิได้ปฏิบัติตามผู้ใด นอกจากชายผู้ถูกอาคมเท่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(25,9,'25:9','จงดูเถิด! พวกเขาได้เปรียบเปรยตัวอย่างต่างๆ แก่เจ้า (มุฮัมมัด) อย่างไร? พวกเขาจึงหลงทางแล้วพวกเขาก็ไม่สามารถจะพบทางแห่งความจริงได้');
INSERT INTO "king_fahad_thai" VALUES(25,10,'25:10','ความจําเริญยิ่งแด่พระองค์ ผู้ซึ่งหากพระองค์ทรงประสงค์จะให้เจ้ามีดียิ่งกว่านั้น คือมีสวนกลากหลาย ณ เบื้องล่างมีลําน้ําหลายสายไหลผ่าน และทรงให้เจ้ามีวังหลายหลัง');
INSERT INTO "king_fahad_thai" VALUES(25,11,'25:11','แต่ว่าพวกเขาปฏิเสธวันอวสาน และเราได้เตรียมไฟอันร้อนแรงไว้สําหรับผู้ปฏิเสธวันอวสาน');
INSERT INTO "king_fahad_thai" VALUES(25,12,'25:12','เมื่อนรกญะฮันนัมเป็นพวกเขาจากที่ไกล ๆ พวกเขาก็จะได้ยินเสียงคุไหม้และเสียงเดือดพล่านของมัน');
INSERT INTO "king_fahad_thai" VALUES(25,13,'25:13','และเมื่อพวกเขาถูกโยนลงไปในสถานที่แคบ ในสภาพที่ถูกมัดมือติดกับลําคอ ณ ที่นั้นพวกเขาจะวิงวอนขอความพินาศให้แก่ตัวเขา');
INSERT INTO "king_fahad_thai" VALUES(25,14,'25:14','“ในวันนี้เจ้าอย่าได้วิงวอนขอความพินาศเพียงครั้งเดียว แต่จงวิงวอนขอความพินาศหลายๆ ครั้ง”');
INSERT INTO "king_fahad_thai" VALUES(25,15,'25:15','จงกล่าวเถิดมุฮัมมัด “สิ่งนั้น ดีกว่าหรือว่าสวนสวรรค์ชั่วนิรันดร์ ที่บรรดาผู้ยําเกรงถูกสัญญาไว้ว่า สําหรับพวกเขาจะได้รับการตอบแทนและทางกลับที่ดี');
INSERT INTO "king_fahad_thai" VALUES(25,16,'25:16','“สําหรับพวกเขาในสวนสวรรค์นั้น จะได้รับสิ่งที่พวกเขาประสงค์ โดยพํานักอยู่ตลอดกาล มันเป็นสัญญาที่ถูกวอนขอต่อพระเจ้าของเจ้า”');
INSERT INTO "king_fahad_thai" VALUES(25,17,'25:17','และวันที่พระองค์ทรงรวบรวมพวกเขาและบรรดาผู้ที่พวเขาเคารพภักดีอื่นจากอัลลอฮ์ แล้วพระองค์ตรัสขึ้นว่า “พวกเจ้าทําให้บรรดาบ่าวของข้าเหล่านั้นหลงทางกระนั้นหรือ? หรือว่าพวกเขาหลงทางกันเอง?”');
INSERT INTO "king_fahad_thai" VALUES(25,18,'25:18','พวกเขากล่าวว่า “มหาบริสุทธิ์พระองค์ท่านการบังควรแก่พวกเรา ที่พวกเราจะยึดถือผู้คุ้มครองอื่นๆนอกจากพระองค์ แต่ทว่าพระองค์ได้ทรงประทานปัจจุยให้แก่พวกเขา และบรรพบุรุษของพวกเขา จนกระทั่งพวกเขาได้ลืมต่อการรําลึก และพวกเขาได้เป็นหมู่ชนที่วิบัติ”');
INSERT INTO "king_fahad_thai" VALUES(25,19,'25:19','แน่นอนพวกเขาได้ปฏิเสธพวกเจ้าในสิ่งที่พวกเจ้ากล่าว ดังนั้น พวกเจ้าจึงไม่สามารถเลิ่ยงมิให้มีการลงโทษ และไม่สามารถจะให้ความ ช่วยเหลือได้ และผูใดในหมู่พวกเจ้ากระทําผิด เราจะให้เขาลิ้มรสการลงโทษอันมหันต์');
INSERT INTO "king_fahad_thai" VALUES(25,20,'25:20','และเรามิได้ส่งคนใดจากบรรดาร่อซูลก่อนหน้าเจ้า นอกจากพวกเขาจะรับประทานอาหารอย่างสามัญชน และเดินท่องไปในตลาด และเราได้ทําให้บางคนในพวกเจ้าเป็นการทดสอบแก่อีกบางคน เพื่อดูว่าพวกเจ้าจะอดทนไหม ? และพระเจ้าของเจ้านั้นทรงเห็นทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(25,21,'25:21','และบรรดาผู้ที่ไม่หวังจะพบเรากล่าวว่า “ไฉนเล่ามะลาอิกะฮ์จึงไม่ถูกส่งลงมายังพวกเราหรือเราไม่เห็นพระเจ้าของเรา” แน่นอนพวกเขาหยิ่งยะโสในตัวของพวกเขามาก และพวกเขาได้ละเมิดขอบเขตอย่างมาก');
INSERT INTO "king_fahad_thai" VALUES(25,22,'25:22','วันที่พวกเขาเห็นมะลาอิกะฮ์ ในวันนั้นจะไม่มีข่าวดีสําหรับบรรดาผู้กระทําความผิด และมะลาอิกะฮ์จะกล่าวว่า “สวรรค์จะถูกห้ามอย่างเด็ดขาดสําหรับพวกเขา”');
INSERT INTO "king_fahad_thai" VALUES(25,23,'25:23','และเรามุ่งสู่ส่วนหนึ่งของการงานที่พวกเขาได้ปฏิบัติไป แล้วเราจะทําให้มันไร้คุณค่ากลายเป็นละอองผุ่นที่ปลิวว่อน');
INSERT INTO "king_fahad_thai" VALUES(25,24,'25:24','ชาวสวนสวรรค์ในวันนั้นจะอยู่ในที่พํานักอันดี และที่พักผ่อนอันสบายยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(25,25,'25:25','และวันที่ท้องฟ้าจะแตกออกจากก้อนเมฆ และมะลาอิกะฮ์ถูกส่งทยอยลงมา');
INSERT INTO "king_fahad_thai" VALUES(25,26,'25:26','ในวันนั้น อํานาจอันแท้จริงเป็นสิทธิ์ของพระผู้ทรงกรุณาปรานี และมันเป็นวันที่ลําบากแก่ผู้ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(25,27,'25:27','และวันที่ผู้อธรรมจะกัดมือของเขาแล้วจะกล่าวว่า “โอ้! ถ้าฉันได้ยึดแนวทางร่วมกับอัลร่อซูลก็จะเป็นการดี”');
INSERT INTO "king_fahad_thai" VALUES(25,28,'25:28','“โอ้ความวิบัติแก่ฉัน ! หากฉันไม่คบคนนั้นเป็นเพื่อน”');
INSERT INTO "king_fahad_thai" VALUES(25,29,'25:29','แน่นอน เขาได้ทําให้ฉันหลงผิดจากการตักเตือน หลังจากที่มันได้มันมายังฉัน และชัยฏอนมารร้ายนั้น มันเป็นผู้เหยียดหยามมนุษย์เสมอ”');
INSERT INTO "king_fahad_thai" VALUES(25,30,'25:30','และอัลร่อซูลได้กล่าวว่า “ข้าแต่พระเจ้าของข้าพระองค์ แท้จริงชนชาติของข้าพระองค์ได้ยึดเอาอัลกรุอานนี้เป็นที่ทอดทิ้งเสียแล้ว”');
INSERT INTO "king_fahad_thai" VALUES(25,31,'25:31','และเช่นนั้นแหละ เราได้ทําได้ทําได้มีศัตรูผู้กระทําผิดแก่นะบีทุกคน และพอเพียงแล้วที่พระเจ้าของเจ้าเป็นผู้แนะทางฮิดายะฮ์ และทรงเป็นผู้ช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(25,32,'25:32','และบรรดาผู้ปฏิเสธศรัทธา กล่าวว่า”ทําไมอัลกุรอานจึงไม่ถูกประทานลงมาแก่เขาครั้งเดียวกันทั้งหมด?” เช่นนั้นแหละเพื่อเราจะทําให้หัวใจของเจ้ามั่นคงหนักแน่น และเราได้จัดให้เป็นเป็นระเบียบเรียบร้อย');
INSERT INTO "king_fahad_thai" VALUES(25,33,'25:33','และพวกเขาจะไม่นําข้อเปรียบเทียบ(ข้อสงสัย) ใด ๆ มายังเจ้า เว้นแต่เราจะได้นําความจริงมาให้เจ้า และการอธิบายอย่างดียิ่ง');
INSERT INTO "king_fahad_thai" VALUES(25,34,'25:34','บรรดาผู้ที่ถูกลากให้มาชุมนุมกัน จะใช้ใบหน้าของพวกเขาเดินไปยังนรกญะฮันนัม ชนเหล่านั้นจะอยู่ในสถานที่ที่เลว และหลงทางยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(25,35,'25:35','และแน่นอน เราได้ประทานคัมภีร์(เตารอดฮ์) แก่มูซา และเราได้ให้พี่ชายของเขาคือฮารูนเป็นผู้ช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(25,36,'25:36','แล้วเราได้กล่าวว่า “เจ้าทั้งสองจงออกไปยังหมู่ชนที่ปฏิเสธ ไม่เชื่อโองการทั้งหลายของเรา” และเราได้ทําลายพวกเขาอย่างพินาศย่อยยับ');
INSERT INTO "king_fahad_thai" VALUES(25,37,'25:37','และหมู่ชนของนูห์ เมื่อพวกเขาปฏิเสธไม่เชื่อฟังบรรดาร่อซูล เราได้ให้พวกเขาจมน้ําตายและเราได้ทําให้พวกเขาเป็นสัญญาณหนึ่งแก่มนุษยชาติ และเราได้เตรียมการลงโทษอย่างเจ็บปวดไว้สําหรับบรรดาผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(25,38,'25:38','และเราได้ทําลายพวกอ๊าด และพวกษะมูด และชาวบ่อน้ํา และชนชาติอีกมากมายระหว่างนั้น');
INSERT INTO "king_fahad_thai" VALUES(25,39,'25:39','และชนชาติแต่ละสมัยเราได้นําหลักฐานมาชี้แจงแก่เขา และชนชาติแต่ละสมัยนั้นเราก็ได้ทําลายอย่างสิ้นซาก');
INSERT INTO "king_fahad_thai" VALUES(25,40,'25:40','และแน่นอน พวกเขาได้ผ่านมายังหมู่บ้านซึ่งถูกทําลายโดยก้อนหินจากฟากฟ้า แล้วพวกเขาไม่เห็นมันดอกหรือ ? เปล่าหรอก! พวกเขาไม่หวังที่จะกลับคืนชีพอีกต่างหาก');
INSERT INTO "king_fahad_thai" VALUES(25,41,'25:41','และเมื่อพวกเขาเห็นเจ้า(มุฮัมมัด) พวกเขาก็จะไม่ถือเอาเจ้าเป็นอย่างอื่น นอกจากเพื่อเป็นที่ล้อเลียน “นี่หละหรือที่อัลลอฮ์ทรงส่งมาให้เป็นร่อซูล?”');
INSERT INTO "king_fahad_thai" VALUES(25,42,'25:42','“เขาเกือบจะทําให้พวกเราหลงทางไปจาก(การสักการะบูชา) บรรดาพระเจ้าของเรา หากว่าเราไม่อดทนยึดมั่นต่อพระเจ้าเหล่านั้น” และพวกเขาจะรู้เมื่อพวกเขาได้พบเห็นการลงโทษว่าผู้ใดจะหลงทางกันแน่');
INSERT INTO "king_fahad_thai" VALUES(25,43,'25:43','เจ้าไม่เห็นดอกหรือ ผู้ที่ยึดเอาอารมณ์ต่ําของเขาเป็นพระเจ้าของเขา แล้วเจ้าจะเป็นผู้คุ้มครองเขากระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(25,44,'25:44','หรือเจ้าจะคิดว่า ส่วนใหญ่ของพวกเขาจะได้ยินหรือใช้สติปัญญา พวกเขามิใช่อื่นใดดอก นอกจากเป็นเช่นปศุสัตว์ ยิ่งไปกว่านั้น พวกเขายังจะหลงทางเสียอีก');
INSERT INTO "king_fahad_thai" VALUES(25,45,'25:45','เจ้ามิได้พิจารณายัง(อานุภาพแห่ง) พระเจ้าของเจ้าดอกหรือว่า พระองค์ทรงแผ่เงาออกอย่างไร และหากพระองค์ทรงประสงค์ แน่นอนพระองค์จะทรงให้มันหยุดนิ่ง แล้วเราได้ทําให้ดวงอาทิตย์เป็นสัญญาณหนึ่งในการนี้');
INSERT INTO "king_fahad_thai" VALUES(25,46,'25:46','แล้วเราได้ให้เงาสูญสิ้นไปยังเราที่ละน้อย ๆ');
INSERT INTO "king_fahad_thai" VALUES(25,47,'25:47','และพระองค์คือผู้ทรงทําให้กลางคืนเป็นอาภรณ์สําหรับพวกเจ้า และให้การนอนเป็นการพักผ่อน และทําให้กลางวันเป็นการเคลื่อนไหว');
INSERT INTO "king_fahad_thai" VALUES(25,48,'25:48','และพระองค์คือผู้ส่งลม เป็นการนําข่าวดีล่วงหน้า ท่ามกลางความเมตตาของพระองค์ และเราได้ประทานน้ําบริสุทธิ์ลงมาจากฟากฟ้า');
INSERT INTO "king_fahad_thai" VALUES(25,49,'25:49','เพื่อเราจะให้มีชีวิตด้วยมัน (น้ํา) แก่แผ่นดินที่แห้งแล้ง และเราจะให้สิ่งที่เราสร้างมันขึ้นมา เช่น ปศุสัตว์ และมนุษย์มากมาย ดื่มมัน');
INSERT INTO "king_fahad_thai" VALUES(25,50,'25:50','และโดยแน่นอนเราได้ชี้แจงมัน(อัลกุรอาน) ระหว่างพวกเขา เพื่อพวกเขาจะได้พิจารณา แต่ส่วนมากของมนุษย์ไม่ยอมรับนอกจากการดื้อดึงและปฏิเสธ');
INSERT INTO "king_fahad_thai" VALUES(25,51,'25:51','และหากเราประสงค์ แน่นอนเราก็จะส่งผู้ตักเตือนไปยังทุก ๆ เมือง');
INSERT INTO "king_fahad_thai" VALUES(25,52,'25:52','ดังนั้น เจ้าอย่าเชื่อฟังพวกปฏิเสธศรัทธาและจงต่อสู้ดิ้นรนกับพวกเขาด้วยมัน(อัลกุรอาน) โดยการต่อสู้ดิ้นรนอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(25,53,'25:53','และพระองค์คือผู้ทรงทําให้ทะเลทั้งสองบรรจบติดกัน อันนี้จืดสนิทและอันนี้เค็มจัดและทรงทําที่คั่นระหว่างมันทั้งสอง และที่กั้นขวางอันแน่นหนา');
INSERT INTO "king_fahad_thai" VALUES(25,54,'25:54','และพระองค์คือผู้ทรงบังเกิดมนุษย์จากน้ํา(อสุจิ) และทรงทําให้มีเชื้อสายและเครือญาติ และพระเจ้าของเจ้านั้นเป็นผู้ทรงอานุภาพ');
INSERT INTO "king_fahad_thai" VALUES(25,55,'25:55','และพวกเขาเคารพอิบาดะฮ์อื่นจากอัลลอฮ์ซึ่งมันไม่ให้คุณแก่พวกเขาและไม่ให้โทษแก่พวกเขา และผู้ปฏิเสธศรัทธานั้นเป็นผู้ช่วยเหลือ (ชัยฏอน) ให้ฝ่าฝืนพระเจ้าจองเขา');
INSERT INTO "king_fahad_thai" VALUES(25,56,'25:56','และเรามิได้ส่งเจ้ามาเพื่ออื่นใด นอกจากเป็นผู้แจ้งข่าวดีและผู้ตักเตือน');
INSERT INTO "king_fahad_thai" VALUES(25,57,'25:57','จงกล่าวเถิดมุฮัมมัด ”ฉันมิได้ขอค่าจ้างจากพวกท่านในการเผยแพร่ เว้นแต่ว่าผู้ใดประสงค์ก็ให้เขายึดเป็นแนวทางไปสู่พระเจ้าของเขา”');
INSERT INTO "king_fahad_thai" VALUES(25,58,'25:58','และเจ้าจงมอบหมายต่อพระผู้ทรงดํารงชีวิตตลอดกาล ไม่ตาย และจงแซ่ซร้องสดุดีด้วยการสรรเสริญพระองค์ และพอเพียงแล้วสําหรับพระองค์ ผู้ทรงรอบรู้ในความผิดทั้งหลายของปวงบ่าวของพระองค์');
INSERT INTO "king_fahad_thai" VALUES(25,59,'25:59','พระผู้ทรงสร้างชั้นฟ้าทั้งหลายและแผ่นดินและสิ่งที่มีอยู่ในระหว่างสองนั้น ในระยะเวลา 6 วัน แล้วพระองค์ทรงสถิตย์อยู่บนบังลังก์ พระผู้ทรงกรุณาปรานี ดังนั้นจงถามผู้รู้เกี่ยวกับพระองค์');
INSERT INTO "king_fahad_thai" VALUES(25,60,'25:60','และเมื่อได้ถูกกล่าวแก่พวกเขาว่า “จงสุญูดต่อพระผู้ทรงปรานี” พวกเขาได้กล่าวว่า “ใครคือพระผู้ทรงกรุณาปรานี จะให้เราสุญูดตามที่ท่านสั่งเรากระนั้นหรือ?” และมันได้เพิ่มการหันห่างออกไปแก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(25,61,'25:61','ความจําเริญยิ่งแด่พระผู้ทรงทําให้ชั้นฟ้ามีหมู่ดวงดาว และได้ทรงทําให้มีตะเกียง ในนั้น และดวงจันทร์มีแสงนวล');
INSERT INTO "king_fahad_thai" VALUES(25,62,'25:62','และพระองค์คือผู้ทรงบันดาลให้มีกลางคืนและกลางวัน หมุนเวียนแทนที่กัน สําหรับผู้ที่ปรารถนาจะใคร่ครวญ หรือปรารถนาจะขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(25,63,'25:63','และปวงบ่าวของพระผู้ทรงกรุณาปราณีคือ บรรดาผู้ที่เดินบนแผ่นดินด้วยความสงบเสงี่ยม และเมื่อพวกเขาโง่เขลากล่าวทักทายพวกเขา พวกเขาจะกล่าวว่า ศานติ หรือสลาม');
INSERT INTO "king_fahad_thai" VALUES(25,64,'25:64','และบรรดาผู้ใช้เวลากลางคืนทําการสุญูดและยืน(ละหมาด) เพื่อพระเจ้าของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(25,65,'25:65','และบรรดาผู้ที่กล่าวว่า “ข้าแต่พระผู้เป็นเจ้าของเรา ขอพระองค์ทรงปัดเป่าการลงโทษของนรกให้พ้นไปจากเรา แท้จริงการลงโทษของมันนั้นมันคงอยู่ตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(25,66,'25:66','“แท้จริงมันเป็นที่อยู่และที่พํานักอันเลวร้ายยิ่ง”');
INSERT INTO "king_fahad_thai" VALUES(25,67,'25:67','และบรรดาผู้ที่เมื่อพวกเขาใช้จ่าย พวกเขาก็ไม่สุรุ่ยสุร่าย และไม่ตระหนี่ และระหว่างทั้งสองสภาพนั้น พวกเขาอยู่สายกลาง');
INSERT INTO "king_fahad_thai" VALUES(25,68,'25:68','และบรรดาผู้ที่ไม่วิงวอนขอพระเจ้าอื่นใดคู่เคียงกับอัลลอฮ์ และพวกเขาไม่ฆ่าชีวิตซึ่งอัลลอฮ์ทรงห้ามไว้ เว้นแต่');
INSERT INTO "king_fahad_thai" VALUES(25,69,'25:69','การลงโทษในวันกิยามะฮ์จะถูกเพิ่มขึ้นเป็นสองเท่าสําหรับเขา และเขาจะอยู่ในนั้นอย่างอัปยศ');
INSERT INTO "king_fahad_thai" VALUES(25,70,'25:70','เว้นแต่ผู้ที่กลับเนื้อกลับตัว และศรัทธาและประกอบการงานที่ดี เขาเหล่านั้นแหละอัลลอฮ์จะทรงเปลี่ยนความชั่วของพวกเขาเป็นความดี และอัลลอฮ์เป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(25,71,'25:71','และผู้ใดกลับเนื้อกลับตัวและกระทําความดี แท้จริงเขากลับเนื้อกลับตัวเข้าหาอัลลอฮ์อย่างจริงจัง');
INSERT INTO "king_fahad_thai" VALUES(25,72,'25:72','และบรรดาผู้ไม่เป็นพยานในการเท็จ และเมื่อพวกเขาผ่านเรื่องไร้สาระ พวกเขาผ่านไปอย่างมีเกียรติ');
INSERT INTO "king_fahad_thai" VALUES(25,73,'25:73','และบรรดาผู้ที่เมื่อถูกกล่าวเตือนให้รําลึกถึงโองการทั้งหลายของพระเจ้าของพวกเขา พวกเขาจะไม่ผินหลังให้เป็นสภาพเช่นคนหูหนวกตาบอด');
INSERT INTO "king_fahad_thai" VALUES(25,74,'25:74','และบรรดาผู้ที่กล่าวว่า “ข้าแต่พระผู้เป็นเจ้าของเรา ขอพระองค์โปรดประทานแก่เรา ซึ่งคู่ครองของเราและลูกหลานของเรา ให้เป็นที่รื่นรมย์แก่สายตาของเรา และทรงทําให้เราเป็นแบบอย่างแก่บรรดาผู้ยําเกรง”');
INSERT INTO "king_fahad_thai" VALUES(25,75,'25:75','เขาเหล่านั้นจะได้รับการตอบแทน ในการที่พวกเขาอดทน และพวกเขาจะได้พบการกล่าวคําต้อนรับและสลาม');
INSERT INTO "king_fahad_thai" VALUES(25,76,'25:76','โดยพํานักอยู่ในนั้นอย่างถาวร เป็นที่พํานักและที่อาศัยที่น่าอภิรมย์แท้ ๆ');
INSERT INTO "king_fahad_thai" VALUES(25,77,'25:77','จงกล่าวเถิด “พระผู้เป็นเจ้าของฉันจะไม่ใยดีต่อพวกท่าน หากไม่มีการวิงวอนภักดีของพวกท่าน เพราะแน่นอน พวกท่านได้ปฏิเสธไม่รับฟัง ดังนั้น การลงโทษจะเกิดขึ้นอย่างแน่นอน”');
INSERT INTO "king_fahad_thai" VALUES(26,1,'26:1','ฏอ ซีน มีม');
INSERT INTO "king_fahad_thai" VALUES(26,2,'26:2','เหล่านี้คือโองการทั้งหลายอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(26,3,'26:3','บางทีเจ้า (มุฮัมมัด) เป็นผู้ทําลายชีวิตของเจ้า เพราะพวกเขาไม่เป็นผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(26,4,'26:4','หากเราประสงค์ เราจะให้มีสัญญาณหนึ่งจากฟากฟ้ามายังพวกเขา แล้วคอของพวกเขาก็ยอมก้มลมต่อมัน');
INSERT INTO "king_fahad_thai" VALUES(26,5,'26:5','และไม่มีข้อตักเตือนใหม่ อันใดจากพระผู้ทรงกรุณาปราณี เว้นแต่พวกเขาจะผินหลังให้กับมัน');
INSERT INTO "king_fahad_thai" VALUES(26,6,'26:6','แล้วแน่นอนพวกเขาได้ปฏิเสธ ดังนั้นข่าวคราวที่พวกเขาเคยเยาะเย้ยมันนั้นก็จะมายังพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(26,7,'26:7','พวกเขามิได้มองไปยังแผ่นดินดอกหรือว่ากี่มากน้อยแล้วที่เราได้ให้มันงอกเงยออกมาจากทุกชนิดที่ดีมีประโยชน์');
INSERT INTO "king_fahad_thai" VALUES(26,8,'26:8','แท้จริงในการนี้ย่อมเป็นสัญญาณหนึ่งอย่างแน่นอน แต่ส่วนมากของพวกเขาไม่เป็นผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(26,9,'26:9','และแท้จริงพระเจ้าของเจ้านั้น แน่นอนพระองค์เป็นผู้ทรงอํานาจ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(26,10,'26:10','และจงรําลึก เมื่อพระเจ้าของเจ้าทรงเรียกมูซาว่า จงไปยังหมู่ชนผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(26,11,'26:11','คือหมู่ชนของฟิรเอาน์ พวกเขาไม่ยําเกรงดอกหรือ?');
INSERT INTO "king_fahad_thai" VALUES(26,12,'26:12','เขา (มูซา)กล่าวว่า โอ้พระเจ้าของฉันแท้จริงฉันกลัวว่า พวกเขาจะปฏิเสธไม่ยอมเชื่อฉัน');
INSERT INTO "king_fahad_thai" VALUES(26,13,'26:13','และหัวอกของฉันจะอึดอัด และลิ้นของฉันจะไม่คล่อง ดังนั้นพระองค์ทรงโปรดส่งฮารูนมาช่วยฉันด้วยเถิด!');
INSERT INTO "king_fahad_thai" VALUES(26,14,'26:14','และพวกเขามีข้อกล้าวหาต่อฉัน ดังนั้นฉันกลัวว่าพวกเขาจะฆ่าฉัน');
INSERT INTO "king_fahad_thai" VALUES(26,15,'26:15','พระองค์ตรัสว่า ไม่ดอก! ดังนั้นเจ้าทั้งสองจงไปเถิดพร้อมด้วยสัญญาณทั้งหลาย ของเราแท้จริงเราอยู่กับพวกเจ้า เป็นผู้ฟัง');
INSERT INTO "king_fahad_thai" VALUES(26,16,'26:16','ดังนั้นเจ้าทั้งสองจงไปหาฟิรเอาน์ แล้วจงกล่าวว่าเราเป็นทูตของพระเจ้าแห่งสากลโลก');
INSERT INTO "king_fahad_thai" VALUES(26,17,'26:17','แล้วขอให้ส่งวงศ์วานของอิสรออีลไปพร้อมกับเราเถิด');
INSERT INTO "king_fahad_thai" VALUES(26,18,'26:18','เขา(ฟิรเอาน์) กล่าวว่า เรามิได้เลี้ยงดูเจ้าเมื่อขณะเป็นเด็กอยู่กับพวกเราดอกหรือ? และเจ้าได้อยู่กับเราหลายปี ในช่วงชีวิตของเจ้า');
INSERT INTO "king_fahad_thai" VALUES(26,19,'26:19','และเจ้าได้ทําการกระทําของเจ้าซึ่งเจ้าได้กระทําไปแล้ว และเจ้าเป็นผู้หนึ่งในหมู่ผู้เนรคุณ');
INSERT INTO "king_fahad_thai" VALUES(26,20,'26:20','เขา (มูซา) กล่าวว่า ฉันได้กระทํามันโดยที่ฉันไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(26,21,'26:21','ดังนั้น ฉันได้หนีไปจากพวกท่าน เมื่อฉันกลัวพวกท่านแล้วพระเจ้าของฉันได้ทรงประทานฮิกมะฮ์แก่ฉัน และทรงแต่งตั้งฉันให้เป็นร่อซูลคนหนึ่ง');
INSERT INTO "king_fahad_thai" VALUES(26,22,'26:22','และนี่คือบุญคุณที่ท่านรําเลิกมันต่อฉันโดยท่านทําให้วงศืวานอิสรออีลเป็นทาส');
INSERT INTO "king_fahad_thai" VALUES(26,23,'26:23','ฟิรเอาน์ได้กล่าวว่า “และใครคือพระเจ้าแห่งสากลโลก”');
INSERT INTO "king_fahad_thai" VALUES(26,24,'26:24','เขา(มูซา) กล่าวว่า “พระเจ้าแห่งชั้นฟ้าทั้งหลายและแผ่นดิน และสิ่งที่อยู่ในระหว่างทั้งสอง หากพวกท่านเป็นผู้ศรัทธาเชื่อมัน”');
INSERT INTO "king_fahad_thai" VALUES(26,25,'26:25','เขาได้กล่าวแก่ผู้อยู่รอบ ๆ เขาว่า “พวกท่านได้ยินไหม?”');
INSERT INTO "king_fahad_thai" VALUES(26,26,'26:26','เขา(มูซา)กล่าว่า “พระเจ้าของพวกท่าน และของบรรพบุรุษสมัยก่อน ๆ นั้นด้วย”');
INSERT INTO "king_fahad_thai" VALUES(26,27,'26:27','เขา (ฟิรเอาน์) กล่าวว่า “ทจริงร่อซูลของพวกท่านซึ่งได้ถูกส่งมายังพวกท่านนั้นเป็นคนบ้าอย่างแน่นอน”');
INSERT INTO "king_fahad_thai" VALUES(26,28,'26:28','เขา(มูซา) กล่าวว่า “พระเจ้าแห่งทิศตะวันออกและทิศตะวันตก และสิ่งที่อยู่ในระหว่างทั้งสอง หากพวกท่านใช้สติปัญญาพิจารณา');
INSERT INTO "king_fahad_thai" VALUES(26,29,'26:29','เขากล่าวว่า “หากเจ้ายึดถือพระเจ้าอื่นจากฉัน ฉันจะให้เจ้าอยู่ในหมู่ผู้ต้องขัง”');
INSERT INTO "king_fahad_thai" VALUES(26,30,'26:30','เขา(มูซา) กล่าวว่า “แม้ว่าฉันจะนําสิ่งที่ชัดแจ้งมายังท่านกระนั้นหรือ?”');
INSERT INTO "king_fahad_thai" VALUES(26,31,'26:31','เขากล่าวว่า “ก็จงนํามันมาซิ หากเจ้าเป็นคนจริง”');
INSERT INTO "king_fahad_thai" VALUES(26,32,'26:32','ดังนั้น เขาได้โยนไม่เท้าของเขามันคือ งูอย่าชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(26,33,'26:33','และได้ดึงมือของเขาออกมาเป็นสีขางแก่บรรดาผู้มองดู');
INSERT INTO "king_fahad_thai" VALUES(26,34,'26:34','เขาได้กล่าวแก่ขุนนางชั้นผู้ใหญ่รอบๆ เขาว่า “แท้จริงเขาคนนี้คือนักเล่นกลอย่างช่ําชอง');
INSERT INTO "king_fahad_thai" VALUES(26,35,'26:35','เขาต้องการที่ให้พวกท่านออกจากดินแดนของพวกท่านด้วยเล่ห์กลของเขา ดังนั้นพวกท่านจะชี้แนะประการใด”');
INSERT INTO "king_fahad_thai" VALUES(26,36,'26:36','พวกเขากล่าวว่า “จงหน่วงเหนี่ยวเขาและพี่ชายของเขาไว้ก่อน และจงส่งคนไปตามหัวเมืองให้มาชุมนุมกัน');
INSERT INTO "king_fahad_thai" VALUES(26,37,'26:37','เพื่อที่นักเล่นกลผู้ช่ําชองทุกคนจะได้มาหาท่าน”');
INSERT INTO "king_fahad_thai" VALUES(26,38,'26:38','แล้วบรรดานักเล่นกลได้มาชุมนุมกันตามวันเวลาที่กําหนดไว้');
INSERT INTO "king_fahad_thai" VALUES(26,39,'26:39','และได้มีประกาศแก่มหาชน พวกท่านจะไปร่วมชุมนุมด้วยไหม?');
INSERT INTO "king_fahad_thai" VALUES(26,40,'26:40','เพื่อพวกเขาจะได้ตามบรรดานักเล่นกลหากพวกเขาเป็นผู้ชนะ');
INSERT INTO "king_fahad_thai" VALUES(26,41,'26:41','เมื่อพวกนักเล่นกลมาถึง พวกเขากล่าวแก่ฟิรเอาน์ว่า “พวกเราจะมีรางวัลแน่นอนหรือถ้าพวกเราเป็นผู้ชนะ');
INSERT INTO "king_fahad_thai" VALUES(26,42,'26:42','เขากล่าวว่า “ถูกแล้ว และพวกท่านขณะนั้นจะอยู่ในหมู่ผู้ใกล้ชิดอย่างแน่นอน”');
INSERT INTO "king_fahad_thai" VALUES(26,43,'26:43','มูซาได้กล่าวแก่พวกเขาว่า “จงโยนซิสิ่งที่พวกท่านจะต้องโยน”');
INSERT INTO "king_fahad_thai" VALUES(26,44,'26:44','แล้วพวกเขาก็ได้โยนเชือกหลายเส้นของพวกเขาและไม้เท้าหลายอันของพวกเขา และพวกเขากล่าวว่า “ด้วยเกียติยศของฟิรเอาน์แท้จริงเราเป็นผู้ชนะอย่างแน่นอน”');
INSERT INTO "king_fahad_thai" VALUES(26,45,'26:45','ครั้นแล้วมูซาก็ได้โยนไม้เท้าของเขา ณ บัดนั้นมันได้กลืนสิ่งที่พวกเขาได้ลองมันขึ้น');
INSERT INTO "king_fahad_thai" VALUES(26,46,'26:46','พวกนักเล่นกลจึงก้มหัวลงกราบสุญูด');
INSERT INTO "king_fahad_thai" VALUES(26,47,'26:47','พวกเขากล่าวว่า “เราศรัทธาต่อพระเจ้าแห่งสากลโลก”');
INSERT INTO "king_fahad_thai" VALUES(26,48,'26:48','พระเจ้าของมูซา และฮารูน');
INSERT INTO "king_fahad_thai" VALUES(26,49,'26:49','เขากล่าวว่า พวกท่านศรัทธาต่อเขาก่อนที่ฉันจะอนุญาตแก่พวกท่านกระนั้นหรือ? แน่นอนเขาต้องเป็นหัวหน้าของพวกท่านซึ่งได้สอนการเล่นกลแก่พวกท่าน แล้วพวกท่านจะรู้ แน่นอนฉันจะตัดมือของพวกท่านและเท้าของพวกท่านสลับข้างกัน และแน่นอนฉันจะแขวนตรึงไว้ทั้งหมด');
INSERT INTO "king_fahad_thai" VALUES(26,50,'26:50','พวกเขากล่าวว่า ไม่เป็นไรหรอกแท้จริงเรานั้นต้องเป็นผู้กลับไปยังพระเจ้าของเรา');
INSERT INTO "king_fahad_thai" VALUES(26,51,'26:51','แท้จริงเราปรารถนาที่จะให้ พระเจ้าของเราทรงยกโทษแก่เรา เพราะเราเป็นกลุ่มแรกที่เป็นผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(26,52,'26:52','แลเราได้ดลใจให้มูซาออกเดินทาง ในเวลากกลางคืนพร้อมกันกับปวงบ่าวของข้า แท้จริงพวกเจ้ากําลังถูกติดตาม');
INSERT INTO "king_fahad_thai" VALUES(26,53,'26:53','แล้วฟิรเอาน์ได้ส่งคนไปตามหัวเมืองต่างๆ ให้มาร่วมชุมนุม');
INSERT INTO "king_fahad_thai" VALUES(26,54,'26:54','(และว่า) แท้จริงเขาเหล่านั้นเป็นหมู่ชนส่วนน้อย');
INSERT INTO "king_fahad_thai" VALUES(26,55,'26:55','และแท้จริงพวกเขาทําให้เราเกิดโทษะ');
INSERT INTO "king_fahad_thai" VALUES(26,56,'26:56','และแท้จริงพวกเราทั้งหมดอยู่ในสภาพเตรียมพร้อม');
