INSERT INTO "king_fahad_thai" VALUES(26,225,'26:225','เจ้าไม่เห็นดอกหรือว่า แท้จริงพวกเขานั้นเร่ร่อนไปในทุกหนแห่ง');
INSERT INTO "king_fahad_thai" VALUES(26,226,'26:226','และแท้จริงพวกเขานั้นพูดในสิ่งที่พวกเขาไม่ทํากระ');
INSERT INTO "king_fahad_thai" VALUES(26,227,'26:227','นอกจากบรรดาผู้ศรัทธาและปฏิบัติความดีทั้งหลายและรําลึกถึงอัลลอฮ์อย่างมาก และตอบโต้ป้องกันหลังจากที่พวกเขาถูกข่มเหง และบรรดาผู้อธรรมจะได้รู้ว่า ทางกลับอันใดที่พวกเขาจะกลับคืนสู่');
INSERT INTO "king_fahad_thai" VALUES(27,1,'27:1','ฏอ ซีน เหล่านี้คือโองการทั้งหลายของอัรกุรอาน และคัมภีร์อันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(27,2,'27:2','เป็นแนวทางที่ถูกต้อง และข่าวดีสําหรับบรรดาผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(27,3,'27:3','บรรดาผู้ที่ดํารงการละหมาดและบริจาคซะกาต และต่อวันปรโลกพวกเขาเชื่อมั่น');
INSERT INTO "king_fahad_thai" VALUES(27,4,'27:4','แท้จริงบรรดาผู้ไม่ศรัทธาต่อวันปรโลกนั้น เราได้ทําให้การงานของพวกเขาสวยงามแก่พวกเขา ดังนั้นพวกเขาจะระเหเร่ร่อน');
INSERT INTO "king_fahad_thai" VALUES(27,5,'27:5','ชนเหล่านั้น พวกเขาจะได้รับการลงโทษอันชั่วช้า และในวันปรโลกพวกเขาเป็นผู้ขาดทุนยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(27,6,'27:6','และแท้จริง เจ้าจะได้รับอัลกุรอานอย่างแน่นอน จากพระผู้ทรงปรีชาญาณ ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(27,7,'27:7','จงรําลึก เมื่อมูซากล่าวแก่ครอบครัวของเขา ว่า “แท้จริงฉันเห็นไฟ ฉันจะนําข่าวจากที่นั่นมาให้พวกท่านหรือฉันจะนําคบเพลิงมาให้พวกท่าน เพื่อพวกท่านจะได้ทําให้มันอุ่น”');
INSERT INTO "king_fahad_thai" VALUES(27,8,'27:8','ครั้นเมื่อเขามาถึงที่นั่น ได้มีเสียงเรียกขึ้นว่า “ผู้ที่อยู่ในไฟและผู้ที่อยู่รอบ ๆ มัน จะได้รับความจําเริญ และมหาบริสุทธิ์แห่งอัลลอฮ์ พระเจ้าแห่งสากลโลก');
INSERT INTO "king_fahad_thai" VALUES(27,9,'27:9','“โอ้มูซาเอ๋ย ! แท้จริงข้าคืออัลลอฮ์ ผู้ทรงอํานาจ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(27,10,'27:10','“และจงโยนไม้เท้าของเจ้า” เมื่อเขาเห็นมันเคลื่อนไหวคล้ายกับว่ามันเป็นงู เขาก็กลับหลังหันและไม่หันกลับมาอีก “โอ้มูซาเอ๋ย ! เจ้าอย่ากลัว แท้จริงบรรดาร่อซูลนั้นจะไม่กลัวเมื่ออยู่ต่อหน้าข้า”');
INSERT INTO "king_fahad_thai" VALUES(27,11,'27:11','“เว้นแต่ผู้อธรรม แล้วเขาได้เปลี่ยนมาทําความดี หลังจากที่ได้ทําความชั่ว เพราะแท้จริงข้านั้นเป็นผู้อภัย ผู้เมตตาเสมอ”');
INSERT INTO "king_fahad_thai" VALUES(27,12,'27:12','“และจงสอดมือของเจ้าเข้าไปในอกเสื้อของเจ้า มันจะออกมาขาวปราศจากอันตรายใด ๆ (นี่คือสอง) ในเก้าสัญญาณ (ที่เจ้าจะนําไป) ยังฟิรเอาน์และพวกพ้องของเขา แท้จริงพวกเขาเป็นหมู่ชนผู้ฝ่าฝืน”');
INSERT INTO "king_fahad_thai" VALUES(27,13,'27:13','เมื่อสัญญาณของเราได้มาปรากฎชัดแจ้งแก่พวกเขา พวกเขาก็กล่าวว่า “นี่คือเวทมนต์คาถาอันแจ่มแจ้ง”');
INSERT INTO "king_fahad_thai" VALUES(27,14,'27:14','และพวกเขาได้ปฏิเสธมันอย่างยุติธรรมและเย่อหยิ่ง ทั้ง ๆ ที่จิตใจของพวกเขาเชื่อมั่นมันดังนั้นจงดูเถิดว่า บั้นปลายของบรรดาผู้บ่อนทําลายนั้นจะเป็นเช่นไร ?');
INSERT INTO "king_fahad_thai" VALUES(27,15,'27:15','และโดยแน่นอนเราได้ให้ความรู้แก่ดาวูดและสุลัยมาน และเขาทั้งสองกล่าวว่า “บรรดาการสรรเสริญเป็นของอัลลอฮ์ ผู้ทรงโปรดปรานแก่เรา เหนือส่วนมากของปวงบ่าวของพระองค์ผู้ศรัทธาทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(27,16,'27:16','และสุลัยมานเป็นทายาทของดาวูด และเขากล่าวว่า “มหาชนทั้งหลายเอ๋ย ! เราได้รับความรู้ในภาษาของนก และเราได้รับทุก ๆ สิ่ง แท้จริง นี่คือความโปรดปรานอันแท้จริงแน่นอน”');
INSERT INTO "king_fahad_thai" VALUES(27,17,'27:17','และไพร่พลของเขาที่เป็นญินมนุษย์และนก ได้ถูกให้มาชุนนุมต่อหน้าสุลัยมาน และพวกเขาถูกจัดให้เป็นระเบียบ');
INSERT INTO "king_fahad_thai" VALUES(27,18,'27:18','จนกระทั่งเมื่อพวกเขาได้มาถึงทุ่งที่มีมดมาก มดตัวหนึ่งได้พูดว่า “โอ้พวกมดเอ๋ย! พวกเจ้าจงเข้าไปในรังของพวกเจ้าเถิด เพื่อว่าสุลัยมานและไพร่พลของเขาจะได้ไม่บดขยี้พวกเจ้า โดยที่พวกเขาไม่รู้ตัว”');
INSERT INTO "king_fahad_thai" VALUES(27,19,'27:19','เขา (สุลัยมาน) ยิ้มแกมหัวเราะต่อคําพูดของมัน และกล่าวว่า “ข้าแต่พระผู้เป็นเจ้าของข้าพระองค์ ขอพระองค์ทรงโปรดประทานแกข้าพระองค์ เพื่อให้ข้าพระองค์ขอบคุณต่อความโปรดปรานของพระองค์ท่าน ซึ่งพระองค์ท่านได้ทรงโปรดปรานแก่ข้าพระองค์ และบิดามารดาของข้าพระองค์ และให้ข้าพระองค์กระทําความดีเพื่อให้พระองค์ทรงพอพระทัยมัน และทรงให้ข้าพระองค์เข้าอยู่ในความเมตตาของพระองค์ ในหมู่ปวงบ่าวของพระองค์ที่ดีทั้งหลาย”');
INSERT INTO "king_fahad_thai" VALUES(27,20,'27:20','และเขาได้ตรวจดูฝูงนกแล้วกล่าวขึ้นว่า “ทําไมฉันจึงไม่เห็นฮุดฮุด แต่ว่ามันหายไปไหน ?');
INSERT INTO "king_fahad_thai" VALUES(27,21,'27:21','“แน่นอน ฉันจะลงโทษมันด้วยการลงโทษอย่างสาหัส หรือฉันจะฆ่ามันอย่างแน่นอนหรือให้มันนําหลักฐานอันชัดแจ้งมาให้ฉัน”');
INSERT INTO "king_fahad_thai" VALUES(27,22,'27:22','มันหายไปชั่วครู่ (แล้วกลับมา) มันได้กล่าวว่า “ฉันได้ไปตรวจพบสิ่งที่ท่านไม่รู้ และฉันได้นําข่าวอันแน่นอนจากสะบะ มายังท่าน');
INSERT INTO "king_fahad_thai" VALUES(27,23,'27:23','“ฉันได้พบหญิงคนหนึ่ง ปกครองพวกเขา และนางมีทุกสิ่ง และนางมีบัลลังก์อันใหญ่โต”');
INSERT INTO "king_fahad_thai" VALUES(27,24,'27:24','“และฉันได้พบนางและหมู่ชนของนางสักการะบูชาดวงอาทิตย์อื่นจากอัลลอฮ์ และมารชัยฎอนได้ทําให้การงานของพวกเขาเป็นของดีงามแก่พวกเขา และได้กีดกันพวกเขาออกจากแนวทางที่ถูกต้อง ดังนั้นพวกเขาจึงไม่ได้อยู่ในแนวทางที่ถูกต้อง”');
INSERT INTO "king_fahad_thai" VALUES(27,25,'27:25','ทําไมพวกเขาไม่สุญูดต่ออัลลอฮ์ ผู้นําออกมาซึ่งสิ่งที่ซ่อนเร้นอยู่ในชั้นฟ้าทั้งหลายและแผ่นดิน และทรงรอบรู้สิ่งที่พวกเจ้าปิดบัง และสิ่งที่พวกเจ้าเปิดเผย');
INSERT INTO "king_fahad_thai" VALUES(27,26,'27:26','อัลลอฮ์ ไม่มีพระเจ้าอื่นใดนอกจากพระองค์ พระเจ้าแห่งบัลลังก์อันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(27,27,'27:27','เขากล่าวว่า “เราจะคอยดูว่าเจ้าพูดจริงหรือเจ้าอยู่ในหมู่ผู้กล่าวเท็จ”');
INSERT INTO "king_fahad_thai" VALUES(27,28,'27:28','“เจ้าจงนําสารของฉันนี้และส่งมันให้พวกเขา แล้วถอยออกห่างจากพวกเขา ดังนั้นจงคอยดูว่าพวกเขาจะตอบกลับมาว่าอย่างไร ?”');
INSERT INTO "king_fahad_thai" VALUES(27,29,'27:29','(พระราชินี) ทรงกล่าวว่า “โอ้หมู่บริพารทั้งหลายเอ๋ย ! แน่แท้สารอันมีเกียรติถูกนํามาให้ฉัน”');
INSERT INTO "king_fahad_thai" VALUES(27,30,'27:30','“แท้จริงมันมาจากสุลัยมาน และแท้จริงมันเริ่มว่า ด้วยพระนามของอัลลอฮ์ ผู้ทรงกรุณาปรานี ผู้ทรงเมตตาเสมอ”');
INSERT INTO "king_fahad_thai" VALUES(27,31,'27:31','“พวกท่านอย่าเย่อหยิ่งต่อฉัน และจงมาหาฉันอย่างนอบน้อม”');
INSERT INTO "king_fahad_thai" VALUES(27,32,'27:32','พระนางทรงกล่าวว่า “โอ้หมู่บริหารทั้งหลายเอ๋ย ! จงให้ข้อชี้ขาดแก่ฉันในเรื่องของฉัน ฉันไม่อาจจะตัดสินใจในกิจการใด จนกว่าพวกท่านจะอยู่ร่วมด้วย”');
INSERT INTO "king_fahad_thai" VALUES(27,33,'27:33','พวกเขากล่าวว่า “เราเป็นพวกที่มีพลังและเป็นพวกที่มีกําลังรบเข็มแข็ง สําหรับพระบัญชานั้นเป็นของพระนางดังนั้น พระนางได้โปรดตรึกตรองดูสิ่งใดที่พระนางจะทรงบัญชา”');
INSERT INTO "king_fahad_thai" VALUES(27,34,'27:34','พระนางทรงกล่าวว่า “แท้จริงเหล่ากษัตริย์นั้น เมื่อเข้าไปในเมืองใดก็ทําลายมัน และทําให้บรรดาผู้มีอํานาจของเมืองนั้นเป็นผู้ต่ําต้อย และเช่นนั้นแหละพวกเขากระทํากัน”');
INSERT INTO "king_fahad_thai" VALUES(27,35,'27:35','“และแท้จริงฉันจะส่งของกํานัลไปให้พวกเขา แล้วฉันจะเฝ้าคอยดูว่า ผู้ที่ถูกส่งไปนั้นจะกลับมาอย่างไร ?”');
INSERT INTO "king_fahad_thai" VALUES(27,36,'27:36','เมื่อพวกเขาได้เข้าพบสุลัยมานแล้ว เขา(สุลัยมาน) กล่าวว่า “พวกท่านจะนําทรัพย์สินมากํานัลแก่เราหรือ ? สิ่งที่อัลลอฮ์ทรงประทานให้แก่ฉันนั้น ดียิ่งกว่าสิ่งที่พระองค์ประทานให้แก่พวกท่าน แต่พวกท่านดีใจต่อของกํานัลของพวกท่าน');
INSERT INTO "king_fahad_thai" VALUES(27,37,'27:37','“จงกลับไปยังพวกเขา เพราะแน่นอนเราจะนําไพร่พลไปยังพวกเขา โดยที่พวกเขาไม่มีกําลังที่จะต่อต้านมันได้ และแน่นอน เราจะให้พวกเขาออกจากที่นั่นอย่างอัปยศ และพวกเขาจะเป็นผู้ต่ําต้อย');
INSERT INTO "king_fahad_thai" VALUES(27,38,'27:38','เขา (สุลัยมาน) กล่าวว่า “โอ้หมู่บริพารทั้งหลายเอ๋ย ! ผู้ใดในหมู่พวกท่านจะนําบัลลังก์ของนางมายังฉัน ก่อนที่พวกเขาจะมาหาฉันอย่างผู้นอบน้อม”');
INSERT INTO "king_fahad_thai" VALUES(27,39,'27:39','ผู้ปรีชาสามารถล้ําเลิศคนหนึ่งของพวกญินได้กล่าวว่า “ฉันจะนํามันมาเสนอท่าน ก่อนที่ท่านจะลุกขึ้นจากที่นั่งของท่าน และแท้จริงฉันเป็นผู้มีพลังและไว้วางใจได้ในเรื่องนี้”');
INSERT INTO "king_fahad_thai" VALUES(27,40,'27:40','ผู้ที่มีความรู้ในเรื่องคัมภีร์ กล่าวว่า “ฉันจะนํามันมาเสนอท่านชั่วพริบตาเดียว” เมื่อเขา (สุลัยมาน) เห็นมันวางมั่นคงอยู่ต่อหน้าเขา เขากล่าวว่า “นี่เนื่องจากความโปรดปรานของพระเจ้าของฉัน เพื่อพระองค์จะได้ทรงทดสอบฉันว่าฉันกตัญญูหรือเนรคุณ และผู้ใดกตัญญูแท้จริงเขาก็กตัญญูต่อตัวเขาเอง และผู้ใดเนรคุณแท้จริงพระเจ้าของฉันนั้นเป็นผู้ทรงมั่งมี ผู้ทรงเอื้อเฟื้อเผื่อแผ่ยิ่ง”');
INSERT INTO "king_fahad_thai" VALUES(27,41,'27:41','เขากล่าวว่า “พวกท่านจงดัดแปลงบัลลังก์ของพระนาง เพื่อดูซิว่าพระนางจะจํามันได้หรือพระนางจะอยู่ในหมู่ผู้จํามันไม่ได้”');
INSERT INTO "king_fahad_thai" VALUES(27,42,'27:42','ครั้นเมื่อพระนางได้มาถึงก็ได้ทูลพระนางว่า “บัลลังก์ของพระนางเหมือนอย่างนี้หรือ ?” พระนางตรัสว่า “มันคล้ายอย่างนี้แหละ” และเราได้รับความรู้มาก่อนนาง และเราได้เป็นมุสลิมมาก่อนนาง');
INSERT INTO "king_fahad_thai" VALUES(27,43,'27:43','และการที่นางได้สักการะบูชาอื่นจากอัลลอฮฺ ได้หันห่างนางออกไป แท้จริงนางอยู่ในหมู่ชนผู้ปฏิเสธ');
INSERT INTO "king_fahad_thai" VALUES(27,44,'27:44','ได้มีเสียงกล่าวแก่นางว่า “โปรดเข้าไปในวังเถิด” ครั้นเมื่อนางเห็นมันนางคิดว่า มันเป็นสระที่เป็นห้วงน้ํา และนางได้เลิกหน้าแข็งของนาง เขา (สุลัยมาน) กล่าวว่า “มันเป็นวังทําให้ราบเรียบด้วยกระจาก” นางได้กล่าวว่า “ข้าแต่พระเจ้าของฉัน แท้จริงฉันได้อธรรมแก่ตัวฉันเอง และฉันขอนอบน้อมปฏิบัติตามสุลัยมาน เพื่ออัลลอฮ์พระเจ้าแห่งสากลโลก”');
INSERT INTO "king_fahad_thai" VALUES(27,45,'27:45','และโดยแน่นอน เราได้ส่งพี่น้องคนหนึ่งของพวกเขาคือศอและฮ์ ไปยัง(หมู่ชนของ) ษะมูดโดยให้พวกเขาเคารพภักดีต่ออัลลอฮ์ แล้วพวกเขาได้แบ่งออกเป็นสองพวกแล้วโต้เถียงกัน');
INSERT INTO "king_fahad_thai" VALUES(27,46,'27:46','เขา (ศอและฮ์) กล่าวว่า “โอ้หมู่ชนของฉันเอ๋ย ! ทําไมพวกท่านจึงรีบเร่งหาความชั่วก่อนความดีเล่า ? ทําไมพวกท่านจึงไม่ขออภัยต่ออัลลอฮ์เพื่อพวกท่านจะได้รับความเมตตา”');
INSERT INTO "king_fahad_thai" VALUES(27,47,'27:47','พวกเขากล่าวว่า “พวกเราได้ประสบโชคร้ายเพราะท่าน และผู้ที่ร่วมกับท่าน” เขา (ศอและฮ์) กล่าวว่า “โชคร้ายของพวกท่านอยู่ที่อัลลอฮ์ ยิ่งกว่านั้นพวกท่านเป็นหมู่ชนที่ถูกทดสอบ”');
INSERT INTO "king_fahad_thai" VALUES(27,48,'27:48','และในเมืองนั้นมีเก้าคนที่เป็นผู้บ่อนทําลายในแผ่นดิน และพวกเขาไม่เป็นผู้ฟื้นฟูการทําดี');
INSERT INTO "king_fahad_thai" VALUES(27,49,'27:49','พวกเขากล่าวว่า “จงร่วมกันสาบานด้วยพระนามของอัลลอฮ์ แน่นอนพวกเราเตรียมที่จะทําร้ายเขาและครอบครัวของเขาในเวลากลางคืนแล้วเราก็จะกล่าวแก่ทายาทของเขาว่า เราไม่รู้เห็นความพินาศของครอบครัวของเขา และแท้จริงเรานั้นเป็นผู้สัตว์จริง”');
INSERT INTO "king_fahad_thai" VALUES(27,50,'27:50','และพวกเขาได้วางแผน และเราก็ได้วางแผนโดยที่พวกเขาไม่รู้สึกตัว');
INSERT INTO "king_fahad_thai" VALUES(27,51,'27:51','ดังนั้นจงคอยดูเถิด ผลสุดท้ายแห่งแผนการณ์ของพวกเขาจะเป็นเช่นไร กล่าวคือเราได้ทําลายล้างพวกเขา และหมู่ชนของพวกเขารวมทั้งหมด');
INSERT INTO "king_fahad_thai" VALUES(27,52,'27:52','ดังนั้น นั่นคือบ้านของพวกเขาก็ว่างเปล่า ทั้งนี้เพราะพวกเขาอธรรม แท้จริงในการนี้ย่อมเป็นสัญญาณหนึ่งอย่างแน่นอน สําหรับหมู่ชนที่รู้');
INSERT INTO "king_fahad_thai" VALUES(27,53,'27:53','และเราได้ช่วยบรรดาผู้ศรัทธา และพวกเขาเป็นผู้ยําเกรง ให้รอดพ้น');
INSERT INTO "king_fahad_thai" VALUES(27,54,'27:54','และ (จงรําลึกถึง) ลูฏ เมื่อเขากล่าวแก่หมู่ชนของเขา ว่า “พวกท่านกระทําการลามก ทั้งๆ ที่พวกท่านรู้เห็นอยู่กระนั้นหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(27,55,'27:55','“แท้จริงพวกท่านสมสู่พวกผู้ชายด้วยตัณหา แทนพวกผู้หญิงกระนั้นหรือ ? ยิ่งกว่านั้นพวกท่านเป็นหมู่ชนที่โง่เขลา”');
INSERT INTO "king_fahad_thai" VALUES(27,56,'27:56','ดังนั้น คําตอบของหมู่ชนของเขาไม่เป็นอย่างอื่น นอกจากกล่าวว่า “จงให้ตระกูลของลูฏออกจากหมู่บ้านของพวกท่าน แท้จริงพวกเขาเป็นหมู่ชนผู้บริสุทธิ์”');
INSERT INTO "king_fahad_thai" VALUES(27,57,'27:57','แล้วเราได้ช่วยเขาและบริวารของเขา ให้รอดพ้น เว้นแต่ภรรยาของเขา เราได้กําหนดให้นางอยู่ในหมู่ผู้ถูกทําลาย');
INSERT INTO "king_fahad_thai" VALUES(27,58,'27:58','และเราได้ให้ห่าฝน ตกลงมาบนพวกเขา ดังนั้น ฝนของบรรดาผู้ถูกตักเตือนมันชั่วช้าเสียนี่กระไร');
INSERT INTO "king_fahad_thai" VALUES(27,59,'27:59','จงกล่าวเถิด (มุฮัมมัด) บรรดาการสรรเสริญเป็นสิทธิของอัลลอฮ์ และความศานติจงมีแด่ปวงบ่าวของพระองค์ ผู้ซึ่งพระองค์ทรงคัดเลือกแล้ว อัลลอฮ์ดีกว่าหรือสิ่งที่พวกเขาตั้งเป็นภาคี (เจว็ด)');
INSERT INTO "king_fahad_thai" VALUES(27,60,'27:60','หรือผู้ใดเล่าที่สร้างชั้นฟ้าทั้งหลายและแผ่นดิน และทรงหลั่งน้ําจากฟากฟ้าแก่พวกเจ้าแล้วเราได้ให้สวนต่าง ๆ งอกเงยอย่างสวยงาม พวกเจ้า ก็ไม่สามารถที่จะทําให้ต้นไม้งอกเงยขึ้นมาได้จะมีพระเจ้าอื่นคู่เคียงกับอัลลอฮ์อีกหรือ ?เปล่าดอก! พวกเขาเป็นหมู่ชนผู้ตั้งภาคี');
INSERT INTO "king_fahad_thai" VALUES(27,61,'27:61','หรือผู้ใดเล่าที่ทําให้แผ่นดินเป็นที่พํานักและทรงให้มีลําน้ําหลายสายไหลระหว่างมัน และทรงทําให้ภูเขายึดมั่นสําหรับมัน และทรงทําให้มีที่กั้นระหว่างน่านน้ําทั้งสอง จะมีพระเจ้าอื่นใดคู่เคียงกับอัลลอฮ์อีกหรือ เปล่าดอก ! ส่วนมากของพวกเขาไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(27,62,'27:62','หรือผู้ใดเล่าจะตอบรับผู้ร้องทุกข์ เมื่อเขาวิงวอนขอต่อพระองค์ และทรงปลดเปลื้องความชั่วร้ายนั้น และทรงทําให้พวกเจ้า เป็นผู้ปกครองแผ่นดิน จะมีพระเจ้าอื่นคู่เคียงกับอัลลอฮ์อีกหรือ ? ส่วนน้อยเท่านั้นที่พวกเจ้าจะใคร่ครวญ”');
INSERT INTO "king_fahad_thai" VALUES(27,63,'27:63','หรือผู้ใดเล่าจะชี้แนะทางแก่พวกเจ้าในความมืดทึบของแผ่นดินและน่านน้ํา และผู้ใดทรงส่งลมแจ้งข่าวดี ท่ามกลางความเมตตาของพระองค์ จะมีพระเจ้าอื่นคู่เคียงกับอัลลอฮ์อีกหรือ ? อัลลอฮ์ทรงสูงส่งเหนือสิ่งที่พวกเขาตั้งภาคี');
INSERT INTO "king_fahad_thai" VALUES(27,64,'27:64','หรือผู้ใดเล่าจะเริ่มในการสร้าง แล้วทรงให้มันเกิดขึ้นมาอีกครั้งหนึ่ง และผู้ใดทรงประทานปัจจัยยังชีพแก่พวกเจ้า จากฟากฟ้าและแผ่นดิน จะมีพระเจ้าอื่นคู่เคียงกับอัลลอฮ์อีกหรือ? จงกล่าวเถิด (มุฮัมมัด) “จงนําหลักฐานของพวกท่านมา หากพวกท่านเป็นผู้สัตย์จริง”');
INSERT INTO "king_fahad_thai" VALUES(27,65,'27:65','จงกล่าวเถิด (มุฮัมมัด) “ไม่มีผู้ใดในชั้นฟ้าทั้งหลายและแผ่นดินจะรู้ในสิ่งพ้นญาณวิสัย นอกจากอัลลอฮ์ และพวกเขาจะไม่รู้ว่า เมื่อใดพวกเขาจะถูกให้ฟื้นคืนชีพ”');
INSERT INTO "king_fahad_thai" VALUES(27,66,'27:66','แต่ว่าความรู้ของพวกเขาเกี่ยวกับปรโลกนั้น ได้ถึงที่สุดแล้วหรือ ? ทั้ง ๆ ที่พวกเขาอยู่ในการสงสัยในเรื่องของมันยิ่งกว่านั้นพวกเขายังตาบอดต่อเรื่องนั้นอีกด้วย');
INSERT INTO "king_fahad_thai" VALUES(27,67,'27:67','บรรดาผู้ปฏิเสธศรัทธากล่าวกันว่า “ในเมื่อเราและบรรพบุรุษของเราเป็นดินแล้วจะให้ถูกออกมาอีกอย่างแน่นอนกระนั้นหรือ ?”');
INSERT INTO "king_fahad_thai" VALUES(27,68,'27:68','โดยแน่นอน เราได้ถูกสัญญาในเรื่องนี้มาก่อน ทั้งเราและบรรพบุรุษของเรา เรื่องนี้มิใช่อะไรอื่น นอกจากเป็นนิทานโกหกสมัยก่อน ๆ”');
INSERT INTO "king_fahad_thai" VALUES(27,69,'27:69','จงกล่าวเถิด (มุฮัมมัด) “พวกท่านจงท่องเที่ยวไปในแผ่นดิน แล้วจงดูว่าผลสุดท้ายของผู้กระทําผิดนั้นเป็นอย่างไร”');
INSERT INTO "king_fahad_thai" VALUES(27,70,'27:70','และเจ้าอย่าเศร้าโศกต่อพวกเขา และเจ้าอย่าคับใจ ในสิ่งที่พวกเขาวางแผนอุบาย');
INSERT INTO "king_fahad_thai" VALUES(27,71,'27:71','และพวกเขากล่าวว่า “เมื่อใดเล่าสัญญานี้ (จะมาถึง) หากพวกท่านสัตว์จริง”');
INSERT INTO "king_fahad_thai" VALUES(27,72,'27:72','จงกล่าวเถิด (มุฮัมมัด) “หวังว่าบางอย่างที่พวกท่านรีบเร่ง (ให้มันเกิดขึ้น) นั้น กําลังตามหลังใกล้พวกท่านเข้ามาแล้ว”');
INSERT INTO "king_fahad_thai" VALUES(27,73,'27:73','และแท้จริงพระเจ้าของเจ้านั้น แน่นอนพระองค์ทรงเป็นผู้โปรดปรานต่อปวงมนุษย์ แต่ส่วนมากของพวกเขาไม่เป็นผู้ขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(27,74,'27:74','และแท้จริงพระเจ้าของเจ้านั้น แน่นอนพระองค์ทรงรอบรู้สิ่งที่หัวอกของพวกเขาปกปิดอยู่และสิ่งที่พวกเขาเปิดเผย');
INSERT INTO "king_fahad_thai" VALUES(27,75,'27:75','และไม่มีสิ่งใดจะซ่อนเร้นทั้งในชั้นฟ้าและแผ่นดิน เว้นแต่ว่ามันอยู่ในบันทึกอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(27,76,'27:76','แท้จริงอัลกุรอานนี้จะบอกเล่าแก่วงศ์วานของอิสรออีล ส่วนมากซึ่งพวกเขาขัดแย้งกัน');
INSERT INTO "king_fahad_thai" VALUES(27,77,'27:77','และแท้จริงมัน (อัลกุรอาน) เป็นแนวทางที่ถูกต้องและความเมตตา แก่บรรดาผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(27,78,'27:78','แท้จริงพระเจ้าของเจ้าจะทรงตัดสินระหว่างพวกเขา ด้วยข้อวินิจฉัย (ที่ยุติธรรม) ของพระองค์ และพระองค์เป็นผู้ทรงอํานาจ ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(27,79,'27:79','ดังนั้น เจ้าจงมอบหมายต่ออัลลอฮ์ แท้จริง เจ้านั้นอยู่บนสัจธรรมอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(27,80,'27:80','แท้จริงเจ้าจะไม่ทําให้คนตายได้ยิน และจะไม่ทําให้คนหูหนวกได้ยินการเรียกร้องเชิญชวนเมื่อพวกเขาหันหลังกลับ');
INSERT INTO "king_fahad_thai" VALUES(27,81,'27:81','และเจ้ามิได้เป็นผู้ชี้แนะแนวทางแก่คนตาบอด ให้ออกจากความหลงผิดของพวกเขา เจ้าจะไม่ทําให้ผู้ใดได้ยินนอกจากผู้ศรัทธาต่อโองการต่าง ๆ ของเรา โดยที่พวกเขาเป็นผู้นอบน้อม');
INSERT INTO "king_fahad_thai" VALUES(27,82,'27:82','และเมื่อพระดํารัสเกิดขึ้นแก่พวกเขา เราได้ให้สัตว์ออกมาจากแผ่นดินแก่พวกเขา เพื่อกล่าวแก่พวกเขาว่า แท้จริงปวงมนุษย์นั้นไม่ยอมเชื่อมั่นต่อโองการทั้งหลายของเรา');
INSERT INTO "king_fahad_thai" VALUES(27,83,'27:83','และ (จงรําลึกถึง) วันที่เราจะเรียกจากทุก ๆ ชาติ มาชุมนุมกันเป็นหมู่คณะ จากผู้ที่ปฏิเสธโองการทั้งหลายของเราโดยที่พวกเขาจะถูกจัดเป็นกลุ่ม ๆ');
INSERT INTO "king_fahad_thai" VALUES(27,84,'27:84','จนกระทั่งเมื่อพวกเขาได้มาถึง พระองค์ตรัสว่า “พวกเจ้าได้ปฏิเสธโองการทั้งหลายของเรากระนั้นหรือ ? โดยที่พวกเจ้ามิได้รอบรู้มันเลยหรือว่าอะไรเล่าที่พวกเจ้ากระทําไป ?”');
INSERT INTO "king_fahad_thai" VALUES(27,85,'27:85','และหลักฐานได้ปรากฏขึ้นแก่พวกเขาเนื่องจากพวกเขาได้อธรรม ดังนั้นพวกเขาจึงไม่ได้');
INSERT INTO "king_fahad_thai" VALUES(27,86,'27:86','พวกเขามิได้พิจารณาดูหรือว่า แท้จริงเราได้ให้กลางคืนไว้สําหรับพวกเขาได้พักผ่อน และกลางวันให้เห็นแสงสว่าง แท้จริงในการนั้นย่อมเป็นสัญญาณมากหลายสําหรับหมู่ชนผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(27,87,'27:87','และ (จงรําลึกถึง) วันที่สังข์จะถูกเป่าขึ้น ดังนั้นผู้ที่อยู่ในชั้นฟ้าทั้งหลายและผู้ที่อยู่ในแผ่นดินจะตื่นตระหนก เว้นแต่ผู้ที่พระองค์ทรงประสงค์ และทั้งหมดได้มาหาพระองค์ในสภาพผู้ถ่อมตน');
INSERT INTO "king_fahad_thai" VALUES(27,88,'27:88','และเจ้าจะเห็นขุนเขาทั้งหลาย เจ้าจะเกิดว่ามันติดแน่นอยู่กับที่ แต่มันล่องลอยไปเช่นการล่อลอยของเมฆ (นั่นคือ)การงานของอัลลอฮ์ซึ่งพระองค์ทรงทําทุกสิ่งอย่างเรียบร้อย แท้จริงพระองค์เป็นผู้ทรงตระหนักในสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(27,89,'27:89','ผู้ใดนํามาซึ่งความดี เขาจะได้รับความดีมากกว่านั้น และในวันนั้น พวกเขาจะเป็นผู้ปลอดภัยจากการตื่นตระหนก');
INSERT INTO "king_fahad_thai" VALUES(27,90,'27:90','และผู้ใดนํามาซึ่งความชั่ว ใบหน้าของพวกเขาจะถูกโยนกลิ้งลงไปในไฟนรก พวกเจ้าจะไม่ถูกตอบแทน นอกจากสิ่งที่พวกเจ้าได้ปฏิบัติเอาไว้');
INSERT INTO "king_fahad_thai" VALUES(27,91,'27:91','(จงกล่าวเถิดมุฮัมมัด) “แท้จริงฉันได้รับบัญชาว่า จงเคารพภักดีพระเจ้า แห่งเมืองนี้ซึ่งพระองค์ทรงทําให้มันเป็นที่ต้องห้าม และทุกสิ่งทุกอย่างเป็นสิทธิของพระองค์ และฉันได้รับบัญชาให้อยู่ในหมู่ผู้นอบน้อม”');
INSERT INTO "king_fahad_thai" VALUES(27,92,'27:92','และฉันได้รับพระบัญชาให้อ่านอัลกุรอาน” ดังนั้นผู้ใดได้ตามแนวทางที่ถูกต้อง แท้จริงเขาก็จะดําเนินตามแนวทางที่ถูกต้องเพื่อตัวของเขาเอง และผู้ใดหลงผิดก็จงกล่าวเถิด (มุฮัมมัด) “แท้จริงฉันเป็นเพียงผู้หนึ่งในหมู่ผู้ตักเตือน”');
INSERT INTO "king_fahad_thai" VALUES(27,93,'27:93','และจงกล่าวเถิด (มุฮัมมัด) “บรรดาการสรรเสริญเป็นของอัลลอฮ์ พระองค์จะทรงให้พวกเจ้าเห็นสัญญาณทั้งหลายของพระองค์ แล้วพวกเจ้าก็จะรู้จักกัน” และพระเจ้าของเจ้ามิได้เป็นผู้ทรงเพิกเฉย ต่อสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(28,1,'28:1','ฏอ ซีน มีม');
INSERT INTO "king_fahad_thai" VALUES(28,2,'28:2','เหล่านี้คือโองการทั้งหลายแห่งคัมภีร์อันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(28,3,'28:3','เราจะอ่าน แก่เจ้า บางส่วนแห่งเรื่องราวของมูซาและฟิรเอานด้วยความจริง เพื่อหมู่ชนผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(28,4,'28:4','แท้จริงฟิรเอานหยิ่งผยองในแผ่นดิน และทําให้ประชาชนนั้นแตกแยกเป็นกลุ่ม ๆ เขาทําให้ชนกลุ่มหนึ่งในพวกเขาอ่อนแอ โดยฆ่าลูกหลานผู้ชายของพวกเขาและไว้ชีวิตเหล่าสตรีของพวกเขา แท้จริงเขาเป็นผู้หนึ่งในหมู่ผู้บ่อนทําลาย');
INSERT INTO "king_fahad_thai" VALUES(28,5,'28:5','และเราปรารถนาที่จะให้ความโปรดปรานแก่บรรดาผู้ที่อ่อนแอในแผ่นดิน และเราจะทําให้พวกเขาเป็นหัวหน้า และทําให้พวกเขาเป็นผู้รับมรดา');
INSERT INTO "king_fahad_thai" VALUES(28,6,'28:6','และเราได้ให้พวกเขาครอบครองในแผ่นดิน และเราจะให้ฟิรเอานและฮามานตลอดจนไพร่พลของเขาทั้งสอง ได้เห็นสิ่งที่พวกเขามีความกลัว');
INSERT INTO "king_fahad_thai" VALUES(28,7,'28:7','และเราได้ดลใจแก่มารดาของมูซา “จงให้นมแก่เขา เมื่อเจ้ากลัวแทนเขาก็จงโยนเขาลงไปในแม่น้ํา และเจ้าอย่าได้กลัวและอย่าได้เศร้าโศก แท้จริงเราจะให้เขากลับไปหาเจ้า และเราจําทําให้เขาเป็นหนึ่งในบรรดาร่อซูล”');
INSERT INTO "king_fahad_thai" VALUES(28,8,'28:8','ดังนั้นบริวารของฟิรเอานได้เก็บเขาขึ้นมาเพื่อให้เขากลายเป็นศัตรู และความเศร้าโศกแก่พวกเขา แท้จริงฟิรเอาน และฮามานและไพร่พลของเขาทั้งสองเป็นพวกที่มีความผิด');
INSERT INTO "king_fahad_thai" VALUES(28,9,'28:9','และภริยาของฟิรเอานกล่าวว่า “(เขาจะเป็นที่) น่าชื่นชมยินดีแก่ดิฉันและแก่ท่าน อย่าฆ่าเขาเลย บางทีเขาจะเป็นประโยชน์แก่เรา หรือเราจะถือเขาเป็นลูก” และพวกเขาหารู้สึกตัวไม่');
INSERT INTO "king_fahad_thai" VALUES(28,10,'28:10','และจิตใจของมารดาของมูซาได้คลายความวิตกกังวลลง นางเกือบจะเปิดเผยกับเขาหากเรามิได้ทําให้จิตใจของนางมั่นคง เพื่อที่นางจะเป็นหนึ่งในหมู่ผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(28,11,'28:11','และนางได้กล่าวแก่พี่สาวของเขา “จงติดตามไปดูเขา” ดังนั้นเธอ (พี่สาวของมูซา) ได้เห็นเขาแต่ไกล โดยที่พวกเขาไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(28,12,'28:12','และเราได้ห้ามเขาไว้ก่อนแล้วเรื่องแม่นม ดังนั้นเธอ (พี่สาวของมูซา) กล่าวว่า “ฉันจะชี้แนะชาวบ้านให้แก่พวกท่านเอาไหม?เพื่อคุ้มครองเขาแทนพวกท่าน และพวกเขาเป็นผู้ให้คําแนะนําอย่างดี”');
INSERT INTO "king_fahad_thai" VALUES(28,13,'28:13','ดังนั้น เราจึงให้เขากลับไปหามารดาของเขา เพื่อที่จะเป็นที่น่าชื่นชมยินดีแก่นางและนางจะไม่เศร้าโศก และเพื่อนางจะได้รู้ว่า แท้จริงสัญญาของอัลลอฮ์นั้นเป็นจริง แต่ส่วนมากพวกเขาไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(28,14,'28:14','และเมื่อเขาบรรลุความเป็นหนุ่มและเติบโตเต็มที่แล้ว เราได้ให้ความเข้าใจ และความรู้แก่เขา และเช่นนั้นแหละ เราจะตอบแทนแก่บรรดาผู้กระทําความดี');
INSERT INTO "king_fahad_thai" VALUES(28,15,'28:15','และเขา (มูซา) ได้เข้าไปในเมือง ขณะที่ชาวเมืองกําลังพักผ่อน เขาได้เห็นชายสองคนต่อสู้กันอยู่ในนั้น คนหนึ่งมาจากพวกพ้องของเขา และอีกคนหนึ่งมาจากฝ่าย (ที่เป็น) ศัตรูของเขา ดังนั้น คนที่มาจากพวกพ้องของเขาได้ร้องขอความช่วยเหลือ เพื่อให้ปราบฝ่ายที่เป็นศัตรูของเขา มูซาได้ต่อยเขาแล้วได้ฆ่าเขา เขากล่าวว่า “นี่มันเป็นการกระทําของชัยฏอน แท้จริงมันเป็นศัตรูที่ทําให้หลงผิดอย่างแจ้งชัด”');
INSERT INTO "king_fahad_thai" VALUES(28,16,'28:16','เขากล่าวว่า “ข้าแต่พระเจ้าของข้าพระองค์ แท้จริงข้าพระองค์ได้อธรรมต่อตนเอง ดังนั้นขอพระองค์ทรงอภัยให้แก่ข้าพระองค์ด้วย ” แล้วพระองค์ก็ได้อภัยให้เขา แท้จริงพระองค์เป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(28,17,'28:17','เขาได้กล่าวว่า “ข้าแต่พระเจ้าของข้าพระองค์ การที่พระองค์ได้ทรงโปรดปรานแก่ข้าพระองค์ ข้าพระองค์จะไม่เป็นผู้สนับสนุนผู้กระทําผิดอีกต่อไป”');
INSERT INTO "king_fahad_thai" VALUES(28,18,'28:18','เมื่อเข้ามาอยู่ในเมือง เขากลัวว่าจะเกิดภัยแก่เขา ขณะนั้นผู้ที่เคยขอร้องเขาให้ช่วยเหลือเมื่อวานนี้ กําลังร้องเรียกให้ช่วยเขาอีก มูซาจึงพูดกับเขาว่า “แท้จริงเจ้านั้นเป็นผู้หลงผิดอย่างแน่นอน”');
INSERT INTO "king_fahad_thai" VALUES(28,19,'28:19','เมื่อเขาต้องการที่จะปราบผู้ที่เป็นศัตรูกับเขาทั้งสอง เขากล่าวว่า “โอ้มูซาเอ๋ย ! ท่านต้องการที่จะฆ่าฉันดั่งที่ท่านได้ฆ่าคนหนึ่งไปแล้วเมื่อวานนี้หรือ ? ท่านไม่ปรารถนาสิ่งใดนอกจากเป็นผู้ก่อกวนทารุณในแผ่นดิน และท่านไม่ปรารถนาที่จะเป็นผู้ปรองดองให้ดีต่อกัน”');
INSERT INTO "king_fahad_thai" VALUES(28,20,'28:20','และชายคนหนึ่ง ได้มาจากชานเมืองอย่างรีบเร่ง เขากล่าวว่า “โอ้มูซาเอ๋ย ! พวกขุนนางชั้นผู้ใหญ่กําลังปรึกษาหารือกันเกี่ยวกับเรื่องของท่าน เพื่อจะฆ่าท่าน ดังนั้น จงออกไปเถิด แท้จริงฉันเป็นผู้หวังดีต่อท่าน”');
INSERT INTO "king_fahad_thai" VALUES(28,21,'28:21','ดังนั้น เขาจึงออกจากเมืองนั้นในสภาพหวาดกลัวว่าจะเกิดภัย เขากล่าวว่า “ข้าแต่พระเจ้าของข้าพระองค์ โปรดช่วยข้าพระองค์ให้รอดพ้นจากหมู่ผู้อธรรม”');
INSERT INTO "king_fahad_thai" VALUES(28,22,'28:22','และเมื่อเขามุ่งหน้าไปยัง (เมือง) มัดยัน เขากล่าวว่า “หวังว่าพระเจ้าของฉันจะทรงชี้แนะแก่ฉันสู่ทางอันเที่ยงตรง”');
INSERT INTO "king_fahad_thai" VALUES(28,23,'28:23','และเมื่อเขามาพบบ่อน้ําแห่ง (เมือง) มัดยัน เขาได้พบฝูงชนกลุ่มหนึ่งกําลังตักน้ํา และนอกจากพวกเขาเหล่านั้น เขายังได้พบหญิงสองคนคอยห้าม (ฝูงแกะ) เขา (มูซา) กล่าวถามว่า “เรื่องราวของเธอทั้งสองเป็นมาอย่างไร ?” นางทั้งสองกล่าวว่า “เราไม่สามารถตักน้ําได้ จนกว่าคนเลี้ยงแกะเล่านั้นจะถอยออกไป และบิดาของเราก็เป็นคนแก่มากแล้ว”');
INSERT INTO "king_fahad_thai" VALUES(28,24,'28:24','ดังนั้น เขาจึงตักน้ําให้แก่นางทั้งสองแล้วก็กลับไปพักใต้ร่ม และกล่าวว่า “ข้าแต่พระเจ้าของข้าพระองค์ แท้จริงข้าพระองค์อยากได้ในความดีที่พระองค์ทรงประทานลงมาให้แก่ข้าพระองค์”');
INSERT INTO "king_fahad_thai" VALUES(28,25,'28:25','นางคนหนึ่งในสองคนได้มาหาเขา เดินมาอย่างขวยเขิน แล้วกล่าวขึ้นว่า “คุณพ่อของดิฉันขอเชิญท่านไป เพื่อจะตอบแทนค่าแรงแก่ท่านที่ได้ช่วยตักน้ําให้เรา” ครั้นเมื่อเขา (มูซา) ได้มาหาเขา (นะบีชุไอบฺ) และได้เล่าเรื่องราวแก่เขา เขากล่าวว่า “ท่านไม่ต้องกลัว ท่านได้หนีพ้นจากหมู่ชนผู้อธรรมแล้ว”');
INSERT INTO "king_fahad_thai" VALUES(28,26,'28:26','นางคนหนึ่งในสองคนกล่าวว่า “โอ้คุณพ่อจ๋า! จ้างเขาไว้ซิ แท้จริงคนดีที่ท่านควรจะจ้างเขาไว้คือ ผู้ที่แข็งแรง ผู้ที่ซื่อสัตว์”');
INSERT INTO "king_fahad_thai" VALUES(28,27,'28:27','เขา (ชุไอบ) กล่าวว่า “แท้จริง ฉันต้องการที่จะให้ท่านสมรสกับลูกสาวคนหนึ่งในสองคนนี้ โดยท่านจะต้องทํางานให้ฉัน 8 ปี และถ้าท่านทําได้ครบ 10 ปี ก็เป็นความดีที่มาจากท่าน ฉันไม่ต้องการที่จะทําความลําบากให้ท่าน อินชาอัลลอฮ์ ท่านจะพบฉันอยู่ในหมู่คนดี”');
INSERT INTO "king_fahad_thai" VALUES(28,28,'28:28','เขา (มูซา) กล่าวว่า “นั่นคือ (ข้อสัญญา) ระหว่างฉันกับท่าน ฉันจะปฏิบัติให้ครบหนึ่งในกําหนดทั้งสอง จะไม่เกิดโทษแก่ฉัน และอัลลอฮฺทรงเป็นพยานต่อสิ่งที่เรากล่าวเป็นสัญญา”');
INSERT INTO "king_fahad_thai" VALUES(28,29,'28:29','ครั้นเมื่อมูซาปฏิบัติครบกําหนดแล้ว และได้เดินทางไปพร้อมกับครอบครัวของเขา เขาได้มองเห็นไฟลุกอยู่ข้างภูเขาฏูร เขาจึงพูดกับครอบครัวของเขาว่า "จงอยู่ที่นี่ก่อน แท้จริงฉันเห็นไฟ"');
INSERT INTO "king_fahad_thai" VALUES(28,30,'28:30','เมื่อเขาได้มาที่มัน (ไฟ) ได้มีเสียงเรียกจากริมที่ลุ่มทางด้านขวา ในสถานที่ที่มีความจําเริญ ณ ที่ต้นไม้ ว่า “โอ้มูซาเอ๋ย ! แท้จริงข้าคืออัลลอฮ์พระเจ้าแห่งสากลโลก');
INSERT INTO "king_fahad_thai" VALUES(28,31,'28:31','และจงโยนไม้เท้าของเจ้า” เมื่อเขาเห็นมันเคลื่อนไหวคล้ายกับงู เขาก็ผินหลังกลับและไม่กลับมามองอีก “โอ้มูซาเอ๋ย !จงเข้าไปใกล้เถิดและอย่าหวาดกลัว แท้จริงเจ้าอยู่ในหมู่ผู้ปลอดภัย”');
INSERT INTO "king_fahad_thai" VALUES(28,32,'28:32','จงสอดมือของเจ้าเข้าไปในอกเสื้อของเจ้า มันจะออกมาขาว ปราศจากอันตรายใด ๆ และจงเอามือแนบตัวเจ้าไว้เพื่อให้คลายความตกใจ ดังนั้นนั่นคือหลักฐานทั้งสอง จากพระเจ้าของเจ้าไปยังฟิรเอานและบุคคลชั้นหัวหน้าของเขา แท้จริงพวกเขาเป็นหมู่ชนผู้ฝ่าฝืน”');
INSERT INTO "king_fahad_thai" VALUES(28,33,'28:33','เขา (มูซา) กล่าวว่า “ข้าแต่พระเจ้าของข้าพระองค์แท้จริงข้าพระองค์ได้ฆ่าคนหนึ่งจากพวกเขา ดังนั้นข้าพระองค์กลัวว่าพวกเขาจะฆ่าข้าพระองค์');
INSERT INTO "king_fahad_thai" VALUES(28,34,'28:34','และพี่ชายของข้าพระองค์คือฮารูน เขาพูดจาคล่องแคล่วกว่าข้าพระองค์ ดังนั้น ขอได้โปรดส่งเขาเป็นผู้ช่วยร่วมกับข้าพระองค์ด้วยเถิด เพื่อเขาจะได้ยืนยันให้แก่ข้าพระองค์ แท้จริงข้าพระองค์กลัวว่าพวกเขาจะปฏิเสธข้าพระองค์”');
INSERT INTO "king_fahad_thai" VALUES(28,35,'28:35','พระองค์ตรัสว่า “เราจะให้เจ้ามีความเข้มแข็งด้วยพี่ชายของเจ้า และเราจะให้เจ้าทั้งสองมีอํานาจ ดังนั้นพวกเขาจะเข้าไม่ถึงเจ้าทั้งสองดอกเพราะสัญญาณต่าง ๆ ของเรา เจ้าทั้งสองและผู้ตามเจ้าทั้งสองเป็นผู้ชนะ”');
INSERT INTO "king_fahad_thai" VALUES(28,36,'28:36','ดังนั้น เมื่อมูซาได้มาหาพวกเขาพร้อมด้วยสัญญาณทั้งหลายอันชัดแจ้งของเรา พวกเขากล่าวว่า “มันมิใช่อะไรอื่น นอกจากเวทมนตร์ที่ถูกกุขึ้น และเราไม่เคยได้ยินข้อกล่าวอ้างเช่นนี้ในสมัยบรรพบุรุษของเราแต่กาลก่อนเลย”');
INSERT INTO "king_fahad_thai" VALUES(28,37,'28:37','และมูซากล่าวว่า “พระเจ้าของฉันทรงรู้ดียิ่งถึงผู้ที่นําเอาแนวทางที่ถูกต้องมาจากพระองค์และผู้ที่บั้นปลายแห่งที่พํานักจะเป็นของเขา แท้จริงพวกอธรรมนั้นจะไม่ประสบความเจริญ”');
INSERT INTO "king_fahad_thai" VALUES(28,38,'28:38','และฟิรเอานกล่าวว่า “โอ้ปวงบริพารเอ๋ย! ฉันไม่เคยรู้จักพระเจ้าอื่นใดของพวกท่านนอกจากฉัน โอ้ฮามานเอ๋ย ! จงเผาดินให้ฉันด้วยแล้วสร้างโครงสูงระฟ้า เพื่อที่ฉันจะได้ขึ้นไปดูพระเจ้าของมูซา และแท้จริงฉันคิดว่า เขานั้นอยู่ในหมู่ผู้กล่าวเท็จ”');
INSERT INTO "king_fahad_thai" VALUES(28,39,'28:39','และเขา (ฟิรเอาน) และไพร่พลของเขาได้หยิ่งผยอง ในแผ่นดินโดยอธรรม และพวกเขาคิดว่า แท้จริงพวกเขานั้นจะไม่ถูกนํากลับไปยังเรา');
INSERT INTO "king_fahad_thai" VALUES(28,40,'28:40','ดังนั้น เราได้ลงโทษเขาและไพร่พลของเขาเราได้โยนพวกเขาลงไปในทะแล แล้วจงพิจารณาเถิด บั้นปลายของพวกอธรรมเป็นเช่นไร');
INSERT INTO "king_fahad_thai" VALUES(28,41,'28:41','และเราได้ทําให้พวกเขาเป็นหัวหน้า เรียกร้องไปสู่นรกญะฮันนัม และในวันกิยามะฮ์ พวกเขาจะไม่ได้รับความช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(28,42,'28:42','และเราได้ให้การสาปแช่งตามติดพวกเขาในโลกนี้ และในวันกิยามะฮ์พวกเขาจะอยู่ในหมู่ผู้ถูกขับไล่ออกจากความเมตตา');
INSERT INTO "king_fahad_thai" VALUES(28,43,'28:43','และโดยแน่นอนเราได้ประทานคัมภีร์แก่มูซา หลังจากที่เราได้ทําลายชนชาติในรุ่นก่อน ๆ เพื่อให้เป็นที่ประจักษ์แจ้งแก่ปวงมนุษย์ และเป็นแนวทางที่ถูกต้อง และเป็นความเมตตา หวังว่าพวกเขาจะได้พิจารณาใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(28,44,'28:44','และเจ้า (มุฮัมมัด) มิได้ปรากฏอยู่ทางด้านข้างทิศตะวันตก เมื่อเราได้กําหนดกิจการแก่มูซา และเจ้ามิได้ปรากฏอยู่ในหมู่ผู้ร่วมเป็นพยาน');
INSERT INTO "king_fahad_thai" VALUES(28,45,'28:45','และแต่ทว่าเราได้บังเกิดอีกหลายศตวรรษ แล้วการมีชีวิตอยู่ก็ยืนยาวแก่พวกเเขา และเจ้ามิได้ปรากฏอยู่ร่วมกับกลุ่มชนมัดยัน เพื่อสาธยายโองการทั้งหลายของเราแก่พวกเขา แต่ว่าแท้จริงเราเป็นผู้ส่ง (เจ้ามา)');
INSERT INTO "king_fahad_thai" VALUES(28,46,'28:46','และเจ้า (มุฮัมมัด) มิได้ปรากฏอยู่ทางด้านข้างของภูเขาฎูร เมื่อเราได้ร้องเรียก แต่มันเป็นความเมตตาจากพระเจ้าของเจ้าเพื่อเจ้าจักได้ตักเตือนกลุ่มชนหนึ่ง ที่มิได้มีผู้ตักเตือนคนใดมายังพวกเขาก่อนหน้าเจ้า หวังว่าพวกเขาจะได้ใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(28,47,'28:47','และหากมิใช่เคราะห์กรรมหนึ่งประสบแก่พวกเขา เนื่องด้วยน้ํามือของพวกเขาที่ได้กระทําไว้ก่อน แล้วพวกเขาก็จะพูดขึ้นว่า“ข้าแต่พระเจ้าของเรา เหตุใดพระองค์จึงไม่ส่งร่อซูลคนหนึ่งมายังพวกเรา เพื่อจะได้ปฏิบัติตามโองการทั้งกลายของพระองค์ท่านและเราจะได้อยู่ในหมู่ผู้ศรัทธา”');
INSERT INTO "king_fahad_thai" VALUES(28,48,'28:48','ครั้นเมื่อสัจธรรมจากเราได้มายังพวกเขา พวกเขากล่าวว่า “ทําไมเขา (มุฮัมมัด) จึงมิได้รับเยี่ยงกับที่มูซาได้รับเล่า ?” ก็พวกเขามิได้ปฏิเสธสิ่งที่ถูกประทานให้แก่มูซามาก่อนดอกหรือ ? พวกเขากล่าวว่า “ทั้งสองคือเวทมนตร์ที่สนับสนุนซึ่งกันและกัน” และว่า “เราเป็นผู้ปฏิเสธทั้งสิ้น”');
INSERT INTO "king_fahad_thai" VALUES(28,49,'28:49','จงกล่าวเถิด (มุฮัมมัด) “ดังนั้น พวกท่านจงนําคัมภีร์สักเล่มหนึ่งจากอัลลอฮ์ ที่ถูกต้องเหมาะสมยิ่งกว่าทั้งสอง เพื่อฉันจะได้ปฏิบัติตามมัน หากพวกท่านเป็นผู้สัตว์จริง”');
INSERT INTO "king_fahad_thai" VALUES(28,50,'28:50','หากพวกเขาไม่ยอมสนองตอบเจ้า ก็พึงรู้เถิดว่า แท้จริงพวกเขาปฏิบัติตามอารมณ์ต่ําของพวกเขาเท่านั้น และผู้ใดเล่าจะหลงผิดยิ่งไปกว่าผู้ปฏิบัติตามอารมณ์ต่ําของเขา โดยปราศจากแนวทางที่ถูกต้องจากอัลลอฮ์ แท้จริงอัลลอฮ์จะไม่ทรงชี้แนะทางที่ถูกต้องแก่กลุ่มชนผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(28,51,'28:51','และโดยแน่นอน เราได้ให้พระดํารัส (อัลกุรอาน) สืบต่อเนื่องกันแก่พวกเขา เพื่อพวกเขาจะได้ใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(28,52,'28:52','บรรดาผู้ที่เราประทานคัมภีร์แก่พวกเขามาก่อนมัน (อัลกุรอาน) พวกเขาศรัทธาในมัน (อัลกุรอาน)');
INSERT INTO "king_fahad_thai" VALUES(28,53,'28:53','และเมื่อ (อัลกุรอาน) ได้ถูกอ่านแก่พวกเขา พวกเขากล่าวว่า “เราศรัทธาในมัน แท้จริงมันคือสัจธรรมมาจากพระเจ้าของเรา แท้จริงเราเป็นผู้นอบน้อมมาก่อนนี้”');
INSERT INTO "king_fahad_thai" VALUES(28,54,'28:54','ชนเหล่านั้นจะได้รับรางวัลของพวกเขาสองครั้ง เนื่องจากเขาได้อดทน และพวกเขาป้องกันความชั่วด้วยความดี และพวกเขาบริจาคสิ่งที่เราได้ให้เป็นเครื่องยังชีพแก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(28,55,'28:55','และเมื่อพวกเขาได้ยินเรื่องไร้สาระ พวกเขาก็ผินหลังออกห้างไปจากมัน และกล่าวว่า “การงานของเราก็จะได้แก่เรา และการงานของพวกท่านก็จะได้แก่พวกท่าน ศานติแด่พวกท่าน เราจะไม่ขอร่วมกับพวกงมงาย”');
INSERT INTO "king_fahad_thai" VALUES(28,56,'28:56','แท้จริง เจ้าไม่สามารถที่จะชี้แนะทางที่ถูกต้องแก่ผู้ที่เจ้ารักได้ แต่อัลลอฮ์ทรงชี้แนะทางที่ถูกต้องแก่ผู้ที่พระองค์ทรงประสงค์ และพระองค์ทรงรู้ดียิ่งถึงผู้ที่อยู่ในแนวทางที่ถูกต้อง');
INSERT INTO "king_fahad_thai" VALUES(28,57,'28:57','และพวกเขากล่าวว่า “หากเราปฏิบัติตามแนวทางที่ถูกต้องร่วมกับท่าน พวกเราจะถูกฉุดกระชากออกจากแผ่นดินของเรา” และเรามิได้ตั้งหลักแหล่งแก่พวกเขาในเขตหวงห้ามอันปลอดภัยดอกหรือ? ซึ่งผลไม้ทุกชนิดถูกนํามายังที่นั้น เป็นเครื่องยังชีพที่มาจากเรา แต่ส่วนมากของพวกเขาไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(28,58,'28:58','และกี่มากน้อยแล้วที่เราได้ทําลายเมืองซึ่งผยองลําพองในความเป็นอยู่ของมัน ดังนั้นนั่นคือที่พํานักของพวกเขา จะไม่มีใครพักอยู่หลังจากพวกเขาเว้นแต่เพียงเล็กน้อย และเราเป็นผู้รับทายาท');
INSERT INTO "king_fahad_thai" VALUES(28,59,'28:59','และพระเจ้าของเจ้ามิได้เป็นผู้ทําลายเมืองทั้งหลาย จนกว่าพระองค์จะทรงแต่งตั้งร่อซูลขึ้นในเมืองหลวงนั้น โดยสาธยายโองการทั้งหลายของเราแก่พวกเขา และเรามิได้เป็นผู้ทําลายเมืองทั้งหลาย เว้นแต่ชาวเมืองนั้นเป็นผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(28,60,'28:60','และสิ่งใดที่พวกเจ้ามิได้รับนั้น มันเป็นเพียงปัจจัยแห่งชีวิตของโลกนี้ และเป็นเครื่องประดับของมัน แต่ที่อัลลอฮ์นั้นดีกว่าและจีรังกว่า พวกเจ้าไม่ใช้ปัญญาใคร่ครวญดอกหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(28,61,'28:61','ดังนั้นผู้ใดที่เราได้ให้สัญญาแก่เขาซึ่งเป็นสัญญาอันดีงาม เขาก็จะเป็นผู้พบมัน จะเหมือนกับผู้ที่เราได้ให้ปัจจัยแก่เขาซึ่งปัจจัยแห่งชีวิตของโลกนี้ แล้วในวันกิยามะฮฺเขาจะเป็นผู้หนึ่งในหมู่ผู้ถูกนํามาอยู่ต่อหน้ากระนั้นหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(28,62,'28:62','และ (จงรําลึกถึง) วันที่พระองค์ทรงเรียกพวกเขาแล้วตรัสว่า “ไหนเล่าเหล่าภาคีของข้าที่พวกเจ้ากล่าวอ้าง”');
INSERT INTO "king_fahad_thai" VALUES(28,63,'28:63','บรรดาผู้ที่พระดํารัสคู่ควรแก่พวกเขา กล่าวว่า “ข้าแต่พระเข้าของเรา เหล่านี้คือบรรดาผู้ที่เราทําให้หลงผิด เราได้ทําให้พวกเขาหลงผิดเช่นเดียวกับที่เราได้หลงผิดเอง เราขอปลีกตัวออกแด่พระองค์ และพวกเขามิได้เคารพภักดีต่อเราจริง ๆ ดอก”');
INSERT INTO "king_fahad_thai" VALUES(28,64,'28:64','และจะมีเสียงกล่าวว่า “จงเรียกร้องภาคีของพวกเจ้า” พวกเขาก็ร้องเรียกพวกมัน แต่พวกมันไม่ขารับพวกเขา และเมื่อพวกเขาได้เห็นการลงโทษ พวกเขาก็คาดหวังว่าหากพวกเขาได้อยู่ในแนวทางที่ถูกต้อง');
INSERT INTO "king_fahad_thai" VALUES(28,65,'28:65','และวันที่พระองค์ทรงเรียกพวกเขาแล้วตรัสว่า “พวกเจ้าโต้ตอบแก่บรรดาร่อซูลว่าอย่างไร ?”');
INSERT INTO "king_fahad_thai" VALUES(28,66,'28:66','ดังนั้น ข้อแก้ตัวได้ทําให้พวกเขามืดมนในวันนั้น ฉะนั้น พวกเขาจึงไม่ไต่ถามซึ่งกันและกัน');
INSERT INTO "king_fahad_thai" VALUES(28,67,'28:67','ส่วนผู้ลุแก่โทษ และศรัทธา และประกอบความดี บางทีเขาจะอยู่ในหมู่ผู้บรรลุความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(28,68,'28:68','และพระเจ้าของเจ้าทรงสร้างสิ่งที่พระองค์ทรงประสงค์และทรงเลือก สําหรับพวกเขาไม่มีสิทธิ์ในการเลือก มหาบริสุทธิ์ยิ่งแด่อัลลอฮ์ และพระองค์ทรงสูงส่งจากสิ่งที่พวกเขาตั้งภาคี');
INSERT INTO "king_fahad_thai" VALUES(28,69,'28:69','และพระเจ้าของเจ้านั้น ทรงรอบรู้สิ่งที่หัวอกของพวกเขาปกปิดอยู่ และสิ่งที่พวกเขาเปิดเผย');
INSERT INTO "king_fahad_thai" VALUES(28,70,'28:70','และพระองค์คืออัลลอฮ์ ไม่มีพระเจ้าอื่นใดนอกจากพระองค์ สําหรับพระองค์เท่านั้นบรรดาการสรรเสริญในโลกหน้า และการชี้ขาดนั้นเป็นสิทธิของพระองค์เท่านั้น และยังพระองค์เท่านั้นพวกเจ้าจะถูกนํากลับ');
INSERT INTO "king_fahad_thai" VALUES(28,71,'28:71','จงกล่าวเถิด (มุฮัมมัด) พวกท่านเห็นแล้วมิใช่หรือ หากอัลลอฮ์ทรงทําให้กลางคือมีอยู่ตลอดไปแก่พวกท่านจนถึงวันกิยามะฮ์ พระเจ้าองค์ใดเล่าอื่นจากอัลลอฮ์ที่จะนําแสงสว่าง มาให้แก่พวกท่าน พวกท่านไม่รับฟังบ้างหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(28,72,'28:72','จงกล่าวเถิด (มุฮัมมัด) พวกท่านเห็นแล้วมิใช่หรือ หากอัลลอฮ์ทรงทําให้กลางวันมีอยู่ตลอดไปแก่พวกท่านจนถึงวันกิยามะฮ์พระเจ้าองค์ใดเล่าอื่นจากอัลลอฮ์ ที่จะนํากลางคืนมาให้พวกท่าน เพื่อพวกท่านจะได้พักผ่อนในเวลานั้น พวกท่านไม่พิจารณาไตร่ตรองดูบ้างหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(28,73,'28:73','และเพราะความเมตตาของพระองค์ พระองค์ทรงทําให้มีกลางคืนและกลางวัน เพื่อพวกเจ้าจะได้พักผ่อนในเวลานั้น และเพื่อพวกเจ้าจะได้แสวงหาจากความโปรดปรานของพระองค์ และเพื่อพวกเจ้าจะได้ขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(28,74,'28:74','และ (จงรําลึกถึง) วันที่พระองค์ทรงเรียกพวกเขาแล้วตรัสว่า “ไหนเล่าเหล่าภาคีของข้า ที่พวกเจ้ากล่าวอ้าง”');
INSERT INTO "king_fahad_thai" VALUES(28,75,'28:75','และเราได้เอาพยานออกมาจากทุก ๆ ประชาชาติ แล้วเราได้กล่าวว่า “จงนําหลักฐานของพวกเจ้ามา” ดังนั้นพวกเขาจึงรู้ว่า แท้จริงสัจธรรมนั้นเป็นของอัลลอฮ์ และสิ่งที่พวกเขาได้กุขึ้นนั้นได้สูญหายไปจากพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(28,76,'28:76','แท้จริงกอรูนมาจากพวกพ้องของมูซา เขาได้กดขี่ต่อพวกเขา และเราได้ประทานทรัพย์สมบัติมากมาบแก่เขา จนกระทั่งลูกกุญแจทั้งหลายของมันนั้น เมื่อคนแข็งแรงกลุ่มหนึ่งยกแบกด้วยความยากลําบาก เมื่อพวกพ้องของเขากล่าวแก่เขาว่า”อย่าได้หยิ่งผยอง เพราะแท้จริงอัลลอฮไม่ทรงโปรดบรรดาผู้หยิ่งผยอง”');
INSERT INTO "king_fahad_thai" VALUES(28,77,'28:77','และจงแสวงหาสิ่งที่อัลลอฮ์ได้ประทานแก่เจ้าเพื่อปรโลก และอย่าลืมส่วนของเจ้าแห่งโลกนี้ และจงทําความดี เสมือนกับที่อัลลอฮ์ได้ทรงทําความดีแก่เจ้า และอย่าแสวงหาความเสียหายในแผ่นดิน แท้จริง อัลลอฮ์ไม่ทรงโปรดบรรดาผู้บ่อนทําลาย');
INSERT INTO "king_fahad_thai" VALUES(28,78,'28:78','เขา (กอรูน) กล่าวว่า “ฉันได้รับมันเพราะความรู้ของฉัน” ก็เขา (กอรูน) ไม่รู้ดอกหรือว่า แน่นอน อัลลอฮ์ได้ทรงทําลายผู้ที่มีพลังยิ่งกว่าและมีพรรคพวกมากกว่า ก่อนหน้าเขาในศตวรรษก่อน ๆ และบรรดาผู้กระทําความผิดจะไม่ถูกสอบถามเกี่ยวกับความผิดต่าง ๆ ของพวกเขาดอกหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(28,79,'28:79','ดังนั้น เขาได้ออกไปหาพวกพ้องของเขาด้วยเครื่องประดับอย่างโอ่งอ่างของเขา บรรดาผู้ปรารถนาชีวิตแห่งโลกนี้ กล่าวว่า “โอ้ หากเราไม่มีเช่นที่กอรูนได้ถูกประทานมา แท้จริงเขาเป็นผู้มีโชควาสนายิ่งใหญ่จริง ๆ”');
INSERT INTO "king_fahad_thai" VALUES(28,80,'28:80','และบรรดาผู้ได้รับความรู้ กล่าวว่า “ความวิบัติแด่พวกท่าน ! ผลบุญแห่งอัลลอฮ์นั้นดีกว่าแก่ผู้ศรัทธาและกระทําความดีและไม่มีผู้ใดได้รับมันนอกจากบรรดาผู้อดทนเท่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(28,81,'28:81','ดังนั้น เราจึงให้ธรณีสูบเขาและเคหะสถานของเขา สําหรับเขาไม่มีผู้ใดจะช่วยเหลือเขาได้นอกจากอัลลอฮ์ และเขาก็มิใช่เป็นผู้ช่วยเหลือตนเองได้');
INSERT INTO "king_fahad_thai" VALUES(28,82,'28:82','และบรรดาผู้อยากมีฐานะเยี่ยงเขาเมื่อวานนี้ จะกล่าวในวันพรุ่งนี้ว่า “พึงทราบเถิด! เป็นที่แน่นอนว่าอัลลอฮฺนั้นทรงให้กว้างขวางและทรงให้คับแคบซึ่งเครื่องยังชีพแก่ผู้ที่พระองค์ทรงประสงค์จากปวงบ่าวของพระองค์ หากมิใช่อัลลอฮฺทรงโปรดปรานแก่เรา แน่นอนพระองค์ก็จะทรงให้ (ธรณี) สูบเราลงไป พึงทราบเถิด! แท้จริงบรรดาผู้ปฏิเสธศรัทธาจะไม่ประสบชัยชนะ”');
INSERT INTO "king_fahad_thai" VALUES(28,83,'28:83','นั่นคือที่พํานักแห่งปรโลก เราได้เตรียมมันไว้สําหรับบรรดาผู้ที่ไม่ปรารถนาหยิ่งผยองในแผ่นดิน และไม่ก่อการเสียหาย และบั้นปลายย่อมเป็นของบรรดาผู้ยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(28,84,'28:84','ผู้ใดนําเอาความดีมา เขาก็จะได้รับความดียิ่งกว่า และผู้ใดนําความชั่วมา บรรดาผู้กระทําความชั่วทั้งหลายนั้นจะไม่ถูกตอบแทน นอกจากที่พวกเขาได้กระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(28,85,'28:85','แท้จริง พระผู้ทรงประทานอัลกุรอานให้แก่เจ้า แน่นอน ย่อมทรงนําเจ้ากลับสู่ถิ่นเดิน จงกล่าวเถิด (มุฮัมมัด) “พระเจ้าของฉันทรงรู้ดียิ่งกว่า ผู้ใดอยู่ในแนวทางที่ถูกต้องและผู้ใดอยู่ในการหลงผิดที่ชัดแจ้ง”');
INSERT INTO "king_fahad_thai" VALUES(28,86,'28:86','และเจ้ามิได้หวังมาก่อนเลยว่า คัมภีร์นี้จะถูกประทานให้แก่เจ้า เว้นแต่ว่ามันเป็นความเมตตาจากพระเจ้าของเจ้า ดังนั้น เจ้าอย่าเป็นผู้สนับสนุนแก่บรรดาผู้ปฏิเสธศรัทธาเลย');
INSERT INTO "king_fahad_thai" VALUES(28,87,'28:87','และอย่าให้พวกเขาหันเหเจ้าจากโองการทั้งหลายของอัลลอฮ์ หลังจากที่มันได้ถูกประทานลงมาแก่เจ้า และจงเชิญชวนไปสู่พระเจ้าของเจ้าและอย่าอยู่ในหมู่ผู้ตั้งภาคี');
INSERT INTO "king_fahad_thai" VALUES(28,88,'28:88','และอย่าวิงวอนขอต่อพระเจ้าอื่นคู่เคียงกับอัลลอฮ์ ไม่มีพระเจ้าอื่นใดนอกจากพระองค์ทุกสิ่งย่อมพินาศนอกจากพระพักตร์ของพระองค์');
INSERT INTO "king_fahad_thai" VALUES(29,1,'29:1','อะลิฟ ลาม มีม');
INSERT INTO "king_fahad_thai" VALUES(29,2,'29:2','มนุษย์คิดหรือว่า พวกเขาจะถูกทอดทิ้งเพียงแต่พวกเขากล่าวว่าเราศรัทธา และพวกเขาจะไม่ถูกทดสอบ กระนั้นหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(29,3,'29:3','และโดยแน่นอน เราได้ทดสอบบรรดาก่อนหน้าพวกเขา แล้ว ดังนั้นอัลลอฮ์จะทรงจําแนกให้รู้แจ้งถึงบรรดาผู้สัตว์จริง และจะทรงจําแนกให้รู้แจ้งถึงบรรดาผู้กล่าวเท็จ');
INSERT INTO "king_fahad_thai" VALUES(29,4,'29:4','หรือบรรดาผู้กระทําความชั่วทั่งหลายคิดว่าพวกเขาจะรอดพ้นไปจากเรา ชั่วช้าแท้ ๆ สิ่งที่พวกเขาตัดสินกัน');
INSERT INTO "king_fahad_thai" VALUES(29,5,'29:5','ผู้ใดหวังที่จะพบ อัลลอฮ์ ดังนั้นแท้จริงกําหนดของอัลลอฮ์ย่อมมาถึงแน่นอน และพระองค์เป็นทรงได้ยิน ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(29,6,'29:6','และผู้ใดต่อสู้ดิ้นรน แท้จริงเขาย่อมต่อสู้ดิ้นรนเพื่อตัวของเราเอง แท้จริงอัลลอฮ์นั้น แน่นอน ทรงมั่งมีเหนือประชาชาติทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(29,7,'29:7','และบรรดาผู้ศรัทธาและกระทําความดีทั้งกลายนั้น แน่นอนเราจะลบล้างความชั่วทั้งหลายของพวกเขาไปจากพวกเขา และแน่นอนเราจะตอบแทนพวกเขาสิ่งที่ดียิ่ง ซึ่งพวกเขาได้กระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(29,8,'29:8','และเราได้สั่งเสียงมนุษย์ให้ทําดีต่อบิดามารดาของเขา และถ้าทั้งสองบังคับเจ้าเพื่อให้ตั้งภาคีในสิ่งที่เจ้าไม่มีความรู้ เจ้าก็อย่าปฏิบัติตามเขาทั้งสอง ยังข้าคือการกลับของพวกเจ้า ดังนั้นข้าจะแจ้งแก่พวกเข้าในสิ่งที่พวกเจ้าได้กระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(29,9,'29:9','และบรรดาผู้ศรัทธา และกระทําความดีนั้น แน่นอนเราจะให้พวกเขาเข้าอยู่ในหมู่คนดีทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(29,10,'29:10','และในหมู่มนุษย์นั้นมีผู้กล่าวว่า เราศรัทธาต่ออัลลอฮ์ ครั้นเมื่อเขาถูกทําร้ายในทางของอัลลอฮ์ เขาก็ถือเอาการทดสอบของมนุษย์ประหนึ่งการลงโทษของอัลลอฮ์ และเมื่อการช่วยเหลือจากพระเจ้าของเจ้ามาถึง แน่นอนพวกเขาจะกล่าวว่าแท้จริงเราจะร่วมกับพวกท่าน และมิใช่อัลลอฮ์เป็นผู้ทรงรู้ดียิ่งในสิ่งที่มีอยู่ในหัวอก ของประชาชาติทั้งหลายดอกหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(29,11,'29:11','และแน่นอน อัลลอฮ์ทรงรู้ดียิ่งถึงบรรดาผู้ศรัทธา และแน่นอนพระองค์ทรงรู้ดียิ่งถึงพวกมุนาฟิกีน');
INSERT INTO "king_fahad_thai" VALUES(29,12,'29:12','และบรรดาผู้ปฏิเสธศรัทธาได้กล่าวแก่บรรดาผู้ศรัทธาว่า จงปฏิบัติตามแนวทางของเราและเราจะแบกรับความผิดของพวกท่าน และพวกเขามิได้แบกรับความผิดของเขาเหล่านั้นแต่อย่างใด แท้จริงพวกเขาเป็นผู้กล่าวเท็จอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(29,13,'29:13','และแน่นอน พวกเขาจะแบกรับความผิดของพวกเขาและความผิดอื่น ๆ ร่วมกับความผิดของพวกเขา และแน่นอนพวกเขาจะถูกสอบสวนในวันกิยามะฮ์ในสิ่งที่พวกเขาได้กุขึ้น');
INSERT INTO "king_fahad_thai" VALUES(29,14,'29:14','และโดยแน่นอนเราได้ส่งนูห์ไปยังหมู่ชนของเขา และเขาได้อยู่ร่วมกับพวกเขาหนึ่งพันปีเว้นห้าสิบปี (950 ปี)');
INSERT INTO "king_fahad_thai" VALUES(29,15,'29:15','ดังนั้นเราได้ช่วยเขาและพวกพ้องในเรือ ให้รอดพ้น และเราได้ทําให้มันเป็นสัญญาณหนึ่งแก่ประชาชาติ');
INSERT INTO "king_fahad_thai" VALUES(29,16,'29:16','และ (จงรําลึกถึง) อิบรอฮีม เมื่อเขากล่าวแก่หมู่ชนของเขาว่า จงเคารพภักดีต่ออัลลอฮ์และจงยําเกรงต่อพระองค์ นั่นแหละเป็นการดีว่าสําหรับพวกท่าน หากพวกท่านรู้อัลลอฮ์นั้น มันไม่มีอํานาจที่จะให้เครื่องยังชีพแก่พวกท่าน ดังนั้นจงขอเครื่องยังชีพจากอัลลอฮ์เถิด');
INSERT INTO "king_fahad_thai" VALUES(29,17,'29:17','แท้จริงพวกท่านบูชารูปปั้น อื่นจากอัลลอฮ์และพวกท่านกุการมุสาขึ้น แท้จริงบรรดาที่พวกท่านบูชาอื่นจากอัลลอฮ์นั้น มันไม่มีอํานาจที่จะให้เครื่องยังชีพแก่พวกท่าน ดังนั้นจงขอเครื่องยังชีพจากอัลลอฮ์เถิด และจงเคารพภักดีพระองค์และจงขอบคุณต่อพระองค์ ยังพระองค์เท่านั้นพวกท่านจะถูกนํากลับไป');
INSERT INTO "king_fahad_thai" VALUES(29,18,'29:18','และหากพวกท่านปฏิเสธ แน่นอนประชาชาติทั้งหลายก่อนพวกท่านก็ได้ปฏิเสธมาแล้ว หน้าที่ของร่อซูลนั้นมิใช่อะไรอื่น นอกจากการเผยแพร่อันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(29,19,'29:19','และพวกเขามิเห็นดอกหรือว่า อัลลอฮ์ทรงเริ่มการบังเกิดอย่างไร แล้วทรงให้เขากลับฟื้นคืนชีพขึ้นมาอีก แท้จริงนั่นเป็นการง่ายแก่อัลลอฮ์');
INSERT INTO "king_fahad_thai" VALUES(29,20,'29:20','จงกล่าวเถิด (มุฮัมมัด) จงท่องเที่ยวไปตามแผ่นดินแล้วพิจารณาดูว่าพระองค์ทรงให้บังเกิดอย่างไร แล้วอัลลอฮ์ทรงให้ฟื้นคืนชีพในปรโลก แท้จริงอัลลอฮ์นั้นเป็นผู้ทรงอานุภาพเหนือทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(29,21,'29:21','พระองค์ทรงลงโทษผู้ที่พระองค์ทรงประสงค์ และทรงเมตตาผู้ที่พระองค์ทรงประสงค์ และยังพระองค์เท่านั้นพวกท่านจะถูกนํากลับไป');
INSERT INTO "king_fahad_thai" VALUES(29,22,'29:22','และพวกท่านไม่สามารถจะรอดพ้น ไปได้ทั้งในแผ่นดินและในฟากฟ้า และอื่นจากอัลลอฮ์ สําหรับพวกท่านไม่มีผู้คุ้มครอง และไม่มีผู้ช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(29,23,'29:23','และบรรดาผู้ปฏิเสธศรัทธาโองการทั้งหลายของอัลลอฮ์ และการพบพระองค์ ชนเหล่านั้น พวกเขาท้อแท้ต่อความเมตตาของข้า และชนเหล่านั้น สําหรับพวกเขาคือการลงโทษอันเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(29,24,'29:24','ดังนั้น คําตอบของหมู่ชนของเขามิใช่อื่นใดนอกจากกล่าวว่า จงฆ่าเขาหรือเผาเขาเสีย แต่อัลลอฮ์ได้ทรงช่วยเขาให้พ้นจากไฟ แท้จริงในการนี้ย่อมเป็นสัญญาณมากหลายสําหรับหมู่ชนผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(29,25,'29:25','และเขา (อิบรอฮีม) กล่าวว่า แท้จริงพวกท่านได้ยึดเอารูปปั้นต่าง ๆ อื่นจากอัลลอฮ์เพื่อให้เป็นที่รักใคร่ระหว่างพวกท่านในชีวิตแห่งโลกนี้ และในวันกิยามะฮ์บางคนในหมู่พวกท่านก็จะปฏิเสธอีกบางคน และบางคนในหมู่พวกท่านก็จะแช่งด่าอีกบางคน');
INSERT INTO "king_fahad_thai" VALUES(29,26,'29:26','ดังนั้นลูฏ ได้ศรัทธาต่อเขา และเขา (อิบรอฮิม) กล่าวว่า แท้จริงฉันอพยพไปหาพระเจ้าของฉัน แท้จริงพระองค์คือผู้ทรงอํานาจ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(29,27,'29:27','และเราได้ให้อิสหากแก่เขา และเราได้ให้การเป็นนะบี และคัมภีร์แก่ลูกหลานของเขา และเราได้แก่เขาซึ่งรางวัลของเขาในโลกนี้ และแท้จริงเขาในปรโลกจะอยู่ในหมู่คนดี');
INSERT INTO "king_fahad_thai" VALUES(29,28,'29:28','และ (จงรําลึกถึง) ลูฏ เมื่อเขากล่าวแก่หมู่ชนของเขาว่า แท้จริงพวกท่านได้กระทําการลามกซึ่งไม่มีผู้ใดในหมู่มวลชนกระทํามาก่อนพวกท่านเลย');
INSERT INTO "king_fahad_thai" VALUES(29,29,'29:29','แท้จริงพวกท่านสมสู่พวกผู้ชาย และปล้นบนทางหลวงกระนั้นหรือ ? และกระทําอนาจารในที่ชุมชนของพวกท่าน แต่คําตอบของหมู่ชนของเขามิใช่อื่นใดนอกจากกล่าวว่า จงนําการลงโทษของอัลลอฮ์มาให้แก่พวกเราซิ หากท่านอยู่ในหมู่ผู้สัตย์จริง');
INSERT INTO "king_fahad_thai" VALUES(29,30,'29:30','เขา (ลูฏ) กล่าวว่า ข้าแต่พระเจ้าของข้าพระองค์ ขอพระองค์ทรงโปรดช่วยข้าพระองค์ให้อยู่เหนือหมู่ชนผู้บ่อนทําลายด้วยเถิด');
INSERT INTO "king_fahad_thai" VALUES(29,31,'29:31','และครั้นเมื่อฑูตของเรา (มะลาอิกะฮฺ) ได้มาหาอิบรอฮีมพร้อมด้วยข่าวดี พวกเขากล่าวว่า แท้จริงเราเป็นผู้ทําลายชาวเมืองนี้ เพราะว่าชาวเมืองของมันเป็นผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(29,32,'29:32','เขา (อิบรอฮีม) กล่าวว่า แท้จริงในเมืองนั้นมีลูฏอยู่ด้วย พวกเขากล่าวว่า เรารู้ดีว่ามีใครอยู่ในนั้น แน่นอนเราจะช่วยเขาและบริวารของเขาให้รอดพ้น เว้นแต่ภริยาของเขาเพราะนางอยู่ในหมู่ผู้ถูกทําลาย');
INSERT INTO "king_fahad_thai" VALUES(29,33,'29:33','และเมื่อฑูตของเรา (มะลาอิกะฮ์) ได้มาหาลูฏ เขาเป็นทุกข์เพราะพวกเขา และกลัวที่จะให้ความคุ้มครองไม่ได้ ดังนั้น (มะลาอิกะฮ์) จึงกล่าวว่า อย่ากลัวและเศร้าโศกเสียใจ แท้จริงเราเป็นผู้ช่วยเหลือท่านและบริวารของท่านเว้นแต่ภริยาของท่าน เพราะนางอยู่ในหมู่ผู้ถูกทําลาย');
INSERT INTO "king_fahad_thai" VALUES(29,34,'29:34','แท้จริงเราเป็นผู้นําการลงโทษจากฟากฟ้าสู่ชาวเมืองนี้ เนื่องจากพวกเขาฝ่าฝืน');
INSERT INTO "king_fahad_thai" VALUES(29,35,'29:35','และโดยแน่นอน เราได้ทิ้งสัญญาณอันชัดแจ้งของเมืองนี้ ไว้สําหรับหมู่ชนผู้มีปัญญาพิจารณา');
INSERT INTO "king_fahad_thai" VALUES(29,36,'29:36','และยัง (หมู่ชน) มัดยัน (เราได้ส่ง) พี่น้องของพวกเขาคือชุไอบ์ เขากล่าวขึ้นว่า โอ้หมู่ชนของฉันเอ๋ย ! จงเคารพสักการะอัลลอฮ์เถิด และจงกลัววันสุดท้าย และอย่ามุ่งทําความเสียหายในแผ่นดิน โดยเป็นผู้บ่อนทําลาย !');
INSERT INTO "king_fahad_thai" VALUES(29,37,'29:37','แต่พวกเขาได้ปฏิเสธเขา ดังนั้นแผ่นดินไหวอย่างรุนแรงได้คร่าพวกเขา แล้วพวกเขาได้ประสบความหายนะนอนพังพาบตายในบ้านของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(29,38,'29:38','และอ๊าดและซะมูด และได้เป็นที่ประจักษ์แก่พวกเจ้าแล้ว จากที่พํานักของพวกเขา และมารชัยฏอนได้ทําให้การงานของพวกเขา เป็นที่เพริศแพร้วแก่พวกเขา แล้วมันได้หันเหพวกเขาออกจากแนวทางโดยที่พวกเขาเป็นผู้มีสติปัญญาพิจารณา');
INSERT INTO "king_fahad_thai" VALUES(29,39,'29:39','และ (เราได้ทําลาย) กอรูน และฟิรเอาน์และฮามาน และโดยแน่นอนมูซาได้มายังพวกเขาพร้อมด้วยหลักฐานอันชัดแจ้ง แต่พวกเขาหยิ่งผยองในแผ่นดิน และพวกเขาก็หาได้รอดพ้นไปจากเราไม่');
INSERT INTO "king_fahad_thai" VALUES(29,40,'29:40','และแต่ละคนเราได้ลงโทษด้วยความผิดของเขา เช่น บางคนในหมู่พวกเขาเราได้ส่งลมพายุร้ายทําลายเขา และบางคนในหมู่พวกเขา เราได้ลงโทษเขาด้วยเสียงกัมปนาท และบางคนในหมู่พวกเขา เราได้ให้แผ่นดินสูบเขา และบางคนในหมู่พวกเขา เราได้ให้เขาจมน้ําตาย และอัลลอฮ์มิได้ทรงอธรรมแก่พวกเขา แต่พวกเขาต่างหากที่อธรรมต่อพวกเขาเอง');
INSERT INTO "king_fahad_thai" VALUES(29,41,'29:41','อุปมาบรรดาผู้ที่ยึดเอาอื่นจากอัลลอฮ์เป็นผู้คุ้มครองอุปไมยดั่งแมงมุมที่ชักใยทํารัง และแท้จริงรังที่บอบบางที่สุดคือรังของแมงมุม หากพวกเขารู้');
INSERT INTO "king_fahad_thai" VALUES(29,42,'29:42','แท้จริงอัลลอฮ์ทรงรอบรู้สิ่งใด ๆ ที่พวกเขาวิงวอนขออื่นจากพระองค์ และพระองค์เป็นผู้ทรงอํานาจ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(29,43,'29:43','และเหล่านี้คืออุปมาทั้งหลายที่เราได้เปรียบเทียบมัน สําหรับปวงมนุษย์ แต่ไม่มีผู้ใดตระหนักมันหรอก นอกจากผู้มีความรู้');
INSERT INTO "king_fahad_thai" VALUES(29,44,'29:44','อัลลอฮ์ทรงสร้างชั้นฟ้าทั้งหลายและแผ่นดินด้วยความจริงที่แน่นอน แท้จริงในการนี้แน่นอนย่อมเป็นสัญญาณแก่บรรดาผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(29,45,'29:45','เจ้าจงอ่านสิ่งที่ถูกวะฮีย์แก่เจ้าจากคัมภีร์ และจงดํารงการละหมาด (เพราะ) แท้จริงการละหมาด นั้นจะยับยั้งการทําลามกและความชั่วและการรําลึกถึงอัลลอฮ์นั้น ยิ่งใหญ่มาก และอัลลอฮ์ทรงรอบรู้สิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(29,46,'29:46','และพวกเจ้าอย่าโต้เถียงกับพวกอะฮ์ลุลกิตาบเว้นแต่ด้วยวิธีที่ดีกว่า นอกจากบรรดาผุ้อธรรมในหมู่พวกเขา และพวกเจ้าจงกล่าวว่า เราศรัธาในสิ่งที่ถูกประทานแก่เราและสิ่งที่ได้ถูกประทานแก่พวกเจ้า และพระเจ้าของเราและพระเจ้าของพวกเขาเป็นเอกะ และเราเป็นผู้นอบน้อมต่อพระองค์');
INSERT INTO "king_fahad_thai" VALUES(29,47,'29:47','และเช่นนั้นแหละ เราได้ประทานคัมภีร์แก่เจ้า ดังนั้นบรรดาผู้ที่เราได้ประทานคัมภีร์แก่พวกเขา พวกเขาก็ศรัทธาต่อมัน (อัลกุรอาน) และในหมู่เขาเหล่านั้นมีผู้ศรัทธาต่อมัน และไม่มีผู้ใดปฏิเสธโองการทั้งหลายของเรา นอกจากผู้ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(29,48,'29:48','และก่อนหน้านั้นเจ้ามิได้อ่านคัมภีร์ใด ๆ และเจ้ามิได้เขียนมันด้วยมือขวาของเจ้า มิฉะนั้นแล้วพวกกล่าวความเท็จจะสงสัยอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(29,49,'29:49','มิใช่เช่นนั้นดอก มันคือโองการทั้งหลายอันแจ้งชัดอยู่ในหัวอกของบรรดาผู้ได้รับความรู้ และไม่มีผู้ใดปฏิเสธโองการทั้งหลายของเรานอกจากพวกอธรรม');
INSERT INTO "king_fahad_thai" VALUES(29,50,'29:50','และพวกเขากล่าวว่า ทําไมสัญญาณ ต่าง ๆ จากพระเจ้าของเขาจึงมิถูกประทานมายังเขา จงกล่าวเถิด (มุฮัมมัด) แท้จริงสัญญาณเหล่านั้นอยู่ที่อัลลอฮ์ และฉันเป็นผู้ตักเตือนอันกระจ่างแจ้งเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(29,51,'29:51','มิเพียงพอแก่พวกเขาดอกหรือที่เราได้ประทานคัมภีร์ให้แก่เจ้าซึ่งได้ถูกอ่านให้แก่พวกเขาฟัง แท้จริงในการนั้นแน่นอนย่อมเป็นความแมตตาและเป็นการตักเตือนแก่หมู่ชนผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(29,52,'29:52','จงกล่าวเถิด (มุฮัมมัด) พอเพียงแล้วที่อัลลอฮ์ทรงเป็นพยานระหว่างฉันและพวกท่าน พระองค์ทรงรอบรู้สิ่งที่อยู่ในชั้นฟ้าทั้งหลายและแผ่นดิน และบรรดาผู้เชื่อในความเท็จและปฏิเสธศรัทธาต่ออัลลอฮ์ ชนเหล่านั้นแหละพวกเขาเป็นผู้ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(29,53,'29:53','และพวกเขาเร่งเร้าเจ้า ในเรื่องการลงโทษ และหากมิใช่เวลาที่ถูกกําหนดไว้แล้ว การลงโทษก็จะเกิดขึ้นแก่พวกเขาอย่างแน่นอน และมันจะเกิดขึ้น แก่พวกเขาโดยฉับพลันอย่างแน่นอนโดยที่พวกเขาไม่รู้สึกตัว');
INSERT INTO "king_fahad_thai" VALUES(29,54,'29:54','พวกเขาเร่งเร้าเข้าในเรื่องการลงโทษและแท้จริงนรกญะฮันนัมนั้นเป็นที่สะกัดล้อมพวกปฏิเสธศรัทธาไว้อย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(29,55,'29:55','วันซึ่งการลงโทษจะครอบคลุมพวกเขาจากข้างบนพวกเขาและจากใต้เท้าของพวกเขา และพระองค์จะตรัสว่า พวกเจ้าจงลิ้มรสสิ่งที่พวกเจ้าได้ก่อกรรมกระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(29,56,'29:56','โอ้ปวงบ่วงของข้า บรรดาผู้ที่ศรัทธาเอ๋ย ! แท้จริงแผ่นดินของข้านั้นกว้างใหญ่ไพศาล ดังนั้นเฉพาะข้าเท่านั้นพวกเจ้าจงเคารพภักดี');
INSERT INTO "king_fahad_thai" VALUES(29,57,'29:57','ทุก ๆ ชีวิตเป็นผู้ลิ้มรสความตาย แล้วพวกเจ้าจะถูกนํากลับยังเรา');
INSERT INTO "king_fahad_thai" VALUES(29,58,'29:58','และบรรดาผู้ศรัทธาและกระทําความดีทั้งหลาย แน่นอนเราจะให้พวกเขาพํานักอยู่ในห้องพิเศษแห่งสวนสวรรค์ ณ เบื้องล่างมันมีลําน้ําหลายสายไหลผ่าน พวกเขาเป็นผู้พํานักอยู่ในนั้นตลอดกาล รางวัลของผู้กระทําความดีช่างประเสริฐแท้ ๆ');
INSERT INTO "king_fahad_thai" VALUES(29,59,'29:59','บรรดาผู้อดทนขันติ และพวกเขามอบหมายไว้วางใจแด่พระเจ้าของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(29,60,'29:60','และสัตว์ตั้งกี่ชนิดที่มันมิสามารถแสวงหาเครื่องยังชีพของมัน อัลลอฮ์ ทรงประทานเครื่องยังชีพแก่พวกมัน และแก่พวกเจ้า และพระองค์เป็นผู้ทรงได้ยิน ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(29,61,'29:61','และถ้าเจ้าถามพวกเขา ใครเป็นผู้สร้างชั้นฟ้าทั้งหลาย และแผ่นดิน และเป็นผู้ทําให้ดวงอาทิตย์และดวงจันทร์เป็นประโยชน์ แน่นอนพวกเขาจะกล่าวว่าอัลลอฮ์ แล้วทําไมเล่าพวกเขาจึงหันเหออกไปทางอื่น !?');
INSERT INTO "king_fahad_thai" VALUES(29,62,'29:62','อัลลอฮ์ทรงให้เครื่องยังชีพกว้างขวางแก่ผู้ที่พระองค์ทรงประสงค์จากปวงบ่าวของพระองค์และทรงให้คับแคบแก่เขา แท้จริงอัลลอฮ์เป็นผู้ทรงรอบรู้ทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(29,63,'29:63','และถ้าเจ้าถามพวกเขาว่า ใครเล่าทรงหลั่งน้ําลงมาจากฟากฟ้าแล้วทรงให้แผ่นดินมีชีวิตชีวาหลังจากความแห้งแล้งของมัน แน่นอนพวกเขาจะกล่าวว่า อัลลอฮ์ จงกล่าวเถิด (มุฮัมมัด) บรรดาการสรรเสริญเป็นสิทธิ์ของอัลลอฮ์ แต่ว่าส่วนมากพวกเขาไม่ใช้สติปัญญาใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(29,64,'29:64','และการมีชีวิตอยู่ในโลกนี้มิใช่อื่นใด เว้นแต่เป็นการละเล่นและการสนุกสนานร่าเริง และแท้จริงสถานที่ในปรโลกนั้น แน่นอนมันคือชีวิตที่แท้จริงหากพวกเขาได้รู้');
INSERT INTO "king_fahad_thai" VALUES(29,65,'29:65','ดังนั้นเมื่อพวกเขาขึ้นขี่เรือ พวกเขาวิงวอนต่ออัลลอฮ์เป็นผู้บริสุทธิ์ใจในการขอพรต่อพระองค์ ครั้นเมื่อพระองค์ทรงช่วยพวกเขาให้ขึ้นบก แล้วพวกเขาก็ตั้งภาคีต่อพระองค์');
INSERT INTO "king_fahad_thai" VALUES(29,66,'29:66','เพื่อพวกเขาจะเนรคุณต่อสิ่งที่เราได้ประทานแก่พวกเขา และเพื่อพวกเขาจะได้หลงประทานแก่พวกเขา และเพื่อพวกเขาจะได้หลงระเริงแล้วพวกเขาก็จะได้รู้');
INSERT INTO "king_fahad_thai" VALUES(29,67,'29:67','พวกเขาไม่เห็นดอกหรือว่า เราได้ทําเขตหวงห้ามให้เป็นที่ปลอดภัย ขณะที่ประชาชนรอบ ๆ พวกเขาถูกฆ่า ถูกลักพาตัวไป แล้วพวกเขายัวจะศรัทธาต่อความเท็จ และพวกเขายังจะเนรคุณต่อความโปราดปรานของอัลลอฮ์อีกหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(29,68,'29:68','และใครเล่าจะอธรรมยิ่งกว่าผู้กุความเท็จให้แก่อัลลอฮ์ หรือปฏิเสธสัจธรรมเมื่อมันได้มายังเขา ที่พํานักในนรกญะฮันนัมมิใช่สําหรับบรรดาผู้ปฏิเสธศรัทธาดอกหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(29,69,'29:69','และบรรดาผู้ต่อสู้ดิ้นรนในทางของเราแน่นอนเราจะชี้แนะแนวทางที่ถูกต้องแก่พวกเขาสู่ทางของเรา และแท้จริงอัลลอฮ์ทรงอยู่ร่วมกับผู้กระทําความดีทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(30,1,'30:1','อะลิฟ ลาม มีม');
INSERT INTO "king_fahad_thai" VALUES(30,2,'30:2','พวกโรมันถูกพิชิตแล้ว');
INSERT INTO "king_fahad_thai" VALUES(30,3,'30:3','ในดินแดนอันใกล้นี้ แต่หลังจากการปราชัยของพวกเขาแล้วพวกเขาจะได้รับชัยชนะ');
INSERT INTO "king_fahad_thai" VALUES(30,4,'30:4','ในเวลาไม่กี่ปีต่อมา พระบัญชาเป็นสิทธิของอัลลอฮฺ ทั้งก่อนและหลัง (ชัยชนะ) และวันนั้นบรรดาผู้ศรัทธาจะดีใจ');
INSERT INTO "king_fahad_thai" VALUES(30,5,'30:5','ด้วยการช่วยเหลือของอัลลอฮฺ พระองค์ทรงช่วยเหลือผู้ที่พระองค์ทรงประสงค์ และพระองค์เป็นผู้ทรงอํานาจ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(30,6,'30:6','(นั่นคือ) สัญญาของอัลลอฮฺ อัลลอฮฺจะไม่ทรงบิดพริ้วสัญญาของพระองค์แต่ส่วนมากของมนุษย์ไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(30,7,'30:7','พวกเขารู้แต่เพียงผิวเผินในเรื่องการดํารงชีวิตในโลกนี้ และพวกเขาไม่คํานึงถึงการมีชีวิตในปรโลก');
INSERT INTO "king_fahad_thai" VALUES(30,8,'30:8','พวกเขามิได้ใคร่ครวญ ในตัวของพวกเขาดอกหรือว่า อัลลอฮฺมิได้ทรงสร้างชั้นฟ้าทั้งหลายและแผ่นดิน และสิ่งที่อยู่ระหว่างทั้งสอง เพื่อสิ่งอื่นใดเลย เว้นแต่เพื่อความจริงและเวลาที่ถูกกําหนดไว้ และแท้จรงส่วนมากของมนุษย์เป็นผู้ปฏิเสธศรัทธาต่อการพบพระผู้เป็นเจ้าของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(30,9,'30:9','พวกเขามิได้ท่องเที่ยวไปตามแผ่นดินดอกหรือ แล้วพิจารณาดูว่าบั้นปลายของประชาชาติในยุคก่อนหน้าพวกเขาเป็นเช่นใด เขาเหล่านั้นมีพลังที่เข้มแข็งกว่าพวกเขา เขาเหล่านั้นขุดพรวนดินและก่อสร้าง (เคหสถาน) มากกว่าพวกเขาก่อสร้างมัน และบรรดาร่อซูลของพวกเขาได้มาหาพวกเขาด้วยหลักฐานอันชัดแจ้ง ดังนั้น แน่นอนอัลลอฮฺมิได้ทรงอธรรมต่อพวกเขา แต่ว่าพวกเขาอธรรมต่อตัวของพวกเขาเองต่างหาก');
INSERT INTO "king_fahad_thai" VALUES(30,10,'30:10','แล้วบั้นปลายของบรรดาผู้กระทําความชั่วก็คือความชั่ว โดยที่พวกเขาปฏิเสธต่อสัญญาณทั้งหลายของอัลลอฮฺและพวกเขาเย้ยหยันมัน');
INSERT INTO "king_fahad_thai" VALUES(30,11,'30:11','อัลลอฮฺทรงเริ่มการบังเกิด (มนุษย์) แล้วทรงให้มันกลับมาเกิดอีก แล้วพวกเจ้าจะถูกนํากลับไปยังพระองค์');
INSERT INTO "king_fahad_thai" VALUES(30,12,'30:12','และวันที่วาระสุดท้ายจะเกิดขึ้น พวกทําผิดก็จะหมดหวัง');
INSERT INTO "king_fahad_thai" VALUES(30,13,'30:13','และจะไม่มีผู้ใดจากบรรดาภาคีของพวกเขาเป็นผู้ช่วยเหลือพวกเขา และพวกเขาก็เป็นผู้ปฏิเสธบรรดาภาคีของพวกเขาด้วย');
INSERT INTO "king_fahad_thai" VALUES(30,14,'30:14','และวันที่วาระสุดท้ายจะเกิดขึ้น วันนั้นพวกเขาจะแยกออกจากกัน');
INSERT INTO "king_fahad_thai" VALUES(30,15,'30:15','ดังนั้น สําหรับบรรดาผู้ศรัทธาและประกอบความดี พวกเขาก็จะชื่นชมยินดีอยู่ในสวนสวรรค์');
INSERT INTO "king_fahad_thai" VALUES(30,16,'30:16','และส่วนบรรดาผู้ปฏิเสธศรัทธาและไม่ยอมเชื่อต่อสัญญาณทั้งหลายของเรา และการพบ (เรา) ในวันปรโลก ชนเหล่านั้นแหละเป็นผู้อยู่ในการลงโทษอย่างถาวร');
INSERT INTO "king_fahad_thai" VALUES(30,17,'30:17','ดังนั้น มหาบริสุทธิ์ยิ่งแด่อัลลอฮฺ เมื่อพวกเจ้าย่างเข้าสู่ยามเย็น และพวกเจ้าย่างเข้าสู่ยามเช้า');
INSERT INTO "king_fahad_thai" VALUES(30,18,'30:18','มวลการสรรเสริญในชั้นฟ้าทั้งหลายและแผ่นดินเป็นกรรมสิทธิ์ของพระองค์ และในยามพลบค่ํา และเมื่อพวกเจ้าย่างเข้าสู่ยามบ่าย');
INSERT INTO "king_fahad_thai" VALUES(30,19,'30:19','พระองค์ทรงให้มีชีวิตหลังจากการตาย และทรงให้ตายหลังจากมีชีวิต และทรงให้แผ่นดินมีชีวิตชีวาขึ้น หลังจากการแห้งแล้งของมัน และเช่นนั้นแหละพวกเจ้าจะถูกนําออกมา');
INSERT INTO "king_fahad_thai" VALUES(30,20,'30:20','และหนึ่งจากสัญญาณทั้งหลายของพระองค์คือ ทรงสร้างพวกเจ้าจากดิน แล้วพวกเจ้าเป็นมนุษย์แพร่กระจายออกไป');
INSERT INTO "king_fahad_thai" VALUES(30,21,'30:21','และหนึ่งจากสัญญาณทั้งหลายของพระองค์คือ ทรงสร้างคู่ครองให้แก่พวกเจ้าจากตัวของพวกเจ้า เพื่อพวกเจ้าจะได้มีความสุขอยู่กับนาง และ ทรงมีความรักใคร่และความเมตตาระหว่างพวกเจ้า แท้จริงในการนี้ แน่นอน ย่อมเป็นสัญญาณแก่หมู่ชนผู้ใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(30,22,'30:22','และหนึ่งจากสัญญาณทั้งหลายของพระองค์คือ การสร้างชั้นฟ้าทั้งหลายและแผ่นดิน และการแตกต่างของภาษาของพวกเจ้าและผิวพรรณของพวกเจ้า แท้จริงในการนี้แน่นอน ย่อมเป็นสัญญาณสําหรับบรรดาผู้มีความรู้');
INSERT INTO "king_fahad_thai" VALUES(30,23,'30:23','และหนึ่งจากสัญญาณทั้งหลายของพระองค์คือ การหลับนอนของพวกเจ้าในกลางคืนและกลางวัน และการแสวงหาของพวกเจ้าซึ่งความโปรดปรานของพระองค์ แท้จริงในการนี้ แน่นอน ย่อมเป็นสัญญาณแก่หมู่ชนผู้ฟังเพื่อใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(30,24,'30:24','และหนึ่งจากสัญญาณทั้งหลายของพระองค์คือ ทรงให้พวกเจ้าเห็นสายฟ้าแลบเป็นที่หวาดกลัว และเป็นความหวัง และทรงหลั่งน้ําลงมาจากฟากฟ้า และทรงให้แผ่นดินมีชีวิตชีวาด้วยมัน (น้ําฝน) หลังจากการแห้งแล้งของมัน แท้จริงในการนั้น ย่อมเป็นสัญญาณแก่หมู่ชนผู้ใช้สติปัญญา');
INSERT INTO "king_fahad_thai" VALUES(30,25,'30:25','และหนึ่งจากสัญญาณทั้งหลายของพระองค์คือ ชั้นฟ้าและแผ่นดินมั่นคงอยู่ตามพระบัญชาของพระองค์ ครั้นเมื่อพระองค์ทรงร้องเรียกพวกเจ้าอีกครั้งหนึ่งให้ออกจากแผ่นดิน เมื่อนั้นพวกเจ้าก็จะออกมากัน');
INSERT INTO "king_fahad_thai" VALUES(30,26,'30:26','และผู้ที่อยู่ในชั้นฟ้าทั้งหลายและแผ่นดินเป็นกรรมสิทธิ์ของพระองค์ ทั้งมวลเป็นผู้จงรักภักดีต่อพระองค์');
INSERT INTO "king_fahad_thai" VALUES(30,27,'30:27','และพระองค์คือผู้ทรงเริ่มแรกในการสร้าง แล้วทรงให้มันกลับขึ้นมาอีก และมันเป็นการง่ายยิ่งแก่พระองค์ และคุณลักษณะอันสูงส่งในชั้นฟ้าทั้งหลายและแผ่นดินเป็นกรรมสิทธิ์ของพระองค์ และพระองค์เป็นผู้ทรงอํานาจ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(30,28,'30:28','พระองค์ทรงยกอุทธาหรณ์แก่พวกเจ้าที่มาจากตัวของพวกเจ้าเอง จะมีบ้างไหมสําหรับพวกเจ้า (ที่จะยอมให้) ในหมู่ผู้ที่มือขวาของพวกเจ้าครอบครอง (บ่าวทาส) มีหุ้นส่วนในสิ่งที่เราได้ให้เครื่องยังชีพแก่พวกเจ้า แล้วพวกเจ้า (กับพวกเขา) มามีส่วนเท่ากัน โดยพวกเจ้ากลัวพวกเขาเหมือนกับการกลัวของพวกเจ้าด้วยกันเอง เช่นนั้นแหละ เราจําแนกสัญญาณทั้งหลายแก่หมู่ชนผู้ใช้ปัญญาเพื่อไตร่ตรอง');
INSERT INTO "king_fahad_thai" VALUES(30,29,'30:29','เปล่าเลย ! แต่ว่าบรรดาผู้อธรรมได้ปฏิบัติตามอารมณ์ใฝ่ต่ําของพวกเขาโดยปราศจากความรู้ แล้วผู้ใดเล่าจะแนะแนวทางแก่ผู้ที่อัลลอฮฺทรงปล่อยให้เขาหลงทางไปแล้ว และสําหรับพวกเขาจะไม่มีผู้ช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(30,30,'30:30','ดังนั้น เจ้าจงผินหน้าของเจ้าสู่ศาสนาที่เที่ยงแท้ (โดยเป็น) ธรรมชาติของอัลลอฮฺ ซึ่งพระองค์ทรงสร้างมนุษย์ขึ้นมา ไม่มีการเปลี่ยนแปลงในการสร้างของอัลลอฮฺ นั่นคือศาสนาอันเที่ยงตรง แต่ส่วนมากของมนุษย์ไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(30,31,'30:31','และจงยําเกรงพระองค์ และจงดํารงไว้ซึ่งการละหมาด โดยเป็นผู้ผินหน้ากลับไปสู่พระองค์ และอย่าอยู่ในหมู่ผู้ตั้งภาคี');
INSERT INTO "king_fahad_thai" VALUES(30,32,'30:32','(คือ) ในหมู่ผู้แบ่งแยกศาสนาของพวกเขาออกเป็นนิกายต่าง ๆ และแต่ละหมู่คณะก็พอใจต่อสิ่งที่พวกเขามีอยู่');
INSERT INTO "king_fahad_thai" VALUES(30,33,'30:33','และเมื่อทุกขภัยอันใดประสบแก่มนุษย์ พวกเขาก็วิงวอนขอต่อพระเจ้าของพวกเขา โดยเป็นผู้ผินหน้ากลับไปสู่พระองค์ ครั้นเมื่อพระองค์ได้ทรงให้พวกเขาลิ้มรสความเมตตาจากพระองค์ ณ บัดนั้นหมู่หนึ่งจากพวกเขาก็ตั้งภาคีต่อพระเจ้าของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(30,34,'30:34','ก็จงเนรคุณต่อสิ่งที่เราได้ให้แก่พวกเขา แล้วก็จงร่าเริงกันต่อไปเถิด แล้วพวกเจ้าจะได้รู้');
INSERT INTO "king_fahad_thai" VALUES(30,35,'30:35','หรือว่าเราได้ให้หลักฐานอันใดแก่พวกเขา เพื่อมันจะได้พูดในสิ่งที่พวกเขาตั้งภาคีต่อพระองค์');
INSERT INTO "king_fahad_thai" VALUES(30,36,'30:36','และเมื่อเราได้ให้มนุษย์ลิ้มรสความเมตตา พวกเขาก็ดีใจต่อมัน และเมื่อทุกข์ร้ายอันใดประสบแก่พวกเขา เนื่องด้วยสิ่งที่มือของพวกเขาประกอบไว้ แล้วพวกเขาก็หมดอาลัย B30:036:1B อิบนุกะษีรกล่าวว่า อันนี้เป็นการกล่าวถึงข้อเท็จจริงของมนุษย์ นอกจากผู้ที่อัลลอฮฺทรงคุ้มครองเขาคือเมื่อเขาได้รับความโปรดปรานเขาก็ดีใจและลืมตัว แต่เมื่อประสบกับความทุกข์ยาก เขาก็หมดอาลัยและสิ้นหวัง');
INSERT INTO "king_fahad_thai" VALUES(30,37,'30:37','แท้จริงในการนี้ แน่นอน ย่อมเป็นสัญญาณแก่หมู่ชนผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(30,38,'30:38','จงบริจาคแก่ญาติสนิทซึ่งสิทธิของเขา และแก่ผู้ขัดสนและผู้เดินทางนั่นแหละเป็นการดียิ่งสําหรับบรรดาผู้ปรารถนาพระพักตร์ของอัลลอฮฺ และชนเหล่านั้นแหละพวกเขาเป็นผู้ประสบชัยชนะ');
INSERT INTO "king_fahad_thai" VALUES(30,39,'30:39','และสิ่งที่พวกเจ้าจ่ายออกไปจากทรัพย์สิน (ดอกเบี้ย) เพื่อให้มันเพิ่มพูนในทรัพย์สินของมนุษย์ มันจะไม่เพิ่มพูนณ ที่อัลลอฮฺและสิ่งที่พวกเจ้าจ่ายไปจากซะกาต โดยพวกเจ้าปรารถนาพระพักตร์ของอัลลอฮฺ ชนเหล่านั้นแหละพวกเขาคือผู้ได้รับการตอบแทนอย่างทวีคูณ');
INSERT INTO "king_fahad_thai" VALUES(30,40,'30:40','อัลลอฮฺคือผู้ทรงสร้างพวกเจ้า แล้วทรงให้ปัจจัยยังชีพแก่พวกเจ้า แล้วทรงให้พวกเจ้าตาย แล้วทรงให้พวกเจ้าเป็น จะมีผู้ใดบ้างในหมู่ภาคีของพวกเจ้ากระทําสักอย่างหนึ่งจากสิ่งเหล่านั้น มหาบริสุทธิ์แด่พระองค์ และพระองค์ทรงสูงส่งจากสิ่งที่พวกเขาตั้งภาคี');
INSERT INTO "king_fahad_thai" VALUES(30,41,'30:41','การบ่อนทําลาย ได้เกิดขึ้นทั้งทางบกและทางน้ํา เนื่องจากสิ่งที่มือของมนุษย์ได้ขวนขวายไว้เพื่อที่พระองค์จะให้พวกเขาลิ้มรสบางส่วนที่พวกเขาประกอบไว้ โดยที่หวังจะให้พวกเขากลับเนื้อกลับตัว');
INSERT INTO "king_fahad_thai" VALUES(30,42,'30:42','จงกล่าวเถิดมุฮัมมัด “พวกเจ้าจงท่องเที่ยวไปตามแผ่นดิน แล้วจงพิจารณาดูว่าบั้นปลายของกลุ่มชนในอดีตเป็นเช่นไร ส่วนมากของพวกเขาเป็นผู้ตั้งภาคี”');
INSERT INTO "king_fahad_thai" VALUES(30,43,'30:43','ดังนั้น จงมุ่งหน้าของเจ้าเพื่อศาสนาอันเที่ยงธรรม ก่อนที่วันหนึ่งของอัลลอฮฺจะมาถึง ซึ่งไม่มีทางหลีกเลี่ยงได้ วันนั้นพวกเขาจะแตกแยกกัน (เป็นสองฝ่าย)');
INSERT INTO "king_fahad_thai" VALUES(30,44,'30:44','ผู้ใดปฏิเสธศรัทธา การปฏิเสธศรัทธาก็ตกอยู่กับเขา และผู้ใดกระทําความดี พวกเขาก็เตรียมที่พักไว้สําหรับตัวของพวกเขาเอง');
INSERT INTO "king_fahad_thai" VALUES(30,45,'30:45','เพื่อที่พระองค์จะทรงตอบแทนบรรดาผู้ศรัทธาและกระทําความดีทั้งหลาย ด้วยความดีของพระองค์ แท้จริงพระองค์ไม่ทรงชอบพวกปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(30,46,'30:46','และหนึ่งจากสัญญาณทั้งหลายของพระองค์คือ พระองค์ทรงส่งลมเป็นการแจ้งข่าวดีทั้งหลาย และเพื่อพระองค์จะทรงให้พวกเจ้าได้ลิ้มรสความเมตตาของพระองค์ และเพื่อเรือเดินทะเลจะได้แล่นไปโดยพระบัญชาของพระองค์ และเพื่อพวกเจ้าจะได้แสวงหาจากความโปรดปรานของพระองค์ และเพื่อพวกเจ้าจะได้ขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(30,47,'30:47','และโดยแน่นอน เราได้ส่งบรรดาร่อซูลก่อนหน้าเจ้าไปยังหมู่ชนของพวกเขา และเขาเหล่านั้นได้นําหลักฐานต่าง ๆ มาให้ พวกเขาแล้ว ดังนั้น เราได้ตอบแทนบรรดาผู้กระทําความผิดอย่างสาสม และหน้าที่ของเราคือการช่วยเหลือบรรดาผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(30,48,'30:48','อัลลอฮฺทรงเป็นผู้ส่งลมทั้งหลาย แล้วมันได้รวมตัวกันขึ้นเป็นเมฆ แล้วพระองค์ทรงให้มันแผ่กระจายไปตามท้องฟ้า เท่าที่พระองค์ทรงประสงค์ และพระองค์ทรงทําให้มันเป็นกลุ่มก้อน แล้วเจ้าจะเห็นฝนตกลงมาจากท่ามกลางมัน เมื่อมันได้ตกลงมายังผู้ที่พระองค์ทรงประสงค์จากปวงบ่าวของพระองค์ เมื่อนั้นพวกเขาก็ดีใจ');
INSERT INTO "king_fahad_thai" VALUES(30,49,'30:49','และทั้ง ๆ ที่พวกเขา ก่อนที่ฝนจะตกลงมาแก่พวกเขา พวกเขาก็เป็นผู้หมดหวัง');
INSERT INTO "king_fahad_thai" VALUES(30,50,'30:50','ดังนั้น เจ้าจงพิจารณาดูร่องรอยแห่งความเมตตาของอัลลอฮฺว่า พระองค์ทรงให้แผ่นดินมีชีวิตชีวาหลังจากความแห้งแล้งของมันอย่างไร แท้จริงในการนั้น แน่นอน พระองค์ย่อมเป็นผู้ทรงให้มีชีวิตแก่คนที่ตายไปแล้ว และพระองค์นั้นเป็นผู้ทรงอานุภาพเหนือทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(30,51,'30:51','และหากเราได้ส่งลมไป แล้วพวกเขาได้เห็นมัน (พืชผล) เป็นสีเหลือง แน่นอนพวกเขาจะกลายเป็นผู้เนรคุณหลังจากนั้น');
INSERT INTO "king_fahad_thai" VALUES(30,52,'30:52','ดังนั้น แท้จริงเจ้าจะไม่ทําให้คนตายได้ยิน และเจ้าจะไม่ทําให้คนหูหนวกได้ยินการเรียกร้องได้ เมื่อพวกเขาเป็นผู้ผินหลังกลับ');
INSERT INTO "king_fahad_thai" VALUES(30,53,'30:53','และเจ้าก็มิใช่ผู้ชี้นําทางแก่คนตาบอดหลังจากการหลงทางของพวกเขา เจ้ามิได้ทําให้ผู้ใดได้ยิน นอกจากผู้ศรัทธาต่อสัญญาณทั้งหลายของเรา โดยที่พวกเขาเป็นผู้นอบน้อมยอมจํานน');
INSERT INTO "king_fahad_thai" VALUES(30,54,'30:54','อัลลอฮฺทรงเป็นผู้สร้างพวกเจ้าในสภาพอ่อนแอ แล้วหลังจากความอ่อนแอพระองค์ก็ทรงทําให้มีความแข็งแรงแล้วหลังจากความแข็งแรงทรงทําให้อ่อนแอและชราภาพ พระองค์ทรงสร้างสิ่งที่พระองค์ทรงประสงค์และพระองค์เป็นผู้ทรงรอบรู้ อานุภาพ');
INSERT INTO "king_fahad_thai" VALUES(30,55,'30:55','และเมื่อวันอวสานเกิดขึ้น ผู้กระทําผิดทั้งหลายจะสาบานว่า พวกเขามิได้พํานักอยู่ (ในโลกดุนยา) นอกจากเพียงชั่วครู่ชั่วยามเท่านั้น เช่นนั้นแหละ พวกเขาถูกให้หันออก (จากความจริงสู่ความเท็จ)');
INSERT INTO "king_fahad_thai" VALUES(30,56,'30:56','และบรรดาผู้มีความรู้และศรัทธา กล่าว (ตอบโต้พวกเขา) ว่า “โดยแน่นอน พวกท่านได้พํานักอยู่ตามกําหนดของอัลลอฮฺจนกระทั่งวันฟื้นคืนชีพ ดังนั้นนี่คือวันฟื้นคืนชีพแต่พวกท่านไม่ยอมเชื่อ”');
INSERT INTO "king_fahad_thai" VALUES(30,57,'30:57','ในวันนั้นการแก้ตัวของพวกเขาจะไม่อํานวยประโยชน์แก่บรรดาผู้อธรรม และพวกเขาจะไม่ถูกขอร้องให้กลับเนื้อกลับตัว');
INSERT INTO "king_fahad_thai" VALUES(30,58,'30:58','และโดยแน่นอนในอัลกุรอานนี้เราได้ยกอุทาหรณ์ไว้ทุกอย่างสําหรับมนุษย์ และหากว่าเจ้านํามาให้พวกเขาสัญญาณหนึ่ง แน่นอน บรรดาผู้ปฏิเสธศรัทธาจะกล่าวว่า “พวกท่านมิใช่ใครอื่น นอกจากเป็นผู้หลอกลวง”');
INSERT INTO "king_fahad_thai" VALUES(30,59,'30:59','เช่นนั้นแหละ อัลลอฮฺทรงประทับตราบนหัวใจของบรรดาผู้ไม่ยอมรับรู้');
INSERT INTO "king_fahad_thai" VALUES(30,60,'30:60','จงอดทน (ต่อไปเถิดมุฮัมมัด) แท้จริงสัญญาของอัลลอฮฺนั้นเป็นจริงเสมอ และอย่าให้บรรดาผู้ไม่มีความเชื่อมั่นทําให้เจ้ากังวลใจ');
INSERT INTO "king_fahad_thai" VALUES(31,1,'31:1','อะลิฟ ลาม มีม');
INSERT INTO "king_fahad_thai" VALUES(31,2,'31:2','เหล่านั้นคือบรรดาอายาตแห่งคัมภีร์ที่ชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(31,3,'31:3','(เพื่อ) เป็นแนวทางที่ถูกต้องและเป็นการเมตตาแก่บรรดาผู้กระทําความดี');
INSERT INTO "king_fahad_thai" VALUES(31,4,'31:4','(คือ) บรรดาผู้ดํารงการละหมาด และบริจาคซะกาต และพวกเขาเชื่อมั่นต่อวันปรโลก');
INSERT INTO "king_fahad_thai" VALUES(31,5,'31:5','ชนเหล่านั้นอยู่บนแนวทางที่ถูกต้องจากพระเจ้าของพวกเขา และชนเหล่านั้นพวกเขาเป็นผู้ประสบความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(31,6,'31:6','และในหมู่มนุษย์มีผู้ซื้อเอาเรื่องไร้สาระ เพื่อทําให้เขาหลงไปจากทางของอัลลอฮฺ โดยปราศจากความรู้ และถือเอามันเป็นเรื่องขบขัน ชนเหล่านี้พวกเขาจะได้รับการลงโทษอันอัปยศ');
INSERT INTO "king_fahad_thai" VALUES(31,7,'31:7','และเมื่ออายาตทั้งหลายของเราถูกอ่านให้แก่เขา เขาก็ผินหลังให้อย่างจองหอง ประหนึ่งว่าเขาไม่ได้ยินอายาตนั้นๆ ประหนึ่งว่าในหูของเขานั้นหนวก ดังนั้น จงแจ้งข่าวแก่เขาถึงการลงโทษอันเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(31,8,'31:8','แท้จริงบรรดาผู้ศรัทธาและกระทําความดีทั้งหลาย พวกเขาจะได้รับสวนสวรรค์หลากหลายอย่างสุขสําราญ');
INSERT INTO "king_fahad_thai" VALUES(31,9,'31:9','พวกเขาเป็นผู้พํานักอยู่ในนั้นตลอดกาล สัญญาของอัลลอฮฺนั้นเป็นจริง และพระองค์คือผู้ทรงอํานาจผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(31,10,'31:10','พระองค์ทรงสร้างชั้นฟ้าทั้งหลายโดยปราศจากเสาที่พวกเจ้าจะมองเห็นมันได้ และทรงปักเทือกเขาไว้อย่างมั่นคงในแผ่นดินเพื่อมิให้มันสั่นคลอนไปกับพวกเจ้า และทรงให้สัตว์ทุกชนิดแพร่หลายในมัน (แผ่นดิน) และเราได้ให้น้ํา (ฝน) หลั่งลงมาจากฟากฟ้าและเราได้ให้พืชทุกชนิดงอกเงยออกมาเป็นคู่ ๆ อย่างดีงาม');
INSERT INTO "king_fahad_thai" VALUES(31,11,'31:11','นี่คือการสร้างของอัลลอฮฺ ดังนั้น พวกเจ้าจงแสดงให้ข้า (อัลลอฮฺ) เห็นซิว่า อันใดเล่าที่เขาเหล่านั้นได้สร้างมันขึ้นมาอื่นจากพระองค์ แต่ว่าบรรดาผู้อธรรมต่างหากที่อยู่ในการหลงผิดอย่างชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(31,12,'31:12','และโดยแน่นอน เราได้ให้ฮิกมะฮฺ แก่ลุกมานว่า “จงขอบพระคุณต่ออัลลอฮฺ” และผู้ใดขอบคุณ แท้จริงเขาก็ขอบคุณตัวของเขาเอง และผู้ใดปฏิเสธ แท้จริงอัลลอฮฺนั้นทรงพอเพียงและทรงได้รับการสรรเสริญ');
INSERT INTO "king_fahad_thai" VALUES(31,13,'31:13','และจงรําลึกเมื่อลุกมานได้กล่าวแก่บุตรของเขา โดยสั่งสอนเขาว่า “โอ้ลูกเอ๋ย เจ้าอย่าได้ตั้งภาคีใด ๆ ต่ออัลลอฮฺ เพราะแท้จริงการตั้งภาคีนั้นเป็นความผิดอย่างมหันต์ โดยแน่นอน“');
INSERT INTO "king_fahad_thai" VALUES(31,14,'31:14','และเราได้สั่งการแก่มนุษย์เกี่ยวกับบิดา มารดาของเขา โดยที่มารดาของเขาได้อุ้มครรภ์เขาอ่อนเพลียลงครั้งแล้วครั้งเล่า และการหย่านมของเขาในระยะเวลาสองปี เจ้าจงขอบคุณข้า และบิดามารดาของเจ้า ยังเรานั้น คือการกลับไป');
INSERT INTO "king_fahad_thai" VALUES(31,15,'31:15','และถ้าเขาทั้งสองบังคับเจ้าให้ตั้งภาคีต่อข้า โดยที่เจ้าไม่มีความรู้ในเรื่องนั้น เจ้าอย่าได้เชื่อฟังปฏิบัติตามเขาทั้งสอง และจงอดทนอยู่กับเขาทั้งสองในโลกนี้ด้วยการทําความดี และจงปฏิบัติตามทางของผู้ที่กลับไปสู่ข้า และยังเรานั้นถือทางกลับของพวกเจ้า ดังนั้น ข้าจะบอกแก่พวกเจ้าในสิ่งที่พวกเจ้าได้กระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(31,16,'31:16','“โอ้ลูกเอ๋ย แท้จริง (หากว่าความผิดนั้น) มันจะหนักเท่าเมล็ดผักสักเมล็ดหนึ่ง มันจะซ่อนอยู่ในหิน หรืออยู่ในชั้นฟ้าทั้งหลาย หรืออยู่ในแผ่นดิน อัลลอฮฺก็จะทรงนํามันออกมา แท้จริง อัลลอฮฺเป็นผู้ทรงเมตตา ผู้ทรงรอบรู้ยิ่ง”');
INSERT INTO "king_fahad_thai" VALUES(31,17,'31:17','“โอ้ลูกเอ๋ย เจ้าจงดํารงไว้ซึ่งการละหมาด และจงใช้กันให้กระทําความดี และจงห้ามปรามกันให้ละเว้นการทําความชั่ว และจงอดทนต่อสิ่งที่ประสบกับเจ้า แท้จริง นั่นคือส่วนหนึ่งจากกิจการที่หนักแน่น มั่นคง”');
INSERT INTO "king_fahad_thai" VALUES(31,18,'31:18','“และเจ้าอย่าหันแก้ม (ใบหน้า) ของเจ้าให้แก่ผู้คนอย่างยะโส และอย่าเดินไปตามแผ่นดินอย่างไร้มรรยาท แท้จริง อัลลอฮฺ มิทรงชอบทุกผู้หยิ่งจองหอง และผู้คุยโวโอ้อวด”');
INSERT INTO "king_fahad_thai" VALUES(31,19,'31:19','“และเจ้าจงก้าวเท้าของเจ้าพอประมาณ และจงลดเสียงของเจ้าลง แท้จริง เสียงที่น่าเกลียดยิ่งคือเสียง(ร้อง) ของลา”');
INSERT INTO "king_fahad_thai" VALUES(31,20,'31:20','พวกเจ้ามิเห็นดอกหรือว่า แท้จริงอัลลอฮฺทรงอํานวยความสะดวกให้แก่พวกเจ้าสิ่งที่มีอยู่ในชั้นฟ้าทั้งหลายและสิ่งที่มีอยู่ในแผ่นดิน และพระองค์ได้ทรงประทานความ โปรดปรานมากมายของพระองค์อย่างครบครันแก่พวกเจ้า ทั้งที่เปิดเผยและที่ซ่อนเร้นและในหมู่มนุษย์มีผู้โต้เถียงในเรื่องของอัลลอฮฺโดยปราศจากความรู้และปราศจากแนวทางที่ถูกต้องและปราศจากคัมภีร์ที่ให้ความสว่าง (แก่พวกเขา)');
INSERT INTO "king_fahad_thai" VALUES(31,21,'31:21','และได้เมื่อมีการกล่าวแก่พวกเขาว่า “จงปฏิบัติตามสิ่งที่อัลลอฮฺทรงประทานลงมา” พวกเขากล่าวว่า “แต่เราจะปฏิบัติตามสิ่งที่เราพบบรรพบุรุษของเราปฏิบัติในเรื่องนั้น” อะไรกัน ถึงแม้ว่าชัยฏอนจะเรียกร้องพวกเขาสู่การลงโทษที่มีไฟลุกโชนอยู่กระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(31,22,'31:22','และผู้ใดยอมนอบน้อมใบหน้าของเขายังอัลลอฮฺ โดยที่เขาเป็นผู้กระทําดี แน่นอนเขาได้ยึดห่วงอันมั่นคงไว้แล้ว และบั้นปลายของกิจการทั้งหลายย่อมกลับไปหาอัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(31,23,'31:23','และผู้ใดปฏิเสธศรัทธา ก็อย่าให้การปฏิเสธศรัทธาของเขาทําให้เจ้าเศร้าโศกเสียใจ และยังเรานั้นคือทางกลับของพวกเขา ดังนั้น เราจะบอกแก่พวกเขาในสิ่งที่พวกเขาได้กระทําไว้ แท้จริง อัลลอฮฺนั้นเป็นผู้ทรงรอบรู้สิ่งที่อยู่ในทรวงอก');
INSERT INTO "king_fahad_thai" VALUES(31,24,'31:24','เราจะให้เวลาพวกเขาสนุกสนานกันเพียงเล็กน้อย แล้วเราจะไล่ต้อนพวกเขาสู่การลงโทษที่รุนแรง');
INSERT INTO "king_fahad_thai" VALUES(31,25,'31:25','และถ้าเจ้าถามพวกเขา “ใครเป็นผู้สร้างชั้นฟ้าทั้งหลายและแผ่นดิน” แน่นอนพวกเขาจะกล่าวว่า “อัลลอฮฺ” จงกล่าวเถิด (มุฮัมมัด) “บรรดาการสรรเสริญเป็นของอัลลอฮฺ” แต่ทว่าส่วนมากของพวกเขาไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(31,26,'31:26','สิ่งที่อยู่ในชั้นฟ้าทั้งหลายและแผ่นดินเป็นสิทธิของอัลลอฮฺ แท้จริงอัลลอฮฺนั้นคือ ผู้ทรงพอเพียงจากสิ่งทั้งหลาย ผู้ทรงได้รับการสรรเสริญ');
INSERT INTO "king_fahad_thai" VALUES(31,27,'31:27','และหากว่าต้นไม้ทั้งหมดที่มีอยู่ในแผ่นดินเป็นปากกาหลาย ๆ ด้าม และมหาสมุทร (เป็นน้ําหมึก) มีสํารองไว้อีกเจ็ดมหาสมุทรพจนารถของอัลลอฮฺก็จะยังไม่หมดสิ้นไป แท้จริงอัลลอฮฺเป็นผู้ทรงอํานาจ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(31,28,'31:28','การบังเกิดของพวกเจ้าและการฟื้นคืนชีพของพวกเจ้า มิใช่อื่นใดนอกจากเสมือนชีวิตเดียว แท้จริง อัลลอฮฺเป็นผู้ทรงได้ยินผู้ทรงเห็น');
INSERT INTO "king_fahad_thai" VALUES(31,29,'31:29','เจ้ามิเห็นดอกหรือว่า แท้จริงอัลลอฮฺทรงให้กลางคืนคาบเกี่ยวเข้าไปในเวลากลางวัน และทรงให้กลางวันคาบเกี่ยวเข้าไปในเวลากลางคืน และทรงสร้างให้ดวงอาทิตย์และดวงจันทร์ (เป็นประโยชน์แก่มนุษย์) ทุกสิ่งโคจรไปตามวาระที่กําหนดไว้ และแท้จริงอัลลอฮฺนั้นทรงรู้ดียิ่งในสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(31,30,'31:30','นั่นเพราะว่าอัลลอฮฺนั้น พระองค์คือผู้ทรงสัจจะ และเพราะว่าสิ่งที่พวกเขาวิงวอนขออื่นจากพระองค์นั้นเป็นเท็จ');
INSERT INTO "king_fahad_thai" VALUES(31,31,'31:31','เจ้ามิเห็นดอกหรือว่า เรือนั้นแล่นไปตามท้องทะเลเนื่องด้วยความโปรดปรานของอัลลอฮฺ เพื่อพระองค์จะให้พวกเจ้าได้เห็นสัญญาณต่าง ๆ ของพระองค์ แท้จริงในการนั้นย่อมเป็นสัญญาณแก่ผู้อดทนผู้ขอบคุณทุกคน');
INSERT INTO "king_fahad_thai" VALUES(31,32,'31:32','และเมื่อลูกคลื่นซัดมาท่วมมิดตัวพวกเขาคล้ายฝาที่ครอบคลุม พวกเขาก็วิงวอนขอต่ออัลลอฮฺด้วยความบริสุทธิ์ใจต่อพระองค์ ครั้นเมื่อพระองค์ได้ช่วยให้พวกเขาได้ขึ้นบกในหมู่พวกเขามีผู้อยู่ในสายกลาง และไม่มีผู้ปฏิเสธสัญญาณต่าง ๆ ของเรา นอกจากทุกผู้ทรยศผู้เนรคุณ');
INSERT INTO "king_fahad_thai" VALUES(31,33,'31:33','โอ้มนุษย์เอ๋ย พวกเจ้าจงยําเกรงพระเจ้าของพวกเจ้าเถิด และจงกลัววันหนึ่งที่พ่อไม่อาจจะช่วยลูกของเขาได้ และลูกก็ไม่อาจจะช่วยพ่อของเขาได้แต่อย่างใด แท้จริงสัญญาของอัลลอฮฺนั้นเป็นความจริง ดังนั้นอย่าให้การมีชีวิตอยู่ในโลกนี้ล่อลวงพวกเจ้า และอย่าให้หัวหน้าพวกล่อลวง (ชัยฏอน) มาหลอกลวงพวกเจ้าเกี่ยวกับอัลลอฮฺเป็นอันขาด');
INSERT INTO "king_fahad_thai" VALUES(31,34,'31:34','แท้จริงอัลลอฮฺนั้น ความรู้แห่งวันอวสานมีอยู่ ณ ที่พระองค์ และพระองค์ทรงประทานฝนลงมาและพระองค์ทรงรอบรู้สิ่งที่อยู่ในมดลูก และไม่มีชีวิตใดรู้ว่า ณ แผ่นดินใดมันจะตาย');
INSERT INTO "king_fahad_thai" VALUES(32,1,'32:1','อะลิฟ ลาม มีม');
INSERT INTO "king_fahad_thai" VALUES(32,2,'32:2','การประทานลงมาของคัมภีร์นี้ไม่มีข้อสงสัยใด ๆ ในนั้น จากพระเจ้าแห่งสากลโลก');
INSERT INTO "king_fahad_thai" VALUES(32,3,'32:3','หรือพวกเขากล่าวว่า “เขา (มุฮัมมัด) ได้ปั้นแต่งคัมภีร์นี้ขึ้นมา” แต่ว่าคัมภีร์นี้ คือ สัจธรรมจากพระเจ้าของเจ้า เพื่อเจ้าจักได้ตักเตือนกลุ่มชนหนึ่งที่มิได้มีผู้ตักเตือนคนใดมายังพวกเขาก่อนหน้าเจ้า หวังว่าพวกเขาจะได้อยู่ในแนวทางที่ถูกต้อง "');
INSERT INTO "king_fahad_thai" VALUES(32,4,'32:4','อัลลอฮฺคือผู้ทรงสร้างชั้นฟ้าทั้งหลาย และแผ่นดิน และสิ่งที่อยู่ในระหว่างทั้งสองในเวลา 6 วัน แล้วพระองค์ทรงสถิตอยู่บนบัลลังก์ สําหรับพวกเจ้านั้นไม่มีผู้คุ้มครองและผู้ช่วยเหลืออื่นจากพระองค์ แล้วพวกเจ้ามิได้ใคร่ครวญบ้างดอกหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(32,5,'32:5','พระองค์ทรงบริหารกิจการจากชั้นฟ้าสู่แผ่นดิน แล้วมันจะขึ้นไปสู่พระองค์ในวันหนึ่งซึ่งกําหนดของมันเท่ากับหนึ่งพันปีตามที่พวกเจ้านับ');
INSERT INTO "king_fahad_thai" VALUES(32,6,'32:6','นั่นคือพระผู้ทรงรอบรู้สิ่งเร้นลับ และเปิดเผย เป็นผู้ทรงอํานาจ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(32,7,'32:7','ผู้ทรงทําให้ทุกสิ่งที่พระองค์ทรงสร้างมันให้ดีงาม และพระองค์ทรงเริ่มการสร้างมนุษย์จากดิน');
INSERT INTO "king_fahad_thai" VALUES(32,8,'32:8','แล้วทรงให้การสืบตระกูล ของมนุษย์มาจากน้ํา (อสุจิ) อันไร้ค่า');
INSERT INTO "king_fahad_thai" VALUES(32,9,'32:9','แล้วทรงทําให้เขามีสัดส่วนที่สมบูรณ์ และทรงเป่ารูหฺ (วิญญาณ) ของพระองค์เข้าไปในเขาและทรงให้พวกเจ้าได้ยินและได้เห็นและให้มีจิตใจ (สติปัญญา) ส่วนน้อยเท่านั้นที่พวกเจ้าขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(32,10,'32:10','และพวกเขากล่าวกันว่า “เมื่อเราได้สลายตัวลงสู่ใต้ผืนแผ่นดินไปแล้ว เราจะเกิดขึ้นมาใหม่กระนั้นหรือ” แต่พวกเขาปฏิเสธต่อการพบพระเจ้าของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(32,11,'32:11','จงกล่าวเถิดมุฮัมมัด “มะลักผู้ปลิดชีวิต ผู้ได้รับมอบหมายเกี่ยวกับพวกท่าน จะปลิดชีวิตของพวกท่าน แล้วพวกท่านจะถูกนํากลับไปยังพระเจ้าของพวกท่าน "');
INSERT INTO "king_fahad_thai" VALUES(32,12,'32:12','และถ้าเจ้าได้เห็น เมื่อผู้กระทําผิดทั้งหลายก้มศีรษะของพวกเขาลง ณ ที่พระเจ้าของพวกเขา (พลางกล่าวว่า) “ข้าแต่พระเจ้าของเรา เราได้เห็นแล้ว เราได้ยินแล้ว ขอได้ทรงโปรดส่งเรากลับไป (ยังโลกดุนยา) เพื่อเราจะได้กระทําความดี แท้จริงเราเป็นผู้มีความเชื่อมั่นแล้ว (ณบัดนี้)”');
INSERT INTO "king_fahad_thai" VALUES(32,13,'32:13','และถ้าเราประสงค์ แน่นอนเราจะทําให้ทุกชีวิตสู่แนวทางที่ถูกต้องของมัน แต่ว่าคําสัญญาของข้าจะต้องสมจริง แน่นอนข้าจะให้นรกเต็มไปด้วยญินและมนุษย์รวมทั้งหมด');
INSERT INTO "king_fahad_thai" VALUES(32,14,'32:14','ดังนั้น พวกเจ้า (ชาวนรก) จงลิ้มรสเถิด เนื่องด้วยพวกเจ้าได้ลืมการชุมนุมกันในวันนี้ของพวกเจ้า แท้จริงเราก็ลืมพวกเจ้าด้วย และพวกเจ้าจงลิ้มรสการลงโทษอย่างตลอดกาลตามที่พวกเจ้าได้กระทําไว้เถิด');
INSERT INTO "king_fahad_thai" VALUES(32,15,'32:15','แท้จริงบรรดาผู้ศรัทธาต่ออายาตทั้งหลายของเราเท่านั้น ที่เมื่อพวกเขาถูกเตือนให้รําลึกถึงอายาต พวกเขา(ก็จะ)ก้มลงสุญูด และสดุดีสรรเสริญแด่พระจ้าของพวกเขา โดยที่พวกเขาไม่หยิ่งผยอง');
INSERT INTO "king_fahad_thai" VALUES(32,16,'32:16','สีข้างของพวกเขาเคลื่อนห่างจากที่นอน พลางวิงวอนต่อพระเจ้าของพวกเขาด้วยความกลัวและความหวัง และพวกเขาบริจาค สิ่งที่เราได้ให้เป็นเครื่องยังชีพแก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(32,17,'32:17','ดังนั้น จึงไม่มีชีวิตใดรู้สิ่งที่ถูกซ่อนไว้สําหรับพวกเขา ให้เป็นที่รื่นรมย์แก่สายตา เป็นการตอบแทนในสิ่งที่พวกเขาได้กระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(32,18,'32:18','ดังนั้น ผู้ศรัทธาจะเหมือนกับคนชั่วช้ากระนั้นหรือ? พวกเขาย่อมไม่เท่าเทียมกันแน่ ๆ');
INSERT INTO "king_fahad_thai" VALUES(32,19,'32:19','ส่วนบรรดาผู้ศรัทธาและกระทําความดีทั้งหลายนั้น สําหรับพวกเขาคือสวนสวรรค์หลากหลาย เป็นที่พํานักเตรียมไว้ตามที่พวกเขาได้กระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(32,20,'32:20','และส่วนบรรดาผู้ชั่วช้านั้น ที่พํานักของพวกเขาคือไฟนรก คราใดที่พวกเขาต้องการจะออกไปจากมัน พวกเขาจะถูกบังคับให้เข้าไปในนั้นอีก และจะมีเสียงกล่าวแก่พวกเขาว่า “จงลิ้มรสการลงโทษของไฟนรกซึ่งพวกท่านเคยปฏิเสธ ไม่เชื่อมัน”');
INSERT INTO "king_fahad_thai" VALUES(32,21,'32:21','และแน่นอน เราจะให้พวกเขาได้ลิ้มรสการลงโทษอันใกล้ (ในโลกนี้) ก่อนการลงโทษอันยิ่งใหญ่ (ในปรโลก)เพื่อว่าพวกเขาจะกลับมาสํานึกผิด');
INSERT INTO "king_fahad_thai" VALUES(32,22,'32:22','และผู้ใดเล่าจะอธรรมยิ่งไปกว่าผู้ถูกเตือนให้รําลึกถึงอายาตทั้งหลายของพระเจ้าของเขา แล้วเขาก็ผินหลังให้กับอายาตเหล่านั้น แท้จริงเราเป็นผู้จองเวรบรรดาผู้กระทําผิด');
INSERT INTO "king_fahad_thai" VALUES(32,23,'32:23','และโดยแน่นอน เราได้ให้คัมภีร์แก่มูซา ดังนั้น เจ้า(มุฮัมมัด) อย่าอยู่ในการสงสัยต่อการพบมัน และเราได้ทําให้มัน (คัมภีร์อัตเรารอฮฺ) เป็นแนวทางที่ถูกต้องแก่วงศ์วานของอิสรออีล');
INSERT INTO "king_fahad_thai" VALUES(32,24,'32:24','และเราได้จัดให้มีหัวหน้าจากพวกเขา เพื่อจะได้ชี้แนะแนวทางที่ถูกต้องตามคําบัญชาของเรา ในเมื่อพวกเขามีความอดทนและพวกเขาเชื่อมั่นต่ออายาตทั้งหลายของเรา');
INSERT INTO "king_fahad_thai" VALUES(32,25,'32:25','แท้จริงพระเจ้าของเจ้า พระองค์จะทรงตัดสินระหว่างพวกเขาในวันกิยามะฮฺ ในสิ่งที่พวกเขาขัดแย้งกันในเรื่องนั้น');
INSERT INTO "king_fahad_thai" VALUES(32,26,'32:26','ยังมิเป็นที่ประจักษ์ชัดแก่พวกเขาดอกหรือว่า กี่มากน้อยแล้วที่เราได้ทําลายประชาชาติก่อนหน้าของเขาไปหลายชั่วศตวรรษ โดยที่พวกเขา (กุฟฟารมักกะฮฺ) ได้ไปพบเห็นมาในที่พํานักอาศัยของพวกเขา แท้จริง ในการนี้ย่อมเป็นสัญญาณอย่างมากหลาย แล้วพวกเขายังไม่เชื่อฟัง (ใคร่ครวญ) อีกหรือ');
INSERT INTO "king_fahad_thai" VALUES(32,27,'32:27','พวกเขาไม่เห็นดอกหรือว่า เราได้ให้น้ําไหลลงสู่แผ่นดินที่แห้งแล้ง แล้วด้วยมัน (น้ํา) เราได้ให้พืชผลงอกเงยออกมา เพื่อปศุสัตว์ของพวกเขาและตัวของพวกเขาเองได้กินจากมัน');
INSERT INTO "king_fahad_thai" VALUES(32,28,'32:28','และพวกเขากล่าวว่า “เมื่อใดเล่าชัยชนะนี้ (จะเกิดขึ้นแก่พวกท่าน) หากพวกท่านเป็นผู้สัตย์จริง');
INSERT INTO "king_fahad_thai" VALUES(32,29,'32:29','จงกล่าวเถิดมุฮัมมัด “วันแห่งชัยชนะนั้น การศรัทธาของบรรดาผู้ปฏิเสธศรัทธาจะไม่อํานวยประโยชน์แก่พวกเขาเลย และพวกเขาก็จะไม่ถูกให้ยืดเวลาออกไป”');
INSERT INTO "king_fahad_thai" VALUES(32,30,'32:30','ดังนั้น จงผินหลังห่างออกจากพวกเขาเสียเถิด และจงคอยดู แท้จริงพวกเขาก็จะเป็นผู้คอยดู');
INSERT INTO "king_fahad_thai" VALUES(33,1,'33:1','โอ้ นะบี! จงยําเกรงอัลลอฮฺ และอย่าเชื่อฟังพวกปฏิเสธศรัทธาและพวกมุนาฟิกีน แท้จริงอัลลอฮฺนั้นเป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(33,2,'33:2','และจงปฏิบัติตามสิ่งที่ถูกวะฮี แก่เจ้า จากพระเจ้าของเจ้า แท้จริงอัลลอฮฺนั้นทรงรู้ดียิ่งในสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(33,3,'33:3','และจงมอบความไว้วางใจแด่อัลลอฮฺและพอเพียงแล้วที่อัลลอฮฺเป็นผู้คุ้มครอง');
INSERT INTO "king_fahad_thai" VALUES(33,4,'33:4','อัลลอฮฺมิได้ทรงทําให้ชายใดมีสองหัวใจในทรวงอกของเขา และพระองค์มิได้ทรงทําให้ภริยาของพวกเจ้าซึ่งพวกเจ้าหย่าพวกเธอ (ด้วยการกล่าวซิฮารฺ) ว่าเป็นแม่ของพวกเจ้า และพระองค์มิได้ทรงทําให้การเรียก(ลูกบุญธรรม) ของพวกเจ้าว่าเป็นลูก (ที่แท้จริง) ของพวกเจ้า นั่นคือการกล่าวของพวกเจ้าด้วยปากของพวกเจ้า และอัลลอฮฺนั้นตรัสสัจจะ และพระองค์ทรงชี้แนะแนวทางที่ถูกต้อง');
INSERT INTO "king_fahad_thai" VALUES(33,5,'33:5','จงเรียกเขาเหล่านั้นตาม (ชื่อ) พ่อของพวกเขา มันเป็นการเที่ยงธรรมกว่า ณ ที่อัลลอฮฺหากพวกเจ้าไม่รู้จักพ่อ (จริง ๆ) ของพวกเขา ดังนั้น พวกเขาก็คือพี่น้องร่วมในศาสนาของพวกเจ้าและผู้ใกล้ชิดของพวกเจ้า และไม่เป็นที่น่าตําหนิแก่พวกเจ้าในสิ่งที่พวกเจ้าผิดพลาดในเรื่องนั้น แต่สิ่งที่จิตใจของพวกเจ้ามีความมุ่งหมายต่างหาก และอัลลอฮฺเป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(33,6,'33:6','นะบีนั้นเป็นผู้ใกล้ชิดกับบรรดาผู้ศรัทธายิ่งกว่าตัวของพวกเขาเอง และบรรดาภริยาของเขา (นะบี) คือมารดาของพวกเขาและเครือญาติร่วมสายโลหิต บางคนในหมู่พวกเขาใกล้ชิดกับอีกบางคนยิ่กว่าบรรดาผู้ศรัทธาและบรรดาผู้อพยพในบัญญติของอัลลอฮฺ เว้น แต่พวกเจ้าจะกระทําความดีแก่สหายสนิทของ พวกเจ้า นั่นได้มีบันทึกไว้แล้วในคัมภีร์');
INSERT INTO "king_fahad_thai" VALUES(33,7,'33:7','และจงรําลึกถึงขณะที่เราได้เอาคํามั่นสัญญาของพวกเขาจากบรรดานะบี และจากเจ้า และจากนูหฺ และอิบรอฮีม และมูซา และอิซา อิบนฺมัรยัม และเราได้เอาคํามั่นสัญญาอย่างหนักแน่นจากพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(33,8,'33:8','เพื่อพระองค์จะทรงสอบถามบรรดาผู้สัตย์จริง เกี่ยวกับความสัตย์จริงของพวกเขา และพระองค์ทรงเตรียมการลงโทษอันเจ็บปวดไว้แก่บรรดาผู้ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(33,9,'33:9','โอ้บรรดาผู้ศรัทธาเอ๋ย! จงรําลึกถึงความโปรดปรานของอัลลอฮฺที่มีต่อพวกเจ้าขณะที่กองทัพข้าศึกเข้ามารุกรานพวกเจ้า แล้วเราได้ส่งลมพายะพัดใส่พวกเขา และกําลังทหารที่พวกเจ้ามองไม่เห็น และอัลลอฮฺทรงเห็นสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(33,10,'33:10','เมื่อพวกเขายกทัพมายังพวกเจ้า ทั้งจากทางข้างบนของพวกเจ้า และจากทางข้างล่างของพวกเจ้า และเมื่อนัยตาได้เหลือกลานและหัวใจได้มาจุกอยู่ที่ลําคอ และพวกเจ้านึกคิดกันต่าง ๆ นานา เกี่ยวกับอัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(33,11,'33:11','ณ ที่นั้นขณะนั้น บรรดาผู้ศรัทธาได้ถูกทดลอง และพวกเขาถูกทําให้เคลื่อนไหวสั่นสะเทือนอย่างรุนแรง');
INSERT INTO "king_fahad_thai" VALUES(33,12,'33:12','และจงรําลึกถึง เมื่อพวกมุนาฟิกีนและบรรดาผู้ที่หัวใจของพวกเขามีโรค กล่าวว่า “อัลลอฮฺและร่อซูลของพระองค์มิได้สัญญาแก่เราอย่างใด นอกจากการหลอกลวงเท่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(33,13,'33:13','และจงรําลึกถึง เมื่อกลุ่มหนึ่งจากพวกเขา (มุนาฟิกีน) กล่าวว่า “โอ้ชาวยัษริบเอ๋ย! ไม่มีที่ตั้งมั่นสําหรับพวกท่านแล้ว (เพื่อต่อสู้กับข้าศึก) จงกลับไปเสียเถิด” และกลุ่มหนึ่งจากพวกเขาจะขออนุญาตกับท่านนะบีว่า“บ้านของพวกเราไม่มีอะไรปกปิด ไม่มั่นคง” และมันมิได้เป็นเช่นนั้น พวกเขามิได้ประสงค์สิ่งใด นอกจากการหนี (จากการทําสงคราม)');
INSERT INTO "king_fahad_thai" VALUES(33,14,'33:14','และถ้ามีการรุกรานมายังพวกเขาจากรอบนอกเมืองของมัน แล้วพวกเขาได้ถูกปลุกปั่นให้ก่อความไม่สงบ แน่นอน พวกเขาจะกระทําทันที และพวกเขาจะไม่ลังเลแม้แต่น้อย');
INSERT INTO "king_fahad_thai" VALUES(33,15,'33:15','และโดยแน่นอนพวกเขาได้ให้สัญญาต่ออัลลอฮฺมาก่อนแล้วว่า พวกเขาจะไม่หันหลังกลับ และสัญญาของอัลลอฮฺนั้นจะถูกสอบถาม');
INSERT INTO "king_fahad_thai" VALUES(33,16,'33:16','จงกล่าวเถิดมุฮัมมัดว่า “การหนีนั้นจะไม่อํานวยประโยชน์อันใดแก่พวกท่าน หากพวกท่าน (ต้องการ) จะหนีจากความตาย หรือการรบราฆ่าฟันกัน และเมื่อนั้นพวกท่านจะไม่ได้ รื่นเริงกัน นอกจาก (ใน) เวลาอันเล็กน้อย”');
INSERT INTO "king_fahad_thai" VALUES(33,17,'33:17','จงกล่าวเถิดมุฮัมมัด “ใครเล่าจะปกป้องพวกท่านให้พ้นจากอัลลอฮฺไปได้ หากพระองค์ทรงปรารถนาให้ความเสียหายแก่พวกท่าน หรือหากพระองค์ทรงปรารถนาให้ความเมตตาแก่พวกท่าน” และพวกเขาจะไม่พบใครอื่นจากอัลลอฮฺเป็นผู้คุ้มครองและเป็นผู้ช่วยเหลือแก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(33,18,'33:18','แน่นอน อัลลอฮฺทรงรู้บรรดาผู้ขัดขวางในหมู่พวกเจ้า และผู้ที่กล่าวแก่พี่น้องของพวกเขาว่า “มาหาพวกเราทางนี้” และพวกเขาจะไม่มาร่วมกันต่อต้านข้าศึก นอกจากจํานวนเล็กน้อย');
INSERT INTO "king_fahad_thai" VALUES(33,19,'33:19','เป็นคนตระหนี่กับพวกเจ้า ครั้นเมื่อความกลัว (อันตราย) ปรากฏขึ้น เจ้าจะเห็นพวกเขาจ้องมองไปยังเจ้าสายตาของพวกเขาเกลือกกลิ้งเสมือนผู้มีอาการร่อแร่ใกล้จะตาย ต่อเมื่อความกลัว (อันตราย) ได้ผ่านพ้นไปแล้ว พวกเขาก็พูดจาถากถางพวกเจ้าด้วยสํานวนที่เผ็ดร้อน เป็นคนตระหนี่ในเรื่องทรัพย์สิน (ของที่ยึดมาได้จากการทําสงคราม) ชนเหล่านั้นพวกเขามิได้ศรัทธา อัลลอฮฺจึงทรงให้การงานของพวกเขาไม่บังเกิดผล และนั่นเป็นเรื่องง่ายดายแก่อัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(33,20,'33:20','พวกเขาคิดว่าพวกพรรคต่าง ๆ เหล่านั้นยังมิได้ถอยกลับออกไป และหากว่าพวกพรรคต่าง ๆ เหล่านั้นหวนกลับมาอีก พวกมุนาฟิกีนก็คาดหวังกันว่า หากพวกเขาได้ไปอยู่ร่วมกับอาหรับชนบทเพื่อคอยสืบเสาะหาข่าวของพวกเจ้า และหากว่าพวกเขาได้อยู่ร่วมกับพวกเจ้า พวกเขาก็จะไม่ต่อสู้เว้นแต่เพียงเล็กน้อยเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(33,21,'33:21','โดยแน่นอน ในร่อซูลของอัลลอฮฺมีแบบฉบับอันดีงามสําหรับพวกเจ้าแล้ว สําหรับผู้ที่หวัง (จะพบ) อัลลอฮฺและวันปรโลกและรําลึกถึงอัลลอฮฺอย่างมาก');
INSERT INTO "king_fahad_thai" VALUES(33,22,'33:22','และเมื่อบรรดาผู้ศรัทธาได้เห็นพรรคต่าง ๆ เหล่านั้น พวกเขา (มุอฺมิน) ได้กล่าวว่า “นี่คือสิ่งที่อัลลอฮฺและร่อซูลของพระองค์ได้สัญญาไว้แก่เรา และอัลลอฮฺและร่อซูลของพระองค์ตรัสไว้จริงแล้ว” และมันมิได้เพิ่มสิ่งใดให้แก่พวกเขา นอกจากการศรัทธาและการนอบน้อม');
INSERT INTO "king_fahad_thai" VALUES(33,23,'33:23','ในหมู่ผู้ศรัทธามีบุรุษผู้มีสัจจะต่อสิ่งที่พวกเขาได้สัญญาต่ออัลลอฮฺเอาไว้ ดังนั้นในหมู่พวกเขามีผู้ปฏิบัติตามสัญญาของเขา และในหมู่พวกเขามีผู้ที่ยังคอย (การตายชะฮีด) และพวกเขามิได้เปลี่ยนแปลงแต่อย่างใด');
INSERT INTO "king_fahad_thai" VALUES(33,24,'33:24','เพื่ออัลลอฮฺจะได้ทรงตอบแทนบรรดาผู้มีสัจจะในความสัจจริงของพวกเขา และจะทรงลงโทษพวกมุนาฟิกีนหากพระองค์ทรงประสงค์ หรือจะทรงอภัยโทษให้แก่พวกเขาแท้จริงอัลลอฮฺนั้นเป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(33,25,'33:25','และอัลลอฮฺทรงให้พวกปฏิเสธศรัทธาถอยทัพกลับด้วยความเคียดแค้นของพวกเขาโดยที่พวกเขามิได้ประสบความดีแต่อย่างใด และอัลลอฮฺทรงพอเพียงแล้วแก่บรรดาผู้ศรัทธาในการสู้รบ และอัลลอฮฺเป็นผู้ทรงพลัง ผู้ทรงอํานาจอย่างเหลือหลาย');
INSERT INTO "king_fahad_thai" VALUES(33,26,'33:26','และพระองค์ทรงให้พวกอะฮฺลุลกิตาบ (ยิวก๊กบะนีกุร็อยเซาะฮฺ) ที่ได้ช่วยเหลือพวกเขา (พวกมุชริกีน) ลงมาจากป้อมที่มั่นของพวกเขา และทรงบรรจุความหวาดกลัวไว้ในจิตใจของพวกเขา ส่วนหนึ่งพวกเจ้าประหารชีวิต (พวกเขา) และอีกส่วนหนึ่งพวกเจ้าจับเป็นเชลย');
INSERT INTO "king_fahad_thai" VALUES(33,27,'33:27','และพระองค์ได้ทรงให้พวกเจ้าได้รับมรดกปกครองแผ่นดินของพวกเขา และที่อยู่อาศัยของพวกเขา และทรัพย์สินของพวกเขาและแผ่นดินที่พวกเจ้ายังมิเคยเหยียบย่างเข้าไป และอัลลอฮฺเป็นผู้ทรงอานุภาพเหนือทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(33,28,'33:28','โอ้ นะบีเอ๋ย! จงกล่าวแก่บรรดาภริยาของเจ้าเถิดว่า หากพวกเธอปรารถนาการมีชีวิตในโลกนี้และความเพริศแพร้วของมัน ก็จงมาเถิด ฉันจะจัดหา (การเลี้ยงชีพ) ให้แก่พวกเธอ และจะปล่อยพวกเธอให้ออกไปอย่างดีงาม');
INSERT INTO "king_fahad_thai" VALUES(33,29,'33:29','และหากพวกเธอปรารถนาอัลลอฮฺและ ร่อซูลของพระองค์และโลกอาคิเราะฮฺแล้ว แท้จริงอัลลอฮฺได้เตรียมผลบุญอันใหญ่หลวงแก่เหล่าสตรีผู้กระทําความดีในหมู่พวกเธอ');
INSERT INTO "king_fahad_thai" VALUES(33,30,'33:30','โอ้ บรรดาภริยาของนะบีเอ๋ย! ผู้ใดในหมู่พวกเธอนําความชั่วอย่างชัดแจ้งมา การลงโทษจะถูกเพิ่มให้แก่นางเป็นสองเท่า ในการนั้นเป็นการง่ายดายแก่อัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(33,31,'33:31','และผู้ใดในหมู่พวกเธอภักดีต่ออัลลอฮฺและร่อซูลของพระองค์ และกระทําความดีเราจะให้รางวัลของนางแก่นางสองครั้ง และเราได้เตรียมปัจจัยยังชีพอันดีงามแก่นาง');
INSERT INTO "king_fahad_thai" VALUES(33,32,'33:32','โอ้ บรรดาภริยาของนะบีเอ๋ย! พวกเธอไม่เหมือนกับสตรีใด ๆ ในเหล่าสตรีอื่นหากพวกเธอยําเกรง (อัลลอฮฺ) ก็ไม่ควรพูดจาเพราะพริ้งนัก เพราะจะทําให้ผู้ที่ในหัวใจของเขามีโรคเกิดความโลภ แต่จงพูดด้วยถ้อยคําที่พอเหมาะพอควร');
INSERT INTO "king_fahad_thai" VALUES(33,33,'33:33','และจงอยู่ในบ้านเรือนของพวกเธอและอย่าได้โออวดความงาม (ของพวกเธอ) เช่น การอวดความงาม (ของพวกสตรี) แห่งสมัยงมงายในยุคก่อน และจงดํารงการละหมาดและจ่ายซะกาต และจงภักดีต่ออัลลอฮฺและร่อซูลของพระองค์ อัลลอฮฺเพียงแต่ต้องการที่จะขจัดความโสโครกออกไปจากพวกเจ้า โอ้สมาชิกของวงศ์ตระกูล (นะบี) เอ๋ย และทรง (ประสงค์) ที่จะขัดเกลาพวกเจ้าให้สะอาดบริสุทธิ์');
INSERT INTO "king_fahad_thai" VALUES(33,34,'33:34','และจงอ่านสิ่งที่ได้ถูกอ่านในบ้านเรือนของพวกเธอ (เช่น) จากอายาตทั้งหลายของอัลลอฮฺและฮิกมะฮฺ แท้จริงอัลลอฮฺนั้นทรงรอบรู้อย่างละเอียด ทรงรอบรู้ทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(33,35,'33:35','แท้จริง บรรดาผู้นอบน้อมชายและหญิง บรรดาผู้ศรัทธาชายและหญิง บรรดาผู้ภักดีชายและหญิง บรรดาผู้สัตย์จริงชายและหญิง บรรดาผู้อดทนชายและหญิง บรรดาผู้ถ่อมตัวชายและหญิง บรรดาผู้บริจาคทานชายและหญิงบรรดาผู้ถือศีลอดชายและหญิง บรรดาผู้รักษาอวัยวะเพศของพวกเขาที่เป็นชายและหญิง บรรดาผู้รําลึกถึงอัลลอฮฺอย่างมากที่เป็นชายและหญิงนั้น');
INSERT INTO "king_fahad_thai" VALUES(33,36,'33:36','ไม่บังควรแก่ผู้ศรัทธาชายและผู้ศรัทธาหญิง เมื่ออัลลอฮฺและร่อซูลของพระองค์ได้กําหนดกิจการใดแล้ว สําหรับพวกเขาไม่มีทางเลือกในเรื่องของพวกเขา และผู้ใดไม่เชื่อฟังอัลลอฮฺและร่อซูลของพระองค์แล้ว แน่นอนเขาได้หลงผิดอย่างชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(33,37,'33:37','และจงรําลึกถึงขณะที่เจ้าพูดกับผู้ที่อัลลอฮฺทรงโปรดปรานแก่เขา และเจ้าได้ให้ความสงเคราะห์แก่เขา และจงดูแลรักษาภริยาของเจ้าไว้ให้อยู่กับเจ้า และจงยําเกรงอัลลอฮฺ และเจ้าได้ซ่อนไว้ในจิตใจของเจ้าเรื่องที่อัลลอฮฺจะทรงเปิดเผยมัน และเจ้ากลัวเกรงมนุษย์ แต่อัลลอฮฺทรงสมควรยิ่งกว่าที่เจ้าจะกลัวเกรงพระองค์ ครั้นเมื่อเซด ได้หย่ากับนาง แล้ว เราได้ให้เจ้าแต่งงานกับนางเพื่อที่จะไม่เป็นที่ลําบากใจแก่บรรดาผู้ศรัทธาชายใน เรื่องการ (การสมรสกับ) ภริยาของบุตรบุญธรรมของพวกเขา เมื่อพวกเขาหย่ากับพวกนางแล้วและพระบัญชาของอัลลอฮฺนั้นจะต้อง บรรลุผลเสมอ');
INSERT INTO "king_fahad_thai" VALUES(33,38,'33:38','ไม่เป็นการลําบากใจอันใดแก่ท่านนะบี ในสิ่งที่อัลลอฮฺทรงกําหนดให้แก่เขา (นี่คือ) แนวทางของอัลลอฮฺ (ที่ได้มีขึ้นแล้ว) ต่อบรรดาผู้ได้ล่วงลับในสมัยก่อน และพระบัญชาของอัลลอฮฺนั้นได้กําหนดไว้แล้ว');
INSERT INTO "king_fahad_thai" VALUES(33,39,'33:39','บรรดา (นะบี) ผู้ที่ได้เผยแผ่สาสน์ทั้งหลายของอัลลอฮฺ และพวกเขากลัวเกรงพระองค์ และไม่กลัวเกรงผู้ใดนอกจากอัลลอฮฺ และพอเพียงแล้วที่อัลลอฮฺเป็นผู้ทรงชําระสอบสวน');
INSERT INTO "king_fahad_thai" VALUES(33,40,'33:40','มุฮัมมัดมิได้เป็นบิดาผู้ใดในหมู่บุรุษของพวกเจ้า แต่เป็นร่อซูลของอัลลอฮฺและคนสุดท้ายแห่งบรรดานะบี');
INSERT INTO "king_fahad_thai" VALUES(33,41,'33:41','โอ้บรรดาผู้ศรัทธาเอ๋ย! จงรําลึกถึงอัลลอฮฺโดยการรําลึกอย่างมากมาย');
INSERT INTO "king_fahad_thai" VALUES(33,42,'33:42','และแซ่ซ้องสะดุดีพระองค์ทั้งยามเช้าและยามเย็น');
INSERT INTO "king_fahad_thai" VALUES(33,43,'33:43','พระองค์คือผู้ทรงประทานความเมตตาให้แก่พวกเจ้า และมะลาอิกะฮฺของพระองค์ด้วย เพื่อพระองค์จะทรงนําพวกเจ้าออกจากความมืดทึบทั้งหลายสู่ความสว่าง และพระองค์ทรงเมตตาต่อบรรดาผู้ศรัทธาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(33,44,'33:44','การกล่าวคําทักทายของพวกเขาในวันที่พวกเขาพบพระองค์คือ ศานติ (สลาม) และพระองค์ทรงเตรียมรางวัลอันมีเกียรติไว้ให้แก่พวกเขาแล้ว');
INSERT INTO "king_fahad_thai" VALUES(33,45,'33:45','โอ้ นะบีเอ๋ย! แท้จริง เราได้ส่งเจ้ามาเพื่อให้เป็นพยาน และผู้แจ้งข่าวดี และผู้ตักเตือน');
INSERT INTO "king_fahad_thai" VALUES(33,46,'33:46','และเป็นผู้เรียกร้องเชิญชวนไปสู่อัลลอฮฺ ตามพระบัญชาของพระองค์ และเป็นดวงประทีปอันแจ่มจรัส');
INSERT INTO "king_fahad_thai" VALUES(33,47,'33:47','และจงแจ้งข่าวดีแก่บรรดาผู้ศรัทธาว่า แท้จริง สําหรับพวกเขาจะได้รับความโปรดปรานอันใหญ่หลวงจากอัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(33,48,'33:48','และอย่าเชื่อฟังพวกปฏิเสธศรัทธาและพวกมุนาฟิกีน และอย่าสนใจการระรานของพวกเขา และจงมอบความไว้วางใจแด่อัลลอฮฺ และพอเพียงแล้วที่อัลลอฮฺทรงเป็นผู้รับการมอบหมาย');
INSERT INTO "king_fahad_thai" VALUES(33,49,'33:49','โอ้บรรดาผู้ศรัทธาเอ๋ย! เมื่อพวกเจ้าได้สมรสกับบรรดาหญิงผู้ศรัทธา แล้วพวกเจ้าได้หย่าพวกเธอก่อนที่พวกเจ้าจะแตะต้องตัวพวกเธอ (คือร่วมหลับนอนกับพวกเธอ) ดังนั้น สําหรับพวกเจ้าไม่มีสิทธิ์ (ที่จะให้พวกเธออยู่) ในอิดดะฮฺ โดยที่พวกเจ้าจะนับเวลาการอยู่ในอิดดะฮฺ ฉะนั้น พวกเจ้าจงให้ผลประโยชน์แก่พวกเธอบ้าง แล้วปล่อยให้พวกเธอจากไปโดยดีงาม');
INSERT INTO "king_fahad_thai" VALUES(33,50,'33:50','โอ้ นะบีเอ๋ย! เราได้อนุมัติแก่เจ้าในบรรดาภริยาของเจ้า ซึ่งเจ้าได้มอบมะฮัรแก่พวกเธอ และสิ่งที่มือขวาของเจ้าครอบครองจากพวกที่อัลลอฮฺทรงมอบหมายให้แก่เจ้า และบุตรสาวของอา ลุง ของเจ้า และบุตรสาวของอา ป้า ของเจ้า และบุตรสาวของน้า ลุง ของเจ้า และบุตรสาวของน้า ป้า ของเจ้า ซึ่งพวกเธอได้อพยพร่วมไปกับเจ้า และหญิงผู้ศรัทธาเมื่อนางเสนอตัวของนางแก่ท่านนะบีหากท่านนะบีปรารถนาจะสมรสกับนาง ทั้งนี้สําหรับเจ้าโดยเฉพาะมิใช่สําหรับบรรดา ผู้ศรัทธา แน่นอนเรารู้ดีถึงสิ่งที่เราได้กําหนด แก่พวกเขาในเรื่องบรรดาภริยาของพวกเขา และสิ่งที่มือขวาของพวกเขาครอบครอง เพื่อ ที่จะไม่เป็นที่ลําบากใจแก่เจ้าและอัลลอฮฺเป็น ผู้ทรงอภัย ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(33,51,'33:51','เจ้ามีสิทธิ์ที่จะหย่าผู้ที่เจ้าปรารถนาในหมู่พวกเธอ และเจ้าจะรับผู้ที่เจ้าประสงค์มาอยู่ร่วมกับเจ้า และผู้ใดที่เจ้าต้องการ (ให้มาอยู่ร่วม) จากผู้ที่เจ้าเคยแยกกันอยู่ ก็ไม่เป็นที่ตําหนิแก่เจ้า นั่นเป็นการเหมาะสมกว่าที่จะทําให้นัยตาของพวกเธอรื่นรมย์ และไม่ทําให้พวกเธอเศร้าโศก และพวกเธอพอใจในสิ่งที่เจ้าได้ให้แก่พวกเธอทั้งหมด และอัลลอฮฺทรงรอบรู้สิ่งที่อยู่ในจิตใจของพวกเจ้า และอัลลอฮฺเป็นผู้ทรงรอบรู้ ผู้ทรงขันติธรรม');
INSERT INTO "king_fahad_thai" VALUES(33,52,'33:52','ไม่เป็นที่อนุมัติแก่เจ้าที่จะแต่งงานกับหญิงอื่นหลังจากนี้ และเจ้าอย่าได้เปลี่ยนพวกเธอเพื่อภริยาอื่น ๆ ถึงแม้ว่าความสวยงามของพวกเธอจะทําให้เจ้าพอใจก็ตาม นอกจากสิ่งที่มือขวาของเจ้าครอบครอง และอัลลอฮฺนั้นเป็นผู้ทรงเฝ้ามองทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(33,53,'33:53','โอ้ บรรดาผู้ศรัทธาเอ๋ย ! พวกเจ้าอย่าได้เข้าไปในบ้านทั้งหลายของนะบี เว้นแต่จะเป็นที่อนุมัติแก่พวกเจ้า เพื่อรับประทานอาหารโดยมิต้องคอยการปรุงอาหารให้สุกเสียก่อน แต่เมื่อพวกเจ้าได้รับเชิญก็จงเข้าไปครั้นเมื่อพวกเจ้ารับประทานเสร็จแล้วก็จงแยกย้ายกันออกไป และอย่าเป็นผู้ชอบวิสาสะในการสนทนา แท้จริง ในการนั้นย่อมทําความรําคาญให้แก่ท่านนะบี ซึ่งท่านกระดากอายพวกเจ้า แต่อัลลอฮฺไม่ทรงกระดากอายต่อความจริง และเมื่อพวกเจ้าขอสิ่งใดจากพวกนาง ก็จงขอพวกนางจากหลังม่าน เช่นนั้นแหละเป็นการบริสุทธิ์อย่างยิ่งแก่จิตใจของพวกเจ้าและจิตใจของพวกนาง และไม่เป็นการบังควรแก่พวกเจ้าที่จะก่อความรําคาญให้แก่ร่อซูลของอัลลอฮฺ และพวกเจ้าจะต้องไม่แต่งงานกับบรรดาภริยาของท่าน หลังจากท่าน (ได้สิ้นชีพไปแล้ว) เป็นอันขาด แท้จริงในการนั้น ณ ที่อัลลอฮฺเป็นเรื่องที่ใหญ่หลวงนัก');
INSERT INTO "king_fahad_thai" VALUES(33,54,'33:54','หากพวกเจ้าเปิดเผยสิ่งใดหรือปิดบังมัน แท้จริงอัลลอฮฺนั้นทรงรอบรู้ทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(33,55,'33:55','ไม่เป็นที่น่าตําหนิแก่พวกนาง เกี่ยวกับบิดาของพวกนาง และลูก ๆ ของพวกนางและพี่น้องผู้ชายของพวกนาง และลูกชายของพี่น้องหญิงของพวกนาง และบรรดาผู้หญิงของพวกนาง และสิ่งที่มือขวาของพวกนางครอบครอง และพวกนางยําเกรงต่ออัลลอฮฺ แท้จริง อัลลอฮฺนั้นทรงเป็นพยานต่อทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(33,56,'33:56','แท้จริงอัลลอฮฺและมะลาอิกะฮฺของพระองค์ประสาทพรแก่นะบี โอ้ บรรดาผู้ศรัทธาเอ๋ย ! พวกเจ้าจงประสาทพรให้เขาและกล่าวทักทายเขาโดยคารวะ');
INSERT INTO "king_fahad_thai" VALUES(33,57,'33:57','แท้จริง บรรดาผู้กล่าวร้ายแก่อัลลอฮฺและร่อซูลของพระองค์ อัลลอฮฺทรงสาปแช่งพวกเขาทั้งในโลกนี้และปรโลก และทรงเตรียมการลงโทษอันอัปยศไว้สําหรับพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(33,58,'33:58','และบรรดาผู้กล่าวร้ายแก่บรรดาผู้ศรัทธาชายและบรรดาผู้ศรัทธาหญิง ในสิ่งที่พวกเขามิได้กระทํา แน่นอนพวกเขาได้แบกการกล่าวร้าย และบาปอันชัดแจ้งไว้');
INSERT INTO "king_fahad_thai" VALUES(33,59,'33:59','โอ้นะบีเอ๋ย ! จงกล่าวแก่บรรดาภริยาของเจ้า และบุตรสาวของเจ้า และบรรดาหญิงของบรรดาผู้ศรัทธา ให้พวกนางดึงเสื้อคลุมของพวกนางลงมาปิดตัวของพวกนาง นั่น เป็นการเหมาะสมกว่าที่นางจะเป็นที่รู้จัก เพื่อที่พวกนางจะไม่ถูกรบกวน และอัลลอฮฺทรงเป็นผู้อภัยผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(33,60,'33:60','แน่นอน ถ้าพวกมุนาฟิกีนและบรรดาผู้ที่ในหัวใจของพวกเขามีโรค และกลุ่มผู้ก่อกวนความสงบในนครมะดีนะฮฺไม่ระงับ (การกระทําที่เลวทรามของพวกเขา) แน่นอน เราจะให้เจ้ามีอํานาจเหนือพวกเขา แล้วพวกเขาจะไม่กลับมาพํานักเป็นเพื่อนบ้านของเจ้าในนั้นอีกเว้นแต่เพียงชั่วเวลาอันเล็กน้อยเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(33,61,'33:61','พวกเหล่านั้นถูกสาปแช่ง ไม่ว่าพวกเขาจะถูกพบ ณ แห่งหนใด ก็จะถูกจับกุมและถูกสังหาร (โดยปราศจากความเมตตา)');
INSERT INTO "king_fahad_thai" VALUES(33,62,'33:62','นั่นคือแนวทางของอัลลอฮฺแก่บรรดาผู้ที่ล่วงลับไปแล้วแต่กาลก่อน และเจ้าจะไม่พบการเปลี่ยนแปลงใดๆ ในแนวทางของอัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(33,63,'33:63','มีผู้คนถามเจ้าเกี่ยวกับยามอวสาน จงกล่าวเถิด (มุฮัมมัด) “แท้จริงความรู้ในเรื่องนั้นอยู่ ณ ที่อัลลอฮฺ” และอะไรเล่าจะทําให้เจ้ารู้ได้ บางทียามอวสานนั้นอยู่ใกล้ ๆ นี่เอง');
INSERT INTO "king_fahad_thai" VALUES(33,64,'33:64','แท้จริง อัลลอฮฺทรงสาปแช่งบรรดาผู้ปฏิเสธศรัทธา และทรงเตรียมไฟที่ลุกโชติช่วงไว้สําหรับพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(33,65,'33:65','พวกเขาจะพํานักอยู่ในนั้นตลอดกาล พวกเขาจะไม่พบผู้คุ้มครองและผู้ช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(33,66,'33:66','วันที่ใบหน้าของพวกเขาจะถูกพลิกกลับไปกลับมาในไฟนรก พวกเขาจะกล่าวว่า “โอ้ความระทมทุกข์ของเรา ! หากเราได้เชื่อฟังปฏิบัติตามอัลลอฮฺ และเราได้เชื่อฟังปฏิบัติตามร่อซูลก็จะดีหรอก !!”');
INSERT INTO "king_fahad_thai" VALUES(33,67,'33:67','และพวกเขากล่าวว่า “ข้าแต่พระเจ้าของเรา แท้จริงเราได้เชื่อฟังปฏิบัติตามบรรดาหัวหน้าของเรา และบรรดาผู้นําของเรา ดังนั้นพวกเขาได้ทําให้พวกเราหลงทาง');
INSERT INTO "king_fahad_thai" VALUES(33,68,'33:68','ข้าแต่พระเจ้าของเรา ได้โปรดลงโทษพวกเขาสองเท่าเถิด และทรงสาปแช่งพวกเขาซึ่งการสาปแช่งอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(33,69,'33:69','โอ้บรรดาผู้ศรัทธาเอ๋ย ! พวกเจ้าอย่าได้เป็นเช่นบรรดาผู้สบประมาทมูซา แล้วอัลลอฮฺก็ทรงให้เขาพ้นจากที่พวกเขากล่าวร้าย และเขาเป็นผู้ควรแก่การคารวะ ณ ที่อัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(33,70,'33:70','โอ้บรรดาผู้ศรัทธาเอ๋ย ! จงยําเกรงอัลลอฮฺ และจงกล่าวถ้อยคําที่เที่ยงธรรมเถิด');
INSERT INTO "king_fahad_thai" VALUES(33,71,'33:71','พระองค์จะทรงปรับปรุงการงานของพวกเจ้าให้ดีขึ้นสําหรับพวกเจ้า และจะทรงอภัยโทษความผิดของพวกเจ้าให้แก่พวกเจ้าและผู้ใดเชื่อฟังปฏิบัติตามอัลลอฮฺแล่ะร่อซูลของพระองค์ แน่นอนเขาได้รับความสําเร็จใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(33,72,'33:72','แท้จริงเราได้เสนอการอะมานะฮฺ แก่ชั้นฟ้าทั้งหลายและแผ่นดิน และขุนเขาทั้งหลาย แต่พวกมันปฏิเสธจะแบกรับมันและกลัวต่อมัน (คือภาระอันหนักอึ้ง) และมนุษย์ได้แบกรับมัน แท้จริงเขา (มนุษย์) เป็นผู้อธรรมงมงายยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(33,73,'33:73','เพื่ออัลลอฮฺจะได้ทรงลงโทษแก่พวกมุนาฟิกีนชายและพวกมุนาฟิกีนหญิง และบรรดาผู้ตั้งภาคีชาย (มุชริกีน) และบรรดาผู้ตั้งภาคีหญิง (มุชริก๊าต) และเพื่ออัลลอฮฺจะทรงอภัยโทษแก่บรรดาผู้ศรัทธาชาย และบรรดาผู้ศรัทธาหญิง และอัลลอฮฺเป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ.');
INSERT INTO "king_fahad_thai" VALUES(34,1,'34:1','บรรดาการสรรเสริญเป็นของอัลลอฮฺซึ่งสิ่งที่อยู่ในชั้นฟ้าทั้งหลายและสิ่งที่อยู่ในแผ่นดินเป็นกรรมสิทธิ์ของพระองค์ และบรรดาการสรรเสริญในปรโลกเป็นกรรมสิทธิ์ของพระองค์ และพระองค์เป็นผู้ทรงปรีชาญาณ ผู้ทรงรอบรู้เชี่ยวชาญ');
INSERT INTO "king_fahad_thai" VALUES(34,2,'34:2','พระองค์ทรงรอบรู้ถึงสิ่งที่เข้าไปอยู่ในแผ่นดิน และสิ่งที่ออกมาจากมัน และสิ่งที่ลงมาจากฟากฟ้า และสิ่งที่ขึ้นไปสู่ในนั้น และพระองค์เป็นผู้ทรงเมตตา ผู้ทรงอภัยเสมอ');
INSERT INTO "king_fahad_thai" VALUES(34,3,'34:3','และบรรดาผู้ปฏิเสธศรัทธากล่าวว่า “วันอวสานนั้นจะไม่มาถึงเราดอก” จงกล่าวเถิดมุฮัมมัด “หามิได้ ขอสาบานด้วยพระเจ้าของฉัน ผู้ทรงรอบรู้ในสิ่งพ้นญาณวิสัย มันจะเกิดขึ้นแก่พวกท่านอย่างแน่นอน ไม่มีแม้แต่น้ําหนักเพียงเท่าธุลีในชั้นฟ้าทั้งหลาย และในแผ่นดิน และที่เล็กยิ่งกว่านั้นและที่ใหญ่กว่านั้น จะรอดพ้นจากพระองค์ เว้นแต่จะอยู่ในบันทึกอันชัดแจ้งทั้งสิ้น');
INSERT INTO "king_fahad_thai" VALUES(34,4,'34:4','เพื่อที่พระองค์จะทรงตอบแทนแก่บรรดาผู้ศรัทธาและประกอบความดีทั้งหลายชนเหล่านั้น สําหรับพวกเขาจะได้รับการอภัยโทษและปัจจัยยังชีพอันมีเกียรติ (คือสวนสวรรค์)');
INSERT INTO "king_fahad_thai" VALUES(34,5,'34:5','ชนเหล่านั้นสําหรับพวกเขาจะได้รับการลงโทษอันเลวร้ายอย่างเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(34,6,'34:6','ตระหนักดีว่า สิ่งที่ได้ถูกประทานแก่เจ้าจากพระเจ้าของเจ้านั้นเป็นสัจธรรม และจะชี้นําไปสู่แนวทางแห่งพระผู้ทรงอํานาจ ผู้ทรงได้รับการสรรเสริญ');
INSERT INTO "king_fahad_thai" VALUES(34,7,'34:7','และบรรดาผู้ปฏิเสธศรัทธาได้กล่าว (เยาะเย้ย) ว่า “เราจะชี้แนะแก่พวกท่านไหมเล่าถึงชายคนหนึ่งที่เขาจะบอกเล่าแก่พวกท่านว่า เมื่อพวกท่านถูกทําให้แตกสลายกระจัดกระจายเป็นผุยผงแล้ว พวกท่านจะถูกบังเกิดขึ้นมาใหม่"');
INSERT INTO "king_fahad_thai" VALUES(34,8,'34:8','เขาได้ปั้นความเท็จให้แก่อัลลอฮฺ หรือว่าเขาเป็นบ้าไปแล้ว? ” หามิได้! บรรดาผู้ไม่ศรัทธาต่อปรโลกนั้น จะอยู่ในการลงโทษและการหลงผิดอันไกลลิบ');
INSERT INTO "king_fahad_thai" VALUES(34,9,'34:9','พวกเขามิเห็นดอกหรือ ถึงสิ่งที่อยู่เบื้องหน้าพวกเขา และสิ่งที่อยู่เบื้องหลังพวกเขา ที่มีอยู่ในฟากฟ้าและแผ่นดิน หากเราประสงค์เราจะให้แผ่นดินสูบพวกเขาลงไปหรือเราจะให้ส่วนต่าง ๆ จากท้องฟ้าหล่นลงมาบนพวกเขา แท้จริง ในการนี้ย่อมเป็นสัญญาณหนึ่งอย่างแน่นอนแก่บ่าวทุกคนที่หันหน้าเข้าสู่อัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(34,10,'34:10','และโดยแน่นอน เราได้ให้ความโปรดปรานจากเราแก่ดาวู๊ด โอ้ภูเขาเอ๋ย จงแซ่ซ้องสดุดีพร้อมกับเขาและนกด้วย และเราได้ทําให้เหล็กอ่อนสําหรับเขา');
INSERT INTO "king_fahad_thai" VALUES(34,11,'34:11','เจ้าจงทําเสื้อเกราะและทําห่วงของมันให้ได้สัดส่วน และพวกเจ้าจงทําความดีเถิดแท้จริง ข้านั้นรู้เห็นสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(34,12,'34:12','และเราได้ให้มีลมพัดแก่สุลัยมาน ซึ่งมันจะพัดไปในยามเช้าเป็นเวลาหนึ่งเดือน และมันจะพัดกลับในยามเย็นเป็นเวลาหนึ่งเดือน และเราได้ให้ไหลมาแก่เขาซึ่งตาน้ําทองเหลือง (คือให้ทองเหลืองที่หลอมตัวเป็นตาน้ําไหลมาสําหรับสุลัยมาน) ในหมู่ญินนั้น มีผู้ทํางานอยู่เบื้องหน้าเขาด้วย อนุมัติแห่งพระเจ้าของเขา และผู้ใดในหมู่พวกเขาหันเหจากพระบัญชาของเรา เราจะให้เขาลิ้มรสการลงโทษที่มีไฟลุกโชติช่วง');
INSERT INTO "king_fahad_thai" VALUES(34,13,'34:13','พวกเขา (ญิน) ทํางานให้เขา (สุลัยมาน) ตามที่เขาต้องการ (เช่นสร้าง) ปราสาทหลายแห่งที่สูงตระหง่าน และบรรดาหุ่นจําลอง และบรรดาโคมใส่อาหารมีขนาดเท่าบ่อน้ําและบรรดาหม้อสําหรับหุงอาหารตั้งอยู่กับที่ พวกเจ้าจงทํางานเถิด วงศ์วานของดาวู๊ดเอ๋ย! ด้วยการขอบคุณ และส่วนน้อยแห่งปวงบ่าวของเราที่เป็นผู้ขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(34,14,'34:14','ครั้นเมื่อเราได้กําหนดความตายแก่เขา มิได้มีสิ่งใดบ่งชี้แก่พวกเขาถึงความตายของเขา นอกจากปลวกใต้ดินแทะกินไม้เท้าของเขา ดังนั้น เมื่อเขาล้มลงพวกญินก็รู้อย่างชัดแจ้งว่า หากพวกเขารู้ในสิ่งพ้นญาณวิสัยแล้ว พวกเขาจะไม่ต้องมาทนทุกข์ทรมานที่น่าอดสูเช่นนี้');
INSERT INTO "king_fahad_thai" VALUES(34,15,'34:15','โดยแน่นอน สําหรับพวกสะบะอฺนั้นมีสัญญาณหนึ่งในที่อาศัยของพวกเขา มีสวนสองแห่งทางขวาและทางซ้าย พวกเจ้าจงบริโภคจากปัจจัยยังชีพของพระเจ้าของพวกเจ้า และจงขอบคุณต่อพระองค์ อันเป็นดินแดนที่อุดมสมบูรณ์ และมีพระเจ้าผู้ทรงอภัย');
INSERT INTO "king_fahad_thai" VALUES(34,16,'34:16','แต่พวกเขาได้ผินหลัง ดังนั้น เราจึงปล่อยน้ําจากเขื่อนให้ท่วมพวกเขา และเราได้เปลี่ยนให้พวกเขาสวนสองแห่งของพวกเขา แทนสวนอีกสองแห่ง มีผลไม้ขมและต้นไม้พุ่ม และต้นพุทราบ้างเล็กน้อย');
INSERT INTO "king_fahad_thai" VALUES(34,17,'34:17','เช่นนั้นแหละ เราได้ตอบแทนพวกเขา เนื่องจากพวกเขาเนรคุณ และเรามิได้ลงโทษผู้ใด (ด้วยการลงโทษอย่างรุนแรงเช่นนี้) นอกจากพวกเนรคุณ');
INSERT INTO "king_fahad_thai" VALUES(34,18,'34:18','ระหว่างพวกเขาและระหว่างหัวเมืองต่าง ๆ ซึ่งเราได้ให้ความจําเริญในนั้น เราได้ให้มีขึ้นซึ่งหัวเมืองที่เด่นชัด และเราได้กําหนดการเดินทางไว้ในนั้น พวกเจ้าจงเดินทางไปตามนั้นเถิด ทั้งกลางวันและกลางคืนอย่างปลอดภัย');
INSERT INTO "king_fahad_thai" VALUES(34,19,'34:19','แล้วพวกเขาได้พูดว่า “ข้าแต่พระเจ้าของเรา! ขอพระองค์ได้ทรงทําให้การเดินทางของเรายืดยาวขึ้น” และพวกเขาได้อธรรมต่อตัวพวกเขาเอง ดังนั้น เราจึงได้ทําให้พวกเขาเป็นเรื่องเล่าขานติดต่อกันมา และเราได้ทําให้พวกเขาแตกสลายกระจัดกระจายกันออกไป แท้จริงในการนี้ย่อมเป็นสัญญาณมากหลายอย่างแน่นอนแก่ทุกคนผู้อดทน ผู้กตัญญู');
INSERT INTO "king_fahad_thai" VALUES(34,20,'34:20','และโดยแน่นอน อิบลีสได้ทําให้การนึกคิดของมันที่มีต่อพวกเขาเป็นจริง');
INSERT INTO "king_fahad_thai" VALUES(34,21,'34:21','และมันไม่มีอํานาจใด ๆ เหนือพวกเขา เว้นแต่เพื่อเราจะได้รู้ว่าผู้ใดศรัทธาต่อวันปรโลกจากผู้ที่เขามีความสงสัยในเรื่องนั้น และพระเจ้าของเจ้านั้นเป็นผู้ทรงดูแลคุ้มครองทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(34,22,'34:22','จงกล่าวเถิดมุฮัมมัด “พวกท่านจงวิงวอนต่อบรรดาที่พวกท่านจินตนาการ (ว่าเป็นพระเจ้า) อื่นจากอัลลอฮฺพวกมันมิได้ครอบครองแม้แต่น้ําหนักเพียงเท่าธุลีในชั้นฟ้าทั้งหลาย และแผ่นดินและพวกมันมิได้มีหุ้นส่วนในทั้งสองนั้น และสําหรับพระองค์นั้นมิได้มีผู้ช่วยเหลือมาจากพวกมัน');
INSERT INTO "king_fahad_thai" VALUES(34,23,'34:23','การชะฟาอะฮฺ จะไม่เกิดประโยชน์อันใด ณ ที่พระองค์ นอกจากผู้ที่พระองค์ทรงอนุญาตแก่เขาจนกระทั่งเมื่อความหวาดกลัวได้คลายจากจิตใจของพวกเขา พวกเขากล่าวว่า “พระเจ้าของพวกท่านได้ตรัสอะไรนะ ? “พวกเขากล่าวว่า "สัจธรรม และพระองค์เป็นผู้ทรงสูงสุด ผู้ทรงยิ่งใหญ่"');
INSERT INTO "king_fahad_thai" VALUES(34,24,'34:24','จงกล่าวเถิดมุฮัมมัด “ใครเป็นผู้ประทานปัจจัยยังชีพแก่พวกท่านจากชั้นฟ้าทั้งหลายและแผ่นดิน ?” จงกล่าวเถิดมุฮัมมัดว่า “อัลลอฮฺ และแท้จริงไม่เราก็พวกท่านแน่นอนที่อยู่บนแนวทางที่ถูกต้อง หรืออยู่ในการหลงผิดอย่างชัดแจ้ง“');
INSERT INTO "king_fahad_thai" VALUES(34,25,'34:25','จงกล่าวเถิดมุฮัมมัด “พวกท่านจะไม่ถูกสอบสวนเกี่ยวกับที่เราทําผิด และเราก็จะไม่ถูกสอบสวนเกี่ยวกับที่พวกท่านกระทํา”');
INSERT INTO "king_fahad_thai" VALUES(34,26,'34:26','จงกล่าวเถิดมุฮัมมัด “พระเจ้าของเราจะทรงรวบรวมพวกเราทั้งหมด แล้วพระองค์จะทรงตัดสินระหว่างพวกเจ้าด้วยความจริงและพระองค์คือผู้ทรงตัดสิน ผู้ทรงรอบรู้”');
INSERT INTO "king_fahad_thai" VALUES(34,27,'34:27','จงกล่าวเถิดมุฮัมมัด “พวกท่านจงแสดงให้ฉันเห็น บรรดาที่พวกท่านได้นําไปตั้งภาคีร่วมกับพระองค์ ไม่ดอก ! พระองค์คืออัลลอฮฺ ผู้ทรงอํานาจ ผู้ทรงปรีชาญาณ“');
INSERT INTO "king_fahad_thai" VALUES(34,28,'34:28','และเรามิได้ส่งเจ้ามาเพื่ออื่นใด เว้นแต่เป็นผู้แจ้งข่าวดีและเป็นผู้ตักเตือนแก่มนุษย์ทั้งหลาย แต่ว่าส่วนมากของมนุษย์ไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(34,29,'34:29','และพวกเขากล่าวว่า “เมื่อใดเล่าสัญญานี้ (จะมาถึง)? หากพวกท่านเป็นผู้สัตย์จริง“');
INSERT INTO "king_fahad_thai" VALUES(34,30,'34:30','จงกล่าวเถิดมุฮัมมัด “สําหรับพวกท่านมีกําหนดวันวันหนึ่ง ซึ่งพวกท่านจะขอผ่อนผันให้ล่าช้าสักระยะหนึ่งก็ไม่ได้และจะร่นเวลาให้เร็วเข้าก็ไม่ได้”');
INSERT INTO "king_fahad_thai" VALUES(34,31,'34:31','และบรรดาผู้ปฏิเสธศรัทธากล่าวว่า “เราจะไม่ศรัทธาต่ออัลกุรอานนี้ และต่อสิ่งที่มีมาก่อนอัลกุรอาน” และหากเจ้า (มุฮัมมัด)ได้เห็น เมื่อบรรดาผู้อธรรมจะถูกให้หยุดยืนต่อหน้าพระเจ้าของพวกเขา บางคนในพวกเขาจะกล่าวชัดถ้อยคําแก่อีกคนหนึ่ง บรรดาผู้อ่อนแอกว่า (ลูกน้อง) กล่าวแก่บรรดาผู้หยิ่งยะโส(หัวหน้า) ว่า “หากมิใช่พวกท่านแล้ว แน่นอน พวกเราคงได้เป็นผู้ศรัทธากันแล้ว”');
INSERT INTO "king_fahad_thai" VALUES(34,32,'34:32','บรรดาผู้หยิ่งยะโส (หัวหน้า) ก็กล่าวแก่บรรดาผู้อ่อนแอ (ลูกน้อง) ว่า “พวกเรานะหรือที่ได้หน่วงเหนี่ยวพวกท่านจากแนวทางที่ถูกต้องหลังจากที่มันได้มีมายังพวกท่าน มิใช่เช่นนั้น แต่พวกท่านเองเป็นผู้กระทําผิด”');
INSERT INTO "king_fahad_thai" VALUES(34,33,'34:33','บรรดาผู้อ่อนแอ (ลูกน้อง) กล่าวแก่บรรดาผู้หยิ่งยะโส (หัวหน้า) ว่า “มิใช่เช่นนั้นดอก ! แต่มันเป็นแผนการทั้งกลางคืนและกลางวัน เมื่อพวกท่านใช้ให้พวกเราปฏิเสธศรัทธาต่ออัลลอฮฺ และให้เราตั้งภาคีคู่เคียงกับพระองค์”และพวกเขาจะซ่อนความสํานึกผิดเมื่อพวกเขาได้เห็นการลงโทษ และเราได้คล้องพันธนาการที่คอของบรรดาผู้ปฏิเสธศรัทธา พวกเขาจะไม่ได้รับการตอบแทนใด ๆ นอกจากที่พวกเขาได้กระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(34,34,'34:34','และเรามิได้ส่งผู้ตักเตือนคนใดไปยังเมืองใด นอกจากบรรดาผู้ฟุ่มเฟือยของมันจะกล่าวว่า “แท้จริงเราเป็นผู้ปฏิเสธศรัทธาในสิ่งที่พวกท่านถูกส่งมา”');
INSERT INTO "king_fahad_thai" VALUES(34,35,'34:35','และพวกเขาได้กล่าวอีกว่า “พวกเรามีทรัพย์สินและลูกหลานมากกว่า และพวกเราจะไม่ถูกลงโทษ”');
INSERT INTO "king_fahad_thai" VALUES(34,36,'34:36','จงกล่าวเถิดมุฮัมมัด “แท้จริงพระเจ้าของฉันทรงแผ่ปัจจัยยังชีพแก่ผู้ที่พระองค์ทรงประสงค์ และทรงให้คับแคบ แต่ว่าส่วนมากของมนุษย์ไม่รู้”');
INSERT INTO "king_fahad_thai" VALUES(34,37,'34:37','และมิใช่ทรัพย์สินของพวกเจ้าและมิใช่ลูกหลานของพวกเจ้า ที่จะทําให้พวกเจ้าใกล้ชิดสนิทกับเรา นอกจากผู้ศรัทธาและกระทําความดี ดังนั้นชนเหล่านั้นสําหรับพวกเขาจะได้รับการตอบแทนเป็นสองเท่าตามที่พวกเขาได้กระทําไว้ และพวกเขาจะพํานักอยู่ในสวนสวรรค์อย่างผู้ปลอดภัย');
INSERT INTO "king_fahad_thai" VALUES(34,38,'34:38','และบรรดาผู้มุ่งมั่นเพื่อทําลายล้างสัญญาณทั้งหลายของเรา ชนเหล่านี้จะถูกนํามาอยู่ต่อหน้า(เรา) ในการลงโทษ');
INSERT INTO "king_fahad_thai" VALUES(34,39,'34:39','จงกล่าวเถิด “แท้จริงพระเจ้าของฉันทรงแผ่ปัจจัยยังชีพแก่ผู้ที่พระองค์ทรงประสงค์ จากปวงบ่าวของพระองค์ และทรงให้คับแคบแก่เขา และอันใดที่พวกเจ้าบริจาคจากสิ่งใดก็ดีพระองค์จะทรงทดแทนมัน และพระองค์นั้นทรงเป็นผู้ดีเลิศแห่งบรรดาผู้ประทานปัจจัยยังชีพ');
INSERT INTO "king_fahad_thai" VALUES(34,40,'34:40','และวันที่พระองค์จะทรงรวบรวมพวกเขาทั้งหมด แล้วพระองค์จะตรัสแก่มะลาอิกะฮฺว่า “พวกเขาเหล่านั้นนะหรือที่เคารพภักดีต่อพวกเจ้า? “');
INSERT INTO "king_fahad_thai" VALUES(34,41,'34:41','พวกเขา (มะลาอิกะฮฺ) กล่าวว่า “มหาบริสุทธิ์แห่งพระองค์ท่าน พระองค์ทรงเป็นผู้คุ้มครองพวกข้าพระองค์ มิใช่พวกเขา แต่พวกเขาเคารพภักดีญิน (ชัยฏอน) ส่วนมากของพวกเขาเป็นผู้ศรัทธาต่อพวกมัน (ชัยฏอน)”');
INSERT INTO "king_fahad_thai" VALUES(34,42,'34:42','ดังนั้น วันนี้บางคนในพวกเจ้าไม่มีอํานาจที่จะให้คุณและให้โทษซึ่งกันและกัน และเราจะกล่าวแก่บรรดาผู้กระทําผิดเหล่านั้นว่า “พวกเจ้าจงลิ้มรสการลงโทษของไฟนรกซึ่งพวกเจ้าได้ปฏิเสธมัน “');
INSERT INTO "king_fahad_thai" VALUES(34,43,'34:43','และเมื่อบรรดาอายาตอันชัดแจ้งของเราถูกอ่านแก่พวกเขา (มุชริกีน) แล้วพวกเขากล่าวว่า”นี่มิใช่ใครอื่นนอกจากชายคนหนึ่งที่ปราถนาจะยับยั้งพวกท่าน จากการที่บรรพบุรุษของพวกท่านได้เคารพภักดีมาก่อน”และพวกเขากล่าวว่า “นี่มิใช่อะไรอื่น นอกจากเรื่องเท็จที่ถูกอุปโลกน์ขึ้น” และบรรดาผู้ปฏิเสธศรัทธาได้กล่าวในเรื่องของสัจธรรม เมื่อมันได้มีมายังพวกเขาว่า “นี่มิใช่อื่นใดเลยนอกจากเล่ห์กลอย่างชัดแจ้ง”');
INSERT INTO "king_fahad_thai" VALUES(34,44,'34:44','และเรามิได้ประทานบรรดาคัมภีร์ให้แก่พวกเขา (เพื่อให้) พวกเขาศึกษาค้นคว้ากันและเราก็มิได้ส่ง (บรรดาร่อซูล)ไปยังพวกเขาก่อนหน้าเจ้า (ในฐานะ) เป็นผู้ตักเตือน');
INSERT INTO "king_fahad_thai" VALUES(34,45,'34:45','และบรรดาผู้มาก่อนหน้าพวกเขาก็ได้ปฏิเสธ (สัจธรรมมาแล้ว) และพวกเขา (กุเรชมักกะฮฺ) มิได้มีถึงหนึ่งในสิบของที่เราได้ให้(ความมั่งคั่ง) แก่พวกเขา กระนั้นก็ดีพวกเขาก็ได้ปฏิเสธบรรดาร่อซูลของข้า ดังนั้นการปฏิเสธ(ต่อข้าจะมีผล) เป็นอย่างไร (ต่อพวกเขา) !');
INSERT INTO "king_fahad_thai" VALUES(34,46,'34:46','จงกล่าวเถิดมุฮัมมัด “ฉันขอเตือนพวกท่านเพียงข้อเดียวว่า พวกท่านจงยืนขึ้นเพื่ออัลลอฮฺ (ครั้งละ) สองคนและคนเดียว แล้วจงไตร่ตรองดู (ก็จะประจักษ์ว่า) สหายของพวกท่านนั้นมิได้เป็นบ้า แต่เขาเป็นเพียงผู้ตักเตือนพวกท่านถึงการเผชิญหน้ากับการลงโทษอย่างสาหัสเท่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(34,47,'34:47','จงกล่าวเถิดมุฮัมมัด “ไม่มีรางวัลอันใดที่ฉันจะขอจากพวกท่าน เพราะมันเป็นของพวกท่าน แต่รางวัลของฉันอยู่ที่อัลลอฮฺ และพระองค์ทรงเป็นพยานต่อทุกสิ่ง”');
INSERT INTO "king_fahad_thai" VALUES(34,48,'34:48','จงกล่าวเถิดมุฮัมมัด “แท้จริง พระเจ้าของฉันทรงให้ความจริงทําลาย(ความเท็จ เพราะพระองค์เป็น) ผู้ทรงรอบรู้ในสิ่งเร้นลับทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(34,49,'34:49','จงกล่าวเถิดมุฮัมมัด “เมื่อความจริงได้ปรากฏขึ้น ความเท็จก็จะไม่เกิดขึ้นและจะไม่กลับมาอีก”');
INSERT INTO "king_fahad_thai" VALUES(34,50,'34:50','จงกล่าวเถิดมุฮัมมัด "หากฉันหลงผิด ฉันก็หลงผิดเฉพาะตัวของฉันเอง แต่ถ้าฉันอยู่ในแนวทางที่ถูกต้อง นั่นก็คือพระเจ้าของฉันได้ทรงวะฮียฺแก่ฉัน แท้จริงพระองค์เป็นผู้ทรงได้ยินผู้ทรงใกล้ชิด"');
INSERT INTO "king_fahad_thai" VALUES(34,51,'34:51','และหากเจ้าได้เห็นขณะที่พวกเขาตื่นตระหนกตกใจ แล้วไม่มีทางหนีรอด และพวกเขาจะถูกจับเอาไปจากสถานที่อันใกล้');
INSERT INTO "king_fahad_thai" VALUES(34,52,'34:52','และพวกเขาก็จะกล่าวว่า “พวกเราได้ศรัทธาต่อมัน (ความจริง) แล้ว” จะเป็นไปได้อย่างไรที่พวกเขาเรียกร้องจากสถานที่อันไกลเช่นนี้');
INSERT INTO "king_fahad_thai" VALUES(34,53,'34:53','และแน่นอน พวกเขาได้ปฏิเสธศรัทธามั่น (ความจริง) มาก่อนแล้ว และพวกเขาขว้างทิ้ง (ปฏิเสธ) สิ่งเร้นลับจากสถานที่อันไกล');
INSERT INTO "king_fahad_thai" VALUES(34,54,'34:54','และระหว่างพวกเขากับสิ่งที่พวกเขาต้องการ มีสิ่งกีดขวาง ดั่งเช่นที่ได้ถูกปฏิบัติมาก่อนแล้วกับพลพรรคของพวกเขา แท้จริง พวกเขานั้นอยู่ในความสงสัย ความสนเท่ห์');
INSERT INTO "king_fahad_thai" VALUES(35,1,'35:1','บรรดาการสรรเสริญเป็นของอัลลอฮฺผู้ทรงสร้างชั้นฟ้าทั้งหลายและแผ่นดิน ผู้ทรงแต่งตั้งมะลาอิกะฮฺให้เป็นผู้นําข่าว ผู้มีปีกสอง สาม และสี่ ทรงเพิ่มในการสร้างตามที่พระองค์ทรงประสงค์ แท้จริง อัลลอฮฺนั้นเป็นผุ้ทรงอานุภาพเหนือทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(35,2,'35:2','สิ่งใดที่อัลลอฮฺทรงประทานให้จากความเมตตาแก่มนุษย์ชาติ ไม่มีผู้ยับยั้งมันได้ และสิ่งใดที่พระองค์ทรงยับยั้งไว้ ก็ไม่มีผู้ใดให้มันได้หลังจาก (การยับยั้ง) ของพระองค์และพระองค์เป็นผู้ทรงอํานาจ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(35,3,'35:3','โอ้มนุษย์เอ๋ย! พวกเจ้าจงรําลึกถึงความโปรดปรานของอัลลอฮฺที่มีต่อพวกเจ้า จะมีพระผู้สร้างอื่นใดจากอัลลอฮฺกระนั้นหรือ ที่จะประทานปัจจัยยังชีพแก่พวกเจ้าจากฟากฟ้าและแผ่นดิน ไม่มีพระเจ้าอื่นใดนอกจากพระองค์ ดังนั้น ทําไมเล่าพวกเจ้าจึงถูกหลอกลวงให้หันห่างออกไป (จากความจริง)');
INSERT INTO "king_fahad_thai" VALUES(35,4,'35:4','และพวกเขาได้สาบานต่ออัลลอฮฺด้วยการสาบานอย่างแข็งขันของพวกเขาว่า หากมีผู้ตักเตือนมายังพวกเขา แน่นอนพวกเขาก็จะเป็นประชาชาติหนึ่งที่อยู่ในแนวทางที่ถูกต้องยิ่ง (กว่าประชาชาติอื่น ๆ) ครั้นเมื่อได้มีผู้ตักเตือนมายังพวกเขา มันมิได้เพิ่มสิ่งใดแก่พวกเขานอกจากการเตลิดหนี');
INSERT INTO "king_fahad_thai" VALUES(35,5,'35:5','โอ้มนุษย์เอ๋ย ! แท้จริงสัญญาของอัลลอฮฺนั้นเป็นจริงเสมอ ดังนั้น อย่าให้การดํารงชีวิตอยู่ในโลกนี้ล่อลวงพวกเจ้า และอย่าให้การหลอกล่อ (ชัยฏอน) มาล่อลวงพวกเจ้าเกี่ยวกับอัลลอฮฺเป็นอันขาด');
INSERT INTO "king_fahad_thai" VALUES(35,6,'35:6','แท้จริง มารชัยฏอนนั้นเป็นศัตรูกับพวกเจ้า ดังนั้น พวกเจ้าจงถือว่ามันเป็นศัตรู แท้จริง มันเรียกร้องพลพรรคของมัน เพื่อให้พวกมันเป็นสหายแห่งไฟลุกโชติช่วง');
INSERT INTO "king_fahad_thai" VALUES(35,7,'35:7','บรรดาผู้ปฏิเสธศรัทธานั้น สําหรับพวกเขาจะได้รับการลงโทษอันสาหัส ส่วนบรรดาผู้ศรัทธาและกระทําความดีทั้งหลาย สําหรับพวกเขาจะได้รับการอภัยโทษและรางวัลอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(35,8,'35:8','ดังนั้น ผู้ที่ความชั่วแห่งการงานของเขาได้ถูกนําให้เพริศแพร้วแก่เขา แล้วเขาเห็นว่ามันเป็นสิ่งดีกระนั้นหรือ ? แท้จริง อัลลอฮฺจะทรงทําให้หลงผิดแก่ผู้ที่พระองค์ทรงประสงค์และจะทรงชี้แนะแนวทางที่ถูกต้องแก่ผู้ที่พระองค์ทรงประสงค์ ดังนั้น เจ้าอย่าทําให้จิตใจของเจ้ากลับกลายเป็นระทมทุกข์ เนื่องเพราะพวกเขา แท้จริง อัลลอฮฺนั้นเป็นผู้ทรงรอบรู้สิ่งที่พวกเขากระทํา');
INSERT INTO "king_fahad_thai" VALUES(35,9,'35:9','และอัลลอฮฺซึ่งทรงส่งลมทั้งหลายออกไป และมันได้หอบเป็นเมฆขึ้น แล้วเราได้ให้มันพัดพาไปยังดินแดนที่แห้งแล้ง แล้วเราได้ให้แผ่นดินนั้นมีชีวิต (ชุ่มชื้นด้วยน้ําฝน) หลังจาการแห้งแล้งของมัน เช่นนั้นแหละการฟื้นคืนชีพ');
INSERT INTO "king_fahad_thai" VALUES(35,10,'35:10','ผู้ใดต้องการอํานาจ ดังนั้น อํานาจทั้งมวลเป็นของอัลลอฮฺ คํากล่าวที่ดีย่อมจะขึ้นไปสู่พระองค์ และการงานที่ดีนั้นพระองค์ทรงยกย่องสรรเสริญมัน และบรรดาผู้วางแผนชั่วร้ายทั้งหลายนั้น พวกเขาจะได้รับการลงโทษอันเจ็บแสบ และแผนการณ์ของชนเหล่านั้นย่อมจะพินาศ');
INSERT INTO "king_fahad_thai" VALUES(35,11,'35:11','และอัลลอฮฺทรงบังเกิดพวกเจ้ามาจากฝุ่นดิน แล้วก็มาจากเชื้ออสุจิ แล้วทรงทําให้พวกเจ้าเป็นคู่สามีภริยา และจะไม่มีหญิงใดตั้งครรภ์และนางจะไม่คลอด เว้นแต่ด้วยความรอบรู้ของพระองค์ และไม่มีผู้สูงอายุคนใดจะถูกยืดอายุออกไป และอายุของเขาก็จะไม่ถูกตัดทอน เว้นแต่อยู่ในบันทึก (ของพระองค์) แท้จริง นั่นเป็นการง่ายดายสําหรับอัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(35,12,'35:12','และทะเลทั้งสองนั้นไม่เหมือนกัน อันนี้จืดสนิทอร่อยน่าดื่ม พอใจในเครื่องดื่มของมัน และอันนี้เค็มจัด และจากแต่ละทุกแห่งนั้น พวกเจ้าจะได้กินเนื้ออันอ่อนนุ่ม และพวกเจ้า เอาออกมาจาก (ทะเลทั้งสอง) เครื่องประดับ เพื่อใช้มันเป็นอาภรณ์ และเจ้าเห็นเรือแล่น ฝ่าผิวน้ําไป เพื่อพวกเจ้าจะได้แสวงหาความโปรดปรานของพระองค์ และเพื่อพวกเจ้าจะได้ขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(35,13,'35:13','พระองค์ทรงให้กลางคืนคาบเกี่ยวเข้าไปในกลางวัน และทรงให้กลางวันคาบเกี่ยวเข้าไปในกลางคืน และทรงให้ดวงอาทิตย์และดวงจันทร์เป็นประโยชน์ (แก่มนุษย์) ทุกสิ่งโคจรไปตามวาระที่ได้กําหนดไว้ นั่นคือ อัลลอฮฺพระเจ้าของพวกเจ้า อํานาจการปกครองทั้งมวลเป็นสิทธิ์ของพระองค์ และสิ่งที่พวกเจ้าวิงวอนขออื่นจากพระองค์นั้นพวกมันมิได้ครอบครองสิ่งใดแม้แต่เยื่อบางหุ้มเมล็ดอินทผลัม');
INSERT INTO "king_fahad_thai" VALUES(35,14,'35:14','หากพวกเจ้าวิงวอนขอพวกมัน พวกมันจะไม่ได้ยินการวิงวอนขอ ของพวกเจ้า ถึงแม้พวกมันได้ยินพวกมันก็จะไม่ตอบรับพวกเจ้า และในวันกิยามะฮฺพวกมันจะปฏิเสธการตั้งภาคีของพวกเจ้า และไม่มีผู้ใดแจ้งแก่เจ้าได้นอกจากพระผู้ทรงรอบรู้ ตระหนักยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(35,15,'35:15','โอ้มนุษย์เอ๋ย ! พวกเจ้าเป็นผู้ขัดสนต้องการพึ่งอัลลอฮฺ แต่อัลลอฮฺนั้น พระองค์ทรงมั่งมีอย่างล้นเหลือ ผู้ทรงได้รับการสรรเสริญ');
INSERT INTO "king_fahad_thai" VALUES(35,16,'35:16','หากพระองค์ทรงประสงค์ พระองค์ก็จะทรงให้พวกเจ้าสูญสิ้นไป และจะทรงนํามาซึ่งกลุ่มชนรุ่นใหม่');
INSERT INTO "king_fahad_thai" VALUES(35,17,'35:17','และในการณ์นั้นมิใช่เป็นการยากแก่อัลลอฮฺ เลย');
INSERT INTO "king_fahad_thai" VALUES(35,18,'35:18','และไม่มีผู้แบกภาระคนใดที่จะแบกภาระของผู้อื่นได้ และ ถ้าผู้ที่แบกภาระหนักอยู่แล้วขอร้อง (ผู้อื่น) ให้ช่วยแบกมัน ก็จะไม่มีสิ่งใดถูกแบกออกจากเขา ถึงแม้ว่าเขาจะเป็นญาติสนิทก็ตาม แท้จริงเจ้าเป็นเพียงผู้ตักเตือนบรรดาผู้เกรงกลัวพระเจ้าของพวกเขาในสิ่งเร้นลับ และพวกเขาดํารงไว้ซึ่งการละหมาดและผู้ใดขัดเกลาตนเอง แท้จริง เขาก็ขัดเกลาเพื่อตัวของเขาเอง และยังอัลลอฮฺเท่านั้นคือการกลับไป');
INSERT INTO "king_fahad_thai" VALUES(35,19,'35:19','และคนตาบอดกับคนตาดีนั้น ย่อมไม่เหมือนกัน');
INSERT INTO "king_fahad_thai" VALUES(35,20,'35:20','และความมืดทึบกับแสงสว่าง ก็ไม่เหมือนกัน');
INSERT INTO "king_fahad_thai" VALUES(35,21,'35:21','และที่เงาร่มกับที่ร้อนแดด ก็ไม่เหมือนกัน');
INSERT INTO "king_fahad_thai" VALUES(35,22,'35:22','และคนเป็นกับคนตายนั้น ย่อมไม่เหมือนกัน แท้จริง อัลลอฮฺทรงให้ผู้ที่พระองค์ทรงประสงค์ได้ยิน และเจ้าไม่สามารถที่จะให้ผู้ที่อยู่ในหลุมฝังศพได้ยินได้');
INSERT INTO "king_fahad_thai" VALUES(35,23,'35:23','เจ้ามิใช่อื่นใดนอกจากเป็นผู้ตักเตือนเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(35,24,'35:24','แท้จริงเราได้ส่งเจ้ามาด้วยสัจธรรม เป็นผู้แจ้งข่าวดีและผู้ตักเตือน และไม่มีประชาชาติใด (ในอดีต) เว้นแต่จะต้องมีผู้ตักเตือน (ถูกส่งมา) ยังพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(35,25,'35:25','และหากพวกเขาปฏิเสธเจ้า แน่นอนบรรดาผู้ที่มีมาก่อนพวกเขาก็ได้ปฏิเสธ (มาก่อน) แล้ว บรรดาร่อซูลของพวกเขาได้นําหลักฐานอันชัดแจ้งมายังพวกเขา และด้วยคัมภีร์ต่าง ๆและคัมภีร์อันแจ่มจรัส');
INSERT INTO "king_fahad_thai" VALUES(35,26,'35:26','และข้าได้ลงโทษบรรดาผู้ปฏิเสธ ศรัทธา ดังนั้น การปฏิเสธต่อเราจะ (มีผล) เป็นอย่างไร (ต่อพวกเขา) ?');
INSERT INTO "king_fahad_thai" VALUES(35,27,'35:27','เจ้ามิได้พิจารณาดอกหรือว่า แท้จริงอัลลอฮฺนั้นทรงให้น้ําหลั่งลงมาจากฟากฟ้า แล้วเราได้ให้พืชผลงอกเงยออกมาด้วยกัน (จากน้ํา) สีสรรของมันแตกต่างกันไป และในหมู่ภูเขาทั้งหลายมีชนิดต่าง ๆ ขาวและแดง หลากหลายสี และสีดําสนิท');
INSERT INTO "king_fahad_thai" VALUES(35,28,'35:28','และในหมู่มนุษย์ และสัตว์ และปศุสัตว์ ก็มีหลากหลายสีเช่นเดียวกัน แท้จริง บรรดาผู้ที่มีความรู้จากปวงบ่าวของพระองค์เท่านั้นที่เกรงกลัวอัลลอฮฺ แท้จริงอัลลอฮฺ นั้นเป็นผู้ทรงอํานาจ ผู้ทรงอภัยเสมอ');
INSERT INTO "king_fahad_thai" VALUES(35,29,'35:29','แท้จริง บรรดาผู้อ่านคัมภีร์ของอัลลอฮฺ และดํารงการละหมาด และบริจาคสิ่งที่เราได้ให้เป็นปัจจัยยังชีพแก่พวกเขา โดยซ่อนเร้นและเปิดเผย เพื่อหวังการค้าที่ไม่ซบเซา (ขาดทุน)');
INSERT INTO "king_fahad_thai" VALUES(35,30,'35:30','เพื่อพระองค์จะทรงตอบแทนรางวัลของพวกเขา ให้แก่พวกเขาอย่างครบถ้วนและจะทรงเพิ่มให้แก่พวกเขาจากความโปรดปรานของพระองค์ แท้จริง พระองค์นั้นเป็นผู้ทรงอภัย ผู้ทรงชื่นชม (เพราะการภักดีของพวกเขา)');
INSERT INTO "king_fahad_thai" VALUES(35,31,'35:31','และคัมภีร์ที่เราได้ให้แก่เจ้านั้นมันคือสัจธรรม เป็นการยืนยันในสิ่งที่ได้มีมาก่อนมัน แท้จริง อัลลอฮฺนั้นเป็นผู้ทรงตระหนัก ผู้ทรงเห็น ต่อปวงบ่าวของพระองค์');
INSERT INTO "king_fahad_thai" VALUES(35,32,'35:32','และเราได้ให้คัมภีร์ เป็นมรดกสืบทอดมา แก่บรรดาผู้ที่เราคัดเลือกแล้วจากปวงบ่าวของเรา บางคนในหมู่พวกเขาเป็นผู้อธรรมแก่ตัวเอง และบางคนในหมู่พวกแป็นผู้เดินสายกลางและบางคนในหมู่พวกเขาเป็นผู้รุดหน้าในการทําความดีทั้งหลาย ด้วยอนุมัติของอัลลอฮฺ นั่นคือความโปรดปรานอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(35,33,'35:33','สวนสวรรค์หลากหลายเป็นที่พํานักอันสถาพร พวกเขาจะเข้าไปอยู่ในนั้น ในสวนสวรรค์พวกเขาจะได้ประดับด้วยกําไลทองและไข่มุก และอาภรณ์ของพวกเขาในนั้นคือผ้าไหม');
INSERT INTO "king_fahad_thai" VALUES(35,34,'35:34','และพวกเขากล่าวว่า "บรรดาการสรรเสริญเป็นของอัลลอฮฺ ซึ่งพระองค์ทรงขจัดความระทมทุกข์ออกจากเรา แท้จริงพระเจ้าของเราเป็นผู้ทรงอภัย ผู้ทรงชื่นชม (ต่อผู้จงรักภักดีต่อพระองค์)');
INSERT INTO "king_fahad_thai" VALUES(35,35,'35:35','ซึ่งพระองค์ทรงให้เราได้พํานักในสถานที่พํานักอันสถาพร ด้วยความโปรดปรานของพระองค์ ความเหน็ดเหนื่อยจะไม่ประสบแก่เราในนั้น และความเบื่อหน่าย ก็จะไม่ประสบแก่เราในนั้น');
INSERT INTO "king_fahad_thai" VALUES(35,36,'35:36','ส่วนบรรดาผู้ปฏิเสธศรัทธา พวกเขาจะได้รับไฟนรกญะฮันนัม (เป็นการตอบแทน) จะไม่ถูกตัดสินลงโทษให้พวกเขาตายเพื่อที่พวกเขาจะได้ตาย และการลงโทษของมันก็จะไม่ถูกลดหย่อนแก่พวกเขา เช่นนั้นแหละเราจะตอบแทนแก่ทุกผู้เนรคุณ (ปฏิเสธศรัทธา)');
INSERT INTO "king_fahad_thai" VALUES(35,37,'35:37','และพวกเขาจะตะโกนอยู่ในนรกนั้นว่า "ข้าแต่พระเจ้าของเรา ขอพระองค์ได้ทรงโปรดนําเราออกไป (จากนรก) เพื่อเราจะได้ปฏิบัติการงานที่ดี อื่นจากที่เราได้ปฏิบัติไปแล้ว" และเรามิได้ให้อายุของพวกเจ้ายืนนานพอดอกหรือ เพื่อผู้ที่ใคร่ครวญจะได้รําลึกถึงข้อตักเตือนและ (ยิ่งกว่านั้น) ได้มีผู้ตักเตือนมายังพวกเจ้าแล้ว (ดังนั้น พวกเจ้าจงลิ้มรส(การลงโทษ) เถิด เพราะสําหรับบรรดาผู้อธรรมนั้นจะไม่มีผู้ช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(35,38,'35:38','แท้จริง อัลลอฮฺนั้นทรงรอบรู้ในสิ่งเร้นลับแห่งชั้นฟ้าทั้งหลายและแผ่นดิน แท้จริงพระองค์เป็นผู้ทรงรอบรู้สิ่งที่อยู่ในทรวงอก');
INSERT INTO "king_fahad_thai" VALUES(35,39,'35:39','พระองค์คือผู้ทรงแต่งตั้งพวกเจ้าให้เป็นตัวแทนรับช่วงในแผ่นดิน ดังนั้น ผู้ใดปฏิเสธศรัทธา การปฏิเสธของเขาก็จะตกอยู่แก่เขา และการปฏิเสธศรัทธาของพวกเขาจะไม่เพิ่มสิ่งใดให้แก่พวกปฏิเสธศรัทธา ณ ที่พระเจ้าของพวกเขาเลย นอกจากความอัปยศอดสู และการปฏิเสธศรัทธาของพวกเขาจะไม่เพิ่มสิ่งใดให้แก่พวกปฏิเสธศรัทธา นอกจากความหายนะ');
INSERT INTO "king_fahad_thai" VALUES(35,40,'35:40','จงกล่าวเถิดมุฮัมมัด "พวกท่านไม่เห็นดอกหรือ ? บรรดาภาคี (เจว็ด) ของพวกท่านที่พวกท่านวิงวอนขออื่นจากอัลลอฮฺ จงแสดงให้ฉันเห็นซิว่าพวกมันได้สร้างอะไรในแผ่นดินนี้ ? หรือว่าพวกมันมีส่วนร่วมในชั้นฟ้าทั้งหลาย ? " หรือว่าเราได้ให้คัมภีร์แก่พวกมัน พวกมันจึงยึดมั่นอยู่บนหลักฐานจากมัน เปล่าดอก ! บรรดาผู้อธรรมนั้นต่างก็มิได้มีสัญญาอะไรต่อกัน นอกจากการหลอกลวงเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(35,41,'35:41','แท้จริง อัลลอฮฺทรงค้ําจุนชั้นฟ้าทั้งหลายและแผ่นดินเอาไว้ มิให้มันหล่นลงมา และหากมันทั้งสองหล่นลงมา ก็ไม่มีผู้ใดค้ําจุนมันทั้งสองไว้ได้ นอกจากพระองค์ แท้จริงพระองค์นั้นเป็นผู้ทรงขันติ ผู้ทรงอภัยเสมอ');
INSERT INTO "king_fahad_thai" VALUES(35,42,'35:42','และพวกเขาได้สาบานต่ออัลลอฮฺด้วยการสาบานอย่างแข็งขันของพวกเขาว่า หากมีผู้ตักเตือนมายังพวกเขา แน่นอนพวกเขาก็จะเป็นประชาชาติหนึ่งที่อยู่ในแนวทางที่ถูกต้องยิ่ง(กว่าประชาชาติอื่นๆ) ครั้นเมื่อได้มีผู้ตักเตือนมายังพวกเขา มันมิได้เพิ่มสิ่งใดแก่พวกเขานอกจากการเตลิดหนี');
INSERT INTO "king_fahad_thai" VALUES(35,43,'35:43','ด้วยการหยิ่งยะโสในแผ่นดิน และการวางแผนชั่ว แต่แผนชั่วนั้นจะไม่ห้อมล้อมผู้ใด นอกจากเจ้าของของมันเท่านั้น พวกเขาจะคอยอะไรอีกเล่า นอกจากแนวทางของบรรพชน ดังนั้น เจ้าจะไม่พบการเปลี่ยนแปลงในแนวทางของอัลลอฮฺ และเจ้าจะไม่พบการบิดเบือนในแนวทางของอัลลอฮฺแต่ประการใด');
INSERT INTO "king_fahad_thai" VALUES(35,44,'35:44','พวกเขามิได้ท่องเที่ยวไปตามแผ่นดินดอกหรือ แล้วพิจารณาดูว่า บั้นปลายของประชาชาติในยุคก่อนหน้าพวกเขานั้นเป็นเช่นใด ? เป็นที่ปรากฏว่าพวกเหล่านั้นมีพลังเข้มเข็งกว่าพวกเขา และอัลลอฮฺนั้น ไม่มีสิ่งใดในชั้นฟ้าทั้งหลายและแผ่นดินจะทําให้พระองค์หมดความสามารถไปได้ แท้จริงพระองค์เป็นผู้ทรงรอบรู้ ผู้ทรงอานุภาพเสมอ');
INSERT INTO "king_fahad_thai" VALUES(35,45,'35:45','และหากอัลลอฮฺจะทรงเอาโทษมนุษย์ตามที่พวกเขาได้ขวนขวายเอาไว้แล้ว พระองค์จะไม่ทรงให้เหลือไว้บนหน้าแผ่นดินซึ่งสัตว์โลกต่าง ๆ แต่ว่าพระองค์ทรงประวิงเวลาให้พวกเขาจนถึงเวลาที่ถูกกําหนดไว้ ครั้นเมื่อวาระกําหนดของพวกเขาได้มาถึง เมื่อนั้นแหละ แท้จริงอัลลอฮฺเป็นผู้ทรงเห็นปวงบ่าวของพระองค์เสมอ');
INSERT INTO "king_fahad_thai" VALUES(36,1,'36:1','ยาซีน');
INSERT INTO "king_fahad_thai" VALUES(36,2,'36:2','ขอสาบานด้วยอัลกุรอานที่มีคําสั่งอันรัดกุม');
INSERT INTO "king_fahad_thai" VALUES(36,3,'36:3','แท้จริง เจ้าเป็นผู้หนึ่งในหมู่ผู้ถูกส่งมาอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(36,4,'36:4','(เป็นผู้) อยู่บนแนวทางอันเที่ยงธรรม');
INSERT INTO "king_fahad_thai" VALUES(36,5,'36:5','อัลกุรอานนี้เป็นการประทานลงมาจากพระผู้ทรงอํานาจ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(36,6,'36:6','เพื่อเจ้าจะได้ตักเตือนกลุ่มชนหนึ่งซึ่งบรรพบุรุษของพวกเขามิได้ถูกตักเตือนมาก่อนดังนั้น พวกเขาจึงไม่สนใจ');
INSERT INTO "king_fahad_thai" VALUES(36,7,'36:7','โดยแน่นอน พระประกาศิตได้เป็นที่สมจริงแล้วแก่ส่วนมากของพวกเขา เพราะพวกเขาไม่ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(36,8,'36:8','แท้จริงเราได้คล้องพันธนาการที่คอของพวกเขา มันจึงห้อยลงมาที่คางของพวกเขา ดังนั้น (ศรีษะของ)พวกเขาจึงเงยขึ้น');
INSERT INTO "king_fahad_thai" VALUES(36,9,'36:9','และเราได้ทําเครื่องกีดขวางไว้ข้างหน้าพวกเขา และเครื่องกีดขวางไว้ข้างหลังพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(36,10,'36:10','และมีผลเท่ากันแก่พวกเขา เจ้าจะตักเตือนพวกเขาหรือไม่ตักเตือนพวกเขาก็ตาม พวกเขาก็จะไม่ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(36,11,'36:11','แท้จริงเจ้าเพียงแต่ตักเตือนผู้ที่ปฏิบัติตามข้อตักเตือนเท่านั้น และเขาเกรงกลัวพระผู้ทรงกรุณาปรานีโดยทางลับ ดังนั้น จงแจ้งข่าวดีแก่เขาด้วยการอภัยโทษและรางวัลอันมีเกียรติ');
INSERT INTO "king_fahad_thai" VALUES(36,12,'36:12','แท้จริงเราเป็นผู้ให้คนตายกลับมีชีวิตขึ้น และเราบันทึกสิ่งที่พวกเขาได้ประกอบไว้แต่ก่อน และร่องรอยของพวกเขาและทุกสิ่งนั้น เราได้รวบรวมไว้อย่างครบถ้วนในบันทึกอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(36,13,'36:13','และจงเล่าเรื่องชาวเมือง (อันฏอกียะฮฺ) แก่พวกเขา เมื่อมีทูตหลายคนมายังเมืองนั้น');
INSERT INTO "king_fahad_thai" VALUES(36,14,'36:14','เมื่อเราส่งทูตสองคนไปยังพวกเขา พวกเขาได้ปฏิเสธเขาทั้งสอง ดังนั้น เรา (อัลลอฮฺ) จึงเพิ่มพลังด้วยการส่งทูตคนที่สามแล้วพวกเขา (บรรดาทูต) ได้กล่าวว่า “แท้จริงพวกเราถูกส่งมายังพวกท่าน“');
INSERT INTO "king_fahad_thai" VALUES(36,15,'36:15','พวกเขา (ชาวเมือง) กล่าวว่า “พวกท่านมิใช่ใครอื่น นอกจากเป็นสามัญชนเช่นเดียวกับพวกเรา และพระผู้ทรงกรุณาปรานีมิได้ประทานสิ่งใดลงมา พวกท่านมิได้เป็นอื่นใดนอกจากกล่าวเท็จ”');
INSERT INTO "king_fahad_thai" VALUES(36,16,'36:16','พวกเขา (บรรดาทูต) กล่าวว่า “พระเจ้าของเราทรงรู้ดียิ่งว่า แท้จริงเราถูกส่งมายังพวกท่านอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(36,17,'36:17','และไม่มีหน้าที่อื่นใดแก่พวกเรานอกจากการประกาศเชิญชวนอันชัดแจ้งเท่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(36,18,'36:18','พวกเขากล่าวว่า “แท้จริงพวกเราถือเป็นลางร้ายต่อพวกท่าน หากพวกท่านไม่ยอมหยุดยั้ง เราจะเอาหินขว้างพวกท่านจนตายและแน่นอน การลงโทษอันเจ็บปวดจากพวกเราจะประสบแก่พวกท่าน”');
INSERT INTO "king_fahad_thai" VALUES(36,19,'36:19','พวกเขา (บรรดาทูต) กล่าวว่า “ลางร้ายของพวกท่านเพราะพวกท่านเอง พวกท่านได้ถูกตักเตือนมาก่อนแล้วมิใช่หรือ ? เปล่าดอกพวกท่านเป็นหมู่ชนผู้ฝ่าฝืนต่างหาก”');
INSERT INTO "king_fahad_thai" VALUES(36,20,'36:20','และมีชายคนหนึ่งจากสุดหัวเมืองได้มาอย่างรีบเร่ง');
INSERT INTO "king_fahad_thai" VALUES(36,21,'36:21','พวกท่านจงปฏิบัติตามผู้ที่มิได้เรียกร้องรางวัลใด ๆ จากพวกท่าน');
INSERT INTO "king_fahad_thai" VALUES(36,22,'36:22','และทําไมเล่าฉันจะไม่เคารพภักดีผู้ทรงบังเกิดฉัน และยังพระองค์เท่านั้นที่พวกท่านจะถูกนํากลับไป');
INSERT INTO "king_fahad_thai" VALUES(36,23,'36:23','จะให้ฉันยึดถือพระเจ้าอื่นใดนอกจากพระองค์กระนั้นหรือ? หากพระผู้ทรงกรุณาปรานี ทรงประสงค์จะก่อความทุกข์ยากแก่ฉัน การชะฟาอะฮฺของพวกเขาจะไม่ก่อประโยชน์อันใดแก่ฉันเลยและพวกเขาก็จะช่วยฉันให้รอดพ้น(จากการลงโทษ) ไม่ได้เลย');
INSERT INTO "king_fahad_thai" VALUES(36,24,'36:24','แท้จริง เมื่อนั้นฉันจะอยู่ในการหลงผิดอย่างชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(36,25,'36:25','แท้จริงฉันศรัทธาต่อพระเจ้าของพวกท่าน ดังนั้น พวกท่านจงฟังฉันซิ!”');
INSERT INTO "king_fahad_thai" VALUES(36,26,'36:26','เขากล่าวว่า “โอ้ มาตรว่าหมู่ชนของฉันได้รู้ (สภาพของฉัน)');
INSERT INTO "king_fahad_thai" VALUES(36,27,'36:27','ถึงการที่พระเจ้าของฉันทรงอภัยให้แก่ฉัน และทรงทําให้ฉันอยู่ในหมู่ผู้มีเกียรติ”');
INSERT INTO "king_fahad_thai" VALUES(36,28,'36:28','และเรามิได้ส่งไพร่พลลงมาจากฟากฟ้าแก่หมู่ชนของเขาหลังจากเขา และเราก็มิใช่เป็นผู้ส่งพวกเขาลงมา');
INSERT INTO "king_fahad_thai" VALUES(36,29,'36:29','แล้วเมื่อนั้นพวกเขาก็ดับเงียบ');
INSERT INTO "king_fahad_thai" VALUES(36,30,'36:30','โอ้ อนิจจาต่อปวงบ่าว ไม่มีร่อซูลคนใดมายังพวกเขา เว้นแต่พวกเขาได้เย้ยหยันเขา');
INSERT INTO "king_fahad_thai" VALUES(36,31,'36:31','พวกเขามิได้พิจารณาดอกหรือว่า กี่ศตวรรษมาแล้วก่อนหน้าพวกเขาเราได้ทําลายโดยที่เขาเหล่านั้นมิได้กลับมายังพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(36,32,'36:32','และแต่ละคนในพวกเขาทั้งหมดจะถูกนํามาปรากฏตัวต่อหน้าเรา');
INSERT INTO "king_fahad_thai" VALUES(36,33,'36:33','และสัญญาณหนึ่งสําหรับพวกเขาก็คือ แผ่นดินที่แห้งแล้งเราได้ให้มันมีชีวิตชีวาขึ้นมาและเราได้นําเมล็ดพืชออกมาจากมัน ซึ่งส่วนหนึ่งจากเมล็ดพืชนั้นพวกเขาใช้กิน');
INSERT INTO "king_fahad_thai" VALUES(36,34,'36:34','และเราได้ทําให้มีในแผ่นดินนั้นเรือกสวนมากหลาย จากอินทผลัมและองุ่น และเราได้ทํามีตาน้ําในนั้น');
INSERT INTO "king_fahad_thai" VALUES(36,35,'36:35','เพื่อพวกเขาจะได้กินผลไม้ของมันและจากสิ่งที่มือของพวกเขาได้กระทํามันขึ้นแล้วพวกเขาจะไม่ขอบคุณกระนั้นหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(36,36,'36:36','มหาบริสุทธิ์ยิ่งแด่พระผู้ทรงสร้างทุกสิ่งทั้งหมดเป็นคู่ ๆ จากสิ่งที่แผ่นดินได้ (ให้มัน) งอกเงยขึ้นมา และจากตัวของพวกเขาเองและจากสิ่งที่พวกเขาไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(36,37,'36:37','และสัญญาณหนึ่งสําหรับพวกเขาก็คือกลางคืน เราได้ถอนกลางวันออกจากมัน แล้วพวกเขาก็อยู่ในความมืด');
INSERT INTO "king_fahad_thai" VALUES(36,38,'36:38','และดวงอาทิตย์โคจรตามวิถีของมัน นั่นคือ การกําหนดของพระผู้ทรงอํานาจ ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(36,39,'36:39','และดวงจันทร์นั้น เราได้กําหนดให้มันโคจรตามตําแหน่ง จนกระทั่งมันได้กลายมาเป็นเช่นกิ่งอินทผลัมแห้ง');
INSERT INTO "king_fahad_thai" VALUES(36,40,'36:40','ดวงอาทิตย์ก็ไม่สมควร (อนุมัติ) แก่มันที่จะไล่ตามใกล้ดวงจันทร์ และกลางคืนก็จะไม่ล้ําหน้ากลางวัน และทั้งหมดนั้นจะเวียนว่ายอยู่ในจักรราศี');
INSERT INTO "king_fahad_thai" VALUES(36,41,'36:41','และสัญญาณหนึ่งสําหรับพวกเขาก็คือ เราได้บรรทุกลูกหลานของพวกเขาไว้ในเรือจนเต็ม');
INSERT INTO "king_fahad_thai" VALUES(36,42,'36:42','และเราได้สร้างทํานองเดียวกันนี้ (เรือใหญ่) แก่พวกเขา เพื่อให้พวกเขาขับขี่');
INSERT INTO "king_fahad_thai" VALUES(36,43,'36:43','และถ้าเราประสงค์เราก็จะจมพวกเขาเสีย แล้วจะไม่มีผู้ร้องตะโกนเพื่อขอความช่วยเหลือให้แก่เขา และพวกเขาก็จะไม่ถูกช่วยให้รอดพ้น (จากการจมน้ําตาย) ด้วย');
INSERT INTO "king_fahad_thai" VALUES(36,44,'36:44','เว้นแต่ด้วยความเมตตาจากเรา และความเพลิดเพลินชั่วระยะหนึ่ง');
INSERT INTO "king_fahad_thai" VALUES(36,45,'36:45','และเมื่อได้มีเสียงกล่าวแก่พวกเขาว่า “จงระวังสิ่งที่อยู่ข้างหน้าพวกเจ้า และสิ่งที่อยู่ข้างหลังพวกเจ้า เพื่อพวกเจ้าจะได้รับความเมตตา”');
INSERT INTO "king_fahad_thai" VALUES(36,46,'36:46','และไม่มีสัญญาณใดในบรรดาสัญญาณของพระเจ้าของพวกเขาได้มีมายังพวกเขาเว้นแต่พวกเขาจะผินหลังให้แก่ สัญญาณนั้น ๆ');
INSERT INTO "king_fahad_thai" VALUES(36,47,'36:47','และเมื่อมีเสียงกล่าวแก่พวกเขาว่า “จงบริจาคจากสิ่งที่อัลลอฮฺทรงประทานเป็นปัจจัยยังชีพแก่พวกเจ้า” บรรดาผู้ปฏิเสธศรัทธาได้กล่าวแก่บรรดาผู้ศรัทธาว่า “เราจะให้อาหารแก่ผู้ที่หากอัลลอฮฺทรงประสงค์ก็จะให้อาหารแก่เขากระนั้นหรือ ? “พวกท่านมิใช่อื่นใดเลยนอกจากอยู่ในการหลงผิดอย่างชัดแจ้ง “');
INSERT INTO "king_fahad_thai" VALUES(36,48,'36:48','และพวกเขากล่าวว่า “เมื่อใดเล่าสัญญานี้จะเกิดขึ้น หากพวกท่านเป็นผู้สัตย์จริง”');
INSERT INTO "king_fahad_thai" VALUES(36,49,'36:49','พวกเขามิได้คอยสิ่งใดเลยนอกจากเสียงกัมปนาทเพียงครั้งเดียว ซึ่งจะคร่าชีวิต พวกเขาในขณะที่พวกเขาโต้เถียงกันอยู่');
INSERT INTO "king_fahad_thai" VALUES(36,50,'36:50','แล้วพวกเขาก็ไม่สามารถจะสั่งเสียอันใด และพวกเขาก็ไม่ทันจะกลับไปยังครอบครัวของพวกเขาได้');
INSERT INTO "king_fahad_thai" VALUES(36,51,'36:51','และสังข์ก็จะถูกเป่าขั้น ทันใดนั้นพวกเขาจะออกจากหลุมฝังศพ แล้วพวกเขาก็รีบรุดไปยังพระเจ้าของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(36,52,'36:52','พวกเขากล่าวว่า “โอ้ความหายนะที่ประสบแก่เรา ! ใครเล่าที่ให้เราฟื้นขึ้นจากที่นอนของเรา (กุบูร)” (จะมีเสียงกล่าวขึ้นว่า) ”นี่แหละคือสิ่งที่พระผู้ทรงกรุณาปรานีได้ทรงสัญญาไว้ และบรรดาร่อซูลได้กล่าวสมจริงแล้ว“');
INSERT INTO "king_fahad_thai" VALUES(36,53,'36:53','ไม่มีอะไรดอกนอกจากเสียงกัมปนาทเพียงครั้งเดียว ทันใดนั้นพวกเขาทั้งหมดก็จะถูกนํามาปรากฏต่อหน้าเรา');
INSERT INTO "king_fahad_thai" VALUES(36,54,'36:54','ในวันนั้นไม่มีชีวิตใด (ผู้ใด) จะถูกอยุติธรรมแต่ประการใด และพวกเจ้าจะไม่ได้รับการตอบแทน นอกจากสิ่งที่พวกเจ้าได้ปฏิบัติไว้');
INSERT INTO "king_fahad_thai" VALUES(36,55,'36:55','แท้จริง ในวันนั้นชาวสวรรค์จะอยู่ในกิจกรรมอันสุขสําราญ');
INSERT INTO "king_fahad_thai" VALUES(36,56,'36:56','พวกเขาและคู่ครองของพวกเขาจะอยู่ภายใต้ร่มเงา นอนเอกเขนกอยู่บนเก้าอี้นวม');
INSERT INTO "king_fahad_thai" VALUES(36,57,'36:57','สําหรับพวกเขาในสวนสวรรค์นั้นจะมีผลไม้ (หลากชนิด) และสําหรับพวกเขาจะมีสิ่งที่พวกเขาต้องการ');
INSERT INTO "king_fahad_thai" VALUES(36,58,'36:58','“ความศานติ!” พระดํารัสหนึ่งจากพระเจ้าผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(36,59,'36:59','“โอ้บรรดาอาชญากรทั้งหลายเอ๋ย! วันนี้พวกเจ้าจงถอยห่างออกไปให้พ้น');
INSERT INTO "king_fahad_thai" VALUES(36,60,'36:60','ข้ามิได้บัญชาพวกเจ้าดอกหรือ โอ้ลูกหลานของอาดัมเอ๋ย! ว่าพวกเจ้าอย่าได้เคารพบูชาชัยฏอนมารร้าย แท้จริงมันนั้นเป็นศัตรูตัวฉกาจของพวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(36,61,'36:61','และพวกเจ้าจงเคารพภักดีต่อข้า นี่คือแนวทางอันเที่ยงแท้');
INSERT INTO "king_fahad_thai" VALUES(36,62,'36:62','และโดยแน่นอน มันได้ทําให้หมู่ชนจํานวนมากของพวกเจ้าหลงทาง ทําไมพวกเจ้าจึงไม่ใช้สติปัญญาใคร่ครวญเล่า?');
INSERT INTO "king_fahad_thai" VALUES(36,63,'36:63','นี่คือนรกญะฮันนัม ซึ่งพวกเจ้าถูกสัญญาไว้');
INSERT INTO "king_fahad_thai" VALUES(36,64,'36:64','วันนี้พวกเจ้าจะเข้าไปลิ้มรสมัน เนื่องเพราะพวกเจ้าปฏิเสธ');
INSERT INTO "king_fahad_thai" VALUES(36,65,'36:65','วันนี้เราจะปิดผนึกปากของพวกเขาและมือของพวกเขาจะพูดแก่เรา และเท้าของพวกเขาจะเป็นพยานตามที่พวกเขาได้ปฏิบัติไว้');
INSERT INTO "king_fahad_thai" VALUES(36,66,'36:66','และหากเราประสงค์ เราก็จะทําให้ตาของพวกเขาบอดลง แล้วพวกเขาก็จะคลําหาทาง แต่พวกเขาจะเห็นได้อย่างไร?');
INSERT INTO "king_fahad_thai" VALUES(36,67,'36:67','และหากเราประสงค์ เราก็จะแปลงรูปของพวกเขาให้อยู่กับที่ของพวกเขา แล้วพวกเขาก็ไม่อาจจะไปข้างหน้าได้และก็ไม่อาจจะถอยกลับได้');
INSERT INTO "king_fahad_thai" VALUES(36,68,'36:68','และผู้ใดที่เราทําให้เขามีอายุยืนนานเราจะให้กลับคืนสู่สภาพเมื่อตอนแรกเกิดแล้วพวกเขาไม่ไช้สติปัญญาใคร่ครวญบ้างหรือ?');
INSERT INTO "king_fahad_thai" VALUES(36,69,'36:69','เรามิได้สอนกวีนิพนธ์แก่เขา (มุฮัมมัด) และไม่เหมาะสมแก่เขาที่จะเป็นกวีคัมภีร์นี้มิใช่อื่นใดเลย นอกจากเป็นข้อตักเตือนและเป็นคัมภีร์อันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(36,70,'36:70','เพื่อตักเตือนผู้ที่มีชีวิต และเพื่อข้อตักเตือนนั้นเป็นหลักฐานยืนยันแก่บรรดาผู้ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(36,71,'36:71','และพวกเขามิได้พิจารณาดูดอกหรือเราได้สร้างปศุสัตว์ขึ้นมาเพื่อพวกเขาจากสิ่งที่มือของเราได้ทําขึ้น แล้วพวกเขาก็ได้ครอบครองมัน');
INSERT INTO "king_fahad_thai" VALUES(36,72,'36:72','และเราได้ทําให้มันยอมจํานนแก่พวกเขา ดังนั้น บางชนิดมันก็เป็นพาหนะแก่พวกเขา และบางชนิดพวกเขาก็ใช้กินเป็นอาหาร');
INSERT INTO "king_fahad_thai" VALUES(36,73,'36:73','และในตัวมันนั้นมีประโยชน์มากหลายและเครื่องดื่มสําหรับพวกเขา แล้วพวกเขาจะยังไม่ขอบคุณอีกหรือ?');
INSERT INTO "king_fahad_thai" VALUES(36,74,'36:74','และพวกเขาได้ยึดถือเอาพระเจ้ามากหลาย (เป็นที่เคารพสักการะ) อื่นจากอัลลอฮฺหวังว่าพวกเขาจะได้รับความช่วยเหลือ (จากมัน)');
INSERT INTO "king_fahad_thai" VALUES(36,75,'36:75','พวกมันไม่สามารถช่วยเหลือพวกเขาได้ พวกเขาจะถูกนํามาปรากฏตัวเป็นกลุ่ม ๆ เพื่อพวกมัน');
INSERT INTO "king_fahad_thai" VALUES(36,76,'36:76','ดังนั้น อย่าได้ให้คําพูดของพวกเขาเป็นที่เสียใจแก่เจ้า แท้จริงเรารู้ดีถึงสิ่งที่พวกเขาปิดบัง และสิ่งที่พวกเขาเปิดเผย');
INSERT INTO "king_fahad_thai" VALUES(36,77,'36:77','มนุษย์มิได้พิจารณาดูดอกหรือว่าเราได้บังเกิดเขามาจากน้ําอสุจิ แล้วจงดูซิ เขาได้กลายเป็นคู่ปรปักษ์ตัวฉกาจ');
INSERT INTO "king_fahad_thai" VALUES(36,78,'36:78','และเขาได้ยกอุทาหรณ์เปรียบเทียบแก่เรา และเขาได้ลืมต้นกําเนิดของเขา เขากล่าวว่า “ใครเล่าจะให้กระดูกมีชีวิตขึ้นมาอีกในเมื่อมันเป็นผุยผงไปแล้ว”');
INSERT INTO "king_fahad_thai" VALUES(36,79,'36:79','จงกล่าวเถิดมุฮัมมัด “พระผู้ทรงให้กําเนิดมันครั้งแรกนั้น ย่อมจะทรงให้มันมีชีวิตขึ้นมาอีก และพระองค์เป็นผู้ทรงรอบรู้การบังเกิดทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(36,80,'36:80','ผู้ทรงทําให้มีไฟสําหรับพวกเจ้าจากต้นไม้เขียวสด (แล้วจงดูซิ) พวกเจ้าก็ได้จุดมันจากเชื้อไฟนั้น');
INSERT INTO "king_fahad_thai" VALUES(36,81,'36:81','พระองค์ผู้ทรงสร้างชั้นฟ้าทั้งหลายและแผ่นดิน จะไม่ทรงสามารถที่จะสร้างเช่นเดียวกับพวกเขากระนั้นหรือ	? แน่นอน และพระองค์เป็นผู้ทรงสร้างที่ยิ่งใหญ่ ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(36,82,'36:82','แท้จริงพระบัญชาของพระองค์ เมื่อทรงประสงค์สิ่งใด พระองค์ก็จะตรัสแก่มันว่า “จงเป็น” แล้วมันก็จะเป็นขึ้นมา');
INSERT INTO "king_fahad_thai" VALUES(36,83,'36:83','ดังนั้น มหาบริสุทธิ์ยิ่งแด่พระองค์ซึ่งในพระหัตถ์ของพระองค์มีอํานาจเหนือทุกสิ่งและยังพระองค์เท่านั้นที่พวกเจ้าจะถูกนํากลับไป');
INSERT INTO "king_fahad_thai" VALUES(37,1,'37:1','ขอสาบานด้วย (มะลาอิกะฮฺ) ผู้เข้าแถวตามลําดับ');
INSERT INTO "king_fahad_thai" VALUES(37,2,'37:2','และ (มะลาอิกะฮฺ) ผู้ควบคุมอย่างรัดกุม');
INSERT INTO "king_fahad_thai" VALUES(37,3,'37:3','และ (มะลาอิกะฮฺ) ผู้อ่านขัอตักเตือน');
INSERT INTO "king_fahad_thai" VALUES(37,4,'37:4','แท้จริง พระเจ้าของพวกเจ้านั้นทรงเอกะอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(37,5,'37:5','พระเจ้าแห่งชั้นฟ้าทั้งหลายและแผ่นดิน และสิ่งที่อยู่ในระหว่างทั้งสอง และพระเจ้าแห่งทิศทางตะวันออก');
INSERT INTO "king_fahad_thai" VALUES(37,6,'37:6','แท้จริง เราได้ประดับท้องฟ้าแห่งโลกดุนยาอย่างสวยงามด้วยดวงดาวทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(37,7,'37:7','และเพื่อป้องกันจากชัยฏอนมารร้ายทุกตัวที่ดื้อรั้นพยศ');
INSERT INTO "king_fahad_thai" VALUES(37,8,'37:8','พวกมันจะไม่สามารถรับฟัง (จากมะลาอิกะฮฺผู้อยู่) ในชั้นฟ้าชั้นสูงได้ และพวกมันจะถูกขว้างจากทุกๆ ด้าน');
INSERT INTO "king_fahad_thai" VALUES(37,9,'37:9','ถูกขับไล่ใสส่งออกมา และสําหรับพวกมันนั้นจะได้รับการลงโทษอย่างต่อเนื่อง');
INSERT INTO "king_fahad_thai" VALUES(37,10,'37:10','เว้นแต่ตัวใดที่มันฉกฉวยเอาไปได้แม้แต่ครั้งเดียว ก็จะมีเปลวเพลิงอันโชติช่วงไล่ติดตามมันไป');
INSERT INTO "king_fahad_thai" VALUES(37,11,'37:11','เจ้าจงถามพวกเขา (บรรดาผู้ปฏิเสธการฟื้นคืนชีพ) ซิว่า พวกเขามีความแข็งแกร่งยิ่งในสิ่งที่ถูกสร้างมากระนั้นหรือ ? หรือว่าสิ่งที่เราได้สร้างมันมา แท้จริงเราได้สร้างพวกเขามาจากดินเหนียว');
INSERT INTO "king_fahad_thai" VALUES(37,12,'37:12','แต่เจ้าคงแปลกใจ ขณะที่พวกเขาเยาะเย้ย');
INSERT INTO "king_fahad_thai" VALUES(37,13,'37:13','และเมื่อพวกเขาถูกเตือนให้รําลึกพวกเขาก็จะไม่ยอมรับข้อตักเตือน');
INSERT INTO "king_fahad_thai" VALUES(37,14,'37:14','และเมื่อพวกเขามองเห็นสิ่งปาฏิหาริย์พวกเขาก็ชักชวนกันเยาะเย้ย');
INSERT INTO "king_fahad_thai" VALUES(37,15,'37:15','และพวกเขากล่าวว่า “นี่มิใช่อื่นใดเลย นอกจากเล่ห์กลอย่างชัดแจ้ง”');
INSERT INTO "king_fahad_thai" VALUES(37,16,'37:16','เมื่อเราตายไปแล้ว และเราได้กลายเป็นดินผงและกระดูก เราจะถูกให้ฟื้นคืนชีพอีกแน่ละหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(37,17,'37:17','แล้วบรรพบุรุษของพวกเรารุ่นก่อน ๆ นั้นด้วยหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(37,18,'37:18','จงกล่าวเถิดมุฮัมมัด “ใช่แล้ว และพวกเจ้าจะเป็นผู้อับอายขายหน้าอีกด้วย”');
INSERT INTO "king_fahad_thai" VALUES(37,19,'37:19','ความจริงมันเป็นเพียงเสียงแผดตะโกนก้องเพียงครั้งเดียว แล้วพวกเขาจะจ้องมอง');
INSERT INTO "king_fahad_thai" VALUES(37,20,'37:20','แล้วพวกเขาก็กล่าวว่า “โอ้ความหายนะแก่เรา นี่คือวันแห่งการตอบแทน”');
INSERT INTO "king_fahad_thai" VALUES(37,21,'37:21','(มะลาอิกะฮฺจะตอบว่า) “นี่คือวันแห่งการชี้ขาดตัดสิน ซึ่งพวกท่านเคยปฏิเสธมัน”');
INSERT INTO "king_fahad_thai" VALUES(37,22,'37:22','จงรวบรวมบรรดาผู้อธรรม และบรรดาสหายของพวกเขา และสิ่งที่พวกเขาเคารพสักการะ');
INSERT INTO "king_fahad_thai" VALUES(37,23,'37:23','อื่นจากอัลลอฮฺ แล้วจงแนะนําทางแก่พวกเขาไปสู่ทางแห่งนรก');
INSERT INTO "king_fahad_thai" VALUES(37,24,'37:24','และจงยับยั้งพวกเขาไว้ เพราะพวกเขาจะต้องถูกสอบสวน');
INSERT INTO "king_fahad_thai" VALUES(37,25,'37:25','(จะมีเสียงถามขึ้นว่า) “เกิดอะไรขึ้นแก่พวกเจ้า ทําไมจึงไม่ช่วยเหลือซึ่งกันและกัน”');
INSERT INTO "king_fahad_thai" VALUES(37,26,'37:26','แต่ว่าพวกเขาในวันนั้น เป็นผู้ยอมจํานนโดยสิ้นเชิง');
INSERT INTO "king_fahad_thai" VALUES(37,27,'37:27','และบางคนในหมู่พวกเขาจะหันมาหากัน ไต่ถามซึ่งกันและกัน');
INSERT INTO "king_fahad_thai" VALUES(37,28,'37:28','พวกเขากล่าวว่า “แท้จริงพวกท่านเคยเข้ามาหาเราทางด้านขวา”');
INSERT INTO "king_fahad_thai" VALUES(37,29,'37:29','พวกเขา (หัวหน้า) กล่าวว่า “เปล่าดอก ! พวกท่านต่างหากที่ไม่ยอมศรัทธา”');
INSERT INTO "king_fahad_thai" VALUES(37,30,'37:30','และเราไม่มีอํานาจใด ๆ เหนือพวกท่าน แต่ว่าพวกท่านเป็นหมู่ชนที่ดื้อรั้นต่างหาก');
INSERT INTO "king_fahad_thai" VALUES(37,31,'37:31','ดังนั้น พระดํารัสของพระเจ้าของเราจึงคู่ควรแก่เราแล้วแท้จริงเรานั้นเป็นผู้ต้องลิ้มรสอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(37,32,'37:32','เราได้แนะนําพวกท่านให้หลงผิดทั้ง ๆที่ความจริงพวกเราก็หลงผิดอยู่แล้ว');
INSERT INTO "king_fahad_thai" VALUES(37,33,'37:33','แท้จริง พวกเขาในวันนั้นย่อมมีส่วนแบ่งร่วมกันในการได้รับโทษ');
INSERT INTO "king_fahad_thai" VALUES(37,34,'37:34','แท้จริงเช่นนั้นแหละ เราได้ปฏิบัติต่อบรรดาผู้มีความผิด');
INSERT INTO "king_fahad_thai" VALUES(37,35,'37:35','เพราะว่าพวกเขาเหล่านั้นเมื่อได้มีการกล่าวแก่พวกเขาว่า ไม่มีพระเจ้าอื่นใดนอกจากอัลลอฮฺ พวกเขาก็หยิ่งผยอง');
INSERT INTO "king_fahad_thai" VALUES(37,36,'37:36','และพวกเขาจะกล่าวว่า “จะให้เราทอดทิ้งพระเจ้าต่าง ๆ ของพวกเราเพื่อนักกวีบ้า คนหนึ่งกระนั้นหรือ? “');
INSERT INTO "king_fahad_thai" VALUES(37,37,'37:37','เปล่าดอก ! เขา (มุฮัมมัด) ได้นําสัจธรรมมา และเพื่อยืนยันบรรดาร่อซูลต่างหาก');
INSERT INTO "king_fahad_thai" VALUES(37,38,'37:38','แท้จริง พวกเจ้าจะต้องลิ้มรสการลงโทษอันเจ็บปวดอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(37,39,'37:39','และพวกเจ้าจะไม่ได้รับการตอบแทนอื่นใด นอกจากสิ่งที่พวกเจ้าได้กระทําเอาไว้');
INSERT INTO "king_fahad_thai" VALUES(37,40,'37:40','เว้นแต่ปวงบ่าวของอัลลอฮฺผู้ซื่อสัตย์');
INSERT INTO "king_fahad_thai" VALUES(37,41,'37:41','ชนเหล่านั้น สําหรับพวกเขาจะได้รับปัจจัยยังชีพที่แน่นอน');
INSERT INTO "king_fahad_thai" VALUES(37,42,'37:42','ผลไม้หลากชนิด และพวกเขาก็ได้รับเกียรติ');
INSERT INTO "king_fahad_thai" VALUES(37,43,'37:43','ในสวนสวรรค์หลากหลายอันรื่นรมย์');
INSERT INTO "king_fahad_thai" VALUES(37,44,'37:44','อยู่บนเตียงหันหน้าเข้าหากัน');
INSERT INTO "king_fahad_thai" VALUES(37,45,'37:45','คนรับใช้จะวนเวียนรอบตัวพวกเขาพร้อมด้วยแก้ว (เหล้า) ที่มาจากลําธาร');
INSERT INTO "king_fahad_thai" VALUES(37,46,'37:46','(เหล้านั้น) ขาวบริสุทธิ์ อร่อยแก่บรรดาผู้ดื่ม');
INSERT INTO "king_fahad_thai" VALUES(37,47,'37:47','ในนั้นจะไม่ทําให้ปวดมึนศีรษะ และพวกเขาก็จะไม่มึนเมาจากมัน');
INSERT INTO "king_fahad_thai" VALUES(37,48,'37:48','และ ณ ที่พวกเขานั้นมีบรรดาหญิงบริสุทธิ์ ผู้ลดสายตาลงต่ํา มีดวงตาโตสวย');
INSERT INTO "king_fahad_thai" VALUES(37,49,'37:49','เสมือนหนึ่งพวกนางเป็นไข่ถูกหุ้มเปลือกเอาไว้');
INSERT INTO "king_fahad_thai" VALUES(37,50,'37:50','แล้ว (ชาวสวรรค์เหล่านั้น) บางคนในหมู่บ้านพวกเขาจะเข้ามาหากัน ไต่ถาม (ทุกข์สุข) ซึ่งกันและกัน');
INSERT INTO "king_fahad_thai" VALUES(37,51,'37:51','คนหนึ่งในหมู่บ้านพวกเขาก็กล่าวขึ้นว่า “แท้จริง ฉันมีเพื่อนคนหนึ่ง”');
INSERT INTO "king_fahad_thai" VALUES(37,52,'37:52','“เขาเคยกล่าวว่า แท้จริงท่านเป็นคนหนึ่งในหมู่ผู้เชื่อมั่น (ในวันปรโลก) จริงหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(37,53,'37:53','เมื่อเราตายไปแล้ว และเราได้กลายเป็นดินผงและกระดูก เราจะถูกตอบแทนจริงหรือ? “');
INSERT INTO "king_fahad_thai" VALUES(37,54,'37:54','เขา (ชาวสวรรค์) กล่าว (แก่เพื่อน ๆ ของเขา) ว่า “พวกท่านอยากจะมองดูไหมเล่า? “');
INSERT INTO "king_fahad_thai" VALUES(37,55,'37:55','ครั้นเมื่อเขามองลงไป ก็เห็น (เพื่อนของเขา) อยู่ท่ามกลางไฟที่ลุกโชติช่วง');
INSERT INTO "king_fahad_thai" VALUES(37,56,'37:56','เขาจึงกล่าวขึ้นว่า “ขอสาบานด้วยพระนามของอัลลอฮฺ ท่านเกือบทําให้ฉันพังพินาศ”');
INSERT INTO "king_fahad_thai" VALUES(37,57,'37:57','“และหากมิใช่ความโปรดปรานแห่งพระเจ้าของฉันแล้ว ฉันจะต้องอยู่ในหมู่ผู้ถูกนํามาลงโทษอย่างแน่นอน”');
INSERT INTO "king_fahad_thai" VALUES(37,58,'37:58','“ดังนั้น เราจะไม่ตาย');
INSERT INTO "king_fahad_thai" VALUES(37,59,'37:59','เว้นแต่การตายของเราครั้งแรกและเราจะไม่ถูกลงโทษ กระนั้นหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(37,60,'37:60','แท้จริง นี่คือความสําเร็จครั้งยิ่งใหญ่อย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(37,61,'37:61','เพื่อเยี่ยง (การตอบแทน) นี้ บรรดาผู้ต่อสู้ดิ้นรนจงต่อสู้ต่อไปเถิด');
INSERT INTO "king_fahad_thai" VALUES(37,62,'37:62','นั่นเป็นการต้อนรับที่ดีกว่า หรือว่าต้นซักกูม');
INSERT INTO "king_fahad_thai" VALUES(37,63,'37:63','แท้จริง เราได้จัดทําไว้เป็นการทดสอบแก่บรรดาผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(37,64,'37:64','แท้จริง มันเป็นต้นไม้ที่เอาออกมาจากก้นบึ้งของนรกที่มีไฟลุกโชติช่วง');
INSERT INTO "king_fahad_thai" VALUES(37,65,'37:65','ผลของมันคล้ายกับหัวของชัยฏอน');
INSERT INTO "king_fahad_thai" VALUES(37,66,'37:66','แล้วพวกเขาจะกินมัน และพวกเขาจะเติมมันให้เต็มท้อง');
INSERT INTO "king_fahad_thai" VALUES(37,67,'37:67','แล้วนอกจากนั้น พวกเขาจะได้น้ําดื่มที่ผสมจากน้ําเดือด');
INSERT INTO "king_fahad_thai" VALUES(37,68,'37:68','แล้วแท้จริงทางกลับของพวกเขานั้นย่อมไปสู่ไฟที่ลุกโชติช่วงอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(37,69,'37:69','แท้จริงพวกเขาพบบรรพบุรุษของพวกเขาอยู่ในการหลงผิด');
INSERT INTO "king_fahad_thai" VALUES(37,70,'37:70','แล้วพวกเขาก็ยังรีบเร่งเจริญรอยตามพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(37,71,'37:71','และโดยแน่นอน ส่วนมากของชนรุ่นก่อนหน้าพวกเขาได้หลงผิด');
INSERT INTO "king_fahad_thai" VALUES(37,72,'37:72','และโดยแน่นอน เราได้ส่งผู้ตักเตือนไปในหมู่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(37,73,'37:73','ดังนั้นเจ้าจงดูเถิดว่า ผลสุดท้ายของพวกที่ถูกเตือนนั้นเป็นอย่างไร ?');
INSERT INTO "king_fahad_thai" VALUES(37,74,'37:74','เว้นแต่ปวงบ่าวของอัลลอฮฺผู้ซื่อสัตย์');
INSERT INTO "king_fahad_thai" VALUES(37,75,'37:75','และโดยแน่นอนนูหฺได้ร้องขอเราดังนั้นผู้ตอบสนองช่างประเสริฐเสียนี่กระไร');
INSERT INTO "king_fahad_thai" VALUES(37,76,'37:76','และเราได้ช่วยเขาและชุมชนของเขาให้พ้นจากทุกข์ภัยอันมหันต์');
INSERT INTO "king_fahad_thai" VALUES(37,77,'37:77','และเราได้ให้ลูกหลานของเขายังคงมีชีวิตเหลืออยู่');
INSERT INTO "king_fahad_thai" VALUES(37,78,'37:78','และเราได้ปล่อยทิ้งไว้ (เกียรติคุณ) แก่เขาในกลุ่มชนรุ่นหลัง ๆ');
INSERT INTO "king_fahad_thai" VALUES(37,79,'37:79','ความศานติจงมีแด่นูหฺในหมู่ประชาชาติทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(37,80,'37:80','แท้จริง เช่นนั้นแหละเราจะตอบแทนผู้กระทําความดีทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(37,81,'37:81','แท้จริง เขา(นูหฺ) อยู่ในปวงบ่าวของเราผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(37,82,'37:82','แล้วเราได้ให้พวกอื่นจมน้ําตาย');
INSERT INTO "king_fahad_thai" VALUES(37,83,'37:83','และแท้จริง ผู้ที่ดําเนินตามแนวทางของเขานั้น คืออิบรอฮีม');
INSERT INTO "king_fahad_thai" VALUES(37,84,'37:84','เมื่อเขาได้เข้าหาพระเจ้าของเขาด้วยจิตใจที่บริสุทธิ์');
INSERT INTO "king_fahad_thai" VALUES(37,85,'37:85','เมื่อเขาได้กล่าวแก่บิดาของเขาและหมู่ชนของเขาว่า “พวกท่านเคารพภักดีอะไรกัน? “');
INSERT INTO "king_fahad_thai" VALUES(37,86,'37:86','“เพื่อความเท็จกระนั้นหรือ ที่พวกท่านปรารถนาพระเจ้าอื่นจากอัลลอฮฺ ?”');
INSERT INTO "king_fahad_thai" VALUES(37,87,'37:87','“ดังนั้น ความนึกคิดของพวกท่านที่มีต่อพระเจ้าแห่งสากลโลกจะเป็นอย่างไร ?”');
INSERT INTO "king_fahad_thai" VALUES(37,88,'37:88','เขา (อิบรอฮีม) จึงจ้องมองไปยังดวงดาวทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(37,89,'37:89','แล้วเขาก็กล่าวขึ้นว่า “แท้จริงฉันไม่สบายจริง ๆ “');
INSERT INTO "king_fahad_thai" VALUES(37,90,'37:90','ดังนั้น พวกเขาจึงหันหลังให้เขาและกลับออกไป');
INSERT INTO "king_fahad_thai" VALUES(37,91,'37:91','แล้วอิบรอฮีมก็บ่ายหน้าไปยังเจว็ดต่าง ๆ ของพวกเขา แล้วพูดว่า “พวกเจ้าไม่กิน (อาหารเหล่านี้) บ้างหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(37,92,'37:92','“ทําไมพวกเจ้าจึงไม่พูดเล่า ?”');
INSERT INTO "king_fahad_thai" VALUES(37,93,'37:93','แล้วเขาก็หันไปตีพวกมันด้วยมือขวา (ซึ่งถือขวานอยู่)');
INSERT INTO "king_fahad_thai" VALUES(37,94,'37:94','แล้วพวกเขาก็รีบวิ่งมาหาเขา');
INSERT INTO "king_fahad_thai" VALUES(37,95,'37:95','อิบรอฮีมจึงกล่าวว่า “พวกท่านเคารพภักดีสิ่งที่พวกท่านแกะสลัก (มัน) กระนั้นหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(37,96,'37:96','“ทั้ง ๆ ที่อัลลอฮฺทรงสร้างพวกท่านและสิ่งที่พวกท่านประดิษฐ์มันขึ้นมา”');
INSERT INTO "king_fahad_thai" VALUES(37,97,'37:97','พวกเขากล่าวว่า “จงสร้างสถานที่แห่งหนึ่ง (เตาเผา) สําหรับเขา แล้วโยนเขาไปในไฟที่ลุกโชน”');
INSERT INTO "king_fahad_thai" VALUES(37,98,'37:98','ดังนั้น พวกเขาต้องการวางแผนร้ายแก่เขา แต่เราได้ทําให้พวกเขาต่ําต้อย');
INSERT INTO "king_fahad_thai" VALUES(37,99,'37:99','และอิบรอฮีมกล่าวว่า “ฉันจะไปหาพระเจ้าของฉัน แน่นอนพระองค์จะทรงแนะทางให้แก่ฉัน');
INSERT INTO "king_fahad_thai" VALUES(37,100,'37:100','“ข้าแต่พรเจ้าของข้าพระองค์ ขอพระองค์ทรงประทานบุตรที่มาจากหมู่คนดีให้แก่ข้าพระองค์ด้วย”');
INSERT INTO "king_fahad_thai" VALUES(37,101,'37:101','ดังนั้น เราจึงแจ้งข่าวดีแก่เขา (ว่าจะได้) ลูกคนหนึ่ง ที่มีความอดทนขันติ (คือ อิสมาอีล)');
INSERT INTO "king_fahad_thai" VALUES(37,102,'37:102','ครั้นเมื่อเขา (อิสมาอีล) เติบโตขึ้นไปไหนมาไหนกับเขา (อิบรอฮีม) ได้แล้ว อิบรอฮีมได้กล่าวขึ้นว่า “โอ้ลูกเอ๋ย ! แท้จริงพ่อได้เห็นในขณะฝันว่า พ่อได้เชือดเจ้า จงคิดดูซิว่าเจ้าจะเห็นเป็นอย่างไร? “ เขากล่าวว่า “โอ่พ่อจ๋า! พ่อจงปฏิบัติตามที่พ่อได้ถูกบัญชามาเถิด หากอัลลอฮฺทรงประสงค์ พ่อจะเห็นฉันว่า ฉันอยู่ในหมู่ผู้มีความอดทน”');
INSERT INTO "king_fahad_thai" VALUES(37,103,'37:103','ครั้นเมื่อทั้งสอง (พ่อและลูก) ได้ยอมมอบตน (แด่อัลลอฮฺ) อิบรอฮีมได้ให้อีสมาอีลคว่ําหน้าลงกับพื้น');
INSERT INTO "king_fahad_thai" VALUES(37,104,'37:104','และเราได้เรียกเขาว่า “โอ้ อิบรอฮีม” เอ๋ย!');
INSERT INTO "king_fahad_thai" VALUES(37,105,'37:105','“แน่นอน เจ้าได้ปฏิบัติถูกต้องตามฝันแล้ว” แท้จริง เช่นนั้นแหละเราจะตอบแทนผู้กระทําความดีทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(37,106,'37:106','แท้จริง นั่นคือ การทดสอบที่ชัดแจ้งแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(37,107,'37:107','และเราได้ให้ค่าไถ่ตัวเขาด้วยสัตว์เชือดพลีอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(37,108,'37:108','และเราได้ปล่อยทิ้งไว้ (เกียรติคุณ) แก่เขาในกลุ่มชนรุ่นหลัง ๆ');
INSERT INTO "king_fahad_thai" VALUES(37,109,'37:109','ศานติจงมีแต่อิบรอฮีม');
INSERT INTO "king_fahad_thai" VALUES(37,110,'37:110','เช่นนั้นแหละ เราจะตอบแทนผู้กระทําความดีทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(37,111,'37:111','แท้จริง เขา (อิบรอฮีม) เป็นคนหนึ่งในปวงบ่าวของเราผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(37,112,'37:112','เราได้แจ้งข่าวดีแก่เขาว่า จะได้ (ลูกคนหนึ่ง) อิสฮาก เป็นนะบี (จะเป็นหนึ่ง) ในหมู่คนดีทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(37,113,'37:113','และเราได้ให้ความจําเริญแก่เขาและแก่อิสฮาก และในหมู่ลูกหลานของเขาทั้งสองนั้นมีผู้ทําความดีและมีผู้อธรรมแก่ตัวของเขาเองอย่างชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(37,114,'37:114','และโดยแน่นอน เราได้ให้ความโปรดปรานแก่มูซาและฮารูณ');
INSERT INTO "king_fahad_thai" VALUES(37,115,'37:115','และเราได้ช่วยเขาทั้งสองและหมู่ชนของเขาทั้งสอง ให้พ้นจากความเคราะห์ร้ายอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(37,116,'37:116','และเราได้ช่วยเหลือพวกเขา ดังนั้น พวกเขาจึงเป็นผู้มีชัยชนะ');
INSERT INTO "king_fahad_thai" VALUES(37,117,'37:117','และเราได้ประทานคัมภีร์แก่เขาทั้งสองซึ่งสมบูรณ์ชัดเจน (เตารอฮฺ)');
INSERT INTO "king_fahad_thai" VALUES(37,118,'37:118','และเราได้แนะนําเขาทั้งสองสู่แนวทางที่เที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(37,119,'37:119','และเราได้ปล่อยทิ้งไว้ (เกียรติคุณ) แก่เขาทั้งสองในกลุ่มชนรุ่นหลัง ๆ');
INSERT INTO "king_fahad_thai" VALUES(37,120,'37:120','ศานติจงมีแด่มูซาและฮารูณ');
INSERT INTO "king_fahad_thai" VALUES(37,121,'37:121','แท้จริง เช่นนั้นแหละเราจะตอบแทนผู้กระทําความดีทั้งหลาย');
