INSERT INTO "king_fahad_thai" VALUES(37,125,'37:125','“พวกท่านเคารพสักการะบะอฺลฺ (เจว็ด)');
INSERT INTO "king_fahad_thai" VALUES(37,126,'37:126','“อัลลอฮฺคือพระเจ้าของพวกท่านและพระเจ้าของบรรพบุรุษของพวกท่านแต่เก่าก่อน”');
INSERT INTO "king_fahad_thai" VALUES(37,127,'37:127','ดังนั้น พวกเขาจะถูกนํามาลงโทษอย่างแน่นอน');
