INSERT INTO "king_fahad_thai" VALUES(37,168,'37:168','“หากว่าเรามีข้อตักเตือน (คัมภีร์) อยู่กับเรา เช่นเดียวกับหมู่ชนในสมัยก่อน ๆ”');
