INSERT INTO "king_fahad_thai" VALUES(37,180,'37:180','มหาบริสุทธิ์ยิ่งแด่พระเจ้าของเจ้าพระเจ้าแห่งอํานาจจากสิ่งที่พวกเขากล่าวอ้าง');
INSERT INTO "king_fahad_thai" VALUES(37,181,'37:181','ศานติจงมีแด่บรรดาร่อซูลทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(37,182,'37:182','และบรรดาการสรรเสริญทั้งมวลเป็นของอัลลอฮฺ พระเจ้าแห่งสากลโลก');
INSERT INTO "king_fahad_thai" VALUES(38,1,'38:1','ศอด ขอสาบานด้วยอัลกุรอานที่เป็นเครื่องเตือนสติ');
INSERT INTO "king_fahad_thai" VALUES(38,2,'38:2','แต่ว่าบรรดาผู้ปฏิเสธศรัทธาอยู่ในการหยิ่งผยองและการแตกแยก');
INSERT INTO "king_fahad_thai" VALUES(38,3,'38:3','กี่ศตวรรษมาแล้วที่เราได้ทําลายประชาชาติก่อนพวกเขา แล้วพวกเขาได้ร้องเรียกขอความช่วยเหลือทั้ง ๆ ที่ไม่มีเวลาอีกแล้วสําหรับทางรอด');
INSERT INTO "king_fahad_thai" VALUES(38,4,'38:4','และพวกเขาประหลาดใจที่มีผู้ตักเตือนจากหมู่พวกเขามายังพวกเขา และพวกปฏิเสธศรัทธาได้กล่าวว่า “นี่คือมายากร นักโกหกตัวฉกาจ');
INSERT INTO "king_fahad_thai" VALUES(38,5,'38:5','เขาได้ทําให้พระเจ้าหลายองค์เป็นพระเจ้าองค์เดียวกระนั้นหรือ ? แท้จริงนี่เป็นเรื่องประหลาดจริง ๆ “');
INSERT INTO "king_fahad_thai" VALUES(38,6,'38:6','และพวกหัวหน้าของพวกเขาพากันออกไป (พลางกล่าวว่า) “จงก้าวหน้าต่อไปและอดทน ในการยึดมั่นต่อบรรดาพระเจ้าของพวกท่านต่อไป! แท้จริงเป็นเรื่องที่ถูกวางแผนไว้แล้ว');
INSERT INTO "king_fahad_thai" VALUES(38,7,'38:7','“เราไม่เคยได้ยินเรื่องนี้มาก่อนในศาสนาสุดท้าย นี่มิใช่อื่นใด นอกจากเป็นการเสกสรรปั้นแต่งขึ้นมาเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(38,8,'38:8','“ได้มีข้อตักเตือน (อัลกุรอาน) ถูกประทานลงมาแก่เขา แทนพวกเรากระนั้นหรือ ?” หามิได้ พวกเขาอยู่ในการสงสัยจากข้อตักเตือนของข้า ! ไม่เพียงแต่เท่านั้น พวกเขายังมิได้ลิ้มรสการลงโทษของข้า !');
INSERT INTO "king_fahad_thai" VALUES(38,9,'38:9','หรือว่า ณ ที่พวกเขามีขุมคลังแห่งความเมตตาของพระเจ้าของเจ้า ผู้ทรงอํานาจ ผู้ทรงประทานให้อย่างมากมาย');
INSERT INTO "king_fahad_thai" VALUES(38,10,'38:10','หรือว่าอํานาจแห่งชั้นฟ้าทั้งหลายและแผ่นดิน และที่อยู่ระหว่างทั้งสองเป็นของพวกเขา ดังนั้น จงให้พวกเขาขึ้นไปหาสาเหตุกันเถิด');
INSERT INTO "king_fahad_thai" VALUES(38,11,'38:11','(พวกเขา) เป็นไพร่พลจากแนวร่วมต่าง ๆ จะพ่ายแท้หนีเตลิดไปในไม่ช้านี้');
INSERT INTO "king_fahad_thai" VALUES(38,12,'38:12','ก่อนหน้าพวกเขา หมู่ชนของนูหฺและอ๊าด และฟิรเอานฺเจ้าของเสาหมุด ได้ปฏิเสธมาแล้ว');
INSERT INTO "king_fahad_thai" VALUES(38,13,'38:13','และษะมูด และหมู่ชนของลูฏ และชาวป่าทึบ ชนเหล่านี้เป็นแนวร่วม');
INSERT INTO "king_fahad_thai" VALUES(38,14,'38:14','ไม่มีคนหนึ่งคนใด (ในพวกเขา) เว้นแต่ปฏิเสธบรรดาร่อซูล ดังนั้น การลงโทษของข้าจึงเหมาะสม (แก่พวกเขา)');
INSERT INTO "king_fahad_thai" VALUES(38,15,'38:15','และพวกเขาเหล่านั้นมิได้คอยอันใด นอกจากเสียงกัมปนาทเพียงครั้งเดียว โดยไม่มีการชักช้า');
INSERT INTO "king_fahad_thai" VALUES(38,16,'38:16','และพวกเขากล่าวว่า “ข้าแต่พระเจ้าของเรา ขอทรงโปรดเร่งส่วนของเราให้แก่เราโดยเร็วเถิด ก่อนที่จะถึงวันแห่งการชําระบัญชี”');
INSERT INTO "king_fahad_thai" VALUES(38,17,'38:17','เจ้า (มุฮัมมัด) จงอดทนต่อสิ่งที่พวกเขากล่าว และจงรําลึกถึงบ่าวของเรา ดาวู๊ด ผู้ทรงพลัง (ในการปฏิบัติศาสนกิจ) แท้จริงเขาเป็นผู้ผินหน้าเข้าหาอัลลอฮฺเสมอ');
INSERT INTO "king_fahad_thai" VALUES(38,18,'38:18','แท้จริง เราได้ทําให้ภูเขาแซ่ซ้องสดุดีพร้อมกับเขา ทั้งในยามพลบค่ําและยามรุ่งอรุณ');
INSERT INTO "king_fahad_thai" VALUES(38,19,'38:19','และ (เราได้ทําให้) นกมารวมกันทั้งหมด เชื่อฟังเขา');
INSERT INTO "king_fahad_thai" VALUES(38,20,'38:20','และเราได้ทําให้อาณาจักรของเขาเข้มแข็ง และเราได้ให้ความเข้าใจที่ถูกต้องแก่เขา และการตัดสินชี้ขาดในเรื่องต่าง ๆ');
INSERT INTO "king_fahad_thai" VALUES(38,21,'38:21','และมีเรื่องของผู้โต้เถียงมาถึงเจ้า (มุฮัมมัด) บ้างไหม ? เมื่อพวกเขาปีนข้ามกําแพงไปที่มิหฺรอบ (ของดาวู๊ด)');
INSERT INTO "king_fahad_thai" VALUES(38,22,'38:22','เมื่อพวกเขาได้เข้ามาหาดาวู๊ด เขาตกใจกลัวพวกเขา พวกเขากล่าวว่า “อย่าได้กลัวเลย เราคือผู้โต้เถียงสองคนคนหนึ่งในพวกเราได้ล่วงเกินอีกคนหนึ่ง ดังนั้น ได้โปรดตัดสินระหว่างพวกเราด้วยความยุติธรรมและอย่าได้ลําเอียงไม่เที่ยงธรรม และจงชี้แนะเราสู่แนวทางที่เที่ยงตรง”');
INSERT INTO "king_fahad_thai" VALUES(38,23,'38:23','“แท้จริงนี่คือพี่ชายของฉัน เขามีแกะตัวเมีย 99 ตัว และฉันมีแกะตัวเมียตัวเดียวแล้วเขายังพูดว่า เอามันมาให้ฉันซิ และเขาได้ข่มขู่ฉันในคําพูด”');
INSERT INTO "king_fahad_thai" VALUES(38,24,'38:24','เขา (ดาวู๊ด) กล่าวว่า “แน่นอน เขาอธรรมต่อท่านในการขอให้นําแกะของท่านไปรวมกับแกะของเขา และแท้จริงส่วนมากของผู้มีหุ้นส่วนร่วมกัน บางคนในพวกเขามักละเมิดสิทธิของอีกคนหนึ่ง เว้นแต่บรรดาผู้ศรัทธาและประกอบความดีทั้งหลาย และพวกเขาเช่นนี้มีน้อย” และดาวู๊ดรู้สึกว่าเราได้ทดสอบเขา ดังนั้น เขาจึงได้ขออภัยต่อพระเจ้าของเขา และเขาได้ก้มลงรูกัวะและทบทวนความผิดด้วยความเสียใจ');
INSERT INTO "king_fahad_thai" VALUES(38,25,'38:25','ดังนั้น เราได้ให้อภัยแก่เขาในเรื่องนั้น และแท้จริง สําหรับเขานั้นย่อมอยู่ใกล้ชิด ณ ที่เรา และทางกลับที่ดียิ่ง(ในปรโลก)');
INSERT INTO "king_fahad_thai" VALUES(38,26,'38:26','โอ้ดาวู๊ดเอ๋ย ! เราได้แต่งตั้งเจ้าให้เป็นตัวแทนในแผ่นดินนี้ ดังนั้น เจ้าจงตัดสินคดีต่าง ๆ ระหว่างมนุษย์ด้วยความยุติธรรม และอย่าปฏิบัติตามอารมณ์ใฝ่ต่ํา มันจะทําให้เจ้าหลงไปจากทางของอัลลอฮฺ แท้จริงบรรดาผู้ที่หลงไปจากทางของอัลลอฮฺนั้น สําหรับพวกเขาจะได้รับการลงโทษอย่างสาหัส เนื่องด้วยพวกเขาลืมวันแห่งการชําระบัญชี');
INSERT INTO "king_fahad_thai" VALUES(38,27,'38:27','ดังนั้น ความหายนะคือไฟนรกจงประสบแก่บรรดาผู้ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(38,28,'38:28','จะให้เราปฏิบัติต่อบรรดาผู้ศรัทธาและกระทําความดีทั้งหลาย เช่นบรรดาผู้บ่อนทําลายในแผ่นดินกระนั้นหรือ?หรือว่าจะให้เราปฏิบัติต่อบรรดาผู้ยําเกรง เช่นบรรดาคนชั่วกระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(38,29,'38:29','คัมภีร์ (อัลกุรอาน) เราได้ประทานลงมาให้แก่เจ้าซึ่งมีความจําเริญ เพื่อพวกเขาจะได้พินิจพิจารณาอายาตต่าง ๆของอัลกุรอานและเพื่อปวงผู้มีสติปัญญาจะได้ใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(38,30,'38:30','เราได้ประทาน (บุตร) คือสุลัยมานบ่าวผู้ประเสริฐแก่ดาวู๊ด แท้จริงเขาหันหน้าเข้าสู่เราเสมอ');
INSERT INTO "king_fahad_thai" VALUES(38,31,'38:31','และจงรําลึกเมื่อม้าพันธุ์ดีถูกนํามาเสนอแก่เขาในยามเย็นวันหนึ่ง');
INSERT INTO "king_fahad_thai" VALUES(38,32,'38:32','เขากล่าวว่า “ แท้จริงฉันรักทรัพย์สมบัติ (หมายถึงม้า) จนมันทําให้ฉันลืมการรําลึกถึงพระเจ้าของฉัน” จนกระทั่งดวงอาทิตย์ลับขอบฟ้า');
INSERT INTO "king_fahad_thai" VALUES(38,33,'38:33','“จงนํามันกลับมาให้ฉัน” แล้วเขาก็เริ่มลูบขาและคอของมัน');
INSERT INTO "king_fahad_thai" VALUES(38,34,'38:34','และโดยแน่นอนเราได้ทดสอบสุลัยมาน และเราได้วางร่างหนึ่งไว้บนเก้าอี้ของเขา แล้วเขาก็ทบทวนความผิดด้วยความเสียใจ');
INSERT INTO "king_fahad_thai" VALUES(38,35,'38:35','เขากล่าวว่า “ข้าแต่พระเจ้าของข้าพระองค์ ขอพระองค์ทรงอภัยให้แก่ข้าพระองค์ด้วย ขอพระองค์ทรงประทานอํานาจอันกว้างขวางแก่ข้าพระองค์ ซึ่งไม่คู่ควรแก่ผู้ใดนอกจากข้าพระองค์ แท้จริงพระองค์เท่านั้นเป็นผู้ทรงประทานให้อย่างมากมาย');
INSERT INTO "king_fahad_thai" VALUES(38,36,'38:36','และเราได้ทําให้ลมพัดเฉื่อย ๆ ตามบัญชาของเขาไปยังทิศทางที่เขาต้องการ');
INSERT INTO "king_fahad_thai" VALUES(38,37,'38:37','และ (เราได้ทําให้) บรรดาชัยฏอน (อยู่ใต้คําสั่งของเขา) พวกนั้นทั้งหมดเป็นช่างก่อสร้าง และประดาน้ํา');
INSERT INTO "king_fahad_thai" VALUES(38,38,'38:38','และพวกอื่น ๆ ถูกพันธนาการด้วยโซ่ติดกัน');
INSERT INTO "king_fahad_thai" VALUES(38,39,'38:39','นี่คือการประทานให้ของเรา (แก่สุลัยมาน) ดังนั้นเจ้าจะให้แก่ใครก็ได้ หรือจะยับยั้งไม่ให้ใครก็ได้ โดยเจ้าจะไม่ถูกสอบสวน');
INSERT INTO "king_fahad_thai" VALUES(38,40,'38:40','และแท้จริง สําหรับเขานั้นย่อมอยู่ใกล้ชิด ณ ที่เรา และทางกลับที่ดียิ่ง (ในปรโลก)');
INSERT INTO "king_fahad_thai" VALUES(38,41,'38:41','และจงรําลึกถึงบ่าวของเรา อัยยู๊บ เมื่อเขาวิงวอนขอต่อพระเจ้าของเขา โดยกล่าวว่า ชัยฏอนมารร้ายได้ทําให้ฉันได้รับความเหนื่อยยาก และทุกข์ทรมาน');
INSERT INTO "king_fahad_thai" VALUES(38,42,'38:42','จงกระทืบ (แผ่นดิน) ด้วยเท้าของเจ้า นี่คือน้ําเย็นสําหรับอาบชําระล้าง และสําหรับดื่ม');
INSERT INTO "king_fahad_thai" VALUES(38,43,'38:43','และเราได้ประทานครอบครัวของเขาให้แก่เขา และเช่นเดียวกับพวกเขาพร้อมพวกเขา เป็นความเมตตาจากเราและเป็นข้อเตือนสติแด่ปวงผู้มีสติทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(38,44,'38:44','และจงเอาเศษไม้สักกําหนึ่งแล้วฟาดด้วยมัน และอย่าถอนคําสาบาน แท้จริงเราพบว่า เขา (อัยยู๊บ) เป็นผู้อดทนบ่าวผู้ประเสริฐแท้จริงเขาหันหน้าเข้าสู่เราเสมอ');
INSERT INTO "king_fahad_thai" VALUES(38,45,'38:45','และจงรําลึกถึงปวงบ่าวของเรา อิบรอฮีม อิสหาก และยะอฺกู๊บ ผู้ที่เข้มแข็งและสายตาไกล (ในเรื่องศาสนา)');
INSERT INTO "king_fahad_thai" VALUES(38,46,'38:46','เราได้เลือกพวกเขาโดยเฉพาะเพื่อเตือนให้รําลึกถึงปรโลก');
INSERT INTO "king_fahad_thai" VALUES(38,47,'38:47','และแท้จริงพวกเขานั้น ในทัศนะของเรา แน่นอนอยู่ในหมู่ผู้ได้รับเลือกเพราะพวกเขาเป็นคนดี');
INSERT INTO "king_fahad_thai" VALUES(38,48,'38:48','และจงรําลึกถึงอิสมาอีล และอัลยะซะอฺ และซัลกิฟลิ และทุกคนอยู่ในหมู่ผู้ดีเลิศ');
INSERT INTO "king_fahad_thai" VALUES(38,49,'38:49','นี่คือข้อตักเตือน และแท้จริงสําหรับบรรดาผู้ยําเกรงนั้น แน่นอนทางกลับ (ของพวกเขา) ย่อมประเสริฐแท้');
INSERT INTO "king_fahad_thai" VALUES(38,50,'38:50','คือสวนสวรรค์หลากหลายอันสถาพรประตู (ทุกบาน) จะเปิดอ้าไว้สําหรับต้อนรับพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(38,51,'38:51','นอนเอกเขนกอยู่ในสวนสวรรค์ พวกเขาจะเรียกเอาผลไม้ และเครื่องดื่มนานาชนิด');
INSERT INTO "king_fahad_thai" VALUES(38,52,'38:52','และ ณ ที่พวกเขานั้น มีหญิงบริสุทธิ์ผู้ลดสายตาลงต่ํา มีอายุรุ่นราวคราวเดียวกัน');
INSERT INTO "king_fahad_thai" VALUES(38,53,'38:53','นี่คือสิ่งที่พวกเจ้าถูกสัญญาไว้ สําหรับวันแห่งการชําระบัญชี');
INSERT INTO "king_fahad_thai" VALUES(38,54,'38:54','แท้จริง นี่คือปัจจัยยังชีพของเราอย่างแน่นอน มันจะไม่มีวันหมดสิ้น');
INSERT INTO "king_fahad_thai" VALUES(38,55,'38:55','ดังนั้น และแท้จริงสําหรับบรรดาผู้ละเมิดนั้น แน่นอนทางกลับ (ของพวกเขา) ย่อมเลวจริง ๆ');
INSERT INTO "king_fahad_thai" VALUES(38,56,'38:56','คือนรกญะฮันนัม พวกเขาจะเข้าไปเผาไหม้ในมัน ดังนั้นมันเป็นที่พํานักที่ชั่วช้ายิ่ง');
INSERT INTO "king_fahad_thai" VALUES(38,57,'38:57','นี่ (คือการลงโทษอันเจ็บแสบ) ดังนั้นพวกเจ้าจงลิ้มรสมัน (คือ) น้ําเดือดพล่าน และน้ําเลือดน้ําหนอง (ของชาวนรก)');
INSERT INTO "king_fahad_thai" VALUES(38,58,'38:58','และ (การลงโทษ) ชนิดอื่นอีก เยี่ยงการลงโทษดังกล่าวที่เท่าเทียมคู่ควรกัน');
INSERT INTO "king_fahad_thai" VALUES(38,59,'38:59','นี่คือฝูงชนวิ่งกรูกันไปพร้อมกับพวกเจ้า ไม่มีการต้อนรับพวกเขาดอก แท้จริง พวกเขาจะเข้าไปอยู่ในไฟนรก');
INSERT INTO "king_fahad_thai" VALUES(38,60,'38:60','พวกเขากล่าวว่า “แต่ว่าพวกท่านต่างหาก ไม่มีการต้อนรับพวกท่าน เพราะพวกท่านได้เตรียมตัวไว้สําหรับเรา ดังนั้นมันเป็นที่พักอันชั่วช้า');
INSERT INTO "king_fahad_thai" VALUES(38,61,'38:61','พวกเขากล่าวว่า ข้าแต่พระเจ้าของเรา ผู้ใดที่ได้เตรียมการลงโทษนี้ไว้ให้แก่เรา ขอพระองค์ได้ทรงโปรดเพิ่มการลงโทษแก่เขาเป็นสองเท่าในไฟนรก');
INSERT INTO "king_fahad_thai" VALUES(38,62,'38:62','และพวกเขากล่าวว่า มีอะไรเกิดขึ้นแก่เรา ทําไมเราจึงไม่เห็นชายอีกหลายคนที่เรานับพวกเขาว่า อยู่ในหมู่ผู้เลวทรามยิ่ง?');
INSERT INTO "king_fahad_thai" VALUES(38,63,'38:63','เนื่องเพราะเราถือ เอาพวกเขาเป็นที่เยาะเย้ยน่าขัน หรือสายตาของพวกเราคลาดเคลื่อนไปจากพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(38,64,'38:64','แท้จริง นั่นคือความจริง จะมีการโต้เถียงกันระหว่างชาวนรกด้วยกัน');
INSERT INTO "king_fahad_thai" VALUES(38,65,'38:65','จงกล่าวเถิดมุฮัมมัด แท้จริงฉันเป็นแต่ผู้ตักเตือนเท่านั้น ไม่มีพระเจ้าอื่นใดนอกจากอัลลอฮฺผู้ทรงเอกะ ผู้ทรงพิชิต');
INSERT INTO "king_fahad_thai" VALUES(38,66,'38:66','พระเจ้าแห่งชั้นฟ้าทั้งหลาย และแผ่นดิน และสิ่งที่อยู่ในระหว่างทั้งสอง ผู้ทรงอํานาจ ผู้ทรงอภัยอย่างมากหลาย');
INSERT INTO "king_fahad_thai" VALUES(38,67,'38:67','จงกล่าวเถิดมุฮัมมัด นี่คือข่าวสําคัญอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(38,68,'38:68','ที่พวกท่านผินหลังให้กับมัน');
INSERT INTO "king_fahad_thai" VALUES(38,69,'38:69','ฉันไม่เคยรู้มาก่อนเลยในเรื่องของมะลาอิกะฮฺ เมื่อพวกเขาโต้เถียงกัน');
INSERT INTO "king_fahad_thai" VALUES(38,70,'38:70','(เพราะ) มิได้มีวะฮียฺแก่ฉันนอกจากว่าฉันเป็นเพียง ผู้ตักเตือนอันชัดแจ้งเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(38,71,'38:71','จงรําลึกถึงขณะที่พระเจ้าตรัส แก่มะลาอิกะฮฺว่า แท้จริงข้าจะสร้างมนุษย์คนหนึ่งจากดิน');
INSERT INTO "king_fahad_thai" VALUES(38,72,'38:72','ดังนั้น เมื่อข้าได้ทําให้เขามีรูปร่างสมส่วน และได้เป่าวิญญาณจากข้าเข้าไปในตัวเขา ฉะนั้นพวกเจ้าจงก้มลงสุญูดต่อเขา');
INSERT INTO "king_fahad_thai" VALUES(38,73,'38:73','แล้วมะลาอิกะฮฺทั้งมวลก็ได้ก้มลงสุญูด');
INSERT INTO "king_fahad_thai" VALUES(38,74,'38:74','นอกจากอิบลีส มันเย่อหยิ่งจองหองและมันอยู่ในหมู่ผู้ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(38,75,'38:75','พระองค์ตรัสว่า “อิบลีสเอ๋ย อะไรเล่าที่ขัดขวางเจ้ามิให้เจ้าสุญูดต่อสิ่งที่ข้าได้สร้างด้วยมือทั้งสองของข้า ? เจ้าเย่อหยิ่งจองหองนักหรือ หรือว่าเจ้าอยู่ในหมู่ผู้สูงส่ง');
INSERT INTO "king_fahad_thai" VALUES(38,76,'38:76','มันกล่าวว่า “ข้าพระองค์ดีกว่าเขา พระองค์ทรงสร้างข้าพระองค์จากไฟ และทรงสร้างเขาจากดิน');
INSERT INTO "king_fahad_thai" VALUES(38,77,'38:77','พระองค์ตรัสว่า “ดังนั้นเจ้าจงออกไปจากที่นี่ เพราะแท้จริงเจ้าเป็นผู้ถูกขับไล่”');
INSERT INTO "king_fahad_thai" VALUES(38,78,'38:78','และแท้จริงการสาปแช่งของข้าจงประสบแก่เจ้าจนกระทั่งวันแห่งการตอบแทน');
INSERT INTO "king_fahad_thai" VALUES(38,79,'38:79','มันกล่าวว่า “โอ้ พระเจ้าของข้า พระองค์ได้โปรดประวิงเวลาให้แก่ข้าพระองค์จนถึงวันฟื้นคืนชีพด้วยเถิด”');
INSERT INTO "king_fahad_thai" VALUES(38,80,'38:80','พระองค์ตรัสว่า “ดังนั้น แท้จริงเจ้าอยู่ในหมู่ผู้ถูกประวิงเวลา”');
INSERT INTO "king_fahad_thai" VALUES(38,81,'38:81','จนกระทั่งถึงวันแห่งเวลาที่ถูกกําหนดไว้แล้ว');
INSERT INTO "king_fahad_thai" VALUES(38,82,'38:82','มันกล่าวว่า ดังนั้นด้วยพระอํานาจของพระองค์ท่าน แน่นอนข้าพระองค์ก็จะทําให้พวกเขาทั้งหมดหลงผิด');
INSERT INTO "king_fahad_thai" VALUES(38,83,'38:83','เว้นแต่ปวงบ่าวของพระองค์ในหมู่พวกเขาที่มีใจบริสุทธิ์เท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(38,84,'38:84','พระองค์ตรัสว่า “ดังนั้นมันเป็นความจริง และข้าจะกล่าวแต่ความจริงเท่านั้น”');
INSERT INTO "king_fahad_thai" VALUES(38,85,'38:85','แน่นอน ข้าจะให้นรกนั้นเต็มไปด้วยพวกเจ้า และจากผู้ที่เชื่อฟังเจ้าในหมู่พวกเขาทั้งหมด');
INSERT INTO "king_fahad_thai" VALUES(38,86,'38:86','จงกล่าวเถิดมุฮัมมัดว่า ฉันมิได้ขอรางวัลค่าตอบแทนจากพวกท่านในการทําหน้าที่นี้แต่อย่างใด และฉันก็มิได้อยู่ในหมู่ผู้หลอกลวงอ้างสิทธิเลย');
INSERT INTO "king_fahad_thai" VALUES(38,87,'38:87','มันมิใช่อื่นใดนอกจากเป็นการตักเตือนแก่ปวงมนุษย์');
INSERT INTO "king_fahad_thai" VALUES(38,88,'38:88','และแน่นอน เจ้าจะรู้ข่าวคราวของอัลกุรอานในระยะเวลาอันใกล้นี้');
INSERT INTO "king_fahad_thai" VALUES(39,1,'39:1','คัมภีร์นี้เป็นการประทานลงมาจากอัลลอฮฺ ผู้ทรงอํานาจ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(39,2,'39:2','แท้จริงเราได้ประทานคัมภีร์มายังเจ้าด้วยสัจธรรม ดังนั้นเจ้าจงเคารพภักดีต่ออัลลอฮฺ โดยเป็นผู้มีความบริสุทธิ์ใจในศาสนาต่อพระองค์');
INSERT INTO "king_fahad_thai" VALUES(39,3,'39:3','พึงทราบเถิด การอิบาดะฮฺโดยบริสุทธิ์ใจนั้นเป็นของอัลลอฮฺองค์เดียว ส่วนบรรดาผู้ที่ยึดถือเอาบรรดาผู้คุ้มครองอื่นจากอัลลอฮฺ โดยกล่าวว่าเรามิได้เคารพภักดีพวกเขา เว้นแต่เพื่อทําให้เราเข้าใกล้ชิดต่ออัลลอฮฺ แท้จริงอัลลอฮฺจะทรงตัดสินระหว่างพวกเขาในสิ่งที่พวกเขาขัดแย้งกันในเรื่องนั้น แท้จริงอัลลอฮฺจะไม่ทรงชี้นําทางแก่ผู้กล่าวเท็จ ผู้ไม่สํานึกบุญคุณ');
INSERT INTO "king_fahad_thai" VALUES(39,4,'39:4','หากอัลลอฮฺทรงประสงค์จะมีพระโอรส แน่นอนพระองค์จะทรงเลือกจากสิ่งที่พระองค์ทรงสร้างมาตามที่พระองค์ทรงประสงค์มหาบริสุทธิ์แห่งพระองค์ท่าน พระองค์คืออัลลอฮฺ ผู้ทรงเอกะ ผู้ทรงพิชิต');
INSERT INTO "king_fahad_thai" VALUES(39,5,'39:5','พระองค์ทรงสร้างชั้นฟ้าทั้งหลายและแผ่นดินด้วยความจริงอันชัดแจ้ง พระองค์ทรงให้กลางคืนคาบเกี่ยวเข้าไปในกลางวันและทรงให้กลางวันคาบเกี่ยวเข้าไปในกลางคืน และทรงให้ดวงอาทิตย์และดวงจันทร์เป็นประโยชน์ (แก่มนุษย์) ทุกสิ่งโคจรไปตามวาระที่ได้กําหนดไว้ พึงทราบเถิด พระองค์เป็นผู้ทรงอํานาจ ผู้ทรงอภัยอย่างมาก');
INSERT INTO "king_fahad_thai" VALUES(39,6,'39:6','พระองค์ทรงสร้างพวกเจ้าจากชีวิตหนึ่ง แล้วจากชีวิตนั้นทรงทําให้เป็นของคู่ครองของมัน และทรงประทานปศุสัตว์แปดตัวเป็นคู่แก่พวกเจ้า พระองค์ทรงสร้างพวกเจ้าในครรภ์ของมารดาพวกเจ้า เป็นการบังเกิดครั้งแล้วครั้งเล่าอยู่ในความมืดสามชั้น นั่นคืออัลลอฮฺ พระเจ้าของพวกเจ้า พระอํานาจเป็นสิทธิของพระองค์ ไม่มีพระเจ้าอื่นใดนอกจากพระองค์ แล้วทําไมพวกเจ้าจึงผินหน้าไปทางอื่น!');
INSERT INTO "king_fahad_thai" VALUES(39,7,'39:7','หากพวกเจ้าปฏิเสธศรัทธา ดังนั้น แท้จริงอัลลอฮฺนั้น ทรงพอเพียงจากพวกเจ้าและจะไม่ทรงปิติยินดีต่อการเนรคุณของปวงบ่าวของพระองค์ และหากพวกเจ้ากตัญญู พระองค์ก็จะทรงปิติยินดีต่อพวกเจ้า และไม่มีผู้แบกภาระคนใดที่จะแบกภาระของผู้อื่นได้ แล้วยังพระเจ้าของพวกเจ้าคือการกลับของพวกเจ้า และพระองค์ก็จะทรงบอกเล่าพวกเจ้าในสิ่งที่พวกเจ้าได้กระทําไว้แท้จริงพระองค์นั้นเป็นผู้ทรงรอบรู้สิ่งที่อยู่ในทรวงอก');
INSERT INTO "king_fahad_thai" VALUES(39,8,'39:8','และเมื่อทุกขภัยใด ๆ ประสบแก่มนุษย์ เขาก็จะวิงวอนขอต่อพระเจ้าของเขาเป็นผู้หันหน้าเข้าสู่พระองค์อย่างนอบน้อม ครั้นเมื่อพระองค์ทรงประทานความโปรดปรานจากพระองค์ให้แก่เขา เขาก็ลืมสิ่งที่เขาได้เคยวิงวอนขอต่อพระองค์มาแต่ก่อน และเขาได้ตั้งภาคีคู่เคียงกับอัลลอฮฺเพื่อให้หลงจากทางของอัลลอฮฺ จงกล่าวเถิดมุฮัมมัด ท่านจงร่าเริงเพียงระยะหนึ่งต่อการปฏิเสธของท่านเถิด แท้จริงท่านนั้นอยู่ในหมู่ชาวนรก');
INSERT INTO "king_fahad_thai" VALUES(39,9,'39:9','ผู้ที่เขาเป็นผู้ภักดีในยามค่ําคืน ในสภาพของผู้สุญูด และผู้ยืนละหมาดโดยที่เขาหวั่นเกรงต่อโลกอาคิเราะฮฺ และหวังความเมตตาของพระเจ้าของเขา (จะเหมือนกับผู้ที่ตั้งภาคีต่ออัลลอฮฺกระนั้นหรือ?) จงกล่าวเถิดมุฮัมมัด บรรดาผู้รู้และบรรดาผู้ไม่รู้จะเท่าเทียมกันหรือ? แท้จริงบรรดาผู้มีสติปัญญาเท่านั้นที่จะใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(39,10,'39:10','จงกล่าวเถิดมุฮัมมัด โอ้ปวงบ่าวผู้ศรัทธาทั้งหลายเอ๋ย! จงยําเกรงพระเจ้าของพวกท่านเถิด สําหรับบรรดาผู้ทําความดีในโลกนี้คือ (จะได้รับการตอบแทน) ความดีและแผ่นดินของอัลลอฮฺนั้นกว้างใหญ่ไพศาล แท้จริงบรรดาผู้อดทนนั้นจะได้รับการตอบแทนรางวัลของพวกเขาอย่างสมบูรณ์โดยไม่ต้องคํานวณ');
INSERT INTO "king_fahad_thai" VALUES(39,11,'39:11','จงกล่าวเถิดมุฮัมมัด แท้จริงฉันได้ถูกบัญชาให้เคารพภักดีต่ออัลลอฮฺ โดยเป็นผู้มีความบริสุทธิ์ใจในศาสนาต่อพระองค์');
INSERT INTO "king_fahad_thai" VALUES(39,12,'39:12','และฉันได้ถูกบัญชาให้ฉันเป็นคนแรกของปวงชนผู้นอบน้อม');
INSERT INTO "king_fahad_thai" VALUES(39,13,'39:13','จงกล่าวเถิดมุฮัมมัด แท้จริงฉันกลัวการลงโทษแห่งวันอันยิ่งใหญ่ หากฉันฝ่าฝืนพระเจ้าของฉัน');
INSERT INTO "king_fahad_thai" VALUES(39,14,'39:14','จงกล่าวเถิด เฉพาะอัลลอฮฺเท่านั้นที่ฉันเคารพภักดีโดยเป็นผู้มีความบริสุทธิ์ใจในศาสนาของฉันต่อพระองค์');
INSERT INTO "king_fahad_thai" VALUES(39,15,'39:15','ดังนั้นพวกท่านจงสักการะบูชาตามที่พวกท่านประสงค์อื่นจากพระองค์เถิด จงกล่าวเถิดว่า แท้จริงบรรดาผู้ขาดทุนนั้นคือ บรรดาผู้ที่ทําตัวของพวกเขาเอง และครอบครัวของพวกเขาให้ขาดทุนในวันกิยามะฮฺ พึงรู้เถิดว่านั่นคือ การขาดทุนอย่างชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(39,16,'39:16','สําหรับพวกเขานั้นมีชั้นของเปลวไฟนรกปกคลุมเหนือพวกเขา และเบื้องล่างของพวกเขาก็มีชั้นของเปลวไฟนรกอยู่ด้วย สิ่งนั้นแหละที่อัลลอฮฺทรงทําให้ปวงบ่าวของพระองค์กลัว โอ้ปวงบ่าวของข้าเอ๋ย ! จงยําเกรงต่อข้าเถิด');
INSERT INTO "king_fahad_thai" VALUES(39,17,'39:17','และบรรดาผู้ที่หลีกหนีให้ห่างจากพวกเจว็ดเพื่อที่จะไม่สักการะบูชามัน และหันไปจงรักภักดีต่ออัลลอฮฺ สําหรับพวกเขานั้นมีข่าวดีดังนั้นเจ้าจงแจ้งข่าวดีแก่ปวงบ่าวของข้า');
INSERT INTO "king_fahad_thai" VALUES(39,18,'39:18','บรรดาผู้ที่สดับฟังคํากล่าว แล้วปฏิบัติตามที่ดีที่สุดของมัน ชนเหล่านี้คือบรรดาผู้ที่อัลลอฮฺทรงชี้แนะแนวทางที่ถูกต้องแก่พวกเขาและชนเหล่านี้พวกเขาคือผู้ที่มีสติปัญญาใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(39,19,'39:19','ดังนั้นผู้ที่คํามั่นสัญญาแห่งการลงโทษได้คู่ควรแก่เขาแล้ว (เจ้าสามารถจะฮิดายะฮฺให้แก่เขา) กระนั้นหรือ? และเจ้าจะช่วยผู้ที่อยู่ในนรกให้รอดพ้นได้หรือ ?');
INSERT INTO "king_fahad_thai" VALUES(39,20,'39:20','แต่บรรดาผู้ยําเกรงพระเจ้าของพวกเขานั้น สําหรับพวกเขาจะมีคฤหาสน์สง่าโอ่โถงเหนือขึ้นไปอีกก็มีคฤหาสน์สง่าโอ่โถงสร้างไว้ ณ เบื้องล่างของมันมีลําน้ําหลายสายไหลผ่าน (มันเป็น) ข้อสัญญาของอัลลอฮฺ อัลลอฮฺนั้นจะไม่ทรงบิดพริ้วสัญญา');
INSERT INTO "king_fahad_thai" VALUES(39,21,'39:21','เจ้ามิเห็นดอกหรือว่า แท้จริงอัลลอฮฺทรงหลั่งน้ําลงมาจากฟากฟ้า แล้วทรงให้มันไหลซึมลงไปในแผ่นดินเป็นตาน้ําด้วยน้ํานั้นทรงให้พืชงอกออกมาหลายสี แล้วมันก็จะเหี่ยวแห้ง ดังนั้น เจ้าจะเห็นมันกลายเป็นสีเหลือง แล้วพระองค์ทรงทําให้มันเป็นเศษเป็นชิ้น แท้จริงในการนั้นย่อมเป็นข้อเตือนสติแก่ผู้มีสติปัญญาทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(39,22,'39:22','ผู้ใดที่อัลลอฮฺทรงเปิดทรวงอกของเขาเพื่ออิสลาม และเขาอยู่บนแสงสว่างจากพระเจ้าของเขา (จะเหมือนกับผู้ที่หัวใจบอดกระนั้นหรือ?) ดังนั้นความวิบัติจงประสบแด่ผู้ที่หัวใจของพวกเขาแข็งกระด้างต่อการรําลึกถึงอัลลอฮฺ ชนเหล่านี้อยู่ในการหลงผิดอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(39,23,'39:23','อัลลอฮฺได้ทรงประทานคํากล่าวที่ดียิ่งลงมาเป็นคัมภีร์คล้องจองกันกล่าวซ้ํากัน ผิวหนังของบรรดาผู้ที่เกรงกลัวพระเจ้าของพวกเขาจะลุกชันขึ้น แล้วผิวหนังของพวกเขาและหัวใจของพวกเขาจะสงบลงเพื่อรําลึกถึงอัลลอฮฺ นั่นคือการชี้นําทางของอัลลอฮฺ พระองค์จะทรงชี้นําทางแก่ผู้ที่พระองค์ทรงประสงค์และผู้ใดที่อัลลอฮฺทรงให้เขาหลงทาง ดังนั้นสําหรับเขาจะไม่มีผู้ชี้นําทาง');
INSERT INTO "king_fahad_thai" VALUES(39,24,'39:24','ดังนั้นผู้ใดที่ป้องกันใบหน้าของเขาให้พ้นจากการลงโทษอันชั่วช้าในวันกิยามะฮฺ (จะเหมือนกับผู้ที่ปลอดภัยจากการลงโทษกระนั้นหรือ?) และจะมีเสียงกล่าวแก่บรรดาผู้อธรรมว่า จงลิ้มรสสิ่งที่พวกเจ้าแสวงหาไว้เถิด');
INSERT INTO "king_fahad_thai" VALUES(39,25,'39:25','บรรดาหมู่ชนก่อนหน้าพวกเขาได้ปฏิเสธมาแล้ว ดังนั้นการลงโทษได้มีมายังพวกเขา โดยที่พวกเขาไม่รู้สึกตัว');
INSERT INTO "king_fahad_thai" VALUES(39,26,'39:26','ดังนั้น อัลลอฮฺทรงให้พวกเขาลิ้มรสความอัปยศในชีวิตของโลกนี้ และแน่นอนการลงโทษในปรโลกนั้นยิ่งใหญ่กว่า หากพวกเขาได้รู้');
INSERT INTO "king_fahad_thai" VALUES(39,27,'39:27','และโดยแน่นอน เราได้ยกไว้ในทุก ๆ อุทาหรณ์ในอัลกุรอานนี้สําหรับมนุษย์เพื่อพวกเขาจะได้ใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(39,28,'39:28','กุรอานเป็นภาษาอาหรับ ไม่มีการคดเคี้ยวเพื่อพวกเขาจะได้ยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(39,29,'39:29','อัลลอฮฺทรงยกอุทาหรณ์ชายคนหนึ่งเป็นของหุ้นส่วนหลายคน พวกเขาขัดแย้งไม่ลงรอยกัน และชายอีกคนหนึ่งเป็นของชายคนหนึ่งโดยเฉพาะ ทั้งสองนี้จะเป็นอุทาหรณ์ที่เท่าเทียมกันหรือ? การสรรเสริญทั้งมวลเป็นของอัลลอฮฺ แต่ว่าส่วนมากของพวกเขาไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(39,30,'39:30','แท้จริงเจ้าจะต้องตาย และแท้จริงพวกเขาจะต้องตาย');
INSERT INTO "king_fahad_thai" VALUES(39,31,'39:31','แล้วแท้จริงพวกเจ้าในวันกิยามะฮฺจะถกเถียงกันต่อหน้าพระเจ้าของพวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(39,32,'39:32','ดังนั้น ผู้ใดเล่าที่จะอธรรมยิ่งไปกว่าผู้กล่าวเท็จต่ออัลลอฮฺ และปฏิเสธความจริงเมื่อมันได้มีมายังเขา มิใช่ในนรกดอกหรือที่เป็นที่พํานักสําหรับบรรดาผู้ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(39,33,'39:33','ส่วนผู้ที่นําความจริงมา และเขาได้เชื่อมั่นความจริงนั้น ชนเหล่านี้ พวกเขาคือบรรดาผู้ยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(39,34,'39:34','นั่นคือการตอบแทนของบรรดาผู้กระทําความดี');
INSERT INTO "king_fahad_thai" VALUES(39,35,'39:35','เพื่อที่อัลลอฮฺจะทรงลบล้างความชั่วที่พวกเขากระทําไว้ออกจากพวกเขา และจะทรงตอบแทนรางวัลของพวกเขาแก่พวกเขาด้วยสิ่งที่ดียิ่งตามที่พวกเขาได้กระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(39,36,'39:36','อัลลอฮฺจะมิทรงเป็นผู้พอเพียงแก่บ่าวของพระองค์ดอกหรือ และพวกเขายังขู่เจ้าให้กลัวด้วยเจว็ดต่าง ๆ อื่นจากพระองค์ และผู้ใดที่อัลลอฮฺทรงให้เขาหลงทาง ดังนั้นสําหรับเขาจะไม่มีผู้ชี้นําทาง');
INSERT INTO "king_fahad_thai" VALUES(39,37,'39:37','และผู้ใดที่อัลลอฮฺทรงชี้นําทาง ดังนั้นก็ไม่มีผู้ใดจะทําให้เขาหลงทางได้ อัลลอฮฺมิใช่เป็นผู้ทรงอํานาจ ผู้ทรงตอบโต้อย่างเด็ดขาดดอกหรือ');
INSERT INTO "king_fahad_thai" VALUES(39,38,'39:38','และถ้าเจ้าถามพวกเขาว่า ใครเป็นผู้สร้างชั้นฟ้าทั้งหลายและแผ่นดิน แน่นอนพวกเขาจะกล่าวว่าอัลลอฮฺ จงกล่าวเถิดมุฮัมมัดพวกท่านไม่เห็นดอกหรือว่า สิ่งที่พวกท่านวิงวอนขออื่นจากอัลลอฮฺนั้น หากอัลลอฮฺทรงประสงค์จะให้มีความทุกข์ยากแก่ฉันแล้วพวกมันจะปลดเปลื้องความทุกข์ยากของพระองค์ได้ไหม? หรือหากพระองค์ประสงค์จะให้ความเมตตาแก่ฉันพวกมันจะยับยั้งความเมตตาของพระองค์ได้ไหม? จงกล่าวเถิดมุฮัมมัด อัลลอฮฺทรงพอเพียงแก่ฉันแล้ว แต่พระองค์เท่านั้น บรรดาผู้มอบความไว้วางใจจะให้ความไว้วางใจ');
INSERT INTO "king_fahad_thai" VALUES(39,39,'39:39','จงกล่าวเถิดมุฮัมมัด โอ้หมู่ชนของฉันเอ๋ย! จงทํางานตามสภาพของพวกท่าน แท้จริงฉันก็เป็นผู้ทํางาน แล้วพวกท่านจะได้รู้');
INSERT INTO "king_fahad_thai" VALUES(39,40,'39:40','ผู้ที่การลงโทษจะมีมายังเขาก็จะทําให้เขาอัปยศ และการลงโทษตลอดกาลจะประสบแก่เขา');
INSERT INTO "king_fahad_thai" VALUES(39,41,'39:41','แท้จริงเราได้ประทานคัมภีร์แก่เจ้าเพื่อมนุษยชาติด้วยสัจธรรม ดังนั้นผู้ใดปฏิบัติตามแนวทางที่ถูกต้องก็จะได้แก่ตัวของเขาเองและผู้ใดหลงทางเขาก็จะหลงอยู่บนทางที่ผิดและเจ้ามิได้เป็นผู้รับผิดชอบต่อพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(39,42,'39:42','แท้จริงในการนั้น แน่นอนย่อมเป็นสัญญาสําหรับหมู่ชนผู้ใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(39,43,'39:43','หรือว่าพวกเจ้าได้ยึดเอาบรรดาผู้ช่วยเหลืออื่นจากอัลลอฮฺ จงกล่าวเถิดมุฮัมมัดทั้ง ๆ ที่พวกมันมิได้มีอํานาจใด ๆ และพวกมันก็ไม่มีสติปัญญากระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(39,44,'39:44','แล้วพวกท่านจะถูกนํากลับไปยังพระองค์');
INSERT INTO "king_fahad_thai" VALUES(39,45,'39:45','และเมื่อ (พระนาม) อัลลอฮฺถูกกล่าวเพียงพระองค์เดียว จิตใจของบรรดาผู้ไม่ศรัทธาต่อวันอาคิเราะฮฺก็รังเกียจ แต่เมื่อบรรดาเจว็ดถูกกล่าวนอกจากพระองค์ เมื่อนั้นพวกเขาก็ดีใจ');
INSERT INTO "king_fahad_thai" VALUES(39,46,'39:46','จงกล่าวเถิดมุฮัมมัด ข้าแต่อัลลอฮฺพระผู้สร้างชั้นฟ้าทั้งหลายและแผ่นดิน พระผู้ทรงรอบรู้ในสิ่งเร้นลับ และสิ่งเปิดเผย พระองค์ท่านจะทรงตัดสินระหว่างปวงบ่าวของพระองค์ในสิ่งที่พวกเขาขัดแย้งกันอยู่');
INSERT INTO "king_fahad_thai" VALUES(39,47,'39:47','และหากว่าบรรดาผู้อธรรมมีสิ่งที่มีอยู่ในแผ่นดินนี้ทั้งหมด และมีเยี่ยงนั้นอีกด้วยแน่นอนพวกเขาจะขอไถ่ด้วยสิ่งนั้นให้พ้นจากการลงโทษที่ชั่วร้ายในวันกิยามะฮฺ แต่สิ่งที่พวกเขาไม่เคยคาดคิดมาก่อนเลยว่าสิ่งที่มาจากอัลลอฮฺนั้นจะปรากฏขึ้นแก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(39,48,'39:48','และความชั่วทั้งหลายที่พวกเขาได้กระทําไว้ก็จะปรากฏขึ้นต่อหน้าพวกเขา และสิ่งที่พวกเขาได้เคยเยาะเย้ยไว้นั้นก็ห้อมล้อมพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(39,49,'39:49','ครั้นเมื่อทุกขภัยใดประสบแก่มนุษย์เขาก็จะวิงวอนขอเรา ต่อมาเมื่อเราได้ประทานความโปรดปรานจากเราแก่เขา เขาก็กล่าวว่าแท้จริงสิ่งที่ฉันได้รับมานั้นเนื่องจากความรอบรู้ของฉันต่างหาก แต่ (เขาหารู้ไม่ว่า) มันคือการทดสอบ แต่ว่าส่วนมากของพวกเขาไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(39,50,'39:50','โดยแน่นอน บรรดาหมู่ชนก่อนหน้าพวกเขาได้กล่าวมันไว้ เช่นนี้ ดังนั้นสิ่งที่พวกเขาได้กระทําไว้นั้นหาได้อํานวยประโยชน์แก่พวกเขาไม่');
INSERT INTO "king_fahad_thai" VALUES(39,51,'39:51','ฉะนั้นความชั่วทั้งหลายที่เขาได้กระทําไว้ จึงประสบแก่พวกเขา และบรรดาผู้อธรรมจากหมู่ชนเหล่านั้น ความชั่วทั้งหลายที่พวกเขาได้กระทําไว้ก็จะประสบแก่พวกเขาเช่นกันและพวกเขาไม่สามารถจะหนีรอดพ้นไปได้');
INSERT INTO "king_fahad_thai" VALUES(39,52,'39:52','พวกเขาไม่รู้ดอกหรือว่า อัลลอฮฺทรงแผ่ปัจจัยยังชีพแก่ผู้ที่พระองค์ทรงประสงค์ และทรงให้คับแคบ แท้จริงในการนั้นย่อมเป็นสัญญาณมากหลายสําหรับหมู่ชนผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(39,53,'39:53','จงกล่าวเถิดมุฮัมมัด ปวงบ่าวของข้าเอ๋ย! บรรดาผู้ละเมิดต่อตัวของพวกเขาเอง พวกท่านอย่าได้หมดหวังต่อพระเมตตาของอัลลอฮฺ แท้จริงอัลลอฮฺนั้นทรงอภัยความผิดทั้งหลายทั้งมวล แท้จริงพระองค์นั้นเป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(39,54,'39:54','และจงผินหน้าไปหาพระเจ้าของพวกท่าน และจงนอบน้อมต่อพระองค์ ก่อนที่การลงโทษจะมายังพวกท่าน แล้วพวกท่านจะมิได้รับความช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(39,55,'39:55','และจงปฏิบัติตามสิ่งที่ดียิ่งที่ได้ถูกประทานลงมายังพวกท่าน จากพระเจ้าของพวกท่านก่อนที่การลงโทษจะมายังพวกท่านโดยฉับพลัน โดยที่พวกท่านไม่รู้สึกตัว');
INSERT INTO "king_fahad_thai" VALUES(39,56,'39:56','มิฉะนั้น ชีวิตหนึ่งจะกล่าวว่า โอ้ความหายนะจงประสบแก่ข้าพระองค์ ที่ข้าพระองค์ทอดทิ้ง (หน้าที่) ที่มีต่ออัลลอฮฺ และข้าพระองค์เคยอยู่ในหมู่ผู้เยาะเย้ยอีกด้วย');
INSERT INTO "king_fahad_thai" VALUES(39,57,'39:57','หรือมัน (ชีวิต) จะกล่าวว่า หากอัลลอฮฺทรงชี้แนะทางแก่ข้าพระองค์ แน่นอนข้าพระองค์ก็จะอยู่ในหมู่ผู้ยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(39,58,'39:58','หรือมัน (ชีวิต) จะกล่าวขณะที่เห็นการลงโทษว่า มาตรว่า ข้าพระองค์มีโอกาสกลับ (ไปสู่โลกดุนยา) อีกครั้งหนึ่ง ดังนั้นข้าพระองค์ก็จะได้อยู่ในหมู่ผู้กระทําความดี');
INSERT INTO "king_fahad_thai" VALUES(39,59,'39:59','(พระองค์ตรัสว่า) เปล่าเลย! แน่นอนสัญญาณทั้งหลายของเขาได้มายังเจ้าแล้ว แต่เจ้าได้ปฏิเสธมัน และเจ้าได้หยิ่งยะโส และเจ้าได้อยู่ในหมู่ผู้ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(39,60,'39:60','และวันกิยามะฮฺ เจ้าจะเห็นบรรดาผู้ที่กล่าวเท็จต่ออัลลอฮฺ ใบหน้าของพวกเขาดําคล้ํา ดังนั้น ที่พํานักสําหรับบรรดาผู้หยิ่งยะโสนั้นมิใช่นรกดอกหรือ?');
INSERT INTO "king_fahad_thai" VALUES(39,61,'39:61','และอัลลอฮฺจะทรงให้บรรดาผู้ยําเกรงรอดพ้น เพราะชัยชนะของพวกเขา (โดยที่) ความชั่วร้ายจะไม่ประสบแก่พวกเขา และพวกเขาจะไม่เศร้าโศกเสียใจ');
INSERT INTO "king_fahad_thai" VALUES(39,62,'39:62','อัลลอฮฺ คือผู้ทรงสร้างทุกสิ่ง และพระองค์เป็นผู้ทรงดูแลและคุ้มครองทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(39,63,'39:63','การควบคุมดูแลกิจการแห่งชั้นฟ้าทั้งหลายและแผ่นดินเป็นสิทธิของพระองค์ และบรรดาผู้ปฏิเสธสัญญาณทั้งหลายของอัลลอฮฺชนเหล่านั้นพวกเขาเป็นผู้ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(39,64,'39:64','จงกล่าวเถิดมุฮัมมัด พวกท่านใช้ให้ฉันเคารพสักการะสิ่งอื่นจากอัลลอฮฺกระนั้นหรือ? โอ้ปวงบ่าวผู้บัดซบเอ๋ย!');
INSERT INTO "king_fahad_thai" VALUES(39,65,'39:65','และโดยแน่นอน ได้มีวะฮียฺมายังเจ้า (มุฮัมมัด) และมายังบรรดานะบีก่อนหน้าเจ้าหากเจ้าตั้งภาคี (กับอัลลอฮฺ) แน่นอนการงานของเจ้าก็จะไร้ผล และแน่นอนเจ้าจะอยู่ในหมู่ผู้ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(39,66,'39:66','แต่ว่าจงเคารพภักดีต่ออัลลอฮฺ และจงอยู่ในหมู่ผู้กตัญญู');
INSERT INTO "king_fahad_thai" VALUES(39,67,'39:67','และพวกเขามิได้ให้ความยิ่งใหญ่แด่อัลลอฮฺอันพึงมีต่อพระองค์อย่างแท้จริง และแผ่นดินนี้ทั้งหมดเป็นเพียงกําพระหัตถ์หนึ่งของพระองค์ในวันกิยามะฮฺ และชั้นฟ้าทั้งหลายจะม้วนกลิ้งด้วยพระหัตถ์ขวาของพระองค์มหาบริสุทธิ์ยิ่งแด่พระองค์ และพระองค์ทรงสูงส่งเหนือจากสิ่งที่พวกเขาตั้งภาคี');
INSERT INTO "king_fahad_thai" VALUES(39,68,'39:68','และสังข์ได้ถูกเป่าขึ้น แล้วบรรดาผู้ที่อยู่ในชั้นฟ้าทั้งหลาย และแผ่นดินจะล้มลงตายเว้นแต่ผู้ที่อัลลอฮฺประสงค์ แล้วสังข์ได้ถูกเป่าขึ้นอีกครั้งหนึ่ง แล้วพวกเขาก็ลุกขึ้นยืนมองดู');
INSERT INTO "king_fahad_thai" VALUES(39,69,'39:69','และแผ่นดินจะเป็นประกายด้วยรัศมีแห่งพระเจ้าของมัน และบันทึกจะถูกกางแผ่และบรรดานะบี และบรรดาพยานจะถูกนํามาและจะถูกตัดสินระหว่างพวกเขาด้วยความยุติธรรมและพวกเขาจะไม่ถูกอยุติธรรม');
INSERT INTO "king_fahad_thai" VALUES(39,70,'39:70','และทุกชีวิตจะได้รับการตอบแทนอย่างครบครันตามที่มันได้กระทําไว้ และพระองค์ทรงรอบรู้ดียิ่งในสิ่งที่พวกเขาได้กระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(39,71,'39:71','และบรรดาผู้ปฏิเสธศรัทธาจะถูกไล่ต้อนสู่นรกเป็นกลุ่ม ๆ จนกระทั่งเมื่อพวกเขามาถึงมัน ประตูทั้งหลายของมันจะถูกเปิดออก ยามเฝ้าประตูของมันจะกล่าวแก่พวกเขาว่า บรรดาร่อซูลจากพวกท่านมิได้มายังพวกท่านเพื่อสาธยายสัญญาณต่าง ๆแห่งพระเจ้าของพวกท่านแก่พวกท่าน และกล่าวเตือนพวกท่านถึงการพบในวันนี้ของพวกท่านดอกหรือ ? พวกเขากล่าวว่ามีครับ แต่ว่าพระประกาศิตแห่งการลงโทษเป็นที่คู่ควรแล้วแก่บรรดาผู้ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(39,72,'39:72','จะมีเสียงกล่าวว่า พวกท่านจงเข้าไปในประตูทั้งหลายของนรก เป็นผู้พํานักอยู่ในนั้นตลอดกาล ดังนั้นที่พํานักของบรรดาผู้หยิ่งยะโสชั่วช้าแท้ ๆ');
INSERT INTO "king_fahad_thai" VALUES(39,73,'39:73','และบรรดาผู้ยําเกรงพระเจ้าของพวกเขาจะถูกนําสู่สวนสวรรค์เป็นกลุ่ม ๆ จนกระทั่งเมื่อพวกเขามาถึงมัน และ ประตูทั้งหลายของมันจะถูกเปิดออก ยามเฝ้าประตูสวรรค์จะกล่าวแก่พวกเขาว่า ความศานติจงมีแด่พวกท่าน พวกท่านเป็นผู้บริสุทธิ์ดังนั้นจงเข้าไปในสวรรค์เป็นผู้พํานักอยู่ตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(39,74,'39:74','และพวกเขากล่าวว่า บรรดาการสรรเสริญทั้งมวลเป็นของอัลลอฮฺ ผู้ซึ่งได้ทําให้สัญญาของพระองค์เป็นที่สมจริงแก่พวกเรา และทรงทําให้เราได้ครอบครองแผ่นดินในสวนสวรรค์ เพื่อที่เราจะได้พํานักอยู่ตามที่เราประสงค์ ดังนั้นรางวัลของบรรดาผู้กระทําความดีช่างยอดเยี่ยมแท้ ๆ');
INSERT INTO "king_fahad_thai" VALUES(39,75,'39:75','และเจ้าจะเห็นมะลาอิกะฮฺห้อมล้อมรอบ ๆ บังลังก์ แซ่ซ้องสดุดีด้วยการสรรเสริญพระเจ้าของพวกเขา และจะถูกตัดสินระหว่างพวกเขาด้วยความยุติธรรม และจะมีเสียงกล่าวว่า บรรดาการสรรเสริญทั้งมวลเป็นของอัลลอฮฺ พระเจ้าแห่งสากลโลก');
INSERT INTO "king_fahad_thai" VALUES(40,1,'40:1','ฮา มีม');
INSERT INTO "king_fahad_thai" VALUES(40,2,'40:2','คัมภีร์นี้เป็นการประทานลงมาจากอัลลอฮฺ ผู้ทรงอํานาจ ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(40,3,'40:3','ผู้ทรงอภัยในบาป และผู้ทรงรับการขอลุแก่โทษ ผู้ทรงเข้มงวดในการลงโทษ ผู้ทรงเต็มเปี่ยมไปด้วยความโปรดปราน ไม่มีพระเจ้าอื่นใดนอกจากพระองค์ ยังพระองค์คือการกลับไป');
INSERT INTO "king_fahad_thai" VALUES(40,4,'40:4','ไม่มีผู้ใดจะโต้เถียงในอายาตของอัลลอฮฺ (อัลกุรอาน) นอกจากบรรดาผู้ปฏิเสธศรัทธา ดังนั้นอย่าให้การวางมาดของพวกเขาในหัวเมืองต่าง ๆ เป็นที่หลอกลวงแก่เจ้า');
INSERT INTO "king_fahad_thai" VALUES(40,5,'40:5','(เพราะ) ก่อนหน้าพวกเขานั้น หมู่ชนของนูหฺ และพลพรรคต่าง ๆ หลังจากพวกเขาได้ปฏิเสธมาก่อนแล้ว และทุก ๆ ประชาชาติได้ตั้งใจที่จะทําลายล้างร่อซูลของพวกเขาและโต้เถียงด้วยความเท็จ เพื่อที่จะลบล้างความจริ งให้สูญสิ้นไป ดังนั้นข้าจึงได้ลงโทษพวกเขาแล้วเป็นอย่างไรบ้างการลงโทษของข้า');
INSERT INTO "king_fahad_thai" VALUES(40,6,'40:6','และเช่นนั้นแหละ ประกาศิตแห่งพระเจ้าของเจ้าได้เป็นที่สมจริงแก่บรรดาผู้ปฏิเสธศรัทธาว่าพวกเขาเป็นชาวนรก');
INSERT INTO "king_fahad_thai" VALUES(40,7,'40:7','บรรดาผู้แบกบัลลังก์ และผู้ที่อยู่รอบ ๆ บัลลังก์ ต่างก็แซ่ซ้องสดุดีด้วยการสรรเสริญพระเจ้าของพวกเขา และศรัทธาต่อพระองค์ และอภัยโทษให้แก่บรรดาผู้ศรัทธา ข้าแต่พระเจ้าของเรา พระองค์ท่านทรงแผ่ความเมตตาและความรอบรู้ไปทั่วทุกสิ่ง ขอพระองค์ทรงโปรดอภัยแก่บรรดาผู้ลุแก่โทษ และดําเนินตามแนวทางของพระองค์ท่าน และทรงคุ้มครองพวกเขาให้พ้นจากการลงโทษแห่งไพนรก');
INSERT INTO "king_fahad_thai" VALUES(40,8,'40:8','ข้าแต่พระเจ้าของเรา และขอพระองค์ทรงให้พวกเขาได้เข้าในสวนสวรรค์หลากหลายอันสถาพร ซึ่งพระองค์ได้ทรงสัญญาแก่พวกเขาพร้อมทั้งผู้กระทําความดีจากบรรพบุรุษของพวกเขา และคู่ครองของพวกเขาและลูกหลานของพวกเขา แท้จริงพระองค์ท่านนั้นเป็นผู้ทรงมีอํานาจ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(40,9,'40:9','และขอพระองค์ทรงคุ้มครองพวกเขาให้พ้นจากความชั่วทั้งหลาย และผู้ใดที่พระองค์ทรงคุ้มครองให้พ้นจากความชั่วทั้งหลายในวันนั้นดังนั้นแน่นอนพระองค์ท่านทรงเมตตาแก่เขา และนั่นคือมันเป็นความสําร็จอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(40,10,'40:10','แท้จริงบรรดาผู้ปฎิเสธศรัทธานั้นจะมีเสียงตะโกนบอกว่า การเกลียดชังของอัลลอฮฺนั้นยิ่งใหญ่กว่าการเกลียดชังของพวกเจ้าต่อตัวของพวกเจ้าเอง เมื่อพวกเจ้าถูกเรียกร้องสู่การศรัทธา แล้วพวกเจ้าก็ได้ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(40,11,'40:11','พวกเขากล่าวว่า ข้าแต่พระเจ้าของเรา พระองค์ท่านได้ทรงทําให้เราตายสองครั้ง และพระองค์ท่านได้ทรงทําให้เรามีชีวิตสองครั้ง ดังนั้นเราขอสารภาพต่อความผิดทั้งหลายของเราดังนั้นจะมีทางออก(แก่เรา)ไหม?');
INSERT INTO "king_fahad_thai" VALUES(40,12,'40:12','นั่นก็เพราะว่า แท้จริงเมืออัลลอฮฺพระองค์เดียวถูกกล่าวขึ้น พวกเจ้าก็ปฎิเสธศรัทธา และเมื่อหากให้มีการตั้งภาคีกับพระองค์พวกเจ้าก็ศรัทธา ดังนั้น การตัดสินชี้ขาดเป็นสิทธิของอัลลอฮฺ ผู้ทรงสูงส่ง ผู้ทรางยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(40,13,'40:13','และจะไม่มีใครใคร่ครวญนอกจากผู้สํานึกตัว');
INSERT INTO "king_fahad_thai" VALUES(40,14,'40:14','ดังนั้นจงวิงวอนขอต่ออัลลอฮฺ โดยเป็นผู้มีความบริสุทธิ์ใจในศาสนาต่อพระองค์ แม้ว่าพวกปฎิเสธศรัทธาจะเกลียดชังก็ตาม');
INSERT INTO "king_fahad_thai" VALUES(40,15,'40:15','ผู้ทรงตําแหน่งอันสูงเจ้าแห่งบัลลังก์ ทรงส่งวะฮียฺตามพระบัญชาของพระองค์แก่ผู้ที่พระองค์ทรงประสงค์จากปวงบ่าวของพระองค์เพื่อเตือนให้รําลึกถึงวันแห่งการพบปะร่วมกัน (วันกิยามะฮฺ)');
INSERT INTO "king_fahad_thai" VALUES(40,16,'40:16','วันที่พวกเขาจะปรากฎตัวออกมา ไม่มีสิ่งใดของพวกเขาจะซ่อนเร้นไปจากอัลลอฮฺ อํานาจในวันนี้เป็นของผู้ใดเล่า ? แน่นอนมันเป็นของอัลลอฮฺ ผู้ทรงเอกะผู้ทรงพิชิตโดยเด็ดขาด');
INSERT INTO "king_fahad_thai" VALUES(40,17,'40:17','วันนี้ทุกชีวิตจะได้รับการตอบแทนตามที่มันได้กระทําไว้ ไม่มีการอธรรมในวันนี้แท้จริงอัลลอฮฺเป็นผู้ทรงฉับพลันในการสอบสวน');
INSERT INTO "king_fahad_thai" VALUES(40,18,'40:18','และจงเตือนพวกเขาให้ทราบถึงวันที่ใกล้เข้ามา (วันกิยามะฮฺ) เมื่อหัวใจเข้ามาติดอยู่ที่ลําคอด้วยความอดกลั้น ไม่มีมิตรที่สนิทสนมสําหรับบรรดาผู้อธรรม และไม่มีผู้ช่วยเหลือคนใดที่จะถูกเชื่อฟัง');
INSERT INTO "king_fahad_thai" VALUES(40,19,'40:19','พระองค์ทรงรอบรู้การทรยศของดวงตา และสิ่งที่ทรวงอกปกปิดอยู่');
INSERT INTO "king_fahad_thai" VALUES(40,20,'40:20','และ อัลลอฮฺทรงตัดสินด้วยความยุติธรรม และบรรดาผู้ที่วิงวอนขออื่นจากพระองค์นั้น พวกมันไม่อาจจะตัดสินใด ๆ ได้ แท้จริงอัลลอฮฺ พระองค์เป็นผู้ทรงได้ยิน ผู้ทรงเห็น');
INSERT INTO "king_fahad_thai" VALUES(40,21,'40:21','พวกเขามิได้ท่องเที่ยวไปตามแผ่นดินดอกหรือ ? แล้วพิจารณาดูว่าบั้นปลายของประชาชาติในยุคก่อนหน้าพวกเขาเป็นเช่นใด เขาเหล่านั้นมีพลังที่เข้มแข็งกว่าพวกเขา และได้ทิ้งร่องรอยไว้มากมาย แล้วอัลลอฮฺ ก็ได้ทรงลงโทษพวกเขาด้วยความผิดของพวกเขา และไม่มีผู้ใดจะช่วยปกป้องพวกเขาให้พ้นจากอัลลอฮฺได้');
INSERT INTO "king_fahad_thai" VALUES(40,22,'40:22','นั่นก็เพราะว่าเมื่อบรรดาร่อซูลของพวกเขาได้มายังพวกเขาได้มายังพวกเขาพร้อมด้วยหลักฐานต่าง ๆ อันชัดแจ้งพวกเขาก็ได้ปฏิเสธศรัทธา ดังนั้นอัลลอฮฺจึงทรงลงโทษพวกเขา แท้จริงพระองค์เป็นผู้ทรงพลัง ผู้ทรงรุนแรงในการลงโทษ');
INSERT INTO "king_fahad_thai" VALUES(40,23,'40:23','และโดยไม่แน่นอน เราได้ส่งมูซามาพร้อมด้วยสัญญาณต่าง ๆ ของเราและหลักฐานอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(40,24,'40:24','ไปยังฟิรเอานฺ และฮามาน และกอรูน แล้วพวกเขาก็กล่าวว่า (มูซาเป็น) มายากรนักโกหกตัวฉกาจ');
INSERT INTO "king_fahad_thai" VALUES(40,25,'40:25','ครั้นเมื่อมูซาได้มายังพวกเขาด้วยสัจธรรมจากเรา พวกเขากล่าวว่า จงฆ่าลูกชายของบรรดาผู้ศรัทธาร่วมกับเขา และไว้ชีวิตผู้หญิงของพวกเขา แต่แผนการณ์ของพวกปฏิเสธศรัทธานั้นมิใช่อื่นใด นอกจากการผิดพลาด');
INSERT INTO "king_fahad_thai" VALUES(40,26,'40:26','และฟิรเอานฺ กล่าวว่า จงปล่อยฉัน ฉันจะฆ่ามูซา และให้เขาวิงวอนขอพระเจ้าของเขา แท้จริงฉันเกรงว่าเขาจะมาเปลี่ยนศาสนาของพวกท่าน หรือจะก่อการร้ายให้เกิดขึ้นในแผ่นดิน');
INSERT INTO "king_fahad_thai" VALUES(40,27,'40:27','และ มูซากล่าวว่า แท้จริงฉันของความคุ้มครองต่อพระเจ้าของฉัน และพระเจ้าของพวกท่านให้พ้นจากผู้หยิ่งผยองทุกคนที่ไม่ศรัทธาต่อวันแห่งการชําระบัญชี');
INSERT INTO "king_fahad_thai" VALUES(40,28,'40:28','และชายผู้ศรัทธาคนหนึ่งจากพวกพ้องของฟิรเอานฺ ซึ่งปกปิดการศรัทธาของเขากล่าวว่าพวกท่านจะฆ่าชายคนหนึ่ง ที่เขากล่าวว่า พระเจ้าของฉันคืออัลลอฮฺกระนั้นหรือ ? และแน่นอนเยาได้นําหลักฐานทั้งหลายอันชัดแจ้ง มาจากพระเจ้าของพวกท่านมายังพวกท่าน และหากว่าเขาเป็นคนโกหก การโกหกของเขาก็อยู่บนเขาเอง และหากว่าเขาเป็นคนพูดจริง ส่วนหนึ่งจากที่เขาได้สัญญาไว้กับพวกท่านก็จะประสบแก่พวกท่าน แท้จริงอัลลอฮฺจะไม่ชี้แนะทางแก่ผู้ที่เขาเป็นผู้ละเมิดนักโกหกตัวฉกาจ');
INSERT INTO "king_fahad_thai" VALUES(40,29,'40:29','โอ้หมู่ชนของฉันเอ๋ย! วันนี้อํานาจการปกครองเป็นของพวกท่าน เป็นผู้อยู่เบื้องบนในแผ่นดิน (อียิปต์) แล้วใครเล่าจะช่วยเหลือเราจากการลงโทษของอัลลอฮฺ หากมันได้เกิดขึ้นแก่เรา ฟิรเอานฺ กล่าวว่า ฉันมิได้ชี้นําพวกท่านเว้นแต่สิ่งที่ฉันเห็นว่ามันถูกต้อง เท่านั้นและฉันมิได้ชี้แนะทางแก่พวกท่านเว้นแต่ทางที่เป็นสัจธรรมเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(40,30,'40:30','และชายผู้ศรัทธากล่าวอีกว่า โอ้หมู่ชนของฉันเอ๋ย! แท้จริงฉันกลัวแทนพวกท่านยี่ยงวันแห่งการลงโทษของกลุ่มชนต่าง ๆ ในอดีต');
INSERT INTO "king_fahad_thai" VALUES(40,31,'40:31','เยี่ยงกับเคราะห์กรรมของหมู่ชนของนูหฺ และอ๊าด และซะมูด และกับหมู่ชน หลังจากพวกเขา และอัลลอฮฺ มิ ทรงประสงค์การอธรรมใด ๆแก่ปวงบ่าว');
INSERT INTO "king_fahad_thai" VALUES(40,32,'40:32','และโอ้หมู่ชนของฉันเอ๋ย! ฉันกลัวแทนพวกท่าน เยี่ยงวันแห่งการร้องเรียกหาซึ่งกันและกัน');
INSERT INTO "king_fahad_thai" VALUES(40,33,'40:33','วันที่พวกท่านหันหลังกลับหนี ไม่มีผู้ใดจะช่วยปกป้องพวกท่านให้พ้นจากการลงโทษของอัลลอฮฺไดเ และผู้ใดที่อัลลอฮฺทรงให้เขาหลงทางแล้ว ก็จะไม่มีผู้ชี้แนะทางให้แก่เขา');
INSERT INTO "king_fahad_thai" VALUES(40,34,'40:34','และโดยแน่นอน แต่ก่อนนี้ยูซุฟ ได้มายังพวกท่านพร้อมด้วยหลักฐ่านอันชัดแจ้งแต่พวกท่านก็ยังคงอยู่ในการสงสัยในสิ่งที่เขาได้นํามายังพวกท่าน จนกระทั่งเมื่อเขาได้ตายไปแล้ว พวกท่านก็กล่าวว่าอัลลอฮฺจะไม่ทรงตั้งร่อซูลคนใดอีกแล้วหลังจากเขา เช่นนั้นแหละอัลลอฮฺจะทรงให้ผู้ที่เขาฝ่าฝืนสงสัยหลงทาง');
INSERT INTO "king_fahad_thai" VALUES(40,35,'40:35','บรรดาผู้โต้เถียงในสัญญาณต่าง ๆ ของอัลลอฮฺโดยไม่มีหลักฐานใด ๆ มายังพวกเขา เป็นที่น่าเกลียดชังยิ่ง ณ ที่อัลลอฮฺ และ ณ บรรดาผู้ศรัทธา เช่นนั้นแหละอัลลอฮฺทรงประทับบนทุก ๆ หัวใจของผู้จองหองหยิ่งยะโส');
INSERT INTO "king_fahad_thai" VALUES(40,36,'40:36','และฟิรเอานกล่าวว่า โอ้ฮามานเอ๋ย! จงสร้างหอสูงให้ฉันเพื่อฉันจะได้บรรลุถึงทางที่จะขึ้นไป');
INSERT INTO "king_fahad_thai" VALUES(40,37,'40:37','ทางที่จะขึ้นไปสู่ชั้นฟ้าทั้งหลาย เพื่อฉันจะได้เห็นพระเจ้าของมูซา และแท้จริงฉันคิดอย่างแน่ใจแล้วว่าเขาเป็นคนโกหก เช่นนั้นแหละ การงานที่ชั่วช้าของเขาได้ถูกทําให้เพริศแพร้วแก่ฟิรเอานฺ และเขาถูกปิดกั้นจากแนวทาง (ของอัลลอฮฺ) และแผนการของฟิรเอานฺนั้นมิใช่อื่นนอกจากอยู่ในความหายนะ');
INSERT INTO "king_fahad_thai" VALUES(40,38,'40:38','และผู้ศรัทธากล่าวว่า โอ้หมู่ชนของฉันเอ๋ย! จงปฏิบัติตามฉัน ฉันจะชี้แนะทางแก่พวกท่านสู่ทางแห่งสัจธรรม');
INSERT INTO "king_fahad_thai" VALUES(40,39,'40:39','โอ้หมู่ชนของฉันเอ๋ยแท้จริงชีวิตแห่งโลกนี้เป็นเพียงแค่ความเพลิดเพลินเท่านั้นและแท้จริงปรโลกนั้นมันเป็นที่อยู่อันมั่นคง');
INSERT INTO "king_fahad_thai" VALUES(40,40,'40:40','ผู้ใดที่กระทําความชั่ว เขาจะไม่ได้รับการตอบแทน เว้นแต่เยี่ยงเช่นนั้น และผู้ใดกระทําความดีจากเพศชายหรือเพศหญิงก็ตามและเขาเป็นผู้ศรัทธาด้วย ชนเหล่านั้นแหละพวกเขาจะได้เข้าสวนสวรรค์ จะได้รับปัจจัยยังชีพในนั้น โดยปราศจากการคํานวณ');
INSERT INTO "king_fahad_thai" VALUES(40,41,'40:41','และโอ้หมู่ชนของฉันเอ๋ย! ทําไมฉันจึงเชิญชวนพวกท่านไปสู่การรอดพ้น แต่พวกท่านเชิญชวนฉันไปสู่ไฟนรก');
INSERT INTO "king_fahad_thai" VALUES(40,42,'40:42','พวกท่านเชิญชวนฉันให้ปฏิเสธศรัทธาต่ออัลลอฮฺ และให้ฉันตั้งภาคีต่อพระองค์โดยที่ฉันไม่รู้มาก่อนเลยในเรื่องนั้น และฉันได้เชิญชวนพวกท่านไปสู่ผู้ทรงอํานาจ ผู้ทรงอภัยอย่างมากหมาย');
INSERT INTO "king_fahad_thai" VALUES(40,43,'40:43','ไม่ต้องสงสัยเลยว่า แท้จริงที่พวกท่านเชิญชวนฉันไปสู่นั้น มันไม่เหมาะสม (ที่จะเอาใจใส่) แก่มันทั้งในโลกนี้และในปรโลกด้วย และแท้จริง การกลับของเรานั้นไปสู่อัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(40,44,'40:44','ดังนั้นพวกท่านจะต้องระลึกถึงสิ่งที่ฉันได้กล่าวแก่พวกท่าน และฉันขอมอบภารกิจของฉันแต่อัลลอฮฺ แท้จริงอัลลอฮฺทรงเป็นผู้เฝ้าดูปวงบ่าว');
INSERT INTO "king_fahad_thai" VALUES(40,45,'40:45','อัลลอฮฺได้ทรงคุ้มครองเขาให้พ้นจากความชั่วทั้งหลายที่พวกเขาวางแผนไว้ และการลงโทษที่ชั่วช้าก็จะห้อมล้อมบริวารของฟิรเอานฺ');
INSERT INTO "king_fahad_thai" VALUES(40,46,'40:46','ไฟนรกนั้นพวกเขาจะถูกนํามาให้เห็นทั้งในยามเช้า และยามเย็น และวันกิยามะฮฺนั้น จะมีเสียงกล่าวว่า จะให้บริวารของฟิรเอานฺเข้าไปรับการลงโทษอันสาหัสยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(40,47,'40:47','และจงรําลึกถึงเมื่อพวกเขาโต้เถียงกันในนรก พวกอ่อนแอแกล่าวกับพวกหัวหน้าว่า “แท้จริงพวกเราเป็นผู้ตามพวกท่าน ดังนั้นพวกท่านจะช่วยพวกเราให้พ้นจากการลงโทษสักส่วนหนึ่งของไฟนรกนี้ได้ไหม?');
INSERT INTO "king_fahad_thai" VALUES(40,48,'40:48','บรรดาหัวหน้ากล่าวว่า แท้จริงเราทั้งหมดอยู่ในนรก แท้จริงอัลลอฮฺทรงตัดสินระหว่างปวงบ่าวแล้วอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(40,49,'40:49','และบรรดาผู้อยู่ในนรก กล่าวแก่ยามเฝ้าประตูนรกว่า โปรดช่วยวิงวอนขอต่อพระเจ้าของพวกท่านให้ทรงลดหย่อนการลงโทษแก่เราสักวันหนึ่ง');
INSERT INTO "king_fahad_thai" VALUES(40,50,'40:50','พวกเขากล่าวว่า บรรดาร่อซูลของพวกท่านมิได้มายังพวกท่านพร้อมด้วยหลักฐานอันชัดแจ้งดอกหรือ ? พวกเขากล่าวว่ามีครับเขาทั้งหลายกล่าวว่า พวกท่านจงวิงวอนขอเองซิ แต่การวิงวอนขอของผู้ปฏิเสธศรัทธานั้น มิใช่อื่นใดนอกจากอยู่ในการหลงทาง');
INSERT INTO "king_fahad_thai" VALUES(40,51,'40:51','แท้จริงเราจะช่วยเหลือบรรดาร่อซูลของเรา และบรรดาผู้ศรัทธาอย่างแน่นอน ทั้งในชีวิตของโลกนี้ และวันที่ซึ่งปวงพยานจะยืนขึ้นเป็นพยาน');
INSERT INTO "king_fahad_thai" VALUES(40,52,'40:52','วันที่การแก้ตัวของพวกเขาจะไม่อํานวยผลแก่บรรดาผู้อธรรม และสําหรับพวกเขาจะได้รับการสาปแช่ง และสําหรับพวกเขาจะมีที่พํานักอันชั่วช้า');
INSERT INTO "king_fahad_thai" VALUES(40,53,'40:53','และโดนแน่นอนเราได้ประทานการชี้นําทางแก่มูซา และเราได้ให้มรดกแก่วงศ์วารของอิสรออีล คือคัมภีร์');
INSERT INTO "king_fahad_thai" VALUES(40,54,'40:54','เพื่อเป็นการชี้นําทางและเป็นการเตือนรําลึกแก่บรรดาผู้มีสติปัญญา');
INSERT INTO "king_fahad_thai" VALUES(40,55,'40:55','ดังนั้น เจ้าจงอดทนเพราะแท้จริงสัญญาของอัลลอฮฺนั้นเป็นความจริง และจงขออภัยโทษต่อความผิดของเจ้า และจงแซ่ซ้องสดุดีด้วยการสรรเสริญพระเจ้าของเจ้าทั้งในยามเย็นและในยามเช้า');
INSERT INTO "king_fahad_thai" VALUES(40,56,'40:56','แท้จริง บรรดาผู้โต้เถียงในเรื่องอายาตของอัลลอฮฺ (อัลกุรอาน) โดยปราศจากหลักฐานมายังพวกเขานั้น ไม่มีอะไรในทรวงอกของพวกเขานอกจากต้องการจะเป็นใหญ่ ซึ่งพวกเขาจะไม่เป็นผู้บรรลุถึงมันได้ ดังนั้น จงขอความคุ้มครองต่ออัลลอฮฺ แท้จริงพระองค์เป็นผู้ทรงได้ยิน ผู้ทรงเห็น');
INSERT INTO "king_fahad_thai" VALUES(40,57,'40:57','แน่นอนการสร้างชั้นฟ้าทั้งหลายและแผ่นดินนั้นใหญ่ยิ่งกว่าการสร้างมนุษย์ แต่ว่าส่วนมากของมนุษย์ไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(40,58,'40:58','และคนตาบอดกับคนตาดีนั้นย่อมไม่เท่าเทียมกัน และบรรดาผู้ศรัทธาและประกอบความดีทั้งหลายกับพวกกระทําความชั่วก็ไม่เท่าเทียมเช่นกัน เพียงส่วนน้อยเท่านั้นที่พวกเจ้าจะใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(40,59,'40:59','แท้จริง วันอวสานนั้นจะมีมาอย่างแน่นอน ไม่มีข้อสงสัยใด ๆ ในวันนั้น แต่ว่าส่วนมากของมนุษย์ไม่ยอมศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(40,60,'40:60','และพระเจ้าของพวกเจ้าตรัสว่า จงวิงวอนขอต่อข่า ข้าจะตอบรับแก่พวกเจ้า ส่วนบรรดาผู้โอหังกต่อการเคารพภักดีข้านั้น จะเข้าไปอยู่ในนรกอย่างต่ําต้อย');
INSERT INTO "king_fahad_thai" VALUES(40,61,'40:61','อัลลอฮฺผู้ทรงบันดาลกลางคืนให้แก่พวกเจ้า เพื่อจะได้พักผ่อนในเวลาของมัน และกลางวันเพื่อจะได้มองเห็น แท้จริงอัลลอฮฺเป็นเจ้าของความโปรดปรานแก่ปวงมนุษย์แต่ว่าส่วนมากของมนุษย์ไม่ขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(40,62,'40:62','นั่นคืออัลลอฮฺ พระเจ้าของพวกเจ้าผู้ทรงสร้างทุกสิ่ง ไม่มีพระเจ้าอื่นใดนอกจากพระองค์ ดังนั้นทําไมพวกเจ้าจึงถูกหันเหออกจากพระองค์เล่า?');
INSERT INTO "king_fahad_thai" VALUES(40,63,'40:63','เช่นนั้นแหละ บรรดาผู้ที่ปฏิเสธต่อสัญญาณทั้งหลายของอัลลอฮฺ จะถูกทําให้หันเหออก (จากสัจธรรม)');
INSERT INTO "king_fahad_thai" VALUES(40,64,'40:64','อัลลอฮฺคือผู้ทรงทําให้แผ่นดินนี้เป็นที่พํานักแก่พวกเจ้า และชั้นฟ้าเป็นเพดานมั่นคง และทรงทําให้พวกเจ้าเป็นรูปร่าง และทรงทําให้รูปร่างของพวกเจ้าสวยงาม และทรงประทานปัจจัยยังชีพจากสิ่งที่ดี ๆ แก่พวกเจ้านั่นคืออัลลอฮฺ พระเจ้าของพวกเจ้า ดังนั้นอัลลอฮฺพระเจ้าแห่งสากลโลกทรง จําเริญยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(40,65,'40:65','พระองค์คือผู้ทรงมีชีวิต ไม่มีพระเจ้าอื่นใดนอกจากพระองค์ ดังนั้นจงวิงวอนขอต่อพระองค์โดยเป็นผู้มีความบริสุทธิ์ใจในสาสนาของพระองค์ บรรดาการสรรเสริญนั้นเป็นของอัลลอฮฺ พระเจ้าแห่งสากลโลก');
INSERT INTO "king_fahad_thai" VALUES(40,66,'40:66','จงกล่าวเถิดมุฮัมมัด แท้จริงฉันถูกห้ามมิให้เคารพภักดีต่อบรรดาที่พวกท่านวิงวอนขออื่นจากอัลลอฮฺ เมื่อหลักฐานทั้งหลายอันชัดแจ้งจากพระเจ้าของฉันได้มีมายังฉันแล้ว และฉันถูกบัญชาให้นอบน้อมต่อพระเจ้าแห่งสากลโลก');
INSERT INTO "king_fahad_thai" VALUES(40,67,'40:67','พระองค์คือผู้ทรงสร้างพวกเจ้าจากฝุ่นดิน แล้วจากเชื้ออสุจิ แล้วจากก้อนเลือดแล้วทรงให้พวกเจ้าคลอดออกมาเป็นทารกแล้วเพื่อพวกเจ้าจะได้บรรลุสู่วัยฉกรรจ์ของพวกเจ้า แล้วเพื่อพวกเจ้าจะได้เป็นคนชรา และในหมู่พวกเจ้ามีผู้เสียชีวิตในวัยหนุ่ม และเพื่อให้พวกเจ้าจะได้บรรลุสู่วัยที่ถูกกําหนดไว้ และเพื่อพวกเจ้าจะได้ใช้สติปัญญาใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(40,68,'40:68','พระองค์ คือผู้ทรงให้เป็นและทรงให้ตาย ดังนั้นเมื่อพระองค์ทรงกําหนดกิจการใด ๆ พระองค์ก็จะกล่าวแก่มันว่า จงเป็น แล้วมันก็จะเป็นขึ้นมา');
INSERT INTO "king_fahad_thai" VALUES(40,69,'40:69','เจ้าไม่เห็นดอกหรือว่า บรรดาผู้ที่โต้เถียงในอายาตของอัลลอฮฺ (อัลกุรอาน) พวกเขาถูกให้พันเหออกไปอย่างไร ?');
INSERT INTO "king_fahad_thai" VALUES(40,70,'40:70','บรรดาผู้ปฏิเสธต่อคัมภีร์ และต่อสิ่งที่เราได้ส่งมาพร้อมกับรรดาร่อซูลของเราแล้วพวกเขาก็จะได้รู้');
INSERT INTO "king_fahad_thai" VALUES(40,71,'40:71','เมื่อห่วงคล้องคออยู่บนต้นของพวกเขา และโซ่ตรวนถูกลากไป');
INSERT INTO "king_fahad_thai" VALUES(40,72,'40:72','ในน้ําเดือดพล่าน แล้วในไฟนรกพวกเขาจะถูกเผาไหม้');
INSERT INTO "king_fahad_thai" VALUES(40,73,'40:73','แล้วจะมีเสียงกล่าวแก่พวกเขาว่าไหนเล่าสิ่งที่พวกเจ้าตั้งภาคี');
INSERT INTO "king_fahad_thai" VALUES(40,74,'40:74','อื่นจากอัลลอฮฺ พวกเขากล่าวว่ามันได้หลงหายไปจากพวกเราแล้ว แต่ว่าพวกเรามิได้วิงวอนขอต่อสิ่งใดก่อนหน้านี้ดอก! เช่นนั้นแหละอัลลอฮฺทรงให้บรรดาผู้ปฏิเสธศรัทธาหลงทาง');
INSERT INTO "king_fahad_thai" VALUES(40,75,'40:75','นั่นก็เพราะว่า พวกเจ้าหลงระเริงในแผ่นดิน โดยไม่เป็นธรรม และเพราะว่าพวกเจ้าอวดดี');
INSERT INTO "king_fahad_thai" VALUES(40,76,'40:76','จงเข้าไปในประตูทั้งหลายของนรก เป็นผู้พํานักอยู่ตลอดกาลในนั้น ฉะนั้นที่พํานักของพวกหยิ่งผยองมันชั่วช้าแท้ ๆ');
INSERT INTO "king_fahad_thai" VALUES(40,77,'40:77','ดังนั้นเจ้าจงอดทน แท้จริงสัญญาของอัลลอฮฺนั้นเป็นจริง บางทีเราจะให้เจ้าได้เห็นบางสิ่งที่ได้สัญญาแก่พวกเขา หรือเราจะทําให้เจ้าตายเสียก่อน ดังนั้นพวกเขาจะถูกกลับไปยังเรา');
INSERT INTO "king_fahad_thai" VALUES(40,78,'40:78','และโดยแน่นอน เราได้ส่งบรรดาร่อซูลมาก่อนหน้าเจ้าบางคนในหมู่พวกเขามีผู้ที่เราบอกเล่าแก่เจ้า และบางคนในหมู่พวกเขามีผู้ที่เรามิได้บอกเล่าแก่เจ้า และไม่บังควรแก่ร่อซูลที่จะนําสัญญาณใด ๆ มาเว้นแต่ด้วยอนุมัติของอัลลอฮฺมาถึงเรื่องนั้นก็จะถูกตัดสินด้วยความยุติธรรม และขณะนั้นบรรดาผู้กล่าวเท็จก็จะขาดทุนอย่างย่อยยับ');
INSERT INTO "king_fahad_thai" VALUES(40,79,'40:79','อัลลอฮฺคือผู้ทรงทําปศุสัตว์บางชนิดให้พวกเจ้าเพื่อใช้เป็นพาหนะ และบางชนิด เพื่อให้พวกเจ้าใช้กิน');
INSERT INTO "king_fahad_thai" VALUES(40,80,'40:80','และสําหรับพวกเจ้าในตัวมันนั้นมีประโยชน์มากหลาย และเพื่อพวกเจ้าจะได้บรรลุสู่สมความปราถนาที่มีอยู่ในทรวงอกของพวกเจ้า และพวกเจ้าบรรทุกบนหลังมันเช่นเดียวกับใช้บรรทุกบนเรือ');
INSERT INTO "king_fahad_thai" VALUES(40,81,'40:81','และพระองค์ได้ทรงให้พวกเจ้าเห็นสัญญาณต่าง ๆ ของพระองค์ดังนั้น ด้วยสัญญาณต่าง ๆ ของอัลลอฮฺอันใดเล่าที่พวกเจ้าปฏิเสธ');
INSERT INTO "king_fahad_thai" VALUES(40,82,'40:82','พวกเข้ามิได้ท่องเที่ยวไปตามแผ่นดินดอกหรือ ? แล้วพิจารณาดูว่า บั้นปลายของประชาชาติในยุคก่อนหน้าพวกเขาเป็นเช่นใด เขาเหล่านั้นมีจํานวนมากกว่ายพวกเขา และมีพลังเข้มแข็งว่า และได้ทิ้งร่องรอยไว้มากมายในแผ่นดิน ดังนั้นสิ่งที่พวกเขาได้กระทําไว้นั้นหาได้อํานวยประโยชน์แก่พวกเขาไม่');
INSERT INTO "king_fahad_thai" VALUES(40,83,'40:83','ดังนั้นเมื่อบรรดาร่อซูลของพวกเขาได้มายังพวกเขาพร้อมด้วยหลักฐานต่าง ๆ อันชัดแจ้งพวกเขาก็ดีใจกับความรู้ (ทางด้านวัตถุ) ที่มีอยู่กับพวกเขา และสิ่งที่พวกเขาเคยเยาะเย้ยไว้นั้นก็ห้อมล้อมพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(40,84,'40:84','ครั้นเมื่อพวกเขาได้เห็นการลงโทษอย่างหนักของเรา พวกเขาก็กล่าวว่า เราศรัทธาต่อัลลอฮฺองค์เดียว และเราปฏิเสธศรัทธาต่อสิ่งที่เราเคยตั้งภาคีต่อพระองค์');
INSERT INTO "king_fahad_thai" VALUES(40,85,'40:85','แต่การศรัทธาของพวกเขาจะไม่อํานวยประโยชน์แก่พวกเขาเลยในเมื่อพวกเขาได้เห็นการลงโทษอย่างหนักของเรา นี่คือแนวทางของอัลลอฮฺที่ได้มีมาแต่ในอดีตในปวงบ่าวของพระองค์ และขณะนั้นบรรดาผู้ปฏิเสธศรัทธาก็ได้ขาดทุนอย่างย่อยยับ');
INSERT INTO "king_fahad_thai" VALUES(41,1,'41:1','ฮามีม');
INSERT INTO "king_fahad_thai" VALUES(41,2,'41:2','(อัลกุรอานนี้) เป็นการประทานลงมาจากพระผู้ทรงกรุณาปรานี ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(41,3,'41:3','คัมภีร์ซึ่งอายาตทั้งหลายได้ให้คําอธิบายไว้อย่างละเอียดเป็นอัลกุรอานภาษาอาหรับสําหรับหมู่ชนผู้มีความรู้');
INSERT INTO "king_fahad_thai" VALUES(41,4,'41:4','เป็นการแจ้งข่าวดีและเป็นการตักเตือน แต่ส่วนมากของพวกเขาผินหลังให้ ดังนั้น พวกเขาจึงไม่ได้ยิน');
INSERT INTO "king_fahad_thai" VALUES(41,5,'41:5','และพวกเขากล่าวว่า หัวใจของเราอยู่ในที่ปกปิดจากสิ่งที่พวกท่านเชิญชวนเราไปสู่สิ่งนั้น และในหูของเราก็หนวก และระหว่างเรากับท่านก็มีม่านกั้นอยู่ ดังนั้นท่านจงทํา (สิ่งที่ท่านพึงกระทํา) เถิด สําหรับพวกเราก็จะทํา (ตามที่เราต้องการจะกระทํา)');
INSERT INTO "king_fahad_thai" VALUES(41,6,'41:6','จงกล่าวเถิดมุฮัมมัด ฉันก็คือสามัญชนเยี่ยงพวกท่าน แต่ได้มีวะฮียฺแก่ฉันว่า พระเจ้าของพวกท่านนั้นคือพระเจ้าองค์เดียว ดังนั้นจงมุ่งตรงสู่พระองค์เถิด และจงขออภัยต่อพระองค์และความวิบัติจงมีแต่บรรดาผู้ตั้งภาคี');
INSERT INTO "king_fahad_thai" VALUES(41,7,'41:7','บรรดาผู้ไม่จ่ายซะกาต และพวกเขา คือพวกปฏิเสธศรัทธาต่อวันปรโลก');
INSERT INTO "king_fahad_thai" VALUES(41,8,'41:8','แท้จริง บรรดาผู้ศรัทธา และกระทําความดีทั้งหลาย สําหรับพวกเขานั้นจะได้รับรางวัลอย่างมิขาดสาย');
INSERT INTO "king_fahad_thai" VALUES(41,9,'41:9','จงกล่าวเถิดมุฮัมมัด พวกท่านปฏิเสธศรัทธาต่อพระผู้สร้างแผ่นดินเพียงในสองวัน และพวกท่านตั้งภาคีคู่เคียงกับพระองค์กระนั้นหรือ ? นั่นคือพระเจ้าแห่งสากลโลก');
INSERT INTO "king_fahad_thai" VALUES(41,10,'41:10','และใน (แผ่นดิน) นั้นพระองค์ทรงทําให้เทือกเขาตั้งมั่นอยู่บนมัน และทรงให้มีความจําเริญในนั้น และทรงกําหนดปัจจัยยังชีพของมันให้มีขึ้นในนั้นในระยะเวลา 4 วัน อย่างทัดเทียมกันแก่บรรดาผู้ไต่ถาม');
INSERT INTO "king_fahad_thai" VALUES(41,11,'41:11','แล้วพระองค์ทรงมุ่งสู่ฟากฟ้าขณะที่มันเป็นไอหมอก พระองค์จึงตรัสแก่ชั้นฟ้าและแผ่นดินว่า เจ้าทั้งสองจงมาจะโดยเต็มใจหรือไม่เต็มใจก็ตาม มันทั้งสองกล่าวว่า ข้าพระองค์มาอย่างเต็มใจแล้ว');
INSERT INTO "king_fahad_thai" VALUES(41,12,'41:12','ดังนั้นพระองค์ทรงสร้างมันสําเร็จเป็นชั้นฟ้าทั้งเจ็ดในระยะเวลา 2 วัน และทรงกําหนดในทุกชั้นฟ้าหน้าที่ของมัน และได้ประดับท้องฟ้าแห่งโลกนี้ด้วยดวงดาวทั้งหลาย และเป็นการป้องกัน (ให้พ้นจากชัยฏอน) นั่นคือ การกําหนดแห่งพระผู้ทรงอํานาจ ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(41,13,'41:13','แต่ถ้าพวกเขาผินหลังให้ก็จงกล่าวเถิดมุฮัมมัดว่า ฉันขอเตือนพวกท่าน (ให้ระลึกถึงความหายนะเยี่ยงความหายนะของพวกอ๊าดและพวกซะมูด)');
INSERT INTO "king_fahad_thai" VALUES(41,14,'41:14','จงรําลึกเมื่อบรรดาร่อซูลได้มายังพวกเขา (เรียกร้องเชิญชวน) จากทางข้างหน้าพวกเขาและจากทางข้างหลังพวกเขา (โดยกล่าว)ว่า พวกท่านอย่าได้เคารพภักดีผู้ใดนอกจากอัลลอฮฺ พวกเขากล่าวว่า หากพระเจ้าของเราทรงประสงค์ แน่นอนพระองค์จะต้องส่งมะลาอิกะฮฺลงมา ดังนั้นเราจึงปฏิเสธศรัทธาในสิ่งที่พวกท่านถูกส่งมา');
INSERT INTO "king_fahad_thai" VALUES(41,15,'41:15','ส่วนพวกอ๊าดนั้น พวกเขาได้หยิ่งผยองในแผ่นดินโดยไม่เป็นธรรม และพวกเขากล่าวว่า ผู้ใดจะมีพลังเข้มแข็งกว่าพวกเรา ? พวกเขาไม่เห็นดอกหรือว่า แท้จริงอัลลอฮฺผู้ทรงสร้างพวกเขานั้นทรงพลังเข้มแข็งกว่าพวกเขาแต่พวกเขาก็ยังคงปฏิเสธสัญญาณต่าง ๆ ของเรา');
INSERT INTO "king_fahad_thai" VALUES(41,16,'41:16','ดังนั้นเราได้ส่งลมพายุที่หนาวเหน็บมีเสียงกึกก้องมายังพวกเขาในหลายวันแห่งความหายนะ เพื่อเราจะให้พวกเขาลิ้มการลงโทษอันน่าอับยศในชีวิตแห่งโลกนี้ และแน่นอนการลงโทษแห่งปรโลกนั้นย่อมอัปยศยิ่งกว่า และพวกเขาจะไม่ได้รับความช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(41,17,'41:17','และส่วนพวกซะมูดนั้นเราได้ชี้แนะทางให้แก่พวกเขา แต่พวกเขาชอบเลือกเอาการตาบอดมากกว่าการอยู่ในแนวทางที่ถูกต้อง ดังนั้นความหายนะแห่งการลงโทษที่น่าอดสูก็ได้คร่าพวกเขาตามที่พวกเขาได้แสวงหาเอาไว้');
INSERT INTO "king_fahad_thai" VALUES(41,18,'41:18','และเราได้ช่วยบรรดาผู้ศรัทธา และบรรดาผู้ยําเกรงให้รอดพ้น (จากการลงโทษนั้น)');
INSERT INTO "king_fahad_thai" VALUES(41,19,'41:19','และ (จงรําลึกถึง) วันหนึ่งซึ่งเมื่อเหล่าศัตรูของอัลลอฮฺจะถูกชุมนุมเข้าสู่ไฟนรกและ พวกเขาจะถูกจัดแถว ๆ');
INSERT INTO "king_fahad_thai" VALUES(41,20,'41:20','จนกระทั่งเมื่อพวกเขามาถึงนรก หูของพวกเขา และตาของพวกเขาและผิวหนังของพวกเขาก็จะเป็นพยานคัดค้านพวกเขาตามที่พวกเขาได้กระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(41,21,'41:21','และพวกเขากล่าวแก่ผิวหนังของพวกเขาว่า ทําไมพวกเจ้าจึงเป็นพยานคัดค้านแก่เราล่ะ ? พวกมันกล่าวว่า อัลลอฮฺทรงให้เราพูด ซึ่งพระองค์ทรงให้รู้ทุกสิ่งที่พูด และพระองค์ทรงสร้างพวกเจ้าเป็นครั้งแรก และยังพระองค์เท่านั้นที่พวกเจ้าจะถูกนํากลับไป');
INSERT INTO "king_fahad_thai" VALUES(41,22,'41:22','และพวกเจ้าก็ไม่เคยปิดปัง (การทําความชั่ว) ว่าหูของพวกเจ้า และตาของพวกเจ้าและผิวหนังของพวกเจ้าจะเป็นพยานคัดค้านพวกเจ้า แต่พวกเจ้านึกคิดว่าอัลลอฮฺไม่ทรงรอบรู้ส่วนมากในสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(41,23,'41:23','และนั่นและการนึกคิดของพวกเจ้าซึ่งพวกเจ้านึกคิดต่อพระเจ้าของพวกเจ้านั้น เป็นการทําให้พวกเจ้าประสบความหายนะแล้วพวกเจ้าก็กลายเป็นผู้ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(41,24,'41:24','ดังนั้น หากพวกเขาอดทน (ต่อการลงโทษ) ได้ ไฟนรกก็คือที่พํานักของพวกเขา และถ้าพวกเขาวิงวอนขอความโปรดปรานพวกเขาก็จะไม่ได้อยู่ในหมู่ผู้ได้รับความโปรดปราน');
INSERT INTO "king_fahad_thai" VALUES(41,25,'41:25','และเราได้กําหนดสหายไว้สําหรับพวกเขา แล้วพวกเขาก็ทําให้สิ่งที่อยู่ข้างหน้าของพวกเขาและสิ่งที่อยู่เบื้องหนังของพวกเขาให้เป็นที่ลุ่มหลงแก่พวกเขา ดังนั้นพระประกาศิต (การลงโทษ) แก่ประชาชาติต่าง ๆ ของพวกญินและมนุษย์ที่ได้ล่วงลับไปก่อนหน้าพวกเขานั้น ก็เป็นการสาสมแก่พวกเขาแล้ว แท้จริงพวกเขาเป็นผู้ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(41,26,'41:26','และบรรดาผู้ปฏิเสธศรัทธาได้กล่าวว่า พวกท่านอย่าฟังอัลกุรอานนี้ แต่จงทําเสียงอึกทึกในขณะนั้น หวังว่าพวกท่านจะมีชัยชนะ');
INSERT INTO "king_fahad_thai" VALUES(41,27,'41:27','และแน่นอนเราจะตอบแทนพวกเขาตามความชั่วที่พวกเขาได้กระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(41,28,'41:28','นั่นคือ การตอบแทนแก่เหล่าศัตรูของอัลลอฮฺ คือ ไฟนรก สําหรับพวกเขาจะพํานักอยู่ในนรกนั้นตลอดกาลเป็นการตอบแทนตามที่พวกเขาปฏิเสธสัญญาณต่าง ๆ ของเรา');
INSERT INTO "king_fahad_thai" VALUES(41,29,'41:29','และบรรดาผู้ปฏิเสธศรัทธากล่าวว่า ข้าแต่พระจ้าของเราได้โปรดให้เราได้เห็นทั้งสองแห่งหมู่ญินและมนุษย์ผู้ซึ่งทําให้เราหลงทางเพื่อที่เราจะให้มันทั้งสองอยู่ใต้เท้าของเรา เพื่อว่ามันทั้งสองจะได้อยู่ในหมู่ผู้เลวทรามยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(41,30,'41:30','แท้จริงบรรดาผู้กล่าวว่าอัลลอฮฺคือ พระเจ้าของพวกเราแล้วพวกเขาก็ยืนหยัดตามคํากล่าวนั้น มะลากิกะฮฺจะลงมาหาพวกเขา (โดยกล่าวกับพวกเขาว่า) พวกท่านอย่าหวาดกลัวและอย่าเศร้าสลดใจแต่จงต้อนรับข่าวดี คือสวนสวรรค์ซึ่งพวกเจ้าได้ถูกสัญญาไว้');
INSERT INTO "king_fahad_thai" VALUES(41,31,'41:31','พวกเราเป็นผู้อารักขาพวกท่านทั้งในการมีชีวิตอยู่ในโลกนี้และปรโลก และสําหรับพวกท่านในสวนสวรรค์นั้น จะได้สิ่งที่จิตใจของพวกท่านปรารถนา และสําหรับพวกท่านในสวนสวรรค์นั้นจะได้ในสิ่งที่พวกท่านเรียกร้อง');
INSERT INTO "king_fahad_thai" VALUES(41,32,'41:32','เป็นการต้อนรับด้วยความเมตตาจากพระผู้ทรงอภัยผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(41,33,'41:33','และผู้ใดเล่าจะมีคําพูดที่ดีเลิศยิ่งไปกว่าผู้เชิญชวนไปสู่อัลลอฮฺ และเขาปฏิบัติงานที่ดี และกล่าวว่า แท้จริงฉันเป็นคนหนึ่งในบรรดาผู้นอบน้อม');
INSERT INTO "king_fahad_thai" VALUES(41,34,'41:34','และความดีและความชั่วนั้นหาเท่าเทียมกันไม่ เจ้าจงขับไล่ (ความชั่ว) ด้วยสิ่งที่มันดีกว่า แล้วเมื่อนั้นผู้ที่ระหว่างเจ้ากับระหว่างเขาเคยเป็นอริกันก็จะกลับกลายเป็นเยี่ยงมิตรที่สนิทกัน');
INSERT INTO "king_fahad_thai" VALUES(41,35,'41:35','และไม่มีผู้ใดได้รับมัน (คุณธรรมดังกล่าว) นอกจากบรรดาผู้อดทน และจะไม่มีผู้ใดรับมันนอกจากผู้ที่มีโชคลาภอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(41,36,'41:36','และหากว่าการยุแหย่ใด ๆ จากชัยฏอนมายั่วยุเจ้าเข้า ก็จงขอความคุ้มครองต่ออัลลอฮฺเถิด แท้จริงพระองค์เป็นผู้ทรงได้ยินผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(41,37,'41:37','และส่วนหนึ่งจากสัญญาณทั้งหลายของพระองค์ คือ การมีกลางคืน และ กลางวัน และดวงอาทิตย์ และดวงจันทร์ พวกเจ้าอย่าได้สุญูด แต่อัลลอฮฺพระผู้ทรงสร้างพวกมัน หากพวกเจ้าจะเคารพภักดีแด่พระองค์เท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(41,38,'41:38','แต่ถ้าพวกเขาหยิ่งยะโส กระนั้นก็ดีบรรดาผู้ที่อยู่ ณ ที่พระเจ้าของเจ้า (มะลาอิกะฮฺ) ก็จะแซ่ซ้องสดุดีพระองค์ทั้งกลางคืนและกลางวัน โดยที่พวกเขาจะไม่เหนื่อยหน่าย');
INSERT INTO "king_fahad_thai" VALUES(41,39,'41:39','และส่วนหนึ่งจากสัญญาณทั้งหลายของพระองค์ คือ เจ้าจะเห็นแผ่นดินแห้งกรัง ต่อเมื่อพระองค์ทรงหลั่งน้ําฝนลงมาบนมัน มันก็จะมีชีวิตชีวาและให้พืชผล แท้จริง ผู้ซึ่งให้มันมีชีวิตขึ้นมานั้น ก็จะทรงให้ชีวิตแก่คนตายได้อย่างแน่นอน แท้จริงพระองค์นั้นเป็นผู้ทรงอนุภาพเหนือทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(41,40,'41:40','แท้จริงบรรดาผู้บิดเบือนทั้งหลายของเรา พวกเขาจะไม่ซ่อนเร้นไปจากเราได้ผู้ที่ถูกโยนลงในนรกจะดีกว่า หรือผู้ที่มาอย่างปลอดภัยในวันกิยามะฮฺ ? จงทําตามสิ่งที่พวกเจ้าปรารถนาเถิด แท้จริงพระองค์ทรงรู้เห็นในสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(41,41,'41:41','แท้จริงบรรดาผู้ปฏิเสธศรัทธาต่อข้อตักเตือน (อัลกุรอาน) เมื่อได้มีมายังพวกเขาและแท้จริงอัลกุรอานเป็นคัมภีร์ที่มีอํานาจยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(41,42,'41:42','ความเท็จจากข้างหน้าและจากข้างหลังจะไม่คืบคลานเข้าไปสู่อัลกุรอานได้ (เพราะ) เป็นการประทานจากพระผู้ทรงปรีชาญาณผู้ทรงได้รับการสรรเสริญ');
INSERT INTO "king_fahad_thai" VALUES(41,43,'41:43','ไม่มีสิ่งใดที่ถูกกล่าวแก่เจ้า เว้นแต่ได้มีการกล่าวขึ้นแล้ว แก่บรรดาร่อซูล ก่อนหน้าเจ้า แท้จริงพระเจ้าของเจ้านั้นแน่นอน พระองค์ทรงเป็นผู้อภัย และทรงเป็นผู้ลงโทษอย่างเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(41,44,'41:44','และมาตรว่า เราได้ประทานอัลกุรอานมาเป็นภาษาต่างชาติ แน่นอนพวกเขาจะกล่าวว่า ทําไมอายาตทั้งหลายของอัลกุรอานจึงไม่ชัดแจ้งเล่า ? (อัลกุรอาน) เป็นภาษาต่างชาติ และ (นะบี) เป็นคนอาหรับกระนั้นหรือ ? จงกล่าวเถิดมุฮัมมัด อัลกุรอานนั้นเป็นแนวทางที่เที่ยงธรรม และเป็นการบําบัดแก่บรรดาผู้ศรัทธา ส่วนบรรดาผู้ไม่ศรัทธานั้น อัลกุรอานจะทําให้หูของพวกเขาหนวก และนัยตาของพวกเขาบอด ชนเหล่านี้จะถูกร้องเรียกจากสถานที่อันไกล');
INSERT INTO "king_fahad_thai" VALUES(41,45,'41:45','และโดยแน่นอน เราได้ประทานคัมภีร์ แต่ได้เกิดมีการขัดแย้งกันขึ้นในนั้น และมาตรฐานว่าคํากล่าวหนึ่งจากพระเจ้าของเจ้ามิได้ถูกบันทึกไว้ก่อนแล้ว ก็คงจะถูกตัดสินระหว่างพวกเขาอย่างแน่นอน และแท้จริงพวกเขานั้นอยู่ในการคลางแคลงสงสัยจากในคัมภีร์นั้น');
INSERT INTO "king_fahad_thai" VALUES(41,46,'41:46','ผู้ใดกระทําความดีก็จะได้แก่ตัวของเขา และผู้ใดกระทําความชั่วก็จะได้แก่ตัวของเขาเอง และพระเจ้าของเจ้านั้นมิทรงอธรรมต่อปวงบ่าวของพระองค์');
INSERT INTO "king_fahad_thai" VALUES(41,47,'41:47','ความรู้แห่งยามอวสานนั้นถูกอ้างกลับไปยังพระองค์ ไม่มีผลไม้ใดออกมาจากเปลือกของมัน และไม่มีหญิงใดอุ้มครรภ์ หรือคลอดทารกออกมา เว้นแต่ด้วยความรอบรู้ของพระองค์ และวันที่พระองค์ทรงร้องเรียกพวกเขาว่า ไหนเล่าภาคีทั้งหลายของข้า พวกเขาจะกล่าวว่า เราขอยืนยันต่อพระองค์ว่า ไม่มีผู้ใดในหมู่พวกเราเป็นพยานได้');
INSERT INTO "king_fahad_thai" VALUES(41,48,'41:48','และสิ่งที่พวกเขาวิงวอนกราบไหว้ ได้เตลิดหนีไปจากพวกเขา และพวกเขาเชื่อมั่นว่า พวกเขานั้นไม่มีทางที่จะหลบหนีไปได้');
INSERT INTO "king_fahad_thai" VALUES(41,49,'41:49','มนุษย์จะไม่เบื่อหน่ายต่อการวิงวอนขอความดี แต่เมื่อความทุกข์ยากประสบแก่พวกเขาเข้าเขาก็จะท้อถอยหมดอาลัย');
INSERT INTO "king_fahad_thai" VALUES(41,50,'41:50','และเมื่อเราได้ให้เขาลิ้มรสความเมตตาจากเราหลังจากความทุกข์ยากได้ประสบแก่เขา แน่นอนเขาก็จะกล่าวว่า นี่คือความสามารถของฉัน และฉันไม่คิดว่ายามอวสานนั้นจะเกิดขึ้น แต่ถ้าฉันถูกส่งกลับไปยังพระเจ้าของฉัน แน่นอนฉันจะมีคุณความดี ณ ที่พระองค์ ดังนั้นเรา (อัลลอฮฺ) จะให้บรรดาผู้ปฏิเสธศรัทธาได้รู้เห็นในสิ่งที่พวกเขาได้กระทําไว้ และแน่นอนเราจะให้พวกเขาได้ลิ้มรสการลงโทษอันรุนแรง');
INSERT INTO "king_fahad_thai" VALUES(41,51,'41:51','และเมื่อเราได้ให้ความโปรดปรานแก่มนุษย์ เขาก็เหินห่างและปลีกตัวออกไปข้าง ๆ และเมื่อความทุกข์ประสบแก่เขา เขาก็เป็นผู้วิงวอนขออย่างยืดเยื้อ');
INSERT INTO "king_fahad_thai" VALUES(41,52,'41:52','จงกล่าวเถิดมุฮัมมัด พวกท่านไม่เห็นดอกหรือว่า ถ้าอัลกุรอานมาจากอัลลอฮฺ แล้วพวกท่านปฏิเสธที่จะศรัทธาต่ออัลกุรอาน ใครเล่าจะหลงทางมากไปกว่าผู้ที่อยู่ในการแตกแยกอย่างลิบลับ');
INSERT INTO "king_fahad_thai" VALUES(41,53,'41:53','เราจะให้พวกเขาได้เห็นสัญญาณทั้งหลายของเราในขอบเขตอันไกลโพ้นและ ในตัวของพวกเขาเอง จนกระทั่งจะเป็นประจักษ์แก่พวกเขาว่า อัลกุรอานนั้นเป็นความจริง ยังไม่พอเพียงอีกหรือ ที่พระเจ้าของเจ้านั้นทรงเป็นพยานต่อทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(41,54,'41:54','พึงรู้เถิดว่า แท้จริงพวกเขาอยู่ในการสงสัยเกี่ยวกับการพบพระเจ้าของพวกเขาพึงรู้เถิดว่า แท้จริงพระองค์เป็นผู้ทรงล้อมทุก ๆ สิ่งไว้');
INSERT INTO "king_fahad_thai" VALUES(42,1,'42:1','แท้จริงพระองค์ทรงรอบรู้ทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(42,2,'42:2','อัยนฺ ซีน ก๊อฟ');
INSERT INTO "king_fahad_thai" VALUES(42,3,'42:3','เช่นนั้นแหละ ได้มีวะฮียฺมายังเจ้าและมายังบรรดา(ร่อซูล)ก่อนหน้าเจ้า อัลลอฮฺผู้ทรงอํานาจ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(42,4,'42:4','สิ่งที่อยู่ในชั้นฟ้าทั้งหลาย และสิ่งที่อยู่ในแผ่นดินเป็นสิทธิของพระองค์ และพระองค์เป็นผู้สูงส่ง ผู้ทรงยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(42,5,'42:5','ชั้นฟ้าทั้งหลายแทบจะพังทลายลงมาจากเบื้องบนพวกมันขณะที่มะลาอิกะฮฺต่างก็แซ่ซ้องสดุดีด้วยการสรรเสริญต่อพระเจ้าของพวกเขา และขออภัยให้แก่ผู้ที่อยู่ในโลกนี้ พึงรู้เถิดว่า แท้จริงอัลลอฮฺนั้น พระองค์เป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(42,6,'42:6','และบรรดาผู้ที่ยึดถือเอาผู้คุ้มครองอื่นจากพระองค์นั้น อัลลอฮฺทรงเฝ้าดูพวกเขาและเจ้ามิใช่ผู้ดูแลคุ้มครองพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(42,7,'42:7','และเช่นนั้นแหละ เราได้วะฮียฺอัลกุรอานเป็นภาษาอาหรับแก่เจ้า เพื่อเจ้าจะได้ตักเตือนอุมมุลกุรอ (ชาวมักกะฮฺ) และผู้ที่อยู่รอบเมืองนั้น และเตือนถึงวันแห่งการชุมนุมซึ่งไม่มีข้อสงสัยใด ๆ ในวันนั้น พวกหนึ่งจะอยู่ในสวรรค์ และอีกพวกหนึ่งจะอยู่ในไฟที่ลุกช่วงโชติ');
INSERT INTO "king_fahad_thai" VALUES(42,8,'42:8','และหากว่าอัลลอฮฺทรงประสงค์ แน่นอนจะทรงทําให้พวกเขาเป็นประชาชาติเดียวกัน แต่พระองค์จะทรงให้ผู้ที่พระองค์ทรงประสงค์เข้าสู่ความเมตตาของพระองค์ ส่วนบรรดาผู้อธรรมนั้น พวกเขาไม่มีผู้คุ้มครอง และไม่มีผู้ช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(42,9,'42:9','หรือว่าพวกเขาได้ยึดถือเอาคนอื่นจากพระองค์เป็นผู้คุ้มครอง แต่อัลลอฮฺคือผู้คุ้มครอง และพระองค์คือผู้ทรงให้ชีวิตแก่คนตาย และพระองค์คือผู้ทรงอานุภาพเหนือทุกสิ่งอย่าง');
INSERT INTO "king_fahad_thai" VALUES(42,10,'42:10','และอันใดที่พวกเจ้าขัดแย้งกันในเรื่องนั้นๆ ดังนั้นการช้ขาดตัดสินย่อมกลับไปหาอัลลอฮฺ นั่นคืออัลลอฮฺพระเจ้าของฉัน แต่พระองค์เท่านั้นฉันขอมอบหมายและยังพระองค์เท่านั้นฉันจะกลับไปหา');
INSERT INTO "king_fahad_thai" VALUES(42,11,'42:11','พระองค์ผู้ทรงสร้างชั้นฟ้าทั้งหลายและแผ่นดิน พระองค์ทรงทําให้มีคู่ครองแก่พวกเจ้า จากตัวของพวกเจ้าเอง และจากปศุสัตว์ทรงให้มีคู่ผัวเมีย ด้วยเหตุนี้พระองค์ทรงแพร่พันธุ์พวกเจ้าให้มากมาย ไม่มีสิ่งใดเสมอเหมือนพระองค์ และพระองค์เป็นผู้ทรงได้ยิน ผู้ทรงเห็น');
INSERT INTO "king_fahad_thai" VALUES(42,12,'42:12','กุญแจ (การควบคุมกิจการ) แห่งชั้นฟ้าทั้งหลายและแผ่นดินเป็นสิทธิ์ของพระองค์ พระองค์ทรงเพิ่มพูนปัจจัยยังชีพแก่ผู้ที่พระองค์ทรงประสงค์และทรงให้คับแคบ');
INSERT INTO "king_fahad_thai" VALUES(42,13,'42:13','พระองค์ได้ทรงกําหนดศาสนาแก่พวกเจ้าเช่นเดียวกับที่พระองค์ได้ทรงบัญชาแก่นูหฺ และที่เราได้วะฮียฺแก่เจ้าก็เช่นเดียวกับที่เราได้บัญชาแก่อิบรอฮิม และมูซา และอีซาว่า พวกเจ้าจงดํารงศาสนาไว้ให้คงมั่น และอย่าแตกแยกกันในเรื่องศาสนา แต่เป็นเรื่องใหญ่แก่พวกตั้งภาคีที่เจ้าเรียกร้อง เชิญชวนพวกเขาไปสู่ศาสนานั้น อัลลอฮฺทรงเลือกสําหรับพระองค์ผู้ที่พระองค์ทรงประสงค์ และทรงชี้แนะทางสู่พระองค์ผู้ที่ผินหน้าสู่พระองค์');
INSERT INTO "king_fahad_thai" VALUES(42,14,'42:14','และพวกเขามิได้แตกแยกกันเว้นแต่หลังจากได้มีความรู้มายังพวกเขาแล้ว ทั้งนี้เพราะความริษยาระหว่างพวกเขากันเอง และหากมิใช่ลิขิตได้บันทึกไว้ที่พระเจ้าของเจ้าจนถึงวาระที่กําหนดไว้แล้ว แน่นอนก็จะในระหว่างพวกเขาถูกตัดสิน และแท้จริงบรรดาผู้ได้รับมรดก คัมภีร์นี้หลังจากพวกเขานั้น อยู่ในการสงสัยวุ่นวายเกี่ยวกับคัมภีร์นั้น');
INSERT INTO "king_fahad_thai" VALUES(42,15,'42:15','ดังนั้น เพื่อการนี้แหละเจ้าจงเรียกร้องเชิญชวนและดํารงมั่นอยู่ในแนวทางที่เที่ยงธรรมดังที่เจ้าได้รับบัญชา และอย่าได้ปฏิบัติตามอารมณ์ต่ําของพวกเขา และจงกล่าวว่า ฉันได้ศรัทธาในสิ่งที่มีอยู่ในคัมภีร์ตามที่อัลลอฮฺทรงประทานลงมา และฉันรับบัญชาให้ตัดสินระหว่างพวกท่านด้วยความเที่ยงธรรม อัลลอฮฺคือ พระเจ้าของฉันและพระเจ้าของพวกท่าน (การตอบแทน) การงานของฉันก็จะได้แก่ฉันและ (การตอบแทน) การงานของพวกท่านก็จะได้แก่พวกท่าน ไม่มีการโต้แย้งใด ๆ ระหว่างพวกเรากับพวกท่าน อัลลอฮฺจะทรงรวบรวมพวกเราทั้งหมด และยังพระองค์คือการกลับไป');
INSERT INTO "king_fahad_thai" VALUES(42,16,'42:16','ส่วนบรรดาผู้โต้แย้งเกี่ยวกับ (ศาสนาของ) อัลลอฮฺหลังจาก (ศาสนานั้น) ได้เป็นที่ยอมรับแล้ว การโต้แย้งของพวกเขาปราศจากเหตุผลในทัศนะของพระเจ้าของพวกเขา และพวกเขาจะได้รับความกริ้วโกรธ และพวกเขาจะได้รับการลงโทษอย่างสาหัส');
INSERT INTO "king_fahad_thai" VALUES(42,17,'42:17','อัลลอฮฺคือผู้ประทานคัมภีร์นี้ (อัลกุรอาน) ลงมาด้วยความจริงและทรงประทานความสมดุล และอะไรเล่าจะทําให้เจ้ารู้ได้ บางทียามอวสานนี้อยู่ใกล้ ๆ นี่เอง');
INSERT INTO "king_fahad_thai" VALUES(42,18,'42:18','บรรดาผู้ไม่ศรัทธาในเรื่องนี้เร่งเร้าจะให้เกิดขึ้น ส่วนบรรดาผู้ศรัทธาก็มีความหวั่นกลัวในเรื่องยามอวสาน และพวกเขารู้ว่ามันเป็นความจริง พึงรู้เถิดว่าแท้จริงบรรดาผู้โต้เถียงเกี่ยวกับเรื่องยามอวสานนั้นอยู่ในการหลงผิดอันไกลลิบอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(42,19,'42:19','อัลลอฮฺทรงเอ็นดูต่อปวงบ่าวของพระองค์ ทรงประทานปัจจัยยังชีพแก่ผู้ที่พระองค์ทรงประสงค์ และพระองค์เป็นผู้ทรงพลัง ผู้ทรงอํานาจ');
INSERT INTO "king_fahad_thai" VALUES(42,20,'42:20','ผู้ใดปรารถนาผลตอบแทนของปรโลกเราจะเพิ่มผลตอบแทนของเขาแก่เขา และผู้ใดปรารถนาผลตอบแทนของโลกดุนยา เราจะให้แก่เขาบางส่วนในสิ่งนั้น และสําหรับเขาจะไม่ได้ส่วนใดอีกในปรโลก');
INSERT INTO "king_fahad_thai" VALUES(42,21,'42:21','หรือว่าพวกเขามีภาคีต่าง ๆ ที่ได้กําหนดศาสนาแก่พวกเขา ซึ่งอัลลอฮฺมิได้ทรงอนุมัติและหากมิใช่ลิขิตแห่งการตัดสิน (ที่ได้กําหนดไว้ก่อนแล้ว) ก็คงมีการตัดสินใจในระหว่างพวกเขา แท้จริงบรรดาผู้อธรรมสําหรับพวกเขาได้รับการลงโทษอันเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(42,22,'42:22','เจ้าจะเห็นบรรดาผู้อธรรมเป็นผู้หวั่นกลัวเนื่องจากสิ่งที่พวกเขาขวนขวายเอาไว้ และมันจะต้องเกิดขึ้นแก่พวกเขา ส่วนบรรดาผู้ศรัทธาและปฏิบัติความดีต่าง ๆ จะอยู่ในอุทยานแห่งสวนสวรรค์ สําหรับพวกเขาจะได้สิ่งที่พวกเขาปรารถนา ณ ที่พระเจ้าของพวกเจ้านั่นคือความโปรดปรานอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(42,23,'42:23','นั่นคือ (ความโปรดปราน) อัลลอฮฺทรงแจ้งข่าวดีแก่ปวงบ่าวของพระองค์ ซึ่งพวกเขาได้ศรัทธาและปฏิบัติความดีต่าง ๆ จงกล่าวเถิดมุฮัมมัด ฉันมิได้ขอร้องค่าตอบแทนใด ๆ เพื่อการนี้ เว้นแต่เพื่อความรักใคร่ในเครือญาติและผู้ใดกระทําความดี เราจะเพิ่มพูนความดีในนั้นให้แก่เขา แท้จริงอัลลอฮฺเป็นผู้ทรงอภัยผู้ทรงชื่นชม (เพราะการภักดีของพวกเขา)');
INSERT INTO "king_fahad_thai" VALUES(42,24,'42:24','หรือพวกเขากล่าวว่า เขา (มุฮัมมัด) ได้อุปโลกน์ความเท็จให้แก่อัลลอฮฺ ดังนั้นหากอัลลอฮฺทรงประสงค์ พระองค์จะทรงผนึกหัวใจเจ้าก็ได้ และอัลลอฮฺทรงขจัดความเท็จให้หมดไป และทรงยืนยันความจริงด้วยคํากล่าวของพระองค์ (อัลกุรอาน) แท้จริงพระองค์ทรงรอบรู้สิ่งที่อยู่ในทรวงอก');
INSERT INTO "king_fahad_thai" VALUES(42,25,'42:25','และพระองค์คือผู้ทรงรับการขออภัยโทษจากปวงบ่าวของพระองค์ และทรงอภัยจากความผิดทั้งหลายและพระองค์ทรงรอบรู้สิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(42,26,'42:26','และพระองค์ทรงตอบรับ (การวิงวอน) ของบรรดาผู้ศรัทธา และพวกเขากระทําความดีทั้งหลาย และพระองค์จะทรงเพิ่มพูนจากความโปรดปรานของพระองค์แก่พวกเขา และบรรดาผู้ปฏิเสธศรัทธานั้น พวกเขาจะได้รับการลงโทษอย่างสาหัส');
INSERT INTO "king_fahad_thai" VALUES(42,27,'42:27','และหากอัลลอฮฺทรงประทานปัจจัยยังชีพอย่างกว้างขวางแก่ปวงบ่าวของพระองค์ แน่นอนพวกเขาก็จะก่อความเสียหายขึ้นในแผ่นดิน แต่พระองค์ทรงประทานให้ตามปริมาณที่พระองค์ทรงประสงค์ แท้จริงพระองค์เป็นผู้ทรงตระหนักรู้ ผู้ทรงเห็นต่อปวงบ่าวของพระองค์');
INSERT INTO "king_fahad_thai" VALUES(42,28,'42:28','และพระองค์คือผู้ทรงหลั่งน้ําฝนลงมาหลังจากที่พวกเขาหมดหวังกันแล้ว และพระองค์ทรงแผ่กระจายพระเมตตาของพระองค์ และพระองค์เป็นผู้ทรงคุ้มครอง ผู้ทรงได้รับการสรรเสริญ');
INSERT INTO "king_fahad_thai" VALUES(42,29,'42:29','และหนึ่งจากสัญญาณทั้งหลายของพระองค์ คือการสร้างชั้นฟ้าทั้งหลายและแผ่นดิน และสิ่งที่พระองค์ทรงแพร่กระจายไปทั่วในระหว่างทั้งสองนั้นแก่สิ่งที่มีชีวิตทั้งหลายและพระองค์เป็นผู้ทรงอานุภาพที่จะรวบรวมพวกเขาเมื่อพระองค์ทรงประสงค์');
INSERT INTO "king_fahad_thai" VALUES(42,30,'42:30','และเคราะห์กรรมอันใดที่ประสบแก่พวกเจ้า ก็เนื่องด้วยน้ํามือของพวกเจ้าได้ขวนขวายได้ และพระองค์ทรงอภัย (ความผิดให้) มากต่อมากแล้ว');
INSERT INTO "king_fahad_thai" VALUES(42,31,'42:31','และพวกเจ้าไม่สามารถจะหนีรอดไปได้ในแผ่นดินนี้ และอื่นจากอัลลอฮฺ สําหรับพวกเจ้านั้นไม่มีผู้คุ้มครอง และไม่มีผู้ช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(42,32,'42:32','และหนึ่งจากสัญญาณทั้งหลายของพระองค์คือมีนาวาทั้งหลายเดินราบเรียบอยู่ในท้องทะเลเยี่ยงภูเขา');
INSERT INTO "king_fahad_thai" VALUES(42,33,'42:33','ถ้าพระองค์ทรงประสงค์ พระองค์ก็จะทรงให้ลมหยุดนิ่งแล้วมัน (นาวานั้น) ก็จะหยุดลอยนิ่งอยู่ในท้องทะเลนั้น แท้จริงในการนั้นย่อมเป็นสัญญาณแก่ผู้อดทน ผู้ขอบคุณทุกคน');
INSERT INTO "king_fahad_thai" VALUES(42,34,'42:34','หรือพระองค์จะทรงทําให้มัน (นาวานั้น) อับปางลงก็ได้ เนื่องด้วย (ความชั่ว) ที่พวกเขาขวนขวายเอาไว้ และพระองค์ทรงอภัย(ความผิดให้) มากต่อมากแล้ว');
INSERT INTO "king_fahad_thai" VALUES(42,35,'42:35','และเพื่อให้บรรดาผู้โต้แย้งเกี่ยวกับสัญญาณทั้งหลายของเราจะได้รู้ว่า สําหรับพวกเขานั้นไม่มีทางที่จะหลบหนีไปได้');
INSERT INTO "king_fahad_thai" VALUES(42,36,'42:36','และสิ่งใดที่พวกเจ้าได้รับนั้นเป็นเพียงการสนุกสนานเพลิดเพลินแห่งชีวิตของโลกนี้เท่านั้น แต่สิ่งที่มีอยู่ ณ ที่อัลลอฮฺนั้น ดีกว่าและจีรังกว่า สําหรับบรรดาผู้ศรัทธาและพวกเขามอบหมายไว้วางใจแด่พระเจ้าของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(42,37,'42:37','และบรรดาผู้ที่หลีกเลี่ยงการทําบาปใหญ่และการทําลามก และเมื่อพวกเขาโกรธพวกเขาก็อภัยให้');
INSERT INTO "king_fahad_thai" VALUES(42,38,'42:38','และบรรดาผู้ตอบรับต่อพระเจ้าของพวกเขาและดํารงละหมาด และกิจการของพวกเขามีการปรึกษาหารือระหว่างพวกเขาและเขาบริจาคสิ่งที่เราได้ให้เครื่องปัจจัยยังชีพแก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(42,39,'42:39','และบรรดาผู้ที่เมื่อมีความยุติธรรมเกิดขึ้นแก่พวกเขา พวกเขาก็แก้แค้นตอบแทน');
INSERT INTO "king_fahad_thai" VALUES(42,40,'42:40','และการตอบแทนความชั่วคือความชั่วเยี่ยงมัน แต่ผู้ใดอภัย และไกล่เกลี่ยคืนดีกันรางวัลตอบแทนของเขาอยู่ที่อัลลอฮฺ แท้จริงพระองค์ไม่ชอบบรรดาผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(42,41,'42:41','แต่ถ้าผู้ใดแก้แค้นตอบแทนหลังจากได้รับความอธรรม ชนเหล่านั้นจะไม่มีทางตําหนิแก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(42,42,'42:42','ส่วนที่จะเกิดโทษนั้นได้แก่บรรดาผู้ที่อธรรมต่อมนุษย์ และก่อความเสียหายให้เกิดขึ้นในแผ่นดินโดยปราศจากความเป็นธรรม ชนเหล่านี้พวกเขาจะได้รับการลงโทษอันเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(42,43,'42:43','และแน่นอนผู้ที่อดทนและให้อภัย แท้จริงนั่นคือ ส่วนหนึ่งจากกิจการที่หนักแน่นมั่นคง');
INSERT INTO "king_fahad_thai" VALUES(42,44,'42:44','และผู้ใดที่อัลลอฮฺทรงให้เขาหลงทาง ดังนั้นสําหรับเขาจะไม่มีผู้คุ้มครองภายหลังจากพระองค์และเจ้าจะเห็นบรรดาผู้อธรรมเมื่อพวกเขามองเห็นการลงโทษ พวกเขาจะกล่าวว่า มีทางบ้างไหมที่จะกลับไป (ยังโลกดุนยา)');
INSERT INTO "king_fahad_thai" VALUES(42,45,'42:45','และเจ้าจะเห็นพวกเขาถูกนํามาข้างหน้าไฟนรกพวกเขาจะถ่อมตัวลงอย่างน่าสังเวชมองดูอย่างหลบสายตา และบรรดาผู้ศรัทธาจะกล่าวว่า แท้จริงพวกที่ขาดทุนคือ บรรดาผู้ที่ทําตัวของพวกเขาและครอบครัวของพวกเขาให้เสียหายยับเยินในวันกิยามะฮฺ พึงทราบเถิด แท้จริงบรรดาผู้อธรรมนั้นอยู่ในการลงโทษอันถาวร');
INSERT INTO "king_fahad_thai" VALUES(42,46,'42:46','และสําหรับพวกเขาจะไม่มีผู้คุ้มครองให้ความช่วยเหลือแก่พวกเขานอกจากอัลลอฮฺ และผู้ใดที่อัลลอฮฺทรงให้เขาหลงทาง เขาผู้นั้นก็จะไม่มีทาง (ไปสู่การฮิดายะฮฺได้)');
INSERT INTO "king_fahad_thai" VALUES(42,47,'42:47','จงตอบรับการเรียกร้องของพระเจ้าของพวกเจ้าเถิด ก่อนที่วันหนึ่งจะมาถึง ซึ่งจะไม่มีทางหลีกเลี่ยงไปจากอัลลอฮฺได้ และในวันนั้นสําหรับพวกเจ้าจะไม่มีที่พักพิง และพวกเจ้าก็จะไม่มีทางปฏิเสธด้วย');
INSERT INTO "king_fahad_thai" VALUES(42,48,'42:48','แต่ถ้าพวกเขาผินหลังให้ (ไม่ยอมรับการเรียกร้อง) ดังนั้นเรามิได้ส่งเจ้ามายังพวกเขาเพื่อเป็นผู้คุ้มกันรักษา หน้าที่ของเจ้ามิใช่อื่นใดนอกจากการเผยแผ่เท่านั้น และแท้จริงถ้าเราจะให้มนุษย์ลิ้มรสความเมตตาจากเรา เขาก็จะยินดีปรีดาต่อความเมตตานั้น และหากเคราะห์กรรมประสบแก่พวกเขา เนื่องจากน้ํามือของพวกเขาได้ประกอบเอาไว้ ดังนั้นแน่นอนมนุษย์นั้นเป็นผู้เนรคุณเสมอ');
INSERT INTO "king_fahad_thai" VALUES(42,49,'42:49','อํานาจเด็ดขาดแห่งชั้นฟ้าทั้งหลายและแผ่นดินนั้นเป็นสิทธิ์ของอัลลอฮฺ พระองค์ทรงสร้างสิ่งที่พระองค์ทรงประสงค์ พระองค์ทรงประทานลูกหญิงแก่ผู้ที่พระองค์ทรงประสงค์ และทรงประทานลูกชายแก่ผู้ที่พระองค์ทรงประสงค์');
INSERT INTO "king_fahad_thai" VALUES(42,50,'42:50','หรือพระองค์ทรงประทานรวมให้แก่พวกเขาทั้งหลาย และลูกหญิง และพระองค์ทําให้ผู้ที่พระองค์ทรงประสงค์เป็นหมัน แท้จริงพระองค์เป็นผู้ทรงรอบรู้ ผู้ทรงอนุภาพ');
INSERT INTO "king_fahad_thai" VALUES(42,51,'42:51','และไม่เป็นการบังควรแก่มนุษย์คนใดที่จะให้อัลลอฮฺตรัสแก่เขาเว้นแต่โดยทางวะฮียฺ หรือโดยทางเบื้องหลังม่าน หรือโดยที่พระองค์จะส่งทูตมา แล้วเขา (มะลัก) ก็จะนําวะฮียฺมาตามที่พระองค์ทรงประสงค์โดยบัญชาของพระองค์ แท้จริงพระองค์เป็นผู้ทรงสูงส่งผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(42,52,'42:52','และเช่นนั้นแหละเราได้วะฮียฺอัลกุรอาน แก่เจ้าตามบัญชาของเรา เจ้าไม่เคยรู้มาก่อนเลยว่าอะไรคือคัมภีร์ และอะไรคือการศรัทธาแต่ว่าเราได้ทําให้อัลกุรอานเป็นแสงสว่างเพื่อชี้แนะทาง โดยนัยนั้นแก่ผู้ที่เราประสงค์จากปวงบ่าวของเรา และแท้จริงเจ้านั้น จะได้รับการชี้แนะสู่ทางอันเที่ยงธรรมอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(42,53,'42:53','ทางของอัลลอฮฺ ซึ่งสิ่งที่อยู่ในชั้นฟ้าทั้งหลาย และสิ่งที่อยู่ในแผ่นดินเป็นกรรมสิทธิ์ของพระองค์ พึงทราบเถิดกิจการทั้งหลายย่อมไปสู่อัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(43,1,'43:1','ฮามีน');
INSERT INTO "king_fahad_thai" VALUES(43,2,'43:2','ขอสาบานด้วยคัมภีร์อันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(43,3,'43:3','แท้จริงเราได้ทําให้คัมภีร์เป็นกุรอานภาษาอาหรับ เพื่อพวกเจ้าจะได้ใช้สติปัญญา');
INSERT INTO "king_fahad_thai" VALUES(43,4,'43:4','และแท้จริงอัลกุรอานนั้นอยู่ในแม่บทแห่งคัมภีร์ (อัลลูฮุลมะฮฺฟูซ) ณ ที่เรา คือสูงส่งพรั่งพร้อมด้วยปรัชญา');
INSERT INTO "king_fahad_thai" VALUES(43,5,'43:5','ดังนั้นจะให้เราหันเหข้อตักเตือนนี้จากพวกเจ้าเสียทีเดียว เพราะพวกเจ้าเป็นหมู่ชนผู้ฝ่าฝืนกระนั้นหรือ');
INSERT INTO "king_fahad_thai" VALUES(43,6,'43:6','และกี่มากน้อยแล้วที่เราได้ส่งนะบีมาในชนชาติรุ่นก่อน ๆ');
INSERT INTO "king_fahad_thai" VALUES(43,7,'43:7','และไม่มีนะบีคนใดที่ได้มายังพวกเขาเว้นแต่พวกเขาจะได้เยาะเย้ยเขา (นะบี)');
INSERT INTO "king_fahad_thai" VALUES(43,8,'43:8','ฉะนั้นเราจึงได้ทําลาย (หมู่ชน) ซึ่งเข้มแข็งทางสมรรถภาพมากกว่าพวกเขา และอุทาหรณ์ของชนชาติรุ่นก่อน ๆ ก็ได้ล่วงลับไปแล้ว');
INSERT INTO "king_fahad_thai" VALUES(43,9,'43:9','และหากเจ้าถามพวกเขาใครเล่าเป็นผู้สร้างชั้นฟ้าทั้งหลาย และแผ่นดินนี้ แน่นอนพวกเขาจะกล่าวว่า พระผู้ทรงอํานาจ ผู้ทรงรอบรู้ทรงสร้างมันเหล่านั้น');
INSERT INTO "king_fahad_thai" VALUES(43,10,'43:10','ผู้ทรงทําให้แผ่นดินแผ่กว้างสําหรับพวกเจ้า และทรงทําให้มีถนนหนทางในแผ่นดินนั้นสําหรับพวกเจ้า เพื่อพวกเจ้าจะบรรลุสู่เป้าหมาย');
INSERT INTO "king_fahad_thai" VALUES(43,11,'43:11','และเป็นผู้ทรงหลั่งน้ําลงมาจากฟากฟ้าตามปริมาณ และด้วยน้ํานั้นเราได้ทรงทําให้แผ่นดินที่แห้งแล้งมีชีวิตชีวาขึ้น เช่นนั้นแหละพวกเจ้าจะถูกให้ออกมา (จากกุบูร)');
INSERT INTO "king_fahad_thai" VALUES(43,12,'43:12','และเป็นผู้ทรงสร้างทุกสิ่งทั้งหมดเป็นคู่ ๆ และทรงทําให้เรือและปศุสัตว์สําหรับพวกเจ้าเป็นสิ่งที่พวกเจ้าใช้ขี่เป็นพาหนะ');
INSERT INTO "king_fahad_thai" VALUES(43,13,'43:13','เพื่อพวกเจ้าจะได้นั่งอยู่บนหลังของมันอย่างมั่นคง แล้วพวกเจ้าจะได้รําลึกถึงความโปรดปรานของพระเจ้าของพวกเจ้า เมื่อพวกเจ้าได้นั่งขี่บนมันอย่างเรียบร้อยแล้ว พวกเจ้าก็จะกล่าวว่า มหาบริสุทธิ์ผู้ทรงให้พาหนะนี้เป็นความสะดวกแก่เรา และเรานั้นไม่สามารถจะควบคุมมันได้');
INSERT INTO "king_fahad_thai" VALUES(43,14,'43:14','และแท้จริงเราจะต้องเป็นผู้กลับไปสู่พระเจ้าของเราอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(43,15,'43:15','และพวกเขาได้ตั้งบางส่วนจากปวงบ่าวของพระองค์คู่เคียงกับพระองค์ แท้จริงมนุษย์นั้นเนรคุณอย่างชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(43,16,'43:16','หรือว่าพระองค์ยึดเอาลูกหญิงจากสิ่งที่พระองค์ทรงสร้างและพระองค์ทรงเลือกลูกชายให้แก่พวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(43,17,'43:17','และเมื่อผู้ใดในหมู่พวกเขาได้รับข่าวตามที่เขาได้ตั้งอุปมาพระผู้ทรงกรุณาปรานี ใบหน้าของเขากลายเป็นหมองคล้ํา และเศร้าสลด');
INSERT INTO "king_fahad_thai" VALUES(43,18,'43:18','และผู้ที่ถูกเติบโตเลี้ยงดูมาท่ามกลางเครื่องประดับ และในการโต้ถียงก็ไม่มีอะไรชัดแจ้ง (จะตั้งให้เป็นภาคีต่ออัลลอฮฺ) กระนั้นหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(43,19,'43:19','และพวกเขาได้ตั้งมะลาอิกะฮฺ ซึ่งพวกเขาเป็นบ่าวของพระผู้ทรงกรุณาปรานีว่าเป็นเพศหญิง และพวกเขารู้เห็นเป็นพยานในการสร้างพวกเขาทั้งหลาย (มะลาอิกะฮฺ) กระนั้นหรือ ? การเป็นพยานของพวกเขาจะถูกบันทึกไว้และพวกเขาจะถูกสอบสวน');
INSERT INTO "king_fahad_thai" VALUES(43,20,'43:20','และพวกเขากล่าวอีกว่า หากพระผู้ทรงกรุณาปรานี ทรงประสงค์พวกเราจะไม่เคารพสักการะพวกเขาดอก (มะลาอิกะฮฺ) พวกเขาไม่มีความรู้อันใดในเรื่องนั้น พวกเขาไม่มีอะไรเลยนอกจากคาดคะเนเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(43,21,'43:21','หรือว่าเราได้ประทานคัมภีร์เล่มนึ่งแก่พวกเขาก่อนหน้านั้น ดังนั้นพวกเขาจึงยึดถือคัมภีร์นั้นไว้อย่างมั่นคง');
INSERT INTO "king_fahad_thai" VALUES(43,22,'43:22','เปล่าเลย พวกเขากล่าวว่า แท้จริงเราได้พบเห็นบรรพบุรุษของเราอยู่ในแนวทางนี้ ดังนั้นเราจึงดําเนินตามแนวทางของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(43,23,'43:23','และเช่นนั้นแหละ เรามิได้ส่งผู้ตักเตือนคนใดก่อนหน้าเจ้าไปยังเมืองใด เว้นแต่บรรดาผู้ฟุ่มเฟือยของมัน (เมืองนั้น) จะกล่าวว่า แท้จริงเราได้พบเห็นบรรพบุรุษของเราอยู่ในแนวทางนี้ ดังนั้นเราจึงดําเนินตามแนวทางของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(43,24,'43:24','เขา (ร่อซูลของพวกเขา) กล่าวว่าหากว่าฉันได้นํามาให้พวกท่าน ซึ่งแนวทางที่ถูกต้องกว่าที่พวกท่านได้พบเห็นบรรพบุรุษของพวกท่านยึดถืออยู่เล่า ? พวกเขากล่าวว่าแท้จริงเราเป็นผู้ปฏิเสธศรัทธาต่อสิ่งที่พวกท่านถูกส่งมานั้น');
INSERT INTO "king_fahad_thai" VALUES(43,25,'43:25','ดังนั้นเราได้ตอบแทนพวกเขา บัดนี้จงดูเถิดว่าบั้นปลายของบรรดาผู้ปฏิเสธนั้นจะเป็นเช่นไร');
INSERT INTO "king_fahad_thai" VALUES(43,26,'43:26','และจงรําลึกถึงเมื่ออิบรอฮีมได้กล่าวแก่บิดาของเขา และหมู่ชนของเขาว่า แท้จริงฉันขอปลีกตัวจากสิ่งที่พวกท่านเคารพภักดี');
INSERT INTO "king_fahad_thai" VALUES(43,27,'43:27','นอกจาก (อัลลอฮฺ) ซึ่งทรงบังเกิดฉันเท่านั้น เพราะแท้จริงพระองค์จะทรงชี้แนะทางที่ถูกต้องแก่ฉัน');
INSERT INTO "king_fahad_thai" VALUES(43,28,'43:28','และเขา (อิบรอฮีม) ได้ทําให้คํากล่าว (ชะฮาดะฮฺ) อยู่คงต่อไปในลูกหลานของเขา หวังว่าพวกเขาจะกลับมาสํานึกผิด');
INSERT INTO "king_fahad_thai" VALUES(43,29,'43:29','ยิ่งกว่านั้น ข้าได้ให้พวกเขาเหล่านั้น และบรรพบุรุษของพวกเขาหลงระเริงอยู่จนกระทั่งได้มีสัจธรรม (อัลกุรอาน) และร่อซูล ผู้ประกาศสาส์นอย่างชัดแจ้งมายังพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(43,30,'43:30','ครั้งเมื่อได้มีสัจธรรมมายังพวกเขา พวกเขาก็กล่าวว่า นี้คือมายากล และแท้จริง พวกเราเป็นผู้ปฏิเสธศรัทธาต่อสัจธรรมนั้น');
INSERT INTO "king_fahad_thai" VALUES(43,31,'43:31','และพวกเขากล่าวว่า ทําไมอัลกุรอานนี้จึงไม่ถูกประทานลงมาให้แก่ชายผู้มีความสําคัญแห่งสองเมืองนี้');
INSERT INTO "king_fahad_thai" VALUES(43,32,'43:32','พวกเขาเป็นผู้แบ่งปันความเมตตาแห่งพระเจ้าของเจ้า กระนั้นหรือ ? เราต่างหากที่เป็นผู้จัดสรรการทํามาหากินของพวกเขาระหว่างพวกเขาในการมีชีวิตอยู่ในบนโลกนี้ และเราได้เชิดชูบางคนในหมู่พวกเขาเหนือกว่าอีกบางคนหลายชั้น เพื่อบางคนในหมู่พวกเขาจะเอาอีกบางคนมาใช้งาน และความเมตตาของพระเจ้าของเจ้านั้นดียิ่งกว่าที่พวกเขาสะสมไว้');
INSERT INTO "king_fahad_thai" VALUES(43,33,'43:33','และหากมิใช่มนุษย์ทั้งหลายจะได้เป็นประชาชาติหนึ่งเดียวกันแล้ว แน่นอนเราจะให้ผู้ที่ปฏิเสธศรัทธาต่อพระผู้ทรงกรุณาปรานีมีบ้านของพวกเขาหลังคาทําด้วยเงิน และบันไดที่พวกเขาขึ้น (ก็ทําด้วยเงิน)');
INSERT INTO "king_fahad_thai" VALUES(43,34,'43:34','และบ้านของพวกเขามีประตูและเตียงนอน (ทําด้วยเงิน) ซึ่งพวกเขาจะนอนเอกเขนกบนมัน');
INSERT INTO "king_fahad_thai" VALUES(43,35,'43:35','และ (เราจะให้เครื่องประดับแก่พวกเขาที่ทําด้วย) ทองคํา แต่ทั้งหมดนั้นเป็นเพียงปัจจัยชั่วคราวแห่งชีวิตในโลกนี้เท่านั้น ส่วนในปรโลก ณ ที่พระเจ้าของเจ้านั้นสําหรับบรรดาผู้ยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(43,36,'43:36','และผู้ใดผินหลังจากการรําลึกถึงพระผู้ทรงกรุณาปราณี เราจะให้ชัยฏอนตัวหนึ่งแก่เขา แล้วมันก็จะเป็นสหายของเขา');
INSERT INTO "king_fahad_thai" VALUES(43,37,'43:37','และแท้จริง พวกมันจะขัดขวางพวกเขาออกจากทางที่ถูกต้อง แต่พวกเขาคิดว่า พวกเขานั้นอยู่ในแนวทางที่ถูกต้องแล้ว');
INSERT INTO "king_fahad_thai" VALUES(43,38,'43:38','จนกระทั่งเมื่อเขาได้มายังเรา (ในวันกิยามะฮฺ) เขาจะเปรยขึ้นว่า อนิจจา ถ้ระหว่างฉันกับเจ้ามีระยะทางห่างกันเช่นทิศตะวันออกกับทิศตะวันตกก็จะดี ชั่วช้าแท้ ๆ สหายเช่นนี้');
INSERT INTO "king_fahad_thai" VALUES(43,39,'43:39','และในวันนี้มันจะไม่เกิดผลอันใดเลยแก่พวกเจ้า เพราะพวกเจ้าได้อธรรม (แก่ตัวเอง) แม้ว่าพวกเจ้าจะมีหุ้นส่วนร่วมกันในการได้รับโทษก็ตาม');
INSERT INTO "king_fahad_thai" VALUES(43,40,'43:40','แล้วเจ้าจะทําให้คนหูหนวกได้ยินหรือคนตาบอดได้เห็นทาง และผู้ที่อยู่ในการหลงผิดอย่างชัดแจ้งกระนั้นหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(43,41,'43:41','มาตรว่าเราได้ยึดเอาชีวิตเจ้าไป แน่นอนเราก็จะตอบแทนพวกเขาให้สาสม');
INSERT INTO "king_fahad_thai" VALUES(43,42,'43:42','หรือเราจะแสดงให้เจ้าเห็นการลงโทษซึ่งเราได้สัญญากับพวกเขาไว้ แท้จริงเรานั้นมีอนุภาพเหนือพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(43,43,'43:43','ดังนั้นจงยึดมั่นตามที่ได้ถูกวะฮียฺแก่เจ้า แท้จริงเจ้านั้นอยู่บนแนวทางอันเที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(43,44,'43:44','และแท้จริงอัลกุรอานคือข้อตักเตือนแก่เจ้าและแก่หมู่ชนของเจ้า และพวกเจ้าจะถูกสอบสวน');
INSERT INTO "king_fahad_thai" VALUES(43,45,'43:45','และเจ้าจงถามผู้ที่เราได้ส่งมาก่อนเจ้าจากบรรดารอซูลของเราว่า เราได้ตั้งพระเจ้าหลายองค์อื่นจากรพะผู้ทรงกรุณาปราณี เพื่อเคารพบูชากระนั้นหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(43,46,'43:46','และโดยแน่นอนเราได้ส่งมูซาพร้อมด้วยสัญญาณต่าง ๆ ของเราไปยังฟิรเอานฺ และบรรดาผู้นําของเขา แล้วเขากล่าวว่า แท้จริงฉันเป็นร่อซูลของพระเจ้าแห่งสากลโลก');
INSERT INTO "king_fahad_thai" VALUES(43,47,'43:47','ครั้งเมื่อเขาได้มายังพวกเขาพร้อมด้วยสัญญาณต่าง ๆ ของเรา แล้วพวกเขาก็หัวเราะเยาะต่อสัญญาณนั้น ๆ');
INSERT INTO "king_fahad_thai" VALUES(43,48,'43:48','และเรามิได้แสดงสัญญาณอันใดแก่พวกเขา เว้นแต่ว่าแต่ละอันจะยิ่งใหญ่กว่าอีกอันหนึ่งและเราได้คร่าพวกเขาด้วยการลงโทษ หวังว่าพวกเขาจะกลับมาสํานึกผิด');
INSERT INTO "king_fahad_thai" VALUES(43,49,'43:49','และพวกเขากล่าวว่า โอ้มายากรเอ๋ย โปรดวิงวอนต่อพระเจ้าของท่านแก่พวกเราด้วย ตามที่พระองค์ได้ทรงทําสัญญากับท่าน แท้จริงเราจะเป็นผู้อยู่ในแนวทางที่ถูกต้อง');
INSERT INTO "king_fahad_thai" VALUES(43,50,'43:50','ครั้งเมื่อเราได้ปลดเปลื้องการลงโทษให้พ้นไปจากพวกเขาแล้ว เมื่อนั้นพวกเขาก็ผิดสัญญา');
INSERT INTO "king_fahad_thai" VALUES(43,51,'43:51','และฟิรเอานฺได้ประกาศท่ามกลางหมู่ชนของเขา เขากล่าวว่า หมู่ชนของฉันเอ๋ยอาณาจักรแห่งอียิปต์นี้มิได้เป็นของฉันดอกหรือ ? และแม่น้ําเหล่านี้ไหลผ่านเบื้องล่าง(วังของ)ฉัน พวกท่านไม่เห็นดอกหรือ');
INSERT INTO "king_fahad_thai" VALUES(43,52,'43:52','ยิ่งกว่านั้นฉันยังดีกว่าคนนี้ ซึ่งเขาต่ําต้อย และแทบจะพูดจาไม่ชัดถ้อยชัดคํา');
INSERT INTO "king_fahad_thai" VALUES(43,53,'43:53','ถ้าเช่นนั้นแล้วทําไมจึงไม่สวมกําไลทองให้เขาเล่า ? หรือมีมะลาอิกะฮฺติดตามมาอยู่กับเขา');
INSERT INTO "king_fahad_thai" VALUES(43,54,'43:54','ด้วยเหตุนี้เขา (ฟิรเอานฺ) ได้หลอกลวงหมู่ชนของเขา แล้วพวกเขาก็เชื่อฟังเขา แท้จริงพวกเขาเป็นหมู่ชนผู้ฝ่าฝืน');
INSERT INTO "king_fahad_thai" VALUES(43,55,'43:55','เมื่อพวกเขาได้ทําให้เรากริ้ว เราได้ตอบแทนพวกเขาอย่างสาสม แล้วเราได้ให้พวกเขาจมน้ําทั้งหมด');
INSERT INTO "king_fahad_thai" VALUES(43,56,'43:56','และเราได้ทําให้พวกเขาเป็นอดีตที่ล่วงเลยไปและอุทธาหรณ์แก่คนรุ่นต่อไป ๆ ไป');
INSERT INTO "king_fahad_thai" VALUES(43,57,'43:57','และเมื่ออีซา ดูซิหมู่ชนของเจ้าก็โห่ร้องด้วยความขบขัน');
INSERT INTO "king_fahad_thai" VALUES(43,58,'43:58','และพวกเขากล่าวว่า พระเจ้าทั้งหลายของเราดีกว่าหรือว่าเขา (อีซา) พวกเขามิได้เปรียบเทียบเรื่องนี้แก่เจ้าเพื่ออื่นใดนอกจากการโต้เถียง ยิ่งกว่านั้นพวกเขาเป็นหมู่ชนที่ชอบการโต้เถียงอีกด้วย');
INSERT INTO "king_fahad_thai" VALUES(43,59,'43:59','เขา (อีซา) มิใช่ใครคนอื่นนอกจากเป็นบ่าวคนหนึ่ง ซึ่งเรา (อัลลอฮฺ) ได้ให้ความโปรดปรานแก่เขา และเราได้ทําให้เขาเป็นแบบอย่างที่ดีแก่วงศ์วารของอิสรออีล');
INSERT INTO "king_fahad_thai" VALUES(43,60,'43:60','และหากเราประสงค์เราจะตั้งให้มี มะลาอีกะฮฺขึ้นจากในหมู่พวกเจ้าให้เป็นผู้สืบช่วงในแผ่นดินนี้');
INSERT INTO "king_fahad_thai" VALUES(43,61,'43:61','และแท้จริงเขา (อีซา) แน่นอนเป็นเครื่องหมายแห่งยามอวสาน ดังนั้นเจ้าอย่าได้สงสัยในเรื่องนี้ แต่จงปฏิบัติตามฉัน นี้คือแนวทางอันเที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(43,62,'43:62','และอย่าให้ชัยฏอนมาขัดขวางพวกเจ้า แท้จริงมันเป็นศัตรูตัวฉกาจของพวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(43,63,'43:63','และเมื่ออีซาได้มาพร้อมด้วยหลักฐานทั้งหลายอันชัดแจ้ง เขากล่าวว่า แน่นอนฉันได้มาหาพวกท่านพร้อมด้วยการเป็นนะบีและบทบัญญัติแห่งอินญีล และเพื่อฉันจะได้ชี้แจงแก่พวกท่านให้กระจ่างแจ้งในบางเรื่องที่พวกท่านขัดแย้งกัน ดังนั้นพวกท่านจงยําเกรงอัลลอฮฺ และเชื่อฟังปฏิบัติตามฉันเถิด');
INSERT INTO "king_fahad_thai" VALUES(43,64,'43:64','แท้จริงอัลลอฮฺนั้น พระองค์คือพระเจ้าของฉัน และพระเจ้าของพวกท่าน ดังนั้นจงเคารพภักดีพระองค์เถิด นี่คือแนวทางอันเที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(43,65,'43:65','นิกายต่าง ๆ ได้ขัดแย้งกันในระหว่างพวกเขา ดังนั้นความหายนะจงประสบแด่บรรดาผู้อธรรมเนื่องจากการลงโทษแห่งวันอันเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(43,66,'43:66','พวกเขามิได้คอยสิ่งใดนอกจากยามอวสาน ซึ่งมันจะมาหาพวกเขาอย่างกระทันหันโดยที่พวกเขาไม่รู้สึกตัว');
INSERT INTO "king_fahad_thai" VALUES(43,67,'43:67','ในวันนั้นบรรดามิตรสหายจะเป็นศัตรูกัน นอกจากบรรดาผู้ยําเกรง (อัลลอฮฺ)');
INSERT INTO "king_fahad_thai" VALUES(43,68,'43:68','โอ้ปวงบ่าวของข้าเอ๋ย ไม่มีความหวาดกลัวอันใดแก่พวกเจ้าในวันนี้ และพวกเจ้ามิต้องเศร้าสลดใจ');
INSERT INTO "king_fahad_thai" VALUES(43,69,'43:69','บรรดาผู้ศรัทธาต่อสัญญาณทั้งหลายของเรา และพวกเขาเป็นผู้นอบน้อม');
INSERT INTO "king_fahad_thai" VALUES(43,70,'43:70','พวกเจ้าจงเข้าไปในสวนสวรรค์ ทั้งตัวของพวกเจ้าและคู่ครองของพวกเจ้าอย่างแช่มชื่นแจ่มใส');
INSERT INTO "king_fahad_thai" VALUES(43,71,'43:71','จะมีจานทําด้วยทองคําและแก้วน้ําถูกนํามาเวียนรอบ ๆ พวกเขา และในสวนสวรรค์นั้น จะมีสิ่งที่จิตใจของพวกเขาต้องการและสายตาของพวกเขาชื่นชมยินดี และพวกเจ้าจะพํานักอยู่ในนั้นตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(43,72,'43:72','และนั่นคือสวนสวรรค์ ซึ่งพวกเจ้าได้ถูกให้รับเป็นมรดกตามที่พวกเจ้าได้กระทํา (ความดี) ไว้');
INSERT INTO "king_fahad_thai" VALUES(43,73,'43:73','ในสวนสวรรค์นั้นจะมีผลไม้มากมายสําหรับพวกเจ้า พวกเจ้าจะได้กินส่วนหนึ่งจากมัน');
INSERT INTO "king_fahad_thai" VALUES(43,74,'43:74','แท้จริงบรรดาผู้กระทําความผิด (ผู้ปฏิเสธศรัทธา) นั้นจะอยู่ในการลงโทษในนรกญะฮันนัมตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(43,75,'43:75','การลงโทษนั้นจะไม่ถูกลดหย่อนแก่พวกเขา และในการลงโทษนั้นพวกเขาเป็นผู้หมดหวัง');
INSERT INTO "king_fahad_thai" VALUES(43,76,'43:76','และเรามิได้อธรรมต่อพวกเขา แต่พวกเขาต่างหากที่เป็นผู้อธรรมต่อตัวเอง');
INSERT INTO "king_fahad_thai" VALUES(43,77,'43:77','และพวกเขาจะร้องเรียกขึ้นว่า โอ้มาลิก (ยามเฝ้าประตูนรก) โปรดให้พระเจ้าของท่านจัดการให้เราตายเสียเถิด เขา (มาลิก) จะกล่าวว่า แท้จริงส่วนมากของพวกเจ้าเป็นผู้พํานักอยู่ตลอดไป');
INSERT INTO "king_fahad_thai" VALUES(43,78,'43:78','โดยแน่นอนเราได้นําความจริงมายังพวกเจ้าแล้ว แต่ส่วนมากของพวกเจ้าเป็นผู้ชิงชังความจริง');
INSERT INTO "king_fahad_thai" VALUES(43,79,'43:79','หรือว่าพวกเขาได้ตกลงวางแผนในเรื่องใด ดังนั้นแน่นอนเราก็ได้ตกลงวางแผนเช่นกัน (ที่จะทําลายแผนของพวกเขา)');
INSERT INTO "king_fahad_thai" VALUES(43,80,'43:80','หรือพวกเขาคิดว่า เราไม่ได้ยินความลับของพวกเขา และการประชุมลับของพวกเขาแน่นอน (เราได้ยิน) และทูตของเราอยู่กับพวกเขา เพื่อบันทึก');
INSERT INTO "king_fahad_thai" VALUES(43,81,'43:81','จงกล่าวเถิดมุฮัมมัด ถ้าหากพระผู้ทรงกรุณาปรานี ทรงมีโอรส ดังนั้นฉันจะเป็นคนแรกในหมู่ผู้เคารพภักดีทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(43,82,'43:82','มหาบริสุทธิ์แด่พระเจ้าแห่งชั้นฟ้าทั้งหลายและแผ่นดิน พระเจ้าแห่งบัลลังก์ (ทรงบริสุทธิ์) จากสิ่งพวกเขากล่าวอ้าง');
INSERT INTO "king_fahad_thai" VALUES(43,83,'43:83','ดังนั้นเจ้าจงปล่อยพวกเขาให้มั่วสุมและหลงระเริง จนกว่าพวกเขาจะได้พบกับวัน (กิยามะฮฺ) ของพวกเขา ซึ่งพวกเขาถูกสัญญาไว้');
INSERT INTO "king_fahad_thai" VALUES(43,84,'43:84','และพระองค์คือพระผู้เป็นเจ้าแห่งชั้นฟ้า และพระผู้เป็นเจ้าแห่งแผ่นดิน และพระองค์เป็นเจ้าแห่งแผ่นดิน และพระองค์เป็นผู้ทรงปรีชาญาณ ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(43,85,'43:85','ความเจริญสุขจงมีแด่พระผู้ซึ่งอํานาจแห่งชั้นฟ้าทั้งหลายและแผ่นดิน และสิ่งที่อยู่ในระหว่างทั้งสองเป็นกรรมสิทธิ์ของพระองค์ และ ณ ที่พระองค์นั้นคือความรอบรู้แห่งยามอวสาน และยังพระองค์เท่านั้นที่พวกเจ้าจะถูกนํากลับไป');
INSERT INTO "king_fahad_thai" VALUES(43,86,'43:86','และบรรดาผู้ที่วิงวอนขอสิ่งอื่นจากพระองค์นั้น จะไม่มีอํานาจในการขอชะฟาอะฮฺ นอกจากผู้ยืนยันเป็นพยานด้วยความจริง และพวกเขารู้ดี');
INSERT INTO "king_fahad_thai" VALUES(43,87,'43:87','และถ้าพวกเจ้าถามพวกเขาว่า ใครเป็นผู้สร้างพวกเขา แน่นอนพวกเขาจะกล่าวว่าอัลลอฮฺ แล้วทําไมเล่าพวกเขาจึงหันเหออกไปทางอื่น');
INSERT INTO "king_fahad_thai" VALUES(43,88,'43:88','และจะมีเสียงกล่าวของเขาว่า ข้าแต่พระเจ้าของข้าพระองค์ แท้จริงชนเหล่านั้นเป็นหมู่ชนที่ไม่ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(43,89,'43:89','ดังนั้น เจ้าจงให้อภัยแก่พวกเขา และจงกล่าวว่าศานติ แล้วพวกเขาก็จะรู้');
INSERT INTO "king_fahad_thai" VALUES(44,1,'44:1','ฮามีม');
INSERT INTO "king_fahad_thai" VALUES(44,2,'44:2','ขอสาบานด้วยคัมภีร์อันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(44,3,'44:3','แท้จริงเราได้ประทานอัลกุรอานลงมาในคืนอันจําเริญ แท้จริงเราเป็นผู้ตักเตือน');
INSERT INTO "king_fahad_thai" VALUES(44,4,'44:4','ในคืนนั้นทุก ๆ กิจการที่สําคัญถูกจําแนกไว้แล้ว');
INSERT INTO "king_fahad_thai" VALUES(44,5,'44:5','โดยทางบัญชามาจากเรา แท้จริงเราเป็นผู้ส่งมา');
INSERT INTO "king_fahad_thai" VALUES(44,6,'44:6','เป็นความเมตตาจากพระเจ้าของเจ้า แท้จริงพระองค์เป็นผู้ทรงได้ยิน ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(44,7,'44:7','พระเจ้าแห่งชั้นฟ้าทั้งหลาย และแผ่นดินและสิ่งที่อยู่ระหว่างทั้งสอง หากพวกเจ้าเป็นผู้เชื่อมั่น');
INSERT INTO "king_fahad_thai" VALUES(44,8,'44:8','ไม่มีพระผู้เป็นเจ้าอื่นใดอีกนอกจากพระองค์ ผู้ทรงให้เป็นและผู้ทรงให้ตาย พระเจ้าของพวกเจ้าและพระเจ้าแห่งบรรพบุรุษของพวกเจ้าในสมัยก่อน ๆ');
INSERT INTO "king_fahad_thai" VALUES(44,9,'44:9','แต่ทว่าพวกเขากังวลอยู่ในการสงสัย');
INSERT INTO "king_fahad_thai" VALUES(44,10,'44:10','ดังนั้น เจ้าจงคอยเฝ้าดูวันที่ชั้นฟ้าจะนําไอหมอกออกมาซึ่งจะเห็นได้ชัด');
INSERT INTO "king_fahad_thai" VALUES(44,11,'44:11','ซึ่งจะครอบคลุมผู้คน นี่คือการลงโทษอันเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(44,12,'44:12','(พวกเขาจะกล่าวว่า) ข้าแต่พระเจ้าของข้าพระองค์ได้ทรงปลดเปลื้องการลงโทษนี้ ให้พ้นจากข้าพระองค์ แท้จริงพวกเราเป็นผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(44,13,'44:13','ข้อตักเตือนนั้นจะเป็นผลแก่พวกเขาได้อย่างไร ทั้ง ๆ ที่ได้มีร่อซูลผู้ชี้แจงอย่างแจ้งชัดมายังพวกเขาแล้ว');
INSERT INTO "king_fahad_thai" VALUES(44,14,'44:14','แล้วพวกเขาก็ผินหลังออกไปจากเขา และพวกเขากล่าวว่าเขา (มุฮัมมัด) เป็นคนบ้าที่ได้รับการเสี้ยมสอน');
INSERT INTO "king_fahad_thai" VALUES(44,15,'44:15','แน่นอนเราจะปลดเปลื้องการลงโทษนั้นให้พ้นไป (จากพวกเจ้า) ชั่วขณะหนึ่ง แล้วพวกเจ้าก็จะกลับไปสู่สภาพเดิม');
INSERT INTO "king_fahad_thai" VALUES(44,16,'44:16','วันที่เราจะปราบพวกเขาด้วยการปราบครั้งยิ่งใหญ่ แน่นอนเราเป็นผู้ตอบแทนอย่างสาสม');
INSERT INTO "king_fahad_thai" VALUES(44,17,'44:17','และโดยแน่นอน เราได้ทดสอบหมู่ชนของฟิรเอานฺก่อนหน้าพวกเขา และได้มีร่อซูล (มูซา) ผู้มีเกียรติมายังพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(44,18,'44:18','(โดยกล่าวว่า) จงมอบปวงบ่าวของอัลลอฮฺให้แก่ฉัน แท้จริงฉันคือร่อซูลผู้ซื่อสัตย์สําหรับพวกท่าน');
INSERT INTO "king_fahad_thai" VALUES(44,19,'44:19','และพวกท่านอย่ายกตนเหนืออัลลอฮฺ แท้จริงฉันมาหาพวกท่านพร้อมด้วยหลักฐานอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(44,20,'44:20','และแท้จริงฉันขอความคุ้มครองต่อพระเจ้าของฉัน และพระเจ้าของพวกท่านมิให้พวกท่านทําร้ายฉัน');
INSERT INTO "king_fahad_thai" VALUES(44,21,'44:21','และถ้าหากพวกท่านไม่เชื่อฉันก็จงถอยห่างไปจากฉัน');
INSERT INTO "king_fahad_thai" VALUES(44,22,'44:22','แล้วเขาก็วิงวอนขอต่อพระเจ้าของเขาว่า ชนเหล่านี้เป็นหมู่ชนผู้กระทําความผิด');
INSERT INTO "king_fahad_thai" VALUES(44,23,'44:23','ดังนั้น จงเดินทางในเวลากลางคืนพร้อมด้วยปวงบ่าวของข้า เพราะพวกเจ้าจะถูกติดตามอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(44,24,'44:24','และจงปล่อยทะเลให้สงบนิ่ง (เหมือนเดิม) เพราะแท้จริงพวกเขาเป็นไพร่พลที่ถูกจมน้ําตาย');
INSERT INTO "king_fahad_thai" VALUES(44,25,'44:25','กี่มากน้อยที่พวกเขาได้ทิ้งสวนหลากหลาย และน้ําพุหลายแห่ง');
INSERT INTO "king_fahad_thai" VALUES(44,26,'44:26','และเรือกสวนไร่นา และอาคารระโหฐานอันมีเกียรติ');
INSERT INTO "king_fahad_thai" VALUES(44,27,'44:27','และความสะดวกสะบายที่พวกเขาสนุกสนานร่าเริง');
INSERT INTO "king_fahad_thai" VALUES(44,28,'44:28','เช่นนั้นแหละ เราได้ให้หมู่ชนอื่นรับมรดกครอบครองมัน');
INSERT INTO "king_fahad_thai" VALUES(44,29,'44:29','และชั้นฟ้า และแผ่นดินมิได้ร่ําไห้เพราะ (เสียดาย) พวกเขาและพวกเขาจะไม่ถูกประวิงเวลา');
INSERT INTO "king_fahad_thai" VALUES(44,30,'44:30','และโดยแน่นอน เราได้ให้วงศ์วานของอิสราเอลรอดพ้นจากการทรมานที่น่าอดสู');
INSERT INTO "king_fahad_thai" VALUES(44,31,'44:31','จากฟิรเอานฺ แท้จริงเขาเป็นผู้โอหังที่มาจากผู้ฝ่าฝืน');
INSERT INTO "king_fahad_thai" VALUES(44,32,'44:32','และโดยแน่นอนเราได้เลือกพวกเขาเหนือประชาชาติทั้งหลายในยุคนั้น เนื่องด้วยความรอบรู้ของเรา');
INSERT INTO "king_fahad_thai" VALUES(44,33,'44:33','และเราได้ประทานสัญญาณต่าง ๆ แก่พวกเขา ซึ่งในนั้นมีข้อทดสอบอย่างชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(44,34,'44:34','แท้จริงชนเหล่านั้น แน่นอนพวกเขาจะกล่าวว่า');
INSERT INTO "king_fahad_thai" VALUES(44,35,'44:35','มันเป็นเพียงความตายครั้งแรกของเรา และเราจะไม่ถูกให้ฟื้นคืนชีพขึ้นมาอีก');
INSERT INTO "king_fahad_thai" VALUES(44,36,'44:36','ดังนั้น จงนําบรรพบุรุษของเรากลับมาอีกซิ หากพวกท่านเป็นผู้ซื่อสัตย์จริง');
INSERT INTO "king_fahad_thai" VALUES(44,37,'44:37','พวกเขา (กุฟฟารมักกะฮฺ) ดีกว่า หรือว่าหมู่ชนแห่งตุบบะอฺ (ชาวสะบะอฺแห่งเยเมน) และบรรดาหมู่ชนก่อนหน้าพวกเขาเราได้ทําลายล้างพวกเขา เพราะพวกเขาเหล่านั้นเป็นผู้กระทําความผิด');
INSERT INTO "king_fahad_thai" VALUES(44,38,'44:38','และเรามิได้สร้างชั้นฟ้าทั้งหลาย และแผ่นดิน และสิ่งที่อยู่ระหว่างทั้งสองอย่าง อย่างไร้สาระ');
INSERT INTO "king_fahad_thai" VALUES(44,39,'44:39','เรามิได้สร้างทั้งสอง (ดังกล่าวนั้น) เพื่ออื่นใดนอกจากเพื่อความจริง แต่ว่าส่วนมากของพวกเขาไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(44,40,'44:40','แท้จริงวันแห่งการตัดสินนั้นเป็นเวลาที่ถูกกําหนดไว้สําหรับพวกเขาทั้งหมด');
INSERT INTO "king_fahad_thai" VALUES(44,41,'44:41','วันที่ญาติสนิทจะไม่อํานวยประโยชน์อันใดให้แก่ญาติสนิทอีกคนหนึ่งได้ และพวกเขาเองก็จะไม่ได้รับความช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(44,42,'44:42','เว้นแต่ผู้ที่อัลลอฮฺทรงมีเมตตา แท้จริงพระองค์เป็นผู้ทรงอํานาจ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(44,43,'44:43','แท้จริงต้นซักกูม');
INSERT INTO "king_fahad_thai" VALUES(44,44,'44:44','จะเป็นอาหารของผู้ทําบาปมาก');
INSERT INTO "king_fahad_thai" VALUES(44,45,'44:45','เช่นทองแดงที่ถูกหลอมเดือด มันจะต้มเดือดอยู่ในท้อง');
INSERT INTO "king_fahad_thai" VALUES(44,46,'44:46','เช่นการเดือดพล่านของน้ําร้อน');
INSERT INTO "king_fahad_thai" VALUES(44,47,'44:47','(มีเสียงกล่าวแก่ยามเฝ้านรกว่า) จงจับเขาไปและลากเขาไปสู่กลางไฟที่ลุกโชน');
INSERT INTO "king_fahad_thai" VALUES(44,48,'44:48','แล้วจงราดลงบนหัวของเป็นการลงโทษแห่งน้ําเดือด');
INSERT INTO "king_fahad_thai" VALUES(44,49,'44:49','จงลิ้มรสการลงโทษซิ แท้จริงเจ้า (เคยพูดว่า) เป็นผู้มีอํานาจ ผู้มีเกียรติ');
INSERT INTO "king_fahad_thai" VALUES(44,50,'44:50','แท้จริง นี้คือสิ่งที่พวกเจ้าเคยสงสัยไว้');
INSERT INTO "king_fahad_thai" VALUES(44,51,'44:51','แท้จริงบรรดาผู้ยําเกรงจะอยู่ในสถานที่อันสงบปลอดภัย');
INSERT INTO "king_fahad_thai" VALUES(44,52,'44:52','ท่ามกลางสวนสวรรค์หลากหลาย และน้ําพุหลายแห่ง');
INSERT INTO "king_fahad_thai" VALUES(44,53,'44:53','พวกเขาจะสวมอาภรณ์ทําด้วยผ้าไหมละเอียด และผ้าไหมหยาบ หันหน้าเข้าหากัน');
INSERT INTO "king_fahad_thai" VALUES(44,54,'44:54','เช่นนั้นแหละ และเราจะให้พวกเขามีคู่ครองเป็นหญิงสาววัยรุ่นมีดวงตาสวยงาม');
INSERT INTO "king_fahad_thai" VALUES(44,55,'44:55','พวกเขาจะเรียกเอาผลไม้ทุกชนิดที่อยู่ในสวนสวรรค์นั้นอย่างปลอดภัย');
INSERT INTO "king_fahad_thai" VALUES(44,56,'44:56','ในสวนสวรรค์นั้น พวกเขาจะไม่ได้ลิ้มรสความตาย นอกจากความตายครั้งแรก (ในโลกดุนยา) และพระองค์จะทรงคุ้มครองพวกเขาให้พ้นจากการลงโทษแห่งไฟนรก');
INSERT INTO "king_fahad_thai" VALUES(44,57,'44:57','เป็นความโปรดปรานจากพระเจ้าของเจ้า นั่นคือความสําเร็จอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(44,58,'44:58','ดังนั้นแท้จริงเราได้ทําให้อัลกุรอานเป็นที่ง่ายดายในภาษาของเจ้า (อาหรับ) เพื่อพวกเขาจะได้ใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(44,59,'44:59','ฉะนั้น จงคอยดูเถิด แท้จริงพวกเขาก็จะเป็นผู้คอยเช่นกัน');
INSERT INTO "king_fahad_thai" VALUES(45,1,'45:1','ฮา มีม');
INSERT INTO "king_fahad_thai" VALUES(45,2,'45:2','การประทานลงมาขอคัมภีร์นี้จากอัลลอฮฺ ผู้ทรงอํานาจ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(45,3,'45:3','แท้จริงในชั้นฟ้าทั้งหลาย และแผ่นดินนั้น แน่นอนย่อมมีสัญญาณหลากหลายสําหรับบรรดาผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(45,4,'45:4','และในการบังเกิดพวกเจ้า และสิ่งที่พระองค์ทรงให้สัตว์แต่ละชนิดแพร่สะพัดออกไปนั้นย่อมเป็นสัญญาณหลากหลายสําหรับหมู่ชนผู้เชื่อมั่น');
INSERT INTO "king_fahad_thai" VALUES(45,5,'45:5','และการสับเปลี่ยนของกลางคืนและกลางวัน และสิ่งที่อัลลอฮฺทรงหลั่งลงมาจากฟากฟ้า เพื่อเป็นปัจจัยยังชีพนั้น พระองค์ทรงให้แผ่นดินมีชีวิตชีวาโดยน้ําฝนหลังจากการแห้งแล้งของมัน และการเปลี่ยนทิศทางเดินของลมย่อมเป็นสัญญาณหลากหลายสําหรับหมู่ชนผู้ใช้สติปัญญา');
INSERT INTO "king_fahad_thai" VALUES(45,6,'45:6','นั่นคือสัญญาณต่าง ๆ ของอัลลอฮฺ ซึ่งเราได้สาธยายสัญญาณเหล่านั้นแก่เจ้าด้วยความจริง และสัญญาณต่าง ๆ ของพระองค์ที่พวกเขาจะศรัทธากัน');
INSERT INTO "king_fahad_thai" VALUES(45,7,'45:7','ความหายนะจงประสบแด่ทุกคนที่เป็นผู้โกหก ผู้ทําบาปมาก');
INSERT INTO "king_fahad_thai" VALUES(45,8,'45:8','เขาได้ฟังอายาต (อัลกุรอาน) ของอัลลอฮฺถูกสาธยายแก่เขา แล้วเขาก็ดื้อรั้นอย่างยะโส ประหนึ่งว่าเขาไม่เคยฟังอายาตมาก่อนเลยดังนั้นจงแจ้งข่าวแก่เขาถึงการลงโทษอันเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(45,9,'45:9','และเมื่อเขาได้ทราบสิ่งใดจากอายาตของเรา เขาก็ถือเอาอายาตนั้น ๆ เป็นการล้อเลียน ชนเหล่านี้สําหรับพวกเขาจะได้รับการลงโทษอันอัปยศ');
INSERT INTO "king_fahad_thai" VALUES(45,10,'45:10','เบื้องหน้าพวกเขาคือนรกญะฮันมัน และสิ่งที่พวกเขาขวนขวายไว้จะไม่อํานวยประโยชน์อันใดแก่พวกเขาได้ และสิ่งที่พวกเขายึดถือเอาเป็นผู้คุ้มครองอื่นจากอัลลอฮฺ ก็จะไม่อํานวยประโยชน์เช่นกัน และสําหรับพวกเขาจะได้รับการลงโทษอย่างใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(45,11,'45:11','นี่คือแนวทางที่ถูกต้อง ส่วนบรรดาผู้ปฏิเสธศรัทธาต่ออายาต (อัลกุรอาน) แห่งพระเจ้าของพวกเขา สําหรับพวกเขาจะได้รับการลงโทษอันมหันต์อย่างเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(45,12,'45:12','อัลลอฮฺ คือผู้ทรงทําให้ทะเลเป็นประโยชน์สําหรับพวกเจ้า เพื่อให้เรือเดินสมุทรแล่นไปตามน่านน้ําโดยพระบัญชาของพระองค์ และเพื่อพวกเจ้าจะได้แสวงหาความโปรดปรานของพระองค์ และเพื่อพวกเจ้าจะได้ขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(45,13,'45:13','และพระองค์ทรงทําให้สิ่งที่อยู่ในชั้นฟ้าทั้งหลาย และสิ่งที่อยู่ในแผ่นดินเป็นประโยชน์แก่พวกเจ้า ทั้งหมดนี้มาจากพระองค์แท้จริงในการนั้น แน่นอนย่อมเป็นสัญญาณสําหรับหมู่ชนผู้ใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(45,14,'45:14','จงกล่าวเถิดมุฮัมมัดแก่บรรดาผู้ศรัทธาให้พวกเขาอภัยแก่บรรดาผู้ที่ไม่หวังในวันทั้งหลายของอัลลอฮฺ เพื่อพระองค์จะทรงตอบแทนแก่หมู่ชนตามที่พวกเขาได้ขวนขวายเอาไว้');
INSERT INTO "king_fahad_thai" VALUES(45,15,'45:15','ผู้ใดกระทําความดีก็จะได้แก่ตัวของเขาเอง และผู้ใดกระทําความชั่ว ก็จะตกหนักแก่ตัวของเขาเองแล้วพวกเจ้าย่อมจะกลับไปหาพระเจ้าของพวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(45,16,'45:16','และโดยแน่นอนเราได้ประทานคัมภีร์และข้อชี้ขาดตัดสินและการเป็นนะบีแก่วงศ์วานของอิสรออีล และเราได้ให้ปัจจัยยังชีพจากสิ่งดีๆ แก่พวกเขา และเราได้ยกย่องพวกเขาให้เหนือประชาชาติทั้งหลายในยุคนั้น');
INSERT INTO "king_fahad_thai" VALUES(45,17,'45:17','และเราได้ประทานหลักฐานอันชัดแจ้งทั้งหลายแก่พวกเขาเกี่ยวกับเรื่องนี้ พวกเขามิได้ขัดแย้งกันเว้นแต่หลักการ ได้มีความรู้มายังพวกเขาแล้วแต่เนื่องจากความริษยาระหว่างพวกเขากันเอง แท้จริงพระเจ้าจะทรงตัดสินระหว่างพวกเขาในวันกิยามะฮฺ ในสิ่งที่พวกเขาได้ขัดแย้งกันในเรื่องนั้น');
INSERT INTO "king_fahad_thai" VALUES(45,18,'45:18','แล้วเราได้ตั้งเจ้าให้อยู่บนแนวทางหนึ่ง ในเรื่องของศาสนาที่แท้จริง ดังนั้นจงปฏิบัติตามแนวทางนั้น และอย่าได้ปฏิบัติตามอารมณ์ต่ําของบรรดาผู้ไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(45,19,'45:19','แท้จริงพวกเขาไม่อาจจะช่วยเจ้าให้พ้นจากอัลลอฮฺแต่อย่างใด และแท้จริงพวกอธรรมนั้นต่างก็เป็นมิตรซึ่งกันและกัน แต่อัลลอฮฺนั้นทรงเป็นผู้คุ้มครองบรรดาผู้ยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(45,20,'45:20','อัลกุรอานนี้เป็นแสงสว่างแก่มวลมนุษย์ และเป็นแนวทางที่ถูกต้องและความเมตตาแก่หมู่ชนที่มีความเชื่อมั่น');
INSERT INTO "king_fahad_thai" VALUES(45,21,'45:21','หรือบรรดาผู้ที่กระทําความชั่ว คิดหรือว่าเราจะทําให้พวกเขาเป็นเช่นกับบรรดาผู้ศรัทธาและกระทําความดีทั้งหลาย ให้เท่าเทียมกันทั้งในเวลามีชีวิตของพวกเขาและการตายของพวกเขา สิ่งที่พวกเขาตัดสินนั้นมันชั่วแท้ ๆ');
INSERT INTO "king_fahad_thai" VALUES(45,22,'45:22','และอัลลอฮฺทรงสร้างชั้นฟ้าทั้งหลายและแผ่นดินด้วยความเที่ยงธรรม และเพื่อทุก ๆ ชีวิต (ทุกคน) จะได้รับการตอบแทนตามที่ได้ขวนขวยเอาไว้ และพวกเขาจะไม่ถูกอธรรมแต่อย่างใด');
INSERT INTO "king_fahad_thai" VALUES(45,23,'45:23','เจ้าเคยเห็นผู้ที่ยึดถือเอาอารมณ์ต่ําของเขาเป็นพระเจ้าของเขาบ้างไหม ? และอัลลอฮฺจะทรงให้เขาหลงทางด้วยการรอบรู้ (ของพระองค์) และทรงผนึกการการฟังของเขาและหัวใจของเขาและทรงทําให้มีสิ่งบดบังดวงตาของเขา ดังนั้นผู้ใดเล่าจะชี้แนะแก่เขาหลังจากอัลลอฮฺ พวกเจ้ามิได้ใคร่ครวญกันดอกหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(45,24,'45:24','และพวกเขากล่าวว่า ไม่มีชีวิตอื่นใดดอกนอกจากการมีชีวิตของเราในโลกนี้ เราจะตายไปและเราจะมีชีวิตอยู่ และไม่มีสิ่งใดจะมาทําลายเราได้ (ให้เราตาย) นอกจากกาลเวลาเท่านั้น สําหรับพวกเขาไม่มีความรู้ในเรื่องนั้นดอก นอกจากพวกเขาเดาเองเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(45,25,'45:25','และเมื่ออายาตต่าง ๆ อันชัดแจ้งของเราถูกสาธยายแก่พวกเขา ข้อขัดแย้งของพวกเขาก็ไม่มีอะไร นอกจากพวกเขาจะกล่าวว่า จงนําบรรพบุรุษของเราให้คืนชีพกลับมา หากพวกท่านเป็นผู้สัตย์จริง');
INSERT INTO "king_fahad_thai" VALUES(45,26,'45:26','จงกล่าวเถิดมุฮัมมัด อัลลอฮฺทรงให้พวกท่านมีชีวิตขึ้นมา และทรงให้พวกท่านตายไป แล้วพระองค์จะทรงรวบรวมพวกท่านในวันกิยามะฮฺ อย่างไม่มีข้อสงสัยใด ๆ ในเรื่องนั้น แต่ส่วนมากของมนุษย์ไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(45,27,'45:27','และอํานาจเด็ดขาดแห่งชั้นฟ้าทั้งหลายและแผ่นดินนั้นเป็นของอัลลอฮฺ วันแห่งยามอวสานจะเกิดขึ้นในวันนั้น บรรดาผู้กล่าวเท็จจะขาดทุนอย่างย่อยยับ');
INSERT INTO "king_fahad_thai" VALUES(45,28,'45:28','และเจ้าจะเห็น (กลุ่มชน) ทุกชาติอยู่ในสภาพคุกเข่า ทุกชนชาติจะถูกเรียกมาตามบันทึกของตน วันนี้พวกเจ้าจะได้รับการตอบแทนตามที่พวกเจ้าได้กระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(45,29,'45:29','นี้คือบันทึกของเราจะพูดถึงเรื่องของพวกเจ้าด้วยความจริง แท้จริงเราได้ให้บันทึกไว้ตามที่พวกเจ้าได้กระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(45,30,'45:30','ดังนั้นส่วนบรรดาผู้ศรัทธา และกระทําความดีทั้งหลาย พระเจ้าของพวกเจ้าของพวกเขาก็จะทรงให้พวกเขาเข้าอยู่ในความเมตตาของพระองค์ นั่นคือความสําเร็จอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(45,31,'45:31','และส่วนบรรดาผู้ปฏิเสธผู้ศรัทธานั้นอายาตต่าง ๆ ของข้า มิได้ถูกสาธยายแก่พวกเจ้าดอกหรือ ? แต่พวกเจ้าอวดโอหังและพวกเจ้าเป็นหมู่ชนผู้กระทําผิด');
INSERT INTO "king_fahad_thai" VALUES(45,32,'45:32','และเมื่อได้มีการกล่าวว่า แท้จริงสัญญาของอัลลอฮฺนั้นเป็นความจริง และยามอวสานนั้นไม่มีข้อสงสัยใด ๆ ในเรื่องนั้น พวกเจ้าก็จะกล่าวว่า เราไม่รู้ว่ายามอวสานคืออะไร ? เราคิดว่ามันมิใช่อะไรเลย นอกจากเป็นการเดาเท่านั้น และเรามิได้เป็นผู้เชื่อมั่นในเรื่องนี้');
INSERT INTO "king_fahad_thai" VALUES(45,33,'45:33','และความชั่วที่พวกเขาได้กระทําเอาไว้จะเป็นที่ประจักษ์แก่พวกเขา และสิ่งที่พวกเขาเคยเยาะเย้ยไว้นั้นห้อมล้อมพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(45,34,'45:34','และจะมีเสียงกล่าวขึ้นว่า วันนี้เราจะลืมพวกเจ้าเช่นที่พวกเจ้าได้ลืมการพบในวันนี้ของพวกเจ้า และนรกคือที่พํานักของพวกเจ้า และสําหรับพวกเจ้าจะไม่มีผู้ช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(45,35,'45:35','เช่นนั้นแหละ เพราะพวกเจ้าได้ยึดถือเอาอายาตต่าง ๆ ของอัลลอฮฺเป็นการล้อเลียนและชีวิตในโลกดุนยานี้ ได้ล่อลวงพวกเจ้า ดังนั้นวันนี้พวกเข้าจะไม่ถูกนําออกจากนรกนั้นและพวกเข้าจะไม่ถูกขอร้องให้กลับไปแก้ตัวเพื่อขอขมาโทษ');
INSERT INTO "king_fahad_thai" VALUES(45,36,'45:36','ฉะนั้นมวลการสรรเสริญเป็นของอัลลอฮฺ พระเจ้าแห่งชั้นฟ้าทั้งหลาย และพระเจ้าแห่งแผ่นดินนี้ พระเจ้าแห่งสากลโลก');
INSERT INTO "king_fahad_thai" VALUES(45,37,'45:37','ความยิ่งใหญ่ในชั้นฟ้าทั้งหลาย และในแผ่นดินนี้เป็นกรรมสิทธิของอัลลอฮฺเท่านั้น และพระองค์เป็นผู้ทรงอํานาจ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(46,1,'46:1','ฮามีม');
INSERT INTO "king_fahad_thai" VALUES(46,2,'46:2','การประทานลงมาของคัมภีร์นี้จากอัลลอฮฺ ผู้ทรงอํานาจ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(46,3,'46:3','เรามิได้สร้างชั้นฟ้าทั้งหลายและแผ่นดิน และสิ่งที่อยู่ระหว่างทั้งสองเพื่ออื่นใดเว้นแต่ด้วยความจริง และวาระที่ถูกกําหนดไว้ แต่บรรดาผู้ปฏิเสธศรัทธาเป็นผู้ผินหลังให้จากสิ่งที่พวกเขาถูกตักเตือน');
INSERT INTO "king_fahad_thai" VALUES(46,4,'46:4','จงกล่าวเถิด มุฮัมมัด พวกท่านไม่เห็นดอกหรือ สิ่งที่พวกท่านวิงวอนขออื่นจากอัลลอฮฺ จงแสดงให้ข้าเห็นซิว่าพวกมันได้สร้างอะไรในแผ่นดินนี้ หรือว่าพวกมันมีส่วนร่วมใน (การสร้าง) ชั้นฟ้าทั้งหลาย จงนําคัมภีร์ก่อนหน้านี้มาให้ข้าดูซิ หรือจงแสดงร่องรอยแห่งความรู้ (ที่เป็นหลักฐานยืนยันในการนี้) หากพวกท่านเป็นผู้ซื่อสัตย์จริง');
INSERT INTO "king_fahad_thai" VALUES(46,5,'46:5','และใครเล่าจะหลงทางมากไปกว่าผู้ที่วิงวอนขออื่นจากอัลลอฮฺที่มันจะไม่ตอบรับ (การวิงวอนของ) เขาจนถึงวันกิยามะฮฺ และพวกมันเฉยเมยต่อการวิงวอนขอของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(46,6,'46:6','และเมื่อมนุษย์ถูกรวมให้มาชุมนุมกัน พวกมัน (เจว็ด) จะเป็นศัตรูกับพวกเขาและจะเป็นผู้ปฏิเสธการเคารพบูชาของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(46,7,'46:7','และเมื่ออายาตต่าง ๆ อันชัดแจ้งของเรา ถูกสาธยายแก่พวกเขา บรรดาผู้ปฏิเสธศรัทธาได้กล่าวเกี่ยวกับสัจธรรม(อัลกุรอาน) ที่ได้มีมายังพวกเขาว่า นี่คือมายากลอย่างชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(46,8,'46:8','หรือพวกเขากล่าวว่า เขา (มุฮัมมัด) ได้ปั้นแต่งอัลกุรอานนั้น จงกล่าวเถิดมุฮัมมัด ถ้าฉันได้ปั้นแต่งอัลกุรอานขึ้นพวกท่านก็ไม่มีอํานาจอันใดที่จะช่วยเหลือฉันได้จาก (การลงโทษของ) อัลลอฮฺ พระองค์ทรงรู้ดียิ่งถึงสิ่งที่พวกท่านกําลังง่วนอยู่ในเรื่องนี้ พอเพียงแล้วที่พระองค์ทรงเป็นพยานระหว่างฉันกับพวกท่าน และพระองค์เป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(46,9,'46:9','จงกล่าวเถิดมุฮัมมัด ฉันมิได้เป็นคนแรกในบรรดาร่อซูล และฉันไม่รู้ว่าจะเกิดอะไรขึ้นแก่ฉันและแก่พวกท่าน ฉันมิได้ปฏิบัติตามสิ่งใดนอกจากสิ่งที่ถูกวะฮียฺให้แก่ฉัน และฉันมิใช่ใครอื่นนอกจากเป็นผู้ตักเตือนอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(46,10,'46:10','จงกล่าวเถิดมุฮัมมัด พวกท่านไม่เห็นดอกหรือว่า ถ้าหากอัลกุรอานมาจากอัลลอฮฺและพวกท่านปฏิเสธอัลกุรอานนั้น ทั้ง ๆ ที่มีพยานคนหนึ่งจากวงศ์วานของอิสรออีลเป็นพยานต่อลักษณะเช่นเดียวกัน (คือคัมภีร์อัตเตารอฮฺ)แล้วเขาก็ศรัทธาแต่พวกท่านยังดื้อรั้นหยิ่งยะโส แท้จริงอัลลอฮฺจะไม่ทรงชี้แนะทางแก่หมู่ชนผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(46,11,'46:11','และบรรดาผู้ปฏิเสธศรัทธาได้กล่าวแก่บรรดาผู้ศรัทธาว่า หากว่าอัลกุรอานนี้มีความดี พวกเขา (ผู้ศรัทธา) ก็จะไม่รุดหน้าไปยังอัลกุรอานก่อนเราเป็นแน่ และโดยที่พวกเขา (พวกปฏิเสธศรัทธา) มิได้รับการชี้แนะทางด้วยอัลกุรอาน ดังนั้นพวกเขาจึงกล่าวว่า นี่คือเรื่องโกหกแต่ดั้งเดิม');
INSERT INTO "king_fahad_thai" VALUES(46,12,'46:12','และก่อนหน้านี้ (อัลกุรอาน) มีคัมภีร์ของมูซาเป็นแบบอย่างและความเมตตา และนี่อัลกุรอานเป็นคัมภีร์ที่ยืนยันเป็นภาษาอาหรับเพื่อตักเตือนบรรดาผู้กระทําความผิด และเป็นข่าวดีสําหรับผู้กระทําความดี');
INSERT INTO "king_fahad_thai" VALUES(46,13,'46:13','แท้จริงบรรดาผู้ที่กล่าวว่า อัลลอฮฺคือ พระเจ้าของพวกเรา แล้วพวกเขาก็ยืนหยัด (ปฏิบัติ) ตามคํากล่าวนั้น จะไม่มีความหวาดกลัวใด ๆ แก่พวกเขา และพวกเขาก็จะไม่เศร้าสลดใจ');
INSERT INTO "king_fahad_thai" VALUES(46,14,'46:14','ชนเหล่านั้นคือชาวสวนสวรรค์ พวกเขาเป็นผู้พํานักอยู่ในนั้นตลอดกาล เป็นการตอบแทนที่พวกเขาได้กระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(46,15,'46:15','และเราได้สั่งเสียมนุษย์ให้ทําดี ต่อบิดามารดาของเขา มารดาของเขาได้อุ้มครรภ์เขาด้วยความเหนื่อยยาก และได้คลอดเขาด้วยความเจ็บปวด และการอุ้มครรภ์เขาและการหย่านมของเขาในระยะเวลาสามสิบเดือน จนกระทั่งเมื่อเขาบรรลุวัยฉกรรจ์ของเขาและมีอายุถึงสี่สิบปี เขาจะกล่าววิงวอนว่า ข้าแต่พระเจ้าของเข้าพระองค์ขอพระองค์ทรงโปรดประทานแก่ข้าพระองค์ เพื่อให้ข้าพระองค์ขอบคุณต่อความโปรดของพระองค์ท่าน ซึ่งพระองค์ท่านได้ทรงโปรดปรานแก่ข้าพระองค์และบิดามารดาของข้าพระองค์ และให้ข้าพระองค์ทําความดีเพื่อให้ความดีเกิดขึ้นในลูกหลานของข้าพระองค์ แท้จริงข้าพระองค์ขอลุแก่โทษต่อพระองค์ และแท้จริงข้าพระองค์อยู่ในหมู่ผู้นอบน้อม');
INSERT INTO "king_fahad_thai" VALUES(46,16,'46:16','ชนเหล่านี้คือ บรรดาผู้ที่เรารับรองส่วนที่ดียิ่งจากพวกเขา ซึ่งพวกเขาได้ปฏิบัติไว้ และเราจะละเลยความผิดต่าง ๆของพวกเขาโดยอยู่ร่วมกับชาวสวรรค์ เป็นการสัญญาแห่งความจริงซึ่งพวกเขาได้ปฏิบัติไว้ และเราจะละเลยความผิดต่าง ๆของพวกเขาโดยอยู่ร่วมกับชาวสวรรค์ เป็นการสัญญาแห่งความจริงซึ่งพวกเขาได้ถูกสัญญาไว้');
INSERT INTO "king_fahad_thai" VALUES(46,17,'46:17','และผู้ที่กล่าวแก่บิดามารดาของเขา ว่า อุ๊ฟ แก่ท่านทั้งสอง ท่านทั้งสองขู่ฉันว่าฉันจะถูกให้ออกมาฟื้นคืนชีพอีกกระนั้นหรือ ? ทั้ง ๆ ที่หลายศตวรรษก่อนหน้าฉันได้ล่วงลับไปแล้ว และเขาทั้งสองร้องขอความช่วยเหลือต่ออัลลอฮฺ พลางกล่าวแก่ลูกว่า ความหายนะ จงประสบแก่เจ้า จงศรัทธาเถิด แท้จริงสัญญาของอัลลอฮฺนั้นเป็นความจริง แล้วเขาก็พูดว่า เรื่องนี้มิใช่อะไรอื่นนอกจากเป็นนิยายเหลวไหลสมัยก่อนเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(46,18,'46:18','ชนเหล่านี้คือ บรรดาผู้ที่พระดํารัส (แห่งการลงโทษ) เป็นที่คู่ควรแก่พวกเขาที่จะเข้าร่วมอยู่กับหมู่ชนต่าง ๆ แห่งพวกญินและมนุษย์ที่ได้ล่วงลับไปก่อนพวกเขา แท้จริงพวกเขาเป็นผู้ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(46,19,'46:19','และสําหรับทุกกลุ่มย่อมมีลําดับชั้นตามที่พวกเขาได้กระทําไว้ และพระองค์จะทรงตอบแทนพวกเขาอย่างครบถ้วนตามผลงานของพวกเขา โดยที่พวกเขาจะไม่ถูกอยุติธรรม');
INSERT INTO "king_fahad_thai" VALUES(46,20,'46:20','และวันที่บรรดาผู้ปฏิเสธศรัทธาจะถูกนํามาอยู่ต่อหน้าไฟนรก (จะมีเสียงกล่าวแก่พวกเขาว่า) พวกเจ้าได้เอาสิ่งดีงามทั้งหลายของพวกเจ้าในโลกดุนยาไปแล้ว และพวกเจ้าได้มีความสําราญกับมันแล้ว ฉะนั้นวันนี้พวกเจ้าจะได้รับการตอบแทนด้วยการลงโทษอันอัปยศ เนื่องด้วย พวกเจ้าหยิ่งยะโสในแผ่นดินโดยไม่เป็นธรรมและเนื่องด้วยพวกเจ้าฝ่าฝืน');
INSERT INTO "king_fahad_thai" VALUES(46,21,'46:21','จงรําลึกถึง (ฮูด) พี่น้องคนหนึ่งของพวกอ๊าด ขณะที่เขากล่าวเตือนหมู่ชนของเขาที่เนินเขาอัลอะฮิก๊อฟ และแน่นอน บรรดาผู้ตักเตือน (ร่อซูล) ก่อนหน้าเขาและภายหลังเขา (ได้กล่าวตักเตือนว่า) พวกท่านอย่าเคารพอิบาดะฮฺผู้ใดนอกจากอัลลอฮฺ แท้จริงฉันกลัวแทนพวกท่านถึงการลงโทษแห่งวันอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(46,22,'46:22','พวกเขากล่าวว่า ท่านมาหาพวกเราเพื่อจะหันห่างพวกเรา จากการเคารพสักการะพระเจ้าทั้งหลายของเรากระนั้นหรือ ? ดังนั้นจงนํา (การลงโทษ) ตามที่ท่านได้สัญญากับเราไว้ หากท่านอยู่ในหมู่ผู้สัตย์จริง');
INSERT INTO "king_fahad_thai" VALUES(46,23,'46:23','เขา (ฮูด) กล่าวว่า แท้จริงความรู้ (เรื่องการลงโทษ) นั้นอยู่ที่อัลลอฮฺ และฉันขอประกาศแก่พวกท่านตามที่ฉันได้ถูกส่งมาเพื่อการนี้ แต่ฉันเห็นว่าพวกท่านเป็นหมู่ชนผู้งมงาย');
INSERT INTO "king_fahad_thai" VALUES(46,24,'46:24','ครั้นเมื่อพวกเขาเห็นเมฆทึบเคลื่อนมายังที่ราบลุ่มในหมู่บ้านของพวกเขา พวกเขากล่าวว่า นี่คือเมฆที่จะให้น้ําฝนแก่เรา เปล่าเลยมันคือสิ่งที่พวกเจ้าเร่งขอให้เกิด มันคือลมพายุ ในนั้นมีการลงโทษอันเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(46,25,'46:25','มันจะทําลายทุกสิ่งตามพระบัญชาของพระเจ้าของมันแล้วพวกเขาก็กลายเป็นไม่มีอะไรให้แลเห็นนอกจากบ้านพักอาศัยของพวกเขาเท่านั้น เช่นนี้แหละเราจะตอบแทนหมู่ชนผู้กระทําผิด');
INSERT INTO "king_fahad_thai" VALUES(46,26,'46:26','และแน่นอน เราได้ตั้งหลักแหล่งที่มั่นคงแก่พวกเขา โดยที่เรามิได้ตั้งหลักแหล่งที่มั่นคงแก่พวกเจ้าในนั้น และเราได้ทําให้พวกเขามีหู มีตา และมีหัวใจ แต่ว่าหูของพวกเขา ตาของพวกเขา และหัวใจของพวกเขามิได้อํานวยประโยชน์อันใดแก่พวกเขา โดยที่พวกเขาปฏิเสธสัญญาณต่าง ๆ ของอัลลอฮฺ และสิ่งที่พวกเขาได้เคยเยาะเย้ยไว้นั้นก็ห้อมล้อมพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(46,27,'46:27','และโดยแน่นอน เราได้ทําลายหมู่บ้านต่าง ๆ ที่อยู่รอบ ๆ พวกเจ้า และเราได้แจกแจงสัญญาณต่าง ๆ หลายต่อหลายครั้ง หวังว่าพวกเขาจะกลับมาสํานึกผิด');
INSERT INTO "king_fahad_thai" VALUES(46,28,'46:28','ทําไมบรรดาที่พวกเขายึดถือมันเป็นพระเจ้าอื่นจากอัลลอฮฺเพื่อความใกล้ชิด (กับอัลลอฮฺ) จึงไม่ช่วยเหลือพวกเขาเล่า ? แต่พวกมันได้หายสาปสูญไปจากพวกเขา และนั่นคือ การกล่าวเท็จของพวกเขาและสิ่งที่พวกเขากุขึ้น');
INSERT INTO "king_fahad_thai" VALUES(46,29,'46:29','และจงรําลึกเมื่อเราได้ให้ญินจํานวนหนึ่งมุ่งไปยังเจ้า เพื่อฟังอัลกุรอาน ครั้นเมื่อพวกเขามาปรากฏตัวต่อหน้าอัลกุรอาน พวกเขากล่าวว่า จงนิ่งฟังซิ เมื่อ (การอ่าน) จบลงแล้ว พวกเขาก็หันกลับไปยังหมู่ชนของพวกเขาเพื่อทําหน้าที่เป็นผู้ตักเตือน');
INSERT INTO "king_fahad_thai" VALUES(46,30,'46:30','พวกเขากล่าวว่า โอ้หมู่ชนของเราเอ๋ย แท้จริงเราได้ฟังคัมภีร์ (อัลกุรอาน) ถูกประทานลงมาหลังจากมูซา เป็นการยืนยันในสิ่งที่ได้มีมาก่อนอัลกุรอาน เพื่อชี้แนะทางไปสู่สัจธรรม และแนวทางที่เที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(46,31,'46:31','โอ้หมู่ชนของเราเอ๋ย จงตอบรับต่อผู้เรียกร้องของอัลลอฮฺเถิด และจงศรัทธาต่อเขา พระองค์จะทรงอภัยโทษจากความผิดของพวกท่านให้แก่พวกท่าน และจะทรงให้พวกท่านรอดพ้นจากการลงโทษอันเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(46,32,'46:32','และผู้ใดที่ไม่ตอบรับผู้เรียกร้องของอัลลอฮฺ เขาจะไม่รอดพ้น (จากการลงโทษ) ในแผ่นดินนี้ และสําหรับเขาจะไม่มีผู้คุ้มครองอื่นจากพระองค์ ชนเหล่านี้อยู่ในการหลงผิดอย่างชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(46,33,'46:33','และพวกเขาไม่เห็นดอกหรือว่า แท้จริงอัลลอฮฺ ซึ่งทรงสร้างชั้นฟ้าทั้งหลายและแผ่นดินนี้ และมิทรงอ่อนเพลียต่อการสร้างสิ่งเหล่านั้น ย่อมทรงเป็นผู้อานุภาพที่จะให้คนตายมีชีวิตขึ้นมาอีก แน่นอนแท้จริงพระองค์เป็นผู้ทรงอานุภาพเหนือทุก ๆ สิ่ง');
INSERT INTO "king_fahad_thai" VALUES(46,34,'46:34','และวันซึ่งบรรดาผู้ปฏิเสธศรัทธาจะถูกนํามาอยู่ต่อหน้าไฟนรก (จะมีเสียงกล่าวขึ้นว่า) นี่มิใช่ความจริงดอกหรือ ?พวกเขากล่าวว่า แน่นอนครับ ขอสาบานต่อพระเจ้าจงลิ้มรสการลงโทษตามที่พวกเจ้าได้ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(46,35,'46:35','ดังนั้นเจ้าจงอดทนดังเช่นบรรดาผู้ตั้งจิตมั่นแห่งร่อซูลทั้งหลาย ได้อดทนมาก่อนแล้ว และอย่ารีบเร่ง (ให้มีการลงโทษ) แก่พวกเขา วันที่พวกเขาจะเห็นสิ่งที่ถูกสัญญาไว้แก่พวกเขานั้น ประหนึ่งว่าพวกเขามิได้พํานักอยู่ในโลกนี้เว้นแต่เพียงชั่วครู่หนึ่งยามกลางวันเท่านั้น นี้คือการประกาศตักเตือนดังนั้นความหายนะจะไม่ประสบแก่ผู้ใดนอกจากหมู่ชนผู้ฝ่าฝืนเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(47,1,'47:1','บรรดาผู้ปฏิเสธศรัทธา และปิดกั้นให้ห่างจากทางของอัลลอฮฺนั้น พระองค์ได้ทรงทําให้การงานของพวกเขาไร้ผล');
INSERT INTO "king_fahad_thai" VALUES(47,2,'47:2','ส่วนบรรดาผู้ศรัทธาและกระทําความดีทั้งหลาย และศรัทธาในสิ่งที่ถูกประทานแก่มุฮัมมัด และว่าอัลกุรอานนั้นเป็นสัจธรรมมาจากพระเจ้าของพวกเขา พระองค์จะทรงลบล้างความชั่วของพวกเขา ให้ออกไปจากพวกเขาและจะทรงปรับปรุงสภาพของพวกเขาให้ดีขึ้น');
INSERT INTO "king_fahad_thai" VALUES(47,3,'47:3','ทั้งนี้เพราะว่า บรรดาผู้ปฏิเสธศรัทธาได้ปฏิบัติตามความเท็จ แต่ส่วนบรรดาผู้ศรัทธาได้ปฏิบัติตามสัจธรรมมาจากพระเจ้าของพวกเขา เช่นนี้แหละอัลลอฮ.ทรงยกอุทาหรณ์ทั้งหลายของพวกเขาแก่ปวงมนุษย์');
INSERT INTO "king_fahad_thai" VALUES(47,4,'47:4','และเมื่อพวกเจ้าพบบรรดาผู้ปฏิเสธศรัทธาก็จงฟันที่คอ (จงฆ่าเสีย) จนกระทั่งเมื่อพวกเจ้าปราบพวกเขาจนแพ้แล้ว ก็จงจับพวกเขาเป็นเชลยหลังจากนั้นจะปล่อยเป็นไทหรือจะเรียกเอาค่าไถ่ก็ได้ จนกระทั่งการทําสงครามได้สิ้นสุดลงด้วยการวางอาวุธ เช่นนั้นแหละ และหากอัลลอฮ.ทรงประสงค์แน่นอน พระองค์จะทรงตอบแทนการลงโทษพวกเขา แต่ทั้งนี้เพื่อพระองค์จะทรงทดสอบบางคนในหมู่พวกเจ้ากับอีกบางคน ส่วนบรรดาผู้ที่ถูกฆ่าตายในทางของอัลลอฮ. พระองค์จะไม่ทรงทําให้การงานของพวกเขาไร้ผลเป็นอันขาด');
INSERT INTO "king_fahad_thai" VALUES(47,5,'47:5','พระองค์จะทรงชี้แนะทางแก่พวกเขาและจะทรงปรับปรุงสภาพของพวกเขาให้ดีขึ้น');
INSERT INTO "king_fahad_thai" VALUES(47,6,'47:6','และจะทรงให้พวกเขาเขาสวนสวรรค์ ซึ่งพระองค์ทรงแจ้งให้พวกเขารู้แล้ว');
INSERT INTO "king_fahad_thai" VALUES(47,7,'47:7','โอ้บรรดาผู้ศรัทธาเอ๋ย หากพวกเจ้าสนับสนุน (ศาสนาของ) อัลลอฮ. พระองค์ก็จะทรงสนับสนุนพวกเจ้าและจะทรงตรึงเท้าของพวกเจ้าให้มั่นคง');
INSERT INTO "king_fahad_thai" VALUES(47,8,'47:8','ส่วนบรรดาผู้ปฏิเสธศรัทธานั้น ความพินาศหายนะจะได้แก่พวกเขาและพระองค์ได้ทรงทําให้การงานของพวกเขาไร้ผล');
INSERT INTO "king_fahad_thai" VALUES(47,9,'47:9','ทั้งนี้เพราะว่า พวกเขาเกลียดชังสิ่งที่อัลลอฮ.ทรงประทานลงมา พระองค์จึงทรงทําให้การงานของพวกเขาไร้ผล');
INSERT INTO "king_fahad_thai" VALUES(47,10,'47:10','พวกเขามิได้ท่องเที่ยวไปตามแผ่นดินดอกหรือ แล้วพิจารณาดูว่า บั้นปลายของประชาชาติในยุคก่อนหน้าพวกเขานั้นเป็นเช่นใด? อัลลอฮ.ได้ทรงทําลายล้างพวกเขา และสําหรับพวกปฏิเสธศรัทธาก็เป็นเช่นเดียวกัน');
INSERT INTO "king_fahad_thai" VALUES(47,11,'47:11','ทั้งนี้เพราะว่าอัลลอฮ.เป็นผู้ทรงคุ้มครองบรรดาผู้ศรัทธา และแน่นอนพวกปฏิเสธศรัทธาไม่มีผู้คุ้มครองสําหรับพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(47,12,'47:12','แท้จริงอัลลอฮ.จะทรงให้บรรดาผู้ศรัทธาและกระทําความดีทั้งหลายเข้าสู่สวนสวรรค์หลากหลาย ณ เบื้องล่างสวนสวรรค์มีลําน้ําหลายสายไหลผ่าน ส่วนบรรดาผู้ปฏิเสธศรัทธานั้นพวกเขาจะหลงระเริงและกินเยี่ยงปศุสัตว์กัน และไฟนรกคือที่พํานักของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(47,13,'47:13','และกี่เมืองมาแล้ว ที่มันมีพลังเข้มแข็งกว่าเมืองของเจ้า ซึ่งมันขับใสเจ้า (มุฮัมมัด)ออกไป เราได้ทําลายล้างพวกเขา ดังนั้น สําหรับพวกเขาจึงไม่มีผู้ช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(47,14,'47:14','ดังนั้นผู้ที่อยู่บนหลักฐานอันชัดแจ้งจากพระเจ้าของเขาจะเหมือนกับผู้ที่ความชั่วแห่งการงานของเขาได้ถูกทําให้เพริศแพร้วแก่เขา และพวกเขาก็ปฏิบัติตามอารมณ์ต่ําของพวกเขากระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(47,15,'47:15','อุปมาของสวนสวรรค์ซึ่งบรรดาผู้ยําเกรงได้ถูกสัญญาไว้ในสวนสวรรค์นั้นมีธารน้ําหลายสายที่ไม่ผันแปร (ทั้งรสและกลิ่น) และธารน้ํานมหลายสาย ที่รสชาติของมันไม่เปลี่ยนแปลง และธารน้ําจัณฑ์ (เหล้า) หลายสายเป็นโอชะอร่อยแก่ผู้ดื่ม และธารน้ําผึ้งที่สะอาดบริสุทธิ์หลายสาย และสําหรับพวกเขาในสวนสวรรค์นั้นมีผลไม้หลายชนิด และการอภัยโทษจากพระเจ้าของพวกเขาจะเหมือนกับผู้ที่พํานักอยู่ในไฟนรก และถูกให้ดื่มน้ําร้อนจัดแล้วมันตัดลําไส้ของพวกเขากระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(47,16,'47:16','ในหมู่พวกเขามีผู้เงี่ยหูฟังเจ้าจนกระทั่งเมื่อพวกเขาออกไปจากเจ้า พวกเขาก็จะพูดแก่ผู้มีความรู้ว่า เมื่อกี้นี้เขา (มุฮัมมัด) พูดอะไรกัน ชนเหล่านี้แหละคือบรรดาผู้ที่อัลลอฮ.ทรงประทับตราบนหัวใจของพวกเขา และพวกเขาปฏิบัติตามอารมณ์ต่ําของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(47,17,'47:17','ส่วนผู้ที่ปฏิบัติตามแนวทางที่ถูกต้อง พระองค์ทรงเพิ่มแนวทางที่ถูกต้องให้แก่พวกเขา และจะทรงประทานให้แก่พวกเขาซึ่งการยําเกรงของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(47,18,'47:18','ดังนั้น พวกเขามิได้คอยสิ่งใดนอกจากยามอวสานซึ่งมันจะมาหาพวกเขาอย่างกระทันหัน แต่ว่าเครื่องหมายต่าง ๆ ของมันได้มีมาแล้ว ดังนั้น เมื่อการตักเตือนของพวกเขาได้มายังพวกเขาแล้วจะเกิดประโยชน์อันใดเล่าแก่พวกเขา?');
INSERT INTO "king_fahad_thai" VALUES(47,19,'47:19','ฉะนั้นพึงรู้เถิดว่า ไม่มีพระเจ้าอื่นใดนอกจากอัลลอฮ. และจงขออภัยโทษต่อความผิดเพื่อตัวเจ้าและเพื่อบรรดาผู้ศรัทธาหญิง และอัลลอฮ.ทรงรู้ดียิ่งถึงพฤติการณ์ของพวกเจ้าและที่พํานักของพวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(47,20,'47:20','และบรรดาผู้ศรัทธากล่าวว่า ทําไม่สักซูเราะฮฺหนึ่งจึงไม่ถูกประทานลงมา? ครั้นเมื่อซูเราะฮฺหนึ่งที่รัดกุมชัดเจนถูกประทานลงมา และได้มีการทําสงครามถูกรวมไว้ในนั้น เจ้าจะเห็นบรรดาผู้ที่ในหัวใจของพวกเขามีโรคจะจ้องมองไปยังเจ้าเสมือนการมองของผู้เป็นลมใกล้จะตาย ดังนั้นความหายนะจงประสบแก่พวกเขาเถิด');
INSERT INTO "king_fahad_thai" VALUES(47,21,'47:21','การเชื่อฟังและปฏิบัติตามคําพูดที่ดีไพเราะ(นั้นเป็นการที่ดียิ่ง) ดังนั้นเมื่อกิจการใดถูกกําหนดไว้แล้ว หากว่าพวกเขาจริงใจต่ออัลลอฮ.แล้ว ก็จะเป็นการดีแก่เขา');
INSERT INTO "king_fahad_thai" VALUES(47,22,'47:22','ดังนั้น หวังกันว่า หากพวกเจ้าผินหลังให้(กับการอีมานแล้ว)พวกเจ้าก็จะก่อความเสียหายในแผ่นดินและตัดความสัมพันธ์ทางเครือญาติของพวกเจ้ากระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(47,23,'47:23','ชนเหล่านี้คือบรรดาผู้ที่อัลลอฮฺทรงสาปแช่งพวกเขา ดังนั้นพระองค์จึงทรงทําให้พวกเขาหูหนวก และทรงทําให้พวกเขาตาบอด');
INSERT INTO "king_fahad_thai" VALUES(47,24,'47:24','พวกเขามิได้พิจารณาใคร่ครวญอัลกุรอานดอกหรือ? แต่ว่าบนหัวใจของพวกเขามีกุญแจหลายดอกลั่นอยู่');
INSERT INTO "king_fahad_thai" VALUES(47,25,'47:25','แท้จริงบรรดาผู้ผินหลังกลับของพวกเขาหลังจากที่แนวทางที่ถูกต้องเป็นที่ประจักษ์แก่พวกเขาแล้ว ชัยฎอนมารร้ายได้ล่อลวง พวกเขาได้ให้ความหวังแก่พวกเขา (ว่าจะมีชีวิตยืนนาน)');
INSERT INTO "king_fahad_thai" VALUES(47,26,'47:26','ทั้งนี้เพราะว่าพวกเขาได้กล่าวแก่บรรดาผู้เกลียดชังสิ่งที่อัลลอฮ.ทรงประทานลงมาว่าเราจะเชื่อฟังปฏิบัติตามในกิจการบางอย่าง แต่อัลลอฮ.ทรงทราบดีถึงความลับของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(47,27,'47:27','แล้ว(สภาพของพวกเขา) จะเป็นเช่นไร เมื่อมะลาอิกะฮ.มาเอาชีวิตของพวกเขาโดยตีใบหน้าของพวกเขาและหลังของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(47,28,'47:28','ทั้งนี้เพราะว่าพวกเขาได้ปฏิบัติตามสิ่งที่จะก่อความกริ้วแด่อัลลอฮ. และพวกเขารังเกียจความโปรดปรานของพระองค์ ดังนั้นพระองค์จึงทรงทําให้การงานของพวกเขาไร้ผล');
INSERT INTO "king_fahad_thai" VALUES(47,29,'47:29','บรรดาผู้ที่หัวใจของพวกเขามีโรคคิดหรือว่าอัลลอฮ.จะไม่ทรงนําเอาความอิจฉาริษยาของพวกเขาออกมาให้เป็นที่ประจักษ์');
INSERT INTO "king_fahad_thai" VALUES(47,30,'47:30','และหากเราประสงค์แน่นอนเราจะเปิดเผยพวกเขาแก่เจ้า แล้วเจ้าก็จะรู้จักพวกเขาอย่างแน่นอนที่เครื่องหมายของพวกเขา และแน่นอนเจ้าจะรู้จักพวกเขาได้ในน้ําเสียงแห่งการพูด และอัลลอฮฺทรงรู้ดีถึงการงานของพวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(47,31,'47:31','และแน่นอนเราจะทดสอบพวกเจ้าจนกระทั่งเราจะได้รู้ถึงบรรดาผู้ต่อสู้ดิ้นรน และบรรดาผู้หนักแน่นอดทนในหมู่พวกเจ้า และเราจะทดสอบการงานของพวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(47,32,'47:32','แท้จริงบรรดาผู้ปฏิเสธศรัทธาและปิดกั้นให้ห่างจากทางของอัลลอฮ. และต่อต้านร่อซูลนี้หลังจากที่แนวทางที่ถูกต้องได้เป็นที่ประจักษ์แก่พวกเขาแล้ว พวกเขาจะไม่ก่อให้เกิดอันตรายใด ๆ แก่อัลลอฮ.ได้เลย พระองค์จะทรงทําให้การงานของพวกเขาไร้ผล');
INSERT INTO "king_fahad_thai" VALUES(47,33,'47:33','โอ้บรรดาผู้ศรัทธาเอ๋ย จงเชื่อฟังปฏิบัติตามอัลลอฮ.และจงเชื่อฟังปฏิบัติตามร่อซูลคนนี้เถิดและอย่าทําให้การงานของพวกเจ้าไร้ประโยชน์');
INSERT INTO "king_fahad_thai" VALUES(47,34,'47:34','แท้จริง บรรดาผู้ปฏิเสธศรัทธาและปิดกั้นให้ห่างจากทางของอัลลอฮฺ แล้วพวกเขาตายลงทั้ง ๆ ที่พวกเขาเป็นผู้ปฏิเสธศรัทธาอยู่ อัลลอฮฺจะไม่ทรงอภัยโทษให้พวกเขาเลย');
INSERT INTO "king_fahad_thai" VALUES(47,35,'47:35','ดังนั้น พวกเจ้าอย่าท้อแท้และเรียกร้องไปสู่การสงบศึก เพราะพวกเจ้าเป็นผู้อยู่เหนือสุด และอัลลอฮ.ทรงอยู่ร่วมกับพระเจ้าและพระองค์จะไม่ทรงลิดรอนผลตอบแทนแห่งการงานของพวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(47,36,'47:36','การมีชีวิตอยู่ในโลกนี้ เป็นแต่เพียงการละเล่นและการสนุกสนานร่าเริงเท่านั้น และหากพวกเจ้าศรัทธาและยําเกรงพระองค์จะทรงประทานรางวัลของพวกเจ้าแก่พวกเจ้าและพระองค์จะไม่ทรงขอทรัพย์สินของพวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(47,37,'47:37','หากพระองค์จะทรงขอทรัพย์สินต่อพวกเจ้าและทรงรบเร้าพวกเจ้า (ให้บริจาค) พวกเจ้าก็จะตระหนี่ และพระองค์จะทรงนําเอาความอึดอัดใจของพวกเจ้าออกมาให้ประจักษ์');
INSERT INTO "king_fahad_thai" VALUES(47,38,'47:38','พึงรู้เถิดว่าพวกเจ้านี้แหละคือหมู่ชนที่ถูกเรียกร้องให้บริจาคในทางของอัลลอฮ. แต่มีบางคนในหมู่พวกเจ้าเป็นผู้ตระหนี่ ดังนั้น ผู้ใดตระหนี่เขาก็ตระหนี่แก่ตัวของเขาเอง เพราะอัลลอฮ.เป็นผู้ทรงมั่งมี แต่พวกเจ้าเป็นผู้ขัดสนและถ้าพวกเจ้าเผินหลังออก พระองค์ก็จะทรงเปลี่ยนหมู่ชนอื่นแทนพวกเจ้า แล้วพวกเขาเหล่านั้นจะไม่เป็นเช่นพวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(48,1,'48:1','แท้จริงเราได้ให้ชัยชนะแก่เจ้าซึ่งเป็นชัยชนะอย่างชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(48,2,'48:2','เพื่ออัลลอฮจะได้ทรงอภัยโทษความผิดของเจ้าที่ได้ล่วงไปแล้วและที่จะเกิดขึ้นภายหลังและจะทรงให้ความโปรดปรานของพระองค์ครบสมบูรณ์แก่เจ้าและทรงชี้แนะทางแก่เจ้าคือทางอันเที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(48,3,'48:3','และอัลลอฮฺจะทรงช่วยเหลือเจ้าด้วยการช่วยเหลืออย่างเข้มแข็ง');
INSERT INTO "king_fahad_thai" VALUES(48,4,'48:4','พระองค์คือผู้ทรงประทานความเงียบสงบลงมาในจิตใจของบรรดาผู้ศรัทธา เพื่อพวกเขาจะได้เพิ่มพูนการศรัทธาให้กับการศรัทธาของพวกเขา และเป็นของอัลลอฮฺคือไพร่พลแห่งชั้นฟ้าทั้งหลายและแผ่นดิน และอัลลอฮฺเป็นผู้ทรงรอบรู้ผู้ทรงปรีชาญาณเสมอ');
INSERT INTO "king_fahad_thai" VALUES(48,5,'48:5','เพื่อพระองค์จะทรงให้บรรดาผู้ศรัทธาชายและบรรดาผู้ศรัทธาหญิงได้เข้าสวนสวรรค์หลากหลายณเบื้องล่างของสวนสวรรค์มีธารน้ําหลายสายไหลผ่านพวกเขาเป็นผู้พํานักอยู่ตลอดกาลในนั้นและพระองค์จะทรงลบล้างความชั่วของพวกเขาออกจากพวกเขาและนั่นคือชัยชนะอันใหญ่หลวงฯที่อัลลอฮ.');
INSERT INTO "king_fahad_thai" VALUES(48,6,'48:6','และเพื่อพระองค์จะทรงลงโทษแก่พวกมุนาฟิกีนชายและพวกมุนาฟิกีนหญิงและบรรดาผู้ตั้งภาคีชาย(มุชริกีน)และบรรดาผู้ตั้งภาคีหญิง(มุชริก๊าต)โดยพวกเขาคิดเกี่ยวกับอัลลอฮ.ด้วยความคิดร้ายเหตุร้ายเหล่านั้นจงประสบแก่พวกเขาเถิดและอัลลอฮ.ทรงโกรธกริ้วแก่พวกเขาและทรงสาปแช่งพวกเขาอีกทั้งทรงเตรียมนรกญะฮันนัมไว้สําหรับพวกเขาอีกด้วยและมันเป็นทางกลับที่ชั่วร้ายยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(48,7,'48:7','และเป็นของอัลลอฮ.คือไพร่พลแห่งชั้นฟ้าทั้งหลายและแผ่นดินและอัลลอฮ.เป็นผู้ทรงอํานาจผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(48,8,'48:8','แท้จริงเราได้ส่งเจ้า(มุฮัมมัด)มาเพื่อเป็นพยานและผู้แจ้งข่าวดีและผู้แจ้งข่าวร้าย');
INSERT INTO "king_fahad_thai" VALUES(48,9,'48:9','เพื่อให้พวกเจ้าศรัทธาต่ออัลลอฮฺ และร่อซูลของพระองค์ และให้ความช่วยเหลือเขา(ร่อซูล) และแซ่ซ้องสดุดีพระองค์ทั้งในยามเช้าและยามเย็น');
INSERT INTO "king_fahad_thai" VALUES(48,10,'48:10','แท้จริงบรรดาผู้ที่ให้สัตยาบันกับเจ้านั้นเสมือนกับว่าพวกเขาได้ให้สัตยาบันกับอัลลอฮ.พระหัตถ์ของอัลลอฮ.ทรงอยู่เหนือมือของพวกเขาฉะนั้นผู้ใดทําลาย(สัตยาบัน)เสมือนกับว่าเขาทําลายตัวของเขาเองส่วนผู้ใดปฏิบัติตามสัญญาที่เขาได้มีไว้กับอัลลอฮ.โดยครบถ้วนพระองค์ก็จะทรงตอบแทนรางวัลอันใหญ่หลวงแก่เขา');
INSERT INTO "king_fahad_thai" VALUES(48,11,'48:11','ชาวอาหรับชนบทที่เหลืออยู่ในเมืองจะกล่าวแก่เจ้าว่าทรัพย์สินของเราและครอบครัวของเราทําให้เรามีธุระยุ่งอยู่ ดังนั้นได้โปรดขออภัยให้แก่เราด้วย พวกเขากล่าวด้วยลิ้นของพวกเขาโดยไม่มีอะไรในหัวใจของพวกเขา จงกล่าวเถิดมุฮัมมัดใครเล่าจะมีอํานาจอันใดที่จะป้องกันพวกเจ้าจากอัลลอฮฺหากพระองค์ทรงประสงค์ให้ความทุกข์แก่พวกเจ้า หรือพระองค์ทรงประสงค์จะให้ประโยชน์แก่พวกเจ้า แต่ว่าอัลลอฮฺทรงตระหนักยิ่งในสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(48,12,'48:12','หามิได้พวกเจ้าคิดว่าอัลร่อซูลและบรรดามุอ.มินผู้ศรัทธาจะไม่กลับไปยังครอบครัวของพวกเขาเป็นอันขาดและนั่นได้ถูกทําให้เป็นที่เพริศแพร้วในจิตใจของพวกเจ้าและพวกเจ้าได้คิดร้ายและพวกเจ้าเป็นหมู่ชนที่วิบัติ');
INSERT INTO "king_fahad_thai" VALUES(48,13,'48:13','และถ้าผู้ใดมิได้ศรัทธาต่ออัลลอฮฺและร่อซูลของพระองค์ แท้จริงเราได้เตรียมไฟที่ลุกโชติช่วงไว้สําหรับผู้ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(48,14,'48:14','และอํานาจเด็ดขาดแห่งชั้นฟ้าทั้งหลายและแผ่นดินนั้นเป็นของอัลลอฮฺ พระองค์จะทรงอภัยแก่ผู้ที่พระองค์ทรงประสงค์ และจะทรงลงโทษผู้ที่พระองค์ทรงประสงค์ และอัลลอฮฺนั้นเป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(48,15,'48:15','บรรดาผู้ที่เหลืออยู่ในเมืองจะกล่าวว่า เมื่อพวกท่านออกเดินทางไปยังกองทรัพย์เชลย(ที่ค็อยบัร) เพื่อไปยึดเอามา ก็จงปล่อยให้พวกเราออกติดตามพวกท่านไปด้วย พวกเขาประสงค์ที่จะเปลี่ยนคํากล่าวของอัลลอฮฺ จงกล่าวเถิดมุฮัมมัด พวกท่านจะติดตามพวกเราไปไม่ได้เป็นอันขาด เพราะอัลลอฮฺได้ตรัสไว้ก่อนแล้ว พวกเขาก็จะกล่าวอีกว่า หามิได้พวกท่านอิจฉาพวกเรา เปล่าเลยพวกเขาไม่เข้าใจอะไรเลยนอกจากเพียงเล็กน้อย');
INSERT INTO "king_fahad_thai" VALUES(48,16,'48:16','จงกล่าวเถิดมุฮัมมัดแก่ชาวอาหรับชนบทที่เหลืออยู่ในเมืองว่าพวกท่านจะถูกเรียกให้ไปร่วมต่อสู้พวกเขาหรือให้พวกเขายอมจํานนดังนั้นเมื่อพวกท่านเชื่อฟังปฏิบัติตามอัลลอฮก็จะทรงประทานรางวัลอันดีงามแก่พวกท่านแต่ถ้าพวกท่านผินหลังออกมาก่อนแล้วพระองค์จะทรงลงโทษพวกท่านด้วยการลงโทษอันเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(48,17,'48:17','ไม่เป็นการลําบากอันใดแก่คนตาบอดและไม่เป็นการลําบากใจอันใดแก่คนพิการและไม่เป็นการลําบากใจอันใดแก่คนป่วยแต่ถ้าผู้ใดเชื่อฟังปฏิบัติตามอัลลอฮและร่อซูลของพระองค์พระองค์จะทรงให้เขาเข้าสู่สวนสวรรค์หลากหลายณเบื้องล่างมีธารน้ําหลายสายไหลผ่านส่วนผู้ใดผินหลังออกพระองค์จะทรงลงโทษเขาด้วยการลงโทษอันเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(48,18,'48:18','โดยแน่นอนอัลลอฮฺทรงโปรดปรานต่อบรรดาผู้ศรัทธาขณะที่พวกเขาให้สัตยาบันแก่เจ้าใต้ต้นไม้ (ที่ฮุดัยบิยะฮฺ) เพราะพระองค์ทรงรอบรู้ดีถึงสิ่งที่มีอยู่ในจิตใจของพวกเขา พระองค์จึงได้ทรงประทานความสงบใจลงมาบนพวกเขา และได้ทรงตอบแทนให้แก่พวกเขาซึ่งชัยชนะอันใกล้นี้');
INSERT INTO "king_fahad_thai" VALUES(48,19,'48:19','และทรัพย์เชลยอันมากมายที่พวกเขาจะได้รับมันและอัลลอฮ.เป็นผู้ทรงอํานาจผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(48,20,'48:20','อัลลอฮ.ด้ทรงสัญญาแก่พวกเจ้าซึ่งทรัพย์เชลยอันมากมายที่พวกเจ้าจะได้รับมันโดยพระองค์ทรงเร่ง(ทรัพย์เชลยที่ค็อยบัร)อันนี้แก่พวกเจ้าและพระองค์ทรงยับยั้งมือของผู้คน(พวกยะฮูด)จาก(การทําร้าย)พวกเจ้าและเพื่อมัน(การยับยั้งจากการทําร้าย)จะได้เป็นสัญญาณหนึ่งแก่บรรดาผู้ศรัทธาและเพื่อพระองค์จะได้ทรงชี้แนะทางแก่พวกเจ้าสู่ทางอันเที่ยงตรง พระองค์จะได้ทรงชี้แนะทางแก่พวกเจ้าสู่ทางอันเที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(48,21,'48:21','และทรัพย์เชลยอันอื่นอีกที่พวกเจ้าไม่มีกําลังที่จะเอาชนะมันได้แต่อัลลอฮทรงล้อมพวกมันไว้แล้ว (คืออาณาจักรเปอร์เซียและโรมันตะวันออก) และอัลลอฮ.เป็นผู้ทรงอานุภาพเหนือทุกสิ่งอย่าง');
INSERT INTO "king_fahad_thai" VALUES(48,22,'48:22','และถ้าบรรดาผู้ปฏิเสธศรัทธาได้ต่อสู้กับพวกเจ้า พวกเขาก็จะผินหลังกลับอย่างแน่นอน แล้วพวกเขาจะไม่พบผู้คุ้มครองและผู้ช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(48,23,'48:23','นั่นคือแนวทางของอัลลอฮฺแก่บรรดาผู้ที่ล่วงลับไปแล้วแต่กาลก่อน และเจ้าจะไม่พบการเปลี่ยนแปลงใด ๆ ในแนวทางของอัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(48,24,'48:24','และพระองค์คือผู้ทรงยับยั้งมือของพวกเขาจากพวกเจ้า และมือของพวกเจ้าจากพวกเขา (มิให้มีการสู้รบกัน) ที่หุบเขานครมักกฮ. (ที่ฮุดัยบียะฮ.) หลังจากที่พระองค์ได้ทรงให้พวกเจ้ามีชัยชนะเหนือพวกเขา และอัลลอฮ.ทรงรู้เห็นในสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(48,25,'48:25','พวกเขาคือบรรดาผู้ปฏิเสธศรัทธาและปิดกั้นพวกเจ้าให้หันห่างจากมัสยิดอัลฮะรอม และการเชือดสัตว์พลีที่ถูกกักกันไว้มิให้บรรลุสู่ที่เชือดของมัน และหากมิใช่เพราะมีบรรดาผู้ศรัทธาชายและบรรดาผู้ศรัทธาหญิงซึ่งพวกเจ้าไม่รู้จักพวกเขา พวกเจ้าก็จะฆ่าพวกเขา แล้วก็จะก่อให้เกิดโทษแก่พวกเจ้าเพราะพวกเขาโดยไม่รู้ตัว ทั้งนี้เพื่ออัลลอฮฺจะทรงให้ผู้ที่พระองค์ทรงประสงค์เข้าอยู่ในความเมตตาของพระองค์ หากพวกเขาแยกออกจากกัน แน่นอนเราก็จะลงโทษบรรดาผู้ปฏิเสธศรัทธาในหมู่พวกเขาซึ่งการลงโทษอันเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(48,26,'48:26','ขณะที่พวกปฏิเสธศรัทธาได้ทําให้ความหยิ่งยะโสมีขึ้นในจิตใจของพวกเขา ซึ่งเป็นความหยิ่งยะโสในสมัยแห่งความงมงาย อัลลอฮฺจึงประทานความเงียบสงบของพระองค์ให้แก่ร่อซูลของพระองค์และแก่บรรดาผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(48,27,'48:27','โดยแน่นอนอัลลอฮ.ได้ทรงทําให้ความฝันนั้นสมจริงแก่ร่อซูลของพระองค์ด้วยความจริง แน่นอนพวกเจ้าจะได้เข้าสู่มัสยิดอัลฮะรอมอย่างปลอดภัยหากอัลลอฮ.ทรงประสงค์ (อินชาอัลลอฮ.) โดย (บางคน) โกนผมของพวกเจ้าและ (อีกบางคน) ตัดผม พวกเจ้าอย่าได้หวาดกลัว เพราะอัลลอฮ.ทรงรอบรู้สิ่งที่พวกเจ้าไม่รู้ ดังนั้นพระองค์จึงได้ทรงกําหนดชัยชนะอื่นจากนั้น ซึ่งชัยชนะอันใกล้นี้');
INSERT INTO "king_fahad_thai" VALUES(48,28,'48:28','พระองค์คือผู้ทรงส่งร่อซูลของพระองค์พร้อมด้วยแนวทางที่ถูกต้องและศาสนาแห่งสัจธรรมเพื่อพระองค์จะทรงให้ศาสนา (ของพระองค์) นั้นประจักษ์แจ้งเหนือศาสนาอื่นทั้งมวล และพอเพียงแล้วที่อัลลอฮฺทรงเป็นพยาน');
INSERT INTO "king_fahad_thai" VALUES(48,29,'48:29','มุฮัมมัดเป็นร่อซูลของอัลลอฮฺ และบรรดาผู้ที่อยู่ร่วมกับเขา เป็นผู้เข้มแข็งกล้าหาญต่อพวกปฏิเสธศรัทธา เป็นผู้เมตตาสงสารระหว่างพวกเขาเอง เจ้าจะเห็นพวกเขาเป็นผู้รูกั๊วะ ผู้สุญูด โดยแสวงหาคุณความดีจากอัลลอฮฺและความโปรดปราน (ของพระองค์) เครื่องหมายของพวกเขาอยู่บนใบหน้าของพวกเขาเนื่องจากร่องรอยแห่งการสุญูด นั่นคืออุปมาของพวกเขาที่มีอยู่ในอัตเตารอต และอุปมาของพวกเขาที่มีอยู่ในอัลอินญีล ประหนึ่งเมล็ดพืชที่งอกหน่อหรือกิ่งก้านของมันออกมาแล้วทําให้มันงอกงาม แล้วมันก็เติบโตแข็งแรงและทรงตัวอยู่ได้บนลําต้นของมัน นําความปลื้มปิติมาให้แก่ผู้หว่าน เพื่อที่พระองค์จะก่อความโกรธแค้นแก่พวกปฏิเสธศรัทธา เพราะพวกเขา (มุสลิมีน) และอัลลอฮฺทรงสัญญาบรรดาผู้ศรัทธาและกระทําความดีทั้งหลายในหมู่พวกเขาว่าจะได้รับการอภัยโทษและรางวัลอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(49,1,'49:1','โอ้ศรัทธาชนทั้งหลาย ! พวกเจ้าอย่าได้ล้ําหน้า (ในการกระทําใด ๆ) เมื่ออยู่ต่อหน้าอัลลอฮฺ และร่อซูลของพระองค์ พวกเจ้าจงยําเกรงอัลลอฮฺเถิด แท้จริงอัลลอฮฺนั้นเป็นผู้ทรงได้ยิน ผู้ทรงรอบรู้');
INSERT INTO "king_fahad_thai" VALUES(49,2,'49:2','โอ้ศรัทธาชนทั้งหลาย! พวกเจ้าอย่าได้ยกเสียงของพวกเจ้าเหนือเสียงของอัลนะบี และอย่าพูดเสียงดังกับเขา (มุฮัมมัด) เยี่ยงการพูดเสียงดังของบางคนของพวกเจ้ากับอีกบางคน เพราะ (เกรงว่า) การงานต่าง ๆ ของพวกเจ้าจะสูญเสียไป โดยที่พวกเจ้าไม่รู้สึกตัว');
INSERT INTO "king_fahad_thai" VALUES(49,3,'49:3','แท้จริงบรรดาผู้ที่ลดเสียงของพวกเขา ณ ที่ร่อซูลุลลอฮฺนั้น ชนเหล่านั้น คือบรรดาผู้ที่อัลลอฮฺทรงทดสอบจิตใจของพวกเขาเพื่อความยําเกรง สําหรับพวกเขาจะได้รับการอภัยโทษและรางวัลอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(49,4,'49:4','แท้จริงบรรดาผู้ส่งเสียงเรียกเจ้าทางเบื้องหลังห้องหับเหล่านั้น ส่วนใหญ่ของพวกเขาไม่ใช้สติปัญญา');
INSERT INTO "king_fahad_thai" VALUES(49,5,'49:5','และหากว่าพวกเขาอดทนไว้จนกว่าเจ้าจะออกมาหาพวกเขาแล้ว แน่นอนมันย่อมเป็นการดีสําหรับพวกเขา และอัลลอฮ.เป็นผู้ทรงอภัยโทษ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(49,6,'49:6','โอ้ศรัทธาชนทั้งหลาย ! หากคนชั่วนําข่าวใดๆ มาแจ้งแก่พวกเจ้า พวกเจ้าก็จงสอบสวนให้แน่ชัด หาไม่แล้วพวกเจ้าก็จะก่อเคราะห์กรรมแก่พวกหนึ่งโดยไม่รู้ตัว แล้วพวกเจ้าจะกลายเป็นผู้เสียใจในสิ่งที่พวกเจ้าได้กระทําไป');
INSERT INTO "king_fahad_thai" VALUES(49,7,'49:7','และพวกเจ้าพึงรู้เถิดว่า ในหมู่พวกเจ้านั้นมีร่อซูลของอัลลอฮฺอยู่ หากเขา(มุฮัมมัด) เชื่อฟังพวกเจ้าในส่วนใหญ่ของกิจการแล้ว แน่นอนพวกเจ้าก็จะลําบากกัน แต่อัลลอฮฺทรงให้การศรัทธาเป็นที่รักแก่พวกเจ้า และทรงให้การปฏิเสธศรัทธา และความชั่วช้าและการฝ่าฝืนเป็นที่น่าเกลียดชังแก่พวกเจ้า ชนเหล่านั้นคือพวกที่ดําเนินอยู่ในแนวทางที่ถูกต้อง');
INSERT INTO "king_fahad_thai" VALUES(49,8,'49:8','มันเป็นคุณธรรมและความโปรดปรานจากอัลลอฮฺ และอัลลอฮฺนั้นเป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(49,9,'49:9','และหากมีสองฝ่ายจากบรรดาผู้ศรัทธาทะเลาะวิวาทกัน พวกเจ้าก็จงไกล่เกลี่ยระหว่างทั้งสองฝ่าย หากฝ่ายหนึ่งในสองฝ่ายนั้นละเมิดอีกฝ่ายหนึ่ง พวกเจ้าก็จงปรามฝ่ายที่ละเมิดจนกว่าฝ่ายนั้นจะกลับสู่พระบัญชาของอัลลอฮฺ ฉะนั้นหากฝ่ายนั้นกลับ (สู่พระบัญชาของอัลลอฮฺ) แล้ว พวกเจ้าก็จงประนีประนอมระหว่างทั้งสองฝ่ายด้วยความยุติธรรม และพวกเจ้าจงให้ความเที่ยงธรรม(แก่ทั้งสองฝ่าย) เถิด แท้จริงอัลลอฮฺทรงรักใคร่บรรดาผู้ให้ความเที่ยงธรรม');
INSERT INTO "king_fahad_thai" VALUES(49,10,'49:10','แท้จริงบรรดาผู้ศรัทธานั้นเป็นพี่น้องกัน ดังนั้นพวกเจ้าจงไกล่เกลี่ยประนีประนอมกันระหว่างพี่น้องทั้งสองฝ่ายของพวกเจ้า และจงยําเกรงอัลลอฮฺเถิด หวังว่าพวกเจ้าจะได้รับความเมตตา');
INSERT INTO "king_fahad_thai" VALUES(49,11,'49:11','โอ้ศรัทธาชนทั้งหลาย! ชนกลุ่มหนึ่งอย่าได้เยาะเย้ยชนอีกกลุ่มหนึ่ง บางทีชนกลุ่มที่ถูกเยาะเย้ยนั้นจะดีกว่าชนกลุ่มที่เยาะเย้ย และสตรีกลุ่มหนึ่งอย่าได้เยาะเย้ยจะดีกว่ากลุ่มที่เยาะเย้ย และพวกเจ้าอย่าได้ตําหนิตัว ของพวกเจ้าเอง และอย่าได้เรียกกันด้วยฉายาที่ไม่ชอบ ช่างเลวทรามจริง ๆ ที่บรรดาผู้ศรัทธาจะเรียกกันว่าเป็นผู้ฝ่าฝืน ภายหลังจากที่ได้มีการศรัทธากันแล้ว และผู้ใดไม่สํานึกผิด ชนเหล่านั้นคือบรรดาผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(49,12,'49:12','โอ้ศรัทธาชนทั้งหลาย! พวกเจ้าจงปลีกตัวให้พ้นจากส่วนใหญ่ของการสงสัย แท้จริงการสงสัยบางอย่างนั้นเป็นบาป และพวกเจ้าอย่าสอดแนม และบางคนในหมู่พวกเจ้าอย่านินทาซึ่งกันและกัน คนหนึ่งในหมู่พวกเจ้านั้นชอบที่จะกินเนื้อพี่น้องของเขาที่ตายไปแล้วกระนั้นหรือ? พวกเจ้าย่อมเกลียดมัน และจงยําเกรงอัลลอฮฺเถิด แท้จริงอัลลอฮฺนั้นเป็นผู้ทรงอภัยโทษ ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(49,13,'49:13','โอ้มนุษยชาติทั้งหลาย แท้จริงเราได้สร้างพวกเจ้าจากเพศชาย และเพศหญิง และเราได้ให้พวกเจ้าแยกเป็นเผ่า และตระกูลเพื่อจะได้รู้จักกัน แท้จริงผู้ที่มีเกียรติยิ่งในหมู่พวกเจ้า ณ ที่อัลลอฮ.นั้น คือผู้ที่มีความยําเกรงยิ่งในหมู่พวกเจ้า แท้จริงอัลลอฮ.นั้นเป็นผู้ทรงรอบรู้อย่างละเอียดถี่ถ้วน');
INSERT INTO "king_fahad_thai" VALUES(49,14,'49:14','อาหรับชาวชนบทกล่าวว่า เราศรัทธาแล้ว จงกล่าวเถิดมุฮัมมัดว่า พวกท่านยังมิได้ศรัทธา แต่จงกล่าวเถิดว่า เราเข้ารับอิสลามแล้ว เพราะการศรัทธายังมิได้เข้าสู่หัวใจของพวกท่าน และถ้าหากพวกท่านเชื่อฟังปฏิบัติตามอัลลอฮฺและร่อซูลของพระองค์แล้ว พระองค์จะไม่ทําให้การงานของพวกท่านด้อยลงแต่ประการใด แท้จริงอัลลอฮฺเป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(49,15,'49:15','แท้จริงศรัทธาชนที่แท้จริงนั้น คือ บรรดาผู้ศรัทธาต่ออัลลอฮ. และร่อซูลของพระองค์ แล้วพวกเขาไม่สงสัยเคลือบแคลงใจ แต่พวกเขาได้เสียสละต่อสู้ดิ้นรนด้วยทรัพย์สมบัติของพวกเขา และชีวิตของพวกเขาไปในหนทางของอัลลอฮ. ชนเหล่านั้นแหละคือบรรดาผู้สัตย์จริง');
INSERT INTO "king_fahad_thai" VALUES(49,16,'49:16','จงกล่าวเถิดมุฮัมมัดว่า พวกท่านจะบอกอัลลอฮฺเกี่ยวกับศาสนาของพวกท่านกระนั้นหรือ? อัลลอฮฺนั้นทรงรอบรู้สิ่งที่อยู่ในชั้นฟ้าทั้งหลาย และแผ่นดิน และอัลลอฮฺนั้นทรงรอบรู้ทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(49,17,'49:17','พวกเขาถือเป็นบุญคุณแก่เจ้าว่าพวกเขาได้รับอิสลามแล้ว จงกล่าวเถิดมุฮัมมัดว่าพวกท่านอย่าถือเอาการเป็นอิสลามของพวกท่านมาเป็นบุญคุณแก่ฉันเลย แต่ทว่าอัลลอฮ.ทรงประทานบุญคุณแก่พวกท่านต่างหาก โดยชี้นําพวกท่านสู่การศรัทธา หากพวกท่านเป็นผู้สัตย์จริง');
INSERT INTO "king_fahad_thai" VALUES(49,18,'49:18','แท้จริงอัลลอฮฺทรงรอบรู้สิ่งเร้นลับในชั้นฟ้าทั้งหลายและแผ่นดิน และอัลลอฮฺทรงเห็นสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(50,1,'50:1','ก็อฟ ขอสาบานด้วยอัลกุรอานอันทรงเกียรติ');
INSERT INTO "king_fahad_thai" VALUES(50,2,'50:2','แต่ว่าพวกเขาประหลาดใจที่มีผู้ตักเตือนคนหนึ่งจากหมู่พวกเขามายังพวกเขา ดังนั้นพวกปฏิเสธศรัทธาจึงกล่าวว่า นี่มันเป็นเรื่องประหลาดจริง ๆ');
INSERT INTO "king_fahad_thai" VALUES(50,3,'50:3','เมื่อเราตายและกลายเป็นฝุ่นดินไปแล้ว จะกลับมีชีวิตอีกกระนั้นหรือ? นั่นเป็นการกลับที่ไกลเหลือเกิน');
INSERT INTO "king_fahad_thai" VALUES(50,4,'50:4','แน่นอนเรารู้ดีว่า กี่มากน้อยแล้วที่แผ่นดินทําให้พวกเขามีจํานวนลดน้อยลง และ ณ ที่เรานั้นมีบันทึกรักษาไว้');
INSERT INTO "king_fahad_thai" VALUES(50,5,'50:5','เปล่าเลย! พวกเขาปฏิเสธความจริงต่างหาก เมื่อมันได้มีมายังพวกเขา ดังนั้นพวกเขาจึงอยู่ในภาวะที่สับสน');
INSERT INTO "king_fahad_thai" VALUES(50,6,'50:6','พวกเขามิได้มองไปยังฟากฟ้าเหนือพวกเขาดอกหรือ ว่าเราได้สร้างมันและประดับมันไว้อย่างไร และมันไม่มีรอยร้าวหรือช่องโหว่เลย');
INSERT INTO "king_fahad_thai" VALUES(50,7,'50:7','และแผ่นดินนั้นเราได้แผ่ให้มันกว้างออกไปและในแผ่นดินเราได้ปักภูเขาไว้อย่างมั่นคง และในแผ่นดินนั้นเราให้พฤกษชาติทุกชนิดงอกเงยออกมาเป็นคู่ ๆ อย่างสวยงาม');
INSERT INTO "king_fahad_thai" VALUES(50,8,'50:8','เพื่อให้เป็นที่สังเกตและเป็นการเตือนให้รําลึกแก่บ่าวทุกคนผู้สํานึกผิด');
INSERT INTO "king_fahad_thai" VALUES(50,9,'50:9','และเราได้ให้น้ําฝนหลั่งลงมาจากฟากฟ้าเป็นที่จําเริญแล้วด้วยน้ํานั้น เราได้ให้งอกเงยออกมาเป็นสวนอันหลากหลาย และเมล็ดพืชสําหรับเก็บเกี่ยว');
INSERT INTO "king_fahad_thai" VALUES(50,10,'50:10','และต้นอินทผลัม อย่างสูงตระหง่าน ลําต้นของมันมีพวงย้อยลงมามีผลซ้อนกันเป็นตับ');
INSERT INTO "king_fahad_thai" VALUES(50,11,'50:11','เพื่อเป็นปัจจัยยังชีพแก่ปวงบ่าว และด้วยน้ํานั้นเราทําให้ดินแดนที่แห้งแล้งมีชีวิตชีวาขึ้นใหม่ เช่นนั้นแหละการฟื้นคืนชีพ');
INSERT INTO "king_fahad_thai" VALUES(50,12,'50:12','หมู่ชนของนูห.ได้ปฏิเสธมาก่อนหน้าพวกเขาแล้ว และชาวร็อส (บ่อน้ํา) และษะมูด');
INSERT INTO "king_fahad_thai" VALUES(50,13,'50:13','และอ๊าด และฟิรเอาน. และพี่น้องของลู๊ฎ');
INSERT INTO "king_fahad_thai" VALUES(50,14,'50:14','และชาวป่าทึบ และหมู่ชนของตุ๊บบะอ. พวกเหล่านั้นทั้งหมดได้ปฏิเสธบรรดาร่อซูล ดังนั้นสัญญาของเราจึงเหมาะสมคู่ควรแก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(50,15,'50:15','เราได้เหน็ดเหนื่อยต่อการสร้างครั้งแรกกระนั้นหรือ? เปล่าเลย! แต่ว่าพวกเขาอยู่ในการสงสัยต่อการสร้างครั้งใหม่ต่างหาก');
INSERT INTO "king_fahad_thai" VALUES(50,16,'50:16','และโดยแน่นอน เราได้บังเกิดมนุษย์มา และเรารู้ดียิ่งที่จิตใจของเขากระซิบกระซาบแก่เขา และเรานั้นใกล้ชิดเขายิ่งกว่าเส้นเลือดชีวิตของเขาเสียอีก');
INSERT INTO "king_fahad_thai" VALUES(50,17,'50:17','จงรําลึกขณะที่มาะลาอิกะฮ.ผู้บันทึกสองท่านบันทึก ท่านหนึ่งนั่งทางข้างขวา และอีกท่านหนึ่งนั่งทางข้างซ้าย');
INSERT INTO "king_fahad_thai" VALUES(50,18,'50:18','ไม่มีคําพูดคําใดที่เขากล่าวออกมา เว้นแต่ใกล้ ๆ เขานั้นมี (มะลัก) ผู้เฝ้าติดตาม ผู้เตรียมพร้อม (ที่จะบันทึก)');
INSERT INTO "king_fahad_thai" VALUES(50,19,'50:19','และอาการมึนงงแห่งความตายได้ปรากฏขึ้นอย่างประจักษ์แจ้ง และนั่นคือสิ่งที่เจ้าจะหลีกเลี่ยงจากมันไปไม่ได้');
INSERT INTO "king_fahad_thai" VALUES(50,20,'50:20','และสังข์จะถูกเป่าขึ้น นั่นคือวันแห่งการสัญญา');
INSERT INTO "king_fahad_thai" VALUES(50,21,'50:21','และทุก ๆชีวิตจะมาพร้อมกับเขาคือมะลักผู้นําทาง และมะลักผู้เป็นพยาน');
INSERT INTO "king_fahad_thai" VALUES(50,22,'50:22','โดยแน่นอนเจ้าไม่สนใจต่อเรื่องนี้ นั้นเราจึงเปิดสิ่งที่ปกคลุมเจ้าอยู่ให้ออกไปจากเจ้า วันนี้สายตาของเจ้าจึงเฉียบขาด');
INSERT INTO "king_fahad_thai" VALUES(50,23,'50:23','มะลักผู้ถูกมอบหมายกล่าวว่า นี่คือสิ่งที่เตรียมไว้อยู่ที่ข้าพระองค์');
INSERT INTO "king_fahad_thai" VALUES(50,24,'50:24','เจ้าทั้งสองจงโยนทุกคนที่ปฏิเสธศรัทธา และดื้อรั้น');
INSERT INTO "king_fahad_thai" VALUES(50,25,'50:25','ผู้ขัดขวางการทําดี ผู้ฝ่าฝืน ผู้สงสัย');
INSERT INTO "king_fahad_thai" VALUES(50,26,'50:26','ซึ่งตั้งพระเจ้าอื่นใดคู่เคียงกับอัลลอฮ. ดังนั้นเจ้าทั้งสองจงโยนเขาสู่การลงโทษอันสาหัส');
INSERT INTO "king_fahad_thai" VALUES(50,27,'50:27','(ชัยฏอน) สหายของเขากล่าวว่า ข้าแต่พระเจ้าของเรา ข้าพระองค์มิได้ทําให้เขาหลงผิดดอกแต่ทว่าเขาได้อยู่ในการหลงผิดมาก่อนแล้ว');
INSERT INTO "king_fahad_thai" VALUES(50,28,'50:28','พระองค์ตรัสว่า พวกเจ้าอย่ามาโต้เถียงต่อหน้าข้าเลย และแน่นอนข้าได้เตือนพวกเจ้าก่อนหน้านี้แล้ว');
INSERT INTO "king_fahad_thai" VALUES(50,29,'50:29','พระดํารัสของข้า จะไม่ถูกเปลี่ยนแปลง ณ บัดนี้ และข้าก็มิได้เป็นผู้ยุติธรรมต่อปวงบ่าวของข้าเลย');
INSERT INTO "king_fahad_thai" VALUES(50,30,'50:30','วันซื่งเราจะกล่าวแก่นรกกญะฮันนัมว่า เจ้าเต็มแล้วหรือ? และมันจะกล่าวว่า ยังมีเพิ่มอีกไหม?');
INSERT INTO "king_fahad_thai" VALUES(50,31,'50:31','และสวนสวรรค์ก็ถูกนําให้มาใกล้แก่บรรดาผู้ยําเกรงมันมิได้อยู่ไกลเลย');
INSERT INTO "king_fahad_thai" VALUES(50,32,'50:32','นี่คือสิ่งที่พวกเจ้าได้ถูกสัญญาไว้สําหรับทุกคนที่สํานึกผิด (หันหน้าเข้าหาอัลลอฮ.) ผู้รักษาบัญญัติ (ของอัลลอฮ.)');
INSERT INTO "king_fahad_thai" VALUES(50,33,'50:33','ผู้ที่เกรงกลัวพระผู้ทรงกรุณาปรานีโดยทางลับ และมาหา (พระองค์) ด้วยจิตใจที่สํานึกผิดกลับเนื้อกลับตัว');
INSERT INTO "king_fahad_thai" VALUES(50,34,'50:34','พวกเจ้าจงเข้าไปในสวนสวรรค์ด้วยความศานติ นั่นคือวันแห่งการพํานักอยู่ตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(50,35,'50:35','สําหรับพวกเขาจะได้รับสิ่งที่พวกเขาพึงประสงค์ในสวนสวรรค์ และ ณที่เรานั้นยังมีอีกมากมาย');
INSERT INTO "king_fahad_thai" VALUES(50,36,'50:36','เราได้ทําลายผู้คนไปกี่มากน้อยแล้วก่อนหน้าพวกเขาทั้ง ๆ ที่เขาทั้งหลายมีความเข้มแข็งทางสมรรถภาพกว่าพวกเจ้า แล้วพวกเขาได้ออกเดินทางไปตามเมืองต่าง ๆ (พวกเขาเหล่านั้น) มีทางหนีรอดหรือ?');
INSERT INTO "king_fahad_thai" VALUES(50,37,'50:37','แท้จริงในการนั้น แน่นอนย่อมเป็นข้อตักเตือนแก่ผู้มีหัวใจ หรือรับฟังโดยที่เขามีความตั้งใจจริง');
INSERT INTO "king_fahad_thai" VALUES(50,38,'50:38','และโดนแน่นอน เราได้สร้างชั้นฟ้าทั้งหลายและแผ่นดิน และสิ่งที่อยู่ระหว่างมันทั้งสองในเวลา 6 วัน และไม่มีความเหน็ดเหนื่อยใด ๆ มาสัมผัสเรา');
INSERT INTO "king_fahad_thai" VALUES(50,39,'50:39','ดังนั้น เจ้าจงอดทนต่อสิ่งที่พวกเขากล่าวถึง และจงแซ่ซ้องด้วยการสรรเสริญพระเจ้าของเจ้าก่อนการขึ้นของดวงอาทิตย์และก่อนการตก (ของมัน)');
INSERT INTO "king_fahad_thai" VALUES(50,40,'50:40','และระหว่างเวลากลางคืน ก็จงสดุดีพระองค์ด้วย และหลังเวลาสุญูด');
INSERT INTO "king_fahad_thai" VALUES(50,41,'50:41','และเจ้าจงฟังวันที่ผู้เรียกร้องจะร้องเรียกจากสถานที่ใกล้ ๆ');
INSERT INTO "king_fahad_thai" VALUES(50,42,'50:42','วันที่พวกเขาจะได้ยินเสียงเป่าสังข์อย่างแท้จริง นั่นคือวันแห่งการฟื้นคืนชีพ');
INSERT INTO "king_fahad_thai" VALUES(50,43,'50:43','แท้จริงเรานั้นเป็นผู้ให้มีชีวิตขึ้น และเราเป็นผู้ให้(เขา)ตาย และยังเราคือการกลับไป');
INSERT INTO "king_fahad_thai" VALUES(50,44,'50:44','วันที่แผ่นดินจะแยกออกจากพวกเขา (และพวกเขาจะ)ออกมาอย่างรีบเร่ง นั่นคือการชุมนุมที่ง่ายดายแก่เรา');
INSERT INTO "king_fahad_thai" VALUES(50,45,'50:45','เรารู้ดียิ่งถึงสิ่งที่พวกเขากล่าว และเจ้ามิได้เป็นผู้มีอํานาจเหนือพวกเขา ดังนั้นเจ้าจงตักเตือนด้วยอัลกุรอ่านนี้แก่ผู้กลัวต่อสัญญาร้ายของข้า');
INSERT INTO "king_fahad_thai" VALUES(51,1,'51:1','ขอสาบานต่อลมที่พัด (ฝุ่นให้) กระจัดกระจายอย่างปลิวว่อน');
INSERT INTO "king_fahad_thai" VALUES(51,2,'51:2','ขอสาบานต่อเมฆที่พยุง (ฝน) อย่างหนัก');
INSERT INTO "king_fahad_thai" VALUES(51,3,'51:3','ขอสาบานต่อนาวา ที่แล่นไปอย่างสะดวกสบาย');
INSERT INTO "king_fahad_thai" VALUES(51,4,'51:4','ขอสาบานต่อมะลาอิกะฮ. ผู้จัดสรรการงาน');
INSERT INTO "king_fahad_thai" VALUES(51,5,'51:5','แท้จริงสิ่งที่พวกเจ้าถูกสัญญาไว้นั้นเป็นความจริงอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(51,6,'51:6','และแท้จริงการตอบแทนจะเกิดขึ้นอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(51,7,'51:7','ขอสาบานต่อฟากฟ้าที่มีวิถีทางโคจรอย่างมากมาย');
INSERT INTO "king_fahad_thai" VALUES(51,8,'51:8','แท้จริงพวกเจ้าอยู่ในคําพูดที่ขัดแย้งกัน');
INSERT INTO "king_fahad_thai" VALUES(51,9,'51:9','ผู้ที่หันเหออกจากสัจธรรมนั้นเขาจะถูกให้หันเหออกจากการศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(51,10,'51:10','ผู้ที่กล่าวเท็จแก่ท่านนะบี จะถูกสาปแช่ง');
INSERT INTO "king_fahad_thai" VALUES(51,11,'51:11','คือบรรดาผู้ที่พวกเขาอยู่ในการสับสนหลงลืม (เรื่องของวันอาคิเราะฮ.)');
INSERT INTO "king_fahad_thai" VALUES(51,12,'51:12','พวกเขาจะถามว่า วันแห่งการตอบแทนจะมีขึ้นเมื่อใด?');
INSERT INTO "king_fahad_thai" VALUES(51,13,'51:13','วันที่พวกเขาจะถูกทดสอบ ด้วยไฟนรก');
INSERT INTO "king_fahad_thai" VALUES(51,14,'51:14','พวกเจ้าจงลิ้มรสการทดสอบของพวกเจ้าเถิด นี่คือสิ่งที่พวกเจ้าเร่งเร้ามัน');
INSERT INTO "king_fahad_thai" VALUES(51,15,'51:15','แท้จริงบรรดาผู้ยําเกรงจะได้อยู่ในสวนสวรรค์มากหลาย และตาน้ําพุ');
INSERT INTO "king_fahad_thai" VALUES(51,16,'51:16','พวกเขาปิติยินดีในสิ่งที่พระเจ้าของพวกเขาได้ประทานให้แก่พวกเขา แท้จริงพวกเขาก่อนหน้านั้นเป็นผู้กระทําความดี');
INSERT INTO "king_fahad_thai" VALUES(51,17,'51:17','พวกเขาเคยหลับนอนแต่เพียงส่วนน้อยของเวลากลางคืน');
INSERT INTO "king_fahad_thai" VALUES(51,18,'51:18','และในยามรุ่งสางพวกเขาขออภัยโทษ(ต่อพระองค์)');
INSERT INTO "king_fahad_thai" VALUES(51,19,'51:19','และในทรัพย์สมบัติของพวกเขาจัดไว้เป็นส่วนของผู้เอ่ยขอ และผู้ไม่เอ่ยขอ');
INSERT INTO "king_fahad_thai" VALUES(51,20,'51:20','และในแผ่นดินนี้มีสัญญาณต่าง ๆ สําหรับผู้ศรัทธาเชื่อมั่น');
INSERT INTO "king_fahad_thai" VALUES(51,21,'51:21','และในตัวของพวกเจ้าเอง พวกเจ้าไม่เห็นดอกหรือ?');
INSERT INTO "king_fahad_thai" VALUES(51,22,'51:22','และในฟากฟ้ามีปัจจัยยังชีพของพวกเจ้า และสิ่งที่พวกเจ้าถูกสัญญาไว้');
INSERT INTO "king_fahad_thai" VALUES(51,23,'51:23','ดังนั้น จึงขอสาบานต่อพระเจ้าแห่งชั้นฟ้า และแผ่นดินนี้ว่า แท้จริง (สิ่งที่ถูกสัญญาไว้นั้น) เป็นความจริงอย่างแน่นอน เสมือนกับที่พวกเจ้าสนทนากัน');
INSERT INTO "king_fahad_thai" VALUES(51,24,'51:24','เรื่องราวของแขกผู้มีเกียรติของอิบรอฮีมได้มาถึงเจ้าบ้างไหม?');
INSERT INTO "king_fahad_thai" VALUES(51,25,'51:25','เมื่อพวกเขาได้เข้ามาหาเขา (อิบรอฮีม) พวกเขากล่าวว่า ศานติ เขากล่าวว่า(ตอบ) ว่าศานติ (พวกท่านเป็น)หมู่ชนผู้แปลกหน้า');
INSERT INTO "king_fahad_thai" VALUES(51,26,'51:26','แล้วเขาก็รีบเข้าไปหาครอบครัวของเขา แล้วได้นําลูกวัวอ้วน (ซึ่งย่างเสร็จแล้ว)ออกมา');
INSERT INTO "king_fahad_thai" VALUES(51,27,'51:27','และได้วางมันไว้ข้างหน้าพวกเขา เขากล่าวว่า พวกท่านไม่กินหรือ?');
INSERT INTO "king_fahad_thai" VALUES(51,28,'51:28','(เมื่อพวกเขาไม่กินอาหาร) ดังนั้น เขารู้สึกกลัวพวกเขา พวกเขากล่าวว่า อย่ากลัวเลยและได้แจ้งข่าวดีแก่เขาเกี่ยวกับลูกคนหนึ่งที่มีความรู้');
INSERT INTO "king_fahad_thai" VALUES(51,29,'51:29','แล้วภริยาของเขาก็ได้ออกมาตะโกนพลางตบหน้าของนาง แล้วกล่าวว่า “หญิงแก่เป็นหมัน”');
INSERT INTO "king_fahad_thai" VALUES(51,30,'51:30','พวกเขากล่าวว่า เช่นนั้นแหละพระเจ้าของเธอได้ตรัสไว้เช่นนั้น แท้จริงพระองค์เป็นผู้ทรงปรีชาญาณ ผู้ทรงรอบรู้เสมอ');
INSERT INTO "king_fahad_thai" VALUES(51,31,'51:31','เขากล่าวว่า ดังนั้นความมุ่งหมายของพวกท่านคืออะไรเล่า โอ้บรรดาทูตเอ๋ย?');
INSERT INTO "king_fahad_thai" VALUES(51,32,'51:32','พวกเขากล่าวว่า แท้จริงเราถูกส่งมายังหมู่ชน ผู้กระทําผิด');
INSERT INTO "king_fahad_thai" VALUES(51,33,'51:33','เพื่อเราจะได้โยนก้อนหินทําด้วยดินเหนียวแข็งลงบนพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(51,34,'51:34','ถูกตราเป็นเครื่องหมายไว้แล้ว ณ ที่พระเจ้าของเจ้าสําหรับพวกที่ละเมิดขอบเขต');
INSERT INTO "king_fahad_thai" VALUES(51,35,'51:35','ดังนั้น เราได้นําผู้ที่อยู่ในเมืองนั้นจากหมู่ ผู้ศรัทธาออกมาให้พ้น');
INSERT INTO "king_fahad_thai" VALUES(51,36,'51:36','และเราไม่พบผู้ใดในเมืองนั้น นอกจากบ้านหลังหนึ่งของปวงผู้นอบน้อม');
INSERT INTO "king_fahad_thai" VALUES(51,37,'51:37','และเราได้เหลือสัญญาณหนึ่งไว้ สําหรับบรรดาผู้ที่กลัวต่อการลงโทษอันเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(51,38,'51:38','และในเรื่องของมูซา เมื่อเราส่งเขาไปยังฟิร เอาน. พร้อมด้วยหลักฐานอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(51,39,'51:39','แต่ฟิรเอานฺได้ผินหลังออกไปพร้อมกับบริวารของเขา แล้วกล่าว (ถึงการทําหน้าที่ของมูซา) ว่า นักเล่นกลหรือคนบ้า');
INSERT INTO "king_fahad_thai" VALUES(51,40,'51:40','ดังนั้น เราได้เอาเขามา และไพร่พลของเขา แล้วเราได้โยนพวกเขาลงไปในทะเลและตัวเขาก็ถูกประณาม');
INSERT INTO "king_fahad_thai" VALUES(51,41,'51:41','และในเรื่องของอ๊าด เมื่อเราได้ส่งลมพายุที่ทําลายล้างมายังพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(51,42,'51:42','มันมิได้เหลืออะไรทิ้งไว้เลย เมื่อมันได้พัดกระหน่ํามา นอกจากนี้จะทําให้สิ่งนั้นพินาศย่อยยับ');
INSERT INTO "king_fahad_thai" VALUES(51,43,'51:43','และในเรื่องของษะมูด เมื่อมีผู้กล่าวแก่พวกเขาว่า พวกท่านจงสนุกร่าเริงไปชั่วขณะหนึ่งเถิด');
INSERT INTO "king_fahad_thai" VALUES(51,44,'51:44','แต่พวกเขาได้ท้าทายโอหังต่อพระบัญชาของพระเจ้าของพวกเขา ดังนั้นเสียงกัมปนาทก็ได้คร่าชีวิตพวกเขาขณะที่พวกเขาจ้องมองดูอยู่');
INSERT INTO "king_fahad_thai" VALUES(51,45,'51:45','แล้วพวกเขาไม่สามารถจะลุกขึ้นยืนได้ และพวกเขาก็ไม่สามารถช่วยเหลือตัวเองได้');
INSERT INTO "king_fahad_thai" VALUES(51,46,'51:46','และหมู่ชนของนูห.ก่อนหน้านี้ แท้จริงพวกเขาเป็นหมู่ชนที่ฝ่าฝืน');
INSERT INTO "king_fahad_thai" VALUES(51,47,'51:47','และชั้นฟ้า เราได้สร้างมันด้วยพระหัตถ์ของเรา และแท้จริงเราได้แผ่ให้กว้างไพศาล');
INSERT INTO "king_fahad_thai" VALUES(51,48,'51:48','และแผ่นดินนั้น เราได้แผ่ขยายมันออกไป ดังนั้นเราเป็นผู้แผ่ขยายที่ยอดเยี่ยม');
INSERT INTO "king_fahad_thai" VALUES(51,49,'51:49','และจากทุก ๆ สิ่งนั้น เราได้สร้าง (มัน) ขึ้นเป็นคู่ ๆ เพื่อพวกเจ้าจะได้ใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(51,50,'51:50','ดังนั้นพวกท่านจงเร่งรีบไปหาอัลลอฮ. แท้จริง ฉันเป็นผู้ตักเตือนอย่างเปิดเผยจากพระองค์แก่พวกท่าน');
INSERT INTO "king_fahad_thai" VALUES(51,51,'51:51','และพวกท่านอย่าตั้งพระเจ้าอื่นใดเป็นภาคีกับอัลลอฮ. แท้จริง ฉันเป็นผู้ตักเตือนอย่างเปิดเผยจากพระองค์แก่พวกท่าน');
INSERT INTO "king_fahad_thai" VALUES(51,52,'51:52','เช่นนั้นแหละ ไม่มีร่อซูลคนใดมายังบรรดา (หมู่ชน) ก่อนหน้าพวกเขา เว้นแต่พวกเขากล่าวว่าเป็นนักเล่นกลหรือคนบ้า');
INSERT INTO "king_fahad_thai" VALUES(51,53,'51:53','พวกเขาได้สั่งเสียในเรื่องนี้แก่กันกระนั้นหรือ? เปล่าเลย! แต่ว่าพวกเขาเป็นหมู่ชนผู้ละเมิดเกินขอบเขต');
INSERT INTO "king_fahad_thai" VALUES(51,54,'51:54','ดังนั้น เจ้าจงผินหลังออกจากพวกเขาเถิด แล้ว เจ้าจะไม่เป็นผู้ถูกตําหนิ');
INSERT INTO "king_fahad_thai" VALUES(51,55,'51:55','และจงตักเตือนเถิด เพราะแท้จริงการตักเตือนนั้นจะให้ประโยชน์แก่บรรดาผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(51,56,'51:56','และข้ามิได้สร้างญิน และมนุษย์เพื่ออื่นใด เว้นแต่เพื่อเคารพภักดีต่อข้า');
INSERT INTO "king_fahad_thai" VALUES(51,57,'51:57','ข้าไม่ต้องการปัจจัยยังชีพจากพวกเขา และข้าก็ไม่ต้องการให้พวกเขาให้อาหารแก่ข้า');
INSERT INTO "king_fahad_thai" VALUES(51,58,'51:58','แท้จริงอัลลอฮฺ คือผู้ประทานปัจจัยยังชีพอันมากหลาย ผู้ทรงพลัง ผู้ทรงมั่นคง');
INSERT INTO "king_fahad_thai" VALUES(51,59,'51:59','ดังนั้นแท้จริง สําหรับบรรดาผู้ประพฤติผิดนั้น เขาจะได้รับส่วนของการลงโทษเยี่ยงส่วนการลงโทษพวกเพื่อน ๆ ของพวกเขา ดังนั้นพวกเขาอย่าได้รีบเร่งให้ข้า (ลงโทษ) เลย');
INSERT INTO "king_fahad_thai" VALUES(51,60,'51:60','ดังนั้น ความหายนะจะประสบแด่ บรรดาผู้ปฏิเสธศรัทธาในวันของพวกเขาซึ่ง ได้ถูกสัญญาไว้');
INSERT INTO "king_fahad_thai" VALUES(52,1,'52:1','ขอสาบานต่อภูเขาอัฏฏูร');
INSERT INTO "king_fahad_thai" VALUES(52,2,'52:2','ขอสาบานต่อคัมภีร์ ที่ถูกจารึกไว้');
INSERT INTO "king_fahad_thai" VALUES(52,3,'52:3','ในม้วนกระดาษหรือหนังที่กางแผ่');
INSERT INTO "king_fahad_thai" VALUES(52,4,'52:4','ขอสาบานต่ออาคารที่ถูกเยือนอย่างสม่ําเสมอ');
INSERT INTO "king_fahad_thai" VALUES(52,5,'52:5','ขอสาบานต่อท้องฟ้าที่อยู่เบื้องสูง');
INSERT INTO "king_fahad_thai" VALUES(52,6,'52:6','ขอสาบานต่อทะเลที่ลุกโชติช่วง (ในวันกิยามะฮ.)');
INSERT INTO "king_fahad_thai" VALUES(52,7,'52:7','แท้จริงการลงโทษของพระเจ้าของเจ้าจะเกิดขึ้นอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(52,8,'52:8','ไม่มีผู้ใดจะเบี่ยงเบนมันได้');
INSERT INTO "king_fahad_thai" VALUES(52,9,'52:9','วันที่ชั้นฟ้าจะสั่นสะเทือนอย่างชุลมุนวุ่นวาย');
INSERT INTO "king_fahad_thai" VALUES(52,10,'52:10','และเทือกเขาจะปลิวว่อนกระจัดกระจาย');
INSERT INTO "king_fahad_thai" VALUES(52,11,'52:11','ดังนั้นความหายนะในวันนั้นจงประสบแก่บรรดาผู้ปฏิเสธ');
INSERT INTO "king_fahad_thai" VALUES(52,12,'52:12','บรรดาที่พวกเขาอยู่ในการมั่วสุมการละเล่นอย่างไร้สาระ');
INSERT INTO "king_fahad_thai" VALUES(52,13,'52:13','วันที่พวกเขาจะถูกผลักลงนรกญะฮันนัมอย่างผลักใสไล่ส่ง');
INSERT INTO "king_fahad_thai" VALUES(52,14,'52:14','นี่คือไฟนรก ซึ่งพวกเจ้าเคยปฏิเสธมัน');
INSERT INTO "king_fahad_thai" VALUES(52,15,'52:15','นี่คือเล่ห์กลหรือ หรือว่าพวกเจ้ายังมองไม่เห็น?');
INSERT INTO "king_fahad_thai" VALUES(52,16,'52:16','จงเข้าไปเผาไหม้ในนั้น พวกเจ้าจะทนได้หรือทนไม่ได้ก็เท่าเทียมกันสําหรับพวกเจ้า แท้จริงพวกเจ้านั้นจะถูกตอบแทนตามที่พวกเจ้าได้กระทําไว้เท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(52,17,'52:17','แท้จริงบรรดาผู้ยําเกรงนั้นจะได้อยู่ในสวนสวรรค์ และความสุขสําราญ');
INSERT INTO "king_fahad_thai" VALUES(52,18,'52:18','ได้รับความสุขอันล้นพ้นตามที่พระเจ้าของพวกเขาได้ประทานให้แก่พวกเขา และพระเจ้าของพวกเขาจะคุ้มครองพวกเขาให้พ้นจากการลงโทษของเปลวไฟ');
INSERT INTO "king_fahad_thai" VALUES(52,19,'52:19','พวกเจ้าจงกิน จงดื่มอย่างสบายใจตามที่พวกเจ้าได้กระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(52,20,'52:20','นอนเอกเขนกอยู่บนเตียงเรียงชิดติดกัน และเราให้พวกเขามีคู่ครองเป็นหญิงสาวสวยดวงตาโตคม');
INSERT INTO "king_fahad_thai" VALUES(52,21,'52:21','และบรรดาผู้ศรัทธา บรรดาลูกหลานของพวกเขาจะดําเนินตามพวกเขาด้วยการศรัทธา เราจะให้ลูกหลานของพวกเขาอยู่ร่วมกับพวกเขา และเราจะไม่ให้การงานของพวกเขาลดหย่อนลงจากพวกเขาแต่อย่างใดแต่ละคนย่อมได้รับการค้ําประกันในสิ่งที่เขาขวนขวายไว้');
INSERT INTO "king_fahad_thai" VALUES(52,22,'52:22','และเราจะเพิ่มพูนให้แก่พวกเขา ซึ่งผลไม้และเนื้อตามที่พวกเขาต้องการ');
INSERT INTO "king_fahad_thai" VALUES(52,23,'52:23','พวกเขาจะแลกเปลี่ยนถ้วยแก้วกันในสวรรค์ จะไม่มีการพูดจาตลกคะนอง และไม่มีการทําบาป');
INSERT INTO "king_fahad_thai" VALUES(52,24,'52:24','มีเด็กวัยรุ่นของพวกเขาวนเวียนรับใช้พวกเขา เสมือนว่าพวกเขา เช่นไข่มุกถูกปกปิดไว้');
INSERT INTO "king_fahad_thai" VALUES(52,25,'52:25','และบางคนในหมู่พวกเขาจะหันหน้าเข้าหากัน สอบถามซึ่งกันและกัน');
INSERT INTO "king_fahad_thai" VALUES(52,26,'52:26','พวกเขากล่าวว่า แท้จริง แต่ก่อนนี้ (ในโลกดุนยา) พวกเราอยู่กับครอบครัวของเราเป็นผู้มีความวิตกกังวัล');
INSERT INTO "king_fahad_thai" VALUES(52,27,'52:27','ดังนั้นอัลลอฮ.ได้ทรงโปรดปรานแก่เราและได้ทรงปกป้องเราให้พ้นจากการลงโทษแห่งลมร้อน');
INSERT INTO "king_fahad_thai" VALUES(52,28,'52:28','แท้จริง ก่อนหน้านี้เราได้วิงวอนต่อพระองค์ แท้จริงพระองค์นั้นเป็นผู้ทรงเอื้อเฟื้อเผื่อแผ่ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(52,29,'52:29','ดังนั้น จงกล่าวเตือน (พวกเขาเถิด) เนื่องเพราะความโปรดปรานแห่งพระเจ้าของเจ้ามิได้เป็นโหร และมิได้เป็นบ้า');
INSERT INTO "king_fahad_thai" VALUES(52,30,'52:30','หรือพวกเขากล่าวว่า มุฮัมมัดเป็นกวี เราคอยภัยพิบัติที่จะเกิดขึ้นแก่เขา');
INSERT INTO "king_fahad_thai" VALUES(52,31,'52:31','จงกล่าวเถิดมุฮัมมัด พวกท่านจงคอยเถิด แท้จริงฉันก็จะอยู่ในหมู่ผู้คอยร่วมกับพวกท่าน');
INSERT INTO "king_fahad_thai" VALUES(52,32,'52:32','หรือว่าสติปัญญาของพวกเขาใช้พวกเขาให้เชื่อเช่นนั้น หรือว่าพวกเขาเป็นหมู่ชนผู้ฝ่าฝืน');
INSERT INTO "king_fahad_thai" VALUES(52,33,'52:33','หรือพวกเขากล่าวว่า เขาได้เสกสรรปั้นแต่งอัลกุรอานขึ้นเอง เปล่าดอกพวกเขาไม่ศรัทธาต่างหาก');
INSERT INTO "king_fahad_thai" VALUES(52,34,'52:34','ดังนั้น พวกเขาจงนําคํากล่าวเช่นเดียวกันนี้มา หากพวกเขาเป็นผู้สัตย์จริง');
INSERT INTO "king_fahad_thai" VALUES(52,35,'52:35','หรือว่าพวกเขาถูกบังเกิดมาโดยไม่มีผู้ให้บังเกิด หรือว่าพวกเขาเป็นผู้ให้บังเกิดตนเอง');
INSERT INTO "king_fahad_thai" VALUES(52,36,'52:36','หรือว่าพวกเขาเป็นผู้สร้างชั้นฟ้าทั้งหลาย และแผ่นดินนี้ เปล่าเลย เพราะพวกเขาไม่เชื่อมั่นต่างหาก');
INSERT INTO "king_fahad_thai" VALUES(52,37,'52:37','หรือว่าพวกเขามีขุมทรัพย์แห่งพระเจ้าของเจ้า หรือว่าพวกเขาเป็นผู้มีอํานาจจัดการ');
INSERT INTO "king_fahad_thai" VALUES(52,38,'52:38','หรือว่าพวกเขามีบันไดเพื่อพวกเขาจะได้ฟังเรื่องราวต่าง ๆ ในชั้นฟ้า ดังนั้นก็จงให้คนที่ฟังในหมู่พวกเขานําหลักฐานอันชัดแจ้งมา');
INSERT INTO "king_fahad_thai" VALUES(52,39,'52:39','หรือว่าพระองค์ทรงมีบุตรหญิงหลายคน และพวกเจ้ามีบุตรชายหลายคน');
INSERT INTO "king_fahad_thai" VALUES(52,40,'52:40','หรือว่าเจ้าขอค่าจ้างจากพวกเขา ดังนั้นพวกเขา จึงมีภาระหนี้อันหนัก');
INSERT INTO "king_fahad_thai" VALUES(52,41,'52:41','หรือว่าที่พวกเขามีสิ่งเร้นลับ แล้วพวกเขาก็บันทึกไว้');
INSERT INTO "king_fahad_thai" VALUES(52,42,'52:42','หรือว่าพวกเขาต้องการที่จะวางแผนร้าย แต่ว่าบรรดาผู้ปฏิเสธศรัทธานั้น พวกเขาต่างหากที่จะถูกวางแผนร้าย');
INSERT INTO "king_fahad_thai" VALUES(52,43,'52:43','หรือว่าพวกเขามีพระเจ้าอื่นจากอัลลอฮ.จากสิ่งที่พวกเขาตั้งภาคี');
INSERT INTO "king_fahad_thai" VALUES(52,44,'52:44','ครั้นเมื่อพวกเขาเห็นเศษชิ้นหนึ่งร่วงหล่นลงมาจากฟากฟ้า พวกเขาก็กล่าวว่า มันเป็นเพียงก้อนเมฆรวมตัว');
INSERT INTO "king_fahad_thai" VALUES(52,45,'52:45','ดังนั้นจงปล่อยพวกเขาเถิด จนกว่าพวกเขาจะพบกับวันของพวกเขา ซึ่งในวันนั้นพวกเขาจะถูกทําให้เป็นลมหมดสติ');
INSERT INTO "king_fahad_thai" VALUES(52,46,'52:46','วันซึ่งแผนการณ์ร้ายของพวกเขาจะไม่อํานวยประโยชน์อันใดแก่พวกเขา และพวกเขาก็จะไม่ได้รับการช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(52,47,'52:47','และแท้จริงสําหรับบรรดาผู้อธรรมนั้นจะได้รับการลงโทษอื่นจากนั้นอีก แต่ว่าส่วนมากของพวกเขาไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(52,48,'52:48','ดังนั้น จงอดทนต่อพระบัญชาของพระเจ้าของเจ้า เพราะแท้จริง เจ้านั้นอยู่ในเบื้องสายตาของเรา และจงแซ่ซ้องสดุดีด้วยการสรรเสริญพระเจ้าของเจ้าขณะเมื่อเจ้าลุกขึ้นยืน');
INSERT INTO "king_fahad_thai" VALUES(52,49,'52:49','และส่วนหนึ่งของกลางคืน ก็จงแซ่ซ้องสดุดีพระองค์ และเมื่อยามคล้องลับของหมู่ดวงดาว');
INSERT INTO "king_fahad_thai" VALUES(53,1,'53:1','ขอสาบานด้วยดวงดาวเมื่อมันคล้อยตกลงมา');
INSERT INTO "king_fahad_thai" VALUES(53,2,'53:2','สหาย(มุฮัมมัด) ของพวกเจ้ามิได้หลงผิดและเชื่อมั่นในทางที่ผิด');
INSERT INTO "king_fahad_thai" VALUES(53,3,'53:3','และเขามิได้พูดตามอารมณ์');
INSERT INTO "king_fahad_thai" VALUES(53,4,'53:4','อัลกุรอานมิใช่อื่นใดนอกจากเป็นวะฮีย.ที่ถูกประทานลงมา');
INSERT INTO "king_fahad_thai" VALUES(53,5,'53:5','ผู้ทรงพลังอํานาจอันมากมาย (ญิบรีล) ได้สอนเขา');
INSERT INTO "king_fahad_thai" VALUES(53,6,'53:6','ผู้ทรงพลังอันแข็งแรง ดังนั้นเขาจึงปรากฎในสภาพที่แท้จริง');
INSERT INTO "king_fahad_thai" VALUES(53,7,'53:7','ขณะที่เขาอยู่บนขอบฟ้าอันสูงส่ง');
INSERT INTO "king_fahad_thai" VALUES(53,8,'53:8','แล้วเขาได้เข้ามาใกล้ และเข้ามาใกล้จนชิด');
INSERT INTO "king_fahad_thai" VALUES(53,9,'53:9','เขาเข้ามาใกล้ (จนอยู่) ในระยะของปลายคันธนูทั้งสอง หรือใกล้กว่านั้นอีก');
INSERT INTO "king_fahad_thai" VALUES(53,10,'53:10','ดังนั้นเขา (ญิบรีล) จึงนําวะฮียฺมาให้แก่บ่าวของพระองค์ (มุฮัมมัด) สิ่งที่เขาจะนําวะฮียฺมา');
INSERT INTO "king_fahad_thai" VALUES(53,11,'53:11','จิตใจ (ของมุฮัมมัด) มิได้ปฏิเสธสิ่งที่เขาได้เห็น');
INSERT INTO "king_fahad_thai" VALUES(53,12,'53:12','แล้วพวกเจ้าจะโต้เถียงกับเขาเกี่ยวกับสิ่งที่เขาได้เห็นอีกหรือ');
INSERT INTO "king_fahad_thai" VALUES(53,13,'53:13','และโดยแน่นอน เขาได้เห็นญิบรีลในการลงมาอีกครั้งหนึ่ง');
INSERT INTO "king_fahad_thai" VALUES(53,14,'53:14','ณ ที่ต้นพุทธาอันไกลโพ้น');
INSERT INTO "king_fahad_thai" VALUES(53,15,'53:15','ณ ที่นั้น คือสวนสวรรค์อันเป็นที่พํานัก');
INSERT INTO "king_fahad_thai" VALUES(53,16,'53:16','(ขณะนั้น) สิ่งที่ปกคลุม (แสงประกาย) ได้ปกคลุมต้นพุทรา');
INSERT INTO "king_fahad_thai" VALUES(53,17,'53:17','สายตา (ของมุฮัมมัด) มิได้เหลือบแลไปทางอื่น และมิได้ล่วงเกินไป');
INSERT INTO "king_fahad_thai" VALUES(53,18,'53:18','โดยแน่นอนเขาได้เห็นสัญญาณอันยิ่งใหญ่ของพระเจ้าของเขา');
INSERT INTO "king_fahad_thai" VALUES(53,19,'53:19','แล้วพวกเจ้ามิได้เห็นอัลลาต และอัลอุซซา');
INSERT INTO "king_fahad_thai" VALUES(53,20,'53:20','และตัวอื่นคือตัวที่สาม, มะนาต ดอกหรือ?');
INSERT INTO "king_fahad_thai" VALUES(53,21,'53:21','สําหรับพวกเจ้ามีเพศชาย และสําหรับพระองค์ให้เพศหญิงกระนั้นหรือ?');
INSERT INTO "king_fahad_thai" VALUES(53,22,'53:22','ดูซิ นั่น เป็นการแบ่งส่วนที่ไม่ยุติธรรม');
INSERT INTO "king_fahad_thai" VALUES(53,23,'53:23','เหล่านี้มิใช่อื่นใดนอกจากเป็นชื่อที่พวกเจ้าและบรรพบุรุษของพวกเจ้าตั้งมันขึ้นมาเอง อัลลอฮฺมิได้ทรงประทานหลักฐานอันใดลงมาเกี่ยวกับเรื่องนั้นเลย พวกเขามิได้ปฏิบัติตามสิ่งใดนอกจากการคาดคะเนและสิ่งที่อารมณ์ปรารถนา และโดยแน่นอน แนวที่ถูกต้อง (ฮิดายะฮฺ) จากพระเจ้าของพวกเขาได้มีมายังพวกเขาแล้ว');
INSERT INTO "king_fahad_thai" VALUES(53,24,'53:24','หรือว่าสําหรับมนุษย์นั้นจะได้ทุกสิ่งที่เขาปรารถนา');
INSERT INTO "king_fahad_thai" VALUES(53,25,'53:25','(อํานาจนั้น) เป็นกรรมสิทธิ์ของอัลลอฮ.ทั้งในปรโลก และโลกนี้');
INSERT INTO "king_fahad_thai" VALUES(53,26,'53:26','และมะลักกี่มากน้อยในชั้นฟ้าทั้งหลายนั้น การชะฟาอะฮ.ของพวกเขาจะไม่อํานวยประโยชน์อันใด (แก่พวกเขา) เว้นแต่หลังจากอัลลอฮ.จะทรงอนุมัติแก่ผู้ที่พระองค์ทรงประสงค์และทรงพอพระทัย');
INSERT INTO "king_fahad_thai" VALUES(53,27,'53:27','แท้จริง บรรดาผู้ที่ไม่ศรัทธาต่อวันปรโลกนั้น แน่นอนพวกเขาจะตั้งชื่อมะลาอิกะฮฺเป็นเพศหญิง');
INSERT INTO "king_fahad_thai" VALUES(53,28,'53:28','และพวกเขาไม่มีความรู้ในเรื่องนั้นเลย พวกเขามิได้ปฏิบัติตามสิ่งใดนอกจากการคาดคะเนและแท้จริงการคาดคะเนนั้น จะไม่อํานวยประโยชน์อันใดแก่ความจริงได้');
INSERT INTO "king_fahad_thai" VALUES(53,29,'53:29','ดังนั้น เจ้าจงหลบหลีกให้ห่างจากผู้ที่ผินหลังจากการรําลึกนึกถึงเรา (อัลลอฮ.) และเขามิได้ปรารถนาอื่นใดนอกจากการมีชีวิตอยู่ในโลกนี้เท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(53,30,'53:30','นั่นคือสุดยอดแห่งความรู้ของพวกเขาแล้ว แท้จริงพระเจ้าของเจ้านั้น พระองค์ทรงรู้ดียิ่งถึงผู้ที่หลงออกจากทางของพระองค์และพระองค์ทรงรู้ดียิ่งถึงผู้ที่อยู่ในแนวทางที่ถูกต้อง (ฮิดายะฮ.)');
INSERT INTO "king_fahad_thai" VALUES(53,31,'53:31','และสิ่งที่อยู่ในชั้นฟ้าทั้งหลาย และสิ่งที่อยู่ในแผ่นดินนั้นเป็นกรรมสิทธิ์ของอัลลอฮฺเพื่อพระองค์จะทรงตอบแทนบรรดาผู้กระทําความชั่วตามที่พวกเขาประพฤติ และจะทรงตอบแทนบรรดาผู้กระทําความดีด้วยความดี');
INSERT INTO "king_fahad_thai" VALUES(53,32,'53:32','แก่บรรดาผู้หลีกเลี่ยงห่างจากการทําบาปใหญ่ และทําสิ่งลามกทั้งหลาย เว้นแต่ความผิดพลาดเล็กน้อยแท้จริงพระเจ้าของเจ้านั้น ทรงเป็นผู้กว้างขวางในการอภัย พระองค์ทรงรู้จักพวกเจ้าดียิ่ง เมื่อครั้งบังเกิดพวกเจ้าจากแผ่นดิน และเมื่อครั้งพวกเจ้าเป็นทารกอยู่ในครรภ์ของมารดาของพวกเจ้าดังพวกเจ้าอย่าแสดงความบริสุทธิ์แก่ตัวของพวกเจ้าเอง เพราะพระองค์ทรงรู้ดียิ่งถึงผู้ที่มีความยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(53,33,'53:33','เจ้าได้เห็นผู้ที่ผินหลังให้บ้างไหม?');
INSERT INTO "king_fahad_thai" VALUES(53,34,'53:34','และเขาให้เพียงเล็กน้อย และเขาได้ตระหนี่ (ส่วนที่เหลือ)');
INSERT INTO "king_fahad_thai" VALUES(53,35,'53:35','เขามีความรู้ในสิ่งเร้นลับกระนั้นหรือเขาจึงได้เห็น?');
INSERT INTO "king_fahad_thai" VALUES(53,36,'53:36','หรือว่าเขามิได้รับข่าวคราวที่มีอยู่ในคัมภีร์ของมูซา');
INSERT INTO "king_fahad_thai" VALUES(53,37,'53:37','และ (ในคัมภีร์ของ) อิบรอฮีม ผู้ซึ่งปฏิบัติตามสัญญาอย่างครบครัน');
INSERT INTO "king_fahad_thai" VALUES(53,38,'53:38','ว่าไม่มีผู้แบกภาระคนใดที่จะแบกภาระของผู้อื่นได้');
INSERT INTO "king_fahad_thai" VALUES(53,39,'53:39','และมนุษย์จะไม่ได้อะไรเลย นอกจากสิ่งที่เขาได้ขวนขวายเอาไว้');
INSERT INTO "king_fahad_thai" VALUES(53,40,'53:40','และแท้จริงการขวนขวายของเขาก็จะได้เห็นในไม่ช้า');
INSERT INTO "king_fahad_thai" VALUES(53,41,'53:41','แล้วเจาก็จะได้รับการตอบแทนด้วยการตอบแทนที่ครบถ้วนสมบูรณ์');
INSERT INTO "king_fahad_thai" VALUES(53,42,'53:42','และแท้จริงจุดหมายปลายทาง (ของเขา) ย่อมไปสู่พระเจ้าของเจ้า');
INSERT INTO "king_fahad_thai" VALUES(53,43,'53:43','และแท้จริงพระองค์คือผู้ทรงทําให้หัวเราะ และทรงทําให้ร้องไห้');
INSERT INTO "king_fahad_thai" VALUES(53,44,'53:44','และแท้จริงพระองค์คือผุ้ทรงทําให้ตาย และทรงทําให้เป็น');
INSERT INTO "king_fahad_thai" VALUES(53,45,'53:45','และแท้จริงพระองค์ทรงสร้างสามีภรรยาคู่หนึ่ง เป็นเพศชาย และเพศหญิง');
INSERT INTO "king_fahad_thai" VALUES(53,46,'53:46','จากเชื้ออสุจิเมื่อมันหลั่งออกมา');
INSERT INTO "king_fahad_thai" VALUES(53,47,'53:47','และแท้จริง เป็นหน้าที่ของพระองค์ที่จะให้บังเกิดอีกครั้งหนึ่ง');
INSERT INTO "king_fahad_thai" VALUES(53,48,'53:48','และแท้จริงพระองค์ทรงทําให้เขาร่ํารวย และทรงทําให้เขายากจน');
INSERT INTO "king_fahad_thai" VALUES(53,49,'53:49','และแท้จริงพระองค์ทรงเป็นพระเจ้าแห่งดาวสิริอุส (อัชเชียะอฺรอ)');
INSERT INTO "king_fahad_thai" VALUES(53,50,'53:50','และแท้จริงพระองค์ทรงทําลายพวกอ๊าดรุ่นก่อน ๆ');
INSERT INTO "king_fahad_thai" VALUES(53,51,'53:51','และพวกษะมูดก็ไม่ได้ให้มีเหลืออยู่อีกเลย');
INSERT INTO "king_fahad_thai" VALUES(53,52,'53:52','และหมู่ชนของนูหก่อนหน้านี้ แท้จริงพวกเขาเป็นผู้อธรรมยิ่ง และเป็นผู้ละเมิดยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(53,53,'53:53','และเมืองที่พลิกคว่ําลง (อัลมุอ.ตะฟิกะฮ.) พระองค์ทรงให้มันถล่มลง');
INSERT INTO "king_fahad_thai" VALUES(53,54,'53:54','ฉะนั้น สิ่งที่ครอบคลุมมันก็ (คือการลงโทษ) ได้ครอบคลุมมัน');
INSERT INTO "king_fahad_thai" VALUES(53,55,'53:55','ดังนั้น ความโปรดปรานของพวกเจ้าของเจ้าอันใดเล่าที่เจ้ายังสงสัยอยู่');
INSERT INTO "king_fahad_thai" VALUES(53,56,'53:56','นี่คือผู้ตักเตือนที่มาจากปวงผู้ตักเตือนรุ่นก่อน ๆ');
INSERT INTO "king_fahad_thai" VALUES(53,57,'53:57','เวลาที่ใกล้เข้ามา (วันกิยามะฮ) ได้ใกล้เข้ามาแล้ว');
INSERT INTO "king_fahad_thai" VALUES(53,58,'53:58','ไม่มีผู้ใดที่จะปัดเป่าให้พ้นไปได้นอกจากอัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(53,59,'53:59','พวกเจ้ายังคงแปลกใจต่อคํากล่าวนี้อีกหรือ?');
INSERT INTO "king_fahad_thai" VALUES(53,60,'53:60','และพวกเจ้ายังคงหัวเราะ และยังไม่ร้องไห้!');
INSERT INTO "king_fahad_thai" VALUES(53,61,'53:61','และพวกเจ้ายังคงหลงระเริงลืมตัว!');
INSERT INTO "king_fahad_thai" VALUES(53,62,'53:62','ดังนั้นพวกเจ้าจงสุญูดต่ออัลลอฮ.เถิดและจงเคารพภักดีต่อพระองค์เถิด');
INSERT INTO "king_fahad_thai" VALUES(54,1,'54:1','วันกิยามะฮฺ ได้ใกล้เข้ามาแล้ว และดวงจันทร์ได้แยกออก');
INSERT INTO "king_fahad_thai" VALUES(54,2,'54:2','และหากพวกเขาเห็นสัญญาณ (ปาฏิหาริย์) พวกเขาก็ผินหลังให้และกล่าวว่า นี่คือมายากลที่มีมาก่อนแล้ว');
INSERT INTO "king_fahad_thai" VALUES(54,3,'54:3','และพวกเขาได้ปฏิเสธและปฏิบัติตามอารมณ์ใฝ่ต่ําของพวกเขา และกิจการทุกอย่างย่อมเป็นไปตามกําหนด');
INSERT INTO "king_fahad_thai" VALUES(54,4,'54:4','และโดยแน่นอน ได้มีข่าวคราว (ในอดีต) มายังพวกเขาแล้ว ซึ่งในนั้นมีข้อตักเตือน (แก่พวกเขา)');
INSERT INTO "king_fahad_thai" VALUES(54,5,'54:5','(อัลกุรอานนี้) เป็นฮิกมะฮฺอันลึกซึ้ง แต่การตักเตือนนั้นไม่บังเกิดผล (แก่พวกเขา)');
INSERT INTO "king_fahad_thai" VALUES(54,6,'54:6','ดังนั้นเจ้า (มุฮัมมัด) จงผินหลังให้แก่พวกเขา วันซึ่งผู้เรียกร้อง (อิสรอฟีล) จะร้องเรียกไปสู่สิ่งที่น่ากลัวสยองขวัญ');
INSERT INTO "king_fahad_thai" VALUES(54,7,'54:7','สายตาของพวกเขาจะลดต่ําลงขณะที่พวกเขาออกจากกุบูร เสมือนหนึ่งพวกเขาเป็นตั๊กแตนที่บินว่อน');
INSERT INTO "king_fahad_thai" VALUES(54,8,'54:8','รีบวิ่งไปยังผู้เรียกร้อง พวกปฏิเสธศรัทธาจะกล่าวว่า วันนี้เป็นวันที่ลําบากยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(54,9,'54:9','ก่อนหน้าพวกเขานั้น หมู่ชนของนูหฺได้ปฏิเสธ พวกเขาได้ปฏิเสธบ่าวของเราโดยกล่าวว่าเขา (นูหฺ) เป็นคนบ้า และถูกขู่บังคับ');
INSERT INTO "king_fahad_thai" VALUES(54,10,'54:10','เขาจึงวิงวอนขอต่อพระเจ้าของเขาว่า แท้จริงข้าพระองค์ถูกพิชิตเสียแล้ว ได้โปรดช่วยเหลือ(ข้าพระองค์)ด้วย');
INSERT INTO "king_fahad_thai" VALUES(54,11,'54:11','ดังนั้น เราจึงได้เปิดประตูแห่งชั้นฟ้าให้น้ําฝนเทลงมาอย่างหนัก');
INSERT INTO "king_fahad_thai" VALUES(54,12,'54:12','และเราได้ทําให้แผ่นดินแยกออกเป็นตาน้ําไหลพุ่ง ดังนั้น น้ําฝนและตาน้ําได้มาบรรจบกันตามกิจการที่ได้ถูกกําหนดไว้แล้ว');
INSERT INTO "king_fahad_thai" VALUES(54,13,'54:13','และเราได้บรรทุกเขาไว้บนเรือที่ทําด้วยแผ่นไม้กระดาน และตอกติดด้วยตะปู');
INSERT INTO "king_fahad_thai" VALUES(54,14,'54:14','มัน (เรือ) ได้แล่นไปต่อหน้าเรา (ภายใต้การคุ้มครองของเรา) เป็นการตอบแทนแก่ผู้ที่ถูกปฏิเสธ');
INSERT INTO "king_fahad_thai" VALUES(54,15,'54:15','และโดยแน่นอนเราได้ทิ้งมันไว้เป็นสัญญาณหนึ่ง แต่มีผู้ใดบ้างที่รับข้อตักเตือนนั้น');
INSERT INTO "king_fahad_thai" VALUES(54,16,'54:16','ดังนั้น การลงโทษของเราและการตักเตือนของเราเป็นเช่นใดบ้าง?');
INSERT INTO "king_fahad_thai" VALUES(54,17,'54:17','และโดยแน่นอน เราได้ทําให้อัลกุรอานนี้เป็นที่เข้าใจง่ายแก่การรําลึก แล้วมีผู้ใดบ้างที่รับข้อตักเตือนนั้น');
INSERT INTO "king_fahad_thai" VALUES(54,18,'54:18','พวกอ๊าดได้ปฏิเสธ ดังนั้นการลงโทษของเราและการตักเตือนของเราเป็นเช่นใดบ้าง?');
INSERT INTO "king_fahad_thai" VALUES(54,19,'54:19','แท้จริงเราได้ส่งลมพายุที่หนาวเหน็บไปยังพวกเขา ในวันแห่งความหายนะที่ติดต่อกัน');
INSERT INTO "king_fahad_thai" VALUES(54,20,'54:20','ลมได้พัดกระชากผู้คนคล้ายกับว่าพวกเขาเป็นต้นอินทผลัมที่ถูกถอนออกจากราก');
INSERT INTO "king_fahad_thai" VALUES(54,21,'54:21','ดังนั้นการลงโทษของเรา และการตักเตือนของเราเป็นเช่นใดบ้าง?');
INSERT INTO "king_fahad_thai" VALUES(54,22,'54:22','และโดยแน่นอน เราได้ทําให้อัลกุรอานนี้เป็นที่เข้าใจง่ายแก่การรําลึก แล้วมีผู้ใดบ้างที่รับข้อตักเตือนนั้น');
INSERT INTO "king_fahad_thai" VALUES(54,23,'54:23','พวกซะมูดได้ปฏิเสธผู้ตักเตือน (ร่อซูลของพวกเขา)');
INSERT INTO "king_fahad_thai" VALUES(54,24,'54:24','พวกเขากล่าวว่า คนธรรมดา ๆ จากพวกเรานี้นะหรือจะให้เราปฏิบัติตามเขา แน่นอนดังนั้นเราจะอยู่ในการหลงทางและเป็นคนบ้า');
INSERT INTO "king_fahad_thai" VALUES(54,25,'54:25','สาสน์นั้นถูกส่งมาให้แก่เขาแต่ผู้เดียวอื่นจากพวกเรากระนั้นหรือ? เปล่าเลย เขาเป็นคนพูดเท็จ อวดดี ไร้มารยาท');
INSERT INTO "king_fahad_thai" VALUES(54,26,'54:26','พรุ่งนี้พวกเขาจะได้รู้ว่าใครเป็นคนพูดเท็จ อวดดี ไร้มารยาท');
INSERT INTO "king_fahad_thai" VALUES(54,27,'54:27','แท้จริงเราจะส่งอูฐตัวเมียตัวหนึ่งมาเพื่อเป็นการทดสอบแก่พวกเขา ดังนั้น (ซอและฮ) จงคอยดูพวกเขาและจงอดทน');
INSERT INTO "king_fahad_thai" VALUES(54,28,'54:28','และจงบอกพวกเขาว่า น้ํานั้นถูกแบ่งส่วนระหว่างพวกเขา ทุก ๆ ส่วนของน้ําดื่มถูกจัดไว้แล้ว');
INSERT INTO "king_fahad_thai" VALUES(54,29,'54:29','แต่พวกเขาได้ร้องเรียกเพื่อนของพวกเขา เขาได้จับมันฆ่าด้วยดาบอย่างทารุณ');
INSERT INTO "king_fahad_thai" VALUES(54,30,'54:30','ดังนั้น การลงโทษของเรา และการตักเตือนของเราเป็นเช่นใดบ้าง');
INSERT INTO "king_fahad_thai" VALUES(54,31,'54:31','แท้จริง เราได้ส่งเสียงกัมปนาทเพียงครั้งเดียวลงบนพวกเขา แล้วพวกเขากลายเป็นเช่นเศษไม้แห้ง');
INSERT INTO "king_fahad_thai" VALUES(54,32,'54:32','และโดยแน่นอน เราได้ทําให้อัลกุรอานนี้เป็นที่เข้าใจง่ายแก่การรําลึก แล้วมีผู้ใดบ้างที่รับข้อตักเตือนนั้น');
INSERT INTO "king_fahad_thai" VALUES(54,33,'54:33','หมู่ชนของลูฏได้ปฏิเสธต่อการตักเตือน');
INSERT INTO "king_fahad_thai" VALUES(54,34,'54:34','แท้จริง เราได้ส่งพายุหินจากท้องฟ้าลงบนพวกเขา นอกจากวงศ์วานของลูฏ เราได้ช่วยพวกเขาให้รอดพ้นในยามรุ่งสาง');
INSERT INTO "king_fahad_thai" VALUES(54,35,'54:35','เป็นความโปรดปรานจากเรา เช่นนั้นแหละเราตอบแทนผู้กตัญญู');
INSERT INTO "king_fahad_thai" VALUES(54,36,'54:36','และโดยแน่นอนเขา (ลูฏ) ได้ตักเตือนพวกเขาถึงการลงโทษของเรา แต่พวกเขาได้โต้แย้งข้อตักเตือน (ของเรา)');
INSERT INTO "king_fahad_thai" VALUES(54,37,'54:37','และโดยแน่นอน พวกเขาได้ขอร้อง (แกมบังคับ) เขาให้ส่งมอบแขกของเขา (แก่พวกเขา) ดังนั้นเราจึงทําให้นัยตาของพวกเขาบอดลง ฉะนั้นพวกเจ้าจงลิ้มรสการลงโทษของเรา และการตักเตือนของเรา');
INSERT INTO "king_fahad_thai" VALUES(54,38,'54:38','และโดยแน่นอน การลงโทษอันต่อเนื่องก็ได้เกิดขึ้นแก่พวกเขาในเวลาเช้า');
INSERT INTO "king_fahad_thai" VALUES(54,39,'54:39','ฉะนั้นพวกเจ้าจงลิ้มรสการลงโทษของเรา และการตักเตือนของเรา');
INSERT INTO "king_fahad_thai" VALUES(54,40,'54:40','และโดยแน่นอน เราได้ทําให้อัลกุรอานนี้เป็นที่เข้าใจง่าย แก่การรําลึก และมีผู้ใดบ้างที่รับข้อตักเตือนนั้น');
INSERT INTO "king_fahad_thai" VALUES(54,41,'54:41','และโดยแน่นอน ได้มีการตักเตือนมายังวงศ์วานของฟิรเอาน.');
INSERT INTO "king_fahad_thai" VALUES(54,42,'54:42','พวกเขาได้ปฏิเสธสัญญาณต่าง ๆ ของเราทั้งหมด ดังนั้นเราจึงได้ลงโทษพวกเขาซึ่งการลงโทษแห่งผู้ทรงอํานาจ ผู้ทรงอานุภาพ');
INSERT INTO "king_fahad_thai" VALUES(54,43,'54:43','พวกปฏิเสธศรัทธาของพวกเจ้าจะดียิ่งกว่าพวกเหล่านั้นกระนั้นหรือ? หรือว่ามีการยกเว้นโทษสําหรับพวกเจ้าระบุไว้ในคัมภีร์');
INSERT INTO "king_fahad_thai" VALUES(54,44,'54:44','หรือพวกเขากล่าวว่า พวกเราทั้งหมดนั้นเป็นผู้ชนะ');
INSERT INTO "king_fahad_thai" VALUES(54,45,'54:45','พลพรรคพวกนี้จะพ่ายแพ้ และพวกเขาจะผินหลังวิ่งหนี');
INSERT INTO "king_fahad_thai" VALUES(54,46,'54:46','แต่ว่ายามอวสาน (วันกิยามะฮ.) นั้นเป็นกําหนดเวลา (การลงโทษ) ของพวกเขาและยามอวสานนั้นทุกข์ทรมานยิ่ง และขมขื่นยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(54,47,'54:47','แท้จริงบรรดาผู้มีความผิดนั้น อยู่ในการหลงทาง และการเผาไหม้');
INSERT INTO "king_fahad_thai" VALUES(54,48,'54:48','วันที่พวกเขาจะถูกลากบนใบหน้าของพวกเขาลงสู่ไฟนรก (จะมีเสียงกล่าวขึ้นว่า) พวกเจ้าจงลิ้มรสการลงโทษที่มีการเผาไหม้');
INSERT INTO "king_fahad_thai" VALUES(54,49,'54:49','แท้จริงทุก ๆ สิ่งนั้นเราสร้างมันตามสัดส่วน');
INSERT INTO "king_fahad_thai" VALUES(54,50,'54:50','และกิจการของเรา (ในการสร้าง) นั้นเพียง (บัญชา) ครั้งเดียว คล้ายกับชั่วพริบตาเดียว');
INSERT INTO "king_fahad_thai" VALUES(54,51,'54:51','และโดยแน่นอน เราได้ทําลายกลุ่มชนเช่นพวกเจ้าแล้วมีผู้ใดบ้างที่รับข้อตักเตือนนั้น');
INSERT INTO "king_fahad_thai" VALUES(54,52,'54:52','และทุก ๆ สิ่งที่พวกเขากระทํามันนั้นมีอยู่ในบันทึก');
INSERT INTO "king_fahad_thai" VALUES(54,53,'54:53','และทุก ๆ สิ่งทั้งเล็กและใหญ่ก็ได้ถูกบันทึกไว้แล้ว');
INSERT INTO "king_fahad_thai" VALUES(54,54,'54:54','แท้จริงบรรดาผู้ยําเกรงนั้นอยู่ในสวนสวรรค์หลากหลาย และแม่น้ําหลายสาย');
INSERT INTO "king_fahad_thai" VALUES(54,55,'54:55','ในสถานที่อันทรงเกียรติ ณ ที่พระเจ้าผู้ทรงอานุภาพ');
INSERT INTO "king_fahad_thai" VALUES(55,1,'55:1','พระผู้ทรงกรุณาปรานี');
INSERT INTO "king_fahad_thai" VALUES(55,2,'55:2','พระองค์ทรงสอนอัลกุรอาน');
INSERT INTO "king_fahad_thai" VALUES(55,3,'55:3','พระองค์ทรงสร้างมนุษย์');
INSERT INTO "king_fahad_thai" VALUES(55,4,'55:4','พระองค์ทรงสอนเขาให้เปล่งเสียงพูด');
INSERT INTO "king_fahad_thai" VALUES(55,5,'55:5','ดวงอาทิตย์ และดวงจันทร์โคจรตามวิถีที่แน่นอน');
INSERT INTO "king_fahad_thai" VALUES(55,6,'55:6','และผักหญ้า และต้นไม้จะกราบสุญูด');
INSERT INTO "king_fahad_thai" VALUES(55,7,'55:7','และชั้นฟ้านั้นพระองค์ทรงยกมันไว้สูง และทรงวางความสมดุลไว้');
INSERT INTO "king_fahad_thai" VALUES(55,8,'55:8','เพื่อพวกเจ้าจะได้ไม่ฝ่าฝืนในเรื่องการชั่งตวงวัด');
INSERT INTO "king_fahad_thai" VALUES(55,9,'55:9','และจงธํารงไว้ซึ่งการชั่งด้วยความเที่ยงธรรม และอย่าให้ขาดหรือหย่อนตาชั่ง');
INSERT INTO "king_fahad_thai" VALUES(55,10,'55:10','และแผ่นดินนั้น พระองค์ทรงจัดเตรียมมันไว้ เพื่อสรรพสิ่งที่สร้างขึ้น');
INSERT INTO "king_fahad_thai" VALUES(55,11,'55:11','ในแผ่นดินนั้นมีผลไม้ และต้นอินทผลัมที่มีผลซ้อนกันหลายชั้น');
INSERT INTO "king_fahad_thai" VALUES(55,12,'55:12','และเมล็ดที่มีเปลือกและรํา และมีกลิ่นหอม');
INSERT INTO "king_fahad_thai" VALUES(55,13,'55:13','ดังนั้น ด้วยบุญคุณอันใดเล่าของพระเจ้าของเจ้าทั้งสองที่เจ้าทั้งสองปฏิเสธ');
INSERT INTO "king_fahad_thai" VALUES(55,14,'55:14','พระองค์ทรงสร้างมนุษย์จากดินเหนียวมีเสียงเช่นเครื่องปั้นดินเผา');
INSERT INTO "king_fahad_thai" VALUES(55,15,'55:15','และพระองค์ทรงสร้างญินจากเปลงไฟ');
INSERT INTO "king_fahad_thai" VALUES(55,16,'55:16','ดังนั้น ด้วยบุญคุณอันใดเล่าแห่งพระเจ้าของเจ้าทั้งสองที่เจ้าทั้งสองปฏิเสธ?');
INSERT INTO "king_fahad_thai" VALUES(55,17,'55:17','พระเจ้าแห่งทิศตะวันออกทั้งสองและพระเจ้าแห่งทิศตะวันตกทั้งสอง');
INSERT INTO "king_fahad_thai" VALUES(55,18,'55:18','ดังนั้น ด้วยบุญคุณอันใดเล่าแห่งพระเจ้าของเจ้าทั้งสองที่เจ้าทั้งสองปฏิเสธ?');
INSERT INTO "king_fahad_thai" VALUES(55,19,'55:19','พระองค์ทรงทําให้น่านน้ําทั้งสอง (ทะเลและแม่น้ํา) ไหลมาบรรจบกัน');
INSERT INTO "king_fahad_thai" VALUES(55,20,'55:20','ระหว่างมันทั้งสองมีที่กั้นกีดขวาง มันจะไม่ล้ําเขตต่อกัน');
INSERT INTO "king_fahad_thai" VALUES(55,21,'55:21','ดังนั้น ด้วยบุญคุณอันใดเล่าแห่งพระเจ้าของเจ้าทั้งสองที่เจ้าทั้งสองปฏิเสธ?');
INSERT INTO "king_fahad_thai" VALUES(55,22,'55:22','มีไข่มุกและหินปะการังออกมาจากมันทั้งสอง');
INSERT INTO "king_fahad_thai" VALUES(55,23,'55:23','ดังนั้น ด้วยบุญคุณอันใดเล่าแห่งพระเจ้าของเจ้าทั้งสองที่เจ้าทั้งสองปฏิเสธ?');
INSERT INTO "king_fahad_thai" VALUES(55,24,'55:24','และเป็นของพระองค์คือ เรือขนาดใหญ่ลอยเด่นอยู่ในทะเลคล้ายกับภูเขา (บนบก)');
INSERT INTO "king_fahad_thai" VALUES(55,25,'55:25','ดังนั้น ด้วยบุญคุณอันใดเล่าแห่งพระเจ้าของเจ้าทั้งสองที่เจ้าทั้งสองปฏิเสธ?');
INSERT INTO "king_fahad_thai" VALUES(55,26,'55:26','ทุก ๆ สิ่งที่อยู่บนแผ่นดินย่อมแตกดับ');
INSERT INTO "king_fahad_thai" VALUES(55,27,'55:27','และพระพักตร์ของพระเจ้าของเจ้าผู้ทรงยิ่งใหญ่ ผู้ทรงโปรดปรานเท่านั้นที่จะยังคงเหลืออยู่');
INSERT INTO "king_fahad_thai" VALUES(55,28,'55:28','ดังนั้น ด้วยบุญอันใดเล่าแห่งพระเจ้าของเจ้าทั้งสองที่เจ้าทั้งสองปฏิเสธ?');
INSERT INTO "king_fahad_thai" VALUES(55,29,'55:29','ผู้ที่อยู่ในชั้นฟ้าทั้งหลาย และแผ่นดินจะขอพระองค์ทุก ๆ ขณะ พระองค์ทรงมีภาระกิจ');
INSERT INTO "king_fahad_thai" VALUES(55,30,'55:30','ดังนั้น ด้วยบุญคุณอันใดเล่าแห่งพระเจ้าของเจ้าทั้งสองที่เจ้าทั้งสองปฏิเสธ');
INSERT INTO "king_fahad_thai" VALUES(55,31,'55:31','อีกไม่ช้าเราจะจักการกับพวกเจ้า (ในกิจการต่าง ๆ) โอ้มนุษย์และญินเอ๋ย!');
INSERT INTO "king_fahad_thai" VALUES(55,32,'55:32','ดังนั้นด้วยบุญคุณอันใดเล่าแห่งพระเจ้าของเจ้าทั้งสองที่เจ้าทั้งสองปฏิเสธ');
INSERT INTO "king_fahad_thai" VALUES(55,33,'55:33','โอ้ชุมนุมแห่งญินและมนุษย์ทั้งหลาย เอ๋ย! หากพวกเจ้ามีความสามารถที่จะผ่านไปให้พ้นขอบฟ้าทั้งหลายและแผ่นดินนี้ได้ ก็จงผ่านไปให้พ้นเถิด แต่ว่าพวกเจ้าไม่สามารถที่จะผ่านไปให้พ้นได้เว้นแต่ด้วยพลัง (พระบัญชาและพระประสงค์ของอัลลอฮ.)');
INSERT INTO "king_fahad_thai" VALUES(55,34,'55:34','ดังนั้นด้วยบุญคุณอันใดเล่าแห่งพระเจ้าของเจ้าทั้งสองที่เจ้าทั้งสองปฏิเสธ?');
INSERT INTO "king_fahad_thai" VALUES(55,35,'55:35','เปลวไฟ และทองเหลือง จะถูกส่งมายังเจ้าทั้งสองแล้วเจ้าทั้งสองก็ไม่อาจจะป้องกันตนเองได้');
INSERT INTO "king_fahad_thai" VALUES(55,36,'55:36','ดังนั้นด้วยบุญคุณอันใดเล่าแห่งพระเจ้าของเจ้าทั้งสองที่เจ้าทั้งสองปฏิเสธ?');
INSERT INTO "king_fahad_thai" VALUES(55,37,'55:37','ครั้นเมื่อชั้นฟ้าแตกกระจายออก มันจะกลายเป็นสีแดงคล้ายขี้ผึ้ง');
INSERT INTO "king_fahad_thai" VALUES(55,38,'55:38','ดังนั้น ด้วยบุญอันใดเล่าแห่งพระเจ้าของเจ้าทั้งสอง ที่เจ้าทั้งสองปฏิเสธ?');
