INSERT INTO "king_fahad_thai" VALUES(56,51,'56:51','แล้วรวมทั้งพวกเจ้าอีกด้วย โอ้บรรดาผู้หลงผิด บรรดาผู้ปฏิเสธทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(56,52,'56:52','แน่นอนพวกเจ้าจะเป็นผู้กินต้นซักกูม');
INSERT INTO "king_fahad_thai" VALUES(56,53,'56:53','และพวกเขาจะใส่มันเข้าไปเต็มท้อง');
INSERT INTO "king_fahad_thai" VALUES(56,54,'56:54','และพวกเขาจะดื่มน้ํากําลังเดือดตามลงไป');
INSERT INTO "king_fahad_thai" VALUES(56,55,'56:55','พวกเขาจะดื่ม (น้ํา) เช่นกับการดื่มของอูฐ ที่กระหายน้ําจัด');
INSERT INTO "king_fahad_thai" VALUES(56,56,'56:56','นี่คือที่พํานักของพวกเขาในวันแห่งการตอบแทน');
INSERT INTO "king_fahad_thai" VALUES(56,57,'56:57','เรานั้นได้สร้างพวกเจ้าขึ้นมา ไฉนเล่าพวกเจ้าจึงไม่เชื่อ (ในวันฟื้นคืนชีพ)');
INSERT INTO "king_fahad_thai" VALUES(56,58,'56:58','พวกเจ้าเห็นสิ่งที่พวกเจ้าหลั่งออกมา (อสุจิ) แล้วมิใช่หรือ?');
INSERT INTO "king_fahad_thai" VALUES(56,59,'56:59','พวกเจ้าสร้างมันขึ้นมา หรือว่าเราเป็นผู้สร้าง');
INSERT INTO "king_fahad_thai" VALUES(56,60,'56:60','เรานั้นเป็นผู้กําหนดความตายขึ้นในระหว่างพวกเจ้า และเราก็จะไม่ถูกขัดขวาง');
INSERT INTO "king_fahad_thai" VALUES(56,61,'56:61','ในการที่เราจะเปลี่ยนบุคคลเยี่ยงพวกเจ้า และเราจะให้พวกเจ้าเกิดขึ้นมาอีกในสิ่งที่พวกเจ้าไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(56,62,'56:62','และโดยแน่นอน พวกเจ้าได้รู้มาแล้วถึงการเกิดครั้งแรก แล้วไฉนเล่าพวกเจ้าจึงไม่ใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(56,63,'56:63','พวกเจ้าเห็นสิ่งที่พวกเจ้าหว่านมาแล้วมิใช่หรือ?');
INSERT INTO "king_fahad_thai" VALUES(56,64,'56:64','พวกเจ้าทําให้มันงอกเงยขึ้นมา หรือว่าเราเป็นผู้ทําให้มันงอกเงยขึ้นมา?');
INSERT INTO "king_fahad_thai" VALUES(56,65,'56:65','หากเราประสงค์ทําให้มันหักเป็นชิ้น ๆ แล้ว แน่นอนเราก็ย่อมทํามันได้ แล้วพวกเจ้าคงประหลาดใจ');
INSERT INTO "king_fahad_thai" VALUES(56,66,'56:66','(พวกเจ้าจะกล่าวขึ้นว่า) แท้จริงเราได้รับความหายนะแล้ว');
INSERT INTO "king_fahad_thai" VALUES(56,67,'56:67','ไม่เพียงแต่เท่านั้น เรายังขาดแคลนปัจจัยเพาะปลูกอีกด้วย');
INSERT INTO "king_fahad_thai" VALUES(56,68,'56:68','พวกเจ้าเห็นน้ําที่พวกเจ้าดื่มแล้วมิใช่หรือ?');
INSERT INTO "king_fahad_thai" VALUES(56,69,'56:69','พวกเจ้าเป็นผู้หลั่งมันลงมาจากก้อนเมฆ หรือว่าเราเป็นผู้หลั่งมันลงมา');
INSERT INTO "king_fahad_thai" VALUES(56,70,'56:70','หากเราประสงค์ เราจะทําให้มันเค็มจัด แล้วไฉนเล่าพวกเจ้าจึงไม่กตัญญู?');
INSERT INTO "king_fahad_thai" VALUES(56,71,'56:71','พวกเจ้าเห็นไฟที่พวกเจ้าจุดขึ้นมาแล้วมิใช่หรือ?');
INSERT INTO "king_fahad_thai" VALUES(56,72,'56:72','พวกเจ้าเป็นผู้ทําให้ต้นไม้ของมันงอกเงยขึ้นมา หรือว่าเราเป็นผู้ทําให้มันงอกขึ้นมา');
INSERT INTO "king_fahad_thai" VALUES(56,73,'56:73','เราได้ทําให้มันมีขึ้นเพื่อเป็นการเตือนสติ และอํานวนประโยชน์แก่ผู้เดินทางรอนแรม');
INSERT INTO "king_fahad_thai" VALUES(56,74,'56:74','ดังนั้น เจ้าจงสดุดีด้วยพระนามแห่งพระเจ้าของเจ้าผู้ทรงยิ่งใหญ่เถิด');
INSERT INTO "king_fahad_thai" VALUES(56,75,'56:75','ข้า (อัลลอฮ.) ขอสาบานด้วยตําแหน่งต่าง ๆ ของดวงดาว');
INSERT INTO "king_fahad_thai" VALUES(56,76,'56:76','และแท้จริงมันเป็นการสาบานอันยิ่งใหญ่ หากพวกเจ้ารู้');
INSERT INTO "king_fahad_thai" VALUES(56,77,'56:77','นั่นคือ กุรอานอันทรงเกียรติ');
INSERT INTO "king_fahad_thai" VALUES(56,78,'56:78','ซึ่งอยู่ในบันทึกที่ถูกพิทักษ์รักษาไว้');
INSERT INTO "king_fahad_thai" VALUES(56,79,'56:79','ไม่มีผู้ใดจะแตะต้องอัลกุรอาน นอกจากบรรดาผู้บริสุทธิ์เท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(56,80,'56:80','ถูกประทานลงมาจากพระเจ้าแห่งสากลโลก');
INSERT INTO "king_fahad_thai" VALUES(56,81,'56:81','และด้วยเรื่องนี้ (อัลกุรอาน) กระนั้นหรือที่พวกเจ้าปฏิเสธเย้ยหยัน?');
INSERT INTO "king_fahad_thai" VALUES(56,82,'56:82','และทั้งๆ ที่พระองค์ทรงประทานปัจจัยยังชีพแก่พวกเจ้า พวกเจ้าก็ยังคงปฏิเสธศรัทธาต่ออัลลอฮฺ กระนั้นหรือ');
INSERT INTO "king_fahad_thai" VALUES(56,83,'56:83','และเมื่อวิญญาณได้มาถึงคอหอย (กําลังจะตาย) แล้วพวกเจ้าสามารถจะยับยั้งไว้ได้หรือ?');
INSERT INTO "king_fahad_thai" VALUES(56,84,'56:84','และในขณะนั้นพวกเจ้ากําลังมองดูกันอยู่');
INSERT INTO "king_fahad_thai" VALUES(56,85,'56:85','และเรานั้นอยู่ใกล้ชิดเขายิ่งกว่าพวกเจ้าแต่ทว่าพวกเจ้ามองไม่เห็น(มะลาอิกะฮ.)');
INSERT INTO "king_fahad_thai" VALUES(56,86,'56:86','หากว่าพวกเจ้ามิได้อยู่ภายใต้อํานาจของผู้ใด และไม่มีพระเจ้าเป็นผู้มีอํานาจเหนือพวกเจ้าแล้ว');
INSERT INTO "king_fahad_thai" VALUES(56,87,'56:87','ไฉนเล่า พวกเจ้าจึงไม่ให้วิญญาณกลับมาสู่ร่างอีก หากพวกเจ้าพูดจริง?');
INSERT INTO "king_fahad_thai" VALUES(56,88,'56:88','สําหรับผู้ที่หากว่าเขา (ผู้ตาย) เป็นผู้ใกล้ชิดกับอัลลอฮ.');
INSERT INTO "king_fahad_thai" VALUES(56,89,'56:89','ดังนั้นความอิ่มเอิบสดชื่น และสวรรค์อันเป็นที่โปรดปรานจะได้แก่เขา');
INSERT INTO "king_fahad_thai" VALUES(56,90,'56:90','และหากว่าเขาอยู่ในกลุ่มทางขวา (ผู้ได้รับบันทึกด้วยมือขวา)');
INSERT INTO "king_fahad_thai" VALUES(56,91,'56:91','ดังนั้นความปลอดภัยก็เป็นของเจ้า ในฐานะเป็นผู้อยู่ ในกลุ่มทางขวา (ผู้ได้รับบันทึกด้วยมือขวา)');
INSERT INTO "king_fahad_thai" VALUES(56,92,'56:92','และหากว่าเขาอยู่ในหมู่ผู้ปฏิเสธหลงทาง');
INSERT INTO "king_fahad_thai" VALUES(56,93,'56:93','ดังนั้นสิ่งที่เตรียมไว้สําหรับเขาก็คือน้ําร้อนที่กําลังเดือด');
INSERT INTO "king_fahad_thai" VALUES(56,94,'56:94','และเปลวไฟที่ลุกไหม้');
INSERT INTO "king_fahad_thai" VALUES(56,95,'56:95','แท้จริงนี้แหละคือความจริงที่แน่นอน');
INSERT INTO "king_fahad_thai" VALUES(56,96,'56:96','ดังนั้นเจ้าจงสดุดีด้วยพระนามพระเจ้าของเจ้าผู้ยิ่งใหญ่เถิด');
INSERT INTO "king_fahad_thai" VALUES(57,1,'57:1','สิ่งที่อยู่ในชั้นฟ้าทั้งหลาย และในแผ่นดินแซ่ซ้องสดุดีแด่อัลลอฮ. และพระองค์เป็นผู้ทรงอํานาจ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(57,2,'57:2','อํานาจอันเด็ดขาดแห่งชั้นฟ้าทั้งหลายและแผ่นดินนั้นเป็นสิทธิ์ของพระองค์ พระองค์ทรงให้เป็นและทรงให้ตาย และพระองค์เป็นผู้ทรงอานุภาพเหนือทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(57,3,'57:3','พระองค์ทรงเป็นองค์แรกและองค์สุดท้าย และทรงเปิดเผยและทรงเร้นลับ และพระองค์เป็นผู้ทรงรอบรู้ทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(57,4,'57:4','พระองค์คือ พระผู้ทรงสร้างชั้นฟ้าทั้งหลาย และแผ่นดินในระยะ 6 วัน แล้วพระองค์ทรงสถิตย์อยู่บนบัลลังก์ พระองค์ทรงรอบรู้สิ่งที่เข้าไปในแผ่นดิน และสิ่งที่ออกมาจากแผ่นดิน และสิ่งที่ลงมาจากฟากฟ้าและสิ่งที่ขึ้นไปสู่ฟากฟ้าและพระองค์ทรงอยู่กับ พวกเจ้าไม่ว่าพวกเจ้าจะอยู่ ณ แห่งหนใด และ อัลลอฮ.ทรงเห็นสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(57,5,'57:5','อํานาจอันเด็ดขาดแห่งชั้นฟ้าทั้งหลายและแผ่นดินนั้นเป็นสิทธิ์ของพระองค์ และการงานทั้งหลายถูกให้กลับไปยังอัลลอฮ.เท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(57,6,'57:6','พระองค์ทรงให้กลางคืนคาบเกี่ยวเข้าไปในกลางวัน และทรงให้กลางวันคาบเกี่ยวเข้าไปในกลางคืน และพระองค์เป็นผู้ทรงรอบรู้สิ่งที่อยู่ในทรวงอก');
INSERT INTO "king_fahad_thai" VALUES(57,7,'57:7','พวกเจ้าจงศรัทธาต่ออัลลอฮ. และร่อซูลของพระองค์ และจงบริจาคในทางของอัลลอฮจากสิ่งที่พระองค์ทรงทําให้พวกเจ้าเป็นตัวแทนของมัน ดังนั้นบรรดาผู้ศรัทธาและบรรดาผู้บริจาคในหมู่พวกเจ้านั้นสําหรับพวกเขาจะได้รับรางวัลอันใหญ่ยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(57,8,'57:8','และไฉนเล่าพวกเจ้าจึงไม่ศรัทธาต่ออัลลอฮ. ทั้ง ๆ ที่ร่อซูลได้เรียกร้องพวกเจ้าให้ศรัทธาต่อพระเจ้าของพวกเจ้า และแน่นอนพระองค์ได้ทรงเอาสัญญากับพวกเจ้าแล้ว หากพวกเจ้าเป็นผู้ศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(57,9,'57:9','พระองค์เป็นผู้ทรงประทานบรรดาอัลอายาตอันชัดแจ้งลงมาแก่ (มุฮัมมัด) บ่าวของพระองค์ เพื่อทรงนําพวกเจ้าออกจากความมืดทั้งหลายสู่ความสว่าง และแท้จริงอัลลอฮฺนั้นเป็นผู้ทรงเอ็นดู ผู้ทรงเมตตาเสมอต่อพวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(57,10,'57:10','และไฉนเล่าพวกเจ้าจึงไม่บริจาคในทางของอัลลอฮฺ ทั้ง ๆ ที่มรดกแห่งชั้นฟ้าทั้งหลายและแผ่นดินนี้เป็นของอัลลอฮฺ ในหมู่พวกเจ้านั้นมีผู้บริจาคและได้ต่อสู้ (ในทางของอัลลอฮฺ) ก่อนการพิชิต(นครมักกะฮฺ) ชนเหล่านั้นย่อมมีฐานะสูงส่งกว่าบรรดาผู้บริจาคและต่อสู้(ในทางของอัลลอฮฺ) หลังการพิชิต(นครมักกะฮฺ) และอัลลอฮฺทรงสัญญาความดีงาม (สวนสวรรค์) แก่ทั้งสองฝ่าย และอัลลอฮฺนั้นทรงรอบรู้อย่างละเอียดในสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(57,11,'57:11','ใครคือผู้ที่จะให้อัลลอฮฺยืม (บริจาคในทางของอัลลอฮฺ) ด้วยการยืมที่ดี แล้วพระองค์ก็จะทรงเพิ่มพูนผลบุญแก่เขา และเขาจะได้รับรางวัลอันมีเกียรติ (สวนสวรรค์)');
INSERT INTO "king_fahad_thai" VALUES(57,12,'57:12','วันที่เจ้าจะเห็นบรรดาผู้ศรัทธาชายและบรรดาผู้ศรัทธาหญิง แสงสว่างของพวกเขาจะฉายแสง ณ เบื้องหน้าของพวกเขา และ ทางเบื้องขวาของพวกเขา (จะมีเสียงกล่าวว่า) วันนี้มีข่าวดีแก่พวกเจ้า คือสวนสวรรค์หลากหลายมีแม่น้ําหลายสายไหลผ่าน ณ เบื้องล่างของมัน โดยพํานักอยู่ในนั้นตลอดกาล นั่นคือชัยชนะ อันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(57,13,'57:13','วันที่พวกมุนาฟิกีนชาย และพวกมุนาฟิกีนหญิงจะกล่าวแก่บรรดาผู้ศรัทธาว่าจงมองมายังเราเถิด เพื่อเราจะได้รับแสงสว่างของพวกท่านด้วย จะมีเสียงกล่าวว่า จงหันหลังกลับไปทางข้างหลังของพวกเจ้าเพื่อแสวงหาเอาเอง (ขณะนั้น) ก็จะมีกําแพงที่มีประตูบานหนึ่งมาขวางกั้นระหว่างพวกเขา ด้านในของมันนั้นมีความเมตตา และด้านนอกของมัน (ของพวกมุนาฟิกีน) มีการลงโทษ');
INSERT INTO "king_fahad_thai" VALUES(57,14,'57:14','พวกเขา (พวกมุนาฟิกีน) จะร้องเรียกเขาทั้งหลาย (บรรดามุอฺมินีน) ว่าพวกเรามิได้เคยอยู่ร่วมกับพวกท่านดอกหรือ? เขาทั้งหลายกล่าวว่า แน่นอน แต่พวกท่านได้ทําลายตัวของพวกท่านด้วยการกลับกลอกและพวกท่านคอยสิ่งที่เกิดขึ้น (แก่บรรดามุอฺมินีน) และพวกท่านสงสัย (ในเรื่องของศาสนา) และความหวังที่เลื่อนลอยได้หลอกลวงพวกท่าน จนกระทั่งพระบัญชาของอัลลอฮฺได้มีมา และมัน (ชัยฎอน) ได้ล่อลวงพวกเจ้าเกี่ยวกับอัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(57,15,'57:15','ดังนั้น วันนี้การไถ่บาปจะไม่ถูกรับจากพวกเจ้า และจากบรรดาผู้ปฏิเสธศรัทธาที่พํานักของพวกเจ้าคือไฟนรกมันเป็นสถานที่อันเหมาะสมแก่พวกเจ้า และมันเป็นทางกลับที่ชั่วร้ายยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(57,16,'57:16','ยังไม่ถึงเวลาอีกหรือสําหรับบรรดาผู้ศรัทธาที่หัวใจของพวกเขาจะนอบน้อมต่อการรําลึกถึงอัลลอฮฺ และสิ่งซึ่งได้มีลงมาคือความจริง และพวกเขาอย่าได้เป็นเช่นบรรดาผู้ได้รับคัมภีร์มาแต่ก่อนนี้ แล้วช่วงเวลาได้เนิ่นนานเกินไปแก่พวกเขา ดังนั้นจิตใจของพวกเขาจึงแข็งกระด้าง และส่วนมากของพวกเขาจึงเป็นผู้ฝ่าฝืน');
INSERT INTO "king_fahad_thai" VALUES(57,17,'57:17','พึงทราบเถิดว่า แท้จริงอัลลอฮนั้นทรงให้แผ่นดินมีชีวิตชีวาหลังจากการแห้งแล้งของมัน แน่นอนเราได้ทําให้สัญญาณทั้งหลายแจ่มแจ้งแก่พวกเจ้าแล้ว เพื่อพวกเจ้าจะได้ใช้สติปัญญาใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(57,18,'57:18','แท้จริงบรรดาผู้บริจาคชายและบรรดาผู้บริจาคหญิง และพวกเขาได้ให้อัลลอฮยืม (บริจาคในหนทางของอัลลอฮ) ด้วยการยืมที่ดี จะมีเพิ่ม (ผลบุญ) แก่พวกเขาและสําหรับพวกเขาจะได้รับรางวัลอันมีเกียรติ');
INSERT INTO "king_fahad_thai" VALUES(57,19,'57:19','และบรรดาผู้ศรัทธาต่ออัลลอฮ. และบรรดาร่อซูลของพระองค์ ชนเหล่านั้นพวกเขาเป็นผู้สัตย์จริง และเป็นผู้เสียสละชีวิต ณ ที่พระเจ้าของพวกเขา สําหรับพวกเขาจะได้รับรางวัลของพวกเขา และแสงสว่างของพวกเขา ส่วนบรรดาผู้ปฏิเสธศรัทธาและปฏิเสธสัญญาณต่าง ๆ ของเรา ชนเหล่านั้นเป็นชาวนรก');
INSERT INTO "king_fahad_thai" VALUES(57,20,'57:20','พึงทราบเถิดว่า แท้จริงการมีชีวิตอยู่ในโลกนี้มิใช่อื่นใด เว้นแต่เป็นการละเล่นและการสนุกสนานร่าเริงและเครื่องประดับและความโอ้อวดระหว่างพวกเจ้า และการแข่งขันกันสะสมในทรัพย์สินและลูกหลาน เปรียบเสมือนเช่น น้ําฝนที่การงอกเงยพืชผลยังความพอใจให้แก่กสิกรแล้วมันก็เหี่ยวแห้งเจ้าจะเห็นมันเป็นสีเหลือง แล้วมันก็กลายเป็นเศษเป็นชิ้นแห้ง ส่วนในวันปรโลกนั้นมีการลงโทษอย่างสาหัส และมีการอภัยโทษและความโปรดปรานจากอัลลอฮ และการมีชีวิตอยู่ในโลกนี้ มิใช่อื่นใดนอกจากการแสวงหาผลประโยชน์แห่งการหลอกลวงเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(57,21,'57:21','จงเร่งรีบไปสู่การขออภัยโทษจากพระเจ้าของพวกเจ้า และสวนสวรรค์ซึ่งความกว้างของมันประหนึ่งความกว้างของชั้นฟ้าและแผ่นดิน (ซึ่งสวรรค์นั้น) ถูกเตรียมไว้สําหรับบรรดาผู้ศรัทธาต่ออัลลอฮฺและบรรดาร่อซูลของพระองค์ นั่นคือความโปรดปรานของอัลลอฮฺ ซึ่งพระองค์จะทรงประทานมันให้แก่ผู้ที่พระองค์ทรงประสงค์ และอัลลอฮฺนั้นทรงเป็นผู้โปรดปรานอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(57,22,'57:22','ไม่มีเคราะห์กรรมอันใดเกิดขึ้นในแผ่นดินนี้ และไม่มีแม้แต่ในตัวของพวกเจ้าเอง เว้นแต่ได้มีไว้ในบันทึกก่อนที่เราจะบังเกิดมันขึ้นมา แท้จริงนั่นมันเป็นการง่ายสําหรับอัลลอฮ');
INSERT INTO "king_fahad_thai" VALUES(57,23,'57:23','เพื่อพวกเจ้าจะได้ไม่ต้องเสียใจต่อสิ่งที่ได้สูญเสียไปจากพวกเจ้า และไม่ดีใจต่อสิ่งที่พระองค์ทรงประทานแก่พวกเจ้า และอัลลอฮมิทรงชอบทุกผู้หยิ่งจองหอง และผู้คุยโวโอ้อวด');
INSERT INTO "king_fahad_thai" VALUES(57,24,'57:24','คือบรรดาผู้ที่ตระหนี่ และสั่งสอนคนอื่นให้ตระหนี่ และผู้ที่ผินหลังให้ แท้จริงอัลลอฮฺนั้นพระองค์ทรงพอเพียง และทรงได้รับการสรรเสริญอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(57,25,'57:25','โดยแน่นอนเราได้ส่งบรรดาร่อซูลของเราพร้อมด้วยหลักฐานทั้งหลายอันชัดแจ้งและเราได้ประทานคัมภีร์และความยุติธรรมลงมาพร้อมกับพวกเขาเพื่อมนุษย์จะได้ดํารงอยู่บนความเที่ยงธรรม แลเราได้ให้มีเหล็กขึ้นมา เพราะในนั้นมีความแข็งแกร่งมาก และมีประโยชน์มากหลายสําหรับมนุษย์ และเพื่ออัลลอฮฺจะได้ทรงรู้ถึงผู้ที่ช่วยเหลือพระองค์ และบรรดาร่อซูลของพระองค์ (มีความเชื่อมั่น) โดยทางลับ (ต่อพระองค์) แท้จริงอัลลอฮฺนั้นเป็นผู้ทรงพลัง ผู้ทรงอํานาจ');
INSERT INTO "king_fahad_thai" VALUES(57,26,'57:26','(ขอสาบานด้วยพระนามของอัลลอฮฺ) โดยแน่นอนเราได้ส่งนูหฺและอิบรอฮีม และเราได้ทําให้ลูกหลานของเขาทั้งสองเป็นนะบี และเราได้ประทานคัมภีร์ (อัตเตารอฮฺ อัซซะบูร อัลอินญีล และอัลกุรอาน) ดังนั้นบางคนในหมู่พวกเขาก็เป็นผู้อยู่ในแนวทางที่ถูกต้อง และส่วนมากในหมู่พวกเขาก็เป็นผู้ฝ่าฝืนหลงทาง');
INSERT INTO "king_fahad_thai" VALUES(57,27,'57:27','แล้วเราก็ได้ส่งบรรดาร่อซูลของเราติดตามร่องรอยของพวกเขา และเราได้ส่งอีซาอิบนฺ มัรยัม ตามมา และเราได้ประทานอินญีลให้แก่เขา และเราได้บันดาลความสงสารและความเมตตาให้เกิดขึ้นในจิตใจของบรรดาผู้ที่เชื่อฟังปฏิบัติตามเขา ส่วนการถือสันโดษนั้น เรามิได้บัญญัติมันขึ้นมาแก่พวกเขา (เว้นแต่) พวกเขาประดิษฐ์มันขึ้นมา เพื่อแสวงหาความโปรดปรานของอัลลอฮฺ แต่พวกเขามิได้เอาใจใส่เท่าที่ควรจะกระทํามัน กระนั้นก็ดีเราก็ได้ประทานรางวัลของพวกเขาแก่บรรดาผู้ศรัทธาในหมู่พวกเขา แต่ส่วนมากของพวกเขาเป็นผู้ฝ่าฝืน');
INSERT INTO "king_fahad_thai" VALUES(57,28,'57:28','โอ้บรรดผู้ศรัทธาเอ๋ย จงยําเกรงต่ออัลลอฮ และจงศรัทธาต่อร่อซูลของพระองค์เถิด พระองค์จะทรงประทานความเมตตาของพระองค์ให้แก่พวกเจ้าสองเท่าและจะทรงให้มีแสงสว่างแก่พวกเจ้าเพื่อพวกเจ้าจะได้ดําเนินชีวิตด้วยมัน และจะทรงอภัยโทษให้แก่พวกเจ้าและอัลลอฮนั้นเป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(57,29,'57:29','ทั้งนี้เพื่อให้อะฮลุลกิตาบจะได้รู้ว่าพวกเขาไม่มีอํานาจเหนือสิ่งใดในความโปรดปรานของอัลลอฮ และแท้จริงความโปรดปรานของอัลลอฮ และแท้จริงความโปรดปรานนั้นอยู่ในเงื้อมพระหัตถของอัลลอฮซึ่งพระองค์จะทรงประทานความโปรดปรานนั้นแก่ผู้ที่พระองค์ทรงประสงค์ และอัลลอฮเป็นผู้ทรงโปรดปรานอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(58,1,'58:1','โดยแน่นอน อัลลอฮฺทรงได้ยินถ้อยคําของสตรีที่กําลังโต้แย้งกับเจ้าในเรื่องสามีของนางและนางได้ร้องทุกข์ต่ออัลลอฮฺ และอัลลอฮฺนั้นทรงได้ยินการตอบโต้ของเจ้าทั้งสอง แท้จริงอัลลอฮฺเป็นผู้ทรงได้ยิน ผู้ทรงรู้เห็นเสมอ');
INSERT INTO "king_fahad_thai" VALUES(58,2,'58:2','บรรดาผู้เปรียบเทียบภรรยาของพวกเขาในหมู่พวกเจ้าว่าเสมือนแม่ของพวกเขานั้นพวกนางมิได้เป็นแม่ของพวกเขา บรรดาแม่ของพวกมิได้เป็นอื่นใดนอกจากเป็นผู้ให้กําเนิดพวกเขาเท่านั้น และแท้จริงพวกเขานั้นกล่าวคําพูดที่น่าเกลียดและกล่าวเท็จ และแท้จริงอัลลอฮนั้นเป็นผู้ทรงอภัยโทษทรงยกโทษให้เสมอ');
INSERT INTO "king_fahad_thai" VALUES(58,3,'58:3','และบรรดาผู้เปรียบเทียบภรรยาของพวกเขาก็เสมือนแม่ของพวกเขานั้น แล้วพวกเขาจะคืนสู่ถ้อยคําที่พวกเขาได้กล่าวไว้ ดังนั้น (สิ่งที่จําเป็นแก่เขาต้องปฏิบัติคือ) การปล่อยทาสหนึ่งคนก่อนที่เขาทั้งสองจะแตะต้องต่อกัน (ร่วมหลับนอน)นั้นคือสิ่งที่พวกเจ้าถูกเตือน เอาไว้ใช้ให้ปฏิบัติและอัลลอฮทรงรอบรู้ยิ่งในสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(58,4,'58:4','ส่วนผู้ที่ไม่สามารถหา (ทาส) ได้ก็ต้องถือศีลอดสองเดือนติดต่อกัน ก่อนที่เขาทั้งสองจะแตะต้องต่อกัน(ร่วมหลับนอน) สําหรับผู้ที่ไม่สามารถจะถือศิลอดได้ ก็ต้องให้อาหารแก่คนยากจนจํานวนหกสิบคนทั้งนี้เพื่อจะให้พวกเจ้าศรัทธาต่ออัลลอฮ และร่อซูลของพระองค์ นั่นคือขอบเขตของอัลลอฮ และสําหรับพวกปฏิเสธศรัทธานั้นจะได้รับการลงโทษอย่างเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(58,5,'58:5','แท้จริงบรรดาผู้ต่อต้านอัลลอฮฺและร่อซูลของพระองค์ พวกเขาจะถูกทําให้อัปยศเช่นเดียวกับบรรดาก่อนหน้าพวกเขาได้ถูกทําให้อัปยศมาก่อนแล้ว และแน่นอนเราได้ประทานอายาตทั้งหลายอันชัดแจ้ง และสําหรับพวกปฏิเสธศรัทธาจะได้รับการลงโทษอย่างน่าอดสู');
INSERT INTO "king_fahad_thai" VALUES(58,6,'58:6','วันที่อัลลอฮฺจะทรงให้พวกเขาทั้งหมดฟื้นคืนชีพขึ้นมา แล้วพระองค์จะทรงแจ้งให้พวกเขาทราบถึงสิ่งที่พวกเขาปฏิบัติไว้ อัลลอฮฺทรงประเมินมันไว้อย่างครบถ้วน แต่พวกเขาลืมมัน และอัลลอฮฺทรงเป็นพยานต่อทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(58,7,'58:7','เจ้าไม่เห็นดอกหรือว่า อัลลอฮฺทรงรอบรู้สิ่งที่อยู่ในชั้นฟ้าทั้งหลาย และสิ่งที่อยู่ในแผ่นดิน การซุบซิบกันในสามคนจะไม่เกิดขั้น เว้นแต่พระองค์จะทรงเป็นที่สี่ของพวกเขาและมันจะไม่เกิดขึ้นในห้าคน เว้นแต่พระองค์ทรงเป็นที่หกของพวกเขา และมันจะไม่เกิดขึ้นน้อยกว่านั้น และจะไม่เกิดขึ้นมากกว่านั้นเว้นแต่พระองค์จะทรงอยู่ร่วมกับพวกเขา ไม่ว่าพวกเขาจะอยู่ในแห่งหนใด แล้วพระองค์ก็จะทรงแจ้งพวกเขาให้ทราบในวันกิยามะฮฺถึงสิ่งที่พวกเขาได้ปฏิบัติไว้ (ในโลกดุนยา) แท้จริงอัลลอฮเป็นผู้ทรงรอบรู้ทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(58,8,'58:8','เจ้าไม่เห็นดอกหรือว่า บรรดาผู้ที่ถูกห้ามจากการซุบซิบกัน แล้วพวกเขาก็กลับไปกระทําในสิ่งที่พวกเขาได้ถูกห้ามเอาไว้ และพวกเขาซุบซิบนินทากันในการทําบาปและการเป็นศัตรูและการฝ่าฝืนท่านร่อซูล และเมื่อพวกเขามาหาเจ้า พวกเขาจะกล่าวทักทายไม่เหมือนกับที่อัลลอฮฺทรงกล่าวทักทายด้วยคําพูดนั้น และพวกเขากล่าวในหมู่พวกเขาว่าทําไมอัลลอฮฺจึงไม่ทรงลงโทษเราตามที่เราได้กล่าวทักทาย (มุฮัมมัด) นรกก็เป็นการพอเพียงแก่พวกเขาแล้ว พวกเขาก็จะถูกเผาไหม้ในนั้นมันเป็นทางกลับที่ชั่วร้ายยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(58,9,'58:9','โอ้บรรดาผู้ศรัทธาเอ๋ย เมื่อพวกเจ้าซุบซิบต่อกัน ก็อย่าได้ซุบซิบกันด้วยการทําบาปและการเป็นศัตรู และการฝ่าฝืนท่านร่อซูลแต่จงซุบซิบกันเพื่อการทําความดี และการยําเกรง พวกเจ้าจงยําเกรงอัลลอฮ ผู้ซึ่งพวกเจ้าจะถูกรวบรวมให้กลับไปหาพระองค์');
INSERT INTO "king_fahad_thai" VALUES(58,10,'58:10','แท้จริงการซุบซิบนินทากันนั้นเป็นการงานของชัยฏอน เพื่อมันจะก่อความเสียใจให้แก่บรรดามุอฺมิน แต่มันจะไม่ให้ร้ายแต่อย่างใดแก่พวกเขา เว้นแต่ด้วยอนุมัติของอัลลอฮฺ และเฉพาะอัลลอฮฺเท่านั้นบรรดามุอฺมินต้องมอบความไว้วางใจ');
INSERT INTO "king_fahad_thai" VALUES(58,11,'58:11','โอ้บรรดาผู้ศรัทธาเอ๋ย เมื่อได้มีเสียงกล่าวแก่พวกเจ้าว่า จงหลีกที่ให้ในที่ชุมนุม พวกเจ้าก็จงหลีกที่ให้เขาเพราะอัลลอฮฺจะทรงให้ที่กว้างขวางแก่พวกเจ้า (ในวันกิยามะฮฺ) และเมื่อมีเสียงกล่าวว่าจงลุกขึ้นยืนจากที่ชุมนุมนั้น พวกเจ้าก็จงลุกขึ้นยืน เพราะอัลลอฮฺจะทรงยกย่องเทอดเกียรติแก่บรรดาผู้ศรัทธาในหมู่พวกเจ้า และบรรดาผู้ได้รับความรู้หลายชั้น และอัลลอฮฺทรงรอบรู้ยิ่งในสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(58,12,'58:12','โอ้บรรดาผู้ศรัทธาเอ๋ย เมื่อพวกเจ้าจะปรึกษาหารือท่านร่อซูลเป็นการส่วนตัว (เป็นการลับ) พวกเจ้าจงบริจาคทานก่อนการปรึกษาหารือของพวกเจ้า นั่นเป็นการกระทําที่ดีสําหรับพวกเจ้าและเป็นการทําจิตใจให้ผ่องแผ้ว หากพวกเจ้าไม่สามารถหามาได้ แท้จริงอัลลอฮเป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(58,13,'58:13','พวกเจ้ากลัวต่อการบริจาคทานก่อนหน้าการปรึกษาหารือเป็นการส่วนตัวของพวกเจ้ากระนั้นหรือ? หากพวกเจ้ามิได้กระทําเช่นนั้น อัลลอฮฺก็ได้ทรงอภัยโทษให้แก่พวกเจ้าแล้ว ดังนั้นพวกเจ้าจงดํารงไว้ซึ่งการละหมาดและบริจาคซะกาต และจงเชื่อฟังภักดีต่ออัลลอฮฺและร่อซูลของพระองค์ และอัลลอฮฺทรงรอบรู้ยิ่งในสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(58,14,'58:14','เจ้ามิเห็นดอกหรือบรรดาผู้ที่เป็นมิตรกับชนกลุ่มหนึ่งซึ่งอัลลอฮฺทรงกริ้วพวกเขา?พวกเขา(มุนาฟิกีน) มิได้เป็นพวกของพวกเจ้า และมิได้เป็นพวกของพวกเขา (ยะฮูด) และพวกเขาสาบานในเรื่องโกหกทั้งนั้น ทั้ง ๆ ที่พวกเรารู้ดีอยู่แล้ว');
INSERT INTO "king_fahad_thai" VALUES(58,15,'58:15','อัลลอฮทรงเตรียมการลงโทษอย่างสาหัสไว้ให้แก่พวกเขาแล้ว แท้จริงพวกเขานั้น สิ่งที่พวกเขากระทําไปมันชั่วช้าจริง ๆ');
INSERT INTO "king_fahad_thai" VALUES(58,16,'58:16','พวกเขาได้ยึดถือเอาการสาบานของพวกเขาเป็นโล่ห์ป้องกัน แล้วพวกเขาก็ขัดขวางผู้คนให้ออกจากทางของอัลลอฮ ดังนั้นสําหรับพวกเขาจะได้รับการลงโทษอย่างน่าอดสู');
INSERT INTO "king_fahad_thai" VALUES(58,17,'58:17','ทรัพย์สมบัติของพวกเขา และลูกหลานของพวกเขาจะไม่ช่วยพวกเขาให้รอดพ้นจากการลงโทษของอัลลอฮไปได้แต่อย่างใดชนเหล่านั้นเป็นชาวนรก พวกเขาจะพํานักอยู่ในนั้นตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(58,18,'58:18','วันที่อัลลอฮจะทรงให้พวกเขาฟื้นคืนชีพขึ้นมาทั้งหมด แล้วพวกเขาก็จะสาบานต่อพระองค์ดังเช่นที่พวกเขาสาบานต่อพระเจ้าและพวกเขาคิดว่า แท้จริงพวกเขาอยู่บนสิ่งหนึ่งแห่งความจริงแล้ว พึงทราบเถิด แท้จริงพวกเขาเป็นผู้โกหก');
INSERT INTO "king_fahad_thai" VALUES(58,19,'58:19','ชัยฎอนมารร้ายได้เข้าไปครอบงําพวกเขาเสียแล้ว มันจึงทําให้พวกเขาลืมการรําลึกถึงอัลลอฮ ชนเหล่านั้นคือบรรดาพรรคพวกของชัยฏอน พึงทราบเถิดว่า แท้จริงพรรคพวกของชัยฏอนนั้น พวกเขาเป็นผู้ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(58,20,'58:20','แท้จริงบรรดาผู้ฝ่าฝืนอัลลอฮฺ และร่อซูลของพระองค์ ชนเหล่านั้นอยู่ในหมู่ผู้อัปยศอดสู');
INSERT INTO "king_fahad_thai" VALUES(58,21,'58:21','อัลลอฮฺ ทรงกําหนดไว้ว่า แน่นอนข้าและร่อซูลของข้าจะมีชัยชนะเหนือกว่า แท้จริงอัลลอฮฺ นั้นเป็นผู้ทรงพลัง ผู้ทรงอํานาจเหนือ');
INSERT INTO "king_fahad_thai" VALUES(58,22,'58:22','เจ้าจะไม่พบหมู่ชนใดที่พวกเขาศรัทธาต่ออัลลอฮฺ และวันปรโลกรักใคร่ชอบพอผู้ที่ต่อต้านอัลลอฮฺ และร่อซูลของพระองค์ ถึงแม้ว่าพวกเขาจะเป็นพ่อของพวกเขา หรือลูกหลานของพวกเขา หรือพี่น้องของพวกเขา หรือเครือญาติของพวกเขาก็ตาม ชนเหล่านั้นอัลลอฮฺได้ทรงบันทึกการศรัทธาไว้ในจิตใจของพวกเขา และได้ทรงเสริมพวกเขาให้มีพลังมากขึ้นด้วยการสนับสนุนพระองค์ และจะทรงให้พวกเขาได้เข้าสวนสวรรค์หลากหลาย มีแม่น้ําหลายสายไหลผ่าน ณ เบื้องล่างของสวนสวรรค์ โดยเป็นผู้พํานักอยู่ในนั้นตลอดกาล อัลลอฮฺทรงโปรดปรานต่อพวกเขาและพวกเขาก็ยินดีปรีดาต่อพระองค์ ชนเหล่านั้นคือพรรคของอัลลอฮฺ พึงรู้เถิดว่า แท้จริงพรรคของอัลลอฮฺนั้น พวกเขาเป็นผู้ประสบความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(59,1,'59:1','สิ่งที่อยู่ในชั้นฟ้าทั้งหลายและสิ่งที่อยู่ในแผ่นดินต่างแซ่ซ้องสดุดีแด่อัลลอฮ.และพระองค์เป็นผู้ทรงอํานาจผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(59,2,'59:2','พระองค์เป็นผู้ทรงให้บรรดาผู้ปฏิเสธศรัทธาในหมู่พวกอะฮฺลุลกิตาบออกจากบ้านเรือนของพวกเขา เป็นครั้งแรกของการถูกไล่ออกเป็นกลุ่มๆ พวกเจ้ามิได้คาดคิดกันเลยว่าพวกเขาจะออกไป (ในสภาพเช่นนั้น) และพวกเขาคิดว่าแท้จริงป้อมปราการของพวกเขานั้นจะป้องกันพวกเขาให้รอดพ้นจากการลงโทษของอัลลอฮฺได้แต่การลงโทษของอัลลอฮฺได้มีมายังพวกเขาโดยมิได้คาดคิดมาก่อนเลย และพระองค์ทรงทําให้ความหวาดกลัวเกิดขึ้นในจิตใจของพวกเขา โดยพวกเขาได้ทําลายบ้านเรือนของพวกเขาด้วยน้ํามือของพวกเขาเอง และด้วยน้ํามือของบรรดามุอฺมิน จงยึดถือเป็นบทเรียนเถิด โอ้ผู้มีสติปัญญาทั้งหลายเอ๋ย');
INSERT INTO "king_fahad_thai" VALUES(59,3,'59:3','และหากมิใช่เพราะอัลลอฮฺได้ทรงกําหนดการเนรเทศแก่พวกเขาแล้ว แน่นอนพระองค์จะทรงลงโทษพวกเขาในโลกนี้ และสําหรับพวกเขาในปรโลกนั้นก็คือการลงโทษด้วยนรก');
INSERT INTO "king_fahad_thai" VALUES(59,4,'59:4','ทั้งนี้ก็เพราะว่าพวกเขาต่อต้านอัลลอฮฺและร่อซูลของพระองค์ และผู้ใดต่อต้านอัลลอฮฺ แท้จริงอัลลอฮฺเป็นผู้ทรงเข้มงวดในการลงโทษ');
INSERT INTO "king_fahad_thai" VALUES(59,5,'59:5','การที่พวกเจ้าโค่นต้นอินทผาลัมหรือปล่อยให้มันยืนไว้บนรากของมันนั้น เนื่องด้วยอนุมัติของอัลลอฮฺ และเพื่อพระองค์จะทําให้บรรดาผู้ฝ่าฝืนได้รับอัปยศ');
INSERT INTO "king_fahad_thai" VALUES(59,6,'59:6','และสิ่งใดที่อัลลอฮฺทรงให้ร่อซูลของพระองค์ยึดมาได้จากพวกเขา(พวกยะฮูด) พวกเจ้ามิได้เหน็ดเหนื่อยด้วยการขี่ม้าหรือขี่อูฐออกไป แต่อัลลอฮฺทรงให้บรรดาร่อซูลของพระองค์มีอํานาจเหนือผู้ที่พระองค์ทรงประสงค์ และอัลลอฮฺเป็นผู้ทรงอานุภาพเหนือทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(59,7,'59:7','และสิ่งใดที่อัลลอฮฺทรงให้ร่อซูลของพระองค์ยึดมาได้จากชาวเมือง(พวกกุฟฟาร)เป็นสิทธิของอัลลอฮฺและร่อซูลและญาติสนิทและเด็กกําพร้าและผู้ขัดสนและผู้เดินทาง เพื่อมันจะมิได้หมุนเวียนอยู่ในระหว่างผู้มั่งมีของพวกเจ้าเท่านั้น และอันใดที่ท่านได้ห้ามพวกเจ้าก็จงละเว้นเสีย พวกเจ้าจงยําเกรงอัลลอฮฺเถิด แท้จริงอัลลอฮฺเป็นผู้ทรงเข้มงวดในการลงโทษ');
INSERT INTO "king_fahad_thai" VALUES(59,8,'59:8','(สิ่งที่ยึดมาได้จากพวกยะฮูด) เป็นของบรรดาผู้อพยพที่ขัดสนซึ่งถูกขับไล่ออกบ้านเกิดเมืองนอนของพวกเขา และทอดทิ้งทรัพย์สินของพวกเขาเพื่อแสวงหาความโปรดปรานจากอัลลอฮฺ และความยินดีของพระองค์และช่วยเหลืออัลลอฮฺ และร่อซูลของพระองค์ ชนเหล่านั้นพวกเขาคือผู้สัตย์จริง');
INSERT INTO "king_fahad_thai" VALUES(59,9,'59:9','และบรรดาผู้ที่ได้ตั้งหลักแหล่งอยู่ที่นครมะดีนะฮ.(ชาวอันศอร)และพวกเขาศรัทธาก่อนหน้าการอพยพของพวกเขา(ชาวมุฮาญิรีน)พวกเขารักใคร่ผู้ที่อพยพมายังพวกเขาและจะไม่พบความต้องการหรือความอิจฉาอยู่ในทรวงอกของพวกเขาในสิ่งที่ได้ถูกประทานให้และให้สิทธิผู้อื่นก่อนตัวของพวกเขาเองถึงแม้ว่าพวกเขายังมีความต้องการอยู่มากก็ตามและผู้ใดปกป้องการตระหนี่ที่อยู่ในตัวของเขาชนเหล่านั้นพวกเขาเป็นผู้ประสบความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(59,10,'59:10','และบรรดาผู้ที่มาหลังจากพวกเขาโดยพวกเขากล่าวว่า ข้าแต่พระเจ้าของเราทรงโปรดอภัยให้แก่เราและพี่น้องของเราผู้ซึ่งได้ศรัทธาก่อนหน้าเรา และขอพระองค์อย่าได้มีการเคียดแค้นเกิดขึ้นในหัวใจของเราต่อบรรดาผู้ศรัทธา ข้าแต่พระเจ้าของเราแท้จริงพระองค์ท่านเป็นผู้ทรงเอ็นดู ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(59,11,'59:11','เจ้ามิเห็นดอกหรือว่าบรรดาผู้กลับกลอกกล่าวแก่พี่น้องของพวกเขาที่ปฏิเสธศรัทธาในหมู่อะฮฺลุลกิตาบว่า หากพวกท่านถูกไล่ออกแน่นอนเราก็จะออกไปพร้อมกับพวกท่านด้วย และเราจะไม่เชื่อฟังปฏิบัติตามผู้ใดเป็นอันขาดเกี่ยวกับพวกท่าน และหากพวกท่านถูกโจมตีแน่นอนเราจะช่วยเหลือพวกท่าน และอัลลอฮฺทรงเป็นพยานว่า แท้จริงพวกเขาเป็นผู้กล่าวเท็จ');
INSERT INTO "king_fahad_thai" VALUES(59,12,'59:12','หากพวกเขาถูกขับไล่ออกไปบรรดาผู้กลับกลอกเหล่านั้นก็จะไม่ออกไปพร้อมกับพวกเขาและถ้าพวกเขาถูกโจมตีบรรดาผู้กลับกลอกก็จะไม่ช่วยเหลือพวกเขา(ยะฮูดบะนีนะฎีร)และหากบรรดาผู้กลับกลอกช่วยเหลือพวกเขาแน่นอนบรรดาผู้กลับกลอกก็จะผินหลังหนีออกไปพวกเขาก็จะไม่ได้รับความช่วยเหลือ');
INSERT INTO "king_fahad_thai" VALUES(59,13,'59:13','แน่นอนพวกเจ้านั้นเป็นที่หวาดเกรงในทรวงอกของพวกเขายิ่งกว่าที่พวกเขามีต่ออัลลอฮฺ ทั้งนี้ก็เพราะว่าพวกเขาเป็นหมู่ชนที่ไม่เข้าใจ');
INSERT INTO "king_fahad_thai" VALUES(59,14,'59:14','พวกเขาทั้งหมดจะไม่ต่อสู้กับพวกเจ้าเว้นแต่ในเมืองที่มีป้อมปราการหรือจากเบื้องหลังของกําแพง การเป็นศัตรูระหว่างพวกเขากันเองนั้นรุนแรงยิ่งนัก เจ้าเข้าใจว่าพวกเขารวมกันเป็นปึกแผ่นแต่(ความจริงแล้ว)จิตใจของพวกเขาแตกแยกกัน ทั้งนี้ก็เพราะว่าพวกเขาเป็นหมู่ชนที่ไม่ใช้สติปัญญาใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(59,15,'59:15','(สภาพของพวกเขา)ประหนึ่งอุปมาสภาพของบรรดา(พวกยิว)ก่อนหน้าพวกเขาเพียงเล็กน้อยซึ่งพวกเขาได้ลิ้มรสผลร้ายแห่งการงานของพวกเขาและการลงโทษอันเจ็บปวดจะได้แก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(59,16,'59:16','ประหนึ่งเช่นชัยฎอนเมื่อมันกล่าวแก่มนุษย์ว่าจงปฎิเสธศรัทธาเถิด ครั้นเมื่อเขาได้ปฎิเสธศรัทธาแล้วมันจะกล่าวว่า แท้จริงฉันขอปลีกตัวออกจากท่าน แท้จริงฉันกลัวอัลลอฮฺ พระเจ้าแห่งสากลโลก');
INSERT INTO "king_fahad_thai" VALUES(59,17,'59:17','ดังนั้นบั้นปลายของพวกเขาทั้งสองคือพวกเขาทั้งสองจะอยู่ในนรกเป็นผู้พํานักอยู่ในนั้นตลอดกาล และนั่นคือการตอบแทนของบรรดาผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(59,18,'59:18','โอ้บรรดาผู้ศรัทธาเอ๋ยพวกเจ้าจงยําเกรงอัลลอฮฺเถิด และทุกชีวิตจงพิจารณาดูว่าอะไรบ้างที่ตนได้เตรียมไว้สําหรับพรุ่งนี้ (วันกิยามะฮฺ) และจงยําเกรงอัลลอฮฺเถิด แท้จริงอัลลอฮฺนั้นทรงรู้ดียิ่งในสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(59,19,'59:19','และพวกเจ้าอย่าได้เป็นเช่นบรรดาผู้ที่ลืมอัลลอฮมิฉะนั้นอัลลอฮจะทรงทําให้พวกเขาลืมตัวของพวกเขาเองชนเหล่านั้นพวกเขาเป็นผู้ฝ่าฝืน');
INSERT INTO "king_fahad_thai" VALUES(59,20,'59:20','บรรดาชาวนรกกับบรรดาชาวสวนสวรรค์นั้นไม่เหมือนกันดอก บรรดาชาวสวนสวรรค์พวกเขาเป็นผู้ได้รับความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(59,21,'59:21','หากเราประทานอัลกุรอานนี้ลงมาบนภูเขาลูกหนึ่ง แน่นอนเจ้าจะเห็นมันนอบน้อมแตกแยกออกเป็นเสี่ยงๆ เนื่องเพราะความกลัวต่ออัลลอฮฺ อุปมาเหล่านี้เราได้ยกมันมาเปรียบเทียบสําหรับมนุษย์เพื่อพวกเขาจะได้พิจารณาใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(59,22,'59:22','พระองค์คืออัลลอฮซึ่งไม่มีพระเจ้าอื่นใดอีกนอกจากพระองค์ผู้ทรงรอบรู้สิ่งเร้นลับและสิ่งเปิดเผยพระองค์คือผู้ทรงกรุณาปรานีผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(59,23,'59:23','พระองค์คืออัลลอฮฺ ซึ่งไม่มีพระเจ้าอื่นใดอีกนอกจากพระองค์ผู้ทรงอํานาจสูงสุด ผู้ทรงบริสุทธิ์ ผู้ทรงความศานติสุข ผู้ทรงคุ้มครองการศรัทธา ผู้ทรงปกปักรักษาความปลอดภัย ผู้ทรงความยิ่งใหญ่ ภูมิใจมหาบริสุทธิ์แด่อัลลอฮฺให้พ้นจากสิ่งที่พวกเขาตั้งภาคีต่อพระองค์');
INSERT INTO "king_fahad_thai" VALUES(59,24,'59:24','พระองค์คืออัลลอฮผู้ทรงสร้างผู้ทรงให้บังเกิดผู้ทรงทําให้เป็นรูปร่างสําหรับพระองค์คือพระนามทั้งหลายอันสวยงามไพเราะสิ่งที่อยู่ในชั้นฟ้าทั้งหลายและแผ่นดินต่างแซ่ซ้องสดุดีพระองค์และพระองค์เป็นผู้ทรงอํานาจผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(60,1,'60:1','โอ้บรรดาผู้ศรัทธาเอ๋ย พวกเจ้าอย่าได้คบศัตรูของข้าและศัตรูของพวกเจ้าเป็นมิตรโดยให้ความรักใคร่แก่พวกเขา และทั้ง ๆ ที่พวกเขาปฏิเสธศรัทธาต่อสิ่งที่มีมายังพวกเจ้าคือสัจธรรม พวกเขาขับไล่ร่อซูลและโดยเฉพาะพวกเจ้า เนื่องเพราะพวกเจ้าศรัทธาต่ออัลลอฮฺพระเจ้าของพวกเจ้า หากพวกเจ้าได้เคยออกไปต่อสู้ในแนวทางของข้า และแสวงหาความโปรดปรานของข้า (ดังนั้นพวกเจ้าอย่าได้คบพวกปฏิเสธศรัทธาเป็นมิตรอื่นของข้า) โดยซ่อนความรักใคร่แก่พวกเขาอย่างลับ ๆ และข้ารู้ดียิ่งในสิ่งที่พวกเจ้าปิดบัง และสิ่งที่พวกเจ้าเปิดเผย และผู้ใดในหมู่พวกเจ้ากระทําเช่นนั้นแน่นอนเขาได้หลงจากทางอันเที่ยงธรรม');
INSERT INTO "king_fahad_thai" VALUES(60,2,'60:2','หากพวกเขามีสถานะดีกว่าพวกเจ้า พวกเขาก็จะแสดงตัวเป็นศัตรูกับพวกเจ้าและจะยื่นมือของพวกเขาไปยังพวกเจ้า (ทําร้าย) และลิ้นของพวกเขาจะกล่าวร้ายสาปแช่งและพวกเขาใคร่ที่จะให้พวกเจ้าเป็นพวกปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(60,3,'60:3','ความสัมพันธ์ทางเครือญาติของพวกเจ้าและลูกหลานของพวกเจ้า จะไม่อํานวยประโยชน์อื่นใดแก่พวกเจ้าในวันกิยามะฮฺ พระองค์จะทรงตัดสินระหว่างพวกเจ้า และอัลลอฮฺทรงสอดส่องในสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(60,4,'60:4','แน่นอนได้มีแบบอย่างอันดีงามสําหรับพวกเจ้าแล้วใน (ตัว) อิบรอฮีม และบรรดา (มุอฺมิน) ผู้ที่อยู่ร่วมกับเขา เมื่อพวกเขากล่าวแก่หมู่ชนของพวกเขาว่า แท้จริงพวกเราขอปลีกตัวจากพวกท่านและสิ่งที่พวกท่านเคารพบูชาอื่นจากอัลลอฮฺ เราขอปฏิเสธศรัทธาต่อ (ศาสนาของ) พวกท่าน และการเป็นศัตรูและการเกลียดชังระหว่างพวกเรากับพวกท่านได้ปรากฎขึ้นแล้ว (และจะคงอยู่) ตลอดไปจนกว่าพวกท่านจะศรัทธาต่ออัลลอฮฺองค์เดียว นอกจากคํากล่าวของอิบรอฮีมแก่บิดาของเขา (ที่ว่า) แน่นอนฉันจะขออภัยโทษให้แก่ท่านทั้งๆ ที่ฉันไม่มีอํานาจอันใดจะช่วยท่าน (ให้พ้นจากการลงโทษ) จากอัลลอฮฺได้ ข้าแต่พระเจ้าของเราแด่พระองค์ท่านเราขอมอบหมายและยังพระองค์ท่านเท่านั้น เราขอลุแก่โทษ และยังพระองค์ท่านเท่านั้นคือการกลับไป');
INSERT INTO "king_fahad_thai" VALUES(60,5,'60:5','ข้าแต่พระเจ้าของเรา ขอพระองค์ทรงอย่าให้เราเป็นที่ทดสอบของบรรดาผู้ปฏิเสธศรัทธา ขอพระองค์ทรงโปรดอภัยให้แก่เรา ข้าแต่พระเจ้าของเรา แท้จริงพระองค์ท่านเป็นผู้ทรงอํานาจ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(60,6,'60:6','โดยแน่นอน ได้มีแบบอย่างอันดีงามในพวกเขาสําหรับพวกเจ้าแล้ว แก่ผู้ที่หวังใน (การตอบแทนของ)อัลลอฮฺและวันสุดท้ายและผู้ใดผินหลังให้ (การศรัทธา) ดังนั้น แท้จริงอัลลอฮฺ คือ ผู้ทรงพอเพียงผู้ทรงได้รับการสรรเสริญ');
INSERT INTO "king_fahad_thai" VALUES(60,7,'60:7','บางทีอัลลอฮฺจะทรงทําให้เกิดความรักใคร่ระหว่างพวกเจ้ากับบรรดาผู้ที่พวกเจ้าถือเป็นศัตรูในหมู่พวกเขา และอัลลอฮฺเป็นผู้ทรงอานุภาพ และอัลลอฮฺเป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(60,8,'60:8','อัลลอฮฺมิได้ทรงห้ามพวกเจ้าเกี่ยวกับบรรดาผู้ที่มิได้ต่อต้านพวกเจ้าในเรื่องศาสนาและพวกเขามิได้ขับไล่พวกเจ้าออกจากบ้านเรือนของพวกเจ้าในการที่พวกเจ้าจะทําความดีแก่พวกเขา และให้ความยุติธรรมแก่พวกเขา แท้จริงอัลลอฮฺทรงรักผู้มีความยุติธรรม');
INSERT INTO "king_fahad_thai" VALUES(60,9,'60:9','แต่ว่าอัลลอฮทรงห้ามพวกเจ้าเกี่ยวกับบรรดาผู้ที่ต่อต้านพวกเจ้าในเรื่องศาสนาและขับไล่พวกเจ้าออกจากบ้านเรือนของพวกเจ้า และช่วยเหลือให้ขับไล่พวกเจ้าในการที่พวกเจ้าจะผูกมิตรกับพวกเขาและผู้ใดผูกมิตรกับพวกเขา ชนเหล่านั้นพวกเขาเป็นผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(60,10,'60:10','โอ้บรรดาผู้ศรัทธาเอ๋ย เมื่อบรรดาหญิงผู้ศรัทธาเป็นผู้ลี้ภัยมาหาพวกเจ้า ก็จงสอบสวนพวกนาง อัลลอฮฺทรงรู้ดียิ่งในการศรัทธาของพวกนาง ครั้นเมื่อพวกเจ้ารู้ว่าพวกนางเป็นผู้ศรัทธา ก็อย่าได้ส่งพวกนางกลับไปยังพวกปฏิเสธศรัทธา พวกนางมิได้เป็นที่อนุมัติแก่พวกเขา และพวกเขาก็มิได้เป็นที่อนุมัติแก่พวกนาง และจงจ่ายคืนให้พวกเขา (สามีเดิมที่เป็นกาเฟร) สิ่งที่พวกเขาได้จ่ายไป (มะฮัร) และไม่เป็นบาปอันใดแก่พวกเจ้าที่จะแต่งงานกับพวกนาง เมื่อพวกเจ้าได้ให้แก่พวกนางซึ่งของหมั้นของพวกนาง และอย่าหน่วงเหนี่ยวพันธะการแต่งงานของบรรดาหญิงผู้ปฏิเสธศรัทธา และจงขอคืนสิ่งที่พวกเจ้าได้ใช้จ่ายไป และให้พวกเขาขอคืนสิ่งที่พวกเขาได้ใช้จ่ายไป นั่นคือข้อตัดสินของอัลลอฮฺ พระองค์ทรงตัดสินในระหว่างพวกเจ้า และอัลลอฮฺเป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(60,11,'60:11','และถ้าคนใดในบรรดาภริยาของพวกเจ้าหนีออกไปสู่พวกปฏิเสธศรัทธาแล้ว ครั้นเมื่อพวกเจ้าสามารถแก้แค้นริบทรัพย์มาได้ ก็จงจ่ายคืนให้แก่บรรดาผู้ที่บรรดาภริยาของพวกเขาหนีไปเท่ากับจํานวนที่พวกเขาได้จ่ายไป และจงยําเกรงอัลลอฮฺ ผู้ซึ่งพวกเจ้าเป็นผู้ศรัทธาต่อพระองค์เถิด');
INSERT INTO "king_fahad_thai" VALUES(60,12,'60:12','โอ้ นะบีเอ๋ย เมื่อบรรดาหญิงผู้ศรัทธาได้มาหาเจ้าโดยพวกนางเพื่อให้ปฏิญาณแก่เจ้าพวกนางจะไม่ตั้งภาคีใด ๆ ต่ออัลลอฮฺ และจะไม่ขโมย และจะไม่ทําชู้ และจะไม่ฆ่าลูก ๆ ของพวกนาง และจะไม่นํามาซึ่งการใส่ร้ายโดยการเสกสรรลูกหลง พ่อให้เป็นลูกของเขา และจะไม่ขัดขืนคําสั่งของเจ้าในเรื่องดีงาม ดังนั้นจงรับการปฏิญาณของพวกนาง และจงขอต่ออัลลอฮฺให้ทรงอภัยแก่พวกนาง แท้จริงอัลลอฮฺเป็นผู้ทรงอภัยผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(60,13,'60:13','โอ้บรรดาผู้ศรัทธาเอ๋ย พวกเจ้าอย่าได้คบกับหมู่ชนที่อัลลอฮฺทรงกริ้วต่อพวกเขาไว้เป็นมิตรสหาย แน่นอนพวกเขาหมดหวังต่อวันปรโลกแล้วเสมือนกับที่พวกปฏิเสธศรัทธาหมดหวังต่อ (การฟื้นคืนชีพของ) ชาวกุบูร');
INSERT INTO "king_fahad_thai" VALUES(61,1,'61:1','สิ่งที่อยู่ในชั้นฟ้าทั้งหลายและสิ่งที่อยู่ในแผ่นดินต่างแซ่ซ้อง สดุดีแด่อัลลอฮฺ และพระองค์ เป็นผู้ทรงอํานาจ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(61,2,'61:2','โอ้บรรดาผู้ศรัทธาเอ๋ย ทําไมพวกเจ้าจึงกล้าพูดในสิ่งที่พวกเจ้าไม่ปฏิบัติ');
INSERT INTO "king_fahad_thai" VALUES(61,3,'61:3','เป็นที่น่าเกลียดยิ่งที่อัลลอฮฺ การที่พวกเจ้าพูดในสิ่งที่พวกเจ้าไม่ปฏิบัติ');
INSERT INTO "king_fahad_thai" VALUES(61,4,'61:4','แท้จริง อัลลอฮฺทรงรักบรรดาผู้ที่ต่อสู้ในทางของพระองค์เป็นแถวเดียวกัน ประหนึ่งพวกเขาเป็นอาคารที่ยึดมั่นแข็งแรง');
INSERT INTO "king_fahad_thai" VALUES(61,5,'61:5','และจงรําลึกเมื่อมูซาได้กล่าวแก่หมู่ชนของเขาว่า โอ้หมู่ชนของฉันเอ๋ย ทําไมพวกท่านจึงทําร้ายฉัน และแน่นอนพวกท่านรู้แล้วมิใช่ หรือว่าแท้จริงฉันเป็นร่อซูลของอัลลอฮฺมายังพวกท่าน ดังนั้นเมื่อพวกเขาหันเหไป (จากแนวทาง ที่เที่ยงตรง) อัลลอฮฺก็ทรงทําให้หัวใจของพวกเขาหันเหออกไป และอัลลอฮฺนั้นจะไม่ชี้แนะ ทางที่ถูกต้องแก่หมู่ชนผู้ฝ่าฝืน');
INSERT INTO "king_fahad_thai" VALUES(61,6,'61:6','และจงรําลึก เมื่ออีซา อิบนฺ มัรยัม ได้กล่าวว่า โอ้วงศ์วานของอิสรออีลเอ๋ย แท้จริงฉันเป็นร่อซูลของอัลลอฮฺมายังพวกท่าน เป็นผู้ยืนยันสิ่งที่มีอยู่ในเตารอฮฺก่อนหน้าฉันและเป็นผู้แจ้งข่าวดีถึงร่อซูลคนหนึ่งผู้จะมาภายหลังฉัน ชื่อของเขาคือ อะหมัด ครั้นเมื่อเขา (อะหมัด) ได้มายังพวกเขาพร้อมด้วยหลักฐานอันชัดแจ้งแล้ว พวกเขากล่าวว่านี่คือมายากลแท้ ๆ');
INSERT INTO "king_fahad_thai" VALUES(61,7,'61:7','และผู้ใดเล่าจะอธรรมยิ่งกว่าผู้กล่าวเท็จต่ออัลลอฮฺ ด้วยปากของพวกเขาขณะที่เขาถูกเชิญชวนสู่อิสลาม และอัลลอฮฺนั้นจะไม่ชี้แนะทางที่ถูกต้องแก่หมู่ชนผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(61,8,'61:8','พวกเขาปรารถนาที่จะดับรัศมีของอัลลอฮฺด้วยปากของพวกเขา แต่อัลลอฮฺเป็นผู้ทําให้รัศมีของพระองค์สมบูรณ์ แม้ว่าพวกปฏิเสธศรัทธาจะเกลียดชังก็ตาม');
INSERT INTO "king_fahad_thai" VALUES(61,9,'61:9','พระองค์คือผู้ทรงส่งร่อซูลของพระองค์ด้วยการชี้นําทางและศาสนาแห่งสัจธรรมเพื่อพระองค์จะทรงให้ศาสนาอิสลามอยู่เหนือศาสนาทั้งมวล ถึงแม้พวกตั้งภาคีจะเกลียดชังก็ตาม');
INSERT INTO "king_fahad_thai" VALUES(61,10,'61:10','โอ้บรรดาผู้ศรัทธาเอ๋ย ข้าจะชี้แนะแนวทางแก่พวกเจ้าไหมเล่า ถึงการค้าที่จะช่วยพวกเจ้าให้พ้นจากการลงโทษอันเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(61,11,'61:11','นั้นคือพวกเจ้าต้องศรัทธาต่ออัลลอฮฺและร่อซูลของพระองค์ และต่อสู้ดิ้นรนในทางอัลลอฮฺ ด้วยทรัพย์สินของพวกเจ้าและชีวิตของพวกเจ้า นั่นเป็นการดียิ่งสําหรับพวกเจ้าหากพวกเจ้ารู้');
INSERT INTO "king_fahad_thai" VALUES(61,12,'61:12','พระองค์จะทรงอภัยให้แก่พวกเจ้า ซึ่งการทําบาปของพวกเจ้า และจะทรงให้พวกเจ้าเข้าในสวนสวรรค์หลากหลาย มีลําน้ําหลายสายไหลผ่าน ณ เบื้องล่างของสวนสวรรค์ และที่พํานักอันบรมสุขในสวนสวรรค์หลากหลายอันสถาพร นั่นคือชัยชนะอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(61,13,'61:13','และสิ่งอื่น ๆ อีกที่พวกเจ้ารักชอบมันการช่วยเหลือจากอัลลอฮฺและการพิชิตอันใกล้นี้และจงแจ้งข่าวดีแด่บรรดาผู้ศรัทธาเถิด');
INSERT INTO "king_fahad_thai" VALUES(61,14,'61:14','โอ้บรรดาผู้ศรัทธาเอ๋ย ! จงเป็นผู้ช่วยเหลือในทางของอัลลอฮฺ ดังเช่น อีซา อิบนฺมัรยัมได้กล่าวแก่บรรดาสาวกว่า ผู้ใดจะเป็นผู้ช่วยเหลือฉันไปยังอัลลอฮฺบ้าง? บรรดาสาวกได้กล่าวว่า พวกเราเป็นผู้ช่วยเหลือในทางของอัลลอฮฺ ดังนั้นกลุ่มหนึ่งจากวงศ์วารของอิสรออีลได้ศรัทธา และอีกกลุ่มหนึ่งได้ปฏิเสธศรัทธา แต่เราได้ช่วยเสริมสร้างกําลังแก่บรรดาผู้ศรัทธาให้เหนือกว่าศัตรูของพวกเขา แล้วพวกเขาก็กลายเป็นผู้มีชัยชนะ');
INSERT INTO "king_fahad_thai" VALUES(62,1,'62:1','สิ่งที่อยู่ในชั้นฟ้าทั้งหลาย และสิ่งที่อยู่ในแผ่นดินต่างแซ่ซ้องสดุดีแด่อัลลอฮฺ ผู้ทรงอภิสิทธิ์ ผู้ทรงบริสุทธิ์ ผู้ทรงอํานาจ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(62,2,'62:2','พระองค์ทรงเป็นผู้แต่งตั้งร่อซูลขึ้นคนหนึ่งในหมู่ผู้ไม่รู้จักหนังสือจากพวกเขาเองเพื่อสาธยายอายาตต่าง ๆ ของพระองค์แก่พวกเขา และทรงทําให้พวกเขาผุดผ่อง และทรงสอนคัมภีร์และความสุขุมคัมภีร์ภาพแก่พวกเขา และแม้ว่าแต่ก่อนนี้พวกเขาอยู่ในการหลงผิดอย่างชัดแจ้งก็ตาม');
INSERT INTO "king_fahad_thai" VALUES(62,3,'62:3','และกลุ่มชนอื่น ๆ ในกลุ่มพวกเขาที่จะติดตามมาภายหลังจากพวกเขา และพระองค์เป็นผู้ทรงอํานาจ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(62,4,'62:4','นั่นคือความโปรดปรานของอัลลอฮฺทรงประทานความโปรดปรานนั้นแก่ผู้ที่พระองค์ทรงประสงค์ และอัลลอฮฺนั้นเป็นผู้มีบุญคุณอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(62,5,'62:5','อุปมาบรรดาผู้ที่ได้รับคัมภีร์เตารอฮฺแล้วพวกเขามิได้ปฎิบัติตามที่พวกเขาได้รับมอบประหนึ่งเช่นกับลาที่แบกหนังสือจํานวนหนึ่ง (บนหลังของมัน) อุปมาหมู่ชนที่ปฏิเสธต่อสัญญาณต่าง ๆ ของอัลลอฮฺมันช่างชั่วช้าจริง ๆและอัลลอฮฺจะไม่ชี้แนะทางแก่หมู่ชนผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(62,6,'62:6','จงกล่าวเถิดมุฮัมมัด โอ้บรรดายิวเอ๋ย หากพวกท่านอ้างว่าพวกท่านเป็นมิตรของอัลลอฮฺอื่นจากมนุษย์ทั้งหลายแล้วไซร์ ดังนั้นพวกท่านก็จงมุ่งหวังความตายกันเถิด หากพวกท่านเป็นผู้สัตย์จริง');
INSERT INTO "king_fahad_thai" VALUES(62,7,'62:7','แต่ว่าพวกเขาจะไม่มุ่งหวังมันเป็นอันขาด เนื่องด้วยสิ่งที่มือของพวกเขาได้ประกอบ (กรรมชั่ว) ไว้ก่อน และอัลลอฮฺทรงรอบรู้พวกอธรรมทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(62,8,'62:8','จงกล่าวเถิดมุฮัมมัด แท้จริงความตายที่พวกท่านหนีจากมันไปนั้น มันจะมาพบกับพวกท่าน แล้วพวกท่านจะถูกนํากลับไปยังพระผู้ทรงรอบรู้สิ่งเร้นลับและสิ่งเปิดเผย แล้วพระองค์จะทรงแจ้งแก่พวกท่านตามที่พวกท่านได้ประกอบกรรมไว้');
INSERT INTO "king_fahad_thai" VALUES(62,9,'62:9','โอ้บรรดาผู้ศรัทธาเอ๋ย เมื่อได้มีเสียงร้องเรียก (อะซาน) เพื่อทําละหมาดในวันศุกร์ก็จงรีบเร่งไปสู่การรําลึกถึงอัลลอฮฺ และจงละทิ้งการค้าขายเสีย นั่นเป็นการดีสําหรับพวกเจ้าหากพวกเจ้ารู้');
INSERT INTO "king_fahad_thai" VALUES(62,10,'62:10','ต่อเมื่อการละหมาดได้สิ้นสุดลงแล้ว ก็จงแยกย้ายกันไปตามแผ่นดิน และจงแสวงหาความโปรดปรานของอัลลอฮฺและจงรําลึกถึงอัลลอฮฺให้มาก ๆ เพื่อว่าพวกเจ้าจะได้รับชัยชนะ');
INSERT INTO "king_fahad_thai" VALUES(62,11,'62:11','และเมื่อพวกเขาได้เห็นการค้าและการละเล่นพวกเขาก็กรูกันไปที่นั้น และปล่อยเจ้าให้ยืนอยู่คนเดียว จงกล่าวเถิดมุฮัมมัดสิ่งที่มีอยู่ ณ อัลลอฮฺนั้นดีกว่าการละเล่น และการค้าและอัลลอฮฺนั้นทรงเป็นเลิศ ยิ่งในหมู่ผู้ประทานปัจจัยยังชีพ');
INSERT INTO "king_fahad_thai" VALUES(63,1,'63:1','เมื่อพวกสับปลับ (มุนาฟินกูน) มาหาเจ้า พวกเขากล่าวว่า เราขอปฏิญาณว่า แท้จริงท่านเป็นร่อซูลของอัลลอฮฺแต่อัลลอฮฺทรงรู้ดียิ่งว่า แท้จริงเจ้านั้นเป็นร่อซูลของพระองค์อย่างแน่นอน และอัลลอฮฺทรงเป็นพยานว่า แท้จริงพวกมุนาฟิกีนนั้นเป็นผู้กล่าวเท็จอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(63,2,'63:2','พวกเขาได้ถือเอาการสาบานของพวกเขาเป็นโล่ห์แล้วก็กีดกันจากทางของอัลลอฮฺ แท้จริง สิ่งที่พวกเขาได้กระทําไปนั้นช่างชั่วช้าจริง ๆ');
INSERT INTO "king_fahad_thai" VALUES(63,3,'63:3','นั่นเป็นเพราะพวกเขาศรัทธาแล้วก็ปฏิเสธศรัทธา ดังนั้นจึงถูกผนึกบนหัวใจของพวกเขาแล้วพวกเขาก็ไม่เข้าใจ');
INSERT INTO "king_fahad_thai" VALUES(63,4,'63:4','และเมื่อเจ้าได้เห็นพวกเขาแล้วร่างกายของพวกเขาจะเป็นที่พึงพอใจแก่เจ้า และหากพวกเขาพูด เจ้าก็จะฟังคําพูดของพวกเขา ประหนึ่งว่าพวกเขาเป็นท่อนไม้ที่ถูกยันไว้ พวกเขาคิดว่าทุก ๆ เสียงร้องนั้นเป็นปฏิปักษ์ต่อพวกเขา พวกเขาคือศัตรู ดังนั้นจงระวังพวกเขา ขออัลลอฮฺทรงให้ความอัปยศแก่พวกเขา ทําไมเล่าพวกเขาจึงหันเหออกไปทางอื่น');
INSERT INTO "king_fahad_thai" VALUES(63,5,'63:5','และเมื่อได้มีเสียงกล่าวแก่พวกเขาว่า จงมาเถิดร่อซูลของอัลลอฮฺ จงขออภัยโทษให้แก่พวกท่าน พวกเขาก็หันศีรษะของพวกเขาไปอีกทางหนึ่ง และเจ้าเห็นพวกเขาผินหลังออกไปทั้ง ๆ ที่พวกเขาเป็นผู้หยิ่งผยอง');
INSERT INTO "king_fahad_thai" VALUES(63,6,'63:6','มีผลเท่ากันแก่พวกเขาที่เจ้าจะขออภัยโทษให้แก่พวกเขาหรือไม่ขออภัยโทษแก่พวกเขาก็ตาม อัลลอฮฺจะไม่ทรงอภัยโทษให้แก่พวกเขา แท้จริงอัลลอฮฺจะไม่ทรงชี้แนะทางแก่หมู่ชนผู้ฝ่าฝืน');
INSERT INTO "king_fahad_thai" VALUES(63,7,'63:7','พวกเขาคือผู้ที่กล่าวว่า อย่าบริจาคให้แก่ผู้ที่อยู่กับร่อซูลของอัลลอฮฺ เพื่อพวกเขาจะได้แยกย้ายออกไป แต่ขุมคลังแห่งชั้นฟ้าทั้งหลายและแผ่นดินนั้นเป็นของอัลลอฮฺ แต่ว่าพวกมุนาฟิกีนไม่เข้าใจ');
INSERT INTO "king_fahad_thai" VALUES(63,8,'63:8','พวกเขากล่าวว่า ถ้าเรากลับไปยังนครมะดีนะฮฺ พวกที่มีเกียรติกว่าจะขับไล่พวกที่ต่ําต้อยออกจากนครมะดีนะฮฺส่วนอํานาจนั้นเป็นของอัลลอฮฺ และร่อซูลของพระองค์และบรรดาผู้ศรัทธา แต่ว่าพวกมุนาฟิกีนนั้นหารู้ไม่');
INSERT INTO "king_fahad_thai" VALUES(63,9,'63:9','โอ้บรรดาผู้ศรัทธาเอ๋ย อย่าให้ทรัพย์สินของพวกเจ้าและลูกหลานของพวกเจ้าหันเหพวกเจ้าจากการรําลึกถึงอัลลอฮฺ และผู้ใดกระทําเช่นนั้น ชนเหล่านั้นคือพวกที่ขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(63,10,'63:10','และจงบริจาคจากสิ่งที่เราได้ให้ปัจจัยยังชีพแก่พวกเจ้าก่อนที่ความตายจะเกิดขึ้นแก่ผู้ใดในหมู่พวกเจ้า แล้วเขาก็จะกล่าวว่า ข้าแต่พระเจ้าของข้าพระองค์ มาตรว่าพระองค์ท่านทรงผ่อนผันให้แก่ข้าพระองค์อีกชั่วเวลาเพียงเล็กน้อยเพื่อที่ข้าพระองค์จะได้บริจาคและข้าพระองค์ก็จะอยู่ในหมู่คนดีทั้งหลาย (ผู้ทรงคุณธรรม)');
INSERT INTO "king_fahad_thai" VALUES(63,11,'63:11','แต่อัลลอฮฺจะไม่ทรงผ่อนผันให้แก่ชีวิตใด เมื่อกําหนดของมันได้มาถึงแล้ว และอัลลอฮฺนั้นทรงรู้ดียิ่งในสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(64,1,'64:1','สิ่งที่อยู่ในชั้นฟ้าทั้งหลาย และสิ่งที่อยู่ในแผ่นดินต่างแซ่ซ้องสดุดีแด่อัลลอฮฺ อํานาจเด็ดขาดเป็นของพระองค์ มวลการสรรเสริญเป็นของพระองค์ และพระองค์เป็นผู้ทรงอานุภาพเหนือทุกสิ่งอย่าง');
INSERT INTO "king_fahad_thai" VALUES(64,2,'64:2','พระองค์คือผู้ทรงสร้างพวกเจ้า ดังนั้นในหมู่พวกเจ้ามีผู้ปฏิเสธศรัทธา และในหมู่พวกเจ้ามีผู้ศรัทธา และอัลลอฮฺทรงรู้เห็นในสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(64,3,'64:3','พระองค์ทรงสร้างชั้นฟ้าทั้งหลายและแผ่นดินด้วยความจริง และทรงทําให้พวกเจ้าเป็นรูปร่างและทรงทําให้รูปร่างของพวกเจ้าสวยงามยิ่ง และยังพระองค์เท่านั้นคือทางกลับ');
INSERT INTO "king_fahad_thai" VALUES(64,4,'64:4','พระองค์ทรงรอบรู้สิ่งที่อยู่ในชั้นฟ้าทั้งหลายและแผ่นดิน และทรงรอบรู้สิ่งที่พวกเจ้าปกปิดและสิ่งที่พวกเจ้าเปิดเผย และอัลลอฮฺทรงรอบรู้สิ่งที่อยู่ในทรวงอก');
INSERT INTO "king_fahad_thai" VALUES(64,5,'64:5','ข่าวคราวของบรรดาผู้ปฏิเสธศรัทธาในอดีต มิได้มีมายังพวกเจ้าดอกหรือ? พวกเขาได้ลิ้มรสการลงโทษแห่งกิจกรรมของพวกเขา (ในโลกดุนยา) และการลงโทษอันเจ็บปวดจะได้แก่พวกเขา (ในโลกอาคิเราะฮฺ)');
INSERT INTO "king_fahad_thai" VALUES(64,6,'64:6','นั่นเป็นเพราะว่าบรรดาร่อซูลของพวกเขาได้มายังพวกเขาพร้อมด้วยหลักฐานอันชัดแจ้งแต่พวกเขาได้กล่าวว่าสามัญชนเช่นนี้นะหรือจะชี้แนะทางให้แก่เรา พวกเขาได้ปฏิเสธศรัทธา และผินหลังให้แต่อัลลอฮฺทรงพอเพียง(จากการศรัทธาของพวกเขา) เพราะอัลลอฮฺเป็นผู้ทรงมั่งมีอย่างเหลือหลายเป็นผู้ทรงได้รับการสรรเสริญ');
INSERT INTO "king_fahad_thai" VALUES(64,7,'64:7','บรรดาผู้ปฏิเสธศรัทธากล่าวอ้างว่าพวกเขาจะไม่ถูกให้ฟื้นคืนชีพขึ้นมาอีกจงกล่าวเถิดมุฮัมมัดหาเป็นเช่นนั้นไม่ !ขอสาบานต่อพระเจ้าของข้าพระองค์ พวกท่านจะถูกให้ฟื้นคืนชีพขึ้นมาอีกอย่างแน่นอน แล้วพวกท่านจะได้รับแจ้งตามที่พวกท่านได้ประกอบกรรมไว้ และนั่นเป็นการง่ายดายสําหรับอัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(64,8,'64:8','ดังนั้นจงศรัทธาต่ออัลลอฮฺและร่อซูลของพระองค์ และแสงสว่าง (อัลกุรอาน) ซึ่งในสิ่งที่พวกเจ้ากระทํา');
INSERT INTO "king_fahad_thai" VALUES(64,9,'64:9','วันที่พระองค์จะทรงรวบรวมพวกเจ้า เพื่อวันแห่งการชุมนุม นั่นคือวันแห่งชัยชนะ (สําหรับมุอฺมินา) และขาดทุน(สําหรับกาฟิร) ส่วนผู้ได้ศรัทธาต่ออัลลอฮฺ และกระทําความดี พระองค์จะทรงลบล้างความชั่วทั้งหลายของเขาออกไปจากเขา และจะทรงให้เขาเข้าสวนสวรรค์หลากหลาย ณ เบื้องล่างของสวนสวรรค์นั้นมีลําน้ําหลายสายไหลผ่านพวกเขาเป็นผู้พํานักอยู่ในนั้นตลอดการ นั่นคือความสําเร็จอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(64,10,'64:10','ส่วนบรรดาผู้ปฏิเสธศรัทธา และปฏิเสธต่อสัญญาณต่าง ๆ ของเขา ชนเหล่านั้นคือชาวนรก พวกเขาเป็นผู้พํานักอยู่ในนั้นและมันเป็นทางกลับที่ชั่วช้ายิ่ง');
INSERT INTO "king_fahad_thai" VALUES(64,11,'64:11','ไม่มีทุกข์ภัยอันใดเกิดขึ้น เว้นแต่ด้วยอนุมัติของอัลลอฮฺ และผู้ใดศรัทธาต่ออัลลอฮฺพระองค์จะทรงเปิดหัวใจของเขา (สู่แนวทางที่ถูกต้อง) และอัลลอฮฺเป็นผู้ทรงรอบรู้ทุกสิ่งอย่าง');
INSERT INTO "king_fahad_thai" VALUES(64,12,'64:12','และจงเชื่อฟังปฏิบัติตามอัลลอฮฺและจงเชื่อฟังปฏิบัติตามอัลร่อซูล หากพวกเจ้าผินหลังกลับ (ไม่เชื่อฟัง) ดังนั้นหน้าที่ของร่อซูลของเราก็คือการเผยแผ่อันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(64,13,'64:13','อัลลอฮฺ ไม่มีพระเจ้าอื่นใดนอกจากพระองค์ ดังนั้นบรรดาผู้ศรัทธาทั้งหลายจงมอบหมายไว้วางใจต่ออัลลอฮฺเถิด');
INSERT INTO "king_fahad_thai" VALUES(64,14,'64:14','โอ้บรรดาผู้ศรัทธาเอ๋ย แท้จริงในหมู่คู่ครองของพวกเจ้าและลูกหลานของพวกเจ้านั้นมีบางคนเป็นศัตรูแก่พวกเจ้าฉะนั้นจงระวังต่อพวกเขา แต่ถ้าพวกเจ้าอภัยและยกโทษ (แก่พวกเขา) แท้จริงอัลลอฮฺนั้นเป็นผู้ทรงอภัย ผู้เมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(64,15,'64:15','แท้จริงทรัพย์สมบัติของพวกเจ้า และลูกหลานของพวกเจ้านั้นเป็นเครื่องทดสอบและอัลลอฮฺนั้น ณ ที่พระองค์มีรางวัลอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(64,16,'64:16','ดังนั้นจงยําเกรงอัลลอฮฺเถิด เท่าที่พวกเจ้ามีความสามารถ และจงเชื่อฟังและปฏิบัติตามและบริจาคเถิดเพราะเป็นการดียิ่งสําหรับตัวของพวกเจ้า และผู้ใดถูกปกป้องให้พ้นจากความตระหนี่แห่งจิตใจของเขา ชนเหล่านั้นพวกเขาเป็นผู้ประสบความสําเร็จ');
INSERT INTO "king_fahad_thai" VALUES(64,17,'64:17','หากพวกเจ้าให้อัลลอฮฺยืมอย่างดีเยี่ยมแล้ว พระองค์ก็จะทรงทวีการยืมให้แก่พวกเจ้า และจะทรงอภัยให้แก่พวกเจ้า และอัลลอฮฺนั้นเป็นผู้ทรงตอบแทนแก่ผู้กระทําความดีทรงผ่อนผันการลงโทษ');
INSERT INTO "king_fahad_thai" VALUES(64,18,'64:18','ผู้ทรงรอบรู้สิ่งเร้นลับ และสิ่งที่เปิดเผย ผู้ทรงอํานาจ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(65,1,'65:1','โอ้นะบีเอ๋ย เมื่อพวกเจ้าอย่าภริยาก็จงหย่าพวกนางตามกําหนด (อิดดะฮฺ) ของพวกนางและจงนับกําหนดอิดดะฮฺให้ครบ พวกเจ้าจงยําเกรงอัลลอฮฺ พระเจ้าของพวกเจ้าเถิดอย่าขับไล่พวกนางออกจากบ้านของพวกนาง และพวกนางก็อย่าออกจากบ้านเว้นแต่พวกนางจะกระทําลามกอย่างชัดแจ้ง และเหล่านี้คือข้อกําหนดของอัลลอฮฺ และผู้ใดละเมิดข้อกําหนดของอัลลอฮฺ แน่นอนเขาก็ได้อธรรมแก่ตัวของเขาเอง เจ้าไม่รู้ดอกว่าบางทีอัลลอฮฺจะทรงปรับปรุงกิจการ (ของเขา) หลังจากนั้น');
INSERT INTO "king_fahad_thai" VALUES(65,2,'65:2','ต่อเมื่อพวกนางได้อยู่จนครบกําหนดของพวกนางแล้ว ก็จงยับยั้งพวกนางให้อยู่โดยดี หรือให้พวกนางจากไปโดยดี และจงให้มีพยานสองคนเป็นผู้เที่ยงธรรมในหมู่พวกเจ้า และจงให้การเป็นพยานนั้นเป็นไปเพื่ออัลลอฮฺ ดังกล่าวมานั้นผู้ที่ศรัทธาต่ออัลลอฮฺและวันอาคิเราะฮฺจะถูกตักเตือนให้ถือปฏิบัติและผู้ใดยําเกรงอัลลอฮฺ พระองค์ก็จะทรงหาทางออกให้แก่เขา');
INSERT INTO "king_fahad_thai" VALUES(65,3,'65:3','และจะทรงประทานปัจจัยยังชีพแก่เขาจากที่ที่เขามิได้คาดคิด และผู้ใดมอบหมายแด่อัลลอฮฺ พระองค์ก็จะทรงเป็นผู้พอเพียงแก่เขา แท้จริงอัลลอฮฺเป็นผู้ทรงบรรลุในกิจการของพระองค์โดยแน่นอนสําหรับทุกสิ่งอย่างนั้นอัลลอฮฺทรงกําหนดกฎสภาวะไว้แล้ว');
INSERT INTO "king_fahad_thai" VALUES(65,4,'65:4','ส่วนบรรดาผู้หญิงในหมู่ภริยาของพวกเจ้าที่หมดหวังในการมีระดู หากพวกเจ้ายังสงสัย (ในเรื่องอิดดะฮฺของนาง) ดังนั้นพึงรู้เถิดว่าอิดดะฮิของพวกนางคือสามเดือน และบรรดาผู้หญิงที่มิได้มีระดูก็เช่นกัน ส่วนบรรดาผู้ทีครรภ์กําหนดของพวกนางก็คือพวกนางจะคลอดทารกที่อยู่ครรภ์ของพวกนาง และผู้ใดยําเกรงต่ออัลลอฮฺ พระองค์จะทรงทําให้กิจการของเขาสะดวกง่ายดายแก่เขา');
INSERT INTO "king_fahad_thai" VALUES(65,5,'65:5','นั่นคือพระบัญชาของอัลลอฮฺซึ่งพระองค์ได้ทรงประทานพระบัญชานั้นแก่พวกเจ้าและผู้ใดยําเกรงอัลลอฮฺ พระองค์จะทรงลบล้างความชั่วทั้งหลายของเขาออกไปจากเขาและจะทรงเพิ่มรางวัลให้มากขึ้นแก่เขา');
INSERT INTO "king_fahad_thai" VALUES(65,6,'65:6','จงให้พวกนางพํานักอยู่ ณ ที่พวกเจ้า และอย่าทําอันตรายพวกนางเพื่อให้เกิดการคับแค้นแก่พวกนาง และหากพวกนางตั้งครรภ์ก็จงเลี้ยงดูพวกนาง จนกว่าพวกนางจะคลอดทารกที่อยู่ในครรภ์ของพวกนาง ครั้นเมื่อพวกนางได้ให้นมแก่ทารกของพวกเจ้า ก็จงให้พวกนางซึ่งค่าตอบแทนของพวกนาง และจงปรึกษาหารือระหว่างพวกเจ้าด้วยกันโดยดี และเมื่อพวกเจ้าตกลงกันไม่ได้ ก็จงให้หญิงอื่นให้นมแก่เด็กนั้น');
INSERT INTO "king_fahad_thai" VALUES(65,7,'65:7','ควรให้ผู้มีฐานะร่ํารวยจ่ายตามฐานะของเขา ส่วนผู้ที่การยังชีพของเขาเป็นที่คับแค้นแก่เขาก็ให้เขาจ่ายตามที่อัลลอฮฺทรงประทานมาให้แก่เขา อัลลอฮฺมิได้ทรงให้เป็นที่ลําบากแก่ชีวิตใด เว้นแต่ตามที่พระองค์ทรงประทานมาแก่ชีวิตนั้น หลังจากความยากลําบาก อัลลอฮฺจะทรงทําให้เกิดความสะดวกสบาย');
INSERT INTO "king_fahad_thai" VALUES(65,8,'65:8','มีชาวเมืองกี่มากน้อยแล้วที่ฝ่าฝืนพระบัญชาของพระเจ้าของพวกเขา และบรรดาร่อซูลของพระองค์เราได้ชําระพวกเขาด้วยการชําระอย่างเข้มงวด และเราได้ลงโทษพวกเขาด้วยการลงโทษอย่างหนัก');
INSERT INTO "king_fahad_thai" VALUES(65,9,'65:9','ดังนั้นพวกเขาจึงได้ลิ้มรสผลร้ายแห่งกิจกรรมของพวกเขา และบั้นปลาย แห่งกิจกรรมของพวกเขา คือการขาดทุนความหายนะ');
INSERT INTO "king_fahad_thai" VALUES(65,10,'65:10','อัลลอฮฺทรงเตรียมการลงโทษอย่างหนักไว้สําหรับพวกเขาแล้ว ดังนั้นจงยําเกรงอัลลอฮฺเถิด โอ้บรรดาผู้มีสติปัญญาที่ศรัทธาเอ๋ย เพราะแน่นอนอัลลอฮฺได้ทรงประทานข้อเตือนสติ (อัลกุรอาน) ลงมาให้แก่พวกเจ้าแล้ว');
INSERT INTO "king_fahad_thai" VALUES(65,11,'65:11','(และ) ร่อซูลท่านหนึ่ง (มุฮัมมัด) มาสาธยายอายาตต่าง ๆ อันชัดแจ้งของอัลลอฮฺแก่พวกเจ้าเพื่อจะได้นําบรรดาผู้ศรัทธาและกระทําความดีทั้งหลายออกจากความมืดทึบทั้งมวลสู่แสงสว่าง ส่วนผู้ใดศรัทธาต่ออัลลอฮฺและกระทําความดี พระองค์จะทรงให้เขาเข้าสวนสวรรค์หลากหลาย ณ เบื้องล่างสวนสวรรค์นั้นมีลําน้ําหลายสายไหลผ่าน พวกเขาเป็นผู้พํานักอยู่ในนั้นตลอดกาล แน่นอนได้ทรงจัดปัจจัยยังชีพอย่างดีเลิศไว้ให้แก่เขาแล้ว');
INSERT INTO "king_fahad_thai" VALUES(65,12,'65:12','อัลลอฮฺผู้ทรงสร้างชั้นฟ้าทั้งเจ็ด และทรงสร้างแผ่นดินก็เยี่ยงนั้น พระบัญชาจะบงมาท่ามกลางมันทั้งหลาย (ชั้นฟ้าและแผ่นดิน) เพื่อพวกเจ้าจะได้รู้ว่า แท้จริงอัลลอฮฺนั้นเป็นผู้ทรงอานุภาพเหนือทุกสิ่งอย่าง และแท้จริงอัลลอฮฺนั้นทรงห้อมล้อมทุกสิ่งอย่างไว้ด้วยความรอบรู้ (ของพระองค์)');
INSERT INTO "king_fahad_thai" VALUES(66,1,'66:1','โอ้นะบีเอ๋ย ทําไมเจ้าจึงห้ามสิ่งที่อัลลอฮฺได้ทรงอนุมัติแก่เจ้า เพื่อแสวงหาความพึงพอใจบรรดาภริยาของเจ้าเล่า ? และอัลลอฮฺนั้นเป็นผู้ทรงอภัย ผู้ทรงเมตตา');
INSERT INTO "king_fahad_thai" VALUES(66,2,'66:2','แน่นอนอัลลอฮฺได้ทรงกําหนดแก่พวกเจ้าแล้วในการแก้คําสาบานของพวกเจ้าและอัลลอฮฺเป็นผู้ทรงคุ้มครองพวกเจ้า และพระองค์เป็นผู้ทรงรอบรู้ ผู้ทรงปรีชาญาณ');
INSERT INTO "king_fahad_thai" VALUES(66,3,'66:3','และจงรําลึกขณะที่ท่านนะบีได้บอกความลับเรื่องหนึ่งแก่ภริยาบางคนของเขา ครั้นเมื่อนางได้บอกเล่าเรื่องนี้ (แก่คนอื่น) และอัลลอฮฺได้ทรงแจ้งเรื่องนี้แก่เขา (ท่านนะบี) เขาก็ได้แจ้งบางส่วนของเรื่องนี้ และไม่แจ้งบางส่วน ครั้นเมื่อเขา (ท่านนะบี) ได้แจ้งเรื่องนี้แก่นาง นางได้กล่าวว่า ใครบอกเล่าเรื่องนี้แก่ท่าน ? เขา (ท่านนะบี) กล่าวว่าพระผู้ทรงรอบรู้ พระผู้ทรงตระหนักยิ่ง ทรงแจ้งแก่ฉัน');
INSERT INTO "king_fahad_thai" VALUES(66,4,'66:4','หากเจ้าทั้งสองกลับเนื้อกลับตัวขออภัยโทษต่อัลลอฮฺ (ก็จะเป็นการดีแก่เจ้าทั้งสอง) เพราะแน่นอนหัวใจของเจ้าทั้งสองก็โอนอ่อนอยู่แล้ว แต่หากเจ้าทั้งสองร่วมกันต่อต้านเขา (ท่านนะบี) แท้จริงอัลลอฮฺ พระองค์เป็นผู้ทรงคุ้มครองเขา อีกทั้งญิบรีล และบรรดาผู้ศรัทธาที่ดี ๆ และนอกจากนั้น มะลาอิกะฮฺก็ยังเป็นผู้ช่วยเหลือสนับสนุนอีกด้วย');
INSERT INTO "king_fahad_thai" VALUES(66,5,'66:5','หากเขาหย่าพวกนาง บางทีพระเจ้าของเขาจะทรงเปลี่ยนแปลงให้แก่เขามีภริยาที่ดีกว่าพวกนาง เป็นหญิงที่นอบน้อมถ่อมตน เป็นหญิงผู้ศรัทธา เป็นหญิงผู้ภักดี เป็นหญิงผู้ขอลุแก่โทษ เป็นหญิงผู้มั่นต่อการอิบาดะฮฺ เป็นหญิงผู้มั่นต่อการถือศิลอด เป็นหญิงที่เป็นหม้าย และที่เป็นหญิงสาว');
INSERT INTO "king_fahad_thai" VALUES(66,6,'66:6','โอ้บรรดาผู้ศรัทธาเอ๋ย จงคุ้มครองตัวของพวกเจ้าและครอบครัวของพวกเจ้าให้พ้นจากไฟนรก เพราะเชื้อเพลิงของมันคือมนุษย์ และก้อนหิน มีมะลาอิกะฮฺผู้แข็งกร้าวหาญคอยเฝ้ารักษามันอยู่ พวกเขาจะไม่ฝ่าฝืนอัลลอฮฺในสิ่งที่พระองค์ทรงบัญชาแก่พวกเขา และพวกเขาจะปฏิบัติตามที่ถูกบัญชา');
INSERT INTO "king_fahad_thai" VALUES(66,7,'66:7','โอ้บรรดาผู้ปฏิเสธศรัทธาเอ๋ย วันนี้พวกเจ้าอย่าได้แก้ตัวเลย แต่ว่าพวกเจ้าจะถูกตอบแทนตามที่พวกเจ้าได้กระทําไว้');
INSERT INTO "king_fahad_thai" VALUES(66,8,'66:8','โอ้บรรดาผู้ศรัทธาเอ๋ย จงขอลุแก่โทษแด่อัลลอฮฺด้วยการลุแก่โทษอย่างจริงจังเถิดบางทีพระเจ้าของพวกเจ้าจะลบล้างความผิดของพวกเจ้าออกจากพวกเจ้า และจะทรงให้พวกเจ้าเข้าสวนสวรรค์หลากหลาย ณ เบื้องล่างสวนสวรรค์นั้นมีลําน้ําหลายสายไหลผ่าน วันที่อัลลอฮฺจะไม่ทรงทําให้นะบี และบรรดาผู้ศรัทธาร่วมกับเขาต้องอัปยศแสงสว่างของพวกเขาจะส่องจ้าไปเบื้องหน้าของพวกเขาและทางเบื้องขวาของพวกเขา พวกเขาจะกล่าวว่าข้าแต่พระเจ้าของเรา ขอพระองค์ได้ทรงโปรดทําให้แสงสว่างของเราอยู่กับเราตลอดไปและทรงยกโทษให้แก่เราแท้จริงพระองค์ท่านเป็นผู้ทรงอานุภาพเหนือทุกสิ่ง');
INSERT INTO "king_fahad_thai" VALUES(66,9,'66:9','โอ้นะบีเอ๋ย จงต่อสู้กับพวกปฏิเสธศรัทธาและพวกมุนาฟิกีน และจงแข็งกร้าวกับพวกเขา เพราะที่พํานักของพวกเขาคือนรก และมันเป็นทางกลับที่ชั่วช้ายิ่ง');
INSERT INTO "king_fahad_thai" VALUES(66,10,'66:10','อัลลอฮฺทรงยกอุทาหรณ์แก่บรรดาผู้ปฏิเสธศรัทธาถึงภริยาของนูหฺ และภริยาของลู๊ฏ นางทั้งสองอยู่ภายใต้การปกครองของบ่าวที่ดีทั้งสองในหมู่ปวงบ่าวของเรา แต่นางทั้งสองได้ทรยศต่อเขาทั้งสองให้พ้นจากการลงโทษาของอัลลอฮฺแต่ประการใด จึงมีเสียงกล่าวขึ้นว่า เจ้าทั้งสองจงเข้าไปในไฟนรกพร้อมกับบรรดาผู้ที่เข้าไปในมัน');
INSERT INTO "king_fahad_thai" VALUES(66,11,'66:11','และอัลลอฮฺทรงยกอุทาหรณ์แก่บรรดาผู้ศรัทธาถึงภริยาของฟิรเอานฺเมื่อนางได้กล่าวว่า ข้าแต่พระเจ้าของข้าพระองค์ ขอพระองค์ทรงโปรดสร้างบ้านหลังหนึ่งให้แก่ข้าพระองค์ ณ ที่พระองค์ท่านในสวนสวรรค์และทรงโปรดช่วยข้าพระองค์ให้พ้นจากฟิรเอานฺ และการกระทําของเขา และทรงโปรดช่วยข้าพระองค์ให้พ้นจากหมู่ชนผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(66,12,'66:12','และมัรยัมบุตรีของอิมรอน ผู้ซึ่งรักษาพรหมจารีของนาง แล้วเราได้เป่าวิญญาณของเราเข้าไปในนาง และนางได้ศรัทธาต่อบัญญัติต่าง ๆ แห่งพระเจ้าของนาง และ (ได้ศรัทธาต่อ) คัมภีร์ต่าง ๆ ของพระองค์ และนางจึงอยู่ในหมู่ผู้นอบน้อมภักดีทั้งหลาย');
INSERT INTO "king_fahad_thai" VALUES(67,1,'67:1','ความเจริญสุขจงมีแด่พระผู้ซึ่งอํานาจอยู่ในพระหัตถ์ของพระองค์ และพระองค์คือผู้ทรงอานุภาพเหนือทุกสิ่งทุกอย่าง');
INSERT INTO "king_fahad_thai" VALUES(67,2,'67:2','พระผู้ทรงให้มีความตายและให้มีความเป็น เพื่อจะทดสอบพวกเจ้าว่า ผู้ใดบ้างในหมู่พวกเจ้าที่มีผลงานดียิ่ง และพระองค์เป็นผู้ทรงอํานาจ ผู้ทรงให้อภัยเสมอ');
INSERT INTO "king_fahad_thai" VALUES(67,3,'67:3','พระผู้ทรงสร้างชั้นฟ้าทั้งเจ็ดเป็นชั้น ๆ เจ้าจะไม่เห็นแต่อย่างใดในความไม่ได้สัดส่วนในการสร้างของพระผู้ทรงกรุณาปรานี ดังนั้นเจ้าจงหันกลับมามองดูซิ เจ้าเห็นรอยร้าวหรือช่องโหว่บ้างไหม ?');
INSERT INTO "king_fahad_thai" VALUES(67,4,'67:4','แล้วจงหันกลับมามองอีกเป็นครั้งที่สอง สายตานั้นก็จะกลับมายังเจ้าด้วยการยอมจํานนและในสภาพที่ละเหี่ย');
INSERT INTO "king_fahad_thai" VALUES(67,5,'67:5','และโดยแน่นอนเราได้ประดับท้องฟ้าของโลกนี้ด้วยดวงดาวเป็นแสงประทีป และเราได้ทําให้มันเป็นอาวุธไล่ชัยฏอน และเราได้เตรียมการลงโทษด้วยไฟอันร้อนแรงสําหรับพวกมัน');
INSERT INTO "king_fahad_thai" VALUES(67,6,'67:6','และสําหรับบรรดาผู้ปฏิเสธศรัทธาต่อพระเจ้าของพวกเขานั้น คือการลงโทษแห่งนรกญะฮันนัม และมันเป็นทางกลับที่ชั่วช้ายิ่ง');
INSERT INTO "king_fahad_thai" VALUES(67,7,'67:7','เมื่อพวกเขาถูกโยนลงไปในนรกพวกเขาจะได้ยินเสียงของมันครวญครางขณะที่มันกําลังกเดือนพล่าน');
INSERT INTO "king_fahad_thai" VALUES(67,8,'67:8','มันแทบจะระเบิดออกไปเพราะความเคียดแค้น ทุกครั้งที่พวกหนึ่งถูกโยนลงไปในมัน ยามเฝ้านรกจะถามพวกเขาว่า มิได้มีผู้ตักเตือนมายังพวกเจ้าดอกหรือ');
INSERT INTO "king_fahad_thai" VALUES(67,9,'67:9','พวกเขากล่าวว่า มี ได้มีผู้ตักเตือนมายังเรา แต่พวกเราได้ปฏิเสธ และเรากล่าวอีกว่า อัลลอฮฺมิได้ทรงประทานสิ่งใดลงมาพวกท่านต่างหากที่อยู่ในการหลงผิดอย่างมาก');
INSERT INTO "king_fahad_thai" VALUES(67,10,'67:10','และพวกเขากล่าวอีกว่า หากพวกเราฟังและใช้สติปัญญาใคร่ครวญ พวกเราก็จะมิได้มาอยู่เป็นชาวนรกอย่างนี้ดอก');
INSERT INTO "king_fahad_thai" VALUES(67,11,'67:11','พวกเขายอมสารภาพในความผิดของพวกเขา แต่มันห่างไกลไปเสียแล้วสําหรับชาวนรก');
INSERT INTO "king_fahad_thai" VALUES(67,12,'67:12','แท้จริงบรรดาผู้ยําเกรงต่อพระเจ้าของพวกเขาโดยทางลับ สําหรับพวกเขาจะได้รับการอภัยโทษและรางวัลอันใหญ่หลวง');
INSERT INTO "king_fahad_thai" VALUES(67,13,'67:13','และพวกเจ้าจงปิดบังคําพูดของพวกเจ้าหรือเปิดเผยมันก็ตาม แท้จริงพระองค์ทรงรอบรู้สิ่งที่อยู่ในทรวงอก');
INSERT INTO "king_fahad_thai" VALUES(67,14,'67:14','พระผู้ทรงสร้างจะมิทรงรอบรู้ดอกหรือ ? พระองค์คือผู้ทรงรอบรู้อย่างถี่ถ้วนผู้ทรงตระหนักยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(67,15,'67:15','พระองค์คือผู้ทรงทําแผ่นดินนี้ให้ราบเรียบสําหรับพวกเจ้า ดังนั้นจงสัญจรไปตามขอบเขตของมันและจงบริโภคจากปัจจัยยังชีพของพระองค์ และยังพระองค์เท่านั้นการฟื้นคืนชีพ');
INSERT INTO "king_fahad_thai" VALUES(67,16,'67:16','พวกเจ้าจะปลอดภัยละหรือ จากการที่พระองค์ทรงสถิตย์อยู่ ณ ฟากฟ้าจะให้แผ่นดินสูบพวกเจ้าแล้วขณะนั้นมันจะหวั่นไหว');
INSERT INTO "king_fahad_thai" VALUES(67,17,'67:17','หรือว่าพวกเจ้าจะปลอดภัยจากการที่พระผู้ทรงสถิตย์อยู่ ณ ฟากฟ้า จะทรงส่งลมหอบก้อนกรวดให้กระหน่ํามายัง พวกเจ้า แล้วพวกเจ้าจะได้รู้ว่าการตักเตือนของข้าเป็นเช่นใด ?');
INSERT INTO "king_fahad_thai" VALUES(67,18,'67:18','และโดยแน่นอน บรรดา (หมู่ชน) ก่อนหน้าพวกเขาได้ปฏิเสธมาก่อนแล้ว ดังนั้นการปฏิเสธคําเตือนของข้ามีผลเป็นอย่างไร?');
INSERT INTO "king_fahad_thai" VALUES(67,19,'67:19','พวกเขามิได้มองไปดูนกที่ (บิน) อยู่เบื้องบนพวกเขาดอกหรือ? มันกาวปีกและหุบปีก (ของมัน) ไม่มีผู้ใดจะไปดึงมันไว้ได้นอกจากพระผู้ทรงกรุณาปรานี แท้จริงพระองค์ทรงมองเห็นทุกสิ่งอย่าง');
INSERT INTO "king_fahad_thai" VALUES(67,20,'67:20','หรือผู้ใดเล่า ซึ่งเขาเป็นพลพรรคของพวกเจ้าที่จะช่วยเหลือพวกเจ้าอื่นจากพระผู้ทรงกรุณาปรานี? พวกปฏิเสธศรัทธานั้นมิใช่อื่นใดเลยนอกจากในการหลอกลวงเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(67,21,'67:21','หรือผู้ใดเล่าซึ่งเขาจะให้ปัจจัยยังชีพแก่พวกเจ้า หากพระองค์ทรงระงับปัจจัยยังชีพของพระองค์ไว้ แต่ว่าพวกเจ้าดื้อรั้นอยู่ในความหยิ่งยะโส และห่าไกลจากความจริง');
INSERT INTO "king_fahad_thai" VALUES(67,22,'67:22','ผู้ที่เดินคว่ําคมําบนใบหน้าของเขาจะเป็นผู้ที่อยู่ในแนวทางที่ถูกต้องกว่า หรือว่าผู้ที่เดินตัวตรงอยู่บนแนวทางที่เที่ยงตรง');
INSERT INTO "king_fahad_thai" VALUES(67,23,'67:23','จงกล่าวเถิดมุฮัมมัด พระองค์คือผู้ทรงบังเกิดพวกเจ้าจะขอบคุณ');
INSERT INTO "king_fahad_thai" VALUES(67,24,'67:24','จงกล่าวเถิดมุฮัมมัด พระองค์เป็นผู้ทรงแพร่เผ่าพันธ์ของพวกเจ้าในแผ่นดินและพวกเจ้าจะถูกรวบรวมให้กลับไปหาพระองค์');
INSERT INTO "king_fahad_thai" VALUES(67,25,'67:25','และพวกเขากล่าวว่า เมื่อใดเล่าสัญญานี้จะเกิดขึ้น หากพวกท่านเป็นผู้สัตย์จริง');
INSERT INTO "king_fahad_thai" VALUES(67,26,'67:26','จงกล่าวเถิดมุฮัมมัด ความรู้ในเรื่องนั้นอยู่ที่อัลลอฮฺ ความจริงฉันเป็นเพียงผู้ตักเตือนอันแจ่มแจ้งเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(67,27,'67:27','ต่อเมื่อพวกเขาเห็นมัน (การลงโทษ) ใกล้เข้ามาแล้ว ใบหน้าของบรรดาผู้ปฏิเสธศรัทธาก็จะหม่นหมอง และจะมีเสียงกล่าวว่า นี่คือสิ่งที่่พวกเจ้าร้องขอ (ในโลกดุนยา)');
INSERT INTO "king_fahad_thai" VALUES(67,28,'67:28','จงกล่าวเถิดมุฮัมมัด พวกท่านจะบอกฉันซิว่า หากอัลลอฮฺจะทรงทําลายฉันและผู้ที่อยู่ร่วมกับฉันหรือจะทรงเมตตาแก่พวกเรา ดังนั้นผู้ใดเล่าจะช่วยพวกปฏิเสธศรัทธาให้พ้นจากการลงโทษอันเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(67,29,'67:29','จงกล่าวเถิดมุฮัมมัด พระองค์คือพระผู้ทรงกรุณาปราณี เราศรัทธาต่อพระองค์แล้วเราขอมอบหมายแด่พระองค์แล้วจะได้รู้ว่าใครผู้ใดอยู่ในการหลงผิดอันชัดแจ้ง');
INSERT INTO "king_fahad_thai" VALUES(67,30,'67:30','จงกล่าวเถิดมุฮัมมัด พวกท่านจงบอกฉันซิว่า หากแหล่งน้ําของพวกท่านเหือดแห้งลง ดังนั้นผู้ใดเล่าจะนําน้ําที่ท่วมทันมาให้พวกท่าน');
INSERT INTO "king_fahad_thai" VALUES(68,1,'68:1','นูน ขอสาบานด้วยปากกา และสิ่งที่พวกเขาขีดเขียน');
INSERT INTO "king_fahad_thai" VALUES(68,2,'68:2','ด้วยความโปรดปรานแห่งพระเจ้าของเจ้า เจ้ามิได้เป็นผู้เสียสติ');
INSERT INTO "king_fahad_thai" VALUES(68,3,'68:3','และแท้จริงสําหรับเจ้านั้นมีรางวัลอย่างมิขาดสาย');
INSERT INTO "king_fahad_thai" VALUES(68,4,'68:4','และแท้จริง เจ้านั้นอยู่บนคุณธรรมอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(68,5,'68:5','แล้วเจ้าจะได้เห็นและพวกเขาก็จะได้เห็น');
INSERT INTO "king_fahad_thai" VALUES(68,6,'68:6','ว่าผู้ใดในหมู่พวกเจ้าคือผู้วิกลจิริต');
INSERT INTO "king_fahad_thai" VALUES(68,7,'68:7','แท้จริงพระเจ้าของเจ้านั้นพระองค์ทรงรู้ดียิ่งถึงผู้ที่หลงจากทางของพระองค์ และพระองค์ทรงรู้ดียิ่งถึงผู้ที่อยู่ในแนวทางที่ถูกต้อง');
INSERT INTO "king_fahad_thai" VALUES(68,8,'68:8','ดังนั้นเจ้าอย่าได้ปฏิบัติตามบรรดาผู้ปฏิเสธเลย');
INSERT INTO "king_fahad_thai" VALUES(68,9,'68:9','พวกเขาใคร่ที่จะเห็นว่า หากเจ้าอ่อนข้อ แล้วพวกเขาก็จะอ่อนข้อตาม');
INSERT INTO "king_fahad_thai" VALUES(68,10,'68:10','และเจ้าอย่าปฏิบัติตามทุกคนที่เป็นนักสาบานที่ต่ําช้า');
INSERT INTO "king_fahad_thai" VALUES(68,11,'68:11','ผู้นินทาตระเวนใส่ร้ายผู้อื่น');
INSERT INTO "king_fahad_thai" VALUES(68,12,'68:12','ผู้ขัดขวางการทําความดี ผู้อธรรมทําร้ายบาปหนา');
INSERT INTO "king_fahad_thai" VALUES(68,13,'68:13','เป็นคนหยาบคายเลวทราม ยิ่งกว่านั้นยังเป็นคนต่ําช้าแปลกปลอม');
INSERT INTO "king_fahad_thai" VALUES(68,14,'68:14','โดยถือว่าเขาเป็นผู้มีทรัพย์สินและมีบุตรหลานมาก');
INSERT INTO "king_fahad_thai" VALUES(68,15,'68:15','เมื่ออายาต (อัลกุรอาน) ทั้งหลายของเราถูกสาธยายแก่เขา เขากล่าวว่าเป็นนิยายเหลวไหลสมัยก่อน');
INSERT INTO "king_fahad_thai" VALUES(68,16,'68:16','เราจะตีตราเขาบนปลายจมูก');
INSERT INTO "king_fahad_thai" VALUES(68,17,'68:17','แท้จริงเราได้ทดสอบพวกเขา ดั่งเช่นที่เราได้ทดสอบบรรดาเจ้าของสวน เมื่อพวกเขาสาบานว่าจะเก็บเกี่ยวผลของมันในยามรุ่ง');
INSERT INTO "king_fahad_thai" VALUES(68,18,'68:18','และพวกเขามิได้กล่าวคําว่า “อินชาอัลลอฮฺ”');
INSERT INTO "king_fahad_thai" VALUES(68,19,'68:19','ดังนั้น การลงโทษจากพระเจ้าของเจ้าก็ได้มาทําลายสวนนั้น ขณะที่พวกเขากําลังนอนหลับอยู่');
INSERT INTO "king_fahad_thai" VALUES(68,20,'68:20','ครั้นในตอนเช้า มันก็กลายเป็นเช่นถูกตัดอย่างราบเรียบ');
INSERT INTO "king_fahad_thai" VALUES(68,21,'68:21','ดังนั้นพวกเขาจึงกู่ตะโกนให้ตื่นแต่เช้าตรู่');
INSERT INTO "king_fahad_thai" VALUES(68,22,'68:22','จงเข้าไปในสวนของพวกท่านในตอนเช้าตรู่เถิด หากพวกท่านต้องการจะเก็บผลไม้');
INSERT INTO "king_fahad_thai" VALUES(68,23,'68:23','แล้วพวกเขาพากันออกไป แล้วพวกเขาพูดกระซิบซึ่งกันและกันอย่างเบา ๆ');
INSERT INTO "king_fahad_thai" VALUES(68,24,'68:24','ว่า วันนี้อย่าได้ให้คนยากจนขัดสนสักคนหนึ่ง เข้าไปหาพวกท่านในสวนนั้น');
INSERT INTO "king_fahad_thai" VALUES(68,25,'68:25','และพวกเขาก็ได้ออกไปแต่เช้า ตั้งใจว่าจะเก็บผลไม้ให้หมดก่อนที่ยากจนจะเข้าไปในสวน');
INSERT INTO "king_fahad_thai" VALUES(68,26,'68:26','ครั้นเมื่อพวกเขาเห็นสวน (อยู่ในสภาพที่ถูกทําลาย) พวกเขาก็กล่าวขึ้นว่าแท้จริงพวกเราหลงทางเสียแล้ว');
INSERT INTO "king_fahad_thai" VALUES(68,27,'68:27','(บางคนกล่าวว่า) เปล่าดอก พวกเราถูกหวงห้ามสิทธิ์เสียแล้ว');
INSERT INTO "king_fahad_thai" VALUES(68,28,'68:28','คนที่มีสติปัญญาคนหนึ่งในหมู่พวกเขากล่าวว่า ฉันมิได้บอกพวกท่านดอกหรือว่า ทําไม่พวกท่านจึงไม่กล่าวสดุดีแด่อัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(68,29,'68:29','พวกเขาจึงกล่าวว่า มหาบริสุทธิ์แห่งพระเจ้าของเรา แท้จริงเรานั้นเป็นผู้อธรรม');
INSERT INTO "king_fahad_thai" VALUES(68,30,'68:30','แล้วบางคนในหมู่พวกเขาก็หันไปต่อว่าซึ่งกันแล้วกัน');
INSERT INTO "king_fahad_thai" VALUES(68,31,'68:31','พวกเขากล่าวว่าความหายนะประสบแก่เราแล้วเพราะเราเป็นผู้ละเมิดฝ่าฝืน');
INSERT INTO "king_fahad_thai" VALUES(68,32,'68:32','บางทีพระเจ้าของเรา จะทรงเปลี่ยนแปลงสิ่งที่ดีกว่าให้แก่เรา แท้จริงเราหวังในความอภัยต่อพระเจ้าของเรา');
INSERT INTO "king_fahad_thai" VALUES(68,33,'68:33','เช่นนั้นแหละการลงโทษ และแน่นอนการลงโทษในปรโลกนั้นยิ่งใหญ่นัก หากพวกเขาล่วงรู้');
INSERT INTO "king_fahad_thai" VALUES(68,34,'68:34','แท้จริง สําหรับบรรดาผู้ยําเกรง ณ ที่พระเจ้าของพวกเขานั้นคือสวนสวรรค์หลากหลายแห่งบรมสุข');
INSERT INTO "king_fahad_thai" VALUES(68,35,'68:35','ดังนั้นจะให้เราปฏิบัติแก่บรรดาผู้นอบน้อมเสมือนกับเราปฏิบัติแก่บรรดาผู้กระทําผิดกระนั้นหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(68,36,'68:36','เกิดอะไรขึ้นแด่พวกเจ้า ? ทําไมพวกเจ้าจึงตัดสินเช่นนั้น');
INSERT INTO "king_fahad_thai" VALUES(68,37,'68:37','หรือว่าที่พวกเจ้ามีคัมภีร์ไว้สําหรับอ่าน ?');
INSERT INTO "king_fahad_thai" VALUES(68,38,'68:38','ซึ่งในคัมภีร์นั้นมีสิ่งสําหรับพวกเจ้าที่พวกเจ้าจะเลือกเอาได้');
INSERT INTO "king_fahad_thai" VALUES(68,39,'68:39','หรือว่าพวกเจ้าสัญญาผูกพันกับเราจนกระทั่งถึงวันกิยามะฮฺว่า แท้จริงสําหรับพวกเจ้านั้นจําได้ตามที่พวกเจ้าตัดสินเอาไว้');
INSERT INTO "king_fahad_thai" VALUES(68,40,'68:40','มุฮัมมัดจงถามพวกเขาดูว่า คนใดในหมู่พวกเขาจะเป็นหัวหน้าในการตัดสินเรื่องนั้น');
INSERT INTO "king_fahad_thai" VALUES(68,41,'68:41','หรือว่าพวกเขามีหุ้นส่วนกับอัลลอฮฺก็จงให้พวกเขานําหุ้นส่วนเหล่านั้นของพวกเขามา ถ้าหากพวกเขาสัตย์จริง');
INSERT INTO "king_fahad_thai" VALUES(68,42,'68:42','วันที่น้าแข้งจะถูกเลิกขึ้น (ในวันกิยามะฮฺ พระเจ้าจะมาตัดสินคดี หน้าแข้งของพระองค์จะถูกเลิกขึ้น) และพวกเขาจะถูกเรียกให้มาสุญูด แต่พวกเขาไม่สามารถจะสุญูดได้');
INSERT INTO "king_fahad_thai" VALUES(68,43,'68:43','สายตาของพวกเขาจะละห้อย ความต่ําต้อยจะปกคลุมพวกเขา และแน่นอนพวกเขาเคยถูกเรียกให้มาสุญูดแล้วขณะที่พวกเขายังอยู่ในสภาพที่ปลอดภัยดี');
INSERT INTO "king_fahad_thai" VALUES(68,44,'68:44','ดังนั้นจงปล่อยให้ข้าเถิด สําหรับผู้ที่ปฏิเสธต่ออัลกุรอาน เราจะนําพวกเขาลงสู่ (การลงโทษ) ทีละขั้น โดยที่พวกเขาไม่รู้');
INSERT INTO "king_fahad_thai" VALUES(68,45,'68:45','และข้าจะประวิงเวลาให้แก่พวกเขาแท้จริงอุบายของข้านั้นแข็งแรงนัก');
INSERT INTO "king_fahad_thai" VALUES(68,46,'68:46','หรือว่าเจ้าได้ขอค่าตอบแทนจากพวกเขา ดังนั้นพวกเขาจึงแบกภาระหนักเพราะมีหนี้');
INSERT INTO "king_fahad_thai" VALUES(68,47,'68:47','หรือว่าพวกเขารู้ในสิ่งเร้นลับ แล้วพวกเขาก็บันทึกเอาไว้');
INSERT INTO "king_fahad_thai" VALUES(68,48,'68:48','ดังนั้น เจ้า (มุฮัมมัด) จงอดทนต่อไปเถิดต่อบัญชาของพระเจ้าของเจ้า และอย่าเป็นดั่งเช่นเจ้าของปลา (นะบียูนุส) ขณะที่เขาวิงวอนเขาอยู่ในสภาพที่ระทมทุกข์');
INSERT INTO "king_fahad_thai" VALUES(68,49,'68:49','หากมิใช่เพราะความเมตตาจากพระเจ้าของเขามีมายังเขาแล้ว เขาคงถูกเหวี่ยงไปยังที่โล่งเตียน และเขาจะถูกตําหนิด้วย');
INSERT INTO "king_fahad_thai" VALUES(68,50,'68:50','แต่พระเจ้าของเขาได้ทรงคัดเลือกเขา และทรงทําให้เขาอยู่ในหมู่ผู้กระทําความดี');
INSERT INTO "king_fahad_thai" VALUES(68,51,'68:51','และบรรดาผู้ปฏิเสธศรัทธานั้นแทบจะทําให้สายตาของพวกเขาจ้องเขม็งไปยังเจ้า เมื่อพวกเขาได้ยินอัลกุรอานพวกเขาก็กล่าวว่าแท้จริงเขาเป็นคนบ้าแน่ ๆ');
INSERT INTO "king_fahad_thai" VALUES(68,52,'68:52','แต่อัลกุรอานนั้นมิใช่อื่นใดนอกจากเป็นข้อตักเตือนแก่ประชาชาติทั้งหมด');
INSERT INTO "king_fahad_thai" VALUES(69,1,'69:1','สิ่งที่จะเกิดขึ้นอย่างแน่นอน (วันกิยามะฮฺ)');
INSERT INTO "king_fahad_thai" VALUES(69,2,'69:2','สิ่งที่จะเกิดขึ้นอย่างแน่นอนนั้นคืออะไร');
INSERT INTO "king_fahad_thai" VALUES(69,3,'69:3','และอันใดเล่าทําให้เจ้ารู้ได้ว่า สิ่งที่จะเกิดขึ้นอย่างแน่นอนนั้นคืออะไร ?');
INSERT INTO "king_fahad_thai" VALUES(69,4,'69:4','พวกซะมูดและพวกอ๊าดได้ปฏิเสธวันกิยามะฮฺ');
INSERT INTO "king_fahad_thai" VALUES(69,5,'69:5','พวกซะมูด ถูกทําลายด้วยเสียงกําปนาทที่น่ากลัว');
INSERT INTO "king_fahad_thai" VALUES(69,6,'69:6','ส่วนพวกอ๊าด ถูกทําลายด้วยลมพายุที่หนาวเหน็บ และเสียงดังก้อง');
INSERT INTO "king_fahad_thai" VALUES(69,7,'69:7','พระองค์ทรงให้อภัยนั้นเกิดขึ้นแก่พวกเขา เจ็ดคืนกับแปดวันต่อเนื่องกัน แล้วเจ้าจะเห็นหมู่ชนนั้นนอนตายอยู่เช่นนั้นประหนึ่งต้นอินทผลัมที่กลางล้มระเนระนาด');
INSERT INTO "king_fahad_thai" VALUES(69,8,'69:8','แล้วเจ้าเห็นอะไรบ้างหลงเหลือสําหรับพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(69,9,'69:9','ฟิรเอานฺและพวกก่อนหน้าเขา และพวกมุอฺตะฟิกาต (เมืองของพวกลูฏซึ่งถูกพลิกแผ่นดิน) ได้กระทําความผิด');
INSERT INTO "king_fahad_thai" VALUES(69,10,'69:10','พวกเขาได้ฝ่าฝืนต่อร่อซูลแห่งพระเจ้าของพวกเขา ดังนั้นพระองค์จึงทรงลงโทษพวกเขาอย่างหนัก');
INSERT INTO "king_fahad_thai" VALUES(69,11,'69:11','เมื่อน้ําท่วมสูงขึ้น แท้จริงเราได้บรรทุกพวกเจ้าไว้ในเรือของนูหฺ');
INSERT INTO "king_fahad_thai" VALUES(69,12,'69:12','เพื่อเราจักได้ทําให้มันเป็นเครื่องเตือนสติแก่พวกเจ้า และหูที่สําเหนียกจะได้จดจํามันไว้อย่างแม่นยํา');
INSERT INTO "king_fahad_thai" VALUES(69,13,'69:13','ครั้นเมื่อเสียงเป่าครั้งแรกถูกเป่าขึ้นโดยสังข์ (เป็นสัญญาณแจ้งให้ทราบถึงวันกิยามะฮฺ)');
INSERT INTO "king_fahad_thai" VALUES(69,14,'69:14','แผ่นดินและเทือกเขาจะถูกยกขึ้นแล้วมันทั้งสองจะถูกกระแทกกันแตกกระจายเป็นผุยผง');
INSERT INTO "king_fahad_thai" VALUES(69,15,'69:15','ในวันนั้นวันกิยามะฮฺก็จะเกิดขึ้น');
INSERT INTO "king_fahad_thai" VALUES(69,16,'69:16','และชั้นฟ้าก็จะแยกออก แล้วมันก็จะอ่อนกําลังลงอย่างไม่เป็นระเบียบในวันนั้น');
INSERT INTO "king_fahad_thai" VALUES(69,17,'69:17','และมะลักก็จะปรากฏอยู่บนเวหาและ (มะลาอิกะฮฺ) จํานวนแปดท่านจะทูนบังลังก์แห่งพระเจ้าของเจ้าไว้เบื้องบนพวกเขาในวันนั้น');
INSERT INTO "king_fahad_thai" VALUES(69,18,'69:18','วันนั้นพวกเจ้าจะถูกนํามาอยู่ต่อหน้าพระองค์ ไม่มีความลับอันใดจะถูกปิดบังแก่พวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(69,19,'69:19','ส่วนผู้ที่บันทึกของเขาถูกนํามายื่นให้ทางเบื้องขวาของเขา เขาจะกล่าวว่า มาอ่านบันทึกของฉันซิ');
INSERT INTO "king_fahad_thai" VALUES(69,20,'69:20','ความจริงฉันนึกทีเดียวว่า ฉันจะได้พบบัญชีของฉัน');
INSERT INTO "king_fahad_thai" VALUES(69,21,'69:21','แล้วเขาจะมีความเป็นอยู่อย่างสุขสําราญ');
INSERT INTO "king_fahad_thai" VALUES(69,22,'69:22','ในสวนสวรรค์อันสูงส่ง');
INSERT INTO "king_fahad_thai" VALUES(69,23,'69:23','การเด็ดผลไม้ของมันอยู่แค่เอื้อมมือ');
INSERT INTO "king_fahad_thai" VALUES(69,24,'69:24','พวกเจ้าจงกิน จงดื่ม อย่างเกษมสําราญ เพราะสิ่งที่พวกเจ้าได้ปฏิบัติไว้ในวันเวลาที่ได้ผ่านมา');
INSERT INTO "king_fahad_thai" VALUES(69,25,'69:25','ส่วนผู้ที่บันทึกของเขาถูกนํามายื่นให้ทางเบื้องซ้ายของเขา เขาจะกล่าวว่า ฉันภาวนาที่จะไม่ให้บันทึกของฉันถูกนํามายื่น');
INSERT INTO "king_fahad_thai" VALUES(69,26,'69:26','และไม่รู้เสียเลยว่าบัญชีของฉันจะเป็นเช่นใด');
INSERT INTO "king_fahad_thai" VALUES(69,27,'69:27','โอ้ หากว่าความตายได้เกิดขึ้นเสียก็จะดี');
INSERT INTO "king_fahad_thai" VALUES(69,28,'69:28','ทรัพย์สมบัติของฉันไม่ได้คุ้มกันแก่ฉันเลย');
INSERT INTO "king_fahad_thai" VALUES(69,29,'69:29','อํานาจของฉันก็ได้สูญสิ้นไปจากฉันแล้ว');
INSERT INTO "king_fahad_thai" VALUES(69,30,'69:30','(จะมีคําบัญชาแก่มะลาอิกะฮฺว่า) จงนําเขาไป แล้วจําตรวนเสีย');
INSERT INTO "king_fahad_thai" VALUES(69,31,'69:31','แล้วโยนเขาเข้ากองไฟนรก');
INSERT INTO "king_fahad_thai" VALUES(69,32,'69:32','แล้วล่ามโซ่เขา ซึ่งความยาวของมันเจ็ดสิบศอก');
INSERT INTO "king_fahad_thai" VALUES(69,33,'69:33','แท้จริง เขามิได้ศรัทธาต่ออัลลอฮฺ ผู้ยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(69,34,'69:34','และเขามิได้ส่งเสริมให้อาหารแก่คนขัดสน');
INSERT INTO "king_fahad_thai" VALUES(69,35,'69:35','ดังนั้น วันนี้เขาจะไม่มีมิตรสนิท ณ ที่นี้');
INSERT INTO "king_fahad_thai" VALUES(69,36,'69:36','และไม่มีอาหารอย่างใด นอกจากน้ําหนองที่ไหลมาจากแผลของชาวนรก');
INSERT INTO "king_fahad_thai" VALUES(69,37,'69:37','ไม่มีผู้ใดกินมัน นอกจากบรรดาผู้กระทําความผิด');
INSERT INTO "king_fahad_thai" VALUES(69,38,'69:38','เปล่าเลย ข้าขอสาบานต่อสิ่งที่พวกเจ้ามองเห็น');
INSERT INTO "king_fahad_thai" VALUES(69,39,'69:39','และสิ่งที่พวกเจ้ามองไม่เห็น');
INSERT INTO "king_fahad_thai" VALUES(69,40,'69:40','แท้จริงอัลกรุอานนั้นคือคํากล่าวของร่อซูลผู้ทรงเกียรติ');
INSERT INTO "king_fahad_thai" VALUES(69,41,'69:41','และมิใช่คํากล่าวของนักกวี ส่วนน้อยเท่านั้นที่พวกเจ้าศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(69,42,'69:42','และไม่ใช่คํากล่าวของนักพยากรณ์ส่วนน้อยเท่านั้นที่พวกเจ้าใคร่ครวญ');
INSERT INTO "king_fahad_thai" VALUES(69,43,'69:43','เป็นการประทานมาจากพระเจ้าแห่งสากลโลก');
INSERT INTO "king_fahad_thai" VALUES(69,44,'69:44','และหากเขา (มุฮัมมัด) เสกสรรกล่าวคําเท็จบางคําแก่เราแล้ว');
INSERT INTO "king_fahad_thai" VALUES(69,45,'69:45','เราก็จะจับเขาด้วยความมั่นคง');
INSERT INTO "king_fahad_thai" VALUES(69,46,'69:46','แล้วเราก็จะตัดเส้นชีวิตให้ขาดไปจากเขา');
INSERT INTO "king_fahad_thai" VALUES(69,47,'69:47','ดังนั้นจะไม่มีผู้ใดในหมู่พวกเจ้าเป็นผู้คุ้มกันเขาได้');
INSERT INTO "king_fahad_thai" VALUES(69,48,'69:48','และแท้จริงอัลกุรอานนั้นเป็นข้อเตือนสติแก่บรรดาผู้ยําเกรง');
INSERT INTO "king_fahad_thai" VALUES(69,49,'69:49','และแท้จริงเรารู้อย่างแน่นอนว่ามีบรรดาผู้ปฏิเสธอัลกุรอานในหมู่พวกเจ้า');
INSERT INTO "king_fahad_thai" VALUES(69,50,'69:50','และแท้จริงอัลกุรอานนั้น เป็นการเศร้าโศกเสียใจแก่บรรดาผู้ปฏิเสธศรัทธา');
INSERT INTO "king_fahad_thai" VALUES(69,51,'69:51','และแท้จริงอัลกุรอานนั้นคือความจริงอันเที่ยงแท้แน่นอน');
INSERT INTO "king_fahad_thai" VALUES(69,52,'69:52','ดังนั้นเจ้าจงให้ความบริสุทธิ์ด้วยพระนามแห่งพระเจ้าผู้ยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(70,1,'70:1','มีคนหนึ่งได้ขอการลงโทษที่จะต้องเกิดขึ้นอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(70,2,'70:2','สําหรับบรรดาผู้ปฏิเสธศรัทธานั้นไม่มีผู้ปัดป้องใด ๆ ให้พ้นจาการลงโทษไปได้');
INSERT INTO "king_fahad_thai" VALUES(70,3,'70:3','(การลงโทษนั้น) มาจากอัลลอฮฺ ผู้เป็นเจ้าของแห่งทางขึ้นสู่เบื้องสูง');
INSERT INTO "king_fahad_thai" VALUES(70,4,'70:4','มะลาอิกะฮฺและอัรรูหฺ (ญิบริล) จะขึ้นไปหาพระองค์ในวันหนึ่งซึ่งกําหนดของมันเท่ากับห้าหมื่นปี (ของโลกดุนยานี้)');
INSERT INTO "king_fahad_thai" VALUES(70,5,'70:5','ดังนั้นเจ้าจงอดทนด้วยความอดทนที่ดีงามเถิด');
INSERT INTO "king_fahad_thai" VALUES(70,6,'70:6','แท้จริงพวกเขา (มุชริกีน)มองเห็นการลงโทษว่าเป็นเรื่องห่างไกล');
INSERT INTO "king_fahad_thai" VALUES(70,7,'70:7','แต่ว่าเราเห็นมัน (การลงโทษ) นั้นเป็นเรื่องใกล้');
INSERT INTO "king_fahad_thai" VALUES(70,8,'70:8','วันที่ท้องฟ้าจะเป็นเช่นทองแดงที่หลอมละลาย');
INSERT INTO "king_fahad_thai" VALUES(70,9,'70:9','และบรรดาภูเขาจะเป็นเช่นขนสัตว์ที่ปลิวว่อน');
INSERT INTO "king_fahad_thai" VALUES(70,10,'70:10','และมิตรสหายจะไม่ถามถึงกัน');
INSERT INTO "king_fahad_thai" VALUES(70,11,'70:11','ถึงแม้ว่าพวกเขาจะประสานสายตาซึ่งกันและกันก็ตาม ผู้ประพฤติชั่วก็ใคร่จะไถ่ตน ให้พ้นจากการลงโทษของอัลลอฮฺในวันนั้นด้วยบุตรหลานของเขา');
INSERT INTO "king_fahad_thai" VALUES(70,12,'70:12','และด้วยภริยาของเขา และด้วยพี่น้องของเขา');
INSERT INTO "king_fahad_thai" VALUES(70,13,'70:13','และด้วยญาติพี่น้องของเขา ซึ่งได้ให้ที่พักอาศัยแก่เขา');
INSERT INTO "king_fahad_thai" VALUES(70,14,'70:14','และด้วยผู้ที่อยู่ในแผ่นดินทั้งมวล เพื่อที่จะให้เขารอดพ้นจากการลงโทษ');
INSERT INTO "king_fahad_thai" VALUES(70,15,'70:15','ไม่เลยทีเดียว แท้จริงมันเป็นไฟนรกที่ลุกโซน');
INSERT INTO "king_fahad_thai" VALUES(70,16,'70:16','หนังศีรษะถูกลอก ออก (เพราะความร้อนของไฟนรก)');
INSERT INTO "king_fahad_thai" VALUES(70,17,'70:17','มันจะเรียกผู้ที่ผินหลัง และหันห่างจากความจริง');
INSERT INTO "king_fahad_thai" VALUES(70,18,'70:18','และสะสมทรัพย์สินและปิดบังไว้');
INSERT INTO "king_fahad_thai" VALUES(70,19,'70:19','แท้จริงมนุษย์นั้นถูกบังเกิดมาเป็นคนหวั่นไหว');
INSERT INTO "king_fahad_thai" VALUES(70,20,'70:20','เมื่อความทุกข์ยากประสบแก่เขา ก็ตีโพยตีพายกลัดกลุ้ม');
INSERT INTO "king_fahad_thai" VALUES(70,21,'70:21','และเมื่อคุณความดีประสบแก่เขา ก็หวงแหน');
INSERT INTO "king_fahad_thai" VALUES(70,22,'70:22','นอกจากบรรดาผู้กระทําละหมาด');
INSERT INTO "king_fahad_thai" VALUES(70,23,'70:23','บรรดาผู้ที่ดํารงมั่นอยู่ในการทําละหมาดของพวกเขาเป็นประจํา');
INSERT INTO "king_fahad_thai" VALUES(70,24,'70:24','และบรรดาผู้ที่ในทรัพย์สินของพวกเขามีส่วนที่ถูกกําหนดไว้');
INSERT INTO "king_fahad_thai" VALUES(70,25,'70:25','สําหรับผู้ที่เอ่ยขอและผู้ที่ไม่เอ่ยขอ');
INSERT INTO "king_fahad_thai" VALUES(70,26,'70:26','และบรรดาผู้ที่เชื่อมั่นต่อวันแห่งการตอบแทน (วันกิยามะฮฺ)');
INSERT INTO "king_fahad_thai" VALUES(70,27,'70:27','และบรรดาผู้ที่มีความหวาดหวั่นต่อการลงโทษแห่งพระเจ้าของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(70,28,'70:28','แท้จริงการลงโทษแห่งพระเจ้าของพวกเขาไม่เป็นที่ปลอดภัย');
INSERT INTO "king_fahad_thai" VALUES(70,29,'70:29','และบรรดาผู้ที่ระวังรักษาทวารของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(70,30,'70:30','นอกจากแก่คู่ครองของพวกเขา หรือที่มือขวาของพวกเขาครอบครองในลักษณะเช่นนั้นพวกเขาจะไม่เป็นผู้ที่ถูกตําหนิ');
INSERT INTO "king_fahad_thai" VALUES(70,31,'70:31','ดังนั้นผู้ใดล่วงละเมิดนอกเหนือไปจากนั้น ชนเหล่านั้นพวกเขาเป็นผู้ละเมิด');
INSERT INTO "king_fahad_thai" VALUES(70,32,'70:32','และบรรดาผู้ที่ระวังรักษาสิ่งที่ได้รับมอบหมาย (อะมานะฮฺ) ของพวกเขาและคํามั่นสัญญาของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(70,33,'70:33','และบรรดาผู้ที่ดํารงมั่นต่อการเป็นพยานของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(70,34,'70:34','และบรรดาผู้ที่ดํารงรักษาในการละหมาดของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(70,35,'70:35','ชนเหล่านั้นจะอยู่ในสวนสวรรค์อันหลากหลายเป็นผู้ได้รับเกียรติ');
INSERT INTO "king_fahad_thai" VALUES(70,36,'70:36','มีอะไรเกิดขึ้นแก่บรรดาผู้ปฏิเสธศรัทธาที่วิ่งกระหืดกระหอบมายังเจ้า (มุฮัมมัด)');
INSERT INTO "king_fahad_thai" VALUES(70,37,'70:37','พวกเขานั่งเป็นกลุ่ม ๆ ทางขวาหรือทางซ้ายของเจ้า');
INSERT INTO "king_fahad_thai" VALUES(70,38,'70:38','แต่ละคนในหมู่พวกเขาต่างก็อยากจะเข้าไปอยู่ในสวนสวรรค์อันสุขสําราญกระนั้นหรือ ?');
INSERT INTO "king_fahad_thai" VALUES(70,39,'70:39','ไม่เลยทีเดียวแท้จริงเราได้สร้างพวกเขาจากสิ่งที่พวกเขารู้กันดี');
INSERT INTO "king_fahad_thai" VALUES(70,40,'70:40','ข้าขอสาบานต่อพระเจ้าแห่งบรรดาทิศตะวันออก และบรรดาทิศตะวันตกว่า แท้จริงเรา (อัลลอฮฺ) เป็นผู้เดชานุภาพอย่างแน่นอน');
INSERT INTO "king_fahad_thai" VALUES(70,41,'70:41','ต่อการที่เราจะเปลี่ยนตัวแทนซึ่งดีกว่าพวกเขาและเราจะไม่เป็นผู้หมดความสามารถ');
INSERT INTO "king_fahad_thai" VALUES(70,42,'70:42','ดังนั้น เจ้าจงปล่อยพวกเขาให้มั่วสุมและหลงระเริง จนกว่าพวกเขาจะได้พบกับวัน (กิยามะฮฺ) ของพวกเขา ซึ่งพวกเขาถูกสัญญาไว้');
INSERT INTO "king_fahad_thai" VALUES(70,43,'70:43','วันที่พวกเขาจะออกมาจากหลุมฝังศพอย่างรีบเร่งคล้ายกับพวกเขาวิ่งกรูไปยังเจว็ดของพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(70,44,'70:44','สายตาของพวกเขาละห้อยเศร้าสลดความอัปยศปกคลุมพวกเขา นั่นคือวันที่พวกเขาถูกสัญญาไว้');
INSERT INTO "king_fahad_thai" VALUES(71,1,'71:1','แท้จริงเราได้ส่งนูหฺไปยังหมู่ชนของเขา (โดยบัญชาว่า) เจ้าจงกล่าวตักเตือนหมู่ชนของเจ้า ก่อนที่การลงโทษอันเจ็บปวดจะมาถึงพวกเขา');
INSERT INTO "king_fahad_thai" VALUES(71,2,'71:2','เขากล่าวว่า โอ้หมู่ชนของฉันเอ๋ย! แท้จริงฉันคือผู้ตักเตือนอันชัดแจ้งของพวกท่าน');
INSERT INTO "king_fahad_thai" VALUES(71,3,'71:3','พวกท่านจงเคารพภักดีอัลลอฮฺเถิด และจงยําเกรงพระองค์ และจงเชื่อฟังปฏิบัติตามฉัน');
INSERT INTO "king_fahad_thai" VALUES(71,4,'71:4','พระองค์จะทรงอภัยโทษให้แก่พวกท่านในความผิดของพวกท่าน และจะทรงผ่อนผัน พวกท่านจนกระทั่งถึงวาระที่ถูกกําหนดไว้ แท้จริงวาระของอัลลอฮฺนั้น เมื่อมาถึงแล้วมันจะไม่ยืดเวลาต่อไปอีก หากพวกท่านได้รู้');
INSERT INTO "king_fahad_thai" VALUES(71,5,'71:5','เขากล่าวว่า ข้าแต่พระเจ้าของข้าพระองค์ แท้จริงข้าพระองค์ได้เรียกร้องเชิญชวนหมู่ชนของข้าพระองค์ทั้งกลางคืนและกลางวัน');
INSERT INTO "king_fahad_thai" VALUES(71,6,'71:6','แต่การเรียกร้องเชิญชวนของข้าพระองค์มิได้เพิ่มพูนสิ่งใดแก่เขานอกจากการหลบหนี');
INSERT INTO "king_fahad_thai" VALUES(71,7,'71:7','และแท้จริงทุกครั้งที่ข้าพระองค์เรียกร้องเชิญชวนพวกเขาเพื่อที่พระองค์ท่านจะได้อภัยโทษให้แก่พวกเขา พวกเขาก็เอานิ้วมืออุดรูหูของพวกเขา และเอาเสื้อผ้าของพวกเขาคลุมโปง และพวกเขายังดื้อรั้น และหยิ่งยะโสด้วยความจองหอง');
INSERT INTO "king_fahad_thai" VALUES(71,8,'71:8','ครั้นแล้วข้าพระองค์ได้เรียกร้องเชิญชวนพวกเขาอย่างเปิดเผย');
INSERT INTO "king_fahad_thai" VALUES(71,9,'71:9','แล้วข้าพระองค์ก็ได้ประกาศแก่พวกเขาอย่างเปิดเผยอีกทั้งข้าพระองค์ยังได้บอกกล่าวแก่พวกเขาอย่างลับ ๆ อีกด้วย');
INSERT INTO "king_fahad_thai" VALUES(71,10,'71:10','ข้าพระองค์ได้กล่าวว่า พวกท่านจงขออภัยโทษต่อพระเจ้าของพวกท่านเถิด เพราะแท้จริงพระองค์เป็นผู้ทรงอภัยโทษอย่างแท้จริง');
INSERT INTO "king_fahad_thai" VALUES(71,11,'71:11','พระองค์จะทรงหลั่งน้ําฝนอย่างมากมายแก่พวกท่าน');
INSERT INTO "king_fahad_thai" VALUES(71,12,'71:12','และพระองค์จะทรงเพิ่มพูนทรัพย์สินและลูกหลานแก่พวกท่าน และจะทรงทําให้มีสวนมากหลายแก่พวกท่าน และจะทรงทําให้มีลําน้ํามากหลายแก่พวกท่าน');
INSERT INTO "king_fahad_thai" VALUES(71,13,'71:13','ทําไมพวกท่านจึงไม่สํานึกถึงความยิ่งใหญ่ของอัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(71,14,'71:14','และโดยแน่นอน พระองค์ทรงสร้างพวกท่านตามลําดับขั้นตอน');
INSERT INTO "king_fahad_thai" VALUES(71,15,'71:15','พวกเจ้าไม่เห็นดอกหรือว่าอัลลอฮฺทรงสร้างชั้นฟ้าทั้งเจ็ดเป็นชั้น ๆ อย่างไร ?');
INSERT INTO "king_fahad_thai" VALUES(71,16,'71:16','และทรงทําให้ดวงจันทร์ในชั้นฟ้าเหล่านั้นมีแสงสว่าง และทรงทําให้ดวงอาทิตย์มีแสงจ้า');
INSERT INTO "king_fahad_thai" VALUES(71,17,'71:17','และอัลลอฮฺทรงบังเกิดพวกท่านจากแผ่นดินเช่นพืชผัก');
INSERT INTO "king_fahad_thai" VALUES(71,18,'71:18','แล้วจะทรงให้พวกท่านกลับคืนสู่ในแผ่นดิน และจะทรงให้พวกท่านออกมาอีกเพื่อคืนชีพ');
INSERT INTO "king_fahad_thai" VALUES(71,19,'71:19','และอัลลอฮฺทรงทําให้แผ่นดินนี้ราบเรียบกว้างใหญ่สําหรับพวกท่าน');
INSERT INTO "king_fahad_thai" VALUES(71,20,'71:20','เพื่อพวกท่านจะได้สัญจรไปมาตามพื้นที่โล่งกว้างนั้น');
INSERT INTO "king_fahad_thai" VALUES(71,21,'71:21','นูหฺได้กล่าวว่า ข้าแต่พระเจ้าของข้าพระองค์ แท้จริงพวกเขาได้ฝ่าฝืนข้าพระองค์และเชื่อฟังผู้ที่ทรัพย์สินของเขา และลูกหลานของเขามิได้เพิ่มพูนอันใดแก่เขานอกจากการขาดทุน');
INSERT INTO "king_fahad_thai" VALUES(71,22,'71:22','และพวกเขาได้วางแผนร้ายอันยิ่งใหญ่');
INSERT INTO "king_fahad_thai" VALUES(71,23,'71:23','และพวกเขาได้กล่าวว่า พวกท่านอย่าได้ทอดทิ้งพระเจ้าทั้งหลายของพวกท่านเป็นอันขาด พวกท่านอย่าได้ทอดทิ้งวัดดฺ และสุวาอฺ และยะฆูษ และยะอู๊ก และนัซรฺ เป็นอันขาด');
INSERT INTO "king_fahad_thai" VALUES(71,24,'71:24','และโดยแน่นอน พวกเขาได้ทําให้หมู่ชนจํานวนมากหลง ดังนั้นขอพระองค์ท่านอย่าได้เพิ่มอันใดแก่พวกอธรรมเหล่านั้น นอกจากกการหลงผิดเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(71,25,'71:25','อันเนื่องมาจากความผิดมากหลายของพวกเขา พวกเขาจึงถูกจมน้ําตาย และจะถูกให้เข้าอยู่ในไฟนรก ดังนั้นพวกเขาจะไม่ได้พบผู้ช่วยเหลือสําหรับพวกเขาอื่นจากอัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(71,26,'71:26','และนูหฺได้กล่าวว่า ข้าแต่พระเจ้าของข้าพระองค์ ขอพระองค์ทรงอย่าปล่อยให้พวกปฏิเสธศรัทธาหลงเหลืออยู่ในแผ่นดินนี้เลย');
INSERT INTO "king_fahad_thai" VALUES(71,27,'71:27','เพราะแท้จริง หากพระองค์ทรงปล่อยให้พวกเขาหลงเหลืออยู่ พวกเขาก็จะทําให้ปวงบ่าวของพระองค์หลงผิด และพวกเขานั้นนะให้กําเนิดแต่พวกเลวทราม พวกปฏิเสธศรัทธาเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(71,28,'71:28','ข้าแต่พระเจ้าของข้าพระองค์ ขอพระองค์ทรงอภัยโทษให้แก่ข้าพระองค์ และพ่อแม่ของข้าพระองค์ และผู้ที่เข้ามาในบ้านของข้าพระองค์เป็นผู้ศรัทธา และบรรดาผู้ศรัทธาชาย และบรรดาผู้ศรัทธาหญิง และพระองค์ท่านอย่าได้เพิ่มอันใดแก่พวกอธรรมเหล่านั้น นอกจากความพินาศหายนะเท่านั้น');
INSERT INTO "king_fahad_thai" VALUES(72,1,'72:1','จงกล่าวเถิดมุฮัมมัดว่า ได้มีวะฮียฺมายังฉันว่า แท้จริงพวกญินจํานวนหนึ่งได้ฟังฉัน (อ่านกุรอาน) และพวกเขากล่าวว่า แท้จริงเราได้ยินกุรอานที่แปลกประหลาด');
INSERT INTO "king_fahad_thai" VALUES(72,2,'72:2','นําไปสู่ทางที่ถูกต้อง ดังนั้นพวกเราจึงศรัทธาต่ออัลกุรอานนั้น และเราจะไม่ตั้งสิ่งใดเป็นภาคีต่อพระเจ้าของเรา');
INSERT INTO "king_fahad_thai" VALUES(72,3,'72:3','และความจริงนั้น ความยิ่งใหญ่แห่งพระเจ้าของเรานั้นทรงสูงส่งยิ่ง พระองค์ไม่มีภริยาและไม่มีบุตร');
INSERT INTO "king_fahad_thai" VALUES(72,4,'72:4','และแท้จริง คนโง่ในหมู่พวกเราได้กล่าวร้ายต่ออัลลอฮฺอย่างเกินเหตุ');
INSERT INTO "king_fahad_thai" VALUES(72,5,'72:5','และแท้จริงเราคาดคิดว่า มนุษย์และญินจะไม่กล่าวเท็จต่ออัลลอฮฺเป็นอันขาด');
INSERT INTO "king_fahad_thai" VALUES(72,6,'72:6','และแท้จริงมนุษย์บางคนเคยขอความคุ้มครองจากญินบางคน ดังนั้นพวกเขา (มนุษย์) จึงทําให้พวกเขา (ญิน)เพิ่มการหยิ่งจองหองยิ่งขึ้น');
INSERT INTO "king_fahad_thai" VALUES(72,7,'72:7','และแท้จริงพวกเขา (มนุษย์) คาดคิดเช่นเดียวกับที่พวกท่าน (ญิน) คาดคิดว่าอัลลอฮฺ จะไม่ทรงแต่งตั้งผู้ใดขึ้น (เป็นร่อซูล)');
INSERT INTO "king_fahad_thai" VALUES(72,8,'72:8','และแท้จริงเราได้ค้นคว้าหาข่าว ณ ชั้นฟ้า แต่เราได้พบ ณ ที่นั่งเต็มไปด้วยยามเฝ้าผู้เข้มแข็งและเปลวเพลิง');
INSERT INTO "king_fahad_thai" VALUES(72,9,'72:9','และแท้จริงเราเคยนั่ง ณ สถานที่นั่งในท้องฟ้านั้นเพื่อฟัง แต่ขณะนี้ผู้ใดนั่งฟังเขาก็จะพบเปลวเพลิงถูกเตรียมไว้สําหรับเขา');
INSERT INTO "king_fahad_thai" VALUES(72,10,'72:10','และแท้จริงเราไม่รู้ดอกว่า ความชั่วร้ายนั้นจะถูกให้มีขึ้นแก่ผู้ที่อยู่ในแผ่นดินนี้ หรือว่าพระเจ้าของพวกเขาปรารถนาแนวทางที่ถูกต้องแก่พวกเขา');
INSERT INTO "king_fahad_thai" VALUES(72,11,'72:11','และแท้จริงในหมู่พวกเรานั้นมีคนดีและในหมู่พวกเราก็มีคนอื่นจากนั้น พวกเราอยู่ในแนวทางที่แตกต่างกัน');
INSERT INTO "king_fahad_thai" VALUES(72,12,'72:12','และแท้จริงเราคาดคิดว่า เราจะไม่รอดพ้นจาก (การลงโทษ) ของอัลลอฮฺในแผ่นดินนี้ และเราจะหนีไม่รอดพ้นไปจากพระองค์');
INSERT INTO "king_fahad_thai" VALUES(72,13,'72:13','และแท้จริงเมื่อเราได้ยินอัลกุรอานเราก็ศรัทธาต่ออัลกุรอานนั้น ดังนั้นผู้ใดศรัทธาต่อพระเจ้าของเขา เขาก็จะไม่หวั่นเกรงต่อการขาดทุน และการอยุติธรรม');
INSERT INTO "king_fahad_thai" VALUES(72,14,'72:14','และแท้จริงในหมู่พวกเรามีผู้ที่เป็นมุสลิม และในหมู่พวกเรามีผู้อธรรม ดังนั้นผู้ใดนอบน้อม ชนเหล่านั้นพวกเขาได้มุ่งสู่แนวทางที่ถูกต้อง');
INSERT INTO "king_fahad_thai" VALUES(72,15,'72:15','และส่วนบรรดาผู้ที่หันห่างออกจากความจริง พวกเขาก็เป็นฟืนของไฟนรก');
INSERT INTO "king_fahad_thai" VALUES(72,16,'72:16','และหากพวกเขาธํารงมั่นอยู่บนแนวทางที่เที่ยงธรรม แน่นอนเราก็จะให้พวกเขามีริซกีกว้างขวาง');
INSERT INTO "king_fahad_thai" VALUES(72,17,'72:17','เพื่อเราจะทดสอบพวกเขาในเรื่องนี้ และผู้ใดหันห่างจากการรําลึกถึงพระเจ้าของเขา พระองค์จะให้เขาได้รับการลงโทษอันแสนสาหัส');
INSERT INTO "king_fahad_thai" VALUES(72,18,'72:18','และว่าแท้จริงบรรดามัสยิดนั้นเป็นของอัลลอฮฺ ดังนั้น พวกเจ้าอย่าวิงวอนขอผู้ใดเคียงคู่กับอัลลอฮฺ');
INSERT INTO "king_fahad_thai" VALUES(72,19,'72:19','และว่าแท้จริงเมื่อบ่าวของอัลลอฮฺ (มุฮัมมัด) ยืนขึ้นกล่าววิงวอนขอต่อพระองค์พวกเขา (ญิน) ก็ได้ห้อมล้อมเขาอย่างหนาแน่น');
INSERT INTO "king_fahad_thai" VALUES(72,20,'72:20','จงกล่าวเถิดมุฮัมมัด ว่า แท้จริงฉันวิงวอนขอต่อพระเจ้าของฉัน และฉันจะมิตั้งผู้ใดเป็นภาคีต่อพระองค์');
INSERT INTO "king_fahad_thai" VALUES(72,21,'72:21','จงกล่าวเถิดมุฮัมมัดว่า แท้จริงฉันไม่มีอํานาจที่จะให้โทษและให้คุณแก่พวกท่าน');
INSERT INTO "king_fahad_thai" VALUES(72,22,'72:22','จงกล่าวเถิดมุฮัมมัดว่า ไม่มีผู้ใดจะคุ้มครองฉันให้พ้นจาก (การลงโทษของ) อัลลอฮฺได้ และฉันจะไม่พบที่พึ่งอันใดอื่นจากพระองค์เลย');
INSERT INTO "king_fahad_thai" VALUES(72,23,'72:23','เว้นแต่ฉันจะเผยแผ่ (สิ่งที่รับ) จากอัลลอฮฺ และสาส์นของพระองค์ และผู้ใดฝ่าฝืนอัลลอฮฺ และร่อซูลของพระองค์แท้จริงสําหรับ เขานั้นคือไฟนรก เป็นผู้พํานักอยู่ในนั้นตลอดกาล');
INSERT INTO "king_fahad_thai" VALUES(72,24,'72:24','จนกระทั่งเมื่อพวกเขาได้เห็นสิ่งที่พวกเขาถูกสัญญาไว้ แล้วพวกเขาก็จะได้รู้ว่าใครเป็นผู้อ่อนแอยิ่งในการเป็นผู้ช่วยเหลือและมีจํานวนน้อยกว่า');
INSERT INTO "king_fahad_thai" VALUES(72,25,'72:25','จงกล่าวเถิดมุฮัมมัด ฉันไม่รู้สิ่งที่พวกท่านถูกสัญญาไว้นั้น หรือว่าพระเจ้าของฉันจะทรงกําหนดเวลาการลงโทษนั้นให้ห่างไกลออกไป');
INSERT INTO "king_fahad_thai" VALUES(72,26,'72:26','พระผู้ทรงรอบรู้สิ่งเร้นลับ ดังนั้นพระองค์จะไม่ทรงเปิดเผยสิ่งเร้นลับของพระองค์แก่ผู้ใด');
INSERT INTO "king_fahad_thai" VALUES(72,27,'72:27','นอกจากผู้ที่พระองค์ทรงยินดีเช่นร่อซูล ดังนั้นพระองค์จะทรงส่งผู้พิทักษ์เฝ้าดูแลข้างหน้าและข้างหลังเขา');
INSERT INTO "king_fahad_thai" VALUES(72,28,'72:28','เพื่อพระองค์จะทรงรู้ว่า แน่นอนพวกเขาได้เผยแผ่สาส์นของพระเจ้าของพวกเขาแล้ว และพระองค์ได้ทรงห้อมล้อม (รอบรู้) ทุกสิ่งที่อยู่ ณ ที่พวกเขา และพระองค์ทรงนับจํานวนทุก ๆ สิ่งไว้อย่างครบถ้วน');
INSERT INTO "king_fahad_thai" VALUES(73,1,'73:1','โอ้ ผู้คลุมกายอยู่เอ๋ย !');
INSERT INTO "king_fahad_thai" VALUES(73,2,'73:2','จงยืนขึ้น (ละหมาด) เวลากลางคืน เว้นแต่เพียงเล็กน้อย (ไม่ใช่ตลอดคืน)');
INSERT INTO "king_fahad_thai" VALUES(73,3,'73:3','ครึ่งหนึ่งของเวลากลางคืน หรือน้อยกว่านั้นเพียงเล็กน้อย');
INSERT INTO "king_fahad_thai" VALUES(73,4,'73:4','หรือมากกว่านั้น และจงอ่านอัลกุรอานช้า ๆ เป็นจังหวะ (ชัดถ้อยชัดคํา)');
INSERT INTO "king_fahad_thai" VALUES(73,5,'73:5','แท้จริงเราจะประทานวจนะ (วะฮียฺ) อันหนักหน่วงแก่เจ้า');
INSERT INTO "king_fahad_thai" VALUES(73,6,'73:6','แท้จริงการตื่นขึ้นในเวลากลางคืนนั้นเป็นเวลาที่ประทับใจยิ่งและเป็นการอ่านที่ชัดเจนยิ่ง');
INSERT INTO "king_fahad_thai" VALUES(73,7,'73:7','แท้จริงสําหรับเจ้านั้นในเวลากลางวันมีภารกิจมากมาย');
INSERT INTO "king_fahad_thai" VALUES(73,8,'73:8','และจงรําลึกถึงพระนามแห่งพระเจ้าของเจ้า และจงตั้งจิตมั่นต่อพระองค์อย่างเคร่งครัด');
INSERT INTO "king_fahad_thai" VALUES(73,9,'73:9','พระเจ้าแห่งทิศตะวันออกและทิศตะวันตก ไม่มีพระเจ้าอื่นใดนอกจากพระองค์ ดังนั้นจงยึดพระองค์ให้เป็นผู้คุ้มครองเถิด');
INSERT INTO "king_fahad_thai" VALUES(73,10,'73:10','และจงอดทนต่อสิ่งที่พวกเขากล่าวร้ายและจงแยกตัวออกจากพวกเขาด้วยการแยกตัวอย่างสุภาพ');
INSERT INTO "king_fahad_thai" VALUES(73,11,'73:11','และจงปล่อยข้ากับบรรดาผู้ปฏิเสธผู้สําราญ และจงผ่อนผันให้แก่พวกเขาสักเล็กน้อย');
INSERT INTO "king_fahad_thai" VALUES(73,12,'73:12','แท้จริง ณ ที่เรานั้นมีตรวนและกองไฟลุกโชน');
INSERT INTO "king_fahad_thai" VALUES(73,13,'73:13','และอาหารที่ติดลําคอและการลงโทษอันเจ็บปวด');
INSERT INTO "king_fahad_thai" VALUES(73,14,'73:14','วันที่แผ่นดินและภูเขาจะสั่นสะเทือนและภูเขาจะกลายเป็นกองทรายไหลพรู');
INSERT INTO "king_fahad_thai" VALUES(73,15,'73:15','แท้จริงเราได้ส่งร่อซูลคนหนึ่งไปยังพวกเจ้า เพื่อเป็นพยานต่อพวกเจ้า ดังที่เราได้ส่งร่อซูลคนหนึ่งไปยังฟิรเอานฺ');
INSERT INTO "king_fahad_thai" VALUES(73,16,'73:16','แต่ฟิรเอานฺได้ฝ่าฝืนร่อซูลคนนั้น ดังนั้นเราจึงได้ลงโทษเขาด้วยการลงโทษอย่างหนักหน่วง');
INSERT INTO "king_fahad_thai" VALUES(73,17,'73:17','ถ้าพวกเจ้าปฏิเสธ ดังนั้นพวกเจ้าจะปกป้องตนเองต่อวันนั้นได้อย่างไรเล่า ซึ่งจะทําให้เด็กๆ กลายเป็นแก่ (มีผมสีขาวเพราะความตกใจกลัว)');
INSERT INTO "king_fahad_thai" VALUES(73,18,'73:18','(วันนั้น) ชั้นฟ้าจะปริแยกออก และสัญญาของพระองค์จะต้องเกิดขึ้น (อย่างไม่ต้องสงสัย)');
INSERT INTO "king_fahad_thai" VALUES(73,19,'73:19','แท้จริง นี่คือข้อเตือนสติ ดังนั้นผู้ใดประสงค์ก็พึงยึดถือเป็นแนวทางไปสู่พระเจ้าของเขาเถิด');
INSERT INTO "king_fahad_thai" VALUES(73,20,'73:20','และอัลลอฮฺทรงกําหนดเวลากลางคืนและกลางวัน พระองค์ทรงรู้ดีว่าพวกเจ้าไม่สามารถที่จะกําหนดเวลาได้ ด้วยเหตุนี้พระองค์จึงทรงผ่อนผันให้แก่พวกเจ้า ดังนั้นพวกเจ้าจงอ่านอัลกุรอานตามแต่สะดวกเถิด พระองค์ทรงรู้ดีว่า อาจมีบางคนในหมู่พวกเจ้าเป็นคนป่วย และบางคนอื่น ๆ ต้องเดินทางไปดินแดนอื่น เพื่อแสวงหาจากความโปรดปรานของอัลลอฮฺ และบางคนอื่นต่อสู้ในทางของอัลลอฮฺ ดังนั้นพวกเจ้าจงอ่านตามสะดวกจากอัลกุรอานเถิด และจงดํารงไว้ซึ่งการละหมาดและจงบริจาคซะกาต และจงให้อัลลอฮฺยืมอย่างดีเยี่ยมเถิด และความดีอันใดที่พวกเจ้าได้กระทําไว้เพื่อตัวของพวกเจ้าเองพวกเจ้าก็จะพบมัน ณ ที่อัลลอฮฺ ซึ่งเป็นความดีและผลตอบแทนก็ยิ่งใหญ่กว่า ดังนั้นพวกเจ้าจงขออภัยโทษต่ออัลลอฮฺ แท้จริงอัลลอฮฺเป็นผู้ทรงอภัย ผู้ทรงเมตตาเสมอ');
INSERT INTO "king_fahad_thai" VALUES(74,1,'74:1','โอ้ผู้ห่มกายอยู่เอ๋ย !');
INSERT INTO "king_fahad_thai" VALUES(74,2,'74:2','จงลุกขึ้น แล้วประกาศตักเตือน');
INSERT INTO "king_fahad_thai" VALUES(74,3,'74:3','และแด่พระเจ้าของเจ้า จงให้ความเกียงไกร (ต่อพระองค์)');
INSERT INTO "king_fahad_thai" VALUES(74,4,'74:4','และเสื้อผ้าของเจ้า จงทําให้สะอาด');
INSERT INTO "king_fahad_thai" VALUES(74,5,'74:5','และสิ่งสกปรกก็จงหลบหลีกให้ห่างเสีย');
INSERT INTO "king_fahad_thai" VALUES(74,6,'74:6','และอย่าทําคุณ เพื่อหวังการตอบแทนอันมากมาย');
INSERT INTO "king_fahad_thai" VALUES(74,7,'74:7','และเพื่อพระเจ้าของเจ้าเท่านั้นจงอดทน');
INSERT INTO "king_fahad_thai" VALUES(74,8,'74:8','ในที่สุด เมื่อเสียงเป่าถูกเป่าขึ้น');
INSERT INTO "king_fahad_thai" VALUES(74,9,'74:9','นั่นคือ วันนั้น วันแห่งความยากลําบาก');
INSERT INTO "king_fahad_thai" VALUES(74,10,'74:10','แก่บรรดาผู้ปฏิเสธศรัทธา มิใช่เป็นเรื่องง่าย');
INSERT INTO "king_fahad_thai" VALUES(74,11,'74:11','จงปล่อยข้าไว้กับผู้ที่ข้าได้สร้างเขาไว้แต่ลําพังเถิด');
INSERT INTO "king_fahad_thai" VALUES(74,12,'74:12','และข้าได้ทําให้เขามีทรัพย์สมบัติอย่างล้นเหลือ');
INSERT INTO "king_fahad_thai" VALUES(74,13,'74:13','และลูกหลานอย่างพรั่งพร้อม');
INSERT INTO "king_fahad_thai" VALUES(74,14,'74:14','และข้าได้ทําให้เขาสุขสบายอย่างราบรื่น');
INSERT INTO "king_fahad_thai" VALUES(74,15,'74:15','แล้วเขายังโลภที่จะให้ข้าเพิ่มพูนแก่เขาอีก');
INSERT INTO "king_fahad_thai" VALUES(74,16,'74:16','เปล่าเลย ! เพราะว่าเขาเป็นผู้ดื้อรั้นต่อสัญญาณต่าง ๆ ของเรา');
INSERT INTO "king_fahad_thai" VALUES(74,17,'74:17','ในไม่ช้าข้าจะเพิ่มพูนความยากลําบากแก่เขา');
INSERT INTO "king_fahad_thai" VALUES(74,18,'74:18','แท้จริงเขาได้ใคร่ครวญและคาดคะเน');
INSERT INTO "king_fahad_thai" VALUES(74,19,'74:19','ดังนั้นเขาได้รับความหายนะ เขาจะคาดคะเนได้อย่างไร ?');
INSERT INTO "king_fahad_thai" VALUES(74,20,'74:20','แล้วเขาได้รับความหายนะ เขาจะคาดคะเนได้อย่างไร ?');
INSERT INTO "king_fahad_thai" VALUES(74,21,'74:21','แล้วเขาได้ตรึกตรอง');
INSERT INTO "king_fahad_thai" VALUES(74,22,'74:22','แล้วเขาทําหน้าบูดบึ้ง และทําหน้านิ่วคิ้วขมวด');
INSERT INTO "king_fahad_thai" VALUES(74,23,'74:23','แล้วเขาก็ผินหลังออกไป และหยิ่งผยอง');
INSERT INTO "king_fahad_thai" VALUES(74,24,'74:24','แล้วเขากล่าวว่า นี่มิใช่อื่นนอกจากเป็นมายากลที่สืบทอดกันมา');
INSERT INTO "king_fahad_thai" VALUES(74,25,'74:25','นี่มิใช่อื่นใดนอกจากเป็นคําพูดของปุถุชน');
