INSERT INTO "king_fahad_thai" VALUES(76,21,'76:21','บนพวกเขามีอาภรณ์สีเขียวทําด้วยผ้าไหมละเอียด และผ้าไหมหยาบ และถูกประดับด้วยกําไลเงิน และพระเจ้าของพวกเขาจะทรงให้พวกเขาได้ดื่มเครื่องดื่มอันบริสุทธิ์ยิ่ง');
