INSERT INTO "king_fahad_thai" VALUES(79,1,'79:1','ขอสาบานด้วย (มะลาอิกะฮฺ) ผู้ฉุดกระชาก (วิญญาณของผู้ปฏิเสธศรัทธา) อย่างแรง');
