INSERT INTO "king_fahad_thai" VALUES(108,3,'108:3','แท้จริงศัตรูของเจ้านั้นเขาเป็นผู้ถูกตัดขาด');
INSERT INTO "king_fahad_thai" VALUES(109,1,'109:1','จงกล่าวเถิด มุฮัมมัดว่า โอ้บรรดาผู้ปฏิเสธศรัทธาเอ๋ย');
INSERT INTO "king_fahad_thai" VALUES(109,2,'109:2','ฉันจะไม่เคารพภักดีสิ่งที่พวกท่านเคารพภักดีอยู่');
INSERT INTO "king_fahad_thai" VALUES(109,3,'109:3','และพวกท่านก็ไม่ใช่เป็นผู้เคารพภักดีพระเจ้าที่ฉันเคารพภักดี');
INSERT INTO "king_fahad_thai" VALUES(109,4,'109:4','และฉันก็มิใช่เป็นผู้เคารพภักดีสิ่งที่พวกท่านเคารพภักดี');
INSERT INTO "king_fahad_thai" VALUES(109,5,'109:5','และพวกท่านก็มิใช่เป็นผู้เคารพภักดีพระเจ้าที่ฉันเคารพภักดี');
INSERT INTO "king_fahad_thai" VALUES(109,6,'109:6','สําหรับพวกท่านก็คือศาสนาของพวกท่าน และสําหรับฉันก็คือศาสนาของฉัน');
INSERT INTO "king_fahad_thai" VALUES(110,1,'110:1','เมื่อความช่วยเหลือของอัลลอฮฺ และการพิชิตได้มาถึงแล้ว');
INSERT INTO "king_fahad_thai" VALUES(110,2,'110:2','และเจ้าได้เห็นประชาชนเข้าในศาสนาของอัลลอฮฺเป็นหมู่ ๆ');
INSERT INTO "king_fahad_thai" VALUES(110,3,'110:3','ดังนั้นจงแซ่ซ้องสดุดีด้วยการสรรเสริญพระเจ้าของเจ้า และจงขออภัยโทษต่อพระองค์เถิด แท้จริงพระองค์นั้นเป็นผู้ทรงอภัยโทษเสมอ');
INSERT INTO "king_fahad_thai" VALUES(111,1,'111:1','มือทั้งสองของอะบีละฮับจงพินาศ และเขาก็พินาศแล้ว');
INSERT INTO "king_fahad_thai" VALUES(111,2,'111:2','ทรัพย์สมบัติของเขา และสิ่งที่เขาได้ขวนขวายไว้นั้นมิได้อํานวยประโยชน์แก่เขาเลย');
INSERT INTO "king_fahad_thai" VALUES(111,3,'111:3','เขาจะเข้าไปเผาไหม้ในนรกที่มีไฟลุกโชน');
INSERT INTO "king_fahad_thai" VALUES(111,4,'111:4','ทั้งภริยาของเขาด้วย นางเป็นผู้แบกฟืน');
INSERT INTO "king_fahad_thai" VALUES(111,5,'111:5','ที่คอของนางมีเชือกถักด้วยใยอินทผลัม');
INSERT INTO "king_fahad_thai" VALUES(112,1,'112:1','จงกล่าวเถิด มุฮัมมัด พระองค์คืออัลลอฮฺผู้ทรงเอกะ');
INSERT INTO "king_fahad_thai" VALUES(112,2,'112:2','อัลลอฮฺนั้นทรงเป็นที่พึ่ง');
INSERT INTO "king_fahad_thai" VALUES(112,3,'112:3','พระองค์ไม่ประสูติ และไม่ทรงถูกประสูติ');
INSERT INTO "king_fahad_thai" VALUES(112,4,'112:4','และไม่มีผู้ใดเสมอเหมือนพระองค์');
INSERT INTO "king_fahad_thai" VALUES(113,1,'113:1','จงกล่าวเถิด มุฮัมมัด ข้าพระองค์ขอความคุ้มครองต่อพระเจ้าแห่งรุ่งอรุณ');
INSERT INTO "king_fahad_thai" VALUES(113,2,'113:2','ให้พ้นจากความชั่วร้ายที่พระองค์ได้ทรงบันดาลขึ้น');
INSERT INTO "king_fahad_thai" VALUES(113,3,'113:3','และจากความชั่วร้ายแห่งความมืดของเวลากลางคืนเมื่อมันแผ่คลุม');
INSERT INTO "king_fahad_thai" VALUES(113,4,'113:4','และจากความชั่วร้ายของบรรดาผู้เสกเป่าในปมเงื่อน');
INSERT INTO "king_fahad_thai" VALUES(113,5,'113:5','และจากความชั่วร้ายของผู้อิจฉาเมื่อเขาอิจฉา');
INSERT INTO "king_fahad_thai" VALUES(114,1,'114:1','จงกล่าวเถิด มุฮัมมัด ข้าพระองค์ขอความคุ้มครองต่อพระเจ้าแห่งมนุษย์ชาติ');
INSERT INTO "king_fahad_thai" VALUES(114,2,'114:2','พระราชาแห่งมนุษย์ชาติ');
INSERT INTO "king_fahad_thai" VALUES(114,3,'114:3','พระเป็นเจ้าแห่งมนุษย์ชาติ');
INSERT INTO "king_fahad_thai" VALUES(114,4,'114:4','ให้พ้นจากความชั่วร้ายของผู้กระซิบกระซาบที่หลอกล่อ');
INSERT INTO "king_fahad_thai" VALUES(114,5,'114:5','ที่กระซิบกระซาบในหัวอกของมนุษย์');
INSERT INTO "king_fahad_thai" VALUES(114,6,'114:6','จากหมู่ญินและมนุษย์');
