INSERT INTO "magomed" VALUES(44,7,'44:7','Стигланийн а‚ лаьттан а‚ цу шиннан юккъехь мел долчун а Да /ву Иза/‚ шу /ч1ог1а/ тешаш хиллехь.');
INSERT INTO "magomed" VALUES(44,8,'44:8','Вац цхьа а дела Иза воцург‚ Цо денбо‚ Цо болуьйту‚ шун Кхиош-кхобург ву Иза‚ шун хьалхалера дайн а Кхиош-кхобург ву Иза.');
INSERT INTO "magomed" VALUES(44,9,'44:9','ХIаъ‚ уьш шекончохь ловзаш ма бу.');
INSERT INTO "magomed" VALUES(44,10,'44:10','Ахь тергал де стигло билгала кIур бахьар болу де.');
