INSERT INTO "magomed" VALUES(73,15,'73:15','Баккъалла а, Оха ваийтина шуьга элча /Мухьаммад/ шуна тIехь теш хилла‚ Оха ФирIавне а элча /Муса/ вахийтарх терра.');
