INSERT INTO "pioneers" VALUES(1,1,'1:1','Ба номи Худованди бахшандаи меҳрубон.');
INSERT INTO "pioneers" VALUES(1,2,'1:2','Сипос махсуси Аллоҳ таоло–Парвардигори ҷаҳониён аст.');
INSERT INTO "pioneers" VALUES(1,3,'1:3','Бахшандаи меҳрубон аст.');
INSERT INTO "pioneers" VALUES(1,4,'1:4','Молики рӯзи ҷазост.');
INSERT INTO "pioneers" VALUES(1,5,'1:5','[Парвардигоро] Танҳо Туро мепарастем ва танҳо аз Ту ёрӣ меҷӯем.');
INSERT INTO "pioneers" VALUES(1,6,'1:6','Моро ба роҳи рост ҳидоят фармо [ва ҳамвора собитқадам бигардон].');
INSERT INTO "pioneers" VALUES(1,7,'1:7','Роҳи касоне [ҳамчун паёмбарон, шаҳидон, ростгӯён ва накукорон], ки ба онон неъмат додаӣ ва дар зумраи ғазабшудагон [яҳудиён] ва гумроҳон [масеҳиён] нестанд.');
INSERT INTO "pioneers" VALUES(2,1,'2:1','Алиф, лом, мим.');
INSERT INTO "pioneers" VALUES(2,2,'2:2','Ин китобест, ки ҳеҷ тардиде [шакку гумоне] дар [китоби илоҳӣ будани] он нест ва мояи ҳидояти парҳезгорон аст.');
INSERT INTO "pioneers" VALUES(2,3,'2:3','Он касоне, ки [дар ниҳону ошкор] ба ғайб имон доранд ва намоз барпо медоранд ва аз он чи ба эшон рӯзӣ додаем, инфоқ [сарфу харҷ ва хайр] мекунанд.');
INSERT INTO "pioneers" VALUES(2,4,'2:4','Ва онҳое, ки ба он чи бар ту ва [бар паёмбарони] пеш аз ту нозил шудааст, имон меоваранд ва бар рӯзи растохез яқин доранд.');
INSERT INTO "pioneers" VALUES(2,5,'2:5','Онҳо аз ҷониби Парвардигорашон аз ҳидоят бархӯрдоранд ва ононад, ки растагоранд.');
INSERT INTO "pioneers" VALUES(2,6,'2:6','Бар касоне, ки кофир шудаанд, баробар аст, ки оё бимашон додаи, ё надодаӣ, имон нахоҳанд овард.');
INSERT INTO "pioneers" VALUES(2,7,'2:7','Аллоҳ таоло бар дилҳо ва гӯшҳояшон муҳр [-и ғафлат] задааст ва бар чашмҳояшон пардаест ва барояшон азоби бузурге [дар пеш] аст');
INSERT INTO "pioneers" VALUES(2,8,'2:8','Бархе аз мардум мегӯянд: "Ба Аллоҳ таоло ва рӯзи қиёмат имон овардаем", дар ҳоле ки имон надоранд');
INSERT INTO "pioneers" VALUES(2,9,'2:9','Онон [гумон мекунанд] Аллоҳ таоло ва муъминонро фиреб медиҳанд, дар ҳоле ки фақат худро фиреб медиҳанд, вале намефаҳманд.');
INSERT INTO "pioneers" VALUES(2,10,'2:10','Дар дилҳояшон беморӣ аст ва Аллоҳ таоло бар бемории онон афзудааст ва ба хотири дурӯғҳое, ки мегуфтанд, азоби дардноке [дар пеш] хоҳанд дошт');
INSERT INTO "pioneers" VALUES(2,11,'2:11','Ҳар гоҳ ба онон гуфта шавад: "Дар замин фасод накунед", [ба дурӯғ] мегӯянд: "Мо албатта худ ислоҳгарем"');
INSERT INTO "pioneers" VALUES(2,12,'2:12','Огоҳ бошед! Онон [табаҳкор ва] муфсиданд, вале намефаҳманд');
INSERT INTO "pioneers" VALUES(2,13,'2:13','Ва вақте ба онон гуфта шавад: "Шумо низ ҳамчун мардум [-и мунсиф ва воқеъбин] имон оваред", мегӯянд: "Оё [интизор доред] ҳамчун камхирадон имон оварем"? Огоҳ бошед! [Маҳз худи] Ононанд, ки камхирад ҳастанд, вале намедонанд');
INSERT INTO "pioneers" VALUES(2,14,'2:14','Ва ҳангоме ки муъминонро мулоқот мекунанд, мегӯянд: "Мо [низ ҳамчун шумо] имон овардаем" ва ҳангоме ки бо бузургони шайтонсифати худ хилват мекунанд [дар танҳоӣ якҷо мешаванд], мегӯянд: "Мо бо шумо ҳастем ва фақат [онҳоро] масхара мекунем"');
INSERT INTO "pioneers" VALUES(2,15,'2:15','Аллоҳ таоло тамасхурашон мекунад ва дар туғёнашон саргашта нигаҳ медорад');
INSERT INTO "pioneers" VALUES(2,16,'2:16','Онон касоне ҳастанд, ки гумроҳиро ба [баҳои] ҳидоят хариданд, пас, тиҷораташон суде надорад ва ҳидоятёфта набуданд.');
INSERT INTO "pioneers" VALUES(2,17,'2:17','Достони инон [мунофиқон] ҳаммонанди достони касест, ки оташе афрӯхт, пас, чун оташ перомунашро равшан сохт, Аллоҳ таоло нурашонро бигирифт ва дар торикиҳое, ки намебинанд, раҳояшон кард');
INSERT INTO "pioneers" VALUES(2,18,'2:18','Кару лолу кӯранд ва [аз гумроҳии худ] бознамегарданд');
INSERT INTO "pioneers" VALUES(2,19,'2:19','Ё [мисолашон] ҳамчун [гирифторони] борони сахт (сел) аст, ки аз осмон [фурӯ мерезад] ва дар он торикиҳо ва раъду барқ бошад ва онон аз тарси марг ангуштони худро дар гӯшҳояшон фурӯ мебаранд, то садои соиқа [оташак]-ро нашнаванд ва Аллоҳ таоло ба кофирон иҳота дорад');
INSERT INTO "pioneers" VALUES(2,20,'2:20','Наздик аст, ки барқ чашмонашонро бирабояд, ҳар гоҳ ки [барқ мезанад ва роҳро] бар онон равшан месозад, гоме дар [партави] он бармедоранд ва чун торикӣ ононро фаро гирад, [ҳайратзада] бозмеистанд; ва агар Аллоҳ таоло бихоҳад, шунавоӣ ва биноиашонро аз байн мебарад, зеро Аллоҳ таоло бар ҳар коре тавоност');
INSERT INTO "pioneers" VALUES(2,21,'2:21','Эй мардум, Парвардигори худро парастиш кунед; он касе, ки шумо ва пешиниёни шуморо офарид. Бошад, ки парҳезгор шавед.');
INSERT INTO "pioneers" VALUES(2,22,'2:22','Он, ки заминро барои шумо бигустурд ва осмонро [ҳамчун сақфе] болои саратон қарор дод ва аз осмон обе фурӯ фиристод ва ба василаи он анвои меваҳоро ба вуҷуд овард, то рӯзии шумо бошад. Бинобар ин барои Аллоҳ таоло ҳамтоёне қарор надиҳед, дар ҳоле ки [ҳақиқатро] медонед.');
INSERT INTO "pioneers" VALUES(2,23,'2:23','Ва агар дар бораи он чи бар бандаи худ [Муҳаммад] нозил кардаем, дар шакку тардид ҳастед, агар рост мегӯед, сурае ҳаммонанди он биёваред ва гувоҳони худро – ғайр аз Аллоҳ таоло – фаро хонед.');
INSERT INTO "pioneers" VALUES(2,24,'2:24','Пас, агар чунин накардед – ва ҳаргиз натавонед кард – аз оташе битарсед, ки ҳезуми он мардуму сангҳо ҳастанд ва барои кофирон омода шудааст.');
INSERT INTO "pioneers" VALUES(2,25,'2:25','[Эй паёмбар] Ба касоне, ки имон оварда ва корҳои шоиста анҷом додаанд, мужда бидеҳ, ки боғҳое [аз биҳишт] барояшон [дар пеш] аст, ки аз зери [дарахтони] он ҷӯйборҳо ҷорӣ аст. Ҳар замон, ки мевае аз он ба эшон дода шавад, мегӯянд: "Ин ҳамон аст, ки пеш аз ин [дар дунё] рӯзии мо шуда буд; дар ҳоле ки шабеҳи он ба эшон дода шуда буд [на худи он] ва [дар он ҷо] ҳамсарони поке доранд ва ҷовидона дар он хоҳанд буд"');
INSERT INTO "pioneers" VALUES(2,26,'2:26','Аллоҳ таоло аз ин, ки ба пашшае–ё болотар аз он–масал бизанад, шарм намекунад. Онон, ки имон овардаанд, медонанд, ки он [масал] ҳақ аст аз тарафи Парвардигорашон [ва аз он панд мегиранд] ва аммо онҳое, ки кофиранд, мегӯянд: "Аллоҳ таоло аз ин масал чӣ хостааст?" Аллоҳ таоло [бо баёни ин амсол] гурӯҳи бисёреро [ки муставҷиби онанд ҳайрон ва] гумроҳ ва гурӯҳи бисёреро ҳидоят мекунад; ва танҳо [мунофиқон ва] фосиқонро бо он гумроҳ месозад');
INSERT INTO "pioneers" VALUES(2,27,'2:27','[Ҳамон] касоне, ки паймони Аллоҳро – пас аз маҳкам сохтанаш – мешикананд ва равобити [накуи хешовандӣ]-ро, ки Аллоҳ таоло ба барқарории он фармон додааст, қатъ ва дар замин фасод мекунанд. Инон [дар дунё ва охират] зиёндидагонанд.');
INSERT INTO "pioneers" VALUES(2,28,'2:28','Чи гуна [яктоии] Аллоҳро инкор мекунед, дар ҳоле ки моддаи беҷон будед ва Ӯ шуморо ҳаёт бахшид. Боз шуморо мемиронад ва баъд аз он зинда хоҳад кард, баъд аз он [барои бозпурсӣ] ба пешгоҳаш боз гардонда мешавед.');
INSERT INTO "pioneers" VALUES(2,29,'2:29','Ӯст, ки ҳамаи он чиро, ки дар замин аст, бароятон офарид, Пас аз он ба сӯи осмон рӯй ва онҳоро ба сурати ҳафт осмон сомон дод ва Ӯ ба ҳар чиз доност.');
INSERT INTO "pioneers" VALUES(2,30,'2:30','Ва [ёд кун] ҳангоме ки Парвардигорат ба фариштагон гуфт: "Ман дар замин [барои башари номасъулу беихтиёр] ҷонишине хоҳам гумошт [ки масъулу мукаллаф аст]. Онон гуфтанд: Оё касеро дар он ҷо мегуморӣ, ки табаҳкорӣ ва хунрезӣ кунад, дар ҳоле ки мо Туро бо сипос ба покӣ меситоем ва тақдис мекунем? Аллоҳ таоло гуфт: "Ман [дар бораи ҳадафу маслиҳати офариниши ӯ] чизҳое медонам, ки шумо намедонед"');
INSERT INTO "pioneers" VALUES(2,31,'2:31','Он гоҳ ҳамаи номҳо [-и ашё ва мавҷудот]-ро ба Одам омӯхт. Сипас онҳоро ба фариштагон арза дошт ва фармуд: "Агар рост мегӯед, аз номҳои инҳо ба ман хабар диҳед"');
INSERT INTO "pioneers" VALUES(2,32,'2:32','[Фариштагон] Гуфтанд: "Муназзаҳӣ [покӣ], Ту! Мо чизе ҷуз он чи ба мо омӯхтаӣ намедонем. Ту донои ҳакимӣ"');
INSERT INTO "pioneers" VALUES(2,33,'2:33','Фармуд: "Эй Одам, ононро аз номҳояшон огоҳ кун". Ҳангоме ки вай ононро огоҳ кард, [Аллоҳ таоло] фармуд: "Оё ба шумо нагуфтам, ки ман асрори осмонҳову заминро медонам ва аз он чи, ки ошкор мекунед ва пинҳон медоштед, огоҳам"?');
INSERT INTO "pioneers" VALUES(2,34,'2:34','[Ёд кун] Ҳангоме ки ба фариштагон гуфтем: "Барои [бузургдошту эҳтиром бар] Одам саҷда кунед"! Пас, [ҳамагӣ] саҷда карданд, ба ҷуз иблис, ки худдорӣ кард ва такаббур варзид ва аз кофирон шуд.');
INSERT INTO "pioneers" VALUES(2,35,'2:35','Гуфтем: "Эй Одам, ту ва ҳамсарат [Ҳаво] дар биҳишт сукунат кунед ва аз [неъматҳои] он – аз ҳар ҷо, ки мехоҳед – гуворо бихӯред, аммо ба ин дарахт наздик нашавед, ки [ба хотири сарпечии фармони илоҳӣ] дар зумраи ситамгорон хоҳед буд"');
INSERT INTO "pioneers" VALUES(2,36,'2:36','Пас, шайтон муҷиби лағжиш [ва сарпечии] он ду шуд. Сипас Аллоҳ таоло онҳоро аз он чи дар он буданд [биҳишт] берун кард ва [ба он ду ва шайтон] гуфтем: "Ҳамагӣ [ба замин] фуруд оед, дар ҳоле ки душмани якдигар хоҳед буд ва бароятон дар замин то муддати муайян қароргоҳ ва василаи баҳрабардорӣ вуҷуд хоҳад дошт"');
INSERT INTO "pioneers" VALUES(2,37,'2:37','Он гоҳ Одам аз Парвардигораш калимоте фаро гирифт [ва дархости бахшоиш намуд] ва Аллоҳ таоло тавбаи ӯро пазируфт, чаро ки Аллоҳ таоло тавбапазиру меҳрубон аст');
INSERT INTO "pioneers" VALUES(2,38,'2:38','Гуфтем: "Ҳамагӣ аз он [биҳишт] фуруд оед ва ҳар гоҳ аз ҷониби Ман раҳнамуде бароятон омад, касоне, ки аз он [ҳидоят] пайравӣ кунанд, на тарсе хоҳанд дошт ва на андӯҳгин мешаванд"');
INSERT INTO "pioneers" VALUES(2,39,'2:39','Ва касоне, ки кофир шуданд ва нишонаҳои [қудрату яктоии] моро дурӯғ пиндоштанд, онон аҳли дӯзаханд ва ҳамеша дар он хоҳанд буд');
INSERT INTO "pioneers" VALUES(2,40,'2:40','Эй Бани Исроил [фарзандони Яъқуб], неъматҳоеро, ки ба шумо арзонӣ доштам, ба ёд оваред ва ба паймоне, ки бо ман бастаед, вафо кунед, то Ман низ ба паймони [худ бо] шумо [дар бораи неъматҳои дунё ва охират] вафо кунам ва танҳо аз ман битарсед');
INSERT INTO "pioneers" VALUES(2,41,'2:41','Ва ба он чи нозил кардаам [Қуръон] имон биёваред, ки тасдиқкунандаи чизест, ки бо шумост ва нахустин кофир ба он набошед ва оёти маро ба баҳои ночиз нафурӯшед ва танҳо аз Ман битарсед');
INSERT INTO "pioneers" VALUES(2,42,'2:42','Ва, дар ҳоле ки [ҳақиқатро] медонед, ҳақро бо ботил наёмезед ва онро китмон [пинҳон] накунед');
INSERT INTO "pioneers" VALUES(2,43,'2:43','Ва намоз барпо доред ва закот бипардозед ва бо намозгузорон намоз бихонед');
INSERT INTO "pioneers" VALUES(2,44,'2:44','Оё, дар ҳоле ки китоби [Таврот] – ро мехонед, мардумро ба некӣ фармон медиҳеду худро фаромӯш мекунед? Оё намеандешед?');
INSERT INTO "pioneers" VALUES(2,45,'2:45','Ва аз шикебоиву намоз ёрӣ ҷӯед; ва ба ростӣ, ки он [намоз] ҷуз ба фурӯтанон душвору гарон аст');
INSERT INTO "pioneers" VALUES(2,46,'2:46','Ҳамон касоне, ки яқин доранд, ки ба дидори Парвардигори хеш ноил хоҳанд шуд ва ба пешгоҳи Ӯ бозмегарданд');
INSERT INTO "pioneers" VALUES(2,47,'2:47','Эй Бани Исроил, неъматҳоеро, ки ба шумо арзонӣ доштам, ба ёд оваред ва [низ] ин ки шуморо [ба далели бархӯрдорӣ аз ҳидоят] ба ҷаҳониён бартарӣ додам');
INSERT INTO "pioneers" VALUES(2,48,'2:48','Ва аз рӯзе битарсед, ки касе чизе [аз азоби Аллоҳ таоло] – ро аз касе дафъ намекунад ва аз ӯ шафоат пазируфта намешавад ва на ғаромату фидяе гирифта мешавад ва на ёрӣ мешаванд');
INSERT INTO "pioneers" VALUES(2,49,'2:49','Ва [ба ёд оваред] ҳангоме ки шуморо аз чанголи фиръавниён наҷот додем. [Онон] Бадтарин азобро бар саратон меоварданд: Писаронатонро мекуштанд ва занонатонро [барои канизӣ] зинда нигаҳ медоштанд ва дар инҳо озмоиши бузурге аз ҷониби Парвардигоратон барои шумо буд');
INSERT INTO "pioneers" VALUES(2,50,'2:50','Ва [ба ёд оваред] ҳангоме ки дарёро бароятон шикофтем ва шуморо наҷот додем ва фиръавниёнро, дар ҳоле ки [ба азоби онҳо бо чашми худ] менигаристед, ғарқ сохтем');
INSERT INTO "pioneers" VALUES(2,51,'2:51','Ва [ба ёд оваред] ҳангомеро ки [барои нузули Таврот] чиҳил шаб бо Мӯсо ваъда гузоридем, он гоҳ шумо баъд аз ӯ гӯсоларо [ба парастиш] гирифтед, дар ҳоле ки [нисбат ба хештан] ситамгор будед');
INSERT INTO "pioneers" VALUES(2,52,'2:52','Сипас шуморо баъд аз он [гуноҳ] бахшидем; бошад, ки сипосгузорӣ кунед');
INSERT INTO "pioneers" VALUES(2,53,'2:53','Ва [низ ба ёд оваред] ҳангоме ки ба Мӯсо китоби [Таврот] – ро, ки ҷудокунанда [-и ҳақ аз ботил] аст, ато кардем; бошад, ки ҳидоят шавед');
INSERT INTO "pioneers" VALUES(2,54,'2:54','Ва [ба ёд оваред] замонеро, ки Мӯсо ба қавми худ гуфт: "Эй қавми ман, шумо бо интихоби гӯсола [барои парастиш] ба худ ситам кардаед, пас, ба сӯйи Офаридгори худ бозгардед ва тавба кунед ва [хатокорони] худро бикушед. Ин кор барои шумо дар пешгоҳи Парвардигоратон беҳтар аст". Он гоҳ Аллоҳ таоло тавбаи шуморо пазируфт. Ба ростӣ, ки Ӯ тавбапазиру меҳрубон аст');
INSERT INTO "pioneers" VALUES(2,55,'2:55','Ва [ба ёд оваред] ҳангомеро ки гуфтед: "Эй Мӯсо, мо ҳаргиз ба ту имон намеоварем, магар ин ки Аллоҳро ошкоро бубинем". Пас, соиқа [оташак] шуморо дар ҳоле гирифт, ки [азоби худро бо чашми хеш] менигаристед');
INSERT INTO "pioneers" VALUES(2,56,'2:56','Сипас шуморо пас аз маргатон барангехтем, бошад, ки сипосгузорӣ кунед');
INSERT INTO "pioneers" VALUES(2,57,'2:57','Ва абрро бар шумо соябон қарор додем ва манну салво [шаҳду бедона] бароятон фурӯ фиристодем [ва гуфтем]: "Аз неъматҳои покизае, ки ба шумо рӯзӣ додаем, бихӯред" [вале куфр варзидед]. Онон [бо носипосиву нофармонӣ] ба мо ситам накарданд, балки ба худ ситам раво доштанд');
INSERT INTO "pioneers" VALUES(2,58,'2:58','Ва [ба ёд оваред] ҳангомеро, ки гуфтем: "Ба ин шаҳр [Байт-ул-муқаддас] ворид шавед ва аз [неъматҳои] он, аз ҳар ҷо [ва ҳар вақт], ки хостед, бихӯред ва бо фурӯтанӣ аз дарвоза ворид шавед ва бигӯед: "[Парвардигоро, гуноҳонамонро] фурӯ рез". [Чунин бигӯед] То гуноҳонатонро биёмурзем ва [яқинан] бар [подоши] накукорон хоҳем афзуд');
INSERT INTO "pioneers" VALUES(2,59,'2:59','Он гоҳ ситамгарон ин суханро, ки ба онҳо гуфта шуда буд, тағйир доданд [ва ба ҷояш калимоти масхараомезе гуфтанд]. Аз ин рӯ, бар ситамгарон, ба сазои ин нофармониашон, азобе аз осмон нозил кардем');
INSERT INTO "pioneers" VALUES(2,60,'2:60','Ва [ба ёд оваред] ҳангомеро, ки Мӯсо барои қавми хеш об хост, пас, гуфтем: "Асои худро бар санг бизан". Он гоҳ дувоздаҳ чашма аз он ҷӯшид ва ҳар гурӯҳе обнӯшгоҳи худро донист [ва гуфтем]: "Аз рӯзии Аллоҳ таоло бихӯред ва биёшомед ва дар замин ба фасод [ва табоҳӣ] накӯшед"');
INSERT INTO "pioneers" VALUES(2,61,'2:61','Ва [ба ёд оваред] замонеро, ки гуфтед: "Эй Мӯсо, мо ҳаргиз тоби таҳаммули як навъ ғизоро надорем, пас, Парвардигори хешро бихон, то аз рӯиданиҳои замин, аз сабзиву бодиринг ва гандуму наск ва пиёз бароямон бирӯёнад". [Мӯсо] Гуфт: "Оё чизеро, ки пасттар аст, бо чизи беҳтар ҷойгузин мекунед? Ба шаҳре фуруд оед; зеро он чи ки хостед, дар он ҷо бароятон вуҷуд дорад" ва [чунин шуд, ки муҳри] зиллату бенавоӣ бар онон зада шуд ва сазовори хашми Аллоҳ таоло гардиданд, чаро ки онон ба оёти Аллоҳ таоло куфр меварзиданд ва паёмбаронро ба ноҳақ мекуштанд. Инҳо ба хотири он буд, ки гунаҳгору мутаҷовиз буданд');
INSERT INTO "pioneers" VALUES(2,62,'2:62','Дар ҳақиқат касоне, ки [холисона ба ислом] имон овардаанд ва онон, ки яҳудӣ шуданд ва масеҳиёну собеон [фитратгароён], ҳар, ки ба Аллоҳ таоло ва рӯзи қиёмат имон биёварад ва кори шоистае анҷом диҳад, подошашон назди Парвардигорашон аст; на тарсе хоҳанд дошт ва на андӯҳгин мешаванд');
INSERT INTO "pioneers" VALUES(2,63,'2:63','Ва [ба ёд оваред] замонеро, ки аз шумо паймон гирифтем ва [кӯҳи] Турро болои саратон барафроштем [ва гуфтем]: "Он чи [аз аҳкоми Таврот] ба шумо додаем, бо ҷиддият бигиред ва ончиро, ки дар он аст, ёд кунед, бошад, ки парҳезкор шавед"');
INSERT INTO "pioneers" VALUES(2,64,'2:64','Вале баъд [аз он ҳама аҳду паймон] аз ин [фармон] рӯй бартофтед ва агар фазлу бахшиши Аллоҳ таоло бар шумо намебуд, яқинан дар зумраи зиёнкорон будед');
INSERT INTO "pioneers" VALUES(2,65,'2:65','Мусалламан аз [шарҳи ҳоли] касоне аз худатон огоҳ шудаед, ки дар [мавриди ҳукми ҳурмати сайди моҳӣ дар рӯзҳои] шанбе таҷовуз карданд. Пас, ба онҳо гуфтем: "Бӯзинаҳои хору рондашуда бошед"!');
INSERT INTO "pioneers" VALUES(2,66,'2:66','Пас, он [кайфар] – ро ибрате барои мардуми он замон ва наслҳои пас аз эшон ва [ҳамчунин] панду андарзе барои парҳезгорон қарор додем');
INSERT INTO "pioneers" VALUES(2,67,'2:67','Ва [ба ёд оваред] ҳангоме ки Мӯсо ба қавми худ гуфт: "Аллоҳ таоло ба шумо дастур медиҳад, ки модаговеро забҳ кунед". Онон гуфтанд: "Оё моро масхара мекунӣ"? [Мӯсо] гуфт: "[Тамасхур кори ҷоҳилон аст ва] Ба Аллоҳ таоло паноҳ мебарам, аз ин ки дар зумраи ҷоҳилон бошам"');
INSERT INTO "pioneers" VALUES(2,68,'2:68','Гуфтанд: "Аз Парвардигорат бихоҳ, то барои мо равшан кунад, ки он чи гуна [гове] аст"? [Мӯсо] Гуфт: [Аллоҳ таоло] Мефармояд: "Он гове аст на пир ва на ҷавон, балки миёнасоли байни ин ду бошад. Пас, [акнун] он чиро, ки дастур ёфтаед, анҷом диҳед"');
INSERT INTO "pioneers" VALUES(2,69,'2:69','Гуфтанд: "Аз Парвардигорат бихоҳ, то барои мо равшан созад, ки рангаш чи гуна аст"? [Мӯсо] Гуфт: [Аллоҳ таоло] Мефармояд: "Он гов зарди пуррангест, ки [рангаш] бинандагонро шод мекунад"');
INSERT INTO "pioneers" VALUES(2,70,'2:70','Гуфтанд: "Аз Парвардигорат [боз ҳам] бихоҳ, то чигунагии он говро [аз ҳар ҷиҳат] барои мо равшан созад, зеро [вижагиҳои] ин гов ҳанӯз бароямон мубҳам аст ва иншоаллоҳ [бо тавзеҳоти ту] ҳидоят хоҳем шуд"');
INSERT INTO "pioneers" VALUES(2,71,'2:71','[Мӯсо] гуфт: "Вай мефармояд: "Он гове аст на чунон ром, ки заминро ҷуфт кунад ва ё киштзорро об диҳад, [аммо] беайб асту якранг ва дар он ҳеҷ ранги дигаре нест". Онон гуфтанд: "Инак сухани дуруст овардӣ". Сипас [чунон говеро пайдо карданд ва] онро сар буриданду [албатта] наздик буд, ки ин корро накунанд');
INSERT INTO "pioneers" VALUES(2,72,'2:72','Ва [ба ёд оваред] ҳангоме ки фардеро куштед ва дар бораи [қотили] ӯ ба низоъ пардохтед ва Аллоҳ таоло чизеро, ки пинҳон мекардед, ошкор намуд');
INSERT INTO "pioneers" VALUES(2,73,'2:73','Пас гуфтем: "Порае аз [бадани] говро ба мақтул бизанед [то зинда шавад ва қотили хешро муаррифӣ кунад]. Аллоҳ таоло мурдагонро ҳамин гуна зинда мекунад ва нишонаҳои [ошкори қудрати] хешро ба шумо нишон медиҳад, бошад, ки бияндешед"');
INSERT INTO "pioneers" VALUES(2,74,'2:74','Пас аз он [бо вуҷуди ин муъҷизаи бузург] дилҳои шумо сахт шуд, ҳамчун санг, ё сахттар, зеро [ҳоли дилҳоятон ҳаргиз тағйир намекунад, аммо сангҳо дучори тағйиру таҳаввул мешаванд, чунон ки] бархе аз сангҳо мешикофанд ва аз онҳо ҷӯйборҳое ҷорӣ мешаванд ва бархе мешикофанду [чашмаҳои] об аз онҳо меҷӯшад ва бархе аз тарси Аллоҳ таоло [аз фарози кӯҳҳо] фурӯ меафтанд. Ва Аллоҳ таоло аз он чи мекунед, ғофил нест');
INSERT INTO "pioneers" VALUES(2,75,'2:75','[Эй муъминон, ҳоло ки ҳақиқати душмании яҳудиён бароятон ошкор гардид] Оё умед доред, ки ба [оини] шумо имон биёваранд, бо ин ки гурӯҳе аз [уламои] онон суханони Аллоҳро [дар Таврот] мешуниданд ва пас аз фаҳмиданаш [алфозу маонии] онро таҳриф мекарданд, ҳол он ки [аз бузургии гуноҳи хеш] иттилоъ доштанд');
INSERT INTO "pioneers" VALUES(2,76,'2:76','Ва [яке аз фиребкориҳояшон ин аст, ки] вақте бо муъминон рӯбарӯ мешаванд мегӯянд: "[Мо ҳам] имон овардаем", вале ҳангоме ки бо якдигар хилват мекунанд, мегӯянд: "Чаро матолиберо, ки [дар бораи вижагиҳои паёмбар ва ҳаққонияти ислом дар Таврот омадааст ва] Аллоҳ таоло барои шумо баён кардааст, барои мусулмонон бозгӯ мекунед, то [рӯзи қиёмат] дар пешгоҳи Парвардигоратон алайҳи [бар зидди] шумо ба он истидлол кунанд? Оё намефаҳмед"?');
INSERT INTO "pioneers" VALUES(2,77,'2:77','Оё намедонанд, ки Аллоҳ таоло он чиро, ки пинҳон медоранд ва ошкор месозанд, [ҳамаро] медонад?!');
INSERT INTO "pioneers" VALUES(2,78,'2:78','Ва бархе аз онон бесаводоне ҳастанд, ки [фақат ба тиловати содаи Таврот дилхушанд] ва китоби Аллоҳро ҷуз мушти хаёлоти хом намедонанд ва танҳо ба пиндорҳои хеш [ки аз бузургонашон фаро гирифтаанд] дил бастаанд');
INSERT INTO "pioneers" VALUES(2,79,'2:79','Пас, вой бар касоне, ки бо дасти худ менависанд, сипас мегӯянд: "Ин аз ҷониби Аллоҳ таоло аст", то онро ба баҳои андак бифурушанд! Пас, вой бар онҳо аз он чи дастҳояшон навиштааст ва вой бар онон аз он чи [аз ин роҳ] ба даст меоваранд!');
INSERT INTO "pioneers" VALUES(2,80,'2:80','Ва [яҳудиён] гуфтанд: "Оташи дӯзах ҷуз чанд рӯзи маҳдуде ҳаргиз ба мо нахоҳад расид". Бигӯ: "Оё аз назди Аллоҳ таоло паймоне гирифтаед, ки Аллоҳ таоло ҳаргиз хилофи паймонаш накунад? Ё чизеро, ки намедонед, ба Аллоҳ таоло нисбат медиҳед"?!');
INSERT INTO "pioneers" VALUES(2,81,'2:81','Оре, ҳар ки муртакиби гуноҳе шавад ва гуноҳонаш ӯро иҳота кунад, онон аҳли оташанд ва ҷовидона дар он хоҳанд буд');
INSERT INTO "pioneers" VALUES(2,82,'2:82','Ва касоне, ки имон оваранд ва корҳои шоиста анҷом диҳанд, онон аҳли биҳиштанд ва ҷовидона дар он хоҳанд монд');
INSERT INTO "pioneers" VALUES(2,83,'2:83','Ва [ба ёд оваред] ҳангоме ки аз Бани Исроил паймон гирифтем, ки: "Ҷуз Аллоҳи ягонаро парастиш накунед ва ба падару модару наздикону ятимон ва бенавоён некӣ кунед ва ба мардум [сухани] нек бигӯеду намоз барпо доред ва закот бипардозед", вале ҳамаи шумо, ҷуз иддаи каме пушт гардонидед ва рӯйгардон шудед');
INSERT INTO "pioneers" VALUES(2,84,'2:84','Ва [ба ёд оваред] ҳангоме ки аз шумо паймон гирифтем, ки хуни якдигарро нарезед ва ҳамдигарро аз сарзаминатон берун накунед; сипас шумо иқрор кардед ва худ [бар он паймон] гувоҳӣ медиҳед');
INSERT INTO "pioneers" VALUES(2,85,'2:85','Пас аз он, ҳамин шумо ҳастед, ки якдигарро мекушед ва гурӯҳе аз худатонро аз сарзаминашон берун мекунед ва ба гуноҳу ситам алайҳи онон ба якдигар кумак мекунед ва агар [баъзе аз онон дар ҷанг бо ҳампаймонони араби шумо гирифтор шаванд ва ба сурати] асир наздатон биёянд [бо он ки ҳамдини шумо ҳастанд, дар баробари озодиашон] фидя мегиред, ҳол он ки ихроҷи онон [аз ҳамон оғоз] бар шумо ҳаром буд. Оё ба бахше аз [дастуроти] китоб [-и осмонӣ дар мавриди фидя гирифтан] имон меоваред ва ба бахше [ки шуморо дар бораи ҳамдинонатон супориш мекунад] кофир мешавед? Пас, ҷазои ҳар касе аз шумо, ки чунин кунад, ҷуз расвоӣ ва хорӣ дар ин ҷаҳон нахоҳад буд ва дар рӯзи қиёмат ба шадидтарин азоб гирифтор мешаванд; ва [яқин бидонед, ки] Аллоҳ таоло аз он чи анҷом медиҳед, ғофил нест');
INSERT INTO "pioneers" VALUES(2,86,'2:86','Инон касоне ҳастанд, ки зиндагонии дунёро ба баҳои охират хариданд, пас, азоби онҳо тахфиф дода намешавад ва касе ёриашон нахоҳад кард');
INSERT INTO "pioneers" VALUES(2,87,'2:87','Ҳамоно ба Мӯсо китоби [Таврот] додем ва пас аз ӯ паёмбарони пайдарпай фиристодем; ва ба Исо писари Марям муъҷизоту далоили равшан додем ва ӯро ба василаи Рӯҳулқудус [Ҷабраил] тақвият кардем. Оё чунин нест, ки ҳар гоҳ паёмбаре чизе бархилофи ҳавои нафсатон оварад, дар баробараш такаббур намудед ва гурӯҳеро такзиб кардед ва гурӯҳеро куштед');
INSERT INTO "pioneers" VALUES(2,88,'2:88','Ва [яҳудиён барои пайравӣ накардан аз Муҳаммад (с) аз рӯйи тамасхур] гуфтанд: "Дилҳои мо пӯшида ва дар ғилоф аст", [чунин нест] балки Аллоҳ таоло онро ба сазои куфрашон лаънат кардааст, пас, [ба ҳамин далел аст, ки] бисёр кам имон меоваранд');
INSERT INTO "pioneers" VALUES(2,89,'2:89','Ва ҳангоме ки аз тарафи Аллоҳ таоло китобе [Қуръон] барои онон омад, ки тасдиқкунандаи матолибе буд, ки бо худ доштанд [Таврот] ва [ба интизор расидани ин китоб] аз қабл ба худ навиди пирӯзӣ бар кофиронро медоданд, бо ин ҳама вақте он касеро, ки аз пеш шинохта буданд [Муҳаммад], наздашон омад, инкораш карданд. Лаънати Аллоҳ таоло бар кофирон бод!');
INSERT INTO "pioneers" VALUES(2,90,'2:90','Ба чи баҳои баде худро фурӯхтанд, ки аз рӯйи ҳасад [ва бартарихоҳӣ] ба он чи Аллоҳ таоло фиристода буд, кофир шуданд, ки чаро Аллоҳ таоло аз фазли хеш бар ҳар кас аз бандагонаш, ки мехоҳад [ваҳй] нозил мекунад. Пас, [ба хотири ин ҳасодат] беш аз пеш ба хашми [илоҳӣ] гирифтор шуданд ва барои кофирон азоби хоркунандае [дар пеш] аст');
INSERT INTO "pioneers" VALUES(2,91,'2:91','Ва чун ба онон гуфта шавад: "Ба он чи Аллоҳ таоло нозил кардааст, имон биёваред", мегӯянд: "Мо ба чизе имон меоварем, ки бар худи мо нозил шудааст" ва ба ғайри он кофир мешаванд, дар ҳоле ки он [Қуръон сухани] ҳақ ва тасдиқкунандаи матолибе аст, ки бо худ доранд. [Эй паёмбар, ба онон] Бигӯ: "Агар [рост мегӯед ва] муъминед, пас, чаро паёмбарони Аллоҳро пеш аз ин мекуштед"?!');
INSERT INTO "pioneers" VALUES(2,92,'2:92','Ва ба ростӣ, Мӯсо муъҷизаҳову нишонаҳои равшане бароятон овард, он гоҳ шумо пас аз вай дар ҳоле гӯсоларо [барои парастиш] баргузидед, ки [ба хотири ин ширкварзӣ нисбат ба худ] ситамгор будед');
INSERT INTO "pioneers" VALUES(2,93,'2:93','Ва [ба ёд оваред] ҳангомеро, ки аз шумо паймон гирифтем [ки аз фармудаҳои илоҳӣ ва раҳнамудҳои Мӯсо пайравӣ кунед] ва [кӯҳи] Турро болои саратон барафроштем [ва гуфтем]: "Он чи [аз аҳкоми Таврот] ба шумо додаем, маҳкам бигиред ва [ба каломи Аллоҳ таоло] гӯш фаро диҳед". Гуфтанд: "[Бо гӯшҳоямон] Шунидем, [вале бо рафторамон] нофармонӣ кардем"; ва ба сабаби куфрашон бо [муҳаббати] гӯсола дар дилҳояшон ҷой гирифт. [Эй паёмбар, ба онон] Бигӯ: "Агар [воқеан] муъмин ҳастед, имонатон чи фармони баде ба шумо медиҳад"');
INSERT INTO "pioneers" VALUES(2,94,'2:94','Бигӯ: "[Эй қавми яҳуд] Агар назди Аллоҳ таоло сарои охират вижаи шумост на барои дигар мардум, пас, агар рост мегӯед, орзуи марг кунед [то ҳар чи зудтар ба ин ҷойгоҳ даст ёбед]"');
INSERT INTO "pioneers" VALUES(2,95,'2:95','Вале онон ба хотири он чи [аз куфру такзиб ва таҳриф] пеш фиристодаанд, ҳаргиз орзуи марг нахоҳанд кард ва Аллоҳ таоло ба [аҳволи] ситамгорон доност');
INSERT INTO "pioneers" VALUES(2,96,'2:96','[Эй паёмбар] Ҳатман, эшон [яҳудиён]-ро ҳаристарини мардум бар зиндагӣ хоҳӣ ёфт, [ҳатто] ҳаристар аз мушрикон. Ҳар як аз онон дӯст дорад, ки ҳазор сол умр бинад, дар ҳоле ки [ҳатто] агар ин умр [-и дароз] ба ӯ дода шавад, [боз ҳам тӯли умраш] ӯро аз азоб дур намекунад ва Аллоҳ таоло ба он чи мекунанд, биност');
INSERT INTO "pioneers" VALUES(2,97,'2:97','Бигӯ: "Касе, ки душмани Ҷабраил бошад, [дар ҳақиқат душмани Аллоҳ таоло аст, зеро] ӯ ба фармони Аллоҳ таоло Қуръонро бар қалби ту нозил кардааст, ки китобҳои осмонии пеш аз худро тасдиқ мекунад ва барои муъминон ҳидояту башорат аст"');
INSERT INTO "pioneers" VALUES(2,98,'2:98','Ҳар ки душмани Аллоҳ таоло ва фариштагони Ӯ ва паёмбаронаш ва Ҷабраилу Микоил бошад, [бидонад, ки] Аллоҳ таоло душмани кофирон аст');
INSERT INTO "pioneers" VALUES(2,99,'2:99','[Эй паёмбар] Ба ростӣ, ки мо нишонаҳои равшане бар ту нозил кардем ва ҷуз мунҳарифон [аз дин] касе ба онҳо куфр намеварзад');
INSERT INTO "pioneers" VALUES(2,100,'2:100','Оё чунин нест, ки ҳар гоҳ онон паймоне [бо Аллоҳ таоло ва Расул] бастанд, гурӯҳе аз эшон онро дур афканданд [ва паймон шикастанд]?! Оре бештарашон имон намеоваранд');
INSERT INTO "pioneers" VALUES(2,101,'2:101','Ва ҳангоме ки фиристодае аз сӯйи Аллоҳ таоло ба суроғашон омад, ки бо нишонаҳое, ки [дар Таврот] назди онон буд мутобиқат дошт, гурӯҳе аз эшон, ки ба онҳо китоби [Таврот] дода шуда буд, китоби Аллоҳро пушти сар афканданд, гӯӣ [аз он нишонаҳо] ҳеҷ намедонанд');
INSERT INTO "pioneers" VALUES(2,102,'2:102','Ва аз он чи шаётин дар [аҳди] фармонравоии Сулаймон [бар мардум] мехонданд, пайравӣ карданд [ва муддаӣ шуданд, ки Сулаймон подшоҳии хешро бо сеҳр устувор кардааст] ва [ҳол он ки] Сулаймон [ҳаргиз даст ба сеҳр наолуд] ва кофир нашуд, вале шаётин [ки] куфр варзиданд ба мардум сеҳр меомӯхтанд ва [низ] аз он чи бар ду фаришта: Ҳорут ва Морут дар Бобул нозил шуда буд, [пайравӣ карданд, вале он ду фаришта] ба ҳеҷ кас чизе ёд намедоданд, магар ин ки [қаблан аз рӯйи хайрхоҳӣ ба ӯ] мегуфтанд: "Мо василаи озмоиш ҳастем, пас, [бо истифода бурдани сеҳр] кофир нашав". Онон [яҳудиён] аз он ду [фаришта] чизҳое меомӯхтанд, ки [битавонанд] тавассути он миёни марду ҳамсараш ҷудоӣ афкананд, ҳол он ки бидуни иҷозатти Аллоҳ таоло наметавонистанд ба василаи он ба касе зиёне бирасонанд ва чизе меомӯхтанд, ки [фақат] ба онон зиён мерасонад ва суде намедод ва ҳатман медонистанд, ки ҳар кас [муштоқу] харидори он [ҷодуҳо] бошад, дар охират баҳрае нахоҳад дошт. Агар [ҳақиқатро] медонистанд [мутаваҷҷеҳ мешуданд, ки] чи баду зишт буд он чи худро ба он фурӯхтанд!');
INSERT INTO "pioneers" VALUES(2,103,'2:103','Ва агар онҳо имон меоварданд ва парҳезгорӣ мекарданд, агар огаҳӣ доштанд [дармеёфтанд, ки] подоше, ки назди Аллоҳ таоло аст барояшон беҳтар буд');
INSERT INTO "pioneers" VALUES(2,104,'2:104','Эй касоне, ки имон овардаед, [ҳангоме ки аз паёмбар барои дарки оёти Қуръон дархости муҳлат мекунед] нагӯед: "Роъино" [Зеро таъбири мазкур илова бар маънои аслӣ мафҳуми тавҳиномезе ҳамгуни "нодонӣ"-ро ба зеҳн мерасонад], балки бигӯед: "Унзурно" [каме ба мо фурсат бидеҳ] ва [он гоҳ ба оёти Қуръон] гӯш кунед ва [яқин бидонед, ки] кофирон [ва тамасхуркунандагони паёмбар] азоби дарднок [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(2,105,'2:105','Кофирон – аз аҳли китоб ва мушрикон – дӯст намедоранд, ки аз ҷониби Парвардигоратон хайру баракате бар шумо нозил гардад, дар ҳоле ки Аллоҳ таоло раҳмати худро ба ҳар кас, ки бихоҳад, ихтисос медиҳад ва Аллоҳ таоло дорои фазли бузург аст');
INSERT INTO "pioneers" VALUES(2,106,'2:106','Ҳар оятеро насх кунем ё [аз дили мардум бизудоем ва] фаромӯшаш гардонем, беҳтар аз он ё ҳаммонандашро меоварем. [Эй паёмбар] Оё намедонӣ, ки Аллоҳ таоло бар ҳар чиз тавоност');
INSERT INTO "pioneers" VALUES(2,107,'2:107','Оё намедонӣ, ки фармонравоии осмонҳо ва замин аз они Аллоҳ таоло аст ва шумо ба ҷуз Аллоҳ таоло [ҳеҷ] дӯсту ёваре надоред [ки корсозатон бошад]');
INSERT INTO "pioneers" VALUES(2,108,'2:108','[Эй муъминон] Оё мехоҳед аз паёмбари худ [мавориди номаъқуле ҳамчун дидори Аллоҳро] дархост кунед, ки пеш аз ин аз Мӯсо хоста шуда буд? Касе, ки куфрро ба [ҷойи] имон баргузинад, мусалламан роҳи ростро гум кардааст');
INSERT INTO "pioneers" VALUES(2,109,'2:109','Бисёре аз аҳли китоб пас аз ин, ки ҳақ барояшон ошкор гардид, ба хотири ҳасодате, ки дар вуҷудашон аст, орзу доранд, ки шуморо баъд аз имонатон ба ҳоли куфр бозгардонанд. Пас, шумо авф кунед ва [аз эшон] даргузаред, то Аллоҳ таоло фармони хешро [барои ҷиҳод бо онон] содир намояд. Ҳамоно Аллоҳ таоло бар ҳар чизе тавоност');
INSERT INTO "pioneers" VALUES(2,110,'2:110','Ва намоз барпо доред ва закот бипардозед ва [бидонед, ки] ҳар кори неке, ки барои худ пеш мефиристед, [подоши] онро назди Аллоҳ таоло хоҳед ёфт. Яқинан Аллоҳ таоло ба он чи мекунед, биност');
INSERT INTO "pioneers" VALUES(2,111,'2:111','[Аҳли китоб] Гуфтанд: "Ҷуз яҳудӣ ё масеҳӣ [ҳеҷ кас] ҳаргиз дохили биҳишт нахоҳад шуд. Ин орзуҳои [ботили] онҳост." [Эй паёмбар ба онон] Бигӯ: "Агар рост мегӯед, далели худро биёваред"');
INSERT INTO "pioneers" VALUES(2,112,'2:112','Оре, касе, ки мухлисона рӯйи худро таслими Аллоҳ таоло кунад ва накукор бошад, подошаш назди Парвардигораш [маҳфуз] аст ва [чунин касоне] на тарсе хоҳанд дошт ва на андӯҳгин мешаванд');
INSERT INTO "pioneers" VALUES(2,113,'2:113','Яҳудиён гуфтанд: "Масеҳиён барҳақ [ва оини саҳеҳе] нестанд" ва масеҳиён [низ] гуфтанд: "Яҳудиён барҳақ [ва оини саҳеҳе] нестанд", дар ҳоле ки [ҳар ду гурӯҳи] онон китоб [-и осмонӣ]–ро мехонанд. Ҳамчунин, касоне [монанди мушрикон], ки [аз паёми илоҳӣ ҳеҷ] намедонанд [сухане] ҳаммонанди онон гуфтанд. Пас, Аллоҳ таоло рӯзи қиёмат дар мавриди он чи бо ҳам ихтилоф доштанд, миёнашон доварӣ хоҳад кард');
INSERT INTO "pioneers" VALUES(2,114,'2:114','Кист ситамгортар аз он, ки аз бурдани номи Аллоҳ таоло дар масоҷид ҷилавгирӣ кунад ва дар вайронии онҳо бикӯшад?! Шоиста нест, ки онон ҷуз бо ҳоли ваҳшату тарс вориди ин масоҷид шаванд. Баҳраи онон дар дунё хорӣ ва расвоӣ аст ва дар охират [низ] азоби бузурге [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(2,115,'2:115','Машриқу мағриб аз они Аллоҳ таоло аст, пас, ба ҳар сӯйе рӯ кунед, рӯ [ба] Аллоҳ таоло аст. Ҳамоно Аллоҳ таоло кушоишгари доност');
INSERT INTO "pioneers" VALUES(2,116,'2:116','[Аҳли китоб ва мушрикон] Гуфтанд: "Аллоҳ таоло [барои худ] фарзанде баргузидааст". Ӯ [аз чунин нисбатҳое] муназзаҳ [-у пок] аст. [Аллоҳ таоло на танҳо ниёзе ба фарзанд надорад, балки] Он чи дар осмонҳо ва замин аст, мутааллиқ ба Ӯст, [ва] ҳама фармондардорӣ Ӯ ҳастанд');
INSERT INTO "pioneers" VALUES(2,117,'2:117','[Ӯ] падидоварандаи осмонҳо ва замин аст ва чун ба коре ирода фармояд, танҳо ба он мегӯяд: "Бош", пас, мешавад');
INSERT INTO "pioneers" VALUES(2,118,'2:118','Онон, ки [аз аҳли китоб ва мушрикон ҳастанд ва аз оини илоҳи чизе] намедонанд гуфтанд: "Чаро Аллоҳ таоло бо мо сухан намегӯяд? Ё [чаро] муъҷиза ва нишонае барои мо намеояд"? Касоне, ки пеш аз эшон буданд, ҳамин гуна сухан мегуфтанд. [Афкор ва] Дилҳояшон ҳаммонанди якдигар аст. Бе тардид мо нишонаҳо [–и яктоӣ ва қудрати хеш]–ро барои гурӯҳе, ки яқин доранд, ошкор ва равшан сохтаем');
INSERT INTO "pioneers" VALUES(2,119,'2:119','[Эй паёмбар] Мо туро ба ҳақ фиристодем, то башоратбахшу бимдиҳанда бошӣ ва [ту ҳаргиз дар охират] дар бораи [куфру бединии] дузахиён бозхост нахоҳӣ шуд');
INSERT INTO "pioneers" VALUES(2,120,'2:120','Яҳудиён ва масеҳиён ҳаргиз аз ту хушнуд нахоҳанд шуд, магар ин ки аз оини онон пайравӣ кунӣ. Бигӯ: "Бе тардид ҳидоят [-и ростин] ҳидояти Аллоҳ таоло аст" ва [яқин бидон] агар пас аз донише, ки ба ту расидааст, аз ҳавасҳои онон пайравӣ кунӣ, дар баробари Аллоҳ таоло ҳеҷ дӯсту ёваре нахоҳӣ дошт');
INSERT INTO "pioneers" VALUES(2,121,'2:121','Касоне, ки китоби осмонӣ [Таврот ва Инҷил] ба онҳо додаем [ва] онро чунон, ки шоистааст мехонанд, ба [матолиби] он [ки дар бораи ҳаққонияти Муҳаммад ва рисолаташ мебошад] имон меоваранд; ва касоне, ки ба ӯ [ва оинаш] кофир шаванд, онон зиёндидагонанд');
INSERT INTO "pioneers" VALUES(2,122,'2:122','Эй Бани Исроил, неъматҳоеро, ки ба шумо арзонӣ доштам, ба ёд оваред ва [низ] ин ки шуморо [ба далели бархӯрдорӣ аз ҳидоят] ба ҷаҳониён бартарӣ додам');
INSERT INTO "pioneers" VALUES(2,123,'2:123','Аз рӯзе битарсед, ки ҳеҷ кас аз дигаре дифоъ намекунад ва ҳеҷ товону фидяе аз ӯ пазируфта намешавад ва шафоат барояш суде надорад ва [инсонҳо] ёрӣ намешаванд');
INSERT INTO "pioneers" VALUES(2,124,'2:124','Ва [эй паёмбар, ёд кун аз] ҳангоме ки Аллоҳ таоло Иброҳимро бо суханон [-и фарогир бар аҳкому таколифи шаръӣ] озмуд ва ӯ ҳамаро ба хубӣ ба анҷом расонд. [Аллоҳ таоло ба вай] Фармуд: "Ман туро пешвои мардум қарор медиҳам". [Иброҳим] гуфт: "[Парвардигоро] Аз фарзандонам [низ пешвоёне қарор бидеҳ, то мардум ба онон иқтидо кунанд]". [Аллоҳ таоло] Фармуд: "Аҳду паймони Ман [бо ту дар мавриди пешвои дин] ба ситамгорон намерасад"');
INSERT INTO "pioneers" VALUES(2,125,'2:125','Ва [ёд кун аз] ҳангоме ки хонаи [Каъба] – ро маҳалли бозгашт [-и ҳоҷиёни муштоқ] ва [ҷойи] амне барои мардум қарор додем ва [муқаррар кардем, ки] мақоми Иброҳимро намозгоҳе барои хеш интихоб кунед ва мо ба Иброҳим ва Исмоил ваҳй кардем, ки: "Хонаи маро барои тавофкунандагону муътакифон ва рукуъу суҷудкунандагон [намозгузорон] поку покиза кунед"');
INSERT INTO "pioneers" VALUES(2,126,'2:126','Ва [ёд кун аз] ҳангоме ки Иброҳим гуфт: "Парвардигоро, ин [сарзамини Макка]-ро шаҳри амн бигардон ва аҳли онро – [яъне] касоне аз эшонро, ки ба Аллоҳ таоло ва рӯзи қиёмат имон овардаанд – аз меваҳои [гуногун] рӯзӣ бидеҳ". [Аллоҳ таоло] фармуд: "Ҳар ки кофир шавад, андаке баҳрамандаш месозам, сипас ӯро ба азоби оташ мекашонам ва чи бад саранҷоме аст"!');
INSERT INTO "pioneers" VALUES(2,127,'2:127','Ва [ёд кун аз] ҳангоме ки Иброҳим ва Исмоил пояҳои хонаи [Каъба] – ро боло мебурданд [ва мегуфтанд]:"Парвардигоро, [аъмоли неки мо, аз ҷумла бинои ин хонаи муқаддасро] аз мо бипазир, ки бе тардид Ту шунавову доноӣ"');
INSERT INTO "pioneers" VALUES(2,128,'2:128','Парвардигоро, моро [таслим ва] фармонбардори худ қарор бидеҳ ва аз фарзандонамон [низ] уммате фармонбардори худ [падид овар] ва одоби ибодатамонро ба мо нишон бидеҳ ва тавбаи моро бипазир. Ҳаққо, ки Ту тавбапазиру меҳрубонӣ');
INSERT INTO "pioneers" VALUES(2,129,'2:129','Парвардигоро, дар миёни онон [мардуми Макка] паёмбаре аз худашон [аз насли Исмоил] барангез, то оёти Туро бар онон бихонад ва ба эшон китобу ҳикмате биёмӯзаду [аз ширку нодонӣ] покиза гардонад. Ҳамоно ки Ту пирӯзманду ҳакимӣ');
INSERT INTO "pioneers" VALUES(2,130,'2:130','Ва чи касе ҷуз он ки худро ба [сабуксариву] нодонӣ меандозад аз оини Иброҳим рӯйгардон мегардад? Ва ба ростӣ, ки мо ӯро дар дунё баргузидем ва бе тардид дар охират [низ] аз шоистагон хоҳад буд');
INSERT INTO "pioneers" VALUES(2,131,'2:131','Он гоҳ, ки Парвардигораш ба ӯ фармуд: "[Холисона] Таслим шав" [ва ӯ дар посух] гуфт: "Таслими Парвардигори ҷаҳониён шудам"');
INSERT INTO "pioneers" VALUES(2,132,'2:132','Ва Иброҳиму Яқуб фарзандони худро ба ин оин супориш карданд [ва ҳар як ба онон мегуфтанд]: "Эй фарзандони ман, Аллоҳ таоло ин оинро барои шумо баргузида аст [пас, ба он бигаравед] ва аз дунё наравед, магар ин ки таслими [фармони Ҳақ] бошед"');
INSERT INTO "pioneers" VALUES(2,133,'2:133','Оё ҳангоме ки марги Яқуб фаро расид, шумо ҳозир будед? Он гоҳ, ки ба фарзандони худ гуфт: "Баъд аз ман чи чизро мепарастед"? Гуфтанд: "Маъбуди ту ва маъбуди падаронат – Иброҳим ва Исмоилу Исҳоқ – Аллоҳи якторо мепарастем ва мо таслими Ӯ ҳастем"');
INSERT INTO "pioneers" VALUES(2,134,'2:134','Онон уммате буданд, ки даргузаштанд, он чи ки [аз натиҷаи аъмолашон] касб кардаанд, барои онҳост ва он чи шумо касб кардаед, барои шумост ва шумо дар бораи он чи [онон] мекарданд, бозхост нахоҳед шуд');
INSERT INTO "pioneers" VALUES(2,135,'2:135','Ва [аҳли китоб] гуфтанд: "Яҳудӣ ё масеҳӣ шавед, то ҳидоят ёбед". [Эй паёмбар ба онон] Бигӯ: "[На аз оини шумо] Балки аз оини Иброҳим [пайравӣ мекунем], ки ҳақгаро буд ва ҳаргиз дар зумраи мушрикон набуд"');
INSERT INTO "pioneers" VALUES(2,136,'2:136','[Эй муъминон ба аҳли китоб] бигӯед: "Мо ба Аллоҳ таоло имон овардаем ва ба он чи бар мо нозил шудааст ва ба он чи, ки бар Иброҳиму Исмоил ва Исҳоқу Яъқуб ва наводагон [-и Яъқуб] нозил гардид ва [ҳамчунин] ба он чи, ки ба Мӯсо ва Исо дода шудааст ва ба он чи, ки ба паёмбарони [дигар] аз тарафи Парвардигорашон дода шудааст [оре, ба ҳамаи онон имон меоварем] ва миёни ҳеҷ як аз эшон тафовут намегузорем ва мо [ҳамагӣ] таслими Аллоҳ таоло ҳастем"');
INSERT INTO "pioneers" VALUES(2,137,'2:137','Пас, агар онон [низ] ба он чи шумо имон овардаед, имон оваранд, ҳатман, ҳидоят ёфтаанд ва агар пушт гардонанд, пас, ҷуз ин нест, ки ҳатман сари ситез [ва ҷудоӣ] доранд, пас, [эй паёмбар, Парвардигори якто барои ҳимояти ту кофӣ аст ва] ба зудӣ Аллоҳ таоло [шарри] онҳоро аз ту дафъ хоҳад намуд ва Ӯ шунавову доност');
INSERT INTO "pioneers" VALUES(2,138,'2:138','Ранги илоҳӣ [ва дини Ӯро бипазиред ва ба он пойбанд бошед] ва чи касе хушнигортар аз Аллоҳ таоло аст! Ва мо танҳо Ӯро мепарастем');
INSERT INTO "pioneers" VALUES(2,139,'2:139','[Эй паёмбар ба аҳли китоб] Бигӯ: "Оё дар бораи Аллоҳ таоло бо мо муҷодала мекунед, дар ҳоле ки Ӯ парвардигори мо ва шумост ва кирдори мо аз они мо ва кирдори шумо аз они шумост ва мо Ӯро бо ихлос парастиш мекунем"');
INSERT INTO "pioneers" VALUES(2,140,'2:140','[Оё] Мегӯед: "Иброҳиму Исмоил ва Исҳоқу Яъқуб ва наводагон [-и ӯ] яҳудӣ ё масеҳӣ буданд [дар ҳоле ки Таврот ва Инҷил пас аз ин паёмбарон нозил шудааст]"? Бигӯ: "Шумо донотаред ё Аллоҳ таоло"? Ва кист ситамгортар аз он, ки шаҳодатеро, ки аз [ҷониби] Аллоҳ таоло [дар китобҳои осмонӣ дар бораи ин паёмбарон омада ва] назди Ӯст, китмон мекунад? Ва [яқин бидонед, ки] Аллоҳ таоло аз он чи мекунед, ғофил нест');
INSERT INTO "pioneers" VALUES(2,141,'2:141','Онон уммате буданд, ки даргузаштанд, он чи ки [аз натиҷаи аъмолашон] касб кардаанд, барои онҳост ва он чи шумо касб кардаед, барои шумост ва шумо дар бораи он чи [онон] мекарданд, бозхост нахоҳед шуд');
INSERT INTO "pioneers" VALUES(2,142,'2:142','Ба зудӣ гурӯҳе аз мардуми камхирад [яҳудӣ] хоҳанд гуфт: "Чӣ чизе онҳо [мусулмонон]-ро аз қиблаашон [Байт-ул-муқаддас], ки бар он буданд, [ба сӯйи Каъба] бозгардонд"? [Эй паёмбар] Бигӯ: "Машриқу мағриб аз они Аллоҳ таоло аст, ҳар касро бихоҳад ба роҳи рост ҳидоят мекунад"');
INSERT INTO "pioneers" VALUES(2,143,'2:143','Ҳамон гуна [ки қиблаи шумо як қиблаи миёна аст] шуморо низ уммати миёна [ва аҳли эътидол] қарор додем то [дар рӯзи қиёмат] бар мардум гувоҳ бошед [ки паёмбарони илоҳӣ паёмашонро ба дурустӣ расонидаанд] ва паёмбар бар шумо гувоҳ бошад [ки каломи Аллоҳро ба шумо расонидааст] ва мо қиблаеро, ки бештар бар он будӣ [Байт-ул-муқаддас], қарор надодем, магар барои ин ки касеро, ки аз паёмбар пайравӣ мекунад, аз касе, ки ба ақиб бозмегардад [ва аз итоати Аллоҳ таоло ва расулаш рӯйгардон мешавад], бозшиносем. Мусалламан ин [ҳукм] ҷуз бар касоне, ки Аллоҳ таоло ҳидояташон карда бошад, сангину душвор аст. Аллоҳ таоло ҳаргиз имон [ва намоз]-и шуморо зоеъ намегардонад. Ба ростӣ, ки Аллоҳ таоло нисбат ба мардум дилсӯзу меҳрубон аст');
INSERT INTO "pioneers" VALUES(2,144,'2:144','[Эй паёмбар] Ба ростӣ, ки мо гардондани рӯятро ба [сӯйи] осмон [ки дар интизори ваҳйи илоҳӣ барои тағйири қибла ҳастӣ] мебинем. Акнун туро ба [сӯйи] қиблае бозмегардонем, ки ба он хушнуд мешавӣ, пас, рӯйи худро ба сӯйи Масҷид-ул-ҳаром кун ва [шумо низ, эй муъминон, барои адои намоз] ҳар ҷо будед, рӯйи худро ба сӯйи он бигардонед. Касоне, ки китоби [осмонӣ] ба онҳо дода шуд, ба хубӣ медонанд, ки ин [тағйири қибла фармони] ҳақ аст [ва] аз ҷониби Парвардигорашон мебошад ва Аллоҳ таоло аз он чи мекунанд, ғофил нест');
INSERT INTO "pioneers" VALUES(2,145,'2:145','Агар барои онон, ки китоби [осмонӣ] ба эшон дода шудааст, ҳар нишона [ва далеле барои ҳаққонияти тағйири қибла] биёварӣ, [боз ҳам] аз қиблаи ту пайравӣ намекунанд ва ту [низ] аз қиблаи онон пайравӣ нахоҳӣ кард ва бархе аз онон низ аз қиблаи бархе дигар пайравӣ намекунанд ва агар пас аз он ки илму дониш ба ту расидааст, аз хостаҳои [нафсонии] онон [дар мавриди қибла ва дигар аҳком] пайравӣ кунӣ, дар ин сурат мусалламан аз ситамгорон хоҳӣ буд');
INSERT INTO "pioneers" VALUES(2,146,'2:146','Касоне, ки ба онон китоби [осмонӣ] додаем, [Муҳаммад]-ро чунон мешиносанд, ки фарзандони худро мешиносанд [ва медонанд, ки суханаш барҳақ аст], вале гурӯҳе аз онон, дар ҳоле ки худ низ медонанд, ҳақро китмон мекунанд');
INSERT INTO "pioneers" VALUES(2,147,'2:147','[Эй паёмбар он чи бар ту нозил мегардад, ҳукми] Ҳақ аз ҷониби Парвардигори туст, пас, ҳаргиз аз тардидкунандагон мабош');
INSERT INTO "pioneers" VALUES(2,148,'2:148','Ҳар яке [аз умматҳо] ҷиҳате доранд, ки [ҳангоми намоз] бад-он рӯ мекунанд, пас, [шумо эй муъминон] дар некиҳо [бар якдигар] сабқат бигиред. Ҳар ҷо ки бошед, Аллоҳ таоло [дар рӯзи қиёмат] ҳамаи шуморо ҳозир мекунад. Ҳамоно Аллоҳ таоло бар ҳар чизе тавоност');
INSERT INTO "pioneers" VALUES(2,149,'2:149','[Эй паёмбар] Аз ҳар ҷо ки [ба қасди сафар] хориҷ шудӣ, пас, [ба ҳангоми намоз] рӯйи худро ба сӯйи Масҷид-ул-ҳаром кун ва ба дурустӣ, ки ин [рӯ карданат] ҳаққе аз ҷониби Парвардигорат аст ва Аллоҳ таоло аз он чи мекунед, ғофил нест');
INSERT INTO "pioneers" VALUES(2,150,'2:150','Ва аз ҳар ҷо, ки берун шудӣ, пас, [дар ҳангоми намоз] рӯйи худро ба сӯйи Масҷид-ул-ҳаром кун ва [шумо, эй муъминон] ҳар ҷо ки бошед, рӯйи худро ба сӯйи он [масҷид] кунед, то мардум алайҳи шумо ҳуҷҷате надошта бошанд [то бо истинод ба онҳо баҳонатарошӣ кунанд], магар касоне аз эшон, ки ситам карданд [ва ҳамвора душманатон хоҳанд буд]. Пас, аз онҳо натарсед ва аз ман битарсед [ин тағйири қибла барои он буд], ки неъматамро бар шумо тамом кунам [ва шуморо мутамоиз созам], бошад, ки ҳидоят шавед');
INSERT INTO "pioneers" VALUES(2,151,'2:151','Ҳамчунон ки [неъмати дигаре низ ба шумо арзонӣ доштем ва] паёмбаре аз худатон ба миёнатон фиристодем, ки оёти моро бар шумо мехонад ва шуморо [бо амри маъруф ва наҳйи мункар аз палидиҳо] пок мегардонад ва ба шумо китоби [Қуръон] ва ҳикмате меомӯзад ва он чиро [ки аз умури дину дунёятон] намедонистед, ба шумо омӯзиш медиҳад');
INSERT INTO "pioneers" VALUES(2,152,'2:152','Пас, Маро ёд кунед, то шуморо ёд кунам ва Маро сипос гӯед ва ба Ман носипосӣ накунед');
INSERT INTO "pioneers" VALUES(2,153,'2:153','Эй касоне, ки имон овардаед, аз шикебоиву намоз ёрӣ биҷӯед [ки] бе тардид Аллоҳ таоло бо сабркунандагон аст');
INSERT INTO "pioneers" VALUES(2,154,'2:154','Ва ба онҳо, ки дар роҳи Аллоҳ таоло кушта мешаванд, мурда нагӯед, балки онон [назди Парвардигорашон] зиндаанд, вале шумо намефаҳмед');
INSERT INTO "pioneers" VALUES(2,155,'2:155','Ва ҳатман шуморо бо чизе аз [қабили] тарсу гуруснагӣ [қаҳтӣ] ва зиёни молӣ [нобудӣ ё душвории касби он] ва ҷонӣ [маргу мир ё шаҳодат] ва зиёни маҳсулот [-и кишоварзӣ] меозмоем ва [эй паёмбар] сабркунандагонро [ба он чи дар дунё ва охират барояшон фароҳам меояд] башорат бидеҳ');
INSERT INTO "pioneers" VALUES(2,156,'2:156','Онон, ки ҳар гоҳ мусибате ба эшон мерасад, [аз рӯйи ризоият ва таслим] мегӯянд: "Ба ростӣ, ки мо аз они Аллоҳ таоло ҳастем ва ба сӯйи Ӯ бозмегардем"');
INSERT INTO "pioneers" VALUES(2,157,'2:157','Инҳо ҳастанд, ки дурудҳо ва раҳмате аз Парвардигорашон бар эшон аст ва маҳз инҳо ҳидоятёфтагонанд');
INSERT INTO "pioneers" VALUES(2,158,'2:158','"Сафо" ва "Марва" аз нишонаҳои [зоҳири шариати] Аллоҳ таоло аст. Пас, ҳар ки ҳаҷҷи хона [-и Каъба] ё умра ба ҷой меоварад, бар ӯ гуноҳе нест, ки тавофи [саъй] байни он дуро [низ] анҷом диҳад [ва ин кор аз рафторҳои ҷоҳилият шуморида намешавад] ва касе, ки ба майли худ кори неке анҷом диҳад, пас, [бидонад, ки] бе тардид Аллоҳ таоло сипосгузори доност');
INSERT INTO "pioneers" VALUES(2,159,'2:159','Касоне, ки он чиро аз далоили равшан ва ҳидоят нозил кардаем – пас аз он, ки дар китоби [Таврот ва Инҷил] барои мардум баёнаш намудем – пинҳон медоранд, Аллоҳ таоло ононро лаънат мекунад ва лаънаткунандагон [аз фариштагону муъминон] лаънаташон мекунанд');
INSERT INTO "pioneers" VALUES(2,160,'2:160','Магар касоне, ки тавба кунанд ва [корҳои ношоистаи худро] ислоҳ намоянд ва [ҳақиқатро] ошкор созанд. Инон ҳастанд, ки тавбаашонро мепазирам ва Ман тавбапазиру меҳрубонам');
INSERT INTO "pioneers" VALUES(2,161,'2:161','Касоне, ки куфр варзиданд [ва ба инкори ҳақ кӯшиданд] ва дар ҳоли куфр мурданд, лаънати Аллоҳ таоло ва фариштагон ва ҳамаи мардум бар онҳо хоҳад буд');
INSERT INTO "pioneers" VALUES(2,162,'2:162','Дар он [лаънат ва азоби онон] ҷовидона боқӣ мемонанд ва на аз азобашон коста мешавад ва на муҳлат [-и узрхоҳӣ] меёбанд');
INSERT INTO "pioneers" VALUES(2,163,'2:163','[Эй мардум] маъбуди шумо маъбуди ягона аст [ки] ба ҷуз Ӯ [ҳеҷ] маъбуде [ба ҳақ] нест; бахшандаву меҳрубон аст');
INSERT INTO "pioneers" VALUES(2,164,'2:164','Ба ростӣ дар офариниши осмонҳо ва замину гузари шабу рӯз ва киштиҳое, ки барои баҳрабардории мардум дар дарё равонанд ва обе, ки Аллоҳ таоло аз осмон нозил намудааст, ки бо он заминро пас аз хушкӣ ва хазонаш зинда карда ва анвои ҷунбандагонро дар он пароканда аст ва [ҳамчунин] дар тағйири масири бодҳо ва абрҳое, ки дар миёни осмон ва замин ба хидмат [-и инсон] гумошта шудаанд, барои касоне, ки меандешанд, нишонаҳое [аз қудрати Парвардигор] аст');
INSERT INTO "pioneers" VALUES(2,165,'2:165','[Бо вуҷуди ин оёти равшан] бархе аз мардум ҳамтоёни ғайр аз Аллоҳро [барои парастиш] бармегузинанд ва онҳоро ҳамчун Аллоҳ таоло дӯст медоранд, вале касоне, ки имон овардаанд, Аллоҳро бештар дӯст доранд ва онон, ки [бо ширку иртикоби гуноҳон] ситам карданд, ҳангоме ки азоби [қиёмат] – ро бубинанд, [хоҳанд донист], ки тамоми қудрат аз они Аллоҳ таоло аст ва ин ки Аллоҳ таоло сахткайфар аст');
INSERT INTO "pioneers" VALUES(2,166,'2:166','Он гоҳ, ки раҳбарон [-и гумроҳгар] аз пайравони худ безорӣ меҷӯянд ва азобро мебинанд ва асбоб [-у равобите, ки муҷиби наҷоташон мешуд, ҳамагӣ] гусаста мегардад');
INSERT INTO "pioneers" VALUES(2,167,'2:167','Ва [дар ин ҳангом] касоне, ки [дар дунё аз раҳбарони гумроҳгар] пайравӣ карда буданд, мегӯянд: "Кош барои мо бозгаште [ба дунё] буд, то ҳамон гуна, ки онон аз мо безорӣ ҷустанд, [мо низ] аз онҳо безорӣ ҷӯем. Аллоҳ таоло чунин рафтори эшонро, ки мояи ҳасраташон аст, ба онҳо нишон медиҳад ва ҳаргиз аз оташи [дӯзах] берун нахоҳанд омад');
INSERT INTO "pioneers" VALUES(2,168,'2:168','Эй мардум, аз он чи дар замин аст ҳалолу покиза бихӯред ва аз [роҳҳо ва] гомҳои шайтон [ки ба василаи онҳо ба тадриҷ шуморо ба сӯйи азоб мекашад], пайравӣ накунед, ки ба ростӣ ӯ душмани ошкори шумост');
INSERT INTO "pioneers" VALUES(2,169,'2:169','[Ӯ] Шуморо фақат ба бадӣ ва зиштӣ фармон медиҳад ва [бар он медорад, то] он чиро, ки намедонед, ба Аллоҳ таоло нисбат диҳед');
INSERT INTO "pioneers" VALUES(2,170,'2:170','Ва ҳангоме ки ба онон [кофирон] гуфта шавад: "Аз он чи Аллоҳ таоло нозил кардааст, пайравӣ кунед", мегӯянд: [На] Балки мо аз чизе пайравӣ мекунем, ки падарони худро бар он ёфтаем. Оё агар падаронашон [аз ҳақиқат] чизе нафаҳмидаву ҳидоят наёфта бошанд [боз ҳам кӯр – кӯрона аз онон пайравӣ мекунанд]!?');
INSERT INTO "pioneers" VALUES(2,171,'2:171','Масали [даъвати ту – эй Муҳаммад – барои ҳидояти ин] кофирон ҳамчун касест, ки чаҳорпоеро садо мезанад, ки ҷуз садо ва овозе намешунавад. [Ин кофирон дар рӯёрӯӣ бо ҳақ] кару лолу кӯр ҳастанд, аз ин рӯ, намеандешанд');
INSERT INTO "pioneers" VALUES(2,172,'2:172','Эй касоне, ки имон овардаед, аз чизҳои покизае, ки рӯзии шумо кардаем, бихӯред ва шукри Аллоҳро ба ҷо оваред, агар танҳо Ӯро мепарастед');
INSERT INTO "pioneers" VALUES(2,173,'2:173','[Аллоҳ таоло] Танҳо мурдор ва хуну гӯшти хук ва он чиро, ки [ҳангоми забҳ] номи ғайри Аллоҳ таоло бар он бурда шавад, бар шумо ҳаром кардааст. Пас, ҳар ки [ барои ҳифзи ҷони худ ба хӯрдани онҳо] ночор шавад [ва] саркашу зиёдахоҳ набошад, бар ӯ гуноҳе нест. Ба ростӣ, ки Аллоҳ таоло омурзандаву меҳрубон аст');
INSERT INTO "pioneers" VALUES(2,174,'2:174','Касоне, ки он чиро Аллоҳ таоло дар китоби [Таврот дар бораи нубуввати Муҳаммад] нозил кардааст, китмон мекунанд ва онро [барои касби молу мақоми дунё] ба баҳои андаке мефурӯшанд [дар ҳақиқат] ҷуз оташ дар шикамҳояшон фурӯ намебаранд ва Аллоҳ таоло рӯзи қиёмат бо онон сухан намегӯяд ва [аз палидии гуноҳ] покашон намекунад ва азоби дардноке хоҳанд дошт');
INSERT INTO "pioneers" VALUES(2,175,'2:175','Инон касоне ҳастанд, ки гумроҳиро ба [баҳои] ҳидоят ва азобро ба [баҳои] омурзиш хариданд, пас, бар оташи [дӯзах] чи шикебо ҳастанд!');
INSERT INTO "pioneers" VALUES(2,176,'2:176','Ин [муҷозот бар китмони ҳақ] ба хотири он аст, ки Аллоҳ таоло китоб [-ҳои осмони]-ро ба ҳақ нозил кардааст ва касоне, ки дар он ихтилоф карданд, [аз ҳақиқат] бисёр дуранд');
INSERT INTO "pioneers" VALUES(2,177,'2:177','Некӣ [фақат] ин нест, ки [барои намоз] рӯйи худро ба сӯйи машриқ ва [ё] мағриб кунед, балки некӣ [рафтори] он касе аст, ки ба Аллоҳ таоло ва рӯзи қиёмат ва фариштагону китоби [осмонӣ] ва паёмбарон имон оварда бошад ва моли [худ]-ро, бо вуҷуди дӯст доштанаш, ба хешовандону ятимон ва мустамандон ва дар роҳмондагону дархосткунандагон [гадоён] ва дар [роҳи озодии] бардагон бибахшад ва намоз бар пой дорад ва закот бипардозад ва [низ] онон, ки чун аҳд мебанданд, ба паймонашон вафо мекунанд ва [касоне, ки] дар фақру сахтӣ ва зиёну беморӣ ва ба ҳангоми [шиддат гирифтани] набард, [пойдорӣ ва] шикебоӣ мекунанд, онон [ки ба ин вижагиҳо оростаанд], касоне ҳастанд, ки рост гуфтанд ва онон парҳезгоронанд');
INSERT INTO "pioneers" VALUES(2,178,'2:178','Эй касоне, ки имон овардаед, дар [мавриди] куштагон [ки барқасдона ба қатл расидаанд, ҳукми] қасос бар шумо муқаррар гаштааст: Озод дар баробари озод ва барда дар баробари барда ва зан дар баробари зан. Пас, агар касе [валии дам] дар ҳаққи бародар [-и дини]-аш [қотил] бахшиш намояд, бояд [аз азобу озораш бипарҳезад ва бо ӯ] хушрафторӣ намояд ва [қотил низ бояд мутақобилан дия-хунбаҳоро] ба некӣ ба ӯ бипардозад. Ин [ҳукм] тахфифу раҳмате аст аз сӯйи Парвардигоратон. Пас, агар касе баъд аз он [гузашту қабули дия] таҷовуз кунад, азоби дарднок [дар интизор] дорад');
INSERT INTO "pioneers" VALUES(2,179,'2:179','Эй хирадмандон, дар [ин] қасос барои шумо [тадовуми] зиндагӣ аст, бошад, ки тақво пеша кунед');
INSERT INTO "pioneers" VALUES(2,180,'2:180','Бар шумо муқаррар шудааст, ки ҳар гоҳ замони марги яке аз шумо фаро расад, агар моле аз худ ба ҷой гузоштааст, барои падару модар ва хешовандон ба шеваи шоиста васият кунад. Ин [кор] ҳаққе аст бар парҳезкорон [ки бояд анҷом диҳанд]');
INSERT INTO "pioneers" VALUES(2,181,'2:181','Пас, ҳар ки он [васият]-ро пас аз шуниданаш тағйир диҳад, гуноҳаш танҳо бар [уҳдаи] касонест, ки онро тағйир медиҳанд. Ба ростӣ, ки Аллоҳ таоло шунавову доност');
INSERT INTO "pioneers" VALUES(2,182,'2:182','Пас, касе, ки аз беадолатӣ [ва майли беҷойи] васияткунанда [нисбат ба ворисон] ё аз гуноҳи ӯ [дар васият ба кори хилоф] битарсад ва [бо ислоҳи васият] миёни онон [ворисон]-ро созиш диҳад, бар ӯ гуноҳе нест, [ки] ҳамоно Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(2,183,'2:183','Эй касоне, ки имон овардаед, рӯза бар шумо воҷиб шудааст, ҳамон гуна ки бар касоне, ки пеш аз шумо буданд, низ воҷиб шуда буд; бошад, ки парҳезкор шавед');
INSERT INTO "pioneers" VALUES(2,184,'2:184','Рӯзҳои муайян [-ро рӯзадорӣ кунед], пас, ҳар кас аз шумо, ки бемор ё мусофир бошад, [ба андозаи он рӯзҳое, ки натавонистааст рӯза бигирад] теъдоде аз рӯзҳои дигар [-ро рӯза бигирад] ва бар касоне, ки тавоноии онро надоранд, лозим аст каффора бидиҳанд [ва он ба баробари ҳар рӯз] хӯрок додан ба як тиҳидаст аст ва ҳар ки ба майли худ [бештар] накуӣ кунад, он [афзунбахшӣ ба суди ӯст ва] барояш беҳтар аст ва агар [фазилати рӯзадориро] бидонед, рӯза бароятон беҳтар [аз ифтор ва пардохти каффора] аст');
INSERT INTO "pioneers" VALUES(2,185,'2:185','Моҳи рамазон [моҳе] аст, ки Қуръон дар он нозил шудааст; [китобе,] ки роҳнамои мардум аст ва далоили ошкоре аз ҳидоят [бо худ дорад] ва ҷудокунанда [-и ҳақ аз ботил] аст. Пас, ҳар яке аз шумо [ки муқиму солим аст] ин моҳро дарёбад, бояд онро рӯза бидорад ва ҳар ки бемор ё дар сафар бошад, [бояд ба теъдоди рӯзҳое, ки рӯза нагирифтааст] рӯзҳои дигаре [-ро рӯза бигирад]. Аллоҳ таоло [бо баёни ин аҳком] бароятон осонӣ мехоҳад ва душворӣ намехоҳад, то [битавонед рӯзадорӣ дар моҳи рамазон]-ро комил кунед ва Аллоҳро барои он ки ҳидоятатон кардааст, ба бузургӣ ёд кунед; бошад, ки шукр гузоред');
INSERT INTO "pioneers" VALUES(2,186,'2:186','Ва [эй паёмбар] чун бандагонам дар бораи Ман аз ту бипурсанд, [ба онон] бигӯ: Ман наздикам ва дуои дуокунандаро – ҳангоме ки маро бихонад – иҷобат мекунам, пас, [онон фақат] бояд даъвати Маро бипазаранд ва ба Ман имон биёваранд; бошад, ки роҳ ёбанд');
INSERT INTO "pioneers" VALUES(2,187,'2:187','[Эй муъминон] Омезиш бо ҳамсаронатон дар шаб [-ҳои] рӯза бар шумо ҳалол гардидааст. Онон либоси шумо ҳастанд ва шумо либоси эшон. Аллоҳ таоло медонист, ки шумо бар худ хиёнат мекардед [ва бо он ки дар авоили ислом омезиш дар моҳи рамазон ҳаром буд, шумо бар он бетаваҷҷуҳ будед], пас, тавбаатонро пазируфту аз шумо даргузашт. Пас, акнун [дар шабҳои рамазон метавонед] бо онон омезиш кунед ва он чиро, ки Аллоҳ таоло бароятон муқаррар доштааст [аз эшон] талаб кунед. Бихӯред ва биошомед, то риштаи сапеди субҳ [фаҷри содиқ] аз риштаи сиёҳ [шаб] бароятон ошкор гардад, он гоҳ рӯзаро то шаб ба поён бирасонед ва, дар ҳоле ки дар масоҷид муътакиф ҳастед, бо занон омезиш накунед. Ин марзҳо [-и аҳком]-и илоҳӣ аст, пас, ба онҳо наздик нашавед. Аллоҳ таоло чунин оёт [ва аҳком]-и худро барои мардум равшан месозад, бошад, ки парҳезкор шаванд');
INSERT INTO "pioneers" VALUES(2,188,'2:188','Ва амволатонро байни худ ба ботил [ва ноҳақ] нахӯред ва барои он, ки бахше аз моли мардумро ба гуноҳ бихӯред, қазияро ба ҳокимон накашонед, дар ҳоле ки медонед [ки ин кор гуноҳ аст]');
INSERT INTO "pioneers" VALUES(2,189,'2:189','[Эй паёмбар] Аз ту дар бораи ҳилолҳо [-и моҳ] мепурсанд, бигӯ: "Онҳо баёни авқот [ва гоҳшумории табиӣ] барои мардум ва [василаи таъйини вақти] ҳаҷ ҳастанд". Накукорӣ он нест, ки [монанди даврони ҷоҳилият дар ҳоли эҳром] аз пушти хонаҳо ворид шавед, балки накукор касест, ки тақво пеша кунад [пас, парҳезкор бошед] ва ба хонаҳо аз дарҳояшон ворид шавед ва аз Аллоҳ таоло битарсед, бошад, ки растагор шавед');
INSERT INTO "pioneers" VALUES(2,190,'2:190','Ва дар роҳи Аллоҳ таоло бо касоне, ки бо шумо меҷанганд, биҷангед [вале бо мусла кардан ва куштани кӯдакону занон ва пиронсолон] аз ҳад таҷовуз накунед, [ки] ба ростӣ Аллоҳ таоло таҷовузгаронро дӯст намедорад');
INSERT INTO "pioneers" VALUES(2,191,'2:191','Ва онон [мушрикон]-ро ҳар куҷо ёфтед, бикушед ва аз он ҷо [Макка], ки шуморо берун кардаанд, берун кунед ва фитна [куфру ширк] бадтар аз қатл аст ва бо онҳо дар канори Масҷид-ул-ҳаром пайкор накунед, магар ин ки бо шумо дар он ҷо биҷанганд. Пас, агар [дар он ҷо] бо шумо ҷангиданд, эшонро бикушед, [ки] сазои кофирон чунин аст');
INSERT INTO "pioneers" VALUES(2,192,'2:192','Пас, агар [аз куфрашон] даст бардоштанд [аз набард даст бикашед, ки] бе гумон Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(2,193,'2:193','Ва бо онҳо биҷангед, то фитнаи [ширк] боқӣ намонад ва дин [холисона] аз они Аллоҳ таоло гардад. Пас, агар даст бардоштанд, [ситам накунед, ки] ситам ҷуз бар ситамгорон раво нест');
INSERT INTO "pioneers" VALUES(2,194,'2:194','[Ин] Моҳи ҳаром [соли ҳафтуми ҳиҷрӣ, ки имкони умра гузоридан ёфтед] дар баробари моҳи ҳаром [-и соли гузашта] аст [ки мушрикон шуморо аз адои ҳаҷ боздошта буданд] ва [таҷовузи] ҳурматҳо [-и сарзамину моҳи ҳаром] қасос дорад; пас, ҳар ки ситам кард, ба ҳамон гуна ки бар шумо ситам кардааст, бар ӯ ситам раво доред ва аз Аллоҳ таоло парво кунед ва бидонед, ки Аллоҳ таоло бо парҳезкорон аст');
INSERT INTO "pioneers" VALUES(2,195,'2:195','Ва дар роҳи Аллоҳ таоло инфоқ кунед ва [бо фирор аз майдони ҷиҳод ва тарки инфоқ] худро бо дасти хештан ба ҳалокат наафканед ва некӣ кунед, [ки] ба ростӣ Аллоҳ таоло накукоронро дӯст медорад');
INSERT INTO "pioneers" VALUES(2,196,'2:196','Ва ҳаҷҷу умраро барои Аллоҳ таоло ба поён расонед ва агар [ба василаи душман ё беморӣ аз ин кор] бозмонед, он чи аз қурбонӣ [хоҳ аз шутуру гов ё гӯсфанд] фароҳам шавад [забҳ кунед ва он гоҳ аз эҳром хориҷ шавед] ва сарҳои худро натарошед, то қурбонӣ ба ҷойгоҳаш [дар қурбонгоҳ] бирасад ва агар касе аз шумо бемор буду ё нороҳатие дар сар дошт [ки ночор буд бад-он хотир дар ҳоли эҳром сари худро битарошад], пас, фидя диҳад, аз [қабили се рӯз] рӯза ё садақа [таом додани шаш тиҳидасти ҳарам] ё қурбонӣ кардани гӯсфанде [ки миёни тиҳидастони ҳарам тақсим шавад]. Пас, ҳангоме ки дар амон будед, ҳар ки аз умра баҳраманд гардид ва сипас [аъмоли] ҳаҷро оғоз кард, он чи аз қурбонӣ [барояш] муяссар шуд [забҳ кунад] ва ҳар ки [қурбонӣ] наёфт, се рӯз дар [айёми] ҳаҷ ва ҳафт рӯз ҳангоме ки [ба хона] бозгаштед, рӯза бигиред. Ин даҳ [рӯзи] комил аст. Ин [ҳукми ҳаҷҷи таматтуъ] барои касест, ки хонаводааш сокини Масҷид-ул-ҳаром [Макка ва атрофи он] набошад ва аз Аллоҳ таоло парво кунед ва бидонед, ки Аллоҳ таоло сахткайфар аст');
INSERT INTO "pioneers" VALUES(2,197,'2:197','[Мавсими] ҳаҷ дар моҳҳои мушаххасе аст [аз оғози Шаввол то даҳуми Зулҳиҷҷа]; пас, касе, ки дар ин моҳҳо ҳаҷро [бар худ] воҷиб гардонад, [бояд бидонад, ки] дар ҳаҷ, омезиши ҷинсӣ ва [иртикоби] гуноҳ ва даргирӣ [ва ҷидол раво] нест ва он чи аз корҳои нек анҷом медиҳед, Аллоҳ таоло онро медонад; ва [хӯроку пӯшоки мавриди ниёзи ҳаҷро барои худ фароҳам кунед ва] тӯша баргиред, [вале ба ёд дошта бошед, ки] ба ростӣ, беҳтарин тӯша парҳезкорист ва, эй хирадмандон, аз ман парво кунед');
INSERT INTO "pioneers" VALUES(2,198,'2:198','Гуноҳе бар шумо нест, ки [дар айёми ҳаҷ бо хариду фурӯш] аз фазли Парвардигоратон [манфиат ва рӯзӣ] талаб кунед ва ҳангоме ки [рӯзи нуҳум пас аз вуқуф] аз Арафот кӯч кардед, Аллоҳро дар "Машъар-ул-ҳаром" ёд кунед; ва Ӯро ёд кунед, чаро ки шуморо, ки пештар аз гумроҳон будед, [ба адои маносики ҳаҷҷу дигар аъмоли ибодӣ] ҳидоят кард');
INSERT INTO "pioneers" VALUES(2,199,'2:199','Пас, аз ҳамон ҷо, ки мардум [пайравони Иброҳим] равона мешаванд, [шумо низ аз Арафот ба сӯйи Мино] харакат кунед ва аз Аллоҳ таоло омурзиш бихоҳед, [чаро ки] бе тардид Аллоҳ таоло омурзандаву меҳрубон аст');
INSERT INTO "pioneers" VALUES(2,200,'2:200','Пас, чун маносики [ҳаҷҷи] худро ба ҷой овардед, ҳамон гуна, ки падаронатонро [бо фахру ғурур] ёд мекардед, балки [беҳтар ва] бештар аз он Аллоҳро ёд кунед; ва бархе аз мардум [кофироне] ҳастанд, ки мегӯянд: "Парвардигоро, ба мо дар дунё [молу фарзанд] ато кун". Ин шахс дар охират баҳрае [аз неъматҳои илоҳӣ] надорад');
INSERT INTO "pioneers" VALUES(2,201,'2:201','Ва бархе [дигар] аз эшон [муъминоне] ҳастанд, ки мегӯянд: "Парвардигоро, ба мо дар дунё некӣ [рӯзӣ, саломатӣ, дониш ва имон] ато кун ва дар охират [низ] некӣ [ато фармо] ва моро аз азоби оташ нигаҳ дор"');
INSERT INTO "pioneers" VALUES(2,202,'2:202','Инҳоанд, ки аз дастовардашон баҳрае доранд ва Аллоҳ таоло дар ҳисобрасӣ сареъ аст');
INSERT INTO "pioneers" VALUES(2,203,'2:203','Аллоҳро дар рӯзҳои муайян [се рӯзи айёми ташриқ ёздаҳум то сенздаҳуми Зулҳиҷҷа] ёд кунед; пас, касе, ки шитоб кунад ва тайи ду рӯз [аъмолро анҷом диҳад], гуноҳе бар ӯ нест; ва касе, ки таъхир кунад [ва аъмолро дар се рӯз анҷом диҳад], гуноҳе бар ӯ нест. [Ин фурсат] Барои касе аст, ки тақво пеша кунад; ва [эй муъминон] аз Аллоҳ таоло парво кунед ва бидонед, ки ба сӯйи Ӯ ҳашр хоҳед шуд');
INSERT INTO "pioneers" VALUES(2,204,'2:204','Ва [эй паёмбар], аз мардум касе аст, ки гуфтораш дар [мавриди] зиндагии дунё туро ба шигифт меандозад ва Аллоҳро бар [имони қалбӣ ва] он чи дар дилаш дорад, гувоҳ мегирад [то ҷойе, ки ӯро ростгӯ мепиндорӣ] ва ҳол он ки ӯ [дар зумраи] сарсахттарин душманон [ва бадхоҳони ислому муслимин] аст');
INSERT INTO "pioneers" VALUES(2,205,'2:205','Ӯ ҳангоме ки [аз ту] рӯй бармегардонад ва [аз наздат] меравад [ё ба раёсате мерасад], дар роҳи фасод дар замин мекӯшад ва [маҳсулоти] кишоварзӣ ва чаҳорпоёнро нобуд месозад; ва Аллоҳ таоло фасоду [табоҳкорӣ]-ро дӯст надорад');
INSERT INTO "pioneers" VALUES(2,206,'2:206','Ва ҳангоме ки ба ӯ гуфта мешавад: "Аз Аллоҳ таоло битарс", худбузургбинӣ [ва ғурураш] ӯро ба [идома додан бар анҷоми] гуноҳ мекашонад; пас, [оташи] ҷаҳаннам барояш кофӣ аст ва чи бад ҷойгоҳе аст!');
INSERT INTO "pioneers" VALUES(2,207,'2:207','Ва аз мардум касе [низ] ҳаст, ки ҷони худро барои касби хушнудии Аллоҳ таоло дареғ намедорад; ва Аллоҳ таоло нисбат ба бандагон меҳрубон аст');
INSERT INTO "pioneers" VALUES(2,208,'2:208','Эй касоне, ки имон овардаед, ҳамагӣ ба ислом [ва итоати Аллоҳ таоло] дароед ва аз [роҳҳову] гомҳои шайтон пайравӣ накунед. Ба ростӣ, ки ӯ душмани ошкори шумост');
INSERT INTO "pioneers" VALUES(2,209,'2:209','Пас, агар баъд аз он ки нишонаҳои равшану ошкор бароятон омад, лағжиш кардед ва [мунҳариф шудед], бидонед, ки Аллоҳ таоло шикастнопазиру ҳаким аст');
INSERT INTO "pioneers" VALUES(2,210,'2:210','Оё интизорӣ [-и онон, ки пайрави шайтонанд] ғайр аз ин аст, ки Аллоҳ таоло [дар рӯзи қиёмат барои доварӣ] дар сояҳое аз абрҳо ба сӯяшон биёяд ва фариштагон [низ барои иҷрои азоби илоҳӣ ононро иҳота кунанд] ва кор яксара шавад? Ва [огоҳ бошед, ки ҳамаи] корҳо ба сӯйи Аллоҳ таоло бозгардонда мешавад');
INSERT INTO "pioneers" VALUES(2,211,'2:211','[Эй паёмбар], аз Бани Исроил бипурс, [ки] чӣ андоза нишонаҳои ошкор ба онҳо додаем; ва касе, ки неъмати Аллоҳро пас аз он ки насибаш шуд, [ба куфру носипосӣ] табдил кунад, [азоби сахте хоҳад дошт ва бояд бидонад, ки] Аллоҳ таоло сахткайфар аст');
INSERT INTO "pioneers" VALUES(2,212,'2:212','Зиндагии дунё барои кофирон ороста шудааст ва муъминонро масхара мекунанд, ҳол он ки парҳезкорон дар рӯзи қиёмат болотар аз онон ҳастанд; [зеро дар биҳишт ҷовидонанд] ва Аллоҳ таоло ҳар киро бихоҳад, бешумор рӯзӣ медиҳад');
INSERT INTO "pioneers" VALUES(2,213,'2:213','[Дар оғоз] мардум як уммат буданд [аммо дар амри дин ихтилоф карданд]; он гоҳ Аллоҳ таоло паёмбаронро башоратбахшу бимдиҳанда барангехт ва ҳамроҳ бо онон китоб [-и осмонӣ]-ро ба ҳақ нозил кард, то миёни мардум дар он чи ихтилоф доштанд, доварӣ кунанд; ва фақат касоне, ки Таврот] ба онон дода шуда буд – пас аз он ки нишонаҳои равшан ба онҳо расид – ба хотири ситам ва ҳасодате, ки байнашон буд, дар мавриди он ихтилоф карданд; пас, Аллоҳ таоло ба фармони хеш касонеро, ки имон оварда буданд, ба ҳақиқате, ки дар он ихтилоф доштанд, ҳидоят намуд; ва Аллоҳ таоло ҳар киро бихоҳад, ба роҳи рост ҳидоят мекунад');
INSERT INTO "pioneers" VALUES(2,214,'2:214','[Эй муъминон] Оё пиндоштаед, ки вориди биҳишт мешавед, ҳол он ки ҳанӯз монанди он чи бар [сари] пешиниёни шумо омад, бар [сари] шумо наомадааст? Душворӣ ва зиён ба эшон расид ва [он чунон сахтӣ кашиданд ва] ба ларза афтоданд, ки паёмбар ва касоне, ки бо ӯ имон оварда буданд, гуфтанд: "Ёрии Аллоҳ таоло кай фаро хоҳад расид"? Огоҳ бошед! Бе тардид ёрии Аллоҳ таоло наздик аст');
INSERT INTO "pioneers" VALUES(2,215,'2:215','[Эй паёмбар, ёронат] Аз ту мепурсанд, чи чизе инфоқ кунанд, бигӯ: "Ҳар моле, ки инфоқ кардед, барои падару модару наздикон ва ятимону тиҳидастон ва дарроҳмондагон аст ва ҳар кори неке, ки анҷом диҳед, Аллоҳ таоло ба он доност"');
INSERT INTO "pioneers" VALUES(2,216,'2:216','[Эй муъминон] Ҷиҳод [дар роҳи Аллоҳ таоло] бар шумо воҷиб шудааст, дар ҳоле ки барои шумо нохушоянд аст [зеро харҷи молӣ ва ҷонӣ дорад]. Чи басо чизеро хуш надошта бошед, ҳол он ки бароятон беҳтар аст ва чи басо чизеро дӯст дошта бошед, ҳол он ки бароятон бад аст; ва Аллоҳ таоло [хайру шарри корҳоро] медонад ва шумо намедонед');
INSERT INTO "pioneers" VALUES(2,217,'2:217','[Эй паёмбар] Аз ту дар бораи ҷанг дар моҳи ҳаром [Муҳаррам, Раҷаб, Зулқаъда ва Зулҳиҷҷа] мепурсанд, бигӯ: "Ҷанг дар он [моҳҳо гуноҳи] бузург аст ва боздоштан аз роҳи Аллоҳ таоло ва куфр варзидан ба Ӯ ва [ҷилавгирӣ аз вуруди мардум ба] Масҷид-ул-ҳаром ва берун рондани аҳлаш аз он ҷо назди Аллоҳ таоло [гуноҳи] бузургтар аст ва фитна [-и ширк] аз қатл бузургтар аст". Ва [эй муъминон, мушрикон] ҳамвора бо шумо меҷанганд, то агар битавонанд шуморо аз динатон баргардонанд; ва ҳар як аз шумо, ки аз динаш бозгардад ва дар ҳоли куфр бимирад, аъмолаш дар дунё ва охират табоҳ мегардад ва онон аҳли оташанд ва ҳамеша дар он хоҳанд буд');
INSERT INTO "pioneers" VALUES(2,218,'2:218','Касоне, ки имон оварданд ва касоне, ки [барои барпоии дини ҳақ] ҳиҷрат намуданд ва дар роҳи Аллоҳ таоло ҷиҳод карданд, онон ба раҳмати Аллоҳ таоло умедворанд ва Аллоҳ таоло [нисбат ба эшон] омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(2,219,'2:219','[Эй паёмбар, асҳобат] аз ту дар бораи шароб ва қимор мепурсанд, бигӯ: "Дар он ду гуноҳи бузург ва [низ] судҳое барои мардум вуҷуд дорад, вале гуноҳи он ду аз судашон [бештар ва] бузургтар аст"; ва аз ту мепурсанд, чӣ чизе инфоқ кунанд, бигӯ: "Он чӣ афзун ояд [аз ниёзмандии худ]. Аллоҳ таоло оётро инчунин барои шумо равшан месозад; бошад, ки бияндешед"');
INSERT INTO "pioneers" VALUES(2,220,'2:220','Дар бораи дунё ва охират [бияндешед]; ва [эй паёмбар] аз ту дар мавриди [чигунагии сарпарастиашон бар] ятимон мепурсанд; бигӯ: "Ислоҳи [кору парҳез аз даромехтан дар амволи] онон беҳтар аст; ва агар бо [ҷамъ кардани амволи онон бо дороии худ дар нафақаҳои зиндагӣ ва маскан бо] онон ҳамзистӣ кунед, [эроде дар ин кор нест; зеро] онҳо бародарони [динии] шумо ҳастанд". Ва Аллоҳ таоло табаҳкорро аз дурусткор бозмешиносад; ва агар Аллоҳ таоло мехост, [дар мавриди ятимон сахтгирӣ мекард ва] шуморо ба заҳмат меандохт. Ба ростӣ, ки Аллоҳ таоло шикастнопазиру ҳаким аст');
INSERT INTO "pioneers" VALUES(2,221,'2:221','Ва [эй муъминон] бо занони мушрик то имон наёвардаанд, издивоҷ накунед; ва бе гумон канизи муъмин, аз зани [озоди] мушрик беҳтар аст; агарчи [зебоӣ ё дороиаш] мавриди писандатон воқеъ гардад; ва [занони мусулмонро] ба издивоҷи мардони мушрик дарнаёваред, магар он ки имон биёваранд; ва ҳатман бардаи муъмин аз марди [озоди] мушрик беҳтар аст; агарчи шефтаи [дороӣ ва мавқеияти] ӯ шуда бошед. Онон ба сӯйи оташ [-и дузах] даъват мекунанд ва Аллоҳ таоло ба фармон [ва тавфиқ]-и хеш ба сӯйи биҳишт ва омурзиш даъват мекунанд ва оёт [-у аҳкоми] худро барои мардум равшан месозад; бошад, ки панд гиранд');
INSERT INTO "pioneers" VALUES(2,222,'2:222','Ва [эй паёмбар] аз ту дар бораи ҳайз [одати моҳонаи занон] мепурсанд, бигӯ: "Он [ҳолат] навъи нороҳатӣ аст; пас, дар ҳолати қоидагӣ аз занон канорагирӣ кунед ва бо онон наздикӣ накунед, то пок шаванд. Пас, ҳангоме ки пок шуданд [ва ғусл карданд], аз ҳамон ҷойе, ки Аллоҳ таоло ба шумо фармон додааст, бо эшон омезиш кунед. Ба ростӣ, ки Аллоҳ таоло тавбакунандагонро дӯст медорад ва покизагонро [низ] дӯст медорад"');
INSERT INTO "pioneers" VALUES(2,223,'2:223','Занони шумо [дар ҳукми] киштзоре барои шумо ҳастанд, пас, ҳар гуна ки мехоҳед ба киштзори худ дароед ва [тӯшае ҳамчун фарзанди солеҳ] барои худ пеш бифиристед ва аз Аллоҳ таоло парво кунед ва бидонед, ки Ӯро мулоқот хоҳед кард ва [эй паёмбар, дар бораи неъматҳои илоҳӣ] ба муъминон башорат бидеҳ');
INSERT INTO "pioneers" VALUES(2,224,'2:224','Ва Аллоҳро дастовези савгандҳои худ қарор надиҳед, то [ба ин баҳона] аз накукорӣ ва парҳезкориву оштӣ додан миёни мардум [даст бардоред]; ва [бидонед, ки] Аллоҳ таоло шунавову доност');
INSERT INTO "pioneers" VALUES(2,225,'2:225','Аллоҳ таоло шуморо ба хотири савгандҳое, ки беҳуда ва бидуни касд ёд мекунед, бозпурсӣ намекунад, вале ба хотири он чи дилҳоятон [амдан] касб кардааст [ва савгандҳои дурӯғе, ки огоҳона бар забон овардаед] бозхост мекунад ва [агар тавба кунед] Аллоҳ таоло омурзандаи бурдбор аст');
INSERT INTO "pioneers" VALUES(2,226,'2:226','Барои касоне, ки [ба расми ҷоҳилият] бар тарки омезиш бо занони худ савганд ёд кунанд, чаҳор моҳ интизор [ва муҳлат] аст. Пас, агар [дар муддати чаҳор моҳ ё камтар пушаймон шуданд ва ба занонашон] бозгаштанд, [бидонанд, ки] Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(2,227,'2:227','Ва агар [бар тарки занонашон пофишорӣ кунанд ва] тасмим ба талоқ гирифтанд, [бидонанд, ки] ҳамоно Аллоҳ таоло шунавову доност');
INSERT INTO "pioneers" VALUES(2,228,'2:228','Занони талоқшуда [пеш аз издивоҷи муҷаддад] бояд ба муддати се бор покӣ [дидани се бор одати моҳона] интизор бикашанд; ва агар ба Аллоҳ таоло ва рӯзи қиёмат имон доранд, бар онон раво нест, ки он чиро Аллоҳ таоло дар раҳимҳояшон офаридааст, китмон кунанд; ва агар шавҳаронашон хоҳони оштӣ ҳастанд, барои бозгардондани онон [ба зиндагии заношӯӣ] дар ин [муддат аз дигарон] сазовортаранд; ва занон ҳуқуқи шоистае доранд, ҳаммонанд [-у ҳамсангии вазоифе], ки бар уҳда доранд ва мардон [дар сарпарастиву амри талоқ] бар онон бартарӣ доранд; ва Аллоҳ таоло шикастнопазиру ҳаким аст');
INSERT INTO "pioneers" VALUES(2,229,'2:229','Талоқ [-и раҷъӣ] ду бор аст; пас, [аз он мард ё бояд ҳамсарашро] ба тарзи шоиста нигоҳ дорад, ё ба некӣ раҳо кунад; ва [эй мардони мусулмон] бароятон ҳалол нест, ки [заррае] аз он чиро ба онон додаед [маҳрия ва монанди он] пас бигиред, магар он ки [зану шавҳар] битарсанд, ки натавонанд [ҳуқуқи заношӯиву] ҳудуди илоҳиро барпо доранд; пас, [ҳалли ихтилофро ба хешовандон ё дӯстони худ месупоранд; ва шумо эй муъминон] агар тарсидед, ки [мумкин аст завҷайн] ҳудуди илоҳиро барпо надоранд, гуноҳе бар он ду нест, ки зан [барои розӣ кардани мард ба талоқ маҳрияашро бибахшад ё ба ӯ] фидяе бипардозад [ва талоқи хулъ бигирад] . Инҳо ҳудуди [аҳкоми] илоҳӣ аст; пас, аз онҳо таҷовуз накунед; ва ҳар ки аз ҳудуди Аллоҳ таоло таҷовуз кунад, пас чунин одамон, бешак, ситамгоронанд.');
INSERT INTO "pioneers" VALUES(2,230,'2:230','Агар [шавҳар барои бори савум] ӯро талоқ дод, пас аз он [зан] барояш ҳалол нест; магар ин ки [бо қасди издивоҷи ҳақиқӣ ва на барои ҳалол шудан] бо шавҳари ғайр аз ӯ издивоҷ кунад [ва бо вай ҳамбистар шавад. Дар ин сурат] Агар [шавҳари баъдӣ фавт кард ё] ӯро талоқ дод, агар [ду ҳамсари собиқ] мепиндоранд, ки ҳудуди [аҳкоми] Аллоҳро барпо медоранд, гуноҳе бар он ду нест, ки [бо издивоҷи муҷаддад] ба якдигар бозгарданд. Инҳо ҳудуди Аллоҳ таоло аст, ки онҳоро барои мардуме, ки огоҳанд [ва ба ислом имон доранд], баён мекунад');
INSERT INTO "pioneers" VALUES(2,231,'2:231','Ва ҳангоме ки занонро талоқ [-и раҷъӣ] додед ва ба поёни [рӯзҳои] иддаи хеш расиданд, ба шоистагӣ ононро нигаҳ доред ва ё ба некиву хубӣ раҳояшон созед; ва [ҳамчун даврони ҷоҳилият] ононро ба хотири [озору] зиён расондан нигаҳ надоред, то [ба ҳуқуқашон] таҷовуз кунед; ва ҳар ки чунин кунад, ҳатман ба худ ситам кардааст; ва оёти Аллоҳро ба сухра [лаҳву лаъб] нагиред ва неъмати Аллоҳро бар худ ва он чиро, ки аз китоб [Қуръон] ва ҳикмат бар шумо нозил кардааст ва шуморо бо он панд медиҳад, ба ёд биёваред ва аз Аллоҳ таоло парво кунед ва бидонед, ки Аллоҳ таоло ба ҳама чиз доност');
INSERT INTO "pioneers" VALUES(2,232,'2:232','Ва ҳангоме ки занонро талоқ додед ва [муддате] иддаи худро ба поён расонданд, [шумо, эй сарпарастон] ононро боз надоред, ки агар [бар асоси меъёрҳои шаръӣ] ба тарзи писандидае байни хеш ба тавофуқ расида буданд, бо шавҳарони [собиқи] худ издивоҷ кунанд. Ин [дастуре аст], ки ҳар яке аз шумо, ки ба Аллоҳ таоло ва рӯзи қиёмат имон дорад, ба он [ҳукм] панд дода мешавад; ва ин [дастур] бароятон пурбаракаттар ва покизатар аст; ва Аллоҳ таоло [хайру салоҳи шуморо] медонад ва шумо намедонед');
INSERT INTO "pioneers" VALUES(2,233,'2:233','Ва модарон [бояд] фарзандони худро ду соли тамом шир диҳанд. [Ин дастур] Барои касест, ки бихоҳад даврони ширхораги [-и фарзандаш]-ро такмил кунад; ва бар соҳиби фарзанд [падар] лозим аст хӯроку пӯшоки онон [модарон]-ро [дар муддати шир додан] ба таври шоиста [фароҳам кунад]. Ҳеҷ кас ҷуз ба андозаи тавоноиаш мукаллаф намешавад; на модар бояд ба хотири фарзандаш зиён бубинад ва на падар ба сабаби фарзандаш. [Агар падари кӯдак фавт карда буд ва моле ҳам надошт] Монанди ҳамин [тааҳҳудоти падар] бар ворис [низ] лозим аст; пас, агар он ду [падару модар] бо ризояту машварати якдигар бихоҳанд [кӯдакро зудтар] аз шир бозгиранд, гуноҳе бар онҳо нест; ва агар хостед, ки барои фарзандатон дояе бигиред, гуноҳе бар шумо нест; ба шарте ки он чи [аз ҳуқуқу дастмузд] муқаррар кардаед, ба таври шоиста бипардозед; ва аз Аллоҳ таоло парво кунед ва бидонед, ки Аллоҳ таоло ба он чи анҷом медиҳед биност');
INSERT INTO "pioneers" VALUES(2,234,'2:234','Ва афроде аз шумо, ки мемиранд ва ҳамсарон [-и ғайри бордор аз худ] боқӣ мегузоранд, [он занон барои издивоҷи муҷаддад] бояд чаҳор моҳ ва даҳ рӯз интизор бикашанд [ва идда нигаҳ доранд] ва ҳангоме ки муддати иддаи худро ба поён расониданд, дар мавриди он чи онон ба таври шоиста [ва машруъ] дар бораи [зиндагии] худ анҷом диҳанд, гуноҳе бар шумо [сарпарастон] нест; ва Аллоҳ таоло ба он чи мекунед, огоҳ аст');
INSERT INTO "pioneers" VALUES(2,235,'2:235','Ва гуноҳе бар шумо нест, ки ба таври киноя аз занони [бева ё талоқшудае, ки идда нигаҳ доштаанд] хостгорӣ кунед ва ё дар дил тасмим бар ин кор бигиред [бидуни ин ки онро баён кунед]. Аллоҳ таоло медонист, ки шумо ба зудӣ ба ёди онон хоҳед афтод; вале пинҳонӣ ба онҳо ваъда [-и заношӯӣ] надиҳед, магар ин ки [ба киноя] сухани писандида бигӯед ва [ҳамчунин] тасмим ба ақди издивоҷ нагиред, то муддати муқаррар [идда] ба сар ояд; ва бидонед, ки Аллоҳ таоло он чиро, ки дар дилҳои шумост, медонад; пас, аз [мухолифат бо] Ӯ парво кунед ва бидонед, ки ба ростӣ Аллоҳ таоло омурзандаи бурдбор аст');
INSERT INTO "pioneers" VALUES(2,236,'2:236','Агар занонро пеш аз омезиш ва таъйини маҳрия [ба далоиле] талоқ диҳед, гуноҳе бар шумо нест ва [дар ин сурат дилҷӯӣ кунед ва] ононро [бо ҳадяи муносиб] баҳраманд созед; тавонгар ба андозаи тавонаш ва тангдаст ба андозаи тавонаш. [Иҷрои ин ҳукм] Бар накукорон [ҳаққи собит ва] илзомист');
INSERT INTO "pioneers" VALUES(2,237,'2:237','Агар пеш аз наздикӣ бо онон хостед талоқашон бидиҳед ва маҳрияе барояшон таъйин карда будед, нисфи он чиро, ки таъйин кардаед [ба онон бипардозед]; магар ин ки [ҳаққи худро] бибахшанд ё [шавҳар, яъне] касе, ки [тасмимгирӣ дар мавриди] пайванди издивоҷ бо ӯст, гузашт кунад [ва тамоми маҳрияро бипардозад]. Гузашт кардани шумо [ва бахшидани тамоми маҳрия ба занон] ба парҳезкорӣ наздиктар аст. [Эй муъминон, ҳангоми пардохти ҳуқуқ, осонгирӣ ва] некиро миёни худ фаромӯш накунед [ва бидонед, ки] ба ростӣ Аллоҳ таоло ба он чи мекунед биност');
INSERT INTO "pioneers" VALUES(2,238,'2:238','[Эй мусулмонон] бар [анҷоми] ҳама намозҳо ва [ба хусус] намози аср кӯшо бошед [саъю талош кунед] ва бо фурӯтаниву фармонбардорӣ барои Аллоҳ таоло ба по хезед');
INSERT INTO "pioneers" VALUES(2,239,'2:239','Агар [аз хатар] тарсидед, дар ҳоли пиёда ё савора [ё ҳатто бо имо ва ишора намозро ба ҷой оваред] ва чун эмин шудед, Аллоҳро [чунонки ба шумо омӯхтааст] ёд кунед [ва шукрашро ба ҷо оваред], ки он чиро намедонистед ба шумо ёд дод');
INSERT INTO "pioneers" VALUES(2,240,'2:240','Касоне аз шумо, ки маргашон фаро мерасад ва ҳамсароне [аз худ] ба ҷо мегузоранд, бояд барои ҳамсаронашон васият кунанд, ки [ворисон] то як сол ононро [бо пардохтани харҷи зиндагӣ] баҳраманд созанд, бидуни он ки [аз хонаи шавҳар] берун кунанд. Пас, агар [ба майли худ пеш аз поёни як сол хонаро тарк кунанд ва] берун бираванд, дар бораи он чи, ки нисбат ба [зоҳир ва зиндагии] худ ба таври шоиста [ва машруъ] анҷом медиҳанд [аз қабили зинат кардан ва издивоҷи муҷаддад] гуноҳе бар шумо нест; ва Аллоҳ таоло шикастнопазиру ҳаким аст');
INSERT INTO "pioneers" VALUES(2,241,'2:241','Ва барои занони талоқшуда ҳадяи муносибу шоиста лозим аст [ки аз тарафи шавҳар пардохта шавад, то дилшикастагиаш ҷуброн гардад. Иҷрои ин ҳукм], ҳаққе аст бар мардони парҳезкор');
INSERT INTO "pioneers" VALUES(2,242,'2:242','[Эй муъминон] Аллоҳ таоло оёти худро [ки муштамал бар ҳудуд ва аҳком аст] инчунин бароятон ба равшанӣ баён мекунад; бошад, ки бияндешед');
INSERT INTO "pioneers" VALUES(2,243,'2:243','[Эй паёмбар] Оё намедонӣ, [достони] афроде [аз бани Исроил]-ро, ки ҳазорон нафар буданд, ки аз тарси [беморӣ ва] марг аз хонаҳояшон фирор карданд? Аллоҳ таоло ба онон гуфт: Бимиред [пас мурданд]. Сипас Аллоҳ таоло онҳоро зинда кард [то барояшон равшан гардонад, ки тамоми корҳо ба дасти Ӯст]. Ба ростӣ, ки Аллоҳ таоло нисбат ба бандагонаш бахшоиш дорад, вале бештари мардум шукр [-и неъматҳо] -ашро ба ҷой намеоваранд');
INSERT INTO "pioneers" VALUES(2,244,'2:244','Ва [эй муъминон] дар роҳи Аллоҳ таоло биҷангед ва бидонед, ки бе тардид Аллоҳ таоло шунавову доност');
INSERT INTO "pioneers" VALUES(2,245,'2:245','Кист, ки ба Аллоҳ таоло қарз диҳад; қарзи наку [ва аз амволаш дар роҳи хушнудии Парвардигор инфоқ кунад], то [Аллоҳ таоло] онро барояш чандин баробар кунад? Ва Аллоҳ таоло аст, ки [аз рӯйи ҳикмат ва адолат, дар рӯзии бандагонаш] тангӣ ва кушоиш падид меоварад ва [ҳамагӣ дар охират] ба сӯйи Ӯ бозгардонда мешавед');
INSERT INTO "pioneers" VALUES(2,246,'2:246','[Эй паёмбар] Оё [аз достони] гурӯҳе аз [ашрофу бузургони] Бани Исроил хабар дорӣ, ки баъд аз Мӯсо ба паёмбари худ гуфтанд: "Фармонравое бароямон интихоб кун, то [ба дастураш] дар роҳи Аллоҳ таоло биҷангем"? [Паёмбарашон] Гуфт: "Шояд агар ҷиҳод бар шумо муқаррар шавад, [сарпечӣ кунед ва] ҷиҳод накунед". Гуфтанд: "Чаро дар роҳи Аллоҳ таоло ҷиҳод накунем, дар ҳоле ки аз хонаҳоямон ронда шудаем ва писаронамон [-ро куштаанд ё ба асорат бурдаанд]"? Пас, ҳангоме ки ҷанг бар эшон муқаррар шуд – ҷуз иддаи каме аз онон – [ҳамагӣ] сарпечӣ карданд; ва Аллоҳ таоло ба [ҳоли паймоншиканон ва] ситамгорон доност');
INSERT INTO "pioneers" VALUES(2,247,'2:247','Паёмбарашон ба онон гуфт: "Аллоҳ таоло Толутро ба фармонравоии шумо баргузидааст". [Онон] гуфтанд: "Чи гуна ӯ бар мо фармонравоӣ мекунад, дар ҳоле ки мо ба фармонравоӣ аз ӯ сазовортарем ва аз мол [-и дунё низ] баҳраи чандоне [ба вай] дода нашудааст"? Ӯ гуфт: "Ба ростӣ, ки Аллоҳ таоло ӯро бар шумо баргузида ва донишу неруи ҷисмиашро вусъат бахшидааст; ва Аллоҳ таоло фармонравоии худро ба ҳар ки бихоҳад медиҳад ва Аллоҳ таоло кушоишгару доност"');
INSERT INTO "pioneers" VALUES(2,248,'2:248','Паёмбарашон ба онҳо гуфт: Нишонаи [ростӣ ва дурустии] фармонравоии ӯ, ин аст, ки тобути аҳд бароятон мерасад, ки мояи оромише аз сӯйи Парвардигоратон мебошад ва дарунаш бозмондаи мероси хонадони Мӯсо ва Ҳорун [ҳамчун асо ва бархе алвоҳи Таврот] қарор дорад ва дар ҳоле мерасад, ки фариштагон онро бардоштаанд. Агар муъмин бошед, мусалламан дар ин [амр] нишонае [дар мавриди ҳаққонияти Толут] бароятон вуҷуд дорад');
INSERT INTO "pioneers" VALUES(2,249,'2:249','Пас, ҳангоме ки Толут бо лашкариён раҳсипор шуд, ба онон гуфт: "Аллоҳ таоло [шикебоии] шуморо ба василаи як наҳр [об] озмоиш мекунад; ҳар ки аз он бинӯшад, аз [ёрони] ман нест ва ҳар ки аз он нанӯшад, ҳатман аз [ёрони] ман аст; магар касе, ки бо дасти худ муште [аз он об] баргирад [ва ба андозаи кафи дасте биошомад]". Пас, ҷуз иддаи каме ҳамагӣ аз он [об] нӯшиданд; ва ҳангоме ки ӯ ва касоне, ки бо ӯ имон оварда буданд, аз он [наҳр] гузаштанд, [аз камии афроди худ нороҳат шуданд ва бархе] гуфтанд: "Имрӯз мо тавоноии [муқобилият] бо Ҷолут ва сипоҳиёнашро надорем". [Дар ин ҳангом касоне, ки ба дидори Аллоҳ таоло [дар охират] яқин доштанд, гуфтанд: "Чи басо гурӯҳи кучаке, ки ба фармони Аллоҳ таоло бар гурӯҳи бисёр пирӯз шуданд ва [бидонед, ки ҳамвора] Аллоҳ таоло ҳамроҳи сабркунандагон аст"');
INSERT INTO "pioneers" VALUES(2,250,'2:250','Ва ҳангоме ки [Толут ва ҳамроҳонаш] барои [мубориза] бо Ҷолут ва сипоҳиёнаш ба майдон омаданд, гуфтанд: "Парвардигоро, бар [дилҳои] мо сабру шикебоӣ фурӯ рез ва гомҳоямонро устувор бидор ва моро бар гурӯҳи кофирон пирӯз гардон"');
INSERT INTO "pioneers" VALUES(2,251,'2:251','Пас, ба фармони Аллоҳ таоло, онҳоро шикаст доданд ва Довуд [ҷавони неруманду шуҷое, ки дар лашкари Толут буд] Ҷолутро кушт ва Аллоҳ таоло фармонравоиву нубувватро ба ӯ бахшид ва аз он чи мехост, ба ӯ омӯхт; ва агар Аллоҳ таоло [фитнаи] бархе аз мардумро тавассути бархи дигар [дур намекард] намеронд, ҳатман замин табоҳ мегардид; вале Аллоҳ таоло бар ҷаҳониён бахшиш дорад');
INSERT INTO "pioneers" VALUES(2,252,'2:252','[Эй паёмбар] Инҳо оёти Аллоҳ таоло аст, ки онро ба ҳақ бар ту мехонем ва ба ростӣ, ту аз паёмбарон ҳастӣ');
INSERT INTO "pioneers" VALUES(2,253,'2:253','Ин паёмбарон [ки номи бархе аз онон гузашт] бархе аз эшонро [дар дарёфти ваҳй ва теъдоди пайравон] бар бархе дигар бартарӣ додем. Аз [миёни] эшон касе [ҳамчун] буд, ки Аллоҳ таоло бо ӯ сухан гуфт ва дараҷоти бархе [дигар] аз ононро боло бурд; ва ба Исо писари Марям нишонаҳои равшане [бар исботи нубувваташ] додем ва ӯро тавассути Рӯҳулқудус [Ҷабраил] таъйид [ва тақвият] кардем; ва агар Аллоҳ таоло мехост, касоне, ки баъд аз онҳо буданд, пас аз он [ҳама] нишонаҳои равшане, ки барояшон омад, бо якдигар ҷанг намекарданд; вале [бо ҳам] ихтилоф карданд ва бархе аз онон имон оварданд ва бархе куфр варзиданд; ва агар Аллоҳ таоло мехост, [ҳаргиз] бо ҳам намеҷангиданд; вале Аллоҳ таоло он чиро мехоҳад, анҷом медиҳад');
INSERT INTO "pioneers" VALUES(2,254,'2:254','Эй касоне, ки имон овардаед, аз он чи ба шумо рӯзӣ додаем, инфоқ кунед, пеш аз он ки рӯзе фаро расад, ки дар он на хариду фурӯше ҳаст ва на дӯстиву шафоате; ва кофирон [ба сабаби куфрашон] ҳамон ситамгоронанд');
INSERT INTO "pioneers" VALUES(2,255,'2:255','Аллоҳ таоло [маъбуди ростин аст] ҳеҷ маъбуде [ба ҳақ] ҷуз Ӯ нест; зиндаи поянда [ва қоим ба зот] аст; на хоби сабук [ғанав, пинак] Ӯро фаро мегирад ва на хоби сангин; он чи дар осмонҳо ва замин аст, аз они Ӯст. Кист, ки дар назди Ӯ ҷуз ба фармонаш шафоат кунад?! Гузашта ва ояндаи эшон [бандагон]-ро медонад ва [онон] ба чизе аз илми Ӯ иҳота [ва огоҳӣ] намеёбанд, магар он чи худ бихоҳад. Курсии Ӯ осмонҳо ва заминро дар бар гирифтааст ва нигаҳ доштани онҳо бар Ӯ [сангину] душвор нест ва Ӯ баландмартаба [ва] бузург аст');
INSERT INTO "pioneers" VALUES(2,256,'2:256','Дар [пазириши] дин, ҳеҷ иҷборе нест; ба ростӣ, ки роҳи рост [ва ҳидоят] аз роҳи инҳироф [ва гумроҳӣ] равшан шудааст; пас, ҳар кас ба тоғут [он чи ба ҷойи Аллоҳ таоло парастиш мешавад] куфр биварзад ва ба Аллоҳ таоло имон оварад, ҳатман, ба маҳкамтарин дастовез чанг задааст, ки ҳаргиз нахоҳад гусаст; ва Аллоҳ таоло шунавову доност');
INSERT INTO "pioneers" VALUES(2,257,'2:257','Аллоҳ таоло ёвар [ва корсози] касоне аст, ки имон овардаанд; ононро аз торикиҳо ба сӯйи нур берун мебарад; ва касоне, ки кофир шуданд, [корсоз ва] ёварашон тоғут аст, [ки] ононро аз нур [–и имон ва илм] ба сӯйи торикиҳо [–и куфру нодонӣ] берун мебарад. Онон аҳли оташанд [ва] дар он ҷовидонанд');
INSERT INTO "pioneers" VALUES(2,258,'2:258','[Эй паёмбар], оё [достони] он шахсро надонистаӣ, ки аз [сари мастӣ] он ки Аллоҳ таоло бар ӯ фармонравоӣ бахшида буд бо Иброҳим дар бораи Парвардигораш муҷодала мекард? Ҳангоме ки Иброҳим гуфт: "Парвардигори ман касе аст, ки зинда мекунад ва мемиронад". [Намруд] Гуфт: "Ман [низ] зинда мекунам ва мемиронам". Иброҳим гуфт: "Аллоҳ таоло хуршедро аз машриқ [берун] меоварад, пас, ту онро аз мағриб биёвар". [Дар ин ҷо] Он ки куфр варзида буд, мабҳут шуд; ва Аллоҳ таоло ситамгоронро ҳидоят намекунад');
INSERT INTO "pioneers" VALUES(2,259,'2:259','Ё ҳамчун он, ки аз шаҳр [-и вайрону матрук] гузашт, ки сақфҳо ва деворҳояш фурӯ рехта буд. [Ӯ бо худ] Гуфт: "Чи гуна Аллоҳ таоло [аҳли] ин [диёр] – ро пас аз маргашон зинда мекунад"? Пас Аллоҳ таоло ӯро [ба муддати] сад сол миронид, [ва] сипас зинда кард [ва ба ӯ] гуфт: "Чанд муддат [дар ин ҳол] мондаӣ"? Гуфт: "Як рӯз ё бахше аз як рӯз [дар ин ҳол] мондаам". Фармуд: "Чунин нест; сад сол [дар чунин ҳоле] мондаӣ. Ба хӯрданиву нӯшиданиат бингар, ки бо гузашти замон дигаргун нашудааст ва ба дарозгӯши худ бингар [ки чӣ гуна муталошӣ шудааст. Мо чунин кардем то ба суолат посух гӯем] ва туро барои мардум нишонае [дар мавриди қиёмат] қарор диҳем. [Акнун] Ба устухонҳои он [ҳайвон] нигоҳ кун, ки чи гуна онҳоро ба ҳам пайванд медиҳем ва сипас гӯште бар он мепӯшонем". Пас, ҳангоме ки [қудрати илоҳӣ бар зинда кардани мурдагон] барояш ошкор шуд, гуфт: "[Ба яқин] Медонам, ки Аллоҳ таоло бар ҳар коре тавоност"');
INSERT INTO "pioneers" VALUES(2,260,'2:260','Ва [Эй паёмбар, ёд кун аз] ҳангоме ки Иброҳим гуфт: "Парвардигоро, ба ман нишон бидеҳ, чи гуна мурдагонро зинда мекунӣ"? [Аллоҳ таоло] Фармуд: "Магар имон наёвардаӣ"? Гуфт: "Бале, вале [мехоҳам] қалбам ором бигирад". Фармуд: "Пас, чаҳор парранда бигир [ва бикуш] ва пора – пора кун [ва дар ҳам биёмез], сипас бар ҳар кӯҳе порае аз онҳоро бигзор, [ва] онгоҳ онҳоро [ба сӯйи худ] фаро хон; [хоҳӣ дид, ки зинда мешаванд ва] шитобон ба сӯйи ту меоянд; ва [эй Иброҳим], бидон, ки Аллоҳ таоло шикастнопазиру ҳаким аст"');
INSERT INTO "pioneers" VALUES(2,261,'2:261','Масали [аҷру савоби] касоне, ки амволи худро дар роҳи Аллоҳ таоло инфоқ мекунанд, ҳаммонанди донаест, ки ҳафт хӯша мерӯёнад, ки дар ҳар хӯша сад дона аст; ва Аллоҳ таоло барои ҳар ки бихоҳад, [подошашро] чанд баробар мекунад ва Аллоҳ таоло кушоишгару доност');
INSERT INTO "pioneers" VALUES(2,262,'2:262','Касоне, ки амволашонро дар роҳи Аллоҳ таоло мебахшанд ва дар пайи бахшиши худ миннату озор раво намедоранд, подошашон назди Парвардигорашон [маҳфуз] аст; на тарсе хоҳанд дошт ва на андӯҳгин мешаванд');
INSERT INTO "pioneers" VALUES(2,263,'2:263','Сухани писандида [дар бархӯрд бо ниёзмандон] ва гузашт [аз исрору тундии онон], беҳтар аз садақае аст, ки озоре ба дунболаш бошад; ва Аллоҳ таоло бениёзу бурдбор аст');
INSERT INTO "pioneers" VALUES(2,264,'2:264','Эй касоне, ки имон овардаед, садақаҳои худро бо миннату озор ботил накунед, ҳамчун касе, ки моли худро барои намоиш додан ба мардум инфоқ мекунад ва ба Аллоҳу рӯзи қиёмат имон надорад; пас, масали ӯ ҳамчун масали санги софест, ки рӯйи он [андаке] хок нишастааст; он гоҳ борони тунде бар он мезанад [ва ҳамаи хокҳоро аз санг мешӯяд] ва онро соф [-у бидуни хок] раҳо мекунад. Онон [риёкорон] аз он чи, ки анҷом додаанд, чизе ба даст намеоваранд; ва Аллоҳ таоло гурӯҳи кофиронро ҳидоят намекунад');
INSERT INTO "pioneers" VALUES(2,265,'2:265','Ва масали касоне, ки амволи худро барои талаби хушнудии Аллоҳ таоло ва ба хотири боваре, ки дар дилҳояшон [ба подоши Аллоҳ таоло] доранд, инфоқ мекунанд, ҳамчун бӯстоне бар теппае аст, ки борони тунде бар он борад ва меваҳояшро дучандон ба бор оварад; ва агар борони тунде ҳам ба он нарасад, борони сабук [низ замини покашро пурсамар месозад]; ва Аллоҳ таоло аз он чи мекунед, огоҳ аст');
INSERT INTO "pioneers" VALUES(2,266,'2:266','Оё ҳеҷ як аз шумо дӯст дорад, ки боғе аз дарахтони хурмо ва ангур дошта бошад, ки аз зери онҳо ҷӯйборҳо [-и гуворо] равон аст ва ҳар гуна мева барояш ба бор меоварад ва [аммо] дар ҳоле ки ба синни пирӣ расида ва фарзандон [-и хурдсол] ва нотавон дорад, ногаҳон гирдбоди оташин дар он занаду [ҳамаи боғ] бисӯзад? [Ҳоли касоне, ки садақаҳои худро бо миннату озор ва риё ботил мекунанд низ чунин аст]. Аллоҳ таоло бад-ин гуна оёт [-и худ]-ро барои шумо равшан месозад; бошад, ки биандешед');
INSERT INTO "pioneers" VALUES(2,267,'2:267','Эй касоне, ки имон овардаед, аз чизҳои покизае, ки ба даст овардаед ва аз он чи аз замин барои шумо баровардаем, инфоқ кунед; ва ба суроғ [-и моли] нопок наравед, ки аз он инфоқ намоед; дар ҳоле ки худатон ҳозир ба пазируфтанаш нестед, магар бо чашмпӯшӣ [ва бемайлӣ] нисбат ба он; [пас, чӣ гуна чизеро, ки барои худ намеписандед, барои Аллоҳ таоло раво медоред]? Ва бидонед, ки Аллоҳ таоло бениёзу сутуда аст');
INSERT INTO "pioneers" VALUES(2,268,'2:268','Шайтон шуморо [ба ҳангоми инфоқ метарсонад ва] ваъдаи тиҳидастӣ медиҳад ва шуморо ба зиштиҳо [гуноҳ намудан] амр мекунад; вале Аллоҳ таоло аз ҷониби худ ба шумо ваъдаи омурзиш ва афзуни [-и неъмат] медиҳад ва Аллоҳ таоло кушоишгару доност');
INSERT INTO "pioneers" VALUES(2,269,'2:269','[Аллоҳ таоло] Ба ҳар ки бихоҳад, ҳикмат [ростӣ дар гуфтор ва кирдор] медиҳад ва бе тардид, ба ҳар ки ҳикмат дода шавад, хайри фаровоне ато шудааст; ва ба ҷуз хирадмандон [аз оёти илоҳӣ] панд намегиранд');
INSERT INTO "pioneers" VALUES(2,270,'2:270','Ҳар чизе, ки инфоқ кунед ё ҳар назре, ки [бо худ] аҳд кунед, ҳатман, Аллоҳ таоло онро медонад [Ҳар ки аз иҷрои фармудаҳои илоҳӣ ҷилавгирӣ кунад, ситамгор аст] ва ситамгорон [дар охират] ҳеҷ ёваре надоранд');
INSERT INTO "pioneers" VALUES(2,271,'2:271','Агар садақаҳоро ошкор кунед, чи накуст! Ва агар онхоро пинҳон доред ва ба тиҳидастон бидиҳед, бароятон беҳтар аст ва [ин гуна садақаи холисона ва пинҳонӣ] аз гуноҳонатон мезудояд; ва [бидонед, ки дар ҳама сурат] Аллоҳ таоло ба он чи мекунед, огоҳ аст');
INSERT INTO "pioneers" VALUES(2,272,'2:272','[Эй паёмбар] Ҳидояти онон бар уҳдаи ту нест; балки Аллоҳ таоло ҳар киро бихоҳад ҳидоят мекунад; ва [эй муъминон] он чи аз моли [худ] инфоқ мекунед, ба суди худатон аст ва [ба ёд дошта бошед, ки] ҷуз барои хушнудии Аллоҳ таоло инфоқ накунед; ва он чи аз мол инфоқ мекунед, [подошаш] ба таври комил ва тамом ба шумо дода мешавад ва [ҳаргиз] ситам нахоҳед дид');
INSERT INTO "pioneers" VALUES(2,273,'2:273','[Садақоти шумо] Барои афроди тангдасте аст, ки худро вақф [-и ҷиҳод] дар роҳи Аллоҳ таоло кардаанд ва наметавонанд [барои таъмини харҷҳои зиндагӣ] дар замин сафар кунанд. Аз шиддати [наҷобат ва] хештандорӣ афроди ноогоҳ ононро бениёз мепиндоранд. Эшонро аз нишонаҳояшон [ки дар либосу вазъияти зоҳириашон ошкор аст, ба таври ошкор] мешиносӣ [ва онон ҳаргиз] бо исрор чизе аз мардум намехоҳанд; ва он чи аз мол инфоқ мекунед, ҳатман, Аллоҳ таоло аз он огоҳ аст');
INSERT INTO "pioneers" VALUES(2,274,'2:274','Касоне, ки амволи худро дар шабу рӯз, пинҳону ошкор инфоқ мекунанд, подошашон назди Парвардигорашон [маҳфуз] аст; на тарсе хоҳанд дошт ва на андӯҳгин мешаванд');
INSERT INTO "pioneers" VALUES(2,275,'2:275','Касоне, ки рибо мехӯранд, [рӯзи қиёмат аз қабр] барнамехезанд; магар монанди бархостани касе, ки бар асари тамоси шайтон девона шудааст ва [наметавонад таодули худро ҳифз кунад]. Ин бад-он сабаб аст, ки онон [рибохорон] гуфтанд: "Доду ситад низ монанди рибост [ва ин ҳар ду молро афзун мекунад]"; дар ҳоле ки Аллоҳ таоло, доду ситадро ҳалол ва риборо ҳаром намудааст. Пас, ҳар ки аз [ҷониби] Парвардигораш панде ба ӯ бирасад ва [аз рибохорӣ] даст бардорад, он чи гузашта ва [судҳое, ки қабл аз нузули ҳукми рибо дарёфт кардааст] аз они ӯст ва [дар мавриди оянда низ] кораш ба Аллоҳ таоло вогузор мешавад; ва [аммо] касоне, ки [ба рибохорӣ] бозмегарданд, аҳли оташанд ва дар он ҷовидонанд');
INSERT INTO "pioneers" VALUES(2,276,'2:276','Аллоҳ таоло риборо нобуд [ва бебаракат] мегардонад ва садақотро [бо изофа кардан бар савобаш] афзоиш [ва баракат] медиҳад; ва Аллоҳ таоло ҳеҷ кофири лаҷуҷу гунаҳгореро дӯст намедорад');
INSERT INTO "pioneers" VALUES(2,277,'2:277','Касоне, ки имон овардаанд ва корҳои шоиста анҷом додаанд ва намоз барпо доштаанд ва закот пардохтаанд, подошашон назди Парвардигорашон [маҳфуз] аст; на тарсе хоҳанд дошт ва на андӯҳгин мешаванд');
INSERT INTO "pioneers" VALUES(2,278,'2:278','Эй касоне, ки имон овардаед, аз Аллоҳ таоло парво кунед [битарсед] ва агар муъмин ҳастед, он чиро, ки аз [мутолиботи] рибо боқӣ мондааст, раҳо кунед');
INSERT INTO "pioneers" VALUES(2,279,'2:279','Агар [чунин] накардед, бидонед, ки Аллоҳ таоло ва расулаш бо шумо пайкор хоҳанд кард; ва агар тавба кардед, асли сармояҳоятон аз они шумост; на [бо гирифтани суди сармояатон ба касе] ситам мекунед ва на [бо чашмпӯшӣ аз он суди ҳаром] бар шумо ситам меравад');
INSERT INTO "pioneers" VALUES(2,280,'2:280','Ва агар [шахси қарздор] тангдаст бувад, то [ҳангоми] кушоиш [ва тавоноии бозпардохт ба ӯ] муҳлат диҳед ва [агар тавоноии пардохт надошт], бахшидани он [қарз] бароятон беҳтар аст, агар [савоби ин корро] бидонед');
INSERT INTO "pioneers" VALUES(2,281,'2:281','Ва аз [азоби] рӯзе битарсед, ки дар он ба сӯйи Аллоҳ таоло бозгардонда мешавед; сипас ба ҳар кас [подоши] он чи анҷом додааст, ба таври комил дода мешавад ва ба онон ситам нахоҳад шуд');
INSERT INTO "pioneers" VALUES(2,282,'2:282','Эй касоне, ки имон овардаед, агар ба ҳамдигар воме [пардохтед ё муомилаи насияе анҷом] додед, ки саррасиди муайяне дошт, онро бинависед; ва бояд нависанда [қарордоди ин корро] бар асоси адолат байни шумо бинависад ва ҳеҷ нависандае набояд аз навиштанаш худдорӣ кунад, ҳамон гуна [ва ба шукронаи он], ки Аллоҳ таоло ба ӯ омӯзиш додааст; ва касе, ки вом бар уҳдаи ӯст [қарздор] бояд имло кунад ва ӯ [нависанда] бинависад; ва бояд аз Аллоҳ таоло, ки Парвардигори ӯст, бипарҳезад ва аз [маблағ ва муддати] он [вом] чизе накоҳад. Агар касе, ки вом бар уҳдаи ӯст, сафеҳ [кӯдак ё девона] ё нотавон аст ё худ наметавонад имло кунад, бояд сарпарасти ӯ бо [риояти] адолат имло кунад ва ду шоҳид аз мардонатонро [бар он қарордод] гувоҳ бигиред. Агар ду мард набошад, як марду ду зан аз миёни гувоҳоне, ки [ба адолати онон] ризоият доред [гувоҳ бигиред], то агар яке аз он ду [зан] фаромӯш кард, дигаре ба ӯ ёдоварӣ кунад; ва ҳангоме ки гувоҳон [барои адои шаҳодат] даъват шаванд, набояд [аз омадан] худдорӣ кунанд. Ва аз навиштани [қарз] то саррасидаш [озурда ва] дилгир нашавед, хоҳ [маблағаш] кам бошад хоҳ зиёд. Ин [кор] дар назди Аллоҳ таоло ба адолат наздиктар ва барои гувоҳӣ додан устувортар ва барои он, ки [дар миқдор ва муддати вом] дучори тардид нашавед, [ба адолат] наздиктар аст; магар ин ки доду ситади нақдӣ бошад, ки байни худ даст ба даст мекунед. Дар ин сурат гуноҳе бар шумо нест, ки онро нанависед; ва ҳангоме ки доду ситад [-и нақдӣ] кардед, гувоҳ бигиред ва [дар ин миён] набояд ба нависанда ва гувоҳ [ҳеҷ] ранҷу зиёне бирасад; ва агар [чунин] кунед; бе шак [баёнгари] нофармонии шумо хоҳад буд; ва [эй муъминон], аз Аллоҳ таоло парво кунед [ва муомилотро ба ҳамон шакле, ки амр мекунад, анҷом диҳед]. Аллоҳ таоло [он чиро, ки барои дунё ва охирататон судманд аст] ба шумо омӯзиш медиҳад ва Аллоҳ таоло ба ҳар чиз доност');
INSERT INTO "pioneers" VALUES(2,283,'2:283','Агар дар сафар будед ва нависандае наёфтед [ки санади вомро бинависад, чизе] гарав бигиред ва агар яке аз шумо дигареро амин донист [ва чизе гарав нагирифт ва навиштаву гувоҳе низ надошт], он ки амин шумурда шудааст, бояд амонат [ва қарзи] худро бозпас диҳад ва бояд аз Аллоҳ таоло, ки Парвардигори ӯст, парво кунад; ва шаҳодатро китмон накунед ва [бидонед, ки] ҳар касе онро китмон кунад, бе шак, қалбаш гунаҳкор аст; ва Аллоҳ таоло ба он чи мекунед, доност');
INSERT INTO "pioneers" VALUES(2,284,'2:284','Он чи дар осмонҳо ва замин аст, аз они Аллоҳ таоло аст. Агар он чиро дар дили худ доред, ошкор кунед ё пинҳонаш намоед, Аллоҳ таоло шуморо бар [асоси] он муҳосиба [бозпурсӣ] мекунад; пас, [бар асоси бахшиш ва раҳмати хеш] ҳар киро бихоҳад, мебахшад ва [аз рӯйи адолат ва ҳикматаш низ] ҳар киро бихоҳад, азоб мекунад; ва Аллоҳ таоло бар [анҷоми] ҳар коре тавоност');
INSERT INTO "pioneers" VALUES(2,285,'2:285','Расул [-и Аллоҳ таоло] ба он чи аз [сӯйи] Парвардигораш бар ӯ нозил шуда, имон овардааст ва муъминон [низ] ҳамагӣ ба Аллоҳ таоло ва фариштагону китобҳо ва паёмбаронаш имон овардаанд; [ва суханашон ин аст, ки]: "Миёни ҳеҷ як аз паёмбаронаш [дар имон ба эшон] тафовуте намегузорем" ва гуфтанд: "Шунидем ва итоат кардем. Парвардигоро, омурзиши Туро [хоҳонем] ва бозгашти [тамоми умур] ба сӯйи Туст"');
INSERT INTO "pioneers" VALUES(2,286,'2:286','Аллоҳ таоло ҳеҷ касро ҷуз ба андозаи тавонаш таклиф [уҳдадор] намекунад. Он чи [аз хубӣ] ба даст овардааст ба суди ӯст ва он чи [аз бадӣ] касб кардааст, ба зиёни ӯст. [Паёмбарон ва муъминон гуфтанд:] "Парвардигоро, агар фаромӯш ё хато кардем, моро бозхост макун. Парвардигоро, бори гарон [ва таклифи сангин] бар [дӯши] мо магзор; чунон ки онро [ба муҷозоти гуноҳ ва саркашӣ] бар [дӯши] касоне, ки пеш аз мо буданд [яҳудиён] ниҳодӣ. Парвардигоро, он чи, ки тоби таҳаммулашро надорем, бар [дӯши] мо магзор ва моро биёмурз ва ба мо раҳм кун. Ту ёр [-у корсози] моӣ; пас, моро бар гурӯҳи кофирон пирӯз гардон"');
INSERT INTO "pioneers" VALUES(3,1,'3:1','АЛМ [Алиф, Лом, Мим]');
INSERT INTO "pioneers" VALUES(3,2,'3:2','Аллоҳ таоло [маъбуди ростин] аст, ки ҳеҷ маъбуде [ба ҳақ] ҷуз Ӯ нест; зиндаи поянда [ва қоим ба зот] аст');
INSERT INTO "pioneers" VALUES(3,3,'3:3','Ин китоб [Қуръон]-ро, ки таъйидкунандаи китобҳои [осмонии] пешин аст, ба дурустӣ бар ту нозил кард ва Тавроту Инҷилро...');
INSERT INTO "pioneers" VALUES(3,4,'3:4','Пеш аз он [Тавроту Инҷилро] барои ҳидояти мардум нозил кард; ва [низ] Фурқонро [Қуръон, ки ҷудокунандаи ҳақ аз ботил аст] нозил намуд. Касоне, ки ба оёти Аллоҳ таоло кофир шуданд, азоби сахте [дар пеш] доранд; ва Аллоҳ таоло шикастнопазир [-у] интиқомгиранда аст');
INSERT INTO "pioneers" VALUES(3,5,'3:5','Бе тардид, ҳеҷ чиз - [на] дар замин ва на дар осмон – аз Аллоҳ таоло пӯшида намемонад');
INSERT INTO "pioneers" VALUES(3,6,'3:6','Ӯст, ки шуморо ҳар гуна ки бихоҳад дар раҳимҳо шакл медиҳад. Ҳеҷ маъбуде [ба ҳақ] ҷуз Ӯ шикастнопазиру ҳаким нест');
INSERT INTO "pioneers" VALUES(3,7,'3:7','[Эй паёмбар] Ӯст, ки китоб [-и Қуръон]-ро бар ту нозил кард. Бахше аз он оёти муҳкам [сареҳу равшан] аст, ки онҳо асоси китобанду [бахши] дигар муташобеҳот аст [ки таъвилпазиранд]; аммо касоне, ки дар дилҳояшон инҳироф аст, барои фитнаҷӯӣ [ва гумроҳ кардани мардум] ва ба хотири таъвили он [ба дилхоҳи худ], аз муташобеҳи он пайравӣ мекунанд; ҳол он ки таъвилашро [касе] ҷуз Аллоҳ таоло намедонад; ва росихони дар илм [уламои дин] мегӯянд: "Мо ба ҳамаи он [оёт, чи муҳкаму чи муташобеҳ] имон овардем; ҳама аз тарафи Парвардигори мост"; ва ҷуз хирадмандон касе панд намегирад');
INSERT INTO "pioneers" VALUES(3,8,'3:8','Онон мегӯянд: "Парвардигоро, дилҳои моро – баъд аз он, ки ҳидоятамон кардӣ – [аз роҳи ҳақ] мунҳариф магардон ва аз сӯйи худ раҳмате ба мо арзонӣ дор, [ки] бе тардид, Ту бисёр бахшандаӣ');
INSERT INTO "pioneers" VALUES(3,9,'3:9','Парвардигоро, бешак, Ту мардумро барои рӯзе, ки дар [омадани] он тардид нест, ҷамъ хоҳӣ кард. Ҳатман, Аллоҳ таоло дар ваъдаи [худ] хилоф намекунад"');
INSERT INTO "pioneers" VALUES(3,10,'3:10','Дар ҳақиқат, касоне, ки куфр варзиданд, амволу фарзандонашон чизе аз [азоби] Аллоҳро аз онон дур нахоҳад кард ва онон худ ҳезуми оташ [-и дӯзах] ҳастанд');
INSERT INTO "pioneers" VALUES(3,11,'3:11','[Шеваи ин кофирон] Ҳамчун шеваи хонадони Фиръавн ва [равиши] касонест, ки пеш аз онҳо буданд: Оёти моро такзиб карданд; пас, Аллоҳ таоло ононро ба [сазои] гуноҳонашон азоб кард; ва Аллоҳ таоло [нисбат ба кофирон] сахткайфар аст');
INSERT INTO "pioneers" VALUES(3,12,'3:12','Ба касоне, ки куфр варзиданд, бигӯ: "Ба зудӣ [аз муъминон] шикаст хоҳед хӯрд ва [он гоҳ дар қиёмат] ба сӯйи ҷаҳаннам маҳшур мешавед; ва чи бад ҷойгоҳе аст"!');
INSERT INTO "pioneers" VALUES(3,13,'3:13','Ҳатман, дар [рӯёрӯйи] ду гурӯҳе, ки [дар Бадр] бо ҳам рӯбарӯ шуданд, нишона [ва ибрат]-е барои шумо буд; як гурӯҳ дар роҳи Аллоҳ таоло меҷангид ва [гурӯҳи] дигар кофирон буданд. Онҳо ба чашми худ [муъминонро] ду баробари он чи буданд, медиданд; ва Аллоҳ таоло ҳар киро бихоҳад, бо ёрии худ таъйид мекунад. Ба ростӣ, дар ин [моҷаро] барои аҳли биниш, ибрате аст');
INSERT INTO "pioneers" VALUES(3,14,'3:14','Ишқ ба хостаниҳо [-и моддӣ], аз [ҷумла] занону фарзандон ва амволи ҳангуфт – аз тилло ва нуқра ва аспҳои нишондор ва домҳову киштзор [ҳо] – барои мардум ороста шудааст. Ин [ҳама, лаззатҳо ва] колои [зудгузари] зиндагии дунёст ва [ҳол он ки] саранҷоми нек назди Аллоҳ таоло аст');
INSERT INTO "pioneers" VALUES(3,15,'3:15','[Эй паёмбар, ба онон] Бигӯ: "Оё [мехоҳед] шуморо аз чизе огоҳ кунам, ки аз ин [дороиҳо ва лаззатҳои моддӣ] беҳтар бошад"? Касоне, ки тақво пеша кардаанд, назди Парвардигорашон боғҳое доранд, ки аз зери [дарахтони] он ҷӯйборҳо равон аст; ҷовидона дар он хоҳанд буд ва ҳамсарони покиза [ва беайб доранд] ва [ҳамчунин] аз хушнудии Аллоҳ таоло [бархӯрдоранд]; ва Аллоҳ таоло ба [аҳволи] бандагон биност');
INSERT INTO "pioneers" VALUES(3,16,'3:16','[Ин биҳиштиён] Касоне [ҳастанд], ки мегӯянд: "Парвардигоро, ба ростӣ, ки мо имон овардаем; пас, гуноҳонамонро биёмурз ва моро аз азоби оташ нигаҳ дор"');
INSERT INTO "pioneers" VALUES(3,17,'3:17','Онон [дар баробари мушкилот ва дар роҳи итоат ва тарки гуноҳ] шикебоӣ меварзанд ва [дар гуфтору рафторашон] ростгӯ ҳастанд ва [дар баробари фармудаҳои илоҳӣ] фармонбардоранд ва [барои хушнудии Бори таоло] инфоқ мекунанд ва дар саҳаргоҳон омурзиш металабанд');
INSERT INTO "pioneers" VALUES(3,18,'3:18','Аллоҳ таоло, ки [дар ҷаҳони ҳастӣ] барподорандаи адл аст, гувоҳӣ додааст, ки маъбуде [ба ҳақ] ҷуз Ӯ нест ва фариштагону уламо [низ бар ягонагӣ ва адолати илоҳӣ гувоҳӣ медиҳанд]. Маъбуде [ба ҳақ] ҷуз Ӯ нест, [ки] шикастнопазиру ҳаким аст');
INSERT INTO "pioneers" VALUES(3,19,'3:19','Дар ҳақиқат, дини [писандида] назди Аллоҳ таоло ҳамон ислом аст; ва касоне, ки китоби [осмонӣ] ба онон дода шудааст, бо якдигар ба ихтилоф напардохтанд, магар пас аз он, ки илм барояшон [ҳосил] омад [ва ҳуҷҷат барояшон тамом шуд]. Он [ихтилоф ва тафриқа] ҳам ба хотири саркашӣ ва ҳасаде [буд], ки миёнашон вуҷуд дошт; ва ҳар ки ба оёти Аллоҳ таоло куфр биварзад, [бидонад, ки] Аллоҳ таоло дар ҳисобрасӣ сареъ аст');
INSERT INTO "pioneers" VALUES(3,20,'3:20','[Эй паёмбар] Агар [дар бораи ислом ва тавҳид] бо ту муҷодала карданд, бигӯ: "Ман дар баробари Аллоҳ таоло [ва авомиру аҳкомаш] таслим шудаам ва пайравонам [низ чунин ҳастанд]"; ва ба аҳли китоб [яҳудиён ва масеҳиён] ва мушрикон бигӯ: "Оё шумо ҳам таслим шудаед"? Агар таслим шаванд, ҳатман ҳидоят ёфтаанд ва агар рӯйгардон шуданд [ва сарпечӣ карданд, ғамгин машав, зеро] ту вазифае ҷуз расондан [-и ваҳй] надорӣ; ва Аллоҳ таоло ба [аҳволи] бандагон биност');
INSERT INTO "pioneers" VALUES(3,21,'3:21','Ба ростӣ, касоне, ки ба оёти Аллоҳ таоло куфр меварзанд ва паёмбаронро ба ноҳақ [ва фақат аз рӯйи ситаму душманӣ] мекушанд ва [низ] касонеро, ки ба адолат амр мекунанд, ба қатл мерасонанд, ононро ба азоби дарднок башорат деҳ!');
INSERT INTO "pioneers" VALUES(3,22,'3:22','Онон касоне ҳастанд, ки аъмолашон дар дунё ва охират табоҳ шудааст ва [дар охират] ҳеҷ ёваре надоранд');
INSERT INTO "pioneers" VALUES(3,23,'3:23','[Эй паёмбар] Оё достони касонеро, ки баҳрае аз китоб [-и Таврот] ёфтаанд, надонистаӣ, ки чун ба [бозгашт ба] сӯйи китоби Аллоҳ таоло фаро хонда мешаванд, то миёнашон ҳукм кунад, гурӯҳе аз [уламову бузургони] онон [ки ҳақиқатро хилофи майлу манфиати худ мебинанд] ба ҳоли сарпечӣ рӯ барметобанд?');
INSERT INTO "pioneers" VALUES(3,24,'3:24','Ин [рӯйгардонӣ аз ҳақ], бад-он сабаб аст, ки гуфтанд: "Оташ [-и дӯзах] ҷуз чанд рӯзе ба мо нахоҳад расид"; ва ин [ифтироҳо] ва дурӯғҳое, ки мебофтанд, ононро дар динашон фирефта сохт');
INSERT INTO "pioneers" VALUES(3,25,'3:25','Пас, [ҳоли пушаймониашон] чи гуна хоҳад буд? [Оре, дар бадтарин ҳол хоҳанд буд] Он гоҳ ки ононро барои рӯзе, ки [ҳеҷ] тардиде дар он нест, гирд оварем ва ба ҳар кас [подоши] он чи, ки фароҳам кардааст, ба тамом ва камол дода шавад ва ба эшон ситам нахоҳад шуд');
INSERT INTO "pioneers" VALUES(3,26,'3:26','[Эй паёмбар] Бигӯ: "Бор Илоҳо, эй Фармонравои ҳастӣ, ҳар киро бихоҳӣ, ҳукумат мебахшӣ ва аз ҳар ки бихоҳӣ, ҳукуматро бозпас мегирӣ; ва ба ҳар ки бихоҳӣ, иззат [-у қудрат] медиҳӣ ва ҳар киро бихоҳӣ, хор медорӣ. [Парвардигоро] Хубӣ ба дасти Туст ва бе гумон, Ту бар ҳар чизе тавоноӣ');
INSERT INTO "pioneers" VALUES(3,27,'3:27','Шабро ба рӯз дармеоварӣ ва рӯзро ба шаб дармеоварӣ [ва аз тӯли яке мекоҳӣ ва бар дигаре меафзоӣ] ва [мавҷуди] зиндаро аз [моддаи] беҷон берун меоварӣ [рӯйиши гиёҳ аз дона] ва [моддаи] беҷонро аз [мавҷуди] зинда хориҷ месозӣ [берун омадани тухм аз паррандагон] ва ба ҳар ки бихоҳӣ, бешумор рӯзӣ медиҳӣ"');
INSERT INTO "pioneers" VALUES(3,28,'3:28','Муъминон набояд кофиронро ба ҷойи муъминон дӯсти худ бигиранд; ва ҳар ки чунин кунад, бо Аллоҳ таоло ҳеҷ пайванде надорад ва [Аллоҳ таоло аз ӯ безорӣ меҷӯяд]; магар он ки [ночор шавед бо нармӣ дар гуфтору рафтор] хешро аз [озори] эшон дар амон доред [ва ба хотири ҳифзи ҷонатон эҳтиёт кунед]; ва Аллоҳ таоло шуморо аз [кайфари] хеш барҳазар медорад; ва бозгашт [-и ҳама] ба сӯйи Аллоҳ таоло аст');
INSERT INTO "pioneers" VALUES(3,29,'3:29','[Эй паёмбар] Бигӯ: "Агар ҳар он чи [аз дӯстӣ ва муҳаббати кофирон, ки] дар дилҳоятон аст, пинҳон доред, ё ошкораш кунед, Аллоҳ таоло онро медонад, [ва низ] он чиро, ки дар осмонҳо ва замин аст, медонад; ва Аллоҳ таоло бар ҳар чизе тавоност"');
INSERT INTO "pioneers" VALUES(3,30,'3:30','[Рӯзи қиёмат] Рӯзе [аст], ки ҳар кас он чиро аз кори нек [ва бад] анҷом додааст, ҳозир мебинад ва орзу мекунад миёни ӯ ва аъмоли баде, ки анҷом додааст, фосилаи бисёре бошад; ва Аллоҳ таоло шуморо аз [нофармонӣ ва кайфари] худ барҳазар медорад; ва Аллоҳ таоло нисбат ба бандагон [-и худ] дилсӯз аст');
INSERT INTO "pioneers" VALUES(3,31,'3:31','[Эй паёмбар] Бигӯ: "Агар Аллоҳро дӯст медоред, аз ман пайравӣ кунед, то Аллоҳ таоло шуморо дӯст бидорад ва гуноҳонатонро биёмурзад; ва Аллоҳ таоло омурзандаи меҳрубон аст"');
INSERT INTO "pioneers" VALUES(3,32,'3:32','[Эй паёмбар] Бигӯ: "Аллоҳ таоло ва паёмбар [-и Ӯ]-ро итоат кунед"; пас, агар пушт карданд [ва сарпечӣ намуданд], ҳатман, Аллоҳ таоло кофиронро дӯст надорад');
INSERT INTO "pioneers" VALUES(3,33,'3:33','Ба ростӣ, ки Аллоҳ таоло Одаму Нуҳ ва хонадони Иброҳиму хонадони Имронро бар ҷаҳониён [бартарӣ дод ва] баргузид');
INSERT INTO "pioneers" VALUES(3,34,'3:34','Фарзандоне, ки бархе аз онон [дар тавҳиду ибодат] аз [пайи] бархи дигаранд; ва Аллоҳ таоло шунавову доност');
INSERT INTO "pioneers" VALUES(3,35,'3:35','[Эй паёмбар, ёд кун аз] Ҳангоме ки ҳамсари Имрон гуфт: "Парвардигоро, ман он чиро дар шикам дорам, барои Ту назр кардам, ки озод [аз ҳар чиз дар хидмати Байт-ул-муқаддас ва парастишгари Ту] бошад; пас, аз ман бипазир, ки Ту худ шунавову доноӣ"');
INSERT INTO "pioneers" VALUES(3,36,'3:36','Пас, ҳангоме ки ӯро бизод, гуфт: "Парвардигоро, ман ӯро духтар зодаам – вале Аллоҳ таоло ба он чи ӯ ба дунё оварда буд, донотар буд – ва писар ҳаммонанди духтар нест ва ман ӯро Марям ном ниҳодам ва ӯву фарзандонашро аз [шарри] шайтони рондашуда дар паноҳи Ту қарор медиҳам"');
INSERT INTO "pioneers" VALUES(3,37,'3:37','Он гоҳ Парвардигораш ӯ [Марям]-ро ба накуӣ пазируфт ва [ниҳоли вуҷудашро] ба шеваи шоистае парвариш дод ва сарпарастиашро ба Закариё супурд. Ҳар гоҳ Закариё вориди ибодатгоҳ [-и ӯ] мешуд, наздаш ғизое меёфт [пас, бо шигифтӣ] мегуфт: "Эй Марям, ин [ғизо] аз куҷо бароят омадааст"? [Марям] Мегуфт: "Ин [ғизо] аз ҷониби Аллоҳ таоло аст. Бе гумон, Аллоҳ таоло ба ҳар ки бихоҳад, бешумор рӯзӣ медиҳад"');
INSERT INTO "pioneers" VALUES(3,38,'3:38','Он ҷо [буд, ки] Закариё Парвардигорашро хонд [ва] гуфт: "Парвардигоро, аз ҷониби хеш фарзанди поку писандида ба ман ато кун, [ки] бе тардид, Ту шунавандаи дуоӣ"');
INSERT INTO "pioneers" VALUES(3,39,'3:39','Пас, дар ҳоле ки ӯ [Закариё] истода дар ибодатгоҳ намоз мехонд, фариштагон нидояш доданд, ки: "Аллоҳ таоло туро ба [вилодати] Яҳё башорат медиҳад, [ки] тасдиқкунандаи калимаи Аллоҳ таоло [Исо] аст ва [дар илму ибодат, бар қавми хеш] сарвар аст ва порсост [ба шаҳавот таваҷҷуҳе надорад] ва паёмбаре аз шоистагон аст"');
INSERT INTO "pioneers" VALUES(3,40,'3:40','[Закариё] Гуфт: "Парвардигоро, чи гуна маро фарзанде хоҳад буд, ҳол он ки пирӣ ба суроғам омадаву ҳамсарам нозост"? [Аллоҳ таоло] Гуфт: "[амри офариниш] Чунин аст, [ки] Аллоҳ таоло ҳар чи бихоҳад анҷом медиҳад"');
INSERT INTO "pioneers" VALUES(3,41,'3:41','[Закариё] Гуфт: "Парвардигоро, бароям нишонае қарор бидеҳ"; [Аллоҳ таоло] Фармуд: "Нишонаат ин аст, ки се [шабона] рӯз [бидуни ин ки бемор бошӣ] ҷуз бо ишора қодир ба сухан гуфтан бо мардум нестӣ [пас, сабур бош] ва Парвардигоратро бисёр ёд кун ва шомгоҳону субҳгоҳон [Ӯро] тасбеҳ бигӯ"');
INSERT INTO "pioneers" VALUES(3,42,'3:42','Ва [Эй паёмбар, ёд кун аз] ҳангоме ки фариштагон гуфтанд: "Эй Марям, Аллоҳ таоло туро баргузида ва пок сохта ва туро [дар асри худ] бар занони ҷаҳон бартарӣ додааст"');
INSERT INTO "pioneers" VALUES(3,43,'3:43','Эй Марям, [ҳамвора] ибодатгари Парвардигори хеш бошу [ва барои Ӯ] саҷда бигзор ва ҳамроҳи рукуъкунандагон рукуъ кун"');
INSERT INTO "pioneers" VALUES(3,44,'3:44','[Эй паёмбар] Ин [хабар] аз хабарҳои ғайбест, ки ба ту ваҳй мекунем ва ту ҳангоме ки [уламои мазҳаби яҳуд] қаламҳои худро [барои қуръакашӣ ба об] меафканданд, то [таъйин кунанд, ки] кадом як сарпарастии Марямро ба уҳда гирад, наздашон набудӣ; ва [низ] вақте ки [ба сари ин мавзӯъ] бо якдигар кашмакаш мекарданд, канорашон ҳузур надоштӣ');
INSERT INTO "pioneers" VALUES(3,45,'3:45','Ва [Эй паёмбар, ёд кун аз] ҳангоме ки фариштагон гуфтанд: "Эй Марям, Аллоҳ таоло туро ба калимае аз ҷониби хеш башорат медиҳад, ки номаш Масеҳ – Исо ибни Марям – аст; дар ҳоле ки [ӯ] дар дунё ва охират обрӯманд ва аз наздикони [даргоҳи илоҳӣ] аст"');
INSERT INTO "pioneers" VALUES(3,46,'3:46','Ӯ дар гаҳвора [ба эъҷоз] ва дар кӯҳансолӣ [ба ваҳйи илоҳӣ] бо мардум сухан мегӯяд ва аз шоистагон аст"');
INSERT INTO "pioneers" VALUES(3,47,'3:47','[Марям] Гуфт: "Парвардигоро, чи гуна фарзанде хоҳам дошт, дар ҳоле [ҳеҷ] башаре ба ман даст назадааст"? [Фаришта] Гуфт: "Аллоҳ таоло бад-ин сон ҳар чиро бихоҳад, меофаринад, [ва] ҳар гоҳ, ки иродаи чизе кунад, фақат ба он мегӯяд: "Бош", пас, он [бедиранг] мавҷуд мешавад"');
INSERT INTO "pioneers" VALUES(3,48,'3:48','Ва [низ башорат дод, ки Аллоҳ таоло] ба ӯ китобу ҳикмат ва Тавроту Инҷил меомӯзад');
INSERT INTO "pioneers" VALUES(3,49,'3:49','Ва паёмбаре ба сӯйи Бани Исроил аст, ки [ба онон мегӯяд]: "Ман аз сӯйи Парвардигоратон барои шумо нишонае овардаам: Ман аз гил бароятон [чизе] ба шакли парранда месозам; он гоҳ дар он медамам ва ба фармони Аллоҳ таоло [он гили беҷон табдил ба] паррандае мешавад; ва [ҳамчунин] ба фармони Аллоҳ таоло кӯри модарзод ва мубталоён ба песиро беҳбудӣ мебахшам ва мурдагонро зинда мекунам ва аз он чи мехӯред ва дар хонаҳоятон захира мекунед, ба шумо хабар медиҳам. Мусалламан, агар имон дошта бошед, дар инҳо нишонае барои шумост [ки нубувватамро бовар кунед]');
INSERT INTO "pioneers" VALUES(3,50,'3:50','Ва [мегӯяд: "Омадаам то] тасдиқкунандаи Таврот бошам, ки пеш аз ман [нозил шуда] аст; ва [фиристода шудаам] то бархе аз чизҳоеро, ки бар шумо ҳаром шуда буд, ҳалол кунам; ва нишонае аз тарафи Парвардигоратон барои шумо овардаам; пас, аз Аллоҳ таоло парво (ва тақво) дошта бошед ва маро итоат кунед');
INSERT INTO "pioneers" VALUES(3,51,'3:51','Ба ростӣ, ки Аллоҳ таоло, Парвардигори ману Парвардигори шумост; пас, Ӯро бипарастед, [ки] роҳи рост ин аст"');
INSERT INTO "pioneers" VALUES(3,52,'3:52','Сипас чун Исо ба куфри онон пай бурд, гуфт: "Чи касоне ёварони ман [дар роҳи даъват] ба сӯйи Аллоҳ таоло ҳастанд"? Ҳавориюн гуфтанд: "Мо ёварони [дини] Аллоҳ таоло ҳастем; ба Аллоҳ таоло имон овардаем ва [ту низ, эй Исо] гувоҳ бош, ки мо таслим [-и Ӯ] ҳастем');
INSERT INTO "pioneers" VALUES(3,53,'3:53','Парвардигоро, ба он чи нозил кардаӣ, имон овардем ва аз фиристода [-и Ту] пайравӣ намудем; пас, моро дар зумраи гувоҳон [ба ҳақ ва муъминон ба паёмбарат] бинавис"');
INSERT INTO "pioneers" VALUES(3,54,'3:54','[Пас аз муддате кофирони Бани Исроил барои куштани Исо] Макр варзиданд ва Аллоҳ таоло [низ дар посухашон] макр намуд ва Аллоҳ таоло бехтарин макрангезон аст');
INSERT INTO "pioneers" VALUES(3,55,'3:55','[Ёд кун аз] Ҳангоме ки Аллоҳ таоло [ба Исо] фармуд: "Эй Исо, ман туро бармегирам ва ба сӯйи хеш боло мебарам ва туро аз [олоишу туҳмати] касоне, ки куфр варзиданд, пок мегардонам ва то рӯзи растохез касонеро, ки аз ту пайравӣ карданд [ва ба Муҳаммад имон оварданд], бартар аз касоне қарор медиҳам, ки куфр варзиданд. Сипас бозгаштатон ба сӯйи ман аст; он гоҳ дар [мавриди] он чи дар он ихтилоф мекардед, миёнатон доварӣ мекунам');
INSERT INTO "pioneers" VALUES(3,56,'3:56','Аммо касоне, ки куфр варзиданд, дар дунё ва охират ба азоби сахт муҷозоташон мекунам ва ёвароне нахоҳанд дошт');
INSERT INTO "pioneers" VALUES(3,57,'3:57','Ва аммо касоне, ки имон оварданд ва корҳои шоиста анҷом доданд, [Аллоҳ таоло] подошашонро ба таври комил ба эшон хоҳад дод; ва Аллоҳ таоло ситамгоронро дӯст намедорад');
INSERT INTO "pioneers" VALUES(3,58,'3:58','[Эй паёмбар] Инҳо, ки [аз моҷарои Исо] бар ту мехонем, аз нишонаҳо [-и равшан аст далолаткунанда бар ростгӯии ту ва дурустии ин] андарзи ҳикматомез [Қуръон]');
INSERT INTO "pioneers" VALUES(3,59,'3:59','Дар ҳақиқат, масали Исо назди Аллоҳ таоло ҳамчун масали [офариниши] Одам аст, [ки] ӯро аз хок офариду сипас ба ӯ фармуд: "Бош"; пас, [бедиранг] мавҷуд шуд');
INSERT INTO "pioneers" VALUES(3,60,'3:60','[Он чи дар бораи Исо баён кардем] Ҳақиқате аз ҷониби Парвардигори туст; пас, ҳаргиз аз тардидкунандагон мабош');
INSERT INTO "pioneers" VALUES(3,61,'3:61','Пас, [эй паёмбар] ҳар ки [аз масеҳиёни Наҷрон] пас аз он донише, ки дар бораи ӯ [Исо] бароят [ҳосил] омадааст, бо ту баҳсу ҷадал кунад, [ба онҳо] бигӯ: "Биёед [ҳамагиро] фаро бихонем: писарони мову писарони шумо ва занони мову занони шумо ва худи мову шумо [низ ҳозир шавем]; сипас ба [даргоҳи илоҳӣ тазарруъ ва] дуо кунем ва лаънати Аллоҳро бар дурӯғгӯён қарор диҳем"');
INSERT INTO "pioneers" VALUES(3,62,'3:62','Ин ҳамон достони ҳақиқӣ [-и Масеҳ] аст ва ҳеҷ маъбуде [ба ҳақ] ҷуз Аллоҳ таоло нест ва бе гумон, Аллоҳ таоло аст, ки шикастнопазир [ва] ҳаким аст');
INSERT INTO "pioneers" VALUES(3,63,'3:63','Пас, агар [аз сухани ҳақ] рӯй гардонанд, [бидон, ки] Аллоҳ таоло аз [ҳоли] муфсидон огоҳ аст');
INSERT INTO "pioneers" VALUES(3,64,'3:64','[Эй паёмбар] Бигӯ: "Эй аҳли китоб, ба сӯйи сухане биёед, ки миёни мо ва шумо одилона аст [ва инҳирофе аз ҳақ дар он нест]: ки ҷуз Аллоҳро напарастем ва чизеро [дар қудрату тадбир] бо Ӯ шарик насозем ва бархе аз мо бархи дигарро ба ҷой Аллоҳ таоло [ба парастиш] нагиранд". Агар онон [аз ин даъват] рӯй гардонанд, пас, [эй муъминон, ба эшон] бигӯед: "Гувоҳ бошед, ки мо таслим [-и авомири илоҳӣ] ҳастем"');
INSERT INTO "pioneers" VALUES(3,65,'3:65','Эй аҳли китоб, чаро дар бораи Иброҳим муҷодила мекунед; дар ҳоле ки Таврот ва Инҷил баъд аз ӯ нозил шудааст? Оё намеандешед?');
INSERT INTO "pioneers" VALUES(3,66,'3:66','Ҳон! Шумо [аҳли китоб] касоне ҳастед, ки дар бораи он чи нисбат ба он огоҳӣ ва илм доштед, баҳсу ситез мекардед; пас, чаро [инак] дар мавриди чизе, ки бад-он илм надоред [ва дар китобҳоятон наёмадааст], баҳсу ситез мекунед? Дар ҳоле ки Аллоҳ таоло [ҳақоиқи умурро] медонад ва шумо намедонед');
INSERT INTO "pioneers" VALUES(3,67,'3:67','Иброҳим на яҳудӣ буд ва на масеҳӣ; балки ҳақгарои мусулмон буд ва ҳаргиз дар зумраи мушрикон набуд');
INSERT INTO "pioneers" VALUES(3,68,'3:68','Ба ростӣ, сазовортарин мардум ба Иброҳим ҳамон касоне ҳастанд, ки аз ӯ пайравӣ кардаанд; ва [аз ҷумла] ин паёмбар [-и шумо-Муҳаммад] ва касоне, ки [мусулмонанд ва ба ӯ] имон овардаанд; ва Аллоҳ таоло [ёру] корсози муъминон аст');
INSERT INTO "pioneers" VALUES(3,69,'3:69','[Эй муъминон] Гурӯҳе аз аҳли китоб орзу мекарданд, кош шуморо гумроҳ мекарданд; дар ҳоле ки ҷуз худашон [касе]–ро гумроҳ намекунанд ва [аммо] намефаҳманд');
INSERT INTO "pioneers" VALUES(3,70,'3:70','Эй аҳли китоб, чаро ба оёти Аллоҳ таоло кофир мешавед, дар ҳоле ки худ [ба дурустии он] гувоҳӣ медиҳед?');
INSERT INTO "pioneers" VALUES(3,71,'3:71','Эй аҳли китоб, чаро ҳақро ба ботил меомезед ва ҳақиқат [-и паёмбарии Муҳаммад]-ро пинҳон мекунед, дар ҳоле ки худ медонед?');
INSERT INTO "pioneers" VALUES(3,72,'3:72','Гурӯҳе аз [уламои] аҳли китоб [ба ҳамдигар] гуфтанд: "Дар оғози рӯз ба он чи ба муъминон нозил шудааст, имон биёваред ва дар поён [-и рӯз] кофир шавед; шояд онон [низ тардид кунанд ва аз ислом] баргарданд');
INSERT INTO "pioneers" VALUES(3,73,'3:73','Ва [ин ки] имон наёваред магар ба касе, ки аз дини шумо пайравӣ мекунад". [Эй паёмбар, ба онон] Бигӯ: "Дар ҳақиқат, ҳидоят ҳидояти Аллоҳ таоло аст. [Боз гуфтанд: "Ҳаргиз чизе ба мусулмонон наёмӯзед ва иҷозат надиҳед] ба каси он чи ба шумо додааст, дода шавад ё [далеле дар ихтиёрашон нагузоред, ки битвонанд] дар пешгоҳи Парвардигоратон бо шумо баҳс кунанд". [Эй паёмбар] Бигӯ: "Бе тардид, фазл [-у бартарӣ] ба дасти Аллоҳ таоло аст; онро ба ҳар ки бихоҳад, мебахшад; ва Аллоҳ таоло кушоишгари доност');
INSERT INTO "pioneers" VALUES(3,74,'3:74','Раҳмати хешро ба ҳар ки бихоҳад, ихтисос медиҳад; ва Аллоҳ таоло дорои бахшиши бузург [ва беандоза] аст"');
INSERT INTO "pioneers" VALUES(3,75,'3:75','Аз аҳли китоб касе ҳаст, ки агар моли фаровоне ба ӯ амонат диҳӣ, онро ба ту бозмегардонад; ва аз [миёни] онон касе ҳаст, ки агар ӯро бар диноре амин бишуморӣ, онро ба ту бознамегардонад; магар он ки пайваста бар [дарёфти] он истодагӣ кунӣ. Ин бад-он хотир аст, ки онон мегӯянд: "Дар мавриди [хӯрдани амволи] қавми араб, гуноҳе бар мо нест"; ва [бад-ин гуна онон] дар ҳоле ки [ҳақиқатро] медонанд, бар Аллоҳ таоло дурӯғ мебанданд');
INSERT INTO "pioneers" VALUES(3,76,'3:76','Оре, [ин кор гуноҳ аст; аммо] ҳар ки ба паймони худ вафо кунад ва парҳезгорӣ намояд, [бидонад, ки] бе тардид, Аллоҳ таоло парҳезгоронро дӯст медорад');
INSERT INTO "pioneers" VALUES(3,77,'3:77','Касоне, ки паймони Аллоҳ таоло [дар бораи иҷрои аҳком] ва савгандҳои худ [дар бораи пайравӣ аз Муҳаммад]-ро ба баҳои ночизе мефурушанд, яқинан баҳрае дар охират нахоҳанд дошт ва Аллоҳ таоло рӯзи қиёмат бо онон сухан намегӯяд ва ба эшон наменигарад ва онҳоро [аз гуноҳ] пок намесозад ва азоби дардноке [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(3,78,'3:78','Ва гурӯҳе аз онон [яҳудиён] ҳастанд, ки забони худро ба [ҳангоми тиловати] китоби [Таврот] чунон мегардонанд, ки гумон мекунед, [он чи мехонанд, воқеан,] аз [матолиби он] китоб аст, дар ҳоле ки аз он китоб нест; ва [боз ба дурӯғ] мегӯянд: "Он [ҷумалот] аз ҷониби Аллоҳ таоло аст"; дар сурате ки аз ҷониби Аллоҳ таоло нест ва бар Аллоҳ таоло дурӯғ мебанданд, дар ҳоле ки худ [низ ҳақиқатро] медонанд');
INSERT INTO "pioneers" VALUES(3,79,'3:79','Барои ҳеҷ башаре сазовор нест, ки Аллоҳ таоло ба ӯ китоб ва ҳукм [донишу фаҳм] ва паёмбарӣ диҳад, сипас ӯ ба мардум бигӯяд: "Ба ҷойи Аллоҳ таоло, бандагони ман бошед". Балки [сазовор аст, ки паёмбарон ба мардум бигӯянд]: "Ба хотири он, ки китоб [-и Таврот]-ро омӯзиш медодед ва аз он ҷо, ки [ақоиду аҳкоми онро ба дигарон] дарс медодед, [фақеҳону донишмандони] илоҳӣ бошед"');
INSERT INTO "pioneers" VALUES(3,80,'3:80','Ва на ин ки ба шумо фармон диҳад, ки фариштагон ва паёмбаронро [ба ҷойи Аллоҳ таоло] соҳибихтиёр [ба парастиш] бигиред. Оё [ин шахс] шуморо пас аз он, ки мусулмон шудед, ба куфр фармон медиҳад?');
INSERT INTO "pioneers" VALUES(3,81,'3:81','Ва [эй паёмбар, ёд кун аз] ҳангоме ки Аллоҳ таоло аз паёмбарон паймон гирифт [ва фармуд]: "Ҳар гоҳ ба шумо китобу ҳикмате додам, он гоҳ фиристодае [Муҳаммад] ба сӯятон омад, ки он чиро бо шумост, тасдиқ кард, бояд ба ӯ имон оваред ва ёриаш кунед". [Он гоҳ] Фармуд: "Оё иқрор кардед ва паймони муҳками маро дар ин бора пазируфтед"? [Онон] Гуфтанд: "Иқрор кардем". [Аллоҳ таоло] Фармуд: "Пас, [бар худу умматҳоятон] гувоҳ бошед ва ман [низ] бо шумо аз гувоҳонам"');
INSERT INTO "pioneers" VALUES(3,82,'3:82','Пас, ҳар ки баъд аз ин [паймон, аз аҳди хеш] рӯй бигардонад, ононанд, ки фосиқанд');
INSERT INTO "pioneers" VALUES(3,83,'3:83','Оё [кофирон, оине] ба ҷуз дини Аллоҳро меҷӯянд? Ҳол он ки ҳар ки [ва ҳар чи] дар осмонҳо ва замин аст – хоҳ ноҳоҳ – сар ба фармони Ӯ ниҳодааст ва [ҳамагӣ] ба сӯйи Ӯ бозгардонда мешаванд');
INSERT INTO "pioneers" VALUES(3,84,'3:84','[Эй паёмбар] Бигӯ: "Ба Аллоҳ таоло ва он чи бар мо нозил шудааст, имон овардем ва [ҳамчунин ба] он чи бар Иброҳим ва Исмоил ва Исҳоқу Яъқуб ва [паёмбароне, ки аз] наводагон [-и Яқуб буданд] нозил гардидааст ва он чи ба Мӯсо ва Исо ва [дигар] паёмбарон аз ҷониби Парвардигорашон дода шудааст; миёни ҳеҷ як аз онон фарқе намегузорем ва таслими Ӯ ҳастем"');
INSERT INTO "pioneers" VALUES(3,85,'3:85','Ҳар ки дине ғайр аз ислом баргузинад, ҳаргиз аз ӯ пазируфта нахоҳад шуд ва дар охират аз зиёнкорон аст');
INSERT INTO "pioneers" VALUES(3,86,'3:86','Чи гуна Аллоҳ таоло қавмеро ҳидоят мекунад, ки баъд аз имонашон ва [пас аз он ки] гувоҳӣ доданд ба ин ки паёмбар [-и ислом] барҳақ аст, барояшон далоили равшан омад, кофир шуданд? Ва, албатта, Аллоҳ таоло қавми ситамгорро ҳидоят намекунад');
INSERT INTO "pioneers" VALUES(3,87,'3:87','Ҷазояшон ин аст, ки лаънати Аллоҳ таоло ва фариштагону мардум ҳамагӣ бар онҳост');
INSERT INTO "pioneers" VALUES(3,88,'3:88','Дар он [лаънат ва азоби онон] ҷовидона боқӣ мемонанд ва на аз азобашон коста мешавад ва на муҳлат [-и узрхоҳӣ] меёбанд');
INSERT INTO "pioneers" VALUES(3,89,'3:89','Магар касоне, ки пас аз он тавба карданд ва [бо дурусткорӣ, рафторашонро] ислоҳ намуданд; пас, бе гумон, Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(3,90,'3:90','Ба ростӣ касоне, ки пас аз имони худ кофир шуданд ва бар куфр [-и хеш] афзуданд, ҳаргиз тавбаи онон пазируфта нахоҳад шуд ва ононанд, ки гумроҳанд');
INSERT INTO "pioneers" VALUES(3,91,'3:91','Касоне, ки куфр варзиданд ва дар ҳоли куфр аз дунё рафтанд, агарчи рӯйи замин пур аз тилло бошад ва онро ба унвони фидя [ва каффораи аъмоли бади хеш] бипардозанд, ҳаргиз аз ҳеҷ як аз онҳо пазируфта нахоҳад шуд ва муҷозоти дарднок [дар пеш] доранд ва ёвароне нахоҳанд дошт');
INSERT INTO "pioneers" VALUES(3,92,'3:92','[Эй муъминон] Ҳаргиз ба [манзалату савоби] накукорӣ даст намеёбед, магар он ки аз он чи дӯст медоред, инфоқ кунед; ва бе тардид, Аллоҳ таоло ба он чи инфоқ мекунед, доност');
INSERT INTO "pioneers" VALUES(3,93,'3:93','Ҳама ғизоҳо барои Бани Исроил ҳалол буд, магар он чи Исроил (Яъқуб) пеш аз нузули Таврот бар худ ҳаром карда буд. [Эй паёмбар, ба яҳудиён] Бигӯ: "Агар рост мегӯед [ва суханамро бовар надоред], Тавротро биёваред ва онро бихонед"');
INSERT INTO "pioneers" VALUES(3,94,'3:94','Пас, ҳар ки пас аз он бар Аллоҳ таоло дурӯғ бандад, ононанд, ки ситамгоранд');
INSERT INTO "pioneers" VALUES(3,95,'3:95','[Эй паймбар] Бигӯ: "Аллоҳ таоло [дар мавриди Яъқуб ва ҳар чи нозил фармудааст] рост гуфт; пас, аз оини Иброҳим пайравӣ кунед, ки ҳақгаро буд ва ҳаргиз дар зумраи мушрикон набуд"');
INSERT INTO "pioneers" VALUES(3,96,'3:96','Дар ҳақиқат, нахустин хонае, ки барои [ибодати] мардум қарор дода шуд, ҳамонест, ки дар Макка қарор дорад, ки пурбаракат ва [мояи] ҳидояти ҷаҳониён аст');
INSERT INTO "pioneers" VALUES(3,97,'3:97','Дар он [хона], нишонаҳои равшан, [аз ҷумла] мақоми Иброҳим аст ва [дигар ин ки] ҳар ки дохили он [ҳарам] шавад, дар амон хоҳад буд; ва бар мардум [воҷиб] аст, ки барои [ибодати] Аллоҳ таоло қасди он хона кунанд [албатта, барои] касе, ки тавоноии [молӣ ва ҷисмӣ] рафтан ба сӯйи онро дорад; ва ҳар ки [ба фаризаи ҳаҷ] куфр варзад, бе тардид, Аллоҳ таоло аз ҷаҳониён бениёз аст');
INSERT INTO "pioneers" VALUES(3,98,'3:98','[Эй паёмбар] Бигӯ: "Эй аҳли китоб, чаро ба Аллоҳ таоло куфр меварзед? Ҳол он ки Аллоҳ таоло бар он чи мекунед, гувоҳ аст"');
INSERT INTO "pioneers" VALUES(3,99,'3:99','Бигӯ: "Эй аҳли китоб, чаро касеро, ки имон овардааст, аз роҳи Аллоҳ таоло бозмедоред ва онро мунҳариф [раҳгумзадаву гумроҳшуда] мехоҳед? Дар ҳоле ки шумо [худ ба дурустии он] гувоҳед; ва Аллоҳ таоло аз он чи мекунед, ғофил нест"');
INSERT INTO "pioneers" VALUES(3,100,'3:100','Эй касоне, ки имон овардаед, агар аз гурӯҳе аз аҳли китоб итоат кунед, шуморо пас аз имонатон ба куфр бозмегардонанд');
INSERT INTO "pioneers" VALUES(3,101,'3:101','Ва чи гуна шумо кофир мешавед, дар ҳоле ки оёти Аллоҳ таоло бар шумо хонда мешавад ва паёмбараш миёни шумост? Ва ҳар ки ба [китоби] Аллоҳ таоло [ва суннати паёмбараш] тамассук ҷӯяд, ҳатман, ба роҳи рост ҳидоят шудааст');
INSERT INTO "pioneers" VALUES(3,102,'3:102','Эй касоне, ки имон овардаед, аз Аллоҳ таоло – он гуна ки шоистаи парво кардан [тарсидан] аз Ӯст – парво кунед [битарсед] ва ҷуз дар мусулмонӣ намиред');
INSERT INTO "pioneers" VALUES(3,103,'3:103','Ва ҳамагӣ ба ресмони Аллоҳ таоло [Қуръону Суннат] чанг занед ва пароканда нашавед; ва неъмати [бузурги] Аллоҳро бар худ ба ёд оред, ки чи гуна душмани якдигар будед ва ӯ миёни дилҳоятон улфат эҷод кард ва ба [лутфу баракати] неъматаш бародар шудед; ва [пеш аз он ба хотири куфратон] бар остонаи партгоҳи оташ будед, [ки] Аллоҳ таоло шуморо аз он наҷот дод. Аллоҳ таоло, инчунин, оёти худро бароятон ошкор месозад; бошад, ки ҳидоят ёбед');
INSERT INTO "pioneers" VALUES(3,104,'3:104','Ва [эй муъминон] бояд гувоҳе аз шумо бошанд, ки [мардумро] ба кор [-ҳои] шоиста [ки ақлу дин меписандад] водоранд ва аз зиштӣ боздоранд; ва ононанд, ки растагоранд');
INSERT INTO "pioneers" VALUES(3,105,'3:105','Ва монанди касоне набошед, ки пас аз он ки далоили равшан барояшон омад, пароканда шуданд ва ихтилоф карданд. Онон азоби бузург [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(3,106,'3:106','Рӯзе, ки чеҳраҳое сапед ва чеҳраҳое сиёҳ мегардад; аммо [ба] онон, ки чеҳраҳояшон сиёҳ шудааст [гуфта мешавад]: "Оё баъд аз имонатон кофир шудед? Пас, ба сабаби он чи куфр меварзидед, азобро бичашед"');
INSERT INTO "pioneers" VALUES(3,107,'3:107','Ва аммо онон, ки чеҳраҳояшон сапед гаштааст, дар раҳмати Аллоҳ таоло [дар биҳишти барин] ҷой мегиранд ва дар он ҷовидонанд');
INSERT INTO "pioneers" VALUES(3,108,'3:108','[Эй паёмбар] Инҳо оёти Аллоҳ таоло аст, ки онро ба ҳақ бар ту мехонем; ва Аллоҳ таоло [ҳеҷ гоҳ] ситаме барои ҷаҳониён намехоҳад');
INSERT INTO "pioneers" VALUES(3,109,'3:109','Ва ҳар чи дар осмонҳо ва замин аст, аз они Аллоҳ таоло аст ва [ҳама] корҳо ба сӯйи Аллоҳ таоло бозгардонда мешавад');
INSERT INTO "pioneers" VALUES(3,110,'3:110','[Эй уммати Муҳаммад] Шумо беҳтарин уммате ҳастед, ки барои мардум падидор шудаед: Ба кори нек фармон медиҳед ва аз кори нописанд бозмедоред ва ба Аллоҳ таоло имон доред; ва агар аҳли китоб [низ] имон меоварданд, ҳатман, барояшон беҳтар буд; бархе аз онон муъминанд ва [аммо] бештарашон [фосиқу] нофармонанд');
INSERT INTO "pioneers" VALUES(3,111,'3:111','[Фосиқони аҳли китоб] Ҷуз озори андак [ҳамчун таъна ва тамасхур] ҳаргиз зиёне ба шумо нахоҳанд расонд ва агар бо шумо биҷанганд, [шикаст мехӯранд ва] ба шумо пушт мекунанд; он гоҳ [низ аз сӯйи ҳеҷ кас] ёрӣ намешаванд');
INSERT INTO "pioneers" VALUES(3,112,'3:112','[Яҳудиён] Ҳар куҷо ёфт шаванд, [доғи нангу] хорӣ бар онон зада шудааст; магар он ки дар паноҳи амони Аллоҳ таоло ва мардум бошанд; ва онон ба хашме аз Аллоҳ таоло гирифтор шудаанд ва [доғи] дармондагӣ бар онон зада шудааст. Ин бад-он сабаб буд, ки ба оёти Аллоҳ таоло куфр меварзиданд ва паёмбаронро ба ноҳақ мекуштанд; [ва низ] ин [сазо] ба хотири он буд, ки нофармонӣ карданд ва аз ҳудуд [-и илоҳӣ] таҷовуз менамуданд');
INSERT INTO "pioneers" VALUES(3,113,'3:113','[Вале ҳамаи онон] Яксон нестанд. Гурӯҳе аз аҳли китоб ҳастанд, ки дурусткоранд, [ва] дар дили шаб, дар ҳоле ки ба намоз истодаанд, оёти Аллоҳро мехонанд');
INSERT INTO "pioneers" VALUES(3,114,'3:114','[Бархе аз] Онон ба Аллоҳ таоло ва рӯзи қиёмат имон доранд ва [мардумро] ба некӣ амр мекунанд ва аз бадӣ бозмедоранд ва дар [анҷоми] корҳои нек мешитобанд ва ононанд, ки аз шоистагонанд');
INSERT INTO "pioneers" VALUES(3,115,'3:115','Ва ҳар кори неке, ки анҷом диҳанд, ҳаргиз дар бораи он носипосӣ нахоҳанд дид; ва Аллоҳ таоло ба [ҳоли] парҳезгорон доност');
INSERT INTO "pioneers" VALUES(3,116,'3:116','Бе гумон касоне, ки куфр варзиданд, ҳаргиз амволу фарзандонашон чизе аз [азоби] Аллоҳро аз эшон дафъ нахоҳад кард ва онон аҳли оташанд [ва] ҷовидона дар он мемонанд');
INSERT INTO "pioneers" VALUES(3,117,'3:117','Масали он чи [кофирон] дар ин зиндагии дунё инфоқ мекунанд, ҳаммонанди боде аст, ки сармои сахте бо худ дорад ва ба киштзори қавме, ки бар худ ситам кардаанд, мерасад ва нобудашон месозад. Аллоҳ таоло ба онон ситам накардааст, балки эшон худ бар хештан ситам кардаанд');
INSERT INTO "pioneers" VALUES(3,118,'3:118','Эй касоне, ки имон овардаед, аз ғайри худатон [дӯсту] ҳамроз нагиред. Онон аз ҳеҷ нобакорӣ [ва шарре] дар ҳаққи шумо кӯтоҳӣ намекунанд [ва] дӯст доранд, ки шумо дар ранҷу заҳмат бошед. Дар ҳақиқат, [нишонаҳои] душманӣ аз гуфторашон ошкор аст ва он чи дар дилҳояшон пинҳон медоранд бузургтар аст. Агар бияндешед, ҳатман, [хоҳед донист, ки] мо нишонаҳо [-и пандомӯз ва роҳи саодати дунё ва охират]-ро бароятон ба равшанӣ баён кардаем');
INSERT INTO "pioneers" VALUES(3,119,'3:119','Ҳон! Шумо [эй муъминон] касоне ҳастед, ки ононро дӯст доред, дар ҳоле ки онҳо шуморо дӯст надоранд ва шумо ба ҳама китоб [-ҳои осмонӣ] имон доред [вале онон ба Қуръон имон надоранд]; ва чун бо шумо мулоқот мекунанд, мегӯянд: "Имон овардаем" ва чун танҳо мешаванд, аз шиддати хашм бар шумо сарангуштон [-и худ]-ро ба дандон мегазанд. [Эй паёмбар, ба онон] Бигӯ: "Ба хашми худ бимиред". Бе гумон, Аллоҳ таоло ба [рози] даруни синаҳо доност');
INSERT INTO "pioneers" VALUES(3,120,'3:120','[Эй муъминон] Агар ба шумо некӣ [ва хушӣ] расад, ононро андӯҳгин месозад ва агар ба шумо бадӣ [ва газанде] расад, бад-он шодмон мешаванд; ва агар [бар тақдири илоҳӣ] сабру парҳезгорӣ намоед, макру найрангашон ҳеҷ зиёне ба шумо намерасонад, [чаро ки] бе тардид, Аллоҳ таоло ба он чи мекунанд, иҳота дорад');
INSERT INTO "pioneers" VALUES(3,121,'3:121','Ва [эй паёмбар, ба ёд овар] замоне, ки [дар ҷанги Уҳуд] бомдодон аз хона ва кошонаат берун омадӣ, [ва] муъминонро барои ҷангидан дар мавозеи худ мегуморидӣ; ва Аллоҳ таоло шунавову доност');
INSERT INTO "pioneers" VALUES(3,122,'3:122','Ва [ба ёд овар] ҳангоме ки ду гурӯҳ аз шумо [Бани Салама ва Бани Ҳориса] хостанд сустӣ варзанд [ва ҳамроҳи мунофиқон ба Мадина бозгарданд]; вале Аллоҳ таоло ёвар [-у нигаҳдор]-и онон буд; ва муъминон бояд танҳо бар Аллоҳ таоло таваккал кунанд');
INSERT INTO "pioneers" VALUES(3,123,'3:123','Ва [эй муъминон] бе гумон, Аллоҳ таоло шуморо дар [ҷанги] Бадр дар ҳоле ёрӣ кард, ки [ба сабаби камбуди афрод ва аслиҳа] нотавон будед; пас, аз Аллоҳ таоло парво кунед; бошад, ки сипос гузоред');
INSERT INTO "pioneers" VALUES(3,124,'3:124','[Эй паёмбар, ба ёд овар] он гоҳ ки ба муъминон мегуфтӣ: "Оё бароятон кофӣ нест, ки Парвардигоратон шуморо бо се ҳазор фаришта, ки [аз осмон] фуруд меоянд, ёрӣ кунад"');
INSERT INTO "pioneers" VALUES(3,125,'3:125','Оре, агар шикебоиву порсоӣ кунед ва душманон дар ин [ҳаяҷон ва] шитоби хеш бар шумо битозанд, Парвардигоратон шуморо бо панҷ ҳазор фариштаи нишондор ёрӣ хоҳад кард');
INSERT INTO "pioneers" VALUES(3,126,'3:126','Ва Аллоҳ таоло он [ёрӣ]-ро ҷуз башорате барои [пирӯзии] шумо қарор надод, то [бад-ин васила] дилҳоятон бад-он оромиш гирад; ва [-гарна] пирӯзӣ ҷуз аз ҷониби Аллоҳи шикастнопазиру ҳаким нест');
INSERT INTO "pioneers" VALUES(3,127,'3:127','[Ин пирӯзӣ барои он буд] То [Аллоҳ таоло] гурӯҳе аз касонеро, ки куфр варзиданд, ҳалок созад, ё хору мағлубашон намояд, то маъюс [ва хор ба Макка] бозгарданд');
INSERT INTO "pioneers" VALUES(3,128,'3:128','[Эй паёмбар] Ҳеҷ чизе аз кор [-и бандагон] дар дасти ту нест; ё [Аллоҳ таоло] тавбаи онон [мушрикон]-ро мепазирад [ва аз гуноҳашон дармегузарад] ё азобашон мекунад; зеро онон ситамгоранд');
INSERT INTO "pioneers" VALUES(3,129,'3:129','Он чи дар осмонҳо ва замин аст, аз они Аллоҳ таоло аст. Ӯ ҳар киро, ки бихоҳад [ба раҳмат] меомурзад ва ҳар киро бихоҳад [ба адолат] азоб мекунад; ва Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(3,130,'3:130','Эй касоне, ки имон овардаед, риборо [бо суди] чанд баробар нахӯред ва аз Аллоҳ таоло парво кунед; бошад, ки растагор шавед');
INSERT INTO "pioneers" VALUES(3,131,'3:131','Ва аз оташе, ки барои кофирон омода шудааст, бипарҳезед');
INSERT INTO "pioneers" VALUES(3,132,'3:132','Ва аз Аллоҳу паёмбар итоат кунед; бошад, ки мавриди раҳмат [-и Аллоҳ таоло] қарор гиред');
INSERT INTO "pioneers" VALUES(3,133,'3:133','Ва ба сӯйи омурзиши Парвардигоратон ва биҳиште бишитобед, ки густура [паҳно]-и он [ба андозаи] осмонҳо ва замин аст [ва] барои парҳезгорон муҳайё шудааст');
INSERT INTO "pioneers" VALUES(3,134,'3:134','Он касоне, ки дар [ҳангоми] тавонгарӣ ва тангдастӣ инфоқ мекунанд ва хашми худро фурӯ мебаранд ва аз мардум дармегузаранд; ва Аллоҳ таоло накукоронро дӯст медорад');
INSERT INTO "pioneers" VALUES(3,135,'3:135','Ва [ҳамон] касоне, ки чун муртакиб [-и гуноҳи бузург ва] кори зиште шуданд ё [бо анҷоми дигар гуноҳон] бар худ ситам карданд, Аллоҳро ба ёд меоваранд ва барои гуноҳонашон омурзиш мехоҳанд – ва ҷуз Аллоҳ таоло чи касе гуноҳонро меомурзад? – Ва бар он чи кардаанд пофишорӣ намекунанд, дар ҳоле ки медонанд [гунаҳкоранд ва Аллоҳ таоло бахшоянда ва тавбапазир аст]');
INSERT INTO "pioneers" VALUES(3,136,'3:136','Подоши эшон омурзише аз [ҷониби] Парвардигорашон аст ва боғҳое, ки аз зери [дарахтони] он ҷӯйборҳо ҷорӣ аст; ҷовидона дар он мемонанд; ва подоши [накукорон ва] аҳли амал чӣ накуст!');
INSERT INTO "pioneers" VALUES(3,137,'3:137','[Эй осебдидагони Уҳуд] Бе тардид пеш аз шумо [низ муъминоне будаанд, ки ранҷ кашиданд ва] суннатҳое гузаштааст; пас, дар замин бигардед ва бингаред, ки саранҷоми такзибкунандагон [-и оёти илоҳӣ] чи гуна будааст');
INSERT INTO "pioneers" VALUES(3,138,'3:138','Ин [Қуръон] баёни ошкор барои мардум ва ҳидояту андарзе барои парҳезгорон аст');
INSERT INTO "pioneers" VALUES(3,139,'3:139','Ва [эй муъминон, аз ҷиҳод дар роҳи Аллоҳ таоло] суст нашавед ва андӯҳгин нагардед; ва [бидонед, ки] агар имон дошта бошед, шумо бартаред');
INSERT INTO "pioneers" VALUES(3,140,'3:140','Агар [дар Уҳуд] ба шумо газанде расид, ба душманони шумо низ [дар ҷанги Бадр ба ҳамон шакл] осеб расид [пас, муқовимат кунед]. Мо ин рӯзҳо [-и шикасту пирӯзӣ]-ро миёни мардум мегардонем, то Аллоҳ таоло, муъминон [-и воқеӣ]-ро маълум дорад ва аз шумо муъминон [онеро, ки собитқадам аст] бар дигарон гувоҳ гирад; ва Аллоҳ таоло ситамгоронро дӯст намедорад');
INSERT INTO "pioneers" VALUES(3,141,'3:141','Ва [моҷарои Уҳуд василаи имтиҳон буд] то Аллоҳ таоло касонеро, ки имон овардаанд [аз гуноҳонашон пок кунад] ва холис гардонад ва кофиронро нобуд созад');
INSERT INTO "pioneers" VALUES(3,142,'3:142','[Эй асҳоби Муҳаммад], оё пиндоштаед, ки [танҳо бо иддаои имон] ба биҳишт меравед, ҳол он ки Аллоҳ таоло ҳанӯз ҷиҳодгарону шикебоёни шуморо маълум надоштааст?');
INSERT INTO "pioneers" VALUES(3,143,'3:143','Ва [эй муъминон] ба ростӣ, ки пеш аз рӯёрӯ шудан бо марг [дар ғазваи Уҳуд] орзуи шаҳодат доштед. Саранҷом он [маъракаи ҷанг]-ро дидед ва [акнун пешорӯйи шумост ва] ба он менигаред');
INSERT INTO "pioneers" VALUES(3,144,'3:144','Ва Муҳаммад ҷуз фиристодае нест, ки пеш аз ӯ [низ] фиристодагоне [буданд ва] гузаштанд. Оё агар ӯ бимирад ё кушта шавад, ба ақиб бозмегардед [ва дини худро раҳо мекунед]? Ва ҳар ки [аз ақидаи ростини худ] бозгардад, [бидонад, ки] ҳаргиз ба Аллоҳ таоло зиёне намерасонад ва [ба зудӣ] Аллоҳ таоло сипосгузоронро подош хоҳад дод');
INSERT INTO "pioneers" VALUES(3,145,'3:145','Ва ҳеҷ касе ҷуз ба фармони Аллоҳ таоло намемирад, [чаро ки марг] сарнавиште аст замондор [ва муайян]; ва ҳар ки подоши ин дунёро бихоҳад, насибаш мекунем ва ҳар ки подоши охиратро бихоҳад [низ] насибаш хоҳем кард ва [ба зудӣ] сипосгузоронро подош хоҳем дод');
INSERT INTO "pioneers" VALUES(3,146,'3:146','Ва чи бисёр паёмбароне, ки тӯдаҳои анбӯҳе ҳамроҳашон набард карданд; онон ҳеҷ гоҳ дар баробари [сахтиҳо ва] он чи, ки дар роҳи Аллоҳ таоло ба эшон расид, сустӣ наварзиданд ва [дар муқобили душман] дармонда нашуданд; ва Аллоҳ таоло шикебоёнро дӯст медорад');
INSERT INTO "pioneers" VALUES(3,147,'3:147','Ва суханашон танҳо ин буд, ки: "Парвардигоро, гуноҳонамонро биёмурз ва аз зиёдаравии мо дар корамон [даргузар] ва гомҳоямонро устувор бидор ва моро бар гурӯҳи кофирон пирӯз гардон"');
INSERT INTO "pioneers" VALUES(3,148,'3:148','Пас Аллоҳ таоло, подоши ин дунё ва подоши неки охиратро ба онон ато кард; ва Аллоҳ таоло накукоронро дӯст медорад');
INSERT INTO "pioneers" VALUES(3,149,'3:149','Эй касоне, ки имон овардаед, агар аз касоне, ки куфр варзидаанд, итоат кунед, шуморо [ба куфр] бозмегардонанд, онгоҳ [дар дунё ва охират] зиёндида мегардед');
INSERT INTO "pioneers" VALUES(3,150,'3:150','[Бар кофирон такя накунед], балки Аллоҳ таоло [ёру] корсози шумост ва Ӯ беҳтарин ёригарон аст');
INSERT INTO "pioneers" VALUES(3,151,'3:151','Ба зудӣ дар дилҳои касоне, ки куфр варзиданд, биму ҳарос хоҳем афканд; бад-ин хотир, ки чизеро шарики Аллоҳ таоло қарор додаанд, ки ҳеҷ далеле бар [ҳаққонияти] он нозил накардааст; ва ҷойгоҳашон оташ аст ва ҷойгоҳи ситамгорон чӣ бад аст!');
INSERT INTO "pioneers" VALUES(3,152,'3:152','Ва он гоҳ ки [дар оғози ҷанги Уҳуд] ба фармони Ӯ онон [кофирон]-ро мекуштед, ба ростӣ, Аллоҳ таоло ваъдаи худро ба шумо таҳаққуқ бахшид, то [тарсидед ва дар ҳифзи мавқеияти худ] суст шудед ва дар кор [-и ҷангу ҷамъоварии ғанимат] бо якдигар ба низоъ пардохтед; ва пас аз он ки [Аллоҳ таоло] он чиро дӯст медоштед [пирӯзӣ], ба шумо нишон дод, [аз дастури паёмбар] сарпечӣ кардед; бархе аз шумо хоҳони дунё буданд ва бархе хоҳони охират. Сипас [Аллоҳ таоло] барои он ки шуморо биёзмояд, аз [таъқиби] онон мунсарифатон кард [ва пирӯзиятон ба шикаст анҷомид] ва [чун аз гуноҳ ва кӯтоҳии худ дар нофармонӣ аз расул пушаймон шудед] аз шумо даргузашт; ва Аллоҳ таоло нисбат ба муъминон бахшиш [-и бисёр] дорад');
INSERT INTO "pioneers" VALUES(3,153,'3:153','Ва [эй муъминон, ба ёд оваред] ҳангоме ки [дар ҳоли гурез аз кӯҳ] боло мерафтед ва [чунон тарсида будед, ки] ба ҳеҷ кас таваҷҷуҳ намекардед ва паёмбар [ки дар майдони ҷанг муқовимат мекард] аз пушти саратон шуморо садо мезад [ки: "Эй бандагони Аллоҳ таоло, ба сӯйи ман бишитобед"; вале шумо таваҷҷуҳ намекардед]; пас, [Аллоҳ таоло низ] бо андӯҳе дар пайи андӯҳи [дигар] муҷозотатон кард. Ин бад-ин хотир буд, ки бар он чи аз даст додаед [пирӯзӣ ва ғанимат] ва он чи бар саратон омадааст [шикасту тарс], андӯҳгин нашавед ва [бидонед, ки] Аллоҳ таоло ба он чи мекунед, огоҳ аст');
INSERT INTO "pioneers" VALUES(3,154,'3:154','Сипас [Аллоҳ таоло] баъд аз он андӯҳ оромише бар шумо фурӯ фиристод, [ба гунае] ки гурӯҳе аз шуморо [ба сабаби итминону оромише, ки дар дилҳояшон буд] хоби сабук фаро гирифт ва гурӯҳи дигар [мунофиқин] танҳо дар фикри ҷони худ буданд ва дар бораи Аллоҳ таоло гумонҳои нораво [ва ботиле] ҳамчун гумонҳои [даврони] ҷоҳилият доштанд. Онон мегуфтанд: "Оё мо дар ин кор ихтиёре дорем"? [Эй паёмбар, ба онон] Бигӯ: "Ҳамаи корҳо [ва шикасту пирӯзӣ] ба дасти Аллоҳ таоло аст". Онҳо чизеро дар дилҳояшон пинҳон медоранд, ки барои ту ошкор намесозанд, [ва] мегӯянд: "Агар дар ин кор ихтиёр доштем, дар ин ҷо кушта намешудем". Бигӯ: "Агар шумо дар хонаҳои худ низ будед, касоне, ки кушта шудан бар онон муқаррар шуда буд, ҳатман, [бо пойи худ] ба сӯйи қатлгоҳҳои хеш мерафтанд". Ва [ин рӯйдодҳо] барои он аст, ки Аллоҳ таоло он чиро дар синаҳоятон [пинҳон] доред [дар амал] биёзмояд ва он чиро дар дилҳоятон аст, пок гардонад; ва Аллоҳ таоло ба [рози] даруни синаҳо доност');
INSERT INTO "pioneers" VALUES(3,155,'3:155','[Эй асҳоби паёмбар] Афроде аз шумо, ки дар рӯзи бархӯрди ду гурӯҳ [мусулмонон ва кофирон дар ҷанги Уҳуд] фирор карданд, дар ҳақиқат шайтон ононро ба сабаби баъзе аз кирдор [ва гуноҳон]-е, ки муртакиб шуда буданд, ба инҳироф кашонд ва, ҳатман, Аллоҳ таоло аз онон даргузашт. Бе гумон, Аллоҳ таоло омурзандаи бурдбор аст');
INSERT INTO "pioneers" VALUES(3,156,'3:156','Эй касоне, ки имон овардаед, монанди афроде набошед, ки куфр варзиданд ва дар бораи бародаронашон – ҳангоме ки [дар пайи касби рӯзӣ] ба сафар рафтанд [ва мурданд] ё дар ҷанг ширкат карданд [ва кушта шуданд] – гуфтанд: "Агар онон назди мо буданд, намемурданд ва кушта намешуданд". [Ин эътиқод дар вуҷудашон ниҳода шуд] То Аллоҳ таоло [бад-ин гуна андӯҳ ва] ҳасрате дар дилҳояшон карор диҳад; ва Аллоҳ таоло [аст, ки] зинда мекунад ва мемиронад; ва Аллоҳ таоло ба он чи мекунед, биност');
INSERT INTO "pioneers" VALUES(3,157,'3:157','Ва агар дар роҳи Аллоҳ таоло кушта шавед ё бимиред, ҳатман, омурзиш ва раҳмати Аллоҳ таоло беҳтар аст аз чизе [ки] онон [дунёдӯстон] ҷамъ мекунанд');
INSERT INTO "pioneers" VALUES(3,158,'3:158','Ва агар бимиред ё [дар арсаи ҷиҳод] кушта шавед, яқинан ба сӯйи Аллоҳ таоло маҳшур мешавед');
INSERT INTO "pioneers" VALUES(3,159,'3:159','[Эй паёмбар] Ба [сабаби] раҳмати илоҳӣ аст, ки ту бо онон [асҳоб] нармхӯ [ва меҳрубон] шудӣ; ва агар тундхӯ ва сангдил будӣ, ҳатман, аз атрофат пароканда мешуданд; пас, аз эшон даргузар ва барояшон омурзиш бихоҳ ва дар корҳо бо онон машварат кун ва он гоҳ ки тасмим [бар анҷоми коре] гирифтӣ, бар Аллоҳ таоло таваккал кун; [чаро ки] бе тардид, Аллоҳ таоло таваккалкунандагонро дӯст медорад');
INSERT INTO "pioneers" VALUES(3,160,'3:160','Агар Аллоҳ таоло шуморо ёрӣ кунад, ҳеҷ кас бар шумо пирӯз нахоҳад шуд ва [аммо] агар шуморо ба худ вогузорад, кист, ки пас аз Ӯ ёриатон кунад? Ва муъминон бояд танҳо бар Аллоҳ таоло таваккал кунанд');
INSERT INTO "pioneers" VALUES(3,161,'3:161','Ва сазовор нест ҳеҷ паёмбаре [бо гирифтани саҳми изофӣ аз ғаноим ба асҳобаш] хиёнат кунад; ва ҳар ки хиёнат кунад, рӯзи қиёмат, бо он чи [дар он] хиёнат кардааст, меояд; сипас ба ҳар кас подоши ҳар чи [аз неку бад] кардааст, ба таври комил дода мешавад ва ба онон ситам нахоҳад шуд');
INSERT INTO "pioneers" VALUES(3,162,'3:162','Оё касе, ки хушнудии Аллоҳро металабад, ҳаммонанди касест, ки ба хашме аз Аллоҳ таоло дучор шуда ва ҷойгоҳаш дӯзах аст? Ва [дӯзах] чи бад ҷойгоҳест!');
INSERT INTO "pioneers" VALUES(3,163,'3:163','[Ҳар як аз] Эшон дараҷот [-и мутафовит]-е назди Аллоҳ таоло доранд ва Аллоҳ таоло ба он чи мекунанд, биност');
INSERT INTO "pioneers" VALUES(3,164,'3:164','Ба ростӣ, Аллоҳ таоло бар муъминон миннат ниҳод, ки миёни онон паёмбаре аз худашон барангехт, то оёти Ӯ [Қуръон]–ро бар эшон бихонад ва [аз гуноҳ] покашон гардонад ва ба онон китоби [Қуръон] ва ҳикмат [суннат] биёмурзад; дар ҳоле ки дар гузашта дар гумроҳии ошкоре буданд');
INSERT INTO "pioneers" VALUES(3,165,'3:165','[Эй муъминон] Оё чун ба шумо [дар ҷанги Уҳуд] мусибате расид – [бо он ки дар ҷанги Бадр] ду баробарашро [ба душманони худ] расондед – гуфтед: "Ин [мусибат] аз куҷо [ба мо] расид"? [Эй паёмбар] Бигӯ: "Он [шикаст] аз ҷониби худатон [ва натиҷаи нофармонӣ аз дастури паёмбар] аст". Бе гумон, Аллоҳ таоло ба ҳар коре тавоност');
INSERT INTO "pioneers" VALUES(3,166,'3:166','Ва рӯзе, ки [дар ҷанги Уҳуд] ду гурӯҳ [муъминон ва кофирон] бо ҳам бархӯрд карданд, он чи ба шумо расид, ба фармони Аллоҳ таоло буд [то шуморо биёзмояд] ва муъминонро маълум дорад');
INSERT INTO "pioneers" VALUES(3,167,'3:167','Ва касонеро, ки нифоқ варзиданд [низ] маълум дорад, ки [чун] ба эшон гуфта шуд: "Биёед, дар роҳи Аллоҳ таоло биҷангед ё [аз хонавода ва қабилаатон] дифоъ кунед". Гуфтанд: "Агар медонистем, ҷанге рӯй хоҳад дод, ҳатман, аз шумо пайравӣ мекардем". Онон дар он ҳангом ба куфр наздиктар буданд то имон; ба забони хеш чизе мегӯянд, ки дар дилҳояшон нест ва Аллоҳ таоло ба он чи [дар дил] пинҳон мекунанд, огоҳтар аст');
INSERT INTO "pioneers" VALUES(3,168,'3:168','Касоне, ки [худ дар хона] нишастанд [аз ҷиҳод сар боз заданд] ва дар бораи бародарони худ гуфтанд: "Агар онҳо аз мо пайравӣ мекарданд, кушта намешуданд". [Эй паёмбар, ба ин мунофиқон] Бигӯ: "Агар рост мегӯед, пас, маргро аз худ дур созед"');
INSERT INTO "pioneers" VALUES(3,169,'3:169','Ҳаргиз касонеро, ки дар роҳи Аллоҳ таоло кушта шудаанд, мурда мапиндор; балки зиндаанд ва назди Парвардигорашон рӯзӣ дода мешаванд');
INSERT INTO "pioneers" VALUES(3,170,'3:170','Онон ба он чи Аллоҳ таоло аз фазли худ ба эшон додааст, шодмонанд ва барои [саранҷоми неки] касоне, ки [роҳи] ононро пай гирифтаанд ва [ҳанӯз] ба онон напайвастаанд, шодӣ мекунанд; чаро ки [ҳеҷ як аз] эшон тарсу андӯҳе нахоҳанд дошт');
INSERT INTO "pioneers" VALUES(3,171,'3:171','[Ҳамчунин] Ба неъмату фазли Аллоҳ таоло шодмонанд ва ин ки [мебинанд] Аллоҳ таоло подоши муъминонро зоеъ намекунад');
INSERT INTO "pioneers" VALUES(3,172,'3:172','Касоне, ки даъвати Аллоҳ таоло ва паёмбарро – пас аз он ки [дар ғазваи Уҳуд] ба онон ҷароҳат [ва сахтии бисёр] расид – иҷобат карданд [ва барои таъқиби мушрикон ба сӯйи "Ҳамро-ул-асад" рафтанд], барои [он идда аз] эшон, ки некӣ ва парҳезкорӣ карданд, подоши бузурге [дар пеш] аст');
INSERT INTO "pioneers" VALUES(3,173,'3:173','Ҳамон касоне, ки бархе [аз мушрикон] ба онон гуфтанд: "Мардум [-и Макка] барои [ҷанг бо] шумо гирд омадаанд; пас, аз онҳо битарсед" ва [аммо ин сухан] бар имонашон афзуд ва гуфтанд: "Аллоҳ таоло барои [муҳофизат аз] мо бас аст ва [Ӯ] беҳтарин [муроқиб ва] коргузор аст"');
INSERT INTO "pioneers" VALUES(3,174,'3:174','Пас ба неъмату фазли Аллоҳ таоло [аз Ҳамро-ул-асад ба Мадина] бозгаштанд, дар ҳоле ки ҳеҷ осебе ба онон нарасида буд ва ҳамчунон дар пайи [касби] хушнудии Аллоҳ таоло буданд; ва Аллоҳ таоло фазлу бахшиши бузурге дорад');
INSERT INTO "pioneers" VALUES(3,175,'3:175','Дар ҳақиқат, ин шайтон аст, ки [шуморо аз ҳайбати] дӯстонаш метарсонад, пас, агар имон доред, аз онон [мушрикон] натарсед ва аз ман битарсед');
INSERT INTO "pioneers" VALUES(3,176,'3:176','Ва [эй паёмбар] касоне, ки дар [ёрӣ кардани] куфр мешитобанд, туро андӯҳгин насозанд. Яқинан, онон ҳаргиз зиёне ба Аллоҳ таоло намерасонанд. Аллоҳ таоло мехоҳад, ки баҳрае барояшон дар охират қарор надиҳад ва азоби бузург [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(3,177,'3:177','Ба ростӣ, касоне, ки куфрро ба [баҳои] имон хариданд, ҳаргиз ба Аллоҳ таоло зиёне намерасонанд ва азоби дардноке [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(3,178,'3:178','Ва касоне, ки кофир шуданд, мапиндоранд, ки чун ба онон муҳлат медиҳем ба судашон аст. Ҷуз ин нест, ки ба онон муҳлат медиҳем, то бар гуноҳи [худ] бияфзоянд ва азоби хоркунандае [пурдарде дар пеш] доранд');
INSERT INTO "pioneers" VALUES(3,179,'3:179','[Эй муъминон] Чунин набуд [ва аз ҳикмати илоҳӣ нест], ки Аллоҳ таоло муъминонро бар ин ҳол, ки шумо ҳастед [во гузорад ва муъмину мунофиқро дар канори якдигар] раҳо созад, [балки шуморо бо таколифу озмоишҳои гуногун меозмояд] то палидро аз пок ҷудо кунед; ва чунин набуд, ки Аллоҳ таоло [барои бозшиносии муъминон аз мунофиқон] шуморо аз [асрори] ғайб огоҳ кунад; вале Аллоҳ таоло аз миёни фиристодагонаш ҳар киро бихоҳад, бармегузинад [ва ӯро бар бахше аз ғайб огоҳ мекунад]; пас, ба Аллоҳ таоло ва фиристодагонаш имон биёваред; ва агар имон биёваред ва тақво пеша кунед, подоши бузурге бароятон [дар пеш] аст');
INSERT INTO "pioneers" VALUES(3,180,'3:180','Ва касоне, ки ба он чи Аллоҳ таоло аз фазли хеш ба онон додааст, бухл меварзанд, гумон накунанд, ки он [бухл] барояшон хайр аст; балки барояшон шар аст. Дар рӯзи қиёмат он чи ба он бухл меварзиданд, тавқи гарданашон хоҳад шуд [ва бо он азоб мешаванд]; ва мероси осмонҳову замин аз они Аллоҳ таоло аст ва Аллоҳ таоло ба он чи мекунед, огоҳ аст');
INSERT INTO "pioneers" VALUES(3,181,'3:181','Бе тардид, Аллоҳ таоло сухани касонеро шунид, ки гуфтанд: "Аллоҳ таоло фақир аст ва [аз мо қарз мехоҳад ва] мо бениёзем". [Мо ин] Гуфторашон ва [низ] қатли ноҳаққи паёмбаронро хоҳем навишт ва рӯзи қиёмат ба [онон] мегӯем: "Азоби сӯзон [-и дӯзах]-ро бичашед"');
INSERT INTO "pioneers" VALUES(3,182,'3:182','[Эй яҳудиён] Ин [муҷозот] ба хотири кирдори пешини шумост; ва [-гарна] Аллоҳ таоло ҳаргиз ба бандагони [худ] ситамгор нест');
INSERT INTO "pioneers" VALUES(3,183,'3:183','[Ин яҳудиён ҳамон] Касоне ҳастанд, ки гуфтанд: "Аллоҳ таоло бо мо аҳду паймон бастааст, ки ба ҳеҷ паёмбаре имон наёварем, то [фиристодае ҳамроҳи худ] бароямон қурбоние биёварад, ки оташ [-и аз соиқа бархоста] онро [ба нишонаи пазириш] бисӯзонад". [Эй паёмбар, ба онон] Бигӯ: "Ҳатман, пеш аз ман паёмбароне буданд, ки далоили ошкорро ҳамроҳи он чи гуфтед, бароятон оварданд. Агар рост мегӯед, пас, чаро ононро куштед"?');
INSERT INTO "pioneers" VALUES(3,184,'3:184','Пас, агар [яҳудиён ва кофирон] такзибат карданд, [ғамгин мабош ва бидон] паёмбароне, ки пеш аз ту буданд ва муъҷизоту навиштаҳо ва китобҳои равшангар оварда буданд, [низ] такзиб шуданд');
INSERT INTO "pioneers" VALUES(3,185,'3:185','Ҳар касе чашандаи [таъми] марг аст ва яқинан рӯзи қиёмат подошҳоятонро ба тамомӣ хоҳед гирифт; пас, ҳар киро аз оташ дур доранд ва дар биҳишт дароваранд, ҳатман, растагор шудааст; ва зиндагии дунё чизе ҷуз мояи фиреб нест');
INSERT INTO "pioneers" VALUES(3,186,'3:186','[Эй муъминон] Бе тардид шумо дар [мавриди базли] амволу ҷонҳоятон озмоиш мешавед ва [ҳамчунин] аз касоне, ки пеш аз шумо ба онон китоби [осмонӣ] дода шудааст [яҳудиён ва масеҳиён] ва [низ] аз касоне, ки ширк варзиданд, суханони озордиҳандаи бисёре хоҳед шунид; ва [аммо] агар сабр кунед ва тақво пеша созед, [шоистатар аст; зеро] ин [истодагӣ] баёнгари азми устувор [-и шумо] дар корҳост');
INSERT INTO "pioneers" VALUES(3,187,'3:187','Ва [эй паёмбар, ёд кун] ҳангоме ки Аллоҳ таоло аз касоне, ки китоби [осмонӣ] ба онон дода шуда буд, паймон гирифт, ки: "Ҳатман, [омӯзаҳои ҳидоятбахши] онро барои мардум баён намоед ва [он чиро, ки нубуввати Муҳаммад хабар медиҳад] китмонаш накунед"; ва [-ле] он [аҳд]-ро пушти сари хеш андохтанд [бад-он таваҷҷуҳе накарданд] ва ба баҳои андаке [ҳамчун молу мақом] фурӯхтанд. Чи бад аст, он чи мехаранд!');
INSERT INTO "pioneers" VALUES(3,188,'3:188','Касоне, ки аз аъмоли [зишти] худ хушнуд мегарданд ва дӯст доранд дар муқобили кор [-и нек] -е, ки анҷом надодаанд, мавриди ситоиш қарор гиранд, гумон мабар, ки аз азоб [-и илоҳӣ] барканоранд; [балки] барои онон азоби дардноке [дар пеш] аст');
INSERT INTO "pioneers" VALUES(3,189,'3:189','Фармонравоии осмонҳо ва замин аз они Аллоҳ таоло аст ва Аллоҳ таоло ба ҳар чизе тавоност');
INSERT INTO "pioneers" VALUES(3,190,'3:190','Ба ростӣ, дар офариниши осмонҳо ва замин ва омадушуди шабу рӯз нишонаҳо [и ошкор]-е барои хирадмандон аст');
INSERT INTO "pioneers" VALUES(3,191,'3:191','[Ҳамон] Касоне, ки Аллоҳро [дар ҳамаи аҳвол] истода ва нишаста ва бар паҳлу орамида, ёд мекунанд ва дар офариниши осмонҳо ва замин меандешанд [ва мегӯянд]: "Парвардигоро, инҳоро беҳуда наофаридаӣ; муназзаҳӣ Ту. Пас, моро аз азоби оташ [-и дузах] нигаҳ дор');
INSERT INTO "pioneers" VALUES(3,192,'3:192','Парвардигоро, касеро, ки Ту ба оташ [-и дузах] дароварӣ, яқинан, хору расвояш кардаӣ ва ситамгорон [ҳеҷ] ёваре надоранд');
INSERT INTO "pioneers" VALUES(3,193,'3:193','Парвардигоро, мо шунидем, даъватгаре ба имон фаро мехонад, ки: "Ба Парвардигори худ имон оваред". Пас, мо имон овардем. Парвардигоро, пас, гуноҳонамонро бибахш ва бадиҳоямонро бипӯшон ва моро дар зумраи некон бимирон');
INSERT INTO "pioneers" VALUES(3,194,'3:194','Парвардигоро, он чиро, ки ба василаи фиристодагонат ба мо ваъда додаӣ, ба мо ато кун ва дар рӯзи қиёмат расвоямон магардон. Бе тардид, Ту хилофи ваъда намекунӣ"');
INSERT INTO "pioneers" VALUES(3,195,'3:195','Пас, Парвардигорашон дуои ононро иҷобат кард [ва фармуд]: "Ман [подоши] амали ҳеҷ соҳибамале аз шуморо – аз мард ё зан – ки ҳамнавъи якдигаред, табоҳ намекунам; пас, касоне, ки [аз ватани худ] ҳиҷрат кардаанд ва [ё] аз хонаҳои худ ронда шудаанд ва дар роҳи ман озор дидаанд ва ҷангидаанд ва кушта шудаанд, ҳатман, бадиҳояшонро аз онон мезудоям ва [ба унвони] подоше аз ҷониби Аллоҳ таоло эшонро дар боғҳое дармеоварам, ки аз зери [дарахтони] он ҷӯйборҳо ҷорист ва Аллоҳ таоло аст, ки подоши наку назди Ӯст');
INSERT INTO "pioneers" VALUES(3,196,'3:196','[Эй паёмбар] Рафтуомади кофирон [барои тиҷорат] дар шаҳрҳо [ва молу мақоми онон] туро нафиребад');
INSERT INTO "pioneers" VALUES(3,197,'3:197','[Ин] Баҳрамандии ночизест; сипас ҷойгоҳашон дузах аст ва чи бад ҷойгоҳе аст!');
INSERT INTO "pioneers" VALUES(3,198,'3:198','Аммо касоне, ки тақвои Парвардигорашонро намудаанд, [дар биҳишт] боғҳое доранд, ки аз зери [дарахтони] он ҷӯйборҳо ҷорист [ва] ҷовидона дар он мемонанд. [Ин] Пазироӣ аз ҷониби Аллоҳ таоло [муҳайё шуда] аст ва он чи назди Аллоҳ таоло аст, барои накукорон беҳтар аст');
INSERT INTO "pioneers" VALUES(3,199,'3:199','Ва касоне аз аҳли китоб ҳастанд, ки ба Аллоҳ таоло ва он чи бар шумо нозил шуда ва он чи бар худашон нозил шудааст, имон доранд; дар баробари Аллоҳ таоло фурӯтан ҳастанд ва оёти Аллоҳро ба баҳои ночизе намефурушанд. Инонанд, ки подошашон назди Парвардигорашон [маҳфуз] аст. Ба ростӣ, ки Аллоҳ таоло дар ҳисобрасӣ сареъ аст');
INSERT INTO "pioneers" VALUES(3,200,'3:200','Эй касоне, ки имон овардаед, [бар таколифи динӣ ва ранҷҳои дунявӣ] шикебоӣ варзед ва [дар баробари душманон] пойдор бошед ва парҳезгорӣ пеша кунед; бошад, ки растагор шавед');
INSERT INTO "pioneers" VALUES(4,1,'4:1','Эй мардум, аз Парвардигоратон парво кунед [битарсед], [ҳамон зоте] ки шуморо аз як тан офарид ва ҳамсарашро [низ] аз ӯ офарид ва аз [насли] он ду мардону занони бисёре пароканд. Ва аз Парвардигоре, ки ба [номи] Ӯ аз якдигар дархост мекунед, парво [тақво] намоед ва аз [гусастани] пайванди хешовандӣ бипарҳезед. Бе гумон, Аллоҳ таоло ҳамвора муроқиб [-у нигаҳбони] шумост');
INSERT INTO "pioneers" VALUES(4,2,'4:2','Ва [эй сарпарастон] амволи ятимонро [пас аз булуғу рушд] ба эшон бозгардонед ва [моли камарзишу] номарғуб [-и худ]-ро бо [моли арзишманду] марғуб [-и онон] иваз накунед ва амволи эшонро ҳамроҳи амволи худ нахӯред. Ба ростӣ, ки ин [кор] гуноҳи бузурге аст');
INSERT INTO "pioneers" VALUES(4,3,'4:3','Ва агар тарсидед, ки [ба хотири костан аз маҳрия ё бадрафторӣ] натавонед адолатро дар ҳаққи духтарони ятим риоя намоед, [аз издивоҷ бо онон сарфи назар кунед ва] аз занон [-и дигар]-е, ки мавриди писанди шумост, [як ё] ду ва [ё] се ва [ё] чаҳор танро ба ҳамсарӣ дароваред. Пас, агар бим доред, ки натавонед адолат кунед, ба як [зан] ё ба он чи [аз канизон], ки молики онҳоед [иктифо намоед]. Ин [кор] наздиктар аст ба ин, ки ситам накунед');
INSERT INTO "pioneers" VALUES(4,4,'4:4','Ва маҳрияи занонро ба унвони ҳадя [ва фаризаи илоҳӣ] бо хушдилӣ ба онон бидиҳед. Пас, агар онон чизе аз онро бо ризоияти хотир ба шумо бахшиданд, онро хушу гуворо бихӯред');
INSERT INTO "pioneers" VALUES(4,5,'4:5','Ва ба камхирадоне, ки Аллоҳ таоло шуморо ба сарпарастии онон гумоштааст, амволашонро надиҳед ва аз [даромади] он ба эшон хӯроку пӯшок диҳед ва бо онон ба шоистагӣ сухан бигӯед');
INSERT INTO "pioneers" VALUES(4,6,'4:6','Ва ятимонро [аз лиҳози тавоноӣ бар муроқибат аз амволашон] биёзмоед то вақте ба [синни] издивоҷ бирасанд, пас, агар дар эшон рушд [-и фикрии кофӣ] ёфтед, амволашонро ба онон бидиҳед ва онро [аз бими он ки мабодо] бузург шаванд [ва аз шумо пас бигиранд], ба исрофу шитоб махӯред. Ва ҳар ки бениёз аст, бояд [аз гирифтани уҷрату музди сарпарастӣ] худдорӣ кунад ва ҳар ки ниёзманд аст, бояд ба тарзи шоиста [ва мутобиқи урф аз он] бихӯрад. Пас, ҳар гоҳ амволашонро ба онон боз гардондед, бар эшон гувоҳ бигиред ва [бидонед] Аллоҳ таоло барои ҳисобрасӣ кофӣ аст');
INSERT INTO "pioneers" VALUES(4,7,'4:7','Мардон аз он чи падару модар ва хешовандон [ба унвони ирс] бар ҷой гузоштаанд, саҳме доранд ва занон [низ] аз он чи падару модар ва хешовандон бар ҷой гузоштаанд, саҳме доранд – хоҳ он [мол] кам бошад ё зиёд. [Чунин] Баҳрае [аз ҷониби Аллоҳ таоло] муайян [ва муқаррар шуда] аст');
INSERT INTO "pioneers" VALUES(4,8,'4:8','Ва ҳар гоҳ хешовандон [-и ғайри ворис] ва ятимону тиҳидастон бар тақсими [мерос] ҳозир шаванд, [пеш аз тақсим чизе] аз он [амволро аз боби истиҳбоб] ба онон бидиҳед ва ба шоистагӣ бо эшон сухан бигӯед');
INSERT INTO "pioneers" VALUES(4,9,'4:9','Ва касоне, ки агар фарзандони нотавоне аз худ бар ҷой бигзоранд, бар [ояндаи] онон бим доранд, бояд [аз ситам дар бораи ятимони мардум низ] битарсанд. Пас, бояд аз Аллоҳ таоло парво кунанд [битарсанд] ва сухани [санҷида ва] дуруст бигӯянд');
INSERT INTO "pioneers" VALUES(4,10,'4:10','Дар ҳақиқат, касоне, ки амволи ятимонро ба ситам мехӯранд, ҷуз ин нест, ки оташ дар шиками хеш фурӯ мебаранд ва ба зудӣ ба оташе барафрӯхта дармеоянд');
INSERT INTO "pioneers" VALUES(4,11,'4:11','Аллоҳ таоло дар бораи фарзандонатон ба шумо супориш мекунад: Саҳми писар монанди саҳми ду духтар аст. Ва агар [ворисон ду духтар ё] бештар аз ду духтар бошад, саҳми онон ду савуми тарака аст. Ва агар фақат як духтар бошад, саҳми ӯ нисфи тарака аст. Агар [майит] фарзанд дошта бошад, барои ҳар як аз падару модараш, як шашуми тарака аст ва агар фарзанд надошта бошад ва [танҳо] падару модараш аз ӯ ирс бибаранд, дар ин сурат, барои модараш як савум [сеяк] аст [ва барои падар ду савум] ва агар ӯ [майит] бародароне дошта бошад, саҳми модараш як шашум [шашяк] аст. [Ин тақсими мерос] Пас аз амал ба васиятест, ки карда ва ё [пардохти] воме [ки дорад анҷом мегирад]. Шумо намедонед, ки падаронатон ва [ё] фарзандонатон, кадом як бароятон судмандтар аст. [Ин аҳком] Фариза [-и муайян ва муқаррар] аз ҷониби Аллоҳ таоло аст. Ба ростӣ, ки Аллоҳ таоло донои ҳаким аст');
INSERT INTO "pioneers" VALUES(4,12,'4:12','Ва [эй шавҳарон] агар ҳамсаронатон фарзанде надошта бошанд, ниме аз тарака барои шумост ва агар фарзанде дошта бошанд, як чаҳоруми [чаҳоряк] тарака аз они шумост. [Албатта] Пас аз [амал ба] васияте, ки кардаанд ё [пардохти] воме, ки доранд. Ва агар шумо фарзанде надошта бошед, як чаҳоруми таракаи шумо барои занонатон аст ва агар фарзанде дошта бошед, як ҳаштуми таракаи шумо аз они эшон аст. [Албатта] Пас аз амал ба васияте, ки кардаед ва [пардохти] воме [ки доред]. Ва агар мард ё зане, ки аз ӯ ирс мебаранд, падар ё фарзанд надошта бошад [калола бошад] ва бародар ё хоҳар дошта бошад, пас, барои ҳар як аз онон як шашуми [тарака] аст. Ва агар [хешовандони чунин шахсе] беш аз ин [ду нафар] бошанд, дар ин сурат, дар як савум [аз тарака] шариканд. [Албатта] Пас аз анҷоми васияте, ки бад-он супориш шуда ё воме, ки [бояд пардохт гардад, ба шарти он ки аз ин тариқ], зиёне [ба ворисон] нарасонад. [Ин ҳукм] Супориши Аллоҳ таоло аст ва Аллоҳ таоло донои бурдбор аст');
INSERT INTO "pioneers" VALUES(4,13,'4:13','Инҳо ҳудуди илоҳӣ аст ва ҳар ки аз Аллоҳ таоло ва паёмбараш итоат кунад, вайро ба боғҳое дармеоварад, ки аз зери [дарахтони] он ҷӯйборҳо ҷорӣ аст, дар он ҷовидонаанд ва ин ҳамон комёбии бузург аст');
INSERT INTO "pioneers" VALUES(4,14,'4:14','Ва ҳар ки аз Аллоҳ таоло ва паёмбараш нофармонӣ намояд ва аз ҳудуди Ӯ таҷовуз кунад, вайро дар оташе ворид мекунад, ки ҷовидона дар он хоҳад монд ва барояш азоби хоркунанда [дар пеш] аст');
INSERT INTO "pioneers" VALUES(4,15,'4:15','Ва касоне аз занони шумо, ки муртакиби зино мешаванд, чаҳор нафар [марди одил] аз худатонро бар онон гувоҳ гиред. Пас, агар гувоҳе доданд, он занонро дар хонаҳо нигаҳ доред, то маргашон фаро расад ё Аллоҳ таоло роҳе барояшон қарор диҳад');
INSERT INTO "pioneers" VALUES(4,16,'4:16','Аз миёни шумо он марду зане, ки муртакиби он [амали зишт] мешаванд, [бо танбеҳу тавбих] озорашон диҳед. Пас, агар тавба карданд ва дурусткор шуданд, аз эшон сарфи назар кунед, [чаро ки] бе тардид, Аллоҳ таоло тавбапазири меҳрубон аст');
INSERT INTO "pioneers" VALUES(4,17,'4:17','Ҷуз ин нест, ки [пазириши] тавба аз сӯйи Аллоҳ таоло барои касонест, ки аз рӯйи нодонӣ кори ношоистае анҷом медиҳанд, он гоҳ ба зудӣ [ва пеш аз фаро расидани марг] тавба мекунанд. Инонанд, ки Аллоҳ таоло тавбаашонро мепазирад ва Аллоҳ таоло донои ҳаким аст');
INSERT INTO "pioneers" VALUES(4,18,'4:18','Ва барои касоне, ки [то поёни умр ҳамвора] муртакиби корҳои ношоиста мешаванд ва ҳангоме ки марги яке аз онон фаро мерасад мегӯяд: "Акнун тавба кардам" [ҳеҷ] тавба [ва бахшише дар кор] нест ва на барои касоне, ки дар ҳоли куфр мемиранд. Инон ҳастанд, ки барояшон азоби дардноке муҳайё сохтаем');
INSERT INTO "pioneers" VALUES(4,19,'4:19','Эй касоне, ки имон овардаед, барои шумо ҳалол нест, ки занон [-и падаронатон]-ро бар хилофи майлашон ба ирс баред [ё маҷбур ба издивоҷ бо дигарон кунед] ва [дар мавриди ҳамсарони худатон низ ҳукм ин аст, ки] ононро зери фишор қарор надиҳед, то бархе аз он чиро, ки [ба унвони маҳрия] ба эшон додаед, пас бигиред, магар ин ки муртакиби амали ношоистаи ошкоре [монанди зино] шаванд. [Аммо дар мавриди занони покдомани худ накугуфтор бошед] Ва бо онҳо ба таври шоиста рафтор кунед ва агар аз онон [хушатон наёмад ва] кароҳат доштед, пас, [шикебоӣ кунед]. Чи басо чизеро хуш намедоред ва Аллоҳ таоло хайри бисёре дар он қарор медиҳад');
INSERT INTO "pioneers" VALUES(4,20,'4:20','Агар хостед ҳамсар [-и дигаре] ба ҷойи ҳамсар [-и пешини худ] баргузинед ва ба яке аз онон моли фаровоне [ба унвони маҳрия] пардохтаед, чизе аз онро пас нагиред. Оё мехоҳед он [мол]-ро ба буҳтон ва гуноҳи ошкор бозпас бигиред?');
INSERT INTO "pioneers" VALUES(4,21,'4:21','Ва чи гуна он [маҳрия]-ро бозпас мегиред, дар ҳоле ки аз якдигар баҳраманд шудаед [ва аз асрори якдигар огоҳед] ва онон [ҳангоми ақди издивоҷ] аз шумо паймони маҳкаму устувор гирифтаанд');
INSERT INTO "pioneers" VALUES(4,22,'4:22','Ва бо заноне, ки падаронатон [бо онҳо] издивоҷ кардаанд, [пас аз марги падар] издивоҷ накунед, магар он чи [аз ин навъ издивоҷ, ки дар замони ҷоҳилият рух дода ва] бахшида шудааст, [чаро ки] бе тардид, ин кор амали зишту танаффурангез ва роҳи нодуруст аст');
INSERT INTO "pioneers" VALUES(4,23,'4:23','[Издивоҷ бо ин афрод] Бар шумо ҳаром шудааст: модару духтар ва хоҳару амма ва холаву духтари бародару духтари хоҳар, модаре, ки ба шумо шир додааст ва хоҳари разоӣ ва модари зану духтари ҳамсаре, ки [он духтар] дар хонаи шумо парвариш ёфтааст ва бо он ҳамсар ҳамбистар шудаед – вале агар бо он зан омезиш накардаед, гуноҳе нест [ки бо духтараш издивоҷ кунед] – ҳамчунин, ҳамсари писаратон, ки аз пушти худатон аст ва издивоҷ бо ду хоҳар [низ бар шумо ҳаром аст], магар он чи [аз ин навъ издивоҷ, ки дар замони ҷоҳилият рух дода ва] бахшида шудааст, [чаро ки] бе тардид, Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(4,24,'4:24','Занони шавҳардор [бар шумо ҳароманд], магар заноне, ки [канизатон ҳастанд ва шумо] молики онҳо шудаед. [Ин] Фаризаи илоҳист, ки бар шумо муқаррар доштааст ва ғайр аз инҳо [ки гуфта шуд], бароятон ҳалол аст, дар ҳоле ки покдоманӣ меварзед ва зинокор нестед, бо амволи худ [занони дигарро] талаб кунед. Пас, он заноне, ки [ба издивоҷ даровардед ва] аз онон ком гирифтед, бояд маҳрияашонро ба унвони фаризае ба онон бидиҳед ва дар мавриди он чи баъд аз таъйини маҳрия [дар мавриди бешу ками он] бо якдигар тавофуқ мекунед, гуноҳе бар шумо нест. Бе гумон, Аллоҳ таоло донои ҳаким аст');
INSERT INTO "pioneers" VALUES(4,25,'4:25','Ва ҳар як аз шумо аз лиҳози молӣ тавоноии издивоҷ бо занони озодаи муъминро надорад, пас, бо канизакони боимоне, ки дар ихтиёри шумо [ё дигар мусулмонон] ҳастанд [издивоҷ кунад]. Ва Аллоҳ таоло ба имонатон донотар аст. [Шумо ва онон] Ҳамагӣ аз ҷинси якдигаред [ва бо якдигар баробаред], пас, бо иҷозати сарпарасташон бо онон издивоҷ кунед ва маҳрияҳояшонро ба таври писандида ба эшон бидиҳед, [ба шарти он ки] покдоман бошанд на зинокор ва на [дар зумраи] касоне, ки пинҳонӣ [барои худ] дӯст мегиранд. Он гоҳ чун издивоҷ карданд, агар муртакиби зино шуданд, пас, муҷозоташон [панҷоҳ тозиёна, яъне] нисфи муҷозоти занони озод аст. Ин [иҷозати издивоҷ бо канизон] барои касе аз шумост, ки аз олоиш ба гуноҳ битарсад, ва [-ле дар мавриди издивоҷ бо канизон] агар шикебоӣ [ва покдоманӣ] пеша кунед, бароятон беҳтар аст ва Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(4,26,'4:26','Аллоҳ таоло мехоҳад, ки [аҳкоми хешро] бароятон равшан созад ва шуморо ба роҳу равиши касоне, ки пеш аз шумо буданд, роҳнамоӣ кунад ва Аллоҳ таоло донои ҳаким аст');
INSERT INTO "pioneers" VALUES(4,27,'4:27','Ва Аллоҳ таоло мехоҳад тавбаи шуморо бипазирад [ва аз олудагии гуноҳ покатон намояд], ва [-ле] касоне, ки пайрави шаҳавот ҳастанд, мехоҳанд, ки шумо дастхуши инҳирофи бузурге шавед');
INSERT INTO "pioneers" VALUES(4,28,'4:28','Аллоҳ таоло [бо ташреъи ин аҳком] мехоҳад бар шумо осон гирад. Ва инсон нотавон офарида шудааст');
INSERT INTO "pioneers" VALUES(4,29,'4:29','Эй касоне, ки имон овардаед, амволи якдигарро ба ботил [ва аз роҳҳои номашруъе ҳамчун ғасбу дуздӣ ва ришва] нахӯред, магар ин ки тиҷорате бо ризоияти шумо [анҷом гирифта] бошад; ва [ҳамчунин] якдигарро накушед ва худкушӣ накунед; ҳамоно Аллоҳ таоло [нисбат] ба шумо меҳрубон аст [ва шуморо аз ин корҳо боз медорад]');
INSERT INTO "pioneers" VALUES(4,30,'4:30','Ва ҳар ки [огоҳона ва] аз рӯйи таҷовуз ва ситам чунин кунад, пас, ба зудӣ ӯро дар оташ [-и сӯзон] дармеоварем ва ин [кор] барои Аллоҳ таоло осон аст');
INSERT INTO "pioneers" VALUES(4,31,'4:31','[Эй муъминон] Агар аз гуноҳони бузурге, ки аз он наҳй шудаед, дурӣ кунед, гуноҳон [-и сағиратон]-ро аз шумо мезудоем ва шуморо дар ҷойгоҳи шоистаи [биҳишт] ворид мекунем');
INSERT INTO "pioneers" VALUES(4,32,'4:32','[Эй муъминон] Он чиро, ки Аллоҳ таоло ба василаи он бархе аз шуморо бар бархе дигар бартарӣ додааст, орзу макунед [то дучори ҳасад нашавед]. Мардон аз он чи ба даст овардаанд, [подошу] баҳрае доранд ва занон [низ] аз он чи ба даст овардаанд, [аҷру] баҳрае доранд ва аз бахшиши Аллоҳ таоло [чизе] бихоҳед. Бе тардид, Аллоҳ таоло ба ҳар чизе доност');
INSERT INTO "pioneers" VALUES(4,33,'4:33','Мо барои ҳар кас ворисоне қарор додаем, ки аз он чи падару модар ва хешовандон бар ҷой гузоштаанд, ирс бибаранд ва [низ] ба касоне, ки [бо онон] паймони [бародарӣ ва ёрӣ] бастаед, саҳмашонро [аз ирс] бидиҳед. Бе гумон, Аллоҳ таоло бар ҳар чизе гувоҳ аст');
INSERT INTO "pioneers" VALUES(4,34,'4:34','Мардон бар занон сарпарасту нигаҳбонанд, ба хотири он ки Аллоҳ таоло бархе аз эшонро бар бархе бартарӣ додааст ва [низ] ба хотири он ки аз амволи хеш [барои занон] харҷ мекунанд. Пас, занони солеҳ, [ононад, ки ҳам аз Аллоҳ таоло ва ҳам аз шавҳаронашон] фармонбардоранд [ва] ба поси он чи Аллоҳ таоло [барояшон] ҳифз кардааст [асрору ҳуқуқи шавҳарони худро] дар ғайби [онон] ҳифз мекунанд. Ва [эй шавҳарон] ба заноне, ки аз нофармониашон бим доред, панду андарз диҳед, [агар фармонбардор нашуданд] дар бистар аз эшон дурӣ кунед [ва агар таъсир накард], ононро [тавре, ки озор набинанд] бизанед. Пас, агар аз шумо итоат карданд, ҳеҷ роҳе [барои баҳонаҷӯӣ ва сарзаниш] бар онҳо маҷӯед [ва бидонед, ки] ба ростӣ, Аллоҳ таоло баландмартабаи бузург аст');
INSERT INTO "pioneers" VALUES(4,35,'4:35','Ва [шумо, эй сарпарастони зану мард] агар аз ихтилофу ҷудоӣ миёни он ду [зану шавҳар] бим доштед, пас, як довар аз хонаводаи шавҳар ва як довар аз хонаводаи зан [таъйин кунед ва барои тасмимгирӣ] бифиристед. Агар ин ду [довар] қасди ислоҳ [ва оштӣ байни завҷайн] дошта бошанд, Аллоҳ таоло миёни он ду [зану шавҳар] созгорӣ хоҳад дод. Бе гумон, Аллоҳ таоло донои огоҳ аст');
INSERT INTO "pioneers" VALUES(4,36,'4:36','Ва Аллоҳро бипарастед ва чизеро бо Ӯ шарик нагардонед ва ба падару модар некӣ кунед ва [низ] ба хешовандону ятимон ва тиҳидастону ҳамсояи хешованд ва ҳамсояи ғайри хешованд ва ҳамнишину [мусофири] дар роҳмонда ва [канизону] бардагоне, ки молики онҳо ҳастед [некӣ кунед ва бидонед, ки] яқинан, Аллоҳ таоло касеро, ки мутакаббир ва фахрфурӯш бошад, дӯст намедорад');
INSERT INTO "pioneers" VALUES(4,37,'4:37','[Ҳамон] Касоне, ки бухл меварзанд ва мардумро ба бухл вомедоранд ва он чи [аз сарвату дониш]-ро, ки Аллоҳ таоло аз фазли хеш ба онон бахшидааст, пинҳон мекунанд. Ва мо барои кофирон [ва носипосон] азоби хоркунанда муҳайё кардаем');
INSERT INTO "pioneers" VALUES(4,38,'4:38','Ва [низ барои] касоне, ки амволашонро барои [худнамоӣ ва] нишон додан ба мардум инфоқ мекунанд ва ба Аллоҳ таоло ва рӯзи охират имон надоранд [чунин шахсе ёри шайтон аст] ва касе,ки шайтон ҳамдамаш бошад, чи бад ҳамдаме дорад!');
INSERT INTO "pioneers" VALUES(4,39,'4:39','Ва барои онон чи зиёне дошт, агар ба Аллоҳ таоло ва рӯзи охират имон меоварданд ва аз он чи Аллоҳ таоло ба онон рӯзӣ додааст, инфоқ мекарданд? Ва Аллоҳ таоло ба [ҳолу аъмоли] эшон доност');
INSERT INTO "pioneers" VALUES(4,40,'4:40','Бе гумон, Аллоҳ таоло ба андозаи заррае [ба бандагонаш] ситам намекунад ва агар кори неке бошад, онро дучандон мекунад ва аз пешгоҳи худ [ба онон] подоши бузурге ато мекунад');
INSERT INTO "pioneers" VALUES(4,41,'4:41','Ва ҳол [-у рӯзи нобоварон] чи гуна бошад, он гоҳ, ки аз ҳар уммате шоҳидеро [ки паёмбари ҳамон уммат бошад] ба миён меоварем ва туро [низ эй паёмбар, дар бораи иблоғи паёми илоҳӣ] бар инон гувоҳ меоварем');
INSERT INTO "pioneers" VALUES(4,42,'4:42','Он рӯз касоне, ки кофир шуданд ва аз паёмбар нофармонӣ карданд, орзу мекунанд, ки эй кош, бо хок яксон мешуданд ва ҳеҷ суханеро наметавонанд аз Аллоҳ таоло пинҳон кунанд');
INSERT INTO "pioneers" VALUES(4,43,'4:43','Эй касоне, ки имон овардаед, дар ҳоли мастӣ ба намоз наистед, то замоне, ки бидонед чи мегӯед ва [ҳамчунин] ҳангоме ки ҷунуб ҳастед, то ғусл накардаед, ба намоз [ва масҷид] наздик нашавед, магар он ки раҳгузар бошед. Ва агар бемор ё мусофир будед, ё яке аз шумо аз макони қазои ҳоҷат омад ё бо занон омезиш кардед ва [барои вузу ё ғусл] об наёфтаед, пас, [бояд] бо хоки пок таяммум кунед, [ба ин равиш, ки] чеҳра ва дастҳоятонро [бо он] маҳс намоед. Бе гумон, Аллоҳ таоло бахшояндаи омурзгор аст');
INSERT INTO "pioneers" VALUES(4,44,'4:44','[Эй паёмбар] Оё касоне [аз яҳудиён]-ро, ки баҳрае аз китоб ба онҳо дода шудааст, надидаӣ [ки чи гуна] гумроҳиро мехаранд ва мехоҳанд шумо [муъминон низ монанди эшон] гумроҳ шавед');
INSERT INTO "pioneers" VALUES(4,45,'4:45','Ва [эй муъминон] Аллоҳ таоло ба душманони шумо донотар аст ва кофӣ аст, ки Аллоҳ таоло ёр [-у корсозатон] бошад ва кофист, ки Аллоҳ таоло ёвар [-у мададгоратон] бошад');
INSERT INTO "pioneers" VALUES(4,46,'4:46','Бархе аз яҳудиён, калимоти [Аллоҳ таоло]-ро аз ҷойи худ таҳриф мекунанд ва бо печондани забони худ ва ба қасди таъна задан дар дин [бо даромехтани забони ибрӣ бо арабӣ ба паёмбар] мегӯянд: "Шунидем ва нофармонӣ кардем" ва "Бишнав, ки [кош] ношунаво гардӣ"! Ва [низ ба тамасхур мегӯянд]: "Роъино" [ки дар ибрӣ мафҳуми тавҳиномезе дорад]. Вале агар онон мегуфтанд: "Шунидем ва итоат кардем" ва "[суханони моро] бишнав ва ба мо муҳлат бидеҳ", ҳатман, барояшон беҳтар ва дурусттар буд. Вале Аллоҳ таоло ононро ба хотири куфрашон лаънат кардааст. Пас, ҷуз [гурӯҳи] андак имон намеоваранд');
INSERT INTO "pioneers" VALUES(4,47,'4:47','Эй аҳли китоб, ба он чи ки [бар Муҳаммад] нозил кардем ва китоби осмонӣ шуморо тасдиқ мекунад, имон биёваред, пеш аз он ки чеҳраҳоро маҳв кунем ва онҳоро [ҳамчун] пушти сарашон [софу зишт] бигардонем, ё эшонро ҳамчун асҳоби сабт [яҳудиёни нофармоне, ки рӯзи шанбе моҳӣ гирифтанд] лаънат кунем [ва ба шакли маймун дароварем] ва [бидонед, ки] фармони Аллоҳ таоло, ҳатман, ба вуқуъ мепайвандад');
INSERT INTO "pioneers" VALUES(4,48,'4:48','Бе гумон, Аллоҳ таоло ин гуноҳро, ки ба Ӯ ширк оварда шавад, намебахшад ва ғайр аз онро – барои ҳар ки бихоҳад – мебахшад; ва ҳар ки ба Аллоҳ таоло ширк варзад, яқинан, гуноҳи бузург ба гардан гирифтааст');
INSERT INTO "pioneers" VALUES(4,49,'4:49','[Эй паёмбар] Оё надидӣ касонеро, ки хештанро пок мешуморанд [ва худситоӣ мекунанд? Чунин нест], балки Аллоҳ таоло ҳар киро бихоҳад пок мегардонад ва ба эшон ба андозаи зарраи ночиз [ҳатто ба андозаи нахи рӯйи ҳастаи хурмо] ситам намешавад');
INSERT INTO "pioneers" VALUES(4,50,'4:50','[Эй паёмбар] Бингар, ки чи гуна бар Аллоҳ таоло дурӯғ мебанданд! Ва ҳамин [амал ба унвони] гуноҳи ошкор кофӣ аст');
INSERT INTO "pioneers" VALUES(4,51,'4:51','Оё афроде [аз қавми яҳуд]-ро, ки баҳрае аз китоби [осмонӣ] ба онон дода шудааст, надидаӣ [ки чи гуна] ба бутҳо ва маъбудони ботил имон меоваранд? [Ин афрод аз рӯйи дӯстӣ ва мадоро] Дар бораи касоне, ки куфр варзидаанд, мегӯянд: "Инон аз касоне, ки имон овардаанд, ҳидоятёфтатаранд"');
INSERT INTO "pioneers" VALUES(4,52,'4:52','Онон касоне ҳастанд, ки Аллоҳ таоло лаънаташон кардааст ва ҳар киро Аллоҳ таоло лаънат кунад, ҳаргиз барояш ёваре нахоҳӣ ёфт');
INSERT INTO "pioneers" VALUES(4,53,'4:53','Оё онон баҳрае аз ҳукумат доранд? [Агар ҳам доштанд] Камтарин чизе ба мардум намедоданд');
INSERT INTO "pioneers" VALUES(4,54,'4:54','Оё ба мардум [Муҳаммад ва ёронаш] ба хотири он чи Аллоҳ таоло аз фазли хеш ба онон бахшида кардааст, ҳасад меварзанд? Дар ҳақиқат, Мо [пештар низ] ба хонадони Иброҳим китобу ҳикмат додем ва ба онон фармонравоии бузурге бахшидем');
INSERT INTO "pioneers" VALUES(4,55,'4:55','Пас, бархе аз онон ба вай имон оварданд ва бархе аз онон аз ӯ рӯй гардонданд [пас, нисбат ба даъвати Муҳаммад низ чунин карданд] ва [барои ин кофирон] оташи афрӯхтаи дузах кофист');
INSERT INTO "pioneers" VALUES(4,56,'4:56','Бе гумон, касонеро, ки ба оёти мо куфр варзиданд, дар оташ [-и сӯзон] ворид хоҳем кард, ки ҳар чи пӯсташон бирён гардад, пӯстҳои дигаре ба ҷояш қарор медиҳем, то [таъми] азобро бичашанд. Ба ростӣ, ки Аллоҳ таоло шикастнопазири ҳаким аст');
INSERT INTO "pioneers" VALUES(4,57,'4:57','Ва касонеро, ки имон овардаанд ва корҳои шоиста анҷом додаанд, ба боғҳое [аз биҳишт] ворид хоҳем намуд, ки аз зери [дарахтони] он ҷӯйборҳо ҷорӣ аст; ҳамвора дар он хоҳанд монд ва дар он ҷо ҳамсарони покиза доранд ва ононро зери сояҳои густурда [ва анбӯҳ] ҷой медиҳем');
INSERT INTO "pioneers" VALUES(4,58,'4:58','Аллоҳ таоло ба шумо фармон медиҳад, ки амонатҳоро [ба таври комил] ба соҳибонашон бозгардонед ва ҳангоме ки миёни мардум доварӣ мекунед, ба адолат доварӣ кунед. Аллоҳ таоло ба амри нек пандатон медиҳад. Ба ростӣ, ки Аллоҳ таоло шунавои биност');
INSERT INTO "pioneers" VALUES(4,59,'4:59','Эй касоне, ки имон овардаед, аз Аллоҳ таоло ва паёмбар ва коргузорон [-у фармондеҳони мусулмони худ] итоат кунед ва агар дар чизе ихтилоф кардед, онро ба [Китоби] Аллоҳ таоло ва [Суннати] паёмбар бозгардонед. Агар ба Аллоҳ таоло ва рӯзи қиёмат имон доред, [яқин дошта бошед, ки] ин [бозгашт ва Қуръону Суннат бароятон] беҳтар ва хушфарҷомтар аст');
INSERT INTO "pioneers" VALUES(4,60,'4:60','[Эй паёмбар] Оё афроде [аз яҳудиён]-ро надидаӣ, ки гумон мекунанд, ба он чи бар ту нозил шуда ва он чи пеш аз ту нозил шуда имон овардаанд, ва [-ле] мехоҳанд барои доварӣ назди Тоғут [ва ҳокимони худкома] раванд? Бо он ки ба эшон дастур дода шудааст, ки ба ӯ куфр варзанд, ва [-ле] шайтон мехоҳад ононро дар гумроҳии дуру дарозе қарор диҳад');
INSERT INTO "pioneers" VALUES(4,61,'4:61','Ва чун ба онон гуфта шавад: "Ба сӯйи он чи Аллоҳ таоло нозил кардааст [Қуръон] ва ба сӯйи паёмбари [Ӯ] биёед", мунофиқонро мебинӣ, ки аз ту сахт рӯй мегардонанд');
INSERT INTO "pioneers" VALUES(4,62,'4:62','Пас, чи гуна аст, ки вақте ба [сазои] корҳои бадашон мусибате ба онон мерасад, назди ту меоянд ва ба Аллоҳ таоло савганд ёд мекунанд, ки: "Манзури мо [аз додхоҳӣ назди дигарон чизе] ҷуз некӣ ва тавофуқ [миёни тарафайн] набудааст"?');
INSERT INTO "pioneers" VALUES(4,63,'4:63','Инон ҳамон касоне ҳастанд, ки Аллоҳ таоло медонад, ки чи дар дил доранд. Пас, [эй паёмбар] аз онон рӯй бигардон ва андарзашон бидеҳ ва бо баёни расо [натоиҷи] кирдорашонро ба эшон гӯшзад кун');
INSERT INTO "pioneers" VALUES(4,64,'4:64','Ва ҳеҷ паёмбареро нафиристодем, магар барои ин ки ба фармони Аллоҳ таоло [мардум аз вай] итоат кунанд ва агар ҳангоме ки онон [бо гуноҳу нофармонӣ] бар хештан ситам мекарданд, [то зинда ҳастӣ] наздат меомаданд ва аз Аллоҳ таоло омурзиш мехостанд, бе тардид Аллоҳро тавбапазири меҳрубон меёфтанд');
INSERT INTO "pioneers" VALUES(4,65,'4:65','На! Савганд ба Парвардигорат, [эй Муҳаммад] ки онон имон намеоваранд, магар ин ки дар ихтилофоташон туро довар қарор диҳанд ва аз доварии ту дар дили хеш эҳсоси нороҳатӣ накунанд ва комилан таслим бошанд');
INSERT INTO "pioneers" VALUES(4,66,'4:66','Ва агар [монанди Бани Исроил] бар онон муқаррар мекардем, ки "[нофармонони] худро бикушед ё аз хонаву шаҳратон берун биравед", ҷуз андаке аз эшон [ба он дастурҳо] амал намекарданд. Ва агар ба он чи андарз дода шуданд, амал мекарданд, бе тардид барояшон беҳтар ва дар пойдоришон [бар дин] муассиртар буд');
INSERT INTO "pioneers" VALUES(4,67,'4:67','Ва дар ин сурат, аз пешгоҳи худ подоши бузурге ба онон медодем');
INSERT INTO "pioneers" VALUES(4,68,'4:68','Ва ба роҳи рост ҳидояташон мекардем');
INSERT INTO "pioneers" VALUES(4,69,'4:69','Ва касе, ки Аллоҳ таоло ва паёмбарро итоат кунад, [рӯзи қиёмат] ҳамнишини касоне хоҳад буд, ки Аллоҳ таоло эшонро гиромӣ доштааст, [яъне] бо паёмбарону сиддиқон [тасдиқкунандагони паёмбар] ва шаҳидону солеҳон. Ва инон чи наку рафиқоне ҳастанд!');
INSERT INTO "pioneers" VALUES(4,70,'4:70','Ин фазлу бахшиш аз ҷониби Аллоҳ таоло аст ва ҳамин бас, ки Аллоҳ таоло [ба аҳволи бандагон] доност');
INSERT INTO "pioneers" VALUES(4,71,'4:71','Эй касоне, ки имон овардаед, силоҳи худро баргиред, сипас гурӯҳ-гурӯҳ ё як порча [барои ҷиҳод бо душман] раҳсипор гардед');
INSERT INTO "pioneers" VALUES(4,72,'4:72','Ва [эй мусулмонон] бе тардид, аз [миёни] шумо касе ҳаст, ки [ба хотири тарс аз ширкат дар майдони ҷиҳод] сустӣ меварзад ва агар мусибате ба шумо бирасад, мегӯяд: "Аллоҳ таоло ба ман лутф кард, ки ҳамроҳи онон [дар ҷанг] ҳозир набудам"');
INSERT INTO "pioneers" VALUES(4,73,'4:73','Ва [-ле] агар бахшишу ғанимате аз ҷониби Аллоҳ таоло ба шумо расад, чунонки гӯё ҳаргиз миёни шумо ва ӯ [робитаи] дӯстӣ набудааст, [бо ҳасрат] мегӯяд: "Эй кош, ман низ ҳамроҳи онон будам ва ба комёбии бузурге мерасидам"');
INSERT INTO "pioneers" VALUES(4,74,'4:74','Пас, касоне, ки зиндагии дунёро ба охират фурӯхтаанд,бояд дар роҳи Аллоҳ таоло пайкор кунанд ва ба ҳар касе, ки дар роҳи Аллоҳ таоло пайкор кунад, хоҳ кушта шавад ё пирӯз гардад, ба зудӣ подоши бузурге хоҳем дод');
INSERT INTO "pioneers" VALUES(4,75,'4:75','Ва [эй муъминон] шуморо чи шудааст, ки дар роҳи Аллоҳ таоло ва [барои ёрии] мардону занон ва кӯдакони ситамдида пайкор намекунед? Ҳамон касоне, ки мегӯянд: "Парвардигоро, моро аз ин шаҳр [-и Макка], ки аҳлаш ситамгоранд, берун бибар ва аз сӯйи хеш барои мо ёру корсозе қарор бидеҳ ва аз ҷониби худ ёвару мададгоре бароямон бигумор"');
INSERT INTO "pioneers" VALUES(4,76,'4:76','Онон, ки имон овардаанд, дар роҳи Аллоҳ таоло пайкор мекунанд ва онон, ки кофиранд, дар роҳи Тоғут [ва ҳокимони худкома] меҷанганд. Пас, бо ёрони шайтон биҷангед, ки ҳатман, найранг [ва нақшаи] шайтон заиф аст');
INSERT INTO "pioneers" VALUES(4,77,'4:77','[Эй паёмбар] Оё касонеро надидӣ, ки [дар Макка] ба онҳо гуфта шуд: "[Акнун] Даст аз ҷанг бардоред ва намоз барпо доред ва закот бипардозед", ва [-ле] чун [дар Мадина] ҷиҳод бар онон муқаррар шуд, он гоҳ гурӯҳе аз эшон аз мардум [-и мушрики Макка] тарсиданд, монанди тарс аз Аллоҳ таоло ё [ҳатто] бештар ва гуфтанд: "Парвардигоро, чаро ҷиҳодро бар мо муқаррар намудӣ? Чаро ба мо то муддати кӯтоҳе муҳлат надодӣ [то аз неъматҳои дунявӣ баҳраманд гардем]"? [Ба онон] Бигӯ: "Бархӯрдорӣ [аз лаззатҳои ин] дунё ночиз аст ва барои касе, ки парҳезкор бошад, сарои охират беҳтар аст ва [дар он ҷо] кучактарин ситаме ба шумо нахоҳад шуд');
INSERT INTO "pioneers" VALUES(4,78,'4:78','Ҳар куҷо бошед, марг шуморо дармеёбад, ҳарчанд дар бурҷҳои устувор бошед. Ва агар ба онон [мунофиқон] хайре [ҳамчун фарзанд ва моли фаровон] бирасад, мегӯянд: "Ин аз ҷониби Аллоҳ таоло аст" ва агар шарре [ҳамчун маргу зиёни молӣ] ба эшон бирасад, [расули Аллоҳро бадном медонанд ва ба ӯ] мегӯянд: "Ин аз ҷониби туст". [Эй паёмбар, ба онон] Бигӯ: "Ҳама [чиз] аз ҷониби Аллоҳ таоло аст". Чаро ин қавм ҳозир нестанд, ки суханеро дарк кунанд');
INSERT INTO "pioneers" VALUES(4,79,'4:79','[Эй инсон] Он чи аз некиҳо ба ту мерасад, аз ҷониби Аллоҳ таоло аст ва он чи аз бадиҳо ба ту мерасад, аз ҷониби худи туст. Ва [эй паёмбар] Мо туро барои [ҳидояти] мардум фиристодем ва гувоҳии Аллоҳ таоло [дар ин бора] кофӣ аст');
INSERT INTO "pioneers" VALUES(4,80,'4:80','Ҳар ки аз паёмбар итоат кунад, дар хақиқат, аз Аллоҳ таоло итоат кардааст ва ҳар ки рӯйгардон шавад, [аз ӯ андуҳгин мабош, эй паёмбар, зеро Мо ҳаргиз] туро бар онон нигаҳбон [-у муроқиб] нафиристодаем');
INSERT INTO "pioneers" VALUES(4,81,'4:81','Онон [назди ту] мегӯянд: "Фармонбардорем", вале чун аз ҳузурат берун мераванд, гурӯҳе аз онон шабона чизе ҷуз он чи ту гуфтаӣ, тадбир мекунанд ва Аллоҳ таоло ҳар чиро, ки шабона дар сар мепарваранд, сабт мекунад. Пас, аз онон рӯй бигардон ва бар Аллоҳ таоло таваккал кун ва ҳамин бас, ки Аллоҳ таоло [муроқибу] коргузор [-и ту] бошад');
INSERT INTO "pioneers" VALUES(4,82,'4:82','Оё дар Қуръон намеандешанд, ки агар аз сӯйи [касе] ғайр аз Аллоҳ таоло буд, ҳатман, ихтилоф [ва таноқузи] бисёре дар он меёфтанд?');
INSERT INTO "pioneers" VALUES(4,83,'4:83','Ҳангоме ки хабаре аз эминӣ [ва пирӯзӣ] ё тарс [ва шикаст] ба онон мерасад, [бидуни ҳеҷ таҳқиқе] онро фош месозанд, ва [-ле] агар онро ба паёмбар ва фармондеҳонашон бозгардонанд, афроде аз онон, ки аҳли [фаҳму] истинбот ҳастанд, ҳатман, [маслиҳати эълом ё китмони ахборро беҳтар] медонанд. Ва [эй муъминон] агар фазлу раҳмати Аллоҳ таоло бар шумо набуд, ҷуз андаке [ҳамагӣ] аз шайтон пайравӣ мекардед');
INSERT INTO "pioneers" VALUES(4,84,'4:84','Пас, [эй паёмбар] дар роҳи Аллоҳ таоло пайкор кун. Ту уҳдадори касе ҷуз худ нестӣ, ва [-ле] муъминонро [низ ба ҷиҳод] ташвиқ кун. Умед аст, ки Аллоҳ таоло осеби кофиронро аз шумо боздорад ва Аллоҳ таоло қудраташ бештар ва кайфараш сахттар аст');
INSERT INTO "pioneers" VALUES(4,85,'4:85','Касе, ки шафоати писандидае кунад, аз [хайри] он насибе хоҳад дошт ва касе, ки шафоати нописанде кунад, аз [шарри] он насибе хоҳад дошт. Ва Аллоҳ таоло ҳамвора бар ҳар чизе муроқиб аст');
INSERT INTO "pioneers" VALUES(4,86,'4:86','[Эй муъминон] Ҳар гоҳ бо дуруди самимона ба шумо салом гӯянд, шумо посухи беҳтар ва ё ҳамонанди он бигӯед, [чаро ки] бе тардид, Аллоҳ таоло ҳамвора бар ҳар чизе ҳисобгар аст');
INSERT INTO "pioneers" VALUES(4,87,'4:87','Аллоҳ таоло [ки маъбуди ростин аст ва] ҳеҷ маъбуде ҷуз Ӯ нест, яқинан, ҳамагӣ шуморо рӯзи қиёмат, ки тардиде дар [вуқуи] он нест, ҷамъ мекунад. Ва кист, ки аз Аллоҳ таоло ростгӯйтар бошад?');
INSERT INTO "pioneers" VALUES(4,88,'4:88','[Эй муъминон] Шуморо чи пеш омадааст, ки дар мавриди [тарзи рафтор бо] мунофиқон ду гурӯҳ шудаед, ҳол он ки Аллоҳ таоло ононро ба хотири он чи анҷом додаанд, сарнагун сохта [ва ба куфру гумроҳӣ бозгардонда] аст? Оё мехоҳед, касеро, ки Аллоҳ таоло гумроҳ кардааст, ҳидоят кунед? Ва [-ле] ҳар киро Аллоҳ таоло гумроҳ кунад, роҳе [ба сӯйи ҳидоят] барояш нахоҳӣ ёфт');
INSERT INTO "pioneers" VALUES(4,89,'4:89','Онон [мунофиқон] орзу мекунанд, ки шумо низ ҳамчун эшон куфр биварзед, то бо ҳам баробар шавед. Пас, ҳеҷ як аз ононро ба дӯстӣ нагиред, магар он ки [мусулмон шаванд ва] дар роҳи Аллоҳ таоло ҳиҷрат кунанд. Пас, агар аз [ислом ва ҳиҷрат] рӯй бартофтанд [ва рафторашонро идома доданд], ҳар ҷо ки онҳоро ёфтед, дастгирашон кунед ва бикушедашон ва аз онон дӯсту ёваре ихтиёр накунед');
INSERT INTO "pioneers" VALUES(4,90,'4:90','Магар онҳое, ки бо ҳампаймонони шумо паймон бастаанд ё онҳое, ки наздатон меоянд ва аз ҷанг бо шумо ё қавми худ ба танг омадаанд. Ва агар Аллоҳ таоло мехост, ононро бар шумо мусаллат мекард, ки бо шумо биҷанганд, пас, агар аз шумо канорагирӣ карданд ва бо шумо наҷангиданд ва таслим [-у фармонбардор]-и шумо шуданд, [бидонед, ки дигар] Аллоҳ таоло барои шумо роҳе [барои таҷовуз] бар онон қарор надодааст');
INSERT INTO "pioneers" VALUES(4,91,'4:91','[Эй муъминон] Ба зудӣ [гурӯҳи] дигаре [аз мунофиқон]-ро хоҳед ёфт, ки [зоҳиран] мехоҳанд аз шумо ва қавми худ дар амон бошанд, ва [-ле] ҳар гоҳ ба фитнаи [ширк] бозгардонда шаванд, дар он фурӯ мераванд. Пас, агар аз шумо канора нагирифтанд ва аз дари сулҳу оштӣ ворид нашуданд ва даст аз [ҷанг бо] шумо барнадоштанд, он гоҳ ононро ҳар куҷо ёфтед, дастгирашон кунед ва бикушедашон. Онон ҳастанд, ки мо барои шумо алайҳи эшон далели ошкор [барои дастгирӣ ва қатлашон] қарор додаем');
INSERT INTO "pioneers" VALUES(4,92,'4:92','Ҳеҷ муъмине муҷоз нест [иҷозат надорад], ки муъминеро бикушад, магар ба иштибоҳ [ва беқасд]. Ва касе, ки муъминеро ба иштибоҳ бикушад, бояд як бардаи муъмин озод кунад ва хунбаҳое ба хонаводаи ӯ [ба ворисони мақтул] бипардозад, магар ин ки онҳо гузашт кунанд. Ва агар [мақтул] аз қавме бошад, ки душманони шумо ҳастанд ва [худи] ӯ муъмин аст, озод кардани як бардаи муъмин [ба унвони каффора кофӣ аст, зеро кофир аз муъмин ирс намебарад] ва агар [мақтул муъмин набошад, вале] аз қавме бошад, ки миёни шумо ва эшон паймоне барқарор аст, бояд хунбаҳое ба хонаводаи ӯ бипардозад ва як бардаи муъминро [низ] озод кунад. Ва ҳар ки [тавони молӣ надошт ё бардае барои озод кардан] наёфт, пас, [бояд] ба унвони тавбае ба даргоҳи Аллоҳ таоло ду моҳ паёпай рӯза бигирад. Ва ҳамвора Аллоҳ таоло донои ҳаким аст');
INSERT INTO "pioneers" VALUES(4,93,'4:93','Ва ҳар кас муъминеро амдан ба қатл бирасонад, кайфараш дузах аст, [ки] дар он ҷовидона мемонад ва Аллоҳ таоло бар вай ғазаб мекунад ва ӯро аз раҳмати хеш дур месозад ва азоби бузурге барояш омода сохтааст');
INSERT INTO "pioneers" VALUES(4,94,'4:94','Эй касоне, ки имон овардаед, ҳангоме ки дар роҳи Аллоҳ таоло [барои ҷиҳод] раҳсипор шудед, [паҳлуҳои корро хуб] баррасӣ кунед ва ба касе, ки изҳори сулҳу ислом намуд, нагӯед: "Ту муъмин нестӣ", то [битавонед ӯро бикушед ва ғаноиму] сармояи нопойдори дунёро ба даст оваред, зеро ғаниматҳои бисёре [барои шумо] назди Аллоҳ таоло аст. Шумо низ қаблан ин чунин будед [ва имонатонро пинҳон мекардед], сипас Аллоҳ таоло бар шумо миннат ниҳод [ва ҳидоят шудед]. Бинобар ин, ба дурустӣ баррасӣ кунед, [чаро ки] бе тардид, Аллоҳ таоло ба он чи анҷом медиҳед, огоҳ аст');
INSERT INTO "pioneers" VALUES(4,95,'4:95','Он муъминон [-и хонанишин]-е, ки бидуни ҳеҷ беморӣ ва ранҷе аз ҷиҳод бознишастанд, бо муҷоҳидоне, ки дар роҳи Аллоҳ таоло бо молу ҷони худ ҷиҳод карданд, [ҳаргиз] яксон нестанд. Аллоҳ таоло касонеро, ки бо молу ҷонашон ҷиҳод карданд, бар хонанишинон ба мартабаи [бузург] бартарӣ бахшидааст ва Аллоҳ таоло бар ҳар як [аз муҷоҳидон ё хонанишинони маъзур] ваъдаи наку додааст, ва [-ле] Аллоҳ таоло муҷоҳидонро бар хонанишинон бо подоши бузург бартарӣ додааст');
INSERT INTO "pioneers" VALUES(4,96,'4:96','[Подоше, ки] Ба унвони дараҷоту омурзиш ва раҳмате аз [пешгоҳи] Ӯ [насибашон мегардад] ва Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(4,97,'4:97','Касоне, ки [бо мондан дар сарзамини куфр] нисбат ба хеш ситамгор буданд, [вақте] фариштагон ҷонашонро мегиранд, [ба онон] мегӯянд: "Дар чи [ҳол] будед"? Посух медиҳанд: "Мо дар замин аз ситамдидагон будем". [Фариштагон] Мегӯянд: "Оё замини Аллоҳ таоло густурда набуд, то дар он муҳоҷират кунед [ва имонатонро ҳифз намоед]"? Пас, ҷойгоҳашон дузах аст ва чи бад саранҷомест!');
INSERT INTO "pioneers" VALUES(4,98,'4:98','Магар мардону занон ва кӯдакони ситамдидае, ки наметавонанд чорае бияндешанд ва роҳ ба ҷойе намебаранд');
INSERT INTO "pioneers" VALUES(4,99,'4:99','Пас, онон [ки узре доранд] умед аст, ки Аллоҳ таоло аз эшон даргузарад ва ҳамвора Аллоҳ таоло бахшояндаи омурзгор аст');
INSERT INTO "pioneers" VALUES(4,100,'4:100','Ва ҳар ки дар роҳи Аллоҳ таоло ҳиҷрат кунад, дар замин сарпаноҳи бисёр ва кушоише [дар рӯзӣ ва кор] меёбад ва касе, ки ба қасди ҳиҷрат ба сӯйи Аллоҳ таоло ва паёмбараш аз хонааш хориҷ шавад ва он гоҳ маргаш фаро расад, ҳатман, подошаш бар [уҳдаи] Аллоҳ таоло аст ва Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(4,101,'4:101','Ва [эй муъминон] ҳар гоҳ дар замин сафар кунед, агар тарсидед, онон, ки куфр варзиданд, бар шумо зиёне бирасонанд, гуноҳе бар шумо нест, ки аз [ракаоти] намоз бикоҳед [ва онро шикаста бихонед], зеро кофирон ҳамвора бароятон душмани ошкоре ҳастанд');
INSERT INTO "pioneers" VALUES(4,102,'4:102','Ва [эй паёмбар] чун [вақти ҷанг] дар миёни онон будӣ ва барояшон намоз барпо доштӣ, пас, бояд гурӯҳе аз онон бо ту [ба намоз] истанд ва [ҳамчунин] бояд ҷангафзорҳояшонро бо худ баргиранд [ва як ракаатро ҳамроҳи ту бихонанд ва намозро ба сурати фуродо (танҳо) идома диҳанд] ва чун саҷда карданд [ва намозашон тамом шуд], бояд пушти саратон қарор гиранд ва бояд он дастае, ки намоз нахондаанд, биёянд ва [як ракъат] бо ту намоз бихонанд [ва пас аз ин ки намозат ба поён расид, идомаи намозашонро комил кунанд] ва [онон низ] бояд эҳтиёт кунанд ва ҷангафзорҳояшонро [дар намоз бо худ] баргиранд, [зеро] кофирон умед доранд, ки шумо аз ҷангафзорҳо ва таҷҳизоти худ ғофил шавед, то якбора бар шумо битозанд. Ва агар аз борон [ё монеаҳои дигар] дар ранҷ будед ва [ё] бемор [ва маҷруҳ] будед, гуноҳе бар шумо нест, ки ҷангафзорҳои худро [бар замин] бигзоред, вале ҷониби эҳтиётро риоя кунед. Ба ростӣ, ки Аллоҳ таоло барои кофирон азоби хоркунанда муҳайё кардааст');
INSERT INTO "pioneers" VALUES(4,103,'4:103','Пас, чун намозро ба поён расонидед, Аллоҳро [дар ҳама ҳол] истода ва нишаста ва бар паҳлу хуфта [хобида] ёд кунед; ва ҳар гоҳ оромиш ёфтед [ва тарс аз байн рафт], намозро [комил] барпо доред, [чаро ки] бе тардид, намоз [фаризаест, ки] дар авқоти муайяне бар муъминон воҷиб шудааст');
INSERT INTO "pioneers" VALUES(4,104,'4:104','Ва [эй муъминон] дар таъқиби он гурӯҳ [душман] сустӣ накунед. Агар шумо [аз хастагӣ ва ҷароҳат] ранҷ мебаред, онон низ чунон ки шумо ранҷ мебаред, ранҷ бурдаанд, вале шумо умеде ба Аллоҳ таоло доред, ки онон надоранд. Ва Аллоҳ таоло донои ҳаким аст');
INSERT INTO "pioneers" VALUES(4,105,'4:105','[Эй паёмбар] Мо [ин] китобро ба ҳақ бар ту нозил кардаем, то ба [муҷиби] он чи Аллоҳ таоло ба ту омӯхтааст, дар миёни мардум доварӣ кунӣ ва мудофеи хиёнаткорон мабош');
INSERT INTO "pioneers" VALUES(4,106,'4:106','Ва аз Аллоҳ таоло омурзиш бихоҳ. Бе тардид, Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(4,107,'4:107','Ва аз касоне, ки [бо иртикоби гуноҳ] ба худ хиёнат мекунанд, дифоъ накун, [чаро ки] бе гумон, Аллоҳ таоло ҳар киро хиёнаткор [ва] гунаҳгор бошад, дӯст намедорад');
INSERT INTO "pioneers" VALUES(4,108,'4:108','Онҳо [зишткориҳои худро] аз мардум пинҳон медоранд, вале наметавонанд, чизеро аз Аллоҳ таоло пинҳон кунанд ва [ҳол он ки] Ӯ ҳамроҳашон аст, он гоҳ, ки шабона чораандешӣ мекунанд ва [аз сари дурӯғу ифтиро] суханоне мегӯянд, ки Ӯ намеписандад ва Аллоҳ таоло ҳамвора ба он чи мекунанд, иҳота дорад');
INSERT INTO "pioneers" VALUES(4,109,'4:109','Ҳон! Шумо касоне ҳастед, ки дар зиндагии дунё аз онон дифоъ кардед. Пас, кист, ки рӯзи қиёмат дар баробари Аллоҳ таоло аз онон дифоъ кунад? Ё чи касест, ки [муроқибу] коргузорашон бошад?');
INSERT INTO "pioneers" VALUES(4,110,'4:110','Ва ҳар ки амали ношоистае муртакиб шавад ё [бо нофармонӣ аз дастури Аллоҳ таоло ва расулаш] ба худ ситам кунад, [ва] сипас аз Аллоҳ таоло омурзиш бихоҳад, Аллоҳро омурзандаи меҳрубон хоҳад ёфт');
INSERT INTO "pioneers" VALUES(4,111,'4:111','Ва ҳар ки гуноҳе муртакиб шавад, пас, танҳо ба зиёни хеш [чунин] мекунад ва Аллоҳ таоло ҳамвора донои ҳаким аст');
INSERT INTO "pioneers" VALUES(4,112,'4:112','Ва ҳар ки хато ё гуноҳе муртакиб шавад, сипас онро бар гардани бегуноҳе бияндозад, ҳатман, [бори] буҳтон ва гуноҳи ошкореро бар дӯш гирифтааст');
INSERT INTO "pioneers" VALUES(4,113,'4:113','Ва [эй паёмбар] агар фазли Аллоҳу раҳматаш бар ту набуд, гурӯҳе аз онон қасд доштанд, ки туро гумроҳ кунанд [то ноодилона қазоват кунӣ], ва [-ле] ҷуз худашон [касе]-ро гумроҳ намесозанд ва ҳеҷ зиёне ба ту намерасонанд. Ва Аллоҳ таоло китоб [Қуръон] ва ҳикмат [Суннат]-ро бар ту нозил кард ва он чиро намедонистӣ, ба ту омӯхт ва ҳамвора фазли Аллоҳ таоло бар ту бузург [-у бисёр] будааст');
INSERT INTO "pioneers" VALUES(4,114,'4:114','Дар бисёре аз розгӯиҳои онон [мардум] хайре нест, магар [дар гуфтори] касе, ки ба [додани] садақае ё коре писандида ё созише миёни мардум фармон диҳад. Ва ҳар ки барои хушнудии Аллоҳ таоло чунин кунад, ба зудӣ подоши бузурге ба вай хоҳем дод');
INSERT INTO "pioneers" VALUES(4,115,'4:115','Ва ҳар кас пас аз он ки [роҳи ҳақ ва] ҳидоят барояш равшан шуд, бо паёмбар мухолифат кунад ва аз роҳе ҷуз роҳи муъминон пайравӣ кунад, ӯро ба он чи [барои худ баргузида ва аз он] пайравӣ кардааст, вомегузорем ва ба дузах мекашонем. Ва [ҳаққо, ки дузах] чи бад ҷойгоҳест!');
INSERT INTO "pioneers" VALUES(4,116,'4:116','Аллоҳ таоло [ин] гуноҳро, ки ба ӯ ширк оварда шавад, ҳатман, намеомурзад ва ба ҷуз он [ҳар гуноҳе]-ро барои ҳар ки бихоҳад, меомурзад ва ҳар ки ба Аллоҳ таоло ширк оварад, пас, бе тардид, дар гумроҳии дуру дарозе афтодааст');
INSERT INTO "pioneers" VALUES(4,117,'4:117','[Мушрикон] Ба ҷойи Аллоҳ таоло бутҳои модинае [ҳамчун Лоту Уззо]-ро мехонанд ва [дар ҳақиқат] ҷуз шайтони саркашро намехонанд');
INSERT INTO "pioneers" VALUES(4,118,'4:118','[Ҳамон ки] Аллоҳ таоло ӯро аз раҳмати хеш дур сохт ва ӯ гуфт: "Аз бандагони Ту саҳми муайян бархоҳам гирифт');
INSERT INTO "pioneers" VALUES(4,119,'4:119','Ва мусалламан, ононро гумроҳ мекунам ва ба орзуҳои ботил меафканам ва ба эшон дастур медиҳам, ки гӯши чаҳорпоёнро бишкофанд ва водорашон мекунам, то офариниши Аллоҳро тағйир диҳанд". Ва ҳар ки шайтонро ба ҷойи Аллоҳ таоло дӯст [-у корсози] худ гирад, ҳатман, зиёни ошкор кардааст');
INSERT INTO "pioneers" VALUES(4,120,'4:120','[Оре] Шайтон ба онон ваъда медиҳад ва дар [доми] орзуҳо меандозадашон ва ба онон ваъдае ҷуз фиреб намедиҳад');
INSERT INTO "pioneers" VALUES(4,121,'4:121','Онон [пайравони шайтон] ҷойгоҳашон дузах аст ва аз он ҳеҷ роҳи фироре намеёбанд');
INSERT INTO "pioneers" VALUES(4,122,'4:122','Ва касоне, ки имон овардаанд ва корҳои шоиста анҷом додаанд, ба зудӣ ононро дар боғҳое [аз биҳишт] ворид мекунем, ки ҷӯйборҳо аз зери [дарахтони] он ҷорист, ҷовидона дар он хоҳанд монд, ваъдаи Аллоҳ таоло ҳақ аст ва кист, ки дар гуфтор [-у ваъдаҳояш] аз Аллоҳ таоло ростгӯйтар бошад?!');
INSERT INTO "pioneers" VALUES(4,123,'4:123','[Эй мусулмонон, наҷоту растагорӣ вобаста] Ба орзуҳои шумо ва орзуҳои аҳли китоб нест. Ҳар кас кори ношоистае анҷом диҳад, ба кайфараш хоҳад расид ва ҷуз Аллоҳ таоло барои худ дӯсту ёваре нахоҳад ёфт');
INSERT INTO "pioneers" VALUES(4,124,'4:124','Ва ҳар кас, аз зану мард, ки корҳои шоиста кунад ва муъмин бошад, [ҳамаи] онон вориди биҳишт мешаванд ва ба андозаи зарраи ночизе [ҳатто ба андозаи шикофи ҳастаи хурмо низ] ба эшон ситам нахоҳад шуд');
INSERT INTO "pioneers" VALUES(4,125,'4:125','Ва чи касе некоинтар аз касест, ки худро таслими Аллоҳ таоло карда ва накукор аст ва аз оини Иброҳими ҳақгаро пайравӣ намудааст? Ва Аллоҳ таоло Иброҳимро ба дӯстии худ баргузид');
INSERT INTO "pioneers" VALUES(4,126,'4:126','Ва он чи дар осмонҳо ва замин аст, аз они Аллоҳ таоло аст ва Аллоҳ таоло ҳамвора ба ҳар чизе иҳота дорад');
INSERT INTO "pioneers" VALUES(4,127,'4:127','Ва [эй паёмбар, мусулмонон] аз ту дар бораи занон назархоҳӣ мекунанд; Бигӯ: "Аллоҳ таоло дар бораи онон фатво медиҳад ва дар он чи аз китоб [Қуръон] бар шумо хонда мешавад, [низ] омадааст, ки [ҳукми Ӯ] дар бораи духтарони ятиме, ки ҳаққи муқаррарашон [аз маҳрияи ворис]-ро намепардозед ва майл ба издивоҷ бо онон надоред [ва аз рӯйи тамаъ дар амволашон ононро аз издивоҷ манъ мекунед] ва [ҳамчунин, дастураш дар мавриди] кӯдакони нотавон [ин аст], ки: "Дар ҳаққи ятимон ба адлу инсоф бикӯшед" ва ҳар он чи аз некиҳо, ки анҷом медиҳед, Аллоҳ таоло ба он огоҳ аст');
INSERT INTO "pioneers" VALUES(4,128,'4:128','Ва агар зане аз носозгорӣ ё рӯйгардонии шавҳараш бим дошт, пас, бар он ду нафар гуноҳе нест, ки бо [сарфи назар аз бархе ҳуқуқ ва вазоиф] бо якдигар оштӣ кунанд. Ва оштӣ беҳтар [аз талоқ] аст ва [-ле тамаъ ва] бухл нафсҳоро фаро гирифтааст [ва ҳеҷ кас аз ҳаққи хеш намегузарад] ва агар некӣ кунед ва парҳезкорӣ пеша созед, ҳатман, Аллоҳ таоло ба он чи анҷом медиҳед, огоҳ аст');
INSERT INTO "pioneers" VALUES(4,129,'4:129','Ва [шумо мардон] ҳарчанд бикӯшед, ҳаргиз наметавонед [аз назари муҳаббати қалбӣ] миёни занон адолат барқарор кунед, пас, яксара [ба яке майл накунед ва] аз яке рӯ нагардонед, ки ӯро билотаклиф раҳо кунед. Ва агар созиш намоед ва парҳезгорӣ кунед, бе тардид, Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(4,130,'4:130','Ва агар он ду [зану шавҳар] аз якдигар ҷудо шаванд, Аллоҳ таоло ҳар яке аз ононро аз кушоиш [ва бахшиши] хеш бениёз месозад ва Аллоҳ таоло ҳамвора кушоишгару ҳаким аст');
INSERT INTO "pioneers" VALUES(4,131,'4:131','Ва он чи дар осмонҳо ва замин аст аз они Аллоҳ таоло аст ва мо ба касоне, ки пеш аз шумо ба онон китоб [-и осмонӣ] дода шуд ва [ҳамчунин] ба шумо супориш кардем, ки: "Аз Аллоҳ таоло парво кунед [битарсед]" ва агар кофир шудед, [бидонед, ки] он чи дар осмонҳо ва замин аст, аз они Аллоҳ таоло аст ва Аллоҳ таоло бениёзи сутуда аст');
INSERT INTO "pioneers" VALUES(4,132,'4:132','Ва он чи дар осмонҳо ва замин аст, аз они Аллоҳ таоло аст ва ҳамин бас, ки Аллоҳ таоло [муроқибу] коргузор [-и бандагон] бошад');
INSERT INTO "pioneers" VALUES(4,133,'4:133','Эй мардум, агар Ӯ бихоҳад, шуморо аз миён мебарад ва дигаронро меоварад ва Аллоҳ таоло ҳамвора бар ин [кор] тавоност');
INSERT INTO "pioneers" VALUES(4,134,'4:134','Ҳар ки [дар қиболи корҳояш] подоши дунёро мехоҳад, пас, [бидонад, ки] подоши дунё ва охират назди Аллоҳ таоло аст ва Аллоҳ таоло ҳамвора шунавову биност');
INSERT INTO "pioneers" VALUES(4,135,'4:135','Эй касоне, ки имон овардаед, барподорандаи адолат бошед [ва] барои Аллоҳ таоло шаҳодат диҳед, агарчи ба зиёни худатон ё падару модар ва наздикон [-и шумо] бошад. Агар [яке аз ду тарафи даъво] тавонгар ё ниёзманд бошад, [боз ҳам] Аллоҳ таоло [аз шумо] ба он ду нафар сазовортар аст, пас, аз ҳавои нафс пайравӣ накунед, ки [аз ҳақ] мунҳариф мешавед. Ва агар забонро бигардонед, [ва ҳақро таҳриф кунед] ё [аз изҳори он] худдорӣ намоед, мусалламан, Аллоҳ таоло ба он чи анҷом медиҳед, огоҳ аст');
INSERT INTO "pioneers" VALUES(4,136,'4:136','Эй касоне, ки имон овардаед, ба Аллоҳ таоло ва паёмбараш ва китобе, ки бар ӯ нозил кардааст ва [ба ҳар] китобе, ки пеш аз ин фиристодааст, имон биёваред [ва бар он пойдор бошед] ва ҳар, ки ба Аллоҳ таоло ва фариштагони Ӯ ва китобҳову паёмбаронаш ва рӯзи қиёмат куфр биварзад, бе тардид, дар гумроҳии дуру дарозе афтодааст');
INSERT INTO "pioneers" VALUES(4,137,'4:137','Бе тардид, касоне ки имон оварданд, сипас кофир шуданд ва пас аз он имон оварданд ва боз кофир шуданд ва он гоҳ бар куфри [худ] афзуданд, Аллоҳ таоло ҳаргиз бар он нест, ки ононро бибахшояд ва ба роҳ [-и рост, ки ба саодати абадӣ хатм мегардад] ҳидояташон кунад');
INSERT INTO "pioneers" VALUES(4,138,'4:138','[Эй паёмбар] Ба мунофиқон хабар бидеҳ, ки азоби дарднок барояшон [дар пеш] аст');
INSERT INTO "pioneers" VALUES(4,139,'4:139','[Ҳамон] Касоне, ки кофиронро ба ҷойи муъминон ба дӯстӣ бармегузинанд. Оё сарбаландиро назди онон меҷӯянд? Пас, [бояд бидонанд, ки] ҳамаи сарбаландӣ аз они Аллоҳ таоло аст');
INSERT INTO "pioneers" VALUES(4,140,'4:140','Ва [эй муъминон] мусалламан, [Аллоҳ таоло ин ҳукмро] дар ин китоб [Қуръон] бар шумо нозил кардааст, ки: "Чун шунидед [афроде] оёти Аллоҳро инкор мекунанд ва онро ба тамасхур мегиранд, бо онон нанишинед, то ба сухани дигар бипардозанд. [Агар бо онон биншинед] Дар ин сурат, ҳатман, шумо [низ] ҳаммонанди онон хоҳед буд". Бе тардид, Аллоҳ таоло ҳамаи мунофиқон ва кофиронро дар дузах гирд меоварад');
INSERT INTO "pioneers" VALUES(4,141,'4:141','[Ҳамон] Касоне, ки [аз сари бадхоҳӣ ҳамвора] шуморо зери назар доранд, агар пирӯзӣ ва кушоише аз ҷониби Аллоҳ таоло насибатон гардад, мегӯянд: "Магар мо бо шумо набудем"? Ва агар баҳрае насиби кофирон гардад, [ба онон] мегӯянд: "Магар мо пуштибонатон набудем ва шуморо аз [газанди] муъминон боз надоштем"? Пас, Аллоҳ таоло рӯзи қиёмат миёни шумо доварӣ мекунад ва Аллоҳ таоло ҳаргиз барои кофирон роҳе [барои тасаллут] бар муъминон қарор надодааст');
INSERT INTO "pioneers" VALUES(4,142,'4:142','Бе тардид, мунофиқон [бо дурӯғгӯӣ ва тазоҳурашон] бо Аллоҳ таоло фиребкорӣ мекунанд [ва ҳол он ки ба сазои рафторашон] Аллоҳ таоло фиребдиҳандаи ононаст; ва чун ба намоз бархезанд, бо сустӣ ва коҳилӣ бармехезанд [ва] бо мардум риёкорӣ мекунанд ва Аллоҳро ҷуз андаке ёд намекунанд');
INSERT INTO "pioneers" VALUES(4,143,'4:143','[Мунофиқон] Байни ин ду [гурӯҳ: муъминон ва кофирон] саргаштаанд, на бо инонанд ва на бо онон. Ва [эй паёмбар] ҳар киро Аллоҳ таоло гумроҳ кунад, роҳе барои [ҳидояти] ӯ нахоҳӣ ёфт');
INSERT INTO "pioneers" VALUES(4,144,'4:144','Эй касоне, ки имон овардаед, кофиронро ба ҷойи муъминон, дӯстони [худ] нагиред. Оё мехоҳед, барои Аллоҳ таоло далели ошкоре ба зиёни худ [барои азоб] падид оваред?');
INSERT INTO "pioneers" VALUES(4,145,'4:145','Ҳамоно мунофиқон дар фурӯтарин дараҷоти дузаханд ва ҳаргиз ёваре барояшон нахоҳӣ ёфт');
INSERT INTO "pioneers" VALUES(4,146,'4:146','Магар онон, ки тавба карданд ва [ботини хешро] ислоҳ намуданд ва ба [китоби] Аллоҳ таоло тамассук ҷустанд ва дини худро барои Аллоҳ таоло холис гардонданд. Пас, инон бо муъминонанд ва Аллоҳ таоло ба муъминон подоши бузурге хоҳад дод');
INSERT INTO "pioneers" VALUES(4,147,'4:147','Агар шукр кунед ва имон оваред, Аллоҳ таоло чи ниёзе ба муҷозоти шумо дорад? Ва Аллоҳ таоло ҳамвора шукргузори доност');
INSERT INTO "pioneers" VALUES(4,148,'4:148','Аллоҳ таоло бадгӯии ошкорро дӯст намедорад, магар аз ҷониби касе, ки ба ӯ ситам шудааст [ва ночор забон ба шикояту нафрин мекушояд]. Ва Аллоҳ таоло ҳамвора шунавову доност');
INSERT INTO "pioneers" VALUES(4,149,'4:149','Агар кори некро ошкор кунед ё онро пинҳон доред ё аз бадӣ [ва ситаме, ки ба шумо расидааст] даргузаред, пас, [бидонед, ки] бе тардид, Аллоҳ таоло ҳамвора омурзандаи тавоност');
INSERT INTO "pioneers" VALUES(4,150,'4:150','Касоне, ки ба Аллоҳ таоло ва паёмбаронаш куфр меварзанд ва мехоҳанд миёни Аллоҳ таоло ва паёмбаронаш ҷудоӣ андозанд ва мегӯянд: "Мо ба баъзе имон дорем ва баъзеро инкор мекунем" ва мехоҳанд дар ин [миён байни куфру имон барои худ] роҳе баргузинанд');
INSERT INTO "pioneers" VALUES(4,151,'4:151','Ононанд, ки ба ростӣ, кофиранд ва мо барои кофирон азоби хоркунандае муҳайё кардаем');
INSERT INTO "pioneers" VALUES(4,152,'4:152','Ва касоне, ки ба Аллоҳ таоло ва паёмбаронаш имон оварданд ва миёни ҳеҷ кадом аз онон фарқ намегузоранд, ба зудӣ [Аллоҳ таоло] подошашонро ато мекунад ва Аллоҳ таоло ҳамвора омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(4,153,'4:153','[Эй паёмбар] Аҳли китоб аз ту мехоҳанд, ки [ба нишонаи ростгӯӣ] барояшон китобе аз осмон нозил кунӣ. [Ин дархосташонро бузург машмор, зеро онон] Аз Мӯсо [чизи бузургтар ва] аҷибтар аз ин хостанд ва гуфтанд: "Аллоҳро ошкоро ба мо нишон бидеҳ". Он гоҳ ба хотири дархости ноҳақашон, соиқа [ё марге] ононро фурӯ гирифт, он гоҳ [чун Аллоҳ таоло ононро дубора зинда кард] пас аз он ки нишонаҳои ошкор барояшон омад, гӯсолапарастӣ карданд, ва [-ле мо пас аз он ки тавба карданд] аз он [гуноҳашон низ] даргузаштем ва ба Мӯсо далели ошкор бахшидем');
INSERT INTO "pioneers" VALUES(4,154,'4:154','Ва ба сабаби паймон [гирифтан аз] онон [кӯҳи] Тӯрро болои сарашон барафроштем ва ба эшон гуфтем: "Бо фурӯтанӣ аз дарвоза [-и Байт-ул-муқаддас] ворид шавед" ва [ҳамчунин] ба онон гуфтем: "Ҳурмати шанберо нашканед [ва аз сайди моҳӣ даст бикашед]" ва аз онон паймони сахту устувор гирифтем');
INSERT INTO "pioneers" VALUES(4,155,'4:155','Пас, ба [сазои] паймоншиканишон ва инкорашон нисбат ба оёти Аллоҳ таоло ва куштори ноҳаққи онон [аз] паёмбарон [лаънаташон кардем] ва [ба хотири ин] суханашон, ки: "Дилҳои мо [ҷойи сухани ҳақ нест ва] пӯшида аст", балки Аллоҳ таоло ба хотири куфрашон бар дилҳояшон муҳр задаст. Бинобар ин ҷуз андаке имон намеоваранд');
INSERT INTO "pioneers" VALUES(4,156,'4:156','Ва [низ] ба сазои куфрашон ва он туҳмати бузурге, ки ба Марям заданд');
INSERT INTO "pioneers" VALUES(4,157,'4:157','Ва [ононро лаънат кардаем ба хотири ин] суханашон, ки: "Мо Масеҳ – Исо писари Марям – паёмбари Аллоҳро куштем", дар ҳоле ки на ӯро куштанд ва на ба дор овехтанд, вале [амр] бар онон муштабаҳ шуд [ва мардеро, ки шабеҳи Исо буд, ба дор кашиданд] ва касоне, ки дар бораи ӯ ихтилоф карданд, ҳатман, дар борааш дучори тардиданд ва ҳеҷ илме бад-он надоранд, магар он ки аз гумон пайравӣ мекунанд ва яқинан, ӯро накуштанд');
INSERT INTO "pioneers" VALUES(4,158,'4:158','Балки Аллоҳ таоло ӯро ба сӯйи Хеш боло бурд. Ва Аллоҳ таоло шикастнопазири ҳаким аст');
INSERT INTO "pioneers" VALUES(4,159,'4:159','Ва [касе] аз аҳли китоб нест, магар ин ки пеш аз маргаш, ҳатман, ба ӯ [Исо] имон меоварад ва рӯзи қиёмат [низ] вай бар [куфру имони] онон гувоҳ хоҳад буд');
INSERT INTO "pioneers" VALUES(4,160,'4:160','Пас, ба сазои ситаме, ки аз яҳудиён сар зад ва ба сабаби боздоштани бисёре [аз мардум] аз роҳи Аллоҳ таоло, [мо низ] чизҳои покизаеро, ки барояшон ҳалол буд, бар онон ҳаром кардем');
INSERT INTO "pioneers" VALUES(4,161,'4:161','Ва [низ ба сабаби] рибо гирифтанашон, бо он ки аз он наҳй шуда буданд ва ин ки моли мардумро ба нораво мехӯрданд. Ва мо барои кофиронашон азоби дарднок муҳайё кардаем');
INSERT INTO "pioneers" VALUES(4,162,'4:162','Вале аз миёни онон росихони дар илм [уламои яҳуд] ва муъминон [яъне онон, ки] ба он чи бар ту нозил шудааст ва он чи ки пеш аз ту нозил шудааст, имон меоваранд ва [ҳамчунин] намозгузорону закотдиҳандагон ва имоноварандагон ба Аллоҳ таоло ва рӯзи бозпасин, ба [ҳамаи] инҳо подоши бузурге хоҳем дод');
INSERT INTO "pioneers" VALUES(4,163,'4:163','[Эй паёмбар] Бе тардид мо ба ту ваҳй фиристодем; ҳамон гуна, ки ба Нуҳу паёмбарони баъд аз ӯ ваҳй фиристодем ва [низ] ба Иброҳиму Исмоил ва Исҳоқу Яъқуб ва наводагон [-и Яъқуб] ва Исову Аюб ва Юнусу Ҳорун ва Сулаймон ваҳй фиристодем ва ба Довуд Забур додем');
INSERT INTO "pioneers" VALUES(4,164,'4:164','Ва паёмбароне [фиристодем], ки дар ҳақиқат, [саргузашти] ононро пеш аз ин барои ту ҳикоят намудем ва паёмбароне [-ро низ барангехтаем], ки [достони] эшонро бароят баён накардаем. Ва Аллоҳ таоло бо Мӯсо ошкор [ва бевосита] сухан гуфт');
INSERT INTO "pioneers" VALUES(4,165,'4:165','Паёмбароне [фиристодем], ки башоратбахшу бимдиҳанда буданд, то барои мардум пас аз [биъсати] паёмбарон дар муқобили Аллоҳ таоло [ҳеҷ баҳона ва] ҳуҷҷате набошад. Ва Аллоҳ таоло ҳамвора шикастнопазири ҳаким аст');
INSERT INTO "pioneers" VALUES(4,166,'4:166','[Эй паёмбар, кофирон паёматро инкор мекунанд] Вале Аллоҳ таоло ба [дурустии] он чи бар ту нозил кардааст, шаҳодат медиҳад. Онро ба илми хеш нозил кардааст ва фариштагон низ шаҳодат медиҳанд. Ва [барои ҳаққонияти Қуръон] кофист, ки Аллоҳ таоло гувоҳ бошад');
INSERT INTO "pioneers" VALUES(4,167,'4:167','Бе тардид, касоне, ки куфр варзиданд ва [мардумро] аз роҳи Аллоҳ таоло боздоштанд, ба гумроҳии дуру дарозе афтодаанд');
INSERT INTO "pioneers" VALUES(4,168,'4:168','Бе тардид, касоне, ки куфр варзиданд ва [бо ин кор бар худ] ситам карданд, Аллоҳ таоло ҳаргиз бар он нест, ки биёмурзадашон ва ба роҳ [-и дуруст низ] ҳидояташон намекунад');
INSERT INTO "pioneers" VALUES(4,169,'4:169','Магар роҳи ҷаҳаннам, ки ҷовидона дар он мемонанд ва ин [кор] бар Аллоҳ таоло осон аст');
INSERT INTO "pioneers" VALUES(4,170,'4:170','Эй мардум, ин паёмбар аз ҷониби Парвардигоратон [ҳидояту дини] ҳақро бароятон овардааст, пас, имон биёваред, ки ба хайр [-у салоҳ]-и шумост. Ва агар [суханашро напазиред ва] куфр биварзед, [бидонед, ки] он чи дар осмонҳо ва замин аст, аз они Аллоҳ таоло аст [ва куфри шумо бар Ӯ зиёне намерасонад] ва Аллоҳ таоло ҳамвора донои ҳаким аст');
INSERT INTO "pioneers" VALUES(4,171,'4:171','Эй [масеҳиёни] аҳли китоб, дар дини худ ғулув накунед ва дар бораи Аллоҳ таоло ҷуз [сухани] ҳақ нагӯед. Бе тардид, Масеҳ – Исо писари Марям – фиристодаи Аллоҳ таоло ва калимаи Ӯст, ки [амр кард: "Бош", пас, бедиранг мавҷуд шуд ва бад-ин сурат ба амри Аллоҳ таоло [Ҷабраил] вайро ба Марям илқо кард ва [Исо] рӯҳе аз ҷониби Ӯст. Пас, ба Аллоҳ таоло ва паёмбаронаш имон биёваред ва нагӯед: "[Маъбуд] Сегона аст". [Аз ин сухан] Бозистед, ки барои шумо беҳтар аст. Аллоҳ таоло танҳо маъбуди ягона аст [ва] аз ин ки фарзанде дошта бошад, пок [-у муназзаҳ] аст. Он чи дар осмонҳо ва замин аст, аз они Ӯст ва ҳамин бас аст, ки Аллоҳ таоло коргузор [-у муроқиби махлуқот] бошад');
INSERT INTO "pioneers" VALUES(4,172,'4:172','Масеҳ ва фариштагони муқарраб ҳаргиз [сар намепечанд ва] ибо надоранд, аз ин ки бандаи Аллоҳ таоло бошанд. Ва ҳар ки аз парастиши Ӯ сарпечӣ кунад ва такаббур варзад, ҳамаи ононро [барои ҳисобрасӣ] назди хеш ҷамъ хоҳад кард');
INSERT INTO "pioneers" VALUES(4,173,'4:173','Аммо онон, ки имон овардаанд ва корҳои шоиста анҷом додаанд, [Аллоҳ таоло] подошҳояшонро ба таври комил медиҳад ва аз фазли хеш бар онҳо меафзояд. Ва аммо онон, ки ибо карданд ва такаббур варзиданд, ба азоби дарднок азобашон мекунад ва барои худ ғайр аз Аллоҳ таоло дӯсту ёваре намеёбанд');
INSERT INTO "pioneers" VALUES(4,174,'4:174','Эй мардум, ба ростӣ, ки барои шумо аз ҷониби Парвардигоратон далели равшан [Муҳаммад] омад ва нури ошкор [Қуръон] ба сӯятон нозил кардем');
INSERT INTO "pioneers" VALUES(4,175,'4:175','Аммо онон, ки ба Аллоҳ таоло имон оварданд ва ба он [Қуръон] тамассук ҷустанд, ҳамаро дар раҳмату фазли худ ворид хоҳад кард ва ба роҳи рост ба сӯйи хеш раҳнамун мегардонад');
INSERT INTO "pioneers" VALUES(4,176,'4:176','[Эй паёмбар, дар бораи чигунагии тақсими ирс байни бародару хоҳари он майите, ки падару модар ва фарзанд надорад] аз ту мепурсанд. Бигӯ: "Аллоҳ таоло дар [мавриди] калола бароятон ҳукм содир мекунад: Агар марде, ки [падару модар ва] фарзанде надорад, фавт кунад ва хоҳаре дошта бошад, нисфи тарака аз они ӯст. [Ва агар зане фавт кунад ва бародаре дошта бошад] Дар сурате ки фарзанд надошта бошад, он мард [ҳамаи таракаро] аз ӯ ба ирс мебарад. Ва агар ду хоҳар [ё бештар аз майит] боқӣ бимонад, ду савуми тарака барои онҳост. Ва агар бародарону хоҳарон [бо ҳам] бошанд, ҳар марде [муодили] саҳми ду занро дорад. Аллоҳ таоло [ин аҳкомро] бароятон равшан месозад, то гумроҳ нашавед [ва дар тақсими ирс ба роҳи хато наравед] ва Аллоҳ таоло ба ҳар чизе доност"');
INSERT INTO "pioneers" VALUES(5,1,'5:1','Эй касоне, ки имон овардаед, ба паймонҳо [-и худ] вафо кунед. [Хӯрдани гушти] Чаҳорпоён бар шумо ҳалол аст, магар он чи [ҳукмаш] бар шумо хонда шавад ва [ба шарти он ки] дар ҳоли эҳром [ҳаҷ ё умра] шикорро ҳалол нашуморед. Ҳамоно Аллоҳ таоло ҳар чи бихоҳад ҳукм мекунад');
INSERT INTO "pioneers" VALUES(5,2,'5:2','Эй касоне, ки имон овардаед, [ҳурмати] шаоири илоҳӣ ва моҳи ҳаром [-ро нигаҳ доред] ва [ҳамчунин, ҳурмати] қурбонии бенишон ва қурбониҳои қалодадор (гардандбанддор) ва роҳиёни Байт-ул-ҳаром, ки фазлу хушнудии Парвардигорашонро металабанд. Ва чун аз эҳром хориҷ шудед, [метавонед озодона] ба шикор бипардозед ва [мабодо] душмании гурӯҳе, ки шуморо аз масҷидулҳаром боз доштанд, ба тааддӣ ва таҷовуз водоратон кунад. Ва дар накукорӣ ва парҳезгорӣ бо якдигар ёрӣ кунед ва дар гуноҳу таҷовуз дастёри ҳам нашавед ва аз Аллоҳ таоло парво кунед [битарсед, чаро ки] бе тардид, Аллоҳ таоло сахткайфар аст');
INSERT INTO "pioneers" VALUES(5,3,'5:3','[Инҳо ҳама] Бар шумо ҳаром шудааст: [Гӯшти] Мурдор ва хуну гӯшти хук ва он чи [ҳангоми забҳ] номи ғайри Аллоҳ таоло бар он бурда шавад ва [ҳайвони ҳалолгӯшти] хафашуда ва ба зарби [чӯбу санг] мурда ва азбаландиафтода ва шоххӯрдаву нимхӯрдаи [ҳайвоноти] дарранда – магар он чи [пеш аз мурдан] забҳ [-и шаръӣ] карда бошед – ва [ҳамчунин] он чи дар баробари бутҳо забҳ мешавад ва он чи бо чӯбҳои қуръа тақсим мекунед. Ва [ба яқин бидонед, ки рӯй овардан ба тамоми] инҳо нофармонӣ аст. Имрӯз кофирон аз дини шумо [ва бозгаштатон ба куфр] ноумед шудаанд, пас, аз онон натарсед ва аз ман битарсед. Имрӯз динатонро комил кардам ва неъматро бар шумо тамом намудам ва исломро [ба унвони беҳтарин] дин бароятон баргузидам. Пас, ҳар ки [барои ҳифзи ҷони худ ва] бидуни он ки ба гуноҳе мутамоил бошад, ночор [ба хӯрдани гӯштҳои манъшуда] шавад [бар ӯ гуноҳе нест]. Ба ростӣ, ки Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(5,4,'5:4','[Эй паёмбар, асҳобат] Аз ту мепурсанд, чи чиз барояшон ҳалол шудааст. Бигӯ: "Чизҳои покиза бароятон ҳалол гаштааст ва [ҳамчунин] он чи ба кумаки ҳайвоноти шикорӣ [ба даст меоваред, чаро], ки онҳоро бо он чи Аллоҳ таоло ба шумо омӯхтааст, дастомӯз мекунед. Пас, аз он чи [худ нахӯрдаанд ва] барои шумо гирифтаанд, бихӯред ва [ба ҳангоми забҳ ё фиристодани ҳайвоноти шикорӣ] номи Аллоҳро бар он бибаред ва аз Аллоҳ таоло парво кунед [битарсед]. Бе гумон, Аллоҳ таоло дар ҳисобрасӣ сареъ аст"');
INSERT INTO "pioneers" VALUES(5,5,'5:5','[Эй мусулмонон] Имрӯз хӯрокҳои покиза бароятон ҳалол шудааст ва [забоиҳу] ғизои аҳли китоб барои шумо ҳалол аст ва ғизои шумо [низ] барои онон ҳалол аст ва [ҳамчунин издивоҷ бо] занони покдомани мусулмон ва занони покдоман аз [аҳли китоб, яъне] касоне, ки пеш аз шумо ба онон китоб [-и осмонӣ] дода шудааст [ҳалол мебошад], ба шарти он ки маҳрияҳои ононро бипардозед, дар ҳоле ки худ покдоман бошед, на зинокор бошед ва на [занонро] пинҳонӣ ба дӯстӣ бигиред. Ва ҳар ки ба [аркони] имон куфр биварзад, ҳатман, амалаш табоҳ шудааст ва дар охират аз зиёндидагон хоҳад буд');
INSERT INTO "pioneers" VALUES(5,6,'5:6','Эй касоне, ки имон овардаед чун ба [қасди] намоз бархостед, рӯйи худ ва дастҳоятонро то оринҷ бишӯед ва саратонро масҳ кунед ва пойҳоятонро то ду шитолинг [буҷулак бишӯед] ва агар ҷунуб будед, худро [бо ғусл кардан] пок созед ва агар бемор ё мусофиред ё ҳар як аз шумо аз маҳалли қазои ҳоҷат омадааст ё бо занон омезиш кардаед ва об намеёбед, пас, ба хоки пок таяммум кунед ва аз он бар сурату дастҳоятон бикашед. Аллоҳ таоло намехоҳад шуморо ба сахтӣ бияндозад, балки мехоҳад шуморо пок созад ва неъмати хешро бар шумо тамом кунад, бошад, ки шукр гузоред');
INSERT INTO "pioneers" VALUES(5,7,'5:7','Ва ба ёд оваред неъмати Аллоҳро бар хеш ва [низ] паймоне, ки шуморо ба [анҷоми] он муваззаф гардондааст, он гоҳ ки [дар бораи даъват ба фармонбардорӣ ба паёмбар] гуфтед: "Шунидем ва итоат кардем". Ва аз Аллоҳ таоло парво кунед [битарсед], [ки] бе тардид, Аллоҳ таоло ба он чи даруни синаҳост, доност');
INSERT INTO "pioneers" VALUES(5,8,'5:8','Эй касоне, ки имон овардаед, барои Аллоҳ таоло ба адл бархезед, [ва] ба адолат шаҳодат диҳед. Ва албатта, набояд душмании як гурӯҳ шуморо бар он дорад, ки адолат накунед. Адолат кунед, ки ин [кор] ба парҳезкорӣ наздиктар аст ва аз Аллоҳ таоло парво кунед [битарсед], [ки] бе тардид, Аллоҳ таоло ба он чи анҷом медиҳед, огоҳ аст');
INSERT INTO "pioneers" VALUES(5,9,'5:9','Аллоҳ таоло ба онон, ки имон овардаанд ва корҳои шоиста анҷом додаанд, ваъдаи омурзиш ва подоши бузурге додааст');
INSERT INTO "pioneers" VALUES(5,10,'5:10','Ва касоне, ки куфр варзиданд ва оёти моро такзиб карданд, онон аҳли дузаханд');
INSERT INTO "pioneers" VALUES(5,11,'5:11','Эй касоне, ки имон овардаед, неъмати Аллоҳро бар худ ба ёд оваред, он гоҳ ки гурӯҳе [аз душманон] қасд карданд, ки ба сӯятон даст дароз кунанд, пас, [Аллоҳ таоло] дасташонро аз шумо боздошт. Ва аз Аллоҳ таоло парво кунед [битарсед]. Ва муъминон бояд танҳо бар Аллоҳ таоло таваккал кунанд');
INSERT INTO "pioneers" VALUES(5,12,'5:12','Ва бе тардид, Аллоҳ таоло аз Бани Исроил паймон гирифт ва аз миёни онон [ба теъдоди қабоилашон] дувоздаҳ солор баргумоштем ва Аллоҳ таоло гуфт: "Ман бо шумо ҳастам. Агар намоз барпо доред ва закот бипардозед ва ба паёмбарони ман имон биёваред ва ононро гиромӣ доред ва дар роҳи Аллоҳ таоло воми наку диҳед, гуноҳонатонро мезудоям ва шуморо ба боғҳое [аз биҳишт] ворид мекунам, ки ҷӯйборҳо аз зери [дарахтони] он ҷорӣ аст. Пас, ҳар як аз шумо, ки баъд аз ин кофир шавад, мусалламан, роҳи ростро гум кардааст"');
INSERT INTO "pioneers" VALUES(5,13,'5:13','Пас, ба [сазои] паймоншиканиашон ононро аз раҳмати хеш дур сохтем ва дилҳояшонро сахт гардондем, [зеро бо тағйири лафзу таъвили маъно] суханон [-и Аллоҳ таоло]-ро аз ҷойгоҳаш таҳриф мекунанд ва бахше аз он чиро бад-он панд дода шуданд, фаромӯш карданд. Ва [эй паёмбар] ту пайваста дар бораи хиёнате аз сӯйи онон огоҳ мешавӣ, магар [шумори] андак аз эшон [ки хиёнаткор нестанд]. Пас, аз онон даргузар ва чашмпӯшӣ кун, [ки] бе тардид, Аллоҳ таоло накукоронро дӯст медорад');
INSERT INTO "pioneers" VALUES(5,14,'5:14','Ва [ҳамчунин] аз касоне, ки гуфтанд: "Мо масеҳӣ ҳастем", паймон гирифтем, [ки пайрави Қуръон ва расули Аллоҳ таоло бошанд] ва [-ле динашонро таҳриф намуданд ва] бахше аз он чиро, ки бад-он панд дода шуданд, фаромӯш карданд ва мо [низ] то рӯзи қиёмат миёнашон душманиву кина афкандем ва [дар он рӯз] Аллоҳ таоло ононро [аз натиҷаи] он чи кардаанд, огоҳ хоҳад сохт');
INSERT INTO "pioneers" VALUES(5,15,'5:15','Эй аҳли китоб, ҳамоно паёмбари мо ба сӯйи шумо омадааст, ки бисёре аз [матолибу омӯзаҳои] китоб [-и осмонии худ]-ро, ки пинҳон мекардед, бароятон ба равшанӣ баён кунад ва аз бисёре [аз матолибу аҳкоми он] даргузарад. Ҳатман, аз ҷониби Аллоҳ таоло бароятон нуру китоби равшангар омадааст');
INSERT INTO "pioneers" VALUES(5,16,'5:16','Аллоҳ таоло ба василаи ин [китоб] касонеро, ки аз хушнудии Ӯ пайравӣ кунанд, ба роҳҳои саломат ҳидоят мекунад ва ба фармони Хеш ононро аз торикиҳо ба сӯйи рӯшноӣ мебарад ва ба роҳи рост ҳидояташон мекунад');
INSERT INTO "pioneers" VALUES(5,17,'5:17','Касоне, ки гуфтанд: "Аллоҳ таоло ҳамон Масеҳ – писари Марям – аст", яқинан, кофир шуданд. [Эй паёмбар, ба онон] Бигӯ: "Агар Аллоҳ таоло бихоҳад, ки Масеҳ – писари Марям – ва модараш ва ҳамаи касонеро, ки рӯйи замин ҳастанд, нобуд кунад, чи касе дар баробари Аллоҳ таоло ихтиёре дорад, ки монеъ шавад? Фармонравоии осмонҳо ва замин ва он чи миёни он ду қарор дорад, аз они Аллоҳ таоло аст; ҳар чи бихоҳад меофаринад ва Аллоҳ таоло бар ҳар чизе тавоност"');
INSERT INTO "pioneers" VALUES(5,18,'5:18','Яҳудиён ва масеҳиён гуфтанд: "Мо фарзандони Аллоҳ таоло ва дӯстони Ӯ ҳастем". [Эй паёмбар, дар посухашон] Бигӯ: "Пас, чаро Ӯ шуморо ба кайфари гуноҳонатон азоб мекунад? Балки шумо [низ] башар ва аз ҷумлаи афроде ҳастед, ки офаридааст. Ҳар киро бихоҳад, меомурзад ва ҳар киро бихоҳад, азоб мекунад ва фармонравоии осмонҳо ва замин ва он чи миёни он ду қарор дорад, аз они Аллоҳ таоло аст ва бозгашт [-и ҳама] ба сӯйи Ӯст"');
INSERT INTO "pioneers" VALUES(5,19,'5:19','Эй аҳли китоб, паёмбари мо ба сӯйи шумо омад, то дар давраи фатрати паёмбарон [фосила байни биъсати Исо ва Муҳаммад] [аҳкому омӯзаҳои илоҳиро] ба равшанӣ бароятон баён кунад, ки мабодо [рӯзи қиёмат] бигӯед: "На башоратбахше ба суроғамон омад, на бимдиҳандае". Пас, яқинан, [инак] паёмбари башоратбахш ва бимдиҳанда ба сӯятон омадааст ва Аллоҳ таоло бар ҳама чиз тавоност');
INSERT INTO "pioneers" VALUES(5,20,'5:20','Ва [эй паёмбар, ёд кун аз] Ҳангоме ки Мӯсо ба қавмаш гуфт: "Эй қавми ман, неъмати Аллоҳро бар худ ба ёд оваред, он гоҳ ки байни шумо паёмбароне барангехт ва [пас аз бардагии Фиръавн афроде аз] шуморо фармонравоён [-и худатон] сохт ва чизҳое ба шумо бахшид, ки ба ҳеҷ як аз ҷаҳониён [-и муосиратон] надода буд');
INSERT INTO "pioneers" VALUES(5,21,'5:21','Эй қавм, ба сарзамини муқаддасе, ки Аллоҳ таоло бароятон муқаррар намудааст, ворид шавед ва ба ақиб бознагардед [ва аз динатон даст накашед], ки зиёндида хоҳед шуд"');
INSERT INTO "pioneers" VALUES(5,22,'5:22','[Онон] Гуфтанд: "Эй Мӯсо, бе гумон он ҷо қавми зӯрманд ҳастанд, [ва] то онон аз он ҷо берун нараванд, мо ҳаргиз вориди он намешавем. Пас, агар аз он ҷо берун бираванд, мо ворид хоҳем шуд"');
INSERT INTO "pioneers" VALUES(5,23,'5:23','Ду тан аз мардоне, ки [аз нофармонӣ ва азоби илоҳӣ] метарсиданд ва Аллоҳ таоло ба онон неъмат [-и итоат ва бандагӣ] дода буд, гуфтанд: "Аз он дарвоза бар эшон [ҳуҷум баред ва] ворид шавед, ки агар аз он даромадед, ҳатман, пирӯз хоҳед шуд ва агар муъминед, бар Аллоҳ таоло таваккал кунед"');
INSERT INTO "pioneers" VALUES(5,24,'5:24','[Бани Исроил] Гуфтанд: "Эй Мӯсо, то замоне ки онон дар он ҷо хастанд, мо ҳаргиз ворид намешавем. Ту ва Парвардигорат биравед ва биҷангед. Мо ҳамин ҷо менишинем"');
INSERT INTO "pioneers" VALUES(5,25,'5:25','[Мӯсо] Гуфт: "Парвардигоро, ман ҷуз ихтиёри худам ва бародарам [ихтиёри шахси дигареро] надорам, пас, миёни мо ва ин гурӯҳи нофармон ҷудоӣ бияфкан"');
INSERT INTO "pioneers" VALUES(5,26,'5:26','[Аллоҳ таоло] Фармуд: "Дар ин сурат [вуруд ба] он [сарзамини муқаддас] то чиҳил сол бар онон ҳаром шудааст, [ва пайваста] дар замин саргардон хоҳанд буд. Пас, ба хотири [рафтори] ин гурӯҳи нофармон афсӯс махӯр"');
INSERT INTO "pioneers" VALUES(5,27,'5:27','Ва [эй паёмбар] достони ду писари Одам [Ҳобил ва Қобил]-ро ба ҳақ бар эшон бихон: ҳангоме ки [ҳар кадом] барои тақарруб [ба даргоҳи Аллоҳ таоло] як қурбонӣ тақдим карданд, аз яке пазируфта шуд ва аз дигаре пазируфта нашуд. [Қобил ба Ҳобил] Гуфт: "Ҳатман, туро хоҳам кушт". [Ҳобил] Гуфт: "Аллоҳ таоло [қурбониро] фақат аз парҳезкорон мепазирад');
INSERT INTO "pioneers" VALUES(5,28,'5:28','Агар ту дастро барои куштани ман дароз кунӣ, ман ҳаргиз дастамро барои куштан ба сӯят дароз нахоҳам кард, [зеро] бе тардид, ман аз Аллоҳ таоло – Парвардигори ҷаҳониён – метарсам');
INSERT INTO "pioneers" VALUES(5,29,'5:29','[Он гоҳ барои он ки ӯро битарсонад гуфт] "Ман мехоҳам, ки ту бо гуноҳи [қатли] ман ва гуноҳи [аъмоли] худат [ба сӯйи Аллоҳ таоло] бозгардӣ ва аз аҳли дузах бошӣ. Ва ин аст сазои ситамгорон"');
INSERT INTO "pioneers" VALUES(5,30,'5:30','Пас, нафси [саркаши] ӯ куштани бародарашро барояш орост [ва муваҷҷаҳу осон ҷилва дод]. Пас, ӯро кушт ва аз зиёнкорон шуд');
INSERT INTO "pioneers" VALUES(5,31,'5:31','Он гоҳ Аллоҳ таоло калоғ [зоғ]-еро фиристод, ки дар замин ҷустуҷӯ [ва ковиш] мекард, то [калоғи мурдаеро дафн кунад ва ин гуна] ба вай нишон диҳад, ки чи гуна ҷасади бародарашро бипӯшонад. [Қобил] Гуфт: "Вой бар ман! Оё нотавон будам аз ин ки ҳамчун ин калоғ бошам ва ҷасади бародарамро бипӯшонам"? Он гоҳ [ба сахтӣ аз кори зишти хеш] пушаймон шуд');
INSERT INTO "pioneers" VALUES(5,32,'5:32','Ба ҳамин сабаб, бар Бани Исроил муқаррар доштем, ки ҳар кас инсонеро ҷуз барои [қасоси] касе ё фасоде [ки] дар замин [муртакиб шудааст] ба қатл бирасонад, чунон аст, ки гӯӣ ҳамаи мардумро куштааст ва ҳар кас [бо худдорӣ аз қатлҳои худсарона ва беҳуда] инсонеро зинда бидорад, чунон аст, ки ҳамаи мардумро зинда кардааст. Ва бе гумон, паёмбарони мо бо далоили равшан барои онон [Бани Исроил] омаданд, [вале] боз ҳам бисёре аз эшон пас аз он [бо иртикоби гуноҳ] дар замин [по аз ҳудуди илоҳӣ фаротар гузоштанд ва] зиёдаравӣ карданд');
INSERT INTO "pioneers" VALUES(5,33,'5:33','Сазои касоне, ки бо Аллоҳ таоло ва паёмбараш меҷанганд ва [бо қатлу дуздӣ ва фаҳшо] дар замин ба фасод мекӯшанд, ҷуз ин нест, ки кушта шаванд ё ба дор овехта шаванд ё даст [-и рост] ва пой [-и чапи] онҳо дар хилоф [ҷиҳати якдигар] қатъ шавад ё аз сарзамин [-и худ] табъид гарданд. Ин [муҷозот мояи] расвоии онҳо дар дунёст ва дар охират [низ] азоби бузург [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(5,34,'5:34','Магар касоне, ки [шумо ҳокимон] пеш аз он ки бар онон даст ёбед, тавба кунанд. Пас, [агар чунин карданд, аз онон даргузаред ва] бидонед, ки Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(5,35,'5:35','Эй касоне, ки имон овардаед, аз Аллоҳ таоло парво кунед [битарсед] ва [бо анҷоми корҳои нек, барои тақарруб] ба сӯйи Ӯ васила биҷӯед ва дар роҳаш ҷиҳод кунед, бошад, ки растагор шавед');
INSERT INTO "pioneers" VALUES(5,36,'5:36','Дар ҳақиқат касоне, ки куфр варзиданд, агар тамоми он чи дар замин аст, барои онон бошад ва ҳаммонандаш [низ] бо он [дар ихтиёрашон бошад ва бихоҳанд], то ба бад-он васила худро аз азоби рӯзи қиёмат бозхаранд, аз эшон пазируфта намешавад ва азоби дарднок [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(5,37,'5:37','Мехоҳанд аз оташ [-и дузах] берун раванд, ва [-ле] аз он [азоб] наметавонанд хориҷ шаванд ва барояшон азоби пойдор [муҳайё] аст');
INSERT INTO "pioneers" VALUES(5,38,'5:38','[Эй ҳокимони мусулмон] Дасти марду зани дуздро ба сазои амале, ки муртакиб шудаанд, ба унвони муҷозоте аз ҷониби Аллоҳ таоло қатъ кунед ва [бидонед, ки] Аллоҳ таоло шикастнопазиру ҳаким аст');
INSERT INTO "pioneers" VALUES(5,39,'5:39','Пас, ҳар ки баъд аз [гуноҳ ва] ситам карданаш тавба кунад ва [аъмоли худро ҷуброн ва] ислоҳ намояд, бе тардид, Аллоҳ таоло тавбаашро мепазирад. Ба ростӣ, ки Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(5,40,'5:40','Оё намедонӣ, ки фармонравоии осмонҳо ва замин аз они Аллоҳ таоло аст, [ва] ҳар киро бихоҳад азоб мекунад ва ҳар киро бихоҳад мебахшад? Ва Аллоҳ таоло бар ҳар чизе тавоност');
INSERT INTO "pioneers" VALUES(5,41,'5:41','Эй паёмбар, касоне, ки дар [роҳи] куфр шитоб мекунанд, туро ғамгин насозад - [хоҳ] аз касоне, ки ба забонашон мегӯянд: "Имон овардем" ва қалбашон имон наовардааст ва [хоҳ] аз яҳудиён [бошанд, ҳамон касоне, ки ба суханони пешвоёну бузургонашон] гӯш месупоранд, [оре, онон фақат] шунавандаи [дурӯғҳо ва муқаллиди дастурҳои] гурӯҳи дигаре [аз уламояшон] ҳастанд, ки [туро бовар надоранд ва ҳаргиз] наздат наомадаанд. Онон суханон [-и Аллоҳ таоло]-ро [бар асоси майлу манфиати худ] аз ҷойгоҳашон таҳриф мекунанд ва [ба якдигар] мегӯянд: "Агар ин [ҳукм аз суйи Муҳаммад] ба шумо дода шуд, пас, [аз ӯ] бипазиред ва агар он [чизи дилхоҳатон аст] ба шумо дода нашуд, [аз ӯ] дурӣ кунед". Ва [эй паёмбар] ҳар ки Аллоҳ таоло гумроҳиашро хоста бошад, ту ҳаргиз дар баробари Аллоҳ таоло барояш ихтиёр надорӣ [ва наметавонӣ аз ӯ дифоъ кунӣ]. Онон [яҳудиён ва мунофиқон] касоне ҳастанд, ки Аллоҳ таоло нахостааст дилҳояшонро [аз палидии куфр] пок кунад. Онон дар дунё расвоӣ [ва хорӣ дар интизор] доранд ва дар охират барояшон азоби бузурге [дар пеш] аст');
INSERT INTO "pioneers" VALUES(5,42,'5:42','Онон [яҳудиён] бисёр гӯш ба дурӯғ месупоранд, [ва] моли ҳароми фаровон мехӯранд. Пас, [эй паёмбар] агар [барои додхоҳӣ] наздат омаданд, миёнашон доварӣ кун ё аз эшон рӯй бигардон. Ва [яқин бидон, ки] агар аз онон рӯй бигардонӣ, ба ту ҳеҷ зиёне намерасонанд, ва [-ле] агар доварӣ кардӣ, бо адолат миёнашон доварӣ кун. Бе тардид, Аллоҳ таоло додгаронро дӯст медорад');
INSERT INTO "pioneers" VALUES(5,43,'5:43','Ва онон чи гуна туро ба доварӣ металабанд, дар ҳоле ки Таврот наздашон аст, [ва] ҳукми Аллоҳ таоло дар он вуҷуд дорад? Сипас баъд аз он [додхоҳӣ, агар сухане хилофи майлашон бигӯӣ, аз дастури Таврот ва ҳукми ту] руй мегардонанд ва инон муъмин нестанд');
INSERT INTO "pioneers" VALUES(5,44,'5:44','Ба ростӣ, Мо Тавротро нозил кардем, ки дар он ҳидоят ва нур аст. Паёмбароне, ки [дар баробари фармони Аллоҳ таоло] таслим буданд, бар асоси [омӯзаҳои] он барои яҳудиён доварӣ мекарданд ва донишмандону фақеҳон [-и яҳудӣ], ки посдорӣ аз китоби Аллоҳ таоло [ва ҷилавгирӣ аз таҳрифи Таврот] ба онон супурда шуда буд ва бар [ҳаққонияти] он гувоҳ буданд [ва бино ба дастурҳояш ҳукм мекарданд]. Пас, [эй яҳудиён, дар иҷрои аҳкоми илоҳӣ] аз мардум натарсед ва аз Ман битарсед ва оётамро ба баҳои ночиз нафурӯшед. Ва ҳар кас ба аҳкоме, ки Аллоҳ таоло нозил кардааст, доварӣ накунад, кофир аст');
INSERT INTO "pioneers" VALUES(5,45,'5:45','Ва дар он [Таврот] барои эшон [Бани Исроил] муқаррар доштем, ки: "Ҷон дар баробари ҷону чашм дар баробари чашм ва бинӣ дар баробари бинӣ ва гӯш дар баробари гӯш ва дандон дар баробари дандон аст ва [ҳатто] захмҳо қасос доранд". Пас, ҳар ки аз он гузашт намояд, он [бахшиш] каффораи [гуноҳони] ӯст. Ва ҳар кас ба аҳкоме, ки Аллоҳ таоло нозил кардааст, ҳукм накунад, [аз ҳудуди илоҳӣ фаротар рафтааст ва] ситамгор аст');
INSERT INTO "pioneers" VALUES(5,46,'5:46','Ва ба дунболи онон [паёмбарони Бани Исроил] Исо писари Марямро фиристодем, ки тасдиқкунандаи Таврот буд, ки пеш аз ӯ фиристода будем ва Инҷилро, ки дар он ҳидоят ва нур буд, ба ӯ додем, ки тасдиқкунандаи Тавроти пеш аз вай буд ва ҳидояту андарзе буд барои парҳезкорон');
INSERT INTO "pioneers" VALUES(5,47,'5:47','Ва аҳли Инҷил бояд ба он чи Аллоҳ таоло дар он [китоб] нозил кардааст, ҳукм кунанд. Ва ҳар ки ба он чи Аллоҳ таоло нозил кардааст, ҳукм накунад, нофармон аст');
INSERT INTO "pioneers" VALUES(5,48,'5:48','Ва [эй паёмбар, мо ин] китобро ба ҳақ бар ту нозил кардем, [ки] тасдиқкунандаи китобҳои пешин аст ва мавриди эътимод [-и китобҳои осмонӣ ба унвони меъёре барои ҳақ ва ботил] аст. Пас, байни онон бар асоси он чи Аллоҳ таоло нозил кардааст, ҳукм кун ва ба ҷойи ҳақиқате, ки [аз ҷониби Қуръон] ба ту раисдааст, аз ҳавасҳои онон пайравӣ макун. [Эй мардум] Мо барои ҳар яке аз шумо оину роҳи равшан карор додем ва агар Аллоҳ таоло мехост, ҳамаи шуморо уммати воҳид карор медод, вале [Аллоҳ таоло мехоҳад] шуморо дар [мавриди] он чи ба шумо бахшидааст, биозмояд, пас, дар некиҳо бар якдигар пешӣ гиред. Бозгашти ҳамаи шумо ба сӯйи Аллоҳ таоло аст. Он гоҳ Ӯ аз он чи дар он ихтилоф мекардед, огоҳатон месозад');
INSERT INTO "pioneers" VALUES(5,49,'5:49','Ва [эй паёмбар] миёни онон [яҳуд] бар асоси он чи Аллоҳ таоло [дар Қуръон] нозил кардааст, ҳукм кун ва аз ҳавасҳояшон пайравӣ макун ва бар ҳазар бош аз ин ки туро аз бархе аз он чи Аллоҳ таоло бар ту нозил кардааст, мунҳариф кунанд. Пас, агар [аз пазириши доварии ту] рӯй гардонанд, бидон, ки Аллоҳ таоло мехоҳад ононро ба [хотири] бархе аз гуноҳонашон муҷозот кунад. Ва ба ростӣ, ки бисёре аз мардум нофармонанд');
INSERT INTO "pioneers" VALUES(5,50,'5:50','Оё онон доварии [бар асоси ченакҳои] ҷоҳилиятро мехоҳанд? Ва барои аҳли яқин, доварии чи касе беҳтар аз Аллоҳ таоло аст?');
INSERT INTO "pioneers" VALUES(5,51,'5:51','Эй касоне, ки имон овардаед, яҳудиён ва масеҳиёнро ба дӯстӣ нагиред. Онон дӯстони якдигаранд ва ҳар ки аз шумо, ки бо эшон дӯстӣ кунад, ҳатман, аз онҳост. Бе тардид, Аллоҳ таоло гурӯҳи ситамгоронро [ба хотири дӯстӣ бо кофирон] ҳидоят намекунад');
INSERT INTO "pioneers" VALUES(5,52,'5:52','Пас, [эй паёмбар] касонеро, ки дар дилҳояшон беморӣ [нифоқ] аст, мебинӣ, ки дар [дӯстӣ бо] онон мешитобанд, [ва] мегӯянд: "Метарсем, ки [агар пирӯз шаванд, аз ҷониби онон] осебе ба мо бирасад". Умед аст, ки Аллоҳ таоло [барои мусулмонон] аз ҷониби Худ пирӯзӣ [ва кушоише] пеш оварад, он гоҳ [ин мунофиқон] аз он чи дар дили худ пинҳон медоштанд, пушаймон шаванд');
INSERT INTO "pioneers" VALUES(5,53,'5:53','Ва онон, ки имон овардаанд мегӯянд: "Оё инҳо [мунофиқон] ҳамон касоне ҳастанд, ки ба Аллоҳ таоло савгандҳои сахт мехӯрданд, ки бо шумо ҳастанд"? Аъмолашон табоҳ гашт ва зиёнкор шуданд');
INSERT INTO "pioneers" VALUES(5,54,'5:54','Эй касоне, ки имон овардаед, ҳар касе аз шумо, ки аз дини худ баргардад [ба Аллоҳ таоло зиёне намерасонад], Аллоҳ таоло ба зудӣ гурӯҳеро меоварад, ки ононро дӯст медорад ва онҳо [низ] Ӯро дӯст медоранд, [онон] нисбат ба муъминон фурӯтан ва дар баробари кофирон сарсахт ҳастанд, дар роҳи Аллоҳ таоло ҷиҳод мекунанд ва аз сарзаниши ҳеҷ маломатгаре намеҳаросанд. Ин фазли Аллоҳ таоло аст, ки ба ҳар кас бихоҳад медиҳад ва Аллоҳ таоло кушоишгари доност');
INSERT INTO "pioneers" VALUES(5,55,'5:55','[Эй муъминон] Бе тардид, [ёвару] дӯсти шумо фақат Аллоҳ таоло ва паёмбараш ва [ҳамчунин] афроде ҳастанд, ки имон овардаанд. [Ҳамон] Касоне, ки намоз барпо медоранд ва бо фурӯтанӣ [ва ризояти комил] закот мепардозанд');
INSERT INTO "pioneers" VALUES(5,56,'5:56','Ва ҳар ки Аллоҳ таоло ва паёмбараш ва муъминонро ба дӯстӣ бигирад, [аз гурӯҳи Аллоҳ таоло аст ва] яқинан, гурӯҳи Аллоҳ таоло пирӯзанд');
INSERT INTO "pioneers" VALUES(5,57,'5:57','Эй касоне, ки имон овардаед, касонеро, ки динатонро ба тамасхур ва бозӣ гирифтаанд, дӯст [-они худ] нагиред, [хоҳ] аз касоне [бошанд], ки пеш аз шумо ба онон китоб дода шудааст ва [хоҳ аз] кофирон. Ва агар имон доред, аз Аллоҳ таоло парво кунед [битарсед]');
INSERT INTO "pioneers" VALUES(5,58,'5:58','Ва [ин афрод касоне ҳастанд, ки] вақте [тавассути азон мардумро] ба намоз мехонед, онро ба масхара ва бозӣ мегиранд, зеро онон гурӯҳе ҳастанд, ки намеандешанд');
INSERT INTO "pioneers" VALUES(5,59,'5:59','[Эй паёмбар] Бигӯ: "Эй аҳли китоб, оё [иллати ин ки] бар мо хурда мегиред, ҷуз ин аст, ки мо ба Аллоҳ таоло ва он чи бар мо нозил шуда ва [ба] он чи пеш аз мо нозил гардидааст, имон овардаем ва [ҳамчунин бар ин боварем, ки] ағлаби шумо нофармонед"');
INSERT INTO "pioneers" VALUES(5,60,'5:60','Бигӯ: "Оё [мехоҳед] шуморо аз [ҳоли] касоне огоҳ кунам, ки назди Аллоҳ таоло ҷазое аз ин бадтар доранд? [Онон] Касоне [ҳастанд], ки Аллоҳ таоло лаънаташон кард ва бар эшон хашм гирифт ва бархе аз ононро [масх намуд ва ба шакли] бузина ва хук даровард ва [низ] касоне, ки [ба ҷойи Аллоҳ таоло] тоғут [ва ҳокимони худкома]-ро бандагӣ карданд. Инон [аз ҳама] бадмақомтар ва аз роҳи рост гумгаштатаранд');
INSERT INTO "pioneers" VALUES(5,61,'5:61','Ва [эй паёмбар] вақте [яҳудиёни мунофиқ] наздатон меоянд, мегӯянд: "Имон овардаем", ҳол он ки бо куфр ворид шуданд ва бо ҳамон [куфр] берун рафтаанд; ва Аллоҳ таоло ба он чи пинҳон мекарданд, донотар аст');
INSERT INTO "pioneers" VALUES(5,62,'5:62','Ва бисёре аз ононро мебинӣ, ки дар гуноҳ ва таҷовуз [ба ҳуқуқи дигарон] ва ҳаромхӯрӣ шитоб мекунанд. Чи бад аст, он чи мекунанд!');
INSERT INTO "pioneers" VALUES(5,63,'5:63','Чаро донишмандон ва фақеҳон [-и яҳуд] ононро аз гуфтори гуноҳ [-олуд] ва ҳаромхорагиашон бознамедоранд? Ростӣ, чи бад аст, он чи анҷом медиҳанд!');
INSERT INTO "pioneers" VALUES(5,64,'5:64','Ва яҳудиён [ҳангоми сахтӣ ва тангдастӣ] гуфтанд: "Дасти Аллоҳ таоло [аз хайру бахшиш] бастааст". Дастҳояшон баста бод ва ба сазои он чи гуфтанд лаънат бар онон бод! [Ҳаргиз чунин нест] Балки ҳар ду дасти Ӯ кушодааст [ва] ҳар гуна ки бихоҳад, мебахшад. [Эй паёмбар] Ин оёт, ки аз тарафи Парвардигорат бар ту нозил шудааст, бар саркашӣ ва куфри бисёре аз онон меафзояд. Мо миёнашон то рӯзи қиёмат душманӣ ва кина афкандем. Ҳар замон оташи ҷанг барафрӯхтанд, Аллоҳ таоло онро хомӯш кард. Онон [пайваста] дар замин ба табаҳкорӣ [ва фасод] мекӯшанд ва Аллоҳ таоло табаҳкоронро дӯст намедорад');
INSERT INTO "pioneers" VALUES(5,65,'5:65','Ва агар аҳли китоб имон меоварданд ва парҳезкорӣ мекарданд, яқинан, гуноҳонашонро мезудудем ва ононро ба боғҳои пурнеъмат [-и биҳишт] ворид мекардем');
INSERT INTO "pioneers" VALUES(5,66,'5:66','Ва агар онон ба Таврот ва Инҷил ва он чи аз сӯйи Парвардигорашон бар онҳо нозил шудааст [Қуръон] амал мекарданд, ҳатман, аз [баракоти осмони] болои сарашон ва аз [неъматҳои замини] зери пояшон бархӯрдор мешуданд. Бархе аз онон миёнарав ҳастанд, ва [-ле] бисёре аз эшон [ба сабаби беимонӣ] корҳои баде анҷом медиҳанд');
INSERT INTO "pioneers" VALUES(5,67,'5:67','Эй паёмбар, он чиро аз тарафи Парвардигорат бар ту нозил шудааст, [ба таври комил ба мардум] иблоғ кун ва агар [чунин] накунӣ, рисолати Ӯро [ба анҷом] нарасондаӣ ва Аллоҳ таоло туро аз [газанди] мардум ҳифз мекунад. Бе тардид, Аллоҳ таоло гурӯҳи кофиронро ҳидоят намекунад');
INSERT INTO "pioneers" VALUES(5,68,'5:68','Бигӯ: "Эй аҳли китоб, шумо бар ҳеҷ [оини дурусте] нестед, магар ин ки Таврот ва Инҷил ва он чиро, ки аз тарафи Парвардигоратон бар шумо нозил шудааст, барпо доред". Ва [эй паёмбар] бе тардид, он чи аз сӯйи Парвардигорат бар ту нозил шудааст, бар саркашӣ ва куфри бисёре аз онон меафзояд, пас, бар [ин] гурӯҳи кофир афсӯс махӯр');
INSERT INTO "pioneers" VALUES(5,69,'5:69','Бе гумон, касоне, ки имон овардаанд ва касоне, ки яҳудӣ ва соибӣ [фитратгаро] ва масеҳӣ ҳастанд, ҳар ки ба Аллоҳ таоло ва рӯзи қиёмат имон оварад ва корҳои шоиста анҷом диҳад, на тарсе хоҳанд дошт ва на андуҳгин мешаванд');
INSERT INTO "pioneers" VALUES(5,70,'5:70','Яқинан, мо аз Бани Исроил [дар мавриди итоат аз фармони ҳақ] паймон гирифтем ва паёмбароне ба сӯяшон фиристодем. Ҳар гоҳ паёмбаре чизе овард, ки ҳавои нафсашон намеписандад, гурӯҳеро дурӯғгӯ хонданд ва гурӯҳеро куштанд');
INSERT INTO "pioneers" VALUES(5,71,'5:71','[Оре, яҳудиён паймон шикастанд] Ва пиндоштанд, ки муҷозоте дар кор нест, пас, кӯр гаштанд [ва ҳақро надиданд] ва кар шуданд [ва ҳақро нашуниданд]. Он гоҳ [тавба карданд ва] Аллоҳ таоло тавбаи эшонро пазируфт. Сипас бисёре аз эшон [боз ҳам дар баробари ҳидоят] кӯру кар шуданд ва Аллоҳ таоло ба он чи анҷом медиҳанд, биност');
INSERT INTO "pioneers" VALUES(5,72,'5:72','Онон, ки гуфтанд: "Аллоҳ таоло ҳамон Масеҳ писари Марям аст", яқинан кофир шуданд, дар ҳоле ки [худи] Масеҳ гуфт: "Эй Бани Исроил, Аллоҳро бипарастед, ки Парвардигори ману Парвадигори шумост. Бе тардид, ҳар ки ба Аллоҳ таоло ширк оварад, Аллоҳ таоло биҳиштро бар ӯ ҳаром кардааст ва ҷойгоҳаш дузах аст ва ситамгорон ҳеҷ ёваре надоранд"');
INSERT INTO "pioneers" VALUES(5,73,'5:73','Касоне, ки [маъбуди ҳақиқиро маҷмуаи падар, писар ва Рӯҳ-ул-қудус донистанд ва] гуфтанд: "Аллоҳ таоло савумин [нафар аз] се [маъбуд] аст", ҳатман, кофир шуданд. Маъбуде [ба ҳақ] ҷуз маъбуди ягона нест. Ва агар аз он чи мегӯянд, бознаистанд, ҳатман, ба афроде аз эшон, ки куфр варзиданд, азоби дардноке хоҳад расид');
INSERT INTO "pioneers" VALUES(5,74,'5:74','Оё ба сӯйи Аллоҳ таоло бознамегарданд ва аз Ӯ талаби омурзиш намекунанд? Ва Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(5,75,'5:75','Масеҳ писари Марям ҷуз паёмбаре нест, ки пеш аз ӯ [низ] паёмбарони дигаре буданд ва модараш зани ростгӯ буд. Ҳар ду [монанди инсонҳои дигар] ғизо мехӯрданд. [Эй паёмбар] Бингар, ки чи гуна оёт [-и худ]-ро барояшон равшан месозем ва боз бингар, ки чи гуна рӯйгардон мешаванд');
INSERT INTO "pioneers" VALUES(5,76,'5:76','[Эй паёмбар] Бигӯ: "Оё чизеро ба ҷойи Аллоҳ таоло мепарастед, ки суду зиёне бароятон надорад? Ва [ҳол он ки] Аллоҳ таоло шунавову доност"');
INSERT INTO "pioneers" VALUES(5,77,'5:77','Бигӯ: "Эй аҳли китоб, дар дини худ ба ноҳақ ғулув [ва зиёдаравӣ] накунед ва аз ҳавасҳои гурӯҳе пайравӣ накунед, ки пештар гумроҳ шуданд ва бисёре [аз пайравонашон]-ро гумроҳ карданд ва аз роҳи рост мунҳариф гаштаанд"');
INSERT INTO "pioneers" VALUES(5,78,'5:78','[Афроде] Аз Бани Исроил, ки куфр варзиданд, аз забони Довуд [дар Забур] ва [аз қавли] Исо писари Марям [дар Инҷил] мавриди лаънат қарор гирифтанд. Ин [нафрин] ба хотири он буд, ки нофармонӣ карданд ва [аз ҳудуди илоҳӣ] таҷовуз менамуданд');
INSERT INTO "pioneers" VALUES(5,79,'5:79','Онон якдигарро аз [гуноҳону] кор [-ҳои] зиште, ки анҷом медоданд, наҳй намекарданд. Ба ростӣ, чи бад буд он чи мекарданд!');
INSERT INTO "pioneers" VALUES(5,80,'5:80','[Эй паёмбар] Бисёре аз ононро мебинӣ, ки бо кофирон дӯстӣ мекунанд. Ба ростӣ, чи зишт аст, он чи барои худ пеш фиристоданд, ки [дар натиҷаи он низ] Аллоҳ таоло бар онон хашм гирифт ва ҷовидона дар азоб мемонанд');
INSERT INTO "pioneers" VALUES(5,81,'5:81','Ва агар [яҳудиён] ба Аллоҳ таоло ва паёмбар ва он чи бар ӯ нозил шудааст, имон меоварданд, онон [кофирон]-ро ба дӯстӣ намегирифтанд, вале бисёре аз онон нофармонанд');
INSERT INTO "pioneers" VALUES(5,82,'5:82','[Эй паёмбар] Яқинан, яҳудиён ва касонеро, ки ширк варзиданд, душмантарини мардум нисбат ба муъминон хоҳӣ ёфт ва наздиктарин дӯстон ба муъминонро касоне хоҳӣ ёфт, ки мегӯянд: "Мо масеҳӣ ҳастем". Ин ба хотири он аст, ки миёни эшон кашишон ва роҳибон [-и ҳақпараст] ҳастанд ва [низ] аз он рӯст, ки такаббур намеварзанд');
INSERT INTO "pioneers" VALUES(5,83,'5:83','Ва чун оётеро, ки бар паёмбар нозил шудааст, мешунаванд, мебинӣ бар асари он чи аз ҳақ шинохтаанд, чашмонашон ашк меборад ва мегӯянд: "Парвардигоро, имон овардем, пас, моро дар зумраи [мусулмонон ва] гувоҳон бинавис');
INSERT INTO "pioneers" VALUES(5,84,'5:84','Чаро ба Аллоҳ таоло ва он чи аз ҳақ ба мо расидааст, имон наёварем, ҳол он ки умед дорем, ки Парвардигорамон моро бо гурӯҳи шоистагон [ба биҳишт] дароварад"?');
INSERT INTO "pioneers" VALUES(5,85,'5:85','Пас, Аллоҳ таоло ба поси он чи гуфтанд, ба онон боғҳое [аз биҳишт] подош дод, ки аз зери [дарахтони] он ҷӯйборҳо ҷорӣ аст ва ҷовидона дар он хоҳанд монд. Ва ин аст ҷазои накукорон');
INSERT INTO "pioneers" VALUES(5,86,'5:86','Ва касоне, ки куфр варзиданд ва оёти моро такзиб карданд, онон аҳли дузаханд');
INSERT INTO "pioneers" VALUES(5,87,'5:87','Эй касоне, ки имон овардаед, чизҳои покизаеро, ки Аллоҳ таоло барои [истифодаи] шумо ҳалол кардааст, [ба баҳонаи зуҳду диндорӣ бар худ] ҳаром накунед ва [аз ҳад] таҷовуз нанамоед. Ба ростӣ, ки Аллоҳ таоло мутаҷовизон [-и ҳудуди илоҳӣ]-ро дӯст намедорад');
INSERT INTO "pioneers" VALUES(5,88,'5:88','Ва аз чизҳои ҳалол [ва] покизае, ки Аллоҳ таоло бар шумо рӯзӣ додааст, бихӯред ва аз Аллоҳ таоло, ки ба Ӯ имон доред, парво кунед');
INSERT INTO "pioneers" VALUES(5,89,'5:89','Аллоҳ таоло шуморо ба хотири савгандҳои беҳуда [ва беихтиёратон] бозхост намекунад, вале дар савгандҳое, ки [огоҳона] мехӯред [ва мешканед], муохаза мекунад. Пас, каффораи он, хӯрок додан ба даҳ тиҳидаст аз ғизоҳои [маъмулӣ ва] мутавассити шаҳратон аст ё либос додан ба даҳ нафар [аз мустамандон] ё озод кардани як барда. Агар касе [тавоноии молӣ надорад ё ҳеҷ як аз инҳоро] намеёбад, пас, се рӯз рӯза [бигирад]. Ин каффораи савгандҳои шумост, [барои] ҳар замон, ки савганд ёд кардед [ва онро шикастед]. Ва савгандҳои худро ҳифз кунед [ва нашканед]. Аллоҳ таоло ин чунин оёти худро барои шумо баён мекунад, бошад, ки сипос гузоред');
INSERT INTO "pioneers" VALUES(5,90,'5:90','Эй касоне, ки имон овардаед, бе тардид, шаробу қимор ва бутҳову тирҳои қуръакашӣ палид [ва] аз кор [-ҳои] шайтон ҳастанд, пас, аз онҳо дурӣ кунед, бошад, ки растагор шавед');
INSERT INTO "pioneers" VALUES(5,91,'5:91','Дар ҳақиқат, шайтон мехоҳад бо шаробу қимор, миёнатон душманӣ ва кина эҷод кунад ва шуморо аз ёди Аллоҳ таоло ва аз намоз боздорад, пас, [эй муъминон, ҳоло ки ҳақиқатро донистед] оё аз онҳо даст бармедоред?');
INSERT INTO "pioneers" VALUES(5,92,'5:92','Ва аз Аллоҳ таоло итоат кунед ва фармонбари паёмбар бошед ва [аз гуноҳу нофармонӣ] ҳазар кунед. Пас, агар рӯй гардондед, бидонед, ки вазифаи паёмбари Мо [чизе] ҷуз иблоғи ошкор нест');
INSERT INTO "pioneers" VALUES(5,93,'5:93','Бар касоне, ки имон овардаанд ва корҳои шоиста анҷом додаанд, гуноҳе дар он чи [пеш аз ҳукми таҳрими шароб] хӯрдаанд, нест, [албатта] дар сурате ки [аз муҳаррамот] парҳез кунанд ва имон биоваранд ва корҳои шоиста анҷом диҳанд, сипас парҳезкорӣ кунад ва имон биоваранд, он гоҳ [беш аз пеш] парҳезкорӣ кунанд ва некӣ намоянд. Ва Аллоҳ таоло накукоронро дӯст медорад');
INSERT INTO "pioneers" VALUES(5,94,'5:94','Эй касоне, ки имон овардаед, Аллоҳ таоло шуморо [дар ҳоле ки муҳрим ҳастед] ба чизе аз шикори [саҳроӣ], ки дар дастраси шумо ва найзаҳоятон бошад, меозмояд, то маълум дорад, чи касе дар ниҳон аз Ӯ метарсад. Пас, ҳар ки баъд аз ин [озмун] аз ҳад даргузарад [ва дар ҳоли ҳаҷ ё умра шикор кунад], азоби дарднок [дар пеш] дорад');
INSERT INTO "pioneers" VALUES(5,95,'5:95','Эй касоне, ки имон овардаед, дар ҳоли эҳром, шикорро накушед ва ҳар касе аз шумо, ки қасдан онро бикушад, бояд ҳаммонанди он чи ки куштааст, каффорае аз чаҳорпоён бидиҳад, [ба шарте] ки ду нафари одил аз шумо [баробарарзишии] онро таъйид кунанд ва [он гоҳ он каффораро ба сурати] қурбонӣ [ба мустамандони перомуни] Каъба бирасонад ё [муодили баҳои онро] ба тиҳидастони [Ҳарам] ғизо диҳад ё [агар тавони молӣ надошт] муодили онро [дар муқобили ҳар фақир як] рӯза бигирад, то кайфари амали худро бичашад. Аллоҳ таоло он чиро, ки [пеш аз ин таҳрим] гузаштааст, авф намуд ва ҳар ки [ба ин гуноҳ] бозгардад, [бидонад, ки] Аллоҳ таоло аз ӯ интиқом мегирад ва Аллоҳ таоло шикастнопазири додситон аст');
INSERT INTO "pioneers" VALUES(5,96,'5:96','[Эй мусулмонон, дар ҳоли эҳром] Сайди дарёӣ ва хӯроки он барои шумо ҳалол шудааст, то шумо [ки дар Макка муқим ҳастед] ва корвониён [ки мусофиранд, ҳамагӣ] аз он бархӯрдор шавед, ва [-ле] то замоне ки муҳрим ҳастед, сайди саҳроӣ бар шумо ҳаром аст ва аз Аллоҳ таоло, ки [рӯзи қиёмат] назди Ӯ ҷамъ мешавед, парво кунед');
INSERT INTO "pioneers" VALUES(5,97,'5:97','Аллоҳ таоло Каъбаи Байт-ул-ҳаромро василае барои устуворӣ [ва сомон бахшидан ба кори] мардум қарор дод ва [ҳамчунин, ҳурмати ҷангу даргирӣ дар] моҳи ҳаром ва [куштани] қурбонӣ [-и бенишон] ва қурбонии қалодадор [-ро барои осоиши мардум вазъ кард]. Ин [гуна аҳком] барои он аст, ки бидонед Аллоҳ таоло он чиро, ки дар осмонҳо ва замин аст медонад ва ин ки Аллоҳ таоло ба ҳар чизе доност');
INSERT INTO "pioneers" VALUES(5,98,'5:98','[Эй мардум] Бидонед, ки Аллоҳ таоло сахткайфар аст ва [дар айни ҳол] Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(5,99,'5:99','Паёмбар вазифае ҷуз расондани [ваҳй] надорад ва Аллоҳ таоло он чиро, ки ошкор мекунед ва пинҳон медоред, [ҳамаро] медонад');
INSERT INTO "pioneers" VALUES(5,100,'5:100','[Эй паёмбар] Бигӯ: "Палид ва пок яксон нестанд, ҳарчанд фаровонии палид [ӣ] туро ба шигифт оварад. Пас, эй хирадмандон, аз Аллоҳ таоло парво кунед, бошад, ки растагор шавед"');
INSERT INTO "pioneers" VALUES(5,101,'5:101','Эй касоне, ки имон овардаед, аз чизҳое напурсед, ки агар [посухи ҳақиқиаш] бароятон ошкор гардад, андуҳгинатон мекунад ва агар замоне, ки Қуръон нозил мегардад, пурсуҷӯ кунед, [ҳукму посухи пурсишҳоятон] бар шумо ошкор мегардад. Аллоҳ таоло аз он [масоил ва баёни онҳо] даргузашт ва Аллоҳ таоло омурзандаи бурдбор аст');
INSERT INTO "pioneers" VALUES(5,102,'5:102','Дар ҳақиқат, гурӯҳе [ки] пеш аз шумо [буданд низ] аз ин [гуна] пурсишҳо карданд, сипас [вақте ки ҷавоби ногувор барояшон омад], ба он [амал накарданд ва] куфр варзиданд');
INSERT INTO "pioneers" VALUES(5,103,'5:103','Аллоҳ таоло ҳеҷ гуна "баҳира" ва "соиба" ва "васила" ва "ҳом" [анвои шутурҳои мухталифе, ки мушрикон вақфи бутҳояшон мекарданд]-ро [барои худ] қарор надодааст, вале касоне, ки куфр варзиданд, [бо иддаои ҳурмати ин ҳайвонот] бар Аллоҳ таоло дурӯғ мебанданд ва бештарашон дарнамеёбанд');
INSERT INTO "pioneers" VALUES(5,104,'5:104','Ва ҳангоме ки ба онон гуфта шавад: "Ба сӯйи он чи Аллоҳ таоло нозил кардааст ва ба сӯйи паёмбар биёед", мегӯянд: "Он чи ниёгонамонро бар он ёфтаем, бароямон кофист". Оё агар ниёгонашон чизе намедонистанд ва ҳидоят наёфта буданд [боз ҳам аз онон пайравӣ мекарданд]?');
INSERT INTO "pioneers" VALUES(5,105,'5:105','Эй касоне, ки имон овардаед, муроқиби [имон ва тоати] худ бошед. Агар ҳидоят ёфта бошед, гумроҳии касоне, ки гумроҳ шудаанд, ба шумо зиёне намерасонад. Бозгашти ҳамаи шумо ба сӯйи Аллоҳ таоло аст, он гоҳ шуморо аз он чи мекардед, огоҳ месозад');
INSERT INTO "pioneers" VALUES(5,106,'5:106','Эй касоне, ки имон овардаед, ҳангоме ки [нишонаҳои] марги яке аз шумо фаро расид, ҳангоми васият бояд аз миёнатон ду нафари одилро гувоҳ бигиред ё агар мусофират кардед ва пешомади марг ба шумо расид [ва мусулмонеро наёфтед], ду нафар аз ғайри худ [ғайри мусулмон]-ро ба гувоҳӣ биталабед ва агар [дар ростгӯии онҳо] тардид доштед, он дуро баъд аз намоз нигоҳ доред, то ба Аллоҳ таоло савганд бихӯранд, ки: "Мо ҳозир нестем он [васият]-ро ба чизе фурӯшем, агарчи [ба суди] хешонамон бошад. Ва шаҳодати Аллоҳро китмон намекунем, ки дар ин сурат, ҳатман, аз гунаҳгорон хоҳем буд"');
INSERT INTO "pioneers" VALUES(5,107,'5:107','Пас, агар маълум шуд, ки он ду муртакиби гуноҳ [хиёнат ва дурӯғгӯӣ] шудаанд, ду тан аз касоне, ки мавриди ҷафои гувоҳон қарор гирифтаанд ва аз наздикон [-и майит] ҳастанд, ба ҷойи онон бархезанд ва ба Аллоҳ таоло савганд бихӯранд, ки: "Гувоҳии мо аз гувоҳии он ду дурустар аст ва мо [аз ҳақ] таҷовуз намекунем ва дар ғайри ин сурат, аз ситамгорон хоҳем буд"');
INSERT INTO "pioneers" VALUES(5,108,'5:108','Ин [равиш] наздиктар аст ба он ки шаҳодат ба сурати дурусташ адо шавад ё шоҳидон битарсанд, ки пас аз савганди онон [ворисон] савгандҳояшон рад шавад [ва хиёнаташон ошкор гардад] ва аз Аллоҳ таоло парво кунед ва [ин пандҳоро] бишнавед ва [бидонед, ки] Аллоҳ таоло мардуми [бадкор ва] нофармонро ҳидоят намекунад');
INSERT INTO "pioneers" VALUES(5,109,'5:109','Рӯзе [хоҳад расид], ки Аллоҳ таоло паёмбаронро гирд меоварад ва мепурсад: "[Умматҳоятон] Чи посухе ба [даъвати] шумо доданд"? Онон гӯянд: "Мо донише [ба ҳақиқати амр] надорем. Бе тардид, Туӣ, ки донои розҳои ниҳонӣ"');
INSERT INTO "pioneers" VALUES(5,110,'5:110','[Эй паёмбар, ёд кун аз] Ҳангоме ки Аллоҳ таоло фармуд: "Эй Исо – писари Марям, неъмати маро бар худ ва бар модарат ба ёд овар, он гоҳ ки туро бо Рӯҳ-ул-қудус [Ҷабраил] таъйид кардам, ки дар гаҳвора [ба эъҷоз] ва дар миёнсолӣ [ба ваҳй] бо мардум сухан мегуфтӣ ва он гоҳ ки ба ту китобу ҳикмат ва Тавроту Инҷил омӯхтам ва он гоҳ ки ба фармони Ман аз гил [чизе] ба шакли паранда месохтӣ ва дар он медамидӣ ва ба фармони Ман паррандае мешуд ва кӯри модарзоду бемории мубтало ба песиро ба фармони Ман шифо медодӣ ва он гоҳ ки мурдагонро ба фармони Ман [зинда аз қабр] берун меовардӣ ва он гоҳ ки [озори] Бани Исроилро – он гоҳ ки муъҷизоти ошкор барояшон оварда будӣ – аз ту боздоштам. Пас, касоне аз онон, ки куфр варзида буданд, гуфтанд: "Ин [корҳо чизе] нест магар афсуни ошкор"');
INSERT INTO "pioneers" VALUES(5,111,'5:111','Ва [Эй Исо, ба ёд овар] Замоне, ки ба ҳавориён ваҳй фиристодам, ки: "Ба Ман ва ба фиристодаам имон биёваред". Онон гуфтанд: "Имон овардем ва гувоҳ бош, ки мо таслим [-и фармонат] шудаем"');
INSERT INTO "pioneers" VALUES(5,112,'5:112','[Ва ёд кун аз] Он гоҳ ки ҳавориён гуфтанд: "Эй Исо – писари Марям, оё Парвардигорат метавонад суфрае [пур аз ғизо] аз осмон бар мо нозил кунад"? [Исо дар посух] Гуфт: "Агар муъмин ҳастед, аз Аллоҳ таоло парво кунед"');
INSERT INTO "pioneers" VALUES(5,113,'5:113','Онон гуфтанд: "Мехоҳем аз он бихӯрем ва дилҳоямон [ба рисолати ту] итминон ёбад ва бидонем, ки ба мо рост гуфтаӣ ва худ аз гувоҳони он бошем"');
INSERT INTO "pioneers" VALUES(5,114,'5:114','Исо – писари Марям - [дуо кард ва] гуфт: "Бор Илоҳо, Парвардигоро, суфраи [пурғизо] аз осмон бар мо бифрист, то иде бошад барои имрӯзиён ва ояндагонамон ва [ҳамчунин] муъҷизае аз [ҷониби Ту] бошад ва ба мо рӯзӣ деҳ, ки Ту беҳтарин рузидиҳандаӣ"');
INSERT INTO "pioneers" VALUES(5,115,'5:115','Аллоҳ таоло [дуои ӯро иҷобат кард ва] фармуд: "Ман онро бар шумо нозил мекунам, аммо ҳар касе аз шумо, ки пас аз он куфр биварзад, ӯро ба чунон азоб [-и сахт] дучор мекунам, ки ҳеҷ як аз ҷаҳониёнро ончунон азоб накарда бошам"');
INSERT INTO "pioneers" VALUES(5,116,'5:116','Ва [ёд кун аз] ҳангоме ки [рӯзи қиёмат] Аллоҳ таоло мефармояд: "Эй Исо – писари Марям, оё ту ба мардум гуфтӣ, ки: "Ману модарамро [ҳамчун] ду маъбуд ба ҷойи Аллоҳ таоло баргузинед"? [Исо] мегӯяд: "[Бор Илоҳо] Ту муназзаҳӣ, шоиста нест, ки ман [дар бораи хештан] чизе, ки ҳаққам нест бигӯям. Агар чунин [суханеро] гуфта бошам, бе гумон, Ту медонӣ. Ту он чиро, ки дар нафси ман аст, медонӣ ва [-ле] ман он чиро, ки дар зоти [поки] Туст намедонам. Бе тардид, Ту худ донои розҳои ниҳонӣ');
INSERT INTO "pioneers" VALUES(5,117,'5:117','Ҷуз он чи маро ба он фармон додӣ, чизе ба онон нагуфтам. [Гуфтам, ки] "Аллоҳро бипарастед, ки Парвардигори ману шумост". Ва то замоне, ки миёнашон ба сар мебурдам, бар [суханони] онон гувоҳ будам ва чун маро [ба сӯйи Хеш] баргирифтӣ, Ту худ муроқибашон будӣ ва Ту бар ҳар чизе гувоҳӣ');
INSERT INTO "pioneers" VALUES(5,118,'5:118','Агар ононро [бо адолатат] азоб кунӣ, бандагони Ту ҳастанд ва агар ононро [бо раҳматат] бубахшоӣ, Туӣ, ки шикастнопазири ҳакимӣ"');
INSERT INTO "pioneers" VALUES(5,119,'5:119','Аллоҳ таоло мефармояд: "Имрӯз рӯзест, ки ростии ростгӯён ба онон суд мебахшад. Онон боғҳое [дар биҳишт] доранд, ки ҷӯйборҳо аз зери [дарахтони] он ҷорӣ аст ва то абад ҷовидона дар он мемонанд. Аллоҳ таоло аз [аъмоли] онон хушнуд аст ва онон [низ] аз [подоши] Ӯ хушнуданд. Ин ҳамон растагории бузург аст"');
INSERT INTO "pioneers" VALUES(5,120,'5:120','Фармонравоии осмонҳо ва замин ва он чи дар онҳост, аз они Аллоҳ таоло аст ва Ӯ бар ҳар чизе тавоност');
INSERT INTO "pioneers" VALUES(6,1,'6:1','Ситоиш Аллоҳ таолорост, ки осмонҳо ва заминро офарид ва торикиҳо ва рӯшноӣ [шабу рӯз]-ро падид овард. Бо ин ҳама, касоне, ки куфр варзидаанд, [дигаронро дар тавону тадбир] бо Парвардигори худ баробар медонанд');
INSERT INTO "pioneers" VALUES(6,2,'6:2','Ӯст, ки шуморо аз гил офарид, он гоҳ муддатеро [барои ҳаётатон] муқаррар дошт ва аҷал, ҳатман, назди Ӯст. Бо ин ҳама шумо [дар қудрати Ӯ барои барпо шудани қиёмат] тардид мекунед');
INSERT INTO "pioneers" VALUES(6,3,'6:3','Ва Ӯст Аллоҳ таоло [Маъбуди ягона] дар осмонҳо ва замин, ниҳону ошкоратонро медонад ва он чиро ба даст меоваред,[ва анҷом медиҳед низ] медонад');
INSERT INTO "pioneers" VALUES(6,4,'6:4','Ва ҳеҷ нишонае аз нишонаҳои Парвардигорашон ба инон [кофирон] намеояд, магар ин ки аз он рӯйгардон мешаванд');
INSERT INTO "pioneers" VALUES(6,5,'6:5','Ҳамоно онон ҳақро, ҳангоме ки ба сӯяшон омад, дурӯғ шумурданд, пас, ба зудӣ хабарҳои он чиро, ки масхарааш мекарданд, ба онон хоҳад расид');
INSERT INTO "pioneers" VALUES(6,6,'6:6','Оё [кофирон] надиданд, ки пеш аз онон чи [бисёр] гурӯҳҳое аз мардумро нобуд кардем? [Умматҳое] Ки дар замин ба эшон чунон иқтидор [ва неруе] дода будем, ки [ҳаргиз] ба шумо чунон имконоте надодаем ва борони осмонро паёпай бар онон фиристодем ва наҳрҳо падид овардем, ки зери [хонаҳои] онон ҷорӣ буд. Пас, [ҳангоме ки носипосӣ карданд, Мо] эшонро ба [сазои] гуноҳҳояшон нобуд кардем ва пас аз онҳо гурӯҳи дигаре падид овардем');
INSERT INTO "pioneers" VALUES(6,7,'6:7','Ва [эй паёмбар, ҳатто] агар китобе [навишта] бар коғаз бар ту нозил мекардем ва эшон он [китоб]-ро бо дастони хеш ламс мекарданд, ҳатман, касоне, ки куфр варзидаанд мегуфтанд: «Ин [китоб чизе] нест, магар ҷодуи ошкор»');
INSERT INTO "pioneers" VALUES(6,8,'6:8','Ва [кофирон] гуфтанд: «Чаро фариштае ба ӯ нозил нашудааст [то гувоҳи ростгӯияш бошад]»? Ва агар фариштае мефиристодем [ва имон намеоварданд], ҳатман, кор ба поён мерасид ва муҳлате [барои тавба] намеёфтанд [ва ҳалок мешуданд]');
INSERT INTO "pioneers" VALUES(6,9,'6:9','Ва агар он [фиристода]-ро фариштае қарор медодем, ҳатман, вайро ба шакли марде дармеовардем [то битавонанд ӯро бубинанд ва суханонашро бифаҳманд] ва яқинан, ононро дучори ҳамон иштибоҳе мекардем, ки қаблан дар он буданд [зеро ӯ низ сурати инсон дошт]');
INSERT INTO "pioneers" VALUES(6,10,'6:10','[Эй паёмбар] Ҳатман, паёмбароне пеш аз ту [низ аз сӯйи кофирон] мавриди тамасхур воқеъ шуданд, пас, он чи ки масхарааш мекарданд, домонашонро гирифт [ва гирифтори азоб шуданд]');
INSERT INTO "pioneers" VALUES(6,11,'6:11','[Эй паёмбар, ба кофирон] Бигӯ: «Дар замин гардиш кунед ва бингаред, ки саранҷоми такзибкунандагон [-и оёти илоҳӣ] чигуна будааст»');
INSERT INTO "pioneers" VALUES(6,12,'6:12','Бигӯ: «Он чи дар осмонҳо ва замин аст, аз они кист»? Бигӯ: «Аз они Аллоҳи мутаол аст, ки раҳматро бар хеш воҷиб гардонидааст, [ва] ҳатман, шуморо дар рӯзи қиёмат, ки дар он шакке нест, [назди худ] ҷамъ хоҳад кард. Ва касоне, ки ба хеш зиён расонидаанд, имон намеоваранд');
INSERT INTO "pioneers" VALUES(6,13,'6:13','Ва ҳарчи дар шабу рӯз ором [-у қарор] дорад аз они Ӯст ва Ӯ шуновову доност');
INSERT INTO "pioneers" VALUES(6,14,'6:14','Бигӯ: «Оё ғайри Аллоҳи мутаолро [барои худ дӯст ва] корсоз гирам? [Парвардигоре] Ки падидоварандаи осмонҳо ва замин аст ва Ӯст, ки рӯзӣ медиҳад ва худ рӯзӣ дода намешавад». Бигӯ: «Ман фармон ёфтаам, ки нахустин касе [аз ин уммат] бошам, ки таслим [-и амри илоҳӣ] шудааст ва [Аллоҳ таоло бар ман фармон дод, ки]: “Ҳаргиз аз мушрикон набошам”');
INSERT INTO "pioneers" VALUES(6,15,'6:15','Бигӯ: «Агар ман аз [ҳукми] парвардигорам нофармонӣ кунам, бе тардид аз азоби рӯзи бузург метарсам»');
INSERT INTO "pioneers" VALUES(6,16,'6:16','Ҳар ки дар он рӯз ба вай азоб нарасад, ҳатман, [Аллоҳ таоло] ба ӯ раҳм кардааст ва ин ҳамон пирӯзии ошкор аст');
INSERT INTO "pioneers" VALUES(6,17,'6:17','[Эй инсон] Агар Аллоҳ таоло [бихоҳад] зиёне ба ту расонад, ҳеҷ касе ҷуз Ӯ бартарафкунандаи он нест; ва агар [ирода кунад, ки] хайре ба ту расонад, [касе боздорандаи он нест] пас, Ӯ бар ҳама чиз тавоност');
INSERT INTO "pioneers" VALUES(6,18,'6:18','Ва Ӯст, ки бар бандагони худ чира [ва мусаллат] аст ва Ӯ ҳакими огоҳ аст');
INSERT INTO "pioneers" VALUES(6,19,'6:19','[Эй паёмбар, бар мушрикон] Бигӯ: «Гувоҳии чи касе аз ҳама беҳтар аст»? Бигӯ: «Аллоҳ таоло [ки] миёни ману шумо гувоҳ аст ва ин Қуръон [аз ҷониби Ӯ] бар ман ваҳй шудааст, то ба василаи он ба шумо ва ҳар ки [ин паём ба ӯ] мерасад, ҳушдор диҳам. Оё шумо ба ростӣ гувоҳӣ медиҳед, ки бо Аллоҳ таоло маъбудони дигаре ҳаст?» Бигӯ: «Ман [ҳаргиз ба чунин чизе] гувоҳӣ намедиҳам». Бигӯ: «Яқинан Ӯ танҳо маъбуди ягона аст ва ман аз он чи [бо Ӯ] шарик қарор медиҳед, безорам»');
INSERT INTO "pioneers" VALUES(6,20,'6:20','Касоне, ки ба онон китоб [-и осмонӣ] додаем, ҳамон гуна ки писарони худро мешиносанд, [паёмбар]-ро мешиносанд. Онон ки [бо пайравӣ аз ҳавои нафс] ба хеш зиён расонидаанд, имон намеоваранд');
INSERT INTO "pioneers" VALUES(6,21,'6:21','Ва кист ситамгортар аз он ки бар Аллоҳ таоло дурӯғ мебандад [ва барояш шарик мепиндорад] ё оёташро дурӯғ меангорад? Яқинан, ситамгорони [мушрик] растагор намешаванд');
INSERT INTO "pioneers" VALUES(6,22,'6:22','Ва [ёд кун аз қиёмат] рӯзе, ки ҳамаи ононро ҷамъ мекунем ва ба касоне, ки ширк варзидаанд, мегӯем: «Маъбудонатон, ки [шарики Аллоҳ таоло] мепиндоштед, куҷо ҳастанд»?');
INSERT INTO "pioneers" VALUES(6,23,'6:23','Онгоҳ [посух ва] узрашон ҷуз ин нест, ки мегӯянд: «Ба Аллоҳ таоло –Парвардигорамон– савганд, ки мо мушрик набудем»');
INSERT INTO "pioneers" VALUES(6,24,'6:24','[Эй паёмбар] Бингар,чигуна бар худ дурӯғ бастанд ва он чи [дар мавриди шафоати маъбудонашон] ба дурӯғ мегуфтанд, [ҳама] маҳву нобуд шуд');
INSERT INTO "pioneers" VALUES(6,25,'6:25','Ва афроде аз онон [мушрикон] ҳастанд, ки [ҳангоми тиловати Қуръон] ба ту гӯш месупоранд ва [-ле] мо бар дилҳояшон парда афкандаем, то онро нафаҳманд ва дар гӯшҳояшон сангинӣ [қарор додаем] ва агар тамоми муъҷизот [ва далоили тавҳид]-ро бубинанд, [боз ҳам] ба он имон намеоваранд, то наздат биёянд ва бо ту муҷодала кунанд. Касоне, ки куфр варзидаанд, мегӯянд: «Ин [Қуръон, чизе] Нест магар афсонаҳои пешиниён»');
INSERT INTO "pioneers" VALUES(6,26,'6:26','Ва онон [мардум]-ро аз пайравии ӯ [паёмбар] бозмедоранд ва худ [низ] аз ӯ дур мешаванд ва [касе] ҷуз хештанро ҳалок намекунанд ва [-ле] намедонанд');
INSERT INTO "pioneers" VALUES(6,27,'6:27','Ва агар [ононро] ҳангоме ки дар баробари оташ [-и дузах] нигаҳ доштаанд, бубинӣ, [аз ҳолашон шигифтзада мешавӣ, ки] мегӯянд: «Эй кош, [ба дунё] бозгардонда мешудем ва оёти Парвардигорамонро такзиб намекардем ва аз муъминон будем»!');
INSERT INTO "pioneers" VALUES(6,28,'6:28','[Чунин нест, ки мегӯянд] Балки он чи пеш аз ин пинҳон мекарданд, барояшон ошкор шудааст ва агар [ҳам ба дунё] бозгардонда шаванд, бе тардид ба он чи аз он наҳй шуда буданд, бозмегарданд ва инон дурӯғгӯ ҳастанд');
INSERT INTO "pioneers" VALUES(6,29,'6:29','Ва [мушрикон] гуфтанд: «Ҷуз зиндагии мо дар дунё ҳеҷ [зиндагии дигаре] нест ва ҳаргиз барангехта нахоҳем шуд»');
INSERT INTO "pioneers" VALUES(6,30,'6:30','Ва агар ононро ҳангоме ки дар пешгоҳи Парвардигорашон нигоҳ дошта мешаванд, бубинӣ, [шигифтзада мешавӣ. Аллоҳ таоло ба онон] мегӯяд: «Оё ин [зиндагии пас аз марг] ҳақ нест?» Онон мегӯянд: «Бале, қасам ба Парвардигорамон [ки ҳақ аст]. [Аллоҳ таоло] мегӯяд: «Пас, ба сазои он ки куфр меварзидед, ин азобро бичашед»');
INSERT INTO "pioneers" VALUES(6,31,'6:31','Касоне, ки дидори Парвардигорро дурӯғ ангоштаанд, ҳатман, зиён диданд, [пас] ҳангоме ки ногаҳон қиёмат бар онон фаро расад, дар ҳоле ки бори [сангини] гуноҳҳони хешро бар пушташон ҳамл мекунанду мегӯянд: «Эй дареғ, бар мо аз он чи дар бораи он [дар дунё] кӯтоҳӣ кардем»! Ҳон! Чи бад аст боре, ки [бар дӯш] мекашанд!');
INSERT INTO "pioneers" VALUES(6,32,'6:32','Зиндагии дунё [чизе] ҷуз бозича ва саргармӣ нест, ва ҳатман, сарои охират барои касоне, ки парво пеша мекунанд, беҳтар аст. Оё намеандешед?');
INSERT INTO "pioneers" VALUES(6,33,'6:33','[Эй паёмбар] ба яқин мо медонем, ки ончи [мушрикон] мегӯянд, туро сахт ғамгин мекунад. Дар воқеъ, онон туро[дар дил] такзиб намекунанд, балки ситамгорон [бо он ки ҳақиқатро медонанд] оёти Аллоҳ таолоро инкор мекунанд');
INSERT INTO "pioneers" VALUES(6,34,'6:34','Ва пеш аз ту низ паёмбароне такзиб шудаанд, вале бар он чи такзиб шуданд ва озор диданд, шикебоӣ карданд, то [саранҷом] ёрии мо бар онон фаро расид ва суханони Аллоҳ таоло [дар мавриди пирӯзии паёмбаронаш]-ро ҳеҷ тағйирдиҳандае нест [ки монеъ гардад] ва мусалламан, ахбори паёмбарони [пешин ва достони шикасти душманонашон] ба ту расидааст');
INSERT INTO "pioneers" VALUES(6,35,'6:35','Ва [эй паёмбар] агар рӯйгардонии онон [аз Қуръон] бар ту ногувор [ва душвор] аст, агар метавонӣ нақбе (тунел) дар замин ё нардбоне дар осмон биҷӯӣ, то муъҷиза [дигар] барояшон биёварӣ [пас, чунон кун, вале суде надорад].Ва агар Аллоҳ таоло мехост, ҳатман, ононро бар [масири] ҳидоят ҷамъ мекард, пас, ҳаргиз аз нодонон мабош');
INSERT INTO "pioneers" VALUES(6,36,'6:36','Ба ростӣ, танҳо касоне [даъвати ҳақро] мепазиранд, ки мешунаванд [ва дарк мекунанд] ва Аллоҳ таоло мурдагонро [рӯзи қиёмат] бармеангезад, сипас ба сӯйи Ӯ бозгардонда мешаванд');
INSERT INTO "pioneers" VALUES(6,37,'6:37','Ва [мушрикон] гуфтанд: «Чаро нишонае [дар бораи ҳаққонияти Муҳаммад] аз тарафи Парвардигораш бар ӯ нозил нашудааст?» [Эй паёмбар] бигӯ: «Бе гумон, Аллоҳ таоло тавоност, ки нишонае [мутобиқи хости онон] нозил кунад», вале бештарашон намедонанд, [ки нузули ваҳй, бар асоси ҳикмату тадбир аст]');
INSERT INTO "pioneers" VALUES(6,38,'6:38','Ва ҳеҷ ҷунбандае дар замин нест ва на ҳеҷ парандае, ки бо болҳои худ парвоз мекунад, магар он ки онҳо [низ дар офариниш ва зиндагӣ] гурӯҳҳое монанди шумо ҳастанд. Мо ҳеҷ чизеро дар китоб [Лавҳи маҳфуз] фуругузор накардаем, сипас [ҳамаро рӯзи қиёмат] назди Парвардигорашон гирд меоваранд');
INSERT INTO "pioneers" VALUES(6,39,'6:39','Ва касоне, ки оёти моро дурӯғ ангоштаанд, кару гунг ва дар торикиҳо[-и ҷаҳл саргашта] ҳастанд. Аллоҳи мутаол ҳаркиро бихоҳад, гумроҳ мекунад ва ҳаркиро бихоҳад, бар роҳи рост қарораш медиҳад');
INSERT INTO "pioneers" VALUES(6,40,'6:40','[Эй паёмбар, ба мушрикон] Бигӯ: «Агар рост мегӯед, ба ман хабар диҳед, ки агар азоби Аллоҳи мутаол ба суроғатон биояд ё [ногаҳон] қиёмат барпо шавад, оё ғайр аз Аллоҳ таолоро [ба ёрӣ] мехонед?»');
INSERT INTO "pioneers" VALUES(6,41,'6:41','Оре, танҳо Ӯро [ба дуо] мехонед ва агар Ӯ бихоҳад, он чиро ки ба хотираш ӯро фаро мехонед, аз байн мебарад ва шумо [низ аз тарси азоб] шарикеро, ки барояш қоил шудаед, аз ёд мебаред');
INSERT INTO "pioneers" VALUES(6,42,'6:42','[Эй паёмбар] Бе тардид, пеш аз ту [низ паёмбароне] ба сӯйи умматҳо[-и пешин] фиристодем ва ононро ба тангдастӣ ва нохушӣ дучор сохтем, бошад, ки [ба даргоҳи илоҳӣ фурӯтанӣ ва] тазарруъ кунанд');
INSERT INTO "pioneers" VALUES(6,43,'6:43','Пас, чаро ҳангоме ки азоби мо ба онон расид, тазарруъ накарданд? Вале [дар ҳақиқат] дилҳояшон сахт шудааст ва шайтон он чиро анҷом медоданд, барояшон оростааст');
INSERT INTO "pioneers" VALUES(6,44,'6:44','Пас, вақте [мушрикон] ончиро, ки ба он панд дода шуда буданд, фаромӯш карданд, дарҳои ҳар чизе [аз неъматҳои илоҳӣ]-ро бар онон кушудем, то ба он чи ба эшон дода шуда буд, шод гардиданд. Ногаҳон ононро [ба куфр] гирифтем, пас, ҳамон дам [ҳамагӣ аз наҷоту раҳмат] маъюс шуданд');
INSERT INTO "pioneers" VALUES(6,45,'6:45','Пас, решаи он гурӯҳе, ки ситам карданд, барканда шуд ва ситоиш махсуси Аллоҳ таоло –Парвардигори ҷаҳониён– аст');
INSERT INTO "pioneers" VALUES(6,46,'6:46','[Эй паёмбар, ба мушрикон] Бигӯ: «Ба ман хабар диҳед, агар Аллоҳ таоло гӯшу чашмҳоятонро бигирад ва бар дилҳоятон муҳр бизанад, кадом маъбуде ба ҷуз Аллоҳ таоло [метавонад] онҳоро ба шумо бозгардонад?» Бубин, чигуна оётро [ба шеваҳои] гуногун [баён] мекунем, онгоҳ онон рӯй мегардонанд');
INSERT INTO "pioneers" VALUES(6,47,'6:47','Бигӯ: «Биандешед, агар азоби илоҳӣ ба ногоҳ ё ошкоро ба суроғатон биояд, оё ҷуз гурӯҳи ситамгорон [касе] нобуд мешавад?»');
INSERT INTO "pioneers" VALUES(6,48,'6:48','Ва мо паёмбаронро ҷуз башоратгар ва бимдиҳанда намефиристем, пас, ҳар ки имон биоварад ва [хештанро] ислоҳ кунад, на тарсе бар онон аст ва на андуҳгин мешаванд');
INSERT INTO "pioneers" VALUES(6,49,'6:49','Ва касоне, ки оёти моро дурӯғ шуморидаанд, ба [сазои] он ки нофармонӣ мекарданд, азоб [-и илоҳӣ] домангирашон хоҳад шуд');
INSERT INTO "pioneers" VALUES(6,50,'6:50','[Эй паёмбар, ба мушрикон] Бигӯ: «Ман намегӯям, ки ганҷинаҳои Аллоҳ таоло назди ман аст ва ғайб [низ] намедонам ва ба шумо намегӯям, ки ман фариштаам. Ман аз чизе пайравӣ намекунам магар аз он чи ки ба ман ваҳй мегардад». Бигӯ: «Оё нобино ва бино [кофир ва муъмин] яксонанд? Оё намеандешед»?');
INSERT INTO "pioneers" VALUES(6,51,'6:51','Ва [эй паёмбар] бо ин [Қуръон] ба онон, ки метарсанд, ба сӯйи Парвардигорашон [барангехта] ва гирд оварда шаванд, ҳушдор деҳ, [чаро] ки ба ҷуз Ӯ барояшон на [дӯсту] корсозе ҳаст ва на шафоатгаре, бошад, ки парво кунанд');
INSERT INTO "pioneers" VALUES(6,52,'6:52','Ва касонеро, ки [фақиранд ва ] бомдоду шомгоҳ Парвардигорашонро мехонанд [ва] хушнудии Ӯро металабанд [аз маҷолиси худ] тард накун [ки ба бузургони мушрикин таваҷҷуҳ кунӣ], на чизе аз ҳисоби онҳо бар туст ва на чизе аз ҳисоби ту бар онҳост, ки [бихоҳӣ аз перомунат] тардашон кунӣ ва аз ситамгорон шавӣ');
INSERT INTO "pioneers" VALUES(6,53,'6:53','Ва ин чунин бархе аз ишонро ба бархе дигар озмудем, то [кофирони сарватманд дар бораи муъминони мустаманд] бигӯянд: «Оё аз миёни мо инҳо ҳастанд, ки Аллоҳи таоло [бо бахшиши тавфиқи имон] бар онон миннат ниҳодааст?» Оё Аллоҳи мутаол ба [ҳоли] сипосгузорон донотар нест?');
INSERT INTO "pioneers" VALUES(6,54,'6:54','Ва [эй Паёмбар] ҳар гоҳ касоне, ки ба оёти Мо имон доранд, назди ту оянд, бигӯ: «Салом бар шумо. Парвардигоратон [дар ҳаққи шумо бахшоиш ва] раҳматро бар худ муқаррар доштааст. Ҳар касе аз шумо, ки ба нодонӣ кори баде анҷом диҳад ва онгоҳ тавба намояд ва [гузаштаи хешро] ислоҳ кунад, пас, [бидон, ки] яқинан, Ӯ омурзандаи меҳрубон аст»');
INSERT INTO "pioneers" VALUES(6,55,'6:55','Ва инчунин оёти [худ]-ро шарҳ медиҳем, то роҳи гунаҳкорон равшан шавад [ва шумо аз он дурӣ кунед]');
INSERT INTO "pioneers" VALUES(6,56,'6:56','[Эй паёмбар, ба мушрикон] Бигӯ: «Ман наҳй шудаам аз ин ки касонеро бипарастам, ки шумо ба ҷойи Аллоҳи мутаол мехонед». Бигӯ: «Ман аз ҳавасҳоятон пайравӣ намекунам, ки [агар чунин кунам] ҳатман, гумроҳ шудаам ва аз ҳидоятёфтагон нахоҳам буд»');
INSERT INTO "pioneers" VALUES(6,57,'6:57','Бигӯ: «Ман далели равшане аз Парвардигорам дорам, ва [-ле] шумо онро дурӯғ пиндоштед.Ва он чи ба шитоб хостораш ҳастед, [азоби илоҳӣ] назди ман нест. Фармон, танҳо аз они Аллоҳ таолост, [ки] ҳақро баён мекунад ва Ӯ беҳтарин ҷудокунанда[-и ҳақ аз ботил] аст»');
INSERT INTO "pioneers" VALUES(6,58,'6:58','[Эй паёмбар, ба мушрикон] Бигӯ: «Агар он чи ки ба шитоб хостораш ҳастед, назди ман буд, [бар саратон фуруд меовардам ва] кори миёни ману шумо ба поён мерасид, ва [-ле] Аллоҳ таоло ба [ҳоли] ситамгорон донотар аст [ва ба онон муҳлат медиҳад]»');
INSERT INTO "pioneers" VALUES(6,59,'6:59','Ва калидҳо [ганҷинаҳо]-и ниҳон назди Ӯст [ва] ҷуз Ӯ [ҳеҷ кас] онро намедонад ва [Ӯст, ки] он чиро дар хушкӣ ва дарёст медонад ва ҳеҷ барге [аз дарахт] намеафтад, магар ин ки онро медонад ва ҳеҷ донае дар торикиҳои замин ва ҳеҷ тару хушке нест, магар ин ки дар китоби равшан [Лавҳи маҳфуз] қарор дорад');
INSERT INTO "pioneers" VALUES(6,60,'6:60','Ва Ӯст, ки шабонгоҳ рӯҳатонро [ҳангоми хоб] мегирад ва [ҳам Ӯст, ки] ончиро дар рӯз ба даст овардаед, медонад. Сипас шуморо дар он [рӯз] бедор мекунад, то он ки замони муайяне сипарӣ гардад,; онгоҳ [ки вафот кардед, дар қиёмат] бозгаштатон ба сӯйи Ӯст. Сипас шуморо аз ончи анҷом медодед, огоҳ мекунад');
INSERT INTO "pioneers" VALUES(6,61,'6:61','Ва Ӯ бар бандагонаш чира аст ва нигаҳбононе [аз фариштагон] бар шумо мефиристад, то замоне, ки марги яке аз шумо фаро расад, фиристодагони мо [бо ҳамроҳи фариштаи марг] ҷонашро мегиранд ва онон [дар маъмурияти худ] кӯтоҳӣ намекунанд');
INSERT INTO "pioneers" VALUES(6,62,'6:62','Он гоҳ ба сӯйи Аллоҳ таоло, ки [ёру] корсози ростинашон аст, бозгардонда мешаванд. Огоҳ бошед! Ҳукм аз они Ӯст ва Ӯ сареътарин ҳисобрас аст');
INSERT INTO "pioneers" VALUES(6,63,'6:63','[Эй паёмбар, ба мушрикон] Бигӯ: «Кист, ки шуморо аз торикиҳо [ва сахтиҳои] хушкӣ ва дарё наҷот медиҳад? [Дар ҳоле ки] Ӯро ба зорӣ ва пинҳонӣ мехонед [ва мегӯед] “Агар моро аз ин [вартаи марг] наҷот диҳад, ҳатман, аз сипосгузорон хоҳем буд”»');
INSERT INTO "pioneers" VALUES(6,64,'6:64','Бигӯ: «Аллоҳи мутаол шуморо аз инҳо ва аз тамоми сахтиҳо наҷот медиҳад,он гоҳ шумо [боз ҳам] ширк меварзед»');
INSERT INTO "pioneers" VALUES(6,65,'6:65','Бигӯ: «Ӯ тавоност, ки бар шумо азобе [ҳамчун соиқа ва тӯфон] аз болои саратон ё [ҳамчун хушксолӣ ва зилзила] аз зери пойҳоятон бифиристад ё шуморо ба сурати гурӯҳҳои пароканда [ва ҷудо аз ҳам] дароварад ва азоби бархе аз шуморо ба бархе [дигар] бичашонад». Бингар чигуна оёт [-и худ]-ро гуногун баён мекунем, бошад, ки дарёбанд');
INSERT INTO "pioneers" VALUES(6,66,'6:66','[Эй паёмбар] Қавми ту он [Қуръон]-ро дурӯғ шумурданд, дар ҳоле ки он [китоб] ҳақ аст. [Ба кофирони қавмат] Бигӯ: «Ман [муроқибу] коргузори шумо нестам»');
INSERT INTO "pioneers" VALUES(6,67,'6:67','Ҳар хабаре саррасиде [барои вуқуъ] дорад ва [ба зудӣ] хоҳед донист [ки саранҷоматон чигуна аст]»');
INSERT INTO "pioneers" VALUES(6,68,'6:68','Ва ҳаргоҳ касонеро дидӣ, ки дар оёти Мо [бо душманӣ ва тамасхур] гуфтугӯ мекунанд, аз онон рӯ бигардон, то ба сухани дигаре бипардозанд.Ва агар шайтон туро ба фаромӯшӣ андохт, пас, аз он ки ёд овардӣ, бо қавми ситамгор [мушрик] манишин');
INSERT INTO "pioneers" VALUES(6,69,'6:69','Ва [ҳарчанд] чизе аз ҳисоб [ ва гуноҳи] онон бар [уҳдаи] парҳезгорон нест, вале ёдоварӣ [ва иршод лозим] аст, бошад, ки парво кунанд');
INSERT INTO "pioneers" VALUES(6,70,'6:70','Ва касонеро, ки динашонро ба бозӣ ва саргармӣ гирифтаанд ва зиндагии дунё ононро фирефтааст, раҳо кун ва [мардумро] бо ин [Қуръон] андарз деҳ, то мабодо касе ба [кайфари] ончи ба даст овардааст, ба ҳалокат биафтад, дар ҳоле ки дар баробари Аллоҳи мутаол барои [раҳоӣ аз азоби] он на [дӯст ва] корсозе дорад ва на шафоатгаре.Ва[ҳатто] агар [бихоҳад барои наҷоти хеш] ҳар фидяе диҳад, аз ӯ пазируфта намешавад. Инҳо ҳастанд, ки ба [кайфари] он чи ба даст овардаанд, ҳалок гаштаанд ва ба [сазоӣ] он ки куфр меварзиданд, шаробе аз оби ҷӯшон ва азобе пурдард [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(6,71,'6:71','[Эй паёмбар] Бигӯ: «Оё ба ҷойи Аллоҳи мутаол чизеро бихонем, ки на ба мо суд медиҳад ва на зиён мерасонад? Ва оё пас аз он ки Аллоҳ таоло моро ҳидоят кардааст, [ба сӯйи гумроҳӣ] ба ақиб бозгардем? Ҳамчун касе, ки шаётин ӯро [фиреб дода ва аз роҳи рост] бадар бурдаанд ва ӯ дар замин ҳайрону саргашта мондааст, дар ҳоле ки ӯ ёрон [-и дилсӯз] дорад, ки ба ҳидоят даъваташ мекунанд [ва мегӯянд]: “Ба сӯйи мо биё”»!Бигӯ: «Бе тардид, ҳидояти [ростин] ҳидояти Аллоҳ таолост ва мо фармон ёфтаем, ки таслими Парвардигори ҷаҳониён бошем');
INSERT INTO "pioneers" VALUES(6,72,'6:72','Ва ин ки намоз барпо доред ва аз ӯ парво кунед ва Ӯст, ки ба пешгоҳаш ҳашр хоҳед шуд»');
INSERT INTO "pioneers" VALUES(6,73,'6:73','Ва Ӯст, ки осмонҳо ва заминро ба ҳақ офарид ва рӯзе, ки [Аллоҳ таоло ба ҳар чизе] бигӯяд: «Мавҷуд шав», пас, бедиранг мавҷуд мешавад. Сухани Ӯ ҳақ аст. Ва рӯзе, ки дар сур дамида мешавад, фармонравоӣ аз они Ӯст. Ӯ донои пинҳону пайдост ва Ӯст, ки ҳакими огоҳ аст');
INSERT INTO "pioneers" VALUES(6,74,'6:74','Ва [ёд кун аз] ҳангоме ки Иброҳим ба падараш –Озар– гуфт: «Оё бутҳоро маъбуди [хеш] мегирӣ? Ба ростӣ, ки ман ту ва қавматро дар гумроҳии ошкоре мебинам»');
INSERT INTO "pioneers" VALUES(6,75,'6:75','Ва ин гуна фармондиҳии [шукӯҳманди] осмонҳо ва заминро ба Иброҳим нишон додем, то [ягонагии Аллоҳ таоло ва гумроҳии Озар ва қавмашро дарёбад ва] аз аҳли яқин бошад');
INSERT INTO "pioneers" VALUES(6,76,'6:76','Пас, ҳангоме ки [торикии] шаб Ӯро дарбар гирифт, ситорае дид, [ва] гуфт: «Ин Парвардигори ман аст».Ва чун ғуруб кард, гуфт: «Ғурубкунандагонро дӯст надорам»');
INSERT INTO "pioneers" VALUES(6,77,'6:77','Пас, ҳангоме ки моҳро тобанда дид, гуфт: «Ин Парвардигори ман аст».Ва чун ғуруб кард, гуфт: «Агар Парвардигорам маро ҳидоят накарда буд, ҳатман, аз гумроҳон будам»');
INSERT INTO "pioneers" VALUES(6,78,'6:78','Пас, ҳангоме ки хуршедро дурахшанда дид, гуфт: «Ин Парвардигори ман аст. Ин бузургтар аст». Он гоҳ вақте ки ғуруб кард, гуфт: «Эй қавм, ман аз он чи [барои Аллоҳ таоло] шарик қарор медиҳед, безорам»');
INSERT INTO "pioneers" VALUES(6,79,'6:79','Ба ростӣ, ки ман ҳақгароёна дин [-у ибодат]-и хешро барои касе холис кардам, ки осмонҳо ва заминро падид овардааст ва ҳаргиз дар зумраи мушрикон нестам»');
INSERT INTO "pioneers" VALUES(6,80,'6:80','Ва қавми ӯ бо вай ба муҷодала пардохтанд, [ва ӯро аз хашми бутҳо тарсонданд. Иброҳим] гуфт: «Оё дар бораи Аллоҳи мутаол бо ман муҷодала мекунед, дар ҳоле ки Ӯ маро [ба роҳи рост] ҳидоят кардааст ва аз он чи бо Ӯ шарик мепиндоред, ҳеҷ наметарсам, магар он ки Парвардигорам чизе [аз суд ва зиён бароям] хоста бошад. Илми Парвардигорам ҳама чизро фаро гирифтааст. Оё панд намегиред?');
INSERT INTO "pioneers" VALUES(6,81,'6:81','Ва чигуна аз он чи шарик [-и Аллоҳ таоло] мепиндоред битарсам бо он ки шумо худ намеҳаросед аз ин ки чизеро шарики Аллоҳи мутаол сохтаед, ки Ӯ [ҳеҷ] далеле дар бораи [ҳаққонияти] он бар шумо нозил накардааст? Пас, агар медонед, [бигӯед] кадом як аз ин ду гурӯҳ [муваҳҳид ва мушрик] ба амният [ва дурӣ аз азоб] сазовортар аст?');
INSERT INTO "pioneers" VALUES(6,82,'6:82','Касоне, ки имон овардаанд ва имонашонро ба [ҳеҷ] ширке наолудаанд, ононанд, ки амн [-у оромиш] доранд ва роҳёфтагонанд»');
INSERT INTO "pioneers" VALUES(6,83,'6:83','Ва ин [истидлол] ҳуҷҷати мо буд, ки ба Иброҳим дар баробари қавмаш додем. [Дар дунё ва охират мақоми] Ҳар киро, ки биҳоҳем, ба дараҷоти болое мебарем. [Эй паёмбар] Бе тардид, Парвардигорат ҳакиму доност');
INSERT INTO "pioneers" VALUES(6,84,'6:84','Ва ба ӯ [Иброҳим] Исҳоқ ва Яъқубро бахшидем ва ҳамаро [ба роҳи рост] ҳидоят кардем.Ва пештар Нуҳро [низ] ҳидоят карда будем ва аз насли ӯ Довуд, Сулаймон, Аюб, Юсуф, Мӯсо ва Ҳорунро [ба роҳи ҳақ раҳнамун гаштем] ва накукоронро ингуна подош медиҳем');
INSERT INTO "pioneers" VALUES(6,85,'6:85','Ва [ҳамчунин] Закариё, Яҳё, Исо ва Илёс[-ро роҳ намудем, ки] ҳама аз шоистагон буданд');
INSERT INTO "pioneers" VALUES(6,86,'6:86','Ва Исмоил, Ясаъ, Юнус ва Лут[-ро низ ҳидоят кардем] ва ҷумлагиро бар ҷаҳониён бартарӣ додем');
INSERT INTO "pioneers" VALUES(6,87,'6:87','Ва [низ] аз падарон ва фарзандону бародаронашон [бархеро бартарӣ додем ва] ононро баргузидем ва ба роҳи рост ҳидояташон кардем');
INSERT INTO "pioneers" VALUES(6,88,'6:88','Ин ҳидояти Аллоҳи мутаол аст, ки ҳар касе аз бандагонашро, ки бихоҳад, бо он ҳидоят мекунад ва агар ширк меварзиданд, он чи [аз аъмоли нек] ки анҷом дода буданд, табоҳ мешуд');
INSERT INTO "pioneers" VALUES(6,89,'6:89','Инон касоне ҳастанд, ки ба эшон китобу ҳикмат ва нубувват бахшидем. Пас, [эй паёмбар] агар инон [ мушрикон] ба ин [паёмбарон ва китобҳои илоҳӣ] куфр биварзанд, ҳатман, гурӯҳ [-и муҳоҷирин ва ансор ва тобеин]-ро бар [ҳифзу нигаҳбонии] он [усулу ақоид] мегуморем, ки нисбат ба он кофир набошанд');
INSERT INTO "pioneers" VALUES(6,90,'6:90','Онон касоне ҳастанд, ки Аллоҳ таоло ҳидояташон кардааст, пас, [эй паёмбар] аз ҳидоят [-у роҳи рост]-и эшон пайравӣ кун, [ва ба қавмат] бигӯ: «Ман дар баробари ин [рисолат] подоше аз шумо намехоҳам. Ин [Қуръон чизе] нест, магар [ёдоварӣ ва] панде барои ҷаҳониён»');
INSERT INTO "pioneers" VALUES(6,91,'6:91','Ва [мушрикон бузургӣ ва мақоми] Аллоҳ таолоро чунон ки сазовори Ӯст нашинохтанд, чаро ки гуфтанд: «Аллоҳ таоло бар [ҳеҷ] башаре чизе нозил накардааст». [Эй паёмбар] Бигӯ: «Он китоберо, ки Мӯсо овард, чи касе нозил кардааст? [Ҳамон китобе, ки] барои мардум рӯшноӣ ва раҳнамуде буд [ва шумо яҳудиён] онро ба сурати [пароканда бар] туморҳо дармеоваред [ва ҳар чиро ки мехоҳед] аз он ошкор месозед ва бисёреро пинҳон мекунед.Ва [акнун, эй қавми араб] чизҳое [аз оёт ва ахбори Қуръон] ба шумо омӯхта шуд, ки на худ медонистед ва на падаронатон». [Эй паёмбар] Бигӯ: «Аллоҳ таоло [онро нозил кардааст». Онгоҳ [дар ҷаҳлу гумроҳӣ] раҳояшон кун, то дар жарфо [-и ақоиди ботил]-и худ саргарми бозӣ бошанд');
INSERT INTO "pioneers" VALUES(6,92,'6:92','Ва ин [Қуръон] китоби мубораке аст, ки онро нозил кардем, [то] тасдиқкунандаи чизе бошад, ки пеш аз он нозил шудааст ва [ҳамчунин] барои он аст, ки ба [сокинони] Умму-л-қуро [Макка] ва [мардуми] перомунаш ҳушдор диҳӣ.Ва касоне, ки ба охират имон меоваранд, ба ин [китоб] низ имон меоваранд ва бар намози хеш мувозибат мекунанд');
INSERT INTO "pioneers" VALUES(6,93,'6:93','Ва кист ситамгортар аз он ки дурӯғе ба Аллоҳ таоло бибандад ё бигӯяд: «Ба ман [низ] ваҳй шудааст», дар ҳоле ки ҳеҷ ваҳйе ба ӯ нарасидааст ва [ё] касе, ки бигӯяд: «Ман [ҳам] монанди он чи ки Аллоҳ таоло нозил кардааст, [бароятон] нозил мекунам»? Ва [эй паёмбар, саҳнаи ҳавлноке аст] агар ҳангомеро бубинӣ, ки [ин] ситамгорон дар [тарсу] сахтиҳои марг фурӯ рафтаанд ва фариштагон [-и қабзи рӯҳ] дастҳояшонро [ба сӯйи онон] кушудаанд [ва фарёд мезананд]: «Ҷонҳои хешро [аз бадан] берун диҳед. Имрӯз ба [сазои] он чи ба ноҳақ бар Аллоҳ таоло дурӯғ мебастед ва дар баробари оёташ такаббур мекардед, ба азоби хоркунанда муҷозот мешавед»');
INSERT INTO "pioneers" VALUES(6,94,'6:94','Ва [рӯзи қиёмат Аллоҳи мутаол мефармояд] «ҳамон гуна ки шуморо нахустин бор [бидуни молу мақом] офаридем, [акнун низ] ҳатман, танҳо ба сӯйи мо омадаед ва он чиро ба шумо арзонӣ карда будем, пушти сари худ [дар дунё] вогузоштед ва [маъбудону] шафоатгаронеро, ки дар [кори] хеш шарикони [Аллоҳ таоло] мепиндоштед, ҳамроҳатон намебинем. Ба ростӣ, пайванди миёни шумо гусаста аст ва он чи [шарики мо ва шафеъи худ] мепиндоштед, гум гаштааст»');
INSERT INTO "pioneers" VALUES(6,95,'6:95','Бе тардид Аллоҳ таоло шикофандаи дона[-и гиёҳҳо] ва ҳаста[-и хурмо дар дили замин] аст. [Мавҷуди] Зиндаро аз [моддаи] беҷон берун меоварад [равиши гиёҳ аз дона] ва [моддаи ба зоҳир] беҷонро аз [мавҷуди] зинда хориҷ месозад [берун омадани тухм ё нутфа аз ҳайвонот]. Ин Аллоҳ таолост, [ки чунин қудрати мутлақе дорад].Пас, чигуна [аз роҳи бандагии Ӯ] бозгарданда мешавед?');
INSERT INTO "pioneers" VALUES(6,96,'6:96','[Ӯ] Шикофандаи сапедадам аст ва шабро [мояи] оромиш қарор дод ва хуршеду моҳро [низ] бо ҳисоб [ва назми дақиқ падид овард]. Тақдири [Парвардигори] шикастнопазири доно чунинаст');
INSERT INTO "pioneers" VALUES(6,97,'6:97','Ва [эй фарзандони Одам] Ӯст, ки ситорагонро бароятон қарор дод,то дар торикиҳои хушкӣ ва дарё ба [кумаки] онҳо роҳ ёбед. Ба ростӣ, ки мо нишонаҳо [-и тавону тадбири хеш]-ро барои мардуме, ки медонанд, ба тафсил баён кардаем');
INSERT INTO "pioneers" VALUES(6,98,'6:98','Ва Ӯст, ки шуморо аз як тан офарид, пас, барои шумо [дар раҳими модар] қароргоҳе аст ва [дар пушти падар] амонатгоҳе. Мо оёт [-и худ]-ро барои мардуме, ки [каломи илоҳиро] мефаҳманд, ба тафсил баён кардаем');
INSERT INTO "pioneers" VALUES(6,99,'6:99','Ва Ӯст, ки аз осмон обе фурӯ рехт ва аз он [борон] ҳар гуна рӯйидание баровардем ва аз он [низ ҳар кишту гиёҳи] сабзеро рӯёнидем, [ки] аз он [гиёҳҳо низ барои тағзияи шумо] донаҳои мутарокиме [ҳамчун гандуму ҷав] падид меоварем ва [ҳамчунин] аз шукуфтаи нахл хӯшаҳои наздик ба якдигар [рӯёнидем] ва боғҳое аз ангур ва зайтуну анор [офаридем меваҳое, ки] бархе ҳаммонанд ва бархе ноҳаммонанд ҳастанд. Вақте ба бор нишаст ба меваҳои он ва [тарзи] расиданаш бингаред. Дар ин [офариниш] барои мардуме, ки имон доранд, нишонаҳои [равшану қудрати илоҳӣ] аст');
INSERT INTO "pioneers" VALUES(6,100,'6:100','Ва [мушрикон] барои Аллоҳ таоло шариконе аз ҷин қоил шудаанд, ҳол онки [Аллоҳ таоло худ] онҳоро офаридааст.Ва[ҳамчунин] аз рӯйи нодонӣ барояш [фарзандон] писару духтар месозанд. Ӯ аз он чи васф мекунанд, поку бартар аст');
INSERT INTO "pioneers" VALUES(6,101,'6:101','[Ӯ] падидоварандаи осмонҳо ва замин аст, чигуна мумкин аст фарзанд дошта бошад, дар ҳоле ки ҳамсаре надорад? Ва ҳама чизро офарида ва Ӯ ба ҳар чизе доност');
INSERT INTO "pioneers" VALUES(6,102,'6:102','Ин аст Аллоҳ таоло-Парвардигори шумо, ҳеҷ маъбуде [ба ҳақ] ҷуз Ӯ нест, офаринандаи ҳама чиз аст, пас, Ӯро бипарастед ва [бидонед, ки] Ӯ [муроқибу] коргузори ҳама чиз аст');
INSERT INTO "pioneers" VALUES(6,103,'6:103','Чашмҳо Ӯро дарнамеёбанд ва [-ле] Ӯ чашмҳоро дармеёбад ва Ӯст, ки борикбин ва огоҳ аст');
INSERT INTO "pioneers" VALUES(6,104,'6:104','[Эй паёмбар, ба мушрикон бигӯ] «Баростӣ далоили ошкоре аз ҷониби Парвардигоратон барои шумо омадааст, пас, ҳар ки [бо басирату мантиқ дар бораи он] биандешад, ба суди ӯст ва ҳар ки чашм [бар ҳақиқат] бибандад, ба зиёни ӯст ва ман нигаҳбони шумо нестам»');
INSERT INTO "pioneers" VALUES(6,105,'6:105','Ва инчунин нишонаҳо [ва далоили қудрати илоҳӣ]-ро гуногун баён мекунем, то мабодо [мушрикон] бигӯянд: «[Онҳоро аз аҳли китоб] омӯхтаӣ».Ва мо ин оятҳоро барои гурӯҳе, ки медонанд [ва мефаҳманд], ба равшанӣ баён мекунем');
INSERT INTO "pioneers" VALUES(6,106,'6:106','[Эй паёмбар] Аз ончи ки аз ҷониби Парвардигорат ба ту ваҳй шудааст, пайравӣ кун, ҳеҷ маъбуде [ба ҳақ] ҷуз Ӯ нест ва аз мушрикон рӯй бигардон');
INSERT INTO "pioneers" VALUES(6,107,'6:107','Ва агар Аллоҳ таоло мехост, [онон ҳаргиз] ширк намеварзиданд.Ва мо туро бар онон нигаҳбон қарор надодаем ва ту [ҳаргиз муроқибу] коргузорашон нестӣ');
INSERT INTO "pioneers" VALUES(6,108,'6:108','Ва [эй муъминон, ҳаргиз] ба онҳо [бутҳо], ки [мушрикон] ба ҷойи Аллоҳ таоло [ба ниёиш] мехонанд, дашном надиҳед, [чаро ки] онон низ аз рӯйи душманӣ [ва] нодонӣ ба Аллоҳ таоло дашном медиҳанд. Ин гуна барои ҳар уммате кирдорашонро оростем.Сипас бозгашташон [дар қиёмат] ба сӯйи Парвардигорашон аст ва ононро аз ончи анҷом медоданд, огоҳ месозад');
INSERT INTO "pioneers" VALUES(6,109,'6:109','Ва [мушрикон] бо сахттарин савгандҳояшон ба Аллоҳ таоло савганд хӯрданд, ки агар муъҷизае [ки хостаанд] бар эшон биояд, ҳатман, имон меоваранд. [Эй паёмбар] Бигӯ: «Муъҷизот фақат дар ихтиёри Аллоҳ таоло аст»ва шумо [эй муъминон] чи медонед, ки агар [муъҷизае ҳам] биёяд, имон намеоваранд');
INSERT INTO "pioneers" VALUES(6,110,'6:110','Ва мо дилҳо ва дидагонашонро [аз дарки ҳақоиқ] бармегардонем, чунончи нахустин бор ба он имон наёварданд ва ононро дар [нофармонӣ ва] туғёнашон саргашта раҳо мекунем');
INSERT INTO "pioneers" VALUES(6,111,'6:111','Ва агар мо фариштагонро ба сӯйи онон мефиристодем ва [ҳатто] агар мурдагон бо онон ба сухан меомаданд ва ҳар чизеро даста-даста дар баробарашон гирд меовардем, [то ростгӯии паёмбарро гувоҳӣ диҳанд], боз ҳам имон намеоварданд, магар ин ки Аллоҳ таоло бихоҳад, вале бештарашон нодонӣ мекунанд');
INSERT INTO "pioneers" VALUES(6,112,'6:112','Ва [эй паёмбар- Муҳаммад, туро бо душмании мушрикон озмудем ва] ҳамчунин, барои ҳар паёмбаре душмане аз шаётини инс ва ҷин қарор додем, ки бархе аз онон дар ниҳон ба бархе дигар суханони фиребанда [ва беасос] илқо мекунанд ва агар Парвардигорат мехост, [ҳаргиз] чунин намекарданд.Пас, ононро ба дурӯғҳое, ки мебофанд, вогузор');
INSERT INTO "pioneers" VALUES(6,113,'6:113','Ва [чунин кардем, то ононро биозмоем, хостем] то дилҳои касоне, ки ба охират имон намеоваранд, ба он [сухани фиребанда] бигарояд ва онро биписандад ва ҳар гуноҳе, ки мехоҳанд муртакиб шаванд');
INSERT INTO "pioneers" VALUES(6,114,'6:114','[Эй паёмбар, ба мушрикон бигӯ] «Оё [сазост, ки барои қазоват дар бораи ихтилофи ману шумо] доваре ҷуз Аллоҳ таоло биҷӯем? Ва ҳол он ки Ӯст, ки ин китобро бо тафсил ба сӯйи шумо нозил кардааст».Ва онҳо, ки китоб [-и осмонӣ] ба эшон додаем, медонанд, ки он [Қуръон] аз ҷониби Парвардигорат ба ҳақ нозил шудааст.Пас, ҳаргиз аз тардидкунандагон набош');
INSERT INTO "pioneers" VALUES(6,115,'6:115','Ва суханони Парвардигорат [Қуръон] бо ростӣ [ дар гуфтор] ва адл [дар аҳком] ба ҳадди тамом ба камол расид. Ҳеҷ тағйирдиҳандае барои суханонаш нест ва Ӯ шунавову доност');
INSERT INTO "pioneers" VALUES(6,116,'6:116','Ва агар аз бештари мардуми замин итоат кунӣ, аз роҳи Аллоҳ таоло гумроҳат мекунанд. Онон ҷуз гумон [-и беасоси худ аз чизе] пайравӣ намекунанд ва ҷуз дурӯғ намебофанд');
INSERT INTO "pioneers" VALUES(6,117,'6:117','Бе тардид, Парвардигорат ба [ҳоли] касе, ки аз роҳи Ӯ мунҳариф мешавад, донотар аст ва Ӯ ба [ҳоли] роҳёфтагон [низ] донотар аст');
INSERT INTO "pioneers" VALUES(6,118,'6:118','Пас, [эй мардум] агар ба оёташ имон доред, [фақат] аз [гӯшти] он чи номи Аллоҳ таоло [ҳангоми забҳ] бар он бурдашудааст, бихӯред');
INSERT INTO "pioneers" VALUES(6,119,'6:119','Ва [эй мусулмонон] шуморо чи мешавад, ки аз он чи номи Аллоҳ таоло бар он бурдашудааст, намехӯред? Ҳол он ки [Аллоҳ таоло] он чиро бар шумо ҳаром кардааст, ба тафсил бароятон баён доштааст [пас, аз онҳо дурӣ кунед] магар чизе, ки ба [хӯрдани] он ночор шавед ва бе тардид, бисёре [аз мардум дигаронро] аз рӯйи нодонӣ бо ҳавасҳои хеш гумроҳ мекунанд. [Эй паёмбар] Яқинан, Парвардигорат ба [ҳоли] таҷовузкорон [аз ҳудуди илоҳӣ] донотар аст');
INSERT INTO "pioneers" VALUES(6,120,'6:120','Ва [эй мардум] гуноҳи ошкор ва пинҳонро раҳо кунед, [чаро ки] бе тардид, касоне, ки муртакиби гуноҳ мешаванд, ба зудӣ дар баробари ончи мекарданд, муҷозот хоҳанд шуд');
INSERT INTO "pioneers" VALUES(6,121,'6:121','Ва [эй муъминон] аз он чи [ҳангоми забҳ] номи Аллоҳ таоло бар он бурданашудааст, нахӯред, чаро ки он [кори] ҳатман, нофармонӣ аст ва дар ҳақиқат шаётин ба дӯстони худ илқо мекунанд, ки бо шумо [дар ин маврид] муҷодала намоянд.Ва агар аз онон итоат кунед, ҳатман, шумо [низ дар зумраи] мушрикон ҳастед');
INSERT INTO "pioneers" VALUES(6,122,'6:122','Оё касе, ки [дилаш аз ҷаҳлу ширк] мурда буд ва мо [бо тавҳиду имон] зиндааш гардонидем ва нуре барояш падид овардем, то дар партави [ҳидоят] он миёни мардум гом бардорад, ҳамчун касест, ки гӯӣ дар торикиҳо [гирифтор] аст ва аз он берун омаданӣ нест? Он чи кофирон мекарданд, ин гуна барояшон ороста шудааст');
INSERT INTO "pioneers" VALUES(6,123,'6:123','Ва ба ин гуна дар ҳар шаҳре бузургони гунаҳкорашро мегуморем, то дар он ба найранг бипардозанд [ва мардумро аз роҳи Аллоҳ таоло боздоранд], ва [-ле] онон ҷуз ба худ найранг намезананд ва [ин ҳақиқатро] дарк намекунанд');
INSERT INTO "pioneers" VALUES(6,124,'6:124','Ва [мушрикон] чун ояте [аз Қуръон] барояшон меояд мегӯянд: «Ҳаргиз ба он имон намеоварем, магар он ки ҳам монанди он чи ба паёмбарони илоҳӣ додашудааст, ба мо низ дода шавад». Аллоҳ таоло беҳтар медонад, ки рисолати хешро куҷо [ва бар уҳдаи чи касе] қарор диҳад. Ба зудӣ бар онон, ки муртакиби [нофармонӣ ва] гуноҳ шуданд, ба сазои найранге, ки варзида буданд, аз ҷониби Аллоҳ таоло хорӣ ва азоби сахте хоҳад расид');
INSERT INTO "pioneers" VALUES(6,125,'6:125','Пас, ҳар киро Аллоҳ таоло бихоҳад, ҳидоят кунад, дилашро барои [пазириши] Ислом мекушояд ва ҳаркиро бихоҳад, дар гумроҳӣ вогузорад, дилашро танг [ва баста] месозад [ва пазириши ҳақро чунон барояш маҳол мегардонад, ки] гӯӣ мехоҳад аз осмон боло равад [ва наметавонад], Аллоҳ таоло ин чунин азобро бар касоне, ки имон намеоваранд, мегуморад');
INSERT INTO "pioneers" VALUES(6,126,'6:126','Ва [эй паёмбар] ин [дини Ислом] роҳи рости Парвардигори туст. Ба ростӣ, ки мо оёт [-и худ]-ро барои гурӯҳе, ки панд мегиранд, ба тафсил баён кардем');
INSERT INTO "pioneers" VALUES(6,127,'6:127','Барои онон назди Парвардигорашон сарои саломату амният аст ва Ӯ ба [подоши] корҳое, ки мекарданд, [дӯсту] корсозашон аст');
INSERT INTO "pioneers" VALUES(6,128,'6:128','Ва [ёд кун аз қиёмат] рӯзе, ки ҳамаи онон [инсу ҷин]-ро гирд меоварад [ва мефармояд]: «Эй гурӯҳи ҷинниён, аз одамиён [бисёреро гумроҳ кардед ва пайравони] фаровон ёфтед».Ва дӯстони [мушрики] эшон аз [миёни] инсонҳо мегӯянд: «Парвардигоро, бархе аз мо аз бархе дигар баҳрабардорӣ карданд [ва дар куфру гуноҳ ба якдигар ёрӣ расониданд] ва [ин ки] мо ба поёне, ки бароямон муқаррар карда будӣ, расидаем» [Аллоҳ таоло] Мефармояд: «Ҷойгоҳатон оташ аст [ва] дар он ҷовидон хоҳед буд, магар он чиро Аллоҳ таоло бихоҳад [ки аз он бикоҳад ё бибахшояд]. Бе тардид,Парвардигорат ҳакиму доност»');
INSERT INTO "pioneers" VALUES(6,129,'6:129','Ва инчунин баъзе аз ситамгоронро ба баъзе дигар ба [сазои] он чи мекарданд, мусаллат мегардонем');
INSERT INTO "pioneers" VALUES(6,130,'6:130','Эй гурӯҳи ҷин ва инс, оё аз миёни шумо фиристодагоне бароятон наёмаданд, ки оёти Маро бар шумо бихонанд ва аз дидори ин рӯз ба шумо ҳушдор диҳанд? Онон мегӯянд: «[Оре] Мо алайҳи хеш гувоҳӣ медиҳем [ки омаданд]».Ва зиндагии дунё фиребашон дод ва ба зиёни худ гувоҳӣ доданд, ки кофир буданд');
INSERT INTO "pioneers" VALUES(6,131,'6:131','Ин [итмоми ҳуҷҷат] ба он хотир аст, ки Парвардигорат ҳеҷ гоҳ шаҳрҳоро, дар ҳоле ки мардумаш [аз паёми илоҳӣ бехабар ва] ғофиланд, ба ситам нобуд накардааст');
INSERT INTO "pioneers" VALUES(6,132,'6:132','Ва ҳамагон дар баробари он чи анҷом додаанд, дараҷоте доранд ва Парвардигорат аз он чи мекунанд, ғофил нест');
INSERT INTO "pioneers" VALUES(6,133,'6:133','Ва Парвардигорат бениёзу дорои раҳмат [-и бекарон] аст. [Эй бандагони гунаҳгор, Аллоҳ таоло] Агар бихоҳад, шуморо [бо азоб аз миён] мебарад ва пас аз шумо ҳаркиро бихоҳад, ҷойнишин месозад, ҳамчунонки шуморо низ аз насли гурӯҳи дигар офарид');
INSERT INTO "pioneers" VALUES(6,134,'6:134','[Эй кофирон] Он чи ба шумо ваъда дода мешавад, яқинан, хоҳад омад ва шумо наметавонед [Аллоҳ таолоро] нотавон созед [ва аз кайфараш бигурезед]');
INSERT INTO "pioneers" VALUES(6,135,'6:135','Бигӯ: «Эй қавми ман, ба шеваи худ амал кунед [ва бар куфру гумроҳии худ бимонед, ки] бе тардид, ман [низ ба имони хеш] амал мекунам, пас, ба зудӣ хоҳед донист, ки [пирӯзии дунё ва] фарҷоми [некуи]он саро аз они кист. Бе гумон, ситамгорон [-и мушрик] растагор нахоҳанд шуд»');
INSERT INTO "pioneers" VALUES(6,136,'6:136','Ва [мушрикон] барои Аллоҳ таоло аз он чи офаридааст, аз кишту чаҳорпоён, саҳме таъйин карданд ва ба гумони худ гуфтанд: «Ин махсуси Аллоҳ таолост ва ин махсуси шарикони мо [бутҳо]». Пас, он чи барои шариконашон буд, ба Аллоҳ таоло намерасад, ва [-ле] он чи барои Аллоҳ таоло буд, ба шарикони онҳо мерасад. Чи бад доварӣ мекунанд!');
INSERT INTO "pioneers" VALUES(6,137,'6:137','Ва ин гуна буд, ки шарикони онҳо [аз шаётину бутҳо] дар назари бисёре аз мушрикон [ки фақир буданд] қатли фарзандонашонро оростанд [ва зебо ҷилва доданд], то ҳалокашон кунанд ва оинашонро бар онон [ошуфта ва] муштабаҳ созанд.Ва агар Аллоҳ таоло мехост, чунин намекарданд, пас, [эй паёмбар, ту низ] ононро бо [туҳмату] дурӯғҳояшон вогузор');
INSERT INTO "pioneers" VALUES(6,138,'6:138','Ва ба пиндори хеш гуфтанд: «[Истифода аз] Ин чаҳорпоён ва киштзор[-ҳое, ки вақфи бутҳо шуда] мамнуъ аст ва ҳеҷ кас аз он намехӯрад, магар он [гурӯҳ аз ходимони бутҳо] ки мо мехоҳем ва чаҳорпоёне ҳастанд, ки [савор шудан бар] пушташон ҳаром шудааст».Ва домҳое [доштанд], ки [ҳангоми забҳ] номи Аллоҳ таолоро бар онҳо намебурданд, [инҳо ҳама боварҳое буд, ки] ба дурӯғ ба Аллоҳ таоло нисбат медоданд [ва Аллоҳ таоло] ба хотири он чи ба дурӯғ мебофтанд, ҷазояшонро хоҳад дод');
INSERT INTO "pioneers" VALUES(6,139,'6:139','Ва гуфтанд: «Он чи дар шиками ин ҷаҳорпоён аст, [агар зинда мутавалид шавад] ба мардони мо ихтисос дорад ва бар ҳамсаронамон ҳаром шудааст, ва [-ле] агар [ҷанин] мурда бошад, ҳамаи онон [аз зану мард] дар он шариканд. Ба зудӣ [Аллоҳ таоло] ононро ба сазои ин тавсифашон кайфар хоҳад дод. Ба ростӣ, ки Ӯ ҳакими доност');
INSERT INTO "pioneers" VALUES(6,140,'6:140','Касоне, ки аз рӯйи [чаҳлу] бехирадӣ фарзандони худро [аз тарси фақр] куштанд ва бо дурӯғ бастан ба Аллоҳ таоло он чиро, ки Аллоҳ таоло рӯзияшон карда буд, [бар худ] ҳаром донистанд, сахт зиён карданд. Бе тардид, онон гумроҳ шуданд ва ҳидоятёфта набуданд');
INSERT INTO "pioneers" VALUES(6,141,'6:141','Ӯст, ки боғҳое [бо буттаҳои] дорбастдор ва [дарахтони] бедорбаст падид овард ва [низ] дарахти хурмо ва зироате, ки меваҳояш гуногун аст ва дарахти зайтун ва анор, ки [баргҳояшон] ҳаммонанд ва [меваҳояшон] ноҳаммонанд аст. [Эй мардум] Аз меваи он чун ба бор нишаст, бихӯред ва рӯзи чиданаш ҳаққи [закоти бенавоён аз] онро бипардозед, ва [-ле] исроф накунед, [чаро ки] бе тардид, Ӯ исрофкоронро дӯст намедорад');
INSERT INTO "pioneers" VALUES(6,142,'6:142','Ва [низ] аз чаҳорпоён [ҳайвоноти] борбар ва [ҳайвоноти] ғайриборӣ [-ро офарид]. Аз он чи Аллоҳ таоло ба шумо рӯзӣ додааст, бихӯред ва аз гомҳои шайтон пайравӣ накунед. Бе гумон, ӯ душмани ошкоре барои шумост');
INSERT INTO "pioneers" VALUES(6,143,'6:143','Ҳашт [раъси] нару мода [барои истифодаи шумо офарид] аз меш як ҷуфт ва аз буз як ҷуфт, [эй паёмбар, ба мушрикон] бигӯ: «Оё [Аллоҳ таоло] он ду нарро ҳаром кардааст ё модаҳоро? Ё он чи ки раҳимҳои [ин] ду мода дар бар дорад? Агар рост мегӯед [ва бар таҳримашон далеле доред], аз рӯйи илм [-у мантиқ] ба ман хабар диҳед');
INSERT INTO "pioneers" VALUES(6,144,'6:144','Ва аз шутур як ҷуфт ва аз гов як ҷуфт [офарид].Бигӯ: «Оё Аллоҳ таоло] он ду нарро ҳаром кардааст ё ду модаро? Ё он чи ки раҳимҳои [ин] ду мода дар бар дорад? Оё вақте Аллоҳ таоло шуморо ба ин [таҳрим] супориш мекард, [онҷо] ҳозир будед? Пас, кист ситамгортар аз он ки бар Аллоҳ таоло дурӯғ мебандад, то бидуни ҳеҷ [санаду] илме мардумро гумроҳ кунад? Ба ростӣ, ки Аллоҳ таоло гурӯҳи ситамгоронро ҳидоят намекунад»');
INSERT INTO "pioneers" VALUES(6,145,'6:145','Бигӯ: «Дар он чи [аз аҳкоми илоҳӣ, ки] ба ман ваҳй шудааст, [ғизои] ҳаромеро, ки хӯрданаш барои хӯрандае ҳаром бошад, намеёбам, магар [гӯшти] мурдор ё хуни рехта ё гӯшти хук, ки ҳатман, палид [наҷасу ҳаром] аст, ё [қурбоние, ки ҳангоми забҳ] аз рӯйи нофармонӣ, номи ғайри Аллоҳ таоло бар он бурда бошанд». Пас, ҳар ки [барои ҳифзи ҷони худ, ба хӯрдани онҳо] ночор шавад, агар саркаш ва зиёдахоҳ набошад [бар ӯ гуноҳе нест]. Ба ростӣ, ки Парвардигорат омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(6,146,'6:146','Ва ҳар [ҳайвони] чанголдореро бар яҳудиён ҳаром кардем ва аз гову гӯсфанд, чарбҳои он дуро бар онон ҳаром кардем, магар чарбҳое, ки бар пушти онҳо ё даври рӯдаҳост ё он чи бо устухон даромехтааст. Ин гуна ононро ба сазои саркашияшон муҷозот кардем ва бе тардид, Мо ростгӯем');
INSERT INTO "pioneers" VALUES(6,147,'6:147','Пас, [эй паёмбар] агар туро такзиб карданд, бигӯ: «Парвардигоратон раҳмати густурда дорад, ва [-ле] азобаш аз гурӯҳи гунаҳкорон бозгардонда нахоҳад шуд»');
INSERT INTO "pioneers" VALUES(6,148,'6:148','Касоне, ки ширк варзиданд, хоҳанд гуфт: «Агар Аллоҳ таоло мехост, на мо ширк меовардем ва на падаронамон ва [ҳамчунин] чизеро [худсарона] таҳрим намекардем». Касоне, ки пеш аз онон буданд, низ ҳамин гуна [паёмбарони худро] такзиб карданд, то азоби моро чашанд. [Эй паёмбар, ба ин мушрикон] Бигӯ: «Оё [донишу] далеле наздатон ҳаст, ки барои Мо ошкор кунед? [Дар ҳақиқат] шумо ҷуз пиндор [аз чизе] пайравӣ намекунед ва ҷуз дурӯғ намегӯед»');
INSERT INTO "pioneers" VALUES(6,149,'6:149','Бигӯ: «Далели расо ва равшан аз они Аллоҳ таолост ва агар мехост, ҳамаи шуморо ҳидоят мекард»');
INSERT INTO "pioneers" VALUES(6,150,'6:150','Бигӯ: «Гувоҳони худро, ки гувоҳӣ медиҳанд, ки Аллоҳ таоло инҳоро ҳаром кардааст, биёваред». Пас, [ҳатто] агар гувоҳӣ доданд, ту бо онон [ба чунин дурӯғе] гувоҳӣ мадеҳ ва аз ҳавасҳои касоне, ки оёти Моро дурӯғ шумурданд ва касоне, ки ба охират имон намеоваранд ва [маъбудони ботилро] бо Парвардигорашон ҳамто қарор медиҳанд, пайравӣ накун');
INSERT INTO "pioneers" VALUES(6,151,'6:151','Бигӯ: «Биёед он чиро, ки Парвардигоратон бар шумо ҳаром кардааст, бароятон бихонам: Ин ки чизеро бо Ӯ шарик қарор надиҳед ва ба падару модар некӣ кунед ва фарзандони худро аз [тарси] тангдастӣ накушед. Мо ба шумо ва онон рӯзӣ медиҳем ва ба корҳои зишт, чи ошкор ва чи ниҳон, наздик нашавед ва касеро, ки Аллоҳ таоло [қатлашро] ҳаром кардааст ҷуз ба ҳақ накушед. Ин аст [он чи Аллоҳ таоло] шуморо ба [пайравӣ аз] он супориш кардааст, бошад, ки биандешед');
INSERT INTO "pioneers" VALUES(6,152,'6:152','Ва ба моли ятим наздик нашавед, магар ба беҳтарин шакл [ки ба суду салоҳи ӯ бошад], то ба синни [булуғ ва] рушди худ бирасад ва [санҷиши] паймона ва тарозуро ба адолат комил кунед.Мо ҳеҷ касеро ҷуз ба андозаи тавонаш мукаллаф намесозем ва ҳангоме ки сухан мегӯед [ё гувоҳӣ медиҳед], пас, адолатро риоя кунед, ҳарчанд, [дар бораи] хешованд бошад ва ба аҳду паймони Аллоҳ таоло вафо кунед. Ин аст [он чи Аллоҳ таоло] шуморо ба [пайравӣ аз] он супориш кардааст, бошад, ки панд гиред');
INSERT INTO "pioneers" VALUES(6,153,'6:153','Ва ин ки [огоҳ бошед] ин роҳи рости Ман аст, пас, аз он пайравӣ кунед ва аз [дигар] роҳҳо пайравӣ накунед, [чаро ки] шуморо аз роҳи Ӯ ҷудо месозад. Ин аст [он чи Аллоҳ таоло] шуморо ба [пайравӣ аз] он супориш кардааст, бошад, ки парво кунед');
INSERT INTO "pioneers" VALUES(6,154,'6:154','Сипас ба Мӯсо китоб [Таврот] додем, то бар касе, ки некӣ кардааст, неъматро тамом кунем ва барои он ки [ҳукми] ҳар чизеро ба тафсил баён намоем ва раҳнамуду раҳмате гардад, бошад, ки ба дидори Парвардигорашон имон биёваранд');
INSERT INTO "pioneers" VALUES(6,155,'6:155','Ва ин [Қуръон] ки нозил кардем, китоби пурбаракат аст, аз он пайравӣ кунед ва парво намоед, бошад, ки мавриди раҳмат қарор гиред');
INSERT INTO "pioneers" VALUES(6,156,'6:156','[Онро нозил кардем] То нагӯед: «Китоб [-и осмонӣ] танҳо бар ду тоифаи пеш аз мо [ яҳудиён ва масеҳиён] нозил шуда буд ва мо аз қироату тиловати эшон бехабар будем»');
INSERT INTO "pioneers" VALUES(6,157,'6:157','Ё нагӯед: «Агар китоб [-и осмонӣ] бар мо нозил мешуд, аз онон роҳёфтатар будем». Пас, бе тардид, [ин ки] аз ҷониби Парвардигоратон далели равшан ва ҳидоят ва раҳмате бароятон омадааст. Пас, кист ситамгортар аз он ки оёти Аллоҳ таолоро дурӯғ ангорад ва аз он рӯ бигардонад? Ба зудӣ касонеро, ки аз оёти Мо рӯ мегардонанд, ба сазои он ки [ба каломи илоҳӣ] пушт мекарданд, ба азоби сахт [-у дардовар] муҷозот хоҳем кард');
INSERT INTO "pioneers" VALUES(6,158,'6:158','Оё [кофирон] интизори ҷуз ин доранд, ки фариштагон [-и марг] наздашон биёянд ё Парвардигорат [худ барои доварӣ] биояд ё бархе аз нишонаҳои Парвардигорат [дар мавриди қиёмат] фаро расад? Рӯзе, ки бархе аз нишонаҳои Парвардигорат ошкор гардад, имон овардани касе, ки пештар имон наёварда ё бо имонаш хайре ҳосил накарда аст, суде ба ҳолаш надорад. [Эй паёмбар, ба мушрикон] Бигӯ: «Мунтазир бошед. Бе гумон мо [низ] мунтазирем»');
INSERT INTO "pioneers" VALUES(6,159,'6:159','[Эй паёмбар] касоне, ки дини худро пароканда сохтанд ва фирқа-фирқа шуданд, бе тардид туро бо онон коре нест; онон корашон фақат бо Аллоҳ таолост.Онгоҳ [Аллоҳ таоло дар рӯзи қиёмат] аз он чи мекарданд, огоҳашон месозад');
INSERT INTO "pioneers" VALUES(6,160,'6:160','[Дар он рӯз] Ҳар [муъмине] ки кори неке биоварад, даҳ баробараш [подош] хоҳад дошт ва ҳар ки кори баде биоварад, ҷуз монандаш кайфар нахоҳад дид ва ба онон ситам нахоҳад шуд');
INSERT INTO "pioneers" VALUES(6,161,'6:161','[Эй паёмбар, ба мушрикон] Бигӯ: «Бе тардид, Парвардигорам маро ба роҳи рост ҳидоят кардааст: дини пойдор [ва] оини Иброҳим, ки ҳақгаро буд ва ҳаргиз дар зумраи мушрикон набуд»');
INSERT INTO "pioneers" VALUES(6,162,'6:162','Бигӯ: «Бе тардид, намозам ва қурбониҳои ман ва зиндагӣ ва маргам ҳама барои Аллоҳ таоло – Парвардигори ҷаҳониён аст');
INSERT INTO "pioneers" VALUES(6,163,'6:163','[Ҳам Ӯ ки] Шарике надорад ва ба ин [яктопарастӣ холисона] дастур ёфтаам ва ман нахустин мусулмон аз ин қавм ҳастам»');
INSERT INTO "pioneers" VALUES(6,164,'6:164','Бигӯ: «Оё ба ҷуз Аллоҳи мутаол парвардигореро биҷӯем ва ҳол он ки Ӯ Парвардигори ҳама чиз аст? Ва ҳеҷ касе [кори ношоистае] анҷом намедиҳад, магар он ки [бори гуноҳаш] бар худи ӯст ва ҳеҷ гунаҳгоре бори [гуноҳи] дигареро ба дӯш намекашад. Сипас бозгашти [ҳамаи] шумо ба сӯйи Парвардигоратон аст. Он гоҳ [дар рӯзи қиёмат, аз ҳақиқати] он чи дар он ихтилоф доштед, огоҳатон месозад');
INSERT INTO "pioneers" VALUES(6,165,'6:165','Ва Ӯст, ки шуморо ҷойнишини [пешиниён дар] замин қарор дод ва дараҷоти бархе аз шуморо [дар офариниш ва сарват] бар бархи дигар бартарӣ дод, то [ба василаи] он чи ба шумо додааст, озмоишатон кунад. Бе тардид, Парвардигорат зудкайфар аст ва яқинан Ӯ бисёр омурзанда [ва] меҳрубон аст');
INSERT INTO "pioneers" VALUES(7,1,'7:1','Алиф, лом, мим, сод');
INSERT INTO "pioneers" VALUES(7,2,'7:2','[Эй паёмбар, ин Қуръон] Китобест, ки бар ту нозил шудааст, пас, набояд дар синаат нисбат ба он тардиде бошад, [нозил шуда] то бо он [мардумро] бим диҳӣ ва барои муъминон панд [ва тазаккуре] бошад');
INSERT INTO "pioneers" VALUES(7,3,'7:3','[Эй мардум] Он чиро аз ҷониби Парвардигоратон бар шумо нозил шудааст, пайравӣ кунед ва аз [дӯстону] корсозони дигар ба ҷуз Ӯ пайравӣ накунед, чи андак панд мепазиред!');
INSERT INTO "pioneers" VALUES(7,4,'7:4','Чи бисёр [сокинони рустоҳо] ва шаҳрҳо, ки нобудашон кардем ва азоби Мо шабҳангом ё дар ҳоле ки ба хоби нимрӯзӣ фурӯ рафта буданд, ба суроғашон омад');
INSERT INTO "pioneers" VALUES(7,5,'7:5','Ва чун азоби Мо ба суроғашон омад, суханашон ҷуз ин набуд, ки гуфтанд: «Ба ростӣ, ки мо [кофиру] ситамгор будем»');
INSERT INTO "pioneers" VALUES(7,6,'7:6','Мусалламан, Мо аз касоне, ки [паёмбаронамон] ба сӯяшон фиристода шуданд, [дар бораи пазириши паёми ҳақ] суол хоҳем кард ва ҳатман, аз паёмбарон [низ дарбораи посухи умматҳояшон ба даъвати онон] хоҳем пурсид');
INSERT INTO "pioneers" VALUES(7,7,'7:7','Яқинан, [кирдори бандагонро] бо илм [-и худ] барояшон баён мекунем ва мо ҳаргиз ғоиб [ва ғофил аз аҳволи онон] набудаем');
INSERT INTO "pioneers" VALUES(7,8,'7:8','Дар он рӯз, вазн [-у меъёри санҷиши аъмол, адолат] ҳақ аст. Пас,онҳое, ки вазни аъмолашон сангин бошад, ононанд, ки растагоранд');
INSERT INTO "pioneers" VALUES(7,9,'7:9','Ва онҳое, ки вазни аъмолаш сабук бошад, онон касоне ҳастанд, ки ба хеш зиён расонидаанд, чаро ки [бо инкори Қуръон] ба оёти Мо ситам мекарданд');
INSERT INTO "pioneers" VALUES(7,10,'7:10','Ва [эй мардум] ба ростӣ, ки Мо шуморо дар замин ҷой додем ва дар онҷо бароятон васоил [-у имконот]-и зиндагӣ ниҳодем. Чи андак шукр мегузоред!');
INSERT INTO "pioneers" VALUES(7,11,'7:11','Ва бе тардид, Мо [насли] шуморо [дар замин] падид овардем, сипас шуморо [ба беҳтарин ҳолат] шакл додем, он гоҳ ба фариштагон гуфтем: «Барои [бузургдошту эҳтиром, бар] Одам саҷда кунед», пас, [ҳамагӣ] саҷда карданд, магар иблис, ки аз саҷдакунандагон набуд');
INSERT INTO "pioneers" VALUES(7,12,'7:12','[Аллоҳ таоло] Фармуд: «Вақте ки туро [ба саҷда] фармон додам, чи чиз монеат шуд, ки саҷда кунӣ»? [Иблис] Гуфт: «Ман аз ӯ беҳтарам: маро аз оташ офаридӣ ва [-ле] ӯро аз гил падид овардӣ»');
INSERT INTO "pioneers" VALUES(7,13,'7:13','[Аллоҳ таоло] Фармуд: «Аз ин [биҳишти барин] фуруд о, [зеро] ҳаққи ту нест, ки дар он [макон гарданкашӣ ва] такаббур кунӣ. Пас, берун рав, [ки] бе тардид, ту аз хоршудагонӣ»');
INSERT INTO "pioneers" VALUES(7,14,'7:14','[Иблис] Гуфт: «То рӯзе, ки [инсонҳо] барангехта шаванд, маро муҳлат бидеҳ [то фиребашон диҳам]');
INSERT INTO "pioneers" VALUES(7,15,'7:15','[Аллоҳ таоло] Фармуд: «Ҳатман, ту аз муҳлатёфтагонӣ»');
INSERT INTO "pioneers" VALUES(7,16,'7:16','[Иблис] гуфт: «Пас, ба хотири он ки маро ба гумроҳӣ [ва ҳалокат] афкандӣ, ман низ бар сари роҳи мустақими Ту барои [фиреби] онон менишинам');
INSERT INTO "pioneers" VALUES(7,17,'7:17','Сипас бар онон, аз рӯбарӯ ва аз пушти сар ва аз тарафи росту чапашон, дармеоям ва [чунон мекунам] бештарашро шукургузор [-и неъматҳои худ] намеёбӣ»');
INSERT INTO "pioneers" VALUES(7,18,'7:18','[Аллоҳ таоло] Фармуд: «Аз онҷо [биҳишт] накуҳида ва рондашуда берун рав. Ҳар касе аз онон, ки аз ту пайравӣ кунад, [дар сипоҳи туст ва рӯзи қиёмат] ҳатман, дузахро аз ҳамаи шумо пур хоҳам кард');
INSERT INTO "pioneers" VALUES(7,19,'7:19','Ва, эй Одам, ту ва ҳамсарат дар биҳишт сокин шавед ва аз [хӯрданиҳои он аз] ҳар ҷойе ки хостед бихуред, ва [-ле] ба ин дарахт наздик нашавед ки аз ситамгорони [нофармон] хоҳед буд»');
INSERT INTO "pioneers" VALUES(7,20,'7:20','Пас, шайтон он дуро васваса кард, то он чи аз шармгоҳашон ба онон пӯшида монда буд, бар эшон ошкор созад [пас, ҳилае андешид] ва гуфт: «Парвардигоратон шуморо аз [хӯрдани меваи] ин дарахт манъ накард, магар [барои] он ки [мабодо табдил ба] ду фаришта гардед ё аз ҷовидонон шавед»');
INSERT INTO "pioneers" VALUES(7,21,'7:21','Ва барои он ду савганд хӯрд, ки: «Ман, ҳатман, хайрхоҳатон ҳастам»');
INSERT INTO "pioneers" VALUES(7,22,'7:22','Пас, онҳоро бо фиреб [аз мақому манзилашон] фуруд овард ва чун аз [меваи] он дарахт бичашиданд, шармгоҳашон бар онон намоён шуд ва шурӯъ карданд ба қарор додани барги [дарахтони] биҳишт бар [шармгоҳи] худ.Ва Парвардигорашон [инчунин] ба онҳо нидо дод: «Оё шуморо аз [хӯрдани меваи] он дарахт манъ накардам ва ба шумо нагуфтам, ки бе тардид, шайтон душмани ошкори шумост»?!');
INSERT INTO "pioneers" VALUES(7,23,'7:23','[Он ду] Гуфтанд: «Парвардигоро, мо [ бо сарпечӣ аз дастурат] ба хештан ситам кардаем ва агар моро наёмурзӣ ва бар мо раҳм накунӣ, ҳатман, аз зиёнкорон хоҳем буд»');
INSERT INTO "pioneers" VALUES(7,24,'7:24','[Аллоҳ таоло] Фармуд: «[Аз биҳишт бар замин] фуруд оед, [дар ҳоле ки азин пас] бархе аз шумо душмани бархе дигаред ва дар замин бароятон то муддат [-и муайян]-е қароргоҳ ва баҳрамандӣ хоҳад буд»');
INSERT INTO "pioneers" VALUES(7,25,'7:25','[Ва] Фармуд: «Дар он зиндагӣ мекунед ва дар ҳамон[ҷо] мемиред ва [рӯзи қиёмат низ] аз он берун оварда мешавед»');
INSERT INTO "pioneers" VALUES(7,26,'7:26','Эй фарзандони Одам, ҳамоно бароятон либосе фурӯ фиристодем, ки шармгоҳҳотонро мепӯшонад ва [мояи] зинати шумост ва [барои муъмин] либоси парҳезгорӣ беҳтар аст. Ин аз нишонаҳои [қудрати] Аллоҳ таолост, бошад, ки [инсонҳо] панд гиранд');
INSERT INTO "pioneers" VALUES(7,27,'7:27','Эй фарзандони Одам, [огоҳ бошед, ки] шайтон шуморо нафиребад, чунончи падару модаратонро аз биҳишт берун кард ва либосашонро аз танашон [берун] кашид, то шармгоҳашонро ба онон нишон диҳад. Бе тардид, ӯ ва гурӯҳаш аз ҷойе, ки онҳоро намебинед, шуморо мебинанд. Ба ростӣ, ки Мо шаётинро дӯстони касоне қарор додаем, ки имон намеоваранд');
INSERT INTO "pioneers" VALUES(7,28,'7:28','Ва чун [мушрикон] кори зиште анҷом медиҳанд, мегӯянд: «Мо ниёгони худро бар ин [кор] ёфтаем ва Аллоҳ таоло моро ба [анҷоми] он фармон додаст». Бигӯ: «Аллоҳ таоло ҳаргиз ба зишткорӣ фармон намедиҳад. Оё суханеро, ки намедонед, ба Аллоҳ таоло нисбат медиҳед?»');
INSERT INTO "pioneers" VALUES(7,29,'7:29','[Эй паёмбар, ба мушрикон] Бигӯ: «Парвардигорам ба адолат фармон додааст ва [инки ҳама ҷо, ба вижа] дар ҳар масҷиде [холисона] рӯйи хешро ба сӯйи [қиблаи] Ӯ кунед ва Ӯро дар ҳоли [ба дуо ва ниёиш] бихонед ки дин [-и худ]-ро барояш холис намудаед. [Ва бидонед] Ҳамон гуна ки шуморо дар оғоз падид овард, [пас аз марг низ бори дигар ба сӯяш] бозмегардед');
INSERT INTO "pioneers" VALUES(7,30,'7:30','[Аллоҳ таоло] Гурӯҳеро ҳидоят кард ва бар гурӯҳ [-и дигар]-е гумроҳӣ муқаррар гаштааст; зеро онон ба ҷойи Аллоҳ таоло шаётинро [ба унвони] дӯстон [-и хеш] баргузиданд ва гумон мекарданд, ки ҳидоят ёфтаанд»');
INSERT INTO "pioneers" VALUES(7,31,'7:31','Эй фарзандони Одам, ҳангоми рафтан ба масҷид [ва дар ҳар намозе] зинати худро баргиред [ва либоси шоиста бипӯшед] ва [дар зиндагӣ, аз хӯрокҳои покиза] бихӯред ва биошомед, вале исроф накунед, [ки] бе тардид, Аллоҳ таоло исрофкунандагонро дӯст намедорад');
INSERT INTO "pioneers" VALUES(7,32,'7:32','[Эй паёмбар, ба мушрикон] Бигӯ: «Либос [-у зинатҳо]е-ро ки Аллоҳ таоло барои бандагонаш падид оварда ва [ҳамчунин] рӯзиҳои покизаро чи касе ҳаром кардааст?». Бигӯ: «Ин [неъматҳо] дар зиндагии дунё барои касонест, ки имон овардаанд, [ва агар чи кофирон низ аз онҳо баҳраманданд] рӯзи қиёмат махсус [-и муъминон] хоҳад буд. Мо инчунин оёт [-и худ]-ро барои гурӯҳе, ки медонанд, ба тафсил баён мекунем');
INSERT INTO "pioneers" VALUES(7,33,'7:33','Бигӯ: «Парвардигорам зишткориҳо, чи ошкор [бошад] ва чи пинҳон ва гуноҳу ситами ноҳақро ҳаром намудааст ва ин ки чизеро шарики Аллоҳ таоло бидонед, ки далеле бар [ҳаққонияти] он нозил накардааст ва инки чизеро, ки намедонед, ба Аллоҳ таоло нисбат диҳед');
INSERT INTO "pioneers" VALUES(7,34,'7:34','Ҳар уммате аҷале дорад.Пас, ҳангоме ки аҷалашон ба сар ояд, на лаҳзае [аз он] таъхир мекунанд ва на [бар он] пешӣ мегиранд');
INSERT INTO "pioneers" VALUES(7,35,'7:35','Эй фарзандони Одам, агар паёмбароне аз худи шумо [наздатон] биоянд, ки оёти Маро бароятон бихонанд, [аз он пайравӣ кунед] пас, онон, ки парҳезгорӣ намоянд ва [рафторҳои гузаштаро] ислоҳ кунанд, на тарсе хоҳанд дошт ва на андуҳгин мешаванд');
INSERT INTO "pioneers" VALUES(7,36,'7:36','Ва касоне, ки оёти Моро дурӯғ мепиндоранд ва дар баробараш [гарданкашӣ ва] такаббур меварзанд, аҳли дузаханд ва ҷовидона дар он хоҳанд монд');
INSERT INTO "pioneers" VALUES(7,37,'7:37','Пас, кист ситамгортар аз он ки бар Аллоҳ таоло дурӯғ мебандад ё оёташро дурӯғ мепиндорад? Онон касоне ҳастанд, ки насибашон аз ончи [дар Лавҳи маҳфуз] муқаррар шудааст, ба эшон хоҳад расид, то он гоҳ ки фиристодагони Мо ба суроғашон меоянд, ки ҷонашонро бигиранд, [ба онон] мегӯянд: «Онҳое, ки ба ҷойи Аллоҳ таоло мепарастидед, куҷо ҳастанд»? [Онон] Мегӯянд: «Аз [назди] мо гум шуданд».Ва [ин гуна] алайҳи худ гувоҳӣ медиҳанд, ки кофир будаанд');
INSERT INTO "pioneers" VALUES(7,38,'7:38','[Фариштагон] Ба онҳо мегӯянд: «Дар байни гурӯҳҳое аз ҷин ва инс, ки пеш аз шумо [кофиру гумроҳ] буданд, дар оташ [-и дузах] ворид шавед». Ҳар гоҳ гурӯҳе ворид мешаванд, гурӯҳи дигарро лаънат мекунанд, то ҳангоме ки [саранҷом] ҳамагӣ дар онҷо ба ҳам мерасанд. Пайравони [фиребхӯрдаи] онҳо дар бораи раҳбаронашон мегӯянд: «Парвардигоро, инҳо буданд, ки моро гумроҳ карданд. Пас, азоби дучанд аз оташ ба онон бидеҳ». [Аллоҳ таоло] Мефармояд: «Барои ҳар кадом [аз шумо азоби] дучанд аст, вале намедонед»');
INSERT INTO "pioneers" VALUES(7,39,'7:39','Раҳбарони онҳо ба пайравонашон мегӯянд: «Шумо бар мо ҳеҷ имтиёзе надоред, пас, ба [кайфари] ончи мекардед, азобро бичашед»');
INSERT INTO "pioneers" VALUES(7,40,'7:40','Касоне, ки оёти Моро дурӯғ ангоштанд ва дар баробари онҳо [гарданкашӣ ва] такаббур намуданд, дарҳои осмон ҳаргиз барояшон кушода намешавад ва ба биҳишт ворид нахоҳанд шуд, магар онки шутур аз сӯрохи сӯзан ворид шавад.Ва гунаҳгоронро инчунин кайфар медиҳем');
INSERT INTO "pioneers" VALUES(7,41,'7:41','Бар эшон бистаре аз [оташи] дузах [муҳайёст] ва [рӯйандозашон низ] пӯшишҳое [аз оташ аст] ва ситамгоронро инчунин кайфар медиҳем');
INSERT INTO "pioneers" VALUES(7,42,'7:42','Касоне, ки имон овардаанд ва корҳои шоиста кардаанд [ҳар андоза амали нек кардаанд, мепазирем, чаро ки] ва ҳеҷ касро ҷуз ба андозаи тавонаш мукаллаф намесозем. Инон биҳиштиянд ва дар он ҷовидон хоҳанд буд');
INSERT INTO "pioneers" VALUES(7,43,'7:43','Ва ҳар кинаеро аз синаҳояшон мезудоем.Аз зери [қасрҳои] онон ҷуйборҳо ҷорӣ аст ва мегӯянд: «Сипос махсуси Аллоҳ таоло аст, ки моро [тавфиқи ибодат дод ва] ба ин [биҳишт] ҳидоят намуд.Ва агар Аллоҳ таоло ҳидоятамон намекард, ҳаргиз роҳ намеёфтем. Бе тардид, фиристодагони Парвардигорамон, ба ростӣ ва дурустӣ омаданд». [Он гоҳ] Ба онон нидо дода мешавад, ки: «Ин биҳишт ба подоши он чи мекардед, ба шумо расидааст»');
INSERT INTO "pioneers" VALUES(7,44,'7:44','Биҳиштиён дузахиёнро нидо медиҳанд, ки: «Мо он чиро, ки Парвардигорамон ба мо ваъда дода буд, дуруст ёфтем. Оё шумо [низ] он чиро, ки Парвардигоратон ваъда карда буд, дуруст ёфтед?» Онон мегӯянд: «Оре». Пас, нидодиҳандае миёнашон нидо медиҳад, ки: «Лаънати Аллоҳ таоло бар ситамгорон [кофирон] бод»!');
INSERT INTO "pioneers" VALUES(7,45,'7:45','[Ҳамон] Касоне, ки [мардумро] аз роҳи Аллоҳ таоло бозмедоштанд ва онро [бо илқои шубаҳот] мунҳариф мехостанд ва охиратро бовар надоштанд»');
INSERT INTO "pioneers" VALUES(7,46,'7:46','Дар миёни он ду [гурӯҳ: биҳиштӣ ва дузахӣ] монеъе аст ва бар аъроф [баландиҳои он монеъ] мардоне ҳастанд, ки ҳар як [аз он ду гурӯҳ]-ро аз [нишонаҳои] чеҳраашон мешиносанд ва [аз баландии аъроф] аҳли биҳиштро нидо медиҳанд, ки: «Салом бар шумо бод». Инон умеди биҳишт доранд ва ҳанӯз ба он ворид нашудаанд');
INSERT INTO "pioneers" VALUES(7,47,'7:47','Ва чун чашмонашон мутаваҷҷеҳи аҳли дузах мешавад, мегӯянд: «Парвардигоро, моро бо гурӯҳи ситамгорон [дар якҷо] қарор мадеҳ»');
INSERT INTO "pioneers" VALUES(7,48,'7:48','Аҳли аъроф мардоне [аз дузахиёнро], ки ононро аз чеҳраҳояшон мешиносанд, садо мезананд [ва] мегӯянд: «Ҷамъият [-у моландузӣ]-и шумо ва он [ҳама] гарданкашӣ [ва такаббур]-е, ки мекардед, ба ҳолатон суде надошт»');
INSERT INTO "pioneers" VALUES(7,49,'7:49','Оё инҳо [биҳиштиён] ҳамон касоне ҳастанд, ки савганд ёд мекардед, ки Аллоҳ таоло [ҳаргиз] раҳмате ба онон намерасонад? [Сипас Аллоҳ таоло мефармояд: эй муъминон] Ба биҳишт ворид шавед [ки дар онҷо], на тарсе хоҳед дошт ва на андуҳгин мешавед');
INSERT INTO "pioneers" VALUES(7,50,'7:50','Аҳли дузах биҳиштиёнро нидо медиҳанд, ки: «Аз он об ё ончи Аллоҳ таоло рӯзиятон додааст, андаке моро баҳраманд созед» [Онон дар посух] Мегӯянд: «Аллоҳ таоло ҳардуро бар кофирон ҳаром кардааст»');
INSERT INTO "pioneers" VALUES(7,51,'7:51','[Ҳамон] Касоне, ки дини худро ба саргармӣ ва бозӣ гирифтанд ва зиндагии дунё фиребашон дод. Пас, чунон ки онон дидори имрӯзашонро аз ёд бурданд ва оёти Моро инкор карданд, Мо [низ] имрӯз ононро аз ёд мебарем [ва дар азоб раҳо мекунем]');
INSERT INTO "pioneers" VALUES(7,52,'7:52','Ба ростӣ, мо барояшон китобе овардем, ки онро бар [асоси] илм [-и бекарони илоҳӣ] ба тафсил баён кардаем ва [нозил кардем, то] барои гурӯҳе, ки имон меоваранд, ҳидояту раҳмате бошад');
INSERT INTO "pioneers" VALUES(7,53,'7:53','Оё онон [кофирон] ҷуз таъвили оёт [ва фаро расидани қиёмат] интизори [дигаре] доранд? Рӯзе, ки таъвилаш [қиёмат] фаро расад, касоне, ки онро пештар фаромӯш карда буданд, мегӯянд: «Яқинан, фиристодагони Парвардигорамон ба ҳақ омада буданд.Пас, оё [имрӯз] шафоатгароне дорем, ки шафоатамон кунанд ё [метавонем ба дунё] бозгардем, то корҳое ғайр аз ончи мекардем, анҷом диҳем»? Бе тардид, [онон] ба худ зиён заданд ва ончиро ба дурӯғ [маъбуди худ] месохтанд, [ҳама] маҳву нобуд шуд');
INSERT INTO "pioneers" VALUES(7,54,'7:54','[Эй мардум] Бе гумон, Парвардигори шумо Аллоҳ таоло аст, ки осмонҳо ва заминро дар шаш рӯз офарид, сипас бар Арш қарор гирифт. [Торикии] Шабро бо [рӯшноии] рӯз мепӯшонад, ки [ҳаряки] он [дигаре]-ро шитобон меҷӯяд ва хуршеду моҳ ва ситорагонро [падид овард], ки [ҳамагӣ] ба фармони Ӯ ба хидмат [-иинсон] гумошта шудаанд. Огоҳ бошед! Офариниш ва фармонравоӣ аз они Ӯст. Пурбаракат [-у бузург] аст Аллоҳ таолое, ки Парвардигори ҷаҳониён аст');
INSERT INTO "pioneers" VALUES(7,55,'7:55','[Эй муъминон] Парвардигоратонро ба зорӣ ва пинҳонӣ бихонед, ки Ӯ таҷовузкунандагон [-и ҳудуди илоҳӣ]-ро дӯст намедорад');
INSERT INTO "pioneers" VALUES(7,56,'7:56','Ва дар замин, пас аз он ки [бо биъсати паёмбарон ва диндории муъминон] ислоҳ гаштааст, фитна ва фасод накунед ва Ӯ [Аллоҳ таоло]-ро бо биму умед бихонед, [ки] бе тардид, раҳмати Аллоҳ таоло ба накукорон наздик аст');
INSERT INTO "pioneers" VALUES(7,57,'7:57','Ӯст, ки пешопеши [борони] раҳматаш бодҳоро башоратбахш мефиристад, то абрҳои сангинборро [бар дӯш] кашанд [ва] онро ба сӯйи замини [хушку] мурда мефиристем,он гоҳ бар он [сарзамин] об фурӯ меборем ва [аз баракати] он [об] ҳар гуна мевае бармеоварем. Ба ин сон, мурдагонро [низ аз қабр] хориҷ месозем, бошад, ки панд гиред');
INSERT INTO "pioneers" VALUES(7,58,'7:58','Сарзамини покиза [ва омода] гиёҳаш ба фармони Парвардигораш [фаровону пурбор] мерӯяд ва аз он [шӯразоре], ки нопок аст, ҷуз [гиёҳи] андаку беарзиш намерӯяд. Ин гуна оёт [-и худ]-ро барои гурӯҳе, ки сипос мегузоранд, гуногун баён мекунем');
INSERT INTO "pioneers" VALUES(7,59,'7:59','Бе тардид, Мо Нуҳро ба сӯйи қавмаш фиристодем, пас, гуфт: «Эй қавми ман, Аллоҳ таолоро бипарастед, ки ҷуз Ӯ маъбуде [ба ҳақ] надоред. [Агар Ӯро ибодат накунед] Ман бар шумо аз азоби рӯзи бузург метарсам»');
INSERT INTO "pioneers" VALUES(7,60,'7:60','Ашроф [-у бузургон]-и қавмаш гуфтанд: «Ба ростӣ, ки мо туро дар гумроҳии ошкор мебинем»');
INSERT INTO "pioneers" VALUES(7,61,'7:61','Ӯ гуфт: «Эй қавми ман, ҳеҷ гуна гумроҳӣ [ва инҳирофе] дар ман нест, балки ман фиристодае аз [ҷониби] Парвардигори ҷаҳониёнам');
INSERT INTO "pioneers" VALUES(7,62,'7:62','Паёмҳои Парвардигорамро ба шумо мерасонам ва бароятон хайрхоҳӣ мекунам ва аз [шариати] Аллоҳ таоло чизҳое медонам, ки шумо намедонед');
INSERT INTO "pioneers" VALUES(7,63,'7:63','Оё тааҷҷуб кардаед, ки панде аз ҷониби Парвардигоратон тавассути марде аз худи шумо бароятон омада бошад, то шуморо [аз азоби қиёмат] бим диҳад, ки парво пеша кунед ва бошад, ки фарогирифтаи раҳмат гардед»?');
INSERT INTO "pioneers" VALUES(7,64,'7:64','Пас, вайро дурӯғгӯ пиндоштанд,он гоҳ Мо ӯ ва касоне, ки ҳамроҳаш дар киштӣ буданд, наҷот додем ва касоне, ки оёти Моро дурӯғ ангошта буданд, ғарқ кардем. Ба ростӣ, ки онон [дар қабули ҳақ] гурӯҳи нобино буданд.');
INSERT INTO "pioneers" VALUES(7,65,'7:65','Ва ба [сӯйи қавми] Од бародарашон Ҳудро [фиристодем]. Ӯ гуфт: «Эй қавми ман, Аллоҳ таолоро бипарастед, ки ҷуз Ӯ маъбуди [барҳақ] надоред. Оё парво намекунед»?');
INSERT INTO "pioneers" VALUES(7,66,'7:66','Ашроф [-у бузургон]-и қавми ӯ ки кофир буданд, гуфтанд: «Бе гумон, мо туро дар нодонӣ мебинем ва мо, ҳатман, туро аз дурӯғгӯён мепиндорем»');
INSERT INTO "pioneers" VALUES(7,67,'7:67','Гуфт: «Эй қавми ман, ҳеҷгуна нодоние дар ман нест, балки ман фиристодае аз [ҷониби] Парвардигори ҷаҳониён ҳастам');
INSERT INTO "pioneers" VALUES(7,68,'7:68','Паёмҳои Парвардигорамро ба шумо мерасонам ва ман бароятон хайрхоҳи амин ҳастам');
INSERT INTO "pioneers" VALUES(7,69,'7:69','Оё тааҷҷуб кардаед, ки панде аз ҷониби Парвардигоратон тавассути марде аз худи шумо бароятон омада бошад, то шуморо [аз азоби қиёмат] бим диҳад? Ва ба ёд оваред, ҳангоме ки пас аз қавми Нуҳ шуморо ҷонишинон [-и онон] қарор дод ва ба шумо дар офариниш фузунӣ [ва қудрат] дод. Пас, неъматҳои Аллоҳ таолоро ба ёд оваред, бошад, ки растагор шавед»');
INSERT INTO "pioneers" VALUES(7,70,'7:70','[Кофирон] Гуфтанд: «Оё сӯйи мо омадаӣ, ки фақат Аллоҳ таолоро бипарастем ва он чиро падаронамон мепарастиданд, раҳо кунем? Пас, агар аз ростгӯёнӣ, он чи [аз азоби илоҳӣ, ки] ба мо ваъда медиҳӣ биёвар»');
INSERT INTO "pioneers" VALUES(7,71,'7:71','[Ҳуд] гуфт: «Бе тардид, азоб ва хашми [сахт] аз [сӯйи] Парвардигоратон бар шумо воқеъ шуда [ва шуморо фарогирифта] аст. Оё дар бораи исмҳое, ки худи шумо ва падаронатон онҳоро [маъбуд] ном ниҳодаед ва Аллоҳ таоло ҳеҷ далеле бар [ҳаққонияти] он нозил накардааст, бо ман муҷодала мекунед? Пас, мунтазир бошед, [ки] бе гумон, ман [низ] бо шумо аз мунтазиронам»');
INSERT INTO "pioneers" VALUES(7,72,'7:72','Пас, ӯ ва касонеро, ки ҳамроҳаш буданд, ба раҳмат [ва лутфе] аз [ҷониби] хеш наҷот додем ва решаи касоне, ки оёти Моро дурӯғ ангоштанд ва аз муъминон набуданд, барандохтем');
INSERT INTO "pioneers" VALUES(7,73,'7:73','Ва ба [сӯйи қавми] Самуд бародарашон Солеҳ ро [фиристодем] Ӯ гуфт: «Эй қавми ман, Аллоҳ таолоро бипарастед, ки ҷуз Ӯ маъбуд [-и барҳақ] надоред. Бе тардид, далели равшане аз [сӯйи] Парвардигоратон барои шумо омадааст. Ин модашутури Аллоҳ таоло [ки аз дили сахраҳо берун омадааст] бароятон муъҷизаест, пас, ӯро [ба ҳоли худ] вогузоред, то дар замини Аллоҳ таоло [бичарад ва алаф] бихӯрад ва ба ӯ озор [-у осебе] нарасонед, ки шуморо азоби дардноке фаро хоҳад гирифт');
INSERT INTO "pioneers" VALUES(7,74,'7:74','Ва ба ёд оваред, ҳангоме ки [Аллоҳ таоло] шуморо аз [қавми] Од ҷонишинон [-и онон] қарор дод ва дар замин мустақар сохт, ки дар даштҳояш қасрҳое бино мекунед ва дар кӯҳҳо хонаҳое метарошед. Пас, неъматҳои Аллоҳ таолоро ба ёд оваред ва дар замин ба фасод [-у табоҳӣ] накушед»');
INSERT INTO "pioneers" VALUES(7,75,'7:75','Ашроф [-у бузургон]-и қавми ӯ, ки гарданкашӣ мекарданд, ба заифону бечорагон, ба [ҳамон] касоне, ки имон оварда буданд, гуфтанд: «Оё [ба яқин] медонед, ки Солеҳ аз [тарафи] Парвардигораш фиристода шудааст»? [Онон] Гуфтанд: «Мо ба он чи ӯ ба он фиристода шудааст, имон дорем»');
INSERT INTO "pioneers" VALUES(7,76,'7:76','Онон, ки гарданкашӣ мекарданд, гуфтанд: «Мо ба ончи шумо ба он имон овардаед, бовар надорем»');
INSERT INTO "pioneers" VALUES(7,77,'7:77','Пас, модашутурро пай карданд ва аз фармони Парвардигорашон сарпечиданд ва гуфтанд: «Эй Солеҳ, агар аз паёмбарон [-илоҳӣ] ҳастӣ, ончи аз азоб, ки ба мо ваъда медиҳӣ, [бар сарамон] биёвар»');
INSERT INTO "pioneers" VALUES(7,78,'7:78','Саранҷом заминларза ононро фаро гирифт, пас, дар хонаҳои худ ба рӯ афтоданд [ва ҳалок шуданд]');
INSERT INTO "pioneers" VALUES(7,79,'7:79','Пас, [Солеҳ] аз онон рӯ гардониду гуфт: «Эй қавми ман, ман паёми Парвардигорамро ба шумо расонидам ва бароятон хайрхоҳӣ кардам, вале шумо [хайрхоҳону] насиҳатгаронро дӯст намедоред»');
INSERT INTO "pioneers" VALUES(7,80,'7:80','Ва Лутро [ба паёмбарӣ фиристодем], ки ба қавми худ гуфт: «Оё кори бисёр зиштеро анҷом медиҳед, ки пеш аз шумо ҳеҷ як аз ҷаҳониён анҷом надодааст?');
INSERT INTO "pioneers" VALUES(7,81,'7:81','Шумо аз рӯйи шаҳват ба ҷойи занон бо мардон меомезед. Оре, шумо [аз ҳудуди илоҳӣ фаротар рафтаед ва] гурӯҳи таҷовузкоред»');
INSERT INTO "pioneers" VALUES(7,82,'7:82','Ва [-ле] посухи қавмаш ҷуз ин набуд, ки гуфтанд: «Ононро аз шаҳратон берун кунед, [чароки] бе тардид, инҳо афроди покдоман [-у муқаддасмаоб] ҳастанд»');
INSERT INTO "pioneers" VALUES(7,83,'7:83','Пас, Мо ӯ ва хонадонашро наҷот додем, магар ҳамсараш, ки аз бозмондагон [дар азоб] буд');
INSERT INTO "pioneers" VALUES(7,84,'7:84','Ва бороне [аз санг] бар онон боридем. Пас, бингар, ки саранҷоми гунаҳкорон чи гуна буд');
INSERT INTO "pioneers" VALUES(7,85,'7:85','Ва ба сӯйи [мардуми] Мадян бародараш Шуайбро [фиристодем]. Ӯ гуфт: «Эй қавми ман, Аллоҳ таолоро бипарастед, ки ҷуз Ӯ маъбуд [-и барҳақ] надоред. Яқинан, аз [сӯйи] Парвардигоратон барои шумо далели равшан омадааст, пас, [ҳаққи] паймона ва тарозуро комил [-у дуруст] адо кунед ва ба мардум аҷносашонро кам надиҳед ва дар замин пас аз ислоҳаш фасод накунед. Агар имон доред, ин барои шумо беҳтар аст');
INSERT INTO "pioneers" VALUES(7,86,'7:86','Ва бар сари ҳар роҳе нанишинед, ки [бо зӯргирӣ ва дуздӣ мардумро] битарсонед ва ҳаркиро имон дорад, аз роҳи Аллоҳ таоло боздоред ва роҳашро мунҳариф бихоҳед [ки ба ҳидоят даст наёбад]. Ва ба ёд оваред, ҳангомеро ки андак будед, сипас Аллоҳ таоло [теъдоди] шуморо фузунӣ бахшид ва бингаред, ки саранҷоми муфсидон [-и табаҳкор] чи гуна буд');
INSERT INTO "pioneers" VALUES(7,87,'7:87','Агар гурӯҳе аз шумо ба ончи ба он фиристода шудаам, имон оварданд ва гурӯҳи дигар имон наоварданд, пас, сабр кунед, то Аллоҳ таоло миёни мо доварӣ кунад ва Ӯ беҳтарин довар аст»');
INSERT INTO "pioneers" VALUES(7,88,'7:88','Ашроф [-у бузургон]-и қавмаш, ки такаббур меварзиданд гуфтанд: «Эй Шуайб, ҳатман, ту ва касонеро, ки ҳамроҳат имон овардаанд, аз шаҳрамон берун мекунем, [пас, ё биравед] ё ба оини мо баргардед». [Шуайб] Гуфт: «Оё [бозгардем ҳатто] агар аз он кароҳат дошта [ва безор] бошем?');
INSERT INTO "pioneers" VALUES(7,89,'7:89','Агар ба оини шумо баргардем, пас аз он ки Аллоҳ таоло моро аз он наҷот бахшид, дар ҳақиқат, ба Аллоҳ таоло дурӯғ бастаем.Ва моро сазовор нест, ки ба он бозгардем, магар он ки Аллоҳ таоло – Парвардигори мо бихоҳад. Илми Парвардигорамон ҳама чизро фаро гирифтааст.Бар Аллоҳ таоло таваккал кардаем. Парвардигоро, миёни мо ва қавмамон ба ҳақ доварӣ кун ва Ту беҳтарин доварӣ»');
INSERT INTO "pioneers" VALUES(7,90,'7:90','Ва ашроф [-у бузургон]-и қавмаш, ки кофир буданд, гуфтанд: «Агар аз Шуайб пайравӣ кунед, ҳатман, зиёнкор хоҳед буд»');
INSERT INTO "pioneers" VALUES(7,91,'7:91','Он гоҳ зилзила [-и шадид]-е ононро фаро гирифт, пас, дар хонаҳои худ ба рӯ дарафтоданд [ва ҳалок шуданд]');
INSERT INTO "pioneers" VALUES(7,92,'7:92','Онон, ки Шуайбро дурӯғгӯ ангоштанд, [чунон аз байн рафтанд ки] гӯӣ ҳаргиз дар он [диёр] набудаанд ва онон ки Шуайбро дурӯғгӯ ангоштанд, худ зиёнкорон буданд');
INSERT INTO "pioneers" VALUES(7,93,'7:93','Пас, [Шуайб] аз эшон рӯй гардонду гуфт: «Эй қавми ман, бе тардид паёмҳои Парвардигорамро ба шумо расондам ва пандатон додам. Пас, чигуна барои гурӯҳи кофир [-и лаҷуҷ] андуҳгин бошам»?');
INSERT INTO "pioneers" VALUES(7,94,'7:94','Ва мо дар ҳеҷ шаҳр [ва диёре] паёмбаре нафиристодем, магар инки сокинонашро ба ранҷҳо ва сахтиҳо гирифтор сохтем, бошад, ки [ба даргоҳи илоҳӣ фурӯтанӣ ва] тазарруъ кунанд');
INSERT INTO "pioneers" VALUES(7,95,'7:95','Сипас ба ҷойи нохӯшӣ [ва бадӣ] некӣ [ва хушӣ] овардем, то афзун шуданд ва гуфтанд: «Бе тардид, ба падарони мо [низ ба ҳукми табиат] сахтӣ ва роҳате мерасид». Пас, дар ҳоле ки бехабар буданд, ононро [ба азоб] фаро гирифтем');
INSERT INTO "pioneers" VALUES(7,96,'7:96','Ва агар аҳли он шаҳрҳо имон меоварданд ва парво мекарданд, [дарҳои] баракати осмон ва заминро бар онон мекушодем, вале [оёту паёмҳои Моро] дурӯғ ангоштаанд, пас, ба [кайфари] ончи мекарданд, ононро [ба азоб] фаро гирифтем');
INSERT INTO "pioneers" VALUES(7,97,'7:97','Оё сокинони [ин] шаҳрҳо дар амонанд аз инки азоби Мо [ногаҳон] шабҳангом, ки хуфтаанд ба суроғашон биояд?');
INSERT INTO "pioneers" VALUES(7,98,'7:98','Ва оё сокинони [ин] шаҳрҳо дар амонанд аз ин ки азоби Мо нимрӯзӣ, ки ба бозӣ [ва умури беҳуда] саргарманд, ба суроғашон биояд?');
INSERT INTO "pioneers" VALUES(7,99,'7:99','Оё худро аз макр [-у тадбир]-и Аллоҳ таоло эмин донистаанд? [Ҳол он ки] Ҷуз мардуми зиёнкор [касе] худро аз макри Аллоҳ таоло дар амон намедонад');
INSERT INTO "pioneers" VALUES(7,100,'7:100','Оё барои касоне, ки заминро пас аз сокинони [пешини] он ба ирс мебаранд, равшан нашудааст, ки агар мехостем, ононро ба [кайфари] гуноҳонашон нобуд мекардем ва ба дилҳояшон муҳр мениҳодем, то дигар [нидои ҳақро] нашнаванд?');
INSERT INTO "pioneers" VALUES(7,101,'7:101','[Эй паёмбар] Инҳо шаҳрҳоест, ки бархе аз хабарҳояшонро бар ту ҳикоят мекунем. Бе тардид, паёмбарони онҳо далоили равшан барои эшон оварданд, вале онон намехостанд ба чизе, ки пештар дурӯғ шумурда буданд, имон оваранд. Аллоҳ таоло бар дилҳои кофирон, ин гуна муҳр [-и ғафлат] мениҳад');
INSERT INTO "pioneers" VALUES(7,102,'7:102','Ва дар бештарашон вафое ба аҳд наёфтем ва аксарашонро ҷиддан нофармон ёфтем');
INSERT INTO "pioneers" VALUES(7,103,'7:103','Сипас баъд аз онҳо Мӯсоро бо оёти хеш ба сӯйи Фиръавн ва бузургони қавмаш фиристодем, вале онҳо ба он [оёт] куфр варзиданд.Пас, бингар, ки саранҷоми табаҳкорон чигуна будааст');
INSERT INTO "pioneers" VALUES(7,104,'7:104','Ва Мӯсо гуфт: «Эй Фиръавн, ман фиристодае аз [сӯйи] Парвардигори ҷаҳониёнам');
INSERT INTO "pioneers" VALUES(7,105,'7:105','Сазовораст, ки дар мавриди Аллоҳ таоло ҷуз [ба ростӣ ва] ҳақ сухан нагӯем. Бе тардид, ман далел [ва муъҷизаи равшан] аз Парвардигоратон барои шумо овардаам.Пас,Бани Исроилро ҳамроҳи ман бифирист»');
INSERT INTO "pioneers" VALUES(7,106,'7:106','[Фиръавн] Гуфт: «Агар муъҷизае овардаӣ, [ва] агар рост мегӯӣ, онро биёвар»');
INSERT INTO "pioneers" VALUES(7,107,'7:107','Пас, [Мӯсо] асои худро андохт ва ногаҳон [асо табдил ба] аждаҳои ошкор шуд');
INSERT INTO "pioneers" VALUES(7,108,'7:108','Ва дасти худро [аз гиребон] берун овард ва ногаҳон барои тамошогарон сапед [-у дурахшон] менамуд');
INSERT INTO "pioneers" VALUES(7,109,'7:109','Ашроф [-у бузургони] қавми Фиръавн гуфтанд: «Ҳаққо, ки ин [мард] ҷодугари доност»');
INSERT INTO "pioneers" VALUES(7,110,'7:110','Фиръавн] Гуфт: «Ӯ мехоҳад шуморо аз сарзаминатон берун кунад пас назаратон чист»?');
INSERT INTO "pioneers" VALUES(7,111,'7:111','Онон гуфтанд: «Кори ӯ ва бародарашро ба таъхир биандоз ва маъмурони ҷамъовариро ба [ҳамаи] шаҳрҳо бифирист');
INSERT INTO "pioneers" VALUES(7,112,'7:112','То ҳамаи ҷодугарони доно [ва моҳир]–ро наздат оваранд');
INSERT INTO "pioneers" VALUES(7,113,'7:113','Ва [саранҷом] ҷодугарон назди Фиръавн омаданд, [ва] гуфтанд: «Агар мо пирӯз шавем, [назди ту] подоше хоҳем дошт»?');
INSERT INTO "pioneers" VALUES(7,114,'7:114','[Фиръавн] Гуфт: «Оре, ҳатман, шумо аз муқаррабон [-и даргоҳам] хоҳед буд»');
INSERT INTO "pioneers" VALUES(7,115,'7:115','[Дар рӯзи мавъуд ҷодугарон мағрурона] Гуфтанд: «Эй Мӯсо, ё ту нахуст [асои худро] биафкан ё мо [абзори худро] меафканем»');
INSERT INTO "pioneers" VALUES(7,116,'7:116','[Мӯсо] Гуфт: «Шумо биафканед». Ҳангоме ки [абзори худро] афканданд, чашмони мардумро афсун карданд ва ононро сахт тарсонданд ва ҷодуи бузург [падид] оварданд');
INSERT INTO "pioneers" VALUES(7,117,'7:117','Ва [мо] ба Мӯсо ваҳй кардем, ки: «Асои худро биафкан». Пас, ногаҳон он [асо ба мори бузурге табдил шуд ва] ончиро, ки ба дурӯғ сохта буданд, фурӯ балъид');
INSERT INTO "pioneers" VALUES(7,118,'7:118','Пас, ҳақиқат ошкор гардид ва он чи мекарданд, ботил шуд');
INSERT INTO "pioneers" VALUES(7,119,'7:119','Ва [Фиръавну фиръавниён ҳамагӣ дар] онҷо шикаст хӯрданд ва хор гардиданд');
INSERT INTO "pioneers" VALUES(7,120,'7:120','Ва ҷодугарон ба саҷда афтоданд');
INSERT INTO "pioneers" VALUES(7,121,'7:121','Гуфтанд: «Ба Парвардигори ҷаҳониён имон овардем');
INSERT INTO "pioneers" VALUES(7,122,'7:122','Парвардигори Мӯсо ва Ҳорун»');
INSERT INTO "pioneers" VALUES(7,123,'7:123','Фиръавн гуфт: «Пеш аз онки ба шумо иҷозат диҳам, ба ӯ имон овардед? Ҳатман, ин найрангест, ки дар шаҳр ба роҳ андохтаед, то сокинонашро аз он берун кунед. Пас, ба зудӣ [натиҷаи коратонро] хоҳед донист');
INSERT INTO "pioneers" VALUES(7,124,'7:124','Яқинан, дасту поятонро бархилофи якдигар [аз чапу рост] қатъ мекунам ва ҳамаи шуморо ба дор меовезам»');
INSERT INTO "pioneers" VALUES(7,125,'7:125','Онон гуфтанд: «Бе тардид, мо [пас аз марг] ба сӯйи Парвардигорамон бозмегардем');
INSERT INTO "pioneers" VALUES(7,126,'7:126','Ва [эй Фиръавн] ту ҷуз барои ин моро ба кайфар намерасонӣ, ки ба муъҷизоти Парвардигорамон он гоҳ ки ба мо расид, имон овардем. Парвардигоро, бар мо шикебоӣ [ва истиқомат] бибор ва моро дар ҳоле бимирон ки таслими [фармони ту] ҳастем»');
INSERT INTO "pioneers" VALUES(7,127,'7:127','Ва бузургони қавми Фиръавн [ба ӯ] гуфтанд: «Оё Мӯсо ва қавмашро раҳо мекунӣ, то дар ин сарзамин фасод кунанд ва ту ва маъбудонатро вогузорад»? [Фиръавн] Гуфт: «[Ҳаргиз, балки] Писаронашонро хоҳем кушт ва занонашонро [барои канизӣ] зинда хоҳем гузошт ва мо бар онон чираем»');
INSERT INTO "pioneers" VALUES(7,128,'7:128','Мӯсо ба қавмаш гуфт: «Аз Аллоҳ таоло ёрӣ биҷӯед ва шикебоӣ [ва истиқомат] пеша кунед. Бе тардид, замин аз они Аллоҳ таоло аст [ва] онро ба ҳар касе аз бандагонаш, ки бихоҳад, ба мерос медиҳад ва саранҷоми нек аз они парҳезгорон аст»');
INSERT INTO "pioneers" VALUES(7,129,'7:129','[Бани Исроил] Гуфтанд: «Пеш аз он ки назди мо биёӣ ва [ҳатто] пас аз он ки назди мо омадӣ, [аз ситамҳои Фиръавн] озору азият дидаем» [Мӯсо] гуфт: «Умедаст, ки Парвардигори шумо душманатонро ҳалок кунад ва шуморо дар замин ҷонишин [-и онон] созад,он гоҳ бингарад, ки чигуна рафтор мекунед»');
INSERT INTO "pioneers" VALUES(7,130,'7:130','Ва бе тардид, Мо фиръавниёнро ба хушксолӣ ва камбуди маҳсулот дучор кардем, бошад, ки панд гиранд [ва тавба кунанд]');
INSERT INTO "pioneers" VALUES(7,131,'7:131','Пас, ҳангоме ки некӣ [ва фаровонии неъмат] ба онон мерасид, мегуфтанд: «Ин ба хотири [шоистагии] худи мост». Ва агар бадӣ [ва хушксолӣ] ба эшон мерасид, ба Мӯсо ва касоне, ки ҳамроҳаш буданд, фоли бад мезаданд. Огоҳ бошед! Сарнавишти шуми онон танҳо назди Аллоҳ таоло [ва ба дасти Ӯ] аст, аммо бештарашон намедонанд');
INSERT INTO "pioneers" VALUES(7,132,'7:132','Ва [пайравони Фиръавн ба ӯ] гуфтанд: «Ҳар [муъҷиза ва] нишонае бароямон биёварӣ, то бо он ҷодуямон кунӣ, [ҳаргиз] ба ту имон намеоварем»');
INSERT INTO "pioneers" VALUES(7,133,'7:133','Пас, бар онон [балоҳое ҳамчун] тӯфон ва малах ва шапуш ва қурбоқаҳо ва [рӯди пур]-и хун фиристодем, [ки] нишонаҳои ҷудо [аз ҳам ва паёпайе] буданд.Пас, [боз ҳам] гарданкашӣ карданд ва гурӯҳи гунаҳкор буданд');
INSERT INTO "pioneers" VALUES(7,134,'7:134','Ва ҳангоме ки азоб бар онон воқеъ шуд, гуфтанд: «Эй Мӯсо, аз Парвардигорат барои мо бихоҳ, ки бино ба паймоне, ки бо ту дорад, [тавбаи моро бипазирад ва ин балоҳоеро қатъ кунад] Агар ин азобро аз мо бардорӣ, ҳатман, ба ту имон меоварем ва Бани Исроилро ҳамроҳат равона мекунем»');
INSERT INTO "pioneers" VALUES(7,135,'7:135','Пас, ҳангоме ки азобро то муддати муайяне, ки [пеш аз ҳалокат] фурсат доштанд аз онон дур кардем, боз ҳам паймоншиканӣ карданд');
INSERT INTO "pioneers" VALUES(7,136,'7:136','Пас аз онон интиқом гирифтем ва дар дарё ғарқашон сохтем, зеро оёт [-у муъҷизоти] Моро дурӯғ меангоштанд ва аз он ғофил буданд');
INSERT INTO "pioneers" VALUES(7,137,'7:137','Ва ба он гурӯҳе, ки ҳамвора мавриди ситам воқеъ мешуданд, [бахшҳои] шарқӣ ва ғарбии сарзамин [-и Шом]-ро, ки ба он баракат бахшида будем, ба мерос ато кардем ва ба подоши он ки шикебоӣ карданд, ваъдаи некуи Парвардигорат ба Бани Исроил [ёрии ситамдидагон] таҳаққуқ ёфт ва он чиро, ки Фиръавн ва қавмаш месохтанд ва бармеафроштанд, [яксара] вайрон кардем');
INSERT INTO "pioneers" VALUES(7,138,'7:138','Ва Бани Исроилро аз дарё гузарондем, пас, [дар роҳ] ба қавме расиданд, ки барои [парастиши] бутҳояшон гирд омада буданд, [пас, онон низ] гуфтанд: «Эй Мӯсо, ҳамонгуна ки онон маъбудоне доранд, барои мо [низ] маъбуде таъйин кун». [Мӯсо] Гуфт: «Бе тардид, шумо гурӯҳе ҳастед, ки нодонӣ мекунед [ва азамати Аллоҳ таолоро намедонед]');
INSERT INTO "pioneers" VALUES(7,139,'7:139','Яқинан, оини [мушриконаи] инҳо нобудшуданӣ аст ва ончи мекунанд, [низ] ботил аст»');
INSERT INTO "pioneers" VALUES(7,140,'7:140','[Сипас] Гуфт: «Оё ба ҷуз Аллоҳ таоло маъбуди [дигар] бароятон биҷӯям, дар ҳоле ки Ӯ шуморо бар ҷаҳониён [-и ҳамасратон] бартарӣ додааст»?');
INSERT INTO "pioneers" VALUES(7,141,'7:141','Ва [ба ёд оваред] замоне, ки шуморо аз [осеби] фиръавниён наҷот додем, [ки] бадтарин [ва хортарин] азобро бар саратон меоварданд: писаронатонро мекуштанд ва занонатонро [барои канизӣ] зинда нигаҳ медоштанд ва барои шумо дар ин [азобу наҷот] озмоиши бузург аз ҷониби Парвардигоратон буд');
INSERT INTO "pioneers" VALUES(7,142,'7:142','Ва бо Мӯсо сӣ шаб [барои муноҷот] ваъда ниҳодем ва онро бо даҳ шаб [-и дигар] комил кардем.Пас, ваъдаи Парвардигораш [бо ӯ] дар чиҳил шаб тамом шуд. Ва Мӯсо [ҳангоми рафтан ба кӯҳи Тур] ба бародараш Ҳорун гуфт: «Ҷойнишини ман дар миёни қавм бош ва [умурашонро] ислоҳ кун ва аз роҳи муфсидон пайравӣ накун»');
INSERT INTO "pioneers" VALUES(7,143,'7:143','Ва ҳангоме ки Мӯсо ба аҳдгоҳи Мо омад ва Парвардигораш [бевосита] бо ӯ сухан гуфт, [Мӯсо] арз кард: «Парвардиргоро, худро ба ман нишон бидеҳ, то ба Ту бингарам». [Аллоҳ таоло] Фармуд: «[Дар дунё] ҳаргиз Маро нахоҳӣ дид, вале ба ин кӯҳ бингар, агар дар ҷойи худ барқарор боқӣ монад, ту [низ] Моро хоҳӣ дид». Пас, чун Парвардигораш бар [он] кӯҳ таҷаллӣ кард, онро муталошӣ [ва пароканда] сохт ва Мӯсо мадҳуш афтод ва чун ба ҳуш омад, арз кард: «[Бор илоҳо] Ту поку муназзаҳӣ. Ба даргоҳат тавба кардам ва бе тардид, ман [миёни қавми худ] нахустин муъмин ҳастам»');
INSERT INTO "pioneers" VALUES(7,144,'7:144','[Аллоҳ таоло] фармуд: «Эй Мӯсо, [бидон ки] ман туро ба паёмҳо ва каломи [бевоситаи] хеш бар мардумон баргузидам, ончи ба ту мебахшам, бигир ва сипосгузор бош»');
INSERT INTO "pioneers" VALUES(7,145,'7:145','Ва дар алвоҳ [Таврот] барои ӯ дар ҳар мавриде панде [овардем] ва барои ҳар амре шарҳе нигоштем [ва ба Мӯсо фармудем]: «Онро бо ҷиддият бигир ва ба қавмат дастур бидеҳ, ки некутарини онҳоро фаро гиранд [ва ба он амал кунанд] ва ба зудӣ [ҷойгоҳу] сарои нофармононро ба шумо нишон хоҳам дод»');
INSERT INTO "pioneers" VALUES(7,146,'7:146','Ба зудӣ касонеро ки ба ноҳақ дар замин [бартариҷӯӣ ва] такаббур мекунанд, аз [фаҳми] оёти хеш бозхоҳам дошт, [чунон ки] агар ҳар нишонае [аз қудратам]-ро бингаранд, ба он имон наоваранд ва агар роҳи ҳақро бубинанд, онро дар пеш нагиранд ва агар роҳи гумроҳиро бубинанд онро баргузинанд. Ин [муҷозот] барои он аст, ки оёти Моро дурӯғ ангоштанд ва аз онҳо ғофил буданд');
INSERT INTO "pioneers" VALUES(7,147,'7:147','Ва касоне, ки оёти Мо ва дидори охиратро дурӯғ ангоштанд, корҳояшон табоҳ гаштааст. Оё ҷуз [дар баробари] ончи мекарданд, муҷозот мешаванд?');
INSERT INTO "pioneers" VALUES(7,148,'7:148','Ва қавми Мӯсо пас аз [рафтани] ӯ [ба кӯҳи Тур] аз зеварҳояшон муҷассамаи гӯсолаеро сохтанд [ва парастиданд], ки садое [ҳамчун садои гӯсола] дошт. Оё намедиданд, ки [он пайкари беҷон] бо онон сухан намегӯяд ва ба роҳе ҳидояташон намекунад? Онро [ба парастиш] гирифтанд ва [ба хотири ин ширк нисбат ба хеш] ситамгор буданд');
INSERT INTO "pioneers" VALUES(7,149,'7:149','Ва чун [аз кардаи хеш] пушаймон шуданд ва диданд, ки ба ростӣ, гумроҳ шудаанд, гуфтанд: «Агар Парвардигорамон ба мо раҳм накунад ва моро наомурзад, мусалламан, зиёнкор хоҳем буд»');
INSERT INTO "pioneers" VALUES(7,150,'7:150','Ва чун Мӯсо ба сӯйи қавмаш бозгашт [ва ононро бар ширк ва гумроҳӣ ёфт], бо хашму андуҳ гуфт: «Пас аз ман чи бад ҷонишинӣ кардед! Оё аз ҳукми Парвардигоратон пешӣ гирифтед»? Ва [он гоҳ] алвоҳро андохт ва [ришу мӯйи] сари бародари худро гирифт ва ба сӯйи худ кашид. [Ҳорун] Гуфт: «Эй писари модарам, ба ростӣ, ин қавм маро нотавон пиндоштанд [ва таҳқирам карданд] ва наздик буд маро бикушанд, пас, душманонро бо [сарзаниши] ман шодмон накун ва маро дар зумраи гурӯҳи ситамгор [-у нофармон] қарор надеҳ»');
INSERT INTO "pioneers" VALUES(7,151,'7:151','[Мӯсо] Гуфт: «Парвардигоро, ман ва бародарамро биёмурз ва моро дар [паноҳи] раҳмати хеш даровар.Ва Ту меҳрубонтарин меҳрубонӣ»');
INSERT INTO "pioneers" VALUES(7,152,'7:152','Бе тардид, касоне, ки гӯсоларо [ба парастиш] гирифтанд, ба зудӣ хашме аз Парвардигорашон ва зиллате дар зиндагии дунё ба онон хоҳад расид ва мо дурӯғпардозонро инчунин кайфар медиҳем');
INSERT INTO "pioneers" VALUES(7,153,'7:153','Ва касоне, ки муртакиби корҳои ношоиста [куфру гуноҳ] шуданд, сипас баъд аз он тавба намуданд ва имон оварданд, бе гумон, Парвардигорат аз он пас омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(7,154,'7:154','Ва чун хашми Мӯсо фурӯ нишаст, алвоҳ [-и Таврот]-ро баргирифт ва дар навиштаҳояш барои касоне, ки аз Парвардигорашон метарсанд, ҳидояту раҳмати [бузург] буд');
INSERT INTO "pioneers" VALUES(7,155,'7:155','Ва Мӯсо аз қавмаш ҳафтод мардро барои ваъдагоҳи Мо [дар Тӯри Сино] баргузид [то ба хотири ширку гуноҳи қавмашон ба даргоҳи илоҳӣ тавба кунанд]. Пас, ҳангоме ки [ба далели нофармонӣ ва дархости руъяти Аллоҳ таоло] зилзила ононро фаро гирифт [ва ҳамагӣ мурданд, Мӯсо] гуфт: «Парвардигоро, агар мехостӣ, онон ва маро пеш аз ин ҳалок мекардӣ. Оё моро ба хотири ончи бехирадонамон кардаанд, ҳалок мекунӣ? Ин [азоб чизе] нест, магар озмоиши Ту [аз имони мо], ба [василаи] он ҳаркиро бихоҳӣ, гумроҳ месозӣ ва ҳаркиро бихоҳӣ, ҳидоят мекунӣ. Ту [дӯсту] корсози моӣ, пас, моро биёмурз ва бар мо раҳм овар ва Ту беҳтарин омурзандаӣ');
INSERT INTO "pioneers" VALUES(7,156,'7:156','Ва бароямон дар ин дунё ва дар сарои охират некӣ муқаррар фармо, [ки] бе тардид, мо ба сӯйи Ту бозгаштем». [Аллоҳ таоло] Фармуд: «Азобамро ба ҳар ки бихоҳам мерасонам ва раҳматам ҳама чизро фаро гирифтааст, пас, онро барои касоне муқаррар хоҳам дошт, ки парво пеша мекунанд ва закот медиҳанд ва [ҳамчунин] барои касоне, ки ба оёти мо имон меоваранд');
INSERT INTO "pioneers" VALUES(7,157,'7:157','Касоне, ки аз фиристода ва паёмбари дарснохонда [уммӣ] пайравӣ мекунанд, [ҳамон] ки ном [ва нишонаҳои] ӯро назди худ дар Тавроту Инҷил навишта меёбанд, [ҳам ӯ] ки ононро ба некиҳо фармон медиҳад ва аз зиштиҳо [ва корҳои нописанд] бозашон медорад ва покизаҳоро барояшон ҳалол мегардонад ва палидиҳоро ба онон ҳаром мекунад ва бори гарони [таколифи шаръӣ]-ро, ки бар эшон будааст, аз [уҳдаи] онон бармедорад. Пас, касоне, ки ба Ӯ имон оварданд ва гиромияш доштанд ва ёриаш карданд ва аз нуре, ки бо ӯ нозил шудааст, пайравӣ намуданд, ононанд, ки растагоранд»');
INSERT INTO "pioneers" VALUES(7,158,'7:158','Бигӯ: «Эй мардум, ман фиристодаи Аллоҳ таоло ба сӯйи ҳамаи шумо ҳастам.Ҳамон [Парвардигоре], ки фармонравоии осмонҳо ва замин аз они Ӯст, маъбуде [барҳақ] ҷуз Ӯ нест; зинда мекунад ва мемиронад, пас, ба Аллоҳ таоло ва фиристодааш – он паёмбари дарснохондае, ки ба Аллоҳ таоло ва суханонаш бовар дорад, имон биёваред ва аз Ӯ пайравӣ кунед, бошад, ки ҳидоят шавед»');
INSERT INTO "pioneers" VALUES(7,159,'7:159','Ва гурӯҳе аз қавми Мӯсо ҳастанд, ки [мардумро] ба [роҳи] ҳақ ҳидоят мекунанд ва бар асоси он [низ қазоват ва] адолат мекунанд');
INSERT INTO "pioneers" VALUES(7,160,'7:160','Ва ононро ба дувоздаҳ қабила [ки ҳар кадом] шохае аз наводагон [-и Яъқуб] буданд, тақсим кардем ва ба Мӯсо,он гоҳ ки қавмаш аз ӯ об хостанд, ваҳй кардем, ки: «Асоятро бар санг бизан», пас, дувоздаҳ чашмае аз он ҷӯшид [ва] ҳар гурӯҳе чашмаи худро шинохт ва абрро бар [сари] онон соягустар намудем ва бар эшон манну салво фурӯ фиристодем [ва гуфт] «Аз чизҳои покизае, ки рӯзиятон кардаем, бихӯред». Онон [бо нофармонӣ ва мухолифат, дар ҳақиқат] ба Мо ситам накарданд, балки ба хештан ситам менамуданд');
INSERT INTO "pioneers" VALUES(7,161,'7:161','Ва [ёд кун аз] он гоҳ ки ба онон гуфта шуд: «Дар ин шаҳр [Байту-л-муқаддас] сокин шавед ва [аз неъматҳои] он, аз ҳар ҷо [ва ҳар вақт], ки хостед, бихӯред ва [барои узрхоҳӣ] бигӯед: «[Парвардигоро] Гуноҳонамонро фурӯ рез» ва бо фурӯтанӣ аз дарвоза[-и шаҳр] ворид шавед, то гуноҳонатонро биёмурзем, [ва подоши дунё ва охирати] накукоронро хоҳем афзуд»');
INSERT INTO "pioneers" VALUES(7,162,'7:162','Аммо афроде аз онҳо, ки [нофармону] ситамгор буданд, суханро ба чизе ғайр аз ончи ки ба онон гуфта шуда буд, табдил карданд [ва баҷои истиғфор донаи ҷав хостанд]. Пас, ба [кайфари] онки ситам мекарданд, азобе аз осмон бар онон фурӯ фиристодем');
INSERT INTO "pioneers" VALUES(7,163,'7:163','Ва [эй паёмбар] аз онон дар бораи [сокинони] шаҳре, ки наздики дарё буд, бипурс, ҳангоме ки онон аз [ҳукми таҳрими сайд дар рӯзи] шанбе таҷовуз мекарданд, он гоҳ ки моҳиҳои эшон дар рӯзҳои шанбе [ки рӯзи таътилу ибодат буд] рӯйи об зоҳир мешуданд ва рӯзҳои ғайри шанбе бар эшон [ҳеҷ сайде рӯйи об] намеомад. Ононро ба [хотири] он ки нофармонӣ мекарданд, инчунин озмудем');
INSERT INTO "pioneers" VALUES(7,164,'7:164','Ва [ёд кун аз] ҳангоме ки гурӯҳе аз онон [ба гурӯҳи дигар] гуфтанд: «Чаро қавмеро панд медиҳед, [ки саранҷом] Аллоҳ таоло ҳалокашон хоҳад кард ё ба азоби шадид гирифторашон хоҳад намуд»? [Носеҳон] Гуфтанд: «Ин [панду наҳй дар ҳукми] маъзарате ба даргоҳи Парвардигори шумост ва бошад, ки парво кунанд»');
INSERT INTO "pioneers" VALUES(7,165,'7:165','Пас, чун ончиро ба эшон панд дода буданд, ба фаромӯшӣ супурданд, касонеро, ки аз [кори] бад бозмедоштанд, наҷот додем ва касонеро, ки [бо нодида гирифтани ҳукми илоҳӣ ба худ] ситам карда буданд, ба сазои онки нофармонӣ мекарданд, ба азоби шадид гирифтор намудем');
INSERT INTO "pioneers" VALUES(7,166,'7:166','Ва чун дар баробари ончи аз он наҳй шуда буданд, [лаҷоҷату] сарпечӣ карданд, ба онон гуфтем: «Ба сурати бузинаҳое [хору] рондашуда дароед»');
INSERT INTO "pioneers" VALUES(7,167,'7:167','Ва [ёд кун аз] ҳангоме ки Парвардигорат фармуд, ки яқинан, то рӯзи қиёмат, касеро бар онон хоҳад гумошт, ки ҳамвора бадтарин [ва сангинтарин] азоро ба сарашон биёварад. Бе гумон, Парвардигорат зудкайфар аст, [ва-ле] яқинан, Ӯ омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(7,168,'7:168','Ва ононро дар замин ба сурати гурӯҳҳои мухталиф пароканда сохтем. [Бархе] Аз онон шоистаанд ва [бархе миёнарав ҳастанд ва гурӯҳе низ] ғайр аз инанд. Ва ононро ба некиҳо ва бадиҳо озмудем, бошад, ки [ба роҳи ҳақ] боз гарданд');
INSERT INTO "pioneers" VALUES(7,169,'7:169','Ва пас аз онон ҷонишинон [-и ношоиста]-е омаданд, ки китоб [Таврот]-ро ба ирс бурданд. Онон [сарвату] колои ин [дунёи] пастро мегиранд, [ки суханони Аллоҳ таолоро таҳрифу ба ноҳақ ҳукм кунанд] ва мегӯянд: «Бахшида хоҳем шуд». Ва агар коло [-и дигар]-е ҳаммонанди он барояшон биояд, онро [низ] мегиранд. Оё дар китоб [-и осмонӣ] аз онон паймон гирифта нашудааст, ки дар бораи Аллоҳ таоло ҷуз [сухани] ҳақ нагӯянд? [Пас, нофармонияшон аз рӯйи нодонӣ нест, зеро борҳо Тавротро хондаанд] Ва ончиро, ки дар он аст, омӯхтаанд ва сарои охират барои касоне, ки парво кунанд, беҳтар аст. Оё намеандешед?');
INSERT INTO "pioneers" VALUES(7,170,'7:170','Ва касоне, ки ба китоби [Аллоҳ таоло] тамассук меҷӯянд ва намоз барпо медоранд, ба яқин [бидонанд, ки] Мо подоши накукоронро табоҳ намекунем');
INSERT INTO "pioneers" VALUES(7,171,'7:171','Ва [ёд кун аз] ҳангоме ки кӯҳро [аз ҷой баркандем ва] ҳамчун соябоне бар фарозашон барафроштем ва пиндоштанд, ки бар онон фуруд меояд [ва дар ин ҳолат аз онон аҳд гирифтем ва гуфтем] «Он чиро, ки ба шумо додаем, бо неру [ва ҷиддият] бигиред ва ончиро, ки дар он аст, ба ёд дошта бошед [ва ба он амал намоед], бошад, ки парво кунед');
INSERT INTO "pioneers" VALUES(7,172,'7:172','Ва [ёд кун аз] ҳангоме ки Парвардигорат аз пушти фарзандони Одам, наслашонро баргирифт ва ононро бар худашон гувоҳ гирифт [ва фармуд]: «Оё ман Парвардигори шумо нестам»? Онон гуфтанд: «Оре, [бар ин ҳақиқат] гувоҳӣ медиҳем». [Аллоҳ таоло фармуд: «Аз шумо иқрор гирифтам] То мабодо рӯзи қиёмат бигӯед, ки: «Мо, ҳатман, аз ин [амр] бехабар будем»');
INSERT INTO "pioneers" VALUES(7,173,'7:173','Ё [мабодо] бигӯед, ки падаронамон пештар ширк оварда буданд ва мо низ фарзандоне пас аз онон будем. Оё моро ба [сазои] ончи [кофирону] ботиландешон анҷом додаанд, ҳалок мекунӣ»?');
INSERT INTO "pioneers" VALUES(7,174,'7:174','Ва инчунин оётро ба тафсил баён мекунем [то онҳоро бифаҳманд] ва бошад, ки [ба тавҳид] бозгарданд');
INSERT INTO "pioneers" VALUES(7,175,'7:175','Ва [эй паёмбар] саргузашти касеро бар онон ҳикоят кун, ки оёт [-у нишонаҳо]-и хешро ба ӯ додем ва [ӯ ҳаққонияташонро дарк кард, вале] аз онҳо ҷудо [ва орӣ] гашт.Пас, шайтон ба дунболаш афтод ва ӯ аз гумроҳон шуд');
INSERT INTO "pioneers" VALUES(7,176,'7:176','Ва агар мехостем, [мақому манзалати] ӯро ба хотири [пойбандӣ ва амал ба] он [оёт] боло мебурдем, вале ӯ ба дунё [ва пастӣ] гароид ва аз ҳавои нафси хеш пайравӣ кард. Пас, масали ӯ [дар дунёдӯстӣ] ҳамчун масали саг аст, ки агар ба он ҳамла кунӣ [ва аз худ дур кунӣ], забон аз даҳон берун меоварад ва агар онро [ба ҳоли худ] вогузорӣ, [боз ҳам] забон аз даҳон берун меоварад [чунин шахсеро низ чи панд бидиҳӣ ё надиҳӣ, тафовуте надорад]. Ин масали гурӯҳест, ки оёти Моро дурӯғ ангоштанд, пас, ин достонро [бар эшон] ҳикоят кун, бошад, ки бияндешанд');
INSERT INTO "pioneers" VALUES(7,177,'7:177','Чи зишт аст достони гурӯҳе, ки оёти Моро дурӯғ ангоштанд ва ба хеш ситам карданд!');
INSERT INTO "pioneers" VALUES(7,178,'7:178','Касеро, ки Аллоҳ таоло ҳидоят кунад, пас, ӯ ҳидоятёфта [-и воқеӣ] аст ва касеро, ки гумроҳ созад, пас, ононанд, ки зиёндидагонанд');
INSERT INTO "pioneers" VALUES(7,179,'7:179','Ва бе тардид, бисёре аз ҷинниён ва одамиёнро барои дузах офаридаем, [чаро ки] дилҳое доранд, ки бо онҳо [ҳақоиқро] дарк намекунанд ва чашмоне доранд, ки бо онҳо [нишонаҳои тавону тадбири илоҳиро] намебинанд ва гӯшҳое доранд, ки бо онҳо [суханони ҳақро] намешунаванд. Онон ҳамчун чаҳорпоёнанд, балки [аз онҳо ҳам] гумроҳтаранд. [Оре] Ононанд,ки [аз охират] ғофиланд');
INSERT INTO "pioneers" VALUES(7,180,'7:180','Ва зеботарин номҳо барои Аллоҳ таоло аст, пас, Ӯро ба он [номҳо] бихонед ва касонеро, ки дар [мавриди] номҳояш ба инҳироф мераванд [ва онҳоро таъвилу таҳриф мекунанд] раҳо кунед. Онон ба [сазои] ончи мекарданд, муҷозот хоҳанд шуд');
INSERT INTO "pioneers" VALUES(7,181,'7:181','Ва аз [миёни] онон, ки офаридаем, гурӯҳе ҳастанд, ки [мардум] баҳақ ҳидоят менамоянд ва бар [асоси] он ба адолат рафтор мекунанд');
INSERT INTO "pioneers" VALUES(7,182,'7:182','Ва касоне, ки оёти Моро дурӯғ ангоштанд, [дарҳои рӯзиро ба рӯяшон мекушоем, то бештар дар гуноҳ ғарқ шаванд ва ба ин сон] андак андак аз ҷойе, ки намедонанд [ба кайфари худ] гирифторашон хоҳем сохт');
INSERT INTO "pioneers" VALUES(7,183,'7:183','Ва ба онон муҳлат медиҳам. Ба ростӣ, ки тадбири Ман устувор [ва ҳисобшуда] аст');
INSERT INTO "pioneers" VALUES(7,184,'7:184','Оё наандешидаанд, ки ҳамнишини онон [расулуллоҳ] ҳеҷ ҷунуне надорад? Ӯ ҷуз бимдиҳандаи ошкор нест');
INSERT INTO "pioneers" VALUES(7,185,'7:185','Оё дар фармонравоии осмонҳо ва замин ва ончи Аллоҳ таоло офаридааст, наменигаранд ва инки чи басо ҳангоми маргашон наздик шуда бошад? Пас, баъд аз ин [Қуръон] ба кадом сухан имон меоваранд?');
INSERT INTO "pioneers" VALUES(7,186,'7:186','Ҳар киро Аллоҳ таоло гумроҳ кунад, пас, [ҳеҷ] роҳнамое барояш нест ва ононро дар саркашии хеш саргардон раҳо мекунад');
INSERT INTO "pioneers" VALUES(7,187,'7:187','[Эй паёмбар, кофирони Макка] дар бораи қиёмат аз ту мепурсанд, [ки] кай воқеъ мешавад? Бигӯ: «Бе тардид, дониши он назди Парвардигори ман аст ва ҳеҷ кас ҷуз Ӯ [наметавонад] онро дар вақти муайянаш падидор созад.Ин амр [зуҳури қиёмат] бар аҳли осмонҳо ва замин душвор аст ва ҷуз ба таври ногаҳонӣ ба сӯятон намеояд». [Онон чунон] Аз ту мепурсанд, ки гӯё ту худ муштоқи [донистани замони] он ҳастӣ [ҳол он ки ту дар бораи замони вуқуаш ҳеҷ намепурсӣ]. Бигӯ: «Дониши он фақат назди Аллоҳ таоло аст, вале бештари мардум намедонанд»');
INSERT INTO "pioneers" VALUES(7,188,'7:188','[Эй паёмбар] Бигӯ: «Ман молики суду зиёни хештан нестам, магар ончиро, ки Аллоҳ таоло бихоҳад ва агар ғайбро медонистам, хайр [-у суди] бисёре [барои худ] фароҳам месохтам ва ҳеҷ бадӣ [ва зиёне] ба ман намерасид. Ман [касе] нестам, магар бимдиҳанда ва башоратбахш [-и дилсӯз] барои гурӯҳе, ки имон доранд');
INSERT INTO "pioneers" VALUES(7,189,'7:189','[Эй мардум] Ӯст, ки шуморо аз як тан офарид ва ҳамсарашро аз ӯ падид овард, то дар канораш ором гирад. Пас, чун [Одам] бо вай [Ҳаво] даромехт, бордор шуд – бори сабук – ва [нахустин моҳҳои бордориро] гузаронд ва чун сангинбор шуд, Аллоҳ таоло – Парвардигори худро хонданд, ки: «Агар ба мо [фарзанди тандурусту] шоиста ато кунӣ, ҳатман, сипосгузор хоҳем буд»');
INSERT INTO "pioneers" VALUES(7,190,'7:190','Пас, чун [Аллоҳ таоло фарзанди тандуруст ва] шоиста ба онон дод, дар ин неъмат, ки ба онҳо бахшида буд, барояш шариконе қарор доданд. Ва Аллоҳ таоло аз ончи шарик [ва ҳамтои] Ӯ месозанд, бартар аст');
INSERT INTO "pioneers" VALUES(7,191,'7:191','Оё чизҳоеро шарик мегиранд, ки чизе намеофаранд ва худ офарида мешаванд?');
INSERT INTO "pioneers" VALUES(7,192,'7:192','Ва наметавонанд ононро ёрӣ кунанд ва на хештанро ёрӣ медиҳанд');
INSERT INTO "pioneers" VALUES(7,193,'7:193','Ва [эй мушрикон] агар онҳо [бутҳо]-ро ба ҳидоят даъват кунед, аз шумо пайравӣ намекунанд. Чи даъваташон кунед ё хомӯш бошед, бароятон яксон аст [ва посухе нахоҳед шунид]');
INSERT INTO "pioneers" VALUES(7,194,'7:194','Бе гумон, онҳое, ки ба ҷойи Аллоҳ таоло мехонед, офаридагоне ҳамчун шумо ҳастанд [аммо ҷомид ва берӯҳ], пас, онҳоро [дар сахтиҳо] бихонед. Агар рост мегӯед, бояд посухатонро бидиҳанд');
INSERT INTO "pioneers" VALUES(7,195,'7:195','Оё [бутҳое, ки маъбуди худ медонед] пойҳое доранд, ки бо онҳо роҳ бираванд ё дастҳое доранд, ки бо онҳо [ёриатон диҳанд ва аз шумо] дифоъ кунанд? Оё чашмоне доранд, ки бо онҳо бубинанд ё гӯшҳое доранд, ки бо онҳо бишнаванд? [Эй паёмбар, ба мушрикон] Бигӯ: «Шариконатонро бихонед, сипас [алайҳи ман] тадбир [-у найранг] кунед ва маро муҳлат надиҳед');
INSERT INTO "pioneers" VALUES(7,196,'7:196','Бе тардид, [дӯсту] корсози ман Аллоҳ таоло аст, ки [ин] китобро нозил кардааст ва Ӯ барои шоистагон [дӯстӣ ва] корсозӣ мекунад');
INSERT INTO "pioneers" VALUES(7,197,'7:197','Ва касонеро, ки [шумо мушрикон] ба ҷойи Ӯ мехонед, наметавонанд шуморо ёрӣ кунанд ва на ба хештан ёрӣ расонанд');
INSERT INTO "pioneers" VALUES(7,198,'7:198','Ва агар ононро ба ҳидоят даъват кунед, [суханонатонро] намешунаванд ва ононро мебинӣ, ки [зоҳиран] ба ту нигоҳ мекунанд, дар ҳоле [ҷимодоте ҳастанд] ки намебинанд»');
INSERT INTO "pioneers" VALUES(7,199,'7:199','[Эй паёмбар] Гузашт пеша кун ва ба некӣ фармон бидеҳ ва аз нодонон рӯ бигардон');
INSERT INTO "pioneers" VALUES(7,200,'7:200','Ва агар васвасае аз сӯйи шайтон туро барангезонд, ба Аллоҳ таоло паноҳ бибар, [ки] бе тардид, Ӯ шунавову доност');
INSERT INTO "pioneers" VALUES(7,201,'7:201','Дар ҳақиқат, касоне, ки [аз Аллоҳ таоло] парво мекунанд, чун васвасае аз ҷониби шайтон ба онон расад, [Аллоҳ таолоро] ба ёд меоваранд ва он гоҳ [нисбат ба ҳақиқат] бино мешаванд');
INSERT INTO "pioneers" VALUES(7,202,'7:202','Ва бародаронашон [бародарони кофирон ва мушрикон, ки шаётин ҳастанд] ононро ба гумроҳӣ мекашонанд ва [ҳаргиз дар ин кор] кӯтоҳӣ намекунанд');
INSERT INTO "pioneers" VALUES(7,203,'7:203','Ва [эй паёмбар] ҳангоме ки [нузули ваҳй ба таъхир меафтод ва] ту ояте бар эшон намеоварӣ, мегӯянд: «Чаро ту худ онро намесозӣ»? Бигӯ: «Ман фақат аз чизе пайравӣ мекунам, ки аз сӯйи Парвардигорам ба ман ваҳй мешавад. Ин [Қуръон шомили] равшангариҳое аз сӯйи Павардигоратон аст ва барои гурӯҳе, ки имон меоваранд, ҳидояту раҳмат аст');
INSERT INTO "pioneers" VALUES(7,204,'7:204','Ва ҳангоме ки Қуръон хонда мешавад, пас, ба он гӯш фаро диҳед ва хомӯш бошед, бошад, ки фарогири раҳмат шавед»');
INSERT INTO "pioneers" VALUES(7,205,'7:205','Ва Парвардигоратро дар дили хеш бо тазарруъ ва тарс, бидуни овози баландгуфтор [дар] бомдодон ва шомгоҳон ёд кун ва [аз ёди Аллоҳ таоло] ғофил мабош');
INSERT INTO "pioneers" VALUES(7,206,'7:206','[Эй паёмбар] Касоне, ки назди Парвардигори ту ҳастанд [фариштагон], яқинан, аз парастиши Вай [саркашӣ ва] такаббур намеварзанд ва Ӯро ба покӣ меситоянд ва барояш саҷда мекунанд');
INSERT INTO "pioneers" VALUES(8,1,'8:1','[Эй паёмбар] Аз ту дар бораи [тақсим] анфол [ғаноими ҷангӣ] мепурсанд, бигӯ: «Анфол аз они Аллоҳ таоло ва паёмбар аст, пас, аз Аллоҳ таоло парво кунед ва бо якдигар созиш намоед ва агар имон доред, аз Аллоҳ таоло ва паёмбараш итоат кунед');
INSERT INTO "pioneers" VALUES(8,2,'8:2','Бе тардид, муъминон [-и ҳақиқӣ] касоне ҳастанд, ки чун ёди Аллоҳ таоло мешавад, дилҳояшон ҳаросон мегардад ва чун оёташ бар онон хонда мешавад, бар имонашон меафзояд ва бар Парвардигорашон таваккал мекунанд');
INSERT INTO "pioneers" VALUES(8,3,'8:3','Касоне, ки намоз барпо медоранд ва аз ончи рӯзияшон кардаем, инфоқ мекунанд.');
INSERT INTO "pioneers" VALUES(8,4,'8:4','Онон ҳастанд, ки ба ростӣ муъминанд. Барои онон назди Парвардигорашон дараҷот [-и воло] ва омурзишу рӯзии наку хоҳад буд');
INSERT INTO "pioneers" VALUES(8,5,'8:5','[Эй паёмбар, ихтиёри комили ту бар ғаноим муҷиби нохушнудии бархе афрод аст, дуруст] Ҳамон тавре ки Парвардигорат туро ба ҳақ аз хонаат [дар Мадина ба сӯйи майдони Бадр] берун овард ва ҳатман, гурӯҳе аз муъминон [аз ҷангидан] нохушнуд буданд');
INSERT INTO "pioneers" VALUES(8,6,'8:6','Онон пас аз онки ҳақиқат ошкор шуд, дар бораи ҳақ [-и ҷиҳод] бо ту муҷодала мекунанд, гӯё эшонро ба сӯйи марг меронанд, дар ҳоле ки худ [ба мурданашон] менигаранд');
INSERT INTO "pioneers" VALUES(8,7,'8:7','Ва [эй муҷоҳидон, ба ёд оваред] ҳангоме ки Аллоҳ таоло ба шумо ваъда дод, ки [ғаноими] яке аз ду гурӯҳ [корвони тиҷорӣ ё лашкар] насибатон хоҳад шуд ва шумо дӯст доштед, ки корвони ғайри низомӣ [тиҷорӣ] барои шумо бошад ва Аллоҳ таоло мехост бо суханони хеш [дар бораи куштори кофирон] ҳақ [-и Ислом]–ро пойдор гардонад ва решаи кофиронро қатъ кунад');
INSERT INTO "pioneers" VALUES(8,8,'8:8','[Аллоҳ таоло Исломро пирӯз кард] То ҳақро собит намояд ва ботилро аз миён бардорад, ҳарчанд гунаҳгорон [-и мушрик] нохушнуд бошанд');
INSERT INTO "pioneers" VALUES(8,9,'8:9','[Рӯзи Бадрро ба ёд оваред] Он гоҳ ки аз Парвардигоратон фарёдрасӣ [ва ёрӣ] мехостед ва Ӯ [дархости] шуморо пазируфт [ва фармуд]: «Бе гумон, шуморо бо ҳазор фаришта, ки паёпай фуруд меоянд, ёрӣ мекунам»');
INSERT INTO "pioneers" VALUES(8,10,'8:10','Ва Аллоҳ таоло ин [ёрӣ]-ро ҷуз муждае [бароятон] қарор надод, то дилҳоятон бо он ором гирад ва [бидонед, ки] пирӯзӣ ҷуз аз ҷониби Аллоҳ таоло нест. Ба ростӣ, ки Аллоҳ таоло шикастнопазири ҳаким аст');
INSERT INTO "pioneers" VALUES(8,11,'8:11','[Ва ба ёд оваред] Ҳангоме ки хоби сабук, ки оромише аз сӯйи Ӯ буд, шуморо фаро гирифт ва аз осмон обе бар шумо фурӯ фиристод, то бо он покатон кунад ва палидии шайтонро аз шумо дур созад ва дилҳоятонро маҳкам бидорад ва гомҳоятонро бо он устувор намояд');
INSERT INTO "pioneers" VALUES(8,12,'8:12','[Ва эй паёмбар, ба ёд овар] ҳангоме ки Парвардигорат ба фариштагон ваҳй кард,ки: «Ман бо шумо ҳастам, пас, касонеро, ки имон овардаанд, собитқадам доред. Ба зудӣ, дар дили касоне, ки куфр варзиданд, ҳарос меафканам, пас. [эй муҷоҳидон, шамшери хешро] бар фарози гарданҳояшон бизанед ва ҳамаи ангуштонашонро қатъ кунед [то силоҳ барнагиранд]');
INSERT INTO "pioneers" VALUES(8,13,'8:13','Ин ба хотири он аст, ки онон бо Аллоҳ таоло ва расулаш мухолифат карданд ва ҳар ки бо Аллоҳ таоло ва расулаш мухолифат кунад, пас, [бидон, ки] Аллоҳ таоло сахткайфар аст');
INSERT INTO "pioneers" VALUES(8,14,'8:14','[Эй кофирон] Ин [азоби дунё]-ро бичашед ва [бидонед, ки] барои кофирон [дар қиёмат] азоби оташ аст');
INSERT INTO "pioneers" VALUES(8,15,'8:15','Эй касоне, ки имон овардаед, ҳаргоҳ [дар майдони ҷанг] бо анбӯҳи кофирон рӯбарӯ шудед, пас, ба онон пушт накунед [ва нагурезед]');
INSERT INTO "pioneers" VALUES(8,16,'8:16','Ва ҳар ки дар он ҳангом ба онон пушт кунад, магар он ки [ангезааш фиреби душман ё] канорагирӣ барои набарди [дубора] ё пайвастан ба гурӯҳ [-и дигар аз сипоҳи мусулмонон] бошад, ҳатман, ба хашми Аллоҳ таоло гирифтор мегардад ва ҷойгоҳаш дузах аст. Ва чи бад саранҷомест!');
INSERT INTO "pioneers" VALUES(8,17,'8:17','Пас, [эй муъминон, дар рӯзи набард бо мушрикон дар Бадр] шумо [бо неруи худ] ононро накуштед, балки Аллоҳ таоло ононро кушт ва [эй паёмбар] ҳангоме ки [ба сӯяшон хоку санг] меандохтӣ, [дар ҳақиқат] ту наандохтӣ, балки Аллоҳ таоло андохт, то муъминонро ба озмоиши наку аз ҷониби худ биозмояд. Бе тардид, Аллоҳ таоло шунавову доност');
INSERT INTO "pioneers" VALUES(8,18,'8:18','Ин [шикаст ва фирори мушрикон ва сарафрозии муъминон ҳама аз ҷониби Аллоҳ таоло] буд ва Аллоҳ таоло найранги кофиронро суст [-у беасар] мегардонад');
INSERT INTO "pioneers" VALUES(8,19,'8:19','[Эй мушрикон] Агар хоҳони пирӯзӣ [бар мусулмонон ва фарорасидани азоби илоҳӣ ҳастед], инак пирӯзии [Ислом, ки бузургтарин ранҷ ва азоби шумост] ба суроғатон омад.Ва агар [аз куфру душманӣ бо паёмбар] даст бардоред, он [кор] бароятон беҳтар аст, ва [-ле] агар [ба ҷанг бо мусулмонон] баргардед, мо низ бармегардем.Ва [инро бидонед] ки гурӯҳи шумо, ҳарчанд зиёд бошад, ҳаргиз наметавонад шуморо аз чизе бениёз кунад ва [дигар] инки [ҳамвора] Аллоҳ таоло бо муъминон аст');
INSERT INTO "pioneers" VALUES(8,20,'8:20','Эй касоне, ки имон овардаед, аз Аллоҳ таоло ва расулаш итоат кунед ва аз [аҳкому фармонҳои] Ӯ рӯ нагардонед, дар ҳоле ки [оёти Қуръонро] мешунавед');
INSERT INTO "pioneers" VALUES(8,21,'8:21','Ва монанди касоне набошед, ки гуфтанд: «[Оёти илоҳиро] шунидем», ҳол онки намешуниданд');
INSERT INTO "pioneers" VALUES(8,22,'8:22','Бе гумон, бадтарин ҷунбандагон назди Аллоҳ таоло он [касоне ҳастанд, ки дар баробари ҳақ] кару гунг ҳастанд [ва ҳаққо], ки намеандешанд');
INSERT INTO "pioneers" VALUES(8,23,'8:23','Ва агар Аллоҳ таоло хайре дар онон суроғ дошт, [дар баробари каломи ҳақ] шунавояшон менамуд, ва [ле ҳатто] агар шунавояшон месохт, боз ҳам пушт мекарданд ва рӯйгардон мешуданд');
INSERT INTO "pioneers" VALUES(8,24,'8:24','Эй касоне, ки имон овардаед, ҳаргоҳе Аллоҳ таоло ва паёмбар, шуморо ба чизе фарохонданд, ки ба шумо зиндагӣ мебахшад, ононро иҷобат кунед ва бидонед, ки Аллоҳ таоло миёни инсон ва дилаш қарор мегирад [ва аз асрор огоҳ аст] ва ин ки [дар қиёмат ҳамагӣ] назди Ӯ гирд оварда мешавед');
INSERT INTO "pioneers" VALUES(8,25,'8:25','Ва [эй муъминон] аз азоб бипарҳезед, ки танҳо домани ситамгоронатонро намегирад ва бидонед, ки Аллоҳ таоло сахткайфар аст');
INSERT INTO "pioneers" VALUES(8,26,'8:26','Ва ба ёд оваред замонеро, ки шумо дар сарзамини [Макка] андак ва нотавон будед [ва] метарсидед ки мардумон [-и мушрик] шуморо бирабоянд, пас, Аллоҳ таоло [дар Мадина] паноҳатон дод ва бо ёрии хеш нерумандатон сохт ва аз чизҳои покиза ба шумо рӯзӣ бахшид, бошад, ки сипос гузоред');
INSERT INTO "pioneers" VALUES(8,27,'8:27','Эй касоне, ки имон овардаед, [бо сарпечӣ аз авомир ва навоҳӣ] ба Аллоҳ таоло ва паёмбар хиёнат накунед ва [ҳамчунин] дар амонатҳои худ хиёнат накунед, дар ҳоле ки медонед [ин кор гуноҳ аст]');
INSERT INTO "pioneers" VALUES(8,28,'8:28','Ва бидонед, ки бе тардид, амволу фарзандонатон [василаи] озмоиш аст ва подоши бузург назди Аллоҳ таоло аст');
INSERT INTO "pioneers" VALUES(8,29,'8:29','Эй касоне, ки имон овардаед, агар аз Аллоҳ таоло парво кунед, барои шумо [неруи] ташхис [-и ҳаққу ботил] қарор медиҳад ва гуноҳонатонро аз шумо мезудояд ва шуморо меомурзад. Ва Аллоҳ таоло дорои бахшиши бузург аст');
INSERT INTO "pioneers" VALUES(8,30,'8:30','Ва [эй паёмбар, ба ёд овар] он гоҳ ки кофирон дар бораат дасиса мекарданд, ки туро ба зиндон биафкананд ё бикушанд ё [аз Макка] берунат кунанд. Онон макр меандешиданд ва Аллоҳ таоло [низ тадбиру] макр мекард. Ва Аллоҳ таоло беҳтарини макрангезон аст');
INSERT INTO "pioneers" VALUES(8,31,'8:31','Ва чун оёти Мо бар онон хонда шавад, мегӯянд: «Ба хубӣ шунидем. Агар мехостем, мо низ ҳатман, ҳаммонанди ин [суханон]-ро мегуфтем. Ин [Қуръон, чизе] нест, магар афсонаҳои пешиниён»');
INSERT INTO "pioneers" VALUES(8,32,'8:32','Ва [ба ёд овар] он гоҳ ки гуфтанд: «Парвардигоро, агар ин [Қуръон] ба ростӣ ҳақ аст ва аз ҷониби Туст, пас, аз осмон сангҳо [-и вайронгар] бар мо бибор ё азоби дарднок бар мо бифирист»');
INSERT INTO "pioneers" VALUES(8,33,'8:33','Ва[-ле эй Муҳаммад] дар миёни онон ҳастӣ, Аллоҳ таоло бар он нест ки азобашон кунад ва то замоне, ки [аз даргоҳи илоҳӣ] омурзиш металабанд, Аллоҳ таоло азобкунандаи эшон нест');
INSERT INTO "pioneers" VALUES(8,34,'8:34','Ва чаро Аллоҳ таоло [дар охират] азобашон накунад, бо инки онон [мардумро] аз [тавоф ва ибодат дар] Масҷиду-л-ҳаром бозмедоранд? Ва [мушрикон] ҳаргиз дӯстони Аллоҳ таоло набудаанд, [зеро] дӯстони Аллоҳ таоло касе ҷуз парҳезгорон нестанд, вале бештарашон намедонанд');
INSERT INTO "pioneers" VALUES(8,35,'8:35','Ва намозашон назди хонаи [Каъба] чизе ҷуз фарёд кашидан ва каф задан набуд, пас, ба сазои он ки куфр меварзидед, азоб [-и шикасту асорат дар Бадр]–ро бичашед');
INSERT INTO "pioneers" VALUES(8,36,'8:36','Онон ки куфр варзиданд, амволашонро харҷ мекунанд, то [мардумро] аз роҳи Аллоҳ таоло боздоранд, пас, ба зудӣ ҳамаро харҷ мекунанд, он гоҳ [ин кор мояи] ҳасрат ва пушаймонияшон мегардад ва сипас шикаст мехӯранд ва [саранҷом] касоне, ки куфр варзиданд, ба сӯйи [оташи] дузах кашонда мешаванд');
INSERT INTO "pioneers" VALUES(8,37,'8:37','То [тавассути азоби дузах] Аллоҳ таоло [муъминони] покро аз [мушрикони] палид ҷудо кунад ва [тамоми он амволи] палидро [ки барои нобудии Ислом сарф мешуд] бар ҳам ниҳад ва ҳамагиро мутароким созад ва сипас [якҷо] дар дузах қарор диҳад. Инон ҳамон зиёндидагони [воқеӣ] ҳастанд');
INSERT INTO "pioneers" VALUES(8,38,'8:38','[Эй паёмбар] ба касоне, ки куфр варзиданд, бигӯ, [ки] агар [аз ширк ва душманӣ] даст бардоранд, гузаштаҳояшон бахшида мешавад, ва [-ле] агар [ба рафтори гузаштаи худ] бозгарданд, суннат [-у азоби илоҳӣ, ки дар мавриди] пешиниён гузашт, [такрор хоҳад шуд]');
INSERT INTO "pioneers" VALUES(8,39,'8:39','Ва [эй муъминон] бо онон пайкор кунед, то фитна [ширк] решакан шавад ва [парастишу] дин ҳама аз они Аллоҳ таоло гардад. Пас, агар онон [аз ширку озор] даст бардоштанд, [раҳояшон кунед, чаро ки] бе тардид, Аллоҳ таоло ба ончи мекунанд, биност');
INSERT INTO "pioneers" VALUES(8,40,'8:40','Ва агар [аз фармудаҳои илоҳӣ] сарпечӣ кунанд, пас, бидонед ки Аллоҳ таоло [дӯсту] корсози шумост. Чи наку корсоз ва чи наку ёваре ҳаст!');
INSERT INTO "pioneers" VALUES(8,41,'8:41','Ва бидонед ҳар ғанимате, ки ба даст овардед, як панҷумаш [панҷякаш] барои Аллоҳ таоло ва паёмбар ва барои хешовандон [-и паёмбар, аз Бани Ҳошим ва Бани Мутталиб] ва ятимони [ноболиғ] ва тиҳидастон ва дарроҳмондагон аст. Агар ба Аллоҳ таоло ва ончи ки бар бандаи худ дар рӯзи ҷудоӣ [ҳақ аз ботил], рӯзи бархӯрди ду гурӯҳ [муъминон ва мушрикон] нозил кардем, имон доред [ба ин аҳком амал кунед] ва [бидонед, ки] Аллоҳ таоло бар ҳар чизе тавоност');
INSERT INTO "pioneers" VALUES(8,42,'8:42','Он гоҳ ки шумо [муъминон] бар доманаи наздиктар будед ва онон дар доманаи дуртар қарор доштанд ва корвон [-и тиҷории Курайш] поёнтар аз [майдони ҷанг ва дур аз тиррасии] шумо буд ва агар [шумо ва мушрикон] бо якдигар [дар мавриди маҳалли даргирӣ] ваъда гузошта будед, ҳатман, дар ваъдагоҳи худ ихтилоф мекардед, вале [ба иродаи илоҳӣ дар Бадр ҷамъ шудед], то Аллоҳ таоло кореро, ки ҳатман, [бояд] ба вуқуъ мепайваст, анҷом диҳад, то ҳар [кофире] ки ҳалок мешавад, [бо вуҷуди бартарии низомӣ ва ададӣ дар ширку гумроҳӣ] ҳалок гардад ва ҳар [муъмине] ки зинда мемонад, [ба ҳидоят] зиндагӣ ёбад [ва ин марг ва зиндагӣ] бар асоси далели равшан бошад ва ба ростӣ, ки Аллоҳ таоло шунавову доност');
INSERT INTO "pioneers" VALUES(8,43,'8:43','[Ба ёд овар] Он гоҳ ки Аллоҳ таоло [иддаи] ононро дар хобат ба ту андак нишон дод ва агар эшонро бисёр нишон медод, мусалламан, суст [-у баддил] мешудед ва дар кори [ҷанг] ихтилоф мекардед. Вале Аллоҳ таоло [шуморо аз сустӣ ва ихтилоф] дар амон нигоҳ дошт. Бе гумон, Ӯ ба ончи дар синаҳост, огоҳ аст');
INSERT INTO "pioneers" VALUES(8,44,'8:44','Ва [ба ёд овар] ҳангоме ки бо ҳам рӯбарӯ шудед, ононро ба чашми шумо камтеъдод намуд ва шуморо [низ] ба чашми онон андак ҷилва дод, то Аллоҳ таоло кореро, ки ҳатман, [бояд] ба вуқуъ мепайваст, анҷом диҳад ва [бидонед, ки ҳамаи] корҳо ба Аллоҳ таоло бозгардонда мешавад');
INSERT INTO "pioneers" VALUES(8,45,'8:45','Эй касоне, ки имон овардаед, ҳангоме ки бо гурӯҳе [аз душманон] рӯбарӯ шудед, пойдорӣ намоед ва Аллоҳ таолоро бисёр ёд кунед, бошад, ки растагор шавед');
INSERT INTO "pioneers" VALUES(8,46,'8:46','Ва аз Аллоҳ таоло ва паёмбараш итоат кунед ва [дар тасмимгирӣ] бо якдигар кашмакаш накунед, ки суст мешавед ва [тавону] иқтидоратон аз байн меравад. Ва шикебоӣ кунед, ки бе гумон, Аллоҳ таоло бо шикебоён аст');
INSERT INTO "pioneers" VALUES(8,47,'8:47','Ва монанди касоне набошед, ки бо саркашию худнамоӣ дар баробари мардум аз сарзамини худ [Макка] хориҷ шуданд ва [мардумро] аз роҳи Аллоҳ таоло боздоштанд ва Аллоҳ таоло ба ончи мекунанд, иҳота дорад');
INSERT INTO "pioneers" VALUES(8,48,'8:48','Ва [эй муъминон, ба ёд оред] ҳангоме ки шайтон аъмоли ононро дар назарашон орост ва гуфт: «Имрӯз ҳеҷ кас бар шумо пирӯз намешавад ва ман [ёвару] паноҳатон ҳастам». Аммо ҳангоме ки ду гурӯҳ [бо ҳам] рӯбарӯ шуданд, ба ақиб бозгашту гуфт: «Ба ростӣ, ки ман аз шумо безорам ва чизе [фариштагони ёригар] –ро мебинам, ки шумо намебинед. Ман аз Аллоҳ таоло метарсам ва [аз ҳалок бим дорам, чаро ки] Аллоҳ таоло сахткайфар аст»');
INSERT INTO "pioneers" VALUES(8,49,'8:49','Ва [ба ёд оваред] ҳангоме ки мунофиқон ва касоне, ки дар дилҳояшон беморӣ буд, мегуфтанд: «Ин муъминонро динашон фирефта аст» ва ҳар ки бар Аллоҳ таоло таваккал кунад, пас, [осудахотир бошад, чаро ки] бе тардид, Аллоҳ таоло шикастнопазири ҳаким аст');
INSERT INTO "pioneers" VALUES(8,50,'8:50','Ва агар медидӣ,он гоҳ ки фариштагон ҷони кофиронро меситонданд, бар сурату пушташон мезаданд ва [мегӯянд]: «Азоби сӯзонро бичашед');
INSERT INTO "pioneers" VALUES(8,51,'8:51','Ин [ҷондодани сангин ва азоби дарднок] дар муқобили корҳоест, ки аз пеш фиристодаед, ва[-гарна] Аллоҳ таоло нисбат ба бандагони [худ] ситамгор нест».');
INSERT INTO "pioneers" VALUES(8,52,'8:52','[Азоби ин кофирони ҳақситез] Ҳаммонанди шеваи [муҷозоти ] фиръавниён ва [дигар] касонест, ки пеш аз онон буданд, [ки ҳамагӣ] ба оёти Аллоҳ таоло куфр варзиданд ва Аллоҳ таоло ононро ба [сазои] гуноҳонашон гирифтор кард. Бе тардид, Аллоҳ таоло неруманди сахткайфар аст');
INSERT INTO "pioneers" VALUES(8,53,'8:53','Ин [кайфари сахт] аз он рӯст, ки Аллоҳ таоло ҳаргиз бар он набудааст, ки неъматеро, ки бар мардуме арзонӣ доштааст, дигаргун кунад, магар онки онон [рафтори муваҳҳидонаи] худро тағйир диҳанд [ва куфру носипосӣ биварзанд] ва бе тардид, Аллоҳ таоло шунавову доност');
INSERT INTO "pioneers" VALUES(8,54,'8:54','[Кофирон рафторе доранд] Ҳамчун рафтори фиръавниён ва касоне, ки пеш аз онон буданд, ки оёти Парвардигорашонро дурӯғ меангоштанд, пас, [Мо низ] ононро ба кайфари гуноҳҳояшон ҳалок кардем ва фиръавниёнро ғарқ намудем ва ҳамагӣ [мушрику] ситамгор буданд');
INSERT INTO "pioneers" VALUES(8,55,'8:55','Яқинан, бадтарин ҷунбандагон назди Аллоҳ таоло касоне ҳастанд, ки куфр варзиданд ва имон намеоваранд');
INSERT INTO "pioneers" VALUES(8,56,'8:56','[Ҳамон] Касоне, ки бо онон паймон бастӣ, вале ҳар бор паймонашонро мешикананд ва [аз Аллоҳ таоло] парво намекунанд');
INSERT INTO "pioneers" VALUES(8,57,'8:57','Ва [эй паёмбар] агар дар ҷанг бар онон даст ёфтӣ, чунон дар ҳам бикӯбашон, ки ибрати дигарон гардад, бошад, ки [дигарон] панд гиранд');
INSERT INTO "pioneers" VALUES(8,58,'8:58','Ва агар аз гурӯҳе бими хиёнат дорӣ, [паймонашонро] ба онон бозгардон, [то огоҳии тарафайн аз шикасти паймон] ба таври яксон [бошад]. Бе тардид, Аллоҳ таоло хоинонро дӯст надорад');
INSERT INTO "pioneers" VALUES(8,59,'8:59','Ва касоне, ки куфр варзиданд, ҳаргиз мапиндоранд ки ҷон бадар бурдаанд [ва наҷот ёфтаанд], онон ҳаргиз [аз маргу азоб] роҳи гурезе надоранд');
INSERT INTO "pioneers" VALUES(8,60,'8:60','Ва [эй муъминон] ҳарчи дар тавон доред, аз неру ва аспҳои омода [ва ҷангафзор], басиҷ кунед, то бо ин [тадорукот] душмани Аллоҳ таоло ва душмани хешро битарсонед, ҳамчунин, [бо ин таҷҳизот душманони] дигареро ғайр аз эшон, ки шумо ононро намешиносед, ва [-ле] Аллоҳ таоло мешиносадашон [ба ваҳшат бияндозед] ва ҳарчи дар роҳи Аллоҳ таоло инфоқ кунед, подоши он ба шумо бозгардонда мешавад ва бар шумо ситам намеравад');
INSERT INTO "pioneers" VALUES(8,61,'8:61','Ва агар [кофирон] ба сулҳ моил шуданд, пас, ту [низ эй паёмбар] аз дари сулҳ даро ва бар Аллоҳ таоло таваккал кун. Бе гумон, Ӯст, ки шунавову доност');
INSERT INTO "pioneers" VALUES(8,62,'8:62','Ва агар [кофирон бо тазоҳур ба оштӣ] бихоҳанд фиребат диҳанд, пас, Аллоҳ таоло бароят кофӣ аст. Ӯст, ки бо ёрии худ ва муъминон, туро таъйид [-у тақвият] намуд');
INSERT INTO "pioneers" VALUES(8,63,'8:63','Ва байни дилҳояшон [меҳрубонӣ ва] улфат барқарор намуд. [Эй паёмбар] Агар ту тамоми ончиро, ки дар рӯйи замин аст, харҷ мекардӣ, [ҳаргиз] наметавонистӣ байни дилҳояшонро улфат диҳӣ, вале Аллоҳ таоло миёни онон улфат падид овард. Ба ростӣ, ки Ӯ шикастнопазири ҳаким аст');
INSERT INTO "pioneers" VALUES(8,64,'8:64','Эй паёмбар, [ҳимояти] Аллоҳ таоло барои ту ва [низ] барои муъминоне, ки аз ту пайравӣ кардаанд, кофӣ аст');
INSERT INTO "pioneers" VALUES(8,65,'8:65','Эй паёмбар, муъминонро ба ҷанг ташвиқ кун. Агар аз шумо [муъминон] бист нафар [бар ҷиҳод] шикебо бошанд, бар дусад нафар [аз кофирон] ғалаба мекунанд ва агар сад нафар аз шумо [инчунин] бошанд, бар ҳазор нафар аз касоне, ки куфр варзиданд, ғалаба мекунанд, зеро онон гурӯҳе ҳастанд, ки [арзиши ҳақиқии ҷиҳодро] намефаҳманд');
INSERT INTO "pioneers" VALUES(8,66,'8:66','Акнун Аллоҳ таоло ба шумо сабук гирифт ва донист, ки [бар асари ҷанг] заъфе дар вуҷудатон аст. Пас, агар аз [миёни] шумо сад нафар шикебо бошанд, бар дусад нафар [аз кофирон] пирӯз мешаванд ва агар аз шумо ҳазор нафар бошанд, ба фармони Аллоҳ таоло, бар ду ҳазор нафар ғолиб хоҳанд шуд.Ва Аллоҳ таоло бо шикебоён аст');
INSERT INTO "pioneers" VALUES(8,67,'8:67','Барои ҳеҷ паёмбаре сазовор нест, ки асироне [аз лашкари кофирон] дошта бошад, ки [битавонад бо эъдоми онҳо] дар замин куштори бисёре ба роҳ андозад [то ба ин васила душманро ба ваҳшат биандозад], Шумо [муъминон бо гирифтани фидя аз асирони Бадр сарват ва] колои дунёро мехоҳед ва Аллоҳ таоло охиратро [бароятон] мехоҳад ва Аллоҳ таоло шикастнопазиру ҳаким аст');
INSERT INTO "pioneers" VALUES(8,68,'8:68','Агар дар ончи [аз ғаноиму фидяҳо] гирифтаед, ҳукми пешини илоҳӣ вуҷуд надошт [ва онро бароятон ҳалол накарда буд], ҳатман, азоби бузург ба шумо мерасид');
INSERT INTO "pioneers" VALUES(8,69,'8:69','Пас, [акнун] аз ончи ғанимат гирифтаед, ҳалолу покиза бихӯред ва аз Аллоҳ таоло парво кунед. Ба ростӣ, ки Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(8,70,'8:70','Эй паёмбар, ба асироне, ки дар дасти шумо ҳастанд, бигӯ: «Агар Аллоҳ таоло хайре дар дилҳоятон суроғ дошта бошад, беҳтар аз [фидя ва] ончиро, ки аз шумо гирифта шудааст, ба шумо медиҳад ва шуморо меомурзад ва Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(8,71,'8:71','Ва [эй паёмбар] агар бихоҳанд ба ту хиёнат кунанд, пас, [бидон, ки] онон, ҳатман, пеш аз ин [низ] ба Аллоҳ таоло хиёнат кардаанд ва Аллоҳ таоло [туро] бар онон пирӯз намуд ва Аллоҳ таоло донову ҳаким аст');
INSERT INTO "pioneers" VALUES(8,72,'8:72','Касоне, ки имон оварданд ва ҳиҷрат карданд ва бо амволу ҷонҳояшон дар роҳи Аллоҳ таоло ҷиҳод намуданд ва касоне, ки [муҳоҷиронро] паноҳ доданд ва ёрӣ намуданд, инон [ҳама] дӯстони якдигаранд. Ва касоне, ки имон оварданд, ва [-ле аз сарзамини куфр] ҳиҷрат накарданд, шумо ҳеҷ дӯстӣ [ва пайванде] бо онон надоред, магар онки ҳиҷрат кунанд. Ва агар дар [кори] дин аз шумо ёрӣ хостанд, пас, бар шумост, ки [ононро] ёрӣ кунед, магар [инки ин ёрихоҳӣ] алайҳи гурӯҳе бошад, ки миёни шумо ва онон паймоне барқарор аст. Ва Аллоҳ таоло ба ончи мекунед, биност');
INSERT INTO "pioneers" VALUES(8,73,'8:73','Ва касоне, ки куфр варзиданд, [онон низ] дӯстони якдигаранд. Агар [шумо муъминон ба ин супориш дар бораи дӯсту душман] амал накунед, фитна ва фасоди бузург дар замин барпо мегардад');
INSERT INTO "pioneers" VALUES(8,74,'8:74','Ва касоне, ки имон оварданд ва ҳиҷрат намуданд ва дар роҳи Аллоҳ таоло ҷиҳод карданд ва касоне, ки [ин муҳоҷиринро] паноҳ доданд ва ёрӣ намуданд, инон муъминони ҳақиқӣ ҳастанд. Барои онон [дар биҳишт] омурзиш ва рӯзии шоистае хоҳад буд');
INSERT INTO "pioneers" VALUES(8,75,'8:75','Ва касоне, ки пас аз онон имон оварданд ва ҳиҷрат намуданд ва ба ҳамроҳи шумо [муъминон] ҷиҳод карданд, пас, онон [низ] аз шумо ҳастанд. Ва дар [ҳукми] китоби Аллоҳ таоло хешовандон [дар мерос] нисбат ба якдигар сазовортаранд. Ба ростӣ, ки Аллоҳ таоло ба ҳар чизе доност');
INSERT INTO "pioneers" VALUES(9,1,'9:1','[Ин оёт] Эъломи безорӣ [ва поёни тааҳҳуд] аз тарафи Аллоҳ таоло ва паёмбараш нисбат ба мушриконест, ки [шумо мусулмонон] бо онон паймон бастаед');
INSERT INTO "pioneers" VALUES(9,2,'9:2','Пас, [эй мушрикон, фақат] чаҳор моҳ [фурсат доред, ки бо амнияти комил] дар замин бигардед ва бидонед, ки [ҳаргиз] наметавонед Аллоҳ таолоро нотавон созед [ва аз азобаш бигурезед] ва Аллоҳ таоло расвокунандаи кофирон аст');
INSERT INTO "pioneers" VALUES(9,3,'9:3','Ва [ин] эъломест аз ҷониби Аллоҳ таоло ва паёмбараш ба мардум дар рӯзи ҳаҷҷи акбар [иди қурбон], ки: «Аллоҳ таоло ва паёмбараш аз мушрикон безоранд». Пас, агар тавба кунед, барои шумо беҳтар аст ва агар рӯ бигардонед [ва сарпечӣ кунед], пас, бидонед, ки шумо [ҳаргиз] наметавонед Аллоҳ таолоро нотавон созед [ва аз азобаш бигурезед]. Ва [ту эй паёмбар] касонеро ки куфр варзиданд, ба азоби дардноке башорат бидеҳ');
INSERT INTO "pioneers" VALUES(9,4,'9:4','Магар касоне аз мушрикон, ки бо онон паймон бастаед ва чизе аз [тааҳҳудоти худ нисбат ба] шумо фурӯгузор накардаанд ва касеро алайҳи шумо пуштибонӣ накарданд. Пас, паймони ононро то поёни муддаташон ба поён бирасонед, [ки] бе тардид, Аллоҳ таоло парҳезгоронро дӯст медорад');
INSERT INTO "pioneers" VALUES(9,5,'9:5','Пас, чун моҳҳои ҳаром сипарӣ шуд, мушриконро ҳар куҷо ёфтед, бикушед ва ононро дастгир кунед ва дар муҳосира бигузоред ва дар ҳар гузаргоҳе ба камини онон бинишинед.Пас, агар тавба карданд ва намоз барпо доштанд ва закот пардохтанд, раҳояшон кунед, [чаро ки] бе тардид, Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(9,6,'9:6','Ва [эй паёмбар] агар яке аз мушрикон аз ту паноҳ хост, пас, паноҳаш бидеҳ, то сухани Аллоҳ таолоро бишнавад, сипас ӯро ба маҳалли амнаш бирасон. Ин [амон додан ба мушрикон] ба он сабаб аст, ки онон гурӯҳи нодонанд');
INSERT INTO "pioneers" VALUES(9,7,'9:7','Чигуна мушрикон метавонанд назди Аллоҳ таоло ва паёмбараш [аҳду] паймоне дошта бошанд? Магар [дар мавриди] касоне, ки бо онон наздики Масҷиду-л-ҳаром [дар Ҳудайбия] паймон бастаед. Пас, то [мушрикон дар мавриди он аҳд] бо шумо пойдоранд, шумо [низ] бо онон пойдор бошед, [чаро ки] бе тардид, Аллоҳ таоло парҳезгоронро дӯст медорад');
INSERT INTO "pioneers" VALUES(9,8,'9:8','Чигуна [паймони мушрикон арзиш дорад] бо инки агар бар шумо даст ёбанд, дар бораи шумо на [ҳаққи] хешовандиро муроот мекунанд ва на ҳеҷ паймонеро?! Онон шуморо бо забони худ хушнуд мекунанд ва ҳол онки дилҳояшон имтиноъ меварзад ва бештарашон нофармонанд');
INSERT INTO "pioneers" VALUES(9,9,'9:9','Оёти Аллоҳ таолоро ба баҳои ночизе фурӯхтанд ва [мардумро] аз роҳи Ӯ боздоштанд. Ба ростӣ, чи бад аъмоле анҷом медоданд!');
INSERT INTO "pioneers" VALUES(9,10,'9:10','Инон дар ҳаққи ҳеҷ муъмине, на [ҳаққи] хешовандӣ муроот [риоя] мекунанд ва на паймонеро. Ва инонанд, ки таҷовузкоранд');
INSERT INTO "pioneers" VALUES(9,11,'9:11','Пас, [бо ин ҳол] агар тавба карданд ва намоз барпо доштанд ва закот пардохтанд, бародарони динии шумо [мусулмонон] ҳастанд ва Мо оёти худро барои гурӯҳе, ки медонанд, ба тафсил баён мекунем');
INSERT INTO "pioneers" VALUES(9,12,'9:12','Ва агар [тааҳҳуду] савгандашонро пас аз паймони хеш шикастанд ва дар динатон таъна заданд, пас бо пешвоёни куфр биҷангед; зеро онон аҳд [-у паймоне бо шумо] надоранд, бошад, ки [аз рафтори худ] даст бардоранд');
INSERT INTO "pioneers" VALUES(9,13,'9:13','Эй муъминон] Чаро бо гурӯҳе намеҷангед, ки савганд [ва паймони] худро шикастанд ва дар берун рондани паёмбар [аз Макка] кӯшиданд? Онон буданд, ки нахустин бор [бо ёрӣ кардани қабилаи Бакр дар баробари Хузоа ҷангро] бо шумо оғоз карданд. Оё аз онон метарсед? Дар ҳоле ки агар имон доред, Аллоҳ таоло сазовортар аст, ки аз Ӯ битарсед');
INSERT INTO "pioneers" VALUES(9,14,'9:14','Бо онон биҷангед, то Аллоҳ таоло ба дасти шумо азобашон кунад ва хорашон созад ва шуморо бар онон пирӯз гардонад ва дилҳои муъминонро ором бахшад');
INSERT INTO "pioneers" VALUES(9,15,'9:15','Ва [бо шикасти кофирон] хашми дилҳои онон [муъминон] –ро бартараф кунад. Ва Аллоҳ таоло аз ҳар ки бихоҳад, дармегузарад. Ва Аллоҳ таоло донои ҳаким аст');
INSERT INTO "pioneers" VALUES(9,16,'9:16','Оё пиндоштаед, ки [бидуни имтиҳони илоҳӣ] ба ҳоли худ раҳо мешавед? Дар ҳоле ки Аллоҳ таоло ҳанӯз касоне аз шуморо, ки ҷиҳод карданд ва ба ҷуз Аллоҳ таоло ва паёмбар ва муъминон [касеро ба дӯстӣ ва] ҳамрозӣ нагирифтаанд, маълум надоштааст? Ва Аллоҳ таоло ба ончи мекунед, огоҳ аст');
INSERT INTO "pioneers" VALUES(9,17,'9:17','Сазовор нест, ки мушрикон масоҷиди Аллоҳ таолоро обод кунанд, дар ҳоле ки онон [амалан] ба куфри хеш гувоҳанд [ва онро изҳор мекунанд], ононанд, ки аъмолашон табоҳ гаштааст ва худ дар оташи [дузах] ҷовидонанд');
INSERT INTO "pioneers" VALUES(9,18,'9:18','Масоҷиди Аллоҳ таолоро фақат касоне [бояд] обод кунанд, ки ба Аллоҳ таоло ва рӯзи охират имон овардаанд ва намоз барпо медоранд ва закот мепардозанд ва аз ғайри Аллоҳ таоло наметарсанд. Умед аст, ки инон аз ҳидоятёфтагон бошанд');
INSERT INTO "pioneers" VALUES(9,19,'9:19','[Эй мушрикон] Оё об додан ба ҳоҷиён ва обод кардани Масҷиду-л-ҳаромро бо [имону амали] касе, ки ба Аллоҳ таоло ва рӯзи вопасин имон оварда ва дар роҳи Аллоҳ таоло ҷиҳод кардааст, баробар медонед? [Ин ду ҳаргиз] Назди Аллоҳ таоло баробар нестанд. Ва Аллоҳ таоло ситамгорон [-и мушрик]-ро ҳидоят намекунад');
INSERT INTO "pioneers" VALUES(9,20,'9:20','Касоне, ки имон оварданд ва ҳиҷрат карданд ва дар роҳи Аллоҳ таоло бо амвол ва ҷонҳои хеш ҷиҳод карданд, назди Аллоҳ таоло мақоми бартаре доранд ва инон ҳамон растагоронанд');
INSERT INTO "pioneers" VALUES(9,21,'9:21','Парвардигорашон ононро аз ҷониби хеш ба раҳмату хушнудӣ ва боғҳое [дар биҳишт] мужда медиҳад, ки дар онҳо неъматҳои пойдор доранд');
INSERT INTO "pioneers" VALUES(9,22,'9:22','Ҳамвора дар он ҷовидонанд. Ба ростӣ, ки подоши бузург [фақат] назди Аллоҳ таоло аст');
INSERT INTO "pioneers" VALUES(9,23,'9:23','Эй касоне, ки имон овардаед, агар падарону бародаронатон куфрро бар имон тарҷеҳ медиҳанд, [ононро] ба дӯстӣ нагиред ва ҳар касе аз шумо ононро ба дӯстӣ бигирад, онон ҳамон ситамгоронанд');
INSERT INTO "pioneers" VALUES(9,24,'9:24','[Эй паёмбар, ба муъминон] Бигӯ: «Агар падарону фарзандон ва бародарон ва ҳамсарон ва хешовандонатон ва амволе, ки ба даст овардаед ва тиҷорате, ки аз касодиаш метарсед ва хонаҳое, ки ба он дилхуш ҳастед, назди шумо аз Аллоҳ таоло ва паёмбараш ва ҷиҳод дар роҳи Ӯ маҳбубтаранд, пас, мунтазир бошед, то Аллоҳ таоло фармони [азоби] хешро нозил кунад. Ва Аллоҳ таоло гурӯҳи нофармонро ҳидоят намекунад»');
INSERT INTO "pioneers" VALUES(9,25,'9:25','Бе тардид, Аллоҳ таоло шумо [муъминон]-ро дар мавқеиятҳои бисёр ёрӣ кардааст ва низ дар ҷанги Ҳунайн, он гоҳ ки фаровонии теъдодатон шуморо [шоду] шигифтзада кард, вале ҳеҷ суде ба ҳолатон надошт ва замин бо ҳама густурдагӣ бар шумо танг омад, он гоҳ [ба душман] пушт кардед [ва гурехтед]');
INSERT INTO "pioneers" VALUES(9,26,'9:26','Сипас Аллоҳ таоло оромиши хешро бар паёмбараш ва муъминон фурӯ фиристод ва сипоҳиёнеро, ки шумо намедидед [ба кумакатон] фиристод ва ононро, ки куфр варзида буданд, азоб кард. Ва ҷазои кофирон чунин аст');
INSERT INTO "pioneers" VALUES(9,27,'9:27','Сипас Аллоҳ таоло баъд аз он [азоби хоркунанда] тавбаи ҳаркиро бихоҳад, мепазирад ва Аллоҳ таоло омурзандаву меҳрубон аст');
INSERT INTO "pioneers" VALUES(9,28,'9:28','Эй касоне, ки имон овардаед, ҷуз ин нест, ки мушрикон ҳатман, палиданд, пас, набояд баъд аз имсол ба Масҷиду-л-ҳаром наздик шаванд. Ва агар [ба хотири қатъи тиҷорат бо онон] аз тангдастӣ бим доред, [бидонед, ки] агар Аллоҳ таоло бихоҳад, шуморо аз бахшиши хеш бениёз месозад. Бе тардид, Аллоҳ таоло доною ҳаким аст');
INSERT INTO "pioneers" VALUES(9,29,'9:29','Бо касоне [аз аҳли китоб], ки ба Аллоҳ таоло ва рӯзи қиёмат имон намеоранд ва ончиро Аллоҳ таоло ва паёмбараш ҳаром кардаанд, ҳаром намешуморанд ва дини ҳақро намепазиранд, биҷангед, то он гоҳ ки бо шикасту хорӣ ба дасти хеш ҷизя диҳанд');
INSERT INTO "pioneers" VALUES(9,30,'9:30','Ва яҳудиён гуфтанд: «Узайр писари Аллоҳ таоло аст» ва масеҳиён гуфтанд: «Масеҳ писари Аллоҳ таоло аст». Ин сухан [-и ботил]-е аст, ки бар забон меоваранд ва ба гуфтори касоне, ки пеш аз ин куфр варзиданд, шабоҳат дорад. Аллоҳ таоло ҳалокашон кунад! Чигуна [аз ҳақ] мунҳариф мешаванд?');
INSERT INTO "pioneers" VALUES(9,31,'9:31','[Ин мушрикон] Уламо ва роҳибони худ ва [ҳамчунин] Масеҳ писари Марямро ба ҷойи Аллоҳ таоло [ба унвони] маъбудони хеш талаққӣ карданд, ҳол онки дастуре ҷуз ин надоштанд, ки фақат Аллоҳ таолои ягонаро бипарастанд, ки маъбуде [ба ҳақ] ҷуз Ӯ нест. Ӯ аз ончи шарикаш қарор медиҳанд, муназзаҳ аст');
INSERT INTO "pioneers" VALUES(9,32,'9:32','Онон мехоҳанд нури Аллоҳ таолоро бо суханони хеш хомӯш кунанд, вале Аллоҳ таоло ҷуз ин намехоҳад, ки нури хешро комил кунад, ҳарчанд кофирон нохушнуд бошанд');
INSERT INTO "pioneers" VALUES(9,33,'9:33','Ӯст, ки паёмбарашро бо ҳидояту дини ҳақ фиристодааст, то онро бар ҳамаи адён пирӯз гардонад, ҳарчанд мушрикон нохушнуд бошанд');
INSERT INTO "pioneers" VALUES(9,34,'9:34','Эй касоне, ки имон овардаед, бе тардид, бисёре аз уламо[-и яҳудӣ] ва роҳибон, амволи мардумро ба ноҳақ мехӯранд ва [ононро] аз роҳи Аллоҳ таоло бозмедоранд.Ва касоне, ки зару сим меандузанд ва онро дар роҳи Аллоҳ таоло инфоқ намекунанд, пас, ононро ба азоби дарднок башорат бидеҳ');
INSERT INTO "pioneers" VALUES(9,35,'9:35','Рӯзе, ки он [ганҷинаҳо]-ро дар оташи дузах сурх кунанд ва пешонӣ ва паҳлу ва пушташонро бо он доғ ниҳанд [ва бигӯянд]: «Ин аст ончи барои хеш меандӯхтед, пас, бичашед ончиро, ки меандӯхтед»');
INSERT INTO "pioneers" VALUES(9,36,'9:36','Шумори моҳҳо назди Аллоҳ таоло [аз] рӯзе, ки осмонҳо ва заминро офаридааст, дар китоби Аллоҳ таоло [Лавҳи маҳфуз] дувоздаҳ моҳ аст, ки [ҷангу даргирӣ дар] чаҳор моҳ аз он ҳаром аст [Зулқаъда, Зулҳиҷҷа, Муҳаррам ва Раҷаб]. Ин аст оини устувор [ва роҳи ҳақ]. Пас, дар ин [чаҳор моҳ] бар худ ситам накунед ва ҳамагӣ бо мушрикон биҷангед, чунонки ҳамаи онон бо шумо меҷанганд ва бидонед, ки Алллоҳ таоло ҳамроҳи парҳезгорон аст');
INSERT INTO "pioneers" VALUES(9,37,'9:37','Бе тардид, ба таъхир андохтан [-и моҳҳои ҳаром ва барҳам задани тартиби асли онҳо муҷиби] афзоиши куфр аст [ва] касоне, ки куфр варзиданд, бо ин кор ба гумроҳӣ кашида мешаванд. Он [моҳ]-ро як сол ҳалол мешуморанд ва як сол [-и дигар] ҳаромаш медонанд, то бо теъдоди моҳҳое, ки Аллоҳ таоло ҳаром кардааст, баробар созанд. Пас, [ба он сабаб] ончиро, ки Аллоҳ таоло ҳаром кардааст, [бар худ] ҳалол мекунанд.Зиштии аъмолашон бар эшон ороста шудааст ва Аллоҳ таоло гурӯҳи кофиронро ҳидоят намекунад');
INSERT INTO "pioneers" VALUES(9,38,'9:38','Эй касоне, ки имон овардаед, шуморо чи шудааст, ки чун ба шумо гуфта мешавад: «Дар роҳи Аллоҳ таоло раҳсипор [ҷиҳод] шавед», сустӣ ба харҷ медиҳед [ва дар хонаҳоятон менишинед]? Оё ба ҷойи охират ба зиндагии дунё дилхуш кардаед? [Пас, бидонед, ки] колои зиндагии дунё дар баробари охират ҷуз андаке нест');
INSERT INTO "pioneers" VALUES(9,39,'9:39','[Эй муъминон] Агар раҳсипор [-и ҷиҳод] нагардед, [Аллоҳ таоло] ба азоби дарднок гирифторатон мекунад ва ба ҷойи шумо гурӯҳи дигареро меоварад ва [шумо бо нофармонӣ ҳаргиз] ба Ӯ зиёне намерасонед. Ва Аллоҳ таоло бар ҳар чизе тавоност');
INSERT INTO "pioneers" VALUES(9,40,'9:40','[Эй асҳоби паёмбар] Агар шумо ӯро ёрӣ накунед, ҳатман, Аллоҳ таоло ёриаш намуд: ҳангоме ки кофирон ӯро [аз Макка] берун карданд, он гоҳ ки яке аз он ду, ки дар ғор [-и Савр паноҳ гирифта] буданд, ба ҳамроҳи худ [Абубакр] гуфт: «Андуҳгин мабош, [ки] бе тардид, Аллоҳ таоло бо мост». Пас, Аллоҳ таоло оромиши худро бар ӯ фурӯ фиристод ва ӯро бо сипоҳиёне, ки онҳоро намедидед, таъйид [-у тақвият] кард ва сухани касонеро, ки куфр варзиданд, пасттар гардонд. Ва сухани Аллоҳ таолост, ки бартар аст ва Аллоҳ таоло шикастнопазиру ҳаким аст');
INSERT INTO "pioneers" VALUES(9,41,'9:41','Сабукбору гаронбор [ба ҳар ҳолате барои ҷиҳод] раҳсипор шавед ва бо амволу ҷонҳои хеш дар роҳи Аллоҳ таоло ҷиҳод кунед. Агар бидонед, ин барои шумо беҳтар аст');
INSERT INTO "pioneers" VALUES(9,42,'9:42','[Эй паёмбар] Агар [ҳадафи мунофиқон аз ҳамроҳӣ бо ту касби ғанимат ва ёфтани] моле дар дастрас ва сафари [осон ва] кӯтоҳ буд, ҳатман, ба дунболат меомаданд. Вале он роҳи пурмашаққати [ Шом] бар эшон дур [-у душвор] намуд ва ба зудӣ ба Аллоҳ таоло савганд хоҳанд хӯрд, ки: «Агар метавонистем, ҳатман, бо шумо раҳсипор мешудем» [Онон бо нофармонӣ ва савганди дурӯғ] худро ба ҳалокат афканданд ва Аллоҳ таоло медонад, ки онон, яқинан, дурӯғгӯ ҳастанд');
INSERT INTO "pioneers" VALUES(9,43,'9:43','[Эй паёмбар] Аллоҳ таоло аз ту даргузарад! Чаро пеш аз онки [ҳоли] ростгӯён бар ту равшан гардад ва дурӯғгуёнро бозшиносӣ, ба онон иҷозат додӣ [ки дар ҷиҳод ширкат кунанд]?');
INSERT INTO "pioneers" VALUES(9,44,'9:44','Касоне, ки ба Аллоҳ таоло ва рӯзи вопасин имон доранд, дар ҷиҳод бо амволу ҷонҳояшон аз ту иҷозат [муофият] намехоҳанд. Ва Аллоҳ таоло ба [ҳоли] парҳезгорон доност');
INSERT INTO "pioneers" VALUES(9,45,'9:45','Танҳо касоне аз ту иҷозат [-и муофият аз ҷиҳод] мехоҳанд, ки ба Аллоҳ таоло ва рӯзи вопасин имон надоранд ва дилҳояшон ба тардид олуда аст ва дар тардиди худ саргардонанд');
INSERT INTO "pioneers" VALUES(9,46,'9:46','Ва агар [ба ростӣ] иродаи раҳсипорӣ [ба қасди ҷиҳод] доштанд, ҳатман, созу барге барояш фароҳам мекарданд, вале Аллоҳ таоло раҳсипори ононро хуш надошт, пас, эшонро мунсариф намуд ва [ба онон] гуфта шуд: «Бо хонанишинон бимонед»');
INSERT INTO "pioneers" VALUES(9,47,'9:47','[Эй муъминон] Агар [мунофиқон] ҳамроҳи шумо раҳсипор мешуданд, [ба сабаби буздилӣ ва фирор аз майдони ҷанг] ҷуз фитна ва фасод бароятон ба бор намеоварданд ва миёнатон нуфуз менамуданд ва дар ҳаққи шумо фитнаҷӯӣ мекарданд ва дар ҷамъатон ҷосусоне доранд. Ва Аллоҳ таоло ба [ҳоли] ситамгорон огоҳ аст');
INSERT INTO "pioneers" VALUES(9,48,'9:48','Яқинан, пеш аз ин [низ мунофиқон бисёр] фитнаҷӯӣ карданд ва корҳоро бар ту [воруна ва] ошуфта ҷилва доданд, то онки ҳақ омад ва фармони Аллоҳ таоло ошкор гардид, дар ҳоле ки онон кароҳат доштанд');
INSERT INTO "pioneers" VALUES(9,49,'9:49','Ва аз эшон касе ҳаст, ки мегӯяд: «Ба ман иҷозат бидеҳ, [ки ҷиҳод накунам] ва маро дар фитна наандоз». Огоҳ бошед! [Мунофиқон] Ҳам акнун низ дар фитна афтодаанд ва бе тардид, дузах бар кофирон иҳота дорад');
INSERT INTO "pioneers" VALUES(9,50,'9:50','[Эй паёмбар] Агар ба ту некӣ бирасад, ононро нороҳат мекунад ва агар мусибате ба ту расад, мегӯянд: «Мо пеш аз ин чораи кори худ андешидаем» ва шодмон [назди хонаводаҳояшон] бозмегарданд');
INSERT INTO "pioneers" VALUES(9,51,'9:51','Бигӯ: «Ҳаргиз [мусибате] ба мо намерасад, ҷуз ончи Аллоҳ таоло бароямон муқаррар кардааст. Ӯ [дӯсту] корсози мост ва муъминон бояд ба Аллоҳ таоло таваккал кунанд»');
INSERT INTO "pioneers" VALUES(9,52,'9:52','[Ба мунофиқон] Бигӯ: «Оё барои мо ҷуз яке аз ду некӣ [пирӯзӣ ё шаҳодат]-ро интизор доред? Дар ҳоле ки мо интизор мекашем, ки Аллоҳ таоло аз ҷониби хеш ё ба дасти мо ба азоб [-и сахте] гирифторатон кунад. Пас, интизор бикашед, ки бе тардид, мо [низ] бо шумо мунтазирем»');
INSERT INTO "pioneers" VALUES(9,53,'9:53','[Ба мунофиқон] Бигӯ: «Чи бо рағбат, чи бо кароҳат инфоқ кунед, ҳаргиз аз шумо пазируфта намешавад, [чаро ки] бе тардид, шумо гурӯҳи нофармон будаед»');
INSERT INTO "pioneers" VALUES(9,54,'9:54','Ва ҳеҷ чиз монеи қабули бахшишҳояшон нашуд, ҷуз инки онон ба Аллоҳ таоло ва паёмбараш куфр варзиданд ва ҷуз бо сустӣ намоз намегузоранд ва ҷуз ба кароҳат [ва бемайлӣ] инфоқ намекунанд');
INSERT INTO "pioneers" VALUES(9,55,'9:55','Пас, [эй паёмбар] амволу фарзандонашон туро шигифтзада накунад. Бе тардид, Аллоҳ таоло мехоҳад дар зиндагии дунё ба василаи инҳо азобашон кунад ва дар ҳоле ки кофиранд, ҷонашон барояд');
INSERT INTO "pioneers" VALUES(9,56,'9:56','Ва онон [ мунофиқон] ба Аллоҳ таоло савганд ёд мекунанд, ки аз шумо [муъминон] ҳастанд, дар ҳоле ки аз шумо нестанд, вале онон гурӯҳе ҳастанд, ки метарсанд [ва ба ин сабаб савганд мехӯранд]');
INSERT INTO "pioneers" VALUES(9,57,'9:57','Агар паноҳгоҳ ё ғор ё гурезгоҳе пайдо кунанд, шитобон ба сӯйи он рӯ меоваранд');
INSERT INTO "pioneers" VALUES(9,58,'9:58','Ва бархе аз онон ҳастанд, ки дар [тақсими] садақот [ва ғаноим] бар ту хурда мегиранд, пас, агар аз он [амвол] ба эшон дода шавад, хушнуд мегарданд ва агар аз он ба эшон дода нашавад, он гоҳ хашмгин мешаванд');
INSERT INTO "pioneers" VALUES(9,59,'9:59','Ва [ҳатман, бар эшон беҳтар буд], агар онон ба ончи Аллоҳ таоло ва паёмбараш ба эшон додаанд, хушнуд мегаштанд ва мегуфтанд: «Аллоҳ таоло бароямон кофист, ба зудӣ Аллоҳ таоло ва паёмбараш аз фазлу бахшиши худ ба мо медиҳад ва мо ба Аллоҳ таоло [умедвор ва ] муштоқем»');
INSERT INTO "pioneers" VALUES(9,60,'9:60','Садақот [ва закот] ихтисос дорад ба фақирон [-и тиҳидаст] ва мустамандон [-и тангдаст] ва коргузорон [-и ҷамъоварӣ ва тавзеи] он ва дилҷӯшудагон [тозамусулмон] ва барои [озод кардани] бардагон ва [пардохти воми] қарздорон ва дар роҳи Аллоҳ таоло ва [кумак ба] дарроҳмондагон. [Ин] Фаризаи [муқарраршуда] аз ҷониби Аллоҳ таоло аст ва Аллоҳ таоло донои ҳаким аст');
INSERT INTO "pioneers" VALUES(9,61,'9:61','Ва бархе аз онон [мунофиқон] ҳастанд, ки паёмбарро меозоранд ва мегӯянд: «Ӯ зудбовар [ва соропо гӯш] аст». [Эй паёмбар] Бигӯ: «Ӯ барои шумо [фақат] шунавандаи сухани хайр аст. [Вай] ба Аллоҳ таоло имон дорад ва [сухани] муъминонро бовар дорад ва барои касоне аз шумо, ки имон овардаанд, раҳматест». Ва касоне, ки Расулуллоҳро меозоранд, азоби дарднок [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(9,62,'9:62','[Эй муъминон, мунофиқон] Бароятон ба Аллоҳ таоло савганд ёд мекунанд, то шуморо хушнуд созанд, дар ҳоле ки агар имон доштанд, шоистатар [ин] буд, ки Аллоҳ таоло ва паёмбарашро хушнуд созанд');
INSERT INTO "pioneers" VALUES(9,63,'9:63','Оё намедонанд, ҳар ки бо Аллоҳ таоло ва расулаш мухолифат [ва душманӣ] кунад, мусалламан, оташи ҷаҳаннам барои ӯст [ва] ҷовидона дар он хоҳад буд? Ин ҳамон расвоии бузург аст');
INSERT INTO "pioneers" VALUES(9,64,'9:64','Мунофиқон бим доранд, ки [мабодо] сурае дар бораи онон [бар паёмбар] нозил шавад ва онон [ муъминон]-ро аз ончи дар дилҳои эшон [мунофиқон] аст, огоҳ созад. [Эй паёмбар, ба онон] Бигӯ: «[Ҳарчи мехоҳед] Масхара кунед, [ки] бе тардид, Аллоҳ таоло ончиро, ки [аз он мепарҳезед ва] ҳарос доред, ифшо хоҳад кард»');
INSERT INTO "pioneers" VALUES(9,65,'9:65','Ва агар аз онон бипурсӣ: [«Чаро тамасхур мекардед»?] Мусалламан, мегӯянд: «Мо ҷиддӣ намегуфтем ва [шӯхӣ ва] бозӣ мекардем». Бигӯ: «Оё Аллоҳ таоло ва оёти Ӯ ва паёмбарашро масхара мекардед»?');
INSERT INTO "pioneers" VALUES(9,66,'9:66','Узр наоваред. Ба ростӣ, ки шумо пас аз имонатон кофир шудаед. Агар гурӯҳе аз шуморо [ба хотири тавбаашон] бибахшем, гурӯҳи дигарро [ба сабаби саркашӣ ва тарки инфоқ] азоб хоҳем кард, чаро ки бе тардид, онон табаҳкор буданд');
INSERT INTO "pioneers" VALUES(9,67,'9:67','Мардону занони мунофиқ [монанди якдигар ва] аз як гурӯҳанд: ба корҳои зишт фармон медиҳанд ва аз некиҳо бозмедоранд ва дастҳои хешро [аз инфоқ] фурӯ мебанданд [ва] Аллоҳ таолоро фаромӯш карданд. Пас, Аллоҳ таоло [низ бо бетафовутӣ] ононро ба фаромӯшӣ супурд [ва раҳмату тавфиқашро аз онон салб намуд]. Бе гумон, мунофиқон ҳамон фосиқонанд');
INSERT INTO "pioneers" VALUES(9,68,'9:68','Аллоҳ таоло ба мардону занони мунофиқ ва [ҳамчунин] ба кофирон ваъдаи оташи дузах додааст, ҷовидона дар он хоҳанд монд. Ҳамон [оташ] барояшон кофист ва Аллоҳ таоло лаънаташон кардааст ва азоби пойдор [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(9,69,'9:69','[Шумо мунофиқон] Ҳаммонанди касоне [ҳастед], ки пеш аз шумо буданд, [балки] онон аз шумо нерумандтар буданд ва амволу фарзандони бештаре доштанд. Пас, аз баҳраи хеш [дар дунё] бархӯрдор шуданд ва шумо низ аз баҳраи худ бархӯрдор шудед ҳамчунон ки онон ки пеш аз шумо буданд [низ] аз баҳраи хеш бархӯрдор шуданд. Ва шумо [дар гуноҳи такзиби ҳақ ва тавҳин ба паёмбари худ] фурӯ рафтед, ҳамон тавре ки онон фурӯ рафтанд. Онон аъмолашон дар дунё ва охират табоҳ гашт ва ононанд, ки табоҳкоронанд');
INSERT INTO "pioneers" VALUES(9,70,'9:70','Оё хабари касоне, ки пеш аз онон буданд, ба онон нарасидааст? [Касоне ҳамчун] Қавми Нуҳ ва Од ва Самуд ва қавми Иброҳим ва асҳоби Мадян [қавми Шуайб] ва шаҳрҳои зерурӯшуда [-и қавми Лут], ки паёмбаронашон далоили равшан барояшон оварданд [вале онон напазируфтанд]. Пас, Аллоҳ таоло бар он набуд, ки [бо нузули азоб] ба онон ситам кунад, балки худ бар хештан ситам мекарданд');
INSERT INTO "pioneers" VALUES(9,71,'9:71','Ва мардон ва занони муъмин дӯстони якдигаранд, [мардумро] ба некӣ фармон медиҳанд ва аз нописандӣ бозмедоранд ва намоз барпо мекунанд ва закот мепардозанд ва аз Аллоҳ таоло ва паёмбараш итоат менамоянд. Инонанд, ки Аллоҳ таоло мавриди раҳмат қарорашон хоҳад дод. Бе тардид, Аллоҳ таоло шикастнопазири ҳаким аст');
INSERT INTO "pioneers" VALUES(9,72,'9:72','Аллоҳ таоло ба мардону занони муъмин боғҳое [аз биҳишт] ваъда додааст, ки ҷӯйборҳо аз зери [дарахтони] он ҷорӣ аст, ҷовидона дар он хоҳанд монд. Ва [низ] хонаҳои покизае дар биҳиштҳои ҷовидон [ба онон ваъда додааст] ва хушнудии Аллоҳ таоло [аз ҳамаи инҳо] бартар аст. Ин ҳамон [растагорӣ ва] комёбии бузург аст');
INSERT INTO "pioneers" VALUES(9,73,'9:73','Эй паёмбар бо кофирон ва мунофиқон ҷиҳод кун ва бар онон сахтгир бош. Ва ҷойгоҳашон дузах аст ва чи бад саранҷомест!');
INSERT INTO "pioneers" VALUES(9,74,'9:74','[Мунофиқон] Ба Аллоҳ таоло савганд ёд мекунанд, ки [суханони нописанде дар бораи Аллоҳ таоло ва паёмбараш] нагуфтаанд ва ба ростӣ, ки сухани куфр гуфтаанд ва пас аз Ислом оварданашон [боз] кофир шуданд [ва бо дасиса барои қатли паёмбар] тасмим ба коре гирифтанд, ки дар он муваффақ нашуданд. Ва ба айбҷӯӣ [ва мухолифат] барнахостанд, магар пас аз онки Аллоҳ таоло ва паёмбараш [бо бахшидани ғаноим] ононро аз фазли хеш бениёз карданд. Пас, агар тавба кунанд, барояшон беҳтар аст ва агар рӯй бигардонанд, Аллоҳ таоло ононро дар дунё ва охират ба азоби дарднок дучор месозад ва дар замин ҳеҷ корсозу ёваре нахоҳанд дошт');
INSERT INTO "pioneers" VALUES(9,75,'9:75','Баъзе аз онон бо Аллоҳ таоло [аҳду] паймон бастанд, ки: «Агар [Аллоҳ таоло] аз фазли худ [насибе] ба мо диҳад, ҳатман, садақа [ва закот] хоҳем дод ва аз накукорон хоҳем буд»');
INSERT INTO "pioneers" VALUES(9,76,'9:76','Пас чун [Аллоҳ таоло] аз фазли худ ба онон [насибе] бахшид, нисбат ба он бухл варзиданд ва [аз он паймон] рӯ гардонданд ва [аз ҳақ] сарпечӣ карданд');
INSERT INTO "pioneers" VALUES(9,77,'9:77','Пас, бо сазои онки бо Аллоҳ таоло хилофи ваъда карданд ва ба он хотир, ки дурӯғ мегуфтанд, [Аллоҳ таоло низ] то рӯзе, ки Ӯро [дар қиёмат] мебинанд, дар дилҳояшон доғ [-у нанги] нифоқ ниҳод');
INSERT INTO "pioneers" VALUES(9,78,'9:78','Оё надонистаанд, ки Аллоҳ таоло розу наҷвояшонро [ки дар маҳфилҳояшон баён мешавад, ба хубӣ] медонад ва Аллоҳ таоло донои розҳои ниҳон аст?');
INSERT INTO "pioneers" VALUES(9,79,'9:79','[Мунофиқон] Касоне [ҳастанд], ки бар муъминон айб мегиранд, ки ба хости хеш садақот [-и бисёр] медиҳанд ва [ҳамчунин, фуқаро ва] афродеро, ки беш аз тавони моли хеш чизе [барои садақа] намеёбанд, масхара мекунанд. Аллоҳ таоло ононро ба тамасхур мегирад ва барояшон азоби дарднок [дар пеш] хоҳад буд');
INSERT INTO "pioneers" VALUES(9,80,'9:80','[Эй паёмбар] Чи барои онон омурзиш бихоҳӣ, чи омурзиш нахоҳӣ, [суде ба ҳолашон надорад, ҳатто] агар ҳафтод бор барояшон омурзиш бихоҳӣ, Аллоҳ таоло ҳаргиз ононро намеомурзад, чаро ки ба Аллоҳ таоло ва паёмбараш куфр варзиданд, ва Аллоҳ таоло гурӯҳи нофармонро ҳидоят намекунад');
INSERT INTO "pioneers" VALUES(9,81,'9:81','Вомондагон [аз ғазваи Табук] пас аз [мухолифат бо] Расулуллоҳ аз хонанишинии худ шодмон шуданд ва аз инки бо амволу ҷонҳои хеш дар роҳи Аллоҳ таоло ҷиҳод кунанд, кароҳат доштанд ва [ба афроди сустимон] мегуфтанд: «Дар ин гармо раҳсипор [-и ҷиҳод] нашавед». [Эй паёмбар ба онон] Бигӯ: «Агар дармеёфтанд, оташи дузах [аз ин бисёр] сӯзонтар аст»');
INSERT INTO "pioneers" VALUES(9,82,'9:82','Ба сазои ончи мекарданд, бояд [дар дунё] кам биханданд ва [дар охират] бисёр бигирянд');
INSERT INTO "pioneers" VALUES(9,83,'9:83','Ва агар Аллоҳ таоло туро ба сӯйи гурӯҳе аз онҳо бозгардонд ва аз ту барои ҳамроҳӣ [дар ҷиҳоди дигар] иҷозат хостанд, бигӯ: «[Аз ин пас] Ҳаргиз бо ман раҳсипор [-и ҷиҳод] нахоҳед шуд ва ҳаргиз ҳамроҳ бо ман бо ҳеҷ душмане набард нахоҳед кард, зеро шумо нахустинбор ба хонанишинӣ ризоият додед, пас, бо хонанишинон бинишинед');
INSERT INTO "pioneers" VALUES(9,84,'9:84','Ва ҳаргиз бар ҳеҷ мурдае аз онон намоз нахон ва [барои дуо хондан ва талаби омурзиш] бар сари қабраш наист, [чаро ки] бе тардид, онон ба Аллоҳ таоло ва паёмбараш куфр варзидаанд ва дар ҳоле мурданд, ки нофармон буданд');
INSERT INTO "pioneers" VALUES(9,85,'9:85','Ва [эй паёмбар, афзунии] амвол ва фарзандонашон туро шигифтзада накунад. Ба ростӣ, ҷуз ин нест, ки Аллоҳ таоло мехоҳад ба ин [васила] дар дунё азобашон кунад ва дар ҳоле ки кофиранд, ҷонашон барояд');
INSERT INTO "pioneers" VALUES(9,86,'9:86','Ва ҳангоме ки сурае нозил шавад [ва амр мекунад], ки «Ба Аллоҳ таоло имон биёваред ва ҳамроҳи паёмбараш ҷиҳод кунед», сарватмандонашон [барои нарафтан ба ҷиҳод] аз ту иҷозат мехоҳанд ва мегӯянд: «Бигзор, мо бо хонанишинон бошем»');
INSERT INTO "pioneers" VALUES(9,87,'9:87','Онон розӣ шуданд, ки бо хонанишинон бошанд ва бар дилҳояшон муҳр [-и нифоқ] ниҳода шудааст. Пас, [хайру салоҳи хешро] дарнамеёбанд');
INSERT INTO "pioneers" VALUES(9,88,'9:88','Аммо паёмбар ва касоне, ки бо ӯ имон овардаанд, бо амволу ҷонҳои худ [дар роҳи Аллоҳ таоло] ҷиҳод карданд. Ононанд, ки ҳамаи некиҳо барои эшон аст ва ононанд ки растагоранд');
INSERT INTO "pioneers" VALUES(9,89,'9:89','Аллоҳ таоло бар эшон боғҳое [аз биҳишт] муҳайё кардааст, ки ҷуйборҳо аз зери [дарахтони] он ҷорӣ аст [ва] ҷовидона дар он мемонанд. Ин [ҳамон растагорӣ ва] комёбии бузург аст');
INSERT INTO "pioneers" VALUES(9,90,'9:90','Ва узртарошони бодиянишин омаданд, то ба онон иҷозати [маофият аз ҷиҳод] дода шавад ва касоне, ки [дар иддаои имони худ] ба Аллоҳ таоло ва паёмбараш дурӯғ мегуфтанд, [бидуни ҳеҷ узре ва иҷозате ба ҷойи ҷиҳод] хонанишин бошанд. Ба зудӣ ба касоне аз онон, ки куфр варзиданд, азоби дарднок хоҳад расид');
INSERT INTO "pioneers" VALUES(9,91,'9:91','Бар нотавонон [занон ва кӯдакон] ва беморон ва бар касоне, ки чизе намеёбанд, [то дар роҳи ҷиҳод] харҷ кунанд, агар барои Аллоҳ таоло ва паёмбараш хайрхоҳӣ кунанд, гуноҳе нест [ки дар ҷиҳод ширкат накунанд]. Бар накукорон [-и эшон низ] ҳеҷ роҳе [барои сарзаниш ва муҷозот] нест ва Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(9,92,'9:92','Ва [ҳамчунин] гуноҳе нест бар касоне, ки чун наздат омаданд, то [барои рафтан ба ҷиҳод бар чаҳорпое] саворашон кунӣ, [ба онон] гуфтӣ: «Чизе пайдо намекунам, ки шуморо бар он биншонам». Онон бозгаштанд, дар ҳоле ки чашмонашон аз ин андуҳ ашкбор буд, ки чизе намеёбанд, то [дар роҳи ҷиҳод] харҷ кунанд');
INSERT INTO "pioneers" VALUES(9,93,'9:93','Роҳ [-и сарзанишу муҷозот] танҳо барои касоне [кушода] аст, ки бо онки тавонгар [ва имкони ҷиҳод доранд], аз ту иҷозат мехоҳанд [ки дар ҷиҳод ширкат накунанд]. Онон ба ин ризоият доданд, ки бо хонанишинон бошанд ва Аллоҳ таоло бар дилҳояшон муҳр [-и нифоқ] задааст, пас, [хайру салоҳи хешро] дарнамеёбанд');
INSERT INTO "pioneers" VALUES(9,94,'9:94','Вақте [аз ғазваи Табук] ба сӯяшон баргардед, бароятон узр меоваранд. [Эй паёмбар] Бигӯ: «Узр наёваред, мо ҳаргиз [сухани] шуморо бовар намекунем. Дар ҳақиқат, Аллоҳ таоло моро аз ахбори шумо огоҳ кардааст ва ба зудӣ Аллоҳ таоло ва паёмбараш рафторатонро хоҳанд дид [ки оё тавба мекунед ё бар нифоқ мемонед]. Он гоҳ ба сӯйи [Аллоҳ таолои] донои пинҳон ва ошкор бозгардонда мешавед ва Ӯ шуморо аз ончи мекардед, огоҳ мекунад»');
INSERT INTO "pioneers" VALUES(9,95,'9:95','[Эй муъминон, пас аз ҷанг] вақте ба сӯйи онон [ мунофиқон] бозгардед, бароятон ба Аллоҳ таоло савганд ёд мекунанд, то аз [гуноҳи] онон чашмпӯшӣ кунед. Пас, аз эшон рӯ бигардонед, [чаро ки] бе тардид, онон палиданд ва ба [сазои] ончи мекарданд, ҷойгоҳашон дузах аст');
INSERT INTO "pioneers" VALUES(9,96,'9:96','Бароятон савганд ёд мекунанд, то аз онон хушнуд шавед, [ҳатто] агар шумо аз он розӣ шавед, Аллоҳ таоло ҳаргиз аз гурӯҳи нофармон розӣ намегардад');
INSERT INTO "pioneers" VALUES(9,97,'9:97','Куфру нифоқи аъроб [-и бодиянишин аз дигарон] шадидтар аст ва сазовортаранд, ки ҳудуди ончиро, ки Аллоҳ таоло бар паёмбараш нозил кардааст, надонанд. Ва Аллоҳ таоло донову ҳаким аст');
INSERT INTO "pioneers" VALUES(9,98,'9:98','Ва бархе аз аъроб [-и бодиянишин] ҳастанд, ки ончиро [дар роҳи Аллоҳ таоло] харҷ мекунанд, хасорате [барои худ] медонанд ва бароятон интизори пешомадҳои бад доранд [то аз шумо раҳоӣ ёбанд]. Пешомади бад барои онон хоҳад буд ва Аллоҳ таоло шунавову доност');
INSERT INTO "pioneers" VALUES(9,99,'9:99','Ва бархе аз аъроб [-и бодиянишин] ҳастанд, ки ба Аллоҳ таоло ва рӯзи вопасин имон доранд ва ончиро, ки инфоқ мекунанд, боиси наздикӣ ба Аллоҳ таоло ва дуоҳои паёмбар [дар ҳаққи хеш] медонанд. Огоҳ бошед! [Ин инфоқ] Мояи наздикии онҳост. Аллоҳ таоло [дар қиёмат] ононро дар ҷивори раҳмати хеш дархоҳад овард. Бе тардид, Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(9,100,'9:100','Ва пешгомони нахустин аз муҳоҷирону ансор ва касоне, ки ба некӣ аз онон пайравӣ карданд, Аллоҳ таоло аз онон хушнуд гашт ва онон [низ] аз Ӯ хушнуд шуданд ва барояшон боғҳое [дар биҳишт] муҳайё кардааст, ки ҷуйборҳо аз зери [дарахтони] он ҷорӣ аст, ҷовидона дар он хоҳанд монд. Ин ҳамон [растагорӣ ва] комёбии бузург аст');
INSERT INTO "pioneers" VALUES(9,101,'9:101','Ва бархе аз аъроб [-и бодиянишини Мадина], ки перомуни шумо ҳастанд, [гурӯҳи] мунофиқанд ва аз аҳли Мадина [низ] гурӯҳе ба нифоқ хӯ гирифтаанд. [Эй паёмбар] Ту ононро намешиносӣ, [вале] мо ононро мешиносем. Ононро ду бор азоб хоҳем кард, сипас ба сӯйи азоби бузург [дар қиёмат] фиристода мешаванд');
INSERT INTO "pioneers" VALUES(9,102,'9:102','Ва гурӯҳи дигар ба гуноҳони худ [дар бораи фирор аз ҷиҳод] эътироф кардаанд. [Онон] Кори шоиста ва ношоистаро ба ҳам омехтаанд. Умед аст Аллоҳ таоло тавбаи эшонро бипазирад, [чароки] бе тардид, Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(9,103,'9:103','Аз амволи онон [тавбакорон] садақа [ва закот] бигир, то ба ин васила ононро [аз гуноҳ] пок гардонӣ ва дараҷоташонро боло бибарӣ ва барояшон дуо кун, [чароки] яқинан дуои ту оромиш барои ононаст. Ва Аллоҳ таоло шунавову доност');
INSERT INTO "pioneers" VALUES(9,104,'9:104','Оё намедонанд, ки Аллоҳ таоло аст, ки тавбаро аз бандагонаш мепазирад ва садақотро меситонад ва бе тардид, Аллоҳ таолост, ки тавбапазири меҳрубон аст');
INSERT INTO "pioneers" VALUES(9,105,'9:105','Ва [эй паёмбар, ба мутахаллифон аз ҷиҳод ва тавбакорон] бигӯ: «Ба амал кӯшед [ва ончиро, ки аз даст додаед, ҷуброн кунед]. Пас, Аллоҳ таоло ва паёмбараш ва муъминон корҳои шуморо хоҳанд дид ва ба зудӣ ба сӯйи [Аллоҳ таолои] донои пинҳону ошкор бозгардонда мешавед, сипас Ӯ [дар қиёмат] шуморо аз ончи анҷом медодед, огоҳ месозад»');
INSERT INTO "pioneers" VALUES(9,106,'9:106','Ва гурӯҳи дигар [аз мутахаллифони Табук ҳастанд, ки сарнавишташон] мавкул [вобасташуда] ба фармони Аллоҳ таоло аст: ё азобашон мекунад ва ё тавбаашонро мепазирад. Ва Аллоҳ таоло донову ҳаким аст');
INSERT INTO "pioneers" VALUES(9,107,'9:107','Ва касоне [аз мунофиқон ҳастанд], ки барои зиён расондан [ба мусулмонон] ва [тақвияти] куфру тафриқаафканӣ миёни муъминон масҷиде сохтанд, [то] камингоҳе бошад барои касоне, ки бо Аллоҳ таоло ва паёмбараш аз пеш дар ҷанг буданд ва, албатта, онон савганд ёд мекунанд, ки қасде ҷуз некӣ [ва хидмат] надоштаем. Ва Аллоҳ таоло гувоҳӣ медиҳад, ки онон, ҳатман, дурӯғгӯ ҳастанд');
INSERT INTO "pioneers" VALUES(9,108,'9:108','Эй паёмбар] Ҳаргиз дар он масҷид [ба намоз] наист, [зеро] шоистатар аст дар масҷиде [ба намоз] биистӣ, ки аз рӯзи нахуст бар пояи парҳезгорӣ бино шудааст. [Оре, масҷиди Қубост] Ки дар он, мардоне ҳастанд, ки дӯст медоранд, ки [аз гуноҳу олудагӣ] поку покиза гарданд. Ва Аллоҳ таоло покизагонро дӯст медорад');
INSERT INTO "pioneers" VALUES(9,109,'9:109','Оё касе, ки бунёни [кори] хешро бар пояи парҳезгорӣ ва хушнудии Аллоҳ таоло бино ниҳода,беҳтар аст ё касе, ки бунёни хешро бар лаби партгоҳи фурӯрехтание бино ниҳодааст ва бо он дар оташи дузах сарнагун мегардад? Ва Аллоҳ таоло гурӯҳи ситамгоронро [ба сабаби куфру нифоқашон] ҳидоят намекунад');
INSERT INTO "pioneers" VALUES(9,110,'9:110','Он биное, ки онон бунён ниҳодаанд, пайваста дар дилҳояшон мояи тардид [ва нифоқ] хоҳад буд, магар онки дилҳояшон пора-пора шавад [ва бимиранд]. Ва Аллоҳ таоло донову ҳаким аст');
INSERT INTO "pioneers" VALUES(9,111,'9:111','Бе тардид, Аллоҳ таоло аз муъминон ҷонҳо ва амволашонро ба [баҳои] инки биҳишт барои онон бошад, харидааст, ҳамон касоне, ки дар роҳи Аллоҳ таоло меҷанганд ва [кофиронро] мекушанд ва [ё худ ба дасти онҳо] кушта мешаванд. [Ин подош] ба унвони ваъдаи росту дуруст дар Таврот ва Инҷил ва Қуръон бар уҳдаи Ӯст. Ва кист, ки аз Аллоҳ таоло ба паймони хеш вафодортар аст? Пас, [эй муъминон] ба муомилае, ки бо Ӯ кардаед, шодмон бошед ва ин ҳамон [растагорӣ ва] комёбии бузург аст');
INSERT INTO "pioneers" VALUES(9,112,'9:112','[Онон, ки чунин подоше доранд, ҳамон] тавбакунандагон, парастишгарон, сипосгузорон, рӯзадорон, рукуъкунандагон, саҷдакунандагон, водорандагон ба корҳои писандида ва боздорандагон аз корҳои нописанд ва посдорони ҳудуд [ва аҳкоми] илоҳӣ ҳастанд. Ва он муъминонеро, [ки чунин ҳастанд, ба биҳишт] башорат бидеҳ');
INSERT INTO "pioneers" VALUES(9,113,'9:113','Барои паёмбар ва касоне, ки имон оварданд, сазовор нест, ки барои мушрикон, пас аз инки бар эшон ошкор шуд, ки онон аҳли дузаханд, омурзиш бихоҳанд, ҳарчанд аз наздикон бошанд');
INSERT INTO "pioneers" VALUES(9,114,'9:114','Омурзишхоҳии Иброҳим барои падараш [Озар] ҷуз ба хотири ваъдае, ки ба ӯ дода буд, сурат нагирифт, вале ҳангоме ки барояш ошкор шуд, ки вай душмани Аллоҳ таолост, аз ӯ безорӣ ҷуст [ва барояш дуо накард]. Ба ростӣ, ки Иброҳим, басе ниёишгар ва бурдбор буд');
INSERT INTO "pioneers" VALUES(9,115,'9:115','Ва Аллоҳ таоло ҳаргиз бар он нест, ки гурӯҳеро пас аз онки ҳидояташон кардааст, гумроҳ бигзорад. Магар онки чизеро, ки бояд [аз он] парҳез кунанд, барояшон равшан карда бошад [ва онон ба он амал накарда бошанд]. Бе тардид, Аллоҳ таоло ба ҳар чизе доност');
INSERT INTO "pioneers" VALUES(9,116,'9:116','Дар ҳақиқат, фармонравоии осмонҳо ва замин аз они Аллоҳ таолост. [Ӯст, ки] зинда мекунад ва мемиронад ва шумо ба ҷуз Аллоҳ таоло ҳеҷ корсозу ёваре надоред');
INSERT INTO "pioneers" VALUES(9,117,'9:117','Бе гумон, Аллоҳ таоло бар паёмбар ва муҳоҷирин ва ансор, ки дар ҳангоми душворӣ [ғазваи Табук] аз Ӯ пайравӣ карданд, раҳмат овард, баъд аз онки наздик буд дилҳои гурӯҳе аз онон билағжад [ва ба сабаби сахтиҳои фаровон ҷиҳодро тарк кунанд], пас, [боз] тавбаи ононро пазируфт. Бе тардид, Ӯ [нисбат] ба онон дилсӯз [ва] меҳрубон аст');
INSERT INTO "pioneers" VALUES(9,118,'9:118','Ва ба он се нафар, ки [аз ширкат дар ғазваи Табук] тахаллуф намуданд [ва пазириши тавбаашон ба таъхир афтод, низ лутфу эҳсон намуд],он гоҳ ки [мусулмонон аз онон, ки буриданд ва] замин боҳамаи фарохияш бар эшон танг шуд ва аз худ [низ] ба танг омаданд ва донистанд, ки аз Аллоҳ таоло ҷуз ба сӯйи худи Ӯ паноҳе нест. Пас, [Аллоҳ таоло бо бахшоиши хеш] ба онон бозгашт, то тавба кунанд [ва аз эшон даргузашт]. Яқинан, Аллоҳ таоло аст, ки тавбапазири меҳрубон аст');
INSERT INTO "pioneers" VALUES(9,119,'9:119','Эй касоне, ки имон овардаед, аз Аллоҳ таоло парво кунед ва бо ростгӯён бошед');
INSERT INTO "pioneers" VALUES(9,120,'9:120','Сазовор нест, ки аҳли Мадина ва аъроб [-и бодиянишин] перомуни эшон аз [ҳамроҳӣ бо] Расулуллоҳ бозмонанд ва ҳифзи ҷони хешро муқаддам бар ӯ шуморанд, зеро дар роҳи Аллоҳ таоло ҳеҷ ташнагӣ ва ранҷу гуруснагие ба онон намерасад ва дар ҳеҷ маконе, ки кофиронро ба хашм меоварад, қадам намегузоранд ва аз душман ҳеҷ [шикаст ва] зарбае намехӯранд, магар инки ба [подоши] он кори шоиста барояшон сабт мешавад. Баростӣ, ки Аллоҳ таоло подоши накукоронро зоеъ намегардонад');
INSERT INTO "pioneers" VALUES(9,121,'9:121','Ва ҳеҷ моли кучак ва бузургеро [дар роҳи Аллоҳ таоло] инфоқ намекунанд ва [дар роҳи ҷиҳод] аз ҳеҷ сарзамине намегузаранд, магар инки [подошаш] бар эшон сабт мешавад, то Аллоҳ таоло подоши накутарин кирдорашонро ба онон бидиҳад');
INSERT INTO "pioneers" VALUES(9,122,'9:122','Ва сазовор нест, ки муъминон ҳамагӣ раҳсипор [-и ҷиҳод] шаванд. Пас, чаро аз ҳар гурӯҳе аз онон иддае раҳсипор намешаванд [ва иддае низ ҳамроҳи паёмбар намемонанд], то [ба сабаби ончи аз маҳзараш фаро мегиранд] дар ин огоҳӣ биёбанд ва қавми худро вақте ба сӯяшон бозгаштанд, бим диҳанд? Бошад, ки онон [аз кайфари илоҳӣ битарсанд ва] бипарҳезанд');
INSERT INTO "pioneers" VALUES(9,123,'9:123','Эй касоне, ки имон овардаед, бо кофироне, ки наздики [маҳалли зиндагии] шумо ҳастанд, биҷангед [то хатарашонро рафъ намоед] ва онон бояд дар шумо шиддату хушунат биёбанд ва бидонед, ки Аллоҳ таоло бо парҳезгорон аст');
INSERT INTO "pioneers" VALUES(9,124,'9:124','Ва ҳангоме ки сурае нозил мегардад, бархе аз онон [ба дигаре] мегӯянд: « Ин сура ба имони кадом яке аз шумо афзуд»? Аммо касоне, ки имон овардаанд, бар имонашон меафзояд ва [ба он] шодмон мегарданд');
INSERT INTO "pioneers" VALUES(9,125,'9:125','Ва аммо касоне, ки дар дилҳояшон беморӣ [шакку нифоқе] аст, пас, палидӣ бар палидияшон меафзояд ва дар ҳоле ки кофиранд, мемиранд');
INSERT INTO "pioneers" VALUES(9,126,'9:126','Оё онон намебинанд, ки ҳар сол як ё ду бор озмоиш мешаванд? Аммо на тавба мекунанд ва на панд мепазиранд');
INSERT INTO "pioneers" VALUES(9,127,'9:127','Ва ҳангоме ки сурае нозил мешавад [ки аҳволи мунофиқонро баён намудааст], баъзе аз онон ба баъзи дигар нигоҳ мекунанд [ва мегӯянд]: «Оё касе шуморо мебинад»? Сипас [махфиёна аз он маҷлис] бозмегарданд. Аллоҳ таоло дилҳояшонро [аз хайру ҳидоят] бозгардондааст, зеро онон гурӯҳе ҳастанд, ки намефаҳманд');
INSERT INTO "pioneers" VALUES(9,128,'9:128','Яқинан, паёмбаре аз [миёни] худи шумо ба сӯятон омад, ки ранҷдиданатон бар ӯ [гарону] душвор аст ва бар [ҳидояти] шумо сахт исрор дорад ва [нисбат] ба муъминон дилсӯз [ва] меҳрубон аст');
INSERT INTO "pioneers" VALUES(9,129,'9:129','Пас, [эй паёмбар] агар [мушрикон ва мунофиқон аз ҳақ рӯйгардон шуданд, бигӯ: «Аллоҳ таоло барои ман кофист. Ҳеҷ маъбуде [барҳақ] ҷуз Ӯ нест, танҳо бар Ӯ таваккал кардам ва Ӯ Парвардигори Арши бузург аст»');
INSERT INTO "pioneers" VALUES(10,1,'10:1','Алиф, лом, ро. Ин оёти китоби пурҳикмат аст');
INSERT INTO "pioneers" VALUES(10,2,'10:2','Оё барои мардум муҷиби шигифтист, ки ба марде аз худашон ваҳй кардаем, ки: «Ба мардум [дар бораи азоби охират] ҳушдор бидеҳ ва ба касоне, ки имон овардаанд, башорат бидеҳ, ки барои онон назди Парвардигорашон подоши накуст»? Кофирон гуфтанд: «Ин [мард], ҳатман, ҷодугари ошкор аст»');
INSERT INTO "pioneers" VALUES(10,3,'10:3','Бе гумон, Парвардигори шумост, ки осмонҳо ва заминро дар шаш рӯз офарид, сипас бар Арш қарор гирифт. Ӯ кор [-и ҷаҳон]-ро тадбир мекунад, [ва] ҳеҷ шафоатгаре нест, магар пас аз иҷозати Ӯ. Ин Аллоҳ таолост-Парвардигоратон, пас, Ӯро бипарастед. Оё панд намегиред?');
INSERT INTO "pioneers" VALUES(10,4,'10:4','Бозгашти ҳамаи шумо ба сӯйи Ӯст. Ваъдаи Аллоҳ таоло [дар мавриди қиёмат] ҳақ аст. Ӯст, ки офаринишро оғоз мекунад, он гоҳ [пас аз марг] онро бозмегардонад,то касонеро, ки имон овардаанд ва корҳои шоиста кардаанд, ба адолат подош диҳад, касоне, ки куфр варзиданд, ба сазои онки кофир буданд, шарбате аз оби ҷӯшон ва азоби дарднок [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(10,5,'10:5','Ӯст, ки хуршедро дурахшон намуд ва моҳро тобон кард ва барояш ҷойгоҳҳо [-и мушаххасе] қарор дод то [бо тағйир ва вазъи моҳ] шумориши солҳо ва ҳисоб [-и зиндагии худ]-ро бидонед. Аллоҳ таоло инҳоро ҷуз ба ҳақ наофаридааст. Ӯ нишона[-ҳои худ]-ро барои гурӯҳе, ки медонанд, ба тафсил баён мекунад');
INSERT INTO "pioneers" VALUES(10,6,'10:6','Бе гумон, дар омадушуди шабу рӯз ва ончи Аллоҳ таоло дар осмонҳо ва замин офаридааст, барои касоне, ки парво мекунанд, нишонаҳо[-и қудрати илоҳӣ] аст');
INSERT INTO "pioneers" VALUES(10,7,'10:7','Онон, ки ба дидори Мо умед [ва бовар] надоранд ва ба зиндагонии дунё хушнуданд ва ба он дилбастаанд ва касоне, ки аз оёти Мо ғофиланд');
INSERT INTO "pioneers" VALUES(10,8,'10:8','Инон ба сазои ончи мекарданд, ҷойгоҳашон оташи [дузах] аст');
INSERT INTO "pioneers" VALUES(10,9,'10:9','Бе тардид, касоне, ки имон овардаанд ва корҳои шоиста кардаанд, Парвардигорашон ононро ба поси имонашон ба боғҳо [-и биҳишти пур] неъмат ҳидоят мекунад, ки ҷуйборҳо аз зери [дарахтони] он ҷорӣ аст');
INSERT INTO "pioneers" VALUES(10,10,'10:10','Дуояшон дар онҷо [ин аст]: «Илоҳо, Ту поку муназзаҳӣ» ва дурудашон [дар онҷо]: «Салом» аст ва поёни дуояшон ин аст, ки: «Ҳамду сипос, махсуси Аллоҳ таоло – Парвардигори ҷаҳониён аст»');
INSERT INTO "pioneers" VALUES(10,11,'10:11','Ва агар [Аллоҳ таоло] барои мардум, бадиро бо ҳамон шитоб мехост, ки онон некиро мехоҳанд [ва нафринҳое, ки дар ҳаққи худ ва амволу фарзандонашон мекунанд, мустаҷоб мекард], ҳатман, аҷалашон фаро расида буд. Пас, касонеро ки ба дидори Мо умед [-у бовар] надоранд, вомегузорем, то дар [гумроҳӣ ва] саркашияшон саргардон бимонанд');
INSERT INTO "pioneers" VALUES(10,12,'10:12','Ва чун ба инсон газанде мерасад, [дар ҳар ҳоле, ки бошад] ба паҳлу хобида ё нишаста ё истода Моро мехонад. Пас, ҳамин ки он газандро аз вай бартараф кардем, чунон меравад ки гӯӣ ҳаргиз Моро барои дафъи газанде, ки ба ӯ расида буд, нахондааст. Баҳамин сурат, барои исрофкорон [низ] ончи мекарданд, [дар назарашон] ороста шудааст');
INSERT INTO "pioneers" VALUES(10,13,'10:13','Ва [эй мушрикон] бе тардид, мо умматҳои пеш аз шуморо, ҳангоме ки ситам карданд, ҳалок кардем, дар ҳоле ки паёмбаронашон бо далели равшан ба сӯяшон омаданд ва [-ле] онон бар он набуданд, ки имон оваранд. Мо гурӯҳи муҷримонро ин гуна кайфар медиҳем');
INSERT INTO "pioneers" VALUES(10,14,'10:14','Сипас баъд аз онон шумо [мардум]-ро ҷонишинони онҳо [дар] замин қарор додем, то бингарем, ки чигуна рафтор мекунед');
INSERT INTO "pioneers" VALUES(10,15,'10:15','Ва ҳангоме ки оёти равшани Мо бар онон хонда мешавад, касоне, ки ба дидори Мо [дар қиёмат] умед надоранд, мегӯянд: «[Эй Муҳаммад] Қуръоне ғайр аз ин биёвар [ки мутобиқи майли мо бошад], ё онро тағйир бидеҳ». [Ту, эй паёмбар, ба онон ] бигӯ: «Ман ҳақ надорам, ки онро аз пеши худ тағйир диҳам ва пайравӣ намекунам магар аз чизе, ки бар ман ваҳй мешавад ва агар аз Парвардигорам нофармонӣ кунам, аз азоби рӯзи бузург метарсам»');
INSERT INTO "pioneers" VALUES(10,16,'10:16','Бигӯ: «Агар Аллоҳ таоло мехост, на ман онро бар шумо мехондам ва на Ӯ [Аллоҳ таоло] шуморо аз он огоҳ мекард. Ба ростӣ, ки пеш аз ин [даъват] умреро дар миёни шумо гузарондаам. Оё намеандешед»?');
INSERT INTO "pioneers" VALUES(10,17,'10:17','Пас, кист ситамгортар аз онки бар Аллоҳ таоло дурӯғ мебандад ё оёташро дурӯғ меангорад? Бе гумон, муҷримон растагор нахоҳанд шуд');
INSERT INTO "pioneers" VALUES(10,18,'10:18','Ва [мушрикон] ба ҷойи Аллоҳ таоло чизҳоеро мепарастанд, ки на ба онон зиёне мерасонанд ва на судашон мебахшад ва мегӯянд: «Ин бутҳо [воситаҳо ва] шафоатгарони мо назди Аллоҳ таоло ҳастанд». [Эй паёмбар] Бигӯ: «Оё [ба гумони худ] Аллоҳ таолоро аз чизе [шарике] огоҳ месозед, ки дар осмонҳо ва замин [барояш қоилед ва худ] намедонад?» Ӯ муназзаҳ аст ва аз ончи бо вай шарик месозанд, бартар аст');
INSERT INTO "pioneers" VALUES(10,19,'10:19','[Дар оғоз] Мардум ҷуз уммати воҳид [ва яктопараст] набуданд, сипас ихтилоф карданд.Ва агар пештар [дар мавриди муҳлат ба гунаҳгорон ва ҳисобрасӣ дар қиёмат] сухане аз ҷониби Парвардигорат баён нашуда буд, яқинан, [дар ҳамин дунё] дар бораи ончи бо ҳам ихтилоф мекунанд, миёнашон доварӣ мешуд');
INSERT INTO "pioneers" VALUES(10,20,'10:20','Ва [мушрикон] мегӯянд: «Чаро [муъҷиза ва] нишонае аз [ҷониби] Парвардигораш бар ӯ нозил нашудааст»? [Эй паёмбар] Бигӯ: «[Огоҳӣ аз] Ғайб танҳо аз они Аллоҳ таолост, пас, интизор бикашед, ки ман [низ] бо шумо аз мунтазиронам»');
INSERT INTO "pioneers" VALUES(10,21,'10:21','Ва ҳангоме ки ба мардум пас аз [зиён ва] ранҷе, ки ба онон расидааст, [неъмату] раҳмате бичашонем, он гоҳ дар оёти Мо [тамасхуру] найранг мекунанд. Бигӯ: «Аллоҳ таоло дар найранг задан сареътар аст». Бе тардид, фиристодагон [фариштагон]-и Мо ҳарчи найранг мекунед, менависанд');
INSERT INTO "pioneers" VALUES(10,22,'10:22','[Эй мардум] Ӯст, ки шуморо дар хушкӣ ва дарё ба ҳаракат дармеоварад, то он гоҳ ки дар [дарё бар] киштӣ қарор мегиред ва [киштиҳо] бо боди дилпазир ононро [ба сӯйи мақсад] ҳаракат медиҳанд ва [сарнишинон] ба он шодмон мешаванд. [Ногаҳон] Тӯфони шадиде мевазад ва мавҷ аз ҳар тараф [басӯяшон] меояд ва гумон мекунанд, ки роҳи гурезе надоранд [ва ҳалок хоҳанд шуд, пас] Аллоҳ таолоро аз рӯйи ихлос мехонанд, ки: «Агар моро аз ин [гирифторӣ] наҷот диҳӣ, ҳатман, аз сипосгузорон хоҳем шуд»');
INSERT INTO "pioneers" VALUES(10,23,'10:23','Ва чун [Аллоҳ таоло] наҷоташон медиҳад, ба ноҳақ дар замин саркашӣ [ва ситам] мекунанд. Эй мардум, саркашӣ [ва ситами] шумо танҳо ба зиёни худатон аст, [чаро ки фақат] баҳраи [ночизе аз] зиндагии дунё [мебаред ва] сипас бозгаштатон ба сӯйи Мо хоҳад буд,он гоҳ шуморо аз ончи мекардед, бохабар месозем');
INSERT INTO "pioneers" VALUES(10,24,'10:24','Ҷуз ин нест, ки мисоли зиндагии дунё ҳамчун обест, ки аз осмон фурӯ рехтем, пас, гиёҳони [гуногуни] замин аз ончи мардум ва чорпоён мехӯранд, бо он даромехт [ва рӯйид] то он гоҳ ки замин пирояи худро [бо гулҳо ва гиёҳон] баргирифт ва ороста шуд ва сокинонаш пиндоштанд, ки онон бар он тавоноие доранд [ки аз маҳсулоташ баҳраманд гарданд, вале ногаҳон] шабе ё рӯзе фармони [вайронии] Мо омад ва он [кишту мева]-ро чунон дарав кардем, ки гӯӣ дирӯз ҳеҷ набудааст. Мо нишонаҳо[-и худ]-ро барои мардуме, ки меандешанд, инчунин ба равшанӣ баён мекунем');
INSERT INTO "pioneers" VALUES(10,25,'10:25','Ва Аллоҳ таоло [шуморо] ба сарои саломат [биҳишт] фаро мехонад ва ҳаркиро бихоҳад, ба роҳи рост ҳидоят мекунад');
INSERT INTO "pioneers" VALUES(10,26,'10:26','Барои касоне, ки некӣ кардаанд, подоши нек [биҳишт] аст ва афзун бар он [руъяти Бори таолост] ва тирагию хорӣ чеҳраҳояшонро нахоҳад пӯшонд. Инон аҳли биҳиштанд ва ҷовидона дар он хоҳанд монд');
INSERT INTO "pioneers" VALUES(10,27,'10:27','Ва касоне, ки муртакиби бадиҳо шуданд, [бидонанд] сазои [ҳар] бадӣ ҳаммонанди он аст ва хорӣ [чеҳраҳои] ононро мепӯшонад ва дар баробари [азоби] Аллоҳ таоло ҳеҷ [монеъ ва] муҳофизе надоранд. [Чунон мешаванд, ки] гӯӣ чеҳраҳояшон бо порае аз шаби торик пӯшида шудааст. Инон аҳли оташанд ва ҷовидона дар он хоҳанд монд');
INSERT INTO "pioneers" VALUES(10,28,'10:28','[Ва эй паёмбар, ёд кун аз] рӯзе, ки ҳамаи ононро гирд меоварем,он гоҳ ба касоне, ки ширкат варзидаанд, мегӯем: «Шумо ва шариконатон [маъбудонатон] бар ҷойи худ бимонед». Сипас миёни онҳо ҷудоӣ меафканем ва шариконашон мегӯянд: «Дар ҳақиқат, шумо моро намепарастидед');
INSERT INTO "pioneers" VALUES(10,29,'10:29','Ва кофист, ки Аллоҳ таоло миёни мо ва шумо гувоҳӣ диҳад, [ки мо ба ширкварзии шумо розӣ набудем ва] аз парастиши шумо комилан бехабар будем»');
INSERT INTO "pioneers" VALUES(10,30,'10:30','Онҷост, ки ҳар касе ончиро аз пеш фиристодааст, меозмояд ва ҳамагӣ ба сӯйи Аллоҳ таоло – корсози ҳақиқияшон бозгардонда мешаванд ва дурӯғҳое, ки [дар бораи шафоати бутҳо] мебофтанд, ҳама маҳву нобуд мегардад');
INSERT INTO "pioneers" VALUES(10,31,'10:31','[Эй паёмбар, ба мушрикон] Бигӯ: «Кист, ки аз осмону замин ба шумо рӯзӣ мебахшад? Ё кист, ки бар гӯшҳо ва дидагон ҳоким аст? Ва кист, ки [мавҷуди] зиндаро аз [модаи] беҷон берун меоварад ва [модаи] беҷони мурдаро аз [мавҷуди] зинда хориҷ месозад? Ва кист, ки кори [ҷаҳону ҷаҳониён]-ро тадбир мекунад»? Хоҳанд гуфт: «Аллоҳ таоло». Пас, бигӯ: «Оё парво намекунед»?');
INSERT INTO "pioneers" VALUES(10,32,'10:32','Ва пас аз ҳақиқат ҷуз гумроҳӣ чист? Пас, чигуна [аз ҳақ] бозгардонда мешавед?');
INSERT INTO "pioneers" VALUES(10,33,'10:33','Ин гуна сухани Парвардигорат дар бораи касоне, ки [аз фармони Ӯ] нофармонӣ карданд, ба ҳақиқат пайваст, ки: «Онон имон намеоваранд»');
INSERT INTO "pioneers" VALUES(10,34,'10:34','[Эй паёмбар, ба мушрикон] Бигӯ: «Оё аз маъбудонатон [бутҳо] касе ҳаст, ки офаринишро оғоз кунад ва сипас онро [пас аз марг] бозгардонад?» Бигӯ: «Аллоҳ таоло аст, ки офаринишро оғоз мекунад ва сипас онро бозмегардонад, пас, ба куҷо мунҳариф мешавед?»');
INSERT INTO "pioneers" VALUES(10,35,'10:35','Бигӯ: «Оё аз маъбудонатон касе ҳаст, ки ба сӯйи ҳақ раҳбарӣ кунад?». Бигӯ: «[Аллоҳ таоло] аст, ки ба сӯйи ҳақ раҳбарӣ мекунад. Пас, оё касе, ки ба сӯйи ҳақ раҳбарӣ мекунад, сазовортар аст, ки пайравӣ шавад ё касе, ки худ роҳ намеёбад, магар онки ҳидояташ кунанд? Пас, шуморо чи шудааст? Чигуна доварӣ мекунед?»');
INSERT INTO "pioneers" VALUES(10,36,'10:36','Ва бештарашон ҷуз аз гумон пайравӣ намекунанд, [дар ҳоле ки] гумон ҳаргиз [инсонро] аз [шинохти] ҳақ бениёз намекунад. Бе тардид, Аллоҳ таоло ба ончи мекунанд, доност');
INSERT INTO "pioneers" VALUES(10,37,'10:37','Ва нашояд, ки ин Қуръон аз ҷониби ғайри Аллоҳ таоло [ва] ба дурӯғ сохта шуда бошад, балки тасдиқи китобҳоест, ки пеш аз он омадааст ва шарҳи [аҳкоми] онҳост ва тардид дар он нест [ва] аз [ҷониби] Парвардигори ҷаҳониён [нозил шуда] аст');
INSERT INTO "pioneers" VALUES(10,38,'10:38','Ё мегӯянд: «[Муҳаммад] Он [суханон]-ро ба дурӯғ [ба Аллоҳ таоло] нисбат додааст»? Бигӯ: «Агар рост мегӯед, пас, як сура ҳаммонанди он биёваред ва ба ҷуз Аллоҳ таоло ҳаркиро метавонед, [ба ёрӣ] бихонед»');
INSERT INTO "pioneers" VALUES(10,39,'10:39','[Чунин нест] Балки чизеро дурӯғ шумурданд, ки ба шинохташ иҳота надоштанд ва ҳанӯз воқеияти он [ва ҳақиқати ахбораш] бар эшон равшан нашудааст. Касоне, ки пеш аз онон буданд, низ [оёти Қуръонро] ҳамингуна дурӯғ ангоштанд. Пас, [эй паёмбар] бингар, ки оқибати ситамгорон чигуна буд');
INSERT INTO "pioneers" VALUES(10,40,'10:40','Ва аз онон [мушрикон] касе ҳаст, ки ба он имон меоварад ва аз онон касе ҳаст, ки [ҳаргиз] ба он имон намеоварад ва Парвардигорат ба [ҳоли] муфсидон огоҳтар аст');
INSERT INTO "pioneers" VALUES(10,41,'10:41','Ва агар [кофирон] туро дурӯғгӯ ангоштанд, пас, бигӯ: «[Натиҷаи] амали ман барои худам ва [сазои] амали шумо барои худатон аст. Шумо аз ончи ман мекунам, безоред ва ман [низ] аз ончи [шумо] мекунед безорам»');
INSERT INTO "pioneers" VALUES(10,42,'10:42','Ва бархе аз онҳо [дар зоҳир] ба ту гӯш фаро медиҳанд, аммо магар ту метавонӣ ношунавоёнро [ки аз шунидани каломи ҳақ оҷизанд] шунаво кунӣ, ҳарчанд хирадварзӣ накунанд?');
INSERT INTO "pioneers" VALUES(10,43,'10:43','Ва аз эшон касоне ҳастанд, ки [бо чашми зоҳирӣ] туро менигаранд, аммо оё ту метавонӣ нобиноёнро ҳидоят кунӣ, ҳарчанд аҳли басират набошанд?');
INSERT INTO "pioneers" VALUES(10,44,'10:44','Яқинан, Аллоҳ таоло ба мардум ҳеҷ ситаме намекунад, вале мардум [ҳастанд, ки бо ширку нофармонӣ] ба хештан ситам мекунанд');
INSERT INTO "pioneers" VALUES(10,45,'10:45','Ва рӯзе, ки [Аллоҳ таоло] ононро [дар пешгоҳи хеш] гирд меоварад, [чунон эҳсос мекунанд, ки] гӯӣ ҷуз соате аз рӯз [дар дунё] диранг накардаанд [ва дар онҷо] якдигарро мешиносанд. Бе тардид, касоне, ки дидори Аллоҳ таолоро дурӯғ меангоштанд, зиён диданд ва ҳидоят наёфтанд');
INSERT INTO "pioneers" VALUES(10,46,'10:46','Ва [эй паёмбар] агар бахше аз ончи [аз азоб]-ро, ки ба онон ваъда додаем, нишонат диҳем ё ҷони туро [пеш аз азоби онон] бигирем, [дар ҳар ҳол] бозгаштатон ба сӯйи Мост, он гоҳ Аллоҳ таоло бар ончи мекунанд, гувоҳ аст');
INSERT INTO "pioneers" VALUES(10,47,'10:47','Ҳар уммате паёмбаре дорад, пас, ҳангоме ки паёмбарашон омад [бархе муъмин ва бархе кофир шуданд; ва рӯзи қиёмат] миёни онон ба адолат доварӣ мешавад ва [ҳаргиз] ба эшон ситам нахоҳад шуд');
INSERT INTO "pioneers" VALUES(10,48,'10:48','[Мушрикон ба паёмбар] мегӯянд: «Агар [ту ва пайравонат] рост мегӯед, ин ваъдаи [азоб] кай хоҳад буд»?');
INSERT INTO "pioneers" VALUES(10,49,'10:49','Бигӯ: «Ман барои хеш молики ҳеҷ зиёну суде нестам, магар ончи Аллоҳ таоло бихоҳад. Ҳар уммате аҷале дорад ва он гоҳ ки аҷалашон ба сар ояд, на соате [аз он] таъхир мекунанд ва на пешӣ мегиранд»');
INSERT INTO "pioneers" VALUES(10,50,'10:50','[Эй паёмбар, ба кофирон] бигӯ: «Ба ман бигӯед агар азоби Ӯ шабона ё дар [вақти] рӯз бар шумо нозил гардад, [чи мекунед ва] гунаҳкорон чи чизеро аз он [азоб] ба шитоб мехоҳанд?»');
INSERT INTO "pioneers" VALUES(10,51,'10:51','Оё пас аз онки [азоб] воқеъ шуд, бовараш мекунед? Оё акнун? Ва ҳол онки [пештар] онро ба шитоб мехостед');
INSERT INTO "pioneers" VALUES(10,52,'10:52','Сипас ба касоне, ки ситам карданд, гуфта мешавад: «Азоби ҷовидонаро бичашед. Оё чизе дар баробари ончи мекардед муҷозот мешавед?»');
INSERT INTO "pioneers" VALUES(10,53,'10:53','Ва [эй паёмбар, мушрикон] Аз ту хабар мегиранд [ки]: «Оё он [азоб] ҳақиқат дорад»? Бигӯ: «Оре, савганд ба Парвардигорам,ки ҳатман, ҳақиқат дорад ва шумо наметавонед [Аллоҳ таолоро аз вуқуи маод ва азоб] дармонда кунед»');
INSERT INTO "pioneers" VALUES(10,54,'10:54','Ва ҳар ки [бо иртикоби ширку гуноҳ] ситам кардааст, агар ончи дар замин аст, [дар ихтиёр] дошта бошад, ҳатман, онро барои бозхариди [худ аз азоб] худ медиҳад ва [мушрикон] вақте азобро бубинанд, пушаймонии худро пинҳон мекунанд ва миёнашон ба адолат доварӣ мешавад ва бар эшон ситам намеравад');
INSERT INTO "pioneers" VALUES(10,55,'10:55','Огоҳ бошед! Дар ҳақиқат ончи дар осмонҳо ва замин аст, [ҳама] аз они Аллоҳ таолост. Огоҳ бошед! Бе тардид, ваъдаи Аллоҳ таоло [дар мавриди кайфари кофирон] рост аст, вале бештарашон намедонанд');
INSERT INTO "pioneers" VALUES(10,56,'10:56','Ӯст, ки зинда мекунад ва мемиронад ва [ҳамагӣ] ба сӯйи Ӯ бозгардонда мешавед');
INSERT INTO "pioneers" VALUES(10,57,'10:57','Эй мардум, баростӣ, барои шумо аз ҷониби Парвардигоратон мавъизае омадааст ва шифое барои ончи дар дилҳост ва ҳидояту раҳмате барои муъминон');
INSERT INTO "pioneers" VALUES(10,58,'10:58','Бигӯ: «[Муъминон] Бояд ба бахшишу раҳмати Аллоҳ таоло шодмон шаванд, ки ин [неъмат] аз ончи меандузанд, беҳтар аст»');
INSERT INTO "pioneers" VALUES(10,59,'10:59','Бигӯ: «Ба ман хабар диҳед, ки ончи аз [неъмат ва] рӯзӣ, ки Аллоҳ таоло бароятон фиристодааст, [чаро] бахше аз онро ҳаром ва [бахши дигарро] ҳалол талаққӣ кардаед»? Бигӯ: «Оё Аллоҳ таоло бар шумо чунин дастур додааст ё бар Аллоҳ таоло дурӯғ мебандед?»');
INSERT INTO "pioneers" VALUES(10,60,'10:60','Ва касоне, ки бар Аллоҳ таоло дурӯғ мебанданд, [дар бораи] рӯзи қиёмат чи мепиндоранд? Бе гумон, Аллоҳ таоло бар [ҳамаи] мардум фазл [-у бахшиш] дорад, вале бештарашон сипос намегузоранд');
INSERT INTO "pioneers" VALUES(10,61,'10:61','Ва [эй паёмбар] ту машғули ҳеҷ коре нестӣ ва ҳеҷ [ояте аз] онро намехонӣ ва [шумо низ эй мардум] ҳеҷ коре намекунед, магар инки он гоҳ ки ба он мепардозед, Мо бар [корҳои] шумо гувоҳем ва ҳамвазни заррае – [на] дар замин ва на дар осмон – аз Парвардигорат пинҳон нест ва [ҳатто] кучактар ё бузургтар аз он [низ] чизе нест, магар инки бар китоби равшан [сабт шуда] аст');
INSERT INTO "pioneers" VALUES(10,62,'10:62','Огоҳ бошед! Дӯстони Аллоҳ таоло на тарсе хоҳанд дошт ва на андуҳгин мешаванд');
INSERT INTO "pioneers" VALUES(10,63,'10:63','[Ҳамон] Касоне, ки имон оварданд ва парҳезгорӣ мекарданд');
INSERT INTO "pioneers" VALUES(10,64,'10:64','Барои онон дар зиндагии дунё ва дар охират башорат аст. Суханони Аллоҳ таоло [дар бораи пирӯзӣ ва подоши ду ҷаҳон] тағйирнопазир аст. Ин ҳамон [растагорӣ ва] комёбии бузург аст');
INSERT INTO "pioneers" VALUES(10,65,'10:65','[Эй паёмбар] Сухани онон туро ғамгин накунад. Бе тардид, тамоми иззат [-у пирӯзмандӣ] аз они Аллоҳ таолост [ва] Ӯ шунавову доност');
INSERT INTO "pioneers" VALUES(10,66,'10:66','Огоҳ бошед! Ҳар ки [ва ҳар чи] дар осмонҳо ва замин аст, аз они Аллоҳ таоло аст, пас,касоне, ки ба ҷойи Аллоҳ таоло шариконеро мехонанд, аз чи пайравӣ мекунанд? Инон ҷуз аз [тардиду] гумон пайравӣ намекунанд ва ҷуз дурӯғ намегӯянд');
INSERT INTO "pioneers" VALUES(10,67,'10:67','Ӯст, ки шабро бароятон падид овард, то дар он биоромед ва рӯзро равшан сохт [то дар партави нураш ба кор бипардозед]. Бе тардид, дар инҳо барои гурӯҳе, ки [суханони ҳақро] мешунаванд, нишонаҳост');
INSERT INTO "pioneers" VALUES(10,68,'10:68','[Мушрикон] Гуфтанд: «Аллоҳ таоло фарзанде барои худ ихтиёр кардааст». Муназзаҳ аст Ӯ [ва] бениёз аст. Ончи дар осмонҳо ва замин аст, аз они Ӯст. [Эй мушрикон] Шумо бар ин [иддао] далеле надоред. Оё чизеро, ки намедонед, ба дурӯғ ба Аллоҳ таоло нисбат медиҳед?');
INSERT INTO "pioneers" VALUES(10,69,'10:69','Бигӯ: «Касоне, ки ба Аллоҳ таоло нисбати дурӯғ медиҳанд, ҳаргиз растагор намешаванд»');
INSERT INTO "pioneers" VALUES(10,70,'10:70','Онон баҳра [-и андак] дар дунё [доранд], сипас бозгашташон ба сӯи Мост. Он гоҳ ба [сазои] онки куфр меварзиданд, азоби сахт ба онон мечашонем');
INSERT INTO "pioneers" VALUES(10,71,'10:71','[Эй паёмбар] Достони Нуҳро бар онон бозгӯ, он гоҳ ки бар қавмаш гуфт: «Эй қавми ман, агар монанди ман [дар миёни шумо] ва андарз додан ба оёти Аллоҳ таоло бар шумо гарон омадааст, [бидонед, ки ман] бар Аллоҳ таоло таваккал кардааам, пас, [дар тавтеа ва] коратон бо шарикони худ [алайҳи ман] ҳамдаст шавед ва дар коратон пардапӯшӣ накунед,он гоҳ [ҳар чи дар дил доред ва метавонед] алайҳи ман ба кор бандед ва муҳлатам надиҳед');
INSERT INTO "pioneers" VALUES(10,72,'10:72','Агар [аз даъвати ҳақ] рӯйгардон шудед, пас, [бидонед, ки] ман подоше аз шумо нахостаам, [чаро ки] подошам танҳо бар Аллоҳ таоло аст ва ман дастур ёфтаам, ки аз таслимшудагон [-и авомири илоҳӣ] бошам»');
INSERT INTO "pioneers" VALUES(10,73,'10:73','Пас, [қавмаш] ӯро дурӯғгӯ ангоштанд ва Мо ӯ ва касонеро, ки дар киштӣ ҳамроҳаш буданд, наҷот додем ва ононро ҷонишин [-и пешиниён] сохтем ва касонеро, ки оёти Моро дурӯғ меангоштанд, ғарқ кардем. Пас, бингар, ки саранҷоми ҳушдорёфтагон чигуна буд');
INSERT INTO "pioneers" VALUES(10,74,'10:74','Сипас баъд аз ӯ паёмбароне ба сӯйи қавмаш фиристодем ва муъҷизоти равшан [бар эшон] оварданд, вале онҳо бар он набуданд, ки ба чизе имон биёваранд, ки аз пеш онро дурӯғ шумурда буданд. Бад-ин сон, бар дилҳои таҷовузкорон [аз ҳудуди илоҳӣ] муҳр мениҳем');
INSERT INTO "pioneers" VALUES(10,75,'10:75','Сипас баъд аз эшон Мӯсо ва Ҳорунро бо муъҷизоти хеш ба сӯйи Фиръавн ва бузургони қавмаш фиристодем, вале онон гарданкашӣ карданд ва қавме гунаҳкор буданд');
INSERT INTO "pioneers" VALUES(10,76,'10:76','Пас, чун ҳақ аз назди мо ба сӯяшон омад, гуфтанд: «Ин, ҳатман, ҷодуи ошкор аст»');
INSERT INTO "pioneers" VALUES(10,77,'10:77','Мӯсо гуфт: «Оё вақте ҳақ ба сӯятон омад, мегӯед [ҷодуст]? Оё ин ҷодуст? Ва ҳол онки ҷодугарон растагор намешаванд»');
INSERT INTO "pioneers" VALUES(10,78,'10:78','[Фиръавниён] Гуфтанд: «Оё ба сӯйи мо омадаӣ, то моро аз шевае, ки падаронамонро бар он ёфтаем, бозгардонӣ ва [иззату] бузургӣ дар ин сарзамин барои шумо дутан бошад? Мо ҳаргиз ба шумо ду тан имон намеоварем»');
INSERT INTO "pioneers" VALUES(10,79,'10:79','Ва Фиръавн гуфт: «Тамоми ҷодугарони [моҳиру] доноро назди ман биёваред»');
INSERT INTO "pioneers" VALUES(10,80,'10:80','Пас, чун ҷодугарон омаданд, Мӯсо ба онон гуфт: «Ончи [аз васоили ҷоду] мехоҳед, биафканед, [акнун] биафканед»');
INSERT INTO "pioneers" VALUES(10,81,'10:81','Пас, чун афканданд, Мӯсо гуфт: «Ончи овардаед, ҷодуст. Яқинан, Аллоҳ таоло онро ботил хоҳад кард. Бе тардид, Аллоҳ таоло кори муфсидонро ислоҳ намекунад');
INSERT INTO "pioneers" VALUES(10,82,'10:82','Ва Аллоҳ таоло бо суханони хеш ҳақро [собиту] пойдор мекунад, ҳарчанд муҷримон кароҳат дошта бошанд»');
INSERT INTO "pioneers" VALUES(10,83,'10:83','Пас ҳеҷ кас ба Мӯсо имон наёвард, магар гурӯҳе аз қавми ӯ [он ҳам] бо тарси Фиръавн ва атрофиёнаш, ки [мабодо] ононро биозорад. Ва бешак, Фиръавн дар замин саркаш [-у ҷоҳталаб] буд ва ба ростӣ, ки ӯ аз исрофкорон [дар куфру ҷиноят] буд');
INSERT INTO "pioneers" VALUES(10,84,'10:84','Мӯсо гуфт: «Эй қавми ман, агар ба Аллоҳ таоло имон овардаед ва агар аҳли таслим ҳастед, пас, бар Ӯ таваккал кунед»');
INSERT INTO "pioneers" VALUES(10,85,'10:85','Пас, онон гуфтанд: «Бар Аллоҳ таоло таваккал кардаем. Парвардигоро, моро дастхуши фитна [ва шиканҷа ва озори] гурӯҳи ситамгорон қарор мадеҳ');
INSERT INTO "pioneers" VALUES(10,86,'10:86','Ва ба [фазлу] раҳмати хеш моро аз [шарри] гурӯҳи кофирон раҳоӣ бубахш»');
INSERT INTO "pioneers" VALUES(10,87,'10:87','Ба Мӯсо ва бародараш [Ҳорун] ваҳйи кардем, ки: «Барои қавми худ, хонаҳое дар [сарзамини] Миср баргиред ва хонаҳоятонро ибодатгоҳ қарор диҳед ва намоз барпо доред. Ва[эй Мӯсо] муъминонро [ба пирӯзӣ ва биҳишт] башорат бидеҳ»');
INSERT INTO "pioneers" VALUES(10,88,'10:88','Мӯсо гуфт: «Парвардигоро, бе гумон, ту ба Фиръавн ва атрофиёнаш зинату амвол [-и фаровон] дар зиндагии дунё додаӣ.Парвардигоро, то [ба он васила мардумро] аз роҳат гумроҳ кунанд. Парвардигоро, амволашонро нобуд кун ва дилҳояшонро сахт бигардон, ки имон наоваранд, то [вақте ки] азоби дарднокро бубинанд»');
INSERT INTO "pioneers" VALUES(10,89,'10:89','[Аллоҳ таоло] Фармуд: Ҳатман, дуои шумо ду нафар пазируфта шуд, пас, истиқомат кунед ва аз роҳи касоне, ки намедонанд, пайравӣ накунед»');
INSERT INTO "pioneers" VALUES(10,90,'10:90','Ва [саранҷом] Бани Исроилро аз дарё гузарондем,он гоҳ Фиръавн ва сипоҳиёнаш аз рӯйи ситам ва таҷовуз дар пайи онон рафтанд, то ҳангоме ки ғарқоби Ӯ [Фиръавн]-ро фаро гирифт, [бо ноумедӣ] гуфт: «Имон овардам, ки ҳеҷ маъбуде [бар ҳақ] нест, магар касе, ки Бани Исроил ба Ӯ имон овардаанд ва ман аз таслимшудагонам»');
INSERT INTO "pioneers" VALUES(10,91,'10:91','[Ба ӯ гуфта шуд ] «Инак [имон меоварӣ]? Ва ҳол онки пештар сарпечӣ мекардӣ ва аз муфсидон будӣ?');
INSERT INTO "pioneers" VALUES(10,92,'10:92','[Бидон, ки] имрӯз пайкари [беҷони] туро бар баландии [соҳил] меандозем, то барои ояндагонат мояи ибрат бошад ва ба ростӣ, бисёре аз мардум аз нишонаҳои [тавону тадбири] мо ғофиланд');
INSERT INTO "pioneers" VALUES(10,93,'10:93','Ва бе тардид, Мо Бани Исроилро дар ҷойгоҳи шоиста [дар сарзамини Шом] ҷой додем ва аз неъматҳои покиза рӯзияшон кардем. Пас, онҳо ихтилоф накарданд, магар пас аз онки илм [ба ҳаққонияти Қуръон] ба сӯяшон омад. [Эй паёмбар] Парвардигорат рӯзи қиёмат дар бораи ончи ихтилоф мекарданд, ҳатман, миёнашон ҳукм мекунад');
INSERT INTO "pioneers" VALUES(10,94,'10:94','Пас, агар дар ончи бар ту нозил кардаем, тардиде дорӣ, аз касоне, ки пеш аз ту китоб [-ҳоиТаврот ва Инҷил]-ро мехонданд, суол кун. Яқинан ҳақ аз тарафи Парвардигорат ба сӯйи ту омадааст. Пас, ҳаргиз аз тардидкунандагон набош');
INSERT INTO "pioneers" VALUES(10,95,'10:95','Ва [ҳамчунин] аз касоне набош, ки оёти Аллоҳ таолоро дурӯғ ангоштанд, ки аз зиёнкорон хоҳӣ буд');
INSERT INTO "pioneers" VALUES(10,96,'10:96','Бе тардид, касоне, ки сухани Парвардигорат дар бораи [куфр дар дунё бар] онон таҳаққуқ ёфтааст, имон намеоваранд');
INSERT INTO "pioneers" VALUES(10,97,'10:97','Гарчи ҳар нишона [ва панде] барояшон биёяд, [магар] вақте ки азоби дарднокро бубинанд');
INSERT INTO "pioneers" VALUES(10,98,'10:98','Ҳеҷ шаҳре набуд, ки [аҳлаш] имон биёварад ва имонаш ба ҳоли он суд бахшад, магар қавми Юнус, ки чун [ба мавқеъ] имон оварданд, азоби хорӣ [ва расвоӣ] дар зиндагии дунёро аз онон бартараф намудем ва то муддате [ки зинда буданд, аз неъматҳои дунявӣ] бархӯрдорашон сохтем');
INSERT INTO "pioneers" VALUES(10,99,'10:99','Ва [эй паёмбар] агар Парвардигорат мехост, ҳатман, ҳар ки дар замин аст, ҳамагияшон имон меоварданд. Пас, оё ту мардумро водор мекунӣ, ки муъмин шаванд?');
INSERT INTO "pioneers" VALUES(10,100,'10:100','Ва ҳеҷ касе наметавонад, [ба иродаи хеш] имон оварад, магар ба фармони Аллоҳ таоло. Ва [Аллоҳ таоло] палидӣ [ва азобу хорӣ]-ро бар касоне қарор медиҳад, ки намеандешанд');
INSERT INTO "pioneers" VALUES(10,101,'10:101','[Эй паёмбар, ба мушрикон] Бигӯ: «Бингаред, ки [нишонаҳои равшани Аллоҳ таоло] дар осмонҳо ва замин чист»? Ва[-ле ҳамаи] нишонаҳо ва ҳушдорҳо барои гурӯҳе, ки имон намеоваранд, суде намебахшад');
INSERT INTO "pioneers" VALUES(10,102,'10:102','Оё онҳо [чизе] ҷуз ҳаммонанди рӯзгори пешиниён [ва азобҳо ва муҷозотҳояшон]-ро интизор мекашанд? Бигӯ: «Мунтазир бошед, ки ман [низ] бо шумо аз мунтазиронам»');
INSERT INTO "pioneers" VALUES(10,103,'10:103','Сипас [ҳангоми нузули азоб] паёмбаронамон ва касонеро, ки [ба Аллоҳ таоло] имон овардаанд, наҷот медиҳем, зеро ҳақ бар уҳдаи мост, ки муъминонро наҷот диҳем');
INSERT INTO "pioneers" VALUES(10,104,'10:104','Бигӯ: «Эй мардум, агар дар дини ман тардид доред, пас, [огоҳ бошед ки ман] касонеро, ки шумо ба ҷойи Аллоҳ таоло мепарастед, намепарастам, балки он Аллоҳ таолоро мепарастам, ки ҷонатонро мегирад ва фармон ёфтаам, ки аз муъминон бошам»');
INSERT INTO "pioneers" VALUES(10,105,'10:105','Ва [ба ман амр шудааст] ки: «Ҳақгароёна ба ин дин рӯ овар ва ҳаргиз дар зумраи мушрикон набош');
INSERT INTO "pioneers" VALUES(10,106,'10:106','Ва ба ҷойи Аллоҳ таоло, чизеро, ки суду зиёне ба ту намерасонад, [ба парастишу ниёзхоҳӣ] махон, ки агар чунин кунӣ, он гоҳ ҳатман, аз ситамгорон [-и мушрик] хоҳӣ буд');
INSERT INTO "pioneers" VALUES(10,107,'10:107','Ва агар Аллоҳ таоло зиёне ба ту бирасонад, ҳеҷ касе барояш ба ҷуз Ӯ ҳеҷ боздорандае нест ва агар бароят иродаи хайре намояд, барои фазл [-у бахшиши] Ӯ ҳеҷ монеъе нест. [Ӯ бахшиши хешро] Ба ҳар касе аз бандагонаш, ки бихоҳад, мерасонад ва Ӯ омурзандаи меҳрубон аст»');
INSERT INTO "pioneers" VALUES(10,108,'10:108','Бигӯ: «Эй мардум, ҳақ аз ҷониби Парвардигоратон бароятон омадааст, пас, ҳар ки ҳидоят ёбад, ба суди хеш ҳидоят меёбад ва ҳар ки гумроҳ гардад, ба зиёни хеш гумроҳ мегардад ва ман [муроқибу] коргузори шумо нестам»');
INSERT INTO "pioneers" VALUES(10,109,'10:109','Ва [эй паёмбар] аз ончи бар ту ваҳй мешавад пайравӣ кун ва шикебо бош, то Аллоҳ таоло [байни ту ва кофирон] доварӣ кунад ва Ӯ беҳтарин довар аст');
INSERT INTO "pioneers" VALUES(11,1,'11:1','Алиф, лом, ро. Ин [Қуръон] китобест, ки оёти он истеҳком ёфтааст ва сипас аз ҷониби ҳакими огоҳ ба равшанӣ баён шудааст');
INSERT INTO "pioneers" VALUES(11,2,'11:2','[Паёми паёмбар ин аст] Ки ҷуз Аллоҳро напарастед, [ки] бе тардид, ман аз сӯйи Ӯ бароятон бимдиҳанда ва башоратгар ҳастам');
INSERT INTO "pioneers" VALUES(11,3,'11:3','Ва ин ки аз Парвардигоратон омурзиш бихоҳед, сипас ба даргоҳаш тавба кунед, то шуморо [дар зиндагии дунё] то саромади муайяне ба хайру хубӣ баҳраманд созад ва ба ҳар бофазилате [ки яктопарастӣ ва накукорӣ пеша кардааст, подошу] фузунӣ бахшад.Ва агар [аз имон] рӯ бигардонед, [бидонед, ки ман] ба ростӣ, бароятон аз азоби рӯзе саҳмгин метарсам');
INSERT INTO "pioneers" VALUES(11,4,'11:4','[Шумо эй мардум] Бозгаштатон ба сӯйи Аллоҳ таоло аст ва Ӯ бар ҳар чизе тавоност');
INSERT INTO "pioneers" VALUES(11,5,'11:5','Огоҳ бошед! Онҳо [куфрро] дар синаҳояшон ниҳон мекунанд, то онро аз Ӯ [Аллоҳ таоло] пинҳон доранд. Огоҳ бошед! Он гоҳ ки онон ҷомаҳояшонро бар сар мекашанд, Аллоҳ таоло он чи пинҳон месозанд ва он чи ошкор медоранд, [ҳамаро] медонад. Бе тардид, Ӯ ба асрори синаҳо доност');
INSERT INTO "pioneers" VALUES(11,6,'11:6','Ва ҳеҷ ҷунбандае дар замин нест, магар [ин ки] рӯзиаш бар уҳдаи Аллоҳ таоло аст ва [Ӯ] қароргоҳ ва маҳалли маргашро медонад. Ҳама [инҳо] дар китобе равшан [сабт] аст');
INSERT INTO "pioneers" VALUES(11,7,'11:7','Ва Ӯст, ки осмонҳо ва заминро дар шаш рӯз офарид ва [пеш аз он] Арши Ӯ бар об буд, то шуморо биозмояд, ки кадоматон накукортаред. Ва агар [ба мушрикон] бигӯед: «Шумо пас аз марг барангехта мешавед», касоне, ки куфр варзиданд мегӯянд: «Ин [Қуръон чизе] нест, магар ҷодуе ошкор»');
INSERT INTO "pioneers" VALUES(11,8,'11:8','Ва агар мо азобашонро то чанд муддате ба таъхир андозем, албатта,[аз рӯйи тамасхур] мегӯянд: «Чи чизе монеъи [вуқуъи] он шуда аст?» Огоҳ бошед! Рӯзе, ки [азоб] ба суроғашон биёяд, аз онон бозгардонида нахоҳад шуд ва он чиро ба масхара мегирифтанд, домангирашон хоҳад шуд');
INSERT INTO "pioneers" VALUES(11,9,'11:9','Ва агар аз ҷониби хеш раҳмате ба инсон бичашонем, сипас онро аз ӯ бигирем, ҳатман, дилсард [ва] носипос мегардад');
INSERT INTO "pioneers" VALUES(11,10,'11:10','Ва агар пас аз ранҷе, ки ба ӯ расидааст, неъмате ба ӯ бичашонем, ҳатман, мегӯяд: «Сахтиҳо [ва ногувориҳо] аз ман дур шуд». Ҳаққо, ки ӯ шодмону фахрфурӯш аст');
INSERT INTO "pioneers" VALUES(11,11,'11:11','Магар касоне, ки шикебоӣ намуданд ва корҳои нек карданд. Барои эшон омурзишу подоши бузурге [дар пеш] аст');
INSERT INTO "pioneers" VALUES(11,12,'11:12','Ва [эй паёмбар] мабодо [иблоғи] бархе аз он чиро, ки ба сӯят ваҳй мешавад, тарк кунӣ ва синаат аз он ба танг ояд, ки [мушрикон] мегӯянд: «Чаро ганҷе бар ӯ нозил нагаштааст ё фариштае бо ӯ наёмадааст»? Ҷуз ин нест, ки ту фақат бимдиҳандаӣ ва Аллоҳ таоло [муроқибу] коргузори ҳар чизест');
INSERT INTO "pioneers" VALUES(11,13,'11:13','Ё мегӯянд: «[Муҳаммад] Он [китоб]-ро барсохтааст». Бигӯ: «Агар рост мегӯед, даҳ сура барсохта ҳаммонанди он биёваред ва ба ҷуз Аллоҳ таоло ҳар киро метавонед, ба ёрӣ бихонед»');
INSERT INTO "pioneers" VALUES(11,14,'11:14','Пас, агар онон даъвататонро напазируфтанд, бидонед, ки [Қуръон] фақат ба илми Аллоҳ таоло нозил шудааст ва ин ки ҳеҷ маъбуде [барҳақ] ҷуз Ӯ нест. Пас, оё таслим [-и амри Аллоҳ таоло ва паёмбар] мешавед?');
INSERT INTO "pioneers" VALUES(11,15,'11:15','Касоне, ки зиндагии дунё ва таҷаммули онро мехоҳанд, [подоши] корҳояшонро дар ин [дунё] ба тамомӣ ба онон медиҳем ва дар он [подош ҳеҷ] каму косте нахоҳанд дид');
INSERT INTO "pioneers" VALUES(11,16,'11:16','Инон касоне ҳастанд, ки дар охират ба ҷуз оташ [-и дузах ҳеҷ насибе] нахоҳанд дошт ва он чи дар дунё анҷом додаанд, бар бод рафтааст ва он чи мекарданд, табоҳ аст');
INSERT INTO "pioneers" VALUES(11,17,'11:17','Оё касе [паёмбар], ки далели ошкоре [Қуръон] аз Парвардигораш дорад ва шоҳиде [Ҷабраил] аз ҷониби Ӯ [Аллоҳ таоло] онро мехонад, [ҳаммонанди кофирони гумроҳ аст] ва ҳол он ки пеш аз он низ Мӯсо роҳнамо ва мояи раҳмат [-и эшон] буд. Инон [мусулмонон] ба он [китоб] имон доранд ва ҳар касе аз гурӯҳҳо[-и мухталифи мушрикон], ки ба он куфр биварзад [ва инкораш кунад], оташи дузах ваъдагоҳи ӯст. Пас,[эй паёмбар] аз [Қуръон ва ҳақиқати ваъдаҳои] он дар тардид мабош. Бе тардид, он [китобе] росту дуруст аз сӯи Парвардигори туст, вале бештари мардум имон намеоваранд');
INSERT INTO "pioneers" VALUES(11,18,'11:18','Ва кист ситамгортар аз он кас, ки [бо нисбат додани фарзанд ё шарик] бар Аллоҳ таоло дурӯғ мебандад? Онон [рӯзи қиёмат] бар Парвардигорашон арза мешаванд ва гувоҳон [паёмбарон ва фариштагон] мегӯянд: «Инҳо ҳамон касоне ҳастанд, ки бар Парвардигорашон дурӯғ мебастанд». Ҳон! Лаънати Аллоҳ таоло бар ситамгорони [мушрик] бод!!');
INSERT INTO "pioneers" VALUES(11,19,'11:19','[Ҳамон] касоне, ки [мардумро] аз роҳи Аллоҳ таоло бозмедоранд ва онро мунҳариф мехоҳанд ва онон худ охиратро бовар надоранд');
INSERT INTO "pioneers" VALUES(11,20,'11:20','Онон дар замин нотавонкунанда [-и Аллоҳ аз азоби хеш] нестанд ва ҷуз Аллоҳ таоло дӯстоне надоранд. Азоб бар эшон дучанд мешавад,[чаро ки] онон на тавони шунидан [-и ҳақро] доштанд ва на [ҳақоиқро] медиданд');
INSERT INTO "pioneers" VALUES(11,21,'11:21','Инонанд, ки ба хеш зиён заданд ва дурӯғҳое, ки мебофтанд, [ҳама] маҳву нобуд шуд');
INSERT INTO "pioneers" VALUES(11,22,'11:22','Ногузир онон дар охират аз ҳама зиёнкортаранд');
INSERT INTO "pioneers" VALUES(11,23,'11:23','Касоне, ки имон овардаанд ва корҳои шоиста кардаанд ва дар баробари Парвардигорашон фурӯтанӣ намудаанд, инон яқинан биҳиштианд [ва] ҳам инон дар он ҷовидонанд');
INSERT INTO "pioneers" VALUES(11,24,'11:24','Масали ин ду гурӯҳ [кофиру муъмин] ҳаммонанди нобино ва ношунаво [аз як сӯ] ва бино ва шунаво [аз сӯйи дигар] аст. Оё бо якдигар баробаранд? Оё панд намегиред?');
INSERT INTO "pioneers" VALUES(11,25,'11:25','Ва ба ростӣ Мо Нуҳро ба сӯйи қавмаш фиристодем [бо ин паём], ки: «Ба ростӣ, ки ман бароятон бимдиҳандае ошкорам');
INSERT INTO "pioneers" VALUES(11,26,'11:26','Ҷуз Аллоҳро напарастед, [ки] бе тардид ман аз азоби рӯзе дарднок бар шумо метарсам»');
INSERT INTO "pioneers" VALUES(11,27,'11:27','Бузургоне аз қавми ӯ, ки куфр варзида буданд, [дар посух] гуфтанд: «Туро ҷуз башаре ҳаммонанди хеш намебинем ва ба ҷуз фурӯмоягони мо [он ҳам надониста ва] насанҷида [каси дигареро] намебинем, ки аз ту пайравӣ карда бошад ва бароятон [ҳеҷ] бартарие бар худ намебинем, балки шуморо дурӯғгӯ мепиндорем»');
INSERT INTO "pioneers" VALUES(11,28,'11:28','[Нуҳ] гуфт: «Эй қавми ман, ба ман бигӯед, [ки] агар аз сӯйи Парвардигорам далел [-у муъҷизаи] ошкоре дошта бошам ва Ӯ аз ҷониби хеш ба ман раҳмате [ нубувват] бахшида бошад ва [ин мавҳибат] аз диди шумо пӯшида бошад, оё мо метавонем ба [пазириши] он водоратон кунем ва ҳол он ки кароҳат доред?');
INSERT INTO "pioneers" VALUES(11,29,'11:29','Ва эй қавми ман, барои ин [рисолат ман ҳеҷ сарват] ва моле аз шумо дархост намекунам. Подоши ман ҷуз бар [уҳдаи] Аллоҳ таоло нест ва касонеро, ки имон овардаанд, аз худ намеронам. Бе тардид, [рӯзи қиёмат] онон мулоқоткунандаи Парвардигори хешанд, вале ман шуморо гурӯҳе мебинам, ки нодонӣ мекунед');
INSERT INTO "pioneers" VALUES(11,30,'11:30','Ва эй қавми ман, агар ман ононро тард кунам, кист, ки маро дар баробари [кайфари] Аллоҳ таоло ёрӣ кунад? Пас, оё панд намегиред?');
INSERT INTO "pioneers" VALUES(11,31,'11:31','Ва ба шумо намегӯям, ки ганҷинаҳои Аллоҳ таоло назди ман аст ва ғайб намедонам ва намегӯям, ки ман фариштаам ва дар бораи касоне, ки ба чашми шумо ҳақир меоянд, намегӯям, ки Аллоҳ таоло хайре насибашон нахоҳад кард. Аллоҳ таоло ба он чи дар дилҳои эшон аст, огоҳтар аст. [Агар чунин бигӯям] Дар он сурат, ҳатман, аз ситамгорон хоҳам буд»');
INSERT INTO "pioneers" VALUES(11,32,'11:32','[Қавмаш] Гуфтанд: «Эй Нуҳ, ба ростӣ, ки бо мо ҷидол кардӣ ва бисёр [ҳам] ҷидол кардӣ, пас, агар рост мегӯӣ, он чи [аз азоб]-ро, ки ба мо ваъда медиҳӣ, биёвар»');
INSERT INTO "pioneers" VALUES(11,33,'11:33','[Нуҳ] Гуфт: «Танҳо Аллоҳ таоло аст, ки агар бихоҳад, онро бар [сари] шумо меоварад ва шумо оҷизкунанда [-и Ӯ аз азоб кардан] нестед');
INSERT INTO "pioneers" VALUES(11,34,'11:34','Ва агар ман бихоҳам насиҳататон кунам, насиҳат кардани ман суде бароятон надорад, агар Аллоҳ таоло бихоҳад [ки нобуду] гумроҳатон созад. Ӯ Парвардигори шумост ва [ҳамагӣ] ба сӯяш бозгардонда мешавед»');
INSERT INTO "pioneers" VALUES(11,35,'11:35','Оё [мушрикон] мегӯянд: «Ӯ [Муҳаммад] ин суханонро ба дурӯғ ба Аллоҳ таоло нисбат додааст? Бигӯ: «Агар ман инҳоро аз пеши худ сохта бошам ва ба Ӯ нисбат диҳам, гуноҳаш бар [уҳдаи] ман, ва[-ле] ман аз гуноҳони шумо безорам»');
INSERT INTO "pioneers" VALUES(11,36,'11:36','Ва ба Нуҳ ваҳй шуд, ки: «Аз қавми ту ҷуз касоне, ки имон овардаанд, дигар ҳеҷ кас имон намеоварад, пас, бар он чи мекарданд, ғам махӯр');
INSERT INTO "pioneers" VALUES(11,37,'11:37','Ва [акнун] киштиро зери назари Мо ва ба ваҳй [ва роҳнамоӣ]-и Мо бисоз ва дар бораи касоне, ки ситам карданд, бо Ман сухан магӯ [чаро ки] онҳо, ҳатман, ғарқшуданӣ ҳастанд»');
INSERT INTO "pioneers" VALUES(11,38,'11:38','Ва [Нуҳ] киштиро месохт ва гоҳе бузургоне аз қавмаш бар ӯ мегузаштанд, масхарааш мекарданд. [Ӯ] Мегуфт: «Агар моро масхара мекунед, мо низ [ба ҳангомаш] ба ҳамон сурате, ки моро масхара мекунед, шуморо масхара хоҳем кард');
INSERT INTO "pioneers" VALUES(11,39,'11:39','Ба зудӣ хоҳед донист, ки азоби [илоҳӣ] суроғи чи касе меояд, ки хораш созад ва азобе пойдор бар ӯ фуруд ояд»');
INSERT INTO "pioneers" VALUES(11,40,'11:40','[Ин ҳолат ҳамчунон идома дошт] то замоне, ки фармон [-и азоб] фаро расид ва [об аз] танӯр ҷӯшидан гирифт. [Ба Нуҳ] гуфтем: «Аз [аз ҳайвонот] як ҷуфт [нар ва мода] дар он [киштӣ] савор кун ва [ҳамчунин] хонадонатро, ба ҷуз касе, ки ваъда [-и ҳалок] қаблан бар ӯ муқаррар шудааст ва [низ] касонеро, ки имон овардаанд [низ савор кун]».Ва ҷуз иддаи андаке ҳамроҳи ӯ имон наоварда буданд');
INSERT INTO "pioneers" VALUES(11,41,'11:41','[Нуҳ] Гуфт: «Дар он [киштӣ] савор шавед. Ҳаракат ва таваққуфаш ба ном [-у хости] Аллоҳ таоло аст. Бе тардид, Парвардигорам омурзандаву меҳрубон аст»');
INSERT INTO "pioneers" VALUES(11,42,'11:42','Ва он [киштӣ] ононро дар миёни амвоҷи кӯҳпайкаре [пеш] мебурд ва Нуҳ писарашро, ки дар каноре [истода] буд, садо зад: «Писарам, бо мо савор шав ва ҳамроҳи кофирон мабош»');
INSERT INTO "pioneers" VALUES(11,43,'11:43','[Писари Нуҳ] Гуфт: «Ба кӯҳе паноҳ хоҳам бурд, ки аз об ҳифзам кунад». [Нуҳ] Гуфт: «Имрӯз ҳеҷ нигаҳдорандае дар баробари фармони Аллоҳ таоло нест, магар касе, ки [Аллоҳ таоло] ба ӯ раҳм кунад».Ва[-ле ногоҳ] мавҷ дар миёни он ду фосила андохт, пас, [вай низ] аз ғарқшудагон гардид');
INSERT INTO "pioneers" VALUES(11,44,'11:44','Ва гуфта шуд: «Эй замин, обатро фурӯ бар ва эй осмон [аз боридан] боз ист ва об фурӯ нишаст ва кор ба анҷом расид ва [киштӣ] бар [кӯҳи] Ҷудӣ қарор гирифт ва гуфта шуд: «Дурӣ [аз раҳмати илоҳӣ] бар гурӯҳи ситамгорон бод»!');
INSERT INTO "pioneers" VALUES(11,45,'11:45','Ва Нуҳ Парвардигорашро нидо доду гуфт: «Парвардигоро, писарам аз хонадони ман аст ва бе гумон, ваъдаи Ту [дар бораи наҷоти хонадонам] ҳақ аст ва Ту беҳтарин доварӣ»');
INSERT INTO "pioneers" VALUES(11,46,'11:46','[Аллоҳ таоло] Фармуд: «Эй Нуҳ, дар ҳақиқат, ӯ аз хонадони ту нест. [Кирдори] Ӯ кирдори ношоиста аст. Пас, чизеро, ки ба он дониш надорӣ, аз Ман махоҳ. Ман туро панд медиҳам, ки мабодо аз ҷоҳилон бошӣ»');
INSERT INTO "pioneers" VALUES(11,47,'11:47','[Нуҳ] Гуфт: «Парвардигоро, ман ба Ту паноҳ мебарам, ки аз Ту чизе бихоҳам, ки ба он донише надорам ва агар маро набахшӣ ва бар ман раҳм накунӣ, аз зиёнкоронам»');
INSERT INTO "pioneers" VALUES(11,48,'11:48','Гуфта шуд: «Эй Нуҳ, бо [амнияту] саломатӣ аз ҷониби Мо ва [низ ҳамроҳ бо] баракатҳои [фаровон] бар ту ва бар гурӯҳҳое, ки ҳамроҳат ҳастанд, [бар замин] фуруд о ва гурӯҳҳое [низ] ҳастанд, ки ба зудӣ ононро [аз неъматҳо] баҳраманд месозем, сипас азоби дардноке аз сӯйи Мо ба онон хоҳад расид»');
INSERT INTO "pioneers" VALUES(11,49,'11:49','[Эй паёмбар] Инҳо аз хабарҳои ғайб аст, ки онро ба ту ваҳй мекунем. На ту онҳоро пеш аз ин медонистӣ ва на қавмат, пас,[бар озорашон] шикебоӣ кун. Яқинан, оқибати [наку] аз они парҳезгорон аст');
INSERT INTO "pioneers" VALUES(11,50,'11:50','Ва ба сӯйи қавми Од бародарашон Ҳудро [фиристодем, ки] гуфт: «Эй қавми ман, Аллоҳро бипарастед, ки маъбуд [-и барҳақ]-е ҷуз Ӯ надоред [ва] шумо [дар ширк ба Аллоҳ таоло] дурӯғбофоне беш нестед');
INSERT INTO "pioneers" VALUES(11,51,'11:51','«Эй қавми ман, барои [расонидани] ин [даъват] аз шумо подоше наметалабам. Подоши ман танҳо бар [уҳдаи] касест, ки маро офарид. Оё намеандешед?');
INSERT INTO "pioneers" VALUES(11,52,'11:52','Ва эй қавми ман, аз Парвардигоратон талаби омурзиш кунед, сипас ба сӯйи Ӯ бозгардед [ва тавба кунед], то [борони] осмонро паёпай бар шумо бифиристад ва неруе бар неруятон биафзояд ва гунаҳкорона [аз ҳақ] рӯй нагардонед»');
INSERT INTO "pioneers" VALUES(11,53,'11:53','Онон гуфтанд: «Эй Ҳуд, бароямон далели равшане наовардаӣ ва мо ба [хотири] гуфтори ту маъбудонамонро раҳо намекунем ва ба ту имон намеоварем');
INSERT INTO "pioneers" VALUES(11,54,'11:54','[Мо чизе] Ҷуз ин намегӯем, ки бархе аз маъбудонамон ба ту газанде расондаанд [ва девона шудаӣ]». [Ҳуд] Гуфт: «Аллоҳро гувоҳ мегирам ва шумо [низ] гувоҳ бошед, ки ман аз он чи [ба Аллоҳ таоло] шарик қарор медиҳед, безорам');
INSERT INTO "pioneers" VALUES(11,55,'11:55','Пас, ҳамагӣ дар бораи ман найранг кунед ва муҳлатам надиҳед');
INSERT INTO "pioneers" VALUES(11,56,'11:56','Ман бар Аллоҳ таоло, ки Парвардигори ману шумост, таваккал кардам. Ҳеҷ ҷунбандае нест, магар ин ки дар зери қудрат ва фармонравоии Ӯст. Бе тардид, Парвардигорам бар роҳи рост аст');
INSERT INTO "pioneers" VALUES(11,57,'11:57','Пас, агар рӯй бигардонед, [бидонед, ки] ман яқинан, он чиро, ки ба хотираш ба сӯйи шумо фиристода шудаам ба шумо расонидам ва Парвардигори ман, қавме ғайр аз шуморо ҷонишинатон мекунад ва шумо наметавонед ҳеҷ зиёне ба Ӯ бирасонед, [чароки] бе тардид, Парвардигорам бар ҳар чиз нигаҳбон аст»');
INSERT INTO "pioneers" VALUES(11,58,'11:58','Ва ҳангоме ки фармони [азоби] Мо фаро расид, Ҳуд ва касонеро, ки ҳамроҳаш имон оварда буданд, ба раҳмате аз [ҷониби] хеш наҷот додем ва ононро аз азоби сахт раҳонидем');
INSERT INTO "pioneers" VALUES(11,59,'11:59','Ва ин [қавми] Од буданд, ки оёти Парвардигорашонро инкор карданд ва аз паёмбаронаш нофармонӣ намуданд ва аз фармони ҳар cаркаши ситезагаре пайравӣ карданд');
INSERT INTO "pioneers" VALUES(11,60,'11:60','Ва дар ин дунё ва рӯзи қиёмат [низ] лаънате ба дунболашон равон аст. Огоҳ бошед! Қавми Од ба Парвардигорашон куфр варзиданд. Ҳон! Од – қавми Ҳуд [аз раҳмати илоҳӣ] дур бод!');
INSERT INTO "pioneers" VALUES(11,61,'11:61','Ва ба сӯйи [қавми] Самуд бародарашон Солеҳро [фиристодем]. Ӯ гуфт: «Эй қавми ман Аллоҳро бипарастед, ки ҷуз Ӯ маъбуде [бар ҳақ] надоред. Ӯ шуморо аз замин падид овард ва ободонии онро ба шумо вогузошт, пас, аз Ӯ омурзиш биталабед, сипас ба сӯяш бозгардед [ва тавба кунед]. Бе тардид, Парвардигорам наздик [ва] иҷобатгар аст»');
INSERT INTO "pioneers" VALUES(11,62,'11:62','Онон гуфтанд: «Эй Солеҳ, пеш аз ин ту миёни мо мояи умедворӣ будӣ. Оё моро аз парастиши он чи падаронамон мепарастиданд, бозмедорӣ? Ва ба ростӣ, мо аз он чи ба сӯяш даъватамон мекунӣ, сахт тардид дорем»');
INSERT INTO "pioneers" VALUES(11,63,'11:63','[Солеҳ] Гуфт: «Эй қавми ман, ба ман бигӯед,[ки] агар аз сӯйи Парвардигорам далел [-у муъҷизаи] ошкоре дошта бошам ва Ӯ аз ҷониби хеш ба ман раҳмате [нубувват] бахшида бошад, пас, агар аз Ӯ нофармонӣ кунам, чи касе маро дар баробари Аллоҳ таоло ёрӣ мекунад? Пас, шумо ҷуз зиёнкорӣ [чизе] бар ман намеафзоед');
INSERT INTO "pioneers" VALUES(11,64,'11:64','Эй қавми ман, ин модашутури Аллоҳ таоло аст, ки барои шумо нишонаест [бар дурустии даъватам], пас, бигзоред, то дар замини Аллоҳ таоло бичарад ва озоре ба ӯ нарасонед, ки [дар ғайри ин сурат] азобе зудрас шуморо фаро мегирад»');
INSERT INTO "pioneers" VALUES(11,65,'11:65','Пас, он [модашутур]-ро пай (захми) карданд, он гоҳ [Солеҳ ба онон] гуфт: «Се рӯз дар хонаҳоятон [аз неъматҳои дунё] баҳраманд гардед [ва пас аз он азоб хоҳад омад]. Ин ваъдаест, ки дурӯғ нахоҳад буд»');
INSERT INTO "pioneers" VALUES(11,66,'11:66','Пас, чун фармони [азоб]-и мо фаро расид, Солеҳ ва касонеро, ки ҳамроҳаш имон оварда буданд, ба раҳмате аз [ҷониби] хеш аз [азобу] расвоии он рӯз наҷот додем. Бе гумон, Парвардигорат ҳамон тавонманди шикастнопазир аст');
INSERT INTO "pioneers" VALUES(11,67,'11:67','Ва касонеро, ки ситам карда буданд, бонг [-и маргбор]-е фаро гирифт, пас, дар ҷойи хеш ба рӯ афтоданд ва ҳалок шуданд');
INSERT INTO "pioneers" VALUES(11,68,'11:68','Чунончи гӯӣ дар он [диёр] набудаанд. Огоҳ бошед! [Қавми] Самуд ба Парвардигорашон куфр варзиданд. Ҳон! Самуд [аз раҳмати илоҳӣ] дур бод!');
INSERT INTO "pioneers" VALUES(11,69,'11:69','Ба ростӣ, фиристодагони Мо бо башорати [таваллуди фарзанд] назди Иброҳим омаданд,[ва] гуфтанд: «Салом [бар ту]». [Ӯ низ дар посух] Гуфт: «Салом [бар шумо]» ва дере напоид, ки [Иброҳим барояшон] гӯсолае бирён овард');
INSERT INTO "pioneers" VALUES(11,70,'11:70','Пас, чун дид ба сӯйи он [ғизо] даст намебаранд [ва чизе намехӯранд], бо онон эҳсоси бегонагӣ кард ва [аз бими он ки қасди қатлашро дошта бошанд] аз онон тарсе ба дил гирифт. [Фариштагон] Гуфтанд: «Натарс, мо ба сӯйи қавми Лут фиристода шудаем»');
INSERT INTO "pioneers" VALUES(11,71,'11:71','Ва ҳамсараш [Сора, ки пушти парда] истода буд, [аз шунидани хабари таваллуди фарзанд] хандид, он гоҳ ӯро ба Исҳоқ ва пас аз ӯ ба Яъқуб башорат додем');
INSERT INTO "pioneers" VALUES(11,72,'11:72','[Сора] Гуфт: «Эй вой бар ман! Оё дар ҳоле ки худам пиразанам ва ин шавҳарам пирамарде [фартут] аст, фарзанд мезоям? Ба ростӣ, ин чизе шигифтовар аст');
INSERT INTO "pioneers" VALUES(11,73,'11:73','[Фариштагон] Гуфтанд: «Оё аз фармони Аллоҳ таоло тааҷҷуб мекунӣ? Раҳмати Аллоҳ таоло ва баракоташ бар шумо ва аҳли хона бод! Бе тардид, Ӯ cутудае бузургвор аст»');
INSERT INTO "pioneers" VALUES(11,74,'11:74','Пас, чун тарс аз [дили] Иброҳим [берун] рафт ва башорати [таваллуди фарзанд] ба ӯ расид, дар бораи [таъхири азоб ё наҷоти муъминони] қавми Лут бо мо баҳс [ва ба чуну чаро] пардохт');
INSERT INTO "pioneers" VALUES(11,75,'11:75','Ба ростӣ, ки Иброҳим бурдбору бисёрниёишгар ва бозоянда [ба даргоҳи Мо] буд');
INSERT INTO "pioneers" VALUES(11,76,'11:76','[Фариштагон гуфтанд] «Эй Иброҳим, аз ин [сухан] бигзар, [чаро ки] ба ростӣ, фармони [азоби] Парвардигорат фаро расидааст ва мусалламан, азобе бозгаштнопазир барояшон хоҳад омад»');
INSERT INTO "pioneers" VALUES(11,77,'11:77','Ва чун фиристодагони Мо назди Лут омаданд, аз [тасаввури бешармии қавмаш нисбат ба] онон нигарон ва дилтанг шуд ва гуфт: «Имрӯз рӯзи бисёр сахтест»');
INSERT INTO "pioneers" VALUES(11,78,'11:78','Ва қавми ӯ шитобон [ба қасди комҷӯӣ аз меҳмонони Лут] ба [хонаи] ӯ ҳуҷум оварданд, дар ҳоле ки пеш аз он [низ] муртакиби корҳои зиште мешуданд. [Лут]Гуфт: «Эй қавми ман, инҳо духтарони [уммати] ман ҳастанд; [бо эшон издивоҷ кунед]. Онон барои шумо покизатаранд, пас, аз Аллоҳ таоло битарсед ва маро дар [муқобили] меҳмононам шармсор накунед. Оё байни шумо марди хирадманде нест [ки шуморо аз ин кор боздорад]»?');
INSERT INTO "pioneers" VALUES(11,79,'11:79','Онон гуфтанд: «Ту худ медонӣ, ки мо ба духтарони [уммати] ту ниёзе надорем ва хуб медонӣ, ки мо чи мехоҳем»');
INSERT INTO "pioneers" VALUES(11,80,'11:80','[Лут] Гуфт: «Кош, дар баробари шумо қудрате доштам ё метавонистам ба такягоҳи устуворе [ҳамчун хонавода ва қабилаам] паноҳ бибарам»');
INSERT INTO "pioneers" VALUES(11,81,'11:81','[Фариштагон] Гуфтанд: «Эй Лут, мо фиристодагони Парвардигорат ҳастем. Онон ҳаргиз ба ту даст намеёбанд, пас, вақте посе аз шаб гузарад, хонаводаатро [аз ин шаҳр берун] бибар ва ҳеҷ як аз шумо ба пушти сар нигоҳ накунад, магар ҳамсарат, ки он чи [аз азоб], ки ба онон мерасад, ҳатман, ба ӯ [низ] хоҳад расид. Дар ҳақиқат, ваъдагоҳашон субҳ аст. Оё субҳ наздик нест?»');
INSERT INTO "pioneers" VALUES(11,82,'11:82','Пас, чун фармони Мо фаро расид, он [сарзамин]-ро зерурӯ кардем ва бар онон санггилҳое паёпай боридем');
INSERT INTO "pioneers" VALUES(11,83,'11:83','[Сангҳое, ки] Назди Парвардигорат нишондор буд ва ин [азоби дарднок] аз ситамгорони [дигар, аз ҷумла Қурайш] дур нест');
INSERT INTO "pioneers" VALUES(11,84,'11:84','Ва ба сӯйи [қавми] Мадян бародарашон Шуайбро [фиристодем]. Ӯ гуфт: «Эй қавми ман, Аллоҳро бипарастед, ки ба ҷуз ӯ маъбуде [бар ҳақ] надоред ва паймона ва мизонро кам накунед. Ба ростӣ, шуморо дар неъмат мебинем, ва[ле] аз азоби рӯзи фарогир бар шумо метарсам');
INSERT INTO "pioneers" VALUES(11,85,'11:85','Ва эй қавми ман, паймона ва мизонро бо адолат тамом [-у комил] бидиҳед ва колоҳои мардумро кам надиҳед ва дар замин ба фасод [-у гуноҳ]накӯшед');
INSERT INTO "pioneers" VALUES(11,86,'11:86','Агар имон дошта бошед, он чи Аллоҳ таоло [пас аз таҳвили комили паймона аз касби ҳалол] бароятон боқӣ мегузорад, беҳтар аст ва ман бар шумо нигаҳбон нестам»');
INSERT INTO "pioneers" VALUES(11,87,'11:87','Онон гуфтанд: «Эй Шуайб, оё намозат туро бар он медорад, ки [ба мо амр кунӣ, то] даст аз он чи падаронамон мепарстиданд, бардорем ё дар амволамон ҳар коре, ки мехоҳем, накунем? Ту, ки бурдбор [ва] хирадмандӣ [пас, чаро чунин мегӯӣ]?»');
INSERT INTO "pioneers" VALUES(11,88,'11:88','[Шуайб] Гуфт: «Эй қавми ман, ба ман бигӯед, [ки] агар аз сӯйи Парвардигорам далел [-у муъҷизаи] ошкоре дошта бошам ва аз ҷониби хеш рӯзии накуие ба ман ато карда бошад [он гоҳ чи мекунед]? Ва ман намехоҳам дар он чи шуморо аз он бозмедорам, бо шумо мухолифат кунам [вале худ анҷомаш диҳам]. То он ҷо дар тавон дорам, ҷуз ислоҳ намехоҳам ва тавфиқи ман танҳо ба [лутфу хости] Худост, бар Ӯ таваккал кардам ва ба сӯйи Ӯ бозмегардам');
INSERT INTO "pioneers" VALUES(11,89,'11:89','Ва эй қавми ман, душманӣ [ва мухолифат] бо ман сабаб нашавад, ки ҳаммонанди он чи ки ба қавми Нуҳ ё қавми Ҳуд ё қавми Солеҳ расид, ба шумо [низ] бирасад ва [ба хусус азоби] қавми Лут [ки аз лиҳози замону макон] чандон аз шумо дур нест');
INSERT INTO "pioneers" VALUES(11,90,'11:90','Ва аз Парвардигоратон омурзиш бихоҳед, сипас ба сӯйи Ӯ бозгардед [ва тавба кунед, ки] бе тардид, Парвардигорам меҳрубон [ва] дӯстдор [-и тавбакунандагон] аст');
INSERT INTO "pioneers" VALUES(11,91,'11:91','Онон гуфтанд: «Эй Шуайб, бисёре аз он чиро, ки мегӯӣ, намефаҳмем ва яқинан туро дар миёни худ нотавон мебинем ва агар [эҳтироми] қабилаат намебуд, мусалламан, сангсорат мекардем ва ту назди мо тавонманд [гаронқадр] нестӣ»');
INSERT INTO "pioneers" VALUES(11,92,'11:92','[Шуайб] Гуфт: «Эй қавми ман, оё қабилаам назди шумо гиромитар аз Аллоҳ таоло аст, ки [фармони] Ӯро [фаромӯш кардед ва] пушти сари хеш андохтаед. Бе тардид, Парвардигорам ба он чи анҷом медиҳед, иҳота дорад');
INSERT INTO "pioneers" VALUES(11,93,'11:93','Ва эй қавми ман, шумо ба равиши худ амал кунед, ман низ [ба равиши худ] амал мекунам. Ба зудӣ хоҳед донист, ки азоби расвокунанда бар чи касе фуруд меояд ва дурӯғгӯ кист. Шумо мунтазир бошед ва ман [низ] бо шумо дар интизорам»');
INSERT INTO "pioneers" VALUES(11,94,'11:94','Ва чун фармони Мо фаро расид, Шуайб ва касонеро, ки ҳамроҳаш имон оварда буданд, ба раҳмате аз [ҷониби] хеш наҷот додем ва касонеро, ки ситам карданд, бонге [маргбор] фаро гирифт, пас, дар ҷойи худ ба рӯ афтоданд ва ҳалок шуданд;');
INSERT INTO "pioneers" VALUES(11,95,'11:95','Чунон [мурданд] ки гӯӣ ҳаргиз дар он [диёр] набуданд. Ҳон! [Қавми] Мадян [аз раҳмати илоҳӣ] дур бод! Чунончи [қавми] Самуд дур шуданд');
INSERT INTO "pioneers" VALUES(11,96,'11:96','Ва ба ростӣ, Мӯсоро бо оёти худ ва далеле ошкор фиристодем');
INSERT INTO "pioneers" VALUES(11,97,'11:97','Ба сӯйи Фиръавн ва бузургон [-у ашрофи қавм]-и ӯ, пас, онон аз фармони Фиръавн пайравӣ карданд ва фармони Фиръавн дуруст [ва бихрадона] набуд');
INSERT INTO "pioneers" VALUES(11,98,'11:98','[Фиръавн] Рӯзи қиёмат пешопеши қавмаш меравад ва ононро ба оташ [-и дузах] ворид мекунад ва чи бад ҷойгоҳест, ки ба он ворид мешаванд!');
INSERT INTO "pioneers" VALUES(11,99,'11:99','Ва дар ин [дунё] ва рӯзи қиёмат, нафрин ба дунбол доранд ва чи бад армуғоне ба онон мебахшанд!');
INSERT INTO "pioneers" VALUES(11,100,'11:100','[Эй паёмбар] Ин [бархе] аз хабарҳои он [сарзаминҳо ва] шаҳрҳост, ки онҳоро бароят бозгӯ мекунем, бархе аз онҳо [ҳанӯз] побарҷост ва [бархе дигар] вайрон [гаштааст]');
INSERT INTO "pioneers" VALUES(11,101,'11:101','Ва Мо ба эшон ситам накардем, балки онон [худ] ба хештан ситам карданд, пас, ҳангоме ки фармони Парвардигорат [барои азобашон] фаро расид, маъбудонашон, ки ба ҷойи Аллоҳ таоло мехонданд, суде барояшон надоштанд [ва ёриашон надоданд] ва бар онон ҷуз ҳалокат [-у табоҳӣ] наафзуданд');
INSERT INTO "pioneers" VALUES(11,102,'11:102','Ва чунин аст бозхости Парвардигорат, ки аҳолии шаҳрҳоеро, ки ситамгор [-у мушрик] буданд, фаро мегирифт. Бе гумон, бозхости ӯ дарднок ва сахт аст');
INSERT INTO "pioneers" VALUES(11,103,'11:103','Яқинан, дар ин [достонҳо, панд ва] нишонаест барои касе, ки аз азоби охират метарсад. Он [рӯз] рӯзест, ки мардумро барои [ҳисобрасӣ дар] он ҷамъ мекунанд ва он [рӯз] рӯзест, ки [тамоми аҳли маҳшар] онро мебинанд');
INSERT INTO "pioneers" VALUES(11,104,'11:104','Ва Мо онро ҷуз то муддате муайян ба таъхир намеандозем');
INSERT INTO "pioneers" VALUES(11,105,'11:105','Рӯзе биёяд, ки ҳеҷ кас ҷуз ба иҷозати Ӯ [Аллоҳ таоло] сухан намегӯяд, пас, бархе аз онон бадбахтанд ва [бархе дигар] некбахт');
INSERT INTO "pioneers" VALUES(11,106,'11:106','Аммо касоне, ки бадбахт шуданд, [дузахианд ва] дар оташ барояшон [бо ҳар даму боздам] фарёду нолае аст');
INSERT INTO "pioneers" VALUES(11,107,'11:107','То осмонҳо ва замин [боқӣ] аст, дар он [азоб] ҷовидонанд, магар он ки Парвардигорат бихоҳад [ки муваҳҳидони гунаҳгорро аз дузах хориҷ созад]. Бе тардид, Парвардигорат он чиро, ки бихоҳад, анҷом медиҳад');
INSERT INTO "pioneers" VALUES(11,108,'11:108','Ва аммо касоне, ки некбахт [ва саодатманд] шуданд, то осмонҳо ва замин [боқӣ] аст, ҷовидона дар биҳиштанд, магар он ки Парвардигорат бихоҳад [ки муваҳҳидони гунаҳгорро пеш аз вуруд ба биҳишт дар дузах муҷозот кунад. Ин неъмат] бахшише бепоён аст');
INSERT INTO "pioneers" VALUES(11,109,'11:109','Пас, аз [ботил будани] он чи инон [мушрикон] мепарастанд, дар тардид мабош. Онон ин маъбудонро намепарастанд, магар ба ҳамон шакле, ки падаронашон [низ] пештар мепарастиданд ва Мо саҳмашонро [аз азоб] бекамукост ба онон хоҳем дод');
INSERT INTO "pioneers" VALUES(11,110,'11:110','Ва ба ростӣ, [Мо] ба Мӯсо китоб додем, пас, дар он ихтилоф шуд. Ва агар пештар [дар мавриди муҳлат ба гунаҳгорон ва ҳисобрасӣ дар қиёмат] сухане аз ҷониби Парвардигорат баён нашуда буд, яқинан, [дар ҳамин дунё] миёнашон доварӣ мешуд [ва азоб нозил мегашт] ва ба ростӣ, ки онон [яҳудиён ва мушрикон] дар бораи [ҳаққонияти] ин [Қуръон] сахт дар тардиданд');
INSERT INTO "pioneers" VALUES(11,111,'11:111','Ва Парвардигорат [подоши] аъмоли ҳар кадомашро, ҳатман, бекаму кост хоҳад дод. Бе тардид, Ӯ ба он чи мекунанд, огоҳ аст');
INSERT INTO "pioneers" VALUES(11,112,'11:112','Пас, [эй паёмбар] чунон ки фармон ёфтаӣ, [дар баробари мушрикон] истиқомат кун ва касе, ки ҳамроҳи ту [ба пешгоҳи илоҳӣ] рӯ овардааст [низ бояд дар диндорӣ истиқомат кунад] ва саркашӣ накунед. Бе тардид, ӯ ба он чи мекунед, биност');
INSERT INTO "pioneers" VALUES(11,113,'11:113','Ба касоне, ки ситам карданд [кофирон] гароиш наёбед, ки [агар чунин кунед] оташи дузах ба шумо низ хоҳад расид ва дар баробари Аллоҳ таоло [ҳеҷ] дӯсте надоред ва он гоҳ ёрӣ намешавед');
INSERT INTO "pioneers" VALUES(11,114,'11:114','Ва дар ду тарафи рӯз [оғоз ва поёни рӯз] ва соате аз шаб намоз бигзор. Бе тардид, некиҳо бадиҳоро аз байн мебарад. Ин [сухан] пандест барои пандпазирон');
INSERT INTO "pioneers" VALUES(11,115,'11:115','Ва шикебоӣ кун,[ки] бе тардид, Аллоҳ таоло подоши накукоронро табоҳ намекунад');
INSERT INTO "pioneers" VALUES(11,116,'11:116','Пас, чаро дар миёни умматҳои пеш аз шумо, ҷуз андаке аз онон, ки [аз азоб] наҷоташон додем,[муъминон ва] хирадмандоне набуданд, ки [мардумро] аз фасод дар замин боздоранд? Ва касоне, ки ситам карданд, ба дунболи нозу неъмате, ки ёфта буданд, рафтанд ва гунаҳгор буданд');
INSERT INTO "pioneers" VALUES(11,117,'11:117','Ва [суннати] Парвардигорат чунин набудааст, ки шаҳрҳоро, дар ҳоле ки сокинонаш [афроде] накукор буданд, ба ситам нобуд кунад');
INSERT INTO "pioneers" VALUES(11,118,'11:118','Ва агар Парвардигорат мехост, [ҳамаи] мардумро як уммати [муваҳҳид ва дурусткор] қарор медод, вале [онон] ҳамвора мухталифанд');
INSERT INTO "pioneers" VALUES(11,119,'11:119','Магар касоне, ки Парвардигорат ба онон раҳм кард ва барои ҳамин ононро офаридааст ва ваъдаи Парвардигорат [чунин] таҳаққуқ пазируфтааст [ки]: «Ҳатман, дузахро аз ҷинну инсон [-и гунаҳгор] анбошта хоҳам сохт»');
INSERT INTO "pioneers" VALUES(11,120,'11:120','Ва [эй Муҳаммад] аз ахбори паёмбарон ин ҳамаро бар ту ҳикоят мекунем, то дилатро бо онҳо субот [-у оромиш] бахшем ва дар ин [саргузаштҳо] ҳақиқат бароят омадааст ва [ин достонҳо] пандест [барои кофирон] ва тазаккурест барои муъминон');
INSERT INTO "pioneers" VALUES(11,121,'11:121','Ва ба касоне, ки имон намеоваранд, бигӯ: «Шумо ба равиши худ амал кунед, мо [низ ба шеваи худ] амал мекунем');
INSERT INTO "pioneers" VALUES(11,122,'11:122','Ва мунтазир бошед, [ки] бе тардид, мо [низ] мунтазирем»');
INSERT INTO "pioneers" VALUES(11,123,'11:123','Ва [огоҳӣ аз] ғайби осмонҳо ва замин, аз они Аллоҳ таоло аст ва ҳамаи корҳо ба сӯйи ӯ бозгардонда мешавад. Пас, Ӯро бипараст ва бар Ӯ таваккал кун ва Парвардигорат аз он чи мекунед,[ҳаргиз] ғофил нест');
INSERT INTO "pioneers" VALUES(12,1,'12:1','Алиф, лом, ро. Ин оёти китоби равшангар аст');
INSERT INTO "pioneers" VALUES(12,2,'12:2','Мо онро Қуръоне [ба забони] арабӣ нозил кардем, бошад, ки [дар мафоҳимаш] бияндешед');
INSERT INTO "pioneers" VALUES(12,3,'12:3','[Эй паёмбар] мо беҳтарин достонро бо ваҳй кардани ин Қуръон бар ту ҳикоят мекунем ва мусалламан, пештар ту [аз ин достонҳо] бехабар будӣ');
INSERT INTO "pioneers" VALUES(12,4,'12:4','[Ёд кун аз] ҳангоме ки Юсуф ба падараш [Яъқуб] гуфт: «Падарҷон, ман [дар хоб] ёздаҳ ситора ва хуршеду моҳ дидам. Онҳоро дидам, ки бароям саҷда мекарданд»');
INSERT INTO "pioneers" VALUES(12,5,'12:5','[Яъқуб] Гуфт: «Писарам, хобатро барои бародаронат бозгӯ макун, ки бароят найранге меандешанд,[чаро ки] бе тардид, шайтон душмани ошкоре барои инсон аст');
INSERT INTO "pioneers" VALUES(12,6,'12:6','Парвардигорат туро ин гуна бармегузинад ва ба ту [илми] таъбири хобҳоро меомӯзад ва неъматашро бар ту ва бар хонадони Яъқуб тамом мегардонад, чунон ки пеш аз ин [низ] бар аҷдодат – Иброҳим ва Исҳоқ тамом кард. Бе тардид, Парвардигорат донову ҳаким аст»');
INSERT INTO "pioneers" VALUES(12,7,'12:7','Ба ростӣ, ки дар [достони] Юсуф ва бародаронаш, барои пурсишгарон [дар бораи Юсуф ибратҳо ва] нишонаҳост');
INSERT INTO "pioneers" VALUES(12,8,'12:8','Он гоҳ ки [ба якдигар] гуфтанд: «Юсуф ва бародараш [Бинёмин] назди падарамон аз мо маҳбубтаранд, дар ҳоле ки мо як гурӯҳ [-и неруманд] ҳастем. Ҳаққо, ки падарамон [бо ин меҳрварзии бедалел] дар гумроҳии ошкоре аст');
INSERT INTO "pioneers" VALUES(12,9,'12:9','[Яке аз бародарон гуфт] Юсуфро бикушед ё ӯро ба сарзамин [-и дурдаст]-е биафканед, то таваҷҷуҳи падаратон фақат ба [сӯйи] шумо бошад ва пас аз он [тавба кунед ва] афроде шоиста бошед»');
INSERT INTO "pioneers" VALUES(12,10,'12:10','Яке [дигар] аз онон гуфт: «Юсуфро накушед ва агар [мехоҳед] коре анҷом диҳед, ӯро дар қаъри чоҳ биандозед, то бархе аз мусофирон [-и корвон] ӯро баргиранд [ва бо худ ба ҷойе дур баранд]»');
INSERT INTO "pioneers" VALUES(12,11,'12:11','[Сипас назди Яъқуб рафтанд] Ва гуфтанд: «Падарҷон, чаро моро бар Юсуф амин намешуморӣ, ҳол он ки мо хайрхоҳаш ҳастем?');
INSERT INTO "pioneers" VALUES(12,12,'12:12','Фардо ӯро бо мо [ба дашт] бифрист, то [дар чаманҳо] бигардад ва бозӣ кунад ва мо ба хубӣ муроқибаш ҳастем»');
INSERT INTO "pioneers" VALUES(12,13,'12:13','[Яъқуб] Гуфт: «Ин ки ӯро бибаред, маро андуҳгин мекунад ва аз ин метарсам, ки гург ӯро бихӯрад ва шумо аз ӯ ғофил бошед»');
INSERT INTO "pioneers" VALUES(12,14,'12:14','Онон гуфтанд: «Бо вуҷуди ин ки мо як гурӯҳ [-и неруманд] ҳастем, агар [иҷозат диҳем, ки] гург ӯро бихӯрад, ҳатман, зиёнкор хоҳем буд»');
INSERT INTO "pioneers" VALUES(12,15,'12:15','Пас, чун вайро [ҳамроҳи худ] бурданд ва ҳамдаст шуданд, ки ӯро дар қаъри чоҳ биандозанд, ба ӯ ваҳй кардем, ки: «[Саранҷом] Ҳатман, ононро, дар ҳоле ки [дар бораат] ҳеҷ намедонанд, аз [чанду чуни] ин корашон огоҳ хоҳӣ сохт»');
INSERT INTO "pioneers" VALUES(12,16,'12:16','Ва шабонгоҳ гирякунон назди падарашон омаданд');
INSERT INTO "pioneers" VALUES(12,17,'12:17','Гуфтанд: «Падарҷон, мо [дуртар] рафтем, ки мусобиқа диҳем ва Юсуфро назди асоси худ гузоштем, ки гург ӯро хӯрд ва ҳарчанд ростгӯ бошем, ту [ҳаргиз сухани] моро бовар нахоҳӣ кард»');
INSERT INTO "pioneers" VALUES(12,18,'12:18','Ва пероҳани ӯро бо хуни дурӯғине [барои Яъқуб] оварданд. [Ӯ] Гуфт: «[Чунин нест] Балки [ҳавои] нафси шумо коре [ношоиста]-ро бароятон оростааст, пас, сабре наку [бароям беҳтар аст] ва Аллоҳ таоло дар [мавриди] он чи мегӯед, ёригар [-и ман] аст»');
INSERT INTO "pioneers" VALUES(12,19,'12:19','Ва корвоне омад ва онон обовари худро фиристоданд,пас,ӯ далвашро [дар чоҳ] андохт [ва чун Юсуфро боло кашид] гуфт: «Мужда диҳед! Ин як навҷавон аст» ва [пас аз наҷоташ] ӯро ҳамчун як коло[-и арзишманд аз дигарон] пинҳон доштанд, ва[-ле] Аллоҳ таоло ба он чи мекарданд, огоҳ буд');
INSERT INTO "pioneers" VALUES(12,20,'12:20','Ва [саранҷом] ӯро ба баҳое андак, чанд дирҳам, фурӯхтанд ва дар [бораи нигаҳ доштани] ӯ бемайл буданд');
INSERT INTO "pioneers" VALUES(12,21,'12:21','Ва он шахс аз [мардуми] Миср [Азиз], ки ӯро харида буд, ба ҳамсараш гуфт: «Мақомашро гиромӣ бидор. Умед аст, ки бароямон судманд бошад ё ӯро ба фарзандӣ бигирем». Ва инчунин буд, ки Юсуфро дар [он] сарзамин арҷманд [-у муқтадир] гардонидем, то ба ӯ таъбири хоб биёмӯзем; ва Аллоҳ таоло бар кори хеш [тавоно ва] чира аст, вале бештари мардум намедонанд');
INSERT INTO "pioneers" VALUES(12,22,'12:22','Ва чун [Юсуф ба авҷи] неруи ҷавонӣ расид, ба ӯ ҳикмату дониш ато кардем ва накукоронро инчунин подош медиҳем');
INSERT INTO "pioneers" VALUES(12,23,'12:23','Ва он зане, ки ӯ [Юсуф] дар хонааш буд, аз вай комҷӯӣ кард ва дарҳоро басту гуфт: «Биё [ки дар ихтиёри ту ҳастам]». [Юсуф] Гуфт: «Паноҳ бар Аллоҳ таоло! Он мард [шавҳари ту ва] сарвари ман аст [ва] ҷойгоҳамро гиромӣ доштааст [пас, чи гуна ба ӯ хиёнат кунам?] яқинан, ситамгорон растагор намешаванд»');
INSERT INTO "pioneers" VALUES(12,24,'12:24','Ва дар ҳақиқат, [он зан] қасди ӯ кард ва ӯ низ агар бурҳони Парвардигорашро надида буд, қасди он зан мекард. Мо инчунин [кардем], то бадӣ ва зишткориро аз ӯ дур созем. Ба ростӣ, ки ӯ аз бандагони мухлиси Мо буд');
INSERT INTO "pioneers" VALUES(12,25,'12:25','Ва ҳар ду ба тарафи дар шитофтанд ва [ҳамсари азизи Миср] пероҳани ӯро аз пушт пора кард ва [дар ин ҳангом] шавҳарашро дар остонаи дар ёфтанд.[Он] Зан гуфт: «Кайфари касе, ки қасди бад ба хонаводаи ту дошта бошад, чист, ҷуз ин ки зиндон шавад ё азобе дарднок [бубинад]»?');
INSERT INTO "pioneers" VALUES(12,26,'12:26','[Юсуф] Гуфт: «Ӯ аз ман комҷӯӣ кард» ва [дар он ҳангом] шоҳиде аз бастагони он зан шаҳодат дод: «Агар пероҳанаш аз ҷилав пора шуда бошад, ин зан рост мегӯяд ва он мард [Юсуф] дурӯғгӯст');
INSERT INTO "pioneers" VALUES(12,27,'12:27','Ва[-ле] агар пероҳанаш аз пушт пора шуда бошад, ин зан дурӯғ мегӯяд ва он мард ростгӯст»');
INSERT INTO "pioneers" VALUES(12,28,'12:28','Пас, чун [Азизи Миср] дид, ки пероҳани ӯ аз пушт пора шудааст, [ба ҳақиқат пай бурд ва] гуфт: «Ин аз найранги шумо [занон] аст, ба ростӣ, ки найранги шумо бузург аст');
INSERT INTO "pioneers" VALUES(12,29,'12:29','Эй Юсуф, аз ин [моҷаро] даргузар ва [ту эй зан] барои гуноҳат омурзиш бихоҳ, ки бе тардид хатокор будаӣ»');
INSERT INTO "pioneers" VALUES(12,30,'12:30','[Ҳангоме ки ин хабар мунташир шуд] Бархе аз занон дар шаҳр гуфтанд: «Ҳамсари Азиз [-и Миср] аз ғуломи [ҷавони] хеш комҷӯӣ мекунад [ва] сахт шефтааш гаштааст. Мо ӯро воқеан дар гумроҳии ошкоре мебинем»');
INSERT INTO "pioneers" VALUES(12,31,'12:31','Пас, чун [ҳамсари Азиз] найранг [-у бадгӯӣ]-и ононро шунид, [шахсеро барои даъват] ба суроғашон фиристод ва маҷлисе барояшон тартиб дод ва ба ҳар кадом [барои буридани ғизо] корде дод ва ба Юсуф гуфт: «Бар [ҷамъи] онон ворид шав». Пас, [занон] чун ӯро диданд, бисёр бузург [-у зебо] шумурданд ва [чунон маҳви ҷамолаш шуданд] дастҳояшонро захмӣ карданд ва гуфтанд: «Паноҳ бар Аллоҳ таоло! Ин башар нест, ин ҷуз фариштае бузургвор нест»');
INSERT INTO "pioneers" VALUES(12,32,'12:32','[Ҳамсари Азиз] Гуфт: «Ин [ҷавони зеборӯ] ҳамон аст, ки маро дар барои ӯ сарзаниш мекардед. [Оре] Дар ҳақиқат, ман аз ӯ комҷӯӣ намудам ва ӯ хештандорӣ кард ва [инак] агар он чиро ба ӯ дастур медиҳам, анҷом надиҳад, ҳатман, зиндонӣ мешавад ва мусалламан, аз хоршудагон хоҳад буд»');
INSERT INTO "pioneers" VALUES(12,33,'12:33','[Юсуф] Гуфт: «Парвардигоро, зиндон барои ман аз он чи [инҳо] маро ба сӯяш мехонанд, маҳбубтар аст ва агар найрангашонро аз ман нагардонӣ, ба онон гароиш меёбам ва [агар чунин кунам] нодон хоҳам буд»');
INSERT INTO "pioneers" VALUES(12,34,'12:34','Пас, Парвардигораш [дуои] ӯро иҷобат кард ва найрангашонро аз ӯ бозгардонид. Бе тардид, Ӯ шунавову доност');
INSERT INTO "pioneers" VALUES(12,35,'12:35','Он гоҳ пас аз он ки [Азизи Миср ва атрофиёнаш] нишонаҳо[-и покдомании Юсуф]-ро диданд, тасмим гирифтанд [барои сарпӯш гузоштан бар ин беобрӯӣ] ӯро то муддате зиндонӣ кунанд');
INSERT INTO "pioneers" VALUES(12,36,'12:36','Ва ду ҷавон ҳамроҳи ӯ вориди зиндон шуданд. [Рӯзе] Яке аз он ду гуфт: «Ман [дар хоб] худро дидам, ки [ангур барои] шароб мефишорам» ва дигарӣ гуфт: «Ман хоб дидаам, ки бар сари хеш [зарфи] ноне мебарам [ва] парандагон аз он мехӯранд. Моро аз таъбираш огоҳ кун, [ки] ҳатман, туро аз накукорон мебинем»');
INSERT INTO "pioneers" VALUES(12,37,'12:37','[Юсуф] Гуфт: «Ҳеҷ ғизое барои хӯрдан наздатон намеоваранд, магар он ки пеш аз оварданаш, шуморо аз [кайфияту] ҳақиқаташ огоҳ созам. Ин [ахбори ғайбӣ] аз онҳост, ки Парвардигорам ба ман омӯхтааст. Дар ҳақиқат, ман оини қавмеро, ки ба Аллоҳ таоло имон намеоваранд ва ба [сарои] охират [низ] куфр меварзанд, тарк кардам');
INSERT INTO "pioneers" VALUES(12,38,'12:38','Ва аз оини падаронам Иброҳим ва Исҳоқ ва Яъқуб пайравӣ кардам. Барои мо сазовор нест, ки чизеро шарики Аллоҳ таоло қарор диҳем. Ин [яктопарастӣ] аз фазли Аллоҳ таоло бар мо ва бар [ҳамаи] мардум аст, вале бештари мардум сипос намегузоранд');
INSERT INTO "pioneers" VALUES(12,39,'12:39','Эй дӯстони зиндонии ман, оё маъбудони пароканда [ва мутааддид] беҳтаранд, ё Аллоҳи ягонаи пирӯзманд?');
INSERT INTO "pioneers" VALUES(12,40,'12:40','Шумо ба ҷои ӯ [чизеро] намепарастед, магар номҳое [бемаънӣ], ки худ ва падаронатон бар онҳо ниҳодаед. Аллоҳ таоло ҳеҷ далеле бар [ҳақиқати] онҳо нозил накардааст. Фармонравоӣ, танҳо аз они Аллоҳ таоло аст [ва] фармон додаст, ки ҷуз Ӯро напарастед. Ин ҳамон дини рост ва устувор аст; вале бештари мардум намедонанд');
INSERT INTO "pioneers" VALUES(12,41,'12:41','Эй дӯстони зиндонии ман, яке аз шумо [озод мешавад] ва ба сарвари хеш шароб хоҳад нӯшонд ва аммо дигаре ба дор овехта мешавад ва парандагон аз [мағзи] сараш хоҳанд хӯрд. Амре, ки дар [мавриди] он аз ман назар хостед, [чунин] муқаддар шудааст»');
INSERT INTO "pioneers" VALUES(12,42,'12:42','Ва [Юсуф] ба яке аз он ду [нафар], ки донист раҳо мешавад, гуфт: «Аз ман назди сарварат [подшоҳ] ёд кун». Ва[-ле] шайтон ёд кардан [аз Юсуфро назди] сарвараш аз хотири вай [соқии подшоҳ] бурд ва ӯ [Юсуф] чандин сол [-и дигар низ] дар зиндон боқӣ монд');
INSERT INTO "pioneers" VALUES(12,43,'12:43','Ва [рӯзе] подшоҳ гуфт: «Ман [дар хоб] дидам, ки ҳафт гови фарбеҳро ҳафт гови лоғар мехӯрданд ва ҳафт хӯшаи сабз ва [ҳафт хӯшаи] дигарро хушк [дидам]. Эй бузургон, агар таъбири хоб мекунед, дар [бораи] хобам назар диҳед»');
INSERT INTO "pioneers" VALUES(12,44,'12:44','Онон гуфтанд: «[Инҳо] Хобҳое парешон аст ва мо ба таъбири ин [гуна] хобҳо доно нестем»');
INSERT INTO "pioneers" VALUES(12,45,'12:45','Ва яке аз он ду [зиндонӣ], ки наҷот ёфта буд, пас аз муддатҳо [Юсуф]-ро ба ёд овард [ва] гуфт: «Маро ба [зиндон] бифиристед, то дар бораи таъбири он [хоб] ба шумо хабар диҳам»');
INSERT INTO "pioneers" VALUES(12,46,'12:46','[Ӯ ба зиндон рафт ва гуфт] «Юсуф, эй марди ростгӯ, дар бораи ин хоб изҳори назар кун, ки ҳафт гови фарбеҳро ҳафт гови лоғар мехӯрданд ва ҳафт хӯшаи сабз ва [ҳафт хӯшаи] хушкидаи дигар бароямон таъбир кун, то назди мардум баргардам, шояд онҳо [таъбири ин хоб ва тавоноиҳои туро] бидонанд');
INSERT INTO "pioneers" VALUES(12,47,'12:47','[Юсуф] гуфт: «Ҳафт соли паёпай [бо ҷиддият] кишоварзӣ кунед ва он чиро, ки дарав мекунед, ҷуз андаке, ки мехӯред, бо хӯшааш [канор] бигзоред');
INSERT INTO "pioneers" VALUES(12,48,'12:48','Сипас баъд аз он [даврони равнақ] ҳафт [соли қаҳтии] сахт меояд, ки он чиро барои он солҳо [канор] гузоштаед, мехӯред, ба ҷуз андаке, ки онро [барои тухмӣ] захира мекунед');
INSERT INTO "pioneers" VALUES(12,49,'12:49','Сипас баъд аз он ҳафт [сол] соле фаро мерасад, ки борони фаровоне насиби мардум мешавад ва дар он сол, [бар асари фаровонӣ, аз меваҳо] ассора (афшурданиҳо) мегиранд»');
INSERT INTO "pioneers" VALUES(12,50,'12:50','Подшоҳ [чун ин таъбирро шунид] гуфт: «Ӯро назди ман биёваред». Пас, чун фиристода [-и подшоҳ] назди ӯ омад, [Юсуф] гуфт: «Назди сарварат бозгард ва аз ӯ бипурс, ки моҷарои заноне, ки дастҳои худро буриданд, чи буд? Ҳатман, Парвардигорам ба найранги онон огоҳ аст»');
INSERT INTO "pioneers" VALUES(12,51,'12:51','[Подшоҳ он занонро хост ва] Гуфт: «Ҳангоме ки Юсуфро ба сӯйи хеш даъват кардед, ҷараёни коратон чи буд»? Гуфтанд: «Паноҳ бар Аллоҳ таоло! Мо ҳеҷ гуноҳе дар ӯ суроғ надорем». [Дар ин ҳангом] Ҳамсари Азиз гуфт: «Акнун ҳақ ошкор шуд, ман [будам, ки] аз вай комҷӯӣ кардам [ва ӯ покдоманӣ варзид] ва яқинан, ростгӯст');
INSERT INTO "pioneers" VALUES(12,52,'12:52','Ин [эътироф] барои он аст, ки [Юсуф] бидонад, ки ман дар пинҳон ба ӯ хиёнат накардаам [ва дар ғиёбаш ҳақиқатро гуфтаам] ва ин ки Аллоҳ таоло найранги хоинонро ба ҷойе намерасонад');
INSERT INTO "pioneers" VALUES(12,53,'12:53','Ман ҳаргиз худро бегуноҳ намешуморам. Бе шак, нафс [-и васвасагари инсон ӯро] пайваста ба бадӣ фармон медиҳад, магар он ки Парвардигорам раҳм кунад. Бе тардид, Парвардигорам омурзандаи меҳрубон аст»');
INSERT INTO "pioneers" VALUES(12,54,'12:54','Ва подшоҳ гуфт: «Ӯ [ Юсуф]-ро наздам биёваред [то мушовири] хоси худ гардонам». Пас, чун [Юсуф наздаш омад ва] бо вай суҳбат кард, [подшоҳ] гуфт: «Ту имрӯз назди мо арҷманд [ва] амин ҳастӣ»');
INSERT INTO "pioneers" VALUES(12,55,'12:55','Юсуф] гуфт: «Маро бар [сарпарастии] хазонаҳои [молӣ ва ғизоии ин] сарзамин бигумор, [ки] бе тардид, ман нигаҳбоне доно ҳастам»');
INSERT INTO "pioneers" VALUES(12,56,'12:56','Ва ин гуна ба Юсуф дар он сарзамин манзалат [-у қудрат] додем, [он чунон] ки [метавонист ба ҳар ҷо биравад ва] дар ҳар ҷойе аз он ки мехост, иқомат мекард. Мо раҳмати худро ба ҳар ки бихоҳем мерасонем');
INSERT INTO "pioneers" VALUES(12,57,'12:57','Ва подоши охират барои касоне, ки имон овардаанд ва парҳезгорӣ кардаанд, беҳтар аст');
INSERT INTO "pioneers" VALUES(12,58,'12:58','Ва [чун сарзамини Канъонро хушксолӣ фаро гирифт] бародарони Юсуф [барои таҳияи гандум ба Миср] омаданд ва бар ӯ ворид шуданд. Пас, ӯ онҳоро шинохт, дар ҳоле ки онон вайро нашинохтанд');
INSERT INTO "pioneers" VALUES(12,59,'12:59','Пас, чун борҳояшонро омода кард, [ба онон] гуфт: «[Навбати оянда] Бародареро, ки аз падаратон доред, назди ман оваред. Оё намебинед, ки ман паймонаро тамом медиҳам ва беҳтарин мизбонам?');
INSERT INTO "pioneers" VALUES(12,60,'12:60','Пас, агар ӯро наздам наоваред, паймонае назди ман нахоҳед дошт ва наздикам нашавед»');
INSERT INTO "pioneers" VALUES(12,61,'12:61','[Бародарон] Гуфтанд: «Ӯро аз падараш хоҳем хост ва ҳатман, ин корро мекунем»');
INSERT INTO "pioneers" VALUES(12,62,'12:62','Ва [Юсуф] ба ғуломонаш гуфт: «Сармояҳояшонро [ки барои хариди ғалла овардаанд, махфиёна] дар борҳояшон бигузоред, шояд ҳангоме ки ба сӯйи хонаводаи худ бозгаштанд, онро [бубинанд ва] бишносанд. Умед, ки бозоянд»');
INSERT INTO "pioneers" VALUES(12,63,'12:63','Пас, чун ба сӯйи падарашон бозгаштанд, гуфтанд: «Падарҷон, паймона [ва саҳми ғаллаи навбати оянда]-ро аз мо боздоштанд, пас, бародарамон [Бинёмин]-ро бо мо бифирист, то саҳме [аз ғалла] бигирем ва мо ҳатман, муроқибаш хоҳем буд»');
INSERT INTO "pioneers" VALUES(12,64,'12:64','[Яъқуб] гуфт: «Оё ҳамон гуна, ки пештар шуморо нисбат ба бародараш [Юсуф] амин доштам, [ин бор ҳам] бар ӯ амин бидонам? Пас, Аллоҳ таоло беҳтарин нигаҳбон аст ва ӯ меҳрубонтарини меҳрубонон аст»');
INSERT INTO "pioneers" VALUES(12,65,'12:65','Ва чун бори худро кушоданд, сармояашонро ёфтанд, ки ба онон бозгардонда шуда буд. Гуфтанд: «Падарҷон, [дигар] чи мехоҳем? Ин сармояи мост, ки ба мо бозгардонида шудааст, [бад-ин васила] барои хонаводаи худ озуқа меоварем ва бародарамонро [низ] ҳифозат мекунем ва [бо бурдани Бинёмин] як бори шутур изофа мегирем. Ин паймона [барои Азизи Миср кори] осонест');
INSERT INTO "pioneers" VALUES(12,66,'12:66','[Яъқуб] Гуфт: «Ҳаргиз ӯро бо шумо нахоҳам фиристод, то бо ман ба номи Аллоҳ таоло паймони устуворе бибандед, ки ҳатман ӯро назди ман бозмеоваред, магар он ки гирифтор шавед [ва ҳеҷ як наҷот наёбед]. Пас, чун [аҳду] паймони устувори хеш ба ӯ доданд, [Яъқуб] гуфт: «Аллоҳ таоло бар он чи мегӯем, [гувоҳу] нигаҳбон аст»');
INSERT INTO "pioneers" VALUES(12,67,'12:67','Ва [ҳамчунон] гуфт: «Эй писаронам, [барои он ки ҷалби таваҷҷуҳ накунед] аз як дар ворид нашавед, балки аз дарҳои мухталиф дохил шавед ва [албатта, бо ин супориш] наметавонам чизе аз [қазо ва қадари] Аллоҳро аз шумо дур кунам. Ҳукм ҷуз барои Аллоҳ таоло нест. Бар Ӯ таваккал кардам ва таваккалкунандагон бояд бар Ӯ таваккал кунанд');
INSERT INTO "pioneers" VALUES(12,68,'12:68','Ва чун ҳамон гуна ки падарашон ба онон дастур дода буд, ворид шуданд. [Ин шеваи вуруд] Эшонро аз [қазои] Аллоҳ таоло бознадошт, вале [ҳар чи буд] ниёзи қалби Яъқубро бароварда кард. Ӯ аз [баракати] он чи бад-ӯ омӯхта будем, дорои донише фаровон буд, вале бештари мардум намедонанд');
INSERT INTO "pioneers" VALUES(12,69,'12:69','Ва чун [бародарон] бар Юсуф ворид шуданд, бародараш [Бинёмин]-ро назди худ ҷой дод [ва ба оҳистагӣ ба ӯ] гуфт: «Ман бародарат ҳастам, пас, аз корҳое, ки [бародаронамон] мекарданд, андуҳгин мабош»');
INSERT INTO "pioneers" VALUES(12,70,'12:70','Пас, ҳангоме ки борҳояшонро омода кард, ҷоми [подшоҳ]-ро дар бори бародараш [Бинёмин] гузошт, он гоҳ нидодиҳандае бонг баровард [ки]: «Эй корвониён, ҳатман, шумо дузд ҳастед»');
INSERT INTO "pioneers" VALUES(12,71,'12:71','[Бародарони Юсуф] Рӯ ба онон карданд ва гуфтанд: «Чи гум кардаед»?');
INSERT INTO "pioneers" VALUES(12,72,'12:72','[Коргузорон] Гуфтанд: «Паймонаи подшоҳро гум кардаем ва [он ки бонг бароварда буд, гуфт] ҳар ки онро биёварад, як бори шутур [ҷоиза] дорад ва ман зомини ин [подош] ҳастам»');
INSERT INTO "pioneers" VALUES(12,73,'12:73','[Бародарони Юсуф] Гуфтанд: «Ба Аллоҳ таоло савганд, шумо медонед, ки мо наёмадаем, то дар ин сарзамин фасод кунем ва мо [ҳаргиз] дузд набудаем»');
INSERT INTO "pioneers" VALUES(12,74,'12:74','[Онон] Гуфтанд: «Пас, агар дурӯғгӯ бошед, кайфараш чист»?');
INSERT INTO "pioneers" VALUES(12,75,'12:75','Гуфтанд: «Кайфари касе, ки [он паймона] дар бораш пайдо шавад, [ин аст, ки] худаш кайфари он бошад [ва ба бардагии шумо дарояд]. Мо ситамгоронро ин гуна муҷозот мекунем»');
INSERT INTO "pioneers" VALUES(12,76,'12:76','Пас, [Юсуф] пеш аз бори бародараш, шурӯъ ба [ҷустуҷӯи] борҳои онон кард, сипас он [паймона]-ро аз бори бародараш берун кашид. Ин гуна барои Юсуф чораандешӣ кардем, [то битавонад Бинёминро назди худ нигаҳ дорад, зеро ӯ] тибқи оини подшоҳи [Миср] наметавонист бародарашро боздошт кунад, магар ин ки Аллоҳ таоло бихоҳад. Мо дараҷоти ҳар киро бихоҳем боло мебарем ва [бидонед, ки] фаротар аз ҳар соҳибдонише донишваре ҳаст');
INSERT INTO "pioneers" VALUES(12,77,'12:77','[Бародарон] Гуфтанд: «Агар ӯ дуздӣ кардааст [шигифт нест, чаро ки] бародараш [низ] пеш аз ин дуздӣ карда буд. Пас, Юсуф онро дар дили хеш пинҳон дошт ва [нороҳатиаш] бар онон ошкор накард [ва бо худ] гуфт: «Шумо аз назари ҷойгоҳ [аз он чи вонамуд мекунед], бадтаред ва Аллоҳ таоло ба он чи баён мекунед, донотар аст»');
INSERT INTO "pioneers" VALUES(12,78,'12:78','Гуфтанд: «Эй Азиз, ӯ падари пире дорад [ки тоқати дурии фарзандашро надорад], пас, яке аз моро ба ҷойи ӯ бигир. Ба ростӣ, ки мо туро аз накукорон мебинем»');
INSERT INTO "pioneers" VALUES(12,79,'12:79','[Юсуф] Гуфт: «Паноҳ бар Аллоҳ таоло, ки ҷуз он касеро, ки колоямонро наздаш ёфтаем, боздошт кунем! Он гоҳ [агар чунин кунем] ҳатман, ситамгор хоҳем буд»');
INSERT INTO "pioneers" VALUES(12,80,'12:80','Пас, чун [бародарон] аз ӯ ноумед шуданд, наҷвокунон ба гӯшае рафтанд. [Бародари] Бузургашон гуфт: «Магар намедонед, ки падаратон бо номи Аллоҳ таоло паймоне устувор аз шумо гирифтааст ва пештар [низ] дар мавриди Юсуф чи кӯтоҳие кардед? Ман ҳаргиз аз ин сарзамин намеравам, то падарам ба ман иҷозати [бозгашт] диҳад ё [он ки] Аллоҳ таоло дар бораам доварӣ кунад, [ки] бе тардид, Ӯ беҳтарин довар аст');
INSERT INTO "pioneers" VALUES(12,81,'12:81','[Шумо] Назди падаратон бозгардед ва бигӯед: «Эй падар, писарат дуздӣ кард ва мо ҷуз ба он чи медонистем, гувоҳӣ надодем ва аз ғайб огоҳ набудем [ки бидонем дуздӣ мекунад]');
INSERT INTO "pioneers" VALUES(12,82,'12:82','Ва аз [мардуми] шаҳре, ки дар он будем ва аз корвоне, ки бо он омадем, бипурс ва мо, ҳатман, ростгӯем»');
INSERT INTO "pioneers" VALUES(12,83,'12:83','[Яъқуб] Гуфт: «[Чунин нест] Балки ҳавои нафси шумо кор [-и ношоиста]-ро бароятон ороста аст, пас, [чораи кор] сабре накуст. Умед аст, ки Аллоҳ таоло ҳамаи ононро ба ман бирасонад. Бе тардид, Ӯ донои ҳаким аст»');
INSERT INTO "pioneers" VALUES(12,84,'12:84','Ва аз онон рӯ бигардонд ва гуфт: «Дареғ аз Юсуф»! Ва чашмонаш аз шиддати андуҳ [ва гиристан барои фарзандаш] сафед шуд ва ҳамчунон андуҳи худро фурӯ мехӯрд');
INSERT INTO "pioneers" VALUES(12,85,'12:85','[Писаронаш] Гуфтанд: «Ба Аллоҳ таоло савганд, ту [он қадр] пайваста аз Юсуф ёд мекунӣ, то [оқибат] ба сахтӣ бемор шавӣ ё бимирӣ»');
INSERT INTO "pioneers" VALUES(12,86,'12:86','[Яъқуб] Гуфт: «Ман аз дарду андуҳи хеш ба [даргоҳи] Аллоҳ таоло менолам ва аз [лутфу бузургии] Аллоҳ таоло чизҳое медонам, ки шумо намедонед»');
INSERT INTO "pioneers" VALUES(12,87,'12:87','Эй писаронам, биравед ва дар бораи Юсуф ва бародаронаш ҷустуҷӯ кунед [ва хабар бигиред] ва аз раҳмати Аллоҳ таоло маъюс нашавед, [чаро ки] ҷуз гурӯҳи кофирон, касе аз раҳмати Аллоҳ таоло маъюс намегардад»');
INSERT INTO "pioneers" VALUES(12,88,'12:88','Пас, чун [писарони Яъқуб ба Миср рафтанд ва] бар ӯ [ Юсуф] ворид шуданд, гуфтанд: «Эй Азиз, ба мо ва хонадонамон [дар асари фақру хушксолӣ] сахтии фаровоне расидааст ва [инак] колои ночиз [ва моли андаке барои хариди ғалла] бо худ овардаем, пас, паймонаро бароямон комил кун ва бар мо садақа [ва бахшиш] кун. Бе тардид, Аллоҳ таоло бахшандагонро подош медиҳад»');
INSERT INTO "pioneers" VALUES(12,89,'12:89','[Юсуф] Гуфт: «Оё донистед, ки вақте [аз оқибати коратон] бехабар будед, дар ҳаққи Юсуф ва бародараш чи кардед»?');
INSERT INTO "pioneers" VALUES(12,90,'12:90','[Онон] Гуфтанд: «Оё ба ростӣ, ту [ҳамон] Юсуфӣ»? Ӯ гуфт: «[Оре] Ман Юсуфам ва ин бародари ман аст. Яқинан, Аллоҳ таоло бар мо миннат ниҳод. Ба ростӣ, ҳар ки парҳезкорӣ ва шикебоӣ кунад, пас, [бидонад, ки] бе тардид, Аллоҳ таоло подоши накукоронро табоҳ намекунад»');
INSERT INTO "pioneers" VALUES(12,91,'12:91','[Бародаронаш] Гуфтанд: «Ба Аллоҳ таоло савганд, [ки] яқинан, Аллоҳ таоло туро бар мо бартарӣ додааст ва мо, ҳатман, хатокор будем»');
INSERT INTO "pioneers" VALUES(12,92,'12:92','[Юсуф] Гуфт: «Имрӯз ҳеҷ сарзанише бар шумо нест. Аллоҳ таоло шуморо меомурзад ва Ӯ меҳрубонтарини меҳрубонон аст');
INSERT INTO "pioneers" VALUES(12,93,'12:93','[Акнун] Ин пероҳани маро бибаред ва бар рӯи падарам биандозед, то бино гардад ва [сипас] ҳамаи хонаводаи худро назди ман биёваред»');
INSERT INTO "pioneers" VALUES(12,94,'12:94','Ва чун корвон [аз Миср ба сӯйи Канъон] ба роҳ афтод, падарашон [ба атрофиёнаш] гуфт: «Агар маро камхирад нашуморед, [мегӯям, ки] яқинан, бӯйи Юсуфро эҳсос мекунам»');
INSERT INTO "pioneers" VALUES(12,95,'12:95','[Онон] Гуфтанд: «Ба Аллоҳ таоло савганд, ки ҳатман, [боз ҳам] дар иштибоҳи деринаат ҳастӣ»');
INSERT INTO "pioneers" VALUES(12,96,'12:96','Пас, чун муждарасон омад ва он [пероҳан]-ро бар чеҳраи ӯ афканд, пас, ногаҳон бино шуд [ва] гуфт: «Оё ба шумо нагуфтам, ки ман аз [лутфу бузургии] Аллоҳ таоло чизҳое медонам, ки шумо намедонед»?');
INSERT INTO "pioneers" VALUES(12,97,'12:97','Гуфтанд: «Эй падар, барои мо [ба хотири] гуноҳонамон омурзиш бихоҳ, [чаро ки] яқинан, мо гунаҳгор будем»');
INSERT INTO "pioneers" VALUES(12,98,'12:98','[Яъқуб] Гуфт: «Ба зудӣ бароятон аз Парвардигорам омурзиш металабам. Бе тардид, Ӯ омурзандаи меҳрубон аст»');
INSERT INTO "pioneers" VALUES(12,99,'12:99','Пас, [ҳамагӣ ба Миср рафтанд ва] чун бар Юсуф ворид шуданд, ӯ падару модарашро [дар оғӯш гирифт ва] назди худ ҷой доду гуфт: «[Ҳамагӣ ба Миср дароед, ки иншоаллоҳ [аз ҳар гуна озоре] эмин хоҳед буд»');
INSERT INTO "pioneers" VALUES(12,100,'12:100','Ва падару модарашро бар тахт нишонд ва [ҳамагӣ] барояш ба саҷда афтоданд ва [Юсуф] гуфт: «Падарҷон, ин таъбир [-и ҳамон] хобам аст, ки пештар дида будам. Парвардигорам онро рост гардонид [ва таҳаққуқ бахшид]. Яқинан, Ӯ ба ман некӣ кард, ки маро аз зиндон берун овард ва пас аз он ки шайтон миёни ману бародаронам фитна барпо кард, шуморо аз биёбон [-и Канъон ба Миср] овард. Бе тардид, Парвардигорам дар он чи мехоҳад, борикбин аст. Ба ростӣ, ки Ӯ донои ҳаким аст');
INSERT INTO "pioneers" VALUES(12,101,'12:101','Парвардигоро, маро аз фармонравоӣ [баҳрае] ато кардӣ ва илми таъбири хобҳо ба ман омӯхтӣ. Эй Падидоварандаи осмонҳо ва замин, Туӣ, ки дар дунё ва охират [дӯсту] корсози манӣ. Маро мусулмон [фармонбардор] бимирон ва ба шоистагон мулҳақ фармо»');
INSERT INTO "pioneers" VALUES(12,102,'12:102','Эй паёмбар] ин [достон] аз хабарҳои ғайб аст, ки ба ту ваҳй мекунем ва ҳангоме ки [бародарони Юсуф алайҳи вай] бадандешӣ мекарданд ва найранг мезаданд, ту наздашон набудӣ');
INSERT INTO "pioneers" VALUES(12,103,'12:103','Ва бештари мардум муъмин нестанд, ҳатто агар [барои имон оварданашон] ҳирс биварзӣ');
INSERT INTO "pioneers" VALUES(12,104,'12:104','Ту аз онон барои ин [рисолат] подош намехоҳӣ. Ин [Қуръон чизе] нест, магар панде барои ҷаҳониён');
INSERT INTO "pioneers" VALUES(12,105,'12:105','Ва чи бисёр нишонаҳо дар осмонҳо ва замин вуҷуд дорад, ки бар онҳо мегузаранд, ва [-ле] аз онҳо рӯй бармегардонанд [ва таваҷҷуҳ намекунанд]');
INSERT INTO "pioneers" VALUES(12,106,'12:106','Ва бештарашон [дар зоҳир] ба Аллоҳ таоло имон намеоваранд, магар он ки [дар амал ба навъе] мушриканд');
INSERT INTO "pioneers" VALUES(12,107,'12:107','Оё [мушрикон] эмин ҳастанд аз ин ки азобе фарогир аз ҷониби Аллоҳ таоло бар онон фуруд ояд ё дар ҳоле ки ғофиланд, ногаҳон қиёмат бар эшон фаро расад?');
INSERT INTO "pioneers" VALUES(12,108,'12:108','[Эй паёмбар, ба мардум] бигӯ: «Ин роҳи ман аст. Ман бо далели ошкор ба сӯйи Аллоҳ таоло даъват мекунам ва касоне, ки аз ман пайравӣ карданд [низ чунин мекунанд] ва Аллоҳ таоло поку муназзаҳ аст ва ман аз мушрикон нестам»');
INSERT INTO "pioneers" VALUES(12,109,'12:109','Ва Мо пеш аз ту ҷуз мардоне аз аҳли [ҳамин] шаҳрҳо [-ро ба паёмбарӣ] нафиристодаем, ки ба онон ваҳй мекардем. Оё дар замин гардиш накардаанд, то бубинанд саранҷоми касоне, пеш аз онон буданд, чӣ гуна будааст? Ва яқинан, сарои охират барои парҳезгорон беҳтар аст. Оё намеандешед?');
INSERT INTO "pioneers" VALUES(12,110,'12:110','[Мо ҳамчунон ба кофирон муҳлат медодем] То он гоҳ ки паёмбарон [аз нузули азоб] маъюс шуданд ва [мардум] пиндоштанд, ки ба онон дурӯғ гуфта шудааст. [Дар ин ҳангом] Ёрии Мо ба суроғашон омад. Пас, ҳар киро хостем, наҷот ёфт, ва[-ле] азоби сахти Мо аз гурӯҳи муҷримон бозгардонида намешавад');
INSERT INTO "pioneers" VALUES(12,111,'12:111','Яқинан дар достони онон барои хирадмандон ибратест. Ин [Қуръон] сухане нест, ки ба дурӯғ сохта шуда бошад, балки тасдиқкунандаи он чи [аз китобҳое] аст, ки пеш аз он [омада] аст ва равшангари ҳама чиз аст ва раҳнамуду раҳмате аст барои гурӯҳе, ки имон меоварад');
INSERT INTO "pioneers" VALUES(13,1,'13:1','Алиф, лом, мим, ро. Ин оёти китоби [осмонӣ] ва он чи аз [сӯйи] Парвардигорат бар ту нозил шудааст, ҳақ аст, вале бештари мардум имон намеоваранд');
INSERT INTO "pioneers" VALUES(13,2,'13:2','Аллоҳ таоло аст, ки осмонҳоро бидуни сутунҳое, ки онҳоро бубинед, барафрошт, он гоҳ бар Арш қарор гирифт ва хуршеду моҳро ба хидмат [-и инсон] гумоштааст, ки ҳар як то замоне муайян дар [мадори худ] равон бошанд. Ӯ кор [-и ҷаҳон]-ро тадбир мекунад ва оёти [хеш]-ро ба равшани баён медорад, бошад, ки ба дидори Парвардигоратон яқин пайдо кунед');
INSERT INTO "pioneers" VALUES(13,3,'13:3','Ва Ӯст он ки заминро бигустард ва дар он кӯҳҳои устувор ва ҷӯйборҳо падид овард ва дар он аз ҳар гуна мевае ду гуна офарид, шабро ба рӯз [ва рӯзро ба шаб] мепӯшонад. Ҳатман, дар инҳо барои мардуме, ки меандешанд, нишонаҳое [аз қудрати Парвардигор] аст');
INSERT INTO "pioneers" VALUES(13,4,'13:4','Ва дар замин қитаоте аст дар канори ҳам [ки бо якдигар мутафовитанд] ва [низ] боғҳое аз ангур ва киштзорҳову дарахтони хурмо, ки [нару модаанд ва] гоҳ аз як реша мерӯянд ва гоҳ аз решаҳое мутафовитанд ва [ҳамаи онҳо] бо як об сероб мешаванд. Ва [бо ин ҳол] баъзе аз онҳоро дар [таъму хосияти] мева бар дигаре бартарӣ медиҳем. Бе тардид, дар ин [амр низ] барои мардуме, ки хирад меварзанд, нишонаҳо [-и равшане] ҳаст');
INSERT INTO "pioneers" VALUES(13,5,'13:5','Ва [эй паёмбар] агар [аз беимонии кофирон] тааҷҷуб мекунӣ, аҷиб [-тар] гуфтори онон аст, ки [дар бораи миод мегӯянд]: «Оё вақте хок шудем, воқеан [зинда мешавем ва] дар офариниши ҷадиде хоҳем буд»? Инонанд, ки ба Парвардигорашон куфр варзидаанд ва дар гарданҳояшон [аз оташ] занҷирҳост ва онон аҳли оташанд [ва] дар он ҷовидонанд');
INSERT INTO "pioneers" VALUES(13,6,'13:6','Ва [эй паёмбар, мушрикон] пеш аз некӣ [раҳмат] ба шитоб аз ту бадӣ [азоб]-ро дархост мекунанд ва ҳол он ки пеш аз эшон [низ бар кофирон] уқубатҳои ибратомӯз рафтааст ва ба ростӣ, Парвардигорат нисбат ба мардум, бо вуҷуди ситамгориашон омӯрзандааст ва бешак Парвардигорат сахткайфар аст');
INSERT INTO "pioneers" VALUES(13,7,'13:7','Кофирон мегӯянд: «Чаро нишонаҳо [ва муъҷизоте ҳаммонанди Мӯсо ва Исо] аз Парвардигораш бар ӯ нозил нашудааст»? [Эй паёмбар] бе тардид, ту фақат бимдиҳандаӣ ва ҳар қавме [паёмбари] ҳидоятгаре дорад');
INSERT INTO "pioneers" VALUES(13,8,'13:8','Аллоҳ таоло медонад он чиро, ки ҳар модае [дар раҳим] бор мегирад ва [низ] он чиро, ки раҳимҳо мекоҳанд ва он чиро меафзоянд ва ҳар чизе назди Ӯ ба андоза [ва дақиқ] аст');
INSERT INTO "pioneers" VALUES(13,9,'13:9','[Ӯ] Донои ниҳону ошкор [ва] бузургу баландмартаба аст');
INSERT INTO "pioneers" VALUES(13,10,'13:10','[Эй мардум] Ҳар касе аз шумо, ки сухани худро пинҳон кунад ё онро ошкоро баён созад ва ҳар ки дар [торикии] шаб пинҳон гардад ё дар рӯз [ба дунболи анҷоми корҳояш] биравад, [дар илми илоҳӣ] яксон аст [ва Ӯ ҳамаро медонад]');
INSERT INTO "pioneers" VALUES(13,11,'13:11','Ӯ [Аллоҳ таоло] фариштагоне дорад, ки ҳамвора [бандаи] Ӯро аз пешорӯ ва аз пушти сараш ба фармони Аллоҳ таоло ҳифз мекунанд. Бе гумон, Аллоҳ таоло ҳол [ва сарнавишти накуи] ҳеҷ қавмеро тағйир намедиҳад, магар он ки онон он чиро, ки дар дилҳояшон доранд, дигаргун созанд [ва муртакиби гуноҳ шаванд]. Ва ҳангоме ки Аллоҳ таоло барои қавме [гирифторӣ ва] бадӣ бихоҳад, ҳеҷ чиз монеаш нахоҳад шуд ва онон ҷуз [Зоти поки] Ӯ ҳеҷ корсозе надоранд');
INSERT INTO "pioneers" VALUES(13,12,'13:12','Ӯст, ки барқро барои бим [соиқа ва оташ] ва умед [борон] ба шумо менамоёнад ва абрҳои гаронборро падид меоварад');
INSERT INTO "pioneers" VALUES(13,13,'13:13','Раъд ба ситоиши Ӯ ва фариштагон аз хавфу хашят Ӯро тасбеҳ мегӯянд ва [Ӯст, ки] соиқаҳоро мефиристад. Пас, он [соиқаи маргбор] ба ҳар ки Ӯ бихоҳад, бархӯрд мекунад ва онон [кофирон] дар бораи [ягонагии] Аллоҳ таоло муҷодала мекунанд ва Ӯ сахтгир [-у сахткайфар] аст');
INSERT INTO "pioneers" VALUES(13,14,'13:14','Даъват ба ҳақ [тавҳид] аз они Ӯст ва [бутҳо ва дигар] касонеро, ки [мушрикон] ба ҷойи Ӯ мехонанд, [ҳаргиз] ба эшон посухе намедиҳанд, магар монанди касе, ки ду дасташро ба сӯйи об бикушояд, то [об] ба даҳонаш бирасонад, дар ҳоле ки [ҳеҷ обе] ба [даҳони] ӯ намерасад ва дуои кофирон [дар баробари бутҳояшон] ҷуз дар гумроҳӣ нест');
INSERT INTO "pioneers" VALUES(13,15,'13:15','Ва ҳар ки [ва ҳар чи] дар осмонҳо ва замин аст, хоҳу нохоҳ ва [ҳатто] сояҳояшон бомдоду шабонгоҳ барои Аллоҳ таоло саҷда [ва фурӯтанӣ] мекунанд');
INSERT INTO "pioneers" VALUES(13,16,'13:16','[Эй Паёмбар, ба мушрикон] бигӯ: « Парвардигори осмонҳо ва замин кист»? Бигӯ: «Аллоҳ таоло». [Сипас] Бигӯ: «Оё ба ҷойи Ӯ [дӯстону] корсозоне баргузидаед, ки [ҳатто] молики суду зиёни худ нестанд»? Бигӯ: «Оё нобино ва бино баробаранд? Ё торикиҳо ва нур яксонанд? Ё онон шариконе барои Аллоҳ таоло қарор додаанд, ки ҳамчун офариниши Ӯ офаридаанд ва [дар натиҷа амри] офариниш бар онон муштабаҳ шудааст»? Бигӯ: «Аллоҳ таоло офаринандаи ҳама чиз аст ва Ӯ ягонаи пирӯзманд аст');
INSERT INTO "pioneers" VALUES(13,17,'13:17','[Аллоҳ таоло] Аз осмон обе фурӯ борид, он гоҳ рӯдҳо ҳар як ба андозаи [ғунҷоиши] худ ҷорӣ шуданд ва селоб кафк рӯйи худ баровард ва аз он чи бар оташ меафрузанд [ва мегудозанд], то зевар ё колое ба даст оваранд, [низ] кафке ҳаммонанди он [аз об бармеояд]. Аллоҳ таоло ҳаққу ботилро инчунин мисол мезанад. Ва аммо кафк ба каноре меравад ва нест мегардад, вале он чи ба мардум суд мерасонад, бар замин [боқӣ] мемонад. Аллоҳ таоло масалҳо инчунин баён мекунад');
INSERT INTO "pioneers" VALUES(13,18,'13:18','Барои касоне, ки [даъвати] Парвардигорашонро иҷобат карданд, подоши неки [биҳишт дар пеш] аст ва касоне, ки [даъвати[ Ӯро иҷобат накарданд, агар тамоми он чи дар замин аст ва [низ] ҳаммонандашро бо он доштанд, яқинан [ҳозир буданд, ки ҳамаро] барои раҳоии хеш [аз азоб] бипардозанд [вале аз эшон пазируфта нахоҳад шуд]. Онон ҳисоби баде доранд [ва бозхосте бисёр сахт] ва ҷойгоҳашон дузах аст ва чи бад ҷойгоҳе ҳаст!');
INSERT INTO "pioneers" VALUES(13,19,'13:19','Оё касе, ки медонад, ки он чи аз [тарафи] Парвардигорат бар ту нозил шуда, ҳақ аст, монанди касест, ки [нисбат ба ҳақ] нобиност? Танҳо хирадмандон панд мегиранд.');
INSERT INTO "pioneers" VALUES(13,20,'13:20','[Ҳамон] Касоне, ки ба паймони Аллоҳ таоло вафо мекунанд ва аҳд намешикананд');
INSERT INTO "pioneers" VALUES(13,21,'13:21','Ва касоне, ки ҳар он чиро Аллоҳ таоло ба пайванд кардани он фармон додааст, мепайванданд [силаи раҳим мекунанд] ва аз Парвардигорашон метарсанд ва аз сахтии ҳисоб меҳаросанд');
INSERT INTO "pioneers" VALUES(13,22,'13:22','Ва касоне, ки барои касби хушнудии Парвардигорашон шикебоӣ кардаанд ва намоз барпо доштаанд ва аз он чи ба онон рӯзӣ додаем, пинҳону ошкоро инфоқ кардаанд ва бадиро бо некӣ мезудоянд. Ононанд, ки нек фарҷоме хоҳанд дошт.');
INSERT INTO "pioneers" VALUES(13,23,'13:23','[Подошашон] Боғҳои ҷовидон [-и биҳишт аст], ки онон ва ҳар як аз падарон ва ҳамсарону фарзандонашон, ки накукор будаанд, ба он ворид мешаванд ва фариштагон аз ҳар даре бар онон дармеоянд');
INSERT INTO "pioneers" VALUES(13,24,'13:24','[Ва ба онон мегӯянд] «Салом бар шумо ба [подоши] он чи [бар имонатон] шикебоӣ кардаед. Пас, чи накуст фарҷоми он саро!»');
INSERT INTO "pioneers" VALUES(13,25,'13:25','Ва касоне, ки паймони Аллоҳро пас аз устувор карданаш мешикананд ва он чиро, ки Аллоҳ таоло ба пайвастанаш фармон додааст [силаи раҳим] қатъ мекунанд ва дар замин [фитнаву] фасод мекунанд, инонанд, ки [дар охират] лаънат ва бад фарҷоме доранд');
INSERT INTO "pioneers" VALUES(13,26,'13:26','Аллоҳ таоло [неъмату] рӯзиро бар ҳар ки бихоҳад, густурда медорад ва ё танг мегирад ва [кофирон] ба зиндагии дунё шод шуданд, ва[-ле] зиндагии дунё дар [баробари] охират ҷуз баҳрае [ночиз] нест');
INSERT INTO "pioneers" VALUES(13,27,'13:27','Ва касоне, ки куфр варзиданд, мегӯянд: «Чаро нишонае аз [сӯйи] Парвардигораш бар ӯ нозил нашудааст»? Бигӯ: «Бе тардид, Аллоҳ таоло ҳар киро, ки бихоҳад гумроҳ мекунад ва ҳар киро, ки тавба намояд, ба сӯйи хеш ҳидоят мекунад»');
INSERT INTO "pioneers" VALUES(13,28,'13:28','[Ҳамон] Касоне, ки имон овардаанд ва дилҳояшон ба ёди Аллоҳ таоло ором мегирад. Бидонед, ки бо ёди Аллоҳ таоло аст, ки дилҳо ором мегирад');
INSERT INTO "pioneers" VALUES(13,29,'13:29','Касоне, ки имон овардаанд ва корҳои шоиста кардаанд, хушо бар онон! Ва нек саранҷоме доранд');
INSERT INTO "pioneers" VALUES(13,30,'13:30','[Эй паёмбар] Бад-ин сон туро байни уммате фиристодем, ки пеш аз онҳо умматҳое омада [ва рафта] буданд, то он чи бар ту ваҳй кардаем, бар онон бихонӣ ва ҳол он ки онон ба [Парвардигори] раҳмон куфр меварзанд. Бигӯ: «Ӯ Парвардигори ман аст [ва] маъбуде [барҳақ] ҷуз Ӯ нест. Бар Ӯ таваккал кардам ва бозгаштам ба сӯйи Ӯст»');
INSERT INTO "pioneers" VALUES(13,31,'13:31','Ва [ҳатто] агар Қуръоне буд, ки кӯҳҳо аз [ҳайбати] он ба ҳаракат дармеомад ва замин пора-пора мешикофт ё мурдагон аз [тиловати] он ба сухан дармеомаданд, [боз ҳам кофирон имон намеоварданд]. Амри [тадбири ҷаҳон ва нузули муъҷизот] аз они Аллоҳ таоло аст. Оё касоне, ки имон овардаанд, намедонанд, ки агар Аллоҳ таоло мехост, ҳатман, ҳамаи мардумро ҳидоят мекард? Ва касоне, ки куфр варзиданд, пайваста ба [сазои] он чи кардаанд, гирифтори мусибати кӯбандае мешаванд ё [азоби илоҳӣ] наздики хонаҳояшон фуруд меояд ё [саранҷом] ваъдаи Аллоҳ таоло фаро мерасад. Бе тардид, Аллоҳ таоло хилофи ваъда намекунад');
INSERT INTO "pioneers" VALUES(13,32,'13:32','[Эй паёмбар] Яқинан, паёмбароне, ки пеш аз ту буданд, [низ] мавриди тамасхур воқеъ шуданд, он гоҳ ба касоне, ки куфр варзиданд, муҳлат додам, сипас ононро [ба азоб] фурӯ гирифтам. Пас, [бингар, ки] кайфари ман чи гуна буд');
INSERT INTO "pioneers" VALUES(13,33,'13:33','Оё он [Парвардигоре], ки ҳоким бар ҳар кас ва [нозир бар] рафтору кирдори ӯст, [ба парастиш сазовортар аст ё бутҳои беҷон]? Ва [кофирон] барои Аллоҳ таоло шариконе қарор додаанд. Бигӯ: «Ононро ном баред». Оё [мехоҳед ба] ӯ дар бораи он чи дар замин аст ва ӯ намедонад хабар диҳед, ё фақат сухани сатҳие ва [беасос] мегӯед? [Чунин нест] балки барои касоне, ки куфр варзидаанд, найрангашон ороста шудааст ва аз роҳи [ҳақ] боздошта шудаанд ва ҳар киро Аллоҳ таоло гумроҳ гузорад, ҳидоятгаре нахоҳад дошт');
INSERT INTO "pioneers" VALUES(13,34,'13:34','Онон дар зиндагии дунё азоб [-и дардноке] доранд ва ҳатман, азоби охират сахттар аст ва дар баробари [азоби] Аллоҳ таоло ҳеҷ [ёру] мудофеъе надоранд');
INSERT INTO "pioneers" VALUES(13,35,'13:35','Вижагии биҳиште, ки ба парҳезгорон ваъда дода шуда, [чунин аст, ки] ҷӯйборҳо аз зери [дарахтони] он ҷорӣ аст, меваҳояш ҳамешагӣ ва сояаш [низ доимӣ аст]. Ин саранҷоми касонест, ки парҳезгорӣ кардаанд ва саранҷоми кофирон оташ [-и дузах] аст');
INSERT INTO "pioneers" VALUES(13,36,'13:36','Ва касоне, ки ба онон китоби осмонӣ додаем, аз он чи бар ту нозил шудааст, шодмон мешаванд ва бархе аз гурӯҳҳо[-и аҳли китоб] бахше аз онро инкор мекунанд. [Эй паёмбар, ба онон] Бигӯ: «Ҷуз ин нест, ки ман дастур ёфтаам, Аллоҳро бипарастам ва ба Ӯ ширк наварзам, ба сӯйи Ӯ даъват мекунам ва бозгаштам ба сӯйи Ӯст»');
INSERT INTO "pioneers" VALUES(13,37,'13:37','Ва бад-ин сон, ин Қуръонро фармоне ба забони арабӣ [-и фасеҳ] нозил кардем ва агар пас аз донише, ки [аз ҷониби Парвардигор] бароят омадааст, аз ҳавасҳои онон [мушрикон] пайравӣ кунӣ, дар баробари [азоби] Аллоҳ таоло ҳеҷ корсоз ва нигаҳдорандае нахоҳӣ дошт');
INSERT INTO "pioneers" VALUES(13,38,'13:38','Ва бе тардид, пеш аз ту паёмбароне фиристодем ва барои онон [монанди дигар инсонҳо] ҳамсарону фарзандоне қарор додем ва барои ҳеҷ паёмбаре сазовор нест, ки ҷуз ба фармони Аллоҳ таоло [муъҷиза ва] ояте биёварад. Ҳар амре [тақдиру] навиштае [маълум] дорад');
INSERT INTO "pioneers" VALUES(13,39,'13:39','Аллоҳ таоло ҳар чиро, ки бихоҳад аз миён мебарад ва [он чиро бихоҳад] устувор медорад ва умму-л-китоб [Лавҳи маҳфуз] назди Ӯст');
INSERT INTO "pioneers" VALUES(13,40,'13:40','Ва [эй паёмбар] агар бархе аз он чиро, ки ба онон ваъда медиҳем, ба ту нишон диҳем ё [пеш аз фаро расидани азоб] туро бимиронем, [дар ҳар ҳол] фақат таблиғи [рисолат] бар [уҳдаи] туст ва ҳисоб [-у муҷозоташон] бар мост');
INSERT INTO "pioneers" VALUES(13,41,'13:41','Оё [кофирон] надидаанд, ки мо [бо футуҳоти лашкари ислом] аз доманаҳои сарзамини [куфр] мекоҳем [ва бар қаламрави ислом меафзоем]? Ва Аллоҳ таоло ҳукм мекунад ва барои ҳукмаш [ҳеҷ] боздорандае нест ва ӯ дар ҳисобрасӣ сареъ аст');
INSERT INTO "pioneers" VALUES(13,42,'13:42','Ва ба ростӣ, касоне, ки пеш аз онон буданд [барои муқобала бо паёмбаронашон] макр варзиданд, вале тамоми макрҳо [ва тадбирҳо] аз они Аллоҳ таоло аст. Ӯ медонад, ки ҳар касе чи мекунад ва ба зудӣ кофирон хоҳанд донист, ки фарҷоми [накуи] сарои [охират] аз они кист');
INSERT INTO "pioneers" VALUES(13,43,'13:43','Ва касоне, ки куфр варзиданд, мегӯянд: «[Эй Муҳаммад] Ту фиристода [-и Аллоҳ таоло] нестӣ». Бигӯ: «Кофист, ки Аллоҳ таоло ва касе, ки дониши [китобҳои осмонии пешин] назди Ӯст, миёни ману шумо гувоҳ бошад»');
INSERT INTO "pioneers" VALUES(14,1,'14:1','Алиф, лом, ро. Ин [Қуръон] китобест, ки бар ту нозил кардем, то мардумро ба фармони Парвардигорашон аз торикиҳо [-и ширку ҷаҳл] ба рӯшноӣ [-и имон ва дониш] берун оварӣ, ба роҳи [Парвардигори] шикастнопазири сутуда');
INSERT INTO "pioneers" VALUES(14,2,'14:2','Ҳамон] Аллоҳ таоло, ки он чи дар осмонҳо ва замин аст, аз они Ӯст ва вой бар кофирон аз азобе сахт!');
INSERT INTO "pioneers" VALUES(14,3,'14:3','Касоне, ки зиндагии дунёро бар охират тарҷеҳ медиҳанд ва [мардумро] аз роҳи Аллоҳ таоло бозмедоранд ва он роҳро мунҳариф мехоҳанд, инон дар гумроҳии дуру дарозе ҳастанд');
INSERT INTO "pioneers" VALUES(14,4,'14:4','Мо ҳеҷ паёмбареро ҷуз ба забони қавмаш нафиристодем, то [битавонад паёми илоҳиро] барояшон баён кунад. Аллоҳ таоло ҳар киро бихоҳад, [ба адолати хеш] гумроҳ месозад ва [ба фазли хеш] ҳар киро бихоҳад, ҳидоят мекунад ва Ӯ шикастнопазири ҳаким аст');
INSERT INTO "pioneers" VALUES(14,5,'14:5','Ва ба ростӣ, Мо Мӯсоро бо нишонаҳое равшани худ фиристодем [ва ба ӯ дастур додем], ки: «Қавматро аз торикиҳо [-и куфр] ба рӯшноии [тавҳид] берун овар ва рӯзҳои Аллоҳро ба онон ёдоварӣ кун. Бе тардид, дар ин [ёдоварӣ] барои ҳар шикебои шукргузор нишонаҳое [аз яктоӣ ва бузургии Аллоҳ таоло] аст');
INSERT INTO "pioneers" VALUES(14,6,'14:6','Ва [эй паёмбар, ёд кун аз] ҳангоме ки Мӯсо ба қавмаш гуфт: «Неъмати Аллоҳро бар худ ба ёд оваред, он гоҳ, ки аз [чанголи] фиръавниён наҷотатон дод, ки бадтарин [ва сахттарин] азобро бар саратон меоварданд: писаронатонро мекуштанд ва занонатонро [барои канизӣ] зинда мегузоштанд ва дар ин [амр] аз ҷониби Парвардигоратон барои шумо озмоише бузург буд [то шикебоиятонро биёзмояд]»');
INSERT INTO "pioneers" VALUES(14,7,'14:7','Ва [ёд кунед аз] ҳангоме ки Парвардигоратон фармуд, ки: «Агар шукр гузоред, яқинан, [неъматамро] бар шумо меафзоям ва агар носипосӣ кунед, яқинан, азоби ман сахт аст»');
INSERT INTO "pioneers" VALUES(14,8,'14:8','Ва Мӯсо ба [онон] гуфт: «Агар шумо ва ҳамаи мардуми рӯи замин кофир шавед, пас, [ба Аллоҳ таоло ҳеҷ зиёне намерасад, чаро ки] бе тардид, Аллоҳ таоло бениёзи сутуда аст»');
INSERT INTO "pioneers" VALUES(14,9,'14:9','[Эй кофирон] Оё хабари [ҳалокати] касоне, ки пеш аз шумо буданд, [монанди] қавми Нуҳ ва Од ва Самуд ва [низ] онон, ки баъд аз эшон буданд [ва] ҷуз Аллоҳ таоло касе аз онон хабар надорад, ба шумо нарасидааст? Паёмбаронашон бо далоил [-у муъҷизот]-и равшан ба сӯяшон омаданд, вале онон [ба нишонаи мухолифат] дастҳояшонро бар даҳонҳояшон ниҳоданд ва [ангушт ба дандон газиданду] гуфтанд: «Мо он чиро, ки шумо барои [иблоғи] он фиристода шудаед, инкор мекунем ва дар бораи он чи моро ба сӯяш мехонед, сахт дар тардидем»');
INSERT INTO "pioneers" VALUES(14,10,'14:10','Паёмбаронашон гуфтанд: «Оё дар [ягонагии] Аллоҳ таоло – Офаринандаи осмонҳо ва замин тардиде ҳаст? Ӯ шуморо [ба имон] фаро мехонад, то гуноҳонатонро бибахшояд ва ба шумо то муддати муайяне муҳлат диҳад». Онон гуфтанд: «Шумо ҷуз башаре ҳаммонанди мо нестед, [ва] мехоҳед моро аз он чи ки падаронамон мепарастиданд, боздоред. Пас, далели ошкоре [дар мавриди ростгӯиятон] бароямон биёваред»');
INSERT INTO "pioneers" VALUES(14,11,'14:11','Паёмбаронашон ба онон гуфтанд: «[Оре] Мо ҷуз инсонҳое монанди шумо нестем, вале Аллоҳ таоло бар ҳар як аз бандагонаш, ки бихоҳад [бо неъмати нубувват] миннат мениҳад ва сазовор нест, ки мо ҷуз ба иҷозати Аллоҳ таоло барои шумо далеле биёварем. Пас, муъминон бояд бар Аллоҳ таоло таваккал кунанд');
INSERT INTO "pioneers" VALUES(14,12,'14:12','Ва чаро бар Аллоҳ таоло таваккал накунем ва ҳол он ки ӯ моро ба роҳҳои [саодат]-амон ҳидоят намуд? Ва мо бар озоре, ки [дар роҳи рисолат] ба мо мерасонед, ҳатман, шикебоӣ мекунем ва таваккалкунандагон бояд фақат бар Аллоҳ таоло таваккал кунанд»');
INSERT INTO "pioneers" VALUES(14,13,'14:13','Ва касоне, ки куфр варзиданд ба паёмбаронашон гуфтанд: «Ё шуморо аз сарзаминамон берун мекунем ё ба оини мо бозгардед». Пас, Парвардигорашон ба онон ваҳй кард, ки: «Яқинан ситамгоронро нобуд мекунем');
INSERT INTO "pioneers" VALUES(14,14,'14:14','Ва ҳатман, шуморо пас аз онон дар замин ҷой хоҳем дод. Ин [азобу подош] барои касест, ки аз истодан [ба ҳангоми ҳисобрасӣ] дар пешгоҳи ман битарсад ва аз таҳдидам биҳаросад»');
INSERT INTO "pioneers" VALUES(14,15,'14:15','Ва [паёмбарон аз Аллоҳ таоло] дархости пирӯзӣ [бар кофиронро] карданд ва [саранҷом] ҳар гарданкаши ситезаҷӯ ноком [-у нобуд] шуд');
INSERT INTO "pioneers" VALUES(14,16,'14:16','[Ҳамон ки] Дузах пешорӯи ӯст ва ба вай обе аз чирки хунолуди [дузахиён] менӯшонанд');
INSERT INTO "pioneers" VALUES(14,17,'14:17','[Обе] Ки онро ҷуръа-ҷуръа меошомад ва [аз фарти тааффуну талхӣ] фурӯ бурданаш осону гуворо нест ва марг аз ҳар тараф рӯ ба сӯяш меоварад, вале намемирад ва азобе сахт [-у саҳмгине] пешорӯйи ӯст');
INSERT INTO "pioneers" VALUES(14,18,'14:18','Масали касоне, ки ба Правардигорашон куфр варзидаанд, [ин аст, ки] рафторашон ҳамчун хокистарест, ки дар рӯзи тӯфонӣ бод бар он мевазад, ки аз он чи кардаанд, чизе дар даст надоранд. Ин ҳамон гумроҳии дуру дароз аст');
INSERT INTO "pioneers" VALUES(14,19,'14:19','[Эй инсон] Оё надидӣ, ки Аллоҳ таоло осмонҳо ва заминро ба ҳақ офаридааст? Агар бихоҳад, шуморо [аз миён] мебарад ва офаринише ҷадид [ба ҷоятон падид] меоварад');
INSERT INTO "pioneers" VALUES(14,20,'14:20','Ва ин [кор] бар Аллоҳ таоло душвор нест');
INSERT INTO "pioneers" VALUES(14,21,'14:21','Ва [рӯзи қиёмат] ҳамагӣ дар пешгоҳи Аллоҳ таоло ҳозир [-у ошкор] мешаванд, пас, [дар ин ҳангом] ситамдидагон ба касоне, ки гарданкашӣ мекарданд, мегӯянд: « Мо [дар дунё] пайрави шумо будем, пас, оё [имрӯз] метавонед, чизе аз азоби Аллоҳро аз мо боздоред»? Онон мегӯянд: «Агар Аллоҳ таоло моро [дар дунё ба сӯйи растагорӣ] роҳнамоӣ мекард, мо низ роҳро ба шумо нишон медодем. Чи бетобӣ кунем ё шикебоӣ намоем, бароямон яксон аст. [Ҳеҷ] Гурезгоҳе надорем»');
INSERT INTO "pioneers" VALUES(14,22,'14:22','Ва чун кори [доварӣ] анҷом шуд, шайтон мегӯяд: «Бе тардид, Аллоҳ таоло ба шумо ваъда дод ва ваъдаи росту дуруст ва ман ба шумо ваъда[-и дурӯғин] додам ва бо шумо хилофи ваъда кардам ва ман бар шумо ҳеҷ тасаллуте надоштам, ҷуз ин ки даъвататон кардам ва шумо [низ] иҷобатам намудед. Пас, маро сарзаниш накунед ва худро сарзаниш кунед. [Имрӯз] На ман фарёдраси шумо ҳастам ва на шумо фарёдраси ман ҳастед. Ман безорам аз ин ки пеш аз ин маро [бо Аллоҳ таоло] шарик мегирифтед. Ситамгорони [мушрик] азобе пурдард хоҳанд дошт');
INSERT INTO "pioneers" VALUES(14,23,'14:23','Ва касоне, ки имон оварданд ва корҳои шоиста анҷом доданд, ба боғҳои [биҳиште] дароварда мешаванд, ки ҷӯйборҳо аз зери [дарахтони] он ҷорӣ аст. Ба фармони Парвардигорашон дар он ҷовидонанд ва дурудашон дар он ҷо «салом» аст');
INSERT INTO "pioneers" VALUES(14,24,'14:24','[Эй паёмбар] Оё надидӣ, ки Аллоҳ таоло чи гуна масал задааст? Калимаи пок [-и ло илоҳа иллаллоҳ] ҳамчун дарахти [нахли тануманду] покест, ки решааш [дар аъмоқи замин] устувор аст ва шохааш бар осмон [сар бароварда] аст');
INSERT INTO "pioneers" VALUES(14,25,'14:25','Меваашро ҳар лаҳза ба фармони Парвардигораш медиҳад ва Аллоҳ таоло [барои ҳидояти мардум] масалҳо мезанад, бошад, ки панд гиранд');
INSERT INTO "pioneers" VALUES(14,26,'14:26','Ва масали калимаи палид [ширк] ҳамчун дарахти палидест, ки аз замин барканда шуда [ва ҳеҷ] қарору суботе надорад');
INSERT INTO "pioneers" VALUES(14,27,'14:27','Аллоҳ таоло касонеро, ки имон овардаанд, дар зиндагии дунё ва дар охират бо сухани устувор [-и тавҳид] собит мегардонад ва ситамгарон [-и мушрик]-ро гумроҳ мекунад ва Аллоҳ таоло ҳар чи бихоҳад мекунад');
INSERT INTO "pioneers" VALUES(14,28,'14:28','Оё надидӣ он касоне [аз Қурайш]-ро, ки [сипоси] неъмати Аллоҳ таоло [Ҳарами амн ва вуҷуди паёмбар]-ро ба [носипосӣ ва] куфр бадал карданд ва қавмашонро ба сарои ҳалок дароварданд?');
INSERT INTO "pioneers" VALUES(14,29,'14:29','[Он сарои ҳалок] Дузах аст, [ки] дар он ворид мешаванд ва чи бад ҷойгоҳест!');
INSERT INTO "pioneers" VALUES(14,30,'14:30','[Мушрикон] Барои Аллоҳ таоло ҳамтоёне қарор додаанд, то [мардумро] аз роҳи Ӯ гумроҳ кунанд. Бигӯ: «[Чанд рӯзе аз неъматҳои дунё] Баҳра гиред, пас, яқинан бозгаштатон ба сӯйи оташи [дузах] аст»');
INSERT INTO "pioneers" VALUES(14,31,'14:31','[Эй паёмбар] ба бандагони ман, ки имон овардаанд, бигӯ, намоз барпо доранд ва аз он чи рӯзиашон кардаем, пинҳоу ошкор инфоқ кунанд, пеш аз он ки рӯзе фаро расад, ки дар он на хариду фурӯшест ва на дӯстие');
INSERT INTO "pioneers" VALUES(14,32,'14:32','Аллоҳ таоло аст, ки осмонҳо ва заминро офарид ва аз осмон обе фурӯ фиристод ва бо он [анвои] меваҳоро барои рӯзии шумо падид овард ва киштиҳоро ба хидмататон гумошт, то ба фармонаш дар дарё равон бошанд ва ҷуйборҳоро [низ] дар хидмати шумо ниҳод');
INSERT INTO "pioneers" VALUES(14,33,'14:33','Ва хуршеду моҳро, ки пайваста [дар мадори худ] равонанд ва [ҳамчунин] шабу рӯзро ба хидмататон гумошт');
INSERT INTO "pioneers" VALUES(14,34,'14:34','Ва аз ҳар он чи аз ӯ хостед, ба шумо бахшид ва агар неъмати Аллоҳро бишморед, [чунон фаровон аст, ки] наметавонед онро шумориш кунед. Ба ростӣ, ки инсон, [дар ҳаққи хеш] ситамгор [ва нисбат ба Аллоҳ таоло] носипос аст');
INSERT INTO "pioneers" VALUES(14,35,'14:35','Ва [ёд кун аз] Иброҳим ҳангоме ки гуфт: «Парвардигоро, ин шаҳр [Макка]-ро амн бигардон ва ману фарзандонамро аз парастиши бутҳо дур бидор');
INSERT INTO "pioneers" VALUES(14,36,'14:36','Парвардигоро, ба ростӣ, ки онҳо [бутҳо] бисёре аз мардумро [бо тасаввури шафоат] гумроҳ сохтаанд, пас, ҳар ки [дар яктопарастӣ] аз ман пайравӣ кунад, яқинан, аз ман аст ва ҳар ки аз ман нофармонӣ кунад, пас, бе тардид, ту омурзандаи меҳрубонӣ');
INSERT INTO "pioneers" VALUES(14,37,'14:37','Парвардигоро, ман бархе фарзандонам [Исмоил ва хонадонаш]-ро дар замини беобу кишт назди хонаи муҳтарам [-у шукӯҳманд]-и Ту ҷой додам. Парвардигоро, [чунин кардам] то намоз барпо доранд, пас, дилҳои бархе аз мардумро чунон кун, ки ба сӯйи онон [ва ин сарзамин] мутамоил гардад ва ба эшон аз маҳсулоти [мавриди ниёзашон] рӯзӣ бибахш, бошад, ки сипос гузоранд');
INSERT INTO "pioneers" VALUES(14,38,'14:38','Парвардигоро, ту он чиро, ки пинҳон мекунему ошкор месозем, [ҳамаро] медонӣ ва чизе дар замину осмон аз Аллоҳ таоло пӯшида нест');
INSERT INTO "pioneers" VALUES(14,39,'14:39','Ситоиш аз они Аллоҳ таоло аст, ки дар куҳансолӣ Исмоил ва Исҳоқро ба ман ато фармуд. Бе тардид, Парвардигорам шунавандаи дуост');
INSERT INTO "pioneers" VALUES(14,40,'14:40','Парвардигоро, маро барподорандаи намоз қарор бидеҳ ва аз фарзандонам [низ чунин кун]. Парвардигоро, дуоямро бипазир');
INSERT INTO "pioneers" VALUES(14,41,'14:41','Парвардигоро, ман ва падару модарам ва [ҳамаи] муъминонро рӯзе, ки ҳисоб барпо мешавад, биёмурз»');
INSERT INTO "pioneers" VALUES(14,42,'14:42','Ва [эй паёмбар, ҳаргиз] мапиндор, ки Аллоҳ таоло аз он чи ситамгорон мекунанд, ғофил аст. [На, балки кайфари] ононро барои рӯзе ба таъхир меафканад, ки чашмҳо дар он [аз ваҳшат] хира мешаванд');
INSERT INTO "pioneers" VALUES(14,43,'14:43','Дар ҳоле ки [ба сӯйи мунодии маҳшар] шитобонанд ва сарҳоро боло гирифтаанд [ва бо беқарорӣ ба осмон менигаранд] ва чашм бар ҳам намезананд ва дилҳояшон [аз ваҳшат] тиҳӣ гаштааст');
INSERT INTO "pioneers" VALUES(14,44,'14:44','Ва [эй паёмбар] мардумро аз рӯзе битарсон, ки азоби илоҳӣ ба суроғашон меояд, пас, онон, ки [бо куфру ширк ба хеш] ситам кардаанд, мегӯянд: «Парвардигоро, андаке ба мо муҳлат бидеҳ, то [ба дунё бозгардем ва] даъвататро посух гӯем ва аз паёмбарон пайравӣ кунем. [Гуфта шавад] «Шумо набудед, ки пештар савганд мехӯрдед, ки шуморо фаное нест»?');
INSERT INTO "pioneers" VALUES(14,45,'14:45','Ва [магар на ин аст, ки] дар хонаҳои касоне, ки [бо ширку носипосӣ] ба хештан ситам карда буданд [қавми Ҳуд ва Солеҳ] сокин шудед ва бароятон равшан шуд, ки [чун куфр варзиданд] бо онон чи гуна рафтор кардем ва [аз саргузашти пешиниён] бароятон масалҳо задем [вале боз ҳам панд нагирифтед]');
INSERT INTO "pioneers" VALUES(14,46,'14:46','Ва онон ниҳояти найранги худро [барои мухолифат бо паёмбар ва куштани ӯ] ба кор бурданд ва макрашон назди Аллоҳ таоло [ошкор] аст, ҳарчанд найрангашон [чунон заиф аст, ки] наметавонад кӯҳҳоро аз ҷой бардорад');
INSERT INTO "pioneers" VALUES(14,47,'14:47','Пас, ҳаргиз мапиндор, ки Аллоҳ таоло ваъдаи хешро [дар бораи пирӯзии ҳақ] бо паёмбаронаш хилоф мекунад. Бе тардид, Аллоҳ таоло шикастнопазири додситон аст');
INSERT INTO "pioneers" VALUES(14,48,'14:48','Дар он рӯз [ин] замин ба замине дигар ва осмонҳо [ба осмонҳои дигар] табдил мешаванд ва [мардум] дар муқобили Аллоҳи ягонаи пирӯзманд зоҳир мегарданд');
INSERT INTO "pioneers" VALUES(14,49,'14:49','Ва гунаҳгоронро дар он рӯз мебинӣ, ки дар занҷирҳо баста шудаанд');
INSERT INTO "pioneers" VALUES(14,50,'14:50','Ҷомаҳояшон аз қири гудохта аст ва оташ суратҳояшонро мепӯшонад');
INSERT INTO "pioneers" VALUES(14,51,'14:51','То Аллоҳ таоло ҳар киро ба [сазои] он чи кардааст, кайфар диҳад. Ба ростӣ, ки Аллоҳ таоло дар ҳисобрасӣ сареъ аст');
INSERT INTO "pioneers" VALUES(14,52,'14:52','Ин [Қуръон] паёме расо барои мардум аст [то ба василаи он ҳидоят шаванд] ва бо он ҳушдор ёбанд ва бидонанд, ки Ӯ маъбуде яктост ва то хирадмандон панд гиранд');
INSERT INTO "pioneers" VALUES(15,1,'15:1','Алиф, лом, ро. Ин оёти китоб [-и илоҳӣ] ва Қуръони равшангар аст');
INSERT INTO "pioneers" VALUES(15,2,'15:2','Чи басо кофирон [ҳангоми рӯбарӯшавӣ бо азоби қиёмат] орзу кунанд, ки кош мусулмон буданд');
INSERT INTO "pioneers" VALUES(15,3,'15:3','Бигузор бихӯранд ва [аз лаззатҳои дунё] баҳраманд бошанд ва орзуҳо [-и моддӣ] саргармашон созад, пас, ба зудӣ хоҳанд донист');
INSERT INTO "pioneers" VALUES(15,4,'15:4','Мо [сокинони] ҳеҷ [шаҳру] диёреро ҳалок накардем, магар ин ки [муҳлат ва] аҷале муайян доштанд');
INSERT INTO "pioneers" VALUES(15,5,'15:5','Ҳеҷ уммате аз аҷали [муқаррарии] худ на пешӣ мегирад ва на [аз он] ақиб мемонад');
INSERT INTO "pioneers" VALUES(15,6,'15:6','[Кофирон] гуфтанд: «Эй касе, ки [иддао мекунӣ] Қуръон бар ту нозил шудааст, яқинан девонаӣ');
INSERT INTO "pioneers" VALUES(15,7,'15:7','Агар рост мегӯӣ, чаро фариштагонро наздамон намеоварӣ [то дар бораи паёмбариат гувоҳӣ диҳанд]»?');
INSERT INTO "pioneers" VALUES(15,8,'15:8','[Бояд бидонанд, ки] Мо фариштагонро ба ҳақ [ва фақат дар ҳангоми азоби кофирон] нозил мекунем ва [агар барои азобашон нозил шаванд] дигар муҳлате намеёбанд');
INSERT INTO "pioneers" VALUES(15,9,'15:9','Ба ростӣ, ки мо худ Қуръонро нозил кардем ва ҳатман, худ низ [аз ҳар гуна табдилу таҳрифе] нигаҳбонаш ҳастем');
INSERT INTO "pioneers" VALUES(15,10,'15:10','[Эй паёмбар] пеш аз ту низ [фиристодагони худро] миёни гурӯҳҳои пешин фиристодем');
INSERT INTO "pioneers" VALUES(15,11,'15:11','Ҳеҷ паёмбаре наздашон намеомад, магар он ки масхарааш мекарданд');
INSERT INTO "pioneers" VALUES(15,12,'15:12','Инчунин он [такзиб ва тамасхур]-ро дар дилҳои гунаҳгорон [кофирон] роҳ медиҳем');
INSERT INTO "pioneers" VALUES(15,13,'15:13','Онон ба он [Қуръон] имон намеоваранд ва шеваи [Аллоҳ таоло дар муҷозоти] пешиниён [низ чунин] рафтааст [ки ононро бо азобе дарднок ҳалок намояд]');
INSERT INTO "pioneers" VALUES(15,14,'15:14','Ва [ҳатто] агар даре аз осмон ба рӯйи онон мекушодем, то пайваста дар он боло раванд [ва шигифтиҳои малакутро бубинанд]');
INSERT INTO "pioneers" VALUES(15,15,'15:15','Яқинан, боз ҳам мегуфтанд: «Бе тардид, моро чашмбандӣ кардаанд, балки мо гурӯҳе ҷодушудаем»');
INSERT INTO "pioneers" VALUES(15,16,'15:16','Ва ба ростӣ, мо дар осмон ситорагоне [бузургу дурахшон] қарор додем ва онҳоро барои тамошогарон оростем');
INSERT INTO "pioneers" VALUES(15,17,'15:17','Ва онро аз [вуруду дастбурди] ҳар шайтони рондашуда ҳифз кардем');
INSERT INTO "pioneers" VALUES(15,18,'15:18','Магар он ки [аз малакутиён] дуздона сухане бишнавад, ки [дар он сурат] шиҳобе равшан ӯро дунбол мекунад [ва месӯзонад]');
INSERT INTO "pioneers" VALUES(15,19,'15:19','Ва заминро густурдем ва дар он кӯҳҳои устуворе афкандем ва аз ҳар гиёҳи санҷида [ва муносибе] дар он руёнидем');
INSERT INTO "pioneers" VALUES(15,20,'15:20','Ва дар он барои шумо ва барои касе, ки рӯзирасонаш нестед [соири мардум ва мавҷудоти зинда], асбоби зиндагӣ падид овардем');
INSERT INTO "pioneers" VALUES(15,21,'15:21','Ва ҳеҷ чиз нест, магар он ки ганҷинаҳояш назди Мост ва Мо онро ҷуз ба андозаи муайян фурӯ намефиристем');
INSERT INTO "pioneers" VALUES(15,22,'15:22','Ва бодҳоро борваркунанда [-и абрҳо] фиристодем, сипас аз осмонҳо обе нозил кардем ва бо он серобатон сохтем, дар ҳоле ки шумо захиракунандааш нестед');
INSERT INTO "pioneers" VALUES(15,23,'15:23','Ва яқинан, моем, ки зинда мекунем ва мемиронем ва Мо [бозмондаи ҷовиду] ворис [-и ҳамаи ҷаҳониён] ҳастем');
INSERT INTO "pioneers" VALUES(15,24,'15:24','Ва бе тардид, [таваллуду марги] гузаштагони шуморо донистаем ва яқинан [таваллуду марги] ояндагонро [низ ба хубӣ] медонем');
INSERT INTO "pioneers" VALUES(15,25,'15:25','Ва [эй паёмбар] бе гумон, Парвардигорат [ҳамаи] ононро [дар қиёмат] маҳшур мекунад. Ба ростӣ, ки Ӯ ҳакиму доност');
INSERT INTO "pioneers" VALUES(15,26,'15:26','Ва дар ҳақиқат, Мо [нахустин] инсон [Одам]-ро аз гили хушк [-и бозмонда] аз лойи бадбӯ офаридем');
INSERT INTO "pioneers" VALUES(15,27,'15:27','Ва пеш аз он ҷинро аз оташе сӯзон офаридем');
INSERT INTO "pioneers" VALUES(15,28,'15:28','Ва [ёд кун] аз ҳангоме ки Парвардигорат ба фариштагон фармуд: «Ҳамоно ман башареро аз гили хушк [-и бозмонда] аз лойи бадбӯ хоҳем офарид');
INSERT INTO "pioneers" VALUES(15,29,'15:29','Пас, чун [ӯро офаридем ва] ба ӯ сомон додем ва аз рӯҳи хеш дар вай дамидем, [ба нишонаи итоат аз фармони Ман ҳамагӣ] бар ӯ саҷда кунед»');
INSERT INTO "pioneers" VALUES(15,30,'15:30','Пас, фариштагон ҳамагӣ саҷда карданд');
INSERT INTO "pioneers" VALUES(15,31,'15:31','Магар Иблис, ки аз он ки бо саҷдакунандагон бошад, сарпечӣ кард');
INSERT INTO "pioneers" VALUES(15,32,'15:32','[Аллоҳ таоло] Фармуд: «Эй Иблис, чаро ту бо саҷдакунандагон [ҳамроҳ] нестӣ»?');
INSERT INTO "pioneers" VALUES(15,33,'15:33','[Иблис] гуфт: «Ман он нестам, ки барои башаре, ки ӯро аз гили хушк [-и бозмонда] аз лойи бадбӯ офаридаӣ саҷда кунам»');
INSERT INTO "pioneers" VALUES(15,34,'15:34','[Аллоҳ таоло] Фармуд: «Аз он ҷо [биҳишт] берун шав, [ки] бе тардид, ту [аз боргоҳи Мо] рондашудаӣ');
INSERT INTO "pioneers" VALUES(15,35,'15:35','Ва яқинан, то рӯзи қиёмат бар ту лаънат хоҳад буд»');
INSERT INTO "pioneers" VALUES(15,36,'15:36','[Иблис] Гуфт: «Парвардигоро, то рӯзе, ки [мардум] барангехта мешаванд, ба ман муҳлат бидеҳ [ва намирон]»');
INSERT INTO "pioneers" VALUES(15,37,'15:37','[Аллоҳ таоло] Фармуд: «[Бирав, ки] Яқинан, ту аз муҳлатёфтагонӣ');
INSERT INTO "pioneers" VALUES(15,38,'15:38','То рӯзи он вақти маълум [ки тамоми махлуқот бо нафхаи нахуст мемиранд]»');
INSERT INTO "pioneers" VALUES(15,39,'15:39','[Ӯ] Гуфт: «Парвардигоро, ба сабаби он ки маро гумроҳ сохтӣ, дар замин [бадиҳоро] дар назарашон нек ҷилва медиҳам ва ҳамаи ононро ба гумроҳӣ мекашонам');
INSERT INTO "pioneers" VALUES(15,40,'15:40','Магар бандагони холиси Ту аз [миёни] онон [ки ҳидояташон кардаӣ]»');
INSERT INTO "pioneers" VALUES(15,41,'15:41','[Аллоҳ таоло] Фармуд: «Ин [ихлос] роҳи мустақимест [ки ба Ман мерасад]');
INSERT INTO "pioneers" VALUES(15,42,'15:42','Яқинан, ту бар бандагонам тасаллуте надорӣ, магар [бар он мушрикон ва] гумроҳоне, ки аз ту пайравӣ мекунанд');
INSERT INTO "pioneers" VALUES(15,43,'15:43','Ва бе тардид, дузах ваъдагоҳи ҳамаи онон аст');
INSERT INTO "pioneers" VALUES(15,44,'15:44','[Дузах] Ҳафт дар дорад [ва] барои ҳар даре гурӯҳе аз онон тақсим [ва мушаххас] шудаанд»');
INSERT INTO "pioneers" VALUES(15,45,'15:45','Яқинан, парҳезгорон дар боғҳо [-и биҳишт] ва дар [канори] чашмаҳо ҳастанд');
INSERT INTO "pioneers" VALUES(15,46,'15:46','Ҳангоми вуруд ба онон гуфта мешавад] «Ба саломат ва эминӣ ба он [боғҳо] ворид шавед»');
INSERT INTO "pioneers" VALUES(15,47,'15:47','Он чи дар дилҳояшон аз кина [ва ҳасад] аст, бармеканем, бародарона бар тахтҳо муқобили якдигаранд');
INSERT INTO "pioneers" VALUES(15,48,'15:48','Дар он ҷо на ранҷе ба онон мерасад ва на аз он ҷо берун ронда мешаванд');
INSERT INTO "pioneers" VALUES(15,49,'15:49','[Эй паёмбар] Ба бандагонам хабар бидеҳ, ки ман омурзандаи меҳрубонам');
INSERT INTO "pioneers" VALUES(15,50,'15:50','Ва ин ки азоби ман азобе дарднок аст');
INSERT INTO "pioneers" VALUES(15,51,'15:51','Ва аз меҳмонони Иброҳим [низ] ба онон хабар бидеҳ');
INSERT INTO "pioneers" VALUES(15,52,'15:52','Ҳангоме ки бар ӯ ворид шуданд, салом гуфтанд, [аммо чун даст ба ғизо набурданд, Иброҳим] гуфт: «Мо аз шумо ҳаросонем»');
INSERT INTO "pioneers" VALUES(15,53,'15:53','Онон гуфтанд: «Натарс, мо туро ба писаре доно мужда медиҳем»');
INSERT INTO "pioneers" VALUES(15,54,'15:54','[Иброҳим] Гуфт: «Оё бо ин ки солхӯрда шудаам, мужда [-и таваллуди фарзанд] ба ман медиҳед? Ба чи [далел инчунин] башорат медиҳед»?');
INSERT INTO "pioneers" VALUES(15,55,'15:55','Онон гуфтанд: «Мо туро ба дурустӣ башорат додем, пас, ноумед набош»');
INSERT INTO "pioneers" VALUES(15,56,'15:56','[Иброҳим] Гуфт: «Ва ҷуз гумроҳон чи касе аз раҳмати Парвардигораш ноумед мегардад»?');
INSERT INTO "pioneers" VALUES(15,57,'15:57','[Он гоҳ ба фариштагон] гуфт: «Эй фиристодагон, кор [-у маъмурият]-и шумо чист»?');
INSERT INTO "pioneers" VALUES(15,58,'15:58','Онон гуфтанд: «Дар ҳақиқат, мо ба сӯйи қавми гунаҳгор [-и Лут] фиристода шудаем [то ҳалокашон кунем]');
INSERT INTO "pioneers" VALUES(15,59,'15:59','Ба ҷуз хонаводаи Лут, ки мо яқинан, ҳамаи ононро наҷот хоҳем дод');
INSERT INTO "pioneers" VALUES(15,60,'15:60','Магар ҳамсараш, ки муқаддар кардаем, ки аз бозмондагон [дар азоб] бошад»');
INSERT INTO "pioneers" VALUES(15,61,'15:61','Пас, ҳангоме ки фариштагон [-и Аллоҳ таоло ба шакли инсон] назди хонадони Лут омаданд');
INSERT INTO "pioneers" VALUES(15,62,'15:62','[Ӯ] Гуфт: «Шумо гурӯҳе ношинос ҳастед»');
INSERT INTO "pioneers" VALUES(15,63,'15:63','[Фариштагон] Гуфтанд: «Балки мо ҳамон чизеро бароят овардаем, ки [ин мардуми гунаҳгор] дар борааш тардид мекарданд [нузули азоб]');
INSERT INTO "pioneers" VALUES(15,64,'15:64','Ва мо бароят [хабари ҳақиқӣ ва] рост овардаем ва ҳатман, мо ростгӯем');
INSERT INTO "pioneers" VALUES(15,65,'15:65','Пас, посе аз шаб [ки гузашт] хонаводаатро бо худ [аз ин ҷо] бибар ва [худат] ба дунболашон бирав ва ҳеҷ як аз шумо пушти сарашро нигоҳ накунад ва ба ҷойе, ки ба шумо фармон медиҳанд, биравед»');
INSERT INTO "pioneers" VALUES(15,66,'15:66','Ва Мо ӯ [Лут]-ро аз ин амр огоҳ кардем, ки субҳгоҳон решаи онон канда хоҳад шуд');
INSERT INTO "pioneers" VALUES(15,67,'15:67','Ва аҳли шаҳр, дар ҳоле ки [дар бораи омадани афроди ҷадид] бо шодӣ [ба якдигар] хабар медоданд, [ба сӯйи хонаи Лут] омаданд');
INSERT INTO "pioneers" VALUES(15,68,'15:68','[Лут] Гуфт: «Инҳо меҳмонони ман ҳастанд, пас, маро расво [ва шармандаи меҳмононам] накунед');
INSERT INTO "pioneers" VALUES(15,69,'15:69','Ва аз Аллоҳ таоло битарсед ва саршикастаам насозед»');
INSERT INTO "pioneers" VALUES(15,70,'15:70','[Онон] Гуфтанд: «Магар мо туро аз [мизбонии] афроди [ғариб] наҳй накардем»?');
INSERT INTO "pioneers" VALUES(15,71,'15:71','[Лут] Гуфт: «Агар мехоҳед, [коре ҳалол] анҷом диҳед, инҳо духтарони [уммати] ман ҳастанд [бо эшон издивоҷ кунед]»');
INSERT INTO "pioneers" VALUES(15,72,'15:72','Савганд ба ҷони ту [эй паёмбар], ки онон дар мастии хеш саргашта буданд');
INSERT INTO "pioneers" VALUES(15,73,'15:73','Пас, ҳангоми баромадани офтоб бонги [маргбор] ононро фаро гирифт');
INSERT INTO "pioneers" VALUES(15,74,'15:74','Он гоҳ он [сарзамин]-ро зерурӯ кардем ва бар онон санггилҳое паёпай боридем');
INSERT INTO "pioneers" VALUES(15,75,'15:75','Ба яқин, дар ин [кайфар] барои ҳушёрон ибратҳост');
INSERT INTO "pioneers" VALUES(15,76,'15:76','Ва он [шаҳрҳои вайрона ҳанӯз] бар сари роҳи [мусофирон] побарҷост.');
INSERT INTO "pioneers" VALUES(15,77,'15:77','Яқинан, дар ин [азоб] нишонаҳое барои [ибрат гирифтани] муъминон аст');
INSERT INTO "pioneers" VALUES(15,78,'15:78','Ва асҳоби Айка [қавми Шуайб], ҳатман, ситамгор буданд');
INSERT INTO "pioneers" VALUES(15,79,'15:79','Пас, [Мо] аз онон интиқом гирифтем ва [шаҳрҳои вайроншудаи] ин ду [қавм] бар сари роҳ ошкор аст');
INSERT INTO "pioneers" VALUES(15,80,'15:80','Ва бе тардид, аҳли Ҳиҷр [қавми Самуд] паёмбаронро дурӯғгӯ ангоштанд');
INSERT INTO "pioneers" VALUES(15,81,'15:81','Ва [Мо муъҷизот ва] нишонаҳои худро ба онон додем, ва [-ле] аз онҳо рӯйгардон шуданд');
INSERT INTO "pioneers" VALUES(15,82,'15:82','Ва аз кӯҳҳо хонаҳое [барои худ] метарошиданд, ки дар амон бимонанд');
INSERT INTO "pioneers" VALUES(15,83,'15:83','Пас, субҳгоҳон бонг [-и маргбор] ононро фаро гирифт');
INSERT INTO "pioneers" VALUES(15,84,'15:84','Ва он чи [аз амволу хонаҳои маҳкам] ба даст оварда буданд, суде ба ҳолашон надошт [ва азоби илоҳиро дафъ накард]');
INSERT INTO "pioneers" VALUES(15,85,'15:85','Ва Мо осмонҳо ва замин ва он чиро, ки миёни онҳост, ҷуз ба ҳақ наофаридаем ва қиёмат ҳатман, омаданӣ аст, пас, [эй паёмбар аз кофирон] ба накуӣ гузашт кун.');
INSERT INTO "pioneers" VALUES(15,86,'15:86','Бе тардид, Парвардигорат ҳамон офаринишгари доност.');
INSERT INTO "pioneers" VALUES(15,87,'15:87','Ва ба ростӣ, Мо ба ту [сабъу-л-масонӣ» [сураи Фотиҳа] ва Қуръони бузургро додем');
INSERT INTO "pioneers" VALUES(15,88,'15:88','[Бинобар ин] Ҳаргиз ба он чи [аз неъматҳои дунявие], ки гурӯҳҳое аз онон [кофирон]-ро баҳраманд кардаем, чашм надӯз ва ба хотири [носипосӣ ва ширки] онон андуҳгин мабош ва бо муъминон фурӯтанӣ кун');
INSERT INTO "pioneers" VALUES(15,89,'15:89','Ва бигӯ: «Ба ростӣ, ки ман ҳамон бимдиҳандаи ошкорам»');
INSERT INTO "pioneers" VALUES(15,90,'15:90','[Ва ононро аз азоб битарсон] Ҳамон гуна ки [онро] бар тақсимкунандагон [-и оёти илоҳӣ] фиристодем');
INSERT INTO "pioneers" VALUES(15,91,'15:91','Ҳамон касоне, ки Қуръонро [ба] бахшҳо [-е ҳамчун шеъру пешгӯӣ ва ҷоду тақсим] карданд');
INSERT INTO "pioneers" VALUES(15,92,'15:92','Пас, савганд ба Парвардигорат, ҳатман, [дар қиёмат] аз ҳамаи онон суол хоҳем кард');
INSERT INTO "pioneers" VALUES(15,93,'15:93','Аз он чи мекарданд');
INSERT INTO "pioneers" VALUES(15,94,'15:94','Пас, [эй паёмбар] он чи [-ро, ки аз сӯйи Аллоҳ таоло] дастур ёфтаӣ, ошкор кун ва аз мушрикон рӯ бигардон');
INSERT INTO "pioneers" VALUES(15,95,'15:95','Бе тардид, Мо [шарри] масхаракунандагонро аз ту дур мекунем');
INSERT INTO "pioneers" VALUES(15,96,'15:96','Онон, ки [барои худ] бо Аллоҳ таоло маъбуди дигаре қарор медиҳанд, пас, ба зудӣ [натиҷаи ширки хешро] хоҳанд донист');
INSERT INTO "pioneers" VALUES(15,97,'15:97','Ва [эй паёмбар] яқинан, Мо медонем, ки ту аз он чи онон [кофирон] мегӯянд, озурдахотир мегардӣ');
INSERT INTO "pioneers" VALUES(15,98,'15:98','Пас, ба ситоиши Парвардигорат тасбеҳ бигӯ ва аз саҷдакунандагон бош');
INSERT INTO "pioneers" VALUES(15,99,'15:99','Ва то ҳангоме ки маргат фаро мерасад, Парвардигоратро парастиш кун');
INSERT INTO "pioneers" VALUES(16,1,'16:1','[Эй кофирон, лаҳзаи вуқуи қиёмат ва] Фармони Аллоҳ таоло [барои азобатон] наздик шудааст; пас, барои [расидани] он шитоб накунед. Ӯ муназзаҳ аст ва аз он чи бо вай шарик месозанд бартар аст');
INSERT INTO "pioneers" VALUES(16,2,'16:2','[Аллоҳ таоло] Ба фармони хеш фариштагонро бо ваҳй бар ҳар як аз бандагонаш, ки бихоҳад, нозил мекунад [бо ин паём], ки: «[Эй паёмбарон, мардумро аз ширк ба Аллоҳ таоло] бим диҳед [ва ба онон бигӯед, ки] маъбуде [баҳақ] ҷуз ман нест; пас, [эй мардум] аз ман парво кунед»');
INSERT INTO "pioneers" VALUES(16,3,'16:3','Ӯ осмонҳо ва заминро ба ҳақ [ва ҳадафманд] офарид [ва] аз он чи [бо вай] шарик месозанд, бартар аст');
INSERT INTO "pioneers" VALUES(16,4,'16:4','[Аллоҳ таоло] Инсонро аз нутфае офарид ва он гоҳ ӯ ситезагари ошкор шуд');
INSERT INTO "pioneers" VALUES(16,5,'16:5','Ва чаҳорпоёнро бароятон офарид, ки дар онон пӯшиш [-и гарм] ва манофеъ [-и дигаре] аст ва аз [гӯшти бархе аз] онон мехӯред');
INSERT INTO "pioneers" VALUES(16,6,'16:6','Ва дар онҳо бароятон зебоӣ [ва шукӯҳ] аст, он гоҳ ки [онҳоро шомгоҳон] аз чаро бозмеоваред ва [бомдодон] барои чаро [ба саҳро] мефиристед');
INSERT INTO "pioneers" VALUES(16,7,'16:7','[Онҳо] Борҳои сангини шуморо [аз шаҳре] ба шаҳре мебаранд, ки [агар чунин намекарданд] ҷуз бо заҳмат додан ба бадани хеш наметавонистед ба он ҷо бирасед. Ба ростӣ, ки Парвардигоратон дилсӯзу меҳрубон аст');
INSERT INTO "pioneers" VALUES(16,8,'16:8','Ва аспону шутурон ва харонро [низ офарид] то бар онҳо савор шавед ва [низ бароятон мояи] таҷаммул [аст] ва чизҳое меофаринад, ки намедонед.');
INSERT INTO "pioneers" VALUES(16,9,'16:9','Бар Аллоҳ таоло аст, ки роҳи рост [ ислом]-ро [ба бандагонаш бинамояд] ва бархе аз он [роҳҳо] бероҳа аст; ва агар [Аллоҳ таоло] мехост, ҳамаи шуморо ҳидоят мекард');
INSERT INTO "pioneers" VALUES(16,10,'16:10','Ӯст, ки аз осмон обе фурӯ фиристод, ки ошомидании шумо аз он аст ва [гиёҳ ва] дарахте, ки [чаҳорпоёни худро] дар он мечаронед, [низ] аз он [об] аст');
INSERT INTO "pioneers" VALUES(16,11,'16:11','[Аллоҳ таоло] Ба василаи он кишт ва зайтун ва дарахтони хурмо ва ангур ва ҳама навъ меваҳо[-и гуногун] бароятон мерӯёнад. Ба ростӣ, ки дар инҳо барои мардуме, ки меандешанд нишонае [аз қудрати Парвардигор] аст');
INSERT INTO "pioneers" VALUES(16,12,'16:12','Ва шабу рӯз ва хуршеду моҳро ба хидмататон гумошт; ва ситорагон [низ] ба фармонаш дар хидмат [-и инсон] ҳастанд. Бе тардид, дар ин [офариниш] барои мардуме, ки хирадварзӣ мекунанд, нишонае [аз қудрати Парвардигор] аст');
INSERT INTO "pioneers" VALUES(16,13,'16:13','Ва он чиро, ки бароятон дар замин ба рангҳои гуногун офарид [низ ба хидмати шумо гумошт]. Бе тардид, дар ин [умур] барои мардуме, ки панд мегиранд, нишонае [аз қудрати Парвардигор] аст.');
INSERT INTO "pioneers" VALUES(16,14,'16:14','Ӯст, ки дарёро ба хидмататон гумошт, то аз [сайди] он гӯшти тоза бихӯред ва аз он зеваре [ҳамчун марворид ва марҷон] берун меоваред, ки мепӯшед; ва киштиҳоро дар он [дарё] мебинӣ, ки [амвоҷро] мешикофанд, то шумо аз фазл [-у бахшиши] Аллоҳ таоло баҳраманд гардед ва бошад, ки сипос гузоред');
INSERT INTO "pioneers" VALUES(16,15,'16:15','Ва дар замин кӯҳҳои устувор [ва муҳкаме] дарафканд, ки мабодо шуморо биларзонад ва ҷӯйборҳо [-е падид овард], то [бидуни гум шудан] роҳи худро биёбед');
INSERT INTO "pioneers" VALUES(16,16,'16:16','Ва нишонаҳое [барои ёфтани масирҳо дар тӯли рӯз офарид] ва тавассути ситора[-ҳо низ дар шаб] роҳёбӣ мекунанд');
INSERT INTO "pioneers" VALUES(16,17,'16:17','Оё он ки меофаринад, ҳамчун касе аст, ки [ҳеҷ] намеофаринад? Оё панд намегиред?');
INSERT INTO "pioneers" VALUES(16,18,'16:18','Агар [бихоҳед] неъмат[-ҳои] Аллоҳро бишморед, наметавонед онро ба шумораш дароваред. Бе тардид, Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(16,19,'16:19','Ва Аллоҳ таоло он чи пинҳон мекунед ва ошкор месозед, [ҳамаро] медонад.');
INSERT INTO "pioneers" VALUES(16,20,'16:20','Ва онҳоеро, ки [мушрикон] ба ҷойи Аллоҳ таоло [ба парастиш] мехонанд, чизеро намеофаринанд ва худ офарида мешаванд');
INSERT INTO "pioneers" VALUES(16,21,'16:21','Ин бутҳо дар ҳақиқат] мурдаанд на зинда; ва худ намедонанд, ки чи ҳангом барангехта мешаванд');
INSERT INTO "pioneers" VALUES(16,22,'16:22','Маъбуди шумо маъбуде ягона аст ва касоне, ки ба охират имон надоранд, дилҳояшон инкоркунанда [-и ҳақ] аст ва худ мутакаббиранд');
INSERT INTO "pioneers" VALUES(16,23,'16:23','Ҳатман, Аллоҳ таоло он чиро, ки пинҳон мекунанд ва ошкор месозанд [ҳамаро] медонад. Бе тардид, Ӯ мутакаббиронро дӯст намедорад');
INSERT INTO "pioneers" VALUES(16,24,'16:24','Ҳангоме ки ба онон гуфта мешавад: «Парвардигоратон чӣ чизе нозил кардааст?» Мегӯянд: «Афсонаҳои пешиниён»');
INSERT INTO "pioneers" VALUES(16,25,'16:25','[Ҳамчунон дар куфр мемонанд] То рӯзи қиёмат, [ҳам] бори гуноҳони худро ба тамомӣ бар дӯш кашанд ва [ҳам бахше] аз бори гуноҳони касонеро, ки аз рӯйи нодонӣ гумроҳашон кардаанд. Ҳон! Чи бад аст, он чи бар дӯш мекашанд!');
INSERT INTO "pioneers" VALUES(16,26,'16:26','Дар ҳақиқат, касоне, ки пеш аз онон буданд, низ найранг [ва бадандешӣ] карданд; пас, фармони Аллоҳ таоло даррасид, ки бунёдашонро аз асос барандохт ва сақф бар сарашон фурӯ рехт ва аз [ҳамон] ҷойе, ки фикр намекарданд, азоб домангирашон шуд');
INSERT INTO "pioneers" VALUES(16,27,'16:27','Сипас рӯзи қиёмат [Аллоҳ таоло бо азобе дарднок] хорашон месозад ва мефамояд: «Куҷо ҳастанд шарикони ман, ки дар бораи онон [бо паёмбарон ва муъминон] ситез мекардед?» Касоне, ки ба эшон илм дода шудааст [уламои раббонӣ], мегӯянд: «Имрӯз хорӣ ва азоб насиби кофирон аст»');
INSERT INTO "pioneers" VALUES(16,28,'16:28','[Ҳамон] Касоне, ки фариштагон [-и азоб] дар ҳоле ҷонашонро мегиранд, ки [ба сабаби ширк дар дунё] ба худ ситам карда буданд. Пас, [онон дар ҳангоми марг] сари таслим фуруд меоваранд [ва мегӯянд:] «Мо ҳеҷ [кори] баде намекардем. Оре, мусалламан Аллоҳ таоло ба он чи мекардед доност');
INSERT INTO "pioneers" VALUES(16,29,'16:29','Пас, аз дарвозаҳои дузах ворид шавед [ки] ҷовидона дар он хоҳед буд; ва ба ростӣ, ҷойгоҳи гарданкашон чи бад аст!');
INSERT INTO "pioneers" VALUES(16,30,'16:30','Ва ба касоне, ки парҳезкорӣ карданд, гуфта мешавад: «Парвардигоратон [бар паёмбар] чи чиз нозил кардааст?» Онон мегӯянд: «Хайр [ва накуии бисёр]». Барои касоне, ки дар ин дунё некӣ кардаанд, [подош] накуӣ аст ва, ҳатман, сарои охират беҳтар аст; ва, ба ростӣ, чи накуст сарои парҳезкорон!');
INSERT INTO "pioneers" VALUES(16,31,'16:31','[Ҷойгоҳашон] Боғҳое [аз биҳишт] ҷовидон аст, ки ба он ворид мешаванд. Аз зери [дарахтони] он ҷӯйборҳо ҷорӣ аст [ва] дар он ҷо ҳар чи бихоҳанд, барояшон [фароҳам] аст. Аллоҳ таоло парҳезкоронро чунин подош медиҳад');
INSERT INTO "pioneers" VALUES(16,32,'16:32','[Ҳамон] Касоне, ки фариштагон дар ҳоле ҷонашонро меситонанд, ки [аз олудагии куфру гуноҳ] поканд [ва ба онон] мегӯянд: «Дуруд бар шумо. Ба подоши он чи мекардед, [инак] ба биҳишт дароед»');
INSERT INTO "pioneers" VALUES(16,33,'16:33','Оё [кофирон] ҷуз ин интизор доранд, ки фариштагон [барои қабзи рӯҳ] ба суроғашон биёянд ё фармони Парвардигорат [барои азобашон] биёяд? Касоне, ки пеш аз онон буданд [низ] чунин мекарданд ва Аллоҳ таоло ба онон ситам накард, вале онон худ ба хештан ситам мекарданд');
INSERT INTO "pioneers" VALUES(16,34,'16:34','Пас, [кайфари] бадиҳое, ки карда буданд, гиребонгирашон шуд ва он чи масхара мекарданд, ононро фурӯ гирифт');
INSERT INTO "pioneers" VALUES(16,35,'16:35','Ва касоне, ки ширк варзиданд, гуфтанд: «Агар Аллоҳ таоло мехост, на мо ва на падаронамон чизеро ғайр аз Ӯ намепарастидем ва чизеро ҷуз ба [хосту фармони] Ӯ ҳаром намекардем». Касоне, ки пеш аз онон буданд, низ чунин карданд; пас, оё паёмбарон вазифае ҷуз расонидани ошкор [-и ваҳй] доранд?');
INSERT INTO "pioneers" VALUES(16,36,'16:36','Яқинан, Мо дар ҳар уммате паёмбареро [бо ин паём] барангехтем, ки: «Аллоҳи якторо бипарастед ва аз тоғут [маъбудони ғайри Аллоҳ таоло] дурӣ кунед». Пас, Аллоҳ таоло бархе аз ононро ҳидоят кардааст ва бархе аз онон низ сазовори гумроҳӣ шуданд. Пас, дар замин гардиш кунед ва бингаред, ки саранҷоми такзибкунандагон [-и оёти илоҳӣ] чи гуна будааст');
INSERT INTO "pioneers" VALUES(16,37,'16:37','[Эй Паёмбар] ҳар андоза бар ҳидояти онон муштоқ бошӣ, [суде надорад, зеро] Аллоҳ таоло касеро, ки гумроҳ намояд, ҳидоят нахоҳад кард ва онон [ҳеҷ] ёваре надоранд');
INSERT INTO "pioneers" VALUES(16,38,'16:38','[Мушрикон] Бо сахттарин савгандҳояшон ба Аллоҳ таоло савганд хӯрданд, ки: «Аллоҳ таоло касеро, ки мемирад, барнамеангезад». Оре, ин [барангехтани мурдагон] ваъдаест, ки бар ҳақ бар уҳдаи Ӯст, вале бештари мардум намедонанд');
INSERT INTO "pioneers" VALUES(16,39,'16:39','[Ӯ мурдагонро зинда мекунад] То саранҷоми [ҳақиқати] он чиро, ки дар он ихтилоф мекунанд, барояшон равшан созад ва то касоне, ки куфр варзиданд, бидонанд, ки дурӯғгӯ будаанд.');
INSERT INTO "pioneers" VALUES(16,40,'16:40','Сухани Мо дар бораи чизе, ки иродаашро кардаем, фақат ин аст, ки ба он мегӯем: «Мавҷуд шав», пас, бедиранг мавҷуд мешавад');
INSERT INTO "pioneers" VALUES(16,41,'16:41','Ва касоне, ки дар [роҳи] Аллоҳ таоло – пас аз он ки мавриди ситам воқеъ шуданд – ҳиҷрат карданд, дар дунё [дар сарзамин ва] маконе наку ононро ҷой медиҳем; ва яқинан подоши охират бузургтар аст. Агар [онон, ки аз ҳиҷрат худдорӣ карданд, подоши ин корро] медонистанд, [ҳаргиз аз ҳиҷрат бознамемонданд]');
INSERT INTO "pioneers" VALUES(16,42,'16:42','[Муҳоҷирон дар роҳи Аллоҳ таоло] касоне [ҳастанд], ки шикебоӣ намуданд ва бар Парвардигорашон таваккал мекунанд');
INSERT INTO "pioneers" VALUES(16,43,'16:43','Ва [эй Паёмбар] Мо пеш аз ту ба ҷуз мардоне, ки ба онон ваҳй мекардем, [касеро ба рисолат] нафиристодем; пас, [эй мушрикон] агар намедонед, аз аҳли зикр [донишмандони аҳли китоб] бипурсед');
INSERT INTO "pioneers" VALUES(16,44,'16:44','[Он паёмбаронро] Бо далоили равшан ва китобҳо[-и осмонӣ фиристодем]; ва [Мо ин] Қуръонро бар ту нозил кардем, то барои мардум он чиро, ки бар эшон нозил шудааст, равшан созӣ; бошад, ки [дар мафоҳимаш] бияндешанд');
INSERT INTO "pioneers" VALUES(16,45,'16:45','Оё касоне, ки бадандешӣ [ва тавтеа] карданд, [аз ин] эмин ҳастанд, ки Аллоҳ таоло [ҳамаи] ононро дар замин фурӯ барад ё азоби [илоҳӣ] аз ҷойе, ки намедонанд, [ба суроғашон] биёяд?');
INSERT INTO "pioneers" VALUES(16,46,'16:46','Ё [он ки] ба ҳангоми рафту омадашон [гиребони] ононро бигирад? Пас, онон монеъ [-и расидани азоб] нестанд');
INSERT INTO "pioneers" VALUES(16,47,'16:47','Ё [он ки азоби илоҳӣ] дар ҳоле ки [аз он] метарсанд, гиребонгирашон шавад? Пас, бе тардид, Парвардигоратон дилсӯзи меҳрубон аст [ки дар муҷозотатон шитоб намекунанд]');
INSERT INTO "pioneers" VALUES(16,48,'16:48','Оё онон ба чизҳое [ҳамчун дарахтон ва кӯҳҳо], ки Аллоҳ таоло офарида аст, наменигаранд, ки [чи гуна бо ҳаракати хуршеду моҳ] сояҳояшон аз росту чап мегустурад ва дар ҳоле барои Аллоҳ таоло саҷда мекунанд, ки [дар баробараш] хоксоранд?');
INSERT INTO "pioneers" VALUES(16,49,'16:49','Ва ҳар чи дар осмонҳо ва замин аст – аз ҷунбандагону фариштагон - [ҳамагӣ] барои Аллоҳ таоло саҷда меоваранд ва гарданкашӣ намекунанд');
INSERT INTO "pioneers" VALUES(16,50,'16:50','[Фариштагон] Аз Парвардигори хеш, ки [ба зот ва ғалаба ва қудраташ] болои онон аст, метарсанд ва он чӣ фармон дода мешаванд, ҳамон мекунанд');
INSERT INTO "pioneers" VALUES(16,51,'16:51','Ва Аллоҳ таоло фармуд: «Ду маъбуд [-ро барои худ ба парастиш] нагиред. Ҷуз ин нест, ки [маъбуди шумо] ҳамон Аллоҳи яктост; пас, танҳо аз ман парво кунед»');
INSERT INTO "pioneers" VALUES(16,52,'16:52','Ва он чи дар осмонҳо ва замин аст, аз они Ӯст ва парастишу фармондорӣ [низ] ҳамеша ва пайваста барои Ӯст. Оё аз ғайри Аллоҳ таоло парво мекунед?');
INSERT INTO "pioneers" VALUES(16,53,'16:53','Ва [эй мардум] ҳар неъмате, ки доред, аз сӯи Аллоҳ таоло аст; сипас чун ба шумо газанде мерасад, ба пешгоҳи Ӯ нола ва зорӣ мекунед');
INSERT INTO "pioneers" VALUES(16,54,'16:54','Сипас вақте он газандро аз шумо баратараф сохт, он гоҳ гурӯҳе аз шумо ба Парвардигорашон ширк меварзанд');
INSERT INTO "pioneers" VALUES(16,55,'16:55','То саранҷом ба он чӣ ба эшон додаем, носипосӣ кунанд. Пас, [чанд рӯзе аз неъматҳои дунё] бархӯрдор шавед, ки ба зудӣ [натоиҷи кирдоратонро] хоҳед донист');
INSERT INTO "pioneers" VALUES(16,56,'16:56','Ва [мушрикон] аз он чӣ рӯзишон кардаем, барои чизҳое, ки ҳеҷ фаҳме надоранд [бутҳо] саҳме қарор медиҳанд. Ба Аллоҳ таоло савганд, ки дар бораи он ифтиро, ки мебастед, ҳатман, бозхост хоҳед шуд');
INSERT INTO "pioneers" VALUES(16,57,'16:57','Ва [онон фариштагонро] духтароне барои Аллоҳ таоло қарор медиҳанд – Ӯ муназзаҳ аст – ва он чиро майл доранд [писарон] барои худашон [қарор медиҳанд]');
INSERT INTO "pioneers" VALUES(16,58,'16:58','Ва ҳар гоҳ яке аз ононро ба [таваллуди] духтаре башорат диҳанд, дар ҳоле ки хашмашро фурӯ мехӯрад, чеҳрааш [аз андӯҳ] тира мешавад');
INSERT INTO "pioneers" VALUES(16,59,'16:59','Аз бадӣ [ва нороҳатии] он чӣ ба ӯ мужда додаанд, аз қавм [-и хеш] пинҳон мешавад [ва бо худ меандешад, ки] оё ӯро бо сарафкандагӣ нигаҳ дорад ё дар хок пинҳонаш кунад. Ҳон! Чи бад доварӣ мекунанд!');
INSERT INTO "pioneers" VALUES(16,60,'16:60','Сифати зишт [-и чаҳлу куфр] барои касонест, ки ба охират имон надоранд ва сифати бартар аз они Аллоҳ таоло аст ва Ӯ шикастнопазиру ҳаким аст');
INSERT INTO "pioneers" VALUES(16,61,'16:61','Ва агар Аллоҳ таоло мардумро ба хотири зулмашон бозхост мекард, ҷунбандае рӯйи замин боқӣ намегузошт; вале [кайфари] ононро то замони муайян ба таъхир меандозад; ва чун аҷалашон ба сар ояд, на соате [аз он] таъхир мекунанд ва на пешӣ мегиранд');
INSERT INTO "pioneers" VALUES(16,62,'16:62','Ва [мушрикон] он чиро хуш надоранд барои Аллоҳ таоло қарор медиҳанд ва забонашон [чунин] дурӯғ месозад, ки [расттагорӣ ва] накуӣ аз они эшон аст. Ҳаққо, ки оташ насибашон аст ва онон, ҳатман, аз пешгомон [-и дузах] ҳастанд');
INSERT INTO "pioneers" VALUES(16,63,'16:63','[Эй паёмбар] Савганд ба Аллоҳ таоло, ки ба сӯи умматҳое, ки пеш аз ту буданд, [низ паёмбароне] фиристодем; пас, шайтон корҳояшонро барояшон орост; аз ин рӯ, имрӯз [низ дӯст ва] корсозашон ҳам ӯст ва барояшон азобе дарднок [дар пеш] аст');
INSERT INTO "pioneers" VALUES(16,64,'16:64','Ва [эй паёмбар] ин китобро бар ту нозил накардем, магар барои ин ки [ҳақиқати] он чиро, ки [мардум] дар он ихтилоф доранд, барояшон баён кунӣ ва ҳидояту раҳмате бошад барои гурӯҳе, ки имон меоваранд');
INSERT INTO "pioneers" VALUES(16,65,'16:65','Ва Аллоҳ таоло аз осмон обе фурӯ фиристод ва бо он заминро пас аз хазон ва хушкиаш зинда кард. Ҳатман, дар ин амр барои мардуме, ки [сухани ҳақро] мешунаванд, нишонае [аз қудрати Парвардигор] аст');
INSERT INTO "pioneers" VALUES(16,66,'16:66','Ва [эй мардум,] дар [афзоиши] чаҳорпоён барои шумо ибрате аст: аз он чи дар шиками онҳост, аз миёни ғизоҳои ҳазмшуда ва хун шири холисе ба шумо менӯшонем, ки барои нӯшандагон гуворост');
INSERT INTO "pioneers" VALUES(16,67,'16:67','Ва [низ] аз меваҳои дарахтони хурмо ва ангур ҳам шароб [-и мастиовар] ва ҳам рӯзии наку [ва покиза] ба даст меоваред. Бе гумон, дар ин [амр] барои касоне, ки хирад меварзанд, нишонае [аз лутфу қудрати Парвардигор] аст');
INSERT INTO "pioneers" VALUES(16,68,'16:68','Парвардигорат ба занбӯри асал илҳом кард, ки: аз кӯҳҳо ва дарахтон ва аз он чи [мардум] бино мекунанд, [барои худ] хонаҳое баргир');
INSERT INTO "pioneers" VALUES(16,69,'16:69','Он гоҳ аз [шираи] тамоми меваҳо [ва гулҳо] бихӯр ва роҳҳои Парвардигоратро фурӯтанона тай кун». Аз шиками онҳо шаҳде рангоранг берун меояд, ки шифои мардум дар он аст. Ба ростӣ, дар ин [амр] барои мардуме, ки меандешанд, нишонае [аз илму қудрати Парвардигор] аст');
INSERT INTO "pioneers" VALUES(16,70,'16:70','Ва Аллоҳ таоло шуморо офаридааст ва сипас ҷонатонро мегирад; ва бархе аз шумо ба фурӯтарини [даврони] умр [куҳансолӣ] мерасанд, чунончи пас аз донистани [бисёре чизҳо, дигар] чизе намедонанд [ва ҳамаро фаромӯш мекунанд]. Бе тардид, Аллоҳ таоло донову тавоност');
INSERT INTO "pioneers" VALUES(16,71,'16:71','Ва Аллоҳ таоло бархе аз шуморо бар бархе [дигар] дар [неъмату] рӯзӣ бартарӣ додаст; пас, касоне, ки бартарӣ дода шудаанд, ҳозир нестанд, ки аз рӯзии худ ба бардагонашон бидиҳанд, то ҳамагӣ дар он [неъмату дороӣ] баробар шаванд. [Агар худ аз шарик безоранд, чаро барои Аллоҳ таоло шарик қарор медиҳанд?] Пас, оё [бо ин ширкварзӣ] неъмати Аллоҳро инкор мекунанд?');
INSERT INTO "pioneers" VALUES(16,72,'16:72','Ва Аллоҳ таоло барои шумо аз [ҷинси] худатон ҳамсароне қарор дод ва аз ҳамсаронатон барои шумо фарзандон ва наводагоне офарид ва аз чизҳое покиза ба шумо рӯзӣ бахшид. Пас, оё [боз ҳам] ба ботил имон меоваранд ва ба неъмати Аллоҳ таоло носипосӣ мекунанд?');
INSERT INTO "pioneers" VALUES(16,73,'16:73','Ва [мушрикон] ба ҷойи Аллоҳ таоло чизҳоеро мепарастанд, ки молики ҳеҷ рӯзие дар осмонҳо ва замин барояшон нестанд ва тавоноӣ [-и ҳеҷ коре] надоранд');
INSERT INTO "pioneers" VALUES(16,74,'16:74','Пас, барои Аллоҳ таоло амсол [ва шарикҳое мутааддид] қоил нашавед. Бе тардид, Аллоҳ таоло медонад ва шумо намедонед');
INSERT INTO "pioneers" VALUES(16,75,'16:75','Аллоҳ таоло мисоле задааст: бардаи мамлуке, ки қодир ба ҳеҷ коре нест ва [дар муқобил,] касе, ки аз ҷониби хеш ба ӯ рӯзии накуие бахшидаем ва ӯ аз ҳамон [неъматҳои илоҳӣ] пинҳону ошкор инфоқ мекунад. Оё ин ду баробаранд? [Пас, шумо мушрикон чи гуна молики мутлақро бо бутҳои нотавон яксон медонед?] Ситоиш махсуси Аллоҳ таоло аст; аммо бештарашон [ҳаққи ягонагиашро] намедонанд');
INSERT INTO "pioneers" VALUES(16,76,'16:76','Ва Аллоҳ таоло масали [дигаре] задааст: ду мардро [фарз кунед], ки яке аз он ду лол аст ва ҳеҷ коре аз вай барнамеояд ва сарбори дӯсти худ мебошад, ҳар куҷо ӯро мефиристад, ҳеҷ хайре ба ҳамроҳ намеоварад [ва вазоифашро ба дурустӣ анҷом намедиҳад]; оё ӯ бо касе, ки ба адолат фармон медиҳад ва худ бар роҳе мустақим қарор дорад, баробар аст? [Пас, шумо мушрикон чи гуна қодири мутлақро бо бутҳои беэҳсоси нотавон яксон медонед?]');
INSERT INTO "pioneers" VALUES(16,77,'16:77','[Илми] Ғайби осмонҳо ва замин танҳо аз они Аллоҳ таоло аст ва амри [барпоии] қиёмат ҷуз [ба суръати] як чашм бар ҳам задан нест; ё [ҳатто] сареътар. Бе тардид, Аллоҳ таоло бар ҳар чизе тавоност');
INSERT INTO "pioneers" VALUES(16,78,'16:78','[Эй мардум] Аллоҳ таоло шуморо аз шиками модаронатон хориҷ намуд, дар ҳоле ки ҳеҷ чиз намедонистед; ва бароятон гӯшу чашм ва дил [-у ақл] падид овард [то қудратҳои илоҳиро дарк кунед]; бошад, ки сипос гузоред');
INSERT INTO "pioneers" VALUES(16,79,'16:79','Оё [мушрикон] парандагонро надидаанд, ки дар фазои осмон ром шудаанд [ва] касе ҷуз Аллоҳ таоло онҳоро [дар он ҳол] нигаҳ намедорад? Ба ростӣ, дар ин [амр] барои мардуме, ки имон доранд, нишонаҳое [аз қудрати Парвардигор] аст');
INSERT INTO "pioneers" VALUES(16,80,'16:80','Аллоҳ таоло барои шумо аз хонаҳоятон маҳалли сукунат [ва оромиш] қарор дод ва аз пушти чаҳорпоён [низ] бароятон хонаҳое [хаймаҳое] қарор дод, то рӯзи кӯч кардан сабукбор бошед ва рӯзи иқомататон [низ барпоияш осон бошад]; ва аз пашму курку мӯйҳояшон то муддате муайян [бароятон] асоси [хона] ва асбоб [-у васоили зиндагӣ] фароҳам кард');
INSERT INTO "pioneers" VALUES(16,81,'16:81','Аллоҳ таоло аз он чӣ офарид, сояҳое бароятон фароҳам овардааст ва аз кӯҳҳо [низ роҳҳо ва] паноҳгоҳҳое бароятон қарор додааст ва бароятон ҷомаҳое падид овардааст, ки аз [сармо] ва гармо нигоҳатон медорад ва [зиреҳҳо ва] танпӯшҳое, ки шуморо дар ҷанг[-ҳоя]-тон ҳифз мекунад. Ӯ, инчунин, неъматашро бар шумо тамом мегардонад; бошад, ки [дар баробари тавҳид] таслим шавед [ва ширк наварзед]');
INSERT INTO "pioneers" VALUES(16,82,'16:82','Пас, [эй Паёмбар] агар [мушрикон] рӯй гардонанд, ҷуз ин нест, ки вазифаи ту [фақат] паёмрасонии ошкор аст');
INSERT INTO "pioneers" VALUES(16,83,'16:83','[Онон] неъмати Аллоҳро мешиносанд; он гоҳ [боз ҳам] инкораш мекунанд ва бештарашон носипосу кофиранд');
INSERT INTO "pioneers" VALUES(16,84,'16:84','Ва [ёд кун аз] рӯзе, ки аз ҳар уммате гувоҳе [барояшон] бармеангезем; он гоҳ ба касоне, ки куфр варзиданд, на иҷозат [-и сухан гуфтан] дода мешавад ва на аз эшон дархости пузиш мешавад');
INSERT INTO "pioneers" VALUES(16,85,'16:85','Ва касоне, ки ситам карданд, ҳангоме ки азоби [охират]-ро мебинанд; на азобашон сабук мешавад ва на муҳлат дода мешаванд');
INSERT INTO "pioneers" VALUES(16,86,'16:86','Онон, ки ширк варзиданд, вақте шарикони худро мебинанд, мегӯянд: «Парвардигоро, инҳо буданд он шариконе, ки мо ба ҷойи ту мехондем» ва[-ле шарикон] сухани ононро рад мекунанд, ки: «яқинан шумо дурӯғгӯед»');
INSERT INTO "pioneers" VALUES(16,87,'16:87','Дар он рӯз дар баробари Аллоҳ таоло сари таслим меафкананд ва дурӯғҳое, ки [дар бораи шафоати ғайри Аллоҳ таоло] мебофтанд, ҳама маҳву нобуд мегардад');
INSERT INTO "pioneers" VALUES(16,88,'16:88','Касоне, ки куфр варзиданд ва [мардумро] аз роҳи Аллоҳ таоло боздоштанд, ба [кайфари] он ки фасод мекарданд, азобе бар азобашон меафзоем');
INSERT INTO "pioneers" VALUES(16,89,'16:89','Ва рӯзе [-ро ёд кун], ки аз ҳар уммате гувоҳе аз худашон бар онон мегирем [ки ба куфру имонашон гувоҳӣ диҳад] ва туро [эй Муҳаммад] бар инон гувоҳ мегирем; ва Мо ин китоб [Қуръон] - ро бар ту нозил кардем, ки равшангари ҳама чиз аст ва барои мусулмонон раҳнамуд ва раҳмату башорат аст');
INSERT INTO "pioneers" VALUES(16,90,'16:90','Дар ҳақиқат, Аллоҳ таоло [шуморо] ба адлу накукорӣ ва бахшиш ба хешовандон фармон медиҳад ва аз фаҳшо ва зишткорӣ ва саркашӣ наҳй мекунад. Ӯ шуморо панд медиҳад; бошад, ки панд гиред');
INSERT INTO "pioneers" VALUES(16,91,'16:91','Ва чун бо Аллоҳ таоло [ё мардум] паймон бастед, ба он вафо кунед; ва савгандҳои худро пас аз муҳкам сохтанашон нашканед, дар ҳоле ки Аллоҳро бар [вафодории] хеш гувоҳ қарор додаед. Бе тардид, Аллоҳ таоло медонад, ки чи мекунед');
INSERT INTO "pioneers" VALUES(16,92,'16:92','Ва монанди он зан [-и нодоне] набошед, ки риштаи [пашмии тобидаи] хешро аз ҳам бозкушод [ва гусаст; пас, мабодо] савгандҳои худро василаи фиреби якдигар қарор диҳед [он ҳам танҳо] бад-ин сабаб, ки гурӯҳе [сарват ва] ҷамъияташон афзунтар аз дигаре аст. Ҷуз ин нест, ки Аллоҳ таоло шуморо бо ин [кор] меозмояд ва яқинан рӯзи қиёмат он чиро, ки дар он ихтилоф мекардед, бароятон равшан месозад');
INSERT INTO "pioneers" VALUES(16,93,'16:93','Ва агар мехост, ҳамаи шуморо як уммат қарор медод, вале [бо адолати хеш] ҳар киро бихоҳад гумроҳ мекунад ва [бо фазли хеш] ҳар киро бихоҳад, ҳидоят менамояд; ва шумо [рӯзи қиёмат], ҳатман, аз он чи мекардед, бозхост хоҳед шуд');
INSERT INTO "pioneers" VALUES(16,94,'16:94','Ва савгандҳоятонро василаи найранг [ва фиребгарӣ] миёни хеш қарор надиҳед, то мабодо [имонатон ба хатар афтад ва] гоме пас аз устувориаш билағжад ва ба сазои он ки [мардумро] аз роҳи Аллоҳ таоло боздоштаед, [кайфари] ногуворе бичашед ва азобе бузург бароятон [дар пеш] бошад');
INSERT INTO "pioneers" VALUES(16,95,'16:95','Ва паймони Аллоҳро ба баҳои андак нафурӯшед. Агар бидонед, яқинан он чи назди Аллоҳ таоло аст, бароятон беҳтар аст');
INSERT INTO "pioneers" VALUES(16,96,'16:96','Он чи назди шумост, поён мепазирад ва[-ле] он чи назди Аллоҳ таоло аст, боқӣ [ва ҷовид] аст; ва, ҳатман, ба касоне, ки шикебоӣ кардаанд, аҷрашонро бар асоси накутарин [тоат ва] коре, ки мекарданд, хоҳем дод');
INSERT INTO "pioneers" VALUES(16,97,'16:97','Ҳар кас – аз мард ё зан – ки кори нек кунад ва муъмин бошад, ҳатман, ӯро [дар дунё] ба зиндагонии поку писандидае зинда медорем ва [дар охират низ] бар асоси накутарин [тоат ва] коре, ки мекарданд, ба онон подош медиҳем');
INSERT INTO "pioneers" VALUES(16,98,'16:98','Пас, [эй муъмин] ҳангоме ки Қуръон мехонӣ, аз [шарри] шайтони рондашуда ба Аллоҳ таоло паноҳ бибар');
INSERT INTO "pioneers" VALUES(16,99,'16:99','Бе тардид, ӯ бар касоне, ки имон овардаанд ва бар Парвардигорашон таваккал мекунанд [ҳеҷ] тасаллуте надорад');
INSERT INTO "pioneers" VALUES(16,100,'16:100','Тасаллути ӯ танҳо бар касонест, ки ӯро [дӯсту] корсози худ гирифтаанд ва [ҳамчунин бар] касоне, ки ба Аллоҳ таоло ширк меварзанд');
INSERT INTO "pioneers" VALUES(16,101,'16:101','Ва ҳангоме ки [ҳукми] оятеро ҷойгузини ояте [дигар] мекунем – ҳол он ки Аллоҳ таоло ба он чи нозил мекунад, донотар аст - [кофирон] мегӯянд: «[Эй Муҳаммад] ҷуз ин нест, ки ту дурӯғбофӣ». [Чунин нест] Балки бештарашон [аз аҳкоми шаръ ва ҳикмати илоҳӣ чизе] намедонанд');
INSERT INTO "pioneers" VALUES(16,102,'16:102','Бигӯ: «Рӯҳу-л-қудус [Ҷабраил] онро аз ҷониби Парвардигорат бар ҳақ нозил кардааст, то касонеро, ки имон овардаанд, собитқадам гардонад ва барои мусулмонон ҳидоят ва башорате бошад»');
INSERT INTO "pioneers" VALUES(16,103,'16:103','Ва яқинан медонем, ки онон мегӯянд: «Ҳатман, башаре [ин оётро] ба ӯ омӯзиш медиҳад». [На, чунин нест; зеро] забони касе, ки [ин] нисбатро ба ӯ медиҳад, ғайри арабӣ аст, ҳол он ки ин [Қуръон] ба забони арабии равшан аст');
INSERT INTO "pioneers" VALUES(16,104,'16:104','Яқинан, касоне, ки ба оёти Аллоҳ таоло имон намеоваранд, Аллоҳ таоло ҳидояташон намекунад ва барои онон азоби дардноке [дар пеш] аст');
INSERT INTO "pioneers" VALUES(16,105,'16:105','[Муҳаммад дурӯғгӯ нест, балки] Касоне дурӯғ мебофанд, ки ба оёти Аллоҳ таоло имон надоранд ва инон дурӯғгӯён [-и воқеӣ] ҳастанд');
INSERT INTO "pioneers" VALUES(16,106,'16:106','Ҳар ки баъд аз имонаш ба Аллоҳ куфр биварзад [гирифтори азоби илоҳӣ хоҳад шуд]; магар касе, ки [ба ин кор] водор шуда бошад ва[-ле] дилаш ба имон ором [ва устувор] бошад; аммо ҳар ки синаашро ба куфр кушода кард, хашми Аллоҳ таоло бар онон аст ва азобе саҳмгин [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(16,107,'16:107','Ин [муҷозот] аз он рӯст, ки зиндагии дунёро бар охират тарҷеҳ доданд; ва Аллоҳ таоло гурӯҳи кофиронро ҳидоят намекунад');
INSERT INTO "pioneers" VALUES(16,108,'16:108','Инон касоне ҳастанд, ки [бар асари гуноҳонашон] Аллоҳ таоло бар дилҳо ва гӯшу чашмонашон муҳр ниҳодааст; ва инон ғофилон [-и воқеӣ] ҳастанд');
INSERT INTO "pioneers" VALUES(16,109,'16:109','Яқинан онон дар охират зиёнкоранд');
INSERT INTO "pioneers" VALUES(16,110,'16:110','Сипас Парвардигорат нисбат ба касоне, ки пас аз заҷр кашидан [аз озори мушрикон] ҳиҷрат карданд ва сипас ба ҷиҳод бархостанд ва шикебоӣ намуданд, Парвардигорат [нисбат ба онон] пас аз он [ҳама сахтиҳо], ҳатман, омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(16,111,'16:111','[Эй паёмбар, ёд кун аз] Рӯзе, ки ҳар кас дар ҳоле [ба пешгоҳи адли илоҳӣ] меояд, ки [танҳо] аз худ дифоъ мекунад; ва ҳар кас дар баробари он чи кардааст, бе каму кост подош мегирад ва бар онон ситам намеравад');
INSERT INTO "pioneers" VALUES(16,112,'16:112','Ва Аллоҳ таоло шаҳре [ҳамчун Макка]-ро мисол мезанад, ки амну ором буд ва рӯзиаш аз ҳар сӯ ба осонӣ ва фаровонӣ мерасид, вале [мардумонаш] нисбат ба неъматҳои илоҳӣ носипосӣ намуданд ва он гоҳ Аллоҳ таоло низ ба сазои он чи мекарданд, таъми гуруснагӣ ва тарсро ба [аҳли] он чашонид');
INSERT INTO "pioneers" VALUES(16,113,'16:113','Ва паёмбаре аз худшон ба сӯйи онон омад, аммо ӯро дурӯғгӯ ангоштанд; он гоҳ, дар ҳоле ки ситамгор [ва мушрик] буданд, азоби [илоҳӣ] ононро фурӯ гирифт');
INSERT INTO "pioneers" VALUES(16,114,'16:114','Пас аз он чи Аллоҳ таоло рӯзиатон кардааст, ҳалолу покиза бихӯред ва агар фақат Ӯро мепарастед, бар неъмати илоҳӣ шукр гузоред');
INSERT INTO "pioneers" VALUES(16,115,'16:115','Ҷуз ин нест, ки [Аллоҳ таоло] фақат мурдор ва хун ва гӯшти хук ва он чиро, ки [ҳангоми забҳ] номи ғайри Аллоҳ таоло бар он бурда шудааст, бар шумо ҳаром кардааст; пас, ҳар ки [барои ҳифзи ҷонаш ба хӯрдани онҳо] ночор шавад [ва] саркашу зиёдахоҳ набошад, [бар ӯ гуноҳ нест, чаро ки] бе тардид, Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(16,116,'16:116','Ва [эй мушрикон] бо дурӯғе, ки бар забонатон ҷорӣ мешавад, нагӯед: «Ин ҳалол аст ва он ҳаром», то бар Аллоҳ таоло дурӯғ бандед. Касоне, ки бар Аллоҳ таоло дурӯғ мебанданд, ҳаргиз растагор намешаванд');
INSERT INTO "pioneers" VALUES(16,117,'16:117','[Натиҷаи дунёдӯстияшон] Баҳрае андак аст ва [дар охират] азобе дарднок [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(16,118,'16:118','Ва бар яҳудиён он чиро пештар бар ту ҳикоят кардем, ҳаром намудем ва Мо ба онон ситам накардем; балки онҳо худ [буданд, ки дар натиҷаи нофармонӣ] ба хештан ситам мекарданд');
INSERT INTO "pioneers" VALUES(16,119,'16:119','Он гоҳ Парвардигорат нисбат ба касоне, ки аз рӯи нодонӣ гуноҳ карданд ва сипас аз он [иштибоҳот] тавба намуданд ва дурусткорӣ карданд, ҳатман, Парвардигорат пас аз он омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(16,120,'16:120','Бе гумон, Иброҳим пешвое фурӯтан дар баробари Аллоҳ таоло ва ҳақгаро буд ва [ҳаргиз] дар зумраи мушрикон набуд');
INSERT INTO "pioneers" VALUES(16,121,'16:121','Ӯ шукргузори неъматҳои Аллоҳ таоло буд. [Аллоҳ таоло] Вайро баргузид ва ба роҳи рост ҳидоят намуд');
INSERT INTO "pioneers" VALUES(16,122,'16:122','[Мо] Дар дунё ба ӯ некӣ ато кардем ва, ҳатман, ӯ дар охират [низ] аз шоистагон аст');
INSERT INTO "pioneers" VALUES(16,123,'16:123','Сипас [эй Паёмбар] ба ту ваҳй кардем, ки аз оини Иброҳим пайравӣ кун, ки ҳақгаро буд ва [ҳаргиз] дар зумраи мушрикон набуд');
INSERT INTO "pioneers" VALUES(16,124,'16:124','[Вуҷуби бузургдошти рӯзҳои] Шанбе танҳо бар касоне қарор дода шуд, ки дар он ихтилоф карданд; ва, ҳатман, Парвардигорат рӯзи қиёмат дар мавриди он чи ихтилоф мекарданд, миёнашон доварӣ хоҳад кард');
INSERT INTO "pioneers" VALUES(16,125,'16:125','[Эй Паёмбар, мардумро] Бо ҳикмат ва панди наку ба роҳи Парвардигорат даъват намо ва бо равише, ки накутар аст, бо онон [баҳсу] мунозира кун. Бе тардид, Парвардигорат ба [ҳоли] касе, ки аз роҳи ӯ гумроҳ шуд, донотар аст ва ӯ ба [ҳоли] ҳидоятёфтагон [низ] донотар аст');
INSERT INTO "pioneers" VALUES(16,126,'16:126','Ва агар [шумо муъминон] хостед [душманонатонро] муҷозот кунед, пас, чунон муҷозот кунед, ки [онон] шуморо муҷозот кардаанд; ва агар [хештандорӣ ва] шикебоӣ кунед, [бидонед, ки] ҳатман, ин [кор] барои шикебоён беҳтар аст');
INSERT INTO "pioneers" VALUES(16,127,'16:127','Ва [эй Паёмбар] шикебоӣ пеша кун ва шикебоият ҷуз ба [ёрӣ ва] тавфиқи Аллоҳ таоло нест; ва бар онон [кофирон] андӯҳ махӯр ва аз найранге, ки меварзанд, дилтанг мабош');
INSERT INTO "pioneers" VALUES(16,128,'16:128','Бе тардид, Аллоҳ таоло бо касоне аст, ки парҳезкорӣ карданд ва касоне, ки накукоранд');
INSERT INTO "pioneers" VALUES(17,1,'17:1','Поку муназзаҳ аст он [Парвардигоре] ки бандааш [Муҳаммад]-ро шабонгоҳ аз масҷиду-л-ҳаром ба сӯйи Масҷид-ул-ақсо – ки перомунашро баракат додаем – сайр дод, то бархе аз нишонаҳои [қудрати] хешро ба ӯ бинмоёнем. Бе тардед, Ӯ ҳамон шунавои биност');
INSERT INTO "pioneers" VALUES(17,2,'17:2','Ва Мо ба Мӯсо китоби [Таврот] додем ва онро [мояи] ҳидояти Бани Исроил сохтем [ва фармудем], ки: «Ҷуз ман коргузоре [барои худ] барнагузинед»');
INSERT INTO "pioneers" VALUES(17,3,'17:3','Эй фарзандони касоне, ки бо Нуҳ [бар киштӣ] саворашон кардем, ба ростӣ ӯ [Нуҳ] бандае шукргузор буд');
INSERT INTO "pioneers" VALUES(17,4,'17:4','Ва дар китоби [Таврот] ба Бани Исроил ваҳй кардем, ки ҳатман, ду бор дар замин фасод хоҳед кард ва ҳатман, саркашӣ [ва туғёни] бузурге хоҳед намуд');
INSERT INTO "pioneers" VALUES(17,5,'17:5','Пас, ҳар гоҳ ваъдаи нахустин аз он ду [бор] фаро расид, гурӯҳе аз бандагони бисёр неруманд [ва пайкорҷӯйи] худро бар шумо бармеангезем, то миёни хонаҳо [-ятон барои қатлу ғорати шумо] ба ҷустуҷӯ дароянд; ва ин ваъдаи анҷомшуданӣ [ва қатъӣ] буд');
INSERT INTO "pioneers" VALUES(17,6,'17:6','Он гоҳ [пас аз муддате] шуморо бар онон мусаллат мегардонем ва бо амволу фарзандон ёриатон мекунем, ва [теъдоди] нафароти шуморо бештар [аз душман] мегардонем');
INSERT INTO "pioneers" VALUES(17,7,'17:7','Агар [дар гуфтору рафтор] некӣ кунед, ба худ некӣ кардаед; ва агар бадӣ кунед, [боз ҳам ба] худ кардаед. Пас, ҳангоме ки ваъдаи дигар фаро расид [душман ончунон хоратон месозад], ки осори ғаму андӯҳ дар чеҳраҳоятон зоҳир мешавад ва ҳамон гуна ки бори нахуст ворид шуданд [ин бор низ ҳамла меоваранд], то ба Масҷид[-ул-ақсо] дохил шаванд ва то бар ҳар чи даст ёбанд, яксара нобуд кунанд');
INSERT INTO "pioneers" VALUES(17,8,'17:8','[Эй Бани Исроил агар тавба кунед] Умед аст, ки Парвардигоратон бар шумо раҳмат оварад ва агар [ба табоҳӣ] бозгаштед, Мо низ бозмегардем ва дузахро зиндони кофирон кардаем');
INSERT INTO "pioneers" VALUES(17,9,'17:9','Ба ростӣ, ки ин Қуръон ба устувортарин роҳ ҳидоят мекунад ва ба муъминоне, ки корҳои шоиста мекунанд, мужда медиҳад, ки барояшон подоше бузург [дар пеш] аст');
INSERT INTO "pioneers" VALUES(17,10,'17:10','Ва барои касоне, ки ба охират имон намеоваранд, азобе дарднок муҳайё кардаем');
INSERT INTO "pioneers" VALUES(17,11,'17:11','Инсон [дар ҳангоми хашм, аз рӯи нодонӣ ва шитоб нафрин мекунад ва] бадиро металабад, ҳамон гуна ки некиро мехоҳад; ва инсон ҳамвора шитобзада аст');
INSERT INTO "pioneers" VALUES(17,12,'17:12','Шабу рӯзро ду нишона [-и қудрату ягонагии хеш] қарор додем; ва нишонаи шабро маҳв [ва тира] кардем ва нишонаи рӯзро ошкору равшан қарор додем, то аз Парвардигоратон рӯзӣ биҷӯед ва шумори солҳо ва ҳисоб [-и вақоеъ]-ро бидонед; ва ҳар чизеро ба тафсил баён кардем');
INSERT INTO "pioneers" VALUES(17,13,'17:13','Ва [сарнавишти] рафтори ҳар инсонеро [то лаҳзаи ҳисобрасӣ] дар гарданаш бастаем ва рӯзи қиёмат барояш навиштае берун меоварем, ки онро кушода мебинад');
INSERT INTO "pioneers" VALUES(17,14,'17:14','[Ба ӯ мегӯем] «Номаатро бихон. Кофӣ аст, ки имрӯз худ ҳисобгари хеш бошӣ»');
INSERT INTO "pioneers" VALUES(17,15,'17:15','Ҳар ки роҳ [-и рост] ёфт, ҷуз ин нест, ки ба суди хеш роҳ ёфтааст ва ҳар ки гумроҳ шуд, яқинан ба зиёни худ гумроҳ шудааст; ва ҳеҷ гунаҳкоре бори гуноҳи дигареро барнамедорад; ва мо ҳаргиз [қавмеро] муҷозот нахоҳем кард, магар он ки [миёнашон] паёмбаре мабъус карда бошем [то вазоифашонро баён кунад]');
INSERT INTO "pioneers" VALUES(17,16,'17:16','Ва чун бихоҳем, [мардуми] шаҳреро нобуд кунем, [нахуст] ба сарватмандони cаркаши он ҷо фармони] итоат аз Аллоҳ таоло ва пайравӣ аз паёмбарон] медиҳем, сипас вақте дар он ҷо ба фасод [ва мухолифат] бархостанд, ваъдаи [азоби илоҳӣ бар] он муҳаққақ мегардад; пас, ононро ба сахтӣ нобуд мекунем');
INSERT INTO "pioneers" VALUES(17,17,'17:17','Ва чи бисёр наслҳоеро, ки пас аз Нуҳ [зиндагӣ мекарданд] ҳалок кардем ва [ҳамин] кофӣ аст, ки Парвардигорат аз гуноҳони бандагонаш огоҳ [ва нисбат ба онон] биност');
INSERT INTO "pioneers" VALUES(17,18,'17:18','Ҳар ки хоҳони [дунёе] зудгузар аст, ҳар чи бихоҳем [ва] ба ҳар ки бихоҳем [баҳрае] аз он медиҳем; он гоҳ дузахро насибаш мекунем, ки дар он ҷо хору рондашуда ворид мегардад');
INSERT INTO "pioneers" VALUES(17,19,'17:19','Ва ҳар ки [зиндагии] охиратро бихоҳад ва барои [расидан ба] он бо ҷиддият бикӯшад ва муъмин бошад, пас, инонанд, ки аз талошашон қадрдонӣ хоҳад шуд');
INSERT INTO "pioneers" VALUES(17,20,'17:20','Ҳамаро - [хоҳ] ин гурӯҳи [бадкор] ва [ё] он гурӯҳи [накукор] - аз бахшиш [ва неъматҳои дунявии] Парвардигорат баҳраманд месозем ва бахшиши Парвардигорат [аз касе] манъ нашудааст');
INSERT INTO "pioneers" VALUES(17,21,'17:21','Бингар, ки чигуна бархе аз ононро [аз лиҳози молу мақом] бар бархи дигар бартари додаем ва қатъан охират [дар муқоиса бо дунё] аз назари дараҷот, бузургтар ва аз лиҳози фазилат, бартар аст [пас муъмин бояд ба он иштиёқ варзад]');
INSERT INTO "pioneers" VALUES(17,22,'17:22','[Эй инсон, ҳаргиз ҳеҷ] Маъбуди дигареро бо Аллоҳ таоло [шарик] қарор надеҳ, ки накуҳида ва беёвар хоҳӣ монд');
INSERT INTO "pioneers" VALUES(17,23,'17:23','Ва Парвардигорат чунин фармон додааст, ки: «Ҷуз Ӯро напарастед ва ба падару модар некӣ кунед. Ҳар гоҳ яке аз он ду ё ҳар дуи онҳо дар канори ту ба синни пирӣ расиданд, ҳатто камтарин иҳонате ба эшон накун ва бар сарашон фарёд назан ва бо онон ба некӣ [ва меҳрубонӣ] сухан бигӯ');
INSERT INTO "pioneers" VALUES(17,24,'17:24','Ва аз рӯйи меҳрубонӣ дар баробарашон фурӯтан бош ва бигӯ: «Парвардигоро, ҳамон гуна ки [бар ман раҳмат оварданд ва] дар кӯдакӣ парваришам доданд, [Ту низ] ба онон раҳмат овар»');
INSERT INTO "pioneers" VALUES(17,25,'17:25','Парвардигори шумо ба он чи дар дилҳоятон аст, огоҳтар аст. Агар шоиста бошед, яқинан Ӯ дар ҳаққи тавбакорон омурзанда аст');
INSERT INTO "pioneers" VALUES(17,26,'17:26','Ва ҳаққи хешовандро ба ӯ бидеҳ [силаи раҳим ба ҷой овар] ва тиҳидаст ва дарроҳмонда [-ро ёрӣ кун] ва ҳеҷ исрофкорӣ накун');
INSERT INTO "pioneers" VALUES(17,27,'17:27','Дар ҳақиқат, исрофкорон бародарони шаётин ҳастанд ва шайтон нисбат ба Прарвардигораш ҳамвора носипос будааст');
INSERT INTO "pioneers" VALUES(17,28,'17:28','Ва агар ба умеди раҳмат ва [кушоиши рӯзӣ] аз ҷониби Парвардигорат [ба ночор] аз онон рӯй мегардонӣ, пас, ба эшон сухане нарм бигӯ');
INSERT INTO "pioneers" VALUES(17,29,'17:29','Ва [ҳаргиз] даст аз инфоқ набанд ва [низ] кушодадастии бисёр накун, ки маломатшуда ва ҳасратзада мегардӣ');
INSERT INTO "pioneers" VALUES(17,30,'17:30','Бе гумон, Парвардигорат [неъмат ва] рӯзиро бар ҳар кӣ бихоҳад, густурда медорад ва ё танг мегирад. Бе тардид, Ӯ ҳамвора ба [аҳволи] бандагонаш огоҳ ва биност');
INSERT INTO "pioneers" VALUES(17,31,'17:31','Ва фарзандонатонро аз бими тангдастӣ накушед. Мо ба эшон ва шумо рӯзӣ мебахшем. Яқинан куштани онҳо ҳамвора хатое бузург аст.');
INSERT INTO "pioneers" VALUES(17,32,'17:32','Ва ба зино наздик нашавед. Бе тардид, он [кор] бисёр зишту роҳе нописанд аст');
INSERT INTO "pioneers" VALUES(17,33,'17:33','Ва касеро, ки Аллоҳ таоло [куштанашро] ҳаром кардааст, ҷуз ба ҳақ накушед; ва ҳар ки ба ситам кушта шавад, ба валии ӯ қудрат [ва ҳаққи қисос] додаем; пас, вай низ набояд дар [кайфари] қатл зиёдаравӣ кунад. Бе тардид, ӯ [валии дам] таҳти ҳимоя [-и Аллоҳ таоло ва шаръи муқаддас] қарор дорад');
INSERT INTO "pioneers" VALUES(17,34,'17:34','Ва ба моли ятим – ҷуз шевае, ки накутар аст – наздик нашавед, то он гоҳ ки ба ҳадди [рушд ва] булуғаш бирасад ва ба паймон [-и хеш] вафо кунед; чаро ки [аз шумо] дар бораи паймон пурсида хоҳад шуд');
INSERT INTO "pioneers" VALUES(17,35,'17:35','Ва ҳангоме ки [дар хариду фурӯш аҷносро] паймона мекунед, паймонаро комил [-у тамом] бидиҳед ва [онҳоро] бо тарозуи дуруст вазн кунед. Ин [кор] барои шумо беҳтар ва саранҷомаш накутар аст');
INSERT INTO "pioneers" VALUES(17,36,'17:36','Ва аз он чи дар борааш донише надорӣ, пайравӣ накун. Бе тардид, [дар қиёмат] гӯшу чашм ва дил аз ҳама инҳо бозхост хоҳад шуд');
INSERT INTO "pioneers" VALUES(17,37,'17:37','Ва дар замин бо такаббур роҳ нарав. Ту ҳаргиз наметавонӣ [бо роҳ рафтани ғуруромезат] заминро бишкофӣ ва дар баландӣ [ва гарданфарозӣ низ] ба кӯҳҳо намерасӣ');
INSERT INTO "pioneers" VALUES(17,38,'17:38','Ҳамаи инҳо назди Парвардигорат нописанд аст.');
INSERT INTO "pioneers" VALUES(17,39,'17:39','Ин аз он ҳикмат [-ҳое] аст, ки аз ҷониби Парвардигорат ба ту ваҳй шудааст; ва бо Аллоҳ таоло маъбуди дигаре қарор надеҳ, ки накуҳида ва рондашуда ба дузах хоҳӣ афтод');
INSERT INTO "pioneers" VALUES(17,40,'17:40','Оё [шумо мушрикон гумон мекунед, ки] Парвардигоратон [доштани] писаронро ба шумо ихтисос додааст ва худ аз [миёни] фариштагон духтароне баргирифтааст? Ба ростӣ, ки сухане бисёр бузург [ва нораво] мегӯед');
INSERT INTO "pioneers" VALUES(17,41,'17:41','Ва бе тардид, Мо дар ин Қуръон [аҳкому андарзҳоро бо шеваҳои] гуногун баён кардем, то [мардум] панд бигиранд; ва[-ле ин суханон] ҷуз дурӣ [аз ҳақ] бар онон намеафзояд');
INSERT INTO "pioneers" VALUES(17,42,'17:42','[Эй паёмбар] Бигӯ: «Агар [бар фарзи маҳол] чунон ки [мушрикон] мегӯянд, бо Ӯ [Аллоҳ таоло] маъбудоне [дигар] буд, дар ин сурат, онон [барои тақарруб ва касби хушнудии Парвардигор] ба сӯи [Аллоҳ таоло] соҳиби Арш роҳе меҷустанд»');
INSERT INTO "pioneers" VALUES(17,43,'17:43','Ӯ поку муназзаҳ аст ва аз он чи онон мегӯянд, басе бартар аст');
INSERT INTO "pioneers" VALUES(17,44,'17:44','Осмонҳои ҳафтгонаву замин ва ҳар ки дар онҳост, Ӯро ба покӣ меситоянд; ва ҳеҷ чиз нест, магар он ки Ӯро ба покӣ ёд мекунад; вале шумо тасбеҳи ононро дарнамеёбед. Бе тардид, Ӯ ҳамвора бурдбор [ва] омурзанда аст');
INSERT INTO "pioneers" VALUES(17,45,'17:45','Ва [эй паёмбар] ҳангоме ки Қуръон мехонӣ, миёни ту ва касоне, ки ба охират имон надоранд, пардае пӯшида қарор медиҳем');
INSERT INTO "pioneers" VALUES(17,46,'17:46','Ва бар дилҳояшон пӯшише қарор додем, то [паёми] онро дарнаёбанд ва дар гӯшҳояшон сангинӣ [ниҳодем, то ҳақро нашнаванд]; ва чун Парвардигоратро дар Қуръон ба ягонагӣ ёд мекунӣ, бо нафрат пушт мекунанд [ва мегурезанд]');
INSERT INTO "pioneers" VALUES(17,47,'17:47','Ва он гоҳ ки [кофирон] ба ту гӯш месупоранд, Мо беҳтар медонем, ки ба чи [манзур ва чи гуна] гӯш медиҳанд ва [низ] он гоҳ ки наҷво мекунанд [Мо аз суханонаншон огоҳем]; он гоҳ ки ситмакорон [-и мушрик ба мардум] мегӯянд: «Шумо ҷуз аз марди ҷодушудае пайравӣ намекунед»');
INSERT INTO "pioneers" VALUES(17,48,'17:48','Бингар, ки чи гуна бароят мисолҳо задаанд, пас, гумроҳ шуданд ва дар натиҷа наметавонанд роҳе [ба сӯйи ҳақ] бибаранд');
INSERT INTO "pioneers" VALUES(17,49,'17:49','Ва [мушрикон] гуфтанд: «Оё ҳангоме ки устухонҳое [пӯсида ва] пароканда шудем, дубора бо офариниши ҷадид барангехта мешавем?»');
INSERT INTO "pioneers" VALUES(17,50,'17:50','Бигӯ: «[Ҳатто агар] Санг бошед ё оҳан');
INSERT INTO "pioneers" VALUES(17,51,'17:51','Ё [Ҳар] Офаридае, ки дар хотиратон бузург менамояд [боз ҳам Аллоҳ таоло метавонад шуморо зинда кунад]. Пас, хоҳанд гуфт: «Чи касе моро бозмегардонад?». Бигӯ: [Ҳамон Парвардигоре, ки] ки нахустин бор шуморо офарид». Он гоҳ сарҳояшонро [ба нишонаи нобоварӣ] муқобилат такон медиҳанд ва мегӯянд: «Он [ҳодиса] кай хоҳад буд?». Бигӯ: «Чи басо наздик бошад');
INSERT INTO "pioneers" VALUES(17,52,'17:52','Рӯзе, ки [Аллоҳ таоло] шуморо [ба саҳрои маҳшар] фаро мехонад ва шумо бо сипосу ситоиш иҷобаташ мекунед ва мепиндоред, ки ҷуз андаке [дар дунё] ба сар набурдаед»');
INSERT INTO "pioneers" VALUES(17,53,'17:53','Ва [эй Паёмбар] бандагонамро бигӯ бо беҳтарин [алфоз бо якдигар] суҳбат кунанд [ва аз баддаҳонӣ бипарҳезанд; чаро ки] бе тардид, шайтон миёнашон душманӣ меафканад. Ба ростӣ, ки шайтон барои инсон душмане ошкор аст');
INSERT INTO "pioneers" VALUES(17,54,'17:54','[Эй мардум] Парвардигоратон ба [аҳволи] шумо донотар аст; агар бихоҳад, бар шумо раҳмат меоварад ё агар бихоҳад, азобатон мекунад; ва [эй Паёмбар] Мо туро [барои онон нигаҳбон ва] муроқиб нафиристодаем');
INSERT INTO "pioneers" VALUES(17,55,'17:55','Парвардигорат ба [ҳар чи ва] ҳар ки дар осмонҳо ва замин аст, донотар аст; ва ба ростӣ, [Мо] бархе паёмбаронро [бо афзунии пайравон ва додани китоб] бар бархе [дигар] бартарӣ бахшидем ва ба Довуд Забур додем');
INSERT INTO "pioneers" VALUES(17,56,'17:56','[Эй Паёмбар, ба мушрикон] Бигӯ: «Касонеро, ки ба ҷойи Ӯ [маъбуд ва корсози хеш] пиндоштаед, фаро бихонед; пас, [хоҳед дид, ки] наметавонанд газандеро аз шумо дур кунанд ва на қодиранд, онро [сӯйи дигаре] бигардонанд»');
INSERT INTO "pioneers" VALUES(17,57,'17:57','Касонеро, ки онон мехонанд, худ [бо парастиш ва накукорӣ] василае [барои тақарруб] ба Парвардигорашон меҷӯянд [ва дар рақобатанд, то бидонанд] кадом як аз аз онон [ба парастиши Аллоҳ таоло] наздиктаранд ва ба раҳматаш умедворанд ва аз азобаш метарсанд; [чаро ки] бе тардид, азоби Парвардигорат ҳамвора шоистаи парҳез аст');
INSERT INTO "pioneers" VALUES(17,58,'17:58','Ва ҳеҷ шаҳре нест, ки магар ин ки [дар сурати нофармонӣ мардуми] онро пеш аз рӯзи қиёмат нобуд мекунем ё ба азобе сахт гирифтор месозем. Ин [кайфар] дар китоби илоҳӣ [Лавҳи Маҳфуз] сабт аст');
INSERT INTO "pioneers" VALUES(17,59,'17:59','[Чизе] Моро аз фиристодани муъҷизот [дархости мушрикон] бознадошт, магар ин ки пешиниён онро дурӯғ пиндоштанд; ва Мо ба Самуд [қавми Солеҳ] он модашутурро ба унвони нишонаи равшан додем; пас, ба он ситам карданд ва Мо нишонаҳо [ва муъҷизот]-ро ҷуз барои бим додан [-и мардум] намефиристем.');
INSERT INTO "pioneers" VALUES(17,60,'17:60','Ва [ба ёд овар] ҳангоме ки ба ту гуфтем: «Яқинан, Парвардигорат бар мардум иҳота дорад»; ва он руъёеро, ки [дар шаби меъроҷ] ба ту намоёндем; ва [ҳамчунин] он дарахти лаънатшуда [-и зақум] дар Қуръонро ҷуз барои озмоиши мардум қарор надодем; ва Мо ононро бим медиҳем, вале ҷуз бар cаркашии бештарашон намеафзояд');
INSERT INTO "pioneers" VALUES(17,61,'17:61','Ва [ёд кун аз] ҳангоме ки ба фариштагон гуфтем: «Барои Одам саҷда кунед»; пас, [ҳамагӣ] саҷда карданд, ба ҷуз Иблис, ки гуфт: «Оё барои касе саҷда кунам, ки [ӯро] аз гил офаридаӣ?»');
INSERT INTO "pioneers" VALUES(17,62,'17:62','[Ва] Гуфт: «Ба ман хабар бидеҳ, ин аст он ки бар ман [бартарӣ додӣ ва] гиромӣ доштӣ? Агар ба ман то рӯзи қиёмат муҳлат диҳӣ, фарзандонашро – ҷуз иддаи каме- [маҳорзада ва] решакан хоҳам кард»');
INSERT INTO "pioneers" VALUES(17,63,'17:63','[Аллоҳ таоло] Фармуд: «Бирав, ки ҳар як аз эшон аз ту пайравӣ кунад, пас, яқинан дузах подошатон хоҳад буд, ки подоше комил аст');
INSERT INTO "pioneers" VALUES(17,64,'17:64','Ва аз эшон ҳар киро тавонистӣ, ба овози худ барангез [ва ба сӯйи гуноҳ бикаш] ва ба саворону пиёдагонат бар онон бонг бизан [то олудаи гуноҳ гарданд] ва дар амволу фарзандон шарикашон шав ва ба онон ваъда[-ҳои воҳӣ] бидеҳ; ва[-ле] шайтон ба онон ваъда намедиҳад, магар ба фиреб');
INSERT INTO "pioneers" VALUES(17,65,'17:65','[Эй шайтон] Ту ҳаргиз бар бандагони ман тасаллуте нахоҳӣ дошт»; ва [эй Паёмбар] ҳамин бас, ки Парвардигорат [коргузору] нигаҳбони онон [муъминон] бошад');
INSERT INTO "pioneers" VALUES(17,66,'17:66','[Эй мардум] Парвардигори шумо касест, ки киштиро дар дарё бароятон ба ҳаракат дармеоварад, то аз фазлаш баҳраманд гардед. Бе тардид, Ӯ ҳамвора нисбат ба шумо меҳрубон аст');
INSERT INTO "pioneers" VALUES(17,67,'17:67','Ва он гоҳ ки дар дарё газанде ба шумо мерасад, ҳар киро [ба ҳоҷатхоҳӣ] мехонед, нопадид [ва фаромӯш] мегардад, магар [зоти поки] Ӯ; аммо вақте шуморо [аз хатари марг] раҳонад ва ба хушкӣ расонад, [аз яктопарастӣ] рӯйгардон мешавед; ва инсон ҳамвора носипос аст');
INSERT INTO "pioneers" VALUES(17,68,'17:68','Оё [мепиндоред] аз ин ки [Аллоҳ таоло] шуморо дар канораи хушкӣ [дар замин] фурӯ барад ё тӯфони сахте ба сӯятон бифиристад [ва сангборонатон кунад] эмин шудаед? Он гоҳ барои худ ҳеҷ [коргузор ва] нигаҳбоне намеёбед');
INSERT INTO "pioneers" VALUES(17,69,'17:69','Ё эмин аз ин шудаед, ки бори дигар шуморо ба дарё бозгардонад ва тундбоде кӯбанда ба сӯятон бифиристад ва ба сазои куфратон шуморо ғарқ кунад? Он гоҳ барои худ дар баробари Мо ҳеҷ бозпурсе намеёбед [ки далели корамонро ҷӯё шавад]');
INSERT INTO "pioneers" VALUES(17,70,'17:70','Ва ба ростӣ, Мо фарзандони Одамро гиромӣ доштем ва ононро дар хушкӣ ва дарё [бар васоили сафар] савор кардем ва аз анвои [неъматҳои] покиза ба онон рӯзӣ додем ва ононро бар бисёре аз мавҷудоте, ки офаридаем, бартарии бисёр бахшидем');
INSERT INTO "pioneers" VALUES(17,71,'17:71','Ва [ёд кун аз] рӯзе, ки ҳар гурӯҳеро бо пешвояшон фаро мехонем; пас, касоне, ки нома [и аъмол]-ашон ба [дасти] росташон дода мешавад, инон номаи худро [бо шодмонӣ] мехонанд ва кучактарин ситаме намебинанд; [ҳатто ба андозаи риштаи миёни ҳастаи хурмо]');
INSERT INTO "pioneers" VALUES(17,72,'17:72','Ва[-ле] касе, ки дар ин [дунё аз дидани ҳақ] нопайдо будааст, дар охират [низ] нобино ва гумроҳтар аст');
INSERT INTO "pioneers" VALUES(17,73,'17:73','[Эй Паёмбар] Наздик буд онҳо туро [бо васвасаҳои худ] аз он чи бар ту ваҳй кардаем, бифиребанд, то [алфозе] ғайр аз онро [ки ваҳй кардаем] ба дурӯғ ба Мо нисбат диҳӣ ва онгоҳ туро ба дӯстӣ бигиранд');
INSERT INTO "pioneers" VALUES(17,74,'17:74','Ва агар [Мо] туро собитқадам намекардем, ба ростӣ, наздик буд андаке ба онон гароиш биёбӣ');
INSERT INTO "pioneers" VALUES(17,75,'17:75','Дар он сурат, ҳатман, ду чандон [азоб дар ин] зиндагӣ ва ду чандон [пас аз] марг ба ту мечашондем ва он гоҳ барои хеш дар баробари мо ёваре намеёфтӣ');
INSERT INTO "pioneers" VALUES(17,76,'17:76','Ва наздик буд [ки кофирон бо найранг ва душманӣ] туро аз сарзамин [-и Макка] баркананд, то аз он ҷо берунат кунанд ва [агар чунин мекарданд] дар он сурат онон [низ] пас аз ту ҷуз [замони] андаке намемонданд [ва гирифтори азоб мешуданд]');
INSERT INTO "pioneers" VALUES(17,77,'17:77','Ин [шеваи бархӯрд бо миллатҳои носипос] суннати [Мо дар бораи] паёмбароне аст, ки пеш аз ту фиристодем; ва [ҳаргиз] дар суннати Мо [дигаргунӣ ва] тағйире намеёбӣ');
INSERT INTO "pioneers" VALUES(17,78,'17:78','Намозро аз заволи хуршед [ҳангоми зуҳр] то ниҳояти торикии шаб [нимаи шаб] бар по дор ва [ба вижа] намози субҳро. Ба ростӣ, ки намози субҳ [ва тиловати Қуръон дар ин вақт] мавриди мушоҳида [ва дар ҳузури фариштагони рӯзу шаб] аст');
INSERT INTO "pioneers" VALUES(17,79,'17:79','Ва [эй Паёмабр] посе аз шабро [аз хоб] бархез ва намоз бигузор. Ҳатман, [ин намози шаб] барои афзунии мақому мартабаи туст. Умед аст, Парвардигорат туро ба мақоме писандида [шафоат] барангезад');
INSERT INTO "pioneers" VALUES(17,80,'17:80','Ва бигӯ: «Парвардигоро, маро [дар ҳар коре] ба дурустӣ дохил намо ва ба дурустӣ хориҷ соз [чунон ки мавриди ризои туст]; ва аз ҷониби хеш барои ман [дар баробари душманонам] далеле ёрибахше қарор бидеҳ»');
INSERT INTO "pioneers" VALUES(17,81,'17:81','Ва бигӯ: «Ҳақ [ислом] омад ва ботил [ширк] нобуд шуд. Ба ростӣ, ки ботил нобудшуданӣ аст»');
INSERT INTO "pioneers" VALUES(17,82,'17:82','Ва аз Қуръон он чи дармони [ширку куфр] ва раҳмате барои муъминон аст, нозил мекунем ва [ин китоб] бар ситамгорони [мушрик] ҷуз зиён намеафзояд');
INSERT INTO "pioneers" VALUES(17,83,'17:83','Ва чун ба инсон неъмат [-и сарват ва саломатӣ] медиҳем, рӯй мегардонад ва [аз шукргузорӣ ва парастиш] дурӣ мекунад ва чун бадӣ [ва газанде] ба ӯ мерасад, [аз раҳмати илоҳӣ] ноумед мегардад');
INSERT INTO "pioneers" VALUES(17,84,'17:84','[Эй Паёмбар] Бигӯ: «Ҳар кас тибқи равиши хеш [ва мутаносиб бо ҳидоят ё гумроҳияш] рафтор мекунад; ва Парвардигоратон беҳтар медонад, ки чи касе ҳидоятёфтатар аст»');
INSERT INTO "pioneers" VALUES(17,85,'17:85','Ва [эй Паёмбар, кофирон] аз ту дарбораи Рӯҳ мепурсанд; бигӯ: Рӯҳ аз [ҷумлаи умури ғайбӣ ва бархоста аз] фармони Парвардигори ман аст ва ба ҷуз андаке дониш ба шумо надодаанд»');
INSERT INTO "pioneers" VALUES(17,86,'17:86','Ва агар мехостем, ҳатман, он чиро ба ту ваҳй кардаем, [аз ёдат] мебурдем; он гоҳ дар баробари Мо барои худ ҳеҷ [коргузор ва] нигаҳбоне намеёфтӣ [ки монеи фаромӯшиат гардад]');
INSERT INTO "pioneers" VALUES(17,87,'17:87','Вале [чунин накардем ва ин лутф ба хотири] раҳмате аз ҷониби Парвардигори туст. Бе тардид, ҳамвора бахшиши Ӯ бар ту бисёр аст');
INSERT INTO "pioneers" VALUES(17,88,'17:88','[Эй Паёмбар] Бигӯ: «Агар инсу ҷин [ҳамагӣ] ҷамъ шаванд, то ҳаммонанди ин Қуръонро биёваранд, [ҳаргиз] ҳаммонандашро нахоҳанд овард; ҳарчанд онон пуштибони якдигар бошанд»');
INSERT INTO "pioneers" VALUES(17,89,'17:89','Ва ба ростӣ, Мо дар ин Қуръон барои мардум аз ҳар мисол [ва андарзу достон] овардем, вале бештари мардум [аз пазириши он] худдорӣ карданд [ва] ҷуз сари инкор надоштанд');
INSERT INTO "pioneers" VALUES(17,90,'17:90','Ва гуфтанд: «Мо ҳаргиз ба ту имон намеоварем, магар ин ки аз замин [-и Макка] бароямон чашмае ҷорӣ кунӣ');
INSERT INTO "pioneers" VALUES(17,91,'17:91','Ё боғе аз нахл ва ангур дошта бошӣ ва минёни [дарахтони он] ҷӯйҳо равон гардонӣ');
INSERT INTO "pioneers" VALUES(17,92,'17:92','Ё чунончи иддао кардӣ, аз осмон пораҳое [аз азоб] бар сарамон бияфканӣ ё Аллоҳ таоло ва фариштагонро дар баробарамон ҳозир кунӣ [то ба ростгӯият гувоҳӣ диҳад]');
INSERT INTO "pioneers" VALUES(17,93,'17:93','Ё хонае [ороста аз тилло дошта бошӣ ё дар осмон боло равӣ; ва ҳаргиз боло рафтанатро бовар намекунем, магар он ки навиштае бар мо фуруд оварӣ, ки онро бихонем». Бигӯ: «Парвардигорам муназзаҳ аст. Оё ман ҷуз башаре фиристодашуда ҳастам?»');
INSERT INTO "pioneers" VALUES(17,94,'17:94','Ва [чизе] имон овардани мардум нашуд–он гоҳ ки ҳидоят барояшон омад–ҷуз ин ки гуфтанд: «Оё Аллоҳ таоло як инсонро ба паёмбарӣ барангехта аст?»');
INSERT INTO "pioneers" VALUES(17,95,'17:95','[Эй Паёмбар] Бигӯ: «Агар дар замин [ба ҷойи инсон] фариштагоне буданд, ки [ҳамчун шумо] бо оромиш роҳ мерафтанд, ҳатман, аз осмон фариштаеро [аз ҷинси худашон ба унвони] паёмбар бар онон нозил мекардем»');
INSERT INTO "pioneers" VALUES(17,96,'17:96','Бигӯ: «Кофист, ки Аллоҳ таоло миёни ману шумо гувоҳ бошад. Бе тардид, Ӯ ҳамвора [нисбат] ба бандагонаш огоҳ [ва] биност');
INSERT INTO "pioneers" VALUES(17,97,'17:97','Ва ҳар киро Аллоҳ таоло ҳидоят кунад, ӯ ҳидоятёфта [-и воқеӣ] аст ва ҳар киро гумроҳ созад, ҳаргиз барояшон ба ҷуз Ӯ [Аллоҳ таоло] дӯстоне нахоҳӣ ёфт; ва ононро дар қиёмат нобино ва гунг ва ношунаво маҳшур мегардонем, [дар ҳоле ки] бар суратҳояшон [кашида мешаванд]; ҷойгоҳашон дузах аст [ки] ҳар гоҳ шуълааш фурӯ нишинад, шарорае бар онон меафзоем');
INSERT INTO "pioneers" VALUES(17,98,'17:98','Ин ҷазои онон аст; чаро ки оёти Моро инкор карданд ва гуфтанд: «Оё ҳангоме ки устухонҳое [пӯсида ва] пароканда шудем, дубора бо офаринише ҷадид барангехта мешавем?»');
INSERT INTO "pioneers" VALUES(17,99,'17:99','Оё надонистанд Аллоҳро, ки осмонҳо ва заминро офарида аст, метавонад ҳаммонанди ононро биёфаринад ва барояшон аҷале таъйин кардааст, ки ҳеҷ тардиде дар он нест? Ва [-ле] ситамгорон ҷуз инкор [-и чизеро] напазируфтанд');
INSERT INTO "pioneers" VALUES(17,100,'17:100','[Эй Паёмбар, ба мушрикон] Бигӯ: «Агар шумо молики ганҷинаҳои [бахшишу] раҳмати Парвардигорам будед, [боз ҳам] аз бими харҷ кардан [ва тангдастӣ] аз инфоқ худдорӣ мекардед; ва инсон ҳамвора бахил [ва тангназар] аст»');
INSERT INTO "pioneers" VALUES(17,101,'17:101','Ва ба ростӣ, Мо нуҳ муъҷизаи равшан [-ро ба унвони нишонаҳои паёмбарӣ] ба Мӯсо додем; пас, аз Бани Исроил бипурс, он гоҳ ки [Мӯсо] наздашон омад ва Фиръавн ба ӯ гуфт: «Эй Мӯсо, яқинан туро афсуншуда мепиндорам»');
INSERT INTO "pioneers" VALUES(17,102,'17:102','[Мӯсо] Гуфт: «Ту, ҳатман, медонӣ, ки инҳо [муъҷизот]-ро [касе] ҷуз Парвардигори осмонҳо ва замин нафиристодааст. [Ин муъҷизот] Далоили равшан [дар бораи қудрату ягонагии Аллоҳ таоло ва ростгӯии паёмбараш ҳастанд]; ва, эй Фиръавн, ба ростӣ, ки туро ҳалокшуда [ва зиёндида] мепиндорам»');
INSERT INTO "pioneers" VALUES(17,103,'17:103','Пас, [Фиръавн] хост, ки онон [Бани Исроил]-ро аз он сарзамин берун кунад; ва[-ле Мо] ӯ ва тамоми касонеро, ки ҳамроҳаш буданд, ғарқ кардем');
INSERT INTO "pioneers" VALUES(17,104,'17:104','Ва пас аз [нобудии] ӯ ба Бани Исроил гуфтем: «Дар сарзамин [-и Шом] сокин шавед ва ҳангоме ки ваъдаи охират фаро расид, ҳамаи шуморо бо ҳам [ба сарои маҳшар] меоварем»');
INSERT INTO "pioneers" VALUES(17,105,'17:105','Ва ин [Қуръон]-ро бар ҳақ нозил кардем ва бар ҳақ [ва бидуни ҳеҷ таҳрифу тағйире] нозил шуд; ва [эй Муҳаммад] туро ҷуз башоратгар ва бимдиҳанда нафиристодем');
INSERT INTO "pioneers" VALUES(17,106,'17:106','Ва Қуръон [-и гаронқадр]-ро бахш-бахш [ва ба мурур бар ту] нозил кардем, то онро бо диранг [ва оҳистагӣ] барои мардум бихонӣ ва онро ба тадриҷ [ва бар ҳасби рӯйдодҳои мухталиф] нозил кардем');
INSERT INTO "pioneers" VALUES(17,107,'17:107','[Эй Паёмбар, ба кофирон] бигӯ: «[Чи] Ба он имон биёваред ё наёваред [фарқе ба ҳолатон надорад]. Бе тардид, касоне, ки қабл аз [нузули] он дониш [-и китобҳои осмонии пешинро] ёфтаанд, чун [оёти Қуръон] бар онон хонда шавад, саҷдакунон [ бар замин] меафтанд»');
INSERT INTO "pioneers" VALUES(17,108,'17:108','Ва мегӯянд: «Парвардигорамон поку муназзаҳ аст. Яқинан, ваъдаи Парвардигорамон анҷомшуданӣ аст»');
INSERT INTO "pioneers" VALUES(17,109,'17:109','Ва [он гоҳ] гирякунон бар [замин] меафтанд ва [тиловати Қуръон ва тадаббур дар оёташ] бар фурӯтаниашон меафзояд');
INSERT INTO "pioneers" VALUES(17,110,'17:110','Бигӯ: «Аллоҳ таоло»-ро бихонед ё «Раҳмон»-ро бихонед; ҳар кадомро, ки бихонед, накутарини номҳо барои Ӯст». Ва [эй Паёмбар] намозатро [ончунон] ба садои баланд нахон [ки мушрикон бишнаванд] ва онро [он қадр низ] оҳиста нахон [ки ҳатто асҳобат нашнаванд]; ва миёни ин [ду ҳолат] роҳе [миёна] биҷӯй');
INSERT INTO "pioneers" VALUES(17,111,'17:111','Ва бигӯ: «Ситоиш махсуси Аллоҳ таоло аст, ки фарзанде [барои хеш] барнагузидааст ва дар фармонравоӣ ҳеҷ шарике надорад ва ҳаргиз хору нотавон намегардад, ки [ниёз ба дӯст ва] корсоз дошта бошад» ва Ӯро бузург бидор; бузургдоште шоиста');
INSERT INTO "pioneers" VALUES(18,1,'18:1','Ҳамду сипос махсуси Аллоҳ таоло аст, ки Қуръонро бар бандааш [Муҳаммад] нозил кард ва ҳеҷ гуна инҳирофе дар он қарор надод');
INSERT INTO "pioneers" VALUES(18,2,'18:2','[Китобе] Устувор то [кофиронро] аз азобе сахт аз ҷониби Ӯ [Аллоҳ таоло] бим диҳад ва ба муъминоне, ки корҳои шоиста мекунанд, мужда диҳад, ки подоши накуе барояшон [дар пеш] аст');
INSERT INTO "pioneers" VALUES(18,3,'18:3','[Ҳамон биҳиште] ки ҷовидона дар он хоҳанд монад');
INSERT INTO "pioneers" VALUES(18,4,'18:4','Ва [низ] касонеро, ки гуфтанд: «Аллоҳ таоло фарзанде [барои худ] баргузида аст», бим диҳад');
INSERT INTO "pioneers" VALUES(18,5,'18:5','На худашон ба ин [гуфтори нодуруст] илм [-у яқин] доранд на падаронашон. Сухани бисёр нописандест, ки аз даҳонашон берун меояд [ва] ҷуз дурӯғ намегӯянд');
INSERT INTO "pioneers" VALUES(18,6,'18:6','Пас, [эй Паёмбар] наздик аст, агар [қавмат ба ин сухан [Қуръон]] имон наёваранд, дар пайи [беимонии] онон худро [аз таассуф ва андӯҳ] ҳалок кунӣ');
INSERT INTO "pioneers" VALUES(18,7,'18:7','Дар ҳақиқат, Мо он чиро, ки рӯйи замин аст, зеваре барои он қарор додаем, то ононро биёзмоем, ки кадомашон накукортаранд');
INSERT INTO "pioneers" VALUES(18,8,'18:8','Ва Мо [пас аз поёни ҳаёт] он чиро, ки рӯйи замин аст, ба биёбоне хушк ва бегиёҳ табдил хоҳем кард');
INSERT INTO "pioneers" VALUES(18,9,'18:9','Оё пиндоштаӣ, ки [моҷарои] асҳоби Каҳф ва он сангнавишта [ки номашон бар он ҳак шуда буд] аз ҷумлаи нишонаҳои шигифтангез [ва ғайримумкини] Мо будааст?');
INSERT INTO "pioneers" VALUES(18,10,'18:10','Ҳангоме ки он ҷавонмардон ба ғор паноҳ бурданд ва гуфтанд: «Парвардигоро, моро аз ҷониби хеш раҳмате арзонӣ дор ва дар кори [ҳиҷрати] мо бароямон ҳидоят ва наҷот фароҳам соз»');
INSERT INTO "pioneers" VALUES(18,11,'18:11','Пас, дар он ғор бар гӯшҳояшон то солиёни дароз [пардае аз сукуту оромиш] задем [то ба хоб раванд]');
INSERT INTO "pioneers" VALUES(18,12,'18:12','Сипас бедорашон кардем, то бидонем кадом як аз он ду гурӯҳ муддатеро, ки [дар хоб] мондаанд, дурусттар ҳисоб кардааст');
INSERT INTO "pioneers" VALUES(18,13,'18:13','[Эй Паёмбар] Мо достони ононро, ба дурустӣ, бароят бозгӯ мекунем. Онон ҷавононе [шоиста] буданд, ки ба Парвардигорашон имон оварда буданд ва Мо [низ] бар ҳидояташон афзудем');
INSERT INTO "pioneers" VALUES(18,14,'18:14','Ва дилҳояшонро [бар имон] устувор сохтем, он гоҳ ки [дар баробари подшоҳи кофир] қиём карданд ва гуфтанд: «Парвардигори мо парвардигори осмонҳо ва замин аст; ва ҳаргиз ҷуз ӯ маъбудеро намепарастем, ки он гоҳ [агар чунин кунем] сухане газофу нораво гуфтаем»');
INSERT INTO "pioneers" VALUES(18,15,'18:15','[Сипас ба ҳамфикронашон гуфтанд:] «Ин қавми мо [ки] ба ҷои Ӯ [Аллоҳ таоло] маъбудоне [дигар] баргузидаанд, чаро далели ошкоре бар [ҳаққонияти] онон намеоваранд? Пас, кист ситамгортар аз он ки бар Аллоҳ таоло дурӯғ баст [ва барояш шарик қоил шуд]?');
INSERT INTO "pioneers" VALUES(18,16,'18:16','Ва чун аз онон ва аз он чи ба ҷойи Аллоҳ таоло мепарастанд, канорагирӣ кардед, пас, ба ғор паноҳ бибаред, то Парвардигоратон аз [сояи] раҳмати хеш бар шумо бигустаронад ва дар коратон [осоишу] кушоише падид оварад');
INSERT INTO "pioneers" VALUES(18,17,'18:17','Ва [эй Муҳаммад, агар он ҷо будӣ] хуршедро медидӣ, ки вақте тулуъ мекунад, ба самти рост [-и ғор] мутамоил мегардад ва вақте ғуруб мекунад, аз самти чапи онон дур мешуд [ва гармо озорашон намедод]; ва онон дар маҳалле густарда аз он [ғор] буданд. Ин аз нишонаҳои [қудрати] Аллоҳ таоло аст. Ҳар киро Аллоҳ таоло ҳидоят кунад, пас, ӯ ҳидоятёфта [-и воқеӣ] аст ва ҳар киро гумроҳ кунад, ҳаргиз дӯст [ва] роҳнамое барояш намеёбӣ');
INSERT INTO "pioneers" VALUES(18,18,'18:18','Ва [агар ба онҳо нигоҳ мекардӣ] мепиндоштӣ, бедоранд; дар ҳоле ки [бо чашмони боз] хуфта буданд; ва Мо ононро ба паҳлуи росту чап мегардондем [то баданашон осеб набинад]; ва сагашон бар остонаи ғор [нишаста ва ба ҳолати посбонӣ] дастҳояшро кушода буд. Агар [нигоҳашон мекардӣ ва] аз ҳолашон огоҳ мешудӣ, ҳатман, [аз ваҳшат] фирор мекардӣ ва дилат аз тарси онон лабрез мешуд');
INSERT INTO "pioneers" VALUES(18,19,'18:19','Ва ҳамчунон [ки хобашон кардем] ононро [аз хоб] бедор намудем, то байни худ аз якдигар суол кунанд. Яке аз онон гуфт: «Чи муддат дар хоб мондед?» [Бақия] Гуфтанд: «Як рӯз ё бахше аз як рӯз». [Саранҷом] Гуфтанд: «Парвардигоратон донотар аст, ки чи қадар [дар ин ҳолат] мондаед. Пас, [акнун] як нафар аз худатонро бо ин сикка[-ҳоя]-тон ба шаҳр бифиристед, то бубинад, кадом як аз [фурушандагони он ҷо косибӣ ва] ғизояш покизатар аст ва аз [назди] ӯ ғизое бароятон биёварад; ва бояд [дар рафтуомаду харид пинҳонкорӣ ва] зиракӣ намояд ва ҳеҷ касро аз [ҳоли] шумо огоҳ насозад');
INSERT INTO "pioneers" VALUES(18,20,'18:20','Бе тардид, агар онон [маконатонро бидонанд ва] бар шумо даст ёбанд, сангсоратон мекунанд ё шуморо ба оини хеш бозмегардонанд ва дар он сурат, ҳаргиз растагор нахоҳед шуд');
INSERT INTO "pioneers" VALUES(18,21,'18:21','Ва бад-ин гуна [мардуми он сарзамин]-ро аз ҳоли онон огоҳ кардем, то бидонанд, ки ваъдаи Аллоҳ таоло [дар бораи ёрии муъминон ва барангехтани мурдагон] росту дуруст аст ва ин ки дар [фаро расидани] қиёмат тардиде нест. Ҳангоме ки [мардуми шаҳр] байни худ [дар бораи он ҷавонмардони мутаваффо] дучори ихтилоф шуданд, пас, гурӯҳе гуфтанд: «Деворе бар [вуруди] он [ғор] бисозед. Парвардигорашон ба ҳоли онон огоҳтар аст». Бархе дигар, ки [ноогоҳ буданд ва нуфузу] қудрат доштанд, гуфтанд: «[Ба нишонаи бузургдошт] Бар [ғори] эшон парастишгоҳе месозем»');
INSERT INTO "pioneers" VALUES(18,22,'18:22','[Дар бораи теъдодашон] Хоҳанд, гуфт: «Се нафар буданд ва чаҳорумини онон сагашон буд». Ва [гурӯҳе] мегӯянд: «Панҷ нафар буданд ва шашумини онон сагашон буд» - [ки албатта] ҳама аз рӯи ҳадс ва гумон аст – ва [гурӯҳе] мегӯянд: «Ҳафт нафар буданд ва ҳаштумини онон сагашон буд». Бигӯ: «Парвадигорам аз теъдоди онон огоҳтар аст [ва] ҷуз гурӯҳе андак теъдодашонро касе намедонад. Пас, дар бораи онон гуфтугӯе мухтасар [ва баҳсе ором ва гузаро] дошта бош ва дар бораи онон аз ҳеҷ кас суол накун');
INSERT INTO "pioneers" VALUES(18,23,'18:23','Ва ҳаргиз дар мавриди коре нагӯ: «Фардо онро анҷом медиҳам»');
INSERT INTO "pioneers" VALUES(18,24,'18:24','Магар ин ки [бигӯӣ] «Иншоаллоҳ»; ва ҳар гоҳ фаромӯш кардӣ, [бо гуфтани ин лафз] Парвардигоратро ёд кун ва бигӯ: «Умедворам Парвардигорам маро ба роҳе раҳнамун гардад, ки аз ин [роҳ] ба ҳидоят [ва салоҳ] наздиктар бошад»');
INSERT INTO "pioneers" VALUES(18,25,'18:25','Ва онон [асҳоби Каҳф] сесад сол дар ғорашон [дар ҳолати хоб] монданд ва нуҳ сол [низ] бар он афзуданд');
INSERT INTO "pioneers" VALUES(18,26,'18:26','[Эй паёмбар] Бигӯ: «Аллоҳ таоло аз [муддати] монданашон [дар ғор] огоҳтар аст; ғайби осмонҳо ва замин аз они Ӯст. [Ба ростӣ, ки] Ӯ чи бино ва чи шунаво аст! Онон [ҷаҳониён] ба ҷуз Ӯ ҳеҷ [дӯсту] корсозе надоранд ва Ӯ ҳеҷ касро дар ҳукм [-у фармон]-и хеш шарик намесозад»');
INSERT INTO "pioneers" VALUES(18,27,'18:27','Ва [эй паёмбар] аз китоби Парвардигорат он чиро ба ту ваҳй шудааст, тиловат кун. Ҳеҷ кас тағйирдиҳандаи суханони Ӯ [Аллоҳ таоло] нест ва ҳаргиз паноҳгоҳе ҷуз Ӯ намеёбӣ');
INSERT INTO "pioneers" VALUES(18,28,'18:28','Ва худро [бар мусоҳибат] бо касоне ҳамроҳ [ва шикебо] гардон, ки Парвардигорашонро бомдод ва шомгоҳ мехонанд [ва бо он ки фақиранд, танҳо] ризоияти Ӯро мехоҳанд; ва ҳаргиз чашмонатро аз онон барнагардон, то зеварҳои дунё [ва ҳамнишинӣ бо ашроф ва қудратмандон]-ро бихоҳӣ ва [ҳамчунин] аз касе пайравӣ накун, ки дилашро аз ёди хеш ғофил сохтаем ва аз ҳавои нафси хеш пайравӣ карда ва кораш табоҳ аст');
INSERT INTO "pioneers" VALUES(18,29,'18:29','[Эй паёмбар, ба мушрикон] бигӯ: «[Ин Қуръон сухани] Ҳақ аст аз ҷониби Парвардигоратон; пас, ҳар ки мехоҳад, имон оварад ва ҳар ки мехоҳад, кофир шавад». Мо барои ситамгорон оташе омода кардаем, ки саропардаҳояш ононро аз ҳар сӯ фаро мегирад; ва агар [дар талаби об] фарёдрасе биҷӯянд, бо обе ҳамчун миси гудохта ба фарёдашон мерасанд; [обе, ки ҳарораташ] чеҳраҳоро бирён мекунад. Чи бад нушиданӣ ва чи бад ҷойгаҳе аст!');
INSERT INTO "pioneers" VALUES(18,30,'18:30','Касоне, ки имон оварданд ва корҳои шоиста анҷом додаанд [ба яқин, бидонанд, ки] Мо подоши касеро, ки кори накӯ карда бошад, ҳаргиз табоҳ намекунем');
INSERT INTO "pioneers" VALUES(18,31,'18:31','Ононанд, ки биҳиштҳои ҷовидонӣ доранд, ки [аз зери дарахтони] онҳо ҷӯйборҳо ҷорӣ аст. Дар он ҷо бо дастбандҳои заррин ороста мешаванд ва ҷомаҳои [фохири] сабзранг аз ҳарири нозук ва ҳарири захим бар тан мекунанд, дар ҳоле ки бар тахтҳо такя задаанд. Чи подоши шоиста ва чи наку ҷойгоҳе аст!');
INSERT INTO "pioneers" VALUES(18,32,'18:32','Ва [эй паёмбар] барои онон мисоле бизан: ду мард [кофир ва муъмин], ки барои яке аз онон [ки кофир аст] ду боғи ангур қарор додем ва перомуни он ду [боғ]-ро бо дарахтони нахл пӯшонидем ва миёни он киштзоре [пурбор] ниҳодем');
INSERT INTO "pioneers" VALUES(18,33,'18:33','Ҳар ду боғ мева медод ва чизе аз он намекост; ва миёни он ду [боғ] наҳре бузург ҷорӣ кардем');
INSERT INTO "pioneers" VALUES(18,34,'18:34','Ӯ [соҳиби боғ] меваҳое [бисёр ва даромаде фаровон] дошт; пас, ба дӯсти худ, ки бо ӯ гуфтугӯ мекард, гуфт: «Сарвати ман аз ту бештар аст ва аз лиҳози хонавода [ва нафарон] аз ту тавонмандтарам»');
INSERT INTO "pioneers" VALUES(18,35,'18:35','Ва ӯ дар ҳоле ба боғи хеш дохил шуд, ки [ба хотири куфру ғурур] дар ҳаққи хеш ситамгор буд; [пас, ба дӯсташ рӯ оварда] гуфт: «Гумон намекунам, ки ин [боғ] ҳаргиз нобуд гардад');
INSERT INTO "pioneers" VALUES(18,36,'18:36','Ва гумон намекунам, ки қиёмат барпо гардад; ва агар [қиёмате дар кор бошад ва] ба сӯйи Парвардигорам бозгарданда шавам, ҳатман, ҷойгоҳе беҳтар аз ин меёбам»');
INSERT INTO "pioneers" VALUES(18,37,'18:37','Дӯсташ, ки бо вай гуфтугӯ мекард, ба ӯ гуфт: «Оё ба он касе, ки туро аз хок [ва] сипас аз нутфа офарид ва сипас туро марде [комил] сомон дод, кофир шудаӣ?');
INSERT INTO "pioneers" VALUES(18,38,'18:38','Аммо ман [мегӯям:] Ӯ Аллоҳ таоло парвардигори ман аст ва ҳеҷ касро бо Парвардигорам шарик намедонам');
INSERT INTO "pioneers" VALUES(18,39,'18:39','Вақте вориди боғат шудӣ, чаро нагуфтӣ, ҳар чи Аллоҳ таоло бихоҳад [ҳамон мешавад] ва ҳеҷ неруе ҷуз ба [таъйиди] Аллоҳ таоло нест? Агар мебинӣ ман аз назари молу фарзанд аз ту камтарам [муҳим нест]');
INSERT INTO "pioneers" VALUES(18,40,'18:40','Чи басо Парвардигорам [чизе] беҳтар аз боғи ту ба ман бидиҳад ва аз осмон чунон азобе [оташбор] бар боғат фурӯ борад, ки [табдил ба замин] бегиёҳи лағжандае гардад');
INSERT INTO "pioneers" VALUES(18,41,'18:41','Ё обаш дар [аъмоқи замин чунон] фурӯ равад, ки ҳаргиз натавонӣ ба он даст биёбӣ»');
INSERT INTO "pioneers" VALUES(18,42,'18:42','[Саранҷом азоби илоҳӣ фаро расид] Ва тамоми меваҳояшро дар бар гирифт [ва нобуд шуд]. Ва ӯ ба хотири харҷҳое, ки cарфи он ҷо карда буд, пайваста дастҳояшро [аз пушаймонӣ ва андӯҳ] бар ҳам мезад ва дар ҳоле ки дорбаст ва чуббандҳояш фурӯ рехта буд, мегуфт: «Эй кош [дар парастиш] касеро шарики Парвардигорам қарор надода будам»');
INSERT INTO "pioneers" VALUES(18,43,'18:43','Ва ӯ [ки ба хондани худ меболид, инак] гурӯҳе надошт, ки дар баробари [азоби] Аллоҳ таоло ёриаш кунанд ва худ [низ] ёрикунандаи хеш набуд');
INSERT INTO "pioneers" VALUES(18,44,'18:44','Он ҷо [собит шуд, ки ёрӣ ва] корсозӣ аз они [Парвардигори] ҳақ аст. Ӯ подоши [ибодаташ] беҳтар аст ва саранҷоми [бандагони муъминаш] накутар');
INSERT INTO "pioneers" VALUES(18,45,'18:45','Ва [эй паёмбар, фанопазирии] зиндагии дунёро ба обе масал бизан, ки аз осмон фурӯ фиристодем ва ба василаи он гиёҳи замин [сарсабз шуд ва] дар ҳам омехт; сипас [чунон] хушку шикаста шуд, ки бодҳо [ба ҳар сӯ] парокандааш мегардонад; ва Аллоҳ таоло бар ҳар чизе тавоност');
INSERT INTO "pioneers" VALUES(18,46,'18:46','Молу фарзандон зинати зиндагии дунёст ва некиҳои пойдор [рафтору гуфтори шоиста] назди Парвардигорат подошаш беҳтар аст, умед доштан [ба савобаш] накутар');
INSERT INTO "pioneers" VALUES(18,47,'18:47','Ва [ёд кун аз] рӯзе, ки кӯҳҳоро ба ҳаракат дармеоварем ва заминро ошкор [ва ҳамвор] мебинӣ ва ҳама [махлуқот]-ро ҷамъ мекунем ва ҳеҷ як аз ононро [ба ҳоли худ] раҳо намекунем');
INSERT INTO "pioneers" VALUES(18,48,'18:48','Ва [ҳамагӣ] сафкашида бар Парвардигорат арза мешаванд [ва ба онон мегӯянд:] «Бе тардид, чунон ки нахустин бор шуморо офаридем, [имрӯз низ танҳо ва побараҳна] назди Мо омадед; балки мепиндоштед, ки ҳаргиз ваъдагоҳе бароятон қарор намедиҳем»');
INSERT INTO "pioneers" VALUES(18,49,'18:49','Ва нома [-и аъмол дар миён] ниҳода мешавад; пас, гунаҳкоронро мебинӣ, ки аз он чи дар он [нома] аст, тарсон ва нигаронанд ва мегӯянд: «Эй вой бар мо! Ин чи номае аст, ки ҳеҷ [гуфтору рафтори] кучак ва бузургро раҳо накардааст, магар ин ки онро дар шумор овардааст?» Ва ҳар чи кардаанд, ҳозир меёбанд ва Парвардигорат ба ҳеҷ касе ситам намекунад');
INSERT INTO "pioneers" VALUES(18,50,'18:50','Ва [ёд кун аз] ҳангоме ки ба фариштагон гуфтем: «Барои Одам саҷда кунед»; пас, [ҳама] саҷда карданд, магар Иблис, ки аз ҷин буд ва аз фармони Парвардигораш сар печид. Пас, [эй мардум] оё ба ҷойи Ман ӯ ва фарзандонашро дӯстон [-и худ] мегиред, ҳол он ки онон душманатон ҳастанд? Ситамгорон [-и мушрик барои Аллоҳ таоло] чи бад ҷойгузине доранд!');
INSERT INTO "pioneers" VALUES(18,51,'18:51','Ман ононро [ки мушрикона ба парастиш мегиред] на ҳангоми офариниши осмону замин ба гувоҳӣ гирифтам ва на ҳангоми офариниши худашон; ва ман ҳаргиз гумроҳгаронро ёру мададгори [хеш] нагирифтаам');
INSERT INTO "pioneers" VALUES(18,52,'18:52','Ва [ёд кун аз] рӯзе, ки Аллоҳ таоло [ба мушрикон] мефармояд: «Шариконеро, ки барои ман мепиндоштед, фаро бихонед [то шуморо аз азоб бираҳонанд]; пас, онҳоро мехонанд, вале посухашонро намедиҳанд; ва Мо дар миёни ин ду гурӯҳ маҳлакае [аз оташи дузах] қарор додаем');
INSERT INTO "pioneers" VALUES(18,53,'18:53','Ва гунаҳгорон оташ [-и дузах]-ро мебинанд ва ба яқин дармеёбанд, ки дар он хоҳанд афтод ва аз он ҷо ҳеҷ роҳи бозгаште надоранд');
INSERT INTO "pioneers" VALUES(18,54,'18:54','Ва ба ростӣ, дар ин Қуръон барои мардум аз ҳар гуна масале овардем; ва[-ле] инсон беш аз ҳар чизе ба ситез [ва чунучаро] мепардозад');
INSERT INTO "pioneers" VALUES(18,55,'18:55','Ва чизе мардумро бознадошт, ки вақте ҳидоят [паёмбар ва Қуръон] ба сӯяшон омад, имон биёваранд ва аз Парвардигорашон омурзиш бихоҳанд, магар ин ки [аз рӯи лаҷоҷат мехостанд] суннати [Аллоҳ таоло дар мавриди азоби] пешиниён бар [сари] онон [низ] биёяд ё азоби рӯ дар рӯ ба онон бирасад');
INSERT INTO "pioneers" VALUES(18,56,'18:56','Ва мо паёмбаронро ҷуз башоратгар ва бимдиҳанда намефиристем ва касоне, ки куфр варзиданд, ба [суханони беҳуда ва] ботил ситеза мекунанд, то ба василаи он ҳақ [Қуръон]-ро поймол кунанд ва [муъҷизоту] оёти ман ва он чиро, ки ба он бим дода шудаанд, ба тамасхур гирифтанд');
INSERT INTO "pioneers" VALUES(18,57,'18:57','Кист ситамгортар аз он ки ба оёти Парвардигораш панд дода шавад, вале аз он рӯ бигардонад ва корҳои [зишти] гузаштаашро фаромӯш кунад? Мо бар дилҳояшон пардаҳое афкандаем, то [паёми ҳақ]-ро дарнаёбанд ва дар гӯшҳояшон сангинӣ [ниҳодаем, то сухани ҳақро нашнаванд]; ва агар ононро ба сӯи ҳидоят бихонӣ, ҳаргиз ҳидоят намешаванд');
INSERT INTO "pioneers" VALUES(18,58,'18:58','[Эй Паёмбар] Парвардигорат омурзанда ва дорои раҳмат [фаровон] аст. Агар онон [кофирон]-ро ба [сазои] он чи кардаанд, бозхост мекард, ҳатман, дар азобашон шитоб менамуд; [аммо чунин намекунад] балки онон мавъиде доранд, ки ҳаргиз аз он гурезе нахоҳанд дошт');
INSERT INTO "pioneers" VALUES(18,59,'18:59','Мо [сокинони] он шаҳрҳоро ҳангоме ҳалок намудем, ки [ба хотири куфру гуноҳ] ба худ ситам карданд ва барои ҳалокаташон мавъиде таъйин кардем');
INSERT INTO "pioneers" VALUES(18,60,'18:60','[Ёд кун аз] Ҳангоме ки Мӯсо ба [хидматкори] ҷавони худ [Юшаъ ибни Нун] гуфт: «Ҳамчунон хоҳам рафт, то ба маҳалли бархӯрди ду дарё бирасам ё барои муддати тӯлонӣ роҳ бипаймоям [то бандаи дурусткоре биёбам ва нодонистаҳоямро аз ӯ биёмӯзам]»');
INSERT INTO "pioneers" VALUES(18,61,'18:61','Пас, чун ба маҳалли бархӯрди ду дарё расиданд, моҳии худро [ки барои хӯрдан ҳамроҳ доштанд] фаромӯш карданд [пас, Аллоҳ таоло онро зинда кард ва ба об андохт] ва моҳӣ роҳи худро дар дарё пеш гирифт');
INSERT INTO "pioneers" VALUES(18,62,'18:62','Пас, ҳангоме ки [ба роҳ афтоданд ва аз он ҷо] гузаштанд, [Мӯсо] ба [хидматкори] ҷавони худ гуфт: «Ғизоямонро биёвар, ки аз ин сафар бисёр хаста шудаем»');
INSERT INTO "pioneers" VALUES(18,63,'18:63','[Юшаъ] Гуфт: «Ба ёд дорӣ, ҳангоме ки [барои истироҳат] ба канори он тахтасанг ҷой гирифтем, ман [дар он ҷо достони зинда шудани] моҳиро фаромӯш кардам [ки бо ту бигӯям] ва ҷуз шайтон [касе] маро аз ёдоварии он [достон] ба фаромӯшӣ наандохт ва моҳӣ ба тарзе шигифтовар роҳашро дар дарё пеш гирифт»');
INSERT INTO "pioneers" VALUES(18,64,'18:64','[Мӯсо] Гуфт: «Он ҳамон чизест, ки мехостем [ва ҷойгоҳи он бандаи накукор ҳамон ҷост]». Пас, ҷустуҷӯкунон ради пои худро гирифтанд [ва аз ҳамон роҳ] бозгаштанд');
INSERT INTO "pioneers" VALUES(18,65,'18:65','Пас, [дар он ҷо] бандае аз бандагони мо [Хизр]-ро ёфтанд, ки аз ҷониби хеш ба ӯ раҳмате дода будем ва аз назди худ ба вай донише омӯхта будем');
INSERT INTO "pioneers" VALUES(18,66,'18:66','Мӯсо ба ӯ гуфт: «Оё [иҷозат медиҳӣ] ҳамроҳат биёям, то аз он чи ба ту омӯхта шуда ва мояи ҳидояту иршод аст ба ман биёмӯзӣ?»');
INSERT INTO "pioneers" VALUES(18,67,'18:67','[Хизр] Гуфт: «Ту ҳаргиз наметавонӣ, [дар итоат ва ҳамроҳӣ] ҳамроҳи ман шикебоӣ кунӣ');
INSERT INTO "pioneers" VALUES(18,68,'18:68','Ва чи гуна бар [анҷоми] коре, ки ба [рози] он огоҳ нестӣ, шикебоӣ мекунӣ?»');
INSERT INTO "pioneers" VALUES(18,69,'18:69','[Мӯсо] Гуфт: «Иншоаллоҳ шикебо хоҳам буд ва дар ҳеҷ коре аз ту нофармонӣ намекунам»');
INSERT INTO "pioneers" VALUES(18,70,'18:70','[Хизр] Гуфт: «Пас, агар бо ман ҳамроҳ шудӣ, дар бораи ҳеҷ чиз аз ман суол накун, то худ бо ту дар борааш сухане бигӯям»');
INSERT INTO "pioneers" VALUES(18,71,'18:71','Пас, он ду ба роҳ афтоданд, то он ки савори киштӣ шуданд. [Хизр] Киштиро сӯрох кард. [Мӯсо] гуфт: «Онро сӯрох кардӣ, то сарнишинонашро ғарқ кунӣ? Ростӣ, чи кори норавое кардӣ!»');
INSERT INTO "pioneers" VALUES(18,72,'18:72','[Хизр] Гуфт: «Оё нагуфтам, ту ҳаргиз наметавонӣ бо ман шикебоӣ кунӣ?»');
INSERT INTO "pioneers" VALUES(18,73,'18:73','[Мӯсо] гуфт: «Ба хотири он чи фаромӯш кардам, сарзанишам накун ва дар корам бар ман сахт нагир»');
INSERT INTO "pioneers" VALUES(18,74,'18:74','Пас, ба роҳ афтоданд, то ҳангоме ки ба навҷавоне расиданд ва [Хизр] ӯро кушт. [Мӯсо] гуфт: «Шахси [бегуноҳу] покеро куштӣ [он ҳам] бидуни ин ки касеро кушта бошад? Воқеан, кори нописанде кардӣ!»');
INSERT INTO "pioneers" VALUES(18,75,'18:75','Хизр гуфт: «Ба ту нагуфтам, ки ҳаргиз наметавонӣ бо ман шикебоӣ кунӣ?»');
INSERT INTO "pioneers" VALUES(18,76,'18:76','Мӯсо гуфт: «Агар аз ин пас чизе аз ту пурсидам, дигар бо ман ҳамроҳӣ накун ва аз ҷониби ман, ҳатман, маъзур хоҳӣ буд»');
INSERT INTO "pioneers" VALUES(18,77,'18:77','Пас, ба роҳ афтоданд, то ба аҳли шаҳре расиданд [ва] аз онон ғизо хостанд; [аммо онон] аз меҳмон кардани эшон худдорӣ карданд. Сипас дар он ҷо деворе ёфтанд, ки мехост фурӯ бирезад ва [Хизр] онро росту устувор кард. [Мӯсо] Гуфт: «Агар мехостӣ, [метавонистӣ] барои он [кор аз онон] музде бигирӣ»');
INSERT INTO "pioneers" VALUES(18,78,'18:78','[Хизр] Гуфт: «Ин ки замони ҷудоии ману ту фаро расидааст. Ба зудӣ туро аз тавзеҳ [ва далели] он чи ки натавонистӣ дар баробараш шикебоӣ кунӣ, огоҳ хоҳам сохт');
INSERT INTO "pioneers" VALUES(18,79,'18:79','Аммо он киштӣ аз они бенавоёне буд, ки дар дарё кор мекарданд; пас, хостам маъюбаш кунам; [зеро] пеши роҳашон [дар дарё] подшоҳе [ситамгор] буд, ки ҳар киштии [солиме]-ро ба зӯр мегирифт');
INSERT INTO "pioneers" VALUES(18,80,'18:80','Ва аммо он навҷавон падару модараш муъмин буданд; пас, тарсидем, ки он дуро ба саркашӣ ва куфр во дорад');
INSERT INTO "pioneers" VALUES(18,81,'18:81','Аз ин рӯ, хостем, ки Парвардигорашон ба ҷойи ӯ [фарзанди] поктару меҳрубонтаре ба онҳо ато фармояд');
INSERT INTO "pioneers" VALUES(18,82,'18:82','Ва аммо он девор барои ба ду писарбачаи ятим дар он шаҳр буд ва зери он ганҷе буд, ки ба он ду тааллуқ дошт ва падарашон [низ] марде дурусткор буд; пас, Парвардигорат хост, ки онон ба ҳадди булуғ бирасанд ва ганҷи хешро [аз зери он девор] берун оваранд. Ин раҳмате аз ҷониби Парвардигори ту буд ва ман ин [корҳо]-ро худсарона накардам, ин буд тавзеҳ [ва далели] корҳое, ки натавонистӣ дар баробараш шикебоӣ кунӣ»');
INSERT INTO "pioneers" VALUES(18,83,'18:83','Ва [эй паёмбар, мушрикону яҳудиён] дар бораи «Зу-л-қарнайн» аз ту мепурсанд; бигӯ: «Ба зудӣ чизе аз саргузашти ӯ бароятон хоҳам хонд»');
INSERT INTO "pioneers" VALUES(18,84,'18:84','Мо дар замин ба ӯ қудрату ҳукумат додем ва василаи [расидан ба] ҳар чизеро дар ихтиёраш ниҳодем');
INSERT INTO "pioneers" VALUES(18,85,'18:85','Пас, ӯ [аз ин] васоил [барои расидан ба аҳдофаш] истифода кард');
INSERT INTO "pioneers" VALUES(18,86,'18:86','[Рӯзе дар замин ба роҳ афтод] То ба маҳалли ғуруби хуршед расид. [Чунин ба назараш расид] Ки хуршед дар чашмае гарм ва гилолуд ғуруб мекунад ва дар [наздикии он ҷо қавме [кофир]] ёфт. [Ба ӯ] Гуфтем: «Эй Зу-л-қарнайн, ё [ононро] муҷозот мекунӣ ё дар миёнашон [рафтори] накуе пеш мегирӣ»');
INSERT INTO "pioneers" VALUES(18,87,'18:87','[Зу-л-қарнайн] Гуфт: «Аммо касеро, ки ситам карда [ва ширк варзида] аст, муҷозот хоҳем кард, сипас ба сӯйи Парвардигораш бозгардонида мешавад, он гоҳ ӯро ба азобе бисёр сахт муҷозот хоҳад кард');
INSERT INTO "pioneers" VALUES(18,88,'18:88','Ва аммо касе, ки имон оварад ва кори шоиста анҷом диҳад, пас, [дар охират] накутарин подош [биҳишт]-ро хоҳад дошт ва бо мулоимат [ва меҳрубонӣ] бо вай сухан хоҳем гуфт»');
INSERT INTO "pioneers" VALUES(18,89,'18:89','Сипас [Зу-л-қарнайн] аз васоили [худ] истифода кард [ва ба сӯйи машриқ рафт]');
INSERT INTO "pioneers" VALUES(18,90,'18:90','То он ки ба ҷойгоҳи баромадани хуршед расид. [Дар он ҷо хуршедро] Дид, ки бар мардумоне тулуъ мекунад, ки дар баробари он [нуру гармои офтоб] барояшон пӯшише қарор надода будем [на сарпаноҳе на сояи дарахте]');
INSERT INTO "pioneers" VALUES(18,91,'18:91','[Кори Зу-л-қарнайн] Инчунин, буд ва Мо ба он чи ӯ дар ихтиёр дошт [ва анҷом медод], комилан огоҳ будем');
INSERT INTO "pioneers" VALUES(18,92,'18:92','Сипас [Зу-л-қарнайн] аз имконот [-и худ] истифода кард [ва ба ҷойе байни шарқу ғарб рафт]');
INSERT INTO "pioneers" VALUES(18,93,'18:93','[Ӯ ҳамчунон рафт] То ба [дарае] миёни ду кӯҳ расид ва дар канори он ду [кӯҳ] мардумеро ёфт, ки сухане [ба ҷуз забони худро] намефаҳмиданд');
INSERT INTO "pioneers" VALUES(18,94,'18:94','[Онон] Гуфтанд: «Эй Зу-л-қарнайн, Яъҷуҷу Маъҷуҷ дар ин сарзамин [табаҳкорӣ ва] фасод мекунанд. Пас, оё [мумкин аст] харҷе дар ихтиёр қарор диҳем, то миёни мо ва онон садде бисозӣ?»');
INSERT INTO "pioneers" VALUES(18,95,'18:95','Ӯ гуфт: «Он чи [аз қудрату сарват] ки Парвардигорам дар он ба ман тавоноӣ додааст [аз он чи арза мекунед], беҳтар аст; пас, маро бо неру [-и инсонӣ] ёрӣ кунед, [то] миёни шумо ва онон садде муҳками устувор созем');
INSERT INTO "pioneers" VALUES(18,96,'18:96','Ҳатман, қитъаҳои оҳан бароям биёваред [ва рӯйи ҳам бичинед]», то вақте комилан миёни ду кӯҳро баробар сохт [ва пӯшонд. Сипас] гуфт: «[Дар атрофи девораи оҳанин оташ бияфрӯзед ва] дар он бидамед». [Онон чунин карданд] То вақте он [қитаоти оҳан]-ро гудохта кард; [сипас] гуфт: «[Акнун] миси обшуда бароям биёваред, то рӯйи он [девор] бирезам».');
INSERT INTO "pioneers" VALUES(18,97,'18:97','Пас, [Яъҷуҷу Маъҷуҷ] дигар натавонистанд аз он боло раванд ва [ҳатто] натавонистанд сӯрохе дар он эҷод кунанд');
INSERT INTO "pioneers" VALUES(18,98,'18:98','[Он гоҳ Зу-л-қарнайн] Гуфт: «Ин [сад] аз раҳмати Парвардигори ман аст [ва то Аллоҳ таоло бихоҳад, боқӣ мемонад]; аммо ҳангоме ки ваъдаи Парвардигорам фаро расад, онро дарҳам мекӯбад ва ваъдаи Парвардигорам ҳақ аст»');
INSERT INTO "pioneers" VALUES(18,99,'18:99','Ва дар он рӯз [ки ҷаҳон поён мепазирад] ононро [чунон] раҳо мекунем, ки [аз фаровонии ҷамъият] дар ҳам меомезанд ва [ҳамин, ки] дар сур дамида шуд, ҳамаро [дар як ҷо] ҷамъ мекунем');
INSERT INTO "pioneers" VALUES(18,100,'18:100','Ва он рӯз дузахро ошкоро бар кофирон арза медорем');
INSERT INTO "pioneers" VALUES(18,101,'18:101','[Ҳамон] Касоне, ки чашмонашон аз ёди ман дар парда [-и ғафлат] буд ва тавони шунидан [-и оёти илоҳиро] надоштанд');
INSERT INTO "pioneers" VALUES(18,102,'18:102','Оё касоне, ки куфр варзиданд, пиндоштаанд, ки [метавонанд] ба ҷойи ман бандагонамро корсоз [ва дӯсти худ] бигиранд? Бе тардид, Мо дузахро барои кофирон ҷойгоҳи пазироӣ сохтаем');
INSERT INTO "pioneers" VALUES(18,103,'18:103','[Эй Паёмбар] Бигӯ: «[Мехоҳед] Шуморо аз зиёнкортарин [мардум] дар корҳо огоҳ созам?');
INSERT INTO "pioneers" VALUES(18,104,'18:104','Касоне, ки талошашон дар зиндагии дунё табоҳ гаштааст ва худ мепиндорад, ки кори наку мекунанд»');
INSERT INTO "pioneers" VALUES(18,105,'18:105','Онон касоне ҳастанд, ки ба оёти Парвардигорашон ва дидори Ӯ [дар охират] кофир шуданд; пас, корҳояшон табоҳ гашт ва рӯзи қиёмат барояшон [ҳеҷ қадру] арзише қоил намешавем');
INSERT INTO "pioneers" VALUES(18,106,'18:106','Инчунин аст, ки ба хотири куфре, ки варзидаанд ва оёту паёмбарони Маро ба тамасхур гирифтаанд подошашон дузах аст');
INSERT INTO "pioneers" VALUES(18,107,'18:107','Бе тардид, касоне, ки имон овардаанд ва корҳое шоиста анҷом додаанд, боғҳои [биҳишти] барин манзилгоҳашон хоҳад буд');
INSERT INTO "pioneers" VALUES(18,108,'18:108','Ҷовидона дар он хоҳанд монд ва ҳаргиз аз он ҷо ба ҷойи дигаре гароиш надоранд');
INSERT INTO "pioneers" VALUES(18,109,'18:109','[Эй Паёмбар] Бигӯ: «Агар дарё барои [нигориши] калимоти Парвардигорам ранг шавад, пеш аз он ки калимоти Парвардигорам поён ёбад, ҳатман, дарё ба поён мерасад; ҳарчанд [дарёҳое дигар] ҳаммонанди он [дарёро низ] ба кумак биёварем»');
INSERT INTO "pioneers" VALUES(18,110,'18:110','[Эй паёмбар] Бигӯ: «Ман фақат башаре [одӣ] ҳамчун шумо ҳастам. Ба ман ваҳй мешавад, ки танҳо маъбудатон Аллоҳ таоло яктост; пас, ҳар ки ба дидори Парвардигораш [дар охират] умед дорад, бояд кори шоиста анҷом диҳад ва ҳеҷ касро дар парастиши Парвардигораш шарик насозад»');
INSERT INTO "pioneers" VALUES(19,1,'19:1','Коф, ҳо, ё, ъайн, сод');
INSERT INTO "pioneers" VALUES(19,2,'19:2','[Ин] Баёни раҳмати Парвардигорат [нисбат] ба бандааш Закариёст');
INSERT INTO "pioneers" VALUES(19,3,'19:3','Он гоҳ ки Парвардигорашро ба нидое пинҳон [ба дуо] хонд');
INSERT INTO "pioneers" VALUES(19,4,'19:4','Гуфт: «Парвардигоро, ба ростӣ, ки устухонам суст шуда ва мӯйҳоям аз пирӣ сапед гашта ва эй Парвардигорам, [ҳаргиз] дар дуои ту [аз иҷобат] бебаҳра набудаам');
INSERT INTO "pioneers" VALUES(19,5,'19:5','Ва ман пас аз [марги] худ аз хешовандонам [нисбат ба ҳифзи дини Ту] бимнокам ва ҳамсарам [низ] нозост; пас, ба лутфи хеш фарзанде ба ман ато фармо');
INSERT INTO "pioneers" VALUES(19,6,'19:6','[Писаре, ки] Вориси ману вориси хонадони Яъқуб бошад; ва Парвардигоро, ӯро [дар илму дин инсоне] писандида гардон»');
INSERT INTO "pioneers" VALUES(19,7,'19:7','[Аллоҳ таоло дуояшро иҷобат карду фармуд] «Эй Закариё, Мо туро ба [таваллуди] писаре башорат медиҳем, ки номаш Яҳё аст [ва] пеш аз ин ҳамноме барояш қарор надодаем»');
INSERT INTO "pioneers" VALUES(19,8,'19:8','[Закариё] Гуфт: «Парвардигоро, чи гуна писаре хоҳам дошт, дар ҳоле ки ҳамсарам нозост ва ман [низ] аз шиддати пирӣ ба нотавонӣ расидаам?»');
INSERT INTO "pioneers" VALUES(19,9,'19:9','[Фаришта] Гуфт: «Ин гуна аст [ки ту мегӯӣ, вале] Парвардигорат фармудааст: «Ин [амр] барои Ман осон аст ва ба ростӣ, пеш аз ин [низ] туро офаридем, дар ҳоле ки чизе набудӣ»');
INSERT INTO "pioneers" VALUES(19,10,'19:10','[Закариё] Гуфт: «Парвардигоро, барои ман нишонае [бар таҳаққуқи ин башорат] қарор бидеҳ». [Аллоҳ таоло] Фармуд: «Нишонаи ту ин аст, ки cе шабона [рӯз] дар ҳоле ки солим [ва тандуруст] ҳастӣ, бо мардум сухан намегӯӣ»');
INSERT INTO "pioneers" VALUES(19,11,'19:11','Пас, ӯ аз меҳроб [ ҷойгоҳи ибодаташ] ба сӯйи қавмаш рафт ва ба онон ишора кард, ки: «бомдоду шомгоҳ [Парвардигорро] ба покӣ ёд кунед»');
INSERT INTO "pioneers" VALUES(19,12,'19:12','[Аллоҳ таоло фармуд] «Эй Яҳё, китоби [Таврот]-ро бо қувват [ва ҷиддият] бигир» ва дар кӯдакӣ ба ӯ дониш [ва ҳикмат] додем');
INSERT INTO "pioneers" VALUES(19,13,'19:13','Ва аз ҷониби хеш [ба ӯ] муҳаббату покӣ [аз гуноҳон ато кардем] ва ӯ парҳезкор буд');
INSERT INTO "pioneers" VALUES(19,14,'19:14','Ва нисбат ба падару модараш накукор буд ва саркаш [-у] нофармон набуд');
INSERT INTO "pioneers" VALUES(19,15,'19:15','Ва салом [ва амнияти Аллоҳ таоло] бар ӯ бод, рӯзе, ки мутаваллид шуд ва рӯзе, ки мемирад ва рӯзе, ки зинда барангехта мешавад');
INSERT INTO "pioneers" VALUES(19,16,'19:16','Ва дар [ин] китоб [Қуръон] аз Марям ёд кун, он гоҳ ки аз хонаводааш канора гирифт [ва дар] ноҳияи шарқӣ [аз онон] мустақар шуд.');
INSERT INTO "pioneers" VALUES(19,17,'19:17','Ва миёни худ ва онҳо пардае афканд [то хилватгоҳи ибодаташ бошад]. Он гоҳ [мо] Рӯҳи худ [Ҷабраил]-ро ба сӯяш фиристодем ва ба шакли инсоне хушандом бар вай намоён гашт.');
INSERT INTO "pioneers" VALUES(19,18,'19:18','[Марям] Гуфт: «Ман аз [шарри] ту ба [Аллоҳи] Раҳмон паноҳ мебарам. Агар парҳезкорӣ [ба ман осеб нарасон]»');
INSERT INTO "pioneers" VALUES(19,19,'19:19','[Ҷабраил] гуфт: «Яқинан, ман фиристодаи Парвардигорат ҳастам, то ба ту писари покизае бахшам»');
INSERT INTO "pioneers" VALUES(19,20,'19:20','[Марям] гуфт: «Чи гуна мумкин аст писаре дошта бошам; ҳол он ки дасти ҳеҷ инсоне ба ман нарасидааст ва бадкор [низ] набудаам?»');
INSERT INTO "pioneers" VALUES(19,21,'19:21','[Ҷабраил] Гуфт: «[Дастур] чунин аст. Парвардигорат фармуд: «Ин [кор] бар ман осон аст ва ӯро барои мардум нишонае [аз қудрати хеш] қарор медиҳем ва [низ] раҳмате аз сӯйи Мост; ва ин [шеваи таваллуд] амре анҷомёфта [ва ҳатмӣ] аст»');
INSERT INTO "pioneers" VALUES(19,22,'19:22','Пас, [Марям] ба ӯ бордор шуд. Ва бо ӯ ба ҷойе дурдаст [рафт ва аз мардум] канора гирифт');
INSERT INTO "pioneers" VALUES(19,23,'19:23','Пас, дарди зоймон ӯро ба самти як танаи нахл кашонд [ва ӯ дар ҳоле ки дард мекашид] гуфт: «Эй кош, пеш аз ин мемурдам ва ба куллӣ аз ёдҳо мерафтам [то касе дар бораам хаёли ботил накунад]»');
INSERT INTO "pioneers" VALUES(19,24,'19:24','Ногаҳон аз зери [пойи] ӯ [Исо] нидояш дод, ки: «Ғамгин мабош. Парвардигорат зери [пойи] ту чашмаи обе равон сохтааст');
INSERT INTO "pioneers" VALUES(19,25,'19:25','Ва танаи нахлро ба тарафи худ такон бидеҳ, [то] рутаби тоза бар ту фурӯ резад');
INSERT INTO "pioneers" VALUES(19,26,'19:26','Пас, [аз он хурмо] бихӯр ва [аз оби чашма] бинӯш ва дидагонро [ба ин фарзанд] равшан дор; ва агар ҳар як аз мардумро дидӣ, [бо ишора] бигӯ: «Ман барои [Аллоҳи] Раҳмон рӯзаи [cукут] назр кардам ва имрӯз бо ҳеҷ инсоне сухан нахоҳам гуфт»');
INSERT INTO "pioneers" VALUES(19,27,'19:27','Пас, [Марям] дар ҳоле ки ӯ [Исо]-ро бардошта буд, назди қавмаш омад. Онон гуфтанд: «Эй Марям, ба ростӣ, дурӯғи бузурге мегӯӣ [ки навзод бидуни падар мутаваллид шудааст]');
INSERT INTO "pioneers" VALUES(19,28,'19:28','Эй хоҳари Ҳорун, на падарат марди бадкоре буд ва на модарат [зани] бадкорае»');
INSERT INTO "pioneers" VALUES(19,29,'19:29','Пас, Марям ба ӯ [Исо] ишора кард. Онон гуфтанд: «Чи гуна бо кӯдаке дар гаҳвора сухан бигӯем?»');
INSERT INTO "pioneers" VALUES(19,30,'19:30','[Исо ба сухан омад ва] гуфт: «Ман бандаи Аллоҳ таоло ҳастам. Ӯ ба ман китоб дода ва маро паёмбар сохтааст');
INSERT INTO "pioneers" VALUES(19,31,'19:31','Ва маро – ҳар ҷо ки бошам – пурбаракат намуда ва то зинда ҳастам, ба намозу закот супоришам кардааст');
INSERT INTO "pioneers" VALUES(19,32,'19:32','Ва нисбат ба модарам накукор [гардонида] ва маро [дар баробари Парвардигорам] саркаш [-у] нофармон қарор надодааст');
INSERT INTO "pioneers" VALUES(19,33,'19:33','Ва салом бар ман рӯзе, ки мутаваллид шудам ва рӯзе, ки мемирам ва рӯзе, ки зинда барангехта мешавам»');
INSERT INTO "pioneers" VALUES(19,34,'19:34','Ин аст [ҳикояти] Исо писари Марям; [ҳамон] гуфтори росте, ки [гумроҳон] дар борааш тардид [ва ихтилоф] мекунанд');
INSERT INTO "pioneers" VALUES(19,35,'19:35','[Ҳаргиз] Сазовор нест, ки Аллоҳ таоло [барои хеш] фарзанде бигирад. Ӯ [поку] муназзаҳ аст. [Аллоҳ таоло] Ҳар гоҳ кореро ирода кунад, фақат ба он мегӯяд: «Мавҷуд шав»; пас [бедиранг] мавҷуд мешавад');
INSERT INTO "pioneers" VALUES(19,36,'19:36','Ва [Исо ба қавмаш гуфт] «Бе тардид, Аллоҳ таоло Парвардигори ман ва шумост; пас, ӯро парастед [ки] роҳи рост ин аст»');
INSERT INTO "pioneers" VALUES(19,37,'19:37','Он гоҳ гурӯҳҳо [-и мухталифи қавмаш] дар миёни худ дучори ихтилоф шуданд. Пас, вой бар онҳо, ки куфр варзиданд аз мушоҳидаи рӯзи бузург [қиёмат]!');
INSERT INTO "pioneers" VALUES(19,38,'19:38','Он рӯз, ки назди мо меоянд, чи [хуб] шунаво ва бино ҳастанд! Аммо имрӯз [дар зиндагии дунё] ситамгорон дар гумроҳии ошкоранд');
INSERT INTO "pioneers" VALUES(19,39,'19:39','Ва [эй паёмбар] ба онон дар бораи рӯзи [пушаймонӣ ва] ҳасрат ҳушдор бидеҳ; он гоҳ ки кори [доварӣ] анҷом мегирад; ва [имрӯз] онон дар ғафлатанд ва имон намеоваранд');
INSERT INTO "pioneers" VALUES(19,40,'19:40','Яқинан, Мо замин ва ҳар киро бар он аст, ба ирс мебарем ва [ҳама] ба сӯйи Мо бозгардонида шаванд');
INSERT INTO "pioneers" VALUES(19,41,'19:41','Ва [эй паёмбар] дар ин китоб аз Иброҳим ёд кун. Бе тардид, ӯ паёмбаре ростгӯ буд');
INSERT INTO "pioneers" VALUES(19,42,'19:42','Ҳангоме ки ба падараш [Озар] гуфт: «Падарҷон, чаро чизеро парастиш мекунӣ, ки на [дуоятро] мешунавад ва на [ибодататро] мебинад ва на газандеро аз ту дур месозад?');
INSERT INTO "pioneers" VALUES(19,43,'19:43','Падарҷон, яқинан, аз дониши [ваҳй] чизе ба ман расидааст, ки [ҳаргиз] ба ту нарасидааст; пас, аз ман пайравӣ кун, то ба роҳе рост ҳидоят кунам');
INSERT INTO "pioneers" VALUES(19,44,'19:44','Падарҷон, аз шайтон пайравӣ накун, [чаро ки] бе тардид, шайтон нисбат ба [Аллоҳи] Раҳмон нофармон буд');
INSERT INTO "pioneers" VALUES(19,45,'19:45','Падарҷон, ман метарсам, ки аз [ҷониби Аллоҳи] Раҳмон азобе ба ту расад ва [дар дузах] аз дӯстон [ва ҳамнишинони] шайтон бошӣ»');
INSERT INTO "pioneers" VALUES(19,46,'19:46','[Озар] Гуфт: «Эй Иброҳим, оё аз маъбудҳои ман рӯйгардонӣ? Агар [аз ақидаат] даст барнадорӣ, ҳатман, сангсорат мекунам; [бирав] ва муддати зиёде аз ман дур шав»');
INSERT INTO "pioneers" VALUES(19,47,'19:47','[Иброҳим] Гуфт: «Салом [ва амният аз ҷониби ман] бар ту. Ба зудӣ аз Парвардигорам бароят омурзиш хоҳам хост. Бе тардид, ӯ нисбат ба ман бисёр меҳрубон аст');
INSERT INTO "pioneers" VALUES(19,48,'19:48','Ва аз шумо [кофирон] ва он чи ба ҷойи Аллоҳ таоло мехонед, канорагирӣ мекунам ва Парвардигорамро мехонам. Умед аст, ки дар хондани Парвардигорам [аз иҷобат] бебаҳра набошам»');
INSERT INTO "pioneers" VALUES(19,49,'19:49','Пас, чун аз онон ва он чи ба ҷойи Аллоҳ таоло мепарастиданд, канорагирӣ кард, Исҳоқ ва Яъқубро ба ӯ бахшидем ва ҳамаи [онон]-ро паёмбар намудем');
INSERT INTO "pioneers" VALUES(19,50,'19:50','Ва эшонро аз раҳмати хеш баҳраманд сохтем ва барояшон [дар миёни мардум овозаи баланд ва] номи наку ниҳодем');
INSERT INTO "pioneers" VALUES(19,51,'19:51','Ва дар ин китоб аз Мӯсо ёд кун. Бе тардид, ӯ [баргузидае] мухлис ва фиристодаву паёмбар буд');
INSERT INTO "pioneers" VALUES(19,52,'19:52','Ва [мо] ӯро аз самти рости [кӯҳи] Тӯр нидо додем ва муноҷоткунон [ба хеш] наздикаш сохтем');
INSERT INTO "pioneers" VALUES(19,53,'19:53','Ва аз раҳмати худ бародараш Ҳорунро – ки, паёмбар [ва ёвари ӯ] буд – ва ба вай ато кардем');
INSERT INTO "pioneers" VALUES(19,54,'19:54','Ва дар ин китоб аз Исмоил ёд кун. Бе тардид, ӯ ростваъда ва фиристодаву паёмбар буд');
INSERT INTO "pioneers" VALUES(19,55,'19:55','Ва [ҳамвора] хонаводаашро ба намозу закот фармон медод ва назди Парвардигораш писандида буд');
INSERT INTO "pioneers" VALUES(19,56,'19:56','Ва дар ин китоб аз Идрис ёд кун. Бе тардид, ӯ ростгуфтору паёмбаре буд');
INSERT INTO "pioneers" VALUES(19,57,'19:57','Ва [мо] ӯро ба мақоме баланд баркашидем');
INSERT INTO "pioneers" VALUES(19,58,'19:58','Инон касоне аз паёмбарон ҳастанд, ки Аллоҳ таоло бар эшон неъмат [ҳидоят] дод; [афроде] аз фарзандони Одам ва аз касоне, ки ҳамроҳи Нуҳ [бар киштӣ] савор кардем ва аз фарзандони Иброҳим ва Яъқуб ва аз [ҷумла] афроде, ки ҳидоят кардем ва баргузидем [ва] ҳар гоҳ оёти [Парвардигори] Раҳмон бар эшон хонда мешуд, саҷдакунон ва гирён ба хок меафтоданд');
INSERT INTO "pioneers" VALUES(19,59,'19:59','Он гоҳ пас аз онон ҷонишиноне гумроҳ ва ношоиста омаданд, ки намозро табоҳ сохтанд [дар он саҳлангорӣ намуданд] ва аз ҳавасҳо [ва шаҳавот] пайравӣ карданд; ва ба зудӣ [кайфари] гумроҳии [хеш]-ро хоҳанд дид');
INSERT INTO "pioneers" VALUES(19,60,'19:60','Магар касе, ки тавба намуд ва имон овард ва коре шоиста кард. Пас, инон ба биҳишт дохил мешаванд ва бар онон ҳеҷ ситаме намеравад');
INSERT INTO "pioneers" VALUES(19,61,'19:61','Боғҳое ҷовидон, ки [Аллоҳи] Раҳмон аз [олам] нопайдо ба бандагонаш ваъда додааст. Яқинан, ваъдаи ӯ омаданӣ аст');
INSERT INTO "pioneers" VALUES(19,62,'19:62','Дар он ҷо сухани беҳудае намешунавад [ва гуфторашон] ҷуз салом [у таҳият нест]; ва дар он ҷо бар эшон [ҳар] субҳу шом рӯзӣ [беҳисоб фароҳам] аст');
INSERT INTO "pioneers" VALUES(19,63,'19:63','Ин ҳамон биҳиште аст, ки ба ҳар кас аз бандагонамон, ки [дар дунё] парҳезкор бошад, ба мерос медиҳем');
INSERT INTO "pioneers" VALUES(19,64,'19:64','Ва [эй Ҷабраил, ба Муҳаммад бигӯ] «Мо ҷуз ба фармони Парвардигорат фуруд намеоем. Ӯ ҳоким бар оянда ва гузашта ва ҳоли мост; ва Парвардигорат фаромӯшкор нест');
INSERT INTO "pioneers" VALUES(19,65,'19:65','[Ҳамон] Парвардигори осмонҳо ва замин ва он чи миёни онҳост; пас, ӯро парастиш кун ва бар ибодаташ шикебо [ва пойдор] бош. Оё [ҳаммонанд ва] ҳамноме барояш мешиносӣ?»');
INSERT INTO "pioneers" VALUES(19,66,'19:66','Ва инсони [кофир] мегӯяд: «Оё ҳангоме ки бимирам, ба ростӣ, маро зинда [аз гӯр] берун хоҳанд овард?»');
INSERT INTO "pioneers" VALUES(19,67,'19:67','Оё инсон ба ёд намеоварад, ки мо пеш аз ин ӯро офаридем, дар ҳоле ки ҳеҷ набуд?');
INSERT INTO "pioneers" VALUES(19,68,'19:68','Пас, савганд ба Парвардигорат, ки яқинан, онон [мункирони қиёмат]-ро бо шаётин маҳшур хоҳем кард; сипас ҳамаро ба зону дароварда гирдогирди дузах ҳозир мекунем');
INSERT INTO "pioneers" VALUES(19,69,'19:69','Сипас аз ҳар гурӯҳе касонеро дар баробари [фармони Аллоҳи] Раҳмон саркаштар будаанд, берун мекашем');
INSERT INTO "pioneers" VALUES(19,70,'19:70','Он гоҳ, яқинан, Мо ба [ҳоли] касоне, ки барои [дарафтодан ба] он [оташи даҳшатнок] сазовортаранд, донотарем');
INSERT INTO "pioneers" VALUES(19,71,'19:71','Ва ҳеҷ як аз шумо [мардум] нест, магар он ки вориди он мегардад; [муъминон барои убур ва дидан, кофирон барои вуруд ва мондан]. Ин [амр] ҳамвора бар Парвардигорат қазои ҳатмӣ аст');
INSERT INTO "pioneers" VALUES(19,72,'19:72','Сипас касонеро, ки парҳезкорӣ кардаанд, [аз оташ] наҷот медиҳем ва золимонро ба зону даромада, дар он раҳо мекунем');
INSERT INTO "pioneers" VALUES(19,73,'19:73','Ва ҳангоме ки оёти равшани Мо бар онон тиловат мешавад, касоне, ки куфр варзидаанд, ба афроде, ки имон овардаанд, мегӯянд: «Кадом як аз [мо] ду гурӯҳ ҷойгоҳе беҳтару маҷлисе накутар дорад?»');
INSERT INTO "pioneers" VALUES(19,74,'19:74','Чи бисёр наслҳоро пеш аз онон нобуд кардем, ки сарвате бештар ва зоҳире оростатар доштанд');
INSERT INTO "pioneers" VALUES(19,75,'19:75','[Эй паёмбар] Бигӯ: «Ҳар ки дар гумроҳӣ аст, [Аллоҳи] Раҳмон ба ӯ муҳлат медиҳад [то гумроҳиаш бештар шавад] то замоне, ки ваъдаи илоҳиро [ба чашми худ] бубинанд: ё азоби [дунё] ё [кайфари] қиёмат. Пас, ба зудӣ хоҳанд донист, ки чи касе ҷойгоҳаш бадтар ва сипоҳаш нотавонтар аст»');
INSERT INTO "pioneers" VALUES(19,76,'19:76','Ва [аммо] касоне, ки роҳ ёфтаанд, Аллоҳ таоло бар ҳидояташон меафзояд; ва некиҳои пойдор [рафтору гуфтори шоиста] дар пешгоҳи Парвардигорат подоше беҳтар ва саранҷоме накутар дорад');
INSERT INTO "pioneers" VALUES(19,77,'19:77','Оё дидӣ он касеро, ки ба оёти Мо кофир шуд ва гуфт: «Ҳатман, молу фарзанд [-и фаровоне дар охират] ба ман дода хоҳад шуд?»');
INSERT INTO "pioneers" VALUES(19,78,'19:78','Оё ӯ бар ғайб огоҳӣ ёфтааст, ё аз [Аллоҳи] Раҳмон паймоне гирифтааст?');
INSERT INTO "pioneers" VALUES(19,79,'19:79','[Ҳаргиз] Чунин нест; гуфтаашро менависем ва бар азобаш меафзоем');
INSERT INTO "pioneers" VALUES(19,80,'19:80','Ва он чи [аз амволу фарзандон], ки мегӯяд, аз ӯ ба ирс мебарем ва [рӯзи қиёмат тиҳидаст ва] танҳо назди мо меояд');
INSERT INTO "pioneers" VALUES(19,81,'19:81','Ва [мушрикон] ба ҷойи Аллоҳ таоло маъбудоне баргузидаанд, то [ёвару] пуштибонашон бошад');
INSERT INTO "pioneers" VALUES(19,82,'19:82','Чунин нест; [балки он маъбудҳо дар қиёмат] ибодати ононро инкор хоҳанд кард ва душманашон хоҳанд буд');
INSERT INTO "pioneers" VALUES(19,83,'19:83',']Эй паёмбар] Оё надидӣ, ки Мо шаётинро бар кофирон фиристодем, то ононро [бо васвасаҳои худ] таҳрик кунанд [ва аз роҳи Аллоҳ таоло боздоранд]?');
INSERT INTO "pioneers" VALUES(19,84,'19:84','Пас, дар бораи [нобудии] онон шитоб накун. Ҷуз ин нест, ки Мо [рӯзҳои марг ва фаро расидани азобро] ба диққат барояшон мешуморем');
INSERT INTO "pioneers" VALUES(19,85,'19:85','[Ёд кун аз] Рӯзе, ки парҳезкоронро [бо иззату эҳтиром] ба меҳмонии [Аллоҳи] Раҳмон гирд меоварем');
INSERT INTO "pioneers" VALUES(19,86,'19:86','Ва муҷримонро ташнаком ба сӯйи ҷаҳаннам меронем');
INSERT INTO "pioneers" VALUES(19,87,'19:87','Онон ҳаргиз [тавони] шафоат надоранд; магар касе, ки [бо имон ва амали шоистааш] аз [Аллоҳи] Раҳмон паймоне гирифта бошад');
INSERT INTO "pioneers" VALUES(19,88,'19:88','Ва [мушрикон] гуфтанд: «[Аллоҳи] Раҳмон фарзанде [барои хеш] баргузидааст»');
INSERT INTO "pioneers" VALUES(19,89,'19:89','Ба ростӣ, [бо ин иддао] чизе бисёр зишт [ба миён] овардед!');
INSERT INTO "pioneers" VALUES(19,90,'19:90','Аз ин [сухани куфромез] наздик аст осмонҳо аз ҳам бипошад ва замин бишкофад ва кӯҳҳо дар ҳам бишкананд ва фуру резанд');
INSERT INTO "pioneers" VALUES(19,91,'19:91','[Чаро] Ки барои [Аллоҳи] Раҳмон фарзанде иддао кардаанд');
INSERT INTO "pioneers" VALUES(19,92,'19:92','Ва ҳаргиз сазовори [Аллоҳи] Раҳмон нест, ки фарзанде баргузинад');
INSERT INTO "pioneers" VALUES(19,93,'19:93','Дар осмонҳо ва замин ҳеҷ касе нест, магар он ки [дар рӯзи қиёмат хоксор ва] бандавор назди Аллоҳи Раҳмон меояд');
INSERT INTO "pioneers" VALUES(19,94,'19:94','Яқинан, [Аллоҳ таоло ҳамаи] ононро дар шумор оварда ва дуруст баршумурдааст');
INSERT INTO "pioneers" VALUES(19,95,'19:95','Ва ҳамагиашон рӯзи қиёмат танҳо [ва тиҳидаст] назди Ӯ ҳозир мешаванд');
INSERT INTO "pioneers" VALUES(19,96,'19:96','Касоне, ки имон овардаанд ва корҳои шоиста кардаанд, яқинан, [Аллоҳи] Раҳмон [дар дилҳои бандагонаш] барояшон муҳаббате хоҳад ниҳод');
INSERT INTO "pioneers" VALUES(19,97,'19:97','[Эй паёмбар] Бе тардид, Мо ин [Қуръон]-ро бар забони ту осон намудем, то парҳезкоронро бо он башорат диҳӣ ва ситезагарон [-и сарсахт]-ро бим диҳӣ');
INSERT INTO "pioneers" VALUES(19,98,'19:98','Ва чи бисёр наслҳои гузаштаро пеш аз онон нобуд кардем. Оё [вуҷуди] ҳеҷ як аз ононро эҳсос мекунӣ ё камтарин садое аз онон мешунавӣ?');
INSERT INTO "pioneers" VALUES(20,1,'20:1','То, ҳо');
INSERT INTO "pioneers" VALUES(20,2,'20:2','[Эй паёмбар, мо] Қуръонро бар ту нозил накардем, то дар ранҷ бияфтӣ');
INSERT INTO "pioneers" VALUES(20,3,'20:3','[Онро нафиристодем] Магар он ки панде бошад, барои ҳар ки [аз азоби илоҳӣ] метарсад');
INSERT INTO "pioneers" VALUES(20,4,'20:4','[Ин Қуръон] Фуруфиристодае аст аз ҷониби он ки замину осмонҳои баландро офаридааст');
INSERT INTO "pioneers" VALUES(20,5,'20:5','[Парвардигори] Раҳмон [онгуна ки шоистаи Ӯ аст ва бидуни он ки мӯҳтоҷ ба Арш бошад] бар Арш қарор гирифт');
INSERT INTO "pioneers" VALUES(20,6,'20:6','Он чи дар осмонҳо ва замин ва он чи миёни онҳост ва он чи зери хок [пинҳон] аст, [ҳамагӣ] аз они Ӯст');
INSERT INTO "pioneers" VALUES(20,7,'20:7','Ва агар сухан ошкор бигӯӣ [ё онро пинҳон дорӣ], яқинан, Ӯ [рози] ниҳон ва [ҳатто] ниҳонтар [аз он]-ро низ медонад');
INSERT INTO "pioneers" VALUES(20,8,'20:8','Аллоҳ таоло аст, ки ҳеҷ маъбуде [бар ҳақ] ҷуз Ӯ нест [ва] накутарин номҳо аз они Ӯст');
INSERT INTO "pioneers" VALUES(20,9,'20:9','[Эй паёмбар] Оё хабари Мӯсо ба ту расидааст?');
INSERT INTO "pioneers" VALUES(20,10,'20:10','Он гоҳ ки оташеро [аз дур] диду ба хонаводааш гуфт: «[Лаҳзае] Диранг кунед [ки] ман оташе дидаам. Шояд шуълае аз он бароятон биёварам ё канори он оташ роҳнамое биёбам»');
INSERT INTO "pioneers" VALUES(20,11,'20:11','Пас, чун назди он [оташ] омад, нидо дода шуд, ки: «Эй Мӯсо');
INSERT INTO "pioneers" VALUES(20,12,'20:12','Ба ростӣ, ман Парвардигорат ҳастам; пас, [барои муноҷот бо Парвардигор] кафшҳоятро даровар [ки] дар ҳақиқат, ту дар сарзамини муқаддаси «Туво» ҳастӣ');
INSERT INTO "pioneers" VALUES(20,13,'20:13','Ва ман туро [ба паёмбарӣ] баргузидам; пас, ба он чи ваҳй мешавад, гӯш фаро дор');
INSERT INTO "pioneers" VALUES(20,14,'20:14','Бе тардид, манам «Аллоҳ таоло», ки ҳеҷ маъбуде [бар ҳаққе] ҷуз ман нест; пас, маро бипараст ва барои ёди ман намоз бар по дор');
INSERT INTO "pioneers" VALUES(20,15,'20:15','Яқинан, қиёмат омаданӣ аст; [вале] мехоҳам [вақти] онро пинҳон дорам, то ба ҳар касе дар баробари [кор ва] талоше, ки мекунад, подош дода шавад');
INSERT INTO "pioneers" VALUES(20,16,'20:16','Пас, мабодо касе, ки ба он имон надорад ва аз ҳавасҳояш пайравӣ мекунад, туро аз [накукорӣ ва омодагӣ барои] он боздорад, ки ҳалок мешавӣ');
INSERT INTO "pioneers" VALUES(20,17,'20:17','Ва, эй Мӯсо, он чист ба [дасти] ростат?»');
INSERT INTO "pioneers" VALUES(20,18,'20:18','Ӯ гуфт: «Ин асои ман аст; бар он такя мекунам ва бо он барои гӯсфандонам [барги дарахтонро] фуру мерезам ва бо он корҳои дигаре [низ] дорам [ки бароварда мекунам]');
INSERT INTO "pioneers" VALUES(20,19,'20:19','[Аллоҳ таоло] Фармуд: «Эй Мӯсо, онро бияфкан»');
INSERT INTO "pioneers" VALUES(20,20,'20:20','Пас, [Мӯсо] онро [бар замин] афканд ва ногаҳон [табдил ба] море шуд, ки ба суръат мехазид');
INSERT INTO "pioneers" VALUES(20,21,'20:21','[Аллоҳ таоло] Фармуд: «Онро бигир ва натарс. Ба зудӣ онро ба шакли аввалаш бозмегардонем');
INSERT INTO "pioneers" VALUES(20,22,'20:22','Ва дастро дар бағал кун, то бидуни ҳеҷ газанде [ба унвони] муъҷизае дигар сафед [-у дурахшон] берун ояд');
INSERT INTO "pioneers" VALUES(20,23,'20:23','[Ҳадаф аз ироаи ду муъҷиза ин буд], ки бархе аз нишонаҳои бузурги хешро нишонат диҳем');
INSERT INTO "pioneers" VALUES(20,24,'20:24','[Инак] Ба сӯйи Фиръавн бирав [ки] бе тардид, [куфр варзида ва] саркашӣ кардааст»');
INSERT INTO "pioneers" VALUES(20,25,'20:25','[Мӯсо] Гуфт: «Парвардигоро, бар [ҳавсала ва] шикебоиям бияфзо');
INSERT INTO "pioneers" VALUES(20,26,'20:26','Ва корамро бароям осон гардон');
INSERT INTO "pioneers" VALUES(20,27,'20:27','Ва гиреҳ аз забонам боз кун');
INSERT INTO "pioneers" VALUES(20,28,'20:28','То суханамро дарёбанд');
INSERT INTO "pioneers" VALUES(20,29,'20:29','Ва барои ман ёваре аз [афроди] хонадонам қарор бидеҳ');
INSERT INTO "pioneers" VALUES(20,30,'20:30','Ҳорун-бародарамро');
INSERT INTO "pioneers" VALUES(20,31,'20:31','Пуштамро бо ӯ устувор соз');
INSERT INTO "pioneers" VALUES(20,32,'20:32','Ва ӯро дар кори [рисолати] ман шарик кун');
INSERT INTO "pioneers" VALUES(20,33,'20:33','Туро бисёр ба покӣ биситоем');
INSERT INTO "pioneers" VALUES(20,34,'20:34','ва бисёр ёдат кунем');
INSERT INTO "pioneers" VALUES(20,35,'20:35','Бе тардид, Ту ба [ҳоли] мо биноӣ»');
INSERT INTO "pioneers" VALUES(20,36,'20:36','[Аллоҳ таоло] Фармуд: «Эй Мӯсо, хостаат ба ту дода шуд');
INSERT INTO "pioneers" VALUES(20,37,'20:37','Дар ҳақиқат, мо [пештар] як бори дигар [низ] бар ту миннат ниҳода будем');
INSERT INTO "pioneers" VALUES(20,38,'20:38','Он гоҳ ки он чиро, ки [бояд дар бораи ҳифозат аз ту] илҳом мешуд, ба модарат илҳом кардем');
INSERT INTO "pioneers" VALUES(20,39,'20:39','Ки: «ӯро дар сандуқе бигзор ва он [сандуқ]-ро ба дарё [рӯди Нил] бияндоз, то дарё ӯро ба соҳил бияфканад [ва] душмани Ман ва душмани ӯ онро [аз об] бигирад; ва бар ту аз ҷониби хеш муҳре [дар дилҳо] афкандам ва [ҳимоятат кардам] таҳти назар парвариш ёбӣ');
INSERT INTO "pioneers" VALUES(20,40,'20:40','Он гоҳ ки хоҳарат [дар канори руд ҳамроҳ бо сандуқи ту] роҳ мерафт ва [ба касоне, ки туро аз об гирифтанд] гуфт: «Мехоҳед шахсеро нишонатон диҳам, ки сарпарастии ӯро ба уҳда бигирад?» Пас, туро ба модарат бозгардонидем, то чашмаш [ба дидорат] равшан гардад ва андуҳгин набошад; ва [баъдҳо] ту шахсе [қибтӣ]-ро куштӣ ва Мо аз андӯҳ [ва муҷозоти қатл] наҷотат додем ва туро борҳо озмудем. Пас [аз он] чанд сол байни мардуми Мадян ба сар бурдӣ; он гоҳ, эй Мӯсо, бар [тибқи] тақдири [илоҳӣ ва дар замони муқаррар ба ин ҷо] омадӣ');
INSERT INTO "pioneers" VALUES(20,41,'20:41','Ва туро барои худам баргузидам [то паёмамро ба мардум бирасонӣ]');
INSERT INTO "pioneers" VALUES(20,42,'20:42','[Инак] Ту ва бародарат бо нишонаҳои [қудрату ягонагии] Ман равона шавед ва дар [даъвати ҳақ ва] ёд кардани Ман сустӣ наварзед');
INSERT INTO "pioneers" VALUES(20,43,'20:43','Ба сӯйи Фиръавн биравед, ки ӯ [куфр варзида ва] саркашӣ кардааст');
INSERT INTO "pioneers" VALUES(20,44,'20:44','Пас, ба нармӣ бо ӯ сухан бигӯед; бошад, ки панд гирад ё [аз азоби илоҳӣ] битарсад [ва тавба кунад]');
INSERT INTO "pioneers" VALUES(20,45,'20:45','[Мӯсо ва Ҳорун] гуфтанд: «Парвардигоро, мо бимнокем, ки бар мо пешдастӣ намояд [ва пеш аз поёни даъват муҷозотамон кунад] ё [дар баробари сухани ҳақ] саркашӣ кунад»');
INSERT INTO "pioneers" VALUES(20,46,'20:46','[Аллоҳ таоло] фармуд: «Натарсед [ки] яқинан, Ман ҳамроҳатон ҳастам [ва ҳама чизро] мешунавам ва мебинам');
INSERT INTO "pioneers" VALUES(20,47,'20:47','Пас, ба сӯи ӯ биравед ва бигӯед: «Мо фиристодагони Парвардигорат ҳастем. Бани Исроилро ҳамроҳи мо бифирист ва [таҳқиру] озорашон накун. Ба ростӣ, ки Мо барои ту аз ҷониби Парвардигорат нишонаҳои равшане овардаем; ва саломатӣ [аз азоби илоҳӣ] бар касест, ки аз ҳидоят пайравӣ кунад');
INSERT INTO "pioneers" VALUES(20,48,'20:48','Дар ҳақиқат, ба мо ваҳй шудааст, ки азоби [дузах] бар касест, ки [оёт ва муъҷизотро] дурӯғ ангошт ва [аз даъвати паёмбараш] рӯй гардонд»');
INSERT INTO "pioneers" VALUES(20,49,'20:49','[Фиръавн] Гуфт: «Эй Мӯсо, Парвардигори шумо кист?»');
INSERT INTO "pioneers" VALUES(20,50,'20:50','[Мӯсо] гуфт: «Парвардигори мо касест, ки ба ҳар чизе офариниши [зоҳир ва вижагиҳои муносиби] ӯро бахшида ва сипас [онро] ҳидоят намудааст»');
INSERT INTO "pioneers" VALUES(20,51,'20:51','[Фиръавн] Гуфт: «Пас, ҳол [ва сарнавишти наслҳои гузашта [ки кофир будаанд] чист?»');
INSERT INTO "pioneers" VALUES(20,52,'20:52','[Мӯсо] Гуфт: «Дониши он дар китобе [Лавҳи Маҳфуз] назди Парвардигори ман [сабт] аст. Парвардигорам на иштибоҳ мекунад ва на фаромӯш менамояд');
INSERT INTO "pioneers" VALUES(20,53,'20:53','Он [маъбуде], ки заминро бароятон [ҳамчун] гаҳворае [маҳалли осоиш] қарор дод ва дар он роҳҳое эҷод кард ва аз осмон обе фуру фиристод ва ба василаи он анвои гуногуни гиёҳонро [аз замин] берун овардем');
INSERT INTO "pioneers" VALUES(20,54,'20:54','[Ҳам худ аз он] Бихӯред ва [ҳам] чаҳорпоёнатонро бичаронед. Бе тардид, дар ин [умур] барои хирадмандон нишонаҳое [аз қудрати илоҳӣ] аст');
INSERT INTO "pioneers" VALUES(20,55,'20:55','Шуморо аз он [хок] офаридем ва ба он бозмегардонем ва бори дигар [дар қиёмат] аз он берун меоварем');
INSERT INTO "pioneers" VALUES(20,56,'20:56','Ва ба ростӣ, [Мо] ҳамаи нишонаҳои [қудрати] худро ба ӯ нишон додем; вале [ҳамаро] дурӯғ пиндошт ва [даъвати ҳақро] напазируфт');
INSERT INTO "pioneers" VALUES(20,57,'20:57','[Он гоҳ] Гуфт: «Эй Мӯсо, оё назди мо омадӣ, ки бо ҷодуят моро аз сарзаминамон берун кунӣ?');
INSERT INTO "pioneers" VALUES(20,58,'20:58','Пас, яқинан, мо низ ҷодуе монанди он бароят меоварем; пас, дар маконе, ки [фосилааш аз ҳар ду тараф] яксон бошад, ваъдагоҳе миёни Мо ва худ таъйин кун, ки на Мо аз он хилоф кунем ва на ту');
INSERT INTO "pioneers" VALUES(20,59,'20:59','[Мӯсо] гуфт: «Ваъдагоҳи шумо рӯзи ид бошад, ки [ҳамаи] мардум пеш аз зуҳр ҷамъ мешаванд»');
INSERT INTO "pioneers" VALUES(20,60,'20:60','Он гоҳ Фиръавн бозгашт ва [тамоми] найрангу ҳилаи хешро ҷамъ кард ва [ҳамроҳи ҷодугаронаш ба маҳалли мавриди назар] омад');
INSERT INTO "pioneers" VALUES(20,61,'20:61','Мӯсо ба онон гуфт: «Вой бар шумо! Бар Аллоҳ таоло дурӯғ набандед, ки бо азобе [сахт] нобудатон месозад; ва яқинан, ҳар ки [бо фиреб додани мардум бар Аллоҳ таоло] дурӯғ баст, зиёнкор гашт»');
INSERT INTO "pioneers" VALUES(20,62,'20:62','Пас, [ҷодугарон пас аз ҳушдори Мӯсо] дар корашон миёни худ ихтилоф карданд ва пинҳонӣ гуфтугӯ карданд');
INSERT INTO "pioneers" VALUES(20,63,'20:63','[Бархе аз онон] Гуфтанд: «Ин ду [нафар] ҳатман, ҷодугаранд [ва] бо ҷодуи хеш мехоҳанд шуморо аз сарзаминатон берун кунанд ва оин [ва роҳу расм]-и бартари шуморо аз миён бардоранд');
INSERT INTO "pioneers" VALUES(20,64,'20:64','Пас, [ҳамаи] найранги хешро фароҳам кунед [ва ба кор бандед]; сипас дар як саф [барои мубориза] ба пеш оед. Бе тардид, ҳар ки имрӯз [бар душман] чира шавад, пирӯз [ва комёб] гаштааст');
INSERT INTO "pioneers" VALUES(20,65,'20:65','[Ҷодугарон] гуфтанд: «Эй Мӯсо, оё ту [он чиро бо худ дорӣ] меафканӣ ё [мо] нахустин касе бошем, ки меафканад?»');
INSERT INTO "pioneers" VALUES(20,66,'20:66','[Мӯсо] Гуфт: «[На] балки шумо бияфканед». Пас, ногоҳ ресмонҳо ва чӯбдастҳои онон аз ҷодуяшон чунон ба назараш расид, ки гӯӣ ба шитоб мехазанд');
INSERT INTO "pioneers" VALUES(20,67,'20:67','Пас, Мӯсо дар дилаш тарсе эҳсос кард');
INSERT INTO "pioneers" VALUES(20,68,'20:68','Гуфтем: «Натарс, [ки] ту, ҳатман, [пирӯз ва] бартарӣ');
INSERT INTO "pioneers" VALUES(20,69,'20:69','Ва он чиро, ки дар дасти рости худ дорӣ, бияфкан, то [тамоми] он чиро, ки сохтаанд, бибалъад. Бе тардид, он чи онон сохтаанд, фақат найранги ҷодугар аст ва ҷодугар ҳар ҷо, ки бошад [ва ҳар чӣ кунад], пирӯз намегардад»');
INSERT INTO "pioneers" VALUES(20,70,'20:70','[Пас, чун Мӯсо асояшро бар замин андохт, ба мори бузурге табдил шуд ва сохтаҳои ононро балъид], он гоҳ ҷодугарон ба саҷда афтоданд [ва] гуфтанд: «Ба Парвардигори Ҳорун ва Мӯсо имон овардем»');
INSERT INTO "pioneers" VALUES(20,71,'20:71','[Фиръавн] Гуфт: «Оё пеш аз он ки ба шумо иҷозат диҳам ба ӯ имон овардед? Ҳатман, ӯ бузурги шумост, ки ба шумо ҷодугарӣ омӯхтааст. Пас, ҳатман, дасту пойи шуморо бар акси якдигар [аз чапу рост] қатъ мекунам ва ҳамагиатонро дар танаҳои нахл ба дор меовезам ва он гоҳ хуб хоҳед донист, ки азоб [ва шиканҷаи] кадом яки аз мо сахттар ва пояндатар аст; [ман ё Парвардигори Мӯсо]»');
INSERT INTO "pioneers" VALUES(20,72,'20:72','[Ҷодугарон] Гуфтанд: «Ҳаргиз [пайравӣ аз] туро бар ин далоили равшане, ки бароямон омадааст ва [ҳамчунин] бар он ки моро офаридааст, тарҷеҳ намедиҳем; пас, ба ҳар ҳукме, ки мехоҳӣ, ҳукм кун. Ту фақат метавонӣ дар зиндагии ин дунё ҳукм кунӣ');
INSERT INTO "pioneers" VALUES(20,73,'20:73','Бе тардид, мо ба Парвардигорамон имон овардем, то гуноҳонамон ва он чиро аз ҷодугарӣ бар мо таҳмил кардаӣ, бибахшояд; ва Аллоҳ таоло беҳтару пояндатар аст»');
INSERT INTO "pioneers" VALUES(20,74,'20:74','Ҳар ки дар пешгоҳи Парвардигораш гунаҳкор ҳозир шавад, яқинан, оташи дузах барои ӯст; дар он ҷо на мемирад [ ки раҳоӣ ёбад] ва на [ба хушӣ] зиндагӣ мекунад');
INSERT INTO "pioneers" VALUES(20,75,'20:75','Ва ҳар ки муъмин назди ӯ биёяд, дар ҳоле ки корҳои шоиста анҷом дода бошад, пас, инон дараҷоти олӣ доранд');
INSERT INTO "pioneers" VALUES(20,76,'20:76','Боғҳои ҷовидони [биҳишт], ки ҷӯйборҳо зери [дарахтони] он ҷорӣ аст. Ҳамеша дар он хоҳанд буд; ва ин аст подоши касе, ки худро [аз олудагиҳои ширку гуноҳ] пок кардааст');
INSERT INTO "pioneers" VALUES(20,77,'20:77','Ва ба ростӣ, [Мо] ба Мӯсо ваҳй кардем, ки: «Бандагонамро шабона [аз Миср берун] бибар, сипас барои онон дар дарё роҳе хушк бикшо, ки на аз таъқиб [-и душманон] хоҳӣ тарсид ва на [аз ғарқ шудан дар дарё] бим дорӣ');
INSERT INTO "pioneers" VALUES(20,78,'20:78','Он гоҳ Фиръавн бо сипоҳиёнаш ононро дунбол кард ва оби дарё эшонро фуру пӯшонид; чунон пӯшонидане [ки ҳақиқаташро касе ҷуз Аллоҳ таоло намедонад]');
INSERT INTO "pioneers" VALUES(20,79,'20:79','Ва [бад-ин сон Фиръавн қавми худро гумроҳ намуд ва ҳидоят накард');
INSERT INTO "pioneers" VALUES(20,80,'20:80','Эй Бани Исроил, ба ростӣ, [Мо] шуморо аз [чанги] душманонатон наҷот додем ва дар самти рости [кӯҳи] Тӯр бо шумо ваъда гузоштем [то Тавротро нозил кунем] ва бар шумо манн [ширинӣ] ва салво [бедона] фуру фиристодем');
INSERT INTO "pioneers" VALUES(20,81,'20:81','Аз неъматҳои покизае, ки ба шумо рӯзӣ додаем, бихӯред ва дар он зиёдаравӣ накунед, ки хашми Ман бар шумо фуруд меояд; ва ҳар ки хашми ман бар ӯ фуруд ояд, ҳатман, ҳалок мегардад');
INSERT INTO "pioneers" VALUES(20,82,'20:82','Ва яқинан, ман омурзандаи касе ҳастам, ки тавба кунад ва имон оварад ва коре шоиста кунад ва он гоҳ бар ҳидоят [-и худ боқӣ] бимонад');
INSERT INTO "pioneers" VALUES(20,83,'20:83','Эй Мӯсо, чи чиз муҷиб шуд, ки ҷилавтар аз қавмат [ба сӯйи ваъдагоҳи Тӯр] бишитобӣ?');
INSERT INTO "pioneers" VALUES(20,84,'20:84','[Мӯсо] Гуфт: «Парвардигоро, онон ба дунболам меоянд ва ман ба сӯят шитофтам, то [аз ман] хушнуд гардӣ»');
INSERT INTO "pioneers" VALUES(20,85,'20:85','[Аллоҳ таоло] Фармуд: «Пас, [бидон, ки] Мо қавматро пас аз [рафтани] ту озмудем ва Сомирӣ гумроҳашон кард»');
INSERT INTO "pioneers" VALUES(20,86,'20:86','Пас, Мӯсо хашмгин [ва] андӯҳнок ба сӯйи қавмаш бозгашт [ва] гуфт: «Эй қавми ман, магар Парвардигоратон [дар бораи нузули Таврот] ваъдаи наку ба шумо надода буд? Оё бар паймони шумо [бо Аллоҳ таоло] замоне тӯлонӣ гузашт [ва ҳамаро аз ёд бурдед] ё хостед, хашме аз [сӯи] Парвардигоратон бар шумо фуруд ояд, ки ваъдаи маро хилоф кардед?»');
INSERT INTO "pioneers" VALUES(20,87,'20:87','Онон гуфтанд: «[Мо] Ба ихтиёри худ паймони туро нашикастем, вале борҳои сангине аз зару зеварҳои қавм [-и Фиръавн]-ро, ки бар мо ниҳода буданд, [дар оташ] андохтем ва Сомирӣ низ [он чиро, ки иддао мекард, хоки суми асби Ҷабраил аст, дар оташ] андохт»');
INSERT INTO "pioneers" VALUES(20,88,'20:88','Он гоҳ барояшон муҷассамаи гӯсолае сохт, ки садое [ҳамчун садои гӯсола] дошт. Пас, [Сомирӣ ва пайравонаш ба мардум] гуфтанд: «Ин маъбуди шумо ва маъбуди Мӯсо аст, ки онро фаромӯш [ва ин ҷо раҳо] кардааст»');
INSERT INTO "pioneers" VALUES(20,89,'20:89','Оё намедиданд, ки [ин гӯсола] ҳеҷ посухе ба онҳо намедиҳад ва ҳеҷ зиёне ва суде барояшон надорад?');
INSERT INTO "pioneers" VALUES(20,90,'20:90','Ва дар ҳақиқат, пеш аз он [ки Мӯсо бозгардад] Ҳорун низ ба онҳо гуфта буд: «Эй қавми ман, ҷуз ин нест, ки шумо бо ин гӯсола имтиҳон шудаед ва бе тардид, Парвардигори [ҳақиқии] шумо [Аллоҳи] Раҳмон аст; пас, пайрави ман бошед ва аз фармонам итоат кунед»');
INSERT INTO "pioneers" VALUES(20,91,'20:91','[Онон] Гуфтанд: «Мо пайваста бар ин [гӯсолапарастӣ] мемонем, то Мӯсо наздамон бозгардад»');
INSERT INTO "pioneers" VALUES(20,92,'20:92','[Вақте Мӯсо бозомад] Гуфт: «Эй Ҳорун, ҳангоме ки дидӣ онон гумроҳ шудаанд,');
INSERT INTO "pioneers" VALUES(20,93,'20:93','чи чизе туро аз пайравии ман боздошт [ва чаро ононро тарк накардӣ]? Оё аз фармонам сарпечӣ кардӣ?»');
INSERT INTO "pioneers" VALUES(20,94,'20:94','[Ҳорун] Гуфт: «Эй писари модарам, ришу [мӯйи] сарамро нагир. Дар ҳақиқат, тарсидам, ки бигӯӣ, миёни Бани Исроил тафриқа андохтӣ ва сухан [ва супориш]-и маро ба кор набастӣ»');
INSERT INTO "pioneers" VALUES(20,95,'20:95','[Мӯсо] Гуфт: «Эй Сомирӣ, ин чи коре аст, ки кардӣ?»');
INSERT INTO "pioneers" VALUES(20,96,'20:96','Ӯ гуфт: «Ман чизеро дидам, ки онон надиданд ва муште аз хоки пойи [асби] фиристода [Ҷабраил]-ро бардоштам ва [дар пайкари гӯсола] андохтам ва [ҳавои] нафсам [ин корро] инчунин бароям орост»');
INSERT INTO "pioneers" VALUES(20,97,'20:97','[Мӯсо] Гуфт: «Пас, бирав. Бе тардид, баҳраи ту дар зиндагӣ ин аст, ки [чунон матруд шавӣ, ки ба мардум] бигӯӣ: «[Ба ман] Даст назанед»; ва ваъдагоҳе [барои азоб] хоҳӣ дошт, ки ҳаргиз аз он тахаллуф нахоҳад шуд; ва [акнун] ба он маъбуде, ки пайваста дар ибодаташ будӣ, бингар; онро месӯзонем, он гоҳ [ки комилан хокистар шуд,] дар дарё парокандааш месозем»');
INSERT INTO "pioneers" VALUES(20,98,'20:98','[Эй мардум] Маъбуди шумо Аллоҳ таоло аст, ки ҳеҷ маъбуде [бар ҳақ] ҷуз Ӯ нест ва илмаш ҳама чизро фаро гирифтааст');
INSERT INTO "pioneers" VALUES(20,99,'20:99','[Эй Паёмбар] Ин гуна аз ахбор [ва достонҳои паёмбарони] гузашта бар ту ҳикоят мекунем; ва ба ростӣ, ки [мо] аз ҷониби хеш ба ту Қуръоне [пандомӯз] додаем');
INSERT INTO "pioneers" VALUES(20,100,'20:100','Ҳар ки аз он рӯйгардон шавад, яқинан, рӯзи қиёмат бори сангине [аз гуноҳ] бар дӯш хоҳад кашид');
INSERT INTO "pioneers" VALUES(20,101,'20:101','Ҷовидона дар он [азоб] хоҳанд монд; ва чи бад аст боре, ки [кофирон] дар қиёмат хоҳанд кашид!');
INSERT INTO "pioneers" VALUES(20,102,'20:102','Рӯзе, ки дар «сур» дамида мешавад ва дар он рӯз муҷримонро [ки бисёр тарсидаанд, бо чашму бадани] кабуд ҷамъ мекунем');
INSERT INTO "pioneers" VALUES(20,103,'20:103','Байни худ оҳиста сухан мегӯянд: «[Шумо] Беш аз даҳ рӯз [дар дунё] ба сар набурдаед»');
INSERT INTO "pioneers" VALUES(20,104,'20:104','Мо ба он чи эшон мегӯянд, донотарем; он гоҳ ки хирадмандтаринашон мегӯяд: «[Шумо] Танҳо як рӯз ба сар бурдаед»');
INSERT INTO "pioneers" VALUES(20,105,'20:105','Ва [эй паёмбар] аз ту дар бораи кӯҳҳо [дар қиёмат] мепурсанд; пас, бигӯ: «Парвардигорам ононро сахт муталошӣ [ва пароканда] мекунад');
INSERT INTO "pioneers" VALUES(20,106,'20:106','Ва онҳоро софу ҳамвор хоҳад намуд');
INSERT INTO "pioneers" VALUES(20,107,'20:107','[Ҳамчун замине] Ки дар он ҳеҷ пастӣ ва баландӣ намебинӣ»');
INSERT INTO "pioneers" VALUES(20,108,'20:108','Дар он рӯз [ҳамаи мардум] бидуни ҳеҷ мухолифате аз даъваткунанда [-и илоҳӣ] пайравӣ мекунанд ва садоҳо дар баробари Худои меҳрубон паст мешаванд ва аз ҳеҷ чиз ҷуз садои пойе нахоҳӣ шунид');
INSERT INTO "pioneers" VALUES(20,109,'20:109','Дар он рӯз шафоат суд надиҳад, магар онро, ки Худои Раҳмон ба ӯ иҷозат диҳад ва суханашро биписандад');
INSERT INTO "pioneers" VALUES(20,110,'20:110','[Аллоҳ таоло] Он чиро, ки инсонҳо [дар охират] дар пеш доранд ва он чиро, ки [дар дунё] пушти сар гузоштаанд, [ҳамаро] медонад, вале дониши онон ӯро фаро намегирад');
INSERT INTO "pioneers" VALUES(20,111,'20:111','Ва чеҳраҳо дар баробари [Парвардигори] зиндаи пойдор хор мегардад ва ҳар ки [бори] ситам бардошт, зиён дид');
INSERT INTO "pioneers" VALUES(20,112,'20:112','Ва касе, ки аз корҳои шоиста анҷом диҳад ва муъмин бошад, на аз ҳеҷ ситаме меҳаросад ва на аз костан [-и подошаш]');
INSERT INTO "pioneers" VALUES(20,113,'20:113','Ва, инчунин, онро Қуръоне [ба забони] арабӣ нозил кардем ва ҳушдорҳои гуногун дар он баён намудем; бошад, ки [аз азоби илоҳӣ] парҳезанд ё [ин китоб] барояшон панде падид оварад');
INSERT INTO "pioneers" VALUES(20,114,'20:114','Пас, баландмартаба аст Аллоҳ таоло, ки фармонравои ҳақ аст. Ва дар [хондани] Қуръон – пеш аз он ки ваҳйи он бар ту поён ёбад – шитоб макун; ва бигӯ: «Парвардигоро, бар донишам бияфзо»');
INSERT INTO "pioneers" VALUES(20,115,'20:115','Ба ростӣ, пеш аз ин бо Одам паймон бастем [ки аз меваи дарахти мамнуа нахӯрад]; вале [паймонашро] фаромӯш кард ва дар ӯ азме [устувор] наёфтем');
INSERT INTO "pioneers" VALUES(20,116,'20:116','Ва [ёд кун аз ҳангоме ки ба фариштагон гуфтем: «Барои Одам саҷда кунед»; пас, [ҳамагӣ] саҷда карданд, магар Иблис, ки сарпечӣ намуд');
INSERT INTO "pioneers" VALUES(20,117,'20:117','Пас, гуфтем: «Эй Одам, ин [Иблис] душмани ту ва ҳамсари туст; мабодо шуморо аз биҳишт берун кунад, ки ба сахтӣ меафтӣ');
INSERT INTO "pioneers" VALUES(20,118,'20:118','Дар ҳақиқат, ту ин [неъмат]-ро [дар биҳишт] дорӣ, ки на гурусна мешавӣ ва на бараҳна мемонӣ');
INSERT INTO "pioneers" VALUES(20,119,'20:119','Ва ин ки дар он ҷо на ташна мешавӣ ва на офтобзада');
INSERT INTO "pioneers" VALUES(20,120,'20:120','Пас, шайтон ӯро васваса кард [ва] гуфт: «Эй Одам, оё [мехоҳӣ] дарахти ҷовидонагӣ ва фармонравоии фанонопазирро нишонат диҳам?»');
INSERT INTO "pioneers" VALUES(20,121,'20:121','Он гоҳ аз он [дарахти мамнуа] хӯрданд ва шармгоҳашон бар онон намоён шуд ва шурӯъ карданд ба қарор додани барги [дарахтони] биҳишт бар худ [то авраташонро бипӯшонанд]; ва [инчунин буд, ки] Одам аз Парвардигораш сарпечӣ кард ва ба бероҳӣ рафт');
INSERT INTO "pioneers" VALUES(20,122,'20:122','Сипас Парвардигораш ӯро баргузид ва тавбаашро пазируфт ва ҳидояташ кард');
INSERT INTO "pioneers" VALUES(20,123,'20:123','Фармуд: «[Шумо ду тан хамроҳи Иблис] Ҳамагӣ аз биҳишт фуруд оед, дар ҳоле ки душмани якдигаред. Агар аз [ҷониби] ман раҳнамуде бароятон расид, [бидонед] ҳар ки аз ҳидоятам пайравӣ кунад, на гумроҳ мешавад ва на [аз азоби дузах] ба ранҷ меафтад');
INSERT INTO "pioneers" VALUES(20,124,'20:124','Ва касе, ки аз ёди Ман рӯйгардон шавад, яқинан зиндагии сахте хоҳад дошт ва рӯзи қиёмат ӯро нобино бармеангезем');
INSERT INTO "pioneers" VALUES(20,125,'20:125','Вай мегӯяд: «Парвардигоро, чаро маро нобино барангехтӣ; ҳол он ки [дар дунё] бино будам?»');
INSERT INTO "pioneers" VALUES(20,126,'20:126','[Аллоҳ таоло] Мефармояд: «Ҳамон тавр, ки оёти Мо бар ту омад ва онҳоро ба фаромӯшӣ супурдӣ, имрӯз ту [низ] ба ҳамон сурат фаромӯш мешавӣ»');
INSERT INTO "pioneers" VALUES(20,127,'20:127','Ва касеро, ки [дар гумроҳӣ ва гуноҳ] зиёдаравӣ кунад ва ба оёти Парвардигораш имон наоварад, ин гуна кайфар медиҳем; ва ҳатман, азоби охират шадидтар ва пойдортар аст');
INSERT INTO "pioneers" VALUES(20,128,'20:128','Оё барои ҳидояташон кофӣ нест, ки [бидонанд] чи наслҳоеро пеш аз онон нобуд кардем, ки [акнун ин мушрикон] дар хонаҳои эшон [наслҳои гузашта] роҳ мераванд? Ба ростӣ, дар ин [нукта] барои хирадмандон нишонаҳое [ибратангез] аст');
INSERT INTO "pioneers" VALUES(20,129,'20:129','Ва агар аз сӯйи Парвардигорат сухане [дар бораи итмоми ҳуҷҷат бо мардум] нагузашта буд ва замони муайяне дар кор набуд, ҳатман [азоби илоҳӣ дар ҳамин дунё бар онон] лозим мешуд');
INSERT INTO "pioneers" VALUES(20,130,'20:130','Пас [эй Паёмбар,] бар он чӣ мегӯянд, шикебо бош ва Парвардигоратро пеш аз тулуи офтоб [дар намози бомдод] ва пеш аз ғуруб [дар намози аср] ба покӣ ситоиш кун ва соате аз шаб [дар намози хуфтан] ва канораҳои рӯз [дар намози пешин ва шом низ ба ниёиш бипардоз]; бошад, ки [аз подоши илоҳӣ] хушнуд шавӣ');
INSERT INTO "pioneers" VALUES(20,131,'20:131','Ва ҳаргиз ба [неъматҳои дунявӣ ва] он чи ки гурӯҳҳое аз онон [мушрикон]-ро аз онҳо баҳраманд сохтаем, чашм надӯз. Инҳо зинати зиндагии дунёст, то ононро бад-он биозмояд; ва рӯзии Парвардигорат беҳтар ва пойдортар аст');
INSERT INTO "pioneers" VALUES(20,132,'20:132','Ва хонаводаатро ба намоз фармон бидеҳ ва [худ] бар [анҷоми] он шикебо бош [Мо] Аз ту рӯзӣ намехоҳем, [балки] худ ба ту рӯзӣ медиҳем; ва саранҷоми нек барои [аҳли] парҳез аст');
INSERT INTO "pioneers" VALUES(20,133,'20:133','[Мушрикон] гуфтанд: «Чаро [Муҳаммад] муъҷизае аз ҷониби Парвардигораш барои мо намеоварад?» Бигӯ: «Оё [Қуръон ба унвони] далели равшан он чи дар китобҳои пешин аст, барояшон наёмадааст?»');
INSERT INTO "pioneers" VALUES(20,134,'20:134','Агар Мо ононро пеш аз он [ки паёмбар ва китоби осмонӣ биёяд] бо азобе ҳалок мекардем, ҳатман, [рӯзи қиёмат мушрикон] мегуфтанд: «Парвардигоро, чаро паёмбаре ба сӯямон нафиристодӣ, то пеш аз он ки хору расво шавем, аз оёти Ту пайравӣ кунем?»');
INSERT INTO "pioneers" VALUES(20,135,'20:135','[Эй Паёмбар, ба онон] Бигӯ: «Ҳама мунтазиранд; пас, шумо [низ] мунтазир бошед. Ба зудӣ хоҳед донист, ки раҳравони роҳи рост чи касоне ҳастанд ва ҳидоятёфта кист»');
INSERT INTO "pioneers" VALUES(21,1,'21:1','Замони ҳисоби мардум наздик шудааст ва онон дар ноогоҳӣ [аз бозхости охират] рӯй гардонанд');
INSERT INTO "pioneers" VALUES(21,2,'21:2','Ҳеҷ панди тозае аз Парвардигорашон барои онон намеояд, магар он ки бозикунон ба он гӯш медиҳанд');
INSERT INTO "pioneers" VALUES(21,3,'21:3','[Паёми илоҳиро дар ҳоле мешунаванд, ки] дилҳояшон ғофил аст ва касоне, ки [кофир ва] ситамгор буданд, наҷвоҳояшонро пинҳон карданд [ва гуфтанд]: «Оё ин [муддаии паёмбарӣ] ҷуз башаре ҳамчун шумост? Оё дар ҳоле ки [ҳақиқатро] мебинед, ба ҷоду рӯй меоваред?»');
INSERT INTO "pioneers" VALUES(21,4,'21:4','[Паёмбар] гуфт: «Парвардигорам ҳар суханеро, ки дар осмон ва замин бошад медонад ва Ӯ шунавову доност»');
INSERT INTO "pioneers" VALUES(21,5,'21:5','Балки [кофирон дар ситез бо Қуръон] гуфтанд: «[Он чи Муҳаммад мегӯяд] хобҳое ошуфта аст, балки онро ба дурӯғ [ба Аллоҳ таоло] нисбат медиҳад. [На] Балки ӯ шоир аст. Пас, [агар рост мегӯяд] бароямон муъҷизае биёварад, чунон ки паёмбарони пешин [низ бо муъҷизот] фиристода шуданд»');
INSERT INTO "pioneers" VALUES(21,6,'21:6','Пеш аз онон [низ сокинони ҳар] шаҳре, ки нобудаш мекардем, [ба оёт ва муъҷизоти Мо] имон наоварда буданд. Пас, оё [инҳо] имон меоваранд?');
INSERT INTO "pioneers" VALUES(21,7,'21:7','Ва [эй паёмбар] пеш аз ту ҷуз мардоне, ки ба онон ваҳй мекардем [барои ҳидояти мардум] нафиристодем, пас, [эй кофирон] агар намедонед, аз аҳли китоб бипурсед');
INSERT INTO "pioneers" VALUES(21,8,'21:8','Ва [мо] ононро пайкарҳое, ки ғизо нахӯранд, қарор надодем ва [дар дунё низ] ҷовидон набуданд');
INSERT INTO "pioneers" VALUES(21,9,'21:9','Сипас ваъдаеро, ки [дар бораи пирӯзӣ ва наҷот] ба онон дода будем, таҳаққуқ бахшидем ва ононро ба ҳамроҳи ҳар ки хостем, наҷот додем ва исрофкорон [-и кофир]-ро нобуд кардем');
INSERT INTO "pioneers" VALUES(21,10,'21:10','Бе тардид, китобе ба сӯятон нозил кардем, ки иззат [-у шарафи дунё ва охират]-и шумо дар он аст. Оё намеандешед?');
INSERT INTO "pioneers" VALUES(21,11,'21:11','Чи бисёр шаҳрҳоеро, ки [сокинонаш] ситамгор буданд, дар ҳам шикастем ва пас аз онон гурӯҳе дигар падид овардем');
INSERT INTO "pioneers" VALUES(21,12,'21:12','Пас, чун азоби Моро эҳсос мекарданд [ва ба чашм медиданд], ногоҳ аз он ҷо мегурехтанд');
INSERT INTO "pioneers" VALUES(21,13,'21:13','[Ба тамасхур гуфтем] «Фирор накунед. Ба сӯйи хонаҳоятон ва неъматҳое, ки дар он осуда будед, бозгардед. Бошад, ки [фақат дар бораи умури дунявӣ] бозхост шавед»');
INSERT INTO "pioneers" VALUES(21,14,'21:14','гуфтанд: «Вой бар мо [, ки] ҳатман ситамгор будаем!»');
INSERT INTO "pioneers" VALUES(21,15,'21:15','Пас, гуфторашон пайваста ҳамин буд, то он ки ононро [чун гиёҳе] даравшуда [ва] беҷон сохтем');
INSERT INTO "pioneers" VALUES(21,16,'21:16','Ва Мо осмону замин ва он чиро, ки миёни онҳост ба бозича наёфаридаем');
INSERT INTO "pioneers" VALUES(21,17,'21:17','Агар [ба фарз] мехостем, ки саргармие [монанди фарзанд ё ҳамсуҳбат барои худ] баргузинем, онро [на аз бутҳои шумо, балки] аз назди хеш бармегузидем');
INSERT INTO "pioneers" VALUES(21,18,'21:18','Балки Мо ҳақро бар ботил меафканем, пас, онро дар ҳам мешиканад ва ногоҳ он [ботил] нобуд мешавад ва [эй кофирон] вой бар шумо аз он чи тавсиф мекунед!');
INSERT INTO "pioneers" VALUES(21,19,'21:19','Ҳар ки [ва ҳар чи] дар осмонҳо ва замин аст аз они Ӯст ва онон, ки назди Ӯ ҳастанд [фариштагон] аз ибодаташ саркашӣ намекунанд ва хаста намешаванд');
INSERT INTO "pioneers" VALUES(21,20,'21:20','Онон шабу рӯз [илоҳро] ба покӣ меситоянд ва сустӣ намеварзанд');
INSERT INTO "pioneers" VALUES(21,21,'21:21','Оё [ин мушрикон] маъбудоне аз замин баргузидаанд, ки [мурдагонро] зинда мекунанд?');
INSERT INTO "pioneers" VALUES(21,22,'21:22','Агар дар осмон ва замин маъбудоне ба ҷуз Аллоҳ таоло вуҷуд дошт, [дар асари носозгории онҳо] ҳатман, ҳар ду табоҳ мешуданд. Пас, Аллоҳ таоло – Парвардигори Арш – аз тавсифе, ки [мушрикон] мекунанд, поку муназзаҳ аст');
INSERT INTO "pioneers" VALUES(21,23,'21:23','Ӯ ба хотири он чи мекунад, бозхост намешавад, вале онон [дар баробари рафторашон] масъуланд');
INSERT INTO "pioneers" VALUES(21,24,'21:24','Оё [мушрикон] ба ҷуз Ӯ маъбудоне баргузидаанд? [Ба онон] Бигӯ: «Далелатонро биёваред. Ин [Қуръон] китобест, ки ҳамроҳи ман аст ва [инҳо низ] китобҳоест [мутааллиқ ба паёмбароне], ки пеш аз ман буданд [ва дар ҳеҷ кадом далеле бар ҳаққонияти шумо вуҷуд надорад]», балки бештарашон ҳақро намедонанд ва [аз он] рӯйгардонанд');
INSERT INTO "pioneers" VALUES(21,25,'21:25','Ва Мо пеш аз ту ҳеҷ паёмбареро нафиристодем, магар он ки ба ӯ ваҳй кардем, ки [ба мардум бигӯяд]: «Маъбуде [ба ҳақ] ҷуз Ман нест, пас, танҳо Маро бипарастед»');
INSERT INTO "pioneers" VALUES(21,26,'21:26','[Мушрикон бо ин тасаввур, ки фариштагон духтарони Аллоҳ таоло ҳастанд] гуфтанд: «[Аллоҳи] Раҳмон фарзанде [барои хеш] баргузидааст». Ӯ муназзаҳ аст [чунин нест, ки мепиндоранд], балки фариштагон бандагони гиромиаш ҳастанд');
INSERT INTO "pioneers" VALUES(21,27,'21:27','Онон ҳаргиз дар сухан бар Ӯ пешӣ намегузинад ва [пайваста] ба фармонаш амал мекунанд');
INSERT INTO "pioneers" VALUES(21,28,'21:28','[Аллоҳ таоло] Гузашта ва ояндаи ононро медонад ва эшон ҷуз барои касе, ки Ӯ писандад, шафоат намекунанд ва худ аз бими Ӯ ҳаросонанд');
INSERT INTO "pioneers" VALUES(21,29,'21:29','Ва ҳар касе аз онон бигӯяд: «Ба ҷуз ӯ ман [низ] маъбуд ҳастам», пас, вайро ба дузах кайфар медиҳем. [Оре] Ситамгоронро чунин кайфар медиҳем');
INSERT INTO "pioneers" VALUES(21,30,'21:30','Оё касоне, ки куфр варзиданд, надиданд, ки [дарҳои неъмати] осмонҳо ва замин баста буд [на бороне меборид ва на гиёҳе мерӯид], пас, он дуро боз кардем ва ҳар чизи зиндаеро аз об падид овардем? Пас, оё имон намеоваранд?');
INSERT INTO "pioneers" VALUES(21,31,'21:31','Ва дар замин кӯҳҳое устувор ниҳодем, то мабодо ононро биларзонад ва дар он роҳҳое [васеъ] падид овардем, бошад, ки [дар сафарҳояшон ба мақсад] роҳ ёбанд');
INSERT INTO "pioneers" VALUES(21,32,'21:32','Ва осмонро сақфе маҳфуз қарор додем ва онон [мушрикон] аз [тафаккур дар] нишонаҳояш рӯйгардонанд');
INSERT INTO "pioneers" VALUES(21,33,'21:33','Ӯст, ки шабу рӯз ва хуршеду моҳро офарид [ва чунон муназзам сохт, ки] ҳар як [аз инҳо] дар мадоре [муайян] шиноваранд');
INSERT INTO "pioneers" VALUES(21,34,'21:34','[Эй паёмбар] Мо пеш аз ту барои ҳеҷ башаре ҷовидонагӣ қарор надодем. Оё агар ту бимирӣ, онон ҷовидонанд?');
INSERT INTO "pioneers" VALUES(21,35,'21:35','Ҳар касе чашандаи [таъми] марг аст ва [эй мардум] шуморо бо бадӣ ва некӣ меозмоем ва [саранҷом] ба сӯйи Мо бозгарданда мешавед');
INSERT INTO "pioneers" VALUES(21,36,'21:36','Касоне, ки куфр варзиданд, ҳар гоҳ ки туро мебинанд, фақат ба масхараат мегиранд [ва мегӯянд]: «Оё ин ҳамон касест, ки аз маъбудонатон [ба бадӣ] ёд мекунад»? Ва ҳол он ки онон худ ёди [Аллоҳи] Раҳмонро инкор мекунанд');
INSERT INTO "pioneers" VALUES(21,37,'21:37','Инсон [зотан] шитобкор офарида шудааст [ва ҳама чизро пеш аз замони муқаррараш мехоҳад]. Ба зудӣ нишонаҳо [-и азоб]-и хешро нишонатон хоҳем дод, пас, [нузули азобро] ба шитоб нахоҳед');
INSERT INTO "pioneers" VALUES(21,38,'21:38','[Кофирон] мегӯянд: «Агар ростгӯед, ин ваъда [қиёмат] кай хоҳад буд?»');
INSERT INTO "pioneers" VALUES(21,39,'21:39','Касоне, ки куфр варзиданд, агар медонистанд замоне, ки [ба дузах меафтанд] наметавонанд [шуълаҳои] оташро аз чеҳраҳо ва пуштҳояшон боздоранд ва [аз сӯйи ҳеҷ кас] ёрӣ намешаванд [ҳаргиз ин гуна шитобон азобро намехостанд]');
INSERT INTO "pioneers" VALUES(21,40,'21:40','Балки [оташ чунон] ногаҳонӣ ба онон мерасад ва эшонро [чунон саргашта ва] мабҳут мекунад, ки на метавонанд онро [аз худ] дафъ кунанд ва на муҳлати [тавба] ба онон дода мешавад');
INSERT INTO "pioneers" VALUES(21,41,'21:41','Яқинан, паёмбарон пеш аз ту [низ] мавриди тамасхур қарор гирифта буданд, пас, бар сари касоне, ки онҳо [азобҳои илоҳӣ]-ро ба ришханд мегирифтанд, ҳамон чизе омад, ки масхарааш мекарданд');
INSERT INTO "pioneers" VALUES(21,42,'21:42','[Эй паёмбар, ба кофирон] Бигӯ: «Чи касе шуморо дар шабу рӯз аз [азоби Аллоҳи] Раҳмон нигоҳ медорад? Балки [ҳақ ин аст, ки] онон аз ёди Парвардигорашон рӯйгардонанд');
INSERT INTO "pioneers" VALUES(21,43,'21:43','Оё онон маъбудоне доранд, ки [метавонанд] дар баробари Мо аз эшон дифоъ кунанд? [Ҳаргиз!] Онон на метавонанд худро ёрӣ диҳанд ва на аз [азоби] Мо дар амонанд');
INSERT INTO "pioneers" VALUES(21,44,'21:44','Балки [Мо] онон ва падаронашонро [аз неъматҳои дунявӣ] баҳраманд сохтем, то умрашон ба дарозо кашид [ва мағрур шуданд]. Оё намебинанд, ки Мо [ҳамвора] ба суроғи замин меоем ва онро аз ҳар тараф мекоҳем [ва мардумашро нобуд мекунем]? Пас, оё онон [бар қудрати Аллоҳ таоло] пирӯзанд?');
INSERT INTO "pioneers" VALUES(21,45,'21:45','Бигӯ: “Ман фақат шуморо ба василаи ваҳй [аз азоби илоҳӣ] бим медиҳам” ва [-ле] чун ба карон бим дода шавад, даъвати [ҳақ]-ро намешунаванд');
INSERT INTO "pioneers" VALUES(21,46,'21:46','Агар андаке аз азоби Парвардигорат ба онон бирасад, ҳатман, мегӯянд: “Эй вой бар мо [ки], яқинан, ситамгор будаем!”');
INSERT INTO "pioneers" VALUES(21,47,'21:47','Рӯзи қиёмат Мо тарозуҳои адлро [барои ҳисобрасӣ дар миён] мениҳем, пас, ба ҳеҷ кас [камтарин] ситаме намешавад ва агар [кирдорашон] ҳамсанги донаи хардале бошад, онро [ба ҳисоб] меоварем ва кофист, ки Мо ҳисобрас бошем');
INSERT INTO "pioneers" VALUES(21,48,'21:48','Ба ростӣ, Мо ба Мӯсо ва Ҳорун китоб [Таврот] додем ва [ин китоб] барои парҳезкорон равшанӣ ва андарзе аст');
INSERT INTO "pioneers" VALUES(21,49,'21:49','[Ҳамон] касоне, ки дар ниҳон аз Парвардигорашон метарсанд ва аз қиёмат бимноканд');
INSERT INTO "pioneers" VALUES(21,50,'21:50','Ва ин [Қуръон] панди мубораке аст, ки онро нозил кардем. Оё [боз ҳам] инкораш мекунед?');
INSERT INTO "pioneers" VALUES(21,51,'21:51','Ва бе тардид, пеш аз он Мо ба Иброҳим [рушди фикрӣ ва] ҳидояташро ато кардем ва ба [шоистагиҳои] ӯ доно будем;');
INSERT INTO "pioneers" VALUES(21,52,'21:52','Он гоҳ ки ба падари худ ва қавмаш гуфт: “Ин муҷассамаҳо чист, ки ба парастиши онҳо истодаед?”');
INSERT INTO "pioneers" VALUES(21,53,'21:53','Онон гуфтанд: “Мо падаронамонро дидем, ки онҳоро мепарастиданд”');
INSERT INTO "pioneers" VALUES(21,54,'21:54','[Иброҳим] Гуфт: “Ҳатман, шумо ва падаронатон дар гумроҳии ошкор будаед”');
INSERT INTO "pioneers" VALUES(21,55,'21:55','Онон гуфтанд: “Оё [сухани] ҳақ бароямон овардаӣ ё шухӣ мекунӣ?”');
INSERT INTO "pioneers" VALUES(21,56,'21:56','Ӯ гуфт: “Балки [суханам ҳақ аст] Парвардигоратон парвардигори осмонҳо ва замин аст, ҳамон ки онҳоро падид овардааст ва ман бар ин [сухан] гувоҳам”');
INSERT INTO "pioneers" VALUES(21,57,'21:57','Ва [бо худ гуфт] “Ба Аллоҳ таоло савганд, пас аз он ки пушт кардед ва [ба ҷашн] рафтед, барои [нобудии] бутҳоятон тадбире меандешам”');
INSERT INTO "pioneers" VALUES(21,58,'21:58','Пас [дар ғиёби бутпарастон] ҳамаи онҳоро ба ҷуз [бути] бузургашон бишкаст, шояд, ба суроғи он бираванд [то ҳақоиқро барояшон бозгӯ кунад]');
INSERT INTO "pioneers" VALUES(21,59,'21:59','[Вақте онон бутхонаро чунин диданд] Гуфтанд: “Чи касе бо маъбудони мо чунин кардааст? Ҳатман, ӯ ситамгор аст”');
INSERT INTO "pioneers" VALUES(21,60,'21:60','[Бархе] Гуфтанд: “Мо шунидем ҷавоне, ки ӯро Иброҳим мегӯянд, аз онҳо [ба бадӣ] ёд мекунад”');
INSERT INTO "pioneers" VALUES(21,61,'21:61','Гуфтанд: “Пас ӯро дар баробари дидагони мардум биёваред, бошад, ки [бар эътирофаш ба гуноҳ] гувоҳ бошанд”');
INSERT INTO "pioneers" VALUES(21,62,'21:62','[Вақте Иброҳимро оварданд] Гуфтанд: “Эй Иброҳим, оё ту ин [кор] -ро бо маъбудони мо кардаӣ?”');
INSERT INTO "pioneers" VALUES(21,63,'21:63','Ӯ гуфт: “Балки [бути] бузургашон чунин кардааст. Агар сухан мегӯянд, аз онҳо бипурсед»');
INSERT INTO "pioneers" VALUES(21,64,'21:64','Он гоҳ [онон] ба худ омаданд ва [ба якдигар] гуфтанд: “Воқеан, шумо [мушрику] ситамгоред”');
INSERT INTO "pioneers" VALUES(21,65,'21:65','Сипас [боз ҳам] сар ба душманӣ ва инкор бардоштанд [ва гуфтанд] “Ту хуб медонӣ, ки инҳо сухан намегӯянд”');
INSERT INTO "pioneers" VALUES(21,66,'21:66','[Иброҳим] гуфт: “Оё ба ҷойи Аллоҳ таоло чизҳоеро мепарастед, ки на суде бароятон дорад ва на зиёне ба шумо мерасонад?');
INSERT INTO "pioneers" VALUES(21,67,'21:67','Вой бар шумо ва бар он чи ба ҷойи Аллоҳ таоло мепарастед! Оё намеандешед?”');
INSERT INTO "pioneers" VALUES(21,68,'21:68','Онон гуфтанд: “Агар мехоҳед [барои муҷозоташ] коре кунед, ӯро бисӯзонед ва маъбудонатонро ёрӣ диҳед”');
INSERT INTO "pioneers" VALUES(21,69,'21:69','[Саранҷом ӯро дар оташ андохтанд, вале Мо] Гуфтем: “Эй оташ, бар Иброҳим сард ва саломат бош”');
INSERT INTO "pioneers" VALUES(21,70,'21:70','Ва хостанд барои [нобудии ӯ найранге ба кор баранд, вале Мо ононро зиёнкортарин [мардум] сохтем');
INSERT INTO "pioneers" VALUES(21,71,'21:71','Ва ӯ ва Лутро [бо ҳиҷрат] ба сарзамине, ки барои ҷаҳониён дар он баракат ниҳодаем [Шом], наҷот додем');
INSERT INTO "pioneers" VALUES(21,72,'21:72','Ва Исҳоқ ва [навааш] Яъқубро [низ ба унвони бахшиш] афзунӣ ба ӯ бахшидем ва ҳамагиашонро [афроде] шоиста гардонидем');
INSERT INTO "pioneers" VALUES(21,73,'21:73','Ва ононро пешвоёне қарор додем, ки ба фармони Мо [мардумро] ҳидоят мекарданд ва анҷоми корҳои нек ва барпо доштани намоз ва адои закотро ба эшон ваҳй кардем ва онон [ҳама] парастишгари Мо буданд');
INSERT INTO "pioneers" VALUES(21,74,'21:74','Мо ба Лут [иҷозати] қазоват ва илм [-и дин] додем ва ӯро аз шаҳре, ки [сокинонаш] аъмоли зишт [-у палид] анҷом медоданд, наҷот бахшидем. Ба ростӣ, ки онон гурӯҳе палид ва нофармон буданд');
INSERT INTO "pioneers" VALUES(21,75,'21:75','Ва ӯро дар раҳмати худ ворид кардем. Бе гумон, ӯ аз шоистагон буд');
INSERT INTO "pioneers" VALUES(21,76,'21:76','Ва Нуҳро [ёд кун] ҳангоме ки пештар [аз соири паёмбарон моро] нидо дод. Пас, Мо [низ дуои] вайро иҷобат кардем, он гоҳ ӯ ва хонадонашро аз андуҳи бузург наҷот додем');
INSERT INTO "pioneers" VALUES(21,77,'21:77','Ва дар баробари қавме, ки оёти Моро дурӯғ ангошта буданд, ёриаш кардем. Бе тардид, онон гурӯҳи бадкирдоре буданд, пас, ҳамагиашонро ғарқ кардем');
INSERT INTO "pioneers" VALUES(21,78,'21:78','Ва [ёд кун аз] Довуд ва Сулаймон он гоҳ ки дар бораи киштзоре доварӣ мекарданд, ки гӯсфандони қавм шабона дар он чарида буданд ва Мо бар доварии онон гувоҳ будем');
INSERT INTO "pioneers" VALUES(21,79,'21:79','Пас, он [ҳақиқат]-ро ба Сулаймон фаҳмонидем ва ба ҳар як аз онон паёмбарӣ ва дониш [-и амал ба аҳкоми дин] ато кардем ва кӯҳҳо ва парандагон дар хидмати Довуд гумоштем, ки [ҳамроҳ бо ӯ Парвардигорро] тасбеҳ мегуфтанд ва Мо будем, ки ин [ҳама]-ро анҷом додем');
INSERT INTO "pioneers" VALUES(21,80,'21:80','Ва ба ӯ сохтани зиреҳро омӯхтем, то шуморо аз [осеби] ҷангҳоятон ҳифз кунад. Пас, оё шукр мегузоред?');
INSERT INTO "pioneers" VALUES(21,81,'21:81','Ва тундбодро барои Сулаймон [ба хидмат гумоштем], ки ба фармони ӯ ба сӯйи сарзамине, ки дар он баракат ниҳода будем [Шом], равон буд ва мо бар ҳар чиз доно будем');
INSERT INTO "pioneers" VALUES(21,82,'21:82','Ва [низ] аз шаётин касонеро [гумоштем], ки барояш ғаввосӣ мекарданд [ва ҷавоҳир меоварданд] ва корҳое ғайр аз ин [низ] анҷом медоданд ва мо муроқиби [рафтор ва теъдоди] онон будем');
INSERT INTO "pioneers" VALUES(21,83,'21:83','Ва Аюбро [ёд кун] он гоҳ ки Парвардигорашро [чунин нидо дод: “Ранҷ [-у беморӣ] ба ман расидааст ва Ту меҳрубонтарини меҳрубононӣ”');
INSERT INTO "pioneers" VALUES(21,84,'21:84','Пас, Мо [дуои] ӯро иҷобат кардем ва ранҷеро, ки ба ӯ расида буд, бартараф сохтем ва хонаводааш ва [низ теъдоде] ҳаммонандашонро ба ҳамроҳи онон ба вай бозгардонидем, [то] раҳмате аз ҷониби Мо ва панде барои парастишгарон бошад');
INSERT INTO "pioneers" VALUES(21,85,'21:85','Ва [ёд кун аз] Исмоил ва Идрис ва Зу-л-кифл, ки ҳамагӣ аз шикебоён буданд');
INSERT INTO "pioneers" VALUES(21,86,'21:86','Ва [Мо] ононро дар раҳмати хеш ворид кардем. Бе тардид, онон аз шоистагон буданд');
INSERT INTO "pioneers" VALUES(21,87,'21:87','Ва [ёд кун аз] Зу-н-нун [Юнус] он гоҳ ки хашмгин [аз миёни қавмаш] рафт ва чунин пиндошт, ки Мо ҳаргиз бар ӯ танг намегирем [ва ба хотири ин кор муҷозоташ намекунем]. Пас, [вақте моҳӣ ӯро фурӯ бурд] дар торикиҳо нидо дод, ки: “[Парвардигоро] Ҳеҷ маъбуде [ба ҳақ] ҷуз ту нест. Ту муназзаҳӣ. Бе тардид, ман аз ситамгорон будаам”');
INSERT INTO "pioneers" VALUES(21,88,'21:88','Пас дуояшро иҷобат кардем ва аз андуҳ наҷоташ додем ва муъминонро [низ] ин чунин наҷот медиҳем');
INSERT INTO "pioneers" VALUES(21,89,'21:89','Ва [ёд кун аз] Закариё, он гоҳ ки Парвардигорашро [ин гуна] нидо дод: “Парвардигоро, маро танҳо [ва бефарзанд] магзор ва ту беҳтарин бозмондаӣ [ворисонӣ]”');
INSERT INTO "pioneers" VALUES(21,90,'21:90','Пас, дуояшро иҷобат кардем ва Яҳёро ба ӯ бахшидем ва ҳамсарашро [ки нозо буд] барояш шоиста [ва омодаи бордорӣ] намудем. Онон ҳамвора дар корҳои хайр мешитофтанд ва дар ҳоли биму умед Моро мехонданд ва пайваста барои Мо [фармонбардор ва] фурӯтан буданд');
INSERT INTO "pioneers" VALUES(21,91,'21:91','Ва [ёд кун аз Марям] зане, ки домани худро пок нигоҳ дошт ва [Мо] аз рӯҳи хеш дар [вуҷуди] ӯ дамидем ва ӯ ва писарашро барои ҷаҳониён нишонае [аз қудрати илоҳӣ] қарор додем');
INSERT INTO "pioneers" VALUES(21,92,'21:92','[Эй мардум] Ба ростӣ, ин оини шумо оини ягона [ва асосёфта бар яктопарастӣ] аст ва Ман Парвардигоратон ҳастам, пас, Маро бипарастед');
INSERT INTO "pioneers" VALUES(21,93,'21:93','Ва [мардум] миёни худ дар амри [дин]-ашон фирқа-фирқа шуданд; [вале саранҷом] ҳамагӣ ба сӯи Мо бозмегарданд');
INSERT INTO "pioneers" VALUES(21,94,'21:94','Пас, ҳар ки корҳои шоиста анҷом диҳад ва муъмин бошад, дар баробари кӯшиши ӯ носипосӣ нахоҳад шуд ва Мо корҳояшро менависем');
INSERT INTO "pioneers" VALUES(21,95,'21:95','Ва [мардуми] ҳар шаҳреро, ки [бар асари куфру гуноҳ] нобудаш кардем, маҳол аст, ки [битавонанд ба қасди тавба ба дунё] бозгарданд');
INSERT INTO "pioneers" VALUES(21,96,'21:96','То замоне, ки [садди] Яъҷуҷу Маъҷуҷ кушода шавад ва онон аз ҳар баландӣ [ва тепае] шитобон сарозер гарданд');
INSERT INTO "pioneers" VALUES(21,97,'21:97','Ва ваъдаи ҳақ [қиёмат] наздик гардад. Пас, дар он ҳангом чашмҳои касоне, ки куфр варзиданд, [аз шиддати ваҳшат] хира мемонад [ва мегӯянд]: “Эй вой бар мо! Ҳатман, мо аз ин [ҳодиса] дар ғафлат будем! Балки [кофиру] ситамгор будем”');
INSERT INTO "pioneers" VALUES(21,98,'21:98','[Ба онон гуфта мешавад] “Ҳатман, шумо ва он чи ки ба ҷойи Аллоҳ таоло мепарастед, ҳезуми дузахед, [ва ҳамагӣ] дар он ворид хоҳед шуд”');
INSERT INTO "pioneers" VALUES(21,99,'21:99','Агар ин бутҳо маъбудоне [ба ҳақ] буданд, ҳаргиз вориди он намешуданд ва ҳол он ки ҳамагӣ дар он [азоби сӯзон] ҷовидонанд');
INSERT INTO "pioneers" VALUES(21,100,'21:100','Дар он ҷо нолаҳое [дарднок] доранд ва [аз шиддати дарду тарс] чизе намешунаванд');
INSERT INTO "pioneers" VALUES(21,101,'21:101','Касоне, ки пештар аз ҷониби Мо ба эшон ваъдаи наку [биҳишт] дода шудааст, яқинан, аз он [азоб] дур нигаҳ дошта мешаванд');
INSERT INTO "pioneers" VALUES(21,102,'21:102','Онон садои он [оташи ҳавлнок ва фарёдҳои дузахиён]-ро намешунаванд ва дар он чи майл дошта бошанд, ҷовидона [дар осоиш] ҳастанд');
INSERT INTO "pioneers" VALUES(21,103,'21:103','Ваҳшати бузурги [қиёмат] андуҳгинашон намесозад ва фариштагон ба истиқболашон меоянд [ва мегӯянд]: «Ин ҳамон рӯзест, ки ба шумо ваъда дода мешуд»');
INSERT INTO "pioneers" VALUES(21,104,'21:104','Рӯзе, ки осмонро чун туморе навишташуда дар ҳам мепечем, [ва] ҳамон гуна ки нахустин [бор] офаринишро оғоз кардем, [бори дигар] онро бозмегардонем. [Ин] Ваъдае аст бар уҳдаи Мо, ки ҳатман, анҷомаш хоҳем дод');
INSERT INTO "pioneers" VALUES(21,105,'21:105','Ва ба ростӣ, пас аз Таврот дар Забур навиштем, ки: «Заминро бандагони шоистаам ба ирс мебаранд»');
INSERT INTO "pioneers" VALUES(21,106,'21:106','Бе гумон, дар ин [сухан ибрату] паёми равшане барои парастишгарон аст');
INSERT INTO "pioneers" VALUES(21,107,'21:107','Ва [эй Муҳаммад] туро ҷуз раҳмате барои ҷаҳониён нафиристодем');
INSERT INTO "pioneers" VALUES(21,108,'21:108','Бигӯ: «Дар ҳақиқат, ба ман ваҳй мешавад, ки: «Бе тардид, маъбуди шумо маъбуде ягона аст. Пас, оё таслим [-и даъвати ҳақ] мешавед?»');
INSERT INTO "pioneers" VALUES(21,109,'21:109','Пас, агар рӯйгардон шуданд, бигӯ: «Ман [ҳамаи] шуморо ба таври яксон огоҳ кардам [ва дар бораи азоби илоҳӣ ҳушдор додам] ва намедонам, он чи ки ба шумо ваъда дода мешавад, наздик аст ё дур»');
INSERT INTO "pioneers" VALUES(21,110,'21:110','Яқинан, ӯ сухани ошкоро медонад ва он чиро [дар дили хеш] пинҳон мекунед, [низ] медонад');
INSERT INTO "pioneers" VALUES(21,111,'21:111','Ва намедонам, шояд ин [таъхири азоб мояи] озмоиш ва баҳрамандии шумо то муддате [муайян] бошад»');
INSERT INTO "pioneers" VALUES(21,112,'21:112','[Паёмбар] Гуфт: «Парвардигоро, [байни мо ва кофирон] ба ҳақ доварӣ кун ва Парвардигори мо [Аллоҳи] Раҳмон аст, ки дар баробари он чи [аз куфру ширк] мегӯед, аз ӯ ёрӣ мехоҳем»');
INSERT INTO "pioneers" VALUES(22,1,'22:1','Эй мардум, аз Парвардигоратон парво кунед. Бе тардид, зилзилаи қиёмат амри бузурге аст');
INSERT INTO "pioneers" VALUES(22,2,'22:2','Рӯзе, ки онро мебинед, [он чунон ваҳшатнок аст, ки] ҳар модари ширдеҳе [фарзанди] ширхорашро фаромӯш мекунад ва ҳар [мавҷуди] бордоре ҷанини худро [бар замин] мениҳад ва мардумро маст мебинӣ, дар ҳоле ки маст нестанд, балки азоби Аллоҳ таоло шадид аст');
INSERT INTO "pioneers" VALUES(22,3,'22:3','Ва аз [миёни] мардум касе ҳаст, ки бидуни [ҳеҷ] донише дар бораи Аллоҳ таоло муҷодала мекунад ва аз ҳар шайтони саркаше пайравӣ менамояд');
INSERT INTO "pioneers" VALUES(22,4,'22:4','[Дар қазои илоҳӣ бар шайтон] Муқаррар шудааст, ҳар ки ӯро ба дӯстӣ бигирад, ҳатман, гумроҳаш месозад ва ба азоби оташ мекашонад');
INSERT INTO "pioneers" VALUES(22,5,'22:5','Эй мардум, агар дар бораи барангехта шудан [дар қиёмат] тардид доред, пас, [бидонед, ки] бе тардид, Мо шуморо аз хок офаридем, сипас аз нутфа ва он гоҳ аз хуни басташуда, сипас аз порагӯште шаклёфта ва [баъзан] шаклноёфта, [чунин кардем] то [қудрати хешро] бароятон ошкор созем; ва ҳар чиро ки бихоҳем, то муддате муайян дар раҳим [-и модарон] нигаҳ медорем, он гоҳ шуморо ба сурати навзоде берун меоварем, сипас [парвариш меёбед] то ба ҳадди рушд [ва булуғи] худ бирасед ва аз шумо касе ҳаст, ки [дар даврони ҷавонӣ] мемирад ва касе низ ҳаст [ки он қадар умр мебинанд, то] ба ниҳояти фурӯтанӣ [ва куҳансолӣ] мерасад, чунонки пас аз [он ҳама] дониш [ҳамаро фаромӯш мекунад ва] чизе намедонад. Ва [аммо мисоле дигар] заминро хушкида мебинӣ, вале чун оби [борон] бар он фурӯ мефиристем, ба ҷунбиш дармеояд ва рушд мекунад ва аз ҳар навъ [гиёҳи] зебое мерӯёнад');
INSERT INTO "pioneers" VALUES(22,6,'22:6','Ин [шигифтиҳои хилқат] аз он рӯст, ки [бидонед] Аллоҳ таоло ҳақ аст ва ӯст, ки мурдагонро зинда мекунад ва ӯ бар ҳар коре тавоност');
INSERT INTO "pioneers" VALUES(22,7,'22:7','Ва ин ки қиёмат омаданӣ аст [ва] тардиде дар он нест ва ин ки Аллоҳ таоло [ҳамаи] касонеро, ки дар гӯрҳо ҳастанд, бармеангезад');
INSERT INTO "pioneers" VALUES(22,8,'22:8','Ва аз мардум касе ҳаст, ки бидуни ҳеҷ дониш ва ҳидоят ва китоби равшанибахше дар бораи Аллоҳ таоло муҷодала мекунад');
INSERT INTO "pioneers" VALUES(22,9,'22:9','[Аз рӯйи такаббур] Сари хеш бармегардонад, то [мардумро] аз роҳи Аллоҳ таоло гумроҳ созад. Барои ӯ дар ин дунё расвоӣ ва хорӣ аст ва рӯзи қиёмат [низ] азоби оташи сӯзонро ба ӯ мечашонем');
INSERT INTO "pioneers" VALUES(22,10,'22:10','Ин [кайфар] ба сазои корҳоест, ки аз пеш фиристодааст ва Аллоҳ таоло ҳаргиз нисбат ба бандагон [-и худ] ситамгор нест');
INSERT INTO "pioneers" VALUES(22,11,'22:11','Ва аз [миёни] мардум касе ҳаст, ки Аллоҳро бо тардид мепарастад [ва имонаш заиф аст], пас, агар хайре ба ӯ бирасад, [дилаш] ба он ором мегирад [ва бар имонаш боқӣ мемонад] ва агар балое [барои озмоиш] ба ӯ бирасад, рӯ мегардонад [ва ба куфр бозмегардад]. Ӯ дар дунё ва охират зиён кардааст. Ин ҳамон зиёни ошкор аст');
INSERT INTO "pioneers" VALUES(22,12,'22:12','[Инсони кофир] Ба ҷойи Аллоҳ таоло чизеро мехонад, ки на ба ӯ зиёне мерасонад ва на суде мебахшад. Ин ҳамон гумроҳии дуру дароз аст');
INSERT INTO "pioneers" VALUES(22,13,'22:13','Ӯ касеро мехонад, ки қатъан, зиёнаш наздиктар аз судаш мебошад ва чи бад дӯст ва чи бад ҳамдаме аст!');
INSERT INTO "pioneers" VALUES(22,14,'22:14','Бе тардид, Аллоҳ таоло касонеро, ки имон овардаанд ва корҳое шоиста анҷом додаанд, ба боғҳое [аз биҳишт] ворид мекунад, ки ҷӯйборҳо аз зери [дарахтони] он ҷорӣ аст. Ба ростӣ, Аллоҳ таоло он чиро, ки мехоҳад, анҷом медиҳад');
INSERT INTO "pioneers" VALUES(22,15,'22:15','Касе, ки гумон мекунад, ки Аллоҳ таоло ӯ [паёмбар]-ро дар дунё ва охират ҳаргиз ёрӣ нахоҳад кард [ва акнун ҳимояти Аллоҳро мебинад ва хашмгин аст], пас, бояд ресмоне ба сақф [-и хонааш] биёвезад ва [худро дор бизанад, то роҳи нафас]-ро қатъ кунад [ва то сарҳади марг пеш равад], он гоҳ бингарад, ки оё [ин гуна фанду] найранги ӯ хашмашро аз миён мебарад');
INSERT INTO "pioneers" VALUES(22,16,'22:16','Ва бад-ин гуна Мо ин [Қуръон]-ро [ба сурати] оёте равшан нозил кардем ва Аллоҳ таоло ҳар киро хоҳад, ҳидоят мекунад');
INSERT INTO "pioneers" VALUES(22,17,'22:17','Касоне, ки имон овардаанд ва касоне, ки яҳудӣ шуданд ва собеон [фитратгароён] ва масеҳиён ва зартуштиён ва касоне, ки ширк варзиданд, яқинан, рӯзи қиёмат Аллоҳ таоло миёнашон доварӣ мекунад. Бе гумон, Аллоҳ таоло бар ҳар чизе гувоҳ аст');
INSERT INTO "pioneers" VALUES(22,18,'22:18','Оё надидӣ, ки ҳар ки дар осмонҳо ва ҳар ки дар замин аст ва хуршед ва моҳу ситорагон ва кӯҳҳо ва дарахтон ва ҷунбандагон ва бисёре аз мардум барои Аллоҳ таоло саҷда мекунанд? Ва бисёре низ ҳастанд, ки [дар натиҷаи сарпечӣ аз парастиш фармони] азоб бар онон таҳаққуқ ёфтааст ва ҳар ки Аллоҳ таоло хораш созад, касе гиромиаш намедорад. Бе тардид, Аллоҳ таоло ҳар чи бихоҳад анҷом медиҳад');
INSERT INTO "pioneers" VALUES(22,19,'22:19','Ин ду [гурӯҳи муъмин ва кофир] душманони якдигаранд ва дар бораи Парвардигорашон ситез мекунанд. Пас касоне, ки куфр варзиданд, барояшон ҷомаҳое аз оташ буридаанд [ва] аз болои сарашон [бар онон] оби ҷӯшон мерезанд');
INSERT INTO "pioneers" VALUES(22,20,'22:20','Он чи дар дарунашон ҳаст ва [низ] пӯстҳояшон бо он гудохта мешавад');
INSERT INTO "pioneers" VALUES(22,21,'22:21','Ва барояшон гурзҳои оҳанин [муҳайё] аст');
INSERT INTO "pioneers" VALUES(22,22,'22:22','Ҳар гоҳ бихоҳанд, аз [шиддати] андуҳ аз он ҷо [дузах] хориҷ шаванд, ба он бозгардонда мешаванд ва [ба онон гуфта мешавад]: «Азоби сӯзонро бичашед»');
INSERT INTO "pioneers" VALUES(22,23,'22:23','Бе тардид, Аллоҳ таоло касонеро, ки имон овардаанд ва корҳои шоиста анҷом додаанд, ба боғҳое [аз биҳишт] ворид мекунад, ки аз зери [дарахтони] он ҷӯйборҳо ҷорӣ аст. Дар он ҷо ба дастабандаҳое аз тилло ва марворид ороста мешаванд ва либосашон дар он ҷо абрешим аст');
INSERT INTO "pioneers" VALUES(22,24,'22:24','Ва [дар дунё] ба сӯйи гуфтори пок [тавҳиду такбир] ҳидоят мешаванд ва ба роҳи сутуда [ислом] роҳнамоӣ мекарданд');
INSERT INTO "pioneers" VALUES(22,25,'22:25','Бе гумон, касоне, ки куфр варзиданд ва [монанди моҷарои Ҳудайбия мардумро] аз роҳи Аллоҳ таоло бозмедоранд [азобе дарднок дар пеш доранд] ва Масҷиду-л-ҳаром, ки онро барои мардум [қибла ва ибодатгоҳ] қарор додем, муқиму мусофир дар он яксонанд ва ҳар ки дар он ҷо бо ситамгорӣ [ва гуноҳ] инҳироф бихоҳад, аз азобе дарднок ба ӯ мечашонем');
INSERT INTO "pioneers" VALUES(22,26,'22:26','Ва [ёд кун аз] он гоҳ ки маҳалли хона [-и Каъба]-ро барои Иброҳим таъйин кардем [ва гуфтем], ки: «Чизеро шарики Ман қарор надеҳ ва хонаамро барои тавофкунандагон ва қиёмкунандагон ва рукуъкунандагон [ва] cаҷдакунандагон пок гардон');
INSERT INTO "pioneers" VALUES(22,27,'22:27','Ва байни мардум барои [адои] ҳаҷ нидо бидеҳ, то пиёда ва [савор] бар ҳар [василаи сафар ё] шутури лоғаре аз ҳар роҳи дуре ба сӯят биёянд');
INSERT INTO "pioneers" VALUES(22,28,'22:28','То шоҳиди манофеи [гуногуни] хеш бошанд ва дар рӯзҳое муайян [ба ҳангоми қурбонӣ] номи Аллоҳро бар чаҳорпоёне, ки ба онон рӯзӣ додаем, ёд кунанд. Пас, аз [гӯшти] он бихӯред ва ба дармондаи тиҳидаст [низ] ғизо бидиҳед');
INSERT INTO "pioneers" VALUES(22,29,'22:29','Сипас бояд олудагиҳояшонро бартараф созанд ва ба назрҳои хеш вафо кунанд ва [гирдогирди] ин хонаи куҳан [Каъба] тавоф кунанд');
INSERT INTO "pioneers" VALUES(22,30,'22:30','[Аҳком ва маносики ҳаҷ] Ин аст ва ҳар ки [муҳаррамот ва] муқаррароти илоҳиро бузург дорад, ин [кор] назди Парвардигораш барои ӯ беҳтар аст ва [эй мардум] чаҳорпоёне барои [масрафи] шумо ҳалол шудааст, магар он чи [ҳукмаш] бар шумо хонда мешавад. Пас, аз бутҳои палид дурӣ кунед ва аз гуфтори ботил [ва дурӯғ] иҷтиноб намоед');
INSERT INTO "pioneers" VALUES(22,31,'22:31','Дар ҳоле ки [бандаи мухлиси Аллоҳ таоло ва] ҳақгаро бошед ва ба Ӯ ширк наварзед ва ҳар ки ба Аллоҳ таоло ширк варзад, чунон аст, ки гӯӣ аз осмон фурӯ афтад ва мурғони [шикорӣ] вайро бирабоянд ва бод ӯро ба ҷойе дур бияндозад');
INSERT INTO "pioneers" VALUES(22,32,'22:32','[Ҳукми ҳаҷ] Ин аст ва ҳар ки дастурҳои динӣ ва илоҳиро бузург бишморад, яқинан, ин [бузургдошт нишонае] аз парҳезкории дилҳост');
INSERT INTO "pioneers" VALUES(22,33,'22:33','Дар он [чаҳорпоёни вижаи қурбонӣ] то замони муайян [ҳангоми забҳ] барои шумо манофеъ [ва баҳраҳое] аст. Пас, қурбонгоҳи онҳо хонаи куҳани [Каъба] аст');
INSERT INTO "pioneers" VALUES(22,34,'22:34','Ва барои ҳар уммате оине [дар қурбонӣ кардан] қарор додем, то номи Аллоҳро [вақти забҳ] бар чаҳорпоёне, ки рӯзиашон кардааст, зикр кунанд. Пас, маъбуди шумо маъбуди ягона аст. [Ҳама] Дар баробари Ӯ таслим бошед ва [эй паёмбар, хайри дунё ва охиратро] ба фурӯтанон башорат бидеҳ');
INSERT INTO "pioneers" VALUES(22,35,'22:35','[Ҳамон] Касоне, ки чун номи Аллоҳ таоло бурда шавад, дилҳояшон метарсад ва [касоне, ки] дар баробари мусибатҳое, ки ба эшон мерасад, шикебо ҳастанд ва онон, ки намозгузоранд ва аз он чи рӯзишон кардаем, инфоқ мекунанд');
INSERT INTO "pioneers" VALUES(22,36,'22:36','[Қурбонӣ кардани] шутур [ва гов дар мавсими ҳаҷ]-ро барои шумо аз шаоири дин [ва нишонаҳои ибодати Аллоҳ таоло] қарор додем. Дар ин [кор] бароятон хайр [-у баракат] аст. Пас, [ҳангоми қурбонӣ] дар ҳоле ки [шутурон] бар по истодаанд, номи Аллоҳро бар онҳо бибаред [ва наҳрашон кунед]. Он гоҳ чун бар хок афтоданд, аз [гӯшти] онҳо бихӯред ва тиҳидастони обрӯманд ва гадоёнро низ таом диҳед. Онҳо [чаҳорпоён]-ро инчунин ба хидмати шумо гумоштем, бошад, ки сипос гузоред');
INSERT INTO "pioneers" VALUES(22,37,'22:37','Гӯшту хуни ин қурбониҳо ҳаргиз ба Аллоҳ таоло намерасад, балки парҳезкории шумост, ки ба Ӯ мерасад. Ӯ онҳоро инчунин ба хидмати шумо гумошт, то Аллоҳро ба [шукронаи] он ки шуморо ҳидоят кардааст, ба бузургӣ ёд кунед ва накукоронро [ба хайру растагорӣ] башорат бидеҳ');
INSERT INTO "pioneers" VALUES(22,38,'22:38','Аллоҳ таоло аз касоне, ки имон овардаанд, ҳатман, [дар баробари душманонашон] дифоъ мекунад. Бе тардид, Аллоҳ таоло ҳеҷ хиёнаткори носипосеро дӯст намедорад');
INSERT INTO "pioneers" VALUES(22,39,'22:39','Ба касоне, ки [мушрикон] бо онон ҷангидаанд, иҷозат [-и ҷиҳод] дода шудааст, чаро ки ситам дидаанд ва яқинан, Аллоҳ таоло бар ёриашон тавоност');
INSERT INTO "pioneers" VALUES(22,40,'22:40','[Ҳамон] Касоне, ки ба ноҳақ аз диёрашон ронда шуданд, [ва гуноҳе надоштанд] ҷуз ин ки мегуфтанд: «Парвардигори мо Аллоҳ таоло аст». Ва агар Аллоҳ таоло [зулму куфри] бархе аз мардумро ба василаи бархе дигар дафъ намекард, савмааҳо[-и роҳибон] ва маобид [-и масеҳиён] ва ибодатгоҳҳо [-и яҳудиён] ва масоҷиде, ки номи Аллоҳ таоло дар онон бисёр бурда мешавад, ҳатман, вайрон мегардид. Ва Аллоҳ таоло касеро, ки [дини] Ӯро ёрӣ диҳад, яқинан, ёрӣ мекунад. Бе тардид, Аллоҳ таоло қудратманди шикастнопазир аст');
INSERT INTO "pioneers" VALUES(22,41,'22:41','[Ҳамон] Касоне, ки ҳар гоҳ дар замин ба онон қудрат [ва ҳукумат] бибахшем, намоз барпо медоранд ва закот мепардозанд ва [мардумро] ба некӣ фармон медиҳанд ва аз нописанд бозмедоранд. Ва саранҷоми корҳо аз они Аллоҳ таоло аст');
INSERT INTO "pioneers" VALUES(22,42,'22:42','[Эй паёмбар] Агар [ин қавми бутпараст] туро дурӯғгӯ меангоранд, [шикебо бош] ҳатман, пеш аз онон қавми Нуҳ ва Од ва Самуд [низ паёмбаронашонро] дурӯғгӯ меангоштанд');
INSERT INTO "pioneers" VALUES(22,43,'22:43','Ва [ҳамчунин] қавми Иброҳим ва қавми Лут');
INSERT INTO "pioneers" VALUES(22,44,'22:44','Ва асҳоби Мадян [Шуайбро дурӯғгӯ медонистанд] ва Мӯсо [низ тавассути фиръавниён] дурӯғгӯ хонда шуд. Пас, ба кофирон муҳлат додам, он гоҳ ононро [ба азоб] фурӯ гирифтам. Пас, [бингар, ки натиҷаи] инкори [оёт ва муҷозоти] Ман чи гуна бувад');
INSERT INTO "pioneers" VALUES(22,45,'22:45','Чи бисёр шаҳрҳоеро, ки [чун мардумаш кофиру] ситамгор буданд, нобудашон кардем ва [инак] сақфҳояш фурӯ рехтааст ва чи бисёр чоҳ [-ҳо]-и матрук ва қаср [-ҳо]-и устувор [ва бошукӯҳ, ки сокинонашро аз азоби илоҳӣ наҷот надод]');
INSERT INTO "pioneers" VALUES(22,46,'22:46','Оё онон дар замин гардиш накардаанд, то дилҳое дошта бошанд, ки [ҳақиқатро] бо он дарёбанд ё гӯшҳое, ки [ахбори гузаштагонро] бо он бишнаванд? Ба ростӣ, танҳо чашмҳо нест, ки нобино мегардад, балки дилҳое, ки дар синаҳост [бебасират гашта ва барои дидани ҳақ] нобино мешавад');
INSERT INTO "pioneers" VALUES(22,47,'22:47','[Эй Паёмбар, кофирон] ба шитоб аз ту дархости азоб мекунанд ва Аллоҳ таоло ҳаргиз ваъдаашро хилоф намекунад ва дар воқеъ, як рӯз [аз азоби охират] назди Парвардигорат бо ҳазор сол, ки шумо мешуморед, яксон аст');
INSERT INTO "pioneers" VALUES(22,48,'22:48','Ва чи бисёр [аҳолии] шаҳрҳое, ки ба онон муҳлат додам, дар ҳоле ки ситамгор буданд [аммо боз ҳам сарпечӣ карданд], пас, ононро [ба азоб] фурӯ гирифтам ва бозгашт [-и ҳама] ба сӯи Ман аст');
INSERT INTO "pioneers" VALUES(22,49,'22:49','Бигӯ: «Эй мардум, ҷуз ин нест, ки ман барои шумо бимдиҳандае ошкорам»');
INSERT INTO "pioneers" VALUES(22,50,'22:50','Пас, касоне, ки имон овардаанд ва корҳои шоиста анҷом додаанд, омурзишу рӯзии нек [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(22,51,'22:51','Ва касоне, ки дар [роҳи инкор ва такзиби] оёти Мо мекӯшанд, то [ба пиндори хеш] Моро дармонда кунанд, онон аҳли дузаханд');
INSERT INTO "pioneers" VALUES(22,52,'22:52','Ва [Мо] ҳеҷ расул ва паёмбареро пеш аз ту нафиристодем, магар ин ки чун [оёти илоҳиро] тиловат мекард, шайтон дар тиловаташ [калимоти иштибоҳ] илқо мекард. Он гоҳ Аллоҳ таоло он чиро, ки шайтон илқо намуда буд, аз миён мебурд ва сипас оёти хешро устувор месохт ва Аллоҳ таоло донои ҳаким аст');
INSERT INTO "pioneers" VALUES(22,53,'22:53','То [бад-ин васила Аллоҳ таоло] он чиро, ки шайтон илқо мекунад, барои касоне, ки дар дилҳояшон беморӣ [шакку нифоқ] аст ва [ҳамчунин барои] сангдилон василаи озмоиш қарор диҳад. Ва яқинан, ситамгорон [-и мушрик] дар душмании дуру дарозе [бо Аллоҳ таоло ва паёмбараш] ҳастанд');
INSERT INTO "pioneers" VALUES(22,54,'22:54','Ва то касоне, ки дониш ёфтаанд, бидонанд, ки ин [Қуръон] ҳақ аст [ва] аз ҷониби Парвардигори туст ва ба он имон биёваранд ва дилҳояшон барояш [нарму] фурӯтан гардад; ва бе тардид, Аллоҳ таоло касонеро, ки имон овардаанд, ба роҳи рост ҳидоят мекунад');
INSERT INTO "pioneers" VALUES(22,55,'22:55','Касоне, ки куфр варзиданд, ҳамвора дар мавриди он [чи дар Қуръон нозил кардаем] тардид доранд, то он гоҳ ки ногаҳон қиёмат фаро расад ё азоби рӯзи бехайру раҳмат бар онон [фуруд] ояд');
INSERT INTO "pioneers" VALUES(22,56,'22:56','Дар он рӯз фармонравоӣ аз они Аллоҳ таоло аст [ва] миёни онон доварӣ мекунад. Пас касоне, ки имон овардаанд ва корҳои шоиста анҷом додаанд, дар боғҳои пурнеъмат [-и биҳишт] ҳастанд');
INSERT INTO "pioneers" VALUES(22,57,'22:57','Ва касоне, ки куфр варзиданд ва оёти Моро дурӯғ ангоштанд, азоби хоркунандае [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(22,58,'22:58','Ва касоне, ки дар роҳи Аллоҳ таоло ҳиҷрат карданд, сипас кушта шуданд ё вафот карданд, яқинан Аллоҳ таоло ба онон рӯзии накуе медиҳад. Ба ростӣ, ки Аллоҳ таоло беҳтарин рӯзидиҳанда аст');
INSERT INTO "pioneers" VALUES(22,59,'22:59','Бе тардид, ононро ба ҷойгоҳе ворид мекунад, ки аз он хушнуд бошанд ва ба ростӣ, ки Аллоҳ таоло донои бурдбор аст');
INSERT INTO "pioneers" VALUES(22,60,'22:60','Ин аст [ҳукми илоҳӣ дар бораи кофир ва муъмин] ва ҳар кас ба ҳамон миқдор, ки ба ӯ ситам шудааст, муҷозот кунад, сипас [боз] мавриди ситам қарор гирад, Аллоҳ таоло ёриаш хоҳад кард. Бе тардид, Аллоҳ таоло бахшандаи омурзанда аст');
INSERT INTO "pioneers" VALUES(22,61,'22:61','Ин [ёрии мазлумон] аз он рӯст, ки Аллоҳ таоло [бар ҳар коре тавоност; чунон ки] шабро дар рӯз мекашонад ва рӯзро дар шаб дармеоварад; ва бе тардид, Аллоҳ таоло шунавои биност');
INSERT INTO "pioneers" VALUES(22,62,'22:62','Ин бад-он сабаб аст, ки Аллоҳ таоло ҳақ аст ва он чиро, ки [мушрикон] ҷуз ӯ мехонанд, [ҳама] ботил аст ва Аллоҳ таоло баландмартабаи бузург аст');
INSERT INTO "pioneers" VALUES(22,63,'22:63','Оё надидӣ, ки Аллоҳ таоло аз осмон обе фурӯ фиристод ва замин [бар асари он хурраму] сарсабз мегардад? Бе тардид, Аллоҳ таоло борикбини огоҳ аст');
INSERT INTO "pioneers" VALUES(22,64,'22:64','Он чи дар осмонҳо ва замин аст, аз они Ӯст ва бе тардид, Аллоҳ таоло бениёзи сутуда аст');
INSERT INTO "pioneers" VALUES(22,65,'22:65','Оё надидӣ, ки Аллоҳ таоло он чиро дар замин аст, ба хидмати шумо гумоштааст ва киштиҳо ба фармонаш дар дарё равонанд ва осмонро нигаҳ медорад, ки ҷуз ба иҷозати Ӯ бар замин наафтад. Бе тардид, Аллоҳ таоло [нисбат] ба мардум дилсӯзи меҳрубон аст');
INSERT INTO "pioneers" VALUES(22,66,'22:66','Ва Ӯст, ки шуморо зинда кард, сипас мемиронад ва он гоҳ [бори дигар] зинда мекунад. Ба ростӣ, ки инсон [нисбат ба неъматҳои илоҳӣ] бисёр носипос аст');
INSERT INTO "pioneers" VALUES(22,67,'22:67','Барои ҳар уммате оине муқаррар кардаем, ки ба он амал кунанд, пас, [эй паёмбар, пайравони соири адён] набояд бо ту дар ин амр ситез кунанд. Ба роҳи Парвардигорат даъват кун, [чаро ки] ту яқинан, бар [оини дуруст ва] роҳи рост қарор дорӣ');
INSERT INTO "pioneers" VALUES(22,68,'22:68','Ва агар [дар бораи ҳаққонияти дин] бо ту муҷодала карданд, пас, бигӯ: «Аллоҳ таоло ба он чи анҷом медиҳед, донотар аст');
INSERT INTO "pioneers" VALUES(22,69,'22:69','Аллоҳ таоло рӯзи қиёмат дар мавриди он чи дар борааш ихтилоф мекардед, миёнатон доварӣ мекунад»');
INSERT INTO "pioneers" VALUES(22,70,'22:70','Оё надонистаӣ, ки Аллоҳ таоло он чиро дар осмон ва замин аст медонад? Бе гумон [ҳамаи] инҳо дар китобе [сабт] аст. Мусалламан, ин [кор] бар Аллоҳ таоло осон аст');
INSERT INTO "pioneers" VALUES(22,71,'22:71','Ва [мушрикон] ба ҷойи Аллоҳ таоло чизеро мепарастанд, ки ҳеҷ далеле бар [ҳаққонияти] он нозил накардааст ва бад-он огоҳӣ [ва шинохте низ] надоранд ва ситамгорон [-и мушрик дар баробари азоби илоҳӣ] ҳеҷ ёрикунандае надоранд');
INSERT INTO "pioneers" VALUES(22,72,'22:72','Ва ҳангоме ки оёти равшани Мо бар онон хонда мешавад, дар чеҳраи касоне, ки куфр варзидаанд, [осори кароҳат ва] нохушояндӣ мебинӣ. Наздик аст ба касоне, ки оёти Моро барояшон мехонанд, ҳамлавар шаванд. [Эй паёмбар, ба кофирон] Бигӯ: «Оё шуморо ба бадтар аз ин [ки дар интизоратон аст] хабар диҳам? Оташ [-и дузах] аст, ки Аллоҳ таоло онро ба касоне, ки куфр варзиданд, ваъда додаст ва чи бад саранҷомест!»');
INSERT INTO "pioneers" VALUES(22,73,'22:73','Эй мардум, [дар бораи нотавонии бутҳо] масале зада шудааст, пас, ба он гӯш диҳед: Касонеро, ки ба ҷойи Аллоҳ таоло [ба парастиш] мехонед, ҳаргиз наметавонанд магасеро биёфаринанд, ҳатто агар [ҳамагӣ] барои ин [кор] ҷамъ шаванд. Ва агар он магас чизе аз онон бирабояд, наметавонанд онро бозпас гиранд. [Оре] Хостор ва хоста [бут ва магас] ҳар ду нотавонанд');
INSERT INTO "pioneers" VALUES(22,74,'22:74','[Онон] Аллоҳро чунон ки сазовори [бузургдошти] Ӯст, нашинохтаанд. Бе тардид, Аллоҳ таоло неруманди шикастнопазир аст');
INSERT INTO "pioneers" VALUES(22,75,'22:75','Аллоҳ таоло аз миёни фариштагон фиристодагоне [барои иблоғи ваҳй ба паёмбарон] бармегузинад ва аз миёни инсонҳо низ [паёмбароне барои расонидани паёми илоҳӣ ба мардум интихоб мекунад]. Бе тардид, Аллоҳ таоло шунавои биност');
INSERT INTO "pioneers" VALUES(22,76,'22:76','Ӯ оянда ва гузаштаи ононро медонад ва [ҳамаи] корҳо ба Аллоҳ таоло бозгардонда мешавад');
INSERT INTO "pioneers" VALUES(22,77,'22:77','Эй касоне, ки имон овардаед, рукуъ кунед ва саҷда гузоред ва Парвардигоратонро бипарастед ва кори нек анҷом диҳед, бошад, ки растагор шавед');
INSERT INTO "pioneers" VALUES(22,78,'22:78','Ва дар роҳи Аллоҳ таоло – чунон ки сазовори ҷиҳод [дар роҳи] Ӯст – ҷиҳод кунед. Ӯ шуморо баргузид ва дар дин бароятон ҳеҷ сахтӣ [ва тангное] қарор надод. Оини падаратон Иброҳим [низ чунин буд]. Ӯ [Аллоҳ таоло] пеш аз ин [дар китобҳои осмонии гузашта] ва дар ин [Қуръон] шуморо мусулмон номид, то паёмбар бар [иблоғи паёмаш ба] шумо гувоҳ бошад ва шумо [низ] бар мардум гувоҳ бошед [ки ҳамаи паёмбарон паёми илоҳиро ба қавми худ расонидаанд]. Пас, намоз барпо доред ва закот бипардозед ва ба Аллоҳ таоло паноҳ бибаред [ва бар Ӯ таваккал кунед], ки Ӯ [дӯсту] корсози шумост. Пас, чи наку корсозу чи нек ёварест!');
INSERT INTO "pioneers" VALUES(23,1,'23:1','Ба ростӣ, ки муъминон растагор шуданд');
INSERT INTO "pioneers" VALUES(23,2,'23:2','Ҳамон касоне, ки дар намозашон фурӯтан ҳастанд');
INSERT INTO "pioneers" VALUES(23,3,'23:3','Ва онон, ки аз [гуфтору рафтори] беҳуда рӯйгардонанд');
INSERT INTO "pioneers" VALUES(23,4,'23:4','Ва онон, ки закот мепардозанд');
INSERT INTO "pioneers" VALUES(23,5,'23:5','Ва онон, ки шармгоҳашонро ҳифз мекунанд');
INSERT INTO "pioneers" VALUES(23,6,'23:6','Магар дар мавриди ҳамсарон ё канизонашон, ки [дар баҳрагирии ҷинсӣ аз онҳо] накуҳише бар онон нест');
INSERT INTO "pioneers" VALUES(23,7,'23:7','Пас, ҳар ки фаротар аз ин бихоҳад, ононанд, ки аз ҳад даргузаштаанд');
INSERT INTO "pioneers" VALUES(23,8,'23:8','Ва [дар зумраи муъминонанд] касоне, ки амонатҳо ва аҳди худро риоят мекунанд');
INSERT INTO "pioneers" VALUES(23,9,'23:9','Ва касоне, ки бар [адои] намозҳояшон муроқибат мекунанд');
INSERT INTO "pioneers" VALUES(23,10,'23:10','Инонанд, ки ворисон ҳастанд');
INSERT INTO "pioneers" VALUES(23,11,'23:11','[Ҳамон] Касоне, ки биҳишти баринро ба ирс мебаранд [ва] дар он ҷовидонанд');
INSERT INTO "pioneers" VALUES(23,12,'23:12','Ва дар ҳақиқат, инсонро аз чакидае аз гил офаридем');
INSERT INTO "pioneers" VALUES(23,13,'23:13','Сипас ӯро ба шакли нутфае дар қароргоҳе устувор [раҳим] ниҳодем');
INSERT INTO "pioneers" VALUES(23,14,'23:14','Сипас нутфаро [Ба шакли] хуни баста сохтем ва он гоҳ хуни бастаро [ба сурати] порагӯште сохтем, сипас порагӯштро [ба шакли] устухон даровардем ва он гоҳ бар устухонҳо гӯшт пӯшонидем. Сипас он [ҷанин]-ро офаринише дигар бахшидем. Пас, [пурбаракату] бузургвор аст Аллоҳ таоло, ки накутарин офаринанда аст');
INSERT INTO "pioneers" VALUES(23,15,'23:15','Сипас шумо [пас аз тайи мароҳили зиндагӣ] ҳатман, хоҳед мурд');
INSERT INTO "pioneers" VALUES(23,16,'23:16','Он гоҳ бе тардид, дар рӯзи қиёмат барангехта мешавед');
INSERT INTO "pioneers" VALUES(23,17,'23:17','Ва ба яқин Мо бар фарозатон ҳафт осмон офаридем ва [ҳаргиз] аз офариниш ғофил набудаем');
INSERT INTO "pioneers" VALUES(23,18,'23:18','Ва аз осмон ба андозае муайян [барои ниёзи махлуқот] обе фиристодем ва онро дар замин ҷой додем ва ҳатман, барои аз байн бурданаш [низ] комилан тавоноем');
INSERT INTO "pioneers" VALUES(23,19,'23:19','Сипас тавассути он [об] бароятон боғҳое аз дарахтони хурмо ва ангур падид овардем, ки дар он [боғҳо] барои шумо меваҳое бисёре аст ва аз он [маҳсулот] мехӯред');
INSERT INTO "pioneers" VALUES(23,20,'23:20','Ва дарахте [офаридем], ки аз Тӯри Сино бармеояд ва равғани [зайтун] медиҳад ва хӯрише барои хӯрандагон аст');
INSERT INTO "pioneers" VALUES(23,21,'23:21','Ва бе тардид, барои шумо дар [вуҷуди] чаҳорпоён ибрате [аз қудрати Аллоҳ таоло] аст: аз он чи дар шиками онҳост ба шумо [шир] менӯшонем ва дар [вуҷуди] онҳо бароятон судҳои бисёре вуҷуд дорад ва аз [гӯшти] онҳо мехӯред');
INSERT INTO "pioneers" VALUES(23,22,'23:22','[Дар хушкӣ] Бар онҳо савор мешавед ва [дар об] бар киштиҳо');
INSERT INTO "pioneers" VALUES(23,23,'23:23','Ва бе тардид, Мо Нуҳро ба сӯйи қавмаш фиристодем, пас, [ба онон] гуфт: «Эй қавми ман, Аллоҳро бипарастед [ки] ҷуз Ӯ маъбуде [ба ҳақ] надоред. Оё парво намекунед?»');
INSERT INTO "pioneers" VALUES(23,24,'23:24','Пас, ашроф ва бузургони қавмаш, ки куфр варзида буданд, гуфтанд: «Ин [мард касе] нест, магар башаре ҳамчун шумо, [ки] мехоҳад бар шумо бартарӣ ҷӯяд. Агар Аллоҳ таоло мехост, фариштагоне [ба сӯятон] нозил мекард. Мо инро [ки Нуҳ мегӯяд] дар миёни ниёгони худ нашнидаем');
INSERT INTO "pioneers" VALUES(23,25,'23:25','Ӯ марди девонае беш нест. Пас, то муддате мунтазир бошед [ки ё дурӯғаш ошкор гардад ва даст аз даъваташ бардорад ё маргаш фаро расад]');
INSERT INTO "pioneers" VALUES(23,26,'23:26','[Нуҳ] Гуфт: «Парвардигоро, маро дар баробари такзиби онон ёрӣ кун»');
INSERT INTO "pioneers" VALUES(23,27,'23:27','Пас, ба ӯ ваҳй кардем: «Киштиро таҳти назари Мо ва ба ваҳйи Мо бисоз ва ҳангоме ки фармони [азоби] Мо фаро расад ва [ба нишонаи расидани тӯфон об аз] танӯр ҷӯшад, аз ҳар навъи [ҳайвон] як ҷуфти [нару мода] дар киштӣ савор кун ва хонаводаатро [низ ҳамроҳи худ бубар], магар касоне, ки пештар ваъда [-и ҳалок] бар онон муқаррар шудааст ва дар бораи [наҷоти] касоне, ки ситам кардаанд, бо Ман сухан нагӯ, [чаро ки] онон ҳатман, ғарқ хоҳанд шуд');
INSERT INTO "pioneers" VALUES(23,28,'23:28','Пас, ҳангоме ки ту ва ҳамроҳонат бар киштӣ савор шудед, бигӯ: «Сипос махсуси Аллоҳ таоло аст, ки моро аз [чанги] гурӯҳи ситамгор наҷот дод»');
INSERT INTO "pioneers" VALUES(23,29,'23:29','Ва [низ] бигӯ: «Парвардигоро, моро дар манзилгоҳе пурбаракат фуруд овар, ки Ту беҳтарин фурудоварандаӣ»');
INSERT INTO "pioneers" VALUES(23,30,'23:30','Бе тардид, дар ин [моҷаро] нишонаҳое [аз қудрати Аллоҳ таоло дар наҷоти муъминон ва азоби кофирон] аст ва яқинан, Мо озмоишгар [-и имон ва куфри мардум] будаем');
INSERT INTO "pioneers" VALUES(23,31,'23:31','Он гоҳ гурӯҳи дигареро пас аз онон падид овардем');
INSERT INTO "pioneers" VALUES(23,32,'23:32','Ва аз худашон паёмбаре дар [миёни] онон фиристодем [то бигӯяд]: «Аллоҳро бипарастед, [ки] ҷуз Ӯ маъбуде [ба ҳақ] надоред. Оё парво намекунед?»');
INSERT INTO "pioneers" VALUES(23,33,'23:33','Гурӯҳе аз ашроф ва бузургони қавмаш, ки куфр варзида буданд ва дидори охиратро дурӯғ меангоштанд ва дар зиндагии дунё ба онон неъмату осоиш дода будем, гуфтанд: «Ин [мард касе] нест, магар башаре ҳамчун шумо. Аз он чи [шумо] мехӯред, [ӯ низ] мехӯрад ва аз он чи ки шумо менӯшед, [ӯ низ] менӯшад [пас, бартарие надорад, ки паёмбар бошад]');
INSERT INTO "pioneers" VALUES(23,34,'23:34','Ва агар аз башаре ҳамчун худ итоат кунед, дар он сурат яқинан, зиёнкоред');
INSERT INTO "pioneers" VALUES(23,35,'23:35','Оё ӯ ба шумо ваъда медиҳад, ки вақте мурдед ва хоку устухон шудед, [дар қиёмат аз гӯр] берун оварда мешавед?');
INSERT INTO "pioneers" VALUES(23,36,'23:36','Он чи ба шумо ваъда медиҳад, чи дур аст, чи дур!');
INSERT INTO "pioneers" VALUES(23,37,'23:37','[Зиндагонӣ] Ҷуз ҳамин зиндагии дунёии мо нест. [Гурӯҳе] Мемирем [ва гурӯҳе ба ҷойи онҳо] зинда мешавем ва ҳаргиз [пас аз марг] барангехта нахоҳем шуд');
INSERT INTO "pioneers" VALUES(23,38,'23:38','Ӯ [касе] нест, магар мардест, ки бар Аллоҳ таоло дурӯғ мебандад ва мо ба ӯ имон намеоварем»');
INSERT INTO "pioneers" VALUES(23,39,'23:39','Паёмбарашон] Гуфт: «Парвардигоро, маро дар баробари такзиби онон ёрӣ кун»');
INSERT INTO "pioneers" VALUES(23,40,'23:40','[Аллоҳ таоло] Фармуд: «Пас аз андак замоне, [аз кори худ] сахт пушаймон мешаванд»');
INSERT INTO "pioneers" VALUES(23,41,'23:41','Пас, бонг [-и маргбор]-е ононро ба ҳақ фуру гирифт ва [ҳамчун] хору хошокашон намудем. Пас, қавми ситамгор [аз раҳмати Аллоҳ таоло] дур бод!');
INSERT INTO "pioneers" VALUES(23,42,'23:42','Сипас баъд аз онон наслҳое дигар падид овардем');
INSERT INTO "pioneers" VALUES(23,43,'23:43','Ҳеҷ уммате на аз аҷали худ пешӣ мегирад ва на таъхир мекунад');
INSERT INTO "pioneers" VALUES(23,44,'23:44','Сипас паёмбарони худро паёпай фиристодем. Ҳар гоҳ паёмбаре барои [ҳидояти] уммате меомад, ӯро дурӯғгӯ меангоштанд, пас, [Мо низ] ононро яке пас аз дигаре ҳалок кардем ва ононро достонҳо [-и ибратомӯзе барои ояндагон] қарор додем. Пас, нобуд бод, қавме, ки имон намеоваранд!');
INSERT INTO "pioneers" VALUES(23,45,'23:45','Сипас Мӯсо ва бародараш Ҳорунро бо оёти [нуҳгонаи] хеш ва далоили ошкор фиристодем');
INSERT INTO "pioneers" VALUES(23,46,'23:46','Ба сӯйи Фиръавн ва ашрофи [қавми] ӯ, вале онон гарданкашӣ карданд ва гурӯҳе бартарихоҳ буданд');
INSERT INTO "pioneers" VALUES(23,47,'23:47','Ва гуфтанд: «Оё ба ду инсони монанди худ имон биёварем, ҳол он ки қавмашон бардагони мо ҳастанд?»');
INSERT INTO "pioneers" VALUES(23,48,'23:48','Пас он дуро дурӯғгӯ ангоштанд, [ва] саранҷом ҳалок шуданд');
INSERT INTO "pioneers" VALUES(23,49,'23:49','Ва бе тардид, ба Мӯсо китоби [Таврот] додем, бошад, ки [Бани Исроил] ҳидоят шаванд');
INSERT INTO "pioneers" VALUES(23,50,'23:50','Ва писари Марям ва модарашро нишонае [аз қудрати илоҳӣ] карор додем ва ононро дар маконе баланд ва ҳамворе, ки дорои амният ва обе [зулолу] ҷорӣ буд, ҷой додем');
INSERT INTO "pioneers" VALUES(23,51,'23:51','Эй паёмбарон, аз [ғизоҳои] покиза бихӯред ва кори шоиста анҷом диҳед. Яқинан, ман ба он чи мекунед, огоҳам');
INSERT INTO "pioneers" VALUES(23,52,'23:52','Ва дар ҳақиқат, ин дини шумо дини воҳид [ба номи ислом] аст ва ман Парвардигоратон ҳастам. Пас, аз ман парво кунед [битарсед]');
INSERT INTO "pioneers" VALUES(23,53,'23:53','Аммо [пайравонашон ихтилоф карданд ва] дар кори динашон миёни худ фирқа-фирқа ва пароканда гаштанд ва ҳар гурӯҳе ба он чи наздашон аст, дилхушанд [чаро ки худро бар ҳақ медонанд]');
INSERT INTO "pioneers" VALUES(23,54,'23:54','Пас, [эй паёмбар] ононро то вақте [ки азоб фаро мерасад] дар ҷаҳлу нодонишон раҳо кун');
INSERT INTO "pioneers" VALUES(23,55,'23:55','Оё гумон мекунанд, он чи аз сарвату фарзандон, ки ба онон медиҳем,');
INSERT INTO "pioneers" VALUES(23,56,'23:56','[барои ин аст, ки] шитобон некиҳоро ба эшон бирасонем? [На, ҳаргиз чунин нест] Балки онон [ҳақиқатро] дарнамеёбанд');
INSERT INTO "pioneers" VALUES(23,57,'23:57','Касоне, ки аз бими Парвардигорашон тарсонанд');
INSERT INTO "pioneers" VALUES(23,58,'23:58','Ва касоне, ки ба оёти Парвардигорашон имон меоваранд');
INSERT INTO "pioneers" VALUES(23,59,'23:59','Ва касоне, ки ба Парвардигорашон ширк намеварзанд');
INSERT INTO "pioneers" VALUES(23,60,'23:60','Ва касоне, ки дар анҷоми корҳои хайр мекӯшанд ва аз ин ки ба сӯйи Парвардигори хеш бозмегарданд, [ва аз ин ки корҳои некашон мақбули даргоҳи ҳақ воқеъ нашавад] дилҳояшон ҳаросон аст');
INSERT INTO "pioneers" VALUES(23,61,'23:61','Инон дар некиҳо шитоб мекунанд ва дар анҷоми он [аз якдигар] пешӣ мегиранд');
INSERT INTO "pioneers" VALUES(23,62,'23:62','Ва [Мо] ба ҳеҷ кас ҷуз ба андозаи тавонаш таклиф намекунем ва назди Мо китобест, ки [дар бораи аъмоли бандагон] ба ҳақ сухан мегӯяд ва ба онон ситам намешавад');
INSERT INTO "pioneers" VALUES(23,63,'23:63','[На] Балки дилҳояшон аз ин [китоб] дар ғафлат аст ва онон ба ҷуз ин [гуноҳи куфр] корҳое [дигар низ] анҷом медиҳанд');
INSERT INTO "pioneers" VALUES(23,64,'23:64','То ҳангоме ки сарватмандонашонро ба азоб гирифтор кунем, ногаҳон фарёди зорӣ сар медиҳанд');
INSERT INTO "pioneers" VALUES(23,65,'23:65','[Ба онон гуфта мешавад] «Имрӯз нола ва зорӣ накунед, [ки] ҳаргиз аз сӯйи мо ёрӣ нахоҳед шуд');
INSERT INTO "pioneers" VALUES(23,66,'23:66','Бе тардид, оёти Ман пайваста бар шумо хонда мешуд, вале [аз ҳақ] рӯй мегардонед');
INSERT INTO "pioneers" VALUES(23,67,'23:67','Дар ҳоле ки дар баробараш такаббур мекардед [ва дар маҷолиси шабонаи худ] ба бадгӯӣ мепардохтед');
INSERT INTO "pioneers" VALUES(23,68,'23:68','Оё онон дар ин гуфтор [Қуръон] наяндешидаанд ё [магар] чизе барояшон омада, ки барои ниёгонашон наёмадааст?');
INSERT INTO "pioneers" VALUES(23,69,'23:69','Ё паёмбарашонро нашинохтаанд ва [аз ин рӯст, ки] инкораш мекунанд');
INSERT INTO "pioneers" VALUES(23,70,'23:70','Ё мегӯянд, ӯ ҷунун дорад? [На, чунин нест] Балки [ӯ дини] ҳақро барояшон овардааст, вале бештарашон аз [пазириши] ҳақ нохушнуданд');
INSERT INTO "pioneers" VALUES(23,71,'23:71','Агар Аллоҳ таоло аз ҳавасҳои онон пайравӣ кунад [ва умури ҳастӣ ба майли кофирон идора шавад], осмонҳо ва замин ва ҳар ки дар онҳост, табоҳ мегардад, балки барои онон пандашон [Қуръон]-ро овардаем [ки мояи иззату шарафашон аст], вале онон аз пандашон рӯйгардонанд');
INSERT INTO "pioneers" VALUES(23,72,'23:72','[Эй Паёмбар] Оё ту аз онон [барои таблиғи дин] подоше металабӣ? Ва ҳол он ки подоши Парвардигорат [басе] беҳтар аст ва Ӯ беҳтарин рӯзидиҳанда аст');
INSERT INTO "pioneers" VALUES(23,73,'23:73','Ва бе тардид, ту ононро ба роҳе рост даъват мекунӣ');
INSERT INTO "pioneers" VALUES(23,74,'23:74','Касоне, ки ба охират имон надоранд, ҳатман аз ин роҳи [рост] мунҳариф шудаанд');
INSERT INTO "pioneers" VALUES(23,75,'23:75','Агар ба онон раҳм кунем ва балову ранҷеро, ки [аз қаҳтӣ ва гуруснагӣ] дидаанд, аз эшон бартараф созем, бар саркашӣ [ва гумроҳии] хеш пофишорӣ мекунанд [ва ҳамчунон] ба бероҳа мераванд');
INSERT INTO "pioneers" VALUES(23,76,'23:76','Дар ҳақиқат, [Мо] ононро ба азоб [-у бало] гирифтор сохтем [то бедор шаванд], аммо дар баробари Парвардигорашон фурӯтанӣ нанамуданд ва [ба даргоҳаш тазарруъ ва] зорӣ накарданд');
INSERT INTO "pioneers" VALUES(23,77,'23:77','[Онон пайваста чунин ҳастанд] То он гоҳ ки даре аз азоби сахт бар онон мекушоем [ва чунон гирифтор шаванд], ки [аз ҳама ҷо] маъюс гарданд');
INSERT INTO "pioneers" VALUES(23,78,'23:78','[Эй мункирони қиёмат] Аллоҳ таоло аст, ки барои шумо гӯшу чашм ва дил падид овард. Чи андак сипос мегузоред!');
INSERT INTO "pioneers" VALUES(23,79,'23:79','Ва Ӯст, ки шуморо дар замин [офарид ва] пароканда кард ва [рӯзи қиёмат] ба сӯйи Ӯ ҷамъ мешавед');
INSERT INTO "pioneers" VALUES(23,80,'23:80','Ва Ӯст, ки зинда мекунад ва мемиронад ва ихтилофи шабу рӯз аз тадбири Ӯст. Оё намеандешед?');
INSERT INTO "pioneers" VALUES(23,81,'23:81','[Чунин накарданд] Балки онон низ ҳамон [суханонеро] гуфтанд, ки пешиниён мегуфтанд');
INSERT INTO "pioneers" VALUES(23,82,'23:82','Гуфтанд: «Оё ҳангоме ки мурдему хоку устухон шудем, барангехта хоҳем шуд?');
INSERT INTO "pioneers" VALUES(23,83,'23:83','Бе тардид, ин ваъдаро пеш аз ин низ ба мо ва ниёгонамон додаанд. Ин [сухан чизе] нест, магар [дурӯғҳо ва] афсонаҳои пешиниён»');
INSERT INTO "pioneers" VALUES(23,84,'23:84','[Эй паёмбар, ба мункирони меод] бигӯ: «Агар медонед, [бигӯед], замин ва ҳар ки дар он аст, аз они кист?»');
INSERT INTO "pioneers" VALUES(23,85,'23:85','Хоҳанд гуфт: «[Ҳама] Аз они Аллоҳ таоло аст». Бигӯ: «Пас, оё панд намегиред?»');
INSERT INTO "pioneers" VALUES(23,86,'23:86','Бигӯ: «Парвардигори ҳафт осмон ва Парвардигори Арши бузург кист?»');
INSERT INTO "pioneers" VALUES(23,87,'23:87','Хоҳанд гуфт: «[Ҳама] Аз они Аллоҳ таоло аст». Бигӯ: «Пас, оё [аз Аллоҳ таоло] парво намекунед?»');
INSERT INTO "pioneers" VALUES(23,88,'23:88','Бигӯ: «Агар медонед, [бигӯед] фармонравоии ҳама чиз ба дасти кист? Ва [кист, ки] паноҳ медиҳад ва касе дар баробари [азоби] ӯ паноҳ надорад?»');
INSERT INTO "pioneers" VALUES(23,89,'23:89','Хоҳанд гуфт: «[Фармонравоии ҳама чиз] Аз они Аллоҳ таоло аст». Бигӯ: «Пас, чи гуна афсун мешавед [ва намеандешед]?»');
INSERT INTO "pioneers" VALUES(23,90,'23:90','[Чунин нест, ки кофирон гумон мекунанд] Балки Мо ҳақро барояшон овардем ва яқинан, онон дурӯғгӯ ҳастанд');
INSERT INTO "pioneers" VALUES(23,91,'23:91','Аллоҳ таоло ҳаргиз фарзанде барнагузидааст ва ҳеҷ маъбуде бо Ӯ нест. [Агар чунин буд] Он гоҳ ҳар маъбуде он чиро офарида буд, [бо худ] мебурд ва мусалламан, бархе бар бархе дигар бартарӣ меҷустанд [ва низоми ҳастӣ табоҳ мегашт]. Аллоҳ таоло аз он чи [мушрикон] тавсиф мекунанд, муназзаҳ аст');
INSERT INTO "pioneers" VALUES(23,92,'23:92','Ӯ донои ниҳону ошкор аст ва аз он чи бо Вай шарик месозанд, бартар аст');
INSERT INTO "pioneers" VALUES(23,93,'23:93','Бигӯ: «Парвардигоро, агар он [азобе]-ро, ки ба онон ваъда дода мешавад, ба ман нишон медиҳӣ [ки шоҳидаш бошам],');
INSERT INTO "pioneers" VALUES(23,94,'23:94','пас, Парвардигоро маро дар [ҳангоми азоб дар канори ин] гурӯҳи ситамгор қарор мадеҳ»');
INSERT INTO "pioneers" VALUES(23,95,'23:95','Ва ҳатман, Мо тавоноем, ки он чиро ба онон ваъда медиҳем, ба ту нишон диҳем');
INSERT INTO "pioneers" VALUES(23,96,'23:96','[Эй паёмбар] бадиро ба шевае, ки беҳтар аст, дафъ кун [ва бубахшу шикебо бош]. Мо ба он чи [мушрикон дар бораи ту] тавсиф мекунанд, донотарем');
INSERT INTO "pioneers" VALUES(23,97,'23:97','Ва бигӯ: «Парвардигоро, ман аз васвасаҳои шаётин ба Ту паноҳ мебарам');
INSERT INTO "pioneers" VALUES(23,98,'23:98','Ва Парвардигоро, аз ин ки онҳо назди ман ҳозир шаванд, [низ] ба Ту паноҳ мебарам»');
INSERT INTO "pioneers" VALUES(23,99,'23:99','[Кофирон пайваста бар ақидаи ботилашон ҳастанд] То ҳангоме ки марги яке аз онон фаро расад, [он гоҳ дар ҳоле ки] назди Парвардигори [худ нолаву зорӣ мекунад, ба фариштагон] мегӯяд: «Маро [ба дунё] бозгардонед');
INSERT INTO "pioneers" VALUES(23,100,'23:100','Шояд дар он чи [кӯтоҳӣ намуда ва] тарк кардам, кори шоистае анҷом диҳам». Ҳаргиз чунин нест. Бе тардид, ин фақат сухане аст, ки ӯ ба забон мегӯяд [ва ҳаргиз рафтораш тағйир намекунад] ва рӯзе, ки барангехта шаванд, пушти сарашон барзахест');
INSERT INTO "pioneers" VALUES(23,101,'23:101','Рӯзе, ки дар сур дамида мешавад, ҳеҷ як аз [ ифтихороти хонаводагӣ ва] пайвандҳои хешовандӣ миёнашон [барқарор] нахоҳад буд ва [аз ваҳшати азоб] аз [ҳоли] якдигар намепурсанд');
INSERT INTO "pioneers" VALUES(23,102,'23:102','Ва ҳар ки вазнаи аъмолаш сангин бошад, ононанд, ки растагоранд');
INSERT INTO "pioneers" VALUES(23,103,'23:103','Ва ҳар ки вазнаи аъмолаш сабук бошад, ононанд, ки ба хеш зиён задаанд ва дар дузах ҷовидонанд');
INSERT INTO "pioneers" VALUES(23,104,'23:104','Оташ чеҳраҳояшонро месӯзонад ва дар он ҷо рӯйтурш ва зиштманзаранд');
INSERT INTO "pioneers" VALUES(23,105,'23:105','[Ба онон гуфта мешавад] «Магар на ин буд, ки оётам бар шумо хонда мешуд ва онҳоро дурӯғ меангоштед?»');
INSERT INTO "pioneers" VALUES(23,106,'23:106','[Дар посух] Мегӯянд: «Парвардигоро, бадбахтӣ бар мо чира шуд ва гурӯҳе гумроҳ будем');
INSERT INTO "pioneers" VALUES(23,107,'23:107','Парвардигоро, моро аз ин [дузах] берун овар. Пас, агар [ба куфру нофармонӣ] бозгаштем, ҳатман, ситамгорем»');
INSERT INTO "pioneers" VALUES(23,108,'23:108','[Аллоҳ таоло] Мефармояд: «Дар он [оташ] гум шавед ва бо Ман сухан нагӯед');
INSERT INTO "pioneers" VALUES(23,109,'23:109','Гурӯҳе аз бандагонам мегуфтанд: «Парвардигоро, имон овардем, пас, моро бибахшо ва бар мо раҳм кун, [ки] бе тардид, Ту беҳтарин бахшояндаӣ»');
INSERT INTO "pioneers" VALUES(23,110,'23:110','Вале шумо ононро масхара мекардед, то ҷойе, ки [саргарм шудан ба тамасхури эшон] зикр [-у ибодати] Маро аз ёдатон бурд ва ҳамчунон ба онон механдидед');
INSERT INTO "pioneers" VALUES(23,111,'23:111','Имрӯз Ман ононро аз он ҷиҳате, ки шикебоӣ карданд, подош медиҳам. Оре, эшонанд, ки растагоранд»');
INSERT INTO "pioneers" VALUES(23,112,'23:112','[Аллоҳ таоло ба дузахиён] Мегӯяд: «Чанд сол дар замин ба сар бурдед»?');
INSERT INTO "pioneers" VALUES(23,113,'23:113','Мегӯянд: «Як рӯз ё бахше аз як рӯзро ба сар бурдем. Аз ҳисобгарон [ки ҳисоби рӯзу солро нигаҳ медоранд] бипурс»');
INSERT INTO "pioneers" VALUES(23,114,'23:114','Ӯ мегӯяд: «[Оре] Агар медонистед, ҷуз андаке [дар дунё] ба сар набурдед');
INSERT INTO "pioneers" VALUES(23,115,'23:115','Оё пиндоштаед, ки шуморо беҳуда офаридаем ва ба сӯйи Мо бозгардонида намешавед?');
INSERT INTO "pioneers" VALUES(23,116,'23:116','Пас, баландмартаба [ва бузург] аст Аллоҳ таоло [ки] фармонравои ҳақ аст. Ҳеҷ маъбуде [ба ҳақ] ҷуз Ӯ нест [ва] Парвардигори Арши гаронқадр аст');
INSERT INTO "pioneers" VALUES(23,117,'23:117','Ва ҳар ки бо Аллоҳ таоло маъбуди дигареро бихонад, яқинан, ҳеҷ далеле бар [ҳаққонияти] он нахоҳад дошт ва ҳисоби ӯ бо Парвардигораш хоҳад буд. Бе тардид, кофирон растагор намешаванд');
INSERT INTO "pioneers" VALUES(23,118,'23:118','Ва [эй Паёмбар] бигӯ: «Парвардигоро, [гуноҳонамро] бибахшой ва раҳм кун ва Ту беҳтарин бахшандаӣ»');
INSERT INTO "pioneers" VALUES(24,1,'24:1','[Ин] Сураест, ки онро нозил кардаем ва [амал ба аҳкоми] онро воҷиб намудаем ва дар он оёти равшане нозил кардаем, бошад, ки панд гиред');
INSERT INTO "pioneers" VALUES(24,2,'24:2','Ҳар як аз зану марди зинокорро сад тозиёна бизанед ва агар ба Аллоҳ таоло ва рӯзи охират имон доред, набояд дар [иҷрои аҳкоми] дини Аллоҳ таоло нисбат ба он ду [нафар] дилсӯзӣ [ва меҳрубонии бе далел] дошта бошед ва бояд гурӯҳе аз муъминон бар муҷозоташон гувоҳ бошанд');
INSERT INTO "pioneers" VALUES(24,3,'24:3','Марди зинокор ҷуз бо зани зинокор ё мушрик издивоҷ намекунад ва зани зинокорро [низ] ҷуз марди зинокор ё мушрик ба издивоҷи худ дарнамеоварад ва ин [заношӯӣ] бар муъминон ҳаром аст');
INSERT INTO "pioneers" VALUES(24,4,'24:4','Ва касоне, ки [мардону] занони покдоманро [ба зино] муттаҳам мекунанд ва чаҳор шоҳид [бар иддаои худ] намеоваранд, ононро ҳаштод тозиёна бизанед ва ҳаргиз шаҳодаташонро [дар ҳеҷ мавриде] напазиред ва инонанд, ки нофармонанд');
INSERT INTO "pioneers" VALUES(24,5,'24:5','Магар касоне, ки баъд аз он [туҳмат] тавба кунанд ва [рафтори гузаштаи худро] ислоҳ [-у ҷуброн] намоянд, пас, бе тардид, Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(24,6,'24:6','Ва касоне, ки ҳамсарони худро [ба зино] муттаҳам мекунанд ва ҷуз хештан шоҳиде [бар ин иддао] надоранд, пас, ҳар як аз онон бояд чаҳор бор ба Аллоҳ таоло савганд ёд кунад, ки ҳатман, рост мегӯяд');
INSERT INTO "pioneers" VALUES(24,7,'24:7','Ва панҷумин бор [шавҳар бигӯяд], ки лаънати Аллоҳ таоло бар ӯ бод, агар дурӯғгӯ бошад');
INSERT INTO "pioneers" VALUES(24,8,'24:8','Ва кайфари зан дар сурате бардошта мешавад, ки чаҳор бор ба Аллоҳ таоло савганд ёд кунад, ки он мард ҳатман, дурӯғгӯ аст');
INSERT INTO "pioneers" VALUES(24,9,'24:9','Ва панҷумин бор [бигӯяд], ки хашми Аллоҳ таоло бар ӯ бод, агар [шавҳараш] рост гуфта бошад');
INSERT INTO "pioneers" VALUES(24,10,'24:10','Ва [эй мардум] агар бахшиши Аллоҳ таоло ва раҳматаш бар шумо набуд ва ин ки Аллоҳ таоло тавбапазири ҳаким аст [ҳатман, гирифтори азоб мешудед]');
INSERT INTO "pioneers" VALUES(24,11,'24:11','[Эй муъминон] Бе тардид, касоне, ки он туҳмати бузургро [дар бораи Оиша] матраҳ карданд, гурӯҳе аз худи шумо ҳастанд. Гумон накунед, ки ин [моҷаро] барои шумо шарр аст, балки хайре бароятон дар он [нуҳуфта] аст. Барои ҳар як аз онон [ки дар ин шоеапароканӣ нақш доштаанд] кайфари гуноҳест, ки муртакиб шудааст ва барои он касе аз эшон, ки бахши бузурги он [туҳмат]-ро ба уҳда дорад, азоби бузурге [дар пеш] аст');
INSERT INTO "pioneers" VALUES(24,12,'24:12','Чаро ҳангоме ки ин [туҳмат]-ро шунидед, мардону занони муъмин гумони нек ба худ набурданд ва нагуфтанд: «Ин туҳмате [бузургу] ошкор [ба ҳамсари паёмбар] аст?»');
INSERT INTO "pioneers" VALUES(24,13,'24:13','Чаро чаҳор шоҳид бар он наёварданд? Пас, чун шоҳидонро наёварданд, ононанд, ки назди Аллоҳ таоло дурӯғгӯ ҳастанд');
INSERT INTO "pioneers" VALUES(24,14,'24:14','Ва агар бахшиши Аллоҳ таоло ва раҳмати Ӯ дар дунёву охират набуд, ҳатман, ба кайфари он чи бад-он пардохтед, ба азобе [сахту] бузург гирифтор мешудед');
INSERT INTO "pioneers" VALUES(24,15,'24:15','Он гоҳ ки он [шоеа]-ро аз забони якдигар мешунидед ва суханеро даҳон ба даҳон нақл мекардед, ки дар борааш ҳеҷ огоҳӣ [ва яқинӣ] надоштед ва онро сода [ва кучак] мепиндоштед, дар ҳоле ки он [туҳмат] назди Аллоҳ таоло бисёр бузург аст');
INSERT INTO "pioneers" VALUES(24,16,'24:16','Чаро ҳангоме ки онро шунидед, нагуфтед: «Сазовор нест, ки ин [сухани бад]-ро бар забон овардем. [Парвардигоро] Ту муназзаҳӣ, ин дурӯғи бузурге аст?»');
INSERT INTO "pioneers" VALUES(24,17,'24:17','Аллоҳ таоло шуморо панд медиҳад, ки агар муъмин ҳастед, ҳаргиз ин [кор]-ро такрор накунед');
INSERT INTO "pioneers" VALUES(24,18,'24:18','Ва Аллоҳ таоло оёт [-и худ]-ро барои шумо баён мекунад ва Аллоҳ таоло донои ҳаким аст');
INSERT INTO "pioneers" VALUES(24,19,'24:19','Онон, ки дӯст доранд, байни касоне, ки имон овардаанд, зишткорӣ роиҷ гардад, ҳатман, дар дунё ва охират азобе дарднок [дар пеш] доранд ва Аллоҳ таоло [ҳақоиқу масолеҳро] медонад ва шумо намедонед');
INSERT INTO "pioneers" VALUES(24,20,'24:20','Ва агар бахшиши Аллоҳ таоло ва раҳматаш бар шумо набуд ва ин ки Аллоҳ таоло дилсӯзи меҳрубон аст, [ҳатман, муҷозоти сахте дар интизоратон буд]');
INSERT INTO "pioneers" VALUES(24,21,'24:21','Эй касоне, ки имон овардаед, дар роҳҳои шайтон гом нагузоред ва ҳар ки роҳҳои шайтонро мепӯяд, [бидонад, ки] ӯ танҳо ба зишткорӣ ва аъмоли нописанд фармон медиҳад ва агар бахшиши Аллоҳ таоло ва раҳматаш бар шумо набуд, ҳаргиз ҳеҷ як аз шумо [аз гуноҳ] пок намешуд, вале Аллоҳ таоло ҳар киро бихоҳад, пок мекунад ва Аллоҳ таоло шунавои доност');
INSERT INTO "pioneers" VALUES(24,22,'24:22','Афроде аз шумо, ки бартарии [имонӣ] ва вусъати [молӣ] доранд, набояд савганд ёд кунанд, ки ба хешовандон ва тиҳидастон ва муҳоҷирони дар роҳи Аллоҳ таоло [ки муртакиби гуноҳ шудаанд] чизе надиҳанд. Онон бояд афв кунанд ва [аз гуноҳи дигарон] чашм бипӯшанд. Оё дӯст намедоред, ки Аллоҳ таоло [низ] шуморо бибахшад? Ва Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(24,23,'24:23','Касоне, ки занони покдомани бехабар [аз фаҳшо ва] муъминро [ба зино] муттаҳам мекунанд, дар дунё ва охират аз раҳмати Аллоҳ таоло дур гаштаанд ва [дар охират] азобе бузург [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(24,24,'24:24','Рӯзе, ки забонҳо ва дастҳо ва пойҳояшон дар бораи он чи мекарданд [ва мегуфтанд], алайҳи онон гувоҳӣ медиҳанд');
INSERT INTO "pioneers" VALUES(24,25,'24:25','Он рӯз Аллоҳ таоло ҷазои воқеии ононро бекаму кост медиҳад ва хоҳанд донист, ки Аллоҳ таоло ҳамон ҳаққи ошкор аст');
INSERT INTO "pioneers" VALUES(24,26,'24:26','Занони палид барои мардони палиданд ва мардони палид [низ] барои занони палиданд. Ва занони пок аз они мардони поканд ва мардони пок [низ] аз они занони поканд. Инон аз он чи [нопокон дар бораашон] мегӯянд, мубарро ҳастанд [ва] омурзиши [илоҳӣ] ва рӯзии арзишманде [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(24,27,'24:27','Эй касоне, ки имон овардаед, ба хонаҳое ҷуз хонаҳои худ ворид нашавед, магар ин ки иҷозат бигиред ва бар аҳли он [хона] салом кунед. Ин [кор] барои шумо беҳтар аст, бошад, ки панд гиред');
INSERT INTO "pioneers" VALUES(24,28,'24:28','Пас, агар касеро дар он [хона] наёфтед, ворид нашавед, то ба шумо иҷозат дода шавад ва агар ба шумо гуфта шуд: «Бозгардед»! Пас, бозгардед! [Чаро ки] Ин [касби иҷозат] барои шумо шоистатар аст ва Аллоҳ таоло ба он чи мекунед доност');
INSERT INTO "pioneers" VALUES(24,29,'24:29','Бар шумо гуноҳе нест, ки ба хонаҳои ғайримаскунӣ [ва маконҳои умумӣ], ки дар он ҷо [эҳтиёҷ ё] баҳрае доред, ворид шавед ва Аллоҳ таоло он чи ошкор месозед ва он чи пинҳон медоред, [ҳамаро] медонад');
INSERT INTO "pioneers" VALUES(24,30,'24:30','[Эй паёмбар] Ба мардони муъмин бигӯ, чашмони худро [аз нигоҳ ба номаҳрам] фурӯ гиранд ва покдоманӣ пеша кунанд. Ин [кор] барои онон покизатар аст. Бе тардид, Аллоҳ таоло ба он чи анҷом медиҳанд, огоҳ аст');
INSERT INTO "pioneers" VALUES(24,31,'24:31','Ва ба занони муъмин [низ] бигӯ, чашмони худро [аз нигоҳ ба номаҳрам] фурӯ гиранд ва покдоманӣ варзанд ва зевар [-у ороиши] худро ошкор накунанд, магар он чи [ба сурати табиӣ] намоён аст. Ва бояд рӯсарии худро бар сина биафкананд [то мӯйҳо ва синаҳошон пӯшида гардад] ва зинати худро ошкор насозанд, магар барои шавҳар ё падар ё падаршавҳар ё писари худ ё писари шавҳарашон ё бародар ё писари бародар ё писари хоҳар ё [дигар] занон ё канизон ва бардагонашон ё хешовандоне, ки [дигар] ниёз ва тамоюле ба занон надоранд ё кӯдаконе, ки аз масоили ҷинсии занон огоҳӣ наёфтаанд. Ҳамчунин, [ҳангоми роҳ рафтан] пойҳои худро [чунон бар замин] накӯбанд, ки [садои] зиннатҳое, ки [зери либос] пинҳон кардаанд, [халхол] ошкор шавад. Ва эй муъминон, ҳамагӣ ба сӯи Аллоҳ таоло тавба кунед; бошад, ки растагор гардед');
INSERT INTO "pioneers" VALUES(24,32,'24:32','[Эй муъминон] Мардону занони муҷарради худро ҳамсар диҳед ва ғуломону канизонро, ки шоистагӣ доранд [низ ҳамсар диҳед]. Агар онон тиҳидаст бошанд, Аллоҳ таоло аз бахшиш [ва лутфи] хеш бениёзашон месозад ва Аллоҳ таоло кушоишгари огоҳ аст');
INSERT INTO "pioneers" VALUES(24,33,'24:33','Касоне, ки [шароиту имконоти] издивоҷ надоранд, бояд покдоманӣ пеша кунанд, то Аллоҳ таоло ононро аз бахшиши хеш бениёз намояд. Ва бардагоне, ки хостори бозхарид [ва озодии хеш] ҳастанд, агар дар онон [тавоноии пардохти мол ва] шоистагӣ мебинед, [дархосташонро] бипазиред ва аз сарвате, ки Аллоҳ таоло ба шумо ато кардааст, чизе ба онон бахшед [то ба озодиашон кумак кунед]. Ҳамчунин, канизонатонро, ки мехоҳанд покдоман бошанд, ба зино водор накунед, то баҳраи [ночиз] зиндагии дунёро ба даст оваред ва агар касе, ки ононро [ба ин кор] ворид намояд, Аллоҳ таоло пас аз маҷбур шуданашон, ҳатман, [нисбат ба онон] омурзанда [ва] меҳрубон аст');
INSERT INTO "pioneers" VALUES(24,34,'24:34','Ва ба ростӣ, ба сӯйи шумо оёте равшан ва достоне аз зиндагии пешиниёнатон ва панде барои парҳезгорон нозил кардаем');
INSERT INTO "pioneers" VALUES(24,35,'24:35','Аллоҳ таоло нури осмонҳо ва замин аст. Масали нури Ӯ ҳаммонанди чароғдонест, ки дарунаш чароғе [афрӯхта] аст [ва] он чароғ дар шишае қарор дорад [ва] он шиша гӯӣ ситорае дурахшон аст. Ин чароғ бо [равғани] дарахти пурбаракати зайтун афрӯхта мешавад, ки на шарқӣ аст ва на ғарбӣ [ва хуршед ҳамвора бар он метобад]. Равғанаш [чунон зулол аст, ки] наздик аст, бидуни тамос бо оташ шуълавар гардад. Нурест афзун бар нур. [Қалби муъмин низ бо нури ҳидоят ин гуна равшан мешавад] Ва Аллоҳ таоло ҳар киро бихоҳад, ба нури хеш ҳидоят мекунад ва Аллоҳ таоло барои мардум масалҳо мезанад ва Аллоҳ таоло ба ҳар чиз доност');
INSERT INTO "pioneers" VALUES(24,36,'24:36','[Ин чароғи ҳидоят] Дар масоҷидест, ки Аллоҳ таоло амр кардааст, то [қадру манзалатшон] гиромӣ дошта шавад ва номаш дар онҳо бурда шавад, [ва] дар он ҷо бомдодон ва шомгоҳон Ӯро ба покӣ биситоянд');
INSERT INTO "pioneers" VALUES(24,37,'24:37','Мардоне, ки ҳеҷ тиҷорат ва хариду фурӯше ононро аз ёди Аллоҳ таоло ва барпо доштани намоз ва пардохти закот ғофил намекунад, [ва] аз рӯзе, ки дилҳо ва чашмҳо дигаргун мешавад, бим доранд');
INSERT INTO "pioneers" VALUES(24,38,'24:38','То Аллоҳ таоло беҳтар аз он чи анҷом додаанд, ба онон подош диҳад ва аз фазли худ низ бар подошашон бияфзояд ва Аллоҳ таоло ба ҳар ки бихоҳад, беҳисоб рӯзӣ медиҳад');
INSERT INTO "pioneers" VALUES(24,39,'24:39','Ва касоне, ки куфр варзиданд, аъмолашон ҳамчун саробе дар биёбоне ҳамвор аст, ки [аз дур инсони] ташна онро об мепиндорад, то он ки наздикаш мешавад [ва] чизе намеёбад ва [аъмоли кофир низ саробест, ки суде барояш надорад ва дар иваз] Аллоҳро назди хеш меёбад, ки ҳисобашро ба таври комил медиҳад ва Аллоҳ таоло дар ҳисобрасӣ сареъ аст');
INSERT INTO "pioneers" VALUES(24,40,'24:40','Ё [аъмолаш] ҳамчун торикиҳое дар дарёи жарф аст, ки мавҷе онро мепӯшонад ва бар фарози он мавҷе дигар аст, [ва] бар фарозаш абре [торик] қарор дорад. Торикиҳое аст, яке бар фарози дигаре [чунон ки] агар [шахси гумгашта] дасти худро берун оварад, мумкин нест онро бубинад. Ва касе, ки Аллоҳ таоло нуре [аз ҳидоят] барояш қарор надода бошад, пас, [ҳеҷ ҳидоят ва] нуре барояш нест');
INSERT INTO "pioneers" VALUES(24,41,'24:41','Оё надидӣ, ҳар ки дар осмонҳо ва замин аст ва [ҳатто] паррандаҳои дар осмон паркушода [дар осмон ҳамагӣ] Аллоҳро ба покӣ меситоянд. Ҳар як аз онон ниёишу ситоиши хешро медонад ва Аллоҳ таоло ба он чи мекунанд, огоҳ аст');
INSERT INTO "pioneers" VALUES(24,42,'24:42','Ва фармонравоии осмонҳо ва замин аз они Аллоҳ таоло аст ва бозгашт [-и ҳама] ба сӯйи Аллоҳ таоло аст');
INSERT INTO "pioneers" VALUES(24,43,'24:43','Оё надидӣ, ки Аллоҳ таоло абрҳоеро ба оромӣ меронад, сипас байни [аҷзои] он пайванд медиҳад, он гоҳ тӯдаи анбӯҳе падид меоварад, пас, қатраҳои боронро мебинӣ, ки аз миёнаш таровиш мекунад ва аз кӯҳҳое [аз анбӯҳи абрҳо], ки дар осмон аст, донаҳои жола фурӯ меборад, пас, онро ба ҳар ки бихоҳад фурӯ мерезад ва аз ҳар ки бихоҳад бозмедорад. Наздик аст, дурахшандагии барқаш чашмҳоро [аз байн] барад');
INSERT INTO "pioneers" VALUES(24,44,'24:44','Аллоҳ таоло шабу рӯзро мегардонад [ва паёпай меоварад]. Яқинан, дар ин [гардиш] ибрате барои аҳли басират аст');
INSERT INTO "pioneers" VALUES(24,45,'24:45','Ва Аллоҳ таоло ҳар мавҷуди зиндаеро аз об офарид, пас, бархе аз онон бар шиками худ мераванд ва бархе бар ду по роҳ мераванд ва бархе аз онон чаҳор [по] роҳ мераванд. Аллоҳ таоло ҳар чи бихоҳад меофаринад. Бе тардид, Аллоҳ таоло бар ҳама чиз тавоност');
INSERT INTO "pioneers" VALUES(24,46,'24:46','Ба ростӣ, ки [Мо] оёти равшангаре нозил кардем ва Аллоҳ таоло ҳар киро бихоҳад, ба роҳи рост ҳидоят мекунад');
INSERT INTO "pioneers" VALUES(24,47,'24:47','Ва [мунофиқон] мегӯянд: «Ба Аллоҳ таоло ва паёмбар [-и Ӯ] имон овардем ва [аз фармонашон] итоат кардем», аммо пас аз ин [иддао] гурӯҳе аз онон рӯйгардон мешаванд ва инон [дар ҳақиқат] муъмин нестанд');
INSERT INTO "pioneers" VALUES(24,48,'24:48','Ва ҳангоме ки ба сӯйи Аллоҳ таоло ва паёмбараш фарохонда мешаванд, то дар миёнашон доварӣ кунад, ногоҳ гурӯҳе аз онон рӯйгардон мешаванд');
INSERT INTO "pioneers" VALUES(24,49,'24:49','Ва агар ҳақ ба онон бошад, мутеона [фурӯтанона] ба сӯйи ӯ меоянд');
INSERT INTO "pioneers" VALUES(24,50,'24:50','Оё дар дилҳояшон беморӣ аст ё [дар бораи рисолаташ] тардид доранд? Ё метарсанд, ки Аллоҳ таоло ва паёмбараш бар онон ситам кунанд? [На] Балки онон худ ситамгоранд');
INSERT INTO "pioneers" VALUES(24,51,'24:51','Сухани муъминон, ҳангоме ки ба сӯи Аллоҳ таоло ва паёмбараш фарохонда мешаванд, то миёнашон доварӣ кунад, фақат ин аст, ки мегӯянд: «Шунидем ва итоат кардем» ва инонанд, ки растагоранд');
INSERT INTO "pioneers" VALUES(24,52,'24:52','Ва ҳар ки аз Аллоҳ таоло ва паёмбараш итоат кунад ва аз Аллоҳ таоло битарсад ва аз [нофармонии] Ӯ бипарҳезад, пас, инон комёбанд');
INSERT INTO "pioneers" VALUES(24,53,'24:53','[Мунофиқон] Бо сахттарин савгандҳояшон ба Аллоҳ таоло савганд ёд карданд, ки агар ба онон фармон диҳӣ, [барои ҷиҳод] раҳсипор мешаванд. Бигӯ: «Савганд ёд накунед. Итоат [ва суханони дурӯғатон] шинохташуда аст. Бе тардид, Аллоҳ таоло аз он чи мекунед, огоҳ аст»');
INSERT INTO "pioneers" VALUES(24,54,'24:54','Бигӯ: «Аз Аллоҳ таоло ва паёмбар итоат кунед, пас, агар сарпечӣ кардед, паёмбар масъули чизест, ки бар ӯ таклиф шудааст [иблоғи ваҳй] ва шумо низ масъули чизе ҳастед, ки ба он таклиф шудаед; аммо агар аз ӯ итоат кунед, ҳидоят мешавед; ва паёмбар вазифае ҷуз расонидани ошкоро [-и ваҳй] надорад»');
INSERT INTO "pioneers" VALUES(24,55,'24:55','Аллоҳ таоло ба афроде аз шумо, ки имон овардаанд ва корҳои шоиста анҷом додаанд, ваъда додааст, ки ҳатман, ононро дар замин ҷонишин [-и мушрикон] месозад. Ҳамон гуна ки касонеро, ки пеш аз онон буданд, [низ] ҷонишин [-и кофирону ситамгарон] намуд ва [фармудааст] динашонро, ки барои онон писандидааст, барояшон устувор [-у пирӯзманд] созад ва тарсашонро ба оромишу амният табдил кунад [чаро ки] танҳо Маро мепарастанд ва чизеро бо Ман шарик намесозанд ва ҳар ки аз ин пас куфр биварзад, пас, инонанд, ки нофармонанд');
INSERT INTO "pioneers" VALUES(24,56,'24:56','Ва намоз барпо доред ва закот бипардозед ва аз паёмбар итоат кунед, бошад, ки мавриди раҳмат қарор гиред');
INSERT INTO "pioneers" VALUES(24,57,'24:57','[Эй паёмбар] Ҳаргиз мапиндор касоне, ки куфр варзиданд, нотавонкунанда [-и Аллоҳ таоло] дар заминанд [ва метавонанд аз муҷозоти илоҳӣ бигрезанд]. Ҷойгоҳи инон оташ аст ва чи бад бозгаштангоҳест!');
INSERT INTO "pioneers" VALUES(24,58,'24:58','Эй касоне, ки имон овардаед, бардагонатон ва касоне аз [кӯдакони] шумо, ки ба синни булуғ нарасидаанд, бояд дар се вақт аз шумо иҷозат [-и вуруд] бигиранд: пеш аз намози субҳ ва нимрӯз – ҳангоме ки либосҳоятонро берун меоваред – ва пас аз намози хуфтан. [Ин] Се [вақт] ҳангоми хилвати шумост. Ба ҷуз ин [cе вақт] бар шумо ва бар онон ҳеҷ гуноҳе нест [ки бидуни иҷозат ворид шаванд, чаро ки] онон перомунатон дар рафту омаданд, [ва] бо якдигар [муошират мекунед]. Аллоҳ таоло инчунин оёт [-и худ]-ро бароятон баён мекунад ва Аллоҳ таоло донои ҳаким аст');
INSERT INTO "pioneers" VALUES(24,59,'24:59','Ва ҳангоме ки кӯдаконатон ба синни булуғ расиданд, бояд [барои вуруд ба хонаҳои дигарон] иҷозат бигиранд, чунонки бузургсолоне, ки [аҳкомашонро] пеш аз онҳо [шарҳ додем низ] бояд иҷозат мегирифтанд. Аллоҳ таоло инчунин оёташро бароятон баён мекунад ва Аллоҳ таоло донои ҳаким аст');
INSERT INTO "pioneers" VALUES(24,60,'24:60','Ва бар занон [-и ёисаву пиразан ва] аз кор афтодае, ки [дигар майлу] умеди заношӯӣ надоранд, гуноҳе нест, ки пӯшиш [-и сару сурат]-и хешро канор гузоранд, [ба шарти он ки ороиш ва] зинатеро ошкор накунанд ва агар иффат биварзанд [ва худро бипӯшонанд] барояшон беҳтар аст. Ва Аллоҳ таоло шунавои доност');
INSERT INTO "pioneers" VALUES(24,61,'24:61','Бар [афроди] нобино ва лангу бемор [ки аз таколифи иҷтимоӣ – ҳамчун ҷиҳод – маоф шудаанд] гуноҳе нест ва [ҳамчунин] бар худи шумо гуноҳе нест, ки аз хонаҳои худ [ё фарзандону ҳамсаронатон ғизое] бихӯред ё аз хонаҳои падаронатон ё хонаҳои модаронатон ё хонаҳои бародаронатон ё хонаҳои хоҳаронатон ё хонаҳои амуҳоятон ё хонаҳои аммаҳоятон ё хонаҳои тағоҳоятон ё хонаҳои холаҳоятон ё хонае, ки калидаш дар ихтиёри шумост ё [аз хонаҳои] дӯстонатон. Бар шумо гуноҳе нест, ки дастаҷамъӣ ё ҷудогона [ғизо] бихӯред ва ҳангоме ки ба хонаҳо ворид мешавед, ба якдигар салом кунед, ки дуруде муборак [ва] писандида аз сӯйи Аллоҳ таоло аст. Аллоҳ таоло ин гуна оёти [хеш]-ро бароятон ба равшанӣ баён мекунад, бошад, ки биандешед');
INSERT INTO "pioneers" VALUES(24,62,'24:62','Муъминони [воқеӣ] касоне ҳастанд, ки ба Аллоҳ таоло ва Паёмбараш имон овардаанд ва ҳангоме ки барои [иҷрои як] амри иҷтимоӣ бо ӯ [паёмбар] ҳамроҳ ҳастанд, то аз вай иҷозат нагирифтаанд, [аз ҷамъ] хориҷ намешаванд. Бе тардид, касоне, ки аз ту иҷозат мегиранд, ононанд, ки ба Аллоҳу паёмбараш имон овардаанд. Пас, агар барои анҷоми бархе корҳо [-и муҳим]-и худ аз ту иҷозат мехоҳанд, ба ҳар як аз онон, ки мехоҳӣ иҷозат бидеҳ ва аз Аллоҳ таоло барояшон омурзиш бихоҳ. Ба ростӣ, ки Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(24,63,'24:63','Фарохонии паёмбар [барои ширкат дар корҳои гурӯҳе]-ро байни худ ҳамчун фарохонии якдигар талаққӣ накунед [ки битавонед онро бипазиред ё рад кунед]. Яқинан, Аллоҳ таоло афроде аз шуморо, ки пушти дигарон пинҳон мешаванд ва махфиёна [аз назди паёмбар] мегурезанд, [ба хубӣ] мешиносад. пас касоне, ки аз фармонаш сарпечӣ мекунанд, бояд битарсанд аз ин ки андуҳ ва балое бар сарашон биёяд ё ба азобе дарднок гирифтор шаванд');
INSERT INTO "pioneers" VALUES(24,64,'24:64','Огоҳ бошед! Бе тардид, он чи дар осмонҳо ва замин аст аз они Аллоҳ таоло аст ва яқинан, Ӯ медонад, ки шумо [бар чи кору] дар чи ҳоле ҳастед ва рӯзе, ки [бандагон] ба сӯяш бозгардонда мешаванд, ононро аз он чи [дар дунё] кардаанд, огоҳ месозад ва Аллоҳ таоло ба ҳар чизе доност');
INSERT INTO "pioneers" VALUES(25,1,'25:1','Пурбаракат [ва бузургвор] аст, касе, ки Қуръонро [ки ҷудокунандаи ҳақ ва ботил аст] бар бандааш нозил кард, то бимдиҳандаи ҷаҳониён бошад');
INSERT INTO "pioneers" VALUES(25,2,'25:2','Ҳамоно фармонравоии осмонҳо ва замин аз они Ӯст ва фарзанде [барои хеш] барнагузидааст ва дар фармонравоӣ ҳеҷ шарике надорад ва ҳама чизро офаридааст ва андозаи ҳар чизро – чунон ки муносибаш мебошад – тадбир намудааст');
INSERT INTO "pioneers" VALUES(25,3,'25:3','Ва [мушрикон] ба ҷойи Ӯ [Аллоҳ таоло] маъбудоне баргузидаанд, ки чизе намеофаринанд ва худ офарида мешаванд ва молики ҳеҷ зиёну суде барои худ нестанд ва [низ] молики маргу зиндагӣ ва [қодир ба] барангехтани [мурдагон] нестанд');
INSERT INTO "pioneers" VALUES(25,4,'25:4','Ва касоне, ки куфр варзиданд, гуфтанд: «Ин [Қуръон] ҷуз дурӯғе нест, ки [Муҳаммад] бофтааст ва гурӯҳе дигар ӯро бар ин [кор] ёрӣ додаанд». Ҳаққо, ки онон [бо ин сухан] ба ситаму дурӯғи бузурге рӯй овардаанд');
INSERT INTO "pioneers" VALUES(25,5,'25:5','Ва [кофирон] гуфтанд: «[Ин Қуръон ҳамон] афсонаҳои пешиниён аст, ки [Муҳаммад] онро рӯнависӣ кардааст ва ҳар бомдоду шомгоҳ бар ӯ имло мешавад»');
INSERT INTO "pioneers" VALUES(25,6,'25:6','Бигӯ: «Касе онро нозил кардааст, ки рози осмонҳо ва заминро медонад. Бе тардид, Ӯ ҳамвора омурзанда [ва] меҳрубон аст');
INSERT INTO "pioneers" VALUES(25,7,'25:7','Ва [мушрикон] гуфтанд: «Ин чи гуна паёмбарест, ки [ҳамчун мардуми одӣ] ғизо мехӯрад ва дар бозорҳо роҳ меравад? Чаро фариштае бар ӯ нозил нашудааст, то [ёриаш кунад ва] ҳамроҳи ӯ ҳушдордиҳанда бошад?');
INSERT INTO "pioneers" VALUES(25,8,'25:8','Ё чаро ганҷе [аз осмон] бар ӯ фуруд намеояд ва ё чаро боғе надорад, ки аз [меваҳои] он бихӯрад»? Ситамгорон [-и мушрик] гуфтанд: «Шумо ҷуз аз марде ҷодушуда пайравӣ намекунед»');
INSERT INTO "pioneers" VALUES(25,9,'25:9','Бингар, ки чи гуна бароят масалҳо заданд ва гумроҳ шуданд! Чунон ки наметавонанд роҳе [ба сӯи ҳақиқат] биёбанд');
INSERT INTO "pioneers" VALUES(25,10,'25:10','Пурбаракат [ва бузургвор] аст, касе, ки агар бихоҳад, бароят беҳтар аз ин қарор медиҳад: боғҳое, ки ҷӯйборҳо аз зери [дарахтони] он ҷорӣ аст ва [ҳатто] бароят кохҳое падид меоварад');
INSERT INTO "pioneers" VALUES(25,11,'25:11','[Мушрикон бо ин суханон дар пайи ҳақ нарафтанд] Балки қиёматро дурӯғ ангоштанд ва [Мо] барои ҳар ки қиёматро дурӯғ ангорад, оташи сӯзон [-и дузахро] муҳайё кардаем');
INSERT INTO "pioneers" VALUES(25,12,'25:12','Ҳангоме ки [дузах] ононро аз дурдаст мебинад овози хашму хуруш [-и ваҳшатнок]-е аз он мешунаванд');
INSERT INTO "pioneers" VALUES(25,13,'25:13','Ва чун даступобаста [бо ғулу занҷир] дар тангное аз он андохта шаванд, дар он ҷо маргашонро ба фарёд мехоҳанд');
INSERT INTO "pioneers" VALUES(25,14,'25:14','[Ба онон гуфта мешавад] «Имрӯз на як бор, балки борҳо дархости марг кунед»');
INSERT INTO "pioneers" VALUES(25,15,'25:15','[Эй паёмбар, ба кофирон] Бигӯ: «Ин [азоби дарднок] беҳтар аст ё биҳишти ҷовидонӣ, ки ба парҳезкорон ваъда дода шуда ва подошу саранҷоми онон аст?»');
INSERT INTO "pioneers" VALUES(25,16,'25:16','Дар он ҷо ҳар чи бихоҳанд, барояшон [фароҳам] аст [ва] дар он ҷовидонанд. Ин ваъдае аст бар [уҳдаи] Парвардигорат, ки [бандагони парҳезкор анҷомашро] аз Ӯ хостаанд');
INSERT INTO "pioneers" VALUES(25,17,'25:17','Ва рӯзе, ки [ҳамаи] онон ва он чиро, ки ба ҷойи Аллоҳ таоло мепарастиданд, ҷамъ мекунад ва [ба маъбудони ботил] мефармояд: «Оё шумо ин бандагони Маро гумроҳ кардед ё худ роҳро гум карданд?»');
INSERT INTO "pioneers" VALUES(25,18,'25:18','Онон мегӯянд: «Ту муназзаҳӣ, шоистаи мо набуд, ки ба ҷойи Ту [дӯстону] корсозоне баргузинем, вале Ту онон ва падаронашонро [аз неъматҳо] баҳраманд сохтӣ, то ин ки ёд [-и Ту]-ро фаромӯш карданд ва [бад-ин хотир] гурӯҳе ҳалокшуда гаштанд»');
INSERT INTO "pioneers" VALUES(25,19,'25:19','[Аллоҳ таоло ба мушрикон мефармояд] «Бе тардид, [маъбудонатон] иддаои шуморо дурӯғ шумурданд. Пас, [акнун] наметавонед [азобро аз худ] дур кунед ва на [аз касе] ёрӣ биҷӯед ва ҳар як аз шумо, ки [ширк биварзад ва ба худ] ситам кунад, азобе бузург ба вай мечашонем»');
INSERT INTO "pioneers" VALUES(25,20,'25:20','[Эй паёмбар] Пеш аз ту паёмбаронро нафиристодем, магар ин ки онон низ ғизо мехӯрданд ва [ҳамчун дигар инсонҳо] дар бозорҳо роҳ мерафтанд ва мо бархе аз шуморо василаи озмоиши бархе дигар сохтем, [то равшан шавад] оё шикебоӣ мекунед. Ва Парвардигорат ҳамвора биност');
INSERT INTO "pioneers" VALUES(25,21,'25:21','Касоне, ки ба дидори Мо [дар охират] умед надоранд, гуфтанд: «Чаро фариштагоне бар мо нозил нашудаанд, ё [чаро] Парвардигорамонро намебинем»? Бе тардид, онон дар бораи худ такаббур варзиданд ва саркашии бузург карданд');
INSERT INTO "pioneers" VALUES(25,22,'25:22','Рӯзе, ки [кофирон] фариштагонро мебинанд, ҳеҷ башорате барои муҷримон нахоҳад буд, [балки рӯзи кайфару азоб аст] ва [фариштагон] мегӯянд: «[Биҳишт] Бар шумо мамнуъ [ва ҳаром] аст»');
INSERT INTO "pioneers" VALUES(25,23,'25:23','Ва ба [баррасии] корҳояшон мепардозем ва ҳамаро [ҳамчун] ғуборе пароканда месозем');
INSERT INTO "pioneers" VALUES(25,24,'25:24','Дар он рӯз биҳиштиён беҳтарин ҷойгоҳу накутарин истироҳатгоҳро доранд');
INSERT INTO "pioneers" VALUES(25,25,'25:25','Ва [ёд кун аз] рӯзе, ки осмон бо абрҳои сапед шикофта мешавад ва фариштагон [паёпай ба сарзамини маҳшар] фурӯ фиристода мешаванд');
INSERT INTO "pioneers" VALUES(25,26,'25:26','Дар он рӯз фармонравоӣ [-и ростин] аз они [Аллоҳи] Раҳмон аст ва барои кофирон рӯзе душвор аст');
INSERT INTO "pioneers" VALUES(25,27,'25:27','Ва [ёд кун аз] рӯзе, ки ситамгор дастонашро [аз ҳасрат] мегазад ва мегӯяд: «Эй кош, ман роҳе [ҳамоҳанг] бо паёмбар баргузида будам!');
INSERT INTO "pioneers" VALUES(25,28,'25:28','Эй вой бар ман! Эй кош, фалон [кофир]-ро ба дӯстӣ намегирифтам!');
INSERT INTO "pioneers" VALUES(25,29,'25:29','Бе тардид, пас аз он ки Қуръон [ва даъвати паёмбар] ба ман расид, ӯ [буд, ки] маро ба гумроҳӣ кашонд ва шайтон ҳамвора инсонро танҳо [ва хор] вомегузорад»');
INSERT INTO "pioneers" VALUES(25,30,'25:30','Ва Паёмбар гуфт: «Парвардигоро, қавми ман ин Қуръонро раҳо кардаанд»');
INSERT INTO "pioneers" VALUES(25,31,'25:31','Ва инчунин барои ҳар паёмбаре душмане аз бадкорони [қавмаш] қарор додем. Ва кофӣ аст, ки Парвардигорат роҳнамо ва ёварат бошад');
INSERT INTO "pioneers" VALUES(25,32,'25:32','Ва касоне, ки куфр варзиданд, гуфтанд: «Чаро Қуръон якбора бар ӯ нозил нашудааст?» Мо ин гуна [каломи илоҳиро ба мурур нозил] кардаем, то дилатро бо он [ором ва] устувор гардонем ва онро ба тадриҷ бар ту хондаем [то дарку ҳифзаш осон бошад]');
INSERT INTO "pioneers" VALUES(25,33,'25:33','Ва [кофирон] ҳеҷ [эътирозу] масале бароят намеоваранд, магар он ки Мо [низ посухе] бар ҳақ ва хушбаёнтар бароят меоварем');
INSERT INTO "pioneers" VALUES(25,34,'25:34','Касоне, ки ба рӯй дарафтода ба дузах кашонда мешаванд, инон бадҷойгоҳтар ва гумроҳтаранд');
INSERT INTO "pioneers" VALUES(25,35,'25:35','Ва дар ҳақиқат, Мо ба Мӯсо китоб [Таврот] додем ва бародараш Ҳорунро дастёраш сохтем');
INSERT INTO "pioneers" VALUES(25,36,'25:36','Ва гуфтем: «Шумо ду нафар ба сӯи қавме биравед, ки оёти Моро дурӯғ ангоштанд». Пас, [чун фиръавниён сарпечӣ намуданд] ба шиддат нобудашон сохтем');
INSERT INTO "pioneers" VALUES(25,37,'25:37','Ва қавми Нуҳро – он гоҳ ки паёмбаронро дурӯғгӯ ангоштанд – ғарқ кардем ва ононро барои мардум [нишона ва] ибрате қарор додем; ва барои ситамгорон азобе дарднок муҳайё кардаем');
INSERT INTO "pioneers" VALUES(25,38,'25:38','Ва [ҳамчунин] қавми Од ва Самуд ва аҳолии "чоҳ" ва наслҳои бисёреро, ки байни онон буданд [нобуд сохтем]');
INSERT INTO "pioneers" VALUES(25,39,'25:39','Ва барои ҳар як аз онҳо масалҳо задем ва [чун панд нагирифтанд] ҳамаро ба шиддат аз миён бурдем');
INSERT INTO "pioneers" VALUES(25,40,'25:40','Ва [мушрикон] ҳатман, аз [сарзамини қавми Лут ҳамон] шаҳре, ки борони бало бар он борида буд, гузар кардаанд. Оё он [вайронаҳои боқимонда аз азоб] -ро надидаанд? [Чунин нест] Балки ба зинда шудан [пас аз марг] умеде надоранд');
INSERT INTO "pioneers" VALUES(25,41,'25:41','Ва ҳангоме ки туро мебинанд, фақта ба тамасхур мегиранд [ва мегӯянд] «Оё ин ҳамон касест, ки Аллоҳ таоло ба паёмбарӣ фиристодааст?');
INSERT INTO "pioneers" VALUES(25,42,'25:42','Агар бар [парастиши] маъбудонамон [шикебоӣ ва] пойдорӣ намекардем, наздик буд [Муҳаммад] аз [роҳи] онҳо гумроҳамон кунад». Вале он гоҳ ки азоб [-и илоҳӣ]-ро бубинанд, хоҳанд донист чи касе гумроҳтар [буда] аст');
INSERT INTO "pioneers" VALUES(25,43,'25:43','Оё касе, ки ҳавои нафсашро маъбуди худ кардааст, дидаӣ? Оё ту [метавонӣ] нигаҳбони [имони] ӯ бошӣ?');
INSERT INTO "pioneers" VALUES(25,44,'25:44','Оё гумон дорӣ, ки бештарашон мешунаванд ё мефаҳманд? Онон ҷуз ҳаммонанди чаҳорпоён нестанд, балки гумроҳтаранд');
INSERT INTO "pioneers" VALUES(25,45,'25:45','Оё [офариниши] Парвардигоратро надидаӣ, ки чи гуна сояро густаронидааст ва агар мехост, онро собит менамуд? Он гоҳ [тағйири ҷойгоҳи] хуршед [нисбат ба замин]-ро далели [густаришу ҳаракати] он [соя] қарор додем');
INSERT INTO "pioneers" VALUES(25,46,'25:46','Сипас [бо тағйири баландии хуршед сояро кӯтоҳ мекунем ва] онро андак-андак ба сӯйи хеш бозмегирем');
INSERT INTO "pioneers" VALUES(25,47,'25:47','Ва Ӯст, ки шабро [бо торикии фарогираш] пӯшише барои шумо қарор дод ва хобро мояи осоиш ва рӯзро ҳангоми бархостан [ва кору талош] сохт');
INSERT INTO "pioneers" VALUES(25,48,'25:48','Ва Ӯст, ки пеш аз [борони] раҳматаш бодҳоро башоратбахш фиристод ва аз осмон обе пок [ва поккунанда] фурӯ фиристодем');
INSERT INTO "pioneers" VALUES(25,49,'25:49','То бо он сарзамини [хушку] мурдаро зинда гардонем ва чаҳорпоён ва инсонҳои бисёреро, ки офаридаем, сероб созем');
INSERT INTO "pioneers" VALUES(25,50,'25:50','Ва ба ростӣ, он [далоилу нишонаҳои қудрати илоҳӣ]-ро ба суратҳои гуногун баён кардем, то панд гиранд, вале бештар мардум ҷуз [инкору] носипосӣ накарданд');
INSERT INTO "pioneers" VALUES(25,51,'25:51','Ва агар мехостем, дар ҳар диёре бимдиҳандае бармеангехтем');
INSERT INTO "pioneers" VALUES(25,52,'25:52','Пас, [эй паёмбар] аз кофирон итоат накун ва ба василаи Қуръон бо онон ба ҷиҳоди бузурге бархез [ва дар ин роҳ шикебо бош]');
INSERT INTO "pioneers" VALUES(25,53,'25:53','Ва Ӯст, ки ду дарёро ба ҳам омехт: ин яке хушгувор ва ширин аст ва он яке шӯру талх ва дар миёни он ду монеаи нуфузнопазире қарор дод [то бо ҳам наёмезанд]');
INSERT INTO "pioneers" VALUES(25,54,'25:54','Ва Ӯст, ки инсонро аз оби [манӣ] офарид ва ӯро дорои пайванди насабӣ ва сабабӣ гардонид ва Парвардигорат ҳамвора тавоност');
INSERT INTO "pioneers" VALUES(25,55,'25:55','Ва [мушрикон] ба ҷойи Аллоҳ таоло чизеро мепарастанд, ки на ба онон суде мебахшад ва на зиёне мерасонад ва кофир дар баробари Парвардигораш пайваста пуштибон [-и шайтон] аст');
INSERT INTO "pioneers" VALUES(25,56,'25:56','Ва [эй паёмбар] Мо туро ҷуз башоратбахш ва бимдиҳанда нафиристодем');
INSERT INTO "pioneers" VALUES(25,57,'25:57','Бигӯ: «Ман [дар баробари рисолатам] ҳеҷ подоше аз шумо намехоҳам, магар касе, ки бихоҳад роҳе ба сӯи Парвардигораш баргузинад [ва ҳамин бароям кофӣ аст]»');
INSERT INTO "pioneers" VALUES(25,58,'25:58','Ва бар [Парвардигори] зиндае, ки ҳаргиз намемирад, таваккал кун ва бо cитоиш Ӯро ба покӣ ёд кун. Ва ҳамин бас, ки Ӯ ба гуноҳони бандагонаш огоҳ аст');
INSERT INTO "pioneers" VALUES(25,59,'25:59','[Ҳамон] Касе, ки осмонҳо ва замин ва он чиро миёни онҳост дар шаш рӯз офарид, сипас бар Арш қарор гирифт. [Ӯ Аллоҳи] Раҳмон аст, пас, дар мавриди Ӯ аз он ки огоҳ аст, бипурс [ва Ӯ низ касе ҷуз Аллоҳ таоло нест]');
INSERT INTO "pioneers" VALUES(25,60,'25:60','Ва ҳангоме ки ба ба онон гуфта шавад: «Барои [Аллоҳи] Раҳмон саҷда кунед», мегӯянд: «Раҳмон кист? Оё [қарор аст] бар ҳар чи ту амр мекунӣ саҷда барем»? Ва [ин даъвати ту дурӣ аз ҳақ ва] нафраташонро меафзояд');
INSERT INTO "pioneers" VALUES(25,61,'25:61','Пурбаракат [ва бузургвор] аст, он ки дар осмон манзилгоҳҳое [барои ситорагон] қарор дод ва дар [миёни] онҳо хуршед [-и дурахшон] ва моҳи тобоне падид овард');
INSERT INTO "pioneers" VALUES(25,62,'25:62','Ва Ӯст, ки шабу рӯзро дар пайи якдигар қарор дод, барои ҳар ки бихоҳад [аз тадбири илоҳӣ] панд гирад ё сипосгузор бошад');
INSERT INTO "pioneers" VALUES(25,63,'25:63','Ва бандагони [муъмину шоистаи] Раҳмон касоне ҳастанд, ки бо фурӯтанӣ [ва матонат] бар замин роҳ мераванд ва ҳангоме ки ҷоҳилон [бо суханони нописанд] хитобашон мекунанд, [ба ҷои талофӣ бо онон] ба некӣ сухан мегӯянд');
INSERT INTO "pioneers" VALUES(25,64,'25:64','Ва касоне барои Парвардигорашон дар ҳоли саҷда ва қиём шабзиндадорӣ мекунанд');
INSERT INTO "pioneers" VALUES(25,65,'25:65','Ва касоне, ки мегӯянд: «Парвардигоро, азоби дузахро аз мо бозгардон, [ки] бе тардид, азобаш [сахту] пойдор аст');
INSERT INTO "pioneers" VALUES(25,66,'25:66','Ҳаққо, ки дузах бад ҷойгоҳ ва иқоматгоҳест');
INSERT INTO "pioneers" VALUES(25,67,'25:67','Ва [муъминон] касоне ҳастанд, ки чун инфоқ менамоянд, на зиёдаравӣ мекунанд ва на сахтгирӣ ва байни ин ду [ҳолат роҳи] эътидол пеш мегиранд');
INSERT INTO "pioneers" VALUES(25,68,'25:68','Ва касоне ҳастанд, ки бо Аллоҳ таоло маъбуде дигар намехонанд ва касе, ки Аллоҳ таоло [куштанашро] ҳаром кардааст, ҷуз ба ҳақ намекушанд ва зино намекунанд. Ва ҳар ки ин [гуноҳон]-ро муртакиб шавад, кайфари гуноҳи [хеш]-ро мебинад');
INSERT INTO "pioneers" VALUES(25,69,'25:69','Рӯзи қиёмат азобаш дучандон мегардад ва бо хорӣ дар он [азоб] ҷовидон мемонад');
INSERT INTO "pioneers" VALUES(25,70,'25:70','Магар касе, ки тавба кунад ва имон оварад ва коре шоиста анҷом диҳад. Пас, инонанд, ки Аллоҳ таоло бадиҳояшонро ба некиҳо табдил мекунад ва Аллоҳ таоло ҳамвора омурзгору меҳрубон аст');
INSERT INTO "pioneers" VALUES(25,71,'25:71','Ва касе, ки тавба кунад ва коре шоиста анҷом диҳад, пас, яқинан ба даргоҳи Аллоҳ таоло – чунон ки бояду шояд – тавба кардааст');
INSERT INTO "pioneers" VALUES(25,72,'25:72','Ва касоне [муъминанд], ки гувоҳии дурӯғ намедиҳанд [ва дар маҷолиси ботил ҳузур намеёбанд] ва чун ба [кори нописанд ё] беҳудае бархӯрд мекунанд, бузургворона [аз канораш] мегузаранд [ва худро олуда намесозанд]');
INSERT INTO "pioneers" VALUES(25,73,'25:73','Ва касоне, ки чун ба оёти Парвардигорашон панд дода мешаванд, [аз он ғафлат намекунанд ва дар баробари Қуръон] чун карону кӯрон нестанд');
INSERT INTO "pioneers" VALUES(25,74,'25:74','Ва касоне, ки мегӯянд: «Парвардигоро, аз ҳамсарону фарзандонамон [касонеро] ба мо ато фармо, ки [мояи шодии дилҳо ва] равшании чашмҳо бошанд ва моро пешвои парҳезкорон қарор бидеҳ»');
INSERT INTO "pioneers" VALUES(25,75,'25:75','[Оре] Инон ба поси он ки шикебоӣ кардаанд, манозил [-и олии биҳишт]-ро подош мегиранд ва дар он ҷо бо дуруду саломи [фариштагон] рӯ ба рӯ мегарданд');
INSERT INTO "pioneers" VALUES(25,76,'25:76','Дар он ҷо ҷовидонанд. Чи наку ҷойгоҳ ва [чи нек] иқоматгоҳе аст!');
INSERT INTO "pioneers" VALUES(25,77,'25:77','[Ба кофирон] бигӯ: «Агар ибодат ва дуоятон набошад, Парвардигорам эътиное ба шумо надорад [ва арзише бароятон қоил нест]. Ба ростӣ, шумо [даъвати ҳақро] дурӯғ ангоштед, пас, ба зудӣ [кайфари ин кор] гиребонгиратон хоҳад шуд');
INSERT INTO "pioneers" VALUES(26,1,'26:1','То, син, мим');
INSERT INTO "pioneers" VALUES(26,2,'26:2','Ин оёти китоби равшангар аст');
INSERT INTO "pioneers" VALUES(26,3,'26:3','Шояд мехоҳӣ аз [андуҳи] ин ки [мушрикон] имон намеоваранд, худро ҳалок кунӣ');
INSERT INTO "pioneers" VALUES(26,4,'26:4','Агар бихоҳем, аз осмон нишонае бар онон нозил мекунем, ки дар баробараш бо фурӯтанӣ сар фуруд оваранд');
INSERT INTO "pioneers" VALUES(26,5,'26:5','Ва ҳеҷ панди тозае аз сӯи [Аллоҳи] Раҳмон барои онон намеояд, магар ин ки аз он рӯй гардонанд');
INSERT INTO "pioneers" VALUES(26,6,'26:6','Ба ростӣ, онон [даъвати паёмбарон ва азоби қиёматро] дурӯғ ангоштанд, пас, ба зудӣ ахбори он чи масхара мекарданд, ба эшон хоҳад расид');
INSERT INTO "pioneers" VALUES(26,7,'26:7','Оё ба замин наменигаранд, ки чи бисёр аз ҳар гуна [гиёҳи] судманде дар он рӯёнидаем?');
INSERT INTO "pioneers" VALUES(26,8,'26:8','Бе тардид, дар ин [офариниш] нишонае [аз қудрати Аллоҳ таоло бар меод] аст, вале бештарашон имон намеоваранд');
INSERT INTO "pioneers" VALUES(26,9,'26:9','Ва бе тардид, Парвардигорат шикастнопазири меҳрубон аст');
INSERT INTO "pioneers" VALUES(26,10,'26:10','Ва [ёд кун аз] ҳангоме ки Парвардигорат Мӯсоро нидо дод, ки: «Ба сӯйи қавми ситамгор бирав');
INSERT INTO "pioneers" VALUES(26,11,'26:11','– қавми Фиръавн - [ва ба онон бигӯ] оё [аз куфру саркашӣ] намепарҳезанд?»');
INSERT INTO "pioneers" VALUES(26,12,'26:12','[Мӯсо] Гуфт: «Парвардигоро, бим дорам, ки маро дурӯғгӯ ангоранд');
INSERT INTO "pioneers" VALUES(26,13,'26:13','Ва [аз куфрашон ношикебо ва] ғамгин гардам. Пас, [Ҷабраилро] ба сӯйи Ҳорун бифрист [то барои ёриам раҳсипораш кунад]');
INSERT INTO "pioneers" VALUES(26,14,'26:14','Ва онон [ба гумони хеш қасоси] гуноҳе бар ман доранд, пас, метарсам, ки маро бикушанд»');
INSERT INTO "pioneers" VALUES(26,15,'26:15','[Аллоҳ таоло] Фармуд: «Ҳаргиз! Пас бо оёт [ва муъҷизот]-и Мо биравед. Яқинан, [ҳама ҷо] бо шумоем ва [гуфтугӯятонро] мешунавем');
INSERT INTO "pioneers" VALUES(26,16,'26:16','Пас, назди Фиръавн биравед ва бигӯед: «Мо фиристодаи Парвардигори ҷаҳониён ҳастем');
INSERT INTO "pioneers" VALUES(26,17,'26:17','[Ӯ мефармояд] Ки Бани Исроилро ҳамроҳи мо бифирист»');
INSERT INTO "pioneers" VALUES(26,18,'26:18','[Фиръавн] гуфт: «Оё туро дар кӯдакӣ миёни хеш парвариш надодем ва солҳое аз умратро байни мо набудӣ?');
INSERT INTO "pioneers" VALUES(26,19,'26:19','Ва кори худро – чунон ки хостӣ – кардӣ [ва он марди қибтиро куштӣ] ва ту [нисбат ба неъматҳои ман] носипосӣ?!»');
INSERT INTO "pioneers" VALUES(26,20,'26:20','[Мӯсо] гуфт: «Ман он [кор]-ро замоне анҷом додам, ки гумроҳ будам');
INSERT INTO "pioneers" VALUES(26,21,'26:21','Пас, чун аз шумо тарсидам, фирор кардам. Он гоҳ Парвардигорам ба ман дониш бахшид ва маро аз паёмбарон қарор дод');
INSERT INTO "pioneers" VALUES(26,22,'26:22','Ва ин [чи] неъматест, ки ту бар ман миннат мегузорӣ, ки Бани Исроилро бардаи худ сохтаӣ?!»');
INSERT INTO "pioneers" VALUES(26,23,'26:23','Фиръавн гуфт: «Парвардигори ҷаҳониён кист?»');
INSERT INTO "pioneers" VALUES(26,24,'26:24','[Мӯсо] Гуфт: «Парвардигори осмонҳо ва замин ва он чи миёни онҳост. Агар аҳли яқин ҳастед [танҳо ӯро бипарастед]»');
INSERT INTO "pioneers" VALUES(26,25,'26:25','[Фиръавн] Ба касоне, ки дар атрофаш буданд [бо тамасхур] гуфт: «Оё мешунавед [чи мегӯяд]?»');
INSERT INTO "pioneers" VALUES(26,26,'26:26','[Мӯсо] Гуфт: «[Аллоҳ таоло] Парвардигори шумо ва Парвардигори ниёгони шумост');
INSERT INTO "pioneers" VALUES(26,27,'26:27','[Фиръавн] Гуфт: «Ин паёмбаратон, ки ба сӯйи шумо фиристода шудааст, яқинан девона аст»');
INSERT INTO "pioneers" VALUES(26,28,'26:28','[Мӯсо] Гуфт: «Агар биандешед, [хоҳед донист, ки Ӯ] Парвардигори машриқ ва мағриб аст ва ҳар он чи миёни он ду қарор дорад»');
INSERT INTO "pioneers" VALUES(26,29,'26:29','[Фиръавн] Гуфт: «Агар маъбуде ҷуз ман баргузинӣ, ҳатман, зиндонат мекунам»');
INSERT INTO "pioneers" VALUES(26,30,'26:30','[Мӯсо] Гуфт: «Ҳатто агар нишонаи ошкоре [дар мавриди рисолатам] бароят оварда бошам?»');
INSERT INTO "pioneers" VALUES(26,31,'26:31','[Фиръавн] Гуфт: «Агар рост мегӯӣ, онро биёвар»');
INSERT INTO "pioneers" VALUES(26,32,'26:32','Пас, [Мӯсо] асои худро андохт, ногаҳон [табдил ба] аждаҳое ошкор шуд');
INSERT INTO "pioneers" VALUES(26,33,'26:33','Ва дасташро [аз гиребон] берун овард ва ногоҳ дар назари бинандагон сапед [-у равшан] буд');
INSERT INTO "pioneers" VALUES(26,34,'26:34','[Фиръавн] ба бузургони перомунаш гуфт: «Ҳаққо, ки ин [мард] ҷодугари доност');
INSERT INTO "pioneers" VALUES(26,35,'26:35','[Ӯ] Мехоҳад, бо ҷодуяш шуморо аз сарзаминатон берун кунад, пас, назаратон чист?»');
INSERT INTO "pioneers" VALUES(26,36,'26:36','Онон гуфтанд: «[Тасмим дар бораи муҷозоти] ӯ ва бародарашро ба таъхир андоз ва маъмурони ҷамъоварӣ [ҷорзанандагон]-ро ба [ҳамаи] шаҳрҳо бифирист,');
INSERT INTO "pioneers" VALUES(26,37,'26:37','то ҳар ҷодугари моҳир [ва] доноеро наздат биёваранд»');
INSERT INTO "pioneers" VALUES(26,38,'26:38','Пас, [чунин карданд ва саранҷом] ҷодугарон барои ваъдагоҳи рӯзи муайян гирд оварда шуданд');
INSERT INTO "pioneers" VALUES(26,39,'26:39','Ва ба мардум гуфта шуд: «Оё шумо [низ] ҷамъ мешавед, то [бубинем баранда кист]?');
INSERT INTO "pioneers" VALUES(26,40,'26:40','Агар ҷодугарон пирӯз шуданд, [ҳамагӣ] аз онон пайравӣ кунем»');
INSERT INTO "pioneers" VALUES(26,41,'26:41','Пас, вақте ҷодугарон омаданд, ба Фиръавн гуфтанд: «Агар мо пирӯз шавем, оё подоше хоҳем дошт»?');
INSERT INTO "pioneers" VALUES(26,42,'26:42','Ӯ гуфт: «Оре, дар он сурат, ҳатман, аз наздиконам хоҳед буд»');
INSERT INTO "pioneers" VALUES(26,43,'26:43','Мӯсо ба онон гуфт: «Ҳар чи [мехоҳед] биафканед, [акнун] биафканед»');
INSERT INTO "pioneers" VALUES(26,44,'26:44','Пас, онон ресмонҳо ва асоҳояшонро афканданд ва гуфтанд: «Ба иззати Фиръавн савганд, ки мо, ҳатман, пирӯзем»');
INSERT INTO "pioneers" VALUES(26,45,'26:45','Сипас Мӯсо асояшро афканд, ногаҳон [табдил ба аждаҳое шуд ва] он чиро, ки ба дурӯғ сохта буданд, балъид [фуру бурд]');
INSERT INTO "pioneers" VALUES(26,46,'26:46','Пас, ҷодугарон ба саҷда дарафтоданд');
INSERT INTO "pioneers" VALUES(26,47,'26:47','[Ва] Гуфтанд: «Мо ба Парвардигори ҷаҳониён имон овардем');
INSERT INTO "pioneers" VALUES(26,48,'26:48','Парвардигори Мӯсо ва Ҳорун»');
INSERT INTO "pioneers" VALUES(26,49,'26:49','[Фиръавн] Гуфт: «Оё пеш аз он ки ба шумо иҷозат диҳам, ба ӯ имон овардед? Бе тардид, ӯ ҳамон бузурги шумост, ки ба шумо ҷоду омӯхтааст. Пас, ба зудӣ хоҳед донист [ки муҷозотатон чист]. Яқинан, дастҳо ва пойҳоятонро баръакс [-и якдигар аз чапу рост] қатъ мекунам ва ҳамагиатонро ба дор меовезам»');
INSERT INTO "pioneers" VALUES(26,50,'26:50','Онон гуфтанд: «[Дар ин шиканҷа ва азоб] Ҳеҷ зиёне нест. Бе тардид, мо ба сӯйи Парвардигорамон бозмегардем');
INSERT INTO "pioneers" VALUES(26,51,'26:51','Умед дорем, ки Парвардигорамон гуноҳони моро бубахшад, ки мо нахустин касоне ҳастем, ки [аз ин қавм ба Парвардигори Мӯсо] имон овардем»');
INSERT INTO "pioneers" VALUES(26,52,'26:52','Ва ба Мӯсо ваҳй кардем, ки: «Бандагонамро шабона [аз Миср] берун бибар; [зеро сипоҳи Фиръавн] ҳатман, ба дунболатон хоҳад омад»');
INSERT INTO "pioneers" VALUES(26,53,'26:53','Пас Фиръавн [чун аз моҷаро огоҳ гардид] маъмурони ҷамъоварӣ [неру]-ро ба шаҳрҳо фиристод');
INSERT INTO "pioneers" VALUES(26,54,'26:54','[Ва эълон кард] Инҳо [Бани Исроил] иддаи ночизанд');
INSERT INTO "pioneers" VALUES(26,55,'26:55','Ва ҷиддан моро ба хашм овардаанд');
INSERT INTO "pioneers" VALUES(26,56,'26:56','Ва мо ҳамагӣ комилан [ҳушёру] омодаи пайкорем»');
INSERT INTO "pioneers" VALUES(26,57,'26:57','Пас, ононро аз боғҳо ва чашмаҳо [-и сарзамини Миср] берун овардем');
INSERT INTO "pioneers" VALUES(26,58,'26:58','Ва аз ганҷҳо ва хонаҳои муҷаллал [дур сохтем]');
INSERT INTO "pioneers" VALUES(26,59,'26:59','Инчунин [кардем] ва Бани Исроилро вориси он [сарвату дороӣ] кардем');
INSERT INTO "pioneers" VALUES(26,60,'26:60','Пас, [сипоҳи Фиръавн] ҳангоми тулуи офтоб ба таъқиби онон пардохтанд');
INSERT INTO "pioneers" VALUES(26,61,'26:61','Ва чун ду гурӯҳ якдигарро диданд, ёрони Мӯсо гуфтанд: «Ҳатман, [дар чанголи фиръавниён] гирифторем»');
INSERT INTO "pioneers" VALUES(26,62,'26:62','[Мӯсо] Гуфт: «Ҳаргиз! Бе тардид, Парвардигорам бо ман аст ва маро [ба роҳи наҷот] ҳидоят хоҳад кард»');
INSERT INTO "pioneers" VALUES(26,63,'26:63','Пас, ба Мӯсо ваҳй кардем: «Асоятро ба дарё бизан», пас, [Нил] шикофта шуд ва ҳар бахше [аз он] ҳамчун кӯҳе бузург гашт');
INSERT INTO "pioneers" VALUES(26,64,'26:64','Ва он гурӯҳи дигар [сипоҳи Фиръавн]-ро ба он ҷо кашондем');
INSERT INTO "pioneers" VALUES(26,65,'26:65','Ва Мӯсо ва ҳамаи касонеро, ки ҳамроҳаш буданд, наҷот додем');
INSERT INTO "pioneers" VALUES(26,66,'26:66','Ва дигаронро ғарқ намудем');
INSERT INTO "pioneers" VALUES(26,67,'26:67','Ҳатман, дар ин [моҷаро] нишонае [барои ибрат гирифтан] аст, вале бештарашон муъмин набуданд');
INSERT INTO "pioneers" VALUES(26,68,'26:68','Ва бе тардид, Парвардигорат ҳамон шикастнопазири меҳрубон аст');
INSERT INTO "pioneers" VALUES(26,69,'26:69','[Эй паёмбар] Достони Иброҳимро бар онон бозгӯ');
INSERT INTO "pioneers" VALUES(26,70,'26:70','Ҳангоме ки ба падар ва қавмаш гуфт: «Чи чизро мепарастед?»');
INSERT INTO "pioneers" VALUES(26,71,'26:71','Онон гуфтанд: «Бутҳоеро мепарастем ва пайваста сар бар остонашон дорем»');
INSERT INTO "pioneers" VALUES(26,72,'26:72','[Иброҳим] Гуфт: «Оё вақте онҳоро мехонед, [садои] шуморо мешунаванд?');
INSERT INTO "pioneers" VALUES(26,73,'26:73','Ё ба шумо суду зиён мерасонанд?»');
INSERT INTO "pioneers" VALUES(26,74,'26:74','Онон гуфтанд: «[На] Вале падаронамонро ёфтем, ки чунин мекарданд»');
INSERT INTO "pioneers" VALUES(26,75,'26:75','[Иброҳим] гуфт: «Оё дидед [ва донистед] чизҳоеро, ки ҳамвора мепарастидед');
INSERT INTO "pioneers" VALUES(26,76,'26:76','- [ҳам] шумо ва [ҳам] падаронатон –');
INSERT INTO "pioneers" VALUES(26,77,'26:77','яқинан, [ҳамаи] онҳо душмани [ақидаи тавҳидии] ман ҳастанд. Магар Парвардигори ҷаҳониён');
INSERT INTO "pioneers" VALUES(26,78,'26:78','Он [маъбуде], ки маро офарида ва ҳидоятам мекунад');
INSERT INTO "pioneers" VALUES(26,79,'26:79','Ва ҳамоне, ки ба ман ғизо медиҳад ва серобам месозад');
INSERT INTO "pioneers" VALUES(26,80,'26:80','Ва ҳангоме ки бемор мешавам, Ӯст, ки шифоям медиҳад');
INSERT INTO "pioneers" VALUES(26,81,'26:81','Ва касе, ки маро мемиронад ва [дубора] зиндаам мекунад');
INSERT INTO "pioneers" VALUES(26,82,'26:82','Ва касе, ки умед дорам рӯзи ҷазо гуноҳонамро бубахшад');
INSERT INTO "pioneers" VALUES(26,83,'26:83','Парвардигоро, ба ман донише [дар дин] бубахш ва маро ба шоистагон мулҳақ кун');
INSERT INTO "pioneers" VALUES(26,84,'26:84','Ва бароям дар ояндагон овозаи нек қарор бидеҳ');
INSERT INTO "pioneers" VALUES(26,85,'26:85','Ва маро аз ворисони биҳишти пурнеъмат бигардон');
INSERT INTO "pioneers" VALUES(26,86,'26:86','Ва падарамро биёмурз, [ки] бе тардид, дар зумраи гумроҳон буд');
INSERT INTO "pioneers" VALUES(26,87,'26:87','Ва рӯзе, ки [мардум] барангехта мешаванд, маро расво [ва шарманда] насоз');
INSERT INTO "pioneers" VALUES(26,88,'26:88','Ҳамон рӯзе, ки молу фарзандон суде намебахшанд');
INSERT INTO "pioneers" VALUES(26,89,'26:89','Магар касе, ки бо қалби пок [ва холӣ аз ширку нифоқ ва риё] ба пешгоҳи Аллоҳ таоло биёяд»');
INSERT INTO "pioneers" VALUES(26,90,'26:90','Ва [он рӯз] биҳишт барои парҳезкорон наздик мегардад');
INSERT INTO "pioneers" VALUES(26,91,'26:91','Ва дузах барои гумроҳон ошкор мешавад');
INSERT INTO "pioneers" VALUES(26,92,'26:92','Ва ба онон гуфта мешавад: «Он чи ба ҷои Аллоҳ таоло мепарастидед, [акнун] куҷо ҳастанд?');
INSERT INTO "pioneers" VALUES(26,93,'26:93','Оё [онҳо дар баробари азоби илоҳӣ] ёриатон мекунанд ё [ҳатто худ]-ро ёрӣ медиҳанд?»');
INSERT INTO "pioneers" VALUES(26,94,'26:94','Пас, онҳо [бутҳо] ҳамроҳи гумроҳон дар он [оташи ҳавлангез] афканда мешаванд');
INSERT INTO "pioneers" VALUES(26,95,'26:95','Ва [ҳамроҳи] тамоми лашкариёни Иблис');
INSERT INTO "pioneers" VALUES(26,96,'26:96','Дар он ҷо [мушрикон] дар ҳоли ситез [бо маъбудони ботил] мегӯянд:');
INSERT INTO "pioneers" VALUES(26,97,'26:97','«Ба Аллоҳ таоло савганд, ки мо дар гумроҳии ошкоре будем»');
INSERT INTO "pioneers" VALUES(26,98,'26:98','Он гоҳ ки шуморо бо Парвардигори ҷаҳониён баробар медонистем');
INSERT INTO "pioneers" VALUES(26,99,'26:99','Ва моро касе ҷуз муҷримон гумроҳ накард');
INSERT INTO "pioneers" VALUES(26,100,'26:100','Пас, [акнун] на ҳеҷ шафоатгаре дорем');
INSERT INTO "pioneers" VALUES(26,101,'26:101','Ва на ҳеҷ дӯсти меҳрубоне');
INSERT INTO "pioneers" VALUES(26,102,'26:102','Пас, кош бозгаште [ба дунё] доштем ва дар зумраи муъминон қарор мегирифтем»');
INSERT INTO "pioneers" VALUES(26,103,'26:103','Ҳатман, дар ин [моҷаро] нишонае [барои ибрат гирифтан] аст, вале бештарашон муъмин набуданд');
INSERT INTO "pioneers" VALUES(26,104,'26:104','Ва бе тардид, Парвардигорат ҳамон шикастнопазири меҳрубон аст');
INSERT INTO "pioneers" VALUES(26,105,'26:105','Қавми Нуҳ паёмбаронро дурӯғгӯ ангоштанд');
INSERT INTO "pioneers" VALUES(26,106,'26:106','Он гоҳ ки бародарашон Нуҳ ба онон гуфт: «Оё [аз Аллоҳ таоло] парво намекунед?»');
INSERT INTO "pioneers" VALUES(26,107,'26:107','Ба ростӣ, ман барои шумо паёмбари амин ҳастам');
INSERT INTO "pioneers" VALUES(26,108,'26:108','Пас, аз Аллоҳ таоло парво кунед ва мутеи [дастурҳои] ман бошед');
INSERT INTO "pioneers" VALUES(26,109,'26:109','Ва ман [дар баробари рисолатам] ҳеҷ подоше аз шумо намехоҳам. Подоши ман танҳо бар [уҳдаи] Парвардигори ҷаҳониён аст');
INSERT INTO "pioneers" VALUES(26,110,'26:110','Пас, аз Аллоҳ таоло парво кунед ва мутеи [дастурҳои] ман бошед»');
INSERT INTO "pioneers" VALUES(26,111,'26:111','Онон гуфтанд: «Оё ба ту имон биёварем, дар ҳоле ки [фақат] фурӯмоягон] аз ту пайравӣ кардаанд?»');
INSERT INTO "pioneers" VALUES(26,112,'26:112','[Нуҳ] Гуфт: «Ман ба он чи онон [дар гузашта] мекарданд, чи донише дорам?');
INSERT INTO "pioneers" VALUES(26,113,'26:113','Ҳисоби онон танҳо бо Парвардигори ман аст. Агар мефаҳмед [чунин сухани газофе нагӯед]');
INSERT INTO "pioneers" VALUES(26,114,'26:114','Ва ман [ҳаргиз] муъминонро тард [дур] намекунам');
INSERT INTO "pioneers" VALUES(26,115,'26:115','Ман ҷуз бимдиҳандаи ошкор нестам»');
INSERT INTO "pioneers" VALUES(26,116,'26:116','Онон гуфтанд: «Эй Нуҳ, агар [аз даъватат] даст барнадорӣ, ҳатман, сангсор мешавӣ»');
INSERT INTO "pioneers" VALUES(26,117,'26:117','Ӯ гуфт: «Парвардигоро, қавмам маро дурӯғгӯ ангоштанд');
INSERT INTO "pioneers" VALUES(26,118,'26:118','Пас, миёни ману эшон доварӣ кун ва маро ва муъминонеро, ки ҳамроҳам ҳастанд, наҷот бидеҳ»');
INSERT INTO "pioneers" VALUES(26,119,'26:119','Пас, ӯ ва афродеро, ки ҳамроҳаш буданд, дар [он] киштии оганда [ пур аз инсону ҳайвон] наҷот додем');
INSERT INTO "pioneers" VALUES(26,120,'26:120','Ва боқимондагонро ғарқ кардем');
INSERT INTO "pioneers" VALUES(26,121,'26:121','Ҳатман, дар ин [моҷаро] нишонае [барои ибрат гирифтан] аст, вале бештарашон муъмин набуданд');
INSERT INTO "pioneers" VALUES(26,122,'26:122','Ва бе тардид, Парвардигорат ҳамон шикастнопазири меҳрубон аст');
INSERT INTO "pioneers" VALUES(26,123,'26:123','[Қавми] Од паёмбаронро дурӯғгӯ ангоштанд');
INSERT INTO "pioneers" VALUES(26,124,'26:124','Он гоҳ ки бародарашон Ҳуд ба онон гуфт: «Оё [аз Аллоҳ таоло] парво намекунед?”');
INSERT INTO "pioneers" VALUES(26,125,'26:125','Ба ростӣ, ман барои шумо паёмбари амин ҳастам');
INSERT INTO "pioneers" VALUES(26,126,'26:126','Пас, аз Аллоҳ таоло парво кунед ва мутеи [дастурҳои] ман бошед');
INSERT INTO "pioneers" VALUES(26,127,'26:127','Ва ман [дар баробари рисолатам] ҳеҷ подоше аз шумо намехоҳам. Подоши ман танҳо бар [уҳдаи] Парвардигори ҷаҳониён аст');
INSERT INTO "pioneers" VALUES(26,128,'26:128','Оё бар ҳар макони баланде биное беҳуда месозед?');
INSERT INTO "pioneers" VALUES(26,129,'26:129','Ва қасрҳо ва қалъаҳои муҳкаму устувор бино мекунед, гӯиё [дар дунё] ҷовидонаед');
INSERT INTO "pioneers" VALUES(26,130,'26:130','Ва чун [ба касе] ҳамлавар мешавед, [бераҳм ва] золимона ҳамла мекунед');
INSERT INTO "pioneers" VALUES(26,131,'26:131','Пас, аз Аллоҳ таоло парво кунед ва мутеи [дастурҳои] ман бошед');
INSERT INTO "pioneers" VALUES(26,132,'26:132','Ва аз касе парво кунед, ки ба шумо [неъматҳое чунон фаровон] ато кардааст, ки худ [онҳоро беҳтар] медонед');
INSERT INTO "pioneers" VALUES(26,133,'26:133','Ӯ ба шумо чаҳорпоёну фарзандон [-и бисёр] ато кардааст');
INSERT INTO "pioneers" VALUES(26,134,'26:134','Ва боғҳо ва чашмасорҳо');
INSERT INTO "pioneers" VALUES(26,135,'26:135','Ба ростӣ, ки ман аз азоби рӯзи бузург бар шумо метарсам”');
INSERT INTO "pioneers" VALUES(26,136,'26:136','Онон гуфтанд: “Чи моро панд бидиҳӣ, ё надиҳӣ, бароямон яксон аст [ва аз ту тоат намекунем]');
INSERT INTO "pioneers" VALUES(26,137,'26:137','Ин [оини мо ҳамон] равиши пешиниён аст');
INSERT INTO "pioneers" VALUES(26,138,'26:138','ва мо [ҳаргиз] азоб нахоҳем шуд»');
INSERT INTO "pioneers" VALUES(26,139,'26:139','Пас, онон Ҳудро дурӯғгӯ ангоштанд. Пас, Мо [низ] ҳалокашон кардем. Ҳатман, дар ин [моҷаро] нишонае [барои ибрат гирифтан] аст ва бештарашон муъмин набуданд');
INSERT INTO "pioneers" VALUES(26,140,'26:140','Ва бе тардид, Парвардигорат шикастнопазири меҳрубон аст');
INSERT INTO "pioneers" VALUES(26,141,'26:141','[Қавми] Самуд паёмбаронро дурӯғгӯ ангоштанд');
INSERT INTO "pioneers" VALUES(26,142,'26:142','Он гоҳ ки бародарашон Солеҳ ба онон гуфт: «Оё [аз Аллоҳ таоло] парво намекунед?');
INSERT INTO "pioneers" VALUES(26,143,'26:143','Ба ростӣ, ман барои шумо паёмбари амин ҳастам');
INSERT INTO "pioneers" VALUES(26,144,'26:144','Пас, аз Аллоҳ таоло парво кунед ва мутеи [дастурҳои] ман бошед');
INSERT INTO "pioneers" VALUES(26,145,'26:145','Ва ман [дар баробари рисолатам] ҳеҷ подоше аз шумо намехоҳам. Подоши ман танҳо бар [уҳдаи] Парвардигори ҷаҳониён аст');
INSERT INTO "pioneers" VALUES(26,146,'26:146','Оё [мепиндоред, ки] шуморо дар неъматҳое, ки ин ҷост, осуда раҳо мекунанд?');
INSERT INTO "pioneers" VALUES(26,147,'26:147','Дар [ин] боғҳо ва чашмасорҳо');
INSERT INTO "pioneers" VALUES(26,148,'26:148','Ва [дар канори ин] киштзорҳо ва нахлҳое, ки меваҳояш нарму расидааст?');
INSERT INTO "pioneers" VALUES(26,149,'26:149','Ва моҳирона аз кӯҳҳо [барои худ] хонаҳое метарошед');
INSERT INTO "pioneers" VALUES(26,150,'26:150','Пас, аз Аллоҳ таоло парво кунед ва мутеи [дастурҳои] ман бошед»');
INSERT INTO "pioneers" VALUES(26,151,'26:151','Ва аз исрофкорон [-и гунаҳгор] пайравӣ накунед');
INSERT INTO "pioneers" VALUES(26,152,'26:152','[Ҳамон] Касоне, ки дар замин ба фасод мепардозанд ва [хештанро] ислоҳ намекунанд”');
INSERT INTO "pioneers" VALUES(26,153,'26:153','Онон гуфтанд: “[Эй Солеҳ] Ҷуз ин нест, ки ту ҷодушудаӣ');
INSERT INTO "pioneers" VALUES(26,154,'26:154','Ту ҷуз башаре ҳаммонанди мо нестӣ, пас, агар рост мегӯӣ, нишонае [бар паёмбариат] биёвар”');
INSERT INTO "pioneers" VALUES(26,155,'26:155','[Солеҳ] Гуфт: «Ин модашутурест, ки саҳме аз об [-и чашма] дорад ва [рӯзҳои муайяне низ] шумо саҳме аз об доред');
INSERT INTO "pioneers" VALUES(26,156,'26:156','Пас, [камтарин] озоре ба он нарасонед, ки азоби рӯзи бузург шуморо фаро мегирад”');
INSERT INTO "pioneers" VALUES(26,157,'26:157','Пас, онро [захмӣ карданд ва] куштанд ва [он гоҳ аз кардаи худ] пушаймон шуданд');
INSERT INTO "pioneers" VALUES(26,158,'26:158','Пас, азоб [-и илоҳӣ] ононро фурӯ гирифт. Бе гумон, дар ин [моҷаро] нишонае [барои ибрат гирифтан] аст ва бештарашон муъмин набуданд');
INSERT INTO "pioneers" VALUES(26,159,'26:159','Ва бе тардид, Парвардигорат ҳамон шикастнопазири меҳрубон аст');
INSERT INTO "pioneers" VALUES(26,160,'26:160','Қавми Лут [низ] паёмбаронро дурӯғгӯ ангоштанд');
INSERT INTO "pioneers" VALUES(26,161,'26:161','Он гоҳ ки бародарашон Лут ба онон гуфт: «Оё [аз Аллоҳ таоло] парво намекунед?');
INSERT INTO "pioneers" VALUES(26,162,'26:162','Ба ростӣ, ман барои шумо паёмбаре амин ҳастам');
INSERT INTO "pioneers" VALUES(26,163,'26:163','Пас, аз Аллоҳ таоло парво кунед ва мутеи [дастурҳои] ман бошед»');
INSERT INTO "pioneers" VALUES(26,164,'26:164','Ва ман [дар баробари рисолатам] ҳеҷ подоше аз шумо намехоҳам. Подоши ман танҳо бар [уҳдаи] Парвардигори ҷаҳониён аст');
INSERT INTO "pioneers" VALUES(26,165,'26:165','Аз мардуми ҷаҳон бо мардон меомезед');
INSERT INTO "pioneers" VALUES(26,166,'26:166','ва ҳамсаронеро, ки Аллоҳ таоло бароятон офаридааст, раҳо мекунед? Балки шумо гурӯҳи мутаҷовизед');
INSERT INTO "pioneers" VALUES(26,167,'26:167','Онон гуфтанд: «Эй Лут, агар [аз ин амру наҳй] даст барнадорӣ, ҳатман, [аз шаҳр] ихроҷ мешавӣ»');
INSERT INTO "pioneers" VALUES(26,168,'26:168','[Лут] Гуфт: «Ман душмани [сарсахти] ин коратон ҳастам');
INSERT INTO "pioneers" VALUES(26,169,'26:169','Парвардигоро, ман ва хонаводаамро аз [оқибати] он чи [инҳо] анҷом медиҳанд, наҷот деҳ»');
INSERT INTO "pioneers" VALUES(26,170,'26:170','Пас, мо ӯ ва ҳамаи хонаводаашро наҷот додем');
INSERT INTO "pioneers" VALUES(26,171,'26:171','Магар пиразане, ки дар миёни бозмондагон [дар азоб] буд');
INSERT INTO "pioneers" VALUES(26,172,'26:172','Сипас дигаронро нобуд кардем');
INSERT INTO "pioneers" VALUES(26,173,'26:173','Ва бар онон бороне [аз санг] боронидем ва борони бимдодашудагон чи бад буд!');
INSERT INTO "pioneers" VALUES(26,174,'26:174','Бе гумон, дар ин [моҷаро] нишонае [барои ибрат гирифтан] аст ва бештарашон муъмин набуданд');
INSERT INTO "pioneers" VALUES(26,175,'26:175','Ва бе тардид, Парвардигорат ҳамон шикастнопазири меҳрубон аст');
INSERT INTO "pioneers" VALUES(26,176,'26:176','Асҳоби Айка [низ] паёмбаронро дурӯғгӯ ангоштанд');
INSERT INTO "pioneers" VALUES(26,177,'26:177','Он гоҳ ки Шуайб ба онон гуфт: «Оё [аз Аллоҳ таоло] парво намекунед?');
INSERT INTO "pioneers" VALUES(26,178,'26:178','Ба ростӣ, ман барои шумо паёмбаре амин ҳастам');
INSERT INTO "pioneers" VALUES(26,179,'26:179','Пас, аз Аллоҳ таоло парво кунед [битарсед] ва мутеи [дастурҳои] ман бошед»');
INSERT INTO "pioneers" VALUES(26,180,'26:180','Ва ман [дар баробари рисолатам] ҳеҷ подоше аз шумо намехоҳам. Подоши ман танҳо бар [уҳдаи] Парвардигори ҷаҳониён аст');
INSERT INTO "pioneers" VALUES(26,181,'26:181','Паймонаро тамом бидиҳед ва камфурӯшӣ накунед');
INSERT INTO "pioneers" VALUES(26,182,'26:182','Ва бо тарозуи дуруст вазн кунед');
INSERT INTO "pioneers" VALUES(26,183,'26:183','Ва колои мардумро кам надиҳед [ва ҳаққашонро зоеъ накунед] ва дар замин ба фасод [ва табоҳӣ] накӯшед');
INSERT INTO "pioneers" VALUES(26,184,'26:184','Ва аз касе, ки шумо ва умматҳои пешинро офарид, парво кунед [битарсед]');
INSERT INTO "pioneers" VALUES(26,185,'26:185','Онон гуфтанд: «[Эй Шуайб] Ҷуз ин нест, ки ту ҷодушудаӣ');
INSERT INTO "pioneers" VALUES(26,186,'26:186','Ту ҷуз башаре ҳаммонанди мо нестӣ ва мо туро дурӯғгӯ мепиндорем');
INSERT INTO "pioneers" VALUES(26,187,'26:187','Пас, агар рост мегӯӣ, порае аз осмонро бар [сари] мо биандоз»');
INSERT INTO "pioneers" VALUES(26,188,'26:188','[Шуайб] Гуфт: «Парвардигорам ба он чи мекунед, донотар аст»');
INSERT INTO "pioneers" VALUES(26,189,'26:189','Пас, ӯро дурӯғгӯ ангоштанд ва азоби рӯзи абр [-и оташбор] ононро фаро гирифт. Ба ростӣ, ки он [борони оташ] азоби рӯзи ҳавлноке буд');
INSERT INTO "pioneers" VALUES(26,190,'26:190','Бе гумон, дар ин [моҷаро] нишонае [барои ибрат гирифтан] аст; ва бештарашон муъмин набуданд');
INSERT INTO "pioneers" VALUES(26,191,'26:191','Ва бе тардид, Парвардигорат ҳамон шикастнопазири меҳрубон аст');
INSERT INTO "pioneers" VALUES(26,192,'26:192','Ва ба ростӣ, [ин] Қуръон фуруфиристодаи Парвардигори ҷаҳониён аст');
INSERT INTO "pioneers" VALUES(26,193,'26:193','Рӯҳу-л-амин [Ҷабраил] онро фуруд овардааст');
INSERT INTO "pioneers" VALUES(26,194,'26:194','Бар қалби ту [нозил кардааст], то бимдиҳанда бошӣ');
INSERT INTO "pioneers" VALUES(26,195,'26:195','[Қуръонро] Ба забони арабии равшан [нозил намуд]');
INSERT INTO "pioneers" VALUES(26,196,'26:196','Ва бе тардид, [башорати нузули] он дар китобҳои пешиниён [низ омада] аст');
INSERT INTO "pioneers" VALUES(26,197,'26:197','Оё ҳамин нишона барояшон кофӣ нест, ки уламои Бани Исроил аз [ҳақиқати] Қуръон огоҳанд?');
INSERT INTO "pioneers" VALUES(26,198,'26:198','Ва агар онро бар баъзе аз ғайриарабҳо нозил мекардем');
INSERT INTO "pioneers" VALUES(26,199,'26:199','Ва [паёмбар] онро барояшон мехонд, ба он имон намеоварданд');
INSERT INTO "pioneers" VALUES(26,200,'26:200','[Оре] Ин гуна он [куфру такзиб]-ро дар дилҳои гунаҳгорон ҷой додем');
INSERT INTO "pioneers" VALUES(26,201,'26:201','Аммо ба он имон намеоваранд, то он гоҳ ки азоби дарднокро бубинанд');
INSERT INTO "pioneers" VALUES(26,202,'26:202','Пас, ногаҳон дар ҳоле ки бехабаранд, ба суроғашон меояд');
INSERT INTO "pioneers" VALUES(26,203,'26:203','Пас, [дар он ҳол] мегӯянд: “Оё муҳлате [барои тавба] хоҳем дошт?”');
INSERT INTO "pioneers" VALUES(26,204,'26:204','Оё онон азоби моро ба шитоб хосторанд?');
INSERT INTO "pioneers" VALUES(26,205,'26:205','Оё донистӣ, ки агар ононро солҳо [аз зиндагии дунё] баҳраманд созем');
INSERT INTO "pioneers" VALUES(26,206,'26:206','Сипас он чи ки ба онон ваъда дода шудааст, ба суроғашонн биёяд');
INSERT INTO "pioneers" VALUES(26,207,'26:207','Ин баҳрамандиашон [аз дунё дар ҳангоми азоб] суде барояшон нахоҳад дошт?');
INSERT INTO "pioneers" VALUES(26,208,'26:208','Ва Мо [сокинони] ҳеҷ шаҳреро ҳалок накардем, магар ин ки ҳушдордиҳандагоне [аз паёмбарон] доштанд');
INSERT INTO "pioneers" VALUES(26,209,'26:209','То ононро панд диҳанд ва Мо ҳаргиз ситамгор набудаем [ки итмоми ҳуҷҷат накунем]');
INSERT INTO "pioneers" VALUES(26,210,'26:210','Ва ин Қуръонро шаётин нозил накардаанд');
INSERT INTO "pioneers" VALUES(26,211,'26:211','Онон на дархӯри ин коранд ва на тавонашро доранд');
INSERT INTO "pioneers" VALUES(26,212,'26:212','Бе тардид, онон аз шунидани [ваҳй] барканоранд');
INSERT INTO "pioneers" VALUES(26,213,'26:213','Пас, [эй паёмбар] ҳеҷ маъбудеро бо Аллоҳ таоло махон, ки азоб хоҳӣ шуд');
INSERT INTO "pioneers" VALUES(26,214,'26:214','Ва хешовандони наздикатро [аз азоби илоҳӣ] бим деҳ');
INSERT INTO "pioneers" VALUES(26,215,'26:215','Ва бо муъминоне, ки аз ту пайравӣ кардаанд, меҳрубон [-у фурӯтан] бош');
INSERT INTO "pioneers" VALUES(26,216,'26:216','Пас, агар аз [фармони] ту сарпечӣ карданд, бигӯ: “Ба ростӣ, ман аз он чи мекунед, безорам”');
INSERT INTO "pioneers" VALUES(26,217,'26:217','Ва бар [Аллоҳи] шикастнопазири меҳрубон таваккал кун');
INSERT INTO "pioneers" VALUES(26,218,'26:218','Ҳамон ки чун [барои ибодат] бармехезӣ, туро мебинад');
INSERT INTO "pioneers" VALUES(26,219,'26:219','Ва ҳаракат [ва нишасту бархости] туро миёни саҷдакунандагон [мебинад]');
INSERT INTO "pioneers" VALUES(26,220,'26:220','Бе тардид, Ӯ ҳамон шунавои доност');
INSERT INTO "pioneers" VALUES(26,221,'26:221','Оё ба шумо хабар диҳам, ки шаётин бар чи касе нозил мешаванд?');
INSERT INTO "pioneers" VALUES(26,222,'26:222','Бар ҳар дурӯғгӯи гунаҳкор');
INSERT INTO "pioneers" VALUES(26,223,'26:223','Онон шунидаҳое [-ро, ки дуздона аз малакут гӯш додаанд, ба ҷодугарон ва фолгирон] илқо мекунанд ва бештарашон дурӯғгӯ ҳастанд');
INSERT INTO "pioneers" VALUES(26,224,'26:224','Ва [Муҳаммад шоир нест, зеро] шоиронро гумроҳон пайравӣ мекунанд');
INSERT INTO "pioneers" VALUES(26,225,'26:225','Оё надидӣ, ки онон дар ҳар лағву беҳудае [ҳайрону] саргаштаанд?');
INSERT INTO "pioneers" VALUES(26,226,'26:226','Ва чизеро мегӯянд, ки [худ ба он эътиқод надоранд ва] амал намекунанд?');
INSERT INTO "pioneers" VALUES(26,227,'26:227','Магар касоне, ки имон овардаанд ва корҳои шоиста анҷом додаанд ва Аллоҳро бисёр ёд кардаанд ва пас аз он ки ситам диданд, интиқом гирифтанд [ва бо шеъри худ аз ислому муслимин дифоъ намуданд] ва касоне, ки ситам карданд, ба зудӣ хоҳанд донист, ба чи бозгаштгоҳе бозмегарданд');
INSERT INTO "pioneers" VALUES(27,1,'27:1','То, син. Ин оёти Қуръон ва китобе равшангар аст');
INSERT INTO "pioneers" VALUES(27,2,'27:2','[Ки] Ҳидояту башорате барои муъминон аст');
INSERT INTO "pioneers" VALUES(27,3,'27:3','[Ҳамон] Касоне, ки намоз барпо медоранд ва закот мепардозанд ва ба охират яқин доранд');
INSERT INTO "pioneers" VALUES(27,4,'27:4','Дар ҳақиқат, касоне, ки ба охират имон намеоваранд, аъмолашонро барояшон оростаем, пас, [кӯрдилу] саргаштаанд');
INSERT INTO "pioneers" VALUES(27,5,'27:5','Ононанд, ки азоби дарднок [дар пеш] доранд ва дар охират зиёнкортаранд');
INSERT INTO "pioneers" VALUES(27,6,'27:6','Ва [эй паёмбар] яқинан, ту Қуръонро аз пешгоҳи [Парвардигори] ҳакими доно дарёфт мекунӣ');
INSERT INTO "pioneers" VALUES(27,7,'27:7','[Ёд кун аз] Ҳангоме ки Мӯсо ба хонаводааш гуфт: “[Биистед] Ман оташе [аз дур] дидам. Ба зудӣ хабаре аз он бароятон меоварам ё шуълаи оташе меоварам, то гарм шавед”');
INSERT INTO "pioneers" VALUES(27,8,'27:8','Пас, чун ба он [оташ] расид, нидо дода шуд, ки: “Муборак [ва хуҷаста] бод он ки дар оташ аст ва ҳар [фариштае], ки перомуни он аст ва поку муназзаҳ аст Аллоҳ таоло – Парвардигори ҷаҳониён');
INSERT INTO "pioneers" VALUES(27,9,'27:9','Эй Мӯсо, ба ростӣ, ки Манам Аллоҳи шикастнопазири ҳаким');
INSERT INTO "pioneers" VALUES(27,10,'27:10','Ва асоятро биафкан. Пас, чун [Мӯсо] онро дид, ки ҳамчун море [бо суръат] мехазад, [аз тарс] пушт кард ва гурехт ва бознагашт. [Нидо омад] “Эй Мӯсо, натарс! Паёмбарон дар пешгоҳи Ман ҳеҷ тарсе надоранд');
INSERT INTO "pioneers" VALUES(27,11,'27:11','Аммо касе, ки ситам кунад, он гоҳ [пас аз тавба] бадиро ба некӣ табдил намояд, [бидонад, ки] бе тардид, Ман омурзандаи меҳрубонам');
INSERT INTO "pioneers" VALUES(27,12,'27:12','Ва дастатро дар гиребон фурӯ бар, [то] сапед ва бидуни айб [-у бемории песӣ] хориҷ шавад. [Ин муъҷизаест] Дар зумраи муъҷизоти нуҳгона [ки бояд] ба сӯйи Фиръавн ва қавмаш [бибарӣ, чаро ки] бе тардид, онон гурӯҳе нофармонанд”');
INSERT INTO "pioneers" VALUES(27,13,'27:13','Пас, чун оёти Мо равшан [-у возеҳ] ба сӯяшон омад, гуфтанд: “Ин ҷодуе ошкор аст”');
INSERT INTO "pioneers" VALUES(27,14,'27:14','Ва онҳоро аз рӯи ситаму саркашӣ инкор карданд, дар ҳоле ки дилҳояшон ба онҳо яқин доштанд; пас бингар, ки саранҷоми муфсидон чи гуна бувад');
INSERT INTO "pioneers" VALUES(27,15,'27:15','Ва ба ростӣ, ба Довуд ва Сулаймон донише [азим] додем ва онон гуфтанд: “Ситоиш аз они Аллоҳ таоло аст, ки моро бар бисёре аз бандагони муъмини худ бартарӣ бахшид”');
INSERT INTO "pioneers" VALUES(27,16,'27:16','Ва Сулаймон аз Довуд мерос бурд ва гуфт: “Эй мардум, [фаҳми] забони парандагон ба мо омӯхта шуда ва аз ҳар чиз ба мо [баҳрае] додаанд. Бе тардид, ин фазле ошкор [аз ҷониби Парвардигор] аст”');
INSERT INTO "pioneers" VALUES(27,17,'27:17','Ва сипоҳиёни Сулаймон – аз ҷинну инс ва парандагон – наздаш гирд омаданд; он гоҳ муназзам [ва пайваста дар канори якдигар] мерафтанд');
INSERT INTO "pioneers" VALUES(27,18,'27:18','[Онон ба роҳи худ идома доданд], то вақте ба сарзамини мӯрчагон [дар Шом] расиданд. Мӯрчае гуфт: "Эй мӯрчагон, ба лонаҳои худ биравед, то Сулаймон ва лашкариёнаш шуморо [надида ва] надониста поймол накунанд”');
INSERT INTO "pioneers" VALUES(27,19,'27:19','Пас [Сулаймон] аз сухани он [мӯрча] хандид ва гуфт: «Парвардигоро, ба ман тавфиқ бидеҳ, то шукри неъматеро, ки бар ман ва бар падару модарам арзонӣ доштаӣ ба ҷой оварам ва [ёриам кун, то] коре шоистае анҷом диҳам, ки мояи хушнудиат гардад ва ба раҳмати хеш маро дар ҷамъи бандагони шоистаат ворид кун”');
INSERT INTO "pioneers" VALUES(27,20,'27:20','Ва [Сулаймон ҳоли] парандагонро ҷӯё шуд ва гуфт: «Чи шудааст, ки ҳудҳудро намебинам? [Оё ҷойе пинҳон шуда] ё ғоиб аст?');
INSERT INTO "pioneers" VALUES(27,21,'27:21','[Пас, чун ӯро наёфт гуфт] Ҳатман, ба сахтӣ муҷозот мекунам ё сарашро мебурам, магар он ки далели равшане [барои ғайбаташ] биёварад»');
INSERT INTO "pioneers" VALUES(27,22,'27:22','Пас, дере напоид [ки ҳудҳуд омад] ва гуфт: «Аз чизе огоҳӣ ёфтаам, ки ту бад-он огоҳӣ наёфтаӣ ва аз [сарзамини] Сабаъ бароят хабаре боэътимод овардаам');
INSERT INTO "pioneers" VALUES(27,23,'27:23','Ман [он ҷо] занеро ёфтаам, ки бар онон фармонравоӣ мекунад ва [ба ӯ] аз ҳар гуна неъмате додаанд ва тахти бузурге дорад');
INSERT INTO "pioneers" VALUES(27,24,'27:24','Ӯ ва қавмашро дидам, ки ба ҷойи Аллоҳ таоло барои хуршед саҷда мекунанд ва шайтон корҳои [ботили] ононро барояшон ороста ва эшонро аз роҳ [-и рост] боздоштааст, аз ин рӯ, ҳидоят намешаванд»');
INSERT INTO "pioneers" VALUES(27,25,'27:25','[Шайтон фиребашон додааст] То барои Аллоҳ таоло саҷда накунанд [ҳамон маъбуде], ки ниҳони осмонҳо ва заминро ошкор мекунад ва он чиро, ки пинҳон мекунед ва ошкор месозед, [ҳамаро] медонад');
INSERT INTO "pioneers" VALUES(27,26,'27:26','Аллоҳ таоло, ки ҳеҷ маъбуде [ба ҳақ] ҷуз ӯ нест, Парвардигори Арши бузург аст');
INSERT INTO "pioneers" VALUES(27,27,'27:27','[Сулаймон] гуфт: «Хоҳем дид, ки оё рост гуфтаӣ ё дурӯғгӯӣ');
INSERT INTO "pioneers" VALUES(27,28,'27:28','Ин номаи маро бибар ва ба сӯйи онон бияфкан, он гоҳ дур шав ва [дар гӯшае биист ва] бингар, ки [дар бораи он] чи мегӯянд»');
INSERT INTO "pioneers" VALUES(27,29,'27:29','[Маликаи Сабаъ] гуфт: «Эй бузургон, номаи арҷманде ба сӯйи ман афканда шудааст');
INSERT INTO "pioneers" VALUES(27,30,'27:30','Ин [нома] аз ҷониби Сулаймон аст ва чунин аст: «Ба номи Аллоҳи бахшандаи меҳрубон');
INSERT INTO "pioneers" VALUES(27,31,'27:31','Бар ман бартарӣ маҷӯед ва дар ҳоле ки [аз ширк даст бардошта ва] таслим [-и ҳақ ва тавҳид] шудаед, назди ман биёед»');
INSERT INTO "pioneers" VALUES(27,32,'27:32','[Малика] Гуфт: «Эй бузургон, назари худро дар ин маврид бигӯед, [чаро ки] ман ҳаргиз бидуни ҳузур [-у машварат]-и шумо дар бораи коре тасмим нагирифтаам»');
INSERT INTO "pioneers" VALUES(27,33,'27:33','Онон гуфтанд: «Мо неруи бисёр ва ҷангҷӯёни қудратманде дорем, вале ихтиёр [-у тасмими ниҳоӣ] бо туст. Пас, баррасӣ кун, ки чи дастуре бидиҳӣ»');
INSERT INTO "pioneers" VALUES(27,34,'27:34','[Малика] гуфт: «Бе тардид, вақте подшоҳон [ба унвони фотеҳ] вориди шаҳре мешаванд, онро вайрон [ва табоҳ] месозанд ва бузургонашро хору залил мекунанд ва рафторашон ҳамвора чунин будааст');
INSERT INTO "pioneers" VALUES(27,35,'27:35','Ва ман ҳадяе барояшон мефиристам ва мунтазир мемонам, то бубинам, ки фиристодагон чи посухе [бароям] меоваранд»');
INSERT INTO "pioneers" VALUES(27,36,'27:36','Пас, чун [фиристодаи малика] назди Сулаймон омад, [Сулаймон] гуфт: «Оё [мехоҳед] бо мол [-у сарват] ба ман кумак кунед? Он чи Аллоҳ таоло [аз паёмбарӣ ва донишу фармонравӣ] ба ман бахшида, аз он чизе, ки ба шумо ато карда беҳтар аст, балки шумоед, ки ба ҳадяи хеш [ва сарватҳои дунё] шодмон мешавед');
INSERT INTO "pioneers" VALUES(27,37,'27:37','Назди онон бозгард [ва бигӯ, ки] ҳатман, бо лашкариёне ба сӯяшон хоҳем омад, ки тавони муқобила бо онро надошта бошанд ва бе тардид, ононро аз он [сарзамин] хору зор берун мекунем»');
INSERT INTO "pioneers" VALUES(27,38,'27:38','[Он гоҳ] Гуфт: «Эй бузургон, кадом яке аз шумо, пеш аз он ки онон таслимшуда назди ман биёянд, тахти ӯро бароям меоварад?»');
INSERT INTO "pioneers" VALUES(27,39,'27:39','Яке аз ҷинниёни тануманд гуфт: «Пеш аз он ки аз ҷоят бархезӣ, ман онро наздат меоварам ва ҳатман, барои анҷоми ин кор неруманду аминам»');
INSERT INTO "pioneers" VALUES(27,40,'27:40','Касе, ки донише аз китоб [-и илоҳӣ] дошт, гуфт: «Пеш аз он ки чашм бар ҳам бизанӣ, онро бароят меоварам». Пас, чун [Сулаймон] он [тахт]-ро назди худ мустақар дид, гуфт: «Ин аз фазли Парвардигори ман аст, то биозмоядам, ки оё сипос медорам ё носипосӣ мекунам. Пас, ҳар ки сипос гузорад, танҳо ба суди хеш сипос мегузорад ва ҳар ки носипосӣ кунад, пас, [ба зиёни худ носипосӣ кардааст, чаро ки] бе тардид, Парвардигорам бениёзи карим аст');
INSERT INTO "pioneers" VALUES(27,41,'27:41','[Сулаймон] гуфт: «Тахташро [тағйири шакл диҳед ва] барояш ношинос кунед, то бубинам, оё [онро] ташхис медиҳад ё аз касонест, ки [ашё ва амволи худро] намешиносанд');
INSERT INTO "pioneers" VALUES(27,42,'27:42','Пас, чун [маликаи Сабаъ] омад, [ба ӯ] гуфта шуд: «Оё тахти ту ин гуна аст?». [Малика] гуфт: «Гӯё ҳамон аст». Ва [Сулаймон гуфт] «Ин дониш [ва тавоноӣ] пеш аз ин ба мо дода шудааст ва ҳамвора фармонбардор будаем»');
INSERT INTO "pioneers" VALUES(27,43,'27:43','Ва он чи ба ҷойи Аллоҳ таоло мепарастед, ӯро [аз имон ва тавҳид] боздошта буд. Бе тардид, [малика] аз ҷумлаи кофирон буд');
INSERT INTO "pioneers" VALUES(27,44,'27:44','Ба вай гуфта шуд: «Ба саҳн [-и қаср] ворид шав». Пас, ҳангоме ки онро дид, пиндошт, ки оби амиқест ва ҷома аз соқҳо [-и пои] хеш боло кашид [то аз об бигзарад. Сулаймон] Гуфт: «Ин [об нест, балки] кохе аст, ки бо [қитаоти булур фарш шудааст». [Он гоҳ ӯро ба тавҳид даъват кард. Маликаи Сабаъ] Гуфт: «Парвардигоро, ман ба худ ситам кардаам ва [инак] ҳамроҳи Сулаймон таслими Аллоҳ таоло – Парвардигори ҷаҳониён – шудам»');
INSERT INTO "pioneers" VALUES(27,45,'27:45','Ва ба сӯйи қавми Самуд бародарашон Солеҳро фиристодем, ки [бигӯяд] «Аллоҳи ягонаро бипарастед», вале онон ба ду гурӯҳ [муъминон ва кофирон] тақсим шуданд ва ба ситез пардохтанд');
INSERT INTO "pioneers" VALUES(27,46,'27:46','[Солеҳ] Гуфт: «Эй қавми ман, чаро ба ҷойи раҳмат шитобзада хоҳони азоби илоҳӣ ҳастед? Чаро аз Аллоҳ таоло омурзиш намехоҳед? Бошад, ки мавриди раҳмат қарор гиред»');
INSERT INTO "pioneers" VALUES(27,47,'27:47','Онон гуфтанд: «Мо ту ва касонеро, ки ҳамроҳат ҳастанд, ба фоли бад гирифтаем». [Солеҳ] Гуфт: «Фоли бади шумо [ва дониши он] назди Аллоҳ таоло аст, балки шумо гурӯҳе ҳастед, ки озмоиш мешавед»');
INSERT INTO "pioneers" VALUES(27,48,'27:48','Ва дар шаҳр [-и Ҳиҷр] нуҳ нафар [сардастаи гурӯҳ] буданд, ки дар он сарзамин ба фасод мепардохтанд ва [ҳеҷ] кори шоистае намекарданд');
INSERT INTO "pioneers" VALUES(27,49,'27:49','[Онон ба якдигар] Гуфтанд: «Ба Аллоҳ таоло савганд ёд кунед, ки бар ӯ [Солеҳ] ва хонаводааш шабехун бизанем [ва ононро бикушем]. Он гоҳ ба хунхоҳаш бигӯем: «Мо ҳангоми куштори хонаводааш ҳозир набудем ва ҳатман, рост мегӯем»');
INSERT INTO "pioneers" VALUES(27,50,'27:50','Онон [барои қатли Солеҳ ва пайравонаш] найранге ба кор бурданд ва Мо [барои нобудии кофирон] низ найранге андешидем, дар ҳоле ки онон намедонистанд');
INSERT INTO "pioneers" VALUES(27,51,'27:51','Пас, бингар оқибати найрангашон чи шуд: мо ҳамаи он афрод ва қавмашонро нобуд кардем');
INSERT INTO "pioneers" VALUES(27,52,'27:52','Инак хонаҳояшон ба сазои ситаме, ки карданд, [вайрону] тиҳӣ мондааст. Бе гумон, дар ин [кайфар] барои касоне, ки медонанд [ва имон доранд] нишонае [барои ибрат гирифтан] аст');
INSERT INTO "pioneers" VALUES(27,53,'27:53','Ва касонеро, ки имон оварда ва парҳезкор буданд, [ҳамроҳи Солеҳ] наҷот додем');
INSERT INTO "pioneers" VALUES(27,54,'27:54','Ва [ёд кун аз] Лут он гоҳ ки ба қавмаш гуфт: «Бо он ки [зиштӣ ва гуноҳи ливотро] медонед, оё [боз ҳам] муртакиби ин амали бешармона мешавед?');
INSERT INTO "pioneers" VALUES(27,55,'27:55','Оё аз рӯйи шаҳват ба ҷойи занон бо мардон дармеомезед? Балки шумо гурӯҳе нодонед»');
INSERT INTO "pioneers" VALUES(27,56,'27:56','Пас, посухи қавмаш ҷуз ин набуд, ки [ба якдигар] гуфтанд: «Хонаводаи Лутро аз шаҳри худ берун кунед, [чаро ки] бе тардид, инҳо афроде покдоман [муқаддасмаоб] ҳастанд»');
INSERT INTO "pioneers" VALUES(27,57,'27:57','Он гоҳ ӯ ва хонаводаашро наҷот додем, магар ҳамсарашро, ки муқаддар кардем аз бозмондагон [дар азоб] бошад');
INSERT INTO "pioneers" VALUES(27,58,'27:58','Ва бар онон бороне [аз санг] борондем ва борони бимдодашудагон чи бад бувад!');
INSERT INTO "pioneers" VALUES(27,59,'27:59','Бигӯ: «Ситоишу сипос аз они Аллоҳ таоло аст ва салом бар бандагонаш, ки [ононро] баргузидааст» Оё Аллоҳ таоло беҳтар аст ё он чи [бо Ӯ] шарик месозанд?');
INSERT INTO "pioneers" VALUES(27,60,'27:60','[Оё маъбудони шумо беҳтаранд] Ё касе, ки осмонҳо ва заминро офарид ва аз осмон бароятон обе нозил кард ва бо он борон бӯстонҳое хуррам [-у зебо] рӯёнидем, ки ҳаргиз тавони рӯёнидани дарахтонро надоштед? Оё маъбуди дигаре бо Аллоҳ таоло аст? [Ҳаргиз] Балки онҳо гурӯҳе ҳастанд, ки [махлуқотро] ҳамтирози [Аллоҳ таоло] қарор медиҳанд');
INSERT INTO "pioneers" VALUES(27,61,'27:61','[Оё маъбудони шумо беҳтаранд] Ё касе, ки заминро қароргоҳи [амне] сохт ва миёнаш ҷӯйборҳое равон кард ва барояш кӯҳҳое [устувор] падид овард ва миёни ду дарё монеае қарор дод [то оби шӯру ширин бо ҳам наёмезанд]? Оё маъбуди дигаре бо Аллоҳ таоло вуҷуд дорад? [Ҳаргиз] Аммо бештарашон намедонанд');
INSERT INTO "pioneers" VALUES(27,62,'27:62','[Оё маъбудони шумо беҳтаранд] Ё касе, ки [дуои] дармондаро – чун бихондаш – иҷобат мекунад ва гирифториро бартараф месозад ва шуморо ҷонишинони [пешиниён дар] замин қарор медиҳад? Оё маъбуди дигаре бо Аллоҳ таоло вуҷуд дорад? Чи андак панд мегиред!');
INSERT INTO "pioneers" VALUES(27,63,'27:63','[Оё маъбудони шумо беҳтаранд] Ё касе, ки дар торикиҳои биёбон ва дарё [ба василаи моҳу ситорагон] ҳидоятатон мекунад ва он ки пеш аз [борони] раҳматаш бодҳоро ба мужда мефиристад? Оё маъбуди дигаре бо Аллоҳ таоло вуҷуд дорад? Аллоҳ таоло аз он чи [барояш] шарик қарор медиҳанд, бартар аст');
INSERT INTO "pioneers" VALUES(27,64,'27:64','[Оё маъбудони шумо беҳтаранд] Ё касе, ки офаринишро оғоз кард ва сипас онро бозмегардонад ва [ё] он ки аз осмон ва замин ба шумо рӯзӣ медиҳад? Оё маъбуди дигаре бо Аллоҳ таоло вуҷуд дорад? Бигӯ: «Агар ростгӯед, далелатонро биёваред»');
INSERT INTO "pioneers" VALUES(27,65,'27:65','Бигӯ: «Касе дар осмонҳо ва замин аз ғайб огоҳ нест, магар Аллоҳ таоло. Ва намедонанд чи замоне барангехта мешаванд');
INSERT INTO "pioneers" VALUES(27,66,'27:66','Оё илмашон дар бораи охират ба камол расидааст [ва дар борааш яқин доранд? Ҳаргиз], балки онон дар ин маврид дар шак [-у ҳайронӣ] ҳастанд, балки эшон нисбат ба [фаҳми] он [бебасират ва] кӯрдиланд');
INSERT INTO "pioneers" VALUES(27,67,'27:67','Ва касоне, ки куфр варзиданд, гуфтанд: «Оё ҳангоме ки мо ва падаронамон хок шудем, моро [зинда мекунанд ва аз гӯр] берун меоваранд?');
INSERT INTO "pioneers" VALUES(27,68,'27:68','Дар ҳақиқат, инро ба мо ва пеш аз ин ба падаронамон [низ] ваъда додаанд. Ин [сухан чизе] нест, магар афсонаҳои пешиниён»');
INSERT INTO "pioneers" VALUES(27,69,'27:69','[Эй паёмбар, ба кофирон] Бигӯ: «Дар замин гардиш кунед ва бингаред, ки саранҷоми гунаҳгорон чи гуна будааст»');
INSERT INTO "pioneers" VALUES(27,70,'27:70','Ва аз [рӯйгардонии] онон ғамгин набош ва аз найрангҳое, ки меварзанд, дилтанг нашав');
INSERT INTO "pioneers" VALUES(27,71,'27:71','Ва [кофирон] мегӯянд: «Агар ростгӯед, ин ваъда [-и азоб] кай хоҳад буд»?');
INSERT INTO "pioneers" VALUES(27,72,'27:72','Бигӯ: «Чи басо бархе аз он чи ба шитоб мехоҳед, ба шумо наздик бошад»');
INSERT INTO "pioneers" VALUES(27,73,'27:73','Ва бе гумон, Парвардигорат нисбат ба мардум бахшиш [-у раҳмат] дорад, вале бештарашон сипос намегузоранд');
INSERT INTO "pioneers" VALUES(27,74,'27:74','Ва бе тардид, Парвардигорат он чиро дар синаҳояшон пинҳон мекунанд ва он чиро ошкор месозанд, ба хубӣ медонад');
INSERT INTO "pioneers" VALUES(27,75,'27:75','Ва ҳеҷ пинҳонӣ дар осмону замин нест, магар он ки дар китобе равшан [Лавҳи маҳфуз] сабт аст');
INSERT INTO "pioneers" VALUES(27,76,'27:76','Бе гумон, ин Қуръон бештар он чиро, ки Бани Исроил дар борааш ихтилоф доранд, барояшон баён мекунад');
INSERT INTO "pioneers" VALUES(27,77,'27:77','Ва ҳаққо, ки раҳнамун ва раҳмате барои муъминон аст');
INSERT INTO "pioneers" VALUES(27,78,'27:78','Мусалламан, Парвардигорат [дар қиёмат] ба ҳукми хеш миёнашон доварӣ мекунад ва Ӯ шикастнопазири доност');
INSERT INTO "pioneers" VALUES(27,79,'27:79','Пас, бар Аллоҳ таоло таваккал кун. Ба ростӣ, ки ту бар ҳақиқати ошкор ҳастӣ');
INSERT INTO "pioneers" VALUES(27,80,'27:80','Ва ту ҳаргиз наметавонӣ мурдадилонро шунаво созӣ ва [нидои] даъват [-и ҳақ]-ро ба гӯши карон бирасонӣ, он гоҳ ки онон пушт мекунанд ва рӯй мегардонанд');
INSERT INTO "pioneers" VALUES(27,81,'27:81','Ва ту наметавонӣ кӯрдилонро аз гумроҳиашон [бозгардонӣ ва] ҳидоят кунӣ. Ту [метавонӣ паёми ҳақро танҳо] ба гӯши касоне бирасонӣ, ки ба оёти Мо имон доранд ва [дар баробари ҳақ] таслиманд');
INSERT INTO "pioneers" VALUES(27,82,'27:82','Ва он гоҳ ки [дар остонаи қиёмат] фармони [азоб] бар онон воҷиб гардад, ҷунбандаеро барояшон аз замин бурун меоварем, ки бо онон [дар ин маврид] сухан бигӯяд, ки мардум ба оёти Мо яқин надоштанд');
INSERT INTO "pioneers" VALUES(27,83,'27:83','Ва [ёд кун аз] рӯзе, ки аз ҳар уммате гурӯҳе аз онон, ки оёти Моро дурӯғ меангоштанд, ҷамъ мекунем, он гоҳ [барои ҳисобрасӣ ба саф] нигоҳ дошта мешаванд');
INSERT INTO "pioneers" VALUES(27,84,'27:84','То ҳангоме ки [ба ҷойгоҳи ҳисобрасӣ] бирасанд, [дар он ҷо Аллоҳ таоло] мефармояд: «Оё дар ҳоле ки нисбат ба [ҳақиқати] оётам донише надоштед, онҳоро дурӯғ меангоштед? Ин чи коре буд, ки мекардед?»');
INSERT INTO "pioneers" VALUES(27,85,'27:85','Ва ба хотири ситаме, ки карданд, фармон [-и азоб] бар онон воқеъ мегардад ва ҳеҷ сухане [дар дифоъ аз худ] намегӯянд');
INSERT INTO "pioneers" VALUES(27,86,'27:86','Оё надиданд, ки шабро падид овардаем, то дар он оромиш ёбанд ва рӯзро равшанибахш [сохтаем, то ба кор ва талош бипардозанд]? Бе тардид, дар ин [офариниш] барои онон, ки имон меоваранд, нишонаҳо [-и равшане аз қудрати Аллоҳ таоло] аст');
INSERT INTO "pioneers" VALUES(27,87,'27:87','Ва [ёд кун аз] рӯзе, ки дар сур дамида мешавад ва ҳар ки дар осмонҳо ва замин аст, ваҳшат мекунад, магар он ки Аллоҳ таоло бихоҳад ва ҳамагӣ бо хорӣ [ва зиллат] ба пешгоҳи Аллоҳ таоло меоянд');
INSERT INTO "pioneers" VALUES(27,88,'27:88','Ва кӯҳҳоро мебинӣ [ва] онҳоро беҳаракат мепиндорӣ, ҳол он ки монанди гузари абрҳо дар ҳаракатанд. Ин офариниши илоҳӣ аст, ки ҳар чизеро маҳкам [-у устувор] падид овардааст. Бе тардид, Ӯ ба он чи мекунед огоҳ аст');
INSERT INTO "pioneers" VALUES(27,89,'27:89','Ҳар ки [дар рӯзӣ қиёмат] некӣ биёварад, [подоше] беҳтар аз он хоҳад дошт ва аз ваҳшати он рӯз дар амонанд');
INSERT INTO "pioneers" VALUES(27,90,'27:90','Ва ҳар ки бадӣ биёварад, бо рӯяш дар оташ нагунсор мешаванд [ва ба онон гуфта мешавад]: «Оё ҷуз дар баробари он чи кардаед, муҷозот мешавед?»');
INSERT INTO "pioneers" VALUES(27,91,'27:91','[Эй Паёмбар, ба кофирон бигӯ] «Ман фақат маъмурам, ки Парвардигори ин шаҳр [Макка]-ро бипарастам, ҳамон ки ба ин ҷо ҳурмат бахшида ва ҳама чиз аз они Ӯст ва маъмурам, ки таслим [-и фармонаш] бошам');
INSERT INTO "pioneers" VALUES(27,92,'27:92','Ва ин ки Қуръонро тиловат кунам. Пас, ҳар ки ҳидоят шавад, ба суди хеш ҳидоят ёфтааст, ва [ба] ҳар ки гумроҳ мегардад, бигӯ: «Ман фақат бимдиҳандаам»');
INSERT INTO "pioneers" VALUES(27,93,'27:93','Ва бигӯ: «Ситоиш аз они Аллоҳ таоло аст. Ба зудӣ нишонаҳо [-и қудрат]-и хешро нишонатон хоҳад дод ва онҳоро хоҳед шинохт». Ва Парвардигорат аз он чи мекунед, ғофил нест');
INSERT INTO "pioneers" VALUES(28,1,'28:1','То, син, мим');
INSERT INTO "pioneers" VALUES(28,2,'28:2','Ин оёти китоби равшангар аст');
INSERT INTO "pioneers" VALUES(28,3,'28:3','[бахшҳое] аз достони Мӯсо ва Фиръавнро барои касоне, ки имон овардаанд, ба дурустӣ бар ту тиловат мекунем');
INSERT INTO "pioneers" VALUES(28,4,'28:4','Бе тардид, Фиръавн дар сарзамини [Миср] бартарихоҳӣ кард ва мардумашро ба гурӯҳҳо тақсим намуд. Гурӯҳе аз ононро ба нотавонӣ [ва хорӣ] мекашонд, [чунон ки] писаронашонро мекушт ва занонашонро [барои канизӣ] зинда нигаҳ медошт. Ба ростӣ, ки ӯ аз табаҳкорон буд');
INSERT INTO "pioneers" VALUES(28,5,'28:5','Ва мехостем бар Бани Исроил, ки дар он сарзамин ба нотавонӣ кашида шуда буданд, [бо нобуд кардани душманашон] миннат бигузорем ва пешвоён [-и мардум] қарорашон диҳем ва [пас аз нобудии Фиръавн] ононро ворисон [-и сарзамини Шом] намоем');
INSERT INTO "pioneers" VALUES(28,6,'28:6','Ва дар он сарзамин ҳукумат ба эшон диҳем ва он чиро Фиръавн ва [вазираш] Ҳомон ва сипоҳиёнашон ҳамвора аз он метарсиданд [суқути ҳукумат], ба онон нишон диҳем');
INSERT INTO "pioneers" VALUES(28,7,'28:7','Ва ба модари Мӯсо илҳом кардем, ки: «Ӯро шир бидеҳ ва ҳангоме ки [аз осеби фиръавниён] бар ӯ тарсидӣ, [кӯдакро дар сандуқе бигзор ва] ба рӯд [-и Нил] биандозаш ва натарс ва андуҳгин нашав [зеро] ҳатман, ӯро ба ту бозмегардонем ва аз паёмбарон қарораш медиҳем»');
INSERT INTO "pioneers" VALUES(28,8,'28:8','Пас, [чун модари Мӯсо чунин кард] хонадони Фиръавн ӯро [аз об] гирифтанд, то саранҷом [солҳо баъд ин фарзанди Бани Исроил] душман ва [мояи] андуҳашон гардад. Ба ростӣ, ки Фиръавн ва Ҳомон ва сипоҳиёнашон гунаҳгор буданд');
INSERT INTO "pioneers" VALUES(28,9,'28:9','Ва [чун Фиръавн дастури қатли Мӯсоро дод, Осия] ҳамсари Фиръавн гуфт: «[Ин писар] Нури чашми ману туст. Ӯро накушед. Чи басо ба мо суде бибахшад, ё ӯро ба фарзандӣ бигирем», ҳол он ки [оқибати корро] намедонистанд');
INSERT INTO "pioneers" VALUES(28,10,'28:10','Ва дили модари Мӯсо [аз ҳама чиз ҷуз ёди писараш] тиҳӣ гашт ва агар қалбашро қавӣ накарда будем, ки [ба ваъдаи Мо] имон дошта бошад, наздик буд он розро фош кунад');
INSERT INTO "pioneers" VALUES(28,11,'28:11','Ва [модари Мӯсо пас аз он ки ӯро дар Нил раҳо кард] ба хоҳари ӯ гуфт: «Ба дунболаш бирав», пас, [хоҳараш] аз дур ба ӯ менигарист, дар ҳоле ки онон намедонистанд [ки он духтар, хоҳари ин кӯдак аст]');
INSERT INTO "pioneers" VALUES(28,12,'28:12','Ва Мо [шири] ҳамаи занони ширдеҳро аз қабл бар ӯ ҳаром кардем [чунон ки синаи ҳеҷ якро напазируфт]. Он гоҳ [хоҳараш] гуфт: «Мехоҳед хонаводаеро нишонатон диҳам, ки ӯро бароятон нигаҳ доранд ва хайрхоҳаш бошанд?»');
INSERT INTO "pioneers" VALUES(28,13,'28:13','Пас, [онон пазируфтанд ва бад-ин сурат] ӯро ба модараш бозгардонидем, то чашмаш равшан гардад ва ғамгин набошад ва то бидонад, ки ваъдаи Аллоҳ таоло ҳақ аст, вале бештарашон намедонанд');
INSERT INTO "pioneers" VALUES(28,14,'28:14','Ва чун Мӯсо ба рушду камоли хеш расид ва баруманд шуд, ба ӯ ҳикмату дониш [-и оини Бани Исроилро] ато кардем ва накукоронро инчунин подош медиҳем');
INSERT INTO "pioneers" VALUES(28,15,'28:15','Ҳангоме ки мардум [машғули истироҳат ва] бехабар буданд, Мӯсо [махфиёна] вориди шаҳр шуд ва ду мардро дид, ки бо якдигар даргир шудаанд. Яке аз онон [аз қавми Бани Исроил ва] аз дӯстони Мӯсо буд ва дигаре [қибтӣ буд ва] аз душманонаш. Пас, он ки аз дӯстони ӯ буд, алайҳи душманаш аз Мӯсо ёрӣ хост, пас, [Мӯсо] мушт [-и маҳкам]-е ба вай заду ӯро аз пой даровард [он гоҳ] гуфт: «Ин кори шайтон буд. Ҳаққо, ки ӯ душман [-и инсон ва] гумроҳкунандаи ошкоре аст»');
INSERT INTO "pioneers" VALUES(28,16,'28:16','[Сипас] Гуфт: «Парвардигоро, ман ба худ ситам кардаам, пас, маро бибахш» ва [Аллоҳ таоло] ӯро бахшид. Бе тардид, Ӯ омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(28,17,'28:17','[Он гоҳ] Гуфт: «Парвардигоро, ба шукронаи он ки бар ман неъмат [-и қудрату дониш] арзонӣ доштаӣ, ҳаргиз пуштибони муҷримон нахоҳам буд»');
INSERT INTO "pioneers" VALUES(28,18,'28:18','[Мӯсо] Шабро бо тарсу нигаронӣ дар шаҳр ба субҳ расонд, ки ногоҳ марде, ки дирӯз аз ӯ ёрӣ хоста буд, боз ба фарёд аз ӯ ёрӣ хост. Мӯсо ба ӯ гуфт: «Воқеан, ошкор аст, ки ту [моҷароҷӯ ва] гумроҳӣ»');
INSERT INTO "pioneers" VALUES(28,19,'28:19','Пас, ҳангоме ки [Мӯсо] хост, ба касе, ки душмани ҳар дуи онон буд, ҳамла кунад, [он марди қибтӣ] гуфт: «Эй Мӯсо, оё мехоҳӣ ҳамон гуна ки дирӯз касеро куштӣ, [имрӯз низ] маро бикушӣ? Қасди ту фақат зӯргӯӣ дар ин сарзамин аст ва намехоҳӣ аз ислоҳгарон [байни афрод] бошӣ»');
INSERT INTO "pioneers" VALUES(28,20,'28:20','Ва [дар ин ҳангом] марде [ки аз дӯстони Мӯсо буд] аз дуртарин нуқтаи шаҳр шитобон омад [ва] гуфт: «Эй Мӯсо, бузургон [-и қавми Фиръавн] дар бораи куштанат ба машварат нишастаанд, пас, бедиранг [аз шаҳр] хориҷ шав, [ки] ман ҳатман, хайрхоҳат ҳастам»');
INSERT INTO "pioneers" VALUES(28,21,'28:21','Пас, Мӯсо бо тарсу нигаронӣ аз шаҳр берун рафт ва гуфт: «Парвардигоро, маро аз [осеби] ситамгорон наҷот бидеҳ»');
INSERT INTO "pioneers" VALUES(28,22,'28:22','Ва чун ба шаҳри Мадян рӯ овард, гуфт: «Умед аст, Парвардигорам маро ба роҳи рост ҳидоят кунад»');
INSERT INTO "pioneers" VALUES(28,23,'28:23','Ва ҳангоме ки ба [чоҳи] оби Мадян расид, бар сари чоҳ гурӯҳе аз мардумро дид, ки [чаҳорпоёни худро] об медиҳанд ва наздики онон ду занро дид, ки [гӯсфандонашонро] канор мекашиданд [ва аз дигарон фосила мегирифтанд. Мӯсо] гуфт: «Шумо чи мекунед? [Чаро гӯсфандони худро об намедиҳед.] Онон гуфтанд: «Мо [ба рамаи худ] об намедиҳем, то ин чӯпонҳо бозгарданд [ва музоҳимамон набошанд]. Падарамон пирамарде куҳансол аст [ва тавоноии ин корро надорад]»');
INSERT INTO "pioneers" VALUES(28,24,'28:24','Мӯсо барои он ду нафар [гӯсфандонашонро] об дод, он гоҳ сӯйи соя бозгашт ва гуфт: «Парвардигоро, ба ҳар хайре, ки бароям бифиристӣ, сахт ниёзмандам»');
INSERT INTO "pioneers" VALUES(28,25,'28:25','Он гоҳ яке аз он ду [зан], дар ҳоле ки бо шарму ҳаё гом бармедошт, наздаш омаду гуфт: «Падарам даъватат мекунад [ки наздаш биравӣ], то музди он ки [гӯсфандон]-ро бароямон об додӣ, ба ту пардохт кунад». Чун [Мӯсо] назди ӯ рафту саргузашташро барои ӯ ҳикоят кард, вай гуфт: «Натарс, ки аз гурӯҳи ситамгор наҷот ёфтаӣ»');
INSERT INTO "pioneers" VALUES(28,26,'28:26','Яке аз он ду [духтар] гуфт: «Падарҷон, ӯро истихдом кун, [зеро] бе тардид, беҳтарин касе, ки метавонӣ ба кор бигирӣ он аст, ки неруманду амонатдор бошад [ва ӯ чунин аст]»');
INSERT INTO "pioneers" VALUES(28,27,'28:27','[Пирамард ба Мӯсо] Гуфт: «Мехоҳам яке аз ду духтарамро ба ҳамсариат дароварам, ба ин [шарт] ки ҳашт сол бароям кор кунӣ ва агар [ин корро] то даҳ сол идома диҳӣ, лутфу муҳаббате аз сӯйи туст. Ман намехоҳам бар ту сахтгирӣ кунам. Ин шоал-л-лоҳ маро шоиста [ва накукор] хоҳӣ ёфт»');
INSERT INTO "pioneers" VALUES(28,28,'28:28','[Мӯсо] Гуфт: «Ин [қарордод] миёни ману ту бошад, ки ҳар кадом аз ин ду муддат [ҳашт ё даҳ сол]-ро, ки анҷом додам, [ба аҳди худ вафо кардаам], пас, ситаме бар ман набошад [ва нахоҳӣ, ки бештар бимонам] ва Аллоҳ таоло бар он чи мегӯем, гувоҳ аст»');
INSERT INTO "pioneers" VALUES(28,29,'28:29','Ҳангоме ки Мӯсо [он] муддат [-и муайян]-ро ба поён расонд ва ҳамроҳи хонаводааш [аз Мадян ба сӯйи Миср] ҳаракат кард, [ногаҳон дар торикии шаб] аз сӯйи [кӯҳи] Тӯр оташе дид, ба хонаводааш гуфт: «Биистед, ки ман оташе [аз дур] дидам. Шояд хабаре аз он бароятон биёварам ё шуълаи оташе [биёварам], то гарм шавед»');
INSERT INTO "pioneers" VALUES(28,30,'28:30','Пас, вақте ба он оташ наздик шуд, дар канораи рости он сарзамини дар минтақаи пурбаракат [-и Тӯр] аз [миёни] як дарахт нидод дода шуд, ки: «Эй Мӯсо, бе тардид, ман Аллоҳ таоло–Парвардигори ҷаҳониёнам');
INSERT INTO "pioneers" VALUES(28,31,'28:31','Ва асоятро бияфкан». Пас, чун [Мӯсо чунин карду] онро дид, ки ҳамчун море [босуръат] мехазад, [аз тарс] пушт карду гурехт ва бознагашт. [Нидо омад] «Эй Мӯсо, пеш о ва натарс. Ҳатман, ту аз [осеби хатарҳо] эмин шудаӣ');
INSERT INTO "pioneers" VALUES(28,32,'28:32','Ва дастро дар гиребонат фурӯ бар [то] сапеду бидуни айб [ва бемории песӣ] хориҷ шавад ва [барои раҳоӣ] аз ин тарс дастонатро ба самти худ ҷамъ кун [ва ба синаат бичаспон]. Ин ду [муъҷизаи асо ва яди байзо] ду далели [равшан] аз сӯи Парвардигорат барои Фиръавн ва бузургони [қавми] ӯст. Ба ростӣ, ки онон гурӯҳе нофармонанд»');
INSERT INTO "pioneers" VALUES(28,33,'28:33','[Мӯсо] Гуфт: «Парвардигоро, ман яке аз ононро [бидуни амд] куштаам, метарсам, ки [ба қасоси он қатл] маро бикушанд');
INSERT INTO "pioneers" VALUES(28,34,'28:34','Ва бародарам – Ҳорун – забонаш аз ман шевотар аст, пас, ӯро ҳамроҳам бифирист, то ёварам бошад ва ростгӯиямро таъйид кунад, зеро метарсам, маро дурӯғгӯ ангоранд»');
INSERT INTO "pioneers" VALUES(28,35,'28:35','[Аллоҳ таоло] Фармуд: «Туро бо [пайвастани] бародарат тақвият хоҳем намуд ва бароятон тасаллуте қарор медиҳем, ки [фиръавниён ҳаргиз] ба шумо даст наёбанд ва ба [баракати] муъҷизотамон [ки дар ихтиёр доред] шумо ва ҳар ки аз шумо пайравӣ кунад, пирӯзед»');
INSERT INTO "pioneers" VALUES(28,36,'28:36','Пас, чун Мӯсо бо муъҷизоти равшани Мо ба сӯяшон омад, гуфтанд: «Ин [чизе] нест, магар ҷодуе сохта ва мо ҳаргиз ин [иддаои яктопарастӣ]-ро аз падаронамон нашнидаем»');
INSERT INTO "pioneers" VALUES(28,37,'28:37','Ва Мӯсо гуфт: «Парвардигорам беҳтар медонад, чи касе ҳидоятро аз назди Ӯ овардааст ва [огоҳтар аст, ки] оқибати неки он саро барои кист. Бе тардид, ситамгорон росткор намешаванд»');
INSERT INTO "pioneers" VALUES(28,38,'28:38','Фиръавн гуфт: «Эй бузургон, маъбуде ба ҷуз худ бароятон намешиносам, пас, эй Ҳомон, бароям оташе бар гил барафрӯз [ва хишт бипаз], он гоҳ бурҷи баланде бисоз, бошад, ки аз маъбуди Мӯсо бохабар шавам. Ҳар чанд ки ҳатман, ӯро дурӯғгӯ мепиндорам»');
INSERT INTO "pioneers" VALUES(28,39,'28:39','Ва ӯ [Фиръавн] ва лашкариёнаш ба ноҳақ дар сарзамин [-и Миср] саркашӣ карданд ва пиндоштанд, ки ба сӯйи Мо бозгардонда намешаванд');
INSERT INTO "pioneers" VALUES(28,40,'28:40','Пас, [мо] ӯ ва лашкариёнашро [ба азоб] фурӯ гирифтем ва ононро ба дарё андохтем. Пас, бингар, ки оқибати ситамгорон чи гуна буд');
INSERT INTO "pioneers" VALUES(28,41,'28:41','Ва ононро пешвоёне қарор додем, ки [пайравонашонро] ба оташи [дузах] даъват мекарданд ва рӯзи қиёмат ёрӣ намешаванд');
INSERT INTO "pioneers" VALUES(28,42,'28:42','Дар ин дунё ба дунболашон лаънат равон кардем ва рӯзи қиёмат [низ] зиштманзаранд');
INSERT INTO "pioneers" VALUES(28,43,'28:43','Ва ба ростӣ, пас аз он ки умматҳои пешинро нобуд намудем, ба Мӯсо китоб [Таврот] додем, ки [муштамил бар] равшангариҳо ва ҳидоят ва раҳмате барои мардум буд, бошад, ки панд пазиранд');
INSERT INTO "pioneers" VALUES(28,44,'28:44','Ва ту [эй паёмбар] он гоҳ ки фармони [нубувват]-ро ба Мӯсо медодем, дар доманаи ғарбӣ [-и кӯҳи Тӯр] ҳузур надоштӣ ва аз гувоҳон [низ] набудӣ');
INSERT INTO "pioneers" VALUES(28,45,'28:45','Ва [Мо пас аз Мӯсо] наслҳое офаридем ва солҳои тӯлонӣ бар онон гузашт [ва паймони илоҳиро фаромӯш карданд]. Ва ту дар миёни аҳли Мадян иқомат надоштӣ [ки аз достони Мӯсо бохабар бошӣ ва] оёти Моро бар онон [мардуми Макка] бихонӣ, балки Мо будем, ки [ин оётро ба сӯят] мефиристодем');
INSERT INTO "pioneers" VALUES(28,46,'28:46','Ва он гоҳ ки [ба Мӯсо] нидо додем, ту дар канори кӯҳи Тӯр набудӣ, вале ба унвони раҳмате аз сӯйи Парвадигорат [ба ту ваҳй намудем], то гурӯҳеро, ки пеш аз ту бимдиҳандае ба суроғашон наомадааст, [бо ин оёт] бим диҳӣ, бошад, ки панд пазиранд');
INSERT INTO "pioneers" VALUES(28,47,'28:47','[Бо кофирон итмоми ҳуҷҷат кардем] То вақте ба кайфари корҳое, ки дар гузашта муртакиб шудаанд, мусибате ба онҳо мерасад, нагӯянд: «Парвардигоро, чаро паёмбаре бар мо нафиристодӣ, то аз оётат пайравӣ кунем ва аз муъминон бошем»?');
INSERT INTO "pioneers" VALUES(28,48,'28:48','Вале ҳангоме ки [паёмбари] ҳақ аз ҷониби Мо ба суроғашон омад, [яҳудиён аз сари лаҷоҷат] гуфтанд: «Чаро муъҷизоте ҳаммонанд Мӯсо ба ӯ дода нашудааст»? [Дар посухашон бигӯ] «Магар [яҳудиён] дар гузашта муъҷизоти Мӯсоро инкор накарданд»? Онон гуфтанд: «[Қуръону Таврот] ду ҷоду ҳастанд, ки якдигарро таъйид мекунанд» ва гуфтанд: «Мо тамоми [матолиби] онҳоро инкор мекунем»');
INSERT INTO "pioneers" VALUES(28,49,'28:49','Бигӯ: «Агар ростгӯед, аз ҷониби Аллоҳ таоло китобе ҳидоятбахштар аз ин ду [китоб] биёваред, то ман низ аз он пайравӣ кунам»');
INSERT INTO "pioneers" VALUES(28,50,'28:50','Пас, агар [Қурайш] ба дархостат посухе надоданд, бидон, ки онон фақат аз ҳавои нафсашон пайравӣ мекунанд ва кист гумроҳтар аз он ки бидуни раҳнамуде аз сӯи Аллоҳ таоло аз ҳавои нафсаш пайравӣ мекунад? Бе тардид, Аллоҳ таоло гурӯҳи ситамгорро ҳидоят намекунад');
INSERT INTO "pioneers" VALUES(28,51,'28:51','Ва ба ростӣ, Мо ин оёт [ва ҳикоёт]-ро яке пас аз дигаре барои онон [мушрикону яҳудиён] нозил кардем, бошад, ки панд гиранд');
INSERT INTO "pioneers" VALUES(28,52,'28:52','Касоне, ки пеш аз ин [оёти илоҳӣ] ба онон китоб [Таврот] додаем, ба ин [Қуръон] низ имон меоваранд');
INSERT INTO "pioneers" VALUES(28,53,'28:53','Ва чун [оёташ] бар онон хонда мешавад, мегӯянд: «Ба он имон овардем. Яқинан, сухани ҳаққе аст аз ҷониби Парвардигорамон. Ба ростӣ, ки Мо пеш аз [нузули] он низ таслим [-и фармони Парвардигор] будаем»');
INSERT INTO "pioneers" VALUES(28,54,'28:54','Инонанд, ки ба поси он ки шикебоӣ карданд, подошашонро ду бор мегиранд ва бо корҳои нек [гуноҳон ва] бадиҳоро дафъ мекунанд ва аз он чи ба онон рӯзӣ додаем, инфоқ менамоянд');
INSERT INTO "pioneers" VALUES(28,55,'28:55','Ва ҳар гоҳ сухане беҳуда [ва ношоиста аз аҳли китоб] мешунаванд, [бо бетаваҷҷуҳӣ] аз он рӯй мегардонанд ва мегӯянд: «[Подоши] Корҳои мо барои мост ва [кайфари] корҳои шумо аз они шумост, [шумо аз озору носозии мо] дар амонед. Мо ба дунболи [ҳамсуҳбатӣ бо] ҷоҳилон нестем»');
INSERT INTO "pioneers" VALUES(28,56,'28:56','[Эй паёмбар] Бе тардид, ту ҳар киро дӯст дошта бошӣ, наметавонӣ [ба иҷбор ба роҳи рост] ҳидоят кунӣ, балки Аллоҳ таоло аст, ки ҳар касро бихоҳад, ҳидоят мекунад ва ٕӮ ба [ҳоли] роҳёфтагон донотар аст');
INSERT INTO "pioneers" VALUES(28,57,'28:57','Ва [мушрикон] гуфтанд: «Агар ҳамроҳи ту аз ҳидоят пайравӣ кунем, моро аз сарзаминамон мерабоянд». Оё мо ононро дар ҳарами амне ҷой надодем, ки анвои маҳсулот – ба унвони рӯзие аз ҷониби Мо – ба сӯяшон сарозер мегардад? Вале бештарашон [қадри неъматҳои илоҳиро] намедонанд');
INSERT INTO "pioneers" VALUES(28,58,'28:58','Ва чи бисёр [мардуми] шаҳрҳоеро нобуд сохтем, ки дар ҳаққи неъматҳои илоҳӣ носипосӣ карда буданд. Ин хонаҳои [вайроншудаи] онон аст, ки пас аз эшон ҷуз андаке - [касе] дар онҳо сукунат накардааст ва Мо вориси онон будем');
INSERT INTO "pioneers" VALUES(28,59,'28:59','Ва Парвардигорат ҳаргиз [мардуми] шаҳрҳоро нобуд намекунад, магар он ки дар шаҳри бузурге аз онҳо [ҳамчун Макка] паёмбаре бифиристад, ки оёти Моро бар онон бихонад. Ва Мо ҳаргиз шаҳрҳоро нобуд накардем, магар он ки сокинонаш [кофиру] ситамгор будаанд');
INSERT INTO "pioneers" VALUES(28,60,'28:60','Ва он чи ба шумо дода шудааст, баҳра [-и гузаро] ва зинати зиндагии дунёст ва он чи назди Аллоҳ таоло аст, беҳтар ва пойдортар аст. Оё намеандешед?');
INSERT INTO "pioneers" VALUES(28,61,'28:61','Оё касе, ки ба ӯ ваъдаи наку додаем ва ба он хоҳад расид, ҳамчун касест, ки ӯро аз баҳраи зиндагии дунё бархӯрдор сохтаем ва рӯзи қиёмат аз [ҷумлаи] эҳзоршудагон [дар оташи дузах] аст?');
INSERT INTO "pioneers" VALUES(28,62,'28:62','Ва [ёд кун аз] рӯзе, ки Аллоҳ таоло ба онон нидо медиҳад ва мегӯяд: «Афроде, ки шарикони ман [дар парастиш] мепиндоштед, куҷо ҳастанд?»');
INSERT INTO "pioneers" VALUES(28,63,'28:63','Касоне [аз сарони кофирон], ки азоб барояшон ҳатмӣ аст, мегӯянд: «Парвардигоро, инҳо касоне ҳастанд, ки мо гумроҳашон кардем. [Оре] Ононро гумроҳ кардем, чунон ки [худ низ] гумроҳ шудем. Аз [ширку тавассули] эшон ба сӯят безорӣ меҷӯем. [Дар ҳақиқат] Онон моро намепарастиданд [балки шайтонро парастиш мекарданд]»');
INSERT INTO "pioneers" VALUES(28,64,'28:64','Ва [ба мушрикон] гуфта мешавад: «Шариконатонро [ки ба ҷойи Аллоҳ таоло мепарастидед] фаро бихонед [то ёриатон кунанд]». Пас, ононро мехонанд, вале посухе ба эшон намедиҳанд ва азоби [дузах]-ро мебинанд [ва орзу мекунанд], кош ҳидоятёфта буданд');
INSERT INTO "pioneers" VALUES(28,65,'28:65','Ва рӯзе, ки [Аллоҳ таоло] ононро нидо медиҳад ва мефармояд: «Ба [даъвати] паёмбарон чи посухе додед?»');
INSERT INTO "pioneers" VALUES(28,66,'28:66','Дар он рӯз ҳамаи далоил [ва баҳонаҳое, ки барои тавҷеҳ доранд] аз онон пинҳон мегардад ва аз [шиддати азоб аз ҳоли] якдигар намепурсанд');
INSERT INTO "pioneers" VALUES(28,67,'28:67','Аммо касе, ки тавба кунад ва имон оварад ва кори шоиста анҷом диҳад, умед аст, ки аз растагорон бошад');
INSERT INTO "pioneers" VALUES(28,68,'28:68','Ва Парвардигорат ҳар чи бихоҳад, меофаринад ва [ҳар киро бихоҳад ба парастиш ва паёмбарӣ] бармегузинад. Мушрикон ихтиёре надоранд [ки дар ин маврид эътироз кунанд]. Аллоҳ таоло муназзаҳ ва бартар аст аз он чи бо Ӯ шарик медонанд');
INSERT INTO "pioneers" VALUES(28,69,'28:69','Ва Парвардигорат он чи дар синаҳояшон пинҳон мекунанд ва он чи ошкор месозанд, [ҳамаро] медонад');
INSERT INTO "pioneers" VALUES(28,70,'28:70','Ва Ӯ Аллоҳ таоло аст, ки ҳеҷ маъбуде [бар ҳақ] ҷуз Ӯ нест. Ситоиш дар дунёву охират барои Ӯст ва ҳукм [-у фармонравоӣ низ] аз они Ӯст ва [ҳамагӣ] ба сӯйи Ӯ бозгарданда мешавед');
INSERT INTO "pioneers" VALUES(28,71,'28:71','[Ба мушрикон] бигӯ: «Ба ман бигӯед, агар Аллоҳ таоло [торикии] шабро то рӯзи қиёмат бар шумо поянда гардонад, кадом маъбуд ба ҷуз Аллоҳ таоло бароятон равшанӣ меоварад? Оё [сухани ҳақро] намешунавед?');
INSERT INTO "pioneers" VALUES(28,72,'28:72','Бигӯ “Ба ман бигӯед, агар Аллоҳ таоло [рӯшноии] рӯзро то рӯзи қиёмат бар шумо поянда гардонад, кадом маъбуд ба ҷуз Аллоҳ таоло бароятон шабе меоварад, ки дар он ором бигиред? Оё [ин нишонаҳои қудрати илоҳиро] намебинед?');
INSERT INTO "pioneers" VALUES(28,73,'28:73','Ва Аллоҳ таоло аз раҳмати хеш шабу рӯзро бароятон қарор дод, то дар шаб ором гиред ва дар рӯз [талош кунед ва] аз бахшиши Ӯ [рӯзӣ] биҷӯед ва бошад, ки сипос гузоред');
INSERT INTO "pioneers" VALUES(28,74,'28:74','Ва [ёд кун аз] рӯзе, ки Аллоҳ таоло ба онон нидо медиҳад ва мегӯяд: «Афроде, ки шарикони ман [дар парастиш] мепиндоштед, куҷо ҳастанд?»');
INSERT INTO "pioneers" VALUES(28,75,'28:75','Ва аз ҳар уммате гувоҳе бармегузинем [ки паёмбари онон аст ва дар мавриди куфрашон шаҳодат медиҳад]. Сипас ба онон мегӯем: «Далели [куфру ширки] хешро биёваред», пас, медонанд, ки ҳақ аз они Аллоҳ таоло аст ва дурӯғҳое, ки мебофтанд [ва он чи мушрикона мепарастиданд], маҳву нобуд гаштааст');
INSERT INTO "pioneers" VALUES(28,76,'28:76','Қорун аз қавми Мӯсо буд, аммо бар онон фахрфурӯшӣ намуд ва аз ганҷина он қадар ба вай дода будем, ки [ҳатто] ҳамли калидҳояш барои гурӯҳе неруманд мушкил буд. Ҳангоме ки [мардуми] қавмаш ба ӯ гуфтанд: «[Ба хотири сарватат чунин мағруру] сармаст набош. Бе тардид, Аллоҳ таоло сармастонро дӯст намедорад');
INSERT INTO "pioneers" VALUES(28,77,'28:77','Ва бо он чи Аллоҳ таоло ба ту бахшидааст, [савоби] сарои охиратро биҷӯй ва [дар айни ҳол] баҳраатро аз [зиндагии] дунё низ фаромӯш накун ва чунон ки Аллоҳ таоло ба ту некӣ кардааст, ту низ [ба бандагонаш] некӣ кун ва ҳаргиз дар пайи табаҳкорӣ [ва фасод] дар замин набош. Бе тардид, Аллоҳ таоло муфсидонро дӯст намедорад»');
INSERT INTO "pioneers" VALUES(28,78,'28:78','[Қорун] гуфт: «Он чи ба ман дода шуда, дар натиҷаи [қудрату] донишест, ки назди ман аст». Оё намедонист, ки Аллоҳ таоло наслҳоеро пеш аз вай нобуд карда буд, ки аз ӯ нерумандтар ва сарватмандтар буданд? Ва [рӯзи қиёмат] аз муҷримон дар бораи гуноҳонашон суол намешавад [зеро Аллоҳ таоло ҳамаро медонад]');
INSERT INTO "pioneers" VALUES(28,79,'28:79','Он гоҳ Қорун бо [таҷаммулоту] зинатҳояш дар баробари қавмаш зоҳир шуд. Афроде [аз атрофиёни ӯ], ки хостори зиндагии дунё буданд, гуфтанд: «Эй кош, ҳаммонанди он чи ба Қорун дода шудааст, мо низ доштем. Воқеан, ӯ баҳраи бузурге [аз моли дунё] дорад»');
INSERT INTO "pioneers" VALUES(28,80,'28:80','Аммо касоне, ки дониш [-и ҳақиқӣ] ёфта буданд, гуфтанд: «Вой бар шумо! Барои касе, ки имон овардааст ва корҳои шоиста мекунад, савоби илоҳӣ беҳтар аст ва ин [сухан]-ро касоне ба ҷуз шикебоён намепазиранд»');
INSERT INTO "pioneers" VALUES(28,81,'28:81','Он гоҳ Қорунро ҳамроҳи [хонааш дар замин фуру бурдем, пас, ҳеҷ гурӯҳе надошт, ки дар баробари [азоби] Аллоҳ таоло ёриаш кунанд ва [худ низ] натавонист ба хештан ёрӣ расонад');
INSERT INTO "pioneers" VALUES(28,82,'28:82','Ва касоне, ки дирӯз орзу мекарданд, ба ҷойи ӯ бошанд, бомдод [-и рӯзи баъд] мегуфтанд: «Вой бар мо! Гӯӣ Аллоҳ таоло аст, ки [неъмату] рӯзиро бар ҳар ки бихоҳад густурда медорад ва [ё] танг мегирад. Агар Аллоҳ таоло бар мо миннат наниҳода буд, ҳатман, моро [низ ба сазои суханонамон ба жарфои замин] фурӯ мебурд. Эй вой! Гӯӣ кофирон ҳаргиз растагор намешаванд');
INSERT INTO "pioneers" VALUES(28,83,'28:83','Ин сарои охиратро [фақат] барои касоне қарор медиҳем, ки хоҳони гарданкашӣ ва фасод дар замин нестанд ва саранҷоми нек аз они парҳезкорон аст');
INSERT INTO "pioneers" VALUES(28,84,'28:84','[Рӯзи қиёмат] Ҳар ки некӣ биёварад, [подоше] беҳтар аз он хоҳад дошт ва ҳар ки бадӣ биёварад, [бидонад, ки] касоне, ки корҳои ношоиста кардаанд, ҷуз [ба миқдори] он чи мекарданд, кайфар намебинанд');
INSERT INTO "pioneers" VALUES(28,85,'28:85','Ҳамон [Парвардигоре], ки Қуръонро [нозил намуд ва таблиғашро] бар ту воҷиб кард, ҳатман, туро ба ҷойгоҳат [дар Макка] бозмегардонад. Бигӯ: «Парвардигорам беҳтар медонад, чи касе ҳидоят овардааст ва чи касе дар гумроҳии ошкор аст»');
INSERT INTO "pioneers" VALUES(28,86,'28:86','Ва [пеш аз биъсат] ту орзу надоштӣ, ки ин китоб [-и осмонӣ] бар ту нозил шавад. [Ин набуд] Магар раҳмате аз ҷониби Парвардигорат. Пас, ҳаргиз пуштибони кофирон набош');
INSERT INTO "pioneers" VALUES(28,87,'28:87','Ва мабодо [кофирон] туро аз [таблиғи] оёти илоҳӣ – пас аз он ки бар ту нозил шуд – боздоранд! Ва [мардумро] ба сӯйи Парвардигорат даъват кун ва ҳаргиз аз мушрикон набош');
INSERT INTO "pioneers" VALUES(28,88,'28:88','Ва маъбуди дигаре бо Аллоҳ таоло [ба ниёиш] нахон. Ҳеҷ маъбуде [ба ҳақ] ҷуз Ӯ нест. Ҳама чиз фанопазир аст, магар [зоти ҷовид ва] рӯйи Ӯ. Фармонравоӣ [танҳо] аз они Ӯст ва [рӯзи қиёмат ҳамагӣ] ба пешгоҳаш бозгардонда мешавед');
INSERT INTO "pioneers" VALUES(29,1,'29:1','Алиф, лом, мим');
INSERT INTO "pioneers" VALUES(29,2,'29:2','Оё мардум пиндоштаанд, ҳамин ки [ба забон] бигӯянд: «Имон овардем», ононро [ба ҳоли худ] вомегузоранд ва озмуда намешаванд?');
INSERT INTO "pioneers" VALUES(29,3,'29:3','Ва ба ростӣ касонеро, ки пеш аз онон буданд, низ озмудем ва то Аллоҳ таоло касонеро, ки [дар бораи имонашон] рост гуфтаанд, бозшиносад ва дурӯғгӯёнро [низ] маълум гардонад');
INSERT INTO "pioneers" VALUES(29,4,'29:4','Оё касоне, ки корҳои ношоиста кардаанд, пиндоштаанд, ки метавонанд аз Мо пешӣ бигиранд [ва аз азобамон бигурезанд]? Чи бад доварӣ мекунанд!');
INSERT INTO "pioneers" VALUES(29,5,'29:5','Ҳар ки ба дидори Аллоҳ таоло умед дорад, [бидонад, ки] ваъдаи Аллоҳ таоло, ҳатман, хоҳад омад ва ӯ шунавову доност');
INSERT INTO "pioneers" VALUES(29,6,'29:6','Ва ҳар ки [дар роҳи ҳақ] бикӯшад, ба суди хеш кӯшидааст. Бе тардид, Аллоҳ таоло аз ҷаҳониён бениёз аст');
INSERT INTO "pioneers" VALUES(29,7,'29:7','Ва касоне, ки имон овардаанд ва корҳои шоиста анҷом додаанд, яқинан гуноҳонашонро мезудоем ва ононро ба беҳтар аз он чи кардаанд, подош медиҳем');
INSERT INTO "pioneers" VALUES(29,8,'29:8','Ва ба инсон супориш кардем, ки ба падару модараш некӣ намояд ва [гуфтем]: «Агар он ду кӯшиданд, то чизеро, ки ту ба он донише надорӣ шарики Ман созӣ, пас, аз онҳо итоат макун. Бозгаштатон ба сӯи Ман аст, он гоҳ шуморо аз [ҳақиқати] он чи мекардед, огоҳ хоҳам кард');
INSERT INTO "pioneers" VALUES(29,9,'29:9','Ва касоне, ки имон овардаанд ва корҳои шоиста анҷом додаанд, албатта, ононро дар [зумраи] шоистагон дармеоварем');
INSERT INTO "pioneers" VALUES(29,10,'29:10','Ва бархе аз мардум ҳастанд, ки мегӯянд «ба Аллоҳ таоло имон овардем». Аммо ҳангоме ки дар роҳи Аллоҳ таоло озор мебинанд, озори кофиронро ҳамчун азоби Аллоҳ таоло [сахту ҷонкоҳ] медонанд [ва аз имонашон даст мекашанд] ва агар аз сӯйи Парвардигорат пирӯзӣ [ва кушоише бароятон] пеш ояд, мегӯянд: «Бе тардид, мо [низ] бо шумо ҳамроҳ будем». Оё Аллоҳ таоло ба он чи дар дилҳои ҷаҳониён аст, донотар нест?');
INSERT INTO "pioneers" VALUES(29,11,'29:11','Ва ба ростӣ, Аллоҳ таоло касонеро, ки имон овардаанд, ба хубӣ мешиносад ва яқинан, мунофиқонро [низ] мешиносад');
INSERT INTO "pioneers" VALUES(29,12,'29:12','Ва касоне, ки куфр варзиданд, ба касоне, ки имон оварданд, мегуфтанд: «Аз роҳи мо пайравӣ кунед, ва ҳатман, бори гуноҳонатонро ба уҳда мегирем», вале онон ҳаргиз чизе аз гуноҳони эшонро бар уҳда нахоҳанд гирифт. Ба ростӣ, ки онон дурӯғгӯ ҳастанд');
INSERT INTO "pioneers" VALUES(29,13,'29:13','Ва, ҳатман, бори сангини [гуноҳи] худ ва борҳои сангин [-и гуноҳи пайравон]-ашонро ҳамроҳи бори сангини худ ба дӯш хоҳанд кашид ва яқинан, рӯзи қиёмат дар мавриди дурӯғҳое, ки мебофтанд, бозхост хоҳанд шуд');
INSERT INTO "pioneers" VALUES(29,14,'29:14','Ва Мо Нуҳро ба сӯи қавмаш фиристодем ва ӯ нуҳсаду панҷоҳ сол дар миёни онон буд. Он гоҳ, дар ҳоле ки ситамгор [-у кофир] буданд, тӯфон [-и сангин] ононро фаро гирифт');
INSERT INTO "pioneers" VALUES(29,15,'29:15','Пас ӯ [Нуҳ] ва сарнишинони киштиро наҷот додем ва он [киштӣ]-ро нишонае барои [ибрат гирифтани] ҷаҳониён қарор додем');
INSERT INTO "pioneers" VALUES(29,16,'29:16','Ва [ёд кун аз] Иброҳим он гоҳ ки ба қавмаш гуфт: «Аллоҳро бипарастед ва аз Ӯ парво кунед, [ки] агар бидонед, ин кор бароятон беҳтар аст');
INSERT INTO "pioneers" VALUES(29,17,'29:17','Ҷуз ин нест, ки ба ҷойи Аллоҳ таоло шумо фақат бутҳое [аз сангу чӯб]-ро мепарастед ва [бо ин кор ба Аллоҳ таоло] дурӯғ мебандед. Дар ҳақиқат, касонеро, ки шумо ба ҷойи Аллоҳ таоло мепарастед, ихтиёри рӯзиатонро надоранд, пас, [неъмату] рӯзиро танҳо аз пешгоҳи Аллоҳ таоло талаб кунед ва Ӯро бипарастед ва шукри Ӯро ба ҷо оваред [ки] ба сӯйи Ӯ бозгардонда мешавед');
INSERT INTO "pioneers" VALUES(29,18,'29:18','Ва [эй мушрикон] агар шумо [Муҳаммадро] дурӯғгӯ бидонед, [аҷиб нест] бе тардид, бархе аз умматҳои пеш аз шумо низ [паёмбарони илоҳиро] дурӯғгӯ ангоштанд. Ва паёмбар вазифае ҷуз расонидани ошкори [ваҳй] надорад»');
INSERT INTO "pioneers" VALUES(29,19,'29:19','Оё надиданд, ки Аллоҳ таоло чи гуна офаринишро оғоз мекунад, сипас онро [пас аз маргаш] бозмегардонад? Ҳатман, ин кор барои Аллоҳ таоло осон аст');
INSERT INTO "pioneers" VALUES(29,20,'29:20','[Эй паёмбар, ба кофирон] бигӯ: «Дар замин гардиш кунед ва бингаред, ки Аллоҳ таоло чи гуна офаринишро оғоз кард, пас, [ба ҳамин сурат низ] зиндагии дигарро падид меоварад. Бе тардид, Аллоҳ таоло бар ҳар коре тавоност');
INSERT INTO "pioneers" VALUES(29,21,'29:21','Ӯ ҳар киро бихоҳад, азоб мекунад ва бар ҳар ки бихоҳад, раҳмат меоварад ва [ҳамагӣ] ба сӯяш бозгардонда мешавед');
INSERT INTO "pioneers" VALUES(29,22,'29:22','Шумо - [на] дар замин ва на дар осмон – ҳаргиз нотавонкунандаи Ӯ нестед ва ҷуз Аллоҳ таоло ҳеҷ корсозу ёваре надоред»');
INSERT INTO "pioneers" VALUES(29,23,'29:23','Ва касоне, ки ба оёти Аллоҳ таоло ва дидори Ӯ куфр варзиданд, ононанд, ки аз раҳмати Ман маъюсанд ва азоби дардноке барояшон [дар пеш] аст');
INSERT INTO "pioneers" VALUES(29,24,'29:24','Он гоҳ ҷавоби қавми ӯ [Иброҳим] ҷуз ин набуд, ки гуфтанд: «Ӯро бикушед, бисӯзонед». Пас, Аллоҳ таоло аз оташ наҷоташ дод. Бе тардид, дар ин [раҳоӣ аз оташ] барои гурӯҳе, ки имон меоваранд, нишонаҳое [барои ибрат гирифтан] аст');
INSERT INTO "pioneers" VALUES(29,25,'29:25','Ва [Иброҳим] гуфт: «Шумо ба ҷойи Аллоҳ таоло бутҳое барои худ баргузидаед, ки [мояи дӯстӣ ва] муҳаббат миёни шумо дар зиндагии дунё бошад. Он гоҳ рӯзи қиёмат мункири якдигар мешавед ва ҳамдигарро лаънат мекунед ва ҷойгоҳатон оташ аст ва ҳеҷ ёригаре нахоҳед дошт»');
INSERT INTO "pioneers" VALUES(29,26,'29:26','Пас, Лут ба ӯ имон овард ва [Иброҳим] гуфт: «Ман ба сӯйи Парвардигорам [ба сарзамини Шом] ҳиҷрат мекунам. Бе тардид, Ӯ шикастнопазири ҳаким аст»');
INSERT INTO "pioneers" VALUES(29,27,'29:27','Ва Исҳоқу Яъқубро ба ӯ бахшидем ва миёни фарзандонаш нубувват ва китоб [-и осмонӣ] қарор додем ва подошашро дар ин дунё додем ва яқинан, ӯ дар охират [низ] аз шоистагон хоҳад буд');
INSERT INTO "pioneers" VALUES(29,28,'29:28','Ва [ёд кун аз] Лут ҳангоме ки ба қавмаш гуфт: «Воқеан, кори бисёр зиште мекунед, ки пеш аз шумо ҳеҷ як аз мардуми ҷаҳон анҷом надодааст');
INSERT INTO "pioneers" VALUES(29,29,'29:29','Оё бо мардон меомезед ва [ба қасди таҷовуз ба мусофирон] роҳро мебандед ва дар маҷолиси худ муртакиби корҳои нописанд мешавед»? Пас, посухи қавмаш ҷуз ин набуд, ки гуфтанд: «Агар ростгӯӣ, азоби илоҳиро пеш овар»');
INSERT INTO "pioneers" VALUES(29,30,'29:30','[Лут] Гуфт: «Парвардигоро, маро дар баробари муфсидон ёрӣ кун»');
INSERT INTO "pioneers" VALUES(29,31,'29:31','Ва ҳангоме ки фиристодагони мо ба Иброҳим башорати [таваллуди писарашро] доданд, ба ӯ гуфтанд: «Мо сокинони шаҳри [Садум]-ро нобуд хоҳем кард, [чаро ки] бе тардид, мардуми он ҷо ситамгоранд»');
INSERT INTO "pioneers" VALUES(29,32,'29:32','[Иброҳим] Гуфт: «Лут низ дар он ҷост». Фариштагон гуфтанд: «Мо беҳтар медонем чи касоне дар он ҷо ҳастанд. Ӯ ва хонаводаашро, ҳатман, наҷот хоҳем дод, магар ҳамсараш, ки аз бозмондагон [дар азоб] хоҳад буд»');
INSERT INTO "pioneers" VALUES(29,33,'29:33','Ва чун фариштагони мо назди Лут омаданд, аз [тасаввури бешармии қавмаш нисбат ба] онон нигарон ва дилтанг шуд ва [фариштагон] гуфтанд: Натарс ва андуҳгин набош. Мо ту ва хонаводаатро, ҳатман, наҷот медиҳем, магар ҳамсаратро, ки аз бозмондагон [дар азоб] аст');
INSERT INTO "pioneers" VALUES(29,34,'29:34','Мо бар сокинони ин шаҳр – ба кайфари он ки нофармонӣ [ва гуноҳ] кардаанд – азобе аз осмон фуруд меоварем»');
INSERT INTO "pioneers" VALUES(29,35,'29:35','Ва дар ҳақиқат, мо аз он шаҳр [-и вайроншуда] барои касоне, ки меандешанд, нишона [ва ибрати] ошкоре боқӣ гузоштем');
INSERT INTO "pioneers" VALUES(29,36,'29:36','Ва ба сӯйи [сокинони] Мадян бародарашон Шуайб [-ро фирситодем]. Ӯ гуфт: «Эй қавми ман, Аллоҳро бипарастед ва ба рӯзи бозпасин умедвор бошед ва дар замин ба фасод [ва табоҳӣ] накӯшед»');
INSERT INTO "pioneers" VALUES(29,37,'29:37','Вале ӯро дурӯғгӯ ангоштанд ва зилзила ононро фаро гирифт. Пас, дар хонаҳои худ ба рӯй афтоданд ва ҳалок шуданд');
INSERT INTO "pioneers" VALUES(29,38,'29:38','Ва [қавми] Од ва Самудро [низ ҳалок кардем] ва яқинан бархе аз хонаҳои [вайроншудаи] онон бароятон намоён аст. Ва шайтон корҳояшонро дар назарашон биёрост ва ононро аз роҳи [ҳақ] боздошт, дар ҳоле ки [нисбат ба ҳаққу ботил] бино буданд');
INSERT INTO "pioneers" VALUES(29,39,'29:39','Ва Қорун ва Фиръавн ва Ҳомонро [низ нобуд кардем] ва ба ростӣ, Мӯсо бо далоили равшан наздашон омад, пас, онон дар сарзамин [-и Миср] саркашӣ карданд, вале [аз азоби илоҳӣ] пешӣ нагирифтанд [ва роҳи фирор надоштанд]');
INSERT INTO "pioneers" VALUES(29,40,'29:40','Пас, ҳар яке аз эшонро ба [кайфари] гуноҳашон азоб кардем. Пас, ба бархе аз онон борони санг фиристодем ва бархе дигарро бонги маргбор фурӯ гирифт ва бархеро дар замин фурӯ бурдем ва бархеро ғарқ кардем. Аллоҳ таоло ҳаргиз ба онон ситам накард, балки онон [худ] ба хештан ситам мекарданд');
INSERT INTO "pioneers" VALUES(29,41,'29:41','Масали касоне, ки ба ҷойи Аллоҳ таоло дӯстоне [аз сангу чӯб ва мурдагонро ба унвони шафоатгар] барои худ баргузидаанд, ҳамчун масали анкабут аст, ки хонаи [бедавомро паноҳгоҳи хеш] баргузидааст ва бе тардид, сусттарин хонаҳо хонаи анкабут аст. Агар [мушрикон ин ҳақиқатро] медонистанд, [бутҳоро ба парастиш намегирифтанд]');
INSERT INTO "pioneers" VALUES(29,42,'29:42','Яқинан, Аллоҳ таоло ҳар он чиро ба ҷойи Ӯ [ба парастиш] мехонанд медонад ва Ӯ шикастнопазири ҳаким аст');
INSERT INTO "pioneers" VALUES(29,43,'29:43','Ин масалҳоро барои мардум мезанем ва ҷуз доноён касе онҳоро дарнамеёбад');
INSERT INTO "pioneers" VALUES(29,44,'29:44','Аллоҳ таоло осмонҳо ва заминро ба ҳақ офарид. Бе тардид, дар ин [офариниш] барои муъминон нишонае [аз қудрати илоҳӣ] аст');
INSERT INTO "pioneers" VALUES(29,45,'29:45','[Эй паёмбар] Он чиро аз ин китоб бар ту ваҳй шудааст, тиловат кун ва намоз барпо дор. Ба ростӣ, ки намоз инсонро аз гуноҳ ва зишткорӣ бозмедорад ва, албатта, ёди Аллоҳ таоло [аз ҳар коре] болотар аст ва Аллоҳ таоло медонад, ки чи мекунед');
INSERT INTO "pioneers" VALUES(29,46,'29:46','[Эй муъминон] Бо аҳли китоб ҷуз ба беҳтарин шева баҳсу мунозира накунед, магар касоне аз онон, ки ситам карданд [ва бо шумо ба ҷанг брахостанд] ва бигӯед: «Мо ба тамоми он чи [аз сӯйи Аллоҳ таоло] бар мо ва шумо нозил шудааст, имон овардаем ва маъбуди мо ва шумо якест ва мо таслими [фармони] Ӯ ҳастем»');
INSERT INTO "pioneers" VALUES(29,47,'29:47','Ва бад-ин гуна [ки ба паёмбарони пешин китоби осмонӣ фурӯ фиристодем] ин китобро [низ] бар ту нозил кардем, пас, касоне, ки [пеш аз ту] ба онон китоб [-и осмонӣ] додем, ба ин [Қуръон низ] имон меоваранд ва аз инон [мушрикон] касоне ҳастанд, ки ба он имон меоваранд ва ҷуз кофирон [касе] оёти Моро инкор намекунад');
INSERT INTO "pioneers" VALUES(29,48,'29:48','Ва пеш аз [нузули] Қуръон ту [ҳаргиз] ҳеҷ китобе намехондӣ ва бо дасти худ чизе наменавиштӣ, вагарна, ҳатман, ботиландешон [дар бораи ҳақиқати паёмбариат] тардид мекарданд');
INSERT INTO "pioneers" VALUES(29,49,'29:49','Балки он [китоб муштамил бар] оёти равшанест, ки дар синаи донишварон [-и муъмин] ҷой дорад ва ҷуз ситамгорон [-и мушрик касе] оёти Моро инкор намекунад');
INSERT INTO "pioneers" VALUES(29,50,'29:50','Ва [мушрикон] гуфтанд: «Чаро муъҷизоте аз сӯи Парвардигораш бар ӯ нозил нашудааст»? Бигӯ: «Муъҷизот назди Аллоҳ таоло аст [ва ба фармони Ӯ нозил мешавад] ва ман фақат бимдиҳандаи ошкорам»');
INSERT INTO "pioneers" VALUES(29,51,'29:51','Оё барояшон кофӣ нест, ки Мо Қуръонро бар ту нозил кардем, ки [пайваста] бар онон хонда мешавад? Бе гумон, дар ин [Қуръон] барои мардуме, ки имон меоваранд, раҳмату панд аст');
INSERT INTO "pioneers" VALUES(29,52,'29:52','Бигӯ: «Ҳамин бас, ки Аллоҳ таоло миёни ману шумо гувоҳ аст. Ӯ он чиро, ки дар осмонҳо ва замин аст, медонад ва касоне, ки ба ботил гаравидаанд ва ба Аллоҳ таоло куфр варзидаанд, ононанд, ки зиёнкоранд»');
INSERT INTO "pioneers" VALUES(29,53,'29:53','Ва [мушрикон] ба шитоб аз ту дархости азоб мекунанд ва агар мавъиди муқарраре [дар кор] набуд, яқинан, азоби [илоҳӣ] ба [суроғи] онон меомад ва [саранҷом] дар ҳоле ки ғофиланд, ногаҳон ба суроғашон хоҳад омад');
INSERT INTO "pioneers" VALUES(29,54,'29:54','Ва [мушрикон] ба шитоб аз ту дархости азоб мекунанд, ва яқинан, дузах бар кофирон иҳота дорад');
INSERT INTO "pioneers" VALUES(29,55,'29:55','Рӯзе, ки азоб аз болои сару зери пояшон ононро фаро мегирад ва [Аллоҳ таоло ба онон] мефармояд: «[Кайфари] Он чиро анҷом медодед, бичашед');
INSERT INTO "pioneers" VALUES(29,56,'29:56','Эй бандагони ман, ки имон овардаед, бе гумон, замини Ман густарда аст [пас, барои раҳоӣ аз фишори мушрикон ҳиҷрат кунед] ва танҳо Маро парастед»');
INSERT INTO "pioneers" VALUES(29,57,'29:57','Ҳар касе чашандаи [таъми] марг аст; он гоҳ ба сӯи Мо бозгардонда мешавед');
INSERT INTO "pioneers" VALUES(29,58,'29:58','Ва касоне, ки имон овардаанд ва корҳои шоиста анҷом додаанд, ҳатман, ононро дар ғурфаҳое аз биҳишт ҷой медиҳем, ки ҷӯйборҳо аз зери он ҷорӣ аст. Ҷовидона дар он хоҳанд монд. Чи накуст подоши амалкунандагон!');
INSERT INTO "pioneers" VALUES(29,59,'29:59','[Ҳамон] Касоне, ки шикебоӣ варзиданд ва танҳо бар Парвардигорашон таваккал мекунанд');
INSERT INTO "pioneers" VALUES(29,60,'29:60','Ва чи бисёр ҷондороне, ки [наметавонанд] рӯзии худро ҳамл [ё захира] кунанд. Аллоҳ таоло ба онҳо ва шумо рӯзӣ медиҳад ва Ӯ шунавову доност');
INSERT INTO "pioneers" VALUES(29,61,'29:61','Ва агар аз онон [мушрикон] бипурсӣ: «Чи касе осмонҳо ва заминро офарида ва хуршеду моҳро ба хидмат [-и инсон] гумоштааст?». Мусалламан, мегӯянд: «Аллоҳ таоло. Пас, чи гуна [аз ҳақ] рӯйгардон мешаванд?');
INSERT INTO "pioneers" VALUES(29,62,'29:62','Аллоҳ таоло [неъмату] рӯзиро бар ҳар ки бихоҳад густурда медорад ва [ё] танг мегирад. Бе тардид, Аллоҳ таоло ба ҳар чизе доност');
INSERT INTO "pioneers" VALUES(29,63,'29:63','Ва агар аз онон бипурсӣ: «Чи касе аз осмон борон фиристод ва ба василаи он заминро – пас аз хушкӣ ва хазонаш – зинда кард»? Мусалламан, мегӯянд: «Аллоҳ таоло». Бигӯ: «Ҳамду ситоиш махсуси Аллоҳ таоло аст», балки бештарашон [дар ин нишонаҳо] намеандешанд');
INSERT INTO "pioneers" VALUES(29,64,'29:64','Ва ин зиндагии дунё [чизе] ҷуз саргармӣ ва бозича нест ва бе тардид, сарои охират зиндагонӣ [-и ростин] аст. Агар [кофирон ин ҳақиқатро] медонистанд [ба дунё дилбаста намебуданд]');
INSERT INTO "pioneers" VALUES(29,65,'29:65','Вақте [мушрикон] дар киштӣ савор мешаванд, Аллоҳро бо ихлос мехонанд, аммо чун ононро ба хушкӣ мерасонад ва [аз хатари ғарқ шудан] мераҳонад, он гоҳ [боз ҳам] ширк меварзанд');
INSERT INTO "pioneers" VALUES(29,66,'29:66','[Чунин мекунанд] То нисбат ба неъматҳое, ки ба эшон додаем, носипосӣ кунанд ва [аз лаззатҳои зиндагии дунё] баҳраманд бошанд. Пас, ба зудӣ [саранҷоми дарднокашонро] хоҳанд донист');
INSERT INTO "pioneers" VALUES(29,67,'29:67','Оё надидаанд, дар ҳоле ки мардум перомунашон [гирифтори қатлу ғорат ҳастанд ва] мавриди дастбурд қарор мегиранд, Мо ҳарами амне [барои мардуми Макка] падид овардем? Оё [боз ҳам] ба [бутҳои] ботил имон меоваранд ва нисбат ба неъмати Аллоҳ таоло носипосӣ мекунанд?');
INSERT INTO "pioneers" VALUES(29,68,'29:68','Ва кист ситамгортар аз он ки бар Аллоҳ таоло дурӯғ мебандад ё ҳақро – он гоҳ ба ӯ мерасад – дурӯғ меангорад? Оё дар дузах ҷойгоҳе барои кофирон нест?');
INSERT INTO "pioneers" VALUES(29,69,'29:69','Касоне, ки дар роҳи [хушнудии] Мо мекӯшанд, ҳатман, ба роҳҳои хеш ҳидояташон мекунем ва бе тардид, Аллоҳ таоло бо накукорон аст');
INSERT INTO "pioneers" VALUES(30,1,'30:1','Алиф, лом, мим');
INSERT INTO "pioneers" VALUES(30,2,'30:2','Румиён [дар ҷанг бо Форс] шикаст хӯрданд');
INSERT INTO "pioneers" VALUES(30,3,'30:3','Дар наздиктарин сарзамин [марзи Шом бо Форс] ва онон пас аз шикасташон [дар набарди дигаре] пирӯз хоҳанд шуд');
INSERT INTO "pioneers" VALUES(30,4,'30:4','Дар чанд соли [оянда]. Чи қабл ва чи баъд ин [пирӯзӣ сарриштаи] кор дар ихтиёри Аллоҳ таоло аст ва дар он рӯз муъминон шодмон мегарданд');
INSERT INTO "pioneers" VALUES(30,5,'30:5','[Ин шодӣ] ба хотири ёрӣ расонидани Аллоҳ таоло [ба аҳли китоб] аст ва Ӯ ҳар киро бихоҳад, ёрӣ мекунад ва Ӯ шикастнопазири меҳрубон аст');
INSERT INTO "pioneers" VALUES(30,6,'30:6','[Ин пирӯзӣ] Ваъдаи Аллоҳ таоло аст [ва] Аллоҳ таоло ҳаргиз аз ваъдаи худ тахаллуф намекунад, вале бештари мардум намедонанд');
INSERT INTO "pioneers" VALUES(30,7,'30:7','Онон фақат зоҳире аз зиндагии дунёро медонанд ва аз охират ғофиланд');
INSERT INTO "pioneers" VALUES(30,8,'30:8','Оё бо худ намеандешанд, ки Аллоҳ таоло осмонҳо ва замин ва он чиро, ки миёни онҳост, ҷуз ба ҳақ ва барои муддати муайяне наофаридааст? Бе тардид, бисёре аз мардум ба дидори Парвардигорашон [дар қиёматро] бовар надоранд');
INSERT INTO "pioneers" VALUES(30,9,'30:9','Оё [мушрикон] дар замин гардиш накардаанд, то бубинанд саранҷоми касоне, ки пеш аз онон буданд, чи гуна будааст? Онҳо бисёр нерумандтар аз инон буданд ва заминро [барои кишоварзӣ ва ободкорӣ] беш аз инон дигаргун сохтанд ва обод карданд ва паёмбаронашон бо далоили равшан ба суроғашон омаданд [аммо онон инкор карданд ва нобуд шуданд]. Аллоҳ таоло ҳаргиз бар он набуд, ки ба эшон ситам кунад, балки онон худ ба хештан ситам мекарданд');
INSERT INTO "pioneers" VALUES(30,10,'30:10','Он гоҳ саранҷоми касоне, ки бадӣ карда буданд, бисёр бадтар шуд, чаро ки оёти Аллоҳро дурӯғ меангоштанд ва онҳоро ба тамасхур мегирифтанд');
INSERT INTO "pioneers" VALUES(30,11,'30:11','Аллоҳ таоло офаринишро оғоз мекунад, сипас онро [пас аз маргаш] бозмегардонад, он гоҳ [дар қиёмат ҳамагӣ] ба сӯяш бозгардонда мешавед');
INSERT INTO "pioneers" VALUES(30,12,'30:12','Рӯзе, ки қиёмат барпо гардад, гунаҳгорон [аз раҳмати илоҳӣ] ноумед мешаванд');
INSERT INTO "pioneers" VALUES(30,13,'30:13','[Дар он рӯз] Мушрикон аз байни маъбудони худ [ки мепарастиданд] ҳеҷ шафоатгаре нахоҳанд дошт ва нисбат ба маъбудонашон [ки онҳоро ҳамтои Аллоҳ таоло қарор дода буданд] куфр меварзанд');
INSERT INTO "pioneers" VALUES(30,14,'30:14','Рӯзе, ки қиёмат барпо гардад, [мардум бар асоси корҳояшон] аз якдигар ҷудо мешаванд');
INSERT INTO "pioneers" VALUES(30,15,'30:15','Аммо касоне, ки имон овардаанд ва корҳои шоиста анҷом додаанд, дар бӯстоне [аз биҳишт] шодмон мегарданд');
INSERT INTO "pioneers" VALUES(30,16,'30:16','Ва аммо онон, ки куфр варзиданд ва оёти Мо ва дидорамон дар охиратро дурӯғ ангоштанд, инон эҳзоршудагон дар азобанд');
INSERT INTO "pioneers" VALUES(30,17,'30:17','Пас, Аллоҳро ба покӣ ситоиш кунед, он гоҳ ки ба шаб дармеоед ва он дам, ки ба субҳ мерасед');
INSERT INTO "pioneers" VALUES(30,18,'30:18','ва асргоҳон ва ҳангоме ки ба нимрӯз мерасед ва ҳамду ситоиш дар осмонҳо ва замин аз они Ӯст');
INSERT INTO "pioneers" VALUES(30,19,'30:19','Ӯ [мавҷуди] зиндаро аз [моддаи] беҷон [нутфа] падид меоварад ва [моддаи] беҷонро аз [мавҷуди] зинда хориҷ месозад ва заминро – пас аз хушкӣ ва хазонаш – зинда месозад ва [шумо низ рӯзи қиёмат] инчунин барангехта мешавед');
INSERT INTO "pioneers" VALUES(30,20,'30:20','Ва аз нишонаҳои Ӯ [ин] аст, ки шуморо аз хок офарид, сипас инсонҳое шудед [ки таваллуди мисл кардед ва дар замин] пароканда гаштед');
INSERT INTO "pioneers" VALUES(30,21,'30:21','Ва дар нишонаҳои Ӯ [ин] аст, ки барои шумо аз ҷинси худатон ҳамсароне офарид, то дар канорашон ором гиред ва миёнатон муҳаббату меҳрубонӣ падид овард. Бе гумон, дар ин [неъмат] барои хирадварзон нишонаҳое [аз қудрати Парвардигор] аст');
INSERT INTO "pioneers" VALUES(30,22,'30:22','Ва аз нишонаҳои Ӯ офариниши осмонҳо ва замин ва ихтилофи забонҳо ва рангҳои шумост. Бе тардид, дар ин [умур] барои донишварон нишонаҳое [аз қудрати Парвардигор] аст');
INSERT INTO "pioneers" VALUES(30,23,'30:23','Ва аз нишонаҳои Ӯ хоби шумо дар шаб ва рӯз ва талошатон барои баҳрагирӣ аз бахшиши Парвардигор [ва таъмини маош] аст. Бе гумон, дар ин умур барои онон, ки гӯши шунаво доранд, нишонаҳое [аз қудрати Парвардигор] аст');
INSERT INTO "pioneers" VALUES(30,24,'30:24','Ва аз нишонаҳои Ӯ [ин] аст, ки соиқаро барои биму умед нишонатон медиҳад ва аз осмон обе мефиристад ва заминро – пас аз хушкӣ ва хазонаш – бо он зинда месозад. Бе гумон, дар ин умур барои онон, ки меандешанд, нишонаҳое [аз қудрати Парвардигор] аст');
INSERT INTO "pioneers" VALUES(30,25,'30:25','Ва аз нишонаҳои Ӯ [ин] аст, ки осмонҳо ва замин ба фармонаш барпост ва [рӯзи қиёмат] ҳангоме ки бо нидое шуморо аз замин фаро мехонад, ногаҳон [ҳамагӣ аз гӯрҳо] берун меоед');
INSERT INTO "pioneers" VALUES(30,26,'30:26','Ҳар ки [ва ҳар чи] дар осмонҳо ва замин аст аз они Аллоҳ таоло аст ва ҳамагӣ [таслиму] фармонбари Ӯ ҳастанд');
INSERT INTO "pioneers" VALUES(30,27,'30:27','Ӯст, ки офаринишро оғоз мекунад, сипас онро [пас аз маргаш] бозмегардонад ва ин кор барояш осонтар [аз офариниши нахустин] аст ва бартарин васф дар осмонҳо ва замин барои Ӯст ва Ӯ шикастнопазири ҳаким аст');
INSERT INTO "pioneers" VALUES(30,28,'30:28','[Эй мушрикон] Аллоҳ таоло барои шумо аз худатон мисоле меоварад: «Оё аз бардагонатон касе ҳаст, ки дар амволе ба шумо додаем, шарикатон бошад, чунон ки ҳар ду [дар бархӯрдорӣ аз он амвол] яксон бошед ва ҳамон тавр ки [ба хотири тасарруфи мол] аз якдигар бим доред, аз онон низ бим дошта бошед? [Ҳол ки чунин нест, пас, чаро бандагон ва махлуқоти Аллоҳро бо Ӯ шарик медонед]? Мо оятамонро барои касоне, ки меандешанд, ин чунин ба равшани баён мекунем');
INSERT INTO "pioneers" VALUES(30,29,'30:29','[Далели ширк камбуди далоили тавҳидӣ нест] Балки ситамгорон бидуни илму огоҳӣ аз ҳавою ҳаваси худ пайравӣ мекунанд, пас онро, ки Аллоҳ таоло гумроҳ кардааст, чи касе ҳидоят мекунад? Ва онон [дар баробари азоби илоҳӣ] ҳеҷ ёваре нахоҳанд дошт');
INSERT INTO "pioneers" VALUES(30,30,'30:30','Пас, [эй паёмбар,] ҳақгароёна ва холисона ба ин дин рӯ овар. [Ин оини пок] Ислом аст, ки Аллоҳ таоло фитрати мардумро бар асоси он сиришта аст. Ҳеҷ тағйире дар офариниши Аллоҳ таоло нест. Ин аст [роҳи рост ва] дини устувор, вале бештари мардум [ҳақиқатро] намедонанд');
INSERT INTO "pioneers" VALUES(30,31,'30:31','[Эй муъминон, ба даргоҳи Парвардигор тавба кунед ва] Ба сӯи Ӯ бозгардед ва аз Ӯ парво кунед ва намоз барпо доред ва аз мушрикон набошед');
INSERT INTO "pioneers" VALUES(30,32,'30:32','Аз касоне [набошед], ки дар динашон тафриқа эҷод карданд ва даста-даста шуданд ва ҳар гурӯҳе ба он чи назди хеш доранд хушнуданд');
INSERT INTO "pioneers" VALUES(30,33,'30:33','Ҳангоме ки ба мардум [ранҷу] зиёне бирасад, зорикунон Парвардигорашонро мехонанд, сипас чун [Аллоҳ таоло] раҳмате аз ҷониби хеш ба онон бичашонад [ва гиреҳ аз корашон бикшояд], он гоҳ гурӯҳе аз онон ба Парвардигорашон ширк меоваранд');
INSERT INTO "pioneers" VALUES(30,34,'30:34','То ба он чи ки ба онон додаем, носипосӣ кунанд. Пас, [аз лаззатҳои дунявӣ] баҳра бигиред, ки ба зудӣ [натиҷаи кирдоратонро] хоҳед донист');
INSERT INTO "pioneers" VALUES(30,35,'30:35','Оё [дар китобҳои осмонӣ] далели муҳкаме бар онон нозил кардаем, ки аз [дурустии] он чи ба Ӯ [Аллоҳ таоло] ширк варзиданд, сухан мегӯяд [ва ин корро таъйид мекунад]?');
INSERT INTO "pioneers" VALUES(30,36,'30:36','Вақте раҳмате ба мардум бичашонем, аз он хушнуд мегарданд ва агар ба сазои корҳое, ки мекарданд, ранҷу мусибате ба эшон бирасад, [аз раҳмати илоҳӣ] ноумед мешаванд');
INSERT INTO "pioneers" VALUES(30,37,'30:37','Оё надиданд, ки Аллоҳ таоло [неъмату] рӯзиро бар ҳар ки бихоҳад густурда медорад ва [ё] танг мегирад? Бе гумон, дар ин [кушоишу сахтӣ] барои муъминон нишонаҳое [аз лутфу раҳмати Парвардигор] аст');
INSERT INTO "pioneers" VALUES(30,38,'30:38','Пас, ҳаққи [силаи раҳими] хешованд ва [садақа ва закоти] мустаманду дармондаро адо кун. Ин [кор] барои касоне, ки [хушнудии] Аллоҳро металабанд, беҳтар аст ва ононанд, ки растагоранд');
INSERT INTO "pioneers" VALUES(30,39,'30:39','Он чи аз амвол [-и худ, ки ба қасд]-и рибо ба мардум медиҳед, то [бо пас гирифтани он ба мизони бештаре бар амволатон] афзун гардад, назди Аллоҳ таоло афзоиш намеёбад, ва[-ле] он чи барои хушнудии Аллоҳ таоло [ба унвони] закот мепардозед, [баракату афзоиш меёбад] ва [касоне, ки чунин мекунанд] инонанд, ки подошашон [назди Аллоҳ таоло] чанд баробар мегардад');
INSERT INTO "pioneers" VALUES(30,40,'30:40','Аллоҳ таоло аст, ки шуморо офарид ва рӯзӣ дод, он гоҳ шуморо мемиронад ва боз [дар қиёмат] зинда мекунад. Оё дар миёни шариконатон касе ҳаст, ки чизе аз ин [корҳо]-ро анҷом диҳад? Муназзаҳ аст Ӯ ва аз он чи бо Вай шарик месозанд, бартар аст');
INSERT INTO "pioneers" VALUES(30,41,'30:41','Ба сабаби гуноҳоне, ки мардум муртакиб шудаанд, фасод [-у табоҳӣ] дар хушкӣ ва дарё ошкор гаштааст, то [Аллоҳ таоло] ҷазои бархе аз аъмолашонро ба эшон бичашонад, бошад, ки [ба сӯйи ҳақ] бозгарданд');
INSERT INTO "pioneers" VALUES(30,42,'30:42','[Эй паёмбар, ба мушрикон] Бигӯ: «Дар замин гардиш кунед, то бубинед саранҷоми касоне, ки пеш аз шумо буданд, чи гуна будааст. Бештари онон мушрик буданд [ва ба ҳамин сабаб ҳалок шуданд]»');
INSERT INTO "pioneers" VALUES(30,43,'30:43','Пас, ба сӯйи дини мустақим [ва пойдор] рӯй овар, пеш аз он ки рӯзе аз ҷониби Аллоҳ таоло фаро расад, ки бозгаште надорад. Дар он рӯз [мардум] пароканда мешаванд [гурӯҳе ба биҳишт мераванд ва гурӯҳе ба дузах]');
INSERT INTO "pioneers" VALUES(30,44,'30:44','Ҳар ки куфр биварзад, куфраш ба зиёни [худи] ӯст ва ҳар ки коре шоиста анҷом диҳад, [биҳиштро] барои худ омода месозад');
INSERT INTO "pioneers" VALUES(30,45,'30:45','То [Аллоҳ таоло] касонеро, ки имон овардаанд ва корҳои шоиста анҷом додаанд, аз фазли худ подош диҳад. Бе тардид, Ӯ кофиронро дӯст намедорад');
INSERT INTO "pioneers" VALUES(30,46,'30:46','Аз нишонаҳои [қудрати] Ӯ ин аст, ки бодҳои башоратбахшро [бо муждаи борон] мефиристад, то аз раҳмати хеш ба шумо бичашонад ва киштиҳо ба фармонаш равон шаванд, то аз бахшиши Ӯ [рӯзӣ] биҷӯед, бошад, ки сипос гузоред');
INSERT INTO "pioneers" VALUES(30,47,'30:47','Ва дар ҳақиқат, пеш аз ту [низ]паёмбаронеро ба сӯйи қавмашон фиристодем ва онон ба далоили равшан наздашон омаданд [вале онҳоро дурӯғ ангоштанд]. Сипас аз онон, ки муртакиби гуноҳ шуда буданд, интиқом гирифтем [ва муъминонро наҷот додем] ва ёрӣ додани [муъминон [ҳаққе] бар уҳдаи Мо буд');
INSERT INTO "pioneers" VALUES(30,48,'30:48','Аллоҳ таоло аст, ки бодҳоро равон мекунад ва [онҳо низ тудаи] абреро ба ҳаракат дармеоваранд ва [Аллоҳ таоло] онҳоро чунон ки мехоҳад дар [паҳнои] осмон мегустаронад ва ба сурати пораҳое дармеоварад ва боронро мебинӣ, ки аз миёни он [абрҳо] берун меояд ва чун [Парвардигори меҳрубон] ин [борони ҳаётбахш]-ро ба ҳар як аз бандагонаш, ки бихоҳад фурӯборад, он гоҳ аст, ки шодмон мешаванд');
INSERT INTO "pioneers" VALUES(30,49,'30:49','Ва ҳатман, пеш аз он ки бар эшон биборад. [Оре] Пеш аз он ноумед буданд');
INSERT INTO "pioneers" VALUES(30,50,'30:50','Пас, ба осори раҳмати илоҳӣ бингар, ки чи гуна заминро пас аз хушкӣ ва хазонаш зинда мекунад. Ба ростӣ, Ӯст, ки яқинан зиндагибахши мурдагон аст ва Ӯ бар ҳар чизе тавоност');
INSERT INTO "pioneers" VALUES(30,51,'30:51','Агар бод [-и сӯзон]-е бифиристем ва он [киштзорҳояшон]-ро зард [-у пажмурда] бубинанд, ҳатман, аз он пас носипосӣ мекунанд');
INSERT INTO "pioneers" VALUES(30,52,'30:52','Пас, [эй паёмбар] ту ҳаргиз наметавонӣ мурдагонро шунаво созӣ ва овои даъват [-и ҳақ]-ро ба гӯши карон бирасонӣ, он гоҳ ки онон пушт мекунанд ва рӯй мегардонанд');
INSERT INTO "pioneers" VALUES(30,53,'30:53','Ва ту роҳнамои кӯрдилон аз гумроҳияшон нестӣ. Ту танҳо [сухани ҳақро] ба гӯши касоне мерасонӣ, ки ба оёти Мо имон доранд ва [дар баробари фармони Аллоҳ таоло] таслиманд');
INSERT INTO "pioneers" VALUES(30,54,'30:54','Аллоҳ таоло аст, ки шуморо аз [оби] беарзише офарид, сипас баъд аз нотавонӣ [даврони хурдсолӣ] нерумандӣ бахшид, он гоҳ пас аз нерумандӣ заъфу куҳансолӣ падид овард. Ӯ ҳар чи бихоҳад, меофаринад ва Ӯ донову тавоност');
INSERT INTO "pioneers" VALUES(30,55,'30:55','Ва рӯзе, ки қиёмат барпо гардад, гунаҳгорон савганд ёд мекунанд, ки ҷуз соате [дар гӯр] диранг накардаанд. Онон [дар дунё низ] ин гуна ба инҳироф кашида мешуданд');
INSERT INTO "pioneers" VALUES(30,56,'30:56','Ва касоне, ки ба онон дониш ва имон дода шудааст, [ба гунаҳгорон] мегӯянд: «Бе тардид, шумо [бар асоси он чи] дар китоби Аллоҳ таоло [муқаддар будааст], то рӯзи растохез диранг кардаед. Пас, ин рӯзи растохез аст, аммо шумо намедонистед [ва инкораш мекардед]»');
INSERT INTO "pioneers" VALUES(30,57,'30:57','Дар он рӯз узрхоҳии касоне, ки ситам кардаанд, суде надорад ва аз онон дархости пузиш намешавад');
INSERT INTO "pioneers" VALUES(30,58,'30:58','Ба ростӣ, ки дар ин Қуръон барои мардум аз ҳар гуна масале овардем ва агар нишонае [дар мавриди ростгӯият] барояшон биёварӣ, касоне, ки куфр варзиданд, мегӯянд: «Шумо ботиландешоне беш нестед»');
INSERT INTO "pioneers" VALUES(30,59,'30:59','Аллоҳ таоло бар дилҳои касоне, ки [сухани ҳақро] намефаҳманд, ин гуна муҳр [-и ғафлат] мениҳад');
INSERT INTO "pioneers" VALUES(30,60,'30:60','[Эй паёмбар, бар озору такзиби мушрикон] Шикебо бош, [ки] ҳатман, ваъда [-и ёрӣ]-и Аллоҳ таоло ҳақ аст ва мабодо касоне, ки [ба растохез] бовар надоранд, туро аз шикебоӣ боздоранд');
INSERT INTO "pioneers" VALUES(31,1,'31:1','Алиф, лом, мим');
INSERT INTO "pioneers" VALUES(31,2,'31:2','Ин оёти китоби ҳаким аст');
INSERT INTO "pioneers" VALUES(31,3,'31:3','[Ки] Ҳидоят ва раҳмате барои накукорон аст');
INSERT INTO "pioneers" VALUES(31,4,'31:4','Касоне, ки намоз барпо медоранд ва закот мепардозанд ва ба охират яқин доранд');
INSERT INTO "pioneers" VALUES(31,5,'31:5','Онон аз ҷониби Парвардигорашон аз ҳидояте бархӯрдоранд ва ононанд, ки растагоранд');
INSERT INTO "pioneers" VALUES(31,6,'31:6','Ва аз мардум касе ҳаст, ки дар пайи сухани ёва аст, то [дигаронро] аз роҳи Аллоҳ таоло гумроҳ созад ва он [оёти равшангар]-ро ба тамасхур мегирад. Онон [ки рафторашон чунин аст], азоби хоркунанда [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(31,7,'31:7','Ва ҳангоме ки оёти Мо бар ӯ хонда мешавад, мутакаббирона рӯй мегардонад, гӯё онро нашнида [ва] гӯё дар гӯшҳояш сангинӣ аст. Пас, ӯро ба азобе дарднок башорат деҳ');
INSERT INTO "pioneers" VALUES(31,8,'31:8','Бе тардид, касоне, ки имон овардаанд ва корҳои шоиста анҷом додаанд, боғҳои пурнеъмати [биҳишт] барои эшон аст');
INSERT INTO "pioneers" VALUES(31,9,'31:9','Дар он ҷо ҷовидонанд. Ваъдаи Аллоҳ таоло рост аст ва Ӯ шикастнопазири ҳаким аст');
INSERT INTO "pioneers" VALUES(31,10,'31:10','Чунон ки мебинед, [Аллоҳ таоло] осмонҳоро бидуни сутун офарид ва дар замин кӯҳҳои устувор [-у муҳкаме] пайафканад, то [замини] шуморо наларзонад ва аз ҳар гуна ҷунбандае дар он пароканда сохт ва аз осмон обе фиристодем ва аз ҳар [гиёҳ]-и судманде дар он рӯёндем');
INSERT INTO "pioneers" VALUES(31,11,'31:11','Ин офариниши Аллоҳ таоло аст. Инак [эй мушрикон] ба ман нишон диҳед, ки касоне, ки ғайр аз Ӯ ҳастанд [ва шумо мепарастедашон] чи офаридаанд? Балки ситамгорон [мушрикон] дар гумроҳии ошкоранд');
INSERT INTO "pioneers" VALUES(31,12,'31:12','Ба ростӣ, Мо ба Луқмон дониш [-и фаҳми дин ва тадбири зиндагӣ] бахшидем [ва ба ӯ гуфтем], ки: Шукри Аллоҳро ба ҷой овар, пас, ҳар ки шукр кунад, танҳо ба суди хеш шукргузоранда аст ва ҳар ки носипосӣ кунад, пас, [бидонад, ки] бе тардид, Аллоҳ таоло бениёзи сутуда аст');
INSERT INTO "pioneers" VALUES(31,13,'31:13','Ва [ёд кун аз] он гоҳ ки Луқмон ба писараш [чунин] панд медод: «Писарам, ба Аллоҳ таоло ширк наовар [ва чизеро ҳамтояш қарор мадеҳ, чаро ки] яқинан, ширк ситами бузургест»');
INSERT INTO "pioneers" VALUES(31,14,'31:14','Мо инсонро [дар бораи накуӣ] ба падару модараш супориш кардем, [аз он рӯ, ки] модараш ӯро бо нотавонии рӯзафзун бордор шуд ва [даврони ширхорагӣ ва] бозгирифтанаш аз шир ду сол тӯл мекашад. [Ба ӯ гуфтем] Ки: «шукри Ман ва падару модаратро ба ҷо овар, [ки] бозгашт [ҳама] ба сӯйи Ман аст');
INSERT INTO "pioneers" VALUES(31,15,'31:15','Ва агар он ду [мушрик бошанд ва] талош кунанд, ки ту чизеро шарики Ман қарор диҳӣ, ки нисбат ба он донише надорӣ, пас, аз он ду итоат накун ва дар дунё бо эшон ба шоистагӣ [ва накуӣ] рафтор кун ва аз роҳи касе пайравӣ кун, ки [муваҳҳидона] ба сӯйи Ман бозомадааст. Бозгашти ҳама ба сӯйи Ман аст ва аз он чи мекардед, огоҳатон мекунам');
INSERT INTO "pioneers" VALUES(31,16,'31:16','[Луқмон гуфт] «Писарам, агар [амали нек ё бад] ҳамсанги донаи хардал бошад ва даруни тахтасанге ё дар [гӯшае аз] осмонҳо ё дар замин [нуҳуфта] бошад, Аллоҳ таоло онро [рӯзи қиёмат ба ҳисоб] меоварад. Бе тардид, Аллоҳ таоло борикбини огоҳ аст');
INSERT INTO "pioneers" VALUES(31,17,'31:17','Писарам, намоз барпо дор ва [мардумро] ба некӣ фармон бидеҳ ва аз нописанд боздор ва бар он чи [ки аз сахтиҳои ин кор] ба ту мерасад, шикебо бош. Бе тардид, ин аз [ҷумлаи корҳоест, ки ниёзманди иродаи қавӣ ва] азми устувор аст');
INSERT INTO "pioneers" VALUES(31,18,'31:18','Ва [бо беэътиноӣ] аз мардум рӯй нагардон ва мағрурона дар замин роҳ нарав. Ба ростӣ, ки Аллоҳ таоло ҳеҷ мутакаббиру фахрфурӯшеро дӯст намедорад');
INSERT INTO "pioneers" VALUES(31,19,'31:19','Ва [дар манишу рафторат] миёнарав бош ва ба оромӣ сухан бигӯ [на бо садои баланд, чаро ки] ҳатман, зишттарин садо садои харон аст');
INSERT INTO "pioneers" VALUES(31,20,'31:20','[Эй мардум] Оё надидаед, ки Аллоҳ таоло он чиро дар осмонҳо ва замин аст, ба хидмататон гумошта ва [бахшиши] неъматҳои ошкор ва пинҳони хешро бар шумо тамом кард? [Бо ин ҳол] Аз мардум касе ҳаст, ки бидуни ҳеҷ дониш ва ҳидоят ва китоби равшангаре дар [мавриди] Аллоҳ таоло муҷодала мекунад');
INSERT INTO "pioneers" VALUES(31,21,'31:21','Ва ҳангоме ки ба онон гуфта мешавад: «Аз он чи Аллоҳ таоло нозил кардааст, пайравӣ кунед». Мегӯянд: «[На] Балки аз чизе пайравӣ мекунем, ки падаронамонро бар он ёфтем». Оё агар шайтон ононро ба сӯйи азоби [оташи] сӯзон даъват кунад [боз ҳам аз падаронашон пайравӣ мекунанд]?');
INSERT INTO "pioneers" VALUES(31,22,'31:22','Ҳар ки [холисона] ба Аллоҳ таоло рӯ оварад ва накукор бошад, ба ростӣ, ки ба дастовези муҳкаме чанг задааст ва саранҷоми корҳо ба сӯйи Аллоҳ таоло аст');
INSERT INTO "pioneers" VALUES(31,23,'31:23','Ҳар ки куфр варзад, куфраш туро ғамгин насозад. Бозгашти онон ба сӯи Мост, он гоҳ аз он чи кардаанд, огоҳашон месозем. Бе тардид, Аллоҳ таоло аз [рози] дилҳо огоҳ аст');
INSERT INTO "pioneers" VALUES(31,24,'31:24','Онон [кофирон]-ро андаке [аз неъматҳои дунё] баҳраманд месозем, он гоҳ ба таҳаммули азобе сахт дучорашон мекунем');
INSERT INTO "pioneers" VALUES(31,25,'31:25','Ва агар аз мушрикон бипурсӣ: Чи касе осмонҳо ва заминро офаридааст?», қатъан, мегӯянд: «Аллоҳ таоло». Бигӯ: Сипос [-у ситоиш] махсуси Аллоҳ таоло аст [ки шуморо аз ин ҳақиқат огоҳ намуд]», балки бештарашон намедонанд');
INSERT INTO "pioneers" VALUES(31,26,'31:26','Он чи дар осмонҳо ва замин аст, аз они Аллоҳ таоло аст. Бе тардид, Аллоҳ таоло бениёзу сутуда аст');
INSERT INTO "pioneers" VALUES(31,27,'31:27','Агар [ҳамаи] дарахтони рӯи замин қалам шавад ва дарё [ранг гардад] ва ҳафт дарёи дигар ба ёриашон биёяд [то дониши илоҳиро бинависанд], суханони Аллоҳ таоло поён намеёбад. Бе тардид, Аллоҳ таоло шикастнопазиру ҳаким аст');
INSERT INTO "pioneers" VALUES(31,28,'31:28','[Эй мардум] Офариниш ва барангехтани шумо [дар қиёмат] ҳамчун як нафар беш нест. Бе тардид, Аллоҳ таоло шунавову биност');
INSERT INTO "pioneers" VALUES(31,29,'31:29','Оё надидӣ, ки Аллоҳ таоло аз шаб мекоҳад ва бар рӯз меафзояд ва аз рӯз мекоҳад ва бар шаб меафзояд ва хуршеду моҳро ба хидмат [-и инсон] гумоштааст, ки ҳар як то замоне муайян дар [мадори худ] равон бошад? Бе тардид, Аллоҳ таоло аз он чи мекунед, огоҳ аст');
INSERT INTO "pioneers" VALUES(31,30,'31:30','Ин [тақдиру тадбир] аз он рӯст, ки Аллоҳ таоло ҳақ аст ва он чи ҷуз Ӯ мехонанд, [ҳама] ботил аст ва Аллоҳ таоло аст, ки баландмартаба [ва] бузург аст');
INSERT INTO "pioneers" VALUES(31,31,'31:31','Оё надидӣ, ки киштиҳо ба [фармону] неъмати Аллоҳ таоло дар дарё равонанд, то бархе аз нишонаҳои хешро ба шумо бинамояд? Бе тардид, дар ин амр барои ҳар шикебои сипосгузор нишонаҳое [аз қудрату лутфи Парвардигор] аст');
INSERT INTO "pioneers" VALUES(31,32,'31:32','Ва он гоҳ ки мавҷи кӯҳпайкаре ононро иҳота мекунад, Аллоҳро дар ҳоли холисона мехонанд, ки дин [-у парастиш]-ро танҳо вижаи Ӯ медонанд. Он гоҳ чун ононро [аз марг] раҳонад ва ба хушкӣ расонад, бархе аз эшон миёнарав [мешаванд, вале чунон ки бояд, сипос намегузоранд, дар ҳоле ки бархе дигар носипосанд] ва оёти Моро касе ҷуз паймоншиканони носипос инкор намекунанд');
INSERT INTO "pioneers" VALUES(31,33,'31:33','Эй мардум, аз Парвардигоратон парво кунед ва аз рӯзе битарсед, ки на ҳеҷ падаре барои фарзандаш суде дорад ва на ҳеҷ фарзанде судрасони падар аст. Яқинан, ваъдаи Аллоҳ таоло [дар мавриди қиёмат] ҳақиқат дорад. Пас, [ҳушёр бошед, ки лаззатҳои] зиндагии дунёӣ шуморо нафиребад ва шайтон нисбат ба [таъхири азоб ва шикебоии] Аллоҳ таоло фиребатон надиҳад');
INSERT INTO "pioneers" VALUES(31,34,'31:34','Бе тардид, огоҳӣ [аз замони вуқуи] қиёмат [танҳо] назди Аллоҳ таоло аст ва [Ӯст, ки] борон нозил мекунад ва он чиро, ки дар раҳимҳост, медонад ва касе намедонад, фардо чи ба даст меоварад ва ҳеҷ кас огоҳ нест, ки дар кадом сарзамин мемирад. Ба ростӣ, ки Аллоҳ таоло донову огоҳ аст');
INSERT INTO "pioneers" VALUES(32,1,'32:1','Алиф, лом, мим');
INSERT INTO "pioneers" VALUES(32,2,'32:2','Нузули ин китоб, ки дар [ҳаққонияти] он ҳеҷ шакке нест, аз ҷониби Парвардигори ҷаҳониён аст');
INSERT INTO "pioneers" VALUES(32,3,'32:3','[Кофирон] Мегӯянд: «[Муҳаммад] Онро ба дурӯғ [ба Парвардигораш] нисбат медиҳад». [Ҳаргиз чунин нест] Балки он [оёт сухани] ҳаққе аз ҷониби Парвардигори туст, то ба қавме, ки пеш аз ту барояшон [ҳеҷ] бимдиҳандае наомадааст, ҳушдор диҳӣ, бошад, ки ҳидоят шаванд');
INSERT INTO "pioneers" VALUES(32,4,'32:4','Аллоҳ таоло аст, ки осмонҳо ва замин ва он чиро, ки миёни онҳост, дар шаш рӯз офарид, он гоҳ, [ки шоистаи шукуҳи Ӯст] бар Арш қарор гирифт. Шумо ба ҷуз Ӯ ҳеҷ корсозу шафоатгаре надоред. Оё панд намегиред?');
INSERT INTO "pioneers" VALUES(32,5,'32:5','Кор [-и ҷаҳони ҳастӣ]-ро аз осмон то замин тадбир мекунад. Он гоҳ дар рӯзе, ки миқдори он барои шумо [инсонҳо] ҳазор сол аст, [ин корҳо] ба сӯи Ӯ боло меравад');
INSERT INTO "pioneers" VALUES(32,6,'32:6','Ин [Парвардигори шумост] донои пинҳону ошкор, шикастнопазири меҳрубон');
INSERT INTO "pioneers" VALUES(32,7,'32:7','[Ҳамон] Касе, ки ҳар чи офарид, ба накутарин [шакл] офарид ва офариниши [нахустини] инсонро аз гил оғоз кард');
INSERT INTO "pioneers" VALUES(32,8,'32:8','Сипас насли ӯро аз чакидаи оби беарзиш падид овард');
INSERT INTO "pioneers" VALUES(32,9,'32:9','Он гоҳ [андоми] ӯро сомон дод ва аз рӯҳи хеш [рӯҳе, ки худ офарид] дар вай дамид ва барои шумо [неъмати] гӯшу чашм ва дил қарор дод. Чи андак сипос мегузоред!');
INSERT INTO "pioneers" VALUES(32,10,'32:10','[Мушрикон] Мегӯянд: «Оё вақте [мурдем ва] дар замин нопадид шудем, офариниши ҷадиде хоҳем ёфт»? Балки онон дидори Парвардигорашонро мункиранд');
INSERT INTO "pioneers" VALUES(32,11,'32:11','[Эй паёмбар] бигӯ: «Фариштаи марг, ки бар шумо гумошта шудааст, ҷонатонро мегирад, он гоҳ ба сӯйи Парвардигоратон бозгардонда мешавед»');
INSERT INTO "pioneers" VALUES(32,12,'32:12','Ва чун [дар қиёмат] гунаҳгоронро дар пешгоҳи Парвардигорашон бубинӣ, сар ба зер афкандаанд [ва мегӯянд] «Парвардигоро, [акнун ҳақиқатро] дидем ва шунидем, пас, моро бозгардон, то кори шоистае анҷом диҳем. Бе тардид, мо [ба қиёмат] яқин дорем»');
INSERT INTO "pioneers" VALUES(32,13,'32:13','Агар мехостем, ба ҳар касе [тавфиқ ва] ҳидояташро арзонӣ медоштем, вале ин сухан [-у ваъдаи] ман ҳақ аст, ки: «Дузахро аз ҳамаи ҷинниён ва одамиён [-и кофир] анбошта хоҳам кард»');
INSERT INTO "pioneers" VALUES(32,14,'32:14','[Ба онон гуфта мешавад] «Ба сазои он ки дидори имрӯзатонро фаромӯш кардед, [азоби сӯзонро] бичашед. Яқинан, мо [низ] шуморо [дар оташ] раҳо кардаем ва [акнун] ба сазои он чи мекардед, азоби ҷовидонро бичашед');
INSERT INTO "pioneers" VALUES(32,15,'32:15','Фақат касоне ба оёти Мо имон меоваранд, ки чун ба онҳо панд дода шавад, ба саҷда меафтанд ва Парвардигорашонро ба покӣ меситоянд ва такаббур намекунанд');
INSERT INTO "pioneers" VALUES(32,16,'32:16','[Дар дили шаб] Аз бистарҳо бармехезанд ва Парвардигорашонро бо биму умед [ба ниёиш] мехонанд ва аз он чи ба онон рӯзӣ додаем, инфоқ мекунанд');
INSERT INTO "pioneers" VALUES(32,17,'32:17','Пас, ҳеҷ кас намедонад, ба [поси] он чи [ин муъминон дар дунё] анҷом медиҳанд, чи [бисёр мояи] равшании чашмҳо барояшон нуҳуфта аст');
INSERT INTO "pioneers" VALUES(32,18,'32:18','Оё [подоши] касе, ки муъмин аст, ҳамчун касест, ки нофармон аст? Ҳаргиз баробар нестанд');
INSERT INTO "pioneers" VALUES(32,19,'32:19','Аммо касоне, ки имон овардаанд ва корҳои шоиста анҷом додаанд, ҷойгоҳашон боғҳо [-и биҳиштӣ] аст. Подошест ба [поси] он чи мекарданд');
INSERT INTO "pioneers" VALUES(32,20,'32:20','Ва аммо касоне, ки нофармонӣ карданд, ҷойгоҳашон оташ [-и дузах] аст. Ҳар гоҳ бихоҳанд аз он хориҷ шаванд, ба дарунаш бозгардонда мешаванд ва ба онон гуфта мешавад: «Бичашед азоби оташеро, ки ҳамвора дурӯғаш меангоштед»');
INSERT INTO "pioneers" VALUES(32,21,'32:21','Яқинан пеш аз азоби бузурги [охират] аз азоби наздик [ва сахтиҳои ин дунё] ба онон мечашонем, бошад, ки [ба даргоҳи илоҳӣ] бозгарданд');
INSERT INTO "pioneers" VALUES(32,22,'32:22','Кист ситамгортар аз он ки ӯро ба оёти Парвардигораш панд дода мешавад, сипас аз он рӯй мегардонад? Бе тардид, Мо аз гунаҳгорон интиқом мегирем');
INSERT INTO "pioneers" VALUES(32,23,'32:23','Мо ба Мӯсо китоб [Таврот] додем – пас, [эй паёмбар] аз дидори Ӯ дар тардид набош – ва онро раҳнамуди Бани Исроил гардонидем');
INSERT INTO "pioneers" VALUES(32,24,'32:24','ва чун онон [бар ибодат ва дурӣ аз гуноҳ] шикебоӣ мекарданд ва ба оёти Мо яқин доштанд, аз миёнашон пешвоёне баргумоштем, ки ба фармони Мо [мардумро] ҳидоят мекарданд');
INSERT INTO "pioneers" VALUES(32,25,'32:25','Мусалламан, Парвардигори туст, ки рӯзи қиёмат дар мавриди он чи ихтилоф доштанд, миёнашон доварӣ мекунад');
INSERT INTO "pioneers" VALUES(32,26,'32:26','Оё барояшон равшан нашудааст, ки чи бисёр наслҳоеро пеш аз онон нобуд кардем, ки [акнун ин кофирон] дар хонаҳои онҳо роҳ мераванд? Ба ростӣ, ки дар ин [азобу нобудӣ] ибратҳост. Оё [боз ҳам сухани ҳақро] намешунаванд?');
INSERT INTO "pioneers" VALUES(32,27,'32:27','Оё надиданд, ки Мо боронро ба сӯйи замини хушк [ва бегиёҳ] мефиристем ва ба василаи он киштзоре падид меоварем, ки худ ва чаҳорпоёнашон аз он мехӯранд? Оё [нишонаҳои қудрати Аллоҳ таоло бар растохезро] намебинанд?');
INSERT INTO "pioneers" VALUES(32,28,'32:28','Ва [кофирон] мегӯянд: «Агар ростгӯед, ин доварӣ [миёни мо ва шумо] кай фаро мерасад»?');
INSERT INTO "pioneers" VALUES(32,29,'32:29','Бигӯ: «Дар рӯзи доварӣ [ки ҷудоии ҳақ ва ботил аст] имон овардани касоне, ки куфр варзиданд, суде надорад ва ба онон муҳлат [-и тавба] дода намешавад»');
INSERT INTO "pioneers" VALUES(32,30,'32:30','Пас, [эй паёмбар, акнун ки бар ширк пофишорӣ мекунанд] аз эшон рӯй бигардон ва мунтазири [азобашон] бош [ки] бе тардид, онон [низ] мунтазиранд');
INSERT INTO "pioneers" VALUES(33,1,'33:1','Эй паёмбар, аз Аллоҳ таоло парво кун ва аз кофирон ва мунофиқон итоат накун. Бе тардид, Аллоҳ таоло ҳамвора донои ҳаким аст');
INSERT INTO "pioneers" VALUES(33,2,'33:2','Ва аз он чи аз ҷониби Парвардигорат ба ту ваҳй мешавад пайравӣ кун. Бе тардид, Аллоҳ таоло ҳамвора ба он чи мекунед, огоҳ аст');
INSERT INTO "pioneers" VALUES(33,3,'33:3','Ва бар Аллоҳ таоло таваккал кун ва ҳамин басанда аст, ки Аллоҳ таоло корсози ту бошад');
INSERT INTO "pioneers" VALUES(33,4,'33:4','Аллоҳ таоло даруни ҳеҷ марде ду қалб барояш наниҳодааст ва [ба ҳамин сурат] ҳаргиз ҳамсаронатонро ки мавриди зиҳор [ва таҳрими ҷинсӣ] қарор медиҳед, [ҳамчун] модаронатон нагардондааст; ва [ҳамчунин] писархондаҳоятонро писаронатон насохтааст. Ин сухани [нодуруст] аст, ки бар забон меоваред ва Аллоҳ таоло ҳақро мегӯяд ва Ӯст, ки ба роҳи рост ҳидоят мекунад');
INSERT INTO "pioneers" VALUES(33,5,'33:5','Онон [писархондаҳо]-ро ба падаронашон нисбат диҳед, [ки] назди Аллоҳ таоло одилонатар аст. Агар падаронашонро намешиносед, пас, онҳо бародарони динӣ ва бардагони озодшуда [ва дар ҳукми дӯстони] шумо ҳастанд. Дар мавриди он чи [қаблан] иштибоҳ кардаед, гуноҳе бар шумо нест; вале он чи дилҳоятон қасди онро дорад [ва амдан бар забон меоваред, гуноҳ аст; пас, агар тавба кунед] ҳамвора Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(33,6,'33:6','Паёмбар [дар тамоми умур] нисбат ба муъминон, аз худашон сазовортар аст [ва назар ва хосташ авлавият дорад] ва ҳамсарони ӯ [аз лиҳози ҳурмати издивоҷ дар ҳукм] модаронашон ҳастанд; ва дар китоби Аллоҳ таоло, хешовандон [дар ирс бурдан аз якдигар] нисбат ба муъмином ва муҳоҷирон авлавият доранд [ва милоки ирс, хешовандӣ аст на имон]; магар ин ки [бихоҳед] ба дӯстонатон некӣ кунед [ва бахше аз амволатонро ба эшон бидиҳед]. Ин [ҳукм] дар он китоб [Лавҳи маҳфуз] навишта шудааст');
INSERT INTO "pioneers" VALUES(33,7,'33:7','Ва [эй Паёмбар, ёд кун аз] ҳангоме ки аз паёмбарон паймон [яктопарастӣ ва нубувват] гирифтем ва [ба вижа] аз ту ва Нуҳ ва Иброҳим ва Мӯсо ва Исо- писари Марям; ва аз ҳамаи онон паймони маҳкаме [дар бораи таблиғи дин] гирифтем');
INSERT INTO "pioneers" VALUES(33,8,'33:8','[Аллоҳ таоло чунин кард] то [бо кофирон итмоми ҳуҷҷат карда бошад ва] аз ростгӯён [паёмбарон] дар бораи ростиашон бипурсад ва барои кофирон азобе дарднок муҳайё кардааст');
INSERT INTO "pioneers" VALUES(33,9,'33:9','Эй касоне, ки имон овардаед, неъмати Аллоҳро бар худ ба ёд оваред; он гоҳ ки сипоҳиёне [аз қабоили араб] ба сӯятон омаданд. Пас, Мо тундбоде [сахт] ва лашкаре [аз фариштагон] ки онҳоро намедидед, бар онон фиристодем [ва ба сахти шикасташон додем]; ва ҳамвора Аллоҳ таоло ба он чи мекунед биност');
INSERT INTO "pioneers" VALUES(33,10,'33:10','Вақте [дар ғазваи Хандақ] душманон аз боло ва поён [ва аз шарқ ва ғарби шаҳр] ба сӯятон омаданд [ва Мадинаро муҳосира карданд]; он гоҳ ки чашмҳо [ба фаровонии душман] хира гашта ва ҷонҳо [аз шиддат ва ваҳшат] ба гулугоҳ расида буд ва шумо ба Аллоҳ таоло гумонҳо [-и гуногун] мебурдед');
INSERT INTO "pioneers" VALUES(33,11,'33:11','Онҷо буд, ки муъминон озмоиш шуданд ва шадидан ба ваҳшат ва изтироб афтоданд');
INSERT INTO "pioneers" VALUES(33,12,'33:12','Ва [низ ба ёд оваред] ҳангоме ки мунофиқон ва касоне, ки дар дилҳояшон беморӣ [шак ва тардид] буд, мегуфтанд: «Он чи Аллоҳ таоло ва Паёмбараш [дар бораи пирӯзӣ бар кофирон] ваъда додаанд, ҷуз фиребе беш нест»');
INSERT INTO "pioneers" VALUES(33,13,'33:13','Ва [низ] ҳангоме ки гурӯҳе аз онон гуфтанд: «Эй аҳли Ясриб, инҷо ҷойи мондани шумо нест; пас, [ба хонаҳоятон] бозгардед»; ва бархе аз онон аз Паёмбар иҷозати [бозгашт] мехостанд ва мегуфтанд: «Хонаҳои мо бе ҳифзкунанда аст»; дар ҳоле ки бе ҳифзкунанда набуд. Онон чизе ҷуз фирор [аз ҷиҳод] намехостанд');
INSERT INTO "pioneers" VALUES(33,14,'33:14','Агар [лашкари душманон] аз атрофи он [шаҳр] бар онон ворид мешуданд [ва хонаҳояшонро муҳосира мекарданд] ва аз онон хоста мешуд, ба куфр ва ширк бозгарданд, ҳатман чунин [тааҳҳуде ба душман] доданд ва ҷуз теъдоде андак [дар баробари ин дархост] муқовимат намекарданд');
INSERT INTO "pioneers" VALUES(33,15,'33:15','Ва пеш аз ин [вақте ҳамин мунофиқон дар ҷанги Уҳуд аз муқобили кофирон гурехтанд] бо Аллоҳ таоло паймон бастанд, ки [ҳаргиз ба душман] пушт накунанд [ва аз майдон нагурезанд]; ва ҳамвора паймони илоҳӣ бозхост дорад');
INSERT INTO "pioneers" VALUES(33,16,'33:16','[Эй Паёмбар, ба мунофиқон] бигӯ: « Агар аз марг ё кушта шудан фирор кунед, ҳаргиз суде ба ҳолатон надорад; ва дар он сурат низ [аз зиндагии дунё] ҷуз андаке баҳраманд намешавед»');
INSERT INTO "pioneers" VALUES(33,17,'33:17','Бигӯ: «Агар Аллоҳ таоло бароятон мусибат ё раҳмате хоста бошад, кист, ки шуморо дар баробари [иҷрои фармони] Ӯ ҳифз кунад?» ва онон барои хеш ба ҷуз Аллоҳ таоло ҳеҷ корсоз ва ёригаре намеёбанд');
INSERT INTO "pioneers" VALUES(33,18,'33:18','Аллоҳ таоло касонеро аз шумо, ки боздоранда [-и мардум аз ҷиҳод] ҳастанд ва ба бародаронашон мегӯянд: «Ба мо бипайвандед» ба хубӣ мешиносад. [Онон сустбоваранд] ва ҷуз андаке ба майдони ҷанг намеоянд');
INSERT INTO "pioneers" VALUES(33,19,'33:19','[Мунофиқон дар ҳама чиз] бар шумо бухл меварзанд; ва ҳангоме ки бим [ҷанг пеш] меояд, дар ҳоле ки чашмҳояшон [аз ваҳшат дар косахона] мечархад, ҳамчун касе, ки аз [сахтии] марг беҳуш шуда бошад, туро нигоҳ мекунанд; ва чун [буҳрон ва] тарс бартараф шуд, ба хотири ҳирс ва тамаъе, ки ба ғаноим доранд, бо забонҳои тунд ва нешдор шуморо меранҷонанд. Онон [ҳаргиз] имон наёвардаанд; пас, Аллоҳ таоло аъмолашонро табоҳ мегардонад ва ин [кор] ҳамвора бар Аллоҳ таоло осон аст');
INSERT INTO "pioneers" VALUES(33,20,'33:20','[Мунофиқон] мепиндоранд [ки лашкариён] аҳзоб [ҳануз аз атрофи Мадина] нарафтаанд; ва агар аҳзоб бозгарданд, [ин сустбоварон] орзу мекунанд, ки миёни бодиянишинон [пинҳон] бошанд [ва аз ҳамон ҷо] ахбори [даргирии] шуморо ҷӯё шаванд; ва [ҳатто] агар байни [сипоҳи] шумо буданд, ҷуз андаке намеҷангиданд');
INSERT INTO "pioneers" VALUES(33,21,'33:21','Бе тардид, дар [ рафтор ва гуфтори] Расулуллоҳ, барои шумо сармашқи некуе аст; барои ҳар ки ба Аллоҳ таоло ва рӯзи охират умед дорад ва Аллоҳро бисёр ёд мекунад');
INSERT INTO "pioneers" VALUES(33,22,'33:22','Ва чун муъминон [лашкари] аҳзобро диданд, гуфтанд: «Ин [имтиҳони илоҳӣ ва пирӯзии ниҳоияш] ҳамон чизе аст, ки Аллоҳ таоло ва Паёмбараш ба мо ваъда додаанд ва [бар ҳақ ] Аллоҳ таоло ва Паёмбараш рост гуфтанд» ва [мушоҳидаи анбӯҳи душман] ҷуз бар имон ва таслимашон [дар муқобили Парвардигор] наяфзуд');
INSERT INTO "pioneers" VALUES(33,23,'33:23','Аз миёни муъминон мардоне ҳастанд ба аҳд ва паймоне, ки бо Аллоҳ таоло баста буданд, [содиқона] вафо карданд; бархе аз онон то охир бар аҳд ва паймони худ истоданд [ ва дар ин роҳ даргузаштанд ё шаҳид шуданд] ва бархе дигар ҳануз чашминтизор [шаҳодат] ҳастанд ва ҳаргиз [паймони худро] дигаргун насохтаанд');
INSERT INTO "pioneers" VALUES(33,24,'33:24','[Ин рӯйдодҳо пеш меояд] то Аллоҳ таоло ростгӯёнро ба [поси поймардӣ ва] ростгӯияшон подош диҳад ва мунофиқонро – агар бихоҳад – азоб кунад ё тавбаашонро бипазирад. Бе тардид, Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(33,25,'33:25','Ва Аллоҳ таоло [сипоҳи ] касонеро, ки куфр варзида буданд бо [тамоми нокомӣ ва] хашмашон [аз ин ки] ба ҳеҷ ғанимате даст наёфта буданд, [аз атрофи Мадина] бозгардонд ва [пуштибонии] Аллоҳ таоло дар ҷанг, муъминонро кифоят кард; ва Аллоҳ таоло ҳамвора тавонову шикастнопазир аст');
INSERT INTO "pioneers" VALUES(33,26,'33:26','[Аллоҳ таоло] Гурӯҳе аз аҳли китоб [яҳудиёни Бани Қурайза]- ро, ки аз онон [мушрикон] пуштибонӣ карда буданд, аз қалъаҳояшон ба зер кашид ва дар дилҳояшон ваҳшат андохт; [чунон ки шумо] гурӯҳе [аз онон] –ро мекуштед ва гурӯҳеро ба асорат мегирифтед');
INSERT INTO "pioneers" VALUES(33,27,'33:27','Ва заминҳои кишоварзӣ ва хонаҳо ва амволашон ва [ҳамчунин] сарзаминеро, ки ҳаргиз по ба он ҷо нагузошта будед [Хайбар], дар ихтиёратон ниҳод; ва Аллоҳ таоло бар ҳар чизе тавоност');
INSERT INTO "pioneers" VALUES(33,28,'33:28','Эй Паёмбар, ба ҳамсаронат [ки нафақаи бештаре мехоҳанд] бигӯ: « Агар зиндагии дунё ва зинатҳояшро мехоҳед, биёед, то шуморо [бо ҳадяи муносиб] баҳраманд созам ва [бидуни ҳеҷ озоре] ба шоистагӣ раҳоятон кунам');
INSERT INTO "pioneers" VALUES(33,29,'33:29','Ва агар муштоқи Аллоҳ таоло ва паёмбараш ва сарои охират ҳастед, пас, [бидонед, ки] бе тардид, Аллоҳ таоло барои некукоронатон подоши бузурге фароҳам кардаааст»');
INSERT INTO "pioneers" VALUES(33,30,'33:30','Эй занони Паёмбар, ҳар яке аз шумо, ки [гуноҳи] ношоисти ошкоре муртакиб шавад, азобаш дучандон мегардад; ва ин [кор] ҳамвора барои Аллоҳ таоло осон аст');
INSERT INTO "pioneers" VALUES(33,31,'33:31','Ва ҳар яке аз шумо, ки пайваста фармонбардори Аллоҳ таоло ва Паёмбараш бошад ва коре шоиста кунад, подошашро ду баробар медиҳем ва барояш [дар биҳишт] рӯзии арзишманде фароҳам овардаем');
INSERT INTO "pioneers" VALUES(33,32,'33:32','Эй занони Паёмбар, агар парҳезкорӣ кунед, [дар бартарӣ ва шарофат] монанди ҳеҷ як аз занони [одӣ] нахоҳед буд; пас, ҳангоми сухан гуфтан [бо мардон] ноз ва нармиш ба кор набаред, то [мабодо] он ки дар дилаш беморӣ аст, [дар шумо] тамаъ кунад ва ба шеваи шоиста сухан бигӯед');
INSERT INTO "pioneers" VALUES(33,33,'33:33','Ва дар хонаҳоятон бимонед ва ҳамчун ҷилвагари [-и занон дар даврони] ҷоҳилияти пешин, ҷилвагарӣ [ва худнамоӣ] накунед ва намоз барпо доред ва закот бипардозед ва аз Аллоҳ таоло ва Паёмбараш итоат кунед. Эй аҳли хона [паёмбар], ҷуз ин нест Аллоҳ таоло мехоҳад палидӣ [ва озори бегонагон]-ро аз шумо дур намояд ва комилан покатон гардонад');
INSERT INTO "pioneers" VALUES(33,34,'33:34','Ва ҳар он чиро, ки аз оёти илоҳӣ ва суннати Паёмбар дар хонаҳоятон хонда мешавад, ба ёд дошта бошед. Бе тардид, Аллоҳ таоло ҳамвора борикбини огоҳ аст');
INSERT INTO "pioneers" VALUES(33,35,'33:35','Аллоҳ таоло барои мардони мусулмон ва занони мусулмон ва мардони муъмин ва занони муъмин ва мардони фармонбардор ва занони фармонбардор ва мардони ростгӯ ва занони ростгӯ ва мардони шикебо ва занони шикебо ва мардони фурӯтан ва занони фурӯтан ва мардони инфоқгар ва занони инфоқгар ва мардони рӯзадор ва занони рӯзадор ва мардони покдоман ва занони покдоман ва мардон ва заноне, ки Аллоҳро бисёр ёд мекунанд, барои [ҳамаи] онон омурзиш ва подоши бузург фароҳам намудааст');
INSERT INTO "pioneers" VALUES(33,36,'33:36','Барои ҳеҷ мард ва зани муъмине шоиста нест, ки вақте Аллоҳ таоло ва расулаш ба коре фармон медиҳанд [дар иҷрои он фармон] ихтиёре дошта бошанд;ва ҳар ки аз Аллоҳ таоло ва расулаш нофармонӣ кунад, ҳаққо, ки дар гумроҳии ошкоре гирифтор шудааст');
INSERT INTO "pioneers" VALUES(33,37,'33:37','[Ба ёд овар] Вақте ба [Зайд ибни Ҳориса]-касе, ки Аллоҳ таоло ба ӯ неъмат [Ислом] дода буд ва ту неъмат [озодӣ] бахшида будӣ- [ба насиҳат] мегуфтӣ: «Ҳамсаратро барои худ нигаҳ дор ва аз Аллоҳ таоло парво кун» ва дар дили хеш [дар мавриди издивоҷ бо Зайнаб пас аз талоқаш] пинҳон медоштӣ, ки Аллоҳ таоло ошкоркунандаи он буд ва [чун ин издивоҷ бо суннатҳои ҷоҳилӣ мухолиф буд] аз [сарзаниши] мардум метарсидӣ; ҳол он ки Аллоҳ таоло сазовортар аст, ки аз Ӯ битарсӣ. Пас, ҳангоме ки Зайд ниёзашро аз вай ба поён бурд [ва талоқаш дод], ӯро ба издивоҷи ту даровардем, то барои муъминон дар мавриди издивоҷ бо ҳамсарони писархондаҳояшон, ки аз онон ком гирифтаанд [ва талоқашон додаанд], ҳеҷ гуноҳе набошад; ва фармони Аллоҳ таоло ҳамвора анҷомпазир аст');
INSERT INTO "pioneers" VALUES(33,38,'33:38','Бар Паёмбар, дар мавриди он чи Аллоҳ таоло барояш муқаррар [ва ҳалол] доштааст, ҳеҷ гуна гуноҳе вуҷуд надорад. [Ин кор] Суннати илоҳӣ аст [ва] дар бораи касоне [паёмбароне], ки пеш аз ин будаанд [низ сидқ мекунад]; ва фармони Аллоҳ таоло ҳамвора дақиқ ва ҳисобшуда аст');
INSERT INTO "pioneers" VALUES(33,39,'33:39','[ҳамон] касоне, ки паёмҳои Аллоҳро [ба мардум] мерасонанд ва танҳо аз Ӯ метарсанд ва аз ҳеҷ кас ҷуз Аллоҳ таоло тарсе надоранд; ва Аллоҳ таоло барои ҳисобрасии [корҳои бандагонаш] кофӣ аст');
INSERT INTO "pioneers" VALUES(33,40,'33:40','Муҳаммад ҳаргиз падари ҳеҷ яки аз мардони шумо [аз ҷумла Зайд] нест; балки расули Аллоҳ таоло ва хотами паёмбарон аст; ва Аллоҳ таоло ҳамвора ба ҳар чизе доност');
INSERT INTO "pioneers" VALUES(33,41,'33:41','Эй касоне, ки имон овардаед, Аллоҳро бисёр ёд кунед');
INSERT INTO "pioneers" VALUES(33,42,'33:42','Ва бомдод ва шомгоҳ Ӯро ба покӣ биситоед');
INSERT INTO "pioneers" VALUES(33,43,'33:43','Ӯст, ки бар шумо дуруд [ва раҳмат] мефиристад ва фариштагонаш [низ дуоятон мекунанд], то шуморо аз торикиҳо ба сӯйи нур берун оварад ва [Аллоҳ таоло] ҳамвора нисбат ба муъминон меҳрубон аст');
INSERT INTO "pioneers" VALUES(33,44,'33:44','Рӯзе, ки бо Ӯ дидор мекунанд, дурудашон «Салом» аст ва [Аллоҳ таоло] бар эшон подоше арҷманд фароҳам сохтааст');
INSERT INTO "pioneers" VALUES(33,45,'33:45','Эй Паёмбар, Мо туро гувоҳ ва башоратбахш ва бимдиҳанда фиристодаем');
INSERT INTO "pioneers" VALUES(33,46,'33:46','Ва [туро] даъватгаре ба сӯйи Аллоҳ таоло ба фармони Ӯ ва чароғи равшанибахш [қарор додем]');
INSERT INTO "pioneers" VALUES(33,47,'33:47','Ва ба муъминон башорат бидеҳ, ки аз ҷониби Аллоҳ таоло бахшиши бузурге барояшон [дар пеш] аст');
INSERT INTO "pioneers" VALUES(33,48,'33:48','Ва аз кофирон ва мунофиқон итоат накун ва ба озорашон бетаваҷчуҳ бош ва бар Аллоҳ таоло таваккал кун; ва ҳамин басанда аст, ки Аллоҳ таоло [ёвар ва] коргузори ту бошад');
INSERT INTO "pioneers" VALUES(33,49,'33:49','Эй касоне, ки имон овардаед, ҳар гоҳ бо занони муъмин издивоҷ кардед, сипас пеш аз онки бо онон ҳамбистар шавед талоқашон додед, аз ҷониби шумо ҳеҷ иддае бар уҳдаи онон нест, ки ҳисобашро нигоҳ доред; аз ин рӯ, эшонро [бо ҳадяи муносиб] баҳраманд созед ва ба некутарин ваҷҳ раҳояшон созед');
INSERT INTO "pioneers" VALUES(33,50,'33:50','Эй паёмбар, Мо ҳамсаронат, ки маҳрашонро пардохтаӣ ва [низ] канизонеро, ки Аллоҳ таоло [аз баҳраи ҷанг] ба ту арзонӣ доштааст, бароят ҳалол кардаем ва [низ издивоҷ бо] духтари амуҳоят ва духтари аммаҳоят ва духтартағоҳоят ва духтархолаҳоят, ки бо ту ҳиҷрат кардаанд ва [ҳамчунин] агар зани муъмине худро [бидуни маҳрия] ба паёмбар бибахшад, чунончи паёмбар бихоҳад, [метавонад] бо вай издивоҷ кунад. [Ин ҳукм] Вижаи туст, на дигар муъминон. Бе тардид, Мо медонем барои онон дар мавриди ҳамсарон ва канизонашон чи ҳукме таъйин кардаем. [Ин ҳукми вижаро барқарор кардем] То ҳеҷ тангное [дар роҳи анҷоми вазифа] бар ту набошад; ва Аллоҳ таоло ҳамвора омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(33,51,'33:51','[Навбати] ҳар як аз онон [ҳамсаронат]- ро, ки бихоҳӣ [метавонӣ] ба таъхир андозӣ ва ҳар кадомро бихоҳӣ, [метавонӣ] назди хеш ҷой диҳӣ; ва агар ҳар як аз ононро, ки аз ӯ канора гирифтаӣ [ва навбаташ ба таъхир афтодааст] талаб кунӣ, [дар ҳар ҳол] ҳеҷ гуноҳе бар ту нест. Ин [ихтиёр дар интихоб] наздиктар аст ва ин ки чашмашон равшан шавад ва андӯҳгин набошанд ва ҳамагиашон аз он чи ба онон бахшидаӣ хушнуд гардидаанд; ва Аллоҳ таоло медонад, ки шумо [мардон дар бораи муҳаббати бештар ба бархе занонатон] чи дар дил доред; ва Аллоҳ таоло ҳамвора донои бурдбор аст');
INSERT INTO "pioneers" VALUES(33,52,'33:52','Аз ин пас [дигар издивоҷ бо] ҳеҷ зане бароят ҳалол нест ва набояд [бархе аз ҳамсаронатро талоқ диҳӣ, то] ҳамсаронеро ҷойнишини онон кунӣ, ҳатто агар зебоии онон [занони ҷадид] туро хуш ояд. Магар он ки [ба сурати каниз] дар ихтиёрат бошад; ва Аллоҳ таоло ҳамвора бар ҳар чизе нигаҳбон аст');
INSERT INTO "pioneers" VALUES(33,53,'33:53','Эй касоне, ки имон овардаед, ба хонаҳои паёмбар дохил нашавед; магар он ки барои [сарфи] ғизое ба шумо иҷозат диҳанд; бе он ки [зудтар аз вақти ғизо биравед ва] мунтазири омода шуданаш шавед; вале ҳангоме ки шуморо фаро хонданд, ворид шавед ва чун ғизо хӯрдед, пароканда гардед ва саргарми [баҳс ва] гуфтугӯ нашавед. Ин [рафторатон] паёмбарро меозорад ва ӯро аз [ибрози ин мавзӯъ дар баробари] шумо шарм дорад; [вале] Аллоҳ таоло аз [баёни] ҳақ шарм надорад. Ва ҳангоме ки аз ҳамсарони ӯ дархосте доред, [ниёзатонро] аз пушти пардае аз онон бихоҳед [ва дар чеҳраи онон нанигаред; чаро ки] ин [кор] барои дилҳои шумо ва эшон поктар аст; ва [ҳамчунин] бароятон сазовор нест, ки Расулуллоҳро биёзоред ва ҳаргиз шоистаи шумо нест, ки пас аз ӯ бо ҳамсаронаш издивоҷ кунед. Ба ростӣ, ки ин [кор] назди Аллоҳ таоло [гуноҳе] бузург аст');
INSERT INTO "pioneers" VALUES(33,54,'33:54','Агар чизеро ошкор кунед ё пинҳон доред, [бидонед, ки] бетардид, Аллоҳ таоло ба ҳар чизе огоҳ аст');
INSERT INTO "pioneers" VALUES(33,55,'33:55','Бар онон [занони мусулмон] гуноҳе нест [дар надоштани ҳиҷоб] назди падаронашон ва писаронашон ва бародаронашон ва писарони бародаронашон ва писарони хоҳаронашон ва занон [ҳамдин]-ашон ва бардагонашон; ва [эй занони мусулмон] аз Аллоҳ таоло парво кунед. Бе тардид, Аллоҳ таоло бар ҳама чиз гувоҳ аст');
INSERT INTO "pioneers" VALUES(33,56,'33:56','Ба ростӣ, Аллоҳ таоло ва фариштагонаш бар паёмбар дуруд мефиристанд; [пас] эй касоне, ки имон овардаед [шумо низ] бар ӯ дуруд фиристед ва ба шоистагӣ салом гӯед');
INSERT INTO "pioneers" VALUES(33,57,'33:57','Касоне ки Аллоҳ таоло ва паёмбарашро меозоранд, Аллоҳ таоло дар дунё ва охират ононро аз раҳмати хеш дур мекунад ва барояшон азоби хоркунанда омода кардааст');
INSERT INTO "pioneers" VALUES(33,58,'33:58','Ва онон, ки мардон ва занони муъминро бе он ки муртакиби [амали зиште] шуда бошанд, [бо иттиҳомоти нораво] меозоранд, бори дурӯғ ва гуноҳи ошкорро бар дӯш мекашанд');
INSERT INTO "pioneers" VALUES(33,59,'33:59','Эй паёмбар, ба ҳамсарон ва духтаронат ва дигар занони муъмин бигӯ, ки чодарҳояшонро ба шоистагӣ бар [сар ва синаи] хеш бияфкананд. Ин [гуна пӯшиш] муносибтар аст ба ин ки [дар ҷомеа матонат ва виқор] шинохта шаванд ва [дар натиҷа] мавриди озор [ҳарзагон] қарор нагиранд, Ва Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(33,60,'33:60','Агар мунофиқон ва касоне, ки дар дилҳояшон беморӣ [нифоқ] аст ва овозасозӣ дар Мадина [аз дурӯғгӯӣ ва шоеасозӣ] даст барнадоранд, яқинан туро бар онон мусаллат мегардонем. Дар ин сурат ҷуз муддати кӯтоҳе наметавонанд дар ин шаҳр дар канори ту бимонанд');
INSERT INTO "pioneers" VALUES(33,61,'33:61','Лаънатшудагонанд ва ҳар ҷо ки ёфта шаванд, бояд бемуҳобо дастгир ва кушта шаванд');
INSERT INTO "pioneers" VALUES(33,62,'33:62','[Ин] Суннати Аллоҳ таоло дар мавриди афроде [аз мунофиқон] , ки пештар будаанд, ҷорӣ гаштааст; ва ҳаргиз дар суннати Аллоҳ таоло дигаргунӣ [ва тағйире] намеёбӣ');
INSERT INTO "pioneers" VALUES(33,63,'33:63','[Эй паёмбар] Мардум дар бораи [замони вуқуъи] қиёмат аз ту мепурсанд; бигӯ: «Дониши он танҳо назди Аллоҳ таоло аст»; ва ту чи медонӣ? Шояд қиёмат наздик бошад');
INSERT INTO "pioneers" VALUES(33,64,'33:64','Бе тардид, Аллоҳ таоло кофиронро аз раҳмати хеш дур карда ва барояшон оташи афрӯхта муҳайё кардааст');
INSERT INTO "pioneers" VALUES(33,65,'33:65','[Оташе] Ки ҷовидона дар он хоҳанд монд [ва барои дифоъ аз худ] ҳеҷ корсоз ва ёригаре намеёбанд');
INSERT INTO "pioneers" VALUES(33,66,'33:66','Рӯзе, ки чеҳраҳояшон дар оташи дузах дигаргун мешавад, [бо пушаймонӣ] мегӯянд: «Эй кош, аз Аллоҳ таоло ва Паёмбар итоат карда будем');
INSERT INTO "pioneers" VALUES(33,67,'33:67','[Онон баҳона меоваранд ва] мегӯянд: «Парвардигоро, мо аз раҳбарон ва бузургони худ итоат кардем ва онон [буданд], ки моро гумроҳ карданд');
INSERT INTO "pioneers" VALUES(33,68,'33:68','Парвардигоро! Азобашонро дучандон кун ва ба лаънати бузург [ва сахт] лаънат бифармо!»');
INSERT INTO "pioneers" VALUES(33,69,'33:69','Эй касоне, ки имон овардаед, [Паёмбарро наёзоред ва] монанди касоне набошед ки Мӯсоро [бо тӯҳматҳои нораво] озурданд, сипас Аллоҳ таоло ӯро [аз он чи дар ҳаққаш мегуфтанд] табриа намуд; ва [Мӯсо] ҳамвора назди Аллоҳ обрӯманд аст.');
INSERT INTO "pioneers" VALUES(33,70,'33:70','Эй касоне, ки имон овардаед, аз Аллоҳ таоло парво кунед ва сухани рост ва дуруст бигӯед');
INSERT INTO "pioneers" VALUES(33,71,'33:71','[Парҳезгор ва ростгӯ бошед] то Аллоҳ таоло корҳоятонро ислоҳ кунад ва гуноҳонатонро биёмурзад ва [бидонед] ҳар ки аз Аллоҳ таоло ва Паёмбараш итоат кунад, яқинан ба комёбии бузурге даст ёфтааст');
INSERT INTO "pioneers" VALUES(33,72,'33:72','Мо амонати [анҷоми таколифи шаръӣ] – ро бар осмонҳо ва замин ва кӯҳсорон арза доштем ва онҳо аз таҳаммулаш сар боз заданд ва аз он тарсиданд ва [-ле] инсон онро пазируфт. Ҳаққо, ки ӯ [дар ҳаққи хеш] ҳамвора ситамгор ва нодон аст');
INSERT INTO "pioneers" VALUES(33,73,'33:73','[Инсон чунин кард] то Аллоҳ таоло мардону занони мунофиқ ва мардону занони мушрикро азоб кунад ва тавбаи мардону занони муъминро [ки амонатдорони шоистае буданд] бипазирад; ва Аллоҳ таоло ҳамвора омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(34,1,'34:1','Ситоиш махсуси Аллоҳ таоло, ки он чи дар осмонҳо ва замин аст аз они Ӯст ва дар охират [низ] ситоиш аз они Ӯст ва Ӯ ҳакиму огоҳ аст');
INSERT INTO "pioneers" VALUES(34,2,'34:2','Ҳар чи дар замин фурӯ меравад ва ҳар чи аз он бармеояд ва он чи аз осмон фуруд меояд ва он чи ба сӯйи он боло меравад [ҳамаро Аллоҳ таоло] медонад ва Ӯ меҳрубони омурзанда аст');
INSERT INTO "pioneers" VALUES(34,3,'34:3','Касоне, ки куфр варзиданд, мегӯянд: «Қиёмат ҳаргиз ба суроғи мо нахоҳад омад». Бигӯ: «Оре, савганд ба Парвардигорам, ки донои ғайб аст, ҳатман, ба суроғатон меояд. ҳамсанги заррае дар осмонҳо ва замин аз Ӯ пӯшида нест ва кучактар ва бузургтар аз он зарра чизе нест, магар он ки дар китобе ошкор [Лавҳи маҳфуз] сабт аст');
INSERT INTO "pioneers" VALUES(34,4,'34:4','То касонеро, ки имон овардаанд ва корҳои шоиста анҷом додаанд подош диҳад. Эшонанд, ки бахшоиш ва рӯзии арзишманде [дар пеш] доранд»');
INSERT INTO "pioneers" VALUES(34,5,'34:5','Ва касоне, ки дар [роҳи инкор ва такзиби] оёти Мо кӯшиданд, то [ба пиндори хеш] Моро дармонда кунанд, азобе сахт ва дарднок [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(34,6,'34:6','Аҳли дониш [донишмандони саҳоба ва уламои муъмини аҳли китоб] медонанд он чи ки аз сӯйи Парвардигорат бар ту нозил шуда, ҳақ аст ва ба роҳи [Аллоҳ таоло] шикастнопазири сутуда ҳидоят мекунад');
INSERT INTO "pioneers" VALUES(34,7,'34:7','Касоне, ки куфр вазиданд [бо тамасхур ба якдигар] мегӯянд: «Мехоҳед мардеро нишонатон диҳем, ки иддио мекунад вақте [пас аз марг] комилан муталошӣ шудед, [бори дигар] дар офаринише нав дархоҳед омад?');
INSERT INTO "pioneers" VALUES(34,8,'34:8','Оё [ин мард] бар Аллоҳ таоло дурӯғ мебандад ё девона аст?» [ҳаргиз чунин нест] балки касоне, ки ба охират имон надоранд, дар азоб [шадиди охират] ва гумроҳии дуру дароз [дунё] гирифторанд');
INSERT INTO "pioneers" VALUES(34,9,'34:9','Оё [кофирон] ба осмону замине, ки пеши рӯ ва пушти сарашон аст, нанигаристаанд? Агар бихоҳем, ононро [монанди Қорун] дар замин фурӯ мебарем ё [ҳамчун қавми Шуайб] тиккае аз осмонро бар сарашон меафканем. Бе гумон, дар ин [ҳушдор] барои ҳар бандаи тавбакор, нишонае [аз қудрати Аллоҳ таоло] аст');
INSERT INTO "pioneers" VALUES(34,10,'34:10','Мо аз ҷониби хеш ба Довуд фазилате [ҳукмронӣ ва нубувват] бахшидем [ва гуфтем] «Эй кӯҳсорон ва эй парандагон, [дар ситоиш ва ниёиш] бо ӯ ҳамовоз шавед» ва оҳанро барояш нарм кардем [то ҳар чи мехоҳад бисозад]');
INSERT INTO "pioneers" VALUES(34,11,'34:11','[Ба ӯ дастур додем] ки: «Зиреҳҳои баланд [ва фарох] бисоз ва дар бофтан [ва пайвастани ҳалқаҳои зиреҳ] санҷида ва дақиқ кор кун ва [шумо эй мардум] некукорӣ кунед, [ки] бе тардид, Ман ба он чи мекунед, бино ҳастам»');
INSERT INTO "pioneers" VALUES(34,12,'34:12','Ва барои Сулаймон бодро [ба хидмат гумоштем], ки [дар вазиш] субҳгоҳон, масири як моҳаро тай мекард ва шомгоҳон [низ] масири як моҳаро мепаймуд; ва чашмаи миси [гудохта]-ро барояш равон сохтем ва гурӯҳе аз ҷинниён, ба фармони Парвардигораш, назди ӯ кор мекарданд ва ҳар як аз онон, ки аз фармони Мо сарпечӣ мекард, ба ӯ аз азоби сӯзон мечашондем');
INSERT INTO "pioneers" VALUES(34,13,'34:13','Онҳо барои ӯ ҳар чи мехост месохтанд: аз [кох ва] ибодатгоҳ ва муҷассама ва косаҳое [ки аз бузургӣ] ҳамчун ҳавз [буданд] ва дегҳое бисёр бузург ва собит. [Ба онон гуфтем] «Эй оли Довуд, сипоси [неъматҳои Маро] ба ҷой оваред» ва [-ле] андаке аз бандагонам сипосгузоранд');
INSERT INTO "pioneers" VALUES(34,14,'34:14','Пас, чун маргро бар Сулаймон муқаррар доштем, [ҳеҷ кас] ононро аз маргаш огоҳ насохт, магар мӯриёна [кирмак]-е, ки асояшро хӯрд. Пас, чун [пайкари Сулаймон] фурӯ афтод, ҷинниён дарёфтанд, ки агар аз ғайб огоҳ буданд, ҳаргиз дар он азоби сангин [ва кори тоқатфарсо] боқӣ намемонданд');
INSERT INTO "pioneers" VALUES(34,15,'34:15','Дар маҳалли сукунати [қавми] «Сабаъ» ду боғ [бузург] аз рост ва чап [қарор дошт], ки нишонае [аз қудрат ва бахшиши илоҳӣ] буд. [Ба онон гуфтем] «Аз рӯзии Парвардигоратон бихӯред ва шукргузораш бошед, [ки] сарзамине пок ва Парвардигоре омурзанда [доред]');
INSERT INTO "pioneers" VALUES(34,16,'34:16','Аммо онон [аз шукр ва имон] рӯй гардонданд; пас, Мо сели вайронгар ба сӯяшон равон кардем ва ду боғи [пурмаҳсули] ононро ба ду боғи [беарзиш] табдил намудем, [ки танҳо] меваҳои талх ва дарахти шӯрагаз ва андаке дарахти сидр дошт');
INSERT INTO "pioneers" VALUES(34,17,'34:17','Мо ононро ба сазои носипосиашон, инчунин, кайфар додем ва оё ҷуз носипосро кайфар медиҳем?');
INSERT INTO "pioneers" VALUES(34,18,'34:18','Ва [ҳамчунин] миёни онҳо [қавми Сабаъ дар Яман] ва сарзаминҳое, ки ба онҳо баракат дода будем, [Шом] шаҳрҳое [хуррам ва] ба ҳам пайваста қарор додем ва сайру сафаре [осон] дар он муқаррар намудем [ва ба онон гуфтем] «Шабу рӯз байни онҳо эмин [ва осуда] сафар кунед»');
INSERT INTO "pioneers" VALUES(34,19,'34:19','Аммо онон [бо носипосӣ] гуфтанд: «Парвардигоро, миёни сафарҳои мо фосила [-и бештаре] эҷод кун [то арзиши аспҳо ва шутурҳоямон намоён гардад ва бенавоён натавонанд ҳамчун сарватмандон ба роҳати сафар кунанд]» ва ба хештан ситам карданд; пас, Мо [низ саргузашти] ононро достоне [ибратомӯз] барои дигарон сохтем ва сахт [муталошӣ ва] парокандаашон намудем. Ба ростӣ, ки дар ин [моҷаро] барои ҳар шикебоӣ сипосгузор, нишонаҳое [аз қудрати илоҳӣ] аст');
INSERT INTO "pioneers" VALUES(34,20,'34:20','Ва яқинан, иблис гумони худро дар мавриди онон ба таҳаққуқ расонд; пас, ҷуз гурӯҳе [андак] аз муъминон, [ҳамагӣ] аз ӯ пайравӣ карданд');
INSERT INTO "pioneers" VALUES(34,21,'34:21','[Шайтон] бар онон ҳеҷ тасаллуте надошт; балки [Мо мехостем] касеро, ки ба охират имон дорад, аз касе, ки дар бораи он тардид дорад, боз шиносем; ва Парвардигорат бар ҳар чизе нигаҳбон аст');
INSERT INTO "pioneers" VALUES(34,22,'34:22','[Эй паёмбар, ба мушрикон] бигӯ: «Касонеро, ки ба ҷойи Аллоҳ таоло [ёвар ва шафоатгари худ] мепиндоред, [ба фарёд] бихонед. Онон ҳатто ҳамсанги заррае дар осмонҳо ва замин молики чизе нестанд ва на дар [офариниш ва тадбири] он ду [замину осмон] ширкате доранд ва на Ӯ [ Аллоҳ таоло] аз ҷониби онон ҳеҷ пуштибоне дорад');
INSERT INTO "pioneers" VALUES(34,23,'34:23','Шафоат [ҳеҷ кас] назди Ӯ [Аллоҳ таоло] суде надорад; магар барои он ки [Аллоҳ таоло] ба ӯ иҷозат дода бошад. [Дар он рӯз фариштагон аз хавф ва ҳайбати илоҳӣ тарсонанд] то онгоҳ, ки изтироб [ва ташвиш] аз дилҳояшон бартараф мегардад, [гурӯҳе аз онон ба гурӯҳи дигар] мегӯянд: «Парвардигоратон чӣ гуфт?» [Онон дар посух] мегӯянд: «ҳақро [фармуд] ва Ӯ баланмартабаи бузург аст»');
INSERT INTO "pioneers" VALUES(34,24,'34:24','[Эй паёмбар, ба мушрикон] бигӯ: «Чи касе аз осмонҳо ва замин ба шумо рӯзӣ медиҳад?» [Дар посух] бигӯ: «Аллоҳ таоло; ва яқинан [як гурӯҳ аз байни] мо ва шумост ё бар [роҳи] ҳидоят ё дар гумроҳии ошкор»');
INSERT INTO "pioneers" VALUES(34,25,'34:25','Бигӯ: «[Рӯзи қиёмат] Шумо аз гуноҳе, ки мо муртакиб шудаем, бозхост намешавед ва моро [низ] ба хотири он чи шумо анҷом медиҳед, бозхост намекунанд');
INSERT INTO "pioneers" VALUES(34,26,'34:26','Бигӯ: «[Он рӯз] Парвардигорамон [ҳамаи] моро гирд меоварад, сипас миёни мо ба ҳақ [ва адолат] доварӣ мекунад ва Ӯ довари доност»');
INSERT INTO "pioneers" VALUES(34,27,'34:27','Бигӯ: «Касонеро, ки [дар қудрат ва тадбир] шарики Ӯ [Аллоҳ таоло] сохтаед, нишонам диҳед. Ҳаргиз [чунин нест, ки мепиндоред], балки Ӯ шикастнопазири ҳаким аст');
INSERT INTO "pioneers" VALUES(34,28,'34:28','[Эй паёмбар] Мо туро ҷуз башоратбахш ва бимдиҳандае барои ҳамаи мардум нафиристодаем; аммо бештари мардум [ин ҳақиқатро] намедонанд');
INSERT INTO "pioneers" VALUES(34,29,'34:29','Ва [кофирон] мегӯянд: «Агар ростгӯед, ин ваъда [қиёмат] кай хоҳад буд?»');
INSERT INTO "pioneers" VALUES(34,30,'34:30','Бигӯ: «Ваъдагоҳатон рӯзест, ки на соате аз он таъхир мекунед ва на [бар он] пешӣ мегиред»');
INSERT INTO "pioneers" VALUES(34,31,'34:31','Ва касоне, ки куфр варзиданд мегӯянд: «Мо ҳаргиз ба ин Қуръон ва ба он [китобҳое], ки пеш аз он будааст, имон намеоварем». Кош медидӣ он вақтро, ки ин ситамгорон дар пешгоҳи Парвардигорашон нигоҳ дошта мешаванд [ва] сухани якдигарро рад мекунанд [ва ҳар як гуноҳро ба гардани дигаре меандозад]. Онон, ки [дар дунё] ба нотавонӣ кашида шуда буданд ба касоне, ки гарданкашӣ мекарданд, мегӯянд: «Агар шумо набудед, мо ҳатман, имон меовардем»');
INSERT INTO "pioneers" VALUES(34,32,'34:32','Онон, ки гарданкашӣ мекарданд, [дар посух ба] касоне, ки ба нотавонӣ кашида шуда буданд, мегӯянд: «Оё мо [будем, ки] шуморо аз ҳидоят-пас аз он ки ба сӯятон омад- боздоштем? [Ҳаргиз] Балки шумо худ гунаҳгор [ва аҳли фасод] будед»');
INSERT INTO "pioneers" VALUES(34,33,'34:33','Онон, ки ба нотавонӣ кашида шуда баданд, ба касоне, ки гарданкашӣ мекарданд, мегӯянд: «[Чунин нест] балки найранги шабу рӯз [-и шумо сабаб шуд, ки аз ҳидоят бозмонем] онгоҳ, ки ба мо дастур медодед, ба Аллоҳ таоло кофир шавем ва барояш [дар қудрат ва парастиш] ҳамтоёне қарор диҳем». Онон ҳангоме ки азоб [-и илоҳӣ]-ро мебинанд, пушаймонии худро [дар дил] пинҳон мекунанд ва Мо ғулу занҷирҳоро дар гардани ононе, ки куфр варзидаанд, меандозем. Оё ҷуз [ба кайфари] он чи мекарданд, муҷозот мешаванд?');
INSERT INTO "pioneers" VALUES(34,34,'34:34','Ва дар ҳеҷ шаҳре бимдиҳандае нафиристодем, магар ин ки сарватмандони саркаши онҷо гуфтанд: «Паёмеро, ки маъмур ба [иблоғи] он шудаед, ҳатман, инкор мекунем»');
INSERT INTO "pioneers" VALUES(34,35,'34:35','Онон мегуфтанд: «Амвол ва фарзандони мо [аз шумо] бештар аст ва ҳаргиз муҷозот нахоҳем шуд»');
INSERT INTO "pioneers" VALUES(34,36,'34:36','[Эй паёмбар] бигӯ: «Бе тардид, Парвардигорам [барои озмоиши мардум, неъмат ва] рӯзиро бар ҳар ки бихоҳад, густурда медорад ва [ё] танг мегирад; вале бештари мардум намедонанд');
INSERT INTO "pioneers" VALUES(34,37,'34:37','Ва амволу фарзандонатон [фазилате] нест, ки шуморо ба пешгоҳи Мо наздик созад, магар касе, ки имон оварда ва коре шоиста анҷом додааст; пас, онон дар баробари он чи кардаанд, подоши дучандон доранд ва дар хонаҳои баланди биҳишт дар амну осоишанд');
INSERT INTO "pioneers" VALUES(34,38,'34:38','Ва касоне, ки дар [дур кардани мардум аз] оёти Мо мекушанд, то [ба пиндори хеш] Моро дармонда кунанд, ононанд, ки [ба азоби дузах] фаро хонда мешаванд');
INSERT INTO "pioneers" VALUES(34,39,'34:39','[Эй Паёмбар] Бигӯ: «Бе тардид, Парвардигорам [неъмат ва] рӯзиро бар ҳар ки бихоҳад, густурда медорад ва [ё] танг мегирад; ва ҳар чизеро [ки дар роҳи Аллоҳ таоло] инфоқ кунед, Ӯ ба ҷойи он [мол- ва беҳтар аз он –ба шумо] бозпас медиҳад; ва Ӯ беҳтарин рӯзидиҳанда аст»');
INSERT INTO "pioneers" VALUES(34,40,'34:40','Ва [ёд кун аз] рӯзе, ки Аллоҳ таоло ҳамаи ононро бармеангезад, онгоҳ ба фариштагон мегӯяд: «Оё инҳо шуморо мепарастиданд?»');
INSERT INTO "pioneers" VALUES(34,41,'34:41','[Фариштагон] мегӯянд: «Ту муназзаҳӣ; [дӯст ва] корсози мо Ту ҳастӣ, на онон; балки [ин мушрикон] шаётинро мепарастиданд ва бештарашон ба онҳо имон доштанд»');
INSERT INTO "pioneers" VALUES(34,42,'34:42','Пас, имрӯз ҳеҷ як аз шумо суд ва зиёне барои дигаре надорад; ва ба касоне, ки [бо ширк варзидан] ба худ ситам мекарданд, мегӯем: «Бичашед азоби оташеро, ки дурӯғаш меангоштед');
INSERT INTO "pioneers" VALUES(34,43,'34:43','Ва ҳангоме ки оёти равшани Мо бар онон [кофирон] хонда мешавад, мегӯянд: «Ин ҷуз марде нест, ки мехоҳад шуморо аз он чи падаронатон мепарастиданд, боздорад»; ва [низ] мегӯянд: «Ин [Қуръон] ҷуз дурӯғе барбофта нест»; ва касоне, ки куфр варзиданд, дар бораи ҳақ – вақте ба сӯяшон омад – гуфтанд: «Ин [Қуръон, чизе] нест, магар ҷодуе ошкор»');
INSERT INTO "pioneers" VALUES(34,44,'34:44','Мо [пеш аз ин Қуръон] ҳеҷ китобе ба онон [мушрикон] надодаем, ки онро бихонанд [ва бо истинод ба матолибаш туро дурӯғгӯ бидонанд] ва пеш аз ту ҳеҷ бимдиҳандае ба сӯяшон нафиристодаем');
INSERT INTO "pioneers" VALUES(34,45,'34:45','Касоне, ки пеш аз онон буданд [низ паёмбарони илоҳиро] дурӯғгӯ ангоштанд ва [ин дар ҳоле аст, ки мушрикони қавми ту, ҳатто] ба даҳяки он чи [аз қудрату сарват] ба онон дода будем, нарасидаанд, вале [он кофирон] паёмбаронамро дурӯғгӯ ангоштанд; пас, [бингар, ки] кайфари Ман чи гуна буд');
INSERT INTO "pioneers" VALUES(34,46,'34:46','[Эй Паёмбар, ба мушрикон] бигӯ: «Ман фақат шуморо ба як [сухан] панд медиҳам, ки: ду-ду ва як-як [гурӯҳ-гурӯҳ ё инфиродӣ] барои Аллоҳ таоло бархезед, сипас [дар аҳвол ва зиндагии паёмбар] бияндешед, [то бидонед, ки] ин ҳамнишини шумо [Муҳаммад] девона нест; ӯ бимдиҳандаест, ки пеш аз [фаро расидани] азоби сахт, барои [ҳидояти] шумо омадааст»');
INSERT INTO "pioneers" VALUES(34,47,'34:47','Бигӯ: «Ҳар подоше, ки [барои рисолатам] аз шумо хостаам, барои худатон бошад. Подоши ман ҷуз бар [уҳдаи] Аллоҳ таоло нест ва Ӯ бар ҳар чизе гувоҳ аст»');
INSERT INTO "pioneers" VALUES(34,48,'34:48','Бигӯ: «Яқинан Парвардигорам, ки донои розҳои ниҳон аст, [истидлолҳои] ҳақро бар ботил фурӯ меафканад [ва онро нобуд месозад]»');
INSERT INTO "pioneers" VALUES(34,49,'34:49','Бигӯ: «Ҳақ [Ислом] фаро расид ва ботил наметавонад [таъсир ва қудрати ҷадиде] оғоз кунад ва на [нуфузи гузаштаашро] бозгардонад');
INSERT INTO "pioneers" VALUES(34,50,'34:50','Бигӯ: «Агар [ман бо расондани ин паём] гумроҳ шуда бошам, ҷуз ин нест, ки ба зиёни хеш гумроҳ шудаам ва агар ҳидоят ёфта бошам, ба сабаби он [сухане] аст, ки Парвардигорам бар ман ваҳй мекунад. Бе тардид, Ӯ шунаво [ва] наздик аст»');
INSERT INTO "pioneers" VALUES(34,51,'34:51','Кош ҳангомеро медидӣ, ки [кофирон аз мушоҳидаи азоб] ваҳшатзада мешаванд ва [ҳеҷ роҳи] гурезе нест ва ононро аз ҷойи наздике [ки интизорашро надоранд] дастгир мекунанд');
INSERT INTO "pioneers" VALUES(34,52,'34:52','Онон [дар ин ҳол] мегӯянд: «Ба ин [қиёмат] имон овардем»; ва [акнун, ки мурдаанд] чи гуна аз ҷойи [чунин] дур дастрасӣ [ба имон] барояшон мумкин аст?');
INSERT INTO "pioneers" VALUES(34,53,'34:53','Ҳол он ки онон дар дунё ин [қиёмат]-ро инкор мекарданд ва аз дур [ва бидуни дониш ва огоҳӣ, ба Паёмбар] нисбатҳои нораво медонанд');
INSERT INTO "pioneers" VALUES(34,54,'34:54','Ва [саранҷом] миёни онон ва он чи [аз лаззатҳои дунявӣ] ки хуш доранд, ҷудоӣ афтод; чунонки пеш аз ин низ бо амсоли [ва ҳамфикрони] эшон низ чунин рафторе шуд [чаро ки] яқинан онон [низ нисбат ба қиёмат] сахт дар тардид буданд');
INSERT INTO "pioneers" VALUES(35,1,'35:1','Сипос [ва ситоиш] аз они Аллоҳи офаринандаи осмонҳо ва замин аст; [Парвардигоре, ки] фариштагонро бо болҳои дугона ва сегона ва чаҳоргона, фиристодагон [-и хеш ба сӯйи мардум] қарор дод [ва] ҳар чи бихоҳад дар офариниш меафзояд. Бе тардид, Аллоҳ таоло бар ҳар чизе тавоност');
INSERT INTO "pioneers" VALUES(35,2,'35:2','Ҳар [бахшоиш ва] раҳмате, ки Аллоҳ таоло барои мардумон бикушояд, ҳеҷ боздорандае барояш нест ва ҳар чиро бозгирад, фиристандае пас аз Ӯ вуҷуд надорад; ва Ӯ шикастнопазиру ҳаким аст');
INSERT INTO "pioneers" VALUES(35,3,'35:3','Эй мардум, неъмати Аллоҳро бар худ ба ёд оваред. Оё офаринандае ҷуз Аллоҳ таоло ҳаст, ки шуморо аз осмон ва замин рӯзӣ диҳад? Ҳеҷ маъбуде ҷуз Ӯ нест. Пас, чи гуна [аз ҳақ] мунҳариф мешавед?');
INSERT INTO "pioneers" VALUES(35,4,'35:4','[Эй паёмбар] Агар мушрикон туро дурӯғгӯ меангоранд [ғамгин мабош; зеро] паёмбарони пеш аз туро низ дурӯғгӯ ангоштанд ва [саранҷом ҳамаи] корҳо ба Аллоҳ таоло боз гардонида мешавад');
INSERT INTO "pioneers" VALUES(35,5,'35:5','Эй мардум, яқинан, ваъдаи Аллоҳ таоло [дар мавриди қиёмат] ҳақ аст; пас, [ҳушёр бошед, ки лаззатҳои] зиндагии дунё шуморо нафиребад ва шайтон шуморо нисбат ба [таъхир дар азоб ва шикебоӣ] Аллоҳ таоло нафиребад');
INSERT INTO "pioneers" VALUES(35,6,'35:6','Бе тардид, шайтон душмани шумост; пас, шумо низ ӯро душман [-и худ] гиред. Ҷуз ин нест, ки ӯ пайравонашро [ба куфр] мехонад, то аз аҳли оташ бошанд');
INSERT INTO "pioneers" VALUES(35,7,'35:7','Касоне, ки куфр варзидаанд, азобе сахт [дар пеш] доранд ва касоне, ки имон овардаанд ва корҳои шоиста анҷом додаанд омурзиш ва подоше бузург [дар итизор] доранд');
INSERT INTO "pioneers" VALUES(35,8,'35:8','Пас, оё касе, ки кирдори зишташ дар назараш ороста гаштааст ва онро неку мебинад [ҳамчун муъмини ҳақиқатбин аст?] пас, бе тардид, Аллоҳ таоло ҳар киро хоҳад гумроҳ месозад ва ҳар киро бихоҳад ҳидоят мекунад; пас, [эй паёмбар] вуҷудатро ба хотири дареғҳо [-и фаровон] бар [гумроҳии] онон табоҳ накун. Бе тардид, Аллоҳ таоло бар он чи мекунанд, огоҳ аст');
INSERT INTO "pioneers" VALUES(35,9,'35:9','Аллоҳ таоло аст, ки бодҳоро равон мекунад ва [онҳо низ] абреро бармеангезанд; сипас Мо он [абр]-ро ба сӯйи сарзамине [хушк ва] мурда мефиристем ва ба [василаи] он заминро – пас аз хушкӣ ва хазонаш–зинда мекунем. Барангехтан [-и мурдагон аз қабрҳо низ] чунин аст');
INSERT INTO "pioneers" VALUES(35,10,'35:10','Ҳар ки сарбаландӣ [-и дунё ва охиратро] мехоҳад, [бидонад, ки] сарбаландӣ ҳама аз они Аллоҳ таоло аст. Гуфтори пок [ва зикри илоҳӣ] ба сӯяш авҷ мегирад ва кирдори шоистаи он [гуфтор]-ро боло мебарад; ва онон, ки зиштиҳо [ва гуноҳон]-ро дасисачинӣ мекунанд, азоби сахте [дар пеш] доранд ва [нақша ва] найрангашон нобуд мегардад');
INSERT INTO "pioneers" VALUES(35,11,'35:11','Ва Аллоҳ таоло [нахустин] шуморо аз хоке офарид ва он гоҳ [фарзандонаш] аз нутфае [падид овард]; сипас шуморо ҷуфтҳо [-и якдигар] қарор дод. Ҳеҷ [ҷондори] модае бордор намешавад ва зоймон намекунад, магар бо дониш [ва огоҳии] Ӯ; ва ҳеҷ [ҷондори] куҳансоле умри тӯлонӣ намекунад ва аз умраш коста намегрдад, магар он ки дар китобе [Лавҳи маҳфуз] сабт аст. Ба ростӣ, ки ин кор барои Аллоҳ таоло осон аст');
INSERT INTO "pioneers" VALUES(35,12,'35:12','Ва он ду дарё яксон нестанд: ин яке ширин ва хушгувор аст ва нӯшиданаш гуворост ва он яке шӯру талх аст; ва аз [сайди] ҳар як гӯште тоза мехӯред ва зеваре [ҳамчун марворид ва марҷон аз аъмоқаш] берун меоваред, ки онҳоро бар тан мекунед; ва киштиҳоро дар он дарё мебинӣ, ки [синаи амвоҷро] мешикофанд, то аз бахшоиши он баҳраманд гардед; бошад, ки сипос гузоред');
INSERT INTO "pioneers" VALUES(35,13,'35:13','[Аллоҳ таоло] шабро дар рӯз дармеоварад ва рӯзро дар шаб дармеоварад [ва инчунин бар тӯли ҳар яке меафзояд] ва хуршед ва моҳро ба хидмат [-и инсон] гумошт, ки ҳар як то замоне муайян [ дар мадори худ] равон бошанд. Ин аст Аллоҳ таоло–Парвардгори шумо; фармонравоӣ аз они Ӯст ва касонеро, ки ба ҷойи Ӯ мехонед, [дар баробараш чунон фақиранд, ки ҳатто] пусти ҳастаи хурмоеро доро нестанд');
INSERT INTO "pioneers" VALUES(35,14,'35:14','Агар ононро бихонед, садоятонро намешунаванд ва [ба фарзи маҳол] агар бишнаванд, посухатонро намедиҳанд ва рӯзи қиёмат аз ширки шумо безорӣ меҷӯянд; ва [эй паёмбар] ҳеҷ кас ҳамчун [Парвардгори] огоҳ туро [аз ҳақоиқ] хабардор намесозад');
INSERT INTO "pioneers" VALUES(35,15,'35:15','Эй мардум, [ҳамаи] шумо ба Аллоҳ таоло ниёзмандед ва [танҳо] Аллоҳ таоло аст, ки бениёзи сутуда аст');
INSERT INTO "pioneers" VALUES(35,16,'35:16','Ӯ агар бихоҳад, шуморо [аз миён] мебарад ва офаринише ҷадид [ба ҷойи шумо падид] меоварад');
INSERT INTO "pioneers" VALUES(35,17,'35:17','Ва [анҷоми] ин кор барои Аллоҳ таоло душвор нест');
INSERT INTO "pioneers" VALUES(35,18,'35:18','Ва ҳеҷ гунаҳгоре, бори гуноҳи дигареро ба дӯш намегирад ва агар [шахси] гаронборе, [дигареро] барои ҳамли бори [гуноҳони] хеш фаро бихонад, [чизе аз он бор] бардошта намешавад; ҳарчанд, ки ӯ хешованд [-и наздикаш] бошад. [Эй паёмбар] Ҷуз ин нест, ки ту фақат ба касоне ҳушдор медиҳӣ, ки дар ниҳон аз [муҷозоти] Парвардгорашон метарсанд ва намоз барпо медоранд. Ҳар ки [аз гуноҳон] пок шавад, пас? яқинан танҳо ба суди хеш пок гаштааст ва бозгашт [-и ҳама] ба сӯйи Аллоҳ таоло аст');
INSERT INTO "pioneers" VALUES(35,19,'35:19','Ва [кофир ва муъмин баробар нестанд; чунонки] нобино ва бино яксон нестанд');
INSERT INTO "pioneers" VALUES(35,20,'35:20','Ва на торикиҳо ва нур');
INSERT INTO "pioneers" VALUES(35,21,'35:21','Ва на соя ва боди сӯзон');
INSERT INTO "pioneers" VALUES(35,22,'35:22','Ва зиндагон ва мурдагон яксон нестанд. Бе тардид, Аллоҳ таоло [нидои ҳақро] ба гӯши ҳар ки бихоҳад мерасонад ва ту [эй паёмбар] наметавонӣ касонеро, ки дар гӯрҳо ҳастанд, шунаво созӣ');
INSERT INTO "pioneers" VALUES(35,23,'35:23','Ту ҷуз бимдиҳандае [беш] нестӣ');
INSERT INTO "pioneers" VALUES(35,24,'35:24','Ба ростӣ, Мо туро ба ҳақ башоратдиҳанда ва бимдиҳанда фиристодем ва [дар наслҳои гузашта низ] ҳеҷ уммате набуд, магар он ки миёнашон бимдиҳандае гузашта аст');
INSERT INTO "pioneers" VALUES(35,25,'35:25','Ва агар [мушрикон] туро дурӯғгӯ меангоранд, [аҷиб нест; чаро ки] бе тардид, касоне, ки пеш аз онон буданд, [низ] паёмбаронашонро, ки бо далоили равшан ва китобҳои осмонӣ ва китоби равшангар ба суроғашон омаданд, дурӯғгӯ ангоштанд');
INSERT INTO "pioneers" VALUES(35,26,'35:26','Он гоҳ [Ман низ] касонеро, ки куфр варзида буданд, нобуд кардам; пас, [бингар, ки] кайфари Ман чи гуна буд');
INSERT INTO "pioneers" VALUES(35,27,'35:27','Оё надидаӣ, ки Аллоҳ таоло аз осмон обе нозил кард ва ба [василаи] он [аз дарахтони мухталиф] меваҳое падид овардем, ки рангҳояш гуногун аст ва аз кӯҳҳо [низ рагаҳо ва] роҳҳое сафед ва қирмиз ва комилан сиёҳ [офаридем], ки рангҳояш гуногун аст?');
INSERT INTO "pioneers" VALUES(35,28,'35:28','Ва ҳамчунин аз инсонҳо ва ҷунбандагон ва чаҳорпоён [низ махлуқоте офаридем, ки] рангҳояш гуногун аст. Ба ростӣ, аз [миёни] бандагони Аллоҳ таоло, танҳо доноён [ҳастанд, ки ба сифот ва аҳкоми илоҳӣ огоҳӣ доранд ва] аз Ӯ метарсанд. Бе тардид, Аллоҳ таоло шикастнопазири омурзанда аст');
INSERT INTO "pioneers" VALUES(35,29,'35:29','Дар ҳақиқат, касоне, ки китоби Аллоҳро мехонанд ва намоз барпо медоранд ва аз он чи ба эшон рӯзӣ додаем, пинҳону ошкор инфоқ мекунанд, ба тиҷорате [пурсуд] умед доранд, ки ҳаргиз зиён [ва касодӣ] надорад');
INSERT INTO "pioneers" VALUES(35,30,'35:30','То [Аллоҳ таоло] подошашонро ба тамомӣ бидиҳад ва аз фазли хеш бар [ҳаққи] онон биафзояд. Бе тардид, Ӯ омурзандаи сипосгузор аст');
INSERT INTO "pioneers" VALUES(35,31,'35:31','[Эй паёмбар] он чи аз ин китоб ба ту ваҳй кардем ҳақ аст ва он [китобҳои осмонӣ]-ро, ки пештар будааст, тасдиқ мекунад. Бе тардид, Аллоҳ таоло ба [ҳоли] бандагонаш огоҳ ва биност');
INSERT INTO "pioneers" VALUES(35,32,'35:32','Онгоҳ ин китоб [Қуръон]-ро ба гурӯҳе аз бандагонамон, ки баргузидем [уммати Муҳаммад] ба мерос додем; пас, [бархе] аз онон дар ҳаққи хеш ситамгоранд ва [бархе дигар] аз онон миёнарав ҳастанд ва [бархе] аз онон ба фармони Аллоҳ таоло дар некиҳо пештозанд. Ин [мусулмонӣ ва баҳрамандӣ аз Қуръон, фазилат ва] бахшоиши бузург аст');
INSERT INTO "pioneers" VALUES(35,33,'35:33','[Подошашон] боғҳои ҷовидон [-и биҳиштӣ] аст, ки ба он ворид мешаванд. Дар он ҷо ба дастбандҳое аз тилло ва марворид ороста мегарданд ва либосашон аз ҳарир аст');
INSERT INTO "pioneers" VALUES(35,34,'35:34','[Онон пас аз вуруд] мегӯянд: «Сипос [ва ситоиш] аз они Аллоҳ таоло аст, ки андӯҳро аз мо дур кард. Ба ростӣ, ки Парвардигорамон омурзандаи сипосгузор аст');
INSERT INTO "pioneers" VALUES(35,35,'35:35','Он [маъбуде], ки аз фазли хеш моро дар ин сарои ҷовид ҷой дод; дар он ҷо ранҷе ба мо намерасад ва дучори хастагӣ [ва касолат] намешавем');
INSERT INTO "pioneers" VALUES(35,36,'35:36','Ва барои касоне, ки куфр варзидаанд оташи дузах [дар пеш] аст. На фармон [-и марг] бар онон ҷорӣ мешавад, то бимиранд ва на [чизе] аз азобаш бар онон коста мегардад. Мо ҳар носипосеро чунин ҷазо медиҳем');
INSERT INTO "pioneers" VALUES(35,37,'35:37','[Дузахиён] Дар он [оташ] фарёд мезананд: «Парвардигоро, моро [аз ин азоб] берун овар, то ғайр аз он чи [пештар] мекардем, кори шоистае анҷом диҳем». [Ба онон гуфта мешавад] «Оё ба шумо [он қадар фурсат ва] умр надодем, то ҳар ки пандпазир аст, дар он [муддат] панд гирад? Ва [паёмбар низ ба унвони] бимдиҳанда наздатон омад; пас, [ҳоло ки итоат накардед, таъми азобро] бичашед, ки ситамгорон ҳеҷ ёригаре надоранд');
INSERT INTO "pioneers" VALUES(35,38,'35:38','Бе тардид, Аллоҳ таоло донои ниҳони осмонҳо ва замин аст [ва] Ӯст, ки ба рози дилҳо доност');
INSERT INTO "pioneers" VALUES(35,39,'35:39','Ӯст, ки шуморо дар замин ҷонишин [-и пешиниён] қарор дод; пас, ҳар ки куфр биварзад, куфраш ба зиёни худи ӯст; ва кофиронро куфрашон назди Парвардгорашон ҷуз хашм [ва безорӣ] намеафзояд; ва кофиронро куфрашон ҷуз зиён [ва гуноҳ, чизе] намеафзояд');
INSERT INTO "pioneers" VALUES(35,40,'35:40','[Эй паёмбар, ба мушрикон] бигӯ: «Он маъбудоне, ки ба ҷойи Аллоҳ таоло мехонед, нишонам диҳед, чи чизе аз заминро офаридаанд? Ё [ҳамроҳи Аллоҳ таоло] дар офариниши осмонҳо ширкат доштаанд? Ё ба онон [мушрикон] китобе додаем ва аз [матолиби] он нишонаи равшане [бар ҳаққонияти худ] доранд?» [Чунин нест] Балки ситамгорон [-и мушрик] ҷуз фиреб, ба якдигар ваъдае намедиҳанд');
INSERT INTO "pioneers" VALUES(35,41,'35:41','Ба ростӣ, Аллоҳ таоло, ки осмонҳо ва заминро нигаҳ медорад, то [аз масиру мадори худ] мунҳариф нашаванд ва агар инҳироф ёбанд, пас, аз Ӯ касе онҳоро нигаҳ намедорад. Бе тардид, Аллоҳ таоло ҳамвора бурдбори омурзанда аст');
INSERT INTO "pioneers" VALUES(35,42,'35:42','[Кофирон] Бо сахттарин савгандҳояшон ба Аллоҳ таоло савганд ёд карданд, ки агар [паёмбари] бимдиҳандае бар эшон биёяд, аз ҳар уммате роҳёфтатаранд; аммо чун [Муҳаммад ба унвони] бимдиҳандае наздашон омад, ҷуз дурӣ [ва нафрат аз ҳақ чизе] бар онон наафзуд');
INSERT INTO "pioneers" VALUES(35,43,'35:43','[Савгандҳояшон содиқона набуд; балки] Аз рӯйи гарданкашӣ дар замин ва найранги зишт [буд] ва найранги зишт, ҷуз домангири соҳибаш намегардад. Пас, оё [кофирон чизе] ҷуз суннати [илоҳӣ ва кайфари] пешиниёнро интизор доранд? Пас, ҳаргиз дар суннати Аллоҳ таоло табдиле намеёбӣ ва ҳаргиз дар суннати Аллоҳ таоло тағйире намеёбӣ');
INSERT INTO "pioneers" VALUES(35,44,'35:44','Оё [кофирон] дар замин гардиш накардаанд, то бубинанд саранҷоми касоне, ки пеш аз онон ва нерумандтар аз онон буданд, чи гуна шуд? Ҳеҷ чизе дар осмонҳо ва замин нест, ки Аллоҳро нотавон созад [ва аз ҳавзаи қудраташ берун равад]. Бе тардид, Ӯ ҳамвора донову тавоност');
INSERT INTO "pioneers" VALUES(35,45,'35:45','Ва агар Аллоҳ таоло мардумро ба сабаби он чи кардаанд, муҷозот мекард, ҳеҷ ҷунбандаеро бар пушти замин боқӣ намегузошт, аммо [муҳлат медиҳад ва кайфари] ононро то саромади муайяне ба таъхир меандозад; пас, ҳангоме ки аҷалашон [дар қиёмат] фаро расад, яқинан, Аллоҳ таоло ба ҳоли бандагонаш биност.');
INSERT INTO "pioneers" VALUES(36,1,'36:1','Ё, син');
INSERT INTO "pioneers" VALUES(36,2,'36:2','Савганд ба Қуръони ҳаким');
INSERT INTO "pioneers" VALUES(36,3,'36:3','Бе тардид, ту аз паёмбарон [-и Мо] ҳастӣ');
INSERT INTO "pioneers" VALUES(36,4,'36:4','[Ва] Бар роҳи рост [Ислом қарор дорӣ]');
INSERT INTO "pioneers" VALUES(36,5,'36:5','[Ин Қуръон] Фуруфиристодаи [Парвардигори] шикастнопазири меҳрубон аст');
INSERT INTO "pioneers" VALUES(36,6,'36:6','То мардумеро [дар бораи азоби қиёмат] бим диҳӣ, ки падаронашон ҳушдор наёфтаанд ва [бад-ин сабаб] онҳо низ ғофиланд');
INSERT INTO "pioneers" VALUES(36,7,'36:7','Яқинан, фармон [-и азоби илоҳӣ] бар бештарашон таҳаққуқ ёфтааст; пас, [лаҷоҷат мекунанд ва] имон намеоваранд');
INSERT INTO "pioneers" VALUES(36,8,'36:8','[Мисоли гумроҳии кофирон, чунон аст, ки гӯӣ] Мо бар гарданҳояшон занҷирҳое ниҳодаем, ки то зери манаҳояшон [печидашуда] аст, чунонки сарҳояшон боло мондааст [ва аз анҷоми ҳар хайре оҷизанд ва роҳи ҳидоятро намеёбанд]');
INSERT INTO "pioneers" VALUES(36,9,'36:9','Ва аз пеши рӯяшон деворе ва аз пушти сарашон низ деворе ниҳодаем ва чашмонашонро бо пардае пӯшонидаем; аз ин рӯст, ки [ҳақиқатро] намебинанд');
INSERT INTO "pioneers" VALUES(36,10,'36:10','[Эй паёмбар] барояшон яксон аст, ки ононро бим диҳӣ ё надиҳӣ; [дар ҳар ҳол] имон намеоваранд');
INSERT INTO "pioneers" VALUES(36,11,'36:11','Ҷуз ин нест, ки ту ба касе бим медиҳӣ [ва ин кор барои касе суд дорад], ки пайрави Қуръон бошад ва дар ниҳон аз [Парвардигори] Раҳмон битарсад; пас, Ӯро [ки чунин аст] ба омурзиш ва подоши арҷманд башорат бидеҳ');
INSERT INTO "pioneers" VALUES(36,12,'36:12','Яқинан, Моем, ки мурдагонро зинда мекунем ва он чиро, ки аз пеш фиристодаанд ва осори [боқимонда аз] эшонро менависем; ва ҳама чизро дар Китоби равшан [Лавҳ-ул-маҳфуз] ба шумор овардаем');
INSERT INTO "pioneers" VALUES(36,13,'36:13','[Эй паёмбар] барояшон [моҷарои] мардуми он шаҳрро мисол бизан, он гоҳ ки паёмбарон наздашон рафтанд');
INSERT INTO "pioneers" VALUES(36,14,'36:14','Ва вақте ду нафар аз паёмбаронро ба сӯяшон фиристодем; аммо [мардум] ононро дурӯғгӯ ангоштанд; пас, бо фиристодани паёмбари савум он дуро [таъйид ва] тақвият кардем. Онон [ба аҳолии шаҳр] мегуфтанд: «Мо фиристодагон [-и илоҳӣ] ба сӯи шумо ҳастем»');
INSERT INTO "pioneers" VALUES(36,15,'36:15','[Онон дар посух] гуфтанд: «Шумо ҷуз башаре ҳаммонанди мо нестед ва [Парвардигори] Раҳмон чизе [аз ваҳй] нозил накардааст. Шумо ҷуз дурӯғ намегӯед»');
INSERT INTO "pioneers" VALUES(36,16,'36:16','[Паёмбарон] гуфтанд: «Парвардигорамон медонад, ки қатъан, ба сӯйи шумо фиристода шудаем');
INSERT INTO "pioneers" VALUES(36,17,'36:17','Ва мо вазифае ҷуз расонидани ошкор [-и ваҳй] надорем»');
INSERT INTO "pioneers" VALUES(36,18,'36:18','[Мардум] Гуфтанд: «Мо шуморо ба фоли бад гирифтаем. Агар [аз таблиғ] даст барнадоред, ҳатман, сангсоратон мекунем ва бе тардид, азоби дардноке аз [ҷониби] мо ба шумо хоҳад расид»');
INSERT INTO "pioneers" VALUES(36,19,'36:19','[Паёмбарон] Гуфтанд: «Шумии шумо бо худатон [ва ношӣ аз куфру гуноҳ] аст. Оё агар пандатон диҳанд, [онро шум мепиндоред?] балки [ҳақиқат ин аст, ки шумо дар иртикоби гуноҳ] мардуми исрофкоред»');
INSERT INTO "pioneers" VALUES(36,20,'36:20','Ва марде аз дуртарин нуқтаи шаҳр шитобон омад [ва] гуфт: «Эй қавми ман, аз ин паёмбарон пайравӣ кунед»');
INSERT INTO "pioneers" VALUES(36,21,'36:21','Пайрави касоне бошед, ки аз шумо подоше намехоҳанд ва худ ҳидоят ёфтаанд');
INSERT INTO "pioneers" VALUES(36,22,'36:22','Ва чаро маъбудеро напарастам, ки маро офаридааст ва [шумо низ] ба сӯяш бозгардонда мешавед?');
INSERT INTO "pioneers" VALUES(36,23,'36:23','Оё ба ҷои Ӯ маъбудоне баргузинам, ки агар [Парвардигори] Раҳмон бихоҳад, газанде ба ман бирасонад, на шафоаташон бароям суде дорад ва на метавонанд [аз азоби илоҳӣ] наҷотам диҳанд?');
INSERT INTO "pioneers" VALUES(36,24,'36:24','Дар он сурат, яқинан, дар гумроҳии ошкоре хоҳам буд');
INSERT INTO "pioneers" VALUES(36,25,'36:25','Ман ба Парвардигоратон имон овардаам [ва аз марг ҳаросе надорам] пас, суханонамро бишунавед');
INSERT INTO "pioneers" VALUES(36,26,'36:26','[Пас, чун он мард ба дасти қавмаш ба шаҳодат расид] Ба вай гуфта шуд: «Вориди биҳишт шав». Ӯ гуфт: «Эй кош, қавми ман медонистанд,');
INSERT INTO "pioneers" VALUES(36,27,'36:27','ки Парвардигорам маро омурзида ва гиромӣ доштааст»');
INSERT INTO "pioneers" VALUES(36,28,'36:28','Пас аз [шаҳодати] ӯ бар қавмаш ҳеҷ сипоҳе аз осмон нафиристодем ва [пеш аз ин низ] фуру фиристанда набудем');
INSERT INTO "pioneers" VALUES(36,29,'36:29','[Кайфарашон] Ҷуз як бонги маргбор набуд; пас, ба ногоҳ ҳамагӣ [мурда ва] хомӯш гаштанд');
INSERT INTO "pioneers" VALUES(36,30,'36:30','Эй афсӯс, бар ин бандагон! Ҳеҷ паёмбаре ба сӯяшон наёмад, магар он ки масхарааш мекарданд');
INSERT INTO "pioneers" VALUES(36,31,'36:31','Оё надиданд [ва намедонанд] ки қабл аз онон чи бисёр наслҳоро ҳалок кардаем, ки дигар назди инҳо [ба дунё] бознамегарданд?');
INSERT INTO "pioneers" VALUES(36,32,'36:32','Ва [рӯзи қиёмат] ҳеҷ кас нест, магар он ки ҳамагӣ назди Мо эҳзор мешаванд');
INSERT INTO "pioneers" VALUES(36,33,'36:33','Барои онон [ки қиёматро бовар надоранд] замини хушк нишонае [ибратомӯз] аст: Мо он [замин]-ро зинда кардем ва аз [хоки] он донаҳое рӯёнидем, ки [махлуқоти илоҳӣ] аз он мехӯранд');
INSERT INTO "pioneers" VALUES(36,34,'36:34','Ва дар он [замин] боғҳое аз нахл ва ангур падид овардем ва дар он чашмаҳо равон сохтем');
INSERT INTO "pioneers" VALUES(36,35,'36:35','То мардум аз мевааш бихӯранд, дар ҳоле ки ин [маҳсулот, ҳосили талоши онҳо нест ва онҳо]-ро бо дастони худ амал наовардаанд. Пас, оё сипос намегузоранд?');
INSERT INTO "pioneers" VALUES(36,36,'36:36','Пок ва муназзаҳ аст, он [Парвардигоре] ки ҳамаи нару модаҳоро офарида аст; аз он чи замин мерӯёнад [гиёҳону дарахтон] ва аз худашон [инсонҳо] ва аз он чи намедонанд');
INSERT INTO "pioneers" VALUES(36,37,'36:37','Ва шаб барои онон нишонае [дигар аз қудрати илоҳӣ] аст, [ки] рӯзро аз он барканор мекунем ва ногаҳон [ҳама] дар торикӣ фуру мераванд');
INSERT INTO "pioneers" VALUES(36,38,'36:38','Ва хуршед [низ пайваста] ба сӯйи қароргоҳаш дар ҳаракат аст. Ин тақдири [Парвардигори] шикастнопазиру доност');
INSERT INTO "pioneers" VALUES(36,39,'36:39','Ва барои моҳ [низ] манзилгоҳҳое муқаррар намудаем, то [саранҷом аз қурси комил ба ҳилол ва] ба шакли шохаи хушки хурмо бозгардад');
INSERT INTO "pioneers" VALUES(36,40,'36:40','На хуршедро сазовор аст, ки [дар сайри худ] ба моҳ бирасад в на шаб бар рӯз пешӣ мегирад; ва ҳар як аз [ситораву сайёра] дар мадоре шиноваранд');
INSERT INTO "pioneers" VALUES(36,41,'36:41','Нишонае [дигар аз қудрати Парвардигор] барои онон ин аст, ки падаронашонро дар киштӣ анбошта [аз сарнишин ва бор] ҳамл кардем');
INSERT INTO "pioneers" VALUES(36,42,'36:42','Ҳамчунин, барояшон чизе ҳаммонанди он [киштӣ] офаридаем, ки савораш шаванд');
INSERT INTO "pioneers" VALUES(36,43,'36:43','Агар бихоҳем, ғарқашон мекунем; дар ин сурат, на фарёдрасе доранд ва на [аз тақдири илоҳӣ] раҳоӣ меёбанд');
INSERT INTO "pioneers" VALUES(36,44,'36:44','Магар ин ки раҳмате аз ҷониби Мо [бинанд] ва баҳрамандӣ [аз неъматҳои дунё] то замоне [муайян шомили ҳолашон шавад]');
INSERT INTO "pioneers" VALUES(36,45,'36:45','Ва чун ба онон [мушрикон] гуфта мешавад: «Аз он чи пешорӯи шумост [азоби охират] ва он чи пушти сар мегузоред [сахтиҳои дунё] парво кунед; бошад, ки мавриди раҳмат қарор гиред», [итоат намекунанд]');
INSERT INTO "pioneers" VALUES(36,46,'36:46','Ҳеҷ нишонае аз нишонаҳои [қудрат ва бузургии] Парвардигорашон бар онон арза намегардад, магар он ки аз он рӯй мегардонанд');
INSERT INTO "pioneers" VALUES(36,47,'36:47','Ва чун ба онон гуфта мешавад: «Аз он чи Аллоҳ таоло рӯзиятон кардааст, инфоқ кунед», касоне, ки куфр варзидаанд, ба онон, ки имон овардаанд мегӯянд: «Оё ба касе ғизо бидиҳем, ки агар Аллоҳ таоло мехост, [Худ] ба ӯ ғизо медод? Шумо ҷуз дар гумроҳии ошкор нестед»');
INSERT INTO "pioneers" VALUES(36,48,'36:48','Ва [бо тамасхур ба муъминон] мегӯянд: «Агар ростгӯед, ин ваъда [қиёмат] кай хоҳад буд»?');
INSERT INTO "pioneers" VALUES(36,49,'36:49','[Ин мункирони қиёмат] ҷуз дар интизори як бонги маргбор нестанд, ки ногаҳон, дар ҳоле ки саргарми ҷидол [дар умури дунё] ҳастанд, ононро фаро гирад');
INSERT INTO "pioneers" VALUES(36,50,'36:50','Пас, [дар он ҳангом] наметавонанд [ба касе] васият кунанд ва на ба сӯи хонаводаи худ бозгарданд');
INSERT INTO "pioneers" VALUES(36,51,'36:51','Ва [ҳангоме ки барои дувумин бор] дар сур дамида шавад ва ногаҳон аз қабрҳояшон [берун меоянд ва] ба сӯи Парвардигорашон мешитобанд');
INSERT INTO "pioneers" VALUES(36,52,'36:52','[Ва бо нобоварӣ] Мегӯянд: «Вой бар мо! Чи касе моро аз қабрҳоямон барангехт?» [Ба онон гуфта мешавад] «Ин ҳамон [рӯзе] аст, ки [Парвардигори] Раҳмон ваъда дода буд; ва паёмбарон [дар ин бора] рост мегуфтанд');
INSERT INTO "pioneers" VALUES(36,53,'36:53','[Барангехтани мурдагон аз гӯр] Ҷуз як бонги ҳавлнок [дувумин дамидани сур] нест ва ногаҳон ҳамаро назди Мо ҳозир мекунанд');
INSERT INTO "pioneers" VALUES(36,54,'36:54','Пас, [бидонед, ки] имрӯз, ба ҳеҷ кас ситаме намешавад ва ҷуз дар баробари он чи [дар дунё] кардаед, ҷазо намебинед');
INSERT INTO "pioneers" VALUES(36,55,'36:55','Бе тардид, аҳли биҳишт имрӯз бо шодмонӣ саргарм [-и неъматҳои илоҳӣ] ҳастанд');
INSERT INTO "pioneers" VALUES(36,56,'36:56','Онон ва ҳамсаронашон дар сояҳо [-и густурдаи биҳиштӣ] бар тахтҳо такя задаанд');
INSERT INTO "pioneers" VALUES(36,57,'36:57','Дар он ҷо [ҳар гуна] мева ва ҳар чи бихоҳанд, барояшон [фароҳам] ҳаст');
INSERT INTO "pioneers" VALUES(36,58,'36:58','[Бартар аз ин неъматҳо] саломест, ки сухани Парвардигори меҳрубон аст');
INSERT INTO "pioneers" VALUES(36,59,'36:59','[Он гоҳ ба мушрикон хитоб мешавад] «Эй гунаҳкорон, имрӯз [аз муъминон] ҷудо шавед');
INSERT INTO "pioneers" VALUES(36,60,'36:60','Эй фарзандони Одам, оё бо шумо паймон набастам [ва фармон надодам], ки шайтонро напарастед, [ки] қатъан ӯ душмани ошкори шумост?');
INSERT INTO "pioneers" VALUES(36,61,'36:61','Ва ин ки [танҳо] Маро бипарастед, [ки] роҳи рост ҳамин аст?');
INSERT INTO "pioneers" VALUES(36,62,'36:62','Ва бе тардид, шайтон бисёре аз шуморо гумроҳ сохт. Оё андеша намекардед?');
INSERT INTO "pioneers" VALUES(36,63,'36:63','Ин [ҳамон] дузахест, ки ба шумо ваъда дода мешуд');
INSERT INTO "pioneers" VALUES(36,64,'36:64','Имрӯз ба сазои он ки куфр меварзидед, ба он [оташ] ворид шавед»');
INSERT INTO "pioneers" VALUES(36,65,'36:65','Имрӯз бар даҳонҳояшон муҳр мениҳем ва дастҳояшон бо Мо сухан мегӯянд ва пойҳояшон ба он чи мекарданд, гувоҳӣ медиҳанд');
INSERT INTO "pioneers" VALUES(36,66,'36:66','Ва агар бихоҳем, бе тардид, чашмонашонро маҳв мекунем, пас, [бо чашми баста] ба сӯйи [пули] сирот [аз якдигар] пешӣ мегиранд [то ба биҳишт роҳ ёбанд]; аммо магар куҷоро мебинанд?');
INSERT INTO "pioneers" VALUES(36,67,'36:67','Ва агар бихоҳем [зоҳирашонро тағйир медиҳем ва чунон] ононро дар ҷои худ масх [ва замингир] мекунем, ки натавонанд гоме ба пеш бигузоранд ё ба ақиб бозгарданд');
INSERT INTO "pioneers" VALUES(36,68,'36:68','Ва Мо ба ҳар ки умри тӯлонӣ бидиҳем, дар офариниш дигаргунаш мекунем [ва ӯро ба нотавонии зеҳнӣ ва бадани даврони кӯдакӣ бармегардонем]. Пас, оё [дар бораи қудрати Парвардигор] намеандешанд?');
INSERT INTO "pioneers" VALUES(36,69,'36:69','Ва Мо ба ӯ [Муҳаммад] шеър наомӯхтаем ва [шоирӣ] шоистаи ӯ нест. Ин [ки омӯхтаем, чизе] нест, магар андарз ва Қуръоне равшан');
INSERT INTO "pioneers" VALUES(36,70,'36:70','То ҳар киро зинда [-дил] бошад, бим диҳад ва азоб бар кофирон муҳаққақ гардад');
INSERT INTO "pioneers" VALUES(36,71,'36:71','Оё надидаанд, ки Мо аз он чи дастҳоямон сохтааст, чаҳорпоёне барояшон офаридаем, ки молики онҳо ҳастанд?');
INSERT INTO "pioneers" VALUES(36,72,'36:72','Ва онҳоро дар хидмати эшон гузоштаем, то бар бархе савор шаванд ва аз [гӯшти] бархе тағзия кунанд');
INSERT INTO "pioneers" VALUES(36,73,'36:73','Ва аз он [ҳайвонот] баҳраҳои дигар ва нӯшиданиҳое [шир] доранд. Пас, оё сипос намегузоранд?');
INSERT INTO "pioneers" VALUES(36,74,'36:74','[Бо ин ҳама неъмат мушрикон] ба ҷои Аллоҳ таоло маъбудоне баргузидаанд; ба ин умед, ки [аз сӯйи онҳо] ёрӣ шаванд');
INSERT INTO "pioneers" VALUES(36,75,'36:75','Он [маъбудҳои беҷон] қодир ба ёриашон нестанд; ва ин [мушрикон дар қиёмат] лашкаре барои онҳо [бутҳо] хоҳанд буд, ки [ҳамагӣ дар оташи дузах] эҳзор мешаванд');
INSERT INTO "pioneers" VALUES(36,76,'36:76','[Эй Паёмбар] сухани онон туро ғамгин насозад. Бе тардид, Мо он чиро пинҳон мекунанд ва ошкор месозанд, [ба хубӣ] медонем');
INSERT INTO "pioneers" VALUES(36,77,'36:77','Оё инсон надидааст, ки Мо ӯро аз нутфае офаридаем [он гоҳ мароҳили рушдро тай кард ва] сипас душмане ошкор шуд?');
INSERT INTO "pioneers" VALUES(36,78,'36:78','Ва барои Мо мисоле зад ва офариниши хешро фаромӯш кард [ва] гуфт: «Чи касе ин устухонҳоро [дубора] зинда мекунад, дар ҳоле ки пӯсидааст?»');
INSERT INTO "pioneers" VALUES(36,79,'36:79','Бигӯ: «ҳамон касе зиндааш мекунад, ки нахустин бор онро падид овард ва Ӯ ба ҳар офаринише доност');
INSERT INTO "pioneers" VALUES(36,80,'36:80','Ҳамон ки аз дарахти сабз оташе бароятон падид овард ва шумо [дар сурати ниёз] худ аз он [дарахт] оташ меафрӯзед»');
INSERT INTO "pioneers" VALUES(36,81,'36:81','Оё касе, ки осмонҳо ва заминро офаридааст, наметавонад ҳаммонанди инҳоро [пас аз маргашон дубора] биофаринад? Оре [метавонад] ва Ӯ Офаринандаи доност');
INSERT INTO "pioneers" VALUES(36,82,'36:82','Фармони Ӯ чун чизеро ирода кунад, танҳо ин аст, ки ба он мегӯяд: «Мавҷуд шав»; пас, [бедиранг] мавҷуд мешавад');
INSERT INTO "pioneers" VALUES(36,83,'36:83','Пас, пок ва муназзаҳ аст Парвардигоре, ки [ҳокимият ва] ихтиёри ҳама чиз ба дасти Ӯст ва [ҳамагӣ] ба сӯи Ӯ бозгардонида мешавед');
INSERT INTO "pioneers" VALUES(37,1,'37:1','Савганд ба фариштагони саф ба саф [ки барои парастиши Парвардигор истодаанд]');
INSERT INTO "pioneers" VALUES(37,2,'37:2','Ва савганд ба фариштагоне, ки абрҳоро меронанд');
INSERT INTO "pioneers" VALUES(37,3,'37:3','Ва савганд ба фариштагоне, ки Қуръон тиловат мекунанд');
INSERT INTO "pioneers" VALUES(37,4,'37:4','[ки] Бе тардид, маъбуди шумо яктост');
INSERT INTO "pioneers" VALUES(37,5,'37:5','[Ҳамон] Парвардигори осмонҳову замин ва он чи миёни онҳост ва Парвардигори [хуршед дар ғурубгоҳҳо ва] тулуъгоҳҳо');
INSERT INTO "pioneers" VALUES(37,6,'37:6','Ба ростӣ, ки Мо осмони дунёро бо зевари ситорагон оростем');
INSERT INTO "pioneers" VALUES(37,7,'37:7','Ва онро аз [дастрасии] ҳар шайтони саркаш ҳифз кардем');
INSERT INTO "pioneers" VALUES(37,8,'37:8','[Он шаётин ҳаргиз] наметавонанд [суханони фариштагони] олами болоро бишунаванд ва аз ҳар сӯ [бо шаҳобҳои оташин] тирборон мешаванд');
INSERT INTO "pioneers" VALUES(37,9,'37:9','[Аз вуруди онон ба олами фариштагон ҷилавгирӣ мешавад ва ҳамвора ба] ақиб ронда мешаванд ва азоби ҷовидон [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(37,10,'37:10','Магар касе, ки ногаҳон чизе [аз гуфтугӯи фариштагон]-ро дуздида гӯш диҳад, ки [дар ин сурат] шаҳоби дурахшон ӯро дунбол мекунад');
INSERT INTO "pioneers" VALUES(37,11,'37:11','Аз онон [кофирон] бипурс, ки оё офариниши [пайкарҳои] онон сахттар аст ё он чи ки Мо [аз замину осмонҳо ва ҷаҳони бекарон] офаридаем; [дар ҳоле ки] Мо онҳоро аз гили [беарзиш ва] часпида падид овардем');
INSERT INTO "pioneers" VALUES(37,12,'37:12','Ту [аз инкори онон] дар шигифтӣ ва онҳо [низ суханонатро дар бораи қиёмат] масхара мекунанд');
INSERT INTO "pioneers" VALUES(37,13,'37:13','Ва чун панд дода шаванд, намепазиранд');
INSERT INTO "pioneers" VALUES(37,14,'37:14','Ва чун нишонаҳои [дар мавриди ростгӯии Паёмбар] мебинанд, [онро] ба тамасхур мегиранд');
INSERT INTO "pioneers" VALUES(37,15,'37:15','Ва мегӯянд: «Ин [Қуръон чизе] нест, магар ҷодуе ошкор');
INSERT INTO "pioneers" VALUES(37,16,'37:16','Оё ҳангоме ки мурдем ва хок ва устухон шудем, барангехта хоҳем шуд?');
INSERT INTO "pioneers" VALUES(37,17,'37:17','Оё падарони мо [низ барангехта мешаванд]?»');
INSERT INTO "pioneers" VALUES(37,18,'37:18','Бигӯ: «Оре, шумо, дар ҳоле ки хор ҳастед [зинда мешавед]»');
INSERT INTO "pioneers" VALUES(37,19,'37:19','Пас, [вуқуи қиёмат] танҳо бо як бонг [-и саҳмнок] аст ва ногаҳон [ҳама аз қабрҳо берун меоянд ва дар интизори дастури Парвардигор, ваҳшати қиёматро] назорат мекунанд');
INSERT INTO "pioneers" VALUES(37,20,'37:20','Ва мегӯянд: «Вой бар мо! Ин [ҳамон] рӯзи ҷазост»');
INSERT INTO "pioneers" VALUES(37,21,'37:21','[Гуфта мешавад: Оре] ин ҳамон рӯзи довариест, ки дурӯғаш меангоштед»');
INSERT INTO "pioneers" VALUES(37,22,'37:22','[Ба фариштагон гуфта мешавад] «Касонеро, ки [бо ширк ба хеш] ситам карданд,ё бо [ҳамфикрон ва] ҳаммонандҳояшон');
INSERT INTO "pioneers" VALUES(37,23,'37:23','ва он чи ба ҷои Аллоҳ таоло мепарастиданд, гирд оваред ва ба роҳи дузах ҳидояташон кунед');
INSERT INTO "pioneers" VALUES(37,24,'37:24','Ва ононро нигаҳ доред, ки, ҳатман, [пеш аз рафтан ба дузах] бозхост хоҳанд шуд');
INSERT INTO "pioneers" VALUES(37,25,'37:25','[Аз дузахиён мепурсанд] «Чаро [монанди дунё] аз якдигар ҳимоят намекунед?»');
INSERT INTO "pioneers" VALUES(37,26,'37:26','Балки имрӯз [ҳамагӣ] таслим [-и амри Парвардигор] ҳастанд');
INSERT INTO "pioneers" VALUES(37,27,'37:27','Ва сарзанишкунон ба якдигар рӯ меоранд');
INSERT INTO "pioneers" VALUES(37,28,'37:28','[Ва ба роҳбарон ва бузургони худ] мегӯянд: «Шумо [барои ба куфр кашонондани мо бо иддаои дини ростин ва] аз ҷониби ҳақ ба суроғамон меомадед»');
INSERT INTO "pioneers" VALUES(37,29,'37:29','[Роҳбаронашон] Мегӯянд: «Мо муқассир нестем], балки шумо худ имон надоштед');
INSERT INTO "pioneers" VALUES(37,30,'37:30','Ва мо ҳеҷ тасаллуте бар шумо надоштем; балки шумо худ гурӯҳи саркаш будед');
INSERT INTO "pioneers" VALUES(37,31,'37:31','Пас, [акнун] фармони муҷозоти Парвардигорамон бар мо муҳаққақ шуда ва ба ростӣ, мо [ҳамагӣ азоби дузахро] чашандаем');
INSERT INTO "pioneers" VALUES(37,32,'37:32','Пас, шуморо гумроҳ намудем [чаро ки] дар ҳақиқат, худ низ гумроҳ будем»');
INSERT INTO "pioneers" VALUES(37,33,'37:33','Пас, яқинан он рӯз ҳамаи [онон] дар азоб шариканд');
INSERT INTO "pioneers" VALUES(37,34,'37:34','Бе тардид, Мо бо гунаҳкорон инчунин [рафтор] мекунем');
INSERT INTO "pioneers" VALUES(37,35,'37:35','Онон буданд, ки чун ба эшон гуфта мешуд: «Маъбуде [барҳақ] ҷуз Аллоҳ таоло нест» саркашӣ [ва такаббур] мекарданд');
INSERT INTO "pioneers" VALUES(37,36,'37:36','ва мегуфтанд: «Оё мо маъбудонамонро ба хотири [сухани] шоире девона раҳо кунем?»');
INSERT INTO "pioneers" VALUES(37,37,'37:37','[Чунин нест] балки ӯ ҳақ [тавҳид]- ро оварда ва паёмбарони [пешин] – ро тасдиқ кардааст');
INSERT INTO "pioneers" VALUES(37,38,'37:38','Шумо [кофирон], ҳатман, азоби дардноки [дузах]-ро мечашед');
INSERT INTO "pioneers" VALUES(37,39,'37:39','Ва ҷуз [дар баробари] он чи мекардед, муҷозот намешавед');
INSERT INTO "pioneers" VALUES(37,40,'37:40','Ҳама азоб мебинанд, магар бандагони мухлиси Аллоҳ таоло');
INSERT INTO "pioneers" VALUES(37,41,'37:41','Онон [дар пешгоҳи Парвардигор ризқу] рӯзии мушаххасе доранд');
INSERT INTO "pioneers" VALUES(37,42,'37:42','[Анвои] меваҳо [-ро дар ихтиёр хоҳанд дошт] ва гиромӣ дошта мешаванд');
INSERT INTO "pioneers" VALUES(37,43,'37:43','Дар боғҳои пурнеъмати [биҳишт]');
INSERT INTO "pioneers" VALUES(37,44,'37:44','Дар ҳоле ки муқобили якдигар бар тахтҳо [такя задаанд]');
INSERT INTO "pioneers" VALUES(37,45,'37:45','ҷоме аз шароби ноб [ва зулол] перомунашон мегардонанд');
INSERT INTO "pioneers" VALUES(37,46,'37:46','[Шаробе равшан ва] сафед, ки барои нӯшандагонашон лаззатбахш аст');
INSERT INTO "pioneers" VALUES(37,47,'37:47','[Шаробе, ки хуморӣ ва] сардардӣ дар нӯшиданаш нест ва аз он маст намегарданд');
INSERT INTO "pioneers" VALUES(37,48,'37:48','Ва наздашон занони дуруштчашм аст, ки [ танҳо ба шавҳарони худ] назар доранд');
INSERT INTO "pioneers" VALUES(37,49,'37:49','[Занон чунон сафедандоманд, ки] гӯё тухм [-и парандагон] ҳастанд [ва зери пар ва боли онҳо аз дастрасии ҳамагон] пинҳон мондаанд');
INSERT INTO "pioneers" VALUES(37,50,'37:50','[Биҳиштиён дар бораи рӯйдодҳои дунё суҳбат мекунанд ва] бархе аз онон пурсишкунон рӯ ба бархе дигар мекунанд');
INSERT INTO "pioneers" VALUES(37,51,'37:51','Яке аз онон мегӯяд: «Ман [дар дунё] ҳамнишине доштам');
INSERT INTO "pioneers" VALUES(37,52,'37:52','[Ӯ пайваста] Мегуфт: Оё ту воқеан [ба қиёмат] бовар дорӣ?');
INSERT INTO "pioneers" VALUES(37,53,'37:53','Оё ҳангоме ки мурдем ва хок ва устухон шудем, оё ҳақиқатан [зинда мешавем ва ба хотири корҳоямон] ба мо кайфар медиҳанд?»');
INSERT INTO "pioneers" VALUES(37,54,'37:54','[Сипас, ба дӯстони биҳиштиаш] мегӯяд: «Оё [шумо низ ҳамроҳи ман] сар мекашед [то бидонем ӯ куҷост]?»');
INSERT INTO "pioneers" VALUES(37,55,'37:55','Он гоҳ худи ӯ сар мекашад ва ӯро дар миёни дузах мебинад');
INSERT INTO "pioneers" VALUES(37,56,'37:56','Ба ӯ мегӯяд: «Ба Аллоҳ таоло савганд, ки наздик буд маро [бо васвасаҳои куфромезат] нобуд созӣ');
INSERT INTO "pioneers" VALUES(37,57,'37:57','Агар неъмати [ҳидояти] Парвардигорам набуд, ман низ аз ҳозиршудагон [дар дузах] будам»');
INSERT INTO "pioneers" VALUES(37,58,'37:58','[Он гоҳ ба биҳиштиён мегӯяд] «Оё мо дигар марге нахоҳем дошт');
INSERT INTO "pioneers" VALUES(37,59,'37:59','ҷуз марги нахустин, ва азоб намешавем?');
INSERT INTO "pioneers" VALUES(37,60,'37:60','Ба ростӣ, ки ин ҳамон комёбии бузург аст');
INSERT INTO "pioneers" VALUES(37,61,'37:61','Аҳли амал бояд барои чунин [подош] бикӯшанд');
INSERT INTO "pioneers" VALUES(37,62,'37:62','Оё ин [неъмати биҳишт, каримонатар ва] барои пазироӣ беҳтар аст ё дарахти зақум [дар дузах]?');
INSERT INTO "pioneers" VALUES(37,63,'37:63','Бе тардид, Мо он дарахтро мояи озмоиши ситамгорони [мушрик] додаем [чаро ки мегӯянд: «Ҳеҷ дарахте дар оташ намерӯяд»]');
INSERT INTO "pioneers" VALUES(37,64,'37:64','Дар ҳақиқат, он [мояи азоб] дарахте аст, ки аз жарфои дузах мерӯяд');
INSERT INTO "pioneers" VALUES(37,65,'37:65','Меваҳояш [чунон бадманзар аст, ки] гӯё сарҳои шаётин аст');
INSERT INTO "pioneers" VALUES(37,66,'37:66','Онон [дузахиён] аз [меваҳои талхи] он мехӯранд ва шикамҳоро аз онҳо анбошта мекунанд');
INSERT INTO "pioneers" VALUES(37,67,'37:67','Он гоҳ рӯи ин [ғизои нопок, ба ҷойи нӯшидани] омезаҳои аз оби ҷӯшон доранд');
INSERT INTO "pioneers" VALUES(37,68,'37:68','Сипас, яқинан, бозгашташон ба дузах аст');
INSERT INTO "pioneers" VALUES(37,69,'37:69','Онон падарони худро гумроҳ ёфтанд');
INSERT INTO "pioneers" VALUES(37,70,'37:70','Ва [ҳамчунон] дар пайи онон мешитофтанд');
INSERT INTO "pioneers" VALUES(37,71,'37:71','Ва, ҳатман, қабл аз онон низ бештари пешиниён гумроҳ шуданд');
INSERT INTO "pioneers" VALUES(37,72,'37:72','Ва бе тардид, Мо миёнашон ҳушдордиҳандагон [-и бисёр] фиристодем');
INSERT INTO "pioneers" VALUES(37,73,'37:73','Пас, бингар, ки саранҷоми ҳушдорёфтагон чи гуна буд');
INSERT INTO "pioneers" VALUES(37,74,'37:74','[Ҳама гирифтори азоб шуданд] магар бандагони мухлиси Аллоҳ таоло');
INSERT INTO "pioneers" VALUES(37,75,'37:75','Нуҳ ба даргоҳи Мо дуо кард ва [Мо] чи наку иҷобаткунанда будем!');
INSERT INTO "pioneers" VALUES(37,76,'37:76','Ва ӯ ва хонаводаашро [бо ҳамроҳи муъминон] аз балои бузурги [тӯфон] наҷот додем');
INSERT INTO "pioneers" VALUES(37,77,'37:77','Ва [пас аз тӯфон, фақат пайравони муъмин ва] хонаводаи ӯро [дар замин] боқӣ гузоштем');
INSERT INTO "pioneers" VALUES(37,78,'37:78','Ва барояш дар миёни ояндагон [номи нек] бар ҷой ниҳодем');
INSERT INTO "pioneers" VALUES(37,79,'37:79','Салом [ва ситоиши] ҷаҳониён бар Нуҳ бод');
INSERT INTO "pioneers" VALUES(37,80,'37:80','Ба ростӣ Мо накукоронро, инчунин, подош медиҳем');
INSERT INTO "pioneers" VALUES(37,81,'37:81','Бе тардид, ӯ аз бандагони муъмини Мо буд');
INSERT INTO "pioneers" VALUES(37,82,'37:82','Он гоҳ дигаронро [ки дар тӯфон монда буданд] ғарқ кардем');
INSERT INTO "pioneers" VALUES(37,83,'37:83','Ва, ҳатман, Иброҳим аз пайравони ӯ [Нуҳ] буд');
INSERT INTO "pioneers" VALUES(37,84,'37:84','[Ёд кун аз] ҳангоме ки вай бо дили пок [ва холӣ аз ширк] ба [даргоҳи] Парвардигораш омад');
INSERT INTO "pioneers" VALUES(37,85,'37:85','Он гоҳ ки ба падар ва қавмаш гуфт: « Чи чизе мепарастед?');
INSERT INTO "pioneers" VALUES(37,86,'37:86','Оё ба ҷойи Аллоҳ таоло ин маъбудони дурӯғинро мехоҳед?');
INSERT INTO "pioneers" VALUES(37,87,'37:87','Гумонатон дар бораи Парвардигори ҷаҳониён чист?»');
INSERT INTO "pioneers" VALUES(37,88,'37:88','Сипас нигоҳе ба ситорагон афканд [ва андешид]');
INSERT INTO "pioneers" VALUES(37,89,'37:89','Ва гуфт: «Ман беморам [ва дар ҷашн ширкат намекунам]»');
INSERT INTO "pioneers" VALUES(37,90,'37:90','Мардум ӯро тарк карданд ва рафтанд');
INSERT INTO "pioneers" VALUES(37,91,'37:91','Он гоҳ пинҳонӣ назди бутҳояшон рафт ва [бо тамасхур] гуфт: «Чизе намехӯред?');
INSERT INTO "pioneers" VALUES(37,92,'37:92','Шуморо чи шудааст, ки сухан намегӯед?»');
INSERT INTO "pioneers" VALUES(37,93,'37:93','Сипас бо дасти рости худ зарбаҳои [муҳкам] бар онҳо ворид овард [ва ҷуз бути бузург ҳамаро шикаст]');
INSERT INTO "pioneers" VALUES(37,94,'37:94','Пас, қавмаш шитофтанд ва ба сӯйи ӯ омаданд');
INSERT INTO "pioneers" VALUES(37,95,'37:95','[Иброҳим] гуфт: «Оё чизеро, ки худ метарошед, мепарастед?');
INSERT INTO "pioneers" VALUES(37,96,'37:96','Ва ҳол он ки Аллоҳ таоло [ҳам] шумо ва [ ҳам] он чиро анҷом медиҳед офаридааст');
INSERT INTO "pioneers" VALUES(37,97,'37:97','[Вақте бутпарастон аз посух дармонданд] Гуфтанд: «Барояш бинои [баланд] бисозед ва ӯро дар оташ биафканед»');
INSERT INTO "pioneers" VALUES(37,98,'37:98','Пас, барои [нобудии] ӯ найранг хостанд, [вале] Мо ононро паст ва [мағлуб] сохтем [ва оташ бар Иброҳим сард ва беасар шуд]');
INSERT INTO "pioneers" VALUES(37,99,'37:99','Ва гуфт: «Ман ба сӯи [сарзамини дигаре барои ибодати] Парвардигорам меравам ва Ӯ ҳидоятам хоҳад кард');
INSERT INTO "pioneers" VALUES(37,100,'37:100','Парвардигоро, ба ман [фарзанде] аз шоистагон ато кун»');
INSERT INTO "pioneers" VALUES(37,101,'37:101','Мо [дуояшро мустаҷоб кардем ва] ӯро ба таваллуди писари бурдборе башорат додем');
INSERT INTO "pioneers" VALUES(37,102,'37:102','Ва чун [Исмоил] ба [синни навҷавонӣ ва] ҳадди талош [ва ҳамкорӣ] бо ӯ [Иброҳим] расид, ба вай гуфт: Писарам, дар хоб [ба ман ваҳй мешавад ва] мебинам, ки туро забҳ мекунам. Назарат дар ин бора чист?» [Исмоил] гуфт: «Падарҷон, он чиро ки ба ту амр шудааст анҷом бидеҳ. Иншоаллоҳ маро [дар баробари ин фармон] шикебо хоҳӣ ёфт»');
INSERT INTO "pioneers" VALUES(37,103,'37:103','Пас, чун ҳар ду таслими [фармони Парвардигор] шуданд ва [Иброҳим] ӯро бо гуна [бар хок] ниҳод');
INSERT INTO "pioneers" VALUES(37,104,'37:104','Ба ӯ нидо додем, ки «Эй Иброҳим!');
INSERT INTO "pioneers" VALUES(37,105,'37:105','Яқинан хобатро таҳаққуқ бахшидӣ». Мо накукоронро чунин подош медиҳем');
INSERT INTO "pioneers" VALUES(37,106,'37:106','Бе тардид, ин [хоб] озмоише ошкор буд [ва Иброҳим аз он сарбаланд берун омад]');
INSERT INTO "pioneers" VALUES(37,107,'37:107','Ва [ҷони] ӯро дар баробари қурбонии бузурге бозхаридем');
INSERT INTO "pioneers" VALUES(37,108,'37:108','Ва барояш дар миёни ояндагон [номи нек] бар ҷой ниҳодем');
INSERT INTO "pioneers" VALUES(37,109,'37:109','Салом бар Иброҳим');
INSERT INTO "pioneers" VALUES(37,110,'37:110','Мо накукоронро чунин подош медиҳем');
INSERT INTO "pioneers" VALUES(37,111,'37:111','Бе тардид, ӯ аз бандагони муъмини Мо буд');
INSERT INTO "pioneers" VALUES(37,112,'37:112','Ва ӯро ба [таваллуди] Исҳоқ, ки паёмбаре аз шоистагон буд, башорат додем');
INSERT INTO "pioneers" VALUES(37,113,'37:113','[Мо] Ба ӯ ва Исҳоқ баракат додем, ва [бархе] аз афроди дудмонашон накукор буданд ва [бархе дигар] ошкоро нисбат ба хештан ситамгор буданд');
INSERT INTO "pioneers" VALUES(37,114,'37:114','Ва ба ростӣ, бар Мӯсо ва Ҳорун миннат ниҳодем');
INSERT INTO "pioneers" VALUES(37,115,'37:115','Ва он дуро ҳамроҳи қавмашон аз андуҳи бузург [ситами Фиръавн ва хатари ғарқ шудан дар Нил] наҷот додем');
INSERT INTO "pioneers" VALUES(37,116,'37:116','Ва ёриашон кардем, то пирӯз шуданд');
INSERT INTO "pioneers" VALUES(37,117,'37:117','Ва ба он ду китоби равшангар [Таврот] додем');
INSERT INTO "pioneers" VALUES(37,118,'37:118','Ва ба роҳи рост ҳидояташон кардем');
INSERT INTO "pioneers" VALUES(37,119,'37:119','Ва барояшон дар миёни ояндагон [номи нек] бар ҷой ниҳодем');
INSERT INTO "pioneers" VALUES(37,120,'37:120','Салом бар Мӯсо ва Ҳорун');
INSERT INTO "pioneers" VALUES(37,121,'37:121','Мо накукоронро чунин подош медиҳем');
INSERT INTO "pioneers" VALUES(37,122,'37:122','Бе тардид, он ду бандагони муъмини Мо буданд');
INSERT INTO "pioneers" VALUES(37,123,'37:123','Ва, ҳатман, Илёс [низ] яке аз паёмбарони [Мо] буд');
INSERT INTO "pioneers" VALUES(37,124,'37:124','[Ёд кун аз] Ҳангоме ки ӯ ба қавмаш гуфт: «Оё [аз Аллоҳ таоло] парво намекунед?');
INSERT INTO "pioneers" VALUES(37,125,'37:125','Оё [бути] «Баъл»-ро [ба парастиш] мехонед ва беҳтарин офарандаро раҳо мекунед?');
INSERT INTO "pioneers" VALUES(37,126,'37:126','Аллоҳ таоло [-ро тарк мекунед], ки Парвардигори шумо ва Парвардигори падаронатон аст»');
INSERT INTO "pioneers" VALUES(37,127,'37:127','Аммо ӯро дурӯғгӯ ангоштанд ва мусалламан, [барои азоб] ҳозир карда мешаванд');
INSERT INTO "pioneers" VALUES(37,128,'37:128','Магар бандагони мухлиси Аллоҳ таоло');
INSERT INTO "pioneers" VALUES(37,129,'37:129','Мо барояш дар миёни ояндагон [номи нек] бар ҷой ниҳодем');
INSERT INTO "pioneers" VALUES(37,130,'37:130','Салом бар Илёс');
INSERT INTO "pioneers" VALUES(37,131,'37:131','Ба ростӣ, Мо накукоронро чунин подош медиҳем');
INSERT INTO "pioneers" VALUES(37,132,'37:132','Бе тардид, ӯ аз бандагони муъмини Мо буд');
INSERT INTO "pioneers" VALUES(37,133,'37:133','Ва ҳатман, Лут [низ] яке аз паёмбарони [Мо] буд');
INSERT INTO "pioneers" VALUES(37,134,'37:134','[Ёд кун аз] ҳангоме ки ӯ ва ҳамаи хонаводаашро наҷот додем');
INSERT INTO "pioneers" VALUES(37,135,'37:135','Магар пиразане, ки аз боқимондагон [дар азоб] буд');
INSERT INTO "pioneers" VALUES(37,136,'37:136','Сипас дигаронро ҳалок кардем');
INSERT INTO "pioneers" VALUES(37,137,'37:137','Ва шумо [дар сафар ба сарзамини Шом] субҳгоҳон бар [вайронаҳои шаҳрҳои] онон мегузаред,');
INSERT INTO "pioneers" VALUES(37,138,'37:138','ва шабонгоҳ. Оё намеандешед [ва ибрат намегиред]?');
INSERT INTO "pioneers" VALUES(37,139,'37:139','Ва, ҳатман, Юнус [низ] яке аз паёмбарони [Мо] буд');
INSERT INTO "pioneers" VALUES(37,140,'37:140','[Ёд кун аз] ҳангоме ки [ хашмгин аз куфри қавмаш, бидуни иҷозати Парвардигор] ба киштии анбошта [аз мусофир ва бор] гурехт');
INSERT INTO "pioneers" VALUES(37,141,'37:141','Пас, [чун киштӣ дар ҳоли ғарқ шудан буд, Юнус ҳамроҳи мусофирон барои берун андохтани бархе афрод] қуръа андохтанд ва бозанда шуд [ва ӯро ба дарё андохтанд]');
INSERT INTO "pioneers" VALUES(37,142,'37:142','Сипас моҳии [бузурге] ӯро балъид [фурӯ бурд], дар ҳоле ки [худро ба хотири тарки вазифа] сарзаниш мекард');
INSERT INTO "pioneers" VALUES(37,143,'37:143','Пас, агар дар зумраи тасбеҳгӯёни [Парвардигор] набуд,');
INSERT INTO "pioneers" VALUES(37,144,'37:144','Яқинан, то рӯзе, ки [мардум] барангехта мешаванд, дар шиками он [моҳӣ] боқӣ мемонд');
INSERT INTO "pioneers" VALUES(37,145,'37:145','Он гоҳ ӯро дар ҳоле ки [заъиф ва] бемор буд ба сарзамини хушк [ва бегиёҳ] афкандем');
INSERT INTO "pioneers" VALUES(37,146,'37:146','Ва бар [сари] ӯ бутаи каду рӯёндем [то аз соя ва меваҳояш баҳра гирад]');
INSERT INTO "pioneers" VALUES(37,147,'37:147','Ва [дубора] ӯро ба сӯйи сад ҳазор нафар ё бештар [ба рисолат] фиристодем');
INSERT INTO "pioneers" VALUES(37,148,'37:148','Пас, онон имон оварданд ва Мо низ то муддате [аз неъматҳои зиндагӣ] баҳрамандашон сохтем');
INSERT INTO "pioneers" VALUES(37,149,'37:149','Пас, аз эшон [мушрикон] бипурс, оё духтарҳо барои Парвардигори ту ва писарҳо барои онҳост?');
INSERT INTO "pioneers" VALUES(37,150,'37:150','Оё Мо фариштагонро духтар офаридем ва онҳо ҳузур доштанд?');
INSERT INTO "pioneers" VALUES(37,151,'37:151','Огоҳ бош! Онон аз [рӯи туҳмат ва] дурӯғгӯӣ');
INSERT INTO "pioneers" VALUES(37,152,'37:152','мегӯянд: «Аллоҳ таоло фарзанд дорад» ва, яқинан, дурӯғ мегӯянд');
INSERT INTO "pioneers" VALUES(37,153,'37:153','Оё [Аллоҳ таоло] духтаронро бар писарон тарҷеҳ додааст?');
INSERT INTO "pioneers" VALUES(37,154,'37:154','Шуморо чи шудааст? Чи гуна ҳукм мекунед?');
INSERT INTO "pioneers" VALUES(37,155,'37:155','Оё [намеандешед ва] панд намегиред?');
INSERT INTO "pioneers" VALUES(37,156,'37:156','Оё [бар иддаои ботили хеш] далели равшане доред?');
INSERT INTO "pioneers" VALUES(37,157,'37:157','Пас, агар ростгӯед, китобатонро биёваред');
INSERT INTO "pioneers" VALUES(37,158,'37:158','[Мушрикон] байни Ӯ [Аллоҳ таоло] ва фариштагон, хешовандӣ қоил шудаанд; дар ҳоле ки фариштагон ба яқин медонанд, ки [мушрикон рӯзи қиёмат барои ҳисобрасӣ] эҳзор мешаванд');
INSERT INTO "pioneers" VALUES(37,159,'37:159','Аллоҳ таоло аз он чи [мушрикон] тавсиф мекунанд, муназзаҳ аст');
INSERT INTO "pioneers" VALUES(37,160,'37:160','[Мардум дар тавсифи Парвардигор, сухани газоф мегӯянд] магар бандагони мухлиси Аллоҳ таоло');
INSERT INTO "pioneers" VALUES(37,161,'37:161','Пас, шумо [мушрикон] ва он чи мепарастед,');
INSERT INTO "pioneers" VALUES(37,162,'37:162','[ҳаргиз] гумроҳгари [касе аз дини ҳақ] нестед');
INSERT INTO "pioneers" VALUES(37,163,'37:163','Магар он ки [ба тақдири илоҳӣ] аҳли дузах бошад');
INSERT INTO "pioneers" VALUES(37,164,'37:164','Ва [фариштагон мегӯянд]: « Ҳеҷ як аз мо нест, магар он ки мақоми мушаххасе [дар пешгоҳи илоҳӣ] дорад');
INSERT INTO "pioneers" VALUES(37,165,'37:165','Моем, ки [барои парастиш ва ниёиш ба даргоҳи Парвардигор] ба саф истодаем');
INSERT INTO "pioneers" VALUES(37,166,'37:166','Ва бе тардид, моем, ки тасбеҳгӯи [Ӯ] ҳастем»');
INSERT INTO "pioneers" VALUES(37,167,'37:167','Ва [кофирон, пеш аз биъсат] ҳамвора мегуфтанд:');
INSERT INTO "pioneers" VALUES(37,168,'37:168','«Агар китобе аз [китобҳои осмонии] пешиниён наздамон буд,');
INSERT INTO "pioneers" VALUES(37,169,'37:169','яқинан, аз бандагони мухлиси Аллоҳ таоло мешудем»');
INSERT INTO "pioneers" VALUES(37,170,'37:170','Аммо [ҳангоме ки паёмбар бо Қуръон ба сӯяшон омад, ҳаққонияти] онро инкор карданд; пас, ба зудӣ [натиҷаи ин корашонро] хоҳанд донист');
INSERT INTO "pioneers" VALUES(37,171,'37:171','Ва ҳатман суханони Мо дар бораи бандагони фиристодаамон аз пеш содир шудааст');
INSERT INTO "pioneers" VALUES(37,172,'37:172','Онон, ҳатман, аз ҷониби Мо ёрӣ шудаанд');
INSERT INTO "pioneers" VALUES(37,173,'37:173','Ва бе тардид, лашкари Мо пирӯзанд');
INSERT INTO "pioneers" VALUES(37,174,'37:174','Пас, [эй Паёмбар] то муддате аз онон [мушрикон] рӯй бигардон');
INSERT INTO "pioneers" VALUES(37,175,'37:175','Ва ононро [дар ҳангоми нузули азоб] бингар; пас, ба зудӣ худ [низ натиҷаи куфрашонро] хоҳанд дид');
INSERT INTO "pioneers" VALUES(37,176,'37:176','Оё онон азоби моро ба шитоб хосторанд?');
INSERT INTO "pioneers" VALUES(37,177,'37:177','Он гоҳ ки [азоби илоҳӣ] бар хонаҳояшон фуруд ояд, чи бад аст бомдоди ҳушдорёфтагон!');
INSERT INTO "pioneers" VALUES(37,178,'37:178','Ва то муддате аз онон рӯй бигардон');
INSERT INTO "pioneers" VALUES(37,179,'37:179','Ва ононро [дар ҳангоми нузули азоб] бингар; пас, ба зудӣ худ [низ натиҷаи куфрашонро] хоҳанд дид');
INSERT INTO "pioneers" VALUES(37,180,'37:180','Пок ва муназзаҳ аст Парвардигорат - Парвардигори иззат ва қудрат-аз он чи [мушрикон ба дурӯғ] васф мекунанд');
INSERT INTO "pioneers" VALUES(37,181,'37:181','Ва салом бар паёмбарон');
INSERT INTO "pioneers" VALUES(37,182,'37:182','Ва сипос [ва ситоиш] аз они Аллоҳ таоло- Парвардигори ҷаҳониён- аст');
INSERT INTO "pioneers" VALUES(38,1,'38:1','Сод. Савганд ба Қуръони пандомӯз [ки Аллоҳ таоло ҳеҷ шарике надорад]');
INSERT INTO "pioneers" VALUES(38,2,'38:2','Аммо кофирон гирифтори таассуб [-у ҷоҳилият] ва ситезанд');
INSERT INTO "pioneers" VALUES(38,3,'38:3','Чи бисёр наслҳои кофирро пеш аз онон ҳалок кардем, ки [дар ҳангоми нузули азоб] фарёд мезаданд, аммо [он замон дигар] ҳангоми наҷот набуд');
INSERT INTO "pioneers" VALUES(38,4,'38:4','Мушрикон аз ин ки бимдиҳандае аз миёни худашон ба суроғашон омадааст, ибрози шигифтӣ карданд ва гуфтанд: «Ин мард ҷодугару дурӯғгӯст');
INSERT INTO "pioneers" VALUES(38,5,'38:5','Оё ба ҷойи маъбудони мухталиф маъбуде ягона қарор додааст? Воқеан, кори аҷибе аст»');
INSERT INTO "pioneers" VALUES(38,6,'38:6','Бузургонашон ба по хостанд [ва ба пайравони худ гуфтанд]: «Биравед ва бар [парастишу ҳифзи] маъбудонатон истодагарӣ кунед. Бе тардид, [Муҳаммад бо] ин кор хостааст [ки шуморо гумроҳ кунад ва худро ба раёсат бирасонад]');
INSERT INTO "pioneers" VALUES(38,7,'38:7','Мо ин [назарияи тавҳид]-ро ҳатто дар оини масеҳият ҳам нашнидаем. Ин сухан ҷуз дурӯғ [чизе] нест');
INSERT INTO "pioneers" VALUES(38,8,'38:8','Оё аз миёни [ҳамаи] мо Қуръон ба ӯ нозил шудааст»? [Чунин нест, ки онон мегӯянд] Балки онон дар [асли] ваҳйи Ман тардид доранд ва ҳанӯз азоби Маро начашидаанд');
INSERT INTO "pioneers" VALUES(38,9,'38:9','Оё ганҷинаҳои раҳмати Парвардигори шикастнопазири бахшандаат наздашон ҳаст?');
INSERT INTO "pioneers" VALUES(38,10,'38:10','Оё фармонравои осмонҳо ва замин ва байни онҳо ҳастанд? [Агар чунин аст] Бо васоил [-у имконоти худ ба осмон] авҷ бигиранд [ва монеи нузули ваҳй гарданд]');
INSERT INTO "pioneers" VALUES(38,11,'38:11','[Ин кофирон] Лашкаре шикастхӯрда аз ҷумла соири гурӯҳҳо [ва аҳзоби мухолифи паёмбарон] ҳастанд');
INSERT INTO "pioneers" VALUES(38,12,'38:12','Пеш аз онон низ қавми Нуҳ ва Од ва Фиръавни соҳибсипоҳ низ [паёмбарони Моро] такзиб карданд');
INSERT INTO "pioneers" VALUES(38,13,'38:13','Қавми Самуд ва қавми Лут ва асҳоби Айка [қавми Шуайб] низ аз он гурӯҳҳо буданд [ки паёмбаронро такзиб карданд]');
INSERT INTO "pioneers" VALUES(38,14,'38:14','Ҳар як [аз ин гурӯҳҳо] паёмбарон [-и худ]-ро такзиб карданд ва кайфари ман [дар мавридашон] таҳаққуқ ёфт');
INSERT INTO "pioneers" VALUES(38,15,'38:15','Инон фақат бонге саҳмнокро интизор мекашанд, ки ҳеҷ бозгаште надорад');
INSERT INTO "pioneers" VALUES(38,16,'38:16','[Бо тамасхур] Мегӯянд: «Парвардигоро, пеш аз фаро расидани рӯзи ҳисоб баҳраи моро [аз азоб] ба шитоб бидеҳ»');
INSERT INTO "pioneers" VALUES(38,17,'38:17','[Эй паёмбар] Дар баробари гуфторашон шикебо бош ва аз Довуд, бандаи тавонманди Мо ёд кун, ки ҳамвора [бо тавба ва анҷоми кори хайр ба даргоҳи мо] бозмегашт');
INSERT INTO "pioneers" VALUES(38,18,'38:18','Мо кӯҳҳоро ба ӯ ба хидмат гумоштем, ки ҳамнаво бо ӯ шомгоҳон ва бомдодон [Парвардигорро] тасбеҳ мегуфтанд');
INSERT INTO "pioneers" VALUES(38,19,'38:19','Ва парандагонро [низ ба хидмат гумоштем], ки гирд меомаданд ва ҳамагӣ мутеаш буданд [ва бо вай дар зикри Парвардигор ҳамовозӣ мекарданд]');
INSERT INTO "pioneers" VALUES(38,20,'38:20','Мо фармонравоиашро устувор намудем ва ба ӯ паёмбарӣ ва каломе файсалабахш ато кардем');
INSERT INTO "pioneers" VALUES(38,21,'38:21','Оё хабари афроде ба ту расидааст, ки барои додхоҳӣ [ба суроғи Довуд омаданд ва] аз девори ибодатгоҳаш боло рафтанд?');
INSERT INTO "pioneers" VALUES(38,22,'38:22','Чун [бидуни иттилоъ] бар Довуд ворид шуданд, аз диданашон ба ваҳшат афтод; онон гуфтанд: «Натарс, мо ду нафари шокӣ [шикояткунанда] ҳастем, ки яке бар дигаре ситам кардааст. Миёнамон ба адолат доварӣ кун ва аз ҳақ дур нашав ва моро ба роҳи рост ҳидоят кун»');
INSERT INTO "pioneers" VALUES(38,23,'38:23','[Яке аз онон гуфт] «Ин мард бародари ман аст. Ӯ наваду нуҳ меш дорад ва ман як меш дорам ва [ба исрор] мегӯяд: «Он [як меш]-ро ҳам ба ман во гузор» ва дар баҳс бар ман пирӯз шудааст»');
INSERT INTO "pioneers" VALUES(38,24,'38:24','[Довуд] Гуфт: «Мусалламан, ӯ бо хостани меши ту [барои афзудан] ба мешҳои худаш бар ту ситам кардааст ва бисёре аз шарикон [ва ҳамкорон] дар ҳаққи якдигар ситам мекунанд, магар касоне, ки имон овардаанд ва корҳои шоиста анҷом додаанд, ки [онҳо низ] бисёр нодиранд». Довуд [ки бидуни тавзеҳ хостан аз тарафи дигар ҳукм содир карда буд] мутаваҷҷеҳ шуд, ки имтиҳонаш кардаем. Он гоҳ аз Парвардигораш омурзиш хост ва бо фурӯтанӣ ба замин афтод ва [ба даргоҳи илоҳӣ] тавба кард');
INSERT INTO "pioneers" VALUES(38,25,'38:25','Пас, ӯро ба хотири он [хато] бахшидем ва барояш дар пешгоҳи худ мақоме воло қарор додем ва [дар охират] бозгашти некуе хоҳад дошт');
INSERT INTO "pioneers" VALUES(38,26,'38:26','Эй Довуд, мо туро дар замин халифа ва фармонраво қарор додем; Пас, дар миёни мардум ба ҳақ доварӣ кун ва пайрави ҳаво [-и нафс] набош, ки аз роҳи Аллоҳ таоло гумроҳат мекунад. Бе гумон касоне, ки аз роҳи Аллоҳ таоло гумроҳ мешаванд, ба сазои он ки рӯзи ҳисобро фаромӯш кардаанд, азоби шадиде [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(38,27,'38:27','Мо осмон ва замин ва он чиро, ки миёни онҳост, беҳуда наофаридаем. Ин гумони кофирон аст. Пас, вой бар кофирон аз оташи дузах!');
INSERT INTO "pioneers" VALUES(38,28,'38:28','Оё касоне, ки имон овардаанд ва корҳои шоиста анҷом додаанд, монанди касоне ба ҳисоб меоварем, ки дар замин табаҳкорӣ кардаанд? Оё парҳезкоронро монанди бадкорон ба ҳисоб меоварем?');
INSERT INTO "pioneers" VALUES(38,29,'38:29','[Эй Муҳаммад, ин Қуръон] Китобест пурбаракат, ки бар ту нозил кардем, то [мардум] дар оёташ биандешанд ва хирадмандон панд гиранд');
INSERT INTO "pioneers" VALUES(38,30,'38:30','Мо Сулаймонро ба Довуд ато кардем, неку бандае буд ва ҳамвора [ба даргоҳи Парвардигораш] тавба мекард');
INSERT INTO "pioneers" VALUES(38,31,'38:31','[Ёд кун] Он гоҳ ки асргоҳон аспҳои асил [ва тундпо] барояш оварданд');
INSERT INTO "pioneers" VALUES(38,32,'38:32','[Сулаймон саргарми тамошои аспҳо шуд ва вақти намоз гузашт.] Он гоҳ гуфт: «Ба ростӣ, ки дӯстии ин аспҳо, маро аз ёди Парвардигорам ғофил кард, то он ки [хуршед] дар пардаи мағриб пинҳон шуд');
INSERT INTO "pioneers" VALUES(38,33,'38:33','[Бори дигар] Онҳоро назди ман бозгардонед». Он гоҳ [ба нишонаи вақф дар роҳи Аллоҳ таоло] ба соқу гардани аспҳо даст кашид');
INSERT INTO "pioneers" VALUES(38,34,'38:34','Сулаймонро озмудем ва ҷасади навзодеро [ки бидуни таваккал бар Аллоҳ таоло муштоқона дар интизори таваллудаш буд] бар тахташ афкандем [то дарёбад, ки қудрат ба тамомӣ дар ихтиёри Аллоҳ таоло аст]. Он гоҳ [тавба кард ва ба даргоҳи илоҳӣ] бозгашт');
INSERT INTO "pioneers" VALUES(38,35,'38:35','Гуфт: «Парвардигоро, маро биомурз ва ҳукумате ба ман арзонӣ дор, ки пас аз ман беназир бошад, ки бахшоянда Туӣ, Ту»');
INSERT INTO "pioneers" VALUES(38,36,'38:36','Пас, [Мо] бодро дар хидматаш гумоштем, ки ҳар куҷо ирода мекард, ба фармони Ӯ ба оромӣ равон мешуд');
INSERT INTO "pioneers" VALUES(38,37,'38:37','Ва ҳар бинокор ва ғаввосе аз ҷинниёнро [ки мехост, ба фармонаш даровардем]');
INSERT INTO "pioneers" VALUES(38,38,'38:38','Ва [гурӯҳи] дигаре [аз ҷиниён]-ро [ки ҳамагӣ] дар ғулу занҷир буданд, [таҳти султааш] даровардем');
INSERT INTO "pioneers" VALUES(38,39,'38:39','[Ба ӯ гуфтем] «Ин атои Мост, пас, [ба ҳар кас мехоҳӣ] бибахш ва аз ҳар ки мехоҳӣ, боз дор. Ҳисобе бар ту нест»');
INSERT INTO "pioneers" VALUES(38,40,'38:40','Бе тардид, барои Сулаймон дар пешгоҳи худ мақоми воло қарор додем ва [дар охират] бозгаште неку хоҳад дошт');
INSERT INTO "pioneers" VALUES(38,41,'38:41','[Эй паёмбар] Аз бандаи Мо Аюб ёд кун, он гоҳ ки Парвардигорашро нидо дод, ки: «Шайтон маро ба ранҷу дард овардааст»');
INSERT INTO "pioneers" VALUES(38,42,'38:42','[Ба ӯ фармон додем] «Пойи худро бар замин бикӯб [то чашмае биҷӯшад]. Ин чашмасоре хунук барои шустушӯ ва ошомиданист»');
INSERT INTO "pioneers" VALUES(38,43,'38:43','Ва хонаводаашро [ки аз даст дода буд] дучанд ба вай бахшидем, то [нишонаи] раҳмате аз ҷониби Мо ва андарзе барои хирадмандон бошад');
INSERT INTO "pioneers" VALUES(38,44,'38:44','Ва [ба ӯ фармон додем] «Дастае аз шохаҳо [-и борикро] баргир ва бо он [ҳамсаратро] бизан ва савгандатро [ки дар ҳангоми хашм ёд карда будӣ] нашикан. Бе тардид, ӯ марди шикебое буд. Чи неку бандае! Ба ростӣ, ки ӯ бисёр тавбакор буд');
INSERT INTO "pioneers" VALUES(38,45,'38:45','Ва [эй паёмбар] аз бандагони Мо Иброҳим ва Исҳоқ ва Яъқуб, ки тавонманду бобасират буданд');
INSERT INTO "pioneers" VALUES(38,46,'38:46','Мо бо [мавҳибати] ёди охират ба онон хулуси вижае бахшидем');
INSERT INTO "pioneers" VALUES(38,47,'38:47','Онон дар пешгоҳи Мо некмард ва баргузида буданд');
INSERT INTO "pioneers" VALUES(38,48,'38:48','Ва [низ] ёд кун аз Исмоил ва Ясаъ ва Зулкифл, ки ҳамагӣ аз некон буданд');
INSERT INTO "pioneers" VALUES(38,49,'38:49','Ин [Қуръон] пандест ва бе гумон, парҳезгорон [дар биҳишт] манзилгоҳе неку хоҳанд дошт');
INSERT INTO "pioneers" VALUES(38,50,'38:50','Боғҳое ҷовидон, ки дарҳояш ба рӯяшон кушодааст');
INSERT INTO "pioneers" VALUES(38,51,'38:51','Он ҷо, дар ҳоле ки бар тахтҳо такя задаанд, дастури меваҳои фаровон ва нӯшиданӣ медиҳанд');
INSERT INTO "pioneers" VALUES(38,52,'38:52','Ва наздашон занони дуруштчашм аст, ки [ танҳо ба шавҳарони худ] назар доранд ва [ҳамагӣ] ҳамсин ҳастанд');
INSERT INTO "pioneers" VALUES(38,53,'38:53','Ин ҳамон неъматҳоест, ки барои рӯзи ҳисоб ба шумо ваъда дода мешавад');
INSERT INTO "pioneers" VALUES(38,54,'38:54','Ин ризқу рӯзӣ аз ҷониби Мост, ки ҳаргиз [қатъ намешавад ва] поён намепазирад');
INSERT INTO "pioneers" VALUES(38,55,'38:55','Ин [подоши парҳезкорон] аст ва саркашон бадтарин бозгаштро доранд');
INSERT INTO "pioneers" VALUES(38,56,'38:56','[Ҳамон] Дузах, ки дар он ворид мешаванд ва чи бад қароргоҳе аст!');
INSERT INTO "pioneers" VALUES(38,57,'38:57','Ин [муҷозот] оби ҷӯшон ва хунобаи чирколудест, ки бояд аз он бичашанд');
INSERT INTO "pioneers" VALUES(38,58,'38:58','Ва ҷуз инҳо кайфарҳои гуногуни дигаре ҳаммонанди он доранд');
INSERT INTO "pioneers" VALUES(38,59,'38:59','[Ба раҳбарони дузахӣ гуфта мешавад] «Ин гурӯҳ низ ҳамроҳи шумо [ба дузах] дармеоянд». [Онон посух медиҳанд] «Хуш омад бар онон мабод! Чаро ки ба оташ дармеоянд»');
INSERT INTO "pioneers" VALUES(38,60,'38:60','[Пайравон] Мегӯянд: «Балки, шуморо хушомад мабод, ки ин [азоб]-ро пеши пойи мо ниҳодед. Пас, [дузах] чи бад қароргоҳе аст»!');
INSERT INTO "pioneers" VALUES(38,61,'38:61','[Сипас] Мегӯянд: «Парвардигоро, ҳар кас, ки ин [азоб]-ро барои Мо фароҳам кардааст, азобашро дар оташ дучанд кун»');
INSERT INTO "pioneers" VALUES(38,62,'38:62','[Дузахиёни мутакаббир] Мегӯянд: «Чаро касонеро, ки [дар дунё] дар зумраи ашрор мепиндоштем, [дар дузах] намебинем?');
INSERT INTO "pioneers" VALUES(38,63,'38:63','Оё [бе ҷиҳат] ононро, ки тамасхур мегирифтем ё [ин ҷо ҳастанд ва] чашмон [-и мо] бар эшон намеафтад?»');
INSERT INTO "pioneers" VALUES(38,64,'38:64','Ин муҷодалаи аҳли оташ қатъан ростаст');
INSERT INTO "pioneers" VALUES(38,65,'38:65','[Эй паёмбар, ба кофирон] Бигӯ: «Ман фақат бимдиҳандаам ва ҳеҷ маъбуде [ба ҳақ] ҷуз Аллоҳи ягонаи пирӯзманд нест');
INSERT INTO "pioneers" VALUES(38,66,'38:66','Парвардигори осмонҳо ва замин ва он чи миёни онҳост. [Он] Шикастнопазири омурзанда»');
INSERT INTO "pioneers" VALUES(38,67,'38:67','[Эй паёмбар] Бигӯ: «Ин [Қуръон] хабаре бузург аст,');
INSERT INTO "pioneers" VALUES(38,68,'38:68','шумо аз он рӯйгардонед');
INSERT INTO "pioneers" VALUES(38,69,'38:69','[Агар ба ман ваҳй намешуд] Аз олами болотар он гоҳ, ки фариштагон [дар мавриди офариниши инсон] ҷидол мекарданд, ҳеҷ иттилое надоштам');
INSERT INTO "pioneers" VALUES(38,70,'38:70','Танҳо аз он рӯй ба ман ваҳй мешавад, ки бимдиҳандае ошкорам»');
INSERT INTO "pioneers" VALUES(38,71,'38:71','[Ёд кун аз] Ҳангоме ки Парвардигорат ба фариштагон гуфт: «Ман башаре аз гил меофаринам');
INSERT INTO "pioneers" VALUES(38,72,'38:72','Он гоҳ ки ӯро сомон додам ва аз рӯҳи хеш дар ӯ дамидам, дар баробараш саҷда кунед»');
INSERT INTO "pioneers" VALUES(38,73,'38:73','Пас, [чун Одам халқ шуд] фариштагон ҳамагӣ саҷда карданд');
INSERT INTO "pioneers" VALUES(38,74,'38:74','Магар Иблис, ки такаббур варзид ва кофир шуд');
INSERT INTO "pioneers" VALUES(38,75,'38:75','[Аллоҳ таоло] Фармуд: «Эй Иблис, чи чиз муҷиб шуд, ки дар баробари он чи ба дастони худ офаридаам, саҷда накунӣ? Оё такаббур варзидӣ ё бартариҷӯӣ кардӣ»?');
INSERT INTO "pioneers" VALUES(38,76,'38:76','Гуфт: «Ман аз ӯ бартарам: Маро аз оташ офаридаӣ ва ӯро аз гил»');
INSERT INTO "pioneers" VALUES(38,77,'38:77','Аллоҳ таоло фармуд: «Аз биҳишт берун шав, ки ту [манфуру] рондашудаӣ');
INSERT INTO "pioneers" VALUES(38,78,'38:78','Ва лаънати Ман то рӯзи қиёмат бар ту хоҳад буд»');
INSERT INTO "pioneers" VALUES(38,79,'38:79','[Иблис] Гуфт: «Парвардигоро, пас то рӯзе, ки [бандагонат] барангехта мешаванд, ба ман муҳлат бидеҳ»');
INSERT INTO "pioneers" VALUES(38,80,'38:80','[Аллоҳ таоло] Фармуд: «Муҳлат хоҳӣ дошт');
INSERT INTO "pioneers" VALUES(38,81,'38:81','То [рӯзи қиёмат] он вақти муайян»');
INSERT INTO "pioneers" VALUES(38,82,'38:82','[Иблис] Гуфт: «Ба иззатат савганд, ки ҳамаи ононро гумроҳ хоҳам кард');
INSERT INTO "pioneers" VALUES(38,83,'38:83','Магар бандагони холиси Ту аз [миёни] онон [ки ҳидояташон кардаӣ]»');
INSERT INTO "pioneers" VALUES(38,84,'38:84','[Аллоҳ таоло] Фармуд: «Пас, [сухани Ман] ҳақ аст ва ҳақро мегӯям');
INSERT INTO "pioneers" VALUES(38,85,'38:85','Қатъан, ҷаҳаннамро аз ту ва ҳар ки аз ту пайравӣ кунад, анбошта хоҳам сохт»');
INSERT INTO "pioneers" VALUES(38,86,'38:86','[Эй паёмбар, ба мушрикон] Бигӯ: «Ман [дар баробари рисолатам] ҳеҷ подоше аз шумо намехоҳам ва фиребкор нестам [ва ба дурӯғ иддаои нубувват намекунам]»');
INSERT INTO "pioneers" VALUES(38,87,'38:87','Ин [Қуръон] ҷуз панд барои ҷаҳониён [инсон ва ҷин] нест');
INSERT INTO "pioneers" VALUES(38,88,'38:88','Ва қатъан, пас аз марг аз хабари [ҳаққоният ва ростии] он огоҳ хоҳед шуд');
INSERT INTO "pioneers" VALUES(39,1,'39:1','Нузули ин китоб аз ҷониби Аллоҳи шикастнопазири ҳаким аст');
INSERT INTO "pioneers" VALUES(39,2,'39:2','Ин китобро ба ҳақ бар ту нозил кардем, пас, Аллоҳро бо хулуси эътиқод парастиш кун');
INSERT INTO "pioneers" VALUES(39,3,'39:3','Огоҳ бошед, ки дини холис [ва холӣ аз ширк] аз они Аллоҳ таоло аст ва афроде, ки ғайри Аллоҳро [дӯст ва] корсози худ интихоб кардаанд [пиндорашон ин аст, ки]: «Онҳоро фақат ба ин далел мепарастем, ки моро ба Аллоҳ таоло наздик гардонанд [ва манзалатамонро бештар кунанд]». Аллоҳ таоло дар мавориди ихтилофашон миёни онон [ва муваҳҳидони ростин] доварӣ хоҳад кард. Бе тардид, Аллоҳ таоло касеро, ки дурӯғгӯи носипос бошад, ҳидоят нахоҳад кард');
INSERT INTO "pioneers" VALUES(39,4,'39:4','Агар Аллоҳ таоло мехост фарзанде баргузинад, аз миёни офаридагони худ ҳар чи мехост, бармегузид. Ӯ муназзаҳ [аз ин нисбатҳо] аст ва Ӯст Аллоҳи ягонаи пирӯзманд');
INSERT INTO "pioneers" VALUES(39,5,'39:5','Осмонҳо ва заминро ба ҳақ [ва бо ҳадаф] офарид. Шабро бар рӯз мегустуронад ва рӯзро бар шаб ва хуршеду моҳро дар хидмат [-и шумо] гумошт, ки ҳар як то саромади муайяне равон аст. Огоҳ бошед! Ӯ шикастнопазири омурзгор аст');
INSERT INTO "pioneers" VALUES(39,6,'39:6','Ӯ шуморо аз як тан офарид, он гоҳ ҳамсарашро низ аз вай падид овард ва барои [истифодаи] шумо ҳашт навъ аз чаҳорпоён [нару модаи шутур, гов, гӯсфанд ва буз]-ро офарид. Шуморо дар шиками модаратон тайи мароҳили мухталиф дар ниҳонгоҳҳои сегона меофаринад. Ин аст Аллоҳ таоло, Парвардигори шумо, ки фармонравоӣ хоси Ӯст. Маъбуде [ба ҳақ] ҷуз Ӯ нест, пас, чи гуна ба бероҳа меравед?');
INSERT INTO "pioneers" VALUES(39,7,'39:7','Агар носипосӣ кунед, [бидонед, ки] Аллоҳ таоло аз шумо бениёз аст ва ҳаргиз носипосии бандагонашро намеписандад ва агар шукри Ӯро ба ҷой оваред, ин корро бароятон меписандад ва ҳеҷ гунаҳгоре бори гуноҳи дигареро бар дӯш намекашад, пас, бозгаштатон ба сӯйи Парвардигоратон аст. Он гоҳ Ӯ шуморо аз он чи анҷом медодед, огоҳ мекунад. Яқинан, Ӯ аз рози дилҳо огоҳ аст');
INSERT INTO "pioneers" VALUES(39,8,'39:8','Ҳаргоҳ инсон дучори [ранҷу] зиёне мешавад, Парвардигорашро [бо тазарруъ ва зорӣ] мехонад ва ба даргоҳаш бозмегардад. Аммо ҳангоме ки неъмате аз ҷониби худ ба вай мебахшем, мушкилеро, ки қаблан барояш дуо мекард, аз ёд мебарад ва барои Аллоҳ таоло [дар қудрат] ҳамтоёне қоил мешавад, то [дигаронро низ] аз роҳи Ӯ гумроҳ кунад. [Эй паёмбар, ба чунин касе] Бигӯ: «Андак замоне бо носипосӣ [аз неъматҳои дунё] баҳраманд шав, ки қатъан дузахӣ хоҳӣ буд»');
INSERT INTO "pioneers" VALUES(39,9,'39:9','Оё [чунин мушрике беҳтар аст ё] он ки дар тӯли шаб дар ҳоли саҷда ва қиём ба ибодат машғул аст ва аз [азоби] охират тарсон аст ва ба раҳмати Парвардигораш умед дорад? Бигӯ: «Оё касоне, ки [ҳаққи бандагии Парвардигорро] медонанд ва касоне, ки намедонанд, яксонанд? Танҳо хирадмандон панд мепазиранд»');
INSERT INTO "pioneers" VALUES(39,10,'39:10','[Эй паёмбар] Бигӯ: «Эй бандагони боимони ман, аз Парвардигоратон парво кунед. Афроде, ки некуӣ мекунанд [илова бар охират дар ин дунё низ] подоши неку доранд. Замини Аллоҳ таоло густурдааст [пас, агар барои ҳифзи ақидаи худ дучори мушкил шудед, ҳиҷрат кунед]. Қатъан, подоши пойдорон комилу бедареғ ато хоҳад шуд»');
INSERT INTO "pioneers" VALUES(39,11,'39:11','Бигӯ: «Ман маъмурам бо хулуси эътиқод Аллоҳро бипарастам');
INSERT INTO "pioneers" VALUES(39,12,'39:12','Ва маъмур шудаам нахустин касе [дар ин уммат] бошам, ки таслим [маҳз дар муқобили Парвардигор] аст»');
INSERT INTO "pioneers" VALUES(39,13,'39:13','Бигӯ: «Агар дар баробари Парвардигорам нофармон бошам, аз азоби рӯзи бузург [қиёмат] метарсам»');
INSERT INTO "pioneers" VALUES(39,14,'39:14','[Эй паёмбар] Бигӯ: «Ман Аллоҳро бо хулуси эътиқоди худ мепарастам');
INSERT INTO "pioneers" VALUES(39,15,'39:15','Шумо низ ғайр аз Аллоҳ таоло ҳар чиро мехоҳед, бипарастед». Бигӯ: «Зиёнкорон [-и воқеӣ] касоне ҳастанд, ки [ба тамаъи дунё] худ ва хонаводаашонро дар растохез ба зиён андохтаанд. Огоҳ бошед, ки ин ҳамон зиёни ошкор аст');
INSERT INTO "pioneers" VALUES(39,16,'39:16','Бар болои сар ва зери пояшон табақоте аз оташ аст. Ин аст он чи Аллоҳ таоло бандагонашро аз он метарсонад. Эй бандагони Ман, аз [нофармонии] Ман парво кунед');
INSERT INTO "pioneers" VALUES(39,17,'39:17','Башорат барои касонест, ки аз парастиши тоғут [бутҳо] иҷтиноб варзиданд ва [тавбакунон] ба даргоҳи илоҳӣ бозгаштанд. Пас, [эй паёмбар] ба бандагонам [биҳиштро] башорат бидеҳ');
INSERT INTO "pioneers" VALUES(39,18,'39:18','[Ҳамон] Касоне, ки сухан [-ҳои мухталиф]-ро мешунаванд ва аз беҳтарини он пайравӣ мекунанд. Онон касоне ҳастанд, ки Аллоҳ таоло ҳидояташон карда ва хирадманданд');
INSERT INTO "pioneers" VALUES(39,19,'39:19','Оё касеро, ки фармони муҷозот бар ӯ қатъӣ шудааст [метавон ҳидоят намуд]? Оё ту метавонӣ касеро, ки дар оташ аст, наҷот диҳӣ?');
INSERT INTO "pioneers" VALUES(39,20,'39:20','Аммо касоне, ки аз Парвардигорашон парво доранд, [дар биҳишт] ғурфаҳое доранд, ки бар фарози онҳо ғурфаҳое [дигар] сохта шудааст [ва] аз зери онҳо ҷӯйборҳо ҷорист. Аллоҳ таоло [ба дурустӣ] ваъда додааст ва ваъдахилофӣ намекунад');
INSERT INTO "pioneers" VALUES(39,21,'39:21','Оё надидаӣ, ки Аллоҳ таоло бороне аз осмон фиристод ва онро [ба сурати] чашмаҳое дар замин ҷорӣ сохт. Он гоҳ ба василаи он киштзоре бо рангҳои гуногун падид меоварад. Сипас пажмурда мегардад ва мебинӣ, ки зард шудааст. Он гоҳ онро хурду хошок месозад? Бе тардид, дар ин [дигаргуниҳо] панде барои хирадмандон аст');
INSERT INTO "pioneers" VALUES(39,22,'39:22','Оё касе, ки Аллоҳ таоло дилашро пазирои Ислом карда ва аз ҷониби Парвардигораш аз нур [ҳидоят ва басират] бархӯрдор гашта [ҳаммонанди сахтдилон аст]? Вой бар онон дар баробари зикри Аллоҳ таоло сангдил шудаанд! Онон дар гумроҳии ошкоранд');
INSERT INTO "pioneers" VALUES(39,23,'39:23','Аллоҳ таоло беҳтарин суханро дар сурати китобе нозил карда, ки [бархе аз оёташ] ҳаммонанди якдигар ва [бархе низ] мукаррар аст. Касоне, ки аз [муҷозоти] Парвардигорашон бим доранд, [бо шунидани ин оёт] мӯй бар танашон рост мешавад ва сипас дилу ҷонашон ба ёди Аллоҳ таоло ором мегирад. Ин ҳидояти Аллоҳ таоло аст, ки ҳар киро бихоҳад, ба он роҳ менамояд ва ҳар киро Аллоҳ таоло гумроҳ созад, ҳеҷ ҳидоятгаре нахоҳад дошт');
INSERT INTO "pioneers" VALUES(39,24,'39:24','Оё касе, ки дар рӯзи қиёмат [дастонаш баста аст ва ночор] бо рӯяш он азоби сахтро дафъ мекунад, [ҳамчун касест, ки дар осоиши биҳишт қарор дорад]? Ба ситамгорон [-и мушрик] гуфта шавад, [натиҷаи] дастовардатонро бичашед');
INSERT INTO "pioneers" VALUES(39,25,'39:25','Касоне, ки пеш аз онон буданд, низ [оёти Аллоҳро] дурӯғ шумурданд ва аз ҷойе, ки фикр намекарданд, азоб домангирашон шуд');
INSERT INTO "pioneers" VALUES(39,26,'39:26','Пас, Аллоҳ таоло [таъми] расвоӣ ва хориро дар зиндагии дунё ба онон чашонд ва мусалламан, азоби охират бузургтар [ва сахттар] аст. Агар мушрикон медонистанд [ки рафторашон чи пайомаде дорад, ибрат мегирифтанд]');
INSERT INTO "pioneers" VALUES(39,27,'39:27','Дар ин Қуръон аз ҳар [гуна] масале барои мардум овардаем, бошад, ки панд пазиранд');
INSERT INTO "pioneers" VALUES(39,28,'39:28','Қуръоне ба забони фасеҳи арабӣ ва бидуни ҳеҷ гуна ибҳому инҳироф [нозил кардем], бошад, ки [таҳти таълимоташ аз Парвардигори якто] парво кунанд');
INSERT INTO "pioneers" VALUES(39,29,'39:29','Аллоҳ таоло [барои муқоисаи мушрику муваҳҳид] мардеро мисол мезанад, ки чанд шарик дар бораи [моликияти] ӯ пайваста бо ҳам ихтилофу мушоҷара доранд ва дар муқобилаш марде, ки таҳти фармони як нафар қарор дорад. Оё ин ду нафар [аз назари мавқеият] яксонанд? Ситоиш махсуси Аллоҳ таоло аст, вале бештарашон намедонанд');
INSERT INTO "pioneers" VALUES(39,30,'39:30','[Эй паёмбар] Қатъан, ту хоҳӣ мурд ва онон [низ] хоҳанд мурд');
INSERT INTO "pioneers" VALUES(39,31,'39:31','Он гоҳ [шумо-мардум] дар рӯзи қиёмат назди Парвардигоратон [дар бораи масоили мавриди ихтилоф] муҷодала хоҳед кард');
INSERT INTO "pioneers" VALUES(39,32,'39:32','Ва кист ситамгортар аз он ки ба Аллоҳ таоло, дурӯғ нисбат медиҳад ва сухани ростеро, ки ба вай расидааст, дурӯғ мешуморад? Оё дар дузах ҷойгоҳе барои кофирон нест?');
INSERT INTO "pioneers" VALUES(39,33,'39:33','Аммо онон, ки паёми ростин биёваранд [ва дигаронро ба пазириши он даъват кунанд] ва худ низ онро тасдиқ намоянд, ба ростӣ, парҳезгоранд');
INSERT INTO "pioneers" VALUES(39,34,'39:34','Ҳар чи бихоҳанд, назди Парвардигорашон доранд. Подоши некукорон ҳамин аст');
INSERT INTO "pioneers" VALUES(39,35,'39:35','То Аллоҳ таоло бадтарин рафторашонро аз [корномаи] онон бизудояд ва бар асоси беҳтарин аъмолашон ба эшон подош диҳад');
INSERT INTO "pioneers" VALUES(39,36,'39:36','Оё Аллоҳ таоло барои дифоъ аз бандааш кофӣ нест? Аммо мушрикон туро аз бутҳо ва маъбудонашон метарсонанд ва ҳар киро Аллоҳ таоло гумроҳ кунад, ҳеҷ ҳидоятгаре нахоҳад дошт');
INSERT INTO "pioneers" VALUES(39,37,'39:37','Ва ҳар киро, ки Аллоҳ таоло ҳидоят кунад, ҳеҷ кас гумроҳаш нахоҳад сохт. Оё Аллоҳ таоло шикастнопазиру додситон нест?!');
INSERT INTO "pioneers" VALUES(39,38,'39:38','[Эй паёмбар] Агар аз мушрикон бипурсӣ: «Чи касе осмонҳо ва заминро офаридааст»? Мусалламан, мегӯянд: «Аллоҳ таоло». Ба онон бигӯ: «Пас, ба назари шумо, агар Аллоҳ таоло нохушӣ ва ранҷе бароям бихоҳад, оё он чи ба ҷойи Аллоҳ таоло [ба ниёиш] мехонед, метавонанд осебашро бартараф кунанд? Ё агар Аллоҳ таоло раҳмате барои ман бихоҳад, оё метавонанд монеаш шаванд?» Бигӯ: [Пуштибонии] Аллоҳ таоло бароям кофӣ аст. Аҳли таваккал танҳо бар Ӯ таваккал мекунанд»');
INSERT INTO "pioneers" VALUES(39,39,'39:39','Бигӯ: «Эй қавми ман, шумо бар ҳол [ва вазъи ширкомез]-и худ бошед, ман низ [ба вазоифи худ] амал мекунам. Ба зудӣ хоҳед донист,');
INSERT INTO "pioneers" VALUES(39,40,'39:40','[Ба зудӣ хоҳед донист, ] ки кист он ки [дар дунё] гирифтори азоби расвогар мешавад ва [дар охират] азоби поянда бар сараш меборад»');
INSERT INTO "pioneers" VALUES(39,41,'39:41','Ин китоби осмониро барои [ҳидояти] мардум, барҳақ бар ту нозил кардем. Ҳар ки ҳидоят шавад, ба суди худи ӯст ва ҳар ки гумроҳ гашт, ба зиёни хеш гумроҳ шудааст ва ту [нигаҳбон ва] корсозашон нестӣ [ки ононро ба ҳидоят иҷбор кунӣ]');
INSERT INTO "pioneers" VALUES(39,42,'39:42','Аллоҳ таоло ҷонҳоро дар лаҳзаи марг мегирад ва[низ] ҷони касеро, ки намурдааст, ба ҳангоми хобаш [мегирад], он гоҳ ҷонҳоеро, ки ҳукми марг бар онҳо рондааст, нигаҳ медорад ва ҷони дигаронро бозпас мефиристад, то ҳангоми муайян [марг]. Дар ин [амр] барои ҳар ки биандешад, нишонаҳое [аз қиёмат] аст');
INSERT INTO "pioneers" VALUES(39,43,'39:43','Оё мушрикон ғайр аз Аллоҳ таоло [барои худ аз миёни бутҳо] шафоаткунандагоне интихоб кардаанд? [Эй паёмбар, ба онон] Бигӯ: «Ҳатто агар онҳо молики чизе набошанд ва [чизеро] дарк накунанд [боз ҳам онҳоро ба шафоат металабед]»?');
INSERT INTO "pioneers" VALUES(39,44,'39:44','Бигӯ: «Шафоат ба тамомӣ аз они Аллоҳ таоло аст. Фармонравоии осмонҳо ва замин низ аз они Ӯст ва ниҳоятан [ҳамагӣ] ба пешгоҳи Ӯ бозгардонда мешавед»');
INSERT INTO "pioneers" VALUES(39,45,'39:45','Ҳангоме ки Аллоҳ таоло ба яктоӣ [ва тавҳид] ёд мешавад, дилҳои касоне, ки ба охират имон надоранд, мутанаффир [ва безор] мегардад. Ва чун аз маъбудоне ғайр аз Аллоҳ таоло ёд шавад, он гоҳ аст, ки мушрикон шодмон мегарданд');
INSERT INTO "pioneers" VALUES(39,46,'39:46','Бигӯ: «Бор илоҳо, [эй] офаринандаи осмонҳо ва замин, эй донои ниҳону ошкор, Ту дар миёни бандагонат дар мавориди ихтилофашон доварӣ хоҳӣ кард»');
INSERT INTO "pioneers" VALUES(39,47,'39:47','Агар ду баробари он чи дар замин аст, дар ихтиёри ситамгорон [мушрикон] бошад, ҳамаро барои наҷоти худ аз сахтии азоби рӯзи қиёмат фидо мекунанд [Вале пазируфтанӣ нест]. Ва аз ҷониби Аллоҳ таоло умуре барояшон ошкор мегардад, ки тасаввурашро ҳам намекарданд');
INSERT INTO "pioneers" VALUES(39,48,'39:48','[Дар он рӯз] Дастоварди зишташон бар онон ошкор мешавад ва азобе, ки масхара мекарданд, домангирашон хоҳад шуд');
INSERT INTO "pioneers" VALUES(39,49,'39:49','Ҳар гоҳ инсон дучори [ранҷу] зиёне шавад, Моро [ба ниёиш] мехонад ва чун аз ҷониби хеш неъмате ба вай арзонӣ дорем, мегӯяд: "Фақат [лаёқат ва] доноии худам муҷиби ин неъмат шудааст. [На] балки ин озмоиш [-и илоҳӣ] аст, вале бештари мардум намедонанд');
INSERT INTO "pioneers" VALUES(39,50,'39:50','Пешиниёнашон ҳам ҳамин суханро гуфтанд, вале [ба ҳангоми азоб] дастовардашон суде ба ҳолашон надошт');
INSERT INTO "pioneers" VALUES(39,51,'39:51','Ва [уқубати] дастоварди зишташон домангирашон шуд. Ин ситамгорон [-и мушрик] низ ба уқубати дастовардашон гирифтор хоҳанд шуд ва [Парвардигори мутаолро] ба танг нахоҳанд овард');
INSERT INTO "pioneers" VALUES(39,52,'39:52','Оё мушрикон намедонанд, ки Аллоҳ таоло [неъмат ва] рӯзиро бар ҳар касе, ки бихоҳад, густурда медорад ва ё танг мегирад? Бе гумон, дар ин амр барои муъминон нишонаҳое [аз тадбири илоҳӣ] аст');
INSERT INTO "pioneers" VALUES(39,53,'39:53','[Эй паёмбар] Ба бандагонам, ки [дар иртикоб ба ширку гуноҳ] зиёдаравӣ кардаанд, бигӯ: «Аз раҳмати илоҳӣ маъюс набошед, чаро ки [агар тавба кунед] Аллоҳ таоло ҳамаи гуноҳонатонро мебахшад. Ба ростӣ, ки Ӯ бисёр омурзанда ва меҳрубон аст');
INSERT INTO "pioneers" VALUES(39,54,'39:54','Пеш аз он ки азоб ба суроғатон биёяд ва ҳеҷ кас натавонад ёриатон кунад, ба сӯйи Парвардигоратон бозгардед ва таслими [фармони] Ӯ гардед');
INSERT INTO "pioneers" VALUES(39,55,'39:55','Ва пеш аз он ки ногаҳон, дар ҳоле ки ғофилед, азоб ба суроғатон биёяд, аз беҳтарин дастурҳое, ки аз ҷониби Парвардигоратон ба шумо нозил шудааст, пайравӣ кунед');
INSERT INTO "pioneers" VALUES(39,56,'39:56','[Ҳам акнун барҳазар бошед] то касе [дар рӯзи қиёмат] нагӯяд: «Эй дареғ бар ман, ки дар ҳаққи Аллоҳ таоло кӯтоҳӣ намудам ва [муъминонро] масхара мекардам»!');
INSERT INTO "pioneers" VALUES(39,57,'39:57','Ё [гуноҳи худро бар гардани тақдир наандозад ва] нагӯяд: «Агар Аллоҳ таоло маро ҳидоят карда буд, қатъан, парҳезкор будам»');
INSERT INTO "pioneers" VALUES(39,58,'39:58','Ё ҳангоме ки азобро мебинад, нагӯяд: «Эй кош, бозгаште [ба дунё] доштам, то дар зумраи некукорон қарор мегирифтам»');
INSERT INTO "pioneers" VALUES(39,59,'39:59','[Аллоҳ таоло мефармояд] «Оёти Ман бароят омад ва ту онҳоро дурӯғ пиндоштӣ ва такаббур намудӣ ва куфр варзидӣ»');
INSERT INTO "pioneers" VALUES(39,60,'39:60','Ва рӯзи қиёмат касонеро, ки бар Аллоҳ таоло дурӯғ бастаанд, сияҳрӯй мебинӣ. Оё дар дузах ҷойгоҳе барои мутакаббирон нест»?');
INSERT INTO "pioneers" VALUES(39,61,'39:61','Аллоҳ таоло парҳезкоронро бо ворид кардан ба макони комёбиашон, [аз азоб] наҷот медиҳад. [Дар биҳишт] На ранҷе ба онон мерасад ва на андуҳгин мешаванд');
INSERT INTO "pioneers" VALUES(39,62,'39:62','Аллоҳ таоло офаридгори ҳар чизест ва Ӯ бар ҳама чиз [нигаҳбону] корсоз аст');
INSERT INTO "pioneers" VALUES(39,63,'39:63','Калиди ганҷинаҳои осмонҳо ва замин аз они Ӯст ва касоне, ки ба оёти илоҳӣ кофир шуданд, қатъан, зиёнкоранд');
INSERT INTO "pioneers" VALUES(39,64,'39:64','[Эй паёмбар] Бигӯ: «Эй бехабарон, оё ба ман фармон медиҳед, ки [маъбуде] ғайр аз Аллоҳро парастиш кунам?!»');
INSERT INTO "pioneers" VALUES(39,65,'39:65','Ба ту ва паёмбарони пеш аз ту ваҳй шудааст, ки: «Агар ширк биварзӣ, [подоши] корҳоят, қатъан, барбод хоҳад рафт ва зиёнкор хоҳӣ шуд');
INSERT INTO "pioneers" VALUES(39,66,'39:66','Балки танҳо Аллоҳро парастиш кун ва сипосгузор бош»');
INSERT INTO "pioneers" VALUES(39,67,'39:67','Мушрикон Аллоҳро чунон ки сазовори Ӯст, арҷ наниҳоданд, ҳол он ки дар рӯзи қиёмат замин яксара дар қабзаи Ӯст ва осмонҳо ба дасти Вай дарҳампечида хоҳад шуд. Поку муназзаҳ аст Аллоҳ таоло ва аз он чи бо вай шарик месозанд, бартар аст');
INSERT INTO "pioneers" VALUES(39,68,'39:68','[Дар остонаи қиёмат] Дар сур дамида мешавад. Пас, ҳар ки дар осмонҳо ва дар замин аст, мадҳуши марг хоҳад шуд, магар касе, ки Аллоҳ таоло бихоҳад, он гоҳ бори дигар дар он дамида мешавад ва ногаҳон [ҳама аз марг] бармехезанд ва чашм ба роҳ [-и фармони Парваридгор] ҳастанд');
INSERT INTO "pioneers" VALUES(39,69,'39:69','[Дар рӯзи қиёмат] замин ба нури Парвардигораш равшан хоҳад шуд ва нома [-и аъмол] ниҳода мешавад ва паёмбарон ва гувоҳонро меоваранд ва миёни бандагон ба ҳақ ва адолат доварӣ мешавад ва ба ҳеҷ кас ситам нахоҳад шуд');
INSERT INTO "pioneers" VALUES(39,70,'39:70','Ва ҷазои амалкарди ҳар кас ба тамомӣ дода мешавад. Ва Аллоҳ таоло нисбат ба аъмолашон донотар аст');
INSERT INTO "pioneers" VALUES(39,71,'39:71','Кофирон гурӯҳ-гурӯҳ ба сӯйи дузах ронда мешаванд. Ҳамин ки ба он ҷо мерасанд, дарҳояш кушода мегардад ва нигаҳбононаш мегӯянд: «Оё паёмбароне аз миёни худи шумо ба суроғатон наомаданд, ки оёти Парвардигоратонро барои шумо мехонданд ва дар бораи дидори ин рӯз ҳушдор медоданд? Онон [дар посух] мегӯянд: Оре, вале [мо инкорашон кардем ва] фармони муҷозот сазои кофирон аст»');
INSERT INTO "pioneers" VALUES(39,72,'39:72','[Ба онон] Гуфта мешавад: «Аз дарҳои дузах ворид шавед ва ҷовидона дар он ба сар баред. Ва [ба ростӣ] ҷойгоҳи мутакаббирон чи бад [ҷойгоҳе] аст!»');
INSERT INTO "pioneers" VALUES(39,73,'39:73','Ва касоне, ки аз Парвардигорашон парво доштанд, гурӯҳ-гурӯҳ ба сӯйи биҳишт ҳидоят мешаванд. Ҳамин ки ба он ҷо мерасанд, дар ҳоле ки дарҳои биҳишт кушода шудааст, [бо иззату эҳтиром ворид мешаванд] ва нигаҳбонони он ҷо мегӯянд: «Салом бар шумо. Хуш омадед. Ҷовидона ба биҳишт дароед»');
INSERT INTO "pioneers" VALUES(39,74,'39:74','Биҳиштиён мегӯянд: «Ситоиш ва сипос махсуси Аллоҳ таоло аст, ки ваъдаи хешро дар мавриди мо таҳаққуқ бахшид ва сарзамин [-и биҳишт]-ро ба мо мерос дод, ки дар ҳар ҷойи он бихоҳем, манзил мегирем. Ва [ба ростӣ] подоши аҳли амал чи некуст»');
INSERT INTO "pioneers" VALUES(39,75,'39:75','[Дар он рӯз] Фариштагонро хоҳӣ дид, ки перомуни Арш ҳалқа задаанд ва Парвардигорашонро ба покӣ меситоянд. Ва миёни мардум ба ҳаққу адолат доварӣ хоҳад шуд ва гуфта мешавад: «Ситоишу сипос махсуси Аллоҳ таоло Парвардигори ҷаҳониён аст»');
INSERT INTO "pioneers" VALUES(40,1,'40:1','Ҳо, мим');
INSERT INTO "pioneers" VALUES(40,2,'40:2','Нузули [ин] китоб аз сӯйи Аллоҳи шикастнопазири доност');
INSERT INTO "pioneers" VALUES(40,3,'40:3','[Парвардигоре, ки] Омурзандаи гуноҳ ва тавбапазир, сахткайфар ва неъматбахш аст. Ҳеҷ маъбуде ба [ҳақ] ҷуз Ӯ нест ва бозгашт [-и ҳама] ба сӯйи Ӯст');
INSERT INTO "pioneers" VALUES(40,4,'40:4','Танҳо кофирон ҳастанд, ки дар бораи оёти илоҳӣ муҷодала мекунанд, пас, мабодо рафтуомадашон дар шаҳрҳо [барои тиҷорату баҳрамандиашон аз неъматҳои дунё] туро бифиребад');
INSERT INTO "pioneers" VALUES(40,5,'40:5','Пеш аз онон қавми Нуҳ ва пас аз онон низ гурӯҳҳои дигаре [ҳамчун қавми Од ва Самуд ва Лут низ паёмбаронашонро] дурӯғгӯ шумурданд ва ҳар қавме хост паёмбарашро дастгир кунад [ва ба қатл бирасонад]. Онон ба ботил муҷодала карданд, то бад-он васила ҳақро аз миён бардоранд. Ман низ ононро азоб кардам. [Пас, бингаред, ки] азоби Ман чи гуна буд');
INSERT INTO "pioneers" VALUES(40,6,'40:6','Ва фармони азоби Парвардигорат дар бораи кофирон инчунин ба таҳаққуқ пайваст, ки онон аҳли оташанд');
INSERT INTO "pioneers" VALUES(40,7,'40:7','Фариштагоне, ки Аршро ҳамл мекунанд ва онон, ки гирди он ҳалқа задаанд, Парвардигорашонро ба покӣ меситоянд ва ба Ӯ имон доранд ва [ин гуна] барои аҳли имон омурзиш мехоҳанд: «Парвадигоро, раҳмат ва донишат ҳар чизеро фаро гирифтааст, пас, касонеро, ки тавба карданд ва пайрави роҳи Ту шуданд, биёмурз ва аз азоби дузах нигаҳ дор»');
INSERT INTO "pioneers" VALUES(40,8,'40:8','Парвардигоро, онон ва падарон [ва модарон] ва ҳамсарону фарзандони солеҳашонро дар боғҳои ҷовидони биҳишт, ки ваъда додаӣ, дохил кун, ки бе тардид, Ту шикастнопазиру ҳакимӣ');
INSERT INTO "pioneers" VALUES(40,9,'40:9','[Бор илоҳо] Ононро аз [кайфари] бадиҳо ҳифз кун ва ҳар киро дар он рӯз аз [кайфари] бадиҳо нигоҳ дорӣ, фарогирифтаи раҳматат қарор додаӣ. Ин ҳамон комёбии бузург аст»');
INSERT INTO "pioneers" VALUES(40,10,'40:10','Ва кофиронро [дар дузах] нидо медиҳанд, ки: «Душманӣ ва хашми Аллоҳ таоло сахттар аз душманӣ ва хашми шумо нисбат ба худатон аст, чаро ки ба имон даъват мешудед, вале [бо душманӣ нисбат ба худ ақидаи тавҳидро] инкор мекардед»');
INSERT INTO "pioneers" VALUES(40,11,'40:11','Онон [дар посух] мегӯянд: «Парвардигоро, моро ду бор мирондӣ ва ду бор зинда кардӣ, пас, [акнун ки] ба гуноҳони худ эътироф кардем, оё роҳе барои хуруҷ [аз дузах] вуҷуд дорад»?');
INSERT INTO "pioneers" VALUES(40,12,'40:12','[Ба онон гуфта мешавад] «Ин [азоб] бад-он сабаб аст, ки чун Аллоҳ таоло ба яктоӣ хонда мешуд, инкор мекардед, вале агар ба Ӯ ширк оварда мешуд, имон меовардед. [Акнун] Доварӣ аз они Аллоҳи баландмартабаи бузург аст»');
INSERT INTO "pioneers" VALUES(40,13,'40:13','Ӯст, ки нишонаҳои [тавон ва тадбири] худро ба шумо ироа медиҳад ва бароятон [бо нузули борон] аз осмон рӯзӣ мефиристад, вале танҳо касоне панд мегиранд, ки [холисона ба даргоҳи илоҳӣ] тавба кунанд');
INSERT INTO "pioneers" VALUES(40,14,'40:14','[Танҳо] Аллоҳро бо хулуси эътиқод бихонед, ҳарчанд кофирон нохушнуд бошанд');
INSERT INTO "pioneers" VALUES(40,15,'40:15','Ӯ дорои дараҷоти рафеъ ва соҳиби Арш аст ва ба сӯйи ҳар як аз бандагонаш, ки бихоҳад, ба фармони хеш ваҳй мефиристад, то [ба онон] дар бораи рӯзи мулоқот ҳушдор диҳад');
INSERT INTO "pioneers" VALUES(40,16,'40:16','Он рӯз, ки мардум ҳозир ва намоён мешаванд ва чизе аз онон бар Аллоҳ таоло пӯшида нахоҳад монд [Аллоҳ таоло мефармояд]: «Имрӯз фармонравоӣ аз они кист»? [Он гоҳ худ мефармояд] «Аз они Аллоҳи ягонаи пирӯзманд»');
INSERT INTO "pioneers" VALUES(40,17,'40:17','Имрӯз ҳар кас дар баробари он чи кардааст, подош дода мешавад ва ҳеҷ ситаме дар миён нест. Бе гумон, Аллоҳ таоло дар ҳисобрасӣ сареъ аст');
INSERT INTO "pioneers" VALUES(40,18,'40:18','Ба онон дар мавриди рӯзи қиёмат [ки бисёр наздик аст] ҳушдор бидеҳ, он гоҳ ки [аз шиддати ваҳшат] ҷонҳо ба гулӯгоҳ мерасад, дар ҳоле ки саршор аз андуҳ шудаанд. [Дар он рӯз] Ситамгарон на дӯсти меҳрубоне доранд ва на шафеъе, ки суханаш пазируфта шавад');
INSERT INTO "pioneers" VALUES(40,19,'40:19','[Аллоҳ таоло] Ҳаракоти бади чашмҳо ва он чиро, ки дар дилҳои афрод нуҳуфта аст, [ба хубӣ] медонад');
INSERT INTO "pioneers" VALUES(40,20,'40:20','Аллоҳ таоло ба адолат доварӣ мекунад ва маъбудоне, ки [мушрикон] ба ҷойи Аллоҳ таоло ибодат мекунанд, ҳеҷ гуна доварӣ надоранд. Ба ростӣ, ки Аллоҳ таоло шунавову биност');
INSERT INTO "pioneers" VALUES(40,21,'40:21','Оё мушрикон дар замин гардиш накардаанд, то бубинанд саранҷоми касоне, ки пеш аз онон буданд, чи гуна будааст? Он ақвоми гузашта нерумандтар аз инҳо буданд ва осору биноҳои бештаре дар замин падид оварданд, аммо Аллоҳ таоло ононро ба [сабаби] гуноҳонашон азоб кард, дар ҳоле ки дар баробари [азоби] Аллоҳ таоло ҳеҷ [ёр ва] мудофеъе надоштанд');
INSERT INTO "pioneers" VALUES(40,22,'40:22','Ин [кайфар] бад-он сабаб буд, ки Паёмбарашон нишонаҳои равшан барояшон ироа мекарданд, вале онон инкор меварзиданд ва Аллоҳ таоло низ азобашон кард. Бе гумон, Ӯ тавонманди сахткайфар аст');
INSERT INTO "pioneers" VALUES(40,23,'40:23','Мо Мӯсоро бо муъҷизоти худ ва далели қотеъ фиристодем');
INSERT INTO "pioneers" VALUES(40,24,'40:24','Ба сӯйи Фиръавн ва Ҳомон ва Қорун, вале [онон] гуфтанд: «[Ӯ] Ҷодугаре дурӯғгӯст»');
INSERT INTO "pioneers" VALUES(40,25,'40:25','Аммо ҳангоме ки Мӯсо муъҷизоти илоҳӣ ва далоили маҳкамро аз ҷониби Мо бар онон нишондод, [Фиръавн ва ёронаш саркашӣ карданд ва] гуфтанд: «Писарони касонеро, ки ба Мӯсо имон овардаанд, бикушед ва занонашонро [барои канизӣ] зинда нигоҳ доред», вале найранги кофирон маҳкум ба фаност');
INSERT INTO "pioneers" VALUES(40,26,'40:26','Фиръавн гуфт: «Бигузоред, то Мӯсоро бикушам ва [чун касеро надорад] ногузир аст Парвардигорашро ба ёрӣ бихонад [ва ман аз вай ҳаросе надорам]. Метарсам, ки ӯ динатонро дигаргун созад ё дар ин сарзамин табоҳӣ ба бор оварад»');
INSERT INTO "pioneers" VALUES(40,27,'40:27','Мӯсо гуфт: «Ман аз [шарри] ҳар мутакаббире, ки ба рӯзи ҳисоб бовар надорад, ба Парвардигори хеш ва Парвардигори шумо паноҳ мебарам»');
INSERT INTO "pioneers" VALUES(40,28,'40:28','Ва марди муъмине аз хонаводаи Фиръавн, ки имонашро пинҳон медошт, гуфт: «Оё мехоҳед ин мардро [фақат ба хотири ин ҷурм] бикушед, ки мегӯяд: «Парвардигорам Аллоҳ таоло аст» ва [дар таъйиди суханаш низ] далоили равшане аз сӯйи Парвардигратон барои шумо овардааст? Агар дурӯғгӯ бошад, [гуноҳи] дурӯғаш бар уҳдаи худи ӯст ва агар рост гуфта бошад, [дасти кам] бархе аз азобҳое, ки ваъда медиҳад, ба шумо хоҳад расид');
INSERT INTO "pioneers" VALUES(40,29,'40:29','Бе тардид, Аллоҳ таоло касеро, ки исрофкору дурӯғгӯст, ҳидоят намекунад. Эй қавми ман, имрӯз фармонравоӣ дар ихтиёри шумост ва дар ин сарзамин султа доред, вале агар сахтгирии Аллоҳ таоло домангирамон шавад, чи касе моро ёрӣ хоҳад кард»? Фиръавн гуфт: «Ман фақат он чиро салоҳ медонам, мегӯям ва шуморо ба роҳи рост ҳидоят мекунам»');
INSERT INTO "pioneers" VALUES(40,30,'40:30','Он марди муъмин гуфт: «Эй қавми ман, метарсам азобе ҳамчун азоби ақвоми пешин [ки аз паёмбаронашон сарпечӣ карданд] бар саратон биёяд');
INSERT INTO "pioneers" VALUES(40,31,'40:31','Монанди шеваи [азоби] қавми Нуҳ ва Оду Самуд ва касоне ки баъд аз онон буданд, дар ҳоле Аллоҳ таоло ситаме барои бандагонаш намехоҳад');
INSERT INTO "pioneers" VALUES(40,32,'40:32','Ва эй қавми ман, бар шумо аз рӯзе метарсам, ки мардум ба фарёд аз якдигар ёрӣ мехоҳанд');
INSERT INTO "pioneers" VALUES(40,33,'40:33','[Ҳамон] Рӯзе, ки ҳамагӣ [аз тарс] ба ақиб боз мегардед ва дар баробари [муҷозоти] Аллоҳ таоло ҳеҷ муҳофизе надоред ва ҳар киро Аллоҳ таоло гумроҳ кунад, ҳеҷ ҳидоятгаре нахоҳад дошт');
INSERT INTO "pioneers" VALUES(40,34,'40:34','Пеш аз ин низ Юсуф бо далоили равшан наздатон омад, аммо шумо аз он чи бароятон оварда буд, пайваста дар шак будед, то замоне, ки аз дунё рафт [ва бар тардидатон афзуда шуд]. Он гоҳ гуфтед: «Аллоҳ таоло ҳаргиз пас аз вай паёмбареро мабъус нахоҳад кард». Аллоҳ таоло исрофкори бадгумонро ин гуна гумроҳ месозад');
INSERT INTO "pioneers" VALUES(40,35,'40:35','Касоне, ки дар мавриди оёти илоҳӣ, бе он ки далеле дошта бошад, ба муҷодала бармехезад, [рафторашон] назди Аллоҳ таоло ва муъминин сахт нописанд аст. Аллоҳ таоло ин гуна бар дили ҳар мутакаббири худкома муҳр [-и ғафлат] мезанад');
INSERT INTO "pioneers" VALUES(40,36,'40:36','Фиръавн [ба вазираш] гуфт: «Эй Ҳомон, барои ман бинои баланде бисоз, шояд ба роҳҳое бирасам');
INSERT INTO "pioneers" VALUES(40,37,'40:37','Роҳҳое дар осмонҳо [ки маро то Арши илоҳӣ бибарад], то ба маъбуди Мӯсо бингарам [ва аз Ӯ огоҳ шавам], ҳарчанд, ман қатъан ӯро дурӯғгӯ мепиндорам». Бад-ин сон зиштии кирдори Фиръавн [дар назарш] ороста гашт ва аз роҳ [-и ҳақ] бозмонд ва найранги Фиръавн ба зиён ва табоҳӣ анҷомид');
INSERT INTO "pioneers" VALUES(40,38,'40:38','Он марди муъмин гуфт: «Эй қавми ман, аз ман пайравӣ кунед, то шуморо ба роҳи рост ҳидоят кунам');
INSERT INTO "pioneers" VALUES(40,39,'40:39','Эй қавми ман, ин зиндагии дунё баҳрае [андак ва зудгузар] аст ва охират сарои ҷовидон аст');
INSERT INTO "pioneers" VALUES(40,40,'40:40','Ҳар ки кори баде муртакиб шавад, ба таносуби он кайфар хоҳад дид ва мардон ва занони муъмин ва некукор ба биҳишт ворид мешаванд ва дар он ҷо аз неъмати бешумор бархӯрдор хоҳанд шуд');
INSERT INTO "pioneers" VALUES(40,41,'40:41','Эй қавми ман, чи шуда, ки ман шуморо ба роҳи наҷот даъват мекунам, вале шумо маро ба оташ фаро мехонед?');
INSERT INTO "pioneers" VALUES(40,42,'40:42','Маро даъват мекунед, ки Аллоҳро инкор кунам ва чизеро, ки [дар қудрат] бо Аллоҳ таоло шарик гардонам, ки ҳеҷ иттилое аз он надорам, дар ҳоле ки ман шуморо ба сӯйи [Парвардигори] шикастнопазир ва омурзгор фаро мехонам');
INSERT INTO "pioneers" VALUES(40,43,'40:43','Ногузир он чи маро бад-он даъват мекунед, дар дунё ва охират [тавони иҷобати] дуоеро надорад ва бозгашти мо ба пешгоҳи Аллоҳ таоло аст ва исрофкорон [дар куфру гуноҳ] дузахӣ ҳастанд');
INSERT INTO "pioneers" VALUES(40,44,'40:44','Ба зудӣ суханони маро ба ёд хоҳед овард ва ман кори худро ба Аллоҳ таоло вогузор мекунам, зеро Аллоҳ таоло нисбат ба [кирдори] бандагон биност»');
INSERT INTO "pioneers" VALUES(40,45,'40:45','Пас, Аллоҳ таоло ӯро аз осеби дасисаи онон ҳифз намуд ва азоби сахт домангири фиръавниён шуд');
INSERT INTO "pioneers" VALUES(40,46,'40:46','Ҳамон оташе, ки [дар қабрҳояшон] бомдоду шомгоҳ бар он арза мешаванд ва рӯзе, ки қиёмат барпо гардад [фармон мерасад]: «Фиръавниёнро дар сахттарин азоб ворид кунед»');
INSERT INTO "pioneers" VALUES(40,47,'40:47','Он гоҳ ки дар оташи дузах кашмакаш [муҷодала] мекунанд, мардумони зердаст ба роҳбарони гумроҳгар мегӯянд: «Мо пайрави шумо будем, оё мумкин аст бахше аз азоби оташро аз мо дур кунед?»');
INSERT INTO "pioneers" VALUES(40,48,'40:48','Роҳбарони гумроҳгар мегӯянд: «Бе тардид, ҳамаи мо дар оташ хоҳем буд. Аллоҳ таоло миёни бандагонаш доварӣ кардааст»');
INSERT INTO "pioneers" VALUES(40,49,'40:49','Ва касоне, ки дар оташанд, ба нигаҳбонони дузах мегӯянд: «Аз Парвардигоратон бихоҳед, ки барои як рӯз ин азобро аз мо бардорад»');
INSERT INTO "pioneers" VALUES(40,50,'40:50','Онон посух медиҳанд: «Магар паёмбаронатон нишонаҳои равшан бар шумо ироа накарданд»? Дузахиён мегӯянд: «Бале». Ва маъмурон мегӯянд: «Пас, [бе натиҷа] дархост кунед, ки дархости кофирон [мардуду] табоҳ аст»');
INSERT INTO "pioneers" VALUES(40,51,'40:51','Бе гумон, Мо паёмбарони худ ва аҳли имонро дар зиндагии дунё ва рӯзе, ки гувоҳон [ба шаҳодат] бармехезанд, ёрӣ мекунем');
INSERT INTO "pioneers" VALUES(40,52,'40:52','Рӯзе, ки узрхоҳии ситамгарон суде ба ҳолашон нахоҳад дошт. Онон аз раҳмати илоҳӣ ба дуранд ва душвории азоби охиратро [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(40,53,'40:53','Мо ба Мӯсо [мақоми] ҳидоят бахшидем ва Бани Исроилро ворисони Таврот қарор додем');
INSERT INTO "pioneers" VALUES(40,54,'40:54','[Китобе, ки] Барои хирадмандон ҳидояту панд аст');
INSERT INTO "pioneers" VALUES(40,55,'40:55','Пас, [эй паёмбар, бар озори мушрикон] шикебо бош, [ки] яқинан, ваъдаи Аллоҳ таоло рост аст ва барои гуноҳат омурзиш бихоҳ ва Парвардигоратро шомгоҳ ва бомдод ба покӣ ситоиш кун');
INSERT INTO "pioneers" VALUES(40,56,'40:56','Касоне, ки дар мавриди оёти илоҳӣ, бе он ки далеле дар ихтиёрашон бошад, ба муҷодала бармехезанд, дар дилҳояшон [ғуруру] такаббурест, ки ҳаргиз бад-он нахоҳанд расид, пас, [аз шарри эшон] ба Аллоҳ таоло паноҳ бибар, ки ба ростӣ, Ӯ шунавову биност');
INSERT INTO "pioneers" VALUES(40,57,'40:57','Мусалламан, офариниши осмонҳо ва замин бузургтар аз офариниши инсонҳост [пас, барпо кардани қиёмат барои Аллоҳ таоло душвор нест], вале бештари мардум намедонанд');
INSERT INTO "pioneers" VALUES(40,58,'40:58','Ҳаргиз нобино ва бино баробар нестанд, ва муъминони накукор бо бадкорон яксон нахоҳанд буд, [вале] иддаи каме аз шумо панд мепазиред');
INSERT INTO "pioneers" VALUES(40,59,'40:59','Ҳеҷ тардиде дар ин нест, ки рӯзи қиёмат фаро мерасад, вале бештари мардум имон намеоваранд');
INSERT INTO "pioneers" VALUES(40,60,'40:60','Парвардигоратон фармуд: «Маро бихонед, то [дуои] шуморо иҷобат кунам. Касоне, ки аз ибодати Ман саркашӣ мекунанд, ба зудӣ бо хорӣ ба ҷаҳаннам ворид мешаванд»');
INSERT INTO "pioneers" VALUES(40,61,'40:61','Аллоҳ таоло аст, ки шабро бароятон падид овард, то дар он оромиш ёбед ва рӯзро равшанибахш [қарор дод, то ба талош пардозед]. Ба ростӣ, Аллоҳ таоло нисбат ба мардум [лутфу] бахшиш дорад, вале бештари мардум сипос намегузоранд');
INSERT INTO "pioneers" VALUES(40,62,'40:62','Ин аст Аллоҳ таоло Парвардигори шумо, [ки] офаринандаи ҳар мавҷуде [аст]. Ҳеҷ маъбуде [ба ҳақ] ҷуз Ӯ нест, пас, чи гуна [аз парастиши Ӯ] мунҳариф мешавед?');
INSERT INTO "pioneers" VALUES(40,63,'40:63','Ба ҳамин сурат касоне, ки оёти илоҳиро инкор мекунанд, низ [аз ҳақ] мунҳариф мегарданд');
INSERT INTO "pioneers" VALUES(40,64,'40:64','Аллоҳ таоло аст, ки заминро қароргоҳ ва осмонро сарпаноҳатон муқаррар дошт ва шуморо шакле бахшид ва шаклҳоятонро зебо биофарид ва аз неъматҳои покиза ба шумо рӯзӣ дод. Ин аст Аллоҳ таоло – Парвардигори шумо, пас, пурбаракату бузургвор аст Аллоҳ таоло, ки Парвардигори ҷаҳониён аст');
INSERT INTO "pioneers" VALUES(40,65,'40:65','Аллоҳ таоло зинда [ва поянда] аст ва маъбуде [ба ҳақ] ҷуз Ӯ нест, пас, Ӯро бо хулуси эътиқод бихонед. Ситоиш ва сипос махсуси Аллоҳ таоло-Парвардигори ҷаҳониён аст');
INSERT INTO "pioneers" VALUES(40,66,'40:66','[Эй паёмбар] Бигӯ: «Ман аз парастиши афроде, ки шумо ба ҷойи Аллоҳ таоло [ба ниёиш] мехонед, наҳй шудаам, зеро нишонаҳои равшане аз ҷониби Парвардигорам ба ман расидааст ва фармон ёфтаам, ки [танҳо] дар баробари Парвардигори ҷаҳониён таслим бошам»');
INSERT INTO "pioneers" VALUES(40,67,'40:67','Ӯст, ки шуморо аз хок ва сипас [тайи мароҳили мухталиф] аз нутфа ва он гоҳ хуни баста [ҷанини ибтидоӣ] офарид, сипас шуморо [ба сурати] кӯдаке [ба дунё] меоварад ва имкон медиҳад, то ба ҳадди рушд [ва камоли неру] бирасед. Сипас [солҳо зиндагӣ мекунед, то] солхӯрда мешавед. Бархе аз шумо дучори марги зудрас хоҳанд шуд ва [ба гурӯҳе дигар имкон медиҳад], то ба аҷали муайян бирасед, бошад, ки биандешед');
INSERT INTO "pioneers" VALUES(40,68,'40:68','Ӯст, ки зинда мекунад ва мемиронад ва ҳангоме ки чизеро мехоҳад, танҳо ба ӯ мегӯяд: «Мавҷуд шав», пас, [бедиранг] мавҷуд мешавад');
INSERT INTO "pioneers" VALUES(40,69,'40:69','Оё надидӣ касоне, ки дар бораи оёти илоҳӣ муҷодала мекунанд, чи гуна [аз роҳи ҳақ] мунҳариф мегарданд?');
INSERT INTO "pioneers" VALUES(40,70,'40:70','Касоне, ки китоби [осмонӣ] ва паёми паёмбарони моро дурӯғ шумурданд, ба зудӣ [натиҷаи кирдорашонро] хоҳанд донист');
INSERT INTO "pioneers" VALUES(40,71,'40:71','Он гоҳ ки ғул ва занҷир бар гарданашон қарор гирифта ва кашида мешаванд');
INSERT INTO "pioneers" VALUES(40,72,'40:72','[Ибтидо] Дар оби ҷӯшомада ва сипас дар оташи дузах афрӯхта мешаванд');
INSERT INTO "pioneers" VALUES(40,73,'40:73','Он гоҳ ба эшон гуфта мешавад: «Бутҳое, ки шарики [Аллоҳ таоло] қарор медодед, куҷо ҳастанд?');
INSERT INTO "pioneers" VALUES(40,74,'40:74','[Ҳамон маъбудоне, ки] ба ҷойи Аллоҳ таоло [ибодат мекардед]. Онон гӯянд: «Аз [назари] мо нопадид шуданд, гӯӣ мо ҳаргиз пеш аз ин чизеро [ба парастиш] намехондем». Ин гуна Аллоҳ таоло кофиронро гумроҳ мекунад');
INSERT INTO "pioneers" VALUES(40,75,'40:75','Ин [азоб] аз он ҷиҳат аст, ки ба ноҳақ дар замин шодмонӣ ва сармастӣ мекардед ва фахр мефурӯхтед');
INSERT INTO "pioneers" VALUES(40,76,'40:76','[Акнун] Аз дарҳои ҷаҳаннм ворид шавед ва ҷовидона дар он ҷо бимонед. Ва [ба ростӣ, ки] ҷойгоҳи мутакаббирон чи бад [ҷойгоҳе] аст!');
INSERT INTO "pioneers" VALUES(40,77,'40:77','[Эй паёмбар, бар озори мушрикон] Шикебо бош, ки ваъдаи илоҳӣ ҳақ аст ва агар бархе аз муҷозотҳоеро, ки ба онон ваъда додаем, [дар зиндагӣ] ба ту бинамоем ё [пеш аз нузули азоб] ҷони туро бигирем, дар ҳар ҳол ба пешгоҳи мо бозгардонда мешаванд [ва роҳи гурезе аз азоб надоранд]');
INSERT INTO "pioneers" VALUES(40,78,'40:78','Пеш аз ту низ паёмбароне фиристодаем, ки [саргузашти] бархе аз эшонро бароят ҳикоят кардаем ва бархе дигарро ҳикоят накардаем ва ҳеҷ расуле наметавонист бидуни фармони илоҳӣ муъҷизае ироа кунад ва он гоҳ ки фармони Аллоҳ таоло [барои подош ва кайфар] фаро расад, ба ҳақ ва адолат доварӣ хоҳад шуд ва ботиландешон зиёнкор хоҳанд буд');
INSERT INTO "pioneers" VALUES(40,79,'40:79','Аллоҳ таоло аст, ки чаҳорпоёнро бароятон офарид, то аз бархе барои саворӣ ва аз бархе барои тағзия истифода кунед');
INSERT INTO "pioneers" VALUES(40,80,'40:80','Ва барои шумо дар онҳо судҳои дигаре низ ҳаст ва низ барои он ки савор бар онҳо ба мақсаде, ки дар дил доред, бирасед ва бар онҳо ва бар киштиҳо [ба ҳар ҷо ки мехоҳед] мунтақил мешавед');
INSERT INTO "pioneers" VALUES(40,81,'40:81','Аллоҳ таоло нишонаҳои [қудрату тадбири] худро ба шумо нишон медиҳад, пас, кадом як аз нишонаҳои илоҳиро инкор мекунед?');
INSERT INTO "pioneers" VALUES(40,82,'40:82','Оё мушрикон дар замин гардиш накардаанд, то бубинанд саранҷоми касоне, ки пеш аз онон буданд, чи гуна будааст? Он ақвоми гузашта шуморашон аз инҳо афзунтар буд ва осору биноҳои бештаре дар замин падид оварда буданд, аммо [ ҳангоми нузули азоб] дастовардашон суде ба ҳолашон надошт');
INSERT INTO "pioneers" VALUES(40,83,'40:83','Ҳангоме ки паёмбаронашон бо нишонаҳои равшан ба суроғашон омаданд, ба донишу кордонии худ сармаст [ва аз тавҳид рӯйгардон] шуданд ва ҳамон азобе, ки масхарааш мекарданд, домангирашон шуд');
INSERT INTO "pioneers" VALUES(40,84,'40:84','Пас, чун азоби Моро диданд, гуфтанд: «Танҳо ба Аллоҳ таоло имон овардем ва маъбудонеро, ки шарики Ӯ қарор дода будем, инкор мекунем»');
INSERT INTO "pioneers" VALUES(40,85,'40:85','Аммо имон оварданашон ба ҳангоми мушоҳидаи азоб ҳеҷ суде надошт. Ин суннати илоҳӣ [ки дар ҳангоми азоб тавбаи касе пазируфта намегардад, ҳамвора] дар мавриди бандагони Аллоҳ таоло ҷорӣ будааст ва дар ин ҷо кофирон зиёнкор шуданд');
INSERT INTO "pioneers" VALUES(41,1,'41:1','Ҳо, мим');
INSERT INTO "pioneers" VALUES(41,2,'41:2','[Ин Қуръон] Аз сӯи Аллоҳи бахшандаи меҳрубон нозил шудааст');
INSERT INTO "pioneers" VALUES(41,3,'41:3','Китобест, ки оёташ ба забони арабӣ барои мардуме, ки медонанд, ба шевоӣ ва равшанӣ баён шудааст');
INSERT INTO "pioneers" VALUES(41,4,'41:4','[Китобе] Башоратдиҳанда ва бимдиҳанда аст, вале бештари мардум рӯйгардонанд ва [ҳақоиқро] намешунаванд');
INSERT INTO "pioneers" VALUES(41,5,'41:5','Мушрикон гуфтанд: «Дилҳои мо нисбат ба он чи моро даъват мекунӣ, дар пӯшише [аз бетафовутӣ] қарор дорад ва дар гӯшҳоямон сангинӣ аст ва миёни мо ва ту фосила аст, пас, ту ба кори худ машғул бош ва мо низ ба кори худ»');
INSERT INTO "pioneers" VALUES(41,6,'41:6','[Эй паёмбар, ба онон] бигӯ: «Ман фақат башаре ҳамчун шумо ҳастам [бо ин тафовут], ки ба ман ваҳй мешавад: «Маъбуди шумо маъбудест ягона, пас, ба Ӯ рӯй оваред ва аз Ӯ омурзиш бихоҳед ва вой ба ҳоли мушрикон!»');
INSERT INTO "pioneers" VALUES(41,7,'41:7','Онон, ки закот намепардозанд ва охиратро инкор мекунанд');
INSERT INTO "pioneers" VALUES(41,8,'41:8','[Вале] Муъминони некукор подоше бепоён доранд');
INSERT INTO "pioneers" VALUES(41,9,'41:9','Бигӯ: Оё он [Холиқи тавонманд]- еро, ки заминро дар ду рӯз офарид, инкор мекунед ва барояш ҳамтоёне қоил мешавед? Ин [Офаридгори яктост, ки] Парвардигори ҷаҳониён аст');
INSERT INTO "pioneers" VALUES(41,10,'41:10','Ӯ тайи чаҳор рӯз бар рӯйи замин кӯҳҳои устуворро падид овард ва дар он хайру баракати фаровон ниҳод ва ризқу рӯзии онро дуруст ба андозаи ниёзи тақозокунандагон муқаддар намуд');
INSERT INTO "pioneers" VALUES(41,11,'41:11','Он гоҳ ба осмон пардохт, дар ҳоле ки [ҳанӯз] ба сурати газ [дуд] буд. Ва ба осмону замин гуфт: «Хоҳ- нохоҳ аз дари таслим дароед». Он ду гуфтанд: «Бо майл аз дари таслим даромадем»');
INSERT INTO "pioneers" VALUES(41,12,'41:12','Сипас он тӯдаи газро ба сурати ҳафт осмон дар ду рӯз офарид ва ба ҳар осмоне барномаашро ваҳй кард ва осмони дунёро бо чароғҳое [аз ситорагон] оростем ва [аз шарри шаётин] ҳифз намудем. Ин аст тақдири [Парвардигори] шикастнопазири доно»');
INSERT INTO "pioneers" VALUES(41,13,'41:13','[Эй паёмбар] Агар онон [аз пазириши ислом] рӯй гардонанд, бигӯ: «Ба шумо дар бораи соиқае назири [он чи] қавми Оду Самуд [-ро аз пой даровард] ҳушдор медиҳам»');
INSERT INTO "pioneers" VALUES(41,14,'41:14','Он гоҳ ки паёмбарони илоҳӣ паёпай ба суроғашон меомаданд, [бо ин паём] ки: «Ҷуз Аллоҳро напарастед», онон [дар посух] мегуфтанд: «Агар Парвардигорамон мехост, [паёми ҳидоятгаре бифиристад] фариштагоне нозил мекард. Мо паёмеро, ки шумо маъмури [иблоғи] он ҳастед, бовар намекунем»');
INSERT INTO "pioneers" VALUES(41,15,'41:15','Аммо [қавми] Од ба ноҳақ дар замин такаббур меварзиданд ва мегуфтанд: «Чи касе нерумандтар аз мост»? Оё намедонистанд Аллоҳ таоло [он Холиқи якто]-е, ки ононро офарида, нерумандтар аз онон аст? Вале [бо ситезу гарданкашӣ] оёти Моро инкор мекарданд');
INSERT INTO "pioneers" VALUES(41,16,'41:16','Саранҷом тундбоде сарду сахт дар рӯзҳое шум ба онон фиристодем, то азоби расвоӣ [ва сангин]-ро дар зиндагии дунё ба онон бичашонем, вале азоби охират расвокунандатар аст ва [аз ҳеҷ сӯ] ёрӣ нахоҳанд шуд');
INSERT INTO "pioneers" VALUES(41,17,'41:17','Ва аммо қавми Самудро низ ҳидоят кардем, вале онон кӯрдилиро ба ҳидоят тарҷеҳ доданд ва ба [сазои] дастовардашон соиқаи азоби хоркунанда ононро фаро гирифт');
INSERT INTO "pioneers" VALUES(41,18,'41:18','Ва касонеро, ки имон оварда ва [аз гуноҳу нофармонӣ] парво мекарданд, наҷот додем');
INSERT INTO "pioneers" VALUES(41,19,'41:19','[Эй паёмбар, ёд кун аз] Рӯзе, ки душманони Аллоҳро ба сӯйи оташи дузах эҳзор мекунанд ва [аз парокандагӣ] бозмедоранд');
INSERT INTO "pioneers" VALUES(41,20,'41:20','То чун ба дузах мерасанд, гӯшу чашму пусташон аъмолашонро гувоҳӣ медиҳанд');
INSERT INTO "pioneers" VALUES(41,21,'41:21','Онон ба пӯсти худ мегӯянд: «Чаро алайҳи мо гувоҳӣ додед»? [Дар ҷавоб] Мегӯянд: «Аллоҳ таоло [ҳамон Холиқи тавонманде], ки ҳама мавҷудотро гӯё карда, моро низ ба сухан даровардааст ва Ӯст, ки нахустин бор шуморо офарид ва [саранҷом ҳамагӣ] ба пешгоҳи Ӯ бозгардонда мешавед»');
INSERT INTO "pioneers" VALUES(41,22,'41:22','Шумо, ки [гуноҳонатонро] пинҳон намекардед, на аз он бобат буд, ки тасаввур мекардед, гӯшу чашму пӯстатон алайҳи шумо гувоҳӣ намедиҳанд, балки гумон мекардед, ки Аллоҳ таоло бар бисёре аз аъмоле, ки мекунед, огоҳ нест');
INSERT INTO "pioneers" VALUES(41,23,'41:23','Ин пиндори шумо дар бораи Парвардигоратон буд, ки муҷиби ҳалокати шумо гардид ва [саранҷом] зиёнкор шудед');
INSERT INTO "pioneers" VALUES(41,24,'41:24','Агар шикебоӣ ва таҳаммул кунанд [ё накунанд], оташ ҷойгоҳашон аст ва агар талаби ризоият кунанд, пазируфта нахоҳанд шуд');
INSERT INTO "pioneers" VALUES(41,25,'41:25','Мо ҳамнишиноне [аз шаётин] бар онон гумоштем, ки [корҳои] гузашта ва [орзуҳои] ояндаро дар назарашон оростаанд ва [бад-ин сон] фармони муҷозот дар мавриди онон таҳаққуқ ёфт ва ба азоби ақвоми гумроҳе аз ҷину инс, ки пеш аз онон буданд, гирифтор шуданд. Бе гумон, онон зиёнкор буданд');
INSERT INTO "pioneers" VALUES(41,26,'41:26','Кофирон [ки дар баробари ҳақ оҷиз гашта буданд] гуфтанд: «Ба ин Қуръон гӯш накунед ва [ҳангоми тиловати] он ҷанҷол кунед [ва суханони беҳуда бигӯед], бошад, ки пирӯз гардед»');
INSERT INTO "pioneers" VALUES(41,27,'41:27','Пас, яқинан, ба кофирон азоби сахте мечашонем ва ононро бар асоси [ҷазои] бадтарини корҳояшон кайфар медиҳем');
INSERT INTO "pioneers" VALUES(41,28,'41:28','Ин аст сазои душманони Аллоҳ таоло: оташе, ки дар он сукунати ҷовидон доранд, ба кайфари он ки оёти Моро инкор мекарданд');
INSERT INTO "pioneers" VALUES(41,29,'41:29','Кофирон [ба ҳангоми азоб] мегӯянд: «Парвардигоро, [афроде] аз ду гурӯҳ: ҷин ва инсро, ки омили гумроҳиямон буданд, ба мо нишон бидеҳ, ки лагадкӯбашон кунем, то аз ҳама залилтар шаванд»');
INSERT INTO "pioneers" VALUES(41,30,'41:30','[Аммо] Касоне, ки гуфтанд: «Парвардигори мо Аллоҳ таоло аст», он гоҳ [аз бандагии ғайри Ӯ сар боз заданд ва бар эътиқоди худ] пой фишурданд, фариштагон бар онон фуруд меоянд [ва мегӯянд]: «Натарсед ва андуҳ надошта бошед ва башорат бод бар шумо он биҳишти мавъудатон!');
INSERT INTO "pioneers" VALUES(41,31,'41:31','Мо дар зиндагии дунё ва охират дӯстдори шумоем ва дар биҳишт ҳар чи дилатон бихоҳад ва дархост кунед, дар ихтиёри шумост');
INSERT INTO "pioneers" VALUES(41,32,'41:32','Ба унвони пазироӣ аз ҷониби омурзгоре меҳрубон»');
INSERT INTO "pioneers" VALUES(41,33,'41:33','Кист хушгуфтортар аз он ки ба сӯйи Аллоҳ таоло даъват кунад ва ба шоистагӣ амал менамояд ва мегӯяд: «Таслим [-и авомири илоҳӣ] ҳастам»?');
INSERT INTO "pioneers" VALUES(41,34,'41:34','[Ҳаргиз] Некӣ ва бадӣ яксон нест. Ҳамвора ба шевае, ки беҳтар аст, посух бидеҳ, ки дар он сурат касе, ки байни ту ва ӯ душманист, ҳамчун дӯсти меҳрубон гардад');
INSERT INTO "pioneers" VALUES(41,35,'41:35','Танҳо шикебоён ва онон, ки баҳраи бузурге [аз имону ахлоқ] доранд, ба чунин мақоме мерасанд');
INSERT INTO "pioneers" VALUES(41,36,'41:36','Ва ҳар гоҳ аз ҷониби шайтон васвасае туро фаро гирифт, ба Аллоҳ таоло паноҳ бибар, ки яқинан, Ӯ шунавову доност');
INSERT INTO "pioneers" VALUES(41,37,'41:37','Аз нишонаҳои [тавону тадбири] Ӯ шабу рӯз ва хуршеду моҳ аст. Дар баробари хуршеду моҳ саҷда накунед, балки агар танҳо Ӯро мепарастед, бар Аллоҳ таоло, ки онҳоро офаридааст, саҷда кунед');
INSERT INTO "pioneers" VALUES(41,38,'41:38','Ва агар [гурӯҳҳое аз ниёиши Ӯ] такаббур варзанд, [фариштагон ва] муқаррабони даргоҳи Парвардигорат шабу рӯз бидуни хастагӣ Ӯро ба покӣ меситоянд');
INSERT INTO "pioneers" VALUES(41,39,'41:39','Аз ҷумлаи нишонаҳои [қудрати] илоҳӣ ин аст, ки замини хушку фарсударо мебинӣ, ки чун борон бар он фурӯ мерезем, ба ҷунбиш дармеояд ва [гиёҳонаш] рушд мекунад. Он [Парвардигори тавонманде], ки заминро зинда мекунад, мурдагонро низ зинда хоҳад кард. Ба ростӣ, ки Ӯ бар ҳар коре тавоност');
INSERT INTO "pioneers" VALUES(41,40,'41:40','[Гуфтору рафтори] Касоне, ки дар мавриди оёти Мо каҷравӣ [ва онро таҳриф] мекунанд, бар Мо пӯшида нахоҳанд буд. Оё касе, ки ба оташи [дузах] меафтад, беҳтар аст ё он ки дар рӯзи растохез эмин [аз азоб] аст? Ҳар чи мехоҳед, амал кунед, ки бе тардид, Аллоҳ таоло ба он чи мекунед, биност');
INSERT INTO "pioneers" VALUES(41,41,'41:41','Касоне, ки ин Қуръонро, ки барои [ҳидоят] – ашон омадааст, инкор кардаанд [дар қиёмат азоб хоҳанд шуд]. Ин [каломи илоҳӣ] китобе аст шикастнопазир');
INSERT INTO "pioneers" VALUES(41,42,'41:42','Сухани ботил аз пешу пас ба он роҳ намеёбад ва аз ҷониби [Парвардигори] ҳакими сутуда нозил шудааст');
INSERT INTO "pioneers" VALUES(41,43,'41:43','[Эй паёмбар, кофирон] Ба ту ҳамон [суханони ботиле] –ро мегӯянд, ки ба паёмбарони пеш аз ту низ мегуфтанд. Бе гумон, Парвардигорат омурзиш ва [дар айни ҳол] кайфаре дарднок дорад');
INSERT INTO "pioneers" VALUES(41,44,'41:44','Агар Қуръонро [ба забони] ғайриарабӣ нозил карда будем, кофирон мегуфтанд: «Чаро оёташ [арабӣ нест ва] ба равшанӣ баён нашудааст? Китобе ғаёриарабиву паёмбаре араб»? [Эй паёмбар, ба онон] Бигӯ: «Ин китоб барои муъминон мояи ҳидояту шифост ва онон, ки имон намеоваранд, дар гӯшҳояшон сангинӣ аст ва Қуръон барояшон номафҳум аст. [Гӯё] Ононро аз роҳи дур садо мезананд»');
INSERT INTO "pioneers" VALUES(41,45,'41:45','Ва ба ростӣ, [Мо] ба Мӯсо китоб додем, пас, дар он ихтилоф шуд.Ва агар пештар [дар мавриди муҳлат ба гунаҳгорон ва ҳисобрасӣ дар қиёмат] сухане аз ҷониби Парвардигорат баён нашуда буд, яқинан, [дар ҳамин дунё] миёнашон доварӣ мешуд [ва азоб нозил мегашт] ва ба ростӣ, ки онон [яҳудиён ва мушрикон] дар бораи [ҳаққонияти] ин [Қуръон] сахт дар тардиданд');
INSERT INTO "pioneers" VALUES(41,46,'41:46','Ҳар ки ба шоистагӣ амал кунад, ба суди худи ӯст ва ҳар ки бадӣ кунад, ба зиёни хеш бад кардааст ва Парвардигори ту ҳаргиз ба бандагон ситам намекунад');
INSERT INTO "pioneers" VALUES(41,47,'41:47','Огоҳӣ аз [лаҳзаи вуқуи] қиёмат танҳо марбут ба Аллоҳ таоло аст ва хуруҷи ҳар мевае аз шукуфааш ва бордориву вазъи ҳамли ҳар моддае бо огоҳии Ӯст ва он рӯз, ки Аллоҳ таоло ба мушрикон нидо медиҳад, ки: «Афроде, ки [дар қудрат] шарики Ман тасаввур мекардед, [ва ба он тавассул меҷустед] куҷо ҳастанд»? Онон мегӯянд: «Акнун дар баробари ту эътироф мекунем, ки ҳеҷ як аз мо [ба дурустии эътиқоди мушриконаи худ] гувоҳ нест»');
INSERT INTO "pioneers" VALUES(41,48,'41:48','Он чи дар гузашта [такягоҳи худ] мехонданд, аз назарашон нопадид мешавад ва дармеёбанд, ки [аз оташи азоби илоҳӣ] ҳеҷ роҳи гурезе нахоҳанд дошт');
INSERT INTO "pioneers" VALUES(41,49,'41:49','Инсон дар таманнои хайр хастанопазир аст, ва [-ле] агар бадӣ ба ӯ бирасад, маъюсу дилсард хоҳад шуд');
INSERT INTO "pioneers" VALUES(41,50,'41:50','Ва агар пас аз ранҷе, ки ба инсон мерасад, раҳмате аз ҷониби хеш ба ӯ бирасонем, [мағрурона] мегӯяд: «Ин ҳаққи ман аст ва гумон намекунам қиёмат барпо шавад ва агар [ҳам бошад ва] ба сӯйи Парвардигорам бозгардонда шавам, назди Ӯ [сарвату подошҳоии] некуе хоҳам дошт». Мусаламан, кофиронро аз амалкардашон огоҳ хоҳем сохт ва азобе сахт ба онон мечашонем');
INSERT INTO "pioneers" VALUES(41,51,'41:51','Ҳар гоҳ ба инсон неъмате мебахшем, [бо сармастӣ] рӯй мегардонад ва мутакаббирона [аз ҳақ] дур мешавад ва ҳангоме ки ранҷе [ҳамчун фақру беморӣ] ба суроғаш меояд, [ба даргоҳи илоҳӣ] дуо ва дархости фаровон дорад');
INSERT INTO "pioneers" VALUES(41,52,'41:52','[Эй паёмбар, ба кофирон] Бигӯ: «Ба ман бигӯед, [агар Қуръон] аз ҷониби Аллоҳ таоло [расида] бошад ва шумо инкораш кунед, кист гумроҳтар аз он ки [бо вуҷуди ошкор будани ҳақ] дар ситези бепоён ба он қарор дорад?»');
INSERT INTO "pioneers" VALUES(41,53,'41:53','[Дар ин дунё ва ҳангомаи қиёмат] нишонаҳои [тавону тадбири] худро дар офоқи [ҷаҳон] ва дар [вуҷуди] худашон ба онон нишон хоҳем дод, то барояшон равшан шавад, ки Қуръон ҳақ аст. Оё кофӣ нест, ки Парвардигорат бар ҳар чизе гувоҳ аст?');
INSERT INTO "pioneers" VALUES(41,54,'41:54','Огоҳ бошед, ки онон дар мавриди дидори Парвардигорашон [дар қиёмат] тардид доранд! [Ва] Огоҳ бошед, ки Аллоҳ таоло бар ҳар чизе иҳота дорад!');
INSERT INTO "pioneers" VALUES(42,1,'42:1','Ҳо, мим');
INSERT INTO "pioneers" VALUES(42,2,'42:2','Айн, син қоф');
INSERT INTO "pioneers" VALUES(42,3,'42:3','Аллоҳи шикастнопазири ҳаким бар ту ва паёмбарони пеш аз ту чунин ваҳй мефиристад');
INSERT INTO "pioneers" VALUES(42,4,'42:4','Ҳар чи дар осмонҳо ва замин аст, мутааллиқ ба Ӯст ва Ӯ баландмартаба ва бузург аст');
INSERT INTO "pioneers" VALUES(42,5,'42:5','Наздик аст осмонҳо [аз шукӯҳу азамати илоҳӣ] аз фарози якдигар бишкофанд ва фариштагон Парвардигорашонро бо ситоиш тақдис мекунанд ва барои сокинони замин [ки тавба кардаанд] омурзиш металабанд. Огоҳ бошед, ки Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(42,6,'42:6','[Эй паёмбар] Аллоҳ худ муроқиби [рафтори] касонест, ки ғайри Ӯро [дӯсту] корсози худ баргузидаанд ва ту коргузор [ва масъули рафтор]-и онон нестӣ');
INSERT INTO "pioneers" VALUES(42,7,'42:7','Мо Қуръонро ин гуна ба забони арабӣ бар ту ваҳй кардем, то аҳли Макка ва сокинони атрофашро бим диҳӣ ва ба онон дар мавриди рӯзи маҳшар, ки тардиде дар вуқуаш нест, ҳушёр диҳӣ [Он рӯз] гурӯҳе биҳиштианд ва гурӯҳе дар оташи сӯзон');
INSERT INTO "pioneers" VALUES(42,8,'42:8','Агар Аллоҳ таоло мехост, ҳамаи мардумро уммати воҳид [ва яктопараст] қарор медод, вале [ба инсон ихтиёр дод, пас] ҳар киро бихоҳад, ба раҳмати хеш дармеоварад; ва мушрикон ҳеҷ корсоз ва ёваре нахоҳанд дошт');
INSERT INTO "pioneers" VALUES(42,9,'42:9','Оё онон ба ҷойи Ӯ каси [дигарро ба унвони дӯсту] корсоз баргузидаанд? Ҳол он ки корсоз [-и ҷаҳониён] Аллоҳ таоло аст ва Ӯст, ки мурдагонро зинда мекунад ва бар ҳар коре тавоност');
INSERT INTO "pioneers" VALUES(42,10,'42:10','[Бигӯ: «Эй мардум] Ҳар ихтилофе байни шумост, довариаш бо Аллоҳ таоло аст. Чунин аст Аллоҳ таоло Парвардигори ман. Бар Ӯ таваккал кардам ва ба пешгоҳаш бозмеоям»');
INSERT INTO "pioneers" VALUES(42,11,'42:11','Ӯ офаринандаи осмонҳо ва замин аст. Барои шумо ҳамсароне аз [ҷинси] худатон қарор дод ва аз чаҳорпоён низ ҷуфт офарид, бад-ин сурат, теъдоди шуморо афзоиш медиҳад. Ҳеҷ чиз ҳаммонанди Аллоҳ таоло нест ва Ӯ шунаво ва биност');
INSERT INTO "pioneers" VALUES(42,12,'42:12','Калиди [ганҷинаи] осмонҳо ва замин дар ихтиёри Ӯст. [Неъмату] Рӯзиро бар ҳар кас, ки бихоҳад, густурда медорад ва ё танг мегирад. Ба ростӣ, ки Ӯ бар ҳар чизе доност');
INSERT INTO "pioneers" VALUES(42,13,'42:13','Аз дин ҳамонро барои шумо муқаррар кард, ки ба Нуҳ амр карда буд ва низ он чи ба ту ваҳй кардем ва он чи ба Иброҳим ва Мӯсо ва Исо амр кардем, ин ки: «Динро барпо нигоҳ доред ва дар мавриди он фирқа-фирқа нашавед [ва ихтилоф наварзед]». [Пазириши] Даъвати ту бар мушрикон гарон омадааст. Аллоҳ таоло аст, ки ҳар касро бихоҳад [тавфиқи ибодат медиҳад ва] ба сӯйи хеш бармегузинад ва ҳар киро [ки тавба кунад ва ба даргоҳаш] боз ояд, ба сӯйи хеш ҳидоят хоҳад кард');
INSERT INTO "pioneers" VALUES(42,14,'42:14','Ҳангоме ки далоили маҳками нубуввати паёмбар бар кофирон арза шуд, онон аз рӯйи бартарихоҳӣ [ва ҳасодат]-е ки байнашон буд, роҳи тафриқа тай намуданд ва агар фармони Парвардигорат [дар мавриди муҳлат ва таъхири азоб] то замоне муайян аз пеш муқаррар нашуда буд, [бе диранг] миёнашон доварӣ мешуд ва аммо пас аз кофирон [ва ба далели инкору ихтилофи онон] ворисони китоб [Таврот ва Инҷил низ] дар мавриди Қуръон сахт дар тардид ва бадгумонӣ ҳастанд');
INSERT INTO "pioneers" VALUES(42,15,'42:15','[Эй паёмбар, мардумро] ба сӯйи ин [оини илоҳӣ] даъват кун ва чунон ки фармон ёфтаӣ, пойдорӣ кун ва аз ҳавасҳои онон пайравӣ накун ва бигӯ: «Ба ҳамаи китобҳое, ки Аллоҳ таоло нозил кардааст имон дорам ва фармон ёфтаам, ки дар барорбари шумо ба адолат рафтор кунам. Аллоҳ таоло Парвардигори мову шумост. [натиҷаи] аъмоли мо барои мост ва [натиҷаи] аъмоли шумо барои шумо. Ҳеҷ [душманиву] ҷидоле байни мову шумо нест. Аллоҳ таоло ҳамаи моро [дар қиёмат] ҷамъ хоҳад кард ва бозгашти [ҳама] ба сӯи Ӯст»');
INSERT INTO "pioneers" VALUES(42,16,'42:16','Касоне, ки пас аз пазириши умумӣ, дар бораи [ҳаққонияти дини] Аллоҳ таоло далел меоваранд, мантиқашон назди Парвардигорашон [ботил ва ] бепоя аст. Хашм [-и илоҳӣ] гиребонгирашон мешавад ва азобе сахт [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(42,17,'42:17','Аллоҳ таоло аст, ки Қуръон ва миқёси адолатро ба ҳақ нозил кардааст ва ту чи медонӣ? Шояд қиёмат наздик бошад');
INSERT INTO "pioneers" VALUES(42,18,'42:18','Касоне, ки қиёматро бовар надоранд, [бо тамасхур ва] ба шитоб онро мехоҳанд. Ва касоне, ки [ба рӯзи ҷазо] бовар доранд ва медонанд, ки ҳақ аст, аз он дар ҳаросанд. Огоҳ бошед! Онон, ки дар мавриди қиёмат ҷадалу инкор мекунанд, дар гумроҳии бепоёне қарор доранд');
INSERT INTO "pioneers" VALUES(42,19,'42:19','Аллоҳ таоло нисбат ба бандагонаш борикбин [ва меҳрубон] аст. Ба ҳар кас, ки бихоҳад, рӯзӣ мебахшад ва Ӯ тавонои шикастнопазир аст');
INSERT INTO "pioneers" VALUES(42,20,'42:20','Касе, ки кишти охиратро бихоҳад, кишташро баракат мебахшем ва ҳар ки [танҳо] кишти дунёро бихоҳад, дар ихтиёраш қарор медиҳем, вале дар охират баҳрае нахоҳад дошт');
INSERT INTO "pioneers" VALUES(42,21,'42:21','Оё [мушрикон] маъбудоне доранд, ки бидуни иҷозати Аллоҳ таоло [қавонини] дине барои онон муқаррар кардаанд? Агар ҳукми қатъӣ [барои муҳлат додан ба гунаҳкорон] дар миён набуд, [бе диранг] миёнашон доварӣ мешуд ва ситамгорон азобе дарднок [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(42,22,'42:22','[Дар рӯзи қиёмат] ситамгоронро аз дастовардашон ҳаросон мебинӣ ва [хоҳӣ дид, ки кайфари он корҳо] домангирашон аст, вале муъминони накукор дар боғҳои биҳиштанд, ҳар чи бихоҳанд, назди Парвардигорашон доранд. Фазлу бахшиши бузург ҳамин аст');
INSERT INTO "pioneers" VALUES(42,23,'42:23','Ин ҳамон [фазилате] аст, ки Аллоҳ таоло ба бандагони муъмин ва некукораш башорат медиҳад. [Эй паёмбар] Бигӯ: «Дар баробари ин рисолат, подоше аз шумо намехоҳам, ҷуз [риояи дӯстӣ ва] муҳаббати хешовандӣ ва ҳар ки [дар ин росто] иқдоми некуе кунад, бар подошаш дар он [маврид] меафзоем. Ба ростӣ, ки Аллоҳ таоло омурзгори қадршинос аст');
INSERT INTO "pioneers" VALUES(42,24,'42:24','Оё мушрикон мегӯянд: «[Муҳаммад] ба Аллоҳ таоло дурӯғ нисбат додааст»? Агар Аллоҳ таоло бихоҳад, бар дили ту меҳр мениҳад, ва [-ле] Аллоҳ таоло ботилро маҳв мекунад ва ҳақро бо суханони [ва тамҳидоти] хеш ба курсӣ менишонад, ки Ӯ ба рози дилҳо доност');
INSERT INTO "pioneers" VALUES(42,25,'42:25','Ӯст, ки тавбаи бандагонашро мепазирад ва аз бадиҳо дармегузарад ва он чи мекунед, медонад');
INSERT INTO "pioneers" VALUES(42,26,'42:26','Ӯ [дархости] муъминони некукорро мепазирад ва аз фазли хеш бар [подоши] онон меафзояд ва кофирон азобе сахт дар пеш доранд');
INSERT INTO "pioneers" VALUES(42,27,'42:27','Агар Аллоҳ таоло рӯзии [бархе аз] бандагонашро кушоиш медод, дар замин ситаму саркашӣ мекарданд, вале ба андозае, ки бихоҳад муқаррар медорад, ки Ӯ ба [ҳоли] бандагонаш огоҳ ва биност');
INSERT INTO "pioneers" VALUES(42,28,'42:28','Ӯст, ки боронро пас аз навмедӣ [-и мардум] мефиристад ва раҳмати хешро мегустарад ва Ӯ корсозу сутуда аст');
INSERT INTO "pioneers" VALUES(42,29,'42:29','Аз нишонаҳои [тавону тадбири] Ӯ офариниши осмонҳо ва замину мавҷудоти зиндаест, ки дар онҳо пароканда кардааст ва ҳар гоҳ ирода кунад, бар ҷамъоварии онҳо низ тавоност');
INSERT INTO "pioneers" VALUES(42,30,'42:30','Ҳар мусибате, ки ба шумо расад, ба хотири корҳоест, ки анҷом додаед ва [Аллоҳ таоло] аз бисёре [аз гуноҳонатон] гузашт мекунад');
INSERT INTO "pioneers" VALUES(42,31,'42:31','Шумо ҳаргиз дар замин [ва осмон Аллоҳро] ба танг намеоваред ва дар баробари Аллоҳ таоло хеҷ корсозу ёваре нахоҳед дошт');
INSERT INTO "pioneers" VALUES(42,32,'42:32','Аз нишонаҳои [тавону тадбири] илоҳӣ, киштиҳои кӯҳпайкарест, ки дар дарёҳо равонанд');
INSERT INTO "pioneers" VALUES(42,33,'42:33','Агар Аллоҳ таоло бихоҳад, бодро сокин мегардонад, то [киштиҳо] бар паҳнаи дарё беҳаракат бимонанд. Ба ростӣ, дар ин [амр] барои ҳар шикебои сипосгузор нишонаҳоест');
INSERT INTO "pioneers" VALUES(42,34,'42:34','Ё [агар бихоҳад сарнишинони] он киштиҳоро ба кайфари аъмолашон нобуд месозад, дар ҳоле ки Ӯ аз бисёре [аз гуноҳон] гузашт мекунад');
INSERT INTO "pioneers" VALUES(42,35,'42:35','[То муҷиби ибрат бошад] Ва касоне, ки дар мавриди оёти Мо ба муҷодала бармехезанд, бидонанд, ки [агар азоби илоҳӣ фаро расад] роҳи гурезе нахоҳанд дошт');
INSERT INTO "pioneers" VALUES(42,36,'42:36','[Эй мардум] Он чи ба шумо дода шудааст, неъматҳои [гузарои] дунёст ва он чи назди Аллоҳ таоло аст, барои муъминоне, ки бар Парвардигорашон таваккал мекунанд, беҳтар ва пояндтар аст');
INSERT INTO "pioneers" VALUES(42,37,'42:37','Ва [низ барои] онон, ки аз гуноҳони бузургу бешармиҳо канора мегиранд ва онгоҳ, ки хашмгин мешаванд, гузашт мекунанд');
INSERT INTO "pioneers" VALUES(42,38,'42:38','Ва онон, ки [даъвати] Парвардигорашонро пазиро мешаванд ва намоз барпо медоранд ва умурашон дар миёнашон ба машварат ниҳода мешавад ва аз он чи рӯзияшон кардаем, инфоқ мекунанд');
INSERT INTO "pioneers" VALUES(42,39,'42:39','Ва касоне, ки ҳар гоҳ ситаме ба онон бирасад, [ба дифоъ ва] интиқом бармехезанд');
INSERT INTO "pioneers" VALUES(42,40,'42:40','Ва ҷазои ҳар бадӣ кайфаре ҳаммонанди он бадӣ аст [на бештар], пас, ҳар ки гузашт кунад ва корро ба ислоҳ [ва оштӣ] оварад, подошаш бар уҳдаи Аллоҳ таоло аст. Бе гумон, Ӯ ситамгоронро дӯст намедорад');
INSERT INTO "pioneers" VALUES(42,41,'42:41','Албатта, касоне, ки пас аз ситаме, ки дидаанд, интиқом мегиранд, ҳеҷ [бозхосту] сарзанише бар онон нест');
INSERT INTO "pioneers" VALUES(42,42,'42:42','Роҳи [бозхосту] сарзаниш танҳо бар ситамгороне кушода аст, ки ба ноҳақ дар замин бартариҷӯӣ мекунанд. Онон азоби дарднок [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(42,43,'42:43','Аммо ҳар ки шикебоӣ ва гузашт кунад, нишон аз қудрати ирода [-и Ӯ] дорад');
INSERT INTO "pioneers" VALUES(42,44,'42:44','Ҳар киро Аллоҳ таоло гумроҳ кунад, пас, аз Аллоҳ таоло ҳеҷ [ёвару] корсозе нахоҳад дошт ва [дар рӯзи қиёмат] ситамгоронро мебинӣ, ки чун азобро мушоҳида кунанд, мегӯянд: «Оё роҳи бозгаште [ба дунё] ҳаст?»');
INSERT INTO "pioneers" VALUES(42,45,'42:45','[Дар он рӯз] Ситамгоронро мушоҳида хоҳӣ кард, ки бар оташ арза мешаванд ва дар ҳоле ки аз [шиддати] хорӣ фурӯтан шудаанд, бо таги чашм менигаранд ва [дар он ҳол] муъминон мегӯянд: «Зиёнкорон [-и воқеӣ] касоне ҳастанд, ки [ба тамаъи дунё] худ ва хонаводаҳояшонро дар қиёмат ба зиён андохтаанд. Огоҳ бошед! Ситамгорон гирифтори азобе пойдоранд');
INSERT INTO "pioneers" VALUES(42,46,'42:46','Ва онон дар баробари Аллоҳ таоло корсозе нахоҳанд дошт, ки ёриашон кунад ва ҳар киро Аллоҳ таоло гумроҳ кунад, роҳе [барои наҷот] нахоҳад дошт');
INSERT INTO "pioneers" VALUES(42,47,'42:47','[Эй мардум] Пеш аз он ки рӯзе фаро расад, ки бозгаште аз ҷониби Аллоҳ таоло надорад, даъвати Парвардигоратонро бипазиред. Дар он рӯз на паноҳгоҳе хоҳед дошт, на [фурсате барои] инкор');
INSERT INTO "pioneers" VALUES(42,48,'42:48','Пас [эй паёмбар] агар [мушрикон аз даъвати ту] рӯй мегардонанд [тавфиқро аз худ салб мекунанд]. Мо туро нигаҳбони онон нафиристодем ва ту вазифае ҷуз расонидан [-и ваҳй] надорӣ ва чун раҳмате аз ҷониби худ ба инсон бичашонем, бад-он сармаст мешавад ва агар ба сазои рафтори гузаштаи худ дучори мусибате шавад, [ба ҷойи бедорӣ носипосӣ мекунад]. Ба ростӣ, ки инсон бисёр носипос аст');
INSERT INTO "pioneers" VALUES(42,49,'42:49','Фармонравоии осмонҳо ва замин аз они Аллоҳ таоло аст. Ӯ ҳар чи бихоҳад, меофаринад, ба ҳар ки бихоҳад, [фарзанди] духтар мебахшад ва ба ҳар ки бихоҳад, [фарзанди] писар');
INSERT INTO "pioneers" VALUES(42,50,'42:50','Ё писару духтарро -ҳар ду бо ҳам медиҳад ва ҳар киро бихоҳад, нозо мегардонад, ки Ӯ донои тавоност');
INSERT INTO "pioneers" VALUES(42,51,'42:51','Дархӯри ҳеҷ башаре нест, ки Аллоҳ таоло бо ӯ сухан бигӯяд, магар бо ваҳй ё аз фаросӯи ҳиҷобе ё [ин ки Аллоҳ таоло] фариштае бифиристад, ки ба фармонаш ҳар чи бихоҳад, ваҳй кунад. Бе тардид, Ӯ баландмартабаи ҳаким аст');
INSERT INTO "pioneers" VALUES(42,52,'42:52','[Эй Паёмбар] бад-ин гуна Қуръонро [тавассути Ҷабраил ва] ба фармони хеш бар ту ваҳй кардем. [Пеш аз он] Ту огоҳӣ надоштӣ, ки китобу имон чист, вале Мо онро нуре сохтем, ки ҳар касе аз бандагони хешро, ки бихоҳем, бад-он ҳидоят кунем ва мусалламан, ту ба роҳи рост [Ислом] ҳидоят мекунӣ');
INSERT INTO "pioneers" VALUES(42,53,'42:53','Роҳи илоҳӣ, ки он чи дар осмонҳо ва замин аст, аз они Ӯст. Огоҳ бошед, [ҳамаи] умур ба сӯйи Аллоҳ таоло бозмегардад');
INSERT INTO "pioneers" VALUES(43,1,'43:1','Ҳо, мим');
INSERT INTO "pioneers" VALUES(43,2,'43:2','Савганд ба китоби равшангар');
INSERT INTO "pioneers" VALUES(43,3,'43:3','Мо онро Қуръоне ба забони арабӣ падид овардем, бошад, ки хирадварзӣ кунед');
INSERT INTO "pioneers" VALUES(43,4,'43:4','Ин китоб, ки баландмартаба ва ҳикматомӯз аст, дар Лавхи маҳфуз назди Мо сабт шудааст [ва дастхуши тағйир нахоҳад шуд]');
INSERT INTO "pioneers" VALUES(43,5,'43:5','Оё ба сирфи ин ки шумо [мушрикон] гурӯҳе исрофкоред [ва паёми илоҳиро инкор мекунед, нузули] Қуръонро аз шумо боздорем?');
INSERT INTO "pioneers" VALUES(43,6,'43:6','Чи бисёр паёмбароне, ки дар миёни пешиниён фиристодем');
INSERT INTO "pioneers" VALUES(43,7,'43:7','Вале ҳар паёмбаре ба сӯйи онон меомад, масхарааш мекарданд');
INSERT INTO "pioneers" VALUES(43,8,'43:8','Мо афроди нерумандтар аз мушрикони Маккаро ҳалок кардаем ва достони [ибратангези] пешиниён гузашт');
INSERT INTO "pioneers" VALUES(43,9,'43:9','[Эй паёмбар] Агар аз мушрикон бипурсӣ: «Осмонҳо ва заминро чи касе офардиааст?» Ҳатман, мегӯянд: «[Аллоҳи] Шикастнопазири доно»');
INSERT INTO "pioneers" VALUES(43,10,'43:10','Ҳамон [Парвардигоре] ки заминро маҳалли озмоиши шумо қарор дод ва бароятон дар он роҳҳое падид овард, то роҳ ёбед');
INSERT INTO "pioneers" VALUES(43,11,'43:11','Ва ҳамон касе, ки аз осмон боронро ба миқдори муайяне фиристод, он гоҳ сарзамини хазонзада ва хушкро ба василаи он ҳаёт бахшидем. Шумо низ ҳамин гуна [аз гӯрҳо] берун оварда мешавед');
INSERT INTO "pioneers" VALUES(43,12,'43:12','Парвардигоре, ки тамоми махлуқотро офарид ва бароятон аз киштиҳо ва чаҳорпоён василаи саворӣ падид овард');
INSERT INTO "pioneers" VALUES(43,13,'43:13','То бар пушти онҳо қарор гиред ва он гоҳ ки бар онҳо қарор гирифтед, неъмати Парвардигоратонро ёд кунед ва бигӯед: «Поку муназзаҳ аст касе, ки ин [васила]-ро дар хидмати мо гумошт ва мо худ тавони ин корро надоштем');
INSERT INTO "pioneers" VALUES(43,14,'43:14','Ва мо ба пешгоҳи Парвардигорамон бозмегардем»');
INSERT INTO "pioneers" VALUES(43,15,'43:15','Мушрикон аз миёни бандагони Аллоҳ таоло барояш саҳме [аз фарзанд] қарор доданд. Ба ростӣ, ки инсон ошкоро носипос аст');
INSERT INTO "pioneers" VALUES(43,16,'43:16','Оё [мепиндоред] Аллоҳ таоло аз миёни офаридагонаш духтаронро интихоб карда ва писаронро ба шумо ихтисос додааст?');
INSERT INTO "pioneers" VALUES(43,17,'43:17','Аммо ҳар гоҳ ба яке аз онон ҳамон чизеро, ки башорат диҳанд, ки барои Аллоҳи раҳмон қоил ҳастанд [таваллуди духтар], дар ҳоле ки хашми худро фурӯ мехӯрад, чеҳрааш [аз андуҳ] тира мегардад');
INSERT INTO "pioneers" VALUES(43,18,'43:18','Оё касеро, ки дар зевар [ва роҳатӣ] парвариш меёбад ва дар [ҳангоми] ҷидол [қодир ба] баёни мақсудаш нест [ба Аллоҳ таоло нисбат медиҳед]?');
INSERT INTO "pioneers" VALUES(43,19,'43:19','Мушрикон фариштагонро, ки бандагони [Аллоҳи] Раҳмонанд, муаннас [зан] пиндоштанд. Оё ба ҳангоми офариниши онон ҳузур доштанд? Суханонашон сабт мегардад ва [дар рӯзи қиёмат] бозхост хоҳанд шуд');
INSERT INTO "pioneers" VALUES(43,20,'43:20','[Онон барои тавҷеҳи ширки худ] мегӯянд: «Агар [Аллоҳи] Раҳмон мехост, мо фариштагонро парастиш намекардем». Онон дар мавриди ин [сухан] беиттилоанд ва ҷуз сухани дурӯғ намегӯянд');
INSERT INTO "pioneers" VALUES(43,21,'43:21','Оё пеш аз Қуръон китобе ба онон додаем, ки бад-он истидлол кунанд?');
INSERT INTO "pioneers" VALUES(43,22,'43:22','Балки мегӯянд: «Ниёгонамон низ ҳамин оинро доштанд ва мо [низ] роҳи ононро дунбол мекунем»');
INSERT INTO "pioneers" VALUES(43,23,'43:23','Пеш аз ту низ ҳар [паёмбари] ҳушдордиҳандаеро ба ҳар шаҳре фиристодем, сарватмандони саркаши он шаҳр мегуфтанд: «Ниёгонамон низ ҳамин оинро доштанд ва мо [низ] роҳи ононро дунбол мекунем»');
INSERT INTO "pioneers" VALUES(43,24,'43:24','[Паёмбарашон] Мегуфт: «Агар оине ҳидоятбахштар аз он чи ниёгонатон доштанд, оварда бошам [боз ҳам аз онон пайравӣ мекунед]? Онон [дар посух] мегуфтанд: «[Оре] паёмеро, ки маъмури [иблоғи] он шудаед, инкор мекунем»');
INSERT INTO "pioneers" VALUES(43,25,'43:25','Мо низ аз онон интиқом гирифтем, пас, бингар, ки саранҷоми такзибкунандагон чи гуна буд');
INSERT INTO "pioneers" VALUES(43,26,'43:26','[Ёд кун] Ҳангомеро, ки Иброҳим ба падар ва қавмаш гуфт: «Аз он чи шумо мепарастед, безорам');
INSERT INTO "pioneers" VALUES(43,27,'43:27','Магар аз касе, ки маро офаридааст, ки Ӯ ҳидоятам хоҳад кард»');
INSERT INTO "pioneers" VALUES(43,28,'43:28','Иброҳим, яктопарастиро дар миёни наслҳои пас аз худ сухане мондагор қарор дод, бошад, ки [ба пешгоҳи илоҳӣ] бозгарданд');
INSERT INTO "pioneers" VALUES(43,29,'43:29','Ин мушрикони Макка ва падаронашонро [аз неъматҳои дунё] бархӯрдор кардам, то ин ки Қуръон ва паёмбаре равшангар ба суроғашон омад');
INSERT INTO "pioneers" VALUES(43,30,'43:30','Ва он гоҳ ки Қуръон бар онон ироа шуд, гуфтанд: «Ин ҷодуст ва онро инкор мекунем»');
INSERT INTO "pioneers" VALUES(43,31,'43:31','Ва гуфтанд: «Чаро ин Қуръон бар марде бузург аз ду шаҳр [Макка ва Тоиф] нозил нашудааст?»');
INSERT INTO "pioneers" VALUES(43,32,'43:32','Оё онон раҳмати Парвардигоратро тақсим мекунанд? Моем, ки рӯзӣ [ва маишати] ононро дар зиндагии дунё миёнашон тақсим кардаем ва ононро нисбат ба ҳам ба дараҷоте бартарӣ бахшидаем, то аз хадамоти [мутақобили] якдигар истифода кунанд ва раҳмати Парвардигорат аз амволе, ки ҷамъ мекунанд, беҳтар аст');
INSERT INTO "pioneers" VALUES(43,33,'43:33','Агар [нигаронӣ аз онон] набуд, ки ҳама мардум [дар дунёталабӣ ва инкори Аллоҳ таоло] як гурӯҳ шаванд, сақфи хонаи касонеро, ки ба Аллоҳи раҳмон имон надоранд, аз нуқра қарор медодем ва низ паллаҳое [дар он мениҳодем], ки битавонанд аз [табақоти] он [хона] боло раванд');
INSERT INTO "pioneers" VALUES(43,34,'43:34','Ва барои хонаҳояшон дарҳое [аз тилло] мегузоштем ва тахтҳое [қарор медодем], ки бар он такя зананд');
INSERT INTO "pioneers" VALUES(43,35,'43:35','Ва таҷаммул [ва зинатҳое] фаровон [барояшон фароҳам мекардем] ва тамоми инҳо баҳраи [ночизи] зиндагии дунёст ва охират назди Парвардигорат барои парҳезкорон аст');
INSERT INTO "pioneers" VALUES(43,36,'43:36','Ҳар ки аз ёди [Аллоҳи] раҳмон дил бигардонад, шайтоне бар ӯ мегуморем, то ҳамнишинаш бошад [ва ҳамвора фиребаш диҳад]');
INSERT INTO "pioneers" VALUES(43,37,'43:37','Шаётин ин гурӯҳро аз роҳи [Аллоҳ таоло] бозмедоранд, вале онон гумон мекунанд, ки ҳидоят ёфтаанд');
INSERT INTO "pioneers" VALUES(43,38,'43:38','То он гоҳ ки [дар қиёмат] назди Мо биёяд, [ба шайтони ҳамнишинаш] мегӯяд: Эй кош, миёни ману ту фосилае ба дурии шарқу ғарб буд! Чи ҳамнишини баде будӣ!»');
INSERT INTO "pioneers" VALUES(43,39,'43:39','[Аллоҳ таоло мефармояд] «Имрӯз [пушаймонӣ] суде ба ҳолатон нахоҳад дошт; зеро ситам кардаед ва ҳамагӣ дар азоб шарикед»');
INSERT INTO "pioneers" VALUES(43,40,'43:40','[Эй Паёмбар] Оё метавонӣ [паёми худро] ба гӯши ношунавоён бирасонӣ ё нобиноён ва гумроҳони ошкорро [ба роҳи рост] ҳидоят кунӣ?');
INSERT INTO "pioneers" VALUES(43,41,'43:41','Агар ҳам туро пеш аз азоби онон аз дунё [бибарем] дар ҳар ҳол [аз эшон интиқом хоҳем гирифт');
INSERT INTO "pioneers" VALUES(43,42,'43:42','Ё азоберо, ки ба онон ваъда додаем, [дар даврони ҳаётат] ба ту менамоем. Бе тардид, Мо бар онон иқтидор [-и комил] дорем');
INSERT INTO "pioneers" VALUES(43,43,'43:43','Пас, ба он чи ба ту ваҳй мешавад, тамассук биҷӯ, ки бе гумон, бар роҳи рост қарор дорӣ');
INSERT INTO "pioneers" VALUES(43,44,'43:44','Тардиде нест, ки Қуръон барои ту ва қавмат мояи андарз [ ва сарбаландӣ] аст ва [дар рӯзи қиёмат дар бораи имону амал ба он] бозхост хоҳед шуд');
INSERT INTO "pioneers" VALUES(43,45,'43:45','Аз паёмбароне, ки пеш аз ту фиристодем, бипурс, ки оё ғайр аз [Аллоҳи] Раҳмон маъбудоне барои парастиш қарор додаем [мусалламан, чунин нест]');
INSERT INTO "pioneers" VALUES(43,46,'43:46','Мо Мӯсоро бо муъҷизоти хеш ба сӯи Фиръавн ва сарони қавмаш фиристодем ва ба онон гуфт: «Ман фиристодаи Парвардигори ҷаҳониёнам»');
INSERT INTO "pioneers" VALUES(43,47,'43:47','Ва чун оёт [ва муъҷизоти] Моро бар онон ироа кард, ҳамагӣ ба онҳо хандиданд');
INSERT INTO "pioneers" VALUES(43,48,'43:48','Ҳар муъҷизае ба онон ироа додем, яке аз дигаре бузургтар буд ва [чун ҳамаро такзиб карданд] ба азоб гирифторашон кардем, бошад, ки [аз куфр] бозгарданд');
INSERT INTO "pioneers" VALUES(43,49,'43:49','[Пас, ҳангоме ки гӯшаҳое аз азоби илоҳиро чашиданд] гуфтанд: «Эй ҷодугар, аз Парвардигорат бинобар аҳде, ки бо ту дорад, бихоҳ [то азобро аз мо дур кунад], ки мо [дигар] роҳёфтаем»');
INSERT INTO "pioneers" VALUES(43,50,'43:50','Ва чун азобро аз онон бардоштем, паймон шикастанд');
INSERT INTO "pioneers" VALUES(43,51,'43:51','Фиръавн дар миёни қавми худ нидо сар дод ва гуфт: «Эй қавми ман, оё фармонравоии Миср дар ихтиёри ман нест ва ин наҳрҳо аз доманаи қасри ман ҷараён надорад? Оё [қудрату азаматамро] намебинед?');
INSERT INTO "pioneers" VALUES(43,52,'43:52','Ман аз ин марди бемиқдор, ки [ҳатто] наметавонад ба равшанӣ сухан бигӯяд, шоистатарам');
INSERT INTO "pioneers" VALUES(43,53,'43:53','Чаро [ба нишонаи сарварӣ] дастбандҳои зарин [аз осмон] бар ӯ афканда нашудааст [то собит шавад, ки паёмбар аст]? Ё чаро фариштагон ҳамроҳаш наёмадаанд?»');
INSERT INTO "pioneers" VALUES(43,54,'43:54','Ӯ қавми худро ба [бехирадӣ ва] сабуксарӣ кашонд ва ҳамагӣ мутеаш шуданд. Ҳаққо, ки онон [нисбат ба авомири илоҳӣ] нофармон буданд');
INSERT INTO "pioneers" VALUES(43,55,'43:55','Пас, чун моро ба хашм оварданд, аз онон интиқом гирифтем ва ҳамагиро [дар дарё] ғарқ кардем');
INSERT INTO "pioneers" VALUES(43,56,'43:56','Он гоҳ ононро дар шумори пешгомони [куфру нофармонӣ] ва ибрате [барои ояндагон] қарор додем');
INSERT INTO "pioneers" VALUES(43,57,'43:57','Ва чун достони [таваллуди] писари Марям матраҳ шуд, қавми ту [ки гумон карданд, метавонанд монанди масеҳиён чанд маъбуд дошта бошанд] ба ногоҳ шодӣ ва ҳаёҳу ба роҳ андохтанд');
INSERT INTO "pioneers" VALUES(43,58,'43:58','Ва гуфтанд: «Маъбудони мо беҳтаранд ё Исо»? Онон ин суханро танҳо барои ҷидол бо ту унвон кардаанд. Ба ростӣ, ки гурӯҳе ситезаҷӯ ҳастанд');
INSERT INTO "pioneers" VALUES(43,59,'43:59','Исо фақат бандаест, ки бар ӯ неъмат [-и рисолат] арзонӣ доштем ва ӯро намунае [аз қудрати илоҳӣ] барои Бани Исроил қарор додем');
INSERT INTO "pioneers" VALUES(43,60,'43:60','Агар мехостем, [ҳамаи шумо инсонҳоро нобуд мекардем ва] ба ҷоятон фариштагоне падид меовардем, ки дар замин ҷонишин [-и шумо] шаванд');
INSERT INTO "pioneers" VALUES(43,61,'43:61','[Таваллуди] Исо [ба муъҷизаи ҳаёт бахшиданаш] далеле барои қиёмат аст. Пас, ҳаргиз дар мавриди қиёмат шак накунед ва пайрави ман бошед, ки роҳи рост ҳамин аст');
INSERT INTO "pioneers" VALUES(43,62,'43:62','[Огоҳ бошед, ки] Шайтон шуморо [аз роҳи Аллоҳ таоло] бознадорад, чаро ки ӯ душмани ошкори шумост');
INSERT INTO "pioneers" VALUES(43,63,'43:63','Ва чун Исо [муъҷизоту] нишонаҳои равшан бар онон ироа кард, гуфт: «Ман барои шумо ҳикмат овардаам, то [бад-он роҳ ёбед] ва бархе аз мавориди ихтилофатонро равшан кунам, пас, аз Аллоҳ таоло парво кунед ва мутеъи [дастурҳои] ман бошед');
INSERT INTO "pioneers" VALUES(43,64,'43:64','Аллоҳ таоло Парвардигори ман ва шумост, пас, Ӯро бипарастед, ки роҳи рост ҳамин аст»');
INSERT INTO "pioneers" VALUES(43,65,'43:65','Он гоҳ гурӯҳҳои [мухталифи] Бани Исроил миёни худ [дар бораи Исо] дучори ихтилоф шуданд, пас, вой бар ситамгорон аз азоби рӯзи дарднок!');
INSERT INTO "pioneers" VALUES(43,66,'43:66','Оё ҷуз ин интизор доранд, ки қиёмат, дар ҳоле ки ғофиланд, ногаҳон бар онон фаро расад?');
INSERT INTO "pioneers" VALUES(43,67,'43:67','Дӯстон [-и дунёӣ] дар он рӯз душмани якдигаранд, магар парҳезкорон');
INSERT INTO "pioneers" VALUES(43,68,'43:68','[Аллоҳ таоло ба онон мефармояд] «Эй бандагони [парҳезкори] Ман имрӯз тарсу андуҳе нахоҳед дошт');
INSERT INTO "pioneers" VALUES(43,69,'43:69','Ҳамон бандагоне, ки ба оёти Мо имон овардаанд ва таслим [-и фармони Мо] буданд');
INSERT INTO "pioneers" VALUES(43,70,'43:70','Шумо ва дигар муъминоне, ки ҳамчун шумо ҳастанд, ба биҳишт дароед, то [аз неъматҳои ҷовидонаш баҳраманд гардед ва] шодмон шавед');
INSERT INTO "pioneers" VALUES(43,71,'43:71','Зарфҳо ва ҷомҳои зарин перомунашон мегардонанд ва ҳар чи дилҳо бихоҳад ва дидаҳо лаззат барад, дар он ҷо ҳаст ва шумо дар биҳишт ҷовидонаед');
INSERT INTO "pioneers" VALUES(43,72,'43:72','Ин ҳамон биҳиштест, ки дар барорбари рафторатон ба шумо додаанд');
INSERT INTO "pioneers" VALUES(43,73,'43:73','Дар онҷо меваҳои фаровоне дар ихтиёр доред, ки аз онҳо мехӯред');
INSERT INTO "pioneers" VALUES(43,74,'43:74','Бе тардид, муҷримон дар азоби дузах ҷовидонанд');
INSERT INTO "pioneers" VALUES(43,75,'43:75','[Дар ҳоле ки азоб] аз онон коста намешавад ва дар яъсу ноумедӣ фурӯ рафтаанд');
INSERT INTO "pioneers" VALUES(43,76,'43:76','Мо ба онон ситам накардаем, балки онҳо худ ба хештан ситам мекарданд');
INSERT INTO "pioneers" VALUES(43,77,'43:77','[Дузахиён] Нигаҳбони оташро садо мезананд ва фарёд бар меоранд: «Эй Молик [кош] Парвардигорат кори моро яксара кунад [ва бимиронад]» Ӯ мегӯяд: «На, шумо [дар ин азоб] мондагоред»');
INSERT INTO "pioneers" VALUES(43,78,'43:78','Ба ростӣ, ҳақро барои шумо овардем, вале бештаратон аз он кароҳат доштед');
INSERT INTO "pioneers" VALUES(43,79,'43:79','Онон азми [худро барои тавтеа алайҳи ту] ҷазм карданд, Мо низ иродаи муҳкаме [бар ноком гузоштани эшон] дорем');
INSERT INTO "pioneers" VALUES(43,80,'43:80','Оё мепиндоранд, ки Мо суханони сиррӣ ва наҷвояшонро намешунавем? Оре, фариштагони Мо дар канорашон [ҳастанд ва ҳама чизро] сабт мекунанд');
INSERT INTO "pioneers" VALUES(43,81,'43:81','[Эй паёмбар, ба мушрикон] бигӯ: «Агар [ба фарзи маҳол Аллоҳи] Раҳмон фарзанде дошт, ман нахустин парастишгар [-и Ӯ] будам»');
INSERT INTO "pioneers" VALUES(43,82,'43:82','[Маъбуди яктое, ки] Парвардигори осмонҳо ва замин ва Арш аст, аз тавсифи [ҷоҳилонаи] онон муназзаҳ аст');
INSERT INTO "pioneers" VALUES(43,83,'43:83','Пас, [эй паёмбар] ононро [ба ҳоли худ] бигузор, то ёвагӯӣ кунанд ва ба бозӣ саргарм бошанд, то саранҷом бо рӯзи мавъуд [қиёмат] мувоҷеҳ шаванд [ва сазои аъмолашонро бубинанд]');
INSERT INTO "pioneers" VALUES(43,84,'43:84','Ӯст, ки дар осмон ва замин маъбуд аст ва Ӯ ҳакими доност');
INSERT INTO "pioneers" VALUES(43,85,'43:85','Пурбаракат ва бузургвор аст касе, ки фармонравоии осмонҳову замин ва он чи миёни онҳост, аз они Ӯст ва огоҳӣ аз [лаҳзаи барпоии] растохез танҳо дар ихтиёри Ӯст ва [ҳамагӣ] ба пешгоҳи Ӯ бозгардонда мешавед');
INSERT INTO "pioneers" VALUES(43,86,'43:86','Касоне, ки [мушрикон] ба ҷойи Аллоҳ таоло [ба ниёиш] мехонанд, қодир ба шафоат нестанд, магар афроде, ки ба [калимаи тавҳид ва] ҳақ аз руи илм гувоҳӣ медиҳанд');
INSERT INTO "pioneers" VALUES(43,87,'43:87','Агар аз мушрикон бипурсӣ, чи касе ононро офаридааст, мусалламан, мегӯянд: «Аллоҳ таоло». Пас, чи гуна [аз ҳақ] бар мегарданд?');
INSERT INTO "pioneers" VALUES(43,88,'43:88','[Шикоят ва] Сухани паёмбар [ин буд]: «Парвардигоро, инон қавме ҳастанд, ки имон намеоваранд»');
INSERT INTO "pioneers" VALUES(43,89,'43:89','Пас, [эй паёмбар] аз онон даргузар ва [барои раҳоӣ аз шаррашон] бигӯ: «Салом бар Шумо»! Ба зудӣ хоҳанд донист [ки азобашон чи гуна аст]');
INSERT INTO "pioneers" VALUES(44,1,'44:1','Ҳо, мим');
INSERT INTO "pioneers" VALUES(44,2,'44:2','Савганд ба китоби равшангар');
INSERT INTO "pioneers" VALUES(44,3,'44:3','Мо Қуръонро дар шаби муборак [ва фархундаи қадр] нозил кардем, [зеро] ки Мо [ҳамвора] ҳушдордиҳанда будаем');
INSERT INTO "pioneers" VALUES(44,4,'44:4','Дар он [шаб] ҳар амри муҳимме файсала меёбад [ва тадбир мешавад]');
INSERT INTO "pioneers" VALUES(44,5,'44:5','[Ин] корест [ки] аз ҷониби Мо [сурат мегирад] бе гумон, Мо фиристандаи [паёмбарон] будаем');
INSERT INTO "pioneers" VALUES(44,6,'44:6','Ба унвони раҳмате аз Парвардигорат. Бе тардид, Ӯ шунавои доност');
INSERT INTO "pioneers" VALUES(44,7,'44:7','Парвардигори осмонҳову замин ва он чи миёни онҳост. Агар [ба ин ҳақиқат] яқин доред [ба Аллоҳ таоло ва Паёмбараш имон биёваред]');
INSERT INTO "pioneers" VALUES(44,8,'44:8','Ҳеҷ маъбуде [ба ҳақ] ҷуз Ӯ нест, зиндагӣ мебахшад ва мемиронад. Парвардигори шумо ва ниёгони шумост');
INSERT INTO "pioneers" VALUES(44,9,'44:9','Вале мушрикон тардид доранд ва саргарми [лаззатҳои дунё] ва бозӣ ҳастанд');
INSERT INTO "pioneers" VALUES(44,10,'44:10','Пас, [эй паёмбар] мунтазири рӯзе бош, ки осмон, дуде ошкор [падид] оварад');
INSERT INTO "pioneers" VALUES(44,11,'44:11','Ки [тамоми] мардумро фаро мегирад. Ин азобе дарднок аст');
INSERT INTO "pioneers" VALUES(44,12,'44:12','[Он гоҳ кофирон мегӯянд] «Парвардигоро, [ин] азобро аз мо бартараф кун, ки имон овардаем»');
INSERT INTO "pioneers" VALUES(44,13,'44:13','Чи гуна [ва аз куҷо] панд мепазиранд, дар ҳоле ки [пештар] паёмбари равшангар ба сӯяшон омада буд?');
INSERT INTO "pioneers" VALUES(44,14,'44:14','Аммо аз ӯ рӯй гардониданд ва гуфтанд: «[Ӯ] омӯзишёфтаи девона аст»');
INSERT INTO "pioneers" VALUES(44,15,'44:15','[Барои озмоиши шумо] Замоне андак азобро бармедорем, ва [-ле] шумо [ба куфри хеш] бозмегардед');
INSERT INTO "pioneers" VALUES(44,16,'44:16','Рӯзе сахт бар онон метозем. [Оре] Мо [аз кофирон ба хотири куфру такзибашон] интиқом мегирем');
INSERT INTO "pioneers" VALUES(44,17,'44:17','Пеш аз онон низ қавми Фиръавнро озмудем ва паёмбари бузургвор ба сӯяшон омад');
INSERT INTO "pioneers" VALUES(44,18,'44:18','[Ва ба онон гуфт] Ки «[кори Бани Исроил ин] бандагони Аллоҳро ба ман вогузоред, ки ман барои шумо паёмбари аминам');
INSERT INTO "pioneers" VALUES(44,19,'44:19','Ва [низ] бар Аллоҳ таоло бартарӣ маҷӯед, ки ман бароятон далели равшане овардаам');
INSERT INTO "pioneers" VALUES(44,20,'44:20','Ва аз ин ки сангсорам кунед, ба Парвардигори худ ва Парвардигори шумо паноҳ мебарам');
INSERT INTO "pioneers" VALUES(44,21,'44:21','Агар ба ман имон намеоваред, пас, аз ман канора бигиред [ва раҳоям кунед]');
INSERT INTO "pioneers" VALUES(44,22,'44:22','Он гоҳ ба даргоҳи Парвардигораш дуо кард, ки: «[Бор илоҳо] Инон гурӯҳе табоҳкоранд»');
INSERT INTO "pioneers" VALUES(44,23,'44:23','[Ба Мӯсо ваҳй кардем, ки] «Бандагонамро шабона [аз Миср] берун бибар, зеро [сипоҳи Фиръавн] шуморо таъқиб хоҳад кард');
INSERT INTO "pioneers" VALUES(44,24,'44:24','Ва дарёро ором пушти сар бигузор, ки онон сипоҳе ҳастанд ки ғарқ хоҳанд шуд');
INSERT INTO "pioneers" VALUES(44,25,'44:25','Чи бисёр боғҳову чашмасорҳоро, ки раҳо карданд [ва рафтанд]');
INSERT INTO "pioneers" VALUES(44,26,'44:26','Ва киштзорҳову қасрҳои наку [ва зебо]');
INSERT INTO "pioneers" VALUES(44,27,'44:27','Ва неъмате, ки дар он [шодмон] буданд');
INSERT INTO "pioneers" VALUES(44,28,'44:28','[Сарнавишташон] Ин гуна [буд] ва [тамомии] он неъматҳоро ба гурӯҳе дигар вогузоштем');
INSERT INTO "pioneers" VALUES(44,29,'44:29','Осмону замин бар [нобудии] онон қатраи ашке нарехт ва муҳлате [барои тавба] низ наёфтанд');
INSERT INTO "pioneers" VALUES(44,30,'44:30','Бе тардид, Бани Исроилро аз азоби хоркунанда наҷот додем');
INSERT INTO "pioneers" VALUES(44,31,'44:31','Аз Фиръавн, ки [роҳбари] саркаше дар зумраи исрофкорон буд');
INSERT INTO "pioneers" VALUES(44,32,'44:32','Мо бо дониши Худ Бани Исроилро бар мардуми замона бартарӣ додем');
INSERT INTO "pioneers" VALUES(44,33,'44:33','Ва нишонаҳое [аз қудрати хеш] дар ихтиёрашон гузоштем, ки озмоише равшан дар он буд');
INSERT INTO "pioneers" VALUES(44,34,'44:34','Ин мушрикон мегӯянд:');
INSERT INTO "pioneers" VALUES(44,35,'44:35','«Ҷуз марги нахустини мо ҳеҷ [зиндагии дигаре] нест ва ҳаргиз барангехта нахоҳем шуд');
INSERT INTO "pioneers" VALUES(44,36,'44:36','Ва агар рост мегӯед, ниёгонамонро [зинда кунед ва] биёваред»');
INSERT INTO "pioneers" VALUES(44,37,'44:37','Оё эшон беҳтаранд ё қавми [неруманди] Туббаъ ва пешиниёни онҳо, ки чун базаҳкор буданд, ҳалокашон кардем?');
INSERT INTO "pioneers" VALUES(44,38,'44:38','Ва Мо осмону замин ва он чиро, ки миёни онҳост ба бозича наёфаридаем');
INSERT INTO "pioneers" VALUES(44,39,'44:39','Онҳоро ба ҳақ [ва бо ҳадаф] офаридаем, вале бештарашон намедонанд');
INSERT INTO "pioneers" VALUES(44,40,'44:40','Бе тардид, рӯзи доварӣ [қиёмат] ваъдагоҳи ҳамаи онон аст');
INSERT INTO "pioneers" VALUES(44,41,'44:41','Рӯзе, ки ҳеҷ дӯсте барои дӯсти худ судманд нест ва [аз ҳеҷ сӯ] ёрӣ намешаванд');
INSERT INTO "pioneers" VALUES(44,42,'44:42','Магар касе, ки Аллоҳ таоло [бар ӯ] раҳм кунад, ки Ӯ шикастнопазири меҳрубон аст');
INSERT INTO "pioneers" VALUES(44,43,'44:43','Бе гумон, дарахти Заққум');
INSERT INTO "pioneers" VALUES(44,44,'44:44','ғизои гунаҳкорон аст');
INSERT INTO "pioneers" VALUES(44,45,'44:45','[Мевааш аз шиддати ҳарорат] ҳамчун миси гудохта дар шикамҳо меҷӯшад');
INSERT INTO "pioneers" VALUES(44,46,'44:46','Монанди ҷӯшиши оби гарм [ҷӯшидаистода]');
INSERT INTO "pioneers" VALUES(44,47,'44:47','[Хитоб мерасад] «Он [ситамгор]-ро бигиред ва ба миёни дузах дарафканед');
INSERT INTO "pioneers" VALUES(44,48,'44:48','Он гоҳ аз азоби оби доғ бар сараш фурӯ резед»');
INSERT INTO "pioneers" VALUES(44,49,'44:49','[Дар он ҳол ба ӯ гуфта мешавад] «Бичаш, ки ту [дар миёни қавми худ] иззатманд ва бузургворӣ');
INSERT INTO "pioneers" VALUES(44,50,'44:50','Ин ҳамон [сарнавишти дардноке] аст, ки дар мавридаш тардид доштед»');
INSERT INTO "pioneers" VALUES(44,51,'44:51','Яқинан парҳезкорон дар ҷойгоҳи амне ҳастанд');
INSERT INTO "pioneers" VALUES(44,52,'44:52','дар [миёни] боғҳо ва [канори] чашмасорҳои биҳишт');
INSERT INTO "pioneers" VALUES(44,53,'44:53','Либосҳое аз дебои нозук ва дебои захим мепӯшанд, дар ҳоле ки рӯёрӯи якдигар [ба суҳбат] нишастаанд');
INSERT INTO "pioneers" VALUES(44,54,'44:54','Чунин аст [вазъи онон] ва ҳуриёни ғизолчашм [-и биҳиштӣ]-ро ба ҳамсарии онон дармеоварем');
INSERT INTO "pioneers" VALUES(44,55,'44:55','Дар он ҷо ҳар мевае, ки ирода кунанд, бе дағдаға дар ихтиёрашон аст');
INSERT INTO "pioneers" VALUES(44,56,'44:56','Дар биҳишт таъми маргро нахоҳанд чашид, ҷуз ҳамон марги нахустин ва [Парвардигори меҳрубон] аз муҷозоти дузах барканорашон доштааст');
INSERT INTO "pioneers" VALUES(44,57,'44:57','[Ин] Бахшишест аз [ҷониби] Парвардигорат ва ин ҳамон комёбии бузург аст');
INSERT INTO "pioneers" VALUES(44,58,'44:58','[Эй паёмбар] Мо Қуръонро ба забони ту равон [ва осон] сохтем, бошад, ки [мушрикон] панд гиранд');
INSERT INTO "pioneers" VALUES(44,59,'44:59','Пас, дар интизори [ваъдаҳои илоҳӣ] бош, ки бе гумон, онон [низ] мунтазиранд');
INSERT INTO "pioneers" VALUES(45,1,'45:1','Ҳо, мим');
INSERT INTO "pioneers" VALUES(45,2,'45:2','Нузули [ин] китоб аз ҷониби Аллоҳи шикастнопазири ҳаким аст');
INSERT INTO "pioneers" VALUES(45,3,'45:3','Ба ростӣ, ки дар осмонҳо ва замин нишонаҳое [аз қудрати илоҳӣ] барои муъминон [падидор] аст');
INSERT INTO "pioneers" VALUES(45,4,'45:4','Ва [низ] дар офариниши худатон ва мавҷудоти зиндае, ки [дар замин] пароканда месозад, барои аҳли яқин нишонаҳое [аз тавон ва тадбири Аллоҳ таоло ошкор] аст');
INSERT INTO "pioneers" VALUES(45,5,'45:5','Ва дар гардиши паёпайи шабу рӯз ва ризқе, ки Аллоҳ таоло [ба сурати борон] аз осмон мефиристад ва бо он заминро пас аз хазон ва хушкиаш зинда месозад ва [низ] дар гардиши бодҳо барои хирадварзон, нишонаҳое [аз қудрати Парвардигор] аст');
INSERT INTO "pioneers" VALUES(45,6,'45:6','[Эй паёмбар] Инҳо оёт [ва нишонаҳо]-и илоҳист, ки ба ҳақ бар ту мехонем [Ба ростӣ, мушрикон] пас аз [сухани] Аллоҳ таоло ва оёташ кадом суханро бовар мекунанд?');
INSERT INTO "pioneers" VALUES(45,7,'45:7','Вой бар ҳар дурӯғгӯи гунаҳкор!');
INSERT INTO "pioneers" VALUES(45,8,'45:8','[Он кофире] ки оёти илоҳиро, ки бар ӯ хонда мешавад, мешунавад, вале мутакаббирона бар мухолифат исрор дорад. Гӯӣ онҳоро нашунидааст. Пас, ӯро ба азобе дарднок башорат бидеҳ');
INSERT INTO "pioneers" VALUES(45,9,'45:9','[Ҳамон ки] чун бар гӯшае аз оёти Мо воқиф шавад, онро ба масхара мегирад. Онон азобе сангину дарднок [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(45,10,'45:10','Пешопеши эшон [дар охират] дузах аст ва дастовардашон ва [ёварону] корсозоне, ки ба ҷойи Аллоҳ таоло баргузидаанд, ҳеҷ суде ба ҳолашон нахоҳанд дошт ва азобе саҳмгин [дар пеш] доранд');
INSERT INTO "pioneers" VALUES(45,11,'45:11','Ин [Қуръон муҷиби] ҳидоят аст ва касоне, ки оёти Парвардигорашонро инкор мекунанд, азобе аз сахттарин азобҳои дардноки [дузахро дар пеш] доранд');
INSERT INTO "pioneers" VALUES(45,12,'45:12','Аллоҳ таоло аст, ки дарёҳоро дар хидмати шумо гумошт, то киштиҳо ба фармонаш дар он равон шаванд ва [шумо битавонед] аз фазли Ӯ [рӯзи] биҷӯед, бошад, ки сипос гузоред');
INSERT INTO "pioneers" VALUES(45,13,'45:13','Он чи дар осмонҳо ва замин аст, аз [раҳмати] хеш дар хидмати шумо гумошт, ки дар ин [амр] барои андешамандон нишонаҳое [аз лутф ва қудрати илоҳӣ] аст');
INSERT INTO "pioneers" VALUES(45,14,'45:14','[Эй паёмбар] ба муъминон бигӯ: «Аз касоне, ки ба рӯзҳои Аллоҳ таоло [рӯзҳои пирӯзӣ бар куфру интиқом аз мушрикон] умеде надоранд, даргузаранд, то [Аллоҳ таоло ба мавқеи худ ҳар] гурӯҳеро дар баробари он чи ки анҷом додаанд, сазо диҳад»');
INSERT INTO "pioneers" VALUES(45,15,'45:15','Ҳар ки кори шоистае анҷом диҳад, ба суди худ ва ҳар ки кори баде кунад, ба зиёни худ кардааст. Он гоҳ [барои муҳосиба ва ҷазо] ба пешгоҳи Парвардигоратон бозгардонида мешавед');
INSERT INTO "pioneers" VALUES(45,16,'45:16','Ба Бани Исроил Таврот ва [илму] доварӣ [байни мардум] ва нубувват бахшидем ва аз неъматҳои покиза рӯзиашон додем ва ононро [ба мавҳибати тавҳид] бар мардуми замона бартарӣ бахшидем');
INSERT INTO "pioneers" VALUES(45,17,'45:17','Ва дар амри [бозшиносии ҳақ аз ботил], оёти равшане дар ихтиёрашон қарор додем, вале онон ба далели ҳасад ва бартарихоҳӣ, ки байнашон буд, ихтилоф варзиданд ва бе тардид, Парвардигорат рӯзи қиёмат дар мавориди ихтилофашон миёни онон доварӣ хоҳад кард');
INSERT INTO "pioneers" VALUES(45,18,'45:18','Он гоҳ туро бар равише [равшан ва комил] аз амри [дин] қарор додем, пас, пайрави ҳамон роҳ бош ва аз ҳавасҳои нодонон пайравӣ накун');
INSERT INTO "pioneers" VALUES(45,19,'45:19','Онон дар баробар [-и азоб]-и Аллоҳ таоло барои ту ҳеҷ фоидае надоранд. Ситамгорон [ёвару] корсози якдигаранд ва Аллоҳ таоло корсози парҳезкорон аст');
INSERT INTO "pioneers" VALUES(45,20,'45:20','Ин [Қуръон] мояи басирати мардум аст ва ҳидояту раҳматест барои аҳли яқин');
INSERT INTO "pioneers" VALUES(45,21,'45:21','Оё касоне, ки муртакиби корҳои ношоиста шуданд, пиндоштаанд, ки ҳамтирози муъминони некукор қарорашон медиҳем, ба гунае ки зиндагӣ ва маргашон [бо онон] яксон бошад? Чи бад доварӣ мекунанд!');
INSERT INTO "pioneers" VALUES(45,22,'45:22','Аллоҳ таоло осмонҳо ва заминро [ҳадафманд ва] ба ҳақ офарид, то [ҳама озмоиш шаванд] ва ҷазои дастовардҳои ҳама [бандагон] дода шавад, бидуни он ки зулме ба онон шавад');
INSERT INTO "pioneers" VALUES(45,23,'45:23','Оё он касеро дидаӣ, ки огоҳона ҳавои нафсашро ба ҷойи маъбуди худ нишонда [ва таслими он шуда] аст? Аллоҳ таоло низ ӯро гумроҳ сохт ва бар гӯшу дилаш муҳри [ғафлат] ниҳод ва бар дидааш пардае кашид. [Агар Аллоҳ таоло ҳидояташ накунад] пас, аз Аллоҳ таоло чи касе ҳидояташ хоҳад кард? Оё панд намегиред?');
INSERT INTO "pioneers" VALUES(45,24,'45:24','[Мушрикон] мегӯянд: «Ҷуз зиндагии дунёи мо ҳеҷ [хабаре] нест. Зиндагӣ мекунем ва мемирем ва ин гардиши рӯзгор аст, ки моро ба коми марг мебарад». Онон дар мавриди ин [амр] бе иттилоанд ва фақат аз ҳадсу гумон пайравӣ мекунанд');
INSERT INTO "pioneers" VALUES(45,25,'45:25','Ва чун оёти равшангари Мо [дар мавриди қиёмат] бар онон хонда мешавад, далели [инкор]- ашон фақат ин аст, ки мегӯянд: «Агар рост мегӯед, ниёгонамонро [зинда кунед ва] бозгардонед»');
INSERT INTO "pioneers" VALUES(45,26,'45:26','[Эй Паёмбар, ба онон] бигӯ: «Аллоҳ таоло аст, ки шуморо ҳаёт мебахшад ва марг медиҳад. Он гоҳ шуморо дар рӯзи қиёмат, ки ҳеҷ тардид дар он нест, ҷамъ мекунад, вале бештари мардум [ин ҳақиқатро] намедонанд»');
INSERT INTO "pioneers" VALUES(45,27,'45:27','Фармонравоии осмонҳо ва замин аз они Аллоҳ таоло аст ва рӯзе, ки қиёмат барпо шавад, аҳли ботил зиёнкор хоҳанд буд');
INSERT INTO "pioneers" VALUES(45,28,'45:28','[Дар он рӯз] ҳар гурӯҳеро [аз ваҳшати ҳисобу ҷазо хору] ба зонударомада мебинӣ, ҳамаро барои [гирифтани] номаи аъмолашон фаро мехонанд [ва ба онон хитоб мешавад]: «Имрӯз шумо дар баробари он чи кардаед, ҷазо мебинед');
INSERT INTO "pioneers" VALUES(45,29,'45:29','Ин номаи [аъмоли шумо назди] мост, ки ба ҳақ бар шумо гувоҳӣ медиҳад. Мо [тамоми] амалкардатонро [дақиқан] сабт мекардем');
INSERT INTO "pioneers" VALUES(45,30,'45:30','Муъминони некукорро Парвардигорашон фарогирифтаи раҳмати хеш қарор хоҳад дод. Ин ҳамон комёбии ошкор аст');
INSERT INTO "pioneers" VALUES(45,31,'45:31','Вале [ба] кофирон [хитоб мешавад]: «Магар на ин буд, ки дар баробари оёти Ман, ки бар шумо арза мешуд, гарданкашӣ мекардед ва гурӯҳе базаҳкор будед?');
INSERT INTO "pioneers" VALUES(45,32,'45:32','Ва чун гуфта мешуд: «Ваъдаи Аллоҳ таоло ҳақ аст ва [дар вуқуи] қиёмат ҳеҷ тардиде нест», мегуфтед: Намедонем қиёмат чист, фақат тасаввуре [дар] ин маврид] дорем, ва [-ле] ба яқин нарасидаем»');
INSERT INTO "pioneers" VALUES(45,33,'45:33','Рафтори зишташон бар онон ошкор мешавад ва азобе, ки масхарааш мекарданд, домангирашон хоҳад шуд');
INSERT INTO "pioneers" VALUES(45,34,'45:34','[Ба онон] гуфта мешавад: «Имрӯз [дар азоби дузах] раҳоятон мекунем, чунонки шумо [низ дар дунё нисбат] ба дидори имрӯз беэътино будед ва ҷойгоҳатон оташ аст ва ҳеҷ ёваре нахоҳед дошт');
INSERT INTO "pioneers" VALUES(45,35,'45:35','Ин [гирифторӣ] аз он рӯст, ки оёти илоҳиро масхара мекардед ва зиндагии дунё фиребатон дода буд». Имрӯз [кофиронро] аз оташ берун нахоҳанд овард ва аз онон дархости пузиш намешавад');
INSERT INTO "pioneers" VALUES(45,36,'45:36','Пас, ситоиш махсуси Аллоҳ таоло аст, Парвардигори осмонҳо ва замин ва тамоми ҷаҳониён');
INSERT INTO "pioneers" VALUES(45,37,'45:37','Бузургӣ дар осмонҳо ва замин аз они Ӯст ва Ӯ шикастнопазири ҳаким аст');
INSERT INTO "pioneers" VALUES(46,1,'46:1','Ҳо, мим');
INSERT INTO "pioneers" VALUES(46,2,'46:2','Нузули [ин] китоб аз ҷониби Аллоҳи шикастнопазири ҳаким аст');
INSERT INTO "pioneers" VALUES(46,3,'46:3','Осмонҳо ва замин ва он чиро, ки байни онҳост, ба ҳақ [ва ҳадафманд] ва барои муддати муайяне офаридаем, ва [-ле] кофирон аз он чи ба эшон ҳушдор шуда, рӯйгардонанд');
INSERT INTO "pioneers" VALUES(46,4,'46:4','[Эй паёмбар, ба мушрикон] бигӯ: «Оё ба бутҳое, ки ба ҷойи Аллоҳ таоло [ба ниёиш] мехонед, таваҷҷуҳ кардаед? Ба ман нишон диҳед, чи чиз аз [аҷзои] заминро офаридаанд. Оё дар [офариниши] осмонҳо ширкат доштаанд? Агар рост мегӯед, аз китобҳои осмонии пешин ё бозмондае аз дониш [-и гузаштагон далел] биёваред»');
INSERT INTO "pioneers" VALUES(46,5,'46:5','Кист гумроҳтар аз он ки ба ҷойи Аллоҳ таоло афродеро [ба ниёиш] мехонад, ки то рӯзи қиёмат низ посухашро намедиҳанд ва аз дархосташон бехабаранд?');
INSERT INTO "pioneers" VALUES(46,6,'46:6','Ва чун [дар қиёмат] мардум эҳзор шаванд, он маъбудон [-и ботил] душманонашон хоҳанд буд ва ибодаташонро инкор хоҳанд кард');
INSERT INTO "pioneers" VALUES(46,7,'46:7','Ва чун оёти равшангари Мо бар кофирон хонда мешуд, онон дар мавриди сухани ҳаққе, ки бад-эшон расидааст, мегӯянд: «Ин ҷодуе ошкор аст»');
INSERT INTO "pioneers" VALUES(46,8,'46:8','Ё мегӯянд: «[Муҳаммад] Қуръонро худ ҷаъл карда [ва ба Аллоҳ таоло нисбат дода] аст». [Эй паёмбар, ба онон] Бигӯ: «Агар ман онро ҷаъл карда [ва ба Аллоҳ таоло нисбат дода] бошам, пас, шумо наметавонед дар баробари [азоб ва хашми] Аллоҳ таоло аз ман дифоъ кунед. Аллоҳ таоло ба он чи шумо дар борааш суханчинӣ мекунед, огоҳтар аст ва ба унвони гувоҳ байни ману шумо кофист ва Ӯ омурзгори меҳрубон аст»');
INSERT INTO "pioneers" VALUES(46,9,'46:9','Бигӯ: «Ман падидаи навзуҳур дар миёни паёмбарон нестам ва намедонам сарнавишти ман ва шумо чи хоҳад шуд, танҳо аз он чи ба ман ваҳй мешавад, пайравӣ мекунам ва фақат ҳушдордиҳандаи ошкорам»');
INSERT INTO "pioneers" VALUES(46,10,'46:10','Бигӯ: «Ба ман бигӯед, агар Қуръон аз сӯйи Аллоҳ таоло [расида] бошад ва бо ин ки шоҳиде аз Бани Исроил мушобиҳаташро [бо Таврот] гувоҳӣ дода ва имон оварда бошад ва Шумо дар баробараш инкор ва такаббур кунед, [оё гумроҳ ва ситамгар нестед]? Бе гумон, Аллоҳ таоло ситамгоронро ҳидоят нахоҳад кард»');
INSERT INTO "pioneers" VALUES(46,11,'46:11','Кофирон ба муъминон мегӯянд: «Агар Қуръон шоистагӣ дошт, онон [ки бардагон ва фақироне ночизанд] дар пазиришаш бар мо сабқат намегирифтанд». Ва чун бад-он ҳидоят наёфтаанд, мегӯянд: «Ин Қуръон дурӯғи қадимӣ аст»');
INSERT INTO "pioneers" VALUES(46,12,'46:12','Дар ҳоле ки пеш аз Қуръон китоби Мӯсо роҳнамо ва раҳмат буд ва ин [Қуръон] китобест, ки [Тавротро] ба забони [шевои] арабӣ тасдиқ мекунад, то ба ситамгорон ҳушдор диҳад ва барои некукорон башорат бошад');
INSERT INTO "pioneers" VALUES(46,13,'46:13','Касоне, ки гуфтанд: «Парвардигори мо Аллоҳ таоло аст», он гоҳ [аз бандагии ғайри Ӯ сар боз заданд ва бар эътиқоди худ] пой фишурданд, тарсу андӯҳе нахоҳанд дошт');
INSERT INTO "pioneers" VALUES(46,14,'46:14','Онон аҳли биҳиштанд ва ҷовидона дар он ҷо хоҳанд буд. Ин подоши аъмолест, ки анҷом додаанд');
INSERT INTO "pioneers" VALUES(46,15,'46:15','Ба инсон супориш кардем, ки дар мавриди падару модараш ба шоистагӣ некӣ кунад, [ба вижа] модар, ки ба душворӣ бори ҳамли ӯро бар уҳда дорад ва бо душворӣ вазъи ҳамл мекунад ва [даврони] бордорӣ ва ширхорагиаш [ҷамъан ҳудуди] сӣ моҳ аст. [Ӯ парвариш меёбад] то ба ҳадди рушд бирасад ва дар чиҳилсолагӣ [ки тавони ҷисмӣ ва зеҳниаш комил аст] мегӯяд: «Парвардигоро, ба ман тавфиқ бидеҳ, то шукри неъматеро, ки ба ман ва падару модарам арзонӣ доштаӣ, ба ҷо оварам ва кори шоистае анҷом диҳам, ки аз он хушнуд гардӣ ва фарзандонамро шоиста гардон. Ба даргоҳат [тавба намуда ва] бозгаштаам ва таслими [фармонат] ҳастам»');
INSERT INTO "pioneers" VALUES(46,16,'46:16','Онон касоне ҳастанд, ки Мо некутарин корҳояшонро мепазирем ва аз гуноҳонашон мегузарем ва дар зумраи аҳли биҳиштанд. Ин ваъдаи ростест, ки ба он навид дода шудаанд');
INSERT INTO "pioneers" VALUES(46,17,'46:17','Ва он ки ба падару модараш гуфт: «Уф бар шумо! Чи гуна ба ман ваъда медиҳед ки [аз гӯр] берун оварда хоҳам шуд, дар ҳоле ки пеш аз ман наслҳои фаровоне рафтанд [ва боз нагаштанд]?» Падару модараш ба даргоҳи илоҳӣ зорӣ мекунанд [ки фарзандашонро ҳидоят кунад ва ба ӯ мегӯянд] «Вой бар ту! Имон биёвар, ки ваъдаи илоҳӣ рост аст», [аммо] ӯ посух медиҳад: «Ин [ваъдаҳо] фақат афсонаҳои пешиниён аст»');
INSERT INTO "pioneers" VALUES(46,18,'46:18','Ин афрод, дар зумраи гурӯҳҳое аз ҷинну инс ҳастанд, ки пештар фармони азоби илоҳӣ дар мавридашон таҳаққуқ ёфтааст. Бе гумон, онон зиёнкор буданд');
INSERT INTO "pioneers" VALUES(46,19,'46:19','Ҳар гурӯҳ [биҳиштӣ ё дузахӣ] дараҷоте бар ҳасби аъмолашон доранд [то ҳаққе зоеъ нагардад]. Ва Аллоҳ таоло аъмолашонро ба тамомии пурагӣ медиҳад, бе он ки дар ҳаққашон ситаме шавад');
INSERT INTO "pioneers" VALUES(46,20,'46:20','Рӯзе, ки кофирон бар оташ арза мешаванд [ба онон хитоб мешавад]: «Неъматҳои покизаи худро дар зиндагии дунё аз байн бурдед ва аз онҳо баҳраманд шудед, пас, имрӯз ба хотири бартариҷӯии ноҳақ ва инҳирофе, ки дар замин доштед, бо азобе дарднок муҷозот мешавед»');
INSERT INTO "pioneers" VALUES(46,21,'46:21','[Эй паёмбар, саргузашти Ҳуд] бародари қавми Одро ёд кун, он гоҳ ки дар сарзамини «Аҳқоф» [регзоре дар Яман] қавмашро ҳушдор медод, дар ҳоле ки ҳушдордиҳандагони [дигаре низ] дар гузаштаҳои наздик ва дур омада буданд [ва чунин мегуфт], ки: «Танҳо Аллоҳро бипарастед, ки ман аз азоби рӯзи бузурге бар шумо метарсам»');
INSERT INTO "pioneers" VALUES(46,22,'46:22','Онон гуфтанд: «Оё омадаӣ, ки моро аз [парастиши] маъбудонамон бозгардонӣ? Агар рост мегӯӣ, азобе, ки ба мо ваъда медиҳӣ, пеш овар»');
INSERT INTO "pioneers" VALUES(46,23,'46:23','[Ҳуд] гуфт: «[Вақти нузули азобро] Фақат Аллоҳ таоло медонад ва ман паёмеро, ки маъмур [-и иблоғ]-и он шудаам, ба шумо мерасонам, вале шуморо гурӯҳе нодон мебинам»');
INSERT INTO "pioneers" VALUES(46,24,'46:24','Ва чун он [азоб]-ро, ки ба сурати абре дар ҷиҳати [дашту] дарраҳо [-и ҳосилхез]- ашон пеш меомад, мушоҳида карданд, [бо шодмонӣ] гуфтанд: «Ин абрест, ки [раҳмате] бар мо меборад». [Ҳуд гуфт: на] Балки ҳамон [азобе] аст, ки барои расиданаш шитоб доштед; тундбодест, ки азобе дарднок бо худ дорад');
INSERT INTO "pioneers" VALUES(46,25,'46:25','[Тӯфонест, ки] Ҳар чизеро ба фармони Парвардигораш дар ҳам мекӯбад». Он гоҳ чунон [ҳалок] шуданд, ки ҷуз хонаҳо [-и вайрон]-и онон, асаре ба чашм намехӯрад. Мо гунаҳкоронро ин гуна кайфар медиҳем');
INSERT INTO "pioneers" VALUES(46,26,'46:26','Ба қавми [мунқаризшудаи] Од чунон сарвату қудрате дода будем, ки ба шумо [маккагиён ҳаргиз] надодаем ва бо он ки ба онон низ чашму гӯш ва дил дода будем, аммо гӯшу чашм ва дилҳояшон суде ба ҳолашон надошт, зеро оёти илоҳиро инкор мекарданд ва [саранҷом] азобе, ки масхарааш мекарданд, домангирашон шуд');
INSERT INTO "pioneers" VALUES(46,27,'46:27','[Бисёре аз мардуми] шаҳрҳои перомуни шумо [маккагиён]-ро ҳалок кардем, ва [-ле қаблан] нишонаҳо [ва далоили тавҳид]-ро ба шаклҳои мухталиф дар миён овардем, бошад, ки [аз куфру гумроҳӣ] бозгарданд');
INSERT INTO "pioneers" VALUES(46,28,'46:28','Пас, чаро бутҳое, ки [мушрикон ононро] барои тақарруб [ба Аллоҳ таоло] маъбудони худ гирифтаанд, [ба ҳангоми бало] ёриашон накарданд? Балки аз назарашон нопадид шуданд ва [саранҷоми] дурӯғҳову туҳматҳояшон ҳамин буд');
INSERT INTO "pioneers" VALUES(46,29,'46:29','[Эй паёмбар, ба ёд овар] ҳангоме ки гурӯҳе аз ҷинро сӯйи ту фиристодем, ки Қуръонро бишунаванд, пас, чун назди паёмбар ҳузур ёфтанд, [ба якдигар] гуфтанд: «Хомӯш бошед [то ба оёти Қуръон гӯш фаро диҳем] ва чун [тиловаташ] ба поён расид, назди қавми худ бозгаштанд, то ба онон [дар мавриди авоқиби сарпечӣ аз фармони ҳақ] ҳушдор диҳанд');
INSERT INTO "pioneers" VALUES(46,30,'46:30','Гуфтанд: «Эй қавм, мо [оёти] китоберо шунидем, ки пас аз Мӯсо нозил шудааст ва китобҳо [-и осмонӣ]-и пеш аз худро тасдиқ мекунад ва ба [сӯйи] ҳаққу роҳи рост ҳидоят менамояд');
INSERT INTO "pioneers" VALUES(46,31,'46:31','Эй қавм, [суханони тавҳидии] ин даъватгарро, ки ба сӯйи Аллоҳ таоло фаро мехонад, бипазиред ва ба ӯ имон оваред, то [Аллоҳ таоло] бархе аз гуноҳонатонро бибахшад ва шуморо аз азобе дарднок дар амон бидорад');
INSERT INTO "pioneers" VALUES(46,32,'46:32','Ва ҳар ки [суханони] даъватгареро, ки ба сӯйи Аллоҳ таоло фаро мехонад, напазирад [ва ба вай имон наоварад, ҳаргиз Аллоҳро] дар замин ба танг намеоварад ва дар баробари Аллоҳ таоло [ёвар ва] корсозе нахоҳад дошт. Чунин касоне дар гумроҳии ошкоранд»');
INSERT INTO "pioneers" VALUES(46,33,'46:33','Оё мушрикон надидаанд [ва намедонанд], Аллоҳ таоло, ки осмонҳо ва заминро офарид ва аз офариниши онҳо дармонда нашуд, қодир аст, ки мурдагонро низ зинда кунад? Оре, Ӯ бар ҳар коре тавоност');
INSERT INTO "pioneers" VALUES(46,34,'46:34','Рӯзе, ки кофирон бар оташ арза мешаванд [аз онон пурсида мешавад]: «Оё ин оташ ҳақ нест [ва ҳақиқат надорад]?» Онон мегӯянд: «Оре, савганд ба Парвардигорамон [ки чунин аст]». [Он гоҳ Аллоҳ таоло] Мегӯяд: «Пас, ба сазои он ки [қиёматро] инкор мекардед, азобро бичашед»');
INSERT INTO "pioneers" VALUES(46,35,'46:35','Пас, [эй паёмбар] ту низ ҳамчун паёмбарони улулазм [ки ҳамвора собитқадам буданд, дар баробари озору такзиби қавмат] шикебо бош ва дар мавриди [дархости азоби] онон шитоб накун. Рӯзе, ки [ин кофирон] азоби мавъудашонро [дар ҷаҳони охират] бубинанд, [кайфарашон чунон тӯлонӣ аст, ки] гӯӣ фақат бахше аз рӯзро [дар дунё] ба сар бурдаанд. Ин Қуръон паёми [умумӣ барои ҷинну инс] аст. Оё ҷуз нофармонон ҳалок мешаванд?');
INSERT INTO "pioneers" VALUES(47,1,'47:1','Аллоҳ таоло талоши кофиронро, ки [мардумро] аз роҳи Аллоҳ таоло бозмедоранд, табоҳ мекунад');
INSERT INTO "pioneers" VALUES(47,2,'47:2','Ва онон, ки муъмин ва накукор буданд ва ба оёти ҳаққе, ки аз ҷониби Парвардигорашон бар Муҳаммад нозил шудааст, имон овардаанд, бадиҳояшонро мезудояд ва умур [-и дунё ва дин]-ашонро сомон мебахшад [ва ислоҳ мекунад]');
INSERT INTO "pioneers" VALUES(47,3,'47:3','Ин [подош ва ҷазо] бад-он сабаб аст, ки кофирон пайрави ботил шуданд ва муъминон пайрави [оёти] ҳаққи Парвардигорашон. Аллоҳ таоло барои мардум масалҳояшонро чунин баён мекунад');
INSERT INTO "pioneers" VALUES(47,4,'47:4','Чун бо кофирон рӯ ба рӯ шудед, ононро гардан бизанед, то он ки [бисёре аз ононро] бикушед, он гоҳ асиронро ба банд кашед, сипас ё бар онон миннат гузоред [ва бидуни ҳеҷ чашмдоште озодашон кунед] ё [аз онон] фидя бигиред [ва раҳояшон кунед], то сангинии ҷанг фурукаш кунад. Ин [ҳукми илоҳӣ] аст ва агар Аллоҳ таоло мехост, худ аз онон интиқом мегирифт, вале [фармони ҷанг дод] то шуморо баробари якдигар озмоиш кунад ва касоне, ки дар роҳи Аллоҳ таоло кушта шуданд, [Аллоҳ таоло ҳаргиз] натиҷаи талошашонро табоҳ намекунад');
INSERT INTO "pioneers" VALUES(47,5,'47:5','Аллоҳ таоло [ба роҳи рост] ҳидояташон хоҳад кард ва умурашонро сомон хоҳад бахшид');
INSERT INTO "pioneers" VALUES(47,6,'47:6','ва ононро ба биҳиште, ки [авсофашро] барояшон баён кардааст, ворид хоҳад намуд');
INSERT INTO "pioneers" VALUES(47,7,'47:7','Эй касоне, ки имон овардаед, агар [дину паёмбари] Аллоҳро ёрӣ кунед, ӯ низ шуморо ёрӣ мекунад ва гомҳоятонро [собиту] устувор медорад');
INSERT INTO "pioneers" VALUES(47,8,'47:8','Ва бар кофирон нагунсорӣ ва ҳалок бод! Аллоҳ таоло аъмолашонро табоҳ мегардонад');
INSERT INTO "pioneers" VALUES(47,9,'47:9','Зеро он чиро, ки Аллоҳ таоло нозил кардааст, хуш надоштанд, пас, Аллоҳ таоло низ аъмолашонро табоҳ намуд.');
INSERT INTO "pioneers" VALUES(47,10,'47:10','Оё дар замин гардиш накардаанд, то бубинанд саранҷоми касоне, ки пеш аз онон буданд, чи гуна будааст? Аллоҳ таоло [хонаҳояшонро] бар сарашон вайрон кард [ва ононро нобуд сохт] ва ин кофирон [низ кайфаре] ҳаммонанди он [дар пеш] хоҳанд дошт');
INSERT INTO "pioneers" VALUES(47,11,'47:11','Зеро Аллоҳ таоло [ёвару] корсози муъминон аст, ва[-ле] кофирон ҳеҷ корсозе надоранд');
INSERT INTO "pioneers" VALUES(47,12,'47:12','Аллоҳ таоло муъминони накукорро ба боғҳое [аз биҳишт] ворид мекунад, ки ҷӯйборҳо аз зери [дарахтони] он ҷорӣ аст ва кофирон [танҳо дар дунё] баҳраманд мешаванд ва ҳамчун чаҳорпоён мехӯранд ва оташ [-и дузах] ҷойгоҳашон аст');
INSERT INTO "pioneers" VALUES(47,13,'47:13','Ва чи бисёр шаҳрҳое, ки сокинонаш аз [мардуми] шаҳри ту, ки берунат кардаанд, тавонмандтар буданд. Мо ҳалокашон кардем ва [дар баробари азоби илоҳӣ] ҳеҷ ёваре надоштанд');
INSERT INTO "pioneers" VALUES(47,14,'47:14','Оё касе, ки бар шеваи равшане аз ҷониби Парвардигори хеш аст, ҳамчун касоне аст, ки зиштии рафторашон дар назарашон ороста шуда ва аз ҳавасҳои хеш пайравӣ мекунанд?');
INSERT INTO "pioneers" VALUES(47,15,'47:15','Васфи биҳиште, ки ба парҳезкорон ваъда дода шуда, [чунин аст, ки] дар он ҷӯйборҳое ҷорӣ аст, ки таъми обаш тағйирнопазир аст ва ҷӯйборҳое аз шир, ки таъмаш дигаргун намешавад ва ҷӯйборҳое аз шароб, ки мояи лаззати нӯшандагон аст ва ҷӯйборҳое аз асали ноб ва дар он ҷо ҳамаи [анвои] меваҳо дар дастрасашон аст ва [аз ҳама бартар] омурзиши Парвардигорашон. Оё [биҳиштиён] чун сокинони ҷовидони дузаханд, ки обе чунон ҷӯшон ба онон менӯшонанд, ки рӯдаҳояшонро муталошӣ [пора-пора] мекунад?');
INSERT INTO "pioneers" VALUES(47,16,'47:16','[Эй паёмбар] бархе аз мунофиқон ба [гуфтори] ту гӯш фаро медиҳанд ва чун аз ҳузурат хориҷ мешаванд, ба аҳли дониш [бо тамасхур] мегӯянд: «[Фаҳмидед] Лаҳазоте пеш чи мегуфт?» Аллоҳ таоло бар дилҳояшон муҳри [ғафлат] ниҳодааст ва онон аз ҳавасҳои хеш пайравӣ кардаанд');
INSERT INTO "pioneers" VALUES(47,17,'47:17','Аллоҳ таоло бар ҳидояти парҳезкорон меафзояд ва парҳезкориашон [-ро қувват] мебахшад');
INSERT INTO "pioneers" VALUES(47,18,'47:18','Оё кофирон ҷуз ин интизор доранд, ки қиёмат ногаҳон бар онон фаро расад, дар ҳоле ки нишонаҳои он ошкор шудааст? Пас, он гоҳ ки қиёмат фаро расад, панд гирифтанашон чи суде хоҳад дошт?');
INSERT INTO "pioneers" VALUES(47,19,'47:19','[Эй паёмбар] бидон, ки маъбуде [ба ҳақ] ба ҷуз Аллоҳ таоло нест ва барои гуноҳи хеш ва [гуноҳи] мардон ва занони муъмин омурзиш бихоҳ ва [ба ёд дошта бош, ки] Аллоҳ таоло маҳалли рафтуомад [-и рӯзона] ва оромидан [-и шабона]-и шуморо медонад');
INSERT INTO "pioneers" VALUES(47,20,'47:20','Муъминон мегӯянд: «Чаро сурае [дар бораи иҷозати ҷиҳод] нозил намешавад?» Аммо ҳангоме ки оёти сареҳе дар мавриди ҷанг нозил мегардад, [мунофиқону] бемордилонро мебинӣ, ки ҳамчун касе, ки аз [сахтии] марг дар ҳоли беҳушӣ шуда бошад, ба ту менигаранд, [маргу азоби илоҳӣ] бар онон шоистатар аст');
INSERT INTO "pioneers" VALUES(47,21,'47:21','Итоату гуфтори шоиста [беҳтар аст] ва чун тасмими қатъӣ [-и ҷиҳод] гирифта шавад, агар [дар тааҳҳуди худ] бо Аллоҳ таоло содиқ бошанд, барои онон [аз нифоқ ва саркашӣ] беҳтар аст');
INSERT INTO "pioneers" VALUES(47,22,'47:22','[Эй мунофиқон] Оё [ҷуз ин] интизор доред, ки агар [аз Қуръону суннати паёмбараш] рӯй бигардонед, дар замин табоҳӣ кунед ва аз хешовандонатон бибуред?');
INSERT INTO "pioneers" VALUES(47,23,'47:23','Инон касоне ҳастанд, ки Аллоҳ таоло [бо дурӣ аз раҳмати хеш] лаънаташон кардааст, пас, [гӯши дили] эшонро кар ва чашми [дили] ононро кӯр сохтааст');
INSERT INTO "pioneers" VALUES(47,24,'47:24','Оё дар Қуръон тадаббур намекунанд ё бар дилҳояшон қуфлҳо[-и ғафлат] ниҳода шудааст?');
INSERT INTO "pioneers" VALUES(47,25,'47:25','Касоне, ки пас аз равшан шудани [роҳи] ҳидоят ба роҳи пешини хеш бозгаштаанд [ва муртад шуданд], шайтон [аъмоли зишташонро] барояшон ороста ва [бо орзуҳои дароз] фиребашон додааст');
INSERT INTO "pioneers" VALUES(47,26,'47:26','Ин [ҷаҳлу гумроҳӣ] бад-он хотир аст, ки мунофиқон ба мушриконе, ки аз ваҳйи илоҳӣ кароҳат доштанд, гуфтанд: «Мо дар бархе аз умур аз шумо пайравӣ мекунем» ва Аллоҳ таоло пинҳонкориашонро медонад');
INSERT INTO "pioneers" VALUES(47,27,'47:27','Пас, он гоҳ ки фариштагони илоҳӣ бар чеҳра ва пушташон мезананд ва ҷонашонро мегиранд, [ҳоли ин мунофиқон] чи гуна хоҳад буд?');
INSERT INTO "pioneers" VALUES(47,28,'47:28','Ин [кайфар] аз он рӯст, ки онон пайрави роҳе шуданд, ки Аллоҳро ба хашм меоварад ва аз анҷоми корҳое, ки муҷиби хушнудии илоҳӣ мешавад, кароҳат доштанд. Пас, [Аллоҳ таоло низ натиҷаи] корҳояшонро табоҳ сохт');
INSERT INTO "pioneers" VALUES(47,29,'47:29','Оё бемордилон [-и мунофиқ] пиндоштаанд, ки Аллоҳ таоло ҳаргиз кинаҳояшонро ошкор нахоҳад сохт?');
INSERT INTO "pioneers" VALUES(47,30,'47:30','[Эй паёмбар] агар мехостем, ононро ба ту нишон медодем, ки [ботини] эшонро аз зоҳирашон бозшиносӣ; ва мусалламан аз шеваи суханашон ба хубӣ ононро хоҳӣ шинохт ва [бидонед, ки] Аллоҳ таоло аз корҳоятон огоҳ аст');
INSERT INTO "pioneers" VALUES(47,31,'47:31','Ҳатман, [ҳамаи] шуморо озмоиш мекунем, то аз миёнатон муҷоҳидон ва шикебоёнро бозшиносем ва аҳволи шуморо меозмоем [то ростгӯён мушаххас гарданд]');
INSERT INTO "pioneers" VALUES(47,32,'47:32','Касоне, ки пас аз равшан шудани роҳи ҳидоят бар онон, куфр варзиданд ва мардумро аз роҳи Аллоҳ таоло боздоштанд ва ба мухолифат бо паёмбар бархостанд, ҳаргиз зиёне ба Аллоҳ таоло нахоҳанд расонид ва [Аллоҳ таоло низ натиҷаи] аъмолашонро табоҳ хоҳад сохт');
INSERT INTO "pioneers" VALUES(47,33,'47:33','Эй муъминон, ба Аллоҳ таоло ва паёмбар итоат кунед ва [бо куфру риё] аъмоли [шоистаи] хешро табоҳ накунед');
INSERT INTO "pioneers" VALUES(47,34,'47:34','Касоне, ки куфр варзиданд ва [дигаронро низ] аз роҳи Аллоҳ таоло боздоштанд ва дар ҳоли куфр мурданд, Аллоҳ таоло ҳаргиз ононро нахоҳад омурзид');
INSERT INTO "pioneers" VALUES(47,35,'47:35','Пас, [эй муъминон аз рӯёрӯӣ бо душманон] сустӣ наварзед ва [аз тарси ҷанг ононро] ба сулҳу оштӣ даъват накунед, дар ҳоле ки шумо бартаред ва Аллоҳ таоло бо шумост ва аз [подоши] аъмолатон нахоҳад кост');
INSERT INTO "pioneers" VALUES(47,36,'47:36','Ба ростӣ, ки зиндагии дунё бозича ва саргармӣ аст; ва агар имон биёваред ва парҳезкорӣ кунед, [Аллоҳ таоло] подошатонро [бекаму кост] хоҳад дод ва [тамоми] амволатонро намехоҳад [балки мефармояд, ки закоташро бипардозед]');
INSERT INTO "pioneers" VALUES(47,37,'47:37','Агар [тамоми] онро аз шумо бихоҳад ва [бар ин дастур] исрор кунад, бухл меварзед ва [ин кор] кинаҳоятонро ошкор месозад');
INSERT INTO "pioneers" VALUES(47,38,'47:38','Огоҳ бошед! Шумо касоне ҳастед, ки барои инфоқ дар роҳи Аллоҳ таоло даъват мешавед, вале бархе аз шумо бухл меварзанд ва ҳар ки бухл биварзад, ба зиёни хеш бухл варзидааст ва Аллоҳ таоло бениёз аст ва [ин шумо ҳастед, ки] ниёзмандед ва агар [аз ислом ба сӯйи куфр] бозгардед, Аллоҳ таоло гурӯҳи дигареро ҷойгузинатон хоҳад кард, ки ҳамчун шумо [ба бухл мубтало] набошанд');
INSERT INTO "pioneers" VALUES(48,1,'48:1','[Эй паёмбар] Ба ростӣ, ки Мо пирӯзии ошкореро [дар сулҳи Ҳудайбия] бароят муқаддар кардем');
INSERT INTO "pioneers" VALUES(48,2,'48:2','То Аллоҳ таоло гуноҳи гузашта ва ояндаатро биёмурзад ва неъматашро бар ту тамом гардонад ва ба роҳи рост ҳидоятат намояд');
INSERT INTO "pioneers" VALUES(48,3,'48:3','Ва Аллоҳ таоло ба пирӯзии шикастнопазире ёриат кунад');
INSERT INTO "pioneers" VALUES(48,4,'48:4','Ӯст, ки бар дилҳои муъминон оромиш нозил кард, то бар имонашон бияфзоянд; ва сипоҳиёни осмонҳо ва замин аз они Аллоҳ таоло аст; ва Аллоҳ таоло донову ҳаким аст');
INSERT INTO "pioneers" VALUES(48,5,'48:5','[Оре, чунин кард] То мардону занони муъминро ба боғҳое [биҳиште] ворид кунад, ки ҷӯйборҳо аз зери [дарахтони] он ҷорӣ аст; ҷовидона дар он ҷо хоҳанд буд; ва гуноҳонашонро бизудояд [ва биёмурзад]; ва ин назди Аллоҳ таоло [барои муъминин] комёбии бузурге аст');
INSERT INTO "pioneers" VALUES(48,6,'48:6','Ва мунофиқону мушриконро – дар умум аз зану мард, ки нисбат ба Аллоҳ таоло бадгумонанд, муҷозот кунад; ногуворӣ [ва азоби илоҳӣ] бар эшон бод! Аллоҳ таоло бар онон хашм гирифта ва аз раҳмати хеш дурашон карда ва дузахро барояшон омода намудааст; ва [ба ростӣ, ки дузах] чи бад ҷойгоҳе аст!');
INSERT INTO "pioneers" VALUES(48,7,'48:7','Сипоҳиёни осмонҳо ва замин аз они Аллоҳ таоло аст; ва Аллоҳ таоло шикастнопазиру ҳаким аст');
INSERT INTO "pioneers" VALUES(48,8,'48:8','[Эй Паёмбар] Мо туро гувоҳ ва муждадиҳанда ва ҳушдордиҳанда фиристодем');
INSERT INTO "pioneers" VALUES(48,9,'48:9','То [шумо мардум] ба Аллоҳ таоло ва Паёмбараш имон биёваред ва [дини] Ӯро ёрӣ кунед ва бузургаш доред ва бомдоду шомгоҳ Аллоҳро тасбеҳ гӯед');
INSERT INTO "pioneers" VALUES(48,10,'48:10','Бе тардид, касоне, ки бо ту байъат мекунанд, дар ҳақиқат, бо Аллоҳ таоло байъат мекунанд; дасти Аллоҳ таоло болои дастҳои онон аст. Ҳар ки [пас аз байъат] паймоншиканӣ кунад, ба зиёни хеш паймон шикастааст; ва ҳар ки ба паймонаш [дар асоси ёрии дини Аллоҳ таоло ва паёмбараш] вафо кунад, ба зудӣ [Аллоҳ таоло] подоши бузурге ба ӯ хоҳад дод');
INSERT INTO "pioneers" VALUES(48,11,'48:11','Бодияшинони мутахаллиф [ки аз ширкат дар сафари Макка ва сулҳи Ҳудайбия худдорӣ кардаанд, барои тавҷеҳи кори худ] хоҳанд гуфт: «[Муҳофизати] Амвол ва хонаводаамон моро гирифтор кард; бароямон омурзиш бихоҳ». Онон чизе, ки дар дилҳояшон нест, бар забон меоваранд. Бигӯ: «Агар Аллоҳ таоло ирода кунад, ки ба шумо зиён ё суде бирасад, кист, ки дар муқобили [хости] Аллоҳ таоло аз шумо дифоъ кунад? Оре, Аллоҳ таоло аз он чӣ мекунед, огоҳ аст»');
INSERT INTO "pioneers" VALUES(48,12,'48:12','[Узратон ин набуд;] Балки гумон мекардед, ки Паёмбару муъминон [-и ҳамроҳаш аз ин сафари хатарнок] ҳаргиз назди хонаводаи хеш бознахоҳанд гашт; ва ин [андеша] дар дилҳоятон ороста шуд ва гумони бад бурдед [ки Аллоҳ таоло динашро ёрӣ нахоҳад кард; пас, ба хато рафтед] ва ҳалок шудед');
INSERT INTO "pioneers" VALUES(48,13,'48:13','Ҳар ки ба Аллоҳу паёмбараш имон наоварад, пас, [бидонад, ки] мо барои кофирон оташе сӯзон омода кардаем');
INSERT INTO "pioneers" VALUES(48,14,'48:14','Фармонравоии осмонҳо ва замин аз они Аллоҳ таоло аст; ҳар киро бихоҳад меомурзад ва ҳар киро бихоҳад, азоб мекунад; ва Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(48,15,'48:15','[Эй муъминон] Ҳангоме ки озим [-и Хайбар ва] ба гирифтани ғанимат мешавед, мутахаллифон [аз сафари Макка] хоҳанд гуфт: «Иҷозат диҳед, мо низ ба дунболи шумо биёем». Онон мехоҳанд, каломи илоҳиро тағйир диҳанд. [Эй Паёмбар] Ба эшон бигӯ: «Шумо ҳаргиз ба дунболи мо нахоҳед омад [зеро ин ғаниматҳо махсуси касоне аст, ки дар Ҳудайбия ҳузур доштанд ва дар бораи ин ҳукм] Аллоҳ таоло қаблан чунин фармудааст»; он гоҳ хоҳанд гуфт: «[На] Балки шумо нисбат ба мо ҳасад меварзед». [Ҳаргиз чунин нест] Балки ҷуз иддаи каме [аз онон авомири илоҳиро] дарнамеёбанд');
INSERT INTO "pioneers" VALUES(48,16,'48:16','[Эй Паёмбар] Ба мутахаллифон бигӯ: «Ба зудӣ ба муқобила бо гурӯҳе ҷанговар фаро хонда хоҳед шуд, ки ононро аз пой дароваред, магар ин ки таслим шаванд; агар итоат кунед, Аллоҳ таоло подоши накуе ба шумо хоҳад дод; ва агар ҳамчун гузашта сарпечӣ кунед, шуморо бо азобе дарднок муҷозот хоҳад кард»');
INSERT INTO "pioneers" VALUES(48,17,'48:17','Бар нобино ва ланг ва бемор гуноҳе нест [ки дар ҷанг ширкат накунанд]; ва ҳар ки аз Аллоҳ таоло ва паёмбараш итоат кунад, [Аллоҳ таоло] ӯро ба боғҳое [аз биҳишт] ворид мекунад, ки ҷӯйборҳо аз зери [дарахтони] он ҷорӣ аст; ва ҳар ки сарпечӣ кунад, ба азобе дарднок муҷозоташ хоҳад кард');
INSERT INTO "pioneers" VALUES(48,18,'48:18','Аллоҳ таоло аз муъминоне, ки дар канори он дарахт [дар Ҳудайбия] бо ту байъат карданд, хушнуд шуд ва донист, ки дар дилашон чӣ мегузарад; аз ин рӯ оромиш бар онон фуру фиристод ва ба пирӯзии наздик [дар Хайбар] подошашон дод');
INSERT INTO "pioneers" VALUES(48,19,'48:19','ва [ҳамчунин] ғаниматҳои фаровоне, ки ба даст меоварданд; ва Аллоҳ таоло ҳамвора шикастнопазир [ва] ҳаким аст');
INSERT INTO "pioneers" VALUES(48,20,'48:20','Аллоҳ таоло ғаниматҳои фаровонеро ба шумо ваъда дод, ки [дар оянда] ба онҳо даст хоҳед ёфт; пас, ин [пирӯзӣ ва ғаниматҳои Хайбар]-ро барои шумо зудтар фароҳам сохт ва дасти [тааддӣ ва таҷовузи] душманро аз шумо кӯтоҳ намуд, то барои муъминон нишонаи [аз лутфи илоҳӣ] бошад ва шуморо ба роҳи рост ҳидоят кунад');
INSERT INTO "pioneers" VALUES(48,21,'48:21','Ва [ҳамчунин ғаниматҳои] дигаре [дар интизори шумост], ки ҳанӯз ба онҳо даст наёфтаед ва Аллоҳ таоло бар онҳо иҳота дорад [ва аз онҳо огоҳ аст]. Ба ростӣ, ки Аллоҳ таоло бар ҳар коре тавоност');
INSERT INTO "pioneers" VALUES(48,22,'48:22','Агар кофирон ба ҷанги шумо биёянд [аз таваккал ва салобати шумо] пушт карда ва мегурезанд; пас, аз он низ ҳеҷ корсоз ва ёваре нахоҳанд ёфт');
INSERT INTO "pioneers" VALUES(48,23,'48:23','Ин суннати илоҳӣ аст, ки пештар низ ҷорӣ будааст; ва ҳаргиз дар суннати илоҳӣ тағйир ва табдиле нахоҳӣ ёфт');
INSERT INTO "pioneers" VALUES(48,24,'48:24','Ӯст, ки дар Ҳудайбия дасти ононро аз шумо кӯтоҳ намуд [ва монеъ аз он шуд, ки шуморо ба ҷанг во доранд]; ва пас аз пирӯз сохтани шумо дар Макка низ дасти шуморо аз онон боздошт [ки ононро накушед ва ба асорат нагиред]; ва Аллоҳ таоло ҳамвора ба он чӣ мекунед, биност');
INSERT INTO "pioneers" VALUES(48,25,'48:25','Онон касоне буданд, ки кофир шуданд ва шуморо аз [вуруд ба] Масҷид-ул-ҳаром боздоштанд ва низ аз расидани қурбониҳои шумо ба қурбонгоҳаш [дар Мино] ҷилавгирӣ карданд; ва агар мардону занони муъмине [байни мардуми Макка] набуданд, ки [дар сурати ошкор шудани даргирӣ мумкин буд] ононро надониста поймол кунед ва нохоста муртакиби гуноҳ шавед, [фармони ҳамла ба Макка медодем; вале Аллоҳ таоло чунин кард] то [аз маккагиён] ҳар киро бихоҳад, фарогири раҳмати хеш қарор диҳад; ва агар [муъминону кофирон дар Макка] аз якдигар ҷудо буданд, кофиронашонро бо азобе дарднок муҷозот мекардем');
INSERT INTO "pioneers" VALUES(48,26,'48:26','[Ба ёд овар] Он гоҳ ки кофирони [Макка дар Ҳудайбия] қалбан дастхуши таассуб, [он ҳам] таассуби ҷоҳилият шуда буданд, Аллоҳ таоло оромиши [хотире аз ҷониби] хеш бар паёмбараш ва бар муъминин фуру фиристод ва ононро ба асл [ва асоси тавҳид ва] парҳезкорӣ [ло илоҳа илла-Аллоҳ] пойбанд сохт; чаро ки [нисбат ба мушрикон] ба он сазовортар ва шоистатар буданд; ва Аллоҳ таоло ба ҳар чизе доност');
INSERT INTO "pioneers" VALUES(48,27,'48:27','Аллоҳ таоло руъёи расулашро ба дурустӣ таҳаққуқ бахшид, ки ба хости илоҳӣ, дар ҳоле ки сарҳоятонро тарошида муйҳоятонро кӯтоҳ кардаед, дар эминӣ ва бидуни тарс [аз душман] ба Масҷиду-л-ҳаром ворид хоҳед шуд. [Аллоҳ таоло] Чизҳоеро медонист, ки шумо намедонистед; ва илова бар фатҳи Макка пирӯзии наздики [Хайбар]-ро [низ бароятон] муқаррар намуд');
INSERT INTO "pioneers" VALUES(48,28,'48:28','Ӯст, ки паёмбарашро бо [китоби] ҳидоят ва оини ҳақ фиристод, то [ҳаққонияти бартарии] онро бар ҳамаи адён ошкор гардонад; ва Аллоҳ таоло ба унвони гувоҳ кофӣ аст');
INSERT INTO "pioneers" VALUES(48,29,'48:29','Муҳаммад расулуллоҳ ва [саҳобаи муъмин ва фидокораш ҳамон] касоне, ки ҳамвора бо ӯ ҳастанд, дар баробари кофирон [-и ситезаҷӯ] муҳкаму сахтгиранд ва бо якдигар меҳрубон; эшонро дар ҳоли рукуъ ва саҷда мебинӣ, ки пайваста фазлу хушнудии Аллоҳро меҷӯянд; нишони [имону парҳезгории] онон дар чеҳраҳояшон аз асари саҷда ва ибодат ҳувайдост; ин васфи эшон дар Таврот аст; ва тавсифашон дар Инҷил [низ] ҳамчун гиёҳе аст, ки ҷавонаашро бармеоварад ва онро неру мебахшад, то муҳкам гардад ва бар соқаҳояш устувор биистад; чунон ки муҷиби шигифтии кишоварзон шавад; дар натиҷа, Аллоҳ таоло бо [тақвият ва пешрафти] муъминон кофиронро ба хашм дармеоварад. Аллоҳ таоло ба муъминони накукор [саҳоба ва пайравонашон] ваъдаи омурзиш ва подоше бузург додааст');
INSERT INTO "pioneers" VALUES(49,1,'49:1','Эй муъминон, дар баробари Аллоҳ таоло ва паёмбараш [дар ҳеҷ коре] пешӣ нагиред; ва аз Аллоҳ таоло парво кунед, ки Аллоҳ таоло шунавову доност');
INSERT INTO "pioneers" VALUES(49,2,'49:2','Эй муъминон, садоятонро баландтар аз садои Паёмбар накунед; ва ҳамчунон ки бархе аз шумо бо бархе дигар баланд сухан мегӯяд, бо ӯ ба садои баланд сухан нагӯед. Мабодо бе онки бидонед, аъмолатон табоҳ гардад');
INSERT INTO "pioneers" VALUES(49,3,'49:3','Онон, ки садои худро назди расулуллоҳ поин меоваранд, Аллоҳ таоло дилҳояшонро барои парҳезкоре озмуда [ва холис гардонида] аст. Барои онон омурзишу подоши бузурге [дар пеш] аст');
INSERT INTO "pioneers" VALUES(49,4,'49:4','[Вале] Онон, ки аз пушти утоқҳо [-и ҳамсаронат] туро садо мезананд, бештарашон бехираданд');
INSERT INTO "pioneers" VALUES(49,5,'49:5','Ва агар сабр мекарданд, то худ барои мулоқоташон хориҷ шавӣ, барояшон беҳтар буд; ва Аллоҳ таоло омурзгору меҳрубон аст');
INSERT INTO "pioneers" VALUES(49,6,'49:6','Эй муъминон, агар шахси мунҳарифе хабаре бароятон оварад, таҳқиқ кунед, то мабодо надониста ба гурӯҳе [бегуноҳ] осеб бирасонед ва аз кардаи худ пушаймон шавед');
INSERT INTO "pioneers" VALUES(49,7,'49:7','Бидонед, ки расулуллоҳ миёни шумост [ва салоҳатонро беҳтар медонад]; агар дар бисёре аз умур аз [майлу назари] шумо пайравӣ кунад, дучори заҳмат мешавед; вале Аллоҳ таоло имонро маҳбуби шумо намуд ва онро дар қалбҳоятон биёрост [то ислом биёваред] ва куфру нофармонӣ ва саркаширо дар назаратон нохушоянд гардонд. Онон [ки ороста ба ин авсофанд] пӯяндагони роҳи ҳидоят ва камоланд');
INSERT INTO "pioneers" VALUES(49,8,'49:8','[Ин иноят] Ба унвони фазилату неъмате аз ҷониби Аллоҳ таоло [муқаррар шудааст] ва Аллоҳ таоло донои ҳаким аст');
INSERT INTO "pioneers" VALUES(49,9,'49:9','Агар ду гурӯҳ аз муъминон бо ҳам даргир шуданд, миёнашон оштӣ барқарор кунед ва агар як гурӯҳ бар дигаре таҷовуз кард, бо гурӯҳи мутаҷовиз биҷангед, то ба фармони Аллоҳ таоло боз ояд; агар бозгашт, байнашон сулҳе одилона барқарор кунед ва ба адолат рафтор кунед, ки Аллоҳ таоло одилонро дӯст дорад');
INSERT INTO "pioneers" VALUES(49,10,'49:10','Албатта, муъминон бародари якдигаранд; пас, миёни бародарон оштӣ барқарор кунед ва аз Аллоҳ таоло парво кунед; бошад, ки мавриди раҳмат қарор гиред');
INSERT INTO "pioneers" VALUES(49,11,'49:11','Ҳаргиз набояд гурӯҳе [аз шумо] гурӯҳи дигареро масхара кунад; чи басо инон аз онон беҳтар бошанд; ва занон низ набояд занони дигарро масхара намоянд; чи басо инон аз онон бартар бошанд; ва аз якдигар айбҷӯӣ накунед; ва якдигарро бо лақабҳо [-и зишт] хитоб накунед [ки ин кор мисдоқи нофармонӣ аз авомири илоҳӣ аст]; ва бадтарин вижагӣ [барои як мусулмон] ин аст, ки пас аз имон овардан [фисқу] нофармонӣ кунад; ва онон, ки тавба накунанд, [нисбат ба нафси хеш] ситамгоранд');
INSERT INTO "pioneers" VALUES(49,12,'49:12','Эй муъминон, аз бисёре аз гумонҳои бад бипарҳезед; чаро ки бархе аз гумонҳо гуноҳ аст; ва дар бораи якдигар таҷассусу ғайбат накунед; оё ҳеҷ як аз шумо дӯст дорад, ки гӯшти бародари мурдаашро бихӯрад? [Ҳатман] Онро хуш надоред [ғайбат низ ба ҳамин зиштӣ аст]; аз Аллоҳ таоло парво кунед, ки Аллоҳ таоло тавбапазиру меҳрубон аст');
INSERT INTO "pioneers" VALUES(49,13,'49:13','Эй мардум, мо шуморо аз [омезиши] марду зане офаридем ва ба ҷамоатҳо ва қабилаҳои мухталиф тақсим кардем, то якдигарро бозшиносед. Бе тардид, бузургвортарини шумо назди Аллоҳ таоло Парҳезкортарини шумост; ва Аллоҳ таоло [ба он чи мекунед] доно ва огоҳ аст');
INSERT INTO "pioneers" VALUES(49,14,'49:14','Аъроб [-и бодиянишин дар ҳузури паёмбар] гуфтанд: «Имон овардаем». [Эй паёмбар, ба онон] Бигӯ: «Шумо имон наёвардаед; вале бигӯед: «Ислом овардаем»; ва ҳанӯз имон дар қалбҳоятон нуфуз накардааст. Агар аз Аллоҳ таоло ва паёмбараш итоат кунед, аз [подоши] аъмоли шумо ҳеҷ нахоҳад кост. Ба ростӣ, ки Аллоҳ таоло омурзгору меҳрубон аст»');
INSERT INTO "pioneers" VALUES(49,15,'49:15','Муъминон [-и ҳақиқӣ] касоне ҳастанд, ки ба Аллоҳ таоло ва паёмбараш имон овардаанд ва [лаҳзае дар имонашон] тардид накарданд ва бо молу ҷонашон дар роҳи Аллоҳ таоло ба мубориза бархостанд; инонанд, ки [дар эътиқод ва амал] садоқат доранд');
INSERT INTO "pioneers" VALUES(49,16,'49:16','[Эй паёмбар, ба бодиянишинон] Бигӯ: «Оё мехоҳед, Аллоҳро аз дини хеш огоҳ кунед? Дар ҳоле ки Аллоҳ таоло ҳар чиро, ки дар осмонҳо ва замин аст, медонад; ва Аллоҳ таоло ба ҳар чизе огоҳ аст»');
INSERT INTO "pioneers" VALUES(49,17,'49:17','Онон бар ту миннат мениҳанд, ки ислом овардаанд; бигӯ: «Миннати исломи хеш бар ман нагузоред; балки агар [дар ошкор кардани имон] садоқат доред, ин Аллоҳ таоло аст, ки бар шумо миннат дорад, ки ба сӯйи имон ҳидоятатон намудааст»');
INSERT INTO "pioneers" VALUES(49,18,'49:18','Бе тардид, Аллоҳ таоло асрори осмонҳо ва заминро медонад ва ба он чи мекунед, биност');
INSERT INTO "pioneers" VALUES(50,1,'50:1','[Қоф]; савганд ба Қуръони арҷманд [ки растохез омаданӣ аст]');
INSERT INTO "pioneers" VALUES(50,2,'50:2','Мушрикон аз ин ки ҳушдордиҳандае аз миёни худашон ба суроғашон омадааст, ибрози шигифтӣ мекунанд ва мегӯянд: «Моҷарои аҷибе аст');
INSERT INTO "pioneers" VALUES(50,3,'50:3','Он ҳангоме ки мурдем ва хок шудем [дубора барангехта хоҳем шуд?]. Ин бозгаште баид аст»');
INSERT INTO "pioneers" VALUES(50,4,'50:4','Бе тардид, зарроте, ки замин аз бадани онон мекоҳад, [ҳамаро] медонем; ва китобе нигоҳбон дар ихтиёр дорем [ки ҳарчи бар онон муқаддар гашта, дар он сабт аст]');
INSERT INTO "pioneers" VALUES(50,5,'50:5','Вақте ки [Қуръон, яъне каломи] ҳақ бар онон расид, такзибаш карданд; пас, дар ҳолати саргардонӣ [ва парешонӣ] ҳастанд [ва дар бораи Қуръон ҳайронанд]');
INSERT INTO "pioneers" VALUES(50,6,'50:6','Оё кофирон осмонро бар фарозашон наменигаранд, ки чи гуна онро барафроштем ва [ба зевари ситорагон] оростем ва орӣ аз [ҳар гуна] нуқсу халал аст?');
INSERT INTO "pioneers" VALUES(50,7,'50:7','Ва заминро густурдем ва кӯҳҳои собит дар он пай афкандем ва аз ҳар навъ гиёҳи зебо [шодиангезе] дар он рӯёнидем');
INSERT INTO "pioneers" VALUES(50,8,'50:8','[Онҳоро офаридем] То барои ҳар бандаи тавбакор муҷиби бинишу панд бошад');
INSERT INTO "pioneers" VALUES(50,9,'50:9','Ва аз осмон бороне пурбаракат фиристодем ва бо он боғҳо [-и мева] ва [мазореи] дона [ҳо]-и даравкарданӣ рӯёнидем');
INSERT INTO "pioneers" VALUES(50,10,'50:10','Ва дарахтони саркашидаи хурмо бо хушаҳои барҳамнишаста');
INSERT INTO "pioneers" VALUES(50,11,'50:11','Барои рӯзии бандагон; ба василаи он [борон] сарзамини хазонзада ва хушкро ҳаёт бахшидем; хуруҷ аз қабрҳо [дар қиёмат] низ ҳамин гуна аст');
INSERT INTO "pioneers" VALUES(50,12,'50:12','Пеш аз онон низ қавми Нуҳу аҳолии «Рас» [чоҳ] ва қавми Самуд [паёмбаронашонро] дурӯғгӯ шумурданд');
INSERT INTO "pioneers" VALUES(50,13,'50:13','Ва [ҳамчунин қавми] Оду Фиръавн ва қавми Лут');
INSERT INTO "pioneers" VALUES(50,14,'50:14','ва аҳолии «Айка» [қавми Шуайб] ва қавми «Туббаъ»; ҳар як [аз онон] паёмбарони [илоҳӣ]-ро дурӯғгӯ шумурданд; пас, ваъдаи азоби ман [бар онон] таҳаққуқ ёфт');
INSERT INTO "pioneers" VALUES(50,15,'50:15','Оё мо дар офариниши нахустин фуру мондем [то аз бозофаринии он оҷиз бошем]? [Ҳаргиз] Балки онон аз офариниши ҷадид дар [ҳайрат ва] тардиданд');
INSERT INTO "pioneers" VALUES(50,16,'50:16','Мо инсонро офаридем ва аз васвасаҳои замираш огоҳем; ва аз шоҳрагаш ба ӯ наздиктарем');
INSERT INTO "pioneers" VALUES(50,17,'50:17','Вақте ду фаришта, ки бар самти росту чап муроқиб нишастаанд, [рафторашро] сабт мекунанд');
INSERT INTO "pioneers" VALUES(50,18,'50:18','Ҳар сухане, ки бар забон меоварад, дар канораш [фариштаи] муроқибе омодааст');
INSERT INTO "pioneers" VALUES(50,19,'50:19','Сахтиҳои марг, ба ростӣ, даррасид. [Эй инсони ғофил] Ин ҳамон чизест, ки аз он мегурехтӣ');
INSERT INTO "pioneers" VALUES(50,20,'50:20','Ва [барои дувумин бор] дар «сур» дамида мешавад [ва қиёмат барпо мегардад]. Он рӯз [рӯзи таҳаққуқи] ваъда [-и азоб] аст');
INSERT INTO "pioneers" VALUES(50,21,'50:21','Ва ҳар кас дар ҳоле [ки ду фаришта ҳамроҳиаш мекунанд, ба арсаи маҳшар] меояд; яке роҳ менамоёнад ва дигаре [бар аъмолаш] гувоҳ аст');
INSERT INTO "pioneers" VALUES(50,22,'50:22','[Ба ӯ гуфта мешавад:] «Ту аз ин [рӯз] дар ғафлат будӣ ва мо [бо азоби дузах] пардаи [ғафлат]-ро аз муқобилат бардоштем; акнун дидаат тезбин шудааст');
INSERT INTO "pioneers" VALUES(50,23,'50:23','[Фариштаи] Ҳамнишини вай мегӯяд: «Ин ҳамон [номаи аъмоли ӯ] аст, ки назди ман омадааст»');
INSERT INTO "pioneers" VALUES(50,24,'50:24','[Аллоҳ таоло ба он ду фаришта хитоб мекунад:] «Ҳар ситезаҷӯи ҳақситезеро дар дузах бияфканед');
INSERT INTO "pioneers" VALUES(50,25,'50:25','[Ҳар] Монеи хайру мутаҷовиз ва бадгумонро');
INSERT INTO "pioneers" VALUES(50,26,'50:26','Ҳамон касе, ки дар канори Аллоҳ таоло маъбуди дигаре қарор медод. Ӯро дар азобе сахт бияфканед»');
INSERT INTO "pioneers" VALUES(50,27,'50:27','[Шайтони] Ҳамнишинаш мегӯяд: «Парвардигоро, ман ӯро ба туғён накашидам, вале ӯ худ дар гумроҳии бепоёне буд»');
INSERT INTO "pioneers" VALUES(50,28,'50:28','[Аллоҳ таоло] Мефармояд: «Дар пешгоҳи Ман ситез накунед, ки пеш аз ин ба шумо ҳушдор дода будем');
INSERT INTO "pioneers" VALUES(50,29,'50:29','Фармони муҷозот назди Ман тағйирпазир нест; ва Аллоҳ таоло [дар кайфару подош] ба бандагонаш ситам намекунад»');
INSERT INTO "pioneers" VALUES(50,30,'50:30','Рӯзе, ки ба дузах мегӯем: «Оё [аз ситамгарон] анбошташудаӣ?». [Ва ӯ] Мегӯяд: «Оё беш аз ин ҳам ҳаст?»');
INSERT INTO "pioneers" VALUES(50,31,'50:31','[Дар он рӯз] Биҳиштро барои парҳезкорон пеш меоваранд [то неъматҳояшро] аз наздик [бубинанд]');
INSERT INTO "pioneers" VALUES(50,32,'50:32','[Ба онон гуфта мешавад:] «Ин ҳамон подошест, ки ба шумо ваъда дода мешуд, ки барои ҳар тавбакоре аст, ки [ба сӯйи Парвардигораш бозмегардад ва] пойбанд [ба паймону аҳкоми илоҳӣ] аст');
INSERT INTO "pioneers" VALUES(50,33,'50:33','Ҳамон касе, ки дар ниҳон аз [хашми Аллоҳи] раҳмон метарсад ва бо қалбе тавбакор [ба пешгоҳаш] бозмегардад');
INSERT INTO "pioneers" VALUES(50,34,'50:34','Бо саломат ба биҳишт дароед; ин аст рӯзи ҷовидонагӣ»');
INSERT INTO "pioneers" VALUES(50,35,'50:35','Дар он ҷо ҳар чи бихоҳанд, дар ихтиёр доранд ва ҳатто бештар аз он низ дар ихтиёри мост');
INSERT INTO "pioneers" VALUES(50,36,'50:36','Ва чи бисёр умматҳоеро, ки пеш аз мушрикони Макка ҳалок кардем, ки онон [ба маротиб] аз инҳо қавитар буданд; пас, дар гӯша ва канори шаҳрҳо ҷустуҷӯ карданд [ки бубинанд оё аз азобу ҳалокати барояшон] ҳеҷ паноҳ ва гурезгоҳе вуҷуд дорад');
INSERT INTO "pioneers" VALUES(50,37,'50:37','Бе гумон дар ин [сухан] барои соҳибдилон ё касоне, ки бо ҳузур [-и қалб ба нидои тавҳид] гӯш фаро медиҳанд ва ҳушёранд, андарз аст');
INSERT INTO "pioneers" VALUES(50,38,'50:38','Мо осмонҳо ва замин ва он чиро, ки миёни онҳост, дар шаш рӯз офаридем ва [ҳаргиз] ранҷу хастагӣ ба Мо нарасид');
INSERT INTO "pioneers" VALUES(50,39,'50:39','[Эй Паёмбар] Дар баробари суханони мушрикон шикебо бош; ва пеш аз баромадани офтоб ва қабл аз фуру шуданаш Парвардигоратро ба покӣ ситоиш кун [ва намоз бигузор]');
INSERT INTO "pioneers" VALUES(50,40,'50:40','Ва низ [дар посе] аз шаб ва пас аз ҳар намоз ӯро ба покӣ ситоиш кун');
INSERT INTO "pioneers" VALUES(50,41,'50:41','Ва [эй Паёмбар] он гоҳ ки мунодӣ [-и қиёмат] аз маконе наздик нидо медиҳад, гӯш фародор');
INSERT INTO "pioneers" VALUES(50,42,'50:42','Рӯзе, ки ҳамагон бонги саҳмноки растохезро ба ҳақ мешунаванд, [он рӯз] рӯзи хуруҷ [аз гурҳо] аст');
INSERT INTO "pioneers" VALUES(50,43,'50:43','Моем, ки ҳаёт мебахшем ва марг медиҳем; ва бозгашт [-и ҳамагон] ба сӯйи Мост');
INSERT INTO "pioneers" VALUES(50,44,'50:44','Рӯзе, ки замин аз [фарози] онон шикофта мешавад, инсонҳо шитобон [берун меоянд]. Ин эҳзоре аст, ки барои Мо осон аст');
INSERT INTO "pioneers" VALUES(50,45,'50:45','Мо ба он чи [кофирон] мегӯянд, донотарем; ва ту [эй Паёмбар] бар онон мусаллат нестӣ [ки бихоҳӣ ба имон овардан водорашон кунӣ]; пас, касеро, ки аз азоб [ва ҳушдори] Ман метарсад, ба [василаи] Қуръон андарз бидеҳ');
INSERT INTO "pioneers" VALUES(51,1,'51:1','Савганд ба бодҳои хокафшон');
INSERT INTO "pioneers" VALUES(51,2,'51:2','Ва савганд ба абрҳои сангинбор');
INSERT INTO "pioneers" VALUES(51,3,'51:3','Ва савганд ба киштиҳои сабуксайр');
INSERT INTO "pioneers" VALUES(51,4,'51:4','Ва савганд ба фариштагон, ки умури илоҳиро [миёни мардум] тақсим мекунанд');
INSERT INTO "pioneers" VALUES(51,5,'51:5','Ки он чӣ ба шумо [дар мавриди подош ва азоб] ваъда дода шуда, ҳақиқат дорад');
INSERT INTO "pioneers" VALUES(51,6,'51:6','Ва [рӯзи] ҷазо, ҳатман, ба вуқуъ мепайвандад');
INSERT INTO "pioneers" VALUES(51,7,'51:7','Савганд ба осмон, ки [бо ситорагон ороста шуда ва гӯӣ] дорои роҳҳост');
INSERT INTO "pioneers" VALUES(51,8,'51:8','Ки шумо [дар мавриди Паёмбару Қуръон] ихтилофи назар доред');
INSERT INTO "pioneers" VALUES(51,9,'51:9','Ҳар ки [аз роҳи ҳақ] бозгардонда шавад, аз [имон ба] Паёмбар ва Қуръон низ бозгардонда хоҳад шуд');
INSERT INTO "pioneers" VALUES(51,10,'51:10','Марг [ва лаънат] бар дурӯғгӯён!');
INSERT INTO "pioneers" VALUES(51,11,'51:11','Ҳамон касоне, ки дар вартаи ҷаҳл ғофиланд');
INSERT INTO "pioneers" VALUES(51,12,'51:12','Онон [пайваста ва аз рӯйи инкор] мепурсанд: «Рӯзи ҷазо чи замоне аст?»');
INSERT INTO "pioneers" VALUES(51,13,'51:13','Ҳамон рӯзе аст, ки ба оташ муҷозот мешаванд');
INSERT INTO "pioneers" VALUES(51,14,'51:14','[Ва ба онон гуфта мешавад:] «Азоби худро бичашед; ин ҳамон оташест, ки ба шитоб мехостед»');
INSERT INTO "pioneers" VALUES(51,15,'51:15','[Дар рӯзи қиёмат] Мусалламан, парҳезкорон дар боғҳое [аз биҳишт] ва [канори] чашмасорҳо ба сар мебаранд');
INSERT INTO "pioneers" VALUES(51,16,'51:16','Он чи Парвардигорашон ба онон ато кардааст, дарёфт медоранд; зеро дар [зиндагии] гузашта накукор будаанд');
INSERT INTO "pioneers" VALUES(51,17,'51:17','Онон андаке аз шабро мехуфтанд [ва бақияро ба намоз ва ниёиш машғул буданд]');
INSERT INTO "pioneers" VALUES(51,18,'51:18','Ва саҳаргоҳон омурзиш мехостанд');
INSERT INTO "pioneers" VALUES(51,19,'51:19','Ва дар амволашон барои соил ва маҳрум ҳаққе [муайян аз садақа ва закот] буд');
INSERT INTO "pioneers" VALUES(51,20,'51:20','Дар замин нишонаҳо [-и қудрати илоҳӣ] барои аҳли яқин падидор аст');
INSERT INTO "pioneers" VALUES(51,21,'51:21','ва дар вуҷуди худатон; оё намебинед?');
INSERT INTO "pioneers" VALUES(51,22,'51:22','Ва ризқу рӯзиатон ва он чӣ [аз кайфару подош ва хайру шар, ки] ба шумо ваъда дода мешавад, дар осмон аст');
INSERT INTO "pioneers" VALUES(51,23,'51:23','Пас савганд ба Парвардигори осмону замин, ки ваъда [-и қиёмат ва подош ва азоб] ҳамчун сухан гуфтанатон ҳақиқӣ ва қатъӣ аст');
INSERT INTO "pioneers" VALUES(51,24,'51:24','[Эй Паёмбар] Оё достони меҳмонони гиромии Иброҳим ба ту расидааст?');
INSERT INTO "pioneers" VALUES(51,25,'51:25','Чун бар ӯ ворид шуданд ва салом гуфтанд, ӯ гуфт: «Салом» [ва зери лаб гуфт:] «Гурӯҳе ношиносанд»');
INSERT INTO "pioneers" VALUES(51,26,'51:26','Он гоҳ назди хонаводаи худ рафт ва [гӯшти бирёни] гӯсолаи фарбеҳе [барои пазироӣ] овард');
INSERT INTO "pioneers" VALUES(51,27,'51:27','[Ӯ ғизоро] Назди онон гузошт ва [чанд лаҳза баъд] гуфт: «Оё намехӯред?»');
INSERT INTO "pioneers" VALUES(51,28,'51:28','[Пас, чун даст ба ғизо назаданд] Аз онон эҳсоси тарс кард; гуфтанд: «Натарс [мо фариштаем]; ва ӯро ба [таваллуди] писари доно башорат доданд');
INSERT INTO "pioneers" VALUES(51,29,'51:29','Ҳамсараш бо фарёд [-у шигифтӣ] пеш омад ва дар ҳоле ки ба рӯйи худ мезад, гуфт: «Пиразани нозо [фарзанд мезояд]?');
INSERT INTO "pioneers" VALUES(51,30,'51:30','[Фариштагон] гуфтанд: «Парвардигорат чунин фармудааст; ва бе гумон, Ӯ ҳакиму доност»');
INSERT INTO "pioneers" VALUES(51,31,'51:31','[Иброҳим] Гуфт: «Эй фариштагон [-и Аллоҳ таоло], кору маъмурияти шумо чист?»');
INSERT INTO "pioneers" VALUES(51,32,'51:32','[Фариштагон] Гуфтанд: «Мо барои [муҷозоти] қавме муҷрим [ва гунаҳкор] фиристода шудаем');
INSERT INTO "pioneers" VALUES(51,33,'51:33','То сангҳое аз гил [-и сахтшуда] бар сарашон фуру борем');
INSERT INTO "pioneers" VALUES(51,34,'51:34','[Сангҳое] Ки назди Парвардигорат барои исрофкорон нишон гузошта шудааст');
INSERT INTO "pioneers" VALUES(51,35,'51:35','Пас, муъминонеро, ки дар он [шаҳрҳои қавми Лут] буданд, [пеш аз нузули азоб] берун овардем');
INSERT INTO "pioneers" VALUES(51,36,'51:36','Вале фақат як хонаводаро ёфтем, ки [нисбат ба авомири илоҳӣ] таслим буданд');
INSERT INTO "pioneers" VALUES(51,37,'51:37','Ва дар он [шаҳрҳо] барои мардуме, ки аз азоби дарднок [-и илоҳӣ] тарс доранд, нишонае равшан бар ҷой гузоштем');
INSERT INTO "pioneers" VALUES(51,38,'51:38','Дар [достони] Мӯсо [низ нишона ва ибратест]; он гоҳ ки ӯро бо далеле равшан назди Фиръавн фиристодем');
INSERT INTO "pioneers" VALUES(51,39,'51:39','Вале [Фиръавн] бо такя бар қудраташ [ҳақро] напазируфт ва гуфт: [«Ин мард ё] Ҷодугар аст ё девона»');
INSERT INTO "pioneers" VALUES(51,40,'51:40','Ӯро ҳамроҳ бо сипоҳиёнаш гирифтем ва ба дарё афкандем [ва ҳалок кардем]; дар ҳоле ки [дар лаҳзаи марг худро ба хотири нофармонӣ ва куфраш] сарзаниш мекард');
INSERT INTO "pioneers" VALUES(51,41,'51:41','Дар [достони қавми] Од [низ дарси ибратест]; он гоҳ ки тундбоде бе хайру баракат бар онон фиристодем');
INSERT INTO "pioneers" VALUES(51,42,'51:42','[Тундбоде] Ки бар ҳар чиз вазид, хоку хошокаш кард');
INSERT INTO "pioneers" VALUES(51,43,'51:43','Дар [достони қавми] Самуд [низ дарси ибрате аст] он гоҳ ки [тавассути паёмбарашон Солеҳ] ба онон гуфта шуд: «Андаке [аз лаззатҳои зиндагӣ] баҳраманд шавед [ки танҳо се рӯз фурсат доред]»');
INSERT INTO "pioneers" VALUES(51,44,'51:44','Онон аз фармони Парвардигорашон сарпечӣ карданд; пас, дар ҳоле ки [ба чашми худ азобро] медиданд, соиқа ононро фаро гирифт');
INSERT INTO "pioneers" VALUES(51,45,'51:45','[Чунон бар замин афтоданд, ки] На тавони бархостан [ва гурез] доштанд ва на ёрои дифоъ');
INSERT INTO "pioneers" VALUES(51,46,'51:46','Пеш аз он қавми Нуҳро, ки гурӯҳе нофармон буданд [ҳалок кардем]');
INSERT INTO "pioneers" VALUES(51,47,'51:47','Мо осмонро ба тавон [-и худ] бино ниҳодем ва ҳамвора онро вусъат мебахшем');
INSERT INTO "pioneers" VALUES(51,48,'51:48','Ва заминро [барои зиндагӣ] густурдем ва чи нек густуронандае ҳастем!');
INSERT INTO "pioneers" VALUES(51,49,'51:49','Ва ҳама чизро ба сурати завҷ [ҷуфт] офаридем; бошад, ки [таваҷҷуҳ кунед ва] панд гиред');
INSERT INTO "pioneers" VALUES(51,50,'51:50','[Эй Паёмбар, ба мардум бигӯ:] «Пас, ба сӯйи Аллоҳ таоло биштобед, ки ман аз ҷониби Ӯ барои шумо ҳушдордиҳандае ошкорам');
INSERT INTO "pioneers" VALUES(51,51,'51:51','Ва дар канори Аллоҳ таоло маъбуди дигаре қарор надиҳед, яқинан, ман аз [ҷониби] Ӯ барои шумо ҳушдордиҳандае ошкорам»');
INSERT INTO "pioneers" VALUES(51,52,'51:52','Ҳар паёмбаре, ки бар пешиниёнашон мабъус шуд, ҳамин гуна гуфтанд: «[Ӯ ё] Ҷодугар аст ё девона»');
INSERT INTO "pioneers" VALUES(51,53,'51:53','Оё якдигарро ба ин [куфру такзиб] супориш карда буданд? На, балки мардуме саркаш буданд');
INSERT INTO "pioneers" VALUES(51,54,'51:54','Пас, [эй паёмбар] аз онон рӯй бигардон, ки ту [ба хотири куфри онон] ҳаргиз сазовори сарзаниш нестӣ');
INSERT INTO "pioneers" VALUES(51,55,'51:55','Ва [пайваста ба онон] тазаккур бидеҳ, ки, ҳатман, тазаккур барои муъминон судбахш аст');
INSERT INTO "pioneers" VALUES(51,56,'51:56','Ва ҷинну инсро фақат барои ин офаридам, ки Маро ибодат кунанд [ва аз бандагии дигарон сар боз зананд]');
INSERT INTO "pioneers" VALUES(51,57,'51:57','На аз онон рӯзӣ мехоҳам, на ин ки Маро хӯрок диҳанд');
INSERT INTO "pioneers" VALUES(51,58,'51:58','[Зеро] Аллоҳ таоло аст, ки рӯзирасони неруманд ва барқарор аст');
INSERT INTO "pioneers" VALUES(51,59,'51:59','Ситамгорон [-и мушрик низ] ҳамчун ёрон [-и гузашта]-и хеш саҳме аз азоб доранд; пас, ба шитоб [онро] аз Ман нахоҳанд');
INSERT INTO "pioneers" VALUES(51,60,'51:60','Пас, вой бар кофирон аз [азоби] рӯзи мавъудашон!');
INSERT INTO "pioneers" VALUES(52,1,'52:1','Савганд ба кӯҳи Тур');
INSERT INTO "pioneers" VALUES(52,2,'52:2','ва савганд ба китоби навишташуда');
INSERT INTO "pioneers" VALUES(52,3,'52:3','Дар саҳифаҳои кушуда');
INSERT INTO "pioneers" VALUES(52,4,'52:4','Ва савганд ба «Байту-л-маъмур» [ки фариштагон дар он ба ибодати Парвардигор машғуланд]');
INSERT INTO "pioneers" VALUES(52,5,'52:5','Ва савганд ба [осмони баланд ҳамон] сақфи барафрошта [бар замин]');
INSERT INTO "pioneers" VALUES(52,6,'52:6','Ва савганд ба дарёи оганда [аз об]');
INSERT INTO "pioneers" VALUES(52,7,'52:7','Ки бе тардид, азоби Парвардигорат ба вуқуъ хоҳад пайваст');
INSERT INTO "pioneers" VALUES(52,8,'52:8','Ва ҳеҷ боздорандае онро бознадорад');
INSERT INTO "pioneers" VALUES(52,9,'52:9','Рӯзе, ки осмон ба сахтӣ дар ҷунбиш ва изтироб афтад');
INSERT INTO "pioneers" VALUES(52,10,'52:10','Ва кӯҳҳо ба ҳаракате шадид равон шаванд');
INSERT INTO "pioneers" VALUES(52,11,'52:11','Дар он рӯз вой ба ҳоли такзибкунандагон');
INSERT INTO "pioneers" VALUES(52,12,'52:12','Ҳамон касоне, ки дар беҳудагӯӣ саргарми бозианд');
INSERT INTO "pioneers" VALUES(52,13,'52:13','Рӯзе, ки бо хушунат ба сӯйи оташи дузах ронда мешаванд');
INSERT INTO "pioneers" VALUES(52,14,'52:14','[Ба онон гуфта мешавад] «Ин [ҳамон] оташест, ки онро дурӯғ мепиндоштед');
INSERT INTO "pioneers" VALUES(52,15,'52:15','Оё ин [оташу азоби] ҷодуст ё [ҳанӯз ҳам онро] намебинед?');
INSERT INTO "pioneers" VALUES(52,16,'52:16','Ба оташ дароед [ва бисӯзед], ки шикебоӣ ва ношикебоии шумо [дар баробари шуълаҳои сӯзонаш] бароятон яксон аст; танҳо дар баробари он чи кардаед, муҷозот мешавед»');
INSERT INTO "pioneers" VALUES(52,17,'52:17','Ба ростӣ, ки парҳезкорон дар боғҳои биҳишт дар нозу неъматанд');
INSERT INTO "pioneers" VALUES(52,18,'52:18','Ва аз бахшишҳои [бедареғи] Парвардигорашон ва аз ин ки аз азоби дузах нигоҳашон доштааст, шодмонанд');
INSERT INTO "pioneers" VALUES(52,19,'52:19','[Ба онон гуфта мешавад] «Ба подоши кирдоратон [дар дунё инак] гуворо бихӯред ва биошомед»');
INSERT INTO "pioneers" VALUES(52,20,'52:20','Дар ҳоле ки бар тахтҳое радифшуда такя задаанд ва ҳурони ғизолчашмро ба ҳамсарии онон дармеоварем');
INSERT INTO "pioneers" VALUES(52,21,'52:21','Муъминоне, ки фарзандонашон дар имон пайрави онон буданд, фарзандонашонро [низ дар биҳишт] ба онон мулҳақ хоҳем кард ва аз [подоши] амалашон заррае намекоҳем; чаро ки [сарнавишти] ҳар кас дар гарави дастоварди худи ӯст');
INSERT INTO "pioneers" VALUES(52,22,'52:22','[Дар биҳишт аз] Ҳар навъ мева ва гӯште, ки бихоҳанд, пайдарпай дар дастрасашон қарор медиҳем.');
INSERT INTO "pioneers" VALUES(52,23,'52:23','Дар он ҷо ҷомҳои шаробе аз дасти якдигар мегиранд, ки на муҷиби беҳудагӯӣ аст ва на гуноҳ;');
INSERT INTO "pioneers" VALUES(52,24,'52:24','Ва навҷавононе [ки барои хидмат] перомунашон дар рафту омаданд, гӯӣ [аз зебоӣ ва сапедрӯӣ] ҳамчун марвориде дар садаф ҳастанд');
INSERT INTO "pioneers" VALUES(52,25,'52:25','[Биҳиштиён] Ба якдигар рӯ меоваранд ва аз [дунё ва аҳволи гузаштаи] худ суол мекунанд');
INSERT INTO "pioneers" VALUES(52,26,'52:26','Мегӯянд: «Мо дар дунё дар хонаводаи худ [аз азоби илоҳӣ] метарсидем');
INSERT INTO "pioneers" VALUES(52,27,'52:27','Аллоҳ таоло [бо неъмти ислом] бар мо миннат ниҳод ва аз азоби сӯзон [-и дузах] ҳифзамон кард');
INSERT INTO "pioneers" VALUES(52,28,'52:28','Мо дар зиндагии дунё ҳамвора Ӯро [парастиш мекардем ва ба рубубият] мехондем. Бе тардид, Ӯ накукору меҳрубон аст');
INSERT INTO "pioneers" VALUES(52,29,'52:29','Пас, [эй Паёмбар, бо оёти Қуръон ба мардум] андарз бидеҳ, ки ба лутфи Парвардигорат [ту бар хилофи иддаои кофирон] на коҳинӣ ва на маҷнун');
INSERT INTO "pioneers" VALUES(52,30,'52:30','Оё мегӯянд: «[Муҳаммад] Шоирест, ки мунтазирем, то маргаш фаро расад [ва аз суханонаш раҳоӣ ёбем]?»');
INSERT INTO "pioneers" VALUES(52,31,'52:31','Бигӯ: «Мунтазир [-и марги ман] бошед; ман низ бо шумо мунтазир хоҳам монд [то азобатонро ба чашм бинам]»');
INSERT INTO "pioneers" VALUES(52,32,'52:32','Оё ақлҳояшон ба [баёни] ин [суханони ботил] ҳукм мекунад, ё [асосан] мардуме саркаш ҳастанд?');
INSERT INTO "pioneers" VALUES(52,33,'52:33','Оё мегӯянд: «[Муҳаммад] Қуръонро ҷаъл кардааст?». [Ҳаргиз чунин нест] Балки [ғарқ дар такаббуранд, ки] имон намеоваранд');
INSERT INTO "pioneers" VALUES(52,34,'52:34','Агар рост мегӯянд, сухане монанди он биёваранд');
INSERT INTO "pioneers" VALUES(52,35,'52:35','Оё онон аз ҳеҷ офарида шудаанд ё худ офаринандаи хешанд?');
INSERT INTO "pioneers" VALUES(52,36,'52:36','Оё онон осмонҳо ва заминро офаридаанд? [На] Балки онон яқин надоранд [ки Аллоҳ таоло офаринандаи мутлақ аст]');
INSERT INTO "pioneers" VALUES(52,37,'52:37','Оё ганҷинаҳои [неъмат ва ҳидояти] Парвардигорат назди эшон аст? Оё [бар ҳама чиз] тасаллут доранд?');
INSERT INTO "pioneers" VALUES(52,38,'52:38','Оё нардбоне доранд, ки ба василаи он [гуфтори фариштагони ваҳйро] мешунаванд? Пас, ҳар ки шунидааст, бояд далеле равшан биёварад');
INSERT INTO "pioneers" VALUES(52,39,'52:39','Оё Аллоҳ таоло фарзандони духтар дорад ва шумо писар?');
INSERT INTO "pioneers" VALUES(52,40,'52:40','[Эй паёмбар] Оё аз онон подоше дархост мекунӣ, ки [аз пардохташ дар ранҷанд ва харҷаш] барояшон [гарону] сангин аст?');
INSERT INTO "pioneers" VALUES(52,41,'52:41','Оё аз илми ғайб огоҳанд ва [хабарҳои ниҳонро] менависанд?');
INSERT INTO "pioneers" VALUES(52,42,'52:42','Оё қасди найранг[ва нақшаи палиде бароят] доранд? Вале кофирон худ гирифтори найранг [ва тадбири илоҳӣ] ҳастанд');
INSERT INTO "pioneers" VALUES(52,43,'52:43','Оё онон маъбуде ғайр аз Аллоҳ таоло доранд? Аллоҳ таоло аз он чи бо Ӯ шарик қарор медиҳанд, поку муназзаҳ аст');
INSERT INTO "pioneers" VALUES(52,44,'52:44','[Ин кофирон, ҳатто] Агар порае аз осмонро низ дар ҳоли суқут бубинанд, [боз ҳам имон намеоваранд ва] мегӯянд: «Абре мутароким аст»');
INSERT INTO "pioneers" VALUES(52,45,'52:45','Пас, ононро [ба ҳоли худ] раҳо кун, то бо рӯзи мавъудашон, ки [бо нахустин дамидани сур] мадҳуши марг мегарданд, мувоҷеҳ шаванд');
INSERT INTO "pioneers" VALUES(52,46,'52:46','Рӯзе, ки найрангашон судманд нахоҳад буд ва [аз ҳеҷ сӯ] ёрӣ намешаванд');
INSERT INTO "pioneers" VALUES(52,47,'52:47','Ситамгорон [дар дунё низ ба дасти муъминон] ба азоби дигаре гирифтор хоҳанд шуд; вале бештарашон намедонанд');
INSERT INTO "pioneers" VALUES(52,48,'52:48','[Эй Паёмбар] Дар баробари ҳукми Парвардигорат шикебо бош; зеро ту таҳти назар ва ҳифозати Моӣ; ва ҳангоме ки [аз хоб ё ба қасди ибодат] бармехезӣ, Парвардигоратро ба покӣ ситоиш кун');
INSERT INTO "pioneers" VALUES(52,49,'52:49','Ва низ [дар посе] аз шаб Ӯро ситоиш кун [ва намоз бигузор] ва ба ҳангоми нопадид шудани ситорагон [дар сапедадам низ намози субҳ бигузор]');
INSERT INTO "pioneers" VALUES(53,1,'53:1','Савганд ба ситора, он гоҳ ки нопадид шавад');
INSERT INTO "pioneers" VALUES(53,2,'53:2','Ки ёри шумо [Муҳаммад] ҳаргиз гумроҳ нашуда ва ба роҳи табоҳе наафтодааст');
INSERT INTO "pioneers" VALUES(53,3,'53:3','Ва аз рӯйи ҳавои нафс сухан намегӯяд');
INSERT INTO "pioneers" VALUES(53,4,'53:4','[Ин Қуръон] Ваҳй [-и илоҳӣ] аст, ки [ба ӯ] илқо мешавад. Ин [Қуръон] ҷуз панд барои ҷаҳониён [инсон ва ҷин] нест');
INSERT INTO "pioneers" VALUES(53,5,'53:5','[Ҷабраил, ки фариштае аст] Пуртавон таълимаш додааст');
INSERT INTO "pioneers" VALUES(53,6,'53:6','[Ҳамон фариштаи] Хушманзаре, ки [бо чеҳраи ҳақиқиаш дар баробари Паёмбар] истод');
INSERT INTO "pioneers" VALUES(53,7,'53:7','Дар ҳоле ки ӯ дар уфуқи боло [дар осмон] қарор дошт');
INSERT INTO "pioneers" VALUES(53,8,'53:8','Сипас [Ҷабраил] наздик шуд; ва наздиктар');
INSERT INTO "pioneers" VALUES(53,9,'53:9','Пас, [фосилаи ӯ бо Паёмбар] ба андозаи тӯли ду камон камтар буд');
INSERT INTO "pioneers" VALUES(53,10,'53:10','Он гоҳ [он фариштаи бузургвор] он чиро, ки бояд ба бандаи Аллоҳ таоло ваҳй мекард, ваҳй намуд');
INSERT INTO "pioneers" VALUES(53,11,'53:11','Он чиро, ки [паёмбар] дид, қалбаш дурӯғ напиндошт');
INSERT INTO "pioneers" VALUES(53,12,'53:12','Оё бо ӯ дар бораи он чи мебинад, муҷодала мекунед?');
INSERT INTO "pioneers" VALUES(53,13,'53:13','Дар ҳақиқат, паёмбар як бори дигар низ он фариштаро [ба сурати комил] мушоҳида карда буд');
INSERT INTO "pioneers" VALUES(53,14,'53:14','Дар канори [дарахти бузург] «Сидрату-л-мунтаҳо» [дар осмони ҳафтум]');
INSERT INTO "pioneers" VALUES(53,15,'53:15','ки оромишгоҳи биҳишт ҳамон ҷост');
INSERT INTO "pioneers" VALUES(53,16,'53:16','Он гоҳ ки ҳолате он дарахтро пӯшонида буд');
INSERT INTO "pioneers" VALUES(53,17,'53:17','Чашми [паёмбар ба чапу рост] мунҳариф нашуд ва [аз ҳад] таҷовуз накард');
INSERT INTO "pioneers" VALUES(53,18,'53:18','Ба ростӣ, ки ӯ бархе аз бузургтарин нишонаҳои Парвардигорашро [дар он ҷо] мушоҳида кард');
INSERT INTO "pioneers" VALUES(53,19,'53:19','[Эй мушрикон] Оё «Лот»-у «Уззо»-ро дидаед?');
INSERT INTO "pioneers" VALUES(53,20,'53:20','Ва «Маннот» он савумин [бути беарзиш]-ро? [Оё ҳеҷ суду зиёне ба шумо мерасонанд?]');
INSERT INTO "pioneers" VALUES(53,21,'53:21','Оё [иддао мекунед, ки] фарзанди писар барои шумост ва духтар барои Аллоҳ таоло?');
INSERT INTO "pioneers" VALUES(53,22,'53:22','Дар ин сурат, тақсиме ноодилона аст');
INSERT INTO "pioneers" VALUES(53,23,'53:23','Ин бутҳо фақат исмҳое [бемаъно] ҳастанд, ки худ ва падаронатон бо онҳо [маъбудҳои дурӯғини худро] ном ниҳодаед ва Аллоҳ таоло ҳеҷ далеле [бар ҳаққонияти ин кор] нозил накардааст. [Мушрикон дар эътиқодоташон] Фақат пайрави гумону хостаҳои дили хешанд; ҳол он ки [муҷиботи] ҳидоят аз ҷониби Парвардигорашон бар онон расидааст');
INSERT INTO "pioneers" VALUES(53,24,'53:24','Оё инсон ҳар чи [аз шафоати ин маъбудони ботил] орзу кунад, барояш муяссар аст?');
INSERT INTO "pioneers" VALUES(53,25,'53:25','Дунё ва охират аз они Аллоҳ аст [ва ба ҳар кас, ҳар чиро салоҳ бидонад мебахшад]');
INSERT INTO "pioneers" VALUES(53,26,'53:26','Чи бисёр фариштагоне, ки дар осмонҳо ҳастанд, ки шафоаташон ба ҳеҷ ваҷҳ судманд нахоҳад буд; магар пас аз он ки Аллоҳ таоло барои ҳар ки бихоҳад ва хушнуд бошад, иҷозат диҳад');
INSERT INTO "pioneers" VALUES(53,27,'53:27','Касоне, ки ба охират бовар надоранд, бар фариштагони илоҳӣ номи духтар мегузоранд [ва ононро духтарони Аллоҳ таоло медонанд]');
INSERT INTO "pioneers" VALUES(53,28,'53:28','Онон дар мавриди ин амр [комилан] беиттилоанд [ва] фақат аз ҳадсу гумон пайравӣ мекунанд; дар ҳоле ки барои [шинохти] ҳақиқат гумон кофӣ нест');
INSERT INTO "pioneers" VALUES(53,29,'53:29','Пас, [эй Паёмбар] ту низ аз касе, ки аз ёди Мо рӯй гардонида ва фақат [лаззатҳои] зиндагии дунёро мехоҳад, рӯй бартоб');
INSERT INTO "pioneers" VALUES(53,30,'53:30','Ниҳояти дарку фаҳмашон ҳамин аст. Бе тардид, Парвардигорат ба ҳоли касоне, ки аз роҳи Ӯ мунҳариф шудаанд огоҳтар аст; ва низ ба ҳоли роҳёфтагон');
INSERT INTO "pioneers" VALUES(53,31,'53:31','Ҳар чи дар осмонҳо ва замин аст, аз они Аллоҳ таоло аст, [роҳи хайру шарро ба инсон ироа кард] то бадкоронро дар баробари рафторашон кайфар диҳад ва накукоронро [бо неъматҳои биҳишт] подоши наку ато намояд');
INSERT INTO "pioneers" VALUES(53,32,'53:32','Ҳамон касоне, ки аз гуноҳони кабира ва корҳои зишту бешармона дурӣ мекунанд; магар лағжишҳои камаҳамият [ва гуноҳони сағира, ки бо тавба ва ибодат бахшида мешаванд], ки омурзиши Парвардигорат густурда аст; аз ҳамон дам, ки шуморо аз замин падид овард ва он гоҳ ки ба сурати ҷанин дар шиками модаронатон будед, ба [табиати] шумо огоҳтар [аз худатон] аст; пас, худситоӣ накунед, ки Ӯ ба ҳоли парҳезкорон донотар аст');
INSERT INTO "pioneers" VALUES(53,33,'53:33','Оё он касеро, ки [аз ҳақ] рӯй бартофт, дидаӣ?');
INSERT INTO "pioneers" VALUES(53,34,'53:34','Ва андаке инфоқ кард ва [сипас] худдорӣ намуд');
INSERT INTO "pioneers" VALUES(53,35,'53:35','Оё илми ғайб дорад ва [ояндаро] мебинад?');
INSERT INTO "pioneers" VALUES(53,36,'53:36','Оё бохабар нашудааст аз матолиби китоби осмонии Мӯсо');
INSERT INTO "pioneers" VALUES(53,37,'53:37','ва [таълимоти] Иброҳим, ки ҳаққи [рисолат ва бандагии Мо]-ро ба тамомӣ адо кард?');
INSERT INTO "pioneers" VALUES(53,38,'53:38','Ки ҳеҷ гунаҳгоре бори гуноҳи дигареро бар дӯш нахоҳад кашид');
INSERT INTO "pioneers" VALUES(53,39,'53:39','Ва ин ки инсон фақат самараи талош [-у нияти] худро хоҳад дошт');
INSERT INTO "pioneers" VALUES(53,40,'53:40','Ва ин ки бе гумон талоши ӯ [дар муҳосибаи аъмолаш дар рӯзи қиёмат] манзур хоҳад шуд');
INSERT INTO "pioneers" VALUES(53,41,'53:41','Он гоҳ подошашро ба тамомӣ хоҳанд дод');
INSERT INTO "pioneers" VALUES(53,42,'53:42','Ва ин ки бозгашт [-и ҳамаи умур] ба сӯйи Парвардигори туст');
INSERT INTO "pioneers" VALUES(53,43,'53:43','Ва Ӯст, ки механдонад ва мегирёнад');
INSERT INTO "pioneers" VALUES(53,44,'53:44','Ва Ӯст, ки мемиронад ва [дар охират] зинда мекунад');
INSERT INTO "pioneers" VALUES(53,45,'53:45','Ва ҳам Ӯст, ки завҷи нару модаро меофарад,');
INSERT INTO "pioneers" VALUES(53,46,'53:46','аз нутфае, ки [дар раҳим] рехта мешавад');
INSERT INTO "pioneers" VALUES(53,47,'53:47','ва падид овардани дубораи онҳо [дар қиёмат низ бар уҳдаи] Аллоҳ таоло аст');
INSERT INTO "pioneers" VALUES(53,48,'53:48','Ва Ӯст, ки [шуморо] бениёз мекунад ва сармоя мебахшад');
INSERT INTO "pioneers" VALUES(53,49,'53:49','Ва Ӯст, ки Парвардигори [ситораи] «Шиъро» аст');
INSERT INTO "pioneers" VALUES(53,50,'53:50','Ва Ӯст, ки қавми Одро дар гузашта ҳалок кард');
INSERT INTO "pioneers" VALUES(53,51,'53:51','Ва қавми Самудро [низ нобуд кард] ва [ҳеҷ як аз ононро] боқӣ нагузошт');
INSERT INTO "pioneers" VALUES(53,52,'53:52','Ва қавми Нуҳро, ки ситамгортар ва саркаштар буданд, пеш аз он [ҳалок намуд]');
INSERT INTO "pioneers" VALUES(53,53,'53:53','Ва шаҳрҳои зерурӯшуда [-и қавми Лут]-ро дар ҳам кӯбид');
INSERT INTO "pioneers" VALUES(53,54,'53:54','Ва ононро [бо азобе фарогир] – чунон ки бояд пӯшонид');
INSERT INTO "pioneers" VALUES(53,55,'53:55','Ҳол [эй инсон] дар мавриди кадом як аз неъматҳои Парвардигорат тардид раво медорӣ [ва муҷодала мекунӣ]?');
INSERT INTO "pioneers" VALUES(53,56,'53:56','Ин [паёмбар низ] ҳушдордиҳандаест аз [гурӯҳи] ҳушдордиҳандагони пешин');
INSERT INTO "pioneers" VALUES(53,57,'53:57','Қиёмат наздик мешавад');
INSERT INTO "pioneers" VALUES(53,58,'53:58','Ва ҳеҷ кас – ҷуз Аллоҳ таоло – наметавонад ошкораш кунад [ва сахтиҳояшро бартараф созад]');
INSERT INTO "pioneers" VALUES(53,59,'53:59','[Эй мушрикон] Оё аз ин сухан дар шигифтед?');
INSERT INTO "pioneers" VALUES(53,60,'53:60','Ва дар ҳоли ғафлат ва ғурур [ба оёти Қуръон] механдед ва [аз шунидани ҳушдорҳояш наметарсед ва] ашк намерезед?');
INSERT INTO "pioneers" VALUES(53,61,'53:61','Ва шумо бозикунандагонед');
INSERT INTO "pioneers" VALUES(53,62,'53:62','Пас, [ҳамагӣ] барои Аллоҳ таоло саҷда кунед ва [танҳо] Ӯро бипарастед');
INSERT INTO "pioneers" VALUES(54,1,'54:1','Қиёмат наздик шуд ва моҳ [аз ҳам] шикофт');
INSERT INTO "pioneers" VALUES(54,2,'54:2','Агар [кофирон] муъҷизае бубинанд, рӯ мегардонанд ва мегӯянд: «[Ин] Ҷодуе [ботилу] зудгузар аст»');
INSERT INTO "pioneers" VALUES(54,3,'54:3','Онон [оёт ва нишонаҳои илоҳиро] дурӯғ мепиндоранд ва аз ҳавои нафси хеш пайравӣ мекунанд; ва ҳар амре [аз подошу кайфарҳои қиёмат] дар замони махсус ба худ қарор дорад');
INSERT INTO "pioneers" VALUES(54,4,'54:4','Ва мутмаинан [моҷарои азоби ақвоми гузашта ва] ахборе, ки боиси даст кашидан аз гуноҳ мешавад, ба мушрикон расидааст');
INSERT INTO "pioneers" VALUES(54,5,'54:5','Ин [оёти илоҳӣ донишу] ҳикмате расост; вале [барои афроди лаҷуҷ] ҳушдорҳо суде надорад');
INSERT INTO "pioneers" VALUES(54,6,'54:6','Пас, [эй Паёмбар] аз онон рӯ бартоб, [ва мунтазири] рӯзе [бош], ки [фариштаи] даъваткунанда [бо дамидан дар сур] ононро ба амре бас душвор [-у ваҳшатнок] фаро бихонад');
INSERT INTO "pioneers" VALUES(54,7,'54:7','Бо дидагони фуруафтода [аз ваҳшат] аз гурҳо хориҷ мешаванд; [ҳаракаташон ба сӯйи додгоҳи илоҳӣ чунон аст, ки] гӯӣ малахҳое парокандаанд');
INSERT INTO "pioneers" VALUES(54,8,'54:8','[Ҳароснок] Ба сӯйи мунодӣ [-и маҳшар] мешитобанд ва кофирон мегӯянд: «Имрӯз рӯзи душворе аст»');
INSERT INTO "pioneers" VALUES(54,9,'54:9','Пеш аз онон низ қавми Нуҳ [паёмбарашонро] такзиб карданд ва бандаи Моро дурӯғгӯ хонданд ва гуфтанд: «Девона аст»; ва ӯро сахт озурданд');
INSERT INTO "pioneers" VALUES(54,10,'54:10','Пас, ӯ Парвардигорашро хонд [ва гуфт]: «Ман мағлуб шудаам; пас, ёриам фармо [ва аз онон интиқом бигир]»');
INSERT INTO "pioneers" VALUES(54,11,'54:11','Мо низ дарҳои осмонро барои [резиши] борони селосо кушудем');
INSERT INTO "pioneers" VALUES(54,12,'54:12','Ва аз замин низ чашмаҳо ҷорӣ сохтем ва обҳо [аз замину осмон] барои тӯфоне, ки муқаддар шуда буд, ба ҳам пайваст');
INSERT INTO "pioneers" VALUES(54,13,'54:13','Ва Нуҳро бар [киштии] сохта ва пардохта аз тахтаҳо ва мехҳо савор кардем');
INSERT INTO "pioneers" VALUES(54,14,'54:14','Ки таҳти назар [ва ҳифозати] Мо ҳаракат мекард. Ин подоши касе буд, ки [қавмаш] инкораш карда буданд');
INSERT INTO "pioneers" VALUES(54,15,'54:15','Мо он [кайфару наҷот]-ро нишонае [аз қудрати худ] бар ҷой гузоштем; оё пандпазире ҳаст?');
INSERT INTO "pioneers" VALUES(54,16,'54:16','Пас, [бингаред] ки азобу ҳушдорҳои Ман чи гуна буд');
INSERT INTO "pioneers" VALUES(54,17,'54:17','Ба ростӣ, ки Қуръонро барои панд гирифтан осон сохтем; оё пандпазире ҳаст?');
INSERT INTO "pioneers" VALUES(54,18,'54:18','Қавми Од [низ паёмбарашонро] дурӯғгӯ пиндоштанд; пас, [эй аҳли Макка, бингаред, ки] азобу ҳушдорҳои Ман чи гуна буд');
INSERT INTO "pioneers" VALUES(54,19,'54:19','Мо тундбоде сарду сахтро дар як рӯзи шум ва тӯлонӣ бар онон фиристодем');
INSERT INTO "pioneers" VALUES(54,20,'54:20','[Тӯфоне ваҳшатнок] Ки мардумро чунон аз ҷой меканд, ки гӯӣ танаҳои нахли решаканшуда буданд');
INSERT INTO "pioneers" VALUES(54,21,'54:21','Пас, [бингаред] ки азобу ҳушдорҳои Ман чи гуна буд');
INSERT INTO "pioneers" VALUES(54,22,'54:22','Ба ростӣ, ки Қуръонро барои панд гирифтан осон сохтем; оё пандпазире ҳаст?');
INSERT INTO "pioneers" VALUES(54,23,'54:23','Қавми Самуд [низ] ҳушдоркунандагонро дурӯғгӯ пиндоштанд');
INSERT INTO "pioneers" VALUES(54,24,'54:24','Онон гуфтанд: «Оё [сазовор аст] башаре аз миёни худамонро пайравӣ кунем? Дар он сурат дар гумроҳӣ ва сардаргумӣ хоҳем буд');
INSERT INTO "pioneers" VALUES(54,25,'54:25','Оё аз миёни ҳамаи мо танҳо бар ӯ ваҳй нозил шудааст? [Чунин нест] Балки ӯ дурӯғгӯе худписанд аст»');
INSERT INTO "pioneers" VALUES(54,26,'54:26','Фардо хоҳанд донист, ки дурӯғгӯи худписанд кист');
INSERT INTO "pioneers" VALUES(54,27,'54:27','Ба Солеҳ гуфтем] «Барои озмоиши онон модашутуре [-ро, ки дархост кардаанд, аз дили кӯҳ ба сӯяшон] хоҳем фиристод; пас, муроқиби рафторашон бош ва [бар озорашон] шикебоӣ кун');
INSERT INTO "pioneers" VALUES(54,28,'54:28','Ва ба онон эълом кун, ки оби [чоҳ] байни эшон [ва он модашутур] саҳмиябандӣ шудааст ва ҳар ки дар навбати худ бояд ҳозир шавад»');
INSERT INTO "pioneers" VALUES(54,29,'54:29','Вале онон ҳамдасташонро нидо доданд; ӯ низ даст ба кор шуд ва [шутурро] ба қатл расонд');
INSERT INTO "pioneers" VALUES(54,30,'54:30','Пас, [бингаред] ки азобу ҳушдорҳои Ман чи гуна буд');
INSERT INTO "pioneers" VALUES(54,31,'54:31','Мо бар онон бонги маргборе фиристодем; пас, [ҳамагӣ] ҳамчун гиёҳи хушкида [бозмонда аз оғилҳо] хурду рез шуданд');
INSERT INTO "pioneers" VALUES(54,32,'54:32','Ба ростӣ, ки Қуръонро барои панд гирифтан осон сохтем; оё пандпазире ҳаст?');
INSERT INTO "pioneers" VALUES(54,33,'54:33','Қавми Лут [низ] ҳушдордиҳандагонро дурӯғгӯ пиндоштанд');
INSERT INTO "pioneers" VALUES(54,34,'54:34','[Мо низ] Тӯфони регборе бар сарашон фиристодем [ки ҳамагиро ҳалок кард]; магар хонаводаи Лут, ки саҳаргоҳон наҷоташон додем');
INSERT INTO "pioneers" VALUES(54,35,'54:35','[Ин наҷот] Неъмате аз ҷониби Мо буд; ва ба ҳар ки сипосгузорӣ кунад, ин гуна подош медиҳем');
INSERT INTO "pioneers" VALUES(54,36,'54:36','Лут ононро аз кайфари [сахти] Мо бим дод; вале онон дар баробари ҳушдорҳо ба муҷодала [ва ситез] бархостанд');
INSERT INTO "pioneers" VALUES(54,37,'54:37','Онон аз Лут хостанд, ки меҳмононашро [барои комҷӯӣ] дар ихтиёрашон бигзорад; пас, Мо низ чашмонашонро кӯр кардем [ва гуфтем]: «Таъми азобу [натиҷаи] ҳушдорҳоямро бичашед»');
INSERT INTO "pioneers" VALUES(54,38,'54:38','Ва субҳгоҳон азобе пойдор ононро фаро гирифт');
INSERT INTO "pioneers" VALUES(54,39,'54:39','[Ва гуфтем] «Таъми азобу [натиҷаи] ҳушдорҳоямро бичашед»');
INSERT INTO "pioneers" VALUES(54,40,'54:40','Ба ростӣ, ки Қуръонро барои панд гирифтан осон сохтем; оё пандпазире ҳаст?');
INSERT INTO "pioneers" VALUES(54,41,'54:41','Ҳушдордиҳандагон ба суроғи фиръавниён [низ] рафтанд');
INSERT INTO "pioneers" VALUES(54,42,'54:42','[Вале] Онон тамомии оёти Моро дурӯғ шумурданд; мо низ чунон [ки шоистаи Парвардигори] шикастнопазиру муқтадир [аст] азобашон кардем');
INSERT INTO "pioneers" VALUES(54,43,'54:43','[Эй аҳли Макка] Оё кофирони шумо аз онон беҳтаранд ё барои шумо амонномае дар китобҳои [осмонии пешин навишташуда] аст?');
INSERT INTO "pioneers" VALUES(54,44,'54:44','Ё мегӯянд: «Мо ҷамоате муттаҳиду интиқомҷӯ ҳастем»');
INSERT INTO "pioneers" VALUES(54,45,'54:45','Ба зудӣ [дар ҷанги Бадр] ҷамъашон шикаст мехӯранд ва фирорӣ хоҳанд шуд');
INSERT INTO "pioneers" VALUES(54,46,'54:46','Ваъдагоҳашон қиёмат аст, ки [нисбат ба майдони Бадр рӯзгоре бисёр] сахттару талхтар аст');
INSERT INTO "pioneers" VALUES(54,47,'54:47','Гунаҳгорон дар гумроҳӣ ва азобанд');
INSERT INTO "pioneers" VALUES(54,48,'54:48','Рӯзе, ки бо чеҳраҳояшон дар оташ кашида мешаванд [ва ба онон мегӯянд] «Оташи дузахро бичашед»');
INSERT INTO "pioneers" VALUES(54,49,'54:49','Бе тардид, Мо ҳар чизеро ба андоза офаридаем');
INSERT INTO "pioneers" VALUES(54,50,'54:50','Ва [чун чизеро ирода намоем] фармони Мо фақат як [калима] аст; ба суръати як чашм бар ҳам задан [камтар анҷом мепазирад].');
INSERT INTO "pioneers" VALUES(54,51,'54:51','Мо [дар гузашта] ҳаммонанди шумо [кофирон]-ро ҳалок кардем; пас, оё пандпазире ҳаст?');
INSERT INTO "pioneers" VALUES(54,52,'54:52','Рафторашон ба тамомӣ дар номаҳои аъмол сабт шудааст');
INSERT INTO "pioneers" VALUES(54,53,'54:53','Ва ҳар кучаку бузурге [дар он] навишта шудааст');
INSERT INTO "pioneers" VALUES(54,54,'54:54','Парҳезкорон дар боғҳо ва [канори] ҷӯйборҳо [-и биҳиштӣ] ҷой доранд');
INSERT INTO "pioneers" VALUES(54,55,'54:55','Дар маҷлисе саршор аз ростӣ [ва шоистагӣ ва назди фармонравое муқтадир]');
INSERT INTO "pioneers" VALUES(55,1,'55:1','[Аллоҳи] Раҳмон');
INSERT INTO "pioneers" VALUES(55,2,'55:2','Қуръонро омӯзиш дод');
INSERT INTO "pioneers" VALUES(55,3,'55:3','Инсонро офарид');
INSERT INTO "pioneers" VALUES(55,4,'55:4','[Ва] Ба ӯ сухан гуфтан омӯхт');
INSERT INTO "pioneers" VALUES(55,5,'55:5','Хуршеду моҳ бо ҳисобе муназзам [-у дақиқ] дар гардишанд');
INSERT INTO "pioneers" VALUES(55,6,'55:6','Ва ситора ва дарахт [барояш] саҷда мекунанд');
INSERT INTO "pioneers" VALUES(55,7,'55:7','Ӯ осмонро барафрошт ва мизони [адолатро дар замин] барқарор намуд');
INSERT INTO "pioneers" VALUES(55,8,'55:8','[Эй мардум, чунин кард] То дар вазну паймона [ба якдигар] ситам накунед;');
INSERT INTO "pioneers" VALUES(55,9,'55:9','Ва санҷиш [-и ҳуқуқи дигарон]-ро ба адолат баровард кунед ва дар санҷиш зиён нарасонед [ва камфурӯшӣ накунед]');
INSERT INTO "pioneers" VALUES(55,10,'55:10','Ӯ заминро барои [зиндагии] инсонҳо муқаррар дошт');
INSERT INTO "pioneers" VALUES(55,11,'55:11','Ки дар он [анвои] меваҳо ва нахлҳои хушадор аст');
INSERT INTO "pioneers" VALUES(55,12,'55:12','Ва [ҳамчунин] ҳубуботи баргдор ва гиёҳони хушбӯ');
INSERT INTO "pioneers" VALUES(55,13,'55:13','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,14,'55:14','Ӯ инсонро аз гиле ҳамчун сафол офарид');
INSERT INTO "pioneers" VALUES(55,15,'55:15','Ва ҷинро аз шуълае аз оташ халқ кард');
INSERT INTO "pioneers" VALUES(55,16,'55:16','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,17,'55:17','Парвардигори машриқҳо ва мағрибҳост');
INSERT INTO "pioneers" VALUES(55,18,'55:18','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,19,'55:19','Ӯ ду дарё [-е шӯру ширин]-ро равон кард, то [ба якдигар] бирасанд');
INSERT INTO "pioneers" VALUES(55,20,'55:20','[Дар айни ҳол] Миёнашон монеъе аст, ки бо ҳам намеомезанд');
INSERT INTO "pioneers" VALUES(55,21,'55:21','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,22,'55:22','Аз он ду [дарё] марвориду марҷон ба даст меояд');
INSERT INTO "pioneers" VALUES(55,23,'55:23','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,24,'55:24','Киштиҳои кӯҳпайкари бодбонбарафрошта дар дарё барои Ӯст');
INSERT INTO "pioneers" VALUES(55,25,'55:25','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,26,'55:26','Ҳар чӣ рӯи замин аст, фанопазир аст');
INSERT INTO "pioneers" VALUES(55,27,'55:27','Ва [танҳо зоти ҷовиду] рӯйи Парвардигори бошукӯҳу арҷмандат боқӣ хоҳад монд');
INSERT INTO "pioneers" VALUES(55,28,'55:28','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,29,'55:29','[Тамоми] Касоне, ки дар осмонҳо ва замин ҳастанд аз Ӯ дархости ниёз мекунанд ва Ӯ ҳар рӯз [ва ҳар лаҳза] дар коре аст');
INSERT INTO "pioneers" VALUES(55,30,'55:30','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,31,'55:31','Эй гурӯҳи ҷинну инс, ба зудӣ ба ҳисоби шумо мепардозем');
INSERT INTO "pioneers" VALUES(55,32,'55:32','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,33,'55:33','Эй гурӯҳи ҷинну инс, агар метавонед аз каронаҳои осмону замин гузар кунед, [то аз муҷозоти илоҳӣ бираҳед] пас, гузар кунед; қодир [ба ин кор] нахоҳед буд, магар бо қудрати [фаровоне, ки шумо онро надоред]');
INSERT INTO "pioneers" VALUES(55,34,'55:34','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,35,'55:35','Шарорае аз оташ ва зарроти оташе бар шумо борида мешавад, ки [дар баробари он] қодир ба дифоъ нестед');
INSERT INTO "pioneers" VALUES(55,36,'55:36','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,37,'55:37','Он гоҳ ки осмон бишкофад ва ҳамчун равғани гудохта сурхфом гардад [қиёмат оғоз хоҳад шуд]');
INSERT INTO "pioneers" VALUES(55,38,'55:38','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,39,'55:39','Он рӯз [ба иллати равшан будани ҳамаи умур] аз инсу ҷин [дар бораи гуноҳонашон] суол нахоҳад шуд');
INSERT INTO "pioneers" VALUES(55,40,'55:40','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,41,'55:41','Гунаҳкорон аз чеҳраҳояшон шинохта мешаванд; он гоҳ [ононро] аз мӯйи пешонӣ ва пойҳояшон мегиранд [ва ба дузах меандозанд]');
INSERT INTO "pioneers" VALUES(55,42,'55:42','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,43,'55:43','Ин [ҳамон] ҷаҳаннаме аст, ки гунаҳкорон инкораш мекарданд');
INSERT INTO "pioneers" VALUES(55,44,'55:44','[Дар он ҷо] Миёни оташу оби ҷӯшон дар ҳаракатанд');
INSERT INTO "pioneers" VALUES(55,45,'55:45','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,46,'55:46','Ҳар ки аз истодан дар пешгоҳи Парвардигораш [барои ҳисоб] метарсад, ду боғ [дар биҳишт] дорад');
INSERT INTO "pioneers" VALUES(55,47,'55:47','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,48,'55:48','[Бо дарахтоне] Пур аз шохсор');
INSERT INTO "pioneers" VALUES(55,49,'55:49','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,50,'55:50','Дар он боғҳо ду чашмаи равон аст');
INSERT INTO "pioneers" VALUES(55,51,'55:51','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,52,'55:52','Дар он боғҳо аз ҳар мевае ду навъ вуҷуд дорад');
INSERT INTO "pioneers" VALUES(55,53,'55:53','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,54,'55:54','[Биҳиштиён] Бар бистарҳое, ки астарашон аз дебои захим аст, такя мезананд ва меваҳои он боғҳо дар дастрасашон аст');
INSERT INTO "pioneers" VALUES(55,55,'55:55','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,56,'55:56','Дар он боғҳо ҳуроне афтоданигоҳ ҳастанд, ки дасти аҳаде аз инсу ҷин қаблан ба онон нарасидааст');
INSERT INTO "pioneers" VALUES(55,57,'55:57','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,58,'55:58','Гӯӣ он ҳурон ёқуту марҷонанд');
INSERT INTO "pioneers" VALUES(55,59,'55:59','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,60,'55:60','Оё подоши некӣ ҷуз некӣ ҳаст?');
INSERT INTO "pioneers" VALUES(55,61,'55:61','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,62,'55:62','Дар канори он боғҳо ду боғи [биҳиштии] дигар аст');
INSERT INTO "pioneers" VALUES(55,63,'55:63','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,64,'55:64','[Ду боғ] Ки [дарахтонаш] дар ниҳояти сабзӣ [ва хуррамӣ] аст');
INSERT INTO "pioneers" VALUES(55,65,'55:65','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,66,'55:66','Дар он ду [боғи биҳиштӣ] ду чашмаи ҷӯшон ҷорӣ аст');
INSERT INTO "pioneers" VALUES(55,67,'55:67','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,68,'55:68','Ва дар онҳо [дарахтони] мева ва дарахтони хурмо ва анор ҳаст');
INSERT INTO "pioneers" VALUES(55,69,'55:69','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,70,'55:70','Дар миёни он боғҳо занони нексират ва некрӯй ҳузур доранд');
INSERT INTO "pioneers" VALUES(55,71,'55:71','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,72,'55:72','Ҳуроне, ки дар саропардаи хаймаҳои биҳиштӣ нишастаанд');
INSERT INTO "pioneers" VALUES(55,73,'55:73','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,74,'55:74','[Душизагоне, ки] Дасти аҳаде аз инсу ҷин қаблан ба онон нарасидааст');
INSERT INTO "pioneers" VALUES(55,75,'55:75','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,76,'55:76','[Биҳиштиён] Бар болиштҳое сабзу бистарҳое нафису зебо орамидаанд');
INSERT INTO "pioneers" VALUES(55,77,'55:77','Пас, [эй ҷинну инс] кадом як аз неъматҳои Парвардигоратонро инкор мекунед?');
INSERT INTO "pioneers" VALUES(55,78,'55:78','Номи Парвардигори бошукӯҳу арҷмандат бобаракату фархунда аст');
INSERT INTO "pioneers" VALUES(56,1,'56:1','Ҳангоме ки воқеаи қиёмат рух диҳад');
INSERT INTO "pioneers" VALUES(56,2,'56:2','[Хоҳед дид, ки] Дар вуқуи он дурӯғе нест');
INSERT INTO "pioneers" VALUES(56,3,'56:3','[Гурӯҳеро] Хор мекунад ва [гурӯҳеро] рафъати мақом мебахшад');
INSERT INTO "pioneers" VALUES(56,4,'56:4','Он гоҳ ки замин ба сахтӣ ларзонда шавад');
INSERT INTO "pioneers" VALUES(56,5,'56:5','Ва кӯҳҳо ба тамомӣ муталошӣ гардад');
INSERT INTO "pioneers" VALUES(56,6,'56:6','Ва [ҳамчун] ғуборе пароканда шавад');
INSERT INTO "pioneers" VALUES(56,7,'56:7','Ва шумо ба се гурӯҳ тақсим шавед');
INSERT INTO "pioneers" VALUES(56,8,'56:8','[Нахуст] Саодатмандон [ҳастанд]; чи саодатмандоне [ва чи наку ҷойгоҳе]!');
INSERT INTO "pioneers" VALUES(56,9,'56:9','Ва [гурӯҳи дигар] тирабахтон [ҳастанд]; чи тирабахтоне [ва чи бад ҷойгоҳе]!');
INSERT INTO "pioneers" VALUES(56,10,'56:10','Ва [савумин гурӯҳ] пешгомон, ки [дар некиҳо] сабқат гирифтанд');
INSERT INTO "pioneers" VALUES(56,11,'56:11','Онон муқаррабони [даргоҳи илоҳӣ] ҳастанд');
INSERT INTO "pioneers" VALUES(56,12,'56:12','Дар боғҳои пурнеъмат [-и биҳишт ҷой доранд]');
INSERT INTO "pioneers" VALUES(56,13,'56:13','Гурӯҳи бисёре аз онон дар зумраи пешиниён ҳастанд [хоҳ аз уммати ислом ё аз умматҳои гузашта];');
INSERT INTO "pioneers" VALUES(56,14,'56:14','ва андаке аз ояндагон [ва мутааххирон] ҳастанд');
INSERT INTO "pioneers" VALUES(56,15,'56:15','Ки бар тахтҳои гавҳарнишон');
INSERT INTO "pioneers" VALUES(56,16,'56:16','рӯбарӯи ҳам такя задаанд');
INSERT INTO "pioneers" VALUES(56,17,'56:17','Навҷавононе ҳамвора шодоб гирди онон [ба хидмат] мегарданд');
INSERT INTO "pioneers" VALUES(56,18,'56:18','Бо ҷомҳо ва кузаҳо ва қадаҳҳое аз шароби ҷорӣ');
INSERT INTO "pioneers" VALUES(56,19,'56:19','[Шаробе] Ки аз нӯшиданаш сардард намегарданд ва дастхуши мастӣ намешаванд');
INSERT INTO "pioneers" VALUES(56,20,'56:20','Ва аз ҳар мева [дар ихтиёр доранд]');
INSERT INTO "pioneers" VALUES(56,21,'56:21','ва гӯшти паранда, ки бихоҳанд');
INSERT INTO "pioneers" VALUES(56,22,'56:22','Ва ҳуроне ғизолчашм');
INSERT INTO "pioneers" VALUES(56,23,'56:23','Ҳамчун марвориди пинҳон дар садаф');
INSERT INTO "pioneers" VALUES(56,24,'56:24','[Инҳо ҳама] Подоши аъмолашон аст');
INSERT INTO "pioneers" VALUES(56,25,'56:25','Дар он ҷо [сухани] ёва ва гуноҳолуд нахоҳанд шунид');
INSERT INTO "pioneers" VALUES(56,26,'56:26','Дар он ҷо сухане ҷуз салом ва дуруд [-и фариштагон ва биҳиштиён] нест');
INSERT INTO "pioneers" VALUES(56,27,'56:27','Ва [аммо] саодатмандон; чи саодатмандоне!');
INSERT INTO "pioneers" VALUES(56,28,'56:28','Дар канори дарахтони бехори сидр [ҳастанд]');
INSERT INTO "pioneers" VALUES(56,29,'56:29','Ва дарахтони мавз бо хушаҳои барҳамнишаста');
INSERT INTO "pioneers" VALUES(56,30,'56:30','Бо сояе густурда');
INSERT INTO "pioneers" VALUES(56,31,'56:31','Ва оби ҳамвора равон');
INSERT INTO "pioneers" VALUES(56,32,'56:32','Ва меваҳое фаровон,');
INSERT INTO "pioneers" VALUES(56,33,'56:33','ки на тамом шавад ва на мамнуъ гардад');
INSERT INTO "pioneers" VALUES(56,34,'56:34','Ва бистарҳои барафрошта [ва арзишманд]');
INSERT INTO "pioneers" VALUES(56,35,'56:35','Ва ҳамсароне, ки бо офаринише вижа падид овардем [ки зебоӣ ва ҷавонии ҷовидон доранд]');
INSERT INTO "pioneers" VALUES(56,36,'56:36','Ҳамвора душизаанд');
INSERT INTO "pioneers" VALUES(56,37,'56:37','ва шавҳардӯстоне ҳамсиннусол ҳастанд');
INSERT INTO "pioneers" VALUES(56,38,'56:38','[Ҳамаи ин неъматҳо] Барои саодатмандон аст,');
INSERT INTO "pioneers" VALUES(56,39,'56:39','Дар боғҳои пурнеъмат [-и биҳишт ҷой доранд]');
INSERT INTO "pioneers" VALUES(56,40,'56:40','ки бархе аз онон дар зумраи пешиниён ва бархе дигар дар зумраи мутааххирон ҳастанд');
INSERT INTO "pioneers" VALUES(56,41,'56:41','Ва [аммо] тирабахтон; чи тирабахтоне!');
INSERT INTO "pioneers" VALUES(56,42,'56:42','Дар миёни боди заҳрогин ва оби ҷӯшон қарор доранд');
INSERT INTO "pioneers" VALUES(56,43,'56:43','Ва сояҳое аз дуди сиёҳ,');
INSERT INTO "pioneers" VALUES(56,44,'56:44','ки на хунук аст ва на хуш');
INSERT INTO "pioneers" VALUES(56,45,'56:45','Онон дар дунё сарватманду саркаш буданд');
INSERT INTO "pioneers" VALUES(56,46,'56:46','Ва бар гуноҳи бузург [-и ширк] исрор меварзиданд');
INSERT INTO "pioneers" VALUES(56,47,'56:47','Ва мегуфтанд: «Оё ҳангоме ки мурдем ва хоку устухон шудем, барангехта хоҳем шуд?');
INSERT INTO "pioneers" VALUES(56,48,'56:48','Оё ниёкони мо низ [барангехта мешаванд]?»');
INSERT INTO "pioneers" VALUES(56,49,'56:49','[Эй Паёмбар, ба кофирон] Бигӯ: «Бе тардид, гузаштагон ва ояндагон');
INSERT INTO "pioneers" VALUES(56,50,'56:50','ҳама дар ваъдагоҳи қиёмат ҷамъ хоҳанд шуд»');
INSERT INTO "pioneers" VALUES(56,51,'56:51','Он гоҳ шумо, эй гумроҳони такзибкунанда');
INSERT INTO "pioneers" VALUES(56,52,'56:52','Ҳатман, аз [меваи] дарахти зақум хоҳед хӯрд');
INSERT INTO "pioneers" VALUES(56,53,'56:53','Ва шикамҳоро аз он анбошта мекунед');
INSERT INTO "pioneers" VALUES(56,54,'56:54','Он гоҳ рӯйи он оби ҷӯшон менӯшед');
INSERT INTO "pioneers" VALUES(56,55,'56:55','Ҳамчун шутурони ташна');
INSERT INTO "pioneers" VALUES(56,56,'56:56','Ин пазироии онон дар рӯзи ҷазост');
INSERT INTO "pioneers" VALUES(56,57,'56:57','Моем, ки шуморо [аз ҳеҷ] офаридаем; пас, чаро [офариниши дубораро] бовар надоред?');
INSERT INTO "pioneers" VALUES(56,58,'56:58','Оё ба нутфае, ки [дар раҳими ҳамсаронатон] мерезед, таваҷҷуҳ кардаед?');
INSERT INTO "pioneers" VALUES(56,59,'56:59','Оё шумо онро меофаринед ё мо офаринандааш ҳастем?');
INSERT INTO "pioneers" VALUES(56,60,'56:60','Мо дар миёни шумо маргро муқаддар кардем ва нотавон нестем,');
INSERT INTO "pioneers" VALUES(56,61,'56:61','ки амсоли шуморо ҷойгузин [-и худатон] кунем ва шуморо ба гунае ки аз он бехабаред, [дар офаринише нав] падид оварем');
INSERT INTO "pioneers" VALUES(56,62,'56:62','Шумо, ки аз офариниши нахустин ба равшанӣ огоҳ шудаед, чаро [ба ёди охират намеафтед ва] панд намегиред?');
INSERT INTO "pioneers" VALUES(56,63,'56:63','Оё ба донаҳое, ки мекоред, таваҷҷуҳ кардаед?');
INSERT INTO "pioneers" VALUES(56,64,'56:64','Оё шумо ҳастед, ки онро мерӯёнед ё Мо рӯёнандаем?');
INSERT INTO "pioneers" VALUES(56,65,'56:65','Агар мехостем, хошокаш мекардем; чунон ки шигифтзада шавед');
INSERT INTO "pioneers" VALUES(56,66,'56:66','[Ва чунон нобудаш мекардем, ки бигӯед] «Мо зиён кардаем,');
INSERT INTO "pioneers" VALUES(56,67,'56:67','балки ҳама чизро аз даст додаем');
INSERT INTO "pioneers" VALUES(56,68,'56:68','Оё ба обе, ки менӯшед, таваҷҷуҳ кардаед?');
INSERT INTO "pioneers" VALUES(56,69,'56:69','Оё шумо онро аз абр фуру боридед ё Мо фуруборандаи он ҳастем');
INSERT INTO "pioneers" VALUES(56,70,'56:70','Агар мехостем, онро шӯру талх мекардем, пас, чаро сипос намегузоред?');
INSERT INTO "pioneers" VALUES(56,71,'56:71','Оё ба оташе, ки меафрӯзед, таваҷҷуҳ кардаед?');
INSERT INTO "pioneers" VALUES(56,72,'56:72','Оё шумо дарахташро офаридаед Мо офаридаем?');
INSERT INTO "pioneers" VALUES(56,73,'56:73','Мо ин оташро василаи ёдоварии [азоби дузах] ва мояи бархӯрдорӣ [ва роҳатии] мусофирон қарор додаем');
INSERT INTO "pioneers" VALUES(56,74,'56:74','Пас, [эй Паёмбар,] ба номи Парвардигори бузургат тасбеҳ гӯ [ва Ӯро ба покӣ ёд кун]');
INSERT INTO "pioneers" VALUES(56,75,'56:75','Савганд ба ҷойгоҳи ситорагон');
INSERT INTO "pioneers" VALUES(56,76,'56:76','Ки агар бидонед, савганди бузурге аст');
INSERT INTO "pioneers" VALUES(56,77,'56:77','Ки ин [гуфтор] Қуръоне гаронқадр аст');
INSERT INTO "pioneers" VALUES(56,78,'56:78','Дар лавҳи маҳфуз қарор дорад');
INSERT INTO "pioneers" VALUES(56,79,'56:79','Ҷуз фариштагони поксират касе [онро намебинад] ба он дастрасӣ надорад');
INSERT INTO "pioneers" VALUES(56,80,'56:80','Аз сӯйи Парвардигори ҷаҳониён нозил шудааст');
INSERT INTO "pioneers" VALUES(56,81,'56:81','Оё ин суханро сабук мешуморед?');
INSERT INTO "pioneers" VALUES(56,82,'56:82','Ва ба ҷойи шукри рӯзиҳое, ки ба шумо дода шудааст, онро дурӯғ мепиндоред?');
INSERT INTO "pioneers" VALUES(56,83,'56:83','Он гоҳ ки [ҷони наздиконатон] ба гулугоҳ мерасад');
INSERT INTO "pioneers" VALUES(56,84,'56:84','Ва шумо дар он ҳангом наззора мекунед');
INSERT INTO "pioneers" VALUES(56,85,'56:85','Ва Мо аз шумо ба ӯ наздиктарем, вале намебинед');
INSERT INTO "pioneers" VALUES(56,86,'56:86','Агар рост мегӯед ва ҳаргиз дар баробари аъмолатон ҷазо дода намешавед [ва қиёмате нест],');
INSERT INTO "pioneers" VALUES(56,87,'56:87','пас, чаро ҷонашро бознамегардонед?');
INSERT INTO "pioneers" VALUES(56,88,'56:88','Аммо агар ӯ дар зумраи муқаррабон бошад');
INSERT INTO "pioneers" VALUES(56,89,'56:89','Дар оромишу кушоиш ва биҳишти пурнеъмат аст');
INSERT INTO "pioneers" VALUES(56,90,'56:90','Ва агар дар зумраи саодатмандон бошад,');
INSERT INTO "pioneers" VALUES(56,91,'56:91','[ба ӯ гуфта мешавад] «Саломат ва амният бар ту бод, ки аз саодатмандон ҳастӣ»');
INSERT INTO "pioneers" VALUES(56,92,'56:92','Аммо агар аз такзибкунандагони гумроҳ [ва дар зумраи тирабахтон] бошад');
INSERT INTO "pioneers" VALUES(56,93,'56:93','бо оби ҷӯшон [аз ӯ] пазироӣ мешавад');
INSERT INTO "pioneers" VALUES(56,94,'56:94','ва ба [оташи] дузах дармеояд');
INSERT INTO "pioneers" VALUES(56,95,'56:95','Ин [ваъдаи подош ва азоб] яқинан ҳақиқат дорад [ва тардиде дар он нест]');
INSERT INTO "pioneers" VALUES(56,96,'56:96','Пас, [эй Паёмбар] бо [зикри] номи Парвардигори бузургат [ӯро] тасбеҳ гӯй');
INSERT INTO "pioneers" VALUES(57,1,'57:1','Он чӣ дар осмонҳо ва замин аст, барои Аллоҳ таоло тасбеҳ мегӯянд; ва Ӯ пирӯзманди ҳаким аст');
INSERT INTO "pioneers" VALUES(57,2,'57:2','Фармонравоии осмонҳо ва замин аз они Ӯст; ҳаёт мебахшад ва марг медиҳад; ва бар ҳар коре тавоност');
INSERT INTO "pioneers" VALUES(57,3,'57:3','Ӯ азалӣ ва абадӣ аст ва пайдо ва нопайдо; ва ба ҳар чизе доност');
INSERT INTO "pioneers" VALUES(57,4,'57:4','Ӯст, ки осмонҳо ва заминро дар шаш рӯз офарид; он гоҳ бар арш қарор гирифт. Ҳар чӣ дар замин нуфуз мекунад ё аз он бармеояд ва ҳар чӣ аз осмон фуруд меояд ё дар он суъуд мекунад, [ҳамаро] медонад; ва ҳар ҷо, ки бошед, ҳамроҳи шумост; ва Аллоҳ таоло ба он чӣ мекунед, биност');
INSERT INTO "pioneers" VALUES(57,5,'57:5','Фармонравоии осмонҳо ва замин аз они Ӯст; ва ҳамаи умур танҳо ба Ӯ бозгардонда мешавад');
INSERT INTO "pioneers" VALUES(57,6,'57:6','[Бо тағйири тадриҷии фаслҳо] шабро дар рӯз мекашонад ва рӯзро дар шаб; ва Ӯ ба рози дилҳо доност');
INSERT INTO "pioneers" VALUES(57,7,'57:7','Ба Аллоҳ таоло ва Паёмбараш имон оваред ва аз амволе, ки шуморо ҷонишин [ва намояндаи худ дар истифода аз онҳо] қарор додааст, инфоқ кунед; зеро афроде аз шумо, ки имон биёваранд ва инфоқ кунанд, подоши бузурге [дар пеш] дорад');
INSERT INTO "pioneers" VALUES(57,8,'57:8','Чаро ба Аллоҳ таоло имон намеоваред, ҳол он ки Паёмбар шуморо фаромехонад, ки ба Парвардигоратон имон биёваред? Агар бовар доред [ба ёд дошта бошед, ки Аллоҳ таоло дар ин маврид] аз шумо паймон гирифтааст');
INSERT INTO "pioneers" VALUES(57,9,'57:9','Ӯст, ки оёти равшангаре бар бандааш нозил мекунад, то шуморо аз торикиҳо [-и куфру ширк] ба сӯйи нур [-и имон] раҳсипор гардонад, [зеро] Аллоҳ таоло нисбат ба шумо дилсӯзу меҳрубон аст');
INSERT INTO "pioneers" VALUES(57,10,'57:10','Чаро дар роҳи Аллоҳ таоло инфоқ намекунед, ҳол он ки мероси осмонҳо ва замин аз они Аллоҳ таоло аст? Касоне, ки пеш аз фатҳи [Макка] инфоқ карданд ва ҷангиданд, [бо дигарон] баробар нестанд. Дараҷаи онон волотар аз касонест, ки пас аз фатҳ инфоқ карданд ва ҷангиданд, вале Аллоҳ таоло ба ҳар ду гурӯҳ накутарин [подошҳо]-ро ваъда додааст ва Аллоҳ таоло ба он чи мекунед, огоҳ аст');
INSERT INTO "pioneers" VALUES(57,11,'57:11','Кист, ки ба Аллоҳ таоло воме наку диҳад [ва инфоқ кунад], то Аллоҳ таоло низ онро барояш чандин баробар созад ва [дар охират] подоше фохир дошта бошад?');
INSERT INTO "pioneers" VALUES(57,12,'57:12','[Ин подоши бузург дар] Рӯзест, ки мардону занони муъминро менигарӣ, ки нурашон пеши рӯ ва дар самти росташон ба шитоб ҳаракат мекунад [ва ба онон хитоб мешавад]: «Имрӯз башорати боғҳое [аз биҳишт] бар шумо бод, ки ҷӯйборҳо аз зери [дарахтони] он ҷорӣ аст ва ҷовидона дар он ба сар хоҳед бурд. Ин аст ҳамон комёбии бузург».');
INSERT INTO "pioneers" VALUES(57,13,'57:13','Рӯзе, ки мардону занони мунофиқ ба муъминон мегӯянд: «Ба мо назаре бияфканед, то партаве аз нуратон баргирем». Ба онон гуфта мешавад: «Ба ақиб бозгардед ва касби нур кунед. Он гоҳ миёнашон деворе қарор мегирад, ки даре дорад, даруни он [ки ба сӯи муъминон аст] рӯ ба раҳмат аст ва берунаш [ки ба самти мунофиқон аст] рӯ ба азоб аст');
INSERT INTO "pioneers" VALUES(57,14,'57:14','[Мунофиқон] ба муъминон мегӯянд: «Магар мо [дар диндорӣ ва парастиш] ҳамроҳи шумо набудем?». Онон посух медиҳанд: «Бале, вале шумо [бо дурӯягӣ ва тазвир] худро ба фитна андохтед [ва ҳалок кардед] ва дар интизор [-и шикасти муъминон ва бозгашт ба ширку куфр] будед ва [дар бораи ёрӣ расонидани Аллоҳ таоло ба муъминон] тардид доштед ва орзуҳо [-и дурӯғин] фиребатон дод, то он ки фармон [-и маргатон аз ҷониби] Аллоҳ таоло дар ҳоле фаро расид, ки шайтон шуморо дар баробари Ӯ густох карда буд»');
INSERT INTO "pioneers" VALUES(57,15,'57:15','Имрӯз на аз шумо [мунофиқон] ғаромат мепазиранд ва на аз кофирон. Ҷойгоҳатон оташ аст, ки [ба ростӣ] шоистаи шумост ва чи бад саранҷоме аст!');
INSERT INTO "pioneers" VALUES(57,16,'57:16','Оё ҳангоми он фаро нарасидааст, ки дилҳои муъминон ба ёди Аллоҳ таоло ва каломи ҳаққе, ки нозил шудааст, фурутан шавад ва ҳамчун касоне набошанд, ки қаблан аҳли китоб буданд ва чун [аз биъсати паёмбаронашон] замоне тӯлонӣ бар онон гузашт, дилҳояшон сахт шуд ва бисёре аз онон аз динашон мунҳариф шуданд?');
INSERT INTO "pioneers" VALUES(57,17,'57:17','Огоҳ бошед, ки Аллоҳ таоло заминро пас аз хазону хушкиаш зинда месозад. [Эй мардум] Мусалламан, Мо нишонаҳо [-и тавону тадбири худ]-ро барои шумо ба равшанӣ баён кардаем, бошад, ки бияндешед');
INSERT INTO "pioneers" VALUES(57,18,'57:18','Бе гумон, мардону заноне, ки инфоқ мекунанд ва ба Аллоҳ таоло қарзе наку медиҳанд, савобашон дучандон мегардад ва [дар охират] подоше фохир [-у нек] хоҳанд дошт');
INSERT INTO "pioneers" VALUES(57,19,'57:19','Касоне, ки ба Аллоҳ таоло ва паёмбаронаш имон оварданд, ростгӯён [-и росткирдор] ҳастанд ва шаҳидон назди Парвардигорашон ба сар мебаранд ва [дар охират] подошу нурашон маҳфуз аст. Ва касоне, ки куфр варзиданд ва оёти Моро дурӯғ шумурданд, дузахианд');
INSERT INTO "pioneers" VALUES(57,20,'57:20','Бидонед, ки зиндагии дунё, бозича ва саргармӣ ва зевар аст ва фахрфурӯшӣ дар баробари якдигар ва афзунталабӣ дар амволу фарзандон. [Ин зиндагӣ] Дар масал ҳамчун боронест, ки гиёҳон [-и рӯйида ] аз он кишоварзонро ба шигифт меоварад; сипас [ин гиёҳи шодоб] пажмурда шавад, он гоҳ мебинӣ, ки зард мешавад ва сипас хошок мегардад ва дар охират [насиби мунофиқону кофирон] азоби шадид аст ва [насиби муъминон] омурзишу хушнудии илоҳӣ аст. [Дар ҳар ҳол] Зиндагии дунё чизе ҷуз [лаззату] бархӯрдории фонӣ [ва фиребанда] нест');
INSERT INTO "pioneers" VALUES(57,21,'57:21','[Эй мардум] Барои расидан ба омурзиши Парвардигоратон ва биҳиште, ки паҳнои он ҳамчун паҳнои осмону замин аст, аз якдигар пешӣ бигиред. [Ин биҳишт] Барои касоне омода шудааст, ки ба Аллоҳ таоло ва паёмбаронаш имон овардаанд. Ин [подош ношӣ аз] фазли илоҳӣ аст, онро ба ҳар ки бихоҳад [ва шоиста бидонад], ато мекунад, ки Аллоҳ таоло фазлу бахшиши беҳад дорад');
INSERT INTO "pioneers" VALUES(57,22,'57:22','Ҳеҷ осебе дар замин ва ё дар вуҷуди шумо рӯй нахоҳад дод, магар он ки пеш аз эҷодаш дар дафтари [илми илоҳӣ] рақам хӯрдааст. Ба ростӣ, ки ин кор бар Аллоҳ таоло осон аст');
INSERT INTO "pioneers" VALUES(57,23,'57:23','[Ин нуктаро тазаккур додем] То бар он чи аз дастатон меравад, афсӯс нахӯред ва бад-он чи ба даст меоваред, сармаст нагардед, ки Аллоҳ таоло ҳеҷ мутакаббири фахрфурӯшеро дӯст намедорад');
INSERT INTO "pioneers" VALUES(57,24,'57:24','Ҳамон касоне, ки бухл меварзанд ва мардумро ба бухл во медоранд ва ҳар ки [аз инфоқ] рӯй бигардонад, [бидонад, ки] Аллоҳ таоло бениёзи сутуда аст');
INSERT INTO "pioneers" VALUES(57,25,'57:25','Мо паёмбарони хешро бо нишонаҳои равшан фиристодем ва ҳамроҳашон китобу мизон [-и ташхиси ҳақ аз ботил] нозил кардем, то мардум ба адолат бархезанд. Ва оҳан [ва соири фулузот]-ро падид овардем, ки дар он ҳам неруе шадид вуҷуд дорад ва ҳам манофее барои мардум. [Чунин кардем] То Аллоҳ таоло бидонад, чи касоне бидуни он ки Парвардигорашонро бубинанд, [бо ҷангафзорҳои оҳанин] Ӯ ва паёмбаронашро [дар майдонҳои ҷанг] ёрӣ мекунанд. Бе тардид, Аллоҳ таоло неруманди шикастнопазир аст');
INSERT INTO "pioneers" VALUES(57,26,'57:26','Мо Нуҳ ва Иброҳимро [ба рисолат] фиристодем ва дар миёни фарзандонашон [мавҳибати] паёмбарӣ ва китоб муқаррар доштем. Бархе аз онон роҳ ёфтанд ва бисёре аз онон нофармонӣ карданд');
INSERT INTO "pioneers" VALUES(57,27,'57:27','Сипас ба дунболи онон паёмбарони дигарамонро [мабъус кардем] ва сипас Исо писари Марямро фиристодем ва Инҷилро ба ӯ ато намудем ва дар дили пайравонаш раъфат ва меҳрубонӣ ниҳодем, вале [дунёгурезӣ ва] раҳбониятеро, ки бидъат ниҳоданд, Мо бар онон муқаррар надоштем ва ҳарчанд ҳадафашон ҷалби хушнудии илоҳӣ буд, вале ҳаққи онро [чунонки бояд] риоят накарданд. Аз ин рӯ, Мо ба афроде аз эшон, ки имон оварданд, подошашонро ато кардем, вале бисёре аз онон [ба бероҳа рафтанд ва] нофармон буданд');
INSERT INTO "pioneers" VALUES(57,28,'57:28','Эй муъминон, аз Аллоҳ таоло парво кунед ва ба паёмбараш [холисона] имон биёваред, то Аллоҳ таоло аз раҳмати хеш баҳраи дучандон ба шумо ато намояд ва бароятон нуре падид оварад, ки дар партави он гом бардоред ва шуморо бибахшояд. Ба ростӣ, ки Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(57,29,'57:29','[Инро баён кардем] То аҳли китоб бидонанд, ки ба фазлу бахшиши илоҳӣ дастрасӣ надоранд [ва наметавонанд онро ба касе ихтисос диҳанд ё аз касе боздоранд] ва ин ки тамоми бартарӣ ба дасти Аллоҳ таоло аст ва ба ҳар кӣ бихоҳад [ва шоиста бидонад] ато мекунад. Бе гумон, Аллоҳ таоло фазлу бахшиши беҳад дорад');
INSERT INTO "pioneers" VALUES(58,1,'58:1','Аллоҳ таоло сухани он занро шунид, ки бо ту дар бораи шавҳараш суҳбат мекард ва ба Аллоҳ таоло шикоят мебурд. Аллоҳ таоло гуфтугӯи шуморо мешунавад, чаро ки Аллоҳ таоло шунавои биност');
INSERT INTO "pioneers" VALUES(58,2,'58:2','Касоне аз шумо, ки ҳамсарони хешро [аз лиҳози ҳурмати ҳамбистарӣ ҳамчун] модари худ талаққӣ мекунанд, [бидонанд, ки] он занон ҳаргиз модаронашон нахоҳанд буд; модарони эшон фақат заноне ҳастанд, ки онҳоро ба дунё овардаанд. Онон сухане нописанд ва ноҳақ мегӯянд ва [агар тавба кунанд] Аллоҳ таоло бахшояндаи омурзгор аст');
INSERT INTO "pioneers" VALUES(58,3,'58:3','Касоне, ки занонашонро зиҳор мекунанд, сипас аз он чи гуфтаанд, бозмегарданд, бояд пеш аз омезиши ҷинсӣ бо ҳам бардаеро озод кунанд. Ин ҳукмест, ки ба он [панду] андарз дода мешавед ва Аллоҳ таоло ба он чи мекунед, огоҳ аст');
INSERT INTO "pioneers" VALUES(58,4,'58:4','Пас, касе, ки [бардаеро] наёбад, пеш аз омезиши ҷинсӣ ду моҳ паёпай рӯза бигирад ва касе, ки наметавонад [рӯза бигирад, дар он сурат] бояд шаст мискинро таом диҳад. Ин [каффора] барои он аст, ки ба Аллоҳ таоло ва расулаш имон биёваред ва инҳо ҳудуди [аҳкоми] илоҳӣ аст ва барои кофирон азоби дардноке [дар пеш] аст');
INSERT INTO "pioneers" VALUES(58,5,'58:5','Ҳамоно касоне, ки бо Аллоҳ таоло ва расулаш душманӣ мекунанд, хор [-у залил] мешаванд, ҳамон гуна ки пешиниёни онҳо низ хор [-у залил] шуданд ва ба ростӣ, Мо оёти равшане нозил кардем ва барои кофирон азоби хоркунандае [дар пеш] аст');
INSERT INTO "pioneers" VALUES(58,6,'58:6','Рӯзе, ки Аллоҳ таоло ҳамаи онҳоро [аз гӯр] бармеангезад, сипас онҳоро аз он чи кардаанд, бохабар месозад, [ҳамон аъмоле, ки] Аллоҳ таоло ҳисоби онро нигаҳ дошта, аммо онҳо фаромӯшаш кардаанд ва Аллоҳ таоло бар ҳама чиз гувоҳ [ва нозир] аст');
INSERT INTO "pioneers" VALUES(58,7,'58:7','Магар надидаӣ, ки Аллоҳ таоло он чиро, ки дар осмонҳо ва он чиро, ки дар замин аст, медонад? Ҳеҷ наҷвое миёни се нафар набошад, магар он ки Ӯ чаҳорумини онҳост ва на миёни панҷ нафар, магар он ки Ӯ шашумини онҳост ва на камтар аз ин [адад] на бештар, магар он ки ҳар куҷо бошанд, Ӯ ҳамроҳи онҳост. Сипас рӯзи қиёмат онҳоро аз он чи кардаанд, бохабар месозад. Бе гумон, Аллоҳ таоло ба ҳама чиз доност');
INSERT INTO "pioneers" VALUES(58,8,'58:8','[Эй паёмбар,] магар надидаӣ касонеро, ки аз наҷво наҳй шуданд, сипас ба он чӣ аз он наҳй шуда буданд, бозмегарданд ва ба гуноҳ ва таҷовуз ва нофармонӣ аз паёмбар бо ҳам наҷво мекунанд ва ҳангоме ки назди ту меоянд, ба наҷво ба ту дуруд мефиристанд, ки [ҳатто] Аллоҳ таоло бо он [калимот] ба ту дуруду салом нагуфтааст; ва [онҳо] дар дили худ мегӯянд: «Чаро Аллоҳ таоло моро ба [кайфари] он чи мегӯем, азоб намекунад?» Ҷаҳаннам барояшон кофӣ аст, [ки] ба он ворид мешаванд; ва чи бад [саранҷом ва] ҷойгоҳе аст!');
INSERT INTO "pioneers" VALUES(58,9,'58:9','Эй касоне, ки имон овардаед, ҳангоме ки бо якдигар наҷво мекунед, ба қасди гуноҳ ва таҷовуз ва нофармонӣ аз расул наҷво накунед ва ба некӣ ва парҳезгорӣ наҷво кунед ва аз Аллоҳ таоло битарсед, ки ба сӯи Ӯ ҳашр хоҳед шуд.');
INSERT INTO "pioneers" VALUES(58,10,'58:10','Ҷуз ин нест, ки сухани пинҳоне [ки муҷиби гуноҳ ва душманӣ мегардад] аз сӯи шайтон аст, то касонеро, ки имон овардаанд, [бо онҳо гирифтор кунад ва дар натиҷа] андӯҳгин созад ва [ҳол он ки ӯ] ҷуз ба фармони Аллоҳ таоло наметавонад ҳеҷ зараре ба онон бирасонад; пас, муъминон бояд бар Аллоҳ таоло таваккал кунанд');
INSERT INTO "pioneers" VALUES(58,11,'58:11','Эй касоне, ки имон овардаед, ҳангоме ки ба шумо гуфта шавад: «Дар маҷолис ҷо боз кунед», пас, [ҷо] боз кунед [ки бо ин кор] Аллоҳ таоло барои шумо кушоиш меоварад ва ҳангоме ки гуфта шавад: «Бархезед», пас, бархезед. Аллоҳ таоло мақом [-у дараҷот]-и касоне аз шуморо, ки имон овардаанд ва касонеро, ки илм дода шудаанд, боло мебарад ва Аллоҳ таоло ба он чи мекунед, огоҳ аст');
INSERT INTO "pioneers" VALUES(58,12,'58:12','Эй касоне, ки имон овардаед, ҳангоме ки мехоҳед бо расулуллоҳ наҷво кунед, пеш аз наҷвоятон садақае [дар роҳи Аллоҳ таоло] бидиҳед. Ин [кор] барои шумо беҳтару покизатар аст. Аммо агар чизе наёфтед, бе гумон, Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(58,13,'58:13','Оё [аз фақр] тарсидед, ки пеш аз наҷвоятон садақаҳое диҳед? Ҳол ки чунин накардед ва Аллоҳ таоло аз шумо даргузашт, пас, намоз барпо доред ва закот бипардозед, ва аз Аллоҳ таоло ва Паёмбараш итоат кунед ва Аллоҳ таоло ба он чи мекунед, огоҳ аст.');
INSERT INTO "pioneers" VALUES(58,14,'58:14','[Эй паёмбар] Оё надидӣ касонеро, ки бо қавме дӯстӣ карданд, ки Аллоҳ таоло бар онон хашм [-у ғазаб] кардааст; онҳо на аз шумо ҳастанд ва на аз онон [яҳудиён]; ва ба дурӯғ савганд ёд мекунанд [ки мусулмонанд], дар ҳоле ки худ медонанд [ки мунофиқанд].');
INSERT INTO "pioneers" VALUES(58,15,'58:15','Аллоҳ таоло азоби сахте барои онҳо омода кардааст, чаро ки бе гумон, аъмоли баде анҷом медоданд');
INSERT INTO "pioneers" VALUES(58,16,'58:16','Онҳо савгандҳои худро сипар [-и бало] қарор доданд ва [мардумро] аз роҳи Аллоҳ таоло боздоштанд; пас, азоби хоркунандае [дар пеш] доранд.');
INSERT INTO "pioneers" VALUES(58,17,'58:17','Ҳаргиз амволу фарзандонашон чизе аз азоби Аллоҳро аз онҳо дафъ нахоҳад кард. Онҳо аҳл [-и оташ]-и дузаханд ва ҷовидона дар он мемонанд');
INSERT INTO "pioneers" VALUES(58,18,'58:18','Рӯзе, ки Аллоҳ таоло ҳамагии онҳоро бармеангезад, он гоҳ онҳо барои Ӯ [низ] савганд ёд мекунанд, ҳамон гуна ки [имрӯз дар дунё] барои шумо савганд ёд мекунанд ва гумон мекунанд, ки бар чизе [судманд] ҳастанд. Огоҳ бошед [ва бидонед], ки онҳо дурӯғгӯёнанд');
INSERT INTO "pioneers" VALUES(58,19,'58:19','Шайтон бар онҳо чира шуда ва ёди Аллоҳро аз хотирашон бурдааст. Онҳо ҳизби шайтон ҳастанд ва огоҳ бошед [ва бидонед], ки ҳизби шайтон зиёнкоранд');
INSERT INTO "pioneers" VALUES(58,20,'58:20','Бе гумон, касоне, ки бо Аллоҳ таоло ва паёмбараш мухолифат [ва душманӣ] мекунанд, дар зумраи хортарин [афрод]-анд');
INSERT INTO "pioneers" VALUES(58,21,'58:21','Аллоҳ таоло муқаррар доштааст, ки «яқинан, ману расулонам пирӯз мешавем». Бе гумон, Аллоҳ таоло неруманди шикастнопазир аст.');
INSERT INTO "pioneers" VALUES(58,22,'58:22','[Эй паёмбар] ҳеҷ қавмеро намеёбӣ, ки ба Аллоҳ таоло ва рӯзи қиёмат имон дошта бошанд ва бо касоне, ки бо Аллоҳу расулаш [душманӣ ва] мухолифат меварзанд, дӯстӣ кунанд; агарчи падаронашон ё фарзандонашон ё бародаронашон ё хешовандонашон бошанд. Онҳо касоне ҳастанд, ки Аллоҳ таоло имонро дар [сафҳаи] дилҳояшон навиштааст ва ба рӯҳе аз ҷониби худ онҳоро тақвият [ва таъйид] намудааст ва онҳоро ба боғҳое [аз биҳишт] ворид мекунад, ки аз зери [дарахтони] он ҷӯйборҳо ҷорӣ аст, ҷовидона дар он мемонанд. Аллоҳ таоло аз онҳо хушнуд аст ва онҳо [низ] аз Аллоҳ таоло хушнуданд. Онҳо ҳизби Аллоҳ таоло ҳастанд. Огоҳ бошед! Бе тардид, ҳизби Аллоҳ таоло растагоранд');
INSERT INTO "pioneers" VALUES(59,1,'59:1','Он чи дар осмонҳо ва он чи дар замин аст, барои Аллоҳ таоло тасбеҳ мегӯянд ва Ӯ пирӯзманди ҳаким аст');
INSERT INTO "pioneers" VALUES(59,2,'59:2','Ӯст, ки касоне аз аҳли китобро, ки кофир шуданд, бо нахустин бархӯрд [ва гирдоварии лашкар] аз диёрашон берун ронд. Гумон намекардед, ки онҳо хориҷ шуданд ва худашон [низ] гумон мекарданд, ки дижҳо [-и муҳкам]а-шон онҳоро аз [азоби] Аллоҳ таоло монеъ мешавад; пас, [азоби] Аллоҳ таоло аз ҷойе, ки гумон намекарданд, ба суроғашон омад ва дар дилҳояшон тарсу ваҳшат афканд, [ба гунае ки] хонаҳои худро бо дасти худ ва бо дасти муъминон вайрон мекарданд; пас, эй соҳибони биниш, ибрат гиред');
INSERT INTO "pioneers" VALUES(59,3,'59:3','Агар Аллоҳ таоло тарки ватан [ва оворагӣ]-ро бар онон муқаррар надошта буд, яқинан ононро дар [ҳамин] дунё азоб мекард ва барои эшон дар охират азоби оташ [-и ҷаҳаннам] аст');
INSERT INTO "pioneers" VALUES(59,4,'59:4','Ин [оворагӣ ва азоб] ба хотири он аст, ки онҳо бо Аллоҳ таоло ва паёмбараш мухолифат [ва душманӣ] карданд ва ҳар кас бо Аллоҳ таоло мухолифат [ва душманӣ] кунад, бе гумон, Аллоҳ таоло сахткайфар аст');
INSERT INTO "pioneers" VALUES(59,5,'59:5','Он чи аз дарахти хурмо буридед ва ё онро истода бар решааш боқӣ гузоридед, ба фармони Аллоҳ таоло буд, то фосиқонро хор [-у расво] созад.');
INSERT INTO "pioneers" VALUES(59,6,'59:6','Ва он чи Аллоҳ таоло аз [амволи] онҳо ба паёмбараш бозгардонида [ва бахшида] аст, пас, бар он [амвол] на аспе тохтед ва на шутуре; вале Аллоҳ таоло расулонашро бар ҳар кас, ки бихоҳад, чира мегардонад ва Аллоҳ таоло бар ҳар чиз тавоност');
INSERT INTO "pioneers" VALUES(59,7,'59:7','Ва он чи Аллоҳ таоло аз [амволи] аҳли ободиҳо ба паёмбараш бозгардонида [ва бахшида] аст, аз они Аллоҳ таоло ва расул ва хешовандони ӯ ва ятимон ва бенавоён ва дарроҳмондагон аст. То [ин амвол] дар миёни сарватмандони шумо даст ба даст нашавад; ва он чи ки расулуллоҳ ба шумо дод, бигиред ва аз он чи ки шуморо аз он наҳй кард, даст бардоред ва аз Аллоҳ таоло парво кунед, ки Аллоҳ таоло сахткайфар аст');
INSERT INTO "pioneers" VALUES(59,8,'59:8','[Ин амвол] Барои фуқарои муҳоҷире аст, ки аз хона ва амволашон берун ронда шуданд, аз Аллоҳ таоло фазл ва хушнудӣ металабанд ва Аллоҳ таоло ва паёмбарашро ёрӣ мекунанд. Онҳо ростгӯёнанд');
INSERT INTO "pioneers" VALUES(59,9,'59:9','Ва [низ] касоне, ки пеш аз онон дар диёри ислом [Мадина] ҷой гирифта ва имон овардаанд, афродеро, ки ба сӯяшон ҳиҷрат мекунанд, дӯст доранд ва дар дилҳои худ аз он чи [ба муҳоҷирон] дода шуда, эҳсоси ҳасад [ва ниёз] намекунанд ва эшонро бар худ муқаддам медоранд, ҳатто агар худ ниёзманд бошанд; ва касоне, ки аз бухл [-у ҳирс]-и нафси хеш дар амон бимонанд [ва дар роҳи Аллоҳ таоло инфоқ кунанд], онон растагоранд');
INSERT INTO "pioneers" VALUES(59,10,'59:10','Ва [низ] Касоне, ки пас аз онҳо [пас аз муҳоҷирону ансор] омадаанд, мегӯянд: «Парвардигоро, мо ва бародаронамонро, ки дар имон бар мо пешӣ гирифтанд, биёмурз ва дар дилҳоямон нисбат ба касоне, ки имон овардаанд, кинае қарор мадеҳ. Парвардигоро, бе гумон, Ту дилсӯзи меҳрубонӣ»');
INSERT INTO "pioneers" VALUES(59,11,'59:11','Оё мунофиқонро надидӣ, ки пайваста ба бародарони аҳли китобашон, ки куфр варзидаанд, мегӯянд: «Агар шуморо [аз сарзаминатон] берун кунанд, мо [низ] бо шумо берун хоҳем омад ва ҳаргиз [сухани] касеро дар мавриди шумо итоат нахоҳем кард ва агар бо шумо ҷанг шавад, албатта ёритон хоҳем кард?». Аллоҳ таоло гувоҳӣ медиҳад, ки онҳо дурӯғгӯ ҳастанд');
INSERT INTO "pioneers" VALUES(59,12,'59:12','Агар онҳо [яҳудиён]-ро [аз ватанашон] берун кунанд, эшон ҳамроҳи онон берун намераванд ва агар бо онҳо ҷанг шавад, ёриашон нахоҳанд кард ва агар [ҳам] ёриашон кунанд, албатта, пушт [ба майдон] карда, фирор мекунанд ва дигар ёрӣ намешаванд');
INSERT INTO "pioneers" VALUES(59,13,'59:13','[Эй муъминон] Шумо, ҳатман, дар дилҳои онон беш аз Аллоҳ таоло мояи ҳаросед, зеро онон мардумоне ҳастанд, ки намефаҳманд');
INSERT INTO "pioneers" VALUES(59,14,'59:14','Онҳо [яҳудиён] ҳаргиз дастаҷамъӣ бо шумо намеҷанганд, магар дар рустоҳо [ва дижҳо]-и муҳкам ё аз пушти деворҳо. Ҷангашон дар миёни худашон сахт аст. Ту онҳоро муттаҳид мепиндорӣ, дар холе ки дилҳояшон пароканда аст, зеро онҳо қавме ҳастанд, ки намеандешанд');
INSERT INTO "pioneers" VALUES(59,15,'59:15','[Достони ин яҳудиён] Монанди [достони] касонест, ки андаке пеш аз онон буданд [ва дар воқеаи Бадр] сазои кор [-и бад]-и худро чашиданд ва барои эшон азоби дардноке [дар пеш] аст');
INSERT INTO "pioneers" VALUES(59,16,'59:16','Ҳамчун [достони] шайтон аст, ҳангоме ки ба инсон гуфт: «Кофир шав»; пас, чун кофир шуд, [ба ӯ] гуфт: «Ман аз ту безорам, [чаро ки] ман аз Аллоҳ таоло – Парвардигори ҷаҳониён – метарсам»');
INSERT INTO "pioneers" VALUES(59,17,'59:17','Пас, саранҷом [-и кор]-и онҳо ин шуд, ки онҳо ҳар ду дар оташи [дузах] хоҳанд буд. Ҷовидона дар он мемонанд ва ин аст кайфари ситамгорон.');
INSERT INTO "pioneers" VALUES(59,18,'59:18','Эй касоне, ки имон овардаед, аз Аллоҳ таоло парво кунед ва ҳар кас бояд бингарад, ки барои фардо чи пеш фиристодааст ва аз Аллоҳ таоло битарсед. Бе гумон, Аллоҳ таоло ба он чи анҷом медиҳед, огоҳ аст');
INSERT INTO "pioneers" VALUES(59,19,'59:19','Ва ҳамчун касоне набошед, ки Аллоҳро фаромӯш карданд, пас, Аллоҳ таоло [низ] ононро дучори худфаромӯшӣ сохт. Онҳо [бадкору] нофармонанд');
INSERT INTO "pioneers" VALUES(59,20,'59:20','Ҳаргиз аҳли ҷаҳаннам ва аҳли биҳишт яксон нестанд. Аҳли биҳишт растагоранд');
INSERT INTO "pioneers" VALUES(59,21,'59:21','Агар ин Қуръонро бар кӯҳе нозил мекардем, яқинан, онро аз тарси Аллоҳ таоло хокистар ва аз ҳам пошида медидӣ; ва ин масалҳоро барои мардум мезанем, бошад, ки бияндешанд.');
INSERT INTO "pioneers" VALUES(59,22,'59:22','Ӯ Аллоҳ таоло аст, ки ҷуз Ӯ маъбуде [ростин] нест; донои ғайб ва ошкор аст; Ӯ бахшандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(59,23,'59:23','Ӯ Аллоҳест, ки ҷуз Ӯ маъбуде [баҳақ] нест; [Ӯст] фармонраво, муназзаҳ, беайб ва нуқс, тасдиқкунандаи паёмбаронаш, муроқиб [-и аъмоли бандагонаш], қудратманди шикастнопазир, шукӯҳманду шикастдиҳандаи мутлақ, воломақому шоистаи азамат. Аллоҳ таоло аз он чи [барои Ӯ] шарик меоваранд, поку муназзаҳ аст');
INSERT INTO "pioneers" VALUES(59,24,'59:24','Ӯ Аллоҳ таоло аст: холиқи ҳастӣ, офаридгори он аз нестӣ [ва] шаклдиҳандаи махлуқот; номҳои нек барои Ӯст. Он чи дар осмонҳо ва замин аст, тасбеҳи Ӯ мегӯянд ва Ӯ қудратманди шикастнопазир [ва] ҳаким аст.');
INSERT INTO "pioneers" VALUES(60,1,'60:1','Эй касоне, ки имон овардаед, агар барои ҷиҳод дар роҳи Ман ва талаби хушнудии Ман аз [ватани худ] берун омадаед, душмани Ману душмани худатонро дӯст магиред, ки ба онҳо меҳрубонӣ кунӣ; ва ҳол он ки онон ба дини ҳаққе, ки барои шумо омадааст, куфру инкор варзидаанд ва паёмбар ва шуморо овора кардаанд; чаро ки ба Аллоҳ таоло – Парвардигоратон – имон доред. Шумо пинҳонӣ бо онҳо пайванди дӯстӣ барқарор мекунед, дар ҳоле ки Ман ба он чи ки пинҳон медоред ва он чи ки ошкор месозед, донотарам; ва ҳар кас аз шумо, ки чунин [коре] кунад, яқинан роҳи ростро гум кардааст.');
INSERT INTO "pioneers" VALUES(60,2,'60:2','Агар онҳо бар шумо даст ёбанд, душманонатон хоҳанд буд ва ба озоратон дасту забон мекушоянд ва дӯст доранд, ки шумо [низ] кофир гардед');
INSERT INTO "pioneers" VALUES(60,3,'60:3','Рӯзи қиёмат ҳаргиз хешовандонатон ва фарзандонатон ба ҳолатон суде нахоҳанд дошт. Аллоҳ таоло дар миёни шумо ҷудоӣ меандозад ва Аллоҳ таоло ба он чи анҷом медиҳед, биност');
INSERT INTO "pioneers" VALUES(60,4,'60:4','Яқинан барои шумо дар [зиндагии] Иброҳим ва касоне, ки бо ӯ буданд, сармашқи хубе вуҷуд дорад; он гоҳ ки ба қавми худ гуфтанд: «Мо аз шумо ва аз он чи ғайр аз Аллоҳ таоло мепарастед, безорем; ба шумо кофир шудаем ва миёни мо ва шумо душманӣ ва кинаи ҳамешагӣ падид омадааст, то вақте ки ба Аллоҳи ягона имон оваред», магар он сухани Иброҳим, ки ба падараш [Озар] гуфт: «Ман бароят омурзиш талаб мекунам ва дар баробари Аллоҳ таоло барои ту ихтиёри чизеро надорам». Парвардигоро, бар Ту таваккал кардем ва ба сӯйи Ту рӯй овардем ва бозгашт [-и ҳама] ба сӯи Туст.');
INSERT INTO "pioneers" VALUES(60,5,'60:5','Парвардигоро, моро дастхуши [фитнаи] кофирон қарор мадеҳ ва моро биёмурз эй Парвардигори мо; ҳамоно Ту пирӯзманди ҳакимӣ»');
INSERT INTO "pioneers" VALUES(60,6,'60:6','Мусалламан барои шумо дар [зиндагии] онҳо [Иброҳим ва ёронаш] сармашқи хубе аст, барои касоне, ки умед ба Аллоҳ таоло ва рӯзи қиёмат доранд; ва ҳар кас рӯйгардон шавад, [бидонад, ки] Аллоҳ таоло бениёзу сутуда аст');
INSERT INTO "pioneers" VALUES(60,7,'60:7','Чи басо Аллоҳ таоло миёни шумо ва касоне [аз мушрикин], ки бо онҳо душманӣ доштаед, [пайванди] дӯстӣ [ва муҳаббат] барқарор кунад; ва Аллоҳ таоло тавоност; ва Аллоҳ таоло омурзандаи меҳрубон аст.');
INSERT INTO "pioneers" VALUES(60,8,'60:8','Аллоҳ таоло шуморо аз некӣ кардан ва риояти адолат нисбат ба касоне, ки дар [амри] дин бо шумо наҷангидаанд ва шуморо аз диёратон берун накардаанд, наҳй намекунад. Бе гумон, Аллоҳ таоло адолатпешагонро дӯст медорад');
INSERT INTO "pioneers" VALUES(60,9,'60:9','[Ӯ] Танҳо шуморо аз дӯстӣ бо касоне наҳй мекунад, ки дар [амри] дин бо шумо ҷангидаанд ва шуморо аз диёратон берун кардаанд ва бар берун рондани шумо [ба дигарон] кумак кардаанд; ва ҳар кас бо онон дӯстӣ кунад, ононанд, ки ситамгоронанд.');
INSERT INTO "pioneers" VALUES(60,10,'60:10','Эй касоне, ки имон овардаед, ҳангоме ки занони муъмини муҳоҷир ба назди шумо меоянд, онҳоро биозмоед; Аллоҳ таоло ба имонашон донотар аст. Пас, агар онҳоро [занони] муъмин ёфтед, эшонро ба сӯйи кофирон бознагардонед. На он занон бар эшон [кофирон] ҳалоланд ва на он [мардон] бар ин занони ҳалол; ва ҳар чи [шавҳарони кофир] харҷи [ин занон] кардаанд, ба онҳо бидиҳед; ва бар шумо гуноҳе нест, ки пас аз он ки маҳрияҳояшонро пардохтед, бо онон издивоҷ кунед; ва ба [нигаҳ доштани пайванди заношӯӣ бо] занони кофир дил набандед; [ва чун занони шумо назди онон раванд, раҳояшон кунед] ва он чиро харҷ кардаед, [аз эшон] мутолиба кунед; ва [кофирон низ] он чиро харҷ кардаанд, мутолиба кунанд. Ин ҳукми Аллоҳ таоло аст, ки дар миёни шумо доварӣ мекунад; ва Аллоҳ таоло донои ҳаким аст');
INSERT INTO "pioneers" VALUES(60,11,'60:11','Агар яке аз ҳамсарони шумо ба сӯйи кофирон рафт, он гоҳ шумо [дар ҷанг бар онон пирӯз шудед ва] ба интиқом аз кофирон бархостед, пас, ба касоне, ки ҳамсаронашон [ба сӯйи куффор] рафтаанд, ҳаммонанди он чиро, ки харҷ кардаанд, [аз ғаноим] бипардозед; ва аз Аллоҳе, ки шумо ба Ӯ имон доред, парво кунед');
INSERT INTO "pioneers" VALUES(60,12,'60:12','Эй паёмбар, ҳангоме ки занони муъмин назди ту омаданд, то бо ту байъат кунанд, ки чизеро бо Аллоҳ таоло шарик насозанд ва дуздӣ накунанд ва муртакиби зино нашаванд ва фарзандони худро накушанд ва фарзандеро ба дурӯғ ба шавҳаронашон нисбат надиҳанд ва дар корҳои нек аз ту нофармонӣ накунанд, бо онон байъат кун ва аз Аллоҳ таоло барояшон омурзиш бихоҳ. Бе гумон, Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(60,13,'60:13','Эй касоне, ки имон овардаед, бо қавме, ки Аллоҳ таоло бар онон хашм [-у ғазаб] гирифтааст, дӯстӣ накунед. Онҳо аз охират маъюсанд, ҳамон гуна ки кофирон аз гӯрхуфтагон [мурдагони дар гӯристон дафншуда] маъюсанд');
INSERT INTO "pioneers" VALUES(61,1,'61:1','Он чи дар осмонҳо ва он чи дар замин аст, тасбеҳи Аллоҳ таоло мегӯянд ва Ӯ пирӯзманди ҳаким аст');
INSERT INTO "pioneers" VALUES(61,2,'61:2','Эй касоне, ки имон овардаед, чаро чизеро мегӯед, ки [ба он] амал намекунед?');
INSERT INTO "pioneers" VALUES(61,3,'61:3','Назди Аллоҳ таоло сахт нописанд [ва муҷиби хашм] аст, ки чизеро бигӯед, ки [ба он] амал намекунед.');
INSERT INTO "pioneers" VALUES(61,4,'61:4','Бе гумон, Аллоҳ таоло касонеро, ки чун бинои оҳанин саф баста [ва] дар роҳи Ӯ пайкор мекунанд, дӯст медорад');
INSERT INTO "pioneers" VALUES(61,5,'61:5','Ва [эй паёмбар, ба ёд овар] ҳангомеро, ки Мӯсо ба қавмаш гуфт: «Эй қавми ман, чаро маро озор [ва азият] мерасонед, дар ҳоле ки медонед, бе гумон, ман фиристодаи Аллоҳ таоло ба сӯи шумо ҳастам?». Пас, чун онон [аз ҳақ] мунҳариф шуданд, Аллоҳ таоло дилҳояшонро мунҳариф сохт; ва Аллоҳ таоло қавми нофармонро ҳидоят намекунад.');
INSERT INTO "pioneers" VALUES(61,6,'61:6','Ва [ёдовар бош] ҳангоме ки Исо писари Марям гуфт: «Эй Бани Исроил, бе гумон, ман фиристодаи Аллоҳ таоло ба сӯйи шумо ҳастам. Он чи аз Таврот, ки пеш аз ман буда тасдиқ мекунам ва [низ] муждадиҳанда ба расуле ҳастам, ки баъд аз ман меояд ва номаш «Аҳмад» аст». Пас ҳангоме ки ӯ [Аҳмад] бо муъҷизаҳо [ва далоили равшан] ба сӯи онон омад, гуфтанд: «Ин ҷодуи ошкор аст».');
INSERT INTO "pioneers" VALUES(61,7,'61:7','Чи касе ситамгортар аз он касе аст, ки бар Аллоҳ таоло дурӯғ бубандад, дар ҳоле ки ба [дини] ислом даъват мешавад? Ва Аллоҳ таоло гурӯҳи ситамгоронро ҳидоят намекунад.');
INSERT INTO "pioneers" VALUES(61,8,'61:8','Онҳо мехоҳанд нури Аллоҳро бо даҳони худ хомӯш кунанд, вале Аллоҳ таоло комилкунандаи нури хеш аст; ҳарчанд кофирон хуш надошта бошанд.');
INSERT INTO "pioneers" VALUES(61,9,'61:9','Ӯст, ки паёмбарашро бо ҳидояту дини ҳақ фиристодааст, то онро бар ҳамаи адён пирӯз гардонад, ҳарчанд мушрикон нохушнуд бошанд');
INSERT INTO "pioneers" VALUES(61,10,'61:10','Эй касоне, ки имон овардаед, оё [мехоҳед] шуморо ба тиҷорате роҳнамоӣ кунам, ки шуморо аз азоби дарднок наҷот диҳад?');
INSERT INTO "pioneers" VALUES(61,11,'61:11','Ба Аллоҳ таоло ва паёмбараш имон биёваред ва бо амвол ва ҷонҳоятон дар роҳи Аллоҳ таоло ҷиҳод кунед. Агар бидонед, ин [кор] барои шумо беҳтар аст.');
INSERT INTO "pioneers" VALUES(61,12,'61:12','[Агар чунин кардед, Аллоҳ таоло] гуноҳонатонро мебахшад ва шуморо дар боғҳое [аз биҳишт] ворид мекунад, ки ҷӯйборон аз зери [дарахтони] он ҷорӣ аст ва дар хонаҳои покиза дар биҳишт ҷовидон [ҷой медиҳад]. Ин комёбии бузурге аст.');
INSERT INTO "pioneers" VALUES(61,13,'61:13','Ва [неъмати] дигаре, ки онро дӯст доред, [низ ба шумо иноят мефармояд, ки ҳамоно] ёрӣ аз сӯйи Аллоҳ таоло ва пирӯзии наздик аст; ва муъминонро [ба ин пирӯзӣ] башорат деҳ.');
INSERT INTO "pioneers" VALUES(61,14,'61:14','Эй касоне, ки имон овардаед, ёварони [дини] Аллоҳ таоло бошед; ҳамон гуна ки Исо – писари Марям – ба ҳавориюн гуфт: «Чи касоне ёварони ман [дар даъват] ба сӯи Аллоҳ таоло ҳастанд?». Ҳавориюн гуфтанд: «Мо ёварони [дини] Аллоҳ таоло ҳастем». Пас, гурӯҳе аз Бани Исроил имон оварданд ва гурӯҳе кофир шуданд. Он гоҳ мо касонеро, ки имон оварда буданд, бар душманонашон неру [ва тавоноӣ] додем, ва саранҷом пирӯз шуданд');
INSERT INTO "pioneers" VALUES(62,1,'62:1','Он чи дар осмонҳо ва он чи дар замин аст, тасбеҳи Аллоҳ таоло мегӯянд; [Аллоҳ таоло] ки фармонраво, муназзаҳ [ва] пирӯзманду ҳаким аст');
INSERT INTO "pioneers" VALUES(62,2,'62:2','Ӯст, ки аз миёни бесаводон паёмбаре аз миёни худашон барангехт, ки оятҳояшро бар онҳо бихонад ва эшонро пок гардонад ва ба онон Китоб [Қуръон] ва ҳикмат биёмӯзад; агарчи пеш аз ин дар гумроҳии ошкор буданд');
INSERT INTO "pioneers" VALUES(62,3,'62:3','Ва [ин паёмбар] бар гурӯҳи дигаре аз онон [низ мабъус шудааст], ки ҳанӯз ба онҳо [наслҳои пешин] напайвастаанд; ва Ӯ шикастнопазири ҳаким аст');
INSERT INTO "pioneers" VALUES(62,4,'62:4','Ин фазли Аллоҳ таоло аст, ки онро ба ҳар кас, ки бихоҳад, мебахшад; ва Аллоҳ таоло дорои фазли азим аст');
INSERT INTO "pioneers" VALUES(62,5,'62:5','Масали касоне, ки [амал ба] Таврот ба онон таклиф шуд, вале онро [чунончи бояду шояд] риоя накарданд, ҳамчун харест, ки китобҳоеро ҳамл мекунад [аммо аз он чизе намефаҳмад]. Чи бад аст масали гурӯҳе, ки оёти илоҳиро такзиб карданд! Ва Аллоҳ таоло гурӯҳи ситамгоронро ҳидоят намекунад');
INSERT INTO "pioneers" VALUES(62,6,'62:6','Бигӯ: «Эй яҳудиён, агар гумон доред, ки [танҳо] шумо дӯстони Аллоҳ таоло ҳастед, на мардуми дигар, пас, агар рост мегӯед, орзуи марг кунед»');
INSERT INTO "pioneers" VALUES(62,7,'62:7','Вале онҳо ба хотири он чи аз пеш фиристодаанд, ҳаргиз он орзуро нахоҳанд кард; ва Аллоҳ таоло [аз ҳоли] ситамгорон огоҳ аст');
INSERT INTO "pioneers" VALUES(62,8,'62:8','[Эй Паёмбар] Бигӯ: «Он марге, ки аз он фирор мекунед, яқинан, ба шумо хоҳад расид, сипас ба сӯйи [Аллоҳ таоло он] донои ғайб ва ошкор бозгардонда мешавед; он гоҳ шуморо аз он чи анҷом медодед, огоҳ месозад»');
INSERT INTO "pioneers" VALUES(62,9,'62:9','Эй касоне, ки имон овардаед, ҳангоме ки дар рӯзи ҷумъа барои намоз азон гуфта шавад, ба сӯйи [намозу] зикри Аллоҳ таоло бишитобед ва хариду фурӯшро раҳо кунед. Агар бидонед, ин барои шумо беҳтар аст');
INSERT INTO "pioneers" VALUES(62,10,'62:10','Пас, ҳангоме ки намоз поён ёфт, [барои касби ризқу рӯзӣ] дар замин пароканда шавед ва аз фазли Аллоҳ таоло талаб кунед; ва Аллоҳро бисёр ёд кунед, то растагор шавед');
INSERT INTO "pioneers" VALUES(62,11,'62:11','[Эй Паёмбар, бархе аз ин мардум] Ҳангоме ки тиҷорат ё саргармие бубинанд, [аз атрофи ту] ба сӯйи он пароканда мешаванд ва туро истода [бар минбар] раҳо мекунанд. Бигӯ: «Он чи дар назди Аллоҳ таоло аст, беҳтар аз саргармӣ ва тиҷорат аст; ва Аллоҳ таоло беҳтарин рӯзидиҳанда аст»');
INSERT INTO "pioneers" VALUES(63,1,'63:1','Ҳангоме ки мунофиқон назди ту оянд, [савганд мехӯранд ва] мегӯянд: «Мо гувоҳӣ медиҳем, ки, яқинан, ту фиристодаи Аллоҳ таоло ҳастӣ»; ва Аллоҳ таоло медонад, ки бе гумон, ту фиристодаи Ӯ ҳастӣ ва Аллоҳ таоло гувоҳӣ медиҳад, ки мунофиқон, яқинан, дурӯғгӯ ҳастанд');
INSERT INTO "pioneers" VALUES(63,2,'63:2','Онҳо савгандҳои худро сипар қарор доданд, пас, [мардумро] аз роҳи Аллоҳ таоло боздоштанд. Ростӣ, ки онон чи бад мекунанд!');
INSERT INTO "pioneers" VALUES(63,3,'63:3','Ин бад-он хотир аст, ки онҳо имон оварданд, сипас кофир шуданд, он гоҳ бар дилҳояшон муҳр ниҳода шуда, пас, дарнамеёбанд');
INSERT INTO "pioneers" VALUES(63,4,'63:4','Ва ҳангоме ки онҳоро бубинӣ, ҷисм [ва чеҳра]-ашон туро ба шигифт оварад ва агар [сухан] гӯянд, [ба хотири фасоҳати каломашон] ба суханонашон гӯш медиҳӣ. Гӯё, онҳо чӯбҳои такядода ба деворанд, ҳар бонгеро алайҳи худ мепиндоранд. Онон душман [-и ҳақиқӣ] ҳастанд; пас, аз онон бар ҳазар бош. Аллоҳ таоло онҳоро бикушад! Чи гуна [аз ҳақ] мунҳариф мешаванд?');
INSERT INTO "pioneers" VALUES(63,5,'63:5','Ва ҳангоме ки ба онҳо гуфта шавад: «Биёед, то расулуллоҳ барои шумо омурзиш бихоҳад», сарҳои худро такон медиҳанд ва онҳоро мебинӣ, ки [аз суханони ту] рӯйгардон мешаванд ва такаббур меварзанд');
INSERT INTO "pioneers" VALUES(63,6,'63:6','Барои онҳо яксон аст, ки барояшон омурзиш бихоҳӣ ё нахоҳӣ; Аллоҳ таоло ҳаргиз ононро намебахшад. Бе гумон, Аллоҳ таоло қавми нофармонро ҳидоят намекунад');
INSERT INTO "pioneers" VALUES(63,7,'63:7','Онҳо касоне ҳастанд, ки мегӯянд: «Ба онон, ки назди Расулуллоҳ ҳастанд, [чизе] инфоқ накунед, то [аз гирди ӯ] пароканда шаванд; ҳоло онки ганҷинаҳои осмон ва замин аз они Аллоҳ таоло аст, вале мунофиқон дарнамеёбанд');
INSERT INTO "pioneers" VALUES(63,8,'63:8','Онҳо мегӯянд: «Агар ба Мадина бозгардем, яқинан соҳибони иззат фурумоягонро аз он ҷо берун мекунанд»; дар ҳоле ки иззат аз они Аллоҳ таоло ва фиристодаи Ӯ ва муъминон аст; вале мунофиқон намедонанд');
INSERT INTO "pioneers" VALUES(63,9,'63:9','Эй касоне, ки имон овардаед, амволу фарзандонатон шуморо аз ёди Аллоҳ таоло ғофил накунад; ва ҳар кас, ки чунин кунад, онон зиёнкоранд');
INSERT INTO "pioneers" VALUES(63,10,'63:10','Ва аз он чи ба шумо рӯзӣ додаем, [дар роҳи Аллоҳ таоло] инфоқ кунед, пеш аз он ки марг ба суроғи яке аз шумо ояд ва бигӯяд: «Парвардигоро, эй кош [марги] маро муддати каме ба таъхир меандохтӣ, то [дар роҳи ту] садақа диҳам ва аз накукорон бошам»');
INSERT INTO "pioneers" VALUES(63,11,'63:11','Ва[-ле] ҳар кас аҷалаш фаро расад, ҳаргиз Аллоҳ таоло [онро] ба таъхир намеандозад, ва Аллоҳ таоло аз он чи мекунед, огоҳ аст');
INSERT INTO "pioneers" VALUES(64,1,'64:1','Он чи дар осмонҳо ва он чи дар замин аст, ҳама тасбеҳи Аллоҳ таоло мегӯянд. Фармонравоӣ аз они Ӯст ва сипос [-у ситоиш низ] аз они Ӯст ва Ӯ бар ҳама чиз тавоност');
INSERT INTO "pioneers" VALUES(64,2,'64:2','Ӯст, ки шуморо офарид; пас, [гурӯҳе] аз шумо кофиранд ва [гурӯҳе] аз шумо муъмин ҳастанд; ва Аллоҳ таоло аз он чи мекунед, огоҳ аст');
INSERT INTO "pioneers" VALUES(64,3,'64:3','Осмонҳо ва заминро ба ҳақ офарид ва шуморо [дар раҳими модаронатон] шаклу сурат бахшид. Пас, шаклу сурататонро наку [ва зебо] гардонд, ва бозгашт [-и ҳама] ба сӯйи Ӯст');
INSERT INTO "pioneers" VALUES(64,4,'64:4','Он чиро, ки дар осмонҳо ва замин аст медонад ва он чиро пинҳон медоред ва он чиро ки ошкор мекунед, [низ] медонад; ва Аллоҳ таоло аз он чи дар синаҳост, огоҳ аст');
INSERT INTO "pioneers" VALUES(64,5,'64:5','Оё хабари касонеро, ки пеш аз ин кофир шуданд, ба шумо нарасидааст? Онҳо [таъми] кайфари кори худро чашиданд ва азоби дардноке хоҳанд дошт');
INSERT INTO "pioneers" VALUES(64,6,'64:6','Ин бад-он сабаб аст, ки паёмбаронашон бо муъҷизот [ва далоили равшан] ба сӯйи онон омаданд, пас, [онҳо аз рӯи такаббур] гуфтанд: «Оё башаре [монанди мо] мехоҳад ҳидоятамон кунад?». Он гоҳ кофир шуданд ва рӯй гардонданд ва Аллоҳ таоло [аз онон ва имонашон] бениёз буд; ва Аллоҳ таоло бениёзи сутуда аст');
INSERT INTO "pioneers" VALUES(64,7,'64:7','Касоне, ки кофир шуданд, гумон бурданд, ки ҳаргиз барангехта нахоҳанд шуд. Бигӯ: «Оре; ба Парвардигорам савганд, яқинан [ҳама] барангехта хоҳед шуд; он гоҳ аз он чи мекардед, ба шумо хабар хоҳанд дод ва ин [кор] бар Аллоҳ таоло осон аст»');
INSERT INTO "pioneers" VALUES(64,8,'64:8','Пас, ба Аллоҳ таоло ва Паёмбараш ва нуре, ки нозил кардаем, имон биёваред; ва Аллоҳ таоло аз он чи мекунед, огоҳ аст.');
INSERT INTO "pioneers" VALUES(64,9,'64:9','Рӯзе, ки ҳамаи шуморо [дар маҳшар] гирд меоварад. Он рӯз рӯзи зиёнкоре аст ва ҳар кас ба Аллоҳ таоло имон оварад ва корҳои шоиста анҷом диҳад, гуноҳони ӯро мебахшад ва ӯро ба боғҳое [аз биҳишт] ворид мекунад, ки ҷӯйборҳо зери [дарахтони] он ҷорӣ аст; ҷовидона дар он мемонанд. Ин комёбии бузург аст.');
INSERT INTO "pioneers" VALUES(64,10,'64:10','Ва касоне, ки кофир шуданд ва оёти Моро такзиб карданд, онон аҳли оташ [-и дузах]-анд ва ҷовидона дар он мемонанд; ва чи бад [саранҷом ва] ҷойгоҳе аст!');
INSERT INTO "pioneers" VALUES(64,11,'64:11','Ҳеҷ мусибате [ба инсон] намерасад, магар ба ҳукм [-у фармони] Аллоҳ таоло; ва ҳар кас ба Аллоҳ таоло имон оварад, [Аллоҳ таоло] қалбашро ҳидоят мекунад; ва Аллоҳ таоло ба ҳама чиз доност.');
INSERT INTO "pioneers" VALUES(64,12,'64:12','Аз Аллоҳ таоло итоат кунед ва расулро [низ] итоат кунед. Пас, агар рӯй бигардонед, [бидонед, ки] фиристодаи Мо вазифае ҷуз иблоғи ошкор надорад.');
INSERT INTO "pioneers" VALUES(64,13,'64:13','Аллоҳ таоло аст, ки ҳеҷ маъбуде [ба ҳақ] ҷуз Ӯ нест; пас муъминон бояд бар Аллоҳ таоло таваккал кунанд.');
INSERT INTO "pioneers" VALUES(64,14,'64:14','Эй касоне, ки имон овардаед, ба ростӣ, ки [баъзе] аз ҳамсаронатон ва фарзандонатон душманони шумо ҳастанд; пас, аз онҳо барҳазар бошед; ва агар афв кунед ва чашм бипӯшед ва бибахшед, бе гумон, Аллоҳ таоло омурзандаи меҳрубон аст.');
INSERT INTO "pioneers" VALUES(64,15,'64:15','Ҷуз ин нест, ки амволу фарзандонатон василаи омурзиши [шумо] ҳастанд; ва Аллоҳ таоло аст, ки подоши бузург назди Ӯст.');
INSERT INTO "pioneers" VALUES(64,16,'64:16','Пас то он ҷо ки метавонед, аз Аллоҳ таоло парво кунед ва [ҳақро] бишнавед ва фармон баред ва инфоқ кунед, ки барои худи шумо беҳтар аст; ва касоне, ки аз бухлу ҳирси нафси хеш дар амон бимонанд, ононанд, ки растагоранд.');
INSERT INTO "pioneers" VALUES(64,17,'64:17','Агар ба Аллоҳ таоло қарзи наку диҳед, онро барои шумо афзун мекунад ва шуморо меомурзад; ва Аллоҳ таоло қадршиноси бурдбор аст.');
INSERT INTO "pioneers" VALUES(64,18,'64:18','[Ӯ] Донои ниҳону ошкор [ва] бузургу баландмартаба аст');
INSERT INTO "pioneers" VALUES(65,1,'65:1','Эй Паёмбар, ҳаргоҳ хостед занонро талоқ диҳед, пас, онҳоро дар замони [оғози] идда талоқ диҳед ва ҳисоби иддаро нигаҳ доред ва аз Аллоҳ таоло, ки Парвардигори шумост, парво кунед. На шумо онҳоро аз хонаҳояшон берун кунед ва на онҳо берун раванд; магар он ки кори зишти ошкоре муртакиб шаванд; ва ин ҳудуди [аҳкоми] Аллоҳ таоло аст ва ҳар кас аз ҳудуди Аллоҳ таоло таҷовуз кунад, мусалламан, ба худ ситам кардааст. Ту намедонӣ, шояд Аллоҳ таоло пас аз он, вазъи тозае фароҳам оварад [ки ба сулҳу оштӣ мунҷар шавад]');
INSERT INTO "pioneers" VALUES(65,2,'65:2','Пас, чун [онҳоро талоқ додед ва] иддаашон ба сар расид, эшонро ба тарзи шоистае нигаҳ доред ё ба тарзи шоистае аз онҳо ҷудо шавед; ва ду марди одил аз худатонро гувоҳ бигиред ва шаҳодатро барои Аллоҳ таоло барпо доред. Ин [ҳукме аст], ки ҳар кас ба Аллоҳ таоло ва рӯзи қиёмат имон дорад, ба он панду андарз дода мешавад; ва ҳар кас, ки аз Аллоҳ таоло битарсад, роҳи наҷоте барои ӯ қарор медиҳад');
INSERT INTO "pioneers" VALUES(65,3,'65:3','Ва ӯро аз ҷойе, ки гумон надорад, рӯзӣ медиҳад; ва ҳар кас бар Аллоҳ таоло таваккал кунад, пас, ҳамон ӯро кофӣ аст. Бе гумон, Аллоҳ таоло фармони худро ба анҷом мерасонад. Мусалламан, Аллоҳ таоло барои ҳар чизе андозае қарор додааст.');
INSERT INTO "pioneers" VALUES(65,4,'65:4','Ва касоне аз занонатон, ки аз одати моҳона маъюс шудаанд, агар [ба бордории онон] шак кардед, иддаи онон се моҳ аст ва [низ] онҳо, ки ҳайз нашудаанд ва иддаи занони бордор ин аст, ки вазъи ҳамл кунанд ва ҳар кас аз Аллоҳ таоло битарсад корашро барояш осон месозад');
INSERT INTO "pioneers" VALUES(65,5,'65:5','Ин фармони Аллоҳ таоло аст, ки бар шумо нозил кардааст; ва ҳар кас аз Аллоҳ таоло парво кунад, Аллоҳ таоло гуноҳҳояшро аз ӯ мезудояд [ва мебахшад] ва подоши ӯро бузург мегардонад');
INSERT INTO "pioneers" VALUES(65,6,'65:6','Онон [занони муталлақа]-ро дар ҳадди тавони худ ҳар ҷо ки худатон сукунат доред, [ва дар тавонии шумост] сукунат диҳед ва ба онҳо зиён нарасонед, то [арсаро] бар онон танг кунед [ва пеш аз поёни идда маҷбур ба тарки манзил шаванд] ва агар бордор ҳастанд, нафақаи онҳоро бидиҳед, то вазъи ҳамл кунанд. Пас, агар [фарзандатонро] барои шумо шир медиҳанд, муздашонро бипардозед ва [ин корро] ба некӣ ва бо машварати ҳамдигар анҷом диҳед; ва агар ба тавофуқ нарасидед, пас зани дигаре [ба дархости шавҳар] ӯро шир диҳад');
INSERT INTO "pioneers" VALUES(65,7,'65:7','Онон, ки доро [ва сарватманд] ҳастанд, бояд аз дороӣ [ва сарвати] худ инфоқ кунанд ва касе, ки тангдаст аст, бояд аз он чи ки Аллоҳ таоло ба ӯ дода инфоқ кунад. Аллоҳ таоло ҳеҷ касро ҷуз ба он андоза, ки ба ӯ [тавон] додааст, мукаллаф намекунад. Аллоҳ таоло ба зудӣ баъд аз сахтӣ [ва тангдастӣ] осонӣ [ва кушоиш] қарор медиҳад');
INSERT INTO "pioneers" VALUES(65,8,'65:8','Чи бисёр [аҳолии] шаҳрҳо, ки аз фармони Парвардигорашон ва [фармони] паёмбаронаш сарпечӣ карданд; пас, Мо ба шиддат аз онҳо ҳисоб кашидем ва ба азобе сахт [ва саҳмнок] азобашон кардем.');
INSERT INTO "pioneers" VALUES(65,9,'65:9','Пас онон кайфари кори худро чашиданд ва саранҷоми корашон зиён буд.');
INSERT INTO "pioneers" VALUES(65,10,'65:10','Аллоҳ таоло азоби сахте барояшон муҳайё кардааст. Пас, эй хирадмандоне, ки имон овардаед, аз Аллоҳ таоло парво кунед. Ҳамоно Аллоҳ таоло зикр [Қуръон]-ро ба сӯйи шумо нозил кардааст');
INSERT INTO "pioneers" VALUES(65,11,'65:11','[Ва низ] Паёмбаре [фиристода], ки оёти равшани Аллоҳро бар шумо мехонад, то касонеро, ки имон оварда ва корҳои шоиста анҷом додаанд, аз торикиҳо ба сӯйи нур берун оварад; ва ҳар кас ба Аллоҳ таоло имон оварад ва кори шоиста анҷом диҳад, [Аллоҳ таоло] ӯро ба боғҳое [аз биҳишт] ворид мекунад, ки аз зери [дарахтони] он ҷӯйборҳо ҷорӣ аст; ҷовидона дар он бимонанд. Ба ростӣ, Аллоҳ таоло ризқу рӯзиашро наку гардонидааст');
INSERT INTO "pioneers" VALUES(65,12,'65:12','Аллоҳ таоло аст, ки ҳафт осмонро офарид ва аз замин [низ] ҳаммонанди онҳоро [офарид] ва фармони [Аллоҳ таоло] пайваста дар миёни онҳо нозил мешавад, то бидонед, ки Аллоҳ таоло бар ҳар чиз тавоност ва ин ки илми Аллоҳ таоло бар ҳама чиз иҳота дорад');
INSERT INTO "pioneers" VALUES(66,1,'66:1','Эй Паёмбар, чаро чизеро, ки Аллоҳ таоло бар ту ҳалол кардааст, ба хотири хушнудии ҳамсаронат [бар худ] ҳаром мекунӣ? Ва [бидон, ки дар ҳама ҳол] Аллоҳ таоло омурзандаи меҳрубон аст.');
INSERT INTO "pioneers" VALUES(66,2,'66:2','Ба ростӣ, ки Аллоҳ таоло шикасти савгандҳоятонро [бо додани каффора] бар шумо раво доштааст; ва Аллоҳ таоло дӯстдори шумост ва Ӯ донои ҳаким аст.');
INSERT INTO "pioneers" VALUES(66,3,'66:3','Ва ҳангоме ки Паёмбар ба баъзе аз ҳамсаронаш суханонеро ба роз гуфт, пас, чун [он ҳамсар] онро [ба дигаре] хабар дод ва Аллоҳ таоло ӯ [Паёмбар]-ро бар он огоҳ кард, қисмате аз онро барои ӯ баён кард ва аз қисмате [дигар] худдорӣ намуд. Пас, ҳангоме ки [паёмбар] ӯро аз он хабар дод, [он зан] гуфт: «Чи касе инро ба ту хабар додааст?». [Паёмбар] Гуфт: «[Аллоҳи] донои огоҳ маро хабар додааст»');
INSERT INTO "pioneers" VALUES(66,4,'66:4','[Эй ҳамсарони паёмбар] агар шумо ду нафар ба даргоҳи Аллоҳ таоло тавба кунед [ба суди шумост]; бе гумон, дилҳоятон [аз ҳақ] мунҳариф гаштааст ва агар шумо ду нафар бар алайҳи ӯ ҳамдаст [муттафиқ] шавед, [бидонед] ки Аллоҳ таоло дӯсту ёвари ӯст ва [низ] Ҷабраил ва мардумони шоиста – аз муъминону фариштагон – [низ] баъд аз он пуштибон [мададгори ӯ] ҳастанд');
INSERT INTO "pioneers" VALUES(66,5,'66:5','Чи басо агар шуморо талоқ диҳад, Парвардигораш ҳамсароне беҳтар аз шумо барояш ҷойгузин кунад, [ки] дӯшизагон ё занони мусулмон, муъмин, фармонбардор, тавбакор, ибодатгар [ва] рӯзадор бошанд');
INSERT INTO "pioneers" VALUES(66,6,'66:6','Эй касоне, ки имон овардаед, худ ва хонаводаатонро аз оташе, ки ҳезуми он мардуму сангҳост нигаҳ доред; [оташе, ки] бар он фариштагони хашин ва сахтгир [гуморида шудаанд], ки ҳаргиз аз Аллоҳ таоло дар [мавриди] он чи ба онон фармон дода, нофармонӣ намекунанд ва ҳар чи фармон меёбанд, анҷом медиҳанд.');
INSERT INTO "pioneers" VALUES(66,7,'66:7','Эй касоне, ки кофир шудаед, имрӯз узрхоҳӣ накунед. Ҷуз ин нест, ки дар баробари он чи мекардед, ҷазо дода мешавед');
INSERT INTO "pioneers" VALUES(66,8,'66:8','Эй касоне, ки имон овардаед, ба сӯйи Аллоҳ таоло тавба кунед, тавбае холисона. Умед аст Парвардигоратон гуноҳонатонро аз шумо бизудояд ва шуморо ба боғҳое [аз биҳишт] ворид кунад, ки ҷӯйборҳо аз зери [дарахтони] он ҷорӣ аст. Дар рӯзе ки Аллоҳ таоло паёмбар ва касонеро ки ҳамроҳи ӯ эмон овардаанд хор намекунад. Нурашон пешопеши онон ба самти росташон дар ҳаракат аст, мегӯянд: «Парвардигоро, нури моро ба тамом [ва камол] бирасон ва моро биёмурз. Бе гумон, Ту бар ҳар чиз тавоноӣ».');
INSERT INTO "pioneers" VALUES(66,9,'66:9','Эй Паёмбар, бо кофирону мунофиқон ҷиҳод кун ва бар онҳо сахт бигир ва ҷойгоҳашон ҷаҳаннам аст; ва чи бад ҷойгоҳе аст!');
INSERT INTO "pioneers" VALUES(66,10,'66:10','Аллоҳ таоло барои касоне, ки кофир шудаанд, ҳамсари Нуҳ ва ҳамсари Лутро масал задааст, ки он ду дар никоҳи ду тан аз бандагони солеҳи Мо буданд, пас, ба он ду хиёнат карданд ва [он ду бандаи солеҳ] натавонистанд, чизе аз [азоби] Аллоҳро аз он ду [зан] дафъ кунанд ва [ба онҳо] гуфта шуд: «Ҳамроҳи воридшавандагони ба оташи [ҷаҳаннам шумо низ] ворид шавед»');
INSERT INTO "pioneers" VALUES(66,11,'66:11','Аллоҳ таоло барои касоне, ки имон овардаанд, ҳамсари Фиръавн [Осия]-ро масал задааст, ҳангоме ки гуфт: «Парвардигоро, хонае бароям назди худ дар биҳишт бисоз ва маро аз Фиръавн ва кирдораш наҷот бидеҳ ва маро аз қавми ситамгар раҳоӣ бахш»');
INSERT INTO "pioneers" VALUES(66,12,'66:12','Ва [низ] Марям – духтари Имрон – ро [масал задааст], ки шармгоҳи хешро [пок] нигаҳ дошт, пас, Мо аз рӯҳи худ дар он дамидем ва [ӯ] калимоти Парвардигораш ва китобҳои Ӯро тасдиқ кард ва аз фармонбардорон буд');
INSERT INTO "pioneers" VALUES(67,1,'67:1','Пурбаракату бузургвор аст касе, ки фармонравоии ҷаҳони ҳастӣ ба дасти Ӯст ва Ӯ бар ҳар чиз тавоност');
INSERT INTO "pioneers" VALUES(67,2,'67:2','[Ҳамон] Касе, ки маргу зиндагиро офарид, то шуморо биозмояд, ки кадом яке аз шумо накукортар аст; ва Ӯ пирӯзманду бахшанда аст');
INSERT INTO "pioneers" VALUES(67,3,'67:3','Он ки ҳафт осмонро бар фарози якдигар офарид. Дар офариниш [-и Аллоҳ таоло] ҳеҷ беназмӣ ва халале намебинӣ. Бори дигар нигоҳ кун; оё нуқсе мебинӣ?');
INSERT INTO "pioneers" VALUES(67,4,'67:4','Бори дигар [ба олами ҳастӣ] нигоҳ кун; [нуқсе нахоҳӣ дид ва] чашми ту, дар ҳоле ки хаста ва нотавон аст ба сӯйи ту бозмегардад');
INSERT INTO "pioneers" VALUES(67,5,'67:5','Ба ростӣ, Мо осмони дунёро бо чароғҳо оростем ва онҳоро барои рондани шаётин қарор додем ва барои онон азоби [оташи] сӯзон омода сохтем');
INSERT INTO "pioneers" VALUES(67,6,'67:6','Ва барои касоне, ки ба Парвардигорашон кофир шуданд, азоби ҷаҳаннам аст; ва чӣ бад ҷойгоҳе аст!');
INSERT INTO "pioneers" VALUES(67,7,'67:7','Ҳангоме ки дар он андохта шаванд, аз он наърае [ваҳшатнок] мешунаванд, дар ҳоле ки он меҷӯшад');
INSERT INTO "pioneers" VALUES(67,8,'67:8','Аз шиддати хашм наздик аст [ҷаҳаннам] пора-пора шавад. Ҳар гоҳ ки гурӯҳе дар он андохта мешаванд, нигаҳбононаш аз онон мепурсанд: «Оё бимдиҳандае ба суроғатон наомад?»');
INSERT INTO "pioneers" VALUES(67,9,'67:9','Мегӯянд: «Бале; бимдиҳанда ба сӯи мо омад, он гоҳ мо ӯро такзиб кардем ва гуфтем; «Ҳаргиз Аллоҳ таоло чизе нозил накардааст. Шумо ҷуз дар гумроҳии бузург нестед»');
INSERT INTO "pioneers" VALUES(67,10,'67:10','Ва [боз] мегӯянд: «Агар мо мешунидем ё тааққул мекардем, дар [зумраи] дузахиён набудем»');
INSERT INTO "pioneers" VALUES(67,11,'67:11','Пас, онҳо ба гуноҳи худ эътироф мекунанд. Пас, дурӣ [аз раҳмати Аллоҳ таоло] баҳраи дузахиён бод!');
INSERT INTO "pioneers" VALUES(67,12,'67:12','Бе гумон, касоне, ки дар ниҳон аз Парвардигорашон метарсанд, онон омурзишу подоши бузурге доранд');
INSERT INTO "pioneers" VALUES(67,13,'67:13','[Хоҳ] Гуфтори худро пинҳон доред ё онро ошкор созед, [ба ҳар ҳол] Ӯ ба он чӣ дар дилҳост, огоҳ аст');
INSERT INTO "pioneers" VALUES(67,14,'67:14','Оё касе, ки [ҳамаи мавҷудотро] офаридааст, [ҳоли бандагонро] намедонад? Дар ҳоле ки Ӯ борикбин ва огоҳ аст');
INSERT INTO "pioneers" VALUES(67,15,'67:15','Ӯ касе аст, ки заминро барои шумо ром гардонид, пас, дар гӯша ва канори он роҳ биравед ва аз рӯзии Ӯ бихӯред; ва бозгашт [-и ҳама] ба сӯйи Ӯст');
INSERT INTO "pioneers" VALUES(67,16,'67:16','Оё худро аз [азоби] касе, ки дар осмон аст, дар амон медонед, ки [фармон диҳад] замин шуморо фуру барад ва ногаҳон ба ларзиш дарояд?');
INSERT INTO "pioneers" VALUES(67,17,'67:17','Ё худро аз касе, ки дар осмон аст, дар амон медонед, ки тӯфони сахте бар шумо фиристад? Пас, ба зудӣ хоҳед донист, ки ҳушдори Ман чи гуна аст!');
INSERT INTO "pioneers" VALUES(67,18,'67:18','Ҳамоно касоне, ки пеш аз онон буданд, [низ даъвати паёмбаронро] такзиб карданд; пас, [бингар, ки] уқубати Ман чи гуна буд!');
INSERT INTO "pioneers" VALUES(67,19,'67:19','Оё ба парандагоне, ки бар фарози сарашон аст, нигоҳ накарданд, ки гоҳе болҳои худро мегушоянд ва гоҳе фуру мебанданд? Ҷуз [Аллоҳи] Раҳмон [касе] онҳоро [бар фарози осмон] нигоҳ намедорад; бе гумон, Ӯ ба ҳар чиз биност');
INSERT INTO "pioneers" VALUES(67,20,'67:20','Оё он касе, ки лашкари шумост, метавонад шуморо дар баробари Аллоҳ таоло ёрӣ диҳад? Кофирон ҷуз дар фиреб [ва ғурурашон] нестанд');
INSERT INTO "pioneers" VALUES(67,21,'67:21','Ё он касе, ки шуморо рӯзӣ медиҳад, агар рӯзиашро боздорад, [чи касе метавонад ниёзи шуморо таъмин кунад?]. Вале онҳо дар саркашӣ ва фирор аз ҳақиқат лаҷоҷат меварзанд');
INSERT INTO "pioneers" VALUES(67,22,'67:22','Оё касе, ки ба рӯ афтода [ва нагунсор] ҳаракат мекунад, ба ҳидоят наздиктар аст, ё он ки росту устувор бар роҳи рост гом бармедорад?');
INSERT INTO "pioneers" VALUES(67,23,'67:23','Бигӯ: «Ӯ касе аст, ки шуморо офарид ва бароятон гӯшу чашмон ва дил қарор дод; [аммо шумо] андаке сипосгузорӣ мекунед»');
INSERT INTO "pioneers" VALUES(67,24,'67:24','Бигӯ: «Ӯ касест шуморо дар замин пароканд ва ба сӯйи Ӯ ҳашр мешавед»');
INSERT INTO "pioneers" VALUES(67,25,'67:25','Ва мегӯянд: «Агар рост мегӯед, ин ваъда [-и қиёмат] чи замоне аст?»');
INSERT INTO "pioneers" VALUES(67,26,'67:26','Бигӯ: «Илм [-и қиёмат] танҳо назди Аллоҳ таоло аст ва ман фақат бимдиҳандаи ошкорам»');
INSERT INTO "pioneers" VALUES(67,27,'67:27','Пас, чун он [ваъда ва азоби илоҳӣ]-ро аз наздик бубинанд, чеҳраи касоне, ки кофир шудаанд, сиёҳу андӯҳгин мегардад ва [ба онон] гуфта мешавад: «Ин ҳамон чизе аст, ки дархост мекардед»');
INSERT INTO "pioneers" VALUES(67,28,'67:28','[Эй Паёмбар] Бигӯ: «Ба ман хабар диҳед, агар Аллоҳ таоло маро ва касонеро, ки бо ман ҳастанд, нобуд кунад, ё бар мо раҳмат оварад, пас, кист, ки кофиронро аз азоби дарднок паноҳ диҳад?»');
INSERT INTO "pioneers" VALUES(67,29,'67:29','Бигӯ: «Ӯ [Аллоҳи] Раҳмон аст? ба Ӯ имон овардаем ва бар Ӯ таваккал кардаем, пас, [эй кофирон] ба зудӣ хоҳед донист, ки чи касе дар гумроҳии ошкор аст»');
INSERT INTO "pioneers" VALUES(67,30,'67:30','[Эй паёмбар] Бигӯ: «Ба ман хабар диҳед, агар оби [ошомидании] шумо [дар замин] фуру равад, чи касе метавонад барои шумо оби равону гуворо] биёварад?»');
INSERT INTO "pioneers" VALUES(68,1,'68:1','Нун. Савганд ба қалам ва он чи менависанд');
INSERT INTO "pioneers" VALUES(68,2,'68:2','Ки ту [эй паёмбар] ба неъмат [ва фазли] Парвардигорат девона нестӣ');
INSERT INTO "pioneers" VALUES(68,3,'68:3','Ва барои ту подоше бепоён [дар пеш] аст');
INSERT INTO "pioneers" VALUES(68,4,'68:4','Ва яқинан ту ахлоқи бисёр наку ва волое дорӣ');
INSERT INTO "pioneers" VALUES(68,5,'68:5','Пас, ба зудӣ хоҳӣ дид ва онҳо [низ] хоҳанд дид,');
INSERT INTO "pioneers" VALUES(68,6,'68:6','ки кадом як аз шумо девона аст');
INSERT INTO "pioneers" VALUES(68,7,'68:7','Парвардигорат беҳтар медонад, чи касе аз роҳи Ӯ гумроҳ гашта ва [низ] Ӯ ба ҳидоятёфтагон донотар аст');
INSERT INTO "pioneers" VALUES(68,8,'68:8','Пас аз такзибкунандагон итоат макун');
INSERT INTO "pioneers" VALUES(68,9,'68:9','Онҳо дӯст медоранд [ва мехоҳанд], ки нармӣ [ва мадоро] кунӣ, то онон [низ] нармӣ [ва мадоро] кунанд');
INSERT INTO "pioneers" VALUES(68,10,'68:10','Ва аз ҳар фурумояе, ки бисёр савганд [-и дурӯғ] ёд мекунад, итоат макун');
INSERT INTO "pioneers" VALUES(68,11,'68:11','[Он ки] Бисёр айбҷуст ва ба суханчинӣ омадушуд мекунад');
INSERT INTO "pioneers" VALUES(68,12,'68:12','Ва бисёр монеи кори хайр ва мутаҷовизу гунаҳгор аст');
INSERT INTO "pioneers" VALUES(68,13,'68:13','Гузашта аз он, хашину бетабор ва бадном аст');
INSERT INTO "pioneers" VALUES(68,14,'68:14','Бад-он хотир, ки соҳиби молу фарзандони бисёр аст');
INSERT INTO "pioneers" VALUES(68,15,'68:15','Ҳангоме ки оёти Мо бар ӯ хонда шавад, мегӯяд: «[Инҳо] Афсонаҳои гузаштагон аст»');
INSERT INTO "pioneers" VALUES(68,16,'68:16','Ба зудӣ бар бинии ӯ доғ [-и нангу расвоӣ] мениҳем');
INSERT INTO "pioneers" VALUES(68,17,'68:17','Мо онҳоро озмоиш кардем, ҳамон гуна ки соҳибони боғро озмудем, ҳангоме ки савганд ёд карданд, ки субҳгоҳон [ки мустамандон набошанд] меваҳои боғро бичинанд');
INSERT INTO "pioneers" VALUES(68,18,'68:18','Ва [-ле] «иншоаллоҳ» нагуфтанд');
INSERT INTO "pioneers" VALUES(68,19,'68:19','Пас, азобе [фарогир] аз сӯйи Парвардигорат бар [боғи] онҳо фуруд омад, дар ҳоле ки ҳама дар хоб буданд');
INSERT INTO "pioneers" VALUES(68,20,'68:20','Пас, он боғ [-и сабзу хуррам пас аз оташсӯзӣ] ҳамчун шаби сиёҳ шуд');
INSERT INTO "pioneers" VALUES(68,21,'68:21','Он гоҳ субҳгоҳон якдигарро нидо доданд');
INSERT INTO "pioneers" VALUES(68,22,'68:22','Ки агар мехоҳед, дарав кунед, ба сӯйи киштзор [ва боғ]-и хеш биравед');
INSERT INTO "pioneers" VALUES(68,23,'68:23','Пас [ба сӯйи киштзор] ҳаракат карданд, дар ҳоле ки оҳиста бо ҳам мегуфтанд:');
INSERT INTO "pioneers" VALUES(68,24,'68:24','«[Муроқиб бошед] Ки имрӯз ҳеҷ [ниёзманд ва] бенавое дар он [боғ] ворид нашавад!»');
INSERT INTO "pioneers" VALUES(68,25,'68:25','Ва субҳгоҳон бо тасмими ҷиддӣ бар ҷилавгирӣ [аз вуруди мустамандон ба самти боғ] берун шуданд');
INSERT INTO "pioneers" VALUES(68,26,'68:26','Пас, ҳангоме ки он [боғ]-ро диданд, гуфтанд: «Яқинан мо [иштибоҳ омадаем ва] роҳро гум кардаем');
INSERT INTO "pioneers" VALUES(68,27,'68:27','балки ҳама чизро аз даст додаем');
INSERT INTO "pioneers" VALUES(68,28,'68:28','Яке аз беҳтарин [ва хирадмандтарин]-и эшон гуфт: «Оё ба шумо нагуфтам, чаро [шукру] тасбеҳ [-и Аллоҳро] намегӯед?»');
INSERT INTO "pioneers" VALUES(68,29,'68:29','Гуфтанд: «Парвардигори мо поку муназзаҳ аст. Мусалламан, мо ситамгор будем»');
INSERT INTO "pioneers" VALUES(68,30,'68:30','Он гоҳ маломаткунон ба якдигар рӯ оварданд');
INSERT INTO "pioneers" VALUES(68,31,'68:31','[ва] Гуфтанд: «Вой бар мо, ки туғёнгар [ва саркаш] будем!');
INSERT INTO "pioneers" VALUES(68,32,'68:32','Умедворем, ки Парвардигорамон [моро бибахшад ва] дар иваз чизе беҳтар аз он [боғ] ба мо диҳад, [чаро ки] бе гумон, мо ба сӯи Парвардигори худ рӯй овардаем»');
INSERT INTO "pioneers" VALUES(68,33,'68:33','Азоби [Аллоҳ таоло дар дунё] чунин аст ва агар медонистанд, азоби охират мусалламан бузургтар [-у бештар] аст.');
INSERT INTO "pioneers" VALUES(68,34,'68:34','Ба ростӣ, барои парҳезкорон назди Парвардигорашон боғҳои пурнеъмат аст');
INSERT INTO "pioneers" VALUES(68,35,'68:35','Оё мо мусулмононро ҳамчун муҷримон [ва гунаҳгорон] қарор медиҳем?');
INSERT INTO "pioneers" VALUES(68,36,'68:36','Шуморо чи шудааст? Чӣ гуна доварӣ мекунед?');
INSERT INTO "pioneers" VALUES(68,37,'68:37','Оё шумо китобе [осмонӣ] доред, ки дар он фаромегиред?');
INSERT INTO "pioneers" VALUES(68,38,'68:38','Ва [оё] он чиро интихоб мекунед, дар он [китоб] барои шумо [навишта шуда] аст?');
INSERT INTO "pioneers" VALUES(68,39,'68:39','Ё ин ки бар [уҳдаи] Мо аҳду паймони устуворе то рӯзи қиёмат доред, ки ҳар чиро ҳукм кунед, [ҳақ] барои шумо бошад?');
INSERT INTO "pioneers" VALUES(68,40,'68:40','[Эй Паёмбар,] аз онҳо бипурс кадом як аз онон дар баробари ин [иддаоҳо] мутааҳҳид аст?');
INSERT INTO "pioneers" VALUES(68,41,'68:41','Ва ё ин ки [маъбудон ва] шариконе доранд? Пас агар рост мегӯянд, бояд шарикон [ва маъбудони] хешро биёваранд');
INSERT INTO "pioneers" VALUES(68,42,'68:42','Рӯзе, ки [соқ ошкор гардад ва] кор сахту душвор шавад ва [мардум] ба саҷда фаро хонда мешаванд, вале [кофирон ва мунофиқон] наметавонанд [саҷда кунанд]');
INSERT INTO "pioneers" VALUES(68,43,'68:43','[Дар ҳоле ки] Дидагонашон [аз тарсу надомат] фуру афтода ва зиллату хорӣ вуҷудашонро фаро гирифтааст ва, яқинан, пеш аз ин [дар дунё] дар ҳоле ки тандуруст буданд, ба саҷда фаро хонда мешуданд');
INSERT INTO "pioneers" VALUES(68,44,'68:44','Пас, [эй Паёмбар] Маро бо он касе, ки ин сухан [Қуръон]-ро такзиб мекунад, вогузор. Мо ононро андак-андак [ва] ба гунае ки дарнаёбанд [ва аз ҷое ки мутаваҷҷеҳ нагарданд], ба сӯйи азоб хоҳем кашонд');
INSERT INTO "pioneers" VALUES(68,45,'68:45','Ва ба онон муҳлат медиҳам. Ба ростӣ, ки тадбири Ман устувор [ва ҳисобшуда] аст');
INSERT INTO "pioneers" VALUES(68,46,'68:46','[Эй Паёмбар] Магар ту аз эшон музде дархост мекунӣ, ки [аз адои он дар ранҷанд ва] бар эшон сангин аст?');
INSERT INTO "pioneers" VALUES(68,47,'68:47','Ё [асрори] ғайб назди онҳост ва онон [он чиро мегуянд, аз рӯи он] менависанд');
INSERT INTO "pioneers" VALUES(68,48,'68:48','Пас, [эй паёмбар] барои фармон [ва ҳукми] Парвардигорат сабр кун ва монанди соҳиби моҳӣ [Юнус] мабош, ки бо диле шармсор аз хашму андӯҳ [Аллоҳро] нидо дод.');
INSERT INTO "pioneers" VALUES(68,49,'68:49','Агар неъмат [-у раҳмате] аз сӯи Парвардигораш ба ёрии ӯ наомада буд, яқинан, дар ҳоле ки накӯҳида буд, [аз шиками моҳӣ] ба саҳрои хушкида берун афканда мешуд');
INSERT INTO "pioneers" VALUES(68,50,'68:50','Пас, Парвардигораш ӯро баргузид ва аз солеҳон қарор дод');
INSERT INTO "pioneers" VALUES(68,51,'68:51','Ва [эй паёмбар] наздик аст онон, ки кофир шуданд, ҳангоме ки оёти Қуръонро мешунаванд, туро чашмзахм зананд ва [ба хотири хашму ҳасадашон] мегӯянд: «Ҳатман, ӯ девона аст»');
INSERT INTO "pioneers" VALUES(68,52,'68:52','Дар ҳоле ки ин [Қуръон] ҷуз панде барои ҷаҳониён нест.');
INSERT INTO "pioneers" VALUES(69,1,'69:1','[Рӯзи] Воқеъшуданӣ [растохез]');
INSERT INTO "pioneers" VALUES(69,2,'69:2','Он воқеъшуданӣ чист?');
INSERT INTO "pioneers" VALUES(69,3,'69:3','Ва ту чи донӣ он воқеъшуданӣ чист [ва чи гуна аст]?');
INSERT INTO "pioneers" VALUES(69,4,'69:4','[Қавми] Самуд ва [қавми] Од [қиёмати] дарҳамкӯбандаро такзиб карданд');
INSERT INTO "pioneers" VALUES(69,5,'69:5','Ва аммо [қавми] Самуд ба бонги саҳмгин ҳалок шуданд');
INSERT INTO "pioneers" VALUES(69,6,'69:6','Ва аммо [қавми] Од бо тундбоде саркаш ва сард ба ҳалокат расиданд');
INSERT INTO "pioneers" VALUES(69,7,'69:7','[Аллоҳ таоло] ҳафт шабу ҳафт рӯз паёпай он [азоб]-ро бар онҳо мусаллат намуд. Пас, [агар он ҷо будӣ] [он] қавмро [дар асари он тундбод] монанди танаҳои пӯсидаи дарахтони хурмо бар замин афтода [ва ҳалокшуда] медидӣ');
INSERT INTO "pioneers" VALUES(69,8,'69:8','Пас, оё аз онҳо касеро мебинӣ, ки боқӣ монда бошад?');
INSERT INTO "pioneers" VALUES(69,9,'69:9','Ва Фиръавну касоне, ки пеш аз ӯ буданд ва [мардуми] шаҳрҳои вайроншудаи [қавми Лут] муртакиби гуноҳони [бузург] шуданд');
INSERT INTO "pioneers" VALUES(69,10,'69:10','Пас, онҳо аз [фармони] фиристодаи Парвардигорашон сарпечӣ карданд, он гоҳ [Аллоҳ таоло] ононро ба азоби сахте гирифтор кард');
INSERT INTO "pioneers" VALUES(69,11,'69:11','Ҳангоме ки об туғён кард, Мо шуморо дар киштӣ савор кардем');
INSERT INTO "pioneers" VALUES(69,12,'69:12','То панде барои шумо қарор диҳем ва гӯшҳои шунаво онро бишнаванд ва ба ёд биспоранд');
INSERT INTO "pioneers" VALUES(69,13,'69:13','Пас, он гоҳ ки як бор дар сур дамида шавад');
INSERT INTO "pioneers" VALUES(69,14,'69:14','Ва замину кӯҳҳо аз ҷо канда шуда ва якбора дарҳам кӯбида [ва муталошӣ] шаванд');
INSERT INTO "pioneers" VALUES(69,15,'69:15','Дар он рӯз воқеаи [азими] қиёмат ба вуқуъ] мепайвандад');
INSERT INTO "pioneers" VALUES(69,16,'69:16','Ва осмон шикофта мешавад ва дар он рӯз суст мегардад [ва фурӯ мерезад]');
INSERT INTO "pioneers" VALUES(69,17,'69:17','Ва фариштагон бар канораҳои он [осмон] қарор мегиранд ва он рӯз ҳашт [фаришта] арши Парвардигоратро бар сари худ ҳамл мекунанд');
INSERT INTO "pioneers" VALUES(69,18,'69:18','Дар он рӯз шумо [ҳамагӣ барои ҳисобрасӣ ба пешгоҳи Аллоҳ таоло] арза мешавед ва ҳеҷ чиз аз [корҳои] шумо пинҳон нахоҳад монд');
INSERT INTO "pioneers" VALUES(69,19,'69:19','Аммо касе, ки номаи [аъмолаш]-ро ба дасти росташ диҳанд, мегӯяд: «Биёед, нома [-и аъмол] -и маро бихонед');
INSERT INTO "pioneers" VALUES(69,20,'69:20','Ман яқин доштам, ки бо ҳисоб [-и аъмол]-и худ мувоҷеҳ хоҳам шуд»');
INSERT INTO "pioneers" VALUES(69,21,'69:21','Пас, ӯ дар як зиндагии писандида [ва ризоятбахш] хоҳад буд');
INSERT INTO "pioneers" VALUES(69,22,'69:22','Дар биҳиште барин');
INSERT INTO "pioneers" VALUES(69,23,'69:23','Ки меваҳояш [барои чидан] дар дастрас аст');
INSERT INTO "pioneers" VALUES(69,24,'69:24','[Ва ба онҳо гуфта мешавад]: «Ба [хотири] он чи дар айёми гузашта пеш фиристодаед, бихӯред ва биёшомед, гувороятон бод»');
INSERT INTO "pioneers" VALUES(69,25,'69:25','Ва аммо касе, ки нома [-и аъмол]-ашро ба дасти чапаш диҳанд, мегӯяд: «Эй кош, ҳаргиз нома [-и аъмол] - амро ба ман намедоданд');
INSERT INTO "pioneers" VALUES(69,26,'69:26','Ва намедонистам, ҳисобам чист!');
INSERT INTO "pioneers" VALUES(69,27,'69:27','Эй кош, [бо марг] ҳама чиз тамом мешуд [ва поёни кор буд]!');
INSERT INTO "pioneers" VALUES(69,28,'69:28','Молу дороиям маро бениёз накард [ва суде набахшид]');
INSERT INTO "pioneers" VALUES(69,29,'69:29','Қудрату фармонравоиям [низ] аз [дасти] ман рафт [ва ба куллӣ нобуд шуд]»');
INSERT INTO "pioneers" VALUES(69,30,'69:30','[Ба фариштагон гуфта мешавад] «Ӯро бигиред ва дар банду занҷир кунед');
INSERT INTO "pioneers" VALUES(69,31,'69:31','Сипас ӯро ба [оташи] дузах бияфканед.');
INSERT INTO "pioneers" VALUES(69,32,'69:32','Он гоҳ вайро дар занҷире бибандед, ки тӯлаш ҳафтод зироъ аст');
INSERT INTO "pioneers" VALUES(69,33,'69:33','Бе гумон, ӯ ҳаргиз ба Аллоҳи бузург имон намеовард');
INSERT INTO "pioneers" VALUES(69,34,'69:34','Ва ҳаргиз [мардумро] бар таом додани бенаво[-ён] ташвиқ намекард');
INSERT INTO "pioneers" VALUES(69,35,'69:35','Пас, имрӯз дар ин ҷо дӯсти самимӣ [ва меҳрубоне] надорад [ки ёриаш кунад]');
INSERT INTO "pioneers" VALUES(69,36,'69:36','Ва ғизое ба ҷуз чирку хун надорад');
INSERT INTO "pioneers" VALUES(69,37,'69:37','[Ғизое, ки] Ҷуз гунаҳгорон онро намехӯранд»');
INSERT INTO "pioneers" VALUES(69,38,'69:38','Пас, савганд ба он чи мебинед');
INSERT INTO "pioneers" VALUES(69,39,'69:39','Ва ба он чи намебинед');
INSERT INTO "pioneers" VALUES(69,40,'69:40','Бе тардид, ин [Қуръон сухани Аллоҳ таоло аст, ки иблоғаш ба паёмбар бар уҳдаи] фиристодаи бузургвор [Ҷабраил] аст');
INSERT INTO "pioneers" VALUES(69,41,'69:41','Ва инҳо гуфтори шоире нест, [аммо шумо кофирон] камтар имон меоваред');
INSERT INTO "pioneers" VALUES(69,42,'69:42','Ва [низ] гуфтори коҳине нест, [аммо шумо] камтар панд мегиред.');
INSERT INTO "pioneers" VALUES(69,43,'69:43','Аз сӯйи Парвардигори ҷаҳониён нозил шудааст');
INSERT INTO "pioneers" VALUES(69,44,'69:44','Ва агар [паёмбар] суханон [-и дурӯғ] бар Мо мебаст');
INSERT INTO "pioneers" VALUES(69,45,'69:45','Мусалламан, Мо дасти рости ӯро мегирифтем');
INSERT INTO "pioneers" VALUES(69,46,'69:46','Сипас [шоҳ] раги қалбашро қатъ мекардем');
INSERT INTO "pioneers" VALUES(69,47,'69:47','Ва ҳеҷ як аз шумо наметавонист монеъ [-и азоб]-и ӯ гардад');
INSERT INTO "pioneers" VALUES(69,48,'69:48','Яқинан, ин [Қуръон] панду тазкире барои парҳезкорон аст');
INSERT INTO "pioneers" VALUES(69,49,'69:49','Ва Мо яқинан, медонем, ки бархе аз шумо такзибкунандагон [-и ин каломи илоҳӣ] ҳастед');
INSERT INTO "pioneers" VALUES(69,50,'69:50','Ва ҳамоно ин [Қуръон] бар кофирон мояи ҳасрату надомат аст');
INSERT INTO "pioneers" VALUES(69,51,'69:51','Ва бе гумон, ин [Қуръон] ҳақиқати ростин аст');
INSERT INTO "pioneers" VALUES(69,52,'69:52','Пас, [эй паёмбар] бо [зикри] номи Парвардигори бузургат [Ӯро] тасбеҳ гӯй');
INSERT INTO "pioneers" VALUES(70,1,'70:1','Дархосткунандае [ба тамасхур] тақозои азоберо кард, ки вуқуаш [дар қиёмат] ҳатмӣ аст');
INSERT INTO "pioneers" VALUES(70,2,'70:2','[Ин азоб] Барои кофирон аст [ва] ҳеҷ дафъкунандае надорад');
INSERT INTO "pioneers" VALUES(70,3,'70:3','Аз сӯйи Аллоҳ таоло [ба вуқуъ мепайвандад, ки] соҳиби маротиб ва дараҷоти волост');
INSERT INTO "pioneers" VALUES(70,4,'70:4','Фариштагон ва Рӯҳ [Ҷабраил] ба сӯйи Ӯ уруҷ мекунанд, дар рӯзе, ки миқдораш [дар миқёси башарӣ] панҷоҳ ҳазор сол аст');
INSERT INTO "pioneers" VALUES(70,5,'70:5','Пас, ту [эй паёмбар, дар баробари тамасхури эшон] сабри наку пеша кун');
INSERT INTO "pioneers" VALUES(70,6,'70:6','Бе гумон, онҳо он [рӯз]-ро дур мебинанд');
INSERT INTO "pioneers" VALUES(70,7,'70:7','Ва Мо онро наздик мебинем');
INSERT INTO "pioneers" VALUES(70,8,'70:8','[Ҳамон] Рӯзе, ки осмон чун миси гудохта шавад');
INSERT INTO "pioneers" VALUES(70,9,'70:9','Ва кӯҳҳо монанди пашми рангин [муталошӣ ва пароканда] шавад');
INSERT INTO "pioneers" VALUES(70,10,'70:10','Ва ҳеҷ дӯсти самимӣ [ва хешованде] аз [ҳоли] дӯсти самимӣ [ва хешовандаш] намепурсад');
INSERT INTO "pioneers" VALUES(70,11,'70:11','[Дар ҳоле ки] Онон ба якдигар нишон дода мешаванд, гунаҳгор [дӯст дорад ва] орзу мекунад, ки фарзандони худро дар баробари [раҳоӣ аз] азоби он рӯз бидиҳад');
INSERT INTO "pioneers" VALUES(70,12,'70:12','Ва [ҳамчунин] ҳамсару бародарашро');
INSERT INTO "pioneers" VALUES(70,13,'70:13','ва қабилаашро, ки [ҳамеша] ба ӯ ҷо ва паноҳ медод');
INSERT INTO "pioneers" VALUES(70,14,'70:14','Ва [ҳамчунин] ҳамаи онҳоеро, ки рӯйи замин ҳастанд, [фидо кунад] то наҷот ёбад');
INSERT INTO "pioneers" VALUES(70,15,'70:15','[Аммо] Ҳаргиз чунин нест! Бе гумон, он шуълаҳои [оташи] сӯзон аст');
INSERT INTO "pioneers" VALUES(70,16,'70:16','[Ки] пӯст аз сар бармеканад');
INSERT INTO "pioneers" VALUES(70,17,'70:17','Касонеро, ки [ба фармони Аллоҳ таоло] пушт карданд ва [аз ҳақ] рӯй гардониданд, [ба сӯи худ] фаро мехонад');
INSERT INTO "pioneers" VALUES(70,18,'70:18','Ва [низ касонеро, ки] амволро ҷамъоварӣ ва захира карданд [ва ҳаққи онро напардохтанд]');
INSERT INTO "pioneers" VALUES(70,19,'70:19','Яқинан, инсон камтоқат [-у ҳарис] офарида шудааст');
INSERT INTO "pioneers" VALUES(70,20,'70:20','Ҳар гоҳ бадӣ [ва мусибате] ба ӯ расад, бетобӣ мекунад');
INSERT INTO "pioneers" VALUES(70,21,'70:21','Ва ҳар гоҳ хубӣ ба ӯ расад, боздоранда [ва бахил] мегардад');
INSERT INTO "pioneers" VALUES(70,22,'70:22','Магар намозгузорон');
INSERT INTO "pioneers" VALUES(70,23,'70:23','Ҳамон касоне, ки бар намозҳояшон ҳамеша пойдорӣ мекунанд.');
INSERT INTO "pioneers" VALUES(70,24,'70:24','Ва касоне, ки дар амволашон ҳаққи [муайяну] маълуме ҳаст');
INSERT INTO "pioneers" VALUES(70,25,'70:25','Барои мустаманд ва маҳрум');
INSERT INTO "pioneers" VALUES(70,26,'70:26','Ва [ҳамон] касоне, ки рӯзи ҷазоро тасдиқ мекунанд');
INSERT INTO "pioneers" VALUES(70,27,'70:27','Ва касоне, ки аз азоби Парвардигорашон метарсанд');
INSERT INTO "pioneers" VALUES(70,28,'70:28','[Чаро ки] Бе гумон, аз азоби Парвардигорашон дар амон натавонанд буд');
INSERT INTO "pioneers" VALUES(70,29,'70:29','Ва касоне, ки шармгоҳҳояшонро нигаҳ медоранд');
INSERT INTO "pioneers" VALUES(70,30,'70:30','Магар бо ҳамсарону канизоне, ки молики онҳо ҳастанд, ки [дар ин сурат] сарзанише бар онҳо нест');
INSERT INTO "pioneers" VALUES(70,31,'70:31','Пас, ҳар кас фаротар аз инҳоро бихоҳад, онон мутаҷовизон ҳастанд');
INSERT INTO "pioneers" VALUES(70,32,'70:32','Ва касоне, ки амонатҳо ва паймонҳояшонро риоя мекунанд');
INSERT INTO "pioneers" VALUES(70,33,'70:33','Ва касоне, ки ба шаҳодатҳои худ мутааҳҳиданд');
INSERT INTO "pioneers" VALUES(70,34,'70:34','Ва касоне, ки бар намозашон пойдорӣ мекунанд');
INSERT INTO "pioneers" VALUES(70,35,'70:35','Инон дар боғҳо [-и биҳишт] гиромӣ дошта мешаванд');
INSERT INTO "pioneers" VALUES(70,36,'70:36','Пас, [эй паёмбар] кофиронро чи шудааст, ки ба сӯйи ту мешитобанд?');
INSERT INTO "pioneers" VALUES(70,37,'70:37','[Ва] Аз росту чап гурӯҳ-гурӯҳ [ҳуҷум меоваранд?]');
INSERT INTO "pioneers" VALUES(70,38,'70:38','Оё ҳар кадом аз онҳо [бо он аъмоли нописандашон] тамаъ доранд, ки дар биҳишти пурнеъмат [-и илоҳӣ] дароварда шаванд?');
INSERT INTO "pioneers" VALUES(70,39,'70:39','Ҳаргиз чунин нест! Бе гумон, Мо онҳоро аз он чи [худ] медонанд, офаридаем');
INSERT INTO "pioneers" VALUES(70,40,'70:40','Пас, ба Парвардигори машриқҳо ва мағрибҳо савганд мехӯрам, ки Мо яқинан қодирем');
INSERT INTO "pioneers" VALUES(70,41,'70:41','Бар ин ки [касони дигаре] беҳтар аз эшонро ҷойгузин кунем ва Мо [барои анҷоми ин кор] дармонда ва нотавон нестем');
INSERT INTO "pioneers" VALUES(70,42,'70:42','Пас, [эй паёмбар] ононро ба ҳоли худ вогузор, то дар ботили худ фуру раванд ва бозӣ кунанд, то ба рӯзе бирасанд, ки бад-эшон ваъда дода шудааст');
INSERT INTO "pioneers" VALUES(70,43,'70:43','[Ҳамон] Рӯзе, ки шитобон аз қабрҳо хориҷ мешаванд, гӯӣ ба сӯйи бутҳо медаванд');
INSERT INTO "pioneers" VALUES(70,44,'70:44','[Дар ҳоле ки] Чашмҳояшон [аз шарму ваҳшат] ба зер афтода ва зиллат [-у хорӣ] ононро пӯшидааст. Ин ҳамон рӯзест, ки ба эшон ваъда дода мешавад');
INSERT INTO "pioneers" VALUES(71,1,'71:1','Бе гумон, Мо Нуҳро ба сӯйи қавмаш фиристодем [ва гуфтем]: «Қавми худро – пеш аз он ки азоби дарднок ба суроғашон ояд – ҳушдор деҳ [ва битарсон]');
INSERT INTO "pioneers" VALUES(71,2,'71:2','[Нуҳ] Гуфт: «Эй қавм, ман барои шумо бимдиҳандае ошкор ҳастам');
INSERT INTO "pioneers" VALUES(71,3,'71:3','Ки Аллоҳро бипарастед ва аз Ӯ парво намоед ва мутеи [дастурҳои] ман бошед');
INSERT INTO "pioneers" VALUES(71,4,'71:4','[Пас, агар чунин кардед, Аллоҳ таоло] гуноҳони шуморо меомурзад ва шуморо то замони муайяне муҳлат медиҳад, зеро ҳангоме ки аҷали илоҳӣ фаро расад, таъхир нахоҳад дошт, агар медонистед»');
INSERT INTO "pioneers" VALUES(71,5,'71:5','Ва Нуҳ [пас аз насиҳату даъват] гуфт: «Парвардигоро, ба ростӣ, ки ман қавми худро шабу рӯз [ба имон] даъват кардам');
INSERT INTO "pioneers" VALUES(71,6,'71:6','Вале даъвати ман ҷуз ба гурезашон [аз ҳақ] наафзуд');
INSERT INTO "pioneers" VALUES(71,7,'71:7','Ва ман ҳар чи даъваташон кардам, то саранҷоми ононро биёмурзӣ, ангуштони худро дар гӯшҳояшон фурӯ карданд ва либосҳояшонро [ба худ] печиданд ва [дар мухолифат] исрор [-у пофишорӣ] карданд ва ба шиддат гарданкашӣ ва такаббур намуданд');
INSERT INTO "pioneers" VALUES(71,8,'71:8','Он гоҳ онҳоро бо садои баланд [ва ошкор] даъват кардам');
INSERT INTO "pioneers" VALUES(71,9,'71:9','Он гоҳ ошкоро барояшон гуфтам ва дар ниҳон барои [ҳар кадом аз] онон баён кардам');
INSERT INTO "pioneers" VALUES(71,10,'71:10','Пас, гуфтам: «Аз Парвардигори хеш омурзиш бихоҳед [ки] бе гумон, Ӯ бисёр омурзанда аст');
INSERT INTO "pioneers" VALUES(71,11,'71:11','То аз осмон [борони] паёпай бар шумо бифиристад');
INSERT INTO "pioneers" VALUES(71,12,'71:12','Ва шуморо бо амволу фарзандони бисёр ёрӣ кунад, ба шумо боғҳои [сарсабз] бидиҳад ва бароятон ҷӯйборҳо[-и ҷорӣ] қарор диҳад');
INSERT INTO "pioneers" VALUES(71,13,'71:13','Шуморо чи шудааст, ки барои Аллоҳ таоло азамат [-у шукӯҳ] қоил нестед?');
INSERT INTO "pioneers" VALUES(71,14,'71:14','Дар ҳоле ки шуморо дар мароҳили гуногун офарид');
INSERT INTO "pioneers" VALUES(71,15,'71:15','Оё намебинед, чи гуна Аллоҳ таоло ҳафт осмонро бар фарози якдигар офарид?');
INSERT INTO "pioneers" VALUES(71,16,'71:16','Ва моҳро дар миёни осмонҳо мояи рӯшноӣ қарор дод ва хуршедро чароғи [фурӯзоне] гардонид?');
INSERT INTO "pioneers" VALUES(71,17,'71:17','Ва Аллоҳ таоло шуморо ҳамчун гиёҳе аз замин рӯёнид');
INSERT INTO "pioneers" VALUES(71,18,'71:18','Сипас шуморо ба он [замин] бозмегардонад ва бори дигар шуморо берун меоварад');
INSERT INTO "pioneers" VALUES(71,19,'71:19','Ва Аллоҳ таоло заминро барои шумо густурда сохт');
INSERT INTO "pioneers" VALUES(71,20,'71:20','То аз роҳҳои паҳновари он [ба сӯйи мақсади худ] рафтуомад кунед»');
INSERT INTO "pioneers" VALUES(71,21,'71:21','Нуҳ гуфт: «Парвардигоро, онҳо аз ман нофармонӣ намуданд ва аз касе пайравӣ карданд, ки молу фарзандаш чизе ҷуз зиёнкорӣ бар ӯ наафзудааст');
INSERT INTO "pioneers" VALUES(71,22,'71:22','Ва [он пешвоёни гумроҳ] найранги бузурге ба кор бурданд');
INSERT INTO "pioneers" VALUES(71,23,'71:23','Ва гуфтанд: «Маъбудони худро раҳо накунед ва [ба хусус] «Вадда» ва «Сувоъ» ва «Яғус» ва «Яъуқ» ва «Наср»-ро [раҳо накунед]');
INSERT INTO "pioneers" VALUES(71,24,'71:24','Ба ростӣ, онҳо бисёреро гумроҳ карданд ва [ту эй Парвардигор] ситамгоронро ҷуз гумроҳӣ маафзой»');
INSERT INTO "pioneers" VALUES(71,25,'71:25','[Саранҷом онон] Ба кайфари гуноҳонашон [ҳамагӣ] ғарқ шуданд, пас, дар оташ [-и дузах] дароварда шуданд ва дар баробари [азоби] илоҳӣ ёвар [-у мададгоре] барои худ наёфтанд');
INSERT INTO "pioneers" VALUES(71,26,'71:26','Нуҳ гуфт: «Парвардигоро, ҳеҷ як аз кофиронро бар рӯйи замин боқӣ магузор');
INSERT INTO "pioneers" VALUES(71,27,'71:27','Чаро ки агар онҳоро боқӣ гузорӣ, бандагонатро гумроҳ мекунанд ва ҷуз [насле] бадкор ва кофир ба вуҷуд намеоваранд');
INSERT INTO "pioneers" VALUES(71,28,'71:28','Парвардигоро, маро ва падару модарам ва ҳар он касеро, ки бо имон вориди хонаи ман мешавад ва [тамоми] мардони муъмин ва занони муъминро биёмурз ва ситамгоронро ҷуз ҳалокат [ва нобудӣ] маафзо»');
INSERT INTO "pioneers" VALUES(72,1,'72:1','[Эй паёмбар] Бигӯ: «Ба ман ваҳй шудааст, ки гурӯҳе аз ҷинниён [ба ин Қуръон] гӯш фаро додаанд, пас, гуфтанд: «Бе гумон, мо Қуръоне шигифтовар шунидем');
INSERT INTO "pioneers" VALUES(72,2,'72:2','Ки ба роҳи рост ҳидоят мекунад. Пас, мо ба он имон овардем ва ҳаргиз касеро бо Парвардигорамон шарик қарор намедиҳем');
INSERT INTO "pioneers" VALUES(72,3,'72:3','Ва ин ки шаъну азамати Парвардигорамон бас [азиму] баланд аст. Ӯ [ҳаргиз барои худ] ҳамсару фарзанде интихоб накардааст');
INSERT INTO "pioneers" VALUES(72,4,'72:4','Ва ин ки сафеҳ [-у нодон]-и мо [шайтон] дар бораи Аллоҳ таоло суханони норавое мегуфт');
INSERT INTO "pioneers" VALUES(72,5,'72:5','Ва мо [чунин] гумон мекардем, ки ҳаргиз инсу ҷин бар Аллоҳ таоло дурӯғ намебанданд');
INSERT INTO "pioneers" VALUES(72,6,'72:6','Ва ин ки мардоне аз одамиён ба мардоне аз ҷинниён паноҳ мебурданд ва [бад-ин васила] бар гумроҳӣ ва саркашии эшон меафзуданд');
INSERT INTO "pioneers" VALUES(72,7,'72:7','Ва ин ки онон [кофирон] гумон мекарданд – ҳамон гуна ки шумо [ҷинниён] мепиндоштед – ки Аллоҳ таоло ҳеҷ касро [дубора] зинда намегардонад');
INSERT INTO "pioneers" VALUES(72,8,'72:8','Ва ин ки мо осмонро ҷустуҷӯ кардем, пас, онро пур аз муҳофизони неруманд ва тирҳои шиҳоб ёфтем');
INSERT INTO "pioneers" VALUES(72,9,'72:9','Ва ин ки дар [осмон] барои шунидан ба камин менишастем, [аммо] акнун ҳар ки бихоҳад ба гӯш бошад, тири шиҳобе дар камини худ меёбад');
INSERT INTO "pioneers" VALUES(72,10,'72:10','Ва ин ки мо намедонем, оё барои аҳли замин шарре ирода шуда, ё Парвардигорашон хайру ҳидояти эшонро хостааст');
INSERT INTO "pioneers" VALUES(72,11,'72:11','Ва ин ки дар миёни мо афроде солеҳ ва афроде нодуруст, мо гурӯҳҳое мутафовит [ва гуногун] ҳастем');
INSERT INTO "pioneers" VALUES(72,12,'72:12','Ва ин ки мо яқин дорем, ки ҳаргиз наметавонем дар рӯйи замин Аллоҳро оҷизу нотавон кунем ва ҳаргиз бо гурез наметавонем, Ӯро нотавон созем [ва роҳи фироре надорем]');
INSERT INTO "pioneers" VALUES(72,13,'72:13','Ва ин ки мо ҳангоме ки [сухани] ҳидоятро шунидем, ба он имон овардем. Пас, ҳар касе, ки ба Парвардигораш имон оварад, на аз нуқсон меҳаросаду на аз ситам [бим дорад]');
INSERT INTO "pioneers" VALUES(72,14,'72:14','Ва ин ки гурӯҳе аз мо мусулмон ва гурӯҳе кофир [ва золим] ҳастанд. Пас, ҳар кас, ки ислом оварда, ононанд, ки роҳи растагориро ҷустуҷу карда [ва баргузида]-анд');
INSERT INTO "pioneers" VALUES(72,15,'72:15','Ва аммо кофирон [ва ситамгорон] ҳезуми [оташи] ҷаҳаннам хоҳанд буд');
INSERT INTO "pioneers" VALUES(72,16,'72:16','Ва ин ки агар онҳо бар роҳи рост пойдорӣ кунанд, албатта, бо оби фаровон серобашон мекунем');
INSERT INTO "pioneers" VALUES(72,17,'72:17','То онҳоро бо он [неъматҳо] биозмоем. Ва ҳар кас аз ёд [-у зикри] Парвардигораш рӯ бартобад, [Аллоҳ таоло] ӯро ба азобе сахт гирифтор месозад');
INSERT INTO "pioneers" VALUES(72,18,'72:18','Ва ин ки масоҷид аз они Аллоҳ таоло аст, пас, касеро бо Аллоҳ таоло [ба парастишу ёрихоҳӣ] нахонед');
INSERT INTO "pioneers" VALUES(72,19,'72:19','Ва ин ки ҳангоме ки бандаи Аллоҳ таоло [Муҳаммад] қиём кард, то Ӯро бихонад [ва ибодат кунад], наздик буд, ки [аз издиҳом бар гирди ӯ] бар сари ҳам фурӯ резанд»');
INSERT INTO "pioneers" VALUES(72,20,'72:20','[Эй паёмбар, ба онҳо] Бигӯ: «Ман танҳо Парвардигорамро мехонам ва касеро бо Ӯ шарик намесозам»');
INSERT INTO "pioneers" VALUES(72,21,'72:21','Бигӯ: «Бе гумон, ман наметавонам ба шумо зиёне бирасонам ва на хайру суде»');
INSERT INTO "pioneers" VALUES(72,22,'72:22','Бигӯ: «[Агар ман низ бар хилофи фармонаш рафтор кунам] ҳеҷ кас маро дар баробари Ӯ ҳимоят намекунад ва паноҳгоҳе ҷуз Ӯ намеёбам');
INSERT INTO "pioneers" VALUES(72,23,'72:23','[Вазифаи ман] Танҳо иблоғе аз ҷониби Аллоҳ таоло ва [расонидани] паёмҳои ӯст. Ва ҳар кас аз Аллоҳу паёмбараш нофармонӣ кунад, бе гумон, оташи ҷаҳаннам аз они Ӯст, ҷовидона дар он хоҳад монд»');
INSERT INTO "pioneers" VALUES(72,24,'72:24','[Кори кофирон ҳамчунон идома дорад] То замоне, ки он чиро ба онҳо ваъда дода шуда, бубинанд. Он гоҳ хоҳанд донист, ки чи касе ёвараш нотавонтар ва ҷамъияташ камтар аст');
INSERT INTO "pioneers" VALUES(72,25,'72:25','[Эй паёмбар] Бигӯ: «Ман намедонам, оё он чи ки ба шумо ваъда дода шуда, наздик аст ё Парвардигорам замоне [дур] барои он қарор медиҳад»');
INSERT INTO "pioneers" VALUES(72,26,'72:26','[Аллоҳ таоло] Донои ғайб аст ва ҳеҷ касро бар [асрори] ғайби худ огоҳ намесозад');
INSERT INTO "pioneers" VALUES(72,27,'72:27','Магар расулоне, ки ононро писандида [ва баргузида] аст. Пас, бе гумон, аз пеши рӯйи ӯ ва пушти сараш нигаҳбоне [аз фариштагон барои муҳофизат аз вай] мегуморад');
INSERT INTO "pioneers" VALUES(72,28,'72:28','То бидонад, ки [паёмбарон] рисолатҳои Парвардигорашонро иблоғ кардаанд ва [Аллоҳ таоло] ба он чи назди онҳост иҳота дорад ва ҳар чизеро ба адад баршумурдааст');
INSERT INTO "pioneers" VALUES(73,1,'73:1','Эй ҷомабахудпечида!');
INSERT INTO "pioneers" VALUES(73,2,'73:2','Шабро – ҷуз андаке – ба по хез [ва ибодат кун]');
INSERT INTO "pioneers" VALUES(73,3,'73:3','Нимае аз онро ё андаке аз он кам кун');
INSERT INTO "pioneers" VALUES(73,4,'73:4','Ё андаке бар [нисфи] он биафзо ва Қуръонро бо тартил [-у тааммул] бихон');
INSERT INTO "pioneers" VALUES(73,5,'73:5','Яқинан Мо ба зудӣ сухане сангин бар ту нозил хоҳем кард');
INSERT INTO "pioneers" VALUES(73,6,'73:6','Бе гумон, шабзиндадорӣ [бар нафс] душвор аст ва барои гуфтор [ва ниёиш] муносибтар ва устувортар аст');
INSERT INTO "pioneers" VALUES(73,7,'73:7','Ва яқинан ту дар рӯз машғулияти бисёр дорӣ');
INSERT INTO "pioneers" VALUES(73,8,'73:8','Ва номи Парвардигоратро ёд кун ва танҳо ба Ӯ дил бибанд');
INSERT INTO "pioneers" VALUES(73,9,'73:9','[Ҳамон] Парвардигори машриқу мағриб, ки маъбуде [ба ҳақ] ҷуз Ӯ нест. Пас, Ӯро корсоз [-у нигаҳбони худ] баргузин');
INSERT INTO "pioneers" VALUES(73,10,'73:10','Ва бар он чи [мушрикон] мегӯянд, шикебо бош ва ба шеваи наку [ва шоиста] аз онон дурӣ кун');
INSERT INTO "pioneers" VALUES(73,11,'73:11','Ва маро бо ин такзибкунандагони бархурдор аз неъмат вогузор ва ба онон каме муҳлат деҳ');
INSERT INTO "pioneers" VALUES(73,12,'73:12','Бе гумон, назди Мо қайд [-у бандҳо]-и сангин ва [оташи] дузах аст');
INSERT INTO "pioneers" VALUES(73,13,'73:13','Ва таоме гулугир ва азобе дарднок');
INSERT INTO "pioneers" VALUES(73,14,'73:14','Рӯзе, ки замину кӯҳҳо ба ларза дарояд ва кӯҳҳо [муталошӣ шаванд ва] ҳамчун тӯдаи реги нарм [равон] гарданд');
INSERT INTO "pioneers" VALUES(73,15,'73:15','Яқинан, Мо паёмбаре ба сӯйи шумо фиристодем, ки бар шумо шоҳид [ва гувоҳ] аст, ҳамон гуна ки ба сӯйи Фиръавн паёмбаре фиристодем');
INSERT INTO "pioneers" VALUES(73,16,'73:16','Он гоҳ Фиръавн аз он паёмбар нофармонӣ [ва бо ӯ мухолифат] кард ва Мо ӯро ба сахтӣ муҷозот кардем');
INSERT INTO "pioneers" VALUES(73,17,'73:17','Пас, агар кофир шавед, чи гуна худро [аз азоб] дар амон медоред, [дар] рӯзе, ки кӯдаконро пир мекунад?');
INSERT INTO "pioneers" VALUES(73,18,'73:18','[Дар он рӯз] Осмон шикофта мешавад [ва] ваъдаи Ӯ, ҳатман, ба вуқуъ мепайвандад.');
INSERT INTO "pioneers" VALUES(73,19,'73:19','Бе гумон, ин [оёт] панд [-у ҳушдоре] аст, то ҳар ки бихоҳад, ба сӯи Парвардигори худ роҳе дар пеш гирад');
INSERT INTO "pioneers" VALUES(73,20,'73:20','[Эй паёмбар] Яқинан Парвардигорат медонад, ки ту ва гурӯҳе аз онҳо, ки бо ту ҳастанд, наздики ду савуми шаб [аз cе ду ҳиссаи шаб] ва [ё] нисф [ё] як савум [сеяк]-и онро ба [намоз] бармехезед. Ва Аллоҳ таоло шабу рӯзро андозагирӣ мекунад. [Ӯ] Медонад, ки шумо наметавонед шумориши онро [дақиқ] муҳосиба кунед, пас, шуморо бахшид. Пас, акнун ҳар чи [барои шумо] муяссар бошад, аз Қуръон бихонед. [Аллоҳ таоло] Медонад, ки бархе аз шумо бемор хоҳанд шуд ва гурӯҳе [дигар] барои ба даст овардани фазлу неъмати Аллоҳ таоло дар замин сафар мекунанд ва гурӯҳе [низ] дар роҳи Аллоҳ таоло ҷиҳод мекунанд, пас, он чи муяссар бошад, аз он бихонед ва намоз барпо доред ва закот диҳед ва ба Аллоҳ таоло қарзи наку диҳед ва [бидонед, ки] он чи аз корҳои хайр пешопеш барои худ мефиристед, [онро] назди Аллоҳ таоло ба беҳтарин ваҷҳ ва бузургтарин подош хоҳед ёфт ва аз Аллоҳ таоло омурзиш бихоҳед, ки ҳамоно Аллоҳ таоло омурзандаи меҳрубон аст');
INSERT INTO "pioneers" VALUES(74,1,'74:1','Эй ҷомабарсаркашида!');
INSERT INTO "pioneers" VALUES(74,2,'74:2','Бархез ва бим деҳ!');
INSERT INTO "pioneers" VALUES(74,3,'74:3','Ва Парвардигоратро бузург шумор!');
INSERT INTO "pioneers" VALUES(74,4,'74:4','Ва ҷомаро покиза бидор!');
INSERT INTO "pioneers" VALUES(74,5,'74:5','Ва аз палидӣ дурӣ кун!');
INSERT INTO "pioneers" VALUES(74,6,'74:6','Барои ин базлу бахшиш макун, ки афзунталабӣ кунӣ');
INSERT INTO "pioneers" VALUES(74,7,'74:7','Ва барои [хушнудии] Парвардигорат шикебо бош');
INSERT INTO "pioneers" VALUES(74,8,'74:8','Пас, ҳангоме ки дар «сур» дамида шавад');
INSERT INTO "pioneers" VALUES(74,9,'74:9','Он рӯз рӯзи сахт [ва душворе] аст');
INSERT INTO "pioneers" VALUES(74,10,'74:10','Бар кофирон осон нест');
INSERT INTO "pioneers" VALUES(74,11,'74:11','[Эй паёмбар] Маро бо касе, ки ӯро танҳо [ва бидуни амволу авлод] офаридаам, вогузор');
INSERT INTO "pioneers" VALUES(74,12,'74:12','[Ҳамон касе, ки] моли фаровоне барояш қарор додам');
INSERT INTO "pioneers" VALUES(74,13,'74:13','Ва фарзандоне, ки ҳамеша [дар хидмати ӯ ва] бо ӯ ҳастанд');
INSERT INTO "pioneers" VALUES(74,14,'74:14','Ва ҳамаи васоил [ва имконоти] зиндагиро дар ихтиёраш қарор додам');
INSERT INTO "pioneers" VALUES(74,15,'74:15','Боз [ҳам] тамаъ дорад, ки [бар он] биафзоям');
INSERT INTO "pioneers" VALUES(74,16,'74:16','Ҳаргиз [чунин нахоҳад шуд]! Бе гумон, ӯ нисбат ба оёти Мо душманӣ дорад');
INSERT INTO "pioneers" VALUES(74,17,'74:17','Ва ба зудӣ ӯро ба машаққату сахтӣ меандозам');
INSERT INTO "pioneers" VALUES(74,18,'74:18','Зеро ӯ [барои мубориза бо Қуръон] андешид ва муҳосиба кард');
INSERT INTO "pioneers" VALUES(74,19,'74:19','Пас, марг бар ӯ бод! Чи гуна андоза [ва муҳосиба] кард?');
INSERT INTO "pioneers" VALUES(74,20,'74:20','Боз [ҳам] марг бар ӯ бод! Чи гуна андоза [ва муҳосиба] кард?');
INSERT INTO "pioneers" VALUES(74,21,'74:21','Сипас назар афканд');
INSERT INTO "pioneers" VALUES(74,22,'74:22','Он гоҳ чеҳра дар ҳам кашид ва рӯ турш кард');
INSERT INTO "pioneers" VALUES(74,23,'74:23','Сипас [ба ҳақ] пушт намуд ва такаббур варзид');
INSERT INTO "pioneers" VALUES(74,24,'74:24','Он гоҳ гуфт: «Ин [Қуръон] чизе ҷуз ҷодуе, ки [аз дигарон] омӯхта шуда, нест');
INSERT INTO "pioneers" VALUES(74,25,'74:25','Ва ин [чизе] ҷуз гуфтори башар нест»');
INSERT INTO "pioneers" VALUES(74,26,'74:26','Ба зудӣ ӯро ба дузах дархоҳам овард');
INSERT INTO "pioneers" VALUES(74,27,'74:27','Ва ту чи донӣ, ки дузах чи гуна аст?');
INSERT INTO "pioneers" VALUES(74,28,'74:28','[Оташи сӯзонест, ки] На [чизеро] боқӣ мегузорад ва на [чизеро] раҳо мекунад');
INSERT INTO "pioneers" VALUES(74,29,'74:29','Пӯстро [дигаргун карда] месӯзонад');
INSERT INTO "pioneers" VALUES(74,30,'74:30','Бар он [оташ] нуздаҳ [фаришта] гуморида шудааст.');
INSERT INTO "pioneers" VALUES(74,31,'74:31','Мо маъмурони дузахро фақат аз фариштагон қарор додем ва шумори онҳоро танҳо барои озмоиши кофирон муайян кардаем, то аҳли китоб яқин кунанд ва касоне, ки имон овардаанд, бар имони худ бияфзоянд ва аҳли китоб ва муъминон гирифтори шакку тардид нашаванд ва то касоне, ки дар дилҳояшон мараз аст ва [низ] кофирон бигӯянд: «Аллоҳ таоло аз ин масал [ва тавсиф] чи хостааст?». Бад-ин гуна, Аллоҳ таоло ҳар касро бихоҳад, гумроҳ месозад ва ҳар касро бихоҳад, ҳидоят мекунад ва [шумори] лашкариёни Парвардигоратро [касе] ҷуз Ӯ намедонад ва ин ҷуз панду андарзе барои инсонҳо нест');
INSERT INTO "pioneers" VALUES(74,32,'74:32','Оре, савганд ба моҳ [ки ҳаргиз чунин нест, ки онон тасаввур мекунанд]');
INSERT INTO "pioneers" VALUES(74,33,'74:33','Ва [савганд ба] шаб ҳангоме ки пушт кунад');
INSERT INTO "pioneers" VALUES(74,34,'74:34','Ва ба субҳ ҳангоме ки равшан шавад');
INSERT INTO "pioneers" VALUES(74,35,'74:35','Ки он [оташи дузах] яке аз [азобҳои] бузург [ва ҳавлнок] аст');
INSERT INTO "pioneers" VALUES(74,36,'74:36','Барои инсон бимдиҳанда аст');
INSERT INTO "pioneers" VALUES(74,37,'74:37','Барои касе аз шумо, ки бихоҳад [бо корҳои наку] пеш афтад, ё [бо гуноҳ кардан] ақиб бимонад');
INSERT INTO "pioneers" VALUES(74,38,'74:38','Ҳар кас дар гарави аъмоли хеш аст');
INSERT INTO "pioneers" VALUES(74,39,'74:39','Магар асҳоби ямин [муъминон]');
INSERT INTO "pioneers" VALUES(74,40,'74:40','Ки дар боғҳо [-и биҳишт] ҳастанд ва мепурсанд');
INSERT INTO "pioneers" VALUES(74,41,'74:41','аз гунаҳгорон:');
INSERT INTO "pioneers" VALUES(74,42,'74:42','«Чӣ чиз шуморо ба дузах даровард?');
INSERT INTO "pioneers" VALUES(74,43,'74:43','[Дузахиён] Мегӯянд: «Мо аз намозгузорон набудем');
INSERT INTO "pioneers" VALUES(74,44,'74:44','Ва бенаво[-ён]-ро итъом намекардем');
INSERT INTO "pioneers" VALUES(74,45,'74:45','Ва пайваста ҳамроҳи ёвагӯён [ва аҳли ботил] ҳамсадо мешудем');
INSERT INTO "pioneers" VALUES(74,46,'74:46','Ва ҳамвора рӯзи ҷазоро такзиб мекардем');
INSERT INTO "pioneers" VALUES(74,47,'74:47','То замоне, ки марг ба суроғамон омад');
INSERT INTO "pioneers" VALUES(74,48,'74:48','Пас, шафоати шафоаткунандагон ба онҳо суде намебахшад');
INSERT INTO "pioneers" VALUES(74,49,'74:49','Пас, онҳоро чи шудааст, ки аз панд [-у тазаккур] рӯй гардондаанд?');
INSERT INTO "pioneers" VALUES(74,50,'74:50','Гӯӣ, ки гӯрхароне рамидаанд');
INSERT INTO "pioneers" VALUES(74,51,'74:51','Ки аз [муқобили] шер гурехтаанд');
INSERT INTO "pioneers" VALUES(74,52,'74:52','Балки ҳар кадом аз онҳо [таваққуъ дорад ва] мехоҳад, ки [ба ӯ] номае кушода [ва ҷудогона аз сӯйи Аллоҳ таоло] дода шавад');
INSERT INTO "pioneers" VALUES(74,53,'74:53','Ҳаргиз, чунин нест [ки онон мепиндоранд]! Балки онҳо аз [азоби] охират наметарсанд');
INSERT INTO "pioneers" VALUES(74,54,'74:54','На, чунин нест, ки онҳо мегӯянд, он [Қуръон] як тазаккур ва ёдоварӣ аст');
INSERT INTO "pioneers" VALUES(74,55,'74:55','Пас, ҳар касе, ки бихоҳад аз он панд гирад');
INSERT INTO "pioneers" VALUES(74,56,'74:56','Ва ёд намекунанд [ва панд намегиранд], магар ин ки Аллоҳ таоло бихоҳад. Ӯст, ки сазовори парво кардан ва омурзидан аст.');
INSERT INTO "pioneers" VALUES(75,1,'75:1','Ба рӯзи қиёмат савганд мехӯрам');
INSERT INTO "pioneers" VALUES(75,2,'75:2','Ва ба нафси маломатгар савганд мехӯрам');
INSERT INTO "pioneers" VALUES(75,3,'75:3','Оё инсон мепиндорад, ки [пас аз марг] ҳаргиз устухонҳояшро ҷамъ нахоҳем кард?');
INSERT INTO "pioneers" VALUES(75,4,'75:4','Оре, Мо қодирем, ки [ҳатто хутути сари] ангуштонашро яксону мураттаб кунем.');
INSERT INTO "pioneers" VALUES(75,5,'75:5','Балки инсон мехоҳад, ки [озодона ва бидуни тарс аз охират] дар гуноҳу бадкорӣ мудовимат кунад');
INSERT INTO "pioneers" VALUES(75,6,'75:6','[Аз ин рӯ] Мепурсад: «Рӯзи қиёмат кай хоҳад буд?»');
INSERT INTO "pioneers" VALUES(75,7,'75:7','Пас, ҳангоме ки чашм хира шавад');
INSERT INTO "pioneers" VALUES(75,8,'75:8','Ва моҳ тира [ва бе нур] гардад');
INSERT INTO "pioneers" VALUES(75,9,'75:9','Ва хуршеду моҳ як ҷо ҷамъ шаванд');
INSERT INTO "pioneers" VALUES(75,10,'75:10','Он рӯз инсон мегӯяд: «Роҳи гурез куҷост?»');
INSERT INTO "pioneers" VALUES(75,11,'75:11','Ҳаргиз! Ҳеҷ гурезгоҳе нест');
INSERT INTO "pioneers" VALUES(75,12,'75:12','Он рӯз қароргоҳ [-и ниҳоне] ба сӯйи Парвардигори туст');
INSERT INTO "pioneers" VALUES(75,13,'75:13','Ва дар он рӯз инсонро аз тамоми корҳое, ки аз пеш ё пас фиристода, огоҳ мекунанд');
INSERT INTO "pioneers" VALUES(75,14,'75:14','Балки инсон ба хубӣ бар хештан огоҳ аст');
INSERT INTO "pioneers" VALUES(75,15,'75:15','Агарчи [дар дифоъ аз худ] узрҳояшро дар миён оварад');
INSERT INTO "pioneers" VALUES(75,16,'75:16','[Эй паёмбар, ҳангоми нузули Қуръон] забонатро барои [такрор ва хондани] он шитобзада ҳаракат мадеҳ');
INSERT INTO "pioneers" VALUES(75,17,'75:17','Мусалламан, ҷамъоварӣ ва хондани он бар [уҳдаи] Мост');
INSERT INTO "pioneers" VALUES(75,18,'75:18','Пас, ҳар гоҳ [тавассути Ҷабраил] онро [бар ту] хондем, аз хондани ӯ пайравӣ кун');
INSERT INTO "pioneers" VALUES(75,19,'75:19','Сипас баён карданаш бар [уҳдаи] Мост');
INSERT INTO "pioneers" VALUES(75,20,'75:20','Ҳаргиз чунин нест [ки шумо мушрикон мепиндоред]! Балки дунё [-и зудгузар]-ро дӯст медоред');
INSERT INTO "pioneers" VALUES(75,21,'75:21','Ва охиратро раҳо мекунед');
INSERT INTO "pioneers" VALUES(75,22,'75:22','Он рӯз чеҳраҳое тоза [ва шодоб] аст');
INSERT INTO "pioneers" VALUES(75,23,'75:23','Ба сӯйи Парвардигораш менигарад');
INSERT INTO "pioneers" VALUES(75,24,'75:24','Ва дар он рӯз чеҳраҳое абус [ва дарҳамкашида] аст');
INSERT INTO "pioneers" VALUES(75,25,'75:25','Яқин дорад, ки осебе камаршикан ба ӯ мерасад');
INSERT INTO "pioneers" VALUES(75,26,'75:26','Оре, чун [ҷон] ба гулугоҳаш бирасад');
INSERT INTO "pioneers" VALUES(75,27,'75:27','Ва гуфта шавад: «Чи касест, ки афсун бихонад? [Ва ӯро наҷот диҳад]');
INSERT INTO "pioneers" VALUES(75,28,'75:28','Ва яқин кунад, ки замони ҷудоӣ [аз дунё] аст.');
INSERT INTO "pioneers" VALUES(75,29,'75:29','Ва [кор боло гирад ва] соқҳо[-и пояш аз сахтии ҷон додан] ба ҳам печад.');
INSERT INTO "pioneers" VALUES(75,30,'75:30','Дар он рӯз масири ҳама ба сӯйи Парвардигорат хоҳад буд');
INSERT INTO "pioneers" VALUES(75,31,'75:31','Пас, [мункири меод] на тасдиқ карда ва на намоз гузоридааст');
INSERT INTO "pioneers" VALUES(75,32,'75:32','Балки [Қуръонро] такзиб кард ва [аз имон] рӯ гардонд');
INSERT INTO "pioneers" VALUES(75,33,'75:33','Сипас хиромон ба сӯйи хонаводааш бозгашт');
INSERT INTO "pioneers" VALUES(75,34,'75:34','Вой бар ту! Пас, вой бар ту!');
INSERT INTO "pioneers" VALUES(75,35,'75:35','Боз ҳам вой бар ту! Пас, вой бар ту!');
INSERT INTO "pioneers" VALUES(75,36,'75:36','Оё инсон гумон мекунад, ки [бе ҳадаф ва бидуни ҳисоб ва ҷазо] ба худ раҳо мешавад?');
INSERT INTO "pioneers" VALUES(75,37,'75:37','Оё [ӯ] нутфае аз манӣ набуд, ки [дар раҳим] рехта мешавад?');
INSERT INTO "pioneers" VALUES(75,38,'75:38','Он гоҳ ба сурати хуни баста даромад ва [Аллоҳ таоло ӯро] офарид ва дурусту устувор сохт');
INSERT INTO "pioneers" VALUES(75,39,'75:39','Сипас аз ӯ ду завҷи нару мода падид овард');
INSERT INTO "pioneers" VALUES(75,40,'75:40','Оё [чунин Офаридгоре] қодир нест, ки мурдагонро [дубора] зинда кунад?');
INSERT INTO "pioneers" VALUES(76,1,'76:1','Яқинан, замони тӯлоние бар инсон гузашт, ки чизе [муҳимму] қобили зикр набуд');
INSERT INTO "pioneers" VALUES(76,2,'76:2','Ба ростӣ, Мо инсонро аз нутфаи мухталит [омехта]-е офаридем, ӯро меозмоем. Пас, ӯро шунавову бино қарор додем');
INSERT INTO "pioneers" VALUES(76,3,'76:3','Ҳамоно Мо роҳро ба ӯ нишон додем, хоҳ сипосгузор бошад ё носипос');
INSERT INTO "pioneers" VALUES(76,4,'76:4','Ба ростӣ, Мо барои кофирон занҷирҳо ва бандҳо ва оташи сӯзон муҳайё кардаем');
INSERT INTO "pioneers" VALUES(76,5,'76:5','Бе гумон, накукорон [дар биҳишт] аз ҷоме менӯшанд, ки омехта ба кофур аст');
INSERT INTO "pioneers" VALUES(76,6,'76:6','[Аз] Чашмае, ки бандагони [хоси] Аллоҳ таоло аз он менӯшанд, [ҳар вақту] ҳар ҷо бихоҳанд он [чашма]-ро ҷорӣ месозанд');
INSERT INTO "pioneers" VALUES(76,7,'76:7','[Бандагони накукор] Ба назр вафо мекунанд ва аз рӯзе метарсанд, ки [азобу] шарри он фарогир аст');
INSERT INTO "pioneers" VALUES(76,8,'76:8','Ва ғизоро бо ин ки [ниёз ва] дӯст доранд, ба мустаманду ятим ва асир мебахшанд');
INSERT INTO "pioneers" VALUES(76,9,'76:9','[Ва дар дил мегӯянд] «Мо фақат ба хотири Аллоҳ таоло ба шумо ғизо медиҳем, на аз шумо подоше мехоҳем ва на сипосе');
INSERT INTO "pioneers" VALUES(76,10,'76:10','Ҳамоно мо аз Парвардигорамон метарсем, [аз азоби] рӯзе, ки туршрӯ ва душвор аст»');
INSERT INTO "pioneers" VALUES(76,11,'76:11','Пас, Аллоҳ таоло ононро аз [сахтӣ ва] шарри он рӯз нигаҳ дошт ва ба эшон хуррамӣ ва шодмонӣ бахшид');
INSERT INTO "pioneers" VALUES(76,12,'76:12','Ва ба [шукронаи] сабре, ки карданд, биҳишт ва [либосҳои] ҳарир [-и биҳиштӣ] ба эшон подош дод');
INSERT INTO "pioneers" VALUES(76,13,'76:13','Дар он ҷо бар тахтҳо [-и зебо] такя кардаанд, на [ҳарорати] офтобе дар он ҷо мебинед ва на [сӯзи] сармое');
INSERT INTO "pioneers" VALUES(76,14,'76:14','Ва сояҳояш бар онон фурӯ афтода ва меваҳояш [барои чидан] дар дастрас аст');
INSERT INTO "pioneers" VALUES(76,15,'76:15','Ва дар гирдогирди эшон зарфҳои симин [-и ғизо] ва косаҳои булурин [-и нӯшиданӣ] гардонида мешавад');
INSERT INTO "pioneers" VALUES(76,16,'76:16','[Косаҳои] Булурине аз нуқра, ки онҳоро ба андозаи муносиб [лабрез ва] омода кардаанд');
INSERT INTO "pioneers" VALUES(76,17,'76:17','Ва дар он ҷо ҷомҳое сероб мешаванд, ки омезааш занҷабил аст');
INSERT INTO "pioneers" VALUES(76,18,'76:18','[Ин ҷомҳо аз] Чашмае пур мегардад, ки дар он ҷо Салсабил номида мешавад');
INSERT INTO "pioneers" VALUES(76,19,'76:19','Ва ҳамвора навҷавононе ҷовидона бар гирдашон мечарханд, ки ҳар гоҳ онҳоро бубинӣ, гумон мекунӣ, ки марвориди пароканда ҳастанд');
INSERT INTO "pioneers" VALUES(76,20,'76:20','Ва чун [ба ҳар самте] бингарӣ, он ҷо неъмати бисёр ва фармонравоии азиме мебинӣ');
INSERT INTO "pioneers" VALUES(76,21,'76:21','Бар онон [биҳиштиён] либосҳое сабзранг аз дебои нозук ва дебои захим [пӯшонида шуда]- аст ва бо дастбандҳое аз нуқра ороста шудаанд ва Парвардигорашон шароби пок ба эшон менӯшонад');
INSERT INTO "pioneers" VALUES(76,22,'76:22','[Ва ба онҳо гуфта мешавад] «Ин [неъматҳо] подоши шумост ва [бидонед, ки] аз саъю кӯшиши шумо қадрдонӣ шудааст»');
INSERT INTO "pioneers" VALUES(76,23,'76:23','[Эй паёмбар] Яқинан, Мо Қуръонро бар ту тадриҷан нозил кардем');
INSERT INTO "pioneers" VALUES(76,24,'76:24','Пас, бар ҳукми Парвардигорат шикебо бош ва аз ҳеҷ кадом аз гунаҳгорон ва бединонашон фармон мабар');
INSERT INTO "pioneers" VALUES(76,25,'76:25','Ва субҳу шом номи Парвардигоратро ёд кун');
INSERT INTO "pioneers" VALUES(76,26,'76:26','Ва [низ] бахше аз шаб барояш саҷда кун [ва намоз бигузор] ва дар бахши баланде аз шаб [қисмати дарози шаб, ки ҳама дар хобанд] Ӯро тасбеҳ гӯй');
INSERT INTO "pioneers" VALUES(76,27,'76:27','Бе гумон, инон [кофирон] дунёи зудгузарро дӯст медоранд ва рӯзи сахте [-ро, ки дар пеш доранд] пушти сари худ раҳо мекунанд [ва ба он аҳамият намедиҳанд]');
INSERT INTO "pioneers" VALUES(76,28,'76:28','Мо онҳоро офаридем ва пайванди вуҷудашонро муҳкам кардем ва ҳар замон, ки бихоҳем, ҷойи ононро ба [гурӯҳи дигаре] монандашон медиҳем');
INSERT INTO "pioneers" VALUES(76,29,'76:29','Бе гумон, ин ёдоварӣ [ва панд] аст, пас, ҳар кас, ки бихоҳад, роҳе ба сӯйи Парвардигораш баргузинад');
INSERT INTO "pioneers" VALUES(76,30,'76:30','Ва шумо чизеро намехоҳед, магар ин ки Аллоҳ таоло бихоҳад. Бе гумон, Аллоҳ таоло донои ҳаким аст');
INSERT INTO "pioneers" VALUES(76,31,'76:31','Ҳар касро, ки бихоҳад, дар раҳмати худ ворид мекунад ва барои ситамгорон азоби дардноке омода кардааст');
INSERT INTO "pioneers" VALUES(77,1,'77:1','Савганд ба фариштагоне, ки паёпай фиристода мешаванд');
INSERT INTO "pioneers" VALUES(77,2,'77:2','Ҳамчун тундбод мераванд');
INSERT INTO "pioneers" VALUES(77,3,'77:3','Ва савганд ба фариштагоне, ки [абрҳоро] пароканда мекунанд');
INSERT INTO "pioneers" VALUES(77,4,'77:4','Ва савганд ба фариштагоне, ки ҷудокунандаи [ҳақ аз ботил] ҳастанд');
INSERT INTO "pioneers" VALUES(77,5,'77:5','Ва савганд ба фариштагоне, ки ваҳй [-и илоҳӣ]-ро [ба паёмбарон] илқо мекунанд');
INSERT INTO "pioneers" VALUES(77,6,'77:6','Барои итмоми ҳуҷҷат, барои бим ва ҳушдор');
INSERT INTO "pioneers" VALUES(77,7,'77:7','Бе гумон, он чи ба шумо ваъда дода мешавад, воқеъ хоҳад шуд');
INSERT INTO "pioneers" VALUES(77,8,'77:8','Пас, он гоҳ ки ситорагон тира [ва маҳв] шавад');
INSERT INTO "pioneers" VALUES(77,9,'77:9','Ва он гоҳ ки осмон шикофта шавад');
INSERT INTO "pioneers" VALUES(77,10,'77:10','Ва он гоҳ ки кӯҳҳо [аз ҷо канда ва] пароканда шавад');
INSERT INTO "pioneers" VALUES(77,11,'77:11','Ва он гоҳ ки барои паёмбарон [ҷиҳати гувоҳӣ додан дар мавриди умматҳо] вақт таъйин шавад');
INSERT INTO "pioneers" VALUES(77,12,'77:12','Ин [амр] барои чи рӯзе ба таъхир афтодааст?');
INSERT INTO "pioneers" VALUES(77,13,'77:13','Барои рӯзи ҷудоӣ [ва доварӣ]');
INSERT INTO "pioneers" VALUES(77,14,'77:14','Ва ту чи донӣ, ки рӯзи ҷудоӣ [ва доварӣ] чист?');
INSERT INTO "pioneers" VALUES(77,15,'77:15','Дар он рӯз [қиёмат] вой бар такзибкунандагон!');
INSERT INTO "pioneers" VALUES(77,16,'77:16','Оё мо пешиниён[-и муҷрим]-ро нобуд накардем?');
INSERT INTO "pioneers" VALUES(77,17,'77:17','Сипас дигаронро дар пайи онҳо хоҳем овард');
INSERT INTO "pioneers" VALUES(77,18,'77:18','Ин чунин бо гунаҳгорон рафтор мекунем');
INSERT INTO "pioneers" VALUES(77,19,'77:19','Дар он рӯз вой бар такзибкунандагон!');
INSERT INTO "pioneers" VALUES(77,20,'77:20','Оё шуморо аз обе паст [-у ночиз] наофаридем?');
INSERT INTO "pioneers" VALUES(77,21,'77:21','Ки сипас онро дар қароргоҳе маҳфуз [раҳим] қарор додем');
INSERT INTO "pioneers" VALUES(77,22,'77:22','То замоне муайян');
INSERT INTO "pioneers" VALUES(77,23,'77:23','Пас, мо [бар ин кор] тавоно будем ва чи нек тавоно [ва қудратманд] ҳастем');
INSERT INTO "pioneers" VALUES(77,24,'77:24','Дар он рӯз вой бар такзибкунандагон!');
INSERT INTO "pioneers" VALUES(77,25,'77:25','Оё заминро ҷойгоҳи [таҷаммуи мардум] қарор надодем?');
INSERT INTO "pioneers" VALUES(77,26,'77:26','Ҳам дар ҳоли ҳаёт ва ҳам маргашон?');
INSERT INTO "pioneers" VALUES(77,27,'77:27','Ва дар он кӯҳҳои бисёр баланд [ва устувор] қарор додем ва обе ширин [ва гуворо] ба шумо нӯшонидем');
INSERT INTO "pioneers" VALUES(77,28,'77:28','Дар он рӯз вой бар такзибкунандагон!');
INSERT INTO "pioneers" VALUES(77,29,'77:29','[Ба онҳо гуфта мешавад] «Ба сӯйи ҳамон чизе биравед, ки пайваста такзибаш мекардед.');
INSERT INTO "pioneers" VALUES(77,30,'77:30','Биравед ба сӯйи сояи [дудҳои оташи] сешоха');
INSERT INTO "pioneers" VALUES(77,31,'77:31','На сояафкан [ва хунук] аст ва на аз [гармии] шуълаҳои оташ ҷилавгирӣ мекунад»');
INSERT INTO "pioneers" VALUES(77,32,'77:32','Ҳамоно он [дузах] шарораҳое чун кохе [баланд] меафканад.');
INSERT INTO "pioneers" VALUES(77,33,'77:33','Гӯӣ, ки он [шарораҳо] шутурони зардранг ҳастанд');
INSERT INTO "pioneers" VALUES(77,34,'77:34','Дар он рӯз вой бар такзибкунандагон!');
INSERT INTO "pioneers" VALUES(77,35,'77:35','Ин [ҳамон] рӯзест, ки сухан намегӯянд');
INSERT INTO "pioneers" VALUES(77,36,'77:36','Ва ба онҳо иҷозат дода намешавад, то узрхоҳӣ кунанд');
INSERT INTO "pioneers" VALUES(77,37,'77:37','Дар он рӯз, вой бар такзибкунандагон!');
INSERT INTO "pioneers" VALUES(77,38,'77:38','Ин [ҳамон] рӯзи ҷудоӣ [ва доварӣ] аст, ки шумо ва гузаштагонро гирд овардаем');
INSERT INTO "pioneers" VALUES(77,39,'77:39','Пас, агар [ҳила ва] найранге доред, онро дар ҳаққи ман ба кор гиред');
INSERT INTO "pioneers" VALUES(77,40,'77:40','Дар он рӯз вой бар такзибкунандагон!');
INSERT INTO "pioneers" VALUES(77,41,'77:41','Ба ростӣ, ки [дар он рӯз] парҳезкорон дар сояҳо ва [канори] чашмаҳо қарор доранд');
INSERT INTO "pioneers" VALUES(77,42,'77:42','Ва меваҳое, ки майл дошта бошанд');
INSERT INTO "pioneers" VALUES(77,43,'77:43','[Ба онҳо гуфта мешавад] «Ба подоши он чи мекардед, гуворо бихӯред ва биёшомед»');
INSERT INTO "pioneers" VALUES(77,44,'77:44','Мо ин гуна накукоронро подош медиҳем»');
INSERT INTO "pioneers" VALUES(77,45,'77:45','Дар он рӯз вой бар такзибкунандагон!');
INSERT INTO "pioneers" VALUES(77,46,'77:46','«[Эй кофирон, дар ин дунё] андаке бихӯред ва баҳра гиред, зеро шумо гунаҳгоред»');
INSERT INTO "pioneers" VALUES(77,47,'77:47','Дар он рӯз вой бар такзибкунандагон!');
INSERT INTO "pioneers" VALUES(77,48,'77:48','Ва ҳангоме ки ба онҳо гуфта шавад: «[Намоз бигузоред ва] рукуъ кунед, [намоз намегузоранд ва] рукуъ намекунанд');
INSERT INTO "pioneers" VALUES(77,49,'77:49','Дар он рӯз вой бар такзибкунандагон!');
INSERT INTO "pioneers" VALUES(77,50,'77:50','[Агар мардум ба ин Қуръон имон намеоваранд] Пас, баъд аз он ба кадом сухан имон меоваранд?');
INSERT INTO "pioneers" VALUES(78,1,'78:1','[Кофирон] Дар бораи чи чиз аз якдигар суол мекунанд?');
INSERT INTO "pioneers" VALUES(78,2,'78:2','Аз хабаре бузург');
INSERT INTO "pioneers" VALUES(78,3,'78:3','Ҳамон [хабаре] ки онҳо дар он ихтилоф доранд');
INSERT INTO "pioneers" VALUES(78,4,'78:4','Чунин нест [ки онҳо меандешанд] ба зудӣ хоҳанд донист');
INSERT INTO "pioneers" VALUES(78,5,'78:5','Боз ҳам чунин нест; ба зудӣ хоҳанд донист');
INSERT INTO "pioneers" VALUES(78,6,'78:6','Оё заминро бистаре [барои осоиши шумо] қарор надодем?');
INSERT INTO "pioneers" VALUES(78,7,'78:7','Ва кӯҳҳоро мехҳо [-и он] қарор надодем?');
INSERT INTO "pioneers" VALUES(78,8,'78:8','Ва шуморо ҷуфт [нару мода] офаридем.');
INSERT INTO "pioneers" VALUES(78,9,'78:9','Ва хоби шуморо [мояи] оромишатон қарор додем');
INSERT INTO "pioneers" VALUES(78,10,'78:10','Ва шабро пӯшише [бароятон] қарор додем');
INSERT INTO "pioneers" VALUES(78,11,'78:11','Ва рӯзро [василаи] зиндагӣ ва [касби маош] қарор додем');
INSERT INTO "pioneers" VALUES(78,12,'78:12','Ва бар фарози шумо ҳафт [осмон] муҳкам бино кардем');
INSERT INTO "pioneers" VALUES(78,13,'78:13','Ва [хуршедро] чароғе дурахшон офаридем');
INSERT INTO "pioneers" VALUES(78,14,'78:14','Ва аз абрҳои боронзо обе фаровон фурӯ фиристодем');
INSERT INTO "pioneers" VALUES(78,15,'78:15','То бад-он дона ва гиёҳ бисёр бирӯёнем');
INSERT INTO "pioneers" VALUES(78,16,'78:16','Ва боғҳое пурдарахт [бо он парвариш диҳем]');
INSERT INTO "pioneers" VALUES(78,17,'78:17','Бе гумон, рӯзи доварӣ ваъдагоҳ [-и Мо бо шумо] аст');
INSERT INTO "pioneers" VALUES(78,18,'78:18','Рӯзе, ки дар «сур» дамида мешавад ва шумо гурӯҳ-гурӯҳ [ба маҳшар] меоед');
INSERT INTO "pioneers" VALUES(78,19,'78:19','Ва осмон кушуда мешавад ва ба сурати дарҳои мутааддид дармеояд');
INSERT INTO "pioneers" VALUES(78,20,'78:20','Ва кӯҳҳо ба ҳаракат дармеояд ва ба сурати саробе мешавад');
INSERT INTO "pioneers" VALUES(78,21,'78:21','Ва бе гумон, ҷаҳаннам камингоҳест');
INSERT INTO "pioneers" VALUES(78,22,'78:22','Ва маҳалли бозгаште барои туғёнгарон');
INSERT INTO "pioneers" VALUES(78,23,'78:23','Муддати замоне дароз дар он ҷо бимонанд');
INSERT INTO "pioneers" VALUES(78,24,'78:24','Дар он ҷо [чизи] хунуке намечашанд ва на ошомиданӣ [-и гуворое хоҳанд дошт]');
INSERT INTO "pioneers" VALUES(78,25,'78:25','Ҷуз обе сӯзон ва [моеъе, ки] чирку хун [аст]');
INSERT INTO "pioneers" VALUES(78,26,'78:26','[Ин] Кайфаре аст муносиб ва дархӯр [-и аъмолашон]');
INSERT INTO "pioneers" VALUES(78,27,'78:27','Чаро ки онҳо ҳеҷ умеде ба [рӯзи] ҳисоб надоштанд');
INSERT INTO "pioneers" VALUES(78,28,'78:28','Ва оёти Моро ба шиддат такзиб карданд');
INSERT INTO "pioneers" VALUES(78,29,'78:29','Ва Мо ҳама чизро [дар Лавҳи маҳфуз] шуморишу сабт кардаем');
INSERT INTO "pioneers" VALUES(78,30,'78:30','Пас, [кайфари аъмоли худро] бичашед, ки чизе ҷуз азоб бар шумо намеафзоем');
INSERT INTO "pioneers" VALUES(78,31,'78:31','Бе гумон, барои парҳезкорон комёбӣ [-и бузурге] аст');
INSERT INTO "pioneers" VALUES(78,32,'78:32','Боғҳои [мева] ва токзорҳо');
INSERT INTO "pioneers" VALUES(78,33,'78:33','Ва ҳуроне ҷавон ва ҳамсиннусол');
INSERT INTO "pioneers" VALUES(78,34,'78:34','Ва ҷомҳое лабрез ва паёпай [аз шароби покизаи биҳишт]');
INSERT INTO "pioneers" VALUES(78,35,'78:35','Дар он ҷо на сухани беҳудае мешунаванд ва на дурӯғе');
INSERT INTO "pioneers" VALUES(78,36,'78:36','[Ин] Подош аз ҷониби Парвардигори туст ва атое аз рӯйи ҳисоб');
INSERT INTO "pioneers" VALUES(78,37,'78:37','[Ҳамон] Парвардигори осмонҳо ва замин ва он чи дар миёни онҳост; [ҳамон Аллоҳи] Раҳмон [ки] ҳеҷ кас [дар он рӯз] ёрои сухан гуфтан бо ӯро надорад');
INSERT INTO "pioneers" VALUES(78,38,'78:38','Рӯзе, ки рӯҳ [Ҷабраил] ва фариштагон ба саф биистанд, ҳеҷ кас сухан нагӯяд, ҷуз касе, ки Аллоҳи раҳмон ба ӯ иҷозат дода бошад ва [ӯ] сухани дуруст [ва савоб] гӯяд');
INSERT INTO "pioneers" VALUES(78,39,'78:39','Он [рӯз] рӯзи ҳақ аст; пас, ҳар ки хоҳад, роҳи бозгаште ба сӯи Парвардигори худ биҷӯяд');
INSERT INTO "pioneers" VALUES(78,40,'78:40','Ба ростӣ, Мо шуморо аз азобе наздик бим додем: рӯзе, ки инсон он чиро аз қабл бо дастҳои худ фиристодааст, мебинад ва кофир мегӯяд: «Эй кош, ман хок будам [ва барои ҳисоб барангехта намешудам!»');
INSERT INTO "pioneers" VALUES(79,1,'79:1','Савганд ба фариштагоне, ки ҷон [-и кофирон]-ро ба шиддат берун мекашанд');
INSERT INTO "pioneers" VALUES(79,2,'79:2','Ва савганд ба фариштагоне, ки ҷон [-и муъминон]-ро ба нармӣ ва осонӣ мегиранд');
INSERT INTO "pioneers" VALUES(79,3,'79:3','Ва савганд ба фариштагоне, ки [ба амри Аллоҳ таоло аз осмон ба сӯйи замин] шиноваранд');
INSERT INTO "pioneers" VALUES(79,4,'79:4','Ва савганд ба фариштагоне, ки [дар иҷрои авомири илоҳӣ] бар якдигар сабқат мегиранд');
INSERT INTO "pioneers" VALUES(79,5,'79:5','Ва савганд ба фариштагоне [ки ба амри илоҳӣ] корҳоро тадбир мекунанд');
INSERT INTO "pioneers" VALUES(79,6,'79:6','Рӯзе, ки [пас аз аввалин дамидан дар сур] замин [ва кӯҳҳо ва ҳама чиз] ба ларза дарояд');
INSERT INTO "pioneers" VALUES(79,7,'79:7','[Ва] Дар пайи он [дамидани дувуми ҳашр] биёяд');
INSERT INTO "pioneers" VALUES(79,8,'79:8','Дар он рӯз дилҳое сахт музтариб [ва тарсон] аст');
INSERT INTO "pioneers" VALUES(79,9,'79:9','Ва чашмҳои онон [аз тарсу шармсорӣ] фуру афтодааст');
INSERT INTO "pioneers" VALUES(79,10,'79:10','[Кофирон дар дунё] Мегӯянд: «Оё мо [пас аз марг дубора] ба ҳоли аввали худ бозгардонда мешавем?');
INSERT INTO "pioneers" VALUES(79,11,'79:11','Оё ҳангоме ки устухонҳои пӯсида шудем [ва ба хок табдил гаштем, боз ҳам зинда мешавем]?»');
INSERT INTO "pioneers" VALUES(79,12,'79:12','Гӯянд: «[Агар чунин ваъдае дуруст бошад] он гоҳ он бозгаште зиёнбор аст»');
INSERT INTO "pioneers" VALUES(79,13,'79:13','Пас он [бозгашт] танҳо бо як бонги боҳайбат аст');
INSERT INTO "pioneers" VALUES(79,14,'79:14','Ва ногаҳон ҳамагӣ бар арсаи замин [-и маҳшар] зоҳир] мешаванд');
INSERT INTO "pioneers" VALUES(79,15,'79:15','[Эй Паёмбар] Оё достони Мӯсо ба ту расидааст?');
INSERT INTO "pioneers" VALUES(79,16,'79:16','Он гоҳ ки Парвардигораш ӯро дар сарзамини муқаддаси «Туво» нидо дод [ва фармуд]');
INSERT INTO "pioneers" VALUES(79,17,'79:17','«Ба сӯи Фиръавн бирав, ки ӯ туғён кардааст');
INSERT INTO "pioneers" VALUES(79,18,'79:18','Ва ба ӯ бигӯ: «Оё мехоҳӣ [аз куфру гуноҳ] пок шавӣ?');
INSERT INTO "pioneers" VALUES(79,19,'79:19','Ва ман туро ба сӯйи Парвардигорат ҳидоят кунам, то [аз Ӯ] битарсӣ [ва фармонбардор шавӣ]?');
INSERT INTO "pioneers" VALUES(79,20,'79:20','Пас, [Мӯсо] муъҷизаи бузургро ба ӯ нишон дод');
INSERT INTO "pioneers" VALUES(79,21,'79:21','Аммо ӯ такзибу саркашӣ кард');
INSERT INTO "pioneers" VALUES(79,22,'79:22','Сипас пушт кард ва ба кӯшишу талош [алайҳи ӯ] пардохт');
INSERT INTO "pioneers" VALUES(79,23,'79:23','Пас, [қавмашро] ҷамъ карду нидо дод');
INSERT INTO "pioneers" VALUES(79,24,'79:24','Ва гуфт: «Ман Парвардигори бартари шумо ҳастам»');
INSERT INTO "pioneers" VALUES(79,25,'79:25','Пас, Аллоҳ таоло ӯро ба азоби охират ва дунё гирифтор кард');
INSERT INTO "pioneers" VALUES(79,26,'79:26','Бе гумон, дар ин [воқеа] барои ҳар кас, ки [аз Аллоҳ таоло] метарсад, ибрате ҳаст');
INSERT INTO "pioneers" VALUES(79,27,'79:27','Оё офариниши шумо [баъд аз марг] сахттар аст ё осмоне, ки [Аллоҳ таоло] онро бино кард?');
INSERT INTO "pioneers" VALUES(79,28,'79:28','Сақфи онро барафрошту ба он шаклу назм дод');
INSERT INTO "pioneers" VALUES(79,29,'79:29','Ва шабашро торик ва рӯзашро равшан гардонид');
INSERT INTO "pioneers" VALUES(79,30,'79:30','Ва заминро баъд аз он густаронид');
INSERT INTO "pioneers" VALUES(79,31,'79:31','Ва аз он об [чашмаҳо ва чоҳҳо ва] чарогоҳашро берун овард');
INSERT INTO "pioneers" VALUES(79,32,'79:32','Ва кӯҳҳоро муҳкам ва устувор [бар рӯйи замин] қарор дод');
INSERT INTO "pioneers" VALUES(79,33,'79:33','[Ҳамаи инҳо] барои баҳрагирии шумо ва чаҳорпоёнатон аст');
INSERT INTO "pioneers" VALUES(79,34,'79:34','Пас, ҳангоме ки [он] ҳодисаи бузург [-и қиёмат] фаро расад');
INSERT INTO "pioneers" VALUES(79,35,'79:35','Дар он рӯз инсон [тамоми аъмолу] талошҳои худро ба ёд меоварад');
INSERT INTO "pioneers" VALUES(79,36,'79:36','Ва ҷаҳаннам барои ҳар бинандае ошкор мешавад');
INSERT INTO "pioneers" VALUES(79,37,'79:37','Пас, он кас, ки туғён [ва саркашӣ] карда бошад');
INSERT INTO "pioneers" VALUES(79,38,'79:38','Ва зиндагии дунёро [бар охират] тарҷеҳ дода бошад');
INSERT INTO "pioneers" VALUES(79,39,'79:39','Бе тардид, ҷаҳаннам ҷойгоҳи ӯст');
INSERT INTO "pioneers" VALUES(79,40,'79:40','Ва аммо касе, ки аз истодан дар ҳузури Парвардигораш бимнок буда ва нафсро аз ҳаво [ва ҳавас] боздошта бошад');
INSERT INTO "pioneers" VALUES(79,41,'79:41','Ҳатман, биҳишт ҷойгоҳи ӯст');
INSERT INTO "pioneers" VALUES(79,42,'79:42','[Эй Паёмбар] Дар бораи қиёмат аз ту мепурсанд, ки дар чи замоне воқеъ мешавад?');
INSERT INTO "pioneers" VALUES(79,43,'79:43','Туро бо ёдоварии ин сухан чи кор аст?');
INSERT INTO "pioneers" VALUES(79,44,'79:44','[Саранҷом ва] Мунтаҳои [илми] он назди Парвардигори туст');
INSERT INTO "pioneers" VALUES(79,45,'79:45','Ту, ҳатман, бимдиҳандаи касоне ҳастӣ, ки аз он метарсанд');
INSERT INTO "pioneers" VALUES(79,46,'79:46','Рӯзе, ки [кофирон қиёматро] бубинанд, [чунин эҳсос мекунанд, ки] гӯӣ [дар дунё] ҷуз як шомгоҳ ё бомдод ба сар набурдаанд');
INSERT INTO "pioneers" VALUES(80,1,'80:1','[Паёмбар] чеҳра дар ҳам кашид ва рӯй баргардонд');
INSERT INTO "pioneers" VALUES(80,2,'80:2','Аз ин ки он нобино [Абдуллоҳ ибни Умми Мактум] наздаш омад');
INSERT INTO "pioneers" VALUES(80,3,'80:3','Ва [эй Паёмбар] ту чи медонӣ? Чи басо ӯ [аз гуноҳонаш] пок шавад');
INSERT INTO "pioneers" VALUES(80,4,'80:4','Ё панд гирад ва ин панд ба судаш бошад');
INSERT INTO "pioneers" VALUES(80,5,'80:5','Аммо он ки [аз имон] бениёзӣ меварзад');
INSERT INTO "pioneers" VALUES(80,6,'80:6','Пас, ту ба ӯ рӯ меоварӣ');
INSERT INTO "pioneers" VALUES(80,7,'80:7','Дар ҳоле ки агар ӯ худро [аз куфр] пок насозад, эроде бар ту нест');
INSERT INTO "pioneers" VALUES(80,8,'80:8','Аммо касе, ки шитобон ба суроғи ту меояд');
INSERT INTO "pioneers" VALUES(80,9,'80:9','Ва [аз Аллоҳ таоло] метарсад');
INSERT INTO "pioneers" VALUES(80,10,'80:10','Пас, ту аз ӯ ғофил мешавӣ [ва ба дигарон мепардозӣ]');
INSERT INTO "pioneers" VALUES(80,11,'80:11','Ҳаргиз чунин нест; бе гумон, ин [оёт барои] тазаккур ва ёдоварӣ аст');
INSERT INTO "pioneers" VALUES(80,12,'80:12','Пас, ҳар ки бихоҳад, аз он панд гирад');
INSERT INTO "pioneers" VALUES(80,13,'80:13','Дар саҳифаҳои арҷманде [сабт] аст');
INSERT INTO "pioneers" VALUES(80,14,'80:14','[Дар ҷойгоҳе] Баландпоя ва покиза [аз палидӣ]');
INSERT INTO "pioneers" VALUES(80,15,'80:15','Дар дасти сафирон [-и ваҳй] аст');
INSERT INTO "pioneers" VALUES(80,16,'80:16','[Ки] бузургвору накукоранд');
INSERT INTO "pioneers" VALUES(80,17,'80:17','Марг бар инсон [-и кофир, ки] чи қадар носипос аст!');
INSERT INTO "pioneers" VALUES(80,18,'80:18','[Аллоҳ таоло] Ӯро аз чи чиз офаридааст?');
INSERT INTO "pioneers" VALUES(80,19,'80:19','Ӯро аз нутфа [-и ночизе] офарид ва сипас мавзунаш сохт');
INSERT INTO "pioneers" VALUES(80,20,'80:20','Он гоҳ роҳро барояш осон намуд');
INSERT INTO "pioneers" VALUES(80,21,'80:21','Он гоҳ [пас аз поёни умр] ӯро миронд ва дар қабр [пинҳон] намуд');
INSERT INTO "pioneers" VALUES(80,22,'80:22','Сипас ҳар гоҳ бихоҳад, ӯро [зинда мекунад ва] бармеангезад');
INSERT INTO "pioneers" VALUES(80,23,'80:23','Ҳаргиз чунин нест [ки ӯ мепиндорад]. Ӯ ҳанӯз он чиро, ки [Аллоҳ таоло] фармон дода, ба ҷой наовардааст');
INSERT INTO "pioneers" VALUES(80,24,'80:24','Пас, инсон бояд ба ғизои хеш [ва офариниши он] бингарад');
INSERT INTO "pioneers" VALUES(80,25,'80:25','Мо оби фаровон [аз осмон] фуру рехтем');
INSERT INTO "pioneers" VALUES(80,26,'80:26','Сипас заминро аз ҳам шикофтем');
INSERT INTO "pioneers" VALUES(80,27,'80:27','Он гоҳ дар он дона [-ҳои фаровон] рӯёнидем');
INSERT INTO "pioneers" VALUES(80,28,'80:28','Ва ангуру сабзӣ [хӯрокии бисёр]');
INSERT INTO "pioneers" VALUES(80,29,'80:29','Ва зайтуну нахл');
INSERT INTO "pioneers" VALUES(80,30,'80:30','Ва боғҳое [анбуҳ ва] пурдарахт');
INSERT INTO "pioneers" VALUES(80,31,'80:31','Ва [анвои] мева ва алаф [падид овардем]');
INSERT INTO "pioneers" VALUES(80,32,'80:32','[Ҳамаи инҳо] Барои баҳрагирии шумо ва чаҳорпоёнатон аст');
INSERT INTO "pioneers" VALUES(80,33,'80:33','Пас, ҳангоме ки [он] садои боҳайбати [-и қиёмат] фаро расад');
INSERT INTO "pioneers" VALUES(80,34,'80:34','Рӯзе, ки инсон аз бародараш мегурезад');
INSERT INTO "pioneers" VALUES(80,35,'80:35','Ва аз модараш ва аз падараш');
INSERT INTO "pioneers" VALUES(80,36,'80:36','Ва аз занаш ва писаронаш [низ мегурезад]');
INSERT INTO "pioneers" VALUES(80,37,'80:37','Дар он рӯз ҳар касро корест, ки ӯро ба худ машғул медорад');
INSERT INTO "pioneers" VALUES(80,38,'80:38','Чеҳраҳое дар он рӯз кушода ва равшан аст');
INSERT INTO "pioneers" VALUES(80,39,'80:39','[Ба хотири неъмату раҳмати Аллоҳ таоло] хандону шод аст');
INSERT INTO "pioneers" VALUES(80,40,'80:40','Ва чеҳраҳое дар он рӯз ғуборолуд аст');
INSERT INTO "pioneers" VALUES(80,41,'80:41','Сиёҳӣ [ва торикӣ] онҳоро пӯшонидааст');
INSERT INTO "pioneers" VALUES(80,42,'80:42','Инон ҳамон кофирони бадкирдоранд');
INSERT INTO "pioneers" VALUES(81,1,'81:1','Он гоҳ ки хуршед дар ҳам печида [ва торик] гардад');
INSERT INTO "pioneers" VALUES(81,2,'81:2','Ва он гоҳ ки ситорагон бефурӯғ шаванд');
INSERT INTO "pioneers" VALUES(81,3,'81:3','Ва он гоҳ ки кӯҳҳо ба ҳаракат дароянд');
INSERT INTO "pioneers" VALUES(81,4,'81:4','ва он гоҳ ки модашутурони бордор раҳо шаванд');
INSERT INTO "pioneers" VALUES(81,5,'81:5','Ва он гоҳ ки ҳайвоноти ваҳшӣ [дар канори инсон] гирд оварда шаванд');
INSERT INTO "pioneers" VALUES(81,6,'81:6','Ва он гоҳ ки дарёҳо [ҷӯшону] барафрӯхта шаванд');
INSERT INTO "pioneers" VALUES(81,7,'81:7','Ва он гоҳ ки ҳар кас бо ҳамсони худ қарин гардад');
INSERT INTO "pioneers" VALUES(81,8,'81:8','Ва он гоҳ ки аз духтари зиндабагуршуда пурсида шавад');
INSERT INTO "pioneers" VALUES(81,9,'81:9','[Ки] Ба кадомин гуноҳ кушта шудааст');
INSERT INTO "pioneers" VALUES(81,10,'81:10','Ва он гоҳ ки номаҳо [-и аъмол] кушода шавад');
INSERT INTO "pioneers" VALUES(81,11,'81:11','Ва он гоҳ ки парда аз рӯи осмон баргирифта шавад');
INSERT INTO "pioneers" VALUES(81,12,'81:12','Ва он гоҳ ки дузах афрӯхта [ва шуълавар] гардад');
INSERT INTO "pioneers" VALUES(81,13,'81:13','Ва он гоҳ ки биҳишт [барои парҳезкорон] наздик оварда шавад');
INSERT INTO "pioneers" VALUES(81,14,'81:14','[Дар он ҳангом] Ҳар кас хоҳад донист, ки чи чизе [барои охирати худ] омода кардааст');
INSERT INTO "pioneers" VALUES(81,15,'81:15','Пас, савганд ба ситорагоне, ки [ба ҷойгоҳи худ] бозмегарданд');
INSERT INTO "pioneers" VALUES(81,16,'81:16','[Шитобон] Ҳаракат мекунанд [ва аз назар] пинҳон мешаванд');
INSERT INTO "pioneers" VALUES(81,17,'81:17','Ва савганд ба шаб, ҳангоме ки [торикиаш коста шавад ва] пушт кунад');
INSERT INTO "pioneers" VALUES(81,18,'81:18','Ва савганд ба субҳ, ҳангоме ки бидамад');
INSERT INTO "pioneers" VALUES(81,19,'81:19','Бе тардид, ин [Қуръон сухани Аллоҳ таоло аст, ки иблоғаш ба Паёмбар ба уҳдаи] фиристодаи бузургвор [Ҷабраил] аст');
INSERT INTO "pioneers" VALUES(81,20,'81:20','[Ҳамон фариштае] Ки [муқтадир ва] неруманд аст ва назди [Аллоҳи] соҳиби Арш мақоми волое дорад');
INSERT INTO "pioneers" VALUES(81,21,'81:21','[Дар малакути аъло ва олами фариштагон] Аз ӯ фармон мебаранд ва амин [-и ваҳй] аст');
INSERT INTO "pioneers" VALUES(81,22,'81:22','[Эй мардум] Ҳамнишини шумо [Муҳаммад] девона нест');
INSERT INTO "pioneers" VALUES(81,23,'81:23','Ва бе тардид, [Муҳаммад] ӯ [Ҷабраил]-ро дар уфуқи равшан дидааст');
INSERT INTO "pioneers" VALUES(81,24,'81:24','Ва ӯ бар [иблоғи он чи аз тариқи] ғайб [ба вай ваҳй мегардад] бахил нест');
INSERT INTO "pioneers" VALUES(81,25,'81:25','Ва ин [Қуръон] гуфтаи шайтони рондашуда нест');
INSERT INTO "pioneers" VALUES(81,26,'81:26','Пас, ба куҷо меравед?');
INSERT INTO "pioneers" VALUES(81,27,'81:27','Ин [Қуръон] ҷуз панд барои ҷаҳониён [инсон ва ҷин] нест');
INSERT INTO "pioneers" VALUES(81,28,'81:28','Барои ҳар як аз шумо, ки бихоҳад роҳи рости [ҳақро] дар пеш гирад');
INSERT INTO "pioneers" VALUES(81,29,'81:29','Ва шумо [чизеро] намехоҳед, магар он ки Парвардигори ҷаҳониён [ирода кунад ва] бихоҳад');
INSERT INTO "pioneers" VALUES(82,1,'82:1','Он гоҳ ки осмон шикофта шавад');
INSERT INTO "pioneers" VALUES(82,2,'82:2','Он гоҳ ки ситорагон пароканда шаванд [ва фуру резанд]');
INSERT INTO "pioneers" VALUES(82,3,'82:3','Ва он гоҳ ки дарёҳо [ба ҳам омезанд ва] равон гарданд');
INSERT INTO "pioneers" VALUES(82,4,'82:4','Ва он гоҳ ки қабрҳо зеру рӯ шавад');
INSERT INTO "pioneers" VALUES(82,5,'82:5','[Дар он ҳангом] Ҳар кас медонад, чи чизҳоеро пешопеш фиристода ва чи чизҳоеро вопас ниҳода [ва бар ҷой гузоштааст]');
INSERT INTO "pioneers" VALUES(82,6,'82:6','Эй инсон, чи чизе туро [нисбат] ба Парвардигори каримат мағрур сохтааст?');
INSERT INTO "pioneers" VALUES(82,7,'82:7','Он [Парвардигоре], ки туро офарид ва сипас сару сомонат дод ва он гоҳ мутаодил ва муносибат кардааст');
INSERT INTO "pioneers" VALUES(82,8,'82:8','Ва ба ҳар шакле [ва сурате], ки хост, туро таркиб намуд');
INSERT INTO "pioneers" VALUES(82,9,'82:9','Ҳаргиз чунин нест [ки шумо гумон мекунед]; балки шумо рӯзи ҷазоро такзиб мекунед');
INSERT INTO "pioneers" VALUES(82,10,'82:10','Ва бе гумон, нигоҳбононе [аз фариштагон] бар шумо гуморида шудаанд');
INSERT INTO "pioneers" VALUES(82,11,'82:11','Ки нависандагоне бузургворанд');
INSERT INTO "pioneers" VALUES(82,12,'82:12','Он чиро анҷом медиҳед, медонанд [ва аъмоли неку бади шуморо сабт мекунанд]');
INSERT INTO "pioneers" VALUES(82,13,'82:13','Мусалламан, накукорон дар неъмат [-ҳои биҳишт] ҳастанд');
INSERT INTO "pioneers" VALUES(82,14,'82:14','Ва яқинан бадкорон дар [оташи] ҷаҳаннаманд');
INSERT INTO "pioneers" VALUES(82,15,'82:15','Рӯзи ҷазо ба он дароянд [ва бисӯзанд]');
INSERT INTO "pioneers" VALUES(82,16,'82:16','Ва ҳаргиз онон аз он ҷо ба дур ва [дар амон] набошанд');
INSERT INTO "pioneers" VALUES(82,17,'82:17','Ва ту чи донӣ, ки рӯзи ҷазо чист?');
INSERT INTO "pioneers" VALUES(82,18,'82:18','Боз ту чи медонӣ, ки рӯзи ҷазо чист?');
INSERT INTO "pioneers" VALUES(82,19,'82:19','Рӯзе, ки ҳеҷ кас қодир ба анҷоми коре барои дигаре нест; ва дар он рӯзи ҳукм [ва фармон] аз они Аллоҳ таоло аст');
INSERT INTO "pioneers" VALUES(83,1,'83:1','Вой бар камфурӯшон!');
INSERT INTO "pioneers" VALUES(83,2,'83:2','Касоне, ки чун [барои худ] аз мардум паймона мекунанд, ҳаққи худро комил мегиранд');
INSERT INTO "pioneers" VALUES(83,3,'83:3','Ва ҳангоме ки [мехоҳанд] барои онон паймона ё вазн кунанд, кам мегузоранд');
INSERT INTO "pioneers" VALUES(83,4,'83:4','Оё онҳо гумон намекунанд, ки [қиёмат фаро мерасад ва аз қабрҳо] барангехта мешаванд');
INSERT INTO "pioneers" VALUES(83,5,'83:5','Дар рӯзе бузург?');
INSERT INTO "pioneers" VALUES(83,6,'83:6','[Ҳамон] Рӯзе, ки мардум дар пешгоҳи Парвардигори ҷаҳониён меистанд');
INSERT INTO "pioneers" VALUES(83,7,'83:7','Ҳаргиз чунин нест [ки кофирон пиндоштаанд]! Ба ростӣ, ки номаи [аъмоли] бадкорон дар сиҷҷин аст');
INSERT INTO "pioneers" VALUES(83,8,'83:8','Ва ту чи донӣ, ки сиҷҷин чист?');
INSERT INTO "pioneers" VALUES(83,9,'83:9','Китобест, ки [аъмоли бадкорон дар он] навишта шудааст');
INSERT INTO "pioneers" VALUES(83,10,'83:10','Дар он рӯз вой бар такзибкунандагон!');
INSERT INTO "pioneers" VALUES(83,11,'83:11','Онон, ки рӯзи ҷазоро [инкор ва] такзиб мекунанд');
INSERT INTO "pioneers" VALUES(83,12,'83:12','Ва ҷуз ситамкорони гунаҳкор [касе] он [рӯз]-ро такзиб намекунад');
INSERT INTO "pioneers" VALUES(83,13,'83:13','Ҳангоме ки оёти Мо бар ӯ тиловат шавад, гӯяд: «[Ин] Афсонаҳои гузаштагон аст»');
INSERT INTO "pioneers" VALUES(83,14,'83:14','Ҳаргиз чунин нест [ки онҳо гумон мекунанд]; балки [ба сабаби] он чӣ кардаанд, бар дилҳояшон зангор бастааст');
INSERT INTO "pioneers" VALUES(83,15,'83:15','Ҳаргиз чунин нест [ки онҳо мепиндоранд]. Бе гумон, онон дар он рӯз аз [дидори] Парвардигорашон маҳруманд');
INSERT INTO "pioneers" VALUES(83,16,'83:16','Ва [баъд аз ҳисоб] мусалламан вориди дузах мешаванд');
INSERT INTO "pioneers" VALUES(83,17,'83:17','Онгоҳ ба эшон гуфта мешавад: «Ин [оташ] ҳамон чизест, ки онро такзиб мекардед»');
INSERT INTO "pioneers" VALUES(83,18,'83:18','Ҳаргиз чунин нест [ки онҳо мепиндоранд]; бе гумон, номаи [аъмоли] накукорон дар «илиййин» аст');
INSERT INTO "pioneers" VALUES(83,19,'83:19','Ва ту чи донӣ, ки «илиййин» чист?');
INSERT INTO "pioneers" VALUES(83,20,'83:20','Китобест, ки [аъмоли бадкорон дар он] навишта шудааст');
INSERT INTO "pioneers" VALUES(83,21,'83:21','Ки муқаррабони [даргоҳи илоҳӣ] бар он ҳозир шаванд [ва гувоҳӣ диҳанд]');
INSERT INTO "pioneers" VALUES(83,22,'83:22','Ҳамоно накукорон дар неъмат [-ҳои биҳишт] ҳастанд');
INSERT INTO "pioneers" VALUES(83,23,'83:23','Бар тахтҳо [такя зада ва] менигаранд');
INSERT INTO "pioneers" VALUES(83,24,'83:24','Хуррамӣ ва нишоти неъматро дар чеҳраҳояшон [мебинӣ ва] мешиносӣ');
INSERT INTO "pioneers" VALUES(83,25,'83:25','Онҳо аз шароби ноби муҳршуда [нӯшонида ва] сероб мешаванд');
INSERT INTO "pioneers" VALUES(83,26,'83:26','Муҳре, ки бар он ниҳодашуда аз мушк аст ва дар ин [шароб ва дигар неъматҳои биҳиштӣ] муштоқон [-и раҳмат] бояд бар якдигар пешӣ гиранд');
INSERT INTO "pioneers" VALUES(83,27,'83:27','Ва омезааш аз тасним аст');
INSERT INTO "pioneers" VALUES(83,28,'83:28','[Ҳамон] чашмае, ки муқаррабон [-и даргоҳи илоҳӣ] аз он менӯшанд');
INSERT INTO "pioneers" VALUES(83,29,'83:29','Ҳамоно касоне, ки ҷурму гуноҳ карданд, пайваста [дар дунё] бар касоне, ки имон оварда буданд, механдиданд');
INSERT INTO "pioneers" VALUES(83,30,'83:30','Ва ҳар гоҳ [муъминон] аз канорашон мегузаштанд, бо чашму абрӯ ба ҳам ишора мекарданд [ва ононро ба сухрия мегирифтанд]');
INSERT INTO "pioneers" VALUES(83,31,'83:31','Ва чун ба сӯйи хонаводаи худ бозмегаштанд, [ба хотири тамасхури муъминон] шодмону хандон буданд');
INSERT INTO "pioneers" VALUES(83,32,'83:32','Ва ҳангоме ки муъминонро медиданд, мегуфтанд: «Бе гумон, инон гумроҳонанд»');
INSERT INTO "pioneers" VALUES(83,33,'83:33','Дар ҳоле ки онон барои муроқибат [ва нигаҳбонӣ] бар муъминон фиристода нашуда буданд');
INSERT INTO "pioneers" VALUES(83,34,'83:34','Вале имрӯз касоне, ки имон овардаанд, ба кофирон механданд');
INSERT INTO "pioneers" VALUES(83,35,'83:35','Бар тахтҳо [-и ороста нишаста ва ба неъматҳои илоҳӣ] менигаранд');
INSERT INTO "pioneers" VALUES(83,36,'83:36','Оё кофирон [бо чашидани азоб] ҷазои он чиро, ки мекарданд, дарёфт намудаанд?');
INSERT INTO "pioneers" VALUES(84,1,'84:1','Он гоҳ ки осмон шикофта шавад');
INSERT INTO "pioneers" VALUES(84,2,'84:2','Ва ба [фармони] Парвардигораш гӯш фаро диҳад [ва таслим шавад]; ва сазовор аст [ки чунин кунад]');
INSERT INTO "pioneers" VALUES(84,3,'84:3','Ва он гоҳ ки замин густурда [ва ҳамвор] шавад');
INSERT INTO "pioneers" VALUES(84,4,'84:4','Ва ҳар чиро даруни худ дорад, берун резад ва тиҳӣ гардад');
INSERT INTO "pioneers" VALUES(84,5,'84:5','Ва ба [фармони] Парвардигораш гӯш фаро диҳад [ва таслим шавад]; ва сазовор аст [ки чунин кунад]');
INSERT INTO "pioneers" VALUES(84,6,'84:6','Эй инсон, бе гумон, ту дар роҳи [расидан ба] Парвардигорат сахт талошу кӯшиш мекунӣ; пас, Ӯро мулоқот хоҳӣ кард');
INSERT INTO "pioneers" VALUES(84,7,'84:7','Ва аммо касе, ки нома [-и аъмолаш] ба дасти росташ дода шавад');
INSERT INTO "pioneers" VALUES(84,8,'84:8','Ба зудӣ ба ҳисобе осон муҳосиба мешавад');
INSERT INTO "pioneers" VALUES(84,9,'84:9','Ва шодмон ба сӯйи хонаводааш бозмегардад');
INSERT INTO "pioneers" VALUES(84,10,'84:10','Ва аммо касе, ки номаи [аъмолаш] аз пушти сараш ба ӯ дода шавад');
INSERT INTO "pioneers" VALUES(84,11,'84:11','Пас, ҳалоки худро хоҳад талабид');
INSERT INTO "pioneers" VALUES(84,12,'84:12','Ва ба [оташи] ҷаҳаннами шуълавар хоҳад даромад');
INSERT INTO "pioneers" VALUES(84,13,'84:13','Бе гумон, ӯ дар [дунё ба хотири гуноҳонаш] миёни хонаводаи худ шодмон буд');
INSERT INTO "pioneers" VALUES(84,14,'84:14','Ӯ гумон мекард, ки ҳаргиз [ба назди Аллоҳ таоло] бознахоҳад гашт');
INSERT INTO "pioneers" VALUES(84,15,'84:15','Оре, ҳамоно Парвардигораш аз ҳоли вай огоҳ буд');
INSERT INTO "pioneers" VALUES(84,16,'84:16','Пас, ба шафақ савганд мехӯрам');
INSERT INTO "pioneers" VALUES(84,17,'84:17','Ва савганд ба шаб ва он чиро фуру мепӯшад');
INSERT INTO "pioneers" VALUES(84,18,'84:18','Ва савганд ба моҳ чун [фурӯғаш] комил шавад');
INSERT INTO "pioneers" VALUES(84,19,'84:19','Ҳатман, маротибу ҳолоти мухталиферо яке пас аз дигаре [дар масири зиндагии дунявӣ] тай хоҳед кард');
INSERT INTO "pioneers" VALUES(84,20,'84:20','Пас, ононро чи шуда, ки имон намеоваранд?');
INSERT INTO "pioneers" VALUES(84,21,'84:21','Ва [чаро] ҳангоме ки Қуръон бар онон тиловат шавад, саҷда намекунанд?');
INSERT INTO "pioneers" VALUES(84,22,'84:22','Балки касоне, ки кофир шуданд, пайваста [оёти илоҳиро] такзиб мекунанд');
INSERT INTO "pioneers" VALUES(84,23,'84:23','Ва Аллоҳ таоло ба он чи дар дил пинҳон медоранд, донотар аст');
INSERT INTO "pioneers" VALUES(84,24,'84:24','Пас, [эй паёмбар] ононро ба азоби дардноке башорат деҳ');
INSERT INTO "pioneers" VALUES(84,25,'84:25','Магар касоне, ки имон овардаанд ва корҳои шоиста анҷом додаанд, ки барои онон подоше беминнат ва пайваста аст');
INSERT INTO "pioneers" VALUES(85,1,'85:1','Савганд ба осмон, ки дорандаи бурҷҳост');
INSERT INTO "pioneers" VALUES(85,2,'85:2','Ва савганд ба рӯзи мавъуд [қиёмат]');
INSERT INTO "pioneers" VALUES(85,3,'85:3','Ва савганд ба ҳар ки гувоҳӣ диҳад ва ба ҳар ки мавриди гувоҳӣ қарор гирад');
INSERT INTO "pioneers" VALUES(85,4,'85:4','Марг бар одамсӯзони хандақ!');
INSERT INTO "pioneers" VALUES(85,5,'85:5','[Ҳамон оташи] Афрӯхта аз ҳезумҳои бисёр [ки муъминонро дар он месӯзонданд];');
INSERT INTO "pioneers" VALUES(85,6,'85:6','ҳангоме ки бар [канораи] он нишаста буданд');
INSERT INTO "pioneers" VALUES(85,7,'85:7','Ва он чиро бо муъминон анҷом медоданд, тамошо мекарданд');
INSERT INTO "pioneers" VALUES(85,8,'85:8','Ва ҳеҷ эроде аз онон нагирифтанд, ҷуз ин ки ба Аллоҳи пирӯзманди сутуда имон оварда буданд');
INSERT INTO "pioneers" VALUES(85,9,'85:9','[Ҳамон] Касе, ки фармонравоии осмонҳо ва замин аз они Ӯст; ва Аллоҳ таоло бар ҳама чиз гувоҳ аст');
INSERT INTO "pioneers" VALUES(85,10,'85:10','Ба ростӣ, касоне, ки мардону занони муъминро шиканҷа [ва озор] доданд ва пас [аз анҷоми он кор] тавба накарданд, пас, барои эшон азоби ҷаҳаннам [муҳайё] аст ва азоби оташ [дар пеш доранд]');
INSERT INTO "pioneers" VALUES(85,11,'85:11','Ҳамоно барои касоне, ки имон оварданд ва корҳои шоиста карданд, боғҳое [аз биҳишт] аст, ки ҷӯйборҳо зери [дарахтони] он ҷорӣ аст; ва ин аст комёбии бузург');
INSERT INTO "pioneers" VALUES(85,12,'85:12','Бе гумон, [муҷозоту] сахтгирии Парвардигорат бисёр шадид аст');
INSERT INTO "pioneers" VALUES(85,13,'85:13','Дар ҳақиқат, Ӯст, ки [офаринишро] оғоз мекунад ва дубора [баъд аз марг] бозмегардонад');
INSERT INTO "pioneers" VALUES(85,14,'85:14','Ва ҳам Ӯ омурзанда [ва] дӯстор [-и муъминон] аст');
INSERT INTO "pioneers" VALUES(85,15,'85:15','[Ӯ Аллоҳ таоло] Соҳиби Арш, [ва] баландмартаба [ва шукӯҳманд] аст');
INSERT INTO "pioneers" VALUES(85,16,'85:16','[Ва] Он чиро бихоҳад, анҷом медиҳад');
INSERT INTO "pioneers" VALUES(85,17,'85:17','[Эй Паёмбар] Оё достони лашкариён [-и ҳақситез] ба ту расидааст?');
INSERT INTO "pioneers" VALUES(85,18,'85:18','[Лашкари] Фиръавн ва [қавми] Самуд');
INSERT INTO "pioneers" VALUES(85,19,'85:19','Балки касоне, ки кофир шуданд, пайваста дар такзиб [-и ҳақ] ҳастанд');
INSERT INTO "pioneers" VALUES(85,20,'85:20','Ва Аллоҳ таоло аз ҳар сӯ бар онон иҳота дорад');
INSERT INTO "pioneers" VALUES(85,21,'85:21','Оре, ин [оёт шеъру сеҳр нест, балки] Қуръони шукӯҳманд аст');
INSERT INTO "pioneers" VALUES(85,22,'85:22','Дар Лавҳи маҳфуз [аз ҳар гуна таҳрифу табдил дар амон] аст');
INSERT INTO "pioneers" VALUES(86,1,'86:1','Савганд ба осмон ва савганд ба ситораи шабгард');
INSERT INTO "pioneers" VALUES(86,2,'86:2','Ва чи медонӣ, ки ситораи шабгард чист?');
INSERT INTO "pioneers" VALUES(86,3,'86:3','[Ҳамон] Ситораи фурӯзон аст');
INSERT INTO "pioneers" VALUES(86,4,'86:4','Ҳеҷ кас нест магар он ки бар ӯ нигаҳбоне [аз фариштагон гумошта шуда] аст');
INSERT INTO "pioneers" VALUES(86,5,'86:5','Инсон бояд бингарад, ки аз чӣ чиз офарида шудааст');
INSERT INTO "pioneers" VALUES(86,6,'86:6','Аз як оби ҷаҳанда офарида шудааст');
INSERT INTO "pioneers" VALUES(86,7,'86:7','Ки аз миёни [устухони] пушт [-и мард] ва сина [-и зан] берун меояд');
INSERT INTO "pioneers" VALUES(86,8,'86:8','Бе гумон, Аллоҳ таоло бар бозгардондани ӯ [пас аз марг] тавоност');
INSERT INTO "pioneers" VALUES(86,9,'86:9','Рӯзе, ки розҳо ошкор гардад');
INSERT INTO "pioneers" VALUES(86,10,'86:10','[Дар он рӯз] Инсон ҳеҷ тавону ёваре надорад');
INSERT INTO "pioneers" VALUES(86,11,'86:11','Савганд ба осмони пурборон');
INSERT INTO "pioneers" VALUES(86,12,'86:12','Ва савганд ба замини пуршикоф [ки гиёҳон аз он сар бармеоваранд]');
INSERT INTO "pioneers" VALUES(86,13,'86:13','Бе гумон, ин Қуръон сухани ҷудокунандаи ҳақ аз ботил аст');
INSERT INTO "pioneers" VALUES(86,14,'86:14','Ва [сухани] ҳазлу беҳуда нест');
INSERT INTO "pioneers" VALUES(86,15,'86:15','Онон [кофирон] пайваста ҳила ва найранг мекунанд');
INSERT INTO "pioneers" VALUES(86,16,'86:16','Ва ман [низ] ҳила [ва тадбир] мекунам');
INSERT INTO "pioneers" VALUES(86,17,'86:17','Пас, кофиронро муҳлат деҳ, [ва] андаке раҳояшон кун [то ғарқи гуноҳ бошанд]');
INSERT INTO "pioneers" VALUES(87,1,'87:1','Номи Парвардигори баландмартабаатро ба покӣ ёд кун');
INSERT INTO "pioneers" VALUES(87,2,'87:2','[Ҳамон] ки [инсонро] офарид ва ҳамоҳанг ва мутаодил сохт');
INSERT INTO "pioneers" VALUES(87,3,'87:3','Ва [ҳамон] касе, ки андозагирӣ кард ва [ба шоистагӣ офарид] ва сипас ҳидоят намуд');
INSERT INTO "pioneers" VALUES(87,4,'87:4','Ва [он] касе, ки чарогоҳро [барои тағзияи ҷондорон] рӯёнид');
INSERT INTO "pioneers" VALUES(87,5,'87:5','Сипас онро хушку сиёҳ гардонид');
INSERT INTO "pioneers" VALUES(87,6,'87:6','Мо ба зудӣ [Қуръонро] бар ту мехонем; пас, ту ҳаргиз [онро] фаромӯш нахоҳӣ кард');
INSERT INTO "pioneers" VALUES(87,7,'87:7','Магар он чиро, ки Аллоҳ таоло бихоҳад; ҳамоно Ӯ ошкору ниҳонро медонад');
INSERT INTO "pioneers" VALUES(87,8,'87:8','Ва барои ту осонтарин [роҳ]-ро фароҳам мегардонем');
INSERT INTO "pioneers" VALUES(87,9,'87:9','Пас, агар панди муфид бошад, [мардумро бо оёти Қуръон] панд деҳ');
INSERT INTO "pioneers" VALUES(87,10,'87:10','Ба зудӣ, касе, ки [аз Аллоҳ таоло] метарсад, панд мепазирад');
INSERT INTO "pioneers" VALUES(87,11,'87:11','Ва бадбахттарин [-и мардум] аз он дурӣ мегузинад');
INSERT INTO "pioneers" VALUES(87,12,'87:12','[Ҳамон] Касе, ки дар оташи бузург [-и ҷаҳаннам] дархоҳад омад');
INSERT INTO "pioneers" VALUES(87,13,'87:13','Ва дар он [оташ] на мемирад ва на зинда мемонад');
INSERT INTO "pioneers" VALUES(87,14,'87:14','Яқинан, касе, ки худро [аз куфру гуноҳ] пок кунад, растагор хоҳад шуд');
INSERT INTO "pioneers" VALUES(87,15,'87:15','Ва [низ касе, ки] номи Парвардигорашро ёд кунад ва намоз бигузорад');
INSERT INTO "pioneers" VALUES(87,16,'87:16','Вале шумо [мардум] зиндагии дунёро [бар охират] тарҷеҳ медиҳед');
INSERT INTO "pioneers" VALUES(87,17,'87:17','Дар ҳоле ки охират беҳтар ва пояндатар аст');
INSERT INTO "pioneers" VALUES(87,18,'87:18','Ба ростӣ, ин [пандҳо] дар китобҳои осмонии пешин [низ] буд');
INSERT INTO "pioneers" VALUES(87,19,'87:19','Китобҳои Иброҳим ва Мӯсо');
INSERT INTO "pioneers" VALUES(88,1,'88:1','[Эй Паёмбар] Оё хабари қиёмати ҳавлноки фарогир ба ту расидааст?');
INSERT INTO "pioneers" VALUES(88,2,'88:2','Дар он рӯз чеҳраҳое хору залил хоҳанд буд');
INSERT INTO "pioneers" VALUES(88,3,'88:3','[Дар дунё] Пайваста талош карда [ва] хаста шуда [ва чун дар роҳи ҳақ набуда, натиҷае надидаанд]');
INSERT INTO "pioneers" VALUES(88,4,'88:4','Дар оташи сӯзон дармеоянд');
INSERT INTO "pioneers" VALUES(88,5,'88:5','Аз чашмаи бисёр гарм ба онҳо нӯшонда мешаванд');
INSERT INTO "pioneers" VALUES(88,6,'88:6','Ғизое ҷуз хори хушку талх надоранд');
INSERT INTO "pioneers" VALUES(88,7,'88:7','Ки на [ононро] фарбеҳ кунад ва на гуруснагиро дафъ менамояд');
INSERT INTO "pioneers" VALUES(88,8,'88:8','Дар он рӯз чеҳраҳое шодобу тоза ҳастанд');
INSERT INTO "pioneers" VALUES(88,9,'88:9','Ва аз саъй [-ю талош]-и худ хушнуданд');
INSERT INTO "pioneers" VALUES(88,10,'88:10','Дар биҳишти барин ҳастанд');
INSERT INTO "pioneers" VALUES(88,11,'88:11','Дар он ҷо ҳеҷ сухани беҳудае намешунаванд');
INSERT INTO "pioneers" VALUES(88,12,'88:12','Дар он чашма [-ҳои об] равон бошад');
INSERT INTO "pioneers" VALUES(88,13,'88:13','Дар он тахтҳои [зебои] баланд [поя] аст');
INSERT INTO "pioneers" VALUES(88,14,'88:14','Ва ҷомҳое [ки дар канори чашмаҳо] ниҳода шуда');
INSERT INTO "pioneers" VALUES(88,15,'88:15','Ва болиштҳои [муназзам] чидашуда');
INSERT INTO "pioneers" VALUES(88,16,'88:16','Ва [низ] фаршҳои гаронбаҳо густурда шудааст');
INSERT INTO "pioneers" VALUES(88,17,'88:17','Оё [кофирон] ба шутур наменигаранд, ки чи гуна офарида шудааст?');
INSERT INTO "pioneers" VALUES(88,18,'88:18','Ва ба осмон [наменигаранд], ки чи гуна барафрошта шудааст?');
INSERT INTO "pioneers" VALUES(88,19,'88:19','Ва ба кӯҳҳо [наменигаранд], ки чи гуна [муҳкам бар замин] насб шудааст?');
INSERT INTO "pioneers" VALUES(88,20,'88:20','Ва ба замин [наменигаранд], ки чи гуна густурда шудааст?');
INSERT INTO "pioneers" VALUES(88,21,'88:21','Пас, [эй Паёмбар] панд бидеҳ, ки ту танҳо панддиҳандаӣ');
INSERT INTO "pioneers" VALUES(88,22,'88:22','Ту бар онон мусаллат [-у чира] нестӣ [ки бар имон водорашон кунӣ]');
INSERT INTO "pioneers" VALUES(88,23,'88:23','Аммо касе, ки рӯй бигардонад ва кофир шавад');
INSERT INTO "pioneers" VALUES(88,24,'88:24','Сипас Аллоҳ таоло ӯро бо бузургтарин азоб муҷозот мекунад');
INSERT INTO "pioneers" VALUES(88,25,'88:25','Ҳамоно бозгашти онҳо ба сӯйи Мост');
INSERT INTO "pioneers" VALUES(88,26,'88:26','Сипас бе гумон, ҳисобашон [низ] бо Мост');
INSERT INTO "pioneers" VALUES(89,1,'89:1','Савганд ба субҳ');
INSERT INTO "pioneers" VALUES(89,2,'89:2','Ва ба шабҳои даҳгона [-и оғози зулҳиҷҷа]');
INSERT INTO "pioneers" VALUES(89,3,'89:3','Ва ба завҷ ва фард [-и ашё]');
INSERT INTO "pioneers" VALUES(89,4,'89:4','Ва савганд ба шаб, ҳангоме ки [ҳаракат мекунад ва] меравад');
INSERT INTO "pioneers" VALUES(89,5,'89:5','Оё дар ин [чизҳо] савганде барои соҳибхирад нест [ки ӯро қонеъ намояд]?');
INSERT INTO "pioneers" VALUES(89,6,'89:6','[Эй Паёмбар] Оё надидӣ, ки Парвардигорат бо [қавми] Од чи кард?');
INSERT INTO "pioneers" VALUES(89,7,'89:7','[Ҳамон қабилаи] Ирам, ки дорои [кохҳои бо] сутунҳои баланд буданд');
INSERT INTO "pioneers" VALUES(89,8,'89:8','Ки ҳаммонанди он дар шаҳрҳо офарида нашуда буд?');
INSERT INTO "pioneers" VALUES(89,9,'89:9','Ва [низ қавми] Самуд, онҳое, ки сахраҳои сахтро аз [канори] водӣ метарошиданд [ва барои худ хона месохтанд]?');
INSERT INTO "pioneers" VALUES(89,10,'89:10','Ва Фиръавн соҳиби сипоҳи [бузург ва қудратманд]');
INSERT INTO "pioneers" VALUES(89,11,'89:11','[Ҳамон] Касоне, ки дар шаҳрҳо туғён [ва саркашӣ] карданд');
INSERT INTO "pioneers" VALUES(89,12,'89:12','Ва дар онҳо фасоди бисёр ба бор оварданд');
INSERT INTO "pioneers" VALUES(89,13,'89:13','Он гоҳ Парвардигорат тозиёнаи азобро бар онон фуруд овард');
INSERT INTO "pioneers" VALUES(89,14,'89:14','Яқинан Парвардигори ту дар камингоҳ аст');
INSERT INTO "pioneers" VALUES(89,15,'89:15','Ва аммо инсон ҳангоме ки Парвардигораш ӯро биозмояд ва гиромӣ дорад ва ба ӯ неъмат бахшад, [мағрур мегардад ва] мегӯяд: «Парвардигорам маро гиромӣ доштааст»');
INSERT INTO "pioneers" VALUES(89,16,'89:16','Ва аммо ҳангоме ки ӯро биозмояд ва рӯзиашро бар ӯ танг гирад, [дилсард шуда] мегӯяд: «Парвардигорам маро хор кардааст»');
INSERT INTO "pioneers" VALUES(89,17,'89:17','Ҳаргиз чунин нест [ки шумо мепиндоред]; балки шумо ятимро гиромӣ намедоред');
INSERT INTO "pioneers" VALUES(89,18,'89:18','Ва якдигарро бар итъоми мустамандон тарғиб намекунед');
INSERT INTO "pioneers" VALUES(89,19,'89:19','Ва меросро ҳарисона мехӯред [ва ҳаққи заифонро намедиҳед]');
INSERT INTO "pioneers" VALUES(89,20,'89:20','Ва молу сарват [-и дунё]-ро бисёр дӯст медоред');
INSERT INTO "pioneers" VALUES(89,21,'89:21','Ҳаргиз чунин нест [ки шумо гумон мекунед]; ҳангоме ки замин сахт дарҳам кӯбида шавад [ва ҳамвор гардад]');
INSERT INTO "pioneers" VALUES(89,22,'89:22','Ва Парвардигорат [барои додрасӣ] биёяд ва [низ] фариштагон саф дар саф [биистанд]');
INSERT INTO "pioneers" VALUES(89,23,'89:23','Ва дар он рӯз ҷаҳаннам оварда шавад, дар он рӯз инсони [кофир] панд мегирад [ва тавба мекунад]; ва [аммо] ин панд гирифтан чи суде барояш дорад?');
INSERT INTO "pioneers" VALUES(89,24,'89:24','Вай мегӯяд: «Эй кош, [дар дунё] барои [ин] зиндагиам [чизе аз] пеш фиристода будам!');
INSERT INTO "pioneers" VALUES(89,25,'89:25','Пас, дар он рӯз ҳеҷ кас ҳаммонанди азоби Ӯ [Аллоҳ таоло] азоб намекунад');
INSERT INTO "pioneers" VALUES(89,26,'89:26','Ва ҳеҷ кас ҳамчун банд кашидани Ӯ [Аллоҳ таоло] касеро ба банд намекашад');
INSERT INTO "pioneers" VALUES(89,27,'89:27','[Аммо ба муъмин нидо мешавад] «Эй рӯҳи оромёфта!');
INSERT INTO "pioneers" VALUES(89,28,'89:28','Ба сӯйи Парвардигорат бозгард, дар ҳоле ки ту [аз подоши Аллоҳ таоло] хушнудӣ ва Ӯ аз [аъмоли] ту хушнуд аст');
INSERT INTO "pioneers" VALUES(89,29,'89:29','Пас, дар зумраи бандагони [хосси] Ман даро');
INSERT INTO "pioneers" VALUES(89,30,'89:30','Ва ба биҳишти Ман ворид шав»');
INSERT INTO "pioneers" VALUES(90,1,'90:1','Савганд ба ин шаҳр [Макка]');
INSERT INTO "pioneers" VALUES(90,2,'90:2','Ва ту дар ин шаҳр сокинӣ [ва ҷанг дар ин шаҳр барои ту ҳалол аст]');
INSERT INTO "pioneers" VALUES(90,3,'90:3','Ва савганд ба падар [Одам] ва фарзандонаш');
INSERT INTO "pioneers" VALUES(90,4,'90:4','Яқинан, инсонро дар ранҷ офаридем');
INSERT INTO "pioneers" VALUES(90,5,'90:5','Оё ӯ гумон мекунад, ҳеҷ кас бар [муҷозоти] вай тавоно нест?');
INSERT INTO "pioneers" VALUES(90,6,'90:6','Мегӯяд: «[Ба хотири инфоқ] Моли зиёдеро табоҳ кардаам»');
INSERT INTO "pioneers" VALUES(90,7,'90:7','Оё мепиндорад, ки ҳеҷ кас ӯро надидааст?');
INSERT INTO "pioneers" VALUES(90,8,'90:8','Оё барои ӯ ду чашм қарор надодем?');
INSERT INTO "pioneers" VALUES(90,9,'90:9','Ва як забон ва ду лаб?');
INSERT INTO "pioneers" VALUES(90,10,'90:10','Ва ӯро ба ду роҳ [хайр ва шар] роҳнамоӣ кардем');
INSERT INTO "pioneers" VALUES(90,11,'90:11','Пас, ба гардана [и сахт] қадам нагузошт [ва наомад]');
INSERT INTO "pioneers" VALUES(90,12,'90:12','Ва ту чи медонӣ, ки гардана [-и сахт] чист?');
INSERT INTO "pioneers" VALUES(90,13,'90:13','Озод кардани барда аст');
INSERT INTO "pioneers" VALUES(90,14,'90:14','Итъом дар рӯзи қаҳтӣ [ва гуруснагӣ]');
INSERT INTO "pioneers" VALUES(90,15,'90:15','[Хоҳ] Ятиме аз хешовандон [бошад]');
INSERT INTO "pioneers" VALUES(90,16,'90:16','Ё мустаманде хокнишин');
INSERT INTO "pioneers" VALUES(90,17,'90:17','Он гоҳ аз касоне бошад, ки имон овардаанд ва якдигарро ба сабр [бар ибодат ва дурӣ аз гуноҳ] ва меҳрубонӣ [бо бандагон] тавсия намудаанд');
INSERT INTO "pioneers" VALUES(90,18,'90:18','Инон аҳли саодатанд [ки номаи аъмол ба дасти росташон дода мешавад ва аҳли биҳиштанд]');
INSERT INTO "pioneers" VALUES(90,19,'90:19','Аммо касоне, ки ба оёти Мо куфр варзиданд, онон аҳли шақоватанд [ки номаи аъмол ба дасти чапашон дода мешавад ва ба дузах мераванд]');
INSERT INTO "pioneers" VALUES(90,20,'90:20','Бар онон [аз ҳар сӯ] оташи сарпӯшидае аст [ки роҳи фирор надоранд]');
INSERT INTO "pioneers" VALUES(91,1,'91:1','Савганд ба хуршед ва равшании он [ба ҳангоми бомдод]');
INSERT INTO "pioneers" VALUES(91,2,'91:2','Ва савганд ба моҳ, ҳангоме ки баъд аз он барояд');
INSERT INTO "pioneers" VALUES(91,3,'91:3','Ва савганд ба рӯз, ҳангоме ки он [хуршед]-ро равшан [ва ҷилвагар] кунад');
INSERT INTO "pioneers" VALUES(91,4,'91:4','Ва савганд ба шаб, ҳангоме ки онро бипӯшонад');
INSERT INTO "pioneers" VALUES(91,5,'91:5','Ва савганд ба осмон ва ба он ки онро бино кард');
INSERT INTO "pioneers" VALUES(91,6,'91:6','Ва савганд ба замин ва ба он ки онро густуронид');
INSERT INTO "pioneers" VALUES(91,7,'91:7','Ва савганд ба ҷон [-и инсон] ва он ки онро [офарид ва] наку гардонид');
INSERT INTO "pioneers" VALUES(91,8,'91:8','Сипас нофармонӣ ва парҳезгориашро [ба ӯ] илҳом кард');
INSERT INTO "pioneers" VALUES(91,9,'91:9','Бе тардид, ҳар ки нафси худро [аз гуноҳон] пок кард, растагор шуд');
INSERT INTO "pioneers" VALUES(91,10,'91:10','Ва ҳар ки онро [бо гуноҳ] олуда сохт, яқинан, зиёнкор шуд');
INSERT INTO "pioneers" VALUES(91,11,'91:11','[Қавми] Самуд аз рӯйи саркашӣ [паёмбарашонро] такзиб карданд');
INSERT INTO "pioneers" VALUES(91,12,'91:12','Он гоҳ ки бадкортарини эшон [барои иқдом ба ҷиноят] бархост');
INSERT INTO "pioneers" VALUES(91,13,'91:13','Пас, паёмбари Аллоҳ таоло [Солеҳ] ба онон гуфт: «Модашутури Аллоҳ таоло ва [навбати] обхӯрданашро [ҳурмат ниҳед]»');
INSERT INTO "pioneers" VALUES(91,14,'91:14','[Вале онон] ӯро такзиб карданд ва он [модашутур]-ро куштанд; пас, Парвардигорашон ба сабаби гуноҳонашон бар сарашон азоб овард ва ҳамагии ононро бо хок яксон кард');
INSERT INTO "pioneers" VALUES(91,15,'91:15','Ва [Аллоҳ таоло] аз саранҷоми он [кор] бим надорад');
INSERT INTO "pioneers" VALUES(92,1,'92:1','Савганд ба шаб, ҳангоме ки [ҳама чизро] бипӯшонад');
INSERT INTO "pioneers" VALUES(92,2,'92:2','Ва савганд ба рӯз, ҳангоме ки ошкор шавад');
INSERT INTO "pioneers" VALUES(92,3,'92:3','Ва савганд ба он ки нару модаро офарид');
INSERT INTO "pioneers" VALUES(92,4,'92:4','Ки, яқинан, саъй [-ю талош]-и шумо мухталиф [ва неку бад] аст');
INSERT INTO "pioneers" VALUES(92,5,'92:5','Ва аммо касе, ки [дар роҳи Аллоҳ таоло] бахшид ва парҳезкорӣ кард');
INSERT INTO "pioneers" VALUES(92,6,'92:6','Ва [ваъдаи Аллоҳ таоло ва суханони] некро тасдиқ кард');
INSERT INTO "pioneers" VALUES(92,7,'92:7','Пас, Мо [анҷоми аъмоли наку ва инфоқ дар роҳи Аллоҳро] барояш осон месозем');
INSERT INTO "pioneers" VALUES(92,8,'92:8','Ва аммо касе, ки бухл варзид ва бениёзӣ талабид');
INSERT INTO "pioneers" VALUES(92,9,'92:9','Ва [ваъдаи Аллоҳ таоло ва суханони] некро такзиб кард');
INSERT INTO "pioneers" VALUES(92,10,'92:10','Пас, Мо [анҷоми аъмоли шарру палидро] барояш осон месозем');
INSERT INTO "pioneers" VALUES(92,11,'92:11','Ва дар ҳангом [-и маргу] вуруд ба дузах дороияш ба ҳоли ӯ суде нахоҳад дошт');
INSERT INTO "pioneers" VALUES(92,12,'92:12','Мусалламан, ҳидоят [-и бандагон] бар [уҳдаи] Мост');
INSERT INTO "pioneers" VALUES(92,13,'92:13','Ва қатъан охират ва дунё [низ] аз они Мост');
INSERT INTO "pioneers" VALUES(92,14,'92:14','Пас, ман шуморо аз оташе шуълавар бим додам');
INSERT INTO "pioneers" VALUES(92,15,'92:15','Ки ҷуз бадбахттарин [-и мардум] дар он наафтад');
INSERT INTO "pioneers" VALUES(92,16,'92:16','[Ҳамон] Касе, ки [оёти Моро] такзиб кард ва рӯ гардонид');
INSERT INTO "pioneers" VALUES(92,17,'92:17','Ва ба зудӣ парҳезкортарин [-и мардум] аз он дур нигаҳ дошта мешавад');
INSERT INTO "pioneers" VALUES(92,18,'92:18','[Ҳамон] Касе, ки моли худро [дар роҳи Аллоҳ таоло] мебахшад, то [аз гуноҳ] пок шавад');
INSERT INTO "pioneers" VALUES(92,19,'92:19','Ва ҳеҷ касро ба қасди подош ёфтани неъмат намебахшад');
INSERT INTO "pioneers" VALUES(92,20,'92:20','Магар [барои] касби ризо [ва хушнудии] Парвардигори бартараш');
INSERT INTO "pioneers" VALUES(92,21,'92:21','Ва ба ростӣ, ки [бо подоши Аллоҳ таоло] хушнуд хоҳад шуд');
INSERT INTO "pioneers" VALUES(93,1,'93:1','Савганд ба оғози рӯз');
INSERT INTO "pioneers" VALUES(93,2,'93:2','Ва савганд ба шаб, ҳангоме ки ором гирад');
INSERT INTO "pioneers" VALUES(93,3,'93:3','[Ки] Парвардигорат туро во нагузошта ва [бар ту] хашм нагирифтааст');
INSERT INTO "pioneers" VALUES(93,4,'93:4','Ва мусалламан охират барои ту аз дунё беҳтар аст');
INSERT INTO "pioneers" VALUES(93,5,'93:5','Ва ба зудӣ Парвардигорат ба ту [ва уммати ту он қадр] ато хоҳад кард, ки хушнуд гардӣ');
INSERT INTO "pioneers" VALUES(93,6,'93:6','Оё туро ятим наёфт, пас, паноҳ дод?');
INSERT INTO "pioneers" VALUES(93,7,'93:7','Ва туро [гумроҳ ва] саргашта ёфт, пас, ҳидоят кард');
INSERT INTO "pioneers" VALUES(93,8,'93:8','Ва туро фақир ёфт, пас, бениёз намуд');
INSERT INTO "pioneers" VALUES(93,9,'93:9','Пас, [ту низ] ятимро маёзор');
INSERT INTO "pioneers" VALUES(93,10,'93:10','Ва [мустаманди] ниёзхоҳро марон [ва бо ӯ дуруштӣ макун]');
INSERT INTO "pioneers" VALUES(93,11,'93:11','Ва неъматҳои Парвардигоратро бозгӯ кун [ва сипос гузор]');
INSERT INTO "pioneers" VALUES(94,1,'94:1','[Эй паёмбар] Оё Мо синаатро [барои нузули ваҳй] бароят накушудем?');
INSERT INTO "pioneers" VALUES(94,2,'94:2','Ва сангини [-и бори гуноҳ ва душвории рисолат]-ро аз [дӯши] ту барнадоштем?');
INSERT INTO "pioneers" VALUES(94,3,'94:3','Ҳамон [боре], ки бар пуштат сангинӣ мекард');
INSERT INTO "pioneers" VALUES(94,4,'94:4','Ва [ному] овозаи туро баланд сохтем');
INSERT INTO "pioneers" VALUES(94,5,'94:5','Пас, мусалламан, бо [ҳар] душворӣ осонӣ аст');
INSERT INTO "pioneers" VALUES(94,6,'94:6','[Оре] Мусалламан, бо [ҳар] душворӣ осонӣ аст');
INSERT INTO "pioneers" VALUES(94,7,'94:7','Пас, ҳангоме ки [аз кору умури дунё] фориғ шудӣ, [ба ибодати Парвардигорат] бикӯш');
INSERT INTO "pioneers" VALUES(94,8,'94:8','Ва ба сӯйи Парвардигорат роғиб [ва муштоқ] шав');
INSERT INTO "pioneers" VALUES(95,1,'95:1','Савганд ба анҷир ва зайтун [ва ба Фаластин сарзамини рӯиши онҳо]');
INSERT INTO "pioneers" VALUES(95,2,'95:2','Ва савганд ба Тӯри Сино');
INSERT INTO "pioneers" VALUES(95,3,'95:3','Ва савганд ба ин шаҳри амн [Макка]');
INSERT INTO "pioneers" VALUES(95,4,'95:4','[Ки] Яқинан, Мо инсонро дар беҳтарин сурат [ва бо фитрати пок] офаридем');
INSERT INTO "pioneers" VALUES(95,5,'95:5','Сипас ӯро [ки инҳироф ёфт] ба пасттарин [мароҳили] пастӣ баргардонидем.');
INSERT INTO "pioneers" VALUES(95,6,'95:6','Магар касоне, ки имон оварданд ва корҳои шоиста анҷом доданд; пас, барои онон подоше пойдор [дар пеш] аст');
INSERT INTO "pioneers" VALUES(95,7,'95:7','Пас, [эй инсон] чи чизе баъд [аз ин ҳама далоили равшан] туро ба такзиби [рӯзи] ҷазо во медорад?');
INSERT INTO "pioneers" VALUES(95,8,'95:8','Оё Аллоҳ таоло беҳтарин довар [ва ҳокими мутлақ] нест?');
INSERT INTO "pioneers" VALUES(96,1,'96:1','[Эй Паёмбар] Бихон ба номи Парвардигорат, ки [ҳастиро] офарид');
INSERT INTO "pioneers" VALUES(96,2,'96:2','[Ҳамон Парвардигоре, ки] Инсонро аз хуни баста офарид');
INSERT INTO "pioneers" VALUES(96,3,'96:3','Бихон ва Парвардигорат [аз ҳама] бузургворатр аст');
INSERT INTO "pioneers" VALUES(96,4,'96:4','[Ҳамон] Касе, ки ба василаи қалам [навиштан] омӯхт');
INSERT INTO "pioneers" VALUES(96,5,'96:5','Ба инсон он чиро, ки намедонист, омӯхт');
INSERT INTO "pioneers" VALUES(96,6,'96:6','Чунин нест [ки шумо мепиндоред]; ҳаққо, ки инсон туғён [ва саркашӣ] мекунад');
INSERT INTO "pioneers" VALUES(96,7,'96:7','[Баъд] Аз ин ки худро бениёз [ва тавонгар] бубинад');
INSERT INTO "pioneers" VALUES(96,8,'96:8','Яқинан, бозгашт [-и ҳама] ба сӯйи Парвардигори туст');
INSERT INTO "pioneers" VALUES(96,9,'96:9','[Эй Паёмбар] Оё дидаӣ он касе, ки [аз ибодати Аллоҳ таоло] бозмедорад');
INSERT INTO "pioneers" VALUES(96,10,'96:10','Бандаеро, ҳангоме ки намоз мехонад');
INSERT INTO "pioneers" VALUES(96,11,'96:11','Ба ман хабар бидеҳ, агар ин [банда] бар [роҳи] ҳидоят бошад');
INSERT INTO "pioneers" VALUES(96,12,'96:12','[Мардумро] Ба парҳезкорӣ фармон диҳад [пас, сазои касе, ки чунин бандаеро биёзорад чист?]');
INSERT INTO "pioneers" VALUES(96,13,'96:13','Ба ман хабар деҳ, агар ин [шахси саркаш ҳақро] такзиб кард ва рӯйгардон шуд [оё сазовори азоб нест?]');
INSERT INTO "pioneers" VALUES(96,14,'96:14','Оё намедонад, ки бе тардид, Аллоҳ таоло [ҳама аъмолашро] мебинад?');
INSERT INTO "pioneers" VALUES(96,15,'96:15','Чунин нест [ки ӯ гумон мекунад]; агар боз наояд [ва даст аз шарорат барнадорад], ҳатман, мӯйи пешониашро ба сахтӣ хоҳем гирифт [ва ба сӯйи дузах хоҳем кашонид]');
INSERT INTO "pioneers" VALUES(96,16,'96:16','Мӯйи пешонии [ҳамон] дурӯғгӯи хатокорро');
INSERT INTO "pioneers" VALUES(96,17,'96:17','Пас, бояд ҳаммаҷлисонашро [ба кумак] бихонад');
INSERT INTO "pioneers" VALUES(96,18,'96:18','Мо [низ] оташбонони [дузах]-ро фаро хоҳем хонд');
INSERT INTO "pioneers" VALUES(96,19,'96:19','Чунин нест [ки ӯ мепиндорад]; ҳаргиз ӯро итоат макун ва саҷда кун ва [ба Аллоҳ таоло] тақарруб ҷӯй');
INSERT INTO "pioneers" VALUES(97,1,'97:1','Ҳамоно Мо он [Қуръон]-ро дар шаби қадр нозил кардем');
INSERT INTO "pioneers" VALUES(97,2,'97:2','Ва ту чи донӣ шаби қадр чист?');
INSERT INTO "pioneers" VALUES(97,3,'97:3','Шаби қадр беҳтар аз ҳазор моҳ аст');
INSERT INTO "pioneers" VALUES(97,4,'97:4','Фариштагон ва Рӯҳ [Ҷабраил] дар он [шаб] ба фармони Парвардигорашон барои [анҷоми] ҳар коре нозил мешаванд');
INSERT INTO "pioneers" VALUES(97,5,'97:5','[Он шаб] То тулуи фаҷр саломат [ва раҳмат] бошад');
INSERT INTO "pioneers" VALUES(98,1,'98:1','Касоне аз аҳли китоб ва мушрикон, ки кофир шуданд, [аз оини куфр] даст барнамедоранд, то ин ки барои онон далели равшане биёяд');
INSERT INTO "pioneers" VALUES(98,2,'98:2','Паёмбаре аз сӯи Аллоҳ таоло [биёяд], ки саҳифаҳои покро бихонад');
INSERT INTO "pioneers" VALUES(98,3,'98:3','Ва дар он [саҳифаҳо] навиштаҳое устувор [ва дуруст] аст');
INSERT INTO "pioneers" VALUES(98,4,'98:4','Ва касоне, ки ба онон китоб дода шуд, [аз ҷумлаи яҳудиён ва масеҳиён] пароканда нашуданд, [ва ихтилоф накарданд] магар баъд аз он ки далели равшан барои онон омад');
INSERT INTO "pioneers" VALUES(98,5,'98:5','Ва эшон фармон наёфтанд, ҷуз ин ки Аллоҳро бипарастанд, дар ҳоле ки дини худро барои ӯ холис гардонанд [ва аз ширку бутпарастӣ] ба тавҳид рӯ оваранд; ва намоз барпо доранд ва закот бипардозанд; ва ин аст оини ростин ва мустақим');
INSERT INTO "pioneers" VALUES(98,6,'98:6','Бе гумон, касоне аз аҳли китоб ва мушрикон, ки кофир шуданд, ҷовидона дар оташи ҷаҳаннам хоҳанд монд. Онон бадтарин офаридагон ҳастанд');
INSERT INTO "pioneers" VALUES(98,7,'98:7','Ҳамоно касоне, ки имон оварданд ва корҳои шоиста анҷом доданд, онон беҳтарини офаридагон ҳастанд');
INSERT INTO "pioneers" VALUES(98,8,'98:8','Подоши эшон назди Парвардигорашон боғҳои [биҳишти] ҷовидон аст, ки ҷӯйборҳо аз зери [дарахтони] он ҷорист; ҳамеша дар он хоҳанд монд; Аллоҳ таоло аз [аъмоли] онҳо хушнуд аст ва онон [низ] аз [подоши] Ӯ хушнуданд; ва ин [мақом ва подош] барои касест, ки аз Парвардигораш битарсад');
INSERT INTO "pioneers" VALUES(99,1,'99:1','Ҳангоме ки замин бо шадидтарин таконҳояш ба ларза дарояд');
INSERT INTO "pioneers" VALUES(99,2,'99:2','Ва замин борҳои сангинашро берун резад');
INSERT INTO "pioneers" VALUES(99,3,'99:3','Ва инсон мегӯяд: «Он [замин]-ро чӣ шудааст?»');
INSERT INTO "pioneers" VALUES(99,4,'99:4','Дар он рӯз [замин] тамоми хабарҳояшро бозгӯ мекунад');
INSERT INTO "pioneers" VALUES(99,5,'99:5','Зеро Парвардигорат ба ӯ [чунин] ваҳй [ва ҳукм] кардааст');
INSERT INTO "pioneers" VALUES(99,6,'99:6','Дар он рӯз мардум гурӯҳ-гурӯҳ бозмегарданд, то [натиҷаи] аъмолашон ба онон нишон дода шавад');
INSERT INTO "pioneers" VALUES(99,7,'99:7','Пас, ҳар кас ба андозаи заррае кори нек анҷом дода бошад, [подоши] онро мебинад');
INSERT INTO "pioneers" VALUES(99,8,'99:8','Ва ҳар кас ба андозаи заррае кори бад карда бошад, [кайфари] онро мебинад');
INSERT INTO "pioneers" VALUES(100,1,'100:1','Савганд ба аспҳои даванда, ки нафасзанон [ба сӯйи майдони ҷиҳод] пеш мерафтанд');
INSERT INTO "pioneers" VALUES(100,2,'100:2','Ва савганд ба аспҳое, ки [бо бархӯрди сумҳояшон ба сангҳо] шарораи [оташ] эҷод карданд');
INSERT INTO "pioneers" VALUES(100,3,'100:3','Боз савганд ба аспҳое, ки дар субҳгоҳон [бар душман] юриш баранд');
INSERT INTO "pioneers" VALUES(100,4,'100:4','Пас, дар он ҳангом гарду ғубор барангезанд');
INSERT INTO "pioneers" VALUES(100,5,'100:5','Он гоҳ ба миёни [сипоҳи душман] дароянд');
INSERT INTO "pioneers" VALUES(100,6,'100:6','Яқинан, инсон дар баробари [неъматҳои] Парвардигораш бисёр носипос аст');
INSERT INTO "pioneers" VALUES(100,7,'100:7','Ва бе гумон, ӯ бар ин [носипосӣ] гувоҳ аст');
INSERT INTO "pioneers" VALUES(100,8,'100:8','Ва ҳамоно ӯ алоқаи фаровоне ба мол [-и дунё] дорад');
INSERT INTO "pioneers" VALUES(100,9,'100:9','Оё ӯ намедонад, ки дар он рӯз он чи дар гурҳост, [ҳама зинда ва] барангехта шаванд?');
INSERT INTO "pioneers" VALUES(100,10,'100:10','Ва он чи дар синаҳост, [ҳама] ошкор гардад');
INSERT INTO "pioneers" VALUES(100,11,'100:11','Яқинан, дар он рӯз Парвардигорашон ба [вазъу ҳоли] эшон комилан огоҳ аст');
INSERT INTO "pioneers" VALUES(101,1,'101:1','[Он] Фурукӯбанда');
INSERT INTO "pioneers" VALUES(101,2,'101:2','[Он] Фурукӯбанда чист?');
INSERT INTO "pioneers" VALUES(101,3,'101:3','Ва ту чи донӣ, ки [он] фурукӯбанда чист?');
INSERT INTO "pioneers" VALUES(101,4,'101:4','Рӯзе, ки мардум монанди парвонаҳои пароканда [ҳайрону саргардон] хоҳанд буд');
INSERT INTO "pioneers" VALUES(101,5,'101:5','Ва кӯҳҳо монанди пашми рангини ҳаллоҷишуда (задашуда) хоҳанд шуд');
INSERT INTO "pioneers" VALUES(101,6,'101:6','Ва аммо ҳар касе, ки [дар он рӯз] паллаи мизонаш сангин бошад');
INSERT INTO "pioneers" VALUES(101,7,'101:7','[Дар биҳишт] Дар зиндагии роҳатбахш хоҳад буд');
INSERT INTO "pioneers" VALUES(101,8,'101:8','Ва аммо ҳар кас, ки паллаи мизонаш сабук бошад');
INSERT INTO "pioneers" VALUES(101,9,'101:9','Пас, [маскану] паноҳгоҳаш «ҳовия» аст');
INSERT INTO "pioneers" VALUES(101,10,'101:10','Ва ту чи донӣ, ки он [ҳовия] чист?');
INSERT INTO "pioneers" VALUES(101,11,'101:11','Оташе аст сӯзон [ва шуълавар]');
INSERT INTO "pioneers" VALUES(102,1,'102:1','Зиёдахоҳӣ шуморо ба худ машғул [ва аз ёди Парвардигор ғофил] сохт');
INSERT INTO "pioneers" VALUES(102,2,'102:2','То [поёни] коратон ба гуристон расид');
INSERT INTO "pioneers" VALUES(102,3,'102:3','Ҳаргиз чунин нест [ки шумо мепиндоред]; ба зудӣ хоҳед донист');
INSERT INTO "pioneers" VALUES(102,4,'102:4','Боз [ҳам] чунин нест; ба зудӣ хоҳед донист');
INSERT INTO "pioneers" VALUES(102,5,'102:5','Чунин нест [ки шумо гумон мекунед]; агар ба дониши яқинӣ [ва тардиднопазир ҳақиқати қиёматро] медонистед, [аз зиёдахоҳӣ ва ифтихор ба амволу фарзандонатон даст бармедоштед]');
INSERT INTO "pioneers" VALUES(102,6,'102:6','Савганд, ки дузахро хоҳед дид');
INSERT INTO "pioneers" VALUES(102,7,'102:7','Боз ҳам савганд, ки онро бо чашми яқин хоҳед дид');
INSERT INTO "pioneers" VALUES(102,8,'102:8','Он гоҳ, ҳатман, дар рӯзи қиёмат аз [тамоми] неъматҳо бозхост хоҳед шуд');
INSERT INTO "pioneers" VALUES(103,1,'103:1','Савганд ба аср');
INSERT INTO "pioneers" VALUES(103,2,'103:2','Ки инсон дар зиён аст');
INSERT INTO "pioneers" VALUES(103,3,'103:3','Магар муъминони накукоре, ки якдигарро ба ҳақ супориш намудаанд ва ба шикебоӣ тавсия кардаанд');
INSERT INTO "pioneers" VALUES(104,1,'104:1','Вой бар ҳар ғайбаткунанда [ва] айбҷӯе!');
INSERT INTO "pioneers" VALUES(104,2,'104:2','[Ҳамон] Касе, ки моли фаровоне гирд овард ва онро шумориш кард');
INSERT INTO "pioneers" VALUES(104,3,'104:3','Гумон мекунад, ки молаш ӯро ҷовидона месозад');
INSERT INTO "pioneers" VALUES(104,4,'104:4','Ҳаргиз чунин нест [ки ӯ гумон мекунад]; ҳатман, дар оташи дарҳамкӯбанда андохта хоҳад шуд');
INSERT INTO "pioneers" VALUES(104,5,'104:5','Ва [эй Паёмбар] ту чӣ медонӣ, ки оташи дарҳамкӯбанда чист?');
INSERT INTO "pioneers" VALUES(104,6,'104:6','Оташи барафрӯхтаи илоҳӣ аст');
INSERT INTO "pioneers" VALUES(104,7,'104:7','[Оташе] Ки бар дилҳо чира мегардад [ва месӯзонад]');
INSERT INTO "pioneers" VALUES(104,8,'104:8','Бе гумон, он [оташ] бар онон фурубаста [ва аз ҳар сӯ муҳосирашон карда] аст');
INSERT INTO "pioneers" VALUES(104,9,'104:9','[Афрӯхта] Дар сутунҳои баланд аст');
INSERT INTO "pioneers" VALUES(105,1,'105:1','[Эй Паёмбар] Оё надидӣ Парвардигорат бо асҳоби фил чӣ кард?');
INSERT INTO "pioneers" VALUES(105,2,'105:2','Оё [фиребу] нақшаи ононро дар табоҳӣ қарор надод?');
INSERT INTO "pioneers" VALUES(105,3,'105:3','Ва парандагоне гурӯҳ-гурӯҳ бар [сари] онон фиристод');
INSERT INTO "pioneers" VALUES(105,4,'105:4','[Ки] Бо санггил [ки дар минқору чангол доштанд] ононро сангборон мекарданд');
INSERT INTO "pioneers" VALUES(105,5,'105:5','Пас, онҳоро ҳамчун коҳи ҷавидашуда сохт');
INSERT INTO "pioneers" VALUES(106,1,'106:1','Барои [сипосгузорӣ аз] ҳамбастагӣ ва унси Қурайш');
INSERT INTO "pioneers" VALUES(106,2,'106:2','[Ҳамон] Ҳамбастагӣ ва унси онон дар сафарҳои зимистонӣ [ба Яман] ва тобистонӣ [ба Шом]');
INSERT INTO "pioneers" VALUES(106,3,'106:3','Бояд Парвардигори хонаи Каъбаро бипарастанд');
INSERT INTO "pioneers" VALUES(106,4,'106:4','Ҳамон [Парвардигоре], ки ононро аз гуруснагӣ [раҳонид, ба эшон] хӯрок дод ва аз ваҳшату тарс эмин намуд');
INSERT INTO "pioneers" VALUES(107,1,'107:1','[Эй Паёмбар] Оё касе, ки рӯзи ҷазоро дурӯғ меангорад, дидаӣ?');
INSERT INTO "pioneers" VALUES(107,2,'107:2','Пас ӯ [ҳамон] касест, ки ятимро [бо дуруштӣ аз худ] меронад');
INSERT INTO "pioneers" VALUES(107,3,'107:3','Ва [дигаронро] ба итъоми тиҳидаст ташвиқ намекунад');
INSERT INTO "pioneers" VALUES(107,4,'107:4','Пас, вой бар намозгузорон!');
INSERT INTO "pioneers" VALUES(107,5,'107:5','Ҳамон касоне, ки аз намозашон ғофиланд');
INSERT INTO "pioneers" VALUES(107,6,'107:6','Касоне, ки худнамоӣ мекунанд');
INSERT INTO "pioneers" VALUES(107,7,'107:7','Ва [аз амонат додани] васоили зарурии зиндагӣ дареғ меварзанд');
INSERT INTO "pioneers" VALUES(108,1,'108:1','[Эй Паёмбар] Ба ростӣ, ки Мо ба ту хайри бениҳоят ато кардем');
INSERT INTO "pioneers" VALUES(108,2,'108:2','Пас, барои Парвардигорат намоз бихон ва қурбонӣ кун');
INSERT INTO "pioneers" VALUES(108,3,'108:3','Бе тардид, душманат [аз ҳамаи хайроту баракот бебаҳра ва] бурида аст');
INSERT INTO "pioneers" VALUES(109,1,'109:1','[Эй Паёмбар] Бигӯ: «Эй кофирон!');
INSERT INTO "pioneers" VALUES(109,2,'109:2','Он чиро шумо мепарастед, ман намепарастам');
INSERT INTO "pioneers" VALUES(109,3,'109:3','Ва он чиро, ки ман мепарастам, [низ] шумо намепарастед');
INSERT INTO "pioneers" VALUES(109,4,'109:4','Ва на ман парастишгари он чи шумо парастиш кардаед, хоҳам буд');
INSERT INTO "pioneers" VALUES(109,5,'109:5','Ва на шумо парастишгари он чи ман мепарастам, хоҳед буд');
INSERT INTO "pioneers" VALUES(109,6,'109:6','[Бинобар ин] Оини шумо барои худатон ва оини ман барои худам»');
INSERT INTO "pioneers" VALUES(110,1,'110:1','[Эй Паёмбар] Ҳангоме ки ёрии Аллоҳ таоло ва пирӯзӣ фаро расад');
INSERT INTO "pioneers" VALUES(110,2,'110:2','Ва мардумро бубинӣ, ки гурӯҳ-гурӯҳ дар дини Аллоҳ таоло дохил мешаванд');
INSERT INTO "pioneers" VALUES(110,3,'110:3','Пас, ба cитоиши Парвардигорат тасбеҳ гӯй ва аз Ӯ омурзиш бихоҳ; ҳамоно Ӯ бисёр тавбапазир аст');
INSERT INTO "pioneers" VALUES(111,1,'111:1','Бурида бод ҳар ду дасти Абулаҳаб ва ҳалок бод!');
INSERT INTO "pioneers" VALUES(111,2,'111:2','Молу сарваташ ва он чӣ ба даст оварда буд, ба ӯ суде набахшид');
INSERT INTO "pioneers" VALUES(111,3,'111:3','Ба зудӣ ба оташи шуълавар хоҳад даромад');
INSERT INTO "pioneers" VALUES(111,4,'111:4','Ва ҳамсараш [Умми Ҷамил, он] ҳезумкаш [низ вориди оташ мешавад]');
INSERT INTO "pioneers" VALUES(111,5,'111:5','Бар гарданаш танобе аз лифи хурмост');
INSERT INTO "pioneers" VALUES(112,1,'112:1','[Эй Паёмбар] Бигӯ: «Ӯ Аллоҳ таоло яктост');
INSERT INTO "pioneers" VALUES(112,2,'112:2','Аллоҳ таоло бениёз аст [ва ҳама ниёзманди Ӯ ҳастанд]');
INSERT INTO "pioneers" VALUES(112,3,'112:3','На [фарзанде] зода ва на зода шудааст');
INSERT INTO "pioneers" VALUES(112,4,'112:4','Ва ҳеҷ кас ҳаммонанди ва ҳамтои Ӯ набуда ва нест»');
INSERT INTO "pioneers" VALUES(113,1,'113:1','[Эй Паёмбар] Бигӯ: «Ба Парвардигори сапедадам паноҳ мебарам');
INSERT INTO "pioneers" VALUES(113,2,'113:2','Аз шарри тамоми он чи офаридааст');
INSERT INTO "pioneers" VALUES(113,3,'113:3','Ва аз шарри торикии шаб он гоҳ ки ҳама ҷоро фаро гирад');
INSERT INTO "pioneers" VALUES(113,4,'113:4','Ва аз шар [-и занони ҷодугар], ки бо афсун дар гиреҳҳо медаманд');
INSERT INTO "pioneers" VALUES(113,5,'113:5','Ва аз шарри ҳасуд он гоҳ ки ҳасад варзад»');
INSERT INTO "pioneers" VALUES(114,1,'114:1','[Эй Паёмбар] Бигӯ: «Ба Парвардигори мардум паноҳ мебарам');
INSERT INTO "pioneers" VALUES(114,2,'114:2','Фармонравои мардум');
INSERT INTO "pioneers" VALUES(114,3,'114:3','Маъбуди мардум');
INSERT INTO "pioneers" VALUES(114,4,'114:4','Аз шарри [шайтони] васвасагар, ки [ба ҳангоми зикри Аллоҳ таоло] пинҳон мегардад');
INSERT INTO "pioneers" VALUES(114,5,'114:5','Ҳамоне, ки дар дилҳои мардум васваса мекунад');
INSERT INTO "pioneers" VALUES(114,6,'114:6','Аз ҷинниён [бошад] ва [ё аз] одамиён»');
