INSERT INTO "ramdane" VALUES(1,1,'1:1','S yisem n Öebbi, Aênin Ipêunun,');
INSERT INTO "ramdane" VALUES(1,2,'1:2','Ccekôan i Öebbi, Mass imavalen,');
INSERT INTO "ramdane" VALUES(1,3,'1:3','Aênin Ipêunun,');
INSERT INTO "ramdane" VALUES(1,4,'1:4','Agellid n wass iqeîîiyen.');
INSERT INTO "ramdane" VALUES(1,5,'1:5','D Keçç ay naâbed, d Keçç ay nepper d Amâ2iwen;');
INSERT INTO "ramdane" VALUES(1,6,'1:6','Awi ya$ d ubrid iweqmen,');
INSERT INTO "ramdane" VALUES(1,7,'1:7','Abrid n wid i T$atev; ur n wid i K isserfayen, ne$ n im$eôôqen.');
INSERT INTO "ramdane" VALUES(2,1,'2:1','Alm.');
INSERT INTO "ramdane" VALUES(2,2,'2:2','Appan Tezmamt, ur degs ccekk, d nnhaya i wid ipêezziben;');
INSERT INTO "ramdane" VALUES(2,3,'2:3','wid iumnen s tbavnit, ipéallan, ipseddiqen seg wayen iten N$at;');
INSERT INTO "ramdane" VALUES(2,4,'2:4','wid iumnen s wayen i k d irsen, akked wayen id irsen, uqbel ik, u, $ef laxeôt, nitni, pputebbten.');
INSERT INTO "ramdane" VALUES(2,5,'2:5','Widak, di nnhaya n Mass nnsen, widak, d isaâdiyen.');
INSERT INTO "ramdane" VALUES(2,6,'2:6','Wid ijehlen, s tidep, iâuva fellasen ma tnubav ten, ne$ ur ten tnubav. Ur ppamnen.');
INSERT INTO "ramdane" VALUES(2,7,'2:7','Isle$ Öebbi ulawen nnsen, akked imeééu$en nnsen. Irs le$yaô $ef allen nnsen. Fellasen aâaqeb abarar.');
INSERT INTO "ramdane" VALUES(2,8,'2:8','Ger yimdanen, llan wid iqqaôen: "numen s Öebbi akked wass aneggaru”; nitni, ur llin d lmumnin.');
INSERT INTO "ramdane" VALUES(2,9,'2:9','La xedâen Öebbi akked wid iumnen. Ur xedâen siwa iman nnsen, ur faqen.');
INSERT INTO "ramdane" VALUES(2,10,'2:10','Aîan deg ulawen nnsen; Irna yasen Öebbi aîan. Fellasen aâaqeb aqeôêan, ilmend n wayen skiddiben.');
INSERT INTO "ramdane" VALUES(2,11,'2:11','Ma nnan asen: "ur ssefsadet di tmurt"! A d inin: "nekwni d wid, kan, ipleqqimen".');
INSERT INTO "ramdane" VALUES(2,12,'2:12','Day maççi d nitni, s tidep, ay d ifessaden, maca ur faqen?');
INSERT INTO "ramdane" VALUES(2,13,'2:13','Ma nnan asen: "amnet, akken umnen medden"! A d inin: "day a namen, akken umnen ije$lufen"? Day maççi d nitni, s tidep, ay d ije$lufen, maca ur éôin.');
INSERT INTO "ramdane" VALUES(2,14,'2:14','Ma mmugren d wid iumnen, ad inin: "numen". Mara yilin weêêedsen, ar ccwaîen nnsen, ad inin: "nekwni yidwen! Nekwni la nettihi kan".');
INSERT INTO "ramdane" VALUES(2,15,'2:15','Öebbi ad Ittihi fellasen. Isnerna yasen lbwe$v nnsen; ddebdeben.');
INSERT INTO "ramdane" VALUES(2,16,'2:16','Widak, d wid iu$en ttelfan s nnhaya; ur d issasay ssuq nnsen, ur pwanhan.');
INSERT INTO "ramdane" VALUES(2,17,'2:17','Tevôa yidsen am win issa$en times. Mi tnewweô ayen i s d izzin, Öebbi Iddem tafat nnsen, Iooa ten di tillas, ur pwalin.');
INSERT INTO "ramdane" VALUES(2,18,'2:18','Âaégen, gugmen, dre$len; ihi, nitni ur d ppu$alen s abrid.');
INSERT INTO "ramdane" VALUES(2,19,'2:19','Ne$ am usigna igenni, degs tillas, tirmegt, lebôaq. Pparran ivudan nnsen deg imeééu$en nnsen, mgal ssiâqat; ad nnestaôen si tmeppant. Öebbi Izzi i ijehliyen.');
INSERT INTO "ramdane" VALUES(2,20,'2:20','Qôib i sen iêwi lebôaq, iéôi. Yal tikelt ideg i sen icâal, ad ddun syis. Mi sen ixsi, ad êebsen. Ma ihwa yaS i Öebbi, ad asen Ikkes timesliwt akked yiéôi nnsen. Öebbi, s tidep, Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(2,21,'2:21','A yimdanen! Âabdet Mass nnwen ikwen Ixelqen, kunwi, akked wid i kwen id izwaren, amar a pêezzbem;');
INSERT INTO "ramdane" VALUES(2,22,'2:22','i wen Irran tamurt d usu, igenni d lebni; id Isse$li d aman seg igenni, Issuffe$ ed yissen ppemôa, d ôôeéq i kunwi. Ihi, ur pparrat tanudda i Öebbi, u kunwi teéôam.');
INSERT INTO "ramdane" VALUES(2,23,'2:23','Ma tellam di ccekk, $ef wayen id Nessers ar umdan nne£, awit ed tasuôep i p icban. Neccdet ed inagan nnwen, war Öebbi, ma tessawalem tidep.');
INSERT INTO "ramdane" VALUES(2,24,'2:24','Ma ur tessawvem, u, war ccekk, ur tessawavem, êezzbet i tmes isseô$an imdanen, id$a$en, ippuheggan i ijehliyen.');
INSERT INTO "ramdane" VALUES(2,25,'2:25','Becceô wid iumnen, u xeddmen ayen ilhan, ar i nitni, leonanat, ddawat sen isaffen ppazzalen. Yal tikelt ara ppuôeéqen, si ppemôa nnsen, ad inin: "d aya is neppuôeéq, uqbel". Ippunefk asen i ten icban. Din, jjwao nnsen d ayen iûfan. Degsen ad wekkôen.');
INSERT INTO "ramdane" VALUES(2,26,'2:26','Ih, ur Ikukra ara Öebbi, a d Ifk amedya n tizip, ne$ ip i$elben. Wid iumnen éôan neppa d tidep, s$uô Mass nnsen. Wanag wid ijehlen qqaôen: "acu Ib$a Öebbi s umedya agi"? Yis, Itellif aîas; yis, Inehhu aîas. Ur Itellif yis, siwa iballa$en;');
INSERT INTO "ramdane" VALUES(2,27,'2:27','wid ipôuéun lemâahda n Öebbi, deffir lmitaq iS, issemfaôaqen ayen id Iumeô Öebbi ad imlil, issefsaden di tmurt. Widak d imexsuôen.');
INSERT INTO "ramdane" VALUES(2,28,'2:28','Amek ara tnekôem Öebbi? Tellam temmuten, Issader kwen id. Sinna, a kwen Ine$, sinna, a kwen id Issider, sinna, $uôeS a ppu$alem.');
INSERT INTO "ramdane" VALUES(2,29,'2:29','D Neppa i wen Ixelqen ayen akw illan di tmurt; sinna, Izzi ar igenni, Iwqem it d ûa igenwan. Neppa, Issen yal cci.');
INSERT INTO "ramdane" VALUES(2,30,'2:30','Mi Inna Mass ik, i lmalayek: "Nek ad Rre$ aneûûaf di tmurt". Nnant aS: "day a Pperrev, degs, win ara isfesden degs, ara izzizlen idamen? Wanag, nekwenti nesbuccuâ K s ccekôan, u Neôôequ yaK". Inna: "Nek, èôi$ ayen ur teéôimt".');
INSERT INTO "ramdane" VALUES(2,31,'2:31','Isseêfev akw ismawen i Adem. Sinna, Issken iten i lmalayek, Inna: "efkemt iyI d ismawen n wigi, ma kunemti d sut tidep".');
INSERT INTO "ramdane" VALUES(2,32,'2:32','Nnant: "gedha s yisseK! Ur nessin, siwa i a$ Teslemdev. Keçç, s tidep, d Amusnaw, d Bab n ûûwab".');
INSERT INTO "ramdane" VALUES(2,33,'2:33','Inna: "a Adem, efk asent ismawen nnsen". Mi sent imla ismawen nnsen, Inna: "day ur a kwent Nni$ ara: Nek Ssne$ tabavnit n tmurt akked igenwan, u èôi$ ayen d teqqaôem, akked wayen teppeffrem"?');
INSERT INTO "ramdane" VALUES(2,34,'2:34','Mi Nenna i lmalayek: "knumt i Adem"! Knant, siwa Iblis. Iugi, u isme$weô iman is. Illa seg ijehliyen.');
INSERT INTO "ramdane" VALUES(2,35,'2:35','Nenna: "a Adem, ezde$ keçç akked tmeîîut ik, di Loennet, eççet segs, s tawant, ayen i wen ihwan. Ur ppaéet ar ttejôa yagi, mulac a ppilim d imednas".');
INSERT INTO "ramdane" VALUES(2,36,'2:36','Ikellex iten Cciîan fellas. Issuffe$ iten id seg wanda llan. Nenna: "ûûubbet, d icenga bbwaygarawen. I kunwi, di tmurt, i$imi akked zzhu, siwa tallit".');
INSERT INTO "ramdane" VALUES(2,37,'2:37','Adem iîîef awalen n Mass is. Issuref as. Neppa, s tidep, Issuruf, Ipêunu.');
INSERT INTO "ramdane" VALUES(2,38,'2:38','Nenna: "ûûubbet segs, akken ma tellam. War ccekk, a kwen id tas nnhaya s$uôI". Wid iddan d nnhaya, ur ppagwaden, ur $weblen.');
INSERT INTO "ramdane" VALUES(2,39,'2:39','Wid ijehlen u skaddben issekniyen nne£, widak d at tmes, degs ad wekkôen.');
INSERT INTO "ramdane" VALUES(2,40,'2:40','A yAt Isôail! Mmektit ed l$it iW, is ikwen Öeéqe$. Eîîfet di lemâahda W, ad Ïîfe$ di lemâahda nnwen. D nek iwumi ara têezzbem.');
INSERT INTO "ramdane" VALUES(2,41,'2:41','Amnet s wayen id Sserse$, itebbten ayen illan $uôwen. Ur ppilit d imezwura ara t iagwin. Ur ppa$et, s issekniyen iW, ayen ôxisen. D nek iwumi ara têezzbem.');
INSERT INTO "ramdane" VALUES(2,42,'2:42','Ur slusut, i tidep, tikerkas. Ur teffret tidep, u kunwi teéôam.');
INSERT INTO "ramdane" VALUES(2,43,'2:43','èéallet, zekkit, îîinzet akked wid iîîinizen.');
INSERT INTO "ramdane" VALUES(2,44,'2:44','Day a ppamôem ise$ i medden, a ppeppum iman nnwen? U kunwi teqqaôem Tazmamt. Day ur tefhimem ara?');
INSERT INTO "ramdane" VALUES(2,45,'2:45','Ssutret lemâawna n ûûbeô akked téallit. Ih, dayen iwaâôen, siwa i imeéluven,');
INSERT INTO "ramdane" VALUES(2,46,'2:46','wid iêûan ar ad mlilen akked Mass nnsen, u $uôeS, nitni, war ccekk, ad u$alen.');
INSERT INTO "ramdane" VALUES(2,47,'2:47','A yAt Isôail! Mmektit ed l$it iW, is ikwen Öeéqe$. Nek, Smenyafe$ kwen i imavalen.');
INSERT INTO "ramdane" VALUES(2,48,'2:48','Ëezzbet i wass ideg ur ippuoazi yiwen deg umkan n wayev, s wacemma. Ur tepwaqbal ccafuâa segs, ur tepwaîîaf, segs, le$ôama, ur ten ppaâwanen.');
INSERT INTO "ramdane" VALUES(2,49,'2:49','Mi kwen Nsellek seg ugdud n Ferâun, sâaddayen fellawen aâaqeb adiran. Zellun arrac nnwen, ssidiren tilawin nnwen. Ayagi d taluft tabarart, s$uô Mass nnwen.');
INSERT INTO "ramdane" VALUES(2,50,'2:50','Mi, yiswen, Nessemfaôeq ill, Nsellek ikwen, Ne$meq agdud n Ferâun, kunwi la tepwalim.');
INSERT INTO "ramdane" VALUES(2,51,'2:51','Mi Nga lemâahda i Musa, kuéet wavan; sinna, deffir es, teddam d uâajmi. Kunwi d imednas.');
INSERT INTO "ramdane" VALUES(2,52,'2:52','Sinna, deffir waya, Nsemmeê awen, amar a ppesnemmrem.');
INSERT INTO "ramdane" VALUES(2,53,'2:53','Mi Nefka, i Musa, Tazmamt akked ufraz, amar a ppeppwanhum.');
INSERT INTO "ramdane" VALUES(2,54,'2:54','Mi inna Musa i ugdud is: "a yagdud iw! Kunwi, s tidep, tdensem iman nnwen, mi teddam d uâajmi. Ssutret ssmaê i Uxellaq nnwen; mmin$et, ppif i kunwi ar Uxellaq nnwen. Issuref awen. Neppa, s tidep, Issuruf, Ipêunu".');
INSERT INTO "ramdane" VALUES(2,55,'2:55','Mi tennam: "a Musa! Ur neppamen yisek, d awez$i, alamma nwala Öebbi, âinani". Tewwet ikwen id ssiâqa, kunwi tepwalim.');
INSERT INTO "ramdane" VALUES(2,56,'2:56','Sinna, Nerra kwen id, deffir tmeppant nnwen, amar a ppesnemmrem.');
INSERT INTO "ramdane" VALUES(2,57,'2:57','S usigna, Nerra yawen tili. Nessers awen d lmenna, ifeôôuoen: "eççet si téidanin is ikwen N$at". Ur a£ dnisen, maca densen iman nnsen.');
INSERT INTO "ramdane" VALUES(2,58,'2:58','Mi Nenna: "kecmet tamdint agi, eççet degs, s tawant, ayen i wen ihwan; kecmet si tebburt, s lekni; init: nendem. Ad awen Nsemmeê tuccva nnwen, a Nernu i iâaôviyen".');
INSERT INTO "ramdane" VALUES(2,59,'2:59','Wid idensen bedlen awal, ur d win i sen nnan. Nessers ed, i wid idensen, tawa$it seg igenni, ilmend n tucmitin nnsen.');
INSERT INTO "ramdane" VALUES(2,60,'2:60','Mi issuter Musa aman i ugdud is, Nenna: "ewwet aéôu, s uâkkwaz ik". Feggvent ed, segs, mrawsnat tliwa. Yal wa iéôa anda ara issew. "Eççet, esswet, si ôôeéq n Öebbi. Ur ssefsadet di tmurt am imjaêen".');
INSERT INTO "ramdane" VALUES(2,61,'2:61','Mi tennam: "a Musa! Ur nesteqnaâ bippa s yiwen n lqut. Nuj a$ Mass ik, ad a$ d Issuffe$ ayen d teggar tmurt: tizzegzewt is, axyaô is, ticcert is, laâds is, lebûel is". Inna: "teb$am a pbedlem ayen ilhan s wayen ixuûen? Üûubbet ar Maûeô, a n tafem, war ccekk, ayen tessutrem". I$li d fellasen ddell akked ééelt. Sebbeben d urrif n Öebbi. Ayagi, acku llan ugin issekniyen n Öebbi, neqqen lambiyya, war ssebba. Ayagi, imi âuûan, u zgaren tilas.');
INSERT INTO "ramdane" VALUES(2,62,'2:62','Ih, wid iumnen, udayen, imasiêiyen, iûabiyen, kra iumnen s Öebbi akked wass aneggaru, u xeddmen ayen ilhan, ad sâun arraz ar Mass nnsen, ur ppagwaden, ur $weblen.');
INSERT INTO "ramdane" VALUES(2,63,'2:63','Mi Nga laâqed yidwen, Nessuli adrar sennig wen: "eîîfet nezzeh ayen i wen Nefka, mmektit ed i illan degs, amar a pêezzbem".');
INSERT INTO "ramdane" VALUES(2,64,'2:64','Sinna, deffir waya, twexxôem. Lemmer maççi d lfevl n Öebbi fellawen, akked uêunu S, a ppilim seg imexsuôen.');
INSERT INTO "ramdane" VALUES(2,65,'2:65','Ni$ tessnem wid, segwen, iccven di Ssebt. Nenna yasen: "ilit d iddawen imaâfan"!');
INSERT INTO "ramdane" VALUES(2,66,'2:66','Nerra p d amedya i imuddiren akked wid i ten id ineûfen, u d aâggen i wid ipêezziben.');
INSERT INTO "ramdane" VALUES(2,67,'2:67','Mi inna Musa i ugdud is: "Öebbi, s tidep, Iumeô awen d a ppezlum tafunast". Nnan: "day a tettihiv fella$"? Inna: "ad iyi Isseô Öebbi! Ur ppili$ seg at war tussna".');
INSERT INTO "ramdane" VALUES(2,68,'2:68','Nnan: "nuj a$ ar Mass ik, ad a$ d Ibeggen anta p". Inna: "Neppa, s tidep, la Iqqaô: tafunast ur wessret, ur meééiyet, garasen. Xedmet i wen d ippumôen".');
INSERT INTO "ramdane" VALUES(2,69,'2:69','Nnan: "nuj ar Mass ik, ad a$ d Ibeggen ini s". Inna: "Neppa, s tidep, la Iqqaô: d tafunast tawôa$t, ini imfeooeo, ad iaâoeb w ip iwalan".');
INSERT INTO "ramdane" VALUES(2,70,'2:70','Nnan: "nuj ar Mass ik, ad a$ d Ibeggen anta p. £uône$, s tidep, tistan ppemcabint. Ih, ma Ib$a Öebbi, a nepwanhu".');
INSERT INTO "ramdane" VALUES(2,71,'2:71','Inna: "Neppa, s tidep, la Iqqaô: d tafunast ur taâtib si tyerza n tmurt, ne$ si tissit n yigran, tûeêêa, ur tepwacôev". Nnan as: "tura, tebbwiv d tidep". Zlan p. Qôib ur ewqimen.');
INSERT INTO "ramdane" VALUES(2,72,'2:72','Mi ten$am yiwen, wa irra p id $ef wa. Öebbi Issuffe$ ed ayen teppeffrem.');
INSERT INTO "ramdane" VALUES(2,73,'2:73','Nenna: "ewwtet ep s kra ines". Akka i Issidir Öebbi lmeggtin. Issenâat awen issekniyen iS, amar a pfehmem.');
INSERT INTO "ramdane" VALUES(2,74,'2:74','Sinna, deffir waya, qquôen wulawen nnwen; cuban aéôu, ne$ kteô, di ta$eôt. Ih, llan iéôa iseg d pfeggiven isaffen. Llan, s tidep, wid ipceqqiqen, teff$en d waman segsen. S tidep, llan wid i$ellin si tugwdi n Öebbi. Ur I$fil Öebbi $ef wayen txeddmem.');
INSERT INTO "ramdane" VALUES(2,75,'2:75','Day tôe$bem a kwen amnen? Ni$ arbaâ, segsen, ssemêasen i wawal n Öebbi, sinna, mi t fehmen, ad as bedlen udem, u nitni éôan.');
INSERT INTO "ramdane" VALUES(2,76,'2:76','Mi mmugren wid iumnen, ad inin: "numen". Mi qqimen garasen, qqaôen: "day ad asen tinim ayen i wen Imla Öebbi, a kwen nna$en yis, ar Mass nnwen? Day ur tepmeyyizem ara"?');
INSERT INTO "ramdane" VALUES(2,77,'2:77','Ne$ ur éôin ara, Öebbi Issen ayen teffren, akked wayen qqaôen.');
INSERT INTO "ramdane" VALUES(2,78,'2:78','Garasen, at war ta$uôi, ur nessin Tazmamt, siwa ayen nnwan. Nitni $ilen kan.');
INSERT INTO "ramdane" VALUES(2,79,'2:79','A tawa$it n wid ipparun Tazmamt s ifassen nnsen, sinna, qqaôen: "ayagi s$uô Öebbi", iwakken ad a$en, yis, ayen ôxisen! A tawa$it nnsen, ilmend n wayen uran ifassen nnsen! A tawa$it nnsen, $ef wayen keppben!');
INSERT INTO "ramdane" VALUES(2,80,'2:80','Nnan: "ur a$ tepnal tmes, d awez$i, siwa kra n wussan". Ini: "day teîîfem lemâahda s$uô Öebbi? Öebbi, d awez$i, ur Izeggil lemâahda S. Ne$ teqqaôem, $ef Öebbi, ayen ur tessinem"?');
INSERT INTO "ramdane" VALUES(2,81,'2:81','Xaîi, win ikesben tucmip, u tennev as tuccva s..., widak, d at tmes, degs ad wekkôen.');
INSERT INTO "ramdane" VALUES(2,82,'2:82','Wid iumnen, u xeddmen ayen ilhan, widak, d at Loennet, degs ad wekkôen.');
INSERT INTO "ramdane" VALUES(2,83,'2:83','Mi Nga laâqed i At Isôail: "ur âabdet siwa Öebbi. Elhut ar imawlan, ar llzem nnwen, ar igujilen, ar igellilen. Init i medden, awal imaânen. èéÈallet, zekkit". Sinna, twexxôem, siwa kra segwen, u, kunwi, tnumrem.');
INSERT INTO "ramdane" VALUES(2,84,'2:84','Mi wen Nga laâqed nnwen: "ur zzazalet idim nnwen, ur pemssuffu$et seg ixxamen nnwen". D$a twefqem, kunwi d inagan.');
INSERT INTO "ramdane" VALUES(2,85,'2:85','Sinna, kunwi d wid immin$en, tessuff$em arbaâ segwen, seg ixxamen nnsen. Tezzim asen s ibekkaven akked taâdawt. Ma usan kwen id, d imeêbas, tfeddum ten. Irna, ipwagdel awen usuffe$ nnsen. Day tumnem deg uêric n Tezmamt, tnekôem wayev? Anwa ay d arraz n wid, segwen, ixedmen aya, ma maççi d ddell, di tmeddurt n ddunit, u, ass n tnekra, ad ppunefken i uâaqeb uqsiê mavi? Ur I$fil Öebbi $ef wayen txeddmem.');
INSERT INTO "ramdane" VALUES(2,86,'2:86','Widak d wid iu$en tudert n ddunit s laxeôt, ur ippifsus fellasen uâaqeb, ur ppusellken.');
INSERT INTO "ramdane" VALUES(2,87,'2:87','Ni$ Nefka Tazmamt i Musa. Nuzen ed imazanen, deffir es. Nefka abeggen i Âisa, mmi s n Meryem, Null it s ôôuê iqedsen. Yal tikelt ideg ara kwen id ias umazan, s wayen ur awen nehwi, tesme$wôem iman nnwen. Kra teskaddbem ten, kra ten$am ten.');
INSERT INTO "ramdane" VALUES(2,88,'2:88','Nnan: "ulawen nne$ qquôen". Xaîi, Ixza ten Öebbi, ilmend n tijehli nnsen. Drus iten wid iumnen.');
INSERT INTO "ramdane" VALUES(2,89,'2:89','Mi ten id tusa Tezmamt, s$uô Öebbi, d atebbet n wayen illan $uôsen, - u llan, iu$ lêal, ssuturen ad $elben wid ijehlen - mi ten id iusa wayen ssnen, ugin t. Ad Inâal Öebbi ijehliyen!');
INSERT INTO "ramdane" VALUES(2,90,'2:90','I icmet wayen i$ef zzenzen iman nnsen! Ugin ayen id Issers Öebbi. Ëesden mi id Issers Öebbi, si lfevl iS, i win i S ihwan n yimdanen iS. Sebbeben urrif ar wayev. I ijehliyen, aâaqeb isdullun.');
INSERT INTO "ramdane" VALUES(2,91,'2:91','Ma nnan asen: "amnet s wayen id Issers Öebbi"! A d inin: "numen s wayen id irsen $uône$". Nekôen ayen i t id itebâan, u neppa d tidep, d atebbet n wayen illan $uôsen. Ini: "acimi tneqqem lambiyya n Öebbi, zik, ma d lmumnin ay tellam"?');
INSERT INTO "ramdane" VALUES(2,92,'2:92','Ni$ iusa kwen id Musa, s ubeggen. Sinna, deffir es, teddam akked uâajmi. Kunwi d imednas.');
INSERT INTO "ramdane" VALUES(2,93,'2:93','Mi Nga laâqed yidwen, Nessuli adrar sennig wen: "eîîfet nezzeh ayen i wen Nefka, u ssemêesset". Nnan d: "nesla u nâuûa". Ikecm asen uâajmi ar wul, si tjehli nnsen. Ini: "dir it wayen i wen iumeô liman nnwen, ma d lmumnin ay tellam".');
INSERT INTO "ramdane" VALUES(2,94,'2:94','Ini: "maday, ar Öebbi, axxam aneggaru i kunwi kan, ur pkkan ara imdanen, mennit tameppant, ma tessawalem tidep"!');
INSERT INTO "ramdane" VALUES(2,95,'2:95','D awez$i, ur p pmennin, ilmend n wayen xedmen ifassen nnsen. Öebbi Issen imednas.');
INSERT INTO "ramdane" VALUES(2,96,'2:96','A ten tafev, war ccekk, êemmlen tameddurt sennig medden, sennig imcurak. Atan yiwen segsen, issaram ad idder agim iseggwasen. Te$wzi n laâmeô is, ur as tessifrir aâaqeb. Öebbi Ipwali ayen xeddmen.');
INSERT INTO "ramdane" VALUES(2,97,'2:97','Ini: "anwa illan d acengu n Jebrayen"? D neppa, s tidep, it id issersen $ef wul ik, s leb$i n Öebbi. D atebbet n wayen it id izwaren, d nnhaya u d abecceô n lmumnin.');
INSERT INTO "ramdane" VALUES(2,98,'2:98','Win illan d acengu n Öebbi, lmalayek iS, imazanen iS, Jebrayen, Mikail..., ihi, Öebbi d acengu ijehliyen.');
INSERT INTO "ramdane" VALUES(2,99,'2:99','Ni$ Nessers ak d issekniyen iberrzen. I ten inekôen, siwa iballa$en.');
INSERT INTO "ramdane" VALUES(2,100,'2:100','Day yal tikelt ideg ara gen laâqed, a t ixdaâ urbaâ segsen? Ih, tegwti segsen, ur uminen.');
INSERT INTO "ramdane" VALUES(2,101,'2:101','Mi ten id iusa umazan s$uô Öebbi, itebbet ayen illan $uôsen, arbaâ n wid iwumi teppunefk Tezmamt, rran Tazmamt n Öebbi s azagur nnsen, amzun ur ssinen.');
INSERT INTO "ramdane" VALUES(2,102,'2:102','Ddan akked wayen xedmen ccwaîen mgal tagelda n Sliman. Sliman ur ijhil. Maca ccwaîen jehlen. Sseêfaven i medden ssêur, akked wayen id irsen, di Babil, ar lmalayek Harut akked Marut. Ur sselmaden yiwen, mebla ma nnan: "nekwni n$ewwu kan, ur jehhel"! ÊÊÊËefven, $uôsen, amek ara ssemfaôaqen argaz akked tmeîîut is. Ur îîurun yiwen syis, siwa ma Ib$a Öebbi. ÊÊÊËefven ara ten ivuôôen, ur ten ineffaâ. Ni$ éôan ar win i p iu$en, ur as illi umur di laxeôt. War ccekk, ard ucmit wayen is zzenzen leôwaê nnsen, lemmer éôin!');
INSERT INTO "ramdane" VALUES(2,103,'2:103','Lemmer, s tidep, uminen u êezzben, ad ifrir warraz s$uô Öebbi, lemmer ssinen.');
INSERT INTO "ramdane" VALUES(2,104,'2:104','A wid iumnen! Ur qqaret: "Âass a$ d", init: "Wali a$ d", u ssemêesset. I ijehliyen, aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(2,105,'2:105','Ur b$in ara ijehliyen, seg wid iwumi teppunefk Tezmamt, akked imcurak, ad awen d irs kra n lxiô, s$uô Mass nnwen. Öebbi Ismenyaf, s uêunu S, win i S ihwan. Öebbi d Bab n lfevl ameqwôaêan.');
INSERT INTO "ramdane" VALUES(2,106,'2:106','Ur Nessemsay assekni, ne$ ur t Nesseppu, mebla ma Nebbwi d win it yifen, ne$ i t icban. Day ur teéôiv ara Öebbi Izmer i yal cci?');
INSERT INTO "ramdane" VALUES(2,107,'2:107','Day ur teéôiv ara, ar n Öebbi tagelda n tmurt akked igenwan? Ur tesâim, war Öebbi, bab, ne$ asettaô.');
INSERT INTO "ramdane" VALUES(2,108,'2:108','Ne$ teb$am a ppesteqsim Amazan nnwen, akken ippusteqsa, zik, Musa? Win ibeddlen liman s tijehli, innetlaf ar yir abrid.');
INSERT INTO "ramdane" VALUES(2,109,'2:109','Si leêsed nnsen, aîas seg wAt Tezmamt, mi sen d tban tidep, budden awen tijehli, mbaâd liman nnwen. Ssemêet, ssemsut, ar d Ias Öebbi s lameô iS. Ni$ Öebbi Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(2,110,'2:110','èéallet, zekkit. Ayen ara tezzewrem n lxiô i iman nnwen, a t tafem ar Öebbi. Öebbi, s tidep, Ipwali ayen akw txeddmem.');
INSERT INTO "ramdane" VALUES(2,111,'2:111','Nnan: "ur ikeççem Loennet, siwa win illan d uday, ne$ d amasiêi". D wagi ay d asirem nnsen. Ini: "awit ed atebbet nnwen, ma tessawalem tidep".');
INSERT INTO "ramdane" VALUES(2,112,'2:112','Xaîi, win iunzen i Öebbi, u d aâôvi, ad isâu arraz is, ar Mass is. Ur ppagwaden, ur $weblen.');
INSERT INTO "ramdane" VALUES(2,113,'2:113','Nnan wudayen: "imasiêiyen ur sennden s acemma". Nnan imasiêiyen: "udayen ur sennden s acemma". U nitni la qqaôen Tazmamt. Akka, wid ur nessin ara, nnan am nitni. Ad Iêudd Öebbi, garasen, ass n tnekra, $ef wayen i$ef mgarraden.');
INSERT INTO "ramdane" VALUES(2,114,'2:114','Anwa idensen sennig wid igedlen, di leowamaâ n Öebbi, asmekti n yisem iS, u lhan d deg ussexôeb nnsen? Widak, ur iwata ara a ten kecmen, siwa ma s tugwdi. I nitni, ddell di ddunit, u aâaqeb abarar di laxeôt.');
INSERT INTO "ramdane" VALUES(2,115,'2:115','N Öebbi agmuv akked utaram. Ar wanda tezzim, din Udem n Öebbi. Ih, Öebbi d Ajemmal, d Amusnaw.');
INSERT INTO "ramdane" VALUES(2,116,'2:116','Nnan: "Öebbi Isâa mmi S". Gedha s yiS! Ni$ ineS i illan di tmurt akked igenwan. Akw, péallan i Neppa,');
INSERT INTO "ramdane" VALUES(2,117,'2:117','Axellaq n tmurt akked igenwan. Ma Iseyya lameô, ad as Yini kan: "ili"! Ad yili.');
INSERT INTO "ramdane" VALUES(2,118,'2:118','Nnan wid ur nessin: "a lemmer ad a$ d Immeslay Öebbi, ne$ ad a$ d ias ussekni"! Akka ay nnan wid i ten id izwaren, am awalen nnsen. Ppemcabin wulawen nnsen. Ni$ Nbeggen ed issekniyen i ugdud ipputebbten.');
INSERT INTO "ramdane" VALUES(2,119,'2:119','Ih, Nuzen ik id, s tidep, d abcir, u d aneddaô. Ur ippili fellak usteqsi $ef at wu$ud.');
INSERT INTO "ramdane" VALUES(2,120,'2:120','Ur k pôavin wudayen ne$ imasiêiyen, alamma teddiv d ddin nnsen. Ini: "d nehhu n Öebbi ay d nehhu". Ma teddiv akked usirem nnsen, deffir wayen ik ed iusan n tmusni, ur k ipfukku, si Öebbi, bab, ne$ asettaô.');
INSERT INTO "ramdane" VALUES(2,121,'2:121','Wid iwumi Nefka Tazmamt, qqaôen p leqôaya iwatan. Widak umnen yis. Wid i p iugin, widak d imexsuôen.');
INSERT INTO "ramdane" VALUES(2,122,'2:122','A yAt Isôail! Mmektit ed l$it iW, is i kwen £ate$. Nek, Smenyafe$ kwen i imavalen.');
INSERT INTO "ramdane" VALUES(2,123,'2:123','Ëezzbet i wass ideg ur ippuqeîîi yiwen, deg umkan n wayev, s wacemma; ur tepwaqbal le$ôama segs, ur t tenfaâ ccafuâa, ur ppusellken.');
INSERT INTO "ramdane" VALUES(2,124,'2:124','Ibôahim, mi t Ijeôôeb Mass is, s wawalen, iu$ awal. Inna: "Nek, a k Rre$ d win ara inehhun medden". Inna: "u si tarwa w"? Inna: "lemâahda W ur taâna ara imednas".');
INSERT INTO "ramdane" VALUES(2,125,'2:125','Mi Nerra Axxam d lemqam i yimdanen, u d amkan n laman: "erret lemqam n Ibôahim d loamaâ"! Nâuhed i Ibôahim akked Ismaâil: "zzizdget Axxam iW, i wid idewwiôen, wid ip$imin, wid iîîinizen, wid ikennun".');
INSERT INTO "ramdane" VALUES(2,126,'2:126','Mi inna Ibôahim: "a Mass iw! Err wagi d tamdint n laman, £it imezda$ is s ppemôat; wid, segsen, iumnen s Öebbi akked wass aneggaru". Inna: "win ijehlen, a t Ooe$ ad izhu cwiî, sinna, a t Nehôe$ ar uâaqeb n tmes. A tu$alin tucmip"!');
INSERT INTO "ramdane" VALUES(2,127,'2:127','Ibôahim akked Ismaâil, mi ssalayen leêyuv n Uxxam: "a Mass nne$! Eqbel s$uône$, Keçç, s tidep, d Win Isellen, d Amusnaw.');
INSERT INTO "ramdane" VALUES(2,128,'2:128','A Mass nne$! Err ane$ d imvuâen iK, u, si dderya nne$, lois ar aK ianzen. Mel a$ isekkiren nne$, Ssuref a$. Keçç, s tidep, Tessurufev, Tepêunuv.');
INSERT INTO "ramdane" VALUES(2,129,'2:129','A Mass nne$! Azen asen d amazan segsen, ad asen iqqaô issekniyen iK, ad asen isseêfev Tazmamt akked ûûwab, a ten izzizdeg. Keçç, s tidep, d Uzmir, d Bab n ûûwab".');
INSERT INTO "ramdane" VALUES(2,130,'2:130','Anwa iugin ddin n Ibôahim, siwa win iccemten iman is? Ni$ Nefren it di ddunit. Di laxeôt, neppa, war ccekk, seg iâaôviyen.');
INSERT INTO "ramdane" VALUES(2,131,'2:131','Mi s Inna Mass is: "anez"! Inna: "unze$ aS i Mass imavalen".');
INSERT INTO "ramdane" VALUES(2,132,'2:132','Iweûûa yis Ibôahim arraw is - akken Iaâqub -: "a yarraw iw, Öebbi, s tidep, Ifren awen ddin, ur ppemmtet siwa ma tunzem".');
INSERT INTO "ramdane" VALUES(2,133,'2:133','Ne$ tellam d inagan mi d ters tmeppant ar Yaâqub, mi inna i tarwa s: "anwa ara tâabdem deffir i"? Nnan: "a naâbed Öebbi k, u Öebbi n lejdud ik: Ibôahim, Ismaâil, Isêaq. Öebbi Yiwen. Nekwni nunez aS".');
INSERT INTO "ramdane" VALUES(2,134,'2:134','Wigi d lois i iâaddan. Ines ayen ikseb, nnwen ayen tkesbem. Ur kwen steqsayen $ef wayen xeddmen.');
INSERT INTO "ramdane" VALUES(2,135,'2:135','Nnan: "ilit d udayen ne$ d imasiêiyen, a ppepwanhum". Ini: "Xaîi, maca ddin n Ibôahim, imalen ar tidep. Ur illi seg imcurak".');
INSERT INTO "ramdane" VALUES(2,136,'2:136','Init: "numen s Öebbi akked wayen id irsen $uône$, wayen id irsen ar Ibôahim, Ismaâil, Isêaq, Iaâqub, laâôac, ayen ippunefken i Musa, Âisa, ayen ippunefken i lambiyya, s$uô Mass nnsen. Ur nessemgarrad yiwen segsen, nekwni nunez aS".');
INSERT INTO "ramdane" VALUES(2,137,'2:137','Ma umnen ayen icuban ayen tumnem, atnan pwanhan. Ma wexxôen, haaten kan di lefôaq. Iqadd ik Öebbi, segsen. Neppa Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(2,138,'2:138','Ssbi$a, n Öebbi. Anwa yifen Öebbi i ssbi$a? Nekwni d iâabbaden iS.');
INSERT INTO "ramdane" VALUES(2,139,'2:139','Ini: "day ad a$ tnamrem $ef Öebbi, u Neppa d Mass nne$, u d Mass nnwen? I nekwni igiten nne$, i kunwi igiten nnwen. Nekwni d izedganen iS".');
INSERT INTO "ramdane" VALUES(2,140,'2:140','Ne$ a d tinim: "s tidep, Ibôahim, Ismaâil, Isêaq, Iaâqub, laâôac, llan d udayen, ne$ d imasiêiyen"? Ini: "Anwa i isnen sennig akw, d kunwi ne$ d Öebbi"? Anwa i idensen sennig win iffren ccada, i isâa s$uô Öebbi? Ur I$fil Öebbi $ef wayen txeddmem.');
INSERT INTO "ramdane" VALUES(2,141,'2:141','Asatu agi iâadda. Ines ayen ikseb, nnwen ayen tkesbem. Ur fellawen asteqsi $ef wayen xeddmen.');
INSERT INTO "ramdane" VALUES(2,142,'2:142','Ad inin ije$lufen, ger medden: "acu iten iswexxôen si lqibla nnsen, u$uô llan péallan"? Ini: "N Öebbi agmuv akked utaram. Inehhu win i S ihwan, s abrid uwqim".');
INSERT INTO "ramdane" VALUES(2,143,'2:143','Akka ay kwen Nerra d lois alemmas, iwakken a ppilim d inagan n medden, ad yili Umazan d anagi nnwen. Nerra lqibla u$uô teppezziv, iwakken kan a Néeô anwa iddan d Umazan, anwa iu$alen ar deffir. D lhejna, siwa i wid Inha Öebbi. Ur Iveggiâ Öebbi liman nnwen. Ih, Öebbi, i yimdanen, d Umâin, Ipêunu.');
INSERT INTO "ramdane" VALUES(2,144,'2:144','Ni$ Nwala, tezziv udem ik ar igenni. A k Nawi ar lqibla ara tôaviv. Ezzi udem ik ar Loamaâ n Lêaôam. Anida tellam, zzit udmawen $uôes. Ih, wid iwumi teppunefk Tezmamt, éôan p d tidep s$uô Mass nnsen. Ur I$fil Öebbi $ef wayen xeddmen.');
INSERT INTO "ramdane" VALUES(2,145,'2:145','Xas a d tawiv, i wid iwumi teppunefk Tezmamt, yal assekni, ur tezzin ar lqibla k. Keçç, ur tezzi ar lqibla nnsen. Kra segsen, ur tezzin ar lqibla n wiyav. Ma teddiv akked usirem nnsen, mbaâd ma tusa k id, s$uône£, tussna, a ppiliv, imiren, seg imednas.');
INSERT INTO "ramdane" VALUES(2,146,'2:146','Wid iwumi Nefka Tazmamt ssnen p, akken ssnen arraw nnsen. War ccekk, arbaâ segsen teffren tidep, u nitni éôan.');
INSERT INTO "ramdane" VALUES(2,147,'2:147','Tidep s$uô Mass ik, ur ppili seg wid ipcukkun.');
INSERT INTO "ramdane" VALUES(2,148,'2:148','I yal yiwen, loiha u$uô izzi. Sâut timenyugert ar lxiô. Anda teddam, ad Yili Öebbi yidwen akw. Öebbi, s tidep, Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(2,149,'2:149','Ansi d tusiv, ezzi udem ik ar Loamaâ n Lêaôam. Neppat, ih, d tidep s$uô Mass ik. Ur I$fil Öebbi $ef wayen txeddmem.');
INSERT INTO "ramdane" VALUES(2,150,'2:150','Ansi d tef$ev, zzi udem ik ar Loamaâ n Lêaôam. Anida tellam, zzit udmawen nnwen $uôes, iwakken ur kwen pnamaren imdanen, siwa wid idensen, segsen. Ur ten ppagwadet, agwadet iyI. Ad awen Time$ l$it iW, amar a ppepwanhum.');
INSERT INTO "ramdane" VALUES(2,151,'2:151','Akken i wen Nuzen Amazan segwen, immal awen issekniyen nne£, izzizdig ikwen, isselmad awen Tazmamt, akked ûûwab; isselmad awen ayen ur tessinem.');
INSERT INTO "ramdane" VALUES(2,152,'2:152','Mmektit iyI d, a kwen id Mmekti$. Snemmret iyI, ur jehlet.');
INSERT INTO "ramdane" VALUES(2,153,'2:153','A wid iumnen! Ssutret tallalt, s ûûbeô akked téallit. Ih, Öebbi ar iûebôiyen.');
INSERT INTO "ramdane" VALUES(2,154,'2:154','Ur qqaôet mmuten wid ipwan$an deg ubrid n Öebbi. Xaîi d imuddiren, maca ur teêûim ara.');
INSERT INTO "ramdane" VALUES(2,155,'2:155','War ccekk, a kwen Njeôôeb s kra usigwed, n llaé, n nneqs n wayla, n leôwaê, n l$ellat. Ssefreê iûebôiyen;');
INSERT INTO "ramdane" VALUES(2,156,'2:156','Wid, ma u$en tawa$it, qqaôen: "nekwni n Öebbi, nekwni, a nu$al $uôeS".');
INSERT INTO "ramdane" VALUES(2,157,'2:157','Widak, fellasen téilla s$uô Mass nnsen, akked uêunu. Widak d wid ipwanhan.');
INSERT INTO "ramdane" VALUES(2,158,'2:158','Ih, ÛÜafa akked Meôwa, seg isekkiren n Öebbi. Win iêuoen s Axxam, ne$ iéuô it, ulac u$ilif fellas, ma izzi yasent. Yif it win izzin, s leb$i s. Ih, Öebbi Ippara tajmilt, d Amusnaw.');
INSERT INTO "ramdane" VALUES(2,159,'2:159','Ih, wid iteffren ayen id Nessers ubeggen akked nnhaya, mbaâd ma Nbeggen it i yimdanen, di Tezmamt, widak, Ixza iten Öebbi, xzan ten wid ixezzun.');
INSERT INTO "ramdane" VALUES(2,160,'2:160','Kkes ed wid inedmen u xedmen ayen ilhan, u beggnen d. Widak, ad asen Ssurfe$. Nek Ssurufe$, Pêunu$.');
INSERT INTO "ramdane" VALUES(2,161,'2:161','Ih, wid ijehlen u mmuten d ijehliyen, widak, fellasen xezzu n Öebbi, lmalayek, akked yimdanen, meôôa.');
INSERT INTO "ramdane" VALUES(2,162,'2:162','Degs ad wekkôen. Ur ippifsus fellasen uâaqeb, ur illi w ara ten id iwalin.');
INSERT INTO "ramdane" VALUES(2,163,'2:163','Öebbi nnwen d Öebbi Yiwen. Ur illi ôebbi siwa Neppa, Aênin Ipêunun.');
INSERT INTO "ramdane" VALUES(2,164,'2:164','Ih, di lxelq n tmurt akked igenwan: afraz ger yiv akked wass, lbabuô ippazalen di yill, s wayen infâan medden, ayen id Issers Öebbi n waman, seg igenni - Issidir yissen tamurt, deffir tmeppant is, Issenkar, degs, yal lweêc -, abeddel n wavu akked usigna i imuêepmen ad yili ger igenni akked tmurt. War cekk, ard issekniyen i ugdud ipmeyyizen.');
INSERT INTO "ramdane" VALUES(2,165,'2:165','Ger yimdanen, llan wid ippadamen tanudda, war Öebbi. Ëemmlen ten akken Ippuêemmel Öebbi. Wid iumnen zaden akken êemmlen Öebbi. Awufan wid idensen, mi walan aâaqeb, ad éôen ar tazmert akw n Öebbi, u Öebbi Iqseê deg uâaqeb.');
INSERT INTO "ramdane" VALUES(2,166,'2:166','Mara zekkôen uvfiôen nnsen, wid ipwatebâan, u walan aâaqeb, u tfukk yidsen lemxalîa,');
INSERT INTO "ramdane" VALUES(2,167,'2:167','ad inin uvfiôen: "a lemmer a nu$al, a ten nzekkeô akken i $ zekkôen"! Akka ay sen Issken Öebbi igiten nnsen. A yamxix nnsen, ur d teff$en si tmes!');
INSERT INTO "ramdane" VALUES(2,168,'2:168','A yimdanen! Eççet seg wayen iêellen, imaânen, di tmurt. Ur teddut di lateô n Cciîan. Neppa, s tidep, d acengu nnwen n ûûeê.');
INSERT INTO "ramdane" VALUES(2,169,'2:169','Ippameô awen kan tuccva, lâaô; a ppinim, $ef Öebbi, ayen ur tessinem.');
INSERT INTO "ramdane" VALUES(2,170,'2:170','Ma nnan asen: "ddut akked wayen id Issers Öebbi", a d inin: "xaîi, a neddu akked wayen id nufa ar imezwura nne$". I ma llan imezwura nnsen ur meyyzen acemma, ur pwanhan?');
INSERT INTO "ramdane" VALUES(2,171,'2:171','Amedya n wid ijehlen, d amedya n win u$uô ppu$un, seg akken ur issel, siwa i usu$u akked uberreê. D iâeééugen, d igugam, d ider$alen, nitni ur pmeyyizen.');
INSERT INTO "ramdane" VALUES(2,172,'2:172','A wid iumnen! Eççet si téidanin ikwen N$at. Snemmret Öebbi, ma d Neppa ay tâabdem.');
INSERT INTO "ramdane" VALUES(2,173,'2:173','I awen Igdel Öebbi, siwa loifa, idamen, aksum n yilef, akked îêegga i war Öebbi. Win terra tmara, ur ib$i, ur izgir tilas, ur illi d abekkav is. Ih, Öebbi Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(2,174,'2:174','Ih, wid iteffren ayen id Issers Öebbi, si Tezmamt, ppa$en yis ayen ôxisen, widak, siwa times ara ççen, deg iâabbav nnsen. Ur asen Ipmeslay Öebbi, ass n tnekra, ur ten Izzizdig. Fellasen aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(2,175,'2:175','Widak, d wid iu$en ttelfan s nnhaya, aâaqeb s ssmaê. Acu ara ten iûebbôen si tmes?');
INSERT INTO "ramdane" VALUES(2,176,'2:176','Akka, acku Issers ed Öebbi Tazmamt, s tidep. Ih, wid imgarraden $ef Tezmamt, haaten di lfiôaq u$miq.');
INSERT INTO "ramdane" VALUES(2,177,'2:177','Ise$, maççi ma tezzim udmawen nnwen, ar ugmuv akked utaram. Maca, ise$ d wid iumnen s Öebbi, s wass aneggaru, s lmalayek, s Tazmamt, s lambiyya. U ppaken ayla, $ef lemêibba S, i llzem, igujilen, igellilen, win d ibbwi ubrid, imapparen, akw i uberru n waklan. D wid ipéallan, ipzekkin, ippaîîafen di lemâahda nnsen, ma âahden, iûebôiyen di lmeêna akked twa$it, di tegwnip n ccqa. D wigi i illan di tidep, d wigi i ipêezziben.');
INSERT INTO "ramdane" VALUES(2,178,'2:178','A wid iumnen! Iura fellawen ppaô n tmegrav. Lêeôô s lêeôô, akli s wakli, nnta s nnta. Win iwumi isemmeê gma s kra: îlaba s lewqama, feddu s lewqama. Ayagi, d sshala s$uô Mass nnwen, akked uêunu. Win iff$en abrid, deffir waya, ad isâaddi aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(2,179,'2:179','I kunwi, ppaô d tameddurt, a yuâqilen. Amar a pêezzbem.');
INSERT INTO "ramdane" VALUES(2,180,'2:180','Iura fellawen: ma tusa d tmeppant, ar yiwen degwen, ma iooa d lxiô, a t ifk i imawlan is, akked llzem, s lqanun. D ayen ilezmen i wid ipêezziben.');
INSERT INTO "ramdane" VALUES(2,181,'2:181','Win it ibeddlen mbaâd ma isla yas, abekkav is siwa $ef wid it ipbeddilen. Öebbi, s tidep, Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(2,182,'2:182','Ur idnib win iten issemûalaêen acku iugad rrya, ne$ abekkav, s$uô win ippuweûûan. Öebbi, s tidep, Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(2,183,'2:183','A wid iumnen! Iura fellawen uéummu, akken iura $ef imezwura nnwen, amar a pêezzbem.');
INSERT INTO "ramdane" VALUES(2,184,'2:184','Ussan ipwaêesben. Win iuvnen segwen ne$ illan ippinig, annect n wussan nniven. Wid i s izemren, feddu ad cceççen igellil. Win izaden lxiô, ilha yas. Ma tuéamem, ppif i kunwi, lemmer teéôim.');
INSERT INTO "ramdane" VALUES(2,185,'2:185','Aggur n ÔÖemvan, ideg d irs Leqwôan, d nnhaya i medden, d abeggen n nnhaya akked ufraz. Win segwen iwalan illul ed waggur, a t iuzum. Win iuvnen ne$ illan ippinig, annect n wussan nniven. Öebbi Ib$a yawen sshala, ur awen Ib$a ara cedda. A pfukem tallit, a pkabrem Öebbi imi kwen Inha, amar a ppesnemmrem.');
INSERT INTO "ramdane" VALUES(2,186,'2:186','Ma steqsan k, imdanen iW, fellI, Nek Qeôbe$. Pjawabe$ i unuju n yimsuter, ma ipper iyI. Ihi, ad iyI d jawben, ad amnen yissI, amar ad pwanhun.');
INSERT INTO "ramdane" VALUES(2,187,'2:187','Iêell awen, deg iv uéummu, a pqeôôbem ar tlawin nnwen. Nitenti d lebsa nnwen, kunwi d lebsa nnsent. Iéôa Öebbi ar tellam tepkellixem iman nnwen. Issuref awen, Isemmeê awen. Tura qeôôbet $uôsent, nadit ayen i wen Iura Öebbi. Eççet, esswet, ar pferzem lxiv amellal, si lxiv aberkan, di tafrara. Sinna, uéummet ar yiv. Ur pqeôôibet $uôsent, ma tâazlem iman nnwen di lowamaâ. D tigi ay d tilas n Öebbi. Ur pqeôôibet $uôsent! Akka i Ipbeggin Öebbi issekniyen iS i yimdanen, amar ad êezzben.');
INSERT INTO "ramdane" VALUES(2,188,'2:188','Ur ppemyeççat ayla nnwen, garawen, d aêerfi, ur t ppaket i yimêaddan, iwakken a ppeççem amur, seg wayla n medden, di lmenkweô, u kunwi teéôam.');
INSERT INTO "ramdane" VALUES(2,189,'2:189','La k steqsayen $ef tlalit n waggur. Ini: "d lewqat i medden, u i lêio". Ise$, maççi d anekcum ar ixxamen si zdeffir nnsent. Maca, ise$ d aêezzeb i Öebbi. Kecmet ixxamen si tebbura nnsen. Ëezzbet i Öebbi, amar a prebêem.');
INSERT INTO "ramdane" VALUES(2,190,'2:190','Nna$et deg ubrid n Öebbi wid i kwen ipna$en. Ur zegret tilas. Öebbi, s tidep, ur Iêemmel ara wid izegren tilas.');
INSERT INTO "ramdane" VALUES(2,191,'2:191','Nna$et pen anda ten tufam, ssuff$et pen seg wanda i kwen ssuff$en. Ahewwel iwâaô kteô umennu$. Ur ten nepna$et ar Loamaâ n Lêaôam, siwa ma nnu$en kwen degs. Ma nnu$en kwen, nna$et pen. Akka aqeîîi ijehliyen.');
INSERT INTO "ramdane" VALUES(2,192,'2:192','Ma fukken, Öebbi, s tidep, Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(2,193,'2:193','Nna$et pen alamma tfukk lfetna, u iu$al ddin i Öebbi. Ma fukken, ur pna$et, siwa imednas.');
INSERT INTO "ramdane" VALUES(2,194,'2:194','Aggur iôqan s waggur iôqan. Tiririt n ppaô $ef wayen ippugedlen. Win ikwen id ibbwven, awvet ep, akken ikwen id ibbwev. Ëezzbet i Öebbi. Cfut ar Öebbi ar wid ipêezziben.');
INSERT INTO "ramdane" VALUES(2,195,'2:195','Ssedqet deg ubrid n Öebbi. Ur d ppawit tawa$it, s ifassen nnwen. Maânet. Öebbi, s tidep, Iêemmel iâaôviyen.');
INSERT INTO "ramdane" VALUES(2,196,'2:196','Öuêet ar lêio akked lâumôa, i Öebbi. Ma têeûlem, ssadaqa tushilt. Ur pseîîilet iqweôôa nnwen, ar ppawev tikci s amkan is. Win iuvnen, segwen, ne$ ihlek aqeôôu s, ad ifdu s uéummu, ne$ ssadaqa, ne$ iseflan. Mi tellam di laman, win izuôen ur iêuo, ad iseddeq ayen i s isehlen. Win ur nufa ara, ad iuéum krav wussan di lêio, ûa wussan ar tu$alin, akken ad awven mraw. Ayagi i win ur nesâi tawacult illan ar Loamaâ n Lêaôam. Ëezzbet i Öebbi, cfut ar Öebbi Iwâaô deg uâaqeb.');
INSERT INTO "ramdane" VALUES(2,197,'2:197','Lêio deg wagguren ipwassnen. Win iâazmen, degsen, ad iêuo, ihi, di lêio ur telli tuzzuft, ne$ ayen n dir, ne$ nnmara. Ayen txedmem n lxiô, Iéôa t Öebbi. Awit aâwin, maca aâwin amxallef d aêezzeb. Ëezzbet iyI a yuâqilen!');
INSERT INTO "ramdane" VALUES(2,198,'2:198','Uu ili u$ilif fellawen ma tessarmem lfevl s$uô Mass nnwen. Mi d tûubbem si Aâôafat, mmektit ed Öebbi, ppama n lemqam iôqan. Mmektit eP id, akken i kwen Inha, wanag tellam, uqbel, seg im$eôôqen.');
INSERT INTO "ramdane" VALUES(2,199,'2:199','Sinna, ddut anda ddan medden, ssutret ssmaê i Öebbi. Öebbi, s tidep, Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(2,200,'2:200','Mi tfukkem isekkiren nnwen, mmektit ed Öebbi, akken d tesmektayem imezwura nnwen, ne$ kteô. Ger imdanen, wid iqqaôen: "a Mass nne$! Efk a$ di ddunit". Ur ppawin amur di laxeôt.');
INSERT INTO "ramdane" VALUES(2,201,'2:201','Llan, garasen, wid iqqaôen: "a Mass nne$! Efk a$ di ddunit, ise$, di laxeôt, ise$. Esseô a$ seg uâaqeb n tmes".');
INSERT INTO "ramdane" VALUES(2,202,'2:202','Widak, ad awin amur seg wayen kesben. Öebbi Ip$awal deg uqeîîi.');
INSERT INTO "ramdane" VALUES(2,203,'2:203','Mmektit ed Öebbi, deg ussan iâuden. Win i$awlen di sin wussan, ur icciv ara; win iâaîlen, ur icciv ara, ma illa d win ipêezziben. Ëezzbet i Öebbi. Cfut, ar $uôeS ara tepwafesrem.');
INSERT INTO "ramdane" VALUES(2,204,'2:204','Yiwen ger imdanen, iâoeb ik umeslay is, $ef tmeddurt n ddunit. Irra Öebbi d Anagi, $ef wayen illan deg ul is. U neppa iqseê i tilla.');
INSERT INTO "ramdane" VALUES(2,205,'2:205','Mi izzi, ad ikkat di tmurt, iwakken ad issefsed degs, ad isqucc igran akked lmal. Öebbi ur Iêemmel ara asefsed.');
INSERT INTO "ramdane" VALUES(2,206,'2:206','Ma nnan as: "êezzeb i Öebbi"! A t tekcem lqwedôa ubekkav. Ad isteqnaâ s Loahennama. A yir usu!');
INSERT INTO "ramdane" VALUES(2,207,'2:207','Ger imdanen, win izzenzen iman is, issaram llza n Öebbi. Öebbi d Umâin ar imdanen.');
INSERT INTO "ramdane" VALUES(2,208,'2:208','A wid iumnen! Kecmet di tifrat s lekmal is, ur teddut d lateô n Cciîan. Neppa, s tidep, d acengu nnwen n ûûeê.');
INSERT INTO "ramdane" VALUES(2,209,'2:209','Ma teccvem, deffir n tisin ubeggen $uôwen, cfut Öebbi d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(2,210,'2:210','Day a pôaoun kan, ad asen d Ias Öebbi, akked lmalayek, di tili usigna? Ad iûaô lameô. Ar Öebbi ara u$alen lumuô.');
INSERT INTO "ramdane" VALUES(2,211,'2:211','Steqsi At Isôail, acêal issekniyen iberrzen i sen Nefka. I win ipbeddilen tikci n Öebbi, deffir tisin is $uôes, ihi, Öebbi Iqseê deg uâaqeb.');
INSERT INTO "ramdane" VALUES(2,212,'2:212','Wid ijehlen, ccebêen asen tameddurt n ddunit. Ttihin $ef wid iumnen. Wid ipêezziben ad ilin sennig sen, ass n tnekra. Öebbi Ip$iti win i S ihwan, s lufa.');
INSERT INTO "ramdane" VALUES(2,213,'2:213','Llan imdanen d yiwen n lois. Iuzen ed Öebbi lambiyya ad beccôen, ad nabin. Issers ed, yidsen, Tazmamt s tidep, iwakken ad Iêudd ger medden, $ef wayen i$ef mgarraden. I imgarraden fellas, siwa wid iwumi teppunefk, mbaâd ma iusa ten id ubeggen. Ppemyasamen. Inha Öebbi wid iumnen deg wayen n tidep, i$ef mgarraden, s tsriê iS. Öebbi Inehhu win i S ihwan, s abrid uwqim.');
INSERT INTO "ramdane" VALUES(2,214,'2:214','Day t$ilem a pkecmem Loennet, wanag ur kwen id iusi wayen id iusan ar wid i kwen id izwaren? Mmugren d lmeêna, ccqa, zzelzla, armi inna umazan akked wid iumnen yides: "melmi laânaya n Öebbi"? Day, s tidep, ur d teqôib ara laânaya n Öebbi?');
INSERT INTO "ramdane" VALUES(2,215,'2:215','Steqsan k $ef wayen ara fken. Ini: "ayen ara tefkem n lxiô, i imawlan, i llzem, i igujilen, i igellilen, i w ar d iawi ubrid". Ayen ara txedmem n lxiô, war ccekk, Iéôa t Öebbi.');
INSERT INTO "ramdane" VALUES(2,216,'2:216','Iura fellawen umennu$, u neppa d ayen tkeôhem. Ahat tkeôhem ayen ilhan i kunwi, ahat têemmlem ayen n dir i kunwi. Öebbi Issen, kunwi ur tessinem.');
INSERT INTO "ramdane" VALUES(2,217,'2:217','La k steqsayen ma ad yili umennu$ deg waggur iôqan. Ini: "amennu$ degs, d abekkav. Abekkav it i$elben, ar Öebbi, d assefôe$ $ef ubrid n Öebbi, d ankaô is, u d ankaô n Loamaâ n Lêaôam; d assuffe$, segs, imezda$ is". Ahewwel i$leb amennu$. Zgan a kwen pna$en, alamma swexxôen kwen, ma zemren, si ddin nnwen. Win, segwen, iff$en i ddin is, ad immet d ajehli. Widak, mêan igiten nnsen di ddunit, di laxeôt. Widak, d at tmes, degs, nitni, ad wekkôen.');
INSERT INTO "ramdane" VALUES(2,218,'2:218','Ih, wid iumnen, win iunagen u juhden deg ubrid n Öebbi, widak ggunin aêunu n Öebbi. Öebbi Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(2,219,'2:219','La k steqsayen $ef ccôab akked leqwmeô. Ini: "degsen abekkav meqqwôen, akked ibu$ar i medden. Abekkav nnsen i$leb ibu$ar nsen". La k steqsayen, acu ara seddqen. Ini: "ayen iugaren". Akka i wen Ipbeggin Öebbi issekniyen, amar a pmeyyzem');
INSERT INTO "ramdane" VALUES(2,220,'2:220','af ddunit akked laxeôt. La k steqsayen $ef igujilen. Ini: "ppif ma txedmem asen lxiô. Ma txulvem ten, d atmaten nnwen". Öebbi Ifrez ger ufessad akked uâaôvi. Ma ihwa yaS i Öebbi, a kwen Icebbwel. Ih, Öebbi d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(2,221,'2:221','Ur ppa$et timcurak alamma umnent. Taklit iumnen tif tamcarekt, xas taâoeb ikwen. Ur pnasabet imcurak alamma umnen. Akli iumnen yif amcarek, xas iaâoeb ikwen. Widak, neccden ar tmes. Öebbi Inecced ar Loennet akked ssmaê, ma Irad. Ipbeggin issekniyen iS i medden, amar a d mmektin.');
INSERT INTO "ramdane" VALUES(2,222,'2:222','La k steqsayen $ef tarda. Ini: "d aîan. Âazlet tilawin di tarda, ur pqeôôibet $uôsent alamma ûfant. Mi ûfant, qeôôbet $uôsent, akken i wen Iumeô Öebbi". Ih, Öebbi Iêemmel wid iutuben, Iêemmel wid izzizdigen.');
INSERT INTO "ramdane" VALUES(2,223,'2:223','Tilawin nnwen d aêôiq n tyerza nnwen. Melmi teb$am, aset s aêôiq nnwen. Zzewret lxiô i yiman nnwen, êezzbet i Öebbi. Cfut ar kunwi a T temmagrem. Becceô lmumnin.');
INSERT INTO "ramdane" VALUES(2,224,'2:224','Ur ssekcamet Öebbi mara teggallem a ppizdigem, a pêezzbem, a ppessemûalêem medden. Öebbi Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(2,225,'2:225','Ur kwen Ipqissi ara Öebbi $ef tgallit $ef awal. Maca, a kwen Iqiss $ef wayen kesben wulawen nnwen. Öebbi Ipsemmiê, Iêellem.');
INSERT INTO "ramdane" VALUES(2,226,'2:226','Wid iggullen ur pqeôôiben ar tlawin nnsen, ad gganin kué wagguren. Ma bedlen ôôay, ihi, Öebbi Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(2,227,'2:227','Ma âazmen berru, ihi, Öebbi Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(2,228,'2:228','Timnufaq ad aôount krav tardiwin. Ur asent iêell ara ad ffrent ayen Ixleq Öebbi, di tâabbav nnsent, ma umnent s Öebbi akked wass aneggaru. Ma b$an irgazen nnsent ad mûalaêen, izzewr iten lêal a tent id rren, di lmudda nni. Akken pwalasent ay ppalasent, s lqanun. I yirgazen, asurif $er loiha nnsent. Öebbi d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(2,229,'2:229','Berru, sin iberdan; tiririt, s lqanun; aserreê s lewqama. Ur awen iêell ara a ppedmem kra, seg wayen i sent tefkam - siwa ma ugaden ur pqadaôen ara tilas n Öebbi. Ma tugadem ur pqadaôen ara tilas n Öebbi, ulac u$ilif fellasen $ef wayen is ara d tefdu iman is. D tigi ay d tilas n Öebbi, ur tent zegret. Wid izegren tilas n Öebbi, widak, d imednas.');
INSERT INTO "ramdane" VALUES(2,230,'2:230','Ma ibra yas, ur as têell, imiren, alamma tu$ argaz nniven. Ma ibra yas, ulac u$ilif fellasen ma u$alen ar din, ma walan qudôen tilas n Öebbi. D tigi ay d tilas n Öebbi. Ibeggen itent i ugdud issnen.');
INSERT INTO "ramdane" VALUES(2,231,'2:231','Ma tebram i tlawin, u sâaddant tallit nnsent, erret pent id, s lqanun, ne$ serrêet asent, s lqanun. Ur tent pparat s uêeppem, a ppeccvem. Win ixedmen akka, idens iman is. Ur ppuraret s issekniyen n Öebbi. Mmektit ed ayen i kwen I$at Öebbi, akked wayen i wen d Issers si Tezmamt, akw si ûûwab. Ipnabi kwen yis. Ëezzbet i Öebbi. Cfut ar Öebbi Issen yal cci.');
INSERT INTO "ramdane" VALUES(2,232,'2:232','Ma tebram i tlawin, u sâaddant tallit nnsent, ur asent tekset ad a$ent irgazen nnsent, ma mwafaqen s lqanun. Ar waya ay pwanecden wid, segwen, iumnen s Öebbi, akked wass aneggaru. Ppif akka, i tezdeg akked ûûfawa nnwen. Öebbi Issen, kunwi ur tessinem.');
INSERT INTO "ramdane" VALUES(2,233,'2:233','Tiyimmatin ad ssuîvent arraw nnsent, sin iseggwasen mden, i win ib$an ad itim ussuîev. £ef ubabat, lmakla akked lebsa nnsent, s lqanun. Ur ipwakellaf yiman siwa ar wayen iwumi izmer. Tarwa ur teîîuôôu imma s, ur teîîuôôu baba s. I uweôôat, am akka. Ma b$an ad kksen tuîîva, mcawaôen, ulac u$ilif fellasen. Ma teb$am tamôebbit, a ppessuîîev tarwa nnwen, ulac u$ilif fellawen, ma txelûem ayen i wen ilaqen, s lqanun. Ëezzbet i Öebbi. Cfut ar Öebbi Ipwali ayen txeddmem.');
INSERT INTO "ramdane" VALUES(2,234,'2:234','Wid, segwen, issuff$en taôwiêt, u ooan d tilawin, ad aôount kué wagguren akked mraw wussan. Mi sâaddant tallit nnsent, ulac u$ilif fellawen deg wayen xedment deg iman nnsent, di lqanun. Öebbi Iéôa ayen txeddmem.');
INSERT INTO "ramdane" VALUES(2,235,'2:235','Ulac u$ilif fellawen, ma tennam d a pxevbem tilawin, ne$ ma teffrem. Iéôa Öebbi, ar tesmektayem tent id. Maca, ur asent âahdet, di tbavnit, siwa ma tennam awal uwqim. Ur ferrut $ef lemlak, alamma ibbwev lajel iuran. Cfut ar Öebbi Iéôa i illan degwen. Ëadret iman nnwen segS. Cfut ar Öebbi Ipsemmiê, Iêellem.');
INSERT INTO "ramdane" VALUES(2,236,'2:236','Ulac u$ilif fellawen ma tebram i tlawin ur tennulem, ne$ iwumi ur tefkim lwajeb. Efket asent tikci iweqmen. Asâay s tezmert is, igellil s tezmert is. D lwajeb i iâaôviyen.');
INSERT INTO "ramdane" VALUES(2,237,'2:237','Ma tebram asent, uqbel a tent tennalem, u, iu$ lêal, tefkam asent lwajeb, ihi, azgen n wayen i sent tefkam, siwa ma semêent, ne$ isemmeê usemsaô n jjwao. Ssmaê iqôeb nezzeh ar uêezzeb. Ur tepput lfevl garawen. Öebbi, s tidep, Ipwali ayen txeddmem.');
INSERT INTO "ramdane" VALUES(2,238,'2:238','Ëarbet $ef téilla, u taéallit talemmast. Beddet i Öebbi, s wannuz.');
INSERT INTO "ramdane" VALUES(2,239,'2:239','Ma tugadem, s ibeddi, ne$ di rrekba. Ma tellam di laman, mmektit ed Öebbi, akken i wen Isselmed ayen ur tessinem.');
INSERT INTO "ramdane" VALUES(2,240,'2:240','Wid segwen issuff$en taôwiêt, u ooan d tilawin, ad asent vemnen aseggwas n wuççi, ur tent ssuffu$en. Ma ff$ent, ulac u$ilif fellawen, deg wayen xedment, deg iman nnsent, di lqanun. Öebbi d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(2,241,'2:241','I tembwurab taqviât, s lqanun. D lwajeb $ef wid ipêezziben.');
INSERT INTO "ramdane" VALUES(2,242,'2:242','Akka i wen Ipbeggin Öebbi issekniyen iS, amar a pêemûem.');
INSERT INTO "ramdane" VALUES(2,243,'2:243','Day ur twalav ara wid iff$en, seg ixxamen nnsen, d igima, ugaden tameppant. Inna yasen Öebbi: "emmtet"! Sinna, Issader ten id. S tidep, Öebbi d Bab n lfevl $ef yimdanen, maca tegwti n yimdanen ur snemmiren.');
INSERT INTO "ramdane" VALUES(2,244,'2:244','Nna$et deg ubrid n Öebbi. Cfut ar Öebbi Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(2,245,'2:245','Win iôevlen aôeîîal umâin i Öebbi, ad as t id Irr s lufa. Öebbi Ippaîaf, Ismiri. £uôeS a ppu$alem.');
INSERT INTO "ramdane" VALUES(2,246,'2:246','Day ur twalav ara imvebbôen seg wAt Isôail, deffir Musa? Mi nnan i nnbi nnsen: "azen a$ d agellid, a nenna$ deg ubrid n Öebbi". Inna: "ma iura fellawen umennu$, day a ppagim a ppenna$em"? Nnan: "ur a$ issefk ara a neqqim ur nepna$ ara, deg ubrid n Öebbi. Ni$ ssuff$en a$ d, nekwni akked tarwa nne$, seg ixxamen nne$". Mi iura fellasen umennu$, wexxôen, siwa drus segsen. Öebbi Issen imednas.');
INSERT INTO "ramdane" VALUES(2,247,'2:247','Inna yasen nnbi nnsen: "Ni$ Öebbi, s tidep, Iuzen awen d Ïalut, d agellid". Nnan: "amek ara yili d agellid nne$? Nezwar it akken neklal tagelda. Ur as ippunefk wayla". Inna: "Öebbi, s tidep, Ifren it garawen, u Irna yas aîas n tmusni akked loeppa". Ippak Öebbi tagelda S i win i S ihwan. Öebbi d Ajemmal, d Amusnaw.');
INSERT INTO "ramdane" VALUES(2,248,'2:248','Inna yasen, nnbi nnsen: "s tidep, assekni n tgelda s, d tisin $uôwen usenduq. Degs talwit s$uô Mass nnwen, akked wayen id igwran, seg wayen id teooa twacult n Musa, akked tin n Haôun. A t id awint lmalayek. Ih, deg waya, d assekni i kunwi, ma d lmumnin ay tellam".');
INSERT INTO "ramdane" VALUES(2,249,'2:249','Mi iffe$ Ïalut, s lâaskeô, inna: "Öebbi, s tidep, a kwen Ijeôôeb s wasif. Win iswan segs, ur illi yidi. Win ur neswi, neppa, s tidep, yidi. Kkes ed win iswan cwiî, deg ufus is". Swan segs, siwa ixeîîayen segsen. Mi zegren, neppa akked wid iumnen yides, nnan: "ur nezmir assa, i Oalut akked lâaskeô is". Nnan wid issarmen ar nitni ad mlilen d Öebbi: "acêal d abrid, îayfa tameééyant te$leb îayfa tameqwôant, ma Irad Öebbi". Öebbi ar iûebôiyen.');
INSERT INTO "ramdane" VALUES(2,250,'2:250','Mi qublen Oalut akked lâaskeô is, nnan: "a Mass nne$! Azen a$ d ûûbeô, Tebbet isurifen nne$, Awes a$ mgal agdud ijehliyen".');
INSERT INTO "ramdane" VALUES(2,251,'2:251','Irad Öebbi, rebêen ten. Dawed in$a Oalut. Ifka yas Öebbi tagelda akked ûûwab. Isselmed as seg wayen i S ihwan. Lemmer ur Issemnamar ara Öebbi, imdanen, a ppefsed tmurt. Maca, Öebbi d Bab n lfevl $ef imavalen.');
INSERT INTO "ramdane" VALUES(2,252,'2:252','Atnan issekniyen n Öebbi; Nemmal ak ten, s tidep. Keçç, war ccekk, seg imazanen.');
INSERT INTO "ramdane" VALUES(2,253,'2:253','Imazanen agi, Nesmenyaf kra i wiyav. Garasen, wid iwumi Immeslay Öebbi. Issuli kra segsen, tiseddaôin. Nefka abeggen i Âisa, mmi s n Meryem, u Nekfa t s ôôuê iôqan. Lemmer i S ihwi i Öebbi, ur ppemyen$an wid i ten id ivefôen, deffir n tisin ubeggen $uôsen. Maca, mgarraden. Segsen wid iumnen, segsen wid ijehlen. Lemmer i S ihwi i Öebbi, ur ppemyen$an, maca Öebbi Ixeddem ayen i S ihwan.');
INSERT INTO "ramdane" VALUES(2,254,'2:254','A wid iumnen! Seddqet seg wayen ikwen N$at, uqbel tisin n wass ideg ur telli ttjara, ne$ tiddukla, ne$ ccafuâa. Ijehliyen, d nitni ay d imednas.');
INSERT INTO "ramdane" VALUES(2,255,'2:255','Öebbi, ur illi ôebbi siwa Neppa, Amuddir, Imezgi. Ur Ipnuddum, ur Iggan. IneS i illan deg igenwan, i illan di tmurt. Anwa akka ara icufâan $uôeS, siwa ma s tsriê iS? Issen i illan zdat sen, i illan deffir sen. Ur êemûen kra, si tmusni S, siwa ayen i S ihwan. Lewsaâ usgeld iS, d tamurt akked igenwan. Ur T issaâtab uêareb fellasen. Neppa, d Aâlayan, d Ameqwôaêan.');
INSERT INTO "ramdane" VALUES(2,256,'2:256','Ddin maççi s yi$il. Ni$ tepwafôez lewqama si l$welîa. Win iugin iôebbiten n tkerkas u iumen s Öebbi, ihi, iîîef afus ioehden, ur nceqqeq. Öebbi Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(2,257,'2:257','Öebbi d Bab n wid iumnen. Issuffe$ iten id si tillas ar tafat. Imawlan n wid ijehlen, d iôebbiten n tkerkas. Ssuff$en ten id si tafat ar tillas. Widak, d at tmes, nitni, degs ad wekkôen.');
INSERT INTO "ramdane" VALUES(2,258,'2:258','Day ur twalav ara win inumren Ibôahim $ef Mass is, acku Ifka yas Öebbi tagelda? Mi inna Ibôahim: "Mass iw d win Issidiren, Ineqqen", inna d: "nek ssidire$, neqqe$". Inna Ibôahim: "ihi, Öebbi Issenqaô ed iîij seg ugmuv; keçç, ssenqeô it id seg utaram"! Inneêcam win ijehlen. Öebbi ur Inehhu yara agdud imednas.');
INSERT INTO "ramdane" VALUES(2,259,'2:259','Ne$ am win iâaddan di temdint tilemt, thudd wakali. Inna: «amek ara d Issider Öebbi tagi, deffir tmeppant is?» In$a t Öebbi twinas iseggwasen, sinna, Issenker it, Inna: «acêal teqqimev?» Inna d: «qqime$ ibwass, ne$ ur ibbwiv wass». Inna: «xaîi, teqqimev twinas iseggwasen. Muqel lqut ik akked tissit ik, ur xûiôen. Muqel a$yul ik. A k Nerr d assekni i medden. Muqel i$san amek ara ten Nessemlil, sinna, ad asen Nessels aksum». Mi ipputebbet, inna: «éôi$ Öebbi Izmer i yal cci».');
INSERT INTO "ramdane" VALUES(2,260,'2:260','U mi inna Ibôahim: «a Mass iw! Ssenâat iyi amek tessidirev lmeggtin». Inna: «day ur tuminev ara?» Inna: «xaîi, maca iwakken ad ithedden wul iw». Inna: «ihi, îîef kué igvav, awi ten id $uôek, sinna, err, di yal i$il, amur segsen. Sinna, siwel asen, a d asen dumatu. Cfu ar Öebbi d Uohid, d Uûwib».');
INSERT INTO "ramdane" VALUES(2,261,'2:261','Wid ipseddiqen ayla nnsen deg ubrid n Öebbi, cuban aâqqa id igren sa tyedrin. Di yal taydrep, twinas iâqqayen. Öebbi Issugut i win i S Ihwan. Öebbi d Ajemmal, d Amusnaw.');
INSERT INTO "ramdane" VALUES(2,262,'2:262','Wid ipseddiqen ayla nnsen deg ubrid n Öebbi, ur seddan, i wayen ssedqen, tiyta ne$ rregmat, ad awin arraz nnsen s$uô Mass nnsen; ur ppagwaden, ur $weblen.');
INSERT INTO "ramdane" VALUES(2,263,'2:263','Awal imaânen akked ssmaê, ifen aseddeq i vefôent rregmat. Öebbi d Asâay, Iêellem.');
INSERT INTO "ramdane" VALUES(2,264,'2:264','A wid iumnen! Ur ssemsayet ssadaqa nnwen, s tiyta akked rregmat. Am win iseddqen ayla s, iwakken a t walin medden, ur iumin s Öebbi akked wass aneggaru. Icuba taéôut i i$umm wakal. Iwwet fellas uzayev, iooa p d asuki. Ur sfaydin acemma seg wayen bbwin. Öebbi ur Inehhu ara agdud ijehliyen.');
INSERT INTO "ramdane" VALUES(2,265,'2:265','Wid ipseddiqen ayla nnsen, ssaramen ad Iôavi Öebbi, u tebbten iman nnsen, cuban leonan di ti$ilt. Iwwet fellas ugeffur, ifka d sin imuren n l$ella s. Ma ur iwwit fellas ugeffur, d nnda. Öebbi Ipwali ayen txeddmem.');
INSERT INTO "ramdane" VALUES(2,266,'2:266','Yiwen degwen, ma tusa t id tewser, u $uôes tarwa tuôhift, day ad issirem ad isâu leonan n tezdayin akked tfeôôant, isaffen ppazzalen ddaw as, degs, i neppa, yal ppemôa? Têuza t tnifift degs times, iô$a. Akka i wen Ipbeggin Öebbi issekniyen, amar a pmeyyzem.');
INSERT INTO "ramdane" VALUES(2,267,'2:267','A wid iumnen! Seddqet seg wayen imaânen i teîfem, seg wayen i wen d Nessuffe$ si tmurt. Ur fernet ucmit, segs, ayen ur teppadamem siwa ma s tuqna n wallen, a t tseddqem. Cfut ar Öebbi d Asâay, d Amaâzuz.');
INSERT INTO "ramdane" VALUES(2,268,'2:268','Cciîan iâahed awen tiggulelt, iumeô awen lâaô. Öebbi Iâahed awen ssmaê s$uôeS akked lfevl. Öebbi d Ajemmal, d Amusnaw.');
INSERT INTO "ramdane" VALUES(2,269,'2:269','Ippak ûûwab i win i S Ihwan. Win iwumi ippunefk ûûwab, ni$ ippunefk as aîas n lxiô. Siwa uâqilen i ismektayen.');
INSERT INTO "ramdane" VALUES(2,270,'2:270','Ayen tefkam useddeq, ne$ teqqnem n tuqqna, Öebbi, s tidep, Iéôa t. Ur llin imâiwnen i imednas.');
INSERT INTO "ramdane" VALUES(2,271,'2:271','Ma tefkam ssadaqat âinani, ilha. Yif it ma s tuffra, u ma tefkam tent i ime$ban. Ad pwakksent kra si tucmitin nnwen. Öebbi Ippuxebbeô $ef wayen txeddmem.');
INSERT INTO "ramdane" VALUES(2,272,'2:272','Ur fellak a ten tenhuv. Maca Öebbi Inehhu win i S Ihwan. Ayen tefkam n lxiô, i iman nnwen. Ur tefkim siwa $ef udem n Öebbi. Ayen tefkam n lxiô, a kwen id iu$al, u kunwi ur kwen dennisen.');
INSERT INTO "ramdane" VALUES(2,273,'2:273','I ime$ban ifelsen deg ubrid n Öebbi, ur nezmir ad inigen di tmurt; wur nessin, inwa ten d imeôkantiyen, seg wakken êekmen. A ten tâaqlev ar tiddi nnsen: ur ssuturen imdanen s nnbec. Ayen tefkam n lxiô, s tidep, Öebbi Iéôa t.');
INSERT INTO "ramdane" VALUES(2,274,'2:274','Wid ifkan ayla nnsen deg iv, deg wass, s tuffra u âinani, ad awin arraz nnsen ar Mass nnsen; ur ppagwaden, ur $weblen.');
INSERT INTO "ramdane" VALUES(2,275,'2:275','Wid iteppen rrba, ur d pnekkaren siwa am ara d kren win issexbibev Cciîan, mi t innul. Ayagi, acku nnan: «ttjara am rrba kan». Öebbi Iêell ttjara, Igdel rrba. Win n tusa lewûaya s$uô Mass is, d$a ixva, ines ayen iâaddan. Lameô is ar Öebbi. Wid iu$alen, widak d at tmes, nitni, degs, ad wekkôen.');
INSERT INTO "ramdane" VALUES(2,276,'2:276','Ad Imêu Öebbi rrba, ad Isbuôket ssadaqa. Öebbi ur Iêemmel ara yal ajehli amednub.');
INSERT INTO "ramdane" VALUES(2,277,'2:277','Ih, wid iumnen, u xeddmen ayen ilhan, u péallan, u pzekkin, ad awin arraz nnsen ar Mass nnsen; ur ppagwaden, ur $weblen.');
INSERT INTO "ramdane" VALUES(2,278,'2:278','A wid iumnen! Ëezzbet i Öebbi. Eooet ayen d igwran si rrba, ma d lmumnin ay tellam.');
INSERT INTO "ramdane" VALUES(2,279,'2:279','Ma ur tewqimem ara, êûut ara kwen Inna$ Öebbi akked Umazan iS. Ma tnedmem, nnwen ôaselmal. Ur denniset, ur pwadenset.');
INSERT INTO "ramdane" VALUES(2,280,'2:280','I win illan di lhejna, aôout liser. Ma tseddqem, yif it i kunwi, lemmer teéôim.');
INSERT INTO "ramdane" VALUES(2,281,'2:281','Ëezzbet i wass n tu$alin nnwen ar Öebbi. Imiren, yal yiwen ad as ippunefk wayen ikseb; nitni ur pwadensen.');
INSERT INTO "ramdane" VALUES(2,282,'2:282','A wid iumnen! Ma tesâam îlaba ar lajel ipwassnen, arut ep. A p iaru umaru segwen, s ûûfawa. Ur ippagwi umaru a p iaru, akken i s Isselmed Öebbi. Ad iaru ayen ar as yini umeîîalbu. Ad iêezzeb i Öebbi, Mass is. Ur issenqas acemma segs. Ma illa umeîîalbu d abuhal, ne$ d uôhif, ne$ ur izmir ad yini neppa, ihi, ad yini umeddakwel is s ûûfawa. Sbeddet sin inagan seg irgazen nnwen. Ma ur llin ara sin irgazen, argaz akked snat n tlawin, seg wid tôuvam ad cehhden. Ma te$lev yiwet segsent, tayev a d temmekti. Ur ppagwin inagan, ma pwanecden. Ur stehzayet a p tarum, meééiyet ne$ meqqwôet, ar lajel is. Ayagi iwzen kteô ar Öebbi, iwata kteô i ccada, iwulem kteô ad awen ikkes ccekk. Kkes ed ssuq i tefram imiren garawen; ulac u$ilif fellawen, ma ur t turim ara. Awit inagan deg uzenzi nnwen. Ur ippili ujuôu $ef umaru ne$ $ef unagi. Ma tjuôem, ihi, dir ikwen. Ëezzbet i Öebbi. Öebbi Isselmad ikwen. Öebbi Iéôa yal cci.');
INSERT INTO "ramdane" VALUES(2,283,'2:283','Ma d iminigen ay tellam, ur tufim amaru, a ppeppunefk ôôhina. Ma ifka yiwen segwen lamana i wayev, win iwumi teppunefk, a p irr i win i s ifkan lamana s. Ad iêezzeb i Öebbi, Mass is. Ur ppeffret ccada. Win ip iffren, ihi, neppa, idneb wul is. Öebbi Iéôa ay txeddmem.');
INSERT INTO "ramdane" VALUES(2,284,'2:284','N Öebbi i illan deg igenwan, i illan di tmurt. Ama tesseknem i illan deg iman nnwen, ne$ te$bam t, a kwen Iêaseb Öebbi fellas. Ad Isemmeê i win i S Ihwan, ad Iâaqeb win i S Ihwan. Öebbi Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(2,285,'2:285','Iumen Umazan s wayen id iusan, $uôes, s$uô Mass is. Lmumnin, akw, umnen s Öebbi akked lmalayek iS, Tizmamin iS, imazanen iS. Ur nesemgirrid yiwen ger imazanen iS. Nnan: «nesla, nunez. Ssmaê iK a Mass nne$! £uôeK tu$alin.');
INSERT INTO "ramdane" VALUES(2,286,'2:286','Ur Ipkellif Öebbi yiman, siwa ar wayen iwumi izmer. Ines wayen ikseb, mgal is wayen iddem. A Mass nne$! Ur a$ Pqassa ma neppu, ne$ neccev. A Mass nne$! Ur a$ Sbibbi taâkkwemt akken ip Tesbabbev i wid i $ d izwaren. A Mass nne$! Ur a$ Sbibbi ayen iwumi ur nezmir. Semmeê ane$, Suref a$, Ëun fellane$. Keçç d Bab nne$, Srebê a$ $ef ugdud ijehliyen.');
INSERT INTO "ramdane" VALUES(3,1,'3:1','Alm.');
INSERT INTO "ramdane" VALUES(3,2,'3:2','Öebbi, ur illi ôebbi siwa Neppa, Amuddir, Imezgi.');
INSERT INTO "ramdane" VALUES(3,3,'3:3','Issers ak d Tazmamt s tidep, d atebbet n wayen ip id izwaren. Issers ed Tuôa akked Linoil,');
INSERT INTO "ramdane" VALUES(3,4,'3:4','uqbel, d nnhaya i medden. Issers ed Leqwôan. Ih, wid inekôen issekniyen n Öebbi, fellasen aâaqeb uqûiê. Öebbi d Uzmir, Ippara d ppaô.');
INSERT INTO "ramdane" VALUES(3,5,'3:5','Ih, Öebbi ur aS iffir wacemma di tmurt, ne$ deg igenni.');
INSERT INTO "ramdane" VALUES(3,6,'3:6','D Neppa i wen Ippaken udem, di tâabbav, akken i S ihwa. Ur illi ôebbi siwa Neppa, Uzmir, Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(3,7,'3:7','Neppa i k ed Issersen Tazmamt, degs issekniyen uvhiôen, nitni d tayimmap n Tezmamt. Wiyav d licwaô. D$a, wid iwumi mmalen wulawen, teddun akked wayen illan d licwaô, ssaramen ahewwel, ssaramen asefhem is. I Issnen asefhem is, siwa Öebbi. Qqaôen imawlan n tmusni: "numen yis. S$uô Mass nne$ akw". Id ismektayen, siwa uâqilen.');
INSERT INTO "ramdane" VALUES(3,8,'3:8','A Mass nne$! Ur Smili ulawen nne$, mbaâd ma Tenhiv a$. Efk a$ d, s$uôeK, aêunu. Ih, Keçç, ard Am$it.');
INSERT INTO "ramdane" VALUES(3,9,'3:9','A Mass nne$! Keçç, s tidep, ara Ijemmlen medden, ass i$ef ur illi ccekk. S tidep, Öebbi ur Izeggel lemâahda.');
INSERT INTO "ramdane" VALUES(3,10,'3:10','Ih, wid ijehlen, ur sen d ippawi acemma, ar Öebbi, wayla nnsen, ne$ tarwa nnsen. Widak, d nitni ay d isufa n tmes.');
INSERT INTO "ramdane" VALUES(3,11,'3:11','D tin n ugdud n Ferâun akked wid i ten id izwaren. Skaddben issekniyen nne£. Iwwet iten Öebbi, ilmend ibekkaven nnsen. Öebbi Iwâaô i uâaqeb.');
INSERT INTO "ramdane" VALUES(3,12,'3:12','Ini i wid ijehlen: "a ppepwa$elbem, a ppepwafesrem di Loahennama". A yir usu!');
INSERT INTO "ramdane" VALUES(3,13,'3:13','Ni$ d assekni, i kunwi, mi mlalent snat trebbuyaâ. Yiwet tepna$ deg ubrid n Öebbi; tayev d tajehlit, twala d snat, s yiéôi s. Öebbi Ippak afus, s tallalt iS, i win i S ihwan. Deg wayagi, s tidep, aselmed i wid ipwalin.');
INSERT INTO "ramdane" VALUES(3,14,'3:14','D tucbiêt ay d tedheô i yimdanen leêmala n ccehwiyyat: tilawin, tarwa, iâammuôen ibararen n ddheb akked lfeîîa, iâawdiwen n ccetla, lmal, akal. Ayagi, d zzhu n tmeddurt n ddunit. Öebbi, $uôeS amkan lâali, n tu$alin.');
INSERT INTO "ramdane" VALUES(3,15,'3:15','Ini: "ad awen mle$ i yifen aya? I wid ipêezziben, ar Mass nnsen, leonanat, ddawat sen isaffen ppazzalen, degsen ad wekkôen, tilawin iûfan, akked llza s$uô Öebbi". Öebbi Ipwali imdanen;');
INSERT INTO "ramdane" VALUES(3,16,'3:16','wid iqqaôen: "a Mass nne$! Nekwni, s tidep, numen. Ihi, Semmeê a$ ibekkaven nne$, Sellek a$ seg uâaqeb n tmes".');
INSERT INTO "ramdane" VALUES(3,17,'3:17','Iûebôiyen, imawlan n tidep, imvuâen, wid ipseddiqen, wid iste$fiôen di tafrara.');
INSERT INTO "ramdane" VALUES(3,18,'3:18','Icuhhed Öebbi ar ur illi ôebbi, siwa Neppa. Akken lmalayek, akked imawlan n tmusni. Ibedd i lmizan. Ur illi ôebbi, siwa Neppa, Uzmir, Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(3,19,'3:19','Ih, ddin ar Öebbi d annuz. Ur mgarraden wid iwumi teppunefk Tezmamt, armi deffir n tisin n tussna $uôsen, wa iqheô wa. Wid inekôen issekniyen n Öebbi, ihi, Öebbi Ip$awal deg uqeîîi.');
INSERT INTO "ramdane" VALUES(3,20,'3:20','Ma nuqmen k, ini: "unze$ aS i Öebbi. Akken, wid iddan yidi. Ini i wid iwumi teppunefk Tezmamt, akked at war tussna: "ma tunzem"? Ma unzen, ihi, pwanhan. Ma wexxôen, ay fellak siwa anabi. Öebbi Ipwali imdanen.');
INSERT INTO "ramdane" VALUES(3,21,'3:21','Ih, wid inekôen issekniyen n Öebbi, u pna$en lambiyya, war ssebba, u pna$en wid seg yimdanen ippamôen lmizan, becceô iten s uâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(3,22,'3:22','Widak, d wid iwumi mêan igiten nnsen di ddunit, di laxert. Ur sâin isellaken.');
INSERT INTO "ramdane" VALUES(3,23,'3:23','Day ur twalav ara wid iwumi ippunefk kra si Tezmamt, la ssuturen, i Tezmamt n Öebbi, aêuddu garasen? Sinna, ad iwexxeô urbaâ segsen, u nitni pnamaren.');
INSERT INTO "ramdane" VALUES(3,24,'3:24','Ayagi, acku nnan: "ur a$ teppep tmes, siwa kra n wussan". I$uôô iten, di ddin nnsen, wayen id snulfuyen.');
INSERT INTO "ramdane" VALUES(3,25,'3:25','Amek ihi, ma Njemmel iten, ass ideg, ur degs ccekk, yal yiwen ad i$ellet ayen ikseb, nitni ur ppudensen.');
INSERT INTO "ramdane" VALUES(3,26,'3:26','Ini: "a Öebbi, Agellid n tgelda, Teppakev tagelda i win i K ihwan, Teppeksev tagelda i win i K ihwan, Tepâuzzuv win i K ihwan, Tesdulluv win i K ihwan. Lxiô deg ufus i K. Ih, Keçç, Tzemrev i yal cci.');
INSERT INTO "ramdane" VALUES(3,27,'3:27','Tessekcamev iv deg wass. Tessekcamev ass deg iv. Tessuffu$ev amuddir si lmegget. Tessuffu$ev lmegget seg umuddir. Tep$itiv win i K ihwan, war aktili".');
INSERT INTO "ramdane" VALUES(3,28,'3:28','Lmumnin ur pparan ijehliyen d iêbiben nnsen, war lmumnin. Win ixedmen aya, ur illi, s wacemma, ar Öebbi. Kkes ed aêezzeb i têezzbem iman nnwen segsen. Iâaggen awen Öebbi s yiman iS. Tu$alin ar Öebbi.');
INSERT INTO "ramdane" VALUES(3,29,'3:29','Ini: "ama teffrem i illan deg idmaren nnwen, ne$ tessnaâtem t, Öebbi Iéôa t". Iéôa i illan deg igenwan, i illan di tmurt. Öebbi Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(3,30,'3:30','Ass ideg yal iman ad iaf irs ed, wayen ixdem n lxiô, akked wayen ixdem n cceôô, ad issirem a ppili, garas yides, tallit $ezzifen! Iâaggen awen Öebbi, af yiman iS. Öebbi d Umâin ar imdanen.');
INSERT INTO "ramdane" VALUES(3,31,'3:31','Ini: "ma têemmlem Öebbi, ddut yidi. A kwen Iâuzz Öebbi, ad awen Isemmeê ibekkaven nnwen. Öebbi Ipsemmiê, Ipêunu".');
INSERT INTO "ramdane" VALUES(3,32,'3:32','Ini: "ssemêesset i Öebbi akked Umazan". Ma wexxôen, ihi Öebbi ur Iêemmel ara ijehliyen.');
INSERT INTO "ramdane" VALUES(3,33,'3:33','Ih, Öebbi Ismenyaf, i imavalen, Adem, Nuê, tawacult n Ibôahim, tawacult n Âimôan.');
INSERT INTO "ramdane" VALUES(3,34,'3:34','Wa iurew wa. Öebbi Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(3,35,'3:35','Mi tenna tmeîîut n Âimôan: "a Mass iw! Wekkle$ aK ayen illan di tâabbuî iw, d lewkala, Eqbel s$uôi. Keçç, ih, d win Isellen, d Amusnaw".');
INSERT INTO "ramdane" VALUES(3,36,'3:36','Mi p id turew, tenna: "a Mass iw! Urwe$ p id d nnta". - Öebbi Iéôa, sennig akw, d acu d turew. Ddkeô, maççi am nnta. - "Nek semma$ as Meryem, rri$ p seddaw tecvaî iK, neppat akked tarwa s, mgal Cciîan imenfi".');
INSERT INTO "ramdane" VALUES(3,37,'3:37','Iôêeb yis Mass is, asteôêeb ilhan; Isgem ip, tigmi ilhan; Ikwellef ip i Zakariyya. Yal tikelt ara iôuê $uôes Zakariyya ar lmiêreb, a n iaf tesâa ôôeéq. Inna: "a Meryem! Ansi i m d ikka waya"? Tenna: "s$uô Öebbi. Öebbi, s tidep, Iôeééeq win i S ihwan, war aktili".');
INSERT INTO "ramdane" VALUES(3,38,'3:38','D$a Zakariyya inuj $uô Mass is, inna: "a Mass iw! Efk iyi d, s$uôeK, tarwa ifazen. Keçç, s tidep, Tsellev i unuju".');
INSERT INTO "ramdane" VALUES(3,39,'3:39','Mi ibedd, ipéalla di lmeêreb, ssawlent as d lmalayek: "Ibecceô ik Öebbi s Yaêia, d atebbet n wawal n Öebbi, d ssid, d azedgan, d nnbi, seg iâaôviyen".');
INSERT INTO "ramdane" VALUES(3,40,'3:40','Inna: "a Mass iw! Amek ara sâu$ aqcic? Ni$ wessre$, tameîîut iw d tiâiqeôt". Inna: "akka, Öebbi Ixeddem ayen i S ihwan".');
INSERT INTO "ramdane" VALUES(3,41,'3:41','Inna: "a Mass iw! Efk iyi d assekni". Inna: "assekni ik, ur tepmeslayev i medden krav wussan. As$al kan. Mmekti d aîas Mass ik, sbuccaâ iT tameddit, tafrara".');
INSERT INTO "ramdane" VALUES(3,42,'3:42','Mi s nnant lmalayek: "a Meryem! Öebbi, s tidep, Ifren ikem, Izzazdeg ikem, Ismenyaf ikem i tlawin imavalen.');
INSERT INTO "ramdane" VALUES(3,43,'3:43','A Meryem! Anez i Mass im, knu yaS, îîinez akked wid iîîinizen".');
INSERT INTO "ramdane" VALUES(3,44,'3:44','Ayagi si tmucuha i iffren, Nweêêa yak t. Ur telliv yidsen, mi jebden taseqqaôt, anwa segsen ara itkelfen $ef Meryem. Ur telliv yidsen mi mqellaâen.');
INSERT INTO "ramdane" VALUES(3,45,'3:45','Mi s nnant lmalayek: "a Meryem! Öebbi, s tidep, Ibecceô ikem s wawal s$uôeS, ism is: lmasiê Âisa, mmi s n Meryem; mucaâ di ddunit akked laxert; seg iqôiben.');
INSERT INTO "ramdane" VALUES(3,46,'3:46','A d ipmeslay i medden, si dduê, am unqaqôi, ad yili seg iâaôviyen".');
INSERT INTO "ramdane" VALUES(3,47,'3:47','Tenna: "a Mass iw! Amek ara sâu$ aqcic, ur iyi innul urgaz"? Inna: "akka. Öebbi Ixelleq ayen i S ihwan. Ma Iseyya lameô, ad as Yini kan: "ili"! Ad yili".');
INSERT INTO "ramdane" VALUES(3,48,'3:48','Ad as Isselmed Tazmamt, ûûwab, Tuôa, Linoil.');
INSERT INTO "ramdane" VALUES(3,49,'3:49','D amazan ar At Isôail: "ni$ nek usi$ d, $uôwen, s issekniyen s$uô Mass nnwen. Nek ad awen d xelqe$, s talaxt, ayen icban afôux, ad ûuve$ degs, ad iu$al d afôux, ma Ib$a Öebbi. Ad sseêlu$ ader$al, amuvin n lbeôû, a d ssidre$ lmeggtin, ma Ib$a Öebbi. Ad awen ini$ d acu teppeppem, akked wayen txezznem deg ixxamen nnwen. Deg waya, s tidep, d assekni i kunwi, ma d lmumnin ay tellam.');
INSERT INTO "ramdane" VALUES(3,50,'3:50','A d tebbte$ iyi d izwaren di Tuôa, ad awen êelle$ kra n wayen illan ipwagdel awen. Usi$ d, $uôwen, s ussekni, s$uô Mass nnwen. Ëezbet i Öebbi, a$et iyi awal.');
INSERT INTO "ramdane" VALUES(3,51,'3:51','Öebbi, s tidep, d Mass iw, u d Mass nnwen. Ihi, âabdet eP, d wagi ay d abrid uwqim".');
INSERT INTO "ramdane" VALUES(3,52,'3:52','Mi israê Âisa tijehli s$uôsen, inna: "anwi ay d imâiwnen iw ar Öebbi"? Nnan as d lexwan: "nekwni d imâiwnen n Öebbi. Numen s Öebbi, eched ar nekwni d imvuâen.');
INSERT INTO "ramdane" VALUES(3,53,'3:53','A Mass nne$! Numen s wayen id Tessersev, nedda d umazan, Aru ya$ seg inagan".');
INSERT INTO "ramdane" VALUES(3,54,'3:54','Undin. Iundi Öebbi. Öebbi Yif akw imendayen.');
INSERT INTO "ramdane" VALUES(3,55,'3:55','Mi s Inna Öebbi: "a Âisa! Nek ad Ddme$ ôôuê ik, a k id Ssali$ $uôI, a k id Ssiferre$ seg wid ijehlen, ad Rre$, wid iddan yidek, sennig wid ijehlen, ar ass n tnekra". Sinna, tu$alin nnwen $uôI, ad Ëudde$, garawen, $ef wayen i$ef teppemgarradem.');
INSERT INTO "ramdane" VALUES(3,56,'3:56','Ma d wid ijehlen, a ten Âaqbe$ di ddunit, di laxert, aâaqeb aqeûêan. Ur asen ppilin isellaken.');
INSERT INTO "ramdane" VALUES(3,57,'3:57','Wanag wid iumnen u xeddmen ayen ilhan, ad asen Ifk arrazen nnsen. Öebbi ur Iêemmel ara imednas.');
INSERT INTO "ramdane" VALUES(3,58,'3:58','Ayagi i ak Nemmal seg issekniyen, akked usmekti uûwib.');
INSERT INTO "ramdane" VALUES(3,59,'3:59','Ih, amedya n Âisa, ar Öebbi, am umedya n Adem. Ixelq it seg wakal, sinna, Inna yas: "ili"! Illa.');
INSERT INTO "ramdane" VALUES(3,60,'3:60','Tidep, s$uô Mass ik. Ur ppili seg wid ipcukkun.');
INSERT INTO "ramdane" VALUES(3,61,'3:61','I win i k inumren fellas, deffir wayen i k id iusan n tussna, ini: "yyaw a nenced tarwa nne$, tarwa nnwen, tilawin nne$, tilawin nnwen, iman nne$, iman nnwen. Sinna, a nemmesgill, a nd2âu ad Ixzu Öebbi ikeddaben.');
INSERT INTO "ramdane" VALUES(3,62,'3:62','Ih, tagi d tamacahup n tidep, ur illi ôebbi siwa Öebbi. Ih, Öebbi, Neppa, war ccekk, d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(3,63,'3:63','Ma wexxren, ihi Öebbi Issen ifessaden.');
INSERT INTO "ramdane" VALUES(3,64,'3:64','Ini: "a yAt Tezmamt! Yyaw ar wawal azduklan garane$ yidwen. A naâbed siwa Öebbi, ur aS nepcarak acemma; ur pparan, kra segne$ kra, d iôebbiten, war Öebbi". Ma wexxôen, init: "cehhdet ar nekwni d imvuâen".');
INSERT INTO "ramdane" VALUES(3,65,'3:65','A yAt Tezmamt! Acimi temnamarem $ef Ibôahim? Ur n risent Tuôa akked Linoil, armi deffir es, day ur tmeyyzem ara?');
INSERT INTO "ramdane" VALUES(3,66,'3:66','Kunwi, atan temnamarem $ef wayen tessnem. Ihi, acimi temnamarem $ef wayen ur tessinem? Öebbi Issen, kunwi ur tessinem.');
INSERT INTO "ramdane" VALUES(3,67,'3:67','Ur illi Ibôahim d uday ne$ d amasiêi, maca illa d amvuâ imalen ar tidep. Ur illi seg imcurak.');
INSERT INTO "ramdane" VALUES(3,68,'3:68','Ih, imdanen iqeôben nezzeh ar Ibôahim, war ccekk, d wid i t ivefôen, akked Nnbi agi, akked wid iumnen. Öebbi d Bab n lmumnin.');
INSERT INTO "ramdane" VALUES(3,69,'3:69','Arbaâ, seg At Tezmamt, ib$a a kwen itlef. Ara telfen siwa iman nnsen, ur éôin.');
INSERT INTO "ramdane" VALUES(3,70,'3:70','A yAt Tezmamt! Acimi tugim issekniyen n Öebbi, u kunwi tepwalim?');
INSERT INTO "ramdane" VALUES(3,71,'3:71','A yAt Tezmamt! Acimi teslusum, i tidep, ayen idensen, u teppeffrem tidep, kunwi teêûam?');
INSERT INTO "ramdane" VALUES(3,72,'3:72','Arbaâ, seg At Tezmamt, inna: "amnet mi iuli wass, s wayen id irsen ar wid iumnen, nekôet tameddit". Amendeô ma a d u$alen.');
INSERT INTO "ramdane" VALUES(3,73,'3:73','Ppamnet kan win ivefôen ddin nnwen. Ini: "ih, nnhaya d nnhaya n Öebbi. A ppeppunefk i yiwen am wayen i wen ippunefken". Ne$ a kwen namren af Mass nnwen. Ini: "lfevl, s tidep, deg ufus n Öebbi, Ippak it i win i S ihwan". Öebbi d Ajemmal, d Amusnaw.');
INSERT INTO "ramdane" VALUES(3,74,'3:74','Iâateq aêunu S i win i S ihwan. Öebbi d Bab n lfevl ameqwôaêan.');
INSERT INTO "ramdane" VALUES(3,75,'3:75','Seg wAt Tezmamt, illa win, xas teooiv as adrim aîas, ad ak t id irr. Illa wayev segsen, ma teooiv as adinar, ur ak t id ippara, alamma tezgiv $uôes. Akka, acku nitni nnan: "ur a$ d ppawven at war tussna". U ggaren d tikerkas $ef Öebbi, u nitni éôan.');
INSERT INTO "ramdane" VALUES(3,76,'3:76','Xaîi! Win iîîfen di lemâahda s, u iêezzeb...ihi, Öebbi Iêemmel wid ipêezziben.');
INSERT INTO "ramdane" VALUES(3,77,'3:77','Ih, wid ippa$en s lemâahda n Öebbi akked tgallit nnsen, ayen ôxisen, widak, ur ppawin amur di laxert, ur asen Ipmeslay Öebbi, ur ten id Ipmuqul, ass n tnekra, ur ten Izzizdig. Fellasen aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(3,78,'3:78','Ih, arbaâ segsen ssexsaôen Tazmamt s yilsawen nnsen, iwakken a p$ilem n Tezmamt, ayen ur nelli n Tezmamt. Qqaôen: "aya s$uô Öebbi", u neppa ur illi s$uô Öebbi. U qqaren tikerkas $ef Öebbi, u nitni éôan.');
INSERT INTO "ramdane" VALUES(3,79,'3:79','Ur ippunefk ara i umdan iwumi Ifka Öebbi Tazmamt, ûûwab, u Irra t d nnbi, ad iu$al ad yini i medden: "ilit d imdanen iw, war Öebbi". Maca: "ilit d iselmaden, imi tesselmadem Tazmamt, u tlemdem".');
INSERT INTO "ramdane" VALUES(3,80,'3:80','Ur awen Iumeô ara a pperrem lmalayek akked lambiyya, d imawlan. Day awen Iameô tijehli, mbaâd ma tellam d imvuâen?');
INSERT INTO "ramdane" VALUES(3,81,'3:81','Mi Iîîef Öebbi laâqed n lambiyya: "s tidep, Fki$ awen si Tezmamt akw si ûûwab, sinna, iusa kwen id umazan, i itebbten ayen illan $uôwen. Amnet yis, âiwnet ep". Inna: "tâazmem u tqeblem, f waya, laâqed iW"? Nnan as: "naâzem". Inna: "ihi, cehhdet, Nek, yidwen, seg inagan".');
INSERT INTO "ramdane" VALUES(3,82,'3:82','Wid iwexxôen, deffir waya, nitni d iballa$en.');
INSERT INTO "ramdane" VALUES(3,83,'3:83','Day b$an ddin ur nelli n Öebbi? U Neppa, unzen aS i illan di tmurt akked igenwan, b$an ne$ ugin. £uôeS, tu$alin nnsen.');
INSERT INTO "ramdane" VALUES(3,84,'3:84','Ini: "numen s Öebbi, akked wayen id irsen $uône$, akked wayen id irsen ar Ibôahim, Ismaâil, Isêaq, Iaâqub, laâôac, ayen ippunefken i Musa, Âisa, akked lambiyya, s$uô Mass nnsen. Ur nesmenyaf yiwen degsen. Nekwni nunez aS".');
INSERT INTO "ramdane" VALUES(3,85,'3:85','Win ib$an ddin, war annuz, ur ipwaqbal s$uôes. U neppa, di laxert, seg imexsuôen.');
INSERT INTO "ramdane" VALUES(3,86,'3:86','Amek ara Inhu Öebbi agdud inekôen deffir liman is, u cehhden ar Amazan d tidep, u iusa ten id ubeggen? Öebbi ur Inehhu agdud imednas.');
INSERT INTO "ramdane" VALUES(3,87,'3:87','Widak, aqeîîi nnsen d ddell s$uô Öebbi, d lmalayek, akked imdanen meôôa.');
INSERT INTO "ramdane" VALUES(3,88,'3:88','Degs ad wekkôen; ur ippifsus fellasen uâaqeb, ur stiseren.');
INSERT INTO "ramdane" VALUES(3,89,'3:89','Kkes ed wid iu$alen nedmen, deffir waya, u xedmen ayen ilhan. Ih, Öebbi Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(3,90,'3:90','Ih, wid ijehlen, deffir liman nnsen, sinna, rnan di tijehli, ur tepwaqbal nndama nnsen. Widak d im$eôôqen.');
INSERT INTO "ramdane" VALUES(3,91,'3:91','Ih, wid ijehlen, u mmuten d ijehliyen, ur ipwaqbal s$uô yiwen degsen, azal n tmurt n ddheb, a t id ifdu. I widak, aâaqeb aqeôêan, ur sâin amâiwen.');
INSERT INTO "ramdane" VALUES(3,92,'3:92','Ur teppawvem ar ppuba, d awez$i, alamma tseddqem seg wayen têemmlem. Kra n wayen tseddqem n cci, ihi, Öebbi Iéôa t.');
INSERT INTO "ramdane" VALUES(3,93,'3:93','Yal lqut illa iêell i At Isôail, siwa ayen igdel Isôail i iman is, uqbel a d ters Tuôa. Ini: "awit ed Tuôa, $eôt ep, ma tessawalem tidep".');
INSERT INTO "ramdane" VALUES(3,94,'3:94','Wid id igren tikerkas $ef Öebbi, deffir waya, nitni d imednas.');
INSERT INTO "ramdane" VALUES(3,95,'3:95','Ini: "Inna d Öebbi tidep; ddut d ddin n Ibôahim, i imalen ar tidep, ur nelli seg imcurak".');
INSERT INTO "ramdane" VALUES(3,96,'3:96','Ih, axxam amezwaru, i irsen i medden, war ccekk, d win n Bekka. Iôqa, d nnhaya i imavalen.');
INSERT INTO "ramdane" VALUES(3,97,'3:97','Degs issekniyen iberrzen; d axxam n Ibôahim. Win it ikecmen, atan di laman. I Öebbi, iwata ad êuoen medden s Axxam, ma zemren a t awven. Win iugin, ihi, Rebbi ur Iêwao ara imavalen.');
INSERT INTO "ramdane" VALUES(3,98,'3:98','Ini: "a yAt Tezmamt! Acimi tnekôem issekniyen n Öebbi"? Öebbi d Anagi n wayen txeddmem.');
INSERT INTO "ramdane" VALUES(3,99,'3:99','Ini: "a yAt Tezmamt! Acimi teîîeôôifem, wid iumnen, seg ubrid n Öebbi? Tessaramem t iaâwej, u kunwi d inagan". Ur I$fil Öebbi $ef wayen txeddmem.');
INSERT INTO "ramdane" VALUES(3,100,'3:100','A wid iumnen! Ma tu$em awal i urbaâ, seg wid iwumi teppunefk Tezmamt, a kwen rren d ijehliyen, deffir liman nnwen.');
INSERT INTO "ramdane" VALUES(3,101,'3:101','Amek tnekôem, u kunwi $ôan awen d issekniyen n Öebbi, u, garawen, Amazan iS? Win iîîfen di Öebbi, ni$ ipwanha s abrid uwqim.');
INSERT INTO "ramdane" VALUES(3,102,'3:102','A wid iumnen! Ëezzbet i Öebbi, akken Iklal aêezzeb. Ur ppemmtet alamma tunzem.');
INSERT INTO "ramdane" VALUES(3,103,'3:103','Eîfet akw di teêmilt n Öebbi, ur ppemfaôaqet. Mmektit ed ayen ikwen I$at Öebbi. Mi tellam d icenga, Issemlal ulawen nnwen. S l$it iS, tu$alem d atmaten. Tellam $ef rrif n maylellif n tmes, Isellek ikwen segs. Akka i wen Ipbeggin Öebbi issekniyen iS, amendeô ma a ppepwanhum.');
INSERT INTO "ramdane" VALUES(3,104,'3:104','Amar ad yili, segwen, lois i ineccden ar lxiô, ippamôen lqanun, itekksen ayen n dir. Widak d imerbaê.');
INSERT INTO "ramdane" VALUES(3,105,'3:105','Ur ppilit am wid imfaôaqen, u mgarraden, deffir n tisin ubeggen $uôsen. I widak, aâaqeb aqeôêan,');
INSERT INTO "ramdane" VALUES(3,106,'3:106','ass ideg ara imlulen wudmawen, ara ibriken wudmawen. Wanag wid iwumi ara ibriken wudmawen nnsen: "tnekôem, deffir liman nnwen? Jeôôbet aâaqeb, ilmend n wayen tnekôem".');
INSERT INTO "ramdane" VALUES(3,107,'3:107','Wanag wid iwumi ara a imlulen wudmawen nnsen, ad ilin deg uêunu n Öebbi. Nitni, degs, ad wekkôen.');
INSERT INTO "ramdane" VALUES(3,108,'3:108','D wigi ay d issekniyen n Öebbi. Neqqaô ak ten id, s tidep. Ur Ib$i Öebbi ddens i imavalen.');
INSERT INTO "ramdane" VALUES(3,109,'3:109','N Öebbi i illan deg igenwan, i illan di tmurt. Ar Öebbi tu$alin n lumuô.');
INSERT INTO "ramdane" VALUES(3,110,'3:110','Tifem akw leoyus id iff$en i medden. Teppamôem lqanun, teppekksem ayen n dir, tumnem s Öebbi. Ppif, i At Tezmamt, lemmer uminen. Garasen lmumnin; tegwti degsen d iballa$en.');
INSERT INTO "ramdane" VALUES(3,111,'3:111','Cwiî kan ara kwen vuôôen. Ma nnu$en kwen, ad rewlen, sinna, ur sâun amâiwen.');
INSERT INTO "ramdane" VALUES(3,112,'3:112','Iwwet iten ddell anda ma llan, siwa wid iîîfen di teêmilt n Öebbi, u di teêmilt n medden. Lqan urrif n Öebbi, u te$li d tiggulelt fellasen. Ayagi, acku llan nekôen issekniyen n Öebbi, la neqqen lambiyya, war ssebba. Ayagi, imi âuûan u zgaren tilas.');
INSERT INTO "ramdane" VALUES(3,113,'3:113','Ur aâdilen. Illa lois, seg At Tezmamt, i iqqaôen issekniyen n Öebbi deg iv, u nitni kennun.');
INSERT INTO "ramdane" VALUES(3,114,'3:114','Umnen s Öebbi akked wass aneggaru, ppamôen lqanun, tekksen ayen n dir, pêirin ar lxiô. Widak seg iâaôviyen.');
INSERT INTO "ramdane" VALUES(3,115,'3:115','Ayen xedmen n lxiô, ur asen ipwankaô. Öebbi Issen wid ipêezziben.');
INSERT INTO "ramdane" VALUES(3,116,'3:116','Ih, wid ijehlen, ur asen d ippawi acemma, ar Öebbi, wayla nnsen, ne$ tarwa nnsen. Widak d at tmes, degs ad wekkôen.');
INSERT INTO "ramdane" VALUES(3,117,'3:117','Amedya n wayen pseôôifen di tmeddurt n ddunit agi, am umedya n wavu, degs abruri. Iêuza iger n ugdud idensen iman is. Isnegr it. Ur ten Idnis Öebbi; d iman nnsen ay dennisen.');
INSERT INTO "ramdane" VALUES(3,118,'3:118','A wid iumnen! Ur pparat d iêbiben, wur nelli segwen. Ur zgalen a kwen hewwlen. B$an a kwen hejjnen. Ni$ ibda d lbwe$v seg imawen nnsen. Ayen iffren deg ulawen nnsen, i$elb it. Ni$ Nbeggen awen ed issekniyen, ma tepmeyyizem.');
INSERT INTO "ramdane" VALUES(3,119,'3:119','Atan kunwi d wid i ten iêemmlen, ur kwen êemmlen. Tumnem di Tezmamt, s lekmal is. Ma mmugren kwen, ad inin: "numen". Mi $aben, ad $eééen ivudan nnsen si zzâaf fellawen. Ini: "emmtet si zzâaf nnwen"! Ih, Öebbi Iéôa i illan deg idmaren.');
INSERT INTO "ramdane" VALUES(3,120,'3:120','Ma iusa kwen id ise$, a ten isnu$ben. Ma tevôa yidwen tucmip, ad feôêen yis. Ma tûebôem, u têezbem, ur kwen teîîuôôu s wacemma, tandit nnsen. Ni$ Izzi Öebbi i wayen xeddmen.');
INSERT INTO "ramdane" VALUES(3,121,'3:121','Mi teff$ev, ûûbeê zik, si twacult ik, temmalev i lmumnin imukan umennu$, Öebbi Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(3,122,'3:122','Mi qôib feclent snat n trebbuyaâ nnwen, u Öebbi d Bab nnsent. Iwata i yal lmumen, ad ippkel $ef Öebbi.');
INSERT INTO "ramdane" VALUES(3,123,'3:123','Ni$ Iâiwen ikwen Öebbi di Badô, mi tellam tendellem. Ihi, êezzbet i Öebbi, amar a ppesnemmrem.');
INSERT INTO "ramdane" VALUES(3,124,'3:124','Mi tenniv i lmumnin: "day ur testeqnaâm ara, mi kwen Iâiwen Öebbi s krav igima n lmalayek, id irsen"?');
INSERT INTO "ramdane" VALUES(3,125,'3:125','Xaîi, ma tûebôem u têezzbem, ma $ewwsen d fellawen, a kwen Iâiwen Mass nnwen, s smus igima n lmalayek ikkaten.');
INSERT INTO "ramdane" VALUES(3,126,'3:126','Ur Iga akka Öebbi, siwa i ubecceô nnwen, ad rtiêen yis wulawen nnwen. Rrbeê s$uô Öebbi kan, Uzmir, Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(3,127,'3:127','Iwakken ad Iôé arbaâ n wid ijehlen, ne$ a ten Iééaâ, ad u$alen nne$wnin.');
INSERT INTO "ramdane" VALUES(3,128,'3:128','Ur k iaâna lameô s wacemma. Ad asen Issuref, ne$ a ten Iâaqeb, nitni d imednas.');
INSERT INTO "ramdane" VALUES(3,129,'3:129','N Öebbi i illan deg igenwan, i illan di tmurt. Ipsemmiê i win i S ihwan. Ipâaqab win i S ihwan. Öebbi Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(3,130,'3:130','A wid iumnen! Ur teppet rrba ipzeggiden, tepzeggid. Ëezzbet i Öebbi, amar a prebêem.');
INSERT INTO "ramdane" VALUES(3,131,'3:131','Ëezzbet ilmend n tmes i ippuheggan i ijehliyen.');
INSERT INTO "ramdane" VALUES(3,132,'3:132','A$et awal i Öebbi akked Umazan, amar ad yili uêunu fellawen.');
INSERT INTO "ramdane" VALUES(3,133,'3:133','£iwlet ar ssmaê s$uô Mass nnwen, akked Loennet iwsâan am tmurt akked igenwan. Teppuhegga d i wid ipêezziben,');
INSERT INTO "ramdane" VALUES(3,134,'3:134','wid ipseddiqen - sâan ne$ xuûûen -, wid isnusun reffu, wid ipsemmiêen i medden - Öebbi Iêemmel lâuûam -,');
INSERT INTO "ramdane" VALUES(3,135,'3:135','wid, ma xedmen tucmip, ne$ densen iman nnsen, a d mmektin Öebbi, ad ste$fôen $ef ibekkaven nnsen. Anwa Ipsemmiêen ibekkaven siwa Öebbi? Ur pgerninen deg wayen xedmen, u nitni éôan.');
INSERT INTO "ramdane" VALUES(3,136,'3:136','Widak, akafi nnsen d ssmaê n Mass nnsen, akked leonanat, ddawat sen isaffen ppazzalen, degsen ad wekkôen. D arraz igerzen i wid issewqamen.');
INSERT INTO "ramdane" VALUES(3,137,'3:137','Âaddant tedyanin uqbel nnwen. Iniget di tmurt, walit amek tevôa d wid id iggaren tikerkas.');
INSERT INTO "ramdane" VALUES(3,138,'3:138','Wagi d abeggen i medden, d nnhaya akked unecced i wid ipêezziben.');
INSERT INTO "ramdane" VALUES(3,139,'3:139','Ur ppifcilet, ur $webnet, d kunwi i iaâlayen, ma d lmumnin ay tellam.');
INSERT INTO "ramdane" VALUES(3,140,'3:140','Ma iêuza kwen lada, ni$ lada i t icban iêuza agdud. Ussan agi Nerra ten, ger medden, d tidwal, iwakken ad Yissin Öebbi wid iumnen, ad Iddem segwen inagan. Öebbi ur Iêemmel ara imednas.');
INSERT INTO "ramdane" VALUES(3,141,'3:141','Iwakken ad Ijeôôeb Öebbi wid iumnen, u ad Isqucc ijehliyen.');
INSERT INTO "ramdane" VALUES(3,142,'3:142','Day t$ilem a pkecmem Loennet, wanag ur Iéôi ara Öebbi wid ijuhden segwen, ur Issin ara iûebôiyen?');
INSERT INTO "ramdane" VALUES(3,143,'3:143','Ni$ tepmennim tameppant, uqbel a p id temmagrem. Ni$ twalam p, kunwi tepmuqulem.');
INSERT INTO "ramdane" VALUES(3,144,'3:144','Muêemmed d Amazan kan. Ni$ âaddan imazanen, uqbel is. Ma immut ne$ ipwan$a, day a ppu$alem ar deffir? Win ara iu$alen ar deffir, ur iîîuôôu Öebbi s wacemma. Öebbi ad Ikafi wid isnemmiren.');
INSERT INTO "ramdane" VALUES(3,145,'3:145','Ur ipmeppat umdan siwa ma Irad Öebbi, di lajel iuran. Win ib$an akafi n ddunit, ad as t Nefk. Win ib$an akafi n laxeôt, ad as t Nefk. A Nkafi wid isnemmiren.');
INSERT INTO "ramdane" VALUES(3,146,'3:146','Acêal n lambiyya i innu$en, idda yidsen urbaâ iqwan, ur fcilen seg wayen iten issaâtben, deg ubrid n Öebbi, ur xuzen, ur fkin mayna. Öebbi Iêemmel iûebôiyen.');
INSERT INTO "ramdane" VALUES(3,147,'3:147','Awal nnsen, siwa mi nnan: "a Mass nne$! Semmeê a$ ibekkaven nne$, akked tugut nne$ di lameô nne$. Tebbet isurifen nne$, Sserbeê ane$ $ef ugdud ijehliyen".');
INSERT INTO "ramdane" VALUES(3,148,'3:148','Ifka yasen Öebbi akafi n ddunit, akked ukafi lâali n laxert. Öebbi Iêemmel lâuûam.');
INSERT INTO "ramdane" VALUES(3,149,'3:149','A wid iumnen! Ma tu$em awal i wid ijehlen, a kwen rren ar deffir, a ppu$alem txesôem.');
INSERT INTO "ramdane" VALUES(3,150,'3:150','Xaîi! Öebbi d Bab nnwen. Neppa, Yif akw imâiwnen.');
INSERT INTO "ramdane" VALUES(3,151,'3:151','A Nessekcem tugdi deg ulawen n wid ijehlen, imi curken i Öebbi, ayen iwumi ur d irs ara udabu. Axxam nnsen d times. A yir tanezdu$t i imednas!');
INSERT INTO "ramdane" VALUES(3,152,'3:152','Ni$ Issawel awen Öebbi tidep, di lemâahda S, mi ten tesnegrem, s tsriê iS. Armi, mi tfeclem, temnamarem f lameô, taâûam mbaâd ma Issken awen d ayen têemmlem. Garawen wid ib$an ddunit, garawen wid ib$an laxert. Sinna, Issebâad ikwen fellasen, iwakken a kwen Ijeôôeb. Ni$ Isemmeê awen. Öebbi d Bab n lfevl i lmumnin.');
INSERT INTO "ramdane" VALUES(3,153,'3:153','Mi tulim ur d tezzim, u Nnbi inecced ikwen ar ineggura nnwen. Ineûûef awen a$ilif s wayev, iwakken ur t$webnem $ef wayen i wen iôuêen, akked wayen ikwen iu$en. Öebbi Iéôa ayen txeddmem.');
INSERT INTO "ramdane" VALUES(3,154,'3:154','Sinna, deffir u$ilif, Issers awen d laman, ives id i$lin $ef urbaâ segwen. Ni$ arbaâ pêebbiôen $ef yiman nnsen, nnwan, $ef Öebbi, ayen ur nelli d tidep, nnwaya n at war tussna. Qqaôen: "day illa wayen i $ iaânan, di lameô"? Ini: "s tidep, lameô akw i Öebbi". Teffren, deg iman nnsen, ayen ur ak mmalen. Qqaôen: "lemmer illi wayen i $ iaânan, di lameô, ur nepwan$a dagi". Ini: "xas ma tellam deg ixxamen nnwen, wid i$ef tura tmen$iwt, a ten tawev s imîeôêen nnsen. Iwakken ad Ijeôôeb Öebbi i illan deg idmaren nnwen, ad Izzizdeg i illan deg ulawen nnwen. Öebbi Iéôa i illan deg idmaren.');
INSERT INTO "ramdane" VALUES(3,155,'3:155','Ih, wid segwen iwexxôen asmi mlalent trebbuyaâ, d Cciîan kan iten iccacven, ilmend n kra n wayen kesben. Ni$ Isemmeê asen Öebbi. Akka, Öebbi Ipsemmiê, Iêellem.');
INSERT INTO "ramdane" VALUES(3,156,'3:156','A wid iumnen! Ur ppilit am wid ijehlen. Nnan i watmaten nnsen iunagen di tmurt, ne$ ipna$en: "lemmer llin $uône$, ur mmuten, ur twan$an". Ad Irr Öebbi, ayagi, d aneggaf deg ulawen nnsen! Öebbi Issidir, Ineqq. Öebbi Ipwali ayen txeddmem.');
INSERT INTO "ramdane" VALUES(3,157,'3:157','Ma tepwan$am deg ubrid n Öebbi, ne$ temmutem, ssmaê n Öebbi akked uêunu, war ccekk, ifen ayen la jemâan.');
INSERT INTO "ramdane" VALUES(3,158,'3:158','Ma temmutem ne$ tepwan$am, war ccekk, ar Öebbi ara tepwafesrem.');
INSERT INTO "ramdane" VALUES(3,159,'3:159','S uêunu n Öebbi, têelmev $uôsen. Lemmer tqeûêev, tes$aôev ul ik, ad baâden fellak. Semmeê asen, ssuter ad ppusemmêen, ciweô iten $ef lameô. Mi tseyyiv, ppkel $ef Öebbi. Ih, Öebbi Iêemmel wid ippkalen.');
INSERT INTO "ramdane" VALUES(3,160,'3:160','Ma Iâiwen ikwen Öebbi, ur kwen irennu yiwen. Ma Iooa kwen, anwa illan a kwen iâiwen, deffir eS? £ef Öebbi ara ppeklen lmumnin.');
INSERT INTO "ramdane" VALUES(3,161,'3:161','Ur issefk ara i nnbi ad idnes. Win idensen, a d ias s wayen idnes, ass n tnekra. Sinna, yal wa, ad as ippunefk wayen ikseb. Nitni, ur ten pmeôôiôen.');
INSERT INTO "ramdane" VALUES(3,162,'3:162','Day win iddan d llza n Öebbi am win isebebben urrif n Öebbi, u tanezdu$t is d Loahennama? A taggara tucmip!');
INSERT INTO "ramdane" VALUES(3,163,'3:163','Ur aâdilen ar Öebbi. Öebbi Ipwali acu xeddmen.');
INSERT INTO "ramdane" VALUES(3,164,'3:164','Ni$ Öebbi I$at lmumnin, mi sen d Iuzen Amazan segsen, immal asen issekniyen iS, izzizdig iten, isseêfav asen Tazmamt akked ûûwab, ulamma llan, zik, di ttelfan n ûûeê.');
INSERT INTO "ramdane" VALUES(3,165,'3:165','Ma tu$em tiyta, ni$ terram p berdayen. Tennam: "ansi waya"? Ini: "aya seg iman nnwen". Öebbi, s tidep, Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(3,166,'3:166','Ayen ikwen iwwten, ass n temlilit n trebbuyaâ, s tsriê n Öebbi. Iwakken ad Iéeô anwi ay d lmumnin,');
INSERT INTO "ramdane" VALUES(3,167,'3:167','u ad Iéeô anwi ay d ixabiten. Nnan asen: "yyaw, nna$et deg ubrid n Öebbi, ne$ éedmet". Nnan: "lemmer nessin a nenna$, yili nedda n yidwen". Assen, nitni, ar tijehli, ne$ $eô liman, ay qeôben kteô. Qqaren, s yimawen nnsen, ayen ur nelli deg ulawen nnsen. Öebbi Yif akw akken Iéôa ayen teffren.');
INSERT INTO "ramdane" VALUES(3,168,'3:168','Wid innan i watmaten nnsen, u nitni qqimen: "lemmer i $ ssemêessen, ur pwan$en". Ini: "sdarit iman nnwen si tmeppant, ma tessawalem tidep ".');
INSERT INTO "ramdane" VALUES(3,169,'3:169','Ur p$ili mmuten wid ipwan$an deg ubrid n Öebbi. Xaîi, d imuddiren. Ad ppuôeéqen ar Mass nnsen.');
INSERT INTO "ramdane" VALUES(3,170,'3:170','Ad feôêen s wayen i sen Ifka Öebbi, si lfevl iS. Zhan, imi wid deffir sen, mazal ur ten id nelêiq, ur ppagawaden, ur $weblen.');
INSERT INTO "ramdane" VALUES(3,171,'3:171','Zhan s l$it n Öebbi, akked lfevl. Öebbi ur Iveggiâ arraz n lmumnin;');
INSERT INTO "ramdane" VALUES(3,172,'3:172','wid id ijawben i Öebbi akked Umazan, mbaâd ma sâaddan lêif. Arraz ameqwôaêan i wid, segsen, ixeddmen ayen ilhan, u êezzben;');
INSERT INTO "ramdane" VALUES(3,173,'3:173','wid iwumi nnan medden: "ni$ mlalen fellawen imdanen, ihi, agwadet pen". D$a isnerna liman nnsen, nnan: "Iqadd a$ Öebbi, Awkil Amxallef".');
INSERT INTO "ramdane" VALUES(3,174,'3:174','U$alen d s l$it n Öebbi akked lfevl, ur ten innul cceôô; ddan d llza n Öebbi. Öebbi d Bab n lfevl ameqwôaêan.');
INSERT INTO "ramdane" VALUES(3,175,'3:175','Akka kan ay d Cciîan. Issagwad s imeddukwal is. Ur ten ppagwadet, agwadet iyI, ma d lmumnin ay tellam.');
INSERT INTO "ramdane" VALUES(3,176,'3:176','Ur k $webbnen wid ip$awalen ar tijehli. Nitni, s tidep, ur vvuôôen Öebbi, s wacemma. Ib$a Öebbi ur asen Iteg amur di laxert. Fellasen aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(3,177,'3:177','Ih, wid iu$en tijehli s liman, ur vvuôôen Öebbi s wacemma. Fellasen aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(3,178,'3:178','Ur êeppben wid ijehlen, ar telha yasen, lmudda i sen Nefka. Nefka yasen kan lmudda, iwakken ad rnun ibekkaven. Fellasen aâaqeb isdullun.');
INSERT INTO "ramdane" VALUES(3,179,'3:179','Ur illi di leb$i n Öebbi, ad Ioo lmumnin di liêala nnwen, alamma Ifrez axabit seg umâin. Ur illi di leb$i n Öebbi, ad awen Imel tabavnit. Maca, Iferren Öebbi wid i S ihwan, d imazanen iS. Ihi, amnet s Öebbi akked imazanen iS. Ma tumnem, u têezzbem, a ppawim arraz ameqwôaêan.');
INSERT INTO "ramdane" VALUES(3,180,'3:180','Ur êeppben wid ipcuêêun deg wayen i sen Ifka Öebbi, si lfevl iS, ard lxiô nnsen. Xaîi, d cceôô nnsen. Ayen cuêêen a ten iarez seg umgaôv, ass n tnekra. I Öebbi, lweôt n tmurt akked igenwan. Öebbi Iéôa ayen txeddmem.');
INSERT INTO "ramdane" VALUES(3,181,'3:181','Ni$ Isla Öebbi i wawal n wid innan: "s tidep, Öebbi d igellil, nekwni d isâayen". A Naru ayen nnan, akked wayen n$an n lambiyya, war ssebba. Ad asen Nini: "jeôôbet aâaqeb ujajiê".');
INSERT INTO "ramdane" VALUES(3,182,'3:182','Ayagi, ilmend n wayen xedmen ifassen nnwen. Öebbi ur Ipdennis imdanen.');
INSERT INTO "ramdane" VALUES(3,183,'3:183','Wid innan: "Ih, Öebbi Iâahed a$ ur neppamen s Umazan, alamma issenâat a$ d asfel, ara teçç tmes". Ini: "ni$ usan kwen id imazanen, uqbel iw, s ubeggen, akw s wayen d tennam. Ihi, acimi i ten ten$am, ma tessawalem tidep"?');
INSERT INTO "ramdane" VALUES(3,184,'3:184','Ma skaddben k, ni$ skaddben imazanen, uqbel ik, usan d s ubeggen, Ihellilen, akked Tezmamt timnewweôt.');
INSERT INTO "ramdane" VALUES(3,185,'3:185','Yal iman ad ijeôôeb tameppant. Ass n tnekra, ad awen ippunefk, kan, warraz nnwen. Win isellken si tmes, ikecmen Loennet, ni$ irbeê. Tameddurt, di ddunit, d zzhu kan ip$uôôun.');
INSERT INTO "ramdane" VALUES(3,186,'3:186','A ppeppujeôôbem deg wayla nnwen akked yiman nnwen. Ad wwten aîas degwen, wid iwumi teppunefk Tezmamt, uqbel nnwen, akked imcurak. Ma tûebôem u têezzbem, ayagi, s tidep, d ayen isselêayen lumuô.');
INSERT INTO "ramdane" VALUES(3,187,'3:187','Mi Iîîef Öebbi laâqed n wid iwumi teppunefk Tezmamt: "a p tbeggnem i medden, ur p teppeffrem". Rran p deffir izugar nnsen, u$en yis, ayen irxisen. I icmet wayen u$en!');
INSERT INTO "ramdane" VALUES(3,188,'3:188','Ur p$ili ad sellken, seg uâaqeb, wid ifeôêen s wayen fkan, u b$an a ten ccekôen $ef wayen ur xdimen. Fellasen aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(3,189,'3:189','N Öebbi tagelda n tmurt akked igenwan. Öebbi Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(3,190,'3:190','Ih, di lxelq n tmurt akked igenwan, akked umgirred n yiv akked wass, d issekniyen i uâqilen;');
INSERT INTO "ramdane" VALUES(3,191,'3:191','Wid id ismektayen Öebbi s ibeddi, s i$imi, s tuééla. Pmeyyizen lxelq n tmurt akked igenwan. A Mass nne$! Ur Texliqev aya d aêerfi. Gedha s yiseK! Sellek a$ seg uâaqeb n tmes.');
INSERT INTO "ramdane" VALUES(3,192,'3:192','A Mass nne$! Ni$, s tidep, Keçç, Texziv win Teskecmev ar tmes. Ur llin wid ara isellken imednas.');
INSERT INTO "ramdane" VALUES(3,193,'3:193','A Mass nne$! S tidep, nesla i uneccad, la inecced ar liman: "amnet s Mass nnwen"! Numen. A Mass nne$! Semmeê a$ ibekkaven nne$, Kkes ane$ tucmitin nne$, Ssiwev a$, ar ssmaê iK, akked wid zeddigen.');
INSERT INTO "ramdane" VALUES(3,194,'3:194','A Mass nne$! Efk ane$ ayen i a$ Tâahdev s imazanen iK; ur a$ Xezzu ass n tnekra. Keçç, s tidep, ur Tzeglev lemâahda.');
INSERT INTO "ramdane" VALUES(3,195,'3:195','Irra yasen Mass nnsen: "Nek ur Veggiâe$ igi n win, segwen, issewqamen, d ddkeô ne$ d nnta. Wa ikka d seg wa. Wid iunagen, u ssuff$en ten seg ixxamen nnsen, iâatben deg ubrid iW, innu$en, ipwan$an, war ccekk, ad asen Kkse$ tucmitin nnsen, a ten Skecme$ ar leonanat, ddawat sen isaffen ppazzalen". D akafi s$uô Öebbi. Öebbi, $uôeS akafi amxallef.');
INSERT INTO "ramdane" VALUES(3,196,'3:196','Ur k ipqelliq lexdaâ n wid ijehlen di temdint.');
INSERT INTO "ramdane" VALUES(3,197,'3:197','Cwiî n zzhu, sinna, axxam nnsen d Loahennama. A yir usu!');
INSERT INTO "ramdane" VALUES(3,198,'3:198','Maca, i wid iêezzben i Mass nnsen: leonanat, ddawat sen isaffen ppazzalen, degsen ad wekkôen, d inebgawen n Öebbi. U d wayen illan ar Öebbi, d amxallef i izedganen.');
INSERT INTO "ramdane" VALUES(3,199,'3:199','Ih, seg wAt Tezmamt, wid iumnen s Öebbi, s wayen akw i wen ed irsen, akked s wayen i sen d irsen. Unzen i Öebbi, ur u$en ara s issekniyen n Öebbi, ayen ôxisen. I widak, arraz s$uô Mass nnsen. Ni$ Öebbi Ip$awal deg uqeîîi.');
INSERT INTO "ramdane" VALUES(3,200,'3:200','A wid iumnen! Üebôet, aznet ûûbeô, begset, êezzbet i Öebbi, amar a prebêem.');
INSERT INTO "ramdane" VALUES(4,1,'4:1','A yimdanen! Ëezzbet i Mass nnwen, ikwen Ixelqen seg yiwen n lxelq, u Ixelq ed segs ayugan is. Issuffe$ ed, segsen, aîas n yirgazen akked tlawin. Ëezzbet i Öebbi i$ef tepmesteqsayem, akw i tâabbuvin. Öebbi, s tidep, Izga Iquôaâ kwen.');
INSERT INTO "ramdane" VALUES(4,2,'4:2','Efket ayla nnsen i igujilen, ur pparat dduni deg umkan n lâali. Ur pparat ayla nnsen d ayla nnwen. Ih, wagi d abekkav ameqwôan.');
INSERT INTO "ramdane" VALUES(4,3,'4:3','Ma tugadem ur tweznem ara ar igujilen, a$et ayen i wen ihwan n tlawin: snat, kravt, kuét. Ma tugadem ur tessaâdalem ara, yiwet kan, ne$ taklatin nnwen. Akka i iwulem, ur ppakwret.');
INSERT INTO "ramdane" VALUES(4,4,'4:4','Efket tuççit nnsent i tlawin, d tikci. Ma rrant awen d kra segs, s leb$i nnsent, ççet ep, s lfeôê akked llza.');
INSERT INTO "ramdane" VALUES(4,5,'4:5','Ur ppaket i ije$lufen ayla nnwen, win i wen Ifka Öebbi i tmeddurt. Öeéqet pen segs, sselset pen, ééiédet awal nnwen $uôsen.');
INSERT INTO "ramdane" VALUES(4,6,'4:6','Jeôôbet igujilen, alamma bbwven i jjwao. Ma tufam ten éewôen, efket asen ayla nnsen. Ur ten teppet s uveggaâ d usexseô, uqbel ad im$uôen. Ameôkanti ad iêezzeb, ame$bun ad içç s lekyasa. Mara sen tefkem ayla nnsen, s inagan fellas. Nesteqnaâ s Öebbi i leêsab.');
INSERT INTO "ramdane" VALUES(4,7,'4:7','I yirgazen, amur seg wayen d ooan imawlan akked llzem. I tlawin amur seg wayen d ooan imawlan akked llzem. Ama cwiî, ne$ aîas, d amur ilezmen.');
INSERT INTO "ramdane" VALUES(4,8,'4:8','Ma êedôen, i ufôaq, llzem, igujilen, igellilen, ôeéqet pen segs, tinem asen awal uûwib.');
INSERT INTO "ramdane" VALUES(4,9,'4:9','Ad agwaden wid id iooan, deffir sen, tarwa tagellilt. Ad agwaden fellasen. Ad êezzben i Öebbi, s wawal iweznen.');
INSERT INTO "ramdane" VALUES(4,10,'4:10','Ih, wid iteppen, s ddens, ayla igujilen, la teppen kan times deg iâabbav nnsen, ad pwagren deg ujajiê.');
INSERT INTO "ramdane" VALUES(4,11,'4:11','£ef tarwa nnwen, Iweûûa kwen Öebbi: i uqcic amur n snat tullas. Ma llant sennig snat n tullas, nnsent amur wis kué n lweôt. Ma yiwet i illan, ines uzgen. I imawlan is, i yal wa, amur wis sdis n wayen id iooa, ma isâa mmi s. Ma ur isâa ara mmi s, u weôten t imawlan is, amur wis kué i immas. Ma isâa atmaten, i immas amur wis sdis, kkes ed ayen ippuseôfen ne$ îlabat. Imawlan nnwen, arraw nnwen, ur teéôim ara menyif iten, akken i kwen nefâan. D aêeppem n Öebbi. Öebbi, s tidep, Izga d Bab n ûûwab, d Amusnaw.');
INSERT INTO "ramdane" VALUES(4,12,'4:12','Nnwen uzgen n wayen id ooant tlawin nnwen, ma ur sâint ara tarwa. Ma sâant mmit sent, i kunwi amur wis kué n wayen id ooant, kkes ed ayen ippuseôfen ne$ îlabat. I nitenti, amur wis kué n wayen id teooam, ma ur tesâim ara mmit wen. Ma tesâam mmit wen, i nitenti amur wis îam n wayen id teooam, kkes ed ayen ippuseôfen ne$ îlabat. Ma d argaz awêid ne$ d tameîîut tawêiî, ma isâa agmat ne$ taultmap, i yal wa segsen, amur wis sdis. Ma deg waîas ay llan, ad feôqen amur wis krav, war ccqa, kkes ed ayen ippuseôfen ne$ îlabat. D aweûûi n Öebbi. Öebbi Iéôa, Iêellem.');
INSERT INTO "ramdane" VALUES(4,13,'4:13','D tigi ay d tilisa n Öebbi. Win issemêessen i Öebbi akked Umazan iS, a t Issekcem ar leonanat, ddawat sen isaffen ppazzalen, degsen ad wekkôen. D wagi ay d rrbeê ameqwôaêan.');
INSERT INTO "ramdane" VALUES(4,14,'4:14','Win iâuûan Öebbi akked Umazan iS, izegren tilas iS, a t Issekcem ar tmes, degs ad iwekkeô, ad isâaddi aâaqeb isdullun.');
INSERT INTO "ramdane" VALUES(4,15,'4:15','I tid ixedmen lâaô di tlawin nnwen, awit ed, fellasent, kué inagan nnwen. Ma cehhden d, kemnet pent di texxamin, alamma teddem itent tmeppant, ne$ Iga yasent Öebbi abrid.');
INSERT INTO "ramdane" VALUES(4,16,'4:16','Wid segwen it ixedmen, âaqbet pen. Ma nedmen, u xedmen ayen ilhan, anfet asen. Öebbi, s tidep, Izga Issuruf, Ipêunu.');
INSERT INTO "ramdane" VALUES(4,17,'4:17','Issuruf kan Öebbi i wid ixedmen cceôô ur éôin, sinna, steôoâen, war laâvil. Widak, ad asen Issuref Öebbi. Öebbi Izga d Bab n ûûwab, d Amusnaw.');
INSERT INTO "ramdane" VALUES(4,18,'4:18','Ur illi usuref i wid ixedmen tucmitin. Alamma, mi d tusa yiwen tmeppant, a d yini: "nek steôoâe$ tura". Akken, wid immuten u nitni d ijehliyen. Widak, Nhegga yasen d aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(4,19,'4:19','A wid iumnen! Ur awen iêell ara a pweôten tilawin, ur qbilent, ne$ ad asent tekksem jjwao, iwakken a ppawim amur seg wayen i sent tefkam, siwa ma xedment lâaô ibanen. Illit yidsent, s lqanun. Ma tkeôhem tent, ahat tkeôhem ayen ideg Irra Öebbi lxiô meqqwôen.');
INSERT INTO "ramdane" VALUES(4,20,'4:20','Ma teb$am a pbedlem tameîîut s tayev, u tefkam tuççit i yiwet, ur as tekkset acemma. Ma tekksem, d ayen n dir u d abekkav n ûûeê.');
INSERT INTO "ramdane" VALUES(4,21,'4:21','Amek ara tekksem imi temyekcamem, u bbwint s$uôwen laâqed meqqwôen?');
INSERT INTO "ramdane" VALUES(4,22,'4:22','Ur ppa$et tilawin i u$en ibabaten nnwen, kkes ed ayen iâaddan. Ayagi, s tidep, d lâaô, d dduni, d yir brid.');
INSERT INTO "ramdane" VALUES(4,23,'4:23','Pwadeglent awen tyimmatin nnwen, issit nnwen, tissetmatin nnwen, tiâamtiwin nnwen, tixaltitin nnwen, issit n ugmat, issit n tweltmap, tiyimmatin nnwen i kwen issuîîven, tissetmatin nnwen ukud teîîvem, tivegwalin nnwen, issit id tessekrem n tlawin nnwen ukud tcerkem usu - ma ur tecrikem ara usu, ulac u$ilif -, tilawin n warraw id turwem. Ur zdukulet snat tsetmatin, kkes ed ayen iâaddan, - Öebbi, s tidep, Izga Ipsemmiê, Ipêunu -,');
INSERT INTO "ramdane" VALUES(4,24,'4:24','tilawin ijewoen, kkes ed taklatin nnwen. D Öebbi i wen Iuran. Iêell awen, deffir waya, a ppezhum s wayla nnwen, s lewqama, maççi s lemveôôa. Tid, segsent, ukud tezham, fket asent arrazen nnsent, d aêeppem. Ur illi u$ilif fellawen deg wayen i$ef temwafaqem, deffir uêeppem. Öebbi, s tidep, Izga d Bab n ûûwab, d Amusnaw.');
INSERT INTO "ramdane" VALUES(4,25,'4:25','Wid, segwen, ur nezmir ara ad a$en tiêuôiyn iumnen, ad a$en si teklatin nnwen, tid iumnen. Öebbi Yif akw akken Iéôa liman nnwen. Wa seg wa. A$et pent, s tsriê n twacult nnsent, fket asent arrazen nnsent, s lqanun. D tizedganin, maççi d tucmitin, ne$ d tid iff$en abrid. ÛMa u$alent d tiêuôiyn, sinna xedment lâaô, aâaqeb nnsent azgen n win n tiêuôiyn. Akka i win, segwen, ippagwaden abekkav. Ma tûebôem, ppif i kunwi. Öebbi Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(4,26,'4:26','Ib$a Öebbi ad awen Ibeggen, a kwen Iawi d wansayen n wid i kwen id izwaren, a d Iu$al $uôwen. Öebbi d Bab n ûûwab, d Amusnaw.');
INSERT INTO "ramdane" VALUES(4,27,'4:27','Ib$a Öebbi ad awen Issuref. B$an wid iteddun d lemcihwat, a pmalem lmil abarar.');
INSERT INTO "ramdane" VALUES(4,28,'4:28','Ib$a Öebbi ad Issifes fellawen. Ipwaxleq umdan d uôhif.');
INSERT INTO "ramdane" VALUES(4,29,'4:29','A wid iumnen! Ur ppemyeççat ayla garawen, d aêerfi, kkes ed ma d ssuq i$ef temwafaqem. Ur neqqet iman nnwen. Ih, Öebbi Izga Ipêunu fellawen.');
INSERT INTO "ramdane" VALUES(4,30,'4:30','Win ixedmen akka, s zzkaôa akked ddens, deqqal a t Nger ar tmes. Ayagi, izga d ayen isehlen i Öebbi.');
INSERT INTO "ramdane" VALUES(4,31,'4:31','Ma tjunbem ayen meqqwôen, seg wayen i wen ipwakksen, a Nessuref i tucmitin nnwen, a kwen Nessekcem anekcum ukyis.');
INSERT INTO "ramdane" VALUES(4,32,'4:32','Ur ppasmet, ma Ismenyaf Öebbi kra i wiyav. I yirgazen, amur seg wayen kesben; i tlawin, amur seg wayen kesbent. Ssutret i Öebbi, si lfevl iS. Ih, Öebbi Izga Issen yal cci.');
INSERT INTO "ramdane" VALUES(4,33,'4:33','Nerra, i yal wa, iweôôaten n wayen d ooan imawlan, llzem, akked wid ukud tâaqqdem. Efket asen amur nnsen. Öebbi Izga Ipwali yal cci.');
INSERT INTO "ramdane" VALUES(4,34,'4:34','Irgazen ad ûunen tilawin, imi Ismenyafa Öebbi kra $ef kra, ilmend n wayen pseôôifen seg wayla nnsen. Tilawin imaânen, iunzen, ipêaraben $ef sser n wayen Iêureb Öebbi. Tid s$uô tugadem tuêsift, nna$et pent, âazlet pent deg imîeôêen, ôebbit pent. Ma u$ent awen awal, ur tent ppawvet. Ih, Öebbi Izga Âalay, Meqqweô.');
INSERT INTO "ramdane" VALUES(4,35,'4:35','Ma tugadem ad mfaôaqen, aznet amêaddi n twacult n neppa, wayev n twacult n neppat. Ma b$an ad mûalaêen, a ten Issemâaceô Öebbi. Öebbi, s tidep, Izga Âalay, Iéôa.');
INSERT INTO "ramdane" VALUES(4,36,'4:36','Âabdet Öebbi, ur aS pcaraket acemma. Elhut ar lwaldin, ar llzem, ar igujilen, ar ime$ban, ar loaô i wen ippilin akked win ur awen neppili, ar umeddakwel ar yidis, ar iminig, ar waklan nnwen. Öebbi, s tidep, ur Iêemmel ara amzuxi, imnefxi,');
INSERT INTO "ramdane" VALUES(4,37,'4:37','wid ipcuêêun u ppamôen i medden ccêa, iteffren ayen i sen Ifka Öebbi, si lfevl iS - Nhegga d, i ijehliyen, aâaqeb isdullun -;');
INSERT INTO "ramdane" VALUES(4,38,'4:38','wid ipûeôôifen ayla nnsen, iwakken a ten id walin medden, ur uminen s Öebbi akked wass aneggaru; wid isâan Cciîan d amsaâaf. A yir amsaâaf!');
INSERT INTO "ramdane" VALUES(4,39,'4:39','Acu ar asen iôuêen, lemmer uminen s Öebbi akked wass aneggaru, u ûeôôfen seg wayen is iten Iôéeq Öebbi? Öebbi Izga Issen iten.');
INSERT INTO "ramdane" VALUES(4,40,'4:40','Öebbi, s tidep, ur Idennes s wayen iwzen ubelkim. Ma tebbwiv d ise$, a t Irr d sin, a d Ifk, s$uôeS, arraz ameqwôaêan.');
INSERT INTO "ramdane" VALUES(4,41,'4:41','Amek, ihi, ma Nusa, si yal lois, s unagi; ma Nusa, yissek, d anagi $ef wigi?');
INSERT INTO "ramdane" VALUES(4,42,'4:42','Assen, wid ijehlen u âuûan Nnbi, ad ssirmen a ten tesseblaâ tmurt. Ur teffren awal i Öebbi.');
INSERT INTO "ramdane" VALUES(4,43,'4:43','A wid iumnen! Ur pqeôôibet ar téallit ma tsekôem, alamma teéôam acu d teqqaôem. Ne$ ma tellam s loanaba - siwa ma la teppinigem -, alamma tessardem. Ma tuvnem ne$ tunagem, ne$ iusa d yiwen segwen si ccicma, ne$ tennulem tilawin, ur tufim aman, ddmet akal zeddigen, sâaddit ep $ef udmawen nnwen d ifassen nnwen. Ih, Öebbi Izga Ipqili, Ipsemmiê.');
INSERT INTO "ramdane" VALUES(4,44,'4:44','Day ur twalav ara wid iwumi ippunefk kra si Tezmamt? Ppa$en d ttelfan, b$an a ppennetlafem seg ubrid.');
INSERT INTO "ramdane" VALUES(4,45,'4:45','Öebbi Yif akw akken Issen icenga nnwen. Nesteqnaâ s Öebbi d Bab. Nesteqnaâ s Öebbi d Amâiwen.');
INSERT INTO "ramdane" VALUES(4,46,'4:46','Seg udayen, wid iîîeôôifen awalen seg inumak nnsen. Qqaôen: "nesla, naâûa, ssemêes i wayen ur d nnin, muqel a$ d". Ssaâwjen ilsawen nnsen, kkaten di ddin. Lemmer, s tidep, qqaôen: "nesla, nunez, ssel, muqel a$ d", war ccekk, yif it i nitni, u iwqem kteô. Maca, Ixza ten Öebbi, ilmend n tijehli nnsen. Ur ppamnen, siwa ixeîîayen.');
INSERT INTO "ramdane" VALUES(4,47,'4:47','A wid iwumi teppunefk Tezmamt! Amnet s wayen id Nessers, d atebbet n wayen illan $uôwen, uqbel a Nes$ab udmawen, a ten Nerr ar deffir, ne$ a ten Nexzu, akken Nexza at Ssebt. Lameô n Öebbi ad iâaddi.');
INSERT INTO "ramdane" VALUES(4,48,'4:48','Öebbi, s tidep, ur Ipsemmiê mara S carken. Ipsemmiê ayen nniven, i win i S ihwan. Win icurken i Öebbi, ni$ ibbwi abekkav abarar.');
INSERT INTO "ramdane" VALUES(4,49,'4:49','Day ur twalav ara wid iran iman nnsen zeddigit? Maca, Öebbi Izzizdig win i S ihwan. Ur pwadensen ula s uclem utemôiw.');
INSERT INTO "ramdane" VALUES(4,50,'4:50','Wali amek id gren tikerkas $ef Öebbi. Ayagi kan, d abekkav n ûûeê.');
INSERT INTO "ramdane" VALUES(4,51,'4:51','Day ur twalav ara wid iwumi ippunefk kra si Tezmamt? Umnen s iôebbiten n tkerkas, u qqaôen, i wid ijehlen, ar widak pwanhan sennig wid iumnen.');
INSERT INTO "ramdane" VALUES(4,52,'4:52','Widak d wid Ixza Öebbi. Wid ara Ixzu Öebbi, ur asen teppafev amâiwen.');
INSERT INTO "ramdane" VALUES(4,53,'4:53','Ne$ sâan kra si tgelda, d$a ur ppaken i medden ula d atemôiw.');
INSERT INTO "ramdane" VALUES(4,54,'4:54','Ne$ êesden medden, deg wayen i sen Ifka Öebbi, si lfevl iS. Ni$ Nefka, i twacult n Ibôahim, Tazmamt, ûûwab. Nefka yasen tagelda tameqwôaêant.');
INSERT INTO "ramdane" VALUES(4,55,'4:55','Segsen wid iumnen yis, segsen wid i p iugin. Beôka ten ajajiê n Loahennama.');
INSERT INTO "ramdane" VALUES(4,56,'4:56','Ih, wid inekôen issekniyen nne£, deqqal a ten Nerr ar tmes. Mi ô$an igwelman nnsen, ad asen d Nenûef s igwelman nniven, ad jeôôben aâaqeb. Ih, Öebbi Izga d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(4,57,'4:57','Wid iumnen, ixeddmen ayen ilhan, a ten Nessekcem ar leonanat, ddawat sen isaffen ppazzalen, din ad wekkôen, i lebda. I nitni, degsen, tilawin zeddigen; a ten Nawi ar tili isburrun.');
INSERT INTO "ramdane" VALUES(4,58,'4:58','S tidep, Iumeô awen Öebbi a pperrem lamanat i imawlan nnsent, a pêuddem s lmizan, ma têuddem ger medden. Ih, ilha wayen u$uô i kwen Ipnabi Öebbi. Öebbi, s tidep, Izga Issel, Ipwali.');
INSERT INTO "ramdane" VALUES(4,59,'4:59','A wid iumnen! A$et awal i Öebbi. A$et awal i Umazan akked imawlan n lameô segwen. Ma temgarradem $ef lêaoa, erret ep ar Öebbi akked Umazan, ma tumnen s Öebbi akked wass aneggaru. Ppif akka, d ppawil amxallef.');
INSERT INTO "ramdane" VALUES(4,60,'4:60','Day ur twalav ara wid iumnen, zi$emma, s wayen ik ed irsen, d wayen id irsen, uqbel ik? B$an aêuddu iôebbiten n tkerkas, ulamma pwamôen a ten nekôen. Ib$a Cciîan a ten itlef ttelfan u$miq.');
INSERT INTO "ramdane" VALUES(4,61,'4:61','Ma nnan asen: "aset ed ar wayen id Issers Öebbi, akw ar Umazan", a pwaliv ixabiten wexxôen ak, ugin.');
INSERT INTO "ramdane" VALUES(4,62,'4:62','Amek ihi, ma u$en tiyta, ilmend n wayen xedmen ifassen nnsen, sinna, a d asen $uôek, ad ggalen s Öebbi: "neb$a kan lewqama akked lembwafqa".');
INSERT INTO "ramdane" VALUES(4,63,'4:63','Widak, Iéôa Öebbi i illan deg ulawen nnsen. Ihi, îîixeô asen, nabi iten, meslay iten, $ef iman nnsen, s wawal iqenâen.');
INSERT INTO "ramdane" VALUES(4,64,'4:64','Ur d Nuzen Amazan, siwa kan iwakken ad as a$en awal, ma Irad Öebbi. Wid idensen iman nnsen, ma usan k id, d$a ssutren ssmaê i Öebbi, u issuter Umazan ad ppusemmêen, ad afen Öebbi Issuruf, Ipêunu.');
INSERT INTO "ramdane" VALUES(4,65,'4:65','Xaîi! S Mass ik, ur ppamnen, alamma rran k d amêaddi, deg wayen i$ef mgarraden. Sinna, ur ppafen, deg iman nnsen, ara ten ihewwlen, deg wayen id têuddev. Ad anzen yal annuz.');
INSERT INTO "ramdane" VALUES(4,66,'4:66','Lemmer i sen Nuri: "mmin$et"! Ne$ "ff$et seg ixxamen nnwen"! Ur p xeddmen, siwa kra segsen. Ppif, i nitni, lemmer xdimen ayen u$uô pwanecden. D atebbet uûwib.');
INSERT INTO "ramdane" VALUES(4,67,'4:67','Imiren ad asen Nefk, s$uône£, arraz ameqwôaêan.');
INSERT INTO "ramdane" VALUES(4,68,'4:68','A ten Nenhu s abrid uwqim.');
INSERT INTO "ramdane" VALUES(4,69,'4:69','Wid ippa$en awal i Öebbi akked Umazan, widak atnan ar wid I$at Öebbi ger lambiyya, wid issdaqen, ccuhada, ûûalêin. Ay telha tdukli d wigi!');
INSERT INTO "ramdane" VALUES(4,70,'4:70','Wagi d lfevl s$uô Öebbi. Nesteqnaâ s Öebbi d Amusnaw.');
INSERT INTO "ramdane" VALUES(4,71,'4:71','A wid iumnen! Ëadret iman nnwen! èéedmet d igrawen, ne$ ééedmet akw.');
INSERT INTO "ramdane" VALUES(4,72,'4:72','Ih, garawen win ipâaîîilen. Ma tu$em tiyta, ad yini: "I$at iyi Öebbi, ur lli$ yidsen, d anagi".');
INSERT INTO "ramdane" VALUES(4,73,'4:73','Ma iusa kwen id lfevl s$uô Öebbi, ad yini, war ccekk, amzun ur telli tegmap garawen: "a lemmer lli$ yidsen, ad rebêe$ rrbeê ameqwôaêan"!');
INSERT INTO "ramdane" VALUES(4,74,'4:74','Ad nna$en deg ubrid n Öebbi wid izznuzun tameddurt n ddunit i laxert. Win ipna$en deg ubrid n Öebbi, immut ne$ irbeê, ad as Nefk arraz ameqwôaêan.');
INSERT INTO "ramdane" VALUES(4,75,'4:75','Acu kwen iu$en, ula tepna$em ara deg ubrid n Öebbi? Uôhifen seg irgazen, tilawin, igwerdan, i la iqqaôen: "a Mass nne$! Ssuffe$ ane$ si temdint agi ized$en imednas. Efk ane$ d bab s$uôeK, Efk ane$ d asettaô s$uôeK".');
INSERT INTO "ramdane" VALUES(4,76,'4:76','Wid iumnen pna$en deg ubrid n Öebbi. Wid ijehlen pna$en deg ubrid iôebbiten n tkerkas. Nna$et imeddukwal n Cciîan. Ni$ tiêeôci n Cciîan d tuôhift.');
INSERT INTO "ramdane" VALUES(4,77,'4:77','Day ur twalav ara wid iwumi nnan: "brut i ifassen, ééalet, zekkit"? Mi sen iura umennu$, arbaâ, segsen, ugaden imdanen, tugwdi n Öebbi, ne$ tugwdi i p i$elben. Qqaôen: "a Mass nne$! Acimi i $ Turiv amennu$? A lemmer i t Teswexxôev cwiî"! Ini: "zzhu n ddunit d amnuc, tif it laxert i win ipêezziben. Ur tepwadensem ula s yiclem utemôiw".');
INSERT INTO "ramdane" VALUES(4,78,'4:78','Anda tellam a kwen id tawev tmeppant, xas ma tellam di lebôuo ioehden. Ma iusa ten id yise$, ad inin: "wagi s$uô Öebbi". Ma tusa ten id tucmip, ad inin: "tagi s$uôek". Ini: "akw s$uô Öebbi". Acu iu$en agdud agi, ur izmir ad ifhem awalen?');
INSERT INTO "ramdane" VALUES(4,79,'4:79','Yal ise$ i k id iusan, s$uô Öebbi. Yal tucmip i k id iusan, s$uô iman ik. Nuzen ik id i medden, d Amazan. Nesteqnaâ s Öebbi d Anagi.');
INSERT INTO "ramdane" VALUES(4,80,'4:80','Win iu$en awal i Umazan, iu$ awal i Öebbi. Win iwexxôen, ur k id Nuzen d amêareb nnsen.');
INSERT INTO "ramdane" VALUES(4,81,'4:81','Qqaôen: "annuz"! Mi k îîaxxôen, ad iêekku deg iv, urbaâ segsen, ayen ur d tenniv. Öebbi Ipparu ayen êekkun deg iv. Wexxeô asen, ppkel $ef Öebbi. Nesteqnaâ s Öebbi d Awkil.');
INSERT INTO "ramdane" VALUES(4,82,'4:82','Day ur pmeyyizen ara Leqwôan? Lemmer illi si war Öebbi, ad afen degs aîas n wayen imgarraden.');
INSERT INTO "ramdane" VALUES(4,83,'4:83','Ma iusa ten lameô n laman ne$ n lxuf, a t berrêen. Lemmer i t ssawven i Umazan akked imawlan n lameô nnsen, ad xebôen wid, segsen, ib$an ad éôen. Lemmer maççi d lfevl n Öebbi akked uêunu S fellawen, a ppeddum d Cciîan, siwa kra kan.');
INSERT INTO "ramdane" VALUES(4,84,'4:84','Nna$ deg ubrid n Öebbi. D iman ik kan ara tkellfev. Efk afud i lmumnin. Izmer Öebbi ad Issers tazmert n wid ijehlen. Öebbi Yif iten tazmert, Yif iten aâaqeb.');
INSERT INTO "ramdane" VALUES(4,85,'4:85','Win icefâan ccafuâa ilhan, ad iawi, segs, amur. Win icefâan ccafuâa icemten, ad iawi, segs, sin imuren. Öebbi Izga Issenqad yal cci.');
INSERT INTO "ramdane" VALUES(4,86,'4:86','Ma nnan awen d: "azul"! Erret asen azul it yifen, ne$ erret asen t. Ih, Öebbi Izga Iêeppeb yal cci.');
INSERT INTO "ramdane" VALUES(4,87,'4:87','Öebbi, ur illi ôebbi siwa Neppa, a kwen Issemlil, ass n tnekra, ur degs ccekk. Anwa i iqqaôen tidep sennig Öebbi?');
INSERT INTO "ramdane" VALUES(4,88,'4:88','Acu i kwen iu$en: tebvam $ef sin, $ef ixabiten? Izzaâ ten Öebbi ilmend n wayen kesben. Day teb$am a ppenhum win Itlef Öebbi? Win Itlef Öebbi, ur as teppafev abrid.');
INSERT INTO "ramdane" VALUES(4,89,'4:89','B$an a ppilim d ijehliyen am nitni, a ppemcabim. Ur sâut iêbiben segsen, alamma unagen deg ubrid n Öebbi. Ma wexxôen, îîfet pen, nna$et pen anda ten tufam, ur sâut segsen aêbib ne$ asettaô.');
INSERT INTO "ramdane" VALUES(4,90,'4:90','Kkes ed wid iusan ar ugdud ukud tesâam laâqed, ne$ i kwen id iusan, gguman wulawen nnsen a kwen nna$en, ne$ ad nna$en agdud nnsen. Lemmer i S ihwi i Öebbi, yili Ifka yasen adabu fellawen, a kwen nna$en. Ma âazlen iman nnsen segwen, ur kwen nnu$en, ssutren awen d tifrat, ur kwen Ippawi Öebbi $uôsen.');
INSERT INTO "ramdane" VALUES(4,91,'4:91','A ppafem wiyav i ib$an tifrat yidwen, akked tifrat akked ugdud nnsen. Yal tikelt ideg ara ten rren ar lfetna, ad kecmen degs. Ma ur aâzilen ara iman nnsen segwen, ur awen ssutren ara tifrat, ur fukken ara amennu$, îîfet pen, nna$et pen anda ten tufam. Widak, Nefka yawen fellasen adabu n ûûeê.');
INSERT INTO "ramdane" VALUES(4,92,'4:92','Ur ippunefk ara i lmumen ad ine$ lmumen, siwa ma i$lev. Win in$an lmumen di le$wlav, ad iserreê i wakli iumnen, a ppeppunefk ddeyya i twacult is, siwa ma seddqen p. Ma illa seg ugdud acengu nnwen, u neppa d lmumen, ad iserreê i wakli iumnen. Ma illa seg ugdud ukud tesâam laâqed, a ppeppunefk ddeyya i twacult is, ad iserreê i wakli iumnen. Win ur nezmir ara, ad iuéum sin wagguren, wa deffir wa, d nndama i Öebbi. Öebbi Izga Iééeô, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(4,93,'4:93','Win in$an lmumen s lemâamda, aqeîîi ines d Loahennama, degs ad iwekkeô. A d Izâaf Öebbi fellas, a t Ixzu, ad as d Iheggi aâaqeb abarar.');
INSERT INTO "ramdane" VALUES(4,94,'4:94','A wid iumnen! Ma tennu$em deg ubrid n Öebbi, beggnet iman nnwen. Ur qqaôet i win i wen d ssutren tifrat: "ur telliv d lmumen"! Teb$am cci n tmeddurt n ddunit. £uô Öebbi, aîas u$iti. Akka ay tellam zik. Öebbi Ifeôôeo fellawen. Beggnet iman nnwen. Ih, Öebbi Izga Issen ayen txeddmem.');
INSERT INTO "ramdane" VALUES(4,95,'4:95','Ur aâdilen ara wid iqqimen si lmumnin, siwa wid ivôuôan, akked wid ijuhden deg ubrid n Öebbi, s wayla nnsen akked yiman nnsen. Ismenyaf Öebbi wid ijuhden s wayla nnsen akked yiman nnsen, i wid iqqimen. Akw, Iwaââed asen Öebbi ûûam. Ifrez Öebbi imjuhad af wid iqqimen, s warraz ameqwôaêan,');
INSERT INTO "ramdane" VALUES(4,96,'4:96','aseqôeb $uôeS, ssmaê, akked uêunu. Öebbi Izga Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(4,97,'4:97','Ih, i wid iwumi ddment lmalayek leôwaê, u densen iman nnsen, ad inint: "anida tellam"? A d inin: "nella d uôhifen di tmurt". Ad inint: "day ur tewsiâ ara tmurt n Öebbi, a ppinigem degs"? Widak, axxam nnsen d Loahennama. A tu$alin tucmip!');
INSERT INTO "ramdane" VALUES(4,98,'4:98','Kkes ed uôhifen, argaz, tameîîut, agrud, ur nezmir i tsensert, ur nepwanhu s abrid.');
INSERT INTO "ramdane" VALUES(4,99,'4:99','Widak, amar Öebbi ad asen Isemmeê. Öebbi Izga Ipqili, Ipsemmiê.');
INSERT INTO "ramdane" VALUES(4,100,'4:100','Win iunagen deg ubrid n Öebbi, ad iaf, di tmurt, aîas n ssôanat akked lufa. Win iff$en seg uxxam is, d iminig ar Öebbi akked Umazan iS, sinna, temmuger it tmeppant, arraz is atan ar Öebbi. Öebbi Izga Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(4,101,'4:101','Mi teppeddum di tmurt, ulac u$ilif ma teééeméim taéallit, ma tugadem a d ééedmen fellawen wid ijehlen. Ih, ijehliyen banen d icenga nnwen.');
INSERT INTO "ramdane" VALUES(4,102,'4:102','Ma telliv garasen, tezwaôev asen i téallit, ilaq a ppili terbaât, segsen, yidek, s ibeddi, ad îîfen leslaê nnsen. Mi knan, ad ilin deffir wen. A d tas terbaât nniven ur neééul, ad ééalen yidek, ad êadren iman nnsen, akked leslaê nnsen. Ssarmen wid ijehlen, a p$eflem $ef leslaê nnwen akked lqecc nnwen, iwakken a d ééedmen fellawen, yiwet n ééedma. Ulac u$ilif ma tessersem leslaê nnwen, ma ihero ikwen ugeffur ne$ tuvnem. Ëëadret iman nnwen. Ih, Öebbi Ihegga, i ijehliyen, aâaqeb isdullun.');
INSERT INTO "ramdane" VALUES(4,103,'4:103','Mi tfukkem taéallit, mmektit ed Öebbi s ibeddi, s i$imi, s tuééla. Mi teppuvemnem, ééalet. Taéallit, ih, tura i lmumen, s lewqat is.');
INSERT INTO "ramdane" VALUES(4,104,'4:104','Ur fecclet deg uvfaô n ugdud. Ma tmeênem, ula d nitni meênen am kunwi. Tessaramem, ar Öebbi, ayen ur ssaramen. Öebbi Izga Iéôa, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(4,105,'4:105','Ih, Nessers ak d Tazmamt, s tidep, iwakken a pêuddev ger imdanen, s wayen i k id Issenâat Öebbi. Ur ppili d axûim n lxeyyan.');
INSERT INTO "ramdane" VALUES(4,106,'4:106','Ssuter ssmaê i Öebbi. Öebbi, s tidep, Izga Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(4,107,'4:107','Ur pnnavaê $ef wid ixeynen iman nnsen. Öebbi, s tidep, ur Iêemmel ara win ixeynen, idenben.');
INSERT INTO "ramdane" VALUES(4,108,'4:108','B$an ad ffren i medden, ur zmiren ad ffren i Öebbi. Neppa Illa yidsen mara d qqaôen, deg iv, awalen i$ef ur Iôuva. Öebbi Izga Izzi i wayen xeddmen.');
INSERT INTO "ramdane" VALUES(4,109,'4:109','Atan kunwi tnuvêem fellasen, di tmeddurt n ddunit. Anwa ara innavêen Öebbi fellasen, ass n tnekra? Ne$ anwa ar yilin fellasen d awkil?');
INSERT INTO "ramdane" VALUES(4,110,'4:110','Win ixedmen cceôô, ne$ idnes iman is, sinna, issuter ssmaê i Öebbi, ad iaf Öebbi Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(4,111,'4:111','Win ibbwin abekkav, ibbwi it, kan, i iman is. Öebbi Izga Iéôa, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(4,112,'4:112','Win ifalîan ne$ ibbwin abekkav, irra p id $ef wur nexdim, ibbwi lâaô, akked ubekkav n ûûeê.');
INSERT INTO "ramdane" VALUES(4,113,'4:113','Lemmer maççi d lfevl n Öebbi fellak, akked uêunu S, iaâzem urbaâ, segsen, a k telfen. Ur tellifan siwa iman nnsen. Ur k îîuôôun s wacemma. Issers ak d Öebbi Tazmamt akked ûûwab. Isselmed ak ayen ur tessinev. Izga lfevl n Öebbi, fellak, d ameqwôaêan.');
INSERT INTO "ramdane" VALUES(4,114,'4:114','Aîas n wayen qqaôen ur imâin, siwa ayen iumôen ssadaqa, ne$ lqanun, ne$ lemûalêa ger medden. Win ixedmen aya, issarem ad Iôavi Öebbi, deqqal ad as Nefk arraz ameqwôaêan.');
INSERT INTO "ramdane" VALUES(4,115,'4:115','Win ixuûmen Amazan, mbaâd ma tepwabeggen as d nnhaya, iteddun d ubrid ur d wid n lmumnin, a t Neoo akken iooa. A t Nenheô ar Loahennama. A tu$alin tucmip!');
INSERT INTO "ramdane" VALUES(4,116,'4:116','Ih, Öebbi ur Ipsemmiê ma ad aS carken. Ipsemmiê ayen nniven, i win i S ihwan. Win icurken i Öebbi, innetlef ttelfan u$miq.');
INSERT INTO "ramdane" VALUES(4,117,'4:117','La pnujun ar nnta, war Neppa. La pnujun kan ar Cciîan aballa$.');
INSERT INTO "ramdane" VALUES(4,118,'4:118','Ixza t Öebbi. Inna: "ad awi$ amur ippuêeppmen, seg imdanen ik.');
INSERT INTO "ramdane" VALUES(4,119,'4:119','A ten telfe$, a ten ssenêesse$, ad asen amôe$ ad gezmen imeééu$en ubeqôi, ad asen amôe$ ad bedlen lxelq n Öebbi". Win idmen Cciîan d bab, war Öebbi, ixseô lexsaôa n tidep.');
INSERT INTO "ramdane" VALUES(4,120,'4:120','Ipâahad asen, issenêas iten. Ayen i sen iâahed Cciîan, d a$uôôu kan.');
INSERT INTO "ramdane" VALUES(4,121,'4:121','Widak, axxam nnsen d Loahennama, ur ppafen, segs, tisensert.');
INSERT INTO "ramdane" VALUES(4,122,'4:122','Wid iumnen, u xeddmen ayen ilhan, a ten Nessekcem ar leonanat, ddawat sen isaffen ppazzalen, degsen ad wekkôen, i lebda. Lemâahda n Öebbi a ppevôu. Anwa issawalen tidep sennig Öebbi?');
INSERT INTO "ramdane" VALUES(4,123,'4:123','Ur illi s usirem nnwen, ne$ s win n At Tezmamt. Win ixedmen cceôô, ad ippuqeîîi fellas. Ur Ippaf, war Öebbi, bab ne$ asettaô.');
INSERT INTO "ramdane" VALUES(4,124,'4:124','Win ixeddmen ayen ilhan, d ddkeô ne$ d nnta, u neppa iumen..., widak ad kecmen Loennet, ur pwadensen s yiclem utemôiw.');
INSERT INTO "ramdane" VALUES(4,125,'4:125','Anwa iûegmen ddin is sennig win iunzen i Öebbi, u neppa d laâûem, u idda d ddin n Ibôahim, i imalen ar tidep? Öebbi Irra Ibôahim d aêbib.');
INSERT INTO "ramdane" VALUES(4,126,'4:126','N Öebbi i illan deg igenwan, i illan di tmurt. Öebbi Izga Izzi i yal cci.');
INSERT INTO "ramdane" VALUES(4,127,'4:127','Steqsayen k $ef tlawin. Ini: "Öebbi ad awen Issefhem fellasent. Ayen i wen $ôan di Tezmamt, $ef tlawin tigujilin, iwumi ur tefkim ara i sent iuran, u teb$am a tent ta$em. Akked igwerdan imeéyanen. A pbeddem i igujilen s lmizan. Ayen akw txeddmem n lxiô, Öebbi, s tidep, Izga Iéôa t.');
INSERT INTO "ramdane" VALUES(4,128,'4:128','Ma tugad tmeîîut ad as ibru, ne$ ad as iwexxeô urgaz is, ulac u$ilif fellasen ma ufan lemwafaqa garasen. Ppif lemwafaqa. Imdanen, deg iman nnsen d imceôôiyen. Ma tgerzem u têezzbem, Öebbi, s tidep, Izga Iéôa ayen txeddmem.');
INSERT INTO "ramdane" VALUES(4,129,'4:129','Ur tezmirem a ppeddum s lmizan, ger tlawin, xas teb$am. Ihi, ur pmalet yal tumlin a p teooem, amzun d tamejgugelt. Ma tgam ayen ilhan, u têezzbem, s tidep, Öebbi Izga Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(4,130,'4:130','Ma mfaôaqen, ad I$it Öebbi yal wa, s ôôeéq iS. Öebbi Izga d Ajemmal, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(4,131,'4:131','N Öebbi i illan deg igenwan, i illan di tmurt. Ni$ Nweûûa wid iwumi teppunefk Tezmamt, uqbel nnwen, u Nweûûa kwen, a pêezzbem i Öebbi. Ma tnekôem, ihi, n Öebbi i illan deg igenwan, i illan di tmurt. Öebbi Izga d Asâay, d Amaâzuz.');
INSERT INTO "ramdane" VALUES(4,132,'4:132','N Öebbi i illan deg igenwan, i illan di tmurt. Nesteqnaâ s Öebbi d Awkil.');
INSERT INTO "ramdane" VALUES(4,133,'4:133','Ma ihwa yaS, a kwen Ikkes a yimdanen! A d Iawi wiyav. Öebbi Izga Izmer i waya.');
INSERT INTO "ramdane" VALUES(4,134,'4:134','Win ib$an a$ellet n ddunit, ihi, ar Öebbi a$ellet n ddunit akked win n laxeôt. Öebbi Izga Issel, Ipwali.');
INSERT INTO "ramdane" VALUES(4,135,'4:135','A wid iumnen! Teddut s lmizan, d inagan n Öebbi, xas $ef yiman nnwen, ne$ imawlan u llzem, ama d ameôkanti, ama d igellil. Izwar iten Öebbi. Ur teddut d lhawiyya, ur twezznem ara. Ma txutlem, ne$ twexxôem, ihi, Öebbi Izga Iéôa ayen txeddmem.');
INSERT INTO "ramdane" VALUES(4,136,'4:136','A wid iumnen! Amnet s Öebbi akked Umazan iS, akked Tezmamt id Issers i Umazan iS, akked Tezmamt id Issers, uqbel. Win inekôen Öebbi, lmalayek iS, Tizmamin iS, imazanen iS, akked wass aneggaru, ihi innetlaf ttelfan u$miq.');
INSERT INTO "ramdane" VALUES(4,137,'4:137','Ih, wid iumnen, sinna, nekôen, sinna, umnen, sinna, nekôen, sinna, zaden di nnkeô, ur sen Ipsemmiê Öebbi, ur ten Ippawi d ubrid.');
INSERT INTO "ramdane" VALUES(4,138,'4:138','Becceô ixabiten ar fellasen aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(4,139,'4:139','Wid ipparan ijehliyen d iêbiben, war lmumnin, day ssarmen $uôsen tazmert? Tazmert, s tidep, ar Öebbi yakw.');
INSERT INTO "ramdane" VALUES(4,140,'4:140','Ni$ Nessers awen d, di Tezmamt: "ma teslam la nekôen issekniyen n Öebbi, la ttihin fellasen, ur p$imit yidsen, alamma bedlen ameslay. Mulac a ppilim am nitni". Öebbi, s tidep, a d Issemlil ixabiten akked ijehliyen di Loahennama, meôôa.');
INSERT INTO "ramdane" VALUES(4,141,'4:141','Wid i kwen iquôâan, ma Ifka yawen d Öebbi l$elb, a d inin: "day ur nella ara yidwen"? Ma illa wayen rebêen ijehliyen, ad inin: "day ur nêuôeb ara fellawen, nsellek ikwen si lmumnin"? Ad Iêudd Öebbi garawen, ass n tnekra. Ur Ippara ara Öebbi, i ijehliyen, abrid ar lmumnin.');
INSERT INTO "ramdane" VALUES(4,142,'4:142','Ih, ixabiten xedâen Öebbi. D Neppa iten Ixedâan. Ma kren d i téallit, a d kren d imaâguza, akken kan a ten id walin medden. Cwiî kan id smektayen Öebbi.');
INSERT INTO "ramdane" VALUES(4,143,'4:143','Ur banen akka, ne$ akka; $eô wigi, ne$ $eô wiyav. Win Itlef Öebbi ur as teppafev abrid, d awez$i.');
INSERT INTO "ramdane" VALUES(4,144,'4:144','A wid iumnen! Ur pparat ijehliyen d iêbiben, war lmumnin. Day teb$am ad aS tefkem, i Öebbi, adabu ibanen, mgal kwen?');
INSERT INTO "ramdane" VALUES(4,145,'4:145','Ih, ixabiten di lqaâa n wadda n tmes, ur asen teppafev asettaô.');
INSERT INTO "ramdane" VALUES(4,146,'4:146','Kkes ed wid ituben, ixeddmen ayen ilhan, iîîfen di Öebbi, izzazdgen ddin nnsen ar Öebbi. Widak, ad ilin ar lmumnin. Deqqal a d Iawi Öebbi, i lmumnin, arraz ameqwôaêan.');
INSERT INTO "ramdane" VALUES(4,147,'4:147','Acu ara Ixdem Öebbi s uâaqeb nnwen, ma tesnemmrem, tumnem? Ni$ Öebbi Ippara tajmilt, Iéôa.');
INSERT INTO "ramdane" VALUES(4,148,'4:148','Ur Iêemmel ara Öebbi nnaâlat, siwa seg win ipwadensen. Öebbi Izga Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(4,149,'4:149','Ma txedmem lxiô âinani, ne$ teffrem t, ne$ tsemmêem abekkavu, Öebbi Izga Ipsemmiê, d Aêernuk.');
INSERT INTO "ramdane" VALUES(4,150,'4:150','Ih, wid inekôen Öebbi akked imazanen iS, u b$an ad ssemfaôqen Öebbi akked imazanen iS, qqaôen: "numen s kra, nenkeô kra". B$an ad awin abrid garasen.');
INSERT INTO "ramdane" VALUES(4,151,'4:151','Widak, d ijehliyen n ûûeê. Nhegga, i ijehliyen, aâaqeb isdullun');
INSERT INTO "ramdane" VALUES(4,152,'4:152','Wid iumnen s Öebbi akked imazanen iS, ur ten ssemgarraden, widak, deqqal a ten id ias warraz nnsen. Öebbi Izga Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(4,153,'4:153','La k d ssuturen, At Tezmamt, ad asen d tessersev Tazmamt seg igenni. Ni$ ssutren, i Musa, sennig waya. Nnan as: "ssenâat a$ d Öebbi, a T nwali". Tebbwi ten ssiâqa, ilmend n ddens nnsen. Sinna, âabden aâjmi, mbaâd ma iusa ten id ubeggen. Nesemmeê asen aya. Nefka, i Musa, adabu n ûûeê.');
INSERT INTO "ramdane" VALUES(4,154,'4:154','Nessuli adrar sennig sen, ilmend n laâqed nnsen. Nenna yasen: "kecmet si tebburt s lekni". Nenna yasen: "ur txalafet ssebt". Neîîef segsen laâqed iûeêêan.');
INSERT INTO "ramdane" VALUES(4,155,'4:155','Imi neqven laâqed nnsen, u nekôen issekniyen n Öebbi, u n$an lambiyya, war ssebba, u nnan: "ulawen nne$ duben". Xaîi, d Öebbi iten Isel$en, ilmend n tjehli nnsen. Drus kan i iumnen.');
INSERT INTO "ramdane" VALUES(4,156,'4:156','Ilmend n nnekô nnsen, u gren d, $ef Meryem, acehrir abarar;');
INSERT INTO "ramdane" VALUES(4,157,'4:157','u nnan: "nekwni nen$a Âisa, mmi s n Meryem, Amazan n Öebbi". Ur t n$in, ur t semmôen $ef umidag. D lexyal kan ger wallen nnsen. Ih, wid imgarraden fellas, atnan di ccekk fellas, ur éôin fellas acemma, teddun kan d nnwaya. Metbut, ur t nn$in.');
INSERT INTO "ramdane" VALUES(4,158,'4:158','Xaîi! Issuli t Öebbi $uôeS. Öebbi Izga d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(4,159,'4:159','Ur illi yiwen, seg wAt Tezmamt, ur numin yis, uqbel tameppant is. Ass n tnekra ad yili d anagi nnsen.');
INSERT INTO "ramdane" VALUES(4,160,'4:160','Ilmend n ddens n wudayen, Negdel asen tiéidanin i illan êellent asen, imi da$en, îîeôôifen aîas seg ubrid n Öebbi;');
INSERT INTO "ramdane" VALUES(4,161,'4:161','u ssexdamen rrba, i illan tepwakkes asen, u teppen ayla n medden, d aêerfi. Nhegga d, i ijehliyen segsen, aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(4,162,'4:162','Maca, wid, segsen, izaden di tmusni, lmumnin iumnen s wayen ik ed irsen, akked wayen id irsen, uqbel ik, wid ibedden i téallit, wid ipzekkin, wid iumnen s Öebbi akked wass aneggaru, widak, ad asen Nefk arraz ameqwôaêan.');
INSERT INTO "ramdane" VALUES(4,163,'4:163','Ih, Nweêêa yak ed, akken Nweêêa i Nuê, akked lambiyya deffir es. Nweêêa i Ibôahim, Ismaâil, Isêaq, Iaâqub, laâôac, Âisa, Yub, Yunes, Harun, Sliman. Nefka Ihellilen i Dawed.');
INSERT INTO "ramdane" VALUES(4,164,'4:164','Akked lambiyya i$ef ik ed Neêka, uqbel; akked lambiyya, i$ef ur ak ed Neêki. S tidep, Immeslay Öebbi i Musa.');
INSERT INTO "ramdane" VALUES(4,165,'4:165','Lambiyya ipbecciôen, inedôen, iwakken ur pnamaren medden $ef Öebbi, deffir imazanen. Öebbi Izga d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(4,166,'4:166','Maca, Icuhhed Öebbi $ef wayen ik ed Issers, s tmusni S. Ad cehhdent lmalayek. Nesteqnaâ s Öebbi d Anagi.');
INSERT INTO "ramdane" VALUES(4,167,'4:167','Ih, wid ijehlen, u îîeôôifen seg ubrid n Öebbi, ni$ nnetlafen ttelfan u$miq.');
INSERT INTO "ramdane" VALUES(4,168,'4:168','Ih, wid ijehlen u densen, ur sen Ipsemmiê Öebbi, ur ten Ippawi d ubrid,');
INSERT INTO "ramdane" VALUES(4,169,'4:169','siwa abrid n Loahennama, degs ad wekkôen, i lebda. Ayagi, Izga d ayen isehlen i Öebbi.');
INSERT INTO "ramdane" VALUES(4,170,'4:170','A yimdanen! Ni$ iusa kwen id Umazan, s tidep, s$uô Mass nnwen. Amnet, ppif i kunwi. Ma tugim, ihi n Öebbi, i illan di tmurt akked igenwan. Öebbi Izga Iéôa, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(4,171,'4:171','A yAt Tezmamt! Ur sfuhruyet ddin nnwen. Ur qqaret, $ef Öebbi, siwa tidep. Lmasiê Âisa, mmi s n Meryem, d amazan kan n Öebbi; d awal iS i Irra ar Meryem, u d ôôuê s $uôeS. Ihi, amnet s Öebbi akked imazanen iS, ur qqaôet: "krav"! Ëebset, ppif i kunwi. Öebbi d Yiwen kan n Öebbi. Gedha s yiS, ma ad Isâu mmi S! IneS i illan deg igenwan, i illan di tmurt. Nesteqnaâ s Öebbi d Awkil.');
INSERT INTO "ramdane" VALUES(4,172,'4:172','Ur ismummi ara lmasiê, ma illa d amdan n Öebbi. Akken lmalayek tuqôibin. Wid ismummin a T âabden, u zuxxen, ad pwafessren akw $uôeS.');
INSERT INTO "ramdane" VALUES(4,173,'4:173','Wanag wid iumnen, u xedmen ayen ilhan, ad asen Ifk arraz nnsen, ad asen Irnu si lfevl iS. Ma d wid ismummin u zuxxen, a ten Iâaqeb aâaqeb aqeôêan. Ur ppafen, war Öebbi, bab ne$ asettaô.');
INSERT INTO "ramdane" VALUES(4,174,'4:174','A yimdanen! Ni$ iusa kwen id utebbet s$uô Mass nnwen, u Nessers awen ed tafat ibanen.');
INSERT INTO "ramdane" VALUES(4,175,'4:175','Wanag wid iumnen s Öebbi, i iîîfen degS, a ten Issekcem ar uêunu S akked lfevl. A ten Inhu $uôeS, deg ubrid uwqim.');
INSERT INTO "ramdane" VALUES(4,176,'4:176','Cawôen k. Ini: "Öebbi Isfehm awen d $ef llzem. Ma immut urgaz, war tarwa, isâa taultmap, i neppat azgen n wayen id iooa. Neppa a p iwôet, ma ur tesâi tarwa. Ma snat ay llant, amur wis krav i yal ta, n wayen id iooa. Ma llan watmaten akked tessetmatin, i uqcic amur n snat n tullas". Ibeggen awen d Öebbi akken ur tepnnetlafem. Öebbi Iéôa yal cci.');
INSERT INTO "ramdane" VALUES(5,1,'5:1','A wid iumnen! Eîîfet deg iâahden nnwen. Ëellen awen lmal n tejlibt, kkes ed wid i wen nnan. Ur awen têell ara ûûyada, mara têuoem. Öebbi, s tidep, Ippameô ayen i S ihwan.');
INSERT INTO "ramdane" VALUES(5,2,'5:2','A wid iumnen! Ur ssiôxiset isekkiren n Öebbi, aggur iôqan, îêegga, snaoeq, wid iteddun ar Uxxam n Lêaôam, saramen lfevl akked llza n Mass nnsen. Üegdet mi tfukkem. Xas tcuênem agdud i wen igedlen Loamaâ n Lêaôam, ur iwata ara a pzegrem tilas. Mâawanet ar yise$ akked uêezzeb, ur ppemâawanet ar ubekkav akked lbe$wv. Ëezzbet i Öebbi. S tidep, Öebbi Iwâaô deg uâaqeb.');
INSERT INTO "ramdane" VALUES(5,3,'5:3','I wen ipwagedlen d loifa, idim, aksum n yilef, wayen ipwamezlen i war Öebbi, ayen ippuxenqen, ayen i pwaûerâan, ayen igrarben, ayen ippuberrzen, ayen içça lweêc - siwa ma tessawvem tezlam t -, îêegga i tezôa, aseqdec n tseqqaôt. Ayagi d ucmit. Assa, wid ijehlen uysen si ddin nnwen. Ur ten ppagwadet, agwadet iyI. Assa, Nnekmale$ awen ddin nnwen, Tame$ awen l$it iW, Öuva$ awen annuz d ddin. Win terra tmara di tegwnip n llaé, ur iffi$ abrid, Öebbi, s tidep, Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(5,4,'5:4','A k steqsayen acu i sen iêellen. Ini: "iêell awen wayen imaânen. Ayen d teppawin s lewêuc ipûeggiden. Tesêefvem asen ayen i wen Isseêfev Öebbi. Eççet seg wayen i wen d îîfen, mmektit ed fellas isem n Öebbi". Ëezzbet i Öebbi. Öebbi, s tidep, Ip$awal deg uqeîîi.');
INSERT INTO "ramdane" VALUES(5,5,'5:5','Assa, iêell awen wayen imaânen. Iêell awen lqut n wid iwumi teppunefk Tezmamt, - iêell asen lqut nnwen -; tilawin tiêuôiyin iumnen, tilawin tiêuôiyin seg wid iwumi teppunefk Tezmamt, uqbel nnwen, ma tefkam asent tuççit nnsent; d uwqimen maççi d ucmiten, ur teppeddum d temddukwal. Win iugin liman, mêan igiten is. Neppa, di laxeôt, seg imexsuôen.');
INSERT INTO "ramdane" VALUES(5,6,'5:6','A wid iumnen! Ma tekkrem ar téallit, ssirdet udmawen nnwen, ifassen nnwen alamma d ti$wmaô, sâaddit ifassen illexsen $ef iqweôôa nnwen, ssirdet ivaôen nnwen ar twelza. Ma tellam s loanaba, zzizdget. Ma tuvnem ne$ tunagem, ne$ iusa d yiwen segwen si ccicma, ne$ tennulem tilawin, ur tufim aman, ddmet akal zeddigen, sâaddit ep $ef udmawen nnwen akked ifassen nnwen. Ur Ib$a ara Öebbi a kwen Iqelleq, maca Ib$a a kwen Izzizdeg, ad awen Itim l$it iS, amar a pesnemmrem.');
INSERT INTO "ramdane" VALUES(5,7,'5:7','Mmektit ed l$it ikwen I$at Öebbi, akked nnesba ikwen Inuseb, mi tennam: "nesla u nunez". Ëezzbet i Öebbi. Öebbi, s tidep, Issen i illan deg idmaren.');
INSERT INTO "ramdane" VALUES(5,8,'5:8','A wid iumnen! Illit d ôôsem ar Öebbi, d inagan at lmizan. Ur kwen issencaf dd$el n ugdud, ur twezznem. Wezznet. Lmizan iqôeb aîas ar uêezzeb. Ëezzbet i Öebbi. Öebbi, s tidep, Issen ayen txeddmem.');
INSERT INTO "ramdane" VALUES(5,9,'5:9','Iâahed Öebbi ssmaê akked warraz ameqwôaêan, i wid iumnen, u xeddmen ayen ilhan.');
INSERT INTO "ramdane" VALUES(5,10,'5:10','Wid ijehlen u skaddben issekniyen nne£, widak d at ujajiê.');
INSERT INTO "ramdane" VALUES(5,11,'5:11','A wid iumnen! Mmektit ed l$it ikwen I$at Öebbi, amek agdud ipheggi iman is, ad isâaddi afus is fellawen. Iîîef akin ifassen nnsen, segwen. Ëezzbet i Öebbi. £ef Öebbi ara ppeklen lmumnin.');
INSERT INTO "ramdane" VALUES(5,12,'5:12','Ni$ Irra Öebbi laâqed i At Isôail. Nessuffe$ ed, segsen, mrawsin imvebbôen. Inna Öebbi: "Nek yidwen ma téullem, tzekkam, tumnem s imazanen iW, u tâawnem ten, tôevlem i Öebbi aôeîîal igerzen. War ccekk, ad awen Kkse$ tucmitin nnwen, a kwen Skecme$ di leonanat, ddawat sen isaffen ppazzalen. Win ijehlen segwen, deffir waya, ni$ innetlaf seg ubrid umâin".');
INSERT INTO "ramdane" VALUES(5,13,'5:13','Imi ooan laâqed nnsen, Nexza ten, Nesgursel ulawen nnsen. Ssexsaôen inumak n wawalen, ppun amur n wayen i$ef pwafekkôen. Mazal a d temlilev d lexdaâ nnsen, siwa drus segsen. Semmeê asen u ssuref. Öebbi, s tidep, Iêemmel iâaôviyen.');
INSERT INTO "ramdane" VALUES(5,14,'5:14','Wid iqqaôen: "nekwni d imasiêiyen", Nerra laâqed yidsen, ppun amur n wayen i$ef pwafekkôen. Nessker ed, garasen, taâdawt akked dd$el, ar ass n tnekra. Deqqal a ten Ixebbeô Öebbi $ef wayen la ûenâan.');
INSERT INTO "ramdane" VALUES(5,15,'5:15','A yAt Tezmamt! Ni$ iusa kwen id Umazan nne£. Ad awen ibeggen aîas n wayen la teppeffrem, n Tezmamt. Ad Ipsemmiê i waîas. Ni$ tusa kwen id tafat s$uô Öebbi, akked Tezmamt ipbegginen.');
INSERT INTO "ramdane" VALUES(5,16,'5:16','Inehhu yis Öebbi, ar iberdan n tifrat, wid iôe$ben llza S. A ten Issuffe$ si tillas ar tafat, s tsriê iS. A ten Inhu s abrid uwqim.');
INSERT INTO "ramdane" VALUES(5,17,'5:17','Ni$ jehlen wid innan: "Öebbi, s tidep, d lmasiê, mmi s n Meryem". Ini: "anwa isâan kra n lkwelf, s$uô Öebbi, ma ihwa yaS ad Ine$ lmasiê, mmi s n Meryem, imma s, akked wayen akw illan di tmurt"? N Öebbi tagelda n tmurt, igenwan, akked wayen illan garasen. Ixelleq ayen i S ihwan. Öebbi Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(5,18,'5:18','Udayen akked imasiêiyen, nnan: "nekwni d tarwa n Öebbi, d wid Ismenyaf". Ini: "acimi, ihi, ikwen Ipâaqab $ef ibekkaven nnwen? Xaîi, kunwi d imexlaq am wiyav". Ad Isemmeê i win i S ihwan, ad Iâaqeb win i S ihwan. N Öebbi tagelda n tmurt, igenwan, akked wayen illan garasen. Taggara $uôeS.');
INSERT INTO "ramdane" VALUES(5,19,'5:19','A yAt Tezmamt! Ni$ iusa kwen id Umazan nne£, ad awen ibeggen, mi tâadda tallit war imazanen. Akka ur d teqqaôem ara: "ur a$ d iusi ubeccaô, ne$ uneddaô". Ihi, iusa kwen id ubeccaô, uneddaô. Öebbi Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(5,20,'5:20','Mi inna Musa i ugdud is: "a yagdud iw! Mmektit ed l$it ikwen I$at Öebbi. Irra segwen lambiyya, Irra kwen d igeldan. Ifka yawen ayen ur Ifki i yiwen, deg imavalen.');
INSERT INTO "ramdane" VALUES(5,21,'5:21','A yagdud iw! Kecmet akal iôqan, i wen Iura Öebbi. Ur d ppu$alet $er deffir wen, a d tegwrim txesôem".');
INSERT INTO "ramdane" VALUES(5,22,'5:22','Nnan: "a Musa! Degs agdud iâafriten, nekwni ur p nkeççem, d awez$i, alamma ff$en d segs. Mi d ff$en segs, a nekcem".');
INSERT INTO "ramdane" VALUES(5,23,'5:23','Sin irgazen, seg wid iugaden - Isâad iten Öebbi -, nnan: "kecmet $uôsen si tebburt; ma tkecmem, kunwi, ih, a prebêem. Ppeklet af Öebbi, ma d lmumnin ay tellam".');
INSERT INTO "ramdane" VALUES(5,24,'5:24','Nnan: "a Musa! Nekwni ur p nkeççem, d awez$i, skud mazal iten degs. Öuê, keçç d Mass ik, nna$et. Nekwni a neqqim dagi".');
INSERT INTO "ramdane" VALUES(5,25,'5:25','Inna: "a Mass iw! Ay melke$, nek, siwa iman iw akked gma; ihi, Semfaôeq a$ d ugdud".');
INSERT INTO "ramdane" VALUES(5,26,'5:26','Inna: "ihi, tamurt agi tepwagdel asen. Ukuéet iseggwasen ad menîaôen di lberr. Ur $webben $ef ugdud iballa$en".');
INSERT INTO "ramdane" VALUES(5,27,'5:27','Eêku yasen, s tidep, taqûit n sin warrac n Adem, mi îêeggan i sin. S$uô yiwen tepwaqbel. S$uô wayev ur tepwaqbel ara. Inna: "war ccekk, a k n$e$". Inna: "Iqebbel kan Öebbi îêegga s$uô wid ipêezziben.');
INSERT INTO "ramdane" VALUES(5,28,'5:28','Ma ibbwev iyi d ufus ik, ad iyi ten$ev, nek, ur k id ippawev ufus iw, a k n$e$. Nek, ppagwade$ Öebbi, Mass imavalen.');
INSERT INTO "ramdane" VALUES(5,29,'5:29','Nek, b$i$ a ppawiv abekkav iw, akked ubekkav ik, a ppiliv seg wat tmes. Winna d aqeîîi imednas".');
INSERT INTO "ramdane" VALUES(5,30,'5:30','Iaâzem, deg iman is, ad ine$ gma s, d$a in$a t. Igwra d seg imexsuôen.');
INSERT INTO "ramdane" VALUES(5,31,'5:31','Iuzen ed Öebbi tagerfa, tkeôôev akal, iwakken ad as tessenâat amek ara i$umm loeppa n gma s. Inna: "a ssexv iw! Ur bbwive$ ad ili$ am tgerfa yagi, ad $umme$ loeppa n gma". Igwra d seg wid inedmen.');
INSERT INTO "ramdane" VALUES(5,32,'5:32','Ilmend n waya, Nura i At Isôail, ar win in$an amdan ur nen$i yiwen, ur nessefsed di tmurt, amzun in$a imdanen meôôa. Win issadren yiwen, amzun issader imdanen meôôa. Ni$ usan ten id imazanen nne£, s ubeggen. Sinna, deffir waya, s tidep, aîas degsen ssuguten di tmurt.');
INSERT INTO "ramdane" VALUES(5,33,'5:33','Aqeîîi n wid ipna$en Öebbi akked Umazan iS, u ssenkaren asefsed di tmurt, siwa timen$iwt, ne$ asemmeô $ef umidag, ne$ anegzum ufus sya, uvaô sya, ne$ anfay si tmurt. Ayagi, d aqeîîi nnsen di ddunit. Di laxeôt, fellasen aâaqeb ameqwôaêan.');
INSERT INTO "ramdane" VALUES(5,34,'5:34','Kkes ed wid inedmen, uqbel a pvebbôem fellasen. Cfut ar Öebbi Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(5,35,'5:35','A wid iumnen! Ëezzbet i Öebbi, nadit a pqeôôbem $uôeS, nna$et deg ubrid iS, amar a prebêem.');
INSERT INTO "ramdane" VALUES(5,36,'5:36','Ih, wid ijehlen, xas ad sâun i illan akw di tmurt, rnu yas win i t icban, iwakken ad fdun, yis, iman nnsen seg uâaqeb, ass n tnekra, ur ipwaqbal segsen. Fellasen aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(5,37,'5:37','Ad b$un a d ff$en si tmes, ur llin d wid id iteff$en. Fellasen aâaqeb imezgi.');
INSERT INTO "ramdane" VALUES(5,38,'5:38','Amakwar, tamakwart, gezmet asen ifassen nnsen, d aqeîîi n wayen xedmen, d aâaqeb s$uô Öebbi. Öebbi d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(5,39,'5:39','Win inedmen, deffir ddens is, u iu$al d aâôvi, ad as Issuref Öebbi. Öebbi, s tidep, Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(5,40,'5:40','Day ur teéôiv ara n Öebbi tagelda n tmurt akked igenwan? Ipâaqab win i S ihwan, Ipsemmiê i win i S ihwan. Öebbi Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(5,41,'5:41','A Nnbi! Ur k $weblen wid ip$awalen ar tijehli, seg wid iqqaôen numen, s imawen nnsen, ur uminen wulawen nnsen, seg wudayen issemêasen i tikerkas, issemêasen i ugdud nniven, ur d nusi $uôek. Pbeddilen awalen deffir ussefhem nnsen. Qqaôen: "ma ippunefk awen d waya, ddemt ep; ma ur awen d ippunefk ara, $uôwat"! Win Ib$a Öebbi a t Ihewwel, ur t tepsellikev, s wacemma, seg ufus n Öebbi. Widak, d wid iwumi ur Ib$a ara Öebbi, ad izdigen wulawen nnsen. Di ddunit ad pwaxzun. Di laxeôt fellasen aâaqeb abarar.');
INSERT INTO "ramdane" VALUES(5,42,'5:42','Wid issemêasen i tikerkas, iteppen leêôam, ma usan k id, fru garasen, ne$ eoo iten. Ma teooiv ten, ur k îîuôôun s wacemma. Ma têuddev, êudd garasen s lmizan. Öebbi, s tidep, Iêemmel wid iweznen.');
INSERT INTO "ramdane" VALUES(5,43,'5:43','Amek ara k id asen, s aêuddu, u nitni sâan Tuôa, degs aêuddu n Öebbi? Sinna, deffir waya, ad wexxôen. Ur llin wigi si lmumnin.');
INSERT INTO "ramdane" VALUES(5,44,'5:44','Ih, Nessers ed Tuôa, degs nnhaya akked tafat, ferrun yis ger wudayen, lambiyya imvuâen. U iôabaniyen akked lecyux seg wayen jemâen, si Tezmamt n Öebbi, u llan d inagan is. Ur ppagwadet imdanen, agwadet iyI. Ur d ppa$et, s issekniyen iW, ayen ôxisen. Wid ur nepêuddu yara s wayen id Issers Öebbi, widak d ijehliyen.');
INSERT INTO "ramdane" VALUES(5,45,'5:45','Nura yasen degs: ôôuê s ôôuê, tiî s tiî, anzaren s wanzaren, ameééu$ s umeééu$, tu$mest s tu$mest, leoruê ad ppuêesben. Win iooan, d le$ôama s. Wid ur nepêuddu yara s wayen id Issers Öebbi, widak d imednas.');
INSERT INTO "ramdane" VALUES(5,46,'5:46','Nuzen ed, deffir sen, Âisa, mmi s n Meryem, a d itebbet ayen it id izwaren di Tuôa. Nefka yas Linoil, degs nnhaya akked tafat, a d itebbet ayen it id izwaren di Tuôa. D nnhaya u d anabi i wid ipêezziben.');
INSERT INTO "ramdane" VALUES(5,47,'5:47','At Linoil, iwata ad êudden s wayen id Issers, degs, Öebbi. Wid ur nêudd ara s wayen id Issers Öebbi, widak d iballa$en.');
INSERT INTO "ramdane" VALUES(5,48,'5:48','Nessers ak d Tazmamt, s tidep, a d tebbet ayen ip id izwaren n Tezmamt. Nvemn ip. Ihi, êudd garasen s wayen id Issers Öebbi. Ur teddu d lhawiyyat nnsen, beôôa n wayen i k id iusan n tidep. I yal wa, Nefka lqanun akked ubrid. Lemmer i S ihwi i Öebbi, yili Irra kwen d yiwen n lois. Maca, iwakken a kwen Ijeôôeb $ef wayen i wen Ifka, mzizlet ar lxiô. Ar Öebbi tu$alin nnwen akw. A kwen Ixebbeô $ef wayen tellam temgarradem.');
INSERT INTO "ramdane" VALUES(5,49,'5:49','Ëudd garasen s wayen id Issers Öebbi. Ur teddu d lhawiyyat nnsen. £uôek a k hewwlen $ef kra seg wayen i k d Issers Öebbi. Ma wexxôen, éeô ar Öebbi Ib$a kan a ten Iwwet $ef kra ibekkaven nnsen. Ih, aîas n medden d iballa$en.');
INSERT INTO "ramdane" VALUES(5,50,'5:50','Day b$an aêuddu n zzman n war tussna? Anwa yifen Öebbi d Amêaddi, i ugdud ineûêen liman?');
INSERT INTO "ramdane" VALUES(5,51,'5:51','A wid iumnen! Ur pparat, d iêbiben, udayen akked imasiêiyen. Garasen d iêbiben. Win, segwen, i ten irran d iêbiben, ad yili segsen. Öebbi, s tidep, ur Inehhu agdud imednas.');
INSERT INTO "ramdane" VALUES(5,52,'5:52','A pwaliv imuvan n wul, ppazzalen $uôsen, qqaôen asen: "nugad ay d itezzin fella$". Ahat a d Ias Öebbi s l$elb, ne$ s lameô s$uôeS. A d gwrin di nndama, $ef wayen ffren deg iman nnsen.');
INSERT INTO "ramdane" VALUES(5,53,'5:53','A d inin wid iumnen: "day d wigi i iggullen s Öebbi, tagallit meqqwôen, ar nitni llan yidwen"? Mêan igiten nnsen, gwran d d imexsuôen.');
INSERT INTO "ramdane" VALUES(5,54,'5:54','A wid iumnen! Win segwen iooan ddin is..., ihi, deqqal Öebbi a d Iawi agdud Iêemmel, i T iêemmlen; ad ôavin lmumnin, ad qesêen ar ijehliyen. Ad nna$en deg ubrid n Öebbi. Ur ppagwaden alummu n win iplummun. Ayagi d lfevl n Öebbi, Ippak it i win i S ihwan. Öebbi d Ajemmal, d Amusnaw.');
INSERT INTO "ramdane" VALUES(5,55,'5:55','Imawlan nnwen siwa Öebbi akked Umazan iS, wid iumnen, wid ipéallan, pzekkin, u nitni îîinizen.');
INSERT INTO "ramdane" VALUES(5,56,'5:56','Wid irran d imawlan nnsen, Öebbi, Amazan iS, akked wid iumnen..., ih, akabar n Öebbi, d nitni ara irebêen.');
INSERT INTO "ramdane" VALUES(5,57,'5:57','A wid iumnen! Ur pparat d iêbiben wid id ilhan, di ddin nnwen, s wurar d utihi, seg wid iwumi teppunefk Tezmamt, uqbel nnwen, akw seg ijehliyen. Ëezzbet i Öebbi, ma d lmumnin ay tellam.');
INSERT INTO "ramdane" VALUES(5,58,'5:58','Ma teddnem i téallit, fellas ad mmaâlken, ad uraren. Ayagi, acku nitni d agdud ur nefhim.');
INSERT INTO "ramdane" VALUES(5,59,'5:59','Ini: "a yAt Tezmamt! Acu i$ef la $ ttehmem, siwa imi numen s Öebbi, akked wayen id Issers $uône$, akked wayen id Issers uqbel? Tegwti degwen d iballa$en".');
INSERT INTO "ramdane" VALUES(5,60,'5:60','Ini: "ad awen mle$ aqeîîi n Öebbi icemten sennig wagi? Wid Ixza Öebbi, sserfan T, Irra ten d iddawen, d ilfawen". Âabden iôebbiten n tkerkas. Widak, atnan di liêala tucmip mavi, di ttelfan u$miq seg ubrid uûwib.');
INSERT INTO "ramdane" VALUES(5,61,'5:61','Mi d usan $uôwen, qqaôen: "numen". Ni$ kecmen d s tijehli, u nitni ff$en yis. Öebbi Yif akw akken Issen, ayen teffren.');
INSERT INTO "ramdane" VALUES(5,62,'5:62','La tepwaliv aîas, degsen, p$awalen ar ubekkav akked wanfal, teppen leêôam. I icmet wayen xeddmen!');
INSERT INTO "ramdane" VALUES(5,63,'5:63','A lemmer i sen kksen iôabiyen akked lecyax, awal ubekkav akked wuççi n leêôam? I icmet wayen la ûenâen!');
INSERT INTO "ramdane" VALUES(5,64,'5:64','Qqaôen wudayen: "iskkweô ufus n Öebbi". D ifassen nnsen i isekkwôen. Pwaxzan ilmend n wayen d qqaôen. Xaîi! Ifassen iS llin, Ip$iti akken i S ihwa. Ayen i k d irsen, s$uô Mass ik, war ccekk, irennu i waîas segsen, leb$wev akked tijehli. Nessker garasen cceêna, akked dd$el, ar ass n tnekra. Yal tikelt ara caâlen times i îôad, a p Issexsi Öebbi. Pnadin asefsed di tmurt. Öebbi ur Iêemmel ara ifessaden.');
INSERT INTO "ramdane" VALUES(5,65,'5:65','At Tezmamt, lemmer day umnen u êezzben, yili, war ccekk, Nekkes asen tucmitin nnsen, u Nesskecm iten ar leonanat isaâdiyen.');
INSERT INTO "ramdane" VALUES(5,66,'5:66','Lemmer day îîifen di Tuôa akked Linoil, akked wayen i sen d irsen s$uô Mass nnsen, war ccekk, ad ççen sennig sen, akw seddaw ivaôen nnsen. Illa garasen lois iôeznen. Aîas, degsen, dirit wayen xeddmen.');
INSERT INTO "ramdane" VALUES(5,67,'5:67','A yAmazan, mel ayen i k d irsen, s$uô Mass ik. Ma ur t temliv ara, ur tessawvev ara tabrap iS. Öebbi a k Ifukk si l$aci. Öebbi, s tidep, ur Inehhu agdud ijehliyen.');
INSERT INTO "ramdane" VALUES(5,68,'5:68','Ini: "a yAt Tezmamt! Ur tellim deg wacemma, alamma teddam d Tuôa, Linoil, akked wayen i wen d irsen, s$uô Mass nnwen". Ayen i k d irsen s$uô Mass ik, war ccekk, irennu, i waîas segsen, leb$wev akked tijehli. Ur $webbel $ef ugdud ijehliyen.');
INSERT INTO "ramdane" VALUES(5,69,'5:69','Ih, wid iumnen, udayen, iûabiyen, imasiêiyen, wid iumnen s Öebbi akked wass aneggaru, u xeddmen ayen ilhan, ur ppagwaden, ur $weblen.');
INSERT INTO "ramdane" VALUES(5,70,'5:70','Ni$ Nga laâqed i At Isôail, Nuzen asen imazanen. Yal tikelt ara ten id ias umazan s wayen ur asen nehwi, wa a t skiddben, wa a t n$en.');
INSERT INTO "ramdane" VALUES(5,71,'5:71','£ilen ur ippili uhewwel. Dre$len, âaégen. Sinna, Issuref asen Öebbi. Sinna, aîas degsen dre$len, âaégen. Öebbi Ipwali acu xeddmen.');
INSERT INTO "ramdane" VALUES(5,72,'5:72','Ni$ jehlen wid iqqaôen: "Öebbi, s tidep, d lmasiê, mmi s n Meryem". U inna lmasiê: "a yAt Isôail! Âabdet Öebbi, Mass iw, d Mass nnwen". Ih, win icerken i Öebbi, Igdel as Öebbi Loennet. Axxam is d times. Ur llin isellaken i imednas.');
INSERT INTO "ramdane" VALUES(5,73,'5:73','Ni$ jehlen wid iqqaôen: "Ih, Öebbi d wis krav n krav". Ur illi ôebbi siwa Öebbi Yiwen. Ma ur xvin ara i wayen qqaôen, war ccekk, aâaqeb aqeôêan ad iêaz wid ijehlen segsen.');
INSERT INTO "ramdane" VALUES(5,74,'5:74','Day ur tuben ara ar Öebbi, u ssuturen aS ssmaê? Öebbi Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(5,75,'5:75','Lmasiê, mmi s n Meryem, d amazan kan. Ni$ âaddan, uqbel is, imazanen. Imma s tessawal tidep. Llan teppen lqut. Wali amek i asen Nepbeggin issekniyen; sinna, wali amek la ppagwin.');
INSERT INTO "ramdane" VALUES(5,76,'5:76','Ini: "day a ppaâbdem, war Öebbi, ayen ur nezmir a kwen ivuôô, ne$ a kwen infaâ"? Öebbi, Neppa, Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(5,77,'5:77','Ini: "a yAt Tezmamt! Ur rennut, di ddin nnwen, ayen ur nelli d tidep. Ur teddut d lemcihwat n ugdud innetlafen zik, i itelfen aîas, u telfen seg ubrid uûwib.');
INSERT INTO "ramdane" VALUES(5,78,'5:78','Pwaxzan wid ijehlen, seg wAt Isôail, s$uô iles n Dawed akked Âisa, mmi s n Meryem; ayagi, $ef wayen âuûan, u zgaren tilas.');
INSERT INTO "ramdane" VALUES(5,79,'5:79','Ur ppemyekkasen ayen xedmen n dir. I icmet wayen xeddmen!');
INSERT INTO "ramdane" VALUES(5,80,'5:80','A pwaliv aîas segsen, ad xalven wid ijehlen. War ccekk, icmet wayen xedmen i iman nnsen. Ad Izâaf Öebbi fellasen. Nitni ad wekkôen deg uâaqeb.');
INSERT INTO "ramdane" VALUES(5,81,'5:81','Lemmer uminen s Öebbi, Nnbi, akked wayen i sen d irsen, ur ten pparan d iêbiben. Maca aîas, segsen, d iballa$en.');
INSERT INTO "ramdane" VALUES(5,82,'5:82','A ppafev icenga iqeûêanen mavi, n wid iumnen, d udayen akked wid ipcaraken. A ppafev wid iqeôben mavi, s lmaênana, ar wid iumnen, d wid innan: "nekwni d imasiêiyen". Ayagi, acku llan degsen ixelwiyen, iôehbaniyen, u nitni ur sme$wôen ara iman nnsen.');
INSERT INTO "ramdane" VALUES(5,83,'5:83','Ma slan i wayen id iusan ar Umazan, a pwaliv allen nnsen ççuôent d imeîîi, seg wayen ufan n tidep. Qqaôen: "a Mass nne$! Numen, Aru ya$ seg inagan.');
INSERT INTO "ramdane" VALUES(5,84,'5:84','Acu i $ iu$en ur numin s Öebbi akked wayen i $ d iusan n tidep; ur nessarem a $ Issekcem, Mass nne$, s agdud iâaôviyen"?');
INSERT INTO "ramdane" VALUES(5,85,'5:85','A ten Ioazi Öebbi, $ef wayen d qqaôen, s leonanat, ddawat sen isaffen ppazzalen, degsen ad wekkôen. Wagi d aoazi n lâuûam.');
INSERT INTO "ramdane" VALUES(5,86,'5:86','Wid ijehlen, u skaddben issekniyen nne£, widak, d at Loahennama.');
INSERT INTO "ramdane" VALUES(5,87,'5:87','A wid iumnen! Ur pgeddilet ayen imaânen i wen Iêell Öebbi. Ur zegret tilas. Öebbi, s tidep, ur Iêemmel ara wid izegren tilas.');
INSERT INTO "ramdane" VALUES(5,88,'5:88','Eççet seg wayen ikwen Iôéeq Öebbi, iêellen, imaânen. Ëezzbet i Öebbi. YiS, kunwi, ay tumnem.');
INSERT INTO "ramdane" VALUES(5,89,'5:89','Ur kwen Ipqassa Öebbi $ef tgallit n tkerkas. Maca, a kwen Iqiss $ef tgallit n ûûeê. Takweffaôt is: a ppecceççem mraw ime$ban, akken tecceççam tawacult nnwen, ne$ a ten tesselsem, ne$ a pserêem i wakli. Win ur nwâa ara, ad iuéum krav wussan. D tagi ay d takweffaôt n tgallit nnwen, ma teggullem. Eîîfet di tgallit nnwen. Akka i wen d Ipbeggin Öebbi issekniyen, amar a ppesnemmrem.');
INSERT INTO "ramdane" VALUES(5,90,'5:90','A wid iumnen! Ccôab, leqwmeô, tiâancuôin, taseqqaôt, d ayen kan icemten, seg igi n Cciîan. Ihi, veôôeft asen, amar a prebêem.');
INSERT INTO "ramdane" VALUES(5,91,'5:91','Ib$a kan Cciîan ad isker ticenga akked dd$el garawen, s ccôab akked leqwmeô, a kwen iveôôef seg usmekti n Öebbi, akked téallit. Day ur tepfukkum ara?');
INSERT INTO "ramdane" VALUES(5,92,'5:92','A$et awal i Öebbi, a$et awal i Umazan. £uôwat! Ma twexxôem, cfut ar Amazan, iwata yas kan anabi ipbegginen.');
INSERT INTO "ramdane" VALUES(5,93,'5:93','Ur telli tiyta deg wid iumnen, u xeddmen ayen ilhan, $ef wayen ççan, ma êezzben, umnen, xedmen ayen ilhan, sinna êezzben, umnen, sinna, êezzben u maânen. Öebbi Iêemmel lâuûam.');
INSERT INTO "ramdane" VALUES(5,94,'5:94','A wid iumnen! War ccekk, a kwen Iweddeb Öebbi $ef wayen tepûeggidem s ifassen nnwen, u s ixtucen nnwen, iwakken ad Iéeô Öebbi anwa i T ippagwaden, di tbavnit. Win izegren tilas, deffir waya, fellas aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(5,95,'5:95','A wid iumnen! Ur pûeggidet di zzyaôa nnwen. Win iûeggden segwen, u iâammed, aqeîîi: ad iseddeq, si lmal is, ayen in$a s ûûyada s, a d êudden, f waya, sin irgazen iêesbiyen segwen. D tikci ara iawven ar Lkaâba. Ne$ takweffaôt: ad icceçç ime$ban, ne$, ayen iswan aya, ad iuéum. Ad ijeôôeb vveôô n lameô is. Öebbi Isemmeê i wayen iâaddan. Win iu$alen a d Irr Öebbi segs ppaô. Öebbi d Uzmir, Ippara d ppaô.');
INSERT INTO "ramdane" VALUES(5,96,'5:96','Têell awen ûûyada n yill akked uççi s. D lqut i kunwi akw iminigen. Tepwagdel awen ûûyada di tmurt, skud aql akwen d zziyyaô. Ëezzbet i Öebbi, $uôeS ara tepwafesrem.');
INSERT INTO "ramdane" VALUES(5,97,'5:97','Irra Öebbi Lkaâba d axxam iôqan ipwabnan i l$aci, aggur iôqan, asfel, isenoaq, ayagi iwakken a ppeéôem ar Öebbi Iéôa i illan deg igenwan u di tmurt, u Öebbi Issen yal cci.');
INSERT INTO "ramdane" VALUES(5,98,'5:98','Cfut iwâaô uâaqeb n Öebbi. Öebbi, s tidep, Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(5,99,'5:99','Amazan, iwata yas kan unabi. Öebbi Iéôa ayen temmalem u ayen teppeffrem.');
INSERT INTO "ramdane" VALUES(5,100,'5:100','Ini: "ur iaâdil ara wayen n dir akked wayen imaânen", xas teswehm ik lkwetôa n wayen n dir. Ëezzbet i Öebbi, a yuâqilen, amar a prebêem.');
INSERT INTO "ramdane" VALUES(5,101,'5:101','A wid iumnen! Ur steqsayet $ef t$awsiwin ara kwen icwe$ben, ma mlan awen tent. Ma testeqsam fellasent mara d iprusu Leqwôan, ad awen ppufessrent. Öebbi Isemmeê aya. Öebbi Ipsemmiê, Iêellem.');
INSERT INTO "ramdane" VALUES(5,102,'5:102','Ni$ isteqsa fellasent ugdud, uqbel nnwen. Sinna, segsent, gwran d d ijehliyen.');
INSERT INTO "ramdane" VALUES(5,103,'5:103','Ur d Ifki Öebbi Baêiôa, ne$ Sayba, ne$ Waûila, ne$ Ëami. Maca, wid ijehlen gren d tikerkas $ef Öebbi. Tegwti degsen ur fhimen.');
INSERT INTO "ramdane" VALUES(5,104,'5:104','Ma nnan asen: "yyaw ar wayen id Issers Öebbi, u ar Umazan". Qqaôen: "beôka ya$ ayen id nufa, $ef waya, $eô imezwura nne$". I ma imezwura nnsen ur ssinen acemma, ur pwanhan?');
INSERT INTO "ramdane" VALUES(5,105,'5:105','A wid iumnen! Fellawen iman nnwen. Ur kwen iîîuôôu win innetlafen, ma tepwanham. Ar Öebbi, tu$alin nnwen akw. A kwen Ixebbeô $ef wayen txeddmem.');
INSERT INTO "ramdane" VALUES(5,106,'5:106','A wid iumnen! Ccada garawen, ma tusa d tmeppant ar yiwen segwen. Di lawan uweûûi, sin irgazen iêesbiyen, segwen, ne$ wiyav war segwen, ma tellam d iminigen di tmurt, u tusa kwen id twa$it n tmeppant. A ten qesden deffir téallit. Ma tcukkem, sgallet pen s Öebbi: "ur d nesfayday syis, xas iaâna llzem nne$, ur nteffer ccada n Öebbi, mulac, nekwni a nili seg imednab".');
INSERT INTO "ramdane" VALUES(5,107,'5:107','Ma ivehô ed fellasen denben, ad ppuneûfen s wiyav, si loiha n wid vuôôen imezwura. Ad ggalen s Öebbi: "ih, ccada nne$ tif ccada nnsen. Ur nzegger tilas, mulac, war ccekk, a nili seg imednas".');
INSERT INTO "ramdane" VALUES(5,108,'5:108','Ppif akka iwakken a d awin ccada iwulmen, ad agwaden a ppili tgallit deffir tgallit nnsen. Ëezzbet i Öebbi, ssemêesset, Öebbi ur Inehhu agdud iballa$en.');
INSERT INTO "ramdane" VALUES(5,109,'5:109','Ass ideg ara Issemlil Öebbi imazanen, ad Yini: "acu i wen d jawben"? Ad inin: "ur telli tussna $uône$. D Keçç, s tidep, i Issnen tibavniyin".');
INSERT INTO "ramdane" VALUES(5,110,'5:110','Mi Inna Öebbi: "a Âisa, mmi s n Meryem! Mmekti d i kwen £$ate$, keçç akked imma k, mi k Âiwne$ s ôôuê iôqan. Tepmmeslayev i medden si dduê, am unqaqôi. Mi k Slemde$ Tazmamt, ûûwab, Tuôa, Linoil. Mi tûenâav, si talaxt, ûûifa n ufrux, s– tsriê iW; tûuvev degs, tu$al d afôux, s– tsriê iW. Tesseêlayev agugam, amuvin n lberû, s– tsriê iW. Mi tessenkarev lmeggtin, s– tsriê iW. Mi Sbaâde$ fellak At Isôail, mi d tusiv $uôsen, s ubeggen, u nnan wid ijehlen segsen: "ayagi iban d ssêur kan".');
INSERT INTO "ramdane" VALUES(5,111,'5:111','Mi Weêêa$ i inmahalen: "amnet yissI, akw s umazan iW". Nnan: "numen. Echhed ar nekwni d imvuâen".');
INSERT INTO "ramdane" VALUES(5,112,'5:112','Mi nnan inmahalen: "a Âisa, mmi s n Meryem! Ma Izmer Mass ik ad a$ d Issers lmaida seg igenni"? Inna: "êezzbet i Öebbi, ma d lmumnin ay tellam".');
INSERT INTO "ramdane" VALUES(5,113,'5:113','Nnan: "neb$a a neçç segs, a nevmen ulawen nne$, a néeô ar tenniv a$ d tidep, a nili d inagan iS".');
INSERT INTO "ramdane" VALUES(5,114,'5:114','Inna Âisa, mmi s n Meryem: "a Öebbi, Mass nne$, Ssers a$ d lmaida, seg igenni, ad a$ tili d lâid i umezwaru u i uneggaru nne$, u d assekni s $uôeK. £it a$ d. Keçç, Tifev akw im$iten".');
INSERT INTO "ramdane" VALUES(5,115,'5:115','Inna Öebbi: "ad awen p id Sserse$. Win ijehlen segwen, deffir waya, a t Âaqbe$ aâaqeb ur Sâadda$ i yiwen deg imavalen".');
INSERT INTO "ramdane" VALUES(5,116,'5:116','Mi Inna Öebbi: "a Âisa, mmi s n Meryem! D keçç i innan i l$aci: erret a$, nek akked imma, d sin iôebbiten war Öebbi"? Inna: "gedha s yiseK! Ur iyi ippunefk ara a d ini$ ayen i$ef ur sâi$ lêeqq. Lemmer i t id nni$, yili Teéôiv t. Tesnev i illan deg iman iw, ur ssine$ ayen illan deg iman iK. Keçç, s tidep, d Amusnaw n tbavniyin.');
INSERT INTO "ramdane" VALUES(5,117,'5:117','I asen nni$, siwa ayen iyi d Tumôev: âabdet Öebbi, Mass iw, u Mass nnwen. Lli$ d anagi nnsen, skud lli$ garasen. Mi iyi n Tessuliv $uôeK, d Keçç i ten id Iquôâan. Keçç, d Anagi n yal cci.');
INSERT INTO "ramdane" VALUES(5,118,'5:118','Ma Tâaqbev ten, ni$ d imdanen iK, ma Tsemmêev asen, Keçç, s tidep, d Uzmir, d Bab n ûûwab".');
INSERT INTO "ramdane" VALUES(5,119,'5:119','Inna Öebbi: "wagi d ass ideg ara stenfâan imawlan n tidep, si tidep nnsen. I nitni leonanat, ddawat sen isaffen ppazzalen, ad wekkôen degsen, i lebda". Iôuva ten Öebbi, u ôuvan T, d wagi ay d rrbeê ameqwôaêan.');
INSERT INTO "ramdane" VALUES(5,120,'5:120','N Öebbi tagelda n tmurt akked igenwan, akked wayen illan degsen. Neppa Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(6,1,'6:1','A neckeô Öebbi i Ixelqen tamurt akked igenwan. Irra tillas, tafat. Sinna, wid ijehlen pparan tanudda i Mass nnsen.');
INSERT INTO "ramdane" VALUES(6,2,'6:2','Neppa, ikwen Ixelqen s talaxt, sinna, Iseyya lajel. £uôeS lajel ipwassnen. Deffir waya, kunwi la tepcukkum.');
INSERT INTO "ramdane" VALUES(6,3,'6:3','Neppa, d Öebbi deg igenwan, di tmurt. Issen sser nnwen, d wayen d teqqaôem. Iéôa ayen tkesbem.');
INSERT INTO "ramdane" VALUES(6,4,'6:4','Ur ten id ippas ussekni seg issekniyen n Mass nnsen, mebla ma ugin t.');
INSERT INTO "ramdane" VALUES(6,5,'6:5','Ni$ skaddben tidep, mi ten id tusa. A ten id ias usali n wayen i$ef ttihin.');
INSERT INTO "ramdane" VALUES(6,6,'6:6','Day ur walan ara acêal isuta, uqbel nnsen, ay Nekkes. Nesserked iten di tmurt akken ur kwen Nesserked. Nuzen asen aîas ugeffur, Nerra yasen isaffen, ppazzalen seddawat sen. Nesnegr iten ilmend ibekkaven nnsen. Nxelq ed, deffir sen, sself nniven.');
INSERT INTO "ramdane" VALUES(6,7,'6:7','Xas ad ak ed Nessers Tazmamt, di lka$ev, ara nnalen s ifassen nnsen, war ccekk, a d inin wid ijehlen: "wagi, s tidep, siwa ssêur n ûûeê".');
INSERT INTO "ramdane" VALUES(6,8,'6:8','Qqaôen: "a lemmer id irs $uôes lmelk". Lemmer a n Nessers lmelk, war ccekk, ad iûaô lameô, sinna, ur pganin.');
INSERT INTO "ramdane" VALUES(6,9,'6:9','Lemmer i t Nerri d lmelk, yili Nerra t d argaz, ad as Nessels ayen plusun.');
INSERT INTO "ramdane" VALUES(6,10,'6:10','Ni$ mmaâlken $ef imazanen, uqbel ik. Ayen i$ef ttihin, izzi i wid, segsen, immaâlken.');
INSERT INTO "ramdane" VALUES(6,11,'6:11','Ini: "ddut di tmurt, sinna walit amek tevôa d wid iskiddiben".');
INSERT INTO "ramdane" VALUES(6,12,'6:12','Ini: "w i t ilan wayen akw illan di tmurt akked igenwan"? Ini: "d Öebbi". Iura, deg iman iS, aêunu. A kwen Issemlil i wass n tnekra, ur ccekk fellas. Wid isxesôen iman nnsen, ur ppamnen.');
INSERT INTO "ramdane" VALUES(6,13,'6:13','IneS i ized$en deg iv, deg wass. Neppa Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(6,14,'6:14','Ini: "day ara sâu$ bab, war Öebbi, Axellaq n tmurt akked igenwan? Neppa Icceççay ur t cceççayen". Ini: "nek pwamôe$ d ad ili$ d amenzu ara ianzen. Ur ppilit seg imcurak".');
INSERT INTO "ramdane" VALUES(6,15,'6:15','Ini: "nek ugade$, ma aâûa$ Mass iw, aâaqeb n wass ameqwôaêan".');
INSERT INTO "ramdane" VALUES(6,16,'6:16','Win imenâan segs, assen, ni$ Iêun fellas. Wagi d rrebê n ûûeê.');
INSERT INTO "ramdane" VALUES(6,17,'6:17','Ma Isâadda Öebbi fellak cedda, ur p itekkes yiwen, siwa Neppa. Ma Isrebê ik, Neppa Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(6,18,'6:18','D Neppa ay d Aqehhaô, sennig imdanen iS; d Neppa ay d Bab n ûûwab, i Iéôan.');
INSERT INTO "ramdane" VALUES(6,19,'6:19','Ini: "anda p ccada sennig akw tiyav"? Ini: "Öebbi d Anagi gari yidwen. Iweêêa yi d Leqwôan agi, iwakken a kwen nabi$ yis, kunwi akked wid u$uô ibbwev. Day, kunwi, war ccekk, a pcehhdem ar, ar tama n Öebbi, llan iôebbiten nniven"? Ini: "ur cehhde$"! Ini: "Neppa d Öebbi, siwa Yiwen. Nek, rri$ akin ayen tepcarakem".');
INSERT INTO "ramdane" VALUES(6,20,'6:20','Wid iwumi Nefka Tazmamt, ssnen p, akken ssnen arraw nnsen. Wid isxesôen iman nnsen, ihi, ur ppamnen.');
INSERT INTO "ramdane" VALUES(6,21,'6:21','Anwa idensen sennig win d igren tikerkas $ef Öebbi, ne$ iskaddben issekniyen iS? Ih, ur rebêen imednas.');
INSERT INTO "ramdane" VALUES(6,22,'6:22','Ass ideg ara ten Nefser meôôa, sinna a Nini i wid icurken: "anda ten icriken nnwen, i la tâabdem"?');
INSERT INTO "ramdane" VALUES(6,23,'6:23','Deg uhewwel nnsen, siwa ma nnan d: "s Öebbi, Mass nne$, ur nelli d imcurak"!');
INSERT INTO "ramdane" VALUES(6,24,'6:24','Wali amek skaddben $ef yiman nnsen. Itelf iten wayen la d snulfuyen.');
INSERT INTO "ramdane" VALUES(6,25,'6:25','Garasen wid i k issemêasen. Nerra aêurrim $ef ulawen nnsen, iwakken ur t fehmen. Tiâééegt deg imeééu$en nnsen. Ma walan yal assekni, ur ppamnen yis. Alamma, ma usan d $uôek, a k namren, ad ak inin wid ijehlen: ayagi siwa tiêkayin imezwura.');
INSERT INTO "ramdane" VALUES(6,26,'6:26','Sîixxiôen segs, îîeôôifen segs. Ay squccun siwa iman nnsen, ur faqen.');
INSERT INTO "ramdane" VALUES(6,27,'6:27','Lemmer a ten twaliv, mara bedden ar tmes, a d inin: a ssexv nne$! Awi $ irran, ur neskiddib issekniyen n Mass nne$! A nili si lmumnin.');
INSERT INTO "ramdane" VALUES(6,28,'6:28','Xaîi! Iban asen d wayen teffren, zik. Lemmer a d kren, ad u$alen ar wayen i sen ipwagedlen. Nitni, s tidep, d ikeddaben.');
INSERT INTO "ramdane" VALUES(6,29,'6:29','U qqaôen: "siwa tameddurt n ddunit i illan. Nekwni, ur fella$ tanekra".');
INSERT INTO "ramdane" VALUES(6,30,'6:30','Lemmer a pwaliv mara bedden ar Mass nnsen, ad Yini: "ni$ ayagi s tidep"? A d inin: "ih, s Mass nne$"! Ad Yini: "ihi jeôôbet aâaqeb, ilmend n wayen tejhilem".');
INSERT INTO "ramdane" VALUES(6,31,'6:31','Ni$ xesôen wid iskaddben timlilit akked öÖebbi. Alamma, ma iusa ten id yimir, deqzalla, a d inin: "a yamxix nne$, $ef wayen ideg nestehza"! Nitni ad bibben tiâakkwumin nnsen $ef izugar nnsen. I icmet wayen bubben!');
INSERT INTO "ramdane" VALUES(6,32,'6:32','Tameddurt n ddunit d urar kan akked lhawiyya. Axxam aneggaru, war ccekk, yif ip i wid ipêezziben. Day ur tepmeyyizem ara?');
INSERT INTO "ramdane" VALUES(6,33,'6:33','Ni$ Neéôa i$webben ik, s tidep, wayen qqaôen. Ihi, nitni ur k skiddiben. Maca imednas veggôen issekniyen n Öebbi.');
INSERT INTO "ramdane" VALUES(6,34,'6:34','Ni$ skaddben imazanen uqbel ik. Üebôen $ef wayen i ten skaddben, akked wayen i ten meêênen, armi i ten id iusa usellek nne£. Ur illi ubeddel i wawalen n Öebbi. Ni$ usan k id, kra isallen imazanen.');
INSERT INTO "ramdane" VALUES(6,35,'6:35','Ma ééay fellak uwexxeô nnsen, ma tzemôev a tessarmev aderbuz di tmurt, ne$ sellum s igenni, ad asen d tawiv assekni. Ma ihwa yaS i Öebbi, a ten Issemlil ar nnhaya. Ur ppili seg wid ur nessin.');
INSERT INTO "ramdane" VALUES(6,36,'6:36','Siwa wid isellen, ara d ijawben. Lmegtin a ten id Issker Öebbi, sinna, $uôeS tu$alin nnsen.');
INSERT INTO "ramdane" VALUES(6,37,'6:37','Qqaôen: "acimi ur as d irs ara ussekni s$uô Mass is"? Ini: "Öebbi, s tidep, Izmer a d Issers assekni", maca tegwti degsen ur éôin.');
INSERT INTO "ramdane" VALUES(6,38,'6:38','Ur illi lweêc di tmurt, ne$ ufrux ippafgen s wafriwen is, ur nelli d leoyus am kunwi. Ur Nestehza, di Tezmamt, $ef wacemma. Sinna, ar Mass nnsen, ad pwafessren.');
INSERT INTO "ramdane" VALUES(6,39,'6:39','Wid iskaddben issekniyen nne£, âaégen, gugmen, di tillas. Win i S ihwan i Öebbi, a t Itlef. Win i S ihwan, a t Iawi d ubrid uwqim.');
INSERT INTO "ramdane" VALUES(6,40,'6:40','Ini: "twalam ma iusa kwen id uâaqeb n Öebbi, ne$ ma iusa kwen id yimir, day ar war Öebbi ara tnaâôem, ma tessawalem tidep?');
INSERT INTO "ramdane" VALUES(6,41,'6:41','Xaîi! £uôeS ara tnujem. Ad Issiferr ayen i$ef tnujem $uôeS, ma ihwa yaS, a ppeppum ayen tcurkem".');
INSERT INTO "ramdane" VALUES(6,42,'6:42','Ni$ Nuzen ed ar leoyus, uqbel ik. Nesâadda fellasen tilufa, ccqa, amar ad êiwten.');
INSERT INTO "ramdane" VALUES(6,43,'6:43','A lemmer êawten, mi ten id tusa tiyta nne£. Maca, qehôen wulawen nnsen. Cciîan Icebbeê asen ayen xeddmen.');
INSERT INTO "ramdane" VALUES(6,44,'6:44','Mi ppun ayen i sen d smmektin, Nelli yasen tibbura n yal cci, armi, mi feôêen s wayen i sen ippunefken, Newwet iten, deqzalla. D$a qevâen layas.');
INSERT INTO "ramdane" VALUES(6,45,'6:45','Pwakksen wid id igwran seg ugdud idensen. Ccekôan i Öebbi, Mass imavalen.');
INSERT INTO "ramdane" VALUES(6,46,'6:46','Ini: "twalam, ma Iddem awen Öebbi timesliwt nnwen, akked iéôi nnwen; ma Isle$ ulawen nnwen; anwa ôebbi, war Öebbi, ar awen ten id iawin"? Wali amek Nessefham issekniyen, u nitni, sinna, zzin akin.');
INSERT INTO "ramdane" VALUES(6,47,'6:47','Ini: "acu twalam, ma iusa kwen id uâaqeb n Öebbi, deqzalla, ne$ âinani? Day ara Ine$, siwa agdud imednas"?');
INSERT INTO "ramdane" VALUES(6,48,'6:48','Ur d Nuzen imazanen, siwa i ubecceô, d unabi. Wid iumnen, ixeddmen ayen ilhan, ur ppagwaden, ur $weblen.');
INSERT INTO "ramdane" VALUES(6,49,'6:49','Wid iskaddben issekniyen nne£, a ten iêaz uâaqeb, ilmend n wayen be$wven.');
INSERT INTO "ramdane" VALUES(6,50,'6:50','Ini: "ur awen qqare$ sâi$ igerrujen n Öebbi, ur ssine$ tabavnit; ur awen qqare$: nek, d lmelk. Nek, teddu$ kan akked wayen iyi d ippuweêêan". Ini: "day iaâdel uder$al d win ipwalin? Day ur d tepmeyyizem ara"?');
INSERT INTO "ramdane" VALUES(6,51,'6:51','Nabi yis wid iugaden ad pwafessren ar Mass nnsen. Ur sâin bab, war Neppa, ne$ amcafaâ. Amar ad êezzben.');
INSERT INTO "ramdane" VALUES(6,52,'6:52','Ur ppara wid ipnujun $uô Mass nnsen, taûebêit, tameddit, pnadin udem iS. Ur fellak a ten têasbev deg wacemma, ur fellasen a k êasben, deg wacemma. Ma terriv ten, ihi, a ppiliv seg imednas.');
INSERT INTO "ramdane" VALUES(6,53,'6:53','Akka ay Nhewwel kra s wiyav, iwakken a d inin: "day d wigi i I$at Öebbi, garane$"? Day ur Issin ara Öebbi, sennig akw, wid isnemmiren?');
INSERT INTO "ramdane" VALUES(6,54,'6:54','Ma usan k id wid iumnen s issekniyen nne£, ini: "tifrat fellawen"! Mass nnwen Iura, i iman iS, aêunu. Ihi, i win, segwen, ixedmen tucmip, ur iâammed, sin akin, isteôoaâ, u ixdem ayen ilhan, Neppa Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(6,55,'6:55','Akka ay Nfeûûel issekniyen, iwakken a d iban ubrid ibe$wviyen.');
INSERT INTO "ramdane" VALUES(6,56,'6:56','Ini: "nek, ippugdel iyi ad âabde$ wid u$uô la tepnujum, war Öebbi". Ini: "ur teddu$ d lemcihwat nnwen, mulac ad nnetlafe$, ur ppili$ seg wid ipwanhan".');
INSERT INTO "ramdane" VALUES(6,57,'6:57','Ini: "nek, îîfe$ deg ubeggen n Mass iw. Teskaddbem t. Ur illi $uôi wayen u$uô têarem. Ih, aêuddu $eô Öebbi kan. Issawal tidep. Neppa Yif akw wid issenqaden".');
INSERT INTO "ramdane" VALUES(6,58,'6:58','Ini: "lemmer illi $uôi wayen u$uô têarem, yili iûaô lameô, gari yidwen". Öebbi Yif akw akken Issen imednas.');
INSERT INTO "ramdane" VALUES(6,59,'6:59','£uôeS tisura n tbavnit. Siwa Neppa itent Issnen. Iéôa i illan di lberr, di yill. Ur i$elli ifer siwa ma Iéôa t. Akken aâqqa di tillas n tmurt, tizzegzewt, ta$eôt, akw di tezmamt ipbegginen.');
INSERT INTO "ramdane" VALUES(6,60,'6:60','D Neppa i wen ippaddamen leôwaê deg iv. Iéôa ay txeddmem, deg wass. Sinna, Ippara kwen id degs, iwakken ad ivôu lajel ipwassnen. Sinna, $uôeS tu$alin nnwen, sinna, a kwen Ixebbeô $ef wayen txeddmem.');
INSERT INTO "ramdane" VALUES(6,61,'6:61','D Neppa ay d Aqehhaô sennig imdanen iS. Ippazen ed $uôwen imêaraben, alamma, ma tusa d tmeppant ar yiwen segwen, ad ddmen ôôuê is, imceggâen nne£. Nitni ur stehzayen.');
INSERT INTO "ramdane" VALUES(6,62,'6:62','Sinna, ad u$alen ar Öebbi, Bab nnsen n tidep. Aêuddu, day maççi d ayla S? Neppa Yif akw akken Ip$awal ar uqeîîi.');
INSERT INTO "ramdane" VALUES(6,63,'6:63','Ini: "anwa ara kwen Isellken si tillas n lberr d yill, u$uô tnujem s uêiwet akked tugwdi"? "Ma Isellek a$ si tagi, war ccekk, a nili seg wid isnemmiren".');
INSERT INTO "ramdane" VALUES(6,64,'6:64','Ini: "Öebbi Isellek ikwen segs, akw si yal lhemm, sinna, kunwi la tepcarakem".');
INSERT INTO "ramdane" VALUES(6,65,'6:65','Ini: "Neppa, Izmer ad awen d Iazen aâaqeb sennig wen, ne$ seddaw ivaôen nnwen. Ne$ a kwen Issemfaôaq, kra segwen ad jeôôben leqheô n wiyav". Wali amek id Nemmal issekniyen, amar ad meêûen.');
INSERT INTO "ramdane" VALUES(6,66,'6:66','Iskaddeb it ugdud ik, u neppa d tidep. Ini: "ur awen lli$ d awkil.');
INSERT INTO "ramdane" VALUES(6,67,'6:67','Yal asali s lweqt is. A ppu$alem a ppeéôem".');
INSERT INTO "ramdane" VALUES(6,68,'6:68','Ma twalav wid ipnamaren $ef issekniyen nne£, eoo iten, alamma bedlen anamer. Ma iseppu k Cciîan, mi d temmektiv, ur p$imi d ugdud imednas.');
INSERT INTO "ramdane" VALUES(6,69,'6:69','Ur isefk ara i wid ipêezziben, a ten êasben deg wacemma. Maca d asmekti, amar ad êezzben.');
INSERT INTO "ramdane" VALUES(6,70,'6:70','Eoo wid irran ddin nnsen d urar, d aqesseô. T$uôô iten tmeddurt n ddunit. Mmekti d yis, ard a ipwaîîef umdan $ef wayen ikseb. Ur isâi, war Öebbi, bab ne$ amcafaâ. Yal le$ôama ur tepwaqbal segs. Widak d wid ipwaîfen $ef wayen kesben. I nitni tissit iô$an, aâaqeb aqeôêan, ilmend n wayen la jehlen.');
INSERT INTO "ramdane" VALUES(6,71,'6:71','Ini: "day a nâaô, war Öebbi, ayen ur a$ neneffaâ, ur a$ neîîuôôu? U a nu$al a nwexxeô, deffir nnhaya n Öebbi, am win $ewwan ccwaîen di tmurt, u ippuêeyyeô". Pnecciden t imeddukwal ar nnhaya: "yya $uône$"! Ini: "d nnhaya n Öebbi, s tidep, ay d nnhaya. Nepwameô a nanez i Mass imavalen".');
INSERT INTO "ramdane" VALUES(6,72,'6:72','èéalet, êezzbet aS. £uôeS, Neppa, ara tepwafessrem.');
INSERT INTO "ramdane" VALUES(6,73,'6:73','D Neppa i Ixelqen tamurt akked igenwan, s tidep. Ass ideg ara Yini: "ili"! Ad yili. Awal iS d tidep. IneS tagelda. Ass uûuvu n lbuq, Iéôa i iffren d wayen ivehôen. Neppa d Bab n ûûwab, i Iéôan.');
INSERT INTO "ramdane" VALUES(6,74,'6:74','Mi inna Ibôahim i baba s, Azaô: "day a pperrev tiâancuôin d iôebbiten? Nek wala$ k, keçç akked ugdud ik, di ttelfan n ûûeê".');
INSERT INTO "ramdane" VALUES(6,75,'6:75','Akka, Nessken i Ibôahim, tagelda n tmurt akked igenwan, iwakken ad yili seg wid itebbten.');
INSERT INTO "ramdane" VALUES(6,76,'6:76','Mi t i$umm yiv, iwala itri, inna: "wagi d Mass iw". Mi ixsi, inna: "ur êemle$ ara wid ixessin".');
INSERT INTO "ramdane" VALUES(6,77,'6:77','Mi iwala iuli d waggur n tziri, inna: "wagi d Mass iw". Mi i$ab, inna: "ma ur iyi Inha ara Mass iw, ad ili$, war ccekk, seg ugdud im$eôôqen".');
INSERT INTO "ramdane" VALUES(6,78,'6:78','Mi iwala iîij ineqqeô ed, inna: "wagi d Mass iw, wagi meqqweô kteô". Mi i$li, inna: "a yagdud iw! Nek rri$ akkin ayen tepcarakem.');
INSERT INTO "ramdane" VALUES(6,79,'6:79','Nek zzi$ udem iw ar Win Ixelqen tamurt akked igenwan, male$ ar tidep. Ur lli$, nek, seg imcurak".');
INSERT INTO "ramdane" VALUES(6,80,'6:80','Ixuûem it ugdud is, inna: "day ad iyi txaûmem $ef Öebbi? Ni$ Inha yi; ur ugade$ ayen i S tcurkem, siwa ma illa wayen i S ihwan i Mass iw. Tussna n Mass iw tezdi yal cci, day ur d tesmektayem ara?');
INSERT INTO "ramdane" VALUES(6,81,'6:81','Amek ara ugade$ ayen tepcarakem, ur tugadem mi kunwi tepcarakem, i Öebbi, ayen iwumi ur d Issers adabu fellawen? Anwa, ger sin ikabaran, i iklalen laman, maday tessnem"?');
INSERT INTO "ramdane" VALUES(6,82,'6:82','Wid iumnen, ur sselsen ddens i liman nnsen, widak ad ilin di laman, u nitni pwanhan.');
INSERT INTO "ramdane" VALUES(6,83,'6:83','D wagi ay d awal nne£. Nefka yas t i Ibôahim, mgal agdud is. Nessalay tiseddaôin, win i£ £ ihwan. Ih, Mass ik d Bab n ûûwab, d Amusnaw.');
INSERT INTO "ramdane" VALUES(6,84,'6:84','Nefka yas Isêaq akked Yaâqub. I sin, Nenha ten. Nenha, zik, Nuê akw, ger tarwa s, Dawed, Sliman, Yub, Yusef, Musa, Haôun, - Akka ay Nepkafi iâaôviyen -');
INSERT INTO "ramdane" VALUES(6,85,'6:85','Zakarya, Yeêya, Âisa, Ilyas, - akw seg iâaôviyen -');
INSERT INTO "ramdane" VALUES(6,86,'6:86','Ismaâil, Lyasâa, Yunes, Luî, - akw Nesmenyaf iten $ef imavalen -');
INSERT INTO "ramdane" VALUES(6,87,'6:87','akw seg ibabaten nnsen, si tarwa nnsen, akked watmaten nnsen. Nefren iten, u Nebbwi ten d ubrid uwqim.');
INSERT INTO "ramdane" VALUES(6,88,'6:88','Tagi d nnhaya n Öebbi. Inehhu yis win i S ihwan, deg imdanen iS. Lemmer curken, war ccekk, ad asen imêu wayen xeddmen.');
INSERT INTO "ramdane" VALUES(6,89,'6:89','D wigi iwumi d Nebbwi Tazmamt, ûûwab, akked lambiyya. Ma jehlen widak yis, ihi, Nekwni Nwekkel ip i ugdud ur njehhel yis.');
INSERT INTO "ramdane" VALUES(6,90,'6:90','Wigi d wid Inha Öebbi. Ddu d nnhaya nnsen. Ini: "ur awen suture$, fellas, taqdiât. Neppa d asmekti kan i imavalen".');
INSERT INTO "ramdane" VALUES(6,91,'6:91','Ur qudôen Öebbi azal n lqedô iS, mi qqaôen: "ur d Issers Öebbi acemma ar umdan". Ini: "anwa id Issersen Tazmamt, id ibbwi Musa, d tafat u d nnhaya i medden? Tepparam p di lkwa$ev i tesskanem. Aîas ay teppeffrem. Tlemdem ayen ur tessinem, kunwi akked imezwura nnwen". Ini: "D Öebbi". Sinna, anef asen ad uraren s nnmara nnsen.');
INSERT INTO "ramdane" VALUES(6,92,'6:92','Tagi d Tazmamt, Nessers ip id teôqa, a ptebbet ayen ip id izwaren, a ppendeô tayimmap n temdinin akked wayen i s d izzin. Wid iumnen s laxeôt, umnen yis. Nitni bedden i téilla nnsen.');
INSERT INTO "ramdane" VALUES(6,93,'6:93','Anwa idensen sennig win d igren tikerkas $ef Öebbi? Ne$ inna: "ppuweêêa$ d", ur as d ippuweêêa wacemma? U win innan: "a d sserse$ am wayen id Issers Öebbi"? Lemmer a pwaliv imednas, di maylellif n tmeppant, u lmalayek élent ed ifassen nnsent: "ssuff$et ed tiôwiêin nnwen. Assa, a ppeppuqeîîim s uâaqeb isneêcamen, ilmend n wayen teqqaôem $ef Öebbi, ur d tidep. U tellam tessem$waôem iman nnwen, $ef issekniyen iS".');
INSERT INTO "ramdane" VALUES(6,94,'6:94','Ni$ tusam a£ d, d ifôiden, akken ikwen Nexleq abrid amezwaru. Teooam deffir wen, ayen i wen Nefka. Ur Nwala, $uôwen, imcafâen nnwen, wid i tâabdem, nitni, d icriken nnwen. Ni$ temfaôaqem, u ooan kwen wid la tâabdem.');
INSERT INTO "ramdane" VALUES(6,95,'6:95','Öebbi, s tidep, Ifeôôec aâqqa, i$es. Issuffu$ ed amuddir si lmegget. Issuffu$ ed lmegget seg umuddir. Akka ay d Öebbi. Acimi tugim?');
INSERT INTO "ramdane" VALUES(6,96,'6:96','Issenkar tafrara. Irra iv i ureyyeê, iîij d waggur i leêsab. D wagi ay d aseyyi n Uzmir, Amusnaw.');
INSERT INTO "ramdane" VALUES(6,97,'6:97','D Neppa i wen Irran itran, ad awen mlen abrid, di tillas n lberr akked yill. Ni$ Nemmal issekniyen i ugdud issnen.');
INSERT INTO "ramdane" VALUES(6,98,'6:98','D Neppa ikwen id Ixelqen si yiman afôid, sinna: amkan, tarusi. Ni$ Nemmal issekniyen i ugdud ifehhmen.');
INSERT INTO "ramdane" VALUES(6,99,'6:99','D Neppa id Isrusun aman seg igenni. Nessuffu$ ed yissen yal im$an. Nessuffu$ ed yissen tizzegzewt. Nessuffu$ ed yissen iâaqqayen mbabben, iguza n ppmeô id ipûubbun si tezdayin, leonanat n tfeôôant, uzemmur, n ôemman, ppemcabin ne$ ur ppemcabin. Walit ppemôa nnsen mi d teffe$ tebbwa. Ih, deg waya, war ccekk, issekniyen i ugdud iumnen.');
INSERT INTO "ramdane" VALUES(6,100,'6:100','Rran i Öebbi, leonun d icriken, u Ixelq iten. Snulfan aS d arrac, tullas, ur éôin. Gedha s yiS! Ay Aâlay sennig wayen d qqaôen!');
INSERT INTO "ramdane" VALUES(6,101,'6:101','Axellaq n tmurt akked igenwan, amek ara Isâu mmi S, ur Isâi tameddakwelt? U Ixleq yal cci, u Neppa, yal cci, Issen it.');
INSERT INTO "ramdane" VALUES(6,102,'6:102','D wagi ay d Öebbi, Mass nnwen, ur illi ôebbi siwa Neppa. Ixleq yal cci, ihi âabdet eP. Neppa d Awkil n yal cci.');
INSERT INTO "ramdane" VALUES(6,103,'6:103','Ur T tepsami tmu$li. Neppa Ipsami tamu$li. Neppa d Laîif, i Izôan.');
INSERT INTO "ramdane" VALUES(6,104,'6:104','Ni$ iusa kwen id ara twalim, s$uô Mass nnwen. Win iwalan, i iman is; win idre$len, mgal is. Ur lli$ nek d amêareb nnwen.');
INSERT INTO "ramdane" VALUES(6,105,'6:105','Akka ay Nessefham issekniyen, iwakken ad inin: "tlemdev", iwakken a t Nbeggen i ugdud issnen.');
INSERT INTO "ramdane" VALUES(6,106,'6:106','Ddu d wayen ik ed ippuweêêan, s$uô Mass ik; ur illi ôebbi siwa Neppa. Eoo imcurak.');
INSERT INTO "ramdane" VALUES(6,107,'6:107','Lemmer i S ihwi i Öebbi, ur curken. Ur k Nerra d amêareb nnsen, ur telliv d awkil nnsen.');
INSERT INTO "ramdane" VALUES(6,108,'6:108','Ur naâlet wid u$uô la pnujun, war Öebbi. Axaîeô ad naâlen Öebbi, s lbe$wv, war tamusni. Akka ay Ncebbeê igiten is i yal lois. Sinna, ar Mass nnsen, tu$alin nnsen; a ten Ixebbeô $ef wayen xeddmen.');
INSERT INTO "ramdane" VALUES(6,109,'6:109','Ggulen s Öebbi, tagalit meqqwôen, ar ma iusa ten id ussekni, ar ad amnen yis. Ini: "issekniyen ar Öebbi kan". Acu i kwen irran têussem ar, xas ma iusa ten id, ur ppamnen?');
INSERT INTO "ramdane" VALUES(6,110,'6:110','A Neppi ulawen nnsen, d yiéôi nnsen, akken ur uminen yis abrid amezwaru. A ten Neoo di lbe$wv nnsen, d ider$alen.');
INSERT INTO "ramdane" VALUES(6,111,'6:111','Xas Nekwni Nessers asen d lmalayek, u mmeslayen asen d lmeggtin, u Nefser asen d, yal cci, zdat sen, ur ppamnen, siwa ma ihwa yaS i Öebbi. Maca, aîas degsen ur ssinen.');
INSERT INTO "ramdane" VALUES(6,112,'6:112','Akka ay Nerra, i yal nnbi, acengu. Ccwaîen, seg imdanen akw si leonun, ppemweêêin garasen awal uziyn i u$uôôu. Lemmer i S ihwi, i Mass ik, ur p xeddmen. Ihi, eoo iten ar wayen d snulfuyen,');
INSERT INTO "ramdane" VALUES(6,113,'6:113','iwakken ad mmalen $uôes ulawen n wid ur numin s laxeôt, a t ôavin, ad gemren ayen zerâen.');
INSERT INTO "ramdane" VALUES(6,114,'6:114','Day b$i$ amêaddi, war Öebbi? D Neppa i wen d Issersen Tazmamt, s ussefhem. Wid iwumi Nefka Tazmamt, éôan, ar neppat, ters ed s$uô Mass ik, s tidep. Ur ppili seg wid ipcukkun.');
INSERT INTO "ramdane" VALUES(6,115,'6:115','Awal n Mass ik ibbwev, s tidep akked lmizan. Ur illi ubeddel deg wawal iS. Neppa Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(6,116,'6:116','Ma tu$ev awal i tegwti n wid illan di tmurt, a k telfen seg ubrid n Öebbi. Teddun kan d wayen nwan. Nitni snulfuyen d kan.');
INSERT INTO "ramdane" VALUES(6,117,'6:117','Ih, Mass ik Issen sennig akw, anwi innetlafen seg ubrid iS. Neppa Issen sennig akw, anwi ipwanhan.');
INSERT INTO "ramdane" VALUES(6,118,'6:118','Eççet ayen i$ef d immekti isem n Öebbi, ma tumnem s issekniyen iS.');
INSERT INTO "ramdane" VALUES(6,119,'6:119','Acu ikwen iu$en, ur teppeppem ara seg wayen i$ef d immekti isem n Öebbi? Ni$ Issfehm awen ayen i wen Igdel, siwa ma ipwaêeppem fellawen. Ih, aîas ay tellifen, s zzhu nnsen, war tussna. Ni$ Mass ik Iugar akw akken Issen wid izegren tilas.');
INSERT INTO "ramdane" VALUES(6,120,'6:120','Ekkset abekkav uvhiô akked uffir. Ih, wid ibbwin abekkav, ad ppuqeîîin $ef wayen xeddmen.');
INSERT INTO "ramdane" VALUES(6,121,'6:121','Ur teppet ayen i$ef ur d immekti ara, isem n Öebbi. Ih, ayagi d ixmiô. Ih, ccwaîen pweêêin i imeddukwal nnsen, a kwen namren. Ma tu$em asen awal, kunwi, s tidep, tcurkem.');
INSERT INTO "ramdane" VALUES(6,122,'6:122','Ma win illan d lmegget, Nessider it id, Nga yas tafat, iteddu yis ger medden, day d amedya s, win illan di tillas, ur izmir ad iffe$ segsent. Akka, ippucebbeê i ijehliyen, wayen xeddmen.');
INSERT INTO "ramdane" VALUES(6,123,'6:123','Akka ay Nerra, ad ppandin di yal tamdint, ibe$wviyen is, uqsiêen mavi. Ppandin kan i iman nnsen, ur faqen.');
INSERT INTO "ramdane" VALUES(6,124,'6:124','Ma iusa ten id ussekni, ad inin: "ur neppamen, alamma iusa ya$ d ayen id iusan ar imazanen n Öebbi". Öebbi Yif akw akken Issen, anida ara Ifk tabôap iS. Ad iôxisen, ar Öebbi, wid ibe$wven. Fellasen aâaqeb uqsiê, ilmend n wayen ppandin.');
INSERT INTO "ramdane" VALUES(6,125,'6:125','Win Ib$a Öebbi a t Inhu, ad illi idmaren is ar wannuz. Win Ib$a a t Itlef, ad Irr idmaren is deyqen, têeyyôen, amzun ippaâôav ad iali s igenni. Akka i Ippara Öebbi axerwaâ i wid ur numin.');
INSERT INTO "ramdane" VALUES(6,126,'6:126','Wagi d abrid uwqim n Mass ik. Ni$ Nessefham issekniyen i ugdud ismektayen.');
INSERT INTO "ramdane" VALUES(6,127,'6:127','I nitni, axxam n tifrat ar Mass nnsen. Neppa d Bab nnsen, $ef wayen xeddmen.');
INSERT INTO "ramdane" VALUES(6,128,'6:128','Ass ideg ara ten Ifser meôôa: "a yagraw n leonun, ni$ tcerrâem deg imdanen"? A d inin imawlan nnsen, seg imdanen: "a Mass nne$! Kra segne$ $elten si kra, Nebbwev ar lajel nne$, i $ Terriv". Inna: "d times ay d tanezdu$t nnwen, ad degs twekkôem, siwa ma ur aS ihwi i Öebbi". Ih, Mass ik d Bab n ûûwab, d Amusnaw.');
INSERT INTO "ramdane" VALUES(6,129,'6:129','Akka ay Neppara kra imednas d iêbiben n kra, ilmend n wayen keppben.');
INSERT INTO "ramdane" VALUES(6,130,'6:130','"A yagraw n leonun d yimdanen, day ur kwen id usan ara imazanen segwen? Mmalen awen issekniyen iW, nedôen kwen $ef temlilit n wass nnwen agi". Nnan d: "ncehhed $ef yiman nne$". T$uôô iten tmeddurt n ddunit. Cehhden $ef yiman nnsen ar nitni, s tidep, llan d ijehliyen.');
INSERT INTO "ramdane" VALUES(6,131,'6:131','Akka, acku Mass ik ur Isquccu tamdint, s ddens, ma llan imezda$ is d i$wefliyen.');
INSERT INTO "ramdane" VALUES(6,132,'6:132','I yal yiwen, tiseddarin seg wayen ixdem. Ur I$effel Mass ik $ef wayen xeddmen.');
INSERT INTO "ramdane" VALUES(6,133,'6:133','Mass ik d Asâay, d Bab uêunu. Ma ihwa yaS a kwen Ikkes, a d Inûef, deffir wen, ayen i S ihwan, akken ikwen id Ixleq si tarwa n ugdud nniven.');
INSERT INTO "ramdane" VALUES(6,134,'6:134','Ih, ayen i wen ippuâahden, a d ias, war ccekk. Ur tezmirem, kunwi, a ppesfunngem.');
INSERT INTO "ramdane" VALUES(6,135,'6:135','Ini: "a yagdud iw! Xedmet akken tewâam, nek ad xedme$. Deqqal a ppeéôem iwumi ara telhu taggara n Uxxam". S tidep, ur rebêen imednas.');
INSERT INTO "ramdane" VALUES(6,136,'6:136','Gan aS i Öebbi amur, seg wayen id Ifka n l$ella, akked lmal. Nnan: "wagi i Öebbi –- di tikta nnsen -, wagi i icriken nne$". Ayen gan i icriken nnsen, ur ippawev ar Öebbi. Ayen gan i Öebbi, neppa, ad yawev ar icriken nnsen. I icmet wayen id pêuddun!');
INSERT INTO "ramdane" VALUES(6,137,'6:137','Akka ay cebbêen icriken nnsen, i waîas n yimcurak, timen$iwt n tarwa nnsen, iwakken a ten squccen, u ad irwi ddin nnsen, fellasen. Lemmer i S ihwi i Öebbi, ur p xeddmen. Eoo iten ar wayen d snulfuyen.');
INSERT INTO "ramdane" VALUES(6,138,'6:138','Nnan: "lmal akked l$ella yagi ppugedlen, ur ten itepp, siwa win i $ ihwan". S tikta nnsen. Lmal iwumi gedlen iârar nnsen, lmal iwumi ur d ppadren fellasen, isem n Öebbi. D asnulfu mgal iS. A ten Iqeîîi $ef wayen d snulfuyen.');
INSERT INTO "ramdane" VALUES(6,139,'6:139','Qqaôen: "ayen illan deg iâabbav n lmal agi, i ddker nne$ kan, ipwagdel i tlawin nne$". Ma ilul ed immut, ad mlilen $uôes. A ten Iqeîîi $ef wayen d qqaôen. Neppa, s tidep, d Bab n ûûwab, d Amusnaw.');
INSERT INTO "ramdane" VALUES(6,140,'6:140','Ni$ xesôen wid in$an arraw nnsen. D tiderwect, war tussna. Gedlen ayen i s iten I$at Öebbi, snulfan d mgal Öebbi. Ni$ nnetlafen, ur pwanhan.');
INSERT INTO "ramdane" VALUES(6,141,'6:141','D Neppa id Ixelqen leonanat s iâricen, ne$ war iâricen, tizdayin, zerriâa n yal ûûenf, i wuççi; azemmur akked rreman ippemcabin, ne$ ur neppemcabi. Eççet si ppemôa nnsen, ma fkan d, efket leêqq is, ass u$ellet nnsen. Ur ssugutet. Neppa, s tidep, ur Iêemmel ara wid ipêawalen.');
INSERT INTO "ramdane" VALUES(6,142,'6:142','Si lmal, aggway d wussu. Eççet seg wayen ikwen Iôéeq Öebbi. Ur teddut d iberdan n Cciîan. Neppa, s tidep, d acengu nnwen n ûûeê.');
INSERT INTO "ramdane" VALUES(6,143,'6:143','Ïamt tyugiwin, snat u$welmi, snat umâiz. Ini: "d sin n ddker i Igdel, ne$ d snat n nnta, ne$ d ayen illan deg iâabbav n snat n nnta? Xebbôet iyi d, s tussna, ma tessawalem tidep".');
INSERT INTO "ramdane" VALUES(6,144,'6:144','Seg ile$wman, snat, seg ubeqôi, snat. Ini: "d sin n ddker i Igdel, ne$ d snat n nnta, ne$ d ayen illan deg iâabbav n snat n nnta, ne$ tellam d inagan mi kwen id Iweûûa Öebbi $ef waya"? Anwa idensen sennig win id igren tikerkas $ef Öebbi, iwakken ad itlef imdanen, ur issin. Ih, Öebbi ur Inehhu agdud imednas.');
INSERT INTO "ramdane" VALUES(6,145,'6:145','Ini: "ur ufi$ ipwagdel a t içç yiwen, deg wayen iyi d ippuweêêan, siwa ma illa d loifa, ne$ d idim immaren, ne$ d aksum n yilef - wagi, war ccekk, d lewsex -, ne$ îêegga s d$wel i war Öebbi. Win i$ef ippuêeppem, ur ib$i, ur ib$wiv, Mass ik, s tidep, Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(6,146,'6:146','I wudayen, Negdel ayen akw isâan acaren, seg ubeqôi akked u$welmi, Negdel asen tassemt nnsen, siwa tin n iârar nnsen, iâabbav, akked wayen ipwaxelven akked i$san. Akka ay ten Nepkafi, ilmend n îîeôô nnsen. Nekwni, war ccekk, d At Tidep.');
INSERT INTO "ramdane" VALUES(6,147,'6:147','Ma skaddben k, ini: "Mass nnwen d Bab uêunu iwsâan, ur ipwexxiô ssexv iS, af ugdud ibe$wviyen".');
INSERT INTO "ramdane" VALUES(6,148,'6:148','Ad inin imcurak: "lemmer i S ihwi i Öebbi, nekwni akked imezwura nne$, ur ncurek, ur negdel acemma". Akka ay skaddben imezwura nnsen, armi jeôôben ssexv nne£. Ini: "day tesâam tussna ar a$ d tesseknem? Teppeddum kan d nnwaya, kunwi tesnulfuyem d kan".');
INSERT INTO "ramdane" VALUES(6,149,'6:149','Ini: "n Öebbi timenna tunûiêin. Lemmer i S ihwi, yili Inha kwen akw".');
INSERT INTO "ramdane" VALUES(6,150,'6:150','Ini: "awit ed inagan nnwen, ara icehhden ar Öebbi Igdel ayagi". Ma cehhden, ur cehhed yidsen. Ur teddu d lhawiyya n wid iskaddben issekniyen nne£, akked wid ur numin s laxeôt, u pparan tanudda i Mass nnsen.');
INSERT INTO "ramdane" VALUES(6,151,'6:151','Ini: "yyaw, ad ini$. I wen Igdel Mass nnwen: ur aS tepcarakem acemma; elhut ar imawlan nnwen; ur neqqet tarwa nnwen, di tigullelt - d Nekwni ara kwen Iôeéqen yidsen -, ur ppaéet ar lâaô, d uvhiô ne$ d uffir; ur neqqet amdan, d ayen Igdel Öebbi, siwa ma s lêeqq". Ar waya i kwen Iweûûa, amar a pfehmem.');
INSERT INTO "ramdane" VALUES(6,152,'6:152','Ur pqeôôibet s ayla ugujil, siwa s wayen ilhan, alamma immeêtel. Weznet, ktilet s lewqama. Ur Nepkellif amdan siwa ar wayen iwumi izmer. Ma thedôem d, weznet, xas ma $eô llzem nnwen. Qadôet lemâahda n Öebbi. Iweûûa kwen $ef waya, amar a d temmektim.');
INSERT INTO "ramdane" VALUES(6,153,'6:153','D wagi ay d abrid iW uwqim, ddut degs. Ur teddut d iberdan ara kwen isîixôen seg ubrid iS. Iweûûa kwen $ef waya, amar a pêezzbem.');
INSERT INTO "ramdane" VALUES(6,154,'6:154','Sinna, Nefka Tazmamt i Musa. Tewqem i wid imaânen, tfeûûel yal cci, d nnhaya u d aêunu. Amar ad amnen s timlilit n Mass nnsen.');
INSERT INTO "ramdane" VALUES(6,155,'6:155','Tagi d Tazmamt, Nessers ip id, teôqa. Ihi, ddut yides, êezzbet, amar fellawen aêunu.');
INSERT INTO "ramdane" VALUES(6,156,'6:156','Ur d teqqaôem ara: "ters ed kan Tezmamt $eô sin leoyus, uqbel nne$, ur neéôi d acu sselmaden".');
INSERT INTO "ramdane" VALUES(6,157,'6:157','Ne$ a d tinim: "lemmer id ters Tazmamt $uône$, yili nif iten akken nepwanha". Ni$ iusa kwen id ubeggen, s$uô Mass nnwen, d nnhaya u d aêunu. Anwa idensen sennig win iskaddben issekniyen n Öebbi, u iooa iten? A Nqeîîi wid iooan issekniyen nne£, s yir aâaqeb, ilmend n wayen la ppaooan.');
INSERT INTO "ramdane" VALUES(6,158,'6:158','D acu pôaoun, siwa ma ten id asent lmalayek, ne$ a d Ias Mass ik, ne$ a d asen kra issekniyen n Mass ik? Ass ideg ara d asen kra issekniyen n Mass ik, ur infaâ umdan liman is, ma, iu$ lêal, ur iumin ara, ne$ ur iksib lxiô, di liman is. Ini: "gganit, nekwni, ih, la nepgani".');
INSERT INTO "ramdane" VALUES(6,159,'6:159','Ih, wid ibvan ddin nnsen, u gan ikabaren, ur tepkkiv yidsen deg wacemma. Lameô nnsen ar Öebbi kan, sinna, a ten Ixebbeô $ef wayen xeddmen.');
INSERT INTO "ramdane" VALUES(6,160,'6:160','Win id iusan s yise$, ines mraw i t icban; win id iusan s tucmip, ad ippuqeîîi s tin kan i p icban; nitni ur pwadensen.');
INSERT INTO "ramdane" VALUES(6,161,'6:161','Ini: "nek, Inha iyi Mass iw, ar ubrid uwqim, d ddin n ddwam, win n Ibôahim, i imalen ar tidep, ur nelli seg imcurak".');
INSERT INTO "ramdane" VALUES(6,162,'6:162','Ini: "ih, taéallit iw, iseflan iw, tudert iw, tameppant iw, i Öebbi, Mass imavalen.');
INSERT INTO "ramdane" VALUES(6,163,'6:163','Ur aS illi ucarek. Pwamôe$ d s waya: nek d amduâ amenzu".');
INSERT INTO "ramdane" VALUES(6,164,'6:164','Ini: "day b$i$ mass, war Öebbi? U Neppa d Mass n yal cci". Ur ikesseb yal yiman, siwa mgal is. Ur ipbibbi win ibubben taâkkwemt n wayev. Sinna, ar Mass nnwen, tu$alin nnwen. Sinna, a kwen Ixebbeô $ef wayen tellam temgaradem.');
INSERT INTO "ramdane" VALUES(6,165,'6:165','D Neppa i kwen Irran d igensas di tmurt. Irra, kra segwen, tiseddaôin sennig wiyav, iwakken a kwen Ijeôôeb $ef wayen i wen Ifka. Ih, Mass ik Ip$awal deg uâaqeb. Neppa, s tidep, Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(7,1,'7:1','Almû.');
INSERT INTO "ramdane" VALUES(7,2,'7:2','Tazmamt ters ed $uôek, ur teppili segs lhejna deg idmaren ik, iwakken a pnabiv yis; u d asmekti i lmumnin.');
INSERT INTO "ramdane" VALUES(7,3,'7:3','Ddut d wayen id irsen $uôwen, s$uô Mass nnwen. Ur teddut d imawlan, war Neppa. Drus id tesmektayem.');
INSERT INTO "ramdane" VALUES(7,4,'7:4','Acêal n temdinin ay Nessenger. Tusa ten id tiyta nne£, deg ives nnsen, ne$ deg uqeggel nnsen.');
INSERT INTO "ramdane" VALUES(7,5,'7:5','Ti$ôi nnsen, mi ten id tusa tiyta nne£, siwa mi d nnan: "ih, nella d imednas".');
INSERT INTO "ramdane" VALUES(7,6,'7:6','War ccekk, a Nesteqsi wid u$uô id Nuzen, a Nesteqsi imazanen.');
INSERT INTO "ramdane" VALUES(7,7,'7:7','War ccekk, ad asen Neêku, s tmusni, ur Nelli N$ab.');
INSERT INTO "ramdane" VALUES(7,8,'7:8','Awzan, assen, d tidep. Wid iwumi ééayet lweznat nnsen, widak d wid irebêen.');
INSERT INTO "ramdane" VALUES(7,9,'7:9','Wid iwumi fessuset lweznat nnsen, widak d wid ixesôen iman nnsen, seg akken sednasen issekniyen nne£.');
INSERT INTO "ramdane" VALUES(7,10,'7:10','Ni$, s tidep, Nesrekd ikwen di tmurt, Nerra yawen, degs, tamâict nnwen. Drus ay tesnemmirem.');
INSERT INTO "ramdane" VALUES(7,11,'7:11','Ni$, s tidep, Nxelq ikwen, sinna, Nmesl ikwen, sinna, Nenna i lmalayek: "knumt i Adem"! Knant, siwa Iblis. Ur illi seg wid iknan.');
INSERT INTO "ramdane" VALUES(7,12,'7:12','Inna: "acu k iîîfen ur tekniv ara, mi k d Umôe$"? Inna d: "Nek, ife$ t. Txelqev iyi si tmes, Txelqev t si talaxt".');
INSERT INTO "ramdane" VALUES(7,13,'7:13','Inna: "ihi, ûûub segs! Ur ak ippunefk ara a ppesme$wôev iman ik, degs. Effe$, keçç seg wid ixuûen"!');
INSERT INTO "ramdane" VALUES(7,14,'7:14','Inna: "Eoo iyi, ar ass ideg ara d kkren".');
INSERT INTO "ramdane" VALUES(7,15,'7:15','Inna: "keçç, ih, seg wid ara iqqimen".');
INSERT INTO "ramdane" VALUES(7,16,'7:16','Inna d: "imi iyi Tâammdev a$ewwu, ad asen qqime$, war ccekk, deg ubrid iK uwqim.');
INSERT INTO "ramdane" VALUES(7,17,'7:17','Sinna, ad asen d tekke$ zdat, deffir, yeffus, zelmev. Ur Teppafev, $eô tugwti segsen, asnemmer".');
INSERT INTO "ramdane" VALUES(7,18,'7:18','Inna: "effe$ segs, d ameêquô ippuvegôen! War ccekk, ad Acaôe$ Loahennama s wid, segsen, i k ivefôen, akked yiswen akw.');
INSERT INTO "ramdane" VALUES(7,19,'7:19','A Adem, ezde$ Loennet, keçç akked tmeîîut ik, eççet seg wayen i wen ihwan. Ur pqeôôibet ttejôa agi, mulac a ppilim seg imednas".');
INSERT INTO "ramdane" VALUES(7,20,'7:20','I$ewwa ten Cciîan, iwakken ad asen d iban âaryan nnsen i illan iffer. Inna: "Ikkes awen, kan, Mass nnwen, ttejôa agi, iwakken ur teppilim d lmalayek, ne$ ur tepwekkiôem".');
INSERT INTO "ramdane" VALUES(7,21,'7:21','Iggul asen: "nek, i kunwi, d unûiê".');
INSERT INTO "ramdane" VALUES(7,22,'7:22','Isdull iten s u$uôôu. Mi âaôven si ttejôa nni, walan iman nnsen aâran. Lhan d la pcuddun, $ef iman nnsen, afriwen n Loennet. Issawel asen d Mass nnsen: "day ur awen Gdile$ ara ttejôa nni? Ad awen Ini$: war ccekk, Cciîan, i kunwi, d acengu n ûûeê".');
INSERT INTO "ramdane" VALUES(7,23,'7:23','Nnan: "a Mass nne$! Nvuôô iman nne$. Ma ur a$ Tsemmêev, ur Têunev fella$, a nili seg imexsuôen".');
INSERT INTO "ramdane" VALUES(7,24,'7:24','Inna: "erset! Kra d icenga n kra. A pzed$em di tmurt, a ppezhum, tallit kan".');
INSERT INTO "ramdane" VALUES(7,25,'7:25','Irna: "degs a ppidirem, degs a ppemmtem, segs a d teff$em".');
INSERT INTO "ramdane" VALUES(7,26,'7:26','A tarwa n Adem! Ni$ Nessers awen ed aceîîiv ara kwen issôen, ôôêuê, akked uceîîiv uêezzeb. Wagi yif iten". Ayagi seg issekniyen n Öebbi, amar a d mmektin.');
INSERT INTO "ramdane" VALUES(7,27,'7:27','A tarwa n Adem! £uôwat a kwen ihewwel Cciîan, akken id issuffe$ imezwura nnwen si Loennet! Ikkes asen iceîîiven nnsen, iwakken ad asen issken âaryan nnsen. Neppa, war ccekk, ipwali kwen - neppa akked ccetla s - seg wanda ur ten tepwalim. Ni$ Nerra ccwaîen d imawlan n wid ur numin.');
INSERT INTO "ramdane" VALUES(7,28,'7:28','Mi xedmen ayen n dir, ad inin: "nufa d akka ar imezwura nne$, u Öebbi Iumeô a$ d $uôes. Ini: "Öebbi, s tidep, ur Ippameô ar wayen n dir. Day a d tinim $ef Öebbi, ayen ur tessinem"?');
INSERT INTO "ramdane" VALUES(7,29,'7:29','Ini: "Iumeô ed Öebbi lmizan. Zzit $uôeS udmawen nnwen di yal lekni, nujet $uôeS. £uôeS, s ddin iûfan. Akken ikwen id Ixleq, a ppu$alem".');
INSERT INTO "ramdane" VALUES(7,30,'7:30','Arbaâ Inha it, arbaâ iura ttelfan fellas. Nitni, s tidep, rran ccwaîen d iêbiben, war Öebbi. U $ilen nitni, war ccekk, pwanhan.');
INSERT INTO "ramdane" VALUES(7,31,'7:31','A tarwa n Adem! Ccebêet i yal lekni, eççet, esswet, ur sîuqqutet. Neppa, s tidep, ur Iêemmel ara wid isîuqquten.');
INSERT INTO "ramdane" VALUES(7,32,'7:32','Ini: "anwa i igedlen ccbaêa n Öebbi, id Issuffe$ i imdanen iS, akked ôôeéq ilhan"? Ini: "ayagi, di tmeddurt n ddunit, i wid iumnen. I nitni kan ass n tnekra". Akka ay Nfeûûel issekniyen i ugdud issnen.');
INSERT INTO "ramdane" VALUES(7,33,'7:33','Ini: "Mass iw Igdel kan ayen n dir, d uvhiô ne$ d uffir, abekkav, tem$weô war ssebba, a pcarkem i Öebbi ayen iwumi ur d irs udabu, a ppinim $ef Öebbi ayen ur tessinem".');
INSERT INTO "ramdane" VALUES(7,34,'7:34','Yal lois isâa lajel. Ma iusa d lajel nnsen, ur t swexxiôen s ssaâa, ur t id ééaéan.');
INSERT INTO "ramdane" VALUES(7,35,'7:35','A tarwa n Adem! Ma usan kwen id imazanen segwen, mmalen awen issekniyen iW, wid ipêezziben, ixeddmen ayen ilhan, ur ppagwaden, ur $weblen.');
INSERT INTO "ramdane" VALUES(7,36,'7:36','Wid iskaddben issekniyen nne£, u sme$wôen iman nnsen fellasen, widak d at tmes, nitni degs ad wekkôen.');
INSERT INTO "ramdane" VALUES(7,37,'7:37','Anwa idensen sennig win id igren tikerkas $ef Öebbi, ne$ iskaddben issekniyen iS? Widak, a ten iêaz umur i sen iuran di Tezmamt. Alamma, mi ten id usan imceggâen nne£, ad ddmen leôwaê nnsen, ad inin: "anida ten wid u$uô tepnujum, war Öebbi"? A d inin: "ur ten nufi". Ad cehhden, $ef yiman nnsen, ar nitni llan d ijehliyen.');
INSERT INTO "ramdane" VALUES(7,38,'7:38','Inna: "kecmet di tmes ar leoyus n leonun akked yimdanen, i kwen id izwaren"! Yal tikelt ara ikcem lois, ad ixzu gma s. Alamma, mi mlalen degs akw, ad yini uneggaru nnsen $ef umezwaru nnsen: "a Mass nne$! Wigi telfen a$, Aâqeb iten s tmes, berdayen". Inna: "i yal yiwen berdayen, maca ur teéôim ara".');
INSERT INTO "ramdane" VALUES(7,39,'7:39','Inna umezwaru nnsen i uneggaru nnsen: "ur kwen smenyafen i nekwni, ihi jeôôbet aâaqeb, ilmend n wayen tkeppbem".');
INSERT INTO "ramdane" VALUES(7,40,'7:40','Ih, wid iskaddben issekniyen nne£, u sme$wôen iman nnsen fellasen, ur asen tellin tibbura igenni, ur keçmen Loennet, skud ur iâadda ara ul$em di tiî n tsegnit. Akka ay Nepqeîîi ibe$wviyen.');
INSERT INTO "ramdane" VALUES(7,41,'7:41','I nitni ameîôeê di Loahennama, ad $ummen s tmes. Akka ay Nepqeîîi imednas.');
INSERT INTO "ramdane" VALUES(7,42,'7:42','Wid iumnen, ixeddmen ayen ilhan - ur Nepkellif i umdan siwa ayen iwumi izmer - widak, d at Loennet, nitni, degs, ad wekkôen.');
INSERT INTO "ramdane" VALUES(7,43,'7:43','A Nekkes i d igwran n dd$el, deg idmaren nnsen. Isaffen ad ppazzalen ddawat sen. Ad inin: "ccekôan i Öebbi i $ Inhan $eô waya. Ur nepwanhu, lemmer ur a$ Inha ara Öebbi. Ni$ usan d imazanen n Mass nne$, s tidep". Ad pwanecden: "appan Loennet, tweôtem p, ilmend n wayen txeddmem".');
INSERT INTO "ramdane" VALUES(7,44,'7:44','At Loennet ad siwlen i at tmes: "ni$ nufa i $ Iâahed Mass nne$, s tidep. Ma tufam ayen i wen Iâahed Mass nnwen, s tidep"? A inin: "Ih". Iberreê uberraê garasen: "xezzu n Öebbi i imednas,');
INSERT INTO "ramdane" VALUES(7,45,'7:45','wid iîîeôôifen seg ubrid n Öebbi, ssaramen t d uâwij, u nekôen laxeôt".');
INSERT INTO "ramdane" VALUES(7,46,'7:46','Garasen ssteô. £ef iqacucen, irgazen, ssnen ten akw, ar ûûiffat nnsen. Ad siwlen i at Loennet: "tifrat fellawen"! Ur p keççmen, xas ssarmen.');
INSERT INTO "ramdane" VALUES(7,47,'7:47','Mi zzint wallen nnsen ar at tmes, ad inin: "a Mass nne$! Ur a$ Ppara s agdud imednas"!');
INSERT INTO "ramdane" VALUES(7,48,'7:48','Ad siwlen at iqacucen i irgazen, ssnen ten ar ûûiffat nnsen. Ad inin: "acu i wen d issuli wayen tjemâam akked wayen tzuxxem"?');
INSERT INTO "ramdane" VALUES(7,49,'7:49','Day wigi d wid i$ef teggulem, ar ur sen ippak Öebbi aêunu? "Kecmet Loennet, ur teppagwadem, ur t$weblem".');
INSERT INTO "ramdane" VALUES(7,50,'7:50','At tmes ad siwlen i at Loennet: "smiret ed fellane$ aman, ne$ seg wayen is ikwen I$at Öebbi". Ad inin: "Öebbi, s tidep, Igdel iten i ijehliyen,');
INSERT INTO "ramdane" VALUES(7,51,'7:51','wid irran ddin nnsen d zzhu, d urar, u t$uôô iten tmeddurt n ddunit". Ihi, assa, a ten Neppu, akken ppun timlilit n wass nnsen agi, u nekôen issekniyen nne£.');
INSERT INTO "ramdane" VALUES(7,52,'7:52','Ni$ Nusa ten id s Tezmamt, Nfeûûel ip s tussna. D nnhaya, d aêunu i ugdud iumnen.');
INSERT INTO "ramdane" VALUES(7,53,'7:53','Day la pôaoun kan veôôu s? Ass n tisin n veôôu s, ad inin wid illan, iu$ lêal, ppun p: "ni$ usan d imazanen n Mass nne$, s tidep. Day nesâa imcafâen ara $ icafâen, ne$ ad a$ rren, a nexdem ayen mgirrden wayen nxeddem"? Ni$ sxeûôen iman nnsen. Itelf iten wayen d snulfuyen.');
INSERT INTO "ramdane" VALUES(7,54,'7:54','S tidep, Mass nnwen d Öebbi. Ixelq ed tamurt akked igenwan di sdis wussan, sinna, Iâannec $ef usgeld. Ip$ummu iv s wass, ip$awalen $uôes. Iîij, aggur, akked yitran unzen i lameô iS. Day maççi ineS lxelq akked lameô? D Asaâdi Öebbi, Mass imavalen!');
INSERT INTO "ramdane" VALUES(7,55,'7:55','Naâret Mass nnwen, s wannuz, di tbavnit. Neppa, s tidep, ur Iêemmel ara wid izegren tilas.');
INSERT INTO "ramdane" VALUES(7,56,'7:56','Ur ssefsadet di tmurt deffir useêôet is. Naâôet eP, s tugwdi d usirem. Aêunu n Öebbi, s tidep, ar tama n lâuûam.');
INSERT INTO "ramdane" VALUES(7,57,'7:57','D Neppa id Ippaznen avu, d abecceô s uêunu S. Alamma, ma ibbub ed asigna aéayan, Nenheô it ar tmurt immuten. Nesse$li d syis aman. Nessuffe$ ed yissen yal ppemôa. Akka ay d Nessuffu$ lmeggtin, amendeô ma d tesmektim.');
INSERT INTO "ramdane" VALUES(7,58,'7:58','Di tmurt lâali, teff$en d im$an is, ma Irad Öebbi. Di tin icemten, teff$en d kan d iweqweqen. Akka ay Nepbeggin issekniyen i ugdud isnemmiren.');
INSERT INTO "ramdane" VALUES(7,59,'7:59','Ni$ Nuzen ed Nuê i ugdud is. Inna: "a yagdud iw! Âabdet Öebbi! Ur tesâim ôebbi war Neppa. Nek ugade$ fellawen aâaqeb, ass ameqwôaêan".');
INSERT INTO "ramdane" VALUES(7,60,'7:60','Nnan imvebbôen n ugdud is: "$uône$, s tidep, aql ak di ttelfan n ûûeê".');
INSERT INTO "ramdane" VALUES(7,61,'7:61','Inna: "a yagdud iw! Ur illi dgi ttelfan. Maca nek d amazan s$uô Mass imavalen.');
INSERT INTO "ramdane" VALUES(7,62,'7:62','Bbwi$ awen d tibratin n Mass iw, a kwen neûêe$. Ssne$, s$uô Öebbi, ayen ur tessinem.');
INSERT INTO "ramdane" VALUES(7,63,'7:63','Day twehmem mi kwen id iusa usmekti s$uô Mass nnwen, ibbwi t id urgaz segwen, iwakken a kwen inabi, iwakken a pêezzbem, amar fellawen aêunu"?');
INSERT INTO "ramdane" VALUES(7,64,'7:64','Skaddben t. Nsellek it, akked wid illan yides, di lbabuô. Ne$meq wid iskaddben issekniyen nne£. Nitni, s tidep, d agdud ader$al.');
INSERT INTO "ramdane" VALUES(7,65,'7:65','Ar Âad, gmat sen Hud. Inna: "a yagdud iw! Âabdet Öebbi! Ur tesâim ôebbi, war Neppa. Day ur tepêezziben ara"?');
INSERT INTO "ramdane" VALUES(7,66,'7:66','Nnan imvebbôen, wid ijehlen seg ugdud is: "s tidep, $uône$, deg k tisselbi; $uône$, ih, keçç seg wid iskiddiben".');
INSERT INTO "ramdane" VALUES(7,67,'7:67','Inna: "a yagdud iw! Ur dgi tisselbi. Maca nek d amazan s$uô Mass imavalen.');
INSERT INTO "ramdane" VALUES(7,68,'7:68','Bbwi$ awen d tibratin n Mass iw. Nek, i kunwi, d aneûûaê muman.');
INSERT INTO "ramdane" VALUES(7,69,'7:69','Day twehmem mi kwen id iusa usmekti s$uô Mass nnwen, ibbwi t id urgaz segwen, iwakken a kwen inabi. Mmektit ed mi kwen Irra d igensas, deffir ugdud n Nuê. Isem$weô aîas lqedd nnwen. Mmektit ed lxiô n Öebbi, amar a prebêem".');
INSERT INTO "ramdane" VALUES(7,70,'7:70','Nnan: "day a$ d tusiv, iwakken a naâbed Öebbi Yiwen, a neoo ayen llan âabden imezwura nne$? Awi ya$ d ayen is la $ tessagwadev, ma d tidep ay tessawalev".');
INSERT INTO "ramdane" VALUES(7,71,'7:71','Inna: "ni$ iwwet ikwen id ddell akked zzâaf s$uô Mass nnwen. Day ad iyi namren $ef ismawen i tefkam, kunwi akked imezwura nnwen, i wid iwumi ur d Issers Öebbi kra udabu. Ihi, gganit, nek yidwen seg wid ara igganin".');
INSERT INTO "ramdane" VALUES(7,72,'7:72','Nsellek it, akked wid illan yides, s uêunu s$uône£. Nessenger wid iskaddben issekniyen nne£, ur nelli d lmumnin.');
INSERT INTO "ramdane" VALUES(7,73,'7:73','£uô Tamud, gmat sen Üaliê. Inna: "a yagdud iw! Âabdet Öebbi! Ur tesâim ôebbi war Neppa. Ni$ iusa kwen id ubeggen s$uô Mass nnwen. Tal$wemt agi n Öebbi, d assekni i kunwi. Ooet ep a ppeçç di tmurt n Öebbi. Ur p îîuôôut, mulac a kwen iêaz uâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(7,74,'7:74','Mmektit ed mi kwen Irra d igensas, deffir Âad. Isseqâad ikwen di tmurt, tepparram lebôuj di swaêel, teqqazem ixxamen deg durar. Ihi, mmektit ed lxiô n Öebbi, ur ssexsaôet tamurt, s usefsed".');
INSERT INTO "ramdane" VALUES(7,75,'7:75','Imvebbôen seg ugdud is, wid isme$awôen iman nnsen, nnan i wuôhifen, wid segsen iumnen: "day teéôam ar ÛüÜaliê d amazan s$uô Mass is"? Nnan: "ih, numen s wayen is id ippuceggaâ".');
INSERT INTO "ramdane" VALUES(7,76,'7:76','Nnan imzuxiyen: "nekwni, s tidep, nenkeô ayen is tumnem".');
INSERT INTO "ramdane" VALUES(7,77,'7:77','Ëerrfen avaô n tel$wemt, gelzen lameô n Mass nnsen. Nnan: "a Üaliê, awi ya$ d ayen is la $ tessagwadev, ma d amazan ay telliv".');
INSERT INTO "ramdane" VALUES(7,78,'7:78','Tewwet iten zzenzla, gwran d, deg ixxamen nnsen, d loeppat.');
INSERT INTO "ramdane" VALUES(7,79,'7:79','Iwexxeô asen, u inna: "a yagdud iw! Ni$ ssawve$ awen d tabôap n Mass iw, neûêe$ kwen, maca ur têemmlem ara win ineûêen".');
INSERT INTO "ramdane" VALUES(7,80,'7:80','I Luî, mi inna i ugdud is: "day a txeddmem lâaô ur ixdim yiwen, uqbel nnwen, deg imavalen?');
INSERT INTO "ramdane" VALUES(7,81,'7:81','Ih, kunwi tpeddum ar irgazen, tcehwam ten, war tilawin. Kunwi, ih, d agdud izegren tilas".');
INSERT INTO "ramdane" VALUES(7,82,'7:82','Tiririt n ugdud is siwa nnan: "ssuff$et pen si temdint nnwen, nitni d imdanen issaramen tezdeg".');
INSERT INTO "ramdane" VALUES(7,83,'7:83','Nsellek it, neppa akked twacult is, siwa tameîîut is, teqqim akked wid igwran.');
INSERT INTO "ramdane" VALUES(7,84,'7:84','Nesse$li d fellasen ageffur...Wali amek tevôa d ibe$wviyen.');
INSERT INTO "ramdane" VALUES(7,85,'7:85','£uô Madian, gmat sen Cuâayeb. Inna: "a yagdud iw! Âabdet Öebbi! Ur tesâim ôebbi war Neppa. Ni$ iusa kwen id ubeggen s$uô Mass nnwen. Ktilet, weznet, s lewqama. Ur îîuôôut medden di cci nnsen, ur ssefsadet di tmurt, deffir useêôet is. I kunwi yif it wayagi, ma d lmumnin ay tellam.');
INSERT INTO "ramdane" VALUES(7,86,'7:86','Ur p$imit di yal abrid, tessagwadem u tessefôa$em seg ubrid n Öebbi, wid iumnen yiS. Teb$am t d uâwij. Mmektit ed mi tellam d takwemmict, Iketteô ikwen. Walit amek tevôa taggara ifessaden.');
INSERT INTO "ramdane" VALUES(7,87,'7:87','Ma illa iumen urbaâ segwen s wayen is id ppuceggâa$, arbaâ nniven ur iumin ara. Üebôet ard Iêudd Öebbi garane$. Neppa Yif akw imêaddan".');
INSERT INTO "ramdane" VALUES(7,88,'7:88','Nnan d imvebbôen imzuxiyen, n ugdud is,: "a k nessuffe$ a Cuâayeb, keçç, akked wid iumnen yidek, si temdint nne$, ne$ a d tu$alev ar ddin nne$". Inna: "xas nugi?');
INSERT INTO "ramdane" VALUES(7,89,'7:89','Ihi, nger ed tikerkas $ef Öebbi, ma nu$al ar ddin nnwen, mbaâd ma Isellek a$ Öebbi, segs. Ur a$ ilaq ara a nu$al $uôes, siwa ma Irad Öebbi, Mass nne$. Mass nne$ Issemêalleq i yal cci s tmusni. Nepkel $ef Öebbi". A Mass nne$! Fru p garane$ akked ugdud nne$, s tidep. Keçç Tifev akw wid iferrun.');
INSERT INTO "ramdane" VALUES(7,90,'7:90','Nnan imvebbôen, wid ijehlen, n ugdud is: "ma teddam d Cuâayeb, war ccekk a pxesôem."');
INSERT INTO "ramdane" VALUES(7,91,'7:91','Tewwet iten zzenzla, gwran d, deg ixxamen nnsen, d loeppat.');
INSERT INTO "ramdane" VALUES(7,92,'7:92','Wid iskaddben Cuâayeb, u$alen amzun ur zdi$en. Wid iskaddben Cuâayeb, d nitni ay d imexûuôen.');
INSERT INTO "ramdane" VALUES(7,93,'7:93','Iwexxeô asen, u inna: "a yagdud iw! Ni$ ssawve$ awen d tibôatin n Mass iw, neûêe$ kwen. Amek ara $weble$ $ef ugdud ijehliyen"?');
INSERT INTO "ramdane" VALUES(7,94,'7:94','Ur Nuzen ara nnbi ar temdint, mebla ma Newwet imezda$ is s cedda, akked lemveôôa, amar ad êiwten.');
INSERT INTO "ramdane" VALUES(7,95,'7:95','Sinna, Nerra ise$ deg umkan n tucmip, armi wexxôen, nnan: "ni$ jeôôben imezwura nne$ lemveôôa akked ttrika". Newwet iten deqzalla, nitni ur bnin.');
INSERT INTO "ramdane" VALUES(7,96,'7:96','Lemmer kan uminen u êezzben imezda$ n temdinin, war ccekk, ad asen Nelli lbaôakat seg igenni akked tmurt. Maca, skaddben. Nessemêalleq asen, ilmend n wayen keppben.');
INSERT INTO "ramdane" VALUES(7,97,'7:97','Day $ilen imezda$ n temdinin, ur ten id teppawev tiyta nne£, deg yiv, nitni îîsen?');
INSERT INTO "ramdane" VALUES(7,98,'7:98','Ne$ day $ilen imezda$ n temdinin, ur ten id teppawev tiyta nne£, deg wazal, nitni ppuraren?');
INSERT INTO "ramdane" VALUES(7,99,'7:99','Day $ilen sellken i tandit n Öebbi? Siwa agdud i ixesôen, i i$ilen sellken i tandit n Öebbi.');
INSERT INTO "ramdane" VALUES(7,100,'7:100','Ne$ day ur d Iban ara, i wid ara iweôten tamurt, deffir imezda$ is, ar, ma ihwa ya£, a ten Newwet $ef ibekkaven nnsen, a Nesle$ ulawen nnsen, d$a nitni ur sellen?');
INSERT INTO "ramdane" VALUES(7,101,'7:101','D timdinin agi, i$ef ak Nêekku, si teqûivin nnsent. Ni$ usan $uôsent imazanen nnsent, s ubeggen. Ur llint d tid ara iamnen deg wayen skaddbent uqbel. Akka i Iselle$ Öebbi ulawen ijehliyen.');
INSERT INTO "ramdane" VALUES(7,102,'7:102','Ur Nufi nnesba ar tegwti nnsen. Nufa, tegwti nnsen, d iballa$en.');
INSERT INTO "ramdane" VALUES(7,103,'7:103','Sinna, deffir sen, Nuzen ed Musa, s issekniyen nne£, ar Ferâun akked imvebbôen is. Densen ten. Wali amek tevôa d ifessaden.');
INSERT INTO "ramdane" VALUES(7,104,'7:104','Inna Musa: "a Ferâun! Nek d amazan s$uô Mass imavalen.');
INSERT INTO "ramdane" VALUES(7,105,'7:105','D ûûeê, ur qqaôe$, $ef Öebbi, siwa tidep. Ih, usi$ kwen id, s ubeggen n Mass nnwen. Ihi, azen yidi At Isôail".');
INSERT INTO "ramdane" VALUES(7,106,'7:106','Inna: "ma tusiv d s ussekni, zzi t id, ma tessawalav tidep".');
INSERT INTO "ramdane" VALUES(7,107,'7:107','Iveggeô taâkwazt is, appan d azrem n ûûeê.');
INSERT INTO "ramdane" VALUES(7,108,'7:108','Issuffe$ ed afus is, iban ed d amellal i imnezhen.');
INSERT INTO "ramdane" VALUES(7,109,'7:109','Nnan d imvebbôen n ugdud n Ferâun: "wagi, s tidep, d aseêêar amusnaw".');
INSERT INTO "ramdane" VALUES(7,110,'7:110','"Ib$a a kwen issuffe$ si tmurt nnwen, d acu t lameô nnwen"?');
INSERT INTO "ramdane" VALUES(7,111,'7:111','Nnan: "anef as, neppa akked gma s. Azen iberraêen ar temdinin,');
INSERT INTO "ramdane" VALUES(7,112,'7:112','ad ak d awin yal aseêêar amusnaw".');
INSERT INTO "ramdane" VALUES(7,113,'7:113','Usan d iseêêaren n Ferâun, nnan: "s tidep, ad a$ tkafiv, ma d nekwni ara i$elben"?');
INSERT INTO "ramdane" VALUES(7,114,'7:114','Inna: "ih, u, s tidep, a ppilim d iqôiben".');
INSERT INTO "ramdane" VALUES(7,115,'7:115','Nnan: "a Musa! A pveggôev keçç, ne$ a nveggeô nekwni"?');
INSERT INTO "ramdane" VALUES(7,116,'7:116','Inna: "veggôet"! Mi veggôen, sdehcen allen n l$aci, ssagwden ten. Bbwin d ssêur anect ilat.');
INSERT INTO "ramdane" VALUES(7,117,'7:117','Nweêêa i Musa: "veggeô taâkwazt ik"! Apan tesseblaâ ayen d ûenâan.');
INSERT INTO "ramdane" VALUES(7,118,'7:118','Tûaô tidep, inqev wayen xeddmen.');
INSERT INTO "ramdane" VALUES(7,119,'7:119','Akka ay pwa$elben. U$alen ar deffir, d unqisen.');
INSERT INTO "ramdane" VALUES(7,120,'7:120','£lin iseêêaren, knan.');
INSERT INTO "ramdane" VALUES(7,121,'7:121','Nnan: "numen s Mass imavalen,');
INSERT INTO "ramdane" VALUES(7,122,'7:122','Mass n Musa akked Haôun".');
INSERT INTO "ramdane" VALUES(7,123,'7:123','Inna Ferâun: "day tumnem yis, uqbel ma âammde$ awen? Ayagi, war ccekk, d tifxep. Tundim p di temdint, iwakken a ppessuff$em, segs, imezda$ is. Ihi, deqqal a ppeéôem.');
INSERT INTO "ramdane" VALUES(7,124,'7:124','War ccekk, ad awen gezme$ afus ssya, avaô ssya. Sinna, ih, a kwen semmôe$ akw $ef imudag".');
INSERT INTO "ramdane" VALUES(7,125,'7:125','Nnan: "nekwni a nu$al ar Mass nne$.');
INSERT INTO "ramdane" VALUES(7,126,'7:126','Terriv d ppaô segne$, imi kan numen s issekniyen n Mass nne$, mi $ d usan. A Mass nne$! Azen a$ d ûûbeô, Ddem leôwaê nne$ d imvuâen".');
INSERT INTO "ramdane" VALUES(7,127,'7:127','Imvebbôen n ugdud n Ferâun, nnan: "day a ppeooev Musa akked ugdud is, ad ssefsaden di tmurt, a kwen ooen, keçç akked iôebbiten ik"? Inna: "a nene$ arrac nnsen, a nessider tilawin nnsen. Nekwni, sennig sen, d iqehhaôen".');
INSERT INTO "ramdane" VALUES(7,128,'7:128','Inna Musa i ugdud is: "ppret afus n Öebbi, u ûebôet. Tamurt, war ccekk, n Öebbi. A p iwôet win i S ihwan, deg imdanen iS. Taggara i wid ipêezziben".');
INSERT INTO "ramdane" VALUES(7,129,'7:129','Nnan: "njaf uqbel a $ d tasev, u deffir tisin ik". Inna: "amar Mass nnwen ad Ine$ acengu nnwen, a kwen Irr d igensas di tmurt, ad Iwali amek ara txedmem".');
INSERT INTO "ramdane" VALUES(7,130,'7:130','Newwet, d$a, lois n Ferâun s iseggwasen n llaé, akked nneqs n ppemôa, amar a d mmektin.');
INSERT INTO "ramdane" VALUES(7,131,'7:131','Ma iusa ten id ise$, ad inin: "nne$ wagi". Ma tusa ten id tucmip, a p sbibben i Musa akked wid illan yides. Ayen iveôôun yidsen, day maççi ar Öebbi kan? Maca, tegwti segsen ur ssinen.');
INSERT INTO "ramdane" VALUES(7,132,'7:132','Nnan: " wicqa assekni ara d tawiv, ur a$ tepseêêirev yis. Nekwni, ur neppamen yisek".');
INSERT INTO "ramdane" VALUES(7,133,'7:133','Nuzen asen aêemmal, ajôad, tilkin, imqweôqwôen, idammen, d issekniyen ifeûlen. Sme$wôen iman nnsen. Llan d agdud ibe$wviyen.');
INSERT INTO "ramdane" VALUES(7,134,'7:134','Mi tevôa yidsen twa$it, qqaôen: "a Musa! Nâaô Mass ik i nekwni, s wayen i k Iâahed. Ma tekksev a$ tawa$it, a namen yisek, a nazen yidek At Isôail".');
INSERT INTO "ramdane" VALUES(7,135,'7:135','Mi sen Nekkes tawa$it, ar lajel iten iggunin, atnan nitni xedâan.');
INSERT INTO "ramdane" VALUES(7,136,'7:136','Nerra d segsen ppaô, N$emq iten deg ill, imi skaddben issekniyen nne£, ur d celâen degsen.');
INSERT INTO "ramdane" VALUES(7,137,'7:137','Nessewôet i ugdud i illan qehôen t, igmav akked ituram n tmurt, i Neôqa. Itam wawal ûûam n Mass ik, $ef At Isôail, ilmend n wayen ûebôen. Nhudd ayen iûennaâ Ferâun akked ugdud is, u wayen bennun.');
INSERT INTO "ramdane" VALUES(7,138,'7:138','Nesâadda ill i At Isôail. Mmugren d agdud i iâabden tiâancuôin is. Nnan: "a Musa! Err ane$ ôebbi am iôebbiten nnsen". Inna: "kunwi, s tidep, d agdud ur nessin.');
INSERT INTO "ramdane" VALUES(7,139,'7:139','Wigi, s tidep, ad inger wayen ideg llan, ad inqev wayen xeddmen".');
INSERT INTO "ramdane" VALUES(7,140,'7:140','Inna: "day awen budde$ ôebbi, war Öebbi, u Neppa Ismenyaf ikwen $ef imavalen"?');
INSERT INTO "ramdane" VALUES(7,141,'7:141','Mi kwen Nsellek si lois n Ferâun, sukken fellawen aâaqeb uqsiê mavi; neqqen arrac nnwen, ssidiren tilawin nnwen. Deg waya, ajeôôeb s$uô Mass nnwen ameqwôaêan.');
INSERT INTO "ramdane" VALUES(7,142,'7:142','Nemlal akked Musa kravet wavan, Nerna yasen mraw. Tebbwev tallit n Mass is, kuzet wavan. Inna Musa i gma s Haôun: "enûef iyi ar ugdud iw, exdem ayen ilhan, ur teddu d ubrid ifessaden".');
INSERT INTO "ramdane" VALUES(7,143,'7:143','Mi d iusa Musa ar temlilit nne£, u Immeslay as Mass is, inna: "a Mass iw! Ssenâat iyi d, a K wali$". Inna d: "ur iyI tepwaliv, d awez$i, maca muqel s adrar. Ma iqqim deg umekkan is, ad iyI twaliv". Mi d Iusa Mass is s adrar, Irra t d saêel. I$li Musa, ixsef. Mi d iuki, inna: "gedha s yiseK! Utube$ K, nek d amezwaru n lmumnin".');
INSERT INTO "ramdane" VALUES(7,144,'7:144','Inna: "a Musa! Aql I Smenyafe$ k $ef imdanen, i tebôatin iW, i wawal iW. Ddem ayen i k d Fki$, ili seg wid isnemmiren".');
INSERT INTO "ramdane" VALUES(7,145,'7:145','Nura yas, di tlewêin, si yal cci, anabi akked ufeûûel n yal cci. Ddem itent s tezmert, ameô i ugdud ik a tent iîîef s lmaâna nnsent. Ad awen Ssekne$ tanezdu$t iballa$en.');
INSERT INTO "ramdane" VALUES(7,146,'7:146','Ad Veôôfe$, seg issekniyen iW, wid isme$wôen iman nnsen di tmurt, war ssebba. Mi walan yal assekni, ur ppamnen yis. Ma walan abrid uûwib, ur teddun degs. Ma walan abrid n le$wlav, a t awin. Ayagi, acku, s tidep, skaddben issekniyen nne£, ur d celâen degsen.');
INSERT INTO "ramdane" VALUES(7,147,'7:147','Wid iskaddben issekniyen nne£, akked temlilit n laxeôt, ur swin igiten nnsen. Day a ten kafin $ef wayen ur nelli d ayen xeddmen?');
INSERT INTO "ramdane" VALUES(7,148,'7:148','Agdud n Musa, deffir es, isbedd, s ôôêuê is, aâjmi bu loeppa isrugmuten. Day ur walan ara ar, s tidep, ur asen d ipmeslay, ur la ten inehhu s abrid? Âabden t, llan d imednas.');
INSERT INTO "ramdane" VALUES(7,149,'7:149','Mi ppuêeccmen, u walan ar, zi$ nnetlafen, nnan: "ma ur Iêun ara fella$ Mass nne$, ma ur a$ Isemmeê ara, war ccekk, a nu$al seg imexsuôen".');
INSERT INTO "ramdane" VALUES(7,150,'7:150','Mi d iu$al Musa ar ugdud is, s wurrif akked lêemûa, inna: "ay d ucmit wayen is iyi tneûfem, deffir iw! Teb$am a d i$iwwel lameô n Mass nnwen"? Iveggeô tilewêin, iîîef gma s seg uqeôôu, ijebt it id $uôes. Inna d: "a mmi s n yimma, s tidep, iêqeô iyi ugdud, qôib iyi n$an. Ur ssefôaê icenga yissi. Ur iyi pparra seg ugdud imednas".');
INSERT INTO "ramdane" VALUES(7,151,'7:151','Inna: "a Mass iw! Semmeê ane$, nek akked gma, Ssekcem a$ ar uêunu K. Keçç, Tifev akw wid ipêunun".');
INSERT INTO "ramdane" VALUES(7,152,'7:152','Ih, wid iâabden aâajmi, a d i$li fellasen wurrif n Mass nnsen, ad ndellen di tmeddurt n ddunit. Akka ay Nepqeîîi wid id isnulfuyen.');
INSERT INTO "ramdane" VALUES(7,153,'7:153','Wid ixedmen tucmitin, sinna, steôoâen deffir sent, u umnen, Mass ik, s tidep, deffir sent, ar Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(7,154,'7:154','Mi irs wurrif is, iddem ed Musa tilewêin. Di tira nnsent, nnhaya akked uêunu i wid ippagwaden Mass nnsen.');
INSERT INTO "ramdane" VALUES(7,155,'7:155','Ifren ed Musa ûatet irgazen seg ugdud is, ad mlilen yidne£. Mi ten tewwet zzenzla, inna: "a Mass iw! Lemmer i K ihwi, yili, iu$ lêal, Ten$iv ten, u Tegliv yissi. Day ad a$ Temêuv, ilmend n wayen xedmen ije$lufen segne$? Wagi d ahewwel iK kan, Tettellifev syis win i K ihwan, Tnehhuv win i K ihwan. Keçç d Bab nne$, Semmeê a$, Ëun fella$, Keçç Tifev akw wid ipsemmiêen.');
INSERT INTO "ramdane" VALUES(7,156,'7:156','Aru ya$ ise$ di ddunit, di laxeôt; aql a$ nu$al ed $uôeK". Inna d: "aâaqeb iW, a t Sâaddi$ $ef win iyI ihwan. Aêunu W ijemmel yal cci. A t Aru$ i wid ipêezziben, ipzekkin, akked wid iumnen s issekniyen nne£,');
INSERT INTO "ramdane" VALUES(7,157,'7:157','wid iteddun d Umazan, Nnbi ur ne$ôi, i ufan iura $uôsen di Tuôa akked Linoil". Iumeô asen d lqanun, Itekkes asen ayen n dir. Iêell asen lâali, igdel asen dduni. Ikkes asen aruz akked lqid nnsen. Ihi, wid iumnen yis, i t iâawnen, i t isellken, iddan d tafat id irsen yides, widak d imerbaê.');
INSERT INTO "ramdane" VALUES(7,158,'7:158','Ini: "a yimdanen! Nek d Amazan n Öebbi $uôwen meôôa. Neppa, ineS tagelda n tmurt akked igenwan. Ur illi ôebbi siwa Neppa. Issidir, Ineqq". Ihi, amnet s Öebbi akked Umazan iS, Nnbi ur ne$ôi, i iumnen s Öebbi akked wawal iS. Ddut yides, amar a ppepwanhum.');
INSERT INTO "ramdane" VALUES(7,159,'7:159','Deg ugdud n Musa, arbaâ iteddu s tidep. U syis ay pêuddun.');
INSERT INTO "ramdane" VALUES(7,160,'7:160','Nessemfaôaq iten $ef mrawsin laâôac, n leoyus. Mi s issuter ugdud is tissit, Nweêêa i Musa: ewwet aéôu s tâakwaz ik. F$ent ed segs mrawsnat tliwa; akka, yal amdan iéôa anda ara issew. Nesbur iten s usigna, Nessers asen d lmenna akked ifeôôuoen: "eççet seg wayen ilhan, is ikwen N$at". Ur a£ dnisen, maca la ddennisen iman nnsen.');
INSERT INTO "ramdane" VALUES(7,161,'7:161','Mi pwanecden: "zed$et di temdint agi, eççet segs, ayen i wen ihwan, init: "laâfu"! Âaddit di tebburt, s lekni. Ad awen Nsemmeê tuccva nnwen. A Nernu i lâuûam".');
INSERT INTO "ramdane" VALUES(7,162,'7:162','Wid idensen segsen, neûfen awalen s wid ur nelli d wid i sen nnan. Nuzen asen tiyta seg igenni, ilmend n wayen ddennisen.');
INSERT INTO "ramdane" VALUES(7,163,'7:163','Steqsi ten $ef temdint i illan $ef rrif n yill, mi ur d rzin di ssebt, mi ten id usan iselman nnsen, walan ten, ass n ssebt. Ass, war ssebt, ur ten id ppasen. Akka ay ten Njeôôeb, deg wayen ssexsaôen.');
INSERT INTO "ramdane" VALUES(7,164,'7:164','Mi d inna lois, segsen: "iwumi la tepnabim agdud ara Isqucc Öebbi, ne$ ara Iâaqeb s leqseê"? Nnan: "d aste$feô ar Mass nnwen, amar ad êezzben".');
INSERT INTO "ramdane" VALUES(7,165,'7:165','Mi ppun ayen i$ef pwafekkôen, Nsellek wid itekksen ayen n dir, Nesâadda aâaqeb adiran $ef imednas, ilmend n wayen ssexsaôen.');
INSERT INTO "ramdane" VALUES(7,166,'7:166','Mi sme$wôen iman nnsen $ef wayen i sen ipwakksen, Nenna yasen: "ilit d uddawen ucmiten"!');
INSERT INTO "ramdane" VALUES(7,167,'7:167','Mi Ixebbeô Mass ik, ard asen d Iazen ara ten iâaqben, s ééuô, alamma d ass n tnekra. Mass ik, s tidep, Ip$awal ar uâaqeb. Neppa, s tidep, Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(7,168,'7:168','Nessemfaôaq iten di tmurt, d leoyus, garasen wid imaânen, garasen wid ur nemâin. Njeôôeb iten s ise$iyen akked tucmitin, amar a d u$alen s abrid.');
INSERT INTO "ramdane" VALUES(7,169,'7:169','Neûfen ten, deffir sen, ineûûafen i iweôten Tazmamt. Ppaddamen lerbaê n ddunit, qqaôen: "ad a$ semmêen". Ma rnan usan ten id lerbaê, am widen, a ten ddmen. Day ur âahden ara s Tezmamt, ard tidep kan, ara inin $ef Öebbi? U lemden i illan degs. Ppif axxam aneggaru, i wid ipêezziben. Day ur tfehmem ara?');
INSERT INTO "ramdane" VALUES(7,170,'7:170','Wid iîîfen di Tezmamt, ipéallan, s tidep, ur Nveggiâ arraz n lâuûam.');
INSERT INTO "ramdane" VALUES(7,171,'7:171','Mi Nessuli adrar sennig sen, amzun d tili, $ilen, s tidep, ara d i$li fellasen. Ïîfet nezzeh ayen i wen Nefka, smektit ed ayen illan degs, amar a pêezzbem.');
INSERT INTO "ramdane" VALUES(7,172,'7:172','Mi d Iddem Mass ik, si tgeééal n tarwa n Adem, dderya nnsen, Inna yasen a d cehhden $ef yiman nnsen: "day ur Lli$ ara d Mass nnwen"? Nnan: "ih, ncehhed". Akka ur d teqqaôem ara, ass n tnekra: "s tidep, ayagi d ayen ur neéôi".');
INSERT INTO "ramdane" VALUES(7,173,'7:173','Ne$ a d tinim: "d imezwura nne$ kan i icurken, zik. Nekwni d tarwa deffir sen. Day a$ Ten$ev, ilmend n wayen xedmen wid iôejjnen"?');
INSERT INTO "ramdane" VALUES(7,174,'7:174','Akka ay Nfeûûel issekniyen, amar a d u$alen s abrid.');
INSERT INTO "ramdane" VALUES(7,175,'7:175','Eêku yasen d tadyant n win iwumi Nefka issekniyen nne£. Irra ten akin. Idda yides Cciîan, iu$al seg wid i$eôqen.');
INSERT INTO "ramdane" VALUES(7,176,'7:176','Lemmer i £ ihwi, Nessuli t yissen. Maca, ib$a tamurt, idda d lhawiyya s. Ihi, amedya s am win n waydi, ma teddiv p fellas, ad ilehhet, ma teooiv t, ad ilehhet. D wagi ay d amedya n ugdud i iskaddben issekniyen nne£. Eêku d tadyant, amar a d mmektin.');
INSERT INTO "ramdane" VALUES(7,177,'7:177','Agdud iskaddben issekniyen nne£, d yir amedya. Pddennisen iman nnsen.');
INSERT INTO "ramdane" VALUES(7,178,'7:178','Win Inha Öebbi, ihi, idda d ubrid. Wid Itlef Öebbi, nitni d imexsuôen.');
INSERT INTO "ramdane" VALUES(7,179,'7:179','Ni$ Neswejd ed, i Loahennama, aîas n leonun akked yimdanen. Sâan ulawen, ur fehhmen yissen; sâan allen, ur pwalin yissent; sâan imeééu$en, ur sellen yissen. Nitni am lmal, ne$ nnetlafen kteô. Wigi d i$wefliyen.');
INSERT INTO "ramdane" VALUES(7,180,'7:180','I Öebbi, ismawen ûûam. Naâôet eP, yissen. Veôôfet seg wid issexsaôen ismawen iS. Ad ppuqeîîin $ef wayen xeddmen.');
INSERT INTO "ramdane" VALUES(7,181,'7:181','Deg wayen Nexleq, yiwen n lois ivfeô tidep. Ipêuddu syis.');
INSERT INTO "ramdane" VALUES(7,182,'7:182','Wid iskaddben issekniyen nne£, a ten Nawi, cwiî, cwiî, ar wanda ur éôin.');
INSERT INTO "ramdane" VALUES(7,183,'7:183','Ad asen Fke$ lmudda. Ih, ticeôkep iW d tunûiêt.');
INSERT INTO "ramdane" VALUES(7,184,'7:184','Day ur d tepmeyyizem ara? Ur illi umeddakwel nnwen d aôehbani. Neppa, d aneddaô kan ipbegginen.');
INSERT INTO "ramdane" VALUES(7,185,'7:185','Day ur walan ara tagelda n tmurt, igenwan, akked wayen Ixleq Öebbi n cci? Ni$, ahat, iqeôb ed lajel nnsen. Anwa awal, deffir wagi, ar amnen?');
INSERT INTO "ramdane" VALUES(7,186,'7:186','Win Itlef Öebbi, ur Isâi nnahi. A ten Ioo ad hemlen di tjehli nnsen.');
INSERT INTO "ramdane" VALUES(7,187,'7:187','A k steqsayen $ef yimir, melmi ara d yili. Ini: "i t Iéôan siwa Mass iw. D Neppa kan a t id Isseknen, di lweqt is. Ad iaéay di tmurt akked igenwan. Ur kwen id ippawev siwa deqzalla". A k steqsayen amzun tesâiv asali fellas. Ini: "i t Issnen siwa ÔÖebbi", maca tegwti n yimdanen ur ssinen.');
INSERT INTO "ramdane" VALUES(7,188,'7:188','Ini: "ur ksibe$ i iman iw, aba$uô ne$ vveôô, kkes ed ayen Irad Öebbi. Lemmer ssine$ tabavnit, ad ôwu$ lxiô, ur iyi ipnal ixmiô. Nek d aneddaô kan, u d abcir, i ugdud iumnen".');
INSERT INTO "ramdane" VALUES(7,189,'7:189','D Neppa ikwen id Ixelqen si yiwen n yiman. Iddem ed segs tameîîut is, ad izde$ yides. Mi insa yides, tebbwi tadist tafessast; telêa yis. Mi d teûbeê ééayet, naâôen Öebbi: "a Mass nne$! Ma Tefkiv a$ d aâeôvi, war ccekk, a nili seg wid isnemmiren".');
INSERT INTO "ramdane" VALUES(7,190,'7:190','Mi sen d Ifka aâeôdi, rran aS icriken s wayen i sen d Ifka. I aâlay Öebbi, sennig wayen pcaraken!');
INSERT INTO "ramdane" VALUES(7,191,'7:191','Day ad carken ayen ur nexliq acemma, u d nitni i ipwaxelqen?');
INSERT INTO "ramdane" VALUES(7,192,'7:192','Ur zmiren a ten âiwnen, ne$ ad âiwnen iman nnsen.');
INSERT INTO "ramdane" VALUES(7,193,'7:193','Ma tnecdem ten ar nnhaya, ur teddun yidwen. Iâuva fellawen ma tnecdem ten, ne$ ma tessusmem.');
INSERT INTO "ramdane" VALUES(7,194,'7:194','S tidep, wigi tnaâôem, war Öebbi, d imdanen am kunwi. Naâret pen, ma ad awen d rren, maday tessawalem tidep.');
INSERT INTO "ramdane" VALUES(7,195,'7:195','Day sâan ivaôen, ad lêun fellasen, ne$ sâan ifassen, ad ddmen yissen, ne$ sâan allen, ad walin yisent, ne$ sâan imeééu$en, ad slen yissen. Ini: "naâret icriken nnwen, sinna, andit iyi. Ur iyi sganayet".');
INSERT INTO "ramdane" VALUES(7,196,'7:196','Ih, Bab iw d Öebbi id Issersen Tazmamt, i Iêemmlen lâuûam.');
INSERT INTO "ramdane" VALUES(7,197,'7:197','Wid tnaâôem, war Neppa, ur zmiren a kwen âawnen, ne$ ad âawnen iman nnsen.');
INSERT INTO "ramdane" VALUES(7,198,'7:198','Ma tnecdem ten ar nnhaya, ur sellen. A ten twaliv a k id pmuqulen, nitni ur pwalin.');
INSERT INTO "ramdane" VALUES(7,199,'7:199','Ddem ayen ilhan, ameô ar lewqama, veôôef i wid ur nessin.');
INSERT INTO "ramdane" VALUES(7,200,'7:200','Ma ilaâj ik Cciîan ar dduni, ppkel $ef Öebbi. Neppa, s tidep, Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(7,201,'7:201','Ih, wid ipêezziben, ma innul iten urbaâ n ccwaîen, a d mmektin, d$a ad asen llint wallen.');
INSERT INTO "ramdane" VALUES(7,202,'7:202','Zgan $elîen atmaten nnsen, d$a nitni ur xevvun.');
INSERT INTO "ramdane" VALUES(7,203,'7:203','Ma ur asen d tebbwiv ara assekni, ad ak inin: "acimi ur t id isnulfa ara"? Ini: "teddu$ kan d wayen iyi d ippuweêêan, s$uô Mass iw". Wagi, s$uô Mass nnwen, d tafat, d nnhaya, d aêunu, i ugdud iumnen.');
INSERT INTO "ramdane" VALUES(7,204,'7:204','Ma qqaôen Leqwôan ssemêesset as, ssusmet, amar fellawen aêunu.');
INSERT INTO "ramdane" VALUES(7,205,'7:205','Mmekti d Mass ik, deg iman ik, s wannuz akked lxuf, s wawal irsen, taûebêit, tameddit. Ur ppili seg i$wefliyen.');
INSERT INTO "ramdane" VALUES(7,206,'7:206','Ih, wid illan ar Mass ik, ur sme$wôen ara iman nnsen, $ef uâbad iS. Sbuccuâen T, i Neppa kennun.');
INSERT INTO "ramdane" VALUES(8,1,'8:1','Steqsan k $ef rrbeê. Ini: "rrbeê i Öebbi akked Umazan. Ihi, êezzbet i Öebbi, elhut garawen, a$et awal i Öebbi akked Umazan iS, ma d lmumnin ay tellam".');
INSERT INTO "ramdane" VALUES(8,2,'8:2','Lmumnin d wid kan iwumi ippergigi wul nnsen, ma Ipwabder ed Öebbi. Ma $ôan asen issekniyen iS, ad innerni liman nnsen. £ef Mass nnsen, ay ppeklen.');
INSERT INTO "ramdane" VALUES(8,3,'8:3','Wid ibedden i téallit, ipûeôôifen seg wayen is iten N$at.');
INSERT INTO "ramdane" VALUES(8,4,'8:4','Wigi d ûûeê, d lmumnin. I nitni, ar Mass nnsen, tiseddaôin, ssmaê, akked l$it uùkyis.');
INSERT INTO "ramdane" VALUES(8,5,'8:5','Akken ik id Issuffe$ Mass ik, seg uxxam ik, s tidep, wanag ur ib$i urbaâ si lmumnin.');
INSERT INTO "ramdane" VALUES(8,6,'8:6','La k pnamaren $ef tidep, deffir ubeggen is, amzun kan pwanehôen ar tmeppant, nitni pêekkiôen.');
INSERT INTO "ramdane" VALUES(8,7,'8:7','Mi wen Iâahed Öebbi, ara ppili nnwen, yiwet si snat n trebbuyaâ, tessarmem a ppili nnwen tin ur nepwaselleê. Öebbi Ib$a a d itebbet tidep s wawalen iS, ad ôéen wakali ijehliyen.');
INSERT INTO "ramdane" VALUES(8,8,'8:8','A ppili tidep, ad inqev lbaîel, xas ugin ibe$wviyen.');
INSERT INTO "ramdane" VALUES(8,9,'8:9','Mi tepprem afus s$uô Mass nnwen, Ijaweb awen d: "Nek ad awen Fke$ agim lmalayek, mestebâant".');
INSERT INTO "ramdane" VALUES(8,10,'8:10','Irra t kan Öebbi d abecceô iwakken ad nnevmanen yis wulawen nnwen. Ur illi l$elb, siwa s$uô Öebbi. Öebbi, s tidep, d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(8,11,'8:11','Mi kwen I$umm s yives, d îîmana s$uôeS; Isse$li d fellawen aman seg igenni; a kwen Izzizdeg yissen, ad Issebâad, fellawen, a$way n Cciîan, ad Isseohed ulawen nnwen, ad Itebbet, yissen, isurifen.');
INSERT INTO "ramdane" VALUES(8,12,'8:12','Mi Iweêêa Mass ik i lmalayek: "Nek yidkwent; tebbtemt wid iumnen. Ad Skecme$ tugdi deg ulawen n wid ijehlen. Ewwtemt sennig tmegrav, ewwtemt ar ixfawen ivudan".');
INSERT INTO "ramdane" VALUES(8,13,'8:13','Ayagi, acku nitni, s tidep, xuûmen Öebbi akked Umazan iS. I win ixuûmen Öebbi akked Umazan iS, ihi, Öebbi Iwâaô deg uâaqeb.');
INSERT INTO "ramdane" VALUES(8,14,'8:14','Wagi i kunwi, jeôôbet ep! U i ijehliyen, aâaqeb n tmes.');
INSERT INTO "ramdane" VALUES(8,15,'8:15','A wid iumnen! Ma temmugrem d wid ijehlen la d éeddmen, ur sen reggwlet.');
INSERT INTO "ramdane" VALUES(8,16,'8:16','Win i sen irewlen, assen, siwa ma la issewjad i umennu$, ne$ ma ikcem s arbaâ, winna, a t immager wurrif n Öebbi; axxam is d Loahennama, a yir tu$alin!');
INSERT INTO "ramdane" VALUES(8,17,'8:17','Ur ten tennu$em, maca d Öebbi iten Innu$en. Ur teôoimev mi tôeomev, maca Öebbi Iôoem, iwakken ad Ijeôôeb lmumnin s ujeôôeb umâin s$uôeS. Öebbi, s tidep, Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(8,18,'8:18','Akka, Ih, Öebbi Issadar ticeôkep ijehliyen.');
INSERT INTO "ramdane" VALUES(8,19,'8:19','Tessutrem l$elb, atan iusa kwen id. Ma tfukkem, ppif i kunwi. Ma tu$alem, a Nu$al. Ula iwumi tent, bippa, trebbuyaâ nnwen, xas îîuqtent. Ih, Öebbi Idda d lmumnin.');
INSERT INTO "ramdane" VALUES(8,20,'8:20','A wid iumnen! A$et awal i Öebbi akked Umazan iS. Ur T ppaooat, u kunwi tsellem.');
INSERT INTO "ramdane" VALUES(8,21,'8:21','Ur ppilit am wid innan: "nesla", nitni ur sellen.');
INSERT INTO "ramdane" VALUES(8,22,'8:22','£eô Öebbi, s tidep, ineggura imexlaq d iâeééugen, d igugam, ur nfehhem.');
INSERT INTO "ramdane" VALUES(8,23,'8:23','Lemmer Iéôi Öebbi degsen lxiô, yili Irra ten sellen. Ma Irra ten sellen, war ccekk, ad akwren avaô, u nitni aâûan.');
INSERT INTO "ramdane" VALUES(8,24,'8:24','A wid iumnen! Jawbet i Öebbi akked Umazan, ma Inced ikwen ar wayen ikwen issidiren. Cfut ar Öebbi Ippili ger umdan akked wul is. £uôeS, Neppa, s tidep, ara tepwafessrem.');
INSERT INTO "ramdane" VALUES(8,25,'8:25','Ëezzbet i uhewwel. Ur ipêaz ara kan imednas segwen, weêêedsen. Cfut ar Öebbi Iqseê deg uâaqeb.');
INSERT INTO "ramdane" VALUES(8,26,'8:26','Mmektit ed mi, kunwi, drus ay tellam, d uôhifen di tmurt, teppagwadem a kwen xevfen medden. Iûûeô ikwen, Isellek ikwen s l$elb iS, I$at ikwen s wayen ilhan, amender ma a ppesnemmrem.');
INSERT INTO "ramdane" VALUES(8,27,'8:27','A wid iumnen! Ur pxeyyinet Öebbi akked Umazan, ur pxeyyinet lamanat nnwen, kunwi teéôam.');
INSERT INTO "ramdane" VALUES(8,28,'8:28','Cfut ar, ayla nnwen akked tarwa nnwen, d ahewwel kan, u ar £uô Öebbi, ôôaîeb ameqwôaêan.');
INSERT INTO "ramdane" VALUES(8,29,'8:29','A wid iumnen! Ma têezzbem i Öebbi, ad awen Ifk afraz, ad awen Imêu tucmitin nnwen, ad awen Isemmeê. Öebbi d Bab n lfevl ameqwôaêan.');
INSERT INTO "ramdane" VALUES(8,30,'8:30','Ma ppandin ak, wid ijehlen, iwakken a k êebsen, ne$ a k n$en, ne$ a k ssuff$en; ppandin u Öebbi Ippandi. Öebbi Yif akw imendayen.');
INSERT INTO "ramdane" VALUES(8,31,'8:31','Ma $ôan asen issekniyen nne£, ad inin: "ih, nesla. Ma ihwa ya$ a d nini am ayagi. Ayagi, s tidep, siwa d timucuha imezwura".');
INSERT INTO "ramdane" VALUES(8,32,'8:32','Mi nnan: "a Öebbi, ma illa wagi d tidep s$uôeK, Sse$li d, fella$, id$a$en seg igenni, ne$ Awi a$ d aâaqeb aqeôêan".');
INSERT INTO "ramdane" VALUES(8,33,'8:33','Ur illi Öebbi d win ara ten Iâaqben, skud telliv garasen. Ur Illi Öebbi d win ara Ipâaqben ma nitni la suturen ssmaê.');
INSERT INTO "ramdane" VALUES(8,34,'8:34','Acimi ur ten Ipâaqab ara Öebbi, u nitni swexxiôen si Loamaâ n Lêaôam, ur llin d imawlan is. Imawlan is, d wid kan ipêezziben. Maca, tegwti segsen ur éôin.');
INSERT INTO "ramdane" VALUES(8,35,'8:35','Taéallit nnsen deg Uxxam, siwa aseffeô akked llir. Jeôôbet aâaqeb, $ef wayen tjehhlem.');
INSERT INTO "ramdane" VALUES(8,36,'8:36','Ih, wid ijehlen pûeôôifen ayla nnsen, iwakken ad veôôfen seg ubrid n Öebbi. A t ûeôôfen, ad asen iu$al d lexsaôa. Sinna, ad ppu$elben. Wid ijehlen ad pwafessren di Loahennama.');
INSERT INTO "ramdane" VALUES(8,37,'8:37','Iwakken ad Ifrez Öebbi dduni d lâali, ad Irr ixabiten, wa $ef wa, a ten Izdi akw, a ten Irr ar Loahennama. Widak d imexsuôen.');
INSERT INTO "ramdane" VALUES(8,38,'8:38','Ini i wid ijehlen: "ma fukken, ad awen Isemmeê ayen iâaddan; ma u$alen, ihi, akken tevôa d imezwura".');
INSERT INTO "ramdane" VALUES(8,39,'8:39','Nna$et pen alamma tfukk lfetna, u iu$al ddin s lekmal is, i Öebbi. Ma êebsen, Öebbi, s tidep, Ipwali ay xeddmen.');
INSERT INTO "ramdane" VALUES(8,40,'8:40','Ma wexxôen, cfut ar Öebbi d Bab nnwen, Bab amxalef, Asellak amxalef.');
INSERT INTO "ramdane" VALUES(8,41,'8:41','Akken ib$u yili wayen id t$eltem n cci, cfut amur wis smus n Öebbi akked Umazan iS, akked llzem, igujilen, ime$ban, win d ibbwi ubrid; ma illa tumnem s Öebbi akked wayen id Nessers i umdan nne£, ass n ufraz, ass ideg mlalent snat n trebbuyaâ. Öebbi Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(8,42,'8:42','Mi tellam di loiha taqôibt, u nitni agwemmav, imnayen seddawat wen, xas temyefkem ppaâud, tzeglem timlilit. Maca, iwakken ad Isâaddi Öebbi lameô ara iâaddin, ad immet win immuten, s ubeggen, ad yidir win idren, s ubeggen. Öebbi, s tidep, Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(8,43,'8:43','Mi k ten id Issenâat Öebbi, d takwemmict, di targit ik ... Lemmer ik ten id Issenâat qwan, war ccekk, a pfeclem, a ppemnamarem $ef lameô. Maca, Öebbi Ijbeô ikwen. Ih, Neppa Issen i illan deg idmaren.');
INSERT INTO "ramdane" VALUES(8,44,'8:44','Mi wen ten Issenâat, mi ten temmugrem, twalam ten d takwemmict. Iééeméi kwen ar tmu$li nnsen, iwakken ad Isâaddi Öebbi lameô ara iâaddin. Ar Öebbi ppu$alen lumuô.');
INSERT INTO "ramdane" VALUES(8,45,'8:45','A wid iumnen! Ma temmugrem d arbaâ, tebbtet, mmektit ed aîas Öebbi, amar a prebêem.');
INSERT INTO "ramdane" VALUES(8,46,'8:46','A$et awal i Öebbi akked Umazan iS, ur ppemnamaret. Mulac a pfeclem, a pnegfem. Üebôet! S tidep, Öebbi ar iûebôiyen.');
INSERT INTO "ramdane" VALUES(8,47,'8:47','Ur ppilit am wid id iff$en s tem$weô, seg ixxamen nnsen, iwakken a ten id walin medden. Swexxiôen seg ubrid n Öebbi. Öebbi Izzi i wayen xeddmen.');
INSERT INTO "ramdane" VALUES(8,48,'8:48','Mi sen icebbeê Cciîan igiten nnsen, u inna: "ur kwen i$elleb yiwen seg imdanen, ass agi. Nek d amalal nnwen". Mi mwalant snat trebbuyaâ, iuker avaô, inna: "nek xvi$ kwen, nek wala$ ayen ur twalam, nek ugade$ Öebbi. Öebbi Iqseê deg uâaqeb".');
INSERT INTO "ramdane" VALUES(8,49,'8:49','Mi nnan ixabiten akked imehzulen n wul: "wigi, i$uôô iten ddin nnsen". Win ippeklen $ef Öebbi, ihi, Öebbi d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(8,50,'8:50','Lemmer a pwaliv mara ddment lmalayek leôwaê n wid ijehlen, ad kkatent udmawen nnsen, izugar nnsen. "Jeôôbet aâaqeb n tmeô$iwt!');
INSERT INTO "ramdane" VALUES(8,51,'8:51','Ayagi ilmend n wayen d bbwin ifassen nnwen", u Öebbi ur pdennis imdanen.');
INSERT INTO "ramdane" VALUES(8,52,'8:52','Am tikli n ugdud n Ferâun akked wid i ten id izwaren. Nekôen issekniyen n Öebbi. Issemêaleq asen Öebbi s ibekkaven nnsen. Ih, Öebbi d Uzmir, Iqseê deg uâaqeb.');
INSERT INTO "ramdane" VALUES(8,53,'8:53','Akka, acku Öebbi ur Ipbeddil l$it is I$at agdud, alamma bedlen i illan deg iman nnsen. Öebbi Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(8,54,'8:54','Am tikli n ugdud n Ferâun akked wid i ten id izwaren. Skaddben issekniyen n Mass nnsen. Nen$a ten s ibekkaven nnsen, Ne$meq agdud n Ferâun; llan akw d imednas.');
INSERT INTO "ramdane" VALUES(8,55,'8:55','£uô Öebbi, imexlaq ucmiten mavi, d wid ijehlen, d$a ur uminen,');
INSERT INTO "ramdane" VALUES(8,56,'8:56','wid ukud tgiv lemâahda, sinna, di yal tikelt, neqven lemâahda nnsen, u nitni ur pêezziben.');
INSERT INTO "ramdane" VALUES(8,57,'8:57','Ma tugarev ten di îôad, sserwel yissen wid i ten id ivefôen, amar a d mmektin.');
INSERT INTO "ramdane" VALUES(8,58,'8:58','Ma tugadev lexdaâ seg ugdud, err iten akin, s tezdeg. Öebbi, s tidep, ur Iêemmel ara ixeddaâen.');
INSERT INTO "ramdane" VALUES(8,59,'8:59','Ur p$ilin menâan, wid ijehlen. Nitni, s tidep, ur zmiren.');
INSERT INTO "ramdane" VALUES(8,60,'8:60','Heggit asen d ayen iwumi tzemrem n lemêella akked yimnayen, a ppessagwdem yissen acengu n Öebbi, acengu nnwen, akked wiyav, war segsen, ur tessinem; Öebbi Issen iten. Ayen tseddqem n cci, deg ubrid n Öebbi, ad awen d iu$al. Kunwi ur tepwadensem.');
INSERT INTO "ramdane" VALUES(8,61,'8:61','Ma nudan talwit, nadi ep. Ppkel $ef Öebbi. Neppa, s tidep, Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(8,62,'8:62','Ma b$an a k xedâan, ihi, âammed $ef Öebbi, Neppa i k Ifkan afus s tallalt iS, akked lmumnin.');
INSERT INTO "ramdane" VALUES(8,63,'8:63','Issemlal ulawen nnsen. Lemmer a pûeôfev i illan akw di tmurt, ur tessemliliv ulawen nnsen. Maca, Öebbi Issemlal iten. Neppa, s tidep, d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(8,64,'8:64','A Nnbi! Âammed $ef Öebbi, keçç, akked wid iddan yidek si lmumnin.');
INSERT INTO "ramdane" VALUES(8,65,'8:65','A Nnbi! Eg lêir i lmumnin, ad nna$en. Ma llan segwen warem iûebôiyen, ad rnun snat twinas. Ma llan segwen twinas, ad rnun agim n wid ijehlen, acku, nitni d agdud ur nfehhem.');
INSERT INTO "ramdane" VALUES(8,66,'8:66','Tura, Issafes Öebbi fellawen. Iéôa tella garawen rrehma. Ihi, ma llan segwen twinas iûebôiyen, ad rnun snat twinas, ma llan segwen agim, ad rnun sin igima, ma Irad Öebbi. Öebbi ar iûebôiyen.');
INSERT INTO "ramdane" VALUES(8,67,'8:67','Ur issefk ara i Nnbi ad isâu imeêbas, alamma pwa$elben di tmurt. Teb$am tukci n ddunit. Öebbi Ib$a laxeôt. Öebbi d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(8,68,'8:68','Lemmer maççi d ayen iuran s$uô Öebbi, izwar ed, yili a kwen iwwet uâaqeb abarar, ilmend n wayen tebbwim.');
INSERT INTO "ramdane" VALUES(8,69,'8:69','Eççet ayen ilhan, iêellen, seg wayen i t$elltem, êezzbet i Öebbi. Öebbi, s tidep, Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(8,70,'8:70','A Nnbi! Ini i wid teîîfem seg imeêbas: "ma Iufa Öebbi lxiô deg ulawen nnwen, ad awen Ifk ayen yifen ayen i wen ipwakksen, ad awen Isemmeê. Öebbi Ipsemmiê, Ipêunu".');
INSERT INTO "ramdane" VALUES(8,71,'8:71','Ma b$an a k xedâan, ni$ iu$ lêal xedâen Öebbi. D$a, Ikkes asen ôôay. Öebbi Iéôa, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(8,72,'8:72','Ih, wid iumnen, iunagen, ijuhden s wayla nnsen, akked yiman nnsen, deg ubrid n Öebbi, wid isnubgten u fkan afus, widak d imeddukwal bwaygarasen. Wid iumnen ur unagen, ur awen llin d imeddukwal, s wacemma, alamma unagen. Ma ppren afus nnwen di ddin, iwata yawen ad asen tefkem afus, siwa mgal agdud ukud tâaqdem. Öebbi Ipwali ayen txeddmem.');
INSERT INTO "ramdane" VALUES(8,73,'8:73','Wid ijehlen d imeddukwal bwaygarasen. Ma ur tgim ara akka, a ppili lfetna di tmurt, akked usefsed ameqwôan.');
INSERT INTO "ramdane" VALUES(8,74,'8:74','Wid iumnen, iunagen, ijuhden deg ubrid n Öebbi, wid isnubgten u fkan afus, widak, s tidep, d lmumnin. I nitni ssmaê akked l$it ukyis.');
INSERT INTO "ramdane" VALUES(8,75,'8:75','Wid iumnen syin $er da, u unagen, u juhden yidwen, widak segwen. Llzem ppemyilin kteô di Tezmamt n Öebbi. Öebbi, s tidep, Izôa yal cci.');
INSERT INTO "ramdane" VALUES(9,1,'9:1','Öebbi akked Umazan iS kksen aêeppem i imcurak ukud tâaqqdem.');
INSERT INTO "ramdane" VALUES(9,2,'9:2','Ddut di tmurt kué wagguren. Cfut ar kunwi ur tesfunnugem i Öebbi, u Öebbi ad Isdull ijehliyen.');
INSERT INTO "ramdane" VALUES(9,3,'9:3','Aberreê i medden, s$uô Öebbi akked Umazan iS, ass n êioo ameqwôaêan, ar Öebbi akked Umazan iS, kksen aêeppem i imcurak. "Ma testeôoâam, ppif i kunwi, ma twexxôem, cfut ar kunwi ur tesfunnugem i Öebbi". Becceô wid ijehlen s uâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(9,4,'9:4','Kkes ed wid ukud tâaqqdem seg imcurak, sinna ur kwen bbwiven s wacemma, ur ullen yiwen mgal nnwen. Ïîfet di lemâahda nnsen ar lmudda s. Öebbi, s tidep, Iêemmel wid ipêezziben.');
INSERT INTO "ramdane" VALUES(9,5,'9:5','Mi âaddan wagguren iôqan, nna$et imcurak anda ten tufam. Ïîfet pen, êeôset pen, andit asen yal taxettalt. Ma steôoâan, ééulen, zekkan, ooet asen abrid. Öebbi, s tidep, Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(9,6,'9:6','Ma issuter ak laânaya, yiwen seg imcurak, êareb fellas, ard issel i wawal n Öebbi, sinna, ssiwev it s amkan is n laman. Akka, acku nitni, s tidep, d agdud ur nessin.');
INSERT INTO "ramdane" VALUES(9,7,'9:7','Amek ara tili i imcurak, lemâahda s$uô Öebbi u s$uô Umazan iS, siwa wid tâahdem ar Loamaâ n Lêaôam. Skud sweqmen yidwen, sweqmet yidsen. Öebbi, s tidep, Iêemmel wid ipêezziben.');
INSERT INTO "ramdane" VALUES(9,8,'9:8','Amek, mi kwen $elben, ur kwen qudôen ilmend n yi$es, ne$ n lemâahda. Öuvan kwen s imawen nnsen; ulawen nnsen ugin. Tegwti segsen d iballa$en.');
INSERT INTO "ramdane" VALUES(9,9,'9:9','U$en s issekniyen n Öebbi ayen ôxisen. Ssefôa$en $ef ubrid iS. S tidep, ay dir wayen xeddmen nitni!');
INSERT INTO "ramdane" VALUES(9,10,'9:10','Ur qudôen lmumen ilmend n yi$es, ne$ n lemâahda. Wigi, ih, d wid izegren tilas.');
INSERT INTO "ramdane" VALUES(9,11,'9:11','Ma steôoâan, ééulen, zekkan, d atmaten nnwen di ddin. Nepfeûûil issekniyen i ugdud issnen.');
INSERT INTO "ramdane" VALUES(9,12,'9:12','Ma sêenten tagalit nnsen, deffir lemâahda nnsen, u wwten di ddin nnwen, nna$et iqweôôa n tijehli. Nitni, s tidep, ur ppaîafen di tgallit. Amar ad fukken.');
INSERT INTO "ramdane" VALUES(9,13,'9:13','Day ur tepna$em ara agdud i isêenten tagalit nnsen, u ib$an ad ssuff$en Amazan, u nitni heomen kwen d imezwura? Day ten tugadem? D Öebbi i iklalen a T tagwadem, ma d lmumnin ay tellam.');
INSERT INTO "ramdane" VALUES(9,14,'9:14','Nna$et pen, a ten Iâaqeb Öebbi s ufus nnwen, a ten Ixzu, a kwen Iserbeê fellasen, ad Icfu idmaren n ugdud n lmumnin.');
INSERT INTO "ramdane" VALUES(9,15,'9:15','Ad Ikkes urrif n wulawen nnsen. Öebbi Issuruf i win i S ihwan. Öebbi d Amusnaw, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(9,16,'9:16','Day t$ilem a ppepwaterkem, wanag ur Iéôi Öebbi wid, degwen, ijuhden, ur neddi d imawlan, war Öebbi akked Umazan iS, akked lmumnin? Öebbi Iéôa ay txeddmem.');
INSERT INTO "ramdane" VALUES(9,17,'9:17','Ur issefk ara i imcurak ad kecmen leowamaâ n Öebbi, ad cehhden $ef tijehli nnsen. Widak neqven igiten nnsen, di tmes ad wekkôen.');
INSERT INTO "ramdane" VALUES(9,18,'9:18','Ara ikecmen kan leowamaâ n Öebbi, d wid iumnen s Öebbi akked wass aneggaru, iééulen, izekkan, ippagwaden siwa Öebbi. Ahat wigi ad ilin seg wid ipwanhan.');
INSERT INTO "ramdane" VALUES(9,19,'9:19','Day terram win i isswan iéaâlak, i ibedden i Loamaâ n Lêaôam, am win iumnen s Öebbi akked wass aneggaru u innu$ deg ubrid n Öebbi? Ur aâdilen ar Öebbi. Öebbi ur Inehhu agdud imednas.');
INSERT INTO "ramdane" VALUES(9,20,'9:20','Wid iumnen, iunagen, u nnu$en deg ubrid n Öebbi s wayla nnsen akked iman nnsen, aâlay umekkan nnsen, ar Öebbi. Wigi d wid ara irebêen.');
INSERT INTO "ramdane" VALUES(9,21,'9:21','Ibecceô iten Mass nnsen s uêunu s$uôeS, akked llza, akked leonanat, degsen ssaâd imezgi;');
INSERT INTO "ramdane" VALUES(9,22,'9:22','ad wekkôen degsen i lebda. Ih, ar Öebbi, arraz ameqwôaêan.');
INSERT INTO "ramdane" VALUES(9,23,'9:23','A wid iumnen! Ur pparat d imawlan, ibabaten nnwen akked watmaten nnwen, ma smenyafen tijehli i liman. Wid segwen i ten iran d imawlan, ad ilin, nitni, d imednas.');
INSERT INTO "ramdane" VALUES(9,24,'9:24','Ini: "maday, sennig Öebbi akked Umazan iS, akked umennu$ deg ubrid iS, ay têemmlem ibabaten nnwen, tarwa nnwen, atmaten nnwen, tilawin nnwen, alag nnwen, ayla i tkesbem, ttjaôa i tugadem a ppe$li, tinezdu$in i kwen iâaoben, ihi, gganit ar d Ias Öebbi, s lameô iS. Öebbi ur Inehhu agdud iballa$en.');
INSERT INTO "ramdane" VALUES(9,25,'9:25','Ni$ Isrebê ikwen Öebbi deg waîas imukan, akked wass n Ëunain, mi kwen taâoeb îîaqa nnwen. Ur awen d tebbwi acemma. Tamurt iwsâan, tu$al awen tediyeq. D$a tezzim d, trewlem.');
INSERT INTO "ramdane" VALUES(9,26,'9:26','Sinna, Issers ed Öebbi talwit iS $ef Umazan iS, akked lmumnin, Issers ed iâsekôiwen ur tepwalim. Iâaqeb wid ijehlen. D wagi ay d aqeîîi ijehliyen.');
INSERT INTO "ramdane" VALUES(9,27,'9:27','Sinna, deffir waya, Öebbi Issuruf i win i S ihwan. Öebbi Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(9,28,'9:28','A wid iumnen! Imcurak maâfunit kan. Ur d pqeôôiben ar Loamaâ n Lêaôam, mara ifukk useggwas agi nnsen. Ma tugadem lqella, a kwen I$it Öebbi, s lfevl iS, ma ihwa yaS. Ih, Öebbi d Amusnaw, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(9,29,'9:29','Nna$et wid ur numin ara s Öebbi, u s wass aneggaru, ur pgeddilen ayen Igdel Öebbi akked Umazan iS, ur xeddmen ddin n tidep, seg wid iwumi teppunefk Tezmamt, alamma xelûen tabzert s war amunnu, d imejjîuêen.');
INSERT INTO "ramdane" VALUES(9,30,'9:30','Nnan wudayen: "Âuzayeô d mmi S n Öebbi". Nnan imasiêiyen: "lmasiê d mmi S n Öebbi". Ayagi, nnan t id s imawen nnsen. Ppaâwaden d awal n wid ijehlen, n zik. A ten Ine$ Öebbi! Acêal îeôôifen!');
INSERT INTO "ramdane" VALUES(9,31,'9:31','Rran lquôôaê nnsen, ixelwiyen nnsen, lmasiê mmi s n Meryem, d imawlan war Öebbi. Pwamôen kan ad âabden Öebbi Yiwen; ur illi ôebbi siwa Neppa. Gedha syiS, seg wayen pcaraken!');
INSERT INTO "ramdane" VALUES(9,32,'9:32','B$an s yimawen nnsen ad ssexsin tafat n Öebbi. I Ib$a Öebbi, ad Itim kan tafat iS, di nneqwma ijehliyen.');
INSERT INTO "ramdane" VALUES(9,33,'9:33','D Neppa id Iuznen Amazan iS, s nnhaya akked ddin n tidep, iwakken a t Issifer $ef ddin meôôa, xas ugin ijehliyen.');
INSERT INTO "ramdane" VALUES(9,34,'9:34','A wid iumnen! Aîas n lqweôôaê d ixelwiyen, s tidep, teppen d aêerfi ayla n medden. U îeôôifen seg ubrid n Öebbi. Wid ixeznen ddheb d lfeîîa, ur ten pseddiqen deg ubrid n Öebbi, becceô iten s uâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(9,35,'9:35','Ass ideg ara ô$en di tmes n Loahennama, ad kwin, yissen, inyiren nnsen, ibeôdiyen nnsen, iârar nnsen. "D ayagi ay txezznem i iman nnwen. Jeôôbet ayen txezznem!"');
INSERT INTO "ramdane" VALUES(9,36,'9:36','Ih, ar Öebbi, amvan n wagguren: mrawsin; di Tezmamt n Öebbi, ass ideg Ixleq tamurt akked igenwan. Garasen, kué ôqan. D wagi ay d ddin n ddwam. Ur ddenniset iman nnwen degsen. Nna$et ijehliyen $ef ccfa, akken i kwen nnu$en $ef ccfa. Cfut, Öebbi Iteddu d wid ipêezziben.');
INSERT INTO "ramdane" VALUES(9,37,'9:37','Aswexxeô n waggur d timerniwt kan n tijehli. Nnetlafen yis wid ijehlen. Aseggwas a t êellen, aseggwas a t gedlen, iwakken ad wafqen amvan n wayen Igdel Öebbi. Ad êellen ayen Igdel Öebbi. Âaoben ten yir igiten nnsen. Öebbi ur Inehhu agdud ijehliyen.');
INSERT INTO "ramdane" VALUES(9,38,'9:38','A wid iumnen! Acu kwen iu$en? Ma nnan awen: "hejjmet ar ubrid n Öebbi", a ppebrum i iman nnwen. Day kwen taâoeb tmeddurt n ddunit, sennig tin n laxeôt? Zzhu n tmeddurt n ddunit d amejîiîuê kan, zdat win n laxeôt.');
INSERT INTO "ramdane" VALUES(9,39,'9:39','Ma ur tehjjimem ara, a kwen Iâaqeb s lqeôê, a d Iawi agdud nniven, deg umekkan nnwen. Ur T teîîuôum s wacemma. Öebbi Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(9,40,'9:40','Ma ur t tsellkem, ihi, Isellek it Öebbi, mi t ssuff$en wid ijehlen. Wissin n sin, mi llan deg ifri, mi inna i umeddakwel is: "kkes a$wbel, Öebbi, s tidep, yidne$". Issers ed Öebbi talwit iS fellas, Iâawen it s iâsekriwen, ur ten twalam. Isse$li mavi awal n wid ijehlen. Awal n Öebbi, neppa, aâlay sennig akw. Öebbi d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(9,41,'9:41','Hejjmet fessusit u ééayit, juhdet s wayla nnwen akked yiman nnwen, deg ubrid n Öebbi. Ppif wakka i kunwi, lemmer teéôim.');
INSERT INTO "ramdane" VALUES(9,42,'9:42','Lemmer d aba$uô isehlen, d inig wezzilen, yili vefôen k in. Maca, $wezzif fellasen umecwaô. Ad ggalen s Öebbi: "lemmer nezmir, yili neffe$ yidwen". Nn$an iman nnsen, Öebbi Iéôa ar nitni, war ccekk, d ikeddaben.');
INSERT INTO "ramdane" VALUES(9,43,'9:43','Ad ak Isemmeê Öebbi! Acimi i sen tserêev? Ard ak d iban anwi innan tidep, a ppissinev ikeddaben.');
INSERT INTO "ramdane" VALUES(9,44,'9:44','Ur ak d ssutren ara tsriê, wid iumnen s Öebbi akked wass aneggaru, mi juhden s wayla nnsen akked yiman nnsen. Öebbi Issen wid ipêezziben.');
INSERT INTO "ramdane" VALUES(9,45,'9:45','I k issutren tsriê, d wid ur numin s Öebbi akked wass aneggaru. Cukken wulawen nnsen, d$a, s ucukku nnsen, kukran.');
INSERT INTO "ramdane" VALUES(9,46,'9:46','Lemmer b$in tuff$a, yili heggan as d ayen ilaqen. Maca, Iugi Öebbi ad ppucegâan, Iurez iten. Nnan asen: "qqimet d wid iqqimen".');
INSERT INTO "ramdane" VALUES(9,47,'9:47','Lemmer ffi$en yidwen, ar awen rnun siwa ccwal. War ccekk, ad kecmen garawen, b$an ahewwel nnwen. Kra segwen ssemêasen asen. Öebbi Issen imednas.');
INSERT INTO "ramdane" VALUES(9,48,'9:48','Ni$ b$an, si zik, ahewwel. Sxeôben ak lumuô, armi d tusa tidep. Ivehô ed lameô n Öebbi, nitni ur b$in.');
INSERT INTO "ramdane" VALUES(9,49,'9:49','Garasen win id innan: "serreê iyi, ur iyi gar di lfetna"! Day ur $lin ara di lfetna? Loahennama, s tidep, a ppezzi i ijehliyen.');
INSERT INTO "ramdane" VALUES(9,50,'9:50','Ma iusa k id yise$ ad $weblen. Ma tusa k id twa$it, ad inin: "ni$, iu$ lêal, neqva lameô nne$". Ad wexxôen, zzhan.');
INSERT INTO "ramdane" VALUES(9,51,'9:51','Ini: "ur a$ ipêaz wacemma, siwa ayen i $ Iura Öebbi". Neppa d Bab nne$. £ef Öebbi ara ppeklen lmumnin.');
INSERT INTO "ramdane" VALUES(9,52,'9:52','Ini: "acu teggunim ad ivôu yidne$, siwa yiwen si sin ise$iyen? Nekwni negguni a kwen Iwwet Öebbi, s uâaqeb ara d iasen s$uôeS, ne$ seg ifassen nne$. Ihi gganit, nekwni a neggani yidwen".');
INSERT INTO "ramdane" VALUES(9,53,'9:53','Ini: "seddqet, teb$am ne$ tugim, d ayen ur neppwaqbal segwen, d awez$i. Kunwi, s tidep, d agdud iballa$en".');
INSERT INTO "ramdane" VALUES(9,54,'9:54','Ay sen ikksen a tepwaqbal ssadaqa nnsen, imi kan jehlen, s tidep, ar Öebbi akked Umazan iS, u s laâgez kan ay d ppasen ar tpéallit, s usmiglez kan ay pseddiqen.');
INSERT INTO "ramdane" VALUES(9,55,'9:55','Ur k ippaâoab wayla nnsen, ne$ tarwa nnsen. Ib$a kan Öebbi a ten Iâaqeb yissen di tmeddurt n ddunit, ad iffe$ ôôuê nnsen, nitni d ijehliyen.');
INSERT INTO "ramdane" VALUES(9,56,'9:56','Ad ggalen s Öebbi, ar nitni, war ccekk, segwen. Ur nitni segwen, maca d agdud iugaden.');
INSERT INTO "ramdane" VALUES(9,57,'9:57','Lemmer ad afen ssôan, ne$ ifran, ne$ anda ara kecmen, ad ôuêen $uôes s tazla.');
INSERT INTO "ramdane" VALUES(9,58,'9:58','Garasen, wid ikkaten deg k, $ef ssadaqat. Ma ippunefk asen segsent, ad ôavin, ma ur asen ippunefk, ad neêwen.');
INSERT INTO "ramdane" VALUES(9,59,'9:59','Lemmer nitni, s tidep, ôuvan ayen i sen Ifka Öebbi akked Umazan iS, u nnan d: "nesteqnaâ s Öebbi. Ad a$ d Ifk, si lfevl iS, Öebbi akked Umazan iS. Ih, d Öebbi i$ef neô$eb".');
INSERT INTO "ramdane" VALUES(9,60,'9:60','Ssadaqat siwa i ime$ban, iéawaliyen, i wid ice$wlen degsent, i usemlili n wulawen, i userreê imeêbas, i imeîîulba, i ubrid n Öebbi, i win d ifka ubrid. D aêeppem s$uô Öebbi. Öebbi d Amusnaw, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(9,61,'9:61','Garasen, wid icettmen deg Umazan, qqaôen: "neppa d aêessas". Ini: "d aêessas n lxiô i kunwi. Iumen s Öebbi, iumen lmumnin. D aêunu i wid iumnen segwen". I wid icettmen deg Umazan n Öebbi, aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(9,62,'9:62','Ad awen ggalen s Öebbi, iwakken a kwen ôavin. Öebbi akked Umazan iS, Iklal kteô a T ôavin, lemmer llin d lmumnin.');
INSERT INTO "ramdane" VALUES(9,63,'9:63','Day ur éôin ara ar, win ixuûmen Öebbi akked Umazan iS, i neppa times n Loahennama, ad iwekkeô degs? D wagi ay d ddell abarar.');
INSERT INTO "ramdane" VALUES(9,64,'9:64','Ixabiten ugaden ad asen d ters tsuôep, ad asen tessken i illan deg ulawen nnsen. Ini: "mmaâlket. Öebbi, s tidep, a d Issuffe$ ayen tugadem".');
INSERT INTO "ramdane" VALUES(9,65,'9:65','Ma testeqsav ten, a d inin, war ccekk: "la nheddeô kan, nepqessiô". Ini: "day la tettihim di Öebbi akked issekniyen iS, akked Umazan iS?');
INSERT INTO "ramdane" VALUES(9,66,'9:66','Ur steôoiâat. Ni$ tu$alem ar tijehli, deffir liman nnwen". Ma Nsemmeê i urbaâ segwen, a Naâqeb arbaâ, imi nitni llan d ibe$wviyen.');
INSERT INTO "ramdane" VALUES(9,67,'9:67','Ixabiten akked txabitin, wa ippameô i wa, ayen n dir, ppemyegdalen lewqama, pcuddun ifassen nnsen. Ppun Öebbi, Ippu ten. Ih, ixabiten d iballa$en.');
INSERT INTO "ramdane" VALUES(9,68,'9:68','Iwaââed Öebbi, i ixabiten, txabitin, akked ijehliyen, times n Loahennama, degs ad wekkôen. Beôka ten. Ixza ten Öebbi. Fellasen aâaqeb imezgi,');
INSERT INTO "ramdane" VALUES(9,69,'9:69','am imezwura nnwen. Llan ifen kwen tazmert, $elben kwen ayla, tarwa. Zhan deg umur nnsen, tezham deg umur nnwen, akken zhan imezwura nnwen deg umur nnsen. Teccvem akken ccven nitni. Widak, mêan igiten nnsen di ddunit, di laxeôt. Widak d imexsuôen.');
INSERT INTO "ramdane" VALUES(9,70,'9:70','Day ur ten id iusa ara usali imezwura nnsen? Agdud n Nuê, Âad, Tamud, agdud n Ibôahim, At Median, tid ippin. Usan ten id imazanen nnsen, s ubeggen. Ur ten Idennis Öebbi, maca llan dennisen iman nnsen.');
INSERT INTO "ramdane" VALUES(9,71,'9:71','Lmumnin, argaz tameîîut, d imeddukal wa n wa. Ppamôen lewqama, pgeddilen ayen n dir. Péallan, pzekkin, ppa$en awal i Öebbi akked Umazan iS. Wigi, ad Iêun Öebbi fellasen. Ih, Öebbi d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(9,72,'9:72','Iwaââed Öebbi i lmumnin, argaz tameîîut, leonanat, ddawat sen isaffen ppazzalen, ad wekkôen degsen; ixxamen igerzen di leonanat n Âaden, akked llza s$uô Öebbi - iten i$elben akw -. D wagi ay d rrbeê ameqwôaêan.');
INSERT INTO "ramdane" VALUES(9,73,'9:73','A Nnbi! Jahed ijehliyen akked ixabiten. Iqsiê $uôsen. Axxam nnsen d Loahennama, d taggara tucmip!');
INSERT INTO "ramdane" VALUES(9,74,'9:74','Ggulen s Öebbi ur d nnin, wanag nnan awal n tijehli. Jehlen deffir wannuz nnsen. Vemâen ayen iwumi ur wâan. Ur êzinen imi iten I$at, si lfevl iS, Öebbi akked Umazan iS. Ma steôoâen, yif it i nitni. Ma wexxôen, a ten Iâaqeb Öebbi s leqôeê, di ddunit, di laxeôt. Ur sâun, di tmurt, bab ne$ amâiwen.');
INSERT INTO "ramdane" VALUES(9,75,'9:75','Garasen, wid iâahden Öebbi: "ma Ifka ya$ d, si lfevl iS, a nseddeq, a nili si lâuûam".');
INSERT INTO "ramdane" VALUES(9,76,'9:76','Mi sen Ifka si lfevl iS, a t bexwlen, ad wexxôen, ad namren.');
INSERT INTO "ramdane" VALUES(9,77,'9:77','Issegwra yasen d tanaâuôt deg ulawen nnsen, ar ass ideg ara mlilen yideS, imi xed$an Öebbi deg wayen i S âahden, imi llan skiddiben.');
INSERT INTO "ramdane" VALUES(9,78,'9:78','Day ur éôin ara Öebbi Issen sser nnsen akked wayen qqaôen u, ar Öebbi Issen tibavniyin?');
INSERT INTO "ramdane" VALUES(9,79,'9:79','Wid ipqeôôiven di lmumnin i ib$an ad seddqen, u deg wid i iufan siwa tazmert nnsen, d$a ttihin fellasen; ad Ittihi Öebbi fellasen. Fellasen aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(9,80,'9:80','Ssuter ad ppusemmêen, ne$ qqim. Ma tessutrev ûavet iberdan, ur sen Ipsemmiê Öebbi, d awez$i. Akka, imi nitni jehlen ar Öebbi akked Umazan iS. Öebbi ur Inehhu agdud iballa$en.');
INSERT INTO "ramdane" VALUES(9,81,'9:81','Wid igwran, feôêen mi llan deffir Umazan n Öebbi. Ugin ad nna$en s wayla nnsen akked teôwiêt nnsen, deg ubrid n Öebbi. Nnan: "Ur pôuêut deg una$uô". Ini: "times n Loahennama teêma kteô, lemmer day fehhmen".');
INSERT INTO "ramdane" VALUES(9,82,'9:82','Anef ad vsen cwiî, ad run aîas, d aqeîîi n wayen keppben.');
INSERT INTO "ramdane" VALUES(9,83,'9:83','Ma Irra k id Öebbi ar urbaâ segsen, u ssutren ak d tsriê ad ff$en, ini: "d awez$i, ur teppeff$em yidi, ur tepna$em acengu yidi. Akken, kunwi, iâoeb ikwen i$imi, abrid amenzu, ihi, qqimet akked ineggura.');
INSERT INTO "ramdane" VALUES(9,84,'9:84','Ur péalla $ef yiwen degsen, ma immut, d awez$i. Ur pbedda ar tenîelt is. Nitni, s tidep, jehlen ar Öebbi akked Umazan iS, mmuten nitni d iballa$en.');
INSERT INTO "ramdane" VALUES(9,85,'9:85','Ur k ippaâoab wayla nnsen, ne$ dderya nnsen. Öebbi Ib$a kan a ten Iâaqeb yissen di ddunit, ad asen teffe$ taôwiêt nitni d ijehliyen.');
INSERT INTO "ramdane" VALUES(9,86,'9:86','Ma ters ed tsuôep: "amnet s Öebbi, jahdet akked Umazan iS", imeôkantiyen nnsen a k ssutren tsriê, ad inin: "anef ane$ ar wid iqqimen".');
INSERT INTO "ramdane" VALUES(9,87,'9:87','Öuvan ad ilin akked wid igwran. Ppusel$en wulawen nnsen, ur fehhmen.');
INSERT INTO "ramdane" VALUES(9,88,'9:88','Maca, amazan akked wid iumnen yides, jehden s wayla nnsen akked yiman nnsen. Widak, nnsen lxiô. Widak, d nitni ara irebêen.');
INSERT INTO "ramdane" VALUES(9,89,'9:89','Ihegga yasen d Öebbi leonanat, ddawat sen isaffen ppazzalen, degsen ad wekkôen. D wagi ay d rrbeê ameqwôaêan.');
INSERT INTO "ramdane" VALUES(9,90,'9:90','Wid id iufan ssebba, seg waâôaben n ucluê, usan d, ad asen ifk tsriê. Wid iskaddben Öebbi akked Umazan iS, qqimen. Wid ijehlen, segsen, ad sâaddin aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(9,91,'9:91','Ur telli tuzzma $ef uôhifen, imuvan, wid ur nufi ara fken, ma neûêen ar Öebbi akked Umazan iS. Ur telli tuzzma $ef iâaôviyen. Öebbi Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(9,92,'9:92','Am akken, i wid ik id iusan a ten tneqqlev, tenniv: "ur awen ufi$ ara kwen ineqqlen". U$alen, allen feggvent seg imeîîawen, $weblen imi ur ufin ara sedqen.');
INSERT INTO "ramdane" VALUES(9,93,'9:93','Tuzzma kan $ef wid ik issuturen tsriê, nitni d imeôkantiyen. Öuvan ad ilin akked ineggura. Isle$ Öebbi ulawen nnsen, d$a ur ssinen.');
INSERT INTO "ramdane" VALUES(9,94,'9:94','Ad awen ssutren ssmaê, ma tu$alem d $uôsen. Ini: "ur ssuturet ssmaê, ur kwen numin d awez$i. Ni$ Imla ya$ Öebbi asali nnwen. Ad Iéeô Öebbi akked Umazan iS, igiten nnwen. Sinna, a ppu$alem ar Umusnaw n wayen iffren akked wayen ur neffir, ad awen Imel ayen txeddmem".');
INSERT INTO "ramdane" VALUES(9,95,'9:95','Ad awen gren Öebbi, mi d tu$alem $uôsen, iwakken ad asen tanfem. Wexxeôt asen; nitni, s tidep, d laâôuô; axxam nnsen d Loahennama, d aqeîîi n wayen keppben.');
INSERT INTO "ramdane" VALUES(9,96,'9:96','Ad awen ggalen iwakken a ten tôavim. Ma tôuvam ten, ihi, Öebbi ur Ipôavi agdud iballa$en.');
INSERT INTO "ramdane" VALUES(9,97,'9:97','Aâôaben n ucluê zaden di tijehli akked nnxayel, zeggwiren ar uzgar n tilas id Issers Öebbi i Umazan iS. Öebbi d Amusnaw, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(9,98,'9:98','Ger waâôaben n ucluê, wid iêesben ayen seddqen d tabzert. Ggunin a ppekêes fellawen teswiât. Fellasen abeddel adiran n teswiât! Öebbi Issel, Issen.');
INSERT INTO "ramdane" VALUES(9,99,'9:99','Ger waâôaben n ucluê, wid iumnen s Öebbi d wass aneggaru, êesben ayen seddqen a ten iééié ar Öebbi akked téilla Umazan. Day maççi, s tidep, d ayen ara ten iééiéen? A ten Issekcem Öebbi deg uêunu S. Öebbi, s tidep, Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(9,100,'9:100','Imenza i izwaren, seg iminigen, imâiwnen, akked wid i ten iâanden ar lewqama, Iôuva ten Öebbi, ôuvan T. Ihegga yasen d leonanat, ddawat sen isaffen ppazzalen, ad wekkôen degsen, i lebda. D wagi ay d rrbeê ameqwôaêan.');
INSERT INTO "ramdane" VALUES(9,101,'9:101','Ger waâôaben n ucluê, i wen d izzin, llan at sin wudmawen, akw ger imezda$ n Lmadina. Zgan di nnxayel. Ur ten tessinev. Nekwni, Nessen iten. A ten Nâaqeb berdayen, sinna, ad ppunefken i uâaqeb abarar.');
INSERT INTO "ramdane" VALUES(9,102,'9:102','Wiyad qaôôen s ibekkaven nnsen, ssemlalen igi ilhan d win nniven icemten. Izmer Öebbi ad asen Issuref. S tidep, Öebbi Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(9,103,'9:103','Eddem ed ssadaqa seg wayla nnsen, a ten tezzizdeg, a ten tûeffi. èÈéal ilmend nnsen. Ih, tiéilla k d talwit nnsen. Öebbi Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(9,104,'9:104','Day ur éôin ara Öebbi, Neppa, Iqebbel asteôoaâ s$uô ixdimen iS? Ippaîaf ssadaqat. Ih, Öebbi Issuruf, Ipêunu.');
INSERT INTO "ramdane" VALUES(9,105,'9:105','Ini: "xedmet! Ad Iwali igiten nnwen, Öebbi, Umazan iS, akked lmumnin. Tu$alin nnwen ar Umusnaw n wayen iffren akked wayen ur neffir, ad awen Imel ayen txeddmem".');
INSERT INTO "ramdane" VALUES(9,106,'9:106','Wiyav pganin lameô n Öebbi, a ten Iâaqeb, ne$ ad asen Issuref. Öebbi d Amusnaw, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(9,107,'9:107','Wid irran loamaâ i lemveôôa akked tijehli, i lefôaq ger lmumnin; d taxettalt i wid, iu$ lêal, llan pna$en Öebbi akked Umazan iS, ad ggalen, war ccekk: "ay neb$a siwa lewqama". Ad Icehhed Öebbi ar nitni, s tidep, d ikeddaben.');
INSERT INTO "ramdane" VALUES(9,108,'9:108','Ur ppili, degs, d awez$i. Iklal kteô, tilin ik, loamaâ ibnan $ef uêezzeb, seg wass amenzu. Degs irgazen i iêemmlen ad izdigen. Öebbi Iêemmel izedganen.');
INSERT INTO "ramdane" VALUES(9,109,'9:109','Day win issersen lebni s $ef uêezzeb i Öebbi akked llza, ur yif ara win issersen lebni s $ef rrif n ucôuf ara iseggxen? Iseggex yis ar tmes n Loahennama. Öebbi ur Inehhu agdud imednas.');
INSERT INTO "ramdane" VALUES(9,110,'9:110','Izga lebni, bnan, ippara ccekk deg ulawen nnsen, alamma gezmen wulawen nnsen. Öebbi d Amusnaw, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(9,111,'9:111','Ih, Öebbi Iu$ ar lmumnin, iman nnsen akked wayla nnsen, imi Loennet i nitni. Pna$en deg ubrid n Öebbi. Nn$an, pwan$an. D lemâahda fellas, s tidep, di Tuôa, di Linoil akked Leqwôan. Anwa yifen Öebbi di tuîîfa n lemâahda s? Feôêet, ihi, di ssuq tsewwqem. Wagi d rrbeê ameqwôaêan.');
INSERT INTO "ramdane" VALUES(9,112,'9:112','Wid isteôoâen, wid iâabden, wid ipcekkiôen, ixelwiyen, wid iîîinizen, wid ikennun, wid ippamôen lewqama, wid ipgeddilen ayen n dir, wid ipêaraben $ef tlisa n Öebbi ... Becceô lmumnin.');
INSERT INTO "ramdane" VALUES(9,113,'9:113','Ur issefk ara i Nnbi akked wid iumnen, ad ssutren ad pwasemmêen imcurak, xas ppilin asen, ma iban asen d ar, nitni, war ccekk, d at tmes.');
INSERT INTO "ramdane" VALUES(9,114,'9:114','Ibôahim ur issuter ara ad ipwasemmeê baba s, siwa ilmend n lemâahda i s ifka. Mi s d iban ar neppa d acengu n Öebbi, ifra p yides. Ibôahim, war ccekk, ippanez, iêellem.');
INSERT INTO "ramdane" VALUES(9,115,'9:115','Ur p Ixeddem Öebbi, ad Itlef agdud mbaâd ma Inha ten, alamma i sen Ibeggen acu ara êezzben. Öebbi, s tidep, Issen yal cci.');
INSERT INTO "ramdane" VALUES(9,116,'9:116','Ih, Öebbi, IneS tagelda n tmurt akked igenwan, Issidir, Ineqq. Ur awen illi, war Öebbi, bab ne$ amâiwen.');
INSERT INTO "ramdane" VALUES(9,117,'9:117','Ni$ Issuref Öebbi i Nnbi akked iminigen, akked imâiwnen i iddan yides, di lweqt uwâiô, mbaâd ma qôib nneqlaben wulawen n urbaâ segsen. Sinna, Issuref asen. Ih, Neppa, i nitni, dUmâin, d''Aênin.');
INSERT INTO "ramdane" VALUES(9,118,'9:118','U i krav id igwran, armi, tamurt iwsâan, tediyyeq fellasen. Vaqen deg iman nnsen. Ufan ur illi ssôan si Öebbi, siwa Neppa. Sinna, Issuref asen, akken ad steôoâen. Öebbi, s tidep, Issuruf, Ipêunu.');
INSERT INTO "ramdane" VALUES(9,119,'9:119','A wid iumnen! Ëezzbet i Öebbi, ilit seg imawlan n tidep.');
INSERT INTO "ramdane" VALUES(9,120,'9:120','Ur issefk ara i imezda$ n Lmadina, d wayen i sen d izzin n waâôaben n ucluê, ad gwrin deffir Umazan n Öebbi, ne$ ad smenyafen iman nnsen i iman is. Ayagi, acku ur ten ipêaz fad, ne$ âaggu, ne$ llaé, deg ubrid n Öebbi. Ur keçmen akal ara isserfun ijehliyen, ur ppaîîafen acemma s$uô ucengu, siwa ma dayen i sen iuran d igi n wayen ilhan. Öebbi, s tidep, ur Isôuêu arraz iâaôviyen.');
INSERT INTO "ramdane" VALUES(9,121,'9:121','Ur pûeôôifen cwiî ne$ aîas, ur zeggren asif, siwa ma iura yasen, iwakken a ten Ikafi Öebbi $ef wayen ilhan deg wayen xeddmen.');
INSERT INTO "ramdane" VALUES(9,122,'9:122','Ur Issefk ara i lmumnin ad ff$en akw. Awufan ad iffe$ ugraw si yal arbaâ, ad $bun di ddin, ad nabin agdud nnsen, mara u$alen $uôsen. Amar ad steêdren.');
INSERT INTO "ramdane" VALUES(9,123,'9:123','A wid iumnen! Nna$et ijehliyen i kwen iqeôben. Ad afen, $uôwen, teqseê. Cfut, Öebbi Ippili d wid ipêezziben.');
INSERT INTO "ramdane" VALUES(9,124,'9:124','Mi d ters tsuôep, llan garasen wid iqqaôen: "anwa degwen, iwumi terna tagi, di liman"? Ma d wid iumnen, terna yasen liman, u nitni feôêen.');
INSERT INTO "ramdane" VALUES(9,125,'9:125','Ma d wid iwumi blan wulawen, terna ammus i wammus nnsen. Mmuten u nitni d ijehliyen.');
INSERT INTO "ramdane" VALUES(9,126,'9:126','Day ur walan ara, ar nitni la d sskaren lfetna, yal aseggwas, yiwen ubrid ne$ berdayen? Sinna, ur steôoiâen, ur d smektayen.');
INSERT INTO "ramdane" VALUES(9,127,'9:127','Mi d ters tsuôep, ad mesmuqalen: "day illa yiwen ikwen id Ipwalin"? Sinna, ad wexxôen. Isswexxeô Öebbi ulawen nnsen, imi nitni d agdud ur nfehhem.');
INSERT INTO "ramdane" VALUES(9,128,'9:128','Ni$ iusa kwen id Umazan seg iman nnwen. èéay fellas laâtab nnwen. Ipêarab fellawen, d umâin, d aênin i lmumnin.');
INSERT INTO "ramdane" VALUES(9,129,'9:129','Ma wexxôen, ini: "Iqadd iyi Öebbi. Ur illi ôebbi siwa Neppa. FellaS ppekle$. Neppa d Mass n usgeld ameqwôaêan.');
INSERT INTO "ramdane" VALUES(10,1,'10:1','Alô, wigi d issekniyen n Tezmamt tuûwibt.');
INSERT INTO "ramdane" VALUES(10,2,'10:2','Day d lwehma, i medden, ma Nweêêa n i urgaz nnsen: "nabi imdanen, becceô wid iumnen ar ad afen arraz umâin ar Mass nnsen"? Nnan ijehliyen: "wagi, s tidep, d aseêêar n ûûeê".');
INSERT INTO "ramdane" VALUES(10,3,'10:3','Ih, Mass nnwen d Öebbi, i Ixelqen tamurt akked igenwan di sdis wussan. Sinna, Iâannec $ef usgeld, Iseyyi lameô. Ur illi umcafaâ siwa ma Iâammed as. D wagi ay d Öebbi, Mass nnwen. Ihi, âabdet eP. Day ur d tesmektayem ara?');
INSERT INTO "ramdane" VALUES(10,4,'10:4','£uôeS akw tu$alin nnwen. Lemâahda n Öebbi d tidep. Ih, Inezzu axlaq, Ippales, iwakken ad Ikafi wid iumnen u xeddmen ayen ilhan, s lmizan. I wid ijehlen, tissit iô$an, aâaqeb aqeôêan, imi llan jehlen.');
INSERT INTO "ramdane" VALUES(10,5,'10:5','D Neppa i Irran iîij d lefnaô, aggur d tafat. Iwezn as tiswaâin, iwakken a ppissinem amvan iseggwasen, akked leêsab. Ur Ixliq Öebbi ayagi, siwa s tidep. Ipfeûûil issekniyen i ugdud issnen.');
INSERT INTO "ramdane" VALUES(10,6,'10:6','S tidep, di lemgarda ger yiv akked wass, akked wayen Ixleq Öebbi di tmurt akked igenwan, ard issekniyen i ugdud i ipêezziben.');
INSERT INTO "ramdane" VALUES(10,7,'10:7','Ih, wid ur negguni ara timlilit yidne£, i tqennaâ tmeddurt di ddunit, u rekden degs, d wid i istehzan $ef issekniyen nne£.');
INSERT INTO "ramdane" VALUES(10,8,'10:8','Wigi, axxam nnsen di tmes, ilmend n wayen keppben.');
INSERT INTO "ramdane" VALUES(10,9,'10:9','Ih, wid iumnen u xeddmen ayen ilhan, a ten Inhu Mass nnsen, s liman nnsen. Ad ppazzalen isaffen seddawat sen, di leonanat isaâdiyen.');
INSERT INTO "ramdane" VALUES(10,10,'10:10','Anuju nnsen degs: "gedha s yissek a Öebbi"! Azul nnsen d: "tifrat". Anuju nnsen aneggaru: "ccekôan i Öebbi, Mass imavalen".');
INSERT INTO "ramdane" VALUES(10,11,'10:11','Lemmer ad Iaâoel Öebbi cceôô ar imdanen, akken nitni êaren $ef lxiô, yili ibbwev asen lajel nnsen. A Nanef i wid ur negguni ara timlilit yidne£, ad ddebdben di lbe$wv nnsen.');
INSERT INTO "ramdane" VALUES(10,12,'10:12','Amdan, ma iêuza t ccqa, ad a£ £ d isiwel, iééel, iqqim ne$ ibedd. Mi s Nekkes ccqa s, ad iteddu amzun ur d inuj $uône£ilmend n ccqa i t iêuzan. Akka ay ppucebêen igiten nnsen i imaâfan.');
INSERT INTO "ramdane" VALUES(10,13,'10:13','Ni$ Nesqucc isuta, uqbel nnwen, imi densen. Ussan d $uôsen imazanen nnsen, s ubeggen, ur llin d wid ara iamnen. Akka ay Nepqeîîi agdud ibe$wviyen.');
INSERT INTO "ramdane" VALUES(10,14,'10:14','Sinna, Nerra kwen d igensas di tmurt, deffir sen, a Nwali amek ara txedmem.');
INSERT INTO "ramdane" VALUES(10,15,'10:15','Ma $ôan asen issekniyen nne£ iberrzen, wid ur negguni ara timlilit yidne£, ad inin: "awi d Leqwôan nniven, ne$ beddel it". Ini: "ur felli a t beddle$, s leb$i n yiman iw. Teddu$ kan akked wayen iyi d ippuweêêan. Nek ugade$, ma xedâa$ Mass iw, aâaqeb n wass ameqwôaêan".');
INSERT INTO "ramdane" VALUES(10,16,'10:16','Ini: "lemmer i S ihwi i Öebbi, ur awen t id mli$, ur awen t id mmekti$. Ni$ zed$e$ yidwen tameddurt, uqbel is. Day ur tfehmem ara"?');
INSERT INTO "ramdane" VALUES(10,17,'10:17','Anwa i idensen kteô, ger win id igren tikerkas $ef Öebbi, ne$ win inekôen issekniyen iS? Ih, ur rebêen ibe$wviyen.');
INSERT INTO "ramdane" VALUES(10,18,'10:18','La âabden, war Öebbi, ayen ur ten neîîuôu, ur ten nenfaâ. La qqaôen: "wigi d imcafâen nne$ ar Öebbi". Ini: "day a pxebbôem Öebbi $ef wayen ur Issin, di tmurt akked igenwan"? Gedha s yiS! I Aâlay sennig wayen pcaraken!');
INSERT INTO "ramdane" VALUES(10,19,'10:19','Llan imdanen d yiwen n lois kan. Sinna, mgaraden. Lemmer ur d izwar ara wawal, s$uô Mass ik, ad yili uêuddu, garasen, $ef wayen i$ef mgaraden.');
INSERT INTO "ramdane" VALUES(10,20,'10:20','La qqaôen: "a lemmer id irs $uôes ussekni n Mass is"! Ini: "tabavnit ar Öebbi kan. Ihi, gganit. Nek, yidwen, seg wid iggunin".');
INSERT INTO "ramdane" VALUES(10,21,'10:21','Ma Nesêess i imdanen aêunu, deffir twa$it i ten iêuzan, atnan la ppandin i issekniyen nne£. Ini: "Öebbi Yif akw akken Ip$awal di tiêeôci". Imersulen nne£, s tidep, pparun ayen teppandim.');
INSERT INTO "ramdane" VALUES(10,22,'10:22','Neppa i kwen Ippawin di lberr, di yill. Alamma, mi tellam di lembwabeô, teppedum degsen s wavu uûwib, feôêen yis, tusa d tbucivant $uôsen, ppasent ed lemwao $uôsen, si yal tama, êulfan a d tezzi fellasen. Naâôen Öebbi, s lqaâida i Neppa: "ma Tsellkev a$ si tagi, ar a nili seg wid isnemmiren".');
INSERT INTO "ramdane" VALUES(10,23,'10:23','Mi ten Isellek, imiren u$alen be$wven di tmurt, war ssebba. A yimdanen! Lbe$wv nnwen, siwa $ef yiman nnwen. Zzhu di tmeddurt n ddunit, sinna, $uône£ tu$alin nnwen, ad awen Nmel ayen txeddmem.');
INSERT INTO "ramdane" VALUES(10,24,'10:24','Amedya n tmeddurt di ddunit, am aman kan. Nesse$li ten id seg igenni, xelven akked im$an n tmurt, iseg teppen imdanen akked lmal. Mi tcebbeê tmurt, tu$al d tuzyint, $ilen, imezda$ is, ar deg ufus nnsen ay tella. Iusa p id lameô nne£, deg iv, ne$ deg wass, Nerra p d ahicuô, amzun ur teoouoeg ass izeryen. Akka ay Nfeûûel issekniyen i ugdud ipxemmimen.');
INSERT INTO "ramdane" VALUES(10,25,'10:25','Inecced Öebbi s axxam n tifrat. Inehhu win i S ihwan, ar ubrid uwqim.');
INSERT INTO "ramdane" VALUES(10,26,'10:26','I wid iweqmen, lewqama, ternuv as. Udmawen nnsen, ur ten ipnal bufsus, ne$ ddell. Widak, d at Loennet, nitni, degs ad wekkôen.');
INSERT INTO "ramdane" VALUES(10,27,'10:27','Aqeîîi n wid id ibbwin tucmitin, d tucmip itent icban. Ad ndellen. Ur sâun amenêul ar Öebbi. Amzun $ummen wudmawen nnsen, s ccevyat n yiv iberkanen. Widak d at tmes, nitni, degs, ad wekkôen.');
INSERT INTO "ramdane" VALUES(10,28,'10:28','Ass ideg ara ten Nfesser akw, a Nini, imiren, i wid icurken: "imukan nnwen, kunwi akked icriken nnwen"! Sinna Nessemfaôaq iten. A d inin, icriken nnsen: "maççi d nekwni ay tâabdem.');
INSERT INTO "ramdane" VALUES(10,29,'10:29','Ad Iqadd Öebbi, d Anagi garane$ yidwen, ar ur d necliâ deg uâbbed nnwen".');
INSERT INTO "ramdane" VALUES(10,30,'10:30','Din, yal amdan ad iwali ayen iwasa. Ad u$alen ar Öebbi, Bab nnsen n tidep. Itelf iten wayen d snulfuyen.');
INSERT INTO "ramdane" VALUES(10,31,'10:31','Ini: "anwa ikwen Ip$itin seg igenni akked tmurt? Anwa ay d Bab n tmejjin akked yiéôi? Anwa id Issuffu$en amuddir si lmegget, id Issuffu$en lmegget seg umuddir? Anwa i Iseyyin lameô"? A d inin: "d Öebbi". Ini: "ihi, day ur tepêezzibem ara"?');
INSERT INTO "ramdane" VALUES(10,32,'10:32','D wagi ay d Öebbi, Mass nnwen n tidep. Deffir tidep, acu i illan, siwa ttelfan? Ihi, acimi texdâam?');
INSERT INTO "ramdane" VALUES(10,33,'10:33','Akka i iûaô wawal n Mass ik, $ef wid ixedmen tucmitin. Ih, nitni ur ppamnen.');
INSERT INTO "ramdane" VALUES(10,34,'10:34','Ini: "day illa ger icriken nnwen, win id inzan lxelq, sinna, iules as d"? Ini: "Öebbi inezzu lxelq, sinna, Ippales as d. Ihi, amek ara twexxôem"?');
INSERT INTO "ramdane" VALUES(10,35,'10:35','Ini: "day illa, ger icriken nnwen, win i inehhun ar tidep"? Ini: "Öebbi Inehhu ar tidep. Win Inehhun ar tidep, day ur Yif ara, akken Iklal tikli yideS, win ur nennehhu, siwa ma ipwanha? Acu ikwen iu$en? Amek tepseyyim"?');
INSERT INTO "ramdane" VALUES(10,36,'10:36','Tegwti, segsen, teddun kan d nnwaya. Nnwaya, war ccekk, ur tebbwi acemma si tidep. Ih, Öebbi Issen acu xeddmen.');
INSERT INTO "ramdane" VALUES(10,37,'10:37','Leqwôan agi ur d innulfa ara, war Öebbi. Maca, d atebbet n wayen it id izwaren, d afeûûel n Tezmamt, ur degs ccekk, s$uô Mass imavalen.');
INSERT INTO "ramdane" VALUES(10,38,'10:38','Ne$ la qqaôen: "isnulfa t id". Ini: "awit ed tasuôep i t icban, neccdet seg wanda tzemrem, war Öebbi, ma tessawalem tidep".');
INSERT INTO "ramdane" VALUES(10,39,'10:39','Xaîi, skaddben ayen ur nekcim di tmusni nnsen, ur ten id iusi usefhem is. Akka ay skaddben, wid i ten id izwaren. Wali, ihi, amek tevôa d imednas.');
INSERT INTO "ramdane" VALUES(10,40,'10:40','Garasen, wid iumnen yis; garasen, wid ur numin yis. Mass ik Yif akw akken Issen ifessaden.');
INSERT INTO "ramdane" VALUES(10,41,'10:41','Ma skaddben k, ini: "i nek igiten iw, i kunwi igiten nnwen. Ur teppkam deg wayen xedme$, ur ppka$ deg wayen txedmem".');
INSERT INTO "ramdane" VALUES(10,42,'10:42','Garasen, llan wid i k issemêasen. Day tzemrev a pperrev ad slen iâeééugen, ma llan ur fehhmen?');
INSERT INTO "ramdane" VALUES(10,43,'10:43','Garasen, llan wid i k ipmuqulen. Day tzemrev a ppenhuv ider$alen, ma llan ur pwalin?');
INSERT INTO "ramdane" VALUES(10,44,'10:44','Öebbi, s tidep, ur Iddennis imdanen, s wacemma, maca imdanen densen iman nnsen.');
INSERT INTO "ramdane" VALUES(10,45,'10:45','Ass ideg ara ten Ifser, amzun sâaddan kan saâa n wass, ad msaâqalen. Ni$ xesôen wid iskaddben timlilit d Öebbi. Ur pwanhan.');
INSERT INTO "ramdane" VALUES(10,46,'10:46','Ad ak Nessken kra seg wayen i sen Nâahed, ne$ a k id Nawi $uône£. Ihi, $uône£ tu$alin nnsen. Sinna, Öebbi d Anagi n wayen xeddmen.');
INSERT INTO "ramdane" VALUES(10,47,'10:47','I yal lois, amazan. Ma iusa d umazan nnsen, a ppefru garasen s lmizan, ur pwadensen.');
INSERT INTO "ramdane" VALUES(10,48,'10:48','Qqaôen: "melmi lemâahda agi, ma tessawalem tidep?');
INSERT INTO "ramdane" VALUES(10,49,'10:49','Ini: "ur mlike$ i iman iw vveôô, ne$ aba$uô, siwa ayen Ib$a Öebbi. Yal lois s lajel is". Ma iusa d lajel nnsen, ur t swexxiôen s saâa, ur t id sseqôaben.');
INSERT INTO "ramdane" VALUES(10,50,'10:50','Ini: "acu twalam, ma iusa kwen id uâaqeb iS, deg iv, ne$ deg wass? Acimi êaren $uôeS ibe$wviyen?');
INSERT INTO "ramdane" VALUES(10,51,'10:51','D$a, ma iusa d, a ppamnem yis? Tura, aql akwen t$eûbem fellas".');
INSERT INTO "ramdane" VALUES(10,52,'10:52','Sinna, ad asen inin i wid idensen: "jeôôbet aâaqeb n lebda. Ma teppuqeîîan siwa kan $ef wayen tkeppbem"?');
INSERT INTO "ramdane" VALUES(10,53,'10:53','Steqsayen k: "d tidep ayagi"? Ini: "ih, s Mass iw, ar ayagi d tidep! Kunwi ur tezmirem i usfunneg".');
INSERT INTO "ramdane" VALUES(10,54,'10:54','Xas ad isâu, yal amdan idensen, i illan di tmurt, ur d ifeddu yis. Ffren nndama mi walan aâaqeb. Aêuddu garasen, s lmizan, ur pwadensen.');
INSERT INTO "ramdane" VALUES(10,55,'10:55','Day, s tidep, maççi n Öebbi i illan di tmurt akked igenwan? Ih, day lemâahda n Öebbi maççi d tidep? Maca, tegwti segsen ur ssinen.');
INSERT INTO "ramdane" VALUES(10,56,'10:56','Neppa Issidir, Ineqq. £uôeS ara tu$alem.');
INSERT INTO "ramdane" VALUES(10,57,'10:57','A yimdanen! Ni$ iusa kwen id unabi s$uô Mass nnwen, akked ccfa n wayen iblan ulawen, akked nnhaya, akked uêunu i lmumnin.');
INSERT INTO "ramdane" VALUES(10,58,'10:58','Ini: "s lfevl n Öebbi akked uêunu S, s waya, xas ad feôêen". Yif ayen pkemmimen.');
INSERT INTO "ramdane" VALUES(10,59,'10:59','Ini: "twalam ayen i wen d Issers Öebbi n ôôeéq? Terram segs leêlal d leêôam". Ini: "Iâammed awen Öebbi, ne$ tesnulfam d $ef Öebbi"?');
INSERT INTO "ramdane" VALUES(10,60,'10:60','Acu ara meyyzen, ass n tnekra, wid id igren tikerkas $ef Öebbi? Ih, Öebbi d Bab n lfevl af imdanen, maca tegwti segsen ur snemmiren.');
INSERT INTO "ramdane" VALUES(10,61,'10:61','Ur teppiliv di liêala, ur temmalev si Leqwôan, ur tepce$wilem di kra, mebla ma Nella d Inagan nnwen, mara tweqmem. Lmizan ubelkim di tmurt u deg igenni, ur idrig i Mass ik. Akken seddaw waya, ne$ sennig es. Akw, di Tezmamt ipbegginen.');
INSERT INTO "ramdane" VALUES(10,62,'10:62','Ih, s tidep, iêbiben n Öebbi ur ppagwaden, ur $weblen.');
INSERT INTO "ramdane" VALUES(10,63,'10:63','Wid iumnen u llan êezzben,');
INSERT INTO "ramdane" VALUES(10,64,'10:64','a ten beccôen s lxiô di tmeddurt n ddunit, di laxeôt. Ur illi ubeddel deg wawalen n Öebbi. D wagi ay d rrbeê ameqwôaêan.');
INSERT INTO "ramdane" VALUES(10,65,'10:65','Ur k i$webbel wawal nnsen. Ih, tazmert akw n Öebbi. Neppa Isell, d Amusnaw.');
INSERT INTO "ramdane" VALUES(10,66,'10:66','S tidep, day maççi n Öebbi i illan deg igenwan, i illan di tmurt? D acu îîafaôen wid ipnujun, war Öebbi, ar icriken? Ïîafaôen kan nnwaya. Oaââlen kan.');
INSERT INTO "ramdane" VALUES(10,67,'10:67','Neppa, i wen Irran iv i ureyyeê nnwen, ass iwakken a pwalim. Deg waya, s tidep, d issekniyen i ugdud isellen.');
INSERT INTO "ramdane" VALUES(10,68,'10:68','Nnan: "Öebbi Isâa aqcic. Gedha s yiS! Neppa d Ameôkanti. IneS i illan deg igenwan, i illan di tmurt. Ur tesâim adabu $ef waya. Day a d tinim $ef Öebbi, ayen ur tessinem"?');
INSERT INTO "ramdane" VALUES(10,69,'10:69','Ini: "s tidep, wid id igren tikerkas $ef Öebbi, ur rebêen".');
INSERT INTO "ramdane" VALUES(10,70,'10:70','Zzhu di ddunit, sinna, $uône£ tu$alin nnsen. D$a, ad asen Nsâaddi aâaqeb uqsiê, imi llan jehlen.');
INSERT INTO "ramdane" VALUES(10,71,'10:71','Eêku yasen d tadyant n Nuê, mi inna i ugdud is: "a yagdud iw! Ma ééayit fellawen tilin iw, akked usmekti w n issekniyen n Öebbi, $ef Öebbi ppekle$, ihi, zduklet lameô nnwen akked icriken nnwen. Sinna, ur awen teppili lhejna $ef lameô nnwen, d$a, frut ep felli, ur iyi sganit.');
INSERT INTO "ramdane" VALUES(10,72,'10:72','Ma twexxôem, ur awen ssuture$ taqdiât. Ih, taqdiât iw ar Öebbi kan. Felli lameô ad ili$ seg imvuâen.');
INSERT INTO "ramdane" VALUES(10,73,'10:73','Skaddben t. Nsellek it, akked wid illan yides, di lbabuô. Nerra ten d igensas. Ne$meq wid iskaddben issekniyen nne£. Wali amek tevôa d wid ippunedôen.');
INSERT INTO "ramdane" VALUES(10,74,'10:74','Sinna, deffir es, Nuzen ed imazanen ar igduden nnsen. Usan ten id s ubeggen. Ur ssawven ad amnen $ef wayen nekôen zik. Akka ay Nselle$ ulawen ixabiten.');
INSERT INTO "ramdane" VALUES(10,75,'10:75','Sinna, deffir sen, Nuzen ed Musa akked Haôun, ar Ferâun akked imvebbôen is, s issekniyen nne£. Sme$wôen iman nnsen. Llan d agdud ibe$wviyen.');
INSERT INTO "ramdane" VALUES(10,76,'10:76','Mi ten id tusa tidep, s$uône£, nnan: "wagi, ih, d ssêur n ûûeê".');
INSERT INTO "ramdane" VALUES(10,77,'10:77','Inna Musa: "day a tinim d ssêur wagi, i tidep mi kwen id tusa"? Ur rebêen iseêêaren.');
INSERT INTO "ramdane" VALUES(10,78,'10:78','Nnan: "day d tusiv $uône$, ad a$ tesîixxôev seg wayen id nufa ar imezwura nne$, a ppu$alem, di sin, d imeqwôanen di tmurt? Ur numin, nekwni, yiswen".');
INSERT INTO "ramdane" VALUES(10,79,'10:79','Inna Ferâun: "awit iyi d yal aseêêar amusnaw".');
INSERT INTO "ramdane" VALUES(10,80,'10:80','Mi d usan iseêêaren, inna yasen Musa: "veggôet ara tveggôem"!');
INSERT INTO "ramdane" VALUES(10,81,'10:81','Mi veggôen, inna Musa: "ayen d tebbwim d ssêur. S tidep, a t Inqev Öebbi". Ih, Öebbi ur Issewqam ara igiten ifessaden.');
INSERT INTO "ramdane" VALUES(10,82,'10:82','Öebbi Itebbit tidep, s wawal iS, xas ugin ibe$wviyen.');
INSERT INTO "ramdane" VALUES(10,83,'10:83','I iumnen s Musa, siwa tarwa seg ugdud is, ilmend n tugdi uhewwel n Ferâun akked imvebbôen is. Ih, Ferâun d abe$wvi di tmurt. Neppa, s tidep, seg imaâfan.');
INSERT INTO "ramdane" VALUES(10,84,'10:84','Inna Musa: "a yagdud iw! Ma teppamnem s Öebbi, ppeklet fellaS ma d imvuâen ay tellam".');
INSERT INTO "ramdane" VALUES(10,85,'10:85','Nnan: "neppkel $ef Öebbi. A Mass nne$, ur a$ ppara d ahewwel i ugdud imednas;');
INSERT INTO "ramdane" VALUES(10,86,'10:86','Sellek a$, s uêunu K, seg ugdud ijehliyen".');
INSERT INTO "ramdane" VALUES(10,87,'10:87','Nweêêa i Musa akked gma s: "ebnut ixxamen i ugdud nnwen di Maûeô, erret ixxamen nnwen ar lqibla, beddet i téallit. Becceô lmumnin".');
INSERT INTO "ramdane" VALUES(10,88,'10:88','Inna Musa: "a Mass nne$! S tidep, Tefkiv i Ferâun akked imvebbôen is, ôôêuê akked wayla di tmeddurt n ddunit, a Mass nne$, iwakken ad nnetlafen seg ubrid iK. A Mass nne$, Squcc ayla nnsen, S$eô ulawen nnsen, ur ppamnen alamma walan aâaqeb aqeôêan".');
INSERT INTO "ramdane" VALUES(10,89,'10:89','Inna: "ih, tepwaqbel tnaâôit nnwen. Ddut di lewqam, ur teddut d ubrid n wid ur nessin".');
INSERT INTO "ramdane" VALUES(10,90,'10:90','Nezzger At Isôail di yill. Vefôen ten Ferâun akked iâsekôiwen is, s leb$wev d lewâaôa. Armi ibbwev ad i$meq, inna: "umne$! Ih, ur illi ôebbi siwa Win is umnen At Isôail. Nek seg imvuâen".');
INSERT INTO "ramdane" VALUES(10,91,'10:91','Tura! Ni$ zik taâûav, telliv seg ifessaden.');
INSERT INTO "ramdane" VALUES(10,92,'10:92','Assa, a Nsellek loeppa k, a d tegwri d assekni i wid ara k id ineûfen. Aîas n medden, s tidep, stehzan deg issekniyen nne£.');
INSERT INTO "ramdane" VALUES(10,93,'10:93','Ni$ Nessers At Isôail deg umkan umâin, u N$at iten s wayen ilhan. Ur mgaraden armi ten id tusa tussna. Ih, Mass ik ad Iêudd garasen, ass n tnekra, ilmend n wayen i$ef llan mgaraden.');
INSERT INTO "ramdane" VALUES(10,94,'10:94','Ma tcukkev $ef wayen i k ed Nessers, steqsi wid i iqqaôen Tazmamt, uqbel ik. Ni$ tusa k id tidep, s$uô Mass ik, ur ppili seg wid ipcukkun.');
INSERT INTO "ramdane" VALUES(10,95,'10:95','Ur ppili seg wid i iskaddben issekniyen n Öebbi, a ppiliv seg imexsuôen.');
INSERT INTO "ramdane" VALUES(10,96,'10:96','Ih, wid i$ef iûaô Wawal n Mass ik, ur ppamnen,');
INSERT INTO "ramdane" VALUES(10,97,'10:97','xas usan ten id akw issekniyen, alamma walan aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(10,98,'10:98','A lemmer telli temdint iumnen, u infaâ p liman is, siwa agdud n Yunes! Mi umnen, Nveôôef segsen aâaqeb imdelli di tmeddurt n ddunit. Neooa ten zzhan tallit kan.');
INSERT INTO "ramdane" VALUES(10,99,'10:99','Ma ihwa yaS i Mass ik, ad amnen wid illan di ddunit, akken ma llan. Day a pêeppmev, keçç, i imdanen ad amnen?');
INSERT INTO "ramdane" VALUES(10,100,'10:100','Ur ippamen umdan siwa ma Irad Öebbi. Isdullu wid ur nfehhem ara.');
INSERT INTO "ramdane" VALUES(10,101,'10:101','Ini: "walit i illan di tmurt akked igenwan. Ula iwumi issekniyen akked ineddiôen, i ugdud ur neppamen".');
INSERT INTO "ramdane" VALUES(10,102,'10:102','Day a pganin kan amedya n wussan n wid i ten id izwaren? Ini: "gganit, nek yidwen, seg wid ipganin".');
INSERT INTO "ramdane" VALUES(10,103,'10:103','Sinna, a Nsellek imazanen nne£, akked wid iumnen. Akka ay d lwajeb fella£, a Nsellek lmumnin.');
INSERT INTO "ramdane" VALUES(10,104,'10:104','Ini: "a yimdanen! Ma tcukkem di ddin iw, nek ur âabde$ wid tâabdem, war Öebbi. Maca, âabde$ Öebbi ara Idmen leôwaê nnwen. Pwamôe$ ad ili$ si lmumnin".');
INSERT INTO "ramdane" VALUES(10,105,'10:105','Sbedd udem ik i ddin, s umili ar tidep, ur ppili seg imcurak.');
INSERT INTO "ramdane" VALUES(10,106,'10:106','Ur pnuju, war Öebbi, ar wayen ur k inefâan, ur k iîîuôôun. Ma txedmev akken, keçç, imiren, a ppiliv seg imednas.');
INSERT INTO "ramdane" VALUES(10,107,'10:107','Ma Isâadda fellak Öebbi ccqa, ur ak illi ujebbaô siwa Neppa. Ma Irad ak lxiô, ur illi usebîîel i lfevl iS. Ippak it i win i S ihwan, deg ixdimen iS. Neppa Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(10,108,'10:108','Ini: "a yimdanen! Ni$ tusa kwen id tidep s$uô Mass nnwen! Win ipwanhan, ipwanha kan i iman is; win innetlafen, innetlef kan i iman is. Ur lli$, nek, d awkil nnwen".');
INSERT INTO "ramdane" VALUES(10,109,'10:109','Ddu d wayen ik ed ippuweêêan, eûbeô ard Iêudd Öebbi. Neppa, Yif akw imêaddan.');
INSERT INTO "ramdane" VALUES(11,1,'11:1','Alô. Tazmamt, issekniyen iS ppuûewwben, sinna, ppufeûûlen s$uô Uûwib i Ipwaxebbôen.');
INSERT INTO "ramdane" VALUES(11,2,'11:2','Ur âabdet siwa Öebbi. Nek, s$uôeS $uôwen, d aneddaô u d abcir.');
INSERT INTO "ramdane" VALUES(11,3,'11:3','Ssutret ssmaê i Mass nnwen, steôoâet aS, ad Ifeôôeo fellawen afeôôeo ilhan, ar lajel ipwassnen. Ippak lfevl iS i yal bab n lfevl. Ma twexxôem, nekk ugade$ a ppeppuâqbem, ass ameqwran.');
INSERT INTO "ramdane" VALUES(11,4,'11:4','Tu$alin nnwen, ar Öebbi. Neppa Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(11,5,'11:5','Day nitni, s tidep, ur snefvasen ara idmaren nnsen, iwakken ad ffren fellaS? Xas $ummen s iceîîiven nnsen, day ur Iéôa ara ayen teffren akked wayen d qqaôen? Neppa, s tidep, Issen i illan deg idmaren.');
INSERT INTO "ramdane" VALUES(11,6,'11:6','Ur illi lweêc di tmurt, mebla ma illa ôôeéq is ar Öebbi. Issen tanezdu$t is, tanîelt is. Meôôa, di Tezmamt ipbegginen.');
INSERT INTO "ramdane" VALUES(11,7,'11:7','D Neppa i Ixelqen tamurt akked igenwan di sdis wussan. Asgeld iS illa $ef waman. A kwen Ijeôôeb anwi segwen ara ixedmen ayen ilhan, sennig wiyav. Ma tenniv: "ih, kunwi a d tekkrem deffir tmeppant nnwen", wid ijehlen, war ccekk, a d inin: "ayagi, kan, d ssêur n ûûeê".');
INSERT INTO "ramdane" VALUES(11,8,'11:8','Ma Neswexxeô asen aâaqeb ar imir ipwassnen, war ccekk, a d inin: "d acu iT iîîfen"? Ass ideg ara ten id iawev, day illa wa ara ten iveôôfen segs? Ad asen izzi wayen i$ef pmmaâliken.');
INSERT INTO "ramdane" VALUES(11,9,'11:9','Ma Nesêess i umdan aêunu nne ̈£, sinna, Nekkes as t, atan ad iayes, ad ijhel.');
INSERT INTO "ramdane" VALUES(11,10,'11:10','Ma Nesêess as l$it, deffir twa$it i t innulen, atan ad yini: "fukkent felli lemêayen", d$a, neppa, ad ifôeê, ad izux.');
INSERT INTO "ramdane" VALUES(11,11,'11:11','Kkes ed wid iûebbôen, ixeddmen ayen ilhan. I widak ssmaê akked tikci meqqwôen.');
INSERT INTO "ramdane" VALUES(11,12,'11:12','Ahat teppaoav kra n wayen ik ed ippuweêêan. Pzemmiven yis idmaren ik mara qqaôen: "a lemmer i s d irs ugerruj, ne$ idda d, yides, lmelk"! Keçç d aneddaô kan. Öebbi d Awkil n yal cci.');
INSERT INTO "ramdane" VALUES(11,13,'11:13','Ne$ ad inin: "isnulfa t id". Ini: "awit ed, s usnulfu, mraw n tsuôtin am neppa. Naâôet ayen iwumi tzemrem, war Öebbi, ma tessawalem tidep".');
INSERT INTO "ramdane" VALUES(11,14,'11:14','Ma ur awen d rran ara, cfut ar irs ed kan s tussna n Öebbi, ar ur illi ôebbi siwa Neppa. Ihi, ma kunwi d imvuâen?');
INSERT INTO "ramdane" VALUES(11,15,'11:15','I wid ib$an tameddurt n ddunit akked ôôêuê is, a Nkafi, degs, igiten nnsen, ur asen Ntekkes.');
INSERT INTO "ramdane" VALUES(11,16,'11:16','Widak, ur ppafen, di laxeôt, siwa times. Ad i$li wayen ûenâen degs; inqev wayen xeddmen.');
INSERT INTO "ramdane" VALUES(11,17,'11:17','Day win iddan d ubeggen, s$uô Mass is, u immal as t unagi s$uôeS...? U, uqbel is, Tazmamt n Musa, d nnhaya u d aêunu. Wigi umnen yis. Win i p inekôen, seg ikubar, ppaâud is d times. Ur ppili di ccekk fellas. Neppa, ih, d tidep s$uô Mass ik. Maca, tegwti n medden ur ppamnen.');
INSERT INTO "ramdane" VALUES(11,18,'11:18','Anwa idensen sennig win id isnulfan tikerkas $ef Öebbi? Wigi a d rsen i Mass nnsen. A d inin inagan: "d wigi i iskaddben $ef Mass nnsen". Day Öebbi ur Ixezzu yara imednas?');
INSERT INTO "ramdane" VALUES(11,19,'11:19','I isîixxiôen seg ubrid n Öebbi, i t issarmen d uâwij, nitni ur uminen s laxeôt?');
INSERT INTO "ramdane" VALUES(11,20,'11:20','Wigi, ur sfunnugen di tmurt, ur asen illi bab, war Öebbi. Fellasen aâaqeb ar wayev. Ur zmiren ad slen, ur pwalin.');
INSERT INTO "ramdane" VALUES(11,21,'11:21','D wigi i isxesôen iman nnsen. Itelf iten wayen d snulfuyen.');
INSERT INTO "ramdane" VALUES(11,22,'11:22','Ur illi ccekk, ih, nitni ad xesôen, di laxeôt, sennig wiyav.');
INSERT INTO "ramdane" VALUES(11,23,'11:23','Ih, wid iumnen, u xeddmen ayen ilhan, u unzen aS i Mass nnsen, widak d at Loennet, degs ad wekkôen.');
INSERT INTO "ramdane" VALUES(11,24,'11:24','Amedya n snat n trebbuyaâ am uder$al, uâééug, akked win ipwalin, win isellen. Day âadlen sin imedyaten agi? Day ur d tesmektayem ara?');
INSERT INTO "ramdane" VALUES(11,25,'11:25','Ni$ Nuzen ed Nuê i ugdud is: "nek d aneddaô nnwen ipbegginen,');
INSERT INTO "ramdane" VALUES(11,26,'11:26','iwakken ur tâabdem siwa Öebbi. Nek ugaded aâaqeb fellawen, ass aqeôhan".');
INSERT INTO "ramdane" VALUES(11,27,'11:27','Nnan imvebbôen, wid ijehlen, seg ugdud is: "keçç, $uône$, d amdan am nekwni. Nwala, d ôôdiyen nne$, kan, war ameyyez, i k ivefôen. Ur nwala s wacu i $ tifem. Xaîi, nêesb ikwen d ikeddaben".');
INSERT INTO "ramdane" VALUES(11,28,'11:28','Inna: "a yagdud iw! Acu twalam ma ddi$ d ubeggen n Mass iw? Ifka yi d aêunu s$uôeS. Idreg fellawen. Day a t nêeppem fellawen, ma kunwi tugim t?');
INSERT INTO "ramdane" VALUES(11,29,'11:29','A yagdud iw! Ur awen ssuture$ ayla, ilmend is; taqdiât iw ar Öebbi kan. Ur ppara$, nek, wid iumnen. S tidep, ad mlilen d Mass nnsen. Maca, nek wala$ kwen d agdud ur nessin.');
INSERT INTO "ramdane" VALUES(11,30,'11:30','A yagdud iw! Acu ara yi isellken seg ufus n Öebbi, lemmer i ten rri$ akin, day ur d tesmektayem ara?');
INSERT INTO "ramdane" VALUES(11,31,'11:31','Ur awen qqaôe$: $uôi lexzayen n Öebbi. Ur ssine$ tabavnit. Ur qqaôe$: nek d lmelk. Ur qqaôe$ i wid êeqôent wallen nnwen: ur asen Ippak ara Öebbi lxiô, d awez$i. Yif akw Öebbi akken Iéôa, i illan deg iman nnsen. Mulac ad ili$ seg imednas".');
INSERT INTO "ramdane" VALUES(11,32,'11:32','Nnan: "a Nuê! Ni$ tnamrev a$, tessugtev a$ anamer. Ihi, awi ya$ d ayen i $ tâahdev, ma tessawalev tidep".');
INSERT INTO "ramdane" VALUES(11,33,'11:33','Inna: "d Öebbi kan ar awen t id Iawin, ma ihwa yaS. Kunwi ur tesfunnugem.');
INSERT INTO "ramdane" VALUES(11,34,'11:34','Xas b$i$ a kwen neûêe$, ur tesfaydayam seg wanûaê iw, ma Ib$a Öebbi a kwen Itlef. Neppa d Mass nnwen; tu$alin nnwen $uôeS".');
INSERT INTO "ramdane" VALUES(11,35,'11:35','Ne$ qqaôen: "isnulfa t id". Ini: "ma snulfa$ t id, lbe$wv iw i yiri w. Nek, ur ppka$ di lbe$wv nnwen".');
INSERT INTO "ramdane" VALUES(11,36,'11:36','Ippuweêêa d i Nuê: "ur ippamen yiwen, d awez$i, seg ugdud ik, siwa wid illan umnen. Ihi, ur k icqi wayen xeddmen.');
INSERT INTO "ramdane" VALUES(11,37,'11:37','Eûnaâ lbabuô, ger wallen nne£, s uweêêi nne£. Ur iyI d pmeslay $ef wid idensen, war ccekk, ad $emqen".');
INSERT INTO "ramdane" VALUES(11,38,'11:38','La iûennaâ lbabuô. Yal tikelt ara âaddin $uôes imvebbôen n ugdud is, tkaâriren fellas. Inna: "ma temmaâlkem degne$, ihi, a nemmaâlek degwen akken temmaâlkem.');
INSERT INTO "ramdane" VALUES(11,39,'11:39','Deqqal a ppeéôem, u$uô ara d ias uâaqeb ara t isdullen, ara iêaz uâaqeb imezgi".');
INSERT INTO "ramdane" VALUES(11,40,'11:40','Alamma, mi d iusa lameô nne£, la pfeggiven igwefran d ihedman, Nenna: "awi degs tayuga n yal ûûenf, tawacult ik - siwa wid i$ef id izwar wawal -, akked wid iumnen". Drus kan i iumnen yides.');
INSERT INTO "ramdane" VALUES(11,41,'11:41','Inna: "rekbet degs, s yisem n Öebbi, acali s akked trusi s. Mass iw, s tidep, Ipsemmiê, Ipêunu".');
INSERT INTO "ramdane" VALUES(11,42,'11:42','Icula yissen, di lemwao anect idurar. Issawel Nuê i mmi s, i illan weêdes: "a mmi ali d yidne$, ur teppiliv ar ijehliyen"!');
INSERT INTO "ramdane" VALUES(11,43,'11:43','Inna: "ûûôan iw d adrar, ad iyi isellek seg waman". Inna: "assa, ulac leslak si lameô n Öebbi, siwa i win i$ef Iêun". Âaddant lemwao garasen. Iûebê ed seg wid ipwa$emqen.');
INSERT INTO "ramdane" VALUES(11,44,'11:44','Nnan: "a tamurt, seblaâ aman im, a yigenni eûûfu". Aman zzren. Iûaô lameô: irs $ef Loudi. Iffe$ uberraê: akin agdud imednas!');
INSERT INTO "ramdane" VALUES(11,45,'11:45','Inuj Nuê ar Mass is, inna: "a Mass iw! Ni$ mmi n twacult iw. Ih, lemâahda K d tidep, Keçç Tifev akw imêaddan".');
INSERT INTO "ramdane" VALUES(11,46,'11:46','Inna: "a Nuê! Neppa, s tidep, ur illi n wacult ik. Ih, neppa ur ixdim ayen ilhan. Ihi, ur iyI d steqsay $ef wayen ur tessinev. Nek a k Ssakwaye$, ur teppiliv seg wid ur nessin".');
INSERT INTO "ramdane" VALUES(11,47,'11:47','Inna: "a Mass iw! Ili di lâawen iw, iwakken ur K id steqsaye$ $ef wayen ur ssine$. Ma ur iyi Tsemmêev, ur Têunev felli, ad ili$ seg imexsuôen".');
INSERT INTO "ramdane" VALUES(11,48,'11:48','Nnan d: "a Nuê! Ers ed s tifrat s$uône£, akked lbaôaka, fellak akw $ef leoyus i illan yidek. Kra n legyus, a ten N$it. Sinna, fellasen s$uône£, aâaqeb aqeôêan".');
INSERT INTO "ramdane" VALUES(11,49,'11:49','Tagi si teqûivin n tbavnit, Nweêêa yak tent. Ur tent tessinev, keçç, ne$ agdud ik, uqbel wagi. Ihi, eûbeô, taggara, war ccekk, i wid ipêezziben.');
INSERT INTO "ramdane" VALUES(11,50,'11:50','I Âad, gmat sen Hud, inna: "a yagdud iw! Âabdet Öebbi! Ur tesâim ôebbi, war Neppa. Kunwi tesnulfuyem d kan.');
INSERT INTO "ramdane" VALUES(11,51,'11:51','A yagdud iw! Ur awen ssuture$ taqdiât $ef waya. Taqdiât iw ar Win kan, iyi d Ixelqen. Day ur tefhimem ara?');
INSERT INTO "ramdane" VALUES(11,52,'11:52','A yagdud iw! Ssutret ssmaê i Mass nnwen, sinna, steôoâet aS. Ad awen d Isse$li ageffur d ihedman, ad awen irnu tazmert i tezmert nnwen. Ur ppu$alet d ibe$wviyen".');
INSERT INTO "ramdane" VALUES(11,53,'11:53','Nnan as: "a Hud! Ur a$ d tusid s ubeggen, ur neppaoa, nekwni, iôebbiten nne$, ilmend n wawal ik, ur numin yisek.');
INSERT INTO "ramdane" VALUES(11,54,'11:54','Neqqaô kan, d kra iôebbiten nne$, i k i$eôqen". Inna: "nek, Anagi w d Öebbi, u cehhdet ar ur ppka$ ara deg wayen tepcarakem,');
INSERT INTO "ramdane" VALUES(11,55,'11:55','war Neppa. Ihi, andit iyi akken ma tellam, sinna ur iyi sganayet.');
INSERT INTO "ramdane" VALUES(11,56,'11:56','Nek, ppekle$ af Öebbi, Mass iw u Mass nnwen. Ur illi umexluq ur Iîîif seg ixef is. Ih, Mass iw deg ubrid uwqim.');
INSERT INTO "ramdane" VALUES(11,57,'11:57','Ma wexxôen, ni$ ssawve$ awen d ayen i$ef id ppuceggâa$ $uôwen. A d Inûef, Mass iw, agdud war kunwi. Ur T teîîuôum s wacemma. Mass iw, s tidep, d Amêareb n yal cci".');
INSERT INTO "ramdane" VALUES(11,58,'11:58','Mi d iusa lameô nne£, Nsellek Hud akked wid iumnen yides, s uêunu s$uône£. Nsellek iten seg uâaqeb uwâiô.');
INSERT INTO "ramdane" VALUES(11,59,'11:59','Akka Âad. Nekôen issekniyen n Mass nnsen, âuûan imazanen iS. Ddan d lameô n yal aqehhaô iggernennen.');
INSERT INTO "ramdane" VALUES(11,60,'11:60','A ten ivfeô xezzu di ddunit agi, u deg wass n tnekra. Day, war ccekk, ur jhilen ara Âad, ar Mass nnsen? Day maççi: "akin Âad, agdud n Hud"?');
INSERT INTO "ramdane" VALUES(11,61,'11:61','I Tamud, gmat sen ÛüÜaliê, inna: "a yagdud iw! Âabdet Öebbi! Ur tesâim ôebbi, war Neppa. Neppa Ixelq ikwen seg wakal, Izzde$ ikwen fellas. Ssutret aS ssmaê, sinna, steôoâet aS. Mass iw, s tidep, Iqeôb ed, Ippara d awal.');
INSERT INTO "ramdane" VALUES(11,62,'11:62','Nnan as: "a Üaliê, ni$ telliv garane$, uqbel aya, d asirem nne$. Day ad a$ tekksev aâbad n wayen âabden imezwura nne$? Nekwni, s tidep, aql a$ di ccekk imcebbwel, $ef wayen u$uô la $ tneccdev".');
INSERT INTO "ramdane" VALUES(11,63,'11:63','Inna: "a yagdud iw! Acu twalam ma ddi$ d ubeggen n Mass iw? Ifka yi d aêunu s$uôeS. Acu ara yi isellken seg ufus n Öebbi, ma aâûa$ $uôeS? Iyi ternam, siwa taxessaôt.');
INSERT INTO "ramdane" VALUES(11,64,'11:64','A yagdud iw! Tagi d tal$wemt n Öebbi, d assekni i kunwi. Eooet ep a ppeks di tmurt n Öebbi. Ur p pjeyyifet, mulac a kwen id isami uâaqeb iqeôben".');
INSERT INTO "ramdane" VALUES(11,65,'11:65','Gezmen as ivaôen. Inna: "zhut deg ixxamen nnwen, krav wussan. Apan lemâahda ur nepwankaô".');
INSERT INTO "ramdane" VALUES(11,66,'11:66','Mi d iusa lameô nne£, Nsellek Üaliê akked wid iumnen yides, s uêunu s$uône£, si ddell n wass nni. Ih, Mass ik d Uohid, d Uzmir.');
INSERT INTO "ramdane" VALUES(11,67,'11:67','Asu$u iwwet wid idensen, gwran d d imeslax deg ixxamen nnsen,');
INSERT INTO "ramdane" VALUES(11,68,'11:68','amzun ur rbiêen degsen. Day, s tidep, ur jhilen ara Tamud, ar Mass nnsen? Day maççi: "akin Tamud"?');
INSERT INTO "ramdane" VALUES(11,69,'11:69','Ni$ usan d imceggâen nne£ ar Ibôahim, s ubecceô. Nnan: "tifrat"! Inna: "tifrat"! Ur iâaîîel ara ibbwi d aâjmi icwin.');
INSERT INTO "ramdane" VALUES(11,70,'11:70','Mi iwala ifassen nnsen ur d ééilen ara $uôes, isêassef iten, iugad iten. Nnan: "ur ppagwad, nekwni neppuceggaâ ed ar ugdud n Luî".');
INSERT INTO "ramdane" VALUES(11,71,'11:71','Tameîîut is tbedd, tevûa. Nbecceô ip s Isêaq, akked Yaâqub, deffir Isêaq.');
INSERT INTO "ramdane" VALUES(11,72,'11:72','Tenna: "a lêif iw, day a d arwe$, nek d tam$aôt, u argaz agi inu, wesser? Ayagi, s tidep, d lwehma"!');
INSERT INTO "ramdane" VALUES(11,73,'11:73','Nnan: "day twehmev di lameô n Öebbi? Aêunu n Öebbi, akked lbaôaka S, fellawen ay at uxxam. Ih, Neppa d Amaâzuz, Mucaâ".');
INSERT INTO "ramdane" VALUES(11,74,'11:74','Mi s tekkes tugwdi i Ibôahim, u iusa t id ubecceô, inumer a£ $ef ugdud n Luî.');
INSERT INTO "ramdane" VALUES(11,75,'11:75','Ibôahim, s tidep, iêellem, ippanez, isteôoiâ.');
INSERT INTO "ramdane" VALUES(11,76,'11:76','"A Ibôahim, ur keççem deg waya. Ni$ iusa d lameô n Mass ik. Nitni, a ten id ias uâaqeb, war tifrat".');
INSERT INTO "ramdane" VALUES(11,77,'11:77','Mi usan imceggâen nne£ ar Luî, innu$ben, ur isâi i$il a ten ifukk. Inna: "wagi d ass uwâiô".');
INSERT INTO "ramdane" VALUES(11,78,'11:78','Iusa d ugdud is, zeêfen d $uôes. Llan, zik, xeddmen tucmitin. Inna: "a yagdud iw! Atent an issi, nitenti zeddigit, i kunwi. Ëezbet i Öebbi, ur iyi pêeccimet ar inebgawen iw. Day ur illi ara, garawen, urgaz aêesbi"?');
INSERT INTO "ramdane" VALUES(11,79,'11:79','Nnan: "ni$ teéôiv ur nesâi lêeqq deg issi k. Keçç, s tidep, teéôiv d acu neb$a".');
INSERT INTO "ramdane" VALUES(11,80,'11:80','Inna: "a lemmer i wen zmire$, ne$ sâi$ tayep ioehden"!');
INSERT INTO "ramdane" VALUES(11,81,'11:81','Nnan: "a Luî, nekwni d imceggâen n Mass ik. Ur k id ppawven. Öuê akked twacult ik, deg iv. Yiwen degwen ur d itezzi! Kkes ed tameîîut ik. Neppat, s tidep, a ppepwwet s wayen ara ten iwwten. Ih, ppaâud nnsen, taûebêit. Ni$ tqeôb ed tûebêit"?');
INSERT INTO "ramdane" VALUES(11,82,'11:82','Mi d iusa lameô nne£, Nesredm ip. Nesse$li d fellas abruri n yiéôa ibelbaven n talaxt.');
INSERT INTO "ramdane" VALUES(11,83,'11:83','Pwanesxen ar Mass ik. Nitni, ur bâiden i imednas.');
INSERT INTO "ramdane" VALUES(11,84,'11:84','I Madian, gmat sen Cuâayeb, inna: "a yagdud iw! Âabdet Öebbi! Ur tesâim ôebbi, war Neppa. Ur ssenqaset di lkil akked lmizan. Nek, wala$ kwen teôwam, u ugade$ fellawen aâaqeb, ass itezzin.');
INSERT INTO "ramdane" VALUES(11,85,'11:85','A yagdud iw! Ktilet, weznet, s lewqama. Ur îîuôôut imdanen deg wayla nnsen, ur pdenniset, tessefsadem di tmurt.');
INSERT INTO "ramdane" VALUES(11,86,'11:86','Yif it i kunwi wayen d iqqimen i Öebbi, ma d lmumnin ay tellam. Ur lli$, nek, d amêareb nnwen".');
INSERT INTO "ramdane" VALUES(11,87,'11:87','Nnan: "a Cuâayeb, day a$ umôen wawalen ik, a neoo ayen âabden imezwura nne$? Ne$ a nexdem, deg wayla nne$, ayen ur a$ nehwi? Keçç, s tidep, d uêlim, d uûwib".');
INSERT INTO "ramdane" VALUES(11,88,'11:88','Inna: "a yagdud iw! Acu twalam, ma ddi$ d ubeggen n Mass iw? I$at iyi yis, l$it imaânen. Ur b$i$ a kwen mgirrde$, siwa $ef wayen i wen gedle$. B$i$ kan, s wayen zemre$, ad awen ûegme$ tikli. Lâawen iw s$uô Öebbi kan. Ppekle$ fellaS, utube$ T.');
INSERT INTO "ramdane" VALUES(11,89,'11:89','A yagdud iw! Awer kwen teppara nnmara w d ibe$wviyen, a ppevôu yidwen akken tevôa d ugdud n Nuê, ne$ win n Hud, ne$ win n Üaliê! Agdud n Luî, ur ibâad ara segwen.');
INSERT INTO "ramdane" VALUES(11,90,'11:90','Ssutret ssmaê i Mass nnwen, sinna, steroâet aS. Mass iw, s tidep, Ipêunu, Ipêemmil".');
INSERT INTO "ramdane" VALUES(11,91,'11:91','Nnan: "a Cuâayeb, ur nefhim ara aîas n wayen id tenniv. Aql a$ nwala k d uôhif garane$. Lemmer maççi d alag ik, war ccekk, a k neôoem. Ur a$ tezmirev".');
INSERT INTO "ramdane" VALUES(11,92,'11:92','Inna: "a yagdud iw! Day Öebbi, $uôwen, yif iT walag iw, di tezmert? Terram T ar deffir n waârur nnwen. Mass iw, s tidep, Izzi i wayen txeddmem.');
INSERT INTO "ramdane" VALUES(11,93,'11:93','A yagdud iw! Xedmet deg umekkan nnwen, nek ad xedme$. Deqqal a ppeéôem, u$uô ara d ias uâaqeb ara t isdullen, anwa ay d akeddab. Âasset, nek, yidwen, ad âasse$".');
INSERT INTO "ramdane" VALUES(11,94,'11:94','Mi d iusa lameô nne£, Nsellek Cuâayeb akked wid iumnen yides, s uêunu s$uône£. Asu$u iwwet wid idensen, gwran d d imeslax deg ixxamen nnsen,');
INSERT INTO "ramdane" VALUES(11,95,'11:95','amzun ur rbiêen degsen. Day maççi: "akin Madian"! Am: "akin Tamud"?');
INSERT INTO "ramdane" VALUES(11,96,'11:96','Ni$ Nuzen ed Musa, s issekniyen nne£, akked udabu n ûûeê,');
INSERT INTO "ramdane" VALUES(11,97,'11:97','$uô Ferâun akked imvebbôen is. Ddan d lameô n Ferâun. Lameô n Ferâun ur illi d uûwib.');
INSERT INTO "ramdane" VALUES(11,98,'11:98','Ad izwir i ugdud is, ass n tnekra, a ten inheô ar usario n tmes. A yasario ucmit u$uô pwanehôen!');
INSERT INTO "ramdane" VALUES(11,99,'11:99','A ten ivfeô xezzu di tagi, u deg wass n tnekra. A tikci tucmip ippunefken!');
INSERT INTO "ramdane" VALUES(11,100,'11:100','Ayagi, si teqûivin n temdinin, i k d Nessawal. Garasent tid ibedden, tid ipwamegren.');
INSERT INTO "ramdane" VALUES(11,101,'11:101','Ur ten Nednis. Maca, densen iman nnsen. Ur ten nefâan s wacemma, iôebbiten nnsen i la naâôen, war Öebbi, mi d iusa lameô n Mass ik. I sen rnan, siwa ssxev.');
INSERT INTO "ramdane" VALUES(11,102,'11:102','Akka tiyta n Mass ik. Mi Iwwet timdinin i idensen, s tidep, tiyta S teqôeê, teqseê.');
INSERT INTO "ramdane" VALUES(11,103,'11:103','Ih, deg waya, assekni i wid iugaden aâaqeb n laxeôt. Ass nni, d ass unejmaâ n yimdanen. Ass nni, d ass n ccada.');
INSERT INTO "ramdane" VALUES(11,104,'11:104','A t Neswexxeô kan ar lajel iuran.');
INSERT INTO "ramdane" VALUES(11,105,'11:105','Ass ideg ara d ias, ur d ipmeslay yiwen, siwa ma s tesriê iS. Ad ilin igeswaêen, isaâdiyen.');
INSERT INTO "ramdane" VALUES(11,106,'11:106','Wanag igeswaêen ad ilin di tmes, s unazaâ d imeîîi,');
INSERT INTO "ramdane" VALUES(11,107,'11:107','ad wekkôen dinna, ayen ara dumen tmurt akked igenwan, siwa ma ur Irad ara Mass ik. Ih, Mass ik Ixeddem ayen i S ihwan.');
INSERT INTO "ramdane" VALUES(11,108,'11:108','Wanag isaâdiyen, di Loennet, ad wekkôen, dinna, ayen ara dumen tmurt akked igenwan, siwa ma ur Irad ara Mass ik. D tikci n war tilas.');
INSERT INTO "ramdane" VALUES(11,109,'11:109','Ur k ikeççem ccekk $ef wayen âabden widak. Ayen âabden am wayen âabden imezwura nnsen, zik. War ccekk, ad asen Nefk amur nnsen, ur inqis.');
INSERT INTO "ramdane" VALUES(11,110,'11:110','Ni$, s tidep, Nefka Tazmamt i Musa. Mgarraden fellas. Lemmer ur d izwar ara wawal s$uô Mass ik, yili useyyi garasen. Haaten, ih, di ccekk imcebbwel fellas.');
INSERT INTO "ramdane" VALUES(11,111,'11:111','War ccekk, akken ma llan, ad Ikafi Mass ik igiten nnsen, s lmizan. Neppa, s tidep, Issen ayen xeddmen.');
INSERT INTO "ramdane" VALUES(11,112,'11:112','Ihi, ili d uwqim, akken i k ippunefk lameô, keçç akked wid isteôoâen yidek. Ur be$wvet. Neppa, s tidep, Ipwali ay txeddmem.');
INSERT INTO "ramdane" VALUES(11,113,'11:113','Ur pqeôôibet ar wid idensen, a kwen tnal tmes! Ur tesâim bab, war Öebbi, ihi, ur tepwasellkem.');
INSERT INTO "ramdane" VALUES(11,114,'11:114','èéal ûbeê, tameddit, kra n lewqat n yiv. S tidep, ise$iyen neqqven tucmitin. Ayagi d asmekti i wid ismektayen.');
INSERT INTO "ramdane" VALUES(11,115,'11:115','Eûbeô! Öebbi, s tidep, ur Iveggiâ taqdiât n lâuûam.');
INSERT INTO "ramdane" VALUES(11,116,'11:116','A lemmer llan, di tsuta i kwen id izwaren, imezgiyen ineqqven asefsed di tmurt! Qlilit kan seg wid Nsellek segsen. Wid idensen ddan akked wayen i ten izhan, u$alen d ibe$wviyen.');
INSERT INTO "ramdane" VALUES(11,117,'11:117','Ur Isquccu ara Mass ik timdinin, d aêerfi, ma llan imezda$ nnsent d lâuûam.');
INSERT INTO "ramdane" VALUES(11,118,'11:118','Lemmer i S ihwi i Mass ik, ad Irr imdanen d yiwen n lois. Zgan mgarraden.');
INSERT INTO "ramdane" VALUES(11,119,'11:119','Kkes ed wid i$ef Iêun Mass ik. Ixelq iten i wayagi. Itam wawal n Mass ik: "ad Acaôe$ Loahennama, war ccekk, s leonun akked yimdanen mejmulen".');
INSERT INTO "ramdane" VALUES(11,120,'11:120','Ayen akw i k ed Neêka n tmucuha imazanen, iwakken a Ntebbet ul ik, yissent. Id iusan dagi d tidep, d anabi u d asmekti i lmumnin.');
INSERT INTO "ramdane" VALUES(11,121,'11:121','Ini i wid ur numin ara: "xedmet ay tzemrem, nekwni s tidep, la nxeddem.');
INSERT INTO "ramdane" VALUES(11,122,'11:122','Gganit, nekwni la nepgani".');
INSERT INTO "ramdane" VALUES(11,123,'11:123','N Öebbi tabavnit n tmurt akked igenwan. £uôeS ara u$alen akw lumuô. Ihi, âabed iT, ppkel fellaS. Ur I$effel ara Mass ik, $ef wayen txeddmem.');
INSERT INTO "ramdane" VALUES(12,1,'12:1','Alô. Wigi d issekniyen n Tazmamt ipbegginen.');
INSERT INTO "ramdane" VALUES(12,2,'12:2','Ih, Nessader it id d Leqwôan, s taâôabt, amar a pfehmem.');
INSERT INTO "ramdane" VALUES(12,3,'12:3','Nekwni Nêekku yak tiêkayin igerzen mavi, deg wayen i k Nweêêa di Leqwôan agi, xas telliv, uqbel is, seg wid istehzan.');
INSERT INTO "ramdane" VALUES(12,4,'12:4','Mi inna Yusef i baba s: "a baba! Nek wala$ mrawyan itran, iîij, akked waggur, wala$ ten knan iyi".');
INSERT INTO "ramdane" VALUES(12,5,'12:5','Inna: "a mmi! Ur êekku ayen twalav i watmaten ik, ad ak andin ticeôkep. Cciîan, s tidep, d acengu n ûûeê n umdan.');
INSERT INTO "ramdane" VALUES(12,6,'12:6','Akka, a k Ifren Mass ik, u ak Isselmed asefru n temsal, u ad Itim l$it iS, i keçç akked twacult n Yaâqub, akken it Itam, zik, af lejdud ik: Ibôahim akked Isêaq. Mass ik, s tidep, d Amusnaw, d Uûwib".');
INSERT INTO "ramdane" VALUES(12,7,'12:7','Ni$, di Yusef akked watma s, illa ussekni i wid isteqsayen.');
INSERT INTO "ramdane" VALUES(12,8,'12:8','Mi nnan: "war ccekk, ifen a$, Yusef akked gma s, akken iten iêemmel babat nne$, u nekwni d îîeôma. Babat nne$, s tidep, haat di ttelfan n ûûeê.');
INSERT INTO "ramdane" VALUES(12,9,'12:9','En$et Yusef, ne$ nfut ep si tmurt, a d ilhu degwen babat wen, sinna, a ppu$alem d imdanen ixeddmen ayen ilhan".');
INSERT INTO "ramdane" VALUES(12,10,'12:10','Inna win d immeslayen, degsen: "ur neqqet Yusef, veggôet ep ar lqaâa n wannu. Ma tgam akka, a t iddem yiwen umsebrid".');
INSERT INTO "ramdane" VALUES(12,11,'12:11','Nnan: "a babat nne$, acu k iu$en, ur a$ tuminev ara, $ef Yusef. Nekwni, fellas, s tidep, d unûiêen.');
INSERT INTO "ramdane" VALUES(12,12,'12:12','Sseddu t yidne$ azekka, ad izhu, ad iurar. Nekwni, war ccekk, a nêareb fellas".');
INSERT INTO "ramdane" VALUES(12,13,'12:13','Inna: "nek, s tidep, $weble$ ma tebbwim t yidwen, ugade$ a t içç lweêc, kunwi testehzam degs".');
INSERT INTO "ramdane" VALUES(12,14,'12:14','Nnan: "ma içça t lweêc, u nekwni d îîeôma, s tidep a nili d imexsuôen".');
INSERT INTO "ramdane" VALUES(12,15,'12:15','Mi t bbwin, mwafaqen a t rôen ar lqaâa n wannu, Nweêêa yas: "ad asen temlev, s tidep, lameô agi nnsen, nitni ur éôin".');
INSERT INTO "ramdane" VALUES(12,16,'12:16','U$alen d tameddit, ar babat sen, pprun.');
INSERT INTO "ramdane" VALUES(12,17,'12:17','Nnan: "a babat nne$, s tidep, nôuê a nemzizzel, neooa Yusef ar lqecc nne$, içça t lweêc. Ur a$ teppamnev, xas nenna d tidep".');
INSERT INTO "ramdane" VALUES(12,18,'12:18','Bbwin d tasedrit is, degs idamen n rrya. Inna: "xaîi! D iman nnwen i wen d iccacven lameô. Ilha ûûbeô. Anuju $eô Öebbi, mgal ayen d teêkam".');
INSERT INTO "ramdane" VALUES(12,19,'12:19','Usan d iminigen, uznen win id ippagwmen, issader ssvel is. Inna: "a lxiô, wagi d aqcic"! Ffren t, amzun d sselâa. Öebbi Iéôa acu xeddmen.');
INSERT INTO "ramdane" VALUES(12,20,'12:20','Zzenzen t, s ôôxa, s kra iûuôdiyen. Ur as fkin ara azal.');
INSERT INTO "ramdane" VALUES(12,21,'12:21','Inna, i tmeîîut is, Umaûôi i t iu$en: "eôêeb yis, amar ad a$ infaâ, at nerr d mmit nne$". Akka ay Nesseqâad Yusef di tmurt, iwakken ad as Nesseêfev asefru n temsal. Öebbi d Amezwaru s lameô iS, maca tegwti n medden ur éôin ara.');
INSERT INTO "ramdane" VALUES(12,22,'12:22','Mi d immeêtel, Nefka yas ûûwab, tussna. Akka ay Nepkafi iâaôviyen.');
INSERT INTO "ramdane" VALUES(12,23,'12:23','Teb$a t tin u$ur izde$. Terra tibbura, u tenna: "eyya keçç"! Inna: "ad iyi Ineooi Öebbi! Ni$ bab iw iôêeb yissi. S tidep, ur rebêen imednas".');
INSERT INTO "ramdane" VALUES(12,24,'12:24','S tidep, teb$a t, u neppa ib$a p, lemmer ur iwala ara atebbet n Mass is. Akka, iwakken a Nessebâad fellas tuccva, lâaô. Neppa, s tidep, seg imdanen nne£ izedganen.');
INSERT INTO "ramdane" VALUES(12,25,'12:25','Uzzlen ar tebburt, tcerreg as tasedrit is, si zdeffir. Mmugren, argaz is, ar tebburt. Tenna: "acu ay d aqeîîi n win iâabwlen ad iccmet tawacult ik, siwa lêebs ne$ aâaqeb aqeôêan"?');
INSERT INTO "ramdane" VALUES(12,26,'12:26','Inna: "d neppat iyi ib$an". Icuhhed ed yiwen unagi si twacult is: "ma teqqeôs tsedrit si zdat, tenna d tidep, u neppa d akeddab.');
INSERT INTO "ramdane" VALUES(12,27,'12:27','Ma teqqeôs tsedrit si zdeffir, teskaddeb, u neppa seg at tidep".');
INSERT INTO "ramdane" VALUES(12,28,'12:28','Mi iwala teqqeôs tsedrit si zdeffir, inna: "ayagi, war ccekk, si nncub nnkwent. Ih, nncub nnkwent d timeqwôaêanin.');
INSERT INTO "ramdane" VALUES(12,29,'12:29','A Yusef, ppu ayagi, u kem ssuter ssmaê ubekkav im. Kem, s tidep, teccvev".');
INSERT INTO "ramdane" VALUES(12,30,'12:30','La qqaôent tlawin, di temdint: "tameîîut umdebbeô têemmel acrik is. Teshebl ip tayri. F akken nwala, appan, s tidep, di ttelfan n ûûeê".');
INSERT INTO "ramdane" VALUES(12,31,'12:31','Mi tesla s nncub nnsent, tuzen $uôsent, thegga yasent tanalt. I yal yiwet segsent, tefka tafrut. Tenna: "effe$ ed garasent". Mi t walant, isdehc itent, jerêent ifassen nnsent, nnant: "êaca Öebbi, wagi maççi d amdan, wagi siwa d lmelk ukyis".');
INSERT INTO "ramdane" VALUES(12,32,'12:32','Tenna: "d wagi i$ef iyi tlummemt. Ih, b$i$ t, neppa iqqim zeddig. Ma ur ixdim ara ay s umôe$, war ccekk, ad ipwaêbes, ad ipwaêqeô".');
INSERT INTO "ramdane" VALUES(12,33,'12:33','Inna: "a Mass iw! Smenyafe$ lêebs i wayen u$ur iyi d ssawalent. Ma ur Tesbaâdev ara felli nncub nnsent, ad male$ $uôsent, ad ili$ seg wid ur nessin".');
INSERT INTO "ramdane" VALUES(12,34,'12:34','Ijaweb as d Mass is. Issebâad fellas nncub nnsent. Neppa, s tidep, Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(12,35,'12:35','Sinna, mi ten usan issekniyen, ufan, war ccekk, ilaq a t irr ar lêebs, kra n tallit.');
INSERT INTO "ramdane" VALUES(12,36,'12:36','Kecmen yides, ar lêebs, sin ilmeéiyen. Inna yiwen degsen: "nek wala$ iman iw, la teééme$ ccôab". Inna d wayev: "nek wala$ iman iw, la ppawi$ a$ôum $ef uqeôôu w, la teppen segs ifrax. Ssefru ya$ p id. Ih, nwala k seg iâaôviyen".');
INSERT INTO "ramdane" VALUES(12,37,'12:37','Inna: "seg wayen iyi ss$eô Mass iw, ad awen p id ssefru$, uqbel a kwen id iawev lqut i wen ppakken. Nek ooi$ ddin n ugdud ur numin s Öebbi, u nitni nekôen laxeôt.');
INSERT INTO "ramdane" VALUES(12,38,'12:38','Ddi$ d ddin n lejdud iw: Ibôahim, Isêaq akked Yaâqub. Ur a$ ippunefk a nezdukkel acemma i Öebbi. Ayagi si lfevl n Öebbi fellane$, akw $ef imdanen. Maca, tegwti n yimdanen ur snemmiren.');
INSERT INTO "ramdane" VALUES(12,39,'12:39','A yimeddukwal iw n lêebs, menyif imawlan imfaôaqen, ne$ Öebbi Yiwen, Aqehhaô?');
INSERT INTO "ramdane" VALUES(12,40,'12:40','Ay tâabdem, war Neppa, d ismawen kan i sen tefkam, kunwi akked imezwura nnwen. Ur asen d Issers ara Öebbi kra udabu. Lêukm ar Öebbi kan. Iumeô ed ur tâabdem, siwa Neppa. D wagi ay d ddin uûwib, maca tegwti n yimdanen ur ssinen.');
INSERT INTO "ramdane" VALUES(12,41,'12:41','A yimeddukwal iw n lêebs, wanag yiwen degwen ad ismiri ccôab, i bab is, wayev ad ippusemmeô $ef umidag, ifrax ad nneqben aqeôôu s. Ippuseyya lameô $ef wayen iyi d testeqsam".');
INSERT INTO "ramdane" VALUES(12,42,'12:42','Inna i win iêûa ad islek, segsen: "ar bab ik, smekti iyi d". Cciîan iseppu t, ur t id ismekti ar bab is. Irna, di lêebs, kra iseggwasen.');
INSERT INTO "ramdane" VALUES(12,43,'12:43','Inna ugellid: "nek wala$ ûa testan tiqebwaniyin, la tent teppent ûa tuvâifin, akked ûa tyedrin tizegzawin, tiyav d tiqquôanin. A yimvebbôen, ssefrut iyi d targit, ma tellam seg wid issefruyen tirga".');
INSERT INTO "ramdane" VALUES(12,44,'12:44','Nnan: "d tirga iwlellsen. Ur nelli d imusnawen i usefru n tirga".');
INSERT INTO "ramdane" VALUES(12,45,'12:45','Win i isellken si sin nni, armi tallit id immekti, inna: "nek, ad awen d awi$ asefru ines, aznet iyi.');
INSERT INTO "ramdane" VALUES(12,46,'12:46','A Yusef, bab n tidep, ssefru ya$ d: ûa testan tiqebwaniyin, la tent teppent ûa tuvâifin, akked ûa tyedrin tizegzawin, tiyav d tiqquôanin. Amar, ma u$ale$ ar imdanen, amar ad éôen".');
INSERT INTO "ramdane" VALUES(12,47,'12:47','Inna: "zerâat ûa iseggwasen imyevfaôen. Ayen tzerâam, eooet ep di tyedrin is, siwa cwiî ara teççem.');
INSERT INTO "ramdane" VALUES(12,48,'12:48','Sinna, deffir sen, a d asen ûa iwaâôen. Ad ççen ayen i sen theggam siwa cwiî, seg wayen txeznem.');
INSERT INTO "ramdane" VALUES(12,49,'12:49','Sinna a d ias, deffir waya, useggwas n tallalt i medden, ideg ara éden".');
INSERT INTO "ramdane" VALUES(12,50,'12:50','Inna ugellid: "awit ep id $uôi". Mi t id iusa umersul, inna: "u$al ar bab ik, u steqsi t acu ay d l$eôv n tlawin i ijerêen ifassen nnsent. Mass iw, s tidep, Issen nncub nnsent".');
INSERT INTO "ramdane" VALUES(12,51,'12:51','Inna: "acu ara d tinimt mi teb$amt Yusef"? Nnant: "êaca Öebbi, ur nufi $uôes tuccva". Tenna d tmeîîut umvebbeô: "tura, a d tban tidep. D nek i t ib$an, ma d neppa, s tidep, d bab n tidep".');
INSERT INTO "ramdane" VALUES(12,52,'12:52','"Akka, iwakken ad iéeô ur t xdiâa$ ara s tuffra. Öebbi ur Inehhu nncub n lxeyyan.');
INSERT INTO "ramdane" VALUES(12,53,'12:53','Ur pzekki$ iman iw. Tanefsit, s tidep, tezzu$uô ar tuccva, siwa ma Iêun Mass iw. Mass iw, s tidep, Ipsemmiê, Ipêunu".');
INSERT INTO "ramdane" VALUES(12,54,'12:54','Inna ugellid: "awit ep id $uôi, d laâtiq iw". Mi s immeslay, inna: "keçç, assa, nefka yak adabu, laman".');
INSERT INTO "ramdane" VALUES(12,55,'12:55','Inna: "wekkel iyi $ef lemxazen n tmurt, nek d amêareb amusnaw".');
INSERT INTO "ramdane" VALUES(12,56,'12:56','Akka ay Nesseqâad Yusef di tmurt, ad izde$ anda i s ihwa. Nepêunu $ef win i £ ihwan, ur Nesôuêu arraz iâaôviyen.');
INSERT INTO "ramdane" VALUES(12,57,'12:57','S tidep, arraz n laxeôt yif it, i wid iumnen u llan pêezziben.');
INSERT INTO "ramdane" VALUES(12,58,'12:58','Usan watmaten n Yusef, kecmen $uôes, iâqel iten, nitni ur t âqilen.');
INSERT INTO "ramdane" VALUES(12,59,'12:59','Mi sen ifka ppâabgat nnsen, inna: "awit iyi d gmat wen s babat wen. Day ur twalam ara zade$ awen lkil, u, nek, ife$ akw wid isnebgaten?');
INSERT INTO "ramdane" VALUES(12,60,'12:60','Ma ur iyi t id tebbwim ara, ur awen nepktili $uôi, ur iyi d tepsamim ".');
INSERT INTO "ramdane" VALUES(12,61,'12:61','Nnan: "a nêawet baba s fellas; akka ara nexdem".');
INSERT INTO "ramdane" VALUES(12,62,'12:62','Inna i iqeddacen is: "rret selâa nnsen deg icekkaôen nnsen, amar a p âaqlen mara awven ar twacult nnsen, amar a d u$alen".');
INSERT INTO "ramdane" VALUES(12,63,'12:63','Mi bbwven ar babat sen, nnan: "a babat nne$, igdel fella$ lkil, azen gmat nne$ yidne$, iwakken a d nektil. Nekwni, war ccekk, a nêareb fellas".');
INSERT INTO "ramdane" VALUES(12,64,'12:64','Inna: "day a kwen amne$ fellas, akken i kwen umne$, zik, $ef gma s? Öebbi Yif akw imêaraben. Neppa Iphunu sennig akw iêninen".');
INSERT INTO "ramdane" VALUES(12,65,'12:65','Mi ldin lqecc nnsen, ufan tu$al iten id selâa nnsen. Nnan: "a babat nne$, ur neb$i tagi, selâa nne$ tu$al a$ d. A d nessikrer i twacult nne$, a nêareb $ef gmat nne$, a d nernu ppâabga n ul$wem. Tagi d ppâabga tushilt".');
INSERT INTO "ramdane" VALUES(12,66,'12:66','Inna: "ur t sedduye$ yidwen, d awez$i, siwa ma tcubkem iyi s Öebbi, ard a yi t id terrem, siwa ma zzin awen". Mi s âahden, inna: "Öebbi d Awkil $ef wayen id nenna".');
INSERT INTO "ramdane" VALUES(12,67,'12:67','Inna: "a tarwa, ur keççmet si yiwet n tebburt, kecmet si tebbura imgarraden. Ula i wen xedme$, bippa, ar loiha n Öebbi. Aêuddu siwa win n Öebbi. FellaS ppekle$, fellaS ay ppeklen wid ippkalen".');
INSERT INTO "ramdane" VALUES(12,68,'12:68','Mi kecmen akken i sen iumeô babat sen, ula i sen ixdem, bippa, ar loiha n Öebbi. D ayen ilezmen kan i iseyya Yaqub, deg iman is. Neppa, s tidep, isâa tamusni i s Nesselmed, maca tegwti n yimdanen ur ssinen.');
INSERT INTO "ramdane" VALUES(12,69,'12:69','Mi kecmen ar Yusef, ibbwi gma s $uôes, inna: "nekkini d gma k, ur $webbel $ef wayen xeddmen".');
INSERT INTO "ramdane" VALUES(12,70,'12:70','Mi sen ifka ppâabgat nnsen, irra taqbuct deg ucekkaô n gma s. Sinna, iberreê uberraê: "a yat terkaf, kunwi, war ccekk, d imakwaren"!');
INSERT INTO "ramdane" VALUES(12,71,'12:71','Nnan, u zzin $uôsen: "d acu tesôuêem"?');
INSERT INTO "ramdane" VALUES(12,72,'12:72','Nnan: "nesôuê taqbuct ugellid. Win ip id ibbwin, ad as nefk ppâabga n ul$wem. Nek, vemne$ t".');
INSERT INTO "ramdane" VALUES(12,73,'12:73','Nnan: "s Öebbi, nni$ teéôam ur d nusa ara a nessefsed tamurt, ur nelli d imakwaren".');
INSERT INTO "ramdane" VALUES(12,74,'12:74','Nnan: "d acu ay d aqeîîi s, ma teskadbem"?');
INSERT INTO "ramdane" VALUES(12,75,'12:75','Nnan: " aqeîîi n win i p ibbwin di tcekkaôt is, d yiman is. Akka ay nepkafi imednas".');
INSERT INTO "ramdane" VALUES(12,76,'12:76','Ibda la ipqellib icekkaôen, uqbel win n gma s, sinna, issuffe$ ip id seg ucekkaô n gma s. Akka ay Nefka ticeôkep i Yusef. Ur izmir ad iîîef gma s, s lqanun ugellid, siwa ma Irad Öebbi. Nessalay, tiseddaôin, win i £ihwan. Sennig bu tussna, illa Umusnaw.');
INSERT INTO "ramdane" VALUES(12,77,'12:77','Nnan: "ma iuker, ni$ iuker, zik, yiwen n watma s". Iffer ip Yusef $uôes, ur asen p id inna ara. Inna: "aql akwen deg yir êala. Öebbi Yif akw akken Iéôa ayen d teqqaôem".');
INSERT INTO "ramdane" VALUES(12,78,'12:78','Nnan: "a yameqwôan, baba s, s tidep, wesser. Eîîef yiwen segne$ deg umekkan is. Nwala k, ih, telliv si lâuûam".');
INSERT INTO "ramdane" VALUES(12,79,'12:79','Inna: "ur a$ Iâammed Öebbi a neîîef, siwa win u$uô nufa ta$awsa nne$. Mulac, war ccekk, a nili d imednas".');
INSERT INTO "ramdane" VALUES(12,80,'12:80','Mi uysen segs, mcawaôen, inna umeqwôan segsen: "day ur teéôim ara, ar babat wen tcubkem t s Öebbi, u, zik, tveggâam Yusef? Ihi, ur pôuêu$ si tmurt a, d awez$i, alamma iserreê iyi baba, ne$ Ifra Öebbi felli. Neppa, Yif akw imêaddan.');
INSERT INTO "ramdane" VALUES(12,81,'12:81','U$alet ar babat wen, tinem as: a babat nne$, mmi k, s tidep, iuker. Ncehhed kan $ef wayen neéôa, ur nessawev i tbavnit.');
INSERT INTO "ramdane" VALUES(12,82,'12:82','Steqsi tamdint ideg i nella, akked terkift ukud nedda. Ih, d tidep, ay d nenna".');
INSERT INTO "ramdane" VALUES(12,83,'12:83','Inna: "xaîi! Lameô seg iman nnwen. Ilha ûûbeô. Amar Öebbi a yi ten id Iawi akw. Neppa, s tidep, d Bab n ûûwab, d Amusnaw".');
INSERT INTO "ramdane" VALUES(12,84,'12:84','Ijuneb iten, u inna: "a lêif iw $ef Yusef"! Allen is ijegger itent u$ilif, innu$ben.');
INSERT INTO "ramdane" VALUES(12,85,'12:85','Nnan: "s Öebbi, tezgiv tesmektayev d Yusef, alamma tu$alev d amuvin, ne$ seg wid immuten".');
INSERT INTO "ramdane" VALUES(12,86,'12:86','Inna: "la pcetki$ kan i Öebbi, s uneémi w akked u$wbel iw. Ssne$, s$uô Öebbi, ayen ur tessinem.');
INSERT INTO "ramdane" VALUES(12,87,'12:87','A yarraw iw, ôuêet, steqsit $ef Yusef akked gma s. Ur ppayset di tecvaî n Öebbi. Ih, i ippaysen di tecvaî n Öebbi, siwa agdud ijehliyen".');
INSERT INTO "ramdane" VALUES(12,88,'12:88','Mi bbwven $uôes, nnan as: "a yameqwôan, nekwni akked twacult nne$, nepwa$. Nusa d s selâa ôxisen, semd as lkil, seddeq a$. Öebbi, s tidep, Ipkafi wid ipseddiqen".');
INSERT INTO "ramdane" VALUES(12,89,'12:89','Inna: "teéôam i tgam i Yusef akked gma s, mi kunwi, d wid ur nessin"?');
INSERT INTO "ramdane" VALUES(12,90,'12:90','Nnan: "keçç, s tidep, maççi d Yusef "? Inna: "nek d Yusef, atan gma. Ni$ I$at a$ Öebbi. Ih, wid ipêezziben, wid iûebbôen ... Öebbi, s tidep, ur Isôuêu arraz iâaôviyen".');
INSERT INTO "ramdane" VALUES(12,91,'12:91','Nnan: "s Öebbi ar k Ismenyaf Öebbi garane$. Nekwni, s tidep, neccev".');
INSERT INTO "ramdane" VALUES(12,92,'12:92','Inna: "ur telli tiyta degwen, assa. Ad awen Isemmeê Öebbi! Neppa Yif akw wid ipêunun.');
INSERT INTO "ramdane" VALUES(12,93,'12:93','Awit tasedrit iw agi, erret ep $ef udem n baba, ad iêlu yiéôi s. Aset ed $uôi, s twacult nnwen, meôôa".');
INSERT INTO "ramdane" VALUES(12,94,'12:94','Mi teqlaâ terkift, babat sen ibda la iqqaô: "nek, s tidep, sraêe$ rriêa n Yusef, awi d kan ur teqqaôem ara: la isxeôwiv".');
INSERT INTO "ramdane" VALUES(12,95,'12:95','Nnan: "s Öebbi, ar k mazal di ttelfan ik n zik".');
INSERT INTO "ramdane" VALUES(12,96,'12:96','Mi d iusa ubcir, issers as p $ef udem is, llint ed wallen is, inna: "day ur awen nni$ ara: nek ssne$, s$uô Öebbi, ayen ur tessinem"?');
INSERT INTO "ramdane" VALUES(12,97,'12:97','Nnan as: "a babat nne$, ssuter ad a$ pwasemmêen ibekkaven nne$. S tidep, nella d imednab".');
INSERT INTO "ramdane" VALUES(12,98,'12:98','Inna: "deqqal ad ssutre$, i Mass iw, ad awen Isemmeê. Neppa, s tidep, Ipsemmiê, Ipêunu".');
INSERT INTO "ramdane" VALUES(12,99,'12:99','Mi bbwven ar Yusef, isteôêeb s imawlan is. Inna: "kecmet ar Maseô, di laman, ma Irad Öebbi".');
INSERT INTO "ramdane" VALUES(12,100,'12:100','Issuli imawlan is ar usgeld. £lin, knan as. Inna: "a baba! D wagi ay d asefru n targit iw, n zik. Ni$ Issuffe$ ip Mass iw! Ni$ Iga yi leqôaô mi yi d Issuffe$ si lêebs. Ikkes ikwen id si sseêôa, deffir tismin i isker Cciîan, gari d watma. Mass iw, s tidep, d Laîif i win i S ihwan. Ih, Neppa d Amusnaw, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(12,101,'12:101','A Mass iw, ni$ Tefkiv iyi si tgelda, Tesêefvev iyi asefru n temsal. Axellaq n tmurt akked igenwan, Keçç, d Bab iw di ddunit, di laxeôt, Eddem ôôuê iw d amvua, Ssiwev iyi ar wid imaânen".');
INSERT INTO "ramdane" VALUES(12,102,'12:102','Tagi, si teêkayin n tbavnit, Nweêêa yak p in. Ur telliv $uôsen, mi mwafaqen a p andin.');
INSERT INTO "ramdane" VALUES(12,103,'12:103','Tegwti n yimdanen, xas tâapbev, ur ppamnen.');
INSERT INTO "ramdane" VALUES(12,104,'12:104','Ur asen ssutur kra n teqdiât. Neppa, d asmekti kan i imavalen.');
INSERT INTO "ramdane" VALUES(12,105,'12:105','I illan deg issekniyen di tmurt akked igenwan! La teddun fellasen, maca wexxôen asen.');
INSERT INTO "ramdane" VALUES(12,106,'12:106','Tegwti, segsen, ur ppamnen s Öebbi, siwa ma curken.');
INSERT INTO "ramdane" VALUES(12,107,'12:107','Day ppeklen ur asen isburru yara uâaqeb n Öebbi, ne$ ur ten id ippas ara yimir, deqzalla, ur pfaqan?');
INSERT INTO "ramdane" VALUES(12,108,'12:108','Ini: "wagi d abrid iw, la pnuju$ ar Öebbi ad Ildi allen, i nek akked wid iddan yidi. Gedha s Öebbi! Ur lli$ seg wid ipcaraken".');
INSERT INTO "ramdane" VALUES(12,109,'12:109','Nuzen ed kan, uqbel ik, irgazen seg imezda$ n temdinin, iwumi Nweêêa. Day ur unagen ara di tmurt, ad walin amek tevôa d wid i ten id izwaren? Ih, pifxiô axxam aneggaru i wid ipêezziben. Day ur tefhimem ara?');
INSERT INTO "ramdane" VALUES(12,110,'12:110','Alamma, mi uysen imazanen, u meyyzen ar skaddben ten, tusa ten id tallalt s$uône£. Nepsellik win i £ ihwan. Ur ipwakkas ara ssxev nne£, i ugdud ibe$wviyen.');
INSERT INTO "ramdane" VALUES(12,111,'12:111','Ni$, di teêkayin nnsen, illa uselmed i yuâqilen. Ur telli d awal id innulfan. Neppat tetebbet ed ay d izwaren, tfeûûel ed yal cci. D nnhaya u d aêunu i ugdud iumnen.');
INSERT INTO "ramdane" VALUES(13,1,'13:1','Almô. Wigi d issekniyen n Tezmamt. Ayen i k d irsen, s$uô Mass ik, d tidep, maca tegwti n medden ur ppamnen.');
INSERT INTO "ramdane" VALUES(13,2,'13:2','Öebbi i Issulin igenwan war tigwejda ara twalim, sinna, Iâannec $ef usgeld. Iêeppem i yiîij d waggur ad ddun, akw, ar lajel ipwassnen. Iseyyi lameô, Ipfeûûil issekniyen, amar a pepputebbtem $ef temlilit d Mass nnwen.');
INSERT INTO "ramdane" VALUES(13,3,'13:3','Neppa i Isswesâen tamurt. Irra degs idurar, isaffen. Irra tayuga di yal ppemôa. Ip$ummu ass s yiv. Deg waya, s tidep, d issekniyen i ugdud ipxemmimen.');
INSERT INTO "ramdane" VALUES(13,4,'13:4','Di tmurt, tibêirin tioiratin, leonanat n téuôin, nnaâma, tizdayin, v$an ne$ ur v$in, cerken aman nnsen. Nesmenyaf kra i kra, i wuççi. Deg waya, s tidep, d issekniyen i ugdud ifehmen.');
INSERT INTO "ramdane" VALUES(13,5,'13:5','Ma twehmev, issewham ay d qqaôen: "ma nu$al d akal, day fella$, s tidep, lxelq amaynut"? D wigi i inekôen Mass nnsen. Wigi, azaglu $ef tmegôav. Wigi d at tmes, nitni, degs, ad wekkôen.');
INSERT INTO "ramdane" VALUES(13,6,'13:6','Sêaren k ar tucmip, uqbel ise$. Ni$ vôant am tigi, uqbel nnsen. Mass ik, s tidep, $uôeS ssmaê i imdanen deg wayen ccven. Mass ik, s tidep, Iwâaô deg uâaqeb.');
INSERT INTO "ramdane" VALUES(13,7,'13:7','La qqaôen wid ijehlen: "a lemmer id irs $uôes ussekni, s$uô Mass is". Keçç, d aneddaô kan. I yal agdud, win inehhun.');
INSERT INTO "ramdane" VALUES(13,8,'13:8','Öebbi Iéôa i illan di yal tadist, amek teddun inzikmiren. Yal cci, $uôeS, s lmizan.');
INSERT INTO "ramdane" VALUES(13,9,'13:9','Amusnaw n wayen iffren akked wayen ur neffir. Ameqwôan, Aâlayan.');
INSERT INTO "ramdane" VALUES(13,10,'13:10','Iâuva segwen win i$emven awal, d win it iberêen, win iffren deg iv, d win d ivehôen deg wass.');
INSERT INTO "ramdane" VALUES(13,11,'13:11','Isâa iâssasen zdat es, deffir es, pêaraben fellas, s lameô n Öebbi. Öebbi, s tidep, ur Ipbeddil i illan deg ugdud, alamma bedlen i illan deg iman nnsen. Ma Irad Öebbi ayen n dir, i ugdud, ur t itekkes yiwen. Ur sâin amâiwen, war Neppa.');
INSERT INTO "ramdane" VALUES(13,12,'13:12','D Neppa i wen d Ippawin lebôaq, degs tugdi, asirem. Isskar ed asigna aéayan.');
INSERT INTO "ramdane" VALUES(13,13,'13:13','Tesbuccuâ aS termegt, s ccekôan, akken lmalayek, seg wakken ugadent eP. Ippazen ed ssiâqat, Ipêaz yisent win i S ihwan. Nitni pnamaren $ef Öebbi. Neppa teqseê tezmert iS.');
INSERT INTO "ramdane" VALUES(13,14,'13:14','£uô Neppa, anuju n tidep. Wid u$uô la pnujun, war Neppa, ur asen d pparan s wacemma. Am win issendin ifassen i waman, ib$a ad awven s imi s, ur ibbwiv ar leb$i s. Anuju ijehliyen ar ttelfan kan.');
INSERT INTO "ramdane" VALUES(13,15,'13:15','I Öebbi la kennun wid illan di tmurt akked igenwan, b$an ne$ ugin, akked tili nnsen, taûebêit, tameddit.');
INSERT INTO "ramdane" VALUES(13,16,'13:16','Ini: "anwa ay d Mass n tmurt akked igenwan"? Ini: "d Öebbi. Ihi day a pperrem d iêbiben, war Neppa, wid ur nemlik, i yiman nnsen, aba$uô ne$ vveôô"? Ini: "day iaâdel uder$al akked win ipwalin? Ne$, day âadlent tillas akked tafat? Ne$, rran imcurak i Öebbi, i ixelqen am lxelq iS, ufan t ippemcabi"? Ini: "d Öebbi i Ixelqen yal cci. D Neppa ay d Yiwen, d Aqehhaô".');
INSERT INTO "ramdane" VALUES(13,17,'13:17','Isse$lay ed aman, seg igenni. Serrun deg $ezôan, s leqdeô nnsen. Acercuô ippawi ikuftan ippifliwen. Ppemcabin ikuftan ar wayen fessin di tmes, iwakken ad ûenâen ôôêuê ne$ dduzan. Akka ay d Isskan Öebbi tidep akked tkerkas. Ihi ikuftan ad ppuveggôen, ma d wayen inefâen medden, a d igwri di tmurt. Akka ay d Ippak Öebbi imedyaten.');
INSERT INTO "ramdane" VALUES(13,18,'13:18','Lfayeq i wid ijawben i Mass nnsen. Wid ur aS njaweb ara, xas ad sâun i illan akw di tmurt, berdayen, ur ten id ifeddu. I widak, leêsab adiran. Axxam nnsen d Loahennama. A tanezdu$t tucmip!');
INSERT INTO "ramdane" VALUES(13,19,'13:19','Day win iéôan ar ayen i k d irsen, s$uô Mass ik, d tidep, am win idre$len? Siwa uâqilen id ismektayen.');
INSERT INTO "ramdane" VALUES(13,20,'13:20','Wid iîîfen di laâqed n Öebbi, ur pôuéun nnesba.');
INSERT INTO "ramdane" VALUES(13,21,'13:21','Wid issemlilen ayen id Iumeô Öebbi ad imlil, u ppagwaden Mass nnsen, u ppagwaden aqeîîi adiran.');
INSERT INTO "ramdane" VALUES(13,22,'13:22','Wid iûebôen, ssaramen udem n Mass nnsen, bedden i téallit, pseddiqen seg wayen is iten N$at, s tuffra u âinâni, swexxiôen tucmip s yise$. I nitni, agwav s Axxam.');
INSERT INTO "ramdane" VALUES(13,23,'13:23','Leonanat n Âaden ara kecmen, akked wid ixeddmen ayen ilhan, seg ibabaten nnsen, si tlawin nnsen, si tarwa nnsen. Lmalayek a d ppasent $uôsen si yal tabburt.');
INSERT INTO "ramdane" VALUES(13,24,'13:24','Tifrat fellawen, ilmend n wayen tûebôem! D asaâdi, wagwav s Axxam!');
INSERT INTO "ramdane" VALUES(13,25,'13:25','Wid ipôuéun laâqed n Öebbi, deffir lemwafaqa s, wid igezmen ayen d Iumeô Öebbi ad imlil, u ssefsaden di tmurt, wigi, i nitni xezzu, i nitni yir axxam.');
INSERT INTO "ramdane" VALUES(13,26,'13:26','Öebbi Issugut ôôeéq i win i S ihwan, u Iwezzen. Taâoeb iten tmeddurt n ddunit. Tameddurt n ddunit, d urar kan, zdat laxeôt.');
INSERT INTO "ramdane" VALUES(13,27,'13:27','La qqaôen wid ijehlen: "a lemmer id iris, $uôes, ussekni s$uô Mass is"! Ini: "Öebbi, s tidep, Itellif win i S ihwan. Inehhu $uôeS, wid isteôoiâen,');
INSERT INTO "ramdane" VALUES(13,28,'13:28','wid iumnen, iwumi itheddin wul, mara d mmektin Öebbi". Ni$, s usmekti n Öebbi, pheddinen wulawen.');
INSERT INTO "ramdane" VALUES(13,29,'13:29','Ssaâd, akked taggara igerzen, i wid iumnen, ixeddmen ayen ilhan.');
INSERT INTO "ramdane" VALUES(13,30,'13:30','Akka ay k id Nuzen, i lois id zwaren leoyus, ad asen temlev, ayen ik d Nweêêa. Nitni jehlen $uô Uênin. Ini: "Neppa d Mass iw, ur illi ôebbi siwa Neppa; ppekle$ fellaS, asteôoaâ iw $uôeS".');
INSERT INTO "ramdane" VALUES(13,31,'13:31','A lemmer kan Leqwôan isselêa idurar, iceqqeq tamurt, ne$ issenîeq ed lmeggtin! Xaîi! Lumuô akw i Öebbi. Day ur éôin ara wid iumnen, ar, ma ihwa yaS, Öebbi ad Inhu medden meôôa? Zgan wid ijehlen a ten tewwet ssixîa, ilmend n wayen xeddmen. Ne$ a ppevôu ar tama n yixxamen nnsen, alamma tusa d lemâahda n Öebbi. Öebbi, s tidep, ur Ipzeggil ppaâud.');
INSERT INTO "ramdane" VALUES(13,32,'13:32','Ni$ mmaâlken, uqbel ik, deg imazanen. Ooi$ lmudda i wid ijehlen, sinna, Ïîfe$ ten. A yaâaqeb iW imiren!');
INSERT INTO "ramdane" VALUES(13,33,'13:33','Day Win Ibedden, ar yal amdan, $ef wayen ikseb...? Curken aS i Öebbi. Ini: "semmit asen! Ne$, ad aS temlem ayen ur Iéôi di tmurt? Ne$ d ismawen n tkerkas"? Xaîi! Wid ijehlen, âaobent pen nncub nnsen. Ppuveôôfen seg ubrid. Win Itlef Öebbi, ur isâi w ara t inhun.');
INSERT INTO "ramdane" VALUES(13,34,'13:34','Fellasen aâaqeb, di tmeddurt n ddunit. Aâaqeb n laxert, s tidep, aâer. Ur sâin ara ten ifukken, seg ufus n Öebbi.');
INSERT INTO "ramdane" VALUES(13,35,'13:35','Amek tga Loennet i ippuweââden i wid iêezzben: ddaw as isaffen ppazzalen, lqut is akked tili s, d imezgiyen. D tagi ay d taggara n wid iêezzben. Taggara ijehliyen, d times.');
INSERT INTO "ramdane" VALUES(13,36,'13:36','Wid iwumi Nefka Tazmamt, ad feôêen s wayen i k d irsen. Illa wayen ugin kra ikabaren. Ini: "pwamôe$ kan ad âabde$ Öebbi, ur aS pcarake$. Pnuju$ $uôeS, tu$alin iw $uôeS".');
INSERT INTO "ramdane" VALUES(13,37,'13:37','Akka ay t id Nessers, d ûûwab, s taâôabt. Ma teddiv d lhawiyya nnsen, deffir tisin n tussna $uôek, ur k ipfukku bab ne$ asettaô, seg ufus n Öebbi.');
INSERT INTO "ramdane" VALUES(13,38,'13:38','Ni$ Nuzen ed imazanen, uqbel ik, Nefka yasen tilawin, tarwa. Ur ippunefk ara i umazan a d iawi assekni siwa ma Iâammed Öebbi. Yal lajel s Tezmamt is.');
INSERT INTO "ramdane" VALUES(13,39,'13:39','Iseffev u Itebbit Öebbi ay S ihwan. £uôeS tayimmap n Tezmamt.');
INSERT INTO "ramdane" VALUES(13,40,'13:40','Ama Nessken ak kra n wayen i sen Nâahed, ama Neddem ôôuê ik, ay fellak kan d isallen, ay fella£ d aqeîîi.');
INSERT INTO "ramdane" VALUES(13,41,'13:41','Day ur walan ara Neppas ed ar tmurt, Nessenqas di leryaf is? Öebbi Ipseyyi; yiwen ur ipnamar aseyyi iS. Neppa, Ip$awal di leêsab.');
INSERT INTO "ramdane" VALUES(13,42,'13:42','Ni$ ppandin wid i ten id izwaren. Tiêeôci, akw, ar Öebbi. Iéôa i ikseb yal iman. Ad éôen ijehliyen anwa ara iawven s Axxam.');
INSERT INTO "ramdane" VALUES(13,43,'13:43','Qqaôen wid ijehlen: "ur telliv d amazan". Ini: "ad Iqadd Öebbi d Anagi gari yidwen, akked kra isâan tussna n Tazmamt".');
INSERT INTO "ramdane" VALUES(14,1,'14:1','Alô. Tazmamt, Nessers ak p id, iwakken a ppessuff$ev imdanen si tillas ar tafat, s wannuf n Mass nnsen, s abrid n Wuzmir, Amaâzuz,');
INSERT INTO "ramdane" VALUES(14,2,'14:2','Öebbi ilan i illan deg igenwan, i illan di tmurt. A ssxev ijehliyen, ilmend uâaqeb uqsiê,');
INSERT INTO "ramdane" VALUES(14,3,'14:3','i ismenyafen tameddurt n ddunit wala laxeôt, i iswexxiôen seg ubrid n Öebbi, u ssaramen t d uâwij! Widak, atnan di ttelfan u$miq.');
INSERT INTO "ramdane" VALUES(14,4,'14:4','Ur d Nuzen amazan, siwa s tmeslayt n ugdud is, iwakken ad asen ibeggen. Öebbi Itellif win i S ihwan, Inehhu win i S ihwan. Neppa d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(14,5,'14:5','Ih, ni$ Nuzen ed Musa, s issekniyen nne£: "ssuffe$ agdud ik, si tillas ar tafat. Mmekti yas d ussan n Öebbi". Deg waya, war ccekk, d issekniyen i yal aûebôi isnemmiren.');
INSERT INTO "ramdane" VALUES(14,6,'14:6','Mi inna Musa i ugdud is: "mmektit ed l$it ikwen I$at Öebbi, mi kwen Isellek si lois n Ferâun, i issuken fellawen lhemm uqsiê. Zellun arrac nnwen, ssidiren tilawin nnwen. Deg waya, s$uô Mass nnwen, d cedda tameqwôaêant".');
INSERT INTO "ramdane" VALUES(14,7,'14:7','Mi d Inna Mass nnwen: "ma tesnemmrem, ih, ad awen Rnu$. Ma tjehlem, aâaqeb iW, war ccekk, ad iwâaô".');
INSERT INTO "ramdane" VALUES(14,8,'14:8','Inna Musa: "ma tjehlem, kunwi akked wid n tmurt meôôa, ihi, Öebbi d Asâay, d Amaâzuz".');
INSERT INTO "ramdane" VALUES(14,9,'14:9','Day ur kwen id ibbwiv ara usali imezwura nnwen: agdud n Nuê, Âad, Tamud, akked wid iten ivefôen? Ur ten issin yiwen, siwa Öebbi. Usan ten id imazanen nnsen, s ubeggen. Rran ifassen nnsen s imawen nnsen, nnan: "S tidep, nejhel ayen i$ef d teppuceggâam. Ih, nekwni di ccekk imcebbwel, $ef wayen u$uô la $ tneccdem".');
INSERT INTO "ramdane" VALUES(14,10,'14:10','Nnan imazanen nnsen: "day illa ccekk di Öebbi, Axellaq n tmurt akked igenwan? Inecced ikwen id, iwakken ad awen Isemmeê kra ibekkaven nnwen, a kwen Ioo ar lajel ipwassnen". Nnan: "kunwi, s tidep, d imdanen kan, am nekwni. Teb$am ad a$ tesîixxôem, seg wayen llan âabden imezwura nne$. Awit a$ d adabu n ûûeê".');
INSERT INTO "ramdane" VALUES(14,11,'14:11','Nnan asen imazanen nnsen: "nekwni d imdanen kan, am kunwi, maca Öebbi Iteg ccan i win i S ihwan, deg imdanen iS. Ur a$ ippunefk ara ad awen d nawi adabu, siwa s wannuf n Öebbi. £ef Öebbi ara ppeklen lmumnin.');
INSERT INTO "ramdane" VALUES(14,12,'14:12','Acu i $ iu$en, ur nepkal ara $ef Öebbi? Ni$ Imla ya$ iberdan nne$; neûbeô, war ccekk, i lmerta i tesâaddam fella$. £ef Öebbi ara ppeklen, at letkal".');
INSERT INTO "ramdane" VALUES(14,13,'14:13','Nnan wid ijehlen i imazanen nnsen: "war ccekk, a kwen nessuffe$ si tmurt nne$, ne$ a d tu$alem ar ddin nne$". Iweêêa yasen d Mass nnsen: "s tidep, a Nesqucc imednas,');
INSERT INTO "ramdane" VALUES(14,14,'14:14','d$a, war ccekk, a kwen Nerr d imezda$ n tmurt, deffir sen". Ayagi, i win ippagwaden tilin iW, ippagwaden aberri W.');
INSERT INTO "ramdane" VALUES(14,15,'14:15','Ssarmen l$elb. Ixseô yal aqehhaô amnafeq.');
INSERT INTO "ramdane" VALUES(14,16,'14:16','Loahennama deffir es, ad ites aman iwexmiyen.');
INSERT INTO "ramdane" VALUES(14,17,'14:17','Tissit d tije$wimin, ur izmir ad isseblaâ. Tameppant a d teppas si yal tamnaî, neppa ur ipmeppat. Deffir es aâaqeb uqsiê.');
INSERT INTO "ramdane" VALUES(14,18,'14:18','Amedya n wid ijehlen, ar Mass nnsen: igiten nnsen am i$i$den i$ef iédem wavu, ass n tbucivant. Ur d ppawin acemma, seg wayen kesben. D wagi ay d ttelfan u$miq.');
INSERT INTO "ramdane" VALUES(14,19,'14:19','Day ur twalav ara, Öebbi Ixleq tamurt akked igenwan, s tidep? Ma ihwa yaS, a kwen Ikkes, a d Iawi lxelq amaynut.');
INSERT INTO "ramdane" VALUES(14,20,'14:20','Ayagi, i Öebbi, ur illi d ayen izzenzafen.');
INSERT INTO "ramdane" VALUES(14,21,'14:21','A d rsen akw zdat Öebbi. Ad inin uôhifen i imzuxiyen: "s tidep, nella nedda yidwen, ma tzemrem ad a$ tsellkem kra, seg uâaqeb n Öebbi"? A d inin: "lemmer i $ Inha Öebbi, yili nwelleh ikwen. Iâuva fella$, neêmeq ne$ neûbeô, ur a$ illi ssôan".');
INSERT INTO "ramdane" VALUES(14,22,'14:22','Mi isuyya lameô, a d yini Cciîan: "Öebbi, d ûûeê, Iweââd awen lemâahda n tidep. Weââde$ awen, $uôe$ kwen. Ur sâi$ adabu fellawen, siwa anecced nnwen. Tu$em awal iw. Ur iyi plummut, lummet iman nnwen. Ur zmire$ a kwen âawne$, ur tezmirem ad iyi tâawnem. Nekôe$ wid iyi tepcarakem, zik. I imednas, s tidep, aâaqeb aqeôêan".');
INSERT INTO "ramdane" VALUES(14,23,'14:23','Ad skecmen wid iumnen, ixeddmen ayen ilhan, ar leonanat, ddawat sen isaffen ppazzalen, ad wekkôen dinna, s wannuf n Mass nnsen. Azul nnsen dinna d: "tifrat"!');
INSERT INTO "ramdane" VALUES(14,24,'14:24','Day ur twalav ara, amek id Ifka Öebbi amedya? Awal lâali am ttejôa lâali, iéuôan is ôûan, iferkawen is di tegnewt.');
INSERT INTO "ramdane" VALUES(14,25,'14:25','Teppak ed lfakia s, di yal imir, s wannuf n Mass is. Ippawi d Öebbi imedyaten i imdanen, amar a d mmektin.');
INSERT INTO "ramdane" VALUES(14,26,'14:26','Amedya n wawal ucmit, am ttejôa tucmip, teqlaâ sennig wakal, war leqôaô.');
INSERT INTO "ramdane" VALUES(14,27,'14:27','Öebbi Itebbit wid iumnen s wawal utebbet, di tmeddurt n ddunit akked laxeôt. Öebbi Itellif imednas. Öebbi Ixeddem ayen i S ihwan.');
INSERT INTO "ramdane" VALUES(14,28,'14:28','Day ur twalav ara wid ineûfen l$it n Öebbi, s tijehli? Ssersen agdud nnsen deg uxxam n twa$it.');
INSERT INTO "ramdane" VALUES(14,29,'14:29','Loahennama ad ô$en degs: a yir leqôaô!');
INSERT INTO "ramdane" VALUES(14,30,'14:30','Gan aS i Öebbi tanudda, iwakken ad telfen seg ubrid iS. Ini: "zzhut! S tidep, leqôaô nnwen d times".');
INSERT INTO "ramdane" VALUES(14,31,'14:31','Ini i imdanen iW iumnen, ad bedden i téallit, ad seddqen seg wayen iten N$at, s tuffra u âinani, uqbel a d ias wass ideg ur illi ssuq, ur telli tdukli.');
INSERT INTO "ramdane" VALUES(14,32,'14:32','Öebbi i Ixelqen tamurt akked igenwan, id Isse$layen aman seg igenni, Issufu$ ed yissen ppemôa, d ôôeéq i kunwi. Inub fellawen s isaffen, u s lbabuô i iteddun di yill, s lameô iS.');
INSERT INTO "ramdane" VALUES(14,33,'14:33','Inub fellawen s yiîij d waggur, d imezgiyen. Inub fellawen s yiv akked wass.');
INSERT INTO "ramdane" VALUES(14,34,'14:34','Ifka yawen seg wayen akw i S tessutrem. Ma pêesbem l$it n Mass nnwen, ur tessawavem. Ih, amdan, s tidep, d amednus, d ajehli.');
INSERT INTO "ramdane" VALUES(14,35,'14:35','Mi inna Ibôahim: "a Mass iw! Err tamdint agi d tin n laman, Âatq a$, nek d warraw iw, seg wannuz n tâancuôin.');
INSERT INTO "ramdane" VALUES(14,36,'14:36','A Mass iw, nitenti, s tidep, telfent aîas n medden. Win iddan yidi, neppa, s tidep, sgi. Win iyi âuûan, Keçç, s tidep, Tepsemmiêev, Tepêunuv.');
INSERT INTO "ramdane" VALUES(14,37,'14:37','A Mass nne$! Nek rri$ tanezdu$t, i kra n warraw iw, di te$zut ibuôen, ar Uxxam iK iôqan. A Mass nne$! Iwakken ad bedden i téallit. Err ulawen n kra n medden, ad iêninen $uôsen, £it iten s ppemôat, amar ad snemmren.');
INSERT INTO "ramdane" VALUES(14,38,'14:38','A Mass nne$! Keçç, s tidep, Teéôiv ayen neffer akked wayen nenna. Ur iffir wacemma i Öebbi, di tmurt ne$ deg igenni.');
INSERT INTO "ramdane" VALUES(14,39,'14:39','Ccekôan i Öebbi iyi d Ifkan, di tewser iw, Ismaâil akked Isêaq. Mass iw, s tidep, Issemêas i tnaâôit.');
INSERT INTO "ramdane" VALUES(14,40,'14:40','A Mass iw, Err iyi ad bedde$ i téallit. Akked kra, si tarwa w. A Mass iw, Steôêeb s unuju w.');
INSERT INTO "ramdane" VALUES(14,41,'14:41','A Mass nne$! Semmeê ane$, nek d lwaldin iw, akked lmumnin, ass ideg ara d ias uqeîîi".');
INSERT INTO "ramdane" VALUES(14,42,'14:42','Ur p$ili ar I$fel Öebbi, $ef wayen xeddmen imednas. Iooa ten kan, ar ass ideg ara beêêren allen.');
INSERT INTO "ramdane" VALUES(14,43,'14:43','Ad p$awalen, iqweôôa ff$en d, allen ur ferzent, ulawen d ilmawen.');
INSERT INTO "ramdane" VALUES(14,44,'14:44','Nabi imdanen af wass ideg ara ten id yas uâaqeb. Ad inin wid idensen: "a Mass nne$! Efk ane$ lmudda wezzilen, a d njaweb i unabi K, a neddu d imazanen". "Day ur teggulem ara kunwi, zik, ar ur tep$abem?');
INSERT INTO "ramdane" VALUES(14,45,'14:45','Tzed$em ixxamen n wid ivuôôen iman nnsen. U Nbeggen awen d amek i sen Nga, u Nefka yawen d imedyaten".');
INSERT INTO "ramdane" VALUES(14,46,'14:46','Ni$ undin ccbukat nnsen. Ccbukat nnsen haatent ar Öebbi, xas ma ccbukat nnsen d tid ara isse$lin idurar.');
INSERT INTO "ramdane" VALUES(14,47,'14:47','£uôek a p$ilev Öebbi ad Izgel lemâahda S, i imazanen. S tidep, Öebbi d Uzmir, Ippara d ppaô.');
INSERT INTO "ramdane" VALUES(14,48,'14:48','Ass ideg tamurt a ppu$al maççi d tamurt; akken igenwan; a d asen ar Öebbi Yiwen, Aqehhaô.');
INSERT INTO "ramdane" VALUES(14,49,'14:49','A pwaliv, assen, ibe$wviyen pwaqqnen wa $er wa.');
INSERT INTO "ramdane" VALUES(14,50,'14:50','Lebsa nnsen d qevôan, times t$umm udmawen nnsen.');
INSERT INTO "ramdane" VALUES(14,51,'14:51','Iwakken ad Ikafi Öebbi yal amdan, $ef wayen ikseb. Öebbi, s tidep, Ip$awal di leêsab.');
INSERT INTO "ramdane" VALUES(14,52,'14:52','Wagi d anecced i imdanen, ad pwanedôen yis, ad éôen ar siwa Neppa ay d Öebbi Yiwen, a d mmektin uâqilen.');
INSERT INTO "ramdane" VALUES(15,1,'15:1','Alô. Wigi d issekniyen n Tezmamt, u d Leqwôan ipbegginen.');
INSERT INTO "ramdane" VALUES(15,2,'15:2','Ahat wid ijehlen ssaramen ad ilin d imvuâen.');
INSERT INTO "ramdane" VALUES(15,3,'15:3','Eoo iten ad ççen, ad zhun, a ten issedhu usirem. Deqqal ad éôen.');
INSERT INTO "ramdane" VALUES(15,4,'15:4','Ur Nesquccu tamdint, siwa ma d ayen i s iuran, ipwassnen.');
INSERT INTO "ramdane" VALUES(15,5,'15:5','Ur illi lois ara d isqeôben, ne$ ara iswexxôen lajel is.');
INSERT INTO "ramdane" VALUES(15,6,'15:6','Nnan: "a win u$uô d irs usmekti! Keçç, war ccekk, d aôehbani.');
INSERT INTO "ramdane" VALUES(15,7,'15:7','Acimi ur a$ d tusiv ara, s lmalayek, ma d tidep ay tessawalev"?');
INSERT INTO "ramdane" VALUES(15,8,'15:8','Ur Nesrusu lmalayek, siwa s tidep. Imiren, ur pganin.');
INSERT INTO "ramdane" VALUES(15,9,'15:9','Ih, Nekwni Nessers ed asmekti. Ih, Nekwni a Nêareb fellas.');
INSERT INTO "ramdane" VALUES(15,10,'15:10','Ni$ Nuzen ed, uqbel ik, i ikabaren imezwura.');
INSERT INTO "ramdane" VALUES(15,11,'15:11','Ur ten id ippas umazan, mebla ma ttahan fellas.');
INSERT INTO "ramdane" VALUES(15,12,'15:12','Akka ay t Nessekcam deg ulawen ibe$wviyen.');
INSERT INTO "ramdane" VALUES(15,13,'15:13','Ur uminen yis, ulamma ooan d imezwura tannumi.');
INSERT INTO "ramdane" VALUES(15,14,'15:14','Xas ad asen Nelli tabburt igenni, ad ppalin $uôes,');
INSERT INTO "ramdane" VALUES(15,15,'15:15','a d inin, war ccekk: "ittules iéôi nne$, ne$ nekwni d agdud iwumi sseêren".');
INSERT INTO "ramdane" VALUES(15,16,'15:16','Ni$ Nerra itran deg igenni. Ncebbeê iten i wid ipmuqulen.');
INSERT INTO "ramdane" VALUES(15,17,'15:17','Nesdari ten, si yal cciîan imenfi.');
INSERT INTO "ramdane" VALUES(15,18,'15:18','Kkes ed win d issemêessen s tufra, A p iddu, fellas, ujajiê n ûûeê.');
INSERT INTO "ramdane" VALUES(15,19,'15:19','Tamurt Nessewsaâ p; Nerra fellas idurar; Nessem$i d, degs, yal cci, s lmizan.');
INSERT INTO "ramdane" VALUES(15,20,'15:20','Nerra, degs, lqut i kunwi, akw i wid ur tecceççayem ara.');
INSERT INTO "ramdane" VALUES(15,21,'15:21','Lexzayen n yal cci, $uône£ kan. Siwa s lkil ipwassnen i ten id Nesrusu.');
INSERT INTO "ramdane" VALUES(15,22,'15:22','Nuzen ed avu isswayen. Nesse$li d aman seg igenni, yissen Nessew ikwen. Ur llin ara lexzayen is $uôwen.');
INSERT INTO "ramdane" VALUES(15,23,'15:23','Ih, d Nekwni i Issidiren, i Ineqqen. D Nekwni ay d Iweôôaten.');
INSERT INTO "ramdane" VALUES(15,24,'15:24','Ni$ Nessen imezwura nnwen, Nessen ineggura.');
INSERT INTO "ramdane" VALUES(15,25,'15:25','Ih, Mass ik, Neppa, a ten id Ifser. Ih, Neppa, d Bab n ûûwab, d Amusnaw.');
INSERT INTO "ramdane" VALUES(15,26,'15:26','Ni$ Nexleq amdan si talaxt taberkant i meslen.');
INSERT INTO "ramdane" VALUES(15,27,'15:27','Nexleq loenn, iu$ lêal, si tmes n ccili.');
INSERT INTO "ramdane" VALUES(15,28,'15:28','Mi Inna Mass ik, i lmalayek: "Nek Xelqe$ amdan, si talaxt taberkant i meslen.');
INSERT INTO "ramdane" VALUES(15,29,'15:29','Mi t Mesle$, u Üûuve$ as si ôôuê iW, ersemt $uôes, knumt".');
INSERT INTO "ramdane" VALUES(15,30,'15:30','Knant akw lmalayek, akken ma llant,');
INSERT INTO "ramdane" VALUES(15,31,'15:31','siwa Iblis, iugi ad yili ar wid iknan.');
INSERT INTO "ramdane" VALUES(15,32,'15:32','Inna: "a yIblis, acu ik iu$en, ur telliv ar wid iknan"?');
INSERT INTO "ramdane" VALUES(15,33,'15:33','Inna: "ur kennu$ i umdan, i Txelqev si talaxt taberkant i meslen".');
INSERT INTO "ramdane" VALUES(15,34,'15:34','Inna: "effe$ segs! Aql ak, ihi, d imenfi.');
INSERT INTO "ramdane" VALUES(15,35,'15:35','Ih, fellak ddell alamma d ass uqeîîi".');
INSERT INTO "ramdane" VALUES(15,36,'15:36','Inna: "a Mass iw! Eoo iyi alamma d ass n tnekra nnsen".');
INSERT INTO "ramdane" VALUES(15,37,'15:37','Inna: "ih, keçç seg wid ipganin,');
INSERT INTO "ramdane" VALUES(15,38,'15:38','ar ass n yiùmir ipwassnen".');
INSERT INTO "ramdane" VALUES(15,39,'15:39','Inna: "a Mass iw! Imi yi Terriv d a$ewway, war ccekk, ad asen cebbêe$ ddunit, a ten $ewwu$, akken ma llan.');
