INSERT INTO "ramdane" VALUES(15,77,'15:77','Deg waya, s tidep, d assekni i lmumnin.');
INSERT INTO "ramdane" VALUES(15,78,'15:78','Ih, llan At Teégi, war ccekk, d imednas.');
INSERT INTO "ramdane" VALUES(15,79,'15:79','Nerra d ppaô segsen. Ihi, atent an, snat nni, deg ubrid ibanen.');
INSERT INTO "ramdane" VALUES(15,80,'15:80','Ni$ At Lêioô skaddben imazanen.');
INSERT INTO "ramdane" VALUES(15,81,'15:81','Nebbwi yasen d issekniyen nne£. Wexxôen asen.');
INSERT INTO "ramdane" VALUES(15,82,'15:82','Llan qqazen ixxamen n laman, deg idurar.');
INSERT INTO "ramdane" VALUES(15,83,'15:83','Iwwet iten usu$u, taûebêit.');
INSERT INTO "ramdane" VALUES(15,84,'15:84','Ulay sen ixdem wayen la keppben.');
INSERT INTO "ramdane" VALUES(15,85,'15:85','Ur Nexliq tamurt, igenwan, akked wayen illan garasen, siwa s tidep. Imir a d ias, war ccekk. Ihi, ssuref s lekyasa.');
INSERT INTO "ramdane" VALUES(15,86,'15:86','Ih, Mass ik d Axellaq, d Amusnaw.');
