INSERT INTO "ramdane" VALUES(15,88,'15:88','Ur pmuqul ar wayen is N$at kra n twaculin nnsen. Ur $webbel ilmend nnsen. Zger tacvaî ik $ef lmumnin.');
INSERT INTO "ramdane" VALUES(15,89,'15:89','Ini: "nekkini, s tidep, d aneddaô ipbegginen.');
INSERT INTO "ramdane" VALUES(15,90,'15:90','Akken id Nessers i wid ibvan,');
INSERT INTO "ramdane" VALUES(15,91,'15:91','i irran Leqwôan d iêricen.');
