INSERT INTO "ramdane" VALUES(15,95,'15:95','Ih, a k Nqennaâ mgal wid ittihin,');
INSERT INTO "ramdane" VALUES(15,96,'15:96','i irran ôebbi nniven, ar tama n Öebbi. Deqqal ad éôen.');
INSERT INTO "ramdane" VALUES(15,97,'15:97','Ni$ Neéôa ar ul ik, keçç, iaâûeô seg wayen qqaôen.');
INSERT INTO "ramdane" VALUES(15,98,'15:98','Sbuccaâ Mass ik s ccekôan. Ili seg wid ikennun.');
