INSERT INTO "ramdane" VALUES(16,1,'16:1','Ad ias lameô n Öebbi, ur pêirit $uôes. Gedha s yiS! Ay Aâlay, sennig wayen pcaraken.');
INSERT INTO "ramdane" VALUES(16,2,'16:2','Issrusu d lmalayek s ôôuê si lameô iS, $ef win i S ihwan, deg imdanen iS: "Nedôet ar ur illi ôebbi siwa Nek. Ihi, êezzbet iyI".');
INSERT INTO "ramdane" VALUES(16,3,'16:3','Ixleq tamurt akked igenwan, s tidep. Ay Aâlay sennig wayen pcaraken!');
INSERT INTO "ramdane" VALUES(16,4,'16:4','Ixelq ed amdan si tiqqit; atan iu$al aS, d axûim n ûûeê.');
INSERT INTO "ramdane" VALUES(16,5,'16:5','Ixelq awen d lmal, segsen iceîîiven iêman, aba$uô, u, segsen, teppeppem.');
INSERT INTO "ramdane" VALUES(16,6,'16:6','D ccbaêa nnwen mara ten id terrem, u mara ten teksem.');
INSERT INTO "ramdane" VALUES(16,7,'16:7','Ppawin tiâkkwmin nnwen ar tmurt ur teppawvem, siwa ma tâapbem. Ih, Mass nnwen, war ccekk, d Umâin, d Aênin.');
INSERT INTO "ramdane" VALUES(16,8,'16:8','Iâewdiwen, iserdyan, i$wyal, i rrekba nnwen akked ccbaêa. Ixelleq ayen ur tessinem.');
INSERT INTO "ramdane" VALUES(16,9,'16:9','D Öebbi i Ippawin d ubrid. Ff$en segs. Ma ihwa yaS, a kwen Inhu meôôa.');
INSERT INTO "ramdane" VALUES(16,10,'16:10','D Neppa i wen d Isse$layen aman seg igenni. Segsen tissit nnwen, segsen ttjuô i itepp lmal.');
INSERT INTO "ramdane" VALUES(16,11,'16:11','Isskar awen d yissen nnaâma, azemmur, tizdayin, tiéuôin, akked yal ppemôa. Deg waya, s tidep, assekni i ugdud ipxemmimen.');
INSERT INTO "ramdane" VALUES(16,12,'16:12','Inub awen d s yiv akked wass, iîij akked waggur. Itran ppemnuben s lameô iS. Deg waya, s tidep, issekniyen i ugdud ifehmen.');
INSERT INTO "ramdane" VALUES(16,13,'16:13','I wen d Ixleq di tmurt, degs initen imgarraden. Deg waya, s tidep, assekni i ugdud ismektayen.');
INSERT INTO "ramdane" VALUES(16,14,'16:14','D Neppa i Inuben s yill, iwakken a pteppem, segs, aksum aleqqaq, a d teksem, segs, rrêuê i teppeqqnem. A pwaliv lbabuô ipcerrig it, iwakken a pnadim si lfevl iS, amar a ppesnemmrem.');
INSERT INTO "ramdane" VALUES(16,15,'16:15','Issers idurar $ef tmurt, ur teppembwiwil yiswen; isaffen, iberdan, amar a ppepwanham;');
INSERT INTO "ramdane" VALUES(16,16,'16:16','timuliwin: s yitran, nitni pwanhan.');
INSERT INTO "ramdane" VALUES(16,17,'16:17','Day Win Ixelqen am win ur nxelleq? Day ur d tesmektayem ara?');
INSERT INTO "ramdane" VALUES(16,18,'16:18','Lemmer a pêesbem l$it n Öebbi, ur tezmirem. Ih, Öebbi, war ccekk, Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(16,19,'16:19','Öebbi Iéôa ayen teffrem akked wayen teqqaôem.');
INSERT INTO "ramdane" VALUES(16,20,'16:20','Wid u$uô pnujun, war Öebbi, ur illi wayen xelqen. D nitni i ipwaxelqen.');
INSERT INTO "ramdane" VALUES(16,21,'16:21','D lmeggtin ur d imuddiren, ur éôin melmi ara ten id ssekren.');
INSERT INTO "ramdane" VALUES(16,22,'16:22','Öebbi nnwen d Öebbi Yiwen. Wid ur numin ara s laxeôt, d ulawen nnsen i iugin. U nitni pzzuxun.');
INSERT INTO "ramdane" VALUES(16,23,'16:23','War ccekk, Öebbi Iéôa ayen teffren, akked wayen qqaôen. Neppa, s tidep, ur Iêemmel ara imzuxiyen.');
INSERT INTO "ramdane" VALUES(16,24,'16:24','Ma nnan asen: "d acu id Issers Mass nnwen"? Qqaôen: "d timucuha imezwura".');
INSERT INTO "ramdane" VALUES(16,25,'16:25','Ad bibben, ass n tnekra, tiâakkwmin nnsen, s pmam nnsent, akked wumur si tâakkwmin n wid telfen, mebla ma faqen. Day maççi dir it wayen bubben?');
INSERT INTO "ramdane" VALUES(16,26,'16:26','Ih, undin p, wid i ten id izwaren. Öebbi Iaâtad lebni nnsen, si llsas. I$li d ssqef fellasen. Iusa ten id uâaqeb seg wanda ur bnin.');
INSERT INTO "ramdane" VALUES(16,27,'16:27','Sinna, ass n tnekra, a ten Idull, ad Yini: "anda ten icriken iW, i$ef temnamarem"? Nnan imawlan n tussna: "war ccekk, ddell akked ddens, assa, $ef ijehliyen');
INSERT INTO "ramdane" VALUES(16,28,'16:28','iwumi ddment lmalayek leôwaê, nitni densen iman nnsen". A d ssutren tifrat: "ur nexdim kra n tucmip". Xaîi, Öebbi, s tidep, Iéôa ayen txeddmem.');
INSERT INTO "ramdane" VALUES(16,29,'16:29','Kecmet si tebbura, ar Loahennama; degs a pwekkôem. A yicmet uxxam imzuxiyen!');
INSERT INTO "ramdane" VALUES(16,30,'16:30','Ad inin i wid ipêezziben: "d acu id Issers Mass nnwen"? A d inin: "d lxiô". I wid imaânen, ise$ di ddunit agi. Pifxiô, war ccekk, axxam aneggaru. Ay d umâin uxxam n wid ipêezziben!');
INSERT INTO "ramdane" VALUES(16,31,'16:31','Ad kecmen leonanat n Âaden, ddawat sen isaffen ppazzalen; degsen, i nitni, ayen i sen ihwan. Akka i Ipkafi Öebbi wid ipêezziben,');
INSERT INTO "ramdane" VALUES(16,32,'16:32','wid iwumi ddment lmalayek leôwaê, nitni maânen, ad asen inint: "tifrat fellawen! Kecmet Loennet, ilmend n wayen txeddmem".');
INSERT INTO "ramdane" VALUES(16,33,'16:33','Day a pôaoun kan tisin n lmalayek $uôsen, ne$ a d iawev lameô n Mass ik? Akka ay xedmen imezwura nnsen. Ur ten Idnis Öebbi, maca densen iman nnsen.');
INSERT INTO "ramdane" VALUES(16,34,'16:34','D tucmitin xeddmen, i ten iwwten. Izzi yasen wayen i$ef pmmaâliken.');
INSERT INTO "ramdane" VALUES(16,35,'16:35','Nnan wid ipcaraken: "Lemmer i S ihwi i Öebbi, ur naâbed acemma, war Neppa, nekwni akked imezwura nne$. Ur negdel acemma, war ayen Igdel". Akka ay xedmen imezwura nnsen. Day illa wayen iwatan i Umazan, siwa awal ipbegginen?');
INSERT INTO "ramdane" VALUES(16,36,'16:36','Ni$ Nuzen ed amazan i yal lois: "âabdet Öebbi, janbet iôebbiten n tkerkas". Garasen, llan wid Inha Öebbi; garasen, wid i$ef iura ttelfan. Iniget di tmurt, walit amek tevôa d inekkaôen.');
INSERT INTO "ramdane" VALUES(16,37,'16:37','Xas tôe$wbev $ef nnhaya nnsen, Öebbi, s tidep, ur Inehhu wid Itlef. Ur asen illi usettaô.');
INSERT INTO "ramdane" VALUES(16,38,'16:38','Ggulen s Öebbi, liman iûeêêan nnsen, ar ur d Ippara ara Öebbi wid immuten. Xaîi, lemâahda fellas d tidep, maca tegwti n medden ur ssinen.');
INSERT INTO "ramdane" VALUES(16,39,'16:39','Iwakken ad asen d ibeggen ayen i$ef mgarraden, iwakken ad éôen wid ijehlen, ar nitni, war ccekk, llan d ikeddaben.');
INSERT INTO "ramdane" VALUES(16,40,'16:40','Awal nne£ kan, i t$awsa, ma Neb$a p, ad as Nini: "ili"! A ppili.');
INSERT INTO "ramdane" VALUES(16,41,'16:41','Wid iunagen i Öebbi, mbaâd ma pwadensen, a ten Nesserked di ddunit s yise$. Arraz n laxeôt a t i$leb, war ccekk, lemmer éôin');
