INSERT INTO "ramdane" VALUES(16,47,'16:47','Ne$ ur ten ikkat ara cwiî, cwiî. Ih, Mass nnwen, war ccekk, Issuruf, Ipêunu.');
INSERT INTO "ramdane" VALUES(16,48,'16:48','Day ur walan ara ayen akw Ixleq Öebbi, ippara tili s yeffus, zelmev; tkennu i Öebbi, s wannuz.');
INSERT INTO "ramdane" VALUES(16,49,'16:49','I Öebbi ikennu meôôa wayen illan deg igenwan, akked wayen illan di tmurt, imexlaq akked lmalayek. Nitni ur sme$wôen iman nnsen.');
INSERT INTO "ramdane" VALUES(16,50,'16:50','Ppagwaden Mass nnsen, sennig sen, xeddmen ayen i sen ippumôen.');
INSERT INTO "ramdane" VALUES(16,51,'16:51','Inna Öebbi: "ur sâut sin iôebbiten. Neppa, d Öebbi Yiwen kan. Ihi, Nek, agwadet iyI".');
INSERT INTO "ramdane" VALUES(16,52,'16:52','IneS i illan di tmurt akked igenwan. Ddin, ineS i lebda. Day a têezzbem i war Öebbi?');
