INSERT INTO "ramdane" VALUES(22,6,'22:6','Ayagi, acku Öebbi d Tidep. D Neppa id Issidiren lmeggtin. Neppa Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(22,7,'22:7','U imir a d ias, war ccekk fellas. U Öebbi a d Issker wid illan di tneîlin.');
INSERT INTO "ramdane" VALUES(22,8,'22:8','Ger imdanen, illa win ipnamaren $ef Öebbi, war tamusni, ne$ nnhaya, ne$ Tazmamt ifeten.');
INSERT INTO "ramdane" VALUES(22,9,'22:9','Icuff aqenduô, iwakken ad itlef seg ubrid n Öebbi. Fellas xezzu di ddunit. Deg wass n tnekra, a Nessuk fellas aâaqeb n tmeô$iwt.');
INSERT INTO "ramdane" VALUES(22,10,'22:10','Ayagi, $ef wayen id urwen ifassen ik. U Öebbi ur Idennis imdanen.');
INSERT INTO "ramdane" VALUES(22,11,'22:11','Ger yimdanen, win iâabden Öebbi, $ef rrif. Ma iusa t id lxiô, ad ithenni yis. Ma iusa t id uhewwel, ad iswexxeô udem is. Öuêent as, ddunit, laxeôt. Ayagi d lexsaôa n ûûeê.');
INSERT INTO "ramdane" VALUES(22,12,'22:12','Ipnuju, war Öebbi, ar wayen ur t neîîuôôu, ur t neffaâ. D wagi ay d ttelfan u$miq.');
INSERT INTO "ramdane" VALUES(22,13,'22:13','Ipnuju ar bu vveôô izwaren aba$uô ines. Ay d bab ucmit, ay d acrik ucmit.');
INSERT INTO "ramdane" VALUES(22,14,'22:14','Öebbi, s tidep, ad Issekcem wid iumnen, i ixeddmen ayen ilhan, ar Loennet, ddaw as isaffen ppazzalen. Öebbi, s tidep, Ixeddem ayen Ib$a.');
INSERT INTO "ramdane" VALUES(22,15,'22:15','Win i oaâlen ur t Ipsellik ara Öebbi, di ddunit, di laxeôt, ad iâalleq amrar s igenni, sinna, ad igzem nnefs is. Ad iéeô ma tiêêeôci s temêa ayen i t irêan.');
INSERT INTO "ramdane" VALUES(22,16,'22:16','Akka ay t id Nessader, d issekniyen iberrzen. U Öebbi, s tidep, Inehhu win i S ihwan.');
INSERT INTO "ramdane" VALUES(22,17,'22:17','Ih, ass n tnekra, ad Iêudd Öebbi ger wid iumnen, udayen, iûabiyen, imasiêiyen, u iâabbaden n tmes, wid ipcaraken. Öebbi, s tidep, Iwala yal cci.');
INSERT INTO "ramdane" VALUES(22,18,'22:18','Ur twalav ara knan i Öebbi i illan deg igenwan, i illan di tmurt: iîij, aggur, itran, idurar, ttjuô, lêayawan, aîas n yimdanen. Aâaqeb d lewoeb $ef aîas. Win Isdull Öebbi, ur illi w ara t iâuzzen. Ih, Öebbi Ixeddem ayen i S ihwan.');
INSERT INTO "ramdane" VALUES(22,19,'22:19','Atnan sin ixûimen ppemqellaân $ef Mass nnsen. S tmes ay pwafeûlen iceîîiven n wid ijehlen. Ad mmiren, $ef iqweôôa nnsen, waman iô$an');
INSERT INTO "ramdane" VALUES(22,20,'22:20','ara issefsin ayen illan deg iâabbav nnsen, akked igwelman.');
INSERT INTO "ramdane" VALUES(22,21,'22:21','I nitni, ijekwaven n wuzzal.');
INSERT INTO "ramdane" VALUES(22,22,'22:22','Yal tikelt ara ssirmen a d ff$en segs, seg wakken meênen, a ten rren $uôes: "jeôôbet aâaqeb n tmeô$iwt"!');
INSERT INTO "ramdane" VALUES(22,23,'22:23','Öebbi, s tidep, ad Issekcem wid iumnen, ixeddmen ayen ilhan, ar Loennet, ddaw as isaffen ppazzalen. Degs ad qqnen imeqyasen n ddheb d lyaqut. Leêrir deg iceîîiven nnsen.');
INSERT INTO "ramdane" VALUES(22,24,'22:24','Pwanhan s awal umâin, pwanhan s abrid Umaâzuz.');
INSERT INTO "ramdane" VALUES(22,25,'22:25','Ih, i wid ijehlen i issefôa$en abrid n Öebbi, akked Loamaâ n Lêaôam, i Nerra i imdanen, ama d amezda$ n din, ama d abeôôani; i win ib$an degs tijehli, s ddens, a Nesâaddi aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(22,26,'22:26','Mi Nhegga i Ibôahim, amkan n Uxxam: "ur iyI pcarak acemma! Zizdeg axxam iW i wid idewwiôen, wid ibedden, wid iîîinizen, iknan.');
INSERT INTO "ramdane" VALUES(22,27,'22:27','Enced medden ar lêio. A d asen $uôek $ef uvaô, ne$ $ef yal amerkub ulîif. A d asen si yal abrid ibaâden.');
INSERT INTO "ramdane" VALUES(22,28,'22:28','Ad cehhden $ef l$it nnsen. A d smektin ism n Öebbi, deg ussan ipwassnen, $ef ubhim n lmal i sen Ifka. Eççet segs, cceççet ame$bun, aéawali.');
INSERT INTO "ramdane" VALUES(22,29,'22:29','Sinna, ad ssirden, ad qenâen asirem nnsen, ad dewwôen i Uxxam aqdim".');
INSERT INTO "ramdane" VALUES(22,30,'22:30','Akka. Win ippâuzzun ayen Igdel Öebbi, ilha yas kteô, ar Mass is. Ëellen awen lmal, siwa wid i wen mlan. Janbet lâaô n tâancuôin, janbet awal n ééuô;');
INSERT INTO "ramdane" VALUES(22,31,'22:31','am wid imalen ar tidep ar Öebbi, ur aS pcaraken. Win ipcaraken i Öebbi, amzun i$li d seg igenni, u iccelqef it ufôux, ne$ inheô it wavu, s amekkan lqayen.');
INSERT INTO "ramdane" VALUES(22,32,'22:32','Akka. Win ippâuzzun isekkiren n Öebbi, ihi, d aêezzeb n wulawen.');
INSERT INTO "ramdane" VALUES(22,33,'22:33','I kunwi degs aba$uô ar lajel ipwassnen, sinna, îêegga nnsen ar Uxxam aqdim.');
INSERT INTO "ramdane" VALUES(22,34,'22:34','I yal lois, Nefka asekkir, iwakken a d smektin ism n Öebbi, $ef ubhim n lmal is iten N$at. Öebbi nnwen d Öebbi Yiwen. Ihi, anzet aS. Becceô itubiyen,');
INSERT INTO "ramdane" VALUES(22,35,'22:35','wid iwumi ppergigin wulawen nnsen mara d mmektin Öebbi, wid iûebbôen i wayen iten iu$en, wid ipéallan u pseddiqen seg wayen is iten N$at.');
INSERT INTO "ramdane" VALUES(22,36,'22:36','Lmal iqebbwan, Nerra yawen ten, seg isekkiren n Öebbi. Degsen lfayda i kunwi. Bedret fellasen isem n Öebbi, mara d rsen. Mi $lin $ef ibeôdiyen nnsen, eççet segsen, cceççet aâîîaô, aéawali. Akka ay d Nenub fellawen yissen, amar a ppesnemmrem.');
INSERT INTO "ramdane" VALUES(22,37,'22:37','Ur ippawev ar Öebbi uksum nnsen, ne$ idamen nnsen, d awez$i. Ara T iawven, d aêezzeb nnwen. Am akka, Inub fellawen yissen, iwakken ad aS tgem ccan i Öebbi, ilmend n nnhaya nnwen s$uôeS. Becceô wid imaânen.');
INSERT INTO "ramdane" VALUES(22,38,'22:38','Ih, Öebbi Ipfukku wid iumnen. Ih, Öebbi ur Iêemmel ara yal lxayen ajehli.');
INSERT INTO "ramdane" VALUES(22,39,'22:39','Tsriê i wid ipwawwven imi pwadensen - Öebbi, s tidep, Izmer a ten Isellek -,');
INSERT INTO "ramdane" VALUES(22,40,'22:40','wid id ssuff$en seg ixxamen nnsen, war ssebba, imi kan qqaôen: "Mass nne$ d Öebbi". Lemmer ur Iswexxiô ara Öebbi imdanen, kra s kra, ara ipwahudden, war ccekk, d tiqwebtin, timezgida, lemûellat, lowamaâ ideg d smektayen, aîas, ism n Öebbi. Ih, Öebbi Ippaâwan win iT ippaâwanen. Öebbi, s tidep, d Uohid, d Uzmir.');
INSERT INTO "ramdane" VALUES(22,41,'22:41','Wid, ma Nessqâad iten di tmurt, bedden i téallit, pzekkin, ppamôen lewqama, gedlen tuccva. Ar Öebbi ara d gwrin lumuô.');
INSERT INTO "ramdane" VALUES(22,42,'22:42','Ma skaddben k, ni$ skaddben, uqbel nnsen, igduden n Nuê, Âad, akked Tamud,');
INSERT INTO "ramdane" VALUES(22,43,'22:43','agdud n Ibôahim, akked ugdud n Luî,');
INSERT INTO "ramdane" VALUES(22,44,'22:44','At Madyan. Skaddben Musa. Fki$ lmudda i ijehliyen, sinna, Zzi$ d fellasen. A tuzzma W imiren!');
INSERT INTO "ramdane" VALUES(22,45,'22:45','Acêal n temdinin ay Nesqucc imi densent. Haatent ppint. Xlan lebyar, akked lebruo uohiden.');
INSERT INTO "ramdane" VALUES(22,46,'22:46','Day ur unagen ara di tmurt? Ad sâun, $ef waya, ulawen ad fehmen yissen, imeééu$en ara islen yissen. Maca, maççi d allen, d ulawen deg idmaren i idre$len.');
INSERT INTO "ramdane" VALUES(22,47,'22:47','B$an k a d tesqeôbev aâaqeb. Öebbi ur Ipzeggil lemâahda S, d awez$i. Ni$ ass, ar Mass ik, am agim iseggwasen, s leêsab nnwen.');
INSERT INTO "ramdane" VALUES(22,48,'22:48','Acêal n temdinin iwumi Fki$ lmudda, u irna densent. Sinna, Zzi$ fellasent. £uôI tu$alin.');
INSERT INTO "ramdane" VALUES(22,49,'22:49','Ini: "a yimdanen! Nek, $uôwen, d aneddaô kan ipbegginen".');
INSERT INTO "ramdane" VALUES(22,50,'22:50','I wid iumnen, ixeddmen ayen ilhan, ssmaê akked ôôeéq ukyis.');
INSERT INTO "ramdane" VALUES(22,51,'22:51','Wid iaânan asfunneg issekniyen nne£, widak d at ujajiê.');
INSERT INTO "ramdane" VALUES(22,52,'22:52','Ur d Nuzen, uqbel ik, amazan ne$ nnbi, mebla ma ikecm ed Cciîan, deg wayen imenna, ma imenna. Itekkes Öebbi ayen d issekcam Cciîan, sinna, Itebbit issekniyen iS. Öebbi d Amusnaw, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(22,53,'22:53','Iwakken ad Irr aggway n Cciîan d ahewwel, i at wulawen iblan akked at wulawen aquôan. Ih, imednas atnan di t$ennant tu$miqt.');
INSERT INTO "ramdane" VALUES(22,54,'22:54','Iwakken ad éôen wid iwumi teppunefk tmusni, ar neppa d tidep, s$uô Mass ik. Ad amnen yis. Ad as anzen wulawen nnsen. Öebbi, s tidep, Inehhu wid iumnen, s abrid uwqim.');
INSERT INTO "ramdane" VALUES(22,55,'22:55','Zgan wid ijehlen di ccekk fellas, ar ten id ias yimir, deqzalla, ne$ ibbwev iten id uâaqeb n wass abarar.');
INSERT INTO "ramdane" VALUES(22,56,'22:56','Assen, tagelda i Öebbi. Ad Iêudd garasen. Wid iumnen, ixeddmen ayen ilhan, ad ilin di Loennet n ssuâada.');
INSERT INTO "ramdane" VALUES(22,57,'22:57','I wid ijehlen, iskaddben issekniyen nne£, aâaqeb isdullun.');
INSERT INTO "ramdane" VALUES(22,58,'22:58','Wid iunagen, deg ubrid n Öebbi, sinna, pwan$an ne$ mmuten, a ten I$it Öebbi, war ccekk, s ôôeéq umâin. Ih, Öebbi Yif akw im$iten.');
INSERT INTO "ramdane" VALUES(22,59,'22:59','A ten Issekcem anekcum ara ôadin. Ih, Öebbi d Amusnaw, Iêellem.');
INSERT INTO "ramdane" VALUES(22,60,'22:60','Akka. Win iâaqben akken ippuâaqeb, sinna, ipwadnes, war ccekk, a t Isellek Öebbi. Ih, Öebbi Ipqili, Ipsemmiê.');
INSERT INTO "ramdane" VALUES(22,61,'22:61','Akka, acku Öebbi Issekcam iv deg wass, Issekcam ass deg yiv. U Öebbi Issel, Ipwali.');
INSERT INTO "ramdane" VALUES(22,62,'22:62','Akka, acku Öebbi d tidep, ma d ayen u$uô pnujun, war Neppa, d tikerkas. Ih, Öebbi d Aâlâyan, d Ameqwôan.');
INSERT INTO "ramdane" VALUES(22,63,'22:63','Day ur twalav ara Öebbi Isse$li d aman, seg igenni, d$a izzegzew wakal? Ih, Öebbi d Laîif, Iéôa.');
INSERT INTO "ramdane" VALUES(22,64,'22:64','IneS i illan deg igenwan, i illan di tmurt. Ih, Öebbi, ar Neppa d Asâay d Amaâzuz.');
INSERT INTO "ramdane" VALUES(22,65,'22:65','Ur twalav ara Öebbi Inub ed fellawen s wayen illan di tmurt, akked lbabur ippazalen di yill, s lameô iS. Iîîef igenni ur d i$elli f tmurt, siwa ma Iunef as. Ih, Öebbi, war ccekk, Issuruf u Ipêunu i imdanen.');
INSERT INTO "ramdane" VALUES(22,66,'22:66','D Neppa ikwen Issadren, sinna, ara kwen In$en, sinna, ara kwen id Issadren. Amdan, s tidep, d ajehli.');
INSERT INTO "ramdane" VALUES(22,67,'22:67','I yal lois, Nefka asekkir, teddun degs. Ulay$eô i k pna$en $ef lameô. Nuj ar Mass ik; ih, aql ak, keçç, di nnhaya tuwqimt.');
INSERT INTO "ramdane" VALUES(22,68,'22:68','Ma numren k, ini: "Öebbi Yif akw akken Issen ayen txeddmem.');
