INSERT INTO "ramdane" VALUES(26,225,'26:225','Day ur twalav ara, ar nitni, di yal ta$zut pmenîaôen?');
INSERT INTO "ramdane" VALUES(26,226,'26:226','U nitni qqaôen ayen ur xeddmen.');
INSERT INTO "ramdane" VALUES(26,227,'26:227','Kkes ed wid iumnen, ixeddmen ayen ilhan, id ismektayen aîas Öebbi, i irebêen mbaâd ma pwadensen. Ad éôen, wid idensen, tama anida ara ppin.');
INSERT INTO "ramdane" VALUES(27,1,'27:1','Ïs. Atnan issekniyen n Leqwôan, akked Tezmamt ipbegginen.');
INSERT INTO "ramdane" VALUES(27,2,'27:2','D nnhaya u d abecceô i lmumnin:');
INSERT INTO "ramdane" VALUES(27,3,'27:3','wid ibedden i téallit, ippaken zzakat, ipputebbten $ef laxeôt nnsen.');
INSERT INTO "ramdane" VALUES(27,4,'27:4','Ih, wid ur numin ara s laxeôt, Ncebbeê asen igiten nnsen, d$a ddebdeben.');
INSERT INTO "ramdane" VALUES(27,5,'27:5','Widak, fellasen aâaqeb ucmit mavi. Di laxeôt, d nitni ara ixesôen ugwar.');
INSERT INTO "ramdane" VALUES(27,6,'27:6','S tidep, keçç iusa k id Leqwôan, s$uô Bab n ûûwab, Amusnaw.');
INSERT INTO "ramdane" VALUES(27,7,'27:7','Mi inna Musa i twacult is: "nek wala$ times, ad awen d awi$, segs, asali, ne$ ad awen d awi$ asafu iôeqqen, amar a ppesseêmum".');
INSERT INTO "ramdane" VALUES(27,8,'27:8','Mi ibbwev $uôes, ipwanced: "ad iôqu wayen illan di tmes, akked wayen i as d izzin. Gedha s Öebbi, Mass imavalen!');
INSERT INTO "ramdane" VALUES(27,9,'27:9','A Musa! Ih, Nek d Öebbi, Uzmir, Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(27,10,'27:10','Veggeô taâkwazt ik"! Mi p iwala la tesseêôiîiw, amzun d azrem, ibren, ur iwexxeô. "A Musa! Ur ppagwad! Ar $uôI, ih, ur ppagwaden imazanen,');
INSERT INTO "ramdane" VALUES(27,11,'27:11','siwa win idensen, sinna, iu$al iwqem, deffir tuccva. Nek, s tidep, Psemmiêe$, Pêunnu$.');
INSERT INTO "ramdane" VALUES(27,12,'27:12','Ger afus ik di loib ik, a d iffe$ d amellal, ur ihlik. Si tza issekniyen, i Ferâun akked ugdud is. Nitni, war ccekk, d agdud icemten".');
INSERT INTO "ramdane" VALUES(27,13,'27:13','Mi ten id usan issekniyen nne£, âinani, nnan: "wagi iban d ssêur".');
INSERT INTO "ramdane" VALUES(27,14,'27:14','Ugin ten s ddens d pnefxa, u, deg iman nnsen, pputebbten. Wali, ihi, amek tevôa d ifessaden.');
INSERT INTO "ramdane" VALUES(27,15,'27:15','Ni$ Nefka tamusni i Dawed akked Sliman. Nnan: "ccekôan i Öebbi, i a$ Ismenyafen i waîaû n yimdanen iumnen".');
INSERT INTO "ramdane" VALUES(27,16,'27:16','Sliman iwôet Dawed, u inna: "a yimdanen! Nepwaselmed tutlayt n yifôax; ippunefk a$ si yal cci. Ayagi, s tidep, ard lfevl n ûûeê".');
INSERT INTO "ramdane" VALUES(27,17,'27:17','Pwajemmlen d, i Sliman, i2âsekôiwen is si leonun, imdanen, ifôax. Mdarraren.');
INSERT INTO "ramdane" VALUES(27,18,'27:18','Armi, mi bbwven ar te$zut n twevfin, tenna d tweîuft: "a yaweîîuf, kecmemt ar ixxamen nnkwent, mulac, war ccekk, a kwent iéd Sliman, neppa akked iâsekôiwen is, ur faqen".');
INSERT INTO "ramdane" VALUES(27,19,'27:19','Icmumeê, ivûa, $ef wayen d tenna. Inna: "a Mass iw! Err iyi ad snemmre$ l$it iK, is i $ T$atev, nek akked imawlan iw; ad xedme$ ise$ ara Tôaviv; Ssekcem iyi, s uêunu K, ar imdanen iK imaânen".');
INSERT INTO "ramdane" VALUES(27,20,'27:20','Issefqed ifôax, inna: "acimi ur wala$ ara iççibib, day d l$ayeb?');
INSERT INTO "ramdane" VALUES(27,21,'27:21','War ccekk, ad sâaddi$ fellas aâaqeb uqsiê, ne$ a t zlu$, ne$ a d iawi tasebbiwt tubrizt".');
INSERT INTO "ramdane" VALUES(27,22,'27:22','Ur iâaîîel ara. Inna d: "fehme$ ayen ur tefhimev. Usi$ d $uôek si Saba, s usali unûiê.');
INSERT INTO "ramdane" VALUES(27,23,'27:23','Ih, ufi$ en tameîîut d tagelliî nnsen. Ippunefk as si yal cci. £uôes asgeld abarar.');
INSERT INTO "ramdane" VALUES(27,24,'27:24','Ufi$ p in, neppat akked ugdud is, la kennun i yiîij, war Öebbi. Cciîan icebbeê asen igiten nnsen. Iveôf iten seg ubrid, d$a nitni ur pwanhan.');
INSERT INTO "ramdane" VALUES(27,25,'27:25','Acimi ur knan ara i Öebbi, id Issufu$en lesrar, di tmurt akked igenwan, i Iéôan ayen teppeffrem u ayen d tepberriêem?');
INSERT INTO "ramdane" VALUES(27,26,'27:26','Öebbi, ur illi ôebbi siwa Neppa, Mass n usgeld ameqwôaêan".');
INSERT INTO "ramdane" VALUES(27,27,'27:27','Inna: "a néeô ma tessawlev d tidep, ne$ ma keçç seg ikeddaben.');
INSERT INTO "ramdane" VALUES(27,28,'27:28','Öuê s tebôap iw agi, veggeô asen p, sinna, wexxeô asen, wali acu ara d rren".');
INSERT INTO "ramdane" VALUES(27,29,'27:29','Tenna: "a yimvebbôen, tepwaveggeô iyi d tebôap tukyist.');
INSERT INTO "ramdane" VALUES(27,30,'27:30','Ih, s$uô Sliman. Apan: s yisem n Öebbi Aênin Ipêunun.');
INSERT INTO "ramdane" VALUES(27,31,'27:31','Ur ssem$waôet ara iman nnwen $uôi, aset ed $uôi, d imvuâen".');
INSERT INTO "ramdane" VALUES(27,32,'27:32','Tenna: "a yimvebbôen, fket ed ôôay nnwen af lameô iw. Ur pseyyi$ lameô, siwa garawen".');
INSERT INTO "ramdane" VALUES(27,33,'27:33','Nnan: "nekwni d imawlan n tezmert, nesâa lemêella tuqsiêt. Lameô i kem. Muqel d acu ara d tseyyiv".');
INSERT INTO "ramdane" VALUES(27,34,'27:34','Tenna: "s tidep, igeldan ssefsaden tamdint ara kecmen. Sdullun imdanen is lâali. Akka ay xeddmen.');
INSERT INTO "ramdane" VALUES(27,35,'27:35','Nek, ad asen azne$ tarzeft, ad aôou$ acu ara d awin imcegâan".');
INSERT INTO "ramdane" VALUES(27,36,'27:36','Mi tebbwev ar Sliman, inna: "day ad iyi tâawnem s terkunt? Ayen i yi Ifka Öebbi, yif ayen i wen Ifka. D kunwi kan i ifeôêen, s terzeft nnwen.');
INSERT INTO "ramdane" VALUES(27,37,'27:37','U$al $uôsen. Ih, a nôuê $uôsen s lâaskeô, iwumi ur zmiren. A ten id nessuffe$, syenna, ndellen, d imejîuêen".');
INSERT INTO "ramdane" VALUES(27,38,'27:38','Inna: "a yimvebbôen, anwa degwen, ara yi d iawin asgeld is, uqbel a yi d asen d imvuâen"?');
INSERT INTO "ramdane" VALUES(27,39,'27:39','Aâfrit si leonun, inna d: "ad ak t id awi$, uqbel ma tekkrev seg umekkan ik. I wayagi, war ccekk, nek d uzmir muman".');
INSERT INTO "ramdane" VALUES(27,40,'27:40','Inna d bab n tmusni si Tezmamt: "nek ad ak t id awi$, uqbel a pfukkev asmeômec". Mi t iwala irs ed $uôes, inna: "wagi si lfevl n Mass iw, ad iyi ijeôôeb ma ad snemmre$, ne$ ma ad jehle$. Win isnemmren, isnemmer kan i iman is. Win ijehl, ihi, Mass iw d Asâay, d Ukyis".');
INSERT INTO "ramdane" VALUES(27,41,'27:41','Inna: "bedlet as udem usgeld is, a néeô ma tepwanha, ne$ haap seg wid ur nepwanha".');
INSERT INTO "ramdane" VALUES(27,42,'27:42','Mi d tusa, nnan as: "day am wagi, usgeld inem"? Terra d: "amzun d neppa". "Tusa ya$ d tmusni, uqbel neppat, nuzen.');
INSERT INTO "ramdane" VALUES(27,43,'27:43','Isôuê ip wayen tunez, war Öebbi. Tella, s tidep, seg ugdud ijehliyen".');
INSERT INTO "ramdane" VALUES(27,44,'27:44','Nnan as: "ekcem ar lbeôo"! Mi t twala, t$il degs aman; tesban ed ifadden is. Inna: "s tidep, d lbeôo ulelluc imzin". Tenna: "a Mass iw! Ih, dense$ iman iw. Akked Sliman, unze$ aS i Öebbi, Mass imavalen".');
INSERT INTO "ramdane" VALUES(27,45,'27:45','Ni$ Nuzen ed, i Tamud, gmat sen Üaliê: "âabdet Öebbi"! Maca, rran snat n trebbuya$, ppemnamarent.');
INSERT INTO "ramdane" VALUES(27,46,'27:46','Inna: "a yagdud iw! Acimi tep$awalem ar tucmip, uqbel ise$? Lemmer a ppeste$fôem Öebbi, ahat aêunu fellawen".');
INSERT INTO "ramdane" VALUES(27,47,'27:47','Nnan: "tkeêêesev a$, keçç akked wi iddan yidek". Inna: "lfal nnwen ar Öebbi, u kunwi d agdud ara ippuhewwlen".');
INSERT INTO "ramdane" VALUES(27,48,'27:48','Llan, di temdint, tza iterrasen i issefsaden di tmurt, ur pleqqimen.');
INSERT INTO "ramdane" VALUES(27,49,'27:49','Nnan: "yyaw a nemmesgal s Öebbi, ard a neévem deg iv fellas, neppa akked twacult is. Sinna, a nini i bab is: ur nelli d inagan n tmen$iwt n tawacult is. Ih, nekwni, d imawlan n tidep".');
INSERT INTO "ramdane" VALUES(27,50,'27:50','Undin ticeôkep, Nundi ticeôkep, nitni ur faqen.');
INSERT INTO "ramdane" VALUES(27,51,'27:51','Wali amek tevôa di tceôkep nnsen. Nen$ed iten, nitni, akked ugdud nnsen, meôôa.');
INSERT INTO "ramdane" VALUES(27,52,'27:52','Atnan hudden ixxamen nnsen, ilmend n ddens nnsen. Deg wayagi, war ccekk, assekni i ugdud issnen.');
INSERT INTO "ramdane" VALUES(27,53,'27:53','Nsellek wid iumnen u llan pêezziben.');
INSERT INTO "ramdane" VALUES(27,54,'27:54','I Luî, mi inna i ugdud is: "teppeddum ar wayen n dir, u kunwi tepwalim.');
INSERT INTO "ramdane" VALUES(27,55,'27:55','Ih, kunwi teppeddum ar irgazen, teb$am ten, war tilawin. Ih, kunwi d agdud ur nessin".');
INSERT INTO "ramdane" VALUES(27,56,'27:56','Tiririt n ugdud is, siwa mi d nnan: "ssuff$et tawacult n Luî, si temdint nnwen, nitni d imdanen i ib$an tezdeg".');
INSERT INTO "ramdane" VALUES(27,57,'27:57','Nsellek it, neppa akked twacult is, siwa tameîîut is. Nseyya a ppili seg wid igwran.');
INSERT INTO "ramdane" VALUES(27,58,'27:58','Nesse$li d fellasen ageffur. I icmet ugeffur n wid ippunedôen!');
INSERT INTO "ramdane" VALUES(27,59,'27:59','Ini: "ccekôan i Öebbi. Talwit i imdanen iS, wid Ifren". Ppif Öebbi, ne$ ayen pcaraken?');
INSERT INTO "ramdane" VALUES(27,60,'27:60','Day Win Ixelqen tamurt akked igenwan, i wen d Isse$lin aman, seg igenni - Nessenkar, yissen, iqwiôen ippembehwaoen, ur teêwaoem a d tessem$im ttjuô is -, day illa ôebbi, ar tama n Öebbi? Maca, nitni d agdud issaâdalen.');
INSERT INTO "ramdane" VALUES(27,61,'27:61','Day Win Irran tamurt d tanezdu$t, Irra degs isaffen, Irra yas idurar, Irra agadir ger sin ilellen, day illa ôebbi ar tama n Öebbi? Xaîi, tegwti degsen, ur ssinen.');
INSERT INTO "ramdane" VALUES(27,62,'27:62','Day Win Issemêasen i umevôuô, mara S d isiwel; Itekksen ayen n dir; i kwen Ipparan d igensas di tmurt; day illa ôebbi, ar tama n Öebbi? Drus id tesmektayem.');
INSERT INTO "ramdane" VALUES(27,63,'27:63','Day Win i kwen Inehhun, di tillas n lberr akked yill, id Ippaznen avu, d abecceô n ccira uêunu S; day illa ôebbi, ar tama n Öebbi? Ay Aâlay Öebbi, sennig wayen pcaraken!');
INSERT INTO "ramdane" VALUES(27,64,'27:64','Day Win Ibeddun lxelq, sinna, Ippales as; ikwen I$aten seg igenni akked tmurt; day illa ôebbi ar tama n Öebbi? Ini: "awit ed atebbet nnwen, ma tessawalem tidep"!');
INSERT INTO "ramdane" VALUES(27,65,'27:65','Ini: "tabavnit, di tmurt akked igenwan, ur p issin yiwen siwa Öebbi. Ur éôin melmi ara d kkren".');
INSERT INTO "ramdane" VALUES(27,66,'27:66','Ih, tamusni nnsen ur tessawev ar laxeôt? Xaîi, nitni di ccekk fellas. Xaîi, nitni, fellas, dre$len.');
INSERT INTO "ramdane" VALUES(27,67,'27:67','Nnan wid ijehlen: "day ma nu$al d akal, am imezwura nne$, s tidep, fella$ tanekra?');
INSERT INTO "ramdane" VALUES(27,68,'27:68','Ni$ waââden a$ aya, i nekwni akw i imezwura nne$, zik. Ayagi, d timucuha kan imenza".');
INSERT INTO "ramdane" VALUES(27,69,'27:69','Ini: "iniget di tmurt, walit amek tevôa d ibe$wviyen".');
INSERT INTO "ramdane" VALUES(27,70,'27:70','Ur $webben fellasen, ur pxiqqi ilmend n nncub nnsen.');
INSERT INTO "ramdane" VALUES(27,71,'27:71','Nnan: "melmi lemâahda agi, ma d tidep ay tessawalem"?');
INSERT INTO "ramdane" VALUES(27,72,'27:72','Ini: "kra n wayen i$ef têarem, ahat atan ivfeô kwen id".');
INSERT INTO "ramdane" VALUES(27,73,'27:73','Mass ik, s tidep, meqqweô lfevl iS, $ef imdanen. Maca, tegwti degsen ur snemmiren.');
INSERT INTO "ramdane" VALUES(27,74,'27:74','Mass ik, s tidep, Iéôa ayen teffren idmaren nnsen, akked wayen d qqaôen.');
INSERT INTO "ramdane" VALUES(27,75,'27:75','Ur illi wayen iffren, deg igenni akked tmurt, ur nuri di Tezmamt ipbegginen.');
INSERT INTO "ramdane" VALUES(27,76,'27:76','Ih, Leqwôan agi iêekku d i At Isôail, tegwti n wayen i$ef mgarraden.');
INSERT INTO "ramdane" VALUES(27,77,'27:77','Neppa, war ccekk, ard nnhaya akked uêunu i lmumnin.');
INSERT INTO "ramdane" VALUES(27,78,'27:78','Mass ik, s tidep, ad Iseyyi garasen s uêuddu S. Neppa d Uzmir, d Amusnaw.');
INSERT INTO "ramdane" VALUES(27,79,'27:79','Ppkel $ef Öebbi, ih, aql ak di tidep ipbegginen.');
INSERT INTO "ramdane" VALUES(27,80,'27:80','Ih, keçç, ur tepparav lmeggtin ad slen, ur tepparav iâeééugen ad slen i wancad, ma wexxôen.');
INSERT INTO "ramdane" VALUES(27,81,'27:81','Ur fellak nnhaya ider$alen, mgal ttelfan nnsen. Ara islen, siwa wid iumnen s issekniyen nne£. Nitni d imvuâen.');
INSERT INTO "ramdane" VALUES(27,82,'27:82','Mi iûaô wawal fellasen, ad asen d Nessuffe$ lweêc si tmurt; ad asen yini ar imdanen ur metbutit $ef issekniyen nne£.');
INSERT INTO "ramdane" VALUES(27,83,'27:83','Ass ideg ara d Nefser, si yal lois, arbaâ n wid inekôen issekniyen nne£, d tirni,');
INSERT INTO "ramdane" VALUES(27,84,'27:84','alamma, mi d usan, ad Yini: "day tnekôem issekniyen iW, ur ten teskecmem di tmusni? Ne$ d acu tellam txeddmem"?');
INSERT INTO "ramdane" VALUES(27,85,'27:85','Iûaô wawal fellasen, imi densen. Ur d pmeslayen.');
INSERT INTO "ramdane" VALUES(27,86,'27:86','Day ur walan ara ar Nerra yasen iv i ureyyeê nnsen, ass iwakken ad walin? Deg wayagi, s tidep, issekniyen i ugdud iumnen.');
INSERT INTO "ramdane" VALUES(27,87,'27:87','Ass uûuvu n lbuq, ad iagwad i illan deg igenwan, i illan di tmurt, siwa wid i S ihwan i Öebbi. A d asen akw $uôeS, s uêiwet.');
INSERT INTO "ramdane" VALUES(27,88,'27:88','A pwaliv idurar i t$ilev sebken, la teddun am usigna. D igi n Öebbi i Iûegmen yal cci. Neppa, s tidep, Iéôa d acu txedmem.');
INSERT INTO "ramdane" VALUES(27,89,'27:89','Win id iusan s ise$, ad iawi it yifen. Widak, assen, mevmunit si tugdi.');
INSERT INTO "ramdane" VALUES(27,90,'27:90','Wid id iusan s tucmip, ad ppin wudmawen nnsen ar tmes. Ni$ a ppeppuqeîîim kan $ef wayen txeddmem?');
INSERT INTO "ramdane" VALUES(27,91,'27:91','Pwamôe$ kan ad âabde$ Mass n temdint agi, i Iôqa. IneS yal cci. Pwamôe$ d ad ili$ seg imduâen,');
INSERT INTO "ramdane" VALUES(27,92,'27:92','ad male$ Leqwôan. Win ipwanhan, ipwanha kan i iman is. Win innetlafen, ini: "nek d aneddaô kan".');
INSERT INTO "ramdane" VALUES(27,93,'27:93','Ini: "a neckeô Öebbi. Ad awen Issken issekniyen iS. A ten tâaqlem". Mass ik ur I$effel ara $ef wayen txeddmem.');
INSERT INTO "ramdane" VALUES(28,1,'28:1','Ïsm.');
INSERT INTO "ramdane" VALUES(28,2,'28:2','Atnan issekniyen n Tezmamt ipbegginen.');
INSERT INTO "ramdane" VALUES(28,3,'28:3','Ad ak d Neêku si teqûiî n Musa akked Ferâun, s tidep, i ugdud iumnen.');
INSERT INTO "ramdane" VALUES(28,4,'28:4','Ih, Ferâun issem$weô iman is di tmurt; imezda$ ifeôq iten d iêôumen. Isrewrew îayfa segsen, izellu arrac nnsen, issidir tilawin nnsen. Ih, neppa seg ifessaden.');
INSERT INTO "ramdane" VALUES(28,5,'28:5','Neb$a a Nfeôôeo $ef wid irewrwen di tmurt, a ten Nerr d imeqwôanen, a ten Nerr d iweôôaten.');
INSERT INTO "ramdane" VALUES(28,6,'28:6','A ten Nesseqâad di tmurt. A Nessken i Ferâun, Haman, akked iâsekôiwen nnsen, ayen ppagwaden segsen.');
INSERT INTO "ramdane" VALUES(28,7,'28:7','Nweêêa i imma s n Musa: "ssuîîev it, sinna, ma tugadev fellas, err it ar wasif. Ur ppagwad, ur $webben. Ad am t id Nerr, u a t Nerr seg imazanen".');
INSERT INTO "ramdane" VALUES(28,8,'28:8','Teddem it twacult n Ferâun. Ad asen iu$al d acengu, d a$ilif. Ih, Ferâun, Haman, akked iâsekôiwen nnsen llan d imsaben.');
INSERT INTO "ramdane" VALUES(28,9,'28:9','Tenna tmeîîut n Ferâun: "d nnûib n tiî, i nek akked keçç. Ur tneqq, ahat ad a$ infaâ, ne$ a t nerr d mmit nne$". Nitni ur faqen ara.');
INSERT INTO "ramdane" VALUES(28,10,'28:10','Temmar ed tasa n yimma s n Musa. Qôib it id tessken, lemmer ur Nzeyyeô ara ul is, iwakken a ppili si lmumnin.');
INSERT INTO "ramdane" VALUES(28,11,'28:11','Tenna i ultma s: "evfeô it"! Teqquôa t si mbâid, nitni ur faqen.');
INSERT INTO "ramdane" VALUES(28,12,'28:12','Uqbel, Negdel as tid iûûuîuven. Tenna: "ad awen ssekne$ axxam ar awen itkwelfen fellas, u nitni ad as bedden"?');
INSERT INTO "ramdane" VALUES(28,13,'28:13','Nerra t id i imma s, iwakken ad as kkawent wallen is, ur t$webben, iwakken a péeô ar lemâahda n Öebbi d tidep. Maca, tegwti degsen, ur ssinen.');
INSERT INTO "ramdane" VALUES(28,14,'28:14','Mi meqqweô, ittaâqel, Nefka yas tussna, ûûwab. Akka ay Nepkafi imdanen lâali.');
INSERT INTO "ramdane" VALUES(28,15,'28:15','Ikcem tamdint, di lweqt ideg stehzan imezda$ is. Iufa n sin irgazen pna$en, yiwen seg uêôum is, wayev seg icenga s. Win n uêôum is, issuter as d afus, mgal win n icenga s. Iwwet it Musa s lbunia, in$a t. Inna: "ayagi, seg igi n Cciîan. Neppa, s tidep, d acengu n ûûeê, itellifen".');
INSERT INTO "ramdane" VALUES(28,16,'28:16','Inna: "a Mass iw! Nek dense$ iman iw, Semmeê iyi". Isemmeê as. Ih, Neppa Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(28,17,'28:17','Inna: "a Mass iw! S l$it iK felli, ur ppili$, d awez$i, si loiha ibe$wviyen".');
INSERT INTO "ramdane" VALUES(28,18,'28:18','Iufa d iman is di temdint, iugad, iquôaâ. Atan a d ipâaggiv $uôes, win i s d issutren afus, ass izeryen. Inna yas Musa: "keçç, s tidep, d im$eôôeq n ûûeê".');
INSERT INTO "ramdane" VALUES(28,19,'28:19','Acengu nnsen, mi ib$a a t iwwet, inna d: "a Musa! Day teb$iv ad iyi ten$ev, akken ten$iv yiwen, ivelli? Teb$iv kan a ppiliv d aqehhaô di tmurt, ur teb$iv ara a ppiliv seg iâaôviyen".');
INSERT INTO "ramdane" VALUES(28,20,'28:20','Iusa d urgaz si îîerf n temdint, ippazal. Inna d: "a Musa! S tidep, imvebbôen mlalen $ef loal ik, a k n$en. Effe$, nek, seg wid ik ipweûûin".');
INSERT INTO "ramdane" VALUES(28,21,'28:21','Iffe$ segs, iugad, iquôaâ. Inna: "a Mass iw! Sellek iyi seg ugdud imednas".');
INSERT INTO "ramdane" VALUES(28,22,'28:22','Mi ibbwi tikli ar Madian, inna: "ahat Mass iw ad iyi Iawi d ubrid umâin".');
INSERT INTO "ramdane" VALUES(28,23,'28:23','Mi ibbwev s aman n Madian, iufa n arbaâ n l$aci, la sswan. Iufa, di îîerf nnsen, snat n tlawin, îîfent lmal nnsent. Inna: "d acu txeddmemt"? Nnant ed: "ur nesswa alamma wexxôen imeksawen. Babat ne$ d am$aô ameqwôan".');
INSERT INTO "ramdane" VALUES(28,24,'28:24','Issew asent, iu$al ar tili, inna: "a Mass iw! Nek ôe$be$ $ef yal lxiô, ar iyi d Tessersev".');
INSERT INTO "ramdane" VALUES(28,25,'28:25','Tusa t id yiwet segsent, s tikli ikukran, tenna: "atan ak d issawal baba, a k ikafi imi $ tesswev". Mi iôuê $uôes, u iêka yas i ivôan, inna: "ur ppagwad, tepwasellkev seg ugdud imednas".');
INSERT INTO "ramdane" VALUES(28,26,'28:26','Yiwet segsent, tenna: "a baba! Eîîef it d acrik, ih, yif icriken ik, iohed, muman".');
INSERT INTO "ramdane" VALUES(28,27,'28:27','Inna: "b$i$, nek, ad ak fke$ yiwet si yissi yagi, s cceôt a pxedmev $uôi îam iseggwasen. Ma tessawdev mraw, s leb$i k. Ur b$i$ a k hejjne$. Ad ak ili$, ma Irad Öebbi, seg iâaôviyen".');
INSERT INTO "ramdane" VALUES(28,28,'28:28','Irra: "ayagi, gari yidek. A tin sâadda$ ger snat n tallitin, ur ippili felli u$ilif. Öebbi d Awkil, $ef wayen id nenna".');
INSERT INTO "ramdane" VALUES(28,29,'28:29','Mi ifukk Musa tallit, u iseyyeê akked twacult is, iwala times ar tama n udrar. Inna i twacult is: "qqimet, wala$ times, ahat ad awen d awi$, segs, asali ne$ asafu, amar a ppesseêmum".');
INSERT INTO "ramdane" VALUES(28,30,'28:30','Mi ibbwev $uôes, ipwanced ed si ttejôa, di rif ayeffus n te$zut, deg umviq iôqan: "a Musa! S tidep, Nek d Öebbi, Mass imavalen.');
INSERT INTO "ramdane" VALUES(28,31,'28:31','Veggeô taâkwazt ik". Mi p iwala, la tesseêôiîiw amzun d azrem, ibren ur iwexxeô. "A Musa! Qeddem, ur ppagwad, aql ak, ih, di laman.');
INSERT INTO "ramdane" VALUES(28,32,'28:32','Err afus ik di loib ik, a d iffe$ d amellal, ur ihlik. Sseqôeb ed $uôek i$il ik, ad ak tekkes tugwdi. Tigi d sin n ppbut s$uô Mass ik, i Ferâun akked imvebbôen is. Ih, nitni d agdud iballa$en".');
INSERT INTO "ramdane" VALUES(28,33,'28:33','Inna: "a Mass iw! Nek n$i$ yiwen segsen, ugade$ ad iyi n$en.');
INSERT INTO "ramdane" VALUES(28,34,'28:34','Gma Harun yif iyi afeûûeê umeslay. Azen it id yidi d amâiwen, ad iyi tebbet. Nek ugade$ ad iyi nekôen".');
INSERT INTO "ramdane" VALUES(28,35,'28:35','Inna: "a Nesseohed tayepp ik s gma k. Ad awen Nefk, i sin, adabu; ur kwen id ppawven. S issekniyen nne£, d kunwi akked wid iddan yidwen ara irebêen".');
INSERT INTO "ramdane" VALUES(28,36,'28:36','Mi ibbwev $uôsen Musa, s issekniyen iberrzen, nnan: "ayagi, siwa ssêur id innulfan. Ur nesli s waya, $uô ibabaten nne$ imezwura".');
INSERT INTO "ramdane" VALUES(28,37,'28:37','Inna Musa: "Mass iw Iéôa sennig wiyav, anwa iwumi Ifka nnhaya s$uôeS, anwa ara d igwrin deg Uxxam. Imednas, s tidep, ur rebêen".');
INSERT INTO "ramdane" VALUES(28,38,'28:38','Inna Ferâun: "a yimvebbôen, ur awen mli$ ara kra n ôebbi, war nekkini. Ecâal iyi times ar talaxt, a Haman; bnu yi lbeôo aâlayen; ahat ad ali$ ar ÖÔebbi n Musa. Nek, s tidep, êesbe$ t seg wid iskiddiben".');
INSERT INTO "ramdane" VALUES(28,39,'28:39','Neppa, akked iâsekôiwen is, sme$wôen iman nnsen di tmurt, war ssebba. £ilen, ar nitni, ur d ppu$alen $uône£.');
INSERT INTO "ramdane" VALUES(28,40,'28:40','Neppa, akked iâsekôiwen is, Neîîef iten. Nger iten ar yill. Wali amek tevôa d imednas.');
INSERT INTO "ramdane" VALUES(28,41,'28:41','Nerra ten d imeqwôanen i ineccden ar tmes. Ass n tnekra, ur ppusellken.');
INSERT INTO "ramdane" VALUES(28,42,'28:42','Neqqen asen xezzu di ddunit agi. Ass n tnekra, nitni seg ucmiten.');
INSERT INTO "ramdane" VALUES(28,43,'28:43','Ni$ Nefka Tazmamt i Musa, mbaâd ma Nesqucc isuta imezwura. D tafat i imdanen, d nnhaya u d aêunu. Amar a d mmektin.');
INSERT INTO "ramdane" VALUES(28,44,'28:44','Ur telliv ara di loiha utaram, mi Nseyya lameô i Musa. Ur telliv seg inagan.');
INSERT INTO "ramdane" VALUES(28,45,'28:45','Maca, Nxelq ed tisuta, $wezzif laâmeô nnsent. Ur tellid seg imezda$ n Madian, la sen teqqarev issekniyen nne£. Maca, Nella Neppazen.');
INSERT INTO "ramdane" VALUES(28,46,'28:46','Ur telliv ar tama n udrar, mi d Nessawel. Maca d aêunu s$uô Mass ik, iwakken a pnedrev agdud, u$uô ur d iusi uneddaô, uqbel ik. Amar a d mmektin.');
INSERT INTO "ramdane" VALUES(28,47,'28:47','Mulac, ma têuza ten tiyta id urwen ifassen nnsen, a d inin: "a Mass nne$! A lemmer i $ d Tuznev amazan, a neddu d issekniyen iK, a nili seg wid iumnen"!');
INSERT INTO "ramdane" VALUES(28,48,'28:48','Mi ten id tusa tidep s$uône£, nnan: "a lemmer id iusi am wayen id iusan i Musa"! Day ur ugin ara, zik, ayen id iusan i Musa? Nnan: "d sin ssêurat". Mwafaqen, nnan: "nekwni nugi ten i sin".');
INSERT INTO "ramdane" VALUES(28,49,'28:49','Ini: "awit ed Tazmamt, s$uô Öebbi, i yifen snat agi, i nnhaya, ad ddu$ yides, ma tessawalem tidep".');
INSERT INTO "ramdane" VALUES(28,50,'28:50','Ma ur ak d rran ara, efhem ar ddan kan d lhawiyyat nnsen. Anwa innetlafen, sennig win iteddun d lhawiyya s, war nnhaya s$uô Öebbi? Öebbi, s tidep, ur Inehhu agdud imednas.');
INSERT INTO "ramdane" VALUES(28,51,'28:51','Aql a£ Nessawwev asen awal, amendeô ma d mmektin.');
INSERT INTO "ramdane" VALUES(28,52,'28:52','Umnen yis wid iwumi Nefka Tazmamt, uqbel tagi.');
INSERT INTO "ramdane" VALUES(28,53,'28:53','Ma $ôan asen t, ad inin: "numen yis. Ih, ar neppa d tidep, s$uô Mass nne$. Uqbel is, s tidep, nella d imvuâen".');
INSERT INTO "ramdane" VALUES(28,54,'28:54','Wigi a ten id ias ukafi nnsen berdayen, $ef wayen ûebôen, swexxiôen tucmip s yise$, u seg wayen iten N$at, pseddiqen.');
INSERT INTO "ramdane" VALUES(28,55,'28:55','Mi slan i yir awal, ad as veôfen, u qaôen: "i nekwni igiten nne$, i kunwi igiten nnwen. Fellawen talwit. Ur neô$eb $ef at war tussna".');
INSERT INTO "ramdane" VALUES(28,56,'28:56','Keçç, s tidep, ur tnehhuv win têemlev. Maca, Öebbi Inehhu win i S ihwan. Neppa, Yif akw akken Iéôa wid ipwanhan.');
INSERT INTO "ramdane" VALUES(28,57,'28:57','Nnan: "ma nedda d nnhaya yidek, a nepwaxvef seg wakal nne$". Day ur ten Nesseqâad ara di loamaâ n laman, u$uô d ppawvent yal l$ellat? D l$it s$uône£. Maca, tegwti degsen, ur éôin.');
INSERT INTO "ramdane" VALUES(28,58,'28:58','Acêal Nesqucc n temdininn, cerrâent di temâict nnsent. Atnan ixxamen nnsen, ay ten ized$en, deffir sen, siwa ixeîîayen. D Nekwni ay d iweôôaten.');
INSERT INTO "ramdane" VALUES(28,59,'28:59','Ur Isquccu Mass ik timdinin, alamma iuzen i imezda$ nnsent, amazan i sen immalen issekniyen nne£. Ur Nesquccu timdinin, mebla ma llan imezda$ nnsent, d imednas.');
INSERT INTO "ramdane" VALUES(28,60,'28:60','Kra n wayen i wen ippunefken n cci, d zzhu n tmeddurt n ddunit, akked ccbuê is. Ayen illan $eô Öebbi, yif it, ad idum kter. Day ur pmeyyizen ara?');
INSERT INTO "ramdane" VALUES(28,61,'28:61','Day win iwumi Nefka lemâahda igerzen, u immuger ip, am win i Nezha s l$it n tmeddurt n ddunit; sinna, ass n tnekra, neppa seg wid ara d irsen?');
INSERT INTO "ramdane" VALUES(28,62,'28:62','Ass ideg ara sen d Isiwel, ad Yini: "anda ten wid iyY tepcarakem, u tellam tepkabarem"?');
INSERT INTO "ramdane" VALUES(28,63,'28:63','A d inin wid i$ef iûaô wawal: "a Mass nne$! Wigi i njeggeê, njeggeê iten, akken i nepwajeggeê. A nzekki $ureK: maççi d nekwni ay âabden".');
INSERT INTO "ramdane" VALUES(28,64,'28:64','Ad asen inin: "naâôet wid tepcarakem". Ad ten naâôen, ur asen d pparan. Walan aâaqeb. A lemmer llan pwanhan!');
INSERT INTO "ramdane" VALUES(28,65,'28:65','Ass ideg ara sen d Isiwel, ad Yini: "acu d terram i imazanen"?');
INSERT INTO "ramdane" VALUES(28,66,'28:66','Assen, ad dergen fellasen isalen, nitni ur pmesteqsayen.');
INSERT INTO "ramdane" VALUES(28,67,'28:67','Ma d win isteôoâen, iumnen, ixedmen ayen ilhan, ahat ad yili seg imerbaê.');
INSERT INTO "ramdane" VALUES(28,68,'28:68','Mass ik Ixelleq ayen i S ihwan, u Ismenyafa. Ur ten id iûaê usmenyif. Gedha s Öebbi! I Aâlay sennig wayen pcaraken!');
INSERT INTO "ramdane" VALUES(28,69,'28:69','Mass ik Iéôa ayen iffren deg idmaren nnsen, akked wayen cennâen.');
INSERT INTO "ramdane" VALUES(28,70,'28:70','Neppa d Öebbi, ur illi ôebbi siwa Neppa. I Neppa, annuz di tmezwarut, u di tneggarut. I Neppa aêuddu, $uôeS tu$alin nnwen.');
INSERT INTO "ramdane" VALUES(28,71,'28:71','Ini: "acu tmeyyzem, ma Isse$li d Öebbi iv imezgi, fellawen, alamma d ass n tnekra? Anwa ôebbi, war Öebbi, ara wen d iawin tafat? Day ur tsellem ara"?');
INSERT INTO "ramdane" VALUES(28,72,'28:72','Ini: "acu tmeyyzem, ma Issuli Öebbi ass imezgi, fellawen, alamma d ass n tnekra? Anwa ôebbi, war Öebbi, ara wen d iawin iv, a ppestaâfum degs? Day ur tepwalim ara"?');
INSERT INTO "ramdane" VALUES(28,73,'28:73','Seg uêunu S, Iga yawen iv akked wass, i ustaâfu nnwen, akked unadi n lfevl iS. Amar a ppesnemmrem.');
INSERT INTO "ramdane" VALUES(28,74,'28:74','Ass ideg ara sen d Isiwel, ad asen Yini: "anda ten wid iyY tepcarakem, u tellam tepkabarem"?');
INSERT INTO "ramdane" VALUES(28,75,'28:75','A d Nawi anagi, si yal lois. A Nini: "awit ed atebbet nnwen". Ad éôen ar tidep n Öebbi. Itelf iten wayen d snulfuyen.');
INSERT INTO "ramdane" VALUES(28,76,'28:76','Ih, Qaôun illa seg ugdud n Musa. Iqehô iten. Nefka yas igerrujen; s tezmert ugraw ioehden, arfad n tsura nnsen. Mi s inna ugdud is: "ur zehhu! Öebbi, s tidep, ur Iêemmel ara izehwaniyen.');
INSERT INTO "ramdane" VALUES(28,77,'28:77','Deg wayen i k Ifka Öebbi, nadi axxam aneggaru. Ur teppu amur ik, di ddunit. Elhu, akken i Ilha Öebbi $uôek. Ur ssaram lefsad di tmurt. Öebbi, s tidep, ur Iêemmel ifessaden".');
INSERT INTO "ramdane" VALUES(28,78,'28:78','Inna: "ippunefk iyi kan, ilmend n tmusni i sâi$". Day ur iéôa ara ar Öebbi Isqucc, uqbel is, ger isuta, wid i t iugaren di tezmert, isemmden kteô? Ur illi usteqsi ibe$wviyen, af ibekkaven nnsen.');
INSERT INTO "ramdane" VALUES(28,79,'28:79','Iffe$ ed s agdud is, s ôôêuê is. Wid ib$an tameddurt n ddunit, nnan: "awufan nesâa am wayen ippunefken i Qaôun! Ih, neppa d bab n terkunt tabarart".');
INSERT INTO "ramdane" VALUES(28,80,'28:80','Wid, iwumi teppunefk tmusni, nnan: "a ssexv nnwen! Ppwab n Öebbi yif it, i win iumnen, ixeddmen ayen ilhan. Ara d iûaê siwa win iûebôen".');
INSERT INTO "ramdane" VALUES(28,81,'28:81','Nger it seddaw tmurt, neppa akked uxxam is. Ur as telli terbaât ara t isellken, war Öebbi. Ur illi seg wid ipselliken iman nnsen.');
INSERT INTO "ramdane" VALUES(28,82,'28:82','Wid issaramen amkan is, ivelli nni, u$alen nnan: "zi$ Öebbi Issugut ôôeéq i win i S ihwan, deg imdanen iS, u iwezzen. Lemmer ur a$ I$at ara Öebbi, yili Iger a$ seddaw tmurt. Ih, ur rebêen ijehliyen".');
INSERT INTO "ramdane" VALUES(28,83,'28:83','Axxam nni aneggaru, a t Nerr i wid ijunben tem$weô akked usefsed, di tmurt. Tirect i wid ipêezziben.');
INSERT INTO "ramdane" VALUES(28,84,'28:84','Win id iusan s yise$, ad iawi ayen it yifen. Win id iusan s tucmip, ihi, ad ppuqeîîin wid ixedmen tucmitin, $ef wayen kan xeddmen.');
INSERT INTO "ramdane" VALUES(28,85,'28:85','Ih, Win Ilezmen fellak Leqwôan, war ccekk, a k id Irr s axxam. Ini: "Mass iw Yif akw akken Issen anwa id iusan s nnhaya, anwa i illan di ttelfan n ûûeê".');
INSERT INTO "ramdane" VALUES(28,86,'28:86','Ur tessarmev ara, ad ak ed ters Tezmamt, lemmer maççi d aêunu s$uô Mass ik. Ur ppili d amâiwen ijehliyen.');
INSERT INTO "ramdane" VALUES(28,87,'28:87','Ur k swexxiôen seg issekniyen n Öebbi, deffir trusi nnsen $uôek. Nuj ar Mass ik, ur ppili seg wid ipcaraken.');
INSERT INTO "ramdane" VALUES(28,88,'28:88','Ur nâaô, ar tama n Öebbi, ôebbi nniven. Ur illi ôebbi, siwa Neppa. Yal cci ad inger, siwa udem iS. IneS aêuddu. £ureS ara tu$alem.');
INSERT INTO "ramdane" VALUES(29,1,'29:1','Alm.');
INSERT INTO "ramdane" VALUES(29,2,'29:2','Day $ilen imdanen a ten ooen qqaôen: "numen", u nitni ur ppuhewwlen?');
INSERT INTO "ramdane" VALUES(29,3,'29:3','Ni$ Nhewwel wid i ten id izwaren. War ccekk, ad Yissin Öebbi wid iqqaôen tidep, ad Yissin ikeddaben.');
INSERT INTO "ramdane" VALUES(29,4,'29:4','Ne$ $ilen ad a£ zwiren wid ixedmen tucmitin? Dir it wayen pêuddun.');
INSERT INTO "ramdane" VALUES(29,5,'29:5','Win iggunin timlilit akked Öebbi, s tidep, a d ias, war ccekk, lajel n Öebbi. Neppa, Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(29,6,'29:6','Win ijuhden, ijuhed kan i iman is. Öebbi, s tidep, ur Iêwao ara imavalen.');
INSERT INTO "ramdane" VALUES(29,7,'29:7','Wid iumnen, ixeddmen ayen ilhan, war ccekk, ad asen Nekkes tucmitin nnsen, a ten Nkafi $ef wayen xeddmen ifuqen.');
INSERT INTO "ramdane" VALUES(29,8,'29:8','Nweûûa amdan ar lewqama, s imawlan is. Ma lezmen fellak, ad iyI tcarkev ayen ur tessinev, ur asen ppa$ awal. Tu$alin nnwen, $uôI. Ad awen Mle$ acu txeddmem.');
INSERT INTO "ramdane" VALUES(29,9,'29:9','Wid iumnen, ixedmen ayen ilhan, war ccekk, a ten Nessekcem ar iâaôviyen.');
INSERT INTO "ramdane" VALUES(29,10,'29:10','Ger imdanen, wid iqqaôen: "numen s Öebbi". Ma meênen, deg ubrid n Öebbi, ad êesben ahewwel n medden, d aâaqeb n Öebbi. Mi d iusa l$elb, s$uô Mass ik, ar ad inin: "ih, nella yidwen". Day ur Iéôa ara Öebbi, i illan deg idmaren imavalen?');
INSERT INTO "ramdane" VALUES(29,11,'29:11','War ccekk, Öebbi Issen wid iumnen, Issen ixabiten.');
INSERT INTO "ramdane" VALUES(29,12,'29:12','Nnan wid ijehlen, i wid iumnen: "ddut d ubrid nne$, a nbibb tuccva nnwen". Nitni ur pbibbin acemma, si tuccva nnsen. S tidep, nitni d ikeddaben.');
INSERT INTO "ramdane" VALUES(29,13,'29:13','War ccekk, ad bibben tiâekkwmin nnsen, akked tiâekkwmin nniven. Ih, ad ppusteqsin, ass n tnekra, af wayen id snulfuyen.');
INSERT INTO "ramdane" VALUES(29,14,'29:14','Ni$ Nuzen ed Nuê, i ugdud is. Izde$ yides, agim kkes ed smuset iseggwasen. Ibbwi ten uêemmal, nitni d imednas.');
INSERT INTO "ramdane" VALUES(29,15,'29:15','Nsellek it, neppa akked wid iddan di lbabuô. Nerra t d assekni i imavalen.');
INSERT INTO "ramdane" VALUES(29,16,'29:16','Ibôahim, mi inna i ugdud is: "âabdet Öebbi, êezzbet aS. Yif it wakka, i kunwi, lemmer teéôim.');
INSERT INTO "ramdane" VALUES(29,17,'29:17','Tâabdem kan tiâancuôin, war Öebbi. Tesnulfam d tikerkas. Ih, wid tâabdem, war Öebbi, ur zmiren a kwen $iten. Ssutret l$it i Öebbi, âabdet eP, snemmret aS. £uôeS ara tu$alem.');
INSERT INTO "ramdane" VALUES(29,18,'29:18','Ma tnekôem, ihi, uqbel nnwen, nekôen leoyus". I iwatan, i umazan, siwa assiwev ubeggen.');
INSERT INTO "ramdane" VALUES(29,19,'29:19','Day ur walan ara, amek id Inezzu Öebbi lxelq, sinna, Ippales as. Ih, ayagi, i Öebbi, d ushil.');
INSERT INTO "ramdane" VALUES(29,20,'29:20','Ini: "iniget di tmurt, walit amek id Inza lxelq. Sinna, Öebbi a d Iawi lxelq aneggaru. Ih, Öebbi Izmer i yal cci".');
INSERT INTO "ramdane" VALUES(29,21,'29:21','Ikkat win i S ihwan; Ipêunu $ef win i S ihwan. £ureS jebbu nnwen.');
INSERT INTO "ramdane" VALUES(29,22,'29:22','Ur tesfunnugem, kunwi, di tmurt, ne$ deg igenni. Ur tesâim, war Öebbi, bab ne$ asettaô.');
INSERT INTO "ramdane" VALUES(29,23,'29:23','Wid iugin issekniyen n Öebbi, akked temlilit yideS, widak ad aysen aêunu W. I widak, aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(29,24,'29:24','Tiririt n ugdud is, siwa mi d nnan: "en$et ep, ne$ sseô$et ep". Isellek it Öebbi, si tmes. Deg waya, s tidep, d assekni i ugdud iumnen.');
INSERT INTO "ramdane" VALUES(29,25,'29:25','Inna: "teddmem kan tiâancuôin, war Öebbi. Temêemmalem di tmeddurt n ddunit, sinna, ass n tnekra, kra degwen ad nekôen wiyav, kra ad xzun wiyav. Axxam nnwen d times, ur awen illi usellak".');
INSERT INTO "ramdane" VALUES(29,26,'29:26','Iumen it Luî. Inna: "nek, d iminig ar Mass iw. Ih, Neppa, d Uzmir, d Bab n ûûwab".');
INSERT INTO "ramdane" VALUES(29,27,'29:27','N$at it s Isêaq akked Yaâqub. I tarwas, Nefka lambiyya, Tazmamt. Nefka yas taqviât is, di ddunit; u neppa, di laxeôt, war ccekk, seg iâaôviyen.');
INSERT INTO "ramdane" VALUES(29,28,'29:28','Luî, mi inna i ugdud is: "ih, aql akwen la txedmem lâaô, ur ixdim yiwen, zik, deg imavalen.');
INSERT INTO "ramdane" VALUES(29,29,'29:29','La teppeddum, ih, ar irgazen, tgezzmem abrid, txeddmem ayen icemten, deg igrawen nnwen". Tiririt n ugdud is, siwa mi nnan: "awi a$ d aâaqeb n Öebbi, ma telliv seg wid issawalen tidep".');
INSERT INTO "ramdane" VALUES(29,30,'29:30','Inna: "a Mass iw! Efk iyi afus, mgal agdud ifessaden".');
INSERT INTO "ramdane" VALUES(29,31,'29:31','Mi d usan imceggâen nne£, ar Ibôahim, s ubecceô, nnan as: "ih, a Nesqucc imezda$ n temdint agi. Imezda$ is, s tidep, d imednas".');
INSERT INTO "ramdane" VALUES(29,32,'29:32','Inna d: "ni$ degs Luî". Nnan as: "Nekwni, Neéôa sennig akw, i illan degs. A t Naâteq, neppa akked twacult is, siwa tameîîut is, ara yilin seg wid igwran".');
INSERT INTO "ramdane" VALUES(29,33,'29:33','Mi usan imceggâen nne£, ar Luî, i$wbel ilmend nnsen, ur izmir a ten ifukk. Nnan as: "ur ppagwad, ur $webbel. Nekwni a k Naâteq, keçç d twacult ik, siwa tameîîut ik, ara yilin seg wid igwran.');
INSERT INTO "ramdane" VALUES(29,34,'29:34','Ih, nekwni a d Nesse$li ssxev seg igenni, $ef imezda$ n temdint agi, ilmend n lâaô nnsen".');
INSERT INTO "ramdane" VALUES(29,35,'29:35','Ni$ Neooa d segs, assekni iberrzen, i ugdud ipmeyyizen.');
INSERT INTO "ramdane" VALUES(29,36,'29:36','Ar Madian, gmat sen Cuâayeb. Inna: "a yagdud iw! Âabdet Öebbi, gganit ass aneggaru, ur oouôut di tmurt d ifessaden!".');
INSERT INTO "ramdane" VALUES(29,37,'29:37','Nekôen t. Tewwet iten zzenzla. Gwran d, d loeppat, deg ixxamen nnsen.');
INSERT INTO "ramdane" VALUES(29,38,'29:38','Âad akked Tamud - ni$ iban awen d seg ixxamen nnsen -, icebbeê asen Cciîan igiten nnsen. Issuffe$ iten seg ubrid, nitni pfeôôioen.');
INSERT INTO "ramdane" VALUES(29,39,'29:39','Qaôun, Ferâun, Haman, ni$ iusa ten id Musa, s ubeggen. Sme$wôen iman nnsen, di tmurt, ur rbiêen.');
INSERT INTO "ramdane" VALUES(29,40,'29:40','Yal wa, Newwet it $ef ubekkav is. Llan wid iwumi Nuzen tabucivant. Llan wid i iwwet usu$u. Llan wid i Nger seddaw wakal. Llan wid i Ne$meq. Maççi d Öebbi iten idensen, d nitni i idensen iman nnsen.');
INSERT INTO "ramdane" VALUES(29,41,'29:41','Wid iteddun d imawlan, war Öebbi, cban rtila. Tebna tissist. Ihi, axxam ahcican mavi, d tissist n rtila, lemmer ssinen.');
INSERT INTO "ramdane" VALUES(29,42,'29:42','Ih, Öebbi Iéôa akw ayen u$uô naâôen, war Neppa. Neppa, d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(29,43,'29:43','Imedyaten agi, Neppawi ten id, i l$aci. Siwa imusnawen ara ten ifehmen.');
INSERT INTO "ramdane" VALUES(29,44,'29:44','Ixleq Öebbi tamurt akked igenwan, s tidep. Ih, deg waya, assekni i lmumnin.');
INSERT INTO "ramdane" VALUES(29,45,'29:45','Mel ayen i k id ippuweêêan si Tezmamt. Bedd i téallit. Ih, taéallit teppekkes lâaô, tuccva. War ccek, iugar ip usmekti n Öebbi. Öebbi Iéôa acu tûenâam.');
INSERT INTO "ramdane" VALUES(29,46,'29:46','Ur pnamaret At Tezmamt, siwa s wawal aéidan, siwa wid degsen idensen. Init: "numen s wayen id irsen $uône$, id irsen $uôwen. Öebbi nne$, akked Öebbi nnwen, Yiwen. Nekwni $uôeS d imvuâen".');
INSERT INTO "ramdane" VALUES(29,47,'29:47','Akka ay ed Nessers, $uôek, Tazmamt. Wid iwumi Nefka Tazmamt, umnen yis. Ger wigi, kra umnen yis. I inekôen issekniyen nne£, siwa ijehliyen.');
INSERT INTO "ramdane" VALUES(29,48,'29:48','Ur telliv temmalev kra n Tezmamt, uqbel tagi; ur p tepparuv, s uyeffus ik. Yili ad cukken inekkaôen.');
INSERT INTO "ramdane" VALUES(29,49,'29:49','Xaîi, neppa d issekniyen iberrzen, deg idmaren n wid iwumi teppunefk tmusni. Siwa imednas, i inekôen issekniyen nne£.');
INSERT INTO "ramdane" VALUES(29,50,'29:50','Nnan: "a lemmer id rsen fellas, issekniyen s$uô Mass is"! Ini: "issekniyen ar Öebbi kan. Nek, d aneddaô kan, ipbegginen".');
INSERT INTO "ramdane" VALUES(29,51,'29:51','Ne$, ur steqnaâen ara s trusi $uôek, s$uône£, n Tezmamt, malen asen p. Deg waya, s tidep, aêunu, akked usmekti i ugdud iumnen.');
INSERT INTO "ramdane" VALUES(29,52,'29:52','Ini: "Iqadd Öebbi, d anagi gari yidwen. Issen i illan di tmurt akked igenwan. Wid iumnen di tkerkas, u ugin Öebbi, widak d imexsuôen".');
INSERT INTO "ramdane" VALUES(29,53,'29:53','Sêirin k $ef uâaqeb. Lemmer maççi d lajel ipwassnen, a ten id ias uâaqeb. Ih, a ten id ias, deqzalla, ur bnin.');
INSERT INTO "ramdane" VALUES(29,54,'29:54','Sêirin k $ef uâaqeb. Ih, Loahennama a ppezzi i ijehliyen');
INSERT INTO "ramdane" VALUES(29,55,'29:55','Ass ideg ar asen isbur uâaqeb, sennig sen, akw seddaw ivaôen nnsen, ad Yini: "jeôôbet ayen txeddmem".');
INSERT INTO "ramdane" VALUES(29,56,'29:56','A yimdanen iW iumnen, ni$ tamurt iW tewsaâ. Ihi, âabdet iyI, Nek.');
INSERT INTO "ramdane" VALUES(29,57,'29:57','Yal iman, ad ijeôôeb tameppant; sinna, $uône£ a d tu$alem.');
INSERT INTO "ramdane" VALUES(29,58,'29:58','Wid iumnen, ixeddmen ayen ilhan, ad asen Nerr tanezdu$t di Loennet m t$uôfatin, ddaw as isaffen ppazzalen, degs ad wekkôen. D asaâdi warraz n wid ixedmen lxiô,');
INSERT INTO "ramdane" VALUES(29,59,'29:59','wid iûebôen, ippeklen $ef Mass nnsen.');
INSERT INTO "ramdane" VALUES(29,60,'29:60','Acêal n lewêuc, ur d neppawi ôôeéq nnsen. Öebbi Iôeéq iten, u Iôeéq ikwen. Neppa, Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(29,61,'29:61','Ma testeqûav ten: "anwa i Ixelqen tamurt akked igenwan, i Inuben s yiîij akked waggur"? A d inin, war ccekk: "d Öebbi". Ihi, acimi wexxôen?');
INSERT INTO "ramdane" VALUES(29,62,'29:62','Öebbi Issugut l$it i win i S ihwan, deg imdanen iS, u Iwezzen as. Ih, Öebbi Issen yal cci.');
INSERT INTO "ramdane" VALUES(29,63,'29:63','Ma testeqûav ten: "anwa id Isse$layen aman seg igenni, Issidir yissen akal, deffir tmeppant is"? A d inin, war ccekk, "d Öebbi". Ini: "ccekôan i Öebbi". Maca, tegwti degsen, ur fhimen.');
INSERT INTO "ramdane" VALUES(29,64,'29:64','Tameddurt agi, di ddunit, siwa lhawiyya, d wurar. D axxam aneggaru, war ccekk, ay d amuddir, lemmer éôin.');
INSERT INTO "ramdane" VALUES(29,65,'29:65','Mara rekben di lbabuô, ad naâôen Öebbi, d idiniyen zeddigen $uôeS. Mi ten Isellek ar lberr, atnan la pcaraken.');
INSERT INTO "ramdane" VALUES(29,66,'29:66','Ad nekôen ayen i sen Nefka, ad zhun, deqqal ad éôen.');
INSERT INTO "ramdane" VALUES(29,67,'29:67','Day ur walan ara, Nerra loamaâ n laman, wanag la pwaxvafen imdanen i sen d izzin? Day ad amnen di tkerkas, u ad nekôen l$it n Öebbi?');
INSERT INTO "ramdane" VALUES(29,68,'29:68','Anwa i idensen kteô: win id isnulfan tikerkas af Öebbi, ne$ win irran tidep d tikerkas, mi t id tusa? Day maççi d Loahennama ay d tanezdu$t i ijehliyen?');
INSERT INTO "ramdane" VALUES(29,69,'29:69','Wid ijuhden ar loiha nne£, a ten Nenhu, war ccekk, s iberdan nne£. Ih, Öebbi Ippili ar lâuûam.');
INSERT INTO "ramdane" VALUES(30,1,'30:1','Alm.');
INSERT INTO "ramdane" VALUES(30,2,'30:2','IôÔöumiyen rnan ten,');
INSERT INTO "ramdane" VALUES(30,3,'30:3','deg wakal aqôib mavi. Deffir lexsaôa nnsen, ad rnun,');
INSERT INTO "ramdane" VALUES(30,4,'30:4','di kra iseggwasen. Lameô ar Öebbi, uqbel aya, u ssyen $eô da. Assen, ad feôêen lmumnin');
INSERT INTO "ramdane" VALUES(30,5,'30:5','s rrbeê n Öebbi. Isserbaê win i S ihwan. Neppa, d Uzmir, Ipêunu.');
INSERT INTO "ramdane" VALUES(30,6,'30:6','D lemâahda n Öebbi. Öebbi ur Ipxallaf ara lemâahda S, maca, tegwti n medden ur ssinen.');
INSERT INTO "ramdane" VALUES(30,7,'30:7','Ssnen ayen id innunnten, di tmeddurt n ddunit. £ef laxeôt nitni stehzan.');
INSERT INTO "ramdane" VALUES(30,8,'30:8','Day ur meyyzen ara deg iman nnsen? Ur Ixliq Öebbi tamurt, igenwan, akked wayen illan garasen, siwa s tidep, akked lajel ipwassnen. Ih, aîas n medden i inekôen timlilit d Mass nnsen.');
INSERT INTO "ramdane" VALUES(30,9,'30:9','Day ur unagen ara di tmurt, ad walin amek tevôa d wid i ten id izwaren? Llan ifen ten tazmert, $zan tamurt, $elben ten akken i p âamôen. Usan ten id imazanen nnsen, s ubeggen. Ur ten Idnis Öebbi. Maca d nitni i idensen iman nnsen.');
INSERT INTO "ramdane" VALUES(30,10,'30:10','D$a teqseê taggara n laâôuô. Skaddben issekniyen n Öebbi. Pmmaâliken fellasen.');
INSERT INTO "ramdane" VALUES(30,11,'30:11','Öebbi Inezzu lxelq; sinna, Ippales it id. Sinna, $uôeS ara tu$alem.');
INSERT INTO "ramdane" VALUES(30,12,'30:12','Ass ideg ara d ijbu yimir, ad feqâen ibe$wviyen.');
INSERT INTO "ramdane" VALUES(30,13,'30:13','Ur ten pselliken wid pcaraken. Ad nekôen wid pcaraken.');
INSERT INTO "ramdane" VALUES(30,14,'30:14','Ass ideg ara d ijbu yimir, assen ad mfaôaqen.');
INSERT INTO "ramdane" VALUES(30,15,'30:15','Wanag wid iumnen u xeddmen ayen ilhan, ad zhun di tebêirt.');
INSERT INTO "ramdane" VALUES(30,16,'30:16','Ma d wid ijehlen, inekôen issekniyen nne£, akked temlilit n laxeôt, widak ar uâaqeb ara rsen.');
INSERT INTO "ramdane" VALUES(30,17,'30:17','Gedha s Öebbi, tameddit nnwen, taûebêit nnwen!');
INSERT INTO "ramdane" VALUES(30,18,'30:18','I Neppa ccekôan, di tmurt akked igenwan, tameddit u di pnaûfa n wass.');
INSERT INTO "ramdane" VALUES(30,19,'30:19','Issuffu$ ed amuddir si lmegget. Issuffu$ ed lmegget seg umuddir. Issidir akal, deffir tmeppant is. Akka ara d teff$em.');
INSERT INTO "ramdane" VALUES(30,20,'30:20','Seg issekniyen iS: Ixelq ikwen seg wakal. Aql akwen d imdanen tembwezzâam.');
INSERT INTO "ramdane" VALUES(30,21,'30:21','Seg issekniyen iS: Ixelq awen, segwen, tiâggaltin nnwen, a pzed$em yidsent. Irra garawen tayri, lmaênana. Deg waya, s tidep, issekniyen i ugdud ipmeyyizen.');
INSERT INTO "ramdane" VALUES(30,22,'30:22','Seg issekniyen iS: lxelq n tmurt akked igenwan, amgirred ger tutlayin nnwen, akked initen nnwen. Deg waya, s tidep, issekniyen i imusnawen.');
INSERT INTO "ramdane" VALUES(30,23,'30:23','Seg issekniyen iS: ives nnwen deg iv, deg wass, anadi nnwen n lfevl iS. Deg waya, s tidep, issekniyen i ugdud isellen.');
INSERT INTO "ramdane" VALUES(30,24,'30:24','Seg issekniyen iS: Issenâat awen lebôaq, degs tugdi, asirem. Issadar ed aman seg igenni, Issidir yissen akal, deffir tmeppant is. Deg waya, s tidep, issekniyen i ugdud ifehmen.');
INSERT INTO "ramdane" VALUES(30,25,'30:25','Seg issekniyen iS: s lameô iS, igenni akked tmurt bedden. Sinna, ma Isawel awen d, yiwen usiwel, seg wakal kunwi a d teff$em.');
INSERT INTO "ramdane" VALUES(30,26,'30:26','IneS i illan di tmurt akked igenwan. Akken ma llan, unzen aS.');
INSERT INTO "ramdane" VALUES(30,27,'30:27','D Neppa i Inezzun lxelq, sinna Ippales it id. D ayen i S isehlen mavi. IneS umedya aâlayan, di tmurt akked igenwan. Neppa d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(30,28,'30:28','Ibbwi yawen d amedya, seg iman nnwen: day tcerkem d waklan nnwen, ayen i s ikwen N$at, d$a tâadlem yidsen? Day ten teppagwadem, akken teppemyagwadem garawen? Akka ay Nepfeûûil issekniyen i ugdud ipxemmimen.');
INSERT INTO "ramdane" VALUES(30,29,'30:29','Xaîi! Wid idensen teddun d lhawiyyat nnsen, ur ssinen. Anwa ara inhun wid Itlef Öebbi? Ur sâin imâiwnen.');
INSERT INTO "ramdane" VALUES(30,30,'30:30','Smil udem ik adini ar tidep, d lxelq n Öebbi akken id Ixleq imdanen. Ur illi ubeddel, di lxelq n Öebbi. Wagi d ddin amxallef, maca, tegwti n medden ur ssinen.');
INSERT INTO "ramdane" VALUES(30,31,'30:31','D imvuâen iS, êezzbet aS, beddet i téallit, ur ppilit seg wid ipcaraken,');
INSERT INTO "ramdane" VALUES(30,32,'30:32','seg wid ifeôqen ddin nnsen. U$alen d îayfat, yal akabar icôeê s wayen isâa.');
INSERT INTO "ramdane" VALUES(30,33,'30:33','Imdanen, ma iwwet iten wugur, ad naâôen Mass nnsen, d imvuâen iS. Sinna, ma Isêess asen aêunu, s$uôeS, a pwaliv arbaâ degsen pcaraken i Mass nnsen,');
INSERT INTO "ramdane" VALUES(30,34,'30:34','iwakken ad nekôen i sen Nefka. Zhut! Deqqal a ppeéôem.');
INSERT INTO "ramdane" VALUES(30,35,'30:35','Ne$ Nessader asen ed amvebbeô, i ipmeslayen $ef wayen i S pcaraken.');
INSERT INTO "ramdane" VALUES(30,36,'30:36','Mi Nesêess aêunu i imdanen, ad ceôêen yis. Ma têuza ten tucmip, seg wayen id urwen ifassen nnsen, ad aysen.');
INSERT INTO "ramdane" VALUES(30,37,'30:37','Day ur walan ara ar Öebbi Issugut ôôeéq i win i S ihwan, u Iwezzen. Deg waya, s tidep, issekniyen i ugdud iumnen.');
INSERT INTO "ramdane" VALUES(30,38,'30:38','Efk as leêqq is i llezm ik, i ume$bun, i win id ibbwi ubrid. Ppif akka i wid ib$an udem n Öebbi. D widak ay d imerbaê.');
INSERT INTO "ramdane" VALUES(30,39,'30:39','Ayen teppakem n rrba, iwakken ad issem$weô ayla n medden, ar Öebbi ur issim$uô ara. Ayen tepzekkim $ef udem n Öebbi, d ayen ara ibuôekten.');
INSERT INTO "ramdane" VALUES(30,40,'30:40','Öebbi, ikwen id Ixelqen, sinna, ikwen I$aten, sinna, ara kwen In$en, sinna, ara kwen id Issidren. Day ara ixdem kra, seg waya, yiwen seg wid tepcarakem? Gedha s yiS, Aâlayan sennig wid pcaraken!');
INSERT INTO "ramdane" VALUES(30,41,'30:41','Ivehô ed lefsad di lberr u di yill seg wayen id urwen ifassen n medden, iwakken ad Isukk fellasen kra seg wayen xedmen, amar a d u$alen s abrid.');
INSERT INTO "ramdane" VALUES(30,42,'30:42','Ini: "iniget di tmurt, muqlet amek tevôa d imezwura. Tegwti, segsen, pcaraken".');
INSERT INTO "ramdane" VALUES(30,43,'30:43','Err udem ik ar ddin amxallef, uqbel a d ias s$uô Öebbi, ass ur nepwexxiô. Assen, ad pwaferzen.');
INSERT INTO "ramdane" VALUES(30,44,'30:44','Win ijehlen, tijehli s mgal is; wid ixeddmen ayen ilhan, gemren i iman nnsen.');
INSERT INTO "ramdane" VALUES(30,45,'30:45','Iwakken ad Ikafi, s lfevl iS, wid iumnen u xeddmen ayen ilhan. Neppa, s tidep, ur Iêemmel ara ijehliyen.');
INSERT INTO "ramdane" VALUES(30,46,'30:46','Seg issekniyen iS: Ippazen ed avu abcir, ad awen Isêess, cwiî, seg uêunu S; ad iaâjel lbabuô, s lameô iS. A pnadim lfevl iS, amar a ppesnemmrem.');
INSERT INTO "ramdane" VALUES(30,47,'30:47','Ni$ Nuzen ed, uqbel ik, imazanen i igduden nnsen. Usan ten id, s ubeggen. Nerra d ppaô seg wid ibe$wven. Aslak n lmumnin d lwaoeb nne£.');
INSERT INTO "ramdane" VALUES(30,48,'30:48','Öebbi, id Ippaznen avu isselêayen asigna, a t ifser, deg igenni, akken i S ihwa, a t Issemfaôeq, a pwaliv ageffur a d iteffe$ segs. Mi t Issawev ar wid i S ihwan, deg imdanen iS, a ten tafev ceôêen.');
INSERT INTO "ramdane" VALUES(30,49,'30:49','Wanag, iu$ lêal, llan uysen, uqbel ad asen d i$li.');
INSERT INTO "ramdane" VALUES(30,50,'30:50','Wali lateô uêunu n Öebbi: amek id Issidir akal, deffir tmeppant is. Ih, d Win, s tidep, i Issidiren lmeggtin. Neppa Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(30,51,'30:51','Mara Nazen avu, ara walin ippiwôi$, a pdum, deffir es, tjehli nnsen.');
INSERT INTO "ramdane" VALUES(30,52,'30:52','Ma d keçç, s tidep, ur tezmirev a pperrev lmeggtin ad slen, ne$ iâeééugen ad slen i tnaâôit, ma u$alen $er deffir.');
INSERT INTO "ramdane" VALUES(30,53,'30:53','Ur tezmirev a penhuv ider$alen, mgal ttelfan nnsen. Ara terrev ad ak slen, siwa wid iumnen s issekniyen nne£. Nitni d imvuâen.');
INSERT INTO "ramdane" VALUES(30,54,'30:54','Öebbi, ikwen Ixelqen si ôehfan; sinna, deffir ôehfan, Irra tazmert; sinna, deffir tezmert, Irra ôehfan, akked ccib. Ixelleq ayen i S ihwan. Neppa d Amusnaw, d Aêernuk.');
INSERT INTO "ramdane" VALUES(30,55,'30:55','Ass ideg ara d ijbu yimir, ad gallen ibe$wviyen, ar siwa saâa segmi mmuten. Akka ay skiddiben.');
INSERT INTO "ramdane" VALUES(30,56,'30:56','Ad inin wid iwumi teppunefk tussna, akked liman: "ni$ teqqimem di Tezmamt n Öebbi, ar ass n tnekra. Wagi d ass n tnekra, maca tellam ur tessinem".');
INSERT INTO "ramdane" VALUES(30,57,'30:57','Assen, wid idensen, ur ten tepsellik nndama ne$ asuter n ssmaê.');
INSERT INTO "ramdane" VALUES(30,58,'30:58','Ni$ Nefka, i medden, yal amedya di Leqwôan agi. Maca, mi sen d tebbwiv assekni, ad ak inin, war ccekk, wid ijehlen: "kunwi d wid ireoonen".');
INSERT INTO "ramdane" VALUES(30,59,'30:59','Akka i Iselle$ Öebbi ulawen n wid ur nessin.');
INSERT INTO "ramdane" VALUES(30,60,'30:60','Eûbeô, ih, lemâahda n Öebbi d tidep. Ur k sêirin, wid ur nepputebbet!');
INSERT INTO "ramdane" VALUES(31,1,'31:1','Alm.');
INSERT INTO "ramdane" VALUES(31,2,'31:2','Wigi d issekniyen n Tezmamt tuûwibt;');
INSERT INTO "ramdane" VALUES(31,3,'31:3','d nnhaya u d aêunu i iâaôviyen,');
INSERT INTO "ramdane" VALUES(31,4,'31:4','wid ibedden i téallit, ipzekkin, ipputebbten af laxeôt.');
INSERT INTO "ramdane" VALUES(31,5,'31:5','Wigi, di nnhaya s$uô Mass nnsen. Wigi, d isaâdiyen.');
INSERT INTO "ramdane" VALUES(31,6,'31:6','Ger yimdanen, wid ippa$en awal n zzhu, iwakken ad nnetlafen seg ubrid n Öebbi, war tamusni. U fellas ttihin. I wigi, aâaqeb isdullun.');
INSERT INTO "ramdane" VALUES(31,7,'31:7','Ma mlan as issekniyen nne£, ad ibren s tem$weô, amzun ur asen isli, amzun âaégen as imeééu$en. Xebbeô it s uâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(31,8,'31:8','Ih, i wid iumnen, ixeddmen ayen ilhan, leonanat isaâdiyen.');
INSERT INTO "ramdane" VALUES(31,9,'31:9','Degsen ad wekkôen. Lemâahda n Öebbi d tidep. Neppa d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(31,10,'31:10','Ixleq igenwan, war tigejda ara twalim. Irra di tmurt idurar, iwakken ur tepzenzil ara yiswen. Iwezzaâ, degs, yal lxelq. Nessader ed aman seg igenni. Nessem$i d degs, yal tayuga tukyist.');
INSERT INTO "ramdane" VALUES(31,11,'31:11','Wagi d lxelq n Öebbi. Sseknet iyi d, acu d xelqen wid ur nelli d Neppa. Xaîi, imednas atnan di ttelfan n ûûeê.');
INSERT INTO "ramdane" VALUES(31,12,'31:12','Ni$ Nefka i Luqman ûûwab: "snemmer Öebbi. Win isnemmiren, isnemmir kan i iman is. Ma d wid ijehlen, ihi, Öebbi d Asâay, d Amaâzuz".');
INSERT INTO "ramdane" VALUES(31,13,'31:13','Mi inna Luqman i mmi s, la t isselmad: "a mmi! Ur pcarak i Öebbi. Ccerk, s tidep, d ddens abarar".');
INSERT INTO "ramdane" VALUES(31,14,'31:14','Nweûûa amdan $ef imawlan is. Imma s temêen s tadist is. Tuîva s, sin iseggwasen. Snemmer iyI, u i imawlan ik. Tu$alin $uôI.');
INSERT INTO "ramdane" VALUES(31,15,'31:15','Ma êeppmen fellak, ad iyI tcarkev ayen ur tessinev, ur asen teddu di leb$i. Illi yidsen d uwqim di ddunit. Ddu d ubrid n wid isteôoâen $uôI. Sinna, tu$alin nnwen $uôI, ad awen Mle$ ayen txeddmem.');
INSERT INTO "ramdane" VALUES(31,16,'31:16','"Ih, a mmi, xas am taéeyt uâaqqa ucceryad, ara yilin di teéôut, ne$ deg igenwan, ne$ di tmurt, a p id Iawi Öebbi. Ih, Öebbi d Laîif, Iéôa.');
INSERT INTO "ramdane" VALUES(31,17,'31:17','A mmi, bedd i téallit, ameô lewqama, îîixxeô i wayen n dir, eûbeô i wayen ara k id iasen! Aya, s tidep, seg uâzzem i lumuô.');
INSERT INTO "ramdane" VALUES(31,18,'31:18','Ur sxunzuô ar medden, ur teddu di tmurt s îî$aya! Öebbi, s tidep, ur Iêemmel yal imnefxi, amzuxi.');
INSERT INTO "ramdane" VALUES(31,19,'31:19','Iôqiq di tikli k, ssers ûûut ik! Ni$ ûûut ucmit mavi, d win n ye$yal".');
INSERT INTO "ramdane" VALUES(31,20,'31:20','Day ur twalam ara, Inub Öebbi fellawen s wayen akw illan deg igenwan, akked wayen illan di tmurt? Isserwa yawen l$it iS, uvhiô, uffir. U llan l$aci i ipnamaren $ef Öebbi, war tamusni, ne$ nnhaya, ne$ Tazmamt timnewweôt.');
INSERT INTO "ramdane" VALUES(31,21,'31:21','Ma nnan asen: "ddut akked wayen id Issader Öebbi", a d inin: "xaîi, a neddu akked wayen id nufa ar imezwura nne$". I ma Cciîan atan la ten inecced ar uâaqeb n tmes?');
INSERT INTO "ramdane" VALUES(31,22,'31:22','Win ifkan i Öebbi udem amvuâ, u neppa d aâeôvi, iîîef deg ufus iûeêêan mavi. Ar Öebbi, tu$alin n lumuô.');
INSERT INTO "ramdane" VALUES(31,23,'31:23','Win ijehlen, ur k t$webben tjehli s! £uône£, tu$alin nnsen; ad asen Nini acu xedmen. Öebbi, s tidep, Iéôa i illan deg idmaren.');
INSERT INTO "ramdane" VALUES(31,24,'31:24','A ten Neoo ad zhun cwiî, sinna, a Nêeppem fellasen aâaqeb iwaâôen.');
INSERT INTO "ramdane" VALUES(31,25,'31:25','Ma testeqûav ten: "anwa i Ixelqen tamurt akked igenwan"? A d inin, war ccekk: "d Öebbi". Ini: "ccekôan i Öebbi". Maca, tegwti degsen, ur ssinen.');
INSERT INTO "ramdane" VALUES(31,26,'31:26','I Öebbi i illan akw di tmurt akked igenwan. Ih, Öebbi d Asâay, d Amaâzuz.');
INSERT INTO "ramdane" VALUES(31,27,'31:27','Xas lukan ttjuô akw n ddunit d leqlamat, u yill, ernu yas ûa ilellen nniven, d lmidad nnsen, ur pqamen awalen n Öebbi. Ih, Öebbi d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(31,28,'31:28','Lxelq nnwen, tanekra nnwen, amzun d yiman yiwen kan. Ih, Öebbi Issel, Ipwali.');
INSERT INTO "ramdane" VALUES(31,29,'31:29','Day ur twalav ara, Öebbi Issekcam iv deg wass, Issekcam ass deg yiv. Iêeppem i yiîij akked waggur ad lêun, ar lajel ipwassnen. Ih, Öebbi Iéôa ayen txeddmem.');
INSERT INTO "ramdane" VALUES(31,30,'31:30','Akka, acku Öebbi d Tidep. Ayen u$uô naâôen, war Neppa, d tikerkas. Öebbi, d Aâlayan, d Ameqwôan.');
INSERT INTO "ramdane" VALUES(31,31,'31:31','Day ur twalav ara ssfina, teppazal di yill s lqwedôa n Öebbi, iwakken ad awen d Issenâat seg issekniyen iS. Ih, deg wayagi, issekniyen i yal aûebôi isnemmiren.');
INSERT INTO "ramdane" VALUES(31,32,'31:32','Ma tesbur asen lmuja, am tillas, ad nujen ar Öebbi, $uôeS s ddin azedgan. Mi ten Isellek ar lberr, kra, degsen, îîfen deg wawal nnsen. I inekôen issekniyen nne£, siwa yal axeddaâ ajehli.');
INSERT INTO "ramdane" VALUES(31,33,'31:33','A yimdanen! Ëezzbet i Mass nnwen. Agwadet ass ideg ababat ur d ilehhu d mmi s, u mmi s ur d ilehhu d baba s, s wacemma. Ih, lemâahda n Öebbi, d tidep. Ur kwen tep$uôôu tmeddurt n ddunit. Ur kwen issefôa$ im$weôôi, $ef Öebbi.');
INSERT INTO "ramdane" VALUES(31,34,'31:34','Öebbi, s tidep, $uôeS tamusni n yimir. Issadar ed ageffur. Iéôa i illan di tâabbuvin. Ur iéôi yiwen acu ara ikseb azekka; ur iéôi yiwen anwa akal ideg ara immet. Öebbi, s tidep, Iéôa, d Amusnaw.');
INSERT INTO "ramdane" VALUES(32,1,'32:1','Alm.');
INSERT INTO "ramdane" VALUES(32,2,'32:2','Tarusi n Tezmamt, ur illi degs ccekk, s$uô Mass imavalen.');
INSERT INTO "ramdane" VALUES(32,3,'32:3','Ne$ a d inin: "isnulfa p id". Xaîi, neppat, d tidep s$uô Mass ik, iwakken a pnedôev agdud u$uô ur d iusi ara uneddaô, uqbel ik, amar ad pwanhun.');
INSERT INTO "ramdane" VALUES(32,4,'32:4','Öebbi i Ixelqen tamurt, igenwan, akked wayen illan garasen, di sdis wussan; sinna, Iâannec $ef usgeld. Ur tesâim, war Neppa, bab, ne$ amcafaâ. Day ula d tesmektayem ara?');
INSERT INTO "ramdane" VALUES(32,5,'32:5','A d Iseyyi lameô seg igenni ar tmurt; sinna, ad iu$al $uôeS deg ibwass, lmudda s: agim iseggwasen, s leêsab nnwen.');
INSERT INTO "ramdane" VALUES(32,6,'32:6','Akka, Umusnaw n wayen iffren, u n wayen ur neffir, Uzmir Ipêunun.');
INSERT INTO "ramdane" VALUES(32,7,'32:7','I Iûûegmen akw ayen Ixleq. Inza d lxelq n umdan, si talaxt.');
INSERT INTO "ramdane" VALUES(32,8,'32:8','Sinna, Issuffe$ ed tarwa s si l$ella, degs cwiî n waman.');
INSERT INTO "ramdane" VALUES(32,9,'32:9','Sinna, Imesl it, Iûûuv degs, si ôôuê iS. Irra yawen timesliwt, iéôi, ul. Drus ay tesnemmirem.');
INSERT INTO "ramdane" VALUES(32,10,'32:10','Nnan: "ma nweddeô di tmurt, day a d nepwaxleq tikelt nniven"? Xaîi, nekôen timlilit akked Mass nnsen.');
INSERT INTO "ramdane" VALUES(32,11,'32:11','Ini: "ad iddem ôôuê nnwen, lmelk n tmeppant i ippukelfen fellawen. Sinna, ar Mass nnwen a ppu$alem".');
INSERT INTO "ramdane" VALUES(32,12,'32:12','Lemmer a pwaliv ibe$wviyen, ad brun i iqweôôa nnsen, zdat Mass nnsen: "a Mass nne$! Nwala, nesla; Err ane$ a nexdem ayen ilhan. S tidep, numen".');
INSERT INTO "ramdane" VALUES(32,13,'32:13','Lemmer i £ ihwi, yili Nefka, i yal ôôuê, nnhaya s. Maca, ad ivôu wayen Nni$: "war ccekk, ad Acaôe$ Loahennama s leonun akked yimdanen, mejmulen".');
INSERT INTO "ramdane" VALUES(32,14,'32:14','Ihi, jeôôbet imi teppum a d temlilem d wass nnwen agi! Nekwni, ih, Neppu kwen. Jeôôbet aâaqeb, n lebda, ilmend n wayen txeddmem.');
INSERT INTO "ramdane" VALUES(32,15,'32:15','I iumnen kan, s issekniyen nne£, d wid, mara ten id mmektin, ara weddin ad knun, ara isbucuâen Mass nnsen s ccekôan, nitni ur pzuxxun.');
INSERT INTO "ramdane" VALUES(32,16,'32:16','Ppensaren d ibeôdiyen nnsen, seg imîeôêen, pnujun $eô Mass nnsen, s tugwdi d usirem. Seg wayen iten N$at, pseddiqen.');
INSERT INTO "ramdane" VALUES(32,17,'32:17','Ur iéôi yiwen acu i sen Ffre$ n lfeôê. D arraz n wayen llan xeddmen.');
INSERT INTO "ramdane" VALUES(32,18,'32:18','Win illan d lmumen, day am win illan d amed$ul? Ur aâdilen.');
INSERT INTO "ramdane" VALUES(32,19,'32:19','Ihi, i wid iumnen, ixeddmen ayen ilhan, leonanat imôaêbiyen, ara zed$en, ilmend n wayen xeddmeùn.');
INSERT INTO "ramdane" VALUES(32,20,'32:20','Wanag wid ide$wlen, di tmes ara zed$en. Yal tikelt ideg ara b$un a d ff$en segs, a ten rren $uôes, ad asen inin: "jeôôbet aâaqeb n tmes i tnekkôem".');
INSERT INTO "ramdane" VALUES(32,21,'32:21','Ih, a Nessukk fellasen aâaqeb aqôib mavi, war aâaqeb abarar. Ahat a d u$alen s abrid.');
INSERT INTO "ramdane" VALUES(32,22,'32:22','Anwa i idensen sennig n win iwumi d dekôen issekniyen n Mass is, iu$al iwexxeô asen? Ih, a d Nerr ppaô seg ibe$wviyen.');
INSERT INTO "ramdane" VALUES(32,23,'32:23','Ni$ Nefka yas Tazmamt i Musa. Ur ppili di ccekk af temlilit yideS! Nerra p d nnhaya i At Isôail.');
INSERT INTO "ramdane" VALUES(32,24,'32:24','Nesbedd, segsen, iselmaden; nnehun s lameô nne£, mi ûebôen, u llan pputebbten $ef issekniyen nne£.');
INSERT INTO "ramdane" VALUES(32,25,'32:25','Ih, Mass ik, Neppa, ad Iêudd garasen, ass n tnekra, $ef wayen i$ef mgarraden.');
INSERT INTO "ramdane" VALUES(32,26,'32:26','Day ur ten inha ara wayen Nesqucc isuta, uqbel nnsen? La leêêun $ef tnezdu$in nnsen. Deg waya, s tidep, issekniyen. Day ur sellen ara?');
INSERT INTO "ramdane" VALUES(32,27,'32:27','Day ur zôin ara Neppawi aman s akal aquôan; Nessuffu$ ed, yissen, iâaqqayen; teppen, segsen, nitni akked lmal nnsen. Day ur pwalin ara?');
INSERT INTO "ramdane" VALUES(32,28,'32:28','Qqaôen: "melmi l$elb agi, ma tessawalem tidep"?');
INSERT INTO "ramdane" VALUES(32,29,'32:29','Ini: "ass n l$elb, liman n wid ijehlen, ur ten infaâ; ur pganin".');
INSERT INTO "ramdane" VALUES(32,30,'32:30','Ihi, wexxeô asen, ggani. Nitni, s tidep, la pganin.');
INSERT INTO "ramdane" VALUES(33,1,'33:1','A Nnbi! Ëezzeb i Öebbi, ur ppa$ awal i ijehliyen akked ixabiten. Öebbi, s tidep, Izga d Amusnaw, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(33,2,'33:2','Ddu akked wayen i k d Ippuweêêan, s$uô Mass ik. Ih, Öebbi Ippuxebbeô $ef wayen txedmem.');
INSERT INTO "ramdane" VALUES(33,3,'33:3','Ppkel $ef Öebbi. Iqadd Öebbi d Awkil.');
INSERT INTO "ramdane" VALUES(33,4,'33:4','Ur Irra ara Öebbi sin wulawen di tfekka n urgaz. Ur Irra ara tilawin nnwen, i tmetlem ar iârar n tyimmatin nnwen, d tiyimmatin nnwen. Ur irra ara wid id tessekrem, d tarwa nnwen. Ayagi, d awal nnwen, seg imawen nnwen. Öebbi Issawal tidep. Neppa Ippawi d ubrid.');
INSERT INTO "ramdane" VALUES(33,5,'33:5','Ssiwlet asen am ibabaten nnsen; akka i iwqem ar Öebbi. Ma ur tessinen ara anwi ay d ibabaten nnsen, atmaten nnwen di ddin, d iqôiben nnwen. Ur illi u$ilif $ef wayen teffalîam degs, siwa ayen âammden wulawen nnwen. Öebbi Izga Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(33,6,'33:6','Nnbi iqôeb ar lmumnin, sennig iman nnsen. Tilawin is, d tiyimmatin nnsen. Wid ippemyilin, mqaôaben di Tezmamt n Öebbi, sennig lmumnin akked iminigen. Siwa ma tgam tajmilt i imeddukwal nnwen. Ayagi iura di Tezmamt.');
INSERT INTO "ramdane" VALUES(33,7,'33:7','Mi Neîîef si lambiyya lmitaq nnsen, u si keçç, si Nuê, Ibôahim, Musa, u si Âisa, mmi s n Meryem, Neîîef segsen lmitaq lfayeq,');
INSERT INTO "ramdane" VALUES(33,8,'33:8','iwakken ad Iêtec imawlan n tidep, af tidep nnsen. Ihegga d, i ijehliyen, aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(33,9,'33:9','A wid iumnen! Mmektit ed l$it ikwen I$at Öebbi. Mi d usan $uôwen iâsekôiwen, Nuzen asen d avu, akked iâsekôiwen ur twalam. Öebbi Izga Ipwali ayen txeddmem.');
INSERT INTO "ramdane" VALUES(33,10,'33:10','Mi wen d kkan sennig wen, seddawat wen, mi zzint wallen, ulawen bbwven s ijuxax, tepmeyyizem yal ameyyez $ef Öebbi.');
INSERT INTO "ramdane" VALUES(33,11,'33:11','Dinna, ppwajeôôben lmumnin, enhezzen s leqseê.');
INSERT INTO "ramdane" VALUES(33,12,'33:12','Mi d nnan ixabiten, akked imehzulen n wul: "ay $ d Iwaââed Öebbi, akked Umazan iS, siwa a$uôôu".');
INSERT INTO "ramdane" VALUES(33,13,'33:13','Mi d inna urbaâ segsen: "a yAt Yatrib, ur illi umekkan i kunwi, u$alet"! Kra segsen ssutren tsriê i Nnbi, nnan: "ih, ixxamen nne$ aâran". Nitni, ur llin aâran, b$an kan ad rewlen.');
INSERT INTO "ramdane" VALUES(33,14,'33:14','Ma kecmen ten id, si leriaf is, sinna, ssutren asen ad xubten, ad xubten. Cwiî kan ara kukrun fellas.');
INSERT INTO "ramdane" VALUES(33,15,'33:15','Ni$ llan âuhden s Öebbi, uqbel, ar ur ppu$alen ar deffir. Aâahed s Öebbi ad ippuqeîîi!');
INSERT INTO "ramdane" VALUES(33,16,'33:16','Ini: "ur kwen tenfaâ trewla, ma trewlem si tmeppant, ne$ si îôad. Ara tezhum, siwa tallit wezzilen".');
INSERT INTO "ramdane" VALUES(33,17,'33:17','Ini: "anwa wa, ara kwen ikksen seg ufus n Öebbi, ma Ib$a yawen cceôô, ne$ Ib$a yawen aêunu"? Ur asen ppafen, war Öebbi, bab, ne$ asettaô.');
INSERT INTO "ramdane" VALUES(33,18,'33:18','Ni$ Öebbi Iéôa wid degwen, i ipâawwiqen, wid iqqaôen i watmaten nnsen: "Yyat $uône$"! Drus kan degsen, i ikecmen îôad.');
INSERT INTO "ramdane" VALUES(33,19,'33:19','D imceêêiyen ar loiha nnwen. Ma tusa d tugdi, a ten twaliv la k pmuqulen, tezzint wallen nnsen, am win tessexciwit tmeppant. Ma tekkes tugdi, a kwen gezren s yilsawen uqviâen. D imceêêiyen $ef lxiô. Wigi, ur uminen ara. Ihi, Imêa Öebbi igiten nnsen. Ayagi, d ayen isehlen i Öebbi.');
INSERT INTO "ramdane" VALUES(33,20,'33:20','£ilen ikabaren ur ôuêen ara. Lemmer a d asen ikabaren, ad ssirmen, ar nitni, di ûûeêôa ay llan, ar Waâôaben n ucluê, steqsayen fellawen. Lemmer llan yidwen, cwiî kan ara nna$en.');
INSERT INTO "ramdane" VALUES(33,21,'33:21','Ni$, i kunwi, Amazan n Öebbi d amedya amxallef i win ippaôoun Öebbi, akked was aneggaru, id ismektayen aîas Öebbi.');
INSERT INTO "ramdane" VALUES(33,22,'33:22','Mi walan lmumnin ikabaren, nnan: "ayagi d ayen i $ d Iwaââed Öebbi, akked Umazan iS. Öebbi, akked Umazan iS, Ssawlen tidep". Ay sen irna kan, siwa liman akked wannuz.');
INSERT INTO "ramdane" VALUES(33,23,'33:23','Ger lmumnin, llan irgazen i iîîfen deg wayen i$ef âuhden Öebbi. Garasen, wid iêellen timeôoiwt nnsen, garasen wid ipganin. Ur bedlen mavi.');
INSERT INTO "ramdane" VALUES(33,24,'33:24','Iwakken ad Ikafi Öebbi imawlan n tidep, $ef tidep nnsen, u ad Iâaqeb ixabiten, ma ihwa yaS, ne$ ad asen Issuref. Ih, Öebbi Izga Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(33,25,'33:25','Iééaâ Öebbi wid ijehlen, s iêecciô nnsen. Ur ppaddamen lxiô. Issuli Öebbi îôad i lmumnin. Öebbi Izga d Uohid, d Uzmir.');
INSERT INTO "ramdane" VALUES(33,26,'33:26','Issers ed, si lebôuj nnsen, wid i sen ifkan afus, seg wAt Tezmamt. Issekcem tugdi deg ulawen nnsen. Arbaâ ten$am ten, arbaâ têebsem ten.');
INSERT INTO "ramdane" VALUES(33,27,'33:27','Irra kwen tweôtem akal nnsen, ixxamen nnsen, ayla nnsen. D akal ur taâfisem. Ih, Öebbi Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(33,28,'33:28','A Nnbi! Ini i tlawin ik: "maday teb$amt tameddurt n ddunit, akked ccbuê is, yyamt a kwent $ite$, ad a kwent bru$, berru umâin.');
INSERT INTO "ramdane" VALUES(33,29,'33:29','Ma illa teb$amt Öebbi, Amazan iS, akked uxxam aneggaru, ihi, Ihegga d Öebbi, i tâaôviyin, segkwent, arraz ameqwôaêan".');
INSERT INTO "ramdane" VALUES(33,30,'33:30','A tilawin n Nnbi! Tid segkwent ara iawin abekkav ufviê, a ppeppuâaqeb berdayen. Ayagi d ayen isehlen i Öebbi.');
INSERT INTO "ramdane" VALUES(33,31,'33:31','Tin segkwent i iunzen i Öebbi, akked Umazan iS, u txeddem ayen ilhan, ad as Nefk arraz berdayen, u Nhegga yas d l$it ukyis.');
INSERT INTO "ramdane" VALUES(33,32,'33:32','A tilawin n Nnbi! Ur tellimt am tlawin nniven. Ma têezzbemt, ur teppeccvemt deg wawal, a kwent isirem umuvin n wul. Inimt awal uwqim.');
INSERT INTO "ramdane" VALUES(33,33,'33:33','Ilimt deg ixxamen nnkwent. Ur pcebbiêemt acebbeê n at war tussna imenza. èÈéalemt, zekkimt, ôavimt Öebbi akked Umazan iS. Ib$a kan Öebbi a kwent ipwakkes nnkel, a tawacult n Uxxam. U, a kwent Izzizdeg nezzeh.');
INSERT INTO "ramdane" VALUES(33,34,'33:34','Mmektitemt ed ayen i kwent $ôan, deg ixxamen nnkwent, n issekniyen n Öebbi, akw n ûûwab. Ih, Öebbi Izga d Laîif, Ippuxebbeô.');
INSERT INTO "ramdane" VALUES(33,35,'33:35','Ih, imvuâen, timvuâin, lmumnin argaz tameîîut, itubiyen, titubiyin, wid akked tid issawalen tidep, iûebôiyen, tiûebôiyin, wid akked tid ippagwaden, wid akked tid ipseddiqen, wid akked tid ippuéumen, wid akked tid ipêaraben $ef tuzzuft nnsen, wid akked tid id immektayen aîas Öebbi; i wigi, Ihegga d Öebbi ssmaê akked warraz abarar.');
INSERT INTO "ramdane" VALUES(33,36,'33:36','Ur issefk ara i win, ne$ tin iumnen, ad smenyafen lameô nnsen, ma Iseyya d lameô Öebbi, akked Umazan iS. Win inuqmen Öebbi, akked Umazan iS, ni$ innetlaf ttelfan n ûûeê.');
INSERT INTO "ramdane" VALUES(33,37,'33:37','Mi tenniv i win I$at Öebbi, u t$atev: "jmaâ $uôek, tameîîut ik, êezzeb i Öebbi"; teffrev deg iman ik, ayen ara d Isban Öebbi. Tugadev medden. D Öebbi, i iwatan a T tagwadev. Mi s ibra Zayed, Njewo ik yides, iwakken ur teppili lhejna i lmumnin, ma u$en tilawin n wid id ssekren, mara ad sent brun. Ad iûaô lameô n Öebbi.');
INSERT INTO "ramdane" VALUES(33,38,'33:38','Ur telli lhejna f Nnbi, deg wayen Iêeppem fellas Öebbi; d tannumi n Öebbi $ef imezwura. Lameô n Öebbi izga ipwafta.');
INSERT INTO "ramdane" VALUES(33,39,'33:39','Wid issawaven tibôatin n Öebbi, i T ippagwaden, ur ppagwaden yiwen, siwa Öebbi. Öebbi Iqadd i uqeîîi.');
INSERT INTO "ramdane" VALUES(33,40,'33:40','Ur illi Muêemmed d ababat n yiwen seg irgazen nnwen, maca, d Amazan n Öebbi, u d aneggaru n lambiyya. Öebbi Izga Issen yal cci.');
INSERT INTO "ramdane" VALUES(33,41,'33:41','A wid iumnen! Mmektit ed Öebbi, maççi d izli.');
INSERT INTO "ramdane" VALUES(33,42,'33:42','Sbuccuâet eP, ûûbeê, tameddit.');
INSERT INTO "ramdane" VALUES(33,43,'33:43','D Neppa i d Inuben fellawen, akked lmalayek, iwakken a kwen Issuffe$ si tillas ar tafat. Izga Ipêunu $ef lmumnin.');
INSERT INTO "ramdane" VALUES(33,44,'33:44','Asmi ara T magren, azul nnsen d: "tifrat"! Ihegga yasen d arraz ukyis.');
INSERT INTO "ramdane" VALUES(33,45,'33:45','A Nnbi! Nuzen ik id d anagi, d abcir u d aneddaô,');
INSERT INTO "ramdane" VALUES(33,46,'33:46','i ineccden ar Öebbi, s tsriê iS; d asavef icaâlen.');
INSERT INTO "ramdane" VALUES(33,47,'33:47','Becceô lmumnin s lfevl ameqwôan i nitni, s$uô Öebbi.');
INSERT INTO "ramdane" VALUES(33,48,'33:48','Ur ppa$ awal i ijehliyen akked ixabiten. Ur ssemêas, i ccum nnsen. Ppkel $ef Öebbi, Iqadd Öebbi d Awkil.');
INSERT INTO "ramdane" VALUES(33,49,'33:49','A wid iumnen! Ma tu$em tid iumnen, sinna, tebram asent, uqbel ma tennulem tent, ur fellawen ad asent têesbem tallit. £itet pent, serrêet asent, s wudem uwqim.');
INSERT INTO "ramdane" VALUES(33,50,'33:50','A Nnbi! Nêell ak tilawin iwumi tefkiv tiqviâen nnsent, tiklatin ik, si tid i k Iqsed Öebbi, iss is n âam ik, tid n tâamtin ik, tid n xal ik, tid n txaltin ik iunagen yidek, tameîîut iumnen, ma tefka iman is i Nnbi, ma ib$a Nnbi a p ya$. D lemziyya i keçç, war lmumnin. Ni$ Neéôa acu i sen Nseyya af tlawin nnsen, akked teklatin nnsen. Iwakken ur teppili fellak lhejna. Öebbi Izga Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(33,51,'33:51','A ppesganiv tin i k ihwan segsent. A d tawiv $uôek tin i k ihwan, ne$ tin teb$iv si tid tâazlev. Ur fellak tuzzma. Akka i iwulem, iwakken ad qenâant, ur $webbnent, ad ôavint akw, ayen i sent tefkiv. Öebbi Iéôa i illan deg ulawen nnwen. Öebbi Izga d Amusnaw, Iêellem.');
INSERT INTO "ramdane" VALUES(33,52,'33:52','Ur ak êellent tlawin, sya d asawen, ne$ abeddel n tlawin, xas tâaoeb ik ûûifa nnsent, siwa tiklatin ik. Öebbi Izga Issikid yal cci.');
INSERT INTO "ramdane" VALUES(33,53,'33:53','A wid iumnen! Ur keççmet ar texxamin n Nnbi, ar awen iâammed, d inebgawen ar wuççi, ur teggunim. Maca, ma tepwâaôvem, kecmet. Mi teççam, sahlet; ur ssiôxiset ameslay. Ayagi, s tidep, iqelleq Nnbi. Isetêa segwen. Öebbi ur Ipsetêi $ef tidep. Ma tessutrem asent ta$awsa, ssutret ep deffir tsabert. Yif it wakka, i tezdeg n wulawen nnwen, akked wulawen nnsent. Ur awen ilaq ara a pjjerim Amazan n Öebbi, ne$ a ppa$em tilawin is, deffir es, d awez$i. Ayagi, ad yili, ar Öebbi, d lefviêa.');
INSERT INTO "ramdane" VALUES(33,54,'33:54','Ama tessknem d lêaoa, ne$ teffrem p, Öebbi, s tidep, Iéôa yal cci.');
INSERT INTO "ramdane" VALUES(33,55,'33:55','Ur telli tiyta degsent af ibabaten nnsent, arraw nnsent, atmaten nnsent, arraw n wamaten nnsent, arraw n tsetmatin nnsent, tilawin nnsent, ticrikin nnsent. Ad êezzbent i Öebbi. Öebbi, s tidep, d Anagi n yal cci.');
INSERT INTO "ramdane" VALUES(33,56,'33:56','Öebbi akked lmalayek iS, s tidep, Nuban $ef Nnbi. A wid iumnen! Nubet fellas, ternum as azul aêmayan.');
INSERT INTO "ramdane" VALUES(33,57,'33:57','Ih, wid ijjerin Öebbi akked Umazan iS, Ixza ten Öebbi, di ddunit, di laxeôt. Ihegga yasen d aâaqeb isdullun.');
INSERT INTO "ramdane" VALUES(33,58,'33:58','Wid ijjerin lmumnin, argaz tameîîut, $ef wayen ur ksiben, ni$ bbwin acehrir akked ubekkav n ûûeê.');
INSERT INTO "ramdane" VALUES(33,59,'33:59','A Nnbi! Ini i tlawin ik, i iss ik, i tlawin n lmumnin, ad jemâant ijufaô nnsent. Ppif akka, a tent âaqlen, ur tent pooerin. Öebbi Izga Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(33,60,'33:60','Ma ur êbisen ara ixabiten, imehzulen n wul, akked wid ipxeôôiven di Lmadina, ad ak Nebges $uôsen. Sinna, siwa cwiî n tallit ara yilin, degs, d loiran ik.');
INSERT INTO "ramdane" VALUES(33,61,'33:61','Ad pwaxzun anda ten nufan, ad pwaîîfen, ad pwan$en akw.');
INSERT INTO "ramdane" VALUES(33,62,'33:62','D tannumi n Öebbi ar wid iâaddan zik. Ur teppafev, d awez$i, abeddel, di tannumi n Öebbi.');
INSERT INTO "ramdane" VALUES(33,63,'33:63','Steqsan k imdanen, af yimir. Ini: "it Issnen siwa Öebbi". Anwa ad ak imlen, amar imir iqeôb ed?');
INSERT INTO "ramdane" VALUES(33,64,'33:64','Öebbi, s tidep, Ixza ijehliyen, Ihegga yasen d u$ud.');
INSERT INTO "ramdane" VALUES(33,65,'33:65','Ad wekkôen degs, i lebda; ur ppafen bab, ne$ asettaô.');
INSERT INTO "ramdane" VALUES(33,66,'33:66','Ass ideg ara zzin wudmawen nnsen, ar tmes, ad inin: "a yamxix nne$! A lemmer nunez i Öebbi, u nunez i Umazan!');
INSERT INTO "ramdane" VALUES(33,67,'33:67','Ad inin: "a Mass nne$! Nekwni nunez i igeldan nne$, i imeqwôanen nne$, telfen a$ ar yir abrid.');
INSERT INTO "ramdane" VALUES(33,68,'33:68','A Mass nne$! Aâqeb iten berdayen, u Xzu ten s leqseê"!');
INSERT INTO "ramdane" VALUES(33,69,'33:69','A wid iumnen! Ur ppilit am wid i ijjerin Musa. Izekka t Öebbi, seg wayen id nnan. Illa ar Öebbi d amxallef.');
INSERT INTO "ramdane" VALUES(33,70,'33:70','A wid iumnen! Ëezzbet i Öebbi, tinim awal umâin.');
INSERT INTO "ramdane" VALUES(33,71,'33:71','Ad awen Iûeggem igiten nnwen, ad Isemmeê ibekkaven nnwen. Win iôuvan Öebbi akked Umazan iS, ni$ ibbwi arraz ameqwôaêan.');
INSERT INTO "ramdane" VALUES(33,72,'33:72','Naâôev a nefk lamana i igenwan, i tmurt, i idurar. Ugin a p îîfen. Ugaden p. Ibubb ip umdan. Neppa ard amednus ur nessin.');
INSERT INTO "ramdane" VALUES(33,73,'33:73','Ihi, ad Isâaddi Öebbi aâaqeb af ixabiten akked txabitin, wid akked tid ipcaraken. Ad Issuref Öebbi i wid akked tid iumnen. Öebbi Izga Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(34,1,'34:1','Ccekôan i Öebbi, i iIan i illan deg igenwan, i illan di tmurt. I Neppa, ccekôan di laxert. Neppa, d Bab n ûûwab, Ippuxebbeô.');
INSERT INTO "ramdane" VALUES(34,2,'34:2','Iéôa ayen ikeççmen di tmurt, ayen id iteff$en segs, ayen id iprusun seg igenni, ayen ippalin $uôes. Neppa Ipêunu, Ipsemmiê.');
INSERT INTO "ramdane" VALUES(34,3,'34:3','Nnan wid ijehlen: "ur a$ d ippas yimir". Ini: "xaîi, s Mass iw, ara kwen id ias"! Amusnaw n tbavnit, ur idrig fellaS ubruy ubelkim, ama deg igenwan, ama di ddunit; ama seddaw wannect is, ama sennig es; akw, di Tezmamt ipbegginen.');
INSERT INTO "ramdane" VALUES(34,4,'34:4','Ad Ikafi wid iumnen, ixedmen ayen ilhan. I wigi, ssmaê akked ôôeéq ukyis.');
INSERT INTO "ramdane" VALUES(34,5,'34:5','Wid ikkaten deg issekniyen nne£, sfunnugen, widak, fellasen aâaqeb n twa$it taqeôêant.');
INSERT INTO "ramdane" VALUES(34,6,'34:6','Wid iwumi teppunefk tmusni, walan ar ayen id irsen $uôek, s$uô Mass ik, d tidep. Ippawi s abrid n Uzmir, Amaâzuz.');
INSERT INTO "ramdane" VALUES(34,7,'34:7','Nnan wid ijehlen: "day ad awen nessken argaz, ar awen d yinin ar ma teftutsem, yal aftutes, i kunwi lxelq amaynut?');
INSERT INTO "ramdane" VALUES(34,8,'34:8','Iger ed tikerkas $ef Öebbi, ne$ d aôehbani"? Xaîi, wid ur numin ara s laxeôt, haaten deg uâaqeb, akked ttelfan u$miq.');
INSERT INTO "ramdane" VALUES(34,9,'34:9','Day ur walan ara ayen illan zdat sen, u deffir sen, di tmurt akked igenwan? Ma ihwa ya£, a ten Nger seddaw tmurt, ne$ a d Nesse$li fellasen igezzim seg igenni. Deg wayagi, ih, d assekni i yal amdan ucâif.');
INSERT INTO "ramdane" VALUES(34,10,'34:10','Ni$ Nefka s$uône£ lfevl i Dawed. A yidurar, alset yides akked ifrax. Nerra yas uzzal d aleqqaq.');
INSERT INTO "ramdane" VALUES(34,11,'34:11','Eûnaâ tibedâiyin n wuzzal, qiss tindar. Xeddmet ayen ilhan. Nek, Pwali$ ayen txeddmem.');
INSERT INTO "ramdane" VALUES(34,12,'34:12','I Sliman: avu. Win n ûûbeê, aggur; win n tmeddit, aggur. Nelli yas taâwint n nnêas. Kra n leonun ad xedmen fellas, s tsriê n Mass is. Win degsen, iveôfen i lameô nne£, Nessukk fellas, aâaqeb ujajiê.');
INSERT INTO "ramdane" VALUES(34,13,'34:13','Xeddmen as ayen ib$a: leowamaâ, tiâancuôin, loefnat am iêvunen, ticcuytin iqaâden. Exdem s tnemmirt, a tawacult n Dawed, drus n yimdanen iW i isnemmiren!');
INSERT INTO "ramdane" VALUES(34,14,'34:14','Mi Nseyya fellas tameppant, ur as faqen ara immut, armi içça taâkwazt is, ubaââuc n wakal. Mi i$li, êulfan leonun ar, lemmer day ssinen tabavnit, ur qqimen deg uâaqeb isdullun.');
INSERT INTO "ramdane" VALUES(34,15,'34:15','Ni$, i Saba, assekni di tnezdu$in nnsen. Snat tebêirin, ta yeffus, ta zelmev. Eççet si l$it n Mass nnwen, u snemmret aS. Tamurt ilhan, Mass Ipsemmiêen.');
INSERT INTO "ramdane" VALUES(34,16,'34:16','Wexxôen. Nuzen asen aêemmal n Âarim. Tibêirin nnsen, nerra tent d tibêirin id ippaken l$ella ôéagen, tammemayt, akked tzegwart taxeîîayt.');
INSERT INTO "ramdane" VALUES(34,17,'34:17','Akka ay ten Nqeîîi $ef tijehli nnsen. Day a Nqeîîi wur nejhil?');
INSERT INTO "ramdane" VALUES(34,18,'34:18','Nerra garasen u ger temdinin i$ef Nburek, timdinin id ivehôen. Nektal tikli garasent: "merêet degsent di laman, am yiv, am ass"!');
INSERT INTO "ramdane" VALUES(34,19,'34:19','Nnan: "a Mass nne$! Zzi$wzef tallit ger zziaôat nne$"! Densen iman nnsen. Nerra ten d tiqûivin. Nesqucc iten wakali. Ih, deg wayagi, war ccek, d issekniyen i yal aûebôi isnemmiren.');
INSERT INTO "ramdane" VALUES(34,20,'34:20','Ni$ Iblis itebbet ed, segsen, ameyyez is. Vefôen t, siwa arbaâ si lmumnin.');
INSERT INTO "ramdane" VALUES(34,21,'34:21','Ur isâi kra udabu fellasen. Iwakken kan, a Nissin anwa iumnen s laxeôt, u anwa i illan, fellas, di ccekk. Mass ik Ipêarab af yal cci.');
INSERT INTO "ramdane" VALUES(34,22,'34:22','Ini: "naâret wid i tepkabarem, war Öebbi. Ur sâin taéeyt ubelkim, deg igenwan ne$ di tmurt; ur criken degsen; ur aS illi, garasen, umâiwen".');
INSERT INTO "ramdane" VALUES(34,23,'34:23','Cafuâa, s$uôeS, tenfaâ kan win iwumi ip Irad. Alamma, ma tekkes tugdi seg wulawen nnsen, a d inin: "acu id Inna, Mass nnwen"? A d inin: "d tidep, u Neppa d Aâlayen, d Ameqwôan".');
INSERT INTO "ramdane" VALUES(34,24,'34:24','Ini: "anwa ikwen ip$aten, si tmurt akked igenwan"? Ini: "d Öebbi". Nekwni am kunwi, amendeô ma nella di nnhaya, ne$ di ttelfan n ûûeê.');
INSERT INTO "ramdane" VALUES(34,25,'34:25','Ini: "ur teppuqeîîim $ef wayen nfalîa, ur neppuqeîîi $ef wayen txeddmem".');
INSERT INTO "ramdane" VALUES(34,26,'34:26','Ini: "ad a$ Issemlil Mass nne$, sinna, ad Iêudd garane$, s tidep. Neppa d Amêaddi Amusnaw".');
INSERT INTO "ramdane" VALUES(34,27,'34:27','Ini: "sseknet iyi d wid i tqeônem $uôeS, d icriken"! Xaîi, Neppa d Öebbi Uzmir, Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(34,28,'34:28','Ur k id Nuzen, i imdanen, armi d uqniâ; a pbecôôev, a pnedôev. Maca, tegwti n yimdanen, ur ssinen.');
INSERT INTO "ramdane" VALUES(34,29,'34:29','Qqaôen: "melmi lemâahda agi, ma tessawalem tidep"?');
INSERT INTO "ramdane" VALUES(34,30,'34:30','Ini: "$uôwen ppaâud ass, ur tezmirem a t teswexxôem, ne$ a t id tesqeôbem, s saâa".');
INSERT INTO "ramdane" VALUES(34,31,'34:31','Nnan wid ijehlen: "ur neppamen, d awez$i, s Leqwôan agi, ne$ s wayen it id izwaren". Lemmer a pwaliv mara bedden imednas, ar Mass nnsen, pmeslayen garasen. Ad inin uôhifen i imzuxiyen: "lemmer maççi d kunwi, yili nella d lmumnin".');
INSERT INTO "ramdane" VALUES(34,32,'34:32','Ad inin imzuxiyen i uôhifen: "day d nekwni ikwen isfeô$en af nnhaya, mi kwen id iusa? Xaîi, tellam d ibe$wviyen".');
INSERT INTO "ramdane" VALUES(34,33,'34:33','Ad inin uôhifen, i imzuxiyen: "xaîi! Nncub, iv akked wass, mi têeppmem, fellane$, a nagwi Öebbi, u ad aS negg tanudda". Ffren nndama mi walan aâaqeb. Nerra tiqwlavin $ef imegôav n wid ijehlen. Day ara ppuqeîîin $ef wayen ur xdimen?');
INSERT INTO "ramdane" VALUES(34,34,'34:34','Ur Nuzen ara kra uneddaô ar temdint, mebla ma nnan imeôkantiyen is: "nekwni, ih, nugwi ayen is id teppuceggâam".');
INSERT INTO "ramdane" VALUES(34,35,'34:35','Nnan: "nekwni, n$elb ik ayla, tarwa. Ur nelli d wid ara ippuqeîîin".');
INSERT INTO "ramdane" VALUES(34,36,'34:36','Ini: "ihi Mass iw Ip$iti win i S ihwan u Iwezzen; maca, tegwti n imdanen, ur ssinen".');
INSERT INTO "ramdane" VALUES(34,37,'34:37','Maççi d ayla nnwen, ne$ d tarwa nnwen, ara kwen id iééiéen nezzeh $uône£, siwa wid iumnen, ixedmen ayen ilhan. I nitni arraz berdayen, ilmend n wayen xedmen. Ad ilin, di laman, di t$uôfatin.');
INSERT INTO "ramdane" VALUES(34,38,'34:38','Ma d wid ikkaten deg issekniyen nne£, sfunnugen, widak a d rsen i uâaqeb.');
INSERT INTO "ramdane" VALUES(34,39,'34:39','Ini: "ih, Mass iw Ip$iti win i S ihwan, deg imdanen iS. Iwezzen as. Ayen akw ara tefkem n cci, ad awen t id Irr. Neppa, Yif akw im$iten".');
INSERT INTO "ramdane" VALUES(34,40,'34:40','Ass ideg ara ten Ifser, akken ma llan, imiren ad Yini i lmalayek: "wigi, day llan âabden kwent"?');
INSERT INTO "ramdane" VALUES(34,41,'34:41','A d inint: "gedha s yiseK! D Keçç ay d Bab nne$, war nitni. Xaîi, llan âabden leonun. Tegwti degsen umnen yissen".');
INSERT INTO "ramdane" VALUES(34,42,'34:42','Assa, ur imlik yiwen i wayev, segwen, aba$uô ne$ ccqa. A Nini i wid idensen: "jeôôbet aâaqeb n tmes, i tellam tnekôem t"!');
INSERT INTO "ramdane" VALUES(34,43,'34:43','Ma $ôan asen issekniyen nne£ ipbegginen, ad inin: "wagi d argaz kan, i ib$an a kwen istixeô, seg wayen llan âabden imezwura nnwen". Ad inin: "ayagi, siwa d tikerkas id innulfan". Wid ijehlen i tidep, mi ten id tusa, ad inin: "ayagi d ssêur n ûûeê kan"!');
INSERT INTO "ramdane" VALUES(34,44,'34:44','Ur asen Nefki tizmamin, ara lemden; ur asen d Nuzen, uqbel ik, aneddaô.');
INSERT INTO "ramdane" VALUES(34,45,'34:45','Skaddben wid i ten id izwaren. Ur asen tebbwiv ara tis mraw n wayen i ippunefken mi skaddben imazanen iW. A tuzzma W, imiren!');
INSERT INTO "ramdane" VALUES(34,46,'34:46','Ini: "ar yiwet kan, ara kwen nabi$: a pbeddem zdat Öebbi, di sin, ne$ weêêedwen; sinna, a d tesmektim. Ameddakwel nnwen ur illi d aôehbani. Neppa d aneddaô nnwen kan, uqbel aâaqeb uqsiê".');
INSERT INTO "ramdane" VALUES(34,47,'34:47','Ini: "ur awen ssutre$ kra n teqdiât; apan nnwen. Ih, taqdiât iw, ar Öebbi kan. Neppa, $ef yal cci, d Anagi".');
INSERT INTO "ramdane" VALUES(34,48,'34:48','Ini: "ih, Mass iw Ipqeddim tidep, d Amusnaw n tbavnit".');
INSERT INTO "ramdane" VALUES(34,49,'34:49','Ini: "tusa d tidep, ur d nezzunt tkerkas, ur d ppalsent".');
INSERT INTO "ramdane" VALUES(34,50,'34:50','Ini: "ma nnetlafe$, nnetlafe$ kan i iman iw. Ma pwanha$, d ayen iyi d Iweêêa Mass iw. Neppa, ih, d Aqôib, Isell".');
INSERT INTO "ramdane" VALUES(34,51,'34:51','Lemmer a pwaliv mi xelâan, tarewla ur telli. Pwaîîfen, si tama iqeôben.');
INSERT INTO "ramdane" VALUES(34,52,'34:52','Nnan: "numen yis". Amek ara d ssiwven, si tama ibaâden?');
INSERT INTO "ramdane" VALUES(34,53,'34:53','Ni$ llan zik, nekôen t. Rran akin tabavnit, si tama ibaâden.');
INSERT INTO "ramdane" VALUES(34,54,'34:54','Ad yili ufûil, garasen d wayen ctaqen, akken tevôa, zik, d wid i ten icban. Ih, nitni llan di ccekk imcebbwel.');
INSERT INTO "ramdane" VALUES(35,1,'35:1','Ccekôan i Öebbi, Axellaq n tmurt akked igenwan, i Irran lmalayek d timersulin sut sin, krav, akked kué iferrawen. Irennu, di lxelq, ayen i S ihwan. Öebbi, s tidep, Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(35,2,'35:2','Ayen Ifka Öebbi uêunu, i medden, ur t itekkes yiwen. Ayen Iîîef, ur izmir yiwen a t id iazen, deffir eS. Neppa d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(35,3,'35:3','A yimdanen! Mmektit ed a$iti nnwen s$uô Öebbi. Day illa uxellaq, war Öebbi, ikwen iôeéqen seg igenni akked tmurt? Ur illi ôebbi siwa Neppa, ihi acimi tessaâwajem?');
INSERT INTO "ramdane" VALUES(35,4,'35:4','Ma nekôen k, ni$ pwanekôen imazanen, uqbel ik. Ar Öebbi, ara qqlen lumuô.');
INSERT INTO "ramdane" VALUES(35,5,'35:5','A yimdanen! Ih, lemâahda n Öebbi d tidep. Ur kwen tep$uôôu tmeddurt n ddunit! Ur kwen ipkellix um$weôôi, af Öebbi!');
INSERT INTO "ramdane" VALUES(35,6,'35:6','Cciîan, s tidep, d acengu nnwen. Ëesbet ep d acengu. Issawal kan, i ukabar is, ad ilin seg wat tmes.');
INSERT INTO "ramdane" VALUES(35,7,'35:7','I wid ijehlen, aâaqeb uqsiê. I wid iumnen, ixeddmen ayen ilhan, ssmaê akked warraz meqqwôen.');
INSERT INTO "ramdane" VALUES(35,8,'35:8','Day win iwumi Icebbeê yir igiten is, iwakken ad ioâal gerzen...? S tidep, Öebbi Itellif win i S ihwan, Inehhu win i S ihwan. Ur hellek ara iman ik, fellasen. Ih, Öebbi Iéôa acu la ûûenâen.');
INSERT INTO "ramdane" VALUES(35,9,'35:9','Öebbi i Ippaznen avu, issalayen asigna. Neppawi t ar tmurt immuten, Nessidir ed, yis, akal, deffir tmeppant is. Akka, tanekra.');
INSERT INTO "ramdane" VALUES(35,10,'35:10','Win ib$an tazmert, tazmert akw ar Öebbi. Awal ukyis ippali $uôeS. Igi ilhan, Iteg as ccan. Aâaqeb uqsiê i wid ippandin tucmitin. A ppemêu tandit nnsen.');
INSERT INTO "ramdane" VALUES(35,11,'35:11','Ixelq ikwen Öebbi seg wakal; sinna, si tiqqit; sinna, Irra kwen d tiyugiwin. Ur treffed tadist, ur tepparew nnta, siwa ma Iéôa t. Ur Izzi$wzif laâmeô i umuddir, ur as t Issenqas, siwa ma di tezmamt. Ayagi, ih, d ayen isehlen i Öebbi.');
INSERT INTO "ramdane" VALUES(35,12,'35:12','Ur aâdilen sin ilellen: wa zeddig, éid, iwata i tissit; wayev d ameô$an, d aôéagan. Segsen teppeppem aksum aleqqaq, teppekksem d iâqqucen i teppeqnem. Twalav sfina tepcerrig iten, iwakken a pnadim lfevl iS. Amar a ppesnemmrem.');
INSERT INTO "ramdane" VALUES(35,13,'35:13','Issekcam iv deg wass, Issekcam ass deg yiv. Iêeppem i yiîij akked waggur, ad lêun ar lajel ipwassnen. D wagi ay d Öebbi, Mass nnwen. IneS tagelda. Wid i tnaâôem, war Neppa, ur rebêen iclem utemôiw.');
INSERT INTO "ramdane" VALUES(35,14,'35:14','Ma tnaâôem ten, ur sellen i tnaâôit nnwen. Xas slan, ur awen d pparan. Ass n tnekra, ad nekôen ccerk nnwen. Ur k ipxebbiô yiwen am Win Ippuxebbôen.');
INSERT INTO "ramdane" VALUES(35,15,'35:15','A yimdanen! Kunwi d ime$ban zdat Öebbi. Öebbi d Asâay, d Amaâzuz.');
INSERT INTO "ramdane" VALUES(35,16,'35:16','Ma ihwa yaS, a kwen Ikkes, a d Iawi lxelq amaynut.');
INSERT INTO "ramdane" VALUES(35,17,'35:17','I Öebbi, ayagi, ur illi d aéayan.');
INSERT INTO "ramdane" VALUES(35,18,'35:18','Win ibubben ur ipbibbi taâkkwemt n wayev. Ma isuter ed afus win iâabban, ur s ipwakkas wacemma, xas ma si llezm is. Nabi kan wid ippagwaden Mass nnsen di tbavnit, u péallan. Win ipzekkin, i iman is kan i ipzekki. Tu$alin ar Öebbi.');
INSERT INTO "ramdane" VALUES(35,19,'35:19','Ur iaâdil uder$al akked win ipwalin,');
INSERT INTO "ramdane" VALUES(35,20,'35:20','ne$ tillas akked tafat,');
INSERT INTO "ramdane" VALUES(35,21,'35:21','ne$ tili akked una$uô.');
INSERT INTO "ramdane" VALUES(35,22,'35:22','Ur aâdilen imuddiren akked lmeggtin. Öebbi, s tidep, Issel i win i S ihwan. Keçç, ur tezmirev a ppeslev i wid illan di tneîlin.');
INSERT INTO "ramdane" VALUES(35,23,'35:23','Keçç, d aneddaô kan.');
INSERT INTO "ramdane" VALUES(35,24,'35:24','Ih, Nuzen ik id, s tidep, d abeccaô, d aneddaô. Ur illi lois, mebla ma iusa t id uneddaô.');
INSERT INTO "ramdane" VALUES(35,25,'35:25','Ma skaddben k, ni$ skaddben wid i ten id izwaren. Usan ten id imazanen nnsen, s ubeggen, s Ihellilen, s Tezmamt timnewweôt.');
INSERT INTO "ramdane" VALUES(35,26,'35:26','D$a Ssemêalqe$ i wid ijehlen. Amek tuzzma W imiren!');
INSERT INTO "ramdane" VALUES(35,27,'35:27','Ur twalav ara Öebbi Isse$li d aman seg igenni? Nessuffe$ ed, yissen, ppemôa n yal ini. Deg idurar, isôiden icebêanen, izegwa$en n yal ini, akked iberkanen mavi.');
INSERT INTO "ramdane" VALUES(35,28,'35:28','Imdanen, lewêuc, lmal, n yal ini ula d nitni. Akka, ger imdanen iS, d imusnawen kan i ippagwaden Öebbi. Ih, Öebbi d Uzmir, Ipsemmiê');
INSERT INTO "ramdane" VALUES(35,29,'35:29','Ih, wid iqqaôen Tazmamt n Öebbi, ipéallan, ipseddiqen seg wayen iten N$at, s tuffra ne$ âinani, ad ssirmen ttjaôa ur nepquccu.');
INSERT INTO "ramdane" VALUES(35,30,'35:30','Akken ad asen Ifk tiqvaâin nnsen, ad asen Irnu si lfevl iS. Neppa, s tidep, Ipsemmiê, Ippara tajmilt.');
INSERT INTO "ramdane" VALUES(35,31,'35:31','Ayen i k Nweêêa, si Tezmamt, d tidep id itebbten ayen ip id izwaren. Ih, Öebbi, $ef imdanen iS, Ippuxebbeô, Ipwali ten.');
INSERT INTO "ramdane" VALUES(35,32,'35:32','Sinna, Nerra d iweôôaten n Tezmamt, wid Nefren, ger imdanen nne£. Garasen, llan win idensen iman is, garasen alemmas, garasen win izeggwiren ar lxiô, s wannuf n Öebbi. Wagi d lfevl ameqwôan.');
INSERT INTO "ramdane" VALUES(35,33,'35:33','Loennet n Âaden, ara kecmen. Din, ad qqnen imeqyasen n ddheb, akked tyuqat. Leêrir, di lebsa nnsen.');
INSERT INTO "ramdane" VALUES(35,34,'35:34','Ad inin: "cekôan i Öebbi i Issaferen fella$ le$wben. Ih, Mass nne$ Ipsemmiê, Ippara tajmilt.');
INSERT INTO "ramdane" VALUES(35,35,'35:35','I a$ Irran, s lefvl iS, axxam i tnezdu$t; ur a$ ipnal, degs, wugur; ur a$ ipnal, degs, âaggu".');
INSERT INTO "ramdane" VALUES(35,36,'35:36','I wid ijehlen, times n Loahennama. Ur tepfukku fellasen, ad mmten; ur ippifsus, fellasen, uâaqeb is. Akka, ay Nepqeîîi yal ajehli.');
