INSERT INTO "ramdane" VALUES(38,73,'38:73','Knant akw lmalayek, akken ma llant,');
INSERT INTO "ramdane" VALUES(38,74,'38:74','siwa Iblis i izuxxen, illa seg ijehliyen.');
INSERT INTO "ramdane" VALUES(38,75,'38:75','Inna: "A Iblis, w ik igedlen lekni i win Xelqe$ s ifassen iW? Tzuxxev, ne$ telliv seg wid iufraren"?');
INSERT INTO "ramdane" VALUES(38,76,'38:76','Inna: "Nek ife$ t. Txelqev iyi si tmes, Txelqev t si talaxt".');
INSERT INTO "ramdane" VALUES(38,77,'38:77','Inna: "Effe$ segs, keçç d imenfi"!');
INSERT INTO "ramdane" VALUES(38,78,'38:78','Inna: "fellak xezzu, alamma d ass iqeîîiyen".');
INSERT INTO "ramdane" VALUES(38,79,'38:79','Inna: "A Mass iw, eoo iyi ar ass ideg ara d kkren".');
INSERT INTO "ramdane" VALUES(38,80,'38:80','Inna: "keçç, ih, seg wid Ooi$');
INSERT INTO "ramdane" VALUES(38,81,'38:81','ar ass n wukud ipwassnen".');
INSERT INTO "ramdane" VALUES(38,82,'38:82','Inna: "s tezmert iK, a ten $ewwu$, akken ma llan,');
INSERT INTO "ramdane" VALUES(38,83,'38:83','siwa imdanen iK izedganen segsen".');
INSERT INTO "ramdane" VALUES(38,84,'38:84','Inna: "tidep, d tidep ay Nni$.');
INSERT INTO "ramdane" VALUES(38,85,'38:85','Ad Acare$ Loahennama yissek, akked wid, segsen, i k ivefôen, akken ma llan".');
INSERT INTO "ramdane" VALUES(38,86,'38:86','Ini: "ur awen ssutre$ taqdiât af waya. Ur lli$, nek, seg wid ippestaâmilen".');
INSERT INTO "ramdane" VALUES(38,87,'38:87','Neppa, s tidep, d asmekti kan i imavalen.');
INSERT INTO "ramdane" VALUES(38,88,'38:88','Mi tâadda tallit, war ccekk, a kwen id iawev usali s.');
INSERT INTO "ramdane" VALUES(39,1,'39:1','Tarusi n Tezmamt s$uô Öebbi, Uzmir, Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(39,2,'39:2','Ih, Nessader ak d Tazmamt, s tidep. Ihi, aâbed Öebbi, s laâqida i Neppa.');
INSERT INTO "ramdane" VALUES(39,3,'39:3','Day maççi i Öebbi laâqida? Wid ippaddamen imawlan, war Neppa, nnan: "naâbed iten kan, iwakken ad a$ rnun aseqôeb ar Öebbi". Ih, Öebbi ad Iêudd garasen, $ef wayen i$ef mgarraden. Öebbi, war ccekk, ur Inehhu win illan d akeddab, d ajehli.');
INSERT INTO "ramdane" VALUES(39,4,'39:4','Lemmer Ib$i Öebbi ad Isâu aqcic, yili Ifren, deg wayen Ixelleq, ayen i S ihwan. Gedha s yiS! Neppa d Öebbi Yiwen, Aqehhaô.');
INSERT INTO "ramdane" VALUES(39,5,'39:5','Ixleq tamurt akked igenwan, s tidep. Innev Iv i wass, Innev ass i yiv. Iêeppem i yiîij akked waggur, ad lêun, yal wa, ar lajel ipwassnen. Day Neppa maççi d Uzmir Ipsemmiêen?');
INSERT INTO "ramdane" VALUES(39,6,'39:6','Ixelq ikwen si yiwen n lxelq. Irra, segs, tislit is. Issers awen d îam n tyugiwin n lmal. Ixelq ikwen di tâabbuvin n tyimmatin nnwen, lxelq deffir lxelq, di krav n tellisat. D wagi ay d Öebbi, Mass nnwen. IneS tagelda, ur illi ôebbi siwa Neppa. Amek ihi tugim?');
INSERT INTO "ramdane" VALUES(39,7,'39:7','Ma tjehlem, Öebbi ur kwen Iêwao ara. Ur Ipôadi tijehli i imdanen iS. Ma tesnemmrem, a kwen Iôavi. Ur ipbibbi yiwen taâkkwemt n wayev; sinna, ar Mass nnwen tu$alin nnwen; ad awen Imel acu txeddmem. Ih, Neppa, Issen i illan deg idmaren.');
INSERT INTO "ramdane" VALUES(39,8,'39:8','Amdan, ma iêuza t ccqa, ad inuj ar Mass is, d ucâif $uôes; sinna, ma I$at it s$uôeS, ad ippu ayen i$ef inuj $uôeS, uqbel; ad irr i Öebbi tanudda, ad innetlaf seg ubrid iS. Ini: "zzhu cwiî di tijehli k, ihi, keçç seg wat tmes".');
INSERT INTO "ramdane" VALUES(39,9,'39:9','Day win ippanzen deg iv, ikna u ibedd, ipêebbiô i laxeôt, issaram aêunu n Mass is...? Ini: "day âadlen wid issnen akked wid ur nessin? Ay d ismektayen, kan, d uâqilen".');
INSERT INTO "ramdane" VALUES(39,10,'39:10','Ini: "a yimdanen iW iumnen, êezzbet i Mass nnwen. Ayen ilhan i wid imaânen di ddunit agi. Tamurt n Öebbi tewsaâ. Ihi, arraz, war aktili, ad ippunefk i iûebôiyen".');
INSERT INTO "ramdane" VALUES(39,11,'39:11','Ini: "nek pwamôe$ d ad âabde$ Öebbi, s laâqida i Neppa.');
INSERT INTO "ramdane" VALUES(39,12,'39:12','Pwamôe$ d, ad ili$ d amvuâ amenzu".');
INSERT INTO "ramdane" VALUES(39,13,'39:13','Ini: "nek, ugade$, ma aâûa$ Mass iw, aâaqeb n wass ameqwôaêan".');
INSERT INTO "ramdane" VALUES(39,14,'39:14','Ini: "ad âabde$ Öebbi, s laâqida i Neppa.');
INSERT INTO "ramdane" VALUES(39,15,'39:15','Ihi, âabdet i wen ihwan, war Neppa". Ini: "imexsuôen d wid iwedôen iman nnsen, akked twacult nnsen, ass n tnekra". Ni$ d tagi ay d taxessaôt n ûûeê.');
INSERT INTO "ramdane" VALUES(39,16,'39:16','A$embabu n tmes, sennig sen, wayev seddawat sen. Ayagi, Issagwad yis Öebbi imdanen iS: a yimdanen iW, êezzbet iyI!');
INSERT INTO "ramdane" VALUES(39,17,'39:17','Wid ijunben aâbad iôebbiten n tkerkas, u iutuben Öebbi, a ten id ias lfeôê. Ihi, ssefôeê imdanen iW');
INSERT INTO "ramdane" VALUES(39,18,'39:18','i issemêasen i wawal, teddun d ûûam ines. D wigi i Inha Öebbi, d wigi ay d uâqilen.');
INSERT INTO "ramdane" VALUES(39,19,'39:19','Day win i$ef ippuseyya ad isâaddi aâaqeb, day a psellkev keçç, win illan di tmes?');
INSERT INTO "ramdane" VALUES(39,20,'39:20','Ma d wid iêezzben i Mass nnsen, ad sâun ti$uôfatin, bnant ta sennig ta, isaffen seddawat sent ppazzalen, d lemâahda n Öebbi. Öebbi ur Ixeîîu i lemâahda.');
INSERT INTO "ramdane" VALUES(39,21,'39:21','Ur twalav ara, Öebbi Isse$li d aman, seg igenni, Issekcem iten ad sswen akal. Sinna, yissen, a d Issuffe$ im$an n yal ini, ara iu$alen ad isliwen. A ten twaliv d iwôa$en, sinna, a ten Irr d iquôanen. Deg waya, s tidep, asmekti i uâqilen.');
INSERT INTO "ramdane" VALUES(39,22,'39:22','Day win iwumi Illi Öebbi, idmaren is, ar wannuz, d$a neppa di tafat s$uô Mass is...? A ssxev n at wulawen iqquôen, ur d smektayen Öebbi! Widak di ttelfan n ûûeê.');
INSERT INTO "ramdane" VALUES(39,23,'39:23','Issader ed Öebbi ûûam n wawal. Tazmamt, degs acbi, allus. Ad ccirwen segs igwelman akked wulawen n wid ippagwaden Mass nnsen, sinna, igwelman akked wulawen nnsen ad iêninen ar usmekti n Öebbi. D tagi ay d nnhaya n Öebbi. Inehhu yis, win i S ihwan. Win Itlef Öebbi, ur illi wa ara t inhun.');
INSERT INTO "ramdane" VALUES(39,24,'39:24','Day win ipêezziben, s wudem is, seg uâaqeb ucmit, ass n tnekra...? Ad inin i imednas: "jeôôbet ayen tkeppbem"!');
INSERT INTO "ramdane" VALUES(39,25,'39:25','Skaddben wid i ten id izwaren. Iusa ten id uâaqeb, seg wanda ur bnin.');
INSERT INTO "ramdane" VALUES(39,26,'39:26','Issukk fellasen Öebbi aâaqeb, di tmeddurt n ddunit; war ccekk, aâaqeb n laxeôt iugar it, lemmer éeôôen!');
INSERT INTO "ramdane" VALUES(39,27,'39:27','Ni$ Nefka i imdanen, di Leqwôan agi, yal amedya, amar a d mmektin.');
INSERT INTO "ramdane" VALUES(39,28,'39:28','Leqwôan s taâôabt, ur degs tiââawji; amar ad êezzben.');
INSERT INTO "ramdane" VALUES(39,29,'39:29','Ifka d Öebbi amedya n urgaz icerken akked wid ipmeréan, u argaz di tifrat akked yiwen urgaz. Day âadlen sin imedyaten? Ccekôan i Öebbi. Xaîi, tegwti degsen ur ssinen.');
INSERT INTO "ramdane" VALUES(39,30,'39:30','War ccekk, keçç a ppemmtev, nitni ad mmten.');
INSERT INTO "ramdane" VALUES(39,31,'39:31','Sinna, kunwi, ass n tnekra, ar Mass nnwen, a ppemqellaâm.');
INSERT INTO "ramdane" VALUES(39,32,'39:32','Anwa i idensen sennig win iskaddben af Öebbi, iskaddben tidep ma tusa t id? Day maççi di Loahennama, i illa uxxam ijehliyen?');
INSERT INTO "ramdane" VALUES(39,33,'39:33','Wid id iusan s tidep, u pputebbten yis, wigi d wid ipêezziben.');
INSERT INTO "ramdane" VALUES(39,34,'39:34','Ad sâun ayen b$an, ar Mass nnsen. D wagi ay d arraz iâaôviyen.');
INSERT INTO "ramdane" VALUES(39,35,'39:35','Iwakken ad asen Ikkes Öebbi, ayen xedmen adiran mavi; a ten I$it s wayen xeddmen d amxallef.');
INSERT INTO "ramdane" VALUES(39,36,'39:36','Day ur Ipqam ara Öebbi amdan iS? La k ssagwaden s wid, war Neppa. Win Itlef Öebbi, ur illi w ara t inhun.');
INSERT INTO "ramdane" VALUES(39,37,'39:37','Win Inha Öebbi, ur illi w ara t itelfen. Ni$ Öebbi d Uzmir, Bab n ppaô.');
INSERT INTO "ramdane" VALUES(39,38,'39:38','Ma testeqsav ten: "anwa i Ixelqen tamurt akked igenwan? Ad inin, war ccekk: d Öebbi". Ini: "ma twalam wigi tnaâôem, war Öebbi, day zemren, ma Ib$a yi Öebbi ccqa, ad kksen ccqa S? Ne$, ma Ib$a yi aêunu, ad ssbeîlen aêunu S"? Ini: "Iqennaâ iyi Öebbi; fellaS ay ppeklen wid ipkalen".');
INSERT INTO "ramdane" VALUES(39,39,'39:39','Ini: "a yagdud iw! Xedmet akken tennumen, nek la xeddme$". Deqqal a ppeéôem');
INSERT INTO "ramdane" VALUES(39,40,'39:40','anwa u$uô ara d ias uâaqeb ara t isdullen, u$uô ara d irs uâaqeb imezgi.');
INSERT INTO "ramdane" VALUES(39,41,'39:41','Ih, Nessader ed $uôek, Tazmamt i medden, s tidets. Win ipwanhan, i iman is, win innetlafen, innetlaf kan, mgal is. Ur telliv, keçç, d awkil nnsen.');
INSERT INTO "ramdane" VALUES(39,42,'39:42','Öebbi Ippaddam leôwaê mara mmten, akked wid ur nemmut, deg ives nnsen. Iâatteq wid i$ef Iseyya tameppant, Ippara d wiyav, ar lajel ipwassnen. Ih, deg wayagi, ard issekniyen i ugdud ipmeyyizen.');
INSERT INTO "ramdane" VALUES(39,43,'39:43','Ne$ ad ddmen, war Öebbi, imcafâan? Ini: "i ma ur sâin acemma, ur fhimen"?');
INSERT INTO "ramdane" VALUES(39,44,'39:44','Ini: "akw ccafuâa n Öebbi. IneS tagelda n tmurt akked igenwan. Sinna, $uôeS ara tu$alem".');
INSERT INTO "ramdane" VALUES(39,45,'39:45','Mara d Ipwadder Öebbi Yiwen, ad zemven wulawen n wid ur numin ara s laxeôt. Ma pwadren d wid, war Neppa, imiren nitni ad ceôêen.');
INSERT INTO "ramdane" VALUES(39,46,'39:46','Ini: "a Öebbi! Axellaq n tmurt akked igenwan, Amusnaw n wayen iffren d wayen ivehôen, d Keçç ara Iêudden ger imdanen iK, $ef wayen llan mgarraden".');
INSERT INTO "ramdane" VALUES(39,47,'39:47','Wid idensen, lemmer nnsen ayen akw i illan di tmurt, rnu yas am neppa, ur ten id ifeddu, seg uâaqeb ucmit, ass n tnekra. Ad asen d iban, s$uô Öebbi, ayen i$ef ur bnin.');
INSERT INTO "ramdane" VALUES(39,48,'39:48','Ad asen d banent tucmitin kesben, ad asen innev wayen i$ef ttahan.');
INSERT INTO "ramdane" VALUES(39,49,'39:49','Ma iêuza umdan ccqa, ad a£ d inâaô. Sinna, ma Nezger tacvaî nne£ fellas, ad yini: "tusa yi d kan si tmusni". Xaîi! D ajeôôeb, maca aîas degsen ur ssinen.');
INSERT INTO "ramdane" VALUES(39,50,'39:50','Ni$ nnan t id imezwura nnsen. Acu i sen d ibbwi wayen keppben?');
INSERT INTO "ramdane" VALUES(39,51,'39:51','Iwwet iten wayen kesben n tucmitin. Wid idensen, seg wigi, a ten iwwet wayen kesben n tucmitin. Ur zmiren ad sfunngen.');
INSERT INTO "ramdane" VALUES(39,52,'39:52','Day ur éôin ara ar Öebbi Ip$iti win i S ihwan, u Iwezzen? Ih, deg waya, ard issekniyen i ugdud iumnen.');
INSERT INTO "ramdane" VALUES(39,53,'39:53','Ini: "a yimdanen iw i ipêawalen mgal iman nnsen! Ur ppayset aêunu n Öebbi. Ih, Öebbi Ipsemmiê akw ibekkaven. Ih, Neppa Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(39,54,'39:54','Steôoâet i Mass nnwen, anzet aS, uqbel a kwen id ias uâaqeb, d$a ula ikwen isellken.');
INSERT INTO "ramdane" VALUES(39,55,'39:55','Ddut d ûûam n wayen i wen d irsen, s$uô Mass nnwen, uqbel a kwen id ias uâaqeb, deqzalla, kunwi ur tebnim";');
INSERT INTO "ramdane" VALUES(39,56,'39:56','u a d yini ôôuê: "a ssexv iw, $ef wayen stehza$ ar loiha n Öebbi! Lli$, s tidep, seg wid ittihin".');
INSERT INTO "ramdane" VALUES(39,57,'39:57','Ne$ a d yini: "lemmer iyi Inha Öebbi, yili lli$ seg wid ipêezziben".');
INSERT INTO "ramdane" VALUES(39,58,'39:58','Ne$ a d yini mara iwali aâaqeb: "lemmer iyi illi wallus, ad ili$ si lâuûam".');
INSERT INTO "ramdane" VALUES(39,59,'39:59','Xaîi! Usan k id issekniyen iW, teskaddbev ten, tzuxxev, telliv seg ijehliyen.');
INSERT INTO "ramdane" VALUES(39,60,'39:60','Ass n tnekra, a pwaliv berrikit wudmawen n wid id igren tikerkas af Öebbi. Day maççi di Loahennama, i illa uxxam imzuxiyen?');
INSERT INTO "ramdane" VALUES(39,61,'39:61','Ad Isellek Öebbi wid iêezzben; ar rebêen. Ur ten ikkat wugur, ur $webbnen.');
INSERT INTO "ramdane" VALUES(39,62,'39:62','Öebbi Ixleq yal cci. Neppa d Awkil n yal cci.');
INSERT INTO "ramdane" VALUES(39,63,'39:63','IneS tsura n tmurt akked igenwan. Wid inekôen issekniyen n Öebbi, widak d imexsuôen.');
INSERT INTO "ramdane" VALUES(39,64,'39:64','Ini: "day ad yi tamôem ad âabde$, war Öebbi, a wid ur nessin"?');
INSERT INTO "ramdane" VALUES(39,65,'39:65','Ni$ iusa k id uweêêi, u $eô wid i k izwaren. Ma tcurkev, ad mêun igiten ik, a ppiliv seg imexsuôen.');
INSERT INTO "ramdane" VALUES(39,66,'39:66','Xaîi! Aâbed Öebbi. Ili seg wid isnemmiren.');
INSERT INTO "ramdane" VALUES(39,67,'39:67','Ur qudôen ara Öebbi, azal n lqedô iS. Akw tamurt, ass n tnekra, di tumeét iS. Igenwan ad nnefvasen deg ufus iS ayeffus. Gedha s yiS! I Aâlay sennig wayen pcaraken!');
INSERT INTO "ramdane" VALUES(39,68,'39:68','Aûuvu di lbuq: ad ixsef i illan deg igenwan, i illan di tmurt, kkes ed i S ihwan i Öebbi. Aûuvu nniven: nitni a d kkren pmuqulen.');
INSERT INTO "ramdane" VALUES(39,69,'39:69','A pfeooeo tmurt, si tafat n Mass is. A d ters Tezmamt, a d awin lambiyya akked inagan. Ad âaddin di ccôaâ, s tidep, ur pwadensen.');
INSERT INTO "ramdane" VALUES(39,70,'39:70','Yal yiwen ad i$ellet ayen ixdem. Neppa, Yif akw akken Issen, d acu xeddmen.');
INSERT INTO "ramdane" VALUES(39,71,'39:71','Ad pwanehôen wid ijehlen, d igrawen, ar Loahennama. Mi bbwven $uôes, u llint tebbura s, ad asen inin iâssasen is: "day ur kwen in usan ara imazanen, segwen, mmalen awen issekniyen n Mass nnwen, nedôen kwen af temlilit n wass nnwen agi"? A d inin: "Ih. Maca, ad iûaô wawal uâaqeb, af ijehliyen".');
INSERT INTO "ramdane" VALUES(39,72,'39:72','A d inin: "kecmet si tebbura, ar Loahennama! Degs, wekkôet". Ay d ucmit uxxam n imzuxiyen!');
INSERT INTO "ramdane" VALUES(39,73,'39:73','Wid iêezzben i Mass nnsen, ad pwanehôen ar Loennet, d igrawen. Mi bbwven $uôes, u llint tebbura s, ad asen inin iâssasen is: "tifrat fellawen, tgerzem; kecmet ep, twekkôem"!');
INSERT INTO "ramdane" VALUES(39,74,'39:74','Ad inin: "ccekôan i Öebbi i $ Issawven lemâahda S. Ifka ya$ tamurt d lweôt; a nili, di Loennet, anda i $ ihwa. A yarraz asaâdi n wid ixeddmen lxiô!');
INSERT INTO "ramdane" VALUES(39,75,'39:75','A pwaliv lmalayek zzint ed i usgeld, sbuccuâent s ccekôan Mass nnsent. Iâadda ccôaâ garasen, s tidep. Ad inin: "a neckeô Öebbi, Mass imavalen".');
INSERT INTO "ramdane" VALUES(40,1,'40:1','Ëm.');
INSERT INTO "ramdane" VALUES(40,2,'40:2','Tarusi n Tezmamt, s$uô Öebbi, Uzmir, Amusnaw;');
INSERT INTO "ramdane" VALUES(40,3,'40:3','Ipsemmiêen tuccva, Iqeblen nndama, Ipâaqaben s teqseê, Bab n terkunt. Ur illi ôebbi siwa Neppa. £uôeS taggara.');
INSERT INTO "ramdane" VALUES(40,4,'40:4','Siwa wid ijehlen i ipnamaren $ef issekniyen n Öebbi. Ur k ip$uôôu ucali nnsen, di tmurt.');
INSERT INTO "ramdane" VALUES(40,5,'40:5','Uqbel nitni, agdud n Nuê, akked ikabaren deffir sen, skaddben. Yal lois iclaâ ad ixvef amazan is. Ipnamar s tkerkas, iwakken, s yissent, ad iveggeô tidep. Ëekme$ ten. A tiyta W imiren!');
INSERT INTO "ramdane" VALUES(40,6,'40:6','Akka iûaô wawal n Mass ik, af wid ijehlen. Nitni, war ccekk, d at tmes.');
INSERT INTO "ramdane" VALUES(40,7,'40:7','Tid irefden asgeld, akked tid i s izzin, sbuccuâen s ccekôan Mass nnsent, umnent yiS. Ssuturent ad pwasemmêen wid iumnen: "a Mass nne$! Terriv yal cci d aêunu, u d tamusni. Semmeê i wid isteôoâen u ddan d ubrid iK; Sellek iten seg uâaqeb n wu$ud.');
INSERT INTO "ramdane" VALUES(40,8,'40:8','A Mass nne$! Ssekcem iten ar leonanat n Âaden, i Tâahdev, i nitni, u i iâaôviyen seg ibabaten nnsen, tilawin nnsen, tarwa nnsen. Keçç, ih, d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(40,9,'40:9','Aâteq iten si tucmitin. Win Tâatqev, assen, si tucmitin, ni$ Têunev fellas. D wagi ay d rrbeê ameqwôaêan".');
INSERT INTO "ramdane" VALUES(40,10,'40:10','Ad pwanecden wid ijehlen: "taâdawt n Öebbi te$leb taâdawt bbwaygarawen. Mi tepwanecdem ar liman, tugim".');
INSERT INTO "ramdane" VALUES(40,11,'40:11','A d inin: "a Mass nne$! Ten$iv a$ berdayen, Tessidrev a$ berdayen. Nqaôô s ibekkaven nne$. Day illa ubrid n tuff$a"?');
INSERT INTO "ramdane" VALUES(40,12,'40:12','Akka, imi mara ipwanâaô Öebbi Yiwen, kunwi tjehlem. Mara S pcaraken, kunwi tumnem. Aêuddu ar Öebbi Aâlayen, Ameqwôan.');
INSERT INTO "ramdane" VALUES(40,13,'40:13','D Neppa i wen Issnaâtan issekniyen iS. Issadar awen d l$it, seg igenni. Siwa win isteôoiâen, id ismektayen.');
INSERT INTO "ramdane" VALUES(40,14,'40:14','Nujet $uô Öebbi, s laâqida i Neppa, xas ugin ijehliyen.');
INSERT INTO "ramdane" VALUES(40,15,'40:15','Aâlayen nnig idurra, Bab n usgeld, Ippak ôôuê s lameô iS, i win i S ihwan, deg imdanen iS, iwakken ad indeô af wass umlili,');
INSERT INTO "ramdane" VALUES(40,16,'40:16','ass ideg ara d awven, ur illi wayen iffren, degsen, i Öebbi. W ip ilan tgelda, assen? D Öebbi, Yiwen, Aqehhaô.');
INSERT INTO "ramdane" VALUES(40,17,'40:17','Ass ideg ara ippuqeîîi yal iman $ef wayen ikseb; ur ippili ddens assen. Ih, Öebbi Ip$awal deg uqeîîi.');
INSERT INTO "ramdane" VALUES(40,18,'40:18','Nabi ten af ass aqôib, ideg ulawen ad rewlen s igerjumen, ad qebôen. Ur ippilli i imednas, uêbib ne$ amcafaâ, iwumi ara ssemêessen.');
INSERT INTO "ramdane" VALUES(40,19,'40:19','Issen nncub n yiéôi, akked wayen teffren idmaren.');
INSERT INTO "ramdane" VALUES(40,20,'40:20','Öebbi Iferru s tidep. Wid ipnujun ar war Neppa, ur qeîîun acemma. Ih, Öebbi d Win Isellen, Ipwalin.');
INSERT INTO "ramdane" VALUES(40,21,'40:21','Day ur nudan ara tamurt, ad walin amek tevôa d wid i ten id izwaren. Llan ifen ten tazmert, akked lateô, di tmurt. Issemêelleq asen Öebbi, ilmend ibekkaven nnsen. Ur asen illi umêareb, mgal Öebbi.');
INSERT INTO "ramdane" VALUES(40,22,'40:22','Akka, acku ppasen $uôsen, imazanen nnsen, s ubeggen, u nitni jehlen. Issemêelleq asen Öebbi. Ih, Neppa, d Uzmir, d Bab uâaqeb uqsiê.');
INSERT INTO "ramdane" VALUES(40,23,'40:23','Ni$ Nuzen ed Musa s issekniyen nne£, s udabu iberrzen,');
