INSERT INTO "ramdane" VALUES(40,44,'40:44','A d tesmektim ayen i wen qqaôe$. Öôay iw fki$ t i Öebbi. Ni$ Öebbi Ipwali imdanen".');
INSERT INTO "ramdane" VALUES(40,45,'40:45','Isellek it Öebbi si tucmitin i undin. I$wellef lois n Ferâun, s uâaqeb iugaren wiyav:');
