INSERT INTO "ramdane" VALUES(40,63,'40:63','Akka ay wexxôen wid iugin issekniyen n Öebbi.');
INSERT INTO "ramdane" VALUES(40,64,'40:64','Öebbi i wen Irran tamurt d leqôaô, igenni d ssqef; ikwen Imeslen. Iûeggem udem nnwen. I$at ikwen s wayen ilhan. D wagi ay d Öebbi, Mass nnwen. D Asaâdi Öebbi, Mass imavalen.');
INSERT INTO "ramdane" VALUES(40,65,'40:65','Neppa d Amuddir, ur illi ôebbi siwa Neppa. Ihi, naâôet eP, s lâaqida i Neppa. Ccekôan i Öebbi, Mass imavalen.');
INSERT INTO "ramdane" VALUES(40,66,'40:66','Ini: "nek, ipwakkes iyi uâbad n wid tnaârem war Öebbi, mi yi d iusa ubeggen, s$uô Mass iw. Pwamôe$ d, ad anze$ i Mass imavalen.');
INSERT INTO "ramdane" VALUES(40,67,'40:67','Neppa, ikwen Ixelqen seg wakal; sinna, si tiqqit; sinna, seg urgen; sinna, Issuffe$ ikwen id d igwerdan; sinna, a ppim$uôem; sinna, a ppu$alem d im$aôen. Kra segwen, pmeppaten uqbel. Iwakken a ppawvem ar lajel ipwassnen. Amendeô ma a pfehmem.');
INSERT INTO "ramdane" VALUES(40,68,'40:68','D Neppa i Issidiren, i Ineqqen. Ma Iseyya d lameô, ad as Yini kan: "ili"! Ad yili.');
