INSERT INTO "ramdane" VALUES(41,6,'41:6','Ini: "nek d amdan kan, am kunwi. Weêêan iyi d kan ar Öebbi nnwen, d Öebbi Yiwen. Sweqmet $uôeS, ssutret aS ssmaê". A tawa$it n imcurak,');
