INSERT INTO "ramdane" VALUES(41,16,'41:16','Nuzen asen aûeôûaô, deg ussan imevyaôen, iwakken a Nessukk fellasen, aâaqeb imdelli di tmeddurt n ddunit. Aâaqeb n laxeôt, war ccekk, isdullu kteô. Ur asen ippili umâiwen.');
