INSERT INTO "ramdane" VALUES(41,30,'41:30','Ih, wid innan: "Mass nne$ d Öebbi", sinna, nudan lewqama, a d rsent $uôsen lmalayek: "ur ppagwadet, ur $webblet; ppubeccôet s Loennet i wen ippuwaââden".');
