INSERT INTO "ramdane" VALUES(41,34,'41:34','Ur iaâdil ise$ akked tucmip. Err ip akin, s wayen ip yifen. Ma tella cceêna, garak d yiwen, ad iu$al d aêbib i k iêemmlen.');
