INSERT INTO "ramdane" VALUES(41,45,'41:45','Ni$ Nefka yas Tazmamt i Musa. Mgarraden fellas. Lemmer ur d izwar ara wawal s$uô Mass ik, yili tefra garasen. Nitni, fellas, di ccekk imcebbwel.');
