INSERT INTO "ramdane" VALUES(41,47,'41:47','£uôeS ay tella tmusni n yimir. Ur d iteffe$ uzway si txeddact is, ur treffed nnta tadist, ur tepparew, siwa ma Iéôa. Ass ideg ar asen Isiwel: "anda ten icriken iW"? A d inin: "ad aK nini, ur illi garane$ unagi".');
INSERT INTO "ramdane" VALUES(41,48,'41:48','Iooa ten wayen u$uô pnujun zik. Fehmen, ur asen illi ssôan.');
INSERT INTO "ramdane" VALUES(41,49,'41:49','Ur ifeccel ara umdan di tuppra n lxiô. Ma inul it lqeôê, ad iayes, ad innu$ben.');
INSERT INTO "ramdane" VALUES(41,50,'41:50','Ma Nseêess as aêunu nne£, deffir ccqa i t iêuzan, war ccekk, ad yini: "ayagi inu. Ur cukke$ ara imir a d ias. Ma bbwve$ s afus n Mass iw, ad afe$ $uôeS ûûam". Ih, a Nbeggen ayen xedmen, i wid ijehlen, u, ih, ad Nsâaddi fellasen aâaqeb iwaâôen.');
INSERT INTO "ramdane" VALUES(41,51,'41:51','Ma N$at amdan ad iwexxeô, izzi akin. Ma iêuza t lêif, ad ipnuju, irennu.');
INSERT INTO "ramdane" VALUES(41,52,'41:52','Ini: "tmeyyzem ma iusa d s$uô Öebbi, u kunwi tnekôem t"? Anwa innetlafen sennig win illan di ticuîna tu$miqt?');
INSERT INTO "ramdane" VALUES(41,53,'41:53','Ad asen Nessken issekniyen nne£ di tmurt akked igenwan, u deg iman nnsen, alamma pputebbten ar neppa d tidep. Day ur Iqadd ara Mass ik mi Neppa, war ccekk, d Anagi n yal cci?');
INSERT INTO "ramdane" VALUES(41,54,'41:54','Day, s tidep, cukken timlilit d Mass nnsen? Day Neppa, s tidep, ur Izzi ara i yal cci?');
INSERT INTO "ramdane" VALUES(42,1,'42:1','Ëm.');
INSERT INTO "ramdane" VALUES(42,2,'42:2','Âsq.');
INSERT INTO "ramdane" VALUES(42,3,'42:3','Akka i Iweêêa i keçç, akked wid ik id izwaren, Öebbi, Uzmir, Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(42,4,'42:4','IneS i illan deg igenwan, i illan di tmurt. Neppa, d Aâlayen, d Ameqwôahan.');
INSERT INTO "ramdane" VALUES(42,5,'42:5','Qôib ad ccergen igenwan seg ixfawen nnsen, mara d lhint lmalayek deg usbuccaâ s ccekôan n Mass nnsent, u ssuturent ad pwassemêen kra illan di tmurt. War ccekk, Öebbi Ipsemmiê, Ipêunu?');
INSERT INTO "ramdane" VALUES(42,6,'42:6','Wid irran imawlan, war Neppa, Iquôaâ ten Öebbi. Ur telliv, keçç, d awkil nnsen.');
INSERT INTO "ramdane" VALUES(42,7,'42:7','Akka ay k n Nweêêa Leqwôan, s taâôabt, iwakken a pnedôev tayimmap n temdinin, akked wayen i s d izzin; a pnedôev af ass n temlilit, ur degs ccekk; arbaâ di Loennet, arbaâ deg ujajiê.');
INSERT INTO "ramdane" VALUES(42,8,'42:8','Lemmer i S ihwi i Öebbi, yili Izdukkel iten d yiwen n lois. Maca, ad Issekcem win i S ihwan ar uêunu S. Imednas, ur sâin bab ne$ asettaô.');
INSERT INTO "ramdane" VALUES(42,9,'42:9','Ne$ rran imawlan, war Neppa. Öebbi d Neppa ay d Bab, d Neppa i Issidiren lmeggtin, d Neppa i Izemren i yal cci.');
INSERT INTO "ramdane" VALUES(42,10,'42:10','Ayen akw i$ef temgarradem, aêuddu s ar Öebbi. Akka Öebbi, Mass iw; fellaS ppekle$, $eô Neppa ay steôoiâe$.');
INSERT INTO "ramdane" VALUES(42,11,'42:11','Axellaq n tmurt akked igenwan. Seg iman nnwen Irra yawen tiyugiwin; si lmal nnwen, tiyugiwin. Isfukti kwen yisent. Ur illi wayen i T icban. Neppa Issel, Ipwali.');
INSERT INTO "ramdane" VALUES(42,12,'42:12','IneS tisura n tmurt akked igenwan. Issugut ôôeéq i win i S ihwan, Iwezzen. Ih, Neppa Issen yal cci.');
INSERT INTO "ramdane" VALUES(42,13,'42:13','Issers awen d, di ddin, ayen i$ef Iweûûa Nuê. Ayen i k Nweêêa, d ayen i$ef Nweûûa Ibôahim, Musa, akked Âisa: "sbeddet ddin, ur ppemgarradet fellas". èÈÈéay, i imcurak, wayen u$uô la ten tneccdev. Öebbi Ippawi $uôeS, win i S ihwan; Inehhu $uôeS, win isteôoiâen.');
INSERT INTO "ramdane" VALUES(42,14,'42:14','Ur mfaôaqen armi ten id tusa tussna. Ppemvuddun. Lemmer ur d izwar wawal s$uô Mass ik $ef lmijal ipwassnen, yili myefran. Ih, wid iweôten Tazmamt deffir sen, haaten, fellas, di ccekk imcebbwel.');
INSERT INTO "ramdane" VALUES(42,15,'42:15','Ilmend n waya, enced, ssewqem akken i tepwamôev, ur teddu d cchawi nnsen. Ini: "umne$ s wayen id Issader Öebbi n Tira. Pwamôe$ d ad wezne$ garawen. Öebbi d Mass nne$, u d Mass nnwen. I nekwni igiten nne$, i kunwi igiten nnwen. Ur teppili nnmara garane$. Öebbi ad a$ Issemlil. Tu$alin $uôeS".');
INSERT INTO "ramdane" VALUES(42,16,'42:16','Wid ipnamaren af Öebbi, deffir wagwav n loawab $uôsen, ur teswi nnmara nnsen, ar Mass nnsen. Fellasen zzâaf, fellasen aâaqeb uqsiê.');
INSERT INTO "ramdane" VALUES(42,17,'42:17','Öebbi id Issadren Tazmamt, s tidep, akked lmizan. W ad ak imlen, ahat imir iqeôb ed?');
INSERT INTO "ramdane" VALUES(42,18,'42:18','Ëaren $uôes wid ur numin ara yis. Ma d wid iumnen, ugaden t; éôan ar neppa d tidep. Ih, wid icukken deg imir, haaten, war ccekk, di ttelfan u$miq.');
INSERT INTO "ramdane" VALUES(42,19,'42:19','Öebbi d Laîif i imdanen iS. Ip$iti win i S ihwan. Neppa d Uohid, d Uzmir.');
INSERT INTO "ramdane" VALUES(42,20,'42:20','Win ib$an tayerza n laxeôt, ad as Nernu di tyerza ines. Win ib$an tayerza n ddunit, ad as Nefk segs. Ur as illi umur di laxeôt.');
INSERT INTO "ramdane" VALUES(42,21,'42:21','Ne$ sâan icriken, sse$ôen ten di ddin ur Iqbil Öebbi? Lemmer ur d izwir ara wawal n ufraz, a p fru garasen. I imednas, war ccekk, aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(42,22,'42:22','A pwaliv imednas feqâen ilmend n wayen kesben, u neppa iddehm ed fellasen. Wid iumnen, ixeddmen ayen ilhan, ad ilin di tebêirin n leonanat. Nnsen i sen ihwan $uô Mass nnsen. D wagi ay d lfevl ameqwôan.');
INSERT INTO "ramdane" VALUES(42,23,'42:23','D wagi is Ipbecciô Öebbi imdanen iS, wid iumnen, ixeddmen ayen ilhan. Ini: "ur awen ssutre$ taqdiât, af waya, siwa lmaênana i llezm nnwen". Win ixedmen ise$, ad as Nernu degs lehhu. Öebbi, ih, Ipsemmiê, Ippara tajmilt.');
INSERT INTO "ramdane" VALUES(42,24,'42:24','Ne$ a d inin: "isnulfa d tikerkas af Öebbi". Ma ihwa yaS i Öebbi, ad Icemmaâ ul ik. S wawal iS, ad Isqucc Öebbi tikerkas, ad Iêeqqeq tidep. Ih, Neppa Iéôa tababnit n idmaren.');
INSERT INTO "ramdane" VALUES(42,25,'42:25','D Neppa i Iqeblen nndama n yimdanen iS. Ipsemmiê tucmitin. Issen ayen txeddmem.');
INSERT INTO "ramdane" VALUES(42,26,'42:26','Issemêas i wid iumnen, ixeddmen ayen ilhan. Ad asen Irnu si lfevl iS. I ijehliyen, aâaqeb uqsiê.');
INSERT INTO "ramdane" VALUES(42,27,'42:27','Ma Issuget Öebbi l$it i imdanen iS, ad be$wven di tmurt. Maca, Issrusu d, s lmizan, ayen i S ihwan. Ih, Neppa Ippuxebbeô $ef imdanen iS, Ipwali ten.');
INSERT INTO "ramdane" VALUES(42,28,'42:28','D Neppa id Isse$layen ageffur mara aysen. Iweppaâ aêunu S. Neppa d Bab, d Amaâzuz.');
INSERT INTO "ramdane" VALUES(42,29,'42:29','Seg issekniyen iS, lxelq n tmurt, igenwan, akked wayen illan n lewêuc, degsen. Izmer Neppa, ma ihwa yaS, a ten id Issemlil.');
INSERT INTO "ramdane" VALUES(42,30,'42:30','Kra n tiyta ikwen iwwten, siwa ayen d bbwin ifassen nnwen. Aîas i Ipsemmiê.');
INSERT INTO "ramdane" VALUES(42,31,'42:31','Ur tezmirem a ppesfunngem di tmurt. Ur awen illi, war Öebbi, bab ne$ asettaô.');
INSERT INTO "ramdane" VALUES(42,32,'42:32','Seg issekniyen iS, ssfinat di yill, amzun d idurar.');
INSERT INTO "ramdane" VALUES(42,33,'42:33','Ma ihwa yaS ad Issens avu, ad êebsent, d tusbivin sufella s. War ccekk, ayagi d issekniyen i yal aûebôi isnemmiren.');
INSERT INTO "ramdane" VALUES(42,34,'42:34','Ne$ a ten Issenger ilmend n wayen kesben. Aîas i Ipsemmiê.');
INSERT INTO "ramdane" VALUES(42,35,'42:35','Ad éôen wid ipnamaren $ef issekniyen nne£, ar ur asen illi ssôan.');
INSERT INTO "ramdane" VALUES(42,36,'42:36','Ayen akw i wen ippunefken n wayla, d zzhu n tmeddurt di ddunit. Ayen illan ar Öebbi, yif it; ad idum kteô, i wid iumnen, ippeklen $ef Mass nnsen;');
INSERT INTO "ramdane" VALUES(42,37,'42:37','wid ijunben ibekkaven ibararen, akked lâaô, u ssurufen i wid i ten isserfan;');
INSERT INTO "ramdane" VALUES(42,38,'42:38','wid id ipjawaben i Mass nnsen, péallan, ppemcawaôen $ef lameô nnsen, pseddiqen seg wayen iten N$at;');
INSERT INTO "ramdane" VALUES(42,39,'42:39','wid, ma iusa ten lbe$wv, ad qablen.');
INSERT INTO "ramdane" VALUES(42,40,'42:40','Aqeîîi n tucmip, d tucmip i p icban. Win isemmêen, issweqmen, arraz is ar Öebbi. Neppa, ih, ur Iêemmel ara imednas.');
INSERT INTO "ramdane" VALUES(42,41,'42:41','Ma d wid id irran ppaô mi pwadensen, ur telli tiyta degsen.');
INSERT INTO "ramdane" VALUES(42,42,'42:42','Tiyta, kan, deg wid ivuôôen imdanen, iqehôen di tmurt, war ssebba. I wigi, aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(42,43,'42:43','Ma d win iûebôen, isemmêen, ayagi, s tidep, d aseyyi n lumuô.');
INSERT INTO "ramdane" VALUES(42,44,'42:44','Win Itlef Öebbi, ur isâi bab, deffir eS. A pwaliv imednas, mara éôen aâaqeb, ad inin: "ma illa ubrid n tu$alin"?');
INSERT INTO "ramdane" VALUES(42,45,'42:45','A ten twaliv pwafessren fellas, ndellen, ggaren tiî s tugwdi. Ad inin wid iumnen: "imexsuôen, s tidep, d wid i sxesôen iman nnsen, akked twacult nnsen, ass n tnekra". War ccekk, deg uâaqeb imezgi, ara yilin imednas.');
INSERT INTO "ramdane" VALUES(42,46,'42:46','Ur asen llin imawlan, ara ten isellken, war Öebbi. Win Itlef Öebbi, ur isâi abrid.');
INSERT INTO "ramdane" VALUES(42,47,'42:47','Jawbet i Mass nnwen, uqbel a d ias, s$uô Öebbi, ass ur nepwexxiô. Ur awen ippili ssôan assen, ur tezmirem a pnekôem.');
INSERT INTO "ramdane" VALUES(42,48,'42:48','Ma numren, ur k id Nuzen ara d amêareb nnsen. Ay fellak, siwa timenna. Ih, mara Nesêess i umdan aêunu s$uône£, ad ifôeê yis. Mara t tsami tucmip, id bbwin ifassen is, s tidep, amdan ad ikaraâ.');
INSERT INTO "ramdane" VALUES(42,49,'42:49','N Öebbi, tagelda n tmurt akked igenwan. Ixelleq ayen i S ihwan. Ippak nnta i win i S ihwan, Ippak ddkeô i win i S ihwan.');
INSERT INTO "ramdane" VALUES(42,50,'42:50','Ne$ Ipcarak ddkeô d nnta. Isaâqaô win i S ihwan. Ih, Neppa Issen, Izmer.');
INSERT INTO "ramdane" VALUES(42,51,'42:51','Ur ippunefk ara i umdan, ad as Immeslay Öebbi, siwa aweêêi, ne$ deffir ucembir, ne$ a d iazen amazan iwumi ara Iweêêi, s tsriê iS, ayen Ib$a. Ih, Neppa d Aâlayen, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(42,52,'42:52','Akka ik d Nweêêa ôôuê, si lameô nne£; ur telliv d win issnen d acu p Tezmamt, ne$ liman. Maca, nerra p d tafat. Yis Nenehhu win i £ ihwan, seg imdanen nne£. Ih, d Keçç i ippawin d ubrid uwqim;');
INSERT INTO "ramdane" VALUES(42,53,'42:53','abrid n Öebbi, i Isâan i illan di tmurt, i illan deg igenwan. War ccekk, ar Öebbi ay teddun lumuô.');
INSERT INTO "ramdane" VALUES(43,1,'43:1','Ëm.');
INSERT INTO "ramdane" VALUES(43,2,'43:2','S Tezmamt ipbegginen!');
INSERT INTO "ramdane" VALUES(43,3,'43:3','Nerra p d Leqwôan s taâôabt, amar a pfehmem.');
INSERT INTO "ramdane" VALUES(43,4,'43:4','Neppa, ih, ata $uône£, ar Tyimmap n Tezmamt, d aâlayan, d uûwib.');
INSERT INTO "ramdane" VALUES(43,5,'43:5','Day a kwen Nêewwej seg usmekti, imi kunwi d agdud ipêawalen?');
INSERT INTO "ramdane" VALUES(43,6,'43:6','Acêal id Nuzen d nnbi i imezwura!');
INSERT INTO "ramdane" VALUES(43,7,'43:7','Ur ten id ippas nnbi, mebla ma mmaâlken fellas.');
INSERT INTO "ramdane" VALUES(43,8,'43:8','Nemêa wid i ten yifen tazmert. U iûaô umedya imezwura.');
INSERT INTO "ramdane" VALUES(43,9,'43:9','Ma testeqûav ten: "anwa i Ixelqen tamurt akked igenwan"? A d inin, war ccekk: "Ixelq iten Uzmir, Amusnaw";');
INSERT INTO "ramdane" VALUES(43,10,'43:10','i wen Irran tamurt d dduê; i wen Igan, degs, iberdan - amendeô ma a ppeppwanhum -;');
INSERT INTO "ramdane" VALUES(43,11,'43:11','id Isse$layen, seg igenni, aman s lmizan - Nessakway yissen tamurt immuten; akka ara d tekkrem -;');
INSERT INTO "ramdane" VALUES(43,12,'43:12','id Ixelqen akw tiyugiwin, i wen Ifkan, si sfinat akked lmal, ara trekbem.');
INSERT INTO "ramdane" VALUES(43,13,'43:13','A pâanncem $ef iârar nnsen; sinna, a d tesmektim l$it n Mass nnwen, mara pâanncem fellasen. A ppinim: "gedha s Win i d Inuben fella$ s waya, ur t nemlik!');
INSERT INTO "ramdane" VALUES(43,14,'43:14','Ih, a nu$al ar Mass nne$".');
INSERT INTO "ramdane" VALUES(43,15,'43:15','Ëesben ay T iâabden d amur segS. Amdan, s tidep, d anekkaô n ûûeê.');
INSERT INTO "ramdane" VALUES(43,16,'43:16','Ne$ day d Iddem tullas seg wayen Ixleq, u Ifren awen d arrac?');
INSERT INTO "ramdane" VALUES(43,17,'43:17','Ma ibbwev ed usali yiwen segsen, $ef wayen id ifka d amedya i Uênin, ad yibrik wudem is, ad ikkufer:');
INSERT INTO "ramdane" VALUES(43,18,'43:18','"ayen d ikren di rrêuê, ur nessifrir anamer"!');
INSERT INTO "ramdane" VALUES(43,19,'43:19','Ëesben lmalayek, i illan d ixdimen n Uênin, d nnta. Day êevôen i uxlaq nnsent? Tira $ef ccada nnsen; fellasen asteqsi.');
INSERT INTO "ramdane" VALUES(43,20,'43:20','Nnan: "lemmer Irad Uênin, yili ur ten naâbbid". Ur sâin kra n tmusni $ef waya. Nitni la pxeôôiven kan.');
INSERT INTO "ramdane" VALUES(43,21,'43:21','Ne$ Nefka yasen Tazmamt, uqbel Tagi, d$a, degs, îîfen.');
INSERT INTO "ramdane" VALUES(43,22,'43:22','Xaîi! Nnan: "nufa d, ih, imezwura nne$, di laâqida. D$a, nekwni, Nedda di lateô nnsen".');
INSERT INTO "ramdane" VALUES(43,23,'43:23','Am akka, ur d Nuzen, uqbel ik, aneddaô ar temdint, mebla ma nnan isâiyen is: "nufa d imezwura nne$ di laâqida. D$a, nedda d lateô nnsen".');
INSERT INTO "ramdane" VALUES(43,24,'43:24','Inna: "i ma bbwi$ awen d nnhaya yifen tin id tufam ar imezwura nnwen"? Nnan: "nekwni, ihi, nugwi ayen is id teppuceggâam".');
INSERT INTO "ramdane" VALUES(43,25,'43:25','Nerra d ppaô, segsen. Muqel amek tevôa d inekkaôen.');
INSERT INTO "ramdane" VALUES(43,26,'43:26','Mi inna Ibôahim i baba s, akked ugdud is: "nek, ur pkka$ deg wayen la tâabdem,');
INSERT INTO "ramdane" VALUES(43,27,'43:27','kkes ed Win iyi d Ixelqen. D Neppa, s tidep, ara iyi Inhun".');
INSERT INTO "ramdane" VALUES(43,28,'43:28','Irra t d awal imezgi i texlift is, amar a d u$alen s abrid.');
INSERT INTO "ramdane" VALUES(43,29,'43:29','U £ate$ ten, nitni akked imezwura nnsen, armi ten id tusa tidep, akked umazan ipbegginen.');
INSERT INTO "ramdane" VALUES(43,30,'43:30','Mi ten id tusa tidep, nnan: "wagi d ssêur, ihi, nekwni nugi t".');
INSERT INTO "ramdane" VALUES(43,31,'43:31','Nnan: "A lemmer id ssadren Leqwôan agi ar urgaz ameqwôaêan n snat temdinin"!');
INSERT INTO "ramdane" VALUES(43,32,'43:32','Day d nitni i ifeôôqen aêunu n Mass ik? D Nekwni i sen Ifeôôqen tamâict nnsen, di tmeddurt n ddunit, u Nessuli kra segsen, tidekkaôin sennig wiyav, iwakken kra, ad sxedmen wiyav. Aêunu n Mass ik yif ayen jemâan.');
INSERT INTO "ramdane" VALUES(43,33,'43:33','Lemmer maççi yiwen n lois kan ara yilin imdanen, a Nerr tiseddura n lfeîîa i ixxamen n wid inekôen Aênin, akw i tiseddaôin i$ef ara alin;');
INSERT INTO "ramdane" VALUES(43,34,'43:34','i ixxamen nnsen, tibbura; imîeôêen ideg ara élen;');
INSERT INTO "ramdane" VALUES(43,35,'43:35','akked uzewweq. Maca, ayagi akw, d astumen i tmeddurt n ddunit. Laxeôt, ar Mass ik, i wid ipêezziben.');
INSERT INTO "ramdane" VALUES(43,36,'43:36','Win iooan asmekti n Uênin, ad as Nqelled Cciîan, ad as yili d amsaâef.');
INSERT INTO "ramdane" VALUES(43,37,'43:37','Ih, Nitni veôôifen ten seg ubrid, u $ilen ar nitni pwanhan.');
INSERT INTO "ramdane" VALUES(43,38,'43:38','Alamma ma iusa d $uône£, inna d: "ah, awufan gari yidek, amecwaô illan ger ugmuv d wayev". A yir amsaâef!');
INSERT INTO "ramdane" VALUES(43,39,'43:39','Ur kwen infaâ, assa, d awez$i, ma tdensem. Aql akwen tcerkem aâaqeb.');
INSERT INTO "ramdane" VALUES(43,40,'43:40','Day a pperrev, keçç, aâeééug ad issel, ne$ a ppenhuv ader$al, akked win illan di ttelfan n ûûeê?');
INSERT INTO "ramdane" VALUES(43,41,'43:41','Amar a k id Nekkes, d$a a d Nerr ppaô segsen;');
INSERT INTO "ramdane" VALUES(43,42,'43:42','ne$ ak Nessken ayen i sen d Nhegga. £uône£, ih, adabu fellasen.');
INSERT INTO "ramdane" VALUES(43,43,'43:43','Ïîef deg wayen i k d ippuweêêan. Keçç, war ccekk, aql ak deg ubrid uwqim.');
INSERT INTO "ramdane" VALUES(43,44,'43:44','Wagi, ih, d asmekti i keçç, akked ugdud ik; deqqal fellawen asteqsi.');
INSERT INTO "ramdane" VALUES(43,45,'43:45','Steqsi wid id Nuzen, uqbel ik, seg imazanen nne£. Day Nerra, war Aênin, iôebbiten ara âabden?');
INSERT INTO "ramdane" VALUES(43,46,'43:46','Ni$ Nuzen ed Musa s issekniyen nne£, ar Ferâun akked imvebbôen is. Inna: "nek d amazan n Mass imavalen".');
INSERT INTO "ramdane" VALUES(43,47,'43:47','Mi sen d ibbwi issekniyen nne£, nitni la ppavsan degsen.');
INSERT INTO "ramdane" VALUES(43,48,'43:48','Ur asen Nessenâata assekni, siwa ma i$leb gma s. Nesâadda fellasen aâaqeb, amar a d u$alen s abrid.');
INSERT INTO "ramdane" VALUES(43,49,'43:49','Nnan: "a yaseêêar, nâaô a$ Mass ik, s lemâahda i k Ifka. Nekwni ara nepwanhu".');
INSERT INTO "ramdane" VALUES(43,50,'43:50','Mi Nessafer aâaqeb fellasen, imiren ad xedâan.');
INSERT INTO "ramdane" VALUES(43,51,'43:51','Imdeê Ferâun i ugdud is, inna: "a yagdud iw, day maççi inu tagelda n Maseô, akked isaffen agi, i ippazalen seddaw i. Day ur twalam ara?');
INSERT INTO "ramdane" VALUES(43,52,'43:52','Ne$ nek ur ife$ ara imdelli agi, ur nezmir ad ibeggen?');
INSERT INTO "ramdane" VALUES(43,53,'43:53','Acimi ur as qqnen ara imeqyasen n uwôe$, ne$ ur d usint ara yides lmalayek, pwazdan t"?');
INSERT INTO "ramdane" VALUES(43,54,'43:54','Issefqaâ agdud is. Unzen as. Wigi, ih, d agdud ucmit.');
INSERT INTO "ramdane" VALUES(43,55,'43:55','Mi £ zzaâfen, Nerra d ppaô segsen, Ne$meq iten meôôa.');
INSERT INTO "ramdane" VALUES(43,56,'43:56','Nerra ten d asmekti u d amedya, i ineggura.');
INSERT INTO "ramdane" VALUES(43,57,'43:57','Mi sen d ippunefk mmi s n Meryem, d amedya, agdud ik iugwi t.');
INSERT INTO "ramdane" VALUES(43,58,'43:58','Qqaôen: "ppif iôebbiten nne$, ne$ Neppa"? Qqaôen ak d akka, i nnmara. Ih, nitni d agdud ipmeôéan.');
INSERT INTO "ramdane" VALUES(43,59,'43:59','Neppa d amdan kan. N$at it, Nerra t d amedya i At Isôail.');
INSERT INTO "ramdane" VALUES(43,60,'43:60','Lemmer i£ £ ihwi, a Nerr lmalayek di tmurt, deg umkan nnwen.');
INSERT INTO "ramdane" VALUES(43,61,'43:61','Neppa, s tidep, d ppbut n yimir. Ur ppilit di ccekk fellas. Ddut yidi; wagi d abrid uwqim.');
INSERT INTO "ramdane" VALUES(43,62,'43:62','Ur kwen issefra$ Cciîan! Ih, neppa d acengu nnwen n ûûeê.');
INSERT INTO "ramdane" VALUES(43,63,'43:63','Mi n iusa Âisa s issekniyen, inna: "usi$ d $uôwen s ûûwab, ad awen beggne$ kra, seg wayen i$ef temgarradem. Ihi, êezzbet i Öebbi, ta$em iyi awal.');
INSERT INTO "ramdane" VALUES(43,64,'43:64','Öebbi, s tidep, d Mass iw u d Mass nnwen. Ihi, âabdet eÏ, d wagi ay d abrid uwqim".');
