INSERT INTO "ramdane" VALUES(43,69,'43:69','- Wid iumnen s issekniyen nne£, u llan d imvuâen -');
INSERT INTO "ramdane" VALUES(43,70,'43:70','Kecmet Loennet, kunwi akked tlawin nnwen, a ppezhum.');
INSERT INTO "ramdane" VALUES(43,71,'43:71','Ad asen ddewwiôen, s sniwat n uwôe$, akked ibuqalen; degsen ayen cihwant teôwiêin, ayen iqennâen allen. Kunwi, din, ad twekkôem.');
INSERT INTO "ramdane" VALUES(43,72,'43:72','D tagi ay d Loennet i tweôtem, ilmend n wayen txeddmem.');
INSERT INTO "ramdane" VALUES(43,73,'43:73','Degs, i kunwi, aîas n izwayen, segsen a ppeççem.');
INSERT INTO "ramdane" VALUES(43,74,'43:74','Ibe$wviyen, s tidep, deg uâaqeb n Loahennama, ad wekkôen.');
INSERT INTO "ramdane" VALUES(43,75,'43:75','Ur ipfukku fellasen. Nitni, degs, ad kkufferen.');
INSERT INTO "ramdane" VALUES(43,76,'43:76','Ur ten Nednis, maca llan, nitni, d imednas.');
INSERT INTO "ramdane" VALUES(43,77,'43:77','Ad necden: "a Malik, xas ad a$ Iqfeô Mass ik"! A d ini: "war ccekk, a pwellfem"!');
INSERT INTO "ramdane" VALUES(43,78,'43:78','Ni$ Nusa n $uôwen, s tidep, maca tegwti, degwen, keôhen tidep.');
INSERT INTO "ramdane" VALUES(43,79,'43:79','Ne$ undin ticeôkep. Ula d Nekwni Neppandi.');
INSERT INTO "ramdane" VALUES(43,80,'43:80','Ne$ $ilen ur Nsell ara i lesrar nnsen, akked tumlin nnsen. Ihi, imceggâen nne£, $uôsen, pparun.');
INSERT INTO "ramdane" VALUES(43,81,'43:81','Ini: "lemmer illi mmi s i Uênin, nek d aâbad amenzu".');
INSERT INTO "ramdane" VALUES(43,82,'43:82','Gedha s Mass n tmurt akked igenwan, Mass n usgeld, mgal ayen pxeôôiven!');
INSERT INTO "ramdane" VALUES(43,83,'43:83','Anef asen ad nexxôen, ad uraren, alamma mmugren ass nnsen, i sen ippuwaââden.');
INSERT INTO "ramdane" VALUES(43,84,'43:84','D Neppa ay d Öebbi deg igenni, d Öebbi di tmurt, u d Bab n ûûwab, d Amusnaw.');
INSERT INTO "ramdane" VALUES(43,85,'43:85','Iôqa Win Ilan tagelda n tmurt, igenwan, akked wayen illan garasen. Iéôa melmi ay d imir. £uüôeS, ara tu$alem.');
INSERT INTO "ramdane" VALUES(43,86,'43:86','Wid i la naâôen, war Neppa, ur $uôsen kra n ccafuâa, kkes ed inagan n tidep, u nitni éôan.');
INSERT INTO "ramdane" VALUES(43,87,'43:87','Ma testeqûav ten anwa iten Ixelqen, a d inin, war ccekk, "d Öebbi". Ihi, acimi zzin akin?');
