INSERT INTO "ramdane" VALUES(44,14,'44:14','D$a wexxôen as, u nnan: "d aselmad aôehbani". 2');
INSERT INTO "ramdane" VALUES(44,15,'44:15','Ma Nessenqes awen cwiî uâaqeb, ihi, kunwi, a ppu$alem ar din.');
INSERT INTO "ramdane" VALUES(44,16,'44:16','Ass ideg ara Neédem fellasen, ééedma tabarart, war ccekk, a d Nerr ppaô.');
INSERT INTO "ramdane" VALUES(44,17,'44:17','Ni$ Njeôôeb, uqbel nnsen, agdud n Ferâun. Iusa ten id umazan ukyis.');
INSERT INTO "ramdane" VALUES(44,18,'44:18','"Seddut yidi imdanen n Öebbi; nek, $uôwen, d amazan muman.');
