INSERT INTO "ramdane" VALUES(44,20,'44:20','Nek, ad iyi Isseô Mass iw u d Mass nnwen, ur iyi tôeoomem.');
INSERT INTO "ramdane" VALUES(44,21,'44:21','Ma ur iyi tuminem ara, aâzlet iyi".');
INSERT INTO "ramdane" VALUES(44,22,'44:22','Inuj $eô Mass is ar wigi d agdud ibe$wviyen.');
INSERT INTO "ramdane" VALUES(44,23,'44:23','"Awi imdanen iW deg iv. War ccekk, a kwen in vefôen.');
INSERT INTO "ramdane" VALUES(44,24,'44:24','Eoo ill ildi; nitni, ih, d lâaskeô ara i$emqen".');
INSERT INTO "ramdane" VALUES(44,25,'44:25','Ay ooan di tebêirin, akked tliwa,');
INSERT INTO "ramdane" VALUES(44,26,'44:26','igran, ixxamen ukyisen,');
INSERT INTO "ramdane" VALUES(44,27,'44:27','l$it swayes zehhun!');
INSERT INTO "ramdane" VALUES(44,28,'44:28','Akka ay ten Nefka d lweôt i ugdud nniven.');
