INSERT INTO "ramdane" VALUES(44,30,'44:30','Ni$ Nsellek At Isôail, seg uâaqeb isdullun,');
INSERT INTO "ramdane" VALUES(44,31,'44:31','si Ferâun, i illan, s tidep, d amnefxi ger imaâfan.');
INSERT INTO "ramdane" VALUES(44,32,'44:32','S tmusni ay ten Nefren deg imavalen.');
