INSERT INTO "ramdane" VALUES(44,37,'44:37','Menyif nitni ne$ agdud n Tubbaâ, akked wid i ten id izwaren? Nessenger iten. S tidep, nitni llan d ibe$wviyen.');
INSERT INTO "ramdane" VALUES(44,38,'44:38','Ur Nexliq ara tamurt, igenwan, akked wayen illan garasen, i wurar.');
INSERT INTO "ramdane" VALUES(44,39,'44:39','Nxelq iten kan s tidep, maca, tegwti degsen, ur ssinen.');
