INSERT INTO "ramdane" VALUES(44,46,'44:46','akken ppayzagen waman.');
INSERT INTO "ramdane" VALUES(44,47,'44:47','"Ddemt ep, zu$ôet ep, ar pnaûfa ujajiê,');
