INSERT INTO "ramdane" VALUES(45,1,'45:1','Ëm.');
INSERT INTO "ramdane" VALUES(45,2,'45:2','Tarusi n Tezmamt s$uô Öebbi, Uzmir, Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(45,3,'45:3','Di tmurt akked igenwan, s tidep, llan issekniyen i lmumnin.');
