INSERT INTO "ramdane" VALUES(46,1,'46:1','Ëm.');
INSERT INTO "ramdane" VALUES(46,2,'46:2','Tarusi n Tezmamt s$uô Öebbi, Uzmir, Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(46,3,'46:3','Ur Nexliq tamurt, igenwan, akked wayen illan garasen, siwa s tidep, ar lajel ipwassnen. Wid ijehlen numren ayen u$uô pwanedôen.');
INSERT INTO "ramdane" VALUES(46,4,'46:4','Ini: "day ur twalam ara wigi u$uô la tepnujum, war Öebbi? Sseknet iyi d, d acu xelqen si tmurt, ne$ ma sâan amur deg igenwan? Awit iyi d tazmamt id izwaren tagi, ne$ lateô n tmusni, ma tessawalem tidep".');
INSERT INTO "ramdane" VALUES(46,5,'46:5','Anwa innetlafen sennig wid ipnujun, war Öebbi, $eô wayen ur asen d nepparra, ar ass n tnekra, u nitni stehzan deg unuju nnsen?');
INSERT INTO "ramdane" VALUES(46,6,'46:6','Mara twafesrem imdanen, ad ilin d icenga nnsen, ad skiddben aâbad nnsen.');
