INSERT INTO "ramdane" VALUES(46,8,'46:8','Ne$ ad inin: "isnulfa t id". Ini: "lemmer it id snulfa$, ur tezmirem ad iyi tsellkem, s wacemma, si Öebbi. Neppa, Yif akw akken Issen, acu têekkum fellas. Iqadd Neppa d Anagi gari yidwen. Neppa, Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(46,9,'46:9','Ini: "ur lli$, ger imazanen, d win id isnulfuyen. Ur éôi$ acu ara ivôun yidi, ne$ yidwen. La îîafaôe$, kan, ayen iyi d ippuweêêan. Aql i, kan, nek, d aneddaô ipbegginen".');
INSERT INTO "ramdane" VALUES(46,10,'46:10','Ini: "meyyzet ma, ayagi, s$uô Öebbi, u tnekôem t? Icehhed unagi n At Isôail, $ef lmetl is, d$a iumen. Tesme$wrem iman nnwen". Ih, Öebbi ur Inehhu agdud imednas.');
INSERT INTO "ramdane" VALUES(46,11,'46:11','Nnan wid ijehlen, i wid iumnen: "lemmer d ayen ilhan, ur a$ zeggwiren $uôes". Imi ur pwanhan ara syis, qqaôen: "ayagi d tikerkas n zik".');
INSERT INTO "ramdane" VALUES(46,12,'46:12','Uqbel is, Tazmamt n Musa, d nnhaya u d aêunu. Tagi d Tazmamt itebbiten, s wawalen n taâôabt, a ppendeô wid idensen, a ppessefôeê lâuûam.');
INSERT INTO "ramdane" VALUES(46,13,'46:13','Ih, wid iqqaôen: "Mass nne$ d Öebbi, d$a îfen degs, ur ppagaden, ur $weblen".');
INSERT INTO "ramdane" VALUES(46,14,'46:14','Widak d at Loennet, degs ad wekkôen, d arraz n wayen xeddmen.');
INSERT INTO "ramdane" VALUES(46,15,'46:15','Nweûûa amdan ar lewqama s imawlan is. Imma s temêen s tadist, temêen di tinnufra. Ger tadist akked ujumi, caret wagguren. Armi ibbwev d anqaqôi, issawev kuéet isegwasen, inna: "a Mass iw! Eoo iyi a K snemmre$ af l$it i $ T$atev, nek akked lwaldin iw; ad xedme$ ayen ilhan ara K iôavin. Efk iyi d tarwa ilhan. Aql i utube$ K, aql i seg imvuâen".');
INSERT INTO "ramdane" VALUES(46,16,'46:16','£uô wigi, Nqebbel lmefruz igiten nnsen, a Nessuref i tucmitin nnsen, ger at Loennet; d lemâahda n tidep, i sen ippuwaââden.');
INSERT INTO "ramdane" VALUES(46,17,'46:17','Win innan i imawlan is: "tfuh nnwen! Day yi tâahdem a d kkre$? Ni$ âaddan isuta uqbel i". Nitni teppren tacvaî n Öebbi: "a ssexv ik, amen! Ih, lmâahda n Öebbi, d tidep". A d yini: "ayagi, siwa tiêkayin imezwura".');
INSERT INTO "ramdane" VALUES(46,18,'46:18','D wigi i$ef iûaô wawal iaânan leoyus n leonun akked yimdanen, i ten id izwaren. Ih, nitni d imexsuôen.');
INSERT INTO "ramdane" VALUES(46,19,'46:19','I akw tiseddarin n wayen xedmen, iwakken ad yili warraz igiten nnsen. Nitni ur pwadensen.');
INSERT INTO "ramdane" VALUES(46,20,'46:20','Ass ideg ara kecmen ar tmes, wid ijehlen: "tvegâam tiéidanin i s tezham, di tmeddurt nnwen di ddunit. Assa, a ppeppuqeîîim s uâaqeb isdullun, imi tessem$waôem iman nnwen di tmurt, ur teklalem; imi tellam tbe$wlem".');
INSERT INTO "ramdane" VALUES(46,21,'46:21','Mmekti d agmat n Âad, mi indeô agdud is deg igidan, u, s tidep, âaddan d ineddaôen zdat es, u zdeffir es: "siwa Öebbi ara tâabdem. Nek ugade$, fellawen, aâaqeb n wass abarar".');
INSERT INTO "ramdane" VALUES(46,22,'46:22','Nnan d: "day a$ d tusiv, ad a$ tfaôqev akked iôebbiten nne$? Awi a$ d ayen i$ef la $ tessagwadev, ma tessawalev tidep"!');
INSERT INTO "ramdane" VALUES(46,23,'46:23','Inna: "tamusni ar Öebbi kan. Ssawve$ awen ayen i$ef id ppuceggâa$. Maca, wala$ kwen d agdud ur nessin".');
INSERT INTO "ramdane" VALUES(46,24,'46:24','Mi t walan d asigna id ileêêun ar t$wezza nnsen, nnan: "wagi d asigna ar a$ d iawin ageffur". Xaîi! D ayen i$ef têarem, d avu, degs aâaqeb aqeôêan;');
INSERT INTO "ramdane" VALUES(46,25,'46:25','ad isqucc yal cci, s lameô n Mass is. Gwran d ur tepwaliv siwa ixxamen nnsen. Akka i Nepqeîîi igduden ibe$wviyen.');
INSERT INTO "ramdane" VALUES(46,26,'46:26','Ni$ Nessakrer asen ayen ur awen Nessakrer. Nefka yasen imeééu$en, allen, akked wulawen. Ula i sen d bbwin deg wacemma imeééu$en, allen, akked wulawen, mi skaddben issekniyen n Öebbi. Ayen i$ef ttihin, izzi d fellasen.');
INSERT INTO "ramdane" VALUES(46,27,'46:27','Ni$ nessenger akw timdinin i wen d izzin, u Nenewwel asent issekniyen, amar a d u$alent s abrid.');
INSERT INTO "ramdane" VALUES(46,28,'46:28','Acimi ur ten sellken ara wid rran d iôebbiten iééaéan, war Öebbi? Xaîi! Ooan ten. D ayagi ay d tikerkas nnsen, u d wayen id snulfuyen.');
INSERT INTO "ramdane" VALUES(46,29,'46:29','Mi k ed Nebbwi agraw n leonun, ad ssemêessen i Leqwôan; mi s êedôen, nnan: "êesset"! Mi ifukk, u$alen s agdud nnsen d ineddaôen.');
INSERT INTO "ramdane" VALUES(46,30,'46:30','Nnan: "a yagdud nne$, aql a$ nesla i Tezmamt id irsen, deffir Musa, id itebbten ayen ip id izwaren, i inehhun ar tidep akked ubrid uwqim.');
INSERT INTO "ramdane" VALUES(46,31,'46:31','A yagdud nne$, erret awal i uberraê n Öebbi, amnet yiS, ad awen Isemmeê seg ibekkaven nnwen, a kwen Isellek seg uâaqeb aqeôêan".');
INSERT INTO "ramdane" VALUES(46,32,'46:32','Win isâaweégen i uberraê n Öebbi, ur ipsellik di tmurt, ur ippaf imawlan, war Neppa. Widak di ttelfan n ûûeê.');
INSERT INTO "ramdane" VALUES(46,33,'46:33','Day ur walan ara ar Öebbi d Win Ixelqen tamurt akked igenwan, ur Ifcil seg uxlaq nnsen? Izmer a d Issider lmeggtin. Ih, Neppa Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(46,34,'46:34','Ass ideg ara d rsen, ar tmes, wid ijehlen: "maççi s tidep ayagi"? A d inin: "ih, s Mass nne$"! A d Yini: "jeôôbet aâaqeb $ef wayen la tnekôem"!');
INSERT INTO "ramdane" VALUES(46,35,'46:35','Eûbeô akken ûebôen wid iôeûmen seg imazanen. I nitni, ur p$awal. Ass ideg ara walin acu i sen ippuwaââden, amzun ur kminen siwa saâa n wass. D aberreê: "anwa ara ipwakksen, siwa agdud icemten"?');
INSERT INTO "ramdane" VALUES(47,1,'47:1','Wid ijehlen, u ssefôa$en abrid n Öebbi, Itlef igiten nnsen.');
INSERT INTO "ramdane" VALUES(47,2,'47:2','Wid iumnen, ixeddmen ayen ilhan, umnen s wayen id irsen ar Muêemmed, u neppa d tidep s$uô Mass nnsen; Issemsa yasen tucmitin nnsen, Iûeggem asen lêala nnsen.');
INSERT INTO "ramdane" VALUES(47,3,'47:3','Ayagi, acku wid ijehlen îîafaôen tikerkas; ma d wid iumnen, îîafaôen tidep n Mass nnsen. Akka id Ippak Öebbi imedyaten nnsen i imdanen.');
INSERT INTO "ramdane" VALUES(47,4,'47:4','Mara d temlilem akked wid ijehlen, ewwtet ar tmegrav ar ten t$elbem. Arzet pen nezzeh. Sinna, d aserreê ne$ d feddu, alamma isers îôad tiâekkwmin is. Akka, lemmer i S ihwi i Öebbi, yili I$leb iten, maca a kwen Ijeôôeb, kra s kra. Wid ipwan$an deg ubrid n Öebbi, ur pôuêun igiten nnsen, d awez$i.');
INSERT INTO "ramdane" VALUES(47,5,'47:5','A ten Inhu, ad Iûeggem lêala nnsen.');
INSERT INTO "ramdane" VALUES(47,6,'47:6','A ten Issekcem di Loennet i sen Imla.');
INSERT INTO "ramdane" VALUES(47,7,'47:7','A wid iumnen! Ma tefkam afus i Öebbi, ad awen Ifk afus, ad awen itebbet isurifen.');
INSERT INTO "ramdane" VALUES(47,8,'47:8','Wid ijehlen, ay amxix nnsen, ad weddôen igiten nnsen.');
INSERT INTO "ramdane" VALUES(47,9,'47:9','Ayagi, acku ugwin ayen id Issers Öebbi; ihi, Imêa igiten nnsen.');
INSERT INTO "ramdane" VALUES(47,10,'47:10','Aâni ur nudan ara tamurt, ad walin amek tevôa d imezwura nnsen? Issexv iten Öebbi. Am akka i ijehliyen.');
INSERT INTO "ramdane" VALUES(47,11,'47:11','Ayagi, acku Öebbi d bab n wid iumnen. Ijehliyen, ur asen illi bab.');
INSERT INTO "ramdane" VALUES(47,12,'47:12','Ih, Öebbi ad Issekcem wid iumnen, ixeddmen ayen ilhan, di leonanat, ddawat sen isaffen ppazzalen. Wid ijehlen zehhun, teppen akken itepp lmal. D times ay d leqôaô nnsen.');
INSERT INTO "ramdane" VALUES(47,13,'47:13','Acêal n at temdinin i yifen, di tezmert, tamdint i k id iéâan! Nesqucc iten, ur asen illi useîîaô.');
INSERT INTO "ramdane" VALUES(47,14,'47:14','Day win illan ar ubeggen n Mass is, am win, iwumi ppucebbêen yir igiten is, u iîîafaôen chawi nnsen?');
INSERT INTO "ramdane" VALUES(47,15,'47:15','Amek tga Loennet i ippuwaââden i wid ipêezziben: degs isaffen n waman ur nxeppeô, isaffen n yi$i ur tfessed lbenna s, isaffen n ccôab aéidan i wid itessen, isaffen n tament ippuneqqan. Degs, i nitni, yal ppemôa, akked ssmaê n Mass nnsen. Day am wid iwekkôen di tmes; sswan ten s waman ippayzagen, ara icergen afwad nnsen?');
INSERT INTO "ramdane" VALUES(47,16,'47:16','Llan garasen wid i k issemêasen. Sifat ff$en d s$uôek, ad inin i wid iwumi teppunefk tmusni: "d acu id inna kan, llinna"? Wigi d wid iwumi Isle$ Öebbi ulawen, teddun kan d chawi nnsen.');
INSERT INTO "ramdane" VALUES(47,17,'47:17','Wid ipwanhan, Irna asen nnhaya, Ifka yasen aêezzeb nnsen.');
INSERT INTO "ramdane" VALUES(47,18,'47:18','Day la pôaoun kan imir, a ten id ias, deqzalla? Ni$ usant ed licaôat is. Ma iusa d $uôsen, iwumi t usmekti nnsen?');
INSERT INTO "ramdane" VALUES(47,19,'47:19','Cfu ar, s tidep, ur illi ôebbi siwa Öebbi; ssuter ssmaê ubekkav ik, akked wid n lmumnin, argaz tameîîut. Öebbi Iéôa anda têebkem, akked wanda tberrkem.');
INSERT INTO "ramdane" VALUES(47,20,'47:20','La qqaôen wid iumnen: "a lemmer a d ters tsuôep"! Maca, mi d ters tsuôep tuêkimt, ideg id ipwader umennu$, a pwaliv at wul iblan, la k id pmuqulen s tiî n win tessexcawat tmeppant. Yif it i nitni');
INSERT INTO "ramdane" VALUES(47,21,'47:21','wannuz, akked wawal uwqim. Mi ipwagzem ôôay, ma ssawlen tidep ar Öebbi yif it i nitni.');
INSERT INTO "ramdane" VALUES(47,22,'47:22','Day maççi, ma twexxôem, ahat a ppesfesdem tamurt, a pfaôqem alag nnwen?');
INSERT INTO "ramdane" VALUES(47,23,'47:23','Wigi d wid i Ixza Öebbi; Isgugem iten, Isder$el iéôi nnsen.');
INSERT INTO "ramdane" VALUES(47,24,'47:24','Day ur pmeyyizen ara Leqwôan, ne$ u$alent tsergal $ef wulawen?');
INSERT INTO "ramdane" VALUES(47,25,'47:25','Ih, wid ixedâan, deffir ubeggen n nnhaya $uôsen, ikellex iten Cciîan. Iooa ten tallit.');
INSERT INTO "ramdane" VALUES(47,26,'47:26','Ayagi, acku nitni nnan i wid iugin ayen d Issader Öebbi: "ad awen na$ awal di kra n lumuô". Öebbi Iéôa lesrar nnsen.');
INSERT INTO "ramdane" VALUES(47,27,'47:27','Amek ihi, ma ddment lmalayek leôwaê nnsen, la ten kkatent ar udmawen nnsen, iârar nnsen?');
INSERT INTO "ramdane" VALUES(47,28,'47:28','Akka, ihi, ddan akked wayen isserfayen Öebbi. Ugin llza S. D$a Isseôxes igiten nnsen.');
INSERT INTO "ramdane" VALUES(47,29,'47:29','Ne$ $ilen at wul amehzul, ard awez$i, ur d Issuffu$ ara Öebbi leb$wev nnsen?');
INSERT INTO "ramdane" VALUES(47,30,'47:30','Lemmer i £ ihwi, ad ak ten id Nessken, war ccekk, a ten tâaqlev ar udem nnsen, a ten tâaqlev ar ûûenf umeslay. Öebbi Issen igiten nnwen.');
INSERT INTO "ramdane" VALUES(47,31,'47:31','S tidep, a kwen Njeôôeb, alamma Nessen imjuhad, akked iûebôiyen, segwen. A Njeôôeb isallen nnwen.');
INSERT INTO "ramdane" VALUES(47,32,'47:32','Ih, wid ijehlen, issefôa$en abrid n Öebbi, i inuqmen Amazan, deffir ubeggen n nnhaya $uôsen, ur vuôôen Öebbi s wacemma. Ad Isseôxes igiten nnsen.');
INSERT INTO "ramdane" VALUES(47,33,'47:33','A wid iumnen! A$et awal i Öebbi, a$et awal i Umazan, ur ssiôxiset igiten nnwen!');
INSERT INTO "ramdane" VALUES(47,34,'47:34','Ih, wid ijehlen, issefôa$en abrid n Öebbi, sinna, mmuten d ijehliyen, war ccekk, ur sen Ipsemmiê Öebbi, d awez$i.');
INSERT INTO "ramdane" VALUES(47,35,'47:35','Ur ppiôhifet, ur ssuturet tifrat, mara tilim d ine$laben! U Öebbi yidwen, ur kwen Ipêewwio, d awez$i, seg igiten nnwen.');
INSERT INTO "ramdane" VALUES(47,36,'47:36','Tameddurt di ddunit, d urar kan, akked lhawiyya. Ma tumnem u têezbem, ad awen d Ifk arrazen nnwen. Ur awen issutur ayla nnwen.');
INSERT INTO "ramdane" VALUES(47,37,'47:37','Ma Issuter awen t, a kwen Icebbwel, a pcuêêem, a d iffe$ leb$wev nnwen.');
INSERT INTO "ramdane" VALUES(47,38,'47:38','D kunwi i ipwanecden a pseddqem deg ubrid n Öebbi. Llan degwen wid ipcuêêun. Win ipcuêêun, ipcuêêu kan $ef yiman is. Öebbi d Asâay, kunwi d igellilen. Ma twexxôem, a kwen Ineûûef s ugdud ur nelli segwen. D$a ur ppemcabin $uôwen.');
INSERT INTO "ramdane" VALUES(48,1,'48:1','Ih, Nefya yak l$elb iberrzen,');
INSERT INTO "ramdane" VALUES(48,2,'48:2','iwakken ad Isemmeê Öebbi ibekkaven ik id izwaren akked wid igwran. Ad ak Itim l$it iS, a k Iawi d ubrid uwqim.');
INSERT INTO "ramdane" VALUES(48,3,'48:3','A k Iall Öebbi tallalt tuzmirt.');
INSERT INTO "ramdane" VALUES(48,4,'48:4','D Neppa id Issersen talwit deg ulawen n lmumnin, iwakken ad yim$uô liman nnsen. N Öebbi Iâsekôiwen n tmurt akked igenwan. Öebbi Izga Iééeô, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(48,5,'48:5','Iwakken ad Issekcem wid akked tid iumnen, ar leonanat, ddawat sen isaffen ppazzalen, degsen ad wekkôen. Ad asen Issemsu tucmitin nnsen. D wagi, ar Öebbi, ay d rrbeê abarar.');
INSERT INTO "ramdane" VALUES(48,6,'48:6','Ad Iâaqeb ixabiten, tixabitin, imcurak, timcurak, wid id ipfekkiôen Öebbi yir afekkeô. Izzi yasen ddens. Irfa Öebbi fellasen, Ixza ten. Ihegga yasen d Loahennama. A tu$alin tucmip!');
INSERT INTO "ramdane" VALUES(48,7,'48:7','N Öebbi iâsekôiwen n tmurt akked igenwan. Öebbi Izga d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(48,8,'48:8','Ih, Nuzen ik id d anagi, d abcir, d aneddaô,');
INSERT INTO "ramdane" VALUES(48,9,'48:9','iwakken a ppamnem s Öebbi akked Umazan iS, a T kfum, ad aS tanzem, a T tesbuccuâem taûebêit, tameddit.');
INSERT INTO "ramdane" VALUES(48,10,'48:10','Wid iâaqden yidek âaqden kan d Öebbi. Afus n Öebbi nnig ufus nnsen. Win iêenten, d imanis kan i isseênet. Win iîîfen deg wayen i$ef iâuhed Öebbi, ad as Ifk arraz abarar.');
INSERT INTO "ramdane" VALUES(48,11,'48:11','Ad ak inin wid igwran, seg uâôaben n ucluê: "i $ ice$wlen, d ayla nne$ akked twaculin nne$. Ssuter a neppusemmeê". Qqaôen s yilsawen nnsen, ayen ur nelli deg ulawen nnsen. Ini: "anwa ara kwen iselken si Öebbi, s wacemma, ma Ib$a yawen vveôô, ne$ ma Ib$a yawen aba$uô? Xaîi, Izga Öebbi Ippuxebbeô af wayen txeddmem.');
INSERT INTO "ramdane" VALUES(48,12,'48:12','Xaîi, t$ilem Amazan akked lmumnin ur d ppu$alen ara, d awez$i, ar twaculin nnsen. Ayagi, issefôeê ulawen nnwen. Nnwaya nnwen dir ip. Kunwi d agdud imsenger.');
INSERT INTO "ramdane" VALUES(48,13,'48:13','Wid ur numin s Öebbi, akked Umazan iS, ihi, aql a£ Nhegga d ajajiê i ijehliyen.');
INSERT INTO "ramdane" VALUES(48,14,'48:14','N Öebbi, tagelda n tmurt akked igenwan. Ipsemmiê i win i S ihwan, Ipâuqub win i S ihwan. Öebbi Izga Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(48,15,'48:15','A d inin wid igwran: "ma tôuêem a d t$eltem, ooet a$ a neddu yidwen". B$an ad bedlen awalen n Öebbi. Ini: "ur teppeddum yidne$, d awez$i. Akka ay d izwar wawal n Öebbi". A d inin: "teêsedm a$"! Xaîi, cwiî kan ay fehhmen.');
INSERT INTO "ramdane" VALUES(48,16,'48:16','Ini i wid igwran, seg uâôaben n ucluê: "a ppepwanecdem s agdud amaâwaôu, uqsiê; a t tenna$em, ne$ ad anzen. Ma tu$em awal, ad awen Ifk Öebbi arraz igerzen. Ma twexxôem, akken twexxôem uqbel, a kwen Iâaqeb s lqeôê".');
INSERT INTO "ramdane" VALUES(48,17,'48:17','Ur telli tiyta deg uder$al, ne$ deg urejdal, ne$ deg umuvin. Win iunzen i Öebbi akked Umazan iS, a t Issekcem di leonanat, ddawat sen isaffen ppazzalen. Win iwexxôen, a t Iâaqeb s lqeôê.');
INSERT INTO "ramdane" VALUES(48,18,'48:18','Ni$ Iôuva Öebbi lmumnin, mi âaqden yidek, seddaw ttejôa. Iéôa i illan deg ulawen nnsen, Issers d fellasen talwit, I$na ten s l$elb aqôib,');
INSERT INTO "ramdane" VALUES(48,19,'48:19','akked waîas n lerbaê, ara îîfen. Öebbi Izga d Uzmir, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(48,20,'48:20','Iwaââed awen Öebbi a d teîîfem lerbaê aîas. I$awel awen d deg waya. Ikkes fellawen afus n medden. Akken ad yili ussekni i lmumnin, a kwen iawi d ubrid uwqim.');
INSERT INTO "ramdane" VALUES(48,21,'48:21','Tayev, iwumi ur tezmirem, ihi, Izzi as Öebbi. Öebbi Izga Izmer i yal cci.');
INSERT INTO "ramdane" VALUES(48,22,'48:22','Lemmer i kwen nnu$en wid ijehlen, ad rewlen, sinna, ur ppafen bab, ne$ asettaô.');
INSERT INTO "ramdane" VALUES(48,23,'48:23','D tannumi n Öebbi i illan si zik. Ur teppafev abeddel, d awez$i, di tannumi n Öebbi.');
INSERT INTO "ramdane" VALUES(48,24,'48:24','D Neppa i Ikksen afus nnsen segwen, akked ufus nnwen segsen, di te$zut n Mekka, siwev Iss$elb ikwen fellasen. Öebbi Izga Ipwali ayen txeddmem.');
INSERT INTO "ramdane" VALUES(48,25,'48:25','D nitni i ijehlen. Swexxôen kwen si Loamaâ n Lêaôam, ugin ad iawev usfel s amkan is. Lemmer maççi d lmumnin, argaz tameîîut, ur ten tessinem, a ten têazem ur teéôim; segsen a d tegwrim di le$ôuô - ni$ Öebbi Issekcam s aêunu S, win i S ihwan - lemmer mfaôaqen, yili, war ccekk, Nesâadda, $ef wid segsen ijehlen, aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(48,26,'48:26','Mi rran wid ijehlen, aêernun deg ulawen nnsen, aêernun n war tussna, Issers ed Öebbi talwit iS af Umazan iS, akked lmumnin. Iqqen asen awal uêezzeb. Iwata ten, klalen t. Öebbi Izga Iéôa yal cci.');
INSERT INTO "ramdane" VALUES(48,27,'48:27','Ni$ Issawev Öebbi, i Umazan iS, tamu$li s tidep: war ccekk, a pkecmem Loamaâ n Lêaôam, ma Irad Öebbi, di laman, iqweôôa nnwen ûûeîlen u qeôcen, ur teppagwadem. Iéôa ayen ur teéôim. Ihegga yawen d, ugwar aya, l$elb aqôib.');
INSERT INTO "ramdane" VALUES(48,28,'48:28','Neppa id Iuznen Amazan iS s nnhaya, akked ddin n tidep, iwakken a t Issifrer $ef ddin meôôa. Iqadd Öebbi d Anagi.');
INSERT INTO "ramdane" VALUES(48,29,'48:29','Muêemmed d Amazan n Öebbi. Wid illan yides, qesêen ar ijehliyen, êninit garasen. A ten twaliv îîinizen, kennun, ssuturen lfevl s$uô Öebbi akked llza. Ccama nnsen, deg udmawen nnsen, d lateô n lekni. Ata wayen i ten icban di Tuôa akked wayen i ten icban di Linoil: am uâaqqa, iger ed im$i s, isoehd it. Ad iuzur, ad iâaooeô $ef yile$ is. Ippaâoab ifellaêen; i$webben yissen ijehliyen. Iwaââed Öebbi ssmaê akked warraz abarar, i wid degsen iumnen, ixeddmen ayen ilhan.');
INSERT INTO "ramdane" VALUES(49,1,'49:1','A wid iumnen! Ur zeggwiret zdat Öebbi akked Umazan iS. Ëezzbet i Öebbi. Öebbi, s tidep, Issel, d Amusnaw.');
INSERT INTO "ramdane" VALUES(49,2,'49:2','A wid iumnen! Ur zzuhriyet tu$ac nnwen, sennig ta$wect n Nnbi. Ur zaââvet $uôes, akken tepzaââvam garawen, mulac ad mêun igiten nnwen, kunwi ur tfaqem.');
INSERT INTO "ramdane" VALUES(49,3,'49:3','Ih, wid issrusun ûut n ta$wect nnsen, ar Umazan n Öebbi, d wid iwumi Issefsi Öebbi ulawen nnsen, ar uêezzeb. Ad asen ippunefk ssmaê akked warraz abarar.');
INSERT INTO "ramdane" VALUES(49,4,'49:4','Ih, tegwti n wid ik n issawlen, deffir texxamin, ur pmeyyizen.');
INSERT INTO "ramdane" VALUES(49,5,'49:5','Lemmer ûbiôen ard teff$ev $uôsen, ppif i nitni. Öebbi Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(49,6,'49:6','A wid iumnen! Aballa$ ma ibbwi yawen d asali, ppuêeqqeqet, mulac a pvuôôem agdud, war tamusni, a ppu$alem tnedmem deg wayen txedmem.');
INSERT INTO "ramdane" VALUES(49,7,'49:7','Cfut illa garawen Umazan n Öebbi. Ma issemêes awen deg waîas n lumuô, a pjafem. Maca, Öebbi Issaâzez awen liman, Imelleê it deg ulawen nnwen, Isseôéeg awen tijehli, dd$el, lmaâûegga. Widak d wid ipwanhan.');
INSERT INTO "ramdane" VALUES(49,8,'49:8','D lfevl s$uô Öebbi, d l$it. Öebbi Iéôa, d Bab n ûûwab.');
INSERT INTO "ramdane" VALUES(49,9,'49:9','Ma nnu$ent snat n îayfat n lmumnin, êuddet pent. Ma tebw$ev yiwet degsent, $ef tayev, nna$et tin ibwe$ven alamma tevfeô lameô n Öebbi. Ma tevfeô it, ssemûalêet pent, s lmizan. Wezznet. Öebbi Iêemmel wid iwezznen.');
INSERT INTO "ramdane" VALUES(49,10,'49:10','Lmumnin d atmaten kan i illan. Ssemûalêet atmaten nnwen, êezzbet i Öebbi, amar fellawen aêunu.');
INSERT INTO "ramdane" VALUES(49,11,'49:11','A wid iumnen! £uôwat ad imaâlek ugdud $ef wayev, ahat d widen i ten yifen. Ne$ tilawin $ef tiyav, ahat d tiden i tent yifen. Ur pqeôôidet iman nnwen, ur pmirgamet s tselleqwbin. Dir it isem n dd$el, anda illa liman. Wid ur nutub ara, d widak ay d imednas.');
INSERT INTO "ramdane" VALUES(49,12,'49:12','A wid iumnen! Janbet aîas n nnwaya. Ih, nnwaya, tikwal, d abekkav. Ur ppemâassat ara. Ur kkatet wa deg wa. Day ib$u yiwen, degwen, ad içç aksum n gmas lmegget? A t tagwim. Ëezzbet i Öebbi. Ih, Öebbi Issuruf, Ipêunu.');
INSERT INTO "ramdane" VALUES(49,13,'49:13','A yimdanen! Ni$ Nxelq ikwen si ddker akked nnta. Nerra kwen d igduden, d laâôac, a ppemyussanem. Ihi, ukyis nnwen amxallef, ar Öebbi, d win degwen ipêezziben sennig wiyav. Ih, Öebbi Iéôa, Ippuxebbeô.');
INSERT INTO "ramdane" VALUES(49,14,'49:14','Nnan uâôaben n ucluê: "numen". Ini: "ur tuminem". Maca init ed: "nunez"; ur ikcim ara liman ar wulawen nnwen. Ma tôuvam Öebbi akked Umazan iS, ur awen Isôuêu acemma seg igiten nnwen. Öebbi, s tidep, Ipsemmiê, Ipêunu.');
INSERT INTO "ramdane" VALUES(49,15,'49:15','Lmumnin d wid kan iumnen s Öebbi akked Umazan iS, d$a, ur pcukun. Pna$en s wayla nnsen akked yiman nnsen, deg ubrid n Öebbi. D widak ay d imawlan n tidep.');
INSERT INTO "ramdane" VALUES(49,16,'49:16','Ini: "day ad aS temlem i Öebbi, ddin nnwen"? Öebbi Iéôa i illan deg igenwan, i illan di tmurt. Öebbi Issen yal cci.');
INSERT INTO "ramdane" VALUES(49,17,'49:17','Rran ak annuz nnsen, d tajmilt. Ini: "ur iyi telli tejmilt deg annuz nnwen. Xaîi, d Öebbi ikwen I$aten mi kwen Inha ar liman, ma d imawlan n tidep ay tellam".');
INSERT INTO "ramdane" VALUES(49,18,'49:18','Ih, Öebbi Issen tabavnit n tmurt akked igenwan. Öebbi Ipwali ayen txeddmem.');
INSERT INTO "ramdane" VALUES(50,1,'50:1','Q. S Leqwôan, mucaâ!');
INSERT INTO "ramdane" VALUES(50,2,'50:2','Xaîi! Wehmen mi ten id iusa uneddaô, segsen. Nnan ijehliyen: "ayagi d lwehma!');
INSERT INTO "ramdane" VALUES(50,3,'50:3','Ma nemmut, nu$al d akal, ayagi d tu$alin si mbâid"!');
INSERT INTO "ramdane" VALUES(50,4,'50:4','Ni$ Neéôa acêal, segsen, i issenqes wakal. £uône£ Tazmamt iêerrzen.');
INSERT INTO "ramdane" VALUES(50,5,'50:5','Xaîi! Nekôen tidep, mi ten id tusa, d$a terwi lêala nnsen.');
INSERT INTO "ramdane" VALUES(50,6,'50:6','Day ur muqlen ara igenni, sennig sen, amek i t Nebna? Ncebbeê it, ur isâi i$isan.');
INSERT INTO "ramdane" VALUES(50,7,'50:7','Tamurt, Nvelq ip, Nerra degs idurar, Nessem$i d degs tayuga n yal ûûenf amxallef,');
INSERT INTO "ramdane" VALUES(50,8,'50:8','i tmu$li akked usmekti n yal amdan isteôoiâen.');
INSERT INTO "ramdane" VALUES(50,9,'50:9','Nesse$li d seg igenni aman iôqan. Nessem$i d yissen leonanat akked uâqqa n tmegra,');
INSERT INTO "ramdane" VALUES(50,10,'50:10','akked tezdayin ti$wezfanin, s ijeooigen mbabben,');
INSERT INTO "ramdane" VALUES(50,11,'50:11','d ôôeéq i imdanen. Nessider d yissen tamurt immuten. Akka tanekra.');
INSERT INTO "ramdane" VALUES(50,12,'50:12','Inkeô, uqbel nnsen, ugdud n Nuê, At Rrass, Tamud,');
INSERT INTO "ramdane" VALUES(50,13,'50:13','Âad, Ferâun, atmaten n Luî,');
INSERT INTO "ramdane" VALUES(50,14,'50:14','At Laika, akked ugdud n Tubbaâ. Nekôen akw imazanen. Iûaô ussigwed iW.');
INSERT INTO "ramdane" VALUES(50,15,'50:15','Day £ issefcel uxlaq amezwaru? Wanag nitni di ccekk $ef uxlaq amaynut.');
INSERT INTO "ramdane" VALUES(50,16,'50:16','Ni$ Nxelq ed amdan. Neéôa s wacu i t t$ewwu, teôwiêt is. Neqôeb $uôes, sennig iéuôan n temgaôt is.');
INSERT INTO "ramdane" VALUES(50,17,'50:17','Mara îîfen wid ippaîîafen; qqimen, wa $eô yeffus, wa $eô zelmev,');
INSERT INTO "ramdane" VALUES(50,18,'50:18','ur d iqqaô awal, siwa ma illa $uôes, uâssas imêelles.');
INSERT INTO "ramdane" VALUES(50,19,'50:19','Iusa d ddux n tmeppant, ibbwi d tidep: "atan wayen iseg la treggwlev".');
INSERT INTO "ramdane" VALUES(50,20,'50:20','Aûuvu di lbuq. D wagi ay d ass n ussigwed.');
INSERT INTO "ramdane" VALUES(50,21,'50:21','Yal iman a d ias s unehhaô, akked unagi.');
INSERT INTO "ramdane" VALUES(50,22,'50:22','Ni$ telliv testehzav $ef waya. Nekkes ak taduli k. Iéôi k, assa, d uzzal.');
INSERT INTO "ramdane" VALUES(50,23,'50:23','Ad yini umsaâef is: "ata $uôi wayen id ippuheggan".');
INSERT INTO "ramdane" VALUES(50,24,'50:24','Veggôet, ar Loahennama, yal ajehli amâaûi,');
INSERT INTO "ramdane" VALUES(50,25,'50:25','i iugwin lxiô, amjuô, imcebbwel,');
INSERT INTO "ramdane" VALUES(50,26,'50:26','i irran ar tama n Öebbi, ôebbi nniven. Ihi, veggôet ep s aâaqeb uqsiê!');
INSERT INTO "ramdane" VALUES(50,27,'50:27','A d yini umsaâef is: "a Mass nne$! Ur t rri$ d amjuô, d neppa i illan di ttelfan u$miq".');
INSERT INTO "ramdane" VALUES(50,28,'50:28','Inna: "ur ppemnamaret ara $uôI, imi wen d Zzewre$ aâggen.');
INSERT INTO "ramdane" VALUES(50,29,'50:29','Ur ipbeddil $uôI wawal. Nek, ur Ïîuôôu$ imdanen".');
INSERT INTO "ramdane" VALUES(50,30,'50:30','Ass ideg ara Nini i Loahennama: "ma teççuôev"? A d tini: "ma tella tmerna"?');
INSERT INTO "ramdane" VALUES(50,31,'50:31','Tqeôb ed Loennet, ar wid ipêezziben, ur tebâad.');
INSERT INTO "ramdane" VALUES(50,32,'50:32','"D tagi i wen ippuwaââden; i yal atubi iêerzen,');
INSERT INTO "ramdane" VALUES(50,33,'50:33','ippagwaden Aênin di tbavnit, iteddun s wul ucâif.');
INSERT INTO "ramdane" VALUES(50,34,'50:34','Ekcemt ep, di tifrat, d wagi ay d ass uwekkeô"!');
INSERT INTO "ramdane" VALUES(50,35,'50:35','Nnsen, dinna, ayen i sen ihwan; s$uône£, timerna.');
INSERT INTO "ramdane" VALUES(50,36,'50:36','Acêal isuta ay Nesqucc uqbel nnsen! Llan $elben ten di loehd. Qelben, di tmurt, anda t ssôan?');
INSERT INTO "ramdane" VALUES(50,37,'50:37','Deg waya, s tidep, asmekti i win isâan ul, ne$ ifkan tameééu$t, u d anagi.');
INSERT INTO "ramdane" VALUES(50,38,'50:38','Ni$ Nxelq ed tamurt, igenwan, akked wayen illan garasen, di sdis wussan, ur Naâyi.');
INSERT INTO "ramdane" VALUES(50,39,'50:39','Eûbeô i wayen d qqaôen, sbuccaâ s ccekôan Mass ik, uqbel ad inqeô yiîij, uqbel ad i$li.');
INSERT INTO "ramdane" VALUES(50,40,'50:40','Deg iv sbuccaâ iT, akked deffir lekni.');
INSERT INTO "ramdane" VALUES(50,41,'50:41','Ssemêes, ass ideg ara d iberreê uberraê, si tama iqeôben.');
INSERT INTO "ramdane" VALUES(50,42,'50:42','Ass ideg ara slen i usu$u, s tidep, ass nni, d ass n tuff$a.');
INSERT INTO "ramdane" VALUES(50,43,'50:43','Ih, Nekwni Nessidir u Nneqq. £uône£ tu$alin.');
INSERT INTO "ramdane" VALUES(50,44,'50:44','Ass ideg ara tceqqeq tmurt $uôsen s tazla. Afesser agi d ayen isehlen fellane£.');
INSERT INTO "ramdane" VALUES(50,45,'50:45','Nekwni Nif akw akken Neéôa acu id qqaôen. Ur telliv fellasen d aqehhaô. Mmekti d, s Leqwôan, wid ippagwaden aâggen iW.');
INSERT INTO "ramdane" VALUES(51,1,'51:1','S wavu iwezzâen!');
INSERT INTO "ramdane" VALUES(51,2,'51:2','I ibubben taâkkwemt!');
INSERT INTO "ramdane" VALUES(51,3,'51:3','Wid ippazalen s shala!');
INSERT INTO "ramdane" VALUES(51,4,'51:4','Tid ifeôqen lameô!');
INSERT INTO "ramdane" VALUES(51,5,'51:5','Ar ayen i wen ippuâahden, d tidep.');
INSERT INTO "ramdane" VALUES(51,6,'51:6','Aqeîîi, ih, ad iûaô.');
INSERT INTO "ramdane" VALUES(51,7,'51:7','S igenni bu idurra!');
INSERT INTO "ramdane" VALUES(51,8,'51:8','Kunwi temgarradem $ef wawal.');
INSERT INTO "ramdane" VALUES(51,9,'51:9','Iooa t win iooan.');
INSERT INTO "ramdane" VALUES(51,10,'51:10','Tameppant i ikeddaben!');
INSERT INTO "ramdane" VALUES(51,11,'51:11','Nitni, di maylellif, stehzan,');
INSERT INTO "ramdane" VALUES(51,12,'51:12','steqsayen: "anda t wass uqeîîi"?');
INSERT INTO "ramdane" VALUES(51,13,'51:13','Ass ideg, ara ppuhewlen di tmes:');
INSERT INTO "ramdane" VALUES(51,14,'51:14','"Jeôôbet ahewwel nnwen agi i$ef têarem"!');
INSERT INTO "ramdane" VALUES(51,15,'51:15','Ih, wid ipêezziben, di leonanat akked tliwa;');
INSERT INTO "ramdane" VALUES(51,16,'51:16','Ïîfen ayen i sen Ifka Mass nnsen. Ih, nitni llan, iu$ lêal, d lâuûam.');
INSERT INTO "ramdane" VALUES(51,17,'51:17','Deg iv, cwiî ay gganen.');
INSERT INTO "ramdane" VALUES(51,18,'51:18','Di tafrara, nitni ste$fiôen;');
INSERT INTO "ramdane" VALUES(51,19,'51:19','deg wayla nnsen: amur i umappar akked ume$bun.');
INSERT INTO "ramdane" VALUES(51,20,'51:20','U di tmurt, issekniyen i wid ipputebbten.');
INSERT INTO "ramdane" VALUES(51,21,'51:21','Llant deg iman nnwen. Aâni ur tepwalim ara?');
INSERT INTO "ramdane" VALUES(51,22,'51:22','U deg igenni, ôôeéq nnwen, akked wayen i wen ippuwaââden.');
INSERT INTO "ramdane" VALUES(51,23,'51:23','S Mass n tmurt akked igenwan, ar neppa d tidep, am akken kunwi tepmeslayem!');
INSERT INTO "ramdane" VALUES(51,24,'51:24','Ma tebbwev ik id tedyant inebgawen ukyisen n Ibôahim?');
INSERT INTO "ramdane" VALUES(51,25,'51:25','Kecmen $uôes, nnan as: "tifrat"! Inna: "tifrat a wid ur nessin!"');
INSERT INTO "ramdane" VALUES(51,26,'51:26','Inser ar twacult is, ibbwi d aâjmi iqebbwan.');
INSERT INTO "ramdane" VALUES(51,27,'51:27','Iqeôb asen t id; inna: "day ur teppeppem ara"?');
INSERT INTO "ramdane" VALUES(51,28,'51:28','Iêus iugad iten. Nnan as: "ur ppagwad"! D$a beccôen t s uqcic amusnaw');
INSERT INTO "ramdane" VALUES(51,29,'51:29','Tqeôb ed tmeîîut is, tsu$, tewwet deg udem is, tenna d: "tam$aôt tiâiqeôt?"');
INSERT INTO "ramdane" VALUES(51,30,'51:30','Nnan: "akka id Inna Mass im. Ih, Neppa, d Bab n ûûwab, d Amusnaw".');
INSERT INTO "ramdane" VALUES(51,31,'51:31','Inna: "anwa ay d awal nnwen, a yimceggâan"?');
INSERT INTO "ramdane" VALUES(51,32,'51:32','Nnan: "nekwni neppuceggaâ d ar ugdud ibe$wviyen.');
INSERT INTO "ramdane" VALUES(51,33,'51:33','Iwakken a ten neôoem s uéôu n talaxt');
INSERT INTO "ramdane" VALUES(51,34,'51:34','i ipwaôecmen s$uô Mass ik i ucmiten".');
INSERT INTO "ramdane" VALUES(51,35,'51:35','Nessuffe$ kra illan, degs, n lmumnin.');
INSERT INTO "ramdane" VALUES(51,36,'51:36','Nufa kan degs, yiwen uxxam n imvuâen.');
INSERT INTO "ramdane" VALUES(51,37,'51:37','Neooa p id d assekni i wid iugaden aâaqeb aqeôêan.');
INSERT INTO "ramdane" VALUES(51,38,'51:38','I di Musa, mi t id Nuzen i Ferâun, s udabu n ûûeê.');
INSERT INTO "ramdane" VALUES(51,39,'51:39','Iaâûa, akked imalalen is, inna: "d aseêêar, ne$ d aôehbani".');
INSERT INTO "ramdane" VALUES(51,40,'51:40','Neddem it, neppa akked iâsekriwen is, Nveggeô iten ar yill. Tiyta degs.');
INSERT INTO "ramdane" VALUES(51,41,'51:41','I di Âad, mi sen Nuzen avu asexvi.');
INSERT INTO "ramdane" VALUES(51,42,'51:42','Ur ippaooa ayen immuger, alamma irra t am rrmim.');
INSERT INTO "ramdane" VALUES(51,43,'51:43','I Tamud, mi pwanecden: "siwa tallit ara tezhum".');
INSERT INTO "ramdane" VALUES(51,44,'51:44','Âuûan lameô n Mass nnsen. Tewwet iten id ssiâqa, nitni la pwalin.');
INSERT INTO "ramdane" VALUES(51,45,'51:45','Ur zmiren a d kren, ur pwasellken.');
INSERT INTO "ramdane" VALUES(51,46,'51:46','Agdud n Nuê, uqbel, llan, s tidep, d agdud iballa$en.');
INSERT INTO "ramdane" VALUES(51,47,'51:47','Igenni, Nebna t s loehd, d Nekwni it Issewsaân.');
INSERT INTO "ramdane" VALUES(51,48,'51:48','Tamurt, Nessa p. Ay gerzen Wid Itessun!');
INSERT INTO "ramdane" VALUES(51,49,'51:49','Di yal cci, Nexleq tayuga, ahat a d tesmektim.');
INSERT INTO "ramdane" VALUES(51,50,'51:50','Ihi, srifget ar Öebbi. Nek, ih, s$uôeS ar $uôwen, d aneddaô ipbegginen.');
INSERT INTO "ramdane" VALUES(51,51,'51:51','Ur sâut, ar tama n Öebbi, ôebbi nniven! Nek, ih, s$uôeS ar $uôwen, d aneddaô ipbegginen.');
INSERT INTO "ramdane" VALUES(51,52,'51:52','Akka, ur iusi umazan ar wid i ten id izwaren, mebla ma nnan as: "d aseêêar ne$ d aôehbani".');
INSERT INTO "ramdane" VALUES(51,53,'51:53','Day ppuweûûan $ef waya? Xaîi, nitni d agdud izegren tilas.');
INSERT INTO "ramdane" VALUES(51,54,'51:54','Ïîixxeô asen; ur deg k tiyta.');
INSERT INTO "ramdane" VALUES(51,55,'51:55','Smektay ed. Asmekti, s tidep, infaâ lmumnin.');
INSERT INTO "ramdane" VALUES(51,56,'51:56','Xelqe$ leonun akked imdanen, iwakken kan ad iyI âabden.');
