INSERT INTO "ramdane" VALUES(51,59,'51:59','Ih, i wid idensen, amur am umur imeddukwal nnsen. Ulay$eô iyI $eûben.');
INSERT INTO "ramdane" VALUES(51,60,'51:60','A tawa$it n wid ijehlen, ilmend n wass i sen ippuwaââden!');
INSERT INTO "ramdane" VALUES(52,1,'52:1','S udrar!');
INSERT INTO "ramdane" VALUES(52,2,'52:2','S Tezmamt ippujerrden');
