INSERT INTO "ramdane" VALUES(111,4,'111:4','akked tmeîîut is, i ipbibbin as$aô,');
INSERT INTO "ramdane" VALUES(111,5,'111:5','tamrart inzizen, $ef umgaôv is.');
INSERT INTO "ramdane" VALUES(112,1,'112:1','Ini: "Neppa, Öebbi Yiwen.');
INSERT INTO "ramdane" VALUES(112,2,'112:2','Öebbi, ur T ippissin yiwen.');
INSERT INTO "ramdane" VALUES(112,3,'112:3','Ur Iuriw, ur T uriwen.');
INSERT INTO "ramdane" VALUES(112,4,'112:4','Ur illi, d nned iS, yiwen.');
INSERT INTO "ramdane" VALUES(113,1,'113:1','Ini: "ad iyi Iûûeô, Mass n wanqaô!');
INSERT INTO "ramdane" VALUES(113,2,'113:2','Si lhemm n wayen Ixelq,');
INSERT INTO "ramdane" VALUES(113,3,'113:3','si lhemm n tillas ma ssant,');
INSERT INTO "ramdane" VALUES(113,4,'113:4','si lhemm n tid ipûuvun di tyersiwin,');
INSERT INTO "ramdane" VALUES(113,5,'113:5','si lhemm um$ecci, ma iusem.');
INSERT INTO "ramdane" VALUES(114,1,'114:1','Ini: "ad iyi Iûûeô, Mass n yimdanen!');
INSERT INTO "ramdane" VALUES(114,2,'114:2','Agellid n yimdanen,');
INSERT INTO "ramdane" VALUES(114,3,'114:3','Öebbi n yimdanen,');
INSERT INTO "ramdane" VALUES(114,4,'114:4','si lhemm u$way n win ipfezwin,');
INSERT INTO "ramdane" VALUES(114,5,'114:5','i i$ewwun idmaren n yimdanen,');
INSERT INTO "ramdane" VALUES(114,6,'114:6','si leonun u seg yimdanen.');
