INSERT INTO "rawai" VALUES(56,64,'56:64','তোমরা তা অঙ্কুরিত কর, না আমি অঙ্কুরিত করি?');
INSERT INTO "rawai" VALUES(56,65,'56:65','আমি চাইলে তা খড়-কুটায় পরিণত করতে পারি, তখন তোমরা পরিতাপ করতে থাকবে-');
INSERT INTO "rawai" VALUES(56,66,'56:66','(এই বলে,) ‘নিশ্চয় আমরা দায়গ্রস্ত হয়ে গেলাম’।');
INSERT INTO "rawai" VALUES(56,67,'56:67','‘বরং আমরা মাহরূম হয়েছি’।');
INSERT INTO "rawai" VALUES(56,68,'56:68','তোমরা যে পানি পান কর সে ব্যাপারে আমাকে বল।');
INSERT INTO "rawai" VALUES(56,69,'56:69','বৃষ্টিভরা মেঘ থেকে তোমরা কি তা বর্ষণ কর, না আমি বৃষ্টি বর্ষণকারী?');
INSERT INTO "rawai" VALUES(56,70,'56:70','ইচ্ছা করলে আমি তা লবণাক্ত করে দিতে পারি: তবুও কেন তোমরা কৃতজ্ঞ হও না?');
INSERT INTO "rawai" VALUES(56,71,'56:71','তোমরা যে আগুন জ্বালাও সে ব্যাপারে আমাকে বল,');
INSERT INTO "rawai" VALUES(56,72,'56:72','তোমরাই কি এর (লাকড়ির গাছ) উৎপাদন কর, না আমি করি?');
INSERT INTO "rawai" VALUES(56,73,'56:73','একে আমি করেছি এক স্মারক ও মরুবাসীর প্রয়োজনীয় বস্তু।');
INSERT INTO "rawai" VALUES(56,74,'56:74','অতএব তোমার মহান রবের নামে তাসবীহ পাঠ কর।');
INSERT INTO "rawai" VALUES(56,75,'56:75','সুতরাং আমি কসম করছি নক্ষত্ররাজির অস্তাচলের,');
INSERT INTO "rawai" VALUES(56,76,'56:76','আর নিশ্চয় এটি এক মহাকসম, যদি তোমরা জানতে,');
INSERT INTO "rawai" VALUES(56,77,'56:77','নিশ্চয় এটি মহিমান্বিত কুরআন,');
INSERT INTO "rawai" VALUES(56,78,'56:78','যা আছে সুরক্ষিত কিতাবে,');
INSERT INTO "rawai" VALUES(56,79,'56:79','কেউ তা স্পর্শ করবে না পবিত্রগণ ছাড়া।');
INSERT INTO "rawai" VALUES(56,80,'56:80','তা সৃষ্টিকুলের রবের কাছ থেকে নাযিলকৃত।');
INSERT INTO "rawai" VALUES(56,81,'56:81','তবে কি তোমরা এই বাণী তুচ্ছ গণ্য করছ?');
INSERT INTO "rawai" VALUES(56,82,'56:82','আর তোমরা তোমাদের রিয্ক বানিয়ে নিয়েছ যে, তোমরা মিথ্যা আরোপ করবে।');
INSERT INTO "rawai" VALUES(56,83,'56:83','সুতরাং কেন নয়- যখন রূহ কণ্ঠদেশে পৌঁছে যায়?');
INSERT INTO "rawai" VALUES(56,84,'56:84','আর তখন তোমরা কেবল চেয়ে থাক।');
INSERT INTO "rawai" VALUES(56,85,'56:85','আর তোমাদের চাইতে আমি তার খুব কাছে; কিন্তু তোমরা দেখতে পাও না।');
INSERT INTO "rawai" VALUES(56,86,'56:86','তোমাদের যদি প্রতিফল দেয়া না হয়, তাহলে তোমরা কেন');
INSERT INTO "rawai" VALUES(56,87,'56:87','ফিরিয়ে আনছ না রূহকে, যদি তোমরা সত্যবাদী হও?');
INSERT INTO "rawai" VALUES(56,88,'56:88','অতঃপর সে যদি নৈকট্যপ্রাপ্তদের অন্যতম হয়,');
INSERT INTO "rawai" VALUES(56,89,'56:89','তবে তার জন্য থাকবে বিশ্রাম, উত্তম জীবনোপকরণ ও সুখময় জান্নাত।');
INSERT INTO "rawai" VALUES(56,90,'56:90','আর সে যদি হয় ডানদিকের একজন,');
INSERT INTO "rawai" VALUES(56,91,'56:91','তবে (তাকে বলা হবে), ‘তোমাকে সালাম, যেহেতু তুমি ডানদিকের একজন’।');
INSERT INTO "rawai" VALUES(56,92,'56:92','আর সে যদি হয় অস্বীকারকারী ও পথভ্রষ্ট,');
INSERT INTO "rawai" VALUES(56,93,'56:93','তবে তার মেহমানদারী হবে প্রচন্ড উত্তপ্ত পানি দিয়ে,');
INSERT INTO "rawai" VALUES(56,94,'56:94','আর জ্বলন্ত আগুনে প্রজ্জ্বলনে।');
INSERT INTO "rawai" VALUES(56,95,'56:95','নিশ্চয় এটি অবধারিত সত্য।');
INSERT INTO "rawai" VALUES(56,96,'56:96','অতএব তোমার মহান রবের নামে তাসবীহ পাঠ কর।');
INSERT INTO "rawai" VALUES(57,1,'57:1','আসমানসমূহ ও যমীনে যা কিছু আছে, সবই আল্লাহর তাসবীহ পাঠ করে। আর তিনি পরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO "rawai" VALUES(57,2,'57:2','আসমানসমূহ ও যমীনের রাজত্ব তাঁরই। তিনিই জীবন দেন এবং তিনিই মৃত্যু দেন। আর তিনি সকল কিছুর উপর সর্বশক্তিমান।');
INSERT INTO "rawai" VALUES(57,3,'57:3','তিনিই প্রথম ও শেষ এবং প্রকাশ্য ও গোপন; আর তিনি সকল বিষয়ে সম্যক অবগত।');
INSERT INTO "rawai" VALUES(57,4,'57:4','তিনিই আসমানসমূহ ও যমীন ছয় দিনে সৃষ্টি করেছেন, তারপর তিনি আরশে উঠেছেন। তিনি জানেন যমীনে যা কিছু প্রবেশ করে এবং তা থেকে যা কিছু বের হয়; আর আসমান থেকে যা কিছু অবতীর্ণ হয় এবং তাতে যা কিছু উত্থিত হয়। আর তোমরা যেখানেই থাক না কেন, তিনি তোমাদের সাথেই আছেন। আর তোমরা যা কর, আল্লাহ তার সম্যক দ্রষ্টা।');
INSERT INTO "rawai" VALUES(57,5,'57:5','আসমানসমূহ ও যমীনের রাজত্ব তাঁরই এবং আল্লাহরই দিকে সকল বিষয় প্রত্যাবর্তিত হবে।');
INSERT INTO "rawai" VALUES(57,6,'57:6','তিনি রাতকে দিনের মধ্যে প্রবেশ করান এবং দিনকে রাতের মধ্যে প্রবেশ করান। আর তিনি অন্তরসমূহের বিষয়াদি সম্পর্কে সম্যক অবগত।');
INSERT INTO "rawai" VALUES(57,7,'57:7','তোমরা আল্লাহ ও তাঁর রাসূলের প্রতি ঈমান আন এবং তিনি তোমাদেরকে যা কিছুর উত্তরাধিকারী করেছেন, তা থেকে ব্যয় কর। অতঃপর তোমাদের মধ্যে যারা ঈমান আনে ও (আল্লাহর পথে) ব্যয় করে তাদের জন্য রয়েছে বিরাট প্রতিফল।');
INSERT INTO "rawai" VALUES(57,8,'57:8','আর তোমাদের কী হলো যে, তোমরা আল্লাহর প্রতি ঈমান আনছ না? অথচ রাসূল তোমাদেরকে তোমাদের রবের প্রতি ঈমান আনতে আহবান করছে। আর তিনি তোমাদের কাছ থেকে অঙ্গীকার গ্রহণ করেছেন, যদি তোমরা মুমিন হও।');
INSERT INTO "rawai" VALUES(57,9,'57:9','তিনিই তাঁর বান্দার প্রতি সুস্পষ্ট আয়াতসমূহ নাযিল করেন, যাতে তিনি তোমাদেরকে অন্ধকার থেকে আলোর দিকে বের করে আনতে পারেন। আর নিশ্চয় আল্লাহ তোমাদের প্রতি অতিশয় দয়ালু, পরম করুণাময়।');
INSERT INTO "rawai" VALUES(57,10,'57:10','তোমাদের কী হলো যে, তোমরা আল্লাহর পথে ব্যয় করছ না ? অথচ আসমানসমূহ ও যমীনের উত্তরাধিকারতো আল্লাহরই? তোমাদের মধ্যে যারা মক্কা বিজয়ের পূর্বে ব্যয় করেছে এবং যুদ্ধ করেছে তারা সমান নয়। তারা মর্যাদায় তাদের চেয়ে শ্রেষ্ঠ, যারা পরে ব্যয় করেছে ও যুদ্ধ করেছে। তবে আল্লাহ প্রত্যেকের জন্যই কল্যাণের ওয়াদা করেছেন। আর তোমরা যা কর, সে সম্পর্কে আল্লাহ সবিশেষ অবগত।');
INSERT INTO "rawai" VALUES(57,11,'57:11','এমন কে আছে যে, আল্লাহকে উত্তম করয দিবে ? তাহলে তিনি তার জন্য তা বহুগুণে বৃদ্ধি করে দিবেন এবং তার জন্য রয়েছে সম্মানজনক প্রতিদান।');
INSERT INTO "rawai" VALUES(57,12,'57:12','সেদিন তুমি মুমিন পুরুষদের ও মুমিন নারীদের দেখতে পাবে যে, তাদের সামনে ও তাদের ডান পার্শ্বে তাদের নূর ছুটতে থাকবে। (বলা হবে) ‘আজ তোমাদের সুসংবাদ হল জান্নাত, যার তলদেশ দিয়ে নদীসমূহ প্রবাহিত, তথায় তোমরা স্থায়ী হবে। এটাই হল মহাসাফল্য।');
INSERT INTO "rawai" VALUES(57,13,'57:13','সেদিন মুনাফিক পুরুষ ও মুনাফিক নারীগণ ঈমানদারদের বলবে, ‘তোমরা আমাদের জন্য অপেক্ষা কর, তোমাদের নূর থেকে আমরা একটু নিয়ে নেই’, বলা হবে, ‘তোমরা তোমাদের পিছনে ফিরে যাও এবং নূরের সন্ধান কর,’ তারপর তাদের মাঝখানে একটি প্রাচীর স্থাপন করে দেয়া হবে, যাতে একটি দরজা থাকবে। তার ভিতরভাগে থাকবে রহমত এবং তার বহির্ভাগে থাকবে আযাব।');
INSERT INTO "rawai" VALUES(57,14,'57:14','মুনাফিকরা মুমিনদেরকে ডেকে বলবে, ‘আমরা কি তোমাদের সাথে ছিলাম না? তারা বলবে ‘হ্যাঁ, কিন্তু তোমরা নিজেরাই নিজদেরকে বিপদগ্রস্ত করেছ। আর তোমরা অপেক্ষা করেছিলে* এবং সন্দেহ পোষণ করেছিলে এবং আকাঙ্ক্ষা তোমাদেরকে প্রতারিত করেছিল, অবশেষে আল্লাহর নির্দেশ এসে গেল। আর মহা প্রতারক** তোমাদেরকে আল্লাহ সম্পর্কে প্রতারিত করেছিল। *আমাদের অমঙ্গলের। **শয়তান।');
INSERT INTO "rawai" VALUES(57,15,'57:15','সুতরাং আজ তোমাদের কাছ থেকে কোন মুক্তিপণ গ্রহণ করা হবে না এবং যারা কুফরী করেছিল তাদের কাছ থেকেও না। জাহান্নামই তোমাদের আবাসস্থল। সেটাই তোমাদের উপযুক্ত স্থান। আর কতই না নিকৃষ্ট সেই গন্তব্যস্থল!');
INSERT INTO "rawai" VALUES(57,16,'57:16','যারা ঈমান এনেছে তাদের হৃদয় কি আল্লাহর স্মরণে এবং যে সত্য নাযিল হয়েছে তার কারণে বিগলিত হওয়ার সময় হয়নি ? আর তারা যেন তাদের মত না হয়, যাদেরকে ইতঃপূর্বে কিতাব দেয়া হয়েছিল, তারপর তাদের উপর দিয়ে দীর্ঘকাল অতিক্রান্ত হল, অতঃপর তাদের অন্তরসমূহ কঠিন হয়ে গেল। আর তাদের অধিকাংশই ফাসিক।');
INSERT INTO "rawai" VALUES(57,17,'57:17','তোমরা জেনে রাখ যে, আল্লাহ্ যমীনকে তার মৃত্যুর পর পুনর্জীবিত করেন। আমি নিদর্শনসমূহ তোমাদের কাছে সুস্পষ্টভাবে বর্ণনা করেছি, আশা করা যায় তোমরা বুঝতে পারবে।');
INSERT INTO "rawai" VALUES(57,18,'57:18','নিশ্চয় দানশীল পুরুষ ও দানশীল নারী এবং যারা আল্লাহকে উত্তম করয দেয়, তাদের জন্য বহুগুণ বাড়িয়ে দেয়া হবে এবং তাদের জন্য রয়েছে সম্মানজনক প্রতিদান।');
INSERT INTO "rawai" VALUES(57,19,'57:19','আর যারা আল্লাহ ও তাঁর রাসূলদের প্রতি ঈমান আনে, তারাই তাদের রবের নিকট সিদ্দীক ও শহীদ। তাদের জন্য রয়েছে তাদের প্রতিফল এবং তাদের নূর। আর যারা কুফরী করে এবং আমার আয়াতসমূহ অস্বীকার করে, তারাই জাহান্নামের অধিবাসী।');
INSERT INTO "rawai" VALUES(57,20,'57:20','তোমরা জেনে রাখ যে, দুনিয়ার জীবন ক্রীড়া কৌতুক, শোভা-সৌন্দর্য, তোমাদের পারস্পরিক গর্ব-অহঙ্কার এবং ধন-সম্পদ ও সন্তান-সন্ততিতে আধিক্যের প্রতিযোগিতা মাত্র। এর উপমা হল বৃষ্টির মত, যার উৎপন্ন ফসল কৃষকদেরকে আনন্দ দেয়, তারপর তা শুকিয়ে যায়, তখন তুমি তা হলুদ বর্ণের দেখতে পাও, তারপর তা খড়-কুটায় পরিণত হয়। আর আখিরাতে আছে কঠিন আযাব এবং আল্লাহর পক্ষ থেকে ক্ষমা ও সন্তুষ্টি। আর দুনিয়ার জীবনটা তো ধোকার সামগ্রী ছাড়া আর কিছুই নয়।');
INSERT INTO "rawai" VALUES(57,21,'57:21','তোমরা তোমাদের রবের পক্ষ থেকে ক্ষমা ও সেই জান্নাতের দিকে প্রতিযোগিতায় অবতীর্ণ হও, যার প্রশস্ততা আসমান ও যমীনের প্রশস্ততার মত। তা প্রস্তত করা হয়েছে যারা আল্লাহ ও তাঁর রাসূলদের প্রতি ঈমান আনে তাদের জন্য। এটা আল্লাহর অনুগ্রহ। তিনি যাকে ইচ্ছা তা দান করেন। আর আল্লাহ মহা অনুগ্রহশীল।');
INSERT INTO "rawai" VALUES(57,22,'57:22','যমীনে এবং তোমাদের নিজদের মধ্যে এমন কোন মুসীবত আপতিত হয় না, যা আমি সংঘটিত করার পূর্বে কিতাবে লিপিবদ্ধ রাখি না। নিশ্চয় এটা আল্লাহর পক্ষে খুবই সহজ।');
INSERT INTO "rawai" VALUES(57,23,'57:23','যাতে তোমরা আফসোস না কর তার উপর যা তোমাদের থেকে হারিয়ে গেছে এবং তোমরা উৎফুল্ল না হও তিনি তোমাদেরকে যা দিয়েছেন তার কারণে। আর আল্লাহ কোন উদ্ধত ও অহঙ্কারীকে পছন্দ করেন না।');
INSERT INTO "rawai" VALUES(57,24,'57:24','যারা কৃপণতা করে এবং মানুষকে কৃপণতার নির্দেশ দেয়, আর যে মুখ ফিরিয়ে নেয়, তাহলে আল্লাহ নিশ্চয় অভাবমুক্ত, সপ্রশংসিত।');
INSERT INTO "rawai" VALUES(57,25,'57:25','নিশ্চয় আমি আমার রাসূলদেরকে স্পষ্ট প্রমাণাদিসহ পাঠিয়েছি এবং তাদের সাথে কিতাব ও (ন্যায়ের) মানদন্ড নাযিল করেছি, যাতে মানুষ সুবিচার প্রতিষ্ঠা করে। আমি আরো নাযিল করেছি লোহা, তাতে প্রচন্ড শক্তি ও মানুষের জন্য বহু কল্যাণ রয়েছে। আর যাতে আল্লাহ জেনে নিতে পারেন, কে না দেখেও তাঁকে ও তাঁর রাসূলদেরকে সাহায্য করে। অবশ্যই আল্লাহ মহাশক্তিধর, মহাপরাক্রমশালী।');
INSERT INTO "rawai" VALUES(57,26,'57:26','আর আমি তো নূহ ও ইবরাহীমকে রাসূলরূপে পাঠিয়েছিলাম এবং তাদের বংশধরদের মধ্যে নবুওয়াত ও কিতাব দিয়েছিলাম। তারপর তাদের মধ্যে কেউ কেউ সঠিক পথ অবলম্বনকারী ছিল, আর তাদের অধিকাংশই ছিল ফাসিক।');
INSERT INTO "rawai" VALUES(57,27,'57:27','তারপর তাদের পিছনে আমি আমার রাসূলদেরকে অনুগামী করেছিলাম এবং মারইয়াম পুত্র ঈসাকেও অনুগামী করেছিলাম। আর তাকে ইনজীল কিতাব দিয়েছিলাম এবং যারা তার অনুসরণ করেছিল তাদের অন্তরসমূহে করুণা ও দয়ামায়া দিয়েছিলাম। আল্লাহর সন্তুষ্টি লাভের আশায় তারাই বৈরাগ্যবাদের প্রবর্তন করেছিল। এটা আমি তাদের ওপর লিপিবদ্ধ করে দেইনি। তারপর তাও তারা যথাযথভাবে রক্ষণাবেক্ষণ করেনি। আর তাদের মধ্যে যারা ঈমান এনেছিল তাদেরকে আমি তাদের প্রতিদান দিয়েছিলাম এবং তাদের মধ্যে অধিকাংশই ছিল ফাসিক।');
INSERT INTO "rawai" VALUES(57,28,'57:28','হে মুমিনগণ, তোমরা আল্লাহকে ভয় কর এবং তাঁর রাসূলের প্রতি ঈমান আন, তিনি স্বীয় রহমতে তোমাদেরকে দ্বিগুণ পুরস্কার দেবেন, আর তোমাদেরকে নূর দেবেন যার সাহায্যে তোমরা চলতে পারবে এবং তিনি তোমাদেরকে ক্ষমা করে দেবেন। আর আল্লাহ বড়ই ক্ষমাশীল, পরম দয়ালু।');
INSERT INTO "rawai" VALUES(57,29,'57:29','(তা এজন্য যে,) আহলে কিতাবগণ যেন জেনে নিতে পারে, আল্লাহর অনুগ্রহের কোন বস্তুতেই তারা ক্ষমতা রাখে না। আর নিশ্চয় অনুগ্রহ আল্লাহর হাতেই, যাকে ইচ্ছা তিনি তা দেন। আর আল্লাহ মহাঅনুগ্রহের অধিকারী।');
INSERT INTO "rawai" VALUES(58,1,'58:1','আল্লাহ অবশ্যই সে রমনীর কথা শুনেছেন যে তার স্বামীর ব্যাপারে তোমার সাথে বাদানুবাদ করছিল আর আল্লাহর কাছে ফরিয়াদ করছিল। আল্লাহ তোমাদের কথোপকথন শোনেন। নিশ্চয় আল্লাহ সর্বশ্রোতা, সর্বদ্রষ্টা।');
INSERT INTO "rawai" VALUES(58,2,'58:2','তোমাদের মধ্যে যারা তাদের স্ত্রীদের সাথে যিহার* করে, তাদের স্ত্রীগণ তাদের মাতা নয়। তাদের মাতা তো কেবল তারাই যারা তাদেরকে জন্ম দিয়েছে। আর তারা অবশ্যই অসঙ্গত ও অসত্য কথা বলে। আর নিশ্চয় আল্লাহ অধিক পাপ মোচনকারী, বড়ই ক্ষমাশীল। * স্ত্রীকে মায়ের সাথে অথবা মায়ের কোন অংগের সাথে তুলনা করাকে ‘যিহার’ বলে। প্রাচীন আরব সমাজে স্ত্রীকে মায়ের সাথে তুলনা করার মাধ্যমে বৈবাহিক সম্পর্ক ছিন্ন করা হত। ইসলামে এর মাধ্যমে সরাসরি বৈবাহিক সম্পর্ক ছিন্ন হয় না। তবে অসঙ্গত কথা বলার কারণে কাফ্ফারা দিতে হয়।');
INSERT INTO "rawai" VALUES(58,3,'58:3','আর যারা তাদের স্ত্রীদের সাথে ‘যিহার’ করে অতঃপর তারা যা বলেছে তা থেকে ফিরে আসে, তবে একে অপরকে স্পর্শ করার পূর্বে একটি দাস মুক্ত করবে। এর মাধ্যমে তোমাদেরকে উপদেশ দেয়া হচ্ছে। আর তোমরা যা কর, সে সম্পর্কে আল্লাহ সম্যক অবহিত।');
INSERT INTO "rawai" VALUES(58,4,'58:4','কিন্তু যে তা পাবে না, সে লাগাতার দু’মাস সিয়াম পালন করবে, একে অপরকে স্পর্শ করার পূর্বে। আর যে (এরূপ করার) সামর্থ্য রাখে না সে ষাটজন মিসকীনকে খাবার খাওয়াবে। এ বিধান এ জন্য যে, তোমরা যাতে আল্লাহ ও তাঁর রাসূলের প্রতি ঈমান আন। আর এগুলো আল্লাহর (নির্ধারিত) সীমা এবং কাফিরদের জন্য রয়েছে যন্ত্রণাদায়ক আযাব।');
INSERT INTO "rawai" VALUES(58,5,'58:5','যারা আল্লাহ ও তাঁর রাসূলের বিরোধিতা করে তাদেরকে অপদস্থ করা হবে যেভাবে অপদস্থ করা হয়েছিল তাদের পূর্ববর্তীদেরকে। আর আমি নাযিল করেছি সুস্পষ্ট প্রমাণাদি। আর কাফিরদের জন্য রয়েছে অপমানজনক আযাব।');
INSERT INTO "rawai" VALUES(58,6,'58:6','যে দিন আল্লাহ তাদের সকলকে পুনরুজ্জীবিত করে উঠাবেন অতঃপর তারা যে আমল করেছিল তা তাদেরকে জানিয়ে দেবেন। আল্লাহ তা হিসাব করে রেখেছেন যদিও তারা তা ভুলে গেছে। আর আল্লাহ প্রত্যেক বিষয়ে প্রত্যক্ষদর্শী।');
INSERT INTO "rawai" VALUES(58,7,'58:7','তুমি কি লক্ষ্য করনি যে, আসমানসমূহ ও যমীনে যা কিছু আছে নিশ্চয় আল্লাহ তা জানেন? তিন জনের কোন গোপন পরামর্শ হয় না যাতে চতুর্থজন হিসেবে আল্লাহ থাকেন না, আর পাঁচ জনেরও হয় না, যাতে ষষ্ঠজন হিসেবে তিনি থাকেন না। এর চেয়ে কম হোক কিংবা বেশি হোক, তিনি তো তাদের সঙ্গেই আছেন, তারা যেখানেই থাকুক না কেন। তারপর কিয়ামতের দিন তিনি তাদেরকে তাদের কৃতকর্ম সম্পর্কে জানিয়ে দেবেন। নিশ্চয় আল্লাহ সব বিষয়ে সম্যক অবগত।');
INSERT INTO "rawai" VALUES(58,8,'58:8','তুমি কি তাদের প্রতি লক্ষ্য করনি, যাদেরকে গোপন পরামর্শ করতে নিষেধ করা হয়েছিল? তারপরও তারা তারই পুনরাবৃত্তি করল যা করতে তাদেরকে নিষেধ করা হয়েছিল। আর তারা পাপাচার, সীমালঙ্ঘন ও রাসূলের বিরুদ্ধাচরণের জন্য গোপন পরামর্শ করে। আর তারা যখন তোমার কাছে আসে তখন তারা তোমাকে এমন (কথার দ্বারা) অভিবাদন জানায় যেভাবে আল্লাহ তোমাকে অভিবাদন করেননি। আর তারা মনে মনে বলে, ‘আমরা যা বলি তার জন্য আল্লাহ আমাদেরকে শাস্তি দেন না কেন? তাদের জন্য জাহান্নামই যথেষ্ট। তারা তাতে প্রবেশ করবে। আর তা কতইনা নিকৃষ্ট গন্তব্যস্থল!');
INSERT INTO "rawai" VALUES(58,9,'58:9','হে মুমিনগণ, তোমরা যখন গোপনে পরামর্শ করবে তখন তোমরা যেন গুনাহ, সীমালঙ্ঘন ও রাসূলের বিরুদ্ধাচরণের ব্যাপারে গোপন পরামর্শ না কর। আর তোমরা সৎকর্ম ও তাকওয়ার বিষয়ে গোপন পরামর্শ কর। তোমরা আল্লাহকে ভয় কর, যাঁর কাছে তোমাদেরকে সমবেত করা হবে।');
INSERT INTO "rawai" VALUES(58,10,'58:10','গোপন পরামর্শ তো হল মুমিনরা যাতে দুঃখ পায় সে উদ্দেশ্যে কৃত শয়তানের কুমন্ত্রণা মাত্র। আর আল্লাহর অনুমতি ছাড়া সে তাদের কিছুই ক্ষতি করতে পারে না। অতএব আল্লাহরই ওপর মুমিনরা যেন তাওয়াক্কুল করে।');
INSERT INTO "rawai" VALUES(58,11,'58:11','হে মুমিনগণ, তোমাদেরকে যখন বলা হয়, ‘মজলিসে স্থান করে দাও’, তখন তোমরা স্থান করে দেবে। আল্লাহ তোমাদের জন্য স্থান করে দেবেন। আর যখন তোমাদেরকে বলা হয়, ‘তোমরা উঠে যাও’, তখন তোমরা উঠে যাবে। তোমাদের মধ্যে যারা ঈমান এনেছে এবং যাদেরকে জ্ঞান দান করা হয়েছে আল্লাহ তাদেরকে মর্যাদায় সমুন্নত করবেন। আর তোমরা যা কর আল্লাহ সে সম্পর্কে সম্যক অবহিত।');
INSERT INTO "rawai" VALUES(58,12,'58:12','হে মুমিনগণ, তোমরা যখন রাসূলের সাথে একান্তে কথা বলতে চাও, তখন তোমাদের এরূপ কথার পূর্বে কিছু সদাকা পেশ কর। এটি তোমাদের জন্য শ্রেয়তর ও পবিত্রতর; কিন্তু যদি তোমরা সক্ষম না হও তবে আল্লাহ পরম ক্ষমাশীল, অতিশয় দয়ালু।');
INSERT INTO "rawai" VALUES(58,13,'58:13','তোমরা কি ভয় পেয়ে গেলে যে, একান্ত পরামর্শের পূর্বে সদাকা পেশ করবে? হ্যাঁ, যখন তোমরা তা করতে পারলে না, আর আল্লাহও তোমাদের ক্ষমা করে দিলেন, তখন তোমরা সালাত কায়েম কর, যাকাত দাও এবং আল্লাহ ও তাঁর রাসূলের আনুগত্য কর। তোমরা যা কর, আল্লাহ সে সম্পর্কে সম্যক অবগত।');
INSERT INTO "rawai" VALUES(58,14,'58:14','তুমি কি তাদের লক্ষ্য করনি, যারা এমন এক কওমের সাথে বন্ধুত্ব করে যাদের উপর আল্লাহর গযব নিপতিত হয়েছে? তারা তোমাদের দলভুক্ত নয় এবং তোমরাও তাদের দলভুক্ত নও। আর তারা জেনে শুনেই মিথ্যার উপর কসম করে।');
INSERT INTO "rawai" VALUES(58,15,'58:15','আল্লাহ তাদের জন্য কঠোর আযাব প্রস্তুত করে রেখেছেন। নিশ্চয় তারা যা করত তা কতইনা মন্দ!');
INSERT INTO "rawai" VALUES(58,16,'58:16','তারা তাদের কসমগুলোকে ঢাল হিসেবে নিয়েছে। অতঃপর তারা আল্লাহর পথে বাধা প্রদান করেছে। ফলে তাদের জন্য রয়েছে লাঞ্ছনাদায়ক আযাব।');
INSERT INTO "rawai" VALUES(58,17,'58:17','আল্লাহর বিপরীতে তাদের ধন-সম্পদ ও সন্তান-সন্ততি তাদের আদৌ কোন কাজে আসবে না। এরাই জাহান্নামের অধিবাসী। তারা সেখানে স্থায়ী হবে।');
INSERT INTO "rawai" VALUES(58,18,'58:18','সেদিন আল্লাহ তাদের সকলকে পুনরুত্থিত করবেন, তখন তারা আল্লাহর নিকট এমন কসম করবে যেমন কসম তোমাদের নিকট করে থাকে এবং তারা মনে করে যে, তারা কোন কিছুর উপর আছে। জেনে রাখ, নিশ্চয় এরা মিথ্যাবাদী।');
INSERT INTO "rawai" VALUES(58,19,'58:19','শয়তান এদের ওপর চেপে বসেছে এবং তাদেরকে আল্লাহর যিকির ভুলিয়ে দিয়েছে। এরাই শয়তানের দল। জেনে রাখ, নিশ্চয় শয়তানের দল ক্ষতিগ্রস্ত।');
INSERT INTO "rawai" VALUES(58,20,'58:20','নিশ্চয় যারা আল্লাহ ও তাঁর রাসূলের বিরোধিতা করে তারা চরম লাঞ্ছিতদের অন্তর্ভুক্ত।');
INSERT INTO "rawai" VALUES(58,21,'58:21','আল্লাহ লিখে দিয়েছেন যে, ‘আমি ও আমার রাসূলগণ অবশ্যই বিজয়ী হব।’ নিশ্চয় আল্লাহ মহা শক্তিমান, মহা পরাক্রমশালী।');
INSERT INTO "rawai" VALUES(58,22,'58:22','তুমি পাবে না এমন জাতিকে যারা আল্লাহ ও পরকালের প্রতি ঈমান আনে, বন্ধুত্ব করে তার সাথে যে আল্লাহ ও তাঁর রাসূলের বিরোধীতা করে, যদিও তারা তাদের পিতা, অথবা পুত্র, অথবা ভাই, অথবা জ্ঞাতি-গোষ্ঠী হয়। এরাই, যাদের অন্তরে আল্লাহ ঈমান লিখে দিয়েছেন এবং তাঁর পক্ষ থেকে রূহ দ্বারা তাদের শক্তিশালী করেছেন। তিনি তাদের প্রবেশ করাবেন এমন জান্নাতসমূহে যার নিচে দিয়ে ঝর্ণাধারাসমূহ প্রবাহিত হয়। সেখানে তারা স্থায়ী হবে। আল্লাহ তাদের প্রতি সন্তুষ্ট হয়েছেন এবং তারাও আল্লাহর প্রতি সন্তুষ্ট হয়েছে। এরাই আল্লাহর দল। জেনে রাখ, নিশ্চয় আল্লাহর দলই সফলকাম।');
INSERT INTO "rawai" VALUES(59,1,'59:1','আসমানসমূহে ও যমীনে যা কিছু আছে সবই আল্লাহর তাসবীহ পাঠ করছে এবং তিনি মহা পরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO "rawai" VALUES(59,2,'59:2','আহলে কিতাবদের মধ্যে যারা কুফরী করেছিল তিনিই তাদেরকে তাদের ঘর-বাড়ী থেকে বের করে দিয়েছিলেন প্রথমবারের মত। তোমরা ধারণাও করনি যে, তারা বেরিয়ে যাবে। আর তারা ধারণা করেছিল যে, তাদের দুর্গগুলো তাদেরকে আল্লাহর আযাব থেকে রক্ষা করবে। কিন্তু আল্লাহর আযাব এমন এক দিক থেকে আসল যা তারা কল্পনাও করতে পারেনি এবং তিনি তাদের অন্তরসমূহে ত্রাসের সঞ্চার করলেন, ফলে তারা তাদের বাড়ী-ঘর আপন হাতে ও মুমিনদের হাতে ধ্বংস করতে শুরু করল। অতএব হে দৃষ্টিমান লোকেরা তোমরা উপদেশ গ্রহণ কর।’');
INSERT INTO "rawai" VALUES(59,3,'59:3','আর আল্লাহ যদি তাদের জন্য নির্বাসন লিপিবদ্ধ না করতেন, তবে তিনি তাদেরকে দুনিয়াতে শাস্তি দিতেন এবং তাদের জন্য আখিরাতে রয়েছে আগুনের শাস্তি।');
INSERT INTO "rawai" VALUES(59,4,'59:4','এটি এ জন্য যে, তারা সত্যিই আল্লাহ ও তাঁর রাসূলের বিরুদ্ধাচরণ করেছিল। আর যে আল্লাহর বিরুদ্ধাচরণ করে, তবে নিশ্চয় আল্লাহ আযাব প্রদানে কঠোর।');
INSERT INTO "rawai" VALUES(59,5,'59:5','তোমরা যে সব নতুন খেজুর গাছ কেটে ফেলছ অথবা সেগুলোকে তাদের মূলের ওপর দাঁড়িয়ে থাকতে দিয়েছ। তা তো ছিল আল্লাহর অনুমতিক্রমে এবং যাতে তিনি ফাসিকদের লাঞ্ছিত করতে পারেন।');
INSERT INTO "rawai" VALUES(59,6,'59:6','আল্লাহ ইয়াহুদীদের নিকট থেকে তাঁর রাসূলকে ফায়* হিসেবে যা দিয়েছেন তোমরা তার জন্য কোন ঘোড়া বা উটে আরোহণ করে অভিযান পরিচালনা করনি। বরং আল্লাহ তাঁর রাসূলগণকে যাদের ওপর ইচ্ছা কতৃত্ব প্রদান করেন। আল্লাহ সকল কিছুর ওপর সর্বশক্তিমান। *যুদ্ধ ছাড়াই যে ধন-সম্পদ অর্জিত হয় তাকে في বলে। এটি সাধারণত বায়তুল মালে জমা রাখা হয় এবং রাসূল স্বীয় তত্ত্বাবধানে রাষ্ট্রীয় প্রয়োজনে তা ব্যবহার করতেন। আর যুদ্ধের মাধ্যমে যা অর্জিত হয় তাকে গনিমত غنيمت বলা হয়।');
INSERT INTO "rawai" VALUES(59,7,'59:7','আল্লাহ জনপদবাসীদের নিকট থেকে তাঁর রাসূলকে ফায় হিসেবে যা দিয়েছেন তা আল্লাহর, রাসূলের, আত্মীয়-স্বজনদের, ইয়াতীমদের, মিসকীন ও মুসাফিরদের এটি এ জন্য যে, যাতে ধন-সম্পদ তোমাদের মধ্যকার বিত্তশালীদের মাঝেই কেবল আবর্তিত না থাকে। রাসূল তোমাদের যা দেয় তা গ্রহণ কর, আর যা থেকে সে তোমাদের নিষেধ করে তা থেকে বিরত হও এবং আল্লাহকেই ভয় কর, নিশ্চয় আল্লাহ শাস্তি প্রদানে কঠোর।');
INSERT INTO "rawai" VALUES(59,8,'59:8','এই সম্পদ নিঃস্ব মুহাজিরগণের জন্য ও যাদেরকে নিজেদের ঘর-বাড়ী ও ধন-সম্পত্তি থেকে বের করে দেয়া হয়েছিল। অথচ এরা আল্লাহর অনুগ্রহ ও সন্তুষ্টির অন্বেষণ করে এবং আল্লাহ ও তাঁর রাসূলকে সাহায্য করেন। এরাই তো সত্যবাদী।');
INSERT INTO "rawai" VALUES(59,9,'59:9','আর মুহাজিরদের আগমনের পূর্বে যারা মদীনাকে নিবাস হিসেবে গ্রহণ করেছিল এবং ঈমান এনেছিল (তাদের জন্যও এ সম্পদে অংশ রয়েছে), আর যারা তাদের কাছে হিজরত করে এসেছে তাদেরকে ভালবাসে। আর মুহাজরিদেরকে যা প্রদান করা হয়েছে তার জন্য এরা তাদের অন্তরে কোন ঈর্ষা অনুভব করে না। এবং নিজেদের অভাব থাকা সত্ত্বেও নিজেদের ওপর তাদেরকে অগ্রাধিকার দেয়। যাদের মনের কার্পণ্য থেকে রক্ষা করা হয়েছে, তারাই সফলকাম।');
INSERT INTO "rawai" VALUES(59,10,'59:10','যারা তাদের পরে এসেছে তারা বলে: ‘হে আমাদের রব, আমাদেরকে ও আমাদের ভাই যারা ঈমান নিয়ে আমাদের পূর্বে অতিক্রান্ত হয়েছে তাদেরকে ক্ষমা করুন; এবং যারা ঈমান এনেছিল তাদের জন্য আমাদের অন্তরে কোন বিদ্বেষ রাখবেন না; হে আমাদের রব, নিশ্চয় আপনি দয়াবান, পরম দয়ালু।');
INSERT INTO "rawai" VALUES(59,11,'59:11','তুমি কি মুনাফিকদেরকে দেখনি যারা আহলে কিতাবের মধ্য হতে তাদের কাফির ভাইদেরকে বলে, ‘তোমাদেরকে বের করে দেয়া হলে আমরাও তোমাদের সাথে অবশ্য বেরিয়ে যাব এবং তোমাদের ব্যাপারে আমরা কখনোই কারো আনুগত্য করব না। আর তোমাদের সাথে যুদ্ধ করা হলে আমরা অবশ্যই তোমাদেরকে সাহায্য করব।’ আর আল্লাহ সাক্ষ্য দিচ্ছেন যে, তারা মিথ্যাবাদী।');
INSERT INTO "rawai" VALUES(59,12,'59:12','তারা (ইহুদিরা) যদি বহিষ্কৃত হয় তবে এরা (মুনাফিকরা) কখনো তাদের সাথে বেরিয়ে যাবে না আর তাদের (ইয়াহুদীদের) সাথে যদি যুদ্ধ করা হয় এরা (মুনাফিকরা) কখনো তাদেরকে সাহায্য করবে না। আর যদি তাদেরকে সাহায্য করে তবে তারা অবশ্যই পিঠ দেখিয়ে পালাবে; এরপর তারা কোন সাহায্যই পাবে না।');
INSERT INTO "rawai" VALUES(59,13,'59:13','প্রকৃতপক্ষে তাদের অন্তরে আল্লাহর চাইতে তোমাদের ভয় বেশী; এটা এ কারণে যে, তারা অবুঝ সম্প্রদায়।');
INSERT INTO "rawai" VALUES(59,14,'59:14','তারা সম্মিলিতিভাবে তোমাদের বিরুদ্ধে যুদ্ধ করবে না তবে সুরক্ষিত জনপদের মধ্যে অবস্থান করে বা দেয়ালের পেছন হতে; তারা নিজেরা নিজেদেরকে প্রবল শক্তিধর মনে করে; তুমি তাদেরকে ঐক্যবদ্ধ মনে করছ অথচ তাদের অন্তরসমূহ বিচ্ছিন্ন। এটি এজন্য যে, তারা নির্বোধ সম্প্রদায়।');
INSERT INTO "rawai" VALUES(59,15,'59:15','তাদের অব্যবহিত পূর্বসূরিদের ন্যায়, যারা নিজেদের কৃতকর্মের কুফল আস্বাদন করেছে; আর তাদের জন্য রয়েছে যন্ত্রণাদায়ক আযাব।');
INSERT INTO "rawai" VALUES(59,16,'59:16','(এরা) শয়তান-এর ন্যায়, সে মানুষকে বলেছিল, ‘কুফরি কর’, অতঃপর যখন সে কুফরি করল তখন সে বলল, আমি তোমার থেকে মুক্ত; নিশ্চয় আমি সকল সৃষ্টির রব আল্লাহকে ভয় করি।');
INSERT INTO "rawai" VALUES(59,17,'59:17','তাদের দু’জনের পরিণতি ছিল এই যে, তারা দু’জনেই জাহান্নামী হবে, সেখানে তারা স্থায়ী হবে; আর এটাই যালিমদের প্রতিদান।');
INSERT INTO "rawai" VALUES(59,18,'59:18','হে ঈমানদারগণ, তোমরা আল্লাহকে ভয় কর; আর প্রত্যেকের উচিত চিন্তা করে দেখা সে আগামীকালের জন্য কি প্রেরণ করেছে; তোমরা আল্লাহকে ভয় কর। তোমরা যা কর নিশ্চয় আল্লাহ সে বিষয়ে সম্যক অবহিত।');
INSERT INTO "rawai" VALUES(59,19,'59:19','তোমরা তাদের মত হইও না, যারা আল্লাহকে ভুলে গিয়েছিল ফলে আল্লাহও তাদেরকে আত্মবিস্মৃত করে দিয়েছিলেন; আর তারাই হল ফাসিক।');
INSERT INTO "rawai" VALUES(59,20,'59:20','জাহান্নামবাসী ও জান্নাতবাসীরা সমান নয়; জান্নাতবাসীরাই সফলকাম।');
INSERT INTO "rawai" VALUES(59,21,'59:21','এ কুরআনকে যদি আমি পাহাড়ের ওপর নাযিল করতাম তবে তুমি অবশ্যই তাকে দেখতে, আল্লাহর ভয়ে বিনীত ও বিদীর্ণ। মানুষের জন্য আমি এ উদাহরণগুলি পেশ করি; হয়ত তারা চিন্তাভাবনা করবে।');
INSERT INTO "rawai" VALUES(59,22,'59:22','তিনিই আল্লাহ, যিনি ছাড়া কোন ইলাহ নেই; দৃশ্য-অদৃশ্যের জ্ঞাতা; তিনিই পরম করুণাময়, দয়ালু।');
INSERT INTO "rawai" VALUES(59,23,'59:23','তিনিই আল্লাহ; যিনি ছাড়া কোন ইলাহ নেই, তিনিই বাদশাহ, মহাপবিত্র, ত্রুটিমুক্ত, নিরাপত্তাদানকারী, রক্ষক, মহাপরাক্রমশালী, মহাপ্রতাপশালী, অতীব মহিমান্বিত, তারা যা শরীক করে তা হতে পবিত্র মহান।');
INSERT INTO "rawai" VALUES(59,24,'59:24','তিনিই আল্লাহ, স্রষ্টা, উদ্ভাবনকর্তা, আকৃতিদানকারী; তাঁর রয়েছে সুন্দর নামসমূহ; আসমান ও যমীনে যা আছে সবই তার মহিমা ঘোষণা করে। তিনি মহাপরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO "rawai" VALUES(60,1,'60:1','হে ঈমানদারগণ, তোমরা আমার ও তোমাদের শত্রুদেরকে বন্ধুরূপে গ্রহণ করে তাদের প্রতি ভালোবাসা প্রদর্শন করো না, অথচ তোমাদের কাছে যে সত্য এসেছে তা তারা অস্বীকার করেছে এবং রাসূলকে ও তোমাদেরকে বের করে দিয়েছে এজন্য যে, তোমরা তোমাদের রব আল্লাহর প্রতি ঈমান এনেছ। তোমরা যদি আমার পথে সংগ্রামে ও আমার সন্তুষ্টির সন্ধানে বের হও (তবে কাফিরদেরকে বন্ধুরূপে গ্রহণ করো না) তোমরা গোপনে তাদের সাথে বন্ধুত্ব প্রকাশ কর অথচ তোমরা যা গোপন কর এবং যা প্রকাশ কর তা আমি জানি। তোমাদের মধ্যে যে এমন করবে সে সরল পথ হতে বিচ্যুত হবে।');
INSERT INTO "rawai" VALUES(60,2,'60:2','তারা যদি তোমাদেরকে বাগে পায় তবে তোমাদের শত্রু হবে এবং মন্দ নিয়ে তোমাদের দিকে তাদের হাত ও যবান বাড়াবে; তারা কামনা করে যদি তোমরা কুফরি করতে!');
INSERT INTO "rawai" VALUES(60,3,'60:3','কিয়ামত দিবসে তোমাদের আত্নীয়তা ও সন্তান-সন্ততি তোমাদের কোন উপকার করতে পারবে না। তিনি তোমাদের মাঝে ফায়সালা করে দেবেন। তোমরা যা কর আল্লাহ তার সম্যক দ্রষ্টা।');
INSERT INTO "rawai" VALUES(60,4,'60:4','ইবরাহীম ও তার সাথে যারা ছিল তাদের মধ্যে তোমাদের জন্য রয়েছে উত্তম আদর্শ। তারা যখন স্বীয় সম্প্রদায়কে বলছিল, ‘তোমাদের সাথে এবং আল্লাহর পরিবর্তে তোমরা যা কিছুর উপাসনা কর তা হতে আমরা সম্পর্কমুক্ত। আমরা তোমাদেরকে অস্বীকার করি; এবং উদ্রেক হল আমাদের- তোমাদের মাঝে শত্রুতা ও বিদ্বেষ চিরকালের জন্য; যতক্ষণ না তোমরা এক আল্লাহর প্রতি ঈমান আন। তবে স্বীয় পিতার প্রতি ইবরাহীমের উক্তিটি ব্যতিক্রম: ‘আমি অবশ্যই তোমার জন্য আল্লাহর কাছে ক্ষমা প্রার্থনা করব আর তোমার ব্যাপারে আল্লাহর কাছে আমি কোন অধিকার রাখি না।’ হে আমাদের প্রতিপালক, আমরা আপনার ওপরই ভরসা করি, আপনারই অভিমুখী হই আর প্রত্যাবর্তন তো আপনারই কাছে।');
INSERT INTO "rawai" VALUES(60,5,'60:5','হে আমাদের রব, আপনি আমাদেরকে কাফিরদের উৎপীড়নের পাত্র বানাবেন না। হে আমাদের রব, আপনি আমাদের ক্ষমা করে দিন। নিশ্চয় আপনি মহাপরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO "rawai" VALUES(60,6,'60:6','নিশ্চয় তোমাদের জন্য তাদের মধ্যে* উত্তম আদর্শ রয়েছে, যারা আল্লাহ ও শেষ দিবসের প্রত্যাশা করে, আর যে মুখ ফিরিয়ে নেয়, (সে জেনে রাখুক) নিশ্চয় আল্লাহ তো অভাবমুক্ত, সপ্রশংসিত। *ইবরাহীম আ. ও তাঁর অনুসারীদের মধ্যে');
INSERT INTO "rawai" VALUES(60,7,'60:7','যাদের সাথে তোমরা শত্রুতা করছ, আশা করা যায় আল্লাহ তোমাদের ও তাদের মধ্যে বন্ধুত্ব সৃষ্টি করে দেবেন। আর আল্লাহ সর্ব শক্তিমান এবং আল্লাহ অতিশয় ক্ষমাশীল, পরম দয়ালু।');
INSERT INTO "rawai" VALUES(60,8,'60:8','দীনের ব্যাপারে যারা তোমাদের বিরুদ্ধে যুদ্ধ করেনি এবং তোমাদেরকে তোমাদের বাড়ি-ঘর থেকে বের করে দেয়নি, তাদের প্রতি সদয় ব্যবহার করতে এবং তাদের প্রতি ন্যায়বিচার করতে আল্লাহ তোমাদেরকে নিষেধ করছেন না। নিশ্চয় আল্লাহ ন্যায় পরায়ণদেরকে ভালবাসেন।');
INSERT INTO "rawai" VALUES(60,9,'60:9','আল্লাহ কেবল তাদের সাথেই বন্ধুত্ব করতে নিষেধ করেছেন, যারা দীনের ব্যাপারে তোমাদের বিরুদ্ধে যুদ্ধ করেছে এবং তোমাদেরকে তোমাদের ঘর-বাড়ী থেকে বের করে দিয়েছে ও তোমাদেরকে বের করে দেয়ার ব্যাপারে সহায়তা করেছে। আর যারা তাদের সাথে বন্ধুত্ব করে, তারাই তো যালিম।');
INSERT INTO "rawai" VALUES(60,10,'60:10','হে ঈমানদারগণ, তোমাদের কাছে মু’মিন মহিলারা হিজরত করে আসলে তোমরা তাদেরকে পরীক্ষা করে দেখ। আল্লাহ তাদের ঈমান সম্পর্কে অধিক অবগত। অতঃপর যদি তোমরা জানতে পার যে, তারা মুমিন মহিলা, তাহলে তাদেরকে আর কাফিরদের নিকট ফেরত পাঠিও না। তারা কাফিরদের জন্য বৈধ নয় এবং কাফিররাও তাদের জন্য হালাল নয়। তারা* যা ব্যয় করেছে, তা তাদেরকে ফিরিয়ে দাও। তোমরা তাদেরকে বিয়ে করলে তোমাদের কোন অপরাধ হবে না, যদি তোমরা তাদেরকে তাদের মোহর প্রদান কর। আর তোমরা কাফির নারীদের সাথে বৈবাহিক সম্পর্ক বজায় রেখ না, তোমরা যা ব্যয় করেছ, তা তোমরা ফেরত চাও, আর তারা যা ব্যয় করেছে, তা যেন তারা চেয়ে নেয়। এটা আল্লাহর বিধান। তিনি তোমাদের মাঝে ফয়সালা করেন। আর আল্লাহ সর্বজ্ঞ, প্রজ্ঞাময়। *কাফির স্বামীরা');
INSERT INTO "rawai" VALUES(60,11,'60:11','আর তোমাদের স্ত্রীদের মধ্যে যদি কেউ হাতছাড়া হয়ে কাফিরদের নিকট চলে যায়, অতঃপর যদি তোমরা যুদ্ধজয়ী হয়ে গনীমত লাভ কর, তাহলে যাদের স্ত্রীরা চলে গেছে, তাদেরকে তারা যা ব্যয় করেছে, তার সমপরিমাণ প্রদান কর। আর তোমরা আল্লাহকে ভয় কর, যার প্রতি তোমরা বিশ্বাসী।');
INSERT INTO "rawai" VALUES(60,12,'60:12','হে নবী, যখন মুমিন নারীরা তোমার কাছে এসে এই মর্মে বাইআত করে যে, তারা আল্লাহর সাথে কোন কিছু শরীক করবে না, চুরি করবে না, ব্যভিচার করবে না, নিজেদের সন্তানদেরকে হত্যা করবে না, তারা জেনে শুনে কোন অপবাদ রচনা করে রটাবে না এবং সৎকাজে তারা তোমার অবাধ্য হবে না। তখন তুমি তাদের বাইআত গ্রহণ কর এবং তাদের জন্য আল্লাহর নিকট ক্ষমা প্রার্থনা কর। নিশ্চয় আল্লাহ অতিশয় ক্ষমাশীল, পরম দয়ালু।');
INSERT INTO "rawai" VALUES(60,13,'60:13','হে ঈমানদারগণ, তোমরা সেই সম্প্রদায়ের সাথে বন্ধুত্ব করো না, যাদের প্রতি আল্লাহ রাগান্বিত হয়েছেন। তারা তো আখিরাত সম্পর্কে নিরাশ হয়ে পড়েছে, যেমনিভাবে কাফিররা কবরবাসীদের সম্পর্কে নিরাশ হয়েছে।');
INSERT INTO "rawai" VALUES(61,1,'61:1','আসমানসমূহে যা কিছু আছে ও যমীনে যা কিছু আছে, সবই আল্লাহর তাসবীহ পাঠ করছে। আর তিনি মহাপরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO "rawai" VALUES(61,2,'61:2','হে ঈমানদারগণ, তোমরা তা কেন বল, যা তোমরা কর না?');
INSERT INTO "rawai" VALUES(61,3,'61:3','তোমরা যা কর না, তা বলা আল্লাহর নিকট বড়ই ক্রোধের বিষয়।');
INSERT INTO "rawai" VALUES(61,4,'61:4','নিশ্চয় আল্লাহ তাদেরকে ভালবাসেন, যারা তাঁর পথে সারিবদ্ধ হয়ে যুদ্ধ করে যেন তারা সীসা ঢালা প্রাচীর।');
INSERT INTO "rawai" VALUES(61,5,'61:5','আর মূসা যখন তার সম্প্রদায়কে বলেছিল, ‘হে আমার সম্প্রদায়, তোমরা আমাকে কেন কষ্ট দিচ্ছ, অথচ তোমরা নিশ্চয় জান যে, আমি অবশ্যই তোমাদের নিকট আল্লাহর রাসূল’। অতঃপর তারা যখন বাঁকাপথ অবলম্বন করল, তখন আল্লাহ তাদের হৃদয়গুলোকে বাঁকা করে দিলেন। আর আল্লাহ পাপাচারী সম্প্রদায়কে হিদায়াত করেন না।');
INSERT INTO "rawai" VALUES(61,6,'61:6','আর যখন মারইয়াম পুত্র ঈসা বলেছিল, ‘হে বনী ইসরাঈল, নিশ্চয় আমি তোমাদের নিকট আল্লাহর রাসূল। আমার পূর্ববর্তী তাওরাতের সত্যায়নকারী এবং একজন রাসূলের সুসংবাদদাতা যিনি আমার পরে আসবেন, যার নাম আহমদ’। অতঃপর সে যখন সুস্পষ্ট নিদর্শনসমূহ নিয়ে আগমন করল, তখন তারা বলল, ‘এটাতো স্পষ্ট যাদু’।');
INSERT INTO "rawai" VALUES(61,7,'61:7','সেই ব্যক্তির চেয়ে অধিক যালিম আর কে? যে আল্লাহ সম্পর্কে মিথ্যা রচনা করে, অথচ তাকে ইসলামের দিকে আহবান করা হয়। আর আল্লাহ যালিম সম্প্রদায়কে হিদায়াত করেন না।');
INSERT INTO "rawai" VALUES(61,8,'61:8','তারা তাদের মুখের ফুৎকারে আল্লাহর নূরকে নিভিয়ে দিতে চায়, কিন্তু আল্লাহ তাঁর নূরকে পূর্ণতাদানকারী। যদিও কাফিররা তা অপছন্দ করে।');
INSERT INTO "rawai" VALUES(61,9,'61:9','তিনিই তাঁর রাসূলকে হিদায়াত ও সত্যদ্বীন দিয়ে প্রেরণ করেছেন, যাতে তিনি সকল দ্বীনের উপর তা বিজয়ী করে দেন। যদিও মুশরিকরা তা অপছন্দ করে।');
INSERT INTO "rawai" VALUES(61,10,'61:10','হে ঈমানদারগণ, আমি কি তোমাদেরকে এমন এক ব্যবসায়ের সন্ধান দেব, যা তোমাদেরকে যন্ত্রণাদায়ক আযাব থেকে রক্ষা করবে?');
INSERT INTO "rawai" VALUES(61,11,'61:11','তোমরা আল্লাহর প্রতি ও তাঁর রাসূলের প্রতি ঈমান আনবে এবং তোমরা তোমাদের ধন-সম্পদ ও জীবন দিয়ে আল্লাহর পথে জিহাদ করবে। এটাই তোমাদের জন্য কল্যাণকর, যদি তোমরা জানতে।');
INSERT INTO "rawai" VALUES(61,12,'61:12','তিনি তোমাদের জন্য তোমাদের পাপসমূহ ক্ষমা করে দিবেন। আর তোমাদেরকে এমন জান্নাতসমূহে প্রবেশ করাবেন যার তলদেশে নহরসমূহ প্রবাহিত এবং চিরস্থায়ী জান্নাতসমূহে উত্তম আবাসগুলোতেও (প্রবেশ করবেন)। এটাই মহাসাফল্য।');
INSERT INTO "rawai" VALUES(61,13,'61:13','এবং আরো একটি (অর্জন) যা তোমরা খুব পছন্দ কর। (অর্থাৎ) আল্লাহর পক্ষ থেকে সাহায্য ও নিকটবর্তী বিজয়। আর মুমিনদেরকে তুমি সুসংবাদ দাও।');
INSERT INTO "rawai" VALUES(61,14,'61:14','হে মুমিনগণ, তোমরা আল্লাহর সাহায্যকারী হও। যেমন মারইয়াম পুত্র ঈসা হাওয়ারীদেরকে* বলেছিল, আল্লাহর পথে কারা আমার সাহায্যকারী হবে? হাওয়ারীগণ বলল, আমরাই আল্লাহর সাহায্যকারী। তারপর বনী-ঈসরাইলের মধ্য থেকে একদল ঈমান আনল এবং অপর এক দল প্রত্যাখ্যান করল। অতঃপর যারা ঈমান আনল আমি তাদেরকে তাদের শত্রুবাহিনীর ওপর শক্তিশালী করলাম। ফলে তারা বিজয়ী হল। *ঈসাঃ (আঃ) এর খাস অনুসারীদেরকে হাওয়ারী বলা হত।');
INSERT INTO "rawai" VALUES(62,1,'62:1','আসমানসমূহে এবং যমীনে যা আছে সবই পবিত্রতা ঘোষণা করে আল্লাহর। যিনি বাদশাহ, মহাপবিত্র, মহাপরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO "rawai" VALUES(62,2,'62:2','তিনিই উম্মীদের* মাঝে একজন রাসূল পাঠিয়েছেন তাদের মধ্য থেকে, যে তাদের কাছে তেলাওয়াত করে তাঁর আয়াতসমূহ, তাদেরকে পবিত্র করে এবং তাদেরকে শিক্ষা দেয় কিতাব ও হিকমাত। যদিও ইতঃপূর্বে তারা স্পষ্ট গোমরাহীতে ছিল। *উম্মী দ্বারা আরবের লোকদেরকে বুঝানো হয়েছে।');
INSERT INTO "rawai" VALUES(62,3,'62:3','এবং তাদের মধ্য হতে অন্যান্যদের জন্যও, (এ রাসূলকেই পাঠানো হয়েছে) যারা এখনো তাদের সাথে মিলিত হয়নি। আর তিনিই মহা পরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO "rawai" VALUES(62,4,'62:4','এটা আল্লাহর অনুগ্রহ, যাকে ইচ্ছা তিনি তা দান করেন। আর আল্লাহ মহা অনুগ্রহের অধিকারী।');
INSERT INTO "rawai" VALUES(62,5,'62:5','যাদেরকে তাওরাতের দায়িত্বভার দেয়া হয়েছিল তারপর তারা তা বহন করেনি, তারা গাধার মত! যে বহু কিতাবের বোঝা বহন করে। সে সম্প্রদায়ের উপমা কতইনা নিকৃষ্ট, যারা আল্লাহর আয়াতসমূহকে অস্বীকার করে। আর আল্লাহ যালিম সম্প্রদায়কে হিদায়াত করেন না।');
INSERT INTO "rawai" VALUES(62,6,'62:6','বল, হে ইয়াহুদীরা, যদি তোমরা মনে কর যে, (অন্য) মানুষেরা নয়, কেবল তোমরাই আল্লাহর বন্ধু, তাহলে তোমরা মৃত্যু কামনা কর, যদি তোমরা সত্যবাদী হও।');
INSERT INTO "rawai" VALUES(62,7,'62:7','আর তারা, তাদের হাত যা আগে পাঠিয়েছে সে কারণে কখনো মৃত্যু কামনা করবে না। আর আল্লাহ যালিমদের সম্পর্কে সম্যক অবহিত।');
INSERT INTO "rawai" VALUES(62,8,'62:8','বল যে মৃত্যু হতে তোমরা পলায়ন করছ তা অবশ্যই তোমাদের সাথে সাক্ষাৎ করবে। তারপর তোমাদেরকে অদৃশ্য ও দৃশ্য সম্পর্কে পরিজ্ঞাত আল্লাহর কাছে ফিরিয়ে নেয়া হবে। তারপর তিনি তোমাদেরকে জানিয়ে দেবেন যা তোমরা করতে।');
INSERT INTO "rawai" VALUES(62,9,'62:9','হে মুমিনগণ, যখন জুমু‘আর দিনে সালাতের জন্য আহবান করা হয়, তখন তোমরা আল্লাহর স্মরণের দিকে ধাবিত হও। আর বেচা-কেনা বর্জন কর। এটাই তোমাদের জন্য সর্বোত্তম, যদি তোমরা জানতে।');
INSERT INTO "rawai" VALUES(62,10,'62:10','অতঃপর যখন সালাত সমাপ্ত হবে তখন তোমরা পৃথিবীতে ছড়িয়ে পড় আর আল্লাহর অনুগ্রহ হতে অনুসন্ধান কর এবং আল্লাহকে বেশি বেশি স্মরণ কর, যাতে তোমরা সফল হতে পার।');
INSERT INTO "rawai" VALUES(62,11,'62:11','আর তারা যখন ব্যবসায় অথবা ক্রীড়া কৌতুক দেখে তখন তারা তার দিকে ছুটে যায়, আর তোমাকে দাঁড়ান অবস্থায় রেখে যায়। বল, আল্লাহর কাছে যা আছে তা ক্রীড়া- কৌতুক ও ব্যবসায় অপেক্ষা উত্তম। আর আল্লাহ সর্বোত্তম রিযকদাতা।');
INSERT INTO "rawai" VALUES(63,1,'63:1','যখন তোমার কাছে মুনাফিকরা আসে, তখন বলে, আমরা সাক্ষ্য দিচ্ছি যে, নিশ্চয় আপনি আল্লাহর রাসূল এবং আল্লাহ জানেন যে, অবশ্যই তুমি তাঁর রাসূল। আর আল্লাহ সাক্ষ্য দিচ্ছেন যে, অবশ্যই মুনাফিকরা মিথ্যাবাদী।');
INSERT INTO "rawai" VALUES(63,2,'63:2','তারা নিজদের শপথকে ঢাল হিসেবে ব্যবহার করে। অতঃপর তারা আল্লাহর পথ থেকে বিরত রাখে। তারা যা করছে, নিশ্চিয় তা কতইনা মন্দ!');
INSERT INTO "rawai" VALUES(63,3,'63:3','তা এ জন্য যে, তারা ঈমান এনেছিল তারপর কুফরী করেছিল। ফলে তাদের অন্তরসমূহে মোহর লাগিয়ে দেয়া হয়েছে। তাই তারা বুঝতে পারছে না।');
INSERT INTO "rawai" VALUES(63,4,'63:4','আর যখন তুমি তাদের প্রতি তাকিয়ে দেখবে তখন তাদের শরীর তোমাকে মুগ্ধ করবে। আর যদি তারা কথা বলে তুমি তাদের কথা (আগ্রহ নিয়ে) শুনবে। তারা দেয়ালে ঠেস দেয়া কাঠের মতই। তারা মনে করে প্রতিটি আওয়াজই তাদের বিরুদ্ধে। এরাই শত্রু, অতএব এদের সম্পর্কে সতর্ক হও। আল্লাহ এদেরকে ধ্বংস করুন। তারা কিভাবে সত্য থেকে ফিরে যাচ্ছে।');
INSERT INTO "rawai" VALUES(63,5,'63:5','আর তাদেরকে যখন বলা হয় এস, আল্লাহর রাসূল তোমাদের জন্য ক্ষমা প্রার্থনা করবেন, তখন তারা তাদের মাথা নাড়ে*। আর তুমি তাদেরকে দেখতে পাবে, অহঙ্কারবশত বিমুখ হয়ে চলে যেতে। *তারা অস্বীকার স্বরূপ মাথা নাড়ে।');
INSERT INTO "rawai" VALUES(63,6,'63:6','তুমি তাদের জন্য ক্ষমা প্রার্থনা কর অথবা না কর, উভয়টি তাদের ক্ষেত্রে সমান। আল্লাহ তাদেরকে কখনো ক্ষমা করবেন না। অবশ্যই আল্লাহ পাপাচারী সম্প্রদায়কে হেদায়াত দেন না।');
INSERT INTO "rawai" VALUES(63,7,'63:7','তারাই বলে, যারা আল্লাহর রাসূলের কাছে আছে তোমরা তাদের জন্য খরচ করো না, যতক্ষণ না তারা সরে যায়। আর আসমানসমূহ ও যমীনের ধন-ভান্ডার তো আল্লাহরই, কিন্তু মুনাফিকরা তা বুঝে না।');
INSERT INTO "rawai" VALUES(63,8,'63:8','তারা বলে, যদি আমরা মদীনায় ফিরে যাই তাহলে অবশ্যই সেখান থেকে প্রবল দুর্বলকে বহিষ্কার করবে। কিন্তু সকল মর্যাদা তো আল্লাহর, তাঁর রাসূলের ও মুমিনদের। কিন্তু মুনাফিকরা তা জানে না।');
INSERT INTO "rawai" VALUES(63,9,'63:9','হে মুমিনগণ, তোমাদের ধন-সম্পদ ও সন্তান- সন্ততি যেন তোমাদেরকে আল্লাহর স্মরণ থেকে উদাসীন না করে। আর যারা এরূপ করে তারাই তো ক্ষতিগ্রস্ত।');
INSERT INTO "rawai" VALUES(63,10,'63:10','আর আমি তোমাদেরকে যে রিযক দিয়েছি তা থেকে ব্যয় কর, তোমাদের কারো মৃত্যু আসার পূর্বে। কেননা তখন সে বলবে, হে আমার রব, যদি আপনি আমাকে আরো কিছু কাল পর্যন্ত অবকাশ দিতেন, তাহলে আমি দান-সদাকা করতাম। আর সৎ লোকদের অন্তর্ভুক্ত হতাম।');
INSERT INTO "rawai" VALUES(63,11,'63:11','আর আল্লাহ কখনো কোন প্রাণকেই অবকাশ দেবেন না, যখন তার নির্ধারিত সময় এসে যাবে। আর তোমরা যা কর সে সম্পর্কে আল্লাহ সম্যক অবহিত।');
INSERT INTO "rawai" VALUES(64,1,'64:1','যা কিছু রয়েছে আসমানসমূহে এবং যা কিছু রয়েছে যমীনে, সবই আল্লাহর জন্য পবিত্রতা ঘোষণা করে। বাদশাহী তাঁরই এবং প্রশংসা তাঁরই। তিনি সর্ব বিষয়ে সর্বশক্তিমান।');
INSERT INTO "rawai" VALUES(64,2,'64:2','তিনিই তোমাদেরকে সৃষ্টি করেছেন। অতঃপর তোমাদের মধ্যে কতক কাফির এবং কতক মু’মিন। আর তোমরা যে আমল করছ আল্লাহ তার সম্যক দ্রষ্টা।');
INSERT INTO "rawai" VALUES(64,3,'64:3','তিনি আসমানসমূহ ও যমীনকে যথার্থভাবে সৃষ্টি করেছেন এবং তোমাদেরকে আকৃতি দান করেছেন এবং সুন্দর করেছেন তোমাদের আকৃতি। আর প্রত্যাবর্তন তো তাঁরই নিকট।');
INSERT INTO "rawai" VALUES(64,4,'64:4','আসমানসমূহ ও যমীনে যা কিছু আছে তিনি তা জানেন এবং তিনি জানেন যা তোমরা গোপন কর এবং যা তোমরা প্রকাশ কর। আল্লাহ অন্তরসমূহে যা কিছু আছে সে বিষয়ে সম্যক জ্ঞাত।');
INSERT INTO "rawai" VALUES(64,5,'64:5','ইতঃপূর্বে যারা কুফরী করেছে, তাদের সংবাদ কি তোমাদের নিকট পৌঁছেনি। তারা তাদের কর্মের মন্দ পরিণাম আস্বাদন করেছিল এবং তাদের জন্য রয়েছে যন্ত্রণাদায়ক আযাব।');
INSERT INTO "rawai" VALUES(64,6,'64:6','এটি এ জন্য যে, তাদের রাসূলগণ তাদের নিকট সুস্পষ্ট নিদর্শনাবলী নিয়ে আসত, অথচ তারা বলত, মানুষই কি আমাদের পথ প্রদর্শন করবে? অতঃপর তারা কুফরী করল এবং মুখ ফিরিয়ে নিল। আর আল্লাহ বে-পরওয়াই দেখালেন এবং আল্লাহ অভাবমুক্ত পরম, প্রশংসিত।');
INSERT INTO "rawai" VALUES(64,7,'64:7','কাফিররা ধারণা করেছিল যে, তারা কখনোই পুনরুত্থিত হবে না। বল, ‘হ্যাঁ, আমার রবের কসম, তোমরা অবশ্যই পুনরুত্থিত হবে। অতঃপর তোমরা যা আমল করেছিলে তা অবশ্যই তোমাদের জানানো হবে। আর এটি আল্লাহর পক্ষে খুবই সহজ।');
INSERT INTO "rawai" VALUES(64,8,'64:8','অতএব তোমরা আল্লাহ ও তাঁর রাসূলের এবং আমি যে নূর অবতীর্ণ করেছি তার প্রতি ঈমান আন। আর তোমরা যে আমল করছ আল্লাহ সে বিষয়ে সম্যক অবহিত।');
INSERT INTO "rawai" VALUES(64,9,'64:9','স্মরণ কর, যেদিন সমাবেশ দিবসের উদ্দেশ্যে আল্লাহ তোমাদের সমবেত করবেন, ঐ দিনই হচ্ছে লাভ-ক্ষতির দিন। আর যে আল্লাহর প্রতি ঈমান আনে ও সৎকর্ম করে তিনি তার পাপসমূহ মোচন করে দিবেন এবং তাকে প্রবেশ করাবেন জান্নাতসমূহে, যার পাদদেশ দিয়ে ঝর্ণাধারা প্রবাহিত হয়, তথায় তারা স্থায়ী হবে। এটাই মহাসাফল্য।');
INSERT INTO "rawai" VALUES(64,10,'64:10','কিন্তু যারা কুফরী করে এবং আমার আয়াতসমূহ অস্বীকার করে তারাই জাহান্নামের অধিবাসী। তথায় তারা স্থায়ী হবে। আর তা কতইনা নিকৃষ্ট প্রত্যাবর্তনস্থল।');
INSERT INTO "rawai" VALUES(64,11,'64:11','আল্লাহর অনুমতি ছাড়া কোন বিপদই আপতিত হয় না। যে আল্লাহর প্রতি ঈমান আনে, আল্লাহ তার অন্তরকে সৎপথে পরিচালিত করেন। আল্লাহ প্রত্যেক বিষয়ে সর্বজ্ঞ।');
INSERT INTO "rawai" VALUES(64,12,'64:12','তোমরা আল্লাহর আনুগত্য কর এবং রাসূলের আনুগত্য কর। কিন্তু তোমরা যদি মুখ ফিরিয়ে নাও, তবে আমার রাসূলের তো একমাত্র দায়িত্ব হচ্ছে স্পষ্টভাবে বাণী পৌঁছে দেয়া।');
INSERT INTO "rawai" VALUES(64,13,'64:13','আল্লাহ, তিনি ছাড়া কোন ইলাহ নেই এবং আল্লাহর উপরই মুমিনরা যেন তাওয়াক্কুল করে।');
INSERT INTO "rawai" VALUES(64,14,'64:14','হে মুমিনগণ, তোমাদের স্বামী-স্ত্রী ও সন্তান-সন্ততিদের কেউ কেউ তোমাদের দুশমন।* অতএব তোমরা তাদের ব্যাপারে সতর্কতা অবলম্বন কর। আর যদি তোমরা মার্জনা কর, এড়িয়ে যাও এবং মাফ করে দাও তবে নিশ্চয় আল্লাহ পরম ক্ষমাশীল, অসীম দয়ালু। *অর্থাৎ তারা কখনো কখনো আল্লাহর পথে চলা, তাঁর আনুগত্য করা অথবা আল্লাহর যিকর ও আখিরাতের স্মরণ থেকে তোমাদের বিরত রাখতে পারে। এ আয়াতে শত্রুতা ও দুশমনি বলতে এর প্রতিই ইঙ্গিত করা হয়েছে।');
INSERT INTO "rawai" VALUES(64,15,'64:15','তোমাদের ধন-সম্পদ ও সন্তান-সন্ততি তো কেবল পরীক্ষা বিশেষ। আর আল্লাহর নিকটই মহান প্রতিদান।');
INSERT INTO "rawai" VALUES(64,16,'64:16','অতএব তোমরা যথাসাধ্য আল্লাহকে ভয় কর, শ্রবণ কর, আনুগত্য কর এবং তোমাদের নিজদের কল্যাণে ব্যয় কর, আর যাদেরকে অন্তরের কার্পণ্য থেকে রক্ষা করা হয়, তারাই মূলত সফলকাম।');
INSERT INTO "rawai" VALUES(64,17,'64:17','যদি তোমরা আল্লাহকে উত্তম ঋণ দাও, তিনি তা তোমাদের জন্য দ্বিগুন করে দিবেন এবং তোমাদের ক্ষমা করে দিবেন। আল্লাহ গুণগ্রাহী, পরম ধৈর্যশীল।');
INSERT INTO "rawai" VALUES(64,18,'64:18','দৃশ্য ও অদৃশ্যের জ্ঞানধারী, মহাপরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO "rawai" VALUES(65,1,'65:1','হে নবী, (বল), তোমরা যখন স্ত্রীদেরকে তালাক দেবে, তখন তাদের ইদ্দত অনুসারে তাদের তালাক দাও এবং ‘ইদ্দত হিসাব করে রাখবে এবং তোমাদের রব আল্লাহকে ভয় করবে। তোমরা তাদেরকে তোমাদের বাড়ী-ঘর থেকে বের করে দিয়ো না এবং তারাও বের হবে না। যদি না তারা কোন স্পষ্ট অশ্লীলতায় লিপ্ত হয়। আর এগুলো আল্লাহর সীমারেখা। আর যে আল্লাহর (নির্ধারিত) সীমারেখাসমূহ অতিক্রম করে সে অবশ্যই তার নিজের ওপর যুলম করে। তুমি জান না, হয়তো এর পর আল্লাহ, (ফিরে আসার) কোন পথ তৈরী করে দিবেন।');
INSERT INTO "rawai" VALUES(65,2,'65:2','অতঃপর যখন তারা তাদের ইদ্দতের শেষ সীমায় পৌঁছবে, তখন তোমরা তাদের ন্যায়ানুগ প‎ন্থায় রেখে দেবে অথবা ন্যায়ানুগ প‎ন্থায় তাদের পরিত্যাগ করবে এবং তোমাদের মধ্য থেকে ন্যায়পরায়ণ দুইজনকে সাক্ষী বানাবে। আর আল্লাহর জন্য সঠিক সাক্ষ্য দেবে। তোমাদের মধ্যে যে আল্লাহ ও আখিরাত দিবসের প্রতি ঈমান আনে এটি দ্বারা তাকে উপদেশ দেয়া হচ্ছে। যে আল্লাহকে ভয় করে, তিনি তার জন্য উত্তরণের পথ তৈরী করে দেন।');
INSERT INTO "rawai" VALUES(65,3,'65:3','এবং তিনি তাকে এমন উৎস থেকে রিযক দিবেন যা সে কল্পনাও করতে পারবে না। আর যে আল্লাহর ওপর তাওয়াক্কুল করে আল্লাহ তার জন্য যথেষ্ট। আল্লাহ তাঁর উদ্দেশ্য পূর্ণ করবেনই। নিশ্চয় আল্লাহ প্রত্যেক জিনিসের জন্য একটি সময়সীমা নির্ধারণ করে দিয়েছেন।');
INSERT INTO "rawai" VALUES(65,4,'65:4','তোমাদের স্ত্রীদের মধ্যে যারা ঋতুবর্তী হওয়ার কাল অতিক্রম করে গেছে, তাদের ইদ্দত সম্পর্কে তোমরা যদি সংশয়ে থাক এবং যারা এখনও ঋতুর বয়সে পৌঁছেনি তাদের ইদ্দতকালও হবে তিন মাস। আর গর্ভধারিনীদের ইদ্দতকাল সন্তান প্রসব পর্যন্ত। যে আল্লাহকে ভয় করে, তিনি তার জন্য তার কাজকে সহজ করে দেন।');
INSERT INTO "rawai" VALUES(65,5,'65:5','এটি আল্লাহর নির্দেশ, তিনি তা তোমাদের প্রতি অবতীর্ণ করেছেন। আর যে আল্লাহকে ভয় করে তিনি তার গুনাহসমূহ মোচন করে দেন এবং তার প্রতিদানকে মহান করে দেন।');
INSERT INTO "rawai" VALUES(65,6,'65:6','তোমাদের সামর্থ্য অনুযায়ী যেখানে তোমরা বসবাস কর সেখানে তাদেরকেও বাস করতে দাও, তাদেরকে সঙ্কটে ফেলার জন্য কষ্ট দিয়ো না। আর তারা গর্ভবতী হলে তাদের সন্তান প্রসব করা পর্যন্ত তাদের জন্য তোমরা ব্যয় কর; আর তারা তোমাদের জন্য সন্তানকে দুধ পান করালে তাদের পাওনা তাদেরকে দিয়ে দাও এবং (সন্তানের কল্যাণের জন্য) সংগতভাবে তোমাদের মাঝে পরস্পর পরামর্শ কর। আর যদি তোমরা পরস্পর কঠোর হও তবে পিতার পক্ষে অন্য কোন নারী দুধপান করাবে।');
INSERT INTO "rawai" VALUES(65,7,'65:7','সামর্থ্যবান যেন নিজ সামর্থ্য অনুযায়ী ব্যয় করে আর যার রিয্ক সংকীর্ণ করা হয়েছে সে যেন আল্লাহ তাকে যা দিয়েছেন তা হতে ব্যয় করে। আল্লাহ কারো ওপর বোঝা চাপাতে চান না তিনি তাকে যা দিয়েছেন তার চাইতে বেশী। আল্লাহ কঠিন অবস্থার পর সহজতা দান করবেন।');
INSERT INTO "rawai" VALUES(65,8,'65:8','আর অনেক জনপদ তাদের রব ও তাঁর রাসূলগণের নির্দেশের বিরুদ্ধে গিয়েছে। ফলে আমি তাদের কাছ থেকে কঠোর হিসাব নিয়েছি এবং তাদেরকে আমি কঠিন আযাব দিয়েছি।');
INSERT INTO "rawai" VALUES(65,9,'65:9','অতএব তারা নিজদের কৃতকর্মের আযাব আস্বাদন করেছে আর ক্ষতিই ছিল তাদের কাজের পরিণতি।');
INSERT INTO "rawai" VALUES(65,10,'65:10','আল্লাহ তাদের জন্য কঠিন আযাব প্রস্তুত রেখেছেন; সুতরাং তোমরা আল্লাহকে ভয় কর, হে বুদ্ধিসম্পন্ন লোকেরা, যারা ঈমান এনেছে; নিশ্চয় আল্লাহ তোমাদের কাছে পাঠিয়েছেন একটি উপদেশ।');
INSERT INTO "rawai" VALUES(65,11,'65:11','একজন রাসূল, যে তোমাদের কাছে আল্লাহর সুস্পষ্ট আয়াতসমূহ তেলাওয়াত করে; যারা ঈমান আনে ও সৎকর্ম করে যাতে তিনি তাদেরকে অন্ধকার হতে আলোতে বের করে আনতে পারেন। আর যে ব্যক্তি আল্লাহর প্রতি ঈমান আনবে এবং সৎকাজ করবে তিনি তাকে এমন জান্নাতে প্রবেশ করাবেন যার পাদদেশে নহরসমূহ প্রবাহিত; সেখানে তারা স্থায়ী হবে; আল্লাহ তো তাকে অতি উত্তম রিয্ক দেবেন।');
INSERT INTO "rawai" VALUES(65,12,'65:12','তিনি আল্লাহ, যিনি সাত আসমান এবং অনুরূপ যমীন সৃষ্টি করেছেন; এগুলির মাঝে তাঁর নির্দেশ অবতীর্ণ হয় যেন তোমরা জানতে পার যে, আল্লাহ সর্ববিষয়ে ক্ষমতাবান এবং আল্লাহর জ্ঞানতো সব কিছুকে বেষ্টন করে আছে।');
INSERT INTO "rawai" VALUES(66,1,'66:1','হে নবী, আল্লাহ তোমার জন্য যা হালাল করেছেন তোমার স্ত্রীদের সন্তুষ্টি কামনায় তুমি কেন তা হারাম করছ? আর আল্লাহ অতীব ক্ষমাশীল, পরম দয়ালু।');
INSERT INTO "rawai" VALUES(66,2,'66:2','নিশ্চয় তোমাদের জন্য শপথ হতে মুক্তির বিধান দিয়েছেন; আর আল্লাহ তোমাদের অভিভাবক এবং তিনি সর্বজ্ঞ, প্রজ্ঞাবান।');
INSERT INTO "rawai" VALUES(66,3,'66:3','আর যখন নবী তার এক স্ত্রীকে গোপনে একটি কথা বলেছিলেন; অতঃপর যখন সে (স্ত্রী) অন্যকে তা জানিয়ে দিল এবং আল্লাহ তার (নবীর) কাছে এটি প্রকাশ করে দিলেন, তখন নবী কিছুটা তার স্ত্রীকে অবহিত করল আর কিছু এড়িয়ে গেল। যখন সে তাকে বিষয়টি জানাল তখন সে বলল, ‘আপনাকে এ সংবাদ কে দিল?’ সে বলল, ‘মহাজ্ঞানী ও সর্বজ্ঞ আল্লাহ আমাকে জানিয়েছেন।’');
INSERT INTO "rawai" VALUES(66,4,'66:4','যদি তোমরা উভয়ে আল্লাহর কাছে তওবা কর (তবে তা তোমাদের জন্য উত্তম)। কারণ তোমাদের উভয়ের অন্তর বাঁকা হয়েছে, আর তোমরা যদি তার বিরুদ্ধে পরস্পরকে সাহায্য কর তবে আল্লাহই তার অভিভাবক এবং জিব্রীল ও সৎকর্মশীল মু’মিনরাও। তাছাড়া অন্যান্য ফেরেশতারাও তার সাহায্যকারী।');
INSERT INTO "rawai" VALUES(66,5,'66:5','সে যদি তোমাদেরকে তালাক দেয়, তবে আশা করা যায় তার রব তোমাদের পরিবর্তে তোমাদের চাইতে উত্তম স্ত্রী তাকে দিবেন, যারা মুসলিম, মুমিনা, অনুগত, তাওবাকারী, ‘ইবাদতকারী, সিয়াম পালনকারী, অকুমারী ও কুমারী।');
INSERT INTO "rawai" VALUES(66,6,'66:6','হে ঈমানদারগণ, তোমরা নিজেদেরকে ও তোমাদের পরিবার-পরিজনকে আগুন হতে বাঁচাও যার জ্বালানি হবে মানুষ ও পাথর; যেখানে রয়েছে নির্মম ও কঠোর ফেরেশতাকূল, আল্লাহ তাদেরকে যে নির্দেশ দিয়েছেন তারা সে ব্যাপারে তার অবাধ্য হয় না। আর তারা তা-ই করে যা তাদেরকে আদেশ করা হয়।');
INSERT INTO "rawai" VALUES(66,7,'66:7','হে কাফিরগণ, আজ তোমরা ওজর পেশ করো না; তোমরা যে ‘আমল করতে তার প্রতিফলই তোমাদেরকে দেয়া হচ্ছে।');
INSERT INTO "rawai" VALUES(66,8,'66:8','হে ঈমানদারগণ, তোমরা আল্লাহর কাছে তাওবা কর, খাঁটি তাওবা; আশা করা যায় তোমাদের রব তোমাদের পাপসমূহ মোচন করবেন এবং তোমাদেরকে এমন জান্নাতসমূহে প্রবেশ করাবেন যার পাদদেশে নহরসমূহ প্রবাহিত, নবী ও তার সাথে যারা ঈমান এনেছে তাদেরকে সেদিন আল্লাহ লাঞ্ছিত করবেন না। তাদের আলো তাদের সামনে ও ডানে ধাবিত হবে। তারা বলবে, ‘হে আমাদের রব, আমাদের জন্য আমাদের আলো পূর্ণ করে দিন এবং আমাদেরকে ক্ষমা করুন; নিশ্চয় আপনি সর্ববিষয়ে সর্বক্ষমতাবান।’');
INSERT INTO "rawai" VALUES(66,9,'66:9','‘হে নবী, কাফির ও মুনাফিকদের বিরুদ্ধে জিহাদ কর এবং তাদের ব্যাপারে কঠোর হও; আর তাদের আশ্রয়স্থল হবে জাহান্নাম এবং তা কত নিকৃষ্ট গন্তব্যস্থল!');
INSERT INTO "rawai" VALUES(66,10,'66:10','যারা কুফরি করে তাদের জন্য আল্লাহ নূহের স্ত্রীর ও লূতের স্ত্রীর উদাহরণ পেশ করেন; তারা আমার বান্দাদের মধ্য হতে দু’জন সৎবান্দার অধীনে ছিল, কিন্তু তারা উভয়ে তাদের সাথে বিশ্বাসঘাতকতা করেছিল, অতঃপর আল্লাহর আযাব হতে রক্ষায় নূহ ও লূত তাদের কোন কাজে আসেনি। বলা হল, ‘তোমরা উভয়ে প্রবেশকারীদের সাথে জাহান্নামে প্রবেশ কর।’');
INSERT INTO "rawai" VALUES(66,11,'66:11','আর যারা ঈমান আনে তাদের জন্য আল্লাহ ফির‘আউনের স্ত্রীর উদাহরণ পেশ করেন, যখন সে বলেছিল, ‘হে আমার রব, আপনার কাছে আমার জন্য জান্নাতে একটি বাড়ি নির্মাণ করুন এবং আমাকে ফির‘আউন ও তার কর্ম হতে নাজাত দিন, আর আমাকে নাজাত দিন যালিম সম্প্রদায় হতে।');
INSERT INTO "rawai" VALUES(66,12,'66:12','(আল্লাহ আরো উদাহরণ পেশ করেন) ইমরান কন্যা মারয়াম-এর, যে নিজের সতীত্ব রক্ষা করেছিল, ফলে আমি তাতে আমার রূহ থেকে ফুঁকে দিয়েছিলাম। আর সে তার রবের বাণীসমূহ ও তাঁর কিতাবসমূহের সত্যতা স্বীকার করেছিল এবং সে ছিল অনুগতদের অন্তর্ভুক্ত।');
INSERT INTO "rawai" VALUES(67,1,'67:1','বরকতময় তিনি যার হাতে সর্বময় কর্তৃত্ব। আর তিনি সব কিছুর উপর সর্বশক্তিমান।');
INSERT INTO "rawai" VALUES(67,2,'67:2','যিনি মৃত্যু ও জীবন সৃষ্টি করেছেন যাতে তিনি তোমাদেরকে পরীক্ষা করতে পারেন যে, কে তোমাদের মধ্যে আমলের দিক থেকে উত্তম। আর তিনি মহাপরাক্রমশালী, অতিশয় ক্ষমাশীল।');
INSERT INTO "rawai" VALUES(67,3,'67:3','যিনি সাত আসমান স্তরে স্তরে সৃষ্টি করেছেন। পরম করুণাময়ের সৃষ্টিতে তুমি কোন অসামঞ্জস্য দেখতে পাবে না। তুমি আবার দৃষ্টি ফিরাও, কোন ত্রুটি দেখতে পাও কি?');
INSERT INTO "rawai" VALUES(67,4,'67:4','অতঃপর তুমি দৃষ্টি ফিরাও একের পর এক, সেই দৃষ্টি অবনমিত ও ক্লান্ত হয়ে তোমার দিকে ফিরে আসবে।');
INSERT INTO "rawai" VALUES(67,5,'67:5','আমি নিকটবর্তী আসমানকে প্রদীপপুঞ্জ দ্বারা সুশোভিত করেছি এবং সেগুলোকে শয়তানদের প্রতি নিক্ষেপের বস্তু বানিয়েছি। আর তাদের জন্য প্রস্তুত করে রেখেছি জ্বলন্ত আগুনের আযাব।');
INSERT INTO "rawai" VALUES(67,6,'67:6','আর যারা তাদের রবকে অস্বীকার করে, তাদের জন্য রয়েছে জাহান্নামের আযাব। আর কতইনা নিকৃষ্ট সেই প্রত্যাবর্তনস্থল!');
INSERT INTO "rawai" VALUES(67,7,'67:7','যখন তাদেরকে তাতে নিক্ষেপ করা হবে, তখন তারা তার বিকট শব্দ শুনতে পাবে। আর তা উথলিয়ে উঠবে।');
INSERT INTO "rawai" VALUES(67,8,'67:8','ক্রোধে তা ছিন্ন-ভিন্ন হবার উপক্রম হবে। যখনই তাতে কোন দলকে নিক্ষেপ করা হবে, তখন তার প্রহরীরা তাদেরকে জিজ্ঞাসা করবে, ‘তোমাদের নিকট কি কোন সতর্ককারী আসেনি’?');
INSERT INTO "rawai" VALUES(67,9,'67:9','তারা বলবে, ‘হ্যাঁ, আমাদের নিকট সতর্ককারী এসেছিল। তখন আমরা (তাদেরকে) মিথ্যাবাদী আখ্যায়িত করেছিলাম এবং বলেছিলাম, ‘আল্লাহ কিছুই নাযিল করেননি। তোমরা তো ঘোর বিভ্রান্তিতে রয়েছ’।');
INSERT INTO "rawai" VALUES(67,10,'67:10','আর তারা বলবে, ‘যদি আমরা শুনতাম অথবা বুঝতাম, তাহলে আমরা জ্বলন্ত আগুনের অধিবাসীদের মধ্যে থাকতাম না’।');
INSERT INTO "rawai" VALUES(67,11,'67:11','অতঃপর তারা তাদের অপরাধ স্বীকার করবে। অতএব ধ্বংস জ্বলন্ত আগুনের অধিবাসীদের জন্য।');
INSERT INTO "rawai" VALUES(67,12,'67:12','নিশ্চয় যারা তাদের রবকে না দেখেই ভয় করে তাদের জন্য রয়েছে ক্ষমা ও বড় প্রতিদান।');
INSERT INTO "rawai" VALUES(67,13,'67:13','আর তোমরা তোমাদের কথা গোপন কর অথবা তা প্রকাশ কর, নিশ্চয় তিনি অন্তরসমূহে যা আছে সে বিষয়ে সম্যক অবগত।');
INSERT INTO "rawai" VALUES(67,14,'67:14','যিনি সৃষ্টি করেছেন, তিনি কি জানেন না? অথচ তিনি অতি সূক্ষ্মদর্শী, পূর্ণ অবহিত।');
INSERT INTO "rawai" VALUES(67,15,'67:15','তিনিই তো তোমাদের জন্য যমীনকে সুগম করে দিয়েছেন, কাজেই তোমরা এর পথে-প্রান্তরে বিচরণ কর এবং তাঁর রিয্ক থেকে তোমরা আহার কর। আর তাঁর নিকটই পুনরুত্থান।');
INSERT INTO "rawai" VALUES(67,16,'67:16','যিনি আসমানে আছেন,* তিনি তোমাদের সহ যমীন ধসিয়ে দেয়া থেকে কি তোমরা নিরাপদ হয়ে গেছ, অতঃপর আকস্মিকভাবে তা থর থর করে কাঁপতে থাকবে? *‘যিনি আসমানে আছেন’ দ্বারা আল্লাহকে বুঝানো হয়েছে। এর দ্বারা আল্লাহ যে আসমানে আছেন, তা প্রমাণিত হয়।');
INSERT INTO "rawai" VALUES(67,17,'67:17','যিনি আসমানে আছেন, তিনি তোমাদের উপর পাথর নিক্ষেপকারী ঝড়ো হাওয়া পাঠানো থেকে তোমরা কি নিরাপদ হয়ে গেছ, তখন তোমরা জানতে পারবে কেমন ছিল আমার সতর্কবাণী?');
INSERT INTO "rawai" VALUES(67,18,'67:18','আর অবশ্যই তাদের পূর্ববর্তীরাও অস্বীকার করেছিল। ফলে কেমন ছিল আমার প্রত্যাখ্যান (এর শাস্তি)?');
INSERT INTO "rawai" VALUES(67,19,'67:19','তারা কি লক্ষ্য করেনি তাদের উপরস্থ পাখিদের প্রতি, যারা ডানা বিস্তার করে ও গুটিয়ে নেয়? পরম করুণাময় ছাড়া অন্য কেউ এদেরকে স্থির রাখে না। নিশ্চয় তিনি সব কিছুর সম্যক দ্রষ্টা।');
INSERT INTO "rawai" VALUES(67,20,'67:20','পরম করুণাময় ছাড়া তোমাদের কি আর কোন সৈন্য আছে, যারা তোমাদেরকে সাহায্য করবে ? কাফিররা শুধু তো ধোঁকায় নিপতিত।');
INSERT INTO "rawai" VALUES(67,21,'67:21','অথবা এমন কে আছে, যে তোমাদেরকে রিয্ক দান করবে যদি আল্লাহ তাঁর রিয্ক বন্ধ করে দেন? বরং তারা অহমিকা ও অনীহায় নিমজ্জিত হয়ে আছে।');
INSERT INTO "rawai" VALUES(67,22,'67:22','যে ব্যক্তি উপুড় হয়ে মুখের উপর ভর দিয়ে চলে সে কি অধিক হিদায়াতপ্রাপ্ত নাকি সেই ব্যক্তি যে সোজা হয়ে সরল পথে চলে ?');
INSERT INTO "rawai" VALUES(67,23,'67:23','বল, ‘তিনিই তোমাদেরকে সৃষ্টি করেছেন এবং তোমাদের জন্য শ্রবণ ও দৃষ্টিশক্তি এবং অন্তকরণসমূহ দিয়েছেন। তোমরা খুব অল্পই শোকর কর’।');
INSERT INTO "rawai" VALUES(67,24,'67:24','বল, ‘তিনিই তোমাদেরকে যমীনে সৃষ্টি করেছেন এবং তাঁর কাছেই তোমাদেরকে সমবেত করা হবে’।');
INSERT INTO "rawai" VALUES(67,25,'67:25','আর তারা বলে, ‘সে ওয়াদা কখন বাস্তবায়িত হবে, যদি তোমরা সত্যবাদী হও’।');
INSERT INTO "rawai" VALUES(67,26,'67:26','বল, ‘এ বিষয়ের জ্ঞান আল্লাহরই নিকট। আর আমি তো স্পষ্ট সতর্ককারী মাত্র’।');
INSERT INTO "rawai" VALUES(67,27,'67:27','অতঃপর তারা যখন তা* আসন্ন দেখতে পাবে, তখন কাফিরদের চেহারা মলিন হয়ে যাবে এবং বলা হবে, ‘এটাই হল তা, যা তোমরা দাবী করছিলে’। *কিয়ামতের শাস্তি।');
INSERT INTO "rawai" VALUES(67,28,'67:28','বল, ‘তোমরা ভেবে দেখেছ কি’? যদি আল্লাহ আমাকে এবং আমার সাথে যারা আছে, তাদেরকে ধ্বংস করে দেন অথবা আমাদের প্রতি দয়া করেন, তাহলে কাফিরদেরকে যন্ত্রণাদায়ক আযাব থেকে কে রক্ষা করবে’?');
INSERT INTO "rawai" VALUES(67,29,'67:29','বল, ‘তিনিই পরম করুণাময়। আমরা তাঁর প্রতি ঈমান এনেছি এবং তাঁর উপর তাওয়াক্কুল করেছি। কাজেই তোমরা অচিরেই জানতে পারবে কে স্পষ্ট বিভ্রান্তিতে রয়েছে’?');
INSERT INTO "rawai" VALUES(67,30,'67:30','বল, ‘তোমরা ভেবে দেখেছ কি, যদি তোমাদের পানি ভূগর্ভে চলে যায়, তাহলে কে তোমাদেরকে বহমান পানি এনে দিবে’ ?');
INSERT INTO "rawai" VALUES(68,1,'68:1','নূন; কলমের কসম এবং তারা যা লিখে তার কসম!');
INSERT INTO "rawai" VALUES(68,2,'68:2','তোমার রবের অনুগ্রহে তুমি পাগল নও।');
INSERT INTO "rawai" VALUES(68,3,'68:3','আর নিশ্চয় তোমার জন্য রয়েছে অফুরন্ত পুরস্কার।');
INSERT INTO "rawai" VALUES(68,4,'68:4','আর নিশ্চয় তুমি মহান চরিত্রের উপর অধিষ্ঠিত।');
INSERT INTO "rawai" VALUES(68,5,'68:5','অতঃপর শীঘ্রই তুমি দেখতে পাবে এবং তারাও দেখতে পাবে-');
INSERT INTO "rawai" VALUES(68,6,'68:6','তোমাদের মধ্যে কে বিকারগ্রস্ত?');
INSERT INTO "rawai" VALUES(68,7,'68:7','নিশ্চয় তোমার রবই সম্যক পরিজ্ঞাত তাদের ব্যাপারে যারা তাঁর পথ থেকে বিচ্যুত হয়েছে, আর তিনি হিদায়াতপ্রাপ্তদের সম্পর্কেও সম্যক জ্ঞাত।');
INSERT INTO "rawai" VALUES(68,8,'68:8','অতএব তুমি মিথ্যারোপকারীদের আনুগত্য করো না।');
INSERT INTO "rawai" VALUES(68,9,'68:9','তারা কামনা করে, যদি তুমি আপোষকামী হও, তবে তারাও আপোষকারী হবে।');
INSERT INTO "rawai" VALUES(68,10,'68:10','আর তুমি আনুগত্য করো না প্রত্যেক এমন ব্যক্তির যে অধিক কসমকারী, লাঞ্ছিত।');
INSERT INTO "rawai" VALUES(68,11,'68:11','পিছনে নিন্দাকারী ও যে চোগলখুরী করে বেড়ায়,');
INSERT INTO "rawai" VALUES(68,12,'68:12','ভাল কাজে বাধাদানকারী, সীমালঙ্ঘনকারী অপরাধী,');
INSERT INTO "rawai" VALUES(68,13,'68:13','দুষ্ট প্রকৃতির, তারপর জারজ।');
INSERT INTO "rawai" VALUES(68,14,'68:14','এ কারণে যে, সে ছিল ধন-সম্পদ ও সন্তান- সন্ততির অধিকারী।');
INSERT INTO "rawai" VALUES(68,15,'68:15','যখন তার কাছে আমার আয়াতসমূহ তিলাওয়াত করা হয় তখন সে বলে, এগুলো পূর্ববর্তীদের কল্পকাহিনীমাত্র।');
INSERT INTO "rawai" VALUES(68,16,'68:16','অচিরেই আমি তার শুঁড়ের* উপর দাগ দিয়ে দেব। *অর্থাৎ নাকের উপর। বিদ্রূপাত্মক অর্থে ব্যবহৃত।');
INSERT INTO "rawai" VALUES(68,17,'68:17','নিশ্চয় আমি এদেরকে পরীক্ষা করেছি, যেভাবে পরীক্ষা করেছিলাম বাগানের মালিকদেরকে। যখন তারা কসম করেছিল যে, অবশ্যই তারা সকাল বেলা বাগানের ফল আহরণ করবে।');
INSERT INTO "rawai" VALUES(68,18,'68:18','আর তারা ‘ইনশাআল্লাহ’ বলেনি।');
INSERT INTO "rawai" VALUES(68,19,'68:19','অতঃপর তোমার রবের পক্ষ থেকে এক প্রদক্ষিণকারী (আগুন) বাগানের ওপর দিয়ে প্রদক্ষিণ করে গেল, আর তারা ছিল ঘুমন্ত।');
INSERT INTO "rawai" VALUES(68,20,'68:20','ফলে তা (পুড়ে) কালো বর্ণের হয়ে গেল।');
INSERT INTO "rawai" VALUES(68,21,'68:21','তারপর সকাল বেলা তারা একে অপরকে ডেকে বলল,');
INSERT INTO "rawai" VALUES(68,22,'68:22','‘তোমরা যদি ফল আহরণ করতে চাও তাহলে সকাল সকাল তোমাদের বাগানে যাও’।');
INSERT INTO "rawai" VALUES(68,23,'68:23','তারপর তারা চলল, নিম্নস্বরে একথা বলতে বলতে-');
INSERT INTO "rawai" VALUES(68,24,'68:24','যে, ‘আজ সেখানে তোমাদের কাছে কোন অভাবী যেন প্রবেশ করতে না পারে’।');
INSERT INTO "rawai" VALUES(68,25,'68:25','আর তারা ভোর বেলা দৃঢ় ইচ্ছা শক্তি নিয়ে সক্ষম অবস্থায় (বাগানে) গেল।');
INSERT INTO "rawai" VALUES(68,26,'68:26','তারপর তারা যখন বাগানটি দেখল, তখন তারা বলল, ‘অবশ্যই আমরা পথভ্রষ্ট’।');
INSERT INTO "rawai" VALUES(68,27,'68:27','‘বরং আমরা বঞ্চিত’।');
INSERT INTO "rawai" VALUES(68,28,'68:28','তাদের মধ্যে সবচেয়ে ভাল ব্যক্তিটি বলল, ‘আমি কি তোমাদেরকে বলিনি যে, তোমরা কেন (আল্লাহর) তাসবীহ পাঠ করছ না’?');
INSERT INTO "rawai" VALUES(68,29,'68:29','তারা বলল, ‘আমরা আমাদের রবের পবিত্রতা ঘোষণা করছি। অবশ্যই আমরা যালিম ছিলাম’।');
INSERT INTO "rawai" VALUES(68,30,'68:30','তারপর তারা একে অপরের প্রতি দোষারোপ করতে লাগল।');
INSERT INTO "rawai" VALUES(68,31,'68:31','তারা বলল, ‘হায়, আমাদের ধ্বংস! নিশ্চয় আমরা সীমালঙ্ঘনকারী ছিলাম’।');
INSERT INTO "rawai" VALUES(68,32,'68:32','সম্ভবতঃ আমাদের রব আমাদেরকে এর চেয়েও উৎকৃষ্টতর বিনিময় দেবেন। অবশ্যই আমরা আমাদের রবের প্রতি আগ্রহী।');
INSERT INTO "rawai" VALUES(68,33,'68:33','এভাবেই হয় আযাব। আর পরকালের আযাব অবশ্যই আরো বড়, যদি তারা জানত।');
INSERT INTO "rawai" VALUES(68,34,'68:34','নিশ্চয় মুত্তাকীদের জন্য তাদের রবের কাছে রয়েছে নিআমতপূর্ণ জান্নাত।');
INSERT INTO "rawai" VALUES(68,35,'68:35','তবে কি আমি মুসলিমদেরকে (অনুগতদেরকে) অবাধ্যদের মতই গণ্য করব?');
INSERT INTO "rawai" VALUES(68,36,'68:36','তোমাদের কী হল, তোমরা কিভাবে ফয়সালা করছ?');
INSERT INTO "rawai" VALUES(68,37,'68:37','তোমাদের কাছে কি কোন কিতাব আছে যাতে তোমরা পাঠ করছ?');
INSERT INTO "rawai" VALUES(68,38,'68:38','যে, নিশ্চয় তোমাদের জন্য সেখানে রয়েছে যা তোমরা পছন্দ কর?');
INSERT INTO "rawai" VALUES(68,39,'68:39','অথবা তোমাদের জন্য কি আমার উপর কিয়ামত পর্যন্ত বলবৎ কোন অঙ্গীকার রয়েছে যে, অবশ্যই তোমাদের জন্য থাকবে তোমরা যা ফয়সালা করবে?');
INSERT INTO "rawai" VALUES(68,40,'68:40','তুমি তাদেরকে জিজ্ঞাসা কর, কে এ ব্যাপারে যিম্মাদার?');
INSERT INTO "rawai" VALUES(68,41,'68:41','অথবা তাদের জন্য কি অনেক শরীক আছে? তাহলে তারা তাদের শরীকদেরকে উপস্থিত করুক যদি তারা সত্যবাদী হয়।');
INSERT INTO "rawai" VALUES(68,42,'68:42','সে দিন পায়ের গোছা* উন্মোচন করা হবে। আর তাদেরকে সিজদা করার জন্য আহবান জানানো হবে, কিন্তু তারা সক্ষম হবে না। *এ কথার ব্যাপারে ইমাম বুখারী একটি হাদীস বর্ণনা করেন। তা হল, আবু সাঈদ খুদরী রা. বলেন, মহানবী সাল্লাল্লাহু আলাইহি ওয়াসাল্লাম বলেছেন, আমাদের রব তাঁর পায়ের গোছা উন্মুক্ত করবেন, তখন প্রত্যেক মুমিন নর-নারী তাঁকে সিজদা করবে...।');
INSERT INTO "rawai" VALUES(68,43,'68:43','তাদের দৃষ্টিসমূহ অবনত অবস্থায় থাকবে, অপমান তাদেরকে আচ্ছন্ন করবে। অথচ তাদেরকে তো নিরাপদ অবস্থায় সিজদা করার জন্য আহবান করা হত (তখন তো তারা সিজদা করেনি)।');
INSERT INTO "rawai" VALUES(68,44,'68:44','অতএব ছেড়ে দাও আমাকে এবং যারা এ বাণী প্রত্যাখ্যান করে তাদেরকে। আমি তাদেরকে ধীরে ধীরে এমনভাবে পাকড়াও করব যে, তারা জানতে পারবে না।');
INSERT INTO "rawai" VALUES(68,45,'68:45','আর আমি তাদেরকে অবকাশ দেব। অবশ্যই আমার কৌশল অত্যন্ত বলিষ্ঠ।');
INSERT INTO "rawai" VALUES(68,46,'68:46','তুমি কি তাদের কাছে পারিশ্রমিক চাচ্ছ? ফলে তারা ঋণের কারণে ভারাক্রান্ত হয়ে পড়ছে।');
INSERT INTO "rawai" VALUES(68,47,'68:47','অথবা তাদের কাছে কি ‘গায়েব’ (লওহে মাহফুয) আছে যে, তারা লিখে রাখছে।');
INSERT INTO "rawai" VALUES(68,48,'68:48','অতএব তুমি তোমার রবের হুকুমের জন্য ধৈর্যধারণ কর। আর তুমি মাছওয়ালার মত হয়ো না, যখন সে দুঃখে কাতর হয়ে ডেকেছিল।');
INSERT INTO "rawai" VALUES(68,49,'68:49','যদি তার রবের অনুগ্রহ তার কাছে না পৌঁছত, তাহলে সে নিন্দিত অবস্থায় উন্মুক্ত প্রান্তরে নিক্ষিপ্ত হত।');
INSERT INTO "rawai" VALUES(68,50,'68:50','তারপর তার রব তাকে মনোনীত করলেন এবং তাকে সৎকর্মপরায়ণদের অন্তর্ভুক্ত করলেন।');
INSERT INTO "rawai" VALUES(68,51,'68:51','আর কাফিররা যখন উপদেশবাণী শুনে তখন তারা যেন তাদের দৃষ্টি দ্বারা তোমাকে আছড়ে ফেলবে, আর তারা বলে, ‘এ তো এক পাগল’।');
INSERT INTO "rawai" VALUES(68,52,'68:52','আর এ কুরআন তো সৃষ্টিকুলের জন্য শুধুই উপদেশবাণী।');
INSERT INTO "rawai" VALUES(69,1,'69:1','অবশ্যম্ভাবী ঘটনা (কিয়ামত)।');
INSERT INTO "rawai" VALUES(69,2,'69:2','অবশ্যম্ভাবী ঘটনা কী?');
INSERT INTO "rawai" VALUES(69,3,'69:3','আর কিসে তোমাকে জানাবে অবশ্যম্ভাবী ঘটনা কী?');
INSERT INTO "rawai" VALUES(69,4,'69:4','সামূদ ও ‘আদ সম্প্রদায় সজোরে আঘাতকারী (কিয়ামত)কে অস্বীকার করেছিল।');
INSERT INTO "rawai" VALUES(69,5,'69:5','আর সামূদ সম্প্রদায়, তাদেরকে বিকট শব্দ দ্বারা ধ্বংস করা হয়েছিল।');
INSERT INTO "rawai" VALUES(69,6,'69:6','আর ‘আদ সম্প্রদায়, তাদেরকে ধ্বংস করা হয়েছিল প্রচন্ড ঠান্ডা ঝঞ্ঝাবায়ু দ্বারা।');
INSERT INTO "rawai" VALUES(69,7,'69:7','তিনি তাদের উপর তা সাত রাত ও আট দিন বিরামহীনভাবে চাপিয়ে দিয়েছিলেন। ফলে তুমি উক্ত সম্প্রদায়কে সেখানে লুটিয়ে পড়া অবস্থায় দেখতে পেতে যেন তারা সারশূন্য খেজুর গাছের মত।');
INSERT INTO "rawai" VALUES(69,8,'69:8','তারপর তুমি কি তাদের জন্য কোন অবশিষ্ট কিছু দেখতে পাও?');
INSERT INTO "rawai" VALUES(69,9,'69:9','আর ফির‘আউন, তার পূর্ববর্তীরা এবং উল্টে দেয়া জনপদবাসীরা পাপাচারে লিপ্ত হয়েছিল।');
INSERT INTO "rawai" VALUES(69,10,'69:10','আর তারা তাদের রবের রাসূলকে অমান্য করেছিল। সুতরাং তিনি তাদেরকে অত্যন্ত কঠোরভাবে পাকড়াও করলেন।');
INSERT INTO "rawai" VALUES(69,11,'69:11','যখন জলোচ্ছ্বাস হল, অবশ্যই তখন আমি তোমাদেরকে নৌযানে আরোহণ করিয়েছি।');
INSERT INTO "rawai" VALUES(69,12,'69:12','একে তোমাদের নিমিত্তে উপদেশ বানানোর জন্য এবং সংরক্ষণকারী কান তা সংরক্ষণ করার জন্য।');
INSERT INTO "rawai" VALUES(69,13,'69:13','অতঃপর যখন শিংগায় ফুঁক দেয়া হবে- একটি মাত্র ফুঁক।');
INSERT INTO "rawai" VALUES(69,14,'69:14','আর যমীন ও পর্বতমালাকে সরিয়ে নেয়া হবে এবং মাত্র একটি আঘাতে এগুলো চূর্ণ-বিচূর্ণ হয়ে যাবে।');
INSERT INTO "rawai" VALUES(69,15,'69:15','ফলে সে দিন মহাঘটনা সংঘটিত হবে।');
INSERT INTO "rawai" VALUES(69,16,'69:16','আর আসমান বিদীর্ণ হয়ে যাবে। ফলে সেদিন তা হয়ে যাবে দুর্বল বিক্ষিপ্ত।');
INSERT INTO "rawai" VALUES(69,17,'69:17','ফেরেশতাগণ আসমানের বিভিন্ন প্রান্তে থাকবে। সেদিন তোমার রবের আরশকে আটজন ফেরেশতা তাদের উর্ধ্বে বহন করবে।');
INSERT INTO "rawai" VALUES(69,18,'69:18','সেদিন তোমাদেরকে উপস্থিত করা হবে। তোমাদের কোন গোপনীয়তাই গোপন থাকবে না।');
INSERT INTO "rawai" VALUES(69,19,'69:19','তখন যার আমলনামা তার ডান হাতে দেয়া হবে সে বলবে, ‘নাও, আমার আমলনামা পড়ে দেখ’।');
INSERT INTO "rawai" VALUES(69,20,'69:20','‘আমার দৃঢ় বিশ্বাস ছিল যে, আমি আমার হিসাবের সম্মুখীন হব’।');
INSERT INTO "rawai" VALUES(69,21,'69:21','সুতরাং সে সন্তোষজনক জীবনে থাকবে।');
INSERT INTO "rawai" VALUES(69,22,'69:22','সুউচ্চ জান্নাতে,');
INSERT INTO "rawai" VALUES(69,23,'69:23','তার ফলসমূহ নিকটবর্তী থাকবে।');
INSERT INTO "rawai" VALUES(69,24,'69:24','(বলা হবে,) ‘বিগত দিনসমূহে তোমরা যা অগ্রে প্রেরণ করেছ তার বিনিময়ে তোমরা তৃপ্তি সহকারে খাও ও পান কর’।');
INSERT INTO "rawai" VALUES(69,25,'69:25','কিন্তু যার আমলনামা তার বাম হাতে দেয়া হবে সে বলবে, ‘হায়, আমাকে যদি আমার আমলনামা দেয়া না হত’!');
INSERT INTO "rawai" VALUES(69,26,'69:26','‘আর যদি আমি না জানতাম আমার হিসাব’!');
INSERT INTO "rawai" VALUES(69,27,'69:27','‘হায়, মৃত্যুই যদি আমার চূড়ান্ত ফয়সালা হত’!');
INSERT INTO "rawai" VALUES(69,28,'69:28','‘আমার সম্পদ আমার কোন কাজেই আসল না!’');
INSERT INTO "rawai" VALUES(69,29,'69:29','‘আমার ক্ষমতাও আমার থেকে চলে গেল!');
INSERT INTO "rawai" VALUES(69,30,'69:30','(বলা হবে,) ‘তাকে ধর অতঃপর তাকে বেড়ি পরিয়ে দাও।’');
INSERT INTO "rawai" VALUES(69,31,'69:31','‘তারপর তাকে তোমরা নিক্ষেপ কর জাহান্নামে’।');
INSERT INTO "rawai" VALUES(69,32,'69:32','‘তারপর তাকে বাঁধ এমন এক শেকলে যার দৈর্ঘ্য হবে সত্তর হাত।’');
INSERT INTO "rawai" VALUES(69,33,'69:33','সে তো মহান আল্লাহর প্রতি ঈমান পোষণ করত না,');
INSERT INTO "rawai" VALUES(69,34,'69:34','আর মিসকীনকে খাদ্যদানে উৎসাহিত করত না।');
INSERT INTO "rawai" VALUES(69,35,'69:35','অতএব আজ এখানে তার কোন অন্তরঙ্গ বন্ধু থাকবে না।');
INSERT INTO "rawai" VALUES(69,36,'69:36','আর ক্ষত-নিংসৃত পূঁজ ছাড়া কোন খাদ্য থাকবে না,');
INSERT INTO "rawai" VALUES(69,37,'69:37','অপরাধীরাই শুধু তা খাবে।');
INSERT INTO "rawai" VALUES(69,38,'69:38','অতএব তোমরা যা দেখছ, আমি তার কসম করছি।');
INSERT INTO "rawai" VALUES(69,39,'69:39','আর যা তোমরা দেখছ না তারও,');
INSERT INTO "rawai" VALUES(69,40,'69:40','নিশ্চয়ই এটি এক সম্মানিত রাসূলের বাণী।');
INSERT INTO "rawai" VALUES(69,41,'69:41','আর এটি কোন কবির কথা নয়। তোমরা কমই বিশ্বাস কর।');
INSERT INTO "rawai" VALUES(69,42,'69:42','আর কোন গণকের কথাও নয়। তোমরা কমই উপদেশ গ্রহণ কর।');
INSERT INTO "rawai" VALUES(69,43,'69:43','এটি সৃষ্টিকুলের রবের পক্ষ থেকে নাযিলকৃত।');
INSERT INTO "rawai" VALUES(69,44,'69:44','যদি সে আমার নামে কোন মিথ্যা রচনা করত,');
INSERT INTO "rawai" VALUES(69,45,'69:45','তবে আমি তার ডান হাত পাকড়াও করতাম।');
INSERT INTO "rawai" VALUES(69,46,'69:46','তারপর অবশ্যই আমি তার হৃদপিন্ডের শিরা কেটে ফেলতাম।');
INSERT INTO "rawai" VALUES(69,47,'69:47','অতঃপর তোমাদের মধ্যে কেউই তাকে রক্ষা করার থাকত না।');
INSERT INTO "rawai" VALUES(69,48,'69:48','আর এটিতো মুত্তাকীদের জন্য এক নিশ্চিত উপদেশ।');
INSERT INTO "rawai" VALUES(69,49,'69:49','আর আমি অবশ্যই জানি যে, তোমাদের মধ্যে কতক রয়েছে মিথ্যারোপকারী।');
INSERT INTO "rawai" VALUES(69,50,'69:50','আর এটি নিশ্চয় কাফিরদের জন্য এক নিশ্চিত অনুশোচনার কারণ।');
INSERT INTO "rawai" VALUES(69,51,'69:51','আর নিশ্চয় এটি সুনিশ্চিত সত্য।');
INSERT INTO "rawai" VALUES(69,52,'69:52','অতএব তুমি তোমার মহান রবের নামে তাসবীহ পাঠ কর।');
INSERT INTO "rawai" VALUES(70,1,'70:1','এক প্রশ্নকারী জিজ্ঞাসা করল এমন আযাব সম্পর্কে, যা আপতিত হবে-* *আয়াতটির আরেক অর্থ হল, ‘একজন প্রার্থনাকারী এমন আযাবের দো‘আ করল যা আপতিত হবে’।');
INSERT INTO "rawai" VALUES(70,2,'70:2','কাফিরদের উপর, যার কোন প্রতিরোধকারী নেই।');
INSERT INTO "rawai" VALUES(70,3,'70:3','ঊর্ধ্বারোহণের সোপানসমূহের অধিকারী আল্লাহর পক্ষ থেকে,');
INSERT INTO "rawai" VALUES(70,4,'70:4','ফেরেশতাগণ ও রূহ এমন এক দিনে আল্লাহর পানে ঊর্ধ্বগামী হয়, যার পরিমাণ পঞ্চাশ হাজার বছর।');
INSERT INTO "rawai" VALUES(70,5,'70:5','অতএব তুমি উত্তমরূপে ধৈর্যধারণ কর।');
INSERT INTO "rawai" VALUES(70,6,'70:6','তারা তো এটিকে সুদূরপরাহত মনে করে।');
INSERT INTO "rawai" VALUES(70,7,'70:7','আর আমি দেখছি তা আসন্ন।');
INSERT INTO "rawai" VALUES(70,8,'70:8','সেদিন আসমান হয়ে যাবে গলিত ধাতুর ন্যায়।');
INSERT INTO "rawai" VALUES(70,9,'70:9','এবং পর্বতসমূহ হয়ে যাবে রঙিন পশমের ন্যায়।');
INSERT INTO "rawai" VALUES(70,10,'70:10','আর অন্তরঙ্গ বন্ধু অন্তরঙ্গ বন্ধুকে জিজ্ঞাসা করবে না।');
INSERT INTO "rawai" VALUES(70,11,'70:11','তাদেরকে একে অপরের দৃষ্টিগোচর করা হবে। অপরাধী চাইবে যদি সে সেদিনের শাস্তি থেকে তার সন্তান-সন্ততিকে পণ হিসেবে দিয়ে মুক্তি পেতে,');
INSERT INTO "rawai" VALUES(70,12,'70:12','আর তার স্ত্রী ও ভাইকে,');
INSERT INTO "rawai" VALUES(70,13,'70:13','আর তার জ্ঞাতি-গোষ্ঠীকে, যারা তাকে আশ্রয় দিত।');
INSERT INTO "rawai" VALUES(70,14,'70:14','আর যমীনে যারা আছে তাদের সবাইকে, যাতে এটি তাকে রক্ষা করে।');
INSERT INTO "rawai" VALUES(70,15,'70:15','কখনো নয়! এটিতো লেলিহান আগুন।');
INSERT INTO "rawai" VALUES(70,16,'70:16','যা মাথার চামড়া খসিয়ে নেবে।');
INSERT INTO "rawai" VALUES(70,17,'70:17','জাহান্নাম তাকে ডাকবে যে পৃষ্ঠ প্রদর্শন করেছিল এবং মুখ ফিরিয়ে নিয়েছিল ।');
INSERT INTO "rawai" VALUES(70,18,'70:18','আর সম্পদ জমা করেছিল, অতঃপর তা সংরক্ষণ করে রেখেছিল।');
INSERT INTO "rawai" VALUES(70,19,'70:19','নিশ্চয় মানুষকে সৃষ্টি করা হয়েছে অস্থির করে।');
INSERT INTO "rawai" VALUES(70,20,'70:20','যখন তাকে বিপদ স্পর্শ করে তখন সে হয়ে পড়ে অতিমাত্রায় উৎকন্ঠিত।');
INSERT INTO "rawai" VALUES(70,21,'70:21','আর যখন কল্যাণ তাকে স্পর্শ করে তখন সে হয়ে পড়ে অতিশয় কৃপণ।');
INSERT INTO "rawai" VALUES(70,22,'70:22','সালাত আদায়কারীগণ ছাড়া,');
INSERT INTO "rawai" VALUES(70,23,'70:23','যারা তাদের সালাতের ক্ষেত্রে নিয়মিত।');
INSERT INTO "rawai" VALUES(70,24,'70:24','আর যাদের ধন-সম্পদে রয়েছে নির্ধারিত হক,');
INSERT INTO "rawai" VALUES(70,25,'70:25','যাচঞাকারী ও বঞ্চিতের,');
INSERT INTO "rawai" VALUES(70,26,'70:26','আর যারা প্রতিফল-দিবসকে সত্য বলে বিশ্বাস করে।');
INSERT INTO "rawai" VALUES(70,27,'70:27','আর যারা তাদের রবের আযাব সম্পর্কে ভীত-সন্ত্রস্ত।');
INSERT INTO "rawai" VALUES(70,28,'70:28','নিশ্চয় তাদের রবের আযাব থেকে কেউ নিরাপদ নয়।');
INSERT INTO "rawai" VALUES(70,29,'70:29','আর যারা তাদের যৌনাংগসমূহের হিফাযতকারী।');
INSERT INTO "rawai" VALUES(70,30,'70:30','তবে তাদের স্ত্রী ও তাদের ডান হাত যাদের মালিক হয়েছে সে দাসীগণের ক্ষেত্র ছাড়া। তাহলে তারা সে ক্ষেত্রে নিন্দনীয় হবে না।');
INSERT INTO "rawai" VALUES(70,31,'70:31','তবে যে কেউ এদের বাইরে অন্যকে কামনা করে, তারাই তো সীমালংঘনকারী।');
INSERT INTO "rawai" VALUES(70,32,'70:32','আর যারা নিজদের আমানত ও ওয়াদা রক্ষাকারী,');
INSERT INTO "rawai" VALUES(70,33,'70:33','আর যারা তাদের সাক্ষ্যদানে অটল,');
INSERT INTO "rawai" VALUES(70,34,'70:34','আর যারা নিজদের সালাতের হিফাযত করে,');
INSERT INTO "rawai" VALUES(70,35,'70:35','তারাই জান্নাতসমূহে সম্মানিত হবে।');
INSERT INTO "rawai" VALUES(70,36,'70:36','কাফিরদের কী হল যে, তারা তোমার দিকে ছুটছে,');
INSERT INTO "rawai" VALUES(70,37,'70:37','ডানে ও বামে দলে দলে?');
INSERT INTO "rawai" VALUES(70,38,'70:38','তাদের প্রত্যেক ব্যক্তি কি আশা করে যে, তাকে প্রাচুর্যময় জান্নাতে দাখিল করা হবে?');
INSERT INTO "rawai" VALUES(70,39,'70:39','কখনো নয়, নিশ্চয় আমি তাদেরকে সৃষ্টি করেছি তারা যা জানে তা থেকে।');
INSERT INTO "rawai" VALUES(70,40,'70:40','অতএব, আমি উদয়স্থল ও অস্তাচলসমূহের রবের কসম করছি যে, আমি অবশ্যই সক্ষম!');
INSERT INTO "rawai" VALUES(70,41,'70:41','তাদের চাইতে উত্তমদেরকে তাদের স্থলে নিয়ে আসতে এবং আমি অক্ষম নই।');
INSERT INTO "rawai" VALUES(70,42,'70:42','অতএব তাদেরকে ছেড়ে দাও, তারা (বেহুদা কথায়) মত্ত থাকুক আর খেল-তামাশা করুক যতক্ষণ না তারা দেখা পায় সেদিনের, যার প্রতিশ্রুতি তাদেরকে দেয়া হয়েছে।');
INSERT INTO "rawai" VALUES(70,43,'70:43','যেদিন দ্রুতবেগে তারা কবর থেকে বের হয়ে আসবে, যেন তারা কোন লক্ষ্যের দিকে ছুটছে,');
INSERT INTO "rawai" VALUES(70,44,'70:44','অবনত চোখে। লাঞ্ছনা তাদেরকে আচ্ছন্ন করবে! এটিই সেদিন যার ওয়াদা তাদেরকে দেয়া হয়েছিল।');
INSERT INTO "rawai" VALUES(71,1,'71:1','নিশ্চয় আমি নূহকে পাঠিয়েছিলাম তার কওমের কাছে (এ কথা বলে), ‘তোমার কওমকে সতর্ক কর, তাদের নিকট যন্ত্রণাদায়ক আযাব আসার পূর্বে’।');
INSERT INTO "rawai" VALUES(71,2,'71:2','সে বলল, ‘হে আমার কওম! নিশ্চয় আমি তোমাদের জন্য এক স্পষ্ট সতর্ককারী-');
INSERT INTO "rawai" VALUES(71,3,'71:3','যে, তোমরা আল্লাহর ‘ইবাদাত কর, তাঁকে ভয় কর এবং আমার আনুগত্য কর’।');
INSERT INTO "rawai" VALUES(71,4,'71:4','‘তিনি তোমাদের জন্য তোমাদের পাপসমূহ ক্ষমা করবেন এবং তোমাদেরকে নির্দিষ্ট সময় পর্যন্ত অবকাশ দেবেন; আল্লাহর নির্ধারিত সময় আসলে কিছুতেই তা বিলম্বিত করা হয় না, যদি তোমরা জানতে’!');
INSERT INTO "rawai" VALUES(71,5,'71:5','সে বলল, ‘হে আমার রব! আমি তো আমার কওমকে রাত-দিন আহবান করেছি।');
INSERT INTO "rawai" VALUES(71,6,'71:6','‘অতঃপর আমার আহবান কেবল তাদের পলায়নই বাড়িয়ে দিয়েছে’।');
INSERT INTO "rawai" VALUES(71,7,'71:7','‘আর যখনই আমি তাদেরকে আহবান করেছি ‘যেন আপনি তাদেরকে ক্ষমা করেন’, তারা নিজদের কানে আঙ্গুল ঢুকিয়ে দিয়েছে, নিজদেরকে পোশাকে আবৃত করেছে, (অবাধ্যতায়) অনড় থেকেছে এবং দম্ভভরে ঔদ্ধত্য প্রকাশ করেছে’।');
INSERT INTO "rawai" VALUES(71,8,'71:8','‘তারপর আমি তাদেরকে প্রকাশ্যে আহবান করেছি’।');
INSERT INTO "rawai" VALUES(71,9,'71:9','অতঃপর তাদেরকে আমি প্রকাশ্যে এবং অতি গোপনেও আহবান করেছি।');
INSERT INTO "rawai" VALUES(71,10,'71:10','আর বলেছি, ‘তোমাদের রবের কাছে ক্ষমা চাও; নিশ্চয় তিনি পরম ক্ষমাশীল’।');
INSERT INTO "rawai" VALUES(71,11,'71:11','‘তিনি তোমাদের উপর মুষলধারে বৃষ্টি বর্ষণ করবেন,');
INSERT INTO "rawai" VALUES(71,12,'71:12','‘আর তোমাদেরকে ধন-সম্পদ ও সন্তান- সন্ততি দিয়ে সাহায্য করবেন এবং তোমাদের জন্য বাগ-বাগিচা দেবেন আর দেবেন নদী-নালা’।');
INSERT INTO "rawai" VALUES(71,13,'71:13','‘তোমাদের কী হল, তোমরা আল্লাহর শ্রেষ্ঠত্বের পরোয়া করছ না’?');
INSERT INTO "rawai" VALUES(71,14,'71:14','‘অথচ তিনি তোমাদেরকে নানা স্তরে সৃষ্টি করেছেন’।');
INSERT INTO "rawai" VALUES(71,15,'71:15','‘তোমরা কি লক্ষ্য কর না যে, কীভাবে আল্লাহ স্তরে স্তরে সপ্তাকাশ সৃষ্টি করেছেন’?');
INSERT INTO "rawai" VALUES(71,16,'71:16','আর এগুলোর মধ্যে চাঁদকে সৃষ্টি করেছেন আলো আর সূর্যকে সৃষ্টি করেছেন প্রদীপরূপে’।');
INSERT INTO "rawai" VALUES(71,17,'71:17','‘আর আল্লাহ তোমাদেরকে উদগত করেছেন মাটি থেকে’।');
INSERT INTO "rawai" VALUES(71,18,'71:18','‘তারপর তিনি তোমাদেরকে তাতে ফিরিয়ে নেবেন এবং নিশ্চিতভাবে তোমাদেরকে পুনরুত্থিত করবেন’।');
INSERT INTO "rawai" VALUES(71,19,'71:19','‘আর আল্লাহ পৃথিবীকে তোমাদের জন্য বিস্তৃত করেছেন,');
INSERT INTO "rawai" VALUES(71,20,'71:20','যেন তোমরা সেখানে প্রশস্ত পথে চলতে পার’।');
INSERT INTO "rawai" VALUES(71,21,'71:21','নূহ বলল, ‘হে আমার রব! তারা আমার অবাধ্য হয়েছে এবং এমন একজনের অনুসরণ করেছে যার ধন-সম্পদ ও সন্তান-সন্ততি কেবল তার ক্ষতিই বাড়িয়ে দেয়’।');
INSERT INTO "rawai" VALUES(71,22,'71:22','‘আর তারা ভয়ানক ষড়যন্ত্র করেছে’।');
INSERT INTO "rawai" VALUES(71,23,'71:23','আর তারা বলে, ‘তোমরা তোমাদের উপাস্যদের বর্জন করো না; বর্জন করো না ওয়াদ, সুওয়া‘, ইয়াগূছ, ইয়া‘ঊক ও নাসরকে’।');
INSERT INTO "rawai" VALUES(71,24,'71:24','‘বস্তুত তারা অনেককে পথভ্রষ্ট করেছে, আর (হে আল্লাহ) আপনি যালিমদেরকে ভ্রষ্টতা ছাড়া আর কিছুই বাড়াবেন না’।');
INSERT INTO "rawai" VALUES(71,25,'71:25','তাদের পাপের কারণে তাদেরকে ডুবিয়ে দেয়া হল অতঃপর আগুনে প্রবেশ করানো হল; তারা নিজদের সাহায্যকারী হিসেবে আল্লাহ ছাড়া আর কাউকে পায়নি।');
INSERT INTO "rawai" VALUES(71,26,'71:26','আর নূহ বলল, ‘হে আমার রব! যমীনের উপর কোন কাফিরকে অবশিষ্ট রাখবেন না’।');
INSERT INTO "rawai" VALUES(71,27,'71:27','‘আপনি যদি তাদেরকে অবশিষ্ট রাখেন তবে তারা আপনার বান্দাদেরকে পথভ্রষ্ট করবে এবং দুরাচারী ও কাফির ছাড়া অন্য কারো জন্ম দেবে না’।');
INSERT INTO "rawai" VALUES(71,28,'71:28','‘হে আমার রব! আমাকে, আমার পিতা-মাতাকে, যে আমার ঘরে ঈমানদার হয়ে প্রবেশ করবে তাকে এবং মুমিন নারী-পুরুষকে ক্ষমা করুন এবং ধ্বংস ছাড়া আপনি যালিমদের আর কিছুই বাড়িয়ে দেবেন না।’');
INSERT INTO "rawai" VALUES(72,1,'72:1','বল, ‘আমার প্রতি ওহী করা হয়েছে যে, নিশ্চয় জিনদের একটি দল মনোযোগ সহকারে শুনেছে। অতঃপর বলেছে, ‘আমরা তো এক বিস্ময়কর কুরআন শুনেছি,');
INSERT INTO "rawai" VALUES(72,2,'72:2','যা সত্যের দিকে হিদায়াত করে; অতঃপর আমরা তাতে ঈমান এনেছি। আর আমরা কখনো আমাদের রবের সাথে কাউকে শরীক করব না’।');
INSERT INTO "rawai" VALUES(72,3,'72:3','‘আর নিশ্চয় আমাদের রবের মর্যাদা সমুচ্চ। তিনি কোন সংগিনী গ্রহণ করেননি এবং না কোন সন্তান’।');
INSERT INTO "rawai" VALUES(72,4,'72:4','‘আর আমাদের মধ্যকার নির্বোধেরা আল্লাহর ব্যাপারে অবাস্তব কথা- বার্তা বলত’।');
INSERT INTO "rawai" VALUES(72,5,'72:5','‘অথচ আমরা তো ধারণা করতাম যে, মানুষ ও জিন কখনো আল্লাহ সম্পর্কে মিথ্যা আরোপ করবে না’।');
INSERT INTO "rawai" VALUES(72,6,'72:6','আর নিশ্চয় কতিপয় মানুষ কতিপয় জিনের আশ্রয় নিত, ফলে তারা তাদের অহংকার বাড়িয়ে দিয়েছিল।');
INSERT INTO "rawai" VALUES(72,7,'72:7','আর নিশ্চয় তারা ধারণা করেছিল যেমন তোমরা ধারণা করেছ যে, আল্লাহ কাউকে কখনই পুনরুত্থিত করবেন না।');
INSERT INTO "rawai" VALUES(72,8,'72:8','‘আর নিশ্চয় আমরা আকাশ স্পর্শ করতে চেয়েছিলাম*, কিন্তু আমরা সেটাকে পেলাম যে, তা কঠোর প্রহরী এবং উল্কাপিন্ড দ্বারা পরিপূর্ণ’। *আকাশের সংবাদ সংগ্রহ করার চেষ্টা করেছিলাম।');
INSERT INTO "rawai" VALUES(72,9,'72:9','আর আমরা তো সংবাদ শোনার জন্য আকাশের বিভিন্ন ঘাটিতে বসতাম, কিন্তু এখন যে শুনতে চাইবে, সে তার জন্য প্রস্তুত জ্বলন্ত উল্কাপিন্ড পাবে’।');
INSERT INTO "rawai" VALUES(72,10,'72:10','‘আর নিশ্চয় আমরা জানি না, যমীনে যারা রয়েছে তাদের জন্য অকল্যাণ চাওয়া হয়েছে, নাকি তাদের রব তাদের ব্যাপারে মঙ্গল চেয়েছেন’।');
INSERT INTO "rawai" VALUES(72,11,'72:11','‘আর নিশ্চয় আমাদের কতিপয় সৎকর্মশীল এবং কতিপয় এর ব্যতিক্রম। আমরা ছিলাম বিভিন্ন মত ও পথে বিভক্ত’।');
INSERT INTO "rawai" VALUES(72,12,'72:12','‘আর আমরা তো বুঝতে পেরেছি যে, আমরা কিছুতেই যমীনের মধ্যে আল্লাহকে অপারগ করতে পারব না এবং পালিয়েও কখনো তাকে অপারগ করতে পারব না’।');
INSERT INTO "rawai" VALUES(72,13,'72:13','‘আর নিশ্চয় আমরা যখন হিদায়াতের বাণী শুনলাম, তখন তার প্রতি ঈমান আনলাম। আর যে তার রবের প্রতি ঈমান আনে, সে না কোন ক্ষতির আশংকা করবে এবং না কোন অন্যায়ের’।');
INSERT INTO "rawai" VALUES(72,14,'72:14','‘আর নিশ্চয় আমাদের মধ্যে কিছু সংখ্যক আছে আত্মসমর্পণকারী এবং আমাদের মধ্যে কিছু সংখ্যক সীমালংঘনকারী। কাজেই যারা আত্মসমর্পণ করেছে, তারাই সঠিক পথ বেছে নিয়েছে’।');
INSERT INTO "rawai" VALUES(72,15,'72:15','‘আর যারা সীমালঙ্ঘনকারী, তারা তো জাহান্নামের ইন্ধন’।');
INSERT INTO "rawai" VALUES(72,16,'72:16','আর তারা যদি সঠিক পথে অবিচল থাকত, তাহলে আমি অবশ্যই তাদেরকে প্রচুর পরিমাণে পানি পান করাতাম।');
INSERT INTO "rawai" VALUES(72,17,'72:17','যাতে আমি তা দিয়ে তাদেরকে পরীক্ষা করতে পারি। আর যে তার রবের স্মরণ থেকে মুখ ফিরিয়ে নেয়, তাকে তিনি কঠিন আযাবে প্রবেশ করাবেন।');
INSERT INTO "rawai" VALUES(72,18,'72:18','আর নিশ্চয় মসজিদগুলো আল্লাহরই জন্য। কাজেই তোমরা আল্লাহর সাথে অন্য কাউকে ডেকো না।');
INSERT INTO "rawai" VALUES(72,19,'72:19','আর নিশ্চয় আল্লাহর বান্দা* যখন তাঁকে ডাকার জন্য দাঁড়াল, তখন তারা** তার নিকট ভিড় জমাল। *রাসূলুল্লাহ সাল্লাল্লাহু আলাইহি ওয়াসাল্লাম। **কাফিরগণ।');
INSERT INTO "rawai" VALUES(72,20,'72:20','বল, ‘নিশ্চয় আমি আমার রবকে ডাকি এবং তার সাথে কাউকে শরীক করি না’।');
INSERT INTO "rawai" VALUES(72,21,'72:21','বল, ‘নিশ্চয় আমি তোমাদের জন্য না কোন অকল্যাণ করার ক্ষমতা রাখি এবং না কোন কল্যাণ করার’।');
INSERT INTO "rawai" VALUES(72,22,'72:22','বল, ‘নিশ্চয় আল্লাহর কাছ থেকে কেউ আমাকে রক্ষা করতে পারবে না এবং তিনি ছাড়া কখনো আমি কোন আশ্রয়ও পাব না।');
INSERT INTO "rawai" VALUES(72,23,'72:23','কেবল আল্লাহর বাণী ও তাঁর রিসালাত পৌঁছানোই দায়িত্ব। আর যে আল্লাহ ও তাঁর রাসূলকে অমান্য করে, তার জন্য রয়েছে জাহান্নামের আগুন। তাতে তারা চিরস্থায়ী হবে।');
INSERT INTO "rawai" VALUES(72,24,'72:24','অবশেষে যখন তারা তা প্রত্যক্ষ করবে, যে সম্পর্কে তাদেরকে সাবধান করা হয়েছিল। তখন তারা জানতে পারবে যে, সাহায্যকারী হিসেবে কে অধিকতর দুর্বল এবং সংখ্যায় কারা সবচেয়ে কম।');
INSERT INTO "rawai" VALUES(72,25,'72:25','বল, ‘আমি জানি না তোমাদেরকে যার প্রতিশ্রুতি দেয়া হয়েছে, তা কি নিকটবর্তী নাকি এর জন্য আমার রব কোন দীর্ঘ মেয়াদ নির্ধারণ করবেন’।');
INSERT INTO "rawai" VALUES(72,26,'72:26','তিনি অদৃশ্যের জ্ঞানী, আর তিনি তাঁর অদৃশ্যের জ্ঞান কারো কাছে প্রকাশ করেন না।');
INSERT INTO "rawai" VALUES(72,27,'72:27','তবে তাঁর মনোনীত রাসূল ছাড়া। আর তিনি তখন তার সামনে ও তার পিছনে প্রহরী নিযুক্ত করবেন।');
INSERT INTO "rawai" VALUES(72,28,'72:28','যাতে তিনি এটা জানতে পারেন যে, তারা* তাদের রবের রিসালাত পৌঁছিয়েছে কিনা। আর তাদের কাছে যা রয়েছে, তা তিনি পরিবেষ্টন করে রেখেছেন এবং তিনি প্রতিটি বস্তু গুণে গুণে হিসাব করে রেখেছেন। *রাসূলগণ।');
INSERT INTO "rawai" VALUES(73,1,'73:1','হে চাদর আবৃত!');
INSERT INTO "rawai" VALUES(73,2,'73:2','রাতে সালাতে দাঁড়াও কিছু অংশ ছাড়া।');
INSERT INTO "rawai" VALUES(73,3,'73:3','রাতের অর্ধেক কিংবা তার চেয়ে কিছুটা কম।');
INSERT INTO "rawai" VALUES(73,4,'73:4','অথবা তার চেয়ে একটু বাড়াও। আর স্পষ্টভাবে ধীরে ধীরে কুরআন আবৃত্তি কর।');
INSERT INTO "rawai" VALUES(73,5,'73:5','নিশ্চয় আমি তোমার প্রতি এক অতিভারী বাণী নাযিল করছি।');
INSERT INTO "rawai" VALUES(73,6,'73:6','নিশ্চয় রাত-জাগরণ আত্মসংযমের জন্য অধিকতর প্রবল এবং স্পষ্ট বলার জন্য অধিকতর উপযোগী।');
INSERT INTO "rawai" VALUES(73,7,'73:7','নিশ্চয় তোমার জন্য দিনের বেলায় রয়েছে দীর্ঘ কর্মব্যস্ততা।');
INSERT INTO "rawai" VALUES(73,8,'73:8','আর তুমি তোমার রবের নাম স্মরণ কর এবং একাগ্রচিত্তে তাঁর প্রতি নিমগ্ন হও।');
INSERT INTO "rawai" VALUES(73,9,'73:9','তিনি পূর্ব ও পশ্চিমের রব, তিনি ছাড়া কোন (সত্য) ইলাহ নেই। সুতরাং তাঁকেই তুমি কার্য সম্পাদনকারীরূপে গ্রহণ কর।');
INSERT INTO "rawai" VALUES(73,10,'73:10','আর তারা যা বলে, তাতে তুমি ধৈর্য ধারণ কর এবং সুন্দরভাবে তাদেরকে পরিহার করে চল।');
INSERT INTO "rawai" VALUES(73,11,'73:11','আর ছেড়ে দাও আমাকে এবং বিলাস সামগ্রীর অধিকারী মিথ্যারোপকারীদেরকে। আর তাদেরকে কিছুকাল অবকাশ দাও।');
INSERT INTO "rawai" VALUES(73,12,'73:12','নিশ্চয় আমার নিকট রয়েছে শিকলসমূহ ও প্রজ্জ্বলিত আগুন।');
INSERT INTO "rawai" VALUES(73,13,'73:13','ও কাঁটাযুক্ত খাদ্য এবং যন্ত্রণাদায়ক শাস্তি।');
INSERT INTO "rawai" VALUES(73,14,'73:14','যেদিন যমীন ও পর্বতমালা প্রকম্পিত হবে এবং পাহাড়গুলো চলমান বালুকারাশিতে পরিণত হবে।');
INSERT INTO "rawai" VALUES(73,15,'73:15','নিশ্চয় আমি তোমাদের জন্য স্বাক্ষীস্বরূপ তোমাদের কাছে রাসূল পাঠিয়েছি যেমনিভাবে ফির‘আউনের কাছে রাসূল পাঠিয়েছিলাম।');
INSERT INTO "rawai" VALUES(73,16,'73:16','কিন্তু ফির‘আউন রাসূলকে অমান্য করল। তাই আমি তাকে অত্যন্ত শক্তভাবে পাকড়াও করলাম।');
INSERT INTO "rawai" VALUES(73,17,'73:17','অতএব তোমরা যদি কুফরী কর, তাহলে তোমরা সেদিন কিভাবে আত্মরক্ষা করবে যেদিন কিশোরদেরকে বৃদ্ধে পরিণত করবে।');
INSERT INTO "rawai" VALUES(73,18,'73:18','যার কারণে আসমান হবে বিদীর্ণ, আল্লাহর ওয়াদা হবে বাস্তবায়িত।');
INSERT INTO "rawai" VALUES(73,19,'73:19','নিশ্চয় এ এক উপদেশ। অতএব যে চায় সে তার রবের দিকে পথ অবলম্বন করুক।');
INSERT INTO "rawai" VALUES(73,20,'73:20','নিশ্চয় তোমার রব জানেন যে, তুমি রাতের দুই তৃতীয়াংশের কিছু কম, অথবা অর্ধরাত অথবা রাতের এক তৃতীয়াংশ সালাতে দাঁড়িয়ে থাক এবং তোমার সাথে যারা আছে তাদের মধ্য থেকে একটি দলও। আর আল্লাহ রাত ও দিন নিরূপণ করেন। তিনি জানেন যে, তোমরা তা করতে সক্ষম হবে না। তাই তিনি তোমাদেরকে ক্ষমা করলেন। অতএব তোমরা কুরআন থেকে যতটুকু সহজ ততটুকু পড়। তিনি জানেন তোমাদের মধ্যে কেউ কেউ অসুস্থ হয়ে পড়বে। আর কেউ কেউ আল্লাহর অনুগ্রহ সন্ধানে পৃথিবীতে ভ্রমণ করবে, আর কেউ কেউ আল্লাহর পথে লড়াই করবে। অতএব তোমরা কুরআন থেকে যতটুকু সহজ ততটুকু পড়। আর সালাত কায়েম কর, যাকাত দাও এবং আল্লাহকে উত্তম ঋণ দাও। আর তোমরা নিজদের জন্য মঙ্গলজনক যা কিছু অগ্রে পাঠাবে তোমরা তা আল্লাহর কাছে পাবে প্রতিদান হিসেবে উৎকৃষ্টতর ও মহত্তর রূপে। আর তোমরা আল্লাহর কাছে ক্ষমা চাও। নিশ্চয় আল্লাহ অতীব ক্ষমাশীল, পরম দয়ালু।');
INSERT INTO "rawai" VALUES(74,1,'74:1','হে বস্ত্রাবৃত!');
INSERT INTO "rawai" VALUES(74,2,'74:2','উঠ, অতঃপর সতর্ক কর।');
INSERT INTO "rawai" VALUES(74,3,'74:3','আর তোমার রবের শ্রেষ্ঠত্ব ঘোষণা কর।');
INSERT INTO "rawai" VALUES(74,4,'74:4','আর তোমার পোশাক-পরিচ্ছদ পবিত্র কর।');
INSERT INTO "rawai" VALUES(74,5,'74:5','আর অপবিত্রতা বর্জন কর।');
INSERT INTO "rawai" VALUES(74,6,'74:6','আর অধিক পাওয়ার আশায় দান করো না।');
INSERT INTO "rawai" VALUES(74,7,'74:7','আর তোমার রবের জন্যই ধৈর্যধারণ কর।');
INSERT INTO "rawai" VALUES(74,8,'74:8','অতঃপর যখন শিঙ্গায় ফুঁক দেয়া হবে,');
INSERT INTO "rawai" VALUES(74,9,'74:9','আর সেদিন হবে কঠিন দিন।');
INSERT INTO "rawai" VALUES(74,10,'74:10','কাফিরদের জন্য সহজ নয়।');
INSERT INTO "rawai" VALUES(74,11,'74:11','আমাকে এবং যাকে আমি সৃষ্টি করেছি তাকে একাকী ছেড়ে দাও।');
INSERT INTO "rawai" VALUES(74,12,'74:12','আর আমি তাকে দিয়েছি অঢেল সম্পদ,');
INSERT INTO "rawai" VALUES(74,13,'74:13','আর উপস্থিত অনেক পুত্র।');
INSERT INTO "rawai" VALUES(74,14,'74:14','আর তার জন্য (জীবনকে) সুগম স্বাচ্ছন্দ্যময় করেছি।');
INSERT INTO "rawai" VALUES(74,15,'74:15','এসবের পরেও সে আকাংখা করে যে, আমি আরো বাড়িয়ে দেই।');
INSERT INTO "rawai" VALUES(74,16,'74:16','কখনো নয়, নিশ্চয় সে ছিল আমার নিদর্শনাবলীর বিরুদ্ধাচারী।');
INSERT INTO "rawai" VALUES(74,17,'74:17','অচিরেই আমি তাকে জাহান্নামের পিচ্ছিল পাথরে আরোহণ করতে বাধ্য করব।');
INSERT INTO "rawai" VALUES(74,18,'74:18','নিশ্চয় সে চিন্তা ভাবনা করল এবং সিদ্ধান্ত গ্রহণ করল।');
INSERT INTO "rawai" VALUES(74,19,'74:19','অতঃপর সে ধ্বংস হোক! কীভাবে সে সিদ্ধান্ত গ্রহণ করল?');
INSERT INTO "rawai" VALUES(74,20,'74:20','তারপর সে ধ্বংস হোক! কীভাবে সে সিদ্ধান্ত গ্রহণ করল?');
INSERT INTO "rawai" VALUES(74,21,'74:21','তারপর সে তাকাল।');
INSERT INTO "rawai" VALUES(74,22,'74:22','তারপর সে ভ্রূকুঞ্চিত করল এবং মুখ বিকৃত করল।');
INSERT INTO "rawai" VALUES(74,23,'74:23','তারপর সে পিছনে ফিরল এবং অহংকার করল।');
INSERT INTO "rawai" VALUES(74,24,'74:24','অতঃপর সে বলল, ‘এ তো লোক পরম্পরায়প্রাপ্ত যাদু ছাড়া আর কিছুই নয়’।');
INSERT INTO "rawai" VALUES(74,25,'74:25','‘এটা তো মানুষের কথামাত্র’।');
INSERT INTO "rawai" VALUES(74,26,'74:26','অচিরেই আমি তাকে জাহান্নামের আগুনে প্রবেশ করাব।');
INSERT INTO "rawai" VALUES(74,27,'74:27','কিসে তোমাকে জানাবে জাহান্নামের আগুন কী?');
INSERT INTO "rawai" VALUES(74,28,'74:28','এটা অবশিষ্টও রাখবে না এবং ছেড়েও দেবে না।');
INSERT INTO "rawai" VALUES(74,29,'74:29','চামড়াকে দগ্ধ করে কালো করে দেবে।');
INSERT INTO "rawai" VALUES(74,30,'74:30','তার উপর রয়েছে ঊনিশজন (প্রহরী)।');
INSERT INTO "rawai" VALUES(74,31,'74:31','আর আমি ফেরেশতাদেরকেই জাহান্নামের তত্ত্বাবধায়ক বানিয়েছি। আর কাফিরদের জন্য পরীক্ষাস্বরূপ আমি তাদের সংখ্যা নির্ধারণ করেছি। যাতে কিতাবপ্রাপ্তরা দৃঢ়ভাবে বিশ্বাস করে; আর মুমিনদের ঈমান বেড়ে যায় এবং কিতাবপ্রাপ্তরা ও মুমিনরা সন্দেহ পোষণ না করে। আর যেন যাদের অন্তরে রোগ আছে তারা এবং অবশিষ্টরা বলে, এরূপ উপমা দ্বারা আল্লাহ কী ইচ্ছা করেছেন? এভাবেই আল্লাহ যাকে ইচ্ছা পথভ্রষ্ট করেন আর যাকে ইচ্ছা সঠিক পথে পরিচালিত করেন। আর তোমার রবের বাহিনী সম্পর্কে তিনি ছাড়া কেউ জানেন না। আর এ হচ্ছে মানুষের জন্য উপদেশমাত্র।');
INSERT INTO "rawai" VALUES(74,32,'74:32','কখনো নয়, চাঁদের কসম!');
INSERT INTO "rawai" VALUES(74,33,'74:33','রাতের কসম, যখন তা সরে চলে যায়,');
INSERT INTO "rawai" VALUES(74,34,'74:34','প্রভাতের কসম, যখন তা উদ্ভাসিত হয়।');
INSERT INTO "rawai" VALUES(74,35,'74:35','নিশ্চয় জাহান্নাম মহাবিপদসমূহের অন্যতম।');
INSERT INTO "rawai" VALUES(74,36,'74:36','মানুষের জন্য সতর্ককারীস্বরূপ।');
INSERT INTO "rawai" VALUES(74,37,'74:37','তোমাদের মধ্যে যে চায় অগ্রসর হতে অথবা পিছিয়ে থাকতে, তার জন্য।');
INSERT INTO "rawai" VALUES(74,38,'74:38','প্রতিটি প্রাণ নিজ অর্জনের কারণে দায়বদ্ধ।');
INSERT INTO "rawai" VALUES(74,39,'74:39','কিন্তু ডান দিকের লোকেরা নয়,');
INSERT INTO "rawai" VALUES(74,40,'74:40','বাগ-বাগিচার মধ্যে তারা একে অপরকে জিজ্ঞাসা করবে,');
INSERT INTO "rawai" VALUES(74,41,'74:41','অপরাধীদের সম্পর্কে,');
INSERT INTO "rawai" VALUES(74,42,'74:42','কিসে তোমাদেরকে জাহান্নামের আগুনে প্রবেশ করাল?');
INSERT INTO "rawai" VALUES(74,43,'74:43','তারা বলবে, ‘আমরা সালাত আদায়কারীদের মধ্যে অন্তর্ভুক্ত ছিলাম না’।');
INSERT INTO "rawai" VALUES(74,44,'74:44','‘আর আমরা অভাবগ্রস্তকে খাদ্য দান করতাম না’।');
INSERT INTO "rawai" VALUES(74,45,'74:45','‘আর আমরা অনর্থক আলাপকারীদের সাথে (বেহুদা আলাপে) মগ্ন থাকতাম’।');
INSERT INTO "rawai" VALUES(74,46,'74:46','‘আর আমরা প্রতিদান দিবসকে অস্বীকার করতাম’।');
INSERT INTO "rawai" VALUES(74,47,'74:47','''অবশেষে আমাদের কাছে মৃত্যু আগমন করে''।');
INSERT INTO "rawai" VALUES(74,48,'74:48','অতএব সুপারিশকারীদের সুপারিশ তাদের কোন উপকার করবে না।');
INSERT INTO "rawai" VALUES(74,49,'74:49','আর তাদের কী হয়েছে যে, তারা উপদেশ বাণী হতে বিমুখ?');
INSERT INTO "rawai" VALUES(74,50,'74:50','তারা যেন ভীত-সন্ত্রস্ত হয়ে পলায়নরত বন্য গাধা।');
INSERT INTO "rawai" VALUES(74,51,'74:51','যারা সিংহের ভয়ে পলায়ন করেছে।');
INSERT INTO "rawai" VALUES(74,52,'74:52','বরং তাদের মধ্যকার প্রত্যেক ব্যক্তিই কামনা করে যে তাকে উন্মুক্ত গ্রন্থ প্রদান করা হোক।');
INSERT INTO "rawai" VALUES(74,53,'74:53','কখনও নয়! বরং তারা আখিরাতকে ভয় করে না।');
INSERT INTO "rawai" VALUES(74,54,'74:54','কখনও নয়! এটিতো উপদেশ মাত্র।');
INSERT INTO "rawai" VALUES(74,55,'74:55','অতএব যার ইচ্ছা সে তা থেকে উপদেশ গ্রহণ করুক।');
INSERT INTO "rawai" VALUES(74,56,'74:56','আল্লাহর ইচ্ছা ছাড়া কেউ উপদেশ গ্রহণ করতে পারে না। তিনিই ভয়ের যোগ্য এবং ক্ষমার অধিকারী।');
INSERT INTO "rawai" VALUES(75,1,'75:1','আমি কসম করছি কিয়ামতের দিনের!');
INSERT INTO "rawai" VALUES(75,2,'75:2','আমি আরো কসম করছি আত্ম-ভৎর্সনাকারী আত্মার!');
INSERT INTO "rawai" VALUES(75,3,'75:3','মানুষ কি মনে করে যে, আমি কখনই তার অস্থিসমূহ একত্র করব না?');
INSERT INTO "rawai" VALUES(75,4,'75:4','হ্যাঁ, আমি তার আংগুলের অগ্রভাগসমূহও পুনর্বিন্যস্ত করতে সক্ষম।');
INSERT INTO "rawai" VALUES(75,5,'75:5','বরং মানুষ চায় ভবিষ্যতেও পাপাচার করতে।');
INSERT INTO "rawai" VALUES(75,6,'75:6','সে প্রশ্ন করে, ‘কবে কিয়ামতের দিন’?');
INSERT INTO "rawai" VALUES(75,7,'75:7','যখন চক্ষু হতচকিত হবে।');
INSERT INTO "rawai" VALUES(75,8,'75:8','আর চাঁদ কিরণহীন হবে,');
INSERT INTO "rawai" VALUES(75,9,'75:9','আর চাঁদ ও সূর্যকে একত্র করা হবে।');
INSERT INTO "rawai" VALUES(75,10,'75:10','সেদিন মানুষ বলবে, ‘পালাবার স্থান কোথায়’?');
INSERT INTO "rawai" VALUES(75,11,'75:11','না, কোন আশ্রয়স্থল নেই।');
INSERT INTO "rawai" VALUES(75,12,'75:12','ঠাঁই শুধু সেদিন তোমার রবের নিকট।');
INSERT INTO "rawai" VALUES(75,13,'75:13','সেদিন মানুষকে অবহিত করা হবে কী সে অগ্রে পাঠিয়েছিল এবং পশ্চাতে পাঠিয়েছিল।');
INSERT INTO "rawai" VALUES(75,14,'75:14','বরং মানুষ তার নিজের উপর দৃষ্টিমান।');
INSERT INTO "rawai" VALUES(75,15,'75:15','যদিও সে নানা অজুহাত পেশ করে থাকে।');
INSERT INTO "rawai" VALUES(75,16,'75:16','কুরআন তাড়াতাড়ি আয়ত্ত করার উদ্দেশ্যে তুমি তোমার জিহবাকে দ্রুত আন্দোলিত করো না।');
INSERT INTO "rawai" VALUES(75,17,'75:17','নিশ্চয়ই এর সংরক্ষণ ও পাঠ আমার দায়িত্বে।');
INSERT INTO "rawai" VALUES(75,18,'75:18','অতঃপর যখন আমি তা পাঠ করি তখন তুমি তার পাঠের অনুসরণ কর।');
INSERT INTO "rawai" VALUES(75,19,'75:19','তারপর তার বর্ণনার দায়িত্ব আমারই।');
INSERT INTO "rawai" VALUES(75,20,'75:20','কখনো না, বরং তোমরা দুনিয়ার জীবনকে ভালবাস।');
INSERT INTO "rawai" VALUES(75,21,'75:21','আর তোমরা ছেড়ে দিচ্ছ আখিরাতকে।');
INSERT INTO "rawai" VALUES(75,22,'75:22','সেদিন কতক মুখমন্ডল হবে হাস্যোজ্জ্বল।');
INSERT INTO "rawai" VALUES(75,23,'75:23','তাদের রবের প্রতি দৃষ্টিনিক্ষেপকারী।');
INSERT INTO "rawai" VALUES(75,24,'75:24','আর সেদিন অনেক মুখমন্ডল হবে বিবর্ণ-বিষন্ন।');
INSERT INTO "rawai" VALUES(75,25,'75:25','তারা ধারণা করবে যে, এক বিপর্যয় তাদের উপর আপতিত করা হবে।');
INSERT INTO "rawai" VALUES(75,26,'75:26','কখনই না, যখন প্রাণ কণ্ঠাগত হবে।');
INSERT INTO "rawai" VALUES(75,27,'75:27','আর বলা হবে, ‘কে তাকে বাঁচাবে’?');
INSERT INTO "rawai" VALUES(75,28,'75:28','আর সে মনে করবে, এটিই বিদায়ক্ষণ।');
INSERT INTO "rawai" VALUES(75,29,'75:29','আর পায়ের গোছার সংগে পায়ের গোছা জড়িয়ে যাবে।');
INSERT INTO "rawai" VALUES(75,30,'75:30','সেদিন তোমার রবের কাছেই সকলকে হাঁকিয়ে নেয়া হবে।');
INSERT INTO "rawai" VALUES(75,31,'75:31','সুতরাং সে বিশ্বাসও করেনি এবং সালাতও আদায় করেনি।');
INSERT INTO "rawai" VALUES(75,32,'75:32','বরং সে সত্য প্রত্যাখ্যান করেছিল এবং ফিরে গিয়েছিল।');
INSERT INTO "rawai" VALUES(75,33,'75:33','তারপর সে দম্ভভরে পরিবার-পরিজনের কাছে চলে গিয়েছিল।');
INSERT INTO "rawai" VALUES(75,34,'75:34','দুর্ভোগ তোমার জন্য এবং দুর্ভোগ!');
INSERT INTO "rawai" VALUES(75,35,'75:35','তারপরও দুর্ভোগ তোমার জন্য এবং দুর্ভোগ!');
INSERT INTO "rawai" VALUES(75,36,'75:36','মানুষ কি মনে করে যে, তাকে এমনি ছেড়ে দেয়া হবে?');
INSERT INTO "rawai" VALUES(75,37,'75:37','সে কি বীর্যের শুক্রবিন্দু ছিল না যা স্খলিত হয়?');
INSERT INTO "rawai" VALUES(75,38,'75:38','অতঃপর সে ‘আলাকায় পরিণত হয়। তারপর আল্লাহ তাকে সুন্দর আকৃতিতে সৃষ্টি করেছেন এবং সুবিন্যস্ত করেছেন।');
INSERT INTO "rawai" VALUES(75,39,'75:39','অতঃপর তিনি তা থেকে সৃষ্টি করেন জোড়ায় জোড়ায় পুরুষ ও নারী।');
INSERT INTO "rawai" VALUES(75,40,'75:40','তিনি কি মৃতদের জীবিত করতে সক্ষম নন?');
INSERT INTO "rawai" VALUES(76,1,'76:1','মানুষের উপর কি কালের এমন কোন ক্ষণ আসেনি যখন সে উল্লেখযোগ্য কিছুই ছিল না?');
INSERT INTO "rawai" VALUES(76,2,'76:2','আমি মানুষকে সৃষ্টি করেছি মিশ্র শুক্রবিন্দু থেকে, আমি তাকে পরীক্ষা করব, ফলে আমি তাকে বানিয়েছি শ্রবণ ও দৃষ্টিশক্তিসম্পন্ন।');
INSERT INTO "rawai" VALUES(76,3,'76:3','অবশ্যই আমি তাকে পথ প্রদর্শন করেছি, হয় সে শোকরকারী অথবা অকৃতজ্ঞ।');
INSERT INTO "rawai" VALUES(76,4,'76:4','আমি কাফিরদের জন্য প্রস্তুত করে রেখেছি শেকল, বেড়ি ও প্রজ্বলিত অগ্নি।');
INSERT INTO "rawai" VALUES(76,5,'76:5','নিশ্চয় সৎকর্মশীলরা পান করবে এমন পানপাত্র থেকে যার মিশ্রণ হবে কাফূর।');
INSERT INTO "rawai" VALUES(76,6,'76:6','এমন এক ঝর্ণা যা থেকে আল্লাহর বান্দাগণ পান করবে, তারা এটিকে যথা ইচ্ছা প্রবাহিত করবে।');
INSERT INTO "rawai" VALUES(76,7,'76:7','তারা মানত পূর্ণ করে এবং সেদিনকে ভয় করে যার অকল্যাণ হবে সুবিস্তৃত।');
INSERT INTO "rawai" VALUES(76,8,'76:8','তারা খাদ্যের প্রতি আসক্তি থাকা সত্ত্বেও মিসকীন, ইয়াতীম ও বন্দীকে খাদ্য দান করে।');
INSERT INTO "rawai" VALUES(76,9,'76:9','তারা বলে,‘আমরা তো আল্লাহর সন্তুষ্টির উদ্দেশ্যে তোমাদেরকে খাদ্য দান করি। আমরা তোমাদের থেকে কোন প্রতিদান চাই না এবং কোন শোকরও না।');
INSERT INTO "rawai" VALUES(76,10,'76:10','আমরা আমাদের রবের পক্ষ থেকে এক ভয়ংকর ভীতিপ্রদ দিবসের ভয় করি।');
INSERT INTO "rawai" VALUES(76,11,'76:11','সুতরাং সেই দিবসের অকল্যাণ থেকে আল্লাহ তাদের রক্ষা করলেন এবং তাদের প্রদান করলেন উজ্জ্বলতা ও উৎফুল্লতা।');
INSERT INTO "rawai" VALUES(76,12,'76:12','আর তারা যে ধৈর্যধারণ করেছিল তার পরিণামে তিনি তাদেরকে জান্নাত ও রেশমী বস্ত্রের পুরস্কার প্রদান করবেন।');
INSERT INTO "rawai" VALUES(76,13,'76:13','তারা সেখানে সুউচ্চ আসনে হেলান দিয়ে আসীন থাকবে। তারা সেখানে না দেখবে অতিশয় গরম, আর না অত্যধিক শীত।');
INSERT INTO "rawai" VALUES(76,14,'76:14','তাদের উপর সন্নিহিত থাকবে উদ্যানের ছায়া এবং তার ফলমূলের থোকাসমূহ তাদের সম্পূর্ণ আয়ত্তাধীন করা হবে।');
INSERT INTO "rawai" VALUES(76,15,'76:15','তাদের চারপাশে আবর্তিত হবে রৌপ্যপাত্র ও স্ফটিক স্বচ্ছ পানপাত্র-');
INSERT INTO "rawai" VALUES(76,16,'76:16','রূপার ন্যায় শুভ্র স্ফটিক পাত্র; যার পরিমাপ তারা নির্ধারণ করবে।');
INSERT INTO "rawai" VALUES(76,17,'76:17','সেখানে তাদেরকে পান করানো হবে পাত্রভরা আদা-মিশ্রিত সুরা,');
INSERT INTO "rawai" VALUES(76,18,'76:18','সেখানকার এক ঝর্ণা যার নাম হবে সালসাবীল।');
INSERT INTO "rawai" VALUES(76,19,'76:19','আর তাদের চারপাশে প্রদক্ষিণ করবে চিরকিশোরেরা; তুমি তাদেরকে দেখলে বিক্ষিপ্ত মুক্তা মনে করবে।');
INSERT INTO "rawai" VALUES(76,20,'76:20','আর তুমি যখন দেখবে তুমি সেখানে দেখতে পাবে স্বাচ্ছন্দ্য ও বিরাট সাম্রাজ্য।');
INSERT INTO "rawai" VALUES(76,21,'76:21','তাদের উপর থাকবে সবুজ ও মিহি রেশমের পোশাক এবং মোটা রেশমের পোশাক, আর তাদেরকে পরিধান করানো হবে রূপার চুড়ি এবং তাদের রব তাদেরকে পান করাবেন পবিত্র পানীয়।');
INSERT INTO "rawai" VALUES(76,22,'76:22','(তাদেরকে বলা হবে) ‘এটিই তোমাদের পুরস্কার; আর তোমাদের প্রচেষ্টা ছিল প্রশংসাযোগ্য।’');
INSERT INTO "rawai" VALUES(76,23,'76:23','নিশ্চয় আমি তোমার প্রতি পর্যায়ক্রমে আল-কুরআন নাযিল করেছি।');
INSERT INTO "rawai" VALUES(76,24,'76:24','অতএব তোমার রবের হুকুমের জন্য ধৈর্য ধারণ কর এবং তাদের মধ্য থেকে কোন পাপিষ্ঠ বা অস্বীকারকারীর আনুগত্য করো না।');
INSERT INTO "rawai" VALUES(76,25,'76:25','আর সকাল-সন্ধ্যায় তোমার রবের নাম স্মরণ কর,');
INSERT INTO "rawai" VALUES(76,26,'76:26','আর রাতের একাংশে তার উদ্দেশ্যে সিজদাবনত হও এবং দীর্ঘ রাত ধরে তাঁর তাসবীহ পাঠ কর।');
INSERT INTO "rawai" VALUES(76,27,'76:27','নিশ্চয় এরা দুনিয়ার জীবনকে ভালবাসে আর তাদের সামনে রেখে দেয় এক কঠিন দিন।');
INSERT INTO "rawai" VALUES(76,28,'76:28','আমিই তাদেরকে সৃষ্টি করেছি এবং তাদের গ্রন্থি শক্ত করে দিয়েছি আর আমি চাইলে তাদের স্থানে তাদের মত (মানুষ) দিয়ে পরিবর্তন করে দিতে পারি।');
INSERT INTO "rawai" VALUES(76,29,'76:29','নিশ্চয় এটি উপদেশ; অতএব যে চায় সে যেন তার রবের দিকে একটি পথ গ্রহণ করে।');
INSERT INTO "rawai" VALUES(76,30,'76:30','আর আল্লাহ ইচ্ছা না করলে তোমরা ইচ্ছা করবে না; নিশ্চয় আল্লাহ মহাজ্ঞানী, প্রাজ্ঞ।');
INSERT INTO "rawai" VALUES(76,31,'76:31','যাকে ইচ্ছা তিনি স্বীয় রহমতে প্রবেশ করাবেন এবং যালিমদের জন্য তিনি প্রস্তুত রেখেছেন যন্ত্রণাদায়ক আযাব।');
INSERT INTO "rawai" VALUES(77,1,'77:1','কসম কল্যাণের উদ্দেশ্যে প্রেরিত বাতাসের,');
INSERT INTO "rawai" VALUES(77,2,'77:2','আর প্রচন্ড বেগে প্রবাহিত ঝঞ্ঝার।');
INSERT INTO "rawai" VALUES(77,3,'77:3','কসম মেঘমালা ও বৃষ্টি বিক্ষিপ্তকারী বায়ুর,');
INSERT INTO "rawai" VALUES(77,4,'77:4','আর সুস্পষ্টরূপে পার্থক্যকারীর (আল-কুরআনের আয়াতের)।');
INSERT INTO "rawai" VALUES(77,5,'77:5','অতঃপর কসম, উপদেশগ্রন্থ আনয়নকারী (ফেরেশতাদের),');
INSERT INTO "rawai" VALUES(77,6,'77:6','অজুহাত দূরকারী ও সতর্ককারী।');
INSERT INTO "rawai" VALUES(77,7,'77:7','তোমাদেরকে যা কিছুর ওয়াদা দেয়া হয়েছে তা অবশ্যই ঘটবে।');
INSERT INTO "rawai" VALUES(77,8,'77:8','যখন তারকারাজি আলোহীন হবে,');
INSERT INTO "rawai" VALUES(77,9,'77:9','আর আকাশ বিদীর্ণ হবে,');
INSERT INTO "rawai" VALUES(77,10,'77:10','আর যখন পাহাড়গুলি চূর্ণবিচূর্ণ হবে,');
INSERT INTO "rawai" VALUES(77,11,'77:11','আর যখন রাসূলদেরকে নির্ধারিত সময়ে উপস্থিত করা হবে;');
INSERT INTO "rawai" VALUES(77,12,'77:12','কোন্ দিনের জন্য এসব স্থগিত করা হয়েছিল?');
INSERT INTO "rawai" VALUES(77,13,'77:13','বিচার দিনের জন্য।');
INSERT INTO "rawai" VALUES(77,14,'77:14','আর কিসে তোমাকে জানাবে বিচার দিবস কী?');
INSERT INTO "rawai" VALUES(77,15,'77:15','মিথ্যারোপকারীদের জন্য সেদিনের দুর্ভোগ!');
INSERT INTO "rawai" VALUES(77,16,'77:16','আমি কি পূর্ববর্তীদেরকে ধ্বংস করিনি?');
INSERT INTO "rawai" VALUES(77,17,'77:17','তারপর পরবর্তীদেরকে তাদের অনুসারী বানাই।');
INSERT INTO "rawai" VALUES(77,18,'77:18','অপরাধীদের সাথে আমি এমনই করে থাকি।');
INSERT INTO "rawai" VALUES(77,19,'77:19','মিথ্যারোপকারীদের জন্য সেদিনের ধ্বংস!');
INSERT INTO "rawai" VALUES(77,20,'77:20','আমি কি তোমাদেরকে তুচ্ছ পানি দিয়ে সৃষ্টি করিনি?');
INSERT INTO "rawai" VALUES(77,21,'77:21','অতঃপর তা আমি রেখেছি সুরক্ষিত আধারে');
INSERT INTO "rawai" VALUES(77,22,'77:22','একটি নির্দিষ্ট সময় পর্যন্ত।');
INSERT INTO "rawai" VALUES(77,23,'77:23','অতঃপর আমি পরিমাপ করেছি। আর আমিই উত্তম পরিমাপকারী।');
INSERT INTO "rawai" VALUES(77,24,'77:24','মিথ্যারোপকারীদের জন্য সেদিনের দুর্ভোগ!');
INSERT INTO "rawai" VALUES(77,25,'77:25','আমি কি ভূমিকে ধারণকারী বানাইনি');
INSERT INTO "rawai" VALUES(77,26,'77:26','জীবিত ও মৃতদেরকে?');
INSERT INTO "rawai" VALUES(77,27,'77:27','আর এখানে স্থাপন করেছি সুদৃঢ় ও সুউচ্চ পর্বত এবং তোমাদেরকে পান করিয়েছি সুপেয় পানি।');
INSERT INTO "rawai" VALUES(77,28,'77:28','মিথ্যারোপকারীদের জন্য সেদিনের দুর্ভোগ!');
INSERT INTO "rawai" VALUES(77,29,'77:29','(তাদেরকে বলা হবে), তোমরা যা অস্বীকার করতে সেদিকে গমন কর।');
INSERT INTO "rawai" VALUES(77,30,'77:30','যাও তিন শাখা বিশিষ্ট আগুনের ছায়ায়,');
