INSERT INTO "rawai" VALUES(77,35,'77:35','এটা এমন দিন যেদিন তারা কথা বলবে না।');
INSERT INTO "rawai" VALUES(77,36,'77:36','আর তাদেরকে অজুহাত পেশ করার অনুমতিও দেয়া হবে না ।');
INSERT INTO "rawai" VALUES(77,37,'77:37','মিথ্যারোপকারীদের জন্য সেদিনের ধ্বংস!');
INSERT INTO "rawai" VALUES(77,38,'77:38','এটি ফয়সালার দিন; তোমাদেরকে ও পূর্ববর্তীদেরকে আমি একত্র করেছি।');
INSERT INTO "rawai" VALUES(77,39,'77:39','তোমাদের কোন কৌশল থাকলে আমার বিরুদ্ধে প্রয়োগ কর।');
INSERT INTO "rawai" VALUES(77,40,'77:40','মিথ্যারোপকারীদের জন্য সেদিনের দুর্ভোগ!');
INSERT INTO "rawai" VALUES(77,41,'77:41','নিশ্চয় মুত্তাকীরা থাকবে ছায়া ও ঝর্ণাবহুল স্থানে,');
INSERT INTO "rawai" VALUES(77,42,'77:42','আর নিজদের বাসনানুযায়ী ফলমূল-এর মধ্যে।');
INSERT INTO "rawai" VALUES(77,43,'77:43','(তাদেরকে বলা হবে) ‘তোমরা যে আমল করতে তার প্রতিদানস্বরূপ তৃপ্তির সাথে পানাহার কর;');
INSERT INTO "rawai" VALUES(77,44,'77:44','সৎকর্মশীলদের আমরা এমন-ই প্রতিদান দিয়ে থাকি।');
INSERT INTO "rawai" VALUES(77,45,'77:45','মিথ্যারোপকারীদের জন্য সেদিনের দুর্ভোগ!');
INSERT INTO "rawai" VALUES(77,46,'77:46','(হে কাফিররা!) তোমরা আহার কর এবং ভোগ কর ক্ষণকাল; নিশ্চয় তোমরা অপরাধী।');
INSERT INTO "rawai" VALUES(77,47,'77:47','মিথ্যারোপকারীদের জন্য সেদিনের ধ্বংস!');
INSERT INTO "rawai" VALUES(77,48,'77:48','তাদেরকে যখন বলা হয় ‘রুকূ‘ কর,’ তখন তারা রুকূ‘ করত না।');
INSERT INTO "rawai" VALUES(77,49,'77:49','মিথ্যারোপকারীদের জন্য সেদিনের দুর্ভোগ!');
INSERT INTO "rawai" VALUES(77,50,'77:50','সুতরাং কুরআনের পরিবর্তে আর কোন্ বাণীর প্রতি তারা ঈমান আনবে?');
INSERT INTO "rawai" VALUES(78,1,'78:1','কোন্ বিষয় সম্পর্কে তারা পরস্পর জিজ্ঞাসাবাদ করছে ?');
INSERT INTO "rawai" VALUES(78,2,'78:2','মহাসংবাদটি সম্পর্কে,');
INSERT INTO "rawai" VALUES(78,3,'78:3','যে বিষয়ে তারা মতভেদ করছে।');
INSERT INTO "rawai" VALUES(78,4,'78:4','কখনো না, অচিরেই তারা জানতে পারবে।');
INSERT INTO "rawai" VALUES(78,5,'78:5','তারপর কখনো না, তারা অচিরেই জানতে পারবে।');
INSERT INTO "rawai" VALUES(78,6,'78:6','আমি কি বানাইনি যমীনকে শয্যা?');
INSERT INTO "rawai" VALUES(78,7,'78:7','আর পর্বতসমূহকে পেরেক?');
INSERT INTO "rawai" VALUES(78,8,'78:8','আর আমি তোমাদেরকে সৃষ্টি করেছি জোড়ায় জোড়ায়।');
INSERT INTO "rawai" VALUES(78,9,'78:9','আর আমি তোমাদের নিদ্রাকে করেছি বিশ্রাম।');
INSERT INTO "rawai" VALUES(78,10,'78:10','আর আমি রাতকে করেছি আবরণ।');
INSERT INTO "rawai" VALUES(78,11,'78:11','আর আমি দিনকে করেছি জীবিকার্জনের সময়।');
INSERT INTO "rawai" VALUES(78,12,'78:12','আর আমি তোমাদের উপরে বানিয়েছি সাতটি সুদৃঢ় আকাশ।');
INSERT INTO "rawai" VALUES(78,13,'78:13','আর আমি সৃষ্টি করেছি উজ্জ্বল একটি প্রদীপ।');
INSERT INTO "rawai" VALUES(78,14,'78:14','আর আমি মেঘমালা থেকে প্রচুর পানি বর্ষণ করেছি।');
INSERT INTO "rawai" VALUES(78,15,'78:15','যাতে তা দিয়ে আমি শস্য ও উদ্ভিদ উৎপন্ন করতে পারি।');
INSERT INTO "rawai" VALUES(78,16,'78:16','আর ঘন উদ্যানসমূহ।');
INSERT INTO "rawai" VALUES(78,17,'78:17','নিশ্চয় ফয়সালার দিন নির্ধারিত আছে।');
INSERT INTO "rawai" VALUES(78,18,'78:18','সেদিন শিঙ্গায় ফুঁক দেয়া হবে, তখন তোমরা দলে দলে আসবে।');
INSERT INTO "rawai" VALUES(78,19,'78:19','আর আসমান খুলে দেয়া হবে, ফলে তা হবে বহু দ্বারবিশিষ্ট।');
INSERT INTO "rawai" VALUES(78,20,'78:20','আর পর্বতসমূহকে চলমান করা হবে, ফলে সেগুলো মরীচিকা হয়ে যাবে।');
INSERT INTO "rawai" VALUES(78,21,'78:21','নিশ্চয় জাহান্নাম গোপন ফাঁদ।');
INSERT INTO "rawai" VALUES(78,22,'78:22','সীমালঙ্ঘনকারীদের জন্য প্রত্যাবর্তন স্থল।');
INSERT INTO "rawai" VALUES(78,23,'78:23','সেখানে তারা যুগ যুগ ধরে অবস্থান করবে।');
INSERT INTO "rawai" VALUES(78,24,'78:24','সেখানে তারা কোন শীতলতা আস্বাদন করবে না এবং না কোন পানীয়।');
INSERT INTO "rawai" VALUES(78,25,'78:25','ফুটন্ত পানি ও পুঁজ ছাড়া।');
INSERT INTO "rawai" VALUES(78,26,'78:26','উপযুক্ত প্রতিফলস্বরূপ।');
INSERT INTO "rawai" VALUES(78,27,'78:27','নিশ্চয় তারা হিসাবের আশা করত না।');
INSERT INTO "rawai" VALUES(78,28,'78:28','আর তারা আমার আয়াতসমূহকে সম্পূর্ণরূপে অস্বীকার করেছিল।');
INSERT INTO "rawai" VALUES(78,29,'78:29','আর সব কিছুই আমি লিখিতভাবে সংরক্ষণ করেছি।');
INSERT INTO "rawai" VALUES(78,30,'78:30','সুতরাং তোমরা স্বাদ গ্রহণ কর। আর আমি তো কেবল তোমাদের আযাবই বৃদ্ধি করব।');
INSERT INTO "rawai" VALUES(78,31,'78:31','নিশ্চয় মুত্তাকীদের জন্য রয়েছে সফলতা।');
INSERT INTO "rawai" VALUES(78,32,'78:32','উদ্যানসমূহ ও আঙ্গুরসমূহ।');
INSERT INTO "rawai" VALUES(78,33,'78:33','আর সমবয়স্কা উদ্‌ভিন্ন যৌবনা তরুণী।');
INSERT INTO "rawai" VALUES(78,34,'78:34','আর পরিপূর্ণ পানপাত্র।');
INSERT INTO "rawai" VALUES(78,35,'78:35','তারা সেখানে কোন অসার ও মিথ্যা কথা শুনবে না।');
INSERT INTO "rawai" VALUES(78,36,'78:36','তোমার রবের পক্ষ থেকে প্রতিফল, যথোচিত দানস্বরূপ।');
INSERT INTO "rawai" VALUES(78,37,'78:37','যিনি আসমানসমূহ, যমীন ও এতদোভয়ের মধ্যবর্তী সবকিছুর রব, পরম করুণাময়। তারা তাঁর সামনে কথা বলার সামর্থ্য রাখবে না।');
INSERT INTO "rawai" VALUES(78,38,'78:38','সেদিন রূহ* ও ফেরেশতাগণ সারিবদ্ধভাবে দাঁড়াবে, যাকে পরম করুণাময় অনুমতি দেবেন সে ছাড়া অন্যরা কোন কথা বলবে না। আর সে সঠিক কথাই বলবে। *হযরত জিবরীল (আঃ)।');
INSERT INTO "rawai" VALUES(78,39,'78:39','ঐ দিনটি সত্য। অতএব যে চায়, সে তার রবের নিকট আশ্রয় গ্রহণ করুক।');
INSERT INTO "rawai" VALUES(78,40,'78:40','নিশ্চয় আমি তোমাদেরকে একটি নিকটবর্তী আযাব সম্পর্কে সতর্ক করলাম। যেদিন মানুষ দেখতে পাবে, তার দু’হাত কী অগ্রে প্রেরণ করেছে এবং কাফির বলবে ‘হায়, আমি যদি মাটি হতাম’!');
INSERT INTO "rawai" VALUES(79,1,'79:1','কসম নির্মমভাবে (কাফিরদের রূহ) উৎপাটনকারীদের।* * ১-৫ নং আয়াতে বিভিন্ন কাজে নিয়োজিত ফেরেশতাদের কসম করা হয়েছে।');
INSERT INTO "rawai" VALUES(79,2,'79:2','আর কসম সহজভাবে বন্ধনমুক্তকারীদের।');
INSERT INTO "rawai" VALUES(79,3,'79:3','আর কসম দ্রুতগতিতে সন্তরণকারীদের।');
INSERT INTO "rawai" VALUES(79,4,'79:4','আর দ্রুতবেগে অগ্রসরমানদের।');
INSERT INTO "rawai" VALUES(79,5,'79:5','অতঃপর কসম সকল কার্যনির্বাহকারীদের।');
INSERT INTO "rawai" VALUES(79,6,'79:6','সেদিন কম্পনকারী* প্রকম্পিত করবে। *অর্থাৎ প্রথম শিংগাধ্বনি।');
INSERT INTO "rawai" VALUES(79,7,'79:7','তাকে অনুসরণ করবে পরবর্তী কম্পনকারী।* *দ্বিতীয় শিংগাধ্বনি।');
INSERT INTO "rawai" VALUES(79,8,'79:8','সেদিন অনেক হৃদয় ভীত-সন্ত্রস্ত হবে।');
INSERT INTO "rawai" VALUES(79,9,'79:9','তাদের দৃষ্টিসমূহ নত হবে।');
INSERT INTO "rawai" VALUES(79,10,'79:10','তারা বলে, ‘আমরা কি পূর্বাবস্থায় প্রত্যাবর্তিত হবই,');
INSERT INTO "rawai" VALUES(79,11,'79:11','যখন আমরা চূর্ণ-বিচূর্ণ হাড় হয়ে যাব’?');
INSERT INTO "rawai" VALUES(79,12,'79:12','তারা বলে, ‘তাহলে তা তো এক ক্ষতিকর প্রত্যাবর্তন’।');
INSERT INTO "rawai" VALUES(79,13,'79:13','আর ওটা তো কেবল এক বিকট আওয়াজ।');
INSERT INTO "rawai" VALUES(79,14,'79:14','তৎক্ষনাৎ তারা ভূ-পৃষ্ঠে উপস্থিত হবে।');
INSERT INTO "rawai" VALUES(79,15,'79:15','মূসার বৃত্তান্ত তোমার কাছে পৌঁছেছে কি?');
INSERT INTO "rawai" VALUES(79,16,'79:16','যখন তার রব তাকে পবিত্র তুওয়া উপত্যকায় ডেকেছিলেন,');
INSERT INTO "rawai" VALUES(79,17,'79:17','‘ফির‘আউনের কাছে যাও, নিশ্চয় সে সীমালংঘন করেছে’।');
INSERT INTO "rawai" VALUES(79,18,'79:18','অতঃপর বল ‘তোমার কি ইচ্ছা আছে যে, তুমি পবিত্র হবে’?');
INSERT INTO "rawai" VALUES(79,19,'79:19','‘আর আমি তোমাকে তোমার রবের দিকে পথ দেখাব, যাতে তুমি তাঁকে ভয় কর?’');
INSERT INTO "rawai" VALUES(79,20,'79:20','অতঃপর মূসা তাকে বিরাট নিদর্শন দেখাল।');
INSERT INTO "rawai" VALUES(79,21,'79:21','কিন্তু সে অস্বীকার করল এবং অমান্য করল।');
INSERT INTO "rawai" VALUES(79,22,'79:22','তারপর সে ফাসাদ করার চেষ্টায় প্রস্থান করল।');
INSERT INTO "rawai" VALUES(79,23,'79:23','অতঃপর সে লোকদেরকে একত্র করে ঘোষণা দিল।');
INSERT INTO "rawai" VALUES(79,24,'79:24','আর বলল, ‘আমিই তোমাদের সর্বোচ্চ রব’।');
INSERT INTO "rawai" VALUES(79,25,'79:25','অবশেষে আল্লাহ তাকে আখিরাত ও দুনিয়ার আযাবে পাকড়াও করলেন।');
INSERT INTO "rawai" VALUES(79,26,'79:26','নিশ্চয় যে ভয় করে তার জন্য এতে শিক্ষা রয়েছে।');
INSERT INTO "rawai" VALUES(79,27,'79:27','তোমাদেরকে সৃষ্টি করা অধিক কঠিন, না আসমান সৃষ্টি? তিনি তা বানিয়েছেন।');
INSERT INTO "rawai" VALUES(79,28,'79:28','তিনি এর ছাদকে উচ্চ করেছেন এবং তাকে সুসম্পন্ন করেছেন।');
INSERT INTO "rawai" VALUES(79,29,'79:29','আর তিনি এর রাতকে অন্ধকারাচ্ছন্ন করেছেন এবং এর দিবালোক প্রকাশ করেছেন।');
INSERT INTO "rawai" VALUES(79,30,'79:30','এরপর তিনি যমীনকে বিস্তীর্ণ করেছেন।');
INSERT INTO "rawai" VALUES(79,31,'79:31','তিনি তার ভিতর থেকে বের করেছেন তার পানি ও তার তৃণভূমি।');
INSERT INTO "rawai" VALUES(79,32,'79:32','আর পর্বতগুলোকে তিনি দৃঢ়ভাবে প্রতিষ্ঠিত করেছেন।');
INSERT INTO "rawai" VALUES(79,33,'79:33','তোমাদের ও তোমাদের চতুষ্পদ জন্তুগুলোর জীবনোপকরণস্বরূপ।');
INSERT INTO "rawai" VALUES(79,34,'79:34','অতঃপর যখন মহাপ্রলয় আসবে।');
INSERT INTO "rawai" VALUES(79,35,'79:35','সেদিন মানুষ স্মরণ করবে তা, যা সে চেষ্টা করেছে।');
