INSERT INTO "rawai" VALUES(81,23,'81:23','আর সে* তাকে** সুস্পষ্ট দিগন্তে দেখেছে। *মুহাম্মাদ (সাল্লাল্লাহু আলাইহি ওয়াসাল্লাম)।');
